"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[80289],{33080:(e,n,C)=>{C.r(n),C.d(n,{default:()=>t});var O=C(74848);const t=[{name:"\u68a6\u5e7b\u4e4b\u5200",content:(0,O.jsx)(O.Fragment,{children:"\u7b2c \u4e00 \u7ae0 \u3000\u3000\u626c\u5dde\u7684\u7e41\u534e\u51a0\u5168\u56fd\uff0c\u4e5f\u6c47\u805a\u4e86\u5168\u56fd\u76f8\u5f53\u591a\u7684\u8d22\u5bcc\uff0c\u94b1\u591a\u4eba\u4f5c\u602a\uff0c\u81ea\u7136\u5730\u53d1\u5c55\u51fa\u4e86\u5f88\u591a\u4e94 \u5149\u5341\u8272\u7684\u73a9\u4e50\u6587\u5316\uff0c\u4e91\u96c6\u4e86\u5404\u5730\u7684\u7f8e\u5973\uff0c\u5357\u56fd\u4f73\u4e3d\u5a9a\uff0c\u5317\u5730\u80ed\u8102\u9999\u3002 \u3000\u3000\u4e5f\u8bb8\uff0c\u5979\u4eec\u6765\u8fd9\u91cc\u4e0d\u662f\u51fa\u4e8e\u81ea\u613f\uff0c\u4f46\u5927\u591a\u6570\u7684\u7f8e\u5973\u5f88\u5feb\u5c31\u9002\u5e94\u4e86\u8fd9\u91cc\u7684\u9526\u8863\u7389\u98df\uff0c\u7b19\u6b4c \u4e0d\u7f00\u7684\u5962\u534e\u751f\u6d3b\u3002\u4e5f\u5b66\u4f1a\u4e86\u865a\u60c5\u5047\u610f\u7684\u9a97\u4eba\u628a\u620f\uff0c\u8fd9\u65b9\u6cd5\u8d5a\u94b1\u5bb9\u6613\u5440\uff01 \u3000\u3000\u626c\u5dde\u597d\u73a9\uff0c\u4f46\u8981\u82b1\u5927\u628a\u7684\u91d1\u94b1\uff0c\u6240\u4ee5\u6709\u8170\u7f20\u5341\u4e07\u8d2f\u3001\u9a91\u9e64\u4e0b\u626c\u5dde\u7684\u8bd7\u53e5\uff0c\u5341\u4e07\u8d2f\u7a76\u7adf\u662f \u591a\u5c11\u94b1\uff1f\u6050\u6015\u5f88\u96be\u4f5c\u4e00\u4e2a\u5177\u4f53\u7684\u754c\u5b9a\uff0c\u4f46\u7edd\u5bf9\u662f\u8868\u793a\u5f88\u591a\u7684\u610f\u601d\u3002 \u3000\u3000\u4e0d\u5750\u9a6c\u8f66\u8981\u9a91\u9e64\uff0c\u662f\u6807\u51c6\u7684\u9a97\u4eba\u8c0e\u8a00\uff0c\u8981\u591a\u5927\u4e00\u53ea\u767d\u9e64\uff0c\u624d\u80fd\u9a6e\u52a8\u5341\u4e07\u8d2f\u7684\u94b1\uff0c\u4e00\u767e\u53ea\u3001 \u4e00\u5343\u53ea\u4e5f\u4e0d\u884c\u3002 \u3000\u3000\u4f46\u4e2d\u56fd\u7684\u8bfb\u4e66\u4eba\u90fd\u80fd\u4e86\u89e3\u8fd9\u4e24\u53e5\u8bdd\u662f\u4e00\u79cd\u5bd3\u610f\uff0c\u662f\u4e00\u4e2a\u6258\u60f3\uff0c\u4e5f\u662f\u4e00\u79cd\u8bbd\u523a\uff0c\u9ec4\u9e64\u4e00\u53bb \u4e0d\u590d\u8fd4\u3002\u5f53\u7136\u5341\u4e07\u8d2f\u7684\u94b1\u8d22\u7559\u5728\u626c\u5dde\uff0c\u4eba\u80fd\u4e0d\u80fd\u751f\u79bb\u626c\u5dde\uff0c\u5c31\u8981\u770b\u4f60\u51e0\u65f6\u80fd\u9192\u626c\u5dde\u68a6\u4e86\uff1f\u5f97 \u6709\u70b9\u6167\u6839\u624d\u884c\u3002 \u3000\u3000\u5343\u53e5\u5f52\u6210\u4e00\u53e5\u8bf4\uff0c\u5728\u8fd9\u91cc\u8981\u5168\u8eab\u662f\u94b1\u624d\u597d\u73a9\u3002 \u3000\u3000\u626c\u5dde\u7684\u7578\u5f62\u7e41\u8363\uff0c\u4e5f\u62db\u6765\u6c5f\u6e56\u4e0a\u5404\u884c\u5404\u4e1a\u7684\u9876\u5c16\u4eba\u624d\uff0c\u662f\u4e2a\u85cf\u9f99\u5367\u864e\u7684\u5730\u65b9\uff0c\u4e5f\u5e38\u5e38\u53d1 \u751f\u4e86\u4e00\u4e9b\u532a\u5937\u6240\u601d\u7684\u5947\u602a\u4e8b\u60c5\u3002 \u3000\u3000\u626c\u5dde\u77e5\u5e9c\u738b\u5c11\u537f\uff0c\u662f\u4e2a\u7cbe\u660e\u5e72\u7ec3\u7684\u4eba\u7269\uff0c\u65e2\u80fd\u957f\u8896\u5584\u821e\uff0c\u4e5f\u77e5\u89c1\u98ce\u8f6c\u8235\uff0c\u8179\u4e2d\u6709\u6587\u7ae0\uff0c \u80f8\u4e2d\u6709\u97ec\u7565\uff0c\u5728\u662f\u975e\u65e5\u65e5\u6709\uff0c\u591c\u591c\u5947\u6848\u591a\u7684\u626c\u5dde\u57ce\uff0c\u738b\u77e5\u5e9c\u5df2\u5e72\u4e86\u4e09\u5e74\uff0c\u4efb\u671f\u5df2\u6ee1\uff0c\u518d\u54ac\u7259 \u82e6\u6491\u4e00\u4e9b\u65f6\u95f4\uff0c\u4e00\u4e24\u4e2a\u6708\u4e4b\u5185\uff0c\u5c31\u53ef\u80fd\u8c03\u4efb\u4eac\u5b98\u3002\u4ece\u6b64\uff0c\u9752\u4e91\u76f4\u4e0a\uff0c\u524d\u9014\u4e0d\u53ef\u9650\u91cf\u4e86\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u738b\u77e5\u5e9c\u975e\u5e38\u5c0f\u5fc3\uff0c\u4ed6\u793c\u8d24\u4e0b\u58eb\uff0c\u4f7f\u5e55\u4e0b\u51e0\u4f4d\u6587\u6848\u5e08\u7237\uff0c\u90fd\u80fd\u5404\u5c55\u6240\u957f\uff0c\u9f50\u5fc3\u6548\u547d\uff0c \u628a\u4e0d\u5c11\u68d8\u624b\u7684\u5927\u6848\u5b50\uff0c\u5904\u7406\u5f97\u5706\u6da6\u5f97\u4f53\uff0c\u5316\u5e72\u6208\u4e3a\u7389\u5e1b\uff0c\u6d88\u4e8e\u65e0\u5f62\u3002 \u3000\u3000\u738b\u5c11\u537f\u4e5f\u6df1\u77e5\u6c5f\u6e56\u4e2d\u4e8b\uff0c\u522b\u6709\u8e4a\u5f84\uff0c\u4e0d\u662f\u51ed\u4ed7\u5b98\u5a01\u548c\u8bfb\u4e66\u591a\u3001\u660e\u662f\u975e\uff0c\u5c31\u80fd\u529e\u7684\u5b8c\u7f8e\u3002 \u6240\u4ee5\uff0c\u9664\u4e86\u5e9c\u8859\u4e2d\u7684\u73ed\u5934\u6355\u5feb\uff0c\u5c3d\u91cf\u9009\u7cbe\u4efb\u80fd\u4e4b\u5916\uff0c\u8fd8\u91cd\u91d1\u7684\u8058\u4e86\u4e00\u4e9b\u5947\u4eba\u9ad8\u624b\uff0c\u6697\u4e2d\u5e2e\u52a9\uff0c \u4fa6\u7834\u4e86\u4e0d\u5c11\u51f6\u6848\uff0c\u4e5f\u5904\u51b3\u4e86\u4e00\u4e9b\u608d\u532a\u5927\u76d7\uff0c\u653f\u58f0\u5b98\u8a89\uff0c\u90fd\u6709\u7740\u76f8\u5f53\u9ad8\u7684\u8bc4\u4ef7\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\u662f\u738b\u77e5\u5e9c\u4e5f\u4f1a\u8981\u94b1\uff0c\u5982\u679c\u6e05\u5ec9\u7684\u4e00\u4ecb\u4e0d\u53d6\uff0c\u5355\u662f\u793c\u8058\u7684\u6587\u6848\u8944\u52a9\uff0c\u6c5f\u6e56\u9ad8\u4eba\uff0c\u5c31 \u975e\u4ed6\u7684\u4ff8\u94f6\u6240\u80fd\u652f\u5e94\uff0c\u53ea\u662f\u4ed6\u8981\u94b1\u8981\u7684\u6709\u5206\u5bf8\uff0c\u8981\u5f97\u4eba\u5fc3\u7518\u60c5\u613f\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u79cd\u4e8b\u4e5f\u4e0d\u7528\u77e5\u5e9c\u5927\u4eba\u51fa\u9762\uff0c\u6709\u4e00\u4f4d\u6587\u6848\u5e08\u7237\u51fa\u5934\u5c31\u884c\u4e86\u3002 \u3000\u3000\u6570\u5341\u5e74\u6765\u7684\u626c\u5dde\u77e5\u5e9c\uff0c\u5e72\u5f97\u6700\u4e45\u7684\u662f\u4e0a\u4e00\u4efb\u7684\u4e00\u5e74\u534a\uff0c\u90a3\u4f4d\u77e5\u5e9c\u7684\u80cc\u540e\uff0c\u6709\u4e24\u4f4d\u738b\u7237\u6491 \u7740\uff0c\u9760\u5c71\u591f\u5f3a\uff0c\u4f46\u8fd8\u672a\u80fd\u5b8c\u6210\u4ed6\u4e24\u5e74\u626c\u5dde\u77e5\u5e9c\u7684\u5fc3\u613f\uff0c\u5c31\u641e\u7684\u7070\u5934\u571f\u8138\u4e0b\u4e86\u53f0\uff0c\u53ef\u662f\u738b\u5c11\u537f \u5df2\u5e72\u4e86\u4e09\u5e74\uff0c\u662f\u7edd\u65e0\u4ec5\u6709\u7684\u9ad8\u624b\u4e86\u3002 \u3000\u3000\u8fd9\u91cc\u662f\u904d\u5730\u9ec4\u91d1\uff0c\u4f46\u8981\u6709\u9ad8\u660e\u7684\u624d\u6167\u3001\u51fa\u8272\u7684\u6280\u827a\uff0c\u624d\u80fd\u8d5a\u5230\u5927\u94b1\uff0c\u624d\u80fd\u8d5a\u5f97\u8f7b\u677e\u3002 \u3000\u3000\u4eca\u5929\u662f\u738b\u5c11\u537f\u5c31\u4efb\u626c\u5dde\u77e5\u5e9c\u7684\u4e09\u5e74\u5927\u5e86\uff0c\u738b\u5927\u4eba\u4e00\u65e9\u5c31\u4f20\u4e0b\u4e00\u9053\u4ee4\u8c15\uff0c\u4e0d\u51c6\u6709\u4efb\u4f55\u5e86\u795d \u7684\u6d3b\u52a8\uff0c\u7167\u5e38\u503c\u73ed\u7406\u4e8b\uff0c\u548c\u5e73\u5e38\u4e00\u6837\uff0c\u53d7\u7406\u8bbc\u6848\u3002 \u3000\u3000\u8fc7\u4e86\u4eca\u5929\uff0c\u5c31\u7b97\u4efb\u671f\u5c4a\u6ee1\uff0c\u5c31\u7b49\u7740\u63a5\u8c03\u804c\u7684\u7687\u547d\u4e86\u3002 \u3000\u3000\u62ac\u5934\u770b\u770b\u6b63\u5348\u7684\u9633\u5149\uff0c\u738b\u5c11\u537f\u4f38\u4e2a\u61d2\u8170\uff0c\u5401\u4e00\u53e3\u6c14\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u53c8\u8fc7\u53bb\u4e86\u5927\u534a\u5929\uff0c\u4eca\u5929 \u592a\u9633\u4e0b\u4e86\u5c71\uff0c\u5c31\u7b97\u6cd5\u5b9a\u4efb\u671f\u5df2\u6ee1\uff0c\u867d\u7136\u7687\u547d\u672b\u5230\u4e4b\u524d\uff0c\u4ed6\u8fd8\u662f\u626c\u5dde\u77e5\u5e9c\uff0c\u4f46\u5728\u738b\u5c11\u537f\u672c\u4eba\u5fc3 \u4e2d\uff0c\u5374\u5212\u5b9a\u4e00\u4e2a\u6cd5\u5b9a\u4efb\u6ee1\u7684\u754c\u7ebf\u3002\u8fd9\u4e0a\u5348\u4ed6\u4e00\u53e3\u6c14\u6279\u590d\u3001\u5904\u7406\u4e86\u5341\u516b\u4ef6\u516c\u4e8b\uff0c\u4ed6\u8981\u5728\u5fc3\u7406\u7684 \u754c\u9650\u4e0a\uff0c\u4f5c\u5230\u6848\u65e0\u79ef\u724d\u3002 \u3000\u3000\u8be5\u4f11\u606f\u4e00\u4e0b\u5403\u5348\u996d\u4e86\uff0c\u738b\u5c11\u537f\u7f13\u6b65\u884c\u51fa\u4e86\u516c\u4e8b\u623f\uff0c\u8fd9\u4e2a\u6700\u6709\u8d22\u52bf\u7684\u5929\u4e0b\u7b2c\u4e00\u77e5\u5e9c\uff0c\u8ba9\u81ea \u8d1f\u624d\u534e\u7684\u5b98\u573a\u65b0\u79c0\u7fa1\u6155\uff0c\u4e00\u65e6\u6210\u771f\uff0c\u5374\u53c8\u80c6\u98a4\u5fc3\u60ca\uff0c\u516c\u79c1\u538b\u529b\u5927\uff0c\u51f6\u9669\u968f\u65f6\u6765\uff0c\u53ef\u80fd\u8111\u888b\u642c \u4e86\u5bb6\uff0c\u8fd8\u4e0d\u660e\u767d\u662f\u600e\u4e48\u6b7b\u7684\uff1f \u3000\u3000\u4e00\u9053\u5723\u65e8\u62ff\u95ee\u4e0b\u72f1\u3001\u89e3\u4eac\u5019\u5ba1\uff0c\u8fd8\u4e0d\u77e5\u9053\u51fa\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f \u3000\u3000\u8fd9\u91cc\u6709\u4e3e\u56fd\u6700\u591a\u7684\u5bcc\u8c6a\uff0c\u8d22\u4ea7\u65e0\u53ef\u6570\u8ba1\uff0c\u4e5f\u5efa\u7acb\u4e86\u901a\u5f80\u6743\u52bf\u7684\u7f51\u8def\uff0c\u4e5f\u6709\u4eba\u91cd\u91d1\u793c\u8058\u4e86 \u6c5f\u6e56\u4e0a\u5f02\u80fd\u5947\u624d\u4e4b\u58eb\uff0c\u4ee5\u4fdd\u62a4\u4ed6\u4eec\u5b89\u5168\uff0c\u62d3\u5c55\u4ed6\u4eec\u7684\u8d22\u52bf\uff0c\u624b\u6bb5\u51b7\u5389\uff0c\u9634\u9669\u4e07\u7aef\uff0c\u8eab\u8d1f\u4e00\u65b9 \u6cbb\u5b89\u8d23\u4efb\u7684\u77e5\u5e9c\u5927\u4eba\uff0c\u4e5f\u5c31\u4e0d\u80dc\u8d1f\u8377\u4e86\u3002\u4f55\u51b5\uff0c\u8fd8\u8981\u62c5\u5fc3\u81ea\u8eab\u7684\u5b89\u5168\uff0c\u8fd9\u4e2a\u5b98\u5c31\u5f53\u5f97\u5982\u5c65\u8584 \u51b0\uff0c\u5982\u4e34\u6df1\u6e0a\u4e86\u3002 \u3000\u3000\u5b88\u5728\u516c\u4e8b\u623f\u95e8\u53e3\u7684\u4e24\u4e2a\u5b88\u536b\uff0c\u4e00\u4e2a\u662f\u503c\u73ed\u7684\u6355\u5934\uff0c\u5b98\u670d\u4f69\u5200\uff0c\u9887\u6709\u70b9\u5a01\u4eea\u3002\u4e00\u4e2a\u7a7f\u9752\u8863 \u7684\u6c49\u5b50\uff0c\u8db3\u8e6c\u8584\u5e95\u5feb\u9774\uff0c\u6536\u62fe\u7684\u5f88\u4fd0\u843d\uff0c\u4f46\u5374\u8d64\u624b\u7a7a\u62f3\uff0c\u4e0d\u89c1\u5175\u5203\u3002 \u3000\u3000\u4f46\u5185\u884c\u4eba\uff0c\u7a0d\u4e00\u7559\u5fc3\uff0c\u5c31\u53ef\u4ee5\u770b\u51fa\u4ed6\u8170\u4e2d\u56f4\u6709\u5175\u5203\uff0c\u662f\u4e00\u628a\u7f05\u94c1\u8f6f\u5200\u3002 \u3000\u3000\u80fd\u7528\u8fd9\u79cd\u5175\u5668\u7684\u4eba\uff0c\u5185\u529f\u8981\u6709\u76f8\u5f53\u7684\u57fa\u7840\uff0c\u624d\u80fd\u8fd0\u52b2\u5f79\u5200\uff0c\u64cd\u63a7\u81ea\u5982\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u4e00\u7740\u6355\u5feb\u7684\u5b98\u670d\uff0c\u4e00\u7740\u4fbf\u88c5\uff0c\u4f46\u540c\u65f6\u79fb\u52a8\u811a\u6b65\uff0c\u8fc5\u5feb\u5730\u6321\u5728\u4e86\u77e5\u5e9c\u5927\u4eba\u7684\u8eab\u524d\u3002 \u884c\u52a8\u654f\u6377\uff0c\u4e5f\u6709\u4e9b\u7d27\u5f20\u3002 \u3000\u3000\u738b\u5c11\u537f\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u738b\u575a\uff0c\u51fa\u4e86\u4ec0\u4e48\u9ebb\u70e6\uff1f\u201d \u3000\u3000\u7a7f\u7740\u5b98\u670d\u7684\u6355\u5934\uff0c\u5fae\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u662f\u94c1\u603b\u5934\u513f\u7684\u4ea4\u5f85\uff0c\u8981\u5c5e\u4e0b\u5929\u5929\u5c0f\u5fc3\u4fdd\u62a4\u5927\u4eba\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u4f60\u8fd9\u4e2a\u526f\u603b\u6355\u5934\u5c31\u4eb2\u81ea\u8dd1\u6765\u503c\u73ed\u4e86\uff1f\u201d\u738b\u5c11\u537f\u6709\u4e9b\u611f\u52a8\u5730\u9053\uff1a\u201c\u7a76\u7adf\u53d1\u751f\u4e86\u4ec0 \u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u201c\u6628\u591c\u6709\u591c\u884c\u4eba\u8fdb\u5165\u4e86\u5e9c\u8859\uff0c\u201d\u738b\u575a\u671b\u4e86\u90a3\u9752\u8863\u4eba\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u6211\u548c\u94c1\u6355\u5934\u95fb\u60ca\u8d76\u5230\uff0c \u6765\u4eba\u5df2\u88ab\u4f55\u5927\u5149\u5144\u6253\u8dd1\u4e86\uff0c\u8fd8\u8ba9\u6765\u4eba\u6302\u4e86\u5f69\uff0c\u7559\u4e0b\u4ed6\u4e00\u7247\u8863\u8896\u3002\u201d \u3000\u3000\u94c1\u6355\u5934\u662f\u6307\u626c\u5dde\u5e9c\u7684\u603b\u6355\u5934\u94c1\u7fce\uff0c\u738b\u575a\u662f\u626c\u5dde\u5e9c\u7684\u526f\u603b\u6355\u5934\uff0c\u4f55\u5927\u5149\u662f\u738b\u5c11\u537f\u8bf7\u7684\u79c1\u4eba \u4fdd\u9556\u3002\u738b\u5927\u4eba\u516c\u79c1\u5206\u660e\uff0c\u89c9\u5f97\u5e26\u7740\u6355\u5feb\u4f5c\u4fdd\u9556\uff0c\u884c\u52a8\u4e5f\u4e0d\u592a\u65b9\u4fbf\uff0c\u4f55\u5927\u5149\u662f\u94c1\u7fce\u7684\u670b\u53cb\uff0c\u8eab \u624b\u77eb\u5065\uff0c\u5200\u6cd5\u4e00\u6d41\u3002\u94c1\u7fce\u8d39\u4e86\u4e0d\u5c11\u53e3\u820c\uff0c\u624d\u8bf4\u52a8\u4ed6\u5c48\u5c31\u738b\u77e5\u5e9c\u7684\u79c1\u4eba\u4fdd\u9556\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u94c1\u7fce\u3001\u738b\u575a\uff0c\u90fd\u662f\u6b66\u6797\u9ad8\u624b\uff0c\u738b\u5c11\u537f\u82b1\u4e86\u5f88\u5927\u7684\u5de5\u592b\u624d\u8bf4\u52a8\u4e24\u4eba\u51fa\u4efb\u626c\u5dde\u6b63\u3001 \u526f\u603b\u6355\u5934\uff0c\u5c31\u662f\u6355\u5feb\u4e2d\uff0c\u4e5f\u6709\u4e0d\u5c11\u9ad8\u624b\uff0c\u51b2\u7740\u94c1\u7fce\u3001\u738b\u575a\u7684\u9762\u5b50\uff0c\u8fdb\u5165\u4e86\u5e9c\u8859\u3002 \u3000\u3000\u4ed6\u4eec\u6b66\u529f\u9ad8\u5f3a\uff0c\u624d\u628a\u5929\u4e0b\u6700\u96be\u6cbb\u7406\u7684\u626c\u5dde\u5e9c\uff0c\u4fdd\u6301\u4e2a\u6c34\u4e0d\u626c\u6ce2\u7684\u5c40\u9762\uff0c\u81f3\u5c11\u662f\u8868\u9762\u4e0a\u5982 \u6b64\u3002 \u3000\u3000\u738b\u5c11\u537f\u56de\u987e\u4e86\u4f55\u5927\u5149\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5927\u5149\u600e\u4e48\u4e0d\u901a\u70b9\u8baf\u606f\u7ed9\u6211\u5462\uff1f\u201d \u3000\u3000\u201c\u94c1\u7fce\u8bf4\u5927\u4eba\u7684\u6cd5\u5b9a\u4efb\u671f\uff0c\u4eca\u65e5\u5c4a\u6ee1\uff0c\u8981\u5e9c\u8859\u4e2d\u5e73\u5e73\u5b89\u5b89\u5ea6\u8fc7\u4eca\u5929\u3002\u201d\u4f55\u5927\u5149\u7b11\u4e00\u7b11\uff0c \u9053\uff1a\u201c\u6709\u4e8b\u60c5\u660e\u5929\u518d\u8bf4\uff0c\u6240\u4ee5\uff0c\u5c31\u6682\u65f6\u7792\u4f4f\u4e86\u5927\u4eba\u3002\u201d \u3000\u3000\u201c\u53ea\u6015\u4e0d\u6b62\u662f\u8fd9\u4e00\u4ef6\u4e8b\u60c5\u5427\uff1f\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u90a3\u6df7\u5165\u5e9c\u8859\u7684\u591c\u884c\u4eba\uff0c\u5f88\u53ef\u80fd\u522b\u7528\u5fc3\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u4eba\u8bf4\u7684\u5bf9\uff0c\u770b\u6765\u8fd9\u4ef6\u4e8b\u662f\u7792\u4e0d\u4f4f\u4e86\u3002\u201d\u94c1\u7fce\u5feb\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u63a5\u9053\uff1a\u201c\u94c1\u67d0\u4eba\u4e00\u70b9\u5c0f \u5c0f\u5fc3\u613f\uff0c\u7adf\u7136\u65e0\u6cd5\u5f97\u507f\uff0c\u5509\uff01\u6848\u5b50\u592a\u5927\u4e86\uff0c\u94c1\u67d0\u4eba\u625b\u4e0d\u8d77\u6765\u3002\u201d \u3000\u3000\u738b\u5927\u4eba\u8179\u4e2d\u7684\u9965\u997f\uff0c\u7a81\u7136\u6d88\u5931\u4e86\u3002\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e8b\u60c5\u65e2\u7136\u53d1\u751f\u4e86\uff0c\u5c31\u4e0d\u7528\u6015\u5b83\uff0c \u8d70\uff01\u54b1\u4eec\u5230\u623f\u91cc\u8c08\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\uff0c\u8bf7\u5148\u5403\u996d\u5427\uff01\u65f6\u5df2\u8fc7\u5348\u4e86\u3002\u201d\u738b\u575a\u9053\uff1a\u201c\u5927\u4eba\u65e5\u7406\u4e07\u673a\uff0c\u8eab\u4f53\u8981\u4fdd\u91cd\u554a\uff1f\u201d \u3000\u3000\u201c\u628a\u996d\u83dc\u5f00\u5230\u516c\u4e8b\u623f\u6765\uff0c\u4e09\u4f4d\u4e5f\u8be5\u5403\u70b9\u4e1c\u897f\u4e86\uff0c\u54b1\u4eec\u8fb9\u5403\u8fb9\u8c08\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u8868\u73b0\u51fa\u4f53\u6064\u4e0b\u5c5e\u7684\u60c5\u610f\u3002 \u3000\u3000\u94c1\u7fce\u8fde\u53e3\u6c34\u4e5f\u672a\u559d\uff0c\u5df2\u8feb\u4e0d\u53ca\u5f85\u5730\u9053\uff1a\u201c\u662f\u4e00\u693f\u5f88\u9ebb\u70e6\u7684\u5927\u8840\u6848\u2026\u2026\u201d \u3000\u3000\u201c\u6b7b\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u738b\u77e5\u5e9c\u4e5f\u6709\u70b9\u7d27\u5f20\u4e86\u3002 \u3000\u3000\u201c\u6c34\u5e08\u63d0\u7763\u9a6c\u957f\u5c71\u7684\u72ec\u5b50\u9a6c\u656c\u6587\u2026\u2026\u201d \u3000\u3000\u738b\u5c11\u537f\u5fc3\u5934\u5267\u8df3\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u51f6\u624b\u5462\uff1f\u201d \u3000\u3000\u201c\u626c\u5dde\u5de8\u5bcc\u91d1\u767e\u5e74\u7684\u5973\u513f\u91d1\u5c0f\u7709\uff0c\u51f6\u6848\u5c31\u53d1\u751f\u5728\u4e24\u4eba\u65b0\u623f\u4e2d\u2026\u2026\u201d\u94c1\u7fce\u9053\uff1a\u201c\u4eb2\u5bb6\u53d8\u6210 \u4ec7\u5bb6\uff0c\u9a6c\u63d0\u7763\u5e26\u4e86\u767e\u540d\u6c34\u5e08\u7cbe\u9510\u9a81\u5200\u624b\uff0c\u8981\u6293\u91d1\u5c0f\u7709\uff0c\u4e71\u5200\u5206\u5c38\u597d\u4e3a\u5b50\u590d\u4ec7\uff0c\u91d1\u767e\u5e74\u4e5f\u5e26\u4e86 \u6570\u5341\u4e2a\u62a4\u9662\u4fdd\u9556\uff0c\u4fdd\u62a4\u91d1\u5c0f\u7709\uff0c\u4e0d\u51c6\u4eba\u6293\uff0c\u53cc\u65b9\u5251\u62d4\u5f29\u5f20\uff0c\u5bf9\u5cd9\u4e0d\u4e0b\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u542c\u8fc7\u6848\u60c5\uff0c\u53ea\u89c9\u5934\u8111\u80c0\u75db\uff0c\u4e00\u4e2a\u8111\u888b\u4e24\u4e2a\u5927\u3002\u6c34\u5e08\u63d0\u7763\u9a6c\u957f\u5c71\uff0c\u7edf\u8f96\u6570\u4e07\u6c34\u519b\uff0c \u8fd1\u5e74\u6765\u8fde\u527f\u4e86\u6d1e\u5ead\u3001\u592a\u6e56\u4e24\u5904\u6e56\u532a\uff0c\u5e1d\u7737\u6b63\u9686\u3002 \u3000\u3000\u91d1\u767e\u5e74\u662f\u626c\u5dde\u5de8\u5bcc\u4e4b\u4e00\uff0c\u624b\u773c\u901a\u5929\uff0c\u4eac\u4e2d\u5173\u7cfb\u5e7f\u591a\uff0c\u4e0d\u5c11\u5c06\u76f8\u738b\u4faf\u7b49\u4e00\u54c1\u5927\u5b98\uff0c\u90fd\u662f\u4ed6 \u7684\u597d\u53cb\uff0c\u5e9c\u4e2d\u4e5f\u8058\u517b\u7740\u4e0d\u5c11\u6b66\u6797\u9ad8\u624b\u3001\u6c5f\u6e56\u5947\u4eba\u3002\u653e\u4efb\u8fd9\u6837\u4e24\u4e2a\u4eba\u5bf9\u4e0a\u5e72\uff0c\u53ea\u6050\u626c\u5dde\u57ce\u4e5f\u8981 \u88ab\u4ed6\u4eec\u7ffb\u5730\u4e09\u5c3a\u3002 \u3000\u3000\u201c\u94c1\u7fce\uff0c\u201d\u738b\u5927\u4eba\u9547\u5b9a\u4e86\u4e00\u4e0b\u5fc3\u795e\uff0c\u9053\uff1a\u201c\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u52a8\u624b\u51b2\u7a81\uff0c\u4f24\u4ea1\u592a\u591a\uff0c\u4ec7\u6068\u7ed3\u6df1\uff0c \u8fd9\u5ea7\u6570\u767e\u5e74\u7684\u7e41\u534e\u540d\u57ce\uff0c\u4e5f\u8bb8\u8981\u6bc1\u5728\u4ed6\u4eec\u624b\u4e2d\u2026\u2026\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u5df2\u96c6\u5408\u4e86\u4e09\u73ed\u6355\u5feb\uff0c\u6682\u65f6\u628a\u4e24\u65b9\u5206\u9694\uff0c\u5211\u623f\u5f20\u5e08\u7237\uff0c\u4e5f\u5e26\u7740\u4e24\u4f4d\u5728\u73b0\u573a\u8c03\u89e3\u2026\u2026\u201d \u94c1\u7fce\u9053\uff1a\u201c\u4ef5\u4f5c\u73ed\u623f\uff0c\u4e5f\u90fd\u5230\u9f50\uff0c\u5c3d\u91cf\u4fdd\u6301\u4e86\u547d\u6848\u73b0\u573a\uff0c\u5c31\u7b49\u5927\u4eba\u4eb2\u4e34\u6307\u6325\u6392\u89e3\u4e86\u3002\u201d \u3000\u3000\u201c\u7acb\u523b\u5907\u8f7f\u3002\u201d\u738b\u5927\u4eba\u6765\u4e0d\u53ca\u66f4\u6362\u5b98\u670d\u3001\u888d\u5e26\u51a0\u5195\uff0c\u5c31\u5728\u8f7f\u4e2d\u7a7f\u6234\u4e86\u3002 \u3000\u3000\u547d\u6848\u5c31\u53d1\u751f\u5728\u7ed3\u5a5a\u7684\u65b0\u5c45\u4e2d\uff0c\u65b0\u5c45\u5efa\u7b51\u5728\u626c\u5dde\u57ce\u4e1c\u5317\u65b9\u4e00\u7247\u5e7f\u5927\u7684\u571f\u5730\uff0c\u82b1\u6811\u73af\u7ed5\uff0c\u95f9 \u4e2d\u53d6\u9759\uff0c\u7528\u5fc3\u770b\uff0c\u53ef\u4ee5\u770b\u51fa\u6765\u82b1\u6811\u90fd\u662f\u6574\u68f5\u7684\u79fb\u690d\u800c\u6765\uff0c\u623f\u820d\u4e5f\u90fd\u662f\u65b0\u5efa\u800c\u6210\uff0c\u662f\u5f7b\u5934\u5f7b\u5c3e \u7684\u65b0\u5c45\u3002 \u3000\u3000\u65b0\u5c45\u5916\u5200\u67aa\u6392\u5217\uff0c\u6620\u76ee\u751f\u8f89\uff0c\u6570\u767e\u540d\u7cbe\u9510\u519b\u58eb\uff0c\u5f13\u4e0a\u5f26\uff0c\u5200\u51fa\u9798\uff0c\u6392\u6210\u4e86\u653b\u51fb\u7684\u961f\u5f62\uff0c \u5c31\u7b49\u7740\u4e3b\u5e05\u4e00\u58f0\u53f7\u4ee4\uff0c\u5c31\u5f00\u59cb\u51b2\u950b\u9677\u9635\u4e86\u3002 \u3000\u3000\u5927\u6982\u662f\u9a6c\u957f\u5c71\u53c8\u8c03\u6765\u5927\u961f\u519b\u58eb\uff0c\u5df2\u628a\u65b0\u5c45\u56e2\u56e2\u56f4\u56f0\u4e86\u3002 \u3000\u3000\u626c\u5dde\u57ce\u4e2d\u867d\u7136\u65e0\u5947\u4e0d\u6709\uff0c\u4f46\u50cf\u8fd9\u7b49\u5927\u519b\u6392\u5217\uff0c\u5f62\u540c\u653b\u57ce\u7684\u573a\u9762\uff0c\u53ef\u4e5f\u662f\u4ece\u672a\u6709\u8fc7\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u4e0b\u4e86\u8f7f\uff0c\u56db\u987e\u4e00\u773c\uff0c\u9664\u770b\u5230\u58ee\u76db\u7684\u519b\u5a01\u4e4b\u5916\uff0c\u5012\u662f\u672a\u89c1\u5230\u56f4\u89c2\u7684\u6c11\u4f17\u3002 \u3000\u3000\u5927\u6982\u626c\u5dde\u4eba\u90fd\u5f88\u806a\u660e\uff0c\u4e86\u89e3\u5230\u8fd9\u7b49\u5927\u519b\u51b2\u6740\uff0c\u4e00\u65e6\u5f00\u6218\uff0c\u573a\u9762\u5f88\u96be\u63a7\u5236\uff0c\u770b\u70ed\u95f9\uff0c\u62ce\u7740 \u8111\u888b\u770b\uff0c\u5c31\u5927\u5927\u7684\u5212\u4e0d\u6765\u4e86\uff0c\u6240\u4ee5\uff0c\u5927\u5bb6\u90fd\u9000\u907f\u4e09\u820d\u3002 \u3000\u3000\u94c1\u7fce\u5f53\u5148\u5f00\u9053\uff0c\u4e00\u9762\u9ad8\u58f0\u558a\u9053\uff1a\u201c\u626c\u5dde\u77e5\u5e9c\u738b\u5927\u4eba\u5230\u3002\u201d \u3000\u3000\u6392\u5217\u7684\u6c34\u519b\uff0c\u8ba9\u5f00\u4e86\u4e00\u6761\u8fc7\u9053\uff0c\u738b\u5c11\u537f\u5728\u94c1\u7fce\u3001\u738b\u575a\u3001\u4f55\u5927\u5149\u7684\u62a4\u536b\u4e0b\u8fdb\u5165\u65b0\u5c45\u5ead\u9662\u3002 \u3000\u3000\u5ead\u9662\u4e2d\u82b1\u8f69\u3001\u6c34\u69ad\uff0c\u8bbe\u8ba1\u7684\u5e7d\u7f8e\u3001\u96c5\u81f4\uff0c\u4f46\u6c14\u6c1b\u7684\u7d27\u5f20\uff0c\u5c24\u8fc7\u95e8\u5916\u5927\u519b\u5468\u56f4\u7684\u5f62\u52bf\u3002 \u3000\u3000\u6c34\u5e08\u63d0\u7763\u9a6c\u957f\u5c71\uff0c\u7ad9\u5728\u5927\u5385\u524d\u9762\u53f0\u9636\u4e0b\uff0c\u4e24\u4fa7\u8eab\u540e\u6574\u9f50\u7684\u6392\u5217\u4e86\u767e\u591a\u540d\u9a81\u5200\u624b\uff0c\u4e09\u5c3a\u516d \u5bf8\u7684\u539a\u80cc\u8584\u5203\u957f\u5200\uff0c\u90fd\u5df2\u51fa\u9798\uff0c\u5230\u52a0\u4e0a\u4e00\u5c3a\u4e8c\u5bf8\u94f6\u4e1d\u7f20\u7ed5\u7684\u5200\u67c4\uff0c\u770b\u4e0a\u53bb\u5200\u5149\u5982\u96ea\uff0c\u6740\u6c14\u6d53 \u91cd\u51db\u70c8\uff0c\u5341\u5206\u9a87\u4eba\u3002 \u3000\u3000\u91d1\u767e\u5e74\u4e00\u88ad\u9526\u7f0e\u957f\u888d\uff0c\u7ad9\u5728\u53f0\u9636\u4e0a\u5927\u5385\u95e8\u5916\uff0c\u4e24\u4fa7\u5206\u7acb\u7740\u56db\u4e2a\u9ed1\u8863\u52b2\u88c5\u5927\u6c49\uff0c\u8170\u4e2d\u4e00\u6761 \u56db\u5bf8\u5bbd\u7684\u76ae\u5e26\u4e0a\uff0c\u5206\u63d2\u5341\u4e8c\u628a\u95ea\u4eae\u7684\u65e0\u67c4\u98de\u5200\uff0c\u624b\u4e2d\u5374\u5404\u6267\u7740\u4e00\u6839\u4e5d\u5c3a\u4ee5\u4e0a\u7684\u86c7\u5934\u6756\uff0c\u670d\u8272 \u5175\u5203\u76f8\u540c\u3001\u5e74\u9f84\u76f8\u82e5\uff0c\u90fd\u5728\u56db\u5341\u4e0a\u4e0b\u3002 \u3000\u3000\u8fd9\u662f\u6c5f\u6e56\u4e0a\u4e0d\u5e38\u89c1\u5230\u7684\u5175\u5203\uff0c\u66f4\u5c11\u89c1\u5230\u7684\u662f\u5f88\u591a\u4eba\u540c\u65f6\u65bd\u7528\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u8fd9\u79cd\u5947\u5f62\u7684\u5916\u95e8\u5175\u5203\uff0c\u5927\u90fd\u662f\u72ec\u6597\u6280\u827a\uff0c\u5175\u5203\u4e0a\u4e5f\u6709\u5b83\u7684\u79d8\u5bc6\u5999\u7528\uff0c\u4e0d\u9002\u5b9c\u591a\u6570 \u4eba\u540c\u65f6\u7ec3\u4e60\u3002 \u3000\u3000\u86c7\u5934\u6253\u9020\u5341\u5206\u903c\u771f\uff0c\u9aa4\u770b\u4e0a\u53bb\uff0c\u5c31\u50cf\u56db\u4eba\u624b\u4e2d\u5404\u63d0\u4e86\u4e00\u6761\u8fd1\u4e08\u957f\u7684\u9ed1\u8272\u5927\u86c7\u3002 \u3000\u3000\u4f55\u5927\u5149\u7d27\u968f\u738b\u77e5\u5e9c\u7684\u8eab\u4fa7\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u4eba\uff0c\u90a3\u86c7\u5934\u6756\u5185\u85cf\u6709\u6bd2\u9488\uff0c\u975e\u5e38\u7684\u6b79\u6bd2\uff0c\u9a6c\u63d0 \u7763\u8fd9\u6279\u9a81\u5200\u624b\uff0c\u662f\u6c34\u5e08\u4e2d\u7684\u7cbe\u9510\uff0c\u4f46\u771f\u8981\u62da\u8d77\u6765\uff0c\u4f24\u4ea1\u5b9a\u7136\u5341\u5206\u60e8\u91cd\uff0c\u6700\u540e\u7684\u7ed3\u5c40\uff0c\u5c31\u5f88\u96be \u63a7\u5236\u4e86\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u8fd9\u4e00\u961f\u6c34\u5e08\u7cbe\u9510\u7684\u9a81\u5200\u624b\uff0c\u8fd8\u4e0d\u662f\u91d1\u767e\u5e74\u8fd9\u6279\u62a4\u9662\u6b66\u5e08 \u7684\u654c\u624b\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u201d\u4f55\u5927\u5149\u4f4e\u58f0\u9053\uff1a\u201c\u5c31\u4f55\u67d0\u89c2\u5bdf\u6240\u5f97\uff0c\u91d1\u767e\u5e74\u8fd9\u4e9b\u62a4\u9662\u6b66\u58eb\uff0c\u4e0d\u5c11\u662f\u9ad8\u624b\uff0c\u4eba \u6570\u867d\u7136\u4e0d\u591a\uff0c\u4f46\u5982\u4efb\u4ed6\u4eec\u653e\u624b\u65bd\u4e3a\uff0c\u90a3\u5c31\u4f1a\u6740\u6210\u8840\u6ee1\u6c9f\u6e20\uff0c\u5c38\u79ef\u5ead\u9662\u7684\u666f\u51b5\uff0c\u6050\u975e\u5927\u4eba\u80fd\u591f \u625b\u5f97\u8d77\u6848\u5b50\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\uff01\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u53cc\u65b9\u52a8\u624b\u2026\u2026\u201d\u738b\u5c11\u537f\u53e3\u4e2d\u8bf4\u7740\uff0c\u4eba\u5df2\u4e3e\u6b65\u5411\u9a6c\u63d0\u7763\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a \u201c\u63d0\u7763\u5927\u4eba\uff0c\u626c\u5dde\u77e5\u5e9c\u738b\u5c11\u537f\uff0c\u6765\u665a\u4e86\u4e00\u6b65\uff0c\u5931\u793c\u5440\uff01\u5931\u793c\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u56de\u987e\u4e86\u738b\u5927\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u4ea1\u7f8a\u8865\u7262\u5427\uff01\u8bf7\u8d35\u5e9c\u5148\u4e0b\u4e00\u9053\u4ee4\u8c15\uff0c\u628a\u6740\u5bb3\u72ac\u5b50 \u7684\u51f6\u624b\u91d1\u5c0f\u7709\u6293\u8d77\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u9a6c\u63d0\u7763\uff0c\u4ee4\u90ce\u9047\u5bb3\uff0c\u8bda\u5c5e\u4e0d\u5e78\uff01\u201d\u738b\u77e5\u5e9c\u51b3\u5fc3\u628a\u4e8b\u60c5\u625b\u4e0b\u6765\uff0c\u4ee5\u514d\u53cc\u65b9\u53d1\u751f\u68b0\u6597\uff0c\u95f9 \u51fa\u66f4\u5927\u7684\u8840\u6848\u3002\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u51f6\u6848\u53d1\u751f\u5728\u626c\u5dde\u5730\u9762\u4e0a\uff0c\u672c\u5e9c\u804c\u53f8\u6240\u5728\uff0c\u81ea\u662f\u8d23\u65e0 \u65c1\u8d37\uff0c\u4f46\u6848\u60c5\u5c1a\u672a\u660e\u6717\u4e4b\u524d\uff0c\u65e0\u6cd5\u8ba4\u5b9a\u91d1\u5c0f\u7709\u5c31\u662f\u51f6\u624b\u2026\u2026\u201d \u3000\u3000\u9a6c\u63d0\u7763\u8138\u8272\u4e00\u53d8\uff0c\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u65b0\u5c45\u4e4b\u4e2d\uff0c\u53ea\u6709\u4ed6\u4eec\u592b\u59bb\u4e24\u4eba\uff0c\u95e8\u7a97\u5bc6\u95ed\uff0c\u672a\u906d\u7834\u574f\uff0c \u72ac\u5b50\u88ab\u6740\uff0c\u4e00\u5200\u6bd5\u547d\uff0c\u65b0\u5a5a\u4e4b\u591c\uff0c\u65b0\u5a18\u5b50\u6697\u85cf\u5229\u5203\uff0c\u65e9\u6709\u6740\u5bb3\u72ac\u5b50\u4e4b\u5fc3\uff0c\u81ea\u5df2\u5b9a\u8bba\uff0c\u51f6\u5acc\u5c1a \u5728\u65b0\u623f\u4e2d\u2026\u2026\u201d \u3000\u3000\u201c\u6162\u6765\uff0c\u6162\u6765\uff0c\u201d\u738b\u77e5\u5e9c\u6253\u65ad\u4e86\u9a6c\u63d0\u7763\u7684\u8bdd\uff0c\u63a5\u9053\uff1a\u201c\u4ee4\u90ce\u548c\u91d1\u59d1\u5a18\u662f\u65e9\u5c31\u7ed3\u8bc6\u5462\uff1f\u8fd8 \u662f\u8fd1\u65e5\u4e2d\u51ed\u5a92\u5981\u4e4b\u8a00\u64ae\u5408\u6210\u5a5a\uff1f\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u91d1\u767e\u5e74\u5728\u4e00\u4e2a\u4e2d\u5e74\u9053\u4eba\u966a\u62a4\u4e0b\uff0c\u6b65\u4e0b\u53f0\u9636\uff0c\u884c\u8fd1\u4e94\u5c3a\u5904\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u738b\u5c11\u537f\u53ea\u5bf9\u91d1\u767e\u5e74\u70b9\u70b9\u5934\uff0c\u76ee\u5149\u5374\u8f6c\u5230\u90a3\u4e2d\u5e74\u9053\u4eba\u7684\u8eab\u4e0a\uff0c\u738b\u5927\u4eba\u867d\u975e\u6c5f\u6e56\u4e2d\u4eba\uff0c\u4f46\u5374 \u5f88\u4e86\u89e3\u626c\u5dde\u57ce\u5185\uff0c\u9690\u5c45\u7740\u4e0d\u5c11\u6c5f\u6e56\u9ad8\u624b\uff0c\u4e00\u4e2a\u8df3\u51fa\u4e09\u754c\u5916\u7684\u9053\u58eb\uff0c\u7adf\u7136\u4e0d\u907f\u4eba\u8033\u76ee\uff0c\u4ee5\u4fdd\u9556 \u7684\u8eab\u4efd\uff0c\u51fa\u73b0\u5728\u5b98\u5e9c\u6355\u5feb\u9762\u524d\uff0c\u4e0d\u6015\u4eba\u5bfb\u6839\u7a76\u5e95\uff0c\u5fc5\u662f\u4f4d\u6280\u827a\u5353\u7edd\uff0c\u5b64\u50b2\u81ea\u8d1f\u7684\u4eba\u7269\u3002 \u3000\u3000\u7ec6\u770b\u4ed6\u9053\u888d\u4f69\u5251\uff0c\u957f\u9aef\u5782\u80f8\uff0c\u9762\u5982\u6ee1\u6708\uff0c\u957f\u7709\u4eba\u9b13\uff0c\u786e\u4e5f\u6709\u4e00\u70b9\u98d8\u98d8\u4ed9\u98ce\u7684\u6837\u5b50\u3002 \u3000\u3000\u53ea\u542c\u91d1\u767e\u5e74\u63a5\u9053\uff1a\u201c\u5c0f\u5973\u548c\u9a6c\u516c\u5b50\u7ed3\u8bc6\u5df2\u6709\u4e24\u5e74\u4e4b\u4e45\uff0c\u5e74\u8f7b\u4eba\u60c5\u6295\u610f\u5408\uff0c\u9a6c\u516c\u5b50\u77e5\u4e66\u8fbe \u793c\uff0c\u5bcc\u5b66\u6709\u4e3a\uff0c\u91d1\u67d0\u4eba\u4e5f\u8877\u5fc3\u559c\u7231\u3002\u6240\u4ee5\uff0c\u9a6c\u63d0\u7763\u9063\u5a92\u63d0\u4eb2\uff0c\u91d1\u67d0\u5c31\u4e00\u53e3\u5141\u5a5a\uff0c\u4e14\u4e0d\u60dc\u5de8\u8d44\uff0c \u66ff\u4ed6\u4eec\u4fee\u7b51\u4e86\u8fd9\u5ea7\u4fea\u5f71\u5c0f\u7b51\uff0c\u6211\u867d\u662f\u4e2a\u4fd7\u6c14\u7684\u5546\u4eba\u2026\u2026\u201d \u3000\u3000\u738b\u77e5\u5e9c\u4e00\u62ac\u624b\uff0c\u963b\u6b62\u91d1\u767e\u5e74\u8bf4\u4e0b\u53bb\uff0c\u5374\u8f6c\u671b\u7740\u9a6c\u63d0\u7763\u9053\uff1a\u201c\u957f\u5c71\u5144\uff0c\u91d1\u767e\u5e74\u8bf4\u7684\u662f\u771f\u662f \u5047\uff1f\u201d \u3000\u3000\u201c\u771f\u7684\uff0c\u201d\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8fd9\u548c\u52a0\u5bb3\u5c0f\u513f\u7684\u4e8b\uff0c\u6709\u4f55\u5173\u8fde\u5462\uff1f\u201d \u3000\u3000\u201c\u5927\u4eba\u8fd9\u8bdd\u5c31\u6709\u70b9\u7275\u5f3a\u4e86\u2026\u2026\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u5982\u662f\u5168\u65e0\u52a8\u673a\uff0c\u91d1\u5c0f\u7709\u5982\u4f55\u4e0b\u5f97\u4e86\u624b\uff1f\u4f55 \u51b5\u662f\u65b0\u5a5a\u4e4b\u591c\uff0c\u6b63\u8be5\u7537\u6b22\u5973\u7231\u2026\u2026\u201d \u3000\u3000\u201c\u65b0\u5a5a\u4e4b\u591c\uff0c\u95e8\u7a97\u672a\u635f\uff0c\u623f\u4e2d\u53ea\u6709\u4ed6\u4eec\u4e24\u4eba\uff0c\u5c0f\u513f\u88ab\u6740\uff0c\u8840\u67d3\u7f57\u5e0f\uff0c\u201d\u9a6c\u957f\u5c71\u51b7\u51b7\u63a5\u9053\uff1a \u201c\u91d1\u5c0f\u7709\u624b\u63e1\u5229\u5203\u4e0a\uff0c\u8840\u8ff9\u5c24\u5728\uff0c\u4e8b\u8bc1\u660e\u786e\uff0c\u91d1\u5c0f\u7709\u5982\u4e0d\u662f\u51f6\u624b\uff0c\u8d35\u5e9c\u7ed9\u6211\u627e\u4e2a\u51f6\u624b\u51fa\u6765\uff1f\u201d \u3000\u3000\u201c\u957f\u5c71\u5144\uff0c\u53ef\u66fe\u52d8\u67e5\u8fc7\u65b0\u623f\uff1f\uff01\u201d\u738b\u77e5\u5e9c\u6c89\u58f0\u9053\uff1a\u201c\u95e8\u7a97\u786e\u7136\u65e0\u635f\u4e48\uff1f\u201d \u3000\u3000\u201c\u95e8\u7a97\u7531\u5185\u623f\u52a0\u62f4\uff0c\u201d\u9a6c\u957f\u5c71\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u672c\u7763\u548c\u91d1\u767e\u5e74\u540c\u65f6\u5165\u623f\u67e5\u770b\uff0c\u8fd8\u662f\u91d1\u5bb6\u62a4\u9662 \u6b66\u5e08\uff0c\u7528\u638c\u529b\u9707\u5f00\u623f\u95e8\uff0c\u6211\u4eec\u540c\u65f6\u8fdb\u5165\u623f\u4e2d\uff0c\u76ee\u7779\u5c0f\u513f\u60e8\u6b7b\u4e4b\u72b6\uff0c\u672c\u7763\u809d\u80a0\u5bf8\u65ad\uff0c\u4e0b\u4ee4\u4e2d\u519b \u53ec\u96c6\u4eba\u9a6c\uff0c\u91d1\u767e\u5e74\u4e5f\u53ec\u6765\u4e86\u91d1\u5bb6\u7684\u62a4\u9662\u6b66\u5e08\u2026\u2026\u201d \u3000\u3000\u201c\u91d1\u5458\u5916\uff0c\u201d\u738b\u77e5\u5e9c\u6253\u65ad\u4e86\u9a6c\u957f\u5c71\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u9a6c\u63d0\u7763\u8bf4\u7684\uff0c\u91d1\u5458\u5916\u5168\u90fd\u542c\u5230\u4e86\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u90fd\u542c\u5230\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u6709\u865a\u5047\u4e4b\u8bcd\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u6447\u6447\u5934\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u56de\u987e\u4e86\u8eab\u4fa7\u7684\u5211\u623f\u6587\u6848\u9053\uff1a\u201c\u5f20\u5e08\u7237\uff0c\u8bb0\u5f55\u4e0b\u6765\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\u548c\u9a6c\u63d0\u7763\u3001\u91d1\u5458\u5916\u7684\u4ea4\u8c08\u5bf9\u8bdd\uff0c\u90fd\u5df2\u5b57\u5b57\u8bb0\u660e\uff0c\u53e5\u53e5\u843d\u6848\uff0c\u201d\u5f20\u5e08\u7237\u8c26\u606d\u9053\uff1a \u201c\u5927\u4eba\u653e\u5fc3\uff0c\u4e0d\u4f1a\u6709\u4e00\u53e5\u9057\u6f0f\u3002\u201d \u3000\u3000\u9a6c\u63d0\u7763\u5fc3\u5934\u4e00\u98a4\uff0c\u5fd6\u9053\uff1a\u4e45\u95fb\u738b\u5c11\u537f\u5e72\u7ec3\u591a\u624d\uff0c\u679c\u7136\u662f\u4e2a\u5389\u5bb3\u4eba\u7269\uff0c\u4ed6\u8981\u5c0f\u5fc3\u4e00\u4e9b\u4e86\u3002 \u3000\u3000\u6709\u6b64\u4e00\u5ff5\uff0c\u5f3a\u538b\u4e0b\u4e86\u80f8\u4e2d\u7684\u60b2\u75db\u3001\u6012\u706b\uff0c\u8138\u8272\u4e5f\u53d8\u5f97\u597d\u770b\u591a\u4e86\u3002 \u3000\u3000\u201c\u91d1\u67d0\u4eba\u5c3d\u91cf\u4fdd\u6301\u4e86\u73b0\u573a\u7684\u5b8c\u6574\uff0c\u4ee5\u51ed\u5927\u4eba\u52d8\u67e5\uff0c\u8fde\u9a6c\u63d0\u7763\u548c\u91d1\u67d0\u8fdb\u5165\u65b0\u623f\u7684\u75d5\u8ff9\uff0c\u4e5f \u672a\u6253\u626b\u3002\u201d \u3000\u3000\u201c\u91d1\u5458\u5916\u679c\u7136\u662f\u5927\u6709\u89c1\u8bc6\u7684\u4eba\uff0c\u90a3\u5c31\u5e26\u672c\u5e9c\u53bb\u770b\u770b\u73b0\u573a\u5427\uff01\u201d \u3000\u3000\u8fd9\u4e24\u53e5\u8bdd\uff0c\u542c\u4f3c\u8d5e\u626c\uff0c\u4f46\u53c8\u542b\u8bbd\u523a\uff0c\u542c\u5f97\u91d1\u767e\u5e74\u8138\u4e0a\u4e00\u70ed\uff0c\u8f6c\u8eab\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u65b0\u623f\u53cc\u95e8\u7d27\u95ed\uff0c\u4e24\u4e2a\u8eab\u7740\u52b2\u88c5\u7684\u62a4\u9662\uff0c\u5206\u5de6\u53f3\u7ad9\u7acb\u4e24\u4fa7\u3002 \u3000\u3000\u91d1\u767e\u5e74\u63a8\u5f00\u623f\u95e8\uff0c\u4fa7\u8eab\u9000\u4e86\u4e00\u6b65\uff0c\u9053\uff1a\u201c\u5e9c\u53f0\u5927\u4eba\u8bf7\uff0c\u9a6c\u63d0\u7763\u8bf7\u3002\u201d \u3000\u3000\u4ed6\u5fcd\u6c14\u541e\u58f0\uff0c\u5c48\u5df1\u4ece\u4eba\uff0c\u65e8\u5728\u4fdd\u5168\u5973\u513f\u6027\u547d\uff0c\u9a6c\u63d0\u7763\u624b\u63e1\u519b\u6743\uff0c\u5b98\u9636\u4e09\u54c1\uff0c\u62e5\u6709\u6c34\u5e08\u6570 \u4e07\u4e4b\u4f17\uff0c\u7eb5\u6a2a\u4e94\u6e56\u957f\u6c5f\uff0c\u91d1\u767e\u5e74\u867d\u6709\u5de8\u5927\u7684\u8d22\u5bcc\uff0c\u4f46\u5982\u7f20\u5939\u4e0b\u53bb\uff0c\u672a\u5fc5\u80fd\u7a33\u64cd\u80dc\u5238\uff0c\u5bcc\u4e0d\u4e0e \u5b98\u6597\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u6b7b\u7684\u53c8\u662f\u9a6c\u63d0\u7763\u7684\u72ec\u5b50\uff0c\u8fd9\u7b49\u9525\u5fc3\u523a\u9aa8\u7684\u4f24\u75db\uff0c\u4e5f\u5f88\u96be\u602a\u4ed6\u80f8\u4e2d\u6012\u706b\u65e0\u540d\uff0c\u63aa\u65bd \u4e56\u5f20\uff0c\u7adf\u8981\u6740\u6389\u91d1\u5c0f\u7709\uff0c\u4e3a\u513f\u507f\u547d\u3002 \u3000\u3000\u91d1\u767e\u5e74\u76ee\u7779\u8840\u6848\u4e4b\u540e\uff0c\u5df2\u5b58\u4e0b\u5fcd\u8ba9\u4e4b\u5fc3\uff0c\u53ea\u8981\u80fd\u4fdd\u4f4f\u5973\u513f\u4e0d\u53d7\u4f24\u5bb3\uff0c\u4f59\u4e8b\u7686\u53ef\u8ba9\u6b65\u3002 \u3000\u3000\u94c1\u7fce\u62a2\u524d\u4e00\u6b65\uff0c\u5148\u884c\u8fdb\u4eba\u65b0\u623f\uff0c\u76ee\u5149\u8f6c\u52a8\uff0c\u770b\u6e05\u4e86\u65b0\u623f\u5f62\u52bf\uff0c\u624d\u8ba9\u738b\u5927\u4eba\u6b65\u5165\u65b0\u623f\u3002 \u3000\u3000\u91d1\u5c0f\u7709\u5341\u5206\u79c0\u7f8e\uff0c\u53ea\u662f\u795e\u60c5\u592a\u8fc7\u5fe7\u82e6\u54c0\u4f24\uff0c\u8138\u4e0a\u6cea\u75d5\u4ea4\u9519\uff0c\u53cc\u76ee\u8d64\u7ea2\u80bf\u80c0\uff0c\u6728\u7136\u5730\u5750\u5728 \u5e8a\u4e0a\uff0c\u8eab\u4fa7\u7ad9\u7740\u4e00\u4e2a\u9752\u8863\u5c0f\u5a62\uff0c\u4e5f\u662f\u6ee1\u8138\u6101\u82e6\uff0c\u9760\u5e8a\u5934\u4e00\u5f20\u767d\u7389\u6848\u4e0a\uff0c\u653e\u7740\u4e00\u628a\u725b\u89d2\u4f5c\u67c4\u3001 \u7cbe\u5de7\u53c8\u950b\u5229\u7684\u5315\u9996\uff0c\u5229\u5203\u4e0a\u8840\u8ff9\u72b9\u5b58\u3002 \u3000\u3000\u201c\u5927\u4eba\uff0c\u201d\u91d1\u767e\u5e74\u8ddf\u4e0a\u4e00\u6b65\uff0c\u9053\uff1a\u201c\u5c0f\u5973\u6587\u5f31\uff0c\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\uff0c\u7edd\u975e\u51f6\u624b\uff0c\u6848\u60c5\u8be1\u5947\uff0c \u8fd8\u8bf7\u5927\u4eba\u660e\u5bdf\u79cb\u6beb\uff0c\u4e3a\u5c0f\u5973\u6d17\u53bb\u5acc\u7591\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u672a\u7f6e\u53ef\u5426\u3002\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u4e00\u89c1\u91d1\u5c0f\u7709\uff0c\u5fc3\u4e2d\u5df2\u6392\u9664\u5979\u6740\u592b\u884c\u51f6\u7684\u60f3\u6cd5\uff0c\u53ea\u770b\u5979\u90a3\u79cd \u5982\u75f4\u5982\u5446\u7684\u5fe7\u82e6\u795e\u60c5\uff0c\u7edd\u4e0d\u662f\u88c5\u4f5c\u51fa\u6765\uff0c\u800c\u662f\u771f\u7684\u4f24\u5fc3\u6b32\u7edd\uff0c\u53ea\u6015\u5df2\u56e0\u54c0\u75db\u8fc7\u5ea6\uff0c\u4f24\u5230\u8eab\u4f53 \u4e86\u3002 \u3000\u3000\u94c1\u7fce\u4e24\u9053\u70af\u70af\u7684\u76ee\u5149\uff0c\u4e00\u76f4\u56db\u4e0b\u6253\u91cf\uff0c\u4e0d\u653e\u8fc7\u4efb\u4f55\u7ec6\u5fae\u7684\u5730\u65b9\uff0c\u5e0c\u671b\u627e\u51fa\u4e00\u4e9b\u53ef\u7591\u7684\u75d5 \u8ff9\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u62ff\u8d77\u767d\u7389\u6848\u4e0a\u7684\u5315\u9996\uff0c\u4ed4\u7ec6\u770b\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u597d\u4e00\u628a\u950b\u5229\u7684\u5315\u9996\u2026\u2026\u201d \u3000\u3000\u201c\u5c31\u662f\u8fd9\u628a\u5315\u9996\uff0c\u523a\u5165\u4e86\u5c0f\u513f\u7684\u5fc3\u810f\u8981\u5bb3\uff0c\u6df1\u6ca1\u53ca\u67c4\uff0c\u597d\u51f6\u72e0\u7684\u4e00\u5200\u554a\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u7684\u9a6c\u63d0\u7763\uff0c\u8ddf\u8fdb\u4e86\u65b0\u623f\u4e4b\u4e2d\u3002 \u3000\u3000\u201c\u957f\u5c71\u5144\uff0c\u80fd\u80af\u5b9a\u8fd9\u628a\u5315\u9996\u5c31\u662f\u5229\u6b7b\u4ee4\u90ce\u7684\u51f6\u5200\uff1f\u201d \u3000\u3000\u738b\u77e5\u5e9c\u8868\u73b0\u7684\u62c5\u5f53\u6c14\u52bf\uff0c\u4e00\u4e1d\u4e0d\u82df\u7684\u529e\u6848\u7cbe\u795e\uff0c\u5df2\u4f7f\u9a6c\u63d0\u7763\u5fc3\u4e2d\u6298\u670d\uff0c\u4e0d\u6562\u7a0d\u5b58\u8f7b\u5ffd\u4e4b \u5fc3\uff0c\u70b9\u5934\u5e94\u9053\uff1a\u201c\u672c\u7763\u8fdb\u5165\u65b0\u623f\u4e4b\u65f6\uff0c\u5200\u67c4\u4ecd\u63e1\u5728\u91d1\u5c0f\u7709\u7684\u624b\u4e2d\uff0c\u91d1\u59d1\u5a18\u4f0f\u5728\u5c0f\u513f\u5c38\u4f53\u4e0a\u54ed \u6ce3\u3002\u4e8b\u5b9e\u5982\u7ed8\uff0c\u5386\u5386\u5728\u76ee\uff0c\u91d1\u767e\u5e74\u4e5f\u662f\u4eb2\u76ee\u6240\u89c1\uff0c\u77e5\u5e9c\u5927\u4eba\uff0c\u8fd9\u8be5\u662f\u94c1\u8bc1\u5982\u5c71\u4e86\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u91d1\u5458\u5916\uff0c\u8fd9\u628a\u5315\u9996\u2026\u2026\u201d \u3000\u3000\u201c\u786e\u4e3a\u91d1\u67d0\u6240\u6709\uff0c\u5c0f\u5973\u7231\u5176\u8f7b\u5de7\u950b\u5229\uff0c\u5e38\u7528\u4ed6\u524a\u98df\u6c34\u679c\uff0c\u600e\u4e48\u4f1a\u51fa\u73b0\u5728\u65b0\u623f\u4e4b\u4e2d\uff0c\u91d1\u67d0 \u4ea6\u662f\u601d\u89e3\u4e0d\u900f\u2026\u2026\u201d \u3000\u3000\u201c\u91d1\u5458\u5916\uff0c\u8fdb\u5165\u65b0\u623f\u65f6\uff0c\u6b64\u5200\u662f\u5426\u4ecd\u63e1\u5728\u4ee4\u5a9b\u624b\u4e2d\uff0c\u6709\u5982\u9a6c\u63d0\u7763\u8ff0\u8bf4\u60c5\u5f62\u3002\u201d\u738b\u77e5\u5e9c\u6253 \u65ad\u4e86\u91d1\u767e\u5e74\u4e4b\u8a00\uff0c\u63a5\u9053\uff1a\u201c\u5200\u5203\u6df1\u5165\u5728\u9a6c\u516c\u5b50\u7684\u5fc3\u810f\u8981\u5bb3\uff0c\u5200\u67c4\u63e1\u5728\u4ee4\u5a9b\u624b\u4e2d\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u666f\u50cf\u786e\u5982\u63d0\u7763\u5927\u4eba\u6240\u8ff0\uff0c\u4f46\u8fd9\u4e5f\u4e0d\u80fd\u8ba4\u5b9a\uff0c\u5c0f\u5973\u5c31\u662f\u51f6\u624b\u2026\u2026\u201d \u3000\u3000\u738b\u77e5\u5e9c\u6ca1\u6709\u7406\u4f1a\u91d1\u767e\u5e74\uff0c\u5374\u56de\u987e\u5ba4\u5916\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u738b\u526f\u603b\u6355\uff0c\u5e26\u5b8b\u4ef5\u4f5c\u8fdb\u6765\u9a8c\u5c38\uff01\u5f20 \u5211\u6848\u5165\u623f\u5f55\u8bb0\u6848\u60c5\u3002\u201d \u3000\u3000\u5f55\u8bb0\u6848\u60c5\uff0c\u4e0d\u53ea\u662f\u7b14\u8bb0\u53e3\u4f9b\uff0c\u4e3e\u51e1\u73b0\u573a\u4e2d\u4eba\uff0c\u4e00\u8a00\u4e00\u884c\uff0c\u90fd\u8981\u5f55\u8bb0\u4e0b\u6765\uff0c\u7528\u4f5c\u529e\u6848\u53c2\u8003\u3002 \u3000\u3000\u9a6c\u63d0\u7763\u3001\u91d1\u767e\u5e74\u90fd\u4e0d\u662f\u597d\u60f9\u7684\u4eba\u7269\uff0c\u738b\u77e5\u5e9c\u4ea6\u4e0d\u6562\u6709\u4e1d\u6beb\u8f7b\u5ffd\u5927\u610f\u3002 \u3000\u3000\u8fd9\u4e5f\u4f7f\u5f97\u9a6c\u63d0\u7763\u548c\u91d1\u767e\u5e74\u6697\u4e2d\u5fc3\u751f\u8b66\u60d5\u3002 \u3000\u3000\u76ee\u7779\u91d1\u5c0f\u7709\u7684\u6194\u60b4\u795e\u60c5\uff0c\u738b\u77e5\u5e9c\u751a\u662f\u540c\u60c5\uff0c\u672c\u60f3\u8981\u91d1\u767e\u5e74\u5e26\u8d70\u7231\u5973\uff0c\u5148\u884c\u5ef6\u533b\u8bca\u6cbb\uff0c\u4ee5 \u514d\u54c0\u4f24\u6210\u75be\uff0c\u4f46\u53c8\u6050\u5f15\u8d77\u9a6c\u63d0\u7763\u7684\u4e0d\u5feb\uff0c\u53ea\u597d\u5fcd\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u738b\u575a\u5e26\u7740\u5b8b\u4ef5\u4f5c\u548c\u5f20\u5e08\u7237\u6b65\u5165\u65b0\u623f\uff0c\u4ef5\u4f5c\u7acb\u523b\u5c55\u5f00\u5de5\u4f5c\uff0c\u5148\u6380\u5f00\u76d6\u5728\u5c38\u4f53\u4e0a\u7684\u4e00\u6761\u7ea2\u7eeb \u68c9\u88ab\u3002 \u3000\u3000\u539f\u6765\uff0c\u9a6c\u656c\u6587\u7684\u5c38\u4f53\uff0c\u4ecd\u7136\u4ef0\u5367\u5e8a\u4e0a\uff0c\u6ca1\u6709\u79fb\u52a8\uff0c\u4e0a\u8eab\u5149\u8d64\uff0c\u53ea\u7a7f\u7740\u4e00\u6761\u9ec4\u8272\u7ef8\u4e1d\u7684\u77ed \u88e4\u3002 \u3000\u3000\u4f24\u53e3\u5728\u524d\u80f8\u5fc3\u7a9d\u4e0a\uff0c\u4f3c\u662f\u6d41\u8840\u4e0d\u591a\uff0c\u6de1\u84dd\u8272\u7684\u5e8a\u5355\u4e0a\uff0c\u53ea\u6709\u4e24\u7247\u624b\u638c\u5927\u5c0f\u7684\u8840\u8ff9\uff0c\u4f24\u53e3 \u867d\u7136\u6e05\u6670\u53ef\u89c1\uff0c\u4f46\u6d41\u8840\u5df2\u7136\u51dd\u7ed3\uff0c\u5c38\u4f53\u4ef0\u5367\uff0c\u4f3c\u5df2\u50f5\u786c\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u5b8b\u4ef5\u4f5c\uff0c\u6211\u8981\u4f60\u5f53\u82e6\u4e3b\u4e4b\u9762\uff0c\u76f4\u8bf4\u9a8c\u5c38\u60c5\u5f62\uff0c\u5de8\u7ec6\u65e0\u9057\uff0c\u4e0d\u5f97\u9690 \u533f\u3002\u201d \u3000\u3000\u201c\u5351\u804c\u9075\u547d\uff0c\u201d\u5b8b\u4ef5\u4f5c\u4fef\u8eab\u770b\u4e86\u5c38\u4f53\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5229\u5203\u7a7f\u5fc3\uff0c\u4e00\u5200\u6bd9\u547d\uff0c\u201d\u7ffb\u52a8\u5c38\u4f53\u53c8 \u770b\u4e86\u4e00\u9635\uff0c\u63a5\u9053\uff1a\u201c\u5916\u4f24\u53ea\u6b64\u4e00\u5904\u3002\u201d \u3000\u3000\u201c\u8fd8\u4f1a\u6709\u5185\u4f24\u4e48\uff1f\u201d\u91d1\u767e\u5e74\u5fc3\u4e2d\u4e00\u52a8\uff0c\u63a5\u9053\uff1a\u201c\u52b3\u8bf7\u5b8b\u5e08\u7236\u4ed4\u7ec6\u67e5\u9a8c\u4e86\uff1f\u201d \u3000\u3000\u7adf\u7136\u79f0\u4ef5\u4f5c\u4e00\u58f0\u5e08\u7236\uff0c\u8fd9\u91d1\u767e\u5e74\u5b9e\u662f\u4e00\u4f4d\u80fd\u5c48\u80fd\u4f38\u7684\u4eba\u7269\u3002 \u3000\u3000\u201c\u662f\u5426\u8fd8\u6709\u5185\u4f24\uff0c\u9700\u5f97\u4ed4\u7ec6\u67e5\u9a8c\uff0c\u4e0d\u662f\u4e00\u773c\u80fd\u591f\u8fa8\u660e\uff0c\u201d\u5b8b\u4ef5\u4f5c\u9053\uff1a\u201c\u4e8b\u5173\u4eba\u547d\u5927\u4e8b\uff0c \u4ef5\u4f5c\u4e0d\u6562\u5984\u8a00\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u628a\u7389\u6848\u4e0a\u7684\u5315\u9996\uff0c\u9012\u4e86\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u67e5\u9a8c\u4e00\u4e0b\uff0c\u8fd9\u628a\u5315\u9996\uff0c\u662f\u5426\u5c31\u662f\u51f6\u5200\uff1f\u201d \u3000\u3000\u5b8b\u4ef5\u4f5c\u63a5\u8fc7\u5315\u9996\uff0c\u4f38\u91cf\u8fc7\u540e\uff0c\u624b\u6309\u4f24\u53e3\uff0c\u4f5c\u4e86\u4e00\u756a\u6bd4\u8bd5\uff0c\u9053\uff1a\u201c\u4f24\u53e3\u548c\u5200\u578b\u543b\u5408\uff0c\u6b63\u662f \u6740\u4eba\u51f6\u5200\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u63a5\u8fc7\u51f6\u5200\uff0c\u4ea4\u7ed9\u738b\u575a\uff0c\u9053\uff1a\u201c\u5b8b\u4ef5\u4f5c\uff0c\u4f60\u4f30\u7b97\u4e00\u4e0b\uff0c\u9a6c\u516c\u5b50\u6b7b\u4ea1\u6709\u591a\u4e45\u4e86\uff1f\u4f55\u4ee5 \u6d41\u8840\u4e0d\u591a\u3002\u201d \u3000\u3000\u8f6c\u52a8\u4e0b\u5c38\u4f53\u624b\u6307\uff0c\u53c8\u770b\u770b\u51dd\u7ed3\u8840\u8272\uff0c\u5b8b\u4ef5\u4f5c\u9053\uff1a\u201c\u9a6c\u516c\u5b50\u88ab\u6740\u65f6\u95f4\uff0c\u5e94\u8be5\u662f\u56db\u66f4\u548c\u4e94\u66f4 \u76f8\u4ea4\u65f6\u5206\uff0c\u5200\u523a\u5fc3\u810f\u8981\u5bb3\uff0c\u6b63\u662f\u884c\u8840\u56de\u805a\u4e4b\u5904\uff0c\u6d41\u8840\u4e0d\u591a\uff0c\u662f\u56e0\u4e3a\u9a6c\u516c\u5b50\u6ca1\u6709\u6323\u624e\uff0c\u5200\u53c8\u672a \u53ca\u65f6\u62d4\u51fa\uff0c\u51fa\u8840\u5927\u90e8\u6c89\u4eba\u5185\u8179\u3002\u201d \u3000\u3000\u201c\u672c\u7763\u8eab\u7ecf\u767e\u6218\uff0c\u201d\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u770b\u8fc7\u4e0d\u5c11\u88ab\u6740\u5bb3\u7684\u4eba\uff0c\u4e00\u4e2a\u8eab\u53d7\u91cd\u521b\u5de8\u75bc\u7684\u4eba\uff0c\u5728\u90a3 \u77ac\u606f\u4e4b\u95f4\uff0c\u5927\u90fd\u4f1a\u5168\u529b\u53cd\u51fb\u6216\u5954\u9003\uff0c\u600e\u4e48\u4f1a\u4e0d\u4f5c\u6323\u624e\uff1f\u9664\u975e\u2026\u2026\u201d\u7a81\u7136\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u201c\u9664\u975e\u90a3\u4eba\u8eab\u4e0a\u53d7\u4e86\u7981\u5236\uff0c\u5931\u53bb\u6323\u624e\u7684\u80fd\u529b\uff0c\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u8fd9\u8fd8\u5f97\u5927\u4eba\u4e0b\u4ee4\u4ef5\u4f5c\uff0c\u8be6 \u4f5c\u9a8c\u660e\uff0c\u9a6c\u516c\u5b50\u662f\u5426\u5df2\u5148\u53d7\u7981\u5236\uff0c\u518d\u906d\u6740\u5bb3\uff1f\u201d \u3000\u3000\u8fd9\u5c31\u4e0d\u662f\u6ee1\u8179\u7ecf\u4f26\u3001\u5b66\u95ee\u7684\u738b\u77e5\u5e9c\u6240\u80fd\u5224\u65ad\u4e86\uff0c\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u94c1\u6355\u5934\uff0c\u4f60\u7684\u770b\u6cd5 \u5462\uff1f\u201d \u3000\u3000\u94c1\u7fce\u884c\u8fd1\u6728\u69bb\uff0c\u4ed4\u7ec6\u5730\u6253\u91cf\u5c38\u4f53\u4e00\u9635\uff0c\u9053\uff1a\u201c\u91d1\u5e9c\u62a4\u9662\u4e2d\u4e0d\u6cdb\u9ad8\u624b\uff0c\u5929\u67ab\u9053\u957f\u66f4\u662f\u5251\u672f\u3001 \u70b9\u7a74\u540d\u5bb6\uff0c\u4f55\u4e0d\u8bf7\u4ed6\u548c\u94c1\u67d0\u4f1a\u540c\u52d8\u67e5\uff0c\u4ee5\u514d\u5931\u4e4b\u504f\u9887\u3002\u201d \u3000\u3000\u201c\u94c1\u603b\u6355\u5934\u5982\u6b64\u770b\u91cd\u5929\u67ab\u9053\u957f\uff0c\u91d1\u67d0\u81ea\u5f53\u4ece\u547d\u3002\u201d\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u5feb\u8bf7\u5929\u67ab\u9053\u957f\u8fdb \u6765\u3002\u201d \u3000\u3000\u5176\u5b9e\uff0c\u5929\u67ab\u9053\u957f\u5c31\u5728\u65b0\u623f\u5916\u9762\uff0c\u738b\u575a\u548c\u4f55\u5927\u5149\u963b\u6b62\u9a6c\u63d0\u7763\u56db\u4e2a\u6267\u5200\u4ece\u536b\u5165\u5185\uff0c\u4e5f\u963b\u6b62\u4e86 \u91d1\u767e\u5e74\u7684\u62a4\u9662\u6b66\u5e08\u8fdb\u5165\u65b0\u623f\u3002\u4e00\u542c\u53ec\u5524\uff0c\u7acb\u523b\u6b65\u5165\uff0c\u5148\u5bf9\u77e5\u5e9c\u3001\u9a6c\u63d0\u7763\u3001\u91d1\u767e\u5e74\u7acb\u638c\u4e3a\u793c\uff0c \u624d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u94c1\u603b\u6355\u5934\uff0c\u8fd8\u8bb0\u5f97\u5bd2\u5c71\u65e7\u53cb\u5426\uff1f\u201d \u3000\u3000\u201c\u8a00\u91cd\u4e86\uff0c\u4e00\u4eba\u516c\u95e8\uff0c\u8eab\u4e0d\u7531\u5df1\uff0c\u8fde\u6545\u4eba\u65e7\u53cb\uff0c\u90fd\u758f\u4e8e\u63a2\u671b\uff0c\u9053\u5144\uff0c\u522b\u6765\u65e0\u6059\u4e4e\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u8033\u76ee\u7075\u654f\uff0c\u4eba\u5728\u5ead\u9662\u4e2d\uff0c\u5df2\u542c\u5230\u4ed6\u4eec\u5728\u65b0\u623f\u4e2d\u7684\u4ea4\u8c08\uff0c\u6b65\u5165\u65b0\u623f\u540e\uff0c\u76f4\u8d8b\u69bb\u524d\uff0c \u53e3\u4e2d\u5e94\u9053\uff1a\u201c\u6258\u94c1\u603b\u6355\u5934\u4e4b\u798f\uff0c\u8d2b\u9053\u65e0\u75c5\u65e0\u6059\uff0c\u6d3b\u5f97\u5012\u4e5f\u900d\u9065\u3002\u201d \u3000\u3000\u201c\u9053\u5144\u50b2\u5578\u5c11\u6797\uff0c\u8fc7\u7684\u662f\u6e05\u98ce\u660e\u6708\u7684\u795e\u4ed9\u751f\u6d3b\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u6b64\u756a\u91cd\u5c65\u51e1\u5c18\u2026\u2026\u201d \u3000\u3000\u201c\u8001\u53cb\u53d9\u65e7\uff0c\u665a\u70b9\u4e0d\u8fdf\uff0c\u773c\u4e0b\u6700\u91cd\u8981\u7684\u6551\u4eba\u8981\u7d27\u3002\u201d\u5929\u67ab\u9053\u957f\u53cc\u76ee\u70af\u70af\u903c\u6ce8\u5728\u94c1\u7fce\u7684\u8138 \u4e0a\uff0c\u9053\uff1a\u201c\u4ee5\u94c1\u603b\u6355\u5934\u76ee\u529b\u4e4b\u5f3a\uff0c\u65e9\u8be5\u770b\u51fa\u91d1\u59d1\u5a18\u56e0\u54c0\u75db\u592a\u6df1\uff0c\u5df2\u4f24\u5230\u4e2d\u5143\uff0c\u518d\u4e0d\u53ca\u65f6\u65bd\u6551\uff0c \u53ea\u6050\u53c8\u589e\u52a0\u4e00\u6869\u547d\u6848\u4e86\u3002\u201d \u3000\u3000\u201c\u77e5\u5e9c\u5927\u4eba\uff0c\u201d\u94c1\u7fce\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u91d1\u59d1\u5a18\u5a07\u5f31\u4e4b\u8eaf\uff0c\u4e0d\u5b9c\u518d\u62d6\uff0c\u786e\u9700\u8981\u53ca\u65f6\u6551\u6cbb\u4e86\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u70b9\u70b9\u5934\uff0c\u8f6c\u671b\u7740\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u9a6c\u63d0\u7763\u7684\u770b\u6cd5\u5462\uff1f\u201d \u3000\u3000\u201c\u5c0f\u513f\u7684\u547d\u6848\uff0c\u5df2\u7531\u8d35\u5e9c\u63a5\u4e0b\uff0c\u672c\u7763\u4e0d\u613f\u518d\u4f5c\u5e72\u6270\u3002\u201d\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u5982\u4f55\u5904\u7f6e\u91d1\u6c0f\u7236\u5973\uff0c \u672c\u7763\u4e5f\u4e0d\u613f\u518d\u4f5c\u8fc7\u95ee\uff0c\u6211\u8981\u7684\u662f\u6740\u5bb3\u5c0f\u513f\u7684\u51f6\u624b\uff0c\u8d35\u5e9c\u53ea\u8981\u7ed9\u672c\u7763\u4e00\u4e2a\u7834\u6848\u7684\u9650\u671f\uff0c\u6211\u7acb\u523b \u64a4\u8d70\u4eba\u9a6c\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u6697\u6697\u5401\u4e00\u53e3\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u4e09\u4e2a\u6708\u672c\u5e9c\u5f53\u5411\u9a6c\u5144\u6709\u4e2a\u4ea4\u4ee3\uff01\u201d \u3000\u3000\u201c\u4e09\u4e2a\u6708\u7684\u9650\u671f\uff0c\u867d\u7136\u957f\u4e86\u4e00\u4e9b\uff0c\u4f46\u77e5\u5e9c\u5927\u4eba\u5df2\u5f00\u4e86\u53e3\uff0c\u672c\u7763\u53ea\u597d\u4ece\u547d\u4e86\uff0c\u957f\u5c71\u5c31\u5728\u63d0 \u7763\u5e9c\u4e2d\u5019\u4f20\uff0c\u544a\u8f9e\u3002\u201d\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u8fd9\u4e00\u9635\u51b7\u9759\u7684\u89c2\u5bdf\uff0c\u9a6c\u957f\u5c71\u4e5f\u770b\u51fa\u4e86\u91d1\u5c0f\u7709\u4e0d\u662f\u51f6\u624b\uff0c\u8fd9\u4ef6\u6848\u5b50\uff0c\u5c31\u663e\u5f97\u5341\u5206 \u7684\u590d\u6742\u4e86\uff0c\u8981\u7834\u6848\u53ea\u6050\u8981\u5927\u8d39\u5468\u6298\u3002\u738b\u77e5\u5e9c\u63a5\u4e86\u6848\u5b50\uff0c\u4f55\u4e0d\u6765\u4e00\u4e2a\u987a\u6c34\u63a8\u821f\uff0c\u4e00\u80a1\u8111\u5168\u63a8\u5728 \u738b\u77e5\u5e9c\u7684\u5934\u4e0a\uff0c\u65e2\u53ef\u501f\u673a\u4e0b\u53f0\uff0c\u4ea6\u53ef\u4ee5\u82e6\u4e3b\u7684\u8eab\u4efd\u50ac\u903c\u7834\u6848\uff0c\u9a6c\u63d0\u7763\u5fc3\u4e2d\u660e\u767d\uff0c\u738b\u5c11\u537f\u662f\u4e2a \u80fd\u540f\uff0c\u624b\u4e0b\u7684\u6355\u5934\u90fd\u662f\u9ad8\u624b\uff0c\u8bba\u4fa6\u529e\u5211\u6848\u80fd\u529b\u4e4b\u5f3a\uff0c\u53ef\u4e0d\u662f\u4ed6\u7edf\u7387\u7684\u6570\u4e07\u7cbe\u9510\u6c34\u5e08\u80fd\u53ca\u3002 \u3000\u3000\u9a6c\u63d0\u7763\u79bb\u53bb\uff0c\u4e5f\u5e26\u8d70\u4e86\u968f\u540c\u524d\u6765\u7684\u9a81\u5200\u624b\u548c\u6c34\u5e08\u5175\u52c7\u3002 \u3000\u3000\u539f\u672c\u5251\u62d4\u5f29\u5f20\u7684\u5c40\u9762\uff0c\u5b8c\u5168\u677e\u5f1b\u4e0b\u6765\uff0c\u4f46\u738b\u77e5\u5e9c\u7684\u5fc3\u60c5\uff0c\u53cd\u800c\u7ef7\u7684\u66f4\u7d27\u4e86\u3002\u8fd9\u756a\u625b\u4e0b\u4e86 \u7834\u6848\u7684\u627f\u8bfa\uff0c\u65e0\u5f02\u662f\u81ea\u627e\u9ebb\u70e6\uff0c\u9a6c\u63d0\u7763\u4e0d\u662f\u597d\u60f9\u4eba\u7269\uff0c\u4e09\u4e2a\u6708\u5982\u662f\u65e0\u6cd5\u7834\u6848\uff0c\u522b\u8bf4\u9752\u4e91\u76f4\u4e0a\uff0c \u8c03\u804c\u4eac\u5b98\uff0c\u80fd\u4e0d\u80fd\u79bb\u5f00\u626c\u5dde\u5730\u9762\uff0c\u4e5f\u6839\u96be\u4f30\u7b97\u4e86\uff1f \u3000\u3000\u738b\u77e5\u5e9c\u60f3\u5fc3\u4e8b\uff0c\u60f3\u7684\u5982\u9189\u5982\u75f4\uff0c\u94c1\u7fce\u4e0d\u4fbf\u60ca\u6270\uff0c\u56de\u987e\u4e86\u5f20\u5e08\u7237\u4e00\u773c\uff0c\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u9053\uff1a \u201c\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u94c1\u5144\uff0c\u5927\u4eba\u65e9\u5df2\u540c\u610f\u4e86\u6551\u4eba\u8981\u7d27\u3002\u201d\u6709\u4e86\u5f20\u5e08\u7237\u8fd9\u53e5\u8bdd\uff0c\u94c1\u7fce\u7acb\u523b\u51fa\u624b\uff0c\u6293\u4f4f\u4e86\u91d1\u5c0f \u7709\u7684\u53f3\u8155\uff0c\u7389\u8155\u5165\u63e1\uff0c\u94c1\u7fce\u5fc3\u5934\u4e00\u51c9\uff0c\u6562\u60c5\u91d1\u59d1\u5a18\u5df2\u53f3\u8155\u51b0\u51b7\u3002 \u3000\u3000\u603b\u7b97\u94c1\u7fce\u662f\u7ecf\u8fc7\u5927\u98ce\u5927\u6d6a\u7684\u4eba\uff0c\u6c89\u5f97\u4f4f\u6c14\uff0c\u6ca1\u6709\u5931\u58f0\u800c\u53eb\uff0c\u4f46\u5185\u5fc3\u4e2d\u5374\u5341\u5206\u60f6\u6050\uff0c\u5982\u82e5 \u91d1\u5c0f\u7709\u518d\u6b7b\u4e8e\u65b0\u623f\u4e4b\u4e2d\uff0c\u8fd9\u4ef6\u547d\u6848\uff0c\u53c8\u6dfb\u65b0\u6101\uff0c\u4ee5\u91d1\u767e\u5e74\u7231\u5973\u4e4b\u6df1\uff0c\u8d22\u52bf\u4e4b\u96c4\uff0c\u5c82\u80af\u5584\u4e8e\u7f62 \u4f11\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u4e5f\u770b\u51fa\u4e86\u82d7\u5934\uff0c\u6697\u6697\u5401\u4e00\u53e3\u6c14\uff0c\u4f38\u51fa\u53f3\u624b\u63e1\u4f4f\u4e86\u91d1\u5c0f\u7709\u7684\u5de6\u8155\uff0c\u4ed6\u4e86\u89e3\u91d1\u5c0f\u7709 \u662f\u91d1\u767e\u5e74\u7684\u638c\u4e0a\u660e\u73e0\uff0c\u5bc4\u7231\u4e4b\u4fdd\uff0c\u5c24\u8fc7\u81ea\u5df1\u4e8e\u751f\u6b7b\uff0c\u5982\u82e5\u91d1\u5c0f\u7709\u5df2\u4e0d\u5e78\u6b7b\u4ea1\uff0c\u7edd\u4e0d\u5b9c\u5728\u8fd9\u6837 \u4e00\u4e2a\u573a\u5408\u8bf4\u51fa\u6765\uff0c\u6fc0\u52a8\u7edd\u671b\u4e4b\u4e0b\uff0c\u5b9e\u96be\u9884\u6599\u91d1\u767e\u5e74\u4f1a\u4f5c\u51fa\u4ec0\u4e48\u60ca\u5929\u52a8\u5730\u3001\u5927\u9006\u4e0d\u9053\u7684\u4e8b\u3002 \u3000\u3000\u8fd9\u4e24\u5927\u6c5f\u6e56\u9ad8\u624b\u5df2\u6709\u4e86\u5171\u540c\u7684\u8ba4\u77e5\uff0c\u5bf9\u671b\u4e00\u773c\uff0c\u7f13\u7f13\u8fd0\u6c14\u3001\u5faa\u7740\u91d1\u5c0f\u7709\u8109\u7a74\u653b\u5165\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u5df2\u7531\u6c89\u601d\u4e2d\u6e05\u9192\u8fc7\u6765\uff0c\u76ee\u5149\u7acb\u523b\u6295\u6ce8\u5728\u91d1\u5c0f\u7709\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u4ed6\u8bfb\u4e66\u4e07\u5377\uff0c\u6781\u7aef\u806a\u660e\uff0c\u4e00\u770b\u5230\u94c1\u7fce\u548c\u5929\u67ab\u9053\u957f\u51dd\u91cd\u795e\u8272\uff0c\u5c0f\u5fc3\u6a21\u6837\uff0c\u5df2\u5224\u65ad\u51fa\u91d1\u5c0f\u7709 \u4f24\u52bf\u5371\u6076\uff0c\u5fc3\u4e2d\u4e5f\u6709\u70b9\u7126\u8651\u4e86\u3002\u8fd9\u91d1\u5c0f\u7709\u5982\u662f\u4e0d\u5e78\u4f24\u91cd\u6b7b\u4ea1\uff0c\u91d1\u767e\u5e74\u53ef\u80fd\u8fc1\u6012\u4ed6\u4eba\uff0c\u4e0d\u80af\u5408 \u4f5c\uff0c\u8fd9\u693f\u547d\u6848\u5c31\u8d8a\u53d1\u96be\u529e\u4e86\u3002 \u3000\u3000\u91d1\u5c0f\u7709\u5728\u4e24\u5927\u9ad8\u624b\u771f\u6c14\u50ac\u52a8\u4e86\u9759\u6ede\u7684\u884c\u8840\u4e4b\u540e\uff0c\u5f00\u59cb\u4e86\u53cd\u5e94\uff0c\u5a07\u8eaf\u5fae\u5fae\u98a4\u52a8\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u6697\u5438\u4e00\u53e3\u771f\u6c14\uff0c\u4e00\u638c\u62cd\u5728\u91d1\u59d1\u5a18\u80cc\u5fc3\u8981\u7a74\u4e4b\u4e0a\u3002 \u3000\u3000\u91d1\u5c0f\u7709\u6a31\u53e3\u542f\u52a8\u3000\u8fde\u5410\u4e86\u4e09\u53e3\u6de4\u8840\uff0c\u624d\u7f13\u8fc7\u4e00\u53e3\u6c14\uff0c\u7741\u5f00\u773c\u775b\u53eb\u9053\uff1a\u201c\u7239\u7239\u2026\u2026\u201d \u3000\u3000\u201c\u4e58\u5973\u513f\uff0c\u4f60\u5413\u715e\u8001\u7236\u4e86\u3002\u201d\u91d1\u767e\u5e74\u5feb\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u5feb\u8eba\u4e0b\uff0c\u4e0d\u8981\u52a8\u2026\u2026\u201d \u3000\u3000\u201c\u4e1c\u4e3b\uff0c\u6276\u5c0f\u59d0\u4e0b\u5e8a\u8d70\u52a8\u51e0\u6b65\uff0c\u8ba9\u5979\u65e9\u4e9b\u6d3b\u5f00\u884c\u8840\uff0c\u201d\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u518d\u9001\u5c0f\u59d0\u56de\u623f\u4f11 \u606f\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u7a81\u7136\u6d41\u4e0b\u6cea\u6765\uff0c\u9053\uff1a\u201c\u7239\u7239\uff0c\u6211\u8981\u4e3a\u656c\u6587\u62a5\u4ec7\uff01\u201d \u3000\u3000\u201c\u7239\u7b54\u5e94\u4f60\uff0c\u94f6\u5b50\u5316\u6210\u6c34\uff0c\u6d41\u5230\u5317\u4eac\u57ce\uff0c\u6211\u4e5f\u8981\u628a\u51f6\u624b\u8ffd\u51fa\u6765\uff0c\u4e3a\u4f60\u51fa\u4e86\u8fd9\u53e3\u6c14\u3002\u201d \u3000\u3000\u8bdd\u8bf4\u7684\u6709\u70b9\u72c2\uff0c\u4f46\u4e5f\u5ba3\u793a\u4e86\u91d1\u767e\u5e74\u7684\u51b3\u5fc3\uff0c\u4ed6\u662f\u626c\u5dde\u5de8\u5bcc\u4e4b\u4e00\uff0c\u62da\u4e0a\u5168\u90e8\u5bb6\u5f53\u5e72\uff0c\u53ef\u662f \u4e00\u80a1\u60ca\u5929\u52a8\u5730\u7684\u529b\u91cf\uff0c\u7b97\u7b97\u770b\uff0c\u8fd9\u7b14\u4ebf\u4e07\u8d22\u5bcc\uff0c\u80fd\u8bf7\u51fa\u591a\u5c11\u6c5f\u6e56\u9ad8\u4eba\u53c2\u4e0e\u7f09\u51f6\uff1f \u3000\u3000\u738b\u5c11\u537f\u6697\u6697\u5fd6\u9053\uff1a\u770b\u6765\uff0c\u8fd9\u693f\u547d\u6848\uff0c\u786e\u548c\u91d1\u767e\u5e74\u65e0\u5173\uff0c\u539f\u6765\u5fc3\u4e2d\u7684\u4e00\u70b9\u6000\u7591\uff0c\u987f\u7136\u6d88\u9000\uff0c \u4f46\u4e5f\u66f4\u663e\u5f97\u8fd9\u4ef6\u547d\u6848\u7684\u8be1\u79d8\u5947\u5e7b\uff0c\u4e0b\u624b\u65e0\u5904\u4e86\u3002 \u3000\u3000\u738b\u5927\u4eba\u8fd9\u6837\u60f3\uff0c\u4e13\u53f8\u5211\u6848\u7684\u5e08\u7237\u5f20\u5b9d\u5584\u4e5f\u5728\u60f3\uff0c\u91d1\u767e\u5e74\u5acc\u7591\u6d88\u51cf\uff0c\u8fd9\u4ef6\u547d\u6848\u5c31\u8d8a\u53d1\u7684\u68d8 \u624b\u4e86\u3002 \u3000\u3000\u91d1\u767e\u5e74\u5907\u4e86\u9152\u5e2d\uff0c\u8bf7\u4eba\u5165\u5e2d\uff0c\u738b\u77e5\u5e9c\u60f3\u5230\u8fde\u5348\u9910\u8fd8\u672a\u8fdb\u8fc7\uff0c\u8179\u4e2d\u6b63\u611f\u9965\u997f\uff0c\u4e5f\u4e0d\u63a8\u8f9e\uff0c \u7acb\u523b\u5165\u5e2d\uff0c\u91d1\u767e\u5e74\u60f3\u8d76\u6765\u966a\u5ba2\uff0c\u4e5f\u7559\u4e0b\u4e86\u5929\u67ab\u9053\u957f\u3002 \u3000\u3000\u9152\u8fc7\u4e09\u5de1\uff0c\u83dc\u4e0a\u4e94\u5473\uff0c\u738b\u77e5\u5e9c\u653e\u4e0b\u7b77\u5b50\u9053\uff1a\u201c\u547d\u6848\u5947\u8be1\uff0c\u91d1\u5458\u5916\u548c\u5929\u67ab\u9053\u957f\u6709\u4f55\u9ad8\u89c1\u6559 \u6211\uff1f\u201d \u3000\u3000\u8bdd\u867d\u8bf4\u7684\u723d\u6c14\uff0c\u4f46\u5374\u662f\u5355\u5200\u76f4\u5165\uff0c\u4e00\u9488\u89c1\u8840\u7684\u95ee\u6cd5\uff0c\u5b9e\u4e0d\u4fbf\u63a8\u6258\uff0c\u53ea\u80fd\u636e\u5b9e\u56de\u7b54\u3002 \u3000\u3000\u201c\u767e\u5e74\u548c\u51e0\u4f4d\u9ad8\u660e\u7684\u6b66\u5e08\u8c08\u8fc7\uff0c\u4eba\u4e0d\u5165\u65b0\u623f\uff0c\u53ea\u6709\u5728\u5ba4\u5916\u98de\u5200\u2026\u2026\u3002\u201d \u3000\u3000\u201c\u98de\u5200\u7a7f\u7a97\u800c\u5165\u2026\u2026\u201d\u94c1\u7fce\u9053\uff1a\u201c\u7cca\u7a97\u7684\u767d\u7eeb\u4e0a\u5e94\u8be5\u7559\u6709\u5200\u75d5\uff0c\u4f46\u767d\u7eeb\u5b8c\u597d\uff0c\u4e0d\u89c1\u7834\u635f\uff0c \u94c1\u67d0\u4ed4\u7ec6\u67e5\u8fc7\uff0c\u4e5f\u4e0d\u89c1\u7834\u635f\u7684\u75d5\u8ff9\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u5982\u679c\u6362\u4e0a\u4e00\u6574\u5757\u65b0\u7684\u767d\u7eeb\u5462\uff0c\u5c31\u4e0d\u89c1\u75d5\u8ff9\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5012\u4e5f\u53ef\u80fd\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u767d\u7eeb\u662f\u7531\u5185\u5ba4\u88f1\u5728\u7a97\u5b50\u4e0a\uff0c\u9664\u975e\u6709\u4eba\u5728\u5ba4\u5185\u52a8\u624b\u6362\u88f1\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u4e0d\u8bb2\u8bdd\u4e86\uff0c\u4ed6\u7ec6\u60f3\u5386\u7ecf\u8fc7\u7a0b\uff0c\u4e0d\u53ef\u80fd\u7559\u7ed9\u4eba\u88ab\u6574\u7a97\u6237\u7684\u7a7a\u95f4\uff1f\u96be\u9053\u8fd9\u4e16\u4e0a\u771f\u6709\u4e00 \u79cd\u795e\u79d8\u7684\u529b\u91cf\uff0c\u80fd\u64cd\u63a7\u4eba\u7684\u795e\u5fd7\uff0c\u4f7f\u5c0f\u7709\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u4e0b\u624b\u6740\u4e86\u9a6c\u656c\u6587\u2026\u2026\u5fc3\u4e2d\u987f\u751f\u8b66\u60ca\uff0c \u4e0d\u6562\u518d\u6df1\u60f3\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u63a5\u89e6\u8fc7\u4e0d\u5c11\u6c5f\u6e56\u5947\u4eba\uff0c\u6df1\u77e5\u98ce\u5c18\u4e2d\u606f\u9690\u4e86\u4e0d\u5c11\u9ad8\u624b\uff0c\u5177\u6709\u4e86\u5e38\u4eba\u65e0\u6cd5\u60f3\u50cf\u7684\u5947\u5f02\u80fd\u529b\u3002 \u3000\u3000\u201c\u5927\u4eba\uff0c\u201d\u4e00\u76f4\u6ca1\u6709\u5f00\u53e3\u7684\u5211\u6848\u5f20\u5e08\u7237\uff0c\u7a81\u7136\u63a5\u4e86\u8bdd\uff0c\u9053\uff1a\u201c\u547d\u6848\u8be1\u5f02\uff0c\u4e3a\u5b9d\u5584\u5c31\u4efb\u5211 \u6848\u4ee5\u6765\uff0c\u4ece\u6240\u672a\u89c1\u3002\u4f46\u6c34\u7531\u6e90\u6765\uff0c\u6811\u4ece\u6839\u8d77\uff0c\u53ea\u6050\u6e90\u6d41\u6df1\u957f\uff0c\u8d85\u51fa\u6211\u626c\u5dde\u4e00\u5e9c\u7ba1\u8f96\u4e4b\u5730\uff0c\u5c5e \u4e0b\u6597\u80c6\u5efa\u8bae\u7ea6\u8bf7\u4e13\u529e\u5927\u6848\u7684\u6c5f\u5357\u5211\u6355\u5206\u53f8\u534f\u52a9\u7f09\u51f6\uff0c\u4ed6\u4eec\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u4eba\u624b\u4f17\u591a\uff0c\u518d\u7531\u5211\u90e8\u6c5f \u5357\u5206\u53f8\u4ecb\u4eba\uff0c\u4e5f\u53ef\u5206\u62c5\u53bb\u626c\u5dde\u5e9c\u4e0d\u5c11\u8d23\u4efb\uff01\u201d \u3000\u3000\u201c\u5bf9\uff01\u4e45\u95fb\u6c5f\u5357\u5211\u90e8\u5206\u53f8\u793e\u671b\u6708\uff0c\u6280\u827a\u7cbe\u6e5b\uff0c\u529e\u6848\u80fd\u529b\u5947\u5f3a\uff0c\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u5b9d\u5584\uff0c\u4f60 \u7acb\u523b\u529e\u4e2a\u5448\u6587\uff0c\u7531\u738b\u575a\u526f\u603b\u6355\u5934\uff0c\u4eb2\u81ea\u8dd1\u8d75\u91d1\u9675\uff0c\u9080\u8bf7\u675c\u53f8\u4e3b\u4eb2\u81ea\u4e0b\u4e00\u8d9f\u626c\u5dde\u3002\u201d \u3000\u3000\u5f20\u6587\u6848\u79bb\u5f00\u9910\u684c\uff0c\u8d77\u8349\u5448\u6587\uff0c\u8d70\u7b14\u5982\u98de\uff0c\u7247\u523b\u800c\u5c31\uff0c\u738b\u77e5\u5e9c\u770b\u5f97\u5f88\u5feb\uff0c\u4e00\u63a0\u4e4b\u540e\uff0c\u70b9\u70b9 \u5934\u4ea4\u8fd8\u5f20\u5b9d\u5584\u3002 \u3000\u3000\u5448\u6587\u4eba\u5c01\uff0c\u738b\u575a\u5df2\u5728\u4fa7\u5019\u547d\uff0c\u63a5\u8fc7\u5982\u98de\u800c\u53bb\u3002 \u3000\u3000\u91d1\u767e\u5e74\u51b7\u773c\u65c1\u89c2\uff0c\u770b\u7684\u5927\u611f\u656c\u670d\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u4e45\u95fb\u738b\u5c11\u537f\u662f\u4e2a\u80fd\u540f\uff0c\u628a\u8fd9\u4e2a\u5929\u4e0b\u6700\u96be\u6cbb \u7406\u7684\u626c\u5dde\u5e9c\uff0c\u6cbb\u7406\u5f97\u4e95\u4e95\u6709\u6761\uff0c\u53ea\u770b\u8fd9\u4e9b\u6587\u6848\u73ed\u5934\uff0c\u884c\u52a8\u4e4b\u8fc5\u5feb\uff0c\u5c31\u8db3\u4ee5\u4ee4\u4eba\u4f69\u670d\u4e86\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u7a81\u7136\u5e72\u4e86\u9762\u524d\u7684\u9152\u676f\uff0c\u7b11\u9053\uff1a\u201c\u5f3a\u5c06\u624b\u4e0b\u65e0\u5f31\u5175\uff0c\u738b\u5927\u4eba\u662f\u80fd\u540f\uff0c\u5c5e\u4e0b\u4e5f\u90fd\u662f \u9ad8\u624b\uff0c\u5982\u80fd\u5b98\u5c45\u8981\u6d25\uff0c\u7406\u5929\u4e0b\u653f\u4e8b\uff0c\u5b9e\u662f\u5929\u4e0b\u767e\u59d3\u4e4b\u798f\u4e86\u3002\u201d \u3000\u3000\u201c\u9053\u5144\uff0c\u4e0d\u6562\u76f8\u7792\uff0c\u5927\u4eba\u7684\u77e5\u5e9c\u4efb\u671f\u5df2\u6ee1\uff0c\u5347\u4efb\u4eac\u5b98\uff0c\u672c\u5df2\u5728\u671b\uff0c\u4f46\u8fd9\u4ef6\u547d\u6848\u4e0d\u7834\uff0c\u9752 \u4e91\u4e4b\u8def\uff0c\u6050\u5c06\u4e3a\u8fd9\u4ef6\u547d\u6848\u5835\u585e\u4e86\uff0c\u9053\u5144\u65e2\u6709\u60dc\u624d\u4e4b\u5fc3\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u8fd8\u671b\u5927\u4eba\u5927\u529b\u6210\u5168\u3002\u201d \u3000\u3000\u7531\u94c1\u7fce\u63a8\u5d07\u5907\u81f3\u7684\u8bed\u6c14\u4e2d\uff0c\u738b\u77e5\u5e9c\u5df2\u611f\u89c9\u5230\u8fd9\u5929\u67ab\u9053\u957f\u662f\u4e00\u4f4d\u6b66\u6797\u4e2d\u6781\u53d7\u656c\u91cd\u7684\u9ad8\u4eba\uff0c \u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u65f6\u4e5f\uff0c\u8fd0\u4e5f\uff0c\u547d\u4e5f\uff0c\u738b\u67d0\u4eba\u7eb5\u6709\u4e3a\u4e07\u6c11\u9020\u798f\u4e4b\u5fc3\uff0c\u4f46\u5929\u610f\u4e0d\u4ece\u4eba\u613f\uff0c\u4e5f\u662f\u65e0 \u53ef\u5948\u4f55\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u8d2b\u9053\u4e5f\u4ed4\u7ec6\u5730\u770b\u8fc7\u73b0\u573a\uff0c\u8fd9\u4ef6\u547d\u6848\u867d\u7136\u770b\u4e0a\u53bb\u8be1\u5f02\u96be\u6d4b\uff0c\u4f46\u5982\u6709\u8f83\u957f\u7684\u51c6\u5907\u65f6\u95f4\uff0c\u7cbe \u5bc6\u7684\u914d\u5408\uff0c\u4f5c\u5230\u5982\u6b64\u5b8c\u7f8e\u7684\u72af\u7f6a\u5883\u754c\uff0c\u4e5f\u5b9e\u975e\u592a\u96be\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u4e0d\u8bba\u6740\u4eba\u7684\u8fc7\u7a0b\u5982\u4f55\u7cbe\u5bc6\u4fd0\u843d\uff0c\u90fd\u4f1a\u7559\u4e0b\u7834\u7efd\uff01\u201d\u94c1\u7fce\u9053\uff1a\u201c\u5929\u4e0b\u6ca1\u6709\u7edd\u5bf9\u7684\u5b8c\u5168\u72af \u7f6a\uff0c\u95ee\u9898\u662f\u5982\u4f55\u80fd\u627e\u51fa\u6765\uff1f\u201d \u3000\u3000\u201c\u7559\u4e0b\u7684\u72af\u7f6a\u75d5\u8ff9\uff0c\u5e94\u8be5\u5c31\u5728\u8fd9\u65b9\u5706\u6570\u6587\u4e4b\u5185\uff0c\u81f3\u591a\u4e0d\u4f1a\u903e\u8d8a\u8fd9\u5ea7\u65b0\u623f\u3002\u201d\u738b\u77e5\u5e9c\u9053\uff1a \u201c\u94c1\u603b\u6355\u5934\u5fc3\u4e2d\u5982\u6709\u7591\u8651\u4e4b\u5904\uff0c\u53ef\u4ee5\u518d\u4ed4\u7ec6\u5730\u641c\u67e5\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u8bf7\u5de5\u5320\u628a\u8fd9\u5e62\u623f\u5b50\u62c6\u4e86\uff0c\u6162\u6162\u7ec6\u6d3b\u5730\u62c6\u2026\u2026\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u6211\u5c31\u4e0d\u4fe1\u627e\u4e0d\u51fa\u4e00\u70b9 \u75d5\u8ff9\u3002\u201d \u3000\u3000------------- \u3000\u3000\u5b64\u5251\u751f  \u626b\u63cf\u3000  zerotang  OCR  \uff0c\u65e7\u96e8\u697c\u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c \u4e8c \u7ae0 \u3000\u3000\u5929\u67ab\u9053\u957f\u7a81\u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u5954\u5411\u65b0\u623f\u3002 \u3000\u3000\u539f\u6765\uff0c\u51e0\u4eba\u5df2\u79fb\u81f3\u897f\u53a2\u4e2d\uff0c\u98df\u7528\u9152\u996d\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u4f4e\u58f0\u5bf9\u5f20\u5e08\u7237\u9053\uff1a\u201c\u9a6c\u516c\u5b50\u7684\u5c38\u4f53\u8be5\u5982\u4f55\u5904\u7f6e\uff1f\u201d \u3000\u3000\u201c\u6700\u597d\u662f\u539f\u5730\u4fdd\u5b58\uff0c\u4f46\u8fd9\u6050\u6015\u4e0d\u5bb9\u6613\uff1f\u201d\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u53ea\u662f\u4fdd\u5b58\u5c38\u4f53\u4e0d\u8150\u574f\uff0c\u5c31\u8981\u5927\u8d39 \u5468\u6298\u3002\u201d \u3000\u3000\u201c\u5927\u8d39\u5468\u6298\u7684\u610f\u601d\u662f\u2026\u2026\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u8981\u82b1\u8d39\u5f88\u591a\u94b1\uff0c\u8fd8\u662f\u9700\u8981\u4e9b\u73cd\u8d35\u7684\u836f\u7269\uff1f\u201d \u3000\u3000\u201c\u90fd\u9700\u8981\uff0c\u82b1\u9500\u7684\u5e9e\u5927\uff0c\u6050\u975e\u4e00\u822c\u4eba\u6240\u80fd\u8d1f\u62c5\uff0c\u626c\u5dde\u5e9c\u867d\u7136\u6709\u94b1\uff0c\u4f46\u90a3\u90fd\u662f\u516c\u6b3e\uff01\u201d\u5f20 \u5b9d\u5584\u9053\uff1a\u201c\u8017\u7528\u8fc7\u5ea6\uff0c\u5f88\u96be\u4ea4\u4ee3\uff0c\u518d\u8bf4\u4e00\u4e9b\u73cd\u8d35\u836f\u7269\uff0c\u4e5f\u4e0d\u662f\u82b1\u94f6\u5b50\u5c31\u53ef\u4ee5\u4e70\u5230\u2026\u2026\u201d \u3000\u3000\u201c\u5f20\u5e08\u7237\uff0c\u94f6\u94b1\u82b1\u8d39\u4e0a\uff0c\u4e0d\u7528\u62c5\u5fc3\uff0c\u7531\u6211\u91d1\u67d0\u4eba\u627f\u62c5\uff0c\u4f60\u653e\u5fc3\u5927\u80c6\u5730\u652f\u7528\uff0c\u81f3\u4e8e\u73cd\u5947\u7684 \u836f\u7269\uff0c\u6211\u4eec\u91d1\u5bb6\u6536\u85cf\u4e0d\u5c11\uff0c\u4f60\u5f00\u4e2a\u6e05\u5355\u51fa\u6765\uff0c\u53ea\u8981\u626c\u5dde\u57ce\u4e2d\u6709\u7684\uff0c\u6211\u53ef\u4ee5\u9ad8\u4ef7\u8d2d\u4e70\u2026\u2026\u201d \u3000\u3000\u201c\u597d\uff0c\u6709\u91d1\u5458\u5916\u8fd9\u756a\u8bdd\uff0c\u4e8b\u60c5\u5c31\u597d\u529e\u591a\u4e86\u3002\u201d\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u5c38\u4f53\u4e0d\u574f\uff0c\u5e38\u5b58\u4e8e\u6b64\uff0c\u662f\u7528 \u9ec4\u91d1\u5806\u51fa\u6765\u7684\u6210\u7ee9\uff0c\u9a6c\u63d0\u7763\u4e5f\u4f1a\u660e\u767d\u91d1\u5458\u5916\u8fd9\u756a\u60c5\u601d\u6df1\u91cd\u7684\u5fc3\u610f\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5929\u67ab\u9053\u957f\u5df2\u5306\u5306\u8d70\u4e86\u56de\u6765\u3002 \u3000\u3000\u94c1\u7fce\u8d77\u8eab\u8fce\u4e86\u4e0a\u53bb\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u9053\u5144\uff0c\u6709\u4ec0\u4e48\u53d1\u73b0\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u795e\u60c5\u8083\u7136\uff0c\u672a\u7406\u4f1a\u94c1\u7fce\uff0c\u5374\u671b\u7740\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4e1c\u4e3b\uff0c\u8fd1\u6708\u4e2d\uff0c\u53ef\u6709\u4eba\u5230\u5e9c\u4e2d\u62dc \u671b\u8fc7\u4f60\uff1f\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u95ee\u7684\u7a81\u7136\uff0c\u4f46\u770b\u5929\u67ab\u9053\u957f\u7684\u4e25\u8083\u795e\u60c5\uff0c\u5c31\u77e5\u9053\u8fd9\u4f4d\u6b66\u6797\u9ad8\u4eba\u8bf4\u7684\u662f\u4e00\u4ef6\u5341\u5206\u91cd\u8981 \u7684\u4e8b\uff0c\u8fde\u738b\u77e5\u5e9c\u4e5f\u51dd\u795e\u503e\u542c\u4e0b\u6587\u3002 \u3000\u3000\u201c\u4f60\u662f\u8bf4\u6b66\u6797\u4e2d\u4eba\uff1f\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u8fd8\u662f\u4e00\u822c\u7684\u8bbf\u5ba2\uff1f\u201d \u3000\u3000\u201c\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u4e1c\u4e3b\u4e5f\u672a\u5fc5\u80fd\u770b\u7684\u51fa\u6765\uff0c\u201d\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4ed6\u8be5\u662f\u4e00\u4e2a\u5f88\u4f1a\u9690\u85cf\u81ea\u5df1\u7684 \u4eba\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u65e0\u6cd5\u8bf4\u7684\u6e05\u695a\u4e86\u3002\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u8fd1\u6708\u4e2d\uff0c\u6211\u81f3\u5c11\u63a5\u89c1\u5341\u4f4d\u5ba2\u4eba\uff0c\u4e03\u4f4d\u662f\u751f\u610f\u4e0a \u4ea4\u5f80\u7684\u670b\u53cb\uff0c\u4e24\u4f4d\u662f\u4eac\u91cc\u6765\u7684\u592b\u5b50\u5148\u751f\uff0c\u4ed6\u4eec\u90fd\u662f\u5341\u591a\u5e74\u6765\u7684\u8001\u670b\u53cb\u4e86\uff0c\u7edd\u5bf9\u4e0d\u662f\u6b66\u6797\u4e2d \u4eba\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u4e00\u4f4d\u5462\uff1f\u201d\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4e0d\u662f\u8001\u670b\u53cb\u5427\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff0c\u662f\u7b2c\u4e00\u6b21\u89c1\u9762\u7684\u8bbf\u5ba2\uff0c\u4e00\u4e2a\u5e74\u8f7b\u7684\u59d1\u5a18\uff01\u201d \u3000\u3000\u201c\u4e1c\u4e3b\u5411\u4e0d\u5355\u72ec\u89c1\u5ba2\uff0c\u5c24\u5176\u662f\u89c1\u964c\u751f\u4eba\uff0c\u4e0d\u77e5\u8fd9\u6b21\u662f\u54ea\u4e00\u4f4d\u966a\u5728\u4e1c\u4e3b\u8eab\u4fa7\uff1f\u201d \u3000\u3000\u201c\u603b\u7ba1\u8d3e\u82f1\uff0c\u8fd1\u6708\u6765\u90fd\u7531\u4ed6\u966a\u6211\u89c1\u5ba2\u3002\u201d \u3000\u3000\u201c\u5f88\u597d\uff0c\u5f88\u597d\uff0c\u201d\u5929\u67ab\u9053\u957f\u63a5\u9053\uff1a\u201c\u8d3e\u82f1\u80c6\u5927\u5fc3\u7ec6\uff0c\u6280\u827a\u7cbe\u6e5b\uff0c\u5e94\u8be5\u4f1a\u770b\u51fa\u4e00\u4e9b\u82d7\u5934\u624d \u5bf9\uff0c\u6211\u8981\u548c\u4ed6\u8c08\u8c08\uff1f\u201d \u3000\u3000\u201c\u6211\u5dee\u4eba\u53eb\u8d3e\u82f1\uff0c\u8981\u4ed6\u4e00\u9053\u7559\u5728\u5bb6\u4e2d\uff0c\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u6211\u62c5\u5fc3\u662f\u9a6c\u63d0\u7763\u5230\u5bb6\u91cc\u6293\u4eba\u3002\u201d \u3000\u3000\u4e00\u76f4\u5fcd\u8010\u7740\u672a\u518d\u591a\u8a00\u7684\u94c1\u7fce\uff0c\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5929\u67ab\u9053\u5144\uff0c\u4f60\u7a76\u7adf\u8981\u8bf4\u4ec0\u4e48\uff0c \u4f55\u4e0d\u7545\u7545\u5feb\u5feb\u8bf4\u51fa\u6765\uff1f\u201d \u3000\u3000\u9053\u5144\u4e0a\u52a0\u4e86\u5929\u67ab\u7684\u540d\u53f7\uff0c\u94c1\u603b\u6355\u5934\u663e\u7136\u662f\u6709\u4e9b\u706b\u4e86\u3002 \u3000\u3000\u201c\u94c1\u5144\u542c\u8fc7\u9b3c\u5200\u7684\u4f20\u8bf4\u4e48\uff1f\u201d \u3000\u3000\u201c\u542c\u8fc7\u4e00\u4e9b\uff0c\u53ea\u662f\u542c\u5f97\u4e0d\u591a\uff0c\u8bf4\u4ed6\u7684\u5200\u672f\u5df2\u5165\u5316\u5883\uff0c\u6740\u4eba\u4e8e\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u4e2d\uff01\u201d \u3000\u3000\u201c\u9b3c\u5200\u4e5f\u5230\u4e86\u626c\u5dde\u5e9c\uff0c\u201d\u63a5\u53e3\u7684\u7adf\u662f\u738b\u77e5\u5e9c\uff0c\u9053\uff1a\u201c\u4e5f\u5c31\u662f\u6740\u5bb3\u9a6c\u516c\u5b50\u7684\u51f6\u624b\u4e48\uff1f\u201d \u3000\u3000\u201c\u9b3c\u5200\uff0c\u53c8\u79f0\u68a6\u5e7b\u4e4b\u5200\uff0c\u6740\u4eba\u7684\u6280\u5de7\uff0c\u5df2\u7ecf\u5230\u4e86\u4e0d\u7740\u75d5\u8ff9\u7684\u5883\u754c\uff0c\u201d\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u80fd \u5229\u7528\u4eba\u3001\u9e1f\u3001\u82b1\u6811\uff0c\u5e03\u7f6e\u4e0b\u5404\u79cd\u5de7\u5999\u7684\u6740\u4eba\u65b9\u6cd5\uff0c\u4f7f\u4eba\u9632\u4e0d\u80dc\u9632\uff01\u9b3c\u5200\u4e4b\u540d\u7531\u6b64\u800c\u751f\uff0c\u6b64\u4e00 \u5947\u6280\uff0c\u524d\u65e0\u53e4\u4eba\uff0c\u4e5f\u672a\u542c\u8fc7\u6c5f\u6e56\u4e0a\u54ea\u4e2a\u95e8\u6d3e\u6709\u8fc7\u8fd9\u95e8\u6280\u827a\u3002\u201d \u3000\u3000\u201c\u5229\u7528\u82b1\u6811\u6740\u4eba\uff0c\u867d\u662f\u8038\u4eba\u542c\u95fb\uff0c\u4f46\u82b1\u6728\u679c\u6811\uff0c\u90fd\u662f\u690d\u7269\uff0c\u4efb\u4eba\u6446\u5f04\u5e03\u7f6e\uff0c\u201d\u738b\u5c11\u537f\u9053\uff1a \u201c\u5229\u7528\u98de\u9e1f\u6740\u4eba\uff0c\u5c31\u5f97\u6709\u4e00\u4e9b\u7279\u522b\u7684\u6280\u5de7\u4e86\uff0c\u7528\u4eba\u6740\u4eba\uff0c\u5012\u662f\u987a\u7406\u6210\u7ae0\uff0c\u591a\u57cb\u4f0f\u4e00\u4e9b\u6740\u624b\uff0c \u62e9\u673a\u51fa\u624b\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u4eba\uff0c\u5982\u662f\u8fd9\u6837\u7b80\u5355\uff0c\u9b3c\u5200\u8fd8\u6709\u4f55\u53ef\u60e7\uff1f\u4e5f\u4e0d\u914d\u79f0\u4f5c\u68a6\u5e7b\u4e4b\u5200\u4e86\uff0c\u201d\u5929\u67ab\u9053\u957f\u957f\u5401\u53e3 \u6c14\uff0c\u9053\uff1a\u201c\u6740\u624b\u4e5f\u65e0\u6cd5\u63a5\u8fd1\u4ed6\u8981\u6740\u7684\u4eba\uff0c\u53ea\u6709\u6700\u4eb2\u5bc6\u7684\u4eba\uff0c\u624d\u80fd\u6740\u7684\u4e0d\u7740\u75d5\u8ff9\uff0c\u4f7f\u4eba\u65e0\u6cd5\u9632 \u8303\uff01\u201d \u3000\u3000\u201c\u5c31\u50cf\u8fd9\u4ef6\u65b0\u623f\u8840\u6848\u4e00\u6837\uff0c\u201d\u738b\u5927\u4eba\u9053\uff1a\u201c\u4f60\u6211\u90fd\u8ba4\u4e3a\u91d1\u59d1\u5a18\u4e0d\u662f\u51f6\u624b\uff0c\u4f46\u5374\u65e0\u6cd5\u5e2e\u5979 \u6d17\u8131\u5acc\u7591\u3002\u201d \u3000\u3000\u201c\u6293\u5230\u4e86\u9b3c\u5200\uff0c\u5c31\u53ef\u4ee5\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\u4e86\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u9053\u5144\u5982\u80fd\u63d0\u4f9b\u9b3c\u5200\u5176\u4eba\u7684\u5f62\u8c8c\uff0c \u94c1\u67d0\u4eba\u613f\u5c3d\u51fa\u626c\u5dde\u5e9c\u6355\u5feb\u7cbe\u9510\uff0c\u5168\u9762\u8ffd\u7f09\u4ed6\u5230\u6848\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u6c89\u541f\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u4eba\u80fd\u591f\u6307\u51fa\u68a6\u5e7b\u4e4b\u5200\uff0c\u662f\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\u7269\uff1f\u751a\u81f3\u65e0\u6cd5 \u786e\u5b9a\u4ed6\u662f\u7537\u662f\u5973\uff1f\u2026\u2026\u201d \u3000\u3000\u94c1\u7fce\u63a5\u9053\uff1a\u201c\u53ef\u662f\uff0c\u9053\u5144\u65ad\u8a00\u9b3c\u5200\u5df2\u5230\u4e86\u626c\u5dde\uff0c\u96be\u9053\u4e5f\u662f\u81c6\u6d4b\u4e4b\u8bcd\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u201d\u5929\u67ab\u9053\u957f\uff1a\u201c\u9b3c\u5200\u6709\u4e24\u4e2a\u7279\u522b\u602a\u7656\uff0c\u4e00\u662f\u4e0d\u6740\u521d\u5ea6\u4f1a\u9762\u7684\u4eba\u3002\u4e5f\u5c31\u662f\u8bf4 \u4ed6\u51fa\u624b\u4e4b\u524d\u5fc5\u987b\u5148\u8981\u89c1\u88ab\u5bb3\u4eba\u4e00\u9762\u2026\u2026\u201d \u3000\u3000\u91d1\u767e\u5e74\u6253\u4e86\u4e00\u4e2a\u51b7\u98a4\uff0c\u9053\uff1a\u201c\u8001\u592b\u4e0d\u662f\u89c1\u8fc7\u4ed6\u4e86\uff1f\u201d \u3000\u3000\u201c\u4f60\u89c1\u7684\u90a3\u4f4d\u59d1\u5a18\uff0c\u662f\u4e0d\u662f\u9b3c\u5200\u7684\u5e7b\u53d8\u5f62\u50cf\uff0c\u8d2b\u9053\u4e0d\u5728\u73b0\u573a\uff0c\u65e0\u6cd5\u65ad\u8a00\uff0c\u4f46\u8d3e\u82f1\u5b9a\u4f1a\u77a7 \u51fa\u4e00\u4e9b\u75d5\u8ff9\u2026\u2026\u201d \u3000\u3000\u201c\u9a6c\u516c\u5b50\u662f\u88ab\u5bb3\u4eba\uff0c\u4e00\u5b9a\u89c1\u8fc7\u9b3c\u5200\u4e86\u3002\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u5c0f\u5973\u5462\uff1f\u662f\u4e0d\u662f\u4e5f\u7b97\u662f\u88ab\u5bb3\u4eba \u5462\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\uff01\u201d\u738b\u5927\u4eba\u63a5\u53e3\u9053\uff1a\u201c\u6740\u4eba\u5ac1\u7978\uff0c\u91d1\u59d1\u5a18\u6210\u4e86\u7b2c\u4e00\u5acc\u72af\uff0c\u5982\u8bba\u88ab\u5bb3\u4e4b\u8c0b\uff0c\u4f3c\u5e94 \u5728\u9a6c\u5bb6\u4e4b\u4e0a\uff0c\u53ea\u662f\u4e00\u4e2a\u5927\u5bb6\u5343\u91d1\uff0c\u6df1\u95fa\u4f73\u4eba\uff0c\u53c8\u600e\u4e48\u4f1a\u6d89\u5165\u8fd9\u4e9b\u6069\u6028\u4e4b\u4e2d\u5462\uff1f\u201d \u3000\u3000\u201c\u5927\u4eba\uff01\u8fd9\u53ea\u662f\u4e00\u4ef6\u6740\u4eba\u547d\u6848\uff0c\u53ea\u662f\u8fc7\u7a0b\u8be1\u5f02\uff0c\u6848\u60c5\u68d8\u624b\uff0c\u201d\u5f20\u5211\u6848\u9053\uff1a\u201c\u672a\u5fc5\u5c31\u4f1a\u7275 \u6d89\u4e0a\u9b3c\u5200\u8fd9\u7b49\u6c5f\u6e56\u602a\u4eba\uff0c\u5c5e\u4e0b\u518d\u4e09\u7684\u63a8\u60f3\uff0c\u4e00\u76f4\u90fd\u60f3\u4e0d\u51fa\u9b3c\u5200\u6d89\u6848\u7684\u56e0\u7531\u4f55\u5728\uff1f\u201d \u3000\u3000\u201c\u4e5f\u6ca1\u660e\u786e\u7684\u8bc1\u636e\uff0c\u8bf4\u660e\u68a6\u5e7b\u4e4b\u5200\u5df2\u5230\u4e86\u626c\u5dde\u5e9c\u57ce\u3002\u201d\u94c1\u7fce\u9053\uff1a\u201c\u8fd9\u6863\u4e8b\u53ea\u662f\u63a8\u60f3\u2026\u2026\u201d \u3000\u3000\u201c\u6709\u8bc1\u636e\uff0c\u5728\u4f60\u94c1\u603b\u6355\u5934\u4e4b\u524d\uff0c\u8d2b\u9053\u5982\u65e0\u4e09\u5206\u628a\u63e1\u600e\u6562\u5984\u8a00\u3002\u201d \u3000\u3000\u679c\u7136\u662f\u8bf7\u5c06\u4e0d\u5982\u6fc0\u5c06\uff0c\u8fd9\u8001\u9053\u58eb\u4eba\u867d\u8df3\u51fa\u4e09\u754c\u5916\uff0c\u4f46\u5fc3\u4ecd\u7559\u5728\u51e1\u754c\u4e2d\uff0c\u6446\u4e0d\u8131\u540d\u5229\u67b7\u9501\uff0c \u94c1\u7fce\u5fc3\u4e2d\u5fd6\u601d\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u9053\u5144\uff0c\u8bc1\u636e\u4f55\u5728\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5c31\u8c08\u5230\u9b3c\u5200\u5176\u4eba\u7b2c\u4e8c\u4e2a\u602a\u7656\u4e86\uff0c\u5200\u8fc7\u4e0d\u7559\u75d5\uff0c\u4eba\u5230\u5fc5\u7559\u540d\u3002\u201d\u7d27\u63e1\u7684\u53f3\u624b\u7f13\u7f13\u5f20\u5f00\uff0c \u9053\uff1a\u201c\u8fd9\u5c31\u662f\u9b3c\u5200\u6807\u5fd7\uff0c\u5982\u662f\u4ed6\u6d89\u6848\u6740\u4eba\uff0c\u5fc5\u4f1a\u5728\u539f\u5730\u505c\u7559\u4e00\u6708\uff0c\u4e09\u5341\u5929\u4e4b\u540e\uff0c\u624d\u4f1a\u98d8\u7136\u8fdc \u53bb\u3002\u201d \u3000\u3000\u662f\u4e00\u4e2a\u5c0f\u5c0f\u7684\u7389\u96d5\u4eba\u50cf\uff0c\u4e00\u624b\u62c8\u82b1\uff0c\u4e00\u624b\u6267\u5200\uff0c\u957f\u53d1\u6563\u5f00\uff0c\u7709\u5982\u5f2f\u6708\uff0c\u96d5\u5de5\u7cbe\u81f4\uff0c\u6829\u6829 \u5982\u751f\uff0c\u4ecd\u7136\u7ed9\u4eba\u4e00\u79cd\u8ff7\u8499\u7684\u611f\u89c9\uff0c\u4f60\u65e0\u6cd5\u7531\u7389\u96d5\u4eba\u50cf\u4e0a\uff0c\u5224\u5b9a\u4ed6\u662f\u7537\u8fd8\u662f\u5973\uff1f \u3000\u3000\u201c\u9053\u5144\uff0c\u80fd\u786e\u5b9a\u4ed6\u4ecd\u5728\u626c\u5dde\u4e2d\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u5144\u5f1f\u5c31\u8981\u4e0b\u4ee4\u5404\u8def\u7ebf\u6c11\uff0c\u914d\u5408\u5e9c\u4e2d\u6355\u5feb\uff0c \u5168\u9762\u641c\u67e5\u4e86\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u5fae\u5fae\u4e00\u7b11\uff1a\u201c\u542c\u8bf4\uff0c\u4f60\u65e9\u5df2\u5728\u626c\u5dde\u57ce\u4e2d\u5e03\u4e0b\u4e86\u5929\u7f57\u5730\u7f51\uff0c\u5404\u8def\u7684\u96c4\u67ad\u5e2e\u4f1a\uff0c\u90fd \u5728\u4f60\u4e4b\u4e0b\uff0c\u624d\u628a\u8fd9\u5ea7\u5929\u4e0b\u7b2c\u4e00\u5e9c\uff0c\u6cbb\u7406\u7684\u98ce\u5e73\u6d6a\u9759\u2026\u2026\u201d \u3000\u3000\u201c\u60ed\u6127\u5440\uff01\u60ed\u6127\uff0c\u98ce\u5e73\u6d6a\u9759\uff0c\u53c8\u600e\u4f1a\u53d1\u751f\u5982\u6b64\u91cd\u5927\u7684\u8840\u6848\uff0c\u7275\u7d2f\u6240\u53ca\uff0c\u5f88\u53ef\u80fd\u65ad\u9001\u4e86\u60e0 \u6211\u5f88\u6df1\u7684\u6069\u5b98\u524d\u7a0b\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u7ffb\u5730\u4e09\u5c3a\uff0c\u6211\u4e5f\u8981\u628a\u4ed6\u627e\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u94c1\u7fce\uff0c\u6211\u4e0d\u592a\u4e86\u89e3\u6c5f\u6e56\u4e2d\u4e8b\uff0c\u4f46\u7528\u4e0d\u7740\u6025\u4e8e\u627e\u5bfb\u9b3c\u5200\uff0c\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u5c31\u7b97\u627e\u5230\u4e86\uff0c \u4e5f\u4e0d\u80fd\u8bc1\u660e\u4ed6\u662f\u51f6\u624b\uff0c\u4f55\u4e0d\u518d\u7b49\u4e00\u4e24\u5929\uff0c\u7b49\u91d1\u9675\u7684\u675c\u671b\u6708\u5230\u4e86\u518d\u4f5c\u8ba1\u8bae\uff0c\u914d\u5408\u884c\u52a8\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\u8bf4\u7684\u662f\uff0c\u201d\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u6ca1\u6cd5\u8bc1\u660e\u9b3c\u5200\u548c\u8840\u6848\u6709\u5173\uff0c\u4f3c\u4e4e\u7528\u4e0d\u7740\u53bb\u62db\u60f9\u8fd9\u4e48\u4e00\u4e2a \u6c5f\u6e56\u602a\u6770\u3002\u201d \u3000\u3000\u94c1\u7fce\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5e08\u7237\u8bf4\u5f97\u5bf9\uff0c\u5c5e\u4e0b\u4e00\u5207\u9075\u547d\u884c\u4e8b\u3002\u201d \u3000\u3000\u201c\u9053\u957f\uff0c\u4f60\u770b\u8fd9\u4ef6\u4e8b\u54b1\u4eec\u662f\u5426\u8981\u63d2\u624b\u8fdb\u53bb\uff1f\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u5148\u67e5\u51fa\u9b3c\u5200\u7684\u843d\u8db3\u4e4b\u5904\uff0c\u4e00 \u5207\u7167\u6c5f\u6e56\u4eba\u89c4\u77e9\u884c\u4e8b\uff0c\u4e0d\u548c\u5b98\u5e9c\u626f\u4e0a\u5173\u7cfb\uff0c\u5c31\u4e0d\u7528\u6709\u592a\u591a\u7684\u987e\u5fcc\u4e86\uff01\u201d \u3000\u3000\u201c\u4e1c\u4e3b\u8ffd\u67e5\u9b3c\u5200\u884c\u8e2a\uff0c\u8d2b\u9053\u5012\u4e0d\u53cd\u5bf9\uff0c\u4e0d\u8fc7\uff0c\u7b49\u8d2b\u9053\u548c\u8d3e\u82f1\u8c08\u8fc7\u4e4b\u540e\uff0c\u4e1c\u4e3b\u518d\u4e0b\u4ee4\u884c\u52a8 \u4e0d\u8fdf\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u4e00\u4e2a\u56db\u65ec\u5de6\u53f3\u4e2d\u5e74\u4eba\uff0c\u6025\u5feb\u6b65\u884c\u4e86\u8fdb\u6765\uff0c\u770b\u7740\u4ed6\u4eba\u5165\u5ead\u9662\uff0c\u53ea\u4e0d\u8fc7\u4e00\u7728\u773c\u95f4\uff0c\u4eba \u5df2\u8fdb\u5165\u4e86\u53a2\u623f\uff0c\u5bf9\u7740\u738b\u77e5\u5e9c\u505a\u4e00\u8eac\u8eab\uff0c\u5374\u8f6c\u5411\u91d1\u767e\u5e74\uff1a\u201c\u4e1c\u4e3b\u6025\u4ee4\u53ec\u89c1\uff0c\u4e0d\u77e5\u6709\u4f55\u6307\u6559\uff1f\u201d \u3000\u3000\u201c\u662f\u5929\u67ab\u9053\u957f\u627e\u4f60\uff0c\u201d\u91d1\u767e\u5e74\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u597d\u597d\u5730\u8c08\u8c08\u5427\uff01\u201d \u3000\u3000\u738b\u77e5\u5e9c\u4e00\u76f4\u6697\u4e2d\u6253\u91cf\u8d3e\u82f1\uff0c\u53ea\u89c1\u4ed6\u8eab\u8eaf\u4fee\u957f\u3001\u53cc\u76ee\u4e2d\u795e\u5149\u903c\u4eba\uff0c\u60f3\u5230\u4e86\u521a\u624d\u884c\u52a8\u7684\u795e\u901f\uff0c \u5fc3\u4e2d\u6697\u6697\u8d5e\u9053\uff1a\u53c8\u4e00\u4f4d\u6c5f\u6e56\u9ad8\u624b\uff0c\u8fd9\u91d1\u767e\u5e74\u5e9c\u4e2d\u805a\u96c6\u7684\u9ad8\u624b\u5b9e\u529b\uff0c\u53ea\u6015\u8981\u5f3a\u8fc7\u626c\u5dde\u5e9c\u4e86\u3002 \u3000\u3000\u201c\u9053\u957f\u53ec\u89c1\u8d3e\u82f1\uff0c\u6709\u4f55\u6307\u70b9\uff1f\u201d \u3000\u3000\u201c\u8fd9\u8fd1\u6708\u4e4b\u4e2d\uff0c\u90fd\u7531\u8d3e\u5144\u966a\u7740\u4e1c\u4e3b\uff0c\u63a5\u89c1\u5bbe\u5ba2\uff0c\u4e2a\u4e2d\u662f\u5426\u6709\u53ef\u7591\u4eba\u7269\uff1f\u201d \u3000\u3000\u201c\u9053\u957f\u662f\u6307\u4e03\u5929\u4e4b\u524d\uff0c\u4e1c\u4e3b\u63a5\u89c1\u7684\u4e00\u4f4d\u5987\u9053\u4eba\u5bb6\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u6162\u6162\u6162\uff0c\u662f\u59d1\u5a18\u5bb6\u7684\u88c5\u675f\uff0c\u8fd8\u662f\u4e00\u4f4d\u5c11\u5987\uff1f\u8d3e\u5144\u8bf7\u4ed4\u7ec6\u56de\u60f3\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u201c\u6843\u7ea2\u5939\u886b\uff0c\u6c34\u7ea2\u957f\u88e4\uff0c\u88e4\u817f\u62d6\u5730\uff0c\u63a9\u4f4f\u4e86\u53cc\u8db3\uff0c\u91cc\u4e00\u4ef6\u7384\u8272\u62ab\u98ce\u3002\u201d\u8d3e\u82f1\u51dd\u76ee\u601d\u7d22\u4e86 \u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6d53\u7709\u5927\u773c\uff0c\u957f\u53d1\u62ab\u80a9\uff0c\u5de6\u7709\u4e2d\u6709\u4e00\u9897\u7ea2\u75e3\uff0c\u5c0f\u5982\u7c92\u7c73\uff0c\u4e0d\u7559\u5fc3\u5f88\u96be\u770b\u5f97\u51fa\u6765\u3002 \u6ca1\u6709\u5973\u4eba\u7684\u59a9\u5a9a\uff0c\u5374\u591a\u4e86\u4e00\u4efd\u903c\u4eba\u7684\u82f1\u6c14\uff0c\u4ed4\u7ec6\u5730\u54c1\u8bc4\u4e00\u4e0b\uff0c\u5c31\u5f88\u96be\u8bf4\u5979\u662f\u7537\u662f\u5973\u4e86\uff1f\u201d \u3000\u3000\u201c\u4ee5\u8d3e\u5144\u76ee\u529b\u4e4b\u5f3a\uff0c\u9605\u5386\u4e4b\u4e30\uff0c\u4e5f\u65e0\u6cd5\u8fa8\u51fa\u5979\u662f\u7537\u662f\u5973\u4e48\uff1f\u201d \u3000\u3000\u201c\u5979\u53cc\u624b\u4e00\u76f4\u9690\u5728\u62ab\u98ce\u4e4b\u4e2d\uff0c\u6211\u62c5\u5fc3\u5979\u4f1a\u7a81\u7136\u6709\u4e0d\u5229\u4e1c\u4e3b\u7684\u4e3e\u52a8\uff0c\u5438\u5f15\u4e86\u6211\u5927\u90e8\u5206\u7684\u7cbe \u795e\uff0c\u672a\u4f5c\u66f4\u4ed4\u7ec6\u89c2\u5bdf\uff0c\u7c97\u7565\u7684\u5370\u8c61\uff0c\u5c31\u662f\u5982\u6b64\u4e86\u3002\u201d \u3000\u3000\u201c\u4e1c\u4e3b\u5411\u4e0d\u8f7b\u6613\u89c1\u5ba2\uff0c\u600e\u4f1a\u63a5\u89c1\u8fd9\u4e48\u4e00\u4e2a\u4eba\u7269\u5462\uff1f\u201d \u3000\u3000\u201c\u6765\u7684\u592a\u5feb\u4e86\uff0c\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4e0b\u4eba\u901a\u62a5\uff0c\u6211\u8fd8\u672a\u6765\u5f97\u53ca\u56de\u7edd\uff0c\u5979\u4eba\u5df2\u8fdb\u4e86\u4e66\u623f\uff0c\u5e78\u597d \u8d3e\u603b\u7ba1\u53ca\u65f6\u800c\u81f3\u2026\u2026\u201d \u3000\u3000\u201c\u8d2b\u9053\u8bb0\u5f97\u4e1c\u4e3b\u8eab\u4fa7\u5e94\u90fd\u6709\u4ece\u536b\uff0c\u5bf8\u6b65\u4e0d\u79bb\uff0c\u600e\u4f1a\u8ba9\u5979\u51b2\u5165\u4e66\u623f\u4e2d\uff1f\u201d \u3000\u3000\u201c\u4e8b\u540e\uff0c\u6211\u4ed4\u7ec6\u5730\u67e5\u95ee\u8fc7\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u90a3\u5929\uff0c\u8d1f\u8d23\u5b88\u62a4\u4e66\u623f\u7684\u662f\u98de\u5200\u5eb7\u4e09\uff0c\u4e00\u76f4\u5b88\u5728\u4e66\u623f \u5916\u9762\uff0c\u7adf\u4e0d\u77e5\u6709\u4eba\u51b2\u5165\u4e66\u623f\u3002\u201d\u8d3e\u82f1\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5eb7\u4e09\u7684\u6b66\u529f\u4e0d\u9519\uff0c\u98de\u5200\u66f4\u662f\u4e00\u7edd\uff0c \u5f53\u4eca\u4e4b\u4e16\uff0c\u80fd\u7531\u4ed6\u8eab\u4fa7\u7ecf\u8fc7\uff0c\u4e0d\u4e3a\u4ed6\u53d1\u89c9\u7684\u4eba\uff0c\u53ea\u6015\u662f\u5f88\u96be\u627e\u51fa\u51e0\u4e2a\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u90a3\u4eba\u5341\u4e4b\u516b\u4e5d\u662f\u9b3c\u5200\u672c\u4eba\u4e86\uff1f\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u8001\u592b\u65e0\u6028\u65e0\u4ec7\uff0c\u600e\u4e48\u4f1a\u4e00\u5230 \u626c\u5dde\u5c31\u627e\u4e0a\u8001\u592b\u5462\uff1f\u201d \u3000\u3000\u201c\u4e1c\u4e3b\uff0c\u90a3\u4e00\u5929\uff0c\u4ed6\u548c\u4f60\u8c08\u4e9b\u4ec0\u4e48\uff1f\u201d\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u8981\u636e\u5b9e\u800c\u8a00\uff0c\u6709\u4e0d\u5f97\u4e00\u70b9\u9519\u8bef\u9057 \u6f0f\u3002\u5982\u679c\u90a3\u4e2a\u4eba\u771f\u662f\u9b3c\u5200\uff0c\u4f60\u4eec\u5df2\u7ecf\u89c1\u8fc7\u9762\u4e86\uff0c\u4e0b\u4e00\u6b65\u5f88\u53ef\u80fd\u662f\u51fa\u5200\uff0c\u8d2b\u9053\u5e0c\u671b\u80fd\u5728\u4e24\u4f4d\u7684 \u4ea4\u8c08\u8fc7\u7a0b\u4e2d\uff0c\u542c\u51fa\u4e00\u4e9b\u86db\u4e1d\u9a6c\u8ff9\uff0c\u65e2\u53ef\u9884\u4f5c\u9632\u8303\uff0c\u4e5f\u53ef\u4ee5\u7528\u4f5c\u4e0b\u5b9a\u51b3\u5fc3\u7684\u53c2\u8003\uff01\u201d \u3000\u3000\u4e0b\u5b9a\u4ec0\u4e48\u51b3\u5fc3\uff1f\u5929\u67ab\u9053\u957f\u672a\u8bf4\u6e05\u695a\uff0c\u4f46\u5982\u91d1\u767e\u5e74\u611f\u89c9\u5230\u786e\u6709\u5371\u9669\u65f6\uff0c\u5fc5\u4e0d\u7518\u5750\u4ee5\u5f85\u6bd9\uff0c \u5f88\u53ef\u80fd\u5148\u53d1\u5236\u4eba\uff0c\u52a8\u5458\u9ad8\u624b\uff0c\u5168\u529b\u4e00\u640f\u3002 \u3000\u3000\u738b\u5927\u4eba\u660e\u767d\u8fd9\u4e2a\u610f\u601d\uff0c\u5f20\u5e08\u7237\u548c\u94c1\u7fce\uff0c\u4e5f\u542c\u5f97\u5fc3\u4e2d\u660e\u767d\uff0c\u4f46\u5374\u65e0\u4eba\u5f00\u53e3\u3002 \u3000\u3000\u201c\u5979\u8981\u8001\u592b\u4e00\u53cc\u5343\u5e74\u8001\u53c2\uff0c\u4e00\u9897\u77f3\u73e0\uff0c\u4e00\u67c4\u77ed\u5251\uff0c\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u8fd9\u4e9b\u4e1c\u897f\uff0c\u90fd\u662f\u8001\u592b \u82b1\u5c3d\u5fc3\u8840\uff0c\u6536\u8d2d\u5230\u624b\u7684\u4e1c\u897f\uff0c\u5c82\u80af\u5272\u7231\uff0c\u6240\u4ee5\uff0c\u4e00\u53e3\u56de\u7edd\u4e86\u5979\u3002\u201d \u3000\u3000\u201c\u5343\u5e74\u8001\u53c2\u7684\u529f\u80fd\u80fd\u8d77\u6b7b\u56de\u751f\uff0c\u672c\u5e9c\u4e5f\u53ea\u662f\u542c\u4eba\u8c08\u8fc7\uff0c\u4ece\u672a\u89c1\u8fc7\u2026\u2026\u201d\u738b\u5927\u4eba\u9053\uff1a\u201c\u4f46 \u77f3\u73e0\u4e00\u9897\uff0c\u77ed\u5251\u4e00\u628a\uff0c\u80fd\u503c\u51e0\u4f55\uff1f\u91d1\u8001\u600e\u4f1a\u5982\u6b64\u541d\u60dc\u5462\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u53cc\u76ee\u8f6c\u6ce8\u5728\u738b\u77e5\u5e9c\u7684\u8138\u4e0a\uff0c\u77a7\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5927\u4eba\u53ef\u662f\u8981\u76d8\u76d8\u8001\u673d\u7684\u5e95\u5b50\u4e48\uff1f\u201d \u3000\u3000\u201c\u8a00\u91cd\u4e86\uff0c\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u5982\u662f\u6709\u4e0d\u4fbf\u4e4b\u5904\uff0c\u90a3\u5c31\u4e0d\u7528\u8bf4\u4e86\u3002\u201d \u3000\u3000\u201c\u4e1c\u897f\u867d\u7136\u662f\u8d35\uff0c\u4f46\u5165\u8001\u673d\u4e4b\u624b\u7684\u7ecf\u8fc7\uff0c\u5374\u5341\u5206\u6e05\u767d\u3002\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u6211\u662f\u82b1\u7528\u5927\u628a\u91d1 \u94f6\u4e70\u6765\u7684\uff0c\u800c\u4e14\uff0c\u90fd\u6709\u51ed\u8bc1\uff0c\u5927\u4eba\u5982\u679c\u60f3\u8ffd\u67e5\u6765\u6e90\uff0c\u8001\u673d\u613f\u63d0\u4f9b\u5168\u90e8\u7684\u8bc1\u636e\u3002\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u7528\u4e86\uff0c\u201d\u738b\u5927\u4eba\u9053\uff1a\u201c\u672c\u5e9c\u53ea\u662f\u611f\u5230\u597d\u5947\uff0c\u4ec0\u4e48\u6837\u7684\u77f3\u73e0\u548c\u77ed\u5251\uff0c\u80fd\u4ef7\u503c\u8fde\u57ce\uff0c \u8ba9\u4f60\u91d1\u5458\u5916\u5982\u6b64\u5730\u73cd\u60dc\uff1f\u201d \u3000\u3000\u201c\u5251\u540d\u9c7c\u80a0\u5251\uff0c\u77f3\u73e0\u53eb\u6c34\u706b\u76f8\u6d4e\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u4e13\u8bf8\u523a\u50da\u7684\u9c7c\u80a0\u5251\uff1f\u201d\u738b\u5c11\u537f\u6709\u4e9b\u5403\u60ca\u5730\u9053\uff1a\u201c\u90a3\u53ef\u662f\u4f20\u4e16\u4e4b\u5b9d\uff0c\u636e\u8bf4\u80fd\u5207\u91d1\u65ad\u7389 \u524a\u94c1\u5982\u6ce5\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u5927\u4eba\uff0c\u5f84\u5bf8\u94c1\u6761\uff0c\u4e00\u6325\u800c\u65ad\uff0c\u8fce\u98ce\u65ad\u8349\uff0c\u5439\u6bdb\u79bb\u5203\uff0c\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u662f\u4e00\u628a\u597d \u5251\uff0c\u4e0d\u80fd\u8ba9\u5b83\u6ca6\u843d\u6c5f\u6e56\u4e4b\u4e2d\uff0c\u7528\u4f5c\u6740\u4eba\u5229\u5668\u3002\u201d \u3000\u3000\u201c\u91d1\u8001\u6709\u6b64\u4e00\u5ff5\uff0c\u5df2\u5177\u4ec1\u4eba\u4e4b\u5fc3\uff0c\u672c\u5e9c\u597d\u751f\u4f69\u670d\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\u6e0a\u535a\uff0c\u65e0\u6240\u4e0d\u77e5\uff0c\u60f3\u5fc5\u5bf9\u6c34\u706b\u76f8\u6d4e\u77f3\u73e0\uff0c\u4ea6\u6709\u6240\u77e5\u4e86\uff1f\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u6211\u867d\u7136\u73cd \u85cf\u4e86\u6b64\u73e0\uff0c\u53ea\u56e0\u5b83\u7684\u5916\u5f62\u5947\u7279\uff0c\u662f\u5426\u8fd8\u6709\u522b\u7684\u4f5c\u7528\uff0c\u5c31\u4e00\u65e0\u6240\u77e5\u4e86\uff0c\u90a3\u7591\u4f5c\u9b3c\u5200\u7684\u5973\u4eba\uff0c\u6307 \u540d\u7d22\u6c42\u6b64\u73e0\uff0c\u60f3\u6765\u7edd\u4e0d\u662f\u7528\u6765\u628a\u73a9\u9274\u8d4f\u4e86\u3002\u201d \u3000\u3000\u738b\u5927\u4eba\u6c89\u601d\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u73e0\u540d\u6c34\u706b\u76f8\u6d4e\uff0c\u5e94\u8be5\u7531\u4e24\u79cd\u989c\u8272\u7ec4\u5408\u800c\u6210\u4e86\uff0c\u4e00\u79cd\u662f\u7ea2\u8272\uff0c \u53e6\u4e00\u79cd\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u767d\u8272\uff0c\u767d\u7684\u6bd4\u96ea\uff0c\u7ea2\u7684\u706b\u7ea2\uff0c\u4e24\u79cd\u5b8c\u5168\u4e0d\u540c\u7684\u989c\u8272\u7ed3\u5408\u6210\u4e00\u4f53\uff0c\u521d\u770b\u4f3c\u662f\u7531\u4eba\u5de5\u9ecf\u5408 \u800c\u6210\uff0c\u4f46\u4f60\u4ed4\u7ec6\u67e5\u9a8c\uff0c\u5b8c\u5168\u6ca1\u6709\u4eba\u5de5\u7ed3\u5408\u7684\u75d5\u8ff9\uff0c\u81ea\u7136\u754c\u9020\u7269\u4e4b\u5947\uff0c\u7edd\u975e\u4eba\u529b\u53ef\u53ca\uff0c\u201d\u91d1\u767e \u5e74\u9053\uff1a\u201c\u6d51\u7136\u5929\u6210\uff0c\u7ea2\u767d\u5408\u58c1\uff0c\u8001\u673d\u66fe\u548c\u4e00\u4e9b\u5bf9\u5c71\u77f3\u53e4\u7389\uff0c\u6709\u8fc7\u7814\u7a76\u7684\u4eba\uff0c\u628a\u73a9\u8fc7\u6570\u6b21\uff0c\u591a \u6b21\u5546\u8c08\u4e4b\u540e\uff0c\u89c9\u7740\u5b83\u662f\u77f3\u4e2d\u4e4b\u80c6\uff0c\u53ef\u4ee5\u5408\u836f\uff0c\u662f\u6cbb\u7597\u98ce\u762b\u7684\u73cd\u54c1\u3002\u201d \u3000\u3000\u201c\u53ef\u60dc\u672c\u5e9c\u8bfb\u4e66\u6709\u9650\uff0c\u53c8\u5c11\u52a1\u6742\u5b66\uff0c\u4e5f\u60f3\u4e0d\u51fa\u77f3\u73e0\u6765\u5386\uff0c\u4e0d\u8fc7\uff0c\u91d1\u5458\u5916\u5982\u771f\u60f3\u5bfb\u6839\u7a76\u5e95\uff0c \u5012\u975e\u96be\u4e8b\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u5927\u4eba\u6307\u70b9\u3002\u201d \u3000\u3000\u201c\u9c7c\u80a0\u5251\u5207\u91d1\u65ad\u7389\uff0c\u4e5f\u8bb8\u80fd\u5207\u5f00\u8fd9\u77f3\u4e2d\u4e4b\u80c6\uff0c\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u77f3\u80c6\u867d\u7136\u7834\u88c2\uff0c\u4f46\u5e94\u65e0\u635f \u5b83\u7684\u5408\u836f\u6548\u7528\u3002\u201d \u3000\u3000\u4ed6\u8bfb\u4e66\u4e07\u5377\uff0c\u667a\u8c0b\u53cd\u5e94\u4e4b\u5feb\u5b9e\u975e\u8fd9\u4e9b\u6c5f\u6e56\u4eba\u7269\u80fd\u53ca\uff0c\u4f46\u6c5f\u6e56\u4e2d\u4e8b\uff0c\u522b\u6709\u89c4\u8303\uff0c\u6709\u4e9b\u4e8b\uff0c \u738b\u5c11\u537f\u867d\u7136\u65e9\u5df2\u9886\u609f\uff0c\u800c\u4e14\u4e5f\u60f3\u51fa\u4e86\u5e94\u53d8\u4e4b\u6cd5\uff0c\u4f46\u5374\u80fd\u85cf\u6536\u673a\u950b\uff0c\u9690\u5fcd\u4e0d\u8bf4\uff0c\u4ee5\u514d\u950b\u8292\u8fc7\u9732\u3002 \u3000\u3000\u6c34\u706b\u76f8\u6d4e\u8fd9\u7b49\u5929\u6750\u5730\u5b9d\uff0c\u53ef\u9047\u800c\u4e0d\u53ef\u6c42\uff0c\u4e5f\u5f15\u52a8\u4e86\u738b\u5c11\u537f\u5f3a\u70c8\u7684\u597d\u5947\u4e4b\u5fc3\uff0c\u5982\u80fd\u8bf1\u4f7f\u91d1 \u767e\u5e74\u5207\u77f3\u67e5\u770b\uff0c\u5c31\u7b97\u522b\u65e0\u53d1\u73b0\uff0c\u4f46\u5374\u65e0\u635f\u5b83\u7684\u5408\u836f\u4ef7\u503c\u3002 \u3000\u3000\u9b3c\u5200\u80fd\u63a2\u51fa\u6b64\u77f3\u4e0b\u843d\uff0c\u5fc5\u7136\u82b1\u8d39\u4e86\u4e0d\u5c11\u5de5\u592b\uff0c\u6307\u540d\u7d22\u53d6\uff0c\u7edd\u975e\u7528\u4ee5\u5408\u836f\uff0c\u8fd9\u4e2a\u77f3\u73e0\u7684\u4ef7 \u503c\uff0c\u5c31\u975e\u540c\u5c0f\u53ef\u4e86\u3002 \u3000\u3000\u201c\u5927\u4eba\u8bf4\u5f97\u662f\uff0c\u5207\u5f00\u77a7\u77a7\uff0c\u4e5f\u8bb8\u80fd\u770b\u51fa\u4e00\u4e9b\u7384\u673a\u3002\u201d\u5fc3\u5ff5\u65e2\u5b9a\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u548c\u90a3\u5973\u5b50 \u8c08\u8bdd\uff0c\u5c31\u662f\u8fd9\u4e9b\u4e86\uff0c\u9053\u957f\u66ff\u91d1\u67d0\u62ff\u4e2a\u4e3b\u610f\uff0c\u8be5\u5982\u4f55\u5e94\u4ed8\uff1f\u201d \u3000\u3000\u201c\u5979\u8981\u4f60\u4e09\u6837\u4e1c\u897f\uff0c\u4f60\u4e00\u6837\u4e5f\u672a\u5e94\u5141\uff0c\u96be\u9053\u5979\u5c31\u672a\u4f5c\u8868\u793a\uff0c\u8f6c\u8eab\u8d70\u4e86\uff1f\u201d \u3000\u3000\u201c\u8d3e\u82f1\u5df2\u5230\u6211\u8eab\u4fa7\uff0c\u5eb7\u4e09\u4e5f\u5728\u5979\u8eab\u540e\u51fa\u73b0\uff0c\u624b\u4e2d\u8fd8\u63e1\u7740\u98de\u5200\uff0c\u6740\u6211\u7684\u65f6\u673a\uff0c\u5df2\u6682\u6d88\u5931\u3002\u201d \u91d1\u767e\u5e74\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5979\u4e34\u53bb\u4e4b\u65f6\uff0c\u5012\u662f\u8bf4\u4e86\u4e24\u53e5\u8bdd\uff0c\u8bed\u610f\u542b\u6df7\uff0c\u4f3c\u8bd7\u4f3c\u5048\uff0c\u4e0d\u77e5\u5979\u8868\u793a\u7684\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u201c\u738b\u5927\u4eba\u662f\u4e00\u4ee3\u72b6\u5143\u4e4b\u624d\uff0c\u535a\u89c8\u53e4\u4eca\uff0c\u8fd8\u6709\u4ec0\u4e48\u89e3\u4e0d\u5f00\u7684\u8bd7\u5048\uff1f\u201d\u5929\u67ab\u9053\u957f\uff1a\u201c\u4e5f\u8bb8\u90a3 \u4e24\u53e5\u5048\u8bed\uff0c\u624d\u662f\u5979\u8868\u8fbe\u7684\u771f\u610f\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4e09\u5b9d\u5c5e\u7978\u9996\uff0c\u7e41\u534e\u5c3d\u6210\u7a7a\uff0c\u52ab\u706b\u8fde\u5341\u5c45\uff0c\u6ce3\u8840\u4e00\u540d\u57ce\u3002\u597d\u50cf \u5c31\u662f\u8fd9\u56db\u53e5\u4e86\uff0c\u5f53\u65f6\u6211\u8fd8\u6697\u6697\u80cc\u8bf5\u4e86\u4e24\u904d\uff0c\u5e94\u8be5\u4e0d\u4f1a\u6709\u9519\u3002\u201d \u3000\u3000\u201c\u8fd9\u7b97\u4e0d\u4e0a\u8bd7\uff0c\u4e5f\u4e0d\u662f\u5048\u8bed\u3002\u201d\u738b\u77e5\u5e9c\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u800c\u662f\u4e00\u4e2a\u8baf\u606f\uff0c\u4e00\u4e2a\u8840\u6dcb \u6dcb\u7684\u8b66\u544a\uff0c\u53ea\u4e0d\u8fc7\uff0c\u8fd8\u6709\u4e24\u70b9\u672c\u5e9c\u89c1\u89e3\u4e0d\u900f\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u8bf4\u8bf4\u770b\uff0c\u8001\u673d\u6216\u80fd\u63d0\u4f9b\u4e00\u4e9b\u5e2e\u52a9\u3002\u201d \u3000\u3000\u201c\u4e09\u5b9d\u5c5e\u7978\u9996\uff0c\u90a3\u662f\u8bf4\u4f60\u91d1\u5458\u5916\u6301\u6709\u7684\u5b9d\u5251\u3001\u77f3\u73e0\u3001\u8001\u4eba\u53c2\uff0c\u662f\u5f15\u8d77\u8fd9\u6b21\u52ab\u96be\u7684\u8d77\u56e0\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u70b9\u70b9\u5934\uff0c\u663e\u662f\u5df2\u63a5\u53d7\u4e86\u738b\u77e5\u5e9c\u7684\u63a8\u8bba\uff0c\u9053\uff1a\u201c\u7e41\u534e\u5c3d\u6210\u7a7a\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u91cc\u7684\u7e41\u534e\u5c06\u6210\u5f80\u4e8b\u4e91\u70df\uff0c\u4e00\u53bb\u4e0d\u518d\u3002\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u52ab\u706b\u8fde\u5341\u5c45\uff0c\u8fd9\u91cc\u7684\u5c45\u5b57\u5e94\u8be5 \u5f53\u5bb6\u8bb2\uff0c\u626c\u5dde\u57ce\u5343\u5bb6\u4e07\u6237\uff0c\u4e3a\u4ec0\u4e48\u53ea\u6709\u5341\u5bb6\u6709\u6b64\u52ab\u96be\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u6211\u77e5\u9053\u3002\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u626c\u5dde\u57ce\u4e2d\u6709\u5341\u5927\u5bcc\u8c6a\uff0c\u4ed6\u4eec\u7684\u8d22\u529b\u5408\u8d77\u6765\uff0c\u5e94\u80fd\u8d85\u8d8a \u8fc7\u626c\u5dde\u57ce\u6240\u6709\u7684\u8d22\u5bcc\u4e03\u6210\u4ee5\u4e0a\uff0c\u8fd9\u672c\u662f\u76f8\u5f53\u673a\u5bc6\u7684\u4e00\u4ef6\u4e8b\uff0c\u7adf\u88ab\u4ed6\u9b3c\u5200\u6253\u542c\u7684\u6e05\u6e05\u695a\u695a\uff0c\u9b3c \u5200\u8fd9\u4e2a\u4eba\u7684\u786e\u53ef\u6015\u6781\u4e86\u3002\u201d \u3000\u3000\u201c\u6ce3\u8840\u3001\u540d\u57ce\uff0c\u662f\u6307\u8fd9\u573a\u52ab\u96be\u4e4b\u540e\uff0c\u8fd9\u5ea7\u5bcc\u51a0\u4e00\u56fd\u7684\u540d\u57ce\uff0c\u7e41\u534e\u843d\u5c3d\uff0c\u53d8\u6210\u4e00\u5904\u4ee4\u4eba\u4f24 \u5fc3\u6ce3\u8840\u7684\u5e9f\u589f\u4e86\u3002\u201d\u738b\u5927\u4eba\u9053\uff1a\u201c\u7167\u8fd9\u4e48\u8bf4\u8d77\u6765\uff0c\u4e5f\u4e0d\u5168\u662f\u4f60\u4eec\u5341\u5927\u5bcc\u8c6a\u5bb6\u7684\u4e8b\u4e86\uff0c\u626c\u5dde\u5e9c \u5c82\u80fd\u5750\u89c6\u4e0d\u7406\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u77e5\u5e9c\u5927\u4eba\u8bf4\u5f97\u5bf9\uff0c\u626c\u5dde\u57ce\u4e0d\u80fd\u5728\u4f60\u624b\u4e2d\u6c89\u6ca6\u6bc1\u706d\uff0c\u5341\u5927\u5bb6\u65cf\u4e5f\u5e94\u8054\u624b\u62d2\u654c\uff0c \u660e\u5929\u8001\u673d\u5c31\u9080\u8bf7\u4ed6\u4eec\u4e5d\u4f4d\u8fc7\u5e9c\u996e\u5bb4\uff0c\u516c\u5f00\u51fa\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5341\u5bb6\u4eba\u5982\u80fd\u8bda\u5fc3\u5408\u4f5c\uff0c\u9b3c\u5200\u7eb5\u6709\u7ffb\u4e91 \u8986\u96e8\u4e4b\u80fd\uff0c\u4e5f\u96be\u5728\u626c\u5dde\u57ce\u4e2d\u6a2a\u884c\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u672c\u5df2\u6709\u544a\u8f9e\u4e4b\u610f\uff0c\u4f46\u671b\u4e86\u5929\u67ab\u9053\u957f\u4e00\u773c\u4e4b\u540e\uff0c\u91cd\u53c8\u5750\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u4e0d\u8bba\u6765\u4eba\u662f\u7537\u662f\u5973\uff0c\u662f\u4e0d\u662f\u9b3c\u5200\u7684\u5316\u8eab\uff0c\u4f46\u4f20\u6765\u7684\u8b66\u8baf\uff0c\u5374\u5341\u5206\u53ef\u6016\u3002\u201d\u5929\u67ab\u9053\u957f\u9053\uff1a \u201c\u8bf8\u4f4d\u548c\u8d2b\u9053\uff0c\u4f3c\u90fd\u5728\u8fd9\u573a\u52ab\u706b\u4e4b\u4e2d\uff0c\u68a6\u5e7b\u4e4b\u5200\u867d\u7136\u8be1\u5f02\uff0c\u4f46\u8d2b\u9053\u8fd8\u662f\u4e0d\u592a\u76f8\u4fe1\u4ed6\u6562\u5982\u6b64\u5730 \u81ea\u4e0d\u91cf\u529b\uff0c\u8fd9\u4e2d\u95f4\u53ea\u6015\u662f\u53e6\u6709\u66f2\u6298\u3002\u201d \u3000\u3000\u201c\u9053\u957f\uff0c\u81ea\u90a3\u6b21\u4e8b\u4ef6\u4e4b\u540e\uff0c\u8d3e\u67d0\u81ea\u89c9\u6709\u5931\u7b97\u4e4b\u8d23\uff0c\u8fd9\u4e9b\u5929\u6211\u91cd\u65b0\u4f5c\u4e86\u4e9b\u5e03\u7f6e\u3001\u8c03\u6574\uff0c\u9664 \u4e86\u52a0\u5f3a\u5e9c\u4e2d\u7684\u9632\u536b\u4e4b\u5916\uff0c\u53e6\u8bbe\u4e09\u4e2a\u673a\u52a8\u5c0f\u7ec4\uff0c\u968f\u65f6\u53ef\u8d76\u63f4\u5404\u5904\uff0c\u8fd8\u62bd\u51fa\u4e86\u5341\u4e8c\u4e2a\u4eba\u7ec4\u6210\u4e00\u961f\uff0c \u53ef\u4f9b\u4e1c\u4e3b\u8c03\u9063\uff0c\u4ee5\u4f5c\u641c\u7d22\u9b3c\u5200\u4e4b\u7528\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u6697\u6697\u5fd6\u9053\uff1a\u8fd9\u91d1\u5e9c\u4e4b\u4e2d\uff0c\u7a76\u7adf\u6709\u591a\u5c11\u6c5f\u6e56\u9ad8\u624b\uff1f\u4f5c\u4e3a\u62a4\u5b88\u5b85\u9662\u7684\u6b66\u5e08\uff0c\u53ea\u542c\u8fd9\u756a \u8c03\u914d\uff0c\u4eba\u6570\u81f3\u5c11\u6709\u4e8c\u4e09\u5341\u4f4d\u4e4b\u591a\uff0c\u8fd9\u4e9b\u62a4\u9662\uff0c\u5927\u90fd\u662f\u53eb\u5f97\u54cd\u5b57\u53f7\u7684\u4eba\u7269\uff0c\u867d\u975e\u4e2a\u4e2a\u90fd\u662f\u4e00\u6d41 \u9ad8\u624b\uff0c\u4f46\u4e5f\u90fd\u5f97\u6709\u4e00\u624b\u7edd\u6280\uff0c\u4e14\u624d\u80fd\u7ad9\u7a33\u811a\u6b65\u3002\u5341\u5927\u5bcc\u8c6a\u7684\u62a4\u9662\u6b66\u5e08\u52a0\u8d77\u6765\uff0c\u81f3\u5c11\u6709\u767e\u4f4d\u9ad8 \u624b\u4ee5\u4e0a\uff0c\u662f\u4e00\u80a1\u4e0d\u53ef\u5ffd\u89c6\u7684\u529b\u91cf\uff0c\u4fdd\u62a4\u626c\u5dde\uff0c\u5bf9\u4ed8\u68a6\u5e7b\u4e4b\u5200\uff0c\u975e\u5f97\u501f\u91cd\u4ed6\u4eec\u4e0d\u53ef\uff0c\u5211\u90e8\u7684\u603b \u6355\u53f8\uff0c\u6c5f\u5357\u5206\u53f8\uff0c\u53ea\u6015\u4e5f\u672a\u5fc5\u6709\u8fd9\u80a1\u5f3a\u5927\u7684\u529b\u91cf\u3002 \u3000\u3000\u201c\u4e0d\u8bba\u9b3c\u5200\u662f\u5426\u662f\u6740\u4eba\u51f6\u624b\uff0c\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4e5f\u4e0d\u7ba1\u4ed6\u662f\u5426\u5df2\u51c6\u5907\u5bf9\u6211\u51fa\u5200\uff0c\u4f46\u4ed6\u65e2\u5165 \u626c\u5dde\uff0c\u5c31\u5e94\u8be5\u628a\u4ed6\u627e\u51fa\u6765\uff0c\u8fd9\u4ef6\u4e8b\u7531\u5929\u67ab\u9053\u957f\u9886\u5bfc\u7b56\u5212\uff0c\u8d3e\u603b\u7ba1\u5168\u529b\u652f\u63f4\uff01\u201d\u76ee\u5149\u8f6c\u6ce8\u738b\u5c11 \u537f\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u5927\u4eba\u53ef\u6709\u6307\u6559\uff1f\u201d \u3000\u3000\u201c\u91d1\u5458\u5916\u8c03\u5ea6\u6709\u65b9\uff0c\u7528\u4eba\u9002\u624d\u5f97\u5f53\uff0c\u672c\u5e9c\u5f88\u662f\u4f69\u670d\u3002\u201d\u738b\u5927\u4eba\u9053\uff1a\u201c\u56de\u8859\u4e4b\u540e\uff0c\u6211\u5c06\u548c \u94c1\u603b\u6355\u5934\u5546\u8ba1\u4e00\u756a\uff0c\u4f1a\u5408\u5211\u90e8\u3001\u6c5f\u5357\u5206\u53f8\u4eba\u5458\u4e00\u8d77\u884c\u52a8\uff0c\u94c1\u603b\u6355\u5934\u548c\u5929\u67ab\u9053\u957f\u662f\u591a\u5e74\u670b\u53cb\uff0c \u5fc5\u80fd\u5408\u4f5c\u5f97\u5706\u6ee1\u6109\u5feb\uff0c\u672c\u5e9c\u5148\u884c\u544a\u9000\u4e86\u3002\u201d \u3000\u3000\u56de\u5230\u5e9c\u8859\uff0c\u738b\u77e5\u5e9c\u5e76\u672a\u8f6c\u5165\u540e\u5b85\u4f11\u606f\uff0c\u5374\u8ff3\u81ea\u8fdb\u4e86\u4e8c\u5802\u4e66\u623f\u3002 \u3000\u3000\u8fd9\u662f\u738b\u77e5\u5e9c\u5904\u7406\u8981\u6848\u6240\u5728\uff0c\u5f20\u5e08\u7237\u3001\u94c1\u603b\u6355\u4e5f\u8ddf\u4e86\u8fdb\u53bb\u3002\u4f55\u5927\u5149\u672a\u5165\u4e66\u623f\uff0c\u5374\u7ad9\u5728\u4e66\u623f \u95e8\u53e3\uff0c\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u4e0b\u6253\u91cf\uff0c\u8c28\u614e\u4e2d\u900f\u7740\u673a\u8b66\u3002 \u3000\u3000\u201c\u91d1\u767e\u5e74\u4e0d\u8fc7\u662f\u4e00\u4e2a\u5e73\u6c11\u767e\u59d3\uff0c\u5e9c\u4e2d\u8058\u517b\u7684\u6c5f\u6e56\u4eba\u7269\uff0c\u6bd4\u8d77\u626c\u5dde\u5e9c\u6355\u5feb\u73ed\u5f79\uff0c\u529b\u91cf\u4f3c\u662f \u8fd8\u8981\u5f3a\u5927\u4e00\u4e9b\u3002\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u626c\u5dde\u57ce\u4e2d\u5341\u5927\u5bcc\u8c6a\u4e4b\u5bb6\uff0c\u6709\u6b64\u529b\u91cf\uff0c\u54ea\u91cc\u8fd8\u4f1a\u628a\u5b98\u5e9c\u653e\u5728\u773c \u4e2d\u3002\u201d \u3000\u3000\u201c\u5929\u67ab\u9053\u957f\u5251\u672f\u4e0a\u7684\u6210\u5c31\u6781\u9ad8\uff0c\u653e\u773c\u5f53\u4eca\u6c5f\u6e56\u662f\u6570\u5f97\u7740\u7684\u540d\u5bb6\u3002\u201d\u94c1\u7fce\u9053\uff1a \u3000\u3000\u201c\u8d3e\u82f1\u4e5f\u662f\u4e2a\u5389\u5bb3\u4eba\u7269\uff0c\u4f46\u5374\u662f\u4e2a\u540d\u4e0d\u89c1\u7ecf\u4f20\u7684\u4eba\uff0c\u5982\u975e\u4ed6\u4eca\u5929\u9732\u4e86\u4e00\u624b\u2018\u8774\u8776\u7a7f\u82b1 \u6b65\u2019\uff0c\u4e5f\u4e0d\u4f1a\u5f15\u8d77\u5c5e\u4e0b\u7684\u6ce8\u610f\uff0c\u8fd9\u79cd\u5357\u8352\u5947\u6280\uff0c\u4e2d\u539f\u4e00\u5411\u5c11\u89c1\uff01\u201d \u3000\u3000\u201c\u5927\u4eba\uff0c\u4ed6\u4eec\u8fd9\u5341\u5927\u5bb6\u65cf\uff0c\u53ef\u80fd\u4e5f\u662f\u5168\u56fd\u6700\u6709\u94b1\u7684\u4eba\uff0c\u8bf1\u4eba\u89ca\u89ce\uff0c\u5728\u6240\u96be\u514d\uff0c\u4ed6\u4eec\u5bf9\u6297 \u7684\u4e0d\u662f\u4e00\u65b9\u4e00\u5730\u7684\u9f20\u7a83\u72d7\u76d7\u4e4b\u8f88\uff0c\u800c\u662f\u4e3e\u56fd\u7684\u5999\u5077\u9ad8\u624b\u3002\u201d\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u4f46\u76ee\u4e0b\u60c5\u52bf\u8be1\u5f02\uff0c \u5012\u662f\u5e0c\u671b\u4ed6\u4eec\u7684\u5b9e\u529b\u8d8a\u5f3a\u8d8a\u597d\uff0c\u5982\u80fd\u628a\u4ed6\u4eec\u4e32\u8fde\u4e00\u8d77\uff0c\u5bf9\u6297\u9b3c\u5200\uff0c\u5e94\u8be5\u662f\u5927\u6709\u53ef\u80fd\uff0c\u8fd9\u4ef6\u4e8b\uff0c \u6211\u770b\u8981\u94c1\u603b\u6355\u591a\u8d39\u4e9b\u5fc3\u601d\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u4e00\u9635\u6025\u5feb\u7684\u5954\u8dd1\u4e4b\u58f0\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u526f\u603b\u6355\u5934\u738b\u575a\u5e26\u7740\u4e00\u8eab\u98ce\u5c18\u51b2\u5165\u4e66\u623f\u4e4b\u4e2d\u3002 \u3000\u3000\u738b\u5c11\u537f\u7ad9\u8d77\u8eab\u5b50\uff0c\u5fae\u5fae\u6b20\u8eab\uff0c\u9053\uff1a\u201c\u8f9b\u82e6\u4e86\uff0c\u5148\u5750\u4e0b\u5598\u53e3\u6c14\uff0c\u4f11\u606f\u4e00\u4f1a\u3002\u201d \u3000\u3000\u77e5\u5e9c\u5927\u4eba\u5982\u6b64\u4f53\u60dc\u4e0b\u5c5e\uff0c\u5f20\u5b9d\u5584\u3001\u94c1\u7fce\uff0c\u4e5f\u4e0d\u80fd\u5750\u7740\u4e0d\u52a8\u4e86\uff0c\u9f50\u9f50\u8d77\u8eab\uff0c\u9053\uff1a\u201c\u4e00\u8def\u8f9b \u82e6\u3002\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u60ed\u6127\uff0c\u6ca1\u6709\u89c1\u5230\u675c\u53f8\u4e3b\u3002\u201d\u738b\u575a\u9053\uff1a\u201c\u636e\u53f8\u5185\u6267\u4e8b\u6797\u53f8\u6848\u8bf4\uff0c\u675c\u53f8\u4e3b\u5df2\u79bb\u5f00\u91d1\u9675 \u534a\u6708\u4e4b\u4e45\u4e86\uff1f\u201d \u3000\u3000\u201c\u53c8\u53d1\u751f\u4e86\u4ec0\u4e48\u91cd\u5927\u6848\u5b50\uff1f\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u884c\u5411\u4f55\u5904\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u6765\u4e86\u626c\u5dde\uff0c\u201d\u738b\u575a\u9053\uff1a\u201c\u6797\u53f8\u6848\u8bf4\uff0c\u4ed6\u53ea\u8eab\u4e00\u5251\uff0c\u672a\u5e26\u4ece\u5c5e\uff0c\u662f\u4e00\u6b21\u5f88\u673a\u5bc6\u7684 \u884c\u52a8\uff0c\u4f9d\u7167\u5211\u90e8\u603b\u6355\u53f8\u7684\u89c4\u5b9a\uff0c\u6bcf\u9694\u534a\u6708\u8981\u4f20\u56de\u4e00\u6b21\u8baf\u606f\uff0c\u675c\u53f8\u4e3b\u5df2\u7ecf\u903e\u8d8a\u4e86\u4e00\u6b21\u9650\u671f\uff0c\u903e \u9650\u4e09\u6b21\uff0c\u8981\u7acb\u523b\u5448\u62a5\u5211\u90e8\u603b\u6355\u53f8\uff0c\u76ee\u4e0b\u91d1\u9675\u5206\u53f8\u5df2\u96c6\u7ed3\u5927\u90e8\u5206\u597d\u624b\uff0c\u7b49\u5019\u675c\u671b\u6708\u7684\u8baf\u606f\u3001\u5dee \u9063\u3002\u201d \u3000\u3000\u201c\u5230\u4e86\u626c\u5dde\uff1f\u201d\u94c1\u7fce\u5fc3\u4e2d\u5fd6\u9053\uff1a\u6211\u5728\u626c\u5dde\u8bbe\u4e0b\u4e25\u5bc6\u7684\u76d1\u89c6\u7f51\uff0c\u8fdb\u5165\u626c\u5dde\u57ce\u4e2d\uff0c\u5f88\u96be\u907f\u5f00\uff0c \u5982\u82e5\u675c\u671b\u6708\u548c\u9b3c\u5200\uff0c\u90fd\u8fdb\u5165\u4e86\u626c\u5dde\u57ce\uff0c\u6211\u600e\u7684\u5f97\u4e0d\u5230\u4e00\u70b9\u8baf\u606f\uff0c\u96be\u9053\u8fd9\u4e9b\u82e6\u5fc3\u5e03\u8bbe\u7684\u7f51\u8def\uff0c \u7adf\u7136\u5168\u65e0\u4f5c\u7528\uff1f\u90a3\u9b3c\u5200\u672c\u5c31\u795e\u79d8\u96be\u6d4b\uff0c\u884c\u8e2a\u98d8\u5ffd\uff0c\u96be\u4ee5\u6d4b\u5ea6\uff0c\u675c\u671b\u6708\u7adf\u4e5f\u80fd\u5728\u626c\u5dde\u57ce\u4e2d\u6f5c\u8e2a \u9690\u5f62\uff0c\u8f7b\u6613\u5730\u907f\u5f00\u4e86\u6211\u91cd\u91cd\u57cb\u4f0f\u548c\u76d1\u89c6\uff0c\u6c5f\u5357\u540d\u6355\u4e4b\u80fd\uff0c\u679c\u662f\u975e\u540c\u5c0f\u53ef\u4e86\u3002 \u3000\u3000\u201c\u672c\u5e9c\u4e2d\u8fd9\u4ef6\u8840\u6848\uff0c\u6848\u53d1\u4e0d\u8fc7\u4e00\u65e5\uff0c\u6c5f\u5357\u5211\u6355\u5206\u53f8\u60f3\u5fc5\u8fd8\u4e0d\u77e5\u9053\u6b64\u4e8b\u5427\uff1f\u201d\u738b\u5c11\u537f\u9053\uff1a \u201c\u4ed6\u4eec\u62c6\u9605\u4e86\u5448\u6587\u4e4b\u540e\uff0c\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6797\u53f8\u6848\u770b\u8fc7\u5448\u6587\u4e4b\u540e\uff0c\u53ea\u8bf4\u4e86\u4e00\u53e5\u6765\u7684\u597d\u5feb\u3002\u201d\u738b\u575a\u9053\uff1a\u201c\u542c\u53e3\u6c14\u4ed6\u4f3c\u5df2\u7ecf\u5f97\u5230\u4e86\u8baf \u606f\uff0c\u4ed6\u672a\u7ec6\u8bf4\uff0c\u5c5e\u4e0b\u4e5f\u672a\u591a\u95ee\uff01\u201d \u3000\u3000\u201c\u4e1c\u5357\u897f\u5317\u56db\u5927\u5211\u6355\u5206\u53f8\uff0c\u867d\u4ee5\u4fa6\u5bdf\u91cd\u5927\u5211\u6848\u4e3a\u4e3b\uff0c\u201d\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u4f46\u4ea6\u6697\u8d1f\u67e5\u8bbf\u5404\u5730 \u5b98\u540f\u653f\u7ee9\u4e4b\u8d23\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u738b\u575a\uff0c\u6797\u53f8\u6848\u5bf9\u6211\u4eec\u5448\u62a5\u7684\u8840\u6848\uff0c\u5982\u4f55\u6279\u590d\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8bf4\uff0c\u675c\u53f8\u4e3b\u4e00\u6709\u6d88\u606f\uff0c\u7acb\u523b\u628a\u5448\u6587\u8f6c\u4ea4\u8fc7\u53bb\u3002\u201d\u738b\u575a\u9053\uff1a\u201c\u5c5e\u4e0b\u66fe\u7ecf\u8ffd\u95ee\u8fc7\u4e00\u53e5\uff0c \u675c\u671b\u6708\u662f\u5426\u5df2\u5728\u626c\u5dde\uff1f\u5bf9\u65b9\u65e0\u6cd5\u80af\u5b9a\u56de\u7b54\uff0c\u53ea\u8bf4\u675c\u53f8\u4e3b\u8fd9\u6837\u4ea4\u4ee3\u8fc7\uff0c\u8981\u53bb\u626c\u5dde\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\uff0c\u8bf7\u65e9\u4e9b\u4f11\u606f\uff0c\u675c\u671b\u6708\u6765\u5f97\u5982\u6b64\u795e\u79d8\uff0c\u4e5f\u8bb8\u65e9\u5df2\u5f97\u5230\u4e86\u4ec0\u4e48\u6d88\u606f\u3002\u201d\u94c1\u7fce\u9053\uff1a \u201c\u81ea\u4ece\u65b0\u4efb\u5211\u90e8\u603b\u6355\u5934\u4e0a\u4efb\u4e4b\u540e\uff0c\u6210\u7acb\u5211\u6355\u603b\u53f8\uff0c\u8865\u5f3a\u4e86\u4eba\u624b\uff0c\u4e5f\u6269\u5927\u4e86\u6743\u8d23\uff0c\u4e1c\u3001\u5357\u3001\u897f\u3001 \u4e2d\u56db\u5927\u5206\u53f8\uff0c\u76f8\u7ee7\u6210\u7acb\uff0c\u638c\u63a7\u4e86\u5168\u56fd\u5211\u6848\uff0c\u8ffd\u7f09\u51f6\u624b\uff0c\u7834\u4e86\u4e0d\u5c11\u79ef\u6848\uff0c\u5c5e\u4e0b\u51c6\u5907\u6697\u4e2d\u8d70\u8bbf\u4e00 \u4e0b\uff0c\u5e03\u7f72\u7684\u7ebf\u6c11\u7f51\u8def\uff0c\u5c31\u7b97\u627e\u4e0d\u51fa\u9b3c\u5200\uff0c\u4e5f\u8981\u628a\u675c\u671b\u6708\u7684\u884c\u8e2a\u627e\u51fa\u6765\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u522b\u5fd8\u4e86\u548c\u5929\u67ab\u9053\u957f\u63a5\u89e6\u7684\u4e8b\uff0c\u91d1\u767e\u5e74\u62e5\u6709\u7684\u529b\u91cf\u5f88\u5f3a\u5927\u3002\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u77e5\u9053\uff0c\u5e0c\u671b\u5929\u67ab\u9053\u957f\uff0c\u51ed\u85c9\u5728\u6c5f\u6e56\u4e0a\u7684\u58f0\u671b\uff0c\u80fd\u53f7\u53ec\u5341\u5927\u5bb6\u65cf\u4e2d\u7684\u6b66\u5e08\u4eec\u548c\u4ed6\u5bc6 \u5207\u5408\u4f5c\u3002\u201d\u94c1\u7fce\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u6211\u5148\u544a\u9000\uff0c\u4e00\u6709\u6d88\u606f\uff0c\u7acb\u523b\u56de\u62a5\u3002\u201d\u8f6c\u8eab\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u671b\u7740\u94c1\u7fce\u7684\u80cc\u5f71\uff0c\u738b\u5c11\u537f\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u738b\u526f\u603b\u6355\u5934\uff0c\u98ce\u5c18\u672a\u5378\uff0c\u4e5f\u8bf7\u4f60\u4f11\u606f\u4e00\u4e0b\uff0c\u518d \u6253\u8d77\u7cbe\u795e\uff0c\u8944\u52a9\u94c1\u5934\u513f\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u5c5e\u4e0b\u544a\u9000\u3002\u201d \u3000\u3000\u201c\u5b9d\u5584\uff0c\u201d\u738b\u5c11\u537f\u76ee\u7779\u738b\u575a\u7684\u80cc\u5f71\u8fdc\u53bb\uff0c\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u76ee\u4e0b\u5904\u5883\uff0c\u54b1\u4eec\u5e94\u8be5\u5982\u4f55\u884c \u52a8\uff1f\u201d \u3000\u3000\u201c\u7b49\u4e00\u7b49\u673a\u4f1a\u3002\u201d\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u94c1\u7fce\u8fd9\u4e24\u5e74\u6765\uff0c\u529e\u4e86\u4e0d\u5c11\u6848\uff0c\u675c\u671b\u6708\u7684\u884c\u8e2a\uff0c\u94c1\u7fce\u4e5f\u65e0 \u6cd5\u638c\u63e1\uff0c\u8fd9\u4e24\u4ef6\u4e8b\uff0c\u5bf9\u4ed6\u4fe1\u5fc3\u6298\u4f24\u5f88\u5927\u3002\u5176\u5b9e\uff0c\u4e5f\u5e76\u975e\u5168\u65e0\u5934\u7eea\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u8bf4\u8bf4\u770b\uff1f\u4ec0\u4e48\u5934\u7eea\uff1f\u201d\u738b\u77e5\u5e9c\u5fcd\u4e0d\u4f4f\u95ee\u4e86\u4e00\u53e5\u3002 \u3000\u3000\u201c\u7ebf\u7d22\u8fd8\u5728\u91d1\u767e\u5e74\u7684\u8eab\u4e0a\uff0c\u90a3\u795e\u79d8\u4eba\u7269\u65e2\u7136\u5f00\u4e86\u53e3\uff0c\u5c82\u80fd\u8f7b\u6613\u9000\u907f\u3002\u201d\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u91d1 \u5bb6\u73cd\u85cf\u7684\u4e09\u5b9d\uff0c\u77f3\u73e0\u3001\u5b9d\u5251\u3001\u8001\u4eba\u53c2\uff0c\u5f88\u53ef\u80fd\u5f15\u8bf1\u9b3c\u5200\u884c\u52a8\uff0c\u5728\u90a3\u91cc\u5e03\u4e0a\u4e86\u5929\u7f57\u5730\u7f51\uff0c\u5e94\u8be5 \u6709\u5f88\u5927\u7b49\u5230\u4ed6\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u201c\u9ad8\u660e\u554a\uff01\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u6211\u7acb\u523b\u9063\u4eba\u627e\u56de\u94c1\u7fce\uff01\u201d \u3000\u3000\u201c\u6162\u6162\u6162\uff0c\u5927\u4eba\uff0c\u8fd9\u4ef6\u4e8b\u4e0d\u80fd\u5f20\u626c\u3002\u201d\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u5c31\u7b97\u5728\u91d1\u5e9c\u4e2d\u8bbe\u4e0b\u4e86\u5341\u9762\u57cb\u4f0f\uff0c\u4e5f \u8981\u5b89\u6392\u5f97\u58f0\u606f\u4e0d\u95fb\uff0c\u7ea4\u5c18\u4e0d\u626c\uff0c\u624d\u80fd\u5f15\u4eba\u5165\u4f0f\u3002\u4eba\u624b\u5982\u4f55\u8c03\u914d\uff0c\u4e5f\u8981\u94c1\u7fce\u8d39\u756a\u5fc3\u601d\uff0c\u8981\u80fd\u8bef \u5bfc\u654c\u4eba\u7684\u5224\u65ad\uff0c\u4f46\u4e5f\u8981\u5f15\u8d77\u654c\u4eba\u5173\u5fc3\uff0c\u8fd9\u65b9\u9762\uff0c\u6050\u6015\u8fd8\u8981\u52b3\u52a8\u5927\u4eba\u4eb2\u81ea\u51fa\u9a6c\u624d\u6210\u3002\u201d \u3000\u3000\u201c\u53ea\u8981\u6211\u80fd\u51fa\u4e0a\u529b\uff0c\u5b9a\u5f53\u5168\u529b\u4ee5\u8d74\u3002\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u8981\u6211\u4f5c\u4e9b\u4ec0\u4e48\uff0c\u4e14\u53ea\u7ba1\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u6211\u76f8\u4fe1\u91d1\u767e\u5e74\u5df2\u542f\u52a8\u4e86\u5f3a\u70c8\u7684\u597d\u5947\u4e4b\u5fc3\uff0c\u4f46\u5927\u4eba\u4e0d\u5728\u73b0\u573a\uff0c\u662f\u5426\u4f1a\u5f71\u54cd\u5230\u4ed6\u5207\u5f00\u6c34\u706b \u76f8\u6d4e\u77f3\u73e0\u7684\u51b3\u5fc3\uff0c\u8fd8\u5f88\u96be\u6599\u3002\u201d\u5f20\u5b9d\u5584\u4f38\u51fa\u624b\u6307\u62c8\u7740\u5c71\u7f8a\u80e1\u5b50\uff0c\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u518d\u8bf4\u4ee5 \u5927\u4eba\u5b66\u8bc6\u7684\u6e0a\u535a\uff0c\u770b\u4e00\u770b\u5207\u5f00\u7684\u77f3\u73e0\uff0c\u4e5f\u8bb8\u4f1a\u522b\u6709\u6240\u5f97\u5462\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u70b9\u70b9\uff0c\u9053\uff1a\u201c\u660e\u5929\u6211\u53bb\u62dc\u8bbf\u91d1\u767e\u5e74\uff0c\u5e0c\u671b\u80fd\u589e\u957f\u4e00\u4e9b\u89c1\u95fb\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\uff0c\u501f\u673a\u8981\u6c42\u91d1\u767e\u5e74\u3001\u94c1\u603b\u6355\u5934\u914d\u5408\uff0c\u5c3d\u91cf\u9080\u8bf7\u9ad8\u624b\u5728\u5e9c\u4e2d\u57cb\u4f0f\uff0c\u8fd9\u79cd\u4e8b\u4e0d\u5b9c\u4eba\u591a\uff0c \u5374\u8981\u6b66\u529f\u9ad8\u5f3a\uff0c\u201d\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u522b\u62c5\u5fc3\u91d1\u767e\u5e74\u82b1\u53bb\u5927\u628a\u94f6\u5b50\uff0c\u8fd9\u79cd\u8c6a\u5bcc\u76d0\u5546\uff0c\u805a\u96c6\u4e4b\u4e30\uff0c\u5b9e \u975e\u6211\u4eec\u6240\u80fd\u60f3\u50cf\uff0c\u624b\u6bb5\u4e4b\u5e7f\uff0c\u4e5f\u975e\u6211\u4eec\u6240\u4e86\u89e3\uff0c\u5927\u4eba\u4e0d\u7528\u5b58\u4f53\u6064\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u4f1a\u89c1\u673a\u884c\u4e8b\u3002\u201d \u3000\u3000\u94c1\u7fce\u5fc3\u4e2d\u6709\u4e9b\u4e0d\u660e\u767d\uff0c\u4e3a\u4ec0\u4e48\u7cbe\u5fc3\u5b89\u6392\u7684\u7ebf\u6c11\u6697\u693f\uff0c\u7adf\u7136\u672a\u53d1\u6325\u4e00\u70b9\u6548\u7528\u3002\u4ee5\u9b3c\u5200\u7684\u795e \u79d8\uff0c\u9003\u907f\u4ed6\u8fd9\u4e9b\u76d1\u89c6\uff0c\u8fd8\u53ef\u4ee5\u5fcd\u53d7\uff0c\u5927\u540d\u9f0e\u9f0e\u7684\u675c\u671b\u6708\u7adf\u7136\u4e5f\u907f\u8fc7\u4e86\u8fd9\u4e9b\u82e6\u5fc3\u5e03\u7f72\u7684\u76d1\u89c6\uff0c \u94c1\u7fce\u5fc3\u4e2d\u5c31\u4e0d\u670d\u6c14\u4e86\uff0c\u4ed6\u8981\u67e5\u660e\u539f\u56e0\u4f55\u5728\u3002 \u3000\u3000\u81ea\u51fa\u4efb\u626c\u5dde\u5e9c\u7684\u603b\u6355\u5934\u4ee5\u6765\uff0c\u94c1\u7fce\u4ece\u672a\u5355\u72ec\u4e00\u4e2a\u4eba\u5728\u57ce\u4e2d\u901b\u8857\uff0c\u4eca\u591c\uff0c\u4e0d\u4f46\u4e00\u4e2a\u4eba\u72ec\u81ea \u884c\u52a8\uff0c\u800c\u4e14\u4e5f\u6539\u4e86\u88c5\u675f\uff0c\u9752\u7ef8\u957f\u886b\uff0c\u798f\u5b50\u5c65\uff0c\u4e00\u90e8\u82b1\u767d\u8fde\u9b13\u5927\u957f\u9aef\uff0c\u4e0d\u4f46\u5f62\u50cf\u6539\u53d8\uff0c\u8fde\u4e00\u5f20 \u8138\u4e5f\u88ab\u8fde\u53d1\u80e1\u5b50\u906e\u8d77\u6765\uff0c\u5c31\u7b97\u662f\u957f\u5e74\u8ddf\u4ed6\u7684\u4eba\u4e5f\u5f88\u96be\u8ba4\u5f97\u51fa\u6765\u3002 \u3000\u3000\u662f\u82b1\u706f\u521d\u4e0a\u7684\u65f6\u523b\uff0c\u626c\u5dde\u591a\u59ff\u591a\u91c7\u7684\u591c\u751f\u6d3b\uff0c\u4e5f\u6b63\u5f0f\u5c55\u5f00\u3002 \u3000\u3000\u94c1\u7fce\u901b\u8fc7\u4e86\u4e24\u6761\u8857\uff0c\u7a81\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u5230\u54ea\u53bb\u5462\uff1f\u8fd9\u8eab\u5bcc\u8c6a\u7fc1\u7684\u6253\u626e\uff0c\u4e0d\u50cf\u662f\u901b\u8857\u7684\u4eba\u3002 \u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u5df2\u5f15\u8d77\u4e86\u522b\u7684\u6ce8\u610f\uff0c\u6709\u4eba\u5728\u6697\u4e2d\u76d1\u89c6\u4ed6\u4e86\u3002 \u3000\u3000\u4ed6\u53d1\u89c9\u4e86\u90a3\u4e9b\u90fd\u662f\u4ed6\u5e03\u7f6e\u7684\u6697\u693f\u3001\u7ebf\u6c11\uff0c\u5fc3\u751f\u597d\u7b11\uff0c\u8fd9\u4e9b\u5e03\u7f6e\u7684\u6548\u7528\u4e0d\u9519\uff0c\u4e3a\u4f55\u627e\u4e0d\u5230 \u675c\u671b\u6708\u548c\u9b3c\u5200\u7684\u8e2a\u8ff9\uff0c\u6211\u8fd9\u4e2a\u5e03\u6869\u4eba\uff0c\u4e00\u51fa\u73b0\u5c31\u88ab\u4ed6\u4eec\u76ef\u4e0a\u4e86\uff0c\u8fd9\u6bdb\u75c5\u7a76\u7adf\u51fa\u5728\u4ec0\u4e48\u5730\u65b9\uff1f \u3000\u3000\u94c1\u7fce\u60f3\u7684\u5934\u90fd\u6709\u70b9\u75db\u4e86\uff0c\u4f46\u4ecd\u7136\u60f3\u4e0d\u51fa\u539f\u56e0\u4f55\u5728\u3002\u56db\u4e0b\u5f20\u671b\uff0c\u76ef\u4e0a\u6765\u7684\u6697\u6869\uff0c\u90fd\u5df2\u6563\u53bb\uff0c \u8fd9\u4e5f\u6b63\u662f\u4ed6\u548c\u5404\u65b9\u7ebf\u6c11\u7684\u7ea6\u5b9a\uff0c\u53d1\u89c9\u4e86\u53ef\u7591\u4eba\u7269\uff0c\u7acb\u523b\u8f6c\u62a5\u4e0a\u53bb\uff0c\u9664\u7559\u4e0b\u4e00\u4e24\u4e2a\u6700\u9690\u79d8\u7684\u6869 \u811a\u76d1\u89c6\u4e4b\u5916\uff0c\u5176\u4ed6\u4eba\u5c3d\u5feb\u6563\u53bb\uff0c\u4ee5\u514d\u5f15\u4eba\u52a8\u7591\u3002 \u3000\u3000\u81ea\u5e03\u4e0b\u8fd9\u4e9b\u6697\u6869\u3001\u57cb\u4f0f\u4e4b\u540e\uff0c\u94c1\u7fce\u8fd8\u6ca1\u6709\u63a5\u5230\u8fc7\u5448\u62a5\uff0c\u4eca\u591c\u4f3c\u662f\u5e94\u8be5\u6709\u8baf\u606f\u4e0a\u62a5\uff0c\u53ef\u662f \u94c1\u7fce\u5374\u4e0d\u5728\u77e5\u5e9c\u8859\u95e8\uff0c\u60f3\u4e00\u60f3\u5f88\u597d\u7b11\uff0c\u7b2c\u4e00\u4e2a\u88ab\u5b9a\u4e3a\u53ef\u7591\u7684\u4eba\u7269\uff0c\u7adf\u662f\u4ed6\u8fd9\u4e2a\u5b89\u6869\u5e03\u7ebf\u7684\u626c \u5dde\u5e9c\u8859\u603b\u6355\u5934\u3002 \u3000\u3000\u80fd\u5f15\u8d77\u6697\u6869\u7684\u6000\u7591\uff0c\u8db3\u8bc1\u660e\u8fd9\u756a\u6613\u5bb9\u6539\u88c5\u7684\u624b\u6cd5\u4e0d\u9519\uff0c\u675c\u671b\u6708\u5982\u662f\u6709\u5907\u800c\u6765\uff0c\u53ea\u8981\u7528\u70b9 \u6613\u5bb9\u5316\u88c5\uff0c\u4ee5\u626c\u5dde\u7684\u7e41\u534e\uff0c\u4eba\u5982\u6f6e\u6c34\u3001\u63a5\u8e35\u64e6\u80a9\uff0c\u627e\u4e00\u4e2a\u675c\u671b\u6708\u8c08\u4f55\u5bb9\u6613\uff1f\u65e2\u7136\u51fa\u6765\u4e86\uff0c\u4f55 \u4e0d\u8d70\u8bbf\u4e00\u4e9b\u9152\u697c\u540d\u5e97\uff0c\u78b0\u78b0\u8fd0\u6c14\u3002 \u3000\u3000\u4f46\u5230\u54ea\u91cc\u5462\uff1f \u3000\u3000\u4ed6\u867d\u662f\u7ef4\u62a4\u626c\u5dde\u6cbb\u5b89\u7684\u9996\u8111\u4eba\u7269\uff0c\u4f46\u5374\u5f88\u5c11\u51fa\u6e38\uff0c\u626c\u5dde\u57ce\u4e2d\u54ea\u597d\u73a9\uff1f\u771f\u7684\u4e00\u65e0\u6240\u77e5\u3002 \u3000\u3000\u4fe1\u6b65\u884c\u5165\u4e86\u4e00\u95f4\u9152\u697c\uff0c\u770b\u5ea7\u65e0\u865a\u5e2d\uff0c\u751f\u610f\u6781\u597d\u3002 \u3000\u3000\u4e00\u4e2a\u5e97\u5c0f\u4e8c\u8fce\u4e86\u4e0a\u6765\uff0c\u5e26\u94c1\u7fce\u5728\u4e00\u5904\u89d2\u843d\u7684\u5ba2\u4f4d\u5750\u4e0b\uff0c\u9053\uff1a\u201c\u5927\u7237\uff0c\u8981\u5403\u70b9\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u914d\u56db\u6837\u4f73\u80b4\uff0c\u4e00\u58f6\u8001\u9152\uff0c\u9152\u8981\u597d\u9152\uff0c\u83dc\u8981\u53ef\u53e3\u3002\u201d\u94c1\u7fce\u9053\uff1a\u201c\u8d35\u4e00\u70b9\u4e5f\u4e0d\u8981\u7d27\u3002\u201d \u3000\u3000\u201c\u5ba2\u5b98\u653e\u5fc3\uff01\u9152\u83dc\u5982\u4e0d\u53ef\u53e3\uff0c\u600e\u4f1a\u5ea7\u65e0\u865a\u5e2d\uff0c\u518d\u665a\u7247\u523b\uff0c\u5c31\u5f88\u96be\u627e\u5230\u4e00\u4e2a\u5ea7\u4f4d\u4e86\u3002\u201d\u5e97 \u5c0f\u4e8c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u770b\u6837\u5b50\u5ba2\u5b98\u4f3c\u662f\u521d\u6765\u626c\u5dde\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u94c1\u7fce\u63a5\u9053\uff1a\u201c\u4e45\u95fb\u626c\u5dde\u7e41\u534e\u70ed\u95f9\uff0c\u4f3c\u662f\u540d\u4e0d\u865a\u4f20\uff0c\u4f46\u4e0d\u77e5\u8fd9\u7f8e\u9152\u4f73\u80b4\u4e4b\u5916\uff0c\u8fd8 \u6709\u4ec0\u4e48\u597d\u73a9\u7684\u6240\u5728\uff1f\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u626c\u5dde\u57ce\u96c6\u5929\u4e0b\u58f0\u8272\u4e4b\u6700\uff0c\u597d\u73a9\u7684\u5730\u65b9\u5f88\u591a\uff0c\u4e0d\u8fc7\u2026\u2026\u201d\u5e97\u5c0f\u4e8c\u6253\u91cf\u4e86\u94c1\u7fce \u4e00\u9635\uff0c\u770b\u4ed6\u7a7f\u7740\u4e0d\u9519\uff0c\u624d\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u597d\u73a9\u3001\u65b0\u5947\u7684\u6240\u5728\uff0c\u94f6\u5b50\u5c31\u5f97\u82b1\u8d39\u591a\u4e9b\uff0c\u5f53\u7136\uff0c\u4f60\u5982 \u6709\u672c\u9886\uff0c\u4e5f\u53ef\u4ee5\u767d\u5403\u767d\u73a9\uff0c\u4e00\u4e2a\u94dc\u677f\u4e5f\u4e0d\u7528\u82b1\uff01\u201d \u3000\u3000\u201c\u6709\u8fd9\u7b49\u597d\u5730\u65b9\uff0c\u5c82\u53ef\u9519\u8fc7\uff0c\u4f60\u4f19\u8ba1\u6307\u70b9\u4e00\u4e0b\uff0c\u90a3\u662f\u4e2a\u4ec0\u4e48\u6240\u5728\uff1f\u201d\u94c1\u7fce\u4e5f\u660e\u767d\u5982\u4e0d\u82b1 \u70b9\u5c0f\u94b1\uff0c\u5f88\u96be\u95ee\u51fa\u771f\u76f8\uff0c\u638f\u51fa\u4e00\u5757\u788e\u94f6\u5b50\uff0c\u9053\uff1a\u201c\u90a3\u5730\u65b9\u5728\u2026\u2026\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u719f\u7ec3\u53c8\u5feb\u901f\u5730\u63a5\u8fc7\u94f6\u5b50\uff0c\u6536\u5165\u6000\u4e2d\uff0c\u9053\uff1a\u201c\u4e0d\u8fdc\uff0c\u4e0d\u8fdc\uff0c\u8f6c\u8fc7\u4e24\u4e2a\u8857\u53e3\u5c31\u662f\uff0c\u90a3 \u91cc\u53eb\u4f5c\u56db\u51e4\u697c\uff0c\u8fdb\u95e8\u5c31\u8981\u5341\u4e24\u94f6\u5b50\uff0c\u662f\u4e00\u5904\u975e\u5e38\u9ad8\u96c5\u7684\u5730\u65b9\uff0c\u4f46\u5982\u6ca1\u6709\u70b9\u6587\u624d\u6b66\u529f\uff0c\u6700\u597d\u522b \u53bb\uff0c\u53bb\u4e86\u4e5f\u662f\u767d\u9001\u94f6\u5b50\u3002\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u6211\u53ea\u662f\u53bb\u82b1\u94f6\u5b50\u627e\u4e50\u5b50\uff0c\u53ef\u4e0d\u662f\u53bb\u8003\u4e3e\u4eba\uff0c\u8981\u8bfb\u4e07\u5377\u4e66\uff0c\u7ec3\u5b83\u5341\u5e74\u6b66\u529f\uff0c\u8fd9 \u7b49\u5730\u65b9\uff0c\u96be\u9053\u4e5f\u7528\u5f97\u7740\u6587\u7ae0\u6b66\u529f\uff1f\u201d \u3000\u3000\u201c\u767e\u8d27\u5356\u767e\u5ba2\u5440\uff01\u4e0d\u4f46\u6709\u4eba\u53bb\u73a9\uff0c\u800c\u4e14\uff0c\u751f\u610f\u597d\u7684\u90aa\u6c14\u3002\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u7a76\u7adf\u6709\u4ec0\u4e48\u52a8 \u4eba\u4e4b\u5904\uff0c\u4f60\u8001\u5982\u4e0d\u80af\u82b1\u5341\u4e24\u94f6\u5b50\uff0c\u53bb\u5f00\u5f00\u773c\u754c\uff0c\u53ea\u6015\u5f88\u96be\u8bf4\u5f97\u6e05\u695a\u4e86\u3002\u201d\u4e00\u8f6c\u8eab\u53bb\u62db\u547c\u522b\u7684 \u5ba2\u4eba\u4e86\u3002 \u3000\u3000\u94c1\u7fce\u5fae\u5fae\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a\u8fd9\u5c0f\u5b50\u62ff\u4e86\u94f6\u5b50\uff0c\u5374\u53c8\u8bf4\u4e0d\u51fa\u4e00\u4e2a\u540d\u5802\u51fa\u6765\uff0c\u4f46\u5165\u95e8\u5c31\u8981\u5341\u4e24\u94f6 \u5b50\uff0c\u4e0d\u662f\u4e00\u822c\u4eba\u6240\u80fd\u8d1f\u62c5\u7684\uff0c\u7a76\u7adf\u662f\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u6240\u5728\u5462\uff1f\u73a9\u4e50\u8981\u7528\u5230\u6587\u624d\u3001\u6b66\u529f\uff0c\u53ef\u662f\u4ece \u6240\u672a\u95fb\u8fc7\u7684\u4e8b\uff0c\u503c\u5f97\u4e00\u63a2\u5185\u60c5\u3002 \u3000\u3000\u738b\u5c11\u537f\u4fbf\u670d\u7b80\u4ece\uff0c\u53ea\u5e26\u4e86\u4e00\u4e2a\u4fdd\u9556\u4f55\u5927\u5149\uff0c\u5b89\u6b65\u5f53\u8f66\uff0c\u5728\u843d\u65e5\u4f59\u8f89\u4e2d\u8d76\u5230\u4e86\u91d1\u767e\u5e74\u7684\u5e9c \u4e2d\u3002 \u3000\u3000\u51fa\u6765\u8fce\u5ba2\u7684\u662f\u603b\u7ba1\u8d3e\u82f1\u3002\u8d3e\u603b\u7ba1\u8033\u806a\u76ee\u654f\uff0c\u4e00\u773c\u5c31\u770b\u51fa\u4e86\u738b\u77e5\u5e9c\u7684\u8eab\u4efd\uff0c\u4e5f\u4e0d\u591a\u95ee\uff0c\u5c31 \u628a\u738b\u5c11\u537f\u63a5\u5165\u91d1\u767e\u5e74\u7684\u4e66\u623f\u4e2d\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5915\u9633\u5df2\u5c3d\uff0c\u591c\u5e55\u4f4e\u5782\uff0c\u738b\u5c11\u537f\u4ee5\u77e5\u5e9c\u4e4b\u5c0a\uff0c\u591c\u8bbf\u91d1\u5e9c\uff0c\u662f\u628a\u4ea4\u60c5\u6446\u5728\u4e86\u53f0\u9762\u4e0a\u3002 \u91d1\u767e\u5e74\u8fd8\u771f\u6709\u70b9\u53d7\u5ba0\u82e5\u60ca\uff0c\u9063\u5f00\u8eab\u8fb9\u7684\u4ece\u536b\uff0c\u5728\u4e66\u623f\u5916\u9762\u8b66\u6212\uff0c\u623f\u4e2d\u7559\u4e0b\u8d3e\u82f1\u966a\u5ba2\uff0c\u540c\u65f6\uff0c \u4e66\u623f\u4e2d\u4e5f\u638c\u8d77\u706f\u706b\uff0c\u4e00\u7247\u5149\u660e\u3002 \u3000\u3000\u201c\u5927\u4eba\u6298\u8282\u4e0b\u4ea4\uff0c\u591c\u8bbf\u91d1\u5e9c\uff0c\u8001\u673d\u751a\u611f\u8363\u5ba0\u3002\u201d\u91d1\u767e\u5e74\u5927\u6982\u662f\u771f\u7684\u611f\u52a8\u4e86\uff0c\u4e00\u64a9\u888d\u89d2\uff0c \u7adf\u7136\u51c6\u5907\u5927\u793c\u62dc\u89c1\u738b\u77e5\u5e9c\u4e86\u3002 \u3000\u3000\u738b\u5c11\u537f\u6025\u6025\u4f38\u51fa\u53cc\u624b\uff0c\u62e6\u4f4f\u4e86\u91d1\u767e\u5e74\uff0c\u9053\uff1a\u201c\u91d1\u8001\uff0c\u8fd9\u5c31\u89c1\u5916\u4e86\uff01\u4e0b\u5b98\u662f\u8bda\u5fc3\u7ed3\u4ea4\uff0c\u4e3a \u4e86\u91d1\u5c0f\u59d0\u7684\u51a4\u5c48\uff0c\u4e5f\u4e3a\u4e86\u626c\u5dde\u57ce\u4e2d\u5341\u5927\u5bb6\u65cf\u3002\u201d \u3000\u3000\u6276\u7740\u91d1\u767e\u5e74\u843d\u4e86\u5ea7\u4f4d\uff0c\u624d\u5728\u5bf9\u9762\u5750\u4e0b\u3002 \u3000\u3000\u5f53\u5b98\u7684\u505a\u4f5c\u8d77\u6765\uff0c\u6bd4\u8d77\u6c5f\u6e56\u4eba\u8fdc\u8981\u9ad8\u660e\uff0c\u91d1\u767e\u5e74\u611f\u52a8\u7684\u6709\u70b9\u70ed\u6cea\u76c8\u7736\u4e86\uff0c\u91d1\u5c0f\u7709\u662f\u4ed6\u6d3b \u5728\u4eba\u95f4\u7684\u5feb\u4e50\u6240\u501a\uff0c\u738b\u77e5\u5e9c\u51fb\u4e2d\u8981\u5bb3\u4e86\u3002 \u3000\u3000\u201c\u5927\u4eba\uff0c\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u5341\u5bb6\u8c6a\u5bcc\u4e2d\uff0c\u6709\u516b\u5bb6\u613f\u51fa\u5168\u529b\uff0c\u5171\u8944\u76db\u4e3e\uff0c\u53ea\u6709\u4e00\u5bb6\u4e3b\u5f20\u81ea\u626b \u95e8\u524d\u96ea\uff0c\u4e0d\u7ba1\u4ed6\u4eba\u74e6\u4e0a\u971c\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u91d1\u8001\uff0c\u8fd9\u662f\u5341\u5927\u5bb6\u65cf\u7684\u52ab\u96be\uff0c\u4e0d\u662f\u6551\u4eba\u662f\u81ea\u6551\u3002\u201d \u3000\u3000\u201c\u8001\u673d\u4e5f\u662f\u8fd9\u4e48\u8bf4\uff0c\u4f46\u4ed6\u5f88\u56fa\u6267\u3002\u201d\u91d1\u767e\u5e74\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u8bf4\u7b49\u4e8b\u5230\u4e34\u5934\u518d\u8bf4\u4e0d\u8fdf\uff0c \u6211\u4eec\u8bdd\u4e0d\u6295\u673a\uff0c\u4ed6\u8d77\u8eab\u544a\u8f9e\uff0c\u8001\u673d\u4e5f\u672a\u7559\u5ba2\uff0c\u5c31\u8fd9\u6837\u4e00\u62cd\u4e24\u6563\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u54ea\u4e00\u5bb6\uff1f\u5728\u626c\u5dde\u5341\u5927\u8c6a\u5bcc\u4e4b\u5bb6\uff0c\u6392\u540d\u5982\u4f55\uff1f\u201d\u738b\u77e5\u5e9c\u95ee\u9053\uff1a\u201c\u95e8\u4e0b\u62a4\u9662\u3001\u98df\u5ba2\u4e2d\uff0c \u662f\u5426\u6709\u6770\u51fa\u7684\u9ad8\u624b\u3001\u5947\u4eba\uff1f\u201d \u3000\u3000\u201c\u626c\u5dde\u5341\u5927\u8c6a\u5bcc\u5bb6\u65cf\uff0c\u6ca1\u6709\u6b63\u5f0f\u7684\u6392\u540d\uff0c\u4f46\u5f7c\u6b64\u4e4b\u95f4\uff0c\u90fd\u5728\u6697\u4e2d\u63a2\u67e5\u5bf9\u65b9\u7684\u8d22\u5bcc\u53d8\u8fc1\u3002\u201d \u91d1\u767e\u5e74\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u5341\u5927\u5bcc\u8c6a\u5bb6\u65cf\u5f88\u5c11\u5f80\u6765\u7684\u539f\u56e0\uff0c\u9664\u975e\u91cd\u5927\u4e8b\u6545\uff0c\u6bcf\u5e74\u53ea\u6709\u4e00\u6b21\u76f8\u4e92\u62dc\u5e74 \u7684\u6765\u5f80\uff0c\u4f46\u5bf9\u5f7c\u6b64\u95f4\u8d22\u5bcc\u805a\u96c6\u90fd\u5f88\u7559\u5fc3\uff0c\u867d\u975e\u5341\u5206\u4e86\u89e3\uff0c\u4f46\u5927\u7ea6\u60c5\u52bf\uff0c\u603b\u6709\u4e00\u4e2a\u4f30\u7b97\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u5fc3\u4e2d\u5fd6\u9053\uff1a\u539f\u6765\u8fd9\u5341\u5927\u5bcc\u8c6a\u5bb6\u65cf\uff0c\u5f7c\u6b64\u4e4b\u95f4\u4e5f\u5728\u52fe\u5fc3\u6597\u89d2\uff0c\u8c01\u8981\u662f\u6709\u673a\u4f1a\uff0c\u5c31\u4f1a \u541e\u6389\u5bf9\u65b9\uff0c\u52ff\u602a\u4ed6\u4eec\u4e0d\u60dc\u91cd\u91d1\uff0c\u8058\u8bf7\u9ad8\u624b\u62a4\u9662\uff0c\u4ee5\u4fdd\u62a4\u8d22\u5bcc\u5b89\u5168\u3002 \u3000\u3000\u91d1\u767e\u5e74\u6c89\u601d\u4e86\u597d\u4e00\u9635\uff0c\u624d\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u90a3\u4eba\u53eb\u59da\u987a\u5929\uff0c\u5728\u5341\u5927\u5bcc\u8c6a\u4e2d\uff0c\u6392\u540d\u5728\u4e8c\u4e09\u4e4b \u95f4\uff0c\u95e8\u4e0b\u7684\u6b66\u5e08\u4e0d\u591a\uff0c\u4f46\u5374\u90fd\u662f\u5f88\u6770\u51fa\u7684\u9ad8\u624b\uff0c\u662f\u4e2a\u7cbe\u4e8e\u6253\u7b97\u7684\u4eba\u7269\uff0c\u636e\u8001\u592b\u542c\u5230\u7684\u4f20\u8bf4\uff0c \u4ed6\u8058\u7ea6\u4e86\u51e0\u4f4d\u7cbe\u7814\u6bd2\u7269\u7684\u9ad8\u624b\uff0c\u7ec4\u6210\u4e00\u4e2a\u7528\u6bd2\u5c0f\u7ec4\uff0c\u9690\u85cf\u7684\u5f88\u79d8\u5bc6\u3002\u603b\u4e4b\uff0c\u8fd9\u4e2a\u4eba\u6709\u70b9\u795e\u79d8\uff0c \u80fd\u591f\u4e86\u89e3\u7684\u5c31\u662f\u53ef\u4ee5\u770b\u5230\u7684\u90e8\u5206\uff0c\u4f46\u4ed6\u771f\u6b63\u7684\u5b9e\u529b\uff0c\u53ea\u6709\u4ed6\u672c\u4eba\u624d\u4f1a\u660e\u767d\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u5fc3\u4e2d\u5fd6\u9053\uff1a\u626c\u5dde\u5341\u5927\u8c6a\u5bcc\uff0c\u4e0d\u4f46\u5f7c\u6b64\u9632\u8303\u5bf9\u65b9\uff0c\u800c\u4e14\u5404\u9690\u5b9e\u529b\uff0c\u91d1\u767e\u5e74\u88ab\u79f0\u4e3a\u626c \u5dde\u7b2c\u4e00\u5bcc\u8c6a\uff0c\u53ea\u6015\u4e5f\u9690\u85cf\u4e86\u4e0d\u5c11\u672a\u4e3a\u4eba\u77e5\u7684\u795e\u79d8\u529b\u91cf\u3002 \u3000\u3000\u738b\u5c11\u537f\u9707\u60ca\u4e86\uff0c\u8fd9\u5341\u5927\u5bb6\u65cf\uff0c\u7a76\u7adf\u6709\u591a\u5c11\u8d22\u5bcc\uff1f \u3000\u3000\u5e9c\u4e2d\u7f51\u7f57\u4e86\u591a\u5c11\u6c5f\u6e56\u9ad8\u624b\uff1f\u9664\u4e86\u8d29\u5356\u79c1\u76d0\u4e4b\u5916\uff0c\u8fd8\u4f5c\u4e9b\u4ec0\u4e48\u4e70\u5356\uff1f\u5982\u4e0d\u80fd\u67e5\u4e2a\u660e\u767d\uff0c\u626c \u5dde\u5e9c\u6cbb\uff0c\u5c31\u6c38\u65e0\u6e05\u660e\u4e4b\u65e5\u3002 \u3000\u3000\u4e09\u5e74\u77e5\u5e9c\uff0c\u5bf9\u8fd9\u5ea7\u540d\u51a0\u5168\u56fd\u7684\u7e41\u534e\u4e4b\u57ce\uff0c\u4e86\u89e3\u7684\u7adf\u662f\u5982\u6b64\u7684\u80a4\u6d45\uff0c\u60f3\u6765\uff0c\u771f\u662f\u60ed\u6127\uff01\u60ed \u6127\uff01 \u3000\u3000\u60ed\u6127\u5f52\u60ed\u6127\uff0c\u4f46\u738b\u77e5\u5e9c\u4ecd\u4fdd\u6301\u4e86\u9ad8\u5ea6\u7684\u8b66\u89c9\uff0c\u91d1\u5458\u5916\u5e74\u8001\u6210\u7cbe\uff0c\u7edd\u4e0d\u80fd\u5f15\u8d77\u4ed6\u5fc3\u4e2d\u7684\u6000 \u7591\u3002\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u6de1\u6de1\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u626c\u5dde\u57ce\u4e2d\u5341\u5927\u8c6a\u5bcc\uff0c\u59da\u987a\u5929\u4e00\u5bb6\u4e0d\u5408\u4f5c\uff0c\u4e5f\u65e0\u5bb3\u5927\u5c40\uff0c \u4e0d\u7528\u7406\u4f1a\u4ed6\u4e86\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u4f3c\u60f3\u5f00\u53e3\uff0c\u4f46\u5374\u6b32\u8a00\u53c8\u6b62\uff0c\u738b\u77e5\u5e9c\u5df2\u8f6c\u4e86\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u91d1\u8001\uff0c\u90a3\u679a\u6c34\u706b\u76f8\u6d4e\u7684\u77f3 \u73e0\u5185\uff0c\u7a76\u7adf\u662f\u4e9b\u4ec0\u4e48\u5185\u5bb9\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u673d\u8fd8\u672a\u5207\u5f00\u77a7\u770b\uff0c\u5927\u4eba\u6765\u7684\u8fd8\u597d\uff0c\u5f53\u9762\u5207\u5f00\uff0c\u6b64\u7269\u5982\u975e\u4eba\u5de5 \u5408\u6210\uff0c\u5b9a\u6709\u5b83\u7684\u4ef7\u503c\u5999\u7528\uff0c\u8bbe\u975e\u5927\u4eba\u6e0a\u535a\u7684\u5b66\u95ee\uff0c\u6050\u6015\u4e5f\u65e0\u6cd5\u89e3\u5f00\u4e2a\u4e2d\u4e4b\u79d8\u5bc6\u3002\u201d \u3000\u3000\u201c\u91d1\u8001\u8fc7\u5956\u4e86\u3002\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u9b3c\u5200\u6307\u540d\u8981\u6c34\u706b\u76f8\u6d4e\u77f3\u73e0\uff0c\u8fd9\u4e2d\u95f4\u5fc5\u6709\u7f18\u6545\uff0c\u5207\u5f00\u77a7\u77a7\uff0c \u4e5f\u8bb8\u80fd\u627e\u51fa\u539f\u56e0\uff0c\u4f46\u4e5f\u53ef\u80fd\u4e3a\u91d1\u8001\u5e26\u6765\u5371\u9669\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\u9b3c\u5200\u4f1a\u6740\u4e86\u6211\uff1f\u201d\u91d1\u767e\u5e74\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u6211\u628a\u5b9d\u5251\u3001\u77f3\u73e0\u3001\u8001\u4eba\u53c2\uff0c\u5168 \u90fd\u7ed9\u4ed6\uff0c\u4ee5\u9b3c\u5200\u4e4b\u90aa\u6076\uff0c\u4e5f\u672a\u5fc5\u4f1a\u7559\u4e0b\u6211\u7684\u8001\u547d\u3002\u4f55\u51b5\uff0c\u6211\u4e5f\u6ca1\u6709\u6253\u7b97\u7ed9\u4ed6\uff0c\u8001\u673d\u6d3b\u4e86\u8fd9\u4e00 \u5927\u628a\u5e74\u7eaa\uff0c\u5728\u91d1\u94f6\u5806\u91cc\uff0c\u6eda\u4e86\u5927\u534a\u8f88\u5b50\uff0c\u4eab\u5c3d\u4e86\u4eba\u95f4\u5c0a\u8363\uff0c\u98df\u5c3d\u4e86\u4eba\u95f4\u7f8e\u5473\uff0c\u6b7b\u800c\u4f55\u61be\uff1f\u9b3c \u5200\u73b0\u4ee5\u6b7b\u4ea1\u8981\u80c1\u8001\u592b\uff0c\u90a3\u662f\u6253\u9519\u4e3b\u610f\u4e86\uff01\u201d \u3000\u3000\u201c\u91d1\u8001\uff0c\u201d\u738b\u5c11\u537f\u63a5\u9053\uff1a\u201c\u4f60\u8eab\u4f53\u5065\u5eb7\uff0c\u7cbe\u529b\u7545\u65fa\uff0c\u518d\u6d3b\u4e0a\u5341\u5e74\u4e8c\u5341\u5e74\uff0c\u5e94\u5c5e\u6b63\u5e38\u2026\u2026\u201d \u7a81\u7136\u653e\u4f4e\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4f46\u9b3c\u5200\u7ea0\u7f20\u4e0d\u4f11\uff0c\u5012\u662f\u4e00\u4e2a\u9ebb\u70e6\uff0c\u4ee5\u91d1\u8001\u9605\u5386\u4e4b\u4e30\uff0c\u89c9\u7740\u5e94\u8be5\u5982\u4f55 \u5e94\u4ed8\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u91d1\u767e\u5e74\u6c89\u601d\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5982\u80fd\u4e00\u4e3e\u640f\u6740\uff0c\u6216\u662f\u751f\u64d2\u5b9a\u7f6a\uff0c\u65e2\u53ef\u4e3a\u6c11\u9664\u5bb3\uff0c \u4ea6\u53ef\u65ad\u7edd\u540e\u60a3\uff0c\u5927\u4eba\u4ee5\u4e3a\u7136\u5426\uff1f\u201d \u3000\u3000\u201c\u672c\u5e9c\u4ea6\u6709\u6b64\u613f\uff0c\u9664\u53bb\u4e86\u9b3c\u5200\uff0c\u4e5f\u8bb8\u53ef\u514d\u53bb\u626c\u5dde\u57ce\u4e00\u573a\u52ab\u96be\u2026\u2026\u201d\u738b\u5c11\u537f\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c \u63a5\u9053\uff1a\u201c\u4f46\u5982\u8981\u5bf9\u4ed8\u9b3c\u5200\u8fd9\u4e48\u4e00\u4e2a\u795e\u79d8\u4eba\u7269\uff0c\u53ea\u6015\u8981\u597d\u597d\u5730\u8ba1\u7b97\u4e00\u756a\uff0c\u4e0d\u53ef\u8f7b\u7387\u4ece\u4e8b\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\u8bf4\u5f97\u5bf9\uff0c\u4e00\u51fb\u4e0d\u4e2d\uff0c\u540e\u60a3\u65e0\u7a77\uff0c\u4ee5\u9b3c\u5200\u4e4b\u90aa\u6076\uff0c\u600e\u80af\u7f62\u4f11\uff1f\u91d1\u67d0\u4eba\u5c82\u4e0d\u662f\u6c38\u65e0\u5b81 \u65e5\u4e86\uff01\u4ee5\u5927\u4eba\u624d\u601d\u4e4b\u654f\uff0c\u8fd8\u671b\u5e2e\u8001\u673d\u4ee3\u7b79\u4e00\u4e2a\u5b8c\u5168\u4e4b\u7b56\u3002\u201d \u3000\u3000\u8bdd\u8bf4\u5f97\u5a49\u8f6c\u52a8\u542c\uff0c\u4f46\u9aa8\u5b50\u91cc\u70b9\u660e\u4e86\u738b\u5c11\u537f\u65e9\u6709\u8c0b\u7565\uff0c\u6211\u8fd9\u91cc\u5728\u606d\u5019\u5429\u5490\u4e86\uff0c\u679c\u7136\u662f\u8001\u5978 \u5f97\u5f88\u3002 \u3000\u3000\u738b\u5c11\u537f\u4e0d\u80fd\u518d\u88c5\u4e0b\u53bb\u4e86\uff0c\u7b11\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u529e\u6cd5\u662f\u60f3\u4e86\u4e00\u4e2a\uff0c\u6240\u4ee5\uff0c\u987e\u4e0d\u5f97\u5929\u8272\u5df2\u665a\uff0c \u6025\u6025\u5730\u8d76\u6765\u9020\u8bbf\uff0c\u6b64\u4e8b\u5148\u8981\u91d1\u8001\u540c\u610f\uff0c\u4e5f\u8981\u501f\u91cd\u91d1\u8001\u7684\u529b\u91cf\uff0c\u626c\u5dde\u5e9c\u5168\u529b\u914d\u5408\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u7b11\u4e86\uff0c\u8fd9\u9876\u9ad8\u5e3d\u5b50\uff0c\u6234\u7684\u4e0d\u7740\u75d5\u8ff9\uff0c\u542c\u5f97\u53eb\u4eba\u9ad8\u5174\uff0c\u4f46\u4e5f\u628a\u91d1\u767e\u5e74\u63a8\u4e0a\u4e86\u9996\u5f53\u5176 \u51b2\u7684\u4f4d\u7f6e\u3002 \u3000\u3000\u201c\u5927\u4eba\uff0c\u8bf7\u5429\u5490\uff01\u767e\u5e74\u5f53\u5c3d\u5168\u529b\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u4f3c\u662f\u8868\u793a\u4e86\u8fd8\u6709\u9690\u85cf\u7684\u5b9e\u529b\u672a\u51fa\u3002 \u3000\u3000\u738b\u5c11\u537f\u6697\u6697\u5fd6\u9053\uff1a\u548c\u8fd9\u7b49\u6df1\u6c89\u7684\u8001\u5978\u4eba\u7269\u4ea4\u5f80\uff0c\u8fd8\u771f\u5f97\u7528\u4e9b\u5fc3\u673a\u624d\u6210\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u4eba\u5df2\u5524\u9053\uff1a\u201c\u5148\u5207\u5f00\u77f3\u73e0\uff0c\u771f\u5047\u7686\u53ef\uff0c\u6b64\u4e8b\u4e0d\u59a8\u653e\u70b9\u98ce\u58f0\u51fa\u53bb\uff0c\u4f46\u8981\u653e\u7684\u4e0d \u7740\u75d5\u8ff9\uff0c\u4ee5\u514d\u5f15\u9b3c\u5200\u8d77\u7591\u3002\u201d \u3000\u3000\u8868\u8fbe\u65b9\u5f0f\u5341\u5206\u5a49\u8f6c\uff0c\u610f\u601d\u662f\u5207\u5f00\u4e00\u9897\u5047\u7684\u77f3\u73e0\u4e5f\u597d\uff0c\u7528\u5fc3\u662f\u5728\u8bf1\u4f7f\u9b3c\u5200\u5165\u4f0f\u3002 \u3000\u3000\u201c\u5927\u4eba\u89c1\u5916\u4e86\uff0c\u6c34\u706b\u76f8\u6d4e\u7684\u77f3\u73e0\uff0c\u53ea\u6709\u4e00\u9897\uff0c\u5c31\u7b97\u60f3\u9020\u9897\u5047\u7684\u9a97\u9a97\u9b3c\u5200\uff0c\u4e00\u65f6\u95f4\uff0c\u4e5f\u65e0 \u6cd5\u5982\u613f\u3002\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u518d\u8bf4\uff0c\u8fd9\u5bf9\u5927\u4eba\u4e5f\u4e0d\u516c\u5e73\uff0c\u8001\u673d\u81ea\u77e5\uff0c\u8bfb\u4e66\u4e0d\u591a\uff0c\u7a77\u4eca\u751f\u4e4b\u529b\uff0c\u4e5f \u96be\u89e3\u5f00\u77f3\u73e0\u4e4b\u79d8\uff0c\u6c34\u706b\u76f8\u6d4e\u77f3\u73e0\uff0c\u7a76\u7adf\u5185\u85cf\u4f55\u7269\uff0c\u8fd8\u53ea\u662f\u4e00\u9897\u5f15\u4eba\u5165\u80dc\u7684\u987d\u77f3\uff0c\u8fd8\u8981\u5927\u4eba\u8d39 \u756a\u5fc3\u601d\uff0c\u67e5\u770b\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201c\u5927\u81ea\u7136\u9020\u5316\u4e4b\u5947\uff0c\u6050\u975e\u4e0b\u5b98\u8fd9\u70b9\u5b66\u95ee\uff0c\u6240\u80fd\u4e86\u7136\uff0c\u4e0d\u8fc7\uff0c\u65e2\u7136\u9047\u5230\u4e86\uff0c\u603b\u8981\u5c3d\u756a\u5fc3 \u529b\uff0c\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u91d1\u8001\u4e5f\u4e0d\u8981\u5bc4\u671b\u592a\u6df1\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u4eba\u7a0d\u5750\u7247\u523b\uff0c\u8001\u673d\u53bb\u53bb\u5c31\u6765\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u8eab\u7ed5\u5230\u4e00\u4e2a\u5927\u4e66\u67b6\u7684\u540e\u9762\uff0c\u53ea\u542c\u4e00\u9635\u8f7b\u5fae\u7684\u8f67\u8f67\u4e4b\u58f0\u4f20\u6765\uff0c\u60f3\u662f\u91d1\u767e\u5e74\u6253\u5f00\u6697\u95e8\u673a \u5173\u3002 \u3000\u3000\u90a3\u4f3c\u662f\u4e00\u5904\u7edd\u5bf9\u673a\u5bc6\u7684\u5730\u65b9\uff0c\u8fde\u603b\u7ba1\u4e5f\u4e0d\u80fd\u968f\u884c\u62a4\u9a7e\uff0c\u6240\u4ee5\uff0c\u8d3e\u82f1\u7ad9\u7740\u672a\u52a8\u3002 \u3000\u3000\u91d1\u767e\u5e74\u53bb\u7684\u5e76\u4e0d\u592a\u4e45\uff0c\u518d\u5165\u4e66\u623f\u65f6\uff0c\u53f3\u624b\u4e2d\u591a\u4e86\u4e00\u628a\u4e00\u5c3a\u4e8c\u5bf8\u3001\u53e4\u94dc\u5251\u9798\u7684\u77ed\u5251\uff0c\u60f3\u6765\uff0c \u5c31\u662f\u95fb\u540d\u5929\u4e0b\uff0c\u4e13\u8bf8\u523a\u50da\u7684\u9c7c\u80a0\u5251\u4e86\u3002 \u3000\u3000\u5de6\u624b\u4e2d\u6258\u7740\u5f62\u5982\u5c0f\u7403\u7684\u77f3\u73e0\uff0c\u679c\u7136\u4e00\u534a\u7ea2\uff0c\u4e00\u534a\u767d\uff0c\u767d\u7684\u4f3c\u96ea\uff0c\u7ea2\u7684\u5982\u8840\uff0c\u9c9c\u8273\u593a\u76ee\u3002 \u3000\u3000\u738b\u5c11\u537f\u5b66\u8bc6\u6e0a\u535a\uff0c\u5165\u76ee\u7b2c\u4e00\u5370\u8c61\uff0c\u5c31\u611f\u89c9\u8fd9\u4e0d\u4f3c\u5927\u81ea\u7136\u5b55\u80b2\u51fa\u6765\u7684\u5947\u77f3\u5f69\u7389\uff0c\u989c\u8272\u867d\u662f \u660e\u6717\u593a\u76ee\uff0c\u4f46\u5374\u7ed9\u4eba\u4e00\u79cd\u5996\u5f02\u7684\u611f\u89c9\u3002 \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u5927\u4eba\uff0c\u5171\u9274\u8d4f\u5b9d\u5251\u5462\uff1f\u8fd8\u662f\u5148\u770b\u6c34\u706b\u76f8\u6d4e\u77f3\u73e0\uff1f\u201d \u3000\u3000\u201c\u8fd9\u77f3\u73e0\u989c\u8272\u602a\u5f02\uff0c\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u5c31\u5148\u770b\u77f3\u73e0\u5427\uff01\u201d\u65e9\u63a5\u8fc7\u624b\uff0c\u611f\u89c9\u4e2d\u8f7b\u4e86\u4e00\u4e9b\uff0c\u8fd9 \u9897\u77f3\u73e0\u4e0d\u5c0f\uff0c\u4f30\u7b97\u5e94\u8be5\u6709\u4e24\u65a4\u5de6\u53f3\uff0c\u4f46\u6382\u8fc7\u4efd\u91cf\uff0c\u53ef\u80fd\u53ea\u6709\u4e00\u65a4\u4e0a\u4e0b\uff0c\u8fd9\u662f\u4ec0\u4e48\u77f3\u8d28\uff0c\u770b\u4e0a \u53bb\u5341\u5206\u575a\u5b9e\uff0c\u4f46\u8d28\u5730\u5374\u53c8\u4e0d\u91cd\uff0c\u5fc3\u4e2d\u53c8\u591a\u4e86\u4e00\u5c42\u7591\u95ee\u3002 \u3000\u3000\u4ed4\u7ec6\u770b\u7ea2\u767d\u4ea4\u63a5\u4e4b\u5904\uff0c\u786e\u65e0\u9ecf\u63a5\u75d5\u8ff9\uff0c\u4f3c\u662f\u6d51\u7136\u5929\u6210\uff0c\u5982\u662f\u4e00\u9897\u5929\u7136\u7684\u77f3\u73e0\uff0c\u5b83\u80fd\u4ee3\u8868 \u4ec0\u4e48\u5462\uff1f \u3000\u3000\u201c\u91d1\u8001\uff0c\u201d\u738b\u5c11\u537f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5728\u6536\u8d2d\u8fd9\u9897\u77f3\u73e0\u65f6\uff0c\u5e94\u8be5\u542c\u5230\u4e00\u4e9b\u4f20\u8bf4\u624d\u5bf9\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u4eba\u679c\u7136\u9ad8\u660e\u2026\u2026\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u5f53\u65f6\uff0c\u786e\u6709\u4eba\u544a\u8bc9\u8001\u673d\uff0c\u8bf4\u8fd9\u9897\u6c34\u706b\u76f8\u6d4e\u77f3\u73e0\u4e4b\u5185\uff0c \u6709\u5ef6\u5e74\u76ca\u5bff\u4e4b\u836f\uff0c\u8001\u673d\u5c31\u4e70\u4e86\u56de\u6765\u3002\u4f46\u4ed4\u7ec6\u67e5\u770b\uff0c\u53c8\u627e\u4e0d\u51fa\u4e00\u70b9\u75d5\u8ff9\uff0c\u53ea\u597d\u6446\u5728\u5bb6\u8d4f\u73a9\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u627e\u90a3\u4eba\u95ee\u4e2a\u660e\u767d\uff1f\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u8fd9\u7b49\u5929\u6750\u5730\u5b9d\uff0c\u4eba\u524d\u5c11\u89c1\uff0c\u4e66\u4e0a\u4e5f\u5c11\u6709 \u8bb0\u8ff0\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u4eba\u65e9\u5df2\u4e0d\u77e5\u53bb\u5411\uff01\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u4eec\u5728\u626c\u5dde\u7684\u4e07\u5b9d\u658b\u4e2d\u76f8\u9047\uff0c\u4e5f\u53ea\u89c1\u8fc7 \u4e00\u9762\uff0c\u8ddd\u4eca\u5df2\u5341\u5e74\u8fb0\u5149\uff0c\u840d\u8e2a\u4e00\u805a\uff0c\u5373\u65e0\u7f18\u518d\u89c1\uff0c\u6211\u53c8\u5230\u54ea\u91cc\u627e\u4ed6\u5462\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u5fc3\u4e2d\u6709\u4e86\u4e00\u79cd\u6210\u7b97\uff0c\u6697\u5fd6\u9053\uff1a\u90a3\u4eba\u9f13\u52b1\u91d1\u767e\u5e74\u6536\u8d2d\u8fd9\u9897\u77f3\u73e0\uff0c\u53ea\u662f\u5e0c\u671b\u4ed6\u4ee3\u4e3a\u6536 \u5b58\uff0c\u4ee5\u514d\u6d41\u5931\uff0c\u4fbf\u4e8e\u65e5\u540e\u53d6\u56de\u3002\u4f46\u5341\u5e74\u6ca1\u6709\u8baf\u606f\uff0c\u90a3\u4e2a\u4eba\u662f\u4e0d\u662f\u51fa\u4e86\u610f\u5916\u5462\uff1f \u3000\u3000\u5fc3\u4e2d\u60f3\u4e86\u5f88\u591a\uff0c\u4f46\u5374\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u4f46\u8fd9\u9897\u77f3\u73e0\u7684\u4ef7\u503c\uff0c\u5374\u5df2\u53ef\u80af\u5b9a\u3002\u7f13\u7f13\u628a\u624b\u4e2d\u77f3\u73e0\uff0c \u4ea4\u8fd8\u7ed9\u91d1\u767e\u5e74\u3002 \u3000\u3000\u91d1\u767e\u5e74\u5728\u624b\u4e2d\u6382\u4e86\u4e00\u6382\uff0c\u7b11\u9053\uff1a\u201c\u8001\u673d\u8bf7\u6559\u8fc7\u4e0d\u5c11\u6536\u85cf\u5947\u77f3\u7684\u4eba\uff0c\u90fd\u65e0\u6cd5\u8bf4\u51fa\u4e2a\u6240\u4ee5\u7136 \u6765\uff0c\u5e0c\u671b\u5207\u5f00\u4e4b\u540e\uff0c\u80fd\u627e\u51fa\u5b83\u9690\u85cf\u7684\u79d8\u5bc6\u3002\u201d \u3000\u3000\u8d3e\u82f1\u53ca\u65f6\u628a\u4e00\u4ed8\u6298\u53e0\u597d\u7684\u767d\u5e03\uff0c\u653e\u5728\u4e86\u4e66\u6848\u4e0a\u3002 \u3000\u3000\u4f55\u5927\u5149\u5b88\u5728\u4e66\u623f\u7684\u95e8\u53e3\u6240\u5728\uff0c\u91d1\u5e9c\u4e2d\u7684\u6b66\u5e08\uff0c\u66f4\u662f\u5728\u4e66\u623f\u524d\u540e\uff0c\u57cb\u4f0f\u4e86\u5341\u51e0\u4e2a\u9ad8\u624b\uff0c\u9b3c \u5200\u8f7b\u800c\u6613\u4e3e\u5730\u95ef\u5165\u4e66\u623f\uff0c\u7ed9\u4e86\u603b\u7ba1\u8d3e\u82f1\u5f88\u5927\u7684\u8b66\u60d5\uff0c\u8c03\u6574\u9632\u5b88\u7684\u65b9\u5f0f\uff0c\u4ee5\u4fdd\u62a4\u91d1\u767e\u5e74\u7684\u5b89\u5168 \u4e3a\u7b2c\u4e00\u8981\u52a1\u3002\u5c3d\u7ba1\u4e66\u623f\u5916\u6212\u5907\u68ee\u4e25\uff0c\u4f46\u623f\u5185\u5374\u4ec5\u6709\u4e09\u4eba\uff0c\u6709\u4e9b\u4e8b\uff0c\u5c31\u5fc5\u987b\u603b\u7ba1\u81ea\u5df1\u52a8\u624b\u4e86\u3002 \u767d\u5e03\u6298\u53e0\u7684\u5f88\u539a\uff0c\u77f3\u73e0\u653e\u7684\u5f88\u7a33\u5b9a\u3002\u91d1\u767e\u5e74\u4e00\u6309\u673a\u7c27\uff0c\u62bd\u51fa\u4e86\u9c7c\u80a0\u5b9d\u5251\u3002 \u3000\u3000------------- \u3000\u3000\u5b64\u5251\u751f  \u626b\u63cf\u3000  \u695a\u5929\u4fa0\u5f71  OCR  \uff0c\u65e7\u96e8\u697c\u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c \u4e09 \u7ae0 \u3000\u3000\u4f46\u89c1\u5bd2\u8292\u6d41\u5f69\uff0c\u745e\u6c14\u5343\u6761\uff0c\u738b\u5c11\u537f\u7ad9\u7684\u9694\u7740\u4e00\u5f20\u6728\u6848\uff0c\u4ea6\u611f\u89c9\u5217\u4e00\u80a1\u51b7\u68ee\u7684\u5251\u6c14\uff0c\u903c\u4e86 \u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u628a\u4f20\u8bf5\u6c5f\u6e56\u7684\u540d\u5251\uff0c\u679c\u6709\u7740\u4f7f\u4eba\u52a8\u5fc3\u7684\u8bf1\u60d1\u529b\u91cf\u3002 \u3000\u3000\u91d1\u767e\u5e74\u4f3c\u662f\u65e9\u5c31\u60f3\u597d\u4e86\u4e0b\u5200\u7684\u65b9\u5411\uff0c\u7167\u7740\u7ea2\u3001\u767d\u4ea4\u63a5\u4e4b\u5904\uff0c\u4e00\u5251\u5207\u4e0b\u3002 \u3000\u3000\u77f3\u73e0\u5e94\u624b\u800c\u5f00\u3002\u738b\u5c11\u537f\u53ea\u4e00\u773c\u98de\u5c31\u770b\u7684\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u91d1\u767e\u5e74\u5374\u770b\u7684\u76b1\u8d77\u4e86\u7709\uff0c\u60f3\u4e0d\u5230\u6c34\u706b\u76f8\u6d4e\u7684\u77f3\u73e0\u5185\uff0c\u600e\u4f1a\u662f\u8fd9\u4e48\u4e00\u4e2a\u6210\u8272\uff1f \u3000\u3000\u8d3e\u82f1\u4f3c\u4e5f\u6309\u4e0d\u4f4f\u597d\u5947\u4e4b\u5fc3\uff0c\u4fa7\u5934\u770b\u4e86\u4e00\u773c\uff0c\u53ea\u662f\u90a3\u4e48\u5306\u5306\u4e00\u77a5\uff0c\u8138\u8272\u5ffd\u7136\u5927\u53d8\uff0c\u4f46\u53c8\u7acb \u523b\u7ad9\u6b63\u8eab\u8eaf\uff0c\u6697\u6697\u8c03\u6574\u547c\u5438\u3002 \u3000\u3000\u4ed6\u7ad9\u5728\u91d1\u767e\u5e74\u7684\u8eab\u540e\uff0c\u4f46\u5374\u6ca1\u6709\u9003\u8fc7\u738b\u5c11\u537f\u773c\u795e\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u738b\u5c11\u537f\u4e5f\u53ea\u80fd\u786e\u5b9a\u8fd9\u9897\u77f3\u73e0\u662f\u4eba\u5de5\u7cbe\u5bc6\u8bbe\u8ba1\u3001\u5236\u4f5c\u800c\u6210\uff0c\u81f3\u4e8e\u4e0d\u89c1\u63a5\u5408\u7684\u88c2\u75d5\uff0c \u96be\u4e0d\u5012\u738b\u5c11\u537f\uff0c\u7acb\u523b\u60f3\u5230\u4e86\u4e0a\u5143\u8282\u6eda\u5236\u6c64\u5706\u7684\u65b9\u6cd5\uff0c\u4f5c\u597d\u7684\u77f3\u73e0\uff0c\u518d\u7528\u77f3\u7c89\u6eda\u7b5b\u5916\u5f62\uff0c\u770b\u8d77 \u6765\u5c31\u5929\u8863\u65e0\u7f1d\u4e86\u3002 \u3000\u3000\u8fd9\u77f3\u73e0\u548c\u836f\u7269\u65e0\u5173\uff0c\u53ef\u80fd\u548c\u6b66\u529f\u6709\u6240\u7275\u8fde\uff0c\u6240\u4ee5\uff0c\u8d3e\u82f1\u4e00\u770b\u5c31\u61c2\u3002 \u3000\u3000\u201c\u5927\u4eba\uff0c\u8fd9\u662f\u4ec0\u4e48\u56fe\u753b\uff1f\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u5c71\u4e0d\u50cf\u5c71\uff0c\u6c34\u4e0d\u50cf\u6c34\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u50cf\u4e0d\u50cf\u4e00\u5e45\u592a\u6781\u56fe\uff1f\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u592a\u6781\u751f\u4e24\u4eea\uff0c\u8fd9\u5e45\u56fe\u6700\u5927\u7684\u7279\u8272\uff0c\u5c31\u662f\u592a\u6781\u56fe\u6b63 \u5728\u53d8\u5316\uff0c\u4e24\u4eea\u672a\u6210\uff0c\u770b\u8d77\u6765\u5c31\u6709\u4e9b\u8be1\u5f02\u4e86\u3002\u201d \u3000\u3000\u4ed6\u9690\u85cf\u4e86\u8d3e\u82f1\u7684\u9690\u79d8\uff0c\u8fd9\u4e2a\u4eba\u5982\u82e5\u5bf9\u91d1\u767e\u5e74\u7edd\u5bf9\u5fe0\u8bda\uff0c\u81ea\u7136\u4f1a\u8bf4\u51fa\u5185\u60c5\uff0c\u5982\u662f\u9690\u800c\u4e0d\u5ba3\uff0c \u90a3\u5c31\u662f\u522b\u6709\u7528\u5fc3\uff1f\u8fd9\u4e2d\u95f4\u5982\u679c\u5173\u7cfb\u91cd\u5927\u3002 \u3000\u3000\u8d3e\u82f1\u8981\u6709\u4e00\u756a\u5929\u4eba\u4ea4\u6218\uff0c\u624d\u80fd\u6709\u6240\u51b3\u5b9a\uff0c\u6240\u4ee5\uff0c\u738b\u5c11\u537f\u4e5f\u5c31\u9690\u5fcd\u4e0d\u8a00\uff0c\u4ed6\u8981\u51b7\u773c\u65c1\u89c2\u3002 \u3000\u3000\u91d1\u767e\u5e74\u5bf9\u5e9c\u4e2d\u7684\u6b66\u5e08\uff0c\u6709\u591a\u5c11\u5f71\u54cd\u80fd\u529b\uff1f \u3000\u3000\u201c\u5927\u4eba\u9ad8\u660e\uff01\u4f46\u8fd9\u5e45\u592a\u6781\u53d8\u5316\u56fe\uff0c\u65e8\u5728\u8868\u8fbe\u4ec0\u4e48\u5462\uff1f\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u5927\u4eba\u80fd\u5426\u518d\u63d0\u793a\u4e00 \u4e8c\uff1f\u201d \u3000\u3000\u201c\u662f\u8868\u793a\u4e00\u79cd\u673a\u7f18\u2026\u2026\u201d \u3000\u3000\u201c\u673a\u7f18\uff1f\u201d\u91d1\u767e\u5e74\u63a5\u9053\uff1a\u201c\u5927\u4eba\uff0c\u8fd9\u8bf4\u6cd5\u592a\u9690\u6666\u4e86\uff0c\u80fd\u4e0d\u80fd\u8bf4\u7684\u660e\u6717\u4e00\u4e9b\uff1f\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u662f\u4e00\u79cd\u6b66\u529f\uff0c\u4e5f\u53ef\u80fd\u662f\u4e00\u6279\u8d22\u5bcc\u3002\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u5982\u82e5\u80fd\u8ba9\u4eba\u4e00\u773c\u770b\u7a7f\uff0c\u90a3\u4eba\u4e5f \u521b\u5236\u4e0d\u51fa\u8fd9\u6837\u4e00\u9897\u5de7\u593a\u5929\u5de5\u7684\u77f3\u73e0\u4e86\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u8fd8\u662f\u65e0\u6cd5\u5b8c\u5168\u660e\u767d\uff0c\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u65e2\u7136\u9700\u8981\u65f6\u95f4\uff0c\u624d\u80fd\u6c42\u51fa\u5185\u60c5\uff0c\u8fd9\u5207\u5f00\u7684\u77f3 \u73e0\uff0c\u5c31\u7531\u5927\u4eba\u5e26\u56de\uff0c\u6162\u6162\u89c2\u5bdf\u5427\uff1f\u201d \u3000\u3000\u201c\u91d1\u8001\uff0c\u8fd9\u5c31\u548c\u54b1\u4eec\u8ba1\u5212\u4e0d\u7b26\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\u7684\u610f\u601d\u662f\u2026\u2026\u201d\u91d1\u767e\u5e74\u56de\u987e\u4e86\u8d3e\u82f1\u4e00\u773c\uff0c\u9053\uff1a\u201c\u603b\u7ba1\u8d3e\u82f1\uff0c\u7ecf\u7406\u91d1\u5e9c\u4e2d\u5927\u5c0f\u4e8b\u52a1\uff0c \u4e5f\u662f\u91d1\u67d0\u4eba\u7684\u4eb2\u4fe1\uff0c\u5927\u4eba\u6709\u8bdd\uff0c\u5c3d\u7ba1\u76f4\u8bf4\u3002\u201d \u3000\u3000\u201c\u5207\u5f00\u77f3\u73e0\u7684\u4e8b\uff0c\u4e0d\u59a8\u900f\u4e9b\u98ce\u58f0\uff0c\u4e0d\u8fc7\uff0c\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u8981\u5148\u628a\u57cb\u4f0f\u8bbe\u597d\uff0c\u5982\u80fd\u4e00\u4e3e\u64d2 \u4e0b\u9b3c\u5200\uff0c\u4ee5\u7edd\u540e\u60a3\uff0c\u6700\u597d\u4e0d\u8fc7\uff0c\u81f3\u5c11\uff0c\u4e5f\u8981\u5177\u6709\u9632\u6b62\u4ed6\u5728\u8fd9\u91cc\u9020\u6210\u6740\u622e\u7684\u5b9e\u529b\uff0c\u8981\u672c\u5e9c\u5982\u4f55 \u914d\u5408\uff0c\u91d1\u8001\u53ea\u7ba1\u5429\u5490\u3002\u201d \u3000\u3000\u201c\u4e0d\u6562\u5f53\uff0c\u5927\u4eba\u8a00\u91cd\u4e86\u3002\u201d\u56de\u987e\u4e86\u8d3e\u82f1\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u5929\u67ab\u9053\u957f\u51e0\u65f6\u56de\u6765\uff1f\u201d \u3000\u3000\u201c\u4eca\u591c\u4e0d\u56de\uff0c\u660e\u65e5\u5348\u65f6\u4e4b\u524d\uff0c\u5b9a\u53ef\u56de\u5e9c\u3002\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u4eca\u6668\u4f20\u6765\u6d88\u606f\uff0c\u5df2\u53d1\u73b0\u9b3c\u5200\u884c\u8e2a\uff0c \u4e0d\u8fc7\uff0c\u8fd8\u65e0\u6cd5\u8bc1\u5b9e\uff0c\u9053\u957f\u8981\u4eb2\u81ea\u51fa\u52a8\uff0c\u4e00\u67e5\u7a76\u7adf\uff0c\u5982\u679c\u987a\u5229\uff0c\u665a\u4e0a\uff0c\u5c31\u53ef\u4ee5\u7ed9\u4e1c\u4e3b\u4e00\u4e2a\u56de\u62a5 \u4e86\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\uff0c\u5929\u67ab\u9053\u957f\u5e26\u8d70\u4e86\u51e0\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u201c\u516d\u4e2a\uff0c\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u90fd\u662f\u9ad8\u624b\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u6c89\u541f\u4e00\u9635\uff0c\u9053\uff1a\u201c\u9664\u53bb\u4ed6\u4eec\u4e4b\u5916\uff0c\u4f59\u4e0b\u7684\u5b9e\u529b\uff0c\u662f\u5426\u8fd8\u80fd\u5bf9\u6297\u9b3c\u5200\uff1f\u201d \u3000\u3000\u201c\u9b3c\u5200\u7684\u6280\u827a\u5982\u4f55\uff0c\u6211\u4eec\u90fd\u672a\u89c1\u8fc7\uff0c\u65e0\u6cd5\u4f5c\u4e00\u4e2a\u51c6\u786e\u7684\u4f30\u7b97\u2026\u2026\u201d\u8d3e\u82f1\u8c28\u614e\u5730\u9053\uff1a\u201c\u5982 \u82e5\u76ee\u524d\u6df7\u8fdb\u6765\u90a3\u4e2a\u4e0d\u7537\u4e0d\u5973\u7684\u4eba\uff0c\u5c31\u662f\u9b3c\u5200\uff0c\u5c5e\u4e0b\u81ea\u4fe1\u53ef\u4ee5\u548c\u4ed6\u529b\u62fc\u4e09\u5341\u62db\u4ee5\u4e0a\uff0c\u624d\u6709\u5206\u51fa \u80dc\u8d1f\u7684\u673a\u4f1a\uff0c\u6211\u4eec\u5982\u5c3d\u51fa\u5b9e\u529b\uff0c\u4e0d\u7b97\u5929\u67ab\u9053\u957f\uff0c\u548c\u4ed6\u5e26\u8d70\u7684\u516d\u4e2a\u9ad8\u624b\uff0c\u5e94\u53ef\u5e94\u4ed8\u81ea\u5982\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u4e00\u4e0b\u5c31\u542c\u51fa\u4e86\u91cd\u70b9\uff0c\u5173\u952e\u5728\u5c3d\u51fa\u5b9e\u529b\uff0c\u5c31\u662f\u8bf4\u628a\u4e00\u4e9b\u79d8\u5bc6\u9690\u85cf\u7684\u529b\u91cf\uff0c\u4e5f\u62c9\u51fa\u62d2 \u654c\uff0c\u5c31\u7b97\u9b3c\u5200\u627e\u4e0a\u95e8\uff0c\u4e5f\u53ef\u5e94\u4ed8\u3002 \u3000\u3000\u91d1\u767e\u5e74\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u7528\u518d\u9080\u4eba\u52a9\u62f3\u4e86\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8981\u7ea6\u4eba\u52a9\u62f3\uff0c\u4e5f\u53ea\u80fd\u9080\u4e00\u4e9b\u606f\u9690\u5728\u626c\u5dde\u57ce\u90ca\u7684\u6c5f\u6e56\u9ad8\u4eba\uff0c\u81f3 \u4e8e\u5341\u5927\u5bb6\u65cf\u4e2d\u4eba\uff0c\u5c31\u7b97\u80af\u6d3e\u4eba\u6765\uff0c\u4e5f\u662f\u5bb3\u591a\u4e8e\u5229\uff0c\u4ed6\u4eec\u6d3e\u7684\u4eba\uff0c\u4e0d\u4f1a\u662f\u771f\u6b63\u7684\u9ad8\u624b\uff0c\u4f46\u4ed6\u4eec \u4f1a\u501f\u673a\u5730\u7a7f\u5802\u8fc7\u5ba4\uff0c\u81f3\u5c11\uff0c\u4f1a\u628a\u91d1\u5e9c\u4e2d\u7684\u5927\u6982\u5f62\u52bf\uff0c\u6478\u4e2a\u5341\u4e4b\u516b\u4e5d\uff01\u2026\u2026\u201d \u3000\u3000\u91d1\u767e\u5e74\u63a5\u9053\uff1a\u201c\u8d3e\u82f1\uff0c\u4f60\u662f\u8bf4\u8fd9\u626c\u5dde\u9644\u8fd1\uff0c\u8fd8\u9690\u5c45\u4e86\u4e0d\u5c11\u548c\u5341\u5927\u5bcc\u8c6a\u5168\u65e0\u5173\u7cfb\u7684\u9ad8\u624b\uff1f\u201d \u3000\u3000\u201c\u5c31\u8d3e\u67d0\u6240\u77e5\uff0c\u81f3\u5c11\u6709\u4e09\u4e2a\u9ad8\u624b\uff0c\u4e24\u4f4d\u5df2\u4f4f\u4e86\u5341\u5e74\u4ee5\u4e0a\uff0c\u53e6\u4e00\u4f4d\u524d\u5e74\u624d\u6765\uff0c\u4f4f\u4e0b\u6765\u8fd8\u4e0d \u8db3\u4e09\u5e74\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u6697\u6697\u5fd6\u9053\uff1a\u8fd9\u4e2a\u8baf\u606f\uff0c\u4e0d\u77e5\u94c1\u7fce\u662f\u5426\u77e5\u6653\uff1f\u770b\u6765\u91d1\u5e9c\u8033\u76ee\u7684\u7075\u654f\uff0c\u7edd\u4e0d\u5728\u5b98\u5e9c\u4e4b \u4e0b\u4e86\u3002 \u3000\u3000\u91d1\u767e\u5e74\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u5e9c\u4e2d\u65e2\u6709\u6297\u62d2\u9b3c\u5200\u7684\u5b9e\u529b\uff0c\u6682\u4e0d\u7528\u62db\u8bf7\u5916\u63f4\u2026\u2026\u201d \u3000\u3000\u201c\u91d1\u8001\uff0c\u201d\u738b\u5c11\u537f\u63a5\u53e3\u9053\uff1a\u201c\u8981\u672c\u5e9c\u5982\u4f55\u4f5c\u4e3a\uff0c\u4f46\u8bf7\u5429\u5490\u3002\u201d \u3000\u3000\u201c\u8d3e\u82f1\uff0c\u5927\u4eba\u4f53\u6064\uff0c\u4f60\u770b\u8981\u5982\u4f55\u501f\u91cd\u5b98\u5e9c\u529b\u91cf\uff0c\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u5c31\u5f53\u9762\u5411\u5927\u4eba\u63d0\u51fa\u6765 \u5427\u3002\u201d \u3000\u3000\u663e\u7136\uff0c\u91d1\u767e\u5e74\u5df2\u628a\u5bf9\u4ed8\u9b3c\u5200\u7684\u4e8b\uff0c\u4ea4\u7ed9\u8d3e\u82f1\u5168\u6743\u5904\u7406\u4e86\u3002 \u3000\u3000\u201c\u5927\u4eba\uff0c\u201d\u8d3e\u82f1\u8f6c\u5934\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u8bf7\u6055\u8d3e\u67d0\u653e\u80c6\u76f4\u8a00\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u4e0b\u5b98\u8fd9\u53a2\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u201c\u65e2\u662f\u57cb\u4f0f\uff0c\u81ea\u7136\u8981\u79d8\u5bc6\u884c\u52a8\uff0c\u6240\u4ee5\uff0c\u4e0d\u5b9c\u4eba\u591a\u3002\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u4f46\u8981\u6b66\u529f\u597d\uff0c\u4e5f\u8981\u80fd\u4f5c \u70b9\u4e3b\u610f\uff0c\u4ee5\u94c1\u603b\u6355\u5934\uff0c\u6700\u4e3a\u9002\u5408\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u8981\u4ed6\u5e26\u4eba\u5411\u91d1\u8001\u62a5\u5230\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\u522b\u4eba\uff0c\u5e9c\u8859\u4e2d\u4e5f\u9700\u8981\u9ad8\u4eba\u9632\u5b88\uff0c\u94c1\u603b\u6355\u4e00\u4e2a\u4eba\u8d76\u6765\u53c2\u4e0e\uff0c\u6211\u4eec\u5c31\u611f\u6fc0\u4e0d\u5c3d\u4e86\u3002\u201d \u3000\u3000\u4e00\u8d3e\u82f1\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8fd8\u8981\u94c1\u603b\u6355\u5934\u53d7\u70b9\u59d4\u5c48\uff0c\u8981\u4ed6\u6539\u626e\u6613\u5bb9\uff0c\u6f5c\u5165\u91d1\u5e9c\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u4f1a\u544a\u8bc9\u4ed6\u3002\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u6c5f\u5357\u5211\u6355\u5206\u53f8\uff0c\u5982\u82e5\u6709\u4eba\u8d76\u5230\uff0c\u53ef\u8981\u4ed6\u4eec\u548c\u91d1\u5e9c\u8054 \u7edc\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u6700\u597d\u4e0d\u8981\uff0c\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u675c\u671b\u6708\u540d\u52a8\u6c5f\u5357\uff0c\u8fdb\u5165\u91d1\u5e9c\uff0c\u4f1a\u7acb\u523b\u6210\u4e3a\u8f70\u52a8\u626c\u5dde\u7684\u5927\u4e8b\uff0c \u518d\u8bf4\u4ed6\u4eec\u81ea\u6709\u4e00\u5957\u8ffd\u6355\u4eba\u72af\u7684\u65b9\u6cd5\uff0c\u7531\u4ed6\u4eec\u81ea\u5df1\u884c\u52a8\uff0c\u4e5f\u53ef\u6536\u5206\u8fdb\u5408\u51fb\u4e4b\u6548\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u5c31\u8fd9\u4e48\u8bf4\u5b9a\u4e86\uff0c\u4e0b\u5b98\u544a\u8f9e\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u77f3\u73e0\u5df2\u7136\u524a\u5f00\uff0c\u91d1\u67d0\u4eba\u4e00\u65f6\u95f4\u770b\u4e0d\u51fa\u5b83\u7684\u4f5c\u7528\u4f55\u5728\uff1f\u5927\u4eba\u5982\u6709\u5174\u81f4\uff0c\u4f55\u59a8 \u5e26\u56de\u53bb\uff0c\u7814\u7a76\uff0c\u7814\u7a76\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u6026\u7136\u5fc3\u52a8\u4e86\uff0c\u4f46\u4ed6\u5374\u7acb\u4e88\u62d2\u7edd\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u8fd9\u9897\u77f3\u73e0\uff0c\u6216\u6709\u4f5c\u7528\uff0c\u4f46\u4e0b\u5b98\u4e5f\u77a7 \u4e0d\u51fa\u4efb\u4f55\u86db\u4e1d\u8682\u8ff9\uff0c\u4f55\u51b5\uff0c\u8fd9\u662f\u5f15\u6765\u9b3c\u5200\u7684\u8bf1\u7269\u2026\u2026\u201d\u653e\u4f4e\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u6700\u597d\u80fd\u6bd4\u7167\u539f\u4ef6\uff0c \u4f2a\u9020\u4e24\u5957\uff0c\u4ee5\u60d1\u9b3c\u5200\u8033\u76ee\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u5546\u573a\u6253\u6eda\u591a\u5e74\uff0c\u662f\u6210\u4e86\u7cbe\u7684\u72d0\u72f8\uff0c\u867d\u7136\u611f\u89c9\u5230\u738b\u77e5\u5e9c\u8a00\u672a\u5c3d\u610f\uff0c\u4f46\u5374\u672a\u591a\u95ee\uff0c\u5fc3 \u4e2d\u5374\u5df2\u5f00\u59cb\u7422\u78e8\u8bdd\u4e2d\u7684\u5ff5\u610f\u4e86\u3002 \u3000\u3000\u201c\u5927\u4eba\uff0c\u767e\u5e74\u5df2\u53eb\u4eba\u5907\u4e86\u665a\u9910\uff0c\u5927\u4eba\u5403\u8fc7\u518d\u8d70\u3002\u201d \u3000\u3000\u83dc\u4e0d\u591a\uff0c\u4f46\u5374\u90fd\u662f\u96be\u5f97\u5403\u5230\u7684\u73cd\u54c1\uff0c\u5473\u9053\u70e7\u7684\u597d\uff0c\u9152\u53c8\u662f\u7a96\u85cf\u767e\u5e74\u7684\u8001\u9152\uff0c\u738b\u77e5\u5e9c\u867d\u662f \u56db\u54c1\u7687\u5802\uff0c\u4f46\u4e5f\u6ca1\u6709\u5403\u8fc7\u5982\u6b64\u7684\u73cd\u80b4\u7f8e\u9152\uff0c\u4e00\u9910\u996d\u5403\u5b8c\uff0c\u5df2\u662f\u521d\u66f4\u65f6\u5206\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u5df2\u6709\u4e86\u4e03\u6210\u9152\u610f\uff0c\u4e5f\u62d2\u7edd\u4e86\u91d1\u767e\u5e74\u5907\u8f66\u76f8\u9001\u7684\u597d\u610f\uff0c\u5e26\u7740\u4f55\u5927\u5149\uff0c\u8e0f\u7740\u591c\u8272\u6b65\u56de \u5e9c\u8859\u3002 \u3000\u3000\u91d1\u767e\u5e74\u56de\u987e\u4e86\u8d3e\u82f1\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\uff0c\u4f60\u770b\uff0c\u738b\u5927\u4eba\u8f7b\u8f66\u7b80\u4ece\uff0c\u8bbf\u95ee\u91d1\u5e9c\u8fd9\u6863\u4e8b\uff0c\u662f \u5426\u5df2\u6cc4\u6f0f\u51fa\u53bb\uff1f\u201d \u3000\u3000\u201c\u5982\u82e5\u9b3c\u5200\u5df2\u6697\u4e2d\u76d1\u89c6\u54b1\u4eec\uff0c\u77e5\u5e9c\u591c\u8bbf\u4e4b\u4e8b\uff0c\u7edd\u5bf9\u9003\u4e0d\u8fc7\u4ed6\u7684\u773c\u7ebf\u3002\u9664\u4e86\u9b3c\u5200\u4e4b\u5916\uff0c\u53ea \u6015\u5341\u5927\u8c6a\u95e8\uff0c\u4e5f\u6d3e\u6709\u6697\u693f\u76d1\u89c6\u3002\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u4e0d\u80fd\u5927\u610f\uff0c\u4f55\u51b5\uff0c\u738b\u5927\u4eba\u53ea\u5e26\u4e86\u4e00\u4e2a\u4ece \u536b\uff0c\u4eba\u624b\u4e5f\u5355\u8584\u4e86\u4e9b\uff0c\u5e9c\u8859\u8ddd\u6b64\u867d\u7136\u4e0d\u8fdc\uff0c\u4f46\u8981\u7ecf\u8fc7\u4e00\u6bb5\u6768\u67f3\u6cb3\u5cb8\uff0c\u6811\u4e0a\u3001\u821f\u4e2d\uff0c\u90fd\u662f\u9690\u85cf \u6740\u624b\u7684\u597d\u5730\u65b9\u3002\u201d \u3000\u3000\u201c\u5341\u5927\u5bcc\u8c6a\u4e4b\u5bb6\uff0c\u5927\u90e8\u5206\u548c\u91d1\u5e9c\u8fd8\u80fd\u7ef4\u6301\u8868\u9762\u4ea4\u60c5\uff0c\u4f46\u59da\u987a\u5929\u5374\u4e0d\u80fd\u4e0d\u9632\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4f60\u7acb\u523b\u6d3e\u4eba\u6697\u4e2d\u4fdd\u62a4\u738b\u5927\u4eba\uff0c\u8981\u51fa\u52a8\u9ad8\u624b\uff0c\u8981\u4e0d\u7740\u75d5\u8ff9\uff0c\u5982\u82e5\u65e0\u4eba\u60ca\u6270\uff0c \u4e5f\u522b\u8ba9\u738b\u77e5\u5e9c\u53d1\u89c9\u8fd9\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u660e\u767d\u3002\u201d\u8d3e\u82f1\u4eba\u672a\u79bb\u4e66\u623f\u4e00\u6b65\uff0c\u6765\u5230\u95e8\u53e3\uff0c\u4f4e\u58f0\u5429\u5490\u51e0\u53e5\u8bdd\uff0c\u53c8\u9000\u56de\u5230\u91d1\u5458\u5916\u7684 \u8eab\u4fa7\u3002 \u3000\u3000\u5bf9\u8d3e\u82f1\u7684\u8c28\u614e\u5fe0\u5b9e\uff0c\u91d1\u767e\u5e74\u5341\u5206\u6ee1\u610f\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u6709\u4e86\u70b9\u9152\u610f\uff0c\u60f3\u65e9\u4e9b\u4f11\u606f\uff0c \u4f60\u4e5f\u4e0d\u8981\u592a\u75b2\u52b3\uff0c\u8fd1\u65e5\u91cc\u4e8b\u52a1\u7e41\u591a\uff0c\u4f60\u8981\u591a\u591a\u4fdd\u91cd\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u4e1c\u4e3b\u5173\u6000\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u91d1\u767e\u5e74\u672a\u96e8\u7ef8\u7f2a\uff0c\u4f4f\u5728\u4e00\u5e62\u7279\u522b\u8bbe\u8ba1\u7684\u623f\u5b50\u4e2d\uff0c\u91cd\u95e8\u53e0\u6237\uff0c\u673a\u5173\u5bc6\u5e03\uff0c\u9664\u4ed6\u4e4b\u5916\uff0c \u4edf\u4f55\u4eba\u90fd\u4e0d\u4e86\u89e3\u64cd\u7eb5\u95e8\u5f00\u542f\u5173\u95ed\u4e4b\u6cd5\u3002 \u3000\u3000\u8fce\u7740\u591c\u6765\u7684\u51c9\u98ce\u4e00\u5439\uff0c\u738b\u5c11\u537f\u9152\u610f\u9192\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u62ac\u5934\u770b\uff0c\u6d6e\u4e91\u63a9\u6708\uff0c\u7e41\u661f\u95ea\u70c1\uff0c\u56db\u5904\u4e00\u7247\u9759\u5bc2\uff0c\u8fd9\u672c\u662f\u4e00\u5904\u4e2d\u4e0a\u4eba\u5bb6\u7684\u4f4f\u5b85\u533a\uff0c\u5165\u591c\u4e4b \u540e\uff0c\u884c\u4eba\u7a00\u5c11\uff0c\u4e00\u4fa7\u5782\u67f3\u98d8\u98ce\uff0c\u5c0f\u821f\u9a70\u8fc7\uff0c\u5076\u800c\u4f20\u6765\u6728\u6868\u6253\u6c34\u4e4b\u58f0\u3002 \u3000\u3000\u5728\u7e41\u534e\u4f3c\u9526\u7684\u626c\u5dde\u57ce\u4e2d\uff0c\u5f88\u96be\u5f97\u6709\u8fd9\u4e48\u4e00\u5904\u5e7d\u9759\u7684\u6240\u5728\uff0c\u738b\u77e5\u5e9c\u987f\u6709\u7740\u5077\u5f97\u6d6e\u751f\u534a\u65e5\u95f2 \u7684\u8212\u7545\u3002 \u3000\u3000\u4f46\u4f55\u5927\u5149\uff0c\u5374\u662f\u7d27\u5f20\u7684\u624b\u5fc3\u90fd\u6ea2\u51fa\u4e86\u6c57\u6c34\uff0c\u5168\u795e\u51dd\u805a\uff0c\u76ee\u5149\u5982\u9e70\uff0c\u76ef\u5728\u6cb3\u5cb8\u4e0a\u7684\u6768\u67f3\u6811 \u4e0a\u3002 \u3000\u3000\u738b\u5c11\u537f\u5012\u662f\u4e0d\u592a\u7d27\u5f20\uff0c\u4ed6\u7814\u5224\u60c5\u52bf\uff0c\u9b3c\u5200\u672a\u5f97\u4e09\u5b9d\u4e4b\u524d\uff0c\u4e0d\u4f1a\u5bf9\u4ed6\u4e0b\u624b\u3002 \u3000\u3000\u5341\u5927\u8c6a\u95e8\u4e2d\u4eba\uff0c\u90fd\u672a\u53d7\u5230\u4efb\u4f55\u7684\u635f\u5bb3\uff0c\u4e5f\u4e0d\u4f1a\u7518\u5192\u706d\u95e8\u6284\u5bb6\u7684\u5371\u9669\uff0c\u6740\u5b98\u9020\u53cd\u3002 \u3000\u3000\u6b63\u60f3\u62db\u547c\u4f55\u5927\u5149\uff0c\u8981\u4ed6\u8f7b\u677e\u4e00\u4e9b\uff0c\u7a81\u7136\u4f55\u5927\u5149\u6c89\u58f0\u559d\u9053\uff1a\u201c\u9f20\u8f88\u6562\u5c14\u3002\u201d\u5bd2\u5149\u95ea\u52a8\uff0c\u4f55 \u5927\u5149\u5df2\u6296\u51fa\u8170\u95ee\u94a2\u5200\uff0c\u95ea\u7535\u51fb\u51fa\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u58f0\u91d1\u94c1\u4ea4\u545c\uff0c\u4e00\u679a\u98de\u9556\uff0c\u5df2\u88ab\u51fb\u843d\u7740\u5730\uff0c\u4f55\u5927\u5149\u75be\u8de8\u4e24\u6b65\uff0c\u4eba\u5df2\u6321\u5728\u4e86\u738b\u5c11\u537f\u7684 \u8eab\u524d\u3002 \u3000\u3000\u4ed6\u4e00\u76f4\u5728\u5168\u795e\u6212\u5907\u4e4b\u4e2d\uff0c\u53cd\u5e94\u5feb\u901f\u65e0\u5339\u3002 \u3000\u3000\u738b\u5c11\u537f\u5446\u4f4f\u4e86\uff0c\u770b\u91d1\u9556\u957f\u903e\u4e03\u5bf8\uff0c\u662f\u5c5e\u4e8e\u5de8\u578b\u7684\u6697\u5668\uff0c\u5982\u88ab\u5c04\u4e2d\uff0c\u8db3\u4ee5\u81f4\u547d\uff0c\u6697\u6697\u5fd6\u9053\uff1a \u4f1a\u662f\u8c01\u5462\uff1f\u6c5f\u6e56\u4eba\u7269\u884c\u4e8b\u7684\u4e56\u623e\uff0c\u771f\u662f\u975e\u5e38\u60c5\u53ef\u4ee5\u6d4b\u5ea6\u4e86\u3002\u5fc3\u4e2d\u5ffd\u7136\u6709\u70b9\u540e\u6094\uff0c\u4e0d\u8be5\u5a49\u62d2\u91d1 \u767e\u5e74\u6d3e\u9063\u8f66\u62a4\u9001\u4e86\u3002 \u3000\u3000\u65e0\u4eba\u56de\u7b54\u4f55\u5927\u5149\uff0c\u4f46\u4f55\u5927\u5149\u5374\u4e0d\u677e\u61c8\uff0c\u51dd\u795e\u8083\u7acb\uff0c\u4e24\u9053\u76ee\u5149\u76ef\u4f4f\u5728\u4e08\u4f59\u5916\u6cb3\u5cb8\u65c1\u4e00\u682a\u9ad8 \u5927\u7684\u6768\u67f3\u6811\u4e0a\u3002 \u3000\u3000\u660e\u660e\u53d1\u89c9\u4e86\u654c\u4eba\u5c31\u9690\u8eab\u5728\u90a3\u682a\u6768\u67f3\u6811\u4e0a\uff0c\u4f46\u5374\u4e0d\u80fd\u8ffd\u8fc7\u53bb\u903c\u654c\u73b0\u8eab\uff0c\u4ed6\u660e\u767d\uff0c\u4e00\u79bb\u5f00\u738b \u5c11\u537f\u7684\u8eab\u524d\u5de6\u53f3\uff0c\u5c31\u53ef\u5f15\u6765\u654c\u4eba\u5bf9\u738b\u77e5\u5e9c\u7684\u653b\u88ad\uff0c\u5982\u662f\u654c\u4eba\u6709\u4e24\u4e2a\u6740\u624b\uff0c\u518d\u60f3\u56de\u6551\uff0c\u6050\u6015\u5c31 \u63aa\u624b\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u4f55\u5927\u5149\u51fb\u843d\u654c\u4eba\u4e00\u679a\u98de\u9556\uff0c\u5df2\u611f\u89c9\u5230\u5bf9\u65b9\u53d1\u9556\u529b\u9053\u5f88\u5f3a\uff0c\u5355\u662f\u8fd9\u98de\u9556\u7684\u5077\u88ad\uff0c\u738b\u5c11\u537f\u5c31 \u65e0\u80fd\u5bf9\u4ed8\uff0c\u6240\u4ee5\uff0c\u4e0d\u6562\u79bb\u5f00\u3002 \u3000\u3000\u6765\u4eba\u4e5f\u51fa\u4e4e\u610f\u5916\u7684\u6c89\u7740\uff0c\u9759\u9759\u9690\u5728\u6811\u4e0a\uff0c\u4e0d\u8a00\u4e0d\u52a8\u3002 \u3000\u3000\u201c\u5927\u4eba\uff0c\u201d\u4f55\u5927\u5149\u53cc\u76ee\u4ecd\u7136\u76ef\u5728\u90a3\u6768\u67f3\u6811\u4e0a\uff0c\u53e3\u4e2d\u5374\u4f4e\u58f0\u5730\u8bf4\u9053\uff1a\u201c\u654c\u4eba\u5728\u5f85\u673a\u800c\u52a8\uff0c \u4e5f\u53ef\u80fd\u5728\u7b49\u5019\u63f4\u624b\uff0c\u76f8\u6301\u4e0b\u53bb\uff0c\u5bf9\u6211\u4e0d\u5229\uff0c\u6211\u62a4\u7740\u5927\u4eba\uff0c\u9000\u5230\u90a3\u6392\u6c11\u623f\u4e4b\u540e\uff0c\u7ed5\u9053\u56de\u5e9c\u3002\u201d \u3000\u3000\u201c\u4e0d\uff01\u201d\u738b\u5c11\u537f\u63a5\u9053\uff1a\u201c\u8d70\u539f\u8def\u56de\u53bb\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u575a\u5b9a\u51b3\u65ad\uff0c\u4f55\u5927\u5149\u5c31\u662f\u6709\u7406\u4e5f\u4e0d\u4fbf\u4e89\u8bba\uff0c\u9053\uff1a\u201c\u597d\u5427\uff01\u54b1\u4eec\u6162\u6162\u79fb\u52a8\uff0c\u53ea\u8981\u8d70\u8fc7 \u8fd9\u4e00\u6bb5\u6768\u67f3\u6cb3\u5cb8\uff0c\u5c31\u5bb9\u6613\u5e94\u4ed8\u4e86\u3002\u201d \u3000\u3000\u201c\u60f3\u7684\u597d\u554a\uff01\u53ef\u60dc\uff0c\u4f60\u5df2\u7ecf\u6ca1\u6709\u673a\u4f1a\u4e86\uff01\u201d\u968f\u7740\u8bed\u58f0\uff0c\u679d\u53f6\u5bc6\u8302\u5904\uff0c\u98de\u843d\u4e0b\u4e00\u6761\u4eba\u5f71\uff0c \u9ed1\u5dfe\u8499\u9762\uff0c\u9ed1\u886b\u9ed1\u88e4\uff0c\u9664\u4e86\u9732\u51fa\u4e00\u5bf9\u773c\u775b\u548c\u4e00\u53cc\u624b\u4e4b\u5916\uff0c\u5168\u90fd\u5305\u5728\u4e00\u7247\u9ed1\u5e03\u4e4b\u4e2d\uff0c\u5de6\u624b\u6a2a\u5200\uff0c \u53f3\u624b\u4e2d\u5374\u63e1\u4e86\u4e00\u679a\u98de\u9556\u3002 \u3000\u3000\u4f55\u5927\u5149\u4e0d\u6562\u5984\u52a8\u4e86\uff0c\u5728\u4e00\u4e08\u591a\u7684\u8fd1\u8ddd\u79bb\u4e2d\uff0c\u7a0d\u4e3a\u758f\u5ffd\uff0c\u5c31\u53ef\u80fd\u6709\u6551\u63f4\u4e0d\u53ca\u7684\u9057\u61be\uff0c\u6240\u4ee5\uff0c \u62b1\u5200\u8083\u7acb\u4e0d\u52a8\uff0c\u6697\u4e2d\u5374\u8fd0\u96c6\u771f\u6c14\uff0c\u51c6\u5907\u9009\u4e00\u4e2a\u6700\u4f73\u65f6\u673a\uff0c\u4f5c\u51fa\u5c3d\u5168\u529b\u7684\u4e00\u51fb\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u5df2\u5b8c\u5168\u51b7\u9759\u4e86\u4e0b\u6765\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u76ee\u6ce8\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u770b\u6837\u5b50\uff0c\u4f60\u4eec\u662f\u51b2\u7740\u6211\u6765\u4e86\uff0c \u6211\u60f3\u4e0d\u8d77\u6709\u4ec0\u4e48\u539f\u56e0\uff0c\u4f1a\u9020\u6210\u8fd9\u4e00\u573a\u622a\u6740\u7684\u51b2\u7a81\uff1f\u662f\u540d\u5229\uff1f\u8fd8\u662f\u4ec7\u6068\uff1f\u4f60\u4eec\u6c5f\u6e56\u4e2d\u4eba\uff0c\u7d20\u6709 \u6069\u6028\u5206\u660e\u7684\u8bf4\u6cd5\uff0c\u5728\u672a\u9020\u6210\u6d41\u8840\u7684\u6740\u622e\u4e4b\u524d\uff0c\u6211\u5e0c\u671b\u80fd\u660e\u767d\u7ea0\u845b\u6240\u5728\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u662f\u6c5f\u6e56\u4e2d\u4eba\uff0c\u4f46\u4ed6\u8bfb\u4e66\u4e07\u5377\uff0c\u624d\u6167\u5353\u7edd\uff0c\u80fd\u591f\u4e3e\u4e00\u53cd\u4e09\uff0c\u4e3b\u6301\u626c\u5dde\u5e9c\u653f\uff0c\u7ea6\u8058\u4e86\u4e0d \u5c11\u6c5f\u6e56\u9ad8\u4eba\u8944\u52a9\uff0c\u5bf9\u6c5f\u6e56\u4e8b\u7269\uff0c\u4e86\u89e3\u4e0d\u5c11\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u4e24\u9053\u76ee\u5149\u51dd\u4f4f\u5728\u738b\u5c11\u537f\u8138\u4e0a\uff0c\u7a81\u7136\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e00\u4e2a\u597d\u5b98\uff0c\u53ef\u60dc\uff0c\u6211\u4e0d \u80fd\u653e\u4f60\u4e00\u9a6c\uff0c\u4f55\u5927\u5149\u7684\u5200\u6cd5\u5f88\u51cc\u5389\uff0c\u4f46\u4ed6\u53ea\u6709\u4e00\u4e2a\u4eba\uff0c\u6240\u4ee5\uff0c\u4ed6\u65e0\u80fd\u4fdd\u62a4\u4f60\u2026\u2026\u201d \u3000\u3000\u4f46\u95fb\u8863\u8882\u98d8\u98ce\u4e4b\u58f0\uff0c\u53c8\u4e24\u4e2a\u8eab\u7740\u9ed1\u8272\u52b2\u88c5\u7684\u5927\u6c49\uff0c\u7531\u6811\u4e0a\u98de\u8dc3\u800c\u4e0b\uff0c\u4e24\u4e08\u4ee5\u4e0a\u7684\u9ad8\u5ea6\uff0c \u843d\u5730\u5168\u65e0\u58f0\u606f\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u5175\u5203\uff0c\u5df2\u7ecf\u63e1\u5728\u4e86\u624b\u4e2d\uff0c\u4e00\u67c4\u957f\u5251\uff0c\u4e00\u628a\u539a\u80cc\u9b3c\u5934\u5200\uff0c\u4eba\u7740\u5b9e\u5730\uff0c\u5200\u5251\u90fd\u6446\u51fa\u4e86 \u653b\u51fb\u7684\u59ff\u6001\u3002 \u3000\u3000\u738b\u5c11\u537f\u6697\u6697\u5401\u4e00\u53e3\u6c14\uff0c\u5fd6\u9053\uff1a\u8fd9\u4f3c\u662f\u4e00\u573a\u5fc5\u6740\u7684\u62e6\u622a\uff0c\u5f88\u96be\u7528\u53e3\u820c\u89e3\u51b3\u4e86\u3002 \u3000\u3000\u4f55\u5927\u5149\u7a81\u7136\u53d1\u51fa\u4e86\u4e00\u58f0\u957f\u5578\uff0c\u5578\u58f0\u51c4\u5389\uff0c\u5145\u6ee1\u7740\u5fff\u6012\u548c\u65e0\u5948\uff0c\u9759\u591c\u4e2d\u4f20\u51fa\u4e86\u5f88\u8fdc\u5f88 \u8fdc\u2026\u2026\u3002 \u3000\u3000\u8fd9\u5578\u58f0\u5f88\u53ef\u80fd\u5f15\u6765\u6551\u5175\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u9b3c\u5934\u5200\u548c\u957f\u5251\u540c\u65f6\u653b\u51fa\uff0c\u5206\u5de6\u53f3\u6307\u5411\u4f55\u5927\u5149\u7684\u8981\u5bb3\u3002 \u3000\u3000\u4f55\u5927\u5149\u6325\u5200\u75be\u5c55\uff0c\u5168\u529b\u626b\u51fa\u4e00\u5200\uff0c\u91d1\u94c1\u4ea4\u9e23\u58f0\u4e2d\uff0c\u706b\u661f\u8fdb\u98de\uff0c\u7adf\u628a\u4e24\u4ef6\u5175\u5203\uff0c\u5168\u90fd\u9707\u5f00\u3002 \u3000\u3000\u597d\u51cc\u5389\u7684\u4e00\u5200\u3002 \u3000\u3000\u4f46\u4e24\u4e2a\u9ed1\u8863\u4eba\u4ea6\u975e\u5f31\u8005\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u5200\u3001\u5251\u9f50\u6325\uff0c\u5c55\u5f00\u4e86\u5f3a\u70c8\u7684\u653b\u52bf\u3002 \u3000\u3000\u4f55\u5927\u5149\u7f05\u5200\u65cb\u98de\uff0c\u529b\u654c\u4e24\u4eba\uff0c\u4ecd\u662f\u653b\u591a\u5b88\u5c11\uff0c\u4e00\u573a\u51f6\u5389\u7684\u6076\u6218\uff0c\u770b\u5f97\u738b\u5c11\u537f\u89e6\u76ee\u60ca\u5fc3\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u6700\u5148\u73b0\u8eab\u7684\u9ed1\u8863\u4eba\uff0c\u7a81\u7136\u628a\u98de\u9556\u6536\u4e86\u8d77\u6765\uff0c\u7f13\u6b65\u903c\u5411\u738b\u77e5\u5e9c\uff0c\u7b11\u9053\uff1a\u201c\u5927\u4eba\uff0c \u4f60\u592a\u5927\u610f\u4e86\uff0c\u53ea\u5e26\u4e86\u4e00\u4e2a\u4ece\u536b\uff0c\u91d1\u767e\u5e74\u6ca1\u6709\u6d3e\u51fa\u5e9c\u4e2d\u9ad8\u624b\u4fdd\u62a4\u4f60\uff0c\u66f4\u662f\u6709\u610f\u9677\u5bb3\uff0c\u4e8b\u5df2\u81f3\u6b64\uff0c \u4f60\u5c31\u8ba4\u547d\u5427\uff01\u201d\u5de6\u624b\u5355\u5200\u4e00\u4f38\uff0c\u524a\u5411\u54bd\u5589\u8981\u5bb3\u3002 \u3000\u3000\u8fd9\u662f\u8bda\u5fc3\u53d6\u547d\u7684\u4e00\u5200\u3002 \u3000\u3000\u738b\u5c11\u537f\u5fc3\u4e2d\u660e\u767d\uff0c\u7edd\u5bf9\u65e0\u80fd\u95ea\u907f\u5f00\u8fd9\u4e00\u5200\u7684\u653b\u88ad\uff0c\u7d22\u6027\u4e00\u95ed\u53cc\u76ee\u3002 \u3000\u3000\u65e2\u662f\u65e0\u80fd\u95ea\u907f\uff0c\u5c31\u675f\u624b\u5c31\u64d2\u5427\uff01\u8868\u73b0\u51fa\u4e00\u70b9\u8bfb\u4e66\u4eba\u7684\u9aa8\u6c14\u3002 \u3000\u3000\u4ed6\u5bf9\u81ea\u5df1\u7684\u9519\u8bef\u63a8\u65ad\uff0c\u81f4\u906d\u6740\u8eab\u4e4b\u7978\uff0c\u867d\u6709\u9057\u6068\uff0c\u4f46\u66f4\u4ee4\u4ed6\u96be\u8fc7\u7684\u662f\uff0c\u7adf\u7136\u60f3\u4e0d\u51fa\u51f6\u624b \u662f\u8c01\uff1f\u6740\u4ed6\u7684\u539f\u56e0\u4e3a\u4f55\uff1f\u53ef\u60dc\uff0c\u8fd9\u4ef6\u4e8b\u5df2\u7ecf\u6c38\u8fdc\u65e0\u6cd5\u660e\u767d\u4e86\u2026\u2026 \u3000\u3000\u4f46\u524a\u5411\u54bd\u5589\u7684\u4e00\u5200\uff0c\u7adf\u662f\u4e45\u5019\u4e0d\u81f3\u3002 \u3000\u3000\u7741\u773c\u770b\u53bb\uff0c\u7adf\u7136\u6709\u4e00\u53ea\u624b\uff0c\u7531\u8eab\u540e\u4f38\u4e86\u8fc7\u6765\uff0c\u7528\u5927\u62c7\u6307\u548c\u98df\u6307\uff0c\u62e6\u4f4f\u4e86\u8fd1\u5589\u7684\u98de\u5203\u3002 \u3000\u3000\u6b7b\u91cc\u9003\u751f\uff0c\u738b\u5927\u4eba\u7acb\u523b\u4e00\u4e2a\u8f6c\u8eab\uff0c\u95ea\u5f00\u4e86\u4e24\u5c3a\u3002 \u3000\u3000\u8f6c\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u90a3\u62e6\u7740\u5200\u5203\u7684\u4eba\uff0c\u7adf\u662f\u91d1\u5e9c\u603b\u7ba1\u8d3e\u82f1\u3002\u5fc3\u4e2d\u6697\u9053\uff1a\u65e9\u53d1\u89c9\u4e86\uff0c\u8fd9\u4eba\u662f\u9ad8 \u624b\uff0c\u60f3\u4e0d\u5230\u6211\u7684\u63a8\u60f3\uff0c\u7adf\u662f\u5982\u6b64\u7684\u51c6\u786e\uff0c\u4e0d\u7981\u5fae\u5fae\u4e00\u7b11\u3002 \u3000\u3000\u8d3e\u82f1\u5fae\u4e00\u6b20\u8eab\uff0c\u9053\uff1a\u201c\u8d3e\u82f1\u6551\u63f4\u6765\u8fdf\uff0c\u5927\u4eba\u53d7\u60ca\u4e86\u3002\u201d\u5fc3\u4e2d\u5374\u662f\u6697\u6697\u4f69\u670d\uff0c\u5fd6\u9053\uff1a\u8fd9\u4e2a \u6587\u5f31\u4e66\u751f\u554a\uff01\u7adf\u6709\u7740\u89c6\u6b7b\u5982\u5f52\u7684\u8c6a\u6c14\uff0c\u771f\u7684\u662f\u96be\u4e3a\u4ed6\u4e86\u3002 \u3000\u3000\u201c\u591a\u8c22\u6551\u547d\uff0c\u8d3e\u5144\u5f1f\u6765\u7684\u662f\u6070\u5230\u597d\u5904\uff01\u201d\u738b\u5927\u4eba\u8bf4\u5b8c\u8bdd\uff0c\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\u3002 \u3000\u3000\u4e0d\u53eb\u603b\u7ba1\u53eb\u5144\u5f1f\uff0c\u8d3e\u82f1\u5fc3\u4e2d\u6709\u70b9\u602a\uff0c\u4f46\u6709\u66f4\u591a\u7684\u611f\u52a8\u548c\u6e29\u6696\uff0c\u7b11\u9053\uff1a\u201c\u6211\u770b\u770b\u662f\u54ea\u4e00\u8def \u4eba\u9a6c\uff0c\u7adf\u6562\u6740\u5b98\u9020\u53cd\uff1f\u201d \u3000\u3000\u5de6\u624b\u4e00\u6df1\uff0c\u62c9\u4e0b\u4e86\u9ed1\u8863\u5927\u6c49\u7684\u8499\u9762\u9ed1\u5dfe\u3002 \u3000\u3000\u5947\u602a\uff0c\u90a3\u5927\u6c49\u6ca1\u6709\u6323\u624e\uff0c\u7adf\u7136\u8ba9\u8d3e\u82f1\u8f7b\u6613\u5f97\u624b\u3002 \u3000\u3000\u8d3e\u82f1\u770b\u6e05\u695a\u6765\u4eba\u4e4b\u540e\uff0c\u8138\u8272\u5ffd\u7136\u4e00\u53d8\uff0c\u6a2a\u884c\u4e24\u6b65\uff0c\u6321\u5728\u4e86\u738b\u5c11\u537f\u7684\u9762\u524d\uff0c\u53f3\u624b\u62e6\u7740\u7684\u5200 \u5203\uff0c\u4e5f\u540c\u65f6\u677e\u624b\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8138\u8272\u60e8\u767d\uff0c\u4f3c\u662f\u53d7\u5230\u4e86\u6781\u5927\u7684\u51b2\u51fb\uff0c\u82e6\u7b11\u4e00\u4e0b\uff0c\u4e00\u8bed\u4e0d\u53d1\uff0c\u8f6c\u8eab\u4e00\u8dc3\uff0c\u75be\u5954\u800c\u53bb\u3002 \u3000\u3000\u53e6\u5916\u4e24\u4e2a\u9ed1\u8863\u4eba\uff0c\u5200\u3001\u5251\u540c\u65f6\u52a0\u529b\uff0c\u72e0\u653b\u4e24\u62db\uff0c\u903c\u5f97\u4f55\u5927\u5149\u9000\u4e86\u4e00\u6b65\uff0c\u540c\u65f6\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u4f55\u5927\u5149\u6ca1\u6709\u8ffd\u6740\uff0c\u4ed6\u660e\u767d\uff0c\u5c31\u7b97\u8ffd\u4e0a\u53bb\uff0c\u4e5f\u672a\u5fc5\u662f\u4e24\u4eba\u5408\u640f\u7684\u5bf9\u624b\u3002 \u3000\u3000\u738b\u5c11\u537f\u4e5f\u672a\u4e0b\u4ee4\u8ffd\u6740\uff0c\u5fc3\u4e2d\u60f3\u7684\u662f\u8d3e\u82f1\u62c9\u4e0b\u90a3\u4eba\u7684\u8499\u9762\u9ed1\u5dfe\u65f6\uff0c\u90a3\u4eba\u7adf\u65e0\u80fd\u53cd\u6297\uff0c\u8d3e\u82f1 \u7528\u7684\u4ec0\u4e48\u6b66\u529f\u5462\uff1f\u62e6\u4f4f\u4e86\u654c\u4eba\u5200\u5203\uff0c\u5c31\u4f7f\u4eba\u65e0\u529b\u53cd\u51fb\uff0c\u8fd9\u6b66\u529f\uff0c\u4e5f\u592a\u53ef\u6015\u4e86\uff0c\u4eca\u591c\u4e2d\u771f\u662f\u5927 \u5f00\u773c\u754c\u3002 \u3000\u3000\u5c3d\u7ba1\u5fc3\u4e2d\u7591\u95ee\u91cd\u91cd\uff0c\u4f46\u738b\u5927\u4eba\u5374\u88c5\u51fa\u4e00\u526f\u832b\u7136\u65e0\u6240\u77e5\u7684\u8ff7\u60d8\u3002 \u3000\u3000\u4ed6\u53ea\u60f3\u591a\u4e86\u89e3\u522b\u4eba\u4e00\u4e9b\uff0c\u5374\u4e0d\u613f\u522b\u4eba\u77e5\u4ed6\u592a\u6df1\uff0c\u5c3d\u91cf\u5730\u9690\u85cf\u950b\u8292\u3002 \u3000\u3000\u5982\u8bba\u5fc3\u673a\u4e4b\u6df1\uff0c\u4f5c\u5b98\u7684\u53c8\u9ad8\u8fc7\u6c5f\u6e56\u4eba\u7269\u4e86\u3002 \u3000\u3000\u4f46\u8d3e\u82f1\u5374\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u5927\u4eba\uff0c\u4ed6\u4eec\u4e0d\u662f\u9b3c\u5200\u7684\u624b\u4e0b\uff0c\u4e5f\u4e0d\u662f\u5341\u5927\u8c6a\u95e8\u4e2d\u4eba\uff0c\u770b\u8d77\u6765\u626c \u5dde\u57ce\u98ce\u4e91\u9645\u4f1a\uff0c\u6b63\u6709\u4e00\u573a\u98ce\u66b4\u5728\u915d\u917f\u4e2d\uff0c\u5927\u4eba\u8981\u591a\u591a\u73cd\u91cd\uff0c\u6211\u9001\u5927\u4eba\u56de\u5e9c\u5427\uff01\u201d \u3000\u3000\u8868\u73b0\u51fa\u4e86\u5173\u5fc3\u4e4b\u60c5\uff0c\u5374\u672a\u628a\u4e8b\u60c5\u8bf4\u6e05\u695a\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u53e3\u9f7f\u542f\u52a8\uff0c\u6b32\u8a00\u53c8\u6b62\uff0c\u7adf\u7136\u4e5f\u5fcd\u4e0b\u53bb\u4e0d\u95ee\u4e86\u3002 \u3000\u3000\u8d3e\u82f1\u6bd5\u7adf\u53ea\u662f\u91d1\u5e9c\u4e2d\u4e00\u4e2a\u603b\u7ba1\uff0c\u4e0d\u613f\u8bf4\u7684\u592a\u591a\uff0c\u738b\u77e5\u5e9c\u4e5f\u5c31\u4e0d\u4fbf\u6df1\u7a76\u4e86\u3002 \u3000\u3000\u56de\u5230\u5e9c\u8859\uff0c\u526f\u603b\u6355\u5934\u738b\u575a\u6b63\u5e26\u7740\u4e00\u6279\u6355\u5feb\uff0c\u51c6\u5907\u51fa\u52a8\uff0c\u8d76\u5f80\u91d1\u5e9c\u63a5\u4eba\u3002 \u3000\u3000\u8d3e\u82f1\u5fae\u5fae\u9894\u9996\uff0c\u548c\u738b\u575a\u6253\u4e2a\u62db\u547c\uff0c\u4e00\u8bed\u672a\u53d1\uff0c\u8f6c\u5934\u5c31\u8d70\uff0c\u76f4\u56de\u91d1\u5e9c\u53bb\u4e86\u3002 \u3000\u3000\u738b\u5c11\u537f\u6ca1\u6709\u633d\u7559\uff0c\u53ea\u662f\u8f6c\u8fc7\u5934\uff0c\u671b\u7740\u8d3e\u82f1\u8fdc\u53bb\u7684\u80cc\u5f71\uff0c\u76f4\u5230\u8d3e\u82f1\u6d88\u901d\u4e8e\u591c\u8272\u4e4b\u4e2d\uff0c\u624d\u6b65 \u5165\u5e9c\u8859\u4e8c\u5802\u4e66\u623f\u3002 \u3000\u3000\u5211\u6848\u5e08\u7237\u5f20\u5b9d\u5584\uff0c\u6b63\u7b49\u5f97\u4e00\u8138\u7126\u6025\uff0c\u89c1\u5230\u4e86\u738b\u5c11\u537f\uff0c\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5927\u4eba\uff0c\u53ef\u662f \u4e8b\u60c5\u53d8\u5316\u7684\u51fa\u4e86\u610f\u5916\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd0\u6c14\u7b97\u4e0d\u9519\uff0c\u4f55\u5927\u5149\u62da\u547d\u82e6\u6218\uff0c\u6c5f\u6e56\u4e2d\u4eba\u7684\u884c\u4e8b\u51c6\u5219\uff0c\u4e0d\u80fd\u4ee5\u5e38 \u7406\u6d4b\u5ea6\u3002\u201d \u3000\u3000\u201c\u662f\u54ea\u8def\u4eba\u9a6c\uff1f\u201d\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u6025\u6025\u8c0b\u53d6\u5927\u4eba\uff0c\u7528\u5fc3\u4f55\u5728\u5462\uff1f\u201d \u3000\u3000\u201c\u636e\u8bf4\uff0c\u4e0d\u662f\u626c\u5dde\u5341\u5927\u8c6a\u95e8\u4e2d\u4eba\uff0c\u4e5f\u975e\u9b3c\u5200\u5c5e\u4e0b\uff0c\u4f3c\u662f\u53e6\u6709\u4e00\u80a1\u6c5f\u6e56\u52bf\u529b\uff0c\u4fb5\u5165\u4e86\u626c\u5dde\uff0c \u679c\u771f\u5982\u6b64\uff0c\u4e8b\u60c5\u5c31\u8d8a\u6765\u8d8a\u590d\u6742\u4e86\uff01\u201d \u3000\u3000\u201c\u4e0d\u8fc7\uff0c\u4f20\u8a00\u4e5f\u4e0d\u80fd\u8f7b\u4fe1\uff0c\u81f3\u5c11\u4ed6\u662f\u4e2a\u5fc3\u673a\u5f88\u6df1\u7684\u4eba\u3002\u201d\u738b\u5927\u4eba\u63a5\u9053\uff1a\u201c\u672a\u67e5\u660e\u771f\u76f8\u4e4b \u524d\uff0c\u6b64\u7b14\u53ea\u80fd\u5b58\u6863\uff0c\u4ee5\u626c\u5dde\u5e9c\u73b0\u6709\u7684\u5b9e\u529b\uff0c\u6211\u4eec\u80fd\u5bf9\u6297\u4e00\u80a1\u529b\u91cf\uff0c\u5df2\u7ecf\u4e0d\u9519\uff0c\u4f46\u5c31\u76ee\u524d\u6240\u77e5\uff0c \u626c\u5dde\u57ce\u4e2d\u81f3\u5c11\u6709\u4e09\u80a1\u4ee5\u4e0a\u7684\u7ec4\u5408\u5728\u6d3b\u52a8\uff0c\u8fd9\u8fd8\u662f\u628a\u5341\u5927\u5bcc\u8c6a\u5206\u6210\u4e24\u80a1\u52bf\u529b\uff0c\u8ba4\u771f\u7684\u8bf4\u8d77\u6765\uff0c \u4ed6\u4eec\u4e92\u4e0d\u76f8\u5c5e\uff0c\u8fde\u6a2a\u5408\u7eb5\u7684\u7ed3\u5408\uff0c\u968f\u65f6\u4f1a\u56e0\u5229\u5bb3\u6743\u8861\uff0c\u6709\u6240\u6539\u53d8\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u5927\u6c5f\u5357\u5317\uff0c\u7a76\u7adf\u6709\u591a\u5c11\u6c5f\u6e56\u9ad8\u624b\uff0c\u53c8\u6709\u591a\u5c11\u96c6\u805a\u5728\u626c\u5dde \u5e9c\u5185\uff0c\u4fa0\u4ee5\u6b66\u72af\u7981\uff0c\u8fd9\u4e9b\u4eba\uff01\u201d \u3000\u3000\u201c\u5927\u4eba\uff0c\u201d\u5f20\u5b9d\u5584\u63a5\u9053\uff1a\u201c\u4e3e\u56fd\u8d22\u5bcc\uff0c\u5927\u534a\u96c6\u4e2d\u4e8e\u6b64\uff0c\u624d\u5438\u5f15\u4e86\u5404\u65b9\u6280\u827a\u6709\u6210\u7684\u9ad8\u624b\uff0c \u4f1a\u805a\u4e8e\u65af\uff0c\u8fd9\u91cc\u8d5a\u94b1\u5bb9\u6613\u5440\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u54d1\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u662f\uff0c\u540d\u5229\u8bf1\u4eba\uff0c\u8c01\u53c8\u80fd\u771f\u6b63\u5730\u770b\u7834\u6446\u8131\uff0c\u6211\u5012\u53d8\u7684\u6709\u70b9\u75f4 \u4e86\uff0c\u5b9d\u5584\uff0c\u5c31\u76ee\u4e0b\u626c\u5dde\u60c5\u52bf\u800c\u8a00\uff0c\u6211\u4eec\u5e94\u8be5\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u56db\u4e24\u62e8\u5343\u91d1\u54ea\uff01\u5927\u4eba\u5df2\u628a\u5173\u952e\u91cd\u70b9\u79fb\u5230\u4e86\u91d1\u767e\u5e74\u7684\u8eab\u4e0a\uff0c\u201d\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u91d1\u5e9c\u4e2d\u5177\u6709 \u7684\u5b9e\u529b\uff0c\u4f3c\u4e4e\u4e5f\u80fd\u627f\u53d7\u8d77\u8fd9\u4e2a\u62c5\u5b50\uff0c\u5c31\u8ba9\u91d1\u767e\u5e74\u53bb\u5e94\u4ed8\u8fd9\u4ef6\u4e8b\u60c5\u3002\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4e0d\u592a\u597d\u5427\uff1f\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u6211\u8eab\u4e3a\u4e00\u5e9c\u4e4b\u9996\u957f\uff0c\u600e\u80fd\u7f6e\u8eab\u4e8b\u5916\uff1f\u201d \u3000\u3000\u201c\u5927\u4eba\uff0c\u91d1\u5e9c\u5578\u805a\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u5b9e\u529b\u5f3a\u8fc7\u626c\u5dde\u5e9c\u8859\uff0c\u4f55\u51b5\uff0c\u4ed6\u4eec\u51fa\u624b\uff0c\u662f\u4fdd\u62a4\u81ea\u5df1\u7684\u8d22 \u5bcc\u3001\u73cd\u5b9d\u2026\u2026\u201d\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u4ee5\u5927\u4eba\u7684\u5b98\u58f0\uff0c\u81ea\u4e5f\u4e0d\u4f1a\u5750\u89c6\uff0c\u94c1\u7fce\u5df2\u65e5\u591c\u51fa\u52a8\uff0c\u5e0c\u671b\u4ed6\u80fd\u65e9 \u65e5\u67e5\u51fa\u675c\u671b\u6708\u7684\u884c\u8e2a\uff0c\u4f1a\u5408\u5211\u90e8\u6c5f\u5357\u5206\u53f8\u7684\u4eba\uff0c\u534f\u529b\u67e5\u529e\uff0c\u624d\u662f\u4e0a\u7b56\u3002\u626c\u5dde\u5e9c\u5b9e\u4e0d\u7528\u72ec\u62c5\u98ce \u9669\uff0c\u518d\u8bf4\u4f30\u7b97\u654c\u6211\u5b9e\u529b\uff0c\u6211\u4eec\u4e5f\u6709\u529b\u4e0d\u4ece\u5fc3\u4e4b\u611f\uff0c\u8fd9\u8fd0\u7528\u4e4b\u5999\uff0c\u5c31\u5b58\u4e4e\u4e00\u5fc3\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5f20\u5211\u6848\u5b9e\u5728\u662f\u80fd\u8c0b\u5584\u65ad\u7684\u4eba\u7269\uff0c\u5206\u6790\u60c5\u52bf\uff0c\u542c\u5f97\u738b\u5c11\u537f\u8fde\u8fde\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5e0c\u671b\u94c1\u7fce\u80fd \u65e9\u4e9b\u5e26\u56de\u675c\u671b\u6708\u7684\u6d88\u606f\uff0c\u91d1\u5e9c\u4e2d\u8fd8\u8981\u4ed6\u53c2\u4e0e\u7f09\u62ff\u9b3c\u5200\u7684\u8ba1\u5212\u3002\u201d \u3000\u3000\u94c1\u7fce\u4eba\u5728\u56db\u51e4\u697c\u4e2d\u3002 \u3000\u3000\u56db\u51e4\u697c\u4e0d\u662f\u697c\uff0c\u800c\u662f\u4e00\u5ea7\u82b1\u6728\u6276\u758f\u7684\u5ead\u9662\uff0c\u95e8\u53e3\u9ad8\u6311\u7740\u4e24\u76cf\u5782\u843d\u5bab\u706f\uff0c\u8fdb\u5165\u5927\u95e8\uff0c\u662f\u4e00 \u5ea7\u8fce\u5bbe\u7684\u5927\u5385\uff0c\u5385\u5185\u70db\u706b\u8f89\u714c\uff0c\u7167\u7684\u4e00\u7247\u901a\u660e\u3002 \u3000\u3000\u8fd9\u662f\u5ea7\u7b51\u5efa\u7684\u5f88\u5947\u602a\u7684\u5385\u5802\uff0c\u4e1c\u897f\u4e24\u9762\u5404\u6709\u4e00\u4e2a\u5706\u6708\u95e8\uff0c\u95e8\u524d\u5404\u6709\u4e00\u4e9b\u957f\u5f62\u6728\u684c\uff0c\u684c\u540e \u7684\u5927\u6728\u6905\u4e0a\uff0c\u5750\u7740\u4e2a\u957f\u886b\u4e2d\u5e74\u4eba\uff0c\u4e1c\u897f\u5e03\u7f6e\u4e00\u822c\u6a21\u6837\uff0c\u53ea\u662f\u9898\u8054\u4e0d\u540c\u3002 \u3000\u3000\u4e1c\u5706\u95e8\u5199\u7684\u662f\uff0c\u201c\u7edd\u53e4\u5bb9\u8272\u82b1\u6a21\u6837\uff0c\u4e0d\u8bba\u98ce\u91c7\u8bba\u8bcd\u7ae0\u3002\u8bd7\u98ce\u5439\u5f00\u9500\u91d1\u5e10\uff0c\u4efb\u541b\u98ce\u6d41\u4efb\u541b \u72c2\u3002\u201d \u3000\u3000\u6a2a\u6279\u5199\u7684\u662f\u201c\u6587\u9601\u201d\u3002 \u3000\u3000\u94c1\u7fce\u867d\u7136\u4e5f\u8bfb\u8fc7\u51e0\u5e74\u4e66\uff0c\u4f46\u5bf9\u8bd7\u8bcd\uff0c\u5374\u8c08\u4e0d\u4e0a\u901a\u8fbe\uff0c\u518d\u8bf4\u8fd9\u7b49\u6587\u7ae0\u96c5\u4e8b\uff0c\u548c\u6c5f\u6e56\u4e2d\u5173\u8fde \u4e0d\u5927\uff0c\u5c31\u8f6c\u5411\u897f\u5706\u95e8\u884c\u53bb\u3002 \u3000\u3000\u897f\u5706\u95e8\u4e0a\u7684\u6a2a\u6279\u5199\u7740\u201c\u6b66\u9662\u201d\uff0c\u5374\u4e5f\u6709\u4e00\u5e45\u5bf9\u8054\uff0c\u5199\u7740\u201c\u5929\u751f\u4e3d\u8d28\u591a\u81ea\u5f3a\uff0c\u4e00\u821e\u5251\u6c14\u52b2 \u56db\u65b9\u3002\u6280\u827a\u62d3\u5f00\u59fb\u7f18\u8def\uff0c\u5343\u91d1\u7389\u4eba\u8fce\u65b0\u90ce\u3002\u201d \u3000\u3000\u8fd9\u9996\u8bf4\u7684\u5c31\u66f4\u660e\u767d\u4e86\uff0c\u80fd\u901a\u8fc7\u6b66\u9662\u7684\u8003\u9a8c\uff0c\u4e0d\u4f46\u6709\u7f8e\u5973\u966a\u4f8d\uff0c\u8fd8\u6709\u5343\u91d1\u76f8\u8d60\uff0c\u771f\u662f\u4eba\u8d22 \u4e24\u5f97\u3002\u4f46\u4e5f\u9690\u9690\u6709\u6240\u6697\u793a\uff0c\u8def\u9014\u591a\u8270\uff0c\u7f8e\u4eba\u5f3a\u608d\uff0c\u541b\u5b50\u81ea\u91cd\uff0c\u4e0d\u662f\u6280\u827a\u7cbe\u7edd\u8005\uff0c\u4e0d\u8981\u8f7b\u8bd5\uff0c \u4ee5\u514d\u81ea\u53d6\u5176\u8fb1\u3002 \u3000\u3000\u94c1\u7fce\u5fc3\u4e2d\u5fd6\u601d\uff1a\u660e\u660e\u662f\u4e2a\u9ad8\u7b49\u7684\u5993\u9662\uff0c\u5374\u8981\u505a\u51fa\u5982\u6b64\u591a\u7684\u82b1\u4fcf\uff0c\u96be\u9053\u662f\u522b\u6709\u7528\u5fc3\uff1f\u4e0d\u884c\uff0c \u5f97\u5e94\u8bd5\u4e00\u756a\uff0c\u4ee5\u63a2\u7a76\u7adf\u3002\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u4e3e\u6b65\u76f4\u884c\u5230\u897f\u5706\u95e8\u53e3\u3002 \u3000\u3000\u90a3\u5750\u5728\u6728\u684c\u540e\u7684\u957f\u886b\u4eba\uff0c\u7a81\u7136\u7ad9\u8d77\u4e86\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u4f60\u770b\u6e05\u695a\u58c1\u4e0a\u9898\u8bd7\u4e86\uff1f\u201d \u3000\u3000\u539f\u6765\uff0c\u94c1\u7fce\u6613\u5bb9\u6539\u88c5\uff0c\u4f7f\u81ea\u5df1\u8001\u4e86\u5f88\u591a\uff0c\u770b\u4e0a\u53bb\uff0c\u4e0d\u5230\u516d\u5341\uff0c\u4e5f\u6709\u4e94\u5341\u4e03\u516b\u7684\u5c81\u6570\u3002 \u3000\u3000\u94c1\u7fce\u5f53\u7136\u770b\u6e05\u695a\u4e86\u58c1\u4e0a\u7684\u9898\u8bd7\uff0c\u4f46\u8fd8\u672a\u53ca\u5f00\u53e3\uff0c\u90a3\u957f\u886b\u4eba\u5df2\u63a5\u9053\uff1a\u201c\u4e60\u6b66\u7684\u4eba\uff0c\u5927\u90fd\u6ca1 \u6709\u8bfb\u4e66\uff0c\u5c31\u8ba4\u8bc6\u51e0\u4e2a\u5b57\uff0c\u4e5f\u4e0d\u662f\u5f88\u4e86\u89e3\u6587\u5b57\u7ec4\u5408\u7684\u542b\u610f\uff0c\u8fd9\u4e0d\u8981\u7d27\uff0c\u6211\u53ef\u4ee5\u4ed4\u7ec6\u5730\u8bf4\u7ed9\u4f60\u542c\uff0c \u4f60\u8981\u95ee\u95ee\u6e05\u695a\uff0c\u514d\u5f97\u5931\u8d22\u4e4b\u540e\uff0c\u8fd8\u53d7\u5230\u4e00\u756a\u7f9e\u8fb1\uff0c\u5212\u4e0d\u6765\u5440\uff01\u8001\u7237\u5b50\u3002\u201d \u3000\u3000\u8a00\u4e0b\u4e4b\u610f\uff0c\u9887\u6709\u540c\u60c5\u4e4b\u5fc3\u3002 \u3000\u3000\u5927\u7ea6\uff0c\u94c1\u7fce\u7684\u8863\u7740\uff0c\u5f88\u50cf\u4e00\u4e2a\u4e61\u4e0b\u8fdb\u57ce\u7684\u571f\u8d22\u4e3b\uff0c\u6613\u5bb9\u836f\u7269\uff0c\u63a9\u53bb\u4e86\u8138\u4e0a\u7684\u7cbe\u608d\u4e4b\u6c14\uff0c \u518d\u52a0\u4e0a\u94c1\u7fce\u6709\u610f\u7684\u9690\u53bb\u76ee\u4e2d\u795e\u5149\uff0c\u770b\u4e0a\u53bb\u5c31\u50cf\u4e2a\u666e\u901a\u4eba\u4e86\uff0c\u5c31\u7b97\u662f\u5b66\u8fc7\u6b66\u529f\u5427\uff0c\u4e5f\u4e0d\u8fc7\u662f\u4e09 \u811a\u732b\u7684\u628a\u5f0f\u3002 \u3000\u3000\u94c1\u7fce\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u5c31\u7b97\u662f\u5165\u5185\u67e5\u770b\uff0c\u4e5f\u672a\u5fc5\u7531\u4ed6\u89e3\u8bf4\u7684\u6e05\u695a\u3002\u4ed6\u662f\u56db\u51e4\u697c\u4e2d\u4eba\uff0c\u8a00 \u8bed\u95ee\uff0c\u81ea\u4f1a\u900f\u9732\u51fa\u4e0d\u5c11\u5185\u60c5\uff0c\u5f53\u4e0b\u4e00\u6b20\u8eab\uff0c\u9053\uff1a\u201c\u591a\u8c22\u5173\u7167\uff0c\u8001\u673d\u8fd9\u53a2\u6d17\u8033\u606d\u542c\u4e86\u3002\u201d \u3000\u3000\u201c\u5929\u751f\u4e3d\u8d28\u591a\u81ea\u5f3a\uff0c\u90a3\u662f\u8bf4\u592a\u7f8e\u7684\u59d1\u5a18\u5bb6\uff0c\u90fd\u6709\u70b9\u5b64\u50b2\u2026\u2026\u201d\u957f\u886b\u4eba\u6447\u5934\u6643\u8111\u5730\u9053\uff1a \u201c\u4e00\u821e\u5251\u6c14\u52a8\u56db\u65b9\uff0c\u8fd9\u662f\u8bf4\u53ea\u8981\u5979\u62d4\u5251\u4e00\u6325\uff0c\u7acb\u65f6\u5bd2\u5149\u4e07\u9053\uff0c\u745e\u6c14\u5343\u6761\uff0c\u6ee1\u5ba4\u4e2d\u90fd\u662f\u5251\u6c14\uff0c \u51db\u51bd\u903c\u4eba\u3002\u6280\u827a\u5f00\u62d3\u59fb\u7f18\u8def\uff0c\u542c\u8d77\u6765\u662f\u597d\u4e8b\uff0c\u4f46\u91cd\u70b9\u8981\u6b66\u529f\u7cbe\u5947\u624d\u884c\uff0c\u95ef\u8fc7\u4e09\u8bd5\uff0c\u5343\u91d1\u7389\u4eba \u8fce\u65b0\u90ce\uff0c\u90a3\u5c31\u662f\u4eba\u8d22\u517c\u6536\u4e86\u3002\u201d \u3000\u3000\u94c1\u7fce\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u59d1\u5a18\u554a\uff01\u2026\u2026\u201d \u3000\u3000\u957f\u886b\u4eba\u63a5\u9053\uff1a\u201c\u5bf9\uff01\u8001\u7237\u5b50\u9000\u4e86\u5427\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\uff01\u8001\u673d\u4e0d\u5728\u4e4e\u90a3\u5343\u91d1\u8d60\u4e88\uff0c\u4e3d\u8d28\u5929\u751f\uff0c\u5012\u503c\u5f97\u5927\u5f00\u4e00\u6b21\u773c\u754c\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u8001\u673d\u8fd8 \u662f\u8fdb\u53bb\u77a7\u77a7\u7684\u597d\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\u554a\uff01\u201d\u957f\u886b\u4eba\u9053\uff1a\u201c\u77a7\u77a7\u4e5f\u597d\uff0c\u5b81\u613f\u82b1\u4e0b\u6b7b\uff0c\u4f5c\u9b3c\u4e5f\u98ce\u6d41\uff1f\u4f55\u51b5\uff0c\u8fd9\u91cc\uff0c\u4f60 \u4e0d\u4f1a\u6b7b\uff0c\u8fde\u53d7\u4f24\u4e5f\u4e0d\u4f1a\uff0c\u53ea\u4e0d\u8fc7\u82b1\u70b9\u94f6\u5b50\u7f62\u4e86\uff0c\u8fd9\u91cc\u5148\u8981\u5341\u4e24\u8fdb\u95e8\u8d39\u3002\u201d \u3000\u3000\u201c\u6211\u8001\u4eba\u5bb6\u522b\u7684\u6ca1\u6709\uff0c\u5c31\u662f\u6709\u70b9\u94f6\u5b50\u2026\u2026\u201d\u94c1\u7fce\u9053\uff1a\u201c\u5165\u95e8\u5148\u4ed8\u5341\u4e24\u94f6\u5b50\uff0c\u4ee5\u540e\u5462\uff1f\u201d \u3000\u3000\u201c\u4e09\u9053\u6d4b\u8bd5\uff0c\u5404\u6709\u5b9a\u4ef7\uff0c\u7ae5\u53df\u65e0\u6b3a\uff0c\u4e0d\u6298\u4e0d\u6263\u3002\u201d\u957f\u886b\u4eba\u9053\uff1a\u201c\u6211\u53ea\u80fd\u544a\u8bc9\u4f60\u4e2a\u5927\u6982\u6d88 \u8d39\u4ef7\u7801\uff0c\u8bd5\u6d4b\u4e09\u5173\uff0c\u5927\u6982\u603b\u8981\u82b1\u767e\u4e24\u94f6\u5b50\uff0c\u4e0d\u8fc7\uff0c\u6bcf\u4e00\u9053\u6d4b\u8bd5\uff0c\u90fd\u7ed9\u4f60\u4e00\u4e2a\u601d\u91cf\u7684\u673a\u4f1a\uff0c\u81ea \u77e5\u65e0\u671b\uff0c\u6216\u662f\u94f6\u5b50\u4e0d\u591f\uff0c\u90fd\u53ef\u4ee5\u77e5\u96be\u800c\u9000\uff0c\u81f3\u4e8e\u8be6\u7ec6\u60c5\u5f62\uff0c\u5f97\u8981\u8eab\u5386\u5176\u5883\uff0c\u624d\u80fd\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u94c1\u7fce\u638f\u51fa\u4e00\u952d\u5341\u4e24\u7eb9\u94f6\uff0c\u7f34\u4e86\u5165\u95e8\u8d39\uff0c\u5fc3\u4e2d\u5374\u5728\u6697\u6697\u5fd6\u9053\uff1a\u8fd9\u7a76\u7adf\u662f\u94b1\u591a\u4eba\u4f5c\u602a\uff0c\u5174\u8d77 \u7684\u73a9\u4e50\u6587\u5316\uff1f\u8fd8\u662f\u6c5f\u6e56\u4e0a\u6709\u5fc3\u4eba\u8bbe\u8ba1\u51fa\u7684\u6843\u8272\u9677\u9631\uff0c\u7f51\u7f57\u9ad8\u624b\uff0c\u6709\u6240\u56fe\u8c0b\u5462\uff1f\u770b\u6765\u626c\u5dde\u57ce\u8868 \u9762\u5e73\u9759\uff0c\u4f46\u9aa8\u5b50\u91cc\u5374\u662f\u98ce\u4e91\u6697\u6d8c\uff0c\u915d\u917f\u7740\u4e00\u573a\u60ca\u5929\u52a8\u5730\u7684\u53d8\u5316\u3002 \u3000\u3000\u94c1\u7fce\u7684\u601d\u6f6e\u6c79\u6d8c\uff0c\u60f3\u7684\u662f\u626c\u5dde\u57ce\u4e2d\u767e\u4e07\u4eba\u7684\u5b89\u5371\u7978\u798f\uff0c\u5c31\u7b97\u62da\u6389\u4e86\u8fd9\u6761\u8001\u547d\uff0c\u4e5f\u4e0d\u80fd\u8ba9 \u8fd9\u5ea7\u7e41\u534e\u51a0\u5168\u56fd\u7684\u540d\u57ce\uff0c\u8d25\u5728\u6c5f\u6e56\u4eba\u7269\u624b\u4e2d\uff0c\u5fc3\u4e2d\u5728\u60f3\uff0c\u4eba\u5df2\u8e0f\u5165\u6708\u5706\u95e8\u4e2d\u3002 \u3000\u3000\u4e00\u6761\u957f\u957f\u7684\u8d70\u5eca\uff0c\u5341\u6b65\u6709\u4e00\u76cf\u540a\u706f\uff0c\u4e24\u4fa7\u662f\u6731\u7ea2\u680f\u5e72\u3001\u9ad8\u63a5\u5eca\u6cbf\u3001\u957f\u5eca\u66f2\u8f6c\u5728\u82b1\u6811\u4e4b\u4e2d\uff0c \u957f\u5eca\u5bbd\u7ea6\u56db\u5c3a\uff0c\u53ef\u5bb9\u4e24\u4e2a\u4eba\u5e76\u80a9\u800c\u884c\u3002\u7ea2\u7816\u94fa\u5730\uff0c\u9ad8\u51fa\u82b1\u7566\u4e00\u5c3a\uff0c\u591c\u98ce\u5439\u6765\uff0c\u82b1\u9999\u88ad\u4eba\u3002 \u3000\u3000\u4f46\u9ad8\u8fc7\u4e08\u4f59\u680f\u5e72\uff0c\u5374\u7834\u574f\u4e86\u957f\u5eca\u7684\u7f8e\u611f\uff0c\u94c1\u7fce\u60f3\u5230\u4e86\u9e1f\u7b3c\u5b50\uff0c\u4eba\u5982\u679c\u60f3\u79bb\u5f00\u957f\u5eca\uff0c\u5fc5\u987b \u8981\u5288\u5f00\u680f\u5e72\u3002 \u3000\u3000\u957f\u5eca\u5c3d\u5904\uff0c\u662f\u4e00\u5ea7\u96c5\u81f4\u7684\u5385\u5802\uff0c\u4e24\u4e2a\u9752\u8863\u7f8e\u5a62\uff0c\u624b\u6267\u7eb1\u706f\uff0c\u5f53\u95e8\u8fce\u5ba2\u3002 \u3000\u3000\u539f\u60f3\u8fd9\u91cc\u6709\u5ba2\u4eba\u8d70\u52a8\uff0c\u4f46\u5374\u5927\u51fa\u610f\u6599\u4e4b\u5916\uff0c\u96c5\u81f4\u5385\u5802\u4e2d\u4e00\u7247\u5e7d\u9759\uff0c\u9664\u4e86\u4e24\u4e2a\u8fce\u5ba2\u7f8e\u5a62\u4e4b \u5916\uff0c\u518d\u65e0\u522b\u4eba\u3002\u56db\u53ea\u9ad8\u70e7\u7684\u5de8\u70db\uff0c\u5149\u7130\u718a\u718a\uff0c\u7167\u4eae\u4e86\u5385\u5802\u666f\u7269\uff0c\u6e05\u6670\u53ef\u89c1\uff0c\u6700\u9192\u76ee\u7684\u662f\u4e24\u5f20 \u8d34\u5728\u58c1\u4e0a\u7684\u5de8\u5e45\u753b\u50cf\u3002 \u3000\u3000\u90a3\u662f\u4e24\u5e45\u5de5\u7b14\u7ed8\u5236\u7684\u4eba\u7269\u753b\uff0c\u4e24\u4e2a\u7f8e\u4e3d\u7684\u5973\u4eba\uff0c\u7b14\u6cd5\u4f20\u795e\uff0c\u6829\u6829\u5982\u751f\uff0c\u4e00\u7740\u7fe0\u886b\uff0c\u4e00\u7740 \u7ea2\u8863\uff0c\u90fd\u662f\u7d27\u8eab\u7684\u52b2\u88c5\uff0c\u886c\u6258\u51fa\u521a\u5065\u5a40\u5a1c\u7684\u73b2\u73d1\u8eab\u6750\uff0c\u624b\u4e2d\u65e0\u5251\uff0c\u8eab\u4e0a\u4e5f\u672a\u4f69\u5175\u5203\u3002 \u3000\u3000\u5982\u82e5\u8fd9\u4e24\u5e45\u753b\u50cf\uff0c\u753b\u7684\u662f\u6b66\u9662\u53cc\u51e4\uff0c\u5b9e\u5728\u662f\u4e24\u4e2a\u5f88\u7f8e\u7684\u59d1\u5a18\u3002 \u3000\u3000\u94c1\u7fce\u6697\u6697\u5401\u4e00\u53e3\u6c14\uff0c\u5fd6\u9053\uff1a\u5982\u6b64\u7f8e\u5973\uff0c\u600e\u4f1a\u7518\u5fc3\u6ca6\u843d\uff1f \u3000\u3000\u201c\u8001\u7237\u5b50\uff0c\u201d\u7ad9\u5728\u94c1\u7fce\u53f3\u4fa7\u7684\u9752\u8863\u5c0f\u5a62\uff0c\u7a81\u7136\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u8fdb\u53bb\u5427\uff01\u4eba\u6bd4\u753b\u66f4\u597d\u770b\u3002\u201d \u3000\u3000\u94c1\u7fce\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\uff01\u90a3\u5c31\u70e6\u8bf7\u5e26\u6211\u4e00\u7a0b\u3002\u201d \u3000\u3000\u9752\u8863\u5973\u5a62\u7b11\u9053\uff1a\u201c\u8001\u7237\u5b50\u60f3\u89c1\u54ea\u4e00\u4f4d\uff1f\u201d \u3000\u3000\u201c\u600e\u4e48\uff0c\u53ea\u80fd\u89c1\u4e00\u4e2a\uff1f\u201d\u94c1\u7fce\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u80fd\u4e0d\u80fd\u8bf4\u7684\u660e\u767d\u4e00\u4e9b\uff1f\u201d \u3000\u3000\u201c\u8001\u7237\u5b50\uff0c\u7ea2\u88f3\u3001\u7fe0\u7eeb\uff0c\u53ef\u90fd\u662f\u5341\u516b\u4e5d\u5c81\u7684\u5927\u59d1\u5a18\uff0c\u201d\u9752\u8863\u5973\u5a62\u9053\uff1a\u201c\u4e2a\u4e2a\u7f8e\u4e3d\u52a8\u4eba\uff0c \u8001\u7237\u6709\u672c\u9886\u5f97\u5230\u4e00\u4e2a\uff0c\u5df2\u662f\u8273\u798f\u4e0d\u6d45\uff0c\u8fd8\u8d2a\u5fc3\u4e24\u4e2a\u5168\u8981\u554a\uff1f\u201d \u3000\u3000\u201c\u597d\uff01\u90a3\u5c31\u53bb\u89c1\u7ea2\u88f3\uff0c\u201d\u94c1\u7fce\u4e00\u9762\u56de\u7b54\uff0c\u4e00\u9762\u6697\u4e2d\u63d0\u805a\u771f\u6c14\uff0c\u5168\u795e\u6212\u5907\u3002 \u3000\u3000\u9752\u8863\u5973\u5a62\u4e00\u63a8\u7ea2\u88f3\u7684\u753b\u50cf\uff0c\u753b\u50cf\u79fb\u4f4d\uff0c\u73b0\u51fa\u4e86\u4e00\u4e2a\u95e8\u6237\u3002 \u3000\u3000\u9752\u8863\u5973\u5a62\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u8bf7\u8fdb\u5427\uff01\u91cc\u9762\u6709\u4eba\u7b49\u5f85\u3002\u201d \u3000\u3000\u94c1\u7fce\u8fdb\u5165\u95e8\u5185\uff0c\u666f\u7269\u53c8\u662f\u4e00\u53d8\uff0c\u4e00\u4e2a\u5e7f\u655e\u7684\u623f\u95f4\u4e2d\uff0c\u6446\u7740\u4e00\u5f20\u5e73\u9614\u7684\u6728\u69bb\uff0c\u4e0d\u89c1\u6795\u88ab\uff0c \u4f46\u5374\u94fa\u4e86\u4e00\u5f20\u539a\u539a\u7684\u6bdb\u6bef\u3002 \u3000\u3000\u4e00\u4e2a\u5168\u8eab\u6843\u7ea2\u7f57\u886b\uff0c\u6843\u7ea2\u88e4\u7684\u5c11\u5973\uff0c\u76d8\u819d\u5750\u5728\u6728\u69bb\u4e0a\uff0c\u53ea\u4e00\u773c\u5c31\u53ef\u770b\u51fa\u5979\u662f\u7ea2\u88f3\uff0c\u548c\u753b \u50cf\u4e0a\u7ea2\u8863\u5973\u5f62\u8c8c\u4e00\u822c\uff0c\u53ea\u662f\u7709\u76ee\u542b\u60c5\uff0c\u9762\u5e26\u5fae\u7b11\uff0c\u770b\u8d77\u6765\u591a\u4e86\u4e00\u4efd\u7075\u6c14\uff0c\u5c31\u6bd4\u753b\u50cf\u7f8e\u597d\u591a\u4e86\u3002 \u3000\u3000\u7ea2\u8863\u5973\u76ee\u5149\u5728\u94c1\u7fce\u8138\u4e0a\u6253\u91cf\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u8d35\u5e9a\u554a\uff1f\u201d \u3000\u3000\u94c1\u7fce\u5fd6\u9053\uff1a\u6211\u8fd9\u526f\u5356\u76f8\uff0c\u5927\u6982\u662f\u4e0d\u600e\u4e48\u987a\u773c\uff0c\u4e0d\u592a\u53d7\u4eba\u6b22\u8fce\u3002 \u3000\u3000\u201c\u8001\u592b\u4eca\u5e74\u4e94\u5341\u516b\u5c81\uff0c\u662f\u4e0d\u662f\u592a\u8001\u4e86\uff1f\u201d\u94c1\u7fce\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u5ba2\u4eba\uff0c\u662f\u5426\u8fd8\u6709\u5e74\u9f84\u9650\u5236\uff1f\u201d \u3000\u3000\u201c\u8001\u800c\u98ce\u6d41\u662f\u5bff\u5f81\uff0c\u201d\u7ea2\u88f3\u9053\uff1a\u201c\u5e0c\u671b\u8001\u7237\u5b50\u957f\u547d\u767e\u5c81\u3002\u201d\u7a81\u7136\u53cc\u817f\u4e00\u4f38\uff0c\u9732\u51fa\u4e86\u4e00\u5bf9 \u7ea2\u7ee3\u82b1\u978b\uff0c\u53cc\u8db3\u4e4b\u95f4\uff0c\u631f\u4e86\u4e00\u5757\u9752\u7816\uff0c\u63a5\u9053\uff1a\u201c\u53d6\u4e0b\u9752\u7816\uff0c\u4f60\u5c31\u80dc\u4e86\uff0c\u4e0d\u8fc7\uff0c\u5148\u8981\u4ea4\u4e5d\u5341\u4e24 \u94f6\u5b50\u3002\u201d\u8a00\u7f62\uff0c\u95ed\u4e0a\u53cc\u76ee\uff0c\u4e0d\u518d\u7406\u4f1a\u94c1\u7fce\u3002 \u3000\u3000\u94c1\u7fce\u5403\u4e86\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u52a8\u4f5c\u5feb\u5982\u95ea\u7535\uff0c\u7adf\u7136\u672a\u770b\u6e05\u695a\u5979\u5982\u4f55\u628a\u9752\u7816\u631f\u5728\u53cc\u8db3\u4e4b\u4e2d\uff0c\u5982\u662f \u8fd9\u4e00\u817f\u8e22\u5411\u6211\u7684\u8981\u5bb3\uff0c\u6211\u662f\u5426\u80fd\u591f\u95ea\u907f\u5f00\u53bb\uff1f \u3000\u3000\u5982\u6b64\u8eab\u624b\uff0c\u53c8\u4eba\u6bd4\u82b1\u5a07\uff0c\u600e\u80af\u6295\u8eab\u9752\u697c\uff0c\u5356\u8eab\u4e3a\u5a3c\u5462\uff1f\u8fd9\u4e2d\u95f4\u5927\u6709\u8e4a\u8df7\u4e86\u3002 \u3000\u3000\u4f46\u4e5f\u66f4\u575a\u5b9a\u4e86\u4ed6\u4e00\u63a2\u7a76\u7adf\u7684\u51b3\u5fc3\uff0c\u4e00\u9762\u53d6\u51fa\u4e00\u5f20\u767e\u4e24\u7684\u94f6\u7968\uff0c\u653e\u5728\u6728\u69bb\u4e0a\uff0c\u4e00\u9762\u8fd0\u6c14\u6212 \u5907\uff0c\u7f13\u7f13\u4f38\u51fa\u4e86\u53f3\u624b\u3002 \u3000\u3000\u94c1\u7fce\u5fc3\u4e2d\u5f88\u660e\u767d\uff0c\u7ea2\u88f3\u867d\u7136\u95ed\u7740\u53cc\u76ee\uff0c\u4f46\u5468\u56f4\u7684\u52a8\u9759\u3001\u4e3e\u6b62\uff0c\u90fd\u65e0\u6cd5\u7792\u5f97\u8fc7\u5979\uff0c\u4e5f\u65e0\u6cd5 \u9884\u6599\u8fd9\u4e2a\u59d1\u5a18\u4f1a\u65bd\u5c55\u51fa\u4ec0\u4e48\u6837\u7684\u624b\u6bb5\u5bf9\u4ed8\u4ed6\u3002 \u3000\u3000\u4e94\u6307\u89e6\u53ca\u9752\u7816\uff0c\u7a81\u7136\u52a0\u529b\uff0c\u65bd\u5c55\u51fa\u5927\u9e70\u722a\u529f\u3002 \u3000\u3000\u7ea2\u88f3\u7684\u53cd\u5e94\u662f\u975e\u5e38\u7684\u654f\u6377\u5feb\u901f\uff0c\u53cc\u76ee\u5f20\u5f00\uff0c\u4e00\u626c\u79c0\u7709\uff0c\u53cc\u817f\u529b\u9053\u5927\u589e\uff0c\u67f3\u8170\u5fae\u626d\u3002 \u3000\u3000\u4f46\u95fb\u6ce2\u7136\u4e00\u58f0\u8f7b\u54cd\uff0c\u9752\u7816\u4e2d\u65ad\u4e3a\u4e8c\u3002 \u3000\u3000\u7ea2\u88f3\u626d\u65ad\u9752\u7816\uff0c\u662f\u53d6\u5de7\u7684\u4f5c\u6cd5\uff0c\u5e76\u672a\u548c\u94c1\u7fce\u8f83\u91cf\u5185\u529b\u3002 \u3000\u3000\u94c1\u7fce\u5fc3\u4e2d\u660e\u767d\uff0c\u5374\u672a\u70b9\u7834\uff0c\u4f46\u63e1\u5728\u624b\u4e2d\u7684\u534a\u622a\u9752\u7816\uff0c\u5374\u5316\u4f5c\u788e\u7c89\uff0c\u6d12\u843d\u4e00\u5730\u3002 \u3000\u3000\u201c\u8001\u7237\u5b50\u662f\u771f\u4eba\u4e0d\u9732\u76f8\u554a\uff01\u7ea2\u88f3\u5931\u656c\u4e86\u3002\u201d\u8138\u8272\u6625\u98ce\u89e3\u51bb\uff0c\u795e\u6001\u4ea6\u53d8\u6e29\u67d4\u3002 \u3000\u3000\u662f\u7684\uff01\u8001\u7237\u5b50\u5356\u76f8\u4e0d\u597d\uff0c\u4e00\u8138\u5927\u80e1\u5b50\uff0c\u4e5f\u6709\u4e9b\u571f\u5934\u571f\u8111\uff0c\u4f46\u5185\u529b\u6df1\u539a\uff0c\u6b66\u529f\u9ad8\u5f3a\u554a\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u9635\uff0c\u6211\u6709\u70b9\u53d6\u5de7\uff0c\u4f46\u4e0d\u80fd\u7b97\u8d25\u3002\u201d\u7ea2\u88f3\u8bf4\u7684\u5f88\u5766\u767d\uff0c\u9053\uff1a\u201c\u8001\u7237\u5b50\u672a\u52a8\u5fc3\u673a\uff0c\u6240 \u4ee5\uff0c\u5e94\u80dc\u672a\u80dc\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u5bf9\uff0c\u54b1\u4eec\u8fd9\u4e00\u9635\u4e0d\u5206\u80dc\u8d1f\uff01\u201d\u94c1\u7fce\u9053\uff1a\u201c\u662f\u5426\u8fd8\u6709\u4e0b\u4e00\u9635\u5462\uff1f\u201d \u3000\u3000\u201c\u8001\u7237\u5b50\uff0c\u4f60\u6ca1\u8d25\uff0c\u6240\u4ee5\uff0c\u4e5d\u5341\u4e24\u94f6\u5b50\u53ef\u4ee5\u6536\u56de\uff0c\u80fd\u7f62\u624b\u65f6\u4e14\u7f62\u624b\uff0c\u5f97\u9976\u4eba\u5904\u4e14\u9976\u4eba\uff0c \u56de\u53bb\u5427\uff01\u201d\u7ea2\u88f3\u9053\uff1a\u201c\u6211\u795d\u8001\u7237\u5b50\u4e00\u8def\u987a\u98ce\uff0c\u798f\u5bff\u7ef5\u957f\u3002\u201d \u3000\u3000\u94c1\u7fce\u5fd6\u9053\uff1a\u4e0d\u80fd\u9000\uff0c\u4e0d\u5165\u864e\u7a74\uff0c\u7109\u5f97\u864e\u5b50\uff0c\u8fd9\u4e2b\u5934\u5ffd\u51b7\u5ffd\u70ed\uff0c\u521a\u624d\u4e00\u756a\u8bdd\uff0c\u8bf4\u7684\u9887\u89c1\u6027 \u60c5\uff0c\u53ea\u4e0d\u77e5\u5979\u7684\u54c1\u5fb7\u5982\u4f55\uff1f\u662f\u5a07\u5a03\u3001\u8fd8\u662f\u9b54\u5973\uff1f\u5f97\u8bd5\u5979\u4e00\u8bd5\uff0c\u8fd9\u7b49\u98ce\u6708\u6240\u5728\uff0c\u5e94\u65e0\u7981\u5fcc\uff0c\u5fc3 \u4e2d\u5b9a\u4e86\u4e3b\u610f\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u592b\u65e2\u7136\u6765\u4e86\uff0c\u5c82\u80fd\u5165\u5b9d\u5c71\u7a7a\u624b\u800c\u56de\uff0c\u5343\u91d1\u8d60\u4e88\uff0c\u4e0d\u503c\u4e00\u54c2\uff0c \u4f46\u59d1\u5a18\u4e4b\u7f8e\uff0c\u786e\u662f\u751f\u5e73\u4ec5\u89c1\uff0c\u8001\u592b\u4e0d\u662f\u597d\u8272\u4e4b\u5f92\uff0c\u4f46\u4e5f\u9501\u4e0d\u4f4f\u5fc3\u733f\u610f\u9a6c\u4e86\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u4e0d\u4f46\u5145\u6ee1\u6311\u9017\uff0c\u800c\u4e14\u8868\u793a\u7684\u5341\u5206\u9732\u9aa8\u3002 \u3000\u3000\u7ea2\u88f3\u77aa\u5927\u4e86\u4e00\u53cc\u773c\u775b\uff0c\u5728\u94c1\u7fce\u7684\u8138\u4e0a\u77a7\u4e86\u4e00\u9635\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u7b11\u4e86\uff0c\u7b11\u7684\u4e00\u8138\u67f3\u5a9a\u82b1\u5a07\uff0c \u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u8bf4\u7684\u592a\u6587\u96c5\u4e86\uff0c\u8fd9\u91cc\u662f\u9752\u697c\u5993\u9662\uff0c\u7528\u4e0d\u7740\u751c\u8a00\u871c\u8bed\uff0c\u8868\u767d\u6e05\u9ad8\uff0c\u5e72\u8106\u8bf4\uff0c\u4f60 \u8001\u7237\u5b50\u5df2\u6625\u5fc3\u8361\u6f3e\uff0c\u60f3\u62b1\u6211\u4e0a\u5e8a\u4e86\uff0c\u4e0d\u8fc7\uff0c\u8001\u7237\u5b50\uff0c\u90a3\u5f97\u95ef\u8fc7\u4e09\u5173\u624d\u884c\uff0c\u7b2c\u4e00\u5173\u80dc\u8d1f\u672a \u5206\u2026\u2026\u201d \u3000\u3000\u201c\u7b2c\u4e8c\u5173\u5462\uff1f\u201d\u94c1\u7fce\u63a5\u9053\uff1a\u201c\u8fd8\u8bf7\u59d1\u5a18\u660e\u793a\u3002\u201d \u3000\u3000\u7ea2\u88f3\u9053\uff1a\u201c\u7b2c\u4e8c\u5173\u8f7b\u529f\u6697\u5668\u3002\u201d \u3000\u3000\u94c1\u7fce\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u73a9\u547d\u554a\uff01\u8fd9\u4e0d\u8fc7\u662f\u98ce\u6708\u573a\u6240\uff0c\u559d\u559d\u9152\u3001\u5212\u5212\u62f3\uff0c\u4e5f\u7b97\u4e00\u5173\uff0c\u600e\u4e48 \u8ba2\u4e0b\u7684\u6bd4\u8d5b\u89c4\u77e9\u50cf\u662f\u6253\u64c2\u53f0\u5440\uff1f\u201d \u3000\u3000\u201c\u771f\u6b63\u73a9\u547d\u7684\u662f\u7b2c\u4e09\u5173\u2026\u2026\u201d\u7ea2\u88f3\u9053\uff1a\u201c\u90a3\u91cc\u4e0d\u9650\u5b9a\u62f3\u638c\u5175\u5203\uff0c\u603b\u4e4b\uff0c\u4f60\u6709\u591a\u5927\u672c\u9886\uff0c \u90fd\u53ef\u4ee5\u5168\u90e8\u65bd\u5c55\u51fa\u6765\uff0c\u53cc\u65b9\u7acb\u4e86\u751f\u6b7b\u72b6\uff0c\u8c01\u88ab\u6740\u6b7b\u8c01\u5012\u9709\u2026\u2026\u201d \u3000\u3000\u201c\u59d1\u5a18\uff0c\u8fd9\u79cd\u73a9\u6cd5\u5212\u5f97\u6765\u4e48\uff1f\u201d\u94c1\u7fce\u9053\uff1a\u201c\u7537\u5973\u76f8\u60a6\uff0c\u672c\u662f\u4ef6\u8d4f\u5fc3\u4e50\u4e8b\uff0c\u5982\u6b64\u7684\u5200\u6765\u5251 \u5f80\uff0c\u6740\u6210\u4e2a\u8840\u6dcb\u6dcb\u7684\u5c40\u9762\uff0c\u8fd8\u6709\u4ec0\u4e48\u6b22\u6109\u53ef\u8a00\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5374\u662f\u6697\u6697\u9707\u60ca\uff0c\u5fd6\u9053\uff1a\u8fd9\u7a76\u7adf\u662f\u4e2a\u4ec0\u4e48\u7ec4\u5408\uff1f\u76ee\u7684\u4f55\u5728\uff1f\u56db\u51e4\u697c\uff0c\u5df2\u5f00\u8bbe\u8fd1\u5e74\u4e4b\u4e45\uff0c \u8fd9\u7b49\u5bc6\u5ba4\u640f\u547d\u7684\u89c4\u77e9\uff0c\u5b9a\u5df2\u9020\u6210\u4e86\u4e0d\u5c11\u4f24\u4ea1\uff0c\u600e\u4e48\u4f1a\u672a\u542c\u5230\u4e00\u70b9\u513f\u98ce\u58f0\u5462\uff1f\u94c1\u7fce\u554a\uff01\u94c1\u7fce\uff0c \u4f60\u8fd9\u4e2a\u603b\u6355\u5934\u662f\u600e\u4e48\u5e72\u7684\uff0c\u8fd8\u81ea\u8ba4\u5728\u626c\u5dde\u5e03\u4e0b\u4e86\u773c\u7ebf\u7f57\u7f51\uff0c\u60f3\u8d77\u6765\uff0c\u771f\u662f\u60ed\u6127\u7684\u5f88\u554a\uff01 \u3000\u3000\u7ea2\u88f3\u9053\uff1a\u201c\u8fd9\u786e\u5b9e\u5f88\u4e0d\u503c\uff0c\u4e00\u5915\u6b22\u4e50\uff0c\u8981\u62ff\u547d\u53bb\u62fc\uff0c\u4f46\u5bf9\u6211\u800c\u8a00\uff0c\u5374\u662f\u6709\u5b83\u7684\u4ef7\u503c\uff0c\u62b1 \u6211\u4e0a\u5e8a\uff0c\u4e0d\u662f\u6211\u5fc3\u7518\u60c5\u613f\uff0c\u4f46\u6211\u4e0d\u80fd\u62d2\u7edd\u3001\u4e0d\u80fd\u9003\uff0c\u53ea\u6709\u62da\u547d\u4fdd\u8eab\u4e00\u9014\uff0c\u4f60\u4ee5\u6b66\u529f\u5236\u670d\u4e86\u6211\uff0c \u53ea\u597d\u8ba4\u4e86\uff0c\u4e5f\u7b97\u662f\u4e3a\u81ea\u5df1\u627e\u4e00\u4e2a\u5931\u8eab\u7684\u7406\u7531\u5427\uff01\u201d \u3000\u3000\u94c1\u7fce\u5fc3\u4e2d\u8fd8\u6709\u5f88\u591a\u7684\u7591\u95ee\uff0c\u4f46\u5fcd\u4e0b\u4e0d\u95ee\u4e86\uff0c\u4e8b\u6d89\u4eba\u8eab\uff0c\u518d\u95ee\u4e0b\u53bb\uff0c\u5c31\u975e\u5e38\u96be\u4ee5\u542f\u9f7f\u4e86\u3002 \u3000\u3000\u773c\u4e0b\u6700\u91cd\u8981\u7684\u51b3\u5b9a\u662f\u8fd8\u8981\u4e0d\u8981\u6df1\u7a76\u4e0b\u53bb\uff1f \u3000\u3000\u7ea2\u88f3\u7a81\u7136\u62ff\u8d77\u6728\u69bb\u4e0a\u94f6\u7968\uff0c\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u7b2c\u4e00\u5173\u7b97\u4f60\u8fc7\u4e86\uff0c\u4f46\u672a\u5206\u80dc\u8d1f\uff0c\u94f6\u7968\u4f60\u53ef\u4ee5 \u6536\u56de\uff0c\u5f53\u7136\uff0c\u4f60\u5982\u575a\u6301\u8981\u95ef\u7b2c\u4e8c\u5173\uff0c\u4e5f\u53ef\u4ee5\u6539\u4f5c\u7b2c\u4e8c\u5173\u8fc7\u5173\u8d39\u7528\u2026\u2026\u201d \u3000\u3000\u94c1\u7fce\u5df2\u6697\u81ea\u4f5c\u4e86\u51b3\u5b9a\uff0c\u8981\u6df1\u5165\u864e\u7a74\uff0c\u770b\u4e2a\u6c34\u843d\u77f3\u51fa\uff0c\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5982\u82e5\u8001\u592b\u80dc\u4e86 \u7b2c\u4e8c\u5173\uff0c\u8fd8\u8981\u4e0d\u8981\u518d\u95ef\u7b2c\u4e09\u5173\u5462\uff1f\u201d \u3000\u3000\u7ea2\u88f3\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u975e\u95ef\u4e0d\u53ef\u4e48\uff1f\u5c31\u7b97\u4f60\u95ef\u8fc7\u4e09\u5173\uff0c\u62b1\u6211\u4e0a\u5e8a\u2026\u2026\u201d \u3000\u3000\u201c\u5f88\u96be\u8fc7\u4e86\uff0c\u662f\u4e48\uff1f\u8427\u8427\u767d\u53d1\u5bf9\u7ea2\u88c5\uff0c\u4e00\u6811\u68a8\u82b1\u538b\u6d77\u68e0\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u4f60\u8981\u5927\u653e\u60b2\u58f0\u54ed \u4e00\u573a\uff1f\u201d \u3000\u3000\u201c\u4e0d\u80fd\u54ed\u554a\uff01\u800c\u4e14\u8fd8\u5f97\u7b11\uff0c\u201d\u7ea2\u88f3\u9053\uff1a\u201c\u53ea\u662f\u7b11\u7684\u51c4\u51c9\uff0c\u7b11\u7684\u5fc3\u5982\u5200\u7ede\u75db\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e9b \u90fd\u4e0d\u5173\u4f60\u7684\u4e8b\u4e86\uff0c\u8d70\uff01\u6211\u4eec\u53bb\u6bd4\u8bd5\u8f7b\u529f\u6697\u5668\u3002\u201d \u3000\u3000\u8f6c\u8eab\u5728\u58c1\u4e0a\u4e00\u63a8\uff0c\u63a8\u5f00\u4e00\u6247\u95e8\u6237\uff0c\u5927\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u53c8\u4e00\u5ea7\u9ad8\u5927\u7684\u655e\u5385\uff0c\u70db\u706b\u8f89\u714c\uff0c\u7167\u7684\u4e00\u5ba4\u660e\u4eae\uff0c\u4f46\u5374\u653e\u7740\u5341\u4f59\u79cd\u6697\u5668\uff0c\u98de\u5200\u3001\u8896\u7bad\u3001\u94c1 \u83b2\u5b50\uff0c\u94a2\u9556\u3001\u94f6\u68ad\u3001\u91d1\u98de\u73af\u2026\u2026\u90fd\u662f\u5c5e\u578b\u5f0f\u5927\u4e00\u70b9\u7684\u6697\u5668\uff0c\u50cf\u6885\u82b1\u9488\u4e00\u7c7b\u7684\u7ec6\u5c0f\u6697\u5668\uff0c\u90fd\u672a \u5b58\u653e\u3002 \u3000\u3000\u7ea2\u88f3\u7684\u60c5\u7eea\u4f3c\u662f\u5df2\u7ecf\u5b89\u5b9a\u4e0b\u6765\uff0c\u7b11\u9053\uff1a\u201c\u5408\u4e09\u5173\u7684\u51b3\u6597\uff0c\u7edd\u5bf9\u516c\u5e73\uff0c\u8fd9\u91cc\u867d\u7136\u4e5f\u6709\u51e0\u4e2a \u9ad8\u624b\uff0c\u4f46\u4ed6\u4eec\u4e0d\u4f1a\u5e2e\u52a9\u6211\uff0c\u6211\u4eec\u4fe9\u4e00\u5bf9\u4e00\uff0c\u4f60\u5982\u679c\u80fd\u7528\u8fd9\u4e9b\u6697\u5668\u6253\u4f24\u4e86\u6211\uff0c\u81ea\u7136\u7528\u4e0d\u7740\u7b2c\u4e09 \u573a\u7684\u6bd4\u8bd5\uff0c\u6211\u4f1a\u5f97\u5230\u6700\u597d\u7684\u6cbb\u7597\uff0c\u4f24\u52bf\u4e0d\u7528\u5168\u597d\uff0c\u53ea\u8981\u6211\u80fd\u591f\u5fcd\u53d7\uff0c\u6211\u5c31\u4f1a\u53bb\u966a\u4f60\uff0c\u8ba9\u4f60\u8d81 \u5fc3\u5982\u613f\u3002\u201d \u3000\u3000\u201c\u5982\u82e5\u4f24\u7684\u662f\u6211\u5462\uff1f\u201d\u94c1\u7fce\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u4e5f\u5f97\u5230\u6700\u597d\u7684\u6551\u52a9\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u80fd\uff0c\u4f60\u8001\u7237\u5b50\u4e5f\u53ef\u4ee5\u9000\u51fa\u6bd4\u8bd5\uff0c\u201d\u7ea2\u88f3\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8981\u6cbb\u597d\u4e86\u4f24\u52bf\u624d\u80fd\u8d70\uff01\u56db\u51e4 \u697c\u4e2d\u7684\u6b66\u9662\uff0c\u5df2\u6709\u8fc7\u4f24\u4eba\u4e8b\u4ef6\uff0c\u4f46\u5374\u6ca1\u6709\u4eba\u62b1\u6028\u6bd4\u8bd5\u4e0d\u516c\u3002\u201d \u3000\u3000\u94c1\u7fce\u4e0d\u5f97\u4e0d\u91cd\u65b0\u628a\u7ea2\u88f3\u518d\u4f5c\u4f30\u7b97\uff0c\u8fd9\u4e2b\u5934\u5e74\u7eaa\u5f88\u8f7b\uff0c\u4f46\u81ea\u5236\u7684\u80fd\u529b\u5f88\u5f3a\uff0c\u591f\u5766\u7387\uff0c\u4e5f\u80fd \u5fcd\u8010\uff0c\u5979\u538c\u6076\u966a\u4eba\u4e0a\u5e8a\uff0c\u4f46\u53c8\u80fd\u63a5\u53d7\u540e\u679c\uff0c\u6536\u8d77\u6ee1\u8154\u5fc3\u9178\uff0c\u5a07\u7b11\u4f8d\u4eba\u3002 \u3000\u3000\u201c\u8001\u7237\u5b50\uff0c\u67b6\u4e0a\u6697\u5668\uff0c\u968f\u610f\u53d6\u7528\uff0c\u201d\u7ea2\u88f3\u9053\uff1a\u201c\u6211\u4eec\u5f00\u59cb\u5427\uff01\u201d\u968f\u624b\u5728\u67b6\u4e0a\u53d6\u4e86\u4e94\u628a\u98de \u5200\u3002 \u3000\u3000\u94c1\u7fce\u4e5f\u53d6\u4e86\u4e94\u628a\u98de\u5200\uff0c\u51c6\u5907\u4ee5\u98de\u5200\u5bf9\u98de\u5200\uff0c\u6597\u6597\u7ea2\u88f3\u59d1\u5a18\u4e86\u3002 \u3000\u3000\u201c\u8001\u7237\u5b50\uff0c\u7a7f\u7740\u957f\u888d\uff0c\u884c\u52a8\u4e0d\u4fbf\uff0c\u201d\u7ea2\u88f3\u9053\uff1a\u201c\u8131\u4e86\u5427\uff01\u201d \u3000\u3000\u201c\u591a\u8c22\u59d1\u5a18\u63d0\u9192\uff0c\u8001\u592b\u4ece\u547d\u4e86\u3002\u201d\u7f13\u7f13\u8131\u4e0b\u957f\u888d\uff0c\u653e\u5728\u6728\u67b6\u4e00\u8fb9\u3002 \u3000\u3000\u94c1\u7fce\u5fc3\u4e2d\uff0c\u5bf9\u7ea2\u88f3\u53c8\u591a\u4e00\u5c42\u8ba4\u77e5\uff0c\u8fd9\u4e2b\u5934\u867d\u7136\u6210\u957f\u5728\u8be1\u5f02\u7684\u73af\u5883\u4e4b\u4e2d\uff0c\u4f46\u5fc3\u5730\u8fd8\u5f88\u4ec1\u539a\uff0c \u4eba\u4e5f\u8bb8\u6709\u4e9b\u53d8\u4e86\uff0c\u4f46\u5584\u826f\u7684\u672c\u6027\u8fd8\u5728\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u521a\u624d\u4e00\u6b21\u4ea4\u624b\uff0c\u7ea2\u88f3\u8868\u73b0\u51fa\u7684\u6b66\u529f\u6839\u57fa\uff0c\u7075\u654f\u53cd\u5e94\uff0c\u5df2\u4f7f\u5f97\u94c1\u7fce\u6709\u4e86\u8b66\u60d5\uff0c\u4e5f \u4e0d\u6562\u592a\u6258\u5927\u4e86\u3002 \u3000\u3000\u6697\u4e2d\u8fd0\u96c6\u529f\u529b\uff0c\u5de6\u624b\u53d6\u5230\u7684\u98de\u5200\uff0c\u4e5f\u5206\u5165\u53f3\u624b\u4e2d\u4e24\u628a\uff0c\u4e94\u628a\u5200\uff0c\u5206\u6267\u4e24\u624b\u3002 \u3000\u3000\u8fd9\u79cd\u63e1\u5200\u7684\u65b9\u5f0f\uff0c\u8868\u793a\u51fa\u53cc\u624b\u90fd\u80fd\u6253\u51fa\u98de\u5200\uff0c\u662f\u7528\u6697\u5668\u7684\u9ad8\u624b\u3002 \u3000\u3000\u7ea2\u88f3\u770b\u94c1\u7fce\u63e1\u5200\u67b6\u52bf\uff0c\u5fc3\u4e2d\u7a81\u7136\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u8fd9\u4e2a\u770b\u4e0a\u53bb\u6709\u70b9\u571f\u5934\u571f\u8111\u7684\u8001\u5934\u5b50\uff0c\u4e0d\u4f46 \u529f\u529b\u6df1\u539a\uff0c\u800c\u4e14\u6280\u827a\u7cbe\u6e5b\uff0c\u5904\u4e8b\u6709\u6761\u4e0d\u7d0a\uff0c\u5206\u660e\u662f\u4e2a\u8001\u6c5f\u6e56\uff0c\u600e\u7684\u7a7f\u7740\u5982\u6b64\u7684\u571f\u6c14\u3002\u5fc3\u4e2d\u52a8 \u7591\uff0c\u5168\u795e\u51dd\u6ce8\u5728\u94c1\u7fce\u8138\u4e0a\u67e5\u770b\u3002 \u3000\u3000\u663e\u7136\uff0c\u88ab\u5979\u77a7\u51fa\u4e86\u4e00\u4e9b\u6613\u5bb9\u7684\u75d5\u8ff9\u3002 \u3000\u3000\u201c\u8001\u7237\u5b50\uff0c\u53ef\u4e0d\u53ef\u4ee5\u8ba9\u6211\u89c1\u8bc6\u4e00\u4e0b\u4f60\u7684\u771f\u9762\u76ee\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u95ee\u7684\u76f8\u5f53\u673a\u5de7\uff0c\u8a00\u4e0b\u4e4b\u610f\uff0c\u4f3c\u662f\u65e9\u5c31\u770b\u51fa\u4e86\u94c1\u7fce\u662f\u6613\u5bb9\u800c\u6765\u3002 \u3000\u3000\u201c\u5389\u5bb3\u5440\uff01\u59d1\u5a18\uff0c\u201d\u4f38\u624b\u626f\u6389\u4e86\u8138\u4e0a\u7684\u8fde\u9b13\u5927\u80e1\u5b50\uff0c\u5f62\u8c8c\u4e00\u53d8\uff0c\u4f46\u6613\u5bb9\u836f\u7269\u5c24\u5728\uff0c\u770b\u4e0a \u53bb\u8138\u4e0a\u6591\u75d5\u7247\u7247\uff0c\u5c31\u4e0d\u50cf\u4e00\u5f20\u4eba\u8138\u4e86\uff0c\u4ecd\u7136\u63a9\u906e\u4f4f\u672c\u6765\u9762\u76ee\u3002 \u3000\u3000\u201c\u6211\u60f3\uff0c\u4f60\u6ca1\u6709\u65f6\u95f4\u6d17\u53bb\u8138\u4e0a\u7684\u6613\u5bb9\u836f\u7269\uff0c\u201d\u7ea2\u88f3\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u90a3\u4e0d\u662f\u65e0\u52a9\u4e8e \u4f60\u6211\u4e4b\u95f4\u7684\u76f8\u60dc\u4e4b\u60c5\uff0c\u53cd\u5c06\u589e\u591a\u4e86\u4e09\u5206\u6740\u673a\uff0c\u771f\u662f\u5f04\u5de7\u6210\u62d9\u554a\uff01\u201d \u3000\u3000\u94c1\u7fce\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u613f\u95fb\u5176\u8be6\u3002\u201d \u3000\u3000\u201c\u51e1\u662f\u6613\u5bb9\u6df7\u5165\u56db\u51e4\u697c\u8005\uff0c\u6740\u65e0\u8d66\uff0c\u9601\u4e0b\u5df2\u7ecf\u8bc1\u660e\u4e86\u4f60\u662f\u6613\u5bb9\u800c\u6765\uff0c\u201d\u7ea2\u88f3\u7a81\u7136\u653e\u4f4e\u4e86 \u58f0\u97f3\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u552f\u4e00\u8865\u6551\u7684\u529e\u6cd5\u5c31\u662f\u4ee5\u771f\u6b63\u7684\u9762\u76ee\u51fa\u73b0\uff0c\u4e5f\u80fd\u8bc1\u660e\u4f60\u522b\u65e0\u7528\u5fc3\uff0c\u53ea\u662f\u4e3a \u7f8e\u8272\u6240\u52a8\uff0c\u662f\u4e00\u4e2a\u771f\u6b63\u7684\u5ad6\u5ba2\u3002\u201d \u3000\u3000\u94c1\u7fce\u5fc3\u4e2d\u6697\u9053\uff1a\u8fd9\u4e2b\u5934\u534a\u771f\u534a\u5047\uff0c\u5531\u4f5c\u4ff1\u4f73\uff0c\u65e2\u4e0d\u80fd\u731c\u51fa\u5979\u7528\u5fc3\u4f55\u5728\uff0c\u4e5f\u6ca1\u6709\u900f\u9732\u51fa\u56db \u51e4\u697c\u4efb\u4f55\u5185\u60c5\u3002\u4f46\u94c1\u7fce\u4ee5\u4e30\u5bcc\u7684\u9605\u5386\uff0c\u5e38\u5e74\u529e\u6848\u63a8\u7406\u7ecf\u9a8c\uff0c\u7406\u51fa\u51e0\u6761\u7ebf\u7d22\uff0c\u8fd9\u5ea7\u56db\u51e4\u697c\u4e0d\u662f \u5355\u7eaf\u7684\u751f\u610f\u573a\uff0c\u8bbe\u8ba1\u51fa\u6587\u9601\u3001\u6b66\u9662\uff0c\u9009\u51fa\u56db\u4f4d\u7f8e\u5973\uff0c\u662f\u5e03\u4e0b\u7684\u8bf1\u9975\uff0c\u662f\u6843\u8272\u7684\u9677\u9631\uff0c\u4ee5\u7f51\u7f57 \u6c5f\u6e56\u9ad8\u624b\u2026\u2026\u3002 \u3000\u3000\u7edd\u4e0d\u80fd\u66b4\u9732\u51fa\u626c\u5dde\u5e9c\u7684\u603b\u6355\u5934\u7684\u8eab\u4efd\uff0c\u8fd9\u91cc\u4e0d\u5e0c\u671b\u522b\u6709\u7528\u5fc3\u7684\u4eba\u63a2\u8bbf\uff0c\u5f53\u7136\u66f4\u4e0d\u5e0c\u671b\u4ed6 \u8fd9\u4e2a\u638c\u7406\u626c\u5dde\u5730\u65b9\u6cbb\u5b89\u7684\u9996\u8111\u4eba\u7269\u6613\u5bb9\u6405\u5c40\uff0c\u4e00\u65e6\u8eab\u4efd\u66b4\u9732\uff0c\u8fd9\u6b66\u9662\u4e2d\uff0c\u5fc5\u5c06\u4f1a\u5c3d\u51fa\u5168\u529b\u53d6 \u4ed6\u6027\u547d\uff0c\u548c\u7ea2\u88f3\u4e00\u756a\u93d6\u6218\uff0c\u5c0f\u59d1\u5a18\u7684\u6280\u827a\u529f\u529b\uff0c\u5b9e\u975e\u5c0f\u53ef\uff0c\u8fd9\u91cc\u5982\u82e5\u8fd8\u6709\u9ad8\u624b\u4e3b\u6301\uff0c\u5b9e\u5728\u662f \u4e00\u4e2a\u51f6\u9669\u4e4b\u5730\u3002 \u3000\u3000\u4f46\u5982\u5c31\u6b64\u9000\u8d70\uff0c\u90a3\u662f\u767e\u91cc\u884c\u7a0b\u534a\u4e5d\u5341\uff0c\u8fd9\u56db\u51e4\u697c\u7f51\u7f57\u6c5f\u6e56\u9ad8\u4eba\u7684\u624b\u6cd5\uff0c\u4e5f\u5c31\u65e0\u6cd5\u4e86\u89e3\uff0c \u8fd9\u4e9b\u4eba\u662f\u5426\u548c\u9b3c\u5200\u6709\u5173\uff0c\u4e5f\u6210\u4e86\u4e0d\u89e3\u4e4b\u8c1c\u3002\u51e0\u756a\u601d\u7d22\u4e4b\u540e\uff0c\u51b3\u5b9a\u4e86\u5192\u9669\u4e00\u8bd5\u2026\u2026\u3002 \u3000\u3000\u201c\u60f3\u597d\u4e86\u6ca1\u6709\uff1f\u201d\u7ea2\u88f3\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5982\u679c\u4f60\u613f\u610f\u5c3d\u9664\u8138\u4e0a\u7684\u6613\u5bb9\u836f\u7269\uff0c\u6211\u5012\u5e0c\u671b\u4e00\u7779\u4f60 \u7684\u771f\u6b63\u9762\u76ee\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u53c8\u600e\u6837\uff1f\u201d\u94c1\u7fce\u9053\uff1a\u201c\u6bd4\u8bd5\u8fc7\u8f7b\u529f\u6697\u5668\uff0c\u8fd8\u8981\u6709\u4e00\u573a\u751f\u6b7b\u62fc\u6218\uff0c\u6211\u5982\u4e0d\u5e78\u6218\u6b7b\uff0c\u4f60 \u4eec\u53ef\u4ee5\u4ece\u5bb9\u6d17\u53bb\u6211\u8138\u4e0a\u836f\u7269\uff0c\u67e5\u660e\u8eab\u4efd\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u5df2\u7ecf\u51b3\u5b9a\u4e86\uff0c\u975e\u5f97\u5206\u51fa\u80dc\u8d1f\uff0c\u624d\u80af\u7f62\u624b\u3002\u201d\u7ea2\u88f3\u9053\uff1a\u201c\u4f60\u8eab\u624b\u4e0d\u9519\uff0c\u4e5f\u8bb8\u5fc3\u4e2d\u65e9\u6709 \u4e86\u80dc\u6211\u7684\u628a\u63e1\uff0c\u624d\u5982\u6b64\u5484\u5484\u903c\u4eba\u3002\u4f46\u6211\u5e0c\u671b\u4f60\u80fd\u660e\u767d\uff0c\u6b66\u529f\u548c\u6740\u4eba\uff0c\u6709\u5f88\u5927\u7684\u533a\u522b\uff0c\u4f60\u7684\u6b66 \u529f\u80dc\u8fc7\u6211\uff0c\u4f46\u6740\u4eba\u65b9\u6cd5\uff0c\u672a\u5fc5\u5c31\u6bd4\u6211\u9ad8\u660e\u4e86\uff0c\u8fd8\u8bf7\u4e09\u601d\uff01\u201d \u3000\u3000\u201c\u6211\u5df2\u7ecf\u60f3\u5f97\u5f88\u6e05\u695a\u4e86\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u5728\u4e0b\u662f\u4e0d\u5230\u9ec4\u6cb3\u4e0d\u6b7b\u5fc3\uff0c\u80fd\u4f5c\u5165\u5e55\u4e4b\u5bbe\u65f6\uff0c\u59d1\u5a18 \u4ea6\u53ef\u89c1\u5230\u5728\u4e0b\u7684\u771f\u9762\u76ee\u4e86\u3002\u201d \u3000\u3000\u7ea2\u88f3\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u5e76\u975e\u4e00\u4e2a\u597d\u8272\u7684\u4eba\uff0c\u5374\u5e38\u628a\u4e0a\u5e8a\u7684\u4e8b\u6302\u5728\u5634\u8fb9\uff0c\u4f60\u7a76\u7adf\u8981\u63a9\u9970 \u4ec0\u4e48\uff1f\u201d\u7a81\u7136\uff0c\u4e00\u626c\u624b\uff0c\u4e24\u628a\u98de\u5200\u4e00\u5148\u4e00\u540e\uff0c\u75be\u5c04\u800c\u81f3\u3002 \u3000\u3000\u4e24\u628a\u5200\u867d\u7136\u540c\u65f6\u51fa\u624b\uff0c\u4f46\u5374\u4fdd\u6301\u4e86\u76f8\u5f53\u7684\u8ddd\u79bb\uff0c\u4e24\u5200\u98de\u884c\u7684\u901f\u5ea6\uff0c\u4e00\u5feb\u4e00\u6162\u3002 \u3000\u3000\u540c\u4e00\u53ea\u624b\uff0c\u80fd\u53d1\u51fa\u4e24\u79cd\u4e0d\u540c\u7684\u529b\u9053\uff0c\u98de\u5200\u624b\u6cd5\u4e4b\u5947\uff0c\u4e5f\u662f\u94c1\u7fce\u751f\u5e73\u4ec5\u89c1\uff0c\u6c5f\u6e56\u4e0a\u4e5f\u672a\u542c \u95fb\u8fc7\u8fd9\u79cd\u624b\u6cd5\u3002\u8fd9\u4e2a\u6df7\u8ff9\u98ce\u5c18\u7684\u5c0f\u4e2b\u5934\uff0c\u7a76\u7adf\u662f\u4ec0\u4e48\u51fa\u8eab\uff1f\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u7adf\u6709\u5982\u6b64\u6210\u5c31\uff1f \u3000\u3000\u5fc3\u4e2d\u5728\u60f3\uff0c\u53f3\u624b\u7d27\u63e1\u7684\u4e24\u67c4\u98de\u5200\uff0c\u4e5f\u6025\u52b2\u5730\u98de\u5c04\u800c\u51fa\u3002 \u3000\u3000\u94c1\u7fce\u662f\u6697\u5668\u9ad8\u624b\uff0c\u5b58\u5fc3\u5356\u5f04\u4e00\u4e0b\uff0c\u8981\u4ee5\u98de\u5200\u51fb\u843d\u98de\u5200\u3002 \u3000\u3000\u91d1\u94c1\u4ea4\u9e23\u58f0\u4e2d\uff0c\u5f53\u5148\u5c04\u6765\u7684\u98de\u5200\uff0c\u679c\u7136\u88ab\u51fb\u843d\uff0c\u4f46\u7b2c\u4e8c\u67c4\u98de\u5200\u5374\u7a81\u7136\u4e0a\u5347\u4e86\u4e09\u5bf8\uff0c\u907f\u5f00 \u4e86\u7b2c\u4e8c\u628a\u98de\u5200\u7684\u8fce\u51fb\uff0c\u5bd2\u5149\u4e00\u95ea\uff0c\u5df2\u5230\u9762\u95e8\u3002 \u3000\u3000\u53cc\u65b9\u7684\u8ddd\u79bb\u592a\u8fd1\u4e86\uff0c\u4e00\u4e2a\u5931\u8bef\uff0c\u5c31\u5f88\u96be\u6709\u8865\u6551\u7684\u673a\u4f1a\uff0c\u98de\u5200\u5df2\u8fd1\u9762\u988a\uff0c\u94c1\u7fce\u4e00\u5f20\u5634\uff0c\u54ac \u4f4f\u4e86\u5229\u5203\u3002 \u3000\u3000\u7ea2\u88f3\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u597d\u529f\u592b\uff0c\u518d\u63a5\u8fd9\u4e09\u628a\u98de\u5200\u3002\u201d\u559d\u58f0\u4e09\u628a\u98de\u5200\u4e00\u7ebf\u98de\u6765\u3002 \u3000\u3000\u94c1\u7fce\u54ea\u91cc\u8fd8\u6562\u5927\u610f\uff0c\u4ef0\u8eab\u4e00\u5f0f\u94c1\u677f\u6865\uff0c\u5168\u8eab\u7740\u5730\uff0c\u98de\u5200\u63a0\u8eab\u800c\u8fc7\uff0c\u4eba\u5374\u6a2a\u91cc\u4e00\u4e2a\u65cb\u8f6c\uff0c \u7ad9\u4e86\u8d77\u6765\uff0c\u5de6\u624b\u4e09\u628a\u98de\u5200\uff0c\u6ca1\u6709\u5c04\u51fa\uff0c\u4f46\u4e09\u67c4\u5200\u5c16\uff0c\u5374\u6307\u5728\u7ea2\u88f3\u7684\u524d\u80f8\u8981\u5bb3\u4e4b\u5904\u3002 \u3000\u3000\u8fd9\u662f\u7531\u5bf9\u654c\u7ecf\u9a8c\u548c\u6280\u827a\u7ec4\u5408\u800c\u6210\u7684\u6740\u4eba\u65b9\u6cd5\uff0c\u9700\u8981\u4e00\u70b9\u667a\u6167\uff0c\u4e5f\u9700\u8981\u76f8\u5f53\u6b66\u529f\u706b\u5019\uff0c\u4f46 \u5b83\u5374\u4e0d\u662f\u6b66\u529f\uff0c\u4e5f\u4e0d\u662f\u6280\u827a\u7684\u7cbe\u6e5b\u53d8\u5316\uff0c\u53ea\u662f\u5f88\u6709\u6548\u7684\u6740\u4eba\u6280\u5de7\u3002 \u3000\u3000\u7ea2\u88f3\u5446\u4f4f\u4e86\uff0c\u8fd9\u662f\u81f4\u547d\u7684\u53cd\u51fb\uff0c\u5728\u90a3\u751f\u6b7b\u4e00\u5239\u90a3\u95f4\uff0c\u5b8c\u5168\u6ca1\u6709\u8fd8\u624b\u548c\u95ea\u907f\u7684\u673a\u4f1a\uff0c\u4efb\u4eba \u5bb0\u5272\u4e86\uff0c\u4f46\u5bf9\u65b9\u5374\u5200\u4e0b\u7559\u60c5\u3002 \u3000\u3000\u8fd9\u662f\u6c5f\u6e56\u4e0a\u7b2c\u4e00\u6d41\u9ad8\u624b\u7684\u53cd\u5e94\u3002 \u3000\u3000\u94c1\u7fce\u7a81\u7136\u5f03\u53bb\u4e86\u624b\u4e2d\u4e09\u628a\u98de\u5200\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u9635\u4e5f\u7b97\u5931\u624b\uff0c\u54b1\u4eec\u5728\u7b2c\u4e09\u9635\u4e0a\u4e00\u51b3\u80dc\u8d1f \u5427\uff01\u201d \u3000\u3000\u201c\u4e0d\uff01\u6211\u8f93\u4e86\uff0c\u8f93\u7684\u5fc3\u670d\uff0c\u201d\u7ea2\u88f3\u7406\u4e00\u4e0b\u98d8\u6d6e\u5728\u9b13\u89d2\u7684\u4e00\u7efa\u9999\u53d1\uff0c\u9053\uff1a\u201c\u4f60\u5df2\u5f97\u5230\u4e86\u5343 \u91d1\u8d3a\u4eea\uff0c\u4e5f\u53ef\u4ee5\u62b1\u6211\u4e0a\u5e8a\uff0c\u8fdb\u5165\u6b66\u9662\u7684\u4eba\uff0c\u4f60\u662f\u7b2c\u4e8c\u4e2a\u548c\u6211\u7ed3\u7f18\u7684\uff0c\u8d70\u5427\uff01\u6625\u5bb5\u4e00\u523b\u503c\u5343\u91d1\uff0c \u4f60\u5df2\u4eba\u8d22\u4e24\u5f97\u3002\u201d\u653e\u4e0b\u624b\u4e2d\u7684\u98de\u5200\uff0c\u8f6c\u8eab\u884c\u53bb\u3002 \u3000\u3000\u94c1\u7fce\u50bb\u4e86\uff0c\u7ea2\u88f3\u867d\u7f8e\uff0c\u4f46\u4ed6\u4e5f\u4e0d\u80fd\u771f\u7684\u7559\u5bbf\u6b66\u9662\uff0c\u7ea2\u88f3\u8f6c\u53d8\u7684\u592a\u5feb\uff0c\u60c5\u52bf\u5ffd\u7136\u5927\u9006\u8f6c\uff0c \u94c1\u7fce\u6709\u70b9\u5e94\u53d8\u4e0d\u53ca\u4e86\uff0c\u4f46\u5df2\u7bad\u5728\u5f26\u4e0a\uff0c\u4e0d\u5f97\u4e0d\u53d1\uff0c\u53ea\u597d\u8ddf\u5728\u8eab\u540e\u8d70\u4e86\u3002 \u3000\u3000\u60c5\u52bf\u8be1\u5f02\u7684\u5f88\uff0c\u4ee5\u94c1\u7fce\u7ecf\u9a8c\u4e4b\u4e30\uff0c\u4e5f\u65e0\u6cd5\u5224\u5b9a\uff0c\u7ea2\u88f3\u662f\u771f\u7684\u4e3a\u4ed6\u5200\u4e0b\u7559\u60c5\u52a8\u4e86\u5fc3\uff0c\u8fd8\u662f \u8981\u6362\u4e00\u79cd\u624b\u6bb5\u5bf9\u4ed8\u4ed6\uff1f \u3000\u3000------------- \u3000\u3000\u5b64\u5251\u751f  \u626b\u63cf\u3000  \u695a\u5929\u4fa0\u5f71  OCR  \uff0c\u65e7\u96e8\u697c\u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c \u56db \u7ae0 \u3000\u3000\u8fd9\u662f\u96c5\u81f4\u7684\u5c0f\u5385\uff0c\u5c0f\u5385\u8fde\u5367\u5ba4\uff0c\u4e00\u5f20\u7ee3\u7740\u98de\u51e4\u7684\u5782\u5e18\u9694\u6210\u5385\u3001\u5ba4\u4e24\u95f4\u3002 \u3000\u3000\u5c0f\u5385\u4e0a\u6728\u6848\u540e\u65b9\uff0c\u4f9b\u5949\u4e86\u4e00\u4e2a\u795e\u50cf\uff0c\u7528\u4e00\u9053\u9ec4\u7f0e\u5b50\u5782\u5e18\u906e\u4e86\u8d77\u6765\uff0c\u65e0\u6cd5\u770b\u5230\u662f\u4f9b\u5949\u7684\u4ec0 \u4e48\u795e\u7957\uff1f \u3000\u3000\u9752\u697c\u5973\u5b50\u5367\u5ba4\u4e2d\u4f9b\u5949\u795e\u50cf\uff0c\u53ef\u662f\u4ece\u672a\u542c\u95fb\u8fc7\u7684\uff0c\u4f9b\u7684\u662f\u4ec0\u4e48\u795e\u5462\uff1f\u94c1\u7fce\u7a81\u7136\u751f\u51fa\u4e00\u7779\u795e \u7957\u7684\u597d\u5947\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u4f38\u624b\u53bb\u63ed\u9ec4\u5e18\u3002 \u3000\u3000\u201c\u8001\u7237\u5b50\uff0c\u4f60\u7528\u8336\u3002\u201d\u4e24\u4e2a\u9752\u8863\u5973\u5a62\uff0c\u9c7c\u8d2f\u800c\u5165\u3002 \u3000\u3000\u4e00\u4e2a\u624b\u6367\u8336\u76d8\u9999\u8317\uff0c\u4e00\u4e2a\u6367\u7740\u4e24\u53ea\u5927\u7ea2\u70db\u3002 \u3000\u3000\u94c1\u7fce\u53ea\u5f97\u53c8\u5750\u4e86\u4e0b\u53bb\uff0c\u90a3\u662f\u795e\u6848\u524d\u4e00\u5f20\u516b\u4ed9\u684c\uff0c\u4e24\u4fa7\u653e\u7740\u6728\u6905\uff0c\u4f46\u5fc3\u4e2d\u6697\u53eb\u53ef\u60dc\uff0c\u4e00\u6b65 \u4e4b\u5dee\uff0c\u672a\u80fd\u63ed\u5f00\u9ec4\u7f0e\u5b50\u5782\u5e18\u4e00\u770b\u7a76\u7adf\uff0c\u4e5f\u8bb8\u90a3\u795e\u50cf\u80fd\u7ed9\u4ed6\u4e00\u70b9\u542f\u793a\uff0c\u8fd9\u662f\u4e2a\u4ec0\u4e48\u6837\u5b50\u7684\u7ec4\u5408\u3002 \u3000\u3000\u4e24\u4e2a\u5973\u5a62\u672a\u518d\u7406\u4f1a\u94c1\u7fce\uff0c\u53ea\u7ba1\u81ea\u5df1\u5de5\u4f5c\uff0c\u4e24\u53ea\u7ea2\u70db\u71c3\u8d77\uff0c\u5206\u653e\u5728\u795e\u50cf\u4e24\u4fa7\uff0c\u540c\u65f6\u5728\u795e\u6848 \u4e4b\u524d\uff0c\u94fa\u4e0a\u4e86\u4e00\u4e2a\u9ec4\u8272\u7684\u62dc\u57ab\uff0c\u53e6\u4e00\u4e2a\u5973\u5a62\u7aef\u7740\u4e00\u4e2a\u9762\u76c6\uff0c\u4e00\u6761\u96ea\u767d\u7684\u9762\u5dfe\uff0c\u5df2\u653e\u5728\u9762\u76c6\u4e2d\uff0c \u6c34\u8fd8\u5192\u7740\u70ed\u6c14\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u6d17\u8138\uff0c\u62dc\u8fc7\u7956\u5e08\u7237\uff0c\u5c31\u53ef\u4ee5\u548c\u59d1\u5a18\u5165\u6d1e\u623f\u4e86\uff01\u201d \u3000\u3000\u53c8\u662f\u4e00\u4e2a\u4ece\u672a\u542c\u95fb\u8fc7\u7684\u89c4\u77e9\uff0c\u5ad6\u59d1\u5a18\u8fd8\u8981\u5148\u62dc\u7956\u5e08\u7237\uff0c\u592a\u9686\u91cd\u4e86\u5427\uff01\u4ec0\u4e48\u6837\u7684\u7956\u5e08\u7237\uff0c \u7ba1\u7406\u8fd9\u4e00\u884c\u5f53\uff0c\u4e5f\u672a\u542c\u4eba\u8bf4\u8fc7\u3002 \u3000\u3000\u94c1\u7fce\u671b\u7740\u90a3\u70ed\u6c14\u84b8\u817e\u7684\u9762\u6c34\uff0c\u6c89\u541f\u96be\u51b3\uff0c\u6d17\u4e2a\u8138\u5f53\u7136\u5f88\u5bb9\u6613\uff0c\u4f46\u4e00\u6d17\u5c31\u8981\u73b0\u51fa\u672c\u6765\u9762\u76ee\uff0c \u626c\u5dde\u5e9c\u7684\u603b\u6355\u5934\u94c1\u7fce\uff0c\u53ef\u662f\u5927\u540d\u9f0e\u9f0e\u7684\u4eba\u7269\uff0c\u626c\u5dde\u5730\u9762\u4e0a\uff0c\u4e0d\u8ba4\u8bc6\u4ed6\u7684\u4eba\uff0c\u5b9e\u5728\u4e0d\u591a\u3002 \u3000\u3000\u5c31\u7b97\u7ea2\u88f3\u4e0d\u8ba4\u8bc6\u4ed6\uff0c\u4ea6\u5fc5\u4f1a\u6709\u4eba\u6697\u4e2d\u544a\u8bc9\u7ea2\u88f3\uff0c\u94c1\u7fce\u4e00\u76f4\u8ba4\u5b9a\uff0c\u6709\u4eba\u5728\u6697\u4e2d\u76d1\u89c6\u4ed6\u3002 \u3000\u3000\u5782\u5e18\u542f\u52a8\uff0c\u7ea2\u88f3\u7f13\u6b65\u800c\u51fa\uff0c\u6d74\u7f62\u9999\u6c64\u6362\u65b0\u88c5\uff0c\u957f\u53d1\u62ab\u5728\u9999\u80a9\u4e0a\uff0c\u4f53\u6001\u73b2\u73d1\u3001\u9762\u76ee\u59e3\u597d\uff0c \u5b9e\u5728\u662f\u4e00\u4e2a\u5341\u5206\u52a8\u4eba\u7684\u7f8e\u5973\uff0c\u53ea\u770b\u7684\u94c1\u7fce\u6697\u4e2d\u8fd0\u6c14\u8c03\u606f\uff0c\u538b\u5236\u4e0b\u6ce2\u52a8\u7684\u5fc3\u60c5\u3002 \u3000\u3000\u201c\u8001\u7237\u5b50\uff0c\u6d17\u4e2a\u6fa1\u5427\uff1f\u8fd9\u91cc\u6709\u6fa1\u5858\u5b50\u4f3c\u7684\u5927\u6d74\u6c60\u3002\u201d\u7ea2\u88f3\u7b11\u9053\uff1a\u201c\u65b0\u88c5\uff0c\u90fd\u5df2\u51c6\u5907\u59a5\u5f53\uff0c \u5c31\u7b49\u4f60\u6d74\u7f62\u66f4\u8863\u4f5c\u65b0\u90ce\u4e86\u3002\u201d \u3000\u3000\u94c1\u7fce\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u6709\u52b3\u59d1\u5a18\u5e26\u8def\u4e86\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u6697\u4f5c\u76d8\u7b97\uff0c\u501f\u6c90\u6d74\u65f6\u95f4\u597d\u597d\u8ba1\u5212\u4e00\u4e0b\uff0c\u8981\u5c3d\u5feb\u67e5\u660e\u5185\u60c5\u3002\u60c5\u52bf\u5982\u82e5\u53d8\u5316\u592a\u5927\uff0c\u4e0d\u80fd \u4ee5\u4e2a\u4eba\u529b\u91cf\u63a7\u5236\u65f6\uff0c\u5c31\u8c03\u52a8\u73ed\u5934\u6355\u5feb\uff0c\u5927\u6279\u5b98\u5175\uff0c\u5148\u628a\u56db\u51e4\u697c\u4e2d\u4eba\u6293\u4e86\uff0c\u518d\u8ffd\u67e5\u5185\u60c5\u3002 \u3000\u3000\u6709\u4e86\u6700\u540e\u7684\u6253\u7b97\uff0c\u94c1\u7fce\u5fc3\u4e2d\u8f7b\u677e\u591a\u4e86\uff0c\u8138\u4e0a\u4e5f\u6cdb\u8d77\u4e86\u7b11\u610f\u3002 \u3000\u3000\u7ea2\u88f3\u4e00\u76f4\u5728\u6697\u4e2d\u7559\u795e\u7740\u94c1\u7fce\u7684\u795e\u6001\u53d8\u5316\uff0c\u770b\u4ed6\u795e\u60c5\u6109\u60a6\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u4ed6\u7a81\u7136\u4e4b\u95f4 \u5feb\u4e50\u8d77\u6765\uff0c\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u662f\u5373\u5c06\u62b1\u6211\u4e0a\u5e8a\u7684\u559c\u60a6\uff1f\u8fd8\u662f\u522b\u6709\u7528\u5fc3\u7684\u5b89\u6392\uff1f\u4f46\u6b64\u5730\u4e0e\u5916\u754c \u5b8c\u5168\u9694\u7edd\uff0c\u5f88\u96be\u628a\u8baf\u606f\u4f20\u8fbe\u51fa\u53bb\uff0c\u8fd9\u4e2a\u4eba\u597d\u751f\u96be\u6d4b\u9ad8\u6df1\uff01 \u3000\u3000\u767d\u7389\u780c\u7684\u6c34\u6c60\uff0c\u6bd4\u8d77\u4e00\u822c\u7684\u6d17\u6fa1\u76c6\u5b50\uff0c\u8981\u5927\u5341\u500d\u4ee5\u4e0a\u3002 \u3000\u3000\u56de\u5934\u5173\u4e0a\u623f\u95e8\uff0c\u6253\u91cf\u4e86\u6d74\u5ba4\u4e2d\u7684\u666f\u7269\uff0c\u77a7\u4e0d\u51fa\u53ef\u7591\u5730\u65b9\uff0c\u8131\u8863\u4e0b\u6c34\uff0c\u51c6\u5907\u597d\u597d\u6c90\u6d74\u4e00\u756a\uff0c \u518d\u6253\u8d77\u7cbe\u795e\u5e94\u4ed8\u53d8\u5c40\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e00\u9635\u6de1\u6de1\u7684\u9999\u6c14\uff0c\u6251\u5165\u4e86\u9f3b\u4e2d\uff0c\u94c1\u7fce\u867d\u7136\u5df2\u653e\u677e\u4e86\u5fc3\u60c5\uff0c\u4ecd\u7136\u4fdd\u6301\u8b66\u89c9\uff0c\u611f\u5230\u9999 \u6c14\u6765\u7684\u7a81\u7136\u3002\u4f46\u4e3a\u65f6\u5df2\u665a\uff0c\u4eba\u521a\u7ad9\u8d77\uff0c\u53c8\u6655\u5012\u6c60\u4e2d\u3002 \u3000\u3000\u9192\u6765\u65f6\u666f\u7269\u5df2\u53d8\uff0c\u5750\u4e0a\u5ba2\u6210\u4e86\u9636\u4e0b\u56da\uff0c\u53ea\u89c9\u5f97\u8eba\u5728\u4e00\u5806\u5f88\u539a\u7684\u8305\u8349\u4e0a\uff0c\u53cc\u624b\u53cc\u8db3\uff0c\u90fd\u88ab \u6cb9\u6d78\u7684\u4e1d\u7d22\u6346\u4f4f\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u94c1\u7fce\u975e\u5e38\u6c89\u5f97\u4f4f\u6c14\uff0c\u6697\u4e2d\u8c03\u606f\u547c\u5438\uff0c\u611f\u89c9\u529f\u529b\u5e76\u672a\u6d88\u5931\uff0c\u624d\u5fae\u4e00\u633a\u8170\uff0c\u5750\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u94c1\u603b\u6355\u5934\uff0c\u8eab\u4efd\u5df2\u7ecf\u66b4\u9732\uff0c\u6700\u597d\u522b\u8f7b\u4e3e\u5984\u52a8\uff0c\u5982\u80fd\u88c5\u4f5c\u836f\u529b\u672a\u9000\uff0c\u6c89\u7761\u672a\u9192\uff0c\u90a3\u624d\u662f \u4e0a\u4e0a\u4e4b\u7b56\u4e86\u2026\u2026\u201d\u58f0\u97f3\u5f88\u4f4e\u6c89\uff0c\u4f46\u5374\u542c\u5f97\u5b57\u5b57\u4eba\u8033\uff0c\u53ef\u60dc\u8a00\u672a\u5c3d\u610f\uff0c\u8bed\u58f0\u7a81\u7136\u4e2d\u65ad\u3002 \u3000\u3000\u4f46\u8fd9\u5df2\u8ba9\u94c1\u7fce\u9707\u60ca\u4e86\uff0c\u8eab\u4efd\u66b4\u9732\uff0c\u90a3\u662f\u8bf4\u8138\u4e0a\u7684\u6613\u5bb9\u836f\u7269\uff0c\u5df2\u7ecf\u88ab\u6d17\u53bb\uff0c\u8ba4\u51fa\u4ed6\u662f\u8c01\u4e86\u3002 \u3000\u3000\u4e00\u9635\u6b65\u5c65\u58f0\uff0c\u4f20\u4e86\u8fdb\u6765\uff0c\u58f0\u97f3\u5f88\u5fae\u5c0f\uff0c\u4f46\u94c1\u7fce\u542c\u5230\u4e86\uff0c\u4e5f\u4e86\u89e3\u90a3\u4eba\u7a81\u7136\u4e2d\u65ad\u4e86\u8bed\u58f0\u7684\u539f \u56e0\u3002 \u3000\u3000\u968f\u7740\u90a3\u9010\u6e10\u63a5\u8fd1\u7684\u811a\u6b65\u58f0\uff0c\u94c1\u7fce\u53c8\u7f13\u7f13\u8eba\u4e86\u4e0b\u53bb\uff0c\u4f46\u8fd0\u8db3\u76ee\u529b\uff0c\u56db\u4e0b\u6253\u91cf\u3002 \u3000\u3000\u53d1\u89c9\u8fd9\u662f\u4e00\u5927\u95f4\u5bc6\u5ba4\uff0c\u5730\u4e0a\u94fa\u7740\u5f88\u539a\u7684\u8305\u8349\uff0c\u5bf9\u9762\u4e00\u89d2\u5904\uff0c\u5750\u7740\u4e00\u4e2a\u9ed1\u8863\u4eba\uff0c\u80cc\u9760\u5899\u58c1\uff0c \u53cc\u8098\u653e\u5728\u819d\u76d6\u4e0a\uff0c\u4f4e\u5934\u4f0f\u81c2\u5f2f\u4e2d\u4f11\u606f\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u94c1\u7fce\u7528\u5c3d\u4e86\u76ee\u529b\uff0c\u4e5f\u770b\u4e0d\u5230\u4ed6\u7684\u9762\u76ee\u3002 \u3000\u3000\u5f00\u542f\u94c1\u9501\u7684\u58f0\u97f3\u4f20\u6765\uff0c\u94c1\u7fce\u4e5f\u540c\u65f6\u95ed\u4e0a\u4e86\u773c\u775b\u3002 \u3000\u3000\u611f\u89c9\u4e2d\uff0c\u6709\u4eba\u8fdb\u4e86\u5bc6\u5ba4\uff0c\u800c\u4e14\uff0c\u4e0d\u662f\u4e00\u4eba\u3002 \u3000\u3000\u4e00\u80a1\u719f\u6089\u7684\u8102\u7c89\u9999\u6c14\uff0c\u6251\u5165\u9f3b\u4e2d\uff0c\u94c1\u7fce\u80af\u5b9a\u4e86\u6765\u4eba\u4e2d\uff0c\u4e00\u4e2a\u662f\u7ea2\u88f3\u59d1\u5a18\u3002 \u3000\u3000\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u94c1\u7fce\u8fd8\u4e00\u76f4\u60f3\u4e0d\u51fa\u6d74\u5ba4\u4e2d\u90a3\u80a1\u8ff7\u9999\uff0c\u662f\u5982\u4f55\u9001\u5165\u6d74\u5ba4\u7684\u3002 \u3000\u3000\u4ed6\u4ed4\u7ec6\u5730\u67e5\u8fc7\u4e86\u56db\u5468\u7684\u73af\u5883\uff0c\u80af\u5b9a\u56db\u58c1\u6ca1\u6709\u653e\u5165\u8ff7\u9999\u5b54\u9053\u3002 \u3000\u3000\u4f46\u4ed6\u88ab\u8ff7\u5012\u4e86\uff0c\u5982\u65e0\u4eba\u53ca\u65f6\u6551\u63f4\uff0c\u90a3\u5ea7\u767d\u7389\u780c\u6210\u7684\u6c34\u6c60\uff0c\u5c31\u53ef\u80fd\u662f\u4ed6\u57cb\u9aa8\u7684\u6240\u5728\u2026\u2026\u3002 \u3000\u3000\u611f\u89c9\u4e2d\uff0c\u6709\u4e24\u4e2a\u4eba\u884c\u8fd1\u4e86\u4ed6\u8eab\u4fa7\uff0c\u94c1\u7fce\u6f84\u6e05\u5fc3\u4e2d\u6742\u5ff5\u5c3d\u91cf\u4f7f\u547c\u5438\u5e73\u987a\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u90fd\u662f\u6b66\u6797\u9ad8\u624b\uff0c\u53ea\u8981\u547c\u5438\u4e0a\u9732\u51fa\u7591\u70b9\uff0c\u4ed6\u4eec\u90fd\u53ef\u80fd\u63a8\u65ad\u51fa\uff0c\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\u3002\u8981 \u88c5\u5c31\u8981\u88c5\u7684\u4e00\u4e1d\u4e0d\u82df\uff0c\u4e0d\u9732\u51fa\u4efb\u4f55\u7834\u7efd\u3002 \u3000\u3000\u679c\u7136\uff0c\u4e00\u53ea\u624b\u4f38\u8fc7\u6765\u6309\u5728\u4ed6\u524d\u80f8\u4e4b\u4e0a\uff0c\u8db3\u8db3\u505c\u7559\u4e86\u4e00\u523b\u5de5\u592b\u4e4b\u4e45\u3002 \u3000\u3000\u8fd9\u662f\u975e\u5e38\u5371\u9669\u7684\u4e00\u523b\uff0c\u53ea\u8981\u8ba9\u5bf9\u65b9\u53d1\u89c9\u4e86\u60c5\u52bf\u5df2\u6709\u8fc7\u53d8\u5316\uff0c\u90a3\u6309\u5728\u524d\u80f8\u7684\u638c\u52bf\uff0c\u5c31\u53ef\u53d1 \u51fa\u5185\u529b\uff0c\u4e00\u4e3e\u9707\u65ad\u94c1\u7fce\u7684\u5fc3\u8109\u3002\u5728\u6781\u5927\u7684\u538b\u529b\u4e0b\uff0c\u4fdd\u6301\u5e73\u9759\uff0c\u4e0d\u8ba9\u5fc3\u810f\u7684\u8df3\u52a8\u53d1\u751f\u53d8\u5316\uff0c\u975e \u5e38\u56f0\u96be\u3002 \u3000\u3000\u4f46\u94c1\u7fce\u505a\u7684\u5f88\u597d\u3002 \u3000\u3000\u201c\u5fc3\u8df3\u7684\u5f88\u5e73\u987a\uff0c\u4f53\u6001\u59ff\u52bf\u4e5f\u672a\u6539\u53d8\uff0c\u4f3c\u662f\u836f\u529b\u4ecd\u5728\u53d1\u4f5c\u4e2d\uff0c\u4e5f\u672b\u6e05\u9192\u8fc7\u3002\u201d \u3000\u3000\u679c\u7136\u662f\u7ea2\u88f3\u7684\u58f0\u97f3\uff0c\u8fd9\u91cc\u4e0d\u4f46\u8bbe\u4e0b\u4e86\u6843\u8272\u7684\u8bf1\u9975\uff0c\u4e5f\u6709\u6b7b\u4ea1\u7684\u5e03\u5c40\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u53eb\u94c1\u7fce\uff0c\u626c\u5dde\u5e9c\u73b0\u4efb\u603b\u6355\u5934\uff0c\u672c\u662f\u6c5f\u6e56\u767d\u9053\u4e2d\u4e00\u4f4d\u9ad8\u624b\uff0c\u5374\u88ab\u73b0\u4efb\u7684\u626c\u5dde\u77e5\u5e9c \u738b\u5c11\u537f\u7f57\u81f4\u4e3a\u516d\u6247\u95e8\u4e2d\u7684\u9e70\u72ac\u3002\u5176\u4eba\u6b66\u529f\u9ad8\u5f3a\uff0c\u4e3a\u626c\u5dde\u5e9c\u7acb\u4e0b\u4e86\u4e0d\u5c11\u7684\u529f\u52b3\uff0c\u7834\u4e86\u4e0d\u5c11\u5927\u6848 \u5b50\uff0c\u6293\u4e86\u4e0d\u5c11\u7eff\u6797\u9053\u4e0a\u7684\u670b\u53cb\uff0c\u6709\u5f88\u591a\u9ed1\u9053\u670b\u53cb\uff0c\u6068\u4ed6\u5165\u9aa8\uff0c\u8fd9\u4e2a\u4eba\u8981\u4e25\u52a0\u9632\u8303\u3002\u201d\u7a81\u7136\uff0c \u843d\u6307\u5982\u98ce\uff0c\u70b9\u4e86\u94c1\u7fce\u4e09\u5904\u7a74\u9053\u3002 \u3000\u3000\u4e09\u5904\u7a74\u9053\u53d7\u5236\uff0c\u5c31\u7b97\u94c1\u7fce\u60f3\u53cd\u6297\uff0c\u4e5f\u662f\u529b\u6240\u4e0d\u80fd\u4e86\uff0c\u5fc3\u4e2d\u6709\u4e9b\u61ca\u607c\uff0c\u4f46\u8033\u6735\u80fd\u542c\uff0c\u5934\u8111 \u80fd\u60f3\uff0c\u8bf4\u8bdd\u7684\u4ecd\u662f\u90a3\u4e2a\u7537\u4eba\uff0c\u800c\u4e14\uff0c\u53e3\u97f3\u6709\u70b9\u719f\uff0c\u53ef\u80fd\u662f\u8ba4\u8bc6\u7684\u4eba\u3002\u80fd\u8ba4\u51fa\u8fd9\u4e2a\u4eba\u7684\u8eab\u4efd\uff0c \u8fd9\u56db\u51e4\u697c\u4e2d\u7684\u6b66\u9662\u4e4b\u79d8\uff0c\u5927\u6982\u5c31\u53ef\u4ee5\u63ed\u5f00\u4e00\u534a\uff0c\u53ea\u8981\u7741\u5f00\u773c\u775b\u77a7\u4e00\u4e0b\uff0c\u5c31\u53ef\u4ee5\u8ba4\u51fa\u4ed6\u662f\u8c01\uff0c \u800c\u94c1\u7fce\u4ecd\u6709\u7741\u773c\u775b\u7684\u80fd\u529b\u3002 \u3000\u3000\u4f46\u4ed6\u5fcd\u4e0b\u53bb\u4e86\uff0c\u7741\u773c\u4e00\u770b\uff0c\u53ef\u80fd\u8ba4\u51fa\u90a3\u4eba\u8eab\u4efd\uff0c\u4f46\u4e5f\u53ef\u80fd\u62db\u6765\u6740\u8eab\u4e4b\u7978\uff0c\u4e09\u5904\u7a74\u9053\u88ab\u70b9\uff0c \u9650\u5236\u4e86\u4ed6\u80a2\u4f53\u8fd0\u8f6c\u7684\u80fd\u529b\uff0c\u5b8c\u5168\u662f\u4efb\u4eba\u5bb0\u5272\u7684\u5c40\u9762\u3002 \u3000\u3000\u4ed6\u65e0\u6cd5\u5224\u5b9a\u7ea2\u88f3\u548c\u90a3\u4eba\u662f\u5426\u5df2\u771f\u7684\u79bb\u53bb\uff0c\u5728\u6ca1\u6709\u80af\u5b9a\u4ed6\u4eec\u79bb\u5f00\u4e4b\u524d\uff0c\u94c1\u7fce\u4e0d\u6562\u7741\u773c\u77a7\u770b\uff0c \u4e5f\u4e0d\u6562\u5984\u52a8\u4e00\u4e0b\u3002 \u3000\u3000\u53ea\u542c\u7ea2\u88f3\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u9662\u4e3b\uff1f\u4ed6\u5982\u82e5\u6e05\u9192\u4e86\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u5982\u6b64\u7684\u6c89\u6f5c\u4e0d\u52a8\uff0c\u81f3\u5c11 \u5e94\u7728\u52a8\u4e00\u4e0b\u773c\u775b\u3002\u201d \u3000\u3000\u201c\u55ef\uff01\u54b1\u4eec\u53bb\u77a7\u77a7\u90a3\u4e00\u4e2a\u5427\uff01\u201d \u3000\u3000\u94c1\u7fce\u6697\u53eb\u4e86\u4e24\u58f0\u60ed\u6127\uff0c\u5982\u975e\u4e8b\u5148\u5f97\u90a3\u4eba\u8b66\u544a\uff0c\u8981\u4ed6\u88c5\u4f5c\u836f\u529b\u672a\u9192\uff0c\u6700\u540e\u7684\u7ed3\u679c\u662f\u4e00\u573a\u640f \u6740\uff0c\u7ea2\u88f3\u52a0\u4e0a\u9662\u4e3b\uff0c\u8fd9\u7ed3\u679c\u5c31\u4e0d\u7528\u591a\u60f3\u4e86\u3002\u90a3\u4e2a\u4eba\u6307\u70b9\u4ed6\u88c5\u4f5c\u836f\u529b\u672a\u5c3d\uff0c\u8ba9\u4ed6\u66f4\u8fdb\u4e00\u6b65\uff0c\u542c \u5230\u4e86\u4e0d\u5c11\u56db\u51e4\u697c\u4e2d\u7684\u9690\u6848\uff0c\u4e5f\u6551\u4e86\u4ed6\u4e00\u6761\u6027\u547d\u3002 \u3000\u3000\u73b0\u5728\uff0c\u94c1\u7fce\u6700\u60f3\u660e\u767d\u7684\uff0c\u5c31\u662f\u540c\u65e5\u5728\u8fd9\u5ea7\u5bc6\u5ba4\u4e2d\u4eba\u7684\u8eab\u4efd\u3002 \u3000\u3000\u4ed6\u5df2\u77e5\u6653\u90a3\u4eba\u5f88\u6e05\u9192\uff0c\u672a\u4e3a\u836f\u7269\u63a7\u5236\uff0c\u4f46\u9662\u4e3b\u548c\u7ea2\u88f3\u662f\u5426\u5df2\u6e05\u695a\u5462\uff1f\u90a3\u4eba\u7528\u4ec0\u4e48\u65b9\u6cd5\u4fdd \u62a4\u81ea\u5df1\u5462\uff1f \u3000\u3000\u6240\u4ee5\uff0c\u94c1\u7fce\u5f88\u7528\u5fc3\u518d\u542c\uff0c\u4e00\u9762\u6697\u6697\u8fd0\u6c14\u8bd5\u88ab\u70b9\u7684\u7a74\u9053\uff0c\u5e0c\u671b\u5c3d\u5feb\u6062\u590d\u6218\u6597\u7684\u80fd\u529b\u3002 \u3000\u3000\u201c\u7ea2\u88f3\uff0c\u4f60\u505a\u7684\u5f88\u597d\uff0c\u8bf1\u6355\u94c1\u7fce\uff0c\u66f4\u662f\u5927\u529f\u4e00\u4ef6\uff0c\u8fd9\u4e2a\u4eba\u4e0d\u597d\u5bf9\u4ed8\uff0c\u673a\u667a\u6b66\u529f\uff0c\u90fd\u5c5e\u4e00 \u6d41\uff0c\u518d\u52a0\u4e0a\u4e30\u5bcc\u7684\u6c5f\u6e56\u9605\u5386\uff0c\u548c\u5b98\u5e9c\u7684\u80cc\u666f\uff0c\u697c\u4e3b\u65e9\u5df2\u6709\u9664\u6389\u4ed6\u7684\u610f\u601d\uff0c\u4f46\u5374\u8fdf\u8fdf\u4e0d\u6562\u4e0b\u624b\uff0c \u518d\u5f3a\u5927\u7684\u6c5f\u6e56\u7ec4\u5408\uff0c\u4e5f\u4e0d\u80fd\u548c\u5343\u519b\u4e07\u9a6c\u7684\u5b98\u5175\u5bf9\u6297\uff0c\u4f46\u613f\u4ed6\u8fd9\u6b21\u9677\u8eab\u6b66\u9662\u7684\u4e8b\uff0c\u5c5e\u4e8e\u4e2a\u4eba\u7684 \u673a\u5bc6\u884c\u52a8\uff0c\u626c\u5dde\u5e9c\u4e2d\u4eba\u4e0d\u77e5\u9053\u4ed6\u7684\u4e0b\u843d\u2026\u2026\u201d\u9662\u4e3b\u5401\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u697c\u4e3b\u4ee4\u591c\u56de\u6765\uff0c\u7531\u4ed6\u6765 \u51b3\u5b9a\u94c1\u7fce\u7684\u751f\u6b7b\u547d\u8fd0\u5427\uff01\u201d \u3000\u3000\u201c\u9662\u4e3b\uff0c\u94c1\u7fce\u6613\u5bb9\u800c\u6765\uff0c\u5e94\u8be5\u662f\u5355\u72ec\u884c\u52a8\u3002\u201d\u7ea2\u88f3\u9053\uff1a\u201c\u626c\u5dde\u5e9c\u73b0\u5728\u4ecd\u4e0d\u89c1\u53cd\u5e94\uff0c\u60f3\u6765 \u4e0d\u4f1a\u77e5\u9053\u4ed6\u884c\u8e2a\u4f55\u5904\u4e86\uff0c\u5012\u662f\u8fd9\u4f4d\u53c8\u804b\u3001\u53c8\u54d1\u7684\u4ec1\u5144\uff0c\u5df2\u7ecf\u5173\u4e86\u4e03\u5929\uff0c\u5e94\u8be5\u65e9\u4e9b\u5904\u7f6e\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4e00\u76f4\u6000\u7591\u4ed6\u662f\u88c5\u804b\u4f5c\u54d1\uff0c\u4e5f\u6000\u7591\u4ed6\u662f\u94c1\u7fce\u7684\u597d\u670b\u53cb\uff0c\u53d7\u547d\u63a2\u67e5\u56db\u51e4\u697c\uff0c\u6240\u4ee5\uff0c\u7559\u4e0b \u4ed6\u4ee5\u4f5c\u4eba\u8d28\u3002\u201d\u9662\u4e3b\u9053\uff1a\u201c\u5982\u4eca\u94c1\u7fce\u5df2\u7ecf\u88ab\u64d2\uff0c\u8fd9\u804b\u54d1\u5c0f\u5b50\uff0c\u751f\u6b7b\u4f55\u8db3\u6302\u9f7f\uff0c\u7b49\u697c\u4e3b\u56de\u6765\u540e\uff0c \u51b3\u5b9a\u4e86\u94c1\u7fce\u751f\u6b7b\uff0c\u5c4a\u65f6\u4e00\u5e76\u5904\u51b3\uff0c\u518d\u6bc1\u5c38\u706d\u8ff9\uff0c\u4e00\u5207\u5c31\u5316\u4f5c\u4e4c\u6709\u4e86\u3002\u201d \u3000\u3000\u6b65\u5c65\u58f0\u52a8\uff0c\u9662\u4e3b\u548c\u7ea2\u88f3\u4f3c\u5df2\u79bb\u53bb\u3002 \u3000\u3000\u94c1\u7fce\u5fc3\u4e2d\u5fd6\u9053\uff1a\u539f\u6765\u4ed6\u88c5\u804b\u4f5c\u54d1\uff0c\u8ba9\u4eba\u5bf9\u4ed6\u83ab\u6d4b\u9ad8\u6df1\uff0c\u8fd9\u5012\u662f\u4e2a\u4e0a\u4e58\u7684\u7b56\u7565\uff0c\u5bf9\u65b9\u66ff\u4ed6 \u8bbe\u5b9a\u8eab\u4efd\uff0c\u8ba9\u5b9a\u4ed6\u662f\u8c01\uff0c\u5c31\u662f\u8c01\u4e86\u3002 \u3000\u3000\u4f46\u94c1\u7fce\u5fc3\u4e2d\u660e\u767d\uff0c\u4ed6\u4e0d\u804b\u4e0d\u54d1\uff0c\u800c\u4e14\u8033\u806a\u76ee\u654f\uff0c\u6280\u827a\u7cbe\u6e5b\uff0c\u53ea\u6709\u6b66\u529f\u9ad8\u660e\uff0c\u81ea\u4fe1\u5f88\u5f3a\u7684 \u4eba\uff0c\u624d\u6562\u8fd9\u4e48\u63d0\u7740\u8111\u888b\u73a9\uff0c\u89c6\u51f6\u9669\u5982\u5c65\u5e73\u5730\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4e5f\u9700\u8981\u6781\u9ad8\u7684\u667a\u6167\u3001\u624d\u80fd\u3001\u7b56\u7565\uff0c\u88c5\u9f99\u50cf\u9f99\uff0c\u626e\u864e\u50cf\u864e\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u4f1a\u662f\u8c01\u5462\uff1f\u94c1\u7fce\u7ec6\u6570\u76f8\u8bc6\u7684\u670b\u53cb\u3001\u9ad8\u624b\uff0c\u5c31\u662f\u60f3\u4e0d\u51fa\u8fd9\u4e48\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u7741\u773c\u77a7\u770b\uff0c\u5bc6\u5ba4\u9ed1\u6697\u4f9d\u65e7\uff0c\u8fd9\u5ea7\u5bc6\u5ba4\uff0c\u53ef\u80fd\u7b51\u5efa\u5728\u5730\u5e73\u9762\u4e0b\uff0c\u4e00\u76f4\u4e0d\u89c1\u5149\u7ebf\u900f\u5165\u3002 \u3000\u3000\u4f46\u94c1\u7fce\u76ee\u529b\uff0c\u5df2\u80fd\u9002\u5e94\u9ed1\u6697\uff0c\u9662\u4e3b\u548c\u7ea2\u88f3\uff0c\u786e\u5df2\u79bb\u53bb\uff0c\u90a3\u5750\u5728\u5bf9\u9762\u7684\u9ed1\u8863\u4eba\uff0c\u4ecd\u7136\u662f\u539f \u6765\u7684\u5750\u59ff\u672a\u53d8\uff0c\u53cc\u624b\u653e\u5728\u819d\u76d6\u4e0a\uff0c\u8138\u988a\u85cf\u5728\u81c2\u5f2f\u4e2d\u3002 \u3000\u3000\u4f46\u95fb\u90a3\u9ed1\u8863\u4eba\u8bf4\u9053\uff1a\u201c\u6b64\u523b\u5927\u6982\u662f\u592a\u9633\u4e0b\u5c71\u65f6\u5206\uff0c\u8ddd\u5929\u9ed1\u5df2\u4e0d\u592a\u8fdc\uff0c\u6211\u76f8\u4fe1\u90a3\u4f4d\u697c\u4e3b\u56de \u6765\u4e4b\u540e\uff0c\u7edd\u4e0d\u4f1a\u653e\u8fc7\u54b1\u4eec\uff0c\u773c\u4e0b\u54b1\u4eec\u6025\u9700\u8c03\u5242\u4f53\u80fd\uff0c\u4f7f\u5b83\u4fdd\u6709\u6700\u4f73\u72b6\u51b5\uff0c\u4ee5\u5e94\u4ed8\u53ef\u80fd\u6765\u4e34\u7684 \u4e00\u573a\u6076\u6218\u3002\u4f60\u7a74\u9053\u53d7\u5236\uff0c\u662f\u5426\u8981\u6211\u5e2e\u4e00\u628a\u5462\uff1f\u201d \u3000\u3000\u770b\u4ed6\u5750\u59ff\u6001\u52bf\uff0c\u7edd\u65e0\u6cd5\u770b\u5230\u5ba4\u4e2d\u7684\u60c5\u52bf\u53d8\u5316\uff0c\u5982\u82e5\u53ea\u51ed\u542c\u89c9\uff0c\u63e3\u6478\u51fa\u8fd9\u5ba4\u4e2d\u4eba\u4e00\u5207\u884c\u52a8\uff0c \u8fd9\u4eba\u7684\u6b66\u529f\u4e4b\u9ad8\uff0c\u5b9e\u5df2\u51fa\u795e\u5165\u5316\u4e86\u3002 \u3000\u3000\u5982\u662f\u4ed6\u5750\u59ff\u7a0d\u53d8\uff0c\u53c8\u5982\u4f55\u7792\u5f97\u8fc7\u7ea2\u88f3\u548c\u90a3\u4f4d\u9662\u4e3b\u7684\u773c\u775b\u5462\uff1f\u94c1\u7fce\u76f8\u4fe1\u9ed1\u8863\u4eba\u5df2\u88ab\u70b9\u4e86\u7a74 \u9053\uff0c\u4f46\u9662\u4e3b\u548c\u7ea2\u88f3\u90fd\u5df2\u88ab\u4ed6\u7792\u8fc7\uff0c\u4e0d\u77e5\u4ed6\u65e9\u5df2\u81ea\u89e3\u4e86\u7a74\u9053\u3002 \u3000\u3000\u5fc3\u4e2d\u65e2\u751f\u4f69\u670d\uff0c\u4e5f\u5c31\u4e0d\u518d\u77dc\u6301\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u94c1\u7fce\u5f88\u611f\u6fc0\u95f4\u4e0b\u7684\u6307\u70b9\uff0c\u4f7f\u6211\u8f7b\u6613\u8fc7\u5173\uff0c\u5728 \u4e0b\u867d\u7136\u4e60\u7ec3\u8fc7\u81ea\u89e3\u7a74\u9053\u4e4b\u6cd5\uff0c\u4f46\u529f\u529b\u4e0d\u8db3\uff0c\u5982\u82e5\u9601\u4e0b\u80af\u4f38\u63f4\u624b\uff0c\u94c1\u7fce\u975e\u5e38\u6b22\u8fce\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u7a81\u7136\u4e00\u4e2a\u7ffb\u6eda\uff0c\u4eba\u5df2\u5230\u94c1\u7fce\u7684\u8eab\u524d\uff0c\u53f3\u624b\u901f\u62cd\u4e09\u638c\uff0c\u89e3\u4e86\u7a74\u9053\uff0c\u540c\u65f6\uff0c\u5de6\u624b\u5728\u8138 \u4e0a\u4e00\u62b9\uff0c\u9732\u51fa\u6765\u4e00\u5f20\u5341\u5206\u82f1\u4fca\u7684\u8138\u5e9e\uff0c\u7b11\u9053\uff1a\u201c\u533a\u533a\u675c\u671b\u6708\u2026\u2026\u201d \u3000\u3000\u201c\u675c\u53f8\u4e3b\uff0c\u201d\u94c1\u7fce\u5750\u8d77\u8eab\u5b50\uff0c\u4f38\u5c55\u4e00\u4e0b\u53cc\u81c2\uff0c\u9053\uff1a\u201c\u626c\u5dde\u738b\u77e5\u5e9c\u773c\u671b\u675c\u53f8\u4e3b\uff0c\u5982\u5927\u65f1\u4e4b \u671b\u4e91\u9713\uff0c\u5e0c\u80fd\u65e9\u65e5\u4e00\u6664\u3002\u201d \u3000\u3000\u201c\u626c\u5dde\u4e3a\u4e3e\u56fd\u7b2c\u4e00\u7e41\u534e\u4e4b\u57ce\uff0c\u675c\u67d0\u867d\u7136\u6765\u8fc7\uff0c\u4f46\u5fd9\u4e8e\u67e5\u6848\u7f09\u51f6\uff0c\u672a\u80fd\u591a\u7559\uff0c\u6b64\u756a\u4ea6\u662f\u8ffd \u8e2a\u4e00\u540d\u6740\u4eba\u72af\u800c\u6765\u3002\u4e0d\u6599\uff0c\u8fdb\u4eba\u626c\u5dde\u57ce\u4e2d\uff0c\u7adf\u7136\u5931\u5176\u884c\u8e2a\uff0c\u95fb\u5f97\u8fd9\u5ea7\u56db\u51e4\u697c\u5341\u5206\u795e\u79d8\uff0c\u6613\u5bb9 \u63a2\u8bbf\uff0c\u867d\u7136\u53d7\u4e86\u4e0d\u5c11\u6298\u78e8\uff0c\u4f46\u5374\u4e0d\u865a\u6b64\u884c\uff0c\u63a2\u5f97\u4e0d\u5c11\u9690\u5bc6\uff0c\u4e8b\u60c5\u7275\u8fde\u4e4b\u5e7f\uff0c\u5927\u51fa\u4eba\u610f\u6599\u4e4b\u5916\uff0c \u503e\u6c5f\u5357\u5206\u53f8\u4e4b\u529b\uff0c\u4e5f\u96be\u6709\u529e\u7406\u6b64\u6848\u7684\u80fd\u529b\u3002\u201d \u3000\u3000\u201c\u675c\u53f8\u4e3b\u7684\u610f\u601d\u5462\uff1f\u201d\u94c1\u7fce\u9053\uff1a\u201c\u53ef\u6709\u6253\u7b97\uff0c\u5982\u82e5\u626c\u5dde\u5e9c\u6709\u6548\u529b\u4e4b\u5904\uff0c\u94c1\u67d0\u5c06\u5168\u529b\u4ee5 \u8d74\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u76db\u60c5\uff0c\u6211\u77e5\u9053\u738b\u77e5\u5e9c\u6b63\u76f4\u6709\u4e3a\uff0c\u94c1\u5144\u5728\u6c5f\u6e56\u9053\u4e0a\u7684\u5730\u4f4d\uff0c\u5341\u5206\u5d07\u9ad8\uff0c\u626c\u5dde\u5e9c\u8859\u4e2d \u4e5f\u8058\u8bf7\u4e86\u4e00\u4e9b\u6c5f\u6e56\u9ad8\u624b\uff0c\u4f46\u8fd9\u4ef6\u4e8b\u975e\u540c\u5c0f\u53ef\uff0c\u4f3c\u975e\u4e00\u5730\u4e00\u5e9c\u80fd\u529b\u6240\u80fd\u627f\u62c5\u3002\u6240\u4ee5\uff0c\u675c\u67d0\u5df2\u628a \u5185\u60c5\u4f20\u4e66\u5211\u90e8\u603b\u6355\u5934\uff0c\u5e0c\u671b\u80fd\u5c3d\u96c6\u5357\u3001\u5317\u3001\u4e2d\u3001\u897f\u56db\u5927\u540d\u6355\u4e4b\u529b\uff0c\u6d88\u5f2d\u53bb\u7978\u5bb3\u4e4b\u6e90\uff0c\u4e5f\u8bb8\u56db \u5927\u6355\u5934\uff0c\u529b\u6709\u4e0d\u902e\uff0c\u4f46\u603b\u6355\u5934\u4e00\u5b9a\u6709\u8fd9\u4e2a\u667a\u6167\u80fd\u529b\u3002\u201d \u3000\u3000\u201c\u675c\u53f8\u4e3b\uff0c\u201d\u94c1\u7fce\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u626c\u5dde\u5e9c\u53d1\u751f\u4e86\u4e00\u4ef6\u65b0\u623f\u547d\u6848\uff0c\u95e8\u7a97 \u5bc6\u95ed\uff0c\u65b0\u90ce\u88ab\u6740\uff0c\u51f6\u5200\u63e1\u5728\u65b0\u5a18\u5b50\u624b\u4e2d\uff0c\u4f46\u65b0\u5a18\u5b50\u4e0d\u662f\u51f6\u624b\uff0c\u8fd9\u4ef6\u6848\u5b50\u5145\u6ee1\u7740\u795e\u79d8\u3001\u8be1\u5f02\uff0c \u7275\u626f\u4e0a\u6c5f\u6e56\u4e0a\u4e00\u4f4d\u602a\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u68a6\u5e7b\u4e4b\u5200\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u636e\u8bf4\u4ed6\u6709\u4e00\u79cd\u5f79\u4eba\u51fa\u5200\u7684\u5947\u672f\uff0c\u4e0d\u8fc7\uff0c\u6211\u4e0d\u592a\u76f8\u4fe1\uff0c\u8fd9\u4e2d \u95f4\u5b9a\u6709\u66f2\u6298\u4e4b\u5904\uff0c\u5e0c\u671b\u603b\u6355\u5934\u80fd\u4eb2\u6765\u626c\u5dde\uff0c\u53c2\u4e0e\u6b64\u6848\uff0c\u4ee5\u4ed6\u7684\u667a\u6167\uff0c\u4e5f\u8bb8\u80fd\u89e3\u5f00\u4e2a\u4e2d\u795e\u79d8\u3001 \u66f2\u6298\u3002\u54b1\u4eec\u751f\u79bb\u6b64\u5730\u4e4b\u540e\uff0c\u5148\u53bb\u664b\u89c1\u738b\u77e5\u5e9c\uff0c\u518d\u8bf7\u4ed6\u98de\u51fd\u9080\u8bf7\u603b\u6355\u5934\u4e0b\u626c\u5dde\u4e3b\u6301\u5927\u5c40\u3002\u201d \u3000\u3000\u201c\u675c\u5144\uff0c\u6211\u4eec\u79bb\u5f00\u6b66\u9662\uff0c\u662f\u5426\u4f1a\u5f88\u96be\uff1f\u201d\u94c1\u7fce\u9053\uff1a\u201c\u53ef\u8981\u94c1\u67d0\u53ec\u4eba\u76f8\u52a9\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u8981\u8d70\uff0c\u81ea\u662f\u4e0d\u96be\uff0c\u8fd9\u91cc\u53ea\u6709\u6b66\u9662\u9662\u4e3b\u548c\u7ea2\u88f3\u3001\u7fe0\u7eeb\u4e09\u4e2a\u9ad8\u624b\uff0c\u6587\u9601\u4e2d\u4eba\u662f\u5426\u4f1a\u6b66\uff0c \u65e0\u4ece\u5224\u65ad\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u56e0\u4e3a\u56db\u51e4\u697c\u4e2d\u7684\u9ad8\u624b\uff0c\u90fd\u5df2\u968f\u697c\u4e3b\u4ed6\u53bb\uff0c\u7b49\u4ed6\u4eec\u4eca\u591c\u5f52\u6765\u518d\u8d70\uff0c \u5c31\u53ef\u80fd\u4f1a\u9047\u4e0a\u5f3a\u5927\u7684\u963b\u529b\u4e86\u3002\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u73b0\u5728\u4e3a\u4f55\u4e0d\u8d70\uff1f\u201d \u3000\u3000\u201c\u4f60\u96be\u9053\u4e0d\u60f3\u89c1\u89c1\u56db\u51e4\u697c\u4e3b\u4e48\uff1f\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\uff0c\u80fd\u628a\u7ecf\u8425\u5993\u9662\u7684\u65b9\u6cd5\uff0c\u6eb6\u5165\u8bcd \u7ae0\u3001\u6b66\u529f\u4e4b\u4e2d\uff0c\u5728\u98ce\u6708\u573a\u4e2d\u72ec\u6811\u4e00\u5e1c\uff0c\u662f\u4e00\u4e2a\u5341\u5206\u806a\u660e\u7684\u4eba\uff0c\u4e5f\u53ef\u80fd\u662f\u4e00\u4e2a\u9690\u85cf\u5f88\u597d\u7684\u4eba\uff0c \u9664\u4e86\u73b0\u5728\u8fd9\u79cd\u60c5\u51b5\uff0c\u4ee5\u9636\u4e0b\u4e4b\u56da\u548c\u4ed6\u89c1\u9762\u4e4b\u5916\uff0c\u53ea\u6015\u5f88\u96be\u89c1\u5230\u4ed6\u771f\u6b63\u9762\u76ee\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4eba\u5982\u6b64\u795e\u79d8\uff1f\u201d\u94c1\u7fce\u9053\uff1a\u201c\u675c\u5144\u5fc3\u4e2d\u4f3c\u662f\u65e9\u6709\u6210\u7b97\u4e86\uff1f\u201d \u3000\u3000\u201c\u5168\u65e0\u628a\u63e1\uff0c\u5f88\u96be\u542f\u9f7f\uff0c\u8bf4\u51fa\u6765\u5f92\u4e71\u4eba\u610f\uff0c\u597d\u5728\u5165\u591c\u4e4b\u540e\uff0c\u5c31\u53ef\u80fd\u89c1\u4e2a\u5206\u6653\u3002\u201d\u675c\u671b\u6708 \u9053\uff1a\u201c\u94c1\u603b\u6355\u5934\uff0c\u8fd8\u662f\u5fcd\u8010\u4e00\u4f1a\u5427\uff01\u73b0\u5728\uff0c\u6700\u91cd\u8981\u7684\u662f\u8fd0\u6c14\u8c03\u606f\uff0c\u628a\u4f53\u80fd\u8c03\u6574\u5230\u6700\u4f73\u72b6\u51b5\uff0c \u4e0d\u8bba\u4ed6\u662f\u5426\u6211\u5fc3\u4e2d\u6000\u7591\u7684\u4eba\uff0c\u4f46\u4ed6\u7684\u6b66\u529f\uff0c\u7edd\u975e\u5f31\u624b\uff0c\u8fd9\u4e00\u6218\uff0c\u5c06\u5341\u5206\u51f6\u5389\u2026\u2026\u201d\u7a81\u7136\u4e00\u4e2a \u5012\u7ffb\uff0c\u8fc5\u5982\u77f3\u706b\u4e00\u95ea\uff0c\u56de\u5f52\u539f\u4f4d\u3002 \u3000\u3000\u4ecd\u7136\u662f\u9760\u58c1\u800c\u5750\uff0c\u624b\u5728\u819d\u4e0a\uff0c\u5934\u8138\u57cb\u5728\u53cc\u81c2\u5185\uff0c\u5c31\u50cf\u4ed6\u4e00\u76f4\u5750\u5728\u90a3\u91cc\uff0c\u4ece\u6765\u6ca1\u6709\u52a8\u8fc7\u3002 \u3000\u3000\u94c1\u7fce\u7684\u53cd\u5e94\u5c31\u662f\u95ed\u4e0a\u53cc\u773c\uff0c\u7f13\u7f13\u5730\u8eba\u4e86\u4e0b\u53bb\uff0c\u6446\u51fa\u539f\u6765\u4ef0\u5367\u59ff\u52bf\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u94c1\u7fce\u5e76\u6ca1\u6709\u542c\u5230\u4efb\u4f55\u7684\u58f0\u606f\uff0c\u8fd9\u4e00\u5207\u884c\u52a8\uff0c\u53ea\u662f\u8ddf\u7740\u675c\u671b\u6708\u4f5c\u51fa\u7684\u53cd\u5e94\u3002 \u3000\u3000\u4f46\u4ed6\u521a\u521a\u4fee\u6574\u597d\u8eba\u4e0a\u7684\u4f4d\u7f6e\u3001\u59ff\u52bf\uff0c\u539a\u91cd\u7684\u6728\u95e8\uff0c\u7a81\u7136\u5927\u5f00\uff0c\u4e24\u4e2a\u624b\u6267\u706f\u7b3c\u7684\u9ed1\u8863\u4eba\uff0c \u95ea\u8eab\u800c\u5165\u3002 \u3000\u3000\u8fd9\u91cc\u663c\u591c\u4e0d\u5206\uff0c\u5e38\u5e74\u9ed1\u6697\uff0c\u9661\u7136\u95ee\uff0c\u6709\u706f\u5149\u7167\u5c04\u8fc7\u6765\uff0c\u663e\u5f97\u7279\u522b\u660e\u4eae\uff0c\u5ba4\u4e2d\u666f\u7269\u7ea4\u6beb\u6bd5 \u73b0\u3002 \u3000\u3000\u4e8b\u60c5\u6765\u7684\u7a81\u7136\uff0c\u94c1\u7fce\u5fae\u542f\u53cc\u76ee\uff0c\u4ed4\u7ec6\u77a7\u53bb\uff0c\u53ea\u89c1\u7ea2\u88f3\u624b\u63e1\u7740\u4e00\u628a\u950b\u5229\u7684\u5315\u9996\uff0c\u5feb\u6b65\u884c\u4e86 \u8fc7\u6765\uff0c\u4e00\u7728\u773c\u95f4\uff0c\u5df2\u903c\u5230\u4e86\u94c1\u7fce\u8eab\u4fa7\u3002 \u3000\u3000\u4e24\u4e2a\u5de6\u624b\u6267\u706f\u7684\u9ed1\u8863\u4eba\uff0c\u53f3\u624b\u540c\u65f6\u4e5f\u62d4\u51fa\u4e86\u80cc\u4e0a\u7684\u5355\u5200\u3002 \u3000\u3000\u94c1\u7fce\u518d\u4e5f\u88c5\u4e0d\u4e0b\u53bb\u4e86\uff0c\u633a\u8eab\u800c\u8d77\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u7adf\u7136\u52a8\u4e86\u6740\u673a\u5462\uff1f\u201d \u3000\u3000\u7ea2\u88f3\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u7adf\u7136\u662f\u5728\u88c5\u4f5c\u3002\u201d\u5315\u9996\u4e00\u6325\u76f4\u523a\u524d\u80f8\u3002 \u3000\u3000\u201c\u5a4a\u5b50\u65e0\u60c5\uff0c\u53e4\u4eba\u8bda\u4e0d\u6b3a\u6211\u3002\u201d\u65bd\u5c55\u51fa\u7a7a\u624b\u4eba\u767d\u5203\u7684\u62db\u672f\uff0c\u548c\u7ea2\u88f3\u5c55\u5f00\u4e86\u4e00\u573a\u62a2\u5236\u5148\u673a \u7684\u5feb\u653b\u3002 \u3000\u3000\u4e8c\u5341\u4e2a\u7167\u9762\u4e4b\u540e\uff0c\u94c1\u7fce\u627e\u5230\u4e00\u4e2a\u673a\u4f1a\uff0c\u4e00\u628a\u6263\u4f4f\u4e86\u7ea2\u88f3\u7684\u53f3\u8155\uff0c\u7ea2\u88f3\u88ab\u5f3a\u5927\u6263\u62ff\u4e4b\u529b\uff0c \u5c01\u95ed\u4e86\u7a74\u8109\uff0c\u5315\u9996\u843d\u5730\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e24\u4e2a\u9ed1\u8863\u4eba\uff0c\u4e24\u67c4\u5355\u5200\uff0c\u5206\u7531\u5de6\u53f3\u8fdb\u51fb\uff0c\u5200\u4f5c\u5251\u7528\uff0c\u523a\u5411\u4e24\u808b\u3002 \u3000\u3000\u5927\u7ea6\u662f\u56e0\u7ea2\u88f3\u843d\u5165\u4e86\u94c1\u7fce\u624b\u4e2d\uff0c\u4e24\u4eba\u4e0d\u81f4\u6325\u5200\u5288\u65a9\uff0c\u4ee5\u514d\u4f24\u5230\u7ea2\u88f3\u3002 \u3000\u3000\u94c1\u7fce\u640f\u6740\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u4e00\u89c1\u4e24\u4eba\u51fa\u5200\u6001\u52bf\uff0c\u5df2\u77e5\u4e8c\u4eba\u5fc3\u6709\u987e\u5fcc\uff0c\u4e00\u5e26\u7ea2\u88f3\u8eab\u8eaf\uff0c\u5411\u4e24\u4eba\u5200 \u4e0a\u649e\u53bb\u3002\u4e24\u4eba\u679c\u7136\u6025\u6025\u6536\u5200\u800c\u9000\uff0c\u94c1\u7fce\u987a\u52bf\u800c\u653b\uff0c\u51fa\u624b\u5982\u98ce\uff0c\u70b9\u4e2d\u4e86\u4e24\u4eba\u7a74\u9053\u3002 \u3000\u3000\u4f46\u4e24\u4eba\u624b\u4e2d\u7684\u706f\u7b3c\uff0c\u5374\u8131\u624b\u800c\u843d\u3002 \u3000\u3000\u5730\u4e0a\u94fa\u7684\u90fd\u662f\u5e72\u4e86\u7684\u8305\u8349\uff0c\u661f\u661f\u4e4b\u706b\uff0c\u7acb\u53ef\u71ce\u539f\uff0c\u94c1\u7fce\u867d\u7136\u6280\u827a\u7cbe\u6e5b\uff0c\u4f46\u4e5f\u62a2\u6551\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u4f46\u89c1\u9ed1\u5f71\u4e00\u95ea\uff0c\u5fae\u98ce\u98d2\u7136\uff0c\u4e24\u76cf\u8dcc\u843d\u7684\u706f\u7b3c\uff0c\u5df2\u4e3a\u675c\u671b\u6708\u63a5\u5728\u4e86\u624b\u4e2d\u3002 \u3000\u3000\u201c\u770b\u6765\uff0c\u4eca\u513f\u697c\u4e3b\u7684\u8ba1\u5212\uff0c\u8981\u53d8\u66f4\u4e00\u4e0b\u4e86\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u771f\u662f\u2026\u2026\u201d \u3000\u3000\u675c\u671b\u6708\u4e00\u76f4\u6447\u5934\uff0c\u94c1\u7fce\u624d\u53d1\u89c9\u675c\u671b\u6708\u82f1\u4fca\u7684\u8138\u4e0a\uff0c\u4e0d\u77e5\u4f55\u65f6\u5df2\u5957\u4e0a\u4e86\u4e00\u5f20\u4eba\u76ae\u9762\u5177\uff0c\u4e00 \u5f20\u5e73\u5eb8\u814a\u9ec4\u7684\u9762\u5b54\uff0c\u52ff\u602a\u9662\u4e3b\u548c\u7ea2\u88f3\u90fd\u672a\u6ce8\u610f\u5230\u4ed6\uff0c\u4e5f\u672a\u628a\u4ed6\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u94c1\u7fce\u5df2\u77e5\u4ed6\u5fc3\u610f\uff0c\u975e\u4e0d\u5f97\u5df2\uff0c\u4e0d\u613f\u66b4\u9732\u8eab\u4efd\uff0c\u53e3\u6c14\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u5343\u7b97\u4e07\u7b97\uff0c\u7adf\u672a\u7b97\u5230\u7ea2 \u88f3\u59d1\u5a18\u5b58\u4e86\u6740\u6211\u4e4b\u5fc3\uff0c\u53ea\u597d\u5148\u628a\u5979\u9001\u4eba\u8859\u95e8\u4e86\u3002\u201d \u3000\u3000\u7ea2\u88f3\u4f3c\u662f\u80f8\u6709\u6210\u7af9\uff0c\u4e00\u70b9\u4e5f\u4e0d\u5bb3\u6015\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u65e9\u8be5\u60f3\u5230\u7684\uff0c\u4f60\u80fd\u6613\u5bb9\u6539\u626e\uff0c\u4e5f\u53ef \u80fd\u9690\u85cf\u4e86\u6b66\u529f\uff0c\u8fd8\u662f\u4f4e\u4f30\u4e86\u4f60\uff0c\u6211\u8d25\u7684\u5e94\u8be5\uff0c\u4e0d\u8fc7\uff0c\u4f60\u4e5f\u4e0d\u8981\u5f97\u610f\uff0c\u4f60\u53ef\u4ee5\u6740\u4e86\u6211\uff0c\u4f46\u5982\u60f3 \u79bb\u5f00\u8fd9\u91cc\uff0c\u53ea\u6015\u4e0d\u592a\u5bb9\u6613\uff0c\u81f3\u4e8e\u4f60\u9a82\u6211\u5a4a\u5b50\u65e0\u60c5\uff0c\u5c31\u6709\u70b9\u542b\u8840\u55b7\u4eba\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\uff0c\u51a4\u4e86\u4f60\u5566\uff01\u201d\u94c1\u7fce\u9053\uff1a\u201c\u6211\u6309\u7167\u89c4\u77e9\uff0c\u5148\u7f34\u94f6\u5b50\u540e\u6bd4\u6b66\uff0c\u4f60\u5374\u6697\u65bd\u7b97\u8ba1\uff0c\u7528 \u8ff7\u836f\u628a\u6211\u8ff7\u5012\uff0c\u9526\u69bb\u7ee3\u5e10\uff0c\u4e5f\u53d8\u6210\u4e86\u8305\u8349\u5730\u94fa\uff0c\u6d1e\u623f\u82b1\u70db\uff0c\u5374\u6210\u4e86\u6740\u4eba\u7684\u6076\u591c\uff0c\u5982\u4e0d\u662f\u94c1\u67d0 \u4eba\u6709\u70b9\u9632\u8eab\u6280\u827a\uff0c\u65e9\u5df2\u6b7b\u4e8e\u4f60\u7684\u5200\u4e0b\u3002\u4f60\u4ed7\u51ed\u59ff\u8272\uff0c\u5e03\u8bbe\u4e0b\u6740\u4eba\u9677\u9631\uff0c\u4e0d\u77e5\u5df2\u5751\u5bb3\u4e86\u591a\u5c11\u4eba \u547d\uff0c\u4ee4\u65e5\u843d\u5165\u94c1\u67d0\u624b\u4e2d\uff0c\u4e5f\u662f\u62a5\u5e94\u5f53\u5934\u4e86\u3002\u201d \u3000\u3000\u7ea2\u88f3\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4ee5\u8859\u95e8\u6355\u5934\u7684\u8eab\u4efd\uff0c\u6df7\u5165\u56db\u51e4\u697c\u6b66\u9662\u4e2d\u6765\uff0c\u7528\u5fc3\u53f5\u6d4b\uff0c\u5229\u7528\u60f3 \u8ddf\u6211\u4e0a\u5e8a\u4f5c\u4e3a\u501f\u53e3\uff0c\u4e0d\u4f46\u6709\u5931\u7537\u5b50\u6c49\u7684\u6c14\u6168\uff0c\u7b80\u76f4\u662f\u884c\u5f84\u53ef\u5351\uff0c\u6211\u6068\u81ea\u5df1\u624b\u8f6f\u3001\u5fc3\u6148\uff0c\u9519\u4e86 \u4e24\u6b21\u6740\u4f60\u7684\u673a\u4f1a\uff0c\u7b97\u662f\u4ee4\u751f\u4e00\u5927\u6068\u4e8b\u3002\u201d \u3000\u3000\u94c1\u7fce\u7adf\u7136\u88ab\u9a82\u7684\u8138\u90fd\u7ea2\u4e86\uff0c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u867d\u662f\u4f60\u672c\u672b\u5012\u7f6e\uff0c\u7406\u4ea6\u4e0d\u76f4\uff0c\u4f46\u6c14\u52bf \u9887\u58ee\uff0c\u4e0d\u8fc7\uff0c\u6740\u4eba\u507f\u547d\uff0c\u94c1\u67d0\u6267\u6cd5\uff0c\u5411\u4e0d\u5f87\u79c1\uff0c\u65b9\u6cd5\u4e5f\u8bb8\u6709\u6b20\u5149\u660e\uff0c\u4f46\u50cf\u4f60\u4eec\u8fd9\u6837\u5978\u72e1\u7684\u7ec4 \u5408\uff0c\u4e0d\u7528\u975e\u5e38\u624b\u6bb5\uff0c\u5982\u4f55\u80fd\u4f7f\u4f60\u4eec\u73b0\u5f62\uff1f\u2026\u2026\u201d \u3000\u3000\u7ea2\u88f3\u7a81\u7136\u5a07\u5a9a\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u8fd8\u60f3\u548c\u59d1\u5a18\u6d1e\u623f\u82b1\u70db\u4e48\uff1f\u201d \u3000\u3000\u94c1\u7fce\u5fc3\u4e2d\u5fd6\u9053\uff1a\u8fd9\u4e2b\u5934\u4e1c\u62c9\u897f\u626f\uff0c\u4f3c\u662f\u6709\u610f\u5728\u62d6\u5ef6\u65f6\u95f4\uff0c\u4e0d\u80fd\u518d\u4e0a\u5979\u7684\u5f53\u4e86\uff0c\u5fc3\u5ff5\u8f6c\u52a8\uff0c \u7a81\u7136\u51fa\u624b\uff0c\u70b9\u4e86\u7ea2\u88f3\u7684\u9ebb\u7a74\uff0c\u540c\u65f6\uff0c\u677e\u5f00\u4e86\u6263\u5728\u53f3\u8155\u8109\u7a74\u4e0a\u7684\u4e94\u6307\u3002 \u3000\u3000\u7ea2\u88f3\u7ad9\u7acb\u4e0d\u7a33\uff0c\u4e00\u8de4\u8dcc\u5728\u4e86\u5730\u4e0a\uff0c\u53e3\u4e2d\u5374\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u5934\u8001\u72d0\u72f8\uff0c\u9634\u7684\u5f88\u554a\uff01\u4f60\u662f\u8fd0\u6c14 \u592a\u597d\uff0c\u8fd8\u662f\u65e9\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u94c1\u7fce\u88ab\u9a82\u7684\u4e00\u6014\uff0c\u4f4e\u58f0\u770b\u53bb\uff0c\u53ea\u89c1\u4e00\u6761\u9752\u8272\u7684\u5c0f\u86c7\uff0c\u7531\u7ea2\u88f3\u53f3\u81c2\u8863\u8896\u4e2d\u7a9c\u4e86\u51fa\u6765\uff0c\u94bb\u5165 \u5730\u4e0a\u7684\u8305\u8349\u4e2d\u3002 \u3000\u3000\u94c1\u7fce\u6697\u53eb\u4e86\u4e00\u58f0\u60ed\u6127\uff0c\u5fd6\u9053\uff1a\u539f\u6765\u8fd9\u4e2a\u4e2b\u5934\u7adf\u7136\u5728\u8eab\u4e0a\u85cf\u4e86\u4e00\u6761\u5f62\u4f53\u7ec6\u5c0f\u7684\u6bd2\u86c7\uff0c\u60f3\u662f \u521a\u624d\u6263\u7d27\u4e86\u5979\u53f3\u8155\u8109\u7a74\uff0c\u534a\u8eab\u4e0d\u80fd\u6323\u52a8\uff0c\u65e0\u6cd5\u628a\u8eab\u4e0a\u6697\u85cf\u7684\u6551\u547d\u6bd2\u86c7\uff0c\u5feb\u901f\u65bd\u653e\u51fa\u6765\uff0c\u6240\u4ee5\uff0c \u4e00\u9762\u80e1\u8bf4\u516b\u9053\uff0c\u4e00\u9762\u6697\u4e2d\u65bd\u529b\u903c\u4f7f\u6bd2\u86c7\u5916\u51fa\u3002\u518d\u62d6\u5ef6\u7247\u523b\u5de5\u592b\uff0c\u6bd2\u86c7\u722c\u51fa\u8896\u53e3\uff0c\u94c1\u7fce\u5c31\u96be\u9003 \u6bd2\u86c7\u566c\u8155\u4e4b\u5371\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u4e5f\u6709\u8ff9\u53ef\u5bfb\uff0c\u7ea2\u88f3\u5bf9\u4e24\u4e2a\u4ece\u4eba\u53d7\u5236\u4e00\u4e8b\uff0c\u4f3c\u672a\u653e\u5728\u5fc3\u4e0a\uff0c\u706f\u7b3c\u672a\u843d\u5b9e\u5730\uff0c\u5f15\u8d77 \u706b\u707e\uff0c\u4e5f\u4e0d\u56de\u987e\u4e00\u773c\uff0c\u4f3c\u662f\u4e13\u5fc3\u518d\u4f5c\u4e00\u4ef6\u4e8b\u60c5\uff0c\u5f79\u4f7f\u6bd2\u86c7\u51fa\u52a8\u3002 \u3000\u3000\u4f46\u94c1\u7fce\u53ea\u611f\u89c9\u5230\u5979\u5728\u62d6\u5ef6\u65f6\u95f4\uff0c\u70b9\u7a74\u653e\u624b\uff0c\u4e5f\u53ea\u60f3\u6709\u673a\u4f1a\u548c\u675c\u671b\u6708\u7814\u7a76\u4e00\u4e0b\u5fa1\u654c\u4e4b\u7b56\uff0c \u4e2d\u95f2\u7684\u4e00\u4e9b\u5de7\u5408\uff0c\u5c31\u5168\u51ed\u4e09\u5206\u8fd0\u6c14\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u7f8e\u8c8c\u5982\u82b1\u7684\u5927\u59d1\u5a18\uff0c\u8eab\u4e0a\u5374\u85cf\u4e86\u4e00\u6761\u6d3b\u751f\u751f\u7684\u5c0f\u6bd2\u86c7\uff0c\u5728\u8863\u8896\u548c\u808c\u80a4\u4e4b\u95f4\u8815\u8815\u800c\u884c\uff0c \u60f3\u6765\u5c31\u6709\u70b9\u6050\u6016\uff0c\u4e5f\u5b8c\u5168\u5730\u51fa\u4eba\u610f\u6599\u4e4b\u5916\uff0c\u8fd9\u79cd\u6700\u540e\u9632\u536b\u7684\u624b\u6bb5\uff0c\u5931\u8d25\u7684\u673a\u4f1a\u5f88\u5c0f\u3002 \u3000\u3000\u675c\u671b\u6708\u4e5f\u6709\u7740\u5b8c\u5168\u610f\u5916\u7684\u611f\u89c9\uff0c\u671b\u7740\u7ea2\u88f3\u5446\u5446\u51fa\u795e\uff0c\u5927\u6c5f\u5357\u5317\u7684\u6c5f\u6e56\u9053\u4e0a\uff0c\u4ece\u672a\u542c\u95fb\u8fc7 \u79cd\u4e8b\u60c5\u3002\u8fd9\u4e2a\u7f8e\u4e3d\u7684\u59d1\u5a18\uff0c\u7a76\u7adf\u6765\u81ea\u4f55\u5904\uff1f\u8fd9\u79cd\u5f79\u86c7\u4f24\u4eba\u4e4b\u80fd\uff0c\u4e0d\u4f46\u8981\u82b1\u4e0a\u5f88\u591a\u65f6\u95f4\u4e60\u7ec3\u80c6 \u6c14\u548c\u5f79\u86c7\u80fd\u529b\uff0c\u53ef\u80fd\u4e5f\u9700\u8981\u4e00\u4e9b\u836f\u7269\u5e2e\u52a9\uff0c\u6700\u91cd\u8981\u7684\u662f\u8981\u6709\u4f20\u6388\u8fd9\u79cd\u5947\u672f\u7684\u5e08\u957f\u3002 \u3000\u3000\u94c1\u7fce\u4e0d\u61c2\u86c7\u6027\uff0c\u4f46\u60f3\u5230\u90a3\u5b9a\u662f\u4e00\u6761\u5f88\u6bd2\u7684\u5c0f\u86c7\uff0c\u4f0f\u8eab\u6361\u8d77\u4e00\u67c4\u5355\u5200\uff0c\u53cc\u76ee\u5374\u6ce8\u610f\u7740\u5730\u4e0a \u53d8\u5316\uff0c\u4ed6\u4e0d\u6015\u624b\u6267\u5200\u5251\u7684\u6c5f\u6e56\u9ad8\u624b\uff0c\u4f46\u5bf9\u6bd2\u86c7\u5374\u6709\u5f88\u6df1\u7684\u6050\u60e7\u4e4b\u5fc3\uff0c\u624b\u4e2d\u6709\u628a\u5200\uff0c\u5c31\u53ef\u4ee5\u653e \u5fc3\u5bf9\u4ed8\u5b83\u4e86\uff0c\u4e00\u9762\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4eca\u5929\u8fd0\u6c14\u5f88\u2026\u2026\u201d \u3000\u3000\u90a3\u88ab\u70b9\u4e2d\u7a74\u9053\uff0c\u5012\u5728\u5730\u4e0a\u7684\u4e00\u4e2a\u9ed1\u8863\u4eba\uff0c\u8eab\u8eaf\u7a81\u7136\u98a4\u52a8\u4e86\u4e00\u4e0b\uff0c\u7acb\u65f6\uff0c\u6d6e\u4e0a\u4e86\u4e00\u8138\u9ed1\u6c14\uff0c \u90a3\u6761\u9752\u8272\u7684\u5c0f\u86c7\uff0c\u5ffd\u7136\u4e00\u95ea\uff0c\u53c8\u5165\u8349\u4e2d\u3002 \u3000\u3000\u94c1\u7fce\u5355\u5200\u4e00\u6325\uff0c\u65a9\u65ad\u4e00\u628a\u8305\u8349\uff0c\u4f46\u5374\u672a\u65a9\u5230\u90a3\u6761\u5c0f\u86c7\u3002\u597d\u7075\u52a8\u7684\u5c0f\u6bd2\u86c7\u3002 \u3000\u3000\u7ec6\u770b\u90a3\u8138\u6cdb\u9ed1\u6c14\u7684\u4eba\uff0c\u65e9\u5df2\u6c14\u7edd\u800c\u901d\uff0c\u6bd2\u6027\u4e4b\u70c8\uff0c\u89e6\u76ee\u60ca\u5fc3\u3002 \u3000\u3000\u675c\u671b\u6708\u4e5f\u770b\u7684\u5fc3\u5934\u4e00\u9707\uff0c\u9053\uff1a\u201c\u86c7\u867d\u5c0f\uff0c\u6bd2\u5374\u5389\u5bb3\uff0c\u4e0d\u80fd\u7559\u4e0b\u5b83\uff01\u201d\u53f3\u624b\u706f\u7b3c\u6295\u63b7\u4e8e\u5730\uff0c \u7acb\u65f6\u71c3\u8d77\u5730\u4e0a\u8305\u8349\uff0c\u98de\u8d77\u4e00\u811a\uff0c\u628a\u7ea2\u88f3\u8e22\u98de\u51fa\u4e03\u516b\u5c3a\u8fdc\uff0c\u649e\u5728\u5899\u58c1\u4e0a\uff0c\u4eba\u5374\u4e00\u8dc3\u98de\u51fa\u95e8\u5916\u3002 \u3000\u3000\u4e00\u6761\u6bd2\u6027\u5947\u70c8\u7684\u5c0f\u86c7\uff0c\u7acb\u523b\u4f7f\u8fd9\u5ea7\u5bc6\u5ba4\uff0c\u53d8\u6210\u4e86\u5371\u673a\u56db\u4f0f\u7684\u6240\u5728\uff0c\u9884\u5b9a\u7684\u8ba1\u5212\u4e5f\u8981\u6539\u53d8\u3002 \u3000\u3000\u94c1\u7fce\u7d27\u968f\u7740\u51fa\u4e86\u5ba4\u95e8\uff0c\u5bc6\u5ba4\u4e2d\u5df2\u6d53\u70df\u5bc6\u5e03\uff0c\u706b\u86c7\u56db\u7a9c\u3002 \u3000\u3000\u675c\u671b\u6708\u62c9\u4e0a\u6728\u95e8\uff0c\u9053\uff1a\u201c\u6211\u4e00\u811a\u5df2\u8e22\u5f00\u5979\u88ab\u70b9\u7684\u7a74\u9053\uff0c\u80fd\u4e0d\u80fd\u4fdd\u4f4f\u6027\u547d\uff0c\u8981\u770b\u5979\u7684\u9020\u5316 \u4e86\u3002\u201d \u3000\u3000\u5ba4\u5916\u662f\u4e00\u6761\u9ed1\u6697\u7684\u752c\u9053\u3002 \u3000\u3000\u8fd9\u5ea7\u56e0\u4eba\u7684\u5bc6\u5ba4\uff0c\u679c\u7136\u7b51\u5efa\u5728\u5730\u4e0b\u3002 \u3000\u3000\u752c\u9053\u4e24\u65c1\uff0c\u6709\u516b\u4e2a\u7d27\u95ed\u7684\u95e8\u6237\uff0c\u8fd9\u95f4\u5730\u4e0b\u5bc6\u5ba4\uff0c\u4e00\u5171\u6709\u516b\u95f4\u5206\u7acb\u7684\u56da\u623f\uff0c\u4f46\u672a\u95fb\u56da\u623f\u4e2d \u4f20\u51fa\u58f0\u606f\uff0c\u4e0d\u77e5\u662f\u5426\u56da\u7684\u6709\u4eba\uff1f\u94c1\u7fce\u5fc3\u77e5\u6b64\u523b\u4ea6\u4e0d\u662f\u6551\u4eba\u7684\u65f6\u673a\uff0c\u5fc5\u987b\u5c3d\u65e9\u79bb\u5f00\u6b64\u5730\uff0c\u81f3\u5c11 \u4e5f\u8981\u5148\u51fa\u4e86\u8fd9\u5904\u5730\u4e0b\u5bc6\u5ba4\u3002 \u3000\u3000\u4e8b\u60c5\u51fa\u4eba\u610f\u5916\u7684\u987a\u5229\u3002\u611f\u89c9\u5730\u52bf\u6e10\u9ad8\uff0c\u884c\u7ea6\u5341\u4e08\u5de6\u53f3\uff0c\u62c9\u5f00\u4e00\u6247\u95e8\u6237\uff0c\u7acb\u523b\u82b1\u9999\u6251\u9f3b\uff0c \u7adf\u7136\u7f6e\u8eab\u4e8e\u4e00\u5ea7\u5047\u5c71\u6d41\u6c34\uff0c\u6811\u6728\u8471\u73d1\u7684\u56ed\u6797\u4e4b\u4e2d\u3002 \u3000\u3000\u591c\u98ce\u62c2\u9762\uff0c\u4e00\u7247\u5bc2\u7136\uff0c\u5929\u4e0a\u65e0\u6708\u661f\u66f4\u6717\uff0c\u597d\u4e00\u4e2a\u5b81\u9759\u4e4b\u591c\u3002 \u3000\u3000\u675c\u671b\u6708\u548c\u94c1\u7fce\u90fd\u662f\u4e45\u8d70\u6c5f\u6e56\u7684\u4eba\u7269\uff0c\u51fa\u4e86\u90a3\u5ea7\u901a\u5f80\u5730\u5ba4\u7684\u95e8\u6237\uff0c\u5c31\u89c9\u7740\u5f62\u52bf\u4e0d\u5bf9\uff0c\u6240\u4ee5 \u4e24\u4eba\u540c\u65f6\u505c\u4e86\u4e0b\u6765\uff0c\u6253\u91cf\u56db\u5468\u666f\u7269\u3002 \u3000\u3000\u201c\u5feb\u56db\u66f4\u5929\u4e86\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u8fd9\u91cc\u4e0d\u662f\u8fdb\u5165\u56db\u51e4\u697c\u7684\u90a3\u4e00\u5ea7\u82b1\u56ed\uff0c\u626c\u5dde\u96c6\u5929\u4e0b\u5927\u534a\u8d22\u5bcc\uff0c \u6210\u5c31\u4e86\u5f88\u591a\u56ed\u6797\u4e4b\u76db\u7684\u5927\u5b85\u5de8\u9662\uff0c\u8fd9\u5730\u65b9\u662f\u4ed6\u4eec\u8d81\u7740\u6211\u8ff7\u836f\u672a\u9192\u65f6\uff0c\u628a\u6211\u8fd0\u5165\u6b64\u5730\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u4f46\u662f\u4e00\u5ea7\u56ed\u6797\u5de8\u5b85\uff0c\u800c\u4e14\uff0c\u6709\u4e00\u9053\u6eaa\u6d41\u901a\u8fc7\u5b85\u9662\uff0c\u201d\u675c\u671b\u6708\u63a5\u9053\uff1a\u201c\u4e5f\u4e0d\u662f\u4e00\u822c\u7684 \u5047\u5c71\u6d41\u6c34\uff01\u800c\u662f\u53ef\u884c\u821f\u6cb3\u6d41\uff0c\u4e00\u822c\u56ed\u6797\u4e4b\u4e2d\uff0c\u6709\u6b64\u6eaa\u6d41\u89c4\u6a21\u7684\u4e0d\u591a\uff0c\u94c1\u5144\u60f3\u4e00\u60f3\uff0c\u4ec0\u4e48\u6837\u7684 \u8c6a\u95e8\u56ed\u6797\uff0c\u6709\u9a70\u884c\u821f\u63d6\u7684\u6c14\u6d3e\u3002\u201d \u3000\u3000\u94c1\u7fce\u51dd\u795e\u503e\u542c\uff0c\u6c34\u6d41\u58f0\u867d\u4e0d\u6e4d\u6025\uff0c\u4f46\u5374\u5341\u5206\u6d69\u8361\uff0c\u4e3e\u6b65\u5411\u5047\u5c71\u524d\u9762\u884c\u53bb\u3002 \u3000\u3000\u5047\u5c71\u77d7\u7acb\u5728\u4e00\u5ea7\u5927\u83b2\u6c60\u4e2d\uff0c\u6c34\u52bf\u7531\u5c71\u540e\u6697\u6e20\u4e2d\u6d8c\u5165\u83b2\u6c60\uff0c\u53d7\u5047\u5c71\u963b\u6321\uff0c\u6c14\u52bf\u4e00\u7f13\uff0c\u7531\u83b2 \u6c60\u53e6\u4e00\u7aef\u6e20\u9053\u8f6c\u5165\u6797\u6728\u6df1\u5904\u3002 \u3000\u3000\u5f62\u52bf\u81f3\u6b64\uff0c\u94c1\u7fce\u5df2\u5927\u81f4\u660e\u767d\uff0c\u6797\u6728\u63a9\u853d\u4e86\u6697\u6e20\u4eba\u53e3\uff0c\u7ecf\u8fc7\u83b2\u6c60\u53c8\u8fdb\u5165\u53e6\u4e00\u6bb5\u6697\u6e20\u4e4b\u4e2d\uff0c \u6b64\u5730\u8ddd\u5e02\u5185\u4e00\u6761\u6cb3\u9053\u4e0d\u8fdc\uff0c\u6c34\u7531\u6697\u6e20\u5165\u6cb3\uff0c\u4e0d\u5931\u4e00\u6761\u5bc6\u9053\uff0c\u4e5f\u4e0d\u662f\u5f88\u5927\u7684\u5de5\u7a0b\uff0c\u53f9\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u662f\u4e00\u6761\u79d8\u5bc6\u6c34\u9053\uff0c\u548c\u57ce\u4e2d\u4e00\u6761\u6cb3\u9053\u76f8\u63a5\uff0c\u6cb3\u9053\u7531\u5357\u57ce\u63a5\u4e0a\u62a4\u57ce\u6cb3\uff0c\u53ef\u5165\u957f\u6c5f\uff0c\u60f3\u4e00\u60f3\u5e94\u662f \u56db\u901a\u516b\u8fbe\u4e86\uff0c\u53ea\u662f\u8fd9\u5ea7\u89c4\u6a21\u5e9e\u5927\u7684\u56ed\u6797\u5b85\u9662\uff0c\u4e00\u65f6\u95f4\u5374\u60f3\u5b83\u4e0d\u8d77\uff0c\u4e3a\u8c01\u6240\u6709\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6682\u65f6\u4e0d\u7528\u60f3\u5b83\u4e86\uff0c\u5148\u5230\u4f60\u5e9c\u4e0a\u4f11\u606f\u4e00\u4e0b\uff0c\u660e\u65e5\u664b\u89c1\u738b\u77e5\u5e9c\uff0c\u518d\u4f5c\u7814\u5546\uff0c\u4ee4 \u591c\u60c5\u52bf\u53cd\u5e38\uff0c\u6b64\u5730\u4e0d\u5b9c\u4e45\u7559\u3002\u201d \u3000\u3000\u94c1\u7fce\u4e00\u70b9\u5934\uff0c\u4e24\u4eba\u8054\u8882\u800c\u8d77\uff0c\u5feb\u5982\u8759\u8760\u9f50\u98de\u3002 \u3000\u3000\u675c\u671b\u6708\u4e03\u516b\u5929\u6ca1\u6709\u597d\u7761\u8fc7\uff0c\u8fd9\u4e00\u89c9\u7761\u5f97\u5f88\u719f\uff0c\u76f4\u5230\u7b2c\u4e8c\u5929\u8fd1\u5348\u65f6\u5206\uff0c\u624d\u9192\u4e86\u8fc7\u6765\uff0c\u75b2\u52b3 \u5c3d\u590d\uff0c\u76e5\u6d17\u66f4\u8863\u4e4b\u540e\uff0c\u987f\u7136\u7cbe\u795e\u7115\u53d1\u3002 \u3000\u3000\u5403\u4e86\u4e00\u987f\u4e30\u76db\u7684\u5348\u9910\uff0c\u94c1\u7fce\u624d\u5e26\u7740\u675c\u671b\u6708\u53bb\u89c1\u738b\u5927\u4eba\u3002 \u3000\u3000\u5f53\u7136\uff0c\u738b\u77e5\u5e9c\u65e9\u5df2\u5f97\u5230\u4e86\u901a\u62a5\uff0c\u5728\u4e8c\u5802\u4e66\u623f\u4e2d\u63a5\u89c1\u8d35\u5bbe\u3002 \u3000\u3000\u8bba\u5b98\u4f4d\uff0c\u4e24\u4eba\u540c\u5217\u56db\u54c1\uff0c\u804c\u53f8\u4e5f\u4e92\u4e0d\u76f8\u5173\uff0c\u4f46\u738b\u77e5\u5e9c\u662f\u8bf7\u4eba\u5e2e\u5fd9\uff0c\u4f55\u51b5\uff0c\u675c\u671b\u6708\u5934\u4e0a\u8fd8 \u9876\u4e86\u4e2a\u5211\u90e8\u7684\u62db\u724c\uff0c\u4e0d\u770b\u91d1\u9762\u770b\u4f5b\u9762\uff0c\u738b\u77e5\u5e9c\u62a2\u5148\u4e00\u6b65\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u626c\u5dde\u77e5\u5e9c\u738b\u5c11\u537f\uff0c\u89c1 \u8fc7\u675c\u53f8\u4e3b\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\uff0c\u6298\u6740\u675c\u67d0\u4e86\u3002\u201d\u675c\u671b\u6708\u606d\u8eab\u8fd8\u793c\uff0c\u6276\u7740\u738b\u5c11\u537f\u5206\u5bbe\u4e3b\u843d\u4e86\u5ea7\u4f4d\u3002 \u3000\u3000\u966a\u5ba2\u7684\u53ea\u6709\u5211\u6848\u5e08\u7237\u5f20\u5b9d\u5584\u548c\u603b\u6355\u5934\u94c1\u7fce\u3002 \u3000\u3000\u4f55\u5927\u5149\u5b88\u5728\u4e66\u623f\u95e8\u5916\uff0c\u53e6\u6709\u56db\u540d\u7cbe\u5e72\u7684\u6355\u5feb\uff0c\u5e03\u5b88\u5728\u4e66\u623f\u56db\u5468\u3002 \u3000\u3000\u4e66\u7ae5\u732e\u4e0a\u9999\u8317\u4e4b\u540e\uff0c\u5c31\u88ab\u738b\u5927\u4eba\u6452\u9000\u51fa\u4e66\u623f\u4e4b\u5916\uff0c\u6446\u660e\u4e86\u8fd9\u662f\u5f88\u673a\u5bc6\u7684\u4f1a\u8c08\u3002\u738b\u5c11\u537f\u4e5f \u559d\u4e00\u53e3\u8336\uff0c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u624d\u9053\uff1a\u201c\u4e00\u4ef6\u8be1\u5f02\u7684\u65b0\u623f\u547d\u6848\uff0c\u4f7f\u6211\u53d1\u89c9\u4e86\u8fd9\u5ea7\u4e3e\u56fd\u7b2c\u4e00\u7684\u7e41\u534e \u540d\u57ce\uff0c\u9690\u85cf\u7740\u592a\u591a\u7684\u51f6\u9669\u3001\u5371\u673a\uff0c\u5341\u5927\u8c6a\u5bcc\u4e4b\u5bb6\uff0c\u5404\u81ea\u8058\u8bf7\u6b66\u6797\u9ad8\u624b\uff0c\u7ec4\u6210\u4e86\u5f3a\u5927\u7684\u62a4\u9662\u9635 \u5bb9\uff0c\u8bf4\u4ed6\u4eec\u662f\u4fdd\u62a4\u8d22\u5bcc\uff0c\u4e0d\u80fd\u7b97\u9519\uff0c\u4f46\u4ed6\u4eec\u631f\u52bf\u81ea\u91cd\uff0c\u62e5\u6709\u7684\u5b9e\u529b\u592a\u5f3a\u5927\uff0c\u5c31\u4e0d\u628a\u5b98\u5e9c\u653e\u5728 \u773c\u4e2d\u4e86\uff0c\u4ed6\u4eec\u5229\u7528\u6b66\u5e08\u3001\u62a4\u9662\uff0c\u5feb\u610f\u6069\u4ec7\uff0c\u903e\u8d8a\u4e86\u738b\u6cd5\uff0c\u4f46\u5b98\u5e9c\u5374\u65e0\u80fd\u5236\u88c1\u4ed6\u4eec\uff0c\u90a3\u4e9b\u6c5f\u6e56 \u9ad8\u624b\uff0c\u884c\u8e2a\u9690\u79d8\uff0c\u6765\u53bb\u5982\u98ce\uff0c\u5b98\u5e9c\u4e5f\u65e0\u6cd5\u67e5\u51fa\u5934\u7eea\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u4eba\uff0c\u201d\u94c1\u7fce\u7a81\u7136\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u94c1\u67d0\u60ed\u6127\uff0c\u5e72\u4e86\u8fd1\u4e09\u5e74\u626c\u5dde\u5e9c\u7684\u603b\u6355\u5934\uff0c\u4e5f\u53ea\u77e5\u51e0\u5bb6 \u5bcc\u8c6a\u8058\u7684\u6709\u79c1\u4eba\u4fdd\u9556\u3001\u62a4\u9662\uff0c\u5374\u4e0d\u77e5\u4ed6\u7f51\u7f57\u4e86\u5f88\u591a\u6c5f\u6e56\u4e0a\u9876\u5c16\u9ad8\u624b\uff0c\u8fd9\u6b21\u547d\u6848\u7275\u626f\u4e0a\u4e86\u626c\u5dde \u7b2c\u4e00\u5bcc\u8c6a\uff0c\u6211\u624d\u89c1\u8bc6\u5230\u4ed6\u4eec\u62e5\u6709\u7684\u529b\u91cf\u662f\u90a3\u4e48\u5f3a\u5927\uff0c\u8001\u5b9e\u8bf4\u5e9c\u536b\u4e2d\u6355\u5feb\u73ed\u5934\uff0c\u5168\u90e8\u51fa\u52a8\uff0c\u4e5f \u4e0d\u662f\u4ed6\u4eec\u7684\u654c\u624b\uff0c\u8c03\u52a8\u5927\u519b\uff0c\u4e5f\u53ea\u80fd\u6467\u6bc1\u4ed6\u4eec\u7684\u5de2\u7a74\uff0c\u65e0\u6cd5\u628a\u4ed6\u4eec\u7f09\u6355\u5f52\u6848\uff0c\u7ef3\u4e4b\u4ee5\u6cd5\u3002\u201d \u3000\u3000\u201c\u94c1\u5934\u513f\uff0c\u4e0d\u7528\u5f15\u548e\u81ea\u8d23\uff0c\u8fd9\u4e0d\u662f\u4f60\u7684\u9519\u3002\u201d\u5211\u6848\u5e08\u7237\uff0c\u5f20\u5b9d\u5584\u7a81\u7136\u63a5\u53e3\u9053\uff1a\u201c\u6b64\u4e8b\u7531 \u6765\u5df2\u4e45\uff0c\u626c\u5dde\u5e9c\u79ef\u805a\u4e86\u5929\u4e0b\u5927\u534a\u8d22\u5bcc\uff0c\u62db\u5f15\u6765\u9ed1\u767d\u4e24\u9053\u4e0a\u9ad8\u4eba\u4e91\u96c6\uff0c\u4e5f\u662f\u4e8b\u6240\u5fc5\u7136\uff0c\u6709\u4eba\u56e0 \u89ca\u89ce\u8c6a\u95e8\u8d22\u5bcc\uff0c\u6765\u6b64\u5730\u5f85\u673a\u4e0b\u624b\uff0c\u62e5\u6709\u5927\u91cf\u8d22\u5bcc\u7684\u4eba\uff0c\u91cd\u91d1\u793c\u8058\u6b66\u6797\u9ad8\u624b\uff0c\u4fdd\u4ea7\u4fdd\u547d\uff0c\u4e5f\u4e0d \u80fd\u7b97\u9519\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u738b\u77e5\u5e9c\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u8bf4\u4e0b\u53bb\u554a\uff0c\u96be\u5f97\u675c\u53f8\u4e3b\u4e5f\u5728\u8fd9\u91cc\uff0c\u6211\u4eec\u65e0\u80fd\u89e3\u51b3\u7684\u4e8b\u60c5\uff0c\u6b63\u597d \u80fd\u7531\u5211\u90e8\u63a5\u624b\u2026\u2026\u201d \u3000\u3000\u675c\u671b\u6708\u7b11\u4e00\u7b11\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u5f20\u5b9d\u5584\u5401\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u662f\u591a\u5e74\u7d2f\u79ef\u800c\u6210\u7684\u5f62\u52bf\uff0c\u6050\u4e5f\u975e\u4e00\u671d\u4e00\u5915\u6240\u80fd\u6539\u5584\uff0c\u653e\u773c\u4e3e\u56fd\u80fd \u540f\uff0c\u6bd4\u7f8e\u5927\u4eba\u8005\u4e0d\u591a\uff0c\u5927\u4eba\u4e0d\u80fd\uff0c\u53c8\u6709\u4f55\u4eba\u5177\u6709\u6b64\u80fd\u3002\u4e8b\u5b9e\u4e0a\u5341\u5927\u5bcc\u8c6a\uff0c\u5404\u5177\u5b9e\u529b\uff0c\u4fdd\u6301\u4e86 \u4e00\u4e2a\u5fae\u5999\u7684\u5e73\u8861\uff0c\u4e5f\u53ef\u4fdd\u592a\u5e73\u65e0\u4e8b\uff0c\u6015\u7684\u662f\u4e00\u4e24\u5bb6\u65e0\u9650\u6269\u5f20\u52bf\u529b\uff0c\u8d85\u8d8a\u540c\u4faa\uff0c\u751f\u51fa\u9738\u4e3b\u4e4b\u5fc3\uff0c \u90a3\u5c31\u53ef\u80fd\u4e92\u76f8\u503e\u8f67\u4e86\uff0c\u8fd9\u65f6\u523b\uff0c\u5c31\u9700\u8981\u626c\u5dde\u5e9c\u7528\u70b9\u624b\u6bb5\uff0c\u5e2e\u52a9\u5f31\u8005\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\u554a\uff0c\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u4e0d\u80fd\u4e00\u5200\u65a9\u9664\uff0c\u53ea\u6709\u8ba9\u4ed6\u4eec\u4e92\u76f8\u7275\u5236\uff0c\u6682\u4fdd\u73b0\u72b6\uff0c\u4ee5\u5f85 \u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u4e00\u76f4\u4e0d\u80af\u5f00\u53e3\uff0c\u738b\u5c11\u537f\u53ea\u597d\u5f53\u9762\u8bf7\u6559\u4e86\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u65b9\u9762\u675c\u53f8\u4e3b\u6709\u4f55\u9ad8 \u89c1\uff1f\u201d \u3000\u3000\u201c\u5f20\u5e08\u7237\u8bf4\u7684\u4e0d\u9519\uff0c\u5341\u5927\u8c6a\u95e8\u62e5\u6c5f\u6e56\u9ad8\u624b\u81ea\u91cd\u4e00\u4e8b\uff0c\u5b9c\u7f13\u56fe\u4e4b\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u91cd\u8981\u7684 \u662f\uff0c\u6c5f\u6e56\u4e0a\u7684\u6740\u624b\u4e4b\u738b\uff0c\u4e5f\u5df2\u7ecf\u6df7\u5165\u4e86\u626c\u5dde\u57ce\u4e2d\uff0c\u6b64\u4eba\u8be1\u8bc8\u767e\u51fa\uff0c\u6b66\u529f\u9ad8\u5f3a\uff0c\u5f88\u96be\u5bf9\u4ed8\uff0c\u66f4 \u53ef\u6015\u7684\u662f\u4ed6\u6709\u4e00\u6279\u5c5e\u4e0b\uff0c\u52a9\u7ea3\u4e3a\u8650\uff0c\u800c\u4e14\u914d\u5408\u7684\u5341\u5206\u4f73\u5999\uff0c\u51e0\u4e4e\u662f\u65e0\u5f80\u4e0d\u5229\u3002\u6240\u4ee5\uff0c\u4ed6\u6bcf\u5230 \u4e00\u5904\uff0c\u5fc5\u5f15\u8d77\u4e00\u9635\u8840\u96e8\u8165\u98ce\uff0c\u662f\u4e00\u4e2a\u53eb\u4eba\u5341\u5206\u5934\u75bc\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u60f3\u8d77\u4e86\u6628\u591c\u88ab\u523a\u4e00\u4e8b\uff0c\u9053\uff1a\u201c\u5982\u679c\u662f\u6740\u624b\uff0c\u5fc5\u987b\u6709\u94f6\u5b50\uff0c\u624d\u4f1a\u51fa\u5200\uff0c\u53ef\u662f\u626c\u5dde\u57ce \u4e2d\uff0c\u6709\u8c01\u4f1a\u82b1\u94f6\u5b50\u4e70\u6211\u7684\u6027\u547d\u5462\uff1f\u201d \u3000\u3000\u94c1\u7fce\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5927\u4eba\uff0c\u4f60\u9047\u4e0a\u4e86\u523a\u5ba2\uff1f\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u738b\u5c11\u537f\u4e00\u76f4\u6ca1\u6709\u544a\u8bc9\u94c1\u7fce\u9047\u523a\u7684\u4e8b\uff0c\u7b97\u7b97\u65f6\u95f4\uff0c\u5f53\u65f6\uff0c\u4e24\u4eba\u90fd\u5904\u5728\u5341\u5206\u9669\u6076\u7684 \u5883\u9047\u4e2d\u3002 \u3000\u3000\u8be5\u662f\u8bf4\u6e05\u695a\u7684\u65f6\u5019\u4e86\uff0c\u56e0\u4e3a\uff0c\u675c\u671b\u6708\u53cc\u76ee\u51dd\u6ce8\u5728\u738b\u5c11\u537f\u7684\u8138\u4e0a\uff0c\u773c\u5149\u4e2d\u6ee1\u662f\u5173\u6000\u3002 \u3000\u3000\u738b\u5927\u4eba\u5f88\u4ed4\u7ec6\u5730\u8bf4\u660e\u4e86\u9047\u523a\u7ecf\u8fc7\uff0c\u4f46\u5374\u628a\u8d3e\u82f1\u8ba4\u8bc6\u90a3\u9ed1\u8863\u4eba\u4e00\u4e8b\uff0c\u9690\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u94c1\u7fce\u542c\u5f97\u5fae\u73b0\u6012\u5bb9\uff0c\u9053\uff1a\u201c\u5fc5\u662f\u6709\u4eba\u4e70\u51f6\u884c\u523a\uff0c\u8fd9\u4e2a\u51fa\u94b1\u7684\u96c7\u4e3b\uff0c\u5c31\u4f4f\u5728\u626c\u5dde\u57ce\u4e2d\u3002\u5f20 \u5e08\u7237\uff0c\u5fc3\u4e2d\u6000\u6068\u5927\u4eba\u7684\u4eba\uff0c\u4e0d\u4f1a\u5f88\u591a\uff0c\u67e5\u67e5\u770b\uff0c\u6211\u8981\u5c3d\u65e9\u628a\u8fd9\u4e2a\u7978\u6839\u6316\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u94c1\u7fce\uff0c\u8fd9\u4ef6\u4e8b\u4e0d\u7528\u592a\u6025\u2026\u2026\u201d\u738b\u5927\u4eba\u8868\u73b0\u51fa\u4e86\u96cd\u5bb9\u5927\u5ea6\uff0c\u9053\uff1a\u201c\u6025\u7684\u662f\u65e9\u4e9b\u67e5\u51fa\u6740\u6b7b \u9a6c\u516c\u5b50\u7684\u51f6\u624b\uff0c\u91d1\u767e\u5e74\u5df2\u5e03\u4e0b\u5341\u9762\u57cb\u4f0f\uff0c\u6025\u9700\u4f60\u53c2\u4e0e\u5176\u4e8b\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u2026\u2026\u201d\u94c1\u7fce\u4e3a\u96be\u5730\u9053\uff1a\u201c\u523a\u5ba2\u5df2\u548c\u5927\u4eba\u7167\u4e86\u9762\uff0c\u5c82\u80af\u8f7b\u6613\u653e\u624b\uff0c\u4fdd\u62a4\u5927\u4eba\u7684\u5b89\u5168\uff0c \u624d\u662f\u5f53\u6025\u4e4b\u52a1\u3002\u201d \u3000\u3000\u201c\u94c1\u603b\u6355\u5934\uff0c\u201d\u738b\u5927\u4eba\u9053\uff1a\u201c\u672c\u5e9c\u751f\u6b7b\uff0c\u5982\u4f55\u80fd\u6bd4\u5f97\u65b0\u623f\u8840\u6848\u91cd\u8981\uff0c\u56f4\u6355\u68a6\u5e7b\u4e4b\u5200\uff0c\u66f4 \u662f\u60ca\u5929\u52a8\u5730\u7684\u5927\u4e8b\uff0c\u6b64\u6848\u4e0d\u7834\uff0c\u9a6c\u63d0\u7763\u5c82\u80af\u7f62\u4f11\uff0c\u91d1\u767e\u5e74\u8981\u4f60\u6613\u5bb9\u8fdb\u5165\u91d1\u5e9c\uff0c\u8981\u884c\u8e2a\u9690\u79d8\uff0c \u4f60\u65e9\u70b9\u53bb\u5427\uff01\u5e9c\u8859\u4e2d\u4e8b\uff0c\u6709\u738b\u526f\u6355\u5934\u3001\u4f55\u5927\u5149\u548c\u51e0\u4f4d\u6355\u5feb\u73ed\u5934\uff0c\u65e5\u591c\u9632\u5b88\uff0c\u4f60\u4e5f\u5e94\u8be5\u653e\u5fc3 \u4e86\uff01\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u53f3\u624b\u8fde\u6325\uff0c\u5927\u6709\u903c\u8feb\u94c1\u7fce\u5c3d\u5feb\u52a8\u8eab\u7684\u610f\u601d\u3002 \u3000\u3000\u94c1\u7fce\u6709\u7740\u5f88\u591a\u8bdd\u60f3\u8bf4\uff0c\u591c\u8bbf\u56db\u51e4\u891b\u7684\u7ecf\u5386\uff0c\u6076\u6597\u7ea2\u88f3\u7684\u8fc7\u7a0b\uff0c\u4f46\u738b\u5927\u4eba\u4e00\u76f4\u50ac\u4fc3\uff0c\u53ea\u597d \u957f\u8bdd\u77ed\u8bf4\u5730\u9053\uff1a\u201c\u5c5e\u4e0b\u8fd9\u4e00\u5bb5\u7ecf\u5386\uff0c\u675c\u53f8\u4e3b\u5927\u534a\u76ee\u7779\uff0c\u5c31\u7531\u4ed6\u4ee3\u4f5c\u8bf4\u660e\u4e86\uff0c\u5c5e\u4e0b\u8fd9\u5c31\u8d76\u5f80\u91d1 \u5e9c\uff0c\u5c31\u6b64\u522b\u8fc7\u3002\u201d\u62b1\u62f3\u4e00\u793c\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u76ee\u7779\u94c1\u7fce\u80cc\u5f71\u6d88\u5931\uff0c\u6734\u671b\u6708\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u4eba\uff0c\u6709\u4f55\u91cd\u8981\u8d50\u6559\uff0c\u8fde\u94c1\u603b\u6355\u5934\u4e5f\u4e0d\u4fbf \u4e0e\u95fb\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u6014\u4e86\u4e00\u6014\uff0c\u7a81\u7136\u5927\u7b11\u9053\uff1a\u201c\u5389\u5bb3\u5440\uff01\u675c\u5144\uff0c\u5176\u5b9e\uff0c\u4e0d\u662f\u6709\u610f\u7684\u4e0d\u8ba9\u94c1\u7fce\u77e5\u9053\uff0c\u53ea \u662f\u6015\u5f71\u54cd\u5230\u4ed6\u7684\u5fc3\u60c5\uff0c\u4e00\u65e6\u80f8\u6709\u6210\u89c1\uff0c\u5f62\u8bf8\u4e8e\u5916\uff0c\u5c31\u5f71\u54cd\u5230\u4ed6\u548c\u8d3e\u82f1\u7684\u5408\u4f5c\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u613f\u95fb\u5176\u8be6\uff01\u201d \u3000\u3000\u201c\u8d3e\u82f1\u8fd9\u4e2a\u4eba\uff01\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u675c\u5144\u542c\u8fc7\u5427\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u6447\u6447\u5934\u3002 \u3000\u3000\u201c\u540d\u4e0d\u89c1\u7ecf\u4f20\uff0c\u4f46\u5374\u662f\u9876\u5c16\u9ad8\u4eba\uff0c\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u4e0d\u4f46\u6b66\u529f\u5947\u9ad8\uff0c\u8c0b\u7565\u4ea6\u975e\u7b49\u95f2\uff0c\u80fd\u8010 \u5fc3\u5fcd\u6027\uff0c\u88c5\u4f5c\u58f0\u8272\u4e0d\u52a8\uff0c\u662f\u4e2a\u5f88\u53ef\u6015\u7684\u654c\u4eba\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\uff0c\u80fd\u4e0d\u80fd\u4e3e\u51fa\u4e00\u4e2a\u4f8b\u5b50\u6765\uff0c\u675c\u67d0\u4eba\u4e5f\u597d\u53c2\u8be6\u53c2\u8be6\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u70b9\u70b9\u5934\uff0c\u628a\u8d3e\u82f1\u8ba4\u8bc6\u523a\u5ba2\uff0c\u8fde\u540c\u4ed6\u770b\u5230\u5207\u5f00\u6c34\u706b\u76f8\u6d4e\u77f3\u73e0\u540e\u7684\u8868\u60c5\uff0c\u4e5f\u4e00\u9053\u8bf4\u4e86 \u51fa\u6765\uff0c\u53ea\u4e0d\u8fc7\u628a\u81ea\u5df1\u7684\u8bc4\u65ad\u9690\u4e86\u8d77\u6765\uff0c\u5c31\u7b97\u5728\u675c\u671b\u6708\u7684\u9762\u524d\uff0c\u738b\u5927\u4eba\u4e5f\u9690\u85cf\u4e86\u4e00\u4e9b\u667a\u6167\u7684\u950b \u8292\u3002 \u3000\u3000\u675c\u671b\u6708\u6709\u70b9\u9707\u52a8\u4e86\uff0c\u4f46\u6781\u529b\u4fdd\u6301\u7740\u9547\u9759\uff0c\u9053\uff1a\u201c\u523a\u5ba2\u4e00\u4e0b\u5b50\u51fa\u52a8\u4e86\u4e09\u4e2a\u4eba\uff0c\u5012\u5f88\u5c11\u89c1\uff0c \u5f88\u50cf\u90a3\u4f4d\u6740\u624b\u4e4b\u738b\u7684\u4f5c\u98ce\uff0c\u4f46\u4ed6\u4e0d\u4f1a\u4e3b\u52a8\u6740\u4eba\uff0c\u6b63\u5982\u5927\u4eba\u63a8\u8bba\uff0c\u5fc5\u6709\u4eba\u51fa\u4ef7\u96c7\u4ed6\u52a8\u624b\u3002\u8d3e\u82f1 \u65e2\u7136\u8ba4\u8bc6\uff0c\u81ea\u7136\u6709\u673a\u4f1a\u4f7f\u4ed6\u8bf4\u51fa\u6765\uff0c\u5012\u662f\u90a3\u9897\u6c34\u706b\u76f8\u6d4e\u7684\u77f3\u73e0\uff0c\u5207\u5f00\u4e4b\u540e\uff0c\u662f\u4e2a\u4ec0\u4e48\u5f62\u6001\u5185 \u5bb9\uff0c\u5f15\u8d77\u8d3e\u82f1\u7684\u6026\u7136\u5fc3\u52a8\u5462\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u5fc3\u4e2d\u5fd6\u9053\uff1a\u4f60\u4e0d\u662f\u4e5f\u6026\u7136\u5fc3\u52a8\u4e86\u4e48\uff1f\u770b\u6765\uff0c\u90a3\u77f3\u73e0\u786e\u6709\u5b9d\u8d35\u4e4b\u5904\uff0c\u53ea\u662f\u6c5f\u6e56\u4e0a\u77e5 \u9053\u5185\u60c5\u7684\u4eba\u4e0d\u591a\u3002\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u91d1\u767e\u5e74\u8bf4\u5b83\u5c71\u4e0d\u50cf\u5c71\uff0c\u6c34\u4e0d\u50cf\u6c34\uff0c\u6211\u770b\u50cf\u4e00\u5e45\u753b\uff0c \u574f\u7684\u56fe\u753b\uff0c\u4e5f\u6709\u70b9\u50cf\u592a\u6781\u56fe\u2026\u2026\u201d \u3000\u3000\u675c\u671b\u6708\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6b64\u7269\u80fd\u5f15\u52a8\u68a6\u5e7b\u4e4b\u5200\uff0c\u81ea\u6709\u5b83\u53ef\u8d35\u4e4b\u5904\uff0c\u5927\u4eba\u60f3\u5982\u4f55\u4e0b\u624b\u529e\u7406\u6b64 \u6848\u5462\uff1f\u201d \u3000\u3000\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u5148\u64d2\u4e0b\u68a6\u5e7b\u4e4b\u5200\uff0c\u67e5\u6e05\u695a\u9a6c\u516c\u5b50\u88ab\u6740\u7ecf\u8fc7\uff0c\u7ed9\u9a6c\u63d0\u7763\u4e00\u4e2a\u4ea4\u4ee3\uff0c\u518d\u8ffd\u7f09\u523a \u6740\u6211\u7684\u51f6\u624b\uff0c\u7ef3\u4e4b\u4ee5\u6cd5\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4f20\u8bf4\u91d1\u767e\u5e74\u662f\u626c\u5dde\u57ce\u4e2d\u7b2c\u4e00\u5bcc\u8c6a\uff0c\u624b\u4e0b\u9ad8\u624b\uff0c\u9664\u4e86\u8d3e\u82f1\u4e4b\u5916\uff0c\u8fd8\u6709\u4f55\u4eba\uff1f\u201d \u3000\u3000\u201c\u5341\u5927\u5bcc\u8c6a\u90fd\u5c3d\u529b\u4fdd\u5b88\u9690\u79d8\uff0c\u4e0d\u8ba9\u5b9e\u529b\u5916\u6cc4\u3002\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u91d1\u767e\u5e74\u867d\u7136\u5f88\u5c0a\u91cd\u6211\uff0c\u4f46 \u4ecd\u4fdd\u7559\u5f88\u591a\uff0c\u9664\u4e86\u8d3e\u82f1\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4f4d\u5929\u67ab\u9053\u957f\uff0c\u5176\u4ed6\u8fd8\u6709\u5f88\u591a\u9ad8\u624b\uff0c\u53ea\u53ef\u60dc\u6211\u4e0d\u77e5\u4ed6\u4eec\u7684 \u59d3\u540d\uff0c\u5509\uff01\u5176\u5b9e\uff0c\u4ed6\u4eec\u662f\u4e0d\u662f\u9ad8\u624b\uff0c\u6211\u4e5f\u65e0\u80fd\u5206\u8fa8\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u4eba\u867d\u7136\u8bfb\u4e66\u4e07\u5377\uff0c\u535a\u5b66\u591a\u624d\uff0c\u4f46\u6c5f\u6e56\u4e2d\u4e8b\uff0c\u4e94\u7ecf\u56db\u4e66\u4e0a\u53ef\u6ca1\u6709 \u8bb0\u8f7d\uff0c\u5927\u4eba\uff0c\u53c8\u600e\u4f1a\u77e5\u6653\u7684\u592a\u591a\u5462\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u5401\u53e3\u6c14\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u94c1\u603b\u6355\u5934\u548c\u675c\u5144\u662f\u5728\u54ea\u91cc\u89c1\u9762\u7684\uff1f\u201d \u3000\u3000\u201c\u5730\u4e0b\u56da\u5ba4\uff0c\u94c1\u5144\u8fd8\u7ecf\u5386\u4e86\u4e00\u573a\u751f\u6b7b\u6076\u6218\u2026\u2026\u201d\u675c\u671b\u6708\u628a\u5982\u4f55\u548c\u94c1\u7fce\u4f1a\u9762\u7684\u7ecf\u8fc7\uff0c\u4ed4\u7ec6 \u8bf4\u4e86\u4e00\u8ffd\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u542c\u5230\u7ea2\u88f3\u5f79\u86c7\u7531\u8896\u53e3\u7a9c\u51fa\u65f6\uff0c\u8138\u8272\u5927\u53d8\uff0c\u4f3c\u662f\u5bf9\u86c7\u7684\u754f\u60e7\uff0c\u5c24\u8fc7\u9047\u4e0a\u523a\u5ba2\u3002 \u3000\u3000\u201c\u53ef\u8981\u672c\u5e9c\u51fa\u52a8\u5e9c\u536b\u4e2d\u4e09\u73ed\u6355\u5feb\uff0c\u8d76\u53bb\u56db\u51e4\u697c\u4e2d\u62ff\u4eba\uff1f\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u5f53\u7136\uff0c\u5982\u679c\u60c5\u52bf \u6709\u53d8\uff0c\u6211\u4e5f\u53ef\u4ee5\u8981\u5b88\u5907\u5c06\u519b\uff0c\u6d3e\u5175\u9a6c\u53c2\u4e0e\u884c\u52a8\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u6253\u8349\u60ca\u86c7\u4e86\uff0c\u201d\u675c\u671b\u6708\u8bf4\u9053\uff1a\u201c\u7f09\u6355\u6740\u624b\u98de\u8d3c\uff0c\u4e0d\u5b9c\u5927\u519b\u51fa\u52a8\uff0c\u4ed6\u4eec\u5927\u90fd\u5584\u957f\u8f7b \u8eab\u63d0\u7eb5\u4e4b\u672f\uff0c\u7ffb\u623f\u8d8a\u5c4b\uff0c\u5982\u5c65\u5e73\u5730\uff0c\u4eba\u591a\u6613\u4e71\uff0c\u53cd\u800c\u7ed9\u4ed6\u4eec\u9690\u9041\u9003\u8d70\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u70b9\u70b9\u5934\u3002 \u3000\u3000\u675c\u671b\u6708\u53c8\u9053\uff1a\u201c\u91d1\u5e9c\u9ad8\u624b\u4f17\u591a\uff0c\u4e5f\u7528\u4e0d\u7740\u6211\u53c2\u4e0e\u57cb\u4f0f\uff0c\u91d1\u5e9c\u4e2d\u4eba\uff0c\u65e2\u7136\u4e0d\u77e5\u6211\u5df2\u5230\u626c\u5dde\uff0c \u6682\u65f6\u4e0d\u7528\u6cc4\u6f0f\u6d88\u606f\uff0c\u6211\u5c3d\u5feb\u628a\u91d1\u9675\u5f85\u547d\u7684\u51e0\u4e2a\u52a9\u624b\u8c03\u6765\uff0c\u6211\u76f8\u4fe1\u5317\u3001\u897f\u3001\u4e2d\u4e09\u4f4d\u540d\u6355\uff0c\u5982\u82e5 \u65e0\u91cd\u5927\u6848\u4ef6\u8ffd\u67e5\uff0c\u8fd1\u65e5\u90fd\u4f1a\u8d76\u5230\u626c\u5dde\uff0c\u4f46\u4ed6\u4eec\u5982\u4f55\u4e00\u4e2a\u6765\u6cd5\uff0c\u5c31\u975e\u6211\u6240\u80fd\u9884\u6d4b\u4e86\uff0c\u4ed6\u4eec\u90fd\u662f \u529e\u6848\u9ad8\u624b\uff0c\u5404\u4eba\u6709\u5404\u4eba\u7684\u884c\u4e8b\u4e60\u6027\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u6211\u8981\u738b\u526f\u603b\u6355\u5934\u914d\u5408\u884c\u4e8b\uff0c\u4e0d\u5e72\u4e0e\u8bf8\u4f4d\u884c\u52a8\uff0c\u53ea\u4f9b\u5e94\u8bf8\u4f4d\u9700\u8981\uff0c\u4e0d\u8fc7\uff0c\u672c \u5e9c\u5012\u5e0c\u671b\u793e\u5144\u5b89\u6392\u4e2a\u65f6\u95f4\u51fa\u6765\uff0c\u5c11\u537f\u60f3\u77bb\u4ef0\u77bb\u4ef0\u4e09\u5927\u540d\u6355\u7684\u4e30\u91c7\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\u613f\u4e0d\u613f\u89c1\u4e00\u89c1\u5211\u90e8\u7684\u603b\u6355\u5934\u5462\uff1f\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5929\u4e0b\u56db\u5927\u540d\u6355\uff0c\u90fd\u662f\u5979\u7edf\u7387\u7684\u5c5e \u4e0b\u3002\u201d \u3000\u3000\u201c\u56fa\u6240\u613f\u4e5f\uff0c\u4e0d\u6562\u8bf7\u5c14\u3002\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u542c\u8bf4\u603b\u6355\u5934\u662f\u4f4d\u7f8e\u5973\uff1f\u201d \u3000\u3000\u201c\u5c11\u89c1\u7684\u7f8e\u5973\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5343\u5a07\u767e\u5a9a\u4e00\u4f73\u4eba\uff0c\u675c\u67d0\u884c\u8e2a\u904d\u5929\u4e0b\uff0c\u89c1\u8fc7\u7684\u7f8e\u5973\u591a\u77e3\uff01 \u4f46\u50cf\u603b\u6355\u90a3\u6837\u7684\u7f8e\u5973\uff0c\u6211\u8fd8\u4ece\u672a\u89c1\u8fc7\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u542c\u8bf4\u7687\u4e0a\u4e5f\u4e3a\u5979\u52a8\u8fc7\u5fc3\uff0c\u771f\u5e94\u4e86\u90a3\u53e5\u56de\u7738\u4e00\u7b11\u767e\u5a9a\u751f\uff0c\u516d\u5b98\u7c89\u9edb\u5c3d\u5931\u8272 \u4e86\u3002\u201d \u3000\u3000\u201c\u771f\u662f\u597d\u4e8b\u4e0d\u51fa\u95e8\uff0c\u7eef\u95fb\u4f20\u5343\u91cc\u3002\u201d\u675c\u671b\u6708\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u8fc7\u786e\u6709\u5176\u4e8b\uff0c\u4f46\u6211\u4eec\u7684 \u603b\u6355\u5934\u624b\u6bb5\u9ad8\u660e\uff0c\u4e0d\u4f46\u8ba9\u7687\u4e0a\u7684\u7eee\u68a6\u6210\u7a7a\uff0c\u53cd\u6536\u5979\u4f5c\u4e86\u4e49\u5973\uff0c\u4ee5\u516c\u4e3b\u7684\u8eab\u4efd\u517c\u9886\u5211\u90e8\u603b\u6355\u5934\uff0c \u5c31\u65e0\u5f80\u4e0d\u5229\u4e86\u3002\u201d \u3000\u3000\u201c\u5982\u6b64\u624d\u5973\uff0c\u7406\u5f53\u4e00\u89c1\uff0c\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u8fd9\u5c31\u8981\u675c\u5144\u6210\u5168\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\u51fa\u4f7f\u626c\u5dde\uff0c\u5b98\u540d\u8fdc\u64ad\uff0c\u603b\u6355\u5934\u4e5f\u95fb\u540d\u4e45\u77e3\uff01\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5982\u80fd\u4fee\u4e66\u4e00\u5c01\uff0c\u9080\u8bf7 \u5979\u4eb2\u4e0b\u626c\u5dde\uff0c\u67e5\u529e\u5947\u6848\uff0c\u4e0d\u4f46\u5947\u6848\u53ef\u7834\uff0c\u5927\u4eba\u4e5f\u53ef\u4e00\u7779\u5979\u7684\u624d\u6167\u98ce\u8c8c\uff0c\u5c82\u4e0d\u662f\u4e00\u4e3e\u4e24\u5f97\uff1f\u201d \u3000\u3000\u201c\u675c\u5144\u662f\u8bf4\uff0c\u603b\u6355\u5934\u771f\u6709\u7740\u5f88\u5f3a\u7684\u529e\u6848\u80fd\u529b\uff1f\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u4e0d\u662f\u865a\u2026\u2026\u201d\u865a\u4ec0\u4e48\uff1f\u738b \u5927\u4eba\u7a81\u7136\u4f4f\u53e3\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u201c\u5b8c\u5b8c\u5168\u5168\u7684\u771f\u5b9e\u672c\u9886\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u865a\u6709\u5176\u8868\u7684\u4eba\uff0c\u4e5f\u65e0\u6cd5\u7edf\u7387\u56db\u5927\u540d\u6355\uff0c\u6211\u4eec\u90fd \u662f\u771f\u6b63\u5730\u4f69\u670d\u5979\uff0c\u4e0d\u8bba\u662f\u6b66\u529f\u3001\u624d\u667a\uff0c\u6211\u4eec\u90fd\u4e0d\u5982\u5979\uff0c\u56db\u5927\u540d\u6355\uff0c\u53ea\u6709\u4e00\u6837\u6bd4\u5979\u9ad8\u4e9b\u3002\u201d \u3000\u3000\u201c\u54ea\u4e00\u6837\uff1f\u201d\u738b\u5c11\u537f\u597d\u5947\u5730\u95ee\u9053\u3002 \u3000\u3000\u201c\u5e74\u9f84\uff0c\u201d\u675c\u671b\u6708\u7b11\u9053\uff1a\u201c\u6bd4\u5979\u75f4\u957f\u5f88\u591a\uff0c\u5979\u5c31\u4efb\u603b\u6355\u5934\u5df2\u8fd1\u4e24\u5e74\uff0c\u8fd8\u4e0d\u5230\u4e8c\u5341\u5c81\u5427\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u6709\u70b9\u52a8\u5bb9\u4e86\uff0c\u5401\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5982\u6b64\u6587\u6b66\u5168\u624d\u7684\u4eba\uff0c\u5c82\u53ef\u4e0d\u89c1\uff0c\u5c11\u537f\u5373\u523b\u4fee\u4e66\u8bf7 \u547d\u3002\u201d \u3000\u3000\u8bf4\u5199\u5c31\u5199\uff0c\u7acb\u523b\u547d\u4eba\u78e8\u58a8\u4fee\u4e66\u3002 \u3000\u3000------------- \u3000\u3000\u5b64\u5251\u751f  \u626b\u63cf\u3000  zerotang  OCR  \uff0c\u65e7\u96e8\u697c\u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c \u4e94 \u7ae0 \u3000\u3000\u4ed6\u672c\u662f\u4e00\u4ee3\u72b6\u5143\u4e4b\u624d\uff0c\u80f8\u6709\u4e07\u5377\u4e66\uff0c\u8d70\u7b14\u5982\u98de\uff0c\u7247\u523b\u95f4\u6210\u5c31\u4e86\u4e00\u5c01\u6587\u60c5\u5e76\u8302\u7684\u4e66\u4fe1\u3002 \u3000\u3000\u675c\u671b\u6708\u4e5f\u662f\u80f8\u6709\u6587\u58a8\u7684\u4eba\uff0c\u770b\u4e86\u4e00\u904d\uff0c\u4e0d\u4f4f\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5c31\u51ed\u8fd9\u5c01\u4e66\u4fe1\uff0c\u603b\u6355\u5934\u5fc5\u6765\u65e0 \u7591\u3002\u201d\u53e0\u8d77\u6765\uff0c\u6536\u5165\u4e86\u888b\u4e2d\u3002 \u3000\u3000\u201c\u675c\u5144\uff0c\u8fd8\u8981\u5199\u4e2a\u5c01\u5957\u554a\uff01\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u7701\u53bb\u4fe1\u5c01\uff0c\u5c82\u4e0d\u6709\u5931\u606d\u656c\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7528\u4e86\uff0c\u675c\u67d0\u53ef\u80fd\u7528\u98de\u9e3d\u4f20\u4e66\uff0c\u90a3\u5c31\u8d8a\u8f7b\u8d8a\u597d\u4e86\u3002\u201d\u7ad9\u8d77\u8eab\u5b50\uff0c\u4e00\u62b1\u62f3\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c \u4e00\u822c\u800c\u8a00\uff0c\u6740\u624b\u65e2\u7136\u548c\u4f60\u7167\u4e86\u9762\uff0c\u4fbf\u4e0d\u4f1a\u8f7b\u6613\u653e\u624b\uff0c\u675c\u67d0\u672c\u5f53\u7559\u5728\u8fd9\u91cc\u4fdd\u62a4\u5927\u4eba\uff0c\u4f46\u5e9c\u8859\u4e2d \u8fd8\u6709\u4e0d\u5c11\u9ad8\u624b\uff0c\u4e0d\u8fc7\uff0c\u51e1\u4e8b\u5c0f\u5fc3\u4e9b\u597d\uff0c\u4f60\u591a\u73cd\u91cd\uff0c\u675c\u67d0\u6682\u884c\u544a\u8f9e\u3002\u201d \u3000\u3000\u738b\u5927\u4eba\u6ca1\u6709\u7559\u5ba2\uff0c\u6b64\u65f6\u6b64\u60c5\uff0c\u675c\u671b\u6708\u5df2\u5f00\u59cb\u5fd9\u4e8e\u8ffd\u67e5\u6848\u60c5\u3002 \u3000\u3000\u9001\u8d70\u4e86\u675c\u671b\u6708\uff0c\u5f20\u5b9d\u5584\u624d\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5927\u4eba\uff0c\u7a76\u7adf\u662f\u8c01\u8981\u6740\u4f60\uff0c\u5fc3\u4e2d\u53ef\u6709\u8ba1\u8f83\uff0c\u5927\u4eba \u662f\u4e00\u5e9c\u4e4b\u957f\uff0c\u6740\u5b98\u5f62\u540c\u9020\u53cd\uff0c\u662f\u682a\u8fde\u4e5d\u65cf\u7684\u5927\u7f6a\uff0c\u4e00\u822c\u7684\u6c5f\u6e56\u4e2d\u4eba\uff0c\u90fd\u5fcc\u8bb3\u4f24\u5bb3\u5b98\u5458\uff01\u201d \u3000\u3000\u201c\u5b9e\u5728\u60f3\u4e0d\u51fa\u6765\uff0c\u6211\u81ea\u4fe1\u529e\u6848\u516c\u6b63\uff0c\u6ca1\u6709\u9020\u6210\u51a4\u72f1\uff0c\u201d\u738b\u5c11\u537f\u51dd\u795e\u6c89\u601d\u4e86\u826f\u4e45\uff0c\u9053\uff1a \u201c\u8c01\u4f1a\u8981\u6740\u6211\u5462\uff1f\u201d \u3000\u3000\u94c1\u7fce\u6362\u4e86\u4e00\u4ef6\u7d27\u8eab\u52b2\u88c5\uff0c\u8138\u4e0a\u4e5f\u4f5c\u4e86\u4e00\u4e9b\u6539\u53d8\uff0c\u8fdb\u5165\u91d1\u5e9c\uff0c\u76f4\u63a5\u6c42\u89c1\u8d3e\u82f1\u3002\u8d3e\u82f1\u4e00\u773c\u5c31 \u770b\u51fa\u4ed6\u8138\u6d82\u4e86\u836f\u7269\uff0c\u4f46\u4e5f\u65e0\u6cd5\u4e00\u65f6\u95f4\u770b\u51fa\u4ed6\u662f\u4f55\u4eba\u3002\u4f46\u4ed6\u53cd\u5e94\u7075\u654f\uff0c\u8fdb\u5165\u5c0f\u5385\uff0c\u5df2\u6709\u51b3\u65ad\uff0c \u7b11\u4e00\u7b11\u9053\uff1a\u201c\u94c1\u603b\u6355\u5934\u6765\u7684\u6b63\u597d\uff0c\u5929\u67ab\u9053\u957f\u4e5f\u4e8e\u6628\u591c\u5f52\u6765\uff0c\u655d\u4e1c\u4e3b\u4eb2\u81ea\u53c2\u4e0e\u4e86\u8fd9\u4e00\u573a\u8bbe\u4f0f\u4f1a \u8bae\uff0c\u8fd8\u5206\u914d\u4e86\u804c\u53f8\u4efb\u52a1\uff0c\u94c1\u603b\u6355\u5982\u679c\u540c\u610f\uff0c\u5c31\u53ef\u7167\u8ba1\u5212\u884c\u4e8b\u4e86\u3002\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u770b\u8d77\u6765\u94c1\u67d0\u8fd9\u6613\u5bb9\u672f\u8e69\u811a\u7684\u5f88\uff0c\u4e00\u773c\u5c31\u88ab\u77a7\u7a7f\u4e86\u3002\u201d \u3000\u3000\u201c\u8a00\u91cd\uff0c\u8a00\u91cd\uff0c\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u8be5\u5230\u7684\u4eba\uff0c\u90fd\u5df2\u5230\u9f50\uff0c\u53ea\u5dee\u94c1\u603b\u6355\u5934\u4e00\u4eba\uff0c\u655d\u4e1c\u4e3b\u8bf7\u6c42\u77e5 \u5e9c\u5927\u4eba\u8f6c\u544a\u9601\u4e0b\uff0c\u8981\u6613\u5bb9\u8fdb\u5165\u91d1\u5e9c\uff0c\u4ee5\u4fdd\u5b98\u5e9c\u53c2\u4e0e\u7684\u9690\u79d8\u4e0d\u6cc4\uff0c\u6709\u6b64\u4e24\u4e2a\u91cd\u70b9\uff0c\u6240\u4ee5\uff0c\u8d3e\u82f1 \u4e00\u731c\u5c31\u4e2d\u4e86\u3002\u201d \u3000\u3000\u94c1\u7fce\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\u7684\u610f\u601d\u662f\u94c1\u67d0\u53c2\u4e0e\u6b64\u4e8b\u7684\u8eab\u4efd\uff0c\u5c1a\u5c5e\u673a\u5bc6\uff0c\u4e0d\u8981\u66b4\u9732\u4e86\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u4e8b\u4ee5\u5bc6\u6210\u3001\u6cc4\u8d25\uff0c\u5b98\u5e9c\u53c2\u4e0e\uff0c\u6700\u597d\u65e0\u522b\u8ba9\u9b3c\u5200\u77e5\u6653\uff0c\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u4e5f\u53ea\u597d\u8fde\u655d\u5e9c \u4e2d\u4eba\u4e5f\u6682\u65f6\u7792\u8fc7\u4e86\uff0c\u77e5\u9053\u94c1\u603b\u6355\u53c2\u4e0e\u7684\u4eba\uff0c\u53ea\u6709\u655d\u4e1c\u4e3b\uff0c\u5929\u67ab\u9053\u957f\uff0c\u548c\u533a\u533a\u5728\u4e0b\u3002\u201d \u3000\u3000\u94c1\u7fce\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\u5427\uff01\u94c1\u67d0\u4eba\u5206\u914d\u7684\u4ec0\u4e48\u5de5\u4f5c\uff0c\u8d3e\u5144\u5148\u6307\u70b9\u6307\u70b9\u3002\u201d \u3000\u3000\u201c\u603b\u6355\u5934\u6280\u827a\u4e1a\u7cbe\u535a\uff0c\u6240\u4ee5\uff0c\u6ca1\u6709\u56fa\u5b9a\u7684\u804c\u53f8\uff0c\u662f\u8d1f\u8d23\u622a\u51fb\u7684\u884c\u52a8\uff0c\u4e5f\u5c31\u662f\u54ea\u91cc\u9700\u8981\u5230 \u90a3\u91cc\u3002\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u8d3e\u67d0\u4eba\u4e5f\u8d1f\u8d23\u5835\u622a\u884c\u52a8\uff0c\u548c\u603b\u6355\u5934\u5e76\u80a9\u4f5c\u6218\u3002\u201d\u60f3\u5230\u4ed6\u65bd\u5c55\u201c\u8774\u8776\u7a7f\u82b1 \u6b65\u201d\u7684\u9ad8\u660e\u8eab\u624b\uff0c\u94c1\u7fce\u54ea\u6562\u6709\u8f7b\u89c6\u4e4b\u5fc3\uff0c\u7b11\u9053\uff1a\u201c\u548c\u8d3e\u5144\u5e76\u80a9\u5bf9\u654c\uff0c\u94c1\u67d0\u4e4b\u5e78\u4e5f\u3002\u201d\u7a81\u7136\u62b1 \u62f3\u4e00\u63d6\u3002 \u3000\u3000\u8d3e\u82f1\u6025\u6025\u8fd8\u793c\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u8fd9\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u4ee3\u738b\u5927\u4eba\u81f4\u8c22\uff0c\u8d3e\u5144\u6551\u4e86\u4ed6\u4e00\u6b21\u5371\u96be\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u4e0d\u80fd\u518d\u53eb\u603b\u6355\u5934\u4e86\uff0c\u542c\u5f97\u523a\u8033\uff0c \u4e5f\u6cc4\u6f0f\u4e86\u9690\u79d8\u5440\uff01\u201d \u3000\u3000\u201c\u94c1\u5144\u5982\u6b64\u5429\u5490\uff0c\u8d3e\u82f1\u5c31\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\uff0c\u5509\uff01\u90a3\u4e00\u591c\u662f\u8d3e\u67d0\u7684\u758f\u5ffd\uff0c\u4f7f\u5927\u4eba\u53d7\u5230\u4e86\u60ca \u5413\uff0c\u655d\u4e1c\u4e3b\u8fd8\u4e3a\u6b64\u8d23\u53f1\u4e86\u8d3e\u67d0\u601d\u8651\u4e0d\u5468\u3002\u201d \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\uff0c\u5929\u67ab\u9053\u957f\u5927\u6b65\u884c\u4e86\u8fdb\u6765\uff0c\u6253\u91cf\u4e86\u94c1\u7fce\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6765\u7684\u597d\uff0c\u6765\u7684\u597d\u3002\u201d \u3000\u3000\u94c1\u7fce\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u9053\u957f\uff0c\u6709\u4f55\u89c1\u6559\uff0c\u94c1\u67d0\u6d17\u8033\u606d\u542c\u4e86\u3002\u201d \u3000\u3000\u201c\u68a6\u5e7b\u4e4b\u5200\uff0c\u662f\u5426\u8fdb\u5165\u626c\u5dde\uff0c\u8d2b\u9053\u8fd8\u672a\u627e\u5230\u7ebf\u7d22\uff0c\u201d\u5929\u67ab\u9053\u957f\uff1a\u201c\u4f46\u6740\u624b\u4e4b\u738b\u8840\u624b\u65b9\u8f6e\uff0c \u5374\u5e26\u4e86\u4e00\u6279\u4eba\uff0c\u8fdb\u5165\u626c\u5dde\uff0c\u5176\u4eba\u6240\u5230\u4e4b\u5904\uff0c\u5f88\u5feb\u5c31\u5f15\u8d77\u8840\u96e8\u8165\u98ce\uff0c\u547d\u6848\u63a5\u8e35\u800c\u6765\uff0c\u6709\u5f97\u4f60\u5fd9 \u7684\u4e86\u3002\u201d \u3000\u3000\u201c\u9053\u957f\u53ef\u77e5\u4ed6\u56e0\u4f55\u6765\u6b64\uff1f\u201d \u3000\u3000\u201c\u9664\u4e86\u6740\u4eba\u4e4b\u5916\uff0c\u65b9\u8f6e\u4e0d\u4f5c\u7b2c\u4e8c\u79cd\u4e70\u5356\u3002\u201d\u5929\u67ab\u9053\uff1a\u201c\u4f46\u4ec0\u4e48\u4eba\u5f15\u4ed6\u6765\u6b64\uff0c\u8fd8\u6ca1\u6709\u67e5\u51fa \u6765\u3002\u201d \u3000\u3000\u94c1\u7fce\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u65b0\u623f\u547d\u6848\u8fd8\u6ca1\u6709\u5934\u7eea\uff0c\u4e00\u4e2a\u68a6\u5e7b\u4e4b\u5200\uff0c\u5c31\u53eb\u4eba\u5934\u75bc\u4e07\u5206\u4e86\uff0c\u6740\u624b \u4e4b\u738b\u65b9\u8f6e\uff0c\u4e5f\u6765\u6405\u548c\uff0c\u771f\u662f\u4e00\u6ce2\u672a\u5e73\uff0c\u4e00\u6ce2\u53c8\u8d77\u3002\u201d \u3000\u3000\u8d3e\u82f1\u7b11\u9053\uff1a\u201c\u5175\u6765\u5c06\u6321\uff0c\u94c1\u5144\u4e0d\u7528\u5fe7\u5fc3\uff0c\u6740\u624b\u4e4b\u738b\u65b9\u8f6e\uff0c\u4e00\u4e2a\u8df3\u6881\u5c0f\u4e11\u5c14\uff0c\u4f55\u8db3\u9053\u54c9\uff01\u201d \u3000\u3000\u53e3\u6c14\u4e4b\u72c2\uff0c\u4f3c\u662f\u672a\u628a\u4e00\u4ee3\u6740\u624b\u4e4b\u738b\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u542c\u5f97\u4e00\u6014\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u8d3e\u82f1\u4e3a\u4eba\uff0c\u4e00\u5411\u8c28\u614e\uff0c\u4e0d\u80af\u903e\u8d8a\u5206\u5bf8\uff0c\u4eca\u65e5\u600e\u4f1a\u7a81\u7136 \u5730\u98de\u626c\u6d6e\u71e5\u8d77\u6765\uff0c\u53e3\u51fa\u72c2\u8a00\u4f24\u4eba\uff0c\u4f46\u5374\u6709\u4e00\u79cd\u5e72\u4e91\u7684\u8c6a\u6c14\uff0c\u4e0d\u77e5\u662f\u538b\u6291\u592a\u4e45\uff0c\u4e00\u65f6\u5931\u63a7\u7684\u72c2 \u653e\uff0c\u8fd8\u662f\u672c\u6027\u4e2d\u539f\u6709\u7684\u9738\u6c14\uff0c\u4e0d\u5c0f\u5fc3\u6d41\u9732\u4e86\u51fa\u6765\u3002\u201d \u3000\u3000\u8001\u9053\u58eb\u4fee\u517b\u6df1\u539a\uff0c\u8fd9\u6837\u4e00\u60f3\uff0c\u53cd\u89c9\u4e86\u8fd9\u4e2a\u4ea4\u5f80\u6570\u5e74\u7684\u670b\u53cb\uff0c\u7adf\u7136\u662f\u5341\u5206\u964c\u751f\u3002\u662f\u7684\uff0c\u9664 \u4e86\u77e5\u6653\u4ed6\u53eb\u8d3e\u82f1\u4e4b\u5916\uff0c\u4e0d\u77e5\u4ed6\u662f\u4f55\u65b9\u4eba\u6c0f\uff1f\u4e5f\u4e0d\u77e5\u4ed6\u7684\u5e08\u627f\u95e8\u6237\uff1f\u8fde\u4ed6\u7684\u6b66\u529f\u8def\u6570\u90fd\u641e\u4e0d\u6e05 \u695a\uff0c\u4ed6\u8eab\u6000\u5357\u8352\u7edd\u6280\u201c\u8774\u8776\u7a7f\u82b1\u6b65\u201d\uff0c\u8fd8\u662f\u5728\u65e0\u610f\u4e2d\u770b\u5230\u4ed6\u5c55\u73b0\u51fa\u6765\u3002 \u3000\u3000\u5982\u82e5\u4ed6\u4e00\u76f4\u6291\u5236\u7740\u539f\u5177\u6709\u7684\u9738\u6c14\uff0c\u5fcd\u60c5\u8010\u6027\uff0c\u5728\u91d1\u5e9c\u4e2d\u4f5c\u4e2a\u603b\u7ba1\uff0c\u76ee\u7684\u4f55\u5728\u5462\uff1f\u91d1\u767e\u5e74 \u5bf9\u4ed6\u53c8\u4e86\u89e3\u591a\u5c11\uff1f \u3000\u3000\u8054\u60f3\u8d8a\u591a\uff0c\u8d8a\u89c9\u5f97\u6709\u70b9\u67e5\u660e\u6765\u9f99\u53bb\u8109\u7684\u5fc5\u8981\u3002 \u3000\u3000\u4f46\u5929\u67ab\u9605\u5386\u5e7f\u535a\uff0c\u5fc3\u4e2d\u7ffb\u4e91\u8986\u96e8\uff0c\u8868\u9762\u4e0a\u5374\u88c5\u51fa\u4e00\u7247\u5e73\u9759\uff0c\u58f0\u8272\u4e0d\u52a8\uff0c\u5fae\u7b11\u4e0d\u8a00\u3002 \u3000\u3000\u94c1\u7fce\u5c31\u672a\u60f3\u5230\u8fd9\u4e48\u591a\u4e86\uff0c\u672c\u6765\u5c31\u548c\u8d3e\u82f1\u4e0d\u592a\u719f\uff0c\u53ea\u89c9\u8fd9\u4e2a\u4eba\u8eab\u6000\u7edd\u6280\uff0c\u6709\u4e9b\u96be\u6d4b\u9ad8\u6df1\uff0c \u4f46\u8f7b\u85d0\u6740\u624b\u4e4b\u738b\uff0c\u662f\u5f88\u5927\u4e00\u4e2a\u9519\u8bef\uff0c\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u8840\u624b\u65b9\u8f6e\u7684\u53ef\u6016\u4e4b\u5904\uff0c\u9664\u4e86\u6b66\u529f\u4e4b\u5916\uff0c\u662f \u4ed6\u7684\u6740\u4eba\u5e03\u5c40\uff0c\u4f20\u8bf4\u4ed6\u4e0d\u505c\u5730\u6697\u4e2d\u8bad\u7ec3\u6740\u624b\uff0c\u6240\u4ee5\uff0c\u4eba\u624b\u5145\u6c9b\uff0c\u4e00\u4e2a\u5168\u80fd\u6740\u624b\uff0c\u6781\u96be\u57f9\u517b\uff0c \u9664\u4e86\u4e60\u6b66\u7684\u5929\u4efd\u3001\u8d44\u8d28\u4e4b\u5916\uff0c\u8fd8\u8981\u6709\u51b7\u9177\u7684\u751f\u6027\uff0c\u6076\u6bd2\u7684\u5fc3\u80a0\uff0c\u4f46\u65b9\u8f6e\u5374\u628a\u8fd9\u4e9b\u6761\u4ef6\uff0c\u5206\u914d \u5728\u591a\u4eba\u7684\u8eab\u4e0a\uff0c\u628a\u591a\u4eba\u7684\u4f18\u52bf\u5408\u4e00\uff0c\u5728\u5e03\u5c40\u4e2d\u53d1\u6325\u51fa\u6765\uff0c\u6240\u4ee5\uff0c\u4ed6\u51b3\u5fc3\u8981\u6740\u7684\u4eba\uff0c\u5f88\u96be\u9003 \u8fc7\u3002\u201d \u3000\u3000\u8d3e\u82f1\u4f3c\u662f\u5df2\u611f\u89c9\u5230\u60c5\u7eea\u5931\u63a7\uff0c\u4e0d\u518d\u4f5c\u4efb\u4f55\u4e89\u8bba\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u94c1\u5144\u8bf4\u7684\u662f\uff0c\u6740\u624b\u4e4b\u738b\uff0c \u540d\u6ee1\u6c5f\u6e56\uff0c\u5c82\u662f\u6613\u4e0e\u4e4b\u8f88\uff0c\u4e0d\u80fd\u8f7b\u89c6\u4ed6\uff0c\u8d3e\u67d0\u6709\u70b9\u6d6e\u71e5\u4e86\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u5fc3\u9053\uff1a\u5f2f\u5b50\u8f6c\u7684\u597d\u5feb\uff0c\u679c\u7136\u662f\u4e2a\u80fd\u5c48\u80fd\u4f38\u7684\u5927\u4eba\u7269\u3002 \u3000\u3000\u201c\u9053\u957f\uff0c\u8840\u624b\u65b9\u8f6e\u4ee5\u6740\u4eba\u4e3a\u4e1a\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u7275\u5165\u4e86\u65b0\u623f\u547d\u6848\u4e4b\u4e2d\u5462\uff1f\u4ed6\u4ee5\u57cb\u4f0f\u3001 \u5e03\u5c40\u89c1\u957f\uff0c\u6709\u4eba\u80af\u51fa\u5927\u4ef7\u94b1\uff0c\u4ed6\u662f\u5426\u53ef\u80fd\u63a5\u4e0b\u8fd9\u7968\u751f\u610f\u2026\u2026\u201d \u3000\u3000\u201c\u95ee\u9898\u662f\u4ec0\u4e48\u4eba\u8981\u6740\u9a6c\u516c\u5b50\uff0c\u7528\u5fc3\u4f55\u5728\uff1f\u201d\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u5982\u4f55\u80fd\u6df7\u5165\u7d27\u95ed\u7684\u5bc6\u5ba4\u4e2d\uff0c \u4e0d\u7559\u75d5\u8ff9\uff0c\u67e5\u660e\u8fd9\u4e24\u70b9\uff0c\u7834\u6848\u5c31\u6613\u5982\u53cd\u638c\u4e86\u3002\u201d \u3000\u3000\u94c1\u7fce\u7b11\u9053\uff1a\u201c\u7a81\u7834\u8fd9\u4e24\u70b9\u56f0\u96be\uff0c\u6848\u5b50\u5c31\u7b97\u7834\u4e86\uff0c\u9a6c\u5c06\u519b\u6218\u529f\u5f6a\u70b3\uff0c\u6740\u4e86\u4e0d\u5c11\u6e56\u532a\uff0c\u4ec7\u4eba \u662f\u6709\uff0c\u4f46\u5982\u8981\u7b56\u5212\u51fa\u5982\u6b64\u4e00\u6869\u5947\u6848\uff0c\u90a3\u4e9b\u6e56\u532a\u3001\u8349\u83bd\u8fd8\u96be\u6709\u8fd9\u4efd\u624d\u6167\u3001\u5fc3\u673a\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\uff01\u8fd9\u4ef6\u6848\u5b50\u662f\u4e00\u77f3\u4e8c\u9e1f\u4e4b\u8ba1\u3002\u201d\u5929\u67ab\u9053\u957f\u8bf4\u9053\uff1a\u201c\u9664\u4e86\u6740\u5bb3\u9a6c\u516c\u5b50\u4e4b\u5916\uff0c\u8fd8\u8981\u5ac1\u7978 \u7ed9\u91d1\u767e\u5e74\uff0c\u9a6c\u63d0\u7763\u638c\u63e1\u4e86\u6570\u4e07\u6c34\u5e08\u7cbe\u9510\uff0c\u4e00\u65e6\u548c\u91d1\u767e\u5e74\u5c55\u5f00\u706b\u62fc\uff0c\u90a3\u8fd8\u5f97\u4e86\u3002\u201d \u3000\u3000\u201c\u5173\u952e\u5728\u6848\u5b50\u96be\u7834\uff0c\u9a6c\u63d0\u7763\u80fd\u5728\u7bad\u5728\u5f26\u4e0a\u65f6\uff0c\u53ca\u65f6\u6536\u624b\u64a4\u8d70\u5175\u2026\u2026\u201d\u94c1\u7fce\u9053\uff1a\u201c\u90a3\u8bc1\u660e \u4e86\u4ed6\u662f\u4e2a\u5f88\u6709\u667a\u8c0b\u7684\u5c06\u519b\uff0c\u4e0d\u662f\u4e00\u4e2a\u7ea0\u7ea0\u6b66\u592b\uff0c\u53ea\u8981\u80fd\u63ed\u7834\u51f6\u6848\u8fc7\u7a0b\uff0c\u63d0\u7763\u4e00\u5b9a\u80fd\u63a5\u53d7\u8fd9\u4e2a \u4e8b\u5b9e\u3002\u201d \u3000\u3000\u201c\u94c1\u5144\uff0c\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u8d3e\u67d0\u6709\u4e00\u5f97\u4e4b\u611a\uff0c\u4e0d\u77e5\u53ef\u5426\u8bf4\u51fa\u6765\u4ee5\u4f9b\u53c2\u914c\uff1f\u201d \u3000\u3000\u201c\u8d3e\u5144\u8bf7\u8bf4\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u5144\u5f1f\u662f\u606d\u8046\u6559\u76ca\u3002\u201d\u8a00\u8bed\u95ee\uff0c\u5341\u5206\u606d\u656c\u3002 \u3000\u3000\u201c\u5929\u67ab\u9053\u957f\u770b\u51fa\u662f\u4e00\u77f3\u4e8c\u9e1f\u4e4b\u8ba1\uff0c\u975e\u5e38\u9ad8\u660e\uff0c\u6740\u9a6c\u516c\u5b50\u4ee5\u6cc4\u5fc3\u4e2d\u4e4b\u6124\uff0c\u53c8\u6311\u8d77\u9a6c\u3001\u91d1\u4e24 \u5bb6\u51b2\u7a81\uff0c\u5f15\u8d77\u6df7\u4e71\uff0c\u4fbf\u4e8e\u706b\u4e2d\u53d6\u7c9f\uff0c\u5750\u6536\u6e14\u5229\u3002\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u4f46\u9053\u957f\u5fd8\u4e86\u626c\u5dde\u5e9c\uff0c\u8d3e\u82f1\u7684\u770b \u6cd5\u662f\uff0c\u8fd9\u6761\u6076\u6bd2\u7684\u624b\u6cd5\uff0c\u662f\u4e00\u8ba1\u5bb3\u4e09\u8d24\uff0c\u9a6c\u63d0\u7763\uff0c\u655d\u4e1c\u4e3b\uff0c\u738b\u77e5\u5e9c\uff0c\u5168\u90fd\u5728\u8fd9\u4e00\u6848\u7684\u5751\u9677\u4e4b \u4e2d\u3002\u201d \u3000\u3000\u94c1\u7fce\u70b9\u70b9\u5934\u9053\uff1a\u201c\u9ad8\u89c1\uff0c\u9ad8\u89c1\uff0c\u738b\u77e5\u5e9c\u4e00\u80a9\u625b\u8d77\u4e86\u7834\u6848\u5927\u4efb\uff0c\u6848\u5b50\u7834\u4e0d\u4e86\uff0c\u9a6c\u63d0\u7763\u5c82\u80af \u5584\u7f62\u7518\u4f11\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u8fd9\u6848\u5b50\u8981\u591a\u65b9\u5e03\u7ebf\u67e5\u8bbf\uff0c\u4e0d\u8fc7\uff0c\u91cd\u70b9\u8fd8\u662f\u5728\u7f09\u51f6\u4e0a\u9762\u3002\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u6293\u51fa\u4e86\u4f5c \u6848\u51f6\u624b\uff0c\u89e3\u5f00\u5bc6\u5ba4\u6740\u4eba\u65b9\u6cd5\uff0c\u7275\u626f\u5728\u4e09\u4eba\u8eab\u4e0a\u7684\u6069\u6028\uff0c\u4e5f\u5c31\u4e0d\u96be\u8fce\u5203\u800c\u89e3\u4e86\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u4e5f\u4e0d\u5f97\u4e0d\u70b9\u5934\u4e86\uff0c\u5401\u53e3\u6c14\u9053\uff1a\u201c\u8d2b\u9053\u8fd9\u4e9b\u5e74\u89c2\u5bdf\u6240\u5f97\uff0c\u4e0d\u8bba\u662f\u8d29\u592b\u8d70\u5352\uff0c\u5c01\u7586 \u5927\u540f\uff0c\u6bcf\u4e2a\u4eba\u5fc3\u4e2d\u90fd\u9690\u6709\u4e00\u4e9b\u79c1\u4eba\u7684\u79d8\u5bc6\uff0c\u4eb2\u5982\u59bb\u5b50\u513f\u5973\uff0c\u4e5f\u4e0d\u613f\u6cc4\u6f0f\u51fa\u53bb\uff0c\u5927\u591a\u662f\u5c5e\u4e8e\u4e2a \u4eba\u9690\u79c1\uff0c\u5c0f\u6069\u5c0f\u6028\uff0c\u548c\u4e00\u4e24\u4e2a\u4eba\u7684\u540d\u8282\u6709\u5173\uff0c\u8fd9\u4e9b\u4e8b\u4e0d\u8bf4\u4e5f\u7f62\uff0c\u4eba\u751f\u6570\u5341\u5e74\uff0c\u8c01\u80fd\u65e0\u8fc7\u5462\uff1f \u4f46\u5982\u6068\u5230\u6740\u4eba\u6cc4\u6124\uff0c\u5c31\u4e0d\u662f\u4e00\u822c\u7684\u5c0f\u6069\u5c0f\u6028\u4e86\u3002\u5904\u5fc3\u79ef\u8651\uff0c\u82e6\u5fc3\u7b56\u753b\uff0c\u9634\u8c0b\u5e03\u5c40\uff0c\u66f4\u662f\u6709\u7740 \u91cd\u5927\u7684\u76ee\u7684\uff01\u773c\u4e0b\u54b1\u4eec\u5728\u4f5c\u7f09\u5492\u7684\u5de5\u4f5c\uff0c\u5982\u80fd\u6293\u5230\u68a6\u5e7b\u4e4b\u5200\uff0c\u7834\u4e86\u8fd9\u4ef6\u5927\u6848\u4e0d\u7b97\uff0c\u63ed\u7a7f\u68a6\u5e7b \u4e4b\u5200\u7684\u6740\u4eba\u4e4b\u79d8\uff0c\u66f4\u662f\u4e00\u4ef6\u9707\u52a8\u5168\u56fd\u3001\u8f70\u60ca\u6c5f\u6e56\u7684\u5927\u4e8b\uff0c\u5e0c\u671b\u6211\u4eec\u8fd9\u756a\u8bbe\u9975\u9493\u9c7c\u7684\u8ba1\u5212\uff0c\u80fd \u591f\u5f15\u6765\u9b3c\u5200\u3002\u201d \u3000\u3000\u201c\u9053\u957f\uff0c\u626c\u5dde\u57ce\u4e2d\u8fd1\u65e5\u805a\u96c6\u4e86\u4e0d\u5c11\u6c5f\u6e56\u4eba\u7269\uff0c\u660e\u663e\u7684\u662f\u6709\u4e3a\u800c\u6765\uff01\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u662f\u4e0d\u662f \u548c\u4e1c\u4e3b\u7684\u4e09\u5b9d\u6709\u5173\u5462\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u8d2b\u9053\u8fd1\u6765\u65e5\u67e5\u591c\u8bbf\uff0c\u4e5f\u53d1\u89c9\u4e86\u8fd9\u4e2a\u4e8b\u5b9e\uff0c\u4f46\u91d1\u5458\u5916\u6536\u85cf\u4e09\u5b9d\uff0c\u5df2\u6709\u6570\u5e74 \u4e4b\u4e45\uff0c\u6d88\u606f\u5916\u6cc4\uff0c\u6050\u4e5f\u975e\u4eca\u65e5\u59cb\u2026\u2026\u201d \u3000\u3000\u201c\u9053\u957f\uff0c\u77e5\u9053\u7684\u4eba\u4e0d\u591a\u5440\uff0c\u4ee5\u94c1\u67d0\u4e3a\u4f8b\uff0c\u5982\u975e\u8fd9\u4ef6\u65b0\u623f\u547d\u6848\uff0c\u6050\u6015\u5230\u73b0\u5728\uff0c\u626c\u5dde\u5e9c\u8fd8\u4e0d \u4f1a\u77e5\u9053\u91d1\u5458\u5916\u6536\u5b58\u4e86\u9c7c\u80a0\u5251\u8fd9\u7b49\u73cd\u8d35\u7684\u5b9d\u5200\u3002\u201d \u3000\u3000\u201c\u6c5f\u6e56\u9053\u4e0a\u8baf\u606f\u6765\u6e90\u548c\u5b98\u5e9c\u4e0d\u540c\uff0c\u201d\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4ed6\u4eec\u5343\u65b9\u767e\u8ba1\uff0c\u641c\u8d2d\u8fd9\u4e9b\u8baf\u606f\u2026\u2026\u201d \u3000\u3000\u94c1\u7fce\u6447\u5934\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\uff0c\u6211\u4e0d\u82df\u540c\uff0c\u94c1\u67d0\u672a\u53d7\u8058\u4e3a\u626c\u5dde\u7684\u603b\u6355\u5934\u65f6\uff0c\u4e5f\u5e38\u5728\u5e7f\u9675\u4e00 \u5e26\u8d70\u52a8\uff0c\u4f46\u5c31\u6ca1\u6709\u542c\u5230\u8fc7\u4e09\u5b9d\u7684\u4f20\u8bf4\u3002\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u8d3e\u67d0\u4efb\u91d1\u5e9c\u603b\u7ba1\uff0c\u5df2\u5c06\u8fd1\u6570\u5e74\u4e4b\u4e45\uff0c\u4e5f\u672a\u542c\u8fc7\u4e09\u5b9d\u7684\u4e8b\uff0c\u68a6\u5e7b\u4e4b\u5200\u53c8\u600e\u4f1a\u77e5 \u8fd9\u4e2a\u6d88\u606f\u5462\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u76b1\u76b1\u7709\u5934\uff0c\u4f46\u5374\u95ed\u4e0a\u4e86\u5634\u5df4\uff01\u4ed6\u662f\u91d1\u767e\u5e74\u4ea4\u5f80\u6570\u5341\u5e74\u7684\u8001\u670b\u53cb\uff0c\u53c8\u6781\u53d7\u91d1\u767e\u5e74 \u7684\u91cd\u89c6\uff0c\u5bf9\u4e09\u5b9d\u7684\u6765\u9f99\u53bb\u8109\uff0c\u5e94\u8be5\u6709\u4e2a\u900f\u5f7b\u7684\u4e86\u89e3\uff0c\u4f46\u4ed6\u4e0d\u80af\u518d\u5f00\u53e3\uff0c\u522b\u4eba\u4e5f\u4e0d\u4fbf\u8ffd\u95ee\u3002 \u3000\u3000\u94c1\u7fce\u672c\u60f3\u8bf4\u51fa\u56db\u51e4\u697c\u7684\u4e8b\uff0c\u4f46\u8bdd\u5c06\u51fa\u53e3\u65f6\u53c8\u541e\u4e86\u4e0b\u53bb\uff0c\u8981\u8bf4\u5c31\u5f97\u8bf4\u6e05\u695a\uff0c\u675c\u671b\u6708\u5230\u626c\u5dde \u7684\u4e8b\uff0c\u52bf\u96be\u9690\u7792\uff0c\u4e00\u65e6\u53e3\u6c14\u4e0d\u7a33\uff0c\u53ea\u6015\u8fde\u5211\u90e8\u603b\u6355\u5934\u548c\u4e2d\u3001\u5357\u3001\u897f\u3001\u5317\u56db\u65b9\u540d\u6355\u4f1a\u805a\u626c\u5dde\u7684 \u4e8b\uff0c\u90fd\u5f97\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u6d88\u606f\u4e00\u65e6\u6cc4\u9732\u51fa\u53bb\uff0c\u5fc5\u5c06\u5f15\u8d77\u4f1a\u96c6\u5728\u626c\u5dde\u5730\u9762\u4e0a\u6c5f\u6e56\u4eba\u7269\u7684\u8b66\u60d5\uff0c\u4ed6\u4eec\u4f1a\u9690\u85cf\u5f97\u66f4\u5bc6\uff0c \u4e5f\u4f1a\u8bbe\u8ba1\u51fa\u5404\u79cd\u9677\u9631\uff0c\u6765\u5bf9\u4ed8\u5211\u90e8\u603b\u6355\u5934\u548c\u4e2d\u3001\u5357\u3001\u897f\u3001\u5317\uff0c\u56db\u65b9\u540d\u6355\u3002 \u3000\u3000\u660e\u67aa\u6613\u8eb2\uff0c\u6697\u7bad\u96be\u9632\uff0c\u8fd9\u4e9b\u4eba\u4e0d\u8bba\u662f\u54ea\u4e00\u4e2a\uff0c\u56e0\u4ed6\u6cc4\u6f0f\u6d88\u606f\uff0c\u800c\u53d7\u5230\u4f24\u5bb3\uff0c\u90fd\u5c06\u662f\u4ed6\u7ec8 \u8eab\u96be\u5b89\u7684\u4e8b\uff0c\u5c31\u7b97\u8bf4\u51fa\u53bb\u6709\u52a9\u4e8e\u7834\u6848\u7684\u8fdb\u884c\uff0c\u4e5f\u4e0d\u80fd\u8bf4\uff0c\u7acb\u523b\u6539\u53d8\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u8d3e\u5144\uff0c\u54b1\u4eec \u8981\u5982\u4f55\u884c\u52a8\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\u4efb\u4f55\u884c\u52a8\uff0c\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u6211\u4eec\u5c31\u5750\u5728\u6b64\u5904\uff0c\u54c1\u8317\u3002\u4e0d\u8fc7\uff0c\u4e3a\u4e86\u591c\u95f4\u884c\u52a8\u65b9\u4fbf\uff0c\u4ee5 \u514d\u8bef\u4f24\u540c\u4f34\uff0c\u6709\u4e00\u4ef6\u6807\u5e1c\uff0c\u8bf7\u4f69\u4e8e\u5de6\u81c2\u4e4b\u4e0a\uff0c\u5343\u4e07\u8bb0\u7740\uff0c\u4e00\u5b9a\u8981\u6234\u5728\u5de6\u81c2\u4e0a\u3002\u201d \u3000\u3000\u53d6\u51fa\u4e24\u6761\u767d\u8272\u4e1d\u5e26\uff0c\u7f20\u5728\u94c1\u7fce\u548c\u5929\u67ab\u9053\u4eba\u7684\u5de6\u81c2\u8863\u8896\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u6211\u4eec\u7684\u4eba\uff0c\u90fd\u5728\u5de6\u81c2 \u4e0a\u4f69\u6709\u8fd9\u6837\u7684\u4e1d\u5e26\uff0c\u4e24\u4f4d\u7a0d\u4e3a\u7559\u5fc3\u4e00\u4e9b\uff0c\u5f88\u6613\u5206\u8fa8\u654c\u6211\u3002\u8d3e\u67d0\u6682\u65f6\u544a\u9000\u4e00\u4e0b\uff0c\u6211\u53bb\u67e5\u770b\u57cb\u4f0f \u4e0a\u662f\u5426\u8fd8\u6709\u9057\u6f0f\u7684\u5730\u65b9\uff0c\u4e0d\u8bba\u5bf9\u65b9\u662f\u5426\u4f1a\u6765\uff0c\u9700\u8981\u4e24\u4f4d\u5e2e\u52a9\u65f6\uff0c\u81ea\u6709\u4eba\u6765\u62db\u547c\u4e24\u4f4d\u3002\u201d\u8bf4\u5b8c\uff0c \u4e00\u95ea\u8eab\uff0c\u6d88\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\u5feb\u7684\u8eab\u6cd5\uff0c\u53bb\u5982\u95ea\u7535\u98d8\u98ce\uff0c\u8d2b\u9053\u4e5f\u96be\u53ca\u5f97\u3002\u201d\u867d\u662f\u4e00\u53e5\u63a8\u5d07 \u7684\u8bdd\uff0c\u4f46\u5374\u53c8\u4f3c\u5728\u6697\u793a\u4e00\u4e9b\u4ec0\u4e48\u3002\u8fd9\u5ea7\u96c5\u5ba4\u4e2d\u53ea\u6709\u94c1\u7fce\u4e00\u4eba\uff0c\u5f53\u7136\u662f\u8bf4\u7ed9\u94c1\u7fce\u542c\u4e86\u3002 \u3000\u3000\u6c89\u541f\u4e86\u597d\u4e00\u9635\u5de5\u592b\uff0c\u94c1\u7fce\u624d\u5f00\u53e3\u8bf4\u9053\uff1a\u201c\u4e00\u79cd\u6781\u9ad8\u660e\u7684\u95ea\u8f6c\u8eab\u6cd5\uff0c\u5f53\u7136\u9700\u8981\u8f7b\u529f\u9020\u8be3\u914d \u5408\uff0c\u94c1\u67d0\u4e5f\u662f\u671b\u5c18\u83ab\u53ca\u4e86\u3002\u201d \u3000\u3000\u201c\u66ff\u4e24\u4f4d\u9001\u8336\u6765\u4e86\u3002\u201d\u58f0\u97f3\u5f88\u6e05\u695a\uff0c\u4f46\u5374\u5f88\u4f4e\u6c89\u3002 \u3000\u3000\u4e00\u6761\u4eba\u5f71\uff0c\u95ea\u5165\u5ba4\u4e2d\uff0c\u624b\u4e2d\u6367\u7740\u8336\u76d8\uff0c\u4e0a\u9762\u653e\u4e86\u4e00\u58f6\u8336\u548c\u4e09\u4e2a\u74f7\u676f\uff0c\u6765\u4eba\u7684\u5de6\u81c2\u4e0a\uff0c\u4e5f \u7f20\u4e86\u4e00\u6761\u767d\u8272\u7684\u4e1d\u5e26\u3002 \u3000\u3000\u6b64\u523b\uff0c\u591c\u5e55\u4f4e\u5782\uff0c\u5e73\u5e38\u65f6\u65e5\uff0c\u4e00\u5165\u591c\uff0c\u91d1\u5e9c\u4e2d\u706f\u706b\u8f89\u714c\uff0c\u8000\u5982\u767d\u663c\uff0c\u4eca\u591c\u5374\u5927\u53cd\u5e38\u6001\uff0c \u591c\u5e55\u4e0b\u4e0d\u89c1\u706f\u706b\uff0c\u4e00\u7247\u5e7d\u5bc2\uff0c\u5ba4\u4e2d\u66f4\u662f\u9ed1\u6697\uff0c\u5929\u67ab\u3001\u94c1\u7fce\u76ee\u529b\u5145\u6c9b\uff0c\u4e14\u5df2\u9002\u5e94\u9ed1\u6697\uff0c\u624d\u80fd\u770b \u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u4f46\u90a3\u732e\u8336\u7684\u9ed1\u8863\u4eba\uff0c\u4f3c\u4e5f\u6709\u591c\u95f4\u89c6\u7269\u4e4b\u80fd\uff0c\u884c\u52a8\u5b8c\u5168\u4e0d\u53d7\u9ed1\u6697\u7684\u5f71\u54cd\uff0c\u8fc5\u5feb\u5730\u653e\u597d\u8336\u5177\uff0c \u9000\u51fa\u5ba4\u5916\uff0c\u5feb\u6b65\u79bb\u53bb\uff0c\u4f46\u5374\u8d70\u5f97\u542c\u4e0d\u5230\u4e00\u70b9\u58f0\u606f\u3002 \u3000\u3000\u94c1\u7fce\u6697\u6697\u5403\u4e86\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u6d41\u4eba\u7269\u7684\u8eab\u624b\uff0c\u4ed6\u5374\u662f\u4e2a\u4f8d\u5ba2\u5949\u8336\u7684\u6742\u5de5\uff0c\u8fd9\u91d1\u5e9c \u4e2d\u7684\u98df\u5ba2\uff0c\u4f3c\u662f\u4e2a\u4e2a\u4e0d\u80fd\u8f7b\u89c6\uff0c\u6ca1\u6709\u4e00\u70b9\u672c\u9886\u7684\u4eba\uff0c\u5f88\u96be\u5728\u8fd9\u91cc\u7acb\u8db3\u3002\u201d \u3000\u3000\u56de\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u5929\u67ab\u9053\u957f\u5fae\u95ed\u53cc\u76ee\uff0c\u4f3c\u662f\u6b63\u5728\u8c03\u606f\u517b\u795e\u3002 \u3000\u3000\u94c1\u7fce\u5fc3\u4e2d\u6709\u5f88\u591a\u7591\u95ee\uff0c\u4f46\u5374\u95ee\u4e0d\u51fa\u53e3\uff0c\u5929\u67ab\u9053\u957f\u548c\u4ed6\u662f\u591a\u5e74\u597d\u53cb\uff0c\u4f46\u5929\u67ab\u9053\u957f\u7684\u5fc3\u4e2d\uff0c \u5374\u6709\u81ea\u5df1\u4e3b\u89c2\u7684\u8ba4\u77e5\u3002\u76ee\u524d\u7684\u60c5\u51b5\uff0c\u4e0d\u80fd\u81ea\u4f5c\u4e3b\u5f20\uff0c\u5fc5\u987b\u4f9d\u7167\u91d1\u767e\u5e74\u5e03\u7f6e\u884c\u52a8\u3002\u4f46\u68a6\u5e7b\u4e4b\u5200 \u4e5f\u7f62\uff0c\u6740\u624b\u4e4b\u738b\u4e5f\u597d\uff0c\u4ed6\u4eec\u90fd\u662f\u5f53\u524d\u6cbb\u5b89\u4e0a\u7684\u6bd2\u7624\uff0c\u6267\u6cd5\u8005\u7f09\u6355\u7684\u5bf9\u8c61\uff0c\u8fd9\u4e2a\u5927\u76ee\u6807\u65e2\u7136\u4e00 \u81f4\uff0c\u5176\u4ed6\u7684\u7ec6\u8282\u4e5f\u5c31\u4e0d\u7528\u8ba1\u8f83\u4e86\uff0c\u4f55\u4e0d\u501f\u673a\u5c0f\u7761\u4e00\u9635\u3002\u4ed6\u7acb\u523b\u95ed\u4e0a\u53cc\u76ee\uff0c\u6392\u9664\u5fc3\u4e2d\u6742\u5ff5\uff0c\u7761 \u5c06\u8d77\u6765\u3002 \u3000\u3000\u6709\u5929\u67ab\u9053\u957f\u8fd9\u7b49\u9ad8\u4eba\u5728\u4fa7\uff0c\u7528\u4e0d\u7740\u62c5\u5fc3\u6709\u4eba\u6697\u88ad\u3002 \u3000\u3000\u8fd9\u4e00\u7761\u7adf\u7136\u7761\u4e86\u4e09\u4e2a\u66f4\u6b21\u4e4b\u4e45\u3002\u9192\u8fc7\u6765\uff0c\u5929\u5df2\u9ece\u660e\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u5df2\u4e0d\u5728\u77f3\u5ba4\u4e2d\u4e86\uff0c\u8d3e\u82f1\u5374\u65e9\u5df3\u5019\u5728\u8eab\u4fa7\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u94c1\u5144\u7761\u5f97\u5f88\u719f\uff0c\u5144\u5f1f \u4e0d\u6562\u60ca\u6270\uff0c\u59d4\u5c48\u94c1\u5144\u4e86\uff01\u201d \u3000\u3000\u201c\u9053\u957f\u5728\u4fa7\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u5144\u5f1f\u5fc3\u795e\u677e\u61c8\uff0c\u5f88\u719f\u5730\u7761\u4e86\u4e00\u89c9\uff0c\u6628\u591c\u53ef\u6709\u4ec0\u4e48\u53d8\u5316\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e00\u591c\u5e73\u5b89\uff0c\u770b\u6837\u5b50\u8981\u5c48\u7559\u5927\u9a7e\u4e00\u4e9b\u65f6\u65e5\u4e86\u3002\u201d \u3000\u3000\u94c1\u7fce\u5fc3\u4e2d\u6302\u5ff5\u738b\u77e5\u5e9c\u7684\u5b89\u5371\uff0c\u6025\u4e8e\u5f52\u53bb\uff0c\u987a\u4fbf\u548c\u675c\u671b\u6708\u7814\u5546\u4e00\u4e0b\u6848\u60c5\uff0c\u63a2\u8be2\u5404\u65b9\u540d\u6355\u662f \u5426\u5df2\u5230\u626c\u5dde\u7684\u6d88\u606f\u3002\u5401\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u53ef\u5426\u5148\u56de\u8859\u95e8\u4e2d\u53bb\uff0c\u53ea\u8981\u8d3e\u5144\u4e00\u7eb8\u76f8\u53ec\uff0c\u5144\u5f1f\u7acb\u523b \u5e26\u4eba\u624b\u8d76\u6765\u6548\u547d\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u201d\u8d3e\u82f1\u6709\u4e9b\u4e3a\u96be\u5730\u9053\uff1a\u201c\u655d\u4e1c\u4e3b\u544a\u8bc9\u6211\uff0c\u4ed6\u548c\u738b\u5927\u4eba\u5df2\u6709\u534f\u8bae\uff0c\u8981\u94c1\u5144\u7559\u5728\u8fd9 \u91cc\uff0c\u4e00\u76f4\u5230\u68a6\u5e7b\u4e4b\u5200\u51fa\u73b0\u4e3a\u6b62\uff0c\u5c31\u7b97\u56f4\u6355\u4e0d\u6210\uff0c\u8fd9\u6b21\u57cb\u4f0f\u624d\u7b97\u7ed3\u675f\u3002\u90a3\u65f6\uff0c\u94c1\u5144\u518d\u79bb\u53bb\u4e0d\u8fdf\uff0c \u81f3\u4e8e\u5e26\u4eba\u6765\u6b64\u52a9\u6218\uff0c\u90a3\u5c31\u4e0d\u7528\u7740\u4e86\u3002\u201d \u3000\u3000\u94c1\u7fce\u63a5\u9053\uff1a\u201c\u5982\u662f\u68a6\u5e7b\u4e4b\u5200\uff0c\u4e09\u5bb5\u4e0d\u6765\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u4eec\u5c31\u7b49\u5019\u4e09\u5bb5\uff0c\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u94c1\u5144\uff0c\u68a6\u5e7b\u4e4b\u5200\uff0c\u662f\u4e00\u5934\u6210\u7cbe\u7684\u8001\u72d0\u72f8\uff0c\u4ed6\u7684\u6c89\u7740\uff0c \u4e0d\u662f\u5e38\u4eba\u80fd\u53ca\u3002\u201d \u3000\u3000\u201c\u8d3e\u5144\uff0c\u201d\u94c1\u7fce\u63a5\u53e3\u9053\uff1a\u201c\u8fd9\u4e5f\u4e0d\u80fd\u8981\u5144\u5f1f\u505c\u4e0b\u4e00\u5207\u5de5\u4f5c\uff0c\u4e00\u76f4\u7559\u5728\u8fd9\u91cc\u7b49\u5019\u68a6\u5e7b\u4e4b\u5200\uff0c \u8fd9\u6863\u4e8b\uff0c\u5e94\u8be5\u6709\u4e2a\u9650\u671f\u624d\u662f\u3002\u201d \u3000\u3000\u201c\u662f\u6709\u9650\u671f\uff0c\u655d\u4e1c\u4e3b\u7684\u4f30\u7b97\u662f\u4e03\u4e2a\u665a\u4e0a\uff0c\u6211\u4eec\u6709\u5f88\u591a\u63aa\u65bd\uff0c\u914d\u5408\u4e03\u65e5\u4e4b\u671f\uff0c\u9664\u975e\u68a6\u5e7b\u4e4b \u5200\u672a\u5230\u626c\u5dde\u2026\u2026\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u4ed6\u6765\u4e86\uff0c\u5c31\u5f88\u96be\u907f\u5f00\u8fd9\u4e9b\u8bf1\u60d1\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e9b\u9690\u79d8\u4e0d\u80fd\u5916\u6cc4\uff0c\u8fd8 \u8bf7\u94c1\u5144\u8d4f\u7ed9\u655d\u67ec\u4e3b\u548c\u8d3e\u67d0\u4e00\u4e2a\u8138\u9762\uff0c\u7559\u8fc7\u4e03\u65e5\u518d\u8d70\uff01\u201d \u3000\u3000\u94c1\u7fce\u5fc3\u5934\u5f88\u706b\uff0c\u6697\u81ea\u5fd6\u9053\uff1a\u4ec0\u4e48\u6837\u7684\u9690\u79d8\uff0c\u94c1\u67d0\u5168\u65e0\u5934\u7eea\uff0c\u4e0d\u77e5\u9053\u7684\u4e8b\uff0c\u5982\u4f55\u6cc4\u6f0f\uff1f\u91d1 \u767e\u5e74\u53ea\u4e0d\u8fc7\u4e00\u4ecb\u5e73\u6c11\uff0c\u4f46\u6c14\u52bf\u51cc\u4eba\uff0c\u9887\u6709\u5f3a\u884c\u7559\u5ba2\u7684\u5473\u9053\uff0c\u5bf9\u6211\u8fd9\u4e2a\u626c\u5dde\u5e9c\u4e2d\u603b\u6355\u5934\uff0c\u4f3c\u662f \u5168\u4e0d\u653e\u5728\u5fc3\u4e0a\u4e86\uff0c\u771f\u662f\u8d22\u5927\u6c14\u7c97\uff01 \u3000\u3000\u201c\u94c1\u5144\uff0c\u201d\u8d3e\u82f1\u4f3c\u5df2\u77a7\u51fa\u4e86\u94c1\u7fce\u7684\u4e0d\u60a6\u4e4b\u8272\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u77e5\u5e9c\u5927\u4eba\u7684\u51b3\u5b9a\uff0c\u655d\u4e1c\u4e3b\u5982 \u4f55\u6562\u51b3\u5b9a\u94c1\u5144\u7684\u884c\u7a0b\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\uff0c\u53d6\u51fa\u4e00\u5e45\u56fe\u6848\uff0c\u63a5\u9053\uff1a\u201c\u94c1\u5144\u7559\u6b64\u7684\u610f\u4e49\u5341\u5206\u91cd\u5927\uff0c\u5144\u5f1f\u4e5f\u60f3\u4ecb\u4e8e\u91cd\u4efb\u2026\u2026\u201d \u3000\u3000\u4e00\u4e2a\u9752\u8863\u5973\u5a62\uff0c\u7a81\u7136\u884c\u4e86\u8fdb\u6765\uff0c\u624b\u4e2d\u6367\u7740\u4e00\u652f\u5927\u7ea2\u814a\u70db\uff0c\u653e\u5728\u6728\u6848\u4e0a\uff0c\u70b9\u71c3\u4e4b\u540e\uff0c\u6084\u7136 \u800c\u9000\u3002 \u3000\u3000\u5929\u8272\u867d\u5df2\u653e\u4eae\uff0c\u4f46\u5ba4\u4e2d\u4ecd\u7136\u5e7d\u6697\uff0c\u4eae\u8d77\u706b\u70db\uff0c\u7acb\u523b\u4e00\u7247\u901a\u660e\u3002 \u3000\u3000\u8fd9\u91cc\u7684\u4e00\u5207\u4e3e\u63aa\uff0c\u65e0\u4e0d\u914d\u5408\u7684\u6070\u5230\u597d\u5904\uff0c\u7528\u4e0d\u7740\u518d\u51fa\u8a00\u62db\u547c\uff0c\u9700\u8981\u65f6\u603b\u4f1a\u53ca\u65f6\u800c\u81f3\u3002 \u3000\u3000\u8d3e\u82f1\u644a\u5f00\u624b\u4e2d\u56fe\u6848\uff0c\u63a5\u9053\uff1a\u201c\u5144\u5f1f\u60f3\u501f\u91cd\u94c1\u5144\u4e3a\u5357\u533a\u4e3b\u5e05\uff0c\u914d\u5c5e\u56db\u4f4d\u9ad8\u624b\u548c\u5341\u516d\u4e2a\u6b66\u58eb\uff0c \u91d1\u5bb6\u5b85\u9662\u4e00\u5171\u5206\u4e3a\u56db\u533a\uff0c\u6bcf\u533a\u4e00\u4f4d\u4e3b\u5e05\uff0c\u7531\u56db\u4f4d\u9ad8\u624b\u8f85\u52a9\uff0c\u5206\u56db\u533a\u4e8c\u5341\u4e2a\u6b66\u529f\u4e00\u6d41\u7684\u4eba\uff0c\u7ec4 \u6210\u5408\u51fb\u9635\u52bf\uff0c\u53e6\u6709\u4e00\u7ec4\u4eba\u624b\uff0c\u7ec4\u6210\u622a\u51fb\u5c0f\u7ec4\uff0c\u9a70\u63f4\u654c\u4eba\u73b0\u8eab\u533a\u57df\uff1b\u94c1\u5144\u539f\u4e3a\u622a\u51fb\u5c0f\u7ec4\u4e2d\u4eba\uff0c \u4f46\u5357\u533a\u4e3b\u5e05\uff0c\u5927\u75c5\u521d\u6108\uff0c\u4f53\u80fd\u5c1a\u672a\u5c3d\u590d\uff0c\u5144\u5f1f\u548c\u5929\u67ab\u9053\u957f\u5546\u91cf\uff0c\u94c1\u5144\u7684\u6280\u827a\uff0c\u8db3\u4ee5\u5f53\u6b64\u91cd\u4efb\uff0c \u6545\u800c\u6539\u501f\u94c1\u5144\u51fa\u4efb\u5357\u533a\u7684\u4e3b\u5e05\u3002\u201d \u3000\u3000\u201c\u7559\u94c1\u67d0\u4e03\u65e5\uff0c\u5c31\u662f\u4e3a\u8fd9\u4ef6\u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u5012\u4e0d\u5c3d\u7136\uff0c\u5144\u5f1f\u4f30\u7b97\u8fd9\u4e00\u6b21\u56f4\u6740\u5835\u622a\uff0c\u5fc5\u4f1a\u9020\u6210\u76f8\u5f53\u7684\u4f24\u4ea1\u3002\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u6709\u94c1\u5144\u5728 \u573a\u7763\u6218\uff0c\u5f53\u53ef\u51cf\u5c11\u7533\u62a5\u5b98\u5e9c\u7684\u9ebb\u70e6\uff0c\u662f\u6807\u51c6\u7684\u5b98\u5e9c\u4e2d\u4eba\u6293\u5f3a\u76d7\u4e86\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u94c1\u67d0\u4eba\u8fd8\u6709\u8fd9\u4e00\u5c42\u7528\u9014\uff0c\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u82e5\u68a6\u5e7b\u4e4b\u5200\u4e03\u65e5\u4e4b\u5185\u8fd8\u4e0d\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5c31\u9038\u51fa\u7b97\u8ba1\u4e4b\u5916\u4e86\uff0c\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u4e0d\u6562\u518d\u591a\u7559\u94c1\u5144\u4e86\uff0c\u800c\u4e14\uff0c\u8d3e\u67d0\u7ea6\u8bf7\u7684\u51e0\u4f4d\u670b\u53cb\uff0c \u4e5f\u53ea\u80fd\u505c\u7559\u4e03\u65e5\uff0c\u7167\u6211\u4eec\u7684\u8ba1\u7b97\uff0c\u4e03\u65e5\u4e4b\u5185\uff0c\u4e00\u5b9a\u6709\u4eba\u6765\u76d7\u4e09\u5b9d\uff0c\u662f\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\uff0c\u5c31\u4e0d\u6562 \u65ad\u8a00\u4e86\u3002\u201d \u3000\u3000\u201c\u94c1\u67d0\u4e03\u65e5\u4e4b\u4e45\uff0c\u4e0d\u5728\u5e9c\u8859\u51fa\u73b0\uff0c\u662f\u5426\u662f\u4e00\u4e2a\u7834\u7efd\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\uff0c\u94c1\u5144\u8bf7\u653e\u5bbd\u5fc3\uff0c\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u655d\u4e1c\u4e3b\u548c\u77e5\u5e9c\u5927\u4eba\u5546\u8bf7\u94c1\u5144\u8d76\u6765\u91d1\u5e9c\u64d2\u51f6\u65f6\uff0c \u5df2\u601d\u8651\u53ca\u6b64\uff0c\u4f1a\u6709\u4e00\u4f4d\u66ff\u8eab\uff0c\u4ee3\u94c1\u5144\u5728\u5e9c\u8859\u73b0\u8eab\u3002\u201d \u3000\u3000\u94c1\u7fce\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u7b97\u8ba1\u5012\u662f\u7cbe\u5bc6\uff0c\u73b0\u5728\uff0c\u53ea\u5269\u4e00\u4ef6\u4e8b\uff0c\u8ba9\u94c1\u67d0\u653e\u5fc3\u4e0d\u4e0b\u4e86\u3002\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u94c1\u5144\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u201c\u77e5\u5e9c\u5927\u4eba\u7684\u5b89\u5371\u3002\u4f55\u5927\u5149\u867d\u7136\u6280\u827a\u4e0d\u51e1\uff0c\u4f46\u53ea\u4ed6\u4e00\u4eba\uff0c\u5b9e\u529b\u6709\u4e9b\u5355\u8584\uff0c\u738b\u575a\u526f\u603b\u6355\u5934\uff0c \u4ee3\u6211\u5904\u7406\u8859\u95e8\u4e8b\u52a1\uff0c\u6050\u65e0\u6cd5\u5168\u529b\u4fdd\u62a4\u5927\u4eba\u2026\u2026\u201d\u94c1\u7fce\u9053\uff1a\u201c\u8d3e\u5144\uff0c\u5bf9\u6b64\u4e5f\u6709\u5b89\u6392\u4e48\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u9664\u4e86\u94c1\u5144\u548c\u738b\u526f\u603b\u6355\u4e4b\u5916\uff0c\u5e9c\u8859\u81f3\u5c11\u8fd8\u6709\u4e24\u4f4d\u4e00\u6d41\u9ad8\u624b\uff0c\u53ea\u4e0d \u8fc7\uff0c\u4ed6\u4eec\u7a7f\u7684\u662f\u4e00\u822c\u6355\u5934\u7684\u8863\u670d\uff0c\u505a\u7684\u4e00\u822c\u6355\u5feb\u7684\u4e8b\u60c5\uff0c\u4f46\u5982\u8bba\u4ed6\u4eec\u7684\u6280\u827a\u6210\u5c31\uff0c\u7edd\u4e0d\u5728\u4f55 \u5927\u5149\u548c\u738b\u575a\u4e4b\u4e0b\u2026\u2026\u201d \u3000\u3000\u94c1\u7fce\u5403\u60ca\u4e86\uff0c\u8fd9\u662f\u626c\u5dde\u5e9c\u4e2d\u6700\u5927\u7684\u79d8\u5bc6\uff0c\u53ea\u6709\u4ed6\u548c\u738b\u77e5\u5e9c\u77e5\u9053\uff0c\u518d\u65e0\u7b2c\u4e09\u4eba\u77e5\u6653\u5185\u60c5\uff0c \u4f46\u8d3e\u82f1\u5374\u4e00\u53e3\u8bf4\u4e86\u51fa\u6765\uff0c\u8fd9\u53eb\u94c1\u7fce\u5982\u4f55\u4e0d\u60ca\u5462\uff1f \u3000\u3000\u201c\u8d3e\u5144\uff0c\u4e0d\u8bba\u4f60\u5982\u4f55\u77e5\u6653\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u90fd\u4ee4\u94c1\u67d0\u9707\u60ca\u4e0d\u5df2\uff0c\u5982\u82e5\u8d3e\u5144\u4e0d\u80af\u660e\u8a00\u76f8\u544a\uff0c\u94c1\u67d0 \u771f\u662f\u5bdd\u98df\u96be\u5b89\u4e86\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u6709\u7740\u6d53\u539a\u8bf7\u6c42\u7684\u610f\u5473\uff0c\u4f46\u4e5f\u6709\u70b9\u5a01\u80c1\u7684\u610f\u601d\uff0c\u5bdd\u98df\u96be\u5b89\uff0c\u52bf\u5fc5\u8981\u5168\u529b\u8ffd\u67e5\u539f\u56e0\uff0c \u662f\u4e00\u79cd\u660e\u767d\u7684\u8868\u793a\uff0c\u4f60\u4e0d\u8bf4\uff0c\u6211\u8981\u8ffd\u67e5\u5230\u5e95\u4e86\u3002 \u3000\u3000\u8d3e\u82f1\u6709\u70b9\u540e\u6094\u591a\u8a00\uff0c\u8a00\u591a\u5fc5\u5931\u3002 \u3000\u3000\u94c1\u7fce\u7684\u6b66\u529f\u6280\u827a\uff0c\u672a\u653e\u5728\u8d3e\u82f1\u7684\u773c\u4e2d\uff0c\u4f46\u90a3\u626c\u5dde\u5e9c\u603b\u6355\u5934\u7684\u5b98\u65b9\u8eab\u4efd\uff0c\u5374\u4f7f\u8d3e\u82f1\u987e\u5fcc\u91cd \u91cd\uff0c\u957f\u957f\u5401\u4e00\u65e7\u6c14\uff0c\u5410\u51fa\u5fc3\u4e2d\u79ef\u5fff\u3001\u6012\u706b\uff0c\u53e3\u6c14\u5e73\u548c\u5730\u9053\uff1a\u201c\u94c1\u5144\u4e0d\u53ef\u8bef\u4f1a\uff0c\u90a3\u4e24\u4eba\u4e2d\uff0c\u6709 \u4e00\u4e2a\u53eb\u77f3\u742a\u7684\uff0c\u4e09\u5e74\u524d\u548c\u8d3e\u67d0\u6709\u8fc7\u4e00\u6bb5\u65f6\u95f4\u4ea4\u5f80\uff0c\u8d3e\u67d0\u77e5\u4ed6\u51fa\u8eab\u6b66\u5f53\u95e8\u4e0b\uff0c\u5251\u672f\u4e0a\u9020\u8be3\u5f88\u6df1\uff0c \u65e5\u524d\u65e0\u610f\u76f8\u9047\u2026\u2026\u201d \u3000\u3000\u201c\u8d3e\u5144\uff0c\u548c\u4ed6\u676f\u9152\u8a00\u6b22\uff0c\u7545\u53d9\u522b\u540e\u4e4b\u60c5\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5012\u6ca1\u6709\uff0c\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u5144\u5f1f\u6539\u53d8\u5f88\u5927\uff0c\u77f3\u742a\u5e76\u672a\u77a7\u51fa\u6545\u53cb\u7684\u8eab\u4efd\uff0c\u4f46\u5144\u5f1f\u5374\u5f88\u5947\u602a\uff0c \u4ee5\u77f3\u742a\u5251\u827a\u4e4b\u7cbe\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u6295\u8eab\u516c\u95e8\uff0c\u7edd\u4e0d\u4f1a\u53ea\u662f\u4e00\u4e2a\u8859\u5f79\u6355\u5feb\u7684\u8eab\u4efd\uff0c\u672c\u60f3\u62db\u547c\u4ed6\u4e00\u58f0\uff0c \u95ee\u4e2a\u6e05\u695a\uff0c\u4f46\u7ee7\u800c\u4e00\u60f3\uff0c\u8fd9\u53ef\u80fd\u662f\u94c1\u5144\u5e03\u4e0b\u7684\u79d8\u5bc6\u68cb\u5b50\uff0c\u5144\u5f1f\u4e5f\u5c31\u653e\u5f03\u7ea6\u4ed6\u4f1a\u9762\u7684\u8ba1\u5212\u4e86\uff01\u201d \u3000\u3000\u201c\u53e6\u5916\u4e00\u4f4d\u5462\uff1f\u201d\u94c1\u7fce\u9053\uff1a\u201c\u4e5f\u662f\u8d3e\u5144\u8fc7\u53bb\u7684\u670b\u53cb\u4e48\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u5fc3\u4e2d\u9a82\u4e86\u4e00\u58f0\uff0c\u53ef\u6076\uff01\u4eba\u5374\u5c55\u989c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u4e00\u4f4d\uff0c\u5144\u5f1f\u5c31\u4e0d\u8ba4\u8bc6\u4e86\uff0c\u4f46\u4ed6\u548c\u77f3 \u742a\u8d70\u5728\u4e00\u8d77\uff0c\u72b6\u81f3\u4eb2\u5bc6\uff0c\u4e0d\u4f1a\u662f\u65b0\u4ea4\u7684\u670b\u53cb\u2026\u2026\u201d \u3000\u3000\u94c1\u7fce\u63a5\u9053\uff1a\u201c\u5bf9\uff01\u4ed6\u4eec\u662f\u8001\u670b\u53cb\u4e86\uff0c\u4e5f\u786e\u662f\u94c1\u67d0\u5e03\u4e0b\u7684\u4e00\u62db\u6697\u68cb\uff0c\u4f46\u7adf\u7792\u4e0d\u8fc7\u8d3e\u5144\u7684\u4e00 \u53cc\u795e\u76ee\uff0c\u5389\u5bb3\u5440\uff01\u8d3e\u5144\u3002\u201d \u3000\u3000\u518d\u8c08\u4e0b\u53bb\uff0c\u5c31\u5341\u5206\u7684\u4e4f\u5473\u4e86\uff0c\u6240\u4ee5\uff0c\u8d3e\u82f1\u7b11\u4e00\u7b11\uff0c\u8f6c\u8fc7\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u94c1\u5144\u7684\u4f11\u606f\u5730\u65b9\uff0c \u5df2\u5b89\u6392\u5c31\u7eea\uff0c\u4f30\u7b97\u654c\u4eba\u5982\u6709\u884c\u52a8\uff0c\u53ef\u80fd\u5728\u4e8c\u66f4\u4e4b\u540e\uff0c\u4eca\u591c\u521d\u66f4\u4e4b\u524d\uff0c\u94c1\u5144\u7684\u56db\u4f4d\u526f\u624b\uff0c\u4f1a\u5411 \u94c1\u5144\u62a5\u5230\uff0c\u4e5f\u4f1a\u628a\u4ed6\u4eec\u7cbe\u7edd\u7684\u6280\u827a\uff0c\u8bf4\u7ed9\u94c1\u5144\uff0c\u4ee5\u4f5c\u4f60\u6d3e\u7528\u4ed6\u4eec\u7684\u53c2\u8003\uff0c\u73b0\u5728\uff0c\u8bf7\u94c1\u5144\u5148\u53bb \u4f11\u606f\u3002\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u56de\u624b\u4e00\u62db\uff0c\u4e00\u4e2a\u79c0\u4e3d\u7684\u9752\u8863\u5973\u5a62\uff0c\u5feb\u6b65\u5165\u5ba4\uff0c\u5bf9\u94c1\u7fce\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u5c0f\u5a62\u5e26\u8def\uff0c \u94c1\u5927\u4fa0\u8bf7\u3002\u201d \u3000\u3000\u8d3e\u82f1\u601d\u8651\u7cbe\u5bc6\uff0c\u4f3c\u662f\u5df2\u628a\u4e8b\u60c5\u5b89\u6392\u7684\u59a5\u59a5\u5f53\u5f53\uff0c\u94c1\u7fce\u60f3\u4e00\u60f3\uff0c\u4e5f\u89c9\u5230\u6ca1\u6709\u4ec0\u4e48\u53ef\u95ee\u7684\u4e86\uff0c \u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8d3e\u5144\uff0c\u5982\u6709\u5de5\u592b\uff0c\u5144\u5f1f\u60f3\u518d\u548c\u8d3e\u5144\u8c08\u8c08\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u4f1a\u53bb\u966a\u4f60\u5171\u8fdb\u665a\u9910\uff0c\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u94c1\u5144\u5148\u53bb\u4f11\u606f\u5427\uff01\u201d \u3000\u3000\u9752\u8863\u5973\u5a62\u5e26\u94c1\u7fce\u51fa\u5ba4\u800c\u53bb\uff0c\u8d3e\u82f1\u5728\u5ba4\u5de1\u89c6\u4e00\u904d\uff0c\u624d\u5e26\u4e0a\u623f\u95e8\u800c\u53bb\u3002 \u3000\u3000\u4e00\u5e62\u9752\u7816\u780c\u6210\u7684\u4e8c\u697c\uff0c\u94c1\u7fce\u4f4f\u7684\u662f\u697c\u4e0a\u3002\u4e8c\u697c\u5f88\u5bbd\u655e\uff0c\u5206\u9694\u6210\u56db\u4e2a\u5927\u623f\u95f4\uff0c\u5206\u79f0\u6885\u3001\u5170\u3001 \u7af9\u3001\u83ca\uff0c\u6bcf\u95f4\u623f\u95f4\u7684\u989c\u8272\u4e0d\u540c\uff0c\u6885\u5ba4\u662f\u4e2a\u767d\u8272\u7684\u95e8\u6237\uff0c\u6253\u5f00\u6728\u95e8\u4e00\u7247\u767d\uff0c\u767d\u7eeb\u5e54\u58c1\uff0c\u767d\u6be1\u94fa \u5730\uff0c\u5bb6\u5177\u3001\u7528\u5668\u4e00\u8272\u767d\u3002 \u3000\u3000\u53ea\u770b\u4e00\u773c\uff0c\u5c31\u53ef\u4ee5\u770b\u51fa\uff0c\u8fd9\u662f\u63a5\u5f85\u8d35\u5bbe\u7684\u5730\u65b9\uff0c\u623f\u4e2d\u6709\u5957\u623f\uff0c\u5206\u6210\u5367\u623f\u3001\u5ba2\u623f\u3001\u5c0f\u4e66\u623f\uff0c \u8fd8\u6709\u4e2a\u4eba\u4f4f\u7684\u5c0f\u623f\u95f4\uff0c\u8bbe\u5907\u4e4b\u5168\uff0c\u7b2c\u4e00\u6d41\u7684\u5927\u5ba2\u6808\uff0c\u4e5f\u96be\u53ca\u5f97\u3002\u9752\u8863\u5973\u5a62\u52a8\u4f5c\u719f\u7ec3\uff0c\u5148\u9001\u4e0a \u4e00\u676f\u8336\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u94c1\u7237\uff0c\u6211\u53bb\u653e\u6c34\uff0c\u4f60\u5148\u6d17\u4e2a\u6fa1\uff0c\u518d\u544a\u8bc9\u5c0f\u5a62\u5403\u4ec0\u4e48\uff0c\u6211\u53bb\u5429\u5490\u53a8\u4e0b\u51c6 \u5907\u3002\u201d \u3000\u3000\u63d0\u5230\u6d17\u6fa1\u7684\u4e8b\uff0c\u94c1\u7fce\u5fc3\u4e2d\u4e00\u52a8\uff0c\u56db\u51e4\u697c\u7684\u6b66\u9662\u4e2d\uff0c\u4e00\u573a\u6c90\u6d74\uff0c\u51e0\u4e4e\u6d17\u53bb\u4e86\u4e00\u6761\u8001\u547d\uff0c\u5982 \u975e\u9047\u4e0a\u6c5f\u5357\u540d\u6355\u675c\u671b\u6708\uff0c\u53ea\u6050\u6b64\u523b\u5c38\u9aa8\u5df2\u5bd2\u3002 \u3000\u3000\u8fd9\u4f4d\u795e\u51fa\u9b3c\u6ca1\u7684\u6c5f\u5357\u540d\u6355\uff0c\u73b0\u5728\u54ea\u91cc\u5462\uff1f \u3000\u3000\u56db\u51e4\u697c\u6b66\u9662\u4e2d\u7684\u7ea2\u88f3\u662f\u4e2a\u5fc3\u5982\u86c7\u874e\u3001\u4eba\u5982\u82b1\u5a07\u7684\u59d1\u5a18\uff0c\u6b64\u523b\u662f\u5426\u8fd8\u6d3b\u5728\u4eba\u4e16\u4e0a\u5462\uff1f\u5730\u4e0b \u5bc6\u5ba4\u90a3\u628a\u706b\uff0c\u4f1a\u4e0d\u4f1a\u628a\u5979\u70e7\u6b7b\uff1f \u3000\u3000\u7fe0\u7eeb\u867d\u7136\u6ca1\u89c1\u8fc7\uff0c\u4f46\u770b\u753b\u50cf\u4e0a\u7684\u5a07\u7f8e\uff0c\u4e0d\u5728\u7ea2\u88f3\u4e4b\u4e0b\uff0c\u5fc3\u5730\u662f\u5426\u4e5f\u548c\u7ea2\u88f3\u4e00\u6837\u7684\u6076\u6bd2\u5462\uff1f \u3000\u3000\u56db\u51e4\u697c\u6b66\u9662\u4e2d\uff0c\u6709\u5973\u5982\u82b1\uff0c\u5fc3\u5982\u86c7\u874e\uff0c\u4f46\u6587\u9601\u7684\u4eba\u5462\uff1f\u662f\u4e0d\u662f\u4e00\u4e2a\u4e2a\u6ee1\u8179\u6587\u7ae0\uff1f\u6b66\u9662\u4e2d \u6b65\u6b65\u6740\u673a\uff0c\u6587\u9601\u4e2d\u5462\uff1f\u662f\u5426\u83ba\u58f0\u3001\u71d5\u8bed\u541f\u8bcd\u7ae0\uff0c\u5145\u6ee1\u7740\u6587\u96c5\u8bd7\u98ce\u5462\uff1f\u675c\u671b\u6708\u51b3\u5b9a\u6682\u4e0d\u63ed\u5f00\u56db \u51e4\u697c\u4e2d\u6b66\u9662\u4e4b\u79d8\uff0c\u5fc5\u6709\u7528\u5fc3\u3002 \u3000\u3000\u662f\u7684\uff01\u675c\u671b\u6708\u6b64\u523b\u53c8\u5728\u4f55\u5904\u5462\uff1f \u3000\u3000\u675c\u671b\u6708\u6b63\u5728\u56db\u51e4\u697c\u7684\u6587\u9601\u4e2d\u3002 \u3000\u3000\u89c1\u8bc6\u4e86\u6b66\u9662\u8bbe\u7f6e\u7684\u6740\u673a\uff0c\u6587\u9601\u5c82\u53ef\u4e0d\u770b\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u501f\u7740\u591c\u8272\uff0c\u675c\u671b\u6708\u5230\u4e86\u6587\u9601\uff0c\u4eca\u591c\uff0c\u4ed6\u8eab\u7740\u767d\u8863\uff0c\u767d\u8863\u80dc\u96ea\uff0c\u624b\u4e2d\u518d\u52a0\u4e00\u628a\u63cf\u91d1 \u6298\u6247\uff0c\u770b\u4e0a\u53bb\u6587\u96c5\u501c\u50a5\uff0c\u4f3c\u548c\u6b66\u6797\u4eba\u5168\u65e0\u5173\u7cfb\u4e86\u3002 \u3000\u3000\u4eca\u591c\uff0c\u5168\u4ee5\u672c\u6765\u9762\u76ee\u51fa\u73b0\uff0c\u5e74\u5c11\u82f1\u4fca\uff0c\u6f47\u6d12\u51fa\u4f17\uff0c\u662f\u90a3\u79cd\u4ee4\u5973\u4eba\u52a8\u5fc3\u7684\u7537\u4eba\u3002\u96c5\u81f4\u7684\u5ba2 \u5385\u4e2d\uff0c\u8aa7\u7740\u767d\u57ab\u7684\u9910\u684c\u4e0a\uff0c\u6446\u4e86\u516d\u76d8\u4f73\u80b4\uff0c\u4e00\u58f6\u9152\uff0c\u4e00\u4e2a\u4e1d\u8863\u4e3d\u4eba\u966a\u7740\u675c\u671b\u6708\u5728\u559d\u9152\uff0c\u9760\u8fd1 \u7a97\u5b50\u7684\u4e66\u6848\u4e0a\uff0c\u653e\u7740\u6587\u623f\u56db\u5b9d\uff0c\u781a\u4e2d\u58a8\u8ff9\u672a\u5e72\uff0c\u60f3\u662f\u5df2\u541f\u7f62\u8bd7\u8bcd\uff0c\u8fc7\u4e86\u5173\uff0c\u5728\u8fd9\u91cc\u559d\u9152\u8c08\u5929 \u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u5e74\u8f7b\u5973\u5a62\u6b63\u5728\u6536\u62fe\u4e66\u6848\u4e0a\u7684\u6587\u7a3f\uff0c\u770b\u6837\u5b50\uff0c\u8fd9\u6587\u9601\u4e4b\u4e2d\uff0c\u6709\u53e3\u8bd5\uff0c\u4e5f\u6709\u7b14\u8c08\uff0c\u6ca1\u6709 \u70b9\u5b66\u95ee\uff0c\u8fd8\u771f\u662f\u96be\u767b\u8fd9\u5927\u96c5\u4e4b\u5802\u3002 \u3000\u3000\u5e78\u597d\uff0c\u675c\u671b\u6708\u7684\u5b66\u95ee\u4e0d\u9519\uff0c\u4e1c\u3001\u5357\u3001\u4e2d\u3001\u897f\u56db\u5927\u6355\u5934\uff0c\u4ed6\u662f\u6700\u5e74\u8f7b\uff0c\u6700\u82f1\u4fca\uff0c\u4e5f\u662f\u8bfb\u4e66 \u6700\u591a\u7684\u4e00\u4e2a\u3002 \u3000\u3000\u966a\u4f8d\u4ed6\u5403\u9152\u7684\u7eff\u8863\u4e3d\u4eba\uff0c\u7eff\u88e4\u3001\u7eff\u886b\u3001\u7eff\u7ee3\u978b\uff0c\u5934\u4e0a\u4e00\u652f\u7eff\u7389\u9497\uff0c\u7fe0\u5149\u6d41\u7167\uff0c\u4e00\u671b\u5373\u77e5 \u662f\u4e0a\u597d\u7684\u7fe1\u7fe0\u7422\u6210\uff0c\u5168\u8eab\u4e0a\u4e0b\u4e00\u7247\u7eff\uff0c\u9664\u4e86\u4e00\u5f20\u7c89\u8138\u548c\u4e00\u53cc\u7389\u624b\u4e4b\u5916\uff0c\u518d\u4e0d\u89c1\u522b\u7684\u989c\u8272\u3002 \u3000\u3000\u8bba\u59ff\u8272\uff0c\u7eff\u8863\u5973\u6bd4\u8d77\u6b66\u9662\u4e2d\u7684\u7ea2\u88f3\uff0c\u4f3c\u662f\u5728\u4f2f\u3001\u4ef2\u4e4b\u95f4\uff0c\u4f46\u8ff7\u4eba\u5904\u662f\u5979\u7684\u4e00\u8eab\u4e66\u5377\u6c14\uff0c \u4e09\u5206\u5a07\u6175\u6001\uff0c\u6a31\u5507\u5e38\u6302\u7b11\uff0c\u7709\u76ee\u4f20\u60c5\u6765\uff0c\u6d41\u73b0\u51fa\u65e0\u6bd4\u7684\u6e29\u67d4\u3002\u4ee5\u675c\u671b\u6708\u89c1\u8bc6\u4e4b\u591a\uff0c\u9605\u5386\u4e4b\u4e30\uff0c \u4e5f\u770b\u7684\u6709\u70b9\u5446\u4e86\u3002 \u3000\u3000\u201c\u770b\u7684\u592a\u7528\u5fc3\u4e86\uff0c\u201d\u7eff\u8863\u4e3d\u4eba\u7b11\u9053\uff1a\u201c\u96fe\u91cc\u770b\u82b1\uff0c\u624d\u4f1a\u6709\u6726\u80e7\u4e4b\u7f8e\uff0c\u4f60\u8fd9\u6837\u76ef\u7740\u4eba\u5bb6\u770b\uff0c \u6f02\u4eae\u4e5f\u4f1a\u88ab\u4f60\u770b\u4e11\u4e86\u3002\u201d \u3000\u3000\u201c\u79c0\u8272\u672c\u53ef\u9910\uff0c\u537f\u5bb6\u4f55\u5fd2\u5acc\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u59d1\u5a18\u4e4b\u7f8e\uff0c\u5982\u8bd7\u5982\u753b\uff0c\u5c82\u662f\u5306\u5306\u4e00\u77a5\u95f4\uff0c \u80fd\u89c1\u795e\u97f5\u3002\u201d \u3000\u3000\u7eff\u8863\u4e3d\u4eba\u7b11\u4e86\uff0c\u7b11\u7684\u5982\u82b1\u76db\u653e\uff0c\u9b45\u529b\u56db\u5c04\uff0c\u675c\u671b\u6708\u5fc3\u795e\u4e00\u9707\uff0c\u5fd6\u9053\uff1a\u7b11\u7684\u5982\u6b64\u52a8\u4eba\u5fc3\u5f26\uff0c \u5f53\u771f\u662f\u532a\u5937\u6240\u601d\uff0c\u96be\u9053\u8fd9\u4e5f\u662f\u4e00\u79cd\u6b66\u529f\uff0c\u82e6\u7ec3\u800c\u6210\u7684\u6280\u827a\u2026\u2026\u3002 \u3000\u3000\u662f\u7684\uff0c\u5929\u72d0\u5a9a\u7b11\uff0c\u4e00\u79cd\u6d41\u4f20\u4e8e\u6c5f\u6e56\u4e0a\u7684\u8be1\u79d8\u6b66\u529f\uff0c\u542c\u8bf4\u8fd9\u79cd\u6280\u827a\uff0c\u5982\u7ec3\u5230\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c \u80fd\u7b11\u51fa\u5404\u79cd\u795e\u5947\u7684\u5f15\u65bd\u529b\u91cf\uff0c\u80fd\u8ba9\u4eba\u6548\u98de\u86fe\u6251\u706b\uff0c\u80fd\u4f7f\u4eba\u653e\u4e0b\u5175\u5203\uff0c\u7518\u5fc3\u53d7\u622e\uff0c\u5b9e\u5728\u662f\u5f88\u53ef \u6016\u7684\u6280\u827a\u3002\u8fd9\u4e2b\u5934\uff0c\u5982\u53ea\u662f\u82e6\u8bfb\u8bd7\u4e66\u6c42\u6210\u540d\u7684\u4eba\uff0c\u53c8\u600e\u4f1a\u201c\u5929\u72d0\u5a9a\u7b11\u201d\u8fd9\u79cd\u5947\u5e7b\u7684\u6b66\u529f\u5462\uff1f \u3000\u3000\u201c\u7237\uff0c\u60c5\u5df2\u949f\uff0c\u5fc3\u5df2\u8bb8\uff0c\u201d\u7eff\u8863\u4e3d\u4eba\uff0c\u9053\uff1a\u201c\u8fd8\u4e0d\u77e5\u9053\u4f60\u8d35\u59d3\u554a\uff1f\u201d \u3000\u3000\u201c\u6211\u675c\u2026\u2026\u201d\u675c\u4ec0\u4e48\uff0c\u90fd\u6ca1\u6709\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u59d3\u675c\u7684\u5f88\u591a\uff0c\u4f46\u675c\u671b\u6708\u662f\u6c5f\u5357\u7684\u5927\u6355\u5934\uff0c\u53ef\u80fd\u53ea\u6709\u4e00\u4e2a\uff0c\u8bf4\u51fa\u540d\u5b57\uff0c\u4e5f\u7b49\u4e8e\u8868\u9732\u4e86\u8eab\u4efd\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u7eff\u8863\u4e3d\u4eba\u4e5f\u672a\u591a\u95ee\uff0c\u7aef\u8d77\u9762\u524d\u9152\u676f\uff0c\u9053\uff1a\u201c\u529d\u541b\u591a\u5c3d\u4e00\u676f\u9152\uff0c\u9189\u540e\u81ea\u6709\u4f8d\u5e94\u4eba\uff0c \u6211\u53eb\u60dc\u7389\uff0c\u529d\u541b\u60dc\u53d6\u5c11\u5e74\u65f6\u7684\u60dc\u2026\u2026\u201d \u3000\u3000\u8bdd\u8bf4\u7684\u5f88\u660e\u767d\u4e86\uff0c\u653e\u5fc3\u5730\u559d\u5427\uff01\u559d\u9189\u4e86\uff0c\u6709\u4eba\u670d\u4f8d\u4f60\uff0c\u60dc\u7389\u5df2\u5bf9\u4f60\u949f\u60c5\u5fc3\u8bb8\uff0c\u9189\u4e86\u4f60\u5c31 \u53ef\u4ee5\u7559\u5bbf\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u675c\u671b\u6708\u7aef\u8d77\u9152\u676f\u4e00\u996e\u800c\u5c3d\uff0c\u5fc3\u4e2d\u5374\u5728\u5fd6\u9053\uff1a\u9189\u4e86\u53ef\u4ee5\u4f4f\u8fd9\u91cc\uff0c\u4f46\u5982\u4e0d\u9189\u5462\uff1f\u662f\u4e0d\u662f\u4e5f\u53ef \u4ee5\u7559\u4e0b\u6765\uff1f \u3000\u3000\u60dc\u7389\u7a81\u7136\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u6700\u540e\u4e00\u5173\u8981\u8fc7\uff0c\u8fd9\u4e00\u5173\u5bb9\u6613\u5440\uff0c\u9898\u76ee\u4e0d\u51fa\u5510\u8bd7\u5b8b\u8bcd\uff0c \u6211\u8bf4\u4e0a\u4e00\u53e5\uff0c\u4f60\u63a5\u4e0b\u4e00\u53e5\uff0c\u4e09\u53e5\u4e00\u9898\uff0c\u8fde\u7834\u4e09\u9898\uff0c\u5c31\u7b97\u70b9\u4e2d\u4e86\u72b6\u5143\uff0c\u6211\u4eec\u6709\u56db\u4e2a\u59ca\u59b9\uff0c\u90fd\u4f1a \u6765\u5385\u4e2d\u5e94\u70b9\uff0c\u4efb\u541b\u9009\u4e00\u4e2a\uff0c\u4eca\u591c\u4f34\u541b\u7720\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u8fd8\u6709\u8fd9\u79cd\u597d\u4e8b\uff0c\u65e0\u602a\u5979\u53ea\u80fd\u6697\u4e2d\u793a\u610f\uff0c\u4e0d\u80fd\u660e\u767d\u8bf4\u51fa\u6765\uff0c\u6587\u9601\u7684\u98ce\u96c5\uff0c\u6bd4\u6b66\u9662\u9ad8 \u660e\u591a\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u4e5f\u660e\u767d\u5979\u7a81\u7136\u63d0\u9ad8\u58f0\u97f3\uff0c\u662f\u8ba9\u6697\u4e2d\u76d1\u89c6\u7684\u4eba\uff0c\u542c\u5230\u5979\u8bf4\u8bdd\u7684\u5185\u5bb9\uff0c\u5fc3\u4e2d\u6697\u6697\u53f9\u606f\uff0c \u5982\u6b64\u7f8e\u4eba\uff0c\u5982\u6b64\u624d\u60c5\uff0c\u5374\u88ab\u56da\u5728\u4e00\u5ea7\u5993\u9662\u4e2d\uff0c\u8fce\u65b0\u9001\u65e7\u2026\u2026\u3002\u7a81\u7136\u95f4\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u62ac\u8d77\u5934\u6765\uff0c \u76ef\u4f4f\u5728\u60dc\u7389\u8138\u4e0a\u770b\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u4e0d\u540c\u4e0a\u6b21\uff0c\u4e0a\u4e00\u6b21\u770b\u7684\u662f\u7f8e\u4eba\u98ce\u60c5\uff0c\u8fd9\u4e00\u6b21\u5374\u4f3c\u8981\u5728\u60dc\u7389\u7684\u8138\u4e0a\u627e\u51fa\u67d0\u79cd\u79d8\u5bc6\u3002 \u3000\u3000\u60dc\u7389\u7684\u611f\u5e94\u591f\u5feb\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8086\u65e0\u5fcc\u60ee\u4e86\uff0c\u5982\u6b64\u8d2a\u5a6a\u7684\u770b\u6cd5\uff0c\u5c82\u4e0d\u662f\u5c3d\u6cc4\u5fc3\u4e2d\u4e4b\u79d8\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u5b9a\u7136\u4e00\u6b63\u8138\u8272\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u5728\u770b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u60dc\u7389\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u77e5\u9053\u3002\u201d \u3000\u3000\u675c\u671b\u6708\uff1a\u201c\u8bf4\u51fa\u6765\uff0c\u8bf4\u5bf9\u4e86\uff0c\u6709\u8d4f\u3002\u201d \u3000\u3000\u201c\u90a3\u9762\u684c\u5b50\u4e0a\uff0c\u6709\u7b14\u6709\u7eb8\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u5148\u628a\u4f60\u60f3\u770b\u7684\u8bb0\u4e0b\u6765\uff0c\u6211\u518d\u8bf4\u51fa\u6765\uff0c\u662f\u5bf9\u662f\u9519\uff0c \u8c01\u4e5f\u4e0d\u80fd\u8d56\u4e86\uff0c\u4e0d\u8fc7\uff0c\u4e00\u5b9a\u8981\u8bb0\u4e0b\u5fc3\u4e2d\u521d\u6b21\u52a8\u673a\uff0c\u4e0d\u80fd\u4e34\u65f6\u6539\u53d8\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u597d\uff01\u5c31\u7167\u4f60\u60dc\u7389\u59d1\u5a18\u7684\u5429\u5490\u3002\u201d\u771f\u7684\u5230\u4e66\u6848\u524d\u9762\u5750\u4e0b\u6765\uff0c\u63e1\u7b14\u75be\u4e66\u4e86\uff0c\u5fc3 \u4e2d\u5374\u5fd6\u9053\uff1a\u4e0d\u77e5\u9053\u6697\u4e2d\u76d1\u89c6\u4ed6\u7684\u4eba\uff0c\u662f\u5426\u5df2\u79bb\u53bb\uff1f\u4ed6\u4eec\u85cf\u8eab\u4f55\u5904\uff1f\u8fd9\u95f4\u96c5\u5ba4\u4e2d\u80fd\u591f\u85cf\u4eba\u7684\u5730 \u65b9\u4e0d\u591a\u3002 \u3000\u3000\u60dc\u7389\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u884c\u4e86\u8fc7\u6765\uff0c\u5979\u8d70\u7684\u5f88\u6162\uff0c\u4f7f\u675c\u671b\u6708\u6709\u5f88\u4ece\u5bb9\u7684\u65f6\u95f4\uff0c\u6536\u597d\u4e66\u7b3a\u3002 \u3000\u3000\u201c\u5509\uff01\u6bcf\u4e2a\u7537\u4eba\u90fd\u81ea\u79c1\uff0c\u53ef\u4ee5\u6709\u4e09\u59bb\u56db\u59be\uff0c\u4e5f\u53ef\u4ee5\u6e38\u620f\u98ce\u5c18\u3002\u201d\u60dc\u7389\u9053\uff1a\u201c\u5374\u4e0d\u613f\u610f\u81ea \u5df1\u7684\u5973\u4eba\uff0c\u7ea2\u674f\u51fa\u5899\uff0c\u5c31\u7b97\u662f\u4e00\u5bb5\u60c5\u7f18\uff0c\u4e5f\u5e0c\u5bf9\u65b9\u662f\u4e00\u4e2a\u672a\u7ecf\u4eba\u4e8b\u7684\u597d\u59d1\u5a18\uff0c\u4f60\u5e0c\u671b\u770b\u51fa\u6211 \u662f\u5426\u8fd8\u662f\u5904\u5973\u4e4b\u8eab\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u4e0d\u8ba4\uff0c\u4f46\u4e0d\u80fd\u800d\u8d56\uff0c\u4f60\u731c\u5bf9\u4e86\uff0c\u4e0d\u8fc7\uff0c\u76ee\u7684\u4e0d\u540c\u3002\u201d \u3000\u3000\u201c\u6211\u53ea\u60f3\u8bc1\u5b9e\u4e00\u4e0b\u4f60\u7684\u5904\u5883\uff0c\u201d\u675c\u671b\u6708\uff1a\u201c\u51b3\u5b9a\u6211\u5e94\u8be5\u5982\u4f55\u884c\u52a8\uff01\u201d \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u60dc\u7389\u4f3c\u4e4e\u542c\u7cca\u6d82\u4e86\uff0c\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u8fc7\u4e86\u8fd9\u6700\u540e\u4e00\u8bd5\uff0c\u4f60\u5c31\u662f\u6587\u9601\u7684\u65b0\u72b6\u5143\uff0c \u4e03\u5929\u4e4b\u5185\uff0c\u6211\u4eec\u56db\u59ca\u59b9\u4e4b\u5916\uff0c\u8fd8\u6709\u516b\u4e2a\u5973\u5a62\uff0c\u4efb\u541b\u53d7\u6765\uff0c\u4efb\u541b\u601c\u2026\u2026\u201d \u3000\u3000\u201c\u4e03\u5929\u4e4b\u540e\u5462\uff1f\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u662f\u4e0d\u662f\u53ef\u4ee5\u9009\u4e00\u4e2a\u5e26\u5979\u79bb\u5f00\uff1f\u201d \u3000\u3000\u201c\u4eba\u662f\u4e0d\u51c6\u5e26\uff0c\u4f46\u6709\u4e00\u5343\u4e24\u9ec4\u91d1\u7684\u81f4\u8d60\u3002\u201d\u60dc\u7389\u9053\uff1a\u201c\u626c\u5dde\u867d\u662f\u4e3e\u56fd\u6700\u5bcc\u7684\u5546\u533a\uff0c\u4f46\u5343 \u4e24\u9ec4\u91d1\uff0c\u7701\u70b9\u82b1\uff0c\u4e5f\u591f\u4f60\u5706\u4e0a\u4e09\u4e94\u5e74\u4e00\u573a\u626c\u5dde\u68a6\u4e86\u3002\u73b0\u5728\uff0c\u4f60\u51c6\u5907\u4e00\u4e0b\uff0c\u54b1\u4eec\u8bd5\u8fc7\u7b2c\u4e09\u5173\u3002 \u7b2c\u4e00\u9898\u7531\u6211\u4f5c\u4e3b\uff0c\u6211\u63d0\u4e0a\u4e00\u53e5\uff0c\u4f60\u63a5\u4e0b\u4e00\u53e5\uff0c\u4e09\u6b21\u5e94\u5bf9\uff0c\u5c31\u7b97\u8fc7\u5173\u2026\u2026\u201d \u3000\u3000\u201c\u6162\u6765\uff0c\u6162\u6765\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u7b2c\u4e00\u9898\u7531\u4f60\u4f5c\u4e3b\uff0c\u8fd9\u4e8c\u4e09\u9898\u5462\uff1f\u201d \u3000\u3000\u201c\u7b2c\u4e8c\u9898\u7531\u9601\u4e3b\u63d0\u53e5\uff0c\u7b2c\u4e09\u9898\u662f\u4ec0\u4e48\u4eba\u4f5c\u4e3b\uff0c\u6211\u4e0d\u662f\u5f88\u6e05\u695a\uff0c\u53cd\u6b63\u662f\u7531\u9601\u4e3b\u4ea4\u4e0b\u6765\u7684\u5c31 \u662f\u2026\u2026\u201d \u3000\u3000\u8bdd\u9898\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u8fd9\u7b2c\u4e09\u5173\uff0c\u770b\u4f3c\u5bb9\u6613\uff0c\u5176\u5b9e\u96be\uff0c\u5510\u8bd7\u3001\u5b8b\u8bcd\u4f55\u81f3\u6570\u4e07\u53e5\uff0c\u6ca1\u6709\u4eba\u771f\u7684 \u80fd\u628a\u5510\u8bd7\u6570\u5343\u9996\uff0c\u5b8b\u8bcd\u6570\u5343\u7bc7\uff0c\u5168\u90e8\u8bfb\u4f1a\uff0c\u8bb0\u5728\u5fc3\u4e2d\uff0c\u4f55\u51b5\uff0c\u51fa\u9898\u4eba\u6709\u5fc3\u6349\u5f04\uff0c\u9898\u8bcd\u90fd\u662f\u51b7 \u95e8\u7bc7\u7ae0\uff0c\u574a\u95f4\u7684\u552e\u4e66\u4e2d\uff0c\u6839\u672c\u6ca1\u6709\u8bb0\u8f7d\uff0c\u4f46\u4ed6\u4eec\u5374\u6536\u96c6\u4e86\u8bb8\u591a\u6210\u54c1\uff0c\u85cf\u8bf8\u79d8\u5ba4\uff0c\u4f60\u5982\u51fa\u8a00\u53cd \u9a73\uff0c\u4ed6\u4eec\u4f1a\u4eae\u51fa\u4f5c\u8005\u5e74\u4ee3\u3001\u751f\u5730\u3001\u59d3\u540d\u3001\u7ecf\u5386\uff0c\u751a\u81f3\u8fde\u6b7b\u4ea1\u7684\u65f6\u95f4\u90fd\u6709\u8bb0\u8f7d\uff0c\u4f7f\u4f60\u54d1\u53e3\u65e0\u8a00\uff0c \u8fd9\u662f\u65e0\u6cd5\u8d8a\u5ea6\u7684\u4e00\u5173\uff0c\u9664\u975e\u4ed6\u4eec\u6709\u610f\u8ba9\u4f60\u8dc3\u5165\u9f99\u95e8\uff0c\u5e94\u70b9\u4e00\u5c4a\u6587\u9601\u72b6\u5143\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u679c\u7136\u662f\uff0c\u770b\u4f3c\u5bb9\u6613\uff0c\u4f5c\u6765\u96be\uff0c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4ece\u6ca1\u6709\u4eba\u88ab\u70b9\u4f5c\u6587\u9601\u72b6\u5143\u4e86\uff1f\u201d \u3000\u3000\u201c\u6709\u8fc7\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u90a3\u662f\u4e8b\u5148\u8c08\u597d\u7684\uff0c\u9601\u4e3b\u628a\u9898\u76ee\u5356\u7ed9\u4f60\uff0c\u662f\u7a33\u7a33\u5f53\u5f53\u8fc7\u4e09\u5173\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u539f\u6765\u8fd9\u6587\u9601\u9b45\u9996\uff0c\u662f\u53ef\u4ee5\u82b1\u94b1\u4e70\u7684\uff1f\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4e0d\u77e5\u8981\u82b1\u591a\u5c11\u94f6\u5b50\uff1f\u201d \u3000\u3000\u201c\u975e\u5e38\u7684\u6602\u8d35\uff0c\u8fd9\u5c31\u662f\u6211\u8fd8\u80fd\u4fdd\u6709\u5904\u5973\u4e4b\u8eab\u7684\u539f\u56e0\uff0c\u4e0d\u662f\u60c5\u6709\u72ec\u949f\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u5c31\u7b97 \u662f\u5f88\u6709\u94b1\u7684\u4eba\uff0c\u4e5f\u820d\u4e0d\u5f97\u82b1\u4e0b\u53bb\u90a3\u4e48\u5e9e\u5927\u7684\u4e00\u7b14\u94f6\u5b50\uff0c\u9664\u4e86\u626c\u5dde\uff0c\u5929\u4e0b\u518d\u6ca1\u6709\u4e00\u4e2a\u57ce\u5e02\uff0c\u80fd \u5bb9\u4e0b\u56db\u51e4\u697c\u8fd9\u6837\u4e00\u5ea7\u79e6\u697c\u695a\u9986\u4e86\u3002\u201d\u7a81\u7136\u653e\u4f4e\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u675c\u7237\uff0c\u552f\u4e00\u7559\u4e0b\u6765\u7684\u529e\u6cd5\uff0c\u53ea \u6709\u88c5\u9189\uff0c\u4f46\u8981\u88c5\u7684\u50cf\uff0c\u88c5\u7684\u4e0d\u9732\u75d5\u8ff9\u3002\u7b49\u4e00\u4e0b\uff0c\u73b0\u573a\u6709\u4eba\u5728\uff0c\u6211\u81ea\u51fa\u9898\u76ee\u8003\u8fc7\u4f60\uff0c\u4f60\u5c31\u5c3d\u9152 \u4e00\u5927\u676f\uff0c\u88c5\u4f5c\u9189\u5012\uff0c\u4e3a\u514d\u5931\u8bef\uff0c\u6211\u5148\u628a\u9898\u76ee\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u60dc\u7389\u8bf4\u7684\u58f0\u97f3\u975e\u5e38\u5fae\u5c0f\uff0c\u5c0f\u5230\u675c\u671b\u6708\u5168\u795e\u8d2f\u6ce8\u53bb\u542c\u3002 \u3000\u3000\u675c\u671b\u6708\u7684\u56de\u7b54\u662f\u6293\u8fc7\u9152\u58f6\uff0c\u5927\u53e3\u559d\u9152\uff0c\u4e00\u9762\u8fd0\u6c14\u628a\u9152\u903c\u5728\u8138\u4e0a\uff0c\u53cc\u988a\u6cdb\u7ea2\u4e86\uff0c\u4e5f\u903c\u51fa\u4e00 \u8eab\u9152\u6c14\u3002 \u3000\u3000\u8868\u9762\u770b\u662f\u60dc\u7389\u7684\u5a07\u5a9a\uff0c\u4f7f\u4ed6\u52a8\u4e86\u5fc3\uff0c\u4f46\u675c\u671b\u6708\u771f\u6b63\u7684\u7528\u5fc3\uff0c\u8fd8\u662f\u60f3\u591a\u4e86\u89e3\u4e00\u4e9b\u56db\u51e4\u697c\u7684 \u795e\u79d8\uff0c\u60dc\u7389\u6709\u60c5\u7559\uff0c\u4e5f\u5c31\u820d\u96be\u5c31\u6613\u4e86\uff0c\u4f55\u51b5\uff0c\u60dc\u7389\u7684\u5a07\u67d4\uff0c\u4e3e\u4e16\u5c11\u89c1\uff0c\u675c\u671b\u6708\u4e5f\u4e0d\u662f\u5403\u7d20\u7684 \u4eba\uff0c\u80fd\u5f97\u7389\u4eba\u5782\u9752\uff0c\u4f55\u4e50\u800c\u4e0d\u4e3a\u5462\uff1f \u3000\u3000\u90a3\u4e2a\u5e74\u8f7b\u7684\u9752\u8863\u5973\u5a62\uff0c\u5e26\u7740\u4e2a\u84dd\u8863\u5987\u4eba\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u84dd\u8863\u5987\u4eba\u5f00\u53e3\u65e0\u5e26\u7b11\uff0c\u9053\uff1a\u201c\u606d\u559c\u516c\u5b50\u554a\uff01\u4f60\u624d\u9ad8\u516b\u6597\uff0c\u5b66\u5bcc\u4e94\u8f66\uff0c\u8f7b\u8f7b\u677e\u677e\u5730\u5c31\u8fc7\u4e86 \u586b\u8bcd\u3001\u5bf9\u8054\u4e24\u5173\uff0c\u7b2c\u4e09\u5173\u5bb9\u6613\u5440\uff0c\u63a5\u4e24\u53e5\u5510\u8bd7\u3001\u5b8b\u8bcd\uff0c\u4f60\u53ef\u5c31\u662f\u6587\u960e\u9b45\u9996\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u5e26\u7740\u4e00\u8138\u9152\u610f\uff0c\u4e09\u5206\u9189\u6001\uff0c\u9053\uff1a\u201c\u5510\u8bd7\u3001\u5b8b\u8bcd\uff0c\u6211\u8bfb\u2026\u2026\u8bfb\u8fc7\u2026\u2026\u8fc7\uff0c\u719f\u2026\u2026 \u719f\u2026\u2026\u7684\u5f88\u554a\u2026\u2026\u201d \u3000\u3000\u84dd\u8863\u5987\u4eba\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u4f60\u559d\u9189\u4e86\uff1f\u201d \u3000\u3000\u201c\u6ca1\u9189\uff0c\u6ca1\u9189\u2026\u2026\u4e0d\u4fe1\uff0c\u4f60\u4eec\u8bd5\u2026\u2026\u8bd5\u8bd5\u770b\uff01\u201d\u675c\u671b\u6708\u8eab\u5b50\u6447\u98a4\uff0c\u4f3c\u4e4e\u8fde\u7ad9\u4e5f\u7ad9\u4e0d\u7a33\u4e86\u3002 \u3000\u3000\u201c\u6bcf\u8fc7\u4e00\u5173\uff0c\u5c31\u559d\u4e09\u58f6\u9152\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u6211\u65e0\u6cd5\u963b\u6b62\u2026\u2026\u201d \u3000\u3000\u201c\u674e\u767d\u6597\u9152\u8bd7\u767e\u7bc7\uff0c\u5728\u4e0b\u4e5f\u8981\u559d\u676f\u9152\uff0c\u9152\u2026\u2026\u9152\u2026\u2026\u201d\u675c\u671b\u6708\u53e3\u9f7f\u542b\u6df7\u5730\u8bf4\uff1a\u201c\u9152\u80fd\u52a9 \u6211\u624d\u601d\u2026\u2026\u201d \u3000\u3000\u84dd\u8863\u5987\u4eba\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u4f60\u73b0\u5728\u4e0d\u80fd\u63a5\u5bf9\u4e86\u3002\u201d \u3000\u3000\u201c\u80fd\uff0c\u80fd\uff0c\u80fd\uff0c\u8bf7\u80cc\u8bf5\u4e0a\u53e5\u51fa\u6765\uff0c\u5728\u4e0b\u63a5\u7ed9\u4f60\u542c\u3002\u201d \u3000\u3000\u4ed6\u88c5\u7684\u9189\u6001\u903c\u771f\uff0c\u8eab\u4e0a\u6563\u53d1\u51fa\u6d53\u91cd\u7684\u9152\u6c14\uff0c\u84dd\u8863\u5987\u4eba\u4f3c\u5df2\u5b8c\u5168\u76f8\u4fe1\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u60dc\u7389\uff0c \u5c31\u51fa\u9898\u8003\u8003\u4ed6\u5427\uff01\u771f\u80fd\u5bf9\u4e0a\u4f60\u7684\u8bd7\u53e5\uff0c\u6211\u518d\u8bf7\u51fa\u9601\u4e3b\u7684\u547d\u9898\u2026\u2026\u201d\u7a81\u7136\uff0c\u4e0d\u8bf4\u4e86\uff0c\u8bf7\u51fa\u9601\u4e3b \u547d\u9898\uff0c\u90a3\u662f\u4e00\u5b9a\u65e0\u6cd5\u901a\u8fc7\u4e86\u3002\u4e5f\u5c31\u6697\u793a\u9601\u4e3b\u7684\u547d\u9898\uff0c\u5c31\u5e26\u5728\u5979\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u60dc\u7389\u5bf9\u8fd9\u84dd\u8863\u5987\u4eba\u4f3c\u662f\u5f88\u656c\u91cd\uff0c\u5fae\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u60dc\u7389\u9075\u547d\u3002\u201d \u3000\u3000\u4e24\u4eba\u7684\u8c08\u8bdd\u5f88\u5c0f\u5fc3\uff0c\u4f46\u675c\u671b\u6708\u5374\u542c\u5f97\u4e00\u5b57\u4e0d\u9732\uff0c\u4ed6\u529f\u529b\u6df1\u539a\uff0c\u9759\u591c\u4e2d\u80fd\u8fa8\u6811\u53f6\u7740\u5730\u4e4b\u58f0\u3002 \u3000\u3000\u84dd\u8863\u5987\u4eba\u9000\u540e\u4e24\u6b65\uff0c\u5750\u5728\u9752\u8863\u5973\u5a62\u642c\u6765\u7684\u4e00\u5f20\u6728\u6905\u4e0a\uff0c\u5149\u666f\u662f\u8981\u4eb2\u773c\u770b\u60dc\u7389\u547d\u9898\u8bd5\u8003\u4e86\u3002 \u3000\u3000\u60dc\u7389\u6697\u6697\u5401\u4e00\u53e3\u6c14\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u542c\u7740\uff0c\u6211\u63d0\u4e0a\u4e00\u53e5\uff0c\u4f60\u63a5\u4e0b\u4e00\u53e5\uff0c\u539f\u53e5\u4e0d\u5f97\u66f4\u52a8\uff0c \u4e0d\u7528\u80cc\u8bf5\u5168\u8bd7\uff0c\u4e5f\u4e0d\u7528\u63d0\u793a\u4f5c\u8005\u59d3\u540d\uff0c\u662f\u8003\u4f60\u7684\u535a\u5b66\u548c\u8bb0\u5fc6\u3002\u201d\u675c\u671b\u6708\u6447\u6447\u5934\uff0c\u4f3c\u662f\u7531\u6d53\u91cd \u7684\u9152\u610f\u4e2d\u6e05\u9192\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u8bf7\u63d0\u4e0a\u53e5\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u6545\u4eba\u897f\u8f9e\u9ec4\u9e64\u697c\uff0c\u201d \u3000\u3000\u201c\u70df\u82b1\u4e09\u6708\u4e0b\u626c\u5dde\u3002\u201d\u675c\u671b\u6708\u7b11\u9053\uff1a\u201c\u7528\u4e0d\u7740\u518d\u63a5\u4e0b\u53bb\u4e86\uff0c\u662f\u5417\uff01\u5bb9\u6613\u5440\uff01\u5bb9\u6613\u3002\u201d \u3000\u3000\u60dc\u7389\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e09\u6625\u767d\u96ea\u5f52\u9752\u51a2\uff0c\u201d \u3000\u3000\u675c\u671b\u6708\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u4e07\u91cc\u9ec4\u6cb3\u7ed5\u9ed1\u5c71\u3002\u201d \u3000\u3000\u201c\u518d\u63a5\u4e00\u53e5\uff0c\u4f60\u5c31\u8fc7\u4e86\u6211\u8fd9\u4e00\u5173\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u53d6\u6b21\u82b1\u4e1b\u6175\u56de\u989c\uff0c\u201d \u3000\u3000\u201c\u534a\u7f18\u4fee\u9053\u534a\u7f18\u541b\u3002\u201d\u5bf9\u4e0a\u8fd9\u4e00\u53e5\uff0c\u675c\u671b\u6708\u4f38\u624b\u6293\u8fc7\u684c\u5b50\u4e0a\u7684\u4e00\u58f6\u9152\uff0c\u5927\u53e3\u559d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u84dd\u8863\u5987\u4eba\u7f13\u7f13\u7ad9\u8d77\u8eab\uff0c\u4f38\u624b\u7531\u8863\u895f\u5185\u53d6\u51fa\u4e00\u4e2a\u7ea2\u8272\u5927\u5c01\u5957\u3002\u51c6\u5907\u52a8\u7528\u9601\u4e3b\u7684\u547d\u9898\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u675c\u671b\u6708\u7684\u8eab\u5b50\u4e00\u9635\u6447\u6643\uff0c\u549a\u7684\u4e00\u58f0\uff0c\u6454\u4e86\u4e0b\u53bb\uff0c\u8fd9\u4e00\u8de4\u8dcc\u7684\u4e0d\u8f7b\uff0c\u6574\u4e2a\u4eba\u8eba\u5728\u5730\u4e0a \u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u201c\u9189\u4e86\uff0c\u9189\u7684\u4e0d\u7701\u4eba\u4e8b\u3002\u201d\u60dc\u7389\u56de\u987e\u4e86\u9752\u8863\u5973\u5a62\u4e00\u773c\uff1a\u201c\u770b\u770b\u4ed6\u6b7b\u4e86\u3001\u6216\u662f\u4f24\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u5973\u5a62\u5e94\u58f0\u884c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u84dd\u8863\u5987\u4eba\u5148\u6536\u597d\u4e86\u5927\u7ea2\u5c01\u5957\uff0c\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u51c6\u5907\u5982\u4f55\u5904\u7f6e\u4ed6\uff1f\u201d \u3000\u3000\u201c\u770b\u4ed6\u4f24\u52bf\u60c5\u5f62\u3002\u201d\u60dc\u7389\u9053\uff1a\u201c\u4f24\u91cd\u4e86\uff0c\u5c31\u9001\u4ed6\u5c31\u533b\u2026\u2026\u201d \u3000\u3000\u201c\u5982\u679c\u6ca1\u6709\u4f24\u5462\uff1f\u201d\u84dd\u8863\u5987\u4eba\u9053\uff1a\u201c\u53ef\u5426\u628a\u4ed6\u4ea4\u7ed9\u6211\uff1f\u201d \u3000\u3000\u60dc\u7389\u5403\u4e86\u4e00\u60ca\uff0c\u4f46\u5c3d\u91cf\u4fdd\u6301\u7740\u5e73\u9759\uff0c\u9053\uff1a\u201c\u5468\u5b37\u5b37\uff0c\u8981\u4ed6\u4f5c\u751a\uff1f\u201d \u3000\u3000\u5468\u5b37\u5b37\u9053\uff1a\u201c\u6b64\u4eba\u4e94\u5b98\u7aef\u6b63\uff0c\u8eaf\u4f53\u5747\u5300\uff0c\u662f\u4e0a\u597d\u7684\u5165\u836f\u6750\u6599\u3002\u201d \u3000\u3000\u7528\u4eba\u5165\u836f\uff0c\u4ece\u672a\u542c\u95fb\uff0c\u4f46\u5468\u5b37\u5b37\u672a\u518d\u591a\u8bf4\uff0c\u60dc\u7389\u4e5f\u672a\u591a\u95ee\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u4e0d\u592a\u597d \u5427\uff01\u5f85\u4ed6\u5bbf\u9152\u9192\u6765\uff0c\u6211\u529d\u4ed6\u79bb\u53bb\uff0c\u4ed6\u5982\u679c\u8981\u575a\u6301\u518d\u8003\u8bd5\u4e0b\u53bb\uff0c\u518d\u8bf7\u5468\u5b37\u5b37\u6765\u6b64\u4e3b\u6301\u3002\u201d \u3000\u3000------------- \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c \u516d \u7ae0 \u3000\u3000\u675c\u671b\u6708\u4eba\u5f88\u6e05\u9192\uff0c\u4e24\u4eba\u4ea4\u8c08\uff0c\u542c\u5f97\u6e05\u695a\uff0c\u6697\u9053\uff1a\u8981\u6211\u5165\u836f\uff0c\u662f\u4f55\u7528\u610f\uff1f\u6b64\u70b9\u53ef\u7591\uff0c\u5fc5\u5f97 \u67e5\u4e2a\u6e05\u695a\u624d\u884c\u3002 \u3000\u3000\u4f46\u95fb\u5468\u5b37\u5b37\u8bf4\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u4e0d\u662f\u559c\u6b22\u4e0a\u8fd9\u4e2a\u5c0f\u5b50\u4e86\u5427\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u5440\uff01\u201d\u60dc\u7389\u9053\uff1a\u201c\u9601\u4e2d\u89c4\u6212\u68ee\u4e25\uff0c\u6211\u54ea\u91cc\u6562\u52a8\u5916\u5411\u4e4b\u5fc3\uff0c\u8fd9\u4e5f\u5c31\u662f\u6211\u4e0d\u6562\u628a\u4ed6\u4ea4 \u7ed9\u5b37\u5b37\u7684\u539f\u56e0\u3002\u9601\u4e2d\u6709\u660e\u6587\u89c4\u5b9a\uff0c\u5df2\u8fc7\u5173\u7684\u4eba\uff0c\u4e2d\u9014\u9189\u5012\uff0c\u53ef\u7559\u81f3\u9152\u9192\u540e\uff0c\u518d\u7eed\u5e94\u8bd5\u3002\u5f53\u7136\uff0c \u5982\u82e5\u4ed6\u9152\u9192\u540e\uff0c\u4e0d\u613f\u518d\u884c\u5e94\u8bd5\uff0c\u90a3\u5c31\u7b97\u8fc7\u4e86\u8fd9\u6700\u540e\u4e00\u5173\u2026\u2026\u201d \u3000\u3000\u84dd\u8863\u5987\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9601\u4e3b\u7684\u547d\u9898\uff0c\u5df2\u7136\u5907\u59a5\uff0c\u8fd9\u4e2a\u4eba\u5982\u662f\u771f\u6709\u624d\u6c14\uff0c\u5468\u5b37\u5b37\u5012\u5e0c \u671b\u4ed6\u80fd\u987a\u5229\u8fc7\u5173\uff0c\u4f60\u60dc\u7389\u59d1\u5a18\u4e5f\u53ef\u4ee5\u5982\u613f\u4ee5\u507f\u4e86\u3002\u201d\u8bdd\u8bf4\u7684\u5145\u6ee1\u706b\u6c14\uff0c\u4f3c\u662f\u5c55\u5f00\u5185\u8ba7\u4e86\u3002 \u3000\u3000\u60dc\u7389\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5468\u5b37\u5b37\uff0c\u60dc\u7389\u7167\u9601\u4e2d\u89c4\u77e9\u529e\u4e8b\uff0c\u4e0d\u6562\u7a0d\u6709\u8fdd\u8bef\uff0c\u5468\u5b37\u5b37\u89c1\u8c05\u3002\u201d \u3000\u3000\u5468\u5b37\u5b37\u8138\u8272\u94c1\u9752\uff0c\u7f13\u6b65\u884c\u8fd1\u675c\u671b\u6708\uff0c\u8e72\u4e0b\u8eab\u5b50\uff0c\u7f13\u7f13\u4f38\u51fa\u624b\u53bb\u3002 \u3000\u3000\u675c\u671b\u6708\u5f88\u6c89\u5f97\u4f4f\u6c14\uff0c\u547c\u5438\u5747\u5300\uff0c\u6c89\u7761\u5982\u6545\u3002 \u3000\u3000\u60dc\u7389\u7d27\u5f20\u7684\u5fc3\u90fd\u505c\u6b62\u8df3\u52a8\u4e86\uff0c\u4f46\u5979\u54ac\u7740\u7259\u6ca1\u6709\u51fa\u58f0\uff0c\u8138\u4e0a\u8fd8\u5e26\u7740\u5fae\u5fae\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u5468\u5b37\u5b37\u6ca1\u6709\u4f24\u5bb3\u793e\u671b\u6708\u7684\u4e3e\u52a8\uff0c\u6536\u56de\u4e86\u4f38\u51fa\u7684\u624b\u638c\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u4f46\u5374\u5bf9\u60dc\u7389\u4f3c\u662f\u6d41\u9732\u51fa\u4e86\u6781\u5ea6\u7684\u4e0d\u6ee1\u3002 \u3000\u3000\u60dc\u7389\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e2e\u6211\u628a\u675c\u7237\u62ac\u4e0b\u53bb\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u6839\u672c\u5341\u5206\u6e05\u9192\uff0c\u8fd9\u91cc\u53d1\u751f\u7684\u4e8b\uff0c\u4ed6\u5fc3\u4e2d\u660e\u767d\u7684\u5f88\uff0c\u4f46\u4ed6\u4e0d\u80fd\u7ad9\u8d77\u6765\uff0c\u6709\u6240\u884c\u52a8\uff0c \u53ea\u597d\u8ba9\u60dc\u7389\u548c\u90a3\u9752\u8863\u5973\u5a62\uff0c\u62ac\u7740\u884c\u5165\u4e86\u4e00\u4e2a\u623f\u95f4\u4e2d\uff0c\u653e\u5728\u4e00\u5f20\u6728\u69bb\u4e0a\u3002 \u3000\u3000\u60dc\u5de5\u6325\u624b\u5bf9\u5973\u5a62\uff0c\u9053\uff1a\u201c\u5b88\u5728\u5916\u9762\uff0c\u6709\u4eba\u6765\u65f6\u5c31\u53eb\u6211\u4e00\u58f0\u3002\u201d \u3000\u3000\u9752\u8863\u5973\u5a62\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5927\u58f0\u62db\u547c\u6765\u4eba\uff0c\u4ee5\u4ee3\u901a\u62a5\uff0c\u59d1\u5a18\u8bf7\u5c0f\u5fc3\u4e00\u4e9b\u3002\u201d\u8f6c\u8eab\u800c\u53bb\uff0c \u8d70\u5c31\u8d70\u5427\uff0c\u8fd8\u56de\u5934\u5e26\u4e0a\u4e86\u623f\u95e8\u3002 \u3000\u3000\u7528\u4e0d\u7740\u60dc\u7389\u62db\u547c\uff0c\u675c\u671b\u6708\u5df2\u633a\u8eab\u5750\u4e86\u8d77\u6765\uff0c\u56db\u987e\u4e86\u4e00\u773c\uff0c\u53d1\u89c9\u662f\u4e2a\u5e03\u8bbe\u5f88\u7b80\u5355\u7684\u5367\u623f\uff0c \u4e0d\u50cf\u662f\u60dc\u7389\u4f4f\u7684\u5730\u65b9\u3002 \u3000\u3000\u201c\u8fd9\u4e0d\u50cf\u63a5\u5f85\u6587\u9601\u9b45\u9996\u7684\u5ba2\u623f\u554a\uff1f\u201d \u3000\u3000\u201c\u4f60\u8fd8\u6709\u5fc3\u8bf4\u7b11\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u4eba\u5728\u9b3c\u95e8\u5173\u524d\u6253\u4e2a\u8f6c\uff0c\u4e0d\u89c9\u5f97\u6709\u4e9b\u5e78\u8fd0\u4e48\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6709\u59d1\u5a18\u4fdd\u62a4\uff0c\u5728\u4e0b\u611f\u89c9\u5230\u5b89\u5168\u7684\u5f88\u3002\u201d \u3000\u3000\u201c\u6211\uff01\u600e\u4e48\u4fdd\u62a4\u4f60\u5440\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u5468\u5b37\u5b37\u90a3\u4e00\u638c\u62cd\u4e0b\u53bb\uff0c\u4f60\u5c31\u7b97\u4e00\u8eab\u6b66\u529f\uff0c\u4e5f\u96be\u7559\u5f97 \u6027\u547d\u3002\u201d \u3000\u3000\u201c\u8fd9\u679c\u662f\u98ce\u6708\u4e4b\u5730\uff0c\u70df\u82b1\u573a\u6240\uff0c\u53ea\u6709\u7f8e\u4eba\u9001\u60c5\uff0c\u201d\u675c\u671b\u6708\u7b11\u9053\uff1a\u201c\u600e\u4e48\u6709\u6740\u4eba\u60c5\u4e8b\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u4e0d\u7528\u518d\u88c5\u4f5c\u4e86\uff0c\u6211\u76f8\u4fe1\u4f60\u4e00\u76f4\u5f88\u6e05\u9192\uff0c\u88c5\u9189\u4e5f\u88c5\u7684\u5165\u6728\u4e09\u5206\uff0c\u5468\u5b37\u5b37\u5fc3\u4e2d\u867d \u7136\u6709\u70b9\u6000\u7591\uff0c\u8fd8\u662f\u88ab\u4f60\u9a97\u8fc7\u53bb\u4e86\u2026\u2026\u201d \u3000\u3000\u675c\u671b\u6708\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5982\u82e5\u6211\u6ca1\u6709\u8d70\u773c\uff0c\u59d1\u5a18\u4e5f\u662f\u4e00\u4f4d\u6df1\u85cf\u4e0d\u9732\u7684\u9ad8\u624b\uff0c\u6587\u9601\u4e2d\u56db\u5927\u7f8e \u5973\uff0c\u53ef\u90fd\u59d1\u5a18\u4e00\u822c\uff0c\u8eab\u6000\u7edd\u6280\u4e48\uff1f\u201d \u3000\u3000\u201c\u7edd\u6280\uff0c\u592a\u62ac\u4e3e\u6211\u4e86\u5427\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u7ec3\u51e0\u62db\u82b1\u62f3\u7ee3\u817f\uff0c\u7528\u4f5c\u5f3a\u8eab\u800c\u5df2\uff0c\u54ea\u80fd\u5f53\u5f97\u7edd\u6280 \u4e4b\u79f0\u3002\u201d \u3000\u3000\u770b\u5979\u575a\u4e0d\u8ba4\u8d26\uff0c\u675c\u671b\u6708\u4e5f\u4e0d\u518d\u6df1\u7a76\uff0c\u53f9\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u613f\u8c08\uff0c\u4e0d\u8bf4\u4e5f\u7f62\uff01\u4f46\u62a4\u6211\u4e4b\u60c5\uff0c\u5728 \u4e0b\u94ed\u8bb0\u5728\u5fc3\u4e2d\u3002\u770b\u6765\u6b64\u5730\u5df2\u4e0d\u9002\u5408\u6211\u518d\u7559\u4e0b\u53bb\uff0c\u59d1\u5a18\u73cd\u91cd\uff0c\u5728\u4e0b\u5c31\u6b64\u522b\u8fc7\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8d70\u5c31\u8d70\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u60dc\u7389\u7a81\u7136\u4e00\u4f38\u624b\uff0c\u6293\u4f4f\u4e86\u675c\u671b\u6708\uff0c\u9053\uff1a\u201c\u771f\u7684\u8981\u8d70\uff01\u201d \u3000\u3000\u201c\u5468\u5b37\u5b37\u591a\u7591\uff0c\u6211\u76f8\u4fe1\u5979\u4f1a\u6697\u4e2d\u76d1\u89c6\u7740\u59d1\u5a18\u7684\u4e3e\u52a8\uff0c\u5728\u4e0b\u7559\u5728\u8fd9\u91cc\uff0c\u5bf9\u59d1\u5a18\u6709\u5bb3\u65e0\u76ca\u3002\u201d \u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u53bb\u4e4b\u540e\uff0c\u4e8b\u5982\u6625\u68a6\u4e86\u65e0\u75d5\uff0c\u59d1\u5a18\u968f\u4fbf\u627e\u4e2a\u7406\u7531\uff0c\u5c31\u53ef\u5e94\u4ed8\u8fc7\u53bb\u3002\u201d \u3000\u3000\u60dc\u7389\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u6709\u4e9b\u53cd\u5e38\uff0c\u5468\u5b37\u5b37\u4e00\u5411\u4e0d\u7406\u4f1a\u6211\u4eec\u7684\u4e8b\uff0c\u53ea\u662f\u4f20\u8fbe\u9601\u4e3b\u7684\u8bd7 \u7a3f\uff0c\u96be\u9053\u771f\u7684\u8981\u9009\u4f60\u5165\u836f\uff0c\u90a3\u5c31\u9ebb\u70e6\u5927\u4e86\u3002\u201d \u3000\u3000\u4e8b\u60c5\u5165\u6e2f\u4e86\uff0c\u56db\u51e4\u697c\u4e2d\u53c8\u4e00\u4ef6\u5927\u79d8\u5bc6\uff0c\u53ef\u80fd\u4f1a\u5728\u65e0\u610f\u4e2d\u88ab\u63ed\u53d1\u51fa\u6765\uff0c\u6253\u94c1\u8d81\u70ed\uff0c\u675c\u671b\u6708 \u4e58\u52bf\u8ffd\u95ee\u9053\uff1a\u201c\u8981\u7528\u6d3b\u4eba\u5165\u836f\uff1f\u4ece\u672a\u542c\u95fb\uff0c\u53c8\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u5462\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u6869\u5f88\u5927\u7684\u673a\u5bc6\uff0c\u6211\u4e5f\u6240\u77e5\u6709\u9650\uff0c\u4f46\u5df2\u8db3\u591f\u9a87\u4eba\u542c\u95fb\u4e86\uff0c\u5c0f\u5973\u5b50\u4e5f\u662f\u4e3a \u6b64\u800c\u6765\u2026\u2026\u201d \u3000\u3000\u539f\u6765\u662f\u7537\u5973\u4e4b\u95f4\u7684\u8f7b\u677e\u60c5\u4e8b\uff0c\u4e00\u4e0b\u5b50\u53d8\u5f97\u5341\u5206\u4e25\u8083\u4e86\u3002\u675c\u671b\u6708\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u59d1 \u5a18\uff0c\u8bf4\u660e\u767d\uff0c\u4e0d\u8fc7\uff0c\u6211\u8981\u542c\u771f\u8bdd\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\uff0c\u5728\u6211\u672a\u8bf4\u5185\u60c5\u4e4b\u524d\uff0c\u5148\u8bf7\u6559\u4e24\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8bf7\u8bf4\uff01\u201d \u3000\u3000\u201c\u675c\u7237\u53ef\u662f\u6c5f\u5357\u540d\u6355\u675c\u671b\u6708\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u6211\u4e5f\u8981\u542c\u771f\u8bdd\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u5446\u4f4f\u4e86\uff0c\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u65e9\u5df2\u77e5\u9053\u6211\u7684\u8eab\u4efd\u4e86\uff0c\u4e0d\u9519\uff0c\u533a\u533a\u6b63\u662f\u675c\u671b\u6708\u3002\u201d \u3000\u3000\u201c\u7687\u5929\u4e0d\u8d1f\u82e6\u5fc3\u4eba\u554a\uff01\u201d\u60dc\u7389\u9053\uff1a\u201c\u4e00\u5e74\u591a\u6765\u6211\u65e5\u601d\u591c\u68a6\u7684\uff0c\u5c31\u662f\u5e0c\u671b\u80fd\u9047\u4e0a\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u6162\u6765\uff0c\u6162\u6765\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u4eec\u662f\u4ece\u672a\u89c1\u8fc7\uff0c\u6211\u4e5f\u662f\u7b2c\u4e00\u6b21\u8fdb\u5165\u56db\u51e4\u697c\u4e2d\u7684\u6587\u9601\uff0c \u59d1\u5a18\u600e\u4f1a\u4e00\u773c\u5c31\u8ba4\u51fa\u5728\u4e0b\u5462\uff1f\u201d \u3000\u3000\u201c\u4e8b\u6015\u6709\u5fc3\u4eba\u5450\uff01\u6211\u4eec\u6ca1\u6709\u89c1\u8fc7\uff0c\u4f46\u6211\u5bf9\u4f60\u7684\u5f62\u8c8c\u3001\u4eba\u54c1\uff0c\u6253\u542c\u5f97\u5341\u5206\u6e05\u695a\uff0c\u4f60\u540d\u6ee1\u6c5f \u5357\uff0c\u8bc6\u4f60\u4e4b\u4eba\u5f88\u591a\uff0c\u6253\u542c\u8fd9\u4e9b\u4e8b\u5e76\u4e0d\u592a\u96be\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u96be\u7684\u662f\u5982\u4f55\u624d\u80fd\u89c1\u5230\u4f60\u3002\u6211\u60f3\u8fc7\u53bb \u627e\u4f60\uff0c\u4f46\u592a\u5192\u9669\uff0c\u60f3\u5230\u4f60\u65e9\u665a\u4f1a\u6765\u8fd9\u91cc\uff0c\u5374\u672a\u60f3\u5230\u4f60\u6765\u7684\u5982\u6b64\u4e4b\u5feb\uff0c\u4e5f\u8bb8\u662f\u5148\u7236\u9634\u7075\u6709\u77e5\uff0c \u7275\u4f60\u5230\u6b64\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u867d\u662f\u4e00\u4ee3\u540d\u6355\uff0c\u529e\u6848\u65e0\u6570\uff0c\u4f46\u662f\u4e5f\u542c\u5f97\u5fc3\u5934\u832b\u7136\uff0c\u82e6\u7b11\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60 \u81ea\u8bf4\u81ea\u8bdd\uff0c\u5728\u4e0b\u5f88\u96be\u542c\u51fa\u4e2a\u6240\u4ee5\u7136\u6765\uff0c\u53ef\u5426\u8bf4\u5f97\u6e05\u695a\u4e00\u4e9b\u3002\u4f60\u662f\u8c01\uff1f\u6025\u4e8e\u89c1\u6211\uff0c\u7a76\u7adf\u4e3a\u4e86\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u60dc\u7389\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u5904\u5883\u5371\u9669\uff0c\u957f\u8bdd\u77ed\u8bf4\uff0c\u627e\u4f60\u4e3a\u4e86\u62a5\u6848\uff0c\u66ff\u6211\u7236\u4eb2\u96ea\u6068\uff0c \u6211\u82b1\u5c3d\u5fc3\u8840\uff0c\u6df7\u5165\u56db\u51e4\u697c\uff0c\u5c31\u662f\u4e3a\u6b64\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6709\u4e00\u70b9\u5934\u7eea\u4e86\uff0c\u4ee4\u5c0a\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u51b7\u9762\u795e\u533b\u8c2d\u6267\u4e2d\u3002\u201d \u3000\u3000\u201c\u5f53\u4eca\u4e09\u5927\u795e\u533b\u4e4b\u4e00\uff0c\u8c2d\u59d1\u5a18\uff0c\u5728\u4e0b\u5931\u656c\u4e86\u3002\u201d\u675c\u671b\u6708\u7ad9\u8d77\u8eab\u5b50\uff0c\u62b1\u62f3\u4e00\u793c\u3002 \u3000\u3000\u60dc\u7389\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u795e\u533b\u52a0\u4e0a\u51b7\u9762\u4e8c\u5b57\uff0c\u5c31\u6709\u4e9b\u6b20\u7f3a\u533b\u5fb7\u4e86\u3002\u201d \u3000\u3000\u201c\u8c2d\u795e\u533b\u540d\u6ee1\u5929\u4e0b\uff0c\u533b\u672f\u6b66\u529f\uff0c\u5e76\u91cd\u6c5f\u6e56\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4ee5\u4ee4\u5c0a\u4e4b\u80fd\uff0c\u600e\u4f1a\u906d\u9047\u610f\u5916\uff0c \u7a76\u7adf\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u201c\u5584\u6cf3\u8005\uff0c\u6eba\u4e8e\u6c34\uff0c\u5bb6\u7236\u4e00\u751f\u7cbe\u7814\u533b\u672f\uff0c\u5374\u88ab\u4eba\u5165\u4e86\u836f\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u8be6\u60c5\u65e0\u6687\u8ffd\u8ff0\uff0c \u6211\u4e5f\u88ab\u5217\u540d\u8ffd\u6740\uff0c\u4f46\u6211\u4e0d\u559c\u533b\u9053\uff0c\u5f88\u5c11\u968f\u5bb6\u7236\u8d70\u52a8\uff0c\u6c5f\u6e56\u4e0a\u8ba4\u8bc6\u6211\u4e4b\u4eba\u4e0d\u591a\uff0c\u8fd9\u5c31\u7ed9\u4e86\u6211\u4e00 \u4e2a\u9003\u547d\u7684\u673a\u4f1a\uff0c\u4f34\u6211\u591a\u5e74\u4e00\u4f4d\u5973\u5a62\uff0c\u53c8\u4ee3\u6211\u800c\u6b7b\uff0c\u677e\u61c8\u4e86\u5bf9\u65b9\u8ffd\u6740\uff0c\u6211\u5728\u8fd9\u4e2a\u60b2\u60e8\u906d\u9047\u4e2d\uff0c \u5f88\u5feb\u6210\u719f\u3002\u7ec6\u628a\u5f80\u4e8b\u601d\u91cf\u8fc7\uff0c\u7531\u5bb6\u7236\u9057\u7269\u4e2d\uff0c\u627e\u51fa\u4e9b\u86db\u4e1d\u9a6c\u8ff9\u3002\u6240\u4ee5\uff0c\u624d\u8d39\u5c3d\u5fc3\u8840\uff0c\u6df7\u5165\u8fd9 \u56db\u51e4\u697c\u4e2d\uff0c\u76f4\u5230\u4eca\u65e5\u542c\u5230\u5468\u5b37\u5b37\u8bf4\u8981\u4ee5\u4f60\u5165\u836f\uff0c\u89e6\u52a8\u5fc3\u5f26\u2026\u2026\u201d \u3000\u3000\u201c\u5468\u5b37\u5b37\u8981\u89c1\u59d1\u5a18\u4e48\uff1f\u201d\u5b88\u5728\u95e8\u53e3\u7684\u5973\u5a62\uff0c\u5927\u58f0\u547c\u559d\u9053\u3002 \u3000\u3000\u675c\u671b\u6708\u8dc3\u4e0a\u5367\u69bb\uff0c\u60dc\u7389\u5df2\u5e2e\u4ed6\u8131\u4e0b\u978b\u5b50\uff0c\u76d6\u4e0a\u68c9\u88ab\u3002 \u3000\u3000\u84ec\u7136\u4e00\u58f0\uff0c\u6728\u95e8\u88ab\u4eba\u649e\u5f00\uff0c\u5468\u5b37\u5b37\u795e\u60c5\u51b7\u8083\u5730\u884c\u4e86\u8fdb\u6765\u3002\u56db\u4e0b\u6253\u91cf\u4e86\u4e00\u9635\uff0c\u7a81\u7136\u653e\u4e0b\u8138 \u7b11\u9053\uff1a\u201c\u60dc\u7389\u554a\uff0c\u559c\u6b22\u4ed6\uff0c\u600e\u4e48\u4e0d\u5e26\u4ed6\u5230\u4f60\u7684\u623f\u95f4\u4f11\u606f\uff0c\u8fd9\u4e2a\u662f\u4e2b\u5934\u4f4f\u7684\u623f\u95f4\uff0c\u5c82\u4e0d\u592a\u59d4\u5c48 \u4eba\u5bb6\u516c\u5b50\u4e86\u5417\uff1f\u201d\u5979\u56db\u4e0b\u89c2\u5bdf\uff0c\u53d1\u89c9\u60dc\u7389\u8863\u670d\u6574\u9f50\u9497\u53d1\u672a\u4e71\uff0c\u8bf4\u660e\u4e86\u672a\u53d1\u751f\u4efb\u4f55\u4e8b\u60c5\u3002 \u3000\u3000\u201c\u4ed6\u672a\u8fc7\u4e09\u5173\uff0c\u8fd8\u4e0d\u662f\u6587\u9601\u9b45\u9996\uff0c\u5c31\u7b97\u6211\u771f\u7684\u6709\u70b9\u559c\u6b22\u4ed6\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u4ed6\u5165\u6211\u4e4b\u623f\uff0c\u4e0a\u6211 \u4e4b\u5e8a\u3002\u201d\u60dc\u7389\u9053\uff1a\u201c\u501f\u6b64\u5ba4\u7ed9\u4ed6\u4f11\u606f\uff0c\u5df2\u7b97\u662f\u4ec1\u5c3d\u4e49\u81f3\u4e86\uff0c\u7b49\u4ed6\u5bbf\u9152\u9192\u6765\uff0c\u518d\u8bf7\u5468\u5b37\u5b37\u6765\u4e3b \u8bd5\u9601\u4e3b\u9881\u4e0b\u7684\u8bd7\u7a3f\uff0c\u4ed6\u5982\u679c\u80fd\u5e94\u5bf9\u4e0a\u9601\u4e3b\u4e24\u7bc7\u8bd7\u7ae0\uff0c\u518d\u5165\u6211\u7684\u5367\u623f\u4e5f\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u201c\u8bdd\u662f\u4e0d\u9519\uff0c\u4f46\u80fd\u63a5\u4e0b\u9601\u4e3b\u9898\u7ae0\u7684\uff0c\u673a\u4f1a\u4e0d\u5927\uff0c\u201d\u5b37\u5b37\u9053\uff1a\u201c\u5982\u679c\u662f\u4ed6\u901a\u4e0d\u8fc7\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u7f18\u5c3d\u4e8e\u6b64\u4e86\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u4ed6\u8d70\u4ed6\u7684\u9633\u5173\u9053\uff0c\u6211\u8fc7\u6211\u7684\u72ec\u6728\u6865\u3002\u201d \u3000\u3000\u201c\u53ef\u60dc\u5440\uff01\u53ef\u60dc\uff0c\u5982\u6b64\u4eba\u54c1\uff0c\u662f\u53ef\u9047\u4e0d\u53ef\u6c42\u7684\u7f8e\u8d28\uff0c\u201d\u5468\u5b37\u5b37\u9053\uff1a\u201c\u653e\u8fc7\u4e86\uff0c\u5c82\u4e0d\u662f\u53ef \u60dc\u7684\u5f88\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u5468\u5b37\u5b37\u7684\u610f\u601d\u662f\uff1f\u201d \u3000\u3000\u201c\u6211\u5e2e\u4f60\u6210\u5c31\u597d\u4e8b\uff0c\u4f46\u4e03\u65e5\u4e4b\u540e\uff0c\u4f60\u8981\u628a\u4ed6\u4ea4\u7ed9\u6211\u5904\u7f6e\u3002\u201d\u5468\u5b37\u5b37\u9053\uff1a\u201c\u4e2b\u5934\u8fd9\u53ef\u662f\u4e24 \u5168\u5176\u7f8e\u7684\u4e8b\uff0c\u60f3\u4f60\u4e0d\u4f1a\u62d2\u7edd\u5427\uff1f\u201d \u3000\u3000\u201c\u600e\u4e48\u5e2e\u6211\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u4f60\u575a\u6301\u8981\u4ed6\uff0c\u53c8\u4e3a\u4ec0\u4e48\uff1f\u5929\u4e0b\u7684\u7537\u4eba\u90a3\u4e48\u591a\uff1f\u201d \u3000\u3000\u5468\u5b37\u5b37\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u662f\u4ea4\u6362\uff0c\u4f60\u628a\u4ed6\u4ea4\u7ed9\u6211\uff0c\u5c31\u522b\u7ba1\u6211\u600e\u4e48\u5904\u7f6e\u4ed6\u4e86\uff0c\u6b63\u5982\u6211\u628a \u9601\u4e3b\u7684\u547d\u9898\u4ea4\u7ed9\u4f60\uff0c\u5c31\u4e0d\u95ee\u4f60\u5982\u4f55\u5b89\u6392\u4e86\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u5fc3\u4e2d\u5fd6\u601d\uff0c\u8fd9\u4e24\u4e2a\u5973\u4eba\uff0c\u628a\u6211\u5f53\u4f5c\u4ea4\u6613\u8c08\uff0c\u4e5f\u4e0d\u7ba1\u6211\u662f\u5426\u5df2\u7ecf\u9192\u8fc7\u6765\uff0c\u5f53\u771f\u662f\u76ee \u4e2d\u65e0\u4eba\u3002 \u3000\u3000\u201c\u5b37\u5b37\uff0c\u8fd9\u6863\u5b50\u4e8b\uff0c\u662f\u5426\u8981\u544a\u8bc9\u9601\u4e3b\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u731c\u7834\u4e86\u9601\u4e3b\u7684\u547d\u9898\uff0c\u9601\u4e3b\u4e00\u5b9a\u8981\u67e5 \u660e\u5185\u60c5\uff0c\u90a3\u65f6\uff0c\u6211\u8981\u5982\u4f55\u56de\u7b54\uff1f\u201d \u3000\u3000\u201c\u65e2\u60f3\u5077\u9c7c\u5403\uff0c\u5c31\u522b\u6015\u9c7c\u513f\u8165\u3002\u201d\u5468\u5b37\u5b37\u9053\uff1a\u201c\u54ac\u7d27\u7259\u5173\u4e0d\u627f\u8ba4\uff0c\u5c31\u8bf4\u9898\u76ee\u7531\u6211\u4ea4\u7ed9\u4f60\uff0c \u5f53\u9762\u62c6\uff0c\u7acb\u523b\u95ee\uff0c\u8fd9\u5c0f\u5b50\u504f\u504f\u8bfb\u8fc7\u8fd9\u4e9b\u8bd7\uff0c\u5c31\u5bf9\u7b54\u5982\u6d41\u4e86\uff0c\u90a3\u5c31\u4e0d\u5f97\u4e0d\u70b9\u4ed6\u4e00\u5c4a\u6587\u9601\u9b45\u9996\u3002 \u8fd1\u4e24\u5e74\u6765\uff0c\u53ea\u70b9\u4e86\u4e24\u5c4a\uff0c\u73b0\u5728\u4e5f\u8be5\u70b9\u7b2c\u4e09\u5c4a\u4e86\u3002\u5982\u8bf4\u62c5\u5173\u7cfb\uff0c\u6211\u6bd4\u4f60\u62c5\u7684\u591a\u4e86\uff01\u201d \u3000\u3000\u201c\u597d\u5427\uff01\u60dc\u7389\u4e00\u5207\u542c\u5b37\u5b37\u7684\u5b89\u6392\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\u5440\uff01\u542c\u6211\u7684\uff0c\u624d\u4e0d\u4f1a\u9519\u8fc7\uff0c\u60dc\u7389\u554a\uff01\u5b37\u5b37\u5403\u7684\u76d0\uff0c\u6bd4\u4f60\u5403\u7684\u9762\u8fd8\u591a\u3002\u201d\u4f38\u624b\u7531\u6000\u4e2d \u53d6\u51fa\u4e24\u4e2a\u7ea2\u8272\u5c01\u5957\u3002 \u3000\u3000\u8fd9\u4e00\u523b\uff0c\u5f15\u52a8\u4e86\u60dc\u7389\u7684\u597d\u5947\u4e4b\u5fc3\uff0c\u60f3\u7acb\u523b\u53d6\u8fc7\u6765\uff0c\u6253\u5f00\u770b\u4e00\u4e0b\u9601\u4e3b\u7684\u90a3\u9996\u8bd7\uff0c\u4f38\u624b\u53bb\u63a5\u3002 \u3000\u3000\u5468\u5b37\u5b37\u5fae\u4e00\u6c89\u8155\uff0c\u907f\u5f00\u4e86\u60dc\u7389\u7684\u53f3\u624b\uff0c\u7b11\u9053\uff1a\u201c\u60dc\u7389\uff0c\u60f3\u8fc7\u6ca1\u6709\uff1f\u5b37\u5b37\u5df2\u628a\u8fd9\u4e24\u4e2a\u5c01\u5957 \u4ea4\u7ed9\u4f60\uff0c\u4f60\u4eec\u5c31\u8981\u6d1e\u623f\u82b1\u70db\u591c\u4e86\uff0c\u5b37\u5b37\u6211\u5462\uff1f\u4e24\u624b\u7a7a\u7a7a\uff0c\u518d\u65e0\u4e00\u70b9\u5e94\u5bf9\u4e4b\u529b\uff0c\u4f60\u4eec\u5c0f\u4e24\u53e3\u537f \u537f\u6211\u6211\u7684\uff0c\u4e00\u811a\u8e22\u5f00\u6211\uff0c\u6211\u662f\u4e00\u70b9\u8f99\u4e5f\u6ca1\u6709\uff0c\u6211\u5c82\u4e0d\u592a\u5403\u4e8f\u4e86\uff0c\u8fd9\u4e48\u529e\u5427\uff1f\u8ba9\u6211\u5582\u4e00\u7c92\u6bd2\u836f \u7ed9\u8fd9\u5c0f\u5b50\u5403\u4e0b\u53bb\u2026\u2026\u201d \u3000\u3000\u201c\u6bd2\u836f\uff0c\u4ec0\u4e48\u6837\u5b50\u7684\u6bd2\u836f\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u5403\u4e0b\u836f\uff0c\u6bd2\u53d1\u8eab\u6b7b\uff0c\u7559\u7ed9\u6211\u4e00\u5177\u5c38\u4f53\uff0c\u8981\u6211\u5982 \u4f55\u5904\u7406\uff1f\u201d \u3000\u3000\u201c\u653e\u5fc3\uff0c\u653e\u5fc3\uff0c\u8fd9\u662f\u6162\u6027\u6bd2\u836f\uff0c\u5403\u4e0b\u53bb\uff0c\u8981\u7b2c\u5341\u5929\u624d\u4f1a\u53d1\u4f5c\u3002\u201d\u5468\u5b37\u5b37\u9053\uff1a\u201c\u522b\u5fd8\u4e86\uff0c \u6211\u8fd8\u8981\u4ed6\u4eba\u836f\uff0c\u771f\u8981\u628a\u4ed6\u6bd2\u6b7b\u4e86\uff0c\u6211\u635f\u5931\u6bd4\u4f60\u8fd8\u5927\uff0c\u4f60\u53ea\u6709\u4e03\u5929\u9650\u671f\uff0c\u4efb\u4f60\u75af\u6765\uff0c\u4efb\u4f60\u72c2\uff0c \u4ed6\u53ef\u4ee5\u591a\u6d3b\u4e09\u5929\uff0c\u6211\u53ea\u8981\u5229\u7528\u8fd9\u4e09\u5929\u7ed9\u4ed6\u8865\u4e00\u8865\uff0c\u518d\u89e3\u53bb\u8eab\u4e0a\u4e4b\u6bd2\u624d\u597d\u5165\u836f\u3002\u201d \u3000\u3000\u201c\u533b\u8005\u7236\u6bcd\u5fc3\uff0c\u4f46\u51ed\u85c9\u533b\u672f\u548c\u836f\u7269\u7684\u77e5\u8bc6\u4fee\u517b\uff0c\u914d\u5236\u6210\u5404\u79cd\u6bd2\u836f\uff0c\u5c31\u6709\u5931\u533b\u5fb7\u4e86\u3002\u201d\u60dc \u7389\u9053\uff1a\u201c\u5927\u592b\u8fd9\u4e00\u884c\uff0c\u672c\u662f\u4e16\u95f4\u6700\u53d7\u4eba\u656c\u91cd\u7684\u884c\u4e1a\uff0c\u8fd9\u4e00\u6765\uff0c\u53cd\u53d8\u6210\u53ef\u6015\u7684\u884c\u4e1a\uff0c\u5927\u592b\uff0c\u4e5f \u6210\u4e86\u6740\u624b\u7684\u51f6\u624b\u4e86\u2026\u2026\u201d \u3000\u3000\u675c\u671b\u6708\u5fc3\u4e2d\u6697\u9053\uff1a\u51b7\u9762\u795e\u533b\uff0c\u5f53\u5e74\u53ea\u6015\u4e5f\u914d\u5236\u8fc7\u5f88\u591a\u6bd2\u836f\uff0c\u6240\u4ee5\uff0c\u505a\u5973\u513f\u7684\u624d\u4e0d\u613f\u548c\u4ed6 \u5e76\u884c\u6c5f\u6e56\uff0c\u4e5f\u4e0d\u548c\u4ed6\u4eec\u5728\u4e00\u8d77\uff0c\u5979\u8ba8\u538c\u6709\u672f\u65e0\u5fb7\u7684\u5927\u592b\u2026\u2026\u3002 \u3000\u3000\u201c\u4f4f\u53e3\uff0c\u201d\u5468\u5b37\u5b37\u7a81\u7136\u53d8\u4e86\u8138\uff0c\u9053\uff1a\u201c\u4f60\u5728\u80e1\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u4f60\u53ef\u77e5\u9053\u7978\u4ece\u53e3\u51fa\uff0c\u51fa\u4e86\u4e8b\uff0c \u9601\u4e3b\u4e5f\u5e2e\u4e0d\u4e86\u4f60\u3002\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u6211\uff0c\u7a76\u7adf\u54ea\u91cc\u9519\u4e86\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u8fd8\u8bf7\u5b37\u5b37\u6307\u70b9\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u611f\u89c9\u5230\u60dc\u7389\u7684\u5185\u5fc3\u5e76\u65e0\u6050\u60e7\uff0c\u8fd9\u5916\u5f62\u7684\u754f\u60e7\uff0c\u5168\u5c5e\u88c5\u4f5c\uff0c\u8fd9\u4e2b\u5934\u4eba\u4e0d\u5927\uff0c\u5fc3\u673a\u6df1 \u6c89\uff0c\u8fd9\u6837\u7684\u4e3e\u63aa\uff0c\u7a76\u7adf\u5728\u63a2\u7d22\u4ec0\u4e48\uff1f \u3000\u3000\u201c\u540d\u533b\u3001\u826f\u76f8\uff0c\u90fd\u662f\u4e16\u4eba\u6700\u656c\u91cd\u7684\u5927\u4eba\u7269\uff0c\u4f60\u5c82\u53ef\u968f\u53e3\u8d2c\u8d23\uff01\u201d\u5468\u5b37\u5b37\u9053\uff1a\u201c\u4ee5\u540e\u4e0d\u53ef \u518d\u72af\u3002\u201d \u3000\u3000\u201c\u662f\u662f\u662f\uff0c\u60dc\u7389\u77e5\u9519\uff0c\u5b37\u5b37\u80fd\u4ee5\u4eba\u5165\u836f\uff0c\u4e5f\u5e94\u662f\u533b\u9053\u4e2d\u7684\u9ad8\u624b\u4e86\u3002\u201d \u3000\u3000\u201c\u592a\u8fc7\u62ac\u4e3e\u6211\u4e86\uff0c\u201d\u5468\u5b37\u5b37\u9053\uff1a\u201c\u6211\u53ea\u662f\u53d7\u4eba\u6307\u70b9\u3001\u4e13\u8d23\u5bfb\u836f\u7684\u4eba\u3002\u201d \u3000\u3000\u6797\u671b\u6708\u5fc3\u4e2d\u660e\u767d\u4e86\uff0c\u60dc\u7389\u5bfb\u89c5\u7684\u662f\u4e00\u4e2a\u80fd\u4ee5\u4eba\u5165\u836f\u7684\u533b\u9053\u9ad8\u624b\uff0c\u80fd\u4ee5\u4eba\u5165\u836f\u7684\u5927\u592b\u4e0d\u591a\uff0c \u9047\u4e0a\u60dc\u7389\u4e4b\u524d\uff0c\u675c\u671b\u6708\u5c31\u6ca1\u542c\u95fb\u8fc7\u8fd9\u4e9b\u4e8b\u60c5\u3002 \u3000\u3000\u7a81\u7136\uff0c\u611f\u89c9\u5230\u4e8b\u60c5\u7684\u590d\u6742\uff0c\u5df2\u8d85\u8d8a\u4ed6\u80fd\u529b\u638c\u63a7\u7684\u8303\u56f4\u4e4b\u5916\uff0c\u4e0d\u4ec5\u60f3\u5230\u5317\u4eac\u7684\u5211\u90e8\u603b\u6355\u5934\uff0c \u90a3\u4e2a\u7f8e\u4e3d\u7edd\u4f26\u7684\u59d1\u5a18\uff0c\u77e5\u8bc6\u548c\u80fd\u529b\uff0c\u5982\u5979\u7684\u7f8e\u4e3d\u4e00\u822c\u7684\u5353\u8d8a\uff0c\u5979\u8ba9\u4eba\u503e\u6155\u3001\u52a8\u5fc3\uff0c\u4e5f\u4f7f\u4eba\u656c \u4f69\u3001\u5c0a\u91cd\u3002 \u3000\u3000\u60f3\u5230\u5973\u603b\u6355\u5934\u7684\u5f62\u8272\u4e4b\u7f8e\uff0c\u8fd9\u60dc\u7389\u5c31\u4e0d\u7528\u6446\u5728\u5fc3\u4e0a\u4e86\uff0c\u4eba\u4e5f\u53d8\u7684\u66f4\u51b7\u9759\u3002 \u3000\u3000\u5468\u5b37\u5b37\u53f3\u624b\u5927\u62c7\u6307\u548c\u98df\u6307\uff0c\u634f\u4e86\u4e00\u9897\u9ed1\u8272\u836f\u4e38\uff0c\u7f13\u7f13\u9001\u5411\u675c\u671b\u6708\u7684\u53e3\u8fb9\u3002 \u3000\u3000\u60dc\u7389\u8fc5\u5feb\u4f38\u51fa\u624b\uff0c\u6321\u5728\u675c\u671b\u6708\u7684\u5634\u5df4\u524d\u9762\uff0c\u9053\uff1a\u201c\u4e0d\uff01\u4e0d\u80fd\u8ba9\u4ed6\u5403\u6bd2\u836f\uff01\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u201d\u5468\u5b37\u5b37\u9053\uff1a\u201c\u4f60\u53ef\u662f\u60f3\u53d8\u5366\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u4ed6\u9152\u8fd8\u672a\u9192\uff0c\u672c\u8eab\u5b8c\u5168\u6ca1\u6709\u63a7\u5236\u81ea\u5df1\u7684\u80fd\u529b\uff0c\u5403\u4e0b\u8fd9\u9897\u836f\uff0c\u53ef\u80fd \u4f1a\u8981\u4e86\u4ed6\u7684\u547d\uff0c\u5b37\u5b37\uff0c\u6211\u6c42\u6c42\u4f60\uff0c\u8ba9\u4ed6\u9152\u9192\u4e4b\u540e\u518d\u5403\u3002\u201d \u3000\u3000\u201c\u8fd8\u8bf4\u672a\u52a8\u60c5\uff0c\u6211\u770b\u5df2\u7ecf\u662f\u60c5\u7cfb\u5982\u6d77\u4e86\uff01\u201d \u3000\u3000\u201c\u539f\u672c\u53ea\u662f\u7a0d\u6709\u52a8\u5fc3\uff0c\u4f46\u5b37\u5b37\u4e00\u518d\u5f3a\u8c03\u4ed6\u4eba\u54c1\u51fa\u4f17\uff0c\u4e16\u95f4\u5c11\u6709\uff0c\u60dc\u7389\u60f3\u4e00\u60f3\uff0c\u5c31\u52a8\u4e86\u771f \u60c5\uff01\u201d \u3000\u3000\u201c\u60dc\u7389\uff0c\u4f60\u4eec\u5c11\u5e74\u7537\u5973\u7684\u4e8b\uff0c\u6211\u53ef\u4ece\u4e0d\u7ba1\uff0c\u4f46\u4e0d\u53ef\u52a8\u771f\u60c5\uff0c\u5982\u4ed6\u9152\u9192\u4e0d\u80af\u5403\u4e0b\u8fd9\u9897\u6bd2 \u836f\uff0c\u201d\u5468\u5b37\u5b37\u9053\uff1a\u201c\u90a3\u65f6\u53c8\u8be5\u5982\u4f55\uff1f\u4e11\u8bdd\u8bf4\u524d\u9762\uff0c\u5230\u65f6\u5019\uff0c\u53ef\u522b\u602a\u5b37\u5b37\u7ffb\u8138\u3002\u201d \u3000\u3000\u60dc\u7389\u7406\u4e00\u4e0b\u9b13\u8fb9\u6563\u53d1\uff0c\u9053\uff1a\u201c\u5b37\u5b37\uff0c\u5230\u65f6\u5019\uff0c\u8fd8\u80fd\u7531\u5f97\u4ed6\u4e48\uff1f\u201d \u3000\u3000\u5468\u5b37\u5b37\u7b11\u9053\uff1a\u201c\u4e5f\u5bf9\uff01\u4ed6\u4e0d\u5403\uff0c\u8001\u8eab\u5c31\u704c\u4ed6\u541e\u4e0b\u53bb\uff0c\u4e0d\u8fc7\uff0c\u4e2b\u5934\uff0c\u4ed6\u7684\u6b66\u529f\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u60dc\u7389\u8499\u9601\u4e3b\u6307\u70b9\uff0c\u7ec3\u51e0\u624b\u4e09\u811a\u5f0f\u7684\u62f3\u638c\uff0c\u54ea\u91cc\u80fd\u89c2\u5bdf\u51fa\u4ec0\u4e48\uff1f\u4e0d\u8fc7\uff0c\u4ed6\u559d\u7684\u70c2\u9189\u5982\u6ce5\uff0c \u5e94\u8be5\u4e0d\u662f\u9ad8\u4eba\uff0c\u4f1a\u4e0d\u4f1a\u51e0\u624b\u5e84\u5bb6\u628a\u5f0f\uff0c\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u201c\u5c31\u7b97\u4ed6\u662f\u4e2a\u9ad8\u624b\u5427\uff0c\u4e5f\u4e0d\u653e\u5728\u5b37\u5b37\u7684\u773c\u4e2d\uff0c\u60dc\u7389\uff0c\u770b\u7d27\u4ed6\uff0c\u522b\u8ba9\u4ed6\u8dd1\u4e86\uff0c\u4ee4\u665a\u4e0a\u6211\u518d \u6765\uff01\u201d \u3000\u3000\u201c\u5b37\u5b37\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u4e8c\u66f4\u8fc7\u540e\uff0c\u6211\u7b49\u4f60\u3002\u201d \u3000\u3000\u201c\u4e2b\u5934\uff0c\u662f\u4e0d\u662f\u6709\u70b9\u8ffd\u4e0d\u53ca\u5f85\u4e86\uff0c\u54fc\uff01\u5973\u5927\u4e0d\u4e2d\u7559\u554a\uff01\u201d\u5468\u5b37\u5b37\u9053\uff1a\u201c\u8001\u8eab\u6210\u5168\u4f60\uff0c\u4e8c \u66f4\u8fc7\u540e\u6211\u518d\u6765\uff01\u201d \u3000\u3000\u8f6c\u8eab\u51fa\u95e8\u800c\u53bb\u3002 \u3000\u3000\u675c\u671b\u6708\u633a\u8eab\u5750\u8d77\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5973\u4eba\u597d\u8822\uff0c\u518d\u6765\u516b\u4e2a\uff0c\u4e5f\u4e0d\u662f\u4f60\u60dc\u7389\u59d1\u5a18\u7684\u654c\u624b\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u662f\u6740\u624b\uff0c\u4e0d\u64c5\u5fc3\u673a\uff0c\u8bfb\u4e66\u592a\u5c11\uff0c\u4e5f\u4e0d\u80fd\u6709\u673a\u667a\u7684\u53cd\u5e94\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u5979\u4eec\u4e60\u60ef\u4e8e \u542c\u547d\u884c\u4e8b\uff0c\u4e0d\u592a\u4f1a\u81ea\u4f5c\u4e3b\u5f20\uff0c\u4f46\u5979\u4eec\u6b66\u529f\u4e0d\u9519\uff0c\u6740\u4eba\u7684\u624b\u6cd5\u4e0d\u4f46\u6076\u6bd2\uff0c\u800c\u4e14\u9ad8\u660e\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5979\u4eec\uff0c\u610f\u601d\u662f\u5f88\u591a\u4eba\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u6211\u77e5\u9053\uff0c\u6587\u9601\u4e2d\u5c31\u6709\u516b\u4f4d\u5b37\u5b37\uff0c\u5979\u4eec\u8f6e\u6d41\u5f53\u503c\uff0c\u76d1\u89c6\u5ba2\u4eba\u4fdd\u62a4\u6587\u9601\uff0c\u201d\u60dc\u7389\u9053\uff1a \u201c\u7528\u70b9\u5fc3\u773c\u597d\u5bf9\u4ed8\uff0c\u4f46\u4e0d\u80fd\u548c\u5979\u6765\u786c\u7684\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u90fd\u662f\u6587\u9601\u4e3b\u4eba\u7684\u5fc3\u8179\uff0c\u542c\u547d\u9601\u4e3b\uff0c\u201d\u675c\u671b\u6708\u8bf4\u9053\uff1a\u201c\u662f\u90a3\u7c7b\u6b7b\u5fe0\u578b\u7684\u4eba\u7269\uff1f\u201d \u3000\u3000\u60dc\u7389\u6447\u7ecf\u5934\u9053\uff1a\u201c\u662f\u542c\u547d\u9601\u4e3b\uff0c\u4f46\u59be\u8eab\u89c2\u5bdf\uff0c\u5979\u4eec\u4f3c\u662f\u697c\u4e3b\u7684\u4eba\uff0c\u542c\u547d\u9601\u4e3b\uff0c\u4e5f\u76d1\u89c6\u9601 \u4e3b\uff0c\u56db\u51e4\u697c\u7684\u697c\u4e3b\uff0c\u624d\u662f\u4e2a\u53ef\u6015\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u201c\u4e5f\u53ef\u80fd\u662f\u628a\u4ee4\u5c0a\u5165\u836f\u7684\u51f6\u624b\uff1f\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u6df7\u5165\u6587\u9601\u4e00\u5e74\u591a\uff0c\u89c1\u8fc7\u697c\u4e3b\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u5c31\u7b97\u662f\u89c1\u5230\u4e86\uff0c\u4e5f\u4e0d\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6c5f\u5357\u5211\u90e8\u5206\u53f8\uff0c\u6b63\u5f0f\u53d7\u7406\u8c2d\u59d1\u5a18\u7684\u6848\u5b50\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u60dc\u7389\u63a5\u9053\uff1a\u201c\u8fd8\u6709\u4e0d\u8fc7\u5440\uff1f\u2026\u2026\u201d \u3000\u3000\u675c\u671b\u6708\u8138\u8272\u4e00\u6574\uff0c\u4e25\u8083\u5730\u8bf4\u9053\uff1a\u201c\u5211\u90e8\u603b\u6355\u53f8\u3001\u6c5f\u5357\u5206\u53f8\uff0c\u8f96\u5730\u8fbd\u9614\uff0c\u6025\u9700\u8058\u8bf7\u9ad8\u624b\uff0c \u8ffd\u7f09\u9003\u72af\uff0c\u672c\u4eba\u4ee5\u6c5f\u5357\u5206\u53f8\u53f8\u4e3b\u7684\u8eab\u5206\uff0c\u4efb\u547d\u8c2d\u60dc\u7389\u5c0f\u59d0\uff0c\u4e3a\u5206\u53f8\u4e00\u7b49\u6355\u5feb\uff0c\u5e76\u4e3a\u53f8\u4e3b\u52a9\u7406\uff0c \u53d7\u6743\u53ef\u4ee5\u902e\u6355\u4eba\u72af\uff0c\u5982\u9047\u53cd\u6297\uff0c\u5f97\u884c\u683c\u6740\uff0c\u4fdf\u7ea7\u5211\u90e8\u540e\uff0c\u8865\u53d1\u4efb\u547d\u72b6\u3002\u201d \u3000\u3000\u60dc\u7389\u542c\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u6211\uff0c\u6167\u773c\u8bc6\u82f1\u96c4\uff0c\u4f60\u8eab\u6000\u7edd\u6280\uff0c\u6df1\u85cf\u4e0d\u9732\uff0c\u7792\u5f97\u8fc7\u6587\u9601\u9601\u4e3b\u548c\u516b\u4f4d\u5b37\u5b37\uff0c\u4f46\u5982\u4f55\u80fd\u7792 \u5f97\u6211\u675c\u671b\u6708\uff1f\u201d \u3000\u3000\u201c\u5f3a\u4eba\u6240\u96be\u4e86\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u6211\u4e0d\u5e72\uff01\u201d \u3000\u3000\u201c\u60dc\u7389\uff01\u201d\u675c\u671b\u6708\u6e29\u548c\u5730\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u56db\u51e4\u697c\u4e3b\u662f\u8c01\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u662f\u5426\u5c31\u662f\u628a\u4ee4\u5c0a\u5165 \u836f\u7684\u51f6\u624b\uff0c\u4f46\u4ee5\u4ee4\u5c0a\u7684\u6280\u827a\u4e4b\u5f3a\uff0c\u667a\u8ba1\u4e4b\u9ad8\uff0c\u4f46\u5374\u88ab\u90a3\u4eba\u8c0b\u7b97\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u8fd8\u662f\u4e0d\u884c\uff01\u201d\u60dc\u7389\u63a5\u9053\uff1a\u201c\u6211\u81ea\u5e7c\u5c31\u548c\u7236\u4eb2\u5206\u5c45\u4e24\u5730\uff0c\u7167\u987e\u6211\u7684\uff0c\u662f\u4e00\u4e2a\u8001\u4ec6\u548c\u4e00\u4e2a \u4e2b\u5934\uff0c\u5148\u7236\u7eb5\u7136\u8eab\u6000\u7edd\u6280\uff0c\u4e5f\u6ca1\u6709\u673a\u4f1a\u4f20\u7ed9\u6211\uff0c\u6211\u4e00\u4e2a\u624b\u65e0\u7f1a\u4eb2\u4e4b\u529b\u7684\u5f31\u5973\u5b50\uff0c\u5982\u4f55\u80fd\u4f5c\u8ffd \u7f09\u6c5f\u6d0b\u5927\u76d7\u7684\u5211\u90e8\u6355\u5feb\uff1f\u201d \u3000\u3000\u201c\u60dc\u7389\uff0c\u201d\u675c\u671b\u6708\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u624d\u662f\u4f60\u6700\u5927\u7684\u6f0f\u6d1e\uff0c\u542c\u4f60\u53e3\u6c14\uff0c\u4ee4\u5c0a\u819d\u4e0b\uff0c\u53ea\u6709 \u4f60\u4e00\u4e2a\u5973\u513f\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u5982\u6709\u4e09\u5144\u56db\u5f1f\u2026\u2026\u201d\u60dc\u7389\u9053\uff1a\u201c\u8fd9\u4e3a\u7236\u62a5\u4ec7\u7684\u4e8b\uff0c\u4e5f\u4e0d\u7528\u7740\u6211\u7ba1\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u4ee4\u5c0a\u552f\u4e00\u9aa8\u8089\uff0c\u4e5f\u662f\u4ed6\u638c\u4e0a\u660e\u73e0\uff0c\u4ed6\u600e\u5fcd\u8ba9\u4f60\u79bb\u5bb6\u72ec\u5c45\uff0c\u6211\u60f3\u4f60\u79bb\u5bb6\u65f6\u5019\uff0c\u6050\u662f \u5728\u5341\u5c81\u4e4b\u540e\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u53c8\u9519\u4e86\uff0c\u6211\u516b\u5c81\u5c31\u79bb\u5bb6\u72ec\u5c45\u4e86\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u4f60\u6837\u6837\u90fd\u6ca1\u6709\u731c\u5bf9\uff0c\u8fd8\u81ea\u8be9\u6167\u773c\u8bc6\u4eba\uff0c \u6536\u56de\u6210\u547d\u5427\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u516b\u5c81\u79bb\u5bb6\uff0c\u72ec\u5c45\u4e00\u5904\u4eba\u8ff9\u7f55\u81f3\u7684\u5730\u65b9\uff0c\u4ee4\u5c0a\u4e3a\u4e86\u9020\u6210\u4f60\uff0c\u4e5f\u591f\u5fcd\u5fc3\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u7684\u4f4f\u5904\u4eba\u8ff9\u7f55\u81f3\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u90a3\u91cc\u6709\u5f88\u591a\u2026\u2026\u201d\u5f88\u591a\u4ec0\u4e48\uff0c \u60dc\u7389\u6ca1\u6709\u8bf4\u4e0b\u53bb\uff0c\u4f46\u770b\u6837\u5b50\u7edd\u4e0d\u662f\u5f88\u591a\u4eba\u4e86\u3002 \u3000\u3000\u201c\u6211\u8fd8\u77e5\u9053\uff0c\u4f60\u6bcf\u65e5\u529f\u8bfe\u6392\u7684\u5f88\u7d27\uff0c\u4ee4\u5c0a\u5e38\u53bb\u770b\u4f60\uff0c\u4ee4\u5c0a\u4e5f\u4e3a\u4f60\u6536\u96c6\u4e86\u5929\u4e0b\u5947\u836f\uff0c\u52a9\u4f60 \u6613\u7b4b\u6d17\u9ad3\uff0c\u4f60\u5728\u8fd9\u72ec\u5c45\u5341\u5e74\u7684\u5de5\u592b\u4e2d\uff0c\u7b49\u4e8e\u5e38\u4eba\u6570\u5341\u5e74\u7684\u6210\u5c31\u4e86\u3002\u6240\u4ee5\uff0c\u4f60\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5df2\u7ec3 \u5230\u4e86\u6b66\u529f\u4e0d\u7740\u76ae\u76f8\u7684\u5883\u754c\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4ee4\u5c0a\u9ad8\u77bb\u8fdc\u77a9\uff0c\u65e9\u5df2\u601d\u8651\u53ca\u6b64\uff0c\u6240\u4ee5\uff0c\u624d\u5fcd\u5c06\u9aa8 \u8089\u5206\u5272\uff0c\u4e5f\u9020\u6210\u4e86\u4f60\u4e00\u8eab\u5353\u7edd\u7684\u6280\u827a\uff01\u201d \u3000\u3000\u201c\u7239\uff01\u6211\u8981\u66ff\u4f60\u62a5\u4ec7\u2026\u2026\u201d\u5fcd\u4e0d\u4f4f\u7236\u5973\u4eb2\u60c5\u4e86\uff0c\u73e0\u6cea\u6eda\u6eda\uff0c\u987a\u816e\u800c\u4e0b\uff0c\u5583\u5583\u5730\u63a5\u9053\uff1a \u201c\u7c89\u8eab\u788e\u9aa8\uff0c\u5728\u6240\u4e0d\u60dc\u3002\u201d \u3000\u3000\u201c\u8981\u62a5\u4ec7\uff0c\u5c31\u8981\u52a0\u4eba\u5211\u90e8\uff0c\u5929\u4e0b\u56db\u5927\u6355\u5934\uff0c\u90fd\u662f\u4f60\u7684\u63f4\u624b\uff0c\u4f55\u51b5\uff0c\u603b\u6355\u5934\u4e5f\u4e0d\u4f1a\u5750\u89c6\u4e0d \u7ba1\uff01\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u6700\u597d\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u60dc\u7389\u62ed\u53bb\u4e86\u8138\u4e0a\u4e86\u6cea\u75d5\uff0c\u9053\uff1a\u201c\u6ca1\u51fa\u606f\u554a\uff01\u600e\u4e48\u54ed\u8d77\u6765\u4e86\uff0c\u6211\u8bf4\u8fc7\uff0c\u672a\u62a5\u7236\u4ec7\u4e4b\u524d\uff0c\u6211 \u4e0d\u4f1a\u6389\u4e00\u6ef4\u773c\u6cea\u7684\u3002\u201d \u3000\u3000\u201c\u559c\u3001\u6012\u3001\u54c0\u3001\u4e50\uff0c\u51fa\u81ea\u5185\u5fc3\uff0c\u800c\u5f62\u8bf8\u4e8e\u5916\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5c31\u4e0d\u7528\u52c9\u529b\u53bb\u63a7\u5236\u5b83\u4e86\uff01\u201d \u3000\u3000\u60dc\u7389\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u5410\u51fa\u4e00\u8154\u60b2\u75db\uff0c\u9053\uff1a\u201c\u88ab\u4f60\u731c\u5bf9\u4e86\uff0c\u5509\uff0c\u4f60\u5e38\u5e38\u63d0\u5230\u7684\u603b\u6355\u5934\uff0c\u542c \u8bf4\u5979\u662f\u4e00\u4e2a\u5973\u7684\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u5979\u662f\u5c0f\u8c2a\u4eba\u95f4\u7684\u4ed9\u5b50\uff0c\u6280\u827a\u8d85\u7edd\uff0c\u667a\u6167\u8fc7\u4eba\u2026\u2026\u201d \u3000\u3000\u60dc\u7389\u63a5\u9053\uff1a\u201c\u4e5f\u8c8c\u7f8e\u5982\u82b1\uff0c\u4f60\u65e9\u5df2\u5fc3\u6298\u795e\u9189\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u4f55\u6b62\u662f\u6211\uff0c\u56db\u5927\u6355\u5934\uff0c\u4e2a\u4e2a\u90fd\u88ab\u5979\u6298\u670d\u2026\u2026\u201d\u675c\u671b\u6708\u8bf4\u5230\u6b64\u5904\uff0c\u7a81\u7136\u89c9\u7740\u4e0d\u5bf9\uff0c\u8bdd\u6709 \u8bed\u75c5\uff0c\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u6298\u670d\u7684\u610f\u601d\uff0c\u5c31\u662f\u656c\u4f69\u5979\u7684\u624d\u534e\uff0c\u670d\u4ece\u5979\u7684\u9886\u5bfc\u3002\u201d \u3000\u3000\u60dc\u7389\u8bf4\u9053\uff1a\u201c\u5982\u541b\u6240\u8a00\uff0c\u5979\u662f\u5929\u4e0b\u5c11\u6709\u7684\u624d\u5973\u4e86\uff0c\u6587\u6b66\u4e0d\u5f53\uff0c\u96be\u5f97\u554a\uff01\u96be\u5f97\uff0c\u54fc\uff01\u201d\u8138 \u4e0a\u6cdb\u8d77\u4e00\u7247\u4e0d\u5c51\u4e4b\u8272\uff0c\u5927\u6709\u65e5\u540e\u76f8\u89c1\u65f6\uff0c\u8981\u6765\u4e2a\u6280\u827a\u8f83\u6df1\u6d45\uff0c\u989c\u8272\u6bd4\u9ad8\u4f4e\u7684\u6c14\u52bf\u3002 \u3000\u3000\u675c\u671b\u6708\u5fc3\u4e2d\u6697\u5fd6\u9053\uff1a\u8fd9\u4e2b\u5934\u4e00\u65e6\u88ab\u4eba\u63ed\u7a7f\u9690\u85cf\u7684\u771f\u76f8\uff0c\u7adf\u662f\u9738\u6c14\u5341\u8db3\uff0c\u81ea\u8d1f\u7684\u5f88\u3002 \u3000\u3000\u4f46\u5979\u8138\u4e0a\u5e76\u65e0\u72c2\u50b2\u4e4b\u6c14\uff0c\u7709\u5b87\u95f4\u6cdb\u5347\u7684\u662f\u4e00\u80a1\u5f3a\u70c8\u7684\u81ea\u4fe1\u2026\u2026\u7531\u5fc3\u5e95\u5347\u8d77\u7684\u6267\u7740\u3002 \u3000\u3000\u5c31\u7b97\u8c2d\u6267\u4e2d\u628a\u751f\u5e73\u7684\u6280\u827a\u5168\u90fd\u4f20\u7ed9\u4f60\u4e86\uff0c\u90a3\u53c8\u5982\u4f55\uff1f\u96be\u9053\u8fd9\u4e16\u4e0a\u771f\u6709\u9752\u51fa\u4e8e\u84dd\u80dc\u4e8e\u84dd\u7684 \u4e8b\u3002 \u3000\u3000\u4f46\u60f3\u5230\u8c2d\u6267\u4e2d\u7684\u7cbe\u6df1\u533b\u9053\uff0c\u4f1a\u4e0d\u4f1a\u5df2\u7ecf\u627e\u51fa\u4e86\u4f7f\u4f53\u80fd\u8d85\u8d8a\u7684\u6377\u5f84\uff0c\u4ee5\u5979\u6b66\u529f\u4e0d\u7740\u76ae\u76f8\u6210 \u5c31\u800c\u8bba\uff0c\u771f\u6709\u70b9\u53eb\u4eba\u83ab\u6d4b\u9ad8\u6df1\u4e86\u3002 \u3000\u3000\u60dc\u7389\u5ffd\u7136\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u675c\u5144\uff0c\u54b1\u4eec\u6765\u4e00\u4e2a\u541b\u5b50\u534f\u5b9a\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u534f\u5b9a\u4ec0\u4e48\u5462\uff1f\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5e0c\u671b\u8c08\u7684\u662f\u6211\u529b\u80fd\u6240\u53ca\u7684\u4e8b\uff01\u201d\u53e3\u6c14\u7a81\u7136\u95f4\u53d8\u5f97\u5c0f\u5fc3\u8d77 \u6765\u3002 \u3000\u3000\u60dc\u7389\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u6355\u5934\u4e0d\u80af\u6536\u56de\u6210\u547d\uff0c\u5c0f\u5973\u5b50\u53ea\u597d\u552f\u547d\u662f\u4ece\u4e86\uff0c\u4f46\u53ea\u9650\u5bb6\u7236\u8fd9\u4e00 \u6848\u4e3a\u671f\uff0c\u6848\u5b50\u7834\u4e86\uff0c\u6211\u5c31\u8981\u8f9e\u53bb\u6355\u5feb\u804c\u52a1\uff0c\u8fd8\u6211\u672c\u6765\uff0c\u5509\uff01\u6211\u4e0d\u662f\u5a07\u60c5\u505a\u4f5c\uff0c\u5b9e\u5728\u662f\u5148\u7236\u7559 \u4e0b\u5f88\u591a\u7684\u533b\u672f\u95ee\u9898\uff0c\u6211\u90fd\u8fd8\u662f\u4e00\u77e5\u534a\u89e3\uff0c\u6211\u5f97\u8017\u7528\u5927\u91cf\u7684\u65f6\u95f4\u53bb\u7814\u8bfb\u3001\u63e3\u6478\uff0c\u6211\u4e0d\u80fd\u8ba9\u4e5d\u6cc9 \u4e0b\u7684\u6148\u7236\uff0c\u6028\u6211\u4e0d\u5b5d\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u6cf0\u5c71\u538b\u9876\u7684\u5927\u9898\u76ee\uff0c\u4e0d\u7b54\u5e94\u4e5f\u4e0d\u884c\u4e86\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u4f53\u60dc\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u4f46\u6211\u4e5f\u4e0d\u662f\u5f88\u81ea\u79c1\u7684\u4eba\uff0c\u6211\u79bb\u804c\u4e4b\u540e\uff0c\u675c\u5144\u5982\u6709\u9700\u5c0f\u59b9\u6548\u529b \u4e4b\u5904\uff0c\u60dc\u7389\u7edd\u4e0d\u63a8\u8f9e\u3002\u201d\u8bf4\u5b8c\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u4f3c\u6709\u8a00\u672a\u5c3d\u610f\u4e4b\u611f\u3002 \u3000\u3000\u201c\u4f60\uff0c\u6709\u6b64\u4e00\u8bed\uff0c\u675c\u67d0\u5b89\u5fc3\u591a\u4e86\uff0c\u4eca\u591c\u6709\u5e78\u7ed3\u4ea4\uff0c\u5e78\u4f55\u5982\u4e4b\uff0c\u5c31\u6b64\u522b\u8fc7\u4e86\u3002\u201d\u4e00\u62b1\u62f3\uff0c \u8f6c\u8eab\u8981\u8d70\uff01 \u3000\u3000\u201c\u4e0d\u884c\u554a\uff01\u4e8c\u66f4\u65f6\u5206\uff0c\u5468\u5b37\u5b37\u8981\u6765\u770b\u4f60\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u4f60\u8fd9\u4e00\u8d70\uff0c\u8981\u6211\u5982\u4f55\u4ea4\u4ee3\u5462\uff1f\u201d \u3000\u3000\u201c\u6dd8\u6c14\u5440\uff01\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u8981\u6211\u7559\u4e0b\u6765\u53c8\u80fd\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u628a\u4f60\u4ea4\u7ed9\u5979\u5440\uff01\u201d\u60dc\u7389\u9053\uff1a\u201c\u4f60\u8981\u5982\u4f55\u5e94\u53d8\uff0c\u90a3\u662f\u4f60\u7684\u4e8b\u4e86\uff0c\u6740\u5979\u3001\u9a97\u5979\uff0c\u4efb\u4f60\u51b3\u5b9a\uff0c \u5982\u679c\u60f3\u6df7\u4e0a\u56db\u51e4\u697c\u5185\u770b\u770b\uff0c\u4e5f\u662f\u4e00\u4e2a\u673a\u4f1a\uff01\u201d \u3000\u3000\u7ec6\u628a\u4e8b\u60c5\u601d\u91cf\u8fc7\uff0c\u53ea\u6709\u6740\u4e86\u5979\u6700\u5bb9\u6613\uff0c\u4f46\u5982\u80fd\u9a97\u5979\u5408\u4f5c\uff0c\u6548\u7528\u6700\u5927\uff0c\u5f53\u4e0b\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a \u201c\u9a97\u5979\uff0c\u600e\u4e48\u9a97\u54ea\uff1f\u201d \u3000\u3000\u201c\u8fd9\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\uff0c\u201d\u60dc\u7389\u66a7\u6627\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5973\u4eba\u9a97\u7537\u4eba\uff0c\u6211\u542c\u8bf4\u80fd\u9a97\u7684\u4eba\u503e\u5bb6\u8361\u4ea7\uff0c \u7537\u4eba\u4e5f\u53ef\u4ee5\u9a97\u5973\u4eba\u554a\uff01\u600e\u4e48\u9a97\uff1f\u4f60\u5c31\u597d\u597d\u5730\u60f3\u60f3\u5427\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8981\u544a\u8bc9\u6211\uff0c\u6211\u8fd8\u662f\u9ec4\u82b1\u5927\u95fa\u5973\u554a\uff01\u201d\u60dc\u7389\u9053\uff1a\u201c\u4f60\u8bf4\u4e86\uff0c\u6211\u4e5f\u4e0d\u61c2\uff0c\u73b0\u5728\uff0c\u4f60\u5750\u606f \u4e5f\u597d\uff0c\u7761\u4e00\u89c9\u4e5f\u597d\uff0c\u4e8c\u66f4\u5929\uff0c\u6211\u5e26\u5468\u5b37\u5b37\u6765\u770b\u4f60\u3002\u987a\u4fbf\u518d\u544a\u8bc9\u4f60\u4e00\u4e2a\u79d8\u5bc6\uff0c\u60dc\u7389\u662f\u6211\u5728\u6587\u9601 \u7684\u82b1\u540d\u2026\u2026\u201d \u3000\u3000\u675c\u671b\u6708\u63a5\u9053\uff1a\u201c\u59d1\u5a18\u7684\u771f\u540d\u5b57\u5462\uff1f\u201d \u3000\u3000\u201c\u771f\u540d\u4e0d\u5982\u82b1\u540d\u597d\uff0c\u53eb\u82b1\u540d\u90ce\u60c5\u59be\u610f\uff0c\u76f8\u5904\u7684\u8f7b\u677e\u6709\u8da3\uff0c\u4e00\u53eb\u771f\u540d\u5b57\uff0c\u90a3\u5c31\u6709\u70b9\u683c\u683c\u4e0d \u5165\u4e86\uff0c\u8fd8\u662f\u53eb\u6211\u7684\u82b1\u540d\u5427\uff0c\u8c2d\u60dc\u7389\uff0c\u60dc\u7389\uff0c\u60dc\u7389\uff0c\u4f60\u5927\u6355\u5934\u5c31\u591a\u591a\u601c\u60dc\u6211\u5427\uff01\u201d\u63a9\u53e3\u4e00\u7b11\uff0c \u98ce\u60c5\u4e07\u79cd\u3002 \u3000\u3000\u675c\u671b\u6708\u6709\u4e9b\u5fd8\u6211\u5730\u4f38\u51fa\u624b\u53bb\uff0c\u63e1\u4f4f\u4e86\u60dc\u7389\u4e00\u53cc\u624b\uff0c\u90a3\u662f\u53ea\u6ed1\u817b\u7684\u5c0f\u624b\uff0c\u67d4\u82e5\u65e0\u9aa8\u3002 \u3000\u3000\u60dc\u7389\u4efb\u4ed6\u63c9\u63e1\u4e00\u9635\uff0c\u624d\u8f7b\u8f7b\u62bd\u56de\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u60c5\u4e0d\u81ea\u7981\u4e86\uff0c\u522b\u5fd8\u4e86\uff0c\u4f60\u662f\u6211\u7684\u9876\u5934\u4e0a \u53f8\u554a\uff01\u4e5f\u522b\u5fd8\u4e86\u5317\u4eac\u8fd8\u6709\u4e2a\u603b\u6355\u5934\u3002\u201d \u3000\u3000\u8f6c\u8fc7\u8eab\u5b50\uff0c\u4e00\u95ea\u800c\u901d\u3002 \u3000\u3000\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u5356\u5f04\u4e00\u4e0b\u8f7b\u529f\u8eab\u6cd5\u3002 \u3000\u3000\u4f46\u5374\u4f7f\u5f97\u793e\u671b\u6708\u5927\u5927\u5403\u4e86\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u8774\u8776\u7a7f\u82b1\u6b65\uff1f\u201d\u8fd9\u4e2b\u5934\uff0c\u600e\u7684\u5b66\u4f1a\u5357\u8352\u7edd\u6280\uff1f \u3000\u3000\u8fd9\u4e2a\u7f8e\u4e3d\u7684\u59d1\u5a18\uff0c\u5916\u5f62\u4e0a\u5a07\u5a06\u6e29\u67d4\uff0c\u9aa8\u5b50\u91cc\uff0c\u5374\u662f\u6709\u7740\u6211\u884c\u6211\u7d20\u7684\u4e3b\u89c1\u3002\u4f46\u5979\u8fd8\u662f\u5973\u4eba\uff0c \u98de\u918b\u4e00\u5403\u4e09\u5343\u91cc\uff0c\u5fc3\u4e2d\u8bb0\u4e0a\u603b\u6355\u5934\u4e86\u3002 \u3000\u3000\u7ecf\u8fc7\u4e86\u4e00\u6bb5\u957f\u65f6\u95f4\u5750\u606f\uff0c\u675c\u671b\u6708\u611f\u89c9\u4f53\u80fd\u5c3d\u590d\uff0c\u7cbe\u795e\u5065\u65fa\uff0c\u4f46\u4eba\u5374\u8eba\u5728\u6728\u69bb\u4e0a\uff0c\u95ed\u76ee\u5047 \u5bd0\uff0c\u5c31\u7b49\u7740\u5468\u5b37\u5b37\u6765\u4e86\u3002 \u3000\u3000\u5468\u5b37\u5b37\u6765\u7684\u5f88\u51c6\u65f6\uff0c\u4e8c\u66f4\u521a\u8fc7\uff0c\u4eba\u5c31\u5230\u4e86\u5367\u623f\u95e8\u5916\uff0c\u60dc\u7389\u4eb2\u81ea\u63d0\u7740\u4e00\u76cf\u706f\u7b3c\u5e26\u8def\uff0c\u63a8\u5f00 \u6728\u95e8\uff0c\u71c3\u8d77\u6728\u6848\u4e0a\u7684\u7ea2\u70db\uff0c\u628a\u706f\u7b3c\u4ea4\u7ed9\u4e86\u5b88\u5728\u95e8\u5916\u7684\u5973\u5a62\uff0c\u6325\u624b\u4ee4\u5973\u5a62\u9000\u53bb\u3002 \u3000\u3000\u201c\u600e\u4e48\uff1f\u8fd8\u8ba9\u4ed6\u7761\u5728\u4e2b\u5934\u623f\u91cc\u5440\uff01\u5b37\u5b37\u6211\u8fd9\u662f\u767d\u5e2e\u4f60\u4e86\u3002\u201d\u5468\u5b37\u5b37\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u53cc\u76ee \u795e\u51dd\uff0c\u5728\u675c\u671b\u6708\u7684\u8eab\u4e0a\u6253\u91cf\uff0c\u4f3c\u662f\u8981\u7531\u4ed6\u8eab\u4e0a\u770b\u51fa\u4e00\u4e9b\u79d8\u5bc6\u51fa\u6765\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5468\u5b37\u5b37\u662f\u60f3\u4ed4\u7ec6\u67e5\u770b\u4e00\u4e0b\u675c\u671b\u6708\uff0c\u662f\u5426\u5177\u6709\u7740\u5f88\u6df1\u539a\u7684\u6b66\u529f\u57fa\u7840\u3002 \u3000\u3000\u5979\u8bfb\u4e66\u4e0d\u591a\uff0c\u8c08\u4e0d\u4e0a\u7b56\u8c0b\u667a\u8ba1\uff0c\u4f46\u6740\u624b\u7684\u8c28\u614e\uff0c\u5374\u5f88\u6ce8\u610f\u3002 \u3000\u3000\u60dc\u7389\u4e5f\u770b\u5f97\u5f88\u4ed4\u7ec6\uff0c\u88c5\u7761\u7684\u675c\u671b\u6708\uff0c\u80fd\u4e0d\u80fd\u9690\u85cf\u7684\u5f88\u6df1\uff0c\u77a7\u4e0d\u51fa\u4efb\u4f55\u7834\u7efd\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u5468\u5b37\u5b37\u51fa\u624b\u5982\u7535\uff0c\u70b9\u4e86\u675c\u671b\u6708\u4e00\u5904\u7a74\u9053\u3002 \u3000\u3000\u60dc\u7389\u51e0\u4e4e\u8981\u51fa\u624b\u53cd\u51fb\u4e86\uff0c\u4f46\u5374\u54ac\u7740\u7259\u5fcd\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u5b37\u5b37\uff0c\u4f60\u8fd9\u7b97\u4ec0\u4e48\u561b\uff1f\u600e\u4e48\u5ffd\u7136 \u51fa\u624b\u6740\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u201c\u4ed6\u6d3b\u7740\u624d\u80fd\u5165\u836f\uff0c\u6211\u600e\u4f1a\u820d\u5f97\u6740\u4ed6\uff01\u201d\u5468\u5b37\u5b37\u9053\uff1a\u201c\u6211\u53ea\u662f\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u5b37\u5b37\u4e0d\u80fd \u9634\u6c9f\u91cc\u7ffb\u8239\u3002\u201d \u3000\u3000\u201c\u70b9\u7a74\u6cd5\u5440\uff01\u70b9\u4e86\u4ed6\u4ec0\u4e48\u7a74\u9053\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u73b0\u5728\uff01\u8981\u600e\u4e48\u5904\u7f6e\u4ed6\uff1f\u201d\u88c5\u7684\u4e00\u7247\u61f5\u61c2\uff0c \u9a97\u7684\u5468\u5b37\u5b37\u6df1\u60c5\u4e0d\u7591\u3002 \u3000\u3000\u201c\u70b9\u7684\u9ebb\u7a74\uff0c\u6211\u4e0b\u624b\u4e0d\u91cd\uff0c\u53ea\u8981\u4ed6\u7a0d\u6709\u6b66\u529f\u57fa\u7840\uff0c\u4e0d\u7528\u89e3\u7a74\uff0c\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4eba\u5c31\u4f1a\u6e05\u9192\u8fc7 \u6765\uff0c\u73b0\u5728\uff0c\u54b1\u4eec\u53ef\u4ee5\u653e\u5fc3\u8c08\u8c08\u4e86\uff01\u201d\u5468\u5b37\u5b37\u9053\uff1a\u201c\u4f60\u51c6\u5907\u7559\u4ed6\u51e0\u5929\uff1f\u201d \u3000\u3000\u201c\u660e\u5929\u5c31\u5f97\u8981\u4ed6\u8d70\u4e86\u3002\u201d\u60dc\u7389\u9053\uff1a\u201c\u6587\u9601\u4e2d\u89c4\u77e9\uff0c\u4e0d\u80fd\u7559\u7f6e\u95f2\u4eba\uff01\u201d \u3000\u3000\u201c\u4ed6\u4e0d\u662f\u95f2\u4eba\uff0c\u800c\u662f\u65b0\u4e00\u5c4a\u6587\u9601\u9b45\u9996\uff01\u201d\u5468\u5b37\u5b37\u9053\uff1a\u201c\u6309\u89c4\u77e9\uff0c\u4f60\u53ef\u4ee5\u7559\u4ed6\u4e03\u5929\uff01\u4e0d\u8fc7\uff0c \u5b37\u5b37\u89c9\u7740\u4e03\u5929\u592a\u591a\u4e86\uff0c\u7559\u4ed6\u4e09\u5929\u5982\u4f55\uff1f\u201d\u81ea\u8bf4\u81ea\u8bdd\uff0c\u81ea\u4f5c\u4e3b\u610f\u3002 \u3000\u3000\u60dc\u7389\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u9152\u8fd8\u672a\u9192\uff0c\u5c31\u7559\u4ed6\u4eca\u591c\u5427\uff01\u660e\u5929\u5348\u65f6\uff0c\u5b37\u5b37\u6765\u5e26\u4ed6\u8d70\uff01\u201d \u3000\u3000\u201c\u600e\u4e48\u53d8\u5366\u4e86\uff1f\u201d\u5468\u5b37\u5b37\u5ffd\u7136\u52a8\u4e86\u7591\u5fc3\uff0c\u9053\uff1a\u201c\u771f\u662f\u5c11\u5973\u5fc3\uff0c\u6d77\u5e95\u9488\u5450\uff01\u8001\u8eab\u5feb\u88ab\u4f60\u8981 \u7cca\u6d82\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u80fd\u7559\u554a\uff01\u201d\u60dc\u7389\u9053\uff1a\u201c\u6211\u5fc3\u4e2d\u5bf9\u4ed6\u597d\uff0c\u7559\u4e09\u5929\u4e4b\u4e45\uff0c\u6211\u53ea\u6015\u5f88\u96be\u63a7\u5236\u81ea\u5df1\uff0c\u4e09\u65e5\u591c \u7537\u8d2a\u5973\u7231\uff0c\u54ea\u91cc\u8fd8\u820d\u5f97\u5206\u5f00\uff0c\u6211\u4e0d\u80fd\u5192\u8fd9\u4e2a\u9669\u3002\u201d \u3000\u3000\u201c\u55ef\uff01\u6709\u9053\u7406\uff0c\u201d\u5468\u5b37\u5b37\u9053\uff1a\u201c\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5374\u60f3\u7684\u5982\u6b64\u5468\u5230\uff0c\u4f46\u4eca\u591c\u4e00\u5bb5\u2026\u2026\u201d \u3000\u3000\u201c\u5b37\u5b37\u653e\u5fc3\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u6211\u770b\u4ed6\u9152\u8fd8\u672a\u9192\uff0c\u660e\u5929\u4ed6\u5bbf\u9152\u9192\u6765\uff0c\u8ba9\u4ed6\u6d17\u6fa1\u66f4\u8863\uff0c\u5348\u65f6\u8fc7 \u540e\uff0c\u4ed6\u4f1a\u5b8c\u5168\u6e05\u9192\uff0c\u90a3\u65f6\u5b37\u5b37\u5df2\u5230\uff0c\u5c31\u53ef\u4ee5\u5e26\u4ed6\u8d70\u4e86\uff01\u5509\uff01\u6211\u4eec\u76f8\u8bc6\u4e00\u573a\uff0c\u7559\u4ed6\u4e00\u591c\uff0c\u4e5f\u7b97 \u5c3d\u756a\u5fc3\u610f\uff0c\u6b64\u540e\uff0c\u95f4\u5173\u4e0d\u77e5\u4eba\u4f55\u5904\uff1f\u76f8\u89c1\u65e0\u671f\u4e86\u3002\u201d \u3000\u3000\u201c\u4eca\u665a\u4e0a\uff0c\u2026\u2026\u201d \u3000\u3000\u201c\u4eca\u665a\u4e0a\u5e94\u8be5\u5e73\u5b89\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u6211\u4f1a\u8ba9\u4e2b\u5934\u966a\u7740\u6211\u2026\u2026\u201d \u3000\u3000\u5468\u5b37\u5b37\u51b7\u51b7\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u771f\u662f\u957f\u6c5f\u540e\u6d6a\u63a8\u524d\u6d6a\u554a\uff01\u60dc\u7389\uff0c\u4f60\u7684\u9634\u6c89\u548c\u4f60\u7684\u7f8e\u4e3d\uff0c\u5b8c\u5168 \u6b63\u6bd4\uff0c\u771f\u662f\u4eba\u5982\u6625\u82b1\u5a07\uff0c\u5fc3\u4f3c\u86c7\u7aed\u72e0\u2026\u2026\u201d \u3000\u3000\u201c\u5b37\u5b37\uff0c\u4f60\u597d\u50cf\u5f88\u8ba8\u538c\u60dc\u7389\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u9519\u4e86\uff0c\u6211\u662f\u5728\u8d5e\u7f8e\u4f60\uff0c\u201d\u5468\u5b37\u5b37\u9053\uff1a\u201c\u697c\u4e3b\u597d\u50cf\u5c31\u559c\u6b22\u4f60\u8fd9\u6837\u7684\u4eba\uff0c\u5916\u5f62\u59e3\u597d\uff0c\u591f\u7f8e\uff0c \u5fc3\u5730\u9634\u6c89\u9669\u6076\uff0c\u591f\u6bd2\u3002\u201d \u3000\u3000\u201c\u5662\uff01\u5b37\u5b37\u7684\u610f\u601d\u662f\u8bf4\uff0c\u697c\u4e3b\u5f88\u559c\u6b22\u6211\u8fd9\u4e2a\u6027\u683c\u4e86\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u5b37\u5b37\u5982\u80af\u5e2e\u52a9\uff0c\u8ba9\u6211 \u80fd\u664b\u8c12\u697c\u4e3b\uff0c\u60dc\u7389\u611f\u6069\u4e0d\u5c3d\uff0c\u65e5\u540e\u5b9a\u56fe\u62a5\u7b54\u3002\u201d \u3000\u3000\u201c\u55ef\uff01\u697c\u4e3b\u95e8\u4e0b\u6709\u4e24\u4e2a\u5f1f\u5b50\uff0c\u4e00\u7537\u4e00\u5973\u3002\u201d\u5468\u5b37\u5b37\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u90fd\u7b97\u4e0d\u4e0a\u4ed6\u7684\u8863\u94b5\u4f20\u4eba\uff0c \u6211\u4f1a\u7559\u5fc3\uff0c\u6709\u673a\u4f1a\uff0c\u6211\u4f1a\u63a8\u8350\u4f60\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4ef6\u4e8b\u6025\u4e0d\u5f97\uff0c\u80fd\u4e0d\u80fd\u8ba9\u697c\u4e3b\u5782\u9752\uff0c\u8981\u770b\u4f60\u7684\u9020\u5316 \u4e86\u3002\u201d \u3000\u3000\u201c\u60dc\u7389\u4f1a\u77e5\u6069\u56fe\u62a5\u2026\u2026\u201d \u3000\u3000\u5468\u5b37\u5b37\u63a5\u9053\uff1a\u201c\u6211\u53bb\u4e86\uff0c\u660e\u65e5\u5348\u540e\u518d\u6765\u3002\u201d\u8f6c\u8eab\u51fa\u95e8\u800c\u53bb\uff0c\u679c\u7136\uff0c\u672a\u89e3\u675c\u671b\u6708\u7684\u7a74\u9053\u3002 \u3000\u3000\u60dc\u7389\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8981\u6211\u5e2e\u4f60\u5462\uff1f\u8fd8\u662f\u4f60\u81ea\u5df1\u8d77\u6765\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u4e00\u633a\u800c\u8d77\uff0c\u7b11\u9053\uff1a\u201c\u5389\u5bb3\u5440\uff01\u4f60\u600e\u4e48\u77e5\u9053\u5979\u6ca1\u70b9\u4e2d\u6211\u7684\u7a74\u9053\uff1f\u201d \u3000\u3000\u201c\u770b\u8868\u60c5\u5c31\u77e5\u9053\u4e86\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u88c5\u7684\u5f88\u5047\uff0c\u5468\u5b37\u5b37\u592a\u5927\u610f\u4e86\uff0c\u6362\u4e86\u90a3\u4f4d\u5510\u5b37\u5b37\uff0c\u73b0\u5728\uff0c \u4f60\u5c31\u7b11\u4e0d\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u5510\u5b37\u5b37\uff0c\u5f88\u5389\u5bb3\u5417\uff1f\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u8bf4\u7684\u8be6\u5c3d\u4e00\u4e9b\uff0c\u4e5f\u8ba9\u6211\u9047\u4e0a\u65f6\u6709\u4e2a\u51c6\u5907\u3002\u201d \u3000\u3000\u201c\u5979\u6bd4\u5468\u5b37\u5b37\u5e74\u8f7b\uff0c\u4e5f\u957f\u7684\u597d\u770b\u4e00\u4e9b\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u6700\u91cd\u8981\u7684\u662f\u5979\u5f88\u7ec6\u5fc3\uff0c\u9047\u4e0a\u5979\u5c31\u4e0d \u80fd\u7ed9\u5979\u51fa\u624b\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u627f\u6559\uff0c\u627f\u6559\uff01\u73b0\u5728\uff0c\u6211\u60f3\u4e86\u89e3\u4e00\u4e0b\u56db\u51e4\u697c\u4e3b\u4e86\uff0c\u4ed6\u662f\u4e0d\u662f\u628a\u4ee4\u5c0a \u5165\u836f\u7684\u51f6\u624b\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u662f\u6000\u7591\uff0c\u8fd8\u672a\u627e\u51fa\u8bc1\u636e\u3002\u201d\u60dc\u7389\u9053\uff1a\u201c\u7528\u4eba\u5165\u836f\u7684\u533b\u672f\u9053\u6d3e\u4e0d\u591a\uff0c\u6240\u4ee5\u6c5f\u6e56\u4e0a\u5f88 \u5c11\u542c\u95fb\uff0c\u5148\u7236\u5b66\u7684\u4e00\u6d3e\u533b\u9053\uff0c\u53ef\u80fd\u662f\u552f\u4e00\u80fd\u7528\u4eba\u6cbb\u75c5\u7684\u533b\u672f\uff0c\u4f46\u7528\u4eba\u6cbb\u75c5\uff0c\u4e0d\u662f\u7528\u4eba\u5165\u836f\uff0c \u8fd9\u4e24\u4e2a\u89c2\u5ff5\uff0c\u5927\u4e0d\u76f8\u540c\uff0c\u7528\u7684\u4eba\uff0c\u4e5f\u90fd\u662f\u5341\u6076\u4e0d\u8d66\u7684\u5927\u574f\u4eba\uff0c\u8981\u6551\u7684\u4eba\uff0c\u4e5f\u90fd\u662f\u975e\u6551\u4e0d\u53ef\u7684 \u4eba\u3002\u4f46\u5b83\u672f\u6cd5\u7cbe\u6e5b\uff0c\u7acb\u7aff\u89c1\u5f71\uff0c\u4f60\u88ab\u9009\u4e2d\u5165\u836f\uff0c\u8bf4\u660e\u4e86\u4f60\u7684\u8d44\u8d28\u4f18\u5f02\uff0c\u662f\u4e86\u4e0d\u8d77\u7684\u7f8e\u6750\u2026\u2026\u201d \u3000\u3000\u675c\u671b\u6708\u63a5\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u7a76\u7adf\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5c31\u76f4\u622a\u5730\u8bf4\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u201c\u6211\u662f\u8bf4\uff0c\u62ff\u5148\u7236\u5165\u836f\u7684\u4eba\uff0c\u53ef\u80fd\u662f\u4ed6\u540c\u95e8\u4e2d\u4eba\uff0c\u4e0d\u662f\u6211\u7684\u5e08\u4f2f\uff0c\u5c31\u662f\u6211\u7684\u5e08\u53d4\uff0c\u201d\u60dc \u7389\u9053\uff1a\u201c\u662f\u4ed6\u4eec\u8bbe\u8ba1\u5bb3\u4e86\u5148\u7236\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u56db\u51e4\u697c\u4e3b\uff0c\u6839\u53ef\u80fd\u662f\u4ee4\u5c0a\u540c\u95e8\u3002\u201d \u3000\u3000\u60dc\u7389\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f46\u6ca1\u6709\u786e\u5207\u7684\u8bc1\u636e\u4e4b\u524d\uff0c\u6211\u4e0d\u80fd\u4e71\u52a8\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u4e0d\u80fd\u5984\u52a8\u65e0\u540d\uff0c\u6740\u9519\u4eba\uff0c\u662f\u4ee4\u4eba\u9057\u61be\u7684\u5927\u4e8b\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4e5f\u5c06\u4f7f\u4ee4\u5c0a\u6c89\u51a4\u96be \u96ea\uff0c\u542b\u6068\u4e5d\u6cc9\uff01\u4e0d\u8fc7\u2026\u2026\u201d\u7b11\u4e00\u7b11\uff0c\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u5356\u4ec0\u4e48\u5173\u5b50\u561b\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u770b\u4f60\u7b11\u5f97\u597d\u8e4a\u8df7\uff0c\u8ba9\u4eba\u6068\u5f97\u60f3\u54ac\u4f60\u4e24\u53e3\u3002\u201d \u3000\u3000\u201c\u56fa\u6240\u613f\u4e5f\uff0c\u4e0d\u6562\u8bf7\u5c14\u2026\u2026\u201d \u3000\u3000\u60dc\u7389\u706b\u4e86\uff0c\u4e00\u5f20\u5634\u54ac\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u675c\u671b\u6708\u597d\u80c6\u5927\uff0c\u4f38\u624b\u62b1\u4f4f\u4e86\u60dc\u7389\u5a07\u8eaf\uff0c\u4e0d\u6015\u54ac\u554a\uff01 \u3000\u3000\u54ac\u662f\u54ac\u4f4f\u4e86\uff0c\u53ea\u662f\u5730\u65b9\u4e0d\u59a5\u5f53\uff0c\u5634\u54ac\u5634\uff0c\u5c31\u54ac\u7684\u7528\u4e0d\u4e0a\u6c14\u529b\u4e86\u3002 \u3000\u3000\u4e00\u9635\u957f\u543b\u4e4b\u540e\uff0c\u60dc\u7389\u7f13\u7f13\u63a8\u5f00\u4e86\u675c\u671b\u6708\u9053\uff1a\u201c\u8be5\u7559\u4e0b\u4f60\uff0c\u5728\u8fd9\u91cc\u6d1e\u623f\u82b1\u70db\uff0c\u4f60\u662f\u6587\u9601\u9b45 \u9996\uff0c\u975e\u8981\u6211\u4e0d\u53ef\uff0c\u6211\u4e5f\u6ca1\u6709\u7406\u7531\u62d2\u7edd\u4f60\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u675c\u671b\u6708\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u65f6\u673a\u4e0d\u5bf9\uff0c\u6211\u4e0d\u60f3\u903c\u4f60\u5c31\u8303\uff0c\u6211\u8981\u4f60\u5fc3\u7518\u60c5\u613f\uff0c\u50cf\u53ea\u82b1\u8774\u8776\uff0c\u6295 \u5165\u6211\u7684\u6000\u62b1\uff0c\u6211\u8981\u7980\u660e\u603b\u6355\u5934\uff0c\u5a36\u4f60\u4e3a\u59bb\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u7980\u660e\u603b\u6355\u5934\uff0c\u4f60\u6ca1\u6709\u9ad8\u5802\u957f\u8f88\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u8fd8\u662f\u4f60\u81ea\u5df1\u4f5c\u4e0d\u4e86\u4e3b\u5f20\uff1f\u201d\u53c8 \u6709\u70b9\u918b\u610f\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5979\u662f\u9876\u5934\u4e0a\u53f8\u554a\uff01\u5979\u5982\u80fd\u51fa\u9762\uff0c\u8fd9\u573a\u5a5a\u793c\u5c31\u70ed\u95f9\u4e86\u3002\u4e00\u54c1\u5927\u5458\u4e3b \u5a5a\uff0c\u5168\u56fd\u7684\u6355\u5934\u90fd\u53ef\u80fd\u53c2\u52a0\u5927\u5178\uff0c\u8bf4\u4e0d\u5b9a\u7687\u4e0a\u4e5f\u4f1a\u9001\u4e2a\u4ec0\u4e48\u533e\u3001\u724c\u793c\u7269\uff0c\u60dc\u7389\uff0c\u6211\u4e0d\u80fd\u59d4\u5c48 \u4f60\u5440\uff01\u201d \u3000\u3000\u60dc\u7389\u7b11\u4e86\uff0c\u5982\u6b64\u9686\u91cd\u5e84\u4e25\u7684\u5927\u5a5a\uff0c\u53ef\u662f\u5973\u5b69\u5b50\u68a6\u5bd0\u4ee5\u6c42\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u60dc\u7389\u8fd8\u662f\u4fdd\u6301\u4e86\u51b7\u9759\uff0c\u9053\uff1a\u201c\u5509\uff01\u6211\u60f3\u5ac1\u7ed9\u4f60\u7684\u4e8b\uff0c\u538b\u540e\u4e00\u4e9b\uff0c\u62a5\u8fc7\u6211\u6740\u7236\u4e4b\u4ec7\uff0c\u518d \u8c08\u5a5a\u4e8b\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u6f47\u6d12\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u884c\uff01\u592b\u4eba\u600e\u4e48\u8bf4\uff0c\u5728\u4e0b\u5c31\u600e\u4e48\u529e\u3002\u201d \u3000\u3000\u201c\u8c01\u662f\u4f60\u592b\u4eba\u54ea\uff01\u201d\u60dc\u7389\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u5ac1\u8fc7\u53bb\uff0c\u53eb\u592b\u4eba\uff0c\u7b49\u6211\u5ac1\u8fc7\u53bb\u518d\u53eb\u4e0d\u8fdf\uff01\u5509\uff01 \u6211\u65e0\u6cd5\u9884\u6599\u524d\u9014\u6709\u591a\u5c11\u51f6\u9669\uff0c\u627e\u8bc1\u636e\u5df2\u591f\u8270\u82e6\uff0c\u627e\u5230\u4e86\u53c8\u6781\u51f6\u9669\uff0c\u5982\u679c\u6211\u7684\u63a8\u65ad\u6b63\u786e\uff0c\u90a3\u4e00 \u6218\u5fc5\u5c06\u662f\u51f6\u9669\u4e07\u72b6\uff0c\u751f\u6b7b\u96be\u9884\u6599\uff0c\u4f24\u6b8b\u96be\u63a8\u5224\uff0c\u5982\u6211\u4e0d\u5e78\u6b8b\u5e9f\uff0c\u4f60\u8fd8\u4f1a\u8981\u4e00\u4e2a\u7f3a\u817f\u3001\u65ad\u81c2\u7684 \u8001\u5a46\u4e48\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u8981\uff01\u4e0d\u8bba\u4f60\u5982\u4f55\u4f24\u6b8b\uff0c\u6211\u90fd\u4f1a\u5a36\u4f60\u8fc7\u95e8\u2026\u2026\u201d \u3000\u3000\u60dc\u7389\u6709\u70b9\u611f\u52a8\u4e86\uff0c\u6d41\u4e0b\u4e86\u4e24\u884c\u6e05\u6cea\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4e16\u4fd7\u5973\u5b50\uff0c\u65e2\u5df2\u5141\u5a5a\uff0c\u51b3\u4e0d\u6539\u53d8\uff0c\u6211 \u4e5f\u4e0d\u60f3\u5e26\u7740\u6e05\u767d\u8eab\u8eaf\uff0c\u53bb\u89c1\u5148\u7236\uff0c\u6789\u5230\u4eba\u95f4\u8d70\u4e00\u906d\u3002\u6240\u4ee5\uff0c\u884c\u52a8\u4e4b\u524d\uff0c\u6211\u4f1a\u628a\u6e05\u767d\u8eab\u5b50\u4ea4\u7ed9 \u4f60\uff0c\u4e0d\u5e78\u6218\u6b7b\u4e86\uff0c\u4e5f\u6b7b\u7684\u5fc3\u5b89\u7406\u5f97\uff0c\u4e86\u65e0\u9057\u61be\u3002\u53ea\u662f\u73b0\u5728\uff0c\u4f60\u5904\u5883\u5371\u6076\uff0c\u5e94\u4ed8\u8fc7\u5468\u5b37\u5b37\u8fd9\u4e00 \u5173\uff0c\u518d\u8bf4\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4e5f\u5728\u7ec6\u4f5c\u76d8\u7b97\uff0c\u6740\u4e86\u5979\u6700\u5bb9\u6613\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u6211\u8fd8\u8981\u5229\u7528\u5979\u5e26\u6211\u53bb\u89c1\u697c\u4e3b\u3002\u201d\u60dc\u7389\u9053\uff1a\u201c\u4f46\u5979\u8981\u4f60\u5165\u836f\u4e4b\u5fc3\uff0c\u975e\u5e38\u575a\u51b3\u3002\u6240 \u4ee5\uff0c\u6211\u60f3\u5979\u5df2\u5411\u697c\u4e3b\u900f\u9732\u4e86\u4e00\u4e9b\u8baf\u606f\uff0c\u4e0b\u4e0d\u4e86\u53f0\u3002\u6211\u7684\u63a8\u60f3\u662f\u5979\u5348\u540e\u518d\u6765\uff0c\u5f88\u53ef\u80fd\u4e0d\u662f\u5979\u4e00 \u4e2a\u4eba\u4e86\u3002\u5979\u51fa\u624b\u70b9\u4f60\u7a74\u9053\uff0c\u4e5f\u900f\u9732\u51fa\u4e86\u4e00\u4e9b\u8210\u606f\uff0c\u4ee5\u5979\u7684\u4e3a\u4eba\uff0c\u4e0d\u592a\u53ef\u80fd\u5ffd\u7136\u95f4\u53d8\u7684\u8c28\u614e\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5bf9\uff01\u6211\u8be5\u5982\u4f55\u5e94\u5bf9\uff1f\u201d \u3000\u3000\u201c\u8d70\uff01\u201d\u60dc\u7389\u9053\uff1a\u201c\u73b0\u5728\u5c31\u8d70\uff01\u201d \u3000\u3000\u201c\u6211\u4e00\u8d70\u4e86\u4e4b\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4f60\u5462\uff01\u5c82\u4e0d\u8981\u4ee3\u6211\u53d7\u8fc7\uff0c\u5468\u5b37\u5b37\u65e0\u6cd5\u4ea4\u4ee3\uff0c\u4e8b\u5fc5\u4f1a\u8fc1\u6012 \u4e8e\u4f60\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u4ee5\u70b9\u4e86\u6211\u7684\u7a74\u9053\u518d\u8d70\uff01\u201d\u60dc\u7389\u9053\uff1a\u201c\u8981\u5047\u620f\u771f\u505a\uff0c\u8981\u8ba4\u771f\u51fa\u624b\uff0c\u5510\u5b37\u5b37\u4e00\u5b9a\u4f1a\u6765\uff0c \u8fd9\u4e2a\u4eba\u7ec6\u5fc3\u5f02\u5e38\uff0c\u4e0d\u80fd\u7559\u4e0b\u7834\u7efd\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u201c\u7537\u5b50\u6c49\u8981\u5f53\u673a\u7acb\u65ad\uff0c\u7528\u4e0d\u7740\u6df1\u60c5\u6b3e\u6b3e\uff0c\u4e00\u526f\u601c\u9999\u60dc\u7389\u7684\u6837\u5b50\uff01\u201d\u60dc\u7389\u9053\uff1a\u201c\u4f60\u5c3d\u7ba1\u653e \u5fc3\uff0c\u6211\u81ea\u6709\u5e94\u4ed8\u4e4b\u9053\uff0c\u4f60\u5982\u51fa\u624b\u4e0d\u591f\u91cd\uff0c\u90a3\u53cd\u800c\u5bb3\u4e86\u6211\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4f60\u679c\u51b3\u575a\u6bc5\uff0c\u4e0d\u8ba9\u987b\u7709\uff0c\u4f46\u4eca\u65e5\u4e00\u522b\uff0c\u4f55\u65f6\u518d\u89c1\u5462\uff1f\u201d \u3000\u3000\u60dc\u7389\u7b11\u4e86\uff0c\u7b11\u7684\u5982\u82b1\u76db\u653e\uff0c\u9053\uff1a\u201c\u770b\u6765\uff01\u4f60\u4f3c\u4e4e\u662f\u771f\u52a8\u4e86\u60c5\uff0c\u90a3\u4f60\u5c31\u597d\u597d\u542c\u7740\uff0c\u6211\u4e0d\u8981 \u4f60\u5192\u9669\uff0c\u542c\u8bf4\u90a3\u4f4d\u5510\u5b37\u5b37\uff0c\u662f\u56db\u5ddd\u5510\u5bb6\u7684\u4e2b\u5934\uff0c\u4e00\u8eab\u5267\u6bd2\u6697\u5668\uff0c\u975e\u5e38\u53ef\u6015\uff0c\u4e0d\u77e5\u600e\u4e48\u6295\u5165\u4e86 \u697c\u4e3b\u9ebe\u4e0b\u3002\u522b\u4e3a\u6211\u62c5\u5fc3\uff0c\u6211\u7684\u667a\u6167\uff0c\u4e00\u5b9a\u53ef\u4fdd\u6211\u65e0\u4e8b\uff0c\u771f\u7684\u60f3\u89c1\u6211\uff0c\u4e03\u65e5\u540e\uff0c\u5230\u7626\u897f\u6e56\u53bb\u3002 \u7626\u897f\u6e56\u6709\u5ea7\u89c2\u9c7c\u4ead\uff0c\u90a3\u4e00\u6bb5\u4e5f\u662f\u6e56\u6c34\u6700\u6df1\u7684\u6e56\u6bb5\uff0c\u6ce8\u610f\u4e00\u8258\u8001\u65e7\u7684\u9c7c\u8239\uff0c\u8239\u5c31\u5728\u90a3\u6bb5\u6c34\u57df\u6e38 \u8d70\uff0c\u9a76\u8239\u7684\u662f\u4e00\u4e2a\u8001\u4eba\uff0c\u8349\u5c65\u84d1\u8863\uff0c\u9887\u6709\u72ec\u5230\u5bd2\u6c5f\u96ea\u7684\u6c14\u52bf\uff0c\u6211\u65e0\u6cd5\u8bf4\u51fa\u4ed6\u505c\u8239\u7684\u8be6\u7ec6\u5730\u65b9\uff0c \u4f60\u81ea\u5df1\u53bb\u627e\u5427\uff0c\u6211\u4f1a\u4e00\u65e9\u53bb\u8239\u4e0a\u7b49\u4f60\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u70b9\u70b9\u5934\u63a5\u9053\uff1a\u201c\u6211\u4e5f\u7a0d\u52a0\u6539\u626e\uff0c\u51c6\u65f6\u53bb\u89c1\u4f60\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u8981\u6001\u5ea6\u5e84\u91cd\u4e00\u70b9\uff0c\u8001\u6e14\u4eba\u813e\u6c14\u5f88\u5927\uff0c\u4ed6\u662f\u6211\u7684\u4e49\u7236\uff0c\u4e0d\u53ef\u7a0d\u6709\u5f00\u7f6a\u3002\u73b0\u5728\uff0c \u4f60\u70b9\u6211\u7a74\u9053\uff0c\u62b1\u6211\u4e0a\u5e8a\uff0c\u653e\u597d\u6211\uff0c\u76d6\u4e0a\u88ab\u5b50\uff0c\u5c31\u53ef\u4ee5\u8d70\u4e86\uff0c\u7834\u7a97\u800c\u53bb\u5427\uff01\u522b\u8ba9\u4e2b\u5934\u518d\u89c1\u4f60\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u70b9\u70b9\u5934\uff0c\u4e00\u6307\u70b9\u51fa\uff0c\u60dc\u7389\u5e94\u58f0\u800c\u5012\u3002 \u3000\u3000\u51fa\u624b\u76f8\u5f53\u91cd\uff0c\u662f\u771f\u7684\u70b9\u4e86\u6655\u7a74\uff0c\u4f46\u675c\u671b\u6708\u65e9\u5df2\u6709\u5907\uff0c\u4e00\u4f38\u624b\uff0c\u62b1\u4f4f\u4e86\u60dc\u7389\u5012\u4e0b\u7684\u8eab\u5b50\uff0c \u8f7b\u8f7b\u5728\u60dc\u7389\u6a31\u5507\u4e0a\u543b\u4e86\u4e00\u4e0b\uff0c\u653e\u4e0a\u6728\u69bb\uff0c\u76d6\u4e0a\u68c9\u88ab\uff0c\u53c8\u53d6\u51fa\u7ee2\u5e15\uff0c\u5728\u60dc\u7389\u8138\u4e0a\u62c2\u62ed\u4e00\u4e0b\uff0c\u624d \u63a8\u5f00\u540e\u7a97\uff0c\u98de\u8eab\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u884c\u52a8\u5f88\u5c0f\u5fc3\uff0c\u8fd9\u6587\u9601\u7684\u591c\u95f4\uff0c\u867d\u6709\u9ed1\u693f\u57cb\u4f0f\uff0c\u7adf\u88ab\u4ed6\u907f\u4e86\u5f00\u53bb\u3002 \u3000\u3000------------- \u3000\u3000\u5b64\u5251\u751f  \u626b\u63cf\u3000  zerotang  OCR  \uff0c\u65e7\u96e8\u697c\u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c \u4e03 \u7ae0 \u3000\u3000\u8fd9\u662f\u4e2a\u5f88\u5b81\u9759\u7684\u591c\u665a\uff0c\u98d8\u843d\u7740\u8499\u8499\u7684\u7ec6\u96e8\uff0c\u9634\u4e91\u853d\u53bb\u4e86\u661f\u6708\uff0c\u5929\u8272\u5f88\u9ed1\u6697\u3002\u94c1\u7fce\u662f\u5357\u65b9 \u533a\u57df\u7684\u5b88\u5907\u4e3b\u5e05\uff0c\u5e26\u7740\u56db\u5927\u52a9\u624b\uff0c\u9690\u4f0f\u5728\u4e00\u7247\u82b1\u6811\u4e1b\u4e2d\uff0c\u8fd8\u6709\u5341\u516d\u4f4d\uff0c\u80cc\u8d1f\u8bf8\u845b\u5323\u5f29\u7684\u6b66\u58eb\uff0c \u5206\u5e03\u56db\u5468\u3002 \u3000\u3000\u5305\u62ec\u94c1\u7fce\u5728\u5185\uff0c\u6240\u6709\u7684\u4eba\uff0c\u90fd\u7a7f\u7740\u9ed1\u8272\u591c\u884c\u8863\uff0c\u85cf\u5728\u591c\u8272\u6df1\u6c89\u7684\u82b1\u6811\u4e1b\u4e2d\uff0c\u771f\u662f\u6d51\u7136\u5929 \u6210\uff0c\u4e0d\u7740\u4e00\u70b9\u75d5\u8ff9\u3002 \u3000\u3000\u6628\u5929\u665a\u9910\u65f6\u523b\uff0c\u8d3e\u82f1\u4eb2\u7387\u56db\u5927\u526f\u624b\uff0c\u966a\u94c1\u7fce\u5171\u8fdb\u665a\u9910\uff0c\u4e5f\u4ecb\u7ecd\u4e86\u56db\u4eba\u7684\u59d3\u540d\u3001\u6280\u827a\u3002 \u3000\u3000\u4e24\u4e2a\u662f\u6765\u81ea\u897f\u57df\u7684\u9ad8\u624b\uff0c\u90fd\u662f\u56de\u56de\uff0c\u4e5f\u662f\u540c\u5b97\u5144\u5f1f\uff0c\u90fd\u59d3\u9a6c\uff0c\u54e5\u54e5\u53eb\u9a6c\u4fee\uff0c\u5f1f\u5f1f\u53eb\u9a6c\u5f3a\uff0c \u7528\u7684\u5175\u5203\u662f\u5f2f\u6708\u53cc\u5200\uff0c\u53ef\u7528\u56de\u65cb\u4e4b\u529b\uff0c\u4f5c\u98de\u5200\u65bd\u7528\uff0c\u767e\u6b65\u5185\u53d6\u4eba\u9996\u7ea7\uff0c\u662f\u5f88\u96be\u5bf9\u4ed8\u7684\u5175\u5203\uff0c \u4e5f\u662f\u5de8\u578b\u51f6\u5389\u7684\u6697\u5668\u3002 \u3000\u3000\u53e6\u5916\uff0c\u4e24\u4e2a\uff0c\u4e00\u4e2a\u53eb\u5feb\u5200\u738b\u526a\uff0c\u4e00\u4e2a\u94c1\u62f3\u4e25\u65b9\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\uff0c\u53ef\u662f\u6c5f\u5357\u9053\u4e0a\uff0c\u5927\u5927\u6709\u540d\u7684\u4eba\u7269\uff0c\u94c1\u7fce\u867d\u672a\u89c1\u8fc7\uff0c\u53ef\u5df2\u95fb\u540d\u4e45\u77e3\uff01 \u3000\u3000\u5982\u8bba\u6c5f\u6e56\u4e0a\u7684\u58f0\u8a89\u3001\u8eab\u4efd\uff0c\u94c1\u7fce\u4e5f\u53ea\u80fd\u548c\u4eba\u4ee5\u5144\u5f1f\u8bba\u4ea4\uff0c\u4f46\u5728\u8d3e\u82f1\u7684\u5b89\u6392\u4e4b\u4e0b\uff0c\u4e24\u4eba\u7518 \u4e3a\u526f\u624b\uff0c\u800c\u4e14\u5bf9\u94c1\u7fce\u5341\u5206\u5c0a\u91cd\uff0c\u9887\u6709\u5949\u547d\u7ef4\u6743\u7684\u5473\u9053\u3002 \u3000\u3000\u8fd9\u4f7f\u5f97\u94c1\u7fce\u5341\u5206\u5f00\u5fc3\uff0c\u4f46\u4e5f\u987f\u6709\u7740\u8d23\u4efb\u91cd\u5927\u7684\u611f\u89c9\u3002\u53ef\u4e5f\u4f7f\u94c1\u7fce\u5927\u611f\u56f0\u60d1\uff0c\u8d3e\u82f1\u6709\u4ec0\u4e48 \u80fd\u529b\uff0c\u8ba9\u4ed6\u4eec\u4fef\u9996\u542c\u547d\u5462\uff1f \u3000\u3000\u73b0\u5728\uff0c\u738b\u526a\u3001\u4e25\u65b9\uff0c\u5c31\u5206\u5b88\u5728\u94c1\u7fce\u4e24\u4fa7\u3002 \u3000\u3000\u5929\u5165\u4e09\u66f4\uff0c\u5fae\u96e8\u5df2\u6b47\uff0c\u4ecd\u4e0d\u89c1\u6709\u4efb\u4f55\u52a8\u9759\u3002 \u3000\u3000\u8fd9\u662f\u8fdb\u5165\u91d1\u5e9c\u4e2d\u7684\u7b2c\u4e09\u4e2a\u665a\u4e0a\u4e86\uff0c\u94c1\u7fce\u7684\u751f\u6d3b\u4e5f\u5b8c\u5168\u6539\u53d8\uff0c\u767d\u5929\u7761\u89c9\uff0c\u4eba\u591c\u5f53\u73ed\uff0c\u521d\u66f4 \u65f6\u9690\u5165\u82b1\u4e1b\uff0c\u5929\u4eae\u65f6\u56de\u623f\u4f11\u606f\u3002 \u3000\u3000\u94c1\u7fce\u6709\u4e9b\u4e0d\u8010\u4e86\uff0c\u672c\u662f\u516c\u52a1\u7e41\u5fd9\u7684\u603b\u6355\u5934\uff0c\u73b0\u5728\u5374\u5e72\u7684\u662f\u653e\u591c\u54e8\uff0c\u5b88\u6697\u5c97\u7684\u5de5\u4f5c\u3002 \u3000\u3000\u4f46\u89c1\u738b\u526a\u3001\u4e25\u65b9\u6beb\u65e0\u4e0d\u60a6\u4e4b\u8272\uff0c\u94c1\u7fce\u4e5f\u53ea\u6709\u5fcd\u4e0b\u53bb\u4e86\uff0c\u5fc3\u4e2d\u5374\u6697\u81ea\u5fd6\u601d\uff1a\u91d1\u767e\u5e74\u7528\u7684\u4ec0 \u4e48\u65b9\u6cd5\uff0c\u628a\u4e09\u5b9d\u8baf\u606f\u4f20\u51fa\u53bb\u7684\uff1f\u597d\u50cf\u662f\u77f3\u6c89\u5927\u6d77\u5440\uff01\u770b\u6837\u5b50\u771f\u8981\u5728\u8fd9\u91cc\u71ac\u4e0a\u4e03\u4e2a\u591c\u665a\u4e86\uff0c\u98ce \u96e8\u8427\u8427\u4e4b\u591c\uff0c\u5fcd\u53d7\u5343\u868a\u53ee\u2026\u2026\u3002 \u3000\u3000\u7a81\u7136\uff0c\u4e00\u9635\u8f7b\u5fae\u679d\u53f6\u6447\u52a8\u4e4b\u58f0\uff0c\u4f20\u5165\u8033\u9645\uff0c\u4e5f\u6253\u65ad\u4e86\u94c1\u7fce\u7684\u601d\u7eea\u2026\u2026 \u3000\u3000\u738b\u526a\u5df2\u8f7b\u8f7b\u5730\u79fb\u52a8\u8eab\u8eaf\uff0c\u9009\u62e9\u4e86\u4e00\u5904\u89c6\u754c\u8f83\u4e3a\u5bbd\u9614\u7684\u6240\u5728\uff0c\u51dd\u795e\u5411\u5916\u63a2\u89c6\u3002 \u3000\u3000\u4ed6\u5185\u529f\u6df1\u539a\uff0c\u76ee\u529b\u8fc7\u4eba\uff0c\u53c8\u957f\u65f6\u95f4\u9690\u5728\u9ed1\u6697\u4e2d\uff0c\u5df2\u9002\u5e94\u591c\u8272\u7684\u5e7d\u6697\uff0c\u8fd9\u4e00\u5168\u795e\u51dd\u6ce8\u63a2\u89c6\uff0c \u679c\u7136\u770b\u51fa\u4e86\u70b9\u5f81\u5146\u3002 \u3000\u3000\u90a3\u662f\u4e09\u4e08\u5916\uff0c\u4e00\u682a\u679d\u53f6\u5bc6\u8302\u7684\u6995\u6811\u4e0a\uff0c\u5750\u7740\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u90a3\u4eba\u4f3c\u662f\u5df2\u548c\u591c\u8272\u6eb6\u4e3a\u4e00\u4f53\uff0c\u4e0d\u7559\u5fc3\u5f88\u96be\u770b\u5f97\u51fa\u6765\u3002 \u3000\u3000\u80fd\u770b\u51fa\u90a3\u662f\u4e2a\u4eba\uff0c\u4e0d\u4f46\u8981\u4e30\u5bcc\u7684\u9605\u5386\uff0c\u8fd8\u8981\u76f8\u5f53\u7684\u667a\u6167\uff0c\u8981\u7167\u6811\u578b\u63a8\u65ad\uff0c\u90a3\u5730\u65b9\uff0c\u5e94\u8be5 \u662f\u4e00\u4e2a\u7a7a\u9699\uff0c\u5374\u7a81\u7136\u4e3a\u4e00\u4e9b\u4e8b\u7269\u586b\u8865\u4e86\uff0c\u80fd\u7a81\u7136\u51fa\u73b0\u4e8e\u6811\u4e0a\u7a7a\u9699\u4e4b\u5904\u7684\uff0c\u53ea\u6709\u4eba\u3002 \u3000\u3000\u65bd\u5c55\u4f20\u97f3\u4e4b\u672f\uff0c\u628a\u63a8\u5224\u7684\u7ed3\u8bba\uff0c\u544a\u8bc9\u4e86\u94c1\u7fce\u3002 \u3000\u3000\u94c1\u7fce\u7528\u5c3d\u4e86\u76ee\u529b\u770b\uff0c\u7ec8\u4e5f\u53d1\u73b0\u4e86\u90a3\u56e2\u9ed1\u5f71\uff0c\u5374\u5f88\u96be\u80af\u5b9a\u90a3\u662f\u4e2a\u4eba\uff0c\u8bc1\u5b9e\u63a8\u65ad\u6700\u597d\u7684\u529e\u6cd5\uff0c \u662f\u8d70\u8fc7\u53bb\u67e5\u770b\u4e00\u4e0b\u3002 \u3000\u3000\u94c1\u7fce\u6b63\u60f3\u4e0b\u4ee4\u884c\u52a8\uff0c\u90a3\u9ed1\u5f71\u7a81\u7136\u52a8\u4e86\uff0c\u4e00\u4e0b\u5b50\u6ed1\u843d\u6811\u4e0b\uff0c\u6d88\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u738b\u526a\u3001\u4e25\u65b9\uff0c\u90fd\u5f88\u6c89\u5f97\u4f4f\u6c14\uff0c\u4e24\u4eba\u5df2\u4f5c\u52bf\u6b32\u6251\uff0c\u4f46\u94c1\u7fce\u672a\u4f5c\u51b3\u5b9a\uff0c\u4e24\u4eba\u4e5f\u539f\u5730\u672a\u52a8\u3002 \u3000\u3000\u94c1\u7fce\u5728\u60f3\uff0c\u8fd9\u4e9b\u4eba\u6df7\u5165\u4e86\u91d1\u5bb6\u7684\u5927\u5b85\u9662\uff0c\u5374\u9690\u4f0f\u4e0d\u52a8\uff0c\u7528\u5fc3\u4f55\u5728\u5462\uff1f \u3000\u3000\u5f53\u7136\uff0c\u4e0d\u5168\u662f\u4e00\u4e2a\u4eba\uff0c\u4e5f\u8bb8\u6628\u591c\u4ed6\u4eec\u4e5f\u66fe\u7ecf\u8fdb\u5165\u8fc7\u91d1\u5bb6\u5b85\u9662\uff0c\u6084\u7136\u800c\u6765\uff0c\u6084\u7136\u800c\u9000\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u8111\u9645\u95f4\u7075\u5149\u4e00\u95ea\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u7591\u5175\u8ba1\uff01\u5bf9\uff0c\u7591\u5175\u8ba1\uff0c\u6765\u62c9\u7d27\u6211\u4eec\u7684\u795e\u7ecf\uff0c\u738b \u5144\u3001\u4e25\u5144\uff01\u6211\u4eec\u7ed5\u8fc7\u53bb\u3002\u201d \u3000\u3000\u738b\u526a\u3001\u4e25\u65b9\uff0c\u50cf\u4e24\u53ea\u79bb\u5f26\u4e4b\u7bad\uff0c\u75be\u5c04\u800c\u51fa\uff0c\u53ea\u4e00\u8dc3\uff0c\u5df2\u5230\u4e86\u90a3\u682a\u6995\u6811\u4e4b\u4e0b\u3002 \u3000\u3000\u4e00\u9053\u660e\u4eae\u7684\u706f\u5149\uff0c\u4e5f\u7531\u6700\u8fd1\u4e00\u5e62\u4e09\u5c42\u9ad8\u7684\u697c\u9876\u4e0a\u7167\u5c04\u4e0b\u6765\u3002 \u3000\u3000\u539f\u6765\uff0c\u8fd9\u9644\u8fd1\u8fd8\u6709\u591c\u95f4\u641c\u67e5\u654c\u4eba\u7528\u7684\u5b54\u660e\u706f\uff0c\u800c\u4e14\u5149\u7ebf\u5947\u5f3a\uff0c\u6995\u6811\u5468\u56f4\u65b9\u5706\u4e08\u4f59\u7684\u82b1\u4e1b\uff0c \u4e5f\u88ab\u7167\u7684\u7ea4\u6beb\u6bd5\u73b0\u3002 \u3000\u3000\u626c\u5dde\u5e9c\u7684\u603b\u6355\u5934\u94c1\u7fce\uff0c\u5c31\u6ca1\u89c1\u8fc7\u5982\u6b64\u5f3a\u70c8\u7684\u706f\u5149\uff0c\u4e0d\u7981\u6697\u6697\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u8fd9\u91d1\u5e9c \u8bbe\u5907\u4e4b\u597d\uff0c\u5668\u5177\u4e4b\u65b0\uff0c\u4f3c\u90fd\u662f\u5929\u4e0b\u6700\u597d\u7684\u65b0\u5947\u4ea7\u54c1\uff0c\u771f\u662f\u6709\u94b1\u80fd\u4f7f\u9b3c\u63a8\u78e8\u5440\uff01\u8d3e\u82f1\u80fd\u652f\u914d\u6c5f \u6e56\u4e0a\u8bb8\u591a\u9ad8\u624b\uff0c\u4e00\u534a\u4e5f\u662f\u91d1\u767e\u5e74\u7684\u94b1\u4f5c\u602a\u4e86\u3002 \u3000\u3000\u4f46\u53e6\u4e00\u534a\u7684\u529b\u91cf\u5462\uff1f\u8d3e\u82f1\u53c8\u6709\u4f55\u5fb7\u4f55\u80fd\uff0c\u80fd\u652f\u4f7f\u4ed6\u4eec\uff1f\u4e0d\u60f3\u5b83\u4e5f\u5c31\u7f62\u4e86\uff0c\u8fd9\u4e00\u60f3\u554a\uff01\u5c31 \u8d8a\u53d1\u89c9\u7740\u8d3e\u82f1\u7684\u795e\u79d8\u3001\u53ef\u6016\u4e86\u3002 \u3000\u3000\u706f\u706b\u7184\u53bb\uff0c\u4e00\u5207\u90fd\u6062\u590d\u4e86\u539f\u6709\u7684\u9ed1\u6697\u5e7d\u5bc2\u3002 \u3000\u3000\u8fd9\u4e00\u591c\u53d1\u73b0\u4e86\u654c\u8e2a\uff0c\u4f46\u654c\u4eba\u65e2\u672a\u73b0\u8eab\uff0c\u4e5f\u6ca1\u6709\u4efb\u4f55\u4e3e\u52a8\uff0c\u5374\u8bc1\u660e\u4e00\u4ef6\u4e8b\uff0c\u771f\u7684\u662f\u6709\u654c\u4eba \u5230\u4e86\u626c\u5dde\u3002 \u3000\u3000\u5929\u4eae\u6536\u961f\uff0c\u56de\u5230\u4e86\u5c0f\u996d\u5385\u4e2d\uff0c\u996d\u5385\u65e9\u5df2\u6446\u597d\u4e86\u4e30\u76db\u7684\u65e9\u70b9\u3002 \u3000\u3000\u4f7f\u94c1\u7fce\u610f\u5916\u7684\uff0c\u8d3e\u82f1\u548c\u5929\u67ab\u9053\u957f\uff0c\u65e9\u5df2\u5728\u5c0f\u996d\u5385\u4e2d\u7b49\u5019\u3002 \u3000\u3000\u4ed6\u6b63\u8981\u53bb\u89c1\u8d3e\u82f1\uff0c\u4f46\u8d3e\u82f1\u5374\u5148\u4e00\u6b65\u6765\u7b49\u4ed6\u4e86\uff0c\u8fd9\u5c0f\u5b50\u96be\u9053\u4f1a\u7b97\u2026\u2026 \u3000\u3000\u201c\u94c1\u5144\uff0c\u591c\u6765\u98ce\u96e8\u6e7f\u8863\u88f3\uff0c\u8f9b\u82e6\u554a\uff01\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u9053\u957f\u89c9\u7740\u4ee5\u94c1\u5144\u4eca\u65e5\u7684\u8eab\u5206\uff0c\u771f\u662f\u592a \u59d4\u5c48\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\u8bf4\uff0c\u94c1\u67d0\u6628\u591c\u89c1\u5230\u4e86\u654c\u4eba\uff0c\u53ea\u53ef\u60dc\u6ca1\u6709\u7559\u4e0b\u4ed6\uff01\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u4e0d\u80fd\u602a\u94c1\u5144\uff0c\u662f\u5f1f\u5144\u6709\u8bef\u5bfc\u2026\u2026\u201d \u3000\u3000\u201c\u5f53\u65f6\uff0c\u94c1\u67d0\u5ffd\u7136\u60f3\u5230\u4e86\u4e00\u4ef6\u4e8b\uff0c\u56e0\u800c\uff0c\u6ca1\u6709\u4e0b\u4ee4\u8ffd\u88ad\u3002\u201d \u3000\u3000\u201c\u94c1\u5144\u60f3\u5230\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u5144\u5f1f\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u201c\u7591\u5175\u8ba1\uff01\u201d\u94c1\u7fce\u9053\uff1a\u201c\u6211\u60f3\u4e1c\u3001\u5357\u3001\u897f\u3001\u5317\uff0c\u56db\u65b9\u533a\u57df\u4e4b\u5185\uff0c\u90fd\u53ef\u80fd\u6709\u654c\u4eba\u6df7\u4e86\u8fdb\u6765\uff0c \u6216\u662f\u53d1\u73b0\u540e\u4e0d\u6218\u800c\u9000\uff0c\u6216\u662f\u6084\u6765\u6084\u53bb\uff0c\u4ee5\u9020\u6210\u56db\u65b9\u5b88\u52bf\u4e0a\u7684\u7d27\u5f20\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u654c\u4eba\u7684\u9ad8\u624b\u3001\u5f3a\u5c06\uff0c\u65e9\u5df2\u73af\u4f3a\u5728\u4fa7\uff0c\u7b49\u5019\u65f6\u673a\uff0c\u518d\u653b\u4e09\u5b9d\u85cf\u5904\uff0c\u6211\u65b9\u56de\u5175\u6551\u63f4\uff0c \u56db\u65b9\u4e4b\u654c\uff0c\u518d\u4e00\u9f50\u653b\u5165\uff0c\u4f7f\u6211\u5b9e\u529b\u5206\u6563\uff0c\u9996\u5c3e\u4e0d\u80fd\u517c\u987e\uff0c\u65e0\u6cd5\u805a\u96c6\u3002\u5f53\u7136\uff0c\u8fd9\u56db\u65b9\u9762\u7684\u654c\u4eba\uff0c \u4e5f\u53ef\u80fd\u53ea\u4f5c\u7591\u5175\u4e4b\u7528\uff0c\u4e34\u65f6\u518d\u4f5c\u914d\u5408\u8fd0\u7528\u3002\u201d \u3000\u3000\u8d3e\u82f1\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u94c1\u5144\u9ad8\u89c1\uff0c\u94c1\u5144\u89c9\u7740\u4ed6\u4eec\u4f1a\u7528\u51fa\u4ec0\u4e48\u624b\u6bb5\uff0c\u5bf9\u4ed8\u91d1\u5bb6\u5b85\u9662\uff0c\u4e71\u6211\u519b \u5fc3\uff1f\u201d \u3000\u3000\u94c1\u7fce\u5fc3\u4e2d\u6697\u9053\uff1a\u4ee5\u8d3e\u82f1\u7684\u7cbe\u660e\uff0c\u600e\u4f1a\u4e0d\u77e5\uff0c\u8fd9\u5c0f\u5b50\u8bda\u5fc3\u8003\u6211\uff0c\u4e0d\u77e5\u662f\u4f55\u7528\u5fc3\uff1f \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u6700\u53ef\u6015\u7684\u4e00\u79cd\uff0c\u56db\u9762\u653e\u706b\uff0c\u91d1\u5b85\u5e9e\u5927\uff0c\u82b1\u6811\u5982\u6797\uff0c\u4e00\u65e6\u91ce\u706b\u4e71 \u70e7\uff0c\u4e0d\u4f46\u6551\u706b\u4e0d\u6613\uff0c\u4f1a\u8017\u7528\u6211\u4eec\u5927\u6279\u4eba\u529b\uff0c\u9020\u6210\u6df7\u4e71\u4e4b\u5c40\u3002\u8d3e\u5144\u60f3\u662f\u65e9\u6709\u9632\u6b62\u4e4b\u7b56\u4e86\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u6700\u574f\u7684\u60c5\u51b5\u3002\u6240\u4ee5\uff0c\u5144\u5f1f\u7684\u5e03\u7f72\u662f\uff0c\u5168\u529b\u963b\u6740\uff0c\u5404\u533a\u7684\u9632 \u5b88\u4e3b\u529b\u4e0d\u7528\u6295\u5165\u6551\u706b\u884c\u52a8\uff0c\u5168\u529b\u641c\u6740\u654c\u4eba\uff0c\u91d1\u5bb6\u5b85\u9662\u4e2d\uff0c\u8d2d\u7f6e\u4e86\u4e00\u767e\u4e8c\u5341\u4e2a\u8bf8\u845b\u5323\u5f29\uff0c\u662f\u6697 \u5668\u4e2d\u6700\u9738\u9053\u7684\u4e00\u79cd\uff0c\u5b83\u7528\u5f39\u7c27\u53d1\u5c04\uff0c\u5c04\u7a0b\u8fdc\uff0c\u4e00\u5323\u5341\u652f\uff0c\u53ef\u4ee5\u8fde\u7eed\u5c04\u51fa\uff0c\u6740\u4f24\u529b\u975e\u5e38\u5f3a\u5927\uff0c \u5bf9\u4ed8\u4e00\u6d41\u9ad8\u624b\uff0c\u4e5f\u8bb8\u6ca1\u6709\u5927\u7528\uff0c\u4f46\u5bf9\u4ed8\u4e09\u56db\u6d41\u7684\u89d2\u8272\uff0c\u5374\u662f\u5a01\u529b\u5341\u8db3\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5c31\u7b97\u6b66\u529f\u4e0d\u9519\u7684\u9ad8\u624b\uff0c\u4e5f\u5f88\u9003\u8fc7\u4e09\u652f\u5323\u5f29\u7684\u914d\u5408\u653b\u88ad\uff0c\u8fd9\u4e00\u573a\u62da\u6740\uff0c\u5f88\u53ef\u80fd\u4f1a \u9020\u6210\u5f88\u5927\u7684\u4f24\u4ea1\uff0c\u94c1\u5144\u5bf9\u6b64\u4e8b\u6709\u4f55\u6307\u6559\uff1f\u201d \u3000\u3000\u7ed5\u4e86\u534a\u5929\u7684\u5f2f\u5b50\uff0c\u539f\u6765\u662f\u8fd9\u4e48\u56de\u4e8b\uff0c\u8981\u94c1\u7fce\u8868\u793a\u610f\u89c1\uff0c\u662f\u8981\u6c42\u8bc1\u4e00\u4e0b\u5b98\u65b9\u7684\u6001\u5ea6\uff0c\u4e00\u65e6 \u6740\u4f24\u91cd\u5927\uff0c\u5b98\u5e9c\u8981\u5982\u4f55\u5904\u7406\u8fd9\u4ef6\u4e8b\u60c5\uff1f \u3000\u3000\u94c1\u7fce\u50bb\u4e86\uff0c\u4ee5\u6cd5\u8bba\u6cd5\uff0c\u6c5f\u6e56\u4e0a\u5feb\u610f\u6069\u4ec7\uff0c\u6c11\u95f4\u7684\u8fdd\u6cd5\u68b0\u6597\uff0c\u5b98\u5e9c\u4e2d\u7684\u6355\u5feb\uff0c\u5c31\u662f\u8981\u5236\u6b62 \u8fd9\u4e9b\u4e8b\u60c5\u3002 \u3000\u3000\u4f46\u5343\u767e\u5e74\u6765\uff0c\u8fd9\u79cd\u6c5f\u6e56\u6069\u6028\uff0c\u7275\u8fde\u5e7f\u6cdb\uff0c\u9664\u4e86\u4e00\u4e9b\u6d89\u53ca\u5230\u653f\u6743\u671d\u81e3\u7684\u5927\u9634\u8c0b\u5916\uff0c\u6c5f\u6e56\u4e0a \u6b63\u90aa\u4e4b\u95f4\uff0c\u5e2e\u6d3e\u6069\u6028\uff0c\u8d22\u5bcc\u7275\u7f20\uff0c\u6784\u6210\u4e86\u4e00\u79cd\u751f\u6d3b\u9501\u94fe\uff0c\u9690\u542b\u7740\u5fae\u5999\u7684\u6d88\u957f\u4e4b\u673a\uff0c\u53ea\u8981\u4e0d\u6d89 \u53ca\u5584\u826f\u767e\u59d3\u4eba\u5bb6\uff0c\u65e0\u4eba\u51fa\u5934\u62a5\u6848\uff0c\u6355\u5feb\u8859\u5f79\uff0c\u4e5f\u5c31\u773c\u7741\u773c\u95ed\u7684\uff0c\u4e0d\u95fb\u4e0d\u95ee\u3002 \u3000\u3000\u6c89\u541f\u4e86\u826f\u4e45\uff0c\u94c1\u7fce\u624d\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u660e\u706b\u6267\u6756\uff0c\u653b\u5165\u6c11\u5b85\uff0c\u653e\u706b\u884c\u51f6\uff0c\u7f6a\u672c\u8be5\u6b7b\uff0c \u6740\u4e4b\u4f55\u60dc\u3002\u201d \u3000\u3000\u94c1\u603b\u6355\u5934\uff0c\u4e0d\u80fd\u4e0d\u8868\u73b0\u51fa\u4e00\u4e9b\u62c5\u5f53\uff0c\u4f46\u7528\u8bcd\u5df2\u6781\u5c0f\u5fc3\u3002 \u3000\u3000\u8d3e\u82f1\u7b11\u9053\uff1a\u201c\u8bf4\u7684\u662f\uff0c\u91d1\u5b85\u8fbd\u9614\uff0c\u94c1\u5144\u662f\u5357\u65b9\u4e00\u533a\u4e3b\u5e05\uff0c\u6709\u6b64\u4e8b\uff0c\u4e5f\u5c31\u672a\u5fc5\u80fd\u591f\u770b\u5f97\u5230 \u4e86\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u771f\u662f\u59d4\u5c48\u4f60\u94c1\u65bd\u4e3b\u4e86\uff01\u201d \u3000\u3000\u94c1\u7fce\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u51fa\u5bb6\u4eba\uff0c\u5df2\u8df3\u51fa\u4e09\u754c\u5916\uff0c\u4e0d\u5728\u4e94\u884c\u4e2d\uff0c\u5c31\u4e0d\u6015\u624b\u6cbe\u8840\u8165\uff0c\u94c1 \u67d0\u4eba\u6709\u4f60\u8fd9\u79cd\u4e16\u5916\u9ad8\u4eba\u7684\u670b\u53cb\uff0c\u4e0d\u8ba4\u4e5f\u4e0d\u884c\u4e86\u3002\u4e0d\u8fc7\uff0c\u6709\u4ef6\u4e8b\u8981\u8bf4\u660e\u767d\uff0c\u6211\u52a0\u5165\u8fd9\u573a\u640f\u6740\u4e4b \u6218\uff0c\u662f\u8981\u8ffd\u7f09\u6740\u6b7b\u9a6c\u516c\u5b50\u7684\u51f6\u624b\uff0c\u6848\u5b50\u4e0d\u7834\uff0c\u4e8b\u60c5\u4e0d\u80fd\u7b97\u5b8c\uff0c\u8c01\u4e5f\u4e0d\u80fd\u4e2d\u9014\u7f62\u624b\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\u94c1\u5144\u653e\u5fc3\uff0c\u91d1\u59d1\u5a18\u7684\u5acc\u7591\u6d17\u4e0d\u6e05\uff0c\u8d3e\u82f1\u4e5f\u65e0\u6cd5\u5411\u4e1c\u4e3b\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u94c1\u7fce\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8001\u9053\u58eb\uff0c\u4f60\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u5929\u67ab\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ea\u8981\u6211\u8fd8\u80fd\u884c\u52a8\uff0c\u7edd\u4e0d\u4f1a\u4e2d\u9014\u62bd\u817f\uff0c\u96be\u9053\u8fd8\u8981\u6211\u8fd9\u51fa\u5bb6\u4eba\u7acb \u8a93\u4e0d\u6210\uff1f\u201d \u3000\u3000\u201c\u6709\u4f60\u8fd9\u53e5\u660e\u786e\u7684\u8bdd\u5c31\u884c\u4e86\uff0c\u201d\u94c1\u7fce\u7b11\u9053\uff1a\u201c\u8001\u9053\u58eb\u53d1\u8a93\u8d4c\u5492\u7684\u4e8b\uff0c\u8fd8\u672a\u66fe\u542c\u8fc7\uff0c\u514d\u4e86\uff0c \u514d\u4e86\uff01\u201d \u3000\u3000\u8d3e\u82f1\u5bb9\u8272\u4e00\u6574\uff0c\u9053\uff1a\u201c\u91d1\u5e9c\u4e09\u5b9d\uff0c\u5c31\u653e\u5728\u82b1\u56ed\u4e2d\u7684\u2018\u89c2\u9e64\u697c\u2019\u4e0a\uff0c\u90a3\u91cc\u5e94\u8be5\u662f\u626c\u5dde\u57ce\u4e2d \u6700\u9ad8\u7684\u5730\u65b9\uff0c\u697c\u4e0a\u6700\u9ad8\u5904\uff0c\u53eb\u4f5c\u653e\u9e64\u53f0\uff0c\u90a3\u91cc\u91cd\u91cd\u673a\u5173\uff0c\u9664\u4e86\u655d\u4e1c\u4e3b\u4e4b\u5916\uff0c\u8d3e\u67d0\u4e5f\u4e0d\u6562\u6d89 \u8db3\u2026\u2026\u201d \u3000\u3000\u201c\u6162\u6765\uff0c\u6162\u6765\uff01\u201d\u94c1\u7fce\u9053\uff1a\u201c\u662f\u6015\u673a\u5173\u57cb\u4f0f\u4e0d\u6562\u53bb\uff1f\u8fd8\u662f\u91d1\u5e9c\u7981\u5fcc\u4e0d\u80fd\u53bb\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u5410\u51fa\u4e00\u53e3\u957f\u6c14\uff0c\u628a\u4e0a\u5347\u6012\u706b\u538b\u4e86\u4e0b\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u4e1c\u4e3b\u4e0b\u4ee4\uff0c\u4e0d\u5f97\u64c5\u5165\uff0c\u8d3e\u67d0\u4eba\u5c31\u6ca1 \u6709\u673a\u4f1a\u8bd5\u8bd5\u653e\u9e64\u53f0\u5185\u7684\u57cb\u4f0f\u4e86\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u5982\u6b64\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u654c\u4eba\u95ef\u5165\u4e86\u89c2\u9e64\u697c\uff0c\u6211\u4eec\u5982\u4f55\u5f97\u77e5\u5462\uff1f\u5374\u53c8\u5982\u4f55\u65bd\u63f4\uff1f\u201d \u3000\u3000\u201c\u7528\u949f\u58f0\u4f20\u8baf\uff01\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u65bd\u63f4\u7684\u4e8b\uff0c\u7531\u5404\u533a\u4e3b\u5e05\u81ea\u4f5c\u5224\u5b9a\uff0c\u89c9\u5f97\u53ef\u4ee5\u62bd\u8c03\u51fa\u4eba\u529b\u65bd \u63f4\uff0c\u4e5f\u5e0c\u671b\u7559\u4e0b\u4e00\u534a\u529b\u91cf\uff0c\u56fa\u5b88\u9632\u533a\uff0c\u8083\u6e05\u6b8b\u654c\u3002\u201d \u3000\u3000\u94c1\u7fce\u5fc3\u4e2d\u5fd6\u601d\uff1a\u8fd9\u4e2a\u4eba\u8c03\u5ea6\u6709\u65b9\uff0c\u4f3c\u662f\u4e2a\u719f\u8bfb\u7b56\u8c0b\u3001\u6df1\u901a\u5175\u6cd5\u7684\u4eba\u7269\u3002\u8fd9\u4e2a\u4eba\u7a76\u7adf\u662f\u8c01 \u5462\uff1f\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u90a3\u68a6\u5e7b\u4e4b\u5200\uff0c\u662f\u5426\u4f1a\u6765\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\uff0c\u201d\u8d3e\u82f1\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u5929\u67ab\u9053\u957f\u4eb2\u7387\u516d\u4f4d\u6c5f\u6e56\u4e0a\u6700\u64c5\u8ffd\u8e2a\u7684\u9ad8\u624b\uff0c\u67e5\u4e86\u4e03\u65e5 \u4e4b\u4e45\uff0c\u7ebf\u7d22\u5b8c\u5168\u4e2d\u65ad\uff0c\u4ed6\u7684\u4eba\u662f\u5426\u5230\u4e86\u626c\u5dde\uff0c\u90fd\u4e0d\u77e5\u9053\uff0c\u54ea\u91cc\u80fd\u591f\u9884\u6599\u4ed6\u4f1a\u5426\u51fa\u73b0\uff1f\u201d \u3000\u3000\u94c1\u7fce\u5fc3\u4e2d\u6697\u60f3\uff1a\u8fd9\u68a6\u5e7b\u4e4b\u5200\uff0c\u53ef\u7b97\u662f\u6c5f\u6e56\u4e0a\u7b2c\u4e00\u795e\u79d8\u4eba\u7269\u4e86\uff0c\u58f0\u8a89\u4f20\u8bf5\u4e8e\u6c5f\u5357\u9053\u4e0a\uff0c\u540d \u9707\u4e03\u7701\uff0c\u4f46\u771f\u6b63\u80fd\u628a\u4ed6\u7684\u5f62\u8c8c\u63cf\u7ed8\u51fa\u6765\u7684\uff0c\u5374\u65e0\u4eba\u80fd\u8bf4\u7684\u6e05\u695a\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u6ca1\u6709\u4eba\u771f\u6b63\u5730\u89c1 \u8fc7\u4ed6\uff0c\u4f20\u8bf4\u7684\u68a6\u5e7b\u4e4b\u5200\uff0c\u6709\u5982\u4ed6\u5f79\u5200\u6740\u4eba\u7684\u4f20\u8bf4\uff0c\u8fd8\u662f\u4e2a\u4e0d\u89e3\u4e4b\u8c1c\u3002 \u3000\u3000\u201c\u8d2b\u9053\u539f\u672c\u8ba4\u4e3a\u68a6\u5e7b\u4e4b\u5200\uff0c\u4f20\u8bf5\u4e8e\u6c5f\u5357\u9053\u4e0a\u7684\u602a\u5f02\u4e8b\u8ff9\uff0c\u6709\u70b9\u8a00\u8fc7\u5176\u5b9e\uff0c\u81f3\u5c11\uff0c\u8ffd\u51fa\u4ed6 \u7684\u884c\u8e2a\uff0c\u5e94\u8be5\u4e0d\u662f\u592a\u96be\u7684\u4e8b\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e00\u8d9f\u8ffd\u89c5\u884c\u52a8\uff0c\u4f7f\u8d2b\u9053\u611f\u89c9\u5230\uff0c\u518d\u8ffd\u884c\u4e2a\u4e00\u5e74\u534a\u8f7d\uff0c \u4e5f\u627e\u4e0d\u51fa\u68a6\u5e7b\u4e4b\u5200\u7684\u884c\u8e2a\uff0c\u800c\u4e14\uff0c\u8fd9\u79cd\u611f\u89c9\u975e\u5e38\u7684\u660e\u663e\uff0c\u6240\u4ee5\uff0c\u7f62\u624b\u4e0d\u8ffd\u4e86\u2026\u2026\u201d \u3000\u3000\u4e00\u4ee3\u5251\u672f\u540d\u5bb6\u7684\u5929\u67ab\u9053\u957f\uff0c\u5982\u6b64\u5730\u81ea\u706d\u5a01\u98ce\uff0c\u4f7f\u94c1\u7fce\u5927\u611f\u610f\u5916\uff0c\u6697\u9053\uff1a\u8001\u9053\u58eb\u8fd9\u4e00\u6b21\u7684 \u632b\u6298\u611f\uff0c\u4f3c\u662f\u5f88\u91cd\uff0c\u7a76\u7adf\u9047\u4e0a\u4e86\u4ec0\u4e48\u4e8b\u60c5\u5462\uff1f \u3000\u3000\u5929\u67ab\u9053\u957f\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u8981\u89c1\u68a6\u5e7b\u4e4b\u5200\uff0c\u6700\u597d\u662f\u968f\u7f18\u4e86\uff0c\u7528\u4e0d\u7740\u523b\u610f\u53bb\u627e\u4ed6\uff0c \u90a3\u4f1a\u82b1\u8d39\u592a\u591a\u7684\u65f6\u95f4\uff0c\u53c8\u672a\u5fc5\u6709\u6548\u3002\u201d \u3000\u3000\u201c\u795e\u79d8\u5230\u884c\u52a8\u5982\u98ce\uff0c\u4e0d\u7559\u75d5\u8ff9\uff0c\u7684\u786e\u662f\u6709\u4e9b\u53ef\u6016\uff0c\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u4f46\u4e5f\u80fd\u6fc0\u8d77\u4eba\u5f3a\u70c8\u7684\u6597 \u5fd7\u3002\u4e0d\u8fc7\uff0c\u73b0\u5728\uff0c\u7528\u4e0d\u7740\u628a\u65f6\u95f4\u6d6a\u8d39\u5728\u8fd9\u4ef6\u4e8b\u4e0a\uff0c\u6765\u65e5\u65b9\u957f\uff0c\u8d3e\u67d0\u4eba\u5c31\u4e0d\u76f8\u4fe1\uff0c\u68a6\u5e7b\u4e4b\u5200\uff0c \u662f\u4e00\u4e2a\u4e0d\u80fd\u89e3\u5f00\u7684\u795e\u79d8\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\uff01\u6211\u4eec\u627e\u7684\u662f\u6740\u4eba\u51f6\u624b\uff01\u53c8\u4f55\u82e6\u6c89\u8ff7\u4e8e\u8ffd\u89c5\u4e00\u4e2a\u4eba\u7684\u884c\u8e2a\u5462\uff1f\u201d \u3000\u3000\u201c\u94c1\u5144\u8bf4\u7684\u5bf9\uff01\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u738b\u526a\u3001\u4e25\u65b9\uff0c\u662f\u6c5f\u5357\u4e00\u5e26\u5e74\u8f7b\u4e00\u8f88\u4e2d\uff0c\u9ad8\u624b\u4e2d\u7684\u9ad8\u624b\uff0c\u96be \u5f97\u7684\u662f\u806a\u660e\u548c\u5e94\u53d8\u7684\u80fd\u529b\uff0c\u4ea6\u5c5e\u4e00\u6d41\uff0c\u662f\u4e24\u4e2a\u53ef\u7528\u4e4b\u624d\uff0c\u5144\u5f1f\u7279\u522b\u63f4\u5165\u94c1\u5144\u624b\u4e0b\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u70b9\uff0c\u94c1\u67d0\u5f88\u611f\u6fc0\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u5982\u8bba\u6c5f\u6e56\u8eab\u5206\uff0c\u6211\u548c\u4ed6\u4eec\u53ea\u662f\u5728\u4f2f\u3001\u4ef2\u95f4\u2026\u2026\u201d \u3000\u3000\u8d3e\u82f1\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u70b9\u8bf4\u660e\uff0c\u4e00\u65e6\u91d1\u949f\u4f20\u97f3\u6025\u4e71\uff0c\u90a3\u5c31\u53ef\u80fd\u662f\u653e\u9e64\u697c\u9677\u5165\u5371\u5883\uff0c\u5c31 \u8981\u5c3d\u53ef\u80fd\u62bd\u51fa\u4eba\u624b\uff0c\u8d76\u63f4\u89c2\u9e64\u697c\u4e86\uff0c\u5982\u6709\u7279\u522b\u9700\u8981\u94c1\u5144\u5e2e\u52a9\u4e4b\u5904\uff0c\u5144\u5f1f\u4f1a\u6d3e\u4eba\u53e6\u4f5c\u901a\u77e5\uff0c\u73b0 \u5728\u544a\u9000\u3002\u201d \u3000\u3000\u7ad9\u8d77\u6765\u8f6c\u8eab\u800c\u53bb\uff0c\u5929\u67ab\u9053\u957f\u7adf\u4e5f\u8d77\u8eab\uff0c\u4e00\u8bed\u672a\u53d1\uff0c\u968f\u540c\u800c\u53bb\u3002 \u3000\u3000\u94c1\u7fce\u6ca1\u6709\u9001\u5ba2\uff0c\u5fc3\u4e2d\u5374\u5728\u7422\u78e8\u7740\u8d3e\u82f1\u6700\u540e\u51e0\u53e5\u8bdd\u7684\u542b\u610f\uff0c\u89c2\u9e64\u697c\u9677\u5165\u5371\u9669\uff0c\u5e94\u8be5\u662f\u6307\u4e09 \u5b9d\u5df2\u88ab\u4eba\u62a2\u593a\u800c\u53bb\uff0c\u8fd8\u662f\u522b\u6709\u6240\u6307\uff0c\u91d1\u949f\u6025\u4e71\uff0c\u5e94\u8be5\u662f\u8bf4\u6307\u6325\u7684\u67a2\u7ebd\uff0c\u5df2\u53d7\u5230\u5371\u5bb3\uff0c\u5927\u529b\u56de \u63f4\uff0c\u662f\u5f03\u8f66\u4fdd\u5e05\u4e86\uff0c\u5e94\u8be5\u6307\u7684\u662f\u91d1\u767e\u5e74\u624d\u5bf9\uff0c\u8fd9\u4e48\u63a8\u65ad\u8d77\u6765\uff0c\u91d1\u767e\u5e74\u4f1a\u4e00\u76f4\u5750\u9547\u653e\u9e64\u697c\uff0c\u4eb2 \u81ea\u51b3\u5b9a\u5927\u8ba1\u4e86\uff0c\u7231\u5973\u4e4b\u5fc3\uff0c\u8868\u9732\u65e0\u9057\u2026\u2026\u3002 \u3000\u3000\u7ecf\u8fc7\u4e86\u4e00\u5929\u7761\u7720\u4f11\u606f\uff0c\u94c1\u7fce\u7cbe\u795e\u5c3d\u590d\uff0c\u7a7f\u7740\u91d1\u5e9c\u7279\u5236\u7684\u6b66\u58eb\u88c5\uff0c\u6b65\u5165\u91d1\u5e9c\u7684\u82b1\u56ed\u4e4b\u4e2d\u3002 \u82b1\u56ed\u5f88\u5927\uff0c\u767e\u82b1\u4ea4\u690d\uff0c\u7528\u5fc3\u5728\u4f7f\u56ed\u4e2d\u7ecf\u5e38\u6709\u82b1\u76db\u653e\uff0c\u88ad\u4eba\u7684\u82b1\u6c14\uff0c\u4e0d\u81f4\u4e2d\u65ad\u3002 \u3000\u3000\u5728\u7fa4\u82b1\u73af\u7ed5\u4e4b\u4e2d\uff0c\u8038\u7acb\u8d77\u4e00\u5ea7\u9ad8\u697c\uff0c\u4f3c\u662f\u6de1\u9752\u8272\u77f3\u5757\u780c\u6210\uff0c\u975e\u5e38\u6574\u9f50\uff0c\u697c\u7684\u9020\u578b\uff0c\u4e5f\u5fae \u5411\u5185\u6536\u7f29\uff0c\u60f3\u662f\u6c42\u5176\u7a33\u56fa\u3002 \u3000\u3000\u9ad8\u697c\u5468\u56f4\u7684\u82b1\u6811\uff0c\u90fd\u88ab\u94f2\u53bb\uff0c\u56db\u5468\u7a7a\u51fa\u4e86\u4e00\u5927\u7247\u571f\u5730\uff0c\u8fd8\u94fa\u4e86\u767d\u8272\u77f3\u7070\u7816\uff0c\u4e0d\u4f46\u662f\u4e00\u5904 \u51b3\u6b7b\u7684\u597d\u6218\u573a\uff0c\u4e5f\u6709\u9632\u4eba\u63a5\u8fd1\u7684\u4f5c\u7528\uff0c\u5730\u4e0a\u4e00\u9762\u767d\uff0c\u4eba\u7269\u5c31\u6e05\u6670\u53ef\u89c1\u4e86\u3002 \u3000\u3000\u6d4f\u89c8\u82b1\u56ed\u4e00\u5468\uff0c\u4e5f\u770b\u51fa\u4e86\u56ed\u4e2d\u51e0\u5904\u85cf\u8eab\u4e4b\u5904\uff0c\u8fd9\u82b1\u56ed\u4e2d\u4e5f\u65e9\u6709\u4e86\u9632\u5b88\u7684\u5b89\u6392\u3002 \u3000\u3000\u672c\u662f\u4e00\u5ea7\u8bbe\u8ba1\u60a6\u76ee\u7684\u82b1\u56ed\uff0c\u4f46\u56e0\u50fb\u51fa\u4e86\u4e00\u7247\u4e3b\u5730\uff0c\u7834\u574f\u4e86\u6574\u4f53\u4e4b\u7f8e\uff0c\u770b\u4e0a\u53bb\u5c31\u6709\u70b9\u7f3a\u61be \u4e86\u3002 \u3000\u3000\u591c\u5e55\u5782\u4e0b\u65f6\uff0c\u94c1\u7fce\u4e5f\u8fdb\u5165\u4e86\u8d1f\u8d23\u5b88\u62a4\u7684\u91d1\u5bb6\u5357\u9662\u3002 \u3000\u3000\u5feb\u5200\u738b\u526a\u3001\u94c1\u62f3\u4e25\u65b9\uff0c\u9a6c\u4fee\u3001\u9a6c\u5f3a\uff0c\u56db\u5927\u526f\u624b\u65e9\u5df2\u5728\u5ead\u9662\u4e2d\u7b49\u5019\u3002 \u3000\u3000\u201c\u4eca\u591c\u98ce\u4f4f\u96e8\u6b47\uff0c\u591c\u8272\u6e05\u65b0\uff0c\u201d\u738b\u526a\u9053\uff1a\u201c\u521d\u66f4\u4e4b\u540e\uff0c\u5c06\u5347\u8d77\u4e00\u8f6e\u660e\u6708\uff0c\u53d1\u73b0\u654c\u8e2a\u7684\u673a \u4f1a\uff0c\u5c31\u5927\u4e3a\u589e\u5f3a\uff0c\u4e0d\u77e5\u8be5\u5982\u4f55\u5e94\u5bf9\uff1f\u5982\u80fd\u5148\u5f97\u4e3b\u5e05\u6307\u793a\uff0c\u53ef\u514d\u53bb\u9519\u5931\u5148\u673a\u7684\u8bef\u5dee\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u94c1\u7fce\u9053\uff1a\u201c\u56db\u4f4d\u5404\u51ed\u624d\u827a\uff0c\u81ea\u884c\u5e94\u53d8\uff0c\u80fd\u591f\u81ea\u5df1\u5904\u7f6e\uff0c\u5c31\u7528\u4e0d\u7740\u518d\u884c\u8bf7\u793a\uff0c\u800c \u4e14\u51fa\u624b\u4e5f\u4e0d\u7528\u7559\u60c5\u3002\u201d \u3000\u3000\u738b\u526a\u5fae\u4e00\u6b20\u8eab\uff0c\u9053\uff1a\u201c\u656c\u5c0a\u5429\u5490\u3002\u201d\u4e00\u4e2a\u5927\u8f6c\u8eab\uff0c\u8eab\u5982\u63a0\u6ce2\u6d77\u71d5\uff0c\u98de\u5165\u4e09\u4e08\u5916\u4e00\u682a\u5927\u6995 \u6811\u4e0a\uff0c\u9690\u4eba\u6c89\u5bc6\u679d\u53f6\u4e1b\u4e2d\u3002 \u3000\u3000\u90a3\u6b63\u662f\u4e0a\u4e00\u591c\u654c\u4eba\u51fa\u73b0\u4e4b\u5904\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u73af\u7acb\u5728\u94c1\u7fce\u8eab\u4fa7\u7684\u4e25\u65b9\u3001\u9a6c\u4fee\u3001\u9a6c\u5f3a\uff0c\u4e09\u4e2a\u526f\u624b\uff0c\u4e5f\u540c\u65f6\u9690\u4eba\u4e86\u82b1\u8349\u4e1b\u4e2d\u3002 \u3000\u3000\u53ea\u662f\uff0c\u4eca\u591c\u5f62\u52bf\uff0c\u62da\u6740\u7684\u6c14\u52bf\uff0c\u5341\u5206\u6d53\u70c8\uff0c\u56db\u4eba\u9009\u62e9\u7684\u9690\u8eab\u6240\u5728\uff0c\u90fd\u662f\u63a5\u8fd1\u5ead\u9662\u56f4\u5899\u4e4b \u5904\uff0c\u770b\u6837\u5b50\uff0c\u53ea\u8981\u654c\u4eba\u4e00\u5165\u5ead\u9662\uff0c\u5c31\u51c6\u5907\u51fa\u624b\u622a\u6740\u4e86\u3002 \u3000\u3000\u94c1\u7fce\u9009\u62e9\u4e86\u5357\u697c\u9876\u4e0a\u4e00\u5904\u5c4b\u810a\uff0c\u53ef\u4ee5\u76d1\u770b\u5230\u5357\u533a\u5927\u90e8\u5206\u7684\u5730\u7406\u5f62\u52bf\uff0c\u654c\u6211\u6d3b\u52a8\u3001\u5f62\u52bf\u53d8 \u5316\uff0c\u5c3d\u5165\u76ee\u4e2d\u3002 \u3000\u3000\u5929\u8272\u53d8\u5316\uff0c\u679c\u5982\u738b\u526a\u6240\u8a00\uff0c\u521d\u66f4\u4e4b\u540e\uff0c\u4e1c\u65b9\u5929\u9645\u9001\u51fa\u4e86\u4e00\u8f6e\u660e\u6708\u3002 \u3000\u3000\u91d1\u5bb6\u5b85\u9662\u4e2d\uff0c\u4ecd\u7136\u672a\u70b9\u706f\u706b\uff0c\u4f46\u660e\u6708\u7167\u82b1\u6811\uff0c\u666f\u7269\u6e05\u660e\u53ef\u89c1\u3002 \u3000\u3000\u5982\u6b64\u660e\u6708\u591c\uff0c\u5b9e\u975e\u591c\u884c\u4eba\u6d3b\u52a8\u7684\u65f6\u673a\uff0c\u4f46\u9632\u5b88\u5b85\u9662\u7684\u4eba\u624b\uff0c\u4ea6\u5bb9\u6613\u66b4\u9732\u51fa\u85cf\u8eab\u6240\u5728\uff0c\u5982 \u679c\u654c\u4eba\u6709\u7740\u4e8b\u524d\u7684\u7f16\u7ec4\u884c\u52a8\uff0c\u5f3a\u5927\u7684\u7edf\u9a6d\u529b\u91cf\uff0c\u6709\u529b\u7684\u6307\u6325\u7cfb\u7edf\uff0c\u660e\u6708\u4e4b\u591c\uff0c\u5bf9\u53cc\u65b9\u4e00\u822c\u7684 \u6709\u5229\u6709\u5bb3\u4e86\u3002\u68a6\u5e7b\u4e4b\u5200\uff0c\u672a\u89c1\u73b0\u8eab\uff0c\u4ec0\u4e48\u4eba\u80fd\u53ec\u96c6\u7eff\u6797\u9053\u4e0a\u9ad8\u624b\uff0c\u7ec4\u6210\u4e00\u4e2a\u5f3a\u5927\u6709\u529b\u7684\u593a\u5b9d \u96c6\u56e2\u5462\uff1f\u65e2\u672a\u628a\u5b98\u5e9c\u6355\u5feb\u653e\u5728\u773c\u4e2d\uff0c\u4e5f\u4e0d\u987e\u5fcc\u626c\u5dde\u5341\u5927\u5bcc\u8c6a\u7684\u62a4\u9662\u5b9e\u529b\u3002 \u3000\u3000\u8fd9\u5341\u5927\u5bb6\u65cf\u62e5\u6709\u7684\u5f3a\u5927\u5b9e\u529b\uff0c\u8db3\u53ef\u548c\u5f53\u4ee4\u4e5d\u5927\u95e8\u6d3e\u4f1a\u805a\u7684\u529b\u91cf\u6297\u8861\uff01 \u3000\u3000\u5f53\u4eca\u6c5f\u5357\u9053\u4e0a\uff0c\u8c01\u6709\u8fd9\u4e2a\u80fd\u529b\uff0c\u8c01\u6709\u8fd9\u4e2a\u5a01\u671b\uff0c\u638c\u63e1\u4e86\u8fd9\u6279\u9ad8\u624b\u2026\u2026\u3002 \u3000\u3000\u94c1\u7fce\u60f3\u7684\u5934\u90fd\u75bc\u4e86\uff0c\u8fd8\u662f\u60f3\u4e0d\u51fa\u90a3\u4e48\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u626c\u5dde\u867d\u7136\u662f\u9f99\u86c7\u6742\u5904\uff0c\u4f1a\u805a\u4e86\u6c5f\u6e56\u4e0d\u5c11\u9ad8\u624b\uff0c\u4f46\u8fd9\u4e9b\u4eba\u5927\u90fd\u4f9d\u9644\u5728\u5341\u5927\u5bcc\u8c6a\u4e4b\u5bb6\uff0c\u4ea6\u6709 \u72ec\u5c45\u4e00\u5e62\u623f\u820d\u4e4b\u5185\uff0c\u95ed\u95e8\u8c22\u5ba2\uff0c\u6df1\u5c45\u7b80\u51fa\uff0c\u6709\u6240\u884c\u52a8\uff0c\u4e5f\u662f\u5728\u7edd\u5bf9\u9690\u79d8\u4e4b\u4e2d\u3002 \u3000\u3000\u8868\u9762\u4e0a\u770b\u53bb\uff0c\u626c\u5dde\u57ce\u4e00\u76f4\u5f88\u5e73\u9759\uff0c\u627e\u4e0d\u51fa\u4e00\u4e2a\u6709\u7ec4\u7ec7\u7684\u5e2e\u4f1a\uff0c\u5176\u5b9e\u6697\u7901\u5904\u5904\uff0c\u51f6\u9669\u91cd\u91cd\uff0c \u9634\u8c0b\u90fd\u5728\u6697\u4e2d\u8fdb\u884c\u3002 \u3000\u3000\u91d1\u94b1\u5806\u51fa\u4e86\u8000\u773c\u7684\u7e41\u534e\uff0c\u4e5f\u9020\u6210\u53ef\u6015\u7684\u7f6a\u6076\u6e90\u5934\u3002 \u3000\u3000\u5e72\u4e86\u4e09\u5e74\u7684\u626c\u5dde\u603b\u6355\u5934\uff0c\u94c1\u7fce\u5374\u5728\u8fd9\u77ed\u77ed\u6570\u65e5\u4e2d\uff0c\u624d\u4f53\u4f1a\u5230\u8fd9\u91cc\u7684\u771f\u6b63\u9762\u76ee\uff0c\u9053\u9ad8\u4e00\u5c3a\uff0c \u9b54\u9ad8\u4e00\u4e08\uff0c\u5b98\u5e9c\u770b\u5230\u7684\uff0c\u53ea\u4e0d\u8fc7\u5341\u4e4b\u4e00\u4e8c\u2026\u2026\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u4e09\u58f0\u6e05\u8d8a\u7684\u949f\u58f0\uff0c\u4f20\u5165\u4e86\u8033\u9645\u3002 \u3000\u3000\u8fd9\u8868\u793a\u6709\u654c\u4eba\u63a5\u8fd1\u4e86\u201c\u89c2\u9e64\u697c\u201d\u3002 \u3000\u3000\u94c1\u7fce\u5fc3\u4e2d\u5fd6\u9053\uff1a\u672a\u89c1\u4e1c\u3001\u897f\u3001\u5317\u65b9\u4f20\u5165\u8b66\u53f7\uff0c\u600e\u4e48\u4e00\u4e0b\u5b50\u5c31\u6709\u654c\u4eba\u63a5\u8fd1\u4e86\u89c2\u9e64\u697c\uff0c\u662f\u4ece \u54ea\u4e2a\u65b9\u4f4d\u8fc7\u53bb\u7684\uff1f\u2026\u2026 \u3000\u3000\u4f46\u95fb\u4e00\u58f0\u5389\u559d\u9053\uff1a\u201c\u7559\u4e0b\u547d\u6765\uff0c\u201d\u662f\u738b\u526a\u7684\u58f0\u97f3\uff0c\u6b64\u4eba\u7cbe\u660e\u5e72\u7ec3\uff0c\u8033\u76ee\u806a\u654f\uff0c\u5e03\u5b88\u76d1\u89c6 \u54e8\uff0c\u5c1a\u672a\u53d1\u89c9\u654c\u4eba\uff0c\u4f46\u738b\u526a\u53d1\u89c9\u4e86\u3002 \u3000\u3000\u968f\u7740\u90a3\u58f0\u5927\u559d\uff0c\u4e00\u6761\u9ed1\u8863\u4eba\u5f71\uff0c\u7531\u82b1\u6811\u4e1b\u4e2d\u98de\u817e\u800c\u8d77\uff0c\u843d\u7740\u5b9e\u5730\u3002 \u3000\u3000\u867d\u662f\u660e\u6708\u5f53\u5934\uff0c\u4f46\u5728\u90a3\u77ac\u606f\u4e4b\u95f4\uff0c\u4e5f\u65e0\u6cd5\u5206\u6e05\u654c\u6211\uff0c\u5206\u5b88\u5728\u82b1\u6811\u4e1b\u4e2d\u7684\u5323\u5f29\uff0c\u8fd8\u672a\u53ca\u53d1 \u5c04\uff0c\u4e00\u9053\u5bd2\u8679\u5df2\u7535\u5c04\u800c\u81f3\u3002 \u3000\u3000\u5bd2\u5149\u6765\u52bf\u592a\u5feb\uff0c\u9ed1\u8863\u4eba\u88ab\u8feb\u4e3e\u5200\u5c01\u67b6\u3002 \u3000\u3000\u8ffd\u88ad\u4e4b\u4eba\uff0c\u6b63\u662f\u738b\u526a\uff0c\u679c\u662f\u5200\u51fa\u5982\u95ea\u7535\uff0c\u5341\u5206\u7684\u5feb\u901f\uff0c\u88ab\u4eba\u8a89\u79f0\u5feb\u5200\uff0c\u5f53\u4e4b\u65e0\u6127\u3002 \u3000\u3000\u4e24\u4eba\u5200\u52bf\u4e00\u63a5\uff0c\u738b\u526a\u7684\u5200\u6cd5\uff0c\u6a2a\u65a9\u7eb5\u5288\uff0c\u8fde\u7eed\u4e03\u5200\uff0c\u8feb\u7684\u654c\u4eba\u5168\u65e0\u8fd8\u624b\u4e4b\u529b\u3002 \u3000\u3000\u4f46\u90a3\u9ed1\u8863\u4eba\u4ea6\u975e\u5f31\u8005\uff0c\u867d\u7136\u6ca1\u6709\u8fd8\u624b\u4e4b\u529b\uff0c\u4f46\u4ecd\u7136\u628a\u4e03\u5200\u5c01\u67b6\u5f00\u53bb\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u738b\u526a\u7684\u5200\u5feb\uff0c\u4f46\u63e1\u5200\u4e4b\u624b\uff0c\u6c89\u7a33\u6709\u529b\uff0c\u5200\u4e0a\u84c4\u52bf\u751a\u5f3a\uff0c\u6297\u62d2\u738b\u526a\u4e00\u8f6e\u5feb\u653b\uff0c\u7ad9\u7684 \u5f88\u7a33\uff0c\u672a\u79fb\u534a\u6b65\u3002 \u3000\u3000\u53ea\u89c1\u4eba\u5f71\u95ea\uff0c\u4e25\u65b9\u3001\u9a6c\u4fee\u3001\u9a6c\u5f3a\uff0c\u5206\u7531\u82b1\u6811\u4e1b\u4e2d\u98de\u8dc3\u800c\u51fa\uff0c\u5f88\u81ea\u7136\u5730\u5e03\u6210\u4e86\u9632\u5835\u4e4b\u52bf\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5c31\u7b97\u90a3\u9ed1\u8863\u4eba\u80fd\u5c01\u5f00\u738b\u526a\u7684\u5feb\u5200\uff0c\u4e5f\u5f88\u96be\u8131\u51fa\u56f4\u56f0\u4e86\u3002\u56e0\u4e3a\u53bb\u8def\u5df2\u88ab\u4e09\u4eba\u5835\u6b7b\uff0c \u4e00\u9762\u6709\u7a7a\u9699\uff0c\u6b63\u662f\u5f29\u7bad\u5c01\u9501\u7684\u65b9\u4f4d\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u4f3c\u662f\u4e45\u8d70\u6c5f\u6e56\u7684\u4eba\u7269\uff0c\u5bf9\u7559\u6709\u7a7a\u9699\u7684\u65b9\u4f4d\uff0c\u5b58\u5fc3\u89c4\u907f\uff0c\u4f3c\u5df2\u77e5\u9053\u90a3\u662f\u4e00\u4e2a\u9677\u9631\u3002 \u3000\u3000\u94c1\u7fce\u4e5f\u98de\u843d\u73b0\u573a\uff0c\u7ad9\u5728\u4e24\u4e08\u8ddd\u79bb\u4e4b\u5916\uff0c\u4ed6\u8981\u77a7\u6e05\u695a\u9ed1\u8863\u4eba\u5200\u6cd5\u6765\u8def\u3002\u5f53\u7136\uff0c\u4e5f\u60f3\u4e00\u7779\u738b \u526a\u5feb\u5200\u7684\u5a01\u529b\uff0c\u5bf9\u738b\u526a\u7684\u5feb\u5200\uff0c\u95fb\u540d\u5df2\u4e45\uff0c\u4f46\u770b\u4ed6\u4e34\u9635\u51fa\u5200\uff0c\u8fd8\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\uff0c\u81ea\u4e0d\u80fd\u9519\u5931 \u826f\u673a\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u8499\u4e86\u8138\uff0c\u4e0d\u80fd\u5206\u8fa8\u51fa\u4ed6\u7684\u8eab\u5206\uff0c\u4f46\u5200\u6cd5\u7cbe\u719f\uff0c\u80fd\u968f\u7740\u738b\u526a\u7684\u5feb\u5200\u653b\u52bf\uff0c\u968f\u673a\u5e94\u53d8\uff0c \u653e\u5f03\u4e86\u88ab\u56f4\u800c\u53bb\u7684\u6253\u7b97\u4e4b\u540e\uff0c\u66f4\u662f\u5168\u529b\u8fd0\u5200\uff0c\u5b88\u7684\u98ce\u96e8\u4e0d\u900f\u3002 \u3000\u3000\u738b\u526a\u7684\u5200\u6cd5\u53d8\u5316\u5360\u5c3d\u4e86\u4e00\u4e2a\u5feb\u5b57\u8bc0\uff0c\u94c1\u7fce\u8fd8\u662f\u7b2c\u4e00\u6b21\u770b\u5230\u5982\u6b64\u7684\u5feb\u901f\u5200\u6cd5\uff0c\u5177\u6709\u4e86\u51db\u51bd \u7684\u6740\u4eba\u529f\u80fd\u3002 \u3000\u3000\u4f46\u90a3\u9ed1\u8863\u4eba\u5200\u6cd5\u624e\u5b9e\uff0c\u6b63\u4e2d\u8574\u5947\uff0c\u518d\u52a0\u4e0a\u4e0d\u6c42\u6709\u529f\u7684\u6253\u6cd5\uff0c\u5168\u4ee5\u9632\u5b88\u4e3a\u4e3b\uff0c\u738b\u526a\u7684\u5feb\u5200\uff0c \u4e5f\u5c31\u65e0\u5948\u4ed6\u4f55\u4e86\u3002 \u3000\u3000\u4e25\u65b9\u548c\u738b\u526a\u642d\u6321\u591a\u5e74\uff0c\u77e5\u9053\u4ed6\u6740\u4eba\u7684\u80fd\u529b\uff0c\u5e94\u8be5\u5728\u4e00\u767e\u62db\u4e4b\u5185\uff0c\u8d85\u8fc7\u6b64\u9650\uff0c\u5c31\u9677\u5165\u4e86\u82e6 \u6218\uff0c\u5f53\u4e0b\u5927\u559d\u4e00\u58f0\uff0c\u6325\u62f3\u51fb\u51fa\u3002 \u3000\u3000\u4ed6\u53f7\u79f0\u94c1\u62f3\uff0c\u8fd9\u4e00\u62f3\u529b\u9053\u5f3a\u731b\uff0c\u6709\u5982\u94c1\u9524\u51fb\u5ca9\u4e00\u822c\uff0c\u76f4\u649e\u8fc7\u53bb\uff0c\u62f3\u98ce\u5148\u81f3\uff0c\u649e\u5728\u4e86\u9ed1\u8863 \u4eba\u7684\u63e1\u5200\u53f3\u81c2\u4e4b\u4e0a\uff0c\u9ed1\u8863\u4eba\u5200\u52bf\u4e00\u504f\uff0c\u6a2a\u79fb\u534a\u5c3a\u3002 \u3000\u3000\u591f\u4e86\uff0c\u5c31\u8fd9\u534a\u5c3a\u7a7a\u9699\uff0c\u738b\u526a\u7684\u5feb\u5200\uff0c\u5df2\u4e58\u865a\u800c\u5165\uff0c\u5288\u4e2d\u9ed1\u8863\u4eba\u7684\u53f3\u81c2\uff0c\u8fde\u8863\u670d\u5e26\u5411\uff0c\u524a \u4e0b\u6765\u597d\u5927\u4e00\u7247\uff0c\u9c9c\u8840\u6dcb\u6f13\u800c\u4e0b\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u591f\u51f6\u608d\uff0c\u4e00\u54ac\u7259\uff0c\u5168\u529b\u62a1\u5200\uff0c\u4e00\u62db\u201c\u6a2a\u626b\u5343\u519b\u201d\uff0c\u5200\u5982\u5de8\u8f6e\u7535\u8f6c\uff0c\u81c2\u4e0a\u7684\u9c9c\u8840\uff0c \u968f\u8f6e\u8f6c\u7684\u5200\u5149\uff0c\u6492\u51fa\u4e86\u4e00\u7247\u8840\u96e8\u3002 \u3000\u3000\u8fd9\u4e00\u5200\uff0c\u529b\u903e\u5343\u94a7\uff0c\u738b\u526a\u3001\u4e25\u65b9\uff0c\u90fd\u88ab\u8feb\u540e\u9000\u6570\u6b65\uff0c\u4ee5\u907f\u5200\u52bf\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u5374\u501f\u52bf\u4e00\u4e2a\u201c\u6f5c\u9f99\u5347\u5929\u201d\uff0c\u62d4\u8eab\u800c\u8d77\uff0c\u98de\u8d77\u4e86\u4e24\u4e08\u591a\u9ad8\uff0c\u6709\u5982\u5de8\u9e70\u63a0\u7a7a\uff0c\u5411\u5916\u98de \u53bb\u3002 \u3000\u3000\u4e00\u6392\u98de\u77e2\uff0c\u7531\u82b1\u6811\u4e1b\u4e2d\u98de\u5c04\u800c\u51fa\uff0c\u9ed1\u8863\u4eba\u53bb\u5982\u98d8\u98ce\uff0c\u7bad\u4f3c\u6d41\u661f\u8d76\u6708\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u662f\u5426\u4e2d\u4e86 \u7bad\uff0c\u4eba\u5df2\u8d8a\u8fc7\u56f4\u5899\u800c\u53bb\u3002 \u3000\u3000\u738b\u526a\u7b49\u6ca1\u6709\u8ffd\uff0c\u518d\u8ffd\u5c31\u5230\u4e86\u56f4\u5899\u4ee5\u5916\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u4ecd\u7136\u4f5c\u6212\u5907\u4e4b\u52bf\uff0c\u4ee5\u963b\u6740\u7b2c\u4e8c\u6279\u51b2\u4eba\u7684\u654c\u4eba\u3002 \u3000\u3000\u8db3\u8db3\u7b49\u4e86\u4e00\u523b\u5de5\u592b\u4e4b\u4e45\uff0c\u7adf\u518d\u65e0\u4eba\u6765\uff01 \u3000\u3000\u4f46\u95fb\u201c\u89c2\u9e64\u697c\u201d\u5904\uff0c\u53c8\u662f\u4e00\u9635\u6025\u52b2\u7684\u949f\u58f0\u4f20\u6765\u3002 \u3000\u3000\u94c1\u7fce\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u654c\u4eba\u4f3c\u4e4e\u5df2\u7ecf\u903c\u8fd1\u4e86\u653e\u9e64\u697c\u4e0b\uff0c\u54b1\u4eec\u662f\u5426\u8981\u65bd\u63f4\u4e2d\u67a2\u8981\u5730\u5462\uff1f\u201d \u3000\u3000\u738b\u526a\u9053\uff1a\u201c\u5357\u533a\u867d\u65e0\u654c\u8e2a\uff0c\u4f46\u4e0d\u6613\u5f03\u9632\u5b88\u5730\uff0c\u5144\u5f1f\u7684\u610f\u601d\uff0c\u54b1\u4eec\u5206\u51fa\u4e00\u534a\u4eba\u624b\uff0c\u8d76\u63f4\u653e \u9e64\u697c\uff0c\u8bf7\u4e3b\u5e05\u5206\u914d\u4e00\u4e0b\u3002\u201d \u3000\u3000\u94c1\u7fce\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8bf7\u738b\u5144\u3001\u4e25\u5144\uff0c\u8bf7\u548c\u5144\u5f1f\u8d76\u5f80\u9a70\u63f4\uff0c\u9a6c\u6c0f\u5144\u7559\u5728\u5357\u533a\uff0c\u5982\u6709\u654c\u8e2a\uff0c \u5c3d\u5feb\u4f20\u51fa\u8b66\u8baf\uff01\u201d \u3000\u3000\u9a6c\u4fee\u3001\u9a6c\u5f3a\u4e00\u6b20\u8eab\uff0c\u9053\uff1a\u201c\u6211\u7b49\u9075\u547d\u3002\u201d \u3000\u3000\u94c1\u7fce\u4ef0\u8138\u671b\u7740\u4e00\u8f6e\u660e\u6708\uff0c\u770b\u5929\u8272\u5df2\u8fd1\u4e09\u66f4\u65f6\u5206\uff0c\u653e\u9e64\u697c\u867d\u7136\u4f20\u51fa\u4e86\u8b66\u8baf\uff0c\u4f46\u5374\u542c\u4e0d\u5230\u6fc0 \u6597\u558a\u6740\u4e4b\u58f0\uff0c\u90a3\u662f\u8bf4\u660e\u4e86\u4e00\u79cd\u73b0\u50cf\uff0c\u6709\u654c\u4eba\u63a5\u8fd1\u4e86\u4e2d\u67a2\uff0c\u4f46\u5374\u4eba\u6570\u4e0d\u591a\uff0c\u53ef\u80fd\u53ea\u662f\u4e00\u4e24\u4e2a\u4eba\uff0c \u4f46\u5374\u662f\u975e\u5e38\u6770\u51fa\u7684\u9ad8\u624b\uff0c\u6b63\u5728\u548c\u8d1f\u8d23\u9632\u5b88\u653e\u9e64\u697c\u7684\u9ad8\u624b\u7f20\u6597\u3002 \u3000\u3000\u5e78\u597d\u65e9\u53bb\u4e86\u4e00\u8d9f\u82b1\u56ed\uff0c\u719f\u8bb0\u653e\u9e64\u6402\u7684\u4f4d\u7f6e\uff0c\u5e26\u7740\u738b\u526a\u3001\u4e25\u65b9\uff0c\u76f4\u5954\u653e\u9e64\u697c\u5904\u3002 \u3000\u3000\u4e8b\u60c5\u679c\u5982\u94c1\u7fce\u7684\u5224\u65ad\u4e00\u6837\uff0c\u653e\u9e64\u697c\u4e0a\u5df2\u706f\u706b\u901a\u660e\uff0c\u654c\u4eba\u53ea\u6709\u4e24\u4e2a\uff0c\u7a7f\u7740\u9ed1\u8272\u7684\u591c\u8272\u8863\uff0c \u6b63\u5728\u697c\u4e0b\u5e7f\u573a\u4e0a\u548c\u91d1\u5b85\u4e2d\u9ad8\u624b\u62da\u6740\u3002\u91d1\u5b85\u4e2d\u4eba\u5df2\u5012\u4e0b\u516d\u4e2a\uff0c\u4f46\u81f3\u5c11\u8fd8\u6709\u5341\u51e0\u4eba\u5b88\u5728\u697c\u4e0b\u7684\u5927 \u95e8\u5904\uff0c\u8d3e\u82f1\u548c\u5929\u67ab\u9053\u957f\u5168\u90fd\u8d76\u5230\u4e86\u73b0\u573a\uff0c\u53ea\u7ad9\u5728\u4e00\u4fa7\u89c2\u6218\uff0c\u5e76\u672a\u52a0\u5165\u6218\u5173\u3002 \u3000\u3000\u573a\u8fb9\u56db\u5468\uff0c\u7ad9\u4e86\u4e09\u7ec4\u4eba\u624b\uff0c\u5404\u6709\u4e00\u5b9a\u7684\u65b9\u4f4d\uff0c\u4e00\u770b\u5c31\u53ef\u4ee5\u731c\u51fa\u662f\u4e1c\u3001\u5357\u3001\u897f\u3001\u5317\u56db\u5927\u9632 \u533a\u9a70\u63f4\u800c\u6765\u7684\u4eba\u624b\uff0c\u6bcf\u7ec4\u4e09\u4eba\uff0c\u90fd\u662f\u5206\u7531\u4e00\u534a\u4eba\u624b\u8d76\u6765\uff0c\u5404\u533a\u4e3b\u5e05\uff0c\u7387\u9886\u7740\u6700\u7cbe\u9510\u7684\u4e24\u4f4d\u526f \u624b\u8d76\u6765\u52a9\u6218\uff0c\u8fde\u94c1\u7fce\u8fd9\u4e00\u7ec4\uff0c\u521a\u597d\u662f\u5341\u4e8c\u4e2a\u4eba\uff0c\u52a0\u4e0a\u8d3e\u82f1\u7b49\u4e00\u6279\u4eba\u624b\uff0c\u5206\u6210\u4e94\u4e2a\u65b9\u4f4d\uff0c\u9690\u9690 \u6210\u5408\u56f4\u4e4b\u52bf\u3002 \u3000\u3000\u8d3e\u82f1\u8ddd\u79bb\u640f\u6740\u7684\u73b0\u573a\u6700\u8fd1\uff0c\u5e26\u7684\u4eba\u6570\u4e5f\u6700\u591a\uff0c\u4e00\u9a6c\u5f53\u5148\uff0c\u884c\u8fd1\u73b0\u573a\uff0c\u8eab\u540e\u7d27\u968f\u7740\u56db\u4e2a\u75be \u670d\u52b2\u88c5\u7684\u4e2d\u5e74\u4eba\uff0c\u60f3\u6765\u90fd\u662f\u8d1f\u8d23\u622a\u6740\u5c0f\u7ec4\u4e2d\u7684\u9ad8\u624b\u3002 \u3000\u3000\u4f46\u4ed6\u5f88\u6c89\u7740\uff0c\u53ea\u662f\u7f13\u7f13\u903c\u8fd1\u73b0\u573a\uff0c\u4f3c\u65e0\u51fa\u624b\u7684\u6253\u7b97\uff0c\u5374\u5168\u795e\u6ce8\u89c6\u73b0\u573a\u6218\u6597\uff0c\u4f3c\u662f\u60f3\u7531\u6765 \u4eba\u51fa\u624b\u6b66\u529f\u4e2d\uff0c\u63a8\u5224\u51fa\u4e24\u4eba\u7684\u6765\u8def\u3002 \u3000\u3000\u4e24\u4e2a\u5165\u4fb5\u7684\u9ed1\u8863\u4eba\uff0c\u4e2a\u5b50\u4e0d\u5927\uff0c\u53ef\u662f\u80c6\u5b50\u5f88\u5927\uff0c\u5bf9\u5408\u56f4\u4e4b\u52bf\uff0c\u4f3c\u662f\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\uff0c\u5bf9\u903c \u8fd1\u7684\u8d3e\u82f1\u4e94\u5927\u9ad8\u624b\uff0c\u4e5f\u662f\u89c6\u82e5\u65e0\u7779\uff0c\u5168\u5fc3\u8fd0\u5251\uff0c\u8fce\u51fb\u5408\u56f4\u800c\u4e0a\u7684\u56db\u4e2a\u91d1\u5b85\u6b66\u58eb\u3002 \u3000\u3000\u8fd9\u4e9b\u6b66\u58eb\u6709\u591a\u5c11\u5206\u91cf\uff1f\u8d3e\u82f1\u5fc3\u4e2d\u6700\u662f\u6e05\u695a\uff0c\u4ed6\u5bf9\u8fd9\u56db\u4e2a\u4eba\uff0c\u4f3c\u662f\u6709\u7740\u5f88\u5f3a\u7684\u4fe1\u5fc3\uff0c\u4e00\u76f4 \u672a\u4e0b\u4ee4\u518d\u52a0\u4eba\u624b\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u56db\u4e2a\u6b66\u58eb\u5c55\u73b0\u7684\u6280\u827a\uff0c\u76f8\u5f53\u7cbe\u6e5b\uff0c\u56db\u628a\u79cb\u6c34\u96c1\u7fce\u5200\uff0c\u6781\u5c3d\u53d8\u5316\u4e4b\u80fd\uff0c\u5ffd\u800c\u5343\u950b \u805a\u4e00\u5200\uff0c\u6cf0\u5c71\u538b\u9876\u4e00\u822c\u5730\u5288\u4e0b\u6765\uff0c\u5200\u98ce\u5e26\u8d77\u4e86\u4e00\u7247\u5578\u58f0\u3002 \u3000\u3000\u5982\u6b64\u5947\u5e7b\u51f6\u5389\u7684\u5200\u6cd5\uff0c\u4eba\u518d\u591a\u4e00\u4e9b\uff0c\u5c31\u5f88\u96be\u5c55\u73b0\u5a01\u529b\u4e86\u3002 \u3000\u3000\u4e24\u4e2a\u9ed1\u8863\u4eba\uff0c\u5404\u6267\u4e00\u628a\u957f\u5251\uff0c\u770b\u4e0d\u51fa\u5251\u4e0a\u6709\u4f55\u5a01\u52bf\uff0c\u4f46\u56db\u4e2a\u6b66\u58eb\u5c31\u662f\u65e0\u6cd5\u903c\u5f00\u4e24\u4eba\u7684\u5251 \u52bf\u3002 \u3000\u3000\u5bf9\u8fd9\u56db\u4e2a\u5200\u5ba2\u7684\u5200\u6cd5\uff0c\u94c1\u7fce\u5341\u5206\u4f69\u670d\uff0c\u81ea\u5df1\u624b\u4e2d\u7684\u4e00\u628a\u96c1\u7fce\u5200\uff0c\u5c31\u672a\u5fc5\u80fd\u80dc\u8fc7\u56db\u4eba\u3002\u4f46 \u5bf9\u4e24\u4e2a\u4e2a\u5934\u4e0d\u5927\u7684\u9ed1\u8863\u4eba\uff0c\u80fd\u4e45\u6218\u4e0d\u8d25\uff0c\u5fc3\u4e2d\u5b9e\u662f\u6709\u70b9\u4e0d\u670d\uff0c\u5168\u795e\u8d2f\u6ce8\uff0c\u7ec6\u770b\u4e24\u4eba\u5251\u6cd5\uff0c\u4f55 \u4ee5\u80fd\u5728\u56db\u9762\u516b\u65b9\u7684\u5200\u52bf\u731b\u653b\u4e2d\uff0c\u7ad9\u7acb\u4e0d\u52a8\uff0c\u6709\u5982\u4e00\u67f1\u64ce\u5929\u3002 \u3000\u3000\u4ed4\u7ec6\u89c2\u5bdf\u4e4b\u4e0b\uff0c\u7ec8\u4e8e\u53d1\u89c9\uff0c\u4e24\u4eba\u5251\u52bf\u7684\u5947\u5e7b\u4e4b\u5904\uff0c\u6709\u5982\u6625\u8695\u5410\u4e1d\uff0c\u5251\u52bf\u7ef5\u5bc6\u9634\u67d4\uff0c\u5bd3\u53d8 \u4e8e\u7f13\uff0c\u770b\u4f3c\u67d4\u5f31\u65e0\u529b\uff0c\u4f46\u53d8\u5316\u5374\u662f\u5947\u5965\u96be\u6d4b\uff0c\u6bcf\u5f53\u5200\u52bf\u8fd1\u8eab\u65f6\uff0c\u5251\u52bf\u603b\u662f\u62a2\u5f97\u5148\u673a\uff0c\u4e0d\u662f\u5251 \u5c16\u6307\u5230\u654c\u4eba\u63e1\u5200\u7684\u8109\u7a74\uff0c\u5c31\u662f\u642d\u5728\u4e86\u63e1\u5200\u7684\u624b\u8155\u4e0a\uff0c\u903c\u7684\u654c\u4eba\u6536\u6263\u9000\u907f\u3002\u5982\u82e5\u5200\u52bf\u4e0d\u6b62\uff0c\u5c31 \u5148\u884c\u649e\u5728\u5251\u4e0a\u4e86\uff0c\u90a3\u53ef\u662f\u7834\u8109\u65ad\u8155\u4e4b\u75bc\uff0c\u7acb\u523b\u5c06\u5931\u53bb\u518d\u6218\u80fd\u529b\uff0c\u4efb\u4eba\u5bb0\u5272\u4e86\u3002 \u3000\u3000\u94c1\u7fce\u60ca\u9a87\u6781\u4e86\uff0c\u904d\u601d\u6bd5\u751f\u9645\u9047\uff0c\u4ece\u672a\u89c1\u5230\u8fc7\u8fd9\u6837\u5251\u6cd5\uff0c\u770b\u4f3c\u4efb\u654c\u4eba\u8fdb\u9000\u5982\u98ce\uff0c\u5200\u6cd5\u53d8\u5316 \u4e07\u5343\uff0c\u5176\u5b9e\uff0c\u5168\u5728\u5251\u52bf\u7684\u63a7\u5236\u4e4b\u4e0b\u3002 \u3000\u3000\u4f46\u4e24\u4e2a\u9ed1\u8863\u4eba\uff0c\u603b\u662f\u5251\u4e0b\u7559\u60c5\uff0c\u70b9\u5230\u5373\u6536\uff0c\u5982\u6709\u4f24\u4eba\u4e4b\u5fc3\uff0c\u56db\u4e2a\u5200\u5ba2\uff0c\u65e9\u5c31\u65ad\u8155\u5f03\u5200\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u5982\u6b64\u4ec1\u6148\u5462\uff1f\u8fd9\u662f\u4e00\u573a\u51f6\u9669\u7684\u62da\u6218\u554a\uff01 \u3000\u3000\u94c1\u7fce\u5fc3\u4e2d\u5145\u6ee1\u60f6\u60d1\uff0c\u4e5f\u5341\u5206\u4e0d\u89e3\uff0c\u770b\u5411\u5730\u4e0a\u8eba\u7740\u7684\u516d\u4e2a\u6b66\u58eb\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u8fd9\u516d\u4e2a\u4eba\u662f\u600e \u4e48\u4f24\u7684\u5462\uff1f\u4eba\u8eba\u4e0b\u4e86\uff0c\u5374\u4e0d\u89c1\u4f24\u75d5\u8840\u8ff9\uff1f \u3000\u3000\u4f46\u4e8b\u60c5\u5f88\u5feb\u6709\u4e86\u7b54\u6848\uff0c\u53ea\u89c1\u5de6\u9996\u9ed1\u8863\u4eba\uff0c\u5251\u6cd5\u5ffd\u7136\u4e00\u53d8\uff0c\u7531\u5200\u52bf\u7a7a\u9699\u4e2d\u903c\u4e0a\u4e00\u4e2a\u5200\u5ba2\u7684 \u54bd\u5589\uff0c\u90a3\u4eba\u5fae\u5fae\u4e00\u5446\uff0c\u9ed1\u8863\u4eba\u5de6\u624b\u5df2\u70b9\u51fa\u4e00\u6307\uff0c\u6b63\u4e2d\u5200\u5ba2\u6655\u7a74\uff0c\u5200\u5ba2\u8eab\u5b50\u6447\u4e86\u4e24\u6447\uff0c\u5012\u5728\u5730 \u4e0a\u3002 \u3000\u3000\u6655\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u94c1\u7fce\u656c\u4f69\u6781\u4e86\uff0c\u8fde\u51fa\u624b\u7684\u52c7\u6c14\uff0c\u5168\u88ab\u5413\u7684\u98de\u5230\u4e5d\u5bb5\u4e91\u5916\u4e86\u3002\u9ed1\u8863\u4eba\u4e0d\u4f46\u80fd\u968f\u65f6\u65ad\u4eba\u4e4b\u8155\uff0c \u4e5f\u53ef\u4ee5\u968f\u65f6\u53d6\u547d\uff0c\u5982\u6b64\u7684\u6280\u827a\u5883\u754c\uff0c\u5df2\u4e0d\u662f\u8d2a\u751f\u6015\u6b7b\u7684\u4e8b\u4e86\uff0c\u800c\u662f\u767d\u767d\u9001\u4e0a\u53bb\u8ba9\u4eba\u6740\u6389\u3002 \u3000\u3000\u53ea\u542c\u8d3e\u82f1\u559d\u9053\uff1a\u201c\u9000\u4e0b\u6765\uff01\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4e09\u4e2a\u5200\u5ba2\uff0c\u65e9\u5df2\u8d8a\u6253\u8d8a\u6015\uff0c\u54ac\u7740\u7259\u5728\u5fcd\u53d7\u6298\u78e8\uff0c\u8d3e\u82f1\u7ed9\u4e86\u4e00\u4e2a\u53f0\u9636\uff0c\u4e09\u4eba\u7acb\u523b \u9000\u4e0b\uff0c\u4e5f\u4e0d\u5984\u60f3\u66ff\u540c\u4f34\u51fa\u53e3\u6c14\u4e86\u3002 \u3000\u3000\u4eca\u591c\u73b0\u573a\u4e2d\u4eba\uff0c\u90fd\u662f\u9ad8\u624b\uff0c\u5927\u90fd\u770b\u51fa\u4e86\u4e00\u4e9b\u82d7\u5934\uff0c\u8d3e\u82f1\u559d\u9000\u4e86\u5bd2\u5c71\u56db\u5200\uff0c\u7adf\u65e0\u4eba\u518d\u8bf7\u547d \u51fa\u6218\u3002 \u3000\u3000\u8d3e\u82f1\u5fc3\u5934\u82e6\uff0c\u5374\u53c8\u65e0\u6cd5\u8bf4\u51fa\u6765\uff0c\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u4ee5\u4ed6\u7684\u535a\u5b66\u81ea\u8d1f\uff0c\u8fd8\u77a7\u4e0d\u51fa\u8fd9\u4e24\u4e2a\u9ed1\u8863\u4eba \u7528\u7684\u4ec0\u4e48\u5251\u6cd5\uff0c\u4e5f\u672a\u60f3\u51fa\u7834\u89e3\u4e4b\u9053\u3002 \u3000\u3000\u4f46\u73b0\u4e0b\u60c5\u52bf\uff0c\u5374\u903c\u7684\u4ed6\u65e0\u6cd5\u9000\u7f29\uff0c\u53ea\u6709\u786c\u7740\u5934\u76ae\u9876\u4e0a\u53bb\u4e86\uff0c\u603b\u7ba1\u8eab\u5206\u5d07\u9ad8\uff0c\u6709\u65f6\u5374\u5e72\u7684 \u8f9b\u82e6\u3002 \u3000\u3000\u4f46\u4ed6\u5fc3\u673a\u6781\u5176\u6df1\u6c89\uff0c\u5fc3\u4e2d\u867d\u7136\u6050\u614c\uff0c\u4f46\u8868\u9762\u4e0a\u5374\u662f\u4e0d\u9732\u58f0\u8272\uff0c\u7f13\u6b65\u903c\u8fd1\u4e24\u4e2a\u9ed1\u8863\u4eba\uff0c\u9053\uff1a \u201c\u4e24\u4f4d\u53ef\u662f\u68a6\u5e7b\u4e4b\u5200\u7684\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u53f3\u9996\u9ed1\u8863\u4eba\u9519\u540e\u534a\u6b65\uff0c\u770b\u6837\u5b50\u4e00\u5207\u4e8b\u60c5\u90fd\u7531\u5de6\u9996\u7684\u9ed1\u8863\u4eba\u4f5c\u4e3b\u3002 \u3000\u3000\u8499\u9762\u9ed1\u7eb1\u4e2d\u900f\u51fa\u4e24\u9053\u795e\u5149\uff0c\u51dd\u6ce8\u5728\u8d3e\u82f1\u7684\u8138\u4e0a\uff0c\u5374\u4e0d\u95fb\u56de\u7b54\u4e4b\u8a00\u3002 \u3000\u3000\u4f46\u8d3e\u82f1\u5374\u51dd\u795e\u503e\u6ce8\uff0c\u542c\u5f97\u5341\u5206\u7528\u5fc3\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u597d\u50cf\u5728\u65bd\u5c50\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u4e4b\u672f\uff0c\u53ea\u6709\u8d3e\u82f1\u542c\u5230\u4ed6\u8bf4\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u8d3e\u82f1\u4e0d\u505c\u5730\u6447\u5934\uff0c\u60f3\u662f\u4e0d\u540c\u610f\u9ed1\u8863\u4eba\u63d0\u51fa\u7684\u610f\u89c1\uff0c\u56de\u7b54\u9ed1\u8863\u4eba\u8bdd\u65f6\uff0c\u5374\u662f\u516c\u5f00\u5730\u56de\u5e94\uff0c \u9053\uff1a\u201c\u8fd9\u4e9b\u56f0\u96be\uff0c\u6211\u4eec\u53d7\u96c7\u7684\u662f\u4e1c\u4e3b\uff0c\u4fdd\u62a4\u4ed6\u7684\u4f20\u5bb6\u4e4b\u5b9d\uff0c\u65e0\u6743\u4f5c\u4e3b\u8bf7\u4eba\u53c2\u89c2\uff0c\u6211\u4eec\u4e5f\u77e5\u9053 \u89ca\u89ce\u4e09\u5b9d\u7684\u4e5f\u975e\u4f60\u670b\u53cb\u8fd9\u4e00\u4f19\u4eba\uff0c\u68a6\u5e7b\u4e4b\u5200\u548c\u4ed6\u7684\u4e00\u4e9b\u5c5e\u4e0b\uff0c\u662f\u6211\u4eec\u5fc3\u76ee\u4e2d\u6700\u5f3a\u52b2\u7684\u654c\u4eba\u4e4b \u4e00\uff0c\u4f60\u670b\u53cb\u5982\u540c\u5929\u964d\u795e\u5175\uff0c\u6765\u7684\u5168\u51fa\u610f\u5916\uff0c\u6211\u4e0d\u77e5\u4f60\u670b\u53cb\u662f\u4f55\u8bb8\u4eba\u7269\uff1f\u4f46\u5df2\u77e5\u662f\u6700\u5f3a\u7684\u654c\u7fa4 \u4e4b\u4e00\uff0c\u4f60\u670b\u53cb\u4e00\u5b9a\u8981\u770b\uff0c\u53ea\u6709\u628a\u6211\u4eec\u5168\u6570\u6482\u5012\uff0c\u90a3\u5c31\u4efb\u51ed\u53d6\u9605\u4e86\u3002\u201d \u3000\u3000\u7531\u8d3e\u82f1\u7684\u56de\u7b54\u4e2d\uff0c\u573a\u4e2d\u4eba\u5927\u90fd\u5df2\u4e86\u89e3\u4e86\u90e8\u5206\u5185\u5bb9\uff0c\u9ed1\u8863\u4eba\u63d0\u51fa\u4e2a\u8bf4\u660e\uff0c\u53ea\u662f\u89c1\u8bc6\u4e00\u4e0b\u4e09 \u5b9d\uff0c\u5e76\u65e0\u76d7\u53d6\u4e4b\u5fc3\u3002 \u3000\u3000\u4f46\u5374\u4e3a\u8d3e\u82f1\u4e25\u8bcd\u62d2\u7edd\u4e86\u3002 \u3000\u3000\u60f3\u50cf\u4e4b\u4e2d\uff0c\u4e00\u573a\u6076\u6218\uff0c\u53c8\u5c06\u5c55\u5f00\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u7a81\u7136\u5411\u524d\u4e24\u6b65\uff0c\u62bd\u51fa\u80cc\u4e0a\u957f\u5251\uff0c\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\u8bf7\u8ba9\u5f00\u4e00\u6b65\uff0c\u8ba9\u8d2b\u9053\u9886\u6559\u4e00\u4e0b\u8fd9 \u4f4d\u65bd\u4e3b\u5947\u5e7b\u7684\u5251\u672f\u3002\u201d \u3000\u3000\u8bf4\u5947\u5e7b\u5012\u662f\u7531\u8877\u4e4b\u8a00\uff0c\u8fd9\u4f4d\u51fa\u8eab\u6b66\u5f53\u7684\u5251\u672f\u540d\u5bb6\uff0c\u4e5f\u5f04\u4e0d\u6e05\u695a\u90a3\u662f\u4e00\u79cd\u4ec0\u4e48\u5251\u6cd5\uff1f\u5982\u85e4 \u8513\u7ed5\u6811\uff0c\u67d4\u4e1d\u7f20\u8155\uff0c\u4e0d\u7740\u4e00\u70b9\u529b\u9053\uff0c\u5251\u52bf\u603b\u662f\u5728\u654c\u4eba\u624b\u8155\u4e0a\u8f6c\u52a8\uff0c\u8fd9\u7b49\u5251\u6cd5\u8fd8\u6709\u4e00\u4e2a\u7279\u522b\u4e4b \u5904\uff0c\u4e0d\u8bba\u654c\u4eba\u653b\u52bf\u5982\u4f55\u7684\u731b\u70c8\uff0c\u90fd\u6446\u8131\u4e0d\u5f00\uff0c\u5b83\u5374\u4e00\u70b9\u4e5f\u4e0d\u8d39\u6c14\u529b\uff0c\u90a3\u662f\u8bf4\u4ed6\u4eec\u53ef\u4ee5\u4e45\u6218\u4e0d \u75b2\u3002 \u3000\u3000\u201c\u9053\u957f\uff0c\u4f60\u662f\u4e00\u4ee3\u5251\u672f\u540d\u5bb6\uff0c\u8d3e\u82f1\u4eca\u65e5\u62da\u4e0a\u8fd9\u6761\u547d\uff0c\u4e5f\u8981\u627e\u51fa\u8fd9\u5957\u5251\u6cd5\u7834\u7efd\uff0c\u4f60\u8001\u4eba\u5bb6 \u5c31\u4ed4\u7ec6\u5730\u89c2\u6218\u5427\uff01\u5e0c\u671b\u6211\u8fd9\u756a\u727a\u7272\u6709\u70b9\u4ee3\u4ef7\u3002\u201d\u4f38\u624b\u7531\u6000\u4e2d\u53d6\u51fa\u4e00\u628a\u94c1\u6298\u6247\uff0c\u9053\uff1a\u201c\u6211\u6765\u9886 \u6559\uff01\u201d \u3000\u3000\u795e\u8272\u51b7\u8083\uff0c\u4e00\u526f\u65e0\u53ef\u5948\u4f55\u82b1\u843d\u53bb\u7684\u795e\u60c5\uff0c\u4f3c\u660e\u77e5\u8fd9\u4e00\u6218\u51f6\u591a\u5409\u5c11\uff0c\u5374\u6709\u7740\u6177\u6168\u8d74\u6b7b\u7684\u8c6a \u58ee\u3002 \u3000\u3000\u5de6\u9996\u9ed1\u8863\u4eba\u7a81\u7136\u4e00\u4e2a\u8f6c\u8eab\uff0c\u4e00\u62db\u624b\u548c\u53f3\u9996\u9ed1\u8863\u4eba\u8054\u8882\u98de\u8d77\uff0c\u7834\u7a7a\u800c\u53bb\uff0c\u8eab\u6cd5\u5feb\u901f\uff0c\u6709\u5982 \u8759\u8760\u5bb5\u98de\uff0c\u6d88\u5931\u5728\u660e\u6708\u5149\u8f89\u4e4b\u4e2d\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u9ed1\u8863\u4eba\u6765\u7684\u7a81\u7136\uff0c\u6253\u5012\u4e86\u4e03\u4e2a\u6b66\u58eb\uff0c\u9664\u4e86\u8d3e\u82f1\u542c\u5230\u4ed6\u4e00\u9635\u4f20\u97f3\u8a00\u8bed\u5916\uff0c\u6ca1\u6709\u5927\u58f0 \u8bb2\u8fc7\u4e00\u53e5\u8bdd\u3002\u660e\u660e\u5df2\u6151\u654c\u5fc3\u795e\uff0c\u80dc\u5238\u5728\u63e1\uff0c\u5374\u53c8\u7a81\u7136\u79bb\u53bb\u3002\u6253\u5012\u4e86\u4e03\u4e2a\u4eba\uff0c\u4e0d\u89c1\u4e00\u6ef4\u8840\u8ff9\uff0c \u5b58\u5fc3\u5341\u5206\u4ec1\u6148\uff0c\u80f8\u4e2d\u5168\u65e0\u6740\u673a\uff0c\u6765\u5982\u95ea\u7535\uff0c\u53bb\u5982\u98d8\u98ce\uff0c\u4e5f\u672a\u7559\u4e00\u70b9\u75d5\u8ff9\u3002 \u3000\u3000\u8d3e\u82f1\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u795e\u79d8\u554a\uff01\u795e\u79d8\uff0c\u7f9a\u7f8a\u6302\u89d2\uff0c\u65e0\u8ff9\u53ef\u5bfb\uff0c\u7a76\u7adf\u662f\u4f55\u65b9\u795e\u5723\u5462\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u5148\u6551\u4eba\uff0c\u518d\u7814\u7a76\u8fd9\u4e00\u5bf9\u602a\u4eba\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u5728\u573a\u4e4b\u4eba\uff0c\u4e0d\u5c11\u662f\u6c5f\u6e56\u9605\u5386\u4e30\u5bcc\u4e4b\u58eb\uff0c\u4e5f\u90fd\u5728\u5fc3\u4e2d\u7422\u78e8\uff0c\u4f46\u5374\u65e0\u4e00\u4eba\u80fd\u60f3\u51fa\u8fd9\u4e24\u4e2a\u9ed1\u8863 \u4eba\u7684\u6765\u5386\uff0c\u9664\u4e86\u89c9\u7740\u4ed6\u4eec\u6709\u70b9\u7626\u5c0f\u4e4b\u5916\uff0c\u522b\u65e0\u7279\u5f81\u3002 \u3000\u3000\u70b9\u7a74\u7684\u624b\u6cd5\u5012\u4e0d\u7279\u6b8a\uff0c\u4e00\u9635\u63a8\u5b98\u8fc7\u7a74\uff0c\u4e03\u4e2a\u4eba\u5168\u9192\u4e86\u8fc7\u6765\u3002\u5927\u5bb6\u8fd0\u6c14\u4e00\u8bd5\uff0c\u5168\u90fd\u6beb\u53d1\u65e0 \u4f24\u3002\u6c5f\u6e56\u5bf9\u51b3\uff0c\u5200\u5251\u8fc7\u62db\uff0c\u80fd\u9047\u4e0a\u8fd9\u83e9\u8428\u5fc3\u80a0\u9ad8\u624b\uff0c\u662f\u7edd\u65e0\u4ec5\u6709\u7684\u5e78\u8fd0\uff0c\u771f\u662f\u7956\u4e0a\u6709\u5fb7\u3002 \u3000\u3000\u201c\u624b\u4e0b\u7559\u60c5\uff0c\u672a\u4f24\u4e00\u4eba\uff0c\u201d\u5929\u67ab\u9053\u957f\uff0c\u9053\uff1a\u201c\u6bd4\u8d77\u5f53\u5e74\u7684\u4f5b\u5149\u4e0a\u4eba\uff0c\u8fd8\u591a\u4e00\u5206\u4ec1\u6148\uff0c\u96be \u9053\u4ed6\u662f\u4e0a\u4eba\u7684\u4f20\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e0d\u53ef\u80fd\u554a\uff01\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u4e0a\u4eba\u5df2\u6d85\u76d8\u516d\u5341\u5e74\uff0c\u4ece\u672a\u542c\u8bf4\u8fc7\u4ed6\u6536\u8fc7\u5f1f\u5b50\uff0c\u518d\u8bf4\u90a3\u5957\u5251\u6cd5\uff0c \u5e76\u65e0\u4f5b\u610f\uff0c\u800c\u4e14\u662f\u6740\u673a\u51f6\u5389\uff0c\u62db\u62db\u53ef\u4ee5\u65ad\u8155\u53d6\u547d\uff0c\u4f46\u5b83\u7559\u52b2\u4e0d\u53d1\uff0c\u70b9\u5230\u5373\u6536\uff0c\u4e0d\u662f\u5251\u6cd5\u4ec1\u6148\uff0c \u800c\u662f\u8fd0\u5251\u4eba\u7684\u5fc3\u5730\u5584\u826f\u2026\u2026\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u63a5\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u8fd9\u5c31\u53eb\u8d2b\u9053\u60f3\u4e0d\u901a\u4e86\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u68a6\u5e7b\u4e4b\u5200\u8fd8\u672a\u51fa\u73b0\uff0c\u5374\u591a\u4e86\u8fd9\u4e48\u4e00\u7ec4\u5f3a\u654c\uff01\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u8981\u62a4\u5b88\u4e09\u5b9d\uff0c\u6050\u975e\u6613\u4e8b \u4e86\u3002\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u4e00\u58f0\u957f\u7b11\uff0c\u7834\u7a7a\u800c\u6765\uff0c\u4e00\u6761\u4eba\u5f71\uff0c\u7531\u9ad8\u7a7a\u76f4\u843d\u4e0b\u6765\u3002 \u3000\u3000\u5168\u573a\u4e2d\u4eba\uff0c\u90fd\u770b\u7684\u5446\u4f4f\u4e86\uff0c\u8fd9\u4e2a\u4eba\u4e0d\u662f\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\u800c\u6765\u7684\uff0c\u800c\u662f\u7531\u534a\u7a7a\u4e2d\u98de\u6765\u3002 \u3000\u3000\u8d3e\u82f1\u53cd\u5e94\u654f\u9510\uff0c\u4e0d\u770b\u6765\u4eba\uff0c\u5148\u62ac\u5934\u5411\u7a7a\u4e2d\u770b\u53bb\uff0c\u9690\u9690\u7ea6\u7ea6\u770b\u5230\u4e00\u56e2\u9ed1\u5f71\uff0c\u76f4\u6ca1\u9ad8\u7a7a\uff0c\u5fc3 \u4e2d\u4e00\u52a8\uff0c\u4f46\u5374\u6ca1\u6709\u70b9\u7834\u3002 \u3000\u3000\u53cd\u5e94\u7a0d\u4e3a\u8fdf\u7f13\u4e00\u70b9\u7684\u4eba\uff0c\u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u660e\u6708\u5728\u5929\uff0c\u4ec0\u4e48\u4e5f\u770b\u4e0d\u5230\u4e86\u3002 \u3000\u3000\u770b\u4e0d\u5230\uff0c\u5fc3\u4e2d\u5c31\u591a\u4e86\u4e00\u5206\u6000\u7591\uff0c\u8fd9\u4e2a\u4eba\u4f1a\u98de\uff0c\u662f\u7531\u9ad8\u7a7a\u98de\u8fc7\u6765\u7684\u3002 \u3000\u3000\u8f7b\u529f\u7edd\u9ad8\uff0c\u8e0f\u96ea\u65e0\u75d5\uff0c\u5feb\u7684\u4eba\u76ee\u4e0d\u6687\u63a5\u7684\u9ad8\u624b\uff0c\u7ed9\u4eba\u4e00\u79cd\u795e\u51fa\u9b3c\u6ca1\u7684\u611f\u89c9\uff0c\u4f46\u4ed6\u8fd8\u662f\u7528 \u811a\u8dd1\u6765\u7684\uff0c\u53ea\u662f\u8dd1\u7684\u5feb\u7f62\u4e86\uff0c\u4f46\u7edd\u4e0d\u662f\u98de\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e2a\u4eba\u7ed9\u4eba\u7684\u611f\u89c9\u662f\u98de\u6765\u7684\u3002 \u3000\u3000\u6708\u5149\u4e0b\uff0c\u53ea\u89c1\u6765\u4eba\u7a7f\u7740\u4e00\u4ef6\u5bbd\u5927\u7684\u94f6\u7070\u957f\u888d\uff0c\u6574\u4e2a\u4eba\u90fd\u8499\u5728\u90a3\u4e00\u4ef6\u957f\u888d\u4e4b\u4e2d\u3002 \u3000\u3000\u662f\u7684\uff01\u94f6\u7070\u8272\uff0c\u5728\u660e\u6708\u4e4b\u591c\u4e2d\uff0c\u66f4\u6613\u548c\u6708\u8272\u6eb6\u4e3a\u4e00\u4f53\uff0c\u4e5f\u66f4\u96be\u88ab\u4eba\u53d1\u73b0\u3002 \u3000\u3000\u5982\u975e\u9002\u624d\u4e24\u4e2a\u9ed1\u8863\u4eba\uff0c\u95f9\u7684\u4eba\u5fc3\u60f6\u60f6\uff0c\u573a\u4e2d\u4eba\u5927\u534a\u662f\u8001\u6c5f\u6e56\uff0c\u5bf9\u4e8b\u60c5\u7684\u770b\u6cd5\uff0c\u90fd\u4f1a\u4f9d\u7406 \u63a8\u65ad\uff0c\u4e0d\u81f3\u4e71\u4e86\u7ae0\u6cd5\u3002\u4f46\u6b64\u60c5\u6b64\u666f\u4e4b\u4e0b\uff0c\u4eba\u5fc3\u614c\u60d1\u672a\u5b9a\uff0c\u5c31\u96be\u514d\u6709\u70b9\u5947\u602a\u7684\u8054\u60f3\u4e86\uff0c\u800c\u672a\u4f5c \u6df1\u7a76\u3002 \u3000\u3000\u7b2c\u4e00\u4e2a\u8054\u60f3\u662f\u8fd9\u4e2a\u98de\u6765\u7684\u4eba\uff0c\u548c\u4e24\u4e2a\u9ed1\u8863\u4eba\u662f\u540c\u4f19\u7684\uff0c\u4ee5\u4ed6\u98de\u884c\u7edd\u8ff9\u7684\u8eab\u6cd5\u800c\u8a00\uff0c\u6280\u827a \u5c24\u5728\u9ed1\u8863\u4eba\u4e4b\u4e0a\uff0c\u624b\u4e2d\u5175\u5203\u53d8\u5316\u4e4b\u5947\uff0c\u5f53\u7136\u4e5f\u4f1a\u6bd4\u9ed1\u8863\u4eba\u9ad8\u660e\uff0c\u4f46\u4ed6\u4f1a\u4e0d\u4f1a\u548c\u9ed1\u8863\u4eba\u4e00\u822c\uff0c \u6709\u7740\u4ec1\u6148\u7684\u5fc3\u80a0\u5462\uff1f \u3000\u3000\u5982\u679c\u4e0d\u662f\uff0c\u8fd9\u4e00\u6218\u5c31\u4f24\u6b8b\u7d2f\u7d2f\uff0c\u5c38\u6a2a\u5ead\u9662\u4e86\u3002 \u3000\u3000\u94f6\u8863\u4eba\u7f29\u5728\u5bbd\u5927\u7684\u957f\u888d\u4e2d\uff0c\u8083\u7acb\u4e0d\u52a8\uff0c\u6ee1\u573a\u82f1\u96c4\u81ea\u601d\u81ea\u60f3\uff0c\u81ea\u5df1\u5413\u81ea\u5df1\uff0c\u5413\u7684\u4e00\u4e2a\u4e2a\u5664 \u82e5\u5bd2\u8749\uff0c\u6570\u5341\u4eba\u7684\u5927\u573a\u9762\uff0c\u9759\u7684\u843d\u53f6\u53ef\u95fb\uff0c\u542c\u4e0d\u5230\u4e00\u70b9\u58f0\u606f\u3002 \u3000\u3000\u8d3e\u82f1\u7684\u76ee\u5149\u73af\u987e\u5168\u573a\u4e00\u5468\uff0c\u51dd\u6ce8\u94c1\u7fce\u7684\u8138\u4e0a\u4e00\u9635\uff0c\u7b11\u9053\uff1a\u201c\u4eca\u591c\u771f\u6b63\u5927\u5f00\u4e86\u773c\u754c\uff0c\u9ad8\u4eba \u8fde\u7fe9\u51fa\u73b0\uff0c\u53eb\u4eba\u76ee\u4e0d\u6687\u63a5\u3002\u670b\u53cb\uff0c\u6df1\u591c\u9020\u8bbf\uff0c\u53ef\u5426\u8bf4\u660e\u6765\u610f\u5462\uff1f\u201d \u3000\u3000\u94f6\u8863\u4eba\u8eab\u8eaf\u672a\u52a8\uff0c\u5934\u8138\u53cc\u624b\uff0c\u4ecd\u7f29\u5728\u5bbd\u5927\u957f\u888d\u4e2d\uff0c\u4f46\u4e00\u4e2a\u6c99\u54d1\u3001\u9634\u51b7\u7684\u58f0\u97f3\uff0c\u5374\u7531\u957f\u888d \u4e2d\u51b2\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u91d1\u767e\u5e74\u4f55\u5fb7\u4f55\u80fd\uff0c\u5750\u62e5\u4e09\u5b9d\u6570\u5e74\u4e4b\u4e45\uff0c\u8001\u592b\u4eca\u591c\u6765\u6b64\uff0c\u53ea\u60f3\u53d6\u56de\u5c5e\u4e8e\u8001 \u592b\u7684\u4e1c\u897f\uff01\u5982\u80af\u4ea4\u51fa\uff0c\u8001\u592b\u6389\u5934\u5373\u53bb\uff0c\u4e5f\u4e0d\u95ee\u5176\u4ed6\u7684\u4e8b\u4e86\uff01\u201d \u3000\u3000\u8d3e\u82f1\u5fc3\u4e2d\u5fd6\u9053\uff1a\u7ec8\u4e8e\u70b9\u51fa\u4e3b\u9898\u6765\u4e86\uff01\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u4e0d\u77e5\u54ea\u4e00\u6837\u662f\u4f60\u8001\u4eba\u5bb6\u7684\u4e1c\u897f\uff1f\u201d \u3000\u3000\u201c\u6c34\u706b\u76f8\u6d4e\u201d\uff0c\u94f6\u8863\u4eba\u9053\uff1a\u201c\u90a3\u53ea\u662f\u4e00\u5757\u987d\u77f3\uff0c\u91d1\u767e\u5e74\u7559\u5b83\u65e0\u7528\uff0c\u4ea4\u51fa\u6765\uff0c\u4e0e\u4ed6\u65e0\u635f\uff01\u201d \u3000\u3000\u201c\u65e2\u662f\u4e00\u5757\u987d\u77f3\uff0c\u201d\u8d3e\u82f1\u9053\uff1a\u201c\u6ee1\u5c71\u904d\u91ce\u90fd\u662f\uff0c\u8001\u5148\u751f\u53c8\u4f55\u82e6\u5982\u6b64\u5927\u8d39\u5468\u6298\uff0c\u5e03\u4e0b\u4e86\u60ca \u4eba\u7684\u9a97\u5c40\uff0c\u5c82\u4e0d\u662f\u5c0f\u9898\u5927\u4f5c\u4e86\u3002\u201d \u3000\u3000\u4e00\u9762\u548c\u6765\u4eba\u5bf9\u8bdd\uff0c\u4e00\u9762\u987e\u89c6\u5168\u573a\uff0c\u7fa4\u8c6a\u7ecf\u6b64\u4e00\u6bb5\u51b7\u9759\u601d\u8651\uff0c\u5927\u90fd\u6062\u590d\u4e86\u8c6a\u6c14\uff0c\u6218\u5fd7\u4e5f\u5728 \u590d\u5143\u4e4b\u4e2d\uff0c\u4e5f\u8be5\u63ed\u5f00\u8fd9\u4e2a\u8001\u4eba\u7684\u79d8\u5bc6\u4e86\u3002 \u3000\u3000\u94f6\u8863\u8001\u4eba\u6012\u9053\uff1a\u201c\u8001\u592b\u5e03\u4e0b\u4e86\u4ec0\u4e48\u9a97\u5c40\uff1f\u6ee1\u53e3\u7684\u80e1\u8bf4\u516b\u9053\u3002\u201d \u3000\u3000\u8d3e\u82f1\u4e5f\u5728\u7ec6\u4f5c\u63a8\u65ad\uff0c\u7531\u8fde\u756a\u5bf9\u8bdd\u4e4b\u4e2d\uff0c\u5224\u5b9a\u4e86\u6765\u4eba\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\uff0c\u5fc3\u4e2d\u6709\u70b9\u5931\u671b\uff0c\u4f46\u4e5f \u6fc0\u8d77\u4e86\u653e\u624b\u4e00\u640f\u7684\u8c6a\u6c14\u3002\u51b3\u5fc3\u6b63\u5f0f\u63ed\u7a7f\u5e03\u5c40\uff0c\u4f7f\u7fa4\u8c6a\u6218\u5fd7\u5c3d\u590d\uff0c\u4e5f\u51c6\u5907\u8bc9\u8bf8\u6b66\u529f\uff0c\u4e00\u51b3\u80dc\u8d1f \u4e86\u3002 \u3000\u3000\u201c\u8001\u5148\u751f\u5e0c\u671b\u6211\u8bf4\u51fa\u6765\uff0c\u8d3e\u67d0\u4eba\u7edd\u4e0d\u4f1a\u8ba9\u4f60\u5931\u671b\uff0c\u5f53\u4eca\u4e4b\u4e16\u4e5f\u8bb8\u771f\u6709\u4ecb\u4e8e\u4eba\u4ed9\u4e4b\u95f4\u9ad8\u624b\uff0c \u80fd\u9a6d\u98ce\u800c\u884c\uff0c\u4e5f\u6709\u5251\u9053\u4e2d\u7edd\u9876\u9ad8\u624b\uff0c\u9a6d\u5251\u98de\u884c\uff0c\u6740\u4eba\u4e8e\u767e\u4e08\u4e4b\u5916\uff0c\u4f46\u9601\u4e0b\u65e2\u65e0\u80fd\u9a6d\u5251\uff0c\u4e5f\u4e0d \u80fd\u9a7e\u98ce\uff0c\u9a91\u4e00\u53ea\u5927\u9e1f\uff0c\u7531\u9ad8\u7a7a\u8df3\u4e0b\u6765\uff0c\u80fd\u591f\u4e0d\u53d7\u4f24\u5bb3\uff0c\u5168\u4ed7\u90a3\u4ef6\u5927\u888d\uff0c\u5e2e\u52a9\u5378\u53bb\u5760\u843d\u7684\u52b2\u9053\u3002 \u5f53\u7136\uff0c\u8fd9\u4e5f\u8981\u4e0b\u4e00\u756a\u529f\u592b\uff0c\u7ec3\u5b83\u4e2a\u4e00\u5e74\u534a\u8f7d\u7684\u624d\u80fd\u719f\u7ec3\uff0c\u5927\u888d\u91c7\u7528\u94f6\u8f89\u989c\u8272\uff0c\u662f\u4e3a\u4e86\u914d\u5408\u6708 \u8272\uff0c\u626c\u5dde\u7684\u4e8c\u5206\u660e\u6708\u591c\uff0c\u4e5f\u6ca1\u6709\u5927\u767d\u5929\u770b\u5f97\u6e05\u695a\uff0c\u4f46\u4f1a\u7ed9\u4eba\u4e00\u79cd\u9519\u89c9\uff0c\u660e\u6708\u5728\u5929\uff0c\u770b\u5f97\u5341\u5206 \u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bdd\uff0c\u6709\u7740\u6781\u5f3a\u7684\u8bf4\u7406\u57fa\u7840\uff0c\u5c31\u7b97\u4e0d\u5168\u662f\u4e8b\u5b9e\uff0c\u4e5f\u8ba9\u4eba\u5fc3\u60a6\u8bda\u670d\u3002 \u3000\u3000\u679c\u7136\uff0c\u56f4\u5b88\u5728\u56db\u5468\u7684\u7fa4\u8c6a\uff0c\u4e2a\u4e2a\u795e\u60c5\u5c3d\u52a8\uff0c\u6218\u5fd7\u5c3d\u590d\u3002 \u3000\u3000\u201c\u8bf4\u7684\u597d\uff0c\u91d1\u767e\u5e74\u8bf7\u4e86\u4f60\u8fd9\u4e48\u4e00\u4e2a\u603b\u7ba1\uff0c\u4e5f\u7b97\u662f\u6709\u76ee\u6709\u73e0\u4e86\u3002\u201d \u3000\u3000\u5bbd\u5927\u7684\u94f6\u888d\u7a81\u7136\u88c2\u5f00\uff0c\u51fa\u73b0\u4e00\u4e2a\u5168\u8eab\u94f6\u767d\u52b2\u88c5\u7684\u4e2d\u5e74\u4eba\uff0c\u80f8\u524d\u957f\u9aef\u98d8\u52a8\uff0c\u559d\u58f0\u9053\uff1a\u201c\u91d1 \u767e\u5e74\u5728\u54ea\u91cc\uff0c\u8981\u4ed6\u51fa\u6765\u548c\u8001\u5b50\u5bf9\u8d28\uff0c\u770b\u4ed6\u80af\u4e0d\u80af\u627f\u8ba4\uff0c\u4fb5\u541e\u4e86\u8001\u5b50\u7684\u6c34\u706b\u76f8\u6d4e\uff1f\u201d \u3000\u3000------------- \u3000\u3000\u5b64\u5251\u751f  \u626b\u63cf\u3000  zerotang  OCR  \uff0c\u65e7\u96e8\u697c\u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c \u516b \u7ae0 \u3000\u3000\u672c\u6765\u662f\u81ea\u79f0\u8001\u592b\uff0c\u4f46\u8131\u4e0b\u957f\u888d\u4e4b\u540e\uff0c\u771f\u76f8\u6bd5\u9732\uff0c\u81ea\u89c9\u8fd8\u4e0d\u591f\u8001\uff0c\u5c31\u6539\u8001\u5b50\u4e86\u3002\u94c1\u7fce\u4eba\u4e5f \u5b8c\u5168\u5730\u51b7\u9759\u4e0b\u6765\uff0c\u4e09\u5e74\u7684\u6355\u5934\u751f\u6daf\uff0c\u4f7f\u4ed6\u6bd4\u4e00\u822c\u6c5f\u6e56\u540c\u9053\uff0c\u591a\u4e86\u4e00\u5206\u5206\u6790\u4e8b\u7269\u7684\u80fd\u529b\uff0c\u8fd9\u4e24 \u6279\u4eba\u7269\uff0c\u4f3c\u662f\u90fd\u4e3a\u4e09\u5b9d\u800c\u6765\uff0c\u6700\u6ca1\u6709\u7528\u5904\u7684\u6c34\u706b\u76f8\u6d4e\uff0c\u5f88\u53ef\u80fd\u662f\u4e09\u5b9d\u4e2d\u6700\u91cd\u8981\u7684\u4e00\u5b9d\u4e86\uff0c\u4f46 \u5b83\u80fd\u4ee3\u8868\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u8fd9\u4e9b\u4eba\u4f3c\u662f\u7eaf\u4e3a\u4e89\u593a\u4e09\u5b9d\u800c\u6765\uff0c\u548c\u9a6c\u516c\u5b50\u7684\u547d\u6848\uff0c\u6ca1\u6709\u592a\u5927\u5173\u7cfb\uff01 \u3000\u3000\u4f46\u8fd9\u4e9b\u4eba\u548c\u68a6\u5e7b\u4e4b\u5200\uff0c\u662f\u5426\u6709\u4e00\u79cd\u6a2a\u7684\u8fde\u7cfb\u5462\uff1f\u5f53\u7136\u4e5f\u53ef\u80fd\u6709\u4e00\u4e9b\u7eb5\u6a2a\u4ea4\u63a5\u7684\u5173\u7cfb\uff0c\u81f3 \u5c11\u4ed6\u4eec\u6709\u4e00\u4e2a\u76f8\u540c\u7684\u76ee\u7684\uff0c\u90fd\u5728\u89ca\u89ce\u4e09\u5b9d\uff0c\u8fd9\u53ef\u80fd\u4f7f\u4ed6\u4eec\u5408\u4f5c\uff0c\u4e00\u65e6\u5b9d\u7269\u5f97\u5230\u624b\uff0c\u53c8\u53ef\u80fd\u53cd \u76ee\u5927\u62fc\uff0c\u6740\u4e2a\u4f60\u6b7b\u6211\u6d3b\u51fa\u6765\uff0c\u6c5f\u6e56\u4e0a\u7684\u5f8b\u4f8b\uff0c\u4f3c\u4e4e\u7ef4\u7cfb\u5728\u4e00\u79cd\u56fa\u6709\u7684\u56e0\u679c\u8fd0\u52a8\u4e2d\uff0c\u4ee5\u7259\u8fd8\u7259\uff0c \u6076\u65e0\u5584\u7ec8\u3002 \u3000\u3000\u8d3e\u82f1\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u662f\u8c01\uff0c\u548c\u655d\u4e1c\u4e3b\u6709\u8fc7\u4ec0\u4e48\u7ea0\u845b\uff0c\u8fd9\u4e9b\u8fc7\u8282\u4e0e\u6211\u4eec\u65e0\u5173\uff0c \u4f60\u591c\u95ef\u91d1\u5e9c\uff0c\u58f0\u8a00\u62a2\u52ab\uff0c\u662f\u5341\u8db3\u7684\u76d7\u532a\u884c\u4e3a\uff0c\u8d3e\u67d0\u5148\u8981\u6382\u6382\u4f60\u6709\u591a\u5c11\u4efd\u91cf\uff1f\u201d\u7a81\u7136\u4e00\u4e0a\u6b65\uff0c \u5de6\u62f3\u51fb\u51fa\uff0c\u53f3\u624b\u6298\u6247\u4e00\u5f20\uff0c\u7ea2\u5149\u95ea\u52a8\uff0c\u6a2a\u524a\u54bd\u5589\uff0c\u653b\u52bf\u5341\u5206\u51cc\u5389\u3002 \u3000\u3000\u94f6\u8863\u4eba\u53f3\u624b\u4e00\u626c\uff0c\u95ea\u51fa\u4e00\u8f6e\u9752\u5149\uff0c\u8fce\u5411\u6298\u6247\uff0c\u5de6\u62f3\u786c\u5411\u8d3e\u82f1\u7684\u62f3\u4e0a\u8fce\u53bb\u3002\u84ec\u7136\u4e00\u58f0\uff0c\u4e24 \u53ea\u94c1\u62f3\uff0c\u649e\u5728\u4e00\u8d77\uff0c\u8d3e\u82f1\u539f\u5730\u672a\u52a8\uff0c\u90a3\u94f6\u8863\u4eba\u5374\u88ab\u9707\u9000\u4e86\u4e00\u6b65\u3002\u4f46\u8d3e\u82f1\u624b\u4e2d\u6298\u6247\uff0c\u5374\u88ab\u90a3\u4e00 \u8f6e\u9752\u5149\u9707\u5f00\u3002 \u3000\u3000\u8d3e\u82f1\u5fc3\u4e2d\u6697\u9053\uff1a\u4eca\u591c\u6765\u4eba\uff0c\u4f3c\u4e4e\u90fd\u662f\u9ad8\u624b\u4e2d\u7684\u9876\u5c16\u4eba\u7269\uff0c\u5f53\u4e0b\u6296\u64de\u7cbe\u795e\uff0c\u5c55\u5f00\u4e86\u4e00\u8f6e\u5feb \u653b\uff0c\u624b\u4e2d\u6298\u6247\u5ffd\u5f20\u5ffd\u5408\uff0c\u5408\u4f5c\u5251\u523a\uff0c\u5f20\u4f5c\u5200\u5288\uff0c\u5f20\u5408\u4e4b\u95f4\uff0c\u7ea2\u5149\u95ea\u52a8\uff0c\u8000\u773c\u751f\u82b1\u3002 \u3000\u3000\u4ed6\u7684\u653b\u52bf\u592a\u5feb\uff0c\u7adf\u7136\u65e0\u6cd5\u770b\u6e05\u695a\u90a3\u6247\u9762\u7684\u7ea2\u5149\uff0c\u662f\u4f55\u7f18\u6545\u3002 \u3000\u3000\u94f6\u8863\u4eba\u7528\u7684\u5175\u5203\uff0c\u66f4\u662f\u5947\u602a\uff0c\u5f62\u5982\u4e00\u4e2a\u4e7e\u5764\u5708\uff0c\u53f3\u624b\u63e1\u5728\u9884\u7559\u63e1\u628a\u4e0a\uff0c\u9752\u5149\u95ea\u52a8\uff0c\u5bd2\u8292 \u88ad\u4eba\uff0c\u8fb9\u7f18\u5168\u662f\u950b\u5229\u7684\u5203\u9762\uff0c\u5999\u5904\u662f\u53ef\u5927\u53ef\u5c0f\uff0c\u5ffd\u653e\u5ffd\u6536\uff0c\u5168\u5728\u4eba\u64cd\u63a7\u4e4b\u4e0b\u3002 \u3000\u3000\u6c5f\u6e56\u4e0a\u5175\u5203\u4f17\u591a\uff0c\u5341\u516b\u822c\u5175\u5668\u4e4b\u5916\uff0c\u8fd8\u6709\u5f88\u591a\u7684\u5916\u95e8\u5175\u5203\uff0c\u4f46\u8fd9\u7b49\u5706\u5f62\u5982\u5708\u7684\u602a\u5200\uff0c\u5374 \u662f\u4ece\u672a\u89c1\u8fc7\u3002\u6240\u4ee5\uff0c\u6ca1\u6709\u4eba\u80fd\u53eb\u51fa\u5b83\u771f\u6b63\u7684\u540d\u5b57\uff0c\u53ea\u597d\u53eb\u73af\u5f62\u5200\u4e86\u3002 \u3000\u3000\u53ea\u542c\u653e\u9e64\u697c\u4e0a\u4f20\u6765\u4e00\u9635\u5f29\u7bad\u7834\u7a7a\u4e4b\u58f0\uff0c\u7d27\u63a5\u54cd\u8d77\u4e86\u5175\u5203\u89e6\u63a5\u7684\u91d1\u94c1\u4ea4\u9e23\u3002\u8d3e\u82f1\u6012\u53f1\u4e00\u58f0\uff0c \u5de6\u624b\u7a81\u7136\u591a\u4e86\u4e00\u628a\u77ed\u5200\uff0c\u5200\u6247\u914d\u5408\uff0c\u653b\u52bf\u5927\u76db\uff0c\u94f6\u8863\u4eba\u7acb\u523b\u88ab\u56f4\u5728\u6247\u5f71\u548c\u5200\u5149\u4e4b\u4e2d\uff0c\u8033\u9645\u8fd8 \u542c\u5217\u8d3e\u82f1\u7684\u5927\u559d\u4e4b\u58f0\uff0c\u9053\uff1a\u201c\u9a70\u63f4\u653e\u9e64\u697c\u3002\u201d \u3000\u3000\u5176\u5b9e\uff0c\u7528\u4e0d\u7740\u4ed6\u559d\u53eb\uff0c\u5bd2\u5c71\u56db\u5200\u5df2\u62a2\u5148\u5411\u697c\u4e0a\u5954\u53bb\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u6ca1\u6709\u62a2\u767b\u4e0a\u697c\uff0c\u5927\u90e8\u5206\u4eba\u4e5f\u90fd\u672a\u5411\u697c\u4e0a\u6500\u767b\uff0c\u697c\u9876\u72ed\u5c0f\uff0c\u4e0a\u53bb\u4e86\u592a\u591a\u7684\u4eba\uff0c\u53cd \u800c\u65e0\u6cd5\u65bd\u5c55\uff0c\u5bd2\u5c71\u56db\u5200\u662f\u5b88\u697c\u4e3b\u529b\uff0c\u4e5f\u662f\u963b\u6740\u9ad8\u624b\uff0c\u56db\u628a\u5200\u914d\u5408\u5f97\u5b9c\uff0c\u4eba\u4e00\u591a\uff0c\u53cd\u800c\u59a8\u5bb3\u5230 \u4ed6\u4eec\u5408\u51fb\u5200\u9635\u7684\u5a01\u529b\u3002 \u3000\u3000\u4f46\u6240\u6709\u7684\u4eba\uff0c\u90fd\u5728\u5411\u56db\u5916\u79fb\u52a8\uff0c\u6269\u5927\u533a\u57df\uff0c\u51c6\u5907\u5835\u622a\u51b2\u4e0b\u697c\u6765\u7684\u654c\u4eba\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4e5f\u6709\u7b2c\u4e8c\u6279\u4e0a\u697c\u9a70\u63f4\u7684\u4eba\uff0c\u662f\u4e09\u4e2a\u7528\u5251\u7684\u9ad8\u624b\uff0c\u4ed6\u4eec\u8d70\u7684\u4e0d\u5feb\uff0c\u4f46\u5251\u6267\u624b\u4e2d\uff0c\u5168 \u795e\u6212\u5907\u800c\u4e0a\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u51fa\u5251\u653b\u654c\u3002 \u3000\u3000\u4ed6\u4eec\u5fc3\u4e2d\u660e\u767d\uff0c\u4e0d\u662f\u4e89\u98ce\u5ea6\u7684\u65f6\u5019\uff0c\u53ea\u8981\u5bd2\u5c71\u56db\u5200\u4e0d\u9000\u4e0b\u6765\uff0c\u4ed6\u4eec\u8d76\u5230\u697c\u9876\u4e5f\u63d2\u4e0d\u4e0a\u624b\uff0c \u5730\u65b9\u592a\u5c0f\u4e86\u3002 \u3000\u3000\u8d3e\u82f1\u7684\u6280\u827a\uff0c\u9010\u6e10\u5730\u53d1\u6325\uff0c\u5c55\u73b0\u4e86\u771f\u6b63\u7684\u6b66\u529f\uff0c\u5feb\u901f\u7684\u8fd0\u8f6c\uff0c\u8fde\u4eba\u5f71\u4e5f\u770b\u4e0d\u5230\u4e86\uff0c\u53ea\u89c1 \u4e00\u7247\u95ea\u8f6c\u7684\u7ea2\u5f71\uff0c\u5939\u5e26\u7740\u4e00\u9053\u5bd2\u8292\u5728\u6eda\u52a8\u3002 \u3000\u3000\u94c1\u7fce\u6697\u6697\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u5fd6\u9053\uff1a\u5389\u5bb3\u5440\uff01\u5389\u5bb3\uff0c\u8fd9\u4eba\u7684\u6280\u827a\u4e4b\u7cbe\uff0c\u5b9e\u5df2\u5165\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c \u90a3\u94f6\u8863\u4eba\u867d\u5c5e\u7f55\u89c1\u9ad8\u624b\uff0c\u6050\u4ea6\u975e\u8d3e\u82f1\u4e4b\u654c\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u7ea2\u5149\u655b\u6536\uff0c\u62fc\u6740\u573a\u4e2d\u60c5\u666f\uff0c\u5728\u660e\u6708\u8000\u7167\u4e0b\u6e05\u6670\u53ef\u89c1\uff0c\u94f6\u8863\u4eba\u6ca1\u6709\u547c\u53eb\uff0c\u4f46\u4f24\u7684 \u5374\u5341\u5206\u51c4\u60e8\uff0c\u5168\u8eab\u8863\u670d\uff0c\u90fd\u5df2\u88ab\u9c9c\u8840\u67d3\u7ea2\uff0c\u65e0\u6cd5\u4f30\u7b97\u51fa\u4ed6\u8eab\u4e0a\u6709\u591a\u5c11\u4f24\u75d5\uff0c\u56e0\u4e3a\u5168\u8eab\u90fd\u662f\u8840\uff0c \u624b\u4e2d\u7684\u73af\u5f62\u5200\uff0c\u5df2\u653e\u5927\u6210\u4e00\u5c3a\u65b9\u5706\uff0c\u4ecd\u7136\u7d27\u7d27\u5730\u63e1\u5728\u624b\u4e2d\uff0c\u8fd9\u8bf4\u660e\u4e86\uff0c\u4ed6\u867d\u7136\u5168\u8eab\u662f\u4f24\uff0c\u4f46 \u4ecd\u672a\u653e\u5f03\u62b5\u6297\u3002 \u3000\u3000\u4ed6\u6709\u8fc7\u4eba\u7684\u8010\u529b\uff0c\u867d\u7136\u8eab\u4e2d\u4e86\u6570\u5341\u767e\u5200\uff0c\u4f46\u5374\u54ac\u7259\u82e6\u5fcd\uff0c\u672a\u53d1\u51fa\u4e00\u58f0\u75bc\u53eb\u3002\u8d3e\u82f1\u56de\u987e\u4e86 \u5b88\u5728\u697c\u95e8\u53e3\u5904\u7684\u7fa4\u8c6a\u4e00\u773c\uff0c\u9053\uff1a\u201c\u697c\u4e0a\u60c5\u5f62\u5982\u4f55\uff1f\u201d \u3000\u3000\u4e00\u4e2a\u6b66\u58eb\u6b20\u8eab\u5e94\u9053\uff1a\u201c\u8fd8\u4e0d\u6e05\u695a\uff0c\u4f46\u654c\u4eba\u4ecd\u5728\u697c\u4e0a\uff0c\u5bd2\u5c71\u56db\u5200\u5df2\u4e0a\u697c\u591a\u65f6\uff0c\u7b2c\u4e8c\u6279\u4e0a\u697c \u7684\u4eba\u624b\u662f\u7f57\u6d6e\u4e09\u5251\u3002\u201d \u3000\u3000\u8d3e\u82f1\u70b9\u70b9\u5934\uff0c\u672a\u518d\u591a\u8a00\uff0c\u4ed6\u5fc3\u4e2d\u660e\u767d\uff0c\u8fd9\u4e03\u4e2a\u4eba\u662f\u8fd9\u7fa4\u6b66\u58eb\u4e2d\u6700\u7cbe\u9510\u7684\u5200\u5ba2\u3001\u5251\u624b\uff0c\u7531 \u4ed6\u4eec\u4e0a\u697c\u641c\u6740\u654c\u4eba\uff0c\u662f\u6700\u4f73\u7684\u4eba\u9009\u3002\u8fd9\u4e03\u4eba\u5982\u662f\u4ecd\u4e0d\u80fd\u5236\u670d\u654c\u4eba\uff0c\u8fd9\u7fa4\u6b66\u58eb\u4e2d\uff0c\u5c31\u518d\u65e0\u66f4\u9002 \u5408\u7684\u4eba\u9009\u4e86\u3002 \u3000\u3000\u8d3e\u82f1\u5bf9\u4e03\u4eba\u6709\u7740\u5f88\u5927\u7684\u4fe1\u5fc3\uff0c\u5fc3\u4e2d\u4e5f\u5b89\u5b9a\u4e0d\u5c11\uff0c\u56de\u987e\u4e86\u90a3\u4f24\u75d5\u7d2f\u7d2f\u7684\u94f6\u8863\u4eba\u4e00\u773c\uff0c\u9053\uff1a \u201c\u89ca\u89ce\u4e09\u5b9d\uff0c\u6267\u6756\u884c\u62a2\uff0c\u7f6a\u672c\u8be5\u6b7b\uff0c\u4f46\u6211\u4e0d\u6740\u4f60\uff0c\u4f60\u53ef\u4ee5\u8d70\u4e86\u3002\u201d\u8868\u73b0\u51fa\u8c41\u7136\u5927\u5ea6\uff0c\u78ca\u78ca\u98ce \u8303\u3002 \u3000\u3000\u94c1\u7fce\u5fc3\u4e2d\u5fd6\u9053\uff1a\u8fd9\u4eba\u4f24\u7684\u5982\u6b64\u4e4b\u91cd\uff0c\u5931\u8840\u751a\u591a\uff0c\u53ea\u6015\u662f\u8d70\u4e0d\u4e86\u5566\uff01 \u3000\u3000\u679c\u7136\uff0c\u94f6\u8863\u4eba\u8f6c\u8eab\u8d70\u4e86\u4e24\u6b65\uff0c\u4eba\u5df2\u4e0d\u652f\uff0c\u8eab\u8eaf\u4e00\u9635\u6447\u6643\uff0c\u5012\u5728\u4e86\u5730\u4e0a\u3002 \u3000\u3000\u8d3e\u82f1\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u62ac\u4e0b\u53bb\uff0c\u66ff\u4ed6\u6577\u836f\u7597\u4f24\uff0c\u8fd9\u4eba\u6b66\u529f\u9ad8\u660e\uff0c\u751f\u6027\u5014\u5f3a\uff0c\u5f88\u5feb\u5c31\u53ef\u80fd \u597d\uff0c\u4ed6\u4f24\u53e3\u867d\u591a\uff0c\u4f46\u5374\u662f\u76ae\u8089\u4e4b\u4f24\uff0c\u4e3a\u4e86\u9632\u5236\u4ed6\u7a81\u7136\u51fa\u5200\u4f24\u4eba\uff0c\u62ff\u5f00\u4ed6\u7684\u5175\u5203\uff0c\u5236\u4f4f\u53cc\u81c2\u7a74 \u9053\u3002\u201d \u3000\u3000\u4e24\u4e2a\u6b66\u58eb\u62ac\u8d77\u4f24\u8005\uff0c\u5e94\u58f0\u800c\u53bb\u3002 \u3000\u3000\u8d3e\u82f1\u62ac\u5934\u770b\u770b\u89c2\u9e64\u697c\uff0c\u9053\uff1a\u201c\u9053\u957f\u8bf7\u5b88\u62a4\u697c\u4e0b\uff0c\u6211\u4e0a\u697c\u77a7\u77a7\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u77a7\u4e86\uff0c\u697c\u4e0a\u53ea\u6709\u4e00\u4e2a\u654c\u4eba\uff0c\u5df2\u88ab\u6211\u4eec\u5144\u5f1f\u6740\u6b7b\u4e86\u3002\u201d \u3000\u3000\u5bd2\u5c71\u56db\u5200\u9c7c\u8d2f\u6b65\u51fa\u697c\u95e8\uff0c\u8001\u5927\u8d70\u5728\u6700\u524d\u9762\uff0c\u8001\u4e8c\u3001\u8001\u4e09\u62ac\u7740\u4e00\u5177\u5c38\u4f53\uff0c\u8001\u56db\u6267\u5200\u65ad\u540e\u3002 \u3000\u3000\u8d3e\u82f1\u8fce\u4e0a\u53bb\uff0c\u4ed4\u7ec6\u77a7\u4e86\u5c38\u4f53\u4e00\u773c\uff0c\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u5168\u662f\u4e0d\u76f8\u8bc6\u7684\u4eba\u7269\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u8fd9\u7b49 \u6765\u8def\u4e0d\u660e\u7684\u670b\u53cb\uff0c\u6df7\u5165\u4e86\u626c\u5dde\u3002\u800c\u4e14\uff0c\u4e2a\u4e2a\u6b66\u529f\u9ad8\u5f3a\uff0c\u771f\u662f\u4ee4\u4eba\u9707\u60ca\uff0c\u6253\u4e86\u534a\u591c\uff0c\u8fde\u68a6\u5e7b\u4e4b \u5200\u7684\u624b\u4e0b\uff0c\u4e5f\u6ca1\u6709\u89c1\u5230\u4e00\u4e2a\uff0c\u8fd9\u5177\u5c38\u4f53\u57cb\u4e86\u7b97\u5566\uff01\u201d \u3000\u3000\u867d\u662f\u81ea\u8a00\u81ea\u8bed\uff0c\u4f46\u76ee\u5149\u5374\u770b\u5411\u94c1\u7fce\uff0c\u4f3c\u662f\u5f81\u8be2\u4ed6\u7684\u610f\u89c1\u3002 \u3000\u3000\u94c1\u7fce\u88c5\u4f5c\u6ca1\u770b\u5230\uff0c\u4e5f\u4e0d\u53d1\u4e00\u8bed\u3002 \u3000\u3000\u8d3e\u82f1\u4e00\u6325\u624b\uff0c\u5c38\u4f53\u62ac\u8d70\uff0c\u5374\u56de\u9996\u5bf9\u5929\u67ab\u9053\u957f\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\uff0c\u5f7c\u6b64\u4e0d\u76f8\u8bc6\uff0c\u5374\u540c\u65f6\u5fc3\u6000 \u8d2a\u5a6a\uff0c\u5984\u56fe\u53d6\u5f97\u4e09\u5b9d\uff0c\u4f46\u5982\u679c\u4ed6\u4eec\u4e32\u8fde\u4e86\u8d77\u6765\uff0c\u5c31\u5f88\u9ebb\u70e6\u4e86\uff0c\u9053\u957f\u4f55\u4ee5\u6559\u6211\uff1f\u201d \u3000\u3000\u201c\u5148\u627e\u51fa\u4ed6\u4eec\u662f\u8c01\uff0c\u201d\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u67e5\u660e\u4ed6\u4eec\u6765\u5386\uff0c\u90a3\u4e2a\u53d7\u4f24\u7684\u94f6\u8863\u4eba\uff0c\u5e94\u8be5\u662f\u5f88\u597d \u7684\u4e00\u6761\u7ebf\u7d22\u3002\u201d \u3000\u3000\u8d3e\u82f1\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u662f\u3002\u201d\u76ee\u5149\u73af\u987e\u4e86\u5168\u573a\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5404\u4f4d\uff0c\u8bf7\u56de\u9632\u5b88\u4e4b\u533a\uff0c\u5929 \u4e00\u4eae\uff0c\u8bf8\u4f4d\u5c31\u53ef\u4ee5\u56de\u5230\u5ba2\u623f\u4f11\u606f\u4e86\u3002\u201d \u3000\u3000\u94c1\u7fce\u4eba\u5230\u653e\u9e64\u697c\u65f6\uff0c\u5df2\u6697\u4e2d\u4f5c\u4e86\u51b3\u5b9a\uff0c\u80fd\u4e0d\u51fa\u624b\uff0c\u5c31\u4e0d\u51fa\u624b\uff0c\u5168\u529b\u89c2\u5bdf\u4eba\u4eba\u4e8b\u4e8b\u7684\u53d8\u5316\uff0c \u8981\u9ed8\u8bb0\u6765\u4eba\u7684\u51fa\u8eab\u6b66\u529f\uff0c\u8981\u770b\u51fa\u91d1\u5e9c\u4e2d\u7684\u9ad8\u624b\u6280\u827a\uff0c\u5f53\u7136\uff0c\u6700\u91cd\u8981\u7684\u4e00\u70b9\uff0c\u662f\u8981\u89c2\u5bdf\u8d3e\u82f1\u7684 \u8eab\u624b\u3002 \u3000\u3000\u5bf9\u5916\u654c\u7684\u89c2\u5bdf\u5fc3\u5f97\uff0c\u5341\u5206\u5931\u671b\uff0c\u4e24\u4e2a\u9ed1\u8863\u4eba\u5251\u672f\u5947\u5e7b\uff0c\u4f46\u8499\u9762\u8fce\u654c\uff0c\u8fde\u9762\u4e5f\u672a\u89c1\u4e00\u6b21\uff0c \u4e0d\u77e5\u6765\u81ea\u4f55\u5904\uff0c\u4e5f\u4e0d\u77e5\u53bb\u5411\u4f55\u65b9\uff0c\u4e5f\u770b\u4e0d\u51fa\u5251\u6cd5\u6765\u5386\u3002 \u3000\u3000\u6700\u5927\u7684\u6536\u83b7\u662f\u770b\u5230\u4e86\u8d3e\u82f1\u7684\u6b66\u529f\uff0c\u867d\u7136\u770b\u4e0d\u51fa\u4ec0\u4e48\u5934\u7eea\uff0c\u4f46\u5341\u5206\u9ad8\u5f3a\u662f\u4e0d\u4f1a\u9519\u4e86\u3002\u5c06\u4eba \u6bd4\u5df1\uff0c\u94c1\u7fce\u5df2\u81ea\u8ba4\u7edd\u975e\u654c\u624b\u3002\u5bd2\u5c71\u56db\u5200\u5f88\u9ad8\u660e\uff0c\u4e0d\u4ee5\u6210\u8d25\u8bba\u82f1\u96c4\uff0c\u56db\u4eba\u90fd\u7b97\u662f\u5f88\u6770\u51fa\u7684\u5200\u5ba2\u3002 \u3000\u3000\u4e8b\u8fd1\u5c3e\u58f0\uff0c\u5929\u4ea6\u5feb\u4eae\uff0c\u5e94\u8be5\u544a\u8f9e\u4e86\uff0c\u884c\u8fd1\u8d3e\u82f1\uff0c\u8bf4\u51fa\u4e86\u91cd\u521b\u5357\u533a\u654c\u4eba\u7684\u7ecf\u8fc7\uff0c\u5f53\u7136\uff0c\u4e5f \u628a\u5feb\u5200\u738b\u526a\u5938\u8d5e\u4e86\u4e00\u756a\u3002 \u3000\u3000\u8d3e\u82f1\u53ea\u662f\u70b9\u5934\u5fae\u7b11\uff0c\u6ca1\u6709\u4efb\u4f55\u56de\u7b54\u3002 \u3000\u3000\u94c1\u7fce\u56de\u5230\u5367\u5ba4\uff0c\u7761\u4e86\u4e00\u89c9\uff0c\u9192\u6765\u65f6\u5929\u5df2\u8fc7\u5348\uff0c\u7a81\u7136\u60f3\u5230\uff0c\u5e94\u8be5\u56de\u5e9c\u8859\u4e00\u8d9f\uff0c\u89c1\u89c1\u738b\u5927\u4eba\uff0c \u6700\u91cd\u8981\u7684\u662f\u5e0c\u671b\u80fd\u89c1\u675c\u671b\u6708\u4e00\u6b21\uff0c\u544a\u8bc9\u4ed6\u91d1\u5e9c\u4e2d\u53d1\u751f\u7684\u4e8b\u60c5\u3002\u539f\u6765\u9884\u6599\u8d3e\u82f1\u4e0d\u4f1a\u8d5e\u6210\uff0c\u6b64\u4e8b \u5f97\u5927\u8d39\u4e00\u756a\u53e3\u820c\uff0c\u5374\u4e0d\u6599\u8d3e\u82f1\u4e00\u53e3\u7b54\u5141\uff0c\u53ea\u662f\u5631\u5490\u4ed6\uff0c\u5929\u9ed1\u4e4b\u524d\u4e00\u5b9a\u8981\u56de\u6765\uff0c\u884c\u52a8\u5c0f\u5fc3\u4e00\u4e9b\uff0c \u522b\u9732\u51fa\u7834\u7efd\u3002 \u3000\u3000\u94c1\u7fce\u4e0d\u4f46\u6362\u4e86\u88c5\u675f\uff0c\u800c\u4e14\u4e3e\u6b62\u4e5f\u975e\u5e38\u5c0f\u5fc3\uff0c\u4e00\u8def\u4e0a\uff0c\u65e0\u60ca\u65e0\u9669\u5730\u56de\u5230\u4e86\u5e9c\u8859\u3002\u6f5c\u5165\u4e86\u4e8c \u8fdb\u9662\u5185\uff0c\u5df2\u88ab\u5b88\u536b\u53d1\u89c9\uff0c\u4f55\u5927\u5149\u98de\u8e6c\u800c\u81f3\uff0c\u53d1\u89c9\u7adf\u662f\u94c1\u7fce\uff0c\u94c1\u7fce\u6447\u5934\u793a\u610f\u4f55\u5927\u5149\uff0c\u4e0d\u8981\u547c\u53eb \u51fa\u58f0\u3002 \u3000\u3000\u4f55\u5927\u5149\u70b9\u70b9\u5934\uff0c\u5e26\u7740\u94c1\u7fce\u76f4\u5954\u4e66\u623f\u3002 \u3000\u3000\u738b\u5927\u4eba\u6ca1\u6709\u4f11\u606f\uff0c\u5374\u5728\u548c\u5f20\u5b9d\u5584\u7814\u7a76\u6848\u60c5\u3002\u738b\u5c11\u537f\u786e\u662f\u4e00\u4e2a\u597d\u5b98\uff0c\u4e0d\u4f46\u806a\u660e\u7edd\u9876\uff0c\u6ee1\u8179 \u8bd7\u4e66\uff0c\u800c\u4e14\u5b5c\u5b5c\u4e0d\u5026\u5730\u6295\u5165\u5fc3\u8840\uff0c\u7814\u5546\u6848\u60c5\uff0c\u89c1\u94c1\u7fce\u7a81\u7136\u5f52\u6765\uff0c\u6709\u70b9\u610f\u5916\u7684\u611f\u89c9\uff0c\u6014\u4e86\u4e00\u6014\uff0c \u9053\uff1a\u201c\u91d1\u5e9c\u4e2d\u5341\u9762\u57cb\u4f0f\uff0c\u662f\u5426\u5df2\u6536\u5230\u4e86\u610f\u5916\u7684\u6548\u679c\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u4eba\uff0c\u91d1\u5458\u5916\u4e0d\u60dc\u82b1\u8d39\uff0c\u8bf7\u5230\u4e86\u4e0d\u5c11\u9ad8\u624b\uff0c\u201d\u94c1\u7fce\u63a5\u9053\uff1a\u201c\u8d3e\u82f1\u6587\u6b66\u53cc\u5168\uff0c\u9020\u5175\u8c03\u5c06\uff0c \u5e03\u7f72\u6709\u65b9\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u66f4\u662f\u4ee4\u4eba\u60ca\u53f9\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u4e0d\u7b80\u5355\uff0c\u5c48\u5c31\u91d1\u5e9c\u603b\u7ba1\uff0c\u4e5f\u8bb8\u522b\u6709\u6240\u56fe\uff0c\u201d\u738b\u5927\u4eba\u9053\uff1a\u201c\u4f60\u6697\u4e2d\u4fa6\u67e5\u4e00\u4e0b\uff0c\u4e0d \u8981\u6025\u8e81\uff0c\u4ee5\u514d\u8bef\u4e8b\uff0c\u9a6c\u516c\u5b50\u4e00\u6848\u9650\u671f\u5f88\u5feb\u5c31\u5230\uff0c\u8981\u4ee5\u4fa6\u5bdf\u9a6c\u6848\u4e3a\u4e3b\uff01\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u660e\u767d\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u6211\u4e5f\u5947\u602a\uff0c\u5982\u6b64\u4eba\u624d\uff0c\u600e\u80af\u5c48\u5c31\uff0c\u4f46\u76ee\u524d\u4e3a\u6b62\uff0c\u72d0\u72f8\u8fd8\u672a\u9732 \u51fa\u5c3e\u5df4\uff01\u4ed6\u4f5c\u7684\u5f88\u5c3d\u5fc3\uff0c\u91d1\u5e9c\u7684\u8bbe\u8ba1\uff0c\u4e5f\u786e\u5b9e\u53ec\u96c6\u4e86\u4e00\u4e9b\u5f3a\u654c\uff0c\u6628\u5bb5\u534a\u591c\u6076\u6218\uff0c\u8d3e\u82f1\u5c55\u73b0\u4e86 \u60ca\u4eba\u7684\u6b66\u827a\uff0c\u4f46\u6765\u4eba\u4e2a\u4e2a\u795e\u79d8\uff0c\u5374\u53c8\u540d\u4e0d\u89c1\u7ecf\u4f20\u2026\u2026\u201d\u5f53\u4e0b\u628a\u6628\u5929\u60c5\u666f\u5f88\u4ed4\u7ec6\u5730\u63cf\u8ff0\u4e00\u904d\u3002 \u3000\u3000\u738b\u5927\u4eba\u3001\u5f20\u5e08\u7237\u90fd\u542c\u5f97\u5f88\u7528\u5fc3\uff0c\u4e5f\u9762\u73b0\u60ca\u8be7\u4e4b\u8272\u3002 \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u4f46\u68a6\u5e7b\u4e4b\u5200\u6ca1\u6709\u73b0\u8eab\uff0c\u8fde\u5c5e\u4e0b\u4e5f\u672a\u53d1\u73b0\u4e00\u4e2a\u3002\u201d \u3000\u3000\u201c\u60ca\u4e16\u9b54\u5934\uff0c\u81ea\u662f\u667a\u8ba1\u3001\u6b66\u529f\u53cc\u7edd\u7684\u9ad8\u624b\uff0c\u8010\u6027\u4e4b\u9ad8\uff0c\u4e5f\u975e\u5e38\u4eba\u80fd\u53ca\uff0c\u201d\u738b\u5927\u4eba\u9053\uff1a \u201c\u4f46\u4e09\u5b9d\u8bf1\u60d1\u529b\u5341\u5206\u5f3a\u5927\uff0c\u4ed6\u5982\u5230\u4e86\u626c\u5dde\uff0c\u603b\u4f1a\u5fcd\u8010\u4e0d\u4f4f\u7684\u3002\u201d \u3000\u3000\u5f20\u5e08\u7237\u7a81\u7136\u63a5\u53e3\u9053\uff1a\u201c\u94c1\u603b\u6355\uff0c\u4f60\u770b\uff0c\u68a6\u5e7b\u4e4b\u5200\u662f\u5426\u5df2\u7ecf\u53bb\u4e86\u91d1\u5e9c\uff0c\u53ea\u662f\u9690\u8eab\u672a\u73b0\u3002\u201d \u3000\u3000\u201c\u5927\u6709\u53ef\u80fd\uff0c\u94c1\u67d0\u8fd8\u62c5\u5fc3\u4ed6\u653e\u4e0b\u8eab\u6bb5\uff0c\u5316\u8eab\u5728\u6b66\u58eb\u7fa4\u4e2d\u2026\u2026\u201d \u3000\u3000\u738b\u5c11\u537f\u4e00\u4e0b\u5b50\u7ad9\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u5bf9\uff01\u8fd9\u624d\u662f\u6700\u53ef\u6015\u7684\u4e8b\u60c5\uff0c\u771f\u8981\u5982\u6b64\uff0c\u8fde\u91d1\u767e\u5e74\u7684\u6027\u547d\uff0c \u4e5f\u638c\u63a7\u5728\u4ed6\u7684\u624b\u4e2d\u4e86\u3002\u201d \u3000\u3000\u201c\u8d3e\u82f1\u5f88\u7cbe\u660e\uff0c\u4e5f\u5f88\u5c0f\u5fc3\uff0c\u6211\u770b\uff0c\u4ed6\u4f1a\u65e9\u6709\u5b89\u6392\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u5c5e\u4e0b\u62c5\u5fc3\u5927\u4eba\u5b89\u5371\uff0c\u7279 \u56de\u5e9c\u8859\u6765\uff0c\u63a2\u89c6\u5927\u4eba\uff0c\u4e0d\u77e5\u90a3\u6c5f\u5357\u540d\u6355\u675c\u671b\u6708\u662f\u5426\u6765\u8fc7\u5e9c\u8859\uff1f\u201d \u3000\u3000\u201c\u6765\u8fc7\u4e24\u6b21\uff0c\u603b\u662f\u5306\u5306\u800c\u6765\uff0c\u5306\u5306\u800c\u53bb\uff0c\u201d\u738b\u5927\u4eba\u9053\uff1a\u201c\u4f46\u4ed6\u5e26\u6765\u4e86\u4e24\u9879\u5f88\u91cd\u8981\u7684\u6d88\u606f\uff0c \u4e00\u662f\u5211\u90e8\u7684\u603b\u6355\u5934\uff0c\u5df2\u7387\u9886\u4e86\u51e0\u4f4d\u91cd\u8981\u7684\u52a9\u624b\u5357\u4e0b\uff0c\u4ed6\u4eec\u4f1a\u4f5c\u4e9b\u4e86\u89e3\u654c\u60c5\u7684\u884c\u52a8\uff0c\u4e8c\u662f\u5317\u3001 \u897f\u3001\u4e2d\uff0c\u4e09\u5927\u540d\u6355\uff0c\u4e5f\u7387\u5f97\u529b\u624b\u4e0b\u8d76\u6765\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u4eec\u4e5f\u65e9\u5f97\u5bc6\u62a5\uff0c\u5929\u4e0b\u4e94\u4f4d\u6700\u5177\u529e\u6848\u80fd\u529b \u7684\u4eba\uff0c\u90fd\u5c06\u5728\u626c\u5dde\u4f1a\u805a\uff0c\u8fd9\u4e2a\u96c6\u4e2d\u4e86\u4e3e\u56fd\u5927\u534a\u8d22\u5bcc\u7684\u7cbe\u534e\u4e4b\u57ce\uff0c\u4f3c\u662f\u6b63\u5728\u915d\u917f\u7740\u4e00\u573a\u548c\u6c5f\u6e56 \u4eba\u7269\u6709\u5173\u7684\u5927\u53d8\u3002 \u3000\u3000\u7fa4\u6076\u4e91\u96c6\u4e8e\u65af\uff0c\u4e89\u593a\u5de8\u5927\u7684\u8d22\u5bcc\uff0c\u7531\u6765\u5df2\u4e45\uff0c\u4e5f\u5230\u4e86\u64cd\u5200\u4e00\u5272\u7684\u65f6\u673a\u5566\uff01\u9a6c\u516c\u5b50\u7684\u547d\u6848\uff0c \u53ea\u662f\u4e00\u4e2a\u5f00\u59cb\uff0c\u60f3\u5229\u7528\u6b64\u6848\uff0c\u5148\u5f15\u8d77\u626c\u5dde\u7684\u6df7\u4e71\uff0c\u4f46\u771f\u6b63\u7684\u7d22\u5f15\uff0c\u5e94\u8be5\u662f\u91d1\u767e\u5e74\u6536\u85cf\u4e09\u5b9d\u7684 \u9690\u79d8\u5916\u6cc4\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u94c1\u7fce\u9053\uff1a\u201c\u6700\u4e0d\u8d77\u773c\u7684\u6c34\u706b\u76f8\u6d4e\u4e00\u5757\u987d\u77f3\u800c\u5df2\uff0c\u5374\u4f3c\u662f\u4e09\u5b9d\u4e2d\u6700\u62a2\u624b\u7684\u73cd\u7269\uff0c \u94c1\u67d0\u7422\u78e8\u4e86\u5f88\u4e45\uff0c\u5c31\u60f3\u4e0d\u901a\u5b83\u4f55\u4ee5\u73cd\u8d35\u7684\u8981\u4eba\u62ff\u547d\u53bb\u62da\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5728\u987d\u77f3\uff0c\u201d\u738b\u5927\u4eba\u9053\uff1a\u201c\u5728\u5b83\u9690\u85cf\u7684\u79d8\u5bc6\uff0c\u6211\u770b\u8fc7\u5207\u5f00\u7684\u6c34\u706b\u76f8\u6d4e\uff0c\u4e5f\u60f3\u8fc7\u5b83\u7684\u4ef7 \u503c\uff0c\u4f46\u5b83\u542b\u6709\u7684\u79d8\u5bc6\u662f\u4ec0\u4e48\uff1f\u5b83\u53ef\u80fd\u662f\u65e9\u5df2\u6d41\u4f20\u4e8e\u6c5f\u6e56\u4e0a\u7684\u4e00\u4ef6\u7470\u5b9d\uff0c\u4e66\u672c\u4e0a\u6ca1\u6709\u8bb0\u8f7d\uff0c\u6ca1 \u542c\u8fc7\u8fd9\u4e2a\u4f20\u8a00\u7684\u4eba\uff0c\u5b8c\u5168\u65e0\u6cd5\u81c6\u6d4b\u3002\u201d \u3000\u3000\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u4e24\u4e2a\u9ed1\u8863\u4eba\uff0c\u6765\u7684\u7a81\u5140\uff0c\u5e94\u8be5\u662f\u4e2a\u597d\u7ebf\u7d22\u2026\u2026\u201d \u3000\u3000\u201c\u4f46\u4ed6\u4eec\u6765\u5982\u60ca\u9e3f\uff0c\u4e00\u95ea\u800c\u901d\uff0c\u201d\u94c1\u7fce\u8bf4\u9053\uff1a\u201c\u65e0\u6cd5\u8ffd\u5176\u884c\u8e2a\uff0c\u4e5f\u662f\u65e0\u6cd5\u4e0b\u624b\u4fa6\u5bdf\u3002\u201d \u3000\u3000\u201c\u90a3\u94f6\u8863\u4eba\uff0c\u5e94\u8be5\u662f\u77e5\u9053\u5b9d\u77f3\u4e4b\u79d8\u5bc6\uff0c\u201d\u738b\u5927\u4eba\u9053\uff1a\u201c\u53ea\u6015\u8d3e\u82f1\u662f\u4e0d\u4f1a\u548c\u4eba\u5206\u4eab\u7684\uff01\u201d \u3000\u3000\u201c\u8fd9\u53ef\u4ece\u4e24\u65b9\u9762\u4e0b\u624b\u903c\u4ed6\uff0c\u201d\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u662f\u5229\u7528\u4e94\u5927\u6355\u5934\u548c\u5211\u90e8\u7684\u529b\u91cf\uff0c\u903c\u4f7f\u91d1\u767e\u5e74 \u5c31\u8303\uff0c\u4ed6\u4e0d\u6015\u626c\u5dde\u5e9c\uff0c\u4f46\u5bf9\u5211\u90e8\u5e94\u6709\u987e\u8651\u3002\u201d \u3000\u3000\u201c\u5176\u5b9e\uff0c\u8d3e\u82f1\u672c\u4eba\u5c31\u77e5\u9053\u4e00\u4e9b\u5b9d\u77f3\u4e4b\u79d8\uff0c\u201d\u738b\u5927\u4eba\u9053\uff1a\u201c\u77e5\u9053\u591a\u5c11\uff0c\u6211\u65e0\u6cd5\u65ad\u8a00\uff0c\u603b\u4e4b \u77e5\u9053\u4e00\u4e9b\u5c31\u662f\uff0c\u5f53\u4ed6\u521d\u89c1\u6c34\u706b\u76f8\u6d4e\u65f6\uff0c\u6d41\u73b0\u51fa\u60ca\u6115\u4e4b\u8272\uff0c\u662f\u6765\u81ea\u5185\u5fc3\u5174\u594b\u548c\u60ca\u8bb6\u7684\u4ea4\u4f1a\u795e\u60c5\uff0c \u4f46\u4ed6\u9690\u85cf\u7684\u5f88\u597d\uff0c\u5f88\u5feb\u5c31\u5e73\u9759\u4e0b\u6765\uff0c\u6211\u63a8\u60f3\uff0c\u8d3e\u82f1\u5c48\u5c31\u91d1\u5e9c\u603b\u7ba1\uff0c\u5c31\u53ef\u80fd\u548c\u6c34\u706b\u76f8\u6d4e\u6709\u5173\u3002\u201d \u3000\u3000\u201c\u8d3e\u82f1\u6709\u4e24\u6b21\u5931\u8a00\uff0c\u53e3\u6c14\u9738\u9053\uff0c\u6c14\u5ea6\u9ad8\u8d35\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u5982\u5927\u4eba\u6240\u8bf4\uff0c\u4ed6\u6709\u7740\u5f88\u5f3a\u7684\u63a7 \u5236\u80fd\u529b\uff0c\u5f88\u5feb\u6062\u590d\u9547\u9759\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\uff0c\u53ef\u4e0d\u53ef\u4ee5\u628a\u4ed6\u6293\u5165\u5e9c\u8859\uff0c\u52a8\u4e4b\u4ee5\u5389\u5bb3\uff0c\u201d\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u518d\u4e0d\u7136\u5c31\u6765\u4e25\u5211\u903c\u4f9b\uff0c \u96be\u9053\u4ed6\u662f\u94dc\u94f8\u7684\u7f57\u6c49\uff0c\u94c1\u6253\u7684\u91d1\u521a\u4e0d\u6210\uff0c\u4e09\u6728\u4e4b\u4e0b\uff0c\u6211\u8fd8\u6ca1\u6709\u89c1\u8fc7\u95ee\u4e0d\u51fa\u7684\u53e3\u4f9b\uff01\u201d \u3000\u3000\u738b\u5927\u4eba\u53cc\u624b\u4e71\u6447\uff0c\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u4e0d\u884c\uff0c\u4ed6\u4e0d\u662f\u51f6\u5acc\uff0c\u6211\u4eec\u4e0d\u80fd\u52a8\u5211\u903c\u4f9b\uff0c\u771f\u8981\u52a8\u5211\uff0c\u4e5f \u4e0d\u662f\u5e9c\u8859\u4e2d\u73ed\u5f79\u80fd\u591f\u80dc\u4efb\uff0c\u903c\u6025\u4ed6\uff0c\u6765\u4e2a\u53cd\u51fb\u5c60\u6740\uff0c\u5c31\u4e0d\u77e5\u8981\u6b7b\u4ea1\u591a\u5c11\u4eba\u4e86\u2026\u2026\u201d \u3000\u3000\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u2026\u2026\u201d \u3000\u3000\u201c\u522b\u6253\u5982\u610f\u7b97\u76d8\uff0c\u8d3e\u82f1\u4e0d\u662f\u5403\u7d20\u7684\u4eba\uff0c\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u4ed6\u5f88\u7cbe\u4e8e\u7b97\u8ba1\uff0c\u4e0d\u4f1a\u4efb\u4f60\u6446\u5e03\uff0c \u4f55\u51b5\uff0c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u4ed6\u6ca1\u89e6\u72af\u4efb\u4f55\u6cd5\u7eaa\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\uff0c\u4e0d\u53ef\u4e3a\u654c\uff0c\u4ed6\u80fd\u591f\u5e2e\u52a9\u6211\u4eec\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u4f46\u4e14\u8981\u52a8\u4e4b\u4ee5\u60c5\uff0c\u8bf4\u4e4b\u4ee5\u7406\u3002\u201d \u3000\u3000\u201c\u4e24\u4f4d\u8bf4\u7684\u662f\uff01\u201d\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u65e2\u4e0d\u80fd\u5984\u52a0\u4eba\u529b\u5e72\u9884\uff0c\u53ea\u6709\u5750\u5f85\u53d8\u5316\u4e86\u3002\u201d \u3000\u3000\u201c\u6848\u60c5\u81f3\u4eca\uff0c\u4f3c\u5df2\u8d85\u51fa\u4e86\u6211\u4eec\u7684\u80fd\u529b\u8303\u56f4\u3002\u201d\u738b\u5927\u4eba\u9053\uff1a\u201c\u5929\u4e0b\u4e94\u5927\u6355\u5934\uff0c\u90fd\u5c06\u4f1a\u805a\u626c \u5dde\uff0c\u6848\u5b50\u5c31\u4ea4\u7ed9\u4ed6\u4eec\u529e\u4e86\u3002\u4e8b\u5b9e\u4e0a\uff0c\u6211\u76f8\u4fe1\u5df2\u5230\u4e86\u626c\u5dde\u7684\u5927\u6355\u5934\u4eec\uff0c\u53ef\u80fd\u65e9\u5df2\u5377\u5165\u6848\u60c5\u4e4b\u4e2d\uff0c \u4ed6\u4eec\u4e5f\u6709\u529e\u6848\u7684\u5e95\u7ebf\uff0c\u80fd\u6380\u8d77\u591a\u5927\u98ce\u6d6a\uff0c\u5982\u4f55\u5584\u540e\uff0c\u4e5f\u8bb8\u65e9\u6709\u8ba1\u5212\u4e86\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u672a\u4f1a\u89c1\u5211\u90e8\u603b\u6355\u5934\u4e4b\u524d\uff0c\u201d\u94c1\u7fce\u9053\uff1a\u201c\u4e5f\u53ea\u6709\u5750\u4ee5\u5f85\u53d8\u4e86\uff0c\u5c5e\u4e0b\u8fd8\u8981\u56de\u91d1\u5e9c\uff0c\u53c2 \u4e0e\u5408\u6355\u68a6\u5e7b\u4e4b\u5200\u7684\u884c\u52a8\uff0c\u6682\u884c\u544a\u9000\u4e86\u3002\u201d \u3000\u3000\u62b1\u62f3\u4e00\u63d6\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u671b\u7740\u94c1\u7fce\u8fdc\u53bb\u7684\u80cc\u5f71\uff0c\u738b\u5927\u4eba\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5927\u6848\u5b50\u3001\u5927\u6848\u5b50\uff0c\u975e\u501f\u91cd\u5211\u90e8\u4e4b\u529b\u4e0d\u53ef\uff0c \u626c\u5dde\u5e9c\u5df2\u65e0\u80fd\u884c\u52a8\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u626e\u4f5c\u4e86\u4e00\u4e2a\u5356\u7cd6\u846b\u82a6\u7684\u5c0f\u8d29\uff0c\u63a8\u4e00\u8f66\u7cd6\u846b\u82a6\uff0c\u5728\u51e0\u5bb6\u5927\u9152\u697c\u524d\u9762\u6e38\u8361\u3002\u4ed6\u4e45\u5e74\u529e \u6848\uff0c\u5e38\u626e\u4f5c\u5404\u79cd\u4e0d\u540c\u8eab\u5206\u7684\u4eba\uff0c\u800c\u4e14\u4e0d\u60dc\u727a\u7272\uff0c\u6539\u626e\u7684\u5165\u6728\u4e09\u5206\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u4e0d\u4f46\u7a7f\u4e00\u8eab\u7834\u65e7\u7684\u8863\u670d\uff0c\u4e5f\u628a\u82f1\u4fca\u7684\u8138\u5e9e\uff0c\u6d82\u7684\u9ed1\u6c61\u7247\u7247\uff0c\u8fde\u9732\u5728\u5916\u9762\u7684\u624b\u81c2 \u548c\u53cc\u624b\uff0c\u4e5f\u64e6\u7684\u4e00\u7247\u9edd\u9ed1\uff0c\u771f\u662f\u4e0d\u9732\u4e00\u70b9\u75d5\u8ff9\uff0c\u4e5f\u77a7\u4e0d\u51fa\u4efb\u4f55\u4e00\u70b9\u53ef\u7591\u3002\u7cd6\u846b\u82a6\u672c\u662f\u5317\u65b9\u7279 \u4ea7\uff0c\u4f46\u626c\u5dde\u5e9c\u7e41\u534e\u51a0\u5168\u56fd\uff0c\u5404\u5730\u7684\u540d\u4ea7\u5c0f\u5403\uff0c\u4e5f\u90fd\u5728\u626c\u5dde\u96c6\u4e2d\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u63a8\u7740\u8f66\u5b50\u8d70\uff0c\u5728\u4e00\u5bb6\u5927\u9910\u9986\u95e8\u53e3\u505c\u4e86\u4e0b\u6765\uff0c\u5317\u3001\u4e2d\u3001\u897f\uff0c\u4e09\u5927\u540d\u6355\u90fd\u7231\u5403\uff0c\u76f8\u4fe1 \u4ed6\u4eec\u5982\u5230\u4e86\u626c\u5dde\uff0c\u5b9a\u4f1a\u5230\u8fd9\u5bb6\u6709\u540d\u7684\u5927\u9910\u9986\uff0c\u5403\u4e00\u987f\u626c\u5dde\u5927\u83dc\u3002\u8fd9\u5bb6\u9910\u9986\u53eb\u4f5c\u5bcc\u6625\u697c\uff0c\u662f\u626c \u5dde\u6700\u6709\u540d\u7684\u83dc\u9986\u4e4b\u4e00\u3002 \u3000\u3000\u675c\u671b\u6708\u5f88\u6709\u8010\u5fc3\uff0c\u7834\u8863\u98de\u4e1d\uff0c\u7ad9\u5728\u5bd2\u98ce\u4e2d\uff0c\u4e00\u76f4\u7ad9\u4e86\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u4e45\u3002\u7531\u8fd1\u5348\u5f00\u59cb\uff0c\u76f4\u5230 \u5348\u996d\u8fc7\u540e\uff0c\u6ca1\u6709\u89c1\u5230\u4e09\u4f4d\u8001\u670b\u53cb\u4e2d\u4efb\u4f55\u4e00\u4e2a\uff0c\u5374\u5356\u4e86\u4e0d\u5c11\u7cd6\u846b\u82a6\u3002\u773c\u770b\u8fd9\u534a\u65e5\u7684\u8fb0\u5149\uff0c\u662f\u767d \u5fd9\u4e86\uff0c\u6b63\u51c6\u5907\u79bb\u6b64\u4ed6\u5f80\uff0c\u6362\u4e2a\u5730\u65b9\uff0c\u518d\u53bb\u78b0\u78b0\u8fd0\u6c14\uff0c\u5ffd\u89c1\u4e00\u4e2a\u624b\u6267\u7b97\u547d\u62db\u724c\u7684\u4e2d\u5e74\u4eba\uff0c\u7f13\u6b65 \u884c\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u9752\u886b\u5e03\u5c65\uff0c\u957f\u9aef\u98d8\u80f8\uff0c\u770b\u8eab\u6750\u4f3c\u66fe\u76f8\u8bc6\uff0c\u4f46\u80a4\u8272\u9762\u5b54\u5374\u5341\u5206\u964c\u751f\u3002\u4e8b\u5b9e\u4e0a\u662f\u5929\u4e0b \u7684\u7b97\u547d\u5148\u751f\u90fd\u4e00\u6837\uff0c\u628a\u81ea\u5df1\u6253\u626e\u7684\u4ed9\u98ce\u9053\u9aa8\uff0c\u8868\u793a\u4ed6\u662f\u5f97\u9053\u4e4b\u58eb\u3002\u518d\u4e0d\u7136\uff0c\u6253\u626e\u7684\u50cf\u4e2a\u843d\u7b2c \u79c0\u624d\uff0c\u88c5\u51fa\u80f8\u6709\u7ecf\u4e66\u7684\u9971\u5b66\u5148\u751f\u6837\uff0c\u5916\u578b\u4e0a\u5148\u8981\u8ba9\u4eba\u4fe1\u670d\uff0c\u624d\u80fd\u80e1\u8bf4\u516b\u9053\u5730\u9a97\u94b1\u3002 \u3000\u3000\u4f46\u90a3\u5e03\u62db\u4e0a\u5199\u7684\u4e00\u884c\u5b57\uff0c\u5374\u53c8\u7ed9\u4e86\u675c\u671b\u6708\u4e00\u4e2a\u542f\u793a\uff0c\u5199\u7684\u662f\uff1a\u4e2d\u5dde\u674f\u82b1\u5ba2\u94c1\u8bba\u76f8\u3002 \u3000\u3000\u674f\u82b1\u5ba2\u5c31\u674f\u82b1\u5ba2\u5427\uff01\u52a0\u4e0a\u4e2d\u5dde\u4e24\u4e2a\u5b57\uff0c\u5c31\u6709\u70b9\u753b\u86c7\u6dfb\u8db3\u4e86\uff0c\u90a3\u662f\u8bf4\u660e\u6765\u81ea\u4e2d\u5dde\uff0c\u4f46\u4e2d\u5dde \u6ca1\u6709\u540d\u6ee1\u5168\u56fd\u7684\u76f8\u5e08\uff0c\u4e2d\u5dde\u6700\u6709\u540d\u7684\u662f\u5927\u6355\u5934\u5434\u94c1\u5cf0\uff0c\u9022\u95e8\u4e09\u4e0d\u8fc7\uff0c\u4e00\u7b14\u9547\u4e2d\u5dde\u3002 \u3000\u3000\u675c\u671b\u6708\u63a8\u7740\u8f66\u5b50\u8fce\u4e86\u4e0a\u53bb\uff0c\u9053\uff1a\u201c\u5927\u76f8\u5e08\uff0c\u6765\u665a\u4e86\uff0c\u5bcc\u6625\u697c\u5df2\u7ecf\u6253\u70ca\uff0c\u5c31\u4f59\u4e0b\u6b8b\u80b4\u5269\u9152 \u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e00\u5f00\u53e3\uff0c\u771f\u76f8\u5168\u9732\uff0c\u5927\u76f8\u5e08\u76ee\u5149\u76ef\u6ce8\u5728\u675c\u671b\u6708\u8138\u4e0a\u770b\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e24\u4e2a\u4e09\u5341\u591a\u5c81\u7684\u6c49\u5b50\uff0c\u6b63\u597d\u8d70\u8fc7\u6765\uff0c\u8fd1\u4e24\u4eba\u8eab\u4fa7\u65f6\u653e\u6162\u4e86\u811a\u6b65\uff0c\u524d\u9762\u4e00\u4e2a\u8bf4\u9053\uff1a \u201c\u8001\u8868\u554a\uff01\u8981\u4e0d\u8981\u770b\u4e2a\u76f8\u554a\uff01\u8fd1\u51e0\u5929\u8fd0\u6c14\u4e0d\u597d\uff0c\u8f93\u7684\u540a\u86cb\u7cbe\u5149\uff0c\u770b\u4e2a\u76f8\u53bb\u53bb\u9709\u6c14\u2026\u2026\u201d \u3000\u3000\u5927\u76f8\u58eb\u8f6c\u8fc7\u5934\uff0c\u63a5\u9053\uff1a\u201c\u884c\uff01\u94c1\u53e3\u76f4\u65ad\uff0c\u4e0d\u7075\u4e0d\u6536\u94b1\uff0c\u4e0d\u8fc7\uff0c\u4e24\u4f4d\u8981\u7a0d\u5019\u7247\u523b\uff0c\u8fd9\u4f4d\u5356 \u7cd6\u846b\u82a6\u7684\u8001\u5f1f\uff0c\u5148\u5230\u2026\u2026\u201d \u3000\u3000\u540e\u9762\u4e00\u4eba\u4e00\u62c9\u524d\u9762\u7684\u4eba\uff0c\u9053\uff1a\u201c\u5927\u76f8\u5e08\u6709\u5ba2\u4eba\u5566\uff01\u54b1\u4eec\u665a\u4e0a\u518d\u770b\u5427\uff01\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u8bf4\u7740\u8bdd\uff0c\u5306\u5306\u8d70\u8fc7\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u5401\u53e3\u6c14\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u675c\u8001\u5f1f\uff0c\u626c\u5dde\u57ce\u5389\u5bb3\u5440\uff01\u6211\u4e0a\u5348\u5230\uff0c\u4e2d\u5348\u5c31\u88ab\u4eba\u76ef\u4e86\u68a2\u3002\u201d \u3000\u3000\u201c\u843d\u4e86\u5e97\u5417\uff1f\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u4e00\u4e2a\u5ba2\u6808\uff1f\u201d \u3000\u3000\u201c\u4e00\u5bb6\u5c0f\u5ba2\u6808\uff0c\u4f46\u8fd8\u5e72\u51c0\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u53eb\u4f5c\u7eff\u626c\u5c45\u3002\u201d \u3000\u3000\u201c\u6211\u770b\u4e0d\u7528\u518d\u4f4f\u7eff\u626c\u5c45\u4e86\uff0c\u5377\u8d77\u62db\u724c\u8ddf\u6211\u8d70\u5427\uff01\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u79df\u4e86\u4e00\u95f4\u6c11\u5b85\u4f4f\uff0c\u5730 \u65b9\u5f88\u96c5\u9759\uff0c\u5973\u4e3b\u4eba\u70e7\u7684\u4e00\u624b\u597d\u83dc\u3002\u5f53\u7136\uff0c\u6211\u6ca1\u6709\u8fd9\u4e2a\u82d7\u5934\uff0c\u662f\u626c\u5dde\u5e9c\u7684\u5b89\u6392\u3002\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6211\u5e26\u6765\u4e00\u4e2a\u4f19\u8ba1\uff0c\u8fd8\u7559\u5728\u7eff\u626c\u5c45\uff0c\u4ed6\u4eec\u76ef\u4e0a\u6211\uff0c\u4e5f\u6478\u6e05\u4e86\u6211\u7684 \u4f4f\u5904\uff0c\u96be\u5f97\u7684\u673a\u4f1a\u5440\uff01\u5982\u4f55\u80fd\u653e\u8fc7\uff0c\u6211\u60f3\u770b\u770b\u4ed6\u4eec\u662f\u4f55\u65b9\u795e\u5723\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u7eff\u626c\u5c45\uff0c\u665a\u4e0a\u53bb\u627e\u4f60\uff0c\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4e8e\u5144\u548c\u5c91\u5144\uff0c\u662f\u5426\u4e5f\u5230\u4e86\uff1f\u201d \u3000\u3000\u201c\u90fd\u6765\u4e86\uff0c\u603b\u6355\u5934\u8be5\u5230\u4e86\uff0c\u4f46\u8fd8\u6ca1\u6709\u78b0\u4e0a\u5934\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u542c\u8bf4\u603b\u6355\u53f8\u4e2d\u7684\u7cbe\u9510\uff0c\u5c0f \u6587\u3001\u5c0f\u96c5\u3001\u59d1\u5a18\uff0c\u8fde\u540c\u963f\u6a2a\u3001\u963f\u4fdd\uff0c\u5168\u90fd\u4e0b\u4e86\u626c\u5dde\uff0c\u53ea\u7559\u4e0b\u7d20\u559c\u59d1\u5a18\u5b88\u5bb6\uff0c\u597d\u50cf\u5211\u90e8\u4e5f\u5f97\u5230 \u4e00\u4e2a\u8baf\u606f\uff0c\u662f\u4ef6\u5927\u6848\u5b50\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4f60\u7ed9\u4e8c\u5341\u6587\u94b1\uff0c\u54b1\u4eec\u5206\u624b\uff0c\u6700\u597d\u660e\u5929\u5348\u65f6\u518d\u6765\u7eff\u626c\u5c45\u627e\u6211\uff0c\u6211\u8981\u5148\u5f04\u6e05\u695a\u76ef\u4e0a\u6211 \u7684\uff0c\u662f\u54ea\u4e00\u8def\u725b\u9b3c\u86c7\u795e\u3002\u201d\u675c\u671b\u6708\u638f\u51fa\u4e86\u4e8c\u5341\u6587\u94b1\uff0c\u4e00\u4e2a\u4e00\u4e2a\u5730\u6570\u7ed9\u4e86\u5434\u94c1\u5cf0\uff0c\u770b\u4e0a\u53bb\u8fd9\u4e8c \u5341\u6587\u94b1\u4ed8\u7684\u5f88\u5fc3\u75db\u3002 \u3000\u3000\u4e3b\u8981\u662f\u6709\u4e24\u4e2a\u9752\u76ae\u6d41\u5b50\uff0c\u8eb2\u5728\u4e0d\u8fdc\u5904\u7684\u4e00\u682a\u5927\u6811\u540e\u5728\u5077\u770b\uff0c\u4e24\u4e2a\u4eba\u5c31\u505a\u4f5c\u5341\u8db3\u4e86\u3002 \u3000\u3000\u201c\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u626c\u5dde\u5e9c\u7684\u603b\u6355\u5934\uff0c\u4e5f\u5728\u5e02\u9762\u4e0a\u5e03\u4e86\u773c\u7ebf\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u771f\u76f8\u672a\u5f04\u6e05\u695a\u4e4b \u524d\uff0c\u6700\u597d\u522b\u4e0b\u6bd2\u624b\uff0c\u4f24\u4e86\u81ea\u5df1\u4eba\uff0c\u4e0d\u597d\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u201c\u8fd9\u6d88\u606f\u5f88\u53ca\u65f6\uff0c\u6211\u4f1a\u5c0f\u5fc3\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6325\u519b\u4e0d\u4e0b\u9a6c\uff0c\u5404\u81ea\u5954\u524d\u7a0b\uff0c\u660e\u5929\u4e2d\u5348\u518d\u53d9 \u65e7\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u63a8\u7740\u8f66\u5b50\u8d70\u4e86\uff0c\u4e34\u8d70\u524d\u8fd8\u5bf9\u5434\u94c1\u5cf0\u97a0\u4e86\u4e00\u4e2a\u8eac\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u5f88\u60f3\u7b11\uff0c\u4f46\u8fd8\u662f\u5fcd\u4f4f\u4e86\uff0c\u6709\u4eba\u5728\u6697\u4e2d\u76d1\u89c6\u554a\uff01\u4f46\u5374\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5c0f\u5fc3\u554a\uff01 \u522b\u591a\u7ba1\u95f2\u4e8b\uff0c\u660e\u54f2\u4fdd\u8eab\u6700\u91cd\u8981\uff0c\u4e0d\u542c\u8bdd\uff0c\u51fa\u4e86\u6bdb\u75c5\uff0c\u4f60\u53ef\u522b\u602a\u6211\u7684\u76f8\u6cd5\u4e0d\u51c6\u554a\u3002\u201d \u3000\u3000\u58f0\u97f3\u4e0d\u4f4e\uff0c\u4e24\u4e2a\u6811\u540e\u7684\u9752\u76ae\u6d41\u5b50\uff0c\u4e5f\u542c\u5f97\u5341\u5206\u6e05\u695a\u3002 \u3000\u3000\u675c\u671b\u6708\u7ed5\u4e86\u4e00\u4e2a\u5927\u5708\u5b50\uff0c\u5148\u628a\u4f59\u4e0b\u7684\u7cd6\u846b\u82a6\u5904\u7f6e\u597d\uff0c\u624d\u7ed5\u56de\u4f4f\u5904\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u7ed9\u4e86\u4ed6\u5f88\u5927\u7684\u8b66\u89c9\uff0c\u603b\u6355\u5934\u5927\u5f20\u65d7\u9f13\u4e0b\u626c\u5dde\uff0c\u7edd\u4e0d\u6b62\u4e3a\u4e86\u9a6c\u516c\u5b50\u8fd9\u4e00\u4ef6\u547d\u6848\uff0c\u5b9a \u662f\u542c\u5230\u4e86\u66f4\u5927\u7684\u6848\u60c5\uff0c\u624d\u628a\u7cbe\u9510\u6218\u5c06\u5168\u5e26\u6765\u4e86\u3002 \u3000\u3000\u770b\u6765\uff0c\u8fd9\u662f\u53c8\u4e00\u573a\u5927\u62da\u6740\u3002 \u3000\u3000\u662f\u7684\uff01\u53ea\u662f\u4e00\u4ef6\u547d\u6848\uff0c\u4e0d\u4f1a\u628a\u4e2d\u3001\u897f\u3001\u5317\u4e09\u5927\u6355\u5934\uff0c\u5168\u6570\u96c6\u4e2d\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u771f\u662f\u6c5f\u6e56\u98ce\u4e91\u53d8\u5e7b\u6025\uff0c\u6218\u9f13\u6467\u4eba\u6362\u5f81\u8863\u3002 \u3000\u3000\u56de\u5230\u4f4f\u5904\uff0c\u6d17\u4e86\u4e00\u4e2a\u70ed\u6c34\u6fa1\uff0c\u675c\u671b\u6708\u53c8\u6062\u590d\u4e86\u82f1\u4fca\u7684\u5bb9\u8c8c\uff0c\u5973\u4e3b\u4eba\u5df2\u7ecf\u5907\u597d\u83dc\u996d\uff0c\u9001\u5165 \u4e86\u675c\u671b\u6708\u7684\u623f\u4e2d\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4e16\u4e0a\u4e0d\u4f1a\u6709\u8fd9\u4e48\u591a\u597d\u4e8b\uff0c\u8fd9\u662f\u626c\u5dde\u5e9c\u526f\u603b\u6355\u5934\u738b\u575a\u523b\u610f\u7684\u5b89\u6392\uff0c\u83dc\u996d\u53ef\u53e3\uff0c\u8336\u548c \u9152\u4f9b\u5e94\u4e0d\u7f3a\uff0c\u4f4f\u7684\u5730\u65b9\u4e0d\u8c6a\u534e\uff0c\u4f46\u6e05\u9759\u8212\u9002\uff0c\u5973\u4e3b\u4eba\u4e0d\u591f\u5a07\u7f8e\uff0c\u4f46\u5e72\u51c0\u96c5\u81f4\uff0c\u884c\u6b62\u6e29\u67d4\uff0c\u800c \u4e14\uff0c\u9664\u9001\u996d\u4f9b\u8336\uff0c\u7edd\u4e0d\u6253\u6270\uff0c\u7adf\u5f15\u8d77\u4e86\u675c\u671b\u6708\u65e9\u65e5\u6210\u5bb6\u7684\u5ff5\u5934\u3002 \u3000\u3000\u5df2\u6570\u65e5\u4e0d\u89c1\u60dc\u7389\uff0c\u4e5f\u542c\u4e0d\u5230\u5979\u4efb\u4f55\u8baf\u606f\uff0c\u4e0d\u77e5\u5979\u662f\u5426\u4f1a\u88ab\u81ea\u5df1\u62d6\u7d2f\u3002\u867d\u7136\u5979\u81ea\u8d1f\u624d\u6167\uff0c \u8db3\u4ee5\u5e94\u4ed8\u53d8\u5c40\uff0c\u4e5f\u72ec\u5c45\u72ec\u884c\u60ef\u4e86\uff0c\u4f46\u4e00\u4e2a\u5c11\u5973\uff0c\u8981\u5bf9\u4ed8\u4e00\u7fa4\u51f6\u6b8b\u51f6\u9669\u7684\u8001\u5987\u4eba\uff0c\u8270\u82e6\u4e4b\u60c5\uff0c \u53ef\u60f3\u800c\u77e5\u4e86\u3002 \u3000\u3000\u60f3\u7740\u3001\u60f3\u7740\uff0c\u60c5\u6000\u5927\u52a8\uff0c\u6025\u4e4e\u6309\u8010\u4e0d\u4f4f\uff0c\u60f3\u5230\u56db\u51e4\u697c\u6587\u9601\u53bb\u67e5\u4e2a\u7a76\u7adf\u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u662f\u514d\u5f3a\u5fcd\u4e0b\u4e86\uff0c\u5012\u676f\u8336\uff0c\u81ea\u884c\u54c1\u5c1d\u3002 \u3000\u3000\u8336\u662f\u597d\u8336\uff0c\u4ed9\u971e\u96c0\u820c\uff0c\u662f\u8336\u4e2d\u6781\u54c1\uff0c\u738b\u575a\u82b1\u4e86\u4e0d\u5c11\u5de5\u592b\uff0c\u624d\u5f04\u5230\u4e00\u7f50\u9001\u8fc7\u6765\u3002\u4f46\u597d\u8336\u8981 \u6709\u95f2\u60c5\u9038\u5fd7\uff0c\u624d\u80fd\u54c1\u51fa\u8336\u5473\uff0c\u675c\u671b\u6708\u73b0\u5728\u60c5\u6000\u60c6\u6005\uff0c\u5c31\u6ca1\u6709\u8fd9\u4efd\u54c1\u8336\u7684\u5fc3\u60c5\u4e86\uff0c\u771f\u662f\u7261\u4e39\u82b1 \u5f00\u5728\u7530\u7566\u4e0a\uff0c\u7cdf\u8e4b\u4e86\u8fd9\u4efd\u9ad8\u8d35\u3002 \u3000\u3000\u5e78\u597d\uff0c\u738b\u575a\u6765\u4e86\uff0c\u738b\u575a\u6362\u4e86\u4e00\u8eab\u4fbf\u670d\uff0c\u800c\u4e14\u9648\u65e7\u4e0d\u582a\uff0c\u663e\u662f\u5f88\u5c0f\u5fc3\u7684\u884c\u52a8\u3002\u675c\u671b\u6708\u8d77\u8eab \u8fce\u5ba2\uff0c\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u98ce\u5439\u6765\u4e86\u526f\u603b\u6355\u5934\uff1f\u201d \u3000\u3000\u201c\u94c1\u5934\u513f\u56de\u4e86\u5e9c\u8859\uff0c\u89c1\u4e86\u5927\u4eba\uff0c\u4e5f\u5e26\u4e86\u4e00\u4e9b\u6d88\u606f\uff0c\u5927\u4eba\u8981\u6211\u628a\u6d88\u606f\u9012\u8fc7\u6765\uff0c\u201d\u738b\u575a\u9053\uff1a \u201c\u4ee5\u4f9b\u675c\u53f8\u4e3b\u53c2\u8003\u3002\u540c\u65f6\uff0c\u5927\u4eba\u4e5f\u60f3\u542c\u542c\u4f60\u7684\u9ad8\u89c1\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u632f\u8d77\u7cbe\u795e\uff0c\u9053\uff1a\u201c\u597d\uff01\u4f60\u8bf4\u3002\u201d \u3000\u3000\u738b\u575a\u5f88\u4ed4\u7ec6\u5730\u628a\u91d1\u5e9c\u4e2d\u534a\u591c\u6fc0\u6218\u7684\u7ecf\u8fc7\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u4e9b\u90fd\u662f\u94c1\u7fce\u544a\u8bc9\u738b\u5927\u4eba\u7684\u3002 \u3000\u3000\u675c\u671b\u6708\u542c\u5230\u4e24\u4e2a\u9ed1\u8863\u4eba\u65f6\uff0c\u7a81\u7136\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4f46\u5374\u5fcd\u4e0b\u4e86\uff0c\u672a\u4f5c\u4efb\u4f55\u8868\u793a\u3002\u738b\u575a\u8bf4\u5b8c\u7ecf\u8fc7\uff0c \u5e0c\u671b\u675c\u671b\u6708\u80fd\u7ed9\u4ed6\u4e00\u4e2a\u4ea4\u4ee3\uff0c\u4f46\u675c\u671b\u6708\u4e00\u76f4\u5728\u6c89\u601d\u4e0d\u8bed\uff0c\u5fcd\u4e0d\u4f4f\uff0c\u53ea\u597d\u518d\u5f00\u53e3\uff0c\u9053\uff1a\u201c\u675c\u53f8 \u4e3b\uff0c\u8981\u5728\u4e0b\u5982\u4f55\u5411\u5927\u4eba\u56de\u8bdd\uff1f\u201d \u3000\u3000\u201c\u544a\u8bc9\u4ed6\uff0c\u626c\u5dde\u5e9c\u6682\u65f6\u522b\u52a8\uff0c\u4e00\u5207\u7b49\u5211\u90e8\u603b\u6355\u5934\u5230\u8fbe\u4e4b\u540e\uff0c\u518d\u4f5c\u51b3\u5b9a\u3002\u201d\u675c\u671b\u6708\u9053\uff1a \u201c\u738b\u526f\u603b\u6355\uff0c\u6700\u597d\u4e5f\u628a\u9ad8\u624b\u96c6\u4e2d\u5728\u5e9c\u8859\u4e2d\uff0c\u8981\u4ed6\u4eec\u6539\u7a7f\u4e00\u822c\u8859\u5f79\u7684\u8863\u670d\uff0c\u4ee5\u4fdd\u62a4\u5927\u4eba\u7684\u5b89\u5168 \u4e3a\u4e3b\uff0c\u8fd1\u65e5\u4e2d\u6211\u4f1a\u6709\u6240\u884c\u52a8\uff0c\u4e0d\u8981\u518d\u6765\u770b\u6211\uff0c\u6709\u5fc5\u8981\u65f6\uff0c\u6211\u4f1a\u5230\u5e9c\u8859\u4f1a\u89c1\u5927\u4eba\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u53f8\u4e3b\u600e\u4e48\u8bf4\uff0c\u6211\u5c31\u600e\u4e48\u56de\u8bdd\u3002\u201d\u738b\u575a\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u4e00\u5207\u5f00\u9500\u90fd\u5df2\u4ed8\u6e05\uff0c\u800c\u4e14\u53ef\u4f9b \u4e24\u4e2a\u6708\u4ee5\u4e0a\u4e4b\u7528\uff0c\u53f8\u4e3b\u60f3\u5403\u4e9b\u4ec0\u4e48\uff0c\u53ea\u7ba1\u5429\u5490\u8fd9\u91cc\u7684\u4e3b\u4eba\uff0c\u6211\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8fd9\u91cc\u53ea\u89c1\u5230\u5973\u4e3b\u4eba\uff0c\u7537\u4e3b\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u7537\u4e3b\u4eba\uff0c\u201d\u738b\u575a\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4e00\u5207\u90fd\u5f88\u65b9\u4fbf\uff0c\u5973\u4e3b\u4eba\u9664\u4e86\u70e7\u7684\u4e00\u624b\u597d\u83dc \u4e4b\u5916\uff0c\u4e5f\u64c5\u957f\u4e3a\u4eba\u63a8\u62ff\u7b4b\u9aa8\uff0c\u4e0d\u8bba\u4ec0\u4e48\u4e8b\uff0c\u53ea\u8981\u5979\u672c\u4eba\u540c\u610f\u5c31\u884c\u3002\u201d\u8fd9\u8bdd\u5c31\u8bf4\u7684\u6709\u70b9\u66a7\u6627\u4e86\uff0c \u4f46\u4e5f\u8868\u793a\u7684\u5f88\u660e\u767d\u3002 \u3000\u3000\u738b\u575a\u662f\u8001\u5b9e\u4eba\uff0c\u8bf4\u5b8c\u8fd9\u51e0\u53e5\u8bdd\uff0c\u81ea\u5df1\u4e5f\u89c9\u7740\u4e0d\u597d\u610f\u601d\uff0c\u8d77\u8eab\u800c\u53bb\uff0c\u8fde\u5934\u4e5f\u4e0d\u56de\u4e00\u4e0b\u3002 \u3000\u3000\u675c\u671b\u6708\u4e0d\u662f\u5723\u4eba\uff0c\u529e\u6848\u592a\u7d27\u5f20\uff0c\u5076\u800c\u4e5f\u4f1a\u7720\u82b1\u5bbf\u67f3\u4e00\u756a\uff0c\u4f46\u5fc5\u662f\u5728\u6709\u7a7a\u6687\u7684\u65f6\u5019\u3002\u800c\u4e14\uff0c \u7edd\u4e0d\u7559\u604b\uff0c\u4e5f\u5c31\u662f\u8bf4\u4e0d\u548c\u4e00\u4e2a\u5993\u5973\u4f5c\u7b2c\u4e8c\u6b21\u4ea4\u6613\u3002 \u3000\u3000\u9700\u77e5\u675c\u671b\u6708\u5e74\u8f7b\u82f1\u4fca\uff0c\u53c8\u5065\u58ee\uff0c\u5bf9\u5973\u4eba\u4e5f\u5f88\u6e29\u67d4\uff0c\u63a5\u89e6\u8fc7\u4ed6\u7684\u5973\u5b69\u5b50\uff0c\u867d\u53ea\u4e00\u591c\u592b\u59bb\uff0c \u5206\u624b\u65f6\uff0c\u90fd\u6709\u4e9b\u4f9d\u4f9d\u4e0d\u820d\uff0c\u675c\u671b\u6708\u5c31\u4e0d\u6562\u518d\u53bb\u62db\u60f9\u5979\u4eec\u4e86\u3002 \u3000\u3000\u8fd9\u4e5f\u4f7f\u4ed6\u6709\u4e9b\u9003\u907f\u5973\u4eba\uff0c\u8c2d\u60dc\u7389\u662f\u4f8b\u5916\uff0c\u4e0d\u4f46\u7ed9\u4e86\u4ed6\u4e00\u4efd\u60c5\u601d\uff0c\u4e5f\u7ed9\u4e86\u4ed6\u4e00\u4efd\u6025\u6b32\u518d\u89c1 \u7684\u60f3\u5ff5\u3002 \u3000\u3000\u7b97\u7b97\u65e5\u5b50\uff0c\u660e\u5929\u5348\u65f6\u4e4b\u540e\uff0c\u53bb\u89c1\u5434\u94c1\u5cf0\u3002\u5f53\u7136\uff0c\u4e5f\u5e0c\u671b\u65e9\u89c1\u5230\u957f\u5b89\u4e8e\u627f\u5fd7\uff0c\u90a3\u4e2a\u5200\u51fa\u5982 \u95ea\u7535\uff0c\u5bd2\u8292\u7834\u957f\u7a7a\u7684\u897f\u65b9\u540d\u6355\u3002 \u3000\u3000\u5173\u4e1c\u540d\u6355\u5c91\u5578\u864e\uff0c\u4e5f\u662f\u675c\u671b\u6708\u6025\u4e8e\u8981\u89c1\u7684\u4eba\uff0c\u8fd9\u56db\u5927\u540d\u6355\u5404\u5c45\u4e00\u65b9\uff0c\u8f96\u5730\u6570\u7701\uff0c\u6210\u7acb\u4e1c\u3001 \u5357\u3001\u897f\u3001\u4e2d\u56db\u5206\u53f8\u4e4b\u540e\uff0c\u4e0d\u4f46\u4eba\u624b\u589e\u591a\uff0c\u6743\u8d23\u4e5f\u52a0\u91cd\u4e86\uff0c\u4e5f\u8d1f\u8d23\u4fa6\u5bdf\u8f96\u5730\u5b98\u5458\u7684\u8d2a\u6c61\u884c\u52a8\u3002 \u4e0d\u8fc7\uff0c\u53ea\u7ba1\u63d0\u4f9b\u8bc1\u636e\uff0c\u62a5\u5165\u5211\u90e8\u603b\u53f8\uff0c\u5982\u4f55\u5904\u7f6e\uff0c\u5c31\u4e0d\u7528\u4ed6\u4eec\u7ba1\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u79cd\u4fdd\u62a4\u4ed6\u4eec\u7684\u63aa\u65bd\uff0c\u4ee5\u514d\u5206\u53f8\u548c\u5730\u65b9\u5b98\u5458\uff0c\u641e\u7684\u683c\u683c\u4e0d\u5165\uff0c\u529e\u4e8b\u7684\u6548\u7387\u4e0a\uff0c\u5c31\u5927 \u6253\u6298\u6263\u4e86\u3002 \u3000\u3000\u4e24\u5929\u4e4b\u540e\uff0c\u662f\u4f1a\u6664\u60dc\u7389\u7684\u65e5\u5b50\uff0c\u5c3d\u7ba1\u7626\u897f\u6e56\u98ce\u666f\u5982\u753b\uff0c\u662f\u6587\u4eba\u96c5\u58eb\u4f1a\u96c6\u4e4b\u5730\uff0c\u4f46\u675c\u671b\u6708 \u7684\u4e60\u60ef\uff0c\u4e5f\u5fc5\u987b\u5148\u884c\u719f\u6089\u90a3\u91cc\u7684\u5f62\u52bf\u3001\u5730\u7406\u3002\u4f55\u51b5\uff0c\u8981\u4f1a\u7684\uff0c\u9664\u4e86\u60dc\u7389\u4e4b\u5916\uff0c\u5f88\u53ef\u80fd\u8fd8\u6709\u4e00 \u4f4d\u606f\u9690\u7626\u897f\u6e56\u7684\u9ad8\u4eba\u3002\u626c\u5dde\u5730\u9762\u4e0a\uff0c\u6c5f\u6e56\u5f62\u6001\uff0c\u6b63\u5728\u5927\u5e45\u6539\u53d8\uff0c\u5f53\u7136\u4e5f\u53ef\u80fd\u6709\u4e00\u65b9\u4e2d\u4eba\uff0c\u65e9 \u6536\u4e70\u4e86\u4ed6\u3002 \u3000\u3000\u770b\u8d77\u6765\uff0c\u7626\u897f\u6e56\u4f1a\u4f73\u4eba\uff0c\u662f\u4e00\u4ef6\u98ce\u96c5\u597d\u73a9\u7684\u4e8b\uff0c\u4f46\u56e0\u65f6\u95f4\u4e0d\u5b9c\uff0c\u98ce\u96c5\u4e4b\u5730\uff0c\u4e5f\u53ef\u80fd\u6697\u85cf \u51f6\u9669\u4e86\u3002\u675c\u671b\u6708\u5df2\u4f5c\u4e86\u51b3\u5b9a\uff0c\u4f1a\u6664\u8fc7\u4e2d\u5dde\u5434\u94c1\u5cf0\u540e\uff0c\u518d\u51b3\u5b9a\u662f\u5426\u628a\u63a2\u5f97\u7684\u626c\u5dde\u60c5\u52bf\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u4e00\u591c\u597d\u7761\uff0c\u4f7f\u5f97\u675c\u671b\u6708\u7cbe\u795e\u5145\u6c9b\uff0c\u7a0d\u7ecf\u6613\u5bb9\uff0c\u5e26\u4e0a\u4e24\u628a\u77ed\u5251\uff0c\u6084\u7136\u51fa\u95e8\u800c\u53bb\u3002\u675c\u671b\u6708\u5b66 \u7684\u662f\u5251\u672f\uff0c\u5df2\u7ecf\u767b\u5802\u5165\u5ba4\uff0c\u62d2\u654c\u65f6\u7528\u628a\u4e09\u5c3a\u516d\u5bf8\u7684\u4e03\u661f\u5b9d\u5251\uff0c\u662f\u987a\u624b\u7684\u5175\u5203\uff0c\u53ea\u53ef\u60dc\uff0c\u65e0\u6cd5 \u643a\u5e26\uff0c\u626e\u4f5c\u4e2a\u62c5\u592b\u3001\u5c0f\u8d29\u3001\u624b\u4e2d\u63d0\u628a\u957f\u5251\uff0c\u90a3\u5c31\u4e0d\u4f26\u4e0d\u7c7b\u4e86\uff0c\u6240\u4ee5\uff0c\u53ea\u597d\u820d\u957f\u53d6\u77ed\u3002 \u3000\u3000\u62c5\u4e86\u4e00\u62c5\u9752\u83dc\u841d\u535c\uff0c\u675c\u671b\u6708\u5728\u7eff\u626c\u5c45\u56db\u5468\u7ed5\u4e86\u4e00\u5468\uff0c\u672a\u53d1\u73b0\u6697\u693f\u548c\u76d1\u89c6\u7684\u4eba\uff0c\u770b\u770b\u5929\u8272\uff0c \u5df2\u8fd1\u4e2d\u5348\uff0c\u62c5\u7740\u4e00\u62c5\u9752\u83dc\uff0c\u5f00\u8fdb\u4e86\u7eff\u626c\u5c45\uff0c\u5e97\u5c0f\u4e8c\u770b\u4ed6\u62c5\u4e86\u4e00\u62c5\u83dc\uff0c\u4e5f\u672a\u62e6\u963b\uff0c\u675c\u671b\u6708\u7ed5\u5165 \u4e86\u4e00\u5ea7\u5ead\u9662\u4e2d\u3002 \u3000\u3000\u4e00\u6247\u623f\u95e8\u7a81\u7136\u5927\u5f00\uff0c\u5434\u94c1\u5cf0\u6b63\u7ad9\u5728\u95e8\u53e3\u62db\u624b\uff0c\u675c\u671b\u6708\u4e00\u4f4e\u5934\uff0c\u62c5\u7740\u4e00\u62c5\u83dc\u8fdb\u4e86\u623f\u95f4\u3002 \u3000\u3000\u623f\u95f4\u7acb\u523b\u5173\u4e86\u8d77\u6765\u3002 \u3000\u3000\u653e\u4e0b\u83dc\u62c5\uff0c\u675c\u671b\u6708\u62ed\u53bb\u8138\u4e0a\u4e00\u7247\u6c61\u7070\uff0c\u7b11\u9053\uff1a\u201c\u76ee\u524d\u626c\u5dde\u60c5\u52bf\u6df7\u4e71\uff0c\u5230\u5904\u662f\u773c\u7ebf\u3001\u6697\u6869\uff0c \u4e00\u4e0d\u5c0f\u5fc3\uff0c\u5c31\u4f1a\u88ab\u4eba\u76ef\u4e0a\uff0c\u5c0f\u8d29\u3001\u62c5\u592b\uff0c\u662f\u6700\u4e0d\u8d77\u773c\u7684\u4eba\uff0c\u6240\u4ee5\uff0c\u6211\u5c31\u62c5\u4e00\u62c5\u9752\u83dc\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u675c\u5144\u5f1f\u6295\u5165\u5de5\u4f5c\u7684\u7cbe\u795e\uff0c\u5144\u5f1f\u662f\u96be\u4ee5\u53ca\u5f97\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6628\u591c\u4e09\u66f4\u65f6\u5206\uff0c\u4ed6\u4eec\u679c\u7136 \u6478\u4e86\u8fdb\u6765\uff0c\u4ed6\u4eec\u4e00\u8d77\u6765\u4e24\u4e2a\u4eba\uff0c\u88ab\u5144\u5f1f\u6d3b\u6349\u4e00\u5bf9\uff0c\u7a0d\u52a0\u60e9\u6cbb\uff0c\u5df2\u5c3d\u5410\u5b9e\u8a00\uff0c\u8bf4\u4ed6\u4eec\u662f\u626c\u5dde\u5e9c \u5e03\u4e0b\u7684\u773c\u7ebf\uff0c\u53d6\u51fa\u4e00\u9762\u94dc\u724c\uff0c\u8bc1\u5b9e\u4ed6\u4eec\u7684\u8eab\u5206\u3002\u201d \u3000\u3000\u201c\u626c\u5dde\u5e9c\u8859\u4e2d\u4eba\uff0c\u5982\u6b64\u4e0d\u582a\u60ca\u5413\uff0c\u94c1\u7fce\u77e5\u9053\u4e86\uff0c\u5b9a\u7136\u5341\u5206\u96be\u8fc7\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u4e0d\u2026\u2026\u4e0d\u2026\u2026\u675c\u8001\u5f1f\u2026\u2026\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6211\u95ee\u7684\u5f88\u6e05\u695a\uff0c\u4ed6\u4eec\u4e0d\u662f\u626c\u5dde\u5e9c\u7684\u8859\u5f79\u73ed \u5934\uff0c\u662f\u626c\u5dde\u5730\u9762\u4e0a\u9752\u75de\u6df7\u5b50\uff0c\u6b64\u756a\u53d7\u96c7\u626c\u5dde\u5e9c\u8859\uff0c\u7167\u4f60\u8001\u5f1f\u5429\u5490\uff0c\u6211\u6ca1\u6709\u4e3a\u96be\u4ed6\u4eec\uff0c\u60f3\u4e0d\u5230 \u4ed6\u4eec\u8bef\u8ba4\u6211\u662f\u94c1\u5934\u513f\u7684\u670b\u53cb\uff0c\u544a\u8bc9\u6211\u4e00\u4ef6\u5f88\u91cd\u8981\u7684\u6d88\u606f\uff0c\u4ed6\u4eec\u8bf4\u81ea\u5df1\u6ca1\u6709\u63a5\u8fd1\u7684\u80fd\u529b\uff0c\u8981\u6211 \u8f6c\u544a\u94c1\u5934\u513f\u60f3\u6cd5\u5b50\u6df1\u5165\u4fa6\u5bdf\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u5bf9\u8fd9\u4e9b\u6df7\u5403\u6df7\u559d\u5b98\u65b9\u8058\u7ea6\u7684\u773c\u7ebf\uff0c\u5b8c\u5168\u6ca1\u6709\u4fe1\u5fc3\u3002\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf4\u6765\u542c\u542c\uff0c \u8fd9\u4e9b\u9752\u75de\u6d41\u5b50\uff0c\u53ea\u6015\u5f88\u96be\u627e\u51fa\u4e00\u6761\u597d\u7ebf\u7d22\uff0c\u4ed6\u4eec\u65e0\u5fc3\u529e\u4e8b\uff0c\u610f\u5728\u6df7\u51e0\u4e2a\u94b1\u82b1\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u626c\u5dde\u662f\u5426\u6709\u5ea7\u611a\u516c\u56ed\u6797\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u6709\u8fd9\u4e48\u4e00\u5ea7\u56ed\u6797\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u90a3\u662f\u4e00\u7247\u7edd\u5730\uff0c\u4f46\u5f53\u5e74\u7684\u56ed\u4e3b\u7231\u5176\u6e05\u5e7d\uff0c\u529b\u6392 \u4f17\u8bae\uff0c\u5efa\u4e86\u4e00\u5ea7\u56ed\u6797\u5b85\u9662\uff0c\u53ef\u60dc\u5b85\u9662\u5c1a\u672a\u7b51\u5efa\u5b8c\u6210\uff0c\u4e3b\u4eba\u56e0\u75c5\u731d\u901d\uff0c\u8fd9\u5ea7\u5b85\u9662\u7684\u5de5\u7a0b\u8303\u56f4\uff0c \u5927\u4e3a\u7f29\u51cf\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u5c1a\u672a\u5b8c\u5de5\uff0c\u5df2\u6210\u51f6\u5b85\uff0c\u6240\u4ee5\uff0c\u79f0\u5b83\u53eb\u611a\u516c\u56ed\u6797\uff0c\u4e3b\u4eba\u7684\u4e0b\u4e00\u4ee3\uff0c\u4e5f\u672a\u8fc1 \u5165\u4f4f\u8fc7\uff0c\u65b0\u5b85\u5b8c\u6210\uff0c\u5c31\u8352\u5e9f\u4e86\u4e0b\u6765\uff0c\u8ddd\u4eca\u5df2\u5feb\u8fd1\u767e\u5e74\u4e86\u3000\u2026\u2026\u201d \u3000\u3000\u5434\u94c1\u5cf0\u63a5\u9053\uff1a\u201c\u4f60\u53bb\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u542c\u8bf4\u4ee5\u540e\uff0c\u4e5f\u6709\u4eba\u4f4f\u8fc7\uff0c\u4f46\u4e0d\u8fc7\u4e09\u5e74\uff0c\u5168\u5bb6\u5341\u516b\u53e3\u4eba\uff0c\u5728\u4e00\u6b21 \u76d7\u532a\u62a2\u52ab\u4e2d\uff0c\u5168\u906d\u6740\u5bb3\uff0c\u611a\u516c\u56ed\u6797\u88ab\u79f0\u51f6\u5b85\uff0c\u5c31\u66f4\u540d\u5b9e\u76f8\u7b26\u4e86\u3002\u6b64\u540e\uff0c\u5c31\u518d\u65e0\u4eba\u63d0\u8fc7\u8fd9\u5ea7\u5b85 \u9662\uff0c\u56e0\u4e3a\u52ab\u6740\u547d\u6848\uff0c\u53d1\u751f\u5728\u56db\u5341\u5e74\u524d\uff0c\u6211\u672c\u6709\u610f\uff0c\u67e5\u7a76\u4e00\u4e0b\u6b64\u6848\uff0c\u66ff\u6b7b\u8005\u7533\u51a4\uff0c\u4f46\u56e0\u5f53\u5e74\u5efa \u6b64\u611a\u56ed\u4e3b\u4eba\u51e0\u4ee3\u5df2\u8fc1\u79bb\u626c\u5dde\uff0c\u88ab\u6740\u7684\u4e00\u5bb6\u4eba\u4e5f\u672a\u7559\u4e0b\u4e00\u4e2a\u6d3b\u53e3\uff0c\u6beb\u65e0\u7740\u624b\u7684\u5730\u65b9\uff0c\u53c8\u5e74\u4ee3\u4e45 \u8fdc\uff0c\u5e9c\u8859\u4e5f\u65e0\u610f\u518d\u60f9\u9ebb\u70e6\uff0c\u4e8b\u60c5\u5c31\u8fd9\u6837\u505c\u4e86\u4e0b\u6765\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u4e00\u5904\u5730\u65b9\uff0c\u51f6\u5b85\u9b3c\u5c4b\uff0c\u4e5f\u6b63\u662f\u6c5f\u6e56\u51f6\u4eba\u559c\u6b22\u7684\u843d\u811a\u4e4b\u5904\uff0c\u675c\u8001\u5f1f\uff01\u201d\u5434\u94c1\u5cf0\u9053\uff1a \u201c\u54b1\u4eec\u4eca\u591c\u8054\u8882\u884c\u52a8\uff0c\u53bb\u63a2\u67e5\u4e00\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u544a\u8bc9\u4f60\u4e9b\u4ec0\u4e48\uff1f\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u662f\u5177\u4f53\u7684\u7ebf\u7d22\uff0c\u8fd8\u662f\u968f\u53e3\u8bf4\u51fa\u611a\u516c\u56ed\u6797\u8fd9\u4e2a\u5730\u65b9\uff0c \u4ee5\u8ba8\u597d\u4f60\u2026\u2026\u201d \u3000\u3000\u5434\u94c1\u5cf0\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4f60\u597d\u50cf\u5f88\u6392\u65a5\u4ed6\u4eec\uff0c\u4e0d\u76f8\u4fe1\u4ed6\u4eec\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\uff01\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4ed6\u4eec\u5584\u4e8e\u5bdf\u989c\u89c2\u8272\uff0c\u80e1\u8bf4\u516b\u9053\uff0c\u4ee5\u6c42\u8d4f\u9a97\u94b1\u4e3a\u4e3b\u3002\u6240\u4ee5\u5bf9\u4ed6\u4eec\u8bf4 \u7684\u8bdd\uff0c\u5fc5\u987b\u8981\u8be6\u52a0\u5206\u6790\uff0c\u90a3\u611a\u516c\u56ed\u6797\u4e2d\uff0c\u4e5f\u8bb8\u6709\u4eba\uff0c\u4f46\u4ec0\u4e48\u4eba\u5fc5\u5f97\u5148\u67e5\u4e2a\u6e05\u695a\u3002\u626c\u5dde\u60c5\u52bf\u590d \u6742\uff0c\u5341\u5927\u8c6a\u95e8\uff0c\u5404\u81ea\u4e3a\u653f\uff0c\u5374\u53c8\u76f8\u4e92\u8fde\u7cfb\uff0c\u4e00\u4e0b\u5b50\u6345\u5230\u9a6c\u8702\u7a9d\uff0c\u60f9\u51fa\u4e00\u4e9b\u4e0d\u5fc5\u8981\u7684\u9ebb\u70e6\uff0c\u5c31 \u62db\u6765\u9ebb\u70e6\u4e86\u3002\u201d \u3000\u3000\u201c\u675c\u8001\u5f1f\u7684\u610f\u601d\u5462\uff1f\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u96be\u9053\u5750\u89c6\u4e0d\u7406\u2026\u2026\u201d \u3000\u3000\u201c\u5144\u5f1f\u4e4b\u610f\u662f\uff0c\u4f1a\u6664\u5230\u4e8e\u3001\u5c91\u4e8c\u5144\u548c\u603b\u6355\u5934\u4e4b\u540e\uff0c\u518d\u4ed8\u8bf8\u884c\u52a8\uff0c\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u90a3\u65f6\uff0c\u6211\u4eec\u529b\u91cf\u5f3a\u5927\uff0c\u7eb5\u7136\u60f9\u51fa\u9ebb\u70e6\uff0c\u4ea6\u53ef\u4ece\u5bb9\u5e94\u4ed8\uff0c\u4e5f\u53ef\u4f5c\u65ad\u7136\u5904\u7f6e\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u597d\u5427\uff01\u675c\u8001\u5f1f\u719f\u6089\u626c\u5dde\u60c5\u52bf\uff0c\u5982\u6b64\u7684\u8c28\u614e\u5c0f\u5fc3\uff0c\u81ea\u6709\u76d8\u7b97\uff0c\u5434\u67d0\u5bbe\u4e0d\u538b\u4e3b\uff0c \u54b1\u4eec\u7531\u6b64\u523b\u8d77\uff0c\u5168\u529b\u5bfb\u89c5\u4e8e\u3001\u5c91\u4e8c\u5144\u548c\u603b\u6355\u5934\u7b49\u884c\u8e2a\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u5fc3\u4e2d\u5fd6\u9053\uff1a\u603b\u6355\u5934\u5973\u513f\u4e4b\u8eab\uff0c\u5c31\u7b97\u53ef\u4ee5\u626e\u4f5c\u7537\u5b50\uff0c\u4f46\u4ee5\u76ee\u524d\u626c\u5dde\u57ce\u5185\u60c5\u52bf\u590d\u6742\uff0c \u975e\u8981\u5927\u8d39\u5468\u6298\uff0c\u4f5c\u4e00\u756a\u7cbe\u5bc6\u7684\u8bbe\u8ba1\u5e03\u7f6e\uff0c\u5c31\u5f88\u96be\u4e0d\u53d7\u9a9a\u6270\uff0c\u6700\u597d\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u501f\u4f4f\u5728\u626c\u5dde\u5e9c \u8859\u3002 \u3000\u3000\u5fc3\u4e2d\u76d8\u7b97\u51fa\u4e00\u4e2a\u5e95\u5b50\uff0c\u4f46\u5374\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u56e0\u4e3a\uff0c\u4ed6\u4e00\u76f4\u60e6\u5ff5\u7740\u60dc\u7389\u4e4b\u7ea6\uff0c\u8c2d\u59d1\u5a18\u53ef\u80fd\u5e26 \u6765\u4e86\u5f88\u60ca\u4eba\u7684\u6d88\u606f\uff0c\u56db\u51e4\u697c\u4e5f\u662f\u4e00\u4e2a\u795e\u79d8\u6240\u5728\uff0c\u5a49\u62d2\u548c\u5434\u94c1\u5cf0\u63a2\u67e5\u611a\u516c\u56ed\u6797\uff0c\u4e5f\u662f\u62c5\u5fc3\u60f9\u51fa \u9ebb\u70e6\uff0c\u65e0\u6cd5\u8131\u8eab\u8d74\u4f73\u4eba\u4e4b\u7ea6\u3002 \u3000\u3000\u675c\u671b\u6708\u529e\u4e86\u65e0\u6570\u51f6\u6740\u79bb\u5947\u7684\u6848\u5b50\uff0c\u4f46\u5374\u6ca1\u6709\u906d\u9047\u50cf\u626c\u5dde\u8fd9\u6837\u7684\u73af\u5883\uff0c\u5f88\u591a\u5b9e\u529b\u5f3a\u5927\u7684\u7ec4 \u5408\uff0c\u5206\u5e03\u5728\u65b9\u5706\u6570\u5341\u91cc\u4e4b\u5185\uff0c\u5404\u6709\u8303\u56f4\uff0c\u81ea\u6210\u4e00\u5bb6\uff0c\u53ef\u4ee5\u4e3a\u654c\uff0c\u4e5f\u53ef\u4ee5\u4e3a\u53cb\uff0c\u5341\u5927\u5bcc\u8c6a\u4e4b\u95f4\uff0c \u4f3c\u53cb\u975e\u53cb\uff0c\u4ee4\u4eba\u6349\u6478\u4e0d\u5b9a\u3002 \u3000\u3000\u66f4\u53ef\u6015\u7684\u662f\u8fd8\u6709\u5f88\u591a\u9690\u533f\u5728\u6697\u4e2d\u7684\u7ec4\u5408\uff0c\u5982\u56db\u51e4\u697c\u3001\u6740\u624b\u4e4b\u738b\u7b49\uff0c\u5b9e\u529b\u90fd\u5f88\u5f3a\u5927\u3002\u68a6\u5e7b \u4e4b\u5200\uff0c\u66f4\u662f\u884c\u8e2a\u5982\u8c1c\uff0c\u4ed6\u53ef\u80fd\u662f\u6740\u5bb3\u9a6c\u516c\u5b50\u7684\u51f6\u624b\uff0c\u4f46\u4ed6\u4eba\u5728\u4f55\u5904\uff1f\u90fd\u65e0\u6cd5\u786e\u5b9a\uff0c\u8fd9\u4e9b\u4e8b\uff0c \u90fd\u662f\u4ed6\u524d\u6240\u672a\u66fe\u9047\u8fc7\uff0c\u8fd9\u5c31\u4f7f\u5f97\u675c\u671b\u6708\u53d8\u7684\u8c28\u614e\u8d77\u6765\uff0c\u4e0d\u80fd\u7545\u6240\u6b32\u8a00\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u4f55\u7b49\u7684\u7cbe\u660e\u8001\u7ec3\uff0c\u5df2\u9690\u9690\u89c9\u5230\u675c\u671b\u6708\u5fc3\u4e2d\u987e\u5fcc\u5f88\u591a\uff0c\u548c\u4ed6\u4ee5\u524d\u7684\u52c7\u6b66\u8c6a\u58ee\uff0c\u5927\u4e0d \u76f8\u540c\uff0c\u662f\u5e74\u5c81\u6e10\u589e\uff0c\u4eba\u6e10\u7a33\u5065\uff0c\u6216\u662f\u9047\u4e0a\u4e86\u5341\u5206\u68d8\u624b\u7684\u6848\u60c5\uff0c\u4e0d\u6562\u7a0d\u6709\u5931\u9519\u3002 \u3000\u3000\u4e0d\u7ba1\u4e3a\u4ec0\u4e48\uff0c\u5434\u94c1\u5cf0\u4e5f\u4e0d\u4fbf\u6df1\u7a76\u4e0b\u53bb\uff0c\u60f3\u4e00\u60f3\uff0c\u4e24\u4e2a\u4eba\u7684\u529b\u91cf\u5408\u8d77\u6765\uff0c\u4e5f\u5b9e\u5728\u6709\u9650\uff0c\u4e5f \u9876\u4e0d\u4f4f\u60ca\u5929\u52a8\u5730\u7684\u5927\u98ce\u6d6a\uff0c\u5fc3\u4e2d\u539f\u6709\u7684\u4e00\u80a1\u4e0d\u60a6\u4e4b\u610f\uff0c\u4e5f\u5c31\u5316\u4f5c\u4e91\u70df\u800c\u53bb\u3002\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u5144 \u5f1f\u4eca\u5929\u5c31\u5377\u8d77\u62db\u724c\uff0c\u5728\u626c\u5dde\u6e38\u8361\u4e24\u5929\uff0c\u5e0c\u671b\u4e24\u5929\u5185\u80fd\u649e\u4e0a\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u4e00\u8a00\u4e3a\u5b9a\uff0c\u671b\u6708\u4e5f\u5c3d\u8fd9\u4e24\u65e5\u6668\u5149\uff0c\u5e0c\u671b\u80fd\u627e\u51fa\u4ed6\u4eec\u843d\u811a\u4e4b\u5904\uff0c\u4e24\u5929\u4e4b\u540e\uff0c\u518d\u6765\u7eff\u626c\u5c45 \u4e2d\u627e\u4f60\u3002\u201d \u3000\u3000\u201c\u5e0c\u671b\u6211\u4ecd\u7136\u7559\u5728\u8fd9\u91cc\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u7eb5\u7136\u8981\u8d70\uff0c\u4e5f\u4f1a\u5728\u8fd9\u91cc\u7559\u7ed9\u4f60\u8ffd\u89c5\u7684\u7ebf\u7d22\uff0c\u4f60 \u8d70\u5427\uff01\u8fd9\u4e00\u62c5\u9752\u83dc\u841d\u535c\uff0c\u7559\u7ed9\u6211\u5904\u7406\u5c31\u662f\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u4e5f\u4e0d\u5728\u5ba2\u6c14\uff0c\u79bb\u5f00\u4e86\u7eff\u626c\u5c45\uff0c\u76f4\u5954\u626c\u5dde\u5e9c\u8859\uff0c\u5927\u95e8\u8fdb\u5f97\u5f88\u987a\u5229\uff0c\u4f46\u4e00\u5165\u5927\u95e8\uff0c\u7acb \u65f6\u88ab\u56db\u4e2a\u6355\u5feb\u56f4\u4e86\u8d77\u6765\u3002\u6562\u60c5\uff0c\u8fd9\u662f\u8bf1\u6355\u624b\u6cd5\uff0c\u5bf9\u5f62\u8ff9\u53ef\u7591\u7684\u4eba\uff0c\u4e0d\u662f\u9010\u8d70\u4e86\u4e8b\uff0c\u800c\u662f\u8981\u7559 \u4e0b\u843d\u6848\u5ba1\u95ee\u4e86\u3002 \u3000\u3000\u5e78\u597d\uff0c\u526f\u603b\u6355\u738b\u575a\u6765\u7684\u5f88\u5feb\uff0c\u4ed4\u7ec6\u5730\u77a7\u4e86\u51e0\u773c\uff0c\u5206\u8fa8\u51fa\u675c\u671b\u6708\u7684\u8eab\u5206\uff0c\u5e26\u5165\u73ed\u623f\u9053\uff1a \u201c\u675c\u5144\uff0c\u4eb2\u6765\u5e9c\u8859\uff0c\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6709\u4e9b\u4e8b\uff0c\u6211\u4f5c\u4e0d\u4e86\u4e3b\uff0c\u8981\u77e5\u5e9c\u5927\u4eba\u51b3\u5b9a\u7684\u4e8b\u60c5\uff0c\u8fd8\u662f\u89c1\u89c1\u5927\u4eba\u7684\u597d\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u63a2\u95ee\u4e00\u4e0b\uff0c\u662f\u5426\u6709\u5ba2\u4eba\u8fdb\u4f4f\u4e86\u5e9c\u8859\uff1f\u5982\u679c\u6709\uff0c\u5c31\u8981\u5927\u4eba\u8f6c\u544a\uff0c\u5c31\u8bf4\u675c\u671b\u6708\u4e24\u5929\u4e4b \u540e\uff0c\u518d\u6765\u5e9c\u8859\u62dc\u8bbf\uff0c\u90a3\u4f4d\u6765\u5ba2\u662f\u5426\u613f\u63a5\u89c1\u675c\u67d0\uff1f\u5f53\u7136\uff0c\u4ed6\u4e5f\u53ef\u4ee5\u7559\u4e0b\u5730\u70b9\u65f6\u95f4\uff0c\u675c\u67d0\u4eba\u4f1a\u4f9d \u7ea6\u62dc\u8bbf\u2026\u2026\u201d \u3000\u3000\u738b\u575a\u5e0c\u671b\u542c\u4ed6\u8bf4\u51fa\u6765\u4eba\u7684\u8eab\u5206\uff0c\u4f46\u675c\u671b\u6708\u5374\u4e0d\u8bf4\u4e86\uff0c\u738b\u575a\u5fcd\u4e86\u53c8\u5fcd\uff0c\u5fcd\u4e0b\u672a\u95ee\uff0c\u70b9\u70b9\u5934\uff0c \u9053\uff1a\u201c\u6211\u4e00\u5b9a\u627e\u673a\u4f1a\u95ee\u4e2a\u660e\u767d\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u7ad9\u8d77\u8eab\u5b50\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u675c\u67d0\u544a\u8f9e\u3002\u201d\u8f6c\u8eab\u5feb\u6b65\u800c\u53bb\u3002 \u3000\u3000\u7ed5\u8fc7\u4e00\u6761\u5927\u8857\uff0c\u675c\u671b\u6708\u53d1\u89c9\u88ab\u4eba\u76ef\u4e0a\u4e86\uff0c\u76ef\u68a2\u7684\u65b9\u6cd5\u5f88\u9ad8\u660e\uff0c\u7ed5\u8fc7\u4e00\u6761\u8857\uff0c\u884c\u7a0b\u6570\u767e\u4e08\uff0c \u624d\u88ab\u53d1\u89c9\uff0c\u800c\u675c\u671b\u6708\uff0c\u4e00\u76f4\u5f88\u8b66\u89c9\u88ab\u4eba\u76ef\u68a2\u7684\u4e8b\uff0c\u51e0\u4e4e\u8981\u88ab\u4ed6\u8ffd\u5230\u843d\u811a\u5730\u65b9\uff0c\u90a3\u5c31\u9ebb\u70e6\u5927\u4e86\uff0c \u5e78\u597d\u65e9\u4e00\u70b9\u53d1\u89c9\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u5fc3\u4e2d\u5f88\u75db\u6068\uff0c\u4f46\u5374\u65e0\u6cd5\u63a8\u65ad\u51fa\u662f\u54ea\u4e00\u8def\u7684\u4eba\u9a6c\uff0c\u521a\u7531\u8859\u95e8\u51fa\u6765\uff0c\u5e94\u8be5\u4e0d\u662f\u626c\u5dde\u5e9c \u8859\u7684\u773c\u7ebf\u4e86\uff1f\u53ea\u597d\u6539\u53d8\u884c\u7a0b\uff0c\u8f6c\u5411\u4e00\u5904\u50fb\u9759\u7684\u6240\u5728\uff0c\u51c6\u5907\u4e0b\u6bd2\u624b\u60e9\u6cbb\u8fd9\u4e2a\u76ef\u68a2\u4eba\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u52a0\u5feb\u811a\u6b65\uff0c\u7a7f\u884c\u56db\u6761\u5927\u8857\uff0c\u8f6c\u5165\u4e00\u7247\u6797\u6728\u8302\u76db\u7684\u9732\u5929\u8336\u56ed\u3002\u8fd9\u91cc\u5c45\u6c11\u5f88\u5c11\uff0c\u4f46\u5165 \u591c\u4e4b\u524d\u6e38\u4eba\u5f88\u591a\uff0c\u662f\u5e02\u6c11\u665a\u4e0a\u4e58\u51c9\u3001\u804a\u5929\u7684\u6240\u5728\uff0c\u53bb\u4e0d\u8d77\u8336\u9986\u9152\u697c\u7684\u4eba\uff0c\u665a\u4e0a\u4f1a\u6765\u8fd9\u91cc\u6ce1\u7897 \u8336\uff0c\u6d88\u89e3\u53bb\u4e00\u5929\u5de5\u4f5c\u7684\u75b2\u52b3\uff0c\u662f\u8d29\u592b\u8d70\u5352\u7684\u4f1a\u96c6\u4e4b\u5730\u3002 \u3000\u3000------------- \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c \u4e5d \u7ae0 \u3000\u3000\u8fd9\u4e5f\u662f\u4e00\u79cd\u624b\u6bb5\uff0c\u4ed6\u8ffd\u8e2a\u675c\u671b\u6708\uff0c\u53c8\u662f\u89c9\u7740\u675c\u671b\u6708\u7531\u8859\u95e8\u8d70\u51fa\u6765\uff0c\u6709\u4e9b\u53ef\u7591\uff0c\u5374\u672a\u5fc5\u77e5 \u6653\u662f\u4f55\u8bb8\u4eba\u7269\uff0c\u8fd9\u4e00\u9635\u5feb\u62f3\u91cd\u51fb\uff0c\u6253\u4e86\u5c31\u8dd1\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u53ea\u80fd\u81ea\u8ba4\u6666\u6c14\uff0c\u78b0\u4e0a\u4e86\u9ad8\u4eba\uff0c\u65ad\u4e86\u4e24\u6839\u808b\u9aa8\uff0c\u75db\u5f7b\u5fc3\u80ba\uff0c\u672a\u77a7\u51fa\u4e00\u70b9\u540d\u5802\uff0c\u7167\u5b9e \u56de\u62a5\uff0c\u592a\u8fc7\u4e22\u4eba\uff0c\u8fd8\u5f97\u81ea\u5df1\u7f16\u4e00\u5957\u8c0e\u8a00\uff0c\u4ee5\u4f5c\u63a9\u9970\u3002 \u3000\u3000\u6700\u91cd\u8981\u662f\u77ed\u671f\u5185\u65e0\u6cd5\u518d\u884c\u5954\u8d70\uff0c\u8fd9\u4f24\u52bf\u7eb5\u7136\u63a5\u9aa8\u5f88\u597d\uff0c\u4f46\u4e5f\u5f97\u5341\u5929\u534a\u6708\u7684\u517b\u606f\uff0c\u624d\u80fd\u884c \u52a8\u5982\u5e38\u3002\u3000\u4f11\u606f\u534a\u4e2a\u65f6\u8fb0\uff0c\u9ed1\u8863\u4eba\u624d\u7531\u6811\u6797\u4e2d\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4f46\u4ed6\u505a\u68a6\u4e5f\u672a\u60f3\u5230\uff0c\u675c\u671b\u6708\u6539\u4e86\u5f62\u8c8c\uff0c\u53cd\u884c\u76ef\u68a2\uff0c\u800c\u4e14\u975e\u5e38\u5c0f\u5fc3\uff0c\u9ed1\u8863\u4eba\u4e5f\u4e0d\u542b\u7cca\uff0c\u51e0 \u6b21\u7684\u7a81\u7136\u56de\u8eab\u63a2\u67e5\uff0c\u7adf\u672a\u53d1\u89c9\u53ef\u7591\u7684\u7834\u7efd\u3002 \u3000\u3000\u675c\u671b\u6708\u53ea\u8981\u63a2\u77e5\u4ed6\u843d\u811a\u4e4b\u5904\uff0c\u4fdd\u6301\u7740\u76ee\u5149\u6240\u53ca\u7684\u8ddd\u79bb\uff0c\u90a3\u662f\u4e2a\u76f8\u5f53\u8fdc\u7684\u957f\u5ea6\uff0c\u9ed1\u8863\u4eba\u5c31 \u5168\u65e0\u8b66\u89c9\u4e86\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u7684\u843d\u811a\u4e4b\u5904\uff0c\u7adf\u662f\u5434\u94c1\u5cf0\u63d0\u5230\u7684\u611a\u516c\u56ed\u6797\u3002 \u3000\u3000\u675c\u671b\u6708\u6697\u53eb\u4e86\u4e24\u58f0\u60ed\u6127\uff0c\u94c1\u7fce\u5e03\u4e0b\u7684\u773c\u7ebf\uff0c\u5e76\u975e\u5168\u662f\u996d\u6876\uff0c\u611a\u8005\u5343\u5931\uff0c\u4ea6\u6709\u4e00\u5f97\uff0c\u6700\u96be \u80fd\u53ef\u8d35\u7684\u662f\u4ed6\u4eec\u7adf\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u4e0d\u4f5c\u63a2\u7d22\uff0c\u8fd9\u5c31\u4e0d\u81f4\u6253\u8349\u60ca\u86c7\u4e86\uff0c\u4e5f\u4fdd\u4f4f\u4e86\u81ea\u5df1\u7684\u6027\u547d\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u675c\u671b\u6708\u4e5f\u65e0\u6cd5\u5224\u65ad\u51fa\u611a\u516c\u56ed\u6797\u51f6\u5b85\u4e2d\uff0c\u4f4f\u7684\u662f\u4f55\u65b9\u795e\u5723\u3002\u4f46\u4ed6\u80fd\u80af\u5b9a\u662f\u4e00\u6279\u9ad8 \u624b\u4f1a\u96c6\u7684\u7ec4\u5408\uff0c\u4e5f\u53ef\u80fd\u5c31\u662f\u8d3e\u82f1\u548c\u5929\u67ab\u9053\u957f\u82e6\u82e6\u641c\u7d22\u800c\u65e0\u6cd5\u8ffd\u51fa\u884c\u8e2a\u7684\u68a6\u5e7b\u4e4b\u5200\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u675c\u671b\u6708\u4e5f\u672a\u4f5c\u5192\u9669\u63a2\u7d22\uff0c\u8fc5\u5feb\u5730\u79bb\u53bb\u3002 \u3000\u3000\u660e\u5929\uff0c\u4ed6\u5c06\u53bb\u4f1a\u6664\u60dc\u7389\uff0c\u5e0c\u671b\u80fd\u5bf9\u56db\u51e4\u697c\u7684\u5b9e\u529b\uff0c\u591a\u4e00\u4e9b\u4e86\u89e3\u3002\u8fd9\u51e0\u65e5\u7684\u63a2\u7d22\u6697\u8bbf\uff0c\u624d \u53d1\u89c9\u8fd9\u4e2a\u6c47\u805a\u4e86\u8d22\u5bcc\u51a0\u5168\u56fd\u7684\u540d\u57ce\uff0c\u4e5f\u4f1a\u805a\u4e86\u6c5f\u6e56\u4e0a\u65e0\u6cd5\u8ba1\u6570\u7684\u9ad8\u624b\uff0c\u548c\u5f88\u591a\u795e\u79d8\u83ab\u6d4b\u7684\u7ec4 \u5408\uff0c\u771f\u662f\u86c7\u9f20\u7fa4\u96c6\uff0c\u9f99\u76d8\u864e\u8e1e\u3002 \u3000\u3000\u675c\u671b\u6708\u51b3\u5fc3\u628a\u8fd9\u4e9b\u63a2\u5f97\u7684\u9690\u79d8\uff0c\u6682\u65f6\u85cf\u4e8e\u5fc3\u4e2d\uff0c\u4fdf\u603b\u6355\u5934\u548c\u56db\u65b9\u540d\u6355\u4f1a\u9f50\u4e4b\u540e\uff0c\u518d\u63d0\u51fa \u7814\u5546\uff0c\u4ed6\u660e\u767d\u4e1c\u3001\u4e2d\u3001\u897f\u4e09\u5927\u6355\u5934\uff0c\u90fd\u662f\u827a\u9ad8\u80c6\u5927\u7684\u4eba\uff0c\u4e00\u65e6\u638c\u63e1\u4e00\u4e9b\u7ebf\u7d22\uff0c\u5fc5\u5c06\u6df1\u5165\u8ffd\u67e5\uff0c \u5bf9\u65b9\u53c8\u90fd\u662f\u5584\u4e8e\u8ba1\u7b97\u7684\u6c5f\u6e56\u9b54\u5934\uff0c\u8b66\u89c9\u5947\u9ad8\uff0c\u7a0d\u6709\u758f\u5931\uff0c\u9732\u51fa\u7834\u7efd\uff0c\u5fc5\u906d\u8ffd\u6740\uff0c\u4ee5\u6c42\u706d\u53e3\u3002 \u3000\u3000\u4e0d\u96c6\u4e2d\u4e94\u5927\u6355\u5934\uff0c\u5f88\u96be\u548c\u4eba\u5bf9\u6297\uff0c\u5434\u94c1\u5cf0\u3001\u4e8e\u627f\u5fd7\u3001\u5c91\u5578\u864e\u90fd\u662f\u4ed6\u675c\u671b\u6708\u591a\u5e74\u7684\u597d\u53cb\uff0c \u5b9e\u4e0d\u613f\u4ed6\u4eec\u4efb\u4f55\u4e00\u4e2a\u4eba\u53d7\u5230\u4f24\u5bb3\uff0c\u90a3\u5c06\u662f\u4ed6\u7ec8\u8eab\u4e4b\u75db\uff0c\u53ea\u6709\u5f3a\u81ea\u5fcd\u4e0b\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u56de\u5230\u4f4f\u5904\uff0c\u76d8\u5750\u8c03\u606f\uff0c\u628a\u4f53\u80fd\u8c03\u6574\u5230\u6700\u4f73\u72b6\u51b5\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u675c\u671b\u6708\u6ca1\u6709\u6613\u5bb9\uff0c\u4f46\u5374\u6234\u4e86\u4e00\u9876\u8fde\u5761\u8349\u5e3d\uff0c\u906e\u4f4f\u5927\u534a\u7684\u9762\u76ee\uff0c\u624b\u4e2d\u4e5f\u591a\u4e86\u4e00\u6839 \u4e4c\u6728\u6746\u68d2\uff0c\u91cc\u9762\u85cf\u7684\u662f\u987a\u624b\u5175\u5203\u4e03\u661f\u5b9d\u5251\uff0c\u4f46\u6700\u5927\u7684\u6539\u53d8\uff0c\u662f\u7a7f\u4e86\u4e00\u4ef6\u9ed1\u8272\u7f69\u888d\u3002 \u3000\u3000\u6c5f\u6e56\u9053\u4e0a\uff0c\u90fd\u77e5\u9053\u6c5f\u5357\u540d\u6355\u8e0f\u96ea\u65e0\u75d5\u675c\u671b\u6708\u4e00\u8eab\u767d\u8863\uff0c\u52a0\u4e0a\u4e00\u4ef6\u9ed1\u8272\u5916\u5957\uff0c\u5c31\u5f88\u96be\u60f3\u5230 \u4ed6\u662f\u675c\u671b\u6708\u4e86\u3002 \u3000\u3000\u4eca\u65e5\u5929\u8272\u6e05\u6717\uff0c\u7626\u897f\u6e56\u666f\u7269\u5982\u753b\uff0c\u675c\u671b\u6708\u5230\u7684\u5f88\u65e9\u3002\u4e0d\u8fc7\u662f\u65e5\u4e0a\u4e09\u7aff\u65f6\u5206\uff0c\u4eba\u5c31\u4e8e\u7626\u897f \u6e56\u7684\u89c2\u9c7c\u4ead\uff0c\u79df\u4e86\u4e00\u8258\u5c0f\u821f\uff0c\u5148\u7ed5\u7740\u89c2\u9c7c\u4ead\u5de1\u884c\u4e00\u5468\uff0c\u770b\u6e05\u695a\u56db\u5468\u5f62\u52bf\u3002\u4f46\u6b64\u523b\u65f6\u95f4\u8fd8\u65e9\uff0c \u6e38\u4eba\u4e0d\u591a\uff0c\u4e5f\u672a\u89c1\u60dc\u7389\u5f62\u5bb9\u7684\u90a3\u8258\u6e14\u821f\uff0c\u8fd9\u91cc\u6c34\u5448\u78a7\u7eff\uff0c\u79ef\u6c34\u751a\u6df1\uff0c\u4e5f\u662f\u4e00\u4e2a\u666f\u70b9\uff0c\u5cb8\u8fb9\u51e0 \u5904\u5c0f\u7801\u5934\uff0c\u6392\u4e86\u4e0d\u5c11\u51fa\u79df\u5c0f\u821f\uff0c\u4e5f\u6709\u51e0\u8258\u7531\u8239\u8001\u5927\u64cd\u8235\u7684\u5c0f\u578b\u753b\u80aa\u3002 \u3000\u3000\u89c2\u9c7c\u4ead\u7a81\u51fa\u6c34\u4e08\u8bb8\u5de6\u53f3\uff0c\u662f\u4e00\u5ea7\u516b\u89d2\u578b\u7684\u5efa\u7b51\uff0c\u4ead\u4e2d\u7ea6\u6709\u4e09\u4e08\u65b9\u5706\u7684\u5730\u65b9\uff0c\u8bbe\u6709\u8336\u5ea7\uff0c \u4f46\u8fd8\u7a7a\u65e0\u5750\u5ba2\uff0c\u6709\u4e24\u4e2a\u5356\u8336\u7684\u4f19\u8ba1\uff0c\u6b63\u5728\u5916\u7089\u716e\u6c34\uff0c\u51c6\u5907\u8fce\u63a5\u987e\u5ba2\u3002 \u3000\u3000\u675c\u671b\u6708\u8349\u5e3d\u4f4e\u5782\uff0c\u906e\u53bb\u4e86\u5927\u534a\u4e2a\u8138\uff0c\u4e24\u9053\u76ee\u5149\u7531\u5e3d\u6cbf\u900f\u51fa\uff0c\u5374\u628a\u56db\u5468\u666f\u7269\u770b\u5f97\u5341\u5206\u6e05\u695a\uff0c \u4e0d\u7981\u54d1\u7136\u5931\u7b11\uff0c\u6765\u7684\u592a\u65e9\u4e86\uff0c\u4e5f\u8bb8\u771f\u7684\u5df2\u88ab\u60dc\u7389\u4e00\u7f15\u67d4\u60c5\u7275\u7cfb\uff0c\u7adf\u662f\u5982\u6b64\u7684\u60c5\u4e0d\u81ea\u7981\u3002 \u3000\u3000\u4f46\u4ed6\u4ecd\u7136\u4fdd\u6301\u4e86\u5f88\u9ad8\u7684\u8b66\u89c9\uff0c\u6765\u6b64\u4e4b\u524d\uff0c\u5df2\u4f5c\u4e86\u6700\u574f\u7684\u6253\u7b97\uff0c\u4e00\u65e6\u60dc\u7389\u53d7\u5230\u6000\u7591\uff0c\u6216\u662f \u6539\u53d8\u5fc3\u610f\u51fa\u5356\u4e86\u81ea\u5df1\uff0c\u8fd9\u91cc\u5c31\u53ef\u80fd\u5e03\u4e0b\u4e86\u4e00\u4e2a\u9677\u9631\uff0c\u90a3\u5c31\u514d\u4e0d\u4e86\u4e00\u573a\u8840\u6218\u3002\u6240\u4ee5\uff0c\u5e26\u4e0a\u4e86\u5f88 \u5c11\u65bd\u7528\u7684\u6697\u5668\uff0c\u4e5f\u5e26\u6765\u987a\u624b\u7684\u5175\u5200\uff0c\u4ed6\u6ca1\u7ea6\u5e03\u63f4\u5175\uff0c\u51c6\u5907\u5355\u4eba\u4e00\u5251\uff0c\u72ec\u62d2\u5f3a\u654c\uff0c\u8840\u6218\u7626\u897f\u6e56 \u4e86\u3002 \u3000\u3000\u53cc\u624b\u7f13\u7f13\u8fd0\u6868\uff0c\u5c0f\u821f\u5728\u6c34\u9762\u8f7b\u8f7b\u6ed1\u8fc7\uff0c\u7167\u7740\u60dc\u7389\u7684\u8bf4\u8bcd\uff0c\u5728\u89c2\u9c7c\u4ead\u56db\u5468\u5de1\u884c\u3002\u8f7b\u821f\u8361\u6f3e\uff0c \u6c34\u6ce2\u4e0d\u8d77\uff0c\u770b\u4e0a\u53bb\u662f\u4e00\u4e2a\u95f2\u60c5\u9038\u81f4\u7684\u6e38\u6e56\u4eba\uff0c\u60a0\u95f2\u5730\u5728\u89c2\u8d4f\u6e56\u5149\u5c71\u8272\uff0c\u4eab\u53d7\u8fd9\u4e00\u5206\u65e9\u4e0a\u7684\u5e7d \u9759\u3002 \u3000\u3000\u675c\u671b\u6708\u7ed5\u884c\u7684\u5708\u5b50\u5f88\u5927\uff0c\u6709\u65f6\u9760\u8fd1\u6e56\u5cb8\uff0c\u4e00\u5927\u5708\u7ed5\u884c\u4e0b\u6765\uff0c\u82b1\u8d39\u8fd1\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u4e45\u3002\u6e38\u6e56 \u7684\u5ba2\u4eba\u6e10\u6e10\u589e\u591a\uff0c\u675c\u671b\u6708\u4e5f\u53d1\u73b0\u4e86\u4e00\u8258\u6e14\u8239\uff0c\u505c\u5728\u8ddd\u79bb\u89c2\u9c7c\u4ead\u5341\u4e08\u4e4b\u5916\uff0c\u8239\u5934\u7684\u7532\u677f\u4e0a\u5750\u7740 \u4e00\u4e2a\u84d1\u8863\u8349\u5c65\u7684\u9ed1\u8863\u8001\u4eba\uff0c\u5782\u9493\u6e56\u4e2d\u3002 \u3000\u3000\u8fd9\u662f\u6e38\u4eba\u8d4f\u666f\u7684\u5730\u65b9\uff0c\u5f88\u5c11\u6709\u5782\u9493\u7f51\u9c7c\u7684\u4eba\uff0c\u8fd9\u4e2a\u4eba\u5c31\u7279\u522b\u7684\u62a2\u773c\u4e86\u3002\u675c\u671b\u6708\u8f7b\u8f7b\u5401\u4e00 \u53e3\u6c14\uff0c\u51dd\u805a\u76ee\u5149\uff0c\u4ed4\u7ec6\u770b\u90a3\u4e2a\u5782\u9493\u8001\u4eba\uff0c\u80f8\u524d\u767d\u9aef\u98d8\u6d6e\uff0c\u4f46\u4eba\u5374\u5750\u7684\u7a33\u5982\u78d0\u77f3\uff0c\u7ed9\u4eba\u4e00\u79cd\u611f \u89c9\uff0c\u4ed6\u5df2\u548c\u90a3\u8258\u6e14\u8239\u6eb6\u4e3a\u4e00\u4f53\u3002\u675c\u671b\u6708\u662f\u884c\u5bb6\uff0c\u81ea\u7136\u770b\u5f97\u51fa\u6765\uff0c\u90a3\u8001\u4eba\u6b63\u51dd\u805a\u4e86\u5168\u8eab\u529f\u529b\uff0c \u5728\u548c\u4e00\u79cd\u529b\u91cf\u5bf9\u6297\uff0c\u867d\u7136\u53ea\u662f\u9759\u5750\uff0c\u6ca1\u6709\u4efb\u4f55\u52a8\u4f5c\uff0c\u4f46\u6fc0\u70c8\u7684\u7a0b\u5ea6\uff0c\u5374\u4e0d\u8f93\u5200\u5251\u76f8\u51fb\u7684\u6076\u6218\u3002 \u3000\u3000\u548c\u4ec0\u4e48\u529b\u91cf\u5728\u5bf9\u6297\u5462\uff1f\u5bf9\u624b\u5728\u54ea\u91cc\uff1f \u3000\u3000\u90a3\u6e14\u8239\u7684\u56db\u5468\uff0c\u4e0d\u89c1\u4eba\u821f\uff0c\u6e56\u6c34\u4e5f\u5e73\u9759\u65e0\u6ce2\uff0c\u76ee\u529b\u6240\u5230\u4e4b\u5904\uff0c\u770b\u4e0d\u5230\u4efb\u4f55\u548c\u90a3\u8001\u6e14\u4eba\u6297 \u62d2\u7684\u4eba\u3001\u4e8b\u3001\u7269\u7684\u5b58\u5728\u3002 \u3000\u3000\u8fd9\u79cd\u6f5c\u9690\u4e0d\u89c1\u7684\u538b\u529b\uff0c\u4f7f\u5f97\u9605\u5386\u4e30\u5bcc\u7684\u675c\u671b\u6708\u4e5f\u4e3a\u4e4b\u7d27\u5f20\u8d77\u6765\uff0c\u6ca1\u6709\u89c1\u5230\u60dc\u7389\uff0c\u4e5f\u4e0d\u77e5 \u90a3\u84d1\u8863\u8001\u4eba\uff0c\u662f\u5426\u5c31\u662f\u60dc\u7389\u7684\u4e49\u7236\uff0c\u90a3\u6e14\u8239\u4e0a\uff0c\u7a76\u7adf\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f\u60dc\u7389\u662f\u5426\u5728\u90a3\u8258\u6e14\u8239 \u4e0a\u5462\uff1f \u3000\u3000\u8fd9\u4e00\u5207\u7b54\u6848\uff0c\u90fd\u9700\u8981\u731c\u6d4b\u3002\u4f46\u4e00\u76f4\u672a\u53d1\u73b0\u4efb\u4f55\u5f81\u50cf\u3002 \u3000\u3000\u5c0f\u821f\u9010\u6e10\u63a5\u8fd1\u4e86\u6e14\u8239\uff0c\u675c\u671b\u6708\u677e\u5f00\u53cc\u6868\uff0c\u53d6\u8fc7\u4e4c\u6728\u6746\u68d2\uff0c\u65cb\u5f00\u63a5\u5408\u4e4b\u5904\uff0c\u9732\u51fa\u4e03\u661f\u5251\u67c4\uff0c \u8fde\u5251\u5e26\u9798\uff0c\u62bd\u4e86\u51fa\u6765\uff0c\u653e\u7f6e\u8eab\u4fa7\u3002\u8fd9\u65f6\uff0c\u8ddd\u79bb\u6e14\u8239\u53ea\u4f59\u4e0b\u4e09\u4e08\u591a\u8fdc\u3002 \u3000\u3000\u675c\u671b\u6708\u6697\u4e2d\u8fd0\u6c14\uff0c\u4e00\u624b\u6293\u8d77\u4e03\u661f\u5b9d\u5251\uff0c\u51c6\u5907\u8dc3\u767b\u6e14\u8239\u4e86\u3002\u8fd9\u662f\u5fc5\u987b\u8981\u63ed\u5f00\u7684\u79d8\u5bc6\uff0c\u65e2\u7136 \u65e0\u6cd5\u5728\u5916\u9762\u770b\u51fa\u539f\u56e0\uff0c\u53ea\u597d\u5192\u9669\u767b\u821f\uff0c\u4e00\u63a2\u7a76\u7adf\u4e86\u3002 \u3000\u3000\u6b63\u5f85\u98de\u8dc3\u800c\u8d77\u65f6\uff0c\u5fc3\u4e2d\u7a81\u7136\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u5982\u82e5\u662f\u4e3a\u6211\u8bbe\u4e0b\u7684\u9677\u9631\uff0c\u8fd9\u5c82\u4e0d\u662f\u592a\u8fc7\u8f7b\u7387\u4e86\uff0c \u4ee5\u60dc\u7389\u7684\u667a\u6167\uff0c\u5fc5\u4f1a\u60f3\u529e\u6cd5\uff0c\u7559\u4e0b\u4e00\u4e9b\u5f81\u50cf\uff0c\u5982\u662f\u4eba\u5df2\u53d7\u5236\uff0c\u6216\u8005\u88ab\u56db\u51e4\u697c\u4e3b\u6740\u6b7b\uff0c\u90a3\u6e14\u8239 \u4e0a\u8bbe\u5b9a\u7684\u673a\u5173\uff0c\u5fc5\u7136\u5341\u5206\u6076\u6bd2\uff0c\u4f3c\u8fd9\u7b49\u8349\u83bd\u7684\u884c\u52a8\uff0c\u5c82\u4e0d\u662f\u6388\u4eba\u4ee5\u53ef\u4e58\u4e4b\u673a\u3002 \u3000\u3000\u518d\u8bf4\uff0c\u90a3\u84d1\u8863\u8001\u4eba\uff0c\u5206\u660e\u5df2\u8fd0\u805a\u4e86\u5168\u8eab\u529f\u529b\uff0c\u5728\u548c\u4e00\u79cd\u529b\u91cf\u5bf9\u6297\uff0c\u6211\u7adf\u7136\u77a7\u4e0d\u51fa\u4e00\u70b9\u5185 \u60c5\uff0c\u4e00\u5b9a\u662f\u770b\u7684\u4e0d\u591f\u4ed4\u7ec6\uff0c\u592a\u5927\u610f\u4e86\uff0c\u5982\u4eca\u76f8\u8ddd\u4e09\u4e08\u5de6\u53f3\uff0c\u6b63\u662f\u76ee\u529b\u53ef\u53ca\u7ec6\u5fae\u4e4b\u5904\uff0c\u5e94\u8be5\u518d \u884c\u770b\u4e2a\u6e05\u695a\u3002 \u3000\u3000\u4e00\u5ff5\u52a8\u5fc3\uff0c\u7acb\u523b\u6253\u6d88\u4e86\u98de\u767b\u6e14\u821f\u7684\u5ff5\u5934\uff0c\u8c03\u6574\u4e00\u4e0b\u5750\u59ff\uff0c\u628a\u5b9d\u5251\u653e\u5978\uff0c\u51dd\u805a\u76ee\u529b\uff0c\u4ed4\u7ec6 \u770b\u53bb\u3002\u540c\u65f6\uff0c\u5212\u52a8\u5c0f\u821f\u7f13\u7f13\u8f6c\u52a8\u65b9\u5411\uff0c\u51c6\u5907\u7ed5\u884c\u6e14\u821f\uff0c\u770b\u4e2a\u660e\u767d\u3002\u5f85\u5c0f\u821f\u8f6c\u5230\u4e1c\u5357\u65b9\u4f4d\uff0c\u5df2 \u548c\u90a3\u84d1\u8863\u9493\u7fc1\u6210\u4e86\u9762\u5bf9\u9762\u7684\u60c5\u52bf\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u6e14\u7fc1\u53cc\u76ee\u4f3c\u7741\u4f3c\u95ed\uff0c\u663e\u7136\u4e5f\u5728\u6697\u4e2d\u6ce8\u610f\u7740\u56db\u5468\u7684\u60c5\u52bf\u3002 \u3000\u3000\u8fd9\u4f7f\u675c\u671b\u6708\u4f5c\u51fa\u7b2c\u4e8c\u4e2a\u5224\u65ad\uff0c\u8001\u6e14\u7fc1\u4f3c\u662f\u5728\u7b49\u5f85\u4ec0\u4e48\u53d8\u5316\uff0c\u5168\u795e\u6212\u5907\uff0c\u4ee5\u4fbf\u4f5c\u77f3\u7834\u5929\u60ca \u7684\u4e00\u51fb\uff0c\u4e0d\u77e5\u7ec3\u7684\u4ec0\u4e48\u529f\u592b\uff1f\u5168\u529b\u84c4\u52bf\u6212\u5907\uff0c\u7adf\u7136\u628a\u4e00\u4e2a\u4eba\u53d8\u7684\u548c\u6728\u96d5\u77f3\u523b\u7684\u4e00\u822c\u3002 \u3000\u3000\u8fd9\u4e0d\u662f\u65e0\u7684\u653e\u77e2\uff0c\u8001\u6e14\u7fc1\u4e00\u5b9a\u53d1\u73b0\u4e86\u4ec0\u4e48\uff0c\u53cc\u65b9\u4f3c\u5728\u6bd4\u8d5b\u8010\u6027\u3002 \u3000\u3000\u8001\u6e14\u7fc1\u8eab\u540e\uff0c\u5c31\u662f\u8231\u95e8\uff0c\u4f46\u8231\u95e8\u865a\u5782\u4e86\u4e00\u4e2a\u5e03\u5e18\uff0c\u6321\u4f4f\u4e86\u89c6\u7ebf\uff0c\u65e0\u6cd5\u770b\u5230\u8231\u4e2d\u666f\u7269\uff0c\u4e5f \u542c\u4e0d\u5230\u4efb\u4f55\u58f0\u606f\u3002 \u3000\u3000\u675c\u671b\u6708\u60f3\u5230\u4e86\u7a97\u6237\uff0c\u5c0f\u821f\u8f6c\u5411\u4e86\u6e14\u8239\u4fa7\u9762\uff0c\u90a3\u91cc\u6709\u4e00\u6247\u7a97\u5b50\uff0c\u4f46\u5374\u88ab\u8231\u5185\u62c9\u4e0a\u7a97\u5e18\u906e\u4f4f \u4e86\u3002 \u3000\u3000\u60dc\u7389\u662f\u5426\u4eba\u5728\u8231\u5185\uff1f\u662f\u88ab\u4eba\u5236\u4f4f\u4e86\uff0c\u6216\u662f\u6ca1\u6765\u8d74\u7ea6\uff1f\u5fc3\u60c5\u4e00\u9635\u6ce2\u52a8\uff0c\u53c8\u751f\u51fa\u4e86\u98de\u767b\u6e14\u8239 \u7684\u51b2\u52a8\u3002 \u3000\u3000\u4f46\u4ed6\u7a76\u7adf\u4e00\u65b9\u540d\u6355\uff0c\u529e\u8fc7\u4e86\u4e0d\u5c11\u53d1\u751f\u5728\u6c5f\u5357\u7684\u5927\u6848\u5b50\uff0c\u548c\u5f88\u591a\u667a\u8c0b\u3001\u6b66\u529f\u9ad8\u7684\u51f6\u624b\u6597\u6cd5\uff0c \u8b66\u60d5\u5fc3\u9ad8\u4eba\u4e00\u7b49\u3002\u957f\u957f\u5401\u4e24\u53e3\u6c14\uff0c\u5e73\u590d\u4e0b\u6ce2\u52a8\u7684\u5fc3\u60c5\uff0c\u5fd6\u9053\uff1a\u4e8b\u4e0d\u5173\u5df1\uff0c\u5173\u5df1\u5219\u4e71\uff0c\u53e4\u4eba\u8bda \u4e0d\u6b3a\u6211\u3002\u7537\u5973\u4e4b\u95f4\uff0c\u4e00\u65e6\u751f\u60c5\uff0c\u7adf\u662f\u5982\u6b64\u7684\u8ff7\u4e71\u4eba\u5fc3\uff0c\u675c\u671b\u6708\u5440\uff01\u675c\u671b\u6708\uff0c\u4f60\u5982\u56e0\u5fc3\u60c5\u8ff7\u4e71\uff0c \u6599\u654c\u4e0d\u660e\uff0c\u6d89\u9669\u53d7\u4f24\uff0c\u8fd8\u6709\u4f55\u989c\u5f53\u6c5f\u5357\u540d\u6355\u3002\u5fc3\u6ce2\u4e00\u5e73\uff0c\u7075\u70b9\u7acb\u523b\u6e05\u660e\uff0c\u76ee\u5149\u51dd\u6ce8\u7a97\u5e18\u4e0a\uff0c \u4ed4\u7ec6\u67e5\u770b\u3002 \u3000\u3000\u8fd9\u4e00\u7ec6\u5fc3\u67e5\u770b\uff0c\u679c\u7136\u53d1\u73b0\u4e00\u6735\u5c0f\u82b1\uff0c\u51e0\u4e4e\u548c\u7a97\u5e18\u76f8\u540c\u7684\u989c\u8272\uff0c\u4e0d\u4f46\u8981\u4ed4\u7ec6\u770b\uff0c\u800c\u4e14\u8981\u9009 \u5bf9\u89d2\u5ea6\uff0c\u65e5\u5149\u7167\u5c04\u5230\u7684\u5730\u65b9\uff0c\u5b83\u7d27\u9760\u7a97\u6237\u7684\u8fb9\u7f18\uff0c\u8fd1\u4e00\u534a\u88ab\u677f\u58c1\u906e\u4f4f\uff0c\u8fd9\u8868\u793a\u4eba\u5728\u8231\u4e2d\uff0c\u8231 \u4e2d\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u5c31\u65e0\u6cd5\u8868\u8fbe\u4e86\u3002 \u3000\u3000\u4e5f\u8bb8\u662f\u60dc\u7389\u975e\u5e38\u4fe1\u4efb\u675c\u671b\u6708\u7684\u667a\u6167\u548c\u7ec6\u5fc3\uff0c\u624d\u7559\u4e0b\u5982\u6b64\u4e00\u4e2a\u9690\u6666\u4e0d\u660e\u7684\u6697\u793a\u3002\u5f53\u7136\uff0c\u66f4 \u91cd\u8981\u7684\u662f\u6015\u4eba\u8bc6\u7834\uff0c\u592a\u660e\u663e\u4e86\uff0c\u5c31\u4e0d\u80fd\u88ab\u79f0\u8c13\u6697\u8bb0\u3002 \u3000\u3000\u675c\u671b\u6708\u975e\u5e38\u4fe1\u4efb\u81ea\u5df1\u7684\u5224\u65ad\uff0c\u7cbe\u795e\u4e5f\u4e3a\u4e4b\u4e00\u632f\uff0c\u7f13\u7f13\u628a\u5c0f\u8239\u79fb\u540e\u4e08\u8bb8\uff0c\u4f30\u7b97\u81ea\u5df1\u8f7b\u529f\uff0c \u80fd\u4e00\u8dc3\u800c\u767b\u4e0a\u6e14\u821f\u7684\u8ddd\u79bb\u505c\u4e86\u4e0b\u6765\u3002\u4ed6\u5df2\u5fc3\u6ce2\u5e73\u9759\uff0c\u6062\u590d\u4e86\u7406\u6027\uff0c\u7b49\u5f85\u7740\u4e8b\u60c5\u660e\u6717\u5316\uff0c\u518d\u51b3 \u5b9a\u5982\u4f55\u884c\u52a8\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u84d1\u8863\u9493\u7fc1\u3001\u675c\u671b\u6708\uff0c\u548c\u4e00\u4e2a\u76ee\u4e0d\u80fd\u89c1\u7684\u795e\u79d8\u4eba\u7269\uff0c\u5728\u6bd4\u8bd5\u8010\u5fc3\u4e86\u3002\u8c01\u80fd\u9009\u62e9\u5230\u6700 \u6709\u5229\u7684\u65f6\u673a\u51fa\u624b\uff0c\u8c01\u7684\u80dc\u7b97\u5c31\u5927\u4e86\u8bb8\u591a\uff0c\u5c24\u5176\u662f\u6b66\u529f\u5728\u4f2f\u3001\u4ef2\u4e4b\u95f4\u7684\u9ad8\u624b\uff0c\u83b7\u53d6\u5230\u81f4\u547d\u4e00\u51fb \u7684\u5148\u624b\uff0c\u5fc5\u987b\u5728\u5fcd\u8010\u4e2d\u627e\u654c\u7834\u7efd\u3002 \u3000\u3000\u5982\u662f\u60dc\u7389\u5df2\u53d7\u4f24\u81f4\u6b7b\uff0c\u8fd9\u8001\u6e14\u7fc1\u4e5f\u7528\u4e0d\u7740\u5728\u6b64\u71ac\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u60c5\u666f\u6700\u574f\u7684\u7ed3\u679c\u662f\u60dc\u7389\u53d7\u4e86\u4f24\uff0c\u5728\u8231\u4e2d\u517b\u606f\uff0c\u8001\u6e14\u7fc1\u72ec\u62d2\u5f3a\u654c\uff0c\u9009\u62e9\u4e86\u6c34\u57df\u4e2d\u4e00\u51b3\u751f \u6b7b\u3002\u5f53\u7136\uff0c\u5728\u8fd9\u91cc\u4e5f\u53ef\u80fd\u5728\u7b49\u4ed6\u675c\u671b\u6708\u8d76\u6765\u9a70\u63f4\u3002 \u3000\u3000\u6b64\u65f6\u6b64\u60c5\uff0c\u675c\u671b\u6708\u6709\u4e24\u4e2a\u51b3\u5b9a\uff0c\u4e00\u4e2a\u662f\u7b49\u4e0b\u53bb\uff0c\u7b49\u5230\u90a3\u9690\u533f\u7684\u795e\u79d8\u4eba\u7269\u51fa\u73b0\uff0c\u5148\u6321\u8001\u6e14 \u7fc1\u8fd9\u5168\u529b\u84c4\u52bf\u7684\u4e00\u51fb\u3002\u518d\u9009\u62e9\u51fa\u624b\u65f6\u673a\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u662f\u5148\u63a5\u8fd1\u6e14\u821f\uff0c\u770b\u6e05\u695a\u8231\u5185\u5f62\u52bf\uff0c\u8bc1\u5b9e\u4e86\u60dc\u7389\u786e\u5728\u8231\u4e2d\uff0c\u51c6\u5907\u5148\u548c\u60dc\u7389\u4f1a\u5408\uff0c\u518d \u4f5c\u6253\u7b97\u3002 \u3000\u3000\u7b49\u4e0b\u53bb\uff0c\u8fd8\u4e0d\u77e5\u8981\u7b49\u5019\u591a\u4e45\u65f6\u95f4\uff0c\u60dc\u7389\u5982\u662f\u53d7\u4e86\u4f24\uff0c\u8fd8\u80fd\u652f\u6491\u591a\u4e45\u5462\uff1f\u5192\u9669\u8e81\u8fdb\uff0c\u53ef\u80fd \u5f15\u8d77\u8bef\u4f1a\uff0c\u5148\u8981\u62b5\u6321\u8001\u6e14\u7fc1\u84c4\u52bf\u826f\u4e45\u7684\u5168\u529b\u4e00\u51fb\uff0c\u66f4\u53ef\u6015\u7684\u662f\u8fd9\u53ef\u80fd\u7ed9\u4e86\u654c\u4eba\u7b49\u5f85\u7684\u51fa\u624b\u673a \u4f1a\uff0c\u4e5f\u53ef\u80fd\u56e0\u800c\u5bb3\u4e86\u60dc\u7389\u7684\u6027\u547d\u3002 \u3000\u3000\u4efb\u4f60\u675c\u671b\u6708\u673a\u667a\u7edd\u4f26\uff0c\u4f46\u5728\u5fc3\u60ac\u7231\u4fa3\u7684\u60c5\u51b5\u4e0b\uff0c\u7adf\u7136\u4e0d\u77e5\u8be5\u5982\u4f55\u4e0b\u624b\u4e86\u3002\u4f46\u675c\u671b\u6708\u8fd8\u662f \u7f13\u7f13\u5212\u52a8\u5c0f\u821f\uff0c\u5411\u6e14\u8239\u63a5\u8fd1\uff0c\u4e00\u9762\u65bd\u5c55\u201c\u5343\u91cc\u4f20\u97f3\u201d\uff0c\u8bf4\u9053\uff1a\u201c\u6e14\u7fc1\u524d\u8f88\uff0c\u6211\u662f\u675c\u671b\u6708\uff0c\u548c \u60dc\u7389\u6709\u7ea6\uff0c\u4f46\u8239\u4e0a\u7684\u8231\u95e8\u7a97\u6237\uff0c\u90fd\u88ab\u5782\u5e18\u63a9\u906e\uff0c\u89c1\u4e0d\u5230\u8239\u8231\u5185\u7684\u666f\u7269\uff0c\u6211\u8981\u67e5\u770b\u4e00\u4e0b\u8231\u5185\u60c5 \u5f62\uff0c\u5fc5\u987b\u63a5\u8fd1\u6e14\u8239\uff0c\u5e0c\u671b\u8001\u524d\u8f88\u4e0d\u8981\u8bef\u4f1a\uff0c\u8001\u524d\u8f88\u5982\u80fd\u4f20\u8a00\u89e3\u60d1\uff0c\u6d88\u6211\u7591\u8651\uff0c\u665a\u8f88\u81ea\u662f\u9075\u547d \u884c\u4e8b\uff0c\u4e07\u4e00\u4e0d\u80fd\u5206\u5fc3\u4ed6\u987e\uff0c\u53ea\u8981\u7ed9\u6211\u4e00\u70b9\u793a\u610f\uff0c\u665a\u8f88\u4e5f\u4e00\u5b9a\u9075\u884c\u3002\u201d\u8bf4\u5b8c\u8bdd\uff0c\u5168\u795e\u51dd\u6ce8\u5728\u8001 \u6e14\u7fc1\u7684\u8eab\u4e0a\uff0c\u5e0c\u671b\u80fd\u770b\u51fa\u4ed6\u7684\u56de\u5e94\u6697\u793a\u3002 \u3000\u3000\u5f53\u7136\uff0c\u675c\u671b\u6708\u4e5f\u7559\u5fc3\u7740\u56db\u5468\u7684\u60c5\u52bf\u53d8\u5316\u3002 \u3000\u3000\u53d1\u89c9\u4e86\u4e24\u53ea\u5c0f\u821f\uff0c\u7a81\u7136\u8f6c\u5934\u5411\u6e14\u821f\u9a70\u6765\uff0c\u5c0f\u821f\u4e0a\u642d\u4e86\u906e\u9633\u4f1e\uff0c\u4f46\u675c\u671b\u6708\u4ecd\u770b\u51fa\u4e86\u6bcf\u53ea\u5c0f \u821f\u4e0a\u6709\u4e24\u4e2a\u4eba\uff0c\u4e00\u4eba\u64cd\u821f\uff0c\u4e00\u4eba\u5750\u5728\u8239\u5934\uff0c\u62ab\u4e86\u4e00\u4ef6\u5927\u6597\u84ec\uff0c\u8fd9\u5c31\u65e0\u6cd5\u770b\u6e05\u4ed6\u7684\u9762\u76ee\uff0c\u4e5f\u770b \u4e0d\u89c1\u4ed6\u624b\u4e2d\u662f\u5426\u62ff\u6709\u5175\u5203\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u624b\u90fd\u5728\u6597\u84ec\u63a9\u906e\u4e4b\u4e0b\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u8001\u6e14\u7fc1\u4e5f\u6709\u53cd\u5e94\uff0c\u90a3\u5fae\u95ed\u7684\u53cc\u76ee\u7a81\u7136\u5f20\u5f00\uff0c\u4e00\u5f00\u4e00\u95ed\uff0c\u8fde\u7eed\u4e24\u6b21\uff0c\u624d\u53c8\u6062\u590d\u65e7 \u89c2\u3002\u675c\u671b\u6708\u7a81\u7136\u5212\u52a8\u5c0f\u821f\uff0c\u5411\u524d\u9a70\u53bb\uff0c\u51c6\u5907\u5148\u62e6\u4f4f\u51b2\u5411\u6e14\u8239\u7684\u96e8\u53ea\u5c0f\u821f\u3002 \u3000\u3000\u4e09\u8258\u5c0f\u821f\uff0c\u90fd\u52a0\u5feb\u4e86\u901f\u5ea6\u3002 \u3000\u3000\u4f46\u4e09\u4e2a\u9a7e\u821f\u4eba\u7684\u6280\u672f\uff0c\u90fd\u4e0d\u592a\u597d\uff0c\u5c0f\u821f\u901f\u5ea6\u867d\u5feb\uff0c\u4f46\u5374\u884c\u5411\u4e0d\u7a33\uff0c\u6e85\u98de\u8d77\u6781\u5927\u7684\u6d6a\u82b1\uff0c \u5168\u51ed\u85c9\u5f3a\u52b2\u7684\u8155\u529b\uff0c\u6467\u52a8\u884c\u821f\uff0c\u90a3\u662f\u4e8b\u500d\u529f\u534a\u7684\u9a70\u821f\u65b9\u6cd5\uff0c\u4e09\u4e2a\u4eba\u90fd\u662f\u5916\u884c\u3002 \u3000\u3000\u4f46\u771f\u6b63\u7684\u88ad\u51fb\uff0c\u5374\u6765\u81ea\u6c34\u4e2d\u3002\u4e00\u9053\u6c34\u7bad\uff0c\u98de\u8d77\u4e24\u4e08\u591a\u9ad8\uff0c\u88f9\u7740\u4e00\u6761\u4eba\u5f71\uff0c\u75be\u5982\u6d41\u661f\u822c\uff0c \u653b\u5411\u90a3\u76d8\u5750\u5728\u6e14\u8239\u5934\u4e0a\u7684\u84d1\u8863\u8001\u7fc1\uff0c\u4e00\u9053\u51db\u51db\u5bd2\u8292\uff0c\u5df2\u7a81\u51fa\u4eba\u5f71\u6c34\u7bad\u3002\u8fd9\u662f\u4eba\u5200\u5408\u4e00\u7684\u5389\u70c8 \u653b\u52bf\uff0c\u6cf0\u5c71\u538b\u9876\u822c\u4e00\u51fb\u53d6\u547d\u3002 \u3000\u3000\u90a3\u76d8\u5750\u8239\u5934\u8001\u6e14\u7fc1\uff0c\u4e5f\u52a8\u4e86\uff0c\u4e00\u7529\u9c7c\u7aff\uff0c\u5e26\u7740\u94a9\u7ebf\uff0c\u5411\u6c34\u7bad\u8fce\u4e86\u4e0a\u53bb\uff0c\u540c\u65f6\uff0c\u4eba\u4e5f\u817e\u7a7a \u800c\u8d77\u3002 \u3000\u3000\u675c\u671b\u6708\u5927\u559d\u4e00\u58f0\uff1a\u201c\u9f20\u8f88\u6562\u5c14\uff01\u201d\u4eba\u7531\u5c0f\u821f\u4e0a\u98de\u8d77\uff0c\u4e03\u661f\u5b9d\u5251\u540c\u65f6\u51fa\u9798\uff0c\u201c\u516b\u6b65\u767b\u7a7a\u201d \u98de\u5411\u6e14\u821f\uff0c\u79bb\u821f\u65f6\u811a\u4e0b\u52a0\u529b\uff0c\u5c0f\u821f\u75be\u5411\u53e6\u5916\u4e24\u8258\u5c0f\u821f\u4e0a\u649e\u53bb\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\u662f\uff0c\u9a70\u6765\u5c0f\u821f\u4e0a\uff0c\u4e5f\u5404\u6709\u4e00\u4eba\uff0c\u7529\u8131\u6597\u84ec\uff0c\u5404\u6267\u4e00\u628a\u539a\u80cc\u5927\u780d\u5200\uff0c\u98de\u8dc3\u800c\u8d77\uff0c\u540c \u65f6\u62e6\u622a\u675c\u671b\u6708\u3002 \u3000\u3000\u675c\u671b\u6708\u6012\u706b\u6ee1\u80f8\uff0c\u5251\u52bf\u5de6\u53f3\u6447\u6446\uff0c\u5c01\u5f00\u4e86\u4e24\u67c4\u5927\u780d\u5200\uff0c\u91d1\u94c1\u4ea4\u9e23\u58f0\u4e2d\uff0c\u4e09\u4e2a\u4eba\u540c\u65f6\u5411\u6e56 \u6c34\u4e2d\u843d\u53bb\u3002 \u3000\u3000\u8fd9\u4e5f\u60df\u662f\u4e24\u4e2a\u51fa\u5200\u4eba\u7684\u7528\u5fc3\uff0c\u4ed6\u4eec\u672a\u5b58\u5962\u671b\uff0c\u80fd\u51fb\u4f24\u654c\u4eba\uff0c\u53ea\u8981\u80fd\u5728\u4ea4\u624b\u4e00\u62db\u540e\uff0c\u654c\u6211 \u540c\u65f6\u843d\u5165\u6e56\u6c34\u4e2d\uff0c\u963b\u4ed6\u4e00\u963b\u5c31\u884c\u3002\u6240\u4ee5\uff0c\u51fa\u5200\u7528\u529b\u5f88\u5927\uff0c\u7a7a\u4e2d\u4ea4\u624b\uff0c\u4e0d\u80fd\u6362\u6c14\uff0c\u4e00\u53e3\u771f\u6c14\u618b \u4e0d\u4f4f\uff0c\u5c31\u8981\u843d\u5165\u6e56\u6c34\u4e4b\u4e2d\u3002 \u3000\u3000\u675c\u671b\u6708\u4e00\u51fb\u8feb\u4f7f\u4e24\u4e2a\u51fa\u5200\u4eba\u843d\u6c34\uff0c\u81ea\u5df1\u786e\u4e5f\u771f\u6c14\u5df2\u6d4a\uff0c\u8eab\u5b50\u5411\u4e0b\u5760\u53bb\u3002 \u3000\u3000\u4f46\u4ed6\u6362\u6c14\u6781\u5feb\uff0c\u811a\u7740\u6c34\u9762\uff0c\u7a0d\u7a0d\u4e00\u501f\u529b\uff0c\u4eba\u5df2\u4e8c\u6b21\u817e\u7a7a\u800c\u8d77\uff0c\u98de\u843d\u5728\u8239\u5934\u7532\u677f\u4e0a\u3002 \u3000\u3000\u53ef\u60dc\uff0c\u4ecd\u662f\u665a\u4e86\u4e00\u6b65\uff0c\u5927\u6068\u5df2\u6210\uff0c\u4e00\u628a\u950b\u5229\u72ed\u957f\u7684\u7f05\u5200\uff0c\u5df2\u7531\u8001\u6e14\u7fc1\u524d\u80f8\u6d1e\u7a7f\u800c\u8fc7\uff0c\u90a3 \u662f\u81f4\u547d\u7684\u4e00\u51fb\uff0c\u8001\u6e14\u7fc1\u6c14\u606f\u5df2\u7edd\uff0c\u5c38\u4f53\u7684\u8138\u4e0a\uff0c\u6ee1\u5e03\u9ed1\u6c14\uff0c\u663e\u662f\u65e9\u5df2\u4e2d\u4e86\u5947\u70c8\u4e4b\u6bd2\u3002\u4ed6\u76d8\u5750 \u8c03\u606f\uff0c\u4e0d\u8a00\u4e0d\u52a8\uff0c\u8fd0\u805a\u4e86\u5168\u8eab\u529f\u529b\uff0c\u5c31\u662f\u8981\u548c\u5267\u6bd2\u5bf9\u6297\uff0c\u6291\u5236\u5b83\u4e0d\u8ba9\u53d1\u4f5c\uff0c\u4e5f\u7b49\u5f85\u5f3a\u654c\u6765\u88ad\uff0c \u4f5c\u6700\u540e\u2014\u6b21\u8fd8\u51fb\u3002 \u3000\u3000\u675c\u671b\u6708\u5f88\u81ea\u548e\uff0c\u5982\u80fd\u5f53\u673a\u7acb\u65ad\uff0c\u4e0d\u8981\u77bb\u524d\u987e\u540e\u5730\u5f98\u5f8a\uff0c\u65e9\u767b\u6e14\u8239\uff0c\u867d\u672a\u5fc5\u80fd\u6551\u4e86\u8001\u6e14\u7fc1 \u7684\u6027\u547d\uff0c\u4f46\u81f3\u5c11\u53ef\u5ef6\u957f\u4ed6\u6b7b\u4ea1\u65f6\u95f4\u3002 \u3000\u3000\u4f46\u8001\u6e14\u7fc1\u4ea6\u672a\u767d\u6b7b\uff0c\u624b\u4e2d\u9c7c\u94a9\u7684\u4e1d\u7ebf\uff0c\u7f20\u7ed5\u5728\u654c\u4eba\u7684\u8eab\u4e0a\uff0c\u9c7c\u94a9\u4e5f\u65e9\u5d4c\u5165\u654c\u4eba\u7684\u9762\u988a\u4e4b \u4e2d\uff0c\u654c\u4eba\u4e5f\u5012\u5728\u4e86\u8239\u5934\u4e4b\u4e0a\uff0c\u662f\u4e2a\u4e09\u56db\u5341\u5c81\u7684\u4e2d\u5e74\u4eba\uff0c\u5374\u8ba4\u4e0d\u51fa\u662f\u4f55\u8bb8\u4eba\u7269\u3002 \u3000\u3000\u675c\u671b\u6708\u6311\u5f00\u654c\u4eba\u7684\u6cb9\u7ef8\u6c34\u8863\u4e0a\u8eab\uff0c\u53d1\u89c9\u4ed6\u524d\u80f8\u5904\u4e5f\u4e2d\u4e86\u4e00\u638c\uff0c\u53e3\u4e2d\u6ee1\u542b\u7740\u7d2b\u8272\u7684\u8840\u5757\uff0c \u4f3c\u662f\u5185\u810f\u4e94\u8151\uff0c\u90fd\u5df2\u88ab\u638c\u529b\u9707\u788e\u4e86\uff0c\u4e5f\u65e9\u5df2\u6c14\u7edd\u800c\u901d\u3002\u8001\u6e14\u7fc1\u5b9e\u662f\u529f\u529b\u6df1\u539a\u7684\u9ad8\u624b\uff0c\u5982\u975e\u65e9 \u4e2d\u5267\u6bd2\uff0c\u8fd9\u640f\u547d\u7684\u4e00\u51fb\uff0c\u7edd\u4e0d\u4f1a\u540c\u5f52\u4e8e\u5c3d\u3002 \u3000\u3000\u62ac\u5934\u770b\u5411\u6e56\u9762\uff0c\u4e09\u8258\u5c0f\u821f\u5e76\u672a\u76f8\u649e\uff0c\u4e24\u8258\u6765\u821f\uff0c\u5df2\u65e9\u9003\u8d70\uff0c\u5760\u6e56\u4e24\u4e2a\u5200\u624b\uff0c\u4f3c\u4e5f\u88ab\u4eba\u6551 \u8d70\uff0c\u4e0d\u89c1\u8e2a\u8ff9\u3002 \u3000\u3000\u675c\u671b\u6708\u5410\u53e3\u957f\u6c14\uff0c\u4e03\u661f\u5b9d\u5251\u4e00\u6325\uff0c\u6311\u5f00\u4e86\u8231\u95e8\u5782\u5e18\uff0c\u773c\u95f4\u5c31\u770b\u6e05\u8231\u4e2d\u666f\u7269\uff0c\u60dc\u7389\u4e00\u8eab\u84dd \u5e03\u8863\u670d\uff0c\u62ab\u5782\u7684\u957f\u53d1\uff0c\u624e\u6210\u4e86\u4e24\u4e2a\u8fab\u5b50\uff0c\u4eba\u5374\u9760\u5728\u4e00\u5f20\u6728\u6905\u4e0a\uff0c\u4f3c\u662f\u65e9\u5df2\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u675c\u671b\u6708\u51b2\u5165\u8231\u5185\uff0c\u76ee\u5149\u8fc5\u901f\u626b\u5c04\u8239\u8231\u4e00\u5468\uff0c\u5df2\u786e\u5b9a\u518d\u65e0\u4ed6\u4eba\u3002\u52ff\u602a\u8231\u4e2d\u5168\u65e0\u52a8\u9759\uff0c\u6655\u8fc7 \u53bb\u7684\u4eba\uff0c\u81ea\u662f\u65e0\u6cd5\u53cd\u5e94\u3002 \u3000\u3000\u7f13\u6b65\u884c\u8fd1\u60dc\u7389\uff0c\u4f38\u624b\u62e5\u5165\u6000\u4e2d\uff0c\u53d1\u89c9\u4e86\u60dc\u7389\u7684\u8138\u4e0a\uff0c\u4e5f\u5e03\u4e86\u8499\u8499\u9ed1\u6c14\uff0c\u8fd9\u662f\u660e\u663e\u7684\u4e2d\u6bd2 \u4e4b\u5f81\u3002 \u3000\u3000\u675c\u671b\u6708\u5f88\u4ed4\u7ec6\u5730\u5728\u60dc\u7389\u8eab\u4e0a\u67e5\u770b\uff0c\u53d1\u89c9\u5979\u5de6\u80a9\u4e0a\u4e2d\u4e86\u4e00\u679a\u6bd2\u9488\uff0c\u9760\u8fd1\u6728\u6905\u8fb9\u7684\u5c0f\u8336\u51e0\u4e0a\uff0c \u8fd8\u653e\u7740\u4e24\u679a\u4e24\u5bf8\u957f\u77ed\u7684\u94a2\u9488\uff0c\u6cdb\u8d77\u4e00\u7247\u84dd\u6c6a\u6c6a\u7684\u989c\u8272\uff0c\u663e\u662f\u5947\u6bd2\u6dec\u70bc\u4e4b\u7269\u3002 \u3000\u3000\u8fd9\u5c0f\u5de7\u7684\u6697\u5668\uff0c\u672c\u65e0\u4f24\u4eba\u7684\u80fd\u529b\uff0c\u6076\u6bd2\u5904\u5168\u5728\u6dec\u6bd2\u4e4b\u4e0a\uff0c\u624d\u4f7f\u6c5f\u6e56\u4eba\u7269\u95fb\u4e4b\u53d8\u8272\u3002 \u3000\u3000\u60dc\u7389\u8eab\u4e0a\u6709\u4e24\u5904\u4e3b\u8981\u7a74\u9053\uff0c\u4e5f\u5df2\u88ab\u4eba\u5c01\u4f4f\uff0c\u4f3c\u662f\u8981\u5ef6\u7f13\u5267\u6bd2\u653b\u5fc3\u7684\u65f6\u95f4\u3002\u7167\u60c5\u5f62\u63a8\u65ad\u7ecf \u8fc7\uff0c\u662f\u60dc\u7389\u5148\u4e2d\u6bd2\u9488\uff0c\u8001\u6e14\u7fc1\u5c01\u4f4f\u4e86\u5979\u4e24\u5904\u7a74\u9053\u540e\uff0c\u4e5f\u88ab\u6bd2\u9488\u6240\u4f24\uff0c\u5df2\u77e5\u65e0\u6cd5\u8fdc\u8d70\u6c42\u6cbb\u6bd2\u4f24\uff0c \u5c31\u628a\u6e14\u8239\u9a70\u6765\u89c2\u9c7c\u4ead\u7684\u9644\u8fd1\uff0c\u7b49\u5019\u675c\u671b\u6708\u3002\u7136\u540e\uff0c\u76d8\u5750\u8239\u5934\u8c03\u606f\uff0c\u4e00\u9762\u6291\u5236\u6bd2\u6027\u53d1\u4f5c\uff0c\u4e00\u9762 \u51c6\u5907\u62da\u5c3d\u6700\u540e\u4e00\u53e3\u5143\u6c14\uff0c\u548c\u654c\u4eba\u540c\u5f52\u4e8e\u5c3d\uff0c\u662f\u4f55\u7b49\u65e0\u5948\u7684\u5fc3\u60c5\u3002 \u3000\u3000\u60f3\u5230\u6b64\u5904\uff0c\u675c\u671b\u6708\u61ca\u607c\u6781\u4e86\uff0c\u4e5f\u6094\u6068\u6781\u4e86\uff0c\u6068\u4e0d\u5f97\u6253\u81ea\u5df1\u4e24\u4e2a\u8033\u5149\uff0c\u4e00\u5411\u4f5c\u4e8b\u679c\u6562\u51b3\u65ad\uff0c \u4eca\u65e5\u7adf\u5c0f\u5fc3\u8fc7\u5ea6\uff0c\u6765\u7684\u5f88\u65e9\uff0c\u5374\u4e34\u573a\u65f6\u72b9\u8c6b\u4e0d\u51b3\uff0c\u5982\u80fd\u65e9\u4e9b\u767b\u4e0a\u6e14\u821f\uff0c\u654c\u4eba\u8fd8\u672a\u5b8c\u6210\u5e03\u7f72\uff0c \u5148\u628a\u4e24\u4eba\u9001\u533b\uff0c\u5982\u4f55\u4f1a\u94f8\u6b64\u5927\u9519\u3002 \u3000\u3000\u4e0d\u80fd\u518d\u72b9\u8c6b\u4e86\uff0c\u5148\u6551\u60dc\u7389\u8981\u7d27\u3002 \u3000\u3000\u4e00\u5ff5\u52a8\u5fc3\uff0c\u91cd\u767b\u7532\u677f\uff0c\u4e58\u5750\u800c\u6765\u7684\u5c0f\u821f\uff0c\u8fd8\u5728\u89c2\u9c7c\u4ead\u9644\u8fd1\u6f02\u8361\u3002 \u3000\u3000\u521a\u624d\u7684\u640f\u6740\u666f\u8c61\uff0c\u867d\u5341\u5206\u5389\u70c8\uff0c\u4f46\u65f6\u95f4\u5f88\u77ed\uff0c\u770b\u5230\u7684\u4eba\u4e0d\u591a\uff0c\u51e0\u4e2a\u770b\u5230\u7684\u4eba\uff0c\u4e5f\u53d7\u5230\u60ca \u5413\uff0c\u79bb\u6b64\u800c\u53bb\u3002 \u3000\u3000\u675c\u671b\u6708\u4e5f\u4e0d\u987e\u60ca\u4e16\u9a87\u4fd7\uff0c\u98de\u8eab\u800c\u8d77\uff0c\u8fde\u7740\u4e24\u4e2a\u7a7a\u4e2d\u7ffb\uff0c\u98de\u51fa\u4e86\u4e03\u516b\u4e08\u8fdc\uff0c\u4f46\u5df2\u65e0\u6cd5\u51c6\u786e \u5730\u843d\u5728\u5c0f\u821f\u4e0a\u4e86\uff0c\u6574\u4e2a\u5760\u5165\u6e56\u4e2d\u3002\u4f46\u8ddd\u5c0f\u821f\uff0c\u53ea\u4e0d\u8fc7\u56db\u4e94\u5c3a\u8fdc\uff0c\u7531\u6c34\u4e2d\u6d6e\u8d77\uff0c\u4e00\u4e2a\u5411\u524d\u51b2\uff0c \u4e0a\u4e86\u5c0f\u821f\uff0c\u9a70\u8fd1\u6e14\u8239\uff0c\u4e09\u4e0d\u7ba1\u62b1\u8d77\u60dc\u7389\uff0c\u62ff\u4e86\u5b9d\u5251\uff0c\u98de\u843d\u5c0f\u821f\uff0c\u8fd0\u6868\u5411\u5cb8\u8fb9\u51b2\u53bb\u3002 \u3000\u3000\u675c\u671b\u6708\u767b\u9646\u5730\u65b9\uff0c\u662f\u5904\u9760\u8fd1\u5c71\u5761\u7684\u6240\u5728\uff0c\u8fd9\u91cc\u4e0d\u89c1\u6e38\u4eba\uff0c\u6b63\u597d\u53ef\u4ee5\u653e\u5f00\u811a\u7a0b\uff0c\u5feb\u901f\u5954\u884c\u3002 \u7ed5\u8fc7\u4e00\u4e2a\u5c0f\u5f2f\uff0c\u5ffd\u95fb\u5f13\u5f26\u58f0\u52a8\uff0c\u4e24\u53ea\u957f\u7bad\uff0c\u7834\u7a7a\u5c04\u6765\u3002 \u3000\u3000\u675c\u671b\u6708\u4e00\u4e2a\u95ea\u8eab\u8f6c\uff0c\u907f\u5165\u4e00\u682a\u5927\u6811\u540e\u9762\u3002\u7bad\u662f\u907f\u5f00\u4e86\uff0c\u4f46\u5374\u53d1\u89c9\u8d70\u9519\u4e86\u65b9\u4f4d\uff0c\u8981\u56de\u626c\u5dde \u57ce\uff0c\u8fd8\u8981\u6e21\u4e00\u6b21\u7626\u897f\u6e56\u3002\u5c31\u5728\u6b64\u523b\uff0c\u4e09\u4e2a\u5b88\u7740\u7070\u8863\u7684\u5927\u6c49\u7a81\u7136\u51fa\u73b0\uff0c\u4e5f\u7f13\u7f13\u903c\u4e86\u8fc7\u6765\uff0c\u624b\u4e2d \u4e5f\u4eae\u51fa\u5175\u5203\uff0c\u662f\u4e24\u628a\u9b3c\u5934\u5200\u548c\u4e00\u628a\u5434\u94a9\u5251\u3002 \u3000\u3000\u56db\u4e94\u4e08\u5916\uff0c\u4e00\u5904\u7a0d\u9ad8\u571f\u5cad\u4e0a\uff0c\u8fd8\u6709\u56db\u4e2a\u7070\u8863\u5927\u6c49\uff0c\u6b63\u633d\u5f13\u642d\u7bad\uff0c\u5f85\u673a\u5c04\u51fa\u3002\u675c\u671b\u6708\u662f\u4e45 \u7ecf\u5927\u654c\u7684\u4eba\u7269\uff0c\u9047\u4e0a\u62e6\u51fb\u4e4b\u540e\uff0c\u4eba\u53cd\u800c\u9547\u9759\u4e86\u4e0b\u6765\uff0c\u6296\u6296\u8863\u670d\u4e0a\u79ef\u6c34\uff0c\u9009\u4e00\u5904\u8349\u4e1b\uff0c\u653e\u597d\u60dc \u7389\uff0c\u8fce\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4eca\u5929\u7684\u8868\u73b0\u5f88\u53ef\u7b11\uff0c\u4e0a\u5348\u592a\u6c89\u7740\uff0c\u8bef\u4e86\u8001\u6e14\u7fc1\u548c\u60dc\u7389\u7684\u6551\u6cbb\u65f6\u95f4\uff0c\u6b64\u523b\u53c8\u592a\u6025\u8e81\uff0c\u6025\u8e81 \u7684\u614c\u4e0d\u62e9\u8def\uff0c\u8dd1\u9519\u4e86\u65b9\u5411\u3002\u6df1\u6df1\u547c\u5438\u4e24\u53e3\u6c14\uff0c\u63d0\u632f\u4e86\u4e00\u4e0b\u7cbe\u795e\uff0c\u8fce\u5411\u4e09\u4e2a\u5e76\u6392\u800c\u6765\u7684\u654c\u4eba\u3002 \u3000\u3000\u675c\u671b\u6708\u6ca1\u6709\u62d4\u51fa\u60dc\u7389\u80a9\u4e0a\u7684\u6bd2\u9488\uff0c\u4e5f\u672a\u89e3\u5979\u88ab\u70b9\u7684\u7a74\u9053\uff0c\u8fd9\u4e5f\u597d\uff0c\u4e0d\u8bba\u7ecf\u5386\u4e9b\u4ec0\u4e48\u51f6\u9669\uff0c \u60dc\u7389\u90fd\u5728\u6655\u8ff7\u4e2d\uff0c\u4e0d\u4f1a\u53d7\u5230\u6270\u4e71\u2026\u2026 \u3000\u3000\u6025\u6868\u5212\u6ce2\u4e4b\u58f0\uff0c\u4f20\u5165\u8033\u9645\uff0c\u4e24\u8258\u5c0f\u821f\uff0c\u9760\u4e0a\u4e86\u5cb8\uff0c\u4e0a\u6765\u56db\u4e2a\u7070\u8863\u4eba\uff0c\u624b\u63d0\u5175\u5203\u56f4\u4e0a\u6765\uff0c \u4e24\u4e2a\u7528\u7684\u662f\u53cc\u9762\u5175\u5203\u65e5\u6708\u9752\u94dc\u8f6e\uff0c\u53e6\u52a0\u4e24\u628a\u9b3c\u5934\u5200\u3002 \u3000\u3000\u8fd9\u7fa4\u4eba\u540c\u7740\u7070\u8863\uff0c\u4e5f\u6709\u4e00\u5927\u534a\u5175\u5203\u96f7\u540c\uff0c\u663e\u7136\u662f\u6765\u81ea\u4e00\u4e2a\u7ec4\u5408\u7684\u6740\u624b\u3002 \u3000\u3000\u96be\u9053\u662f\u8840\u624b\u65b9\u8f6e\u7684\u5c5e\u4e0b\uff0c\u65b9\u8f6e\u6740\u4eba\uff0c\u8bb2\u6c42\u5e03\u5c40\u7cbe\u5bc6\uff0c\u591a\u65b9\u914d\u5408\uff0c\u4e00\u5411\u662f\u591a\u4eba\u5408\u4f5c\u51fa\u624b\u3002 \u8fd9\u756a\u5835\u6740\uff0c\u8fd8\u51fa\u52a8\u4e86\u56db\u4e2a\u5f13\u7bad\u624b\uff0c\u5b8c\u5168\u662f\u65b9\u8f6e\u7684\u6740\u4eba\u7684\u65b9\u6cd5\u3002 \u3000\u3000\u675c\u671b\u6708\u4e0d\u754f\u60e7\u654c\u4eba\u4f17\u591a\uff0c\u5374\u62c5\u5fc3\u4e00\u65e6\u6076\u6218\u5f00\u59cb\uff0c\u65e0\u6cd5\u4fdd\u62a4\u60dc\u7389\u7684\u5b89\u5168\uff0c\u5fc3\u4e2d\u53c8\u6709\u70b9\u7126\u8651 \u4e86\uff0c\u4f46\u4e5f\u6fc0\u52a8\u4e86\u4ed6\u5f3a\u70c8\u7684\u6740\u673a\uff0c\u4e03\u661f\u5b9d\u5251\u51fa\u9798\uff0c\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u4e03\u4e2a\u4eba\u53cd\u800c\u505c\u4e86\u4e0b\u6765\uff0c\u5e03\u4e86\u4e00\u4e2a\u534a\u6708\u5f62\u7684\u5408\u56f4\u4e4b\u52bf\uff0c\u4e00\u4e2a\u4e2a\u9762\u76ee\u51b7\u971c\uff0c\u6a2a\u5200\u5f85\u654c\uff0c\u4f46\u5374 \u65e0\u4eba\u5f00\u53e3\u8bf4\u8bdd\u3002 \u3000\u3000\u884c\u8fd1\u654c\u4eba\u4e09\u4e08\u5de6\u53f3\u65f6\uff0c\u675c\u671b\u6708\u505c\u4e86\u4e0b\u6765\uff0c\u4e03\u661f\u5251\u4e00\u6307\u5c45\u4e2d\u800c\u7acb\u3001\u624b\u6267\u9752\u94dc\u8f6e\u7684\u5927\u6c49\uff0c\u9053\uff1a \u201c\u4f60\u4eec\u662f\u6740\u624b\u65b9\u8f6e\u624b\u4e0b\u7684\u6740\u624b\uff1f\u201d \u3000\u3000\u4e03\u4e2a\u4eba\u4e0d\u70b9\u5934\uff0c\u4e5f\u4e0d\u6447\u5934\uff0c\u4e5f\u65e0\u4eba\u5f00\u53e3\u7b54\u8bdd\u3002 \u3000\u3000\u201c\u4e5f\u597d\uff01\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5c31\u8981\u6e85\u8840\u62da\u547d\u4e86\uff0c\u591a\u8a00\u4f55\u76ca\uff1f\u8bf8\u4f4d\u53ef\u4ee5\u8f66\u8f6e\u5927\u6218\uff0c\u4e5f\u53ef\u4ee5\u4e00\u62e5 \u800c\u4e0a\u3002\u201d \u3000\u3000\u4e03\u4e2a\u4eba\u50cf\u4e03\u6839\u6728\u68d2\uff0c\u4ecd\u65e0\u4e00\u4eba\u5f00\u53e3\u3002 \u3000\u3000\u675c\u671b\u6708\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u51e0\u4f4d\u6c89\u7740\u7684\u5f88\u5566\uff0c\u65e2\u7136\u4e0d\u80af\u5148\u51fa\u624b\uff0c\u6211\u5c31\u5360\u5148\u673a\u4e86\u3002\u201d \u3000\u3000\u8bdd\u843d\u53e3\uff0c\u5251\u5316\u4e00\u9053\u51b7\u8679\uff0c\u653b\u5411\u5c45\u4e2d\u800c\u7acb\u3001\u624b\u6267\u53cc\u8f6e\u7684\u654c\u4eba\u3002 \u3000\u3000\u9752\u94dc\u65e5\u6708\u8f6e\u548c\u5434\u94a9\u5251\uff0c\u90fd\u6709\u9501\u62ff\u5bf9\u65b9\u5175\u5203\u7684\u4f5c\u7528\uff0c\u6ca1\u6709\u76f8\u5f53\u7684\u6b66\u529f\uff0c\u4e5f\u4e0d\u9002\u5408\u7528\u8fd9\u79cd\u5175 \u5203\uff0c\u5148\u6740\u4f24\u4e86\u8fd9\u4e09\u4e2a\u4eba\uff0c\u4f59\u4e0b\u7684\u5c31\u597d\u89e3\u51b3\u4e86\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u76f4\u6302\u8651\u7740\u60dc\u7389\u7684\u5b89\u5371\uff0c\u51fa\u5251\u5c31\u51b7\u9177\u65e0\u60c5\u4e86\uff0c\u5251\u52bf\u5982\u96f7\uff0c\u5c45\u4e2d\u7684\u7070\u8863\u4eba\u6325\u8d77\u53cc\u8f6e\uff0c \u8fce\u5411\u5251\u52bf\uff0c\u60f3\u9501\u4f4f\u4e03\u661f\u5251\u3002 \u3000\u3000\u4f46\u5251\u52bf\u592a\u5feb\uff0c\u9752\u94dc\u8f6e\u8fce\u5411\u5251\u52bf\uff0c\u8fd8\u672a\u53ca\u53d8\u5316\uff0c\u4e03\u661f\u5251\u4e00\u504f\uff0c\u4e58\u865a\u800c\u5165\uff0c\u524a\u4e0b\u4ed6\u5de6\u80a9\u4e0a\u4e00 \u7247\u76ae\u8089\uff0c\u8840\u96e8\u98de\u6e85\u4e2d\uff0c\u5de6\u624b\u9752\u94dc\u8f6e\u4e5f\u8e2a\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4e00\u5251\u4f24\u654c\uff0c\u65e0\u58f0\u593a\u4eba\u3002 \u3000\u3000\u53ef\u662f\uff0c\u654c\u4eba\u7684\u53cd\u5e94\u4e5f\u5feb\uff0c\u4e24\u628a\u9b3c\u5934\u5200\uff0c\u5206\u7531\u5de6\u53f3\u780d\u4e86\u4e0b\u6765\uff0c\u662f\u5f88\u719f\u7ec3\u7684\u5408\u51fb\u4e4b\u672f\u3002 \u3000\u3000\u675c\u671b\u6708\u5251\u62db\u75be\u53d8\uff0c\u6a2a\u91cc\u8fce\u53bb\uff0c\u6321\u5f00\u53f3\u9762\u4e00\u5200\uff0c\u4eba\u4e5f\u968f\u5251\u8f6c\u56de\uff0c\u8eab\u6cd5\u5251\u62db\u914d\u5408\u7edd\u5999\uff0c\u4f7f\u5de6 \u9762\u780d\u6765\u4e00\u5200\u843d\u7a7a\uff0c\u4e03\u661f\u5251\u5df2\u523a\u5165\u53f3\u9762\u4e00\u4eba\u7684\u8138\u80db\u4e2d\uff0c\u51e0\u4e4e\u5728\u540c\u4e00\u65f6\u95f4\uff0c\u98de\u8d77\u4e00\u811a\uff0c\u628a\u654c\u4eba\u8e22 \u6eda\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u4e5f\u501f\u673a\u6536\u56de\u4e86\u4e03\u661f\u5251\u3002 \u3000\u3000\u7b2c\u4e00\u6ce2\u7684\u51b2\u6740\uff0c\u675c\u671b\u6708\u4ee5\u5feb\u901f\u8eab\u6cd5\uff0c\u914d\u5408\u7740\u95ea\u7535\u822c\u7684\u5251\u6cd5\uff0c\u5728\u6781\u77ed\u7684\u65f6\u95f4\u5185\u4f24\u4e86\u4e24\u4eba\uff0c \u4f46\u5374\u6ca1\u6709\u51fb\u6e83\u654c\u4eba\u7684\u6218\u5fd7\uff0c\u4e03\u4e2a\u4eba\u53c8\u8fc5\u5feb\u7ec4\u6210\u4e00\u4e2a\u5706\u5708\u5f62\u7684\u5305\u56f4\u9635\u52bf\uff0c\u4e00\u773c\u5373\u53ef\u770b\u51fa\u5c55\u5f00\u7684 \u662f\u4e00\u573a\u7fa4\u653b\u4e86\u3002\u4e24\u4e2a\u53d7\u4f24\u7684\u4eba\uff0c\u53ea\u628a\u4f24\u5904\u7528\u767d\u7ee2\u4e00\u88f9\uff0c\u4e5f\u52a0\u5165\u4e86\u56f4\u88ad\u7684\u9635\u52bf\u4e2d\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u6b66\u529f\uff0c\u4e0d\u662f\u5f88\u597d\uff0c\u4f46\u4ed6\u4eec\u8868\u73b0\u51fa\u4e86\u8fd9\u4e2a\u7ec4\u5408\u9886\u8896\u4eba\u7269\u7684\u7edf\u9a6d\u624d\u80fd\uff0c\u975e\u6bd4\u5bfb\u5e38\uff0c\u800c \u4e14\u53d7\u4f24\u4e0d\u9000\uff0c\u4e5f\u5145\u5206\u5730\u8868\u73b0\u51fa\u4ed6\u4eec\u6597\u5fd7\u7684\u575a\u5b9a\u3002 \u3000\u3000\u675c\u671b\u6708\u591a\u5e74\u529e\u6848\uff0c\u4ece\u672a\u9047\u4e0a\u8fc7\u8fd9\u6837\u7684\u9ed1\u9053\u7ec4\u5408\uff0c\u4e00\u65e6\u4ed6\u4eec\u63d0\u9ad8\u4e86\u6740\u624b\u7684\u6b66\u529f\u3001\u6280\u827a\uff0c\u8fd9 \u4e2a\u7ec4\u5408\u5c06\u4f1a\u7a81\u51fa\u6c5f\u6e56\uff0c\u662f\u6700\u96be\u5bf9\u4ed8\u7684\u4e00\u4e2a\u7ec4\u5408\u3002 \u3000\u3000\u4f46\u60c5\u52bf\u7684\u53d8\u5316\uff0c\u5df2\u4e0d\u5bb9\u8bb8\u675c\u671b\u6708\u518d\u4f5c\u6df1\u601d\uff0c\u5434\u94a9\u5251\u548c\u4e24\u67c4\u9b3c\u5934\u5200\uff0c\u5df2\u914d\u5408\u6210\u4e00\u7247\u5149\u5e55\uff0c \u7f69\u5411\u675c\u671b\u6708\u3002 \u3000\u3000\u8fd9\u662f\u7ec4\u5408\u653b\u52bf\uff0c\u5200\u548c\u5251\u5982\u4e00\u6574\u4f53\uff0c\u5408\u51fb\u9635\u52bf\u4e2d\uff0c\u591a\u4ee5\u914d\u5408\u4f73\u5999\u3001\u8fde\u7ef5\u8854\u63a5\u7684\u653b\u52bf\u4e3a\u4e3b\uff0c \u60f3\u8fd9\u7b49\u4e09\u4eba\u7ed3\u5408\u4e00\u4f53\u7684\u653b\u52bf\uff0c\u6781\u4e3a\u5c11\u89c1\u3002 \u3000\u3000\u675c\u671b\u6708\u8fd9\u662f\u7b2c\u4e00\u6b21\u9047\u4e0a\u4e86\u8fd9\u7b49\u6253\u6cd5\uff0c\u4e0d\u7981\u5fae\u5fae\u4e00\u5446\u3002 \u3000\u3000\u5c31\u662f\u8fd9\u5fae\u4e00\u9519\u6115\uff0c\u4e00\u56e2\u5149\u5e55\uff0c\u5df2\u5377\u5230\u8eab\u524d\uff0c\u5df2\u65e0\u6cd5\u5206\u6e05\u695a\u662f\u5251\u662f\u5200\u3002 \u3000\u3000\u675c\u671b\u6708\u7a81\u7136\u611f\u89c9\u5230\u4e0d\u80fd\u9000\u8ba9\uff0c\u8fd8\u6709\u53e6\u4e00\u6ce2\u653b\u52bf\uff0c\u5df2\u5728\u84c4\u52bf\u7b49\u5f85\u3002\u5f53\u4e0b\u4e00\u54ac\u7259\uff0c\u594b\u5c3d\u5168\u529b\uff0c \u6325\u5251\u8fce\u5411\u90a3\u56e2\u5149\u5e55\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u675c\u671b\u6708\u611f\u53d7\u5230\u6781\u5927\u7684\u538b\u529b\uff0c\u4f3c\u4e4e\u627f\u53d7\u7684\u4e0d\u662f\u4e00\u4e2a\u4eba\u7684\u529b\u91cf\uff0c\u800c \u662f\u4e09\u4e2a\u4eba\u7ed3\u5408\u7684\u6c14\u529b\uff0c\u975e\u5e38\u6c89\u91cd\u7684\u4e00\u51fb\u3002 \u3000\u3000\u4f46\u675c\u671b\u6708\u8fd8\u662f\u627f\u53d7\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5982\u82e5\u8fd9\u4e9b\u4eba\u662f\u8840\u624b\u65b9\u8f6e\u7684\u624b\u4e0b\uff0c\u65b9\u8f6e\u5f53\u771f\u662f\u4e00\u4e2a\u7528\u4eba\u7684\u5929\u624d\uff0c\u80fd\u628a\u4e00\u4e9b\u8d44\u8d28\u4e0d\u662f\u6781\u597d\u7684 \u4eba\uff0c\u7ec4\u5408\u6210\u5408\u800c\u4e3a\u4e00\u7684\u5171\u540c\u4f53\uff0c\u8fd9\u9700\u8981\u7684\u4e0d\u4f46\u662f\u5e03\u5c40\u548c\u7b56\u5212\u7684\u80fd\u529b\uff0c\u800c\u662f\u5728\u6280\u827a\u914d\u5408\u4e0a\u7684\u7279 \u6b8a\u667a\u6167\u4e86\u3002\u5316\u8150\u673d\u4e3a\u795e\u5947\u7684\u6280\u827a\u7ec4\u5408\uff0c\u5927\u6982\u5c31\u662f\u5982\u6b64\u4e86\u3002\u4e0a\u597d\u8d44\u8d28\u3001\u7980\u8d4b\u7684\u7ec3\u6b66\u4eba\u624d\uff0c\u662f\u6781 \u96be\u5bfb\u627e\u7684\u4eba\uff0c\u8981\u673a\u7f18\u5de7\u5408\u624d\u884c\u3002 \u3000\u3000\u65b9\u8f6e\u653e\u5f03\u4e86\u78b0\u8fd0\u6c14\u7684\u4e8b\uff0c\u627e\u4e00\u4e9b\u4e0d\u592a\u96be\u627e\u7684\u4e2d\u7b49\u7ea7\u4f4d\uff0c\u800c\u6709\u9002\u5408\u4e60\u6b66\u7684\uff0c\u628a\u4ed6\u4eec\u7f57\u6536\u95e8 \u4e0b\uff0c\u4f20\u4ee5\u5408\u640f\u5947\u6280\uff0c\u628a\u6570\u4eba\u529b\u91cf\u5408\u8d77\u6765\uff0c\u5728\u5bf9\u654c\u62da\u6740\u5c0f\uff0c\u80fd\u5408\u800c\u4e3a\u4e00\u3002\u6240\u4ee5\uff0c\u65b9\u8f6e\u5c31\u4e0d\u4f1a\u9047 \u4e0a\u4eba\u624d\u96be\u6c42\u7684\u82e6\u607c\uff0c\u53ef\u4ee5\u5927\u91cf\u6536\u7f57\u95e8\u4eba\uff0c\u4e5f\u4e0d\u6015\u5927\u91cf\u7684\u727a\u7272\u3002\u675c\u671b\u6708\u51dd\u7acb\u4e0d\u52a8\uff0c\u662f\u85c9\u673a\u8c03\u606f\uff0c \u4e0d\u80fd\u8ba9\u5bf9\u65b9\u770b\u51fa\u4ed6\u63a5\u4e0b\u8fd9\u4e00\u51fb\u611f\u53d7\u5230\u7684\u6c89\u91cd\u538b\u529b\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u5bf9\u65b9\u7684\u7ec4\u5408\u653b\u52bf\uff0c\u4e5f\u4f3c\u662f\u505c\u4e86\u4e0b\u6765\uff0c\u8fd9\u5c31\u7ed9\u4e86\u675c\u671b\u6708\u5145\u5206\u7684\u8c03\u606f\u673a\u4f1a\uff0c\u7cbe\u795e \u4e5f\u6062\u590d\u5065\u65fa\u3002 \u3000\u3000\u4f46\u89c1\u4eba\u5f71\u4ea4\u53c9\u8d70\u52a8\uff0c\u5bf9\u65b9\u53c8\u5e03\u6210\u539f\u6765\u5408\u51fb\u7684\u961f\u5f62\uff0c\u770b\u4e86\u5bf9\u65b9\u5e03\u6210\u7684\u961f\u5f62\u4e4b\u540e\uff0c\u675c\u671b\u6708\u624d \u604d\u7136\u5927\u609f\u3002 \u3000\u3000\u76ee\u5149\u56de\u626b\uff0c\u53d1\u89c9\u8eab\u540e\u7a0d\u504f\u5904\uff0c\u4e5f\u5e03\u6210\u4e86\u4e00\u4e2a\u76f8\u540c\u5408\u51fb\u67b6\u52bf\uff0c\u7531\u90a3\u4e2a\u6ca1\u6709\u53d7\u4f24\uff0c\u624b\u6301\u9752\u94dc \u65e5\u6708\u53cc\u8f6e\u7684\u5c45\u4e2d\uff0c\u5c45\u4e2d\u7684\u4eba\uff0c\u4f3c\u662f\u4e09\u4eba\u4e2d\u7684\u9996\u8111\u3002 \u3000\u3000\u675c\u671b\u6708\u6697\u53eb\u4e86\u4e00\u58f0\u60ed\u6127\uff0c\u5fd6\u9053\uff1a\u8fd9\u662f\u4e00\u6b21\u6b6a\u6253\u6b63\u7740\uff0c\u8fd9\u4e9b\u4eba\u4e60\u7ec3\u8fd9\u7b49\u5408\u51fb\u7684\u6280\u827a\u4e0d\u4e45\uff0c \u4e0d\u591f\u719f\u7ec3\uff0c\u53c8\u53d7\u624d\u667a\u6240\u9650\uff0c\u4e0d\u80fd\u901a\u8fbe\u53d8\u5316\uff0c\u6280\u827a\u53d1\u6325\u4e4b\u540e\uff0c\u4e5f\u9884\u5b9a\u4e86\u51e0\u4e2a\u654c\u4eba\u7684\u53cd\u5e94\uff0c\u5982\u82e5 \u654c\u4eba\u7684\u53cd\u5e94\u51fa\u4e86\u4ed6\u4eec\u7684\u610f\u5916\uff0c\u5c31\u4e0d\u77e5\u5982\u4f55\u7ee7\u7eed\u653b\u52bf\u4e86\u3002\u675c\u671b\u6708\u505c\u4e0b\u8c03\u606f\uff0c\u4e5f\u6b63\u662f\u51fa\u654c\u610f\u5916\u7684 \u53d8\u5316\uff0c\u540e\u9762\u4e00\u7ec4\u4eba\u624b\uff0c\u65e0\u6cd5\u914d\u5408\uff0c\u524d\u9762\u4e00\u7ec4\u4eba\u53d1\u52a8\u7b2c\u4e8c\u6b21\u653b\u52bf\u4e4b\u524d\uff0c\u5fc5\u987b\u8981\u518d\u7ec4\u6210\u4e00\u5b9a\u7684\u961f \u5f62\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u624d\u6167\u6240\u9650\u7684\u6700\u5927\u7f3a\u5931\uff0c\u4e0d\u80fd\u968f\u673a\u5e94\u53d8\u3002 \u3000\u3000\u675c\u671b\u6708\u628a\u63e1\u4f4f\u8fd9\u4e2a\u673a\u4f1a\uff0c\u7a81\u7136\u5251\u52bf\u8f6c\u5411\uff0c\u653b\u51fb\u8eab\u540e\u4e00\u7ec4\u654c\u4eba\u3002 \u3000\u3000\u9752\u94dc\u53cc\u8f6e\u548c\u4e24\u67c4\u9b3c\u5934\u5200\uff0c\u4e5f\u540c\u65f6\u4e3e\u8d77\uff0c\u8fce\u5411\u4e03\u661f\u5b9d\u5251\u3002\u9b3c\u5934\u5200\u548c\u9752\u94dc\u8f6e\uff0c\u7ed3\u5408\u4e4b\u5bc6\uff0c\u6709 \u5982\u540c\u4e00\u4ef6\u5175\u5203\u3002\u4e09\u4e2a\u4eba\u7684\u529b\u91cf\u626d\u6210\u4e86\u4e00\u80a1\uff0c\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u58f0\u4e2d\uff0c\u4e03\u661f\u5251\u751f\u751f\u88ab\u5f39\u9707\u5f00\u53bb\uff0c\u4eba \u4e5f\u88ab\u9707\u7684\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u675c\u671b\u6708\u5e76\u672a\u5168\u529b\u51fa\u624b\uff0c\u8fd9\u4e00\u5251\u53ea\u7528\u4e86\u4e03\u6210\u529f\u529b\uff0c\u65e8\u5728\u6d4b\u9a8c\u4e00\u4e0b\u4ed6\u4eec\u9632\u5b88\u7684\u7ec4\u5408\u529b\u91cf\uff0c\u662f \u5426\u548c\u653b\u52bf\u4e00\u6837\u5f3a\u5927\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u662f\u4e00\u79cd\u524d\u6240\u672a\u89c1\u7684\u5947\u6280\uff0c\u4e0d\u4f46\u4e09\u4e2a\u4eba\u7684\u5175\u5203\u7ec4\u5408\u4e25\u5bc6\uff0c\u4e5f\u628a\u4e09\u4e2a\u4eba\u6c14\u529b\u6c47\u96c6\u4e8e\u4e00 \u8d77\u3002 \u3000\u3000\u53e6\u4e00\u7ec4\u5df2\u5b8c\u6210\u5408\u51fb\u67b6\u52bf\uff0c\u5434\u94a9\u5251\u9886\u5934\u653b\u4e0a\uff0c\u5de6\u53f3\u4e24\u67c4\u9b3c\u5934\u5200\uff0c\u968f\u5251\u800c\u51fa\uff0c\u9b3c\u5934\u5200\u4e0e\u5434\u94a9 \u5251\u8ddd\u79bb\u5f88\u8fd1\uff0c\u53ea\u4e0d\u8fc7\u6570\u5bf8\u4e4b\u95f4\uff0c\u968f\u65f6\u53ef\u4ee5\u5408\u6210\u4e00\u80a1\u529b\u91cf\u3002 \u3000\u3000\u675c\u671b\u6708\u5df2\u9886\u6559\u8fc7\u5408\u51fb\u7684\u529b\u91cf\u5f3a\u5927\uff0c\u95ea\u8eab\u8ba9\u907f\u5f00\u53bb\uff0c\u53ea\u662f\u907f\u654c\u65b9\u5411\u4e0d\u662f\u540e\u9000\uff0c\u800c\u662f\u5411\u5de6\u8fb9 \u6ed1\u5f00\u4e00\u5c3a\uff0c\u56e0\u4e3a\u53f3\u540e\u65b9\uff0c\u6b63\u662f\u53e6\u4e00\u4e2a\u4e09\u4eba\u7ec4\u5408\u7684\u5b88\u62a4\u4f4d\u7f6e\u3002 \u3000\u3000\u4f46\u89c1\u9752\u94dc\u8f6e\u5bd2\u8292\u6d41\u8f6c\uff0c\u5408\u7740\u4e24\u67c4\u9b3c\u5934\u5200\uff0c\u518d\u5ea6\u653b\u4e0a\u6765\u3002\u4f46\u95fb\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\u4e0d\u7edd\uff0c\u4e03\u661f\u5251 \u5feb\u523a\u75be\u65a9\uff0c\u5168\u88ab\u5c01\u67b6\u5f00\u53bb\uff0c\u5200\u8f6e\u5408\u6210\u7684\u6297\u62d2\u4e4b\u529b\uff0c\u65e2\u5f3a\u5927\u53c8\u6709\u6548\u7528\u3002 \u3000\u3000\u5982\u662f\u5355\u6253\u72ec\u6597\uff0c\u8fd9\u4e9b\u4eba\u90fd\u975e\u675c\u671b\u6708\u7684\u5feb\u5251\u4e4b\u654c\uff0c\u6ca1\u6709\u4eba\u80fd\u63a5\u4e0b\u4ed6\u4e09\u62db\u5feb\u653b\uff0c\u4ee5\u6280\u827a\u529f\u529b \u800c\u8bba\uff0c\u4e5f\u548c\u675c\u671b\u6708\u6709\u7740\u5f88\u5927\u7684\u4e00\u6bb5\u8ddd\u79bb\uff0c\u4f46\u4e09\u4eba\u7ec4\u5408\u6210\u5200\u8f6e\u548c\u5200\u5251\u9635\u52bf\uff0c\u5374\u6784\u6210\u4e86\u4e00\u4e2a\u575a\u5f3a \u7684\u653b\u9632\u5821\u5792\uff0c\u627c\u6b62\u4e86\u675c\u671b\u6708\u7684\u9510\u6c14\u3002\u675c\u671b\u6708\u7684\u611f\u89c9\u662f\uff0c\u4e09\u4e2a\u4eba\u5175\u5203\u7ed3\u5408\u7684\u529b\u91cf\uff0c\u4e0d\u662f\u4e09\u4e2a\u4eba \u7684\u6c14\u529b\u52a0\u5728\u4e00\u8d77\uff0c\u800c\u662f\u76f8\u4e58\u6548\u679c\u3002 \u3000\u3000\u8fd9\u4e2d\u95f4\u539f\u56e0\u4f55\u5728\uff1f\u5374\u53c8\u4e0d\u662f\u675c\u671b\u6708\u80fd\u60f3\u51fa\u6765\u4e86\uff0c\u5b83\u662f\u4e00\u79cd\u5947\u5e7b\u7684\u5e03\u5c40\uff0c\u628a\u4e09\u4e2a\u4eba\u7684\u6f5c\u529b\uff0c \u5168\u90fd\u53d1\u6325\u4e86\u51fa\u6765\uff0c\u5982\u662f\u6709\u56db\u7ec4\u4eba\u628a\u624b\u4e2d\u5175\u5668\u7ed3\u5408\u8d77\u6765\uff0c\u7ec4\u6210\u4e00\u4e2a\u65b9\u9635\uff0c\u5408\u56f4\u4e4b\u52bf\u7ec4\u5408\u7684\u518d\u4e25 \u5bc6\u4e00\u4e9b\uff0c\u653b\u52bf\u518d\u7d27\u8fde\u4e00\u8d77\uff0c\u5f53\u4e16\u7b2c\u4e00\u6d41\u9ad8\u624b\uff0c\u4e5f\u65e0\u6cd5\u7a81\u56f4\u800c\u51fa\u4e86\u3002 \u3000\u3000\u5e78\u597d\u654c\u4eba\u53ea\u6709\u4e24\u7ec4\uff0c\u4f46\u8fd9\u4e24\u7ec4\u4eba\u624b\u5df2\u4ea4\u66ff\u5c55\u5f00\u4e86\u731b\u70c8\u653b\u52bf\u3002\u675c\u671b\u6708\u594b\u529b\u5e94\u6218\uff0c\u5341\u51e0\u6b21\u51b2 \u6740\u4e4b\u4e2d\uff0c\u675c\u671b\u6708\u4e5f\u65bd\u5c55\u51fa\u4e86\u5168\u8eab\u672c\u9886\uff0c\u5251\u548c\u98ce\u8f6e\u75be\u8f6c\uff0c\u5374\u5df2\u65e0\u6cd5\u7834\u575a\u4f24\u654c\uff0c\u53cd\u88ab\u903c\u7684\u8282\u8282\u540e \u9000\u3002 \u3000\u3000\u4f46\u8fd9\u4e24\u7ec4\u4eba\u624b\uff0c\u65e0\u6cd5\u7ed3\u6210\u56db\u9762\u5408\u51fb\u7684\u65b9\u9635\uff0c\u7559\u4e0b\u4e86\u4e24\u9762\u7a7a\u9699\uff0c\u5374\u4e5f\u65e0\u6cd5\u56f4\u4f4f\u675c\u671b\u6708\uff0c\u4ed6 \u968f\u65f6\u53ef\u4ee5\u8d70\uff0c\u4f46\u4ed6\u4e0d\u80fd\u7559\u4e0b\u60dc\u7389\u3002 \u3000\u3000\u675c\u671b\u6708\u56f0\u6270\u4e86\uff0c\u6218\u4e0d\u80fd\u80dc\uff0c\u8d70\u53c8\u4e0d\u6210\uff0c\u654c\u4eba\u8fd8\u6709\u4e00\u4e2a\u8eab\u53d7\u8f7b\u4f24\u8005\uff0c\u53cc\u624b\u5206\u6267\u65e5\u6708\u53cc\u8f6e\u4e00 \u4fa7\u89c2\u6218\u3002\u4e0d\u8fdc\u5904\u7684\u5c0f\u5c71\u9876\u4e0a\uff0c\u8fd8\u6709\u56db\u4e2a\u5f13\u7bad\u624b\uff0c\u864e\u89c6\u7708\u7708\uff0c\u8fd9\u5c31\u4f7f\u675c\u671b\u6708\u4e0d\u6562\u5192\u9669\u7ed5\u9053\u53bb\u5e26 \u8d70\u60dc\u7389\u3002 \u3000\u3000\u8fd9\u4e9b\u6740\u624b\uff0c\u8d44\u8d28\u5e73\u5eb8\uff0c\u5c31\u7b97\u8ba4\u8bc6\u5b57\uff0c\u4e5f\u53ea\u662f\u7a0d\u901a\u6587\u58a8\uff0c\u4f46\u8fd9\u4e9b\u4eba\u5177\u6709\u7684\u7279\u8272\uff0c\u53ef\u80fd\u5fe0\u8bda \u4e0d\u4e8c\uff0c\u53ea\u91cd\u6069\u4e49\uff0c\u4e0d\u8fa8\u662f\u975e\uff0c\u53ea\u77e5\u9053\u5949\u547d\u884c\u4e8b\uff0c\u5374\u6ca1\u6709\u4e34\u673a\u5e94\u53d8\u7684\u80fd\u529b\uff0c\u675c\u671b\u6708\u65e0\u6cd5\u4e86\u89e3\u4ed6 \u4eec\uff0c\u4e5f\u65e0\u6cd5\u63a8\u6d4b\u4ed6\u4eec\u4e0b\u4e00\u6b65\uff0c\u4f1a\u6709\u4e9b\u4ec0\u4e48\u884c\u52a8\u3002 \u3000\u3000\u7ecf\u8fc7\u8fd9\u4e00\u6bb5\u89c2\u5bdf\uff0c\u675c\u671b\u6708\u53d1\u89c9\u4e86\u4ed6\u4eec\u5175\u5203\u7ec4\u5408\u7684\u653b\u52bf\u53ef\u80fd\u53ea\u6709\u4e00\u62db\uff0c\u4f46\u8fd9\u4e00\u62db\u5df2\u7ecf\u591f\u4e86\uff0c \u675c\u671b\u6708\u5c3d\u5c55\u5168\u8eab\u6280\u827a\uff0c\u4e5f\u65e0\u80fd\u963b\u6321\u8fd9\u4e00\u62db\u653b\u52bf\u3002\u8fd9\u4e00\u62db\u4e5f\u6709\u5f88\u591a\u7f3a\u70b9\uff0c\u4ed6\u4eec\u4e00\u5b9a\u8981\u6392\u6210\u56fa\u5b9a \u7684\u961f\u5f62\uff0c\u624d\u80fd\u7ed3\u5408\u5175\u5203\uff0c\u53d1\u52a8\u653b\u51fb\uff0c\u7528\u4e8e\u5bf9\u654c\uff0c\u8fd9\u4e5f\u4f7f\u4ed6\u4eec\u6740\u4f24\u7684\u80fd\u529b\u51cf\u81f3\u6700\u4f4e\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u4e5f\u662f\u675c\u671b\u6708\u80fd\u5b58\u6d3b\u4e0b\u6765\u7684\u91cd\u8981\u539f\u56e0\u3002\u6240\u4ee5\uff0c\u4ed6\u7528\u5c3d\u4e86\u5fc3\u673a\u89c2\u5bdf\uff0c\u5374\u770b\u4e0d\u51fa\u8fd9\u4e2a \u7ec4\u5408\u6210\u5f3a\u529b\u653b\u51fb\u7684\u6280\u5de7\u6240\u5728\u3002\u5728\u8fd9\u573a\u640f\u6740\u4e2d\uff0c\u675c\u671b\u6708\u6700\u5927\u7684\u6536\u83b7\u662f\uff0c\u53d1\u89c9\u4e86\u6c5f\u6e56\u4e0a\u8fd9\u4e2a\u53ef\u6015 \u7684\u79d8\u5bc6\u3002\u6b64\u523b\u79bb\u53bb\uff0c\u6b63\u662f\u65f6\u673a\uff0c\u56db\u4e2a\u5f13\u7bad\u624b\uff0c\u867d\u5df2\u7bad\u642d\u5f26\u4e0a\uff0c\u4f46\u675c\u671b\u6708\u81ea\u4fe1\u53ef\u4ee5\u5e94\u4ed8\u81ea\u5982\uff0c \u53ea\u662f\u4e3a\u4e86\u60dc\u7389\uff0c\u4e0d\u80fd\u8d70\uff0c\u60c5\u5b57\u7d2f\u4eba\uff0c\u4f7f\u675c\u671b\u6708\u9677\u5165\u6218\u4e0d\u80fd\u80dc\uff0c\u8d70\u4e0d\u80fd\u8d70\u7684\u56f0\u5883\u4e4b\u4e2d\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e24\u7ec4\u6740\u624b\uff0c\u5df2\u8c03\u6574\u597d\u653b\u51fb\u7684\u961f\u5f62\uff0c\u770b\u4ed6\u4eec\u7ad9\u7acb\u7684\u4f4d\u7f6e\uff0c\u5df2\u6709\u6240\u8c03\u6574\uff0c\u51c6\u5907\u5728\u8fde\u7eed \u7684\u653b\u52bf\u4e2d\uff0c\u80fd\u628a\u675c\u671b\u6708\u51fb\u6740\u5f53\u573a\u3002 \u3000\u3000\u552f\u4e00\u4f7f\u675c\u671b\u6708\u7a0d\u611f\u5b89\u5fc3\u7684\uff0c\u654c\u4eba\u628a\u76ee\u6807\u5b9a\u5728\u4ed6\u4e00\u4e2a\u4eba\u8eab\u4e0a\uff0c\u5bf9\u60dc\u7389\u4f3c\u662f\u5f03\u4e4b\u4e0d\u95ee\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u6df1\u6df1\u5438\u4e00\u53e3\u6c14\uff0c\u538b\u5236\u4e0b\u7d0a\u4e71\u7684\u5fc3\u60c5\uff0c\u7f13\u7f13\u4e3e\u8d77\u4e03\u661f\u5b9d\u5251\uff0c\u8138\u4e0a\u662f\u4e00\u7247\u6ee1\u542b\u60b2\u60c5\u7684 \u51b7\u8083\uff0c\u6a21\u6837\u662f\u5df2\u653e\u5f03\u4e86\u8d70\u7684\u6253\u7b97\uff0c\u51c6\u5907\u594b\u529b\u4e00\u62da\u4e86\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u675c\u671b\u6708\u4e5f\u662f\u8fd9\u79cd\u76d8\u7b97\uff0c\u51c6\u5907\u5728\u8fd9\u4e00\u6b21\u51b2\u6740\u4e2d\uff0c\u4efb\u80a2\u4f53\u53d7\u5230\u4f24\u5bb3\uff0c\u4e5f\u8981\u51fb\u7834\u8fd9\u4e2a \u795e\u5947\u7684\u7ec4\u5408\uff0c\u4ed6\u76f8\u4fe1\u53ea\u8981\u80fd\u51fb\u6740\u6216\u91cd\u4f24\u4e09\u4e2a\u654c\u4eba\u4e2d\u7684\u4e00\u4e2a\uff0c\u8fd9\u4e2a\u5175\u5203\u7ec4\u5408\u4e2d\u7684\u5f3a\u529b\u653b\u52bf\uff0c\u53ef \u80fd\u4f1a\u5931\u53bb\u4f5c\u7528\u3002\u675c\u671b\u6708\u51c6\u5907\u727a\u7272\u4e86\uff0c\u9057\u61be\u7684\u662f\u6ca1\u6709\u4eba\uff0c\u80fd\u628a\u4ed6\u6d41\u8840\u727a\u7272\u5bfb\u627e\u51fa\u6765\u7684\u7ecf\u9a8c\u8f6c\u544a \u7ed9\u4ed6\u7684\u540c\u4f34\uff0c\u4e5f\u6709\u70b9\u540e\u6094\uff0c\u6ca1\u7ea6\u4e2d\u5dde\u540d\u6355\u5434\u94c1\u5cf0\u540c\u6765\u7626\u897f\u6e56\u2026\u2026\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u4f20\u8fc7\u6765\u4e00\u58f0\u5a07\u547c\uff0c\u9053\uff1a\u201c\u675c\u5927\u54e5\uff0c\u4e0d\u8981\u62da\u547d\u554a\uff01\u201d \u3000\u3000\u4e00\u6761\u4eba\u5f71\uff0c\u98de\u8dc3\u800c\u8d77\uff0c\u6d41\u661f\u8d76\u6708\u4e00\u822c\uff0c\u75be\u5c04\u800c\u6765\u3002 \u3000\u3000\u662f\u60dc\u7389\u3002 \u3000\u3000\u675c\u671b\u6708\u5fae\u5fae\u4e00\u6014\uff0c\u7a81\u7136\u62d4\u8eab\u800c\u8d77\uff0c\u72b9\u5982\u6f5c\u9f99\u5347\u5929\u4e00\u822c\uff0c\u4e00\u98de\u4e24\u4e08\u591a\u9ad8\uff0c\u4eba\u5982\u5929\u9a6c\u884c\u7a7a\uff0c \u7531\u4e00\u7ec4\u6740\u624b\u7684\u5934\u9876\u4e0a\u98de\u8fc7\uff0c\u4e0d\u518d\u6253\u7b97\u62da\u547d\u4e86\u3002 \u3000\u3000\u7bad\u98ce\u7834\u7a7a\uff0c\u5b88\u5019\u5728\u5c0f\u5c71\u9876\u4e0a\u7684\u5f13\u7bad\u624b\uff0c\u53ca\u65f6\u53d1\u52a8\u653b\u52bf\u3002\u4ed6\u4eec\u7528\u7684\u662f\u725b\u94c1\u5f3a\u5f13\uff0c\u7bad\u6cd5\u4e5f\u76f8 \u5f53\u7684\u51c6\u786e\uff0c\u8fde\u4e00\u4e2a\u4eba\u98de\u8dc3\u7684\u901f\u5ea6\uff0c\u4e5f\u8ba1\u7b97\u4e86\u8fdb\u53bb\u3002\u8fd9\u56db\u4e2a\u5f13\u7bad\u624b\u4f3c\u662f\u548c\u4e03\u4e2a\u6740\u624b\u4e0d\u540c\uff0c\u4ed6\u4eec \u6709\u76f8\u5f53\u7684\u667a\u6167\uff0c\u56db\u652f\u7bad\u5206\u5c04\u675c\u671b\u6708\u548c\u60dc\u7389\u4e24\u4eba\u3002 \u3000\u3000\u675c\u671b\u6708\u5fc3\u5934\u4e00\u9707\uff0c\u5fd6\u9053\uff1a\u597d\u51c6\u7684\u7bad\u6cd5\uff0c\u957f\u5251\u6325\u51fa\uff0c\u5e7b\u8d77\u4e00\u56e2\u51b7\u8292\uff0c\u4e24\u652f\u8fd1\u8eab\u957f\u7bad\uff0c\u5c3d\u4e3a \u5251\u5149\u51fb\u843d\uff0c\u4f46\u675c\u671b\u6708\u4e5f\u65e0\u6cd5\u518d\u63d0\u805a\u771f\u6c14\uff0c\u5411\u4e0b\u843d\u53bb\u3002 \u3000\u3000\u4f46\u4ed6\u53c8\u62c5\u5fc3\u60dc\u7389\u53d7\u5230\u4f24\u5bb3\uff0c\u8eab\u5b50\u5411\u4e0b\u843d\uff0c\u76ee\u5149\u5374\u6295\u6ce8\u5411\u60dc\u7389\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u60dc\u7389\u5c55\u73b0\u4e86\u51fa\u4eba\u610f\u5916\u7684\u6b66\u529f\u6280\u827a\uff0c\u60ac\u7a7a\u4e00\u4e2a\u8ddf\u6597\uff0c\u4e24\u652f\u957f\u7bad\uff0c\u5168\u88ab\u5979\u63a5\u4f4f\uff0c\u4eba\u5374\u5411\u675c\u671b \u6708\u51b2\u4e86\u8fc7\u53bb\u3002\u4e24\u4e2a\u4eba\u867d\u662f\u5148\u540e\u7740\u5730\uff0c\u76f8\u8ddd\u5374\u53ea\u6709\u4e09\u56db\u5c3a\u8fdc\u3002 \u3000\u3000\u201c\u5f53\u5fc3\u7b2c\u4e8c\u6ce2\u5229\u7bad\u5c04\u6765\u3002\u201d\u675c\u671b\u6708\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4ecd\u5728\u5371\u673a\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u8f6c\u8eab\u770b\u53bb\uff0c\u56db\u4e2a\u5f13\u7bad\u624b\uff0c\u6b63\u5411\u5c71\u4e0b\u683d\u53bb\uff0c\u4e24\u4e2a\u9752\u8863\u4eba\u5bf9\u7740\u675c\u671b\u6708\u4e00\u6325\u624b\uff0c\u9690\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u201c\u662f\u4ed6\u4eec\uff0c\u201d\u675c\u671b\u6708\u81ea\u8a00\u81ea\u8bed\u5730\u8bf4\u9053\uff1a\u201c\u603b\u6355\u5934\u5230\u626c\u5dde\u4e86\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u662f\u8c01\u5440\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u6211\u8bb0\u5f97\uff0c\u4f60\u8bf4\u8fc7\uff0c\u603b\u6355\u5934\u662f\u4e2a\u5973\u7684\uff0c\u957f\u7684\u4f3c\u5929\u4ed9\u753b\u4eba\u4e00\u6837 \u7f8e\u4e3d\uff0c\u4ed6\u4eec\u662f\u4e24\u4e2a\u5927\u7537\u4eba\u554a\uff01\u201d \u3000\u3000\u201c\u662f\u963f\u6a2a\u3001\u963f\u4fdd\u3002\u201d\u675c\u671b\u6708\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e24\u5458\u52c7\u5c06\uff0c\u6280\u827a\u7cbe\u7edd\uff0c\u6700\u64c5\u6f5c\u8e2a\u9690\u85cf\u4e4b\u672f\uff0c \u4ed6\u4eec\u662f\u603b\u6355\u5934\u7684\u4e24\u4e2a\u5fe0\u5b9e\u7684\u4ece\u5c5e\uff0c\u4e5f\u662f\u6c5f\u6e56\u5947\u4eba\uff0c\u4ed6\u4eec\u89c6\u540d\u5229\u5982\u6d6e\u4e91\uff0c\u53ea\u613f\u8ffd\u968f\u603b\u6355\u5934\u4e00\u4e2a \u6548\u529b\u5356\u547d\uff0c\u5374\u4e0d\u613f\u63a5\u53d7\u5b98\u5bb6\u7684\u804c\u52a1\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\u60f3\u5230\u4e86\u8fd8\u6709\u4e03\u4e2a\u6740\u624b\u7559\u5728\u73b0\u573a\uff0c\u8f6c\u5934\u770b\u53bb\uff0c\u4e03\u4eba\u5df2\u7136\u4e0d\u89c1\u3002 \u3000\u3000\u201c\u4e0d\u7528\u627e\u4ed6\u4eec\u4e86\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u603b\u6355\u5934\u8bf4\uff0c\u8fd8\u4e0d\u662f\u6355\u6740\u4ed6\u4eec\u7684\u65f6\u673a\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u603b\u6355\u5934\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u5979\u6551\u4e86\u6211\uff0c\u89e3\u5f00\u4e86\u6211\u7684\u7a74\u9053\uff0c\u4e5f\u89e3\u53bb\u4e86\u6211\u8eab\u4e2d\u7684\u5947\u6bd2\u3002\u201d\u60dc\u7389\u9053\uff1a\u201c\u5979\u8981\u6211\u8f6c\u544a \u4f60\uff0c\u4eca\u591c\u5728\u626c\u5dde\u5e9c\u8859\u89c1\u9762\uff0c\u5979\u5df2\u6536\u5230\u4e86\u4f60\u7684\u5448\u6587\uff0c\u4e5f\u6536\u5230\u4e86\u738b\u77e5\u5e9c\u9080\u5979\u4e0b\u626c\u5dde\u7684\u4fe1\u51fd\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u770b\u6765\u662f\u5979\u5df2\u7ecf\u65e9\u5230\u626c\u5dde\u4e86\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u4e0d\u9519\uff0c\u5979\u662f\u5c0f\u8c2a\u4eba\u95f4\u7684\u4ed9\u5b50\uff0c\u60dc\u7389\u867d\u81ea\u8d1f\u5bb9\u8c8c\uff0c\u4f46\u6bd4\u8d77\u603b\u6355\u5934\u6765\uff0c\u6211 \u53ea\u662f\u4e00\u4e2a\u4e11\u4e2b\u5934\u7f62\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u5984\u81ea\u83f2\u8584\uff0c\u4f60\u4e5f\u662f\u7f8e\u5973\u4e2d\u7684\u7fd8\u695a\u2026\u2026\u201d \u3000\u3000\u60dc\u7389\u82e6\u7b11\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u6211\u81ea\u8d1f\u624d\u6167\u8fc7\u4eba\uff0c\u90fd\u4e2d\u4e86\u5510\u5b37\u5b37\u7684\u6697\u7b97\uff0c\u5509\uff01\u6211\u5982\u80fd\u5c0f\u5fc3\u4e00\u4e9b\uff0c \u5979\u5c31\u4e0d\u4f1a\u5f97\u624b\u4e86\uff0c\u4e49\u7236\u4e3a\u4e86\u6551\u6211\uff0c\u51fa\u624b\u51fb\u9000\u4e86\u56db\u4f4d\u5b37\u5b37\u7684\u8054\u624b\u653b\u52bf\uff0c\u6015\u6bd2\u6c14\u653b\u5fc3\uff0c\u70b9\u6211\u7a74\u9053\u3002 \u5206\u5fc3\u65c1\u987e\u65f6\uff0c\u4e5f\u4e3a\u5510\u5b37\u5b37\u7684\u6bd2\u9488\u6240\u4f24\u3002\u4f46\u4ed6\u8001\u4eba\u5bb6\uff0c\u4ecd\u7136\u594b\u8d77\u4f59\u52c7\uff0c\u91cd\u521b\u4e86\u5510\u5b37\u5b37\uff0c\u81f4\u4f7f\u672c \u8eab\u6bd2\u6027\u5feb\u901f\u53d1\u4f5c\uff0c\u5df2\u65e0\u6cd5\u4fdd\u62a4\u6211\u5165\u57ce\u5c31\u533b\u3002\u8001\u4eba\u5bb6\u542c\u6211\u8bf4\u8fc7\uff0c\u5728\u89c2\u9c7c\u4ead\u9644\u8fd1\u548c\u4f60\u4f1a\u9762\uff0c\u624d\u628a \u8d1f\u8239\u9a70\u5f80\u6e56\u5fc3\u7b49\u4f60\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u4e49\u7236\u662f\u4e2a\u52c7\u8005\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5728\u6bd2\u6027\u53d1\u4f5c\u4e4b\u4e0b\uff0c\u4ecd\u7136\u4f5c\u6700\u540e\u4e00\u51fb\uff0c\u548c\u654c\u4eba\u540c\u5f52\u4e8e \u5c3d\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e9b\u90fd\u662f\u6211\u7684\u9519\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u6211\u65e9\u77e5\u9053\u5510\u5b37\u5b37\u7cbe\u7ec6\u9634\u9669\uff0c\u7adf\u672a\u9884\u9632\u5979\u6697\u65bd\u7b97\u8ba1\uff0c\u7b97 \u4ec0\u4e48\u624d\u6167\u8fc7\u4eba\u5417\uff1f\u7b80\u76f4\u662f\u611a\u7b28\u6781\u4e86\uff0c\u5509\uff01\u8be6\u7ec6\u544a\u8bc9\u6211\u4e8b\u60c5\u7ecf\u8fc7\uff0c\u6211\u8981\u77e5\u9053\u4e49\u7236\u6b7b\u5728\u4e86\u4ec0\u4e48\u4eba \u7684\u624b\u4e2d\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u70b9\u70b9\u5934\uff0c\u4ed4\u7ec6\u5730\u8bf4\u660e\u4e86\u7ecf\u8fc7\u60c5\u5f62\u3002 \u3000\u3000\u201c\u4e0d\u50cf\u662f\u56db\u51e4\u697c\u4e2d\u7684\u4eba\u3002\u201d\u60dc\u7389\u9053\uff1a\u201c\u56db\u51e4\u697c\u4e2d\u4e0d\u53ef\u80fd\u517b\u6709\u8fd9\u4e48\u591a\u7684\u6740\u624b\uff0c\u4e5f\u6ca1\u6709\u5730\u65b9 \u5360\u8bad\u7ec3\u8fd9\u6837\u4e00\u5927\u6279\u4eba\u3002\u201d \u3000\u3000\u201c\u56db\u51e4\u697c\u4e0a\uff0c\u7528\u4e86\u5927\u6279\u7684\u4e2d\u5e74\u5973\u4eba\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u8fd9\u4e0d\u50cf\u662f\u7537\u4eba\u7684\u4f5c\u98ce\uff0c\u4f1a\u4e0d\u4f1a\u662f\u4e2a \u5973\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u6000\u7591\u8fc7\u8fd9\u4ef6\u4e8b\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u53ef\u60dc\u65e0\u6cd5\u8bc1\u660e\uff0c\u6211\u60f3\u63a5\u8fd1\u4ed6\uff0c\u4e5f\u662f\u60f3\u67e5\u660e\u8fd9\u4ef6\u4e8b \u60c5\u3002\u201d\u53f9\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u6211\u592a\u7b28\u4e86\uff0c\u4f4f\u5728\u6587\u9601\u4e00\u5e74\u540e\uff0c\u5fcd\u53d7\u7740\u8fce\u5ba2\u966a\u9152\u7684\u75db\u82e6\uff0c\u7adf\u7136 \u8fd8\u672a\u67e5\u51fa\u697c\u4e3b\u662f\u7537\u662f\u5973\uff1f\u201d \u3000\u3000\u201c\u60dc\u7389\uff0c\u4e0d\u8981\u81ea\u8d23\u592a\u6df1\uff0c\u6c5f\u6e56\u51f6\u9669\uff0c\u9632\u4e0d\u80dc\u9632\uff0c\u6709\u4e9b\u4e0d\u662f\u9760\u6b66\u529f\u3001\u624d\u667a\u80fd\u591f\u5e94\u4ed8\u7684\uff01\u201d \u675c\u671b\u6708\u9053\uff1a\u201c\u9700\u8981\u6c5f\u6e56\u4e0a\u7684\u5386\u7ec3\u7ecf\u9a8c\uff0c\u6709\u8fc7\u8fd9\u4e00\u756a\u523a\u9aa8\u4e4b\u75db\uff0c\u6211\u76f8\u4fe1\u4f60\u5df2\u7ecf\u6210\u719f\u4e86\u4e0d\u5c11\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u5df2\u5f7b\u5e95\u5730\u6e05\u9664\u4e86\u6211\u5fc3\u4e2d\u5b58\u5728\u7684\u50b2\u6c14\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u518d\u6709\u4eba\u60f3\u6697\u7b97\u6211\uff0c\u5c31\u4e0d\u4f1a\u8fd9\u4e48 \u5bb9\u6613\u4e86\uff0c\u53ea\u662f\u8fd9\u6b21\u758f\u5ffd\u7684\u4ee3\u4ef7\u592a\u5927\u4e86\uff0c\u5bb3\u6b7b\u4e86\u6211\u7684\u4e49\u7236\uff0c\u4ed6\u662f\u6211\u8fd9\u4e9b\u5e74\u6765\u7cbe\u795e\u4e0a\u552f\u4e00\u7684\u652f\u67f1\uff0c \u4e16\u4e0a\u6700\u4eb2\u8fd1\u7684\u4eba\u554a\uff01\u6b64\u540e\uff0c\u6211\u5c06\u4e00\u8eab\u98d8\u96f6\uff0c\u56db\u987e\u832b\u832b\u2026\u2026\u201d\u4e24\u884c\u6e05\u6cea\uff0c\u987a\u816e\u800c\u4e0b\uff0c\u5982\u7ebf\u7a7f\u73e0\uff0c \u7ef5\u8fde\u4e0d\u7edd\u3002 \u3000\u3000\u675c\u671b\u6708\u770b\u5979\u8eab\u8eaf\u4e5f\u5fae\u5fae\u6296\u52a8\uff0c\u4f24\u5fc3\u4e4b\u6df1\uff0c\u5df2\u81f3\u6781\u5904\uff0c\u7f13\u7f13\u4f38\u51fa\u624b\u53bb\uff0c\u63e1\u4f4f\u60dc\u7389\u7684\u5de6\u624b\uff0c \u4f4e\u58f0\u9053\uff1a\u201c\u60dc\u7389\uff0c\u8fd9\u4e0d\u662f\u4f24\u5fc3\u7684\u65f6\u523b\uff0c\u6211\u4eec\u8981\u51b7\u9759\u4e0b\u6765\uff0c\u624d\u80fd\u5206\u6790\u654c\u60c5\uff0c\u6740\u4f60\u4e49\u7236\u7684\u4eba\uff0c\u5982 \u975e\u56db\u51e4\u697c\u4e2d\u7684\u6740\u624b\uff0c\u4ed6\u4eec\u6765\u81ea\u4f55\u5904\u5462\uff1f\u201d \u3000\u3000\u8fd9\u786e\u662f\u975e\u5e38\u6709\u6548\u7684\u5b89\u6170\uff0c\u60dc\u7389\u7acb\u523b\u6b62\u4f4f\u4e86\u773c\u6cea\uff0c\u9053\uff1a\u201c\u6211\u8981\u627e\u51fa\u4ed6\u4eec\u66ff\u4e49\u7236\u62a5\u4ec7\u2026\u2026\u201d \u3000\u3000\u201c\u8fd8\u6709\u6211\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4f60\u4e0d\u4f1a\u4e00\u8eab\u98d8\u96f6\uff0c\u6211\u4f1a\u5e38\u4f34\u5728\u4f60\u7684\u8eab\u4fa7\uff0c\u5211\u90e8\u7684\u603b\u6355\u53f8\uff0c\u4e2d\u3001 \u5357\u3001\u897f\u3001\u4e1c\u56db\u5927\u5206\u53f8\u4e2d\u4e0a\u767e\u7684\u5211\u6355\u9ad8\u624b\uff0c\u90fd\u662f\u6211\u4eec\u7684\u6551\u63f4\uff0c\u628a\u8fd9\u4e9b\u6740\u4eba\u4e3a\u4e1a\u7684\u6b79\u5f92\uff0c\u7ef3\u4e4b\u4ee5 \u6cd5\uff0c\u518d\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u6d88\u9065\u6cd5\u5916\uff0c\u8ba1\u4ef7\u53d6\u547d\u3002\u201d \u3000\u3000\u60dc\u7389\u7684\u56de\u62a5\u662f\uff0c\u7d27\u7d27\u5730\u63e1\u4e86\u675c\u671b\u6708\u7684\u624b\uff0c\u76c8\u76c8\u542b\u6cea\u7684\u76ee\u5149\u4e2d\uff0c\u4f20\u6765\u4e86\u4e07\u5343\u7684\u60c5\u610f\u3002 \u3000\u3000\u201c\u6211\u89c1\u8bc6\u4e86\u4f60\u7684\u6b66\u529f\u6210\u5c31\uff0c\u80fd\u5728\u817e\u7a7a\u98de\u8dc3\u4e2d\uff0c\u63a5\u4e0b\u4e86\u4e24\u652f\u957f\u7bad\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u90a3\u53ef\u662f \u786c\u5f13\u5229\u7bad\uff0c\u529b\u9053\u5f88\u5f3a\uff0c\u4e00\u822c\u4eba\u4e0d\u6562\u63a5\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u4e5f\u4e0d\u4f1a\u5192\u9669\u63a5\u7bad\uff0c\u800c\u7528\u957f\u5251\u51fb\u843d\u5b83\u3002\u201d \u3000\u3000\u201c\u4eba\u5bb6\u624b\u4e2d\u6ca1\u6709\u5251\u561b\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u7b11\u9053\uff1a\u201c\u4f46\u4e5f\u5c55\u73b0\u4e86\u4f60\u6280\u827a\u7684\u7cbe\u6e5b\uff0c\u624b\u6cd5\u7684\u5feb\u901f\uff0c\u6709\u4f60\u8fd9\u6837\u4e00\u4f4d\u5e74\u8f7b\u7f8e\u4e3d\u7684\u9ad8\u624b\uff0c \u52a0\u5165\u6c5f\u5357\u5211\u6355\u5206\u53f8\uff0c\u5b9e\u662f\u6c5f\u5357\u4e07\u6c11\u4e4b\u798f\u4e86\u3002\u201d \u3000\u3000\u201c\u671b\u6708\uff0c\u6211\u73b0\u5728\u5012\u662f\u613f\u4e3a\u5211\u6355\u4e86\uff0c\u548c\u51f6\u4eba\u4f5c\u5bf9\uff0c\u6349\u6b79\u5f92\u4e3a\u4e1a\uff0c\u4e3a\u4eba\u95f4\u9664\u5bb3\uff0c\u867d\u7136\u751f\u6d3b\u5728 \u5200\u5149\u5251\u5f71\u4e4b\u4e2d\uff0c\u5374\u662f\u6d3b\u7684\u5f88\u6709\u610f\u601d\uff0c\u4f55\u51b5\uff0c\u6211\u53c8\u80fd\u5e38\u4f34\u968f\u541b\u4fa7\uff0c\u4e0d\u8fc7\uff0c\u6211\u6015\u5440\uff1f\u201d \u3000\u3000\u201c\u6015\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6574\u5929\u548c\u4f60\u817b\u5728\u4e00\u8d77\uff0c\u51fa\u53cc\u5165\u5bf9\u7684\uff0c\u6015\u522b\u4eba\u8bf4\u95f2\u8bdd\u5440\uff01\u201d\u60dc\u7389\u9053\uff1a\u201c\u8981\u6211\u6bcf\u5929\u677f\u7740\u8138\uff0c \u53eb\u4f60\u53f8\u4e3b\u6216\u662f\u603b\u6355\u5934\uff0c\u6211\u53ef\u662f\u53d7\u4e0d\u4e86\u554a\uff01\u201d \u3000\u3000\u201c\u4e0d\u8981\u7d27\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4e0d\u4f1a\u6709\u4eba\u8bf4\u95f2\u8bdd\uff0c\u5982\u4e0d\u5b89\u5fc3\uff0c\u6211\u4eec\u65e9\u628a\u540d\u4efd\u5b9a\u4e86\uff0c\u592b\u59bb\u4fe9\u5e76 \u9a91\u529e\u6848\uff0c\u53ef\u662f\u4e00\u6869\u7f8e\u8c08\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u4f20\u8bf5\u5343\u53e4\u5462\uff01\u201d \u3000\u3000\u201c\u6211\u4e00\u65e6\u4f5c\u4e86\u5211\u6355\uff0c\u80fd\u4e0d\u80fd\u4e2d\u9014\u9000\u51fa\u5462\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u603b\u4e0d\u8ba9\u6211\u4e00\u76f4\u5e72\u4e0b\u53bb\uff0c\u76f4\u5230\u4eba\u8001 \u73e0\u9ec4\u3002\u201d \u3000\u3000\u201c\u603b\u6355\u5934\uff0c\u901a\u60c5\u8fbe\u7406\uff0c\u7edd\u4e0d\u4f1a\u5f3a\u4eba\u6240\u96be\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u4f60\u6709\u4e24\u4e2a\u5f88\u597d\u7684\u8bf7\u9000\u65f6 \u673a\uff1f\u201d \u3000\u3000\u60dc\u7389\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u7684\u65f6\u673a\u5462\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u4eec\u62dc\u5802\u6210\u4eb2\u4e4b\u540e\uff0c\u4f60\u53ef\u4ee5\u8f9e\u9000\uff0c\u4e13\u5fc3\u4e3a\u6211\u59bb\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8981\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u4f60\u60f3\u7684\u597d\u7f8e\u5440\uff01\u5a36\u8fdb\u95e8\uff0c\u5c31\u628a\u6211\u5173\u5728\u5bb6\u91cc\uff0c\u4f60\u597d\u501f\u7740\u529e\u6848\u4e3a\u540d\uff0c \u904d\u6e38\u6c5f\u5357\u98ce\u5149\uff0c\u98ce\u6d41\u5feb\u6d3b\uff0c\u8981\u6211\u72ec\u5b88\u7a7a\u5e0f\u3001\u501a\u95e8\u671b\u592b\u5f52\uff0c\u90a3\u4f1a\u628a\u6211\u6d3b\u6d3b\u7684\u95f7\u6b7b\u3002\u518d\u8bf4\u65b0\u5a5a\u65f6 \u671f\uff0c\u592b\u59bb\u60c5\u610f\u6b63\u6d53\uff0c\u4f60\u820d\u5f97\u4e22\u4e0b\u6211\u4e00\u4e2a\u4eba\u5728\u5bb6\u91cc\u5fcd\u53d7\u90a3\u5bc2\u5bde\u7684\u76f8\u601d\u4e48\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5b9e\u5728\u8bf4\uff0c\u820d\u4e0d\u5f97\uff0c\u4f55\u51b5\uff0c\u4f60\u6280\u827a\u7cbe\u7edd\uff0c\u53ef\u80fd\u8fd8\u5f3a\u6211\u4e09\u5206\uff0c\u5982\u6b64\u9ad8\u660e\u7684\u4e00\u4e2a \u52a9\u624b\uff0c\u7559\u4f60\u5728\u5bb6\u91cc\uff0c\u5c82\u4e0d\u662f\u4e16\u4e0a\u7b2c\u4e00\u53f7\u7b28\u86cb\u3002\u201d \u3000\u3000\u60dc\u7389\u7b11\u4e86\uff0c\u542c\u5230\u5fc3\u7a9d\u91cc\u5566\uff01\u7406\u4e00\u7406\u9b13\u8fb9\u6563\u53d1\uff0c\u9053\uff1a\u201c\u8fd8\u4f1a\u6709\u4ec0\u4e48\u65f6\u673a\u5462\uff0c\u5feb\u4e9b\u8bf4\u554a\uff01\u201d \u3000\u3000\u201c\u4f60\u8eab\u6000\u516d\u7532\uff0c\u6709\u5b69\u5b50\u7684\u65f6\u5019\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u90a3\u65f6\u63d0\u51fa\u4e86\u8f9e\u9000\uff0c\u603b\u6355\u5934\u4e00\u5b9a\u4f1a\u51c6\u3002\u201d \u3000\u3000\u60f3\u50cf\u4e2d\uff0c\u60dc\u7389\u4f1a\u7b11\u7684\uff0c\u4f46\u60dc\u7389\u4e0d\u4f46\u6ca1\u7b11\uff0c\u53cd\u800c\u4f3c\u53d7\u5230\u4ec0\u4e48\u60ca\u5413\uff0c\u8138\u8272\u82cd\u767d\u7684\uff0c\u9053\uff1a\u201c\u6211 \u662f\u6700\u6015\u8fd9\u4ef6\u4e8b\u4e86\uff0c\u751f\u5b69\u5b50\u75bc\u7684\u8981\u547d\uff0c\u53c8\u4e0d\u80fd\u8fd0\u529f\u6297\u62d2\uff0c\u5929\u554a\uff01\u4eba\u600e\u4e48\u53d7\u5f97\u4e86\u554a\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4e16\u4e0a\u5343\u5343\u4e07\u4e07\u7684\u5988\u5988\uff0c\u4e0d\u662f\u90fd\u6d3b\u7684\u5f88\u597d\u5417\uff1f\u800c\u4e14\uff0c\u6709\u4e9b\u751f\u4e0a\u763e\uff0c\u4e00\u8fde\u751f\u4e86 \u516d\u4e03\u4e2a\u3002\u201d \u3000\u3000\u60dc\u7389\u4fa7\u7740\u5934\u513f\u60f3\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\u554a\uff01\u522b\u7684\u5973\u4eba\u90fd\u4e0d\u6015\uff0c\u6211\u53c8\u6015\u4ec0\u4e48\u5462\uff1f\u5982\u679c\u751f\u7684 \u597d\u73a9\uff0c\u6211\u4e5f\u5e2e\u4f60\u591a\u751f\u51e0\u4e2a\u3002\u201d \u3000\u3000\u201c\u60f3\u591a\u751f\u554a\uff01\u6211\u53ef\u4e0d\u4f1a\u540c\u610f\u3002\u201d \u3000\u3000\u201c\u4eba\u5bb6\u90fd\u5e0c\u671b\u591a\u513f\u591a\u5973\uff0c\u4f60\u600e\u4e48\u4e0d\u5e0c\u671b\u554a\uff1f\u201d \u3000\u3000\u201c\u6211\u8981\u4f60\u591a\u817e\u51fa\u4e00\u4e9b\u65f6\u95f4\u966a\u6211\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5e2e\u6211\u591a\u529e\u51e0\u4ef6\u6848\u5b50\uff0c\u4e3a\u4eba\u95f4\u591a\u9664\u6389\u51e0\u4e2a \u4e3a\u975e\u4f5c\u6b79\u4e4b\u5f92\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u662f\u8fd9\u6837\u60f3\uff0c\u4f46\u6211\u8f9e\u53bb\u4e86\u5211\u6355\u4e4b\u540e\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u8fd8\u4f1a\u6709\u4ec0\u4e48\u8eab\u4efd\u6765\u5e2e\u4f60\u529e\u6848\uff1f\u201d \u3000\u3000\u201c\u60dc\u7389\uff0c\u4f60\u60f3\u7684\u592a\u591a\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u6837\u4eba\u624d\uff0c\u603b\u6355\u5934\u600e\u80af\u8ba9\u4eba\u95f2\u7f6e\uff0c\u6211\u4e0d\u80fd\u4ee3 \u5979\u7b54\u5e94\u4f60\u4ec0\u4e48\uff0c\u4f46\u81f3\u5c11\u4f60\u53ef\u4ee5\u7528\u5e2e\u4e08\u592b\u7684\u540d\u4e49\uff0c\u6355\u6740\u6b79\u5f92\u3002\u201d \u3000\u3000\u201c\u6211\u7684\u8981\u6c42\u592a\u591a\u4e86\uff0c\u201d\u60dc\u7389\u7b11\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u6211\u5fc3\u4e2d\u5f88\u660e\u767d\uff0c\u53ea\u662f\u60f3\u6545\u610f\u7f20\u7f20\u4f60\u7f62\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427\uff01\u8fd9\u91cc\u867d\u7136\u5f88\u5e7d\u9759\uff0c\u4f46\u56db\u91ce\u7a7a\u65f7\uff0c\u4eba\u8e2a\u7a00\u5c11\uff0c\u6b63\u662f\u6740\u624b\u51fa\u6ca1\u7684\u597d \u5730\u65b9\u3002\u201d \u3000\u3000\u201c\u6211\u5012\u5e0c\u671b\u4ed6\u4eec\u518d\u6765\u4e00\u6b21\u88ad\u6740\uff01\u201d\u60dc\u7389\u9053\uff1a\u201c\u6211\u8981\u5168\u529b\u65bd\u4e3a\uff0c\u8ba9\u4f60\u770b\u770b\u8fd8\u672a\u8fc7\u95e8\u5ab3\u5987\u7684 \u624b\u6bb5\u3002\u8fc7\u4e86\u95e8\u540e\uff0c\u4f60\u6562\u4e0d\u6562\u6b3a\u4fae\u6211\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u6562\uff0c\u4e5f\u4e0d\u5fcd\u2026\u2026\u201d \u3000\u3000\u201c\u8bf4\u7684\u8ba9\u6211\u597d\u5fc3\u75bc\uff0c\u5feb\u8ba9\u6211\u53d8\u6210\u6076\u5a18\u5b50\u4e86\u3002\u201d\u60dc\u7389\u7275\u7740\u675c\u671b\u6708\u7684\u624b\uff0c\u7ad9\u8d77\u8eab\u5b50\u9053\uff1a\u201c\u5230 \u54ea\u53bb\u5462\uff1f\u4e0d\u80fd\u518d\u56de\u56db\u51e4\u697c\u7684\u6587\u9601\uff0c\u6211\u5df2\u7ecf\u65e0\u5bb6\u53ef\u5f52\u4e86\u3002\u201d \u3000\u3000\u201c\u5230\u6211\u4f4f\u7684\u5730\u65b9\u5c06\u5c31\u4e00\u4e0b\uff0c\u6d17\u8fc7\u6fa1\uff0c\u6362\u4ef6\u8863\u670d\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4e00\u8d77\u53bb\u626c\u5dde\u5e9c\u8859\uff0c\u89c1\u89c1 \u603b\u6355\u5934\u548c\u626c\u5dde\u77e5\u5e9c\u738b\u5927\u4eba\uff0c\u4e5f\u5f88\u53ef\u80fd\u89c1\u5230\u4e2d\u5dde\u5434\u94c1\u5cf0\u3001\u957f\u5b89\u4e8e\u627f\u5fd7\u3001\u5173\u4e1c\u5c91\u5578\u864e\u4e09\u5927\u540d\u6355\uff0c \u4ed6\u4eec\u662f\u6211\u7684\u597d\u670b\u53cb\uff0c\u591a\u6b21\u7684\u5408\u529b\u529e\u6848\uff0c\u5e76\u80a9\u6b7c\u654c\uff0c\u53ef\u7b97\u662f\u8fc7\u547d\u7684\u4ea4\u60c5\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u6709\u4e09\u4e0d\u7f3a \u70b9\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u7f3a\u70b9\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u53ef\u548c\u6211\u6709\u5173\u7cfb\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u82e6\u7b11\u4e00\u4e0b\u9053\uff1a\u201c\u5982\u662f\u6ca1\u5173\u7cfb\uff0c\u6211\u5c31\u4e0d\u4f1a\u8bf4\u4e86\uff0c\u4ed6\u4eec\u7684\u7f3a\u70b9\u662f\u53e3\u6ca1\u906e\u62e6\uff0c\u60f3\u5230\u4ec0\u4e48 \u5c31\u8bf4\u4ec0\u4e48\u3002\u4f60\u7f8e\u8273\u52a8\u4eba\uff0c\u4ed6\u4eec\u4f1a\u5343\u65b9\u767e\u8ba1\u53d6\u7b11\u4f60\uff0c\u4f60\u8d8a\u5bb3\u7f9e\uff0c\u4ed6\u4eec\u5c31\u8d8a\u8bf4\u7684\u9ad8\u5174\uff0c\u5e0c\u671b\u4e0d\u8981 \u771f\u7684\u8bf4\u607c\u4e86\u4f60\u3002\u201d \u3000\u3000\u60dc\u7389\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u6015\uff0c\u4f46\u4e0d\u77e5\u6211\u80fd\u4e0d\u80fd\u53cd\u5507\u76f8\u8ba5\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u53ef\u4ee5\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4f46\u8981\u6587\u96c5\u4e00\u4e9b\uff0c\u662f\u8981\u8868\u73b0\u4f60\u7684\u6e0a\u535a\u3001\u5b66\u95ee\uff0c\u4e0d\u8981\u53e3\u820c\u4f24 \u4eba\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u201d\u4e3e\u6b65\u5411\u524d\u884c\u53bb\uff0c\u540c\u65f6\u653e\u5f00\u4e86\u675c\u671b\u6708\u7684\u624b\u3002 \u3000\u3000\u5979\u5fc3\u4e2d\u660e\u767d\uff0c\u73b0\u5728\u4ecd\u5728\u9669\u5730\uff0c\u968f\u65f6\u4f1a\u6709\u654c\u4eba\u7531\u6697\u4e2d\u7a81\u7136\u88ad\u51fb\uff0c\u7275\u7740\u624b\u8d70\uff0c\u4f1a\u59a8\u788d\u8fd0\u8f6c\u901f \u5ea6\uff0c\u4e5f\u4f1a\u5e26\u6765\u5371\u9669\u3002 \u3000\u3000\u675c\u671b\u6708\u843d\u540e\u4e03\u5c3a\uff0c\u4fdd\u6301\u4e86\u5e94\u53d8\u7684\u8ddd\u79bb\u3002 \u3000\u3000------------- \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c \u5341 \u7ae0 \u3000\u3000\u4e00\u8def\u5e73\u5b89\uff0c\u56de\u5230\u675c\u671b\u6708\u4f4f\u7684\u5730\u65b9\uff0c\u5973\u4e3b\u4eba\u770b\u4ed6\u5e26\u4e2a\u5982\u82b1\u4f3c\u7389\u7684\u5927\u59d1\u5a18\u56de\u6765\uff0c\u5fcd\u4e0d\u4f4f\u591a\u671b \u4e86\u4e00\u773c\uff0c\u4f46\u5374\u6ca1\u6709\u8bf4\u8bdd\uff0c\u53ea\u662f\u628a\u996d\u83dc\u52a0\u6210\u53cc\u4efd\uff0c\u4e5f\u70e7\u4e86\u4e24\u76c6\u6d17\u6fa1\u6c34\u3002 \u3000\u3000\u675c\u671b\u6708\u7a81\u7136\u56de\u8eab\u62b1\u4f4f\u4e86\u60dc\u7389\uff0c\u8fd8\u771f\u628a\u60dc\u7389\u5413\u4e00\u8df3\uff0c\u5979\u867d\u7136\u5728\u98ce\u6708\u573a\u4e2d\u6df7\uff0c\u4f46\u90a3\u662f\u5929\u4e0b\u6700 \u6587\u96c5\u7684\u98ce\u6708\u573a\u6240\uff0c\u591a\u5e74\u6765\u522b\u8bf4\u548c\u4eba\u4e0a\u5e8a\u4e86\uff0c\u8fde\u62b1\u4e5f\u5f88\u5c11\u88ab\u7537\u4eba\u62b1\u8fc7\u3002\u60dc\u7389\u6ca1\u6709\u53cd\u6297\uff0c\u53ea\u662f\u4e00 \u5f20\u8138\u7ea2\u7684\u53d1\u70eb\uff0c\u90a3\u662f\u5a07\u7f9e\u548c\u7d27\u5f20\uff0c\u53e3\u4e2d\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u4e0d\u884c\u554a\uff01\u4e00\u5b9a\u8981\uff0c\u7b49\u5230\u591c\u91cc\u518d\u8bf4\uff0c \u5927\u767d\u5929\uff0c\u6211\u4e0d\u6562\u54ea\uff01\u201d \u3000\u3000\u5979\u7d27\u54ac\u94f6\u7259\u5b66\u6e29\u67d4\uff0c\u81ca\u7684\u8fde\u8033\u6839\u4e5f\u7ea2\u4e86\u8d77\u6765\uff0c\u675c\u671b\u6708\u7b11\u4e00\u7b11\uff0c\u4e00\u53ea\u624b\u5728\u59d1\u5a18\u5168\u8eab\u6e38\u52a8\u8d77 \u6765\uff0c\u7531\u53cc\u80a9\u5230\u67f3\u8170\uff0c\u8fde\u4e00\u53cc\u811a\u4e5f\u4e0d\u653e\u8fc7\uff0c\u59d1\u5a18\u5927\u8db3\uff0c\u4f46\u5374\u5f88\u5747\u5300\u3001\u7ea4\u5de7\u3002\u60dc\u7389\u6ca1\u6709\u6323\u52a8\uff0c\u4f46 \u5374\u5168\u8eab\u5728\u5fae\u5fae\u53d1\u6296\uff0c\u53cc\u76ee\u5fae\u95ed\uff0c\u4e5f\u6d41\u4e0b\u4e86\u6cea\u6c34\u3002 \u3000\u3000\u675c\u671b\u6708\u5403\u4e86\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u73a9\u7b11\u5f00\u8fc7\u5934\u4e86\uff0c\u518d\u4e0d\u9002\u53ef\u800c\u6b62\uff0c\u771f\u8981\u5f15\u8d77\u8bef\u4f1a\uff0c\u5c31\u7b97\u4e0d\u5f53\u573a\u7ffb \u8138\uff0c\u4e5f\u4f1a\u5728\u5fc3\u91cc\uff0c\u7559\u4e0b\u4e86\u4e00\u4e2a\u4f24\u75d5\u3002 \u3000\u3000\u7acb\u523b\u505c\u4e0b\u624b\uff0c\u7f13\u7f13\u63a8\u5f00\u60dc\u7389\u7684\u8eab\u8eaf\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5148\u53bb\u6d17\u6fa1\uff0c\u6211\u4e0a\u8857\u66ff\u4f60\u9009\u4e24\u5957\u8863\u670d\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u5728\u91cf\u6211\u7684\u8eab\u4e0a\u7684\u5c3a\u5bf8\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u59be\u8eab\u541b\u5e38\u62b1\uff0c\u5c3a\u5bf8\u81ea\u601d\u91cf\uff01\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u4ece\u6765\u6ca1\u6709\u62b1\u8fc7\u4f60\uff0c\u5982\u4f55\u60f3\u5f97\u51fa\u5c3a\u5bf8 \u591a\u5c11\uff1f\u73b0\u5728\u6211\u662f\u5168\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u60dc\u7389\u7b11\u4e86\uff0c\u7b11\u7684\u5982\u82b1\u76db\u653e\uff0c\u7a81\u7136\u62b1\u7d27\u4e86\u675c\u671b\u6708\uff0c\u9053\uff1a\u201c\u6211\u8ba4\u4e3a\u4f60\u8981\u5f3a\u66b4\u6211\uff0c\u6211\u6709\u70b9\u7d27\u5f20\u3001 \u5bb3\u6015\uff0c\u4f46\u6211\u4e0d\u60f3\u53cd\u6297\u4f60\uff0c\u65e2\u662f\u90ce\u6b32\u5ff5\u96be\u7981\uff0c\u6211\u51c6\u5907\u9006\u6765\u987a\u53d7\u4e86\u3002\u8c01\u77e5\u9053\u4f60\u5728\u91cf\u5c3a\u5bf8\u5566\uff01\u201d\u6162 \u6162\u5730\u9001\u4e0a\u4e86\u4e24\u7247\u6a31\u5507\uff0c\u9053\uff1a\u201c\u541b\u5b50\u554a\uff01\u662f\u5c0f\u59b9\u8bef\u4f1a\u4f60\u4e86\uff0c\u54e5\uff0c\u6211\u9053\u6b49\u3002\u201d \u3000\u3000\u8bf4\u7684\u5f88\u542b\u7cca\uff0c\u5634\u5df4\u5835\u7d27\u4e86\uff0c\u53e3\u9f7f\u5c31\u5f88\u96be\u6e05\u695a\u3002 \u3000\u3000\u675c\u671b\u6708\u7f13\u7f13\u63a8\u5f00\u5a07\u8eaf\uff0c\u5598\u4e00\u53e3\u5927\u6c14\uff0c\u9053\uff1a\u201c\u6211\u53bb\u53bb\u5c31\u6765\uff01\u201d \u3000\u3000\u201c\u6211\u7b49\u4f60\u56de\u6765\u518d\u6d17\u6fa1\uff01\u201d\u60dc\u7389\u9053\uff1a\u201c\u603b\u4e0d\u80fd\u8ba9\u6211\u6d17\u4e86\u6fa1\uff0c\u518d\u7a7f\u4e0a\u810f\u8863\u670d\uff0c\u8fd8\u662f\u5149\u7740\u8eab\u5b50 \u7b49\u4f60\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u662f\uff01\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u4f1a\u5f88\u5feb\u8d76\u6765\uff0c\u201d\u4e00\u8f6c\u8eab\uff0c\u51fa\u95e8\u8d70\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u56de\u6765\u7684\u5f88\u5feb\uff0c\u62b1\u4e86\u4e09\u5957\u989c\u8272\u3001\u5f0f\u6837\u90fd\u4e0d\u540c\u7684\u8863\u670d\uff0c\u6843\u7ea2\u3001\u67f3\u7eff\u548c\u5929\u84dd\uff0c\u8fd8\u6709\u4e00\u4e9b \u5973\u4eba\u7528\u7684\u5185\u8863\u3001\u7761\u888d\u3001\u80ed\u8102\u82b1\u7c89\uff0c\u60dc\u7389\u68c0\u89c6\u4e86\u4e00\u904d\uff0c\u7b11\u9053\uff1a\u201c\u5f88\u7ec6\u5fc3\u554a\uff01\u4e70\u7684\u90fd\u5408\u5c0f\u59b9\u610f\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u4e70\u4e0d\u5168\uff0c\u7f3a\u4ec0\u4e48\uff0c\u6d17\u5b8c\u6fa1\u3001\u5403\u8fc7\u996d\uff0c\u518d\u53bb\u4e70\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4eca\u5929\u4e5f\u8981\u79bb\u5f00\u8fd9 \u91cc\u4e86\u3002\u201d \u3000\u3000\u201c\u8981\u4f4f\u5165\u626c\u5dde\u5e9c\u7684\u8859\u95e8\u91cc\u4e48\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u90a3\u91cc\u8859\u5f79\u4f17\u591a\uff0c\u65b9\u4fbf\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u738b\u77e5\u5e9c\u4e00\u5b9a\u4f1a\u6709\u5f88\u597d\u7684\u5b89\u6392\uff0c\u201d\u675c\u671b\u6708\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u662f\u7528\u4e0d\u7740\u54b1\u4eec\u8d39\u5fc3\u4e86\u3002\u201d \u3000\u3000\u6d17\u8fc7\u6fa1\uff0c\u60dc\u7389\u7a7f\u4e0a\u4e86\u90a3\u5957\u67f3\u7eff\u8863\u886b\uff0c\u914d\u7684\u662f\u67f3\u7eff\u957f\u88e4\uff0c\u957f\u53d1\u4ecd\u624e\u4e24\u6761\u5c0f\u8fab\u5b50\uff0c\u4e0d\u7b97\u662f\u75be \u670d\u52b2\u88c5\uff0c\u4f46\u884c\u52a8\u65b9\u9762\u5df2\u4fd0\u843d\u4e0d\u5c11\uff0c\u4e5f\u7b97\u662f\u65b9\u4fbf\u6253\u67b6\u7684\u88c5\u675f\u4e86\u3002 \u3000\u3000\u5403\u8fc7\u996d\uff0c\u60dc\u7389\u53c8\u6dfb\u7f6e\u4e86\u4e24\u5957\u9ed1\u8272\u75be\u670d\u52b2\u88c5\uff0c\u624d\u76f4\u5954\u626c\u5dde\u5e9c\u8859\u3002 \u3000\u3000\u626c\u5dde\u5e9c\u526f\u603b\u6355\u5934\u738b\u575a\uff0c\u52b2\u88c5\u4f69\u5200\uff0c\u65e9\u5df2\u5728\u5e9c\u8859\u5916\u9762\u7b49\u5019\u3002\u5e26\u7740\u4e24\u4eba\u7531\u4fa7\u95e8\u8fdb\u5165\u540e\u9662\u82b1\u5385\uff0c \u5385\u4e2d\u5df2\u5750\u4e86\u4e0d\u5c11\u7684\u4eba\uff0c\u6b63\u4e2d\u5ea7\u4f4d\u4e0a\uff0c\u5750\u4e00\u4e2a\u5999\u9f84\u5c11\u5973\uff0c\u4e00\u8eab\u6de1\u9752\u886b\u88e4\uff0c\u65e2\u672a\u7ee3\u82b1\uff0c\u4e5f\u672a\u9576\u8fb9\uff0c \u662f\u975e\u5e38\u6734\u7d20\u7684\u8863\u7740\uff0c\u4f46\u5374\u63a9\u4e0d\u4f4f\u56fd\u8272\u5929\u9999\uff0c\u6b63\u662f\u6551\u8fc7\u60dc\u7389\u7684\u90a2\u90e8\u603b\u6355\u5934\u7a0b\u5c0f\u8776\u3002 \u3000\u3000\u5728\u5979\u8eab\u540e\uff0c\u7ad9\u7740\u4e24\u4e2a\u5c0f\u7f8e\u5973\uff0c\u4e8c\u5341\u5de6\u53f3\u5e74\u7eaa\uff0c\u8eab\u7740\u6de1\u9752\u52b2\u88c5\uff0c\u4f69\u7740\u957f\u5251\uff0c\u662f\u90a3\u79cd\u7acb\u523b\u5c31 \u53ef\u4ee5\u62d4\u5251\u51fa\u624b\u7684\u88c5\u675f\u3002 \u3000\u3000\u675c\u671b\u6708\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u59d1\u5a18\u662f\u603b\u6355\u5934\u7684\u8d34\u8eab\u4ece\u5c5e\uff0c\u5de6\u8fb9\u7684\u662f\u5c0f\u6587\uff0c\u53f3\u9762\u7684\u662f\u5c0f\u96c5\uff0c\u53ef \u522b\u770b\u53ea\u662f\u5a07\u5a07\u67d4\u67d4\u7684\u5c0f\u59d1\u5a18\uff0c\u5374\u662f\u5211\u90e8\u603b\u53f8\u7684\u54fc\u54c8\u4e8c\u5c06\uff0c\u5251\u672f\u7cbe\u6e5b\uff0c\u8c6a\u52c7\u65e0\u5339\u3002\u201d \u3000\u3000\u60dc\u7389\u70b9\u70b9\u5934\uff0c\u671b\u7740\u5c0f\u6587\u3001\u5c0f\u96c5\uff0c\u70b9\u5934\u5fae\u7b11\uff0c\u5c0f\u6587\u548c\u5c0f\u96c5\u8fd8\u62a5\u5979\u4e00\u4e2a\u542b\u9996\u5fae\u7b11\uff0c\u5fc3\u4e2d\u4e5f\u5728 \u6697\u6697\u79f0\u8d5e\uff0c\u597d\u4e00\u4e2a\u6807\u81f4\u59d1\u5a18\uff0c\u675c\u671b\u6708\u8273\u798f\u4e0d\u6d45\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u82b1\u5385\u4e2d\u6240\u6709\u7684\u4eba\u90fd\u5728\u770b\u5979\uff0c\u5305\u62ec\u626c\u5dde\u77e5\u5e9c\u738b\u5927\u4eba\u3002\u9760\u5de6\u9762\u4e00\u6392\u5ea7\u4f4d\u4e0a\u5750\u4e86\u4e09\u4e2a \u4eba\uff0c\u4e00\u4e2a\u866c\u9aef\u5927\u6c49\uff0c\u9ed1\u8863\u9ed1\u62ab\u98ce\uff0c\u624b\u4e2d\u867d\u7136\u672a\u89c1\u5175\u5203\uff0c\u4f46\u9690\u9690\u53ef\u89c1\u62ab\u98ce\u5185\u6302\u4e86\u4e00\u6392\u94f6\u5149\u95ea\u95ea \u7684\u5bbd\u5203\u5c0f\u65a7\uff0c\u201c\u5173\u4e1c\u540d\u6355\u5c91\u5578\u864e\uff0c\u4e00\u638c\u788e\u7891\u77f3\uff0c\u98de\u65a7\u5c60\u72ee\u864e\u3002\u201d\u675c\u671b\u6708\u4f4e\u58f0\u5730\u544a\u8bc9\u60dc\u7389\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u9ed1\u9762\u4fee\u8eaf\uff0c\u4e00\u88ad\u957f\u886b\uff0c\u675c\u671b\u4f4e\u58f0\u9053\uff1a\u201c\u4e2d\u5dde\u540d\u6355\u5434\u94c1\u5cf0\uff0c\u8fce\u95e8\u4e09\u4e0d\u8fc7\uff0c\u4e00\u7b14\u9547\u4e2d \u5dde\uff0c\u6253\u7684\u4e00\u624b\u597d\u9556\u6cd5\u3002\u201d \u3000\u3000\u7b2c\u4e09\u4e2a\u9762\u5982\u53e4\u94dc\uff0c\u6d53\u7709\u6717\u76ee\uff0c\u4e09\u5341\u56db\u4e94\u7684\u5e74\u7eaa\uff0c\u8170\u95f4\u5fae\u5fae\u7a81\u8d77\uff0c\u663e\u7136\u7528\u7684\u8f6f\u5175\u5203\uff0c\u5bb6\u4f19 \u5c31\u56f4\u5728\u8170\u4e2d\u3002\u675c\u671b\u6708\u4f4e\u58f0\u4ecb\u7ecd\u9053\uff1a\u201c\u957f\u5b89\u540d\u6355\u4e8e\u627f\u5fd7\uff0c\u5200\u51fa\u5982\u95ea\u7535\uff0c\u5bd2\u8292\u7834\u957f\u7a7a\uff0c\u662f\u4f4d\u7528\u5200 \u7684\u9ad8\u624b\u3002\u201d \u3000\u3000\u60dc\u7389\u5bf9\u4e09\u4eba\u70b9\u5934\u5fae\u7b11\uff0c\u4e09\u4e2a\u4eba\u4e5f\u5728\u770b\u7740\u60dc\u7389\u7b11\uff0c\u53ea\u662f\u7b11\u7684\u5f88\u66a7\u6627\uff0c\u7751\u4e0a\u4e5f\u6709\u7740\u4e0d\u540c\u8868\u60c5\uff0c \u60dc\u7389\u5fc3\u4e2d\u5f88\u660e\u767d\uff0c\u788d\u4e8e\u603b\u6355\u5934\u548c\u738b\u77e5\u5e9c\u5728\u5750\uff0c\u4e0d\u9002\u5408\u4ed6\u4eec\u5f00\u53e3\uff0c\u4e00\u65e6\u6613\u5730\u800c\u5904\uff0c\u8fd9\u4e09\u4eba\u53e3\u820c \u4e0a\u7684\u7280\u5229\u82b1\u4fcf\uff0c\u5fc5\u7136\u62db\u67b6\u56f0\u96be\uff0c\u5c31\u662f\u73b0\u5728\uff0c\u90a3\u4efd\u66a7\u6627\u7684\u5fae\u7b11\uff0c\u5c31\u8ba9\u60dc\u7389\u6709\u70b9\u7a98\u8feb\u7684\u611f\u89c9\uff0c\u5e78 \u597d\u675c\u671b\u6708\u65e9\u5df2\u63d0\u9192\u8fc7\u5979\uff0c\u6709\u4e86\u5fc3\u7406\u4e0a\u7684\u51c6\u5907\u3002 \u3000\u3000\u201c\u60dc\u7389\u59d1\u5a18\uff0c\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6b22\u8fce\u4f60\u52a0\u5165\u5211\u90e8\u6355\u5feb\u9635\u5bb9\uff0c\u548c\u5929\u4e0b\u51f6\u4eba\u4e3a\u654c\u3002\u201d \u3000\u3000\u60dc\u7389\u76c8\u76c8\u7ad9\u8d77\uff0c\u9053\uff1a\u201c\u5148\u8c22\u603b\u6355\u5934\u7684\u6551\u547d\u4e4b\u6069\uff0c\u518d\u8c22\u63d0\u643a\u4e4b\u5fb7\uff0c\u4f46\u6709\u6240\u547d\uff0c\u60dc\u7389\u5f53\u5168\u529b \u4ee5\u8d74\uff0c\u6b7b\u800c\u65e0\u6094\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u76ee\u4e0b\u6c5f\u5357\u5211\u6355\u5206\u53f8\uff0c\u6b63\u503c\u9700\u4eba\u624b\u4e4b\u9645\uff0c\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5c31\u6d3e\u4f60\u6682\u4e3a\u6c5f\u5357\u5206\u53f8\u7684\u526f \u53f8\u4e3b\uff0c\u5e2e\u52a9\u675c\u671b\u6708\u6355\u76d7\u3001\u7f09\u51f6\uff0c\u5f25\u5e73\u4e71\u6e90\uff0c\u4ee5\u5b89\u4e07\u6c11\u3002\u201d \u3000\u3000\u60dc\u7389\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u60dc\u7389\u5bf8\u529f\u672a\u7acb\uff0c\u600e\u6562\u62c5\u6b64\u91cd\u4efb\uff1f\u201d \u3000\u3000\u201c\u6c5f\u5357\u7eb7\u4e71\uff0c\u7fa4\u9b54\u4e71\u821e\uff0c\u5982\u4e0d\u80fd\u53ca\u65f6\u5f25\u5e73\uff0c\u4e00\u65e6\u661f\u706b\u71ce\u539f\uff0c\u82e6\u7684\u662f\u4e07\u6c11\u82cd\u751f\u3002\u6b64\u65f6\u7528\u4eba\uff0c \u9996\u91cd\u624d\u827a\uff0c\u5211\u6355\u5de5\u4f5c\uff0c\u975e\u6bd4\u4e00\u822c\uff0c\u5200\u4e0a\u8214\u8840\uff0c\u5251\u5e95\u6c42\u751f\uff0c\u968f\u65f6\u53ef\u80fd\u4e3a\u5de5\u4f5c\u635f\u8eaf\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u5df2\u89c1\u8bc6\u8fc7\u4f60\u6b66\u529f\uff0c\u6280\u827a\u51fa\u4f17\u4e0d\u51e1\uff0c\u65e2\u5165\u5211\u6355\u884c\u5217\uff0c\u5c31\u5f97\u4ed8\u51fa\u727a\u7272\uff0c\u804c\u4f4d \u6108\u91cd\uff0c\u4ed8\u51fa\u6108\u591a\uff0c\u8c2d\u59d1\u5a18\u5c31\u4e0d\u7528\u518d\u63a8\u8f9e\u4e86\u3002\u201d \u3000\u3000\u60dc\u7389\u5446\u4e86\u4e00\u5446\uff0c\u5fd6\u9053\uff1a\u5389\u5bb3\u5440\uff01\u65e9\u628a\u6211\u6478\u6e05\u695a\u4e86\u3002\u675c\u5927\u54e5\u6ca1\u548c\u5979\u89c1\u8fc7\u9762\uff0c\u8fd9\u4e9b\u4e8b\uff0c\u90fd\u662f \u5979\u67e5\u51fa\u6765\u7684\uff0c\u600e\u4e48\u67e5\u5440\uff01\u52ff\u602a\u675c\u671b\u6708\u5bf9\u5979\u63a8\u8bb8\u6709\u52a0\uff0c\u8fd9\u4e2a\u59d1\u5a18\u554a\uff01\u771f\u6709\u70b9\u795e\u5947\u83ab\u6d4b\uff0c\u4e0d\u5f97\u4e0d \u53eb\u4eba\u4f69\u670d\u554a\uff01\u5fae\u5fae\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u60dc\u7389\u9075\u547d\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u4f4e\u58f0\u9053\uff1a\u201c\u884c\u554a\uff0c\u60dc\u7389\uff0c\u5df2\u7ecf\u6709\u526f\u53f8\u4e3b\u7684\u67b6\u52bf\u4e86\u3002\u201d \u3000\u3000\u60dc\u7389\u7b11\u4e00\u7b11\uff0c\u6ca1\u6709\u56de\u7b54\uff0c\u5fc3\u4e2d\u5374\u6709\u7740\u4e00\u79cd\u5929\u6daf\u9047\u77e5\u5df1\u7684\u611f\u89c9\uff0c\u4e5f\u5b8c\u5168\u88ab\u7a0b\u5c0f\u8776\u5f81\u670d\u4e86\uff01 \u3000\u3000\u738b\u77e5\u5e9c\u7a81\u7136\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u5211\u90e8\u7684\u7a0b\u603b\u6355\u5934\uff0c\u4e3a\u4fa6\u529e\u5e9c\u8f96\u5730\u4e2d\u4e00\u4ef6\u795e\u79d8\u7684\u6740\u4eba\u8840\u6848\uff0c\u5343 \u91cc\u98ce\u5c18\u4e0b\u626c\u5dde\uff0c\u5a01\u632f\u4e00\u65b9\u7684\u56db\u5927\u540d\u6355\uff0c\u4e5f\u540c\u65f6\u4f1a\u805a\u4e8e\u6b64\uff0c\u4e3e\u56fd\u5211\u6355\u7cbe\u82f1\u5927\u4f1a\u5e08\uff0c\u5c11\u537f\u4f55\u5e78\uff0c \u606d\u9022\u5176\u76db\uff0c\u5982\u679c\u9700\u8981\u626c\u5dde\u5e9c\u652f\u63f4\u7684\u5730\u65b9\uff0c\u53ea\u7ba1\u5f00\u53e3\uff0c\u5c11\u537f\u7edd\u4e0d\u63a8\u8f9e\u3002\u4eca\u665a\u5148\u5907\u6c34\u9152\u4e00\u676f\uff0c\u804a \u8868\u6b22\u8fce\u4e4b\u610f\u3002\u201d \u3000\u3000\u8bf4\u662f\u6c34\u9152\u4e00\u676f\uff0c\u4f46\u5bb4\u5e2d\u5374\u975e\u5e38\u4e30\u5bcc\uff0c\u5c91\u5578\u864e\u3001\u4e8e\u627f\u5fd7\u3001\u5434\u94c1\u5cf0\uff0c\u90fd\u5403\u7684\u9152\u8db3\u996d\u9971\u3002\u738b\u5c11 \u537f\u867d\u662f\u4e2a\u6587\u5f31\u4e66\u751f\uff0c\u4f46\u9152\u91cf\u5f88\u597d\uff0c\u8fde\u6253\u4e86\u4e09\u4e2a\u901a\u5173\u3002\u53ea\u4e0d\u8fc7\uff0c\u56db\u4f4d\u59d1\u5a18\u5374\u8868\u73b0\u7684\u5f88\u6587\u96c5\uff0c\u8f7b \u542f\u6a31\u5507\u559d\u4e00\u53e3\uff0c\u8fd8\u7ed9\u656c\u9152\u4eba\u7684\u662f\u4e00\u4e2a\u5a07\u5a9a\u7684\u5fae\u7b11\uff0c\u8fd9\u5c31\u4f7f\u5f97\u656c\u9152\u4eba\uff0c\u4e0d\u597d\u5f3a\u903c\u5979\u676f\u5230\u9152\u5e72\uff0c \u4e00\u676f\u6362\u4e00\u53e3\uff0c\u4e5f\u5c31\u53ea\u597d\u8ba4\u4e86\uff0c\u5c31\u7b97\u4e00\u676f\u6362\u4e00\u53e3\u5427\uff0c\u7b97\u4e00\u7b97\u559d\u7684\u4e5f\u4e0d\u7b97\u592a\u5c11\u3002\u5c0f\u6587\u3001\u5c0f\u96c5\u8138\u90fd \u559d\u7ea2\u4e86\u3002 \u3000\u3000\u4e00\u5e2d\u5bb4\u7f62\uff0c\u738b\u5c11\u537f\u7acb\u523b\u544a\u9000\uff0c\u4ed6\u660e\u767d\uff0c\u4e94\u5927\u6355\u5934\u805a\u4f1a\u4e8e\u6b64\uff0c\u4e00\u5b9a\u6709\u516c\u4e8b\u8981\u8c08\uff0c\u4ed6\u8fd9\u4e2a\u77e5 \u5e9c\u4e0d\u79bb\u5f00\uff0c\u5b9e\u6709\u70b9\u4e0d\u592a\u65b9\u4fbf\u3002 \u3000\u3000\u9001\u8d70\u4e86\u4e3b\u4eba\u4e4b\u540e\uff0c\u7a0b\u5c0f\u8776\u968f\u624b\u63a9\u4e0a\u4e86\u5385\u95e8\uff0c\u90a3\u662f\u8868\u793a\uff0c\u6709\u516c\u4e8b\u8981\u5546\u91cf\uff0c\u95f2\u6742\u4eba\u7b49\uff0c\u4e0d\u5f97 \u63a5\u8fd1\u3002\u738b\u575a\u9886\u4f1a\u5230\u8fd9\u4e2a\u793a\u610f\uff0c\u6325\u6325\u624b\uff0c\u5e03\u5b88\u82b1\u5385\u7684\u6355\u5feb\u4eec\uff0c\u5411\u540e\u64a4\u9000\u4e86\u4e00\u4e08\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u6362\u4e0a\u4e86\u4e00\u526f\u7b11\u8138\uff0c\u9053\uff1a\u201c\u675c\u5144\uff0c\u5148\u8bf4\u8bf4\u626c\u5dde\u7684\u5f62\u52bf\u3002\u201d \u3000\u3000\u60dc\u7389\u53c8\u591a\u4e86\u4e00\u5c42\u4e86\u89e3\uff0c\u603b\u6355\u5934\u548c\u56db\u5927\u540d\u6355\u4e4b\u95f4\uff0c\u516c\u4e8b\u4e0a\u6709\u804c\u4f4d\u4e4b\u522b\uff0c\u4f46\u79c1\u4e0b\u91cc\u5374\u662f\u5982\u5144 \u548c\u59b9\uff0c\u60c5\u611f\u6df1\u539a\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u671b\u6708\u67e5\u5f97\u7684\u5185\u60c5\uff0c\u53ea\u6015\u4e0d\u5982\u603b\u6355\u5934\u591a\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u5ba2\u6c14\uff0c\u6211\u867d\u7136\u63a2\u8bbf\u51fa\u4e00\u4e9b\u8baf\u606f\uff0c\u4f46\u53ea\u4fa7\u91cd\u4e00\u4e24\u91cd\u70b9\uff0c\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e5f\u90fd\u672a\u7ecf\u6c42 \u8bc1\u8fc7\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u559d\u53e3\u8336\uff0c\u6574\u7406\u4e00\u4e0b\u601d\u7eea\uff0c\u628a\u7ecf\u5386\u8fc7\u7a0b\uff0c\u542c\u95fb\u6240\u5f97\uff0c\u5f88\u8be6\u5c3d\u5730\u8bf4\u4e86\u4e00\u904d\uff0c\u8fde\u8ba4\u8bc6\u60dc \u7389\u7684\u7ecf\u8fc7\uff0c\u4e5f\u4e0d\u9690\u7792\uff0c\u5b8c\u5b8c\u5168\u5168\u5730\u548c\u76d8\u7aef\u4e86\u51fa\u6765\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u542c\u5f97\u7b11\u5bb9\u655b\u6536\uff0c\u4e00\u8138\u4e25\u8083\u3002\u4e09\u5927\u540d\u6355\uff0c\u4e5f\u542c\u5f97\u4e2a\u4e2a\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u4ed6\u4eec\u529e\u6848\u65e0\u6570\uff0c \u4f46\u4e5f\u4ece\u672a\u906d\u9047\u5230\u5982\u6b64\u590d\u6742\u7684\u73af\u5883\uff0c\u4f3c\u4e4e\u6c5f\u6e56\u4e0a\u5927\u534a\u7684\u9b54\u5934\uff0c\u90fd\u805a\u96c6\u5230\u626c\u5dde\u6765\u4e86\u3002\u4e00\u65f6\u95f4\u7adf\u65e0 \u4eba\u5f00\u53e3\u8bf4\u8bdd\uff0c\u4f3c\u4e4e\u90fd\u5728\u7528\u5fc3\u60f3\u554a\uff01 \u3000\u3000\u675c\u671b\u6708\u5401\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5341\u5927\u5bb6\u65cf\u8c6a\u95e8\uff0c\u5404\u62e5\u5b9e\u529b\uff0c\u5206\u5ead\u6297\u793c\uff0c\u867d\u6709\u5f3a\u5f31\u4e4b\u522b\uff0c\u4f46\u5dee\u8ddd\u4e0d \u5927\uff0c\u6700\u91cd\u8981\u7684\u662f\uff0c\u4ed6\u4eec\u5404\u85cf\u79d8\u5bc6\uff0c\u771f\u6b63\u8058\u7ea6\u7684\u5947\u4eba\u9ad8\u624b\uff0c\u90fd\u9690\u4e8e\u6697\u5904\uff0c\u6df1\u6050\u4e3a\u4eba\u4fa6\u77e5\uff0c\u52a0\u4e0a \u4ed6\u4eec\u5e9e\u5927\u7684\u8d22\u5bcc\uff0c\u5916\u7ed3\u5173\u7cfb\uff0c\u4f3c\u662f\u90fd\u6709\u738b\u516c\u5927\u81e3\u6491\u8170\uff0c\u770b\u4f3c\u62e5\u5175\u81ea\u4fdd\uff0c\u5176\u5b9e\u5404\u6000\u9b3c\u80ce\uff0c\u7b49\u673a \u4f1a\u5e76\u541e\u5bf9\u65b9\u3002 \u3000\u3000\u8868\u9762\u4e0a\u662f\u6276\u5f31\u6551\u5371\uff0c\u9aa8\u5b50\u91cc\u5374\u662f\u8fd0\u8f6c\u79c1\u76d0\uff0c\u805a\u96c6\u8d22\u5bcc\u3002\u8fd8\u505a\u4e9b\u4ec0\u4e48\u4e8b\u6ca1\u627e\u5230\u8bc1\u636e\u4e4b\u524d\uff0c \u4e0d\u4fbf\u5984\u8a00\u3002\u671b\u6708\u4e3b\u6301\u6c5f\u5357\u5211\u6355\u5206\u53f8\uff0c\u5c31\u6ca1\u6709\u6000\u7591\u8fc7\u4ed6\u4eec\uff0c\u8fd9\u6b21\u67e5\u529e\u9a6c\u516c\u5b50\u7684\u65b0\u623f\u547d\u6848\uff0c\u624d\u4e86 \u89e3\u4e00\u4e9b\u60c5\u51b5\u3002\u5f53\u7136\uff0c\u4e5f\u53ef\u80fd\u4f1a\u6709\u51e0\u5bb6\u6e05\u6b63\u81ea\u6301\uff0c\u5584\u4e8e\u7ecf\u8425\uff0c\u4e0d\u8d70\u65c1\u95e8\u4e0d\u67d3\u90aa\u98ce\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u6709\u4e9b\u590d\u6742\uff01\u8981\u5f97\u8d39\u4e00\u756a\u5de5\u592b\uff0c\u627e\u7ebf\u7d22\u4e86\u3002\u5faa\u7ebf\u8ffd\u89c5\uff0c\u4e5f\u4e0d \u96be\u67e5\u51fa\u6839\u6e90\uff0c\u8fc7\u53bb\u662f\u65e0\u4eba\u6562\u529e\uff0c\u4e5f\u65e0\u80fd\u8ffd\u67e5\uff0c\u73b0\u5728\uff0c\u5c31\u4e0d\u7528\u518d\u6709\u8fd9\u4e9b\u987e\u8651\u4e86\uff0c\u4e0d\u8bba\u4ed6\u9760\u5c71\u591a \u5927\uff0c\u5b9e\u529b\u591a\u5f3a\uff0c\u53ea\u8981\u627e\u51fa\u7f6a\u8bc1\uff0c\u5c31\u902e\u6355\u5f52\u6848\uff0c\u4f9d\u6cd5\u6cbb\u7f6a\u3002\u81f3\u4e8e\u5bf9\u6297\u4ed6\u4eec\u8c62\u517b\u7684\u4fdd\u9556\u5bb6\u5974\uff0c\u5c31 \u8981\u9760\u5404\u4f4d\u7684\u667a\u6167\u6b66\u529f\u4e86\u3002\u201d \u3000\u3000\u201c\u9664\u4e86\u5341\u5927\u8c6a\u95e8\u4e4b\u5916\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u626c\u5dde\u57ce\uff0c\u8fd8\u53d1\u73b0\u51e0\u4e2a\u795e\u79d8\u7684\u7ec4\u5408\uff0c\u5f00\u8bbe\u6587\u9601\u3001\u6b66 \u9662\u7684\u56db\u51e4\u697c\u4e3b\u80fd\u7528\u4eba\u5165\u836f\uff0c\u4e13\u4ee5\u6740\u4eba\u4e3a\u4e1a\u7684\u6740\u624b\u4e4b\u738b\uff0c\u8840\u624b\u65b9\u8f6e\uff0c\u4e5f\u5e26\u7740\u5927\u6279\u6740\u624b\uff0c\u5728\u626c\u5dde \u6d3b\u52a8\u3002\u5176\u4ed6\u7684\u6f5c\u6765\u626c\u5dde\uff0c\u89ca\u89ce\u91d1\u5bb6\u4e09\u5b9d\u7684\u9ad8\u624b\uff0c\u4e0d\u77e5\u51e1\u51e0\uff0c\u4ed6\u4eec\u6216\u5355\u6216\u53cc\uff0c\u6216\u4e09\u4e94\u4e2a\u6210\u7684\u4e00 \u7ec4\uff0c\u4eba\u5c11\u6b66\u529f\u9ad8\uff0c\u884c\u52a8\u5982\u98d8\u98ce\uff0c\u5ffd\u9690\u5ffd\u73b0\uff0c\u96be\u4ee5\u6349\u6478\uff0c\u53ea\u6709\u5728\u91d1\u5bb6\u5b85\u9662\u4e2d\uff0c\u7b49\u5f85\u4ed6\u4eec\u51fa\u73b0 \u4e86\u2026\u2026\u201d\u8bdd\u81f3\u6b64\u5904\uff0c\u7a81\u7136\u4f4f\u53e3\uff0c\u671b\u7740\u7a0b\u5c0f\u8776\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7cbe\u660e\u554a\uff01\u675c\u5144\uff0c\u6211\u548c\u5c0f\u6587\u662f\u53bb\u8fc7\u91d1\u5bb6\u5b85\u9662\uff0c\u90a3\u4e00\u591c\u660e\u6708\u5728\u5929\uff0c \u4e5f\u4f1a\u8fc7\u91d1\u5b85\u4e2d\u4e00\u4e9b\u9ad8\u624b\uff0c\u8d3e\u82f1\u6280\u827a\u535a\u6742\uff0c\u662f\u4e00\u4e2a\u5f88\u7279\u6b8a\u7684\u4eba\u7269\uff0c\u5bd2\u5c71\u56db\u5200\u4e5f\u7b97\u5f97\u6c5f\u6e56\u4e2d\u4e00\u6d41 \u9ad8\u624b\uff01\u201d \u3000\u3000\u201c\u4f46\u4ed6\u4eec\u90fd\u6298\u5728\u4e86\u603b\u6355\u5934\u548c\u5c0f\u6587\u59d1\u5a18\u7684\u5251\u4e0b\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u90a3\u4e00\u6218\u9707\u6151\u4e86\u5168\u573a\uff0c\u4e5f\u632b \u4e86\u8d3e\u82f1\u7684\u9510\u6c14\u3002\u201d \u3000\u3000\u5c0f\u6587\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u6211\u4eec\u6ca1\u6709\u4f24\u4e00\u4e2a\u4eba\u5566\uff01\u201d \u3000\u3000\u201c\u70b9\u7a74\u5236\u654c\uff0c\u6482\u5012\u5f53\u573a\uff0c\u6bd4\u4f24\u4eba\u7684\u632f\u64bc\u4e4b\u529b\u66f4\u5927\uff0c\u8fd9\u6d88\u606f\u6015\u5df2\u4f20\u904d\u626c\u5dde\u57ce\u4e86\uff0c\u4e24\u4e2a\u7a81\u7136 \u51fa\u73b0\u7684\u9ed1\u8863\u5251\u5ba2\uff0c\u5251\u827a\u7cbe\u7edd\u5947\u5e7b\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u53ea\u662f\u6df7\u6dc6\u4e00\u4e0b\u4ed6\u4eec\u7684\u8033\u76ee\u3002\u201d \u3000\u3000\u201c\u6700\u91cd\u8981\u7684\u662f\uff0c\u59d1\u5a18\u8981\u8bd5\u4e00\u4e0b\u8fd9\u5957\u5251\u6cd5\u7684\u5a01\u529b\u5982\u4f55\u3002\u201d\u5c0f\u6587\u9053\uff1a\u201c\u59d1\u5a18\u5e26\u7740\u6211\u548c\u5c0f\u96c5\u82e6 \u7ec3\u4e86\u56db\u4e2a\u6708\uff0c\u591c\u591c\u7ec3\u7684\u6c57\u6d41\u6d43\u80cc\uff0c\u4e0d\u8fc7\u82e6\u6ca1\u767d\u5403\uff0c\u6c57\u6ca1\u767d\u6d41\uff0c\u8fd9\u786e\u662f\u4e00\u5957\u597d\u5251\u6cd5\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6253\u65ad\u4e86\u5c0f\u6587\u7684\u8bf4\u8bdd\uff0c\u9053\uff1a\u201c\u542c\u8bf4\u68a6\u5e7b\u4e4b\u5200\uff0c\u4e5f\u5df2\u5230\u4e86\u626c\u5dde\uff0c\u53ef\u6709\u4e9b\u4ed6\u7684\u6d88\u606f\uff1f\u201d \u3000\u3000\u201c\u53ea\u542c\u697c\u68af\u54cd\uff0c\u4e0d\u89c1\u4eba\u4e0b\u6765\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5927\u6982\u4e00\u6708\u4e4b\u524d\uff0c\u6709\u4eba\u95ef\u5165\u4e86\u91d1\u767e\u5e74\u5bb6\uff0c\u91cd \u91cd\u5b88\u536b\uff0c\u7adf\u662f\u62e6\u4e0d\u4f4f\u4ed6\uff0c\u6240\u4ee5\uff0c\u4ed6\u89c1\u5230\u4e86\u91d1\u767e\u5e74\uff0c\u5f53\u9762\u7d22\u6c42\u4e09\u5b9d\uff0c\u91d1\u767e\u5e74\u4e0d\u80af\u5e94\u5141\uff0c\u968f\u8eab\u7684 \u4ece\u536b\u9ad8\u624b\u5c31\u548c\u90a3\u4eba\u5728\u5927\u5ba2\u5385\u4e2d\u6253\u4e86\u8d77\u6765\uff0c\u6765\u4eba\u7a7f\u4e86\u4e00\u4ef6\u9ed1\u8272\u5927\u62ab\u98ce\uff0c\u5c31\u50cf\u5c91\u80e1\u5b50\u7684\u7a7f\u7740\u6a21\u6837\uff0c \u4ed6\u5229\u7528\u90a3\u4ef6\u9ed1\u62ab\u98ce\uff0c\u53d8\u5e7b\u51fa\u7537\u5973\u4e0d\u540c\u7684\u5f62\u50cf\uff0c\u6253\u4e86\u534a\u5929\u67b6\uff0c\u7adf\u7136\u5f04\u4e0d\u6e05\u695a\u4ed6\u662f\u7537\u662f\u5973\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u65b9\u9762\u6211\u5c91\u5578\u864e\u53ef\u6ca1\u6709\u8fd9\u4e2a\u672c\u9886\uff0c\u6211\u8fd9\u4e00\u8138\u5927\u80e1\u5b50\uff0c\u4e5f\u6ca1\u6cd5\u53d8\u6210\u60dc\u7389\u59d1\u5a18\u7684\u4fcf\u6a21\u6837\u3002\u201d \u7ec8\u4e8e\u626f\u4e0a\u60dc\u7389\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8eab\u6750\u4e5f\u4e0d\u50cf\uff0c\u5c31\u7b97\u9762\u8c8c\u53ef\u4ee5\u53d8\uff0c\u8eab\u6750\u4e5f\u53d8\u4e0d\u4e86\u3002\u8d3e\u82f1\u5462\uff0c\u6709\u6ca1 \u6709\u51fa\u624b\u5bf9\u654c\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u7b11\u9053\uff1a\u201c\u8d3e\u82f1\u6ca1\u6709\u51fa\u624b\uff0c\u4ed6\u5728\u5168\u529b\u4fdd\u62a4\u626c\u5dde\u7b2c\u4e00\u5bcc\u8c6a\u91d1\u767e\u5e74\u3002\u201d \u3000\u3000\u201c\u90a3\u7ed3\u679c\u5462\uff1f\u662f\u5426\u6709\u4eba\u53d7\u4e86\u4f24\uff1f\u201d\u7a0b\u5c0f\u8776\u5fd9\u9053\uff1a\u201c\u662f\u4e0d\u662f\u5df2\u8bc1\u660e\u4e86\u4ed6\u5c31\u662f\u68a6\u5e7b\u4e4b\u5200\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u4f24\u4eba\uff0c\u53ea\u5728\u91d1\u5b85\u4f17\u591a\u9ad8\u624b\u56f4\u653b\u9632\u5b88\u4e4b\u4e0b\uff0c\u9732\u4e86\u4e00\u624b\u6765\u53bb\u81ea\u5982\u7684\u80fd\u8010\u3002\u201d\u675c\u671b\u6708\u9053\uff1a \u201c\u4e8b\u540e\uff0c\u7531\u5f53\u4eca\u5251\u672f\u540d\u5bb6\uff0c\u51fa\u8eab\u6b66\u5f53\u7684\u5929\u67ab\u9053\u957f\uff0c\u7ec4\u5408\u56db\u4f4d\u6c5f\u6e56\u4e0a\u6709\u540d\u7684\u8ffd\u8e2a\u9ad8\u624b\uff0c\u8ffd\u67e5\u4e86 \u4e03\u516b\u5929\uff0c\u5168\u65e0\u6536\u83b7\uff0c\u90a3\u4e2a\u4eba\u5316\u4f5c\u70df\u4e91\u6d88\u6563\u4e86\uff01\u201d \u3000\u3000\u201c\u675c\u5144\u7684\u770b\u6cd5\u5462\uff1f\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4ed6\u662f\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\uff1f\u201d \u3000\u3000\u201c\u5e94\u8be5\u4e0d\u662f\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5c5e\u4e0b\u8ba4\u4e3a\u6709\u4eba\u5047\u5192\u3002\u5f53\u7136\uff0c\u4e5f\u6709\u53ef\u80fd\u662f\u68a6\u5e7b\u4e4b\u5200\u7684\u5316\u8eab\u4e4b \u4e00\u3002\u201d \u3000\u3000\u957f\u5b89\u4e8e\u627f\u5fd7\uff0c\u63d2\u4e86\u5634\uff0c\u9053\uff1a\u201c\u675c\u5144\uff0c\u4ed6\u7684\u76ee\u7684\u5462\uff1f\u201d \u3000\u3000\u201c\u5f15\u86c7\u51fa\u6d1e\uff0c\u6c42\u8bc1\u4e00\u4e0b\uff0c\u4e09\u5b9d\u662f\u5426\u771f\u5728\u91d1\u767e\u5e74\u7684\u624b\u4e2d\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u91d1\u767e\u5e74\u679c\u7136\u4e0a\u4e86 \u5f53\uff0c\u4f20\u51fa\u4e86\u4e09\u5b9d\u7684\u8baf\u606f\uff0c\u4e5f\u5728\u91d1\u5b85\u4e2d\u8bbe\u4e0b\u4e86\u5341\u9762\u57cb\u4f0f\uff0c\u81f3\u4e8e\u5341\u9762\u57cb\u4f0f\u6709\u591a\u5927\u56f0\u4eba\u80fd\u529b\uff0c\u5c31\u8981 \u8bf7\u6559\u603b\u6355\u5934\u4e86\u3002\u201d \u3000\u3000\u201c\u57cb\u4f0f\u7684\u4eba\u624b\uff0c\u6709\u7740\u76f8\u5f53\u5b9e\u529b\uff0c\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5f15\u51fa\u91d1\u5bb6\u6f5c\u9690\u4e8e\u6697\u4e2d\u7684\u9ad8\u624b\u3001\u5947\u4eba\uff0c\u6050 \u4e5f\u662f\u5bf9\u65b9\u7684\u76ee\u7684\u4e4b\u4e00\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u63a8\u65ad\uff0c\u542c\u5f97\u56db\u5927\u540d\u6355\u90fd\u70b9\u5934\u4e86\u3002 \u3000\u3000\u60dc\u7389\u53c8\u53d1\u89c9\u4e00\u4ef6\u5c0f\u79d8\u5bc6\uff0c\u4e0d\u8bba\u5982\u4f55\u8be1\u79d8\u3001\u91cd\u5927\u4e8b\u4ef6\uff0c\u4f46\u4e94\u5927\u6355\u5934\u8c08\u8bba\u7684\u65b9\u5f0f\uff0c\u90fd\u5f88\u8f7b\u677e\uff0c \u56e0\u4e3a\u8c08\u7684\u65e0\u62d8\u65e0\u675f\uff0c\u624d\u80fd\u5f15\u5f97\u4eba\u4eba\u5f00\u53e3\uff0c\u53cd\u6b63\u8bf4\u9519\u4e86\uff0c\u4e5f\u65e0\u4f24\u5927\u96c5\u3002\u679c\u7136\uff0c\u5434\u94c1\u5cf0\u4e5f\u5f00\u53e3\u4e86\uff0c \u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u50cf\u662f\u7a9d\u91cc\u7ffb\u54ea\uff0c\u5341\u5927\u5bb6\u65cf\uff0c\u5728\u4e92\u76f8\u8f83\u52b2\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4e8b\u975e\u5e38\u53ef\u80fd\uff0c\u4f46\u4e5f\u6709\u5916\u529b\u4ecb\u5165\uff0c\u68a6\u5e7b\u4e4b\u5200\u5982\u679c\u672a\u5230\u626c\u5dde\uff0c\u90a3\u4e2a \u4eba\u53c8\u4f1a\u662f\u8c01\uff1f\u2026\u2026\u201d \u3000\u3000\u60dc\u7389\u7f13\u7f13\u7ad9\u8d77\uff0c\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u662f\u56db\u51e4\u697c\u4e3b\u5462\uff1f\u5979\u7528\u7684\u6740\u624b\uff0c\u5927\u90fd\u662f\u4e2d\u5e74\u5973\u4eba\uff0c\u6211\u5728\u6587\u9601 \u4e00\u5e74\u591a\uff0c\u6ca1\u6709\u89c1\u8fc7\u4ed6\u3002\u4e8b\u5b9e\u4e0a\u662f\uff0c\u5c31\u7b97\u89c1\u5230\u4e86\u4e5f\u4e0d\u8ba4\u8bc6\uff0c\u4e00\u4e2a\u5927\u7537\u4eba\uff0c\u600e\u4f1a\u559c\u6b22\u7528\u5f88\u591a\u4e2d\u5e74 \u5987\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u597d\uff01\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u6761\u5f88\u91cd\u8981\u7684\u7ebf\u7d22\u3002\u201d \u3000\u3000\u201c\u626c\u5dde\u6709\u4e00\u5ea7\u51f6\u5b85\uff0c\u540d\u53eb\u611a\u516c\u56ed\u6797\uff0c\u6570\u5341\u5e74\u65e0\u4eba\u5c45\u4f4f\uff0c\u4f46\u8fd1\u65e5\u4e2d\u5374\u6709\u4e00\u6279\u795e\u79d8\u4eba\u7269\uff0c\u6f5c \u9690\u5b85\u5185\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u8ffd\u8e2a\u4e00\u4eba\uff0c\u770b\u7740\u4ed6\u8fdb\u5165\u4e86\u611a\u516c\u56ed\u6797\uff0c\u4ed6\u4eec\u6ce8\u610f\u7684\u5730\u65b9\uff0c\u4f3c\u4ee5\u626c\u5dde \u5e9c\u8859\u4e3a\u4e3b\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u8138\u8272\u5ffd\u7136\u4e00\u53d8\uff0c\u4f46\u5979\u8b66\u89c9\u5f88\u5feb\uff0c\u7acb\u523b\u6062\u590d\u4e86\u5e38\u6001\u3002 \u3000\u3000\u4f46\u56db\u5927\u540d\u6355\u662f\u4f55\u7b49\u7684\u4eba\u7269\uff0c\u4e2a\u4e2a\u76ee\u5149\u9510\u5229\uff0c\u6d1e\u5bdf\u7ec6\u81f4\uff0c\u867d\u53ea\u6709\u5239\u90a3\u65f6\u95f4\uff0c\u4f46\u56db\u4e2a\u4eba\u90fd\u770b \u5230\u4e86\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u60dc\u7389\u4e5f\u770b\u5230\u4e86\u3002 \u3000\u3000\u56db\u5927\u540d\u6355\u987f\u7136\u6709\u4e00\u79cd\u604d\u7136\u5927\u609f\u7684\u611f\u89c9\uff0c\u7a0b\u5c0f\u8776\u5343\u91cc\u4e0b\u626c\u5dde\uff0c\u5e26\u6765\u4e86\u5211\u90e8\u4e2d\u5927\u90e8\u7cbe\u9510\uff0c\u4e0d \u5168\u662f\u4e3a\u4e86\u9a6c\u516c\u5b50\u7684\u65b0\u623f\u8840\u6848\uff0c\u53e6\u5916\u8fd8\u6709\u7740\u91cd\u5927\u7684\u76ee\u7684\uff0c\u96be\u9053\u4e3a\u4e86\u626c\u5dde\u77e5\u5e9c\u738b\u5c11\u537f\uff1f \u3000\u3000\u7a0b\u5c0f\u8776\u6c89\u601d\u4e86\u4e00\u9635\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u56db\u4f4d\uff0c\u53d1\u89c9\u4e86\u4ec0\u4e48\uff1f\u5434\u5144\uff0c\u4f60\u5148\u8bf4\u3002\u201d \u3000\u3000\u56db\u5927\u540d\u6355\u4e2d\uff0c\u4e2d\u5dde\u5434\u94c1\u5cf0\u5e74\u9f84\u6700\u5927\uff0c\u7ecf\u9a8c\u9605\u5386\u4e5f\u6700\u591a\u3002 \u3000\u3000\u201c\u53d1\u89c9\u4e86\u603b\u6355\u5934\u7075\u5149\u95ea\u52a8\uff0c\u83ab\u975e\u89e6\u52a8\u4e86\u4ec0\u4e48\u5fc3\u4e8b\uff1f\u201d\u5434\u94c1\u5cf0\u8bdd\u8bf4\u7684\u975e\u5e38\u5a49\u8f6c\uff0c\u4f46\u5374\u70b9\u51fa \u4e86\u4e3b\u9898\u3002 \u3000\u3000\u201c\u6211\u77e5\u65e0\u6cd5\u9003\u8fc7\u4f60\u4eec\u7684\u89c2\u5bdf\uff0c\u201d\u7a0b\u5c0f\u8776\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u59b9\u8fd9\u4e00\u70b9\u9547\u9759\u529f\u592b\uff0c\u5b9e\u5728\u662f\u6d45 \u843d\u7684\u5f88\u54ea\uff01\u201d \u3000\u3000\u201c\u90a3\u5c31\u8bf4\u51fa\u6765\u542c\u542c\uff0c\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u5c5e\u4e0b\u7b49\u4e5f\u597d\u4e3a\u603b\u6355\u5934\u5206\u5fe7\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u539f\u60f3\u8ba9\u8bf8\u4f4d\u597d\u597d\u4f11\u606f\u4e00\u591c\uff0c\u660e\u5929\u518d\u8c08\u8fd9\u4e8b\uff0c\u60f3\u4e0d\u5230\u675c\u5144\u51e0\u53e5\u8bdd\uff0c\u7adf\u4f7f\u6211\u9732 \u51fa\u4e86\u7834\u7efd\uff0c\u771f\u662f\u6c89\u4e0d\u4f4f\u6c14\u5440\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e8b\u60c5\u548c\u626c\u5dde\u7684\u738b\u77e5\u5e9c\u6709\u5173\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e5f\u548c\u6c34\u5e08\u63d0\u7763\u9a6c\u5c06\u519b\u6709\u4e9b\u7275\u8fde\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\u4e0d\u65ad\u5730\u8ffd\u95ee\u68a6\u5e7b\u4e4b\u5200\uff0c\u96be\u9053\u8fd9\u4e2a\u4eba\u548c\u738b\u77e5\u5e9c\u4e5f\u6709\u5173\u7cfb\uff1f\u201d \u3000\u3000\u201c\u68a6\u5e7b\u4e4b\u5200\uff0c\u662f\u4e0d\u662f\u5f53\u4eca\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u6700\u597d\u7684\u6740\u624b\uff1f\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f20\u8bf4\u4ed6\u6709\u672f\u6cd5\uff0c\u80fd\u591f \u5f79\u5200\u6740\u4eba\u3002\u201d \u3000\u3000\u201c\u68a6\u5e7b\u4e4b\u5200\uff0c\u786e\u5b9e\u662f\u4e00\u4e2a\u4f20\u5947\u4eba\u7269\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u9a6c\u516c\u5b50\u65b0\u5a5a\u4e4b\u591c\uff0c\u6d1e\u623f\u4e2d\u95e8\u7a97\u7d27\u95ed\uff0c \u65b0\u90ce\u5374\u88ab\u4e00\u5200\u523a\u6b7b\uff0c\u5200\u5c31\u63e1\u5728\u65b0\u5a18\u5b50\u7684\u624b\u4e2d\uff0c\u65b0\u5a18\u5b50\u5c31\u662f\u626c\u5dde\u7b2c\u4e00\u8c6a\u5bcc\u91d1\u767e\u5e74\u7684\u5973\u513f\uff01\u201d \u3000\u3000\u201c\u91d1\u5c0f\u59d0\uff0c\u4f1a\u6b66\u529f\u4e48\uff1f\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u5f62\u8c8c\u5982\u4f55\uff1f\u5728\u626c\u5dde\u7684\u98ce\u8bc4\u53ef\u597d\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u6b66\u529f\uff0c\u5f62\u8c8c\u5417\u2026\u2026\u201d\u675c\u671b\u6708\u76ee\u5149\u63a0\u8fc7\u5c0f\u6587\u3001\u5c0f\u96c5\uff0c\u53c8\u770b\u770b\u60dc\u7389\uff0c\u5999\u7684\u662f\u672a\u770b\u603b\u6355 \u5934\uff0c\u9053\uff1a\u201c\u4e2d\u4e0a\u4e4b\u59ff\uff0c\u4f46\u6e29\u67d4\u7eaf\u60c5\uff0c\u867d\u662f\u8c6a\u95e8\u5973\u513f\uff0c\u4f46\u5f88\u5c11\u629b\u5934\u9732\u9762\uff0c\u98ce\u8bc4\u5f88\u597d\uff0c\u662f\u4e2a\u77e5\u4e66 \u8fbe\u793c\u7684\u5973\u5b69\u5b50\u3002\u201d \u3000\u3000\u201c\u675c\u5144\u5f1f\uff0c\u4f60\u76ee\u5149\u6e38\u8d70\uff0c\u770b\u4e86\u5c0f\u6587\u3001\u5c0f\u96c5\u59d1\u5a18\uff0c\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u7adf\u610f\u72b9\u672a\u8db3\uff0c\u53c8\u770b\u770b\u60dc \u7389\u59d1\u5a18\uff0c\u662f\u4ec0\u4e48\u610f\u601d\u5462\uff1f\u5979\u53ef\u662f\u7edd\u8272\u7f8e\u5973\uff0c\u5982\u82e5\u91d1\u59d1\u5a18\u80fd\u548c\u8fd9\u4e09\u4f4d\u59d1\u5a18\u76f8\u6bd4\uff0c\u90a3\u8be5\u4e0d\u662f\u4e2d\u4e0a \u4e4b\u59ff\u4e86\uff1f\u201d \u3000\u3000\u5c0f\u6587\u3001\u5c0f\u96c5\u63a9\u53e3\u7b11\uff0c\u60dc\u7389\u5374\u4fa7\u987e\u675c\u671b\u6708\uff0c\u770b\u4ed6\u8981\u5982\u4f55\u56de\u7b54\u3002 \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u5f88\u91cd\u8981\uff0c\u8981\u8bf4\u7684\u9ed1\u767d\u5206\u660e\uff01\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u4e0d\u80fd\u6a21\u68f1\u4e24\u53ef\u3002\u201d \u3000\u3000\u201c\u8001\u5b9e\u8bf4\uff0c\u6211\u6ca1\u89c1\u8fc7\u91d1\u5c0f\u7709\uff0c\u4f46\u738b\u5927\u4eba\u548c\u94c1\u603b\u6355\u5934\uff0c\u90fd\u8bf4\u5979\u662f\u4e2a\u7f8e\u5973\u3002\u201d\u675c\u671b\u6708\u9053\uff1a \u201c\u8861\u91cf\u77e5\u5e9c\u5927\u4eba\u7684\u6587\u91c7\u98ce\u96c5\uff0c\u94c1\u7fce\u7684\u89c1\u8bc6\u5e7f\u535a\uff0c\u4ed6\u4eec\u80fd\u8bf4\u51fa\u7f8e\u5973\u4e8c\u5b57\uff0c\u5e94\u8be5\u662f\u8a00\u51fa\u5fc3\u58f0\u3002\u4f55 \u51b5\uff0c\u91d1\u5c0f\u7709\u662f\u672c\u6848\u4e2d\u552f\u4e00\u7684\u5acc\u72af\uff0c\u4f46\u5982\u540c\u5f53\u524d\u7684\u4e09\u4f4d\u7f8e\u4eba\u5e76\u8bba\uff0c\u5c31\u6709\u4e9b\u9ad8\u62ac\u91d1\u5c0f\u7709\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u6ca1\u6709\u89c1\u8fc7\u91d1\u59d1\u5a18\uff0c\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u600e\u53ef\u968f\u53e3\u8912\u8d2c\uff0c\u662f\u4e0d\u662f\u5fc3\u6709\u6240\u60e7\u554a\uff1f\u201d\u8fd9\u756a\u8bdd\uff0c \u5927\u5385\u4e2d\u4eba\u90fd\u7b11\u4e86\uff0c\u53ea\u4e0d\u8fc7\u60dc\u7389\u7b11\u7684\u9762\u5e26\u5a07\u7f9e\u3002 \u3000\u3000\u201c\u6211\u60f3\u89c1\u89c1\u91d1\u5c0f\u7709\u3002\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u675c\u5144\u80fd\u5426\u5b89\u6392\uff0c\u5e0c\u671b\u80fd\u9690\u79d8\u4e00\u4e9b\u3002\u201d \u3000\u3000\u201c\u5e94\u8be5\u4e0d\u96be\uff0c\u660e\u5929\uff0c\u6211\u5c31\u8d70\u8d9f\u91d1\u5e9c\u627e\u94c1\u7fce\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u8bf7\u4ed6\u5b89\u6392\u3002\u603b\u6355\u5934\u5e0c\u671b\u5728\u4ec0 \u4e48\u5730\u70b9\u3001\u65b9\u5f0f\u4e0b\u548c\u5979\u89c1\u9762\uff1f\u201d \u3000\u3000\u201c\u5730\u70b9\u3001\u65b9\u5f0f\u4e0d\u62d8\uff0c\u7531\u5979\u9009\u62e9\uff01\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91cd\u8981\u7684\u662f\u4e0d\u80fd\u8ba9\u592a\u591a\u4eba\u77e5\u9053\uff0c\u6211\u4eec\u5230\u6b64 \u7684\u8baf\u606f\uff0c\u8fd8\u4e0d\u80fd\u4f20\u626c\u51fa\u53bb\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u91d1\u767e\u5e74\u7231\u5973\u5982\u638c\u4e0a\u660e\u73e0\uff0c\u73b0\u5728\u4e3a\u4e86\u4fdd\u62a4\u91d1\u5c0f\u7709\u4e0d\u60dc\u548c\u9a6c\u63d0\u7763 \u7edf\u7387\u7684\u5927\u519b\u5bf9\u6297\uff0c\u76f8\u4fe1\u91d1\u59d1\u5a18\u5df2\u5728\u91cd\u91cd\u7684\u4fdd\u62a4\u4e4b\u4e0b\uff0c\u8fd9\u4ef6\u4e8b\u53ea\u6015\u65e0\u6cd5\u7792\u8fc7\u91d1\u767e\u5e74\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u6709\u7406\uff0c\u53ea\u597d\u52a8\u70b9\u5fc3\u673a\u4e86\uff0c\u501f\u7528\u738b\u77e5\u5e9c\u7684\u540d\u4e49\uff0c\u8bf7\u91d1\u767e\u5e74 \u5e26\u5973\u513f\u6765\u5e9c\u8859\u4e00\u8d9f\uff0c\u5c31\u8bf4\u8981\u95ee\u5979\u4e00\u4e9b\u6848\u60c5\u4e0a\u7684\u7591\u70b9\u3002\u201d \u3000\u3000\u201c\u529e\u6cd5\u662f\u4e0d\u9519\uff01\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4f46\u91d1\u767e\u5e74\u73b0\u5728\u8eab\u5904\u9669\u5883\uff0c\u4e00\u5b9a\u5e26\u7740\u5927\u6279\u7684\u4fdd\u9556\u62a4\u4ece\u3002\u8d3e \u82f1\u4e00\u5b9a\u4f1a\u6765\uff0c\u5176\u4eba\u806a\u660e\u7edd\u9876\uff0c\u53ea\u8981\u4ed6\u89c1\u5230\u603b\u6355\u5934\uff0c\u6216\u662f\u5434\u5144\u7b49\u4eba\uff0c\u5b9a\u4f1a\u8054\u60f3\u5230\u6211\u4eec\u4f1a\u805a\u626c\u5dde \u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u8fde\u4f60\u4e5f\u662f\u4e0d\u4fbf\u51fa\u9762\u4e86\uff0c\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6362\u4e2a\u4eba\u53bb\u5427\uff01\u8bf7\u626c\u5dde\u5e9c\u7684\u738b\u526f\u603b\u6355 \u5934\u8d70\u4e00\u8d9f\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u6211\u4eec\u4f1a\u805a\u626c\u5dde\u7684\u4e8b\uff0c\u8fd8\u4e0d\u80fd\u6cc4\u6f0f\u51fa\u53bb\uff0c\u90a3\u5c31\u8981\u4ed4\u7ec6\u5e03\u7f72\u4e00\u4e0b\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u603b \u6355\u5934\u53ef\u4ee5\u626e\u5145\u626c\u5dde\u5e9c\u7684\u5211\u6848\u4e66\u529e\uff0c\u6211\u4eec\u53ef\u4ee5\u95ed\u95e8\u4e0d\u51fa\uff0c\u4e5f\u53ef\u5316\u8eab\u4f5c\u626c\u5dde\u5e9c\u7684\u73ed\u5934\u8859\u5f79\uff0c\u7a7f\u4e0a \u5b98\u670d\uff0c\u4e0d\u4f1a\u6709\u4eba\u76f8\u4fe1\u5929\u4e0b\u56db\u5927\u6355\u5934\uff0c\u5df2\u4f5c\u4e86\u626c\u5dde\u5e9c\u7684\u8859\u5f79\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u597d\u554a\uff01\u201d\u5c0f\u96c5\u9053\uff1a\u201c\u6211\u548c\u5c0f\u6587\u4e5f\u8981\u53c2\u52a0\u5440\uff01\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u4e24\u4f4d\u592a\u5a07\u5c0f\u4e86\uff0c\u626e\u4f5c\u8859\u5f79\uff0c\u53cd\u800c\u5f15\u4eba\u6ce8\u76ee\uff0c\u4e0d\u6210\uff0c\u4e0d\u6210\u2026\u2026\u201d \u3000\u3000\u201c\u603b\u4e0d\u80fd\u8ba9\u6211\u4eec\u8eb2\u5728\u623f\u4e2d\u4e0d\u51fa\u6765\u554a\uff01\u201d\u5c0f\u96c5\u9053\uff1a\u201c\u90a3\u4f1a\u618b\u6b7b\u4eba\u5566\uff01\u201d \u3000\u3000\u201c\u4e24\u4f4d\u53ef\u4ee5\u626e\u4f5c\u4e66\u7ae5\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u65e2\u53ef\u5c31\u8fd1\u4fdd\u62a4\u603b\u6355\u5934\u548c\u738b\u5927\u4eba\uff0c\u53c8\u53ef\u53c2\u4e0e\u673a\u8981\uff0c \u4ffa\u8001\u5434\u8fd9\u4e2a\u4e3b\u610f\u4e0d\u9519\u5427\uff01\u201d \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u4e3b\u610f\u662f\u597d\u4e3b\u610f\uff0c\u5c31\u662f\u4e0d\u77e5\u9053\u5c0f\u59d0\u4f1a\u4e0d\u4f1a\u540c\u610f\uff1f\u201d \u3000\u3000\u201c\u540c\u610f\uff0c\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6539\u626e\u738b\u5927\u4eba\u7684\u4e66\u7ae5\uff0c\u5c31\u8981\u4fdd\u62a4\u738b\u5927\u4eba\u5b89\u5168\u3002\u201d\u8138\u8272\u4e00\u6574\uff0c \u795e\u60c5\u4e25\u8083\uff0c\u63a5\u9053\uff1a\u201c\u5c0f\u6587\u7531\u4f60\u8d1f\u8d23\uff0c\u7531\u626e\u4e66\u7ae5\u5f00\u59cb\uff0c\u5c31\u4e0d\u80fd\u518d\u79bb\u5f00\u738b\u5927\u4eba\uff0c\u4e00\u76f4\u5230\u738b\u5927\u4eba\u5b89 \u5168\u65e0\u6059\u4e3a\u6b62\uff0c\u4f60\u8981\u548c\u738b\u5927\u4eba\u7684\u4ece\u536b\u534f\u8c03\u4e00\u4e0b\uff0c\u6211\u628a\u738b\u77e5\u5e9c\u7684\u5b89\u5371\u5168\u4ed8\u6258\u7ed9\u4f60\uff0c\u738b\u5927\u4eba\u53d7\u5230\u4f24 \u5bb3\uff0c\u4f60\u8981\u8d1f\u4e00\u5207\u7684\u8d23\u4efb\u3002\u201d \u3000\u3000\u7a0b\u603b\u6355\u5934\u5bf9\u4e24\u4e2a\u60c5\u540c\u59ca\u59b9\u7684\u5973\u5a62\uff0c\u5f88\u5c11\u8fd9\u7b49\u4e25\u5389\u7684\u547d\u4ee4\u8fc7\u3002\u5c0f\u6587\u7684\u795e\u60c5\u4e5f\u53d8\u5f97\u51dd\u91cd\u4e86\uff0c \u5fae\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\u653e\u5fc3\uff0c\u4f24\u5bb3\u738b\u5927\u4eba\u4e4b\u524d\uff0c\u5148\u8981\u628a\u6211\u5c0f\u6587\u6482\u5012\u3002\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u56db\u5927\u540d\u6355\u90fd\u53d8\u5f97\u4e00\u8138\u5e84\u4e25\uff0c\u4e5f\u4e86\u89e3\u5230\uff0c\u7a0b\u5c0f\u8776\u5343\u91cc\u4e0b\u626c\u5dde\uff0c\u4e00\u5927\u534a\u662f\u4e3a\u4e86\u738b\u77e5 \u5e9c\uff0c\u8fd9\u5c31\u4e0d\u5355\u7eaf\u662f\u4e3a\u4e86\u9a6c\u516c\u5b50\u7684\u547d\u6848\u800c\u6765\u3002\u626c\u5dde\u57ce\u6740\u624b\u4e91\u96c6\uff0c\u96be\u9053\u662f\u4e3a\u4e86\u738b\u5c11\u537f\u8fd9\u4f4d\u77e5\u5e9c\u5927 \u4eba\uff0c\u4e3a\u4ec0\u4e48\u5462\uff1f\u56db\u5927\u540d\u6355\u5fc3\u4e2d\u7591\u95ee\u91cd\u91cd\uff0c\u5374\u6ca1\u6709\u4eba\u5f00\u53e3\u8ffd\u95ee\uff0c\u539f\u6765\u5f88\u8f7b\u677e\u7684\u6c14\u6c1b\uff0c\u53d8\u5f97\u4e25\u8083 \u4e86\uff0c\u4e94\u5927\u6355\u5934\u8c08\u7b11\u7528\u5175\u7684\u6001\u5ea6\uff0c\u4e5f\u4e3a\u4e4b\u51dd\u91cd\u8d77\u6765\u3002 \u3000\u3000\u201c\u6740\u624b\u4e4b\u738b\uff0c\u68a6\u5e7b\u4e4b\u5200\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u53ef\u90fd\u662f\u4e3a\u4e86\u738b\u5927\u4eba\u800c\u6765\uff1f\u201d \u3000\u3000\u201c\u5c40\u52bf\u6df7\u6dc6\uff0c\u7591\u4e91\u91cd\u91cd\uff0c\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f46\u738b\u77e5\u5e9c\u662f\u6211\u4eec\u4fdd\u62a4\u7684\u91cd\u70b9\uff0c\u6211\u4eec\u8981\u5316\u89e3\u8fd9\u6ee1 \u5929\u7591\u4e91\uff0c\u5148\u8981\u74e6\u89e3\u4e91\u96c6\u4e8e\u626c\u5dde\u7684\u6740\u624b\u7ec4\u5408\u3002\u4f46\u6211\u4eec\u8fd8\u4e0d\u77e5\u9053\u654c\u60c5\u865a\u5b9e\uff0c\u8fd9\u5c31\u8981\u8bf8\u4f4d\u591a\u7528\u667a\u6167 \u591a\u8f9b\u82e6\u4e86\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u603b\u6355\u5934\u8bf4\u7684\u662f\uff0c\u6211\u4eec\u5e94\u6eb6\u5165\u626c\u5dde\u57ce\u4e2d\uff0c\u6478\u51fa\u8fd9\u4e9b\u6740\u624b\u7ec4\u5408\u7684\u5e95\u7ec6\u548c\u843d\u811a\u4e4b \u5904\uff01\u201d \u3000\u3000\u201c\u592a\u5371\u9669\u4e86\uff0c\u56db\u5927\u540d\u6355\u7684\u5f62\u8c8c\uff0c\u6050\u65e9\u5df2\u88ab\u6740\u624b\u7ec4\u5408\u53d6\u5f97\u4e86\u56fe\u50cf\u3002\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5c31\u7b97\u80fd \u6613\u5bb9\u9a97\u8fc7\u4ed6\u4eec\u7684\u8033\u76ee\uff0c\u4f46\u6740\u624b\u7684\u6027\u683c\u662f\uff0c\u5b81\u53ef\u9519\u6740\uff0c\u4e0d\u7559\u540e\u60a3\uff0c\u53ea\u8981\u88ab\u654c\u4eba\u53d1\u73b0\u4e86\uff0c\u4ed6\u4eec\u5c06 \u4f1a\u5168\u529b\u8ffd\u6740\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u7167\u4e0a\u9762\uff0c\u5c31\u53ea\u597d\u62da\u4e0a\u4e86\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u4eba\u6570\u4f17\u591a\uff0c\u4e00\u4e0a\u5341\u51e0\u4e2a\uff0c\u4f60\u4eec\u4e00\u4eba\u4e4b\u529b\uff0c\u53c8\u80fd\u652f\u6301\u591a\u4e45\u5462\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u7a81\u7136\u60f3\u5230\uff0c\u7626\u897f\u6e56\u9047\u4e0a\u963b\u51fb\u7684\u4e8b\uff0c\u90a3\u4e09\u4eba\u4e00\u7ec4\u7684\u5408\u51fb\u4e4b\u529b\uff0c\u975e\u5e38\u5f3a\u5927\uff0c\u7acb\u523b\u63a5\u53e3\uff0c \u628a\u7626\u897f\u6e56\u9047\u654c\u7684\u640f\u6740\u7ecf\u8fc7\uff0c\u5f88\u4ed4\u7ec6\u5730\u8bf4\u4e86\u51fa\u6765\uff0c\u4f5c\u7ed3\u8bed\uff0c\u9053\uff1a\u201c\u4e00\u65e6\u9047\u4e0a\u4e86\u8fd9\u4e2a\u7ec4\u5408\u7684\u591a\u7ec4 \u56f4\u88ad\uff0c\u5c0f\u5f1f\u662f\u7edd\u65e0\u80fd\u529b\u5e94\u4ed8\u2026\u2026\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u63a5\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u54b1\u4eec\u56db\u4e2a\u4eba\uff0c\u6b66\u529f\u5728\u4f2f\u3001\u4ef2\u4e4b\u95f4\uff0c\u5c31\u7b97\u6709\u5dee\u8ddd\uff0c\u4e5f\u6781\u5fae\u5c0f\uff0c\u4f60 \u675c\u5144\u5bf9\u4ed8\u4e0d\u4e86\u7684\uff0c\u6211\u4eec\u4e5f\u65e0\u6cd5\u5bf9\u4ed8\u4e86\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u3001\u5c91\u5578\u864e\u6ca1\u6709\u5f00\u53e3\uff0c\u4e0d\u8bf4\u8bdd\u5c31\u662f\u9ed8\u8ba4\u4e86\u4e8e\u627f\u5fd7\u7684\u8bf4\u6cd5\u3002 \u3000\u3000\u201c\u5982\u82e5\u4f60\u4eec\u56db\u4f4d\u96c6\u4e2d\u4e00\u5904\uff0c\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u5bf9\u4ed8\u4e09\u4eba\u7684\u5408\u51fb\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u53ef\u4ee5\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4ed6\u4eec\u6700\u5927\u7684\u7f3a\u70b9\uff0c\u662f\u4e0d\u80fd\u4e34\u673a\u5e94\u53d8\uff0c\u4e2a\u4eba\u7684\u6b66\u529f\u9020\u8be3\u6709 \u9650\u3002\u201d \u3000\u3000\u201c\u8981\u4e34\u9635\u89c2\u5bdf\u4e00\u4e0b\uff0c\u627e\u51fa\u4ed6\u4eec\u7684\u7834\u7efd\uff0c\u5408\u6211\u4eec\u56db\u4eba\u4e4b\u529b\uff0c\u521a\u67d4\u4e92\u6d4e\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4e00 \u4e3e\u51fb\u6740\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u5bf9\uff01\u8fd9\u4e09\u4eba\u7ec4\u5408\u7684\u653b\u52bf\uff0c\u65e2\u7136\u662f\u5f88\u5927\u7684\u5a01\u80c1\uff0c\u5982\u80fd\u4e00\u4e3e\u51fb\u7834\uff0c\u90a3\u5c31\u5f7b\u5e95\u5730 \u6b7c\u706d\u4ed6\u4eec\u3002\u201d \u3000\u3000\u8fd9\u662f\u8bf7\u793a\uff0c\u7b49\u5019\u7a0b\u5c0f\u8776\u7684\u51b3\u5b9a\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u6c89\u601d\u4e00\u9635\uff0c\u9053\uff1a\u201c\u597d\u5427\uff01\u6740\u4e4b\u4e0d\u60dc\uff0c\u4f46\u5982\u80fd\u91cd\u521b\u4ed6\u4eec\uff0c\u4f7f\u4ed6\u80a2\u4f53\u6b8b\u7f3a\uff0c\u65e0\u80fd\u518d\u6218\uff0c \u501f\u4ed6\u4eec\u4e4b\u53e3\uff0c\u4f20\u51fa\u8b66\u8baf\uff0c\u5bf9\u6df7\u4e71\u5bf9\u65b9\u7684\u6218\u5fd7\uff0c\u4f1a\u4e0d\u4f1a\u66f4\u6709\u6548\u7528\u5462\uff1f\u201d \u3000\u3000\u201c\u5bf9\u5bf9\u5bf9\uff0c\u603b\u6355\u5934\u88c1\u51b3\u6709\u7406\uff0c\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u501f\u654c\u4e4b\u53e3\uff0c\u6d88\u654c\u6218\u5fd7\uff0c\u54c8\u54c8\uff0c\u4e0a\u7b56\u4e5f\u3002\u201d \u3000\u3000\u201c\u963f\u6a2a\u3001\u963f\u4fdd\uff0c\u795e\u51fa\u9b3c\u6ca1\uff0c\u4e24\u4eba\u5df2\u6df1\u5f97\u9690\u8e2a\u533f\u8ff9\u7684\u5fc3\u6cd5\uff0c\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u6211\u4e5f \u4e0d\u77e5\u9053\u4ed6\u4eec\u533f\u85cf\u4f55\u5904\uff0c\u4f46\u4ed6\u4eec\u603b\u4f1a\u5728\u6700\u9700\u8981\u7684\u65f6\u5019\u51fa\u73b0\u3002\u201d\u76ee\u5149\u4e00\u63a0\u5c0f\u96c5\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u626e\u4e66 \u7ae5\u4e86\uff0c\u548c\u60dc\u7389\u4e00\u8d77\u8ddf\u7740\u6211\uff0c\u4f5c\u4e3a\u5404\u8def\u63a5\u5e94\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u5c0f\u96c5\u7ad9\u8d77\uff0c\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u5c0f\u5a62\u9075\u547d\u3002\u201d \u3000\u3000\u60dc\u7389\u4e5f\u8d77\u8eab\u8bf4\u9053\uff1a\u201c\u5c5e\u4e0b\u4e00\u5207\u542c\u4ee4\uff0c\u6c34\u91cc\u6c34\u4e2d\u53bb\uff0c\u706b\u91cc\u706b\u4e2d\u884c\u3002\u201d \u3000\u3000\u5c0f\u96c5\u59d1\u5a18\u7684\u5251\u672f\u6280\u827a\uff0c\u4e09\u4f4d\u540d\u6355\u77e5\u9053\u751a\u8be6\uff0c\u8001\u5b9e\u8bf4\uff0c\u6bd4\u4ed6\u4eec\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d\u53ca\u3002\u4f46\u60dc\u7389\u53d7 \u6b64\u91cd\u7528\uff0c\u4e09\u5927\u540d\u6355\u5c31\u6709\u70b9\u4e0d\u4ee5\u4e3a\u7136\u4e86\uff0c\u7f8e\u5973\u597d\u770b\uff0c\u5982\u82b1\u60a6\u76ee\uff0c\u4f46\u63a5\u5e94\u5404\u65b9\u7684\u91cd\u4efb\uff0c\u53ef\u662f\u8981\u771f \u5200\u771f\u67aa\u3001\u786c\u78b0\u786c\u5730\u53bb\u62da\u547d\uff0c\u9047\u4e0a\u4e9b\u4ec0\u4e48\u6837\u7684\u5f3a\u654c\u9ad8\u624b\uff0c\u5b8c\u5168\u65e0\u6cd5\u9884\u6599\uff0c\u662f\u4e00\u70b9\u4e5f\u4e0d\u80fd\u53d6\u5de7\u7684 \u4e8b\u3002 \u3000\u3000\u5012\u4e0d\u662f\u8f7b\u85d0\u60dc\u7389\uff0c\u53ea\u662f\u611f\u89c9\u5230\u5982\u6b64\u6f02\u4eae\u3001\u5e74\u8f7b\u7684\u59d1\u5a18\uff0c\u62da\u6b7b\u4e86\u6709\u4e9b\u53ef\u60dc\uff0c\u5c31\u7b97\u53d7\u5230\u4f24\u5bb3\uff0c \u4e5f\u662f\u5927\u5927\u7684\u4e0d\u5e78\u3002 \u3000\u3000\u4e09\u5927\u540d\u6355\u5df2\u4e0d\u592a\u76f8\u4fe1\uff0c\u4e16\u4e0a\u4f1a\u6709\u5982\u6b64\u591a\u7684\u6b66\u529f\u51fa\u4f17\u3001\u5a07\u5a9a\u53ef\u4eba\u7684\u59d1\u5a18\uff0c\u800c\u4e14\uff0c\u90fd\u6295\u5165\u4e86 \u5211\u90e8\u603b\u6355\u53f8\u4e2d\uff0c\u548c\u6c5f\u6e56\u4e0a\u51f6\u4eba\u5468\u65cb\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u662f\u5f02\u6570\uff0c\u5c0f\u6587\u3001\u5c0f\u96c5\u5728\u7a0b\u59d1\u5a18\u7684\u5f15\u8350\u4e0b\uff0c\u540c\u5217\u4e00\u4ee3\u5947\u4eba\u5434\u4e00\u5938\u7684\u95e8\u4e0b\uff0c\u540d\u5e08\u51fa\u9ad8 \u5f92\uff0c\u5c0f\u5c0f\u5e74\u7eaa\u6210\u5c31\u4e86\u5979\u4eec\u4e5f\u65e0\u6cd5\u4f01\u53ca\u7684\u6b66\u529f\uff0c\u4f46\u60dc\u7389\u5462\uff1f\u5982\u65e0\u7edd\u9ad8\u8eab\u624b\uff0c\u8981\u5979\u8eab\u62c5\u5982\u6b64\u7684\u91cd \u4efb\uff0c\u90a3\u5c31\u592a\u6b8b\u5fcd\u4e86\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u3001\u4e8e\u627f\u5fd7\u3001\u5c91\u5578\u864e\uff0c\u867d\u7136\u60f3\u6cd5\u76f8\u540c\uff0c\u4f46\u5374\u628a\u76ee\u5149\u6295\u5411\u4e86\u675c\u671b\u6708\uff0c\u76ee\u5149\u4e2d\u6ee1\u662f\u5173\u6000\uff0c \u5fc3\u4e2d\u8bf4\u9053\uff1a\u8fd9\u53ef\u662f\u675c\u8001\u5f1f\u672a\u6765\u592b\u4eba\uff0c\u82b1\u679d\u6a21\u6837\uff0c\u79c0\u51fa\u7fa4\u4f26\uff0c\u5982\u6b64\u5a07\u59bb\uff0c\u51e0\u4e16\u4fee\u6765\uff0c\u4f60\u8be5\u597d\u597d \u73cd\u60dc\u5979\u5440\uff01\u4e3a\u4ec0\u4e48\u4e0d\u5f00\u5c0a\u53e3\uff1f \u3000\u3000\u8fd9\u4e9b\u8bdd\u6ca1\u6709\u8bf4\u51fa\u6765\uff01\u4f46\u76ee\u5149\u4e2d\u5374\u5df2\u8868\u8fbe\u4e86\u5145\u5206\u7684\u5173\u6000\u3002 \u3000\u3000\u675c\u671b\u6708\u867d\u5df2\u9886\u7565\u5230\u4e09\u4eba\u7684\u597d\u610f\uff0c\u4f46\u5374\u65e0\u6cd5\u8868\u793a\u4ec0\u4e48\u3002\u4ed6\u77e5\u9053\u60dc\u7389\u7684\u51fa\u8eab\uff0c\u8c2d\u795e\u533b\u628a\u7528\u836f \u624d\u80fd\uff0c\u5168\u90e8\u65bd\u5728\u5979\u7684\u8eab\u4e0a\uff0c\u5341\u4f59\u5e74\u5b64\u72ec\u82e6\u7ec3\uff0c\u6210\u5c31\u4e86\u4e00\u8eab\u7edd\u6280\u3002\u4f46\u7a76\u7adf\u8fbe\u5230\u4f55\u7b49\u7a0b\u5ea6\uff0c\u4ed6\u5374 \u662f\u65e0\u6cd5\u9884\u6d4b\uff0c\u53c8\u80fd\u8bf4\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u5e78\u597d\u7a0b\u5c0f\u8776\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e09\u4f4d\u662f\u62c5\u5fc3\u60dc\u7389\u59d1\u5a18\u7684\u5b89\u5371\u2026\u2026\u201d \u3000\u3000\u201c\u4e5f\u662f\u89c9\u7740\u60dc\u7389\u4e00\u4e2a\u5c0f\u5973\u5b50\uff0c\u4e0d\u8db3\u4ee5\u62c5\u5f53\u8fd9\u4e2a\u91cd\u4efb\u3002\u4f46\u4e0d\u77e5\u8981\u4ec0\u4e48\u6837\u7684\u6b66\u529f\u3001\u6280\u827a\u624d\u8db3 \u4ee5\u9002\u4efb\u6b64\u6218\uff1f\u5982\u662f\u6b66\u529f\u548c\u5404\u4f4d\u9547\u5b88\u4e00\u65b9\u7684\u5927\u540d\u6355\u76f8\u82e5\uff0c\u662f\u5426\u53ef\u4ee5\u5462\uff1f\u201d\u8fd9\u5c31\u6709\u4e9b\u4f24\u4eba\u4e86\uff0c\u5c0f \u59d1\u5a18\u5916\u67d4\u5185\u521a\uff0c\u5fc3\u4e2d\u4e00\u6025\uff0c\u5c31\u51fa\u8a00\u65e0\u72b6\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u77e5\u5979\u6027\u683c\uff0c\u9ed8\u7136\u65e0\u8bed\uff0c\u4f46\u4e2d\u5dde\u3001\u957f\u5b89\u3001\u5173\u4e1c\u4e09\u5927\u540d\u6355\uff0c\u90fd\u6709\u70b9\u706b\u4e0a\u5fc3\u5934\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u5fd7\u6df1\u6050\u5c91\u5578\u864e\u7684\u706b\u66b4\u813e\u6c14\uff0c\u51fa\u8a00\u592a\u91cd\uff0c\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u60dc\u7389\u59d1\u5a18\u7684\u610f\u601d\u5462\uff1f\u201d \u3000\u3000\u8bdd\u8bf4\u7684\u5f88\u5a49\u8f6c\uff0c\u5e0c\u671b\u59d1\u5a18\u80fd\u85c9\u673a\u4e0b\u53f0\u3002 \u3000\u3000\u201c\u5982\u82e5\u56db\u4f4d\u613f\u610f\u7ed9\u60dc\u7389\u4e00\u4e2a\u673a\u4f1a\uff0c\u5012\u5e0c\u671b\u56db\u4f4d\u62dc\u9886\u4e00\u4e0b\u7edd\u6280\u3002\u201d \u3000\u3000\u8fd9\u662f\u6307\u540d\u6311\u6218\u4e86\uff0c\u4f46\u628a\u675c\u671b\u6708\u4e5f\u626f\u8fdb\u53bb\uff0c\u4f7f\u50f5\u76f4\u7684\u6c14\u6c1b\u4e2d\uff0c\u7565\u6709\u7f13\u548c\u3002 \u3000\u3000\u4e09\u4eba\u770b\u5411\u7a0b\u5c0f\u8776\uff0c\u7a0b\u59d1\u5a18\u5fae\u5fae\u7b11\uff0c\u4f3c\u662f\u6ca1\u6709\u963b\u6b62\u8fd9\u573a\u6bd4\u6b66\u7684\u610f\u601d\u3002 \u3000\u3000\u5c0f\u6587\u3001\u5c0f\u96c5\u4e5f\u672a\u5f00\u53e3\uff0c\u5979\u4eec\u8981\u770b\u770b\u60dc\u7389\u7684\u6280\u827a\uff0c\u591f\u4e0d\u591f\u52a0\u5165\u5979\u4eec\u4e00\u4f19\u7684\u8d44\u683c\u3002\u4e24\u4eba\u5e76\u4e0d \u6392\u65a5\u60dc\u7389\uff0c\u4f46\u4e5f\u4e0d\u5e0c\u671b\u4e00\u4e2a\u7ee3\u82b1\u6795\u5934\uff0c\u597d\u770b\u4e0d\u4e2d\u7528\u7684\u4eba\uff0c\u574f\u4e86\u5979\u4eec\u540d\u5934\u3002\u675c\u671b\u6708\u5f88\u4e3a\u96be\uff0c\u5982 \u4e0d\u8ba9\u60dc\u7389\u5c55\u73b0\u51fa\u4e00\u8eab\u6210\u5c31\uff0c\u5f88\u96be\u53d7\u5230\u771f\u6b63\u7684\u5c0a\u91cd\u3002\u4f46\u4e09\u5927\u540d\u6355\u7684\u6280\u827a\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u5200\u5251\u65e0\u773c\uff0c \u7a0d\u6709\u5dee\u9519\uff0c\u4e0d\u6b7b\u5fc5\u4f24\uff0c\u5fc3\u4e2d\u4e3a\u96be\u6781\u4e86\uff0c\u4f46\u53c8\u4e0d\u80fd\u51fa\u8a00\u963b\u6b62\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u6709\u610f\u653e\u7eb5\uff0c\u4e09\u5927\u6355\u5934\u5fc3\u4e2d\u706b\u6c14\u66f4\u5927\uff0c\u8fd9\u4e2a\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\u7684\u5c0f\u59d1\u5a18\uff0c\u5e94\u8be5\u53d7\u70b9\u6559\u8bad\u3002 \u3000\u3000\u5c91\u5578\u864e\u970d\u7136\u7ad9\u8d77\uff0c\u4f46\u4e8e\u627f\u5fd7\u5374\u62a2\u5148\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u957f\u5b89\u4e8e\u627f\u5fd7\uff0c\u7b28\u9e1f\u5148\u98de\uff0c\u5148\u9886\u6559\u60dc\u7389 \u59d1\u5a18\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u60dc\u7389\u56db\u987e\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u91cc\u4e48\uff1f\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u5207\u78cb\u6280\u827a\uff0c\u8fd9\u5730\u65b9\u5e94\u8be5\u591f\u4e86\u3002\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u82b1\u5385\u5f88\u5927\uff0c\u53ea\u6446\u4e86\u4e00\u684c\u9152\u5e2d\uff0c\u8fd8\u4f59\u4e0b\u4e00\u5927\u534a\u7684\u5730\u65b9\u3002 \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u8bf4\u7684\u662f\uff0c\u201d\u5f53\u5148\u79bb\u4f4d\u5165\u573a\uff0c\u5c0f\u59d1\u5a18\u4f3c\u662f\u5df2\u7b49\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u5c91\u5578\u864e\u88ab\u4e8e\u627f\u5fd7\u62a2\u4e86\u5148\uff0c\u53ea\u597d\u53c8\u5728\u539f\u4f4d\u5750\u4e0b\u3002 \u3000\u3000\u6c14\u6c1b\u987f\u73b0\u7d27\u5f20\uff0c\u867d\u975e\u62da\u547d\u53ae\u6740\uff0c\u4f46\u6bd4\u6b66\u52a8\u624b\u7684\u4e8b\uff0c\u603b\u96be\u514d\u6263\u4eba\u5fc3\u5f26\u3002 \u3000\u3000\u4e8e\u627f\u5fd7\u7f13\u7f13\u79fb\u6b65\u5165\u573a\uff0c\u9053\uff1a\u201c\u60dc\u7389\u59d1\u5a18\uff0c\u54b1\u4eec\u5148\u5728\u62f3\u811a\u4e0a\u89c1\u4e2a\u80dc\u8d1f\u5427\uff1f\u201d \u3000\u3000\u201c\u60dc\u7389\u9075\u547d\uff0c\u5982\u662f\u4e8e\u540d\u6355\u610f\u5174\u672a\u5c3d\uff0c\u518d\u52a8\u5175\u5203\uff0c\u5c0f\u5973\u5b50\u5df2\u662f\u4e45\u95fb\u4e8e\u5148\u751f\u7684\u95ea\u7535\u5feb\u5200\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u7b11\u4e00\u7b11\uff0c\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5fc3\u4e2d\u5374\u6697\u6697\u5fd6\u9053\uff1a\u5c0f\u4e2b\u5934\u56a3\u5f20\u7684\u5f88\u5566\uff01\u4e0d\u8ba9\u5979\u5403\u70b9\u82e6\u5934\uff0c\u662f \u5f88\u96be\u538b\u5236\u4e0b\u5979\u7684\u6c14\u7130\u4e86\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u5728\u76f8\u8ddd\u4e94\u6b65\u5904\uff0c\u5bf9\u9762\u7ad9\u5b9a\uff0c\u4e8e\u627f\u5fd7\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u5148\u8bf7\u51fa\u624b\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u8ba9\u6211\u5148\u673a\uff0c\u201d\u60dc\u7389\u8bdd\u843d\u53e3\uff0c\u4eba\u4e5f\u540c\u65f6\u53d1\u52a8\uff0c\u8eab\u5b50\u4e00\u6643\uff0c\u5df2\u6b3a\u8fd1\u4e8e\u627f\u5fd7\uff0c\u4e00\u8bb0\u76f4\u62f3\uff0c \u75be\u6363\u524d\u80f8\u3002 \u3000\u3000\u770b\u60dc\u7389\u51fa\u624b\u4e00\u62db\u7684\u8eab\u6cd5\uff0c\u4e8e\u627f\u5fd7\u5fae\u5fae\u5403\u60ca\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u5c0f\u4e2b\u5934\u662f\u6709\u70b9\u65a4\u4e24\u554a\uff01\u8f7b\u654c\u4e4b\u5ff5\uff0c \u987f\u7136\u6d88\u5931\uff0c\u53cd\u624b\u4e00\u638c\u6a2a\u5207\uff0c\u5288\u5411\u60dc\u7389\u53f3\u8155\u3002 \u3000\u3000\u60dc\u7389\u7adf\u4e0d\u95ea\u907f\uff0c\u7389\u8155\u5fae\u8f6c\uff0c\u98df\u6307\u4e00\u4f38\uff0c\u6307\u5411\u4e86\u4e8e\u627f\u5fd7\u7684\u8109\u7a74\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u795e\u4e4e\u5176\u6280\uff0c\u77a7\u7684\u5c0f\u6587\u3001\u5c0f\u96c5\u4e5f\u6697\u6697\u53eb\u597d\u3002\u5434\u94c1\u5cf0\u3001\u5c91\u5578\u864e\u66f4\u662f\u770b\u7684\u5fc3\u5934\u4e00\u5bd2\uff0c\u5fd6 \u9053\uff1a\u5c0f\u4e2b\u5934\uff0c\u624e\u624b\u7684\u5f88\u54ea\uff01\u7adf\u7ec3\u6709\u91d1\u521a\u6307\u529f\u3002 \u3000\u3000\u675c\u671b\u6708\u5374\u770b\u7684\u5927\u4e3a\u62c5\u5fc3\uff0c\u751f\u6050\u8fd9\u4e00\u6307\u622a\u70b9\u8109\u7a74\uff0c\u91cd\u4f24\u4e86\u4e8e\u627f\u5fd7\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u53d8\u51fa\u610f\u5916\uff0c\u4e8e\u627f\u5fd7\u5df2\u6536\u624b\u4e0d\u53ca\uff0c\u4f46\u60dc\u7389\u5374\u53ca\u65f6\u4e00\u504f\u6307\u950b\uff0c\u6307\u529b\u64e6\u8155\u800c\u8fc7\uff0c\u53cd\u800c \u628a\u53f3\u624b\u7389\u8155\u9001\u5165\u4e86\u4e8e\u627f\u5fd7\u7684\u624b\u4e2d\uff0c\u53ea\u8981\u4e8e\u627f\u5fd7\u4e94\u6307\u4e00\u5408\uff0c\u5c31\u53ef\u6263\u4f4f\u4e86\u60dc\u7389\u53f3\u8155\u8109\u7a74\u3002 \u3000\u3000\u771f\u662f\u6beb\u53d1\u4e4b\u95f4\uff0c\u53d8\u5316\u83ab\u6d4b\u3002 \u3000\u3000\u4e8e\u627f\u5fd7\u672b\u6263\u60dc\u7389\u7684\u8109\u7a74\uff0c\u53cd\u800c\u6536\u624b\u800c\u9000\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u518d\u6bd4\u4e86\uff0c\u591a\u8c22\u59d1\u5a18\u624b\u4e0b\u7559\u60c5\u3002\u201d\u7adf \u81ea\u56de\u5f52\u5ea7\u4f4d\u3002 \u3000\u3000\u60dc\u7389\u53e3\u820c\u542f\u52a8\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u5979\u672c\u60f3\u8bf4\u4e24\u53e5\u8c26\u8ba9\u7684\u8bdd\uff0c\u4f46\u60f3\u5230\u5728\u573a\u4e4b\u4eba\uff0c\u4e2a\u4e2a\u795e\u76ee\u5982\u7535\uff0c\u8bf4\u7684\u592a\u865a\u4f2a\uff0c\u53cd\u800c\u6709\u4e9b\u505a\u4f5c \u4e86\uff0c\u53ea\u597d\u95ed\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u201c\u8fd9\u662f\u4e00\u573a\u5f88\u9ad8\u5c1a\u7684\u6bd4\u8bd5\uff0c\u7537\u7684\u541b\u5b50\uff0c\u5973\u7684\u6587\u96c5\uff0c\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u7ed3\u675f\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u8fd8\u6ca1\u6709\uff0c\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u5982\u662f\u603b\u6355\u5934\u4e0d\u53cd\u5bf9\u7684\u8bdd\uff0c\u5c5e\u4e0b\u4e5f\u60f3\u548c\u60dc\u7389\u59d1\u5a18\u8f83\u91cf\u4e00\u9635\u3002\u201d \u3000\u3000\u201c\u4e0d\u53cd\u5bf9\uff01\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u65e2\u7136\u6ca1\u6709\u963b\u6b62\u4e8e\u627f\u5fd7\uff0c\u5982\u4f55\u80fd\u591f\u963b\u62e6\u4f60\uff0c\u4f60\u4eec\u4e24\u4e2a\u4eba\u5546\u91cf\u5427\uff01 \u6bd4\u4ec0\u4e48\uff0c\u7531\u4f60\u4eec\u81ea\u5df1\u51b3\u5b9a\uff01\u201d\u5b8c\u5168\u662f\u4e00\u6d3e\u5927\u516c\u65e0\u79c1\u7684\u6001\u5ea6\u3002 \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u591a\u8c22\u603b\u6355\u5934\u3002\u201d\u76ee\u5149\u8f6c\u6ce8\u5230\u60dc\u7389\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u4f60\u9009\u62e9\uff0c\u662f\u62f3\u811a\u3001\u8fd8\u662f \u5175\u5203\uff1f\u201d \u3000\u3000\u60dc\u7389\u56de\u987e\u4e86\u675c\u671b\u6708\u4e00\u773c\uff0c\u675c\u671b\u6708\u7adf\u7136\u8f6c\u5934\u4ed6\u987e\uff0c\u5168\u65e0\u56de\u5e94\u3002 \u3000\u3000\u4e00\u4e2a\u662f\u672a\u8fc7\u95e8\u7684\u59bb\u5b50\uff0c\u4e00\u4e2a\u662f\u591a\u5e74\u597d\u53cb\uff0c\u675c\u671b\u6708\u5982\u4f55\u5f00\u53e3\u5462\uff1f\u5c31\u53ea\u597d\u88c5\u804b\u4f5c\u54d1\u4e86\uff0c\u8fde\u4e00 \u70b9\u6697\u793a\u4e5f\u4e0d\u7ed9\u60dc\u7389\u3002 \u3000\u3000\u60dc\u7389\u6697\u5fd6\u601d\uff1a\u65e2\u7136\u6253\u4e86\uff0c\u5c31\u6253\u4e2a\u75db\u5feb\u5427\uff01\u4e0d\u7ecf\u4e00\u756a\u5927\u62da\u6740\uff0c\u4ed6\u4eec\u5fc3\u4e2d\u4e00\u76f4\u5b58\u6709\u8f7b\u85d0\u6211\u7684 \u7528\u5fc3\uff0c\u54ac\u54ac\u7259\uff0c\u9053\uff1a\u201c\u5c91\u5927\u6355\u5934\u9009\u5427\uff01\u4f60\u64c5\u957f\u4ec0\u4e48\u5c31\u6bd4\u4ec0\u4e48\uff1f\u5175\u5203\u6697\u5668\uff0c\u60dc\u7389\u65e0\u4e0d\u5949\u966a\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bdd\uff0c\u53c8\u52fe\u52a8\u5c91\u5578\u864e\u7684\u6012\u706b\uff0c\u51b7\u7136\u9053\uff1a\u201c\u90a3\u5c31\u73a9\u4e00\u70b9\u51f6\u9669\u7684\uff0c\u54b1\u4eec\u6bd4\u5175\u5203\uff0c\u59d1\u5a18\u8bf7\u51c6 \u5907\u4e00\u4e0b\u3002\u201d \u3000\u3000\u56de\u8eab\u884c\u81f3\u5927\u5385\u4e00\u89d2\uff0c\u62ff\u8d77\u4e86\u56db\u5c3a\u516b\u5bf8\u7684\u65a9\u9a6c\u5200\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u4ef6\u5f88\u5413\u4eba\u7684\u5175\u5203\uff0c\u53c8\u5bbd\u53c8\u957f\uff0c\u770b\u4e0a\u53bb\uff0c\u50cf\u4e00\u652f\u5927\u94e1\u5200\uff0c\u4f30\u7b97\u4e00\u4e0b\u81f3\u5c11\u6709\u4e8c\u5341\u4e94\u516d \u65a4\u91cd\u3002 \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u5175\u5203\u6709\u70b9\u7b28\u91cd\uff0c\u62a1\u5f00\u6765\uff0c\u6536\u624b\u4e0d\u6613\uff0c\u59d1\u5a18\u8bf7\u591a\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u98a6\u79c0\u7709\uff0c\u5fcd\u4e0b\u672a\u8a00\u3002 \u3000\u3000\u60dc\u7389\u7f13\u7f13\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5c91\u5144\u53ea\u7ba1\u65bd\u5c55\uff0c\u60dc\u7389\u6b7b\u4f24\u65e0\u6028\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u542c\u5f97\u5fc3\u5934\u4e00\u75bc\uff0c\u4f46\u4ecd\u7136\u5fcd\u4e0b\u4e86\uff0c\u6ca1\u6709\u5f00\u53e3\u3002 \u3000\u3000\u5c0f\u6587\u3001\u5c0f\u96c5\u5bf9\u671b\u4e00\u773c\u5782\u9996\u4e0d\u8bed\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u671b\u4e86\u5c91\u5578\u864e\u4e00\u4e0b\uff0c\u5fd6\u9053\uff1a\u600e\u7684\u8fd9\u4e48\u8ba4\u771f\u554a\uff1f\u60dc\u7389\u867d\u7136\u56a3\u5f20\u4e86\u4e00\u4e9b\uff0c\u4f46\u5979\u5fd7\u5728\u4e89\u53d6 \u603b\u6355\u5934\u7684\u8ba4\u540c\uff0c\u60c5\u6709\u53ef\u539f\u54ea\uff01 \u3000\u3000\u4e8e\u627f\u5fd7\u4e5f\u5728\u6697\u6697\u60f3\u9053\uff1a\u5c91\u80e1\u5b50\u554a\uff01\u5c31\u6015\u4f60\u66b4\u8e81\u574f\u4e8b\uff0c\u6211\u624d\u62a2\u5148\u51fa\u624b\uff0c\u8fd8\u5dee\u70b9\u6298\u5728\u5c0f\u59d1\u5a18 \u7684\u624b\u4e2d\uff0c\u4f46\u4ecd\u7136\u6ca1\u6709\u963b\u62e6\u4f4f\u4f60\uff0c\u4f60\u5982\u6b64\u7684\u5927\u5f20\u65d7\u9f13\uff0c\u903c\u51fa\u60dc\u7389\u62da\u547d\u4e4b\u5fc3\uff0c\u4e00\u65e6\u95f9\u51fa\u4e86\u6d41\u8840\u60e8 \u5267\uff0c\u8981\u5982\u4f55\u6536\u62fe\u554a\uff1f \u3000\u3000\u5c31\u5728\u5404\u4eba\u5fd6\u601d\u4e4b\u95f4\uff0c\u5c91\u5578\u864e\u6c89\u58f0\u559d\u9053\uff1a\u201c\u59d1\u5a18\u600e\u4e0d\u4eae\u51fa\u5175\u5203\uff1f\u201d \u3000\u3000\u539f\u6765\uff0c\u60dc\u7389\u4ecd\u7136\u8d64\u624b\u7a7a\u62f3\uff0c\u7ad9\u5728\u573a\u4e2d\u3002 \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u6211\u7684\u5175\u5203\u5c31\u85cf\u5728\u8eab\u4e0a\uff0c\u8be5\u7528\u7684\u65f6\u5019\uff0c\u6211\u81ea\u4f1a\u4eae\u51fa\u6765\uff0c\u5c91\u5144\u4e0d\u7528\u4e3a\u6211\u62c5\u5fc3\u3002\u201d \u3000\u3000\u201c\u90a3\u597d\uff0c\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\u3002\u201d\u5c91\u5578\u864e\u8bdd\u8bf4\u5f97\u5f88\u5ba2\u6c14\uff0c\u4f46\u4e00\u62a1\u65a9\u9a6c\u5200\uff0c\u66b4\u95ea\u51fa\u4e00\u9053\u5bd2\u8292\uff0c \u5e26\u8d77\u4e86\u4e00\u80a1\u5f3a\u70c8\u7684\u5200\u98ce\uff0c\u6a2a\u8170\u65a9\u53bb\uff0c\u5a01\u52bf\u60ca\u4eba\u3002 \u3000\u3000\u60dc\u7389\u6b65\u6cd5\u7075\u5de7\uff0c\u4e00\u4e2a\u95ea\u8f6c\uff0c\u7adf\u7136\u907f\u5f00\u4e86\u3002 \u3000\u3000\u5c91\u5578\u864e\u4e00\u53e3\u6c14\u5288\u516b\u5200\uff0c\u5168\u88ab\u907f\u8fc7\uff0c\u4e0d\u7981\u6012\u6c14\u53c8\u5347\uff0c\u65a9\u9a6c\u5200\u5feb\u901f\u5288\u65a9\uff0c\u5e7b\u8d77\u4e00\u7247\u5200\u8292\u3002 \u3000\u3000\u52a8\u624b\u4e4b\u521d\uff0c\u5c91\u5578\u864e\u8fd8\u7559\u6709\u4f59\u5730\uff0c\u5200\u52bf\u4e00\u76f4\u5728\u63a7\u5236\u4e4b\u4e2d\uff0c\u4f46\u76ee\u7779\u60dc\u7389\u8eab\u6cd5\u7075\u52a8\uff0c\u516b\u5200\u843d\u7a7a\uff0c \u5fc3\u5934\u53c8\u4e0a\u706b\uff0c\u5c31\u771f\u7684\u5168\u529b\u8fd0\u5200\u4e86\u3002\u4f46\u89c1\u5bd2\u8292\u6d41\u8f6c\uff0c\u4e00\u4e08\u5185\u5168\u662f\u5bc6\u5e03\u7684\u5200\u5149\uff0c\u5fc3\u60f3\u81f3\u5c11\u4e5f\u8be5\u628a \u60dc\u7389\u903c\u51fa\u4e00\u4e08\u5f00\u5916\u3002 \u3000\u3000\u54ea\u77e5\u60dc\u7389\u4eba\u968f\u5200\u8f6c\uff0c\u7adf\u7136\u5728\u5200\u5149\u4e2d\u6e38\u8d70\uff0c\u770b\u4e0a\u53bb\u5371\u9669\u6781\u4e86\uff0c\u7a0d\u6162\u4e00\u6b65\uff0c\u5c31\u8981\u88ab\u65a9\u9a6c\u5200\u5288 \u65a9\u5f53\u573a\uff0c\u4f46\u5c31\u662f\u90a3\u5206\u6beb\u4e4b\u5dee\uff0c\u4f24\u4e0d\u5230\u60dc\u7389\u3002 \u3000\u3000\u5c91\u5578\u864e\u6709\u4e9b\u4e0b\u4e0d\u4e86\u53f0\uff0c\u5200\u52bf\u6108\u5288\u6108\u5feb\uff0c\u6709\u5982\u6025\u98ce\u9aa4\u96e8\uff0c\u5bd2\u5149\u76d6\u5929\u94fa\u5730\uff0c\u628a\u60dc\u7389\u4eba\u5f71\u5168\u5377 \u5165\u4e00\u7247\u5200\u5149\u4e2d\uff0c\u4e5f\u903c\u7684\u4e8e\u627f\u5fd7\u548c\u5434\u94c1\u5cf0\uff0c\u4e0d\u5f97\u4e0d\u79fb\u5f00\u5ea7\u4f4d\uff0c\u4ee5\u907f\u5200\u52bf\u3002\u53ea\u89c1\u5200\u5149\u4e0d\u89c1\u4eba\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u5fd7\u4f4e\u58f0\u9053\uff1a\u201c\u5434\u5144\uff0c\u8fd9\u662f\u4ec0\u4e48\u8eab\u6cd5\uff1f\u80fd\u5728\u91cd\u91cd\u5200\u8292\u4e2d\u6e38\u8d70\u5982\u5e38\u3002\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u4f20\u8a00\u4e2d\u7684\u5357\u8352\u7edd\u6280\u8774\u8776\u7a7f\u82b1\u6b65\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u5144\u5f1f\u4e5f\u53ea\u662f\u542c\u4eba\u8bf4\u8fc7\uff0c\u6ca1\u6709\u89c1 \u8fc7\uff0c\u4e0d\u6562\u80af\u5b9a\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u8fd9\u6b63\u662f\u8774\u8776\u7a7f\u82b1\u6b65\uff0c\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8774\u8776\u7a7f\u82b1\uff0c\u80fd\u907f\u5f00\u5343\u679d\u4e07\u53f6\uff0c\u6240\u4ee5\uff0c\u80fd\u5728 \u91cd\u91cd\u5200\u7f51\u4e2d\u6e38\u8d70\u81ea\u5982\u3002\u201d \u3000\u3000\u60dc\u7389\u4e00\u76f4\u672a\u4eae\u5175\u5203\uff0c\u4e5f\u672b\u8fd8\u51fb\uff0c\u53ea\u662f\u4ee5\u7075\u5de7\u7684\u8eab\u6cd5\uff0c\u95ea\u907f\u5bf9\u65b9\u5200\u52bf\u3002 \u3000\u3000\u5c91\u5578\u864e\u4e00\u53e3\u6c14\u5288\u51fa\u4e86\u4e09\u767e\u5200\uff0c\u4ecd\u672a\u903c\u9000\u60dc\u7389\uff0c\u7a81\u7136\u4e00\u6536\u5200\u52bf\uff0c\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff0c\u59d1\u5a18 \u6280\u827a\u7cbe\u6e5b\uff0c\u5c91\u80e1\u5b50\u8ba4\u8f93\u5566\uff01\u201d \u3000\u3000\u51dd\u76ee\u770b\u60dc\u7389\uff0c\u5fae\u5fae\u5598\u606f\uff0c\u9b13\u89d2\u4e5f\u89c1\u6c57\u6c34\uff0c\u8fd9\u4e00\u9635\u95ea\u907f\u5feb\u5200\u6025\u88ad\uff0c\u4f3c\u4e4e\u662f\u7528\u4e86\u4e0d\u5c11\u6c14\u529b\u3002 \u3000\u3000\u5c91\u5578\u864e\u7a81\u7136\u6709\u79cd\u4e0d\u5b89\u7684\u611f\u89c9\uff0c\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u5c91\u80e1\u5b50\u4e00\u628a\u5e74\u7eaa\u4e86\uff0c\u7adf\u7136\u8fd8\u662f\u65e0\u6cd5\u63a7\u5236 \u8fd9\u706b\u66b4\u7684\u813e\u6c14\uff0c\u771f\u662f\u81ea\u53d6\u5176\u8fb1\u554a\uff01\u201d \u3000\u3000\u201c\u6211\u5df2\u7ecf\u5feb\u628a\u6c14\u529b\u7528\u5c3d\u4e86\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u82e5\u5c91\u5144\u518d\u653b\u4e00\u767e\u5200\uff0c\u5c0f\u59b9\u975e\u4f24\u5728\u5200\u4e0b\u4e0d\u53ef\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u56de\u5f52\u5ea7\u4f4d\uff0c\u7b11\u9053\uff1a\u201c\u8f93\u4e86\u5c31\u8ba4\uff0c\u59d1\u5a18\u4e0d\u7528\u5f80\u5c91\u80e1\u5b50\u8138\u4e0a\u8d34\u91d1\uff0c\u5c71\u5ddd\u7075\u6c14\u5b55\u80b2\u800c\u6210 \u7684\u7f8e\u5973\u624d\u4eba\uff0c\u5929\u4e0b\u80fd\u6709\u51e0\u8bb8\uff0c\u5c3d\u4e3a\u603b\u6355\u5934\u6536\u5165\u5211\u90e8\u4e2d\u4e86\uff0c\u8fd9\u4e00\u4ee3\u5211\u6355\u5de5\u4f5c\u5168\u662f\u7f8e\u5973\u5929\u4e0b\uff0c\u8fd9 \u53ef\u662f\u5343\u53e4\u76db\u4e8b\uff0c\u524d\u4e0d\u89c1\u53e4\u4eba\uff0c\u53ea\u6015\u4e5f\u540e\u65e0\u6765\u8005\u4e86\u3002\u201d \u3000\u3000\u5c0f\u96c5\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u82f1\u96c4\u6c14\u77ed\uff0c\u4f60\u4eec\u56db\u5927\u540d\u6355\uff0c\u5a01\u9547\u4e00\u65b9\uff0c\u53d7\u671d\u5ef7\u91cd\u89c6\uff0c\u4e07\u6c11\u7231\u6234\uff0c \u7f09\u51f6\u6355\u76d7\uff0c\u4fdd\u969c\u5584\u826f\uff0c\u4e2a\u4e2a\u90fd\u4f1a\u7559\u540d\u5343\u53e4\u5462\uff01\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u63a5\u9053\uff1a\u201c\u60dc\u7389\u59d0\uff0c\u5c0f\u59b9\u521d\u7779\u8774\u8776 \u7a7f\u82b1\u6b65\uff0c\u5927\u5f00\u4e00\u6b21\u773c\u754c\u4e86\u3002\u201d \u3000\u3000------------- \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u5341\u4e00\u7ae0 \u3000\u3000\u4e0d\u53eb\u60dc\u7389\u53eb\u59d0\u59d0\uff0c\u60fa\u60fa\u76f8\u60dc\u561b\uff0c\u4e5f\u8868\u793a\u7531\u5fc3\u5e95\u63a5\u7eb3\u4e86\u60dc\u7389\u3002 \u3000\u3000\u5c0f\u6587\u6ca1\u8bf4\u8bdd\uff0c\u4f46\u5374\u671b\u7740\u60dc\u7389\u70b9\u5934\u7b11\u3002 \u3000\u3000\u201c\u60dc\u7389\u7684\u6280\u827a\u5f88\u535a\u6742\uff0c\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u90a3\u4e00\u62db\u70b9\u9f99\u624b\uff0c\u975e\u5f97\u7ec3\u8fc7\u91d1\u521a\u6307\u4e00\u7c7b\u7684\u529f\u592b\uff0c\u5f88 \u96be\u4e3a\u7528\u3002\u91d1\u521a\u6307\u4e3a\u5c11\u6797\u7edd\u5b66\uff0c\u70b9\u9f99\u624b\u5374\u662f\u5317\u6d77\u5947\u6280\uff0c\u8774\u8776\u7a7f\u82b1\u6b65\u53c8\u6e90\u51fa\u5357\u8352\uff0c\u4f60\u5b66\u7684\u5f88\u6742\uff0c \u4f46\u90fd\u662f\u5404\u65b9\u6280\u827a\u7684\u7cbe\u8981\u6240\u5728\u3002\u201d \u3000\u3000\u4f46\u5979\u53ea\u4f5c\u8bf4\u660e\uff0c\u5374\u672a\u95ee\u60dc\u7389\u5e08\u627f\u3002 \u3000\u3000\u65e2\u7136\u672a\u95ee\uff0c\u60dc\u7389\u4e5f\u4e0d\u591a\u4f5c\u89e3\u91ca\u4e86\uff0c\u7f13\u6b65\u5165\u5ea7\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u5de5\u4f5c\u65b9\u5411\uff0c\u5927\u81f4\u5df2\u5b9a\uff0c\u73b0\u5728\u662f\u5426\u53ef\u4ee5\u51fa\u52a8\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u73b0\u5728\uff0c\u90fd\u53bb\u4f11\u606f\uff0c\u4e94\u66f4\u540e\uff0c\u4f60\u4eec\u81ea\u7531\u884c\u52a8\uff0c\u8bb0\u7740\u626c\u5dde\u51f6\u4eba\u4e91\u96c6\uff0c\u4e0d\u53ef\u6709\u4e1d \u6beb\u5927\u610f\uff0c\u56db\u4f4d\u4e5f\u4fdd\u6301\u7d27\u5bc6\u7684\u8054\u7cfb\uff0c\u4e0d\u53ef\u4e00\u4eba\u6d89\u9669\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u65e9\u5df2\u4e3a\u4ed6\u4eec\u5b89\u6392\u597d\u5bbf\u4f4f\u4e4b\u5904\uff0c\u4eba\u79bb\u82b1\u5385\uff0c\u7acb\u523b\u6709\u4eba\u5e26\u8def\u3002 \u3000\u3000\u5929\u672a\u4eae\uff0c\u56db\u5927\u6355\u5934\u5c31\u79bb\u5f00\u4e86\u77e5\u5e9c\u8859\u95e8\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u65e9\u8d77\u8eab\uff0c\u5c0f\u6587\u5df2\u5b8c\u5168\u6539\u53d8\u4e86\u5f62\u8c8c\uff0c\u5a07\u5a9a\u7684\u5c0f\u59d1\u5a18\uff0c\u53d8\u6210\u4e86\u4e00\u4e2a\u6587\u79c0\u7684\u5c0f\u4e66\u7ae5\uff0c \u5973\u5b69\u5b50\u6539\u7740\u7537\u88c5\uff0c\u770b\u4e0a\u53bb\u53c8\u5c0f\u4e86\u4e00\u4e9b\uff0c\u4e00\u773c\u4e4b\u4e0b\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5341\u516d\u4e03\u5c81\u7684\u5c0f\u7537\u751f\u3002\u987d\u76ae\u7684\u5c0f \u96c5\u7275\u7740\u5c0f\u6587\u4e00\u53ea\u624b\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u5440\uff01\u8d70\uff0c\u59d0\u59d0\u5e26\u4f60\u4e70\u7cd6\u53bb\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u4e70\u7cd6\u4e86\uff0c\u54b1\u4eec\u73a9\u5bb6\u5bb6\u9152\u5427\uff01\u201d\u5c0f\u6587\u9053\uff1a\u201c\u4f60\u4f5c\u65b0\u5a18\u5b50\uff0c\u6211\u4f5c\u65b0\u90ce\u5b98\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u628a\u60dc\u7389\u4e5f\u5a36\u8fc7\u6765\uff0c\u201d\u5c0f\u96c5\u9053\uff1a\u201c\u8fc7\u5e72\u763e\u561b\uff0c\u5c31\u8fc7\u4e2a\u75db\u5feb\uff01\u201d \u3000\u3000\u201c\u4e00\u7bad\u53cc\u96d5\u554a\uff1f\u90a3\u8981\u5148\u8bf4\u597d\u8c01\u5927\u8c01\u5c0f\u3002\u201d\u60dc\u7389\u7b11\u7740\u8fdb\u4e86\u95e8\uff0c\u9053\uff1a\u201c\u73b0\u5728\u763e\u662f\u8fc7\u4e0d\u6210\u4e86\uff0c \u738b\u5927\u4eba\u5728\u82b1\u5385\u966a\u5c0f\u59d0\u5171\u8fdb\u65e9\u9910\uff0c\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u8bf7\u4e24\u4f4d\u8fc7\u53bb\u966a\u5ba2\u5440\uff01\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u521a\u8fdb\u95e8\u5c31\u548c\u6211\u4e89\u5927\u8bba\u5c0f\uff0c\u201d\u5c0f\u96c5\u9053\uff1a\u201c\u5148\u53bb\u966a\u5c0f\u59d0\u5403\u65e9\u996d\uff0c\u5982\u679c\u6709\u7a7a\u95f2\uff0c\u6211\u8981 \u9001\u4f60\u4eec\u4e24\u4e2a\u8fdb\u6d1e\u623f\u3002\u201d \u3000\u3000\u4e09\u4e2a\u4eba\u8bf4\u7b11\u7740\uff0c\u8fdb\u5165\u4e86\u82b1\u5385\uff0c\u7a0b\u5c0f\u8776\u548c\u738b\u77e5\u5e9c\u65e9\u5df2\u5728\u5ea7\u3002 \u3000\u3000\u738b\u5927\u4eba\u4e00\u76f4\u770b\u7740\u5c0f\u6587\uff0c\u4f3c\u66fe\u76f8\u8bc6\u554a\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u628a\u5c0f\u6587\u5973\u626e\u7537\u88c5\u8bf4\u4e00\u904d\uff0c\u8981\u6c42\u738b\u5927\u4eba\u968f\u65f6\u628a\u5c0f\u6587\u5e26\u5728\u8eab\u8fb9\u3002 \u3000\u3000\u738b\u5c11\u537f\u6709\u4e2a\u79c1\u4eba\u4fdd\u9556\u4f55\u5927\u5149\uff0c\u5c31\u7ad9\u5728\u82b1\u5385\u95e8\u53e3\u4e86\u671b\uff0c\u8fd9\u4e2a\u4eba\u683c\u5b88\u5206\u5bf8\uff0c\u7edd\u4e0d\u548c\u738b\u5927\u4eba\u5171 \u684c\u8fdb\u9910\uff0c\u540c\u8d77\u540c\u5750\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u53c8\u8c08\u8d77\u60f3\u89c1\u91d1\u5c0f\u7709\u7684\u4e8b\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u7acb\u523b\u540c\u610f\uff0c\u4e0d\u8fc7\uff0c\u4e5f\u63d0\u51fa\u4e00\u70b9\u610f\u89c1\uff0c\u65e2\u7136\u662f\u67e5\u95ee\u7591\u70b9\uff0c\u90a3\u5c31\u5047\u620f\u771f\u4f5c\uff0c\u6765\u4e2a\u8fc7\u5802 \u95ee\u8bdd\uff0c\u7a0b\u5c0f\u8776\u4ee5\u4e66\u529e\u8eab\u5206\u53c2\u4e0e\uff0c\u4e34\u65f6\u60f3\u5230\u95ee\u4ec0\u4e48\uff0c\u53ef\u4ee5\u5199\u4e2a\u6761\u5b50\u901a\u77e5\u4ed6\u3002\u53ea\u662f\u5927\u5802\u4e0a\u592a\u5a01\u4e25\uff0c \u6709\u4e9b\u4e0d\u4fbf\uff0c\u5c31\u5728\u8fd9\u82b1\u5385\u4e0a\u5e03\u7f6e\u4e00\u4e0b\uff0c\u8fc7\u5b8c\u5802\u6211\u79bb\u5f00\uff0c\u4f60\u4eec\u53ef\u4ee5\u79c1\u4e0b\u8c08\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u90a3\u65f6\u95f4\uff0c\u6211\u4e5f\u4e0d\u4fbf\u63a5\u8fd1\u5979\uff0c\u6211\u770b\uff0c\u53eb\u60dc\u7389\u966a\u5979\u2026\u2026\u201d\u76ee\u5149\u4e00\u63a0\u60dc\u7389\u63a5\u9053\uff1a \u201c\u4f60\u8981\u8d39\u70b9\u5fc3\u673a\uff0c\u548c\u5979\u591a\u4eb2\u8fd1\uff0c\u770b\u80fd\u4e0d\u80fd\u5957\u51fa\u5979\u7684\u5fc3\u4e2d\u9690\u79d8\uff0c\u4e5f\u770b\u5979\u5bf9\u9a6c\u516c\u5b50\u7684\u771f\u6b63\u60c5\u610f\u5982 \u4f55\uff1f\u4e0d\u8fc7\uff0c\u4ee5\u4e2b\u5934\u8eab\u5206\u548c\u5979\u5957\u8fd1\u4e4e\uff0c\u6536\u6548\u4e0d\u5927\uff0c\u4e5f\u6709\u4e9b\u4e0d\u4fbf\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u7acb\u523b\u63a5\u9053\uff1a\u201c\u786e\u5b9e\u6709\u4e9b\u4e0d\u65b9\u4fbf\uff0c\u5f88\u96be\u6df1\u5165\u4ea4\u8c08\uff0c\u8fd9\u4e48\u5427\uff01\u8981\u60dc\u7389\u59d1\u5a18\uff0c\u4f5c\u6211\u4e00\u4e2a \u8fdc\u4eb2\uff0c\u6211\u7acb\u523b\u5429\u5490\u4e0b\u53bb\uff0c\u8981\u5e9c\u4e2d\u4ec6\u4ece\uff0c\u6539\u79f0\u60dc\u7389\u59d1\u5a18\u4e3a\u8868\u5c0f\u59d0\uff0c\u4e00\u8868\u4e09\u5343\u91cc\uff0c\u4e5f\u4e0d\u7b97\u7528\u9a97\u884c \u8bc8\uff0c\u4e0d\u77e5\u60dc\u7389\u59d1\u5a18\u7684\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u662f\u60dc\u7389\u9ad8\u6500\u4e86\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u4f5c\u4e8b\u5f88\u8fc5\u901f\uff0c\u7acb\u523b\u5429\u5490\u738b\u575a\u526f\u603b\u6355\u5934\uff0c\u540c\u65f6\u6301\u540d\u5e16\u548c\u516c\u6587\u8bf7\u91d1\u767e\u5e74\u5e26\u5973\u513f\uff0c\u5230\u82b1 \u5385\u5e94\u8baf\u3002\u793c\u6570\u5468\u5230\uff0c\u516c\u79c1\u4ea4\u52a0\uff0c\u91d1\u767e\u5e74\u5c31\u5f88\u96be\u63a8\u6258\u4e86\u3002 \u3000\u3000\u867d\u7136\uff0c\u6ca1\u89c1\u5230\u56db\u5927\u540d\u6355\uff0c\u4f46\u738b\u77e5\u5e9c\u5374\u672a\u591a\u95ee\u3002 \u3000\u3000\u4e00\u987f\u65e9\u9910\uff0c\u51b3\u5b9a\u4e86\u884c\u4e8b\u6b65\u9aa4\uff0c\u800c\u4e14\u987e\u8651\u5468\u8be6\uff0c\u56db\u5e73\u516b\u7a33\uff0c\u7a0b\u5c0f\u8776\u4e5f\u4e0d\u5f97\u4e0d\u4f69\u670d\u738b\u5927\u4eba\u7684 \u5904\u4e8b\u660e\u5feb\u4e86\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u8fd9\u6837\u6709\u80fd\u6709\u4e3a\u7684\u597d\u5b98\uff0c\u4e00\u65e6\u80fd\u5165\u4e3b\u4e2d\u67a2\uff0c\u5f88\u53ef\u80fd\u662f\u4e00\u4ee3\u8d24\u76f8\uff0c\u52ff\u602a \u6709\u4eba\u60ee\u5fcc\u4ed6\uff0c\u751a\u81f3\u8981\u96c7\u8bf7\u6740\u624b\u52a0\u5bb3\u4e8e\u4ed6\u4e86\u3002\u4f46\u5fc3\u601d\u4e00\u8f6c\uff0c\u60f3\u6cd5\u53c8\u53d8\uff0c\u5c31\u7b97\u738b\u5c11\u537f\u624d\u80fd\u51fa\u4f17\uff0c \u4f46\u4ed6\u8fd8\u53ea\u662f\u4e00\u4e2a\u5c0f\u5c0f\u77e5\u5e9c\uff0c\u8981\u6210\u4e3a\u5e99\u5802\u4e2d\u680b\u6881\u4e4b\u6750\uff0c\u8fd8\u9700\u8981\u76f8\u5f53\u65f6\u95f4\uff0c\u8fd8\u5f97\u52a0\u4e0a\u8fd0\u6c14\uff0c\u4f55\u5fc5 \u73b0\u5728\u5c31\u5192\u9669\u52a0\u5bb3\u4e8e\u4ed6\u5462\uff1f\u8fd9\u4e2d\u95f4\u4f1a\u4e0d\u4f1a\u522b\u6709\u5185\u60c5\uff1f \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u6697\u4f5c\u51b3\u5b9a\uff0c\u5bf9\u8fd9\u4f4d\u738b\u5927\u4eba\u4e5f\u8981\u591a\u591a\u7559\u5fc3\u4e86\uff0c\u8981\u627e\u673a\u4f1a\u5429\u5490\u5c0f\u6587\u591a\u52a0\u6ce8\u610f\uff0c\u5f53 \u4e0b\u8d77\u8eab\u544a\u9000\uff0c\u79bb\u5f00\u4e86\u82b1\u5385\uff0c\u4e5f\u5e26\u8d70\u4e86\u5c0f\u96c5\u3002\u60dc\u7389\u3001\u5c0f\u6587\u5374\u7559\u5728\u4e86\u738b\u5927\u4eba\u7684\u8eab\u4fa7\uff0c\u7531\u6b64\u523b\u8d77\uff0c \u5c0f\u6587\u5df2\u8eab\u62c5\u91cd\u4efb\uff0c\u5168\u529b\u4fdd\u62a4\u738b\u5c11\u537f\u7684\u5b89\u5168\u4e86\u3002 \u3000\u3000\u738b\u5c11\u537f\u5904\u4e8b\u679c\u51b3\uff0c\u7acb\u523b\u4e0b\u4ee4\u628a\u82b1\u5385\u5e03\u7f6e\u6210\u4e00\u4e2a\u4e34\u65f6\u5ead\u5802\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5145\u4e66\u529e\uff0c\u81ea\u7136\u8981\u6539\u7740\u7537\u88c5\uff0c\u5c0f\u96c5\u548c\u60dc\u7389\u90fd\u5728\u5e2e\u5979\uff0c\u5367\u623f\u4e4b\u4e2d\uff0c\u5c31\u77a7\u51fa\u5979\u4eec\u4e3b\u5a62\u95f4 \u7684\u79c1\u4eba\u60c5\u610f\u4e86\u3002\u7a0b\u5c0f\u8776\u7a7f\u4e86\u4e00\u4ef6\u84dd\u8272\u957f\u886b\uff0c\u5934\u9876\u4e5f\u6234\u4e0a\u6587\u58eb\u5dfe\uff0c\u8fd9\u5c31\u5b8c\u5168\u53d8\u4e86\uff0c\u5a07\u5a07\u5973\u53d8\u6210 \u4e86\u4fcf\u90ce\u541b\u3002 \u3000\u3000\u5c0f\u96c5\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u7f8e\u5c31\u662f\u7f8e\uff0c\u7537\u88c5\u5973\u88c5\u4e00\u822c\u6837\uff0c\u5c0f\u59d0\u5440\uff01\u8ff7\u6b7b\u6211\u5566\uff01\u6211\u8981\u5ac1\u7ed9\u4f60\u5440\uff01\u201d \u3000\u3000\u60dc\u7389\u7ad9\u5728\u4e00\u8fb9\u7b11\uff0c\u5979\u521d\u6765\u4e4d\u5230\uff0c\u4e0d\u6562\uff0c\u4e5f\u4e0d\u597d\u610f\u601d\u80e1\u8bf4\u516b\u9053\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u719f\u7ec3\u5730\u6234\u4e0a\u4e00\u5f20\u4eba\u76ae\u9762\u5177\uff0c\u5ae9\u7ea2\u5c0f\u8138\u7684\u4fcf\u90ce\u541b\uff0c\u53d8\u6210\u4e86\u4e00\u4e2a\u9762\u8272\u6de1\u9ec4\u7684\u4e2d\u5e74\u4eba\uff0c \u56e0\u4e3a\u9762\u5177\u4e0a\u53c8\u52a0\u4e86\u4e24\u6487\u77ed\u9aed\uff0c\u548c\u4e00\u7efa\u5c71\u7f8a\u80e1\u5b50\uff0c\u518d\u7528\u836f\u7269\u6d82\u4e0a\u7389\u8155\uff0c\u4f7f\u80a4\u8272\u4e00\u81f4\uff0c\u624d\u56de\u5934\u7b11 \u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u96c5\u5440\uff01\u4f60\u8fd8\u8981\u4e0d\u8981\u5ac1\u7ed9\u6211\uff01\u201d \u3000\u3000\u5c0f\u96c5\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6094\u5a5a\u4e86\uff0c\u59d0\u59d0\u7231\u4f60\u561b\uff0c\u4f46\u4f60\u8fd9\u4e48\u4e00\u6539\u626e\uff0c\u6211\u548c\u60dc\u7389\u53ef\u600e\u4e48\u529e\u5566\uff1f\u201d \u3000\u3000\u201c\u60dc\u7389\u73b0\u5728\u662f\u738b\u5927\u4eba\u7684\u4eb2\u621a\uff0c\u626c\u5dde\u5e9c\u8859\u4e2d\u7684\u8868\u5c0f\u59d0\uff0c\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u5982\u60f3\u8ddf\u53bb\u770b\uff0c\u53ea \u597d\u59d4\u5c48\u70b9\uff0c\u626e\u6210\u60dc\u7389\u7684\u4e2b\u5934\u4e86\uff01\u201d \u3000\u3000\u60dc\u7389\u6025\u9053\uff1a\u201c\u59d1\u5a18\u554a\uff01\u6211\u548c\u5c0f\u96c5\u59d0\u59d0\u6362\u4e2a\u8eab\u5206\uff0c\u5979\u4f5c\u8868\u5c0f\u59d0\uff0c\u6211\u505a\u4e2b\u5934\u3002\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u5c0f\u96c5\u7684\u5f62\u8c8c\uff0c\u6709\u53ef\u80fd\u65e9\u843d\u5728\u6740\u624b\u7ec4\u5408\u4e4b\u4e2d\uff0c\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e00\u758f\u5ffd\uff0c\u5c31\u4f1a\u9020\u6210 \u5927\u9519\uff0c\u5979\u8981\u53d8\uff0c\u5c31\u53d8\u7684\u548c\u5c0f\u96c5\u5b8c\u5168\u65e0\u5173\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u6211\u53d8\u7537\uff0c\u9ed1\u91cc\u4fcf\u3002\u201d\u5c0f\u96c5\u7b11\u9053\uff1a\u201c\u7edd\u5bf9\u4e0d\u62a2\u60dc\u7389\u59d0\u59d0\u7684\u98ce\u91c7\u3002\u201d \u3000\u3000\u201c\u9ed1\u53ef\u4ee5\uff0c\u4f46\u4e0d\u80fd\u4fcf\uff0c\u4fcf\u4e86\u5c31\u8fd8\u662f\u5c0f\u96c5\u3002\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f46\u4e5f\u4e0d\u80fd\u4e11\uff0c\u90a3\u4f1a\u5f15\u4eba\u6ce8\u76ee\uff0c \u7792\u4e0d\u8fc7\u5185\u884c\u4eba\u3002\u91d1\u767e\u5e74\u5e26\u7684\u4fdd\u9556\uff0c\u5f88\u53ef\u80fd\u90fd\u662f\u6c5f\u6e56\u9ad8\u624b\uff0c\u9664\u4e86\u8d3e\u82f1\u4e4b\u5916\uff0c\u4e5f\u4e0d\u80fd\u5c0f\u89d1\u4ed6\u4eba\u3002\u201d \u3000\u3000\u5c0f\u96c5\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u660e\u767d\u4e86\uff0c\u4e0d\u4f1a\u8bef\u4e8b\u3002\u201d \u3000\u3000\u5c0f\u96c5\u6613\u4e86\u5bb9\uff0c\u5bf9\u955c\u53d1\u7b11\uff0c\u5a07\u5a9a\u7684\u4fcf\u5973\u90ce\uff0c\u53d8\u6210\u4e2a\u76ae\u80a4\u5fae\u9ed1\uff0c\u9762\u8c8c\u5e73\u5eb8\u5927\u59d1\u5a18\uff0c\u518d\u6362\u4e86\u4e00 \u8eab\u4e0b\u4eba\u88c5\uff0c\u5c31\u662f\u4e2a\u5168\u4e0d\u8d77\u773c\u7684\u4e2b\u73af\u4e86\u3002 \u3000\u3000\u60dc\u7389\u5c31\u5927\u4e0d\u540c\u4e86\uff0c\u4e00\u8eab\u84dd\u7f0e\u5b50\u7ee3\u82b1\u8863\u88d9\uff0c\u9ad8\u96c5\u4e2d\u900f\u51fa\u5927\u65b9\uff0c\u770b\u4e0a\u53bb\u7f8e\u6781\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c91\u80e1\u5b50\u8bf4\u7684\u4e0d\u9519\uff0c\u5929\u4e0b\u624d\u6167\u8fc7\u4eba\uff0c\u6280\u827a\u7edd\u4f26\u7684\u7f8e\u5973\uff0c\u90fd\u6295\u5230\u5211\u90e8 \u603b\u6355\u53f8\u6765\u4e86\u3002\u201d \u3000\u3000\u60dc\u7389\u817c\u8146\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6bd4\u8d77\u5c0f\u6587\u3001\u5c0f\u96c5\u59d0\uff0c\u6211\u662f\u4e11\u591a\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u8981\u592a\u81ea\u8c26\u554a\uff01\u201d\u5c0f\u96c5\u9053\uff1a\u201c\u54b1\u4eec\u4e09\u4e2a\u4eba\u662f\u6625\u5170\u3001\u79cb\u83ca\uff0c\u5404\u5177\u5176\u7f8e\uff0c\u8fd8\u6709\u4f4d\u7d20\u559c\u59d0\u59d0\uff0c \u7559\u5728\u5317\u4eac\u6ca1\u6709\u6765\uff0c\u4e5f\u662f\u4e2a\u82b1\u6735\u4f3c\u7684\u7f8e\u4eba\u513f\u3002\u4e0d\u77e5\u54ea\u4e00\u4f4d\u6709\u798f\u7537\u4eba\uff0c\u80fd\u5a36\u5230\u4ed9\u5973\u822c\u7684\u603b\u6355\u5934\uff0c \u5c31\u662f\u8fd9\u56db\u4e2a\u82b1\u679d\u4eba\u6837\u7684\u4e2b\u73af\uff0c\u4e5f\u591f\u4ed6\u77a7\u4e0a\u8001\u534a\u5929\u4e86\u3002\u201d \u3000\u3000\u201c\u60dc\u7389\u662f\u672a\u6765\u7684\u675c\u592b\u4eba\uff0c\u53ef\u4e0d\u662f\u6211\u7684\u4e2b\u5934\u3002\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u5c0f\u96c5\u662f\u5211\u90e8\u603b\u6355\u53f8\u7684\u4e94\u54c1 \u5e26\u5200\u6355\u5feb\uff0c\u4e5f\u4e0d\u662f\u6211\u7684\u4e2b\u5934\u2026\u2026\u201d \u3000\u3000\u5c0f\u96c5\u63a5\u9053\uff1a\u201c\u6211\u4e0d\u8981\u4f5c\u5b98\uff0c\u6211\u8981\u4f5c\u4f60\u7684\u4e2b\u9b1f\uff0c\u6bcf\u5929\u9762\u5bf9\u7740\u5929\u4eba\u822c\u7684\u59d1\u5a18\uff0c\u770b\u7684\u5f00\u5fc3\u5566\uff01\u201d \u3000\u3000\u201c\u6ca1\u51fa\u606f\u554a\uff01\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u8981\u771f\u7684\u5ac1\u4e86\u4eba\uff0c\u4f60\u53ef\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u4f60\u5ac1\u4e86\uff0c\u6211\u5c31\u8ddf\u8fc7\u53bb\uff0c\u4f3a\u5019\u59d1\u5a18\uff0c\u4e5f\u4f3a\u5019\u59d1\u7237\u3002\u201d\u5c0f\u96c5\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u8981\u6211\uff0c\u6211\u5c31\u5230\u5e99 \u91cc\u505a\u5c3c\u59d1\u53bb\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6709\u70b9\u611f\u52a8\uff0c\u5401\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u8981\u662f\u6b7b\u4e86\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u6b7b\u4e86\uff0c\u6211\u548c\u5c0f\u6587\u90fd\u4f1a\u6d3b\u4e0d\u4e0b\u53bb\u3002\u201d\u5c0f\u96c5\u9053\uff1a\u201c\u4f46\u59d1\u5a18\u5185\u529f\u65e5\u76ca\u7cbe\u8fdb\uff0c\u5929\u4e0b\u80fd\u591f\u6740\u4f60 \u7684\u4eba\uff0c\u662f\u6108\u6765\u6108\u5c11\u4e86\u2026\u2026\u201d \u3000\u3000\u60dc\u7389\u542c\u5f97\u5fc3\u5934\u9707\u52a8\uff0c\u6697\u9053\uff1a\u867d\u662f\u8bf4\u7684\u73a9\u7b11\u8bdd\uff0c\u4f46\u5979\u4eec\u4e3b\u4ec6\u95f4\u60c5\u610f\u4e4b\u6df1\uff0c\u5b9e\u5df2\u8fbe\u751f\u6b7b\u540c\u547d \u4e4b\u5883\u3002 \u3000\u3000\u201c\u4e0d\u8ddf\u4f60\u80e1\u626f\u4e86\uff0c\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u51fa\u53bb\u77a7\u77a7\u5979\u3002\u201d \u3000\u3000\u82b1\u5385\u4e0a\u5df2\u5e03\u7f6e\u6210\u95ee\u6848\u5385\u5802\uff0c\u738b\u77e5\u5e9c\u5b98\u888d\u7389\u5e26\uff0c\u9ad8\u5c45\u4e0a\u5ea7\uff0c\u5c0f\u6587\u5c31\u7ad9\u5728\u738b\u5927\u4eba\u8eab\u65c1\u5de6\u4fa7\uff0c \u738b\u77e5\u5e9c\u7684\u4ece\u536b\u4f55\u5927\u5149\uff0c\u4e00\u8eab\u52b2\u88c5\uff0c\u5b88\u5728\u738b\u5927\u4eba\u53f3\u8fb9\uff0c\u5211\u90e8\u5e08\u7237\u5f20\u5b9d\u5584\u5750\u5728\u5de6\u4e0b\u4fa7\uff0c\u5012\u4e5f\u6709\u7740 \u5f00\u5802\u95ee\u6848\u7684\u6c14\u52bf\u3002 \u3000\u3000\u91d1\u767e\u5e74\u6765\u7684\u5f88\u5feb\uff0c\u7a0b\u5c0f\u8776\u521a\u521a\u5750\u597d\uff0c\u8859\u5f79\u5df2\u7980\u62a5\uff1a\u201c\u91d1\u5458\u5916\u7387\u5973\u5230\u6848\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u8d77\u8eab\u8fce\u5230\u82b1\u5385\u95e8\u53e3\uff0c\u738b\u575a\u5df2\u5e26\u7740\u91d1\u5458\u5916\u5230\u4e86\u82b1\u5385\u5916\u9762\u3002 \u3000\u3000\u8d3e\u82f1\u7d27\u968f\u5728\u91d1\u767e\u5e74\u7684\u8eab\u540e\u3002\u516b\u4e5d\u5c3a\u540e\uff0c\u6709\u4e00\u9876\u84dd\u8272\u5c0f\u8f7f\uff0c\u9664\u4e86\u56db\u4e2a\u8f7f\u592b\u4e4b\u5916\uff0c\u4e24\u6392\u8fd8\u968f \u62a4\u6709\u516b\u4e2a\u4eba\uff0c\u4fdd\u62a4\u4e4b\u5bc6\uff0c\u4f3c\u4e4e\u6bd4\u91d1\u767e\u5e74\u672c\u4eba\u8fd8\u91cd\u8981\u4e9b\u3002 \u3000\u3000\u91d1\u767e\u5e74\u9065\u9065\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u600e\u6562\u52b3\u52a8\u5e9c\u53f0\u5927\u4eba\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u4e5f\u62b1\u62f3\u8fd8\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u8bbc\u6848\u7d2f\u4eba\uff0c\u6709\u52b3\u91d1\u5458\u5916\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u91cc\uff0c\u90a3\u91cc\uff0c\u5e9c\u53f0\u5927\u4eba\u4f20\u5ba1\u82b1\u5385\uff0c\u5df2\u662f\u6cd5\u5916\u65bd\u4ec1\uff0c\u91d1\u67d0\u4eba\u94ed\u8bb0\u5728\u5fc3\uff0c\u201d\u91d1\u767e\u5e74\u9053\uff1a \u201c\u4f46\u613f\u65e5\u540e\u6709\u4e00\u56de\u62a5\u3002\u201d \u3000\u3000\u201c\u8a00\u91cd\u4e86\uff0c\u8a00\u91cd\u4e86\uff0c\u201d\u738b\u5927\u4eba\u9053\uff1a\u201c\u6cd5\u6709\u660e\u6587\uff0c\u4e0b\u5b98\u4e5f\u53ea\u80fd\u7a0d\u4f5c\u53d8\u901a\uff0c\u53ea\u6050\u662f\u59d4\u5c48\u91d1\u5458 \u5916\u548c\u4ee4\u5a9b\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u76ee\u5149\u6ce8\u610f\u7684\u662f\u91d1\u5e9c\u603b\u7ba1\u8d3e\u82f1\uff0c\u53cc\u76ee\u5374\u503e\u542c\u738b\u3001\u91d1\u4e24\u4eba\u7684\u4ea4\u8c08\u3002 \u3000\u3000\u5c0f\u8f7f\u5728\u82b1\u5385\u7684\u95e8\u53e3\u505c\u4e0b\uff0c\u516b\u4e2a\u968f\u8f7f\u62a4\u536b\uff0c\u7acb\u523b\u6563\u5e03\u6210\u4e00\u4e2a\u534a\u5706\u5f62\u7684\u4fdd\u62a4\u7f51\u3002 \u3000\u3000\u8f7f\u5e18\u542f\u52a8\uff0c\u91d1\u5c0f\u7709\u7f13\u7f13\u6b65\u51fa\u5c0f\u8f7f\u3002 \u3000\u3000\u738b\u5927\u4eba\u53cd\u5e94\u7075\u654f\uff0c\u4e00\u770b\u8fd9\u67b6\u5f0f\uff0c\u8981\u60dc\u7389\u4e0a\u524d\u8fce\u63a5\uff0c\u6050\u5c06\u5f15\u8bef\u4f1a\uff0c\u7acb\u523b\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u7389\u513f\uff0c \u5feb\u53bb\u63a5\u8fce\u91d1\u5c0f\u59d0\u3002\u201d \u3000\u3000\u60dc\u7389\u5e94\u4e86\u4e00\u58f0\uff0c\u7531\u82b1\u5385\u95e8\u540e\u8f6c\u51fa\u6765\uff0c\u6b3e\u6b3e\u83b2\u6b65\u8fce\u4e0a\u53bb\u3002 \u3000\u3000\u4e0d\u53eb\u60dc\u7389\u53eb\u7389\u513f\uff0c\u662f\u8868\u793a\u4e24\u4eba\u95f4\u7684\u4eb2\u5c5e\u5173\u7cfb\u3002 \u3000\u3000\u679c\u7136\uff0c\u6321\u5728\u5ead\u95e8\u95f4\u7684\u8d3e\u82f1\uff0c\u5411\u540e\u9000\u4e86\u4e24\u6b65\uff0c\u8ba9\u5f00\u53bb\u8def\uff0c\u5b88\u5728\u8f7f\u524d\u4e24\u4e2a\u6b66\u58eb\uff0c\u4e5f\u9000\u5230\u4e24\u8fb9\uff0c \u60dc\u7389\u7b11\u7740\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u91d1\u59d1\u5a18\uff0c\u59e8\u4e08\u8981\u6211\u6765\u966a\u4f60\uff0c\u5c0f\u59b9\u4f1a\u4e00\u76f4\u5b88\u5728\u91d1\u59d0\u59d0\u7684\u8eab\u4fa7\u4e0d\u79bb\u5f00\uff0c \u76f4\u5230\u4f60\u5ead\u5ba1\u5b8c\u6bd5\u3002\u201d \u3000\u3000\u738b\u5927\u4eba\u51fa\u52a8\u4e86\u81f3\u4eb2\u4f84\u5973\uff0c\u966a\u4f34\u91d1\u5c0f\u7709\uff0c\u8001\u72d0\u72f8\u91d1\u767e\u5e74\uff0c\u4e5f\u611f\u52a8\u7684\u6709\u70b9\u53cc\u76ee\u6e7f\u6da6\u4e86\uff0c\u4f4e\u58f0 \u9053\uff1a\u201c\u591a\u8c22\u5e9c\u53f0\u5927\u4eba\uff0c\u6211\u4eec\u7236\u5973\u90fd\u8499\u6069\u4e0d\u6d45\uff0c\u5c0f\u5973\u4e00\u5411\u5b64\u5904\u6df1\u95fa\uff0c\u5c11\u89c1\u573a\u9762\uff0c\u5982\u6b64\u5b89\u6392\uff0c\u5bf9 \u5979\u5e2e\u5fd9\u592a\u5927\u4e86\uff0c\u611f\u6fc0\u5440\uff01\u611f\u6fc0\u3002\u201d \u3000\u3000\u4e24\u4eba\u8c08\u8bdd\u4e4b\u95f4\uff0c\u60dc\u7389\u5df2\u6276\u7740\u91d1\u5c0f\u7709\uff0c\u8fdb\u5165\u4e86\u82b1\u5385\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u8033\u529b\u8fc7\u4eba\uff0c\u5927\u767d\u5929\u80fd\u542c\u5230\u4e94\u4e08\u5185\u6811\u53f6\u843d\u5730\u4e4b\u58f0\uff0c\u91d1\u767e\u5e74\u8bdd\u58f0\u867d\u7136\u4f4e\uff0c\u4f46\u7a0b\u59d1\u5a18\u4ecd \u7136\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002\u6697\u9053\uff1a\u738b\u5c11\u537f\u529e\u4e8b\u4e0d\u4f46\u5706\u878d\u901a\u8fbe\uff0c\u800c\u4e14\u60c5\u7406\u517c\u987e\uff0c\u5229\u7528\u673a\u4f1a\u7684\u624d\u80fd\uff0c\u4e5f\u662f \u5e38\u4eba\u96be\u53ca\uff0c\u5982\u82e5\u60dc\u7389\u4e0d\u662f\u59e8\u4f84\u5973\u7684\u540d\u4efd\uff0c\u8fd9\u4e2a\u8ba1\u8c0b\u5c31\u4e0d\u4f1a\u90a3\u4e48\u611f\u4eba\u4e86\u3002 \u3000\u3000\u5f53\u7136\uff0c\u60dc\u7389\u7684\u673a\u53d8\uff0c\u53e3\u871c\u8bdd\u751c\uff0c\u4e5f\u662f\u8fd9\u4e2a\u5e03\u5c40\u751f\u8272\u52a8\u4eba\u7684\u539f\u56e0\u3002 \u3000\u3000\u201c\u91d1\u8001\u8bf7\u5165\u5385\u4e2d\u5427\uff01\u201d\u738b\u77e5\u5e9c\u9996\u5148\u8f6c\u8eab\u6b65\u5165\u82b1\u5385\u4e2d\u3002 \u3000\u3000\u91d1\u767e\u5e74\u6325\u6325\u624b\uff0c\u793a\u610f\u8d3e\u82f1\u5b88\u5728\u5385\u95e8\u53e3\uff0c\u968f\u6765\u7684\u516b\u4e2a\u4fdd\u9556\uff0c\u5c31\u8fdc\u5728\u4e24\u4e09\u4e08\u5916\u4e86\u3002 \u3000\u3000\u8fd9\u516b\u4eba\uff0c\u4e5f\u90fd\u662f\u6c5f\u6e56\u4e0a\u4e00\u6d41\u9ad8\u624b\uff0c\u7a0b\u5c0f\u8776\u5c31\u8ba4\u8bc6\u56db\u4e2a\uff0c\u90a3\u662f\u548c\u5979\u52a8\u8fc7\u624b\u7684\u5bd2\u5c71\u56db\u5200\uff0c\u5176 \u4ed6\u7684\u56db\u4e2a\u4eba\u662f\u5feb\u5200\u738b\u526a\u3001\u94c1\u62f3\u4e25\u65b9\uff0c\u548c\u897f\u57df\u6765\u7684\u56de\u56de\u9ad8\u624b\u9a6c\u4fee\u3001\u9a6c\u5f3a\u3002 \u3000\u3000\u738b\u526a\u3001\u4e25\u65b9\uff0c\u662f\u884c\u8d70\u5728\u6c5f\u5357\u9053\u4e0a\u7684\u9ad8\u624b\uff0c\u5f88\u591a\u5b88\u62a4\u82b1\u5385\u56db\u5904\u7684\u6355\u5feb\u90fd\u8ba4\u8bc6\u4ed6\u4eec\uff0c\u5f7c\u6b64\u4e5f \u90fd\u6325\u624b\u3001\u70b9\u5934\u5730\u6253\u62db\u547c\uff0c\u9a6c\u4fee\u3001\u9a6c\u5f3a\u5374\u6ca1\u4eba\u8ba4\u8bc6\u4e86\uff0c\u4e24\u4eba\u6765\u81ea\u897f\u57df\uff0c\u5f88\u5c11\u5728\u6c5f\u5357\u9053\u4e0a\u8d70\u52a8\u3002 \u3000\u3000\u8fdb\u5165\u82b1\u5385\uff0c\u5f62\u52bf\u4e0d\u53d8\uff0c\u738b\u77e5\u5e9c\u9ad8\u5c45\u4e3b\u5ea7\uff0c\u5e08\u7237\u3001\u4e66\u529e\uff0c\u90fd\u5df2\u5c31\u4f4d\uff0c\u4e24\u4fa7\u8fd8\u6392\u7740\u516b\u4e2a\u8859\u5f79\uff0c \u5e78\u597d\u662f\u60dc\u7389\u5e26\u7740\u91d1\u5c0f\u7709\uff0c\u5728\u4e0b\u9996\u53f3\u4fa7\u4e24\u5f20\u6728\u6905\u4e0a\u5750\u7740\u3002 \u3000\u3000\u91d1\u767e\u5e74\u6781\u611f\u5c34\u5c2c\uff0c\u73af\u89c6\u5168\u573a\uff0c\u6ca1\u6709\u4ed6\u7684\u5ea7\u4f4d\u3002\u5fc3\u4e2d\u5fd6\u9053\uff1a\u5acc\u72af\u662f\u5973\u513f\uff0c\u5979\u90fd\u6709\u5ea7\u4f4d\uff0c\u6211 \u8fd9\u4e2a\u966a\u5ba1\u7684\u7236\u4eb2\uff0c\u8981\u8dea\u5728\u82b1\u5385\u5927\u5802\u4e0d\u6210\u3002 \u3000\u3000\u5e78\u597d\u738b\u77e5\u5e9c\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u66ff\u91d1\u767e\u5e74\u5b89\u4e2a\u5ea7\u4f4d\u3002\u201d \u3000\u3000\u7acb\u523b\u95ee\u6709\u8859\u5f79\u642c\u62b1\u6728\u6905\u8fc7\u6765\uff0c\u653e\u5728\u5de6\u4fa7\uff0c\u91d1\u767e\u5e74\u4e00\u6b20\u8eab\uff0c\u9053\uff1a\u201c\u8001\u673d\u8c22\u5750\u3002\u201d\u7f13\u6b65\u884c\u8fc7 \u53bb\uff0c\u843d\u4e86\u5ea7\u4f4d\uff0c\u548c\u5973\u513f\u6447\u6447\u76f8\u5bf9\uff0c\u80cc\u5bf9\u7a0b\u5c0f\u8776\u3002 \u3000\u3000\u8fd9\u4e5f\u662f\u738b\u77e5\u5e9c\u7684\u5b89\u6392\uff0c\u8fd9\u4e2a\u4eba\u8001\u5e74\u6210\u7cbe\uff0c\u751f\u6050\u770b\u591a\u4e86\u7a0b\u5c0f\u8776\u5fc3\u751f\u6000\u7591\uff0c\u4e8b\u540e\u63a2\u95ee\u8fd9\u4e2a\u4eba\uff0c \u5c82\u4e0d\u662f\u4e00\u4e2a\u9ebb\u70e6\u3002 \u3000\u3000\u771f\u662f\u987e\u8651\u5468\u5bc6\uff0c\u5fc3\u7ec6\u65e0\u9057\u3002 \u3000\u3000\u5c0f\u96c5\u5462\uff1f\u8eb2\u5728\u82b1\u5385\u4e00\u89d2\u5904\uff0c\u8868\u9762\u4e0a\u65e0\u6240\u4e8b\u4e8b\uff0c\u4f46\u5979\u7ad9\u7684\u4f4d\u7f6e\u5374\u80fd\u76d1\u89c6\u5168\u5385\u4e2d\u4eba\u7684\u4e3e\u52a8\uff0c \u8fde\u5927\u5385\u95e8\u53e3\u5916\uff0c\u4e5f\u53ef\u4ee5\u770b\u5230\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u91d1\u5c0f\u7709\u3002\u201d \u3000\u3000\u60dc\u7389\u4f4e\u58f0\u5728\u5979\u8033\u8fb9\u8bf4\u4e86\u51e0\u53e5\uff0c\u91d1\u5c0f\u7709\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u72af\u5973\u5728\u3002\u201d\u7ad9\u8d77\u8eab\u5b50\uff0c\u5728\u60dc\u7389\u6400\u6276 \u4e0b\uff0c\u8d70\u5411\u5802\u6848\u524d\u9762\u3002 \u3000\u3000\u201c\u4f60\u8eab\u4f53\u865a\u5f31\uff0c\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u4e0d\u7528\u8dea\u4e0b\u4e86\uff0c\u5c31\u7ad9\u5728\u5802\u53e3\u56de\u8bdd\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u8c22\u5927\u4eba\u4f53\u60dc\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u770b\u7684\u5f00\u5fc3\u6781\u4e86\uff0c\u5fd6\u9053\uff1a\u8fd9\u4e2a\u738b\u77e5\u5e9c\u554a\uff01\u5b9e\u5728\u662f\u4e00\u4e2a\u7231\u60dc\u5b50\u6c11\u7684\u597d\u5b98\uff0c\u6211\u91d1\u67d0\u4eba\uff0c \u4e00\u5b9a\u8981\u5c3d\u5fc3\u5c3d\u529b\u5730\u5e2e\u52a9\u4ed6\uff0c\u8ba9\u4ed6\u5b98\u8fd0\u4ea8\u901a\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5c31\u5750\u5728\u91d1\u5c0f\u7709\u5bf9\u9762\uff0c\u770b\u7684\u5341\u5206\u6e05\u695a\uff0c\u8fd9\u4e2a\u5a07\u5f31\u7684\u59d1\u5a18\uff0c\u786e\u5b9e\u4e0d\u4f1a\u6b66\u529f\uff0c\u4e00\u5200\u6bd5\u547d\uff0c \u523a\u5165\u4e86\u65b0\u90ce\u5fc3\u810f\u8981\u5bb3\uff0c\u4e00\u4e2a\u5a07\u5f31\u5973\u5b50\uff0c\u4e0d\u53ef\u80fd\u8ba4\u4f4d\u90a3\u4e48\u51c6\u786e\uff0c\u4e5f\u6ca1\u6709\u90a3\u6837\u5927\u7684\u6c14\u529b\u3002\u4f46\u65b0\u623f \u95e8\u7a97\u7d27\u95ed\uff0c\u6ca1\u6709\u7834\u574f\u75d5\u8ff9\uff0c\u51f6\u5200\u53c8\u63e1\u5728\u5979\u7684\u624b\u4e2d\uff0c\u8fd9\u51f6\u624b\u53c8\u4f1a\u662f\u8c01\u5462\uff1f\u8fd9\u786e\u662f\u4e00\u4ef6\u5927\u8d39\u601d\u91cf \u7684\u51f6\u6848\u3002 \u3000\u3000\u4f46\u95fb\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u91d1\u5c0f\u7709\uff0c\u4f60\u548c\u65b0\u90ce\u9a6c\u656c\u6587\uff0c\u662f\u5982\u4f55\u8ba4\u8bc6\u7684\uff1f\u201d \u3000\u3000\u201c\u5728\u4e00\u6b21\u670b\u53cb\u7684\u5bb4\u4f1a\u4e2d\u7ed3\u8bc6\uff0c\u6b64\u540e\u5c31\u65f6\u5e38\u5f80\u6765\uff0c\u9a6c\u656c\u6587\u5b66\u8bc6\u4e30\u5bcc\uff0c\u4eba\u53c8\u6587\u96c5\u591a\u793c\uff0c\u5bb6\u7236 \u4e5f\u5bf9\u4ed6\u8bc4\u4ef7\u5f88\u9ad8\u3002\u201d\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u9a6c\u5bb6\u6d3e\u4eba\u524d\u6765\u63d0\u4eb2\uff0c\u5bb6\u4eba\u5c31\u4e00\u53e3\u5141\u5a5a\u3002\u201d \u3000\u3000\u201c\u4f60\u548c\u9a6c\u516c\u5b50\u7ed3\u8bc6\u591a\u4e45\uff1f\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u9a6c\u5bb6\u4f55\u65f6\u6d3e\u4eba\u5230\u5e9c\u63d0\u4eb2\uff1f\u201d \u3000\u3000\u201c\u7ea6\u6709\u534a\u5e74\u65f6\u5149\uff0c\u201d\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u4f46\u6210\u4eb2\u65e5\u671f\uff0c\u7ea6\u5728\u5141\u5a5a\u4e00\u5e74\u4e4b\u540e\u3002\u201d \u3000\u3000\u738b\u5927\u4eba\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5bb6\u7236\u8981\u5efa\u4e00\u5e62\u5b85\u9662\uff0c\u4f5c\u4e3a\u966a\u5ac1\uff0c\u201d\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u7ae3\u5de5\u8d39\u65f6\uff0c\u4e00\u5e74\u591a\u624d\u5efa\u597d\u65b0\u623f\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u5e74\u591a\u4e2d\uff0c\u4f60\u4eec\u53ef\u6709\u5f80\u8fd8\uff1f\u201d \u3000\u3000\u201c\u5bb6\u7236\u949f\u7231\u656c\u6587\uff0c\u5e38\u9080\u9a6c\u516c\u5b50\u5bd2\u820d\u4fbf\u9910\uff0c\u4e5f\u966a\u5c0f\u7709\u5f39\u7434\u8d4b\u8bd7\uff0c\u76f8\u5904\u751a\u4e50\u3002\u201d \u3000\u3000\u53ea\u542c\u5979\u7b54\u8bdd\u7684\u6587\u96c5\uff0c\u5c31\u662f\u4e00\u4e2a\u8bfb\u4e66\u751a\u591a\u7684\u624d\u5973\u3002 \u3000\u3000\u201c\u5bf9\u9a6c\u516c\u5b50\u4e4b\u6b7b\uff0c\u4f60\u662f\u4e0d\u662f\u5f88\u4f24\u5fc3\u4e86\uff1f\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u4e3a\u4e86\u6f84\u6e05\u6848\u60c5\uff0c\u4f60\u8981\u5766\u8bda\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u65b0\u5a5a\u4e4b\u591c\uff0c\u5c0f\u7709\u56e0\u75b2\u52b3\u592a\u751a\uff0c\u8eab\u4f53\u53c8\u5f31\u4e0d\u80fd\u652f\uff0c\u7adf\u81ea\u5148\u884c\u7761\u53bb\u3002\u6e05\u9192\u4e4b\u540e\uff0c\u6211\u5374 \u624b\u63e1\u51f6\u5200\uff0c\u656c\u6587\u4e5f\u5df2\u6c14\u7edd\u800c\u901d\uff0c\u5929\u554a\uff01\u6211\u7adf\u6740\u4e86\u81ea\u5df1\u4e08\u592b\uff01\u6211\u60ca\u6050\u8fc7\u751a\uff0c\u5927\u58f0\u547c\u53eb\uff0c\u62a4\u9662\u7834 \u95e8\u800c\u5165\uff0c\u5c0f\u7709\u5df2\u809d\u80a0\u5bf8\u65ad\uff0c\u6025\u6012\u4ea4\u5e76\u4e2d\uff0c\u795e\u667a\u5ffd\u7136\u8ff7\u5931\uff0c\u5982\u975e\u5bb6\u7236\u5ef6\u533b\u8bca\u6cbb\uff0c\u5c0f\u7709\u6050\u6015\u5df2\u8ffd \u968f\u5148\u592b\u4e8e\u6cc9\u4e0b\u4e86\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u5f53\u65f6\u60c5\u666f\uff0c\u6211\u4e5f\u770b\u5230\uff0c\u771f\u662f\u96be\u4e3a\u4f60\u4e86\uff01\u201d\u738b\u77e5\u5e9c\u8138\u8272\u4e00\u6b63\uff0c\u53e3\u6c14\u4e5f\u53d8\u4e86\uff0c\u51b7\u51b7\u5730 \u8bf4\u9053\uff1a\u201c\u5728\u4f60\u548c\u9a6c\u656c\u6587\u76f8\u8bc6\u4e4b\u524d\uff0c\u53ef\u5426\u7ed3\u4ea4\u8fc7\u7537\u53cb\uff0c\u4f60\u8981\u4ece\u5b9e\u62db\u6765\uff0c\u4e0d\u53ef\u9690\u6666\u3002\u201d \u3000\u3000\u201c\u5bb6\u7236\u949f\u7231\u5c0f\u7709\uff0c\u65e0\u5fae\u4e0d\u81f3\uff0c\u4f46\u5bb6\u6559\u5374\u4e25\uff0c\u5c0f\u7709\u4ece\u672a\u5355\u72ec\u8e0f\u9752\u6e38\u8361\uff0c\u51fa\u5fc5\u62a4\u536b\u76f8\u4ece\uff0c\u6570 \u540d\u4e2b\u73af\u548c\u5b37\u5b37\u4f34\u968f\u540c\u884c\uff0c\u9a6c\u516c\u5b50\u4e4b\u524d\uff0c\u4e5f\u6ca1\u6709\u7ed3\u8bc6\u8fc7\u4efb\u4f55\u7537\u5b50\u3002\u201d \u3000\u3000\u201c\u4f60\u7f8e\u4e3d\u52a8\u4eba\uff0c\u5c82\u4f1a\u65e0\u4eba\u949f\u60c5\uff1f\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u6709\u6ca1\u6709\u7f20\u7ed5\u4f60\u7684\u7537\u5b50\uff0c\u4f60\u603b\u8be5\u6e05\u695a\u5427\uff1f\u201d \u3000\u3000\u201c\u5c0f\u7709\u786e\u5b9e\u4e0d\u77e5\uff0c\u5509\uff01\u7eb5\u6709\u5176\u4eba\uff0c\u4e5f\u65e0\u6cd5\u8fd1\u6211\u4e4b\u8eab\uff0c\u5c0f\u7709\u53ea\u7ed3\u8bc6\u8fc7\u4e00\u4e2a\u7537\u53cb\uff0c\u5c31\u662f\u9a6c\u516c \u5b50\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u4f60\u548c\u9a6c\u516c\u5b50\uff0c\u53ef\u66fe\u548c\u597d\uff1f\u201d \u3000\u3000\u8fd9\u5c31\u95ee\u7684\u6709\u70b9\u8f7b\u4f7b\u4e86\uff0c\u91d1\u767e\u5e74\u6709\u70b9\u706b\uff0c\u4f46\u4ed6\u4e5f\u5f88\u60f3\u4e86\u89e3\u5185\u60c5\uff0c\u7236\u5973\u4e4b\u95f4\u53c8\u4e0d\u4fbf\u8c08\u8bba\u6b64\u4e8b\uff0c \u5c31\u5fcd\u4e0b\u6ca1\u6709\u53d1\u4f5c\u3002 \u3000\u3000\u91d1\u5c0f\u7709\u6ee1\u8138\u7f9e\u7ea2\uff0c\u4f46\u4ecd\u7136\u5927\u65b9\u5730\u8bf4\u9053\uff1a\u201c\u9a6c\u516c\u5b50\u77e5\u4e66\u8fbe\u793c\uff0c\u4eba\u53c8\u541b\u5b50\uff0c\u5c11\u5e74\u7537\u5973\uff0c\u65e5\u4e45 \u76f8\u5904\uff0c\u60c5\u96be\u81ea\u7981\u65f6\uff0c\u8033\u9b13\u53ae\u78e8\u5219\u6709\uff0c\u4f46\u656c\u6587\u4ece\u672a\u5931\u793c\u5230\u8981\u5f3a\u5ea6\u5173\u5c71\u60c5\u666f\u3002\u65b0\u5a5a\u4e4b\u591c\uff0c\u5c0f\u7709\u53c8 \u7d2f\u6781\u7761\u53bb\uff0c\u9192\u6765\u5927\u6068\u5df2\u6210\uff0c\u6240\u4ee5\uff0c\u5c0f\u7709\u81f3\u4eca\u4ecd\u662f\u5904\u5b50\u4e4b\u8eab\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u60f3\u4e0d\u51fa\u8fd8\u6709\u4ec0\u4e48\u597d\u95ee\u7684\u4e86\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u9ebb\u70e6\u4f60\u4eec\u7236\u5973\uff0c\u5230\u6b64\u4e00\u884c\uff0c\u9000\u5802\u3002\u201d \u3000\u3000\u6296\u6296\u888d\u8896\uff0c\u8d77\u8eab\u51fa\u4e86\u82b1\u5385\uff0c\u5c0f\u6587\u548c\u4f55\u5927\u5149\u4e5f\u76f8\u968f\u53bb\u3002 \u3000\u3000\u5f20\u5b9d\u5584\u3001\u7a0b\u5c0f\u8776\u4e5f\u8ddf\u7740\u6084\u7136\u8d70\u4e86\u3002 \u3000\u3000\u6392\u5217\u4e24\u4fa7\u7684\u8859\u5f79\uff0c\u9c7c\u8d2f\u9000\u51fa\u4e86\u82b1\u5385\uff0c\u53ea\u6709\u738b\u575a\u8fd8\u966a\u7740\u8d3e\u82f1\u7ad9\u5728\u82b1\u5385\u95e8\u53e3\u4f4e\u58f0\u4ea4\u8c08\u3002 \u3000\u3000\u60dc\u7389\u6276\u7740\u91d1\u5c0f\u7709\uff0c\u9053\uff1a\u201c\u91d1\u59d0\u59d0\uff0c\u53ef\u4ee5\u8d70\u4e86\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u4f3c\u662f\u4e2a\u5f88\u591a\u60c5\u7684\u4eba\uff0c\u4e5f\u8bb8\u662f\u5979\u7684\u751f\u6d3b\u592a\u5bc2\u5bde\u4e86\uff0c\u8fde\u4e2a\u8981\u597d\u7684\u5973\u670b\u53cb\u4e5f\u6ca1\u6709\uff0c\u60dc\u7389 \u5bf9\u5979\u4e00\u756a\u7167\u987e\uff0c\u7adf\u4f7f\u5979\u52a8\u4e86\u611f\u60c5\uff0c\u63e1\u7740\u60dc\u7389\u4e00\u53ea\u624b\uff0c\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u8ddf\u6211\u5230\u5bd2\u820d\u4e2d\u4f4f\u51e0\u5929\u5427\uff01 \u6211\u4eec\u4e00\u89c1\u5982\u6545\uff0c\u5c0f\u59b9\u6709\u4e9b\u820d\u4e0d\u5f97\u79bb\u5f00\u4f60\u4e86\u3002\u201d \u3000\u3000\u60dc\u7389\u5fc3\u4e2d\u5fd6\u9053\uff1a\u56fa\u6240\u613f\u4e5f\uff0c\u4e0d\u4fbf\u8bf7\u5c14\uff0c\u4f46\u4e5f\u4e0d\u80fd\u81ea\u4f5c\u4e3b\u610f\u3002\u76ee\u5149\u8f6c\u52a8\uff0c\u770b\u4e0d\u5230\u4e00\u4e2a\u53ef\u4ee5 \u4f5c\u4e3b\u7684\u4eba\uff0c\u53ea\u597d\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u201c\u8ddf\u5979\u53bb\uff0c\u95fa\u623f\u76f8\u5904\uff0c\u8054\u5e8a\u591c\u8bdd\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u63a2\u51fa\u4e00\u4e9b\u5fc3\u4e2d\u7684\u9690\u79d8\uff0c\u4f46\u4e0d\u8bb8\u8d85\u8fc7\u4e09\u5929\u3002\u201d \u3000\u3000\u60dc\u7389\u542c\u51fa\u662f\u7a0b\u603b\u6355\u5934\u7684\u58f0\u97f3\uff0c\u65bd\u7528\u4e86\u4f20\u97f3\u4e4b\u672f\uff0c\u4f20\u8fbe\u4ee4\u8c15\uff0c\u9020\u5c31\u88c5\u4f5c\u4e00\u756a\u9053\uff1a\u201c\u597d\uff01\u6211 \u4e5f\u6709\u7740\u76f8\u9047\u77e5\u5df1\u7684\u611f\u89c9\uff0c\u201d\u56de\u987e\u4ecd\u7ad9\u5728\u5385\u89d2\u5c0f\u96c5\u4e00\u773c\uff0c\u9053\uff1a\u201c\u7980\u544a\u6211\u59e8\u4e08\u4e00\u58f0\uff0c\u5c31\u8bf4\u6211\u5230\u91d1 \u59d1\u5a18\u5bb6\u4e2d\u4f5c\u5ba2\u53bb\u4e86\uff0c\u5feb\u5219\u4e24\u5929\uff0c\u8fdf\u5219\u4e09\u65e5\uff0c\u4e00\u5b9a\u56de\u6765\u3002\u201d \u3000\u3000\u5c0f\u96c5\u9065\u9065\u4e00\u8eac\u8eab\u5b50\uff0c\u4e00\u4e2a\u662f\u5b57\uff0c\u771f\u662f\u5531\u4f5c\u5177\u4f73\u3002 \u3000\u3000\u91d1\u767e\u5e74\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5e9c\u53f0\u5927\u4eba\u901a\u60c5\u8fbe\u7406\uff0c\u4e0d\u4f1a\u8d23\u5907\u59d1\u5a18\u7684\uff0c\u771f\u8981\u6709\u4e8b\uff0c\u8001\u673d\u66ff\u4f60\u62c5 \u5f85\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u91d1\u4f2f\u4f2f\u3002\u201d\u60dc\u7389\u8bf4\u5b8c\u653e\u8bdd\uff0c\u53c8\u6df1\u6df1\u5730\u8eac\u8eab\u4e00\u793c\u3002 \u3000\u3000\u4e56\u4e56\uff0c\u8868\u73b0\u7684\u7cbe\u91c7\u4f20\u795e\uff0c\u5165\u6728\u4e09\u5206\uff0c\u91d1\u767e\u5e74\u9ad8\u5174\u5730\u634b\u7740\u80e1\u5b50\u7b11\u7740\u9053\uff1a\u201c\u4e56\u4e56\u5973\u554a\uff01\u5c0f\u7709\uff0c \u4f60\u4eec\u4e00\u8f7f\u53cc\u4e58\uff0c\u56de\u5230\u5bb6\uff0c\u6211\u8981\u597d\u597d\u8c22\u8c22\u4f60\u8fd9\u4f4d\u521d\u4ea4\u7684\u95fa\u4e2d\u597d\u53cb\u3002\u201d \u3000\u3000\u5728\u91cd\u91cd\u62a4\u536b\u4e0b\uff0c\u60dc\u7389\u548c\u91d1\u5c0f\u7709\u4e0a\u8f7f\u8d70\u4e86\uff0c\u91d1\u767e\u5e74\u968f\u5728\u8f7f\u540e\uff0c\u8d3e\u82f1\u843d\u540e\u4e00\u4e08\u8fdc\uff0c\u4ee5\u4fbf\u4e8e\u89c2 \u5bdf\u56db\u65b9\u7a81\u73b0\u7684\u53d8\u5316\uff0c\u8fd9\u4e2a\u4eba\u8db3\u667a\u591a\u8c0b\uff0c\u53c8\u5c0f\u5fc3\u8c28\u614e\u3002 \u3000\u3000\u738b\u575a\u4e5f\u5f88\u5c0f\u5fc3\uff0c\u76ee\u9001\u91d1\u767e\u5e74\u7b49\u4e00\u884c\u4eba\u8fdc\u53bb\u4e4b\u540e\uff0c\u624d\u7f13\u7f13\u9000\u5165\u82b1\u5385\u3002 \u3000\u3000\u9000\u5802\u79bb\u53bb\u7684\u738b\u77e5\u5e9c\uff0c\u53c8\u56de\u5230\u4e86\u82b1\u5385\uff0c\u53ea\u4e0d\u8fc7\uff0c\u5df2\u8131\u4e0b\u5b98\u670d\uff0c\u6362\u7a7f\u4e86\u4e00\u4ef6\u957f\u888d\uff0c\u5c0f\u6587\u5c31\u7ad9 \u5728\u4ed6\u8eab\u540e\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u8fd8\u662f\u626e\u4e66\u529e\u7684\u88c5\u675f\uff0c\u5e26\u7740\u77ed\u9aed\u548c\u5c71\u7f8a\u80e1\u5b50\uff0c\u5c0f\u96c5\u6ca1\u51fa\u73b0\uff0c\u60f3\u662f\u8eb2\u56de\u623f\u4e2d\u4e86\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u6325\u6325\u624b\uff0c\u738b\u575a\u9000\u51fa\u4e86\u82b1\u5385\u3002 \u3000\u3000\u201c\u603b\u6355\u5934\u53ef\u6709\u65b0\u7684\u53d1\u73b0\uff1f\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u91d1\u5c0f\u7709\u662f\u5426\u6709\u5acc\u7591\u4e4b\u5904\uff1f\u201d \u3000\u3000\u201c\u53ef\u4ee5\u786e\u5b9a\u7684\u662f\uff0c\u5979\u4e0d\u4f1a\u6b66\u529f\u3002\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f46\u8bfb\u4e66\u4e0d\u5c11\uff0c\u8a00\u8bcd\u6587\u96c5\uff0c\u53e3\u9f7f\u4f36\u4fd0\uff0c\u662f \u4e2a\u806a\u6167\u7684\u59d1\u5a18\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u4e0b\u5b98\u4e5f\u662f\u8fd9\u4e2a\u770b\u6cd5\uff0c\u4f46\u8fd9\u548c\u9a6c\u516c\u5b50\u7684\u6848\u60c5\u6ca1\u6709\u5173\u7cfb\u5427\uff1f\u201d \u3000\u3000\u201c\u91d1\u5c0f\u7709\u5e94\u8be5\u672a\u5b58\u6709\u6740\u5bb3\u9a6c\u516c\u5b50\u7684\u7528\u5fc3\uff0c\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5bf9\u9a6c\u656c\u6587\u7684\u60c5\u610f\u4e5f\u5f88\u771f\u8bda\u3002\u201d \u3000\u3000\u201c\u6240\u89c1\u7565\u540c\u3002\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u4e0b\u5b98\u4e00\u76f4\u4e0d\u76f8\u4fe1\u5979\u662f\u51f6\u624b\uff0c\u4e3a\u4e86\u9632\u6b62\u9a6c\u63d0\u7763\u548c\u91d1\u767e\u5e74\u4e00\u573a \u706b\u62fc\uff0c\u53ea\u597d\u628a\u4e8b\u60c5\u625b\u4e86\u4e0b\u6765\uff0c\u7b54\u5e94\u4ee5\u4e09\u6708\u4e3a\u671f\uff0c\u4fa6\u7834\u6b64\u6848\uff0c\u5509\uff01\u5e0c\u671b\u4ee5\u603b\u6355\u5934\u7684\u624d\u6167\uff0c\u52a9\u4e0b \u5b98\u4e00\u81c2\u4e4b\u529b\uff0c\u5ea6\u6b64\u96be\u5173\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd8\u6709\u4e24\u5904\u7591\u70b9\u6ca1\u80fd\u60f3\u901a\uff0c\u6240\u4ee5\uff0c\u53ea\u80fd\u8bf4\u91d1\u5c0f\u7709\u6ca1\u6709\u6740\u4eba\u7684\u5b58\u5fc3\uff0c\u4f46\u5374\u4e0d\u80fd \u6392\u9664\u5979\u662f\u51f6\u624b\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u5446\u4e86\u4e00\u5446\uff0c\u5fd6\u9053\uff1a\u4f5c\u6355\u5934\u7684\u5fc3\u601d\uff0c\u6bd4\u6211\u8fd9\u4f5c\u5b98\u7684\u8fd8\u591a\u8f6c\u4e86\u4e24\u4e2a\u5f2f\uff0c\u5012\u8981\u660e\u4e86\u4e00\u4e0b\uff0c \u5979\u5fc3\u4e2d\u60f3\u7684\u4ec0\u4e48\uff1f\u6df1\u6df1\u5438\u4e00\u53e3\u6c14\uff0c\u4f7f\u5fc3\u60c5\u5e73\u9759\u4e0b\u6765\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u8bdd\u600e\u4e48\u8bf4\u5462\uff1f\u65e2\u672a\u5b58\u884c\u51f6\u4e4b \u5fc3\uff0c\u4e5f\u6ca1\u6709\u884c\u51f6\u7684\u80fd\u529b\uff0c\u53c8\u4e0d\u80fd\u6392\u9664\u5979\u662f\u51f6\u624b\uff1f\u4e0b\u5b98\u6709\u70b9\u60f3\u4e0d\u901a\u4e86\uff0c\u603b\u6355\u5934\u53ef\u5426\u70b9\u62e8\u3001\u70b9 \u62e8\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u65b0\u623f\u8840\u6848\uff0c\u532a\u5937\u6240\u601d\u554a\uff01\u6b63\u5e38\u7684\u60c5\u666f\u4e0b\uff0c\u5f88\u96be\u7a81\u7834\uff0c\u4e5f\u4e0d \u662f\u4e00\u822c\u4eba\u80fd\u591f\u5e03\u4e0b\u5982\u6b64\u4e00\u4e2a\u8be1\u79d8\u4e4b\u5c40\uff0c\u4f46\u4e8b\u51fa\u5fc5\u6709\u56e0\uff0c\u5c31\u8981\u6709\u5e03\u5c40\u4eba\u7684\u8eab\u5206\u4e86\uff0c\u4e0d\u662f\u5bcc\u8c6a\u3001 \u9ad8\u4f4d\u7684\u4eba\u5f88\u96be\u6709\u8fd9\u4e2a\u80fd\u529b\u2026\u2026\u201d \u3000\u3000\u738b\u77e5\u5e9c\u70b9\u70b9\u5934\uff0c\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u76ee\u7684\u5462\uff1f\u8981\u52a8\u7528\u4ec0\u4e48\u6837\u7684\u9ad8\u4eba\uff0c\u624d\u80fd\u9020\u6210\u8fd9\u6837\u4e00\u4ef6\u6f2b\u65e0 \u5934\u7eea\u7684\u5947\u6848\uff1f\u201d \u3000\u3000\u201c\u76ee\u7684\u662f\u60f3\u5f15\u8d77\u9a6c\u63d0\u7763\u548c\u91d1\u767e\u5e74\uff0c\u706b\u62fc\u4e00\u573a\uff01\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u95f9\u7684\u5929\u7ffb\u5730\u8986\uff0c\u9a6c\u63d0\u7763\u624b \u63e1\u91cd\u5175\uff0c\u4e00\u65e6\u95ef\u4e0b\u5927\u7978\uff0c\u4ed6\u5982\u4f55\u5584\u540e\u5462\uff1f\u90a3\u65f6\uff0c\u4e3a\u9003\u907f\u56fd\u6cd5\u5236\u88c1\uff0c\u5c31\u53ef\u80fd\u2026\u2026\u201d\u7a81\u7136\u4f4f\u53e3\u4e0d \u8bf4\u4e86\u3002 \u3000\u3000\u738b\u5c11\u537f\u542c\u5f97\u8138\u8272\u5927\u53d8\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u6015\u5440\uff01\u4ed6\u7edf\u5e05\u7684\u662f\u6c5f\u5357\u6700\u7cbe\u9510\u7684\u6c34\u5e05\uff0c\u81ea\u527f\u706d\u4e09\u6e56\u532a \u5bc7\u540e\uff0c\u6269\u5145\u5230\u5341\u4e07\u5927\u519b\uff0c\u91d1\u767e\u5e74\u518d\u6709\u94b1\uff0c\u4e5f\u65e0\u6cd5\u548c\u5927\u519b\u5bf9\u6297\uff0c\u903c\u7684\u9a6c\u63d0\u7763\u60c5\u6025\u9020\u53cd\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u53e6\u4e00\u4e2a\u6700\u5927\u7684\u53d7\u5bb3\u4eba\uff0c\u4f1a\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u738b\u77e5\u5e9c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u6211\uff01\u514d\u5b98\u7f62\u804c\uff0c\u89e3\u4eac\u53d7\u5ba1\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u5929\u4e0b\u805a\u96c6\u8d22\u5bcc\u6700\u591a\u7684\u540d\u57ce\uff0c\u4e5f\u5c06\u6ca6\u5165\u5927\u52ab\u96be\u4e2d\uff0c\u5982\u518d\u88ab\u4eba\u9738\u5360\u4e86\u8fd9\u626c\u5dde \u57ce\u805a\u79ef\u7684\u8d22\u5bcc\uff0c\u5c82\u4e0d\u662f\u5982\u864e\u6dfb\u7ffc\uff0c\u4f60\u60f3\u89e3\u4eac\u53d7\u5ba1\uff0c\u8fd8\u8981\u6709\u70b9\u8fd0\u6c14\u624d\u884c\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u5bf9\u5440\uff01\u6211\u719f\u6089\u626c\u5dde\u5f62\u52bf\u3001\u6c11\u60c5\uff0c\u9a6c\u63d0\u7763\u771f\u88ab\u903c\u4e0a\u9020\u53cd\u4e00\u9014\uff0c\u7edd\u4e0d\u4f1a\u8ba9 \u6211\u79bb\u5f00\u626c\u5dde\uff0c\u6700\u597d\u7684\u529e\u6cd5\u662f\u6740\u4e4b\u4ee5\u7edd\u540e\u60a3\u3002\u8fd9\u60f3\u6cd5\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u4f46\u5374\u60ca\u5413\u5730\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4f83\u4f83\u800c\u8c08\uff0c\u9053\uff1a\u201c\u5f88\u663e\u7136\u9a6c\u63d0\u7763\u662f\u88ab\u4eba\u8bbe\u8ba1\u9677\u5bb3\uff0c\u574f\u5728\u4ed6\u7edf\u9886\u4e86\u6700\u7cbe\u9510\u7684\u6c34\u5e08\uff0c \u4f60\u738b\u5927\u4eba\u5b98\u58f0\u4e0d\u9519\uff0c\u5df2\u5f15\u8d77\u4e86\u540f\u90e8\u5173\u6ce8\uff0c\u5f88\u53ef\u80fd\u88ab\u8c03\u5165\u5e9c\u5802\uff0c\u51fa\u638c\u8981\u804c\uff0c\u65b0\u623f\u8840\u6848\u672c\u6765\u548c\u4f60 \u65e0\u5173\uff0c\u4f46\u6848\u53d1\u626c\u5dde\uff0c\u4f60\u5374\u662f\u626c\u5dde\u77e5\u5e9c\uff0c\u6000\u58c1\u5176\u7f6a\uff0c\u5d1b\u8d77\u5f53\u671d\u4e00\u6587\u4e00\u6b66\u4e24\u4e2a\u65b0\u79c0\uff0c\u90fd\u53ef\u80fd\u4f1a\u53d7 \u682a\u8fde\uff0c\u8c01\u4f1a\u7b56\u5212\u51fa\u5982\u6b64\u4e00\u4e2a\u5e03\u5c40\uff0c\u4e00\u8ba1\u5bb3\u53cc\u8d24\u5462\uff1f\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u53cc\u76ee\u5374\u795e\u5149\u51dd\u805a\uff0c\u9ed8\u5bdf\u738b\u5c11 \u537f\u7684\u53cd\u5e94\u3002 \u3000\u3000\u201c\u771f\u662f\u7978\u4ece\u5929\u964d\u554a\uff01\u201d\u738b\u77e5\u5e9c\u795e\u60c5\u9eef\u7136\u5730\u9053\uff1a\u201c\u4e00\u4ef6\u65b0\u623f\u547d\u6848\uff0c\u7ade\u542b\u6709\u5982\u6b64\u91cd\u5927\u7684\u9634\u8c0b\uff0c \u5982\u975e\u603b\u6355\u5934\u8fc7\u4eba\u7684\u5224\u4e8b\u624d\u534e\uff0c\u4e0b\u5b98\u8fd8\u4e00\u76f4\u8499\u5728\u9f13\u4e2d\uff0c\u5509\uff01\u6211\u4e5f\u771f\u8981\u597d\u597d\u5730\u60f3\u4e00\u60f3\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8d35\u5e9c\u5df2\u4e0d\u7528\u592a\u8fc7\u5fc3\u7126\u4f24\u795e\uff0c\u63a8\u65ad\u4e4b\u8a00\uff0c\u7a76\u7adf\u4e0d\u662f\u4e8b\u5b9e\uff0c\u4f55\u51b5\uff0c \u5c0f\u8776\u5df2\u5148\u6709\u8b66\u89c9\uff0c\u5b9a\u4f1a\u5168\u529b\u8ffd\u6c42\uff0c\u5f25\u7978\u673a\u5148\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u82e6\u7b11\u4e00\u4e0b\u9053\uff1a\u201c\u5ba6\u6d77\u4e4b\u4e2d\uff0c\u6697\u6d41\u6c79\u6d8c\uff0c\u8986\u821f\u706d\u9876\uff0c\u8fd8\u4e0d\u77e5\u7978\u7531\u4f55\u6765\u3002\u5c11\u537f\u8fd9\u4fee\u8eab \u4e00\u4e8b\uff0c\u8fd8\u5f97\u4e0b\u756a\u5de5\u592b\u624d\u6210\u3002\u201d\u82e6\u7b11\u4e00\u4e0b\u63a5\u9053\uff1a\u201c\u4f46\u8fd9\u4e2a\u5e03\u5c40\u7684\u5173\u952e\uff0c\u8fd8\u662f\u65b0\u623f\u8840\u6848\uff0c\u7834\u4e86\u6b64 \u6848\uff0c\u5c31\u4e07\u4e8b\u5e73\u5b89\uff0c\u81f3\u5c11\uff0c\u53ef\u4ee5\u6682\u65f6\u7a33\u4f4f\u5927\u5c40\uff0c\u91d1\u5c0f\u7709\u65e2\u65e0\u5fc3\u53c8\u65e0\u529b\uff0c\u600e\u4e48\u4f1a\u6740\u4e86\u9a6c\u516c\u5b50\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6697\u6697\u70b9\u5934\uff0c\u5fd6\u9053\uff1a\u738b\u5c11\u537f\u662f\u4e2a\u597d\u5b98\uff0c\u4ed6\u672c\u8eab\u867d\u53d7\u60ca\u5413\uff0c\u53ef\u662f\u8fd8\u5ff5\u5ff5\u4e0d\u5fd8\u6848\u60c5\uff0c\u5bf9 \u4ed6\u7684\u6000\u7591\uff0c\u770b\u6765\u662f\u6709\u4e9b\u591a\u5fc3\u4e86\uff01\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u6848\u60c5\u7684\u8be1\u5f02\uff0c\u53ef\u80fd\u7275\u6d89\u5230\u6c5f\u6e56\u4e0a\u9b3c\u57df\u4f0e\u4fe9\u4e86\uff0c \u7b2c\u4e00\u662f\u68a6\u5e7b\u4e4b\u5200\uff0c\u8fd9\u4e2a\u4eba\u662f\u5426\u771f\u6709\u5f79\u4eba\u4e0b\u5200\u7684\u5947\u6280\uff0c\u5f88\u96be\u8bc1\u5b9e\uff1f \u3000\u3000\u8fd9\u597d\u50cf\u4f20\u95fb\u4e2d\u7684\u79fb\u9b42\u5927\u6cd5\u4e00\u822c\uff0c\u80fd\u4f7f\u4e00\u4e2a\u4eba\u5728\u795e\u5fd7\u8ff7\u4e71\u4e2d\u51fa\u5200\u6740\u4eba\uff0c\u6f5c\u610f\u8bc6\u6fc0\u53d1\u51fa\u7684\u529b \u91cf\uff0c\u5e38\u5e38\u4f1a\u8d85\u8d8a\u4ed6\u672c\u8eab\u80fd\u529b\u5f88\u591a\u3002\u5f53\u7136\uff0c\u8fd8\u6709\u65bd\u7528\u836f\u7269\u7684\u4f20\u8bf4\uff0c\u80fd\u4f7f\u4eba\u5728\u9884\u5b9a\u65f6\u95f4\u4e00\u5230\uff0c\u4f5c \u51fa\u4e56\u5f20\u3001\u610f\u5916\u4e8b\u60c5\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u53f9\u9053\uff1a\u201c\u4e94\u7ecf\u56db\u4e66\u4e2d\uff0c\u5168\u65e0\u8fd9\u4e9b\u8bb0\u8f7d\uff0c\u53f2\u8bb0\u548c\u6e38\u4fa0\u5217\u4f20\u4e2d\uff0c\u4e5f\u65e0\u8fd9\u4e9b\u4f8b\u6848\uff0c\u5509\uff01 \u770b\u6765\uff0c\u5c11\u537f\u8fd8\u662f\u8bfb\u4e66\u592a\u5c11\u554a\uff01\u201d \u3000\u3000\u201c\u5927\u4eba\uff0c\u201d\u7a0b\u5c0f\u8776\u6e29\u67d4\u5730\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6b66\u529f\u4e2d\u4f20\u95fb\uff0c\u800c\u4e14\u662f\u65c1\u95e8\u5de6\u9053\u7684\u6280\u827a\uff0c\u8fde\u5c0f\u8776\u4e5f \u662f\u534a\u4fe1\u534a\u7591\u3002\u56db\u5927\u540d\u6355\uff0c\u4e2a\u4e2a\u4e45\u8d70\u6c5f\u6e56\uff0c\u89c1\u591a\u8bc6\u5e7f\uff0c\u7ecf\u9a8c\u9605\u5386\u90fd\u5f88\u4e30\u5bcc\uff0c\u4e5f\u65e0\u6cd5\u8bf4\u51fa\u4e2a\u6240\u4ee5 \u7136\u6765\uff0c\u5927\u4eba\u4f55\u7528\u5984\u81ea\u83f2\u8584\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u8bf4\u7684\u662f\u554a\uff01\u7acb\u56fd\u6570\u5343\u5e74\uff0c\u96c6\u4e66\u5341\u4e07\u5377\uff0c\u7a77\u5176\u4e00\u751f\uff0c\u4e5f\u65e0\u6cd5\u5168\u90e8\u8bfb\u901a\u554a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5bf9\u738b\u5c11\u537f\u53c8\u591a\u4e86\u4e00\u5c42\u8ba4\u8bc6\uff0c\u5fd6\u9053\uff1a\u5b98\u573a\u591a\u5e74\uff0c\u8fd8\u4fdd\u6709\u8bfb\u4e66\u4eba\u7684\u4e60\u6c14\uff0c\u8fd9\u5c31\u4e0d\u4f1a\u662f \u5978\u90aa\u4eba\u7269\u4e86\uff0c\u5fc3\u4e2d\u4e5f\u5c31\u5bf9\u738b\u5c11\u537f\u591a\u4e86\u4e00\u4efd\u5173\u6ce8\u4e4b\u60c5\u3002\u6b64\u60c5\u975e\u5f7c\u60c5\uff0c\u4e0d\u6d89\u513f\u5973\u4e4b\u79c1\uff0c\u5979\u8981\u5168\u529b \u4fdd\u4f4f\u8fd9\u4f4d\u597d\u5b98\uff0c\u4e3a\u671d\u5ef7\u7559\u4e00\u4e2a\u8d24\u76f8\u4eba\u624d\u3002 \u3000\u3000\u6709\u6b64\u5fc3\u5ff5\uff0c\u53e3\u6c14\u5c31\u6108\u53d1\u7684\u6e29\u67d4\u4e86\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u4eba\uff0c\u4f60\u8981\u591a\u4fdd\u91cd\u554a\uff01\u6848\u60c5\u6666\u6697\u4e0d\u660e\uff0c \u9634\u8c0b\u4ecd\u5728\u8fdb\u884c\uff0c\u5927\u4eba\u5b89\u5371\uff0c\u5b9e\u662f\u5173\u952e\u4e2d\u6781\u91cd\u8981\u7684\u4e00\u73af\u4e86\u3002\u201d \u3000\u3000\u5979\u8138\u4e0a\u7684\u4eba\u76ae\u9762\u5177\uff0c\u63a9\u53bb\u4e86\u59a9\u5a9a\u989c\u8272\uff0c\u7b11\u8d77\u6765\u5c31\u4e0d\u90a3\u4e48\u52a8\u4eba\u4e86\u3002 \u3000\u3000\u201c\u603b\u6355\u5934\u8a00\u5916\u4e4b\u610f\uff0c\u53ef\u662f\u8bf4\u6709\u4eba\u8981\u523a\u6740\u4e0b\u5b98\u4e86\uff1f\u201d\u738b\u5c11\u537f\u95ee\u7684\u662f\u5355\u5200\u76f4\u5165\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u867d\u7136\u53ea\u662f\u98ce\u95fb\uff0c\u4f46\u5c0f\u5fc3\u4e9b\u603b\u662f\u4e0d\u9519\uff0c\u5927\u4eba\u53ef\u4ee5\u4fe1\u4efb\u5c0f\u6587\uff0c\u5979\u4f1a\u5168\u529b\u4fdd\u62a4\u4f60 \u7684\u5b89\u5168\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u5fd6\u9053\uff1a\u90a3\u4e48\u4e2a\u5a07\u5f31\u6587\u96c5\u7684\u5c0f\u59d1\u5a18\uff0c\u771f\u80fd\u4fdd\u62a4\u6211\u7684\u5b89\u5168\u4e48\uff1f\u5fc3\u4e2d\u5b58\u7591\uff0c\u4eba\u8fd8\u662f\u7b11\u4e00 \u7b11\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\u65e9\u6709\u7b97\u8ba1\uff0c\u4e0b\u5b98\u5b89\u5fc3\u7684\u5f88\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7ad9\u8d77\u8eab\uff0c\u9053\uff1a\u201c\u5c0f\u8776\u544a\u9000\u3002\u201d\u76ee\u5149\u4e00\u63a0\u5c0f\u6587\uff0c\u63a5\u9053\uff1a\u201c\u60dc\u7389\u56de\u6765\u65f6\uff0c\u8981\u5979\u5728\u5e9c\u8859 \u7b49\u6211\uff0c\u597d\u597d\u548c\u4f55\u5927\u5149\u3001\u738b\u526f\u603b\u6355\u5934\u7814\u5546\u4e00\u4e0b\uff0c\u5168\u529b\u4fdd\u62a4\u5927\u4eba\u5b89\u5168\u3002\u201d \u3000\u3000\u5c0f\u6587\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u5c0f\u5a62\u660e\u767d\u4e86\uff0c\u5e9c\u8859\u4e2d\u597d\u50cf\u8fd8\u6709\u4e24\u4f4d\u9ad8\u624b\uff0c\u5c0f\u5a62\u4f1a\u548c\u4ed6\u4eec\u534f\u8c03\u5206\u5de5\uff0c \u8c03\u8ba1\u51fa\u4fdd\u62a4\u738b\u5927\u4eba\u7684\u5e03\u7f72\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u5934\u4e00\u7b11\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u7b11\u4e2d\u6709\u8d5e\u8d4f\u4e4b\u610f\uff0c\u662f\u5609\u8bb8\u5c0f\u6587\u7684\u7ec6\u5fc3\uff0c\u7adf\u7136\u77a7\u51fa\u4e86\u8859\u5f79\u4e2d\uff0c\u8fd8\u9690\u6709\u9ad8\u624b\uff0c\u5f53\u7136\u7a0b\u5c0f\u8776\u662f \u65e9\u5c31\u77a7\u51fa\u6765\u4e86\u3002 \u3000\u3000\u738b\u5927\u4eba\u6ca1\u8bf4\u8bdd\uff0c\u5fc3\u4e2d\u5374\u4e0d\u80fd\u4e0d\u4f69\u670d\u8fd9\u4e9b\u5973\u6355\u5feb\u7684\u5389\u5bb3\uff0c\u94c1\u7fce\u6697\u4f0f\u4e24\u4f4d\u9ad8\u624b\uff0c\u6df7\u5728\u8859\u5f79\u4e2d\uff0c \u4ee5\u4fdd\u62a4\u5e9c\u8859\u7684\u5b89\u5168\uff0c\u738b\u5927\u4eba\u662f\u77e5\u9053\u7684\uff0c\u8fd9\u672c\u662f\u4e00\u4ef6\u79d8\u5bc6\uff0c\u7adf\u7136\u7792\u4e0d\u8fc7\u8fd9\u51e0\u4e2a\u5c0f\u59d1\u5a18\u7684\u773c\u775b\u3002 \u3000\u3000\u5ffd\u7136\u60f3\u5230\u90a3\u4e00\u591c\u91d1\u5e9c\u5f52\u6765\uff0c\u8def\u9047\u62e6\u622a\uff0c\u4f55\u5927\u5149\u7a77\u4e0d\u654c\u4f17\uff0c\u5e78\u5f97\u8d3e\u82f1\u53ca\u65f6\u8d76\u6765\uff0c\u9010\u9000\u5f3a\u654c\uff0c \u90a3\u4e9b\u4eba\u4e2a\u4e2a\u662f\u9b41\u68a7\u5927\u6c49\uff0c\u62a1\u52a8\u5355\u5200\uff0c\u864e\u864e\u751f\u98ce\uff0c\u5c0f\u6587\u79c0\u4e3d\u6587\u96c5\uff0c\u53ea\u6050\u6321\u4e0d\u4f4f\u4eba\u5bb6\u4e00\u5200\uff0c\u5962\u8a00 \u4fdd\u62a4\u6211\u7684\u5b89\u5168\uff0c\u6050\u662f\u8a00\u8fc7\u5176\u5b9e\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u4e5f\u975e\u8f7b\u85d0\u5c0f\u6587\uff0c\u53ea\u662f\u60f3\uff1a\u5979\u90a3\u4e2a\u5a07\u5f31\u6a21\u6837\uff0c\u5982\u4f55\u80fd\u548c\u8eab\u9ad8\u4e03\u5c3a\u3001\u864e\u80cc\u718a\u8170\u7684\u5927\u6c49 \u52a8\u624b\u3002 \u3000\u3000\u53ef\u60dc\uff0c\u4ed6\u6ca1\u6709\u770b\u5230\u60dc\u7389\u548c\u5c91\u5578\u864e\u52a8\u624b\u7684\u8fc7\u7a0b\uff0c\u5982\u679c\u770b\u5230\u4e86\uff0c\u4e5f\u8bb8\u4f1a\u4f7f\u4ed6\u6df1\u690d\u5fc3\u4e2d\u7684\u610f\u5ff5 \u6539\u89c2\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u738b\u5927\u4eba\u5fc3\u4e2d\u4e5f\u6709\u76d8\u7b97\uff0c\u4f55\u5927\u5149\u548c\u94c1\u7fce\u5e03\u4e0b\u7684\u4f0f\u5175\uff0c\u5e76\u975e\u5f31\u8005\uff0c\u518d\u52a0\u738b\u575a\u526f\u603b\u6355\u5934 \u548c\u6570\u5341\u540d\u8859\u5f79\u3001\u4eb2\u5175\uff0c\u4fdd\u62a4\u4ed6\uff0c\u5e94\u8be5\u5f88\u4e25\u5bc6\u4e86\u3002\u4f46\u591a\u4e2a\u5c0f\u7f8e\u5973\u8ddf\u5728\u8eab\u8fb9\uff0c\u4e5f\u662f\u6709\u76ca\u65e0\u5bb3\uff0c\u804b \u5b50\u7684\u8033\u6735\uff0c\u5f53\u4e2a\u914d\u642d\u5427\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u548c\u5c0f\u96c5\u76f8\u5904\u5c45\u5ba4\uff0c\u7814\u8ba8\u7740\u626c\u5dde\u57ce\u76ee\u4e0b\u60c5\u52bf\uff0c\u7a0b\u59d1\u5a18\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u738b\u5c11\u537f \u80af\u62c5\u5f53\uff0c\u6682\u65f6\u628a\u4e71\u5c40\u7a33\u4f4f\uff0c\u4f46\u4e09\u4e2a\u6708\u7684\u9650\u671f\uff0c\u8f6c\u773c\u5c31\u5230\uff0c\u9a6c\u63d0\u7763\u662f\u5426\u80fd\u89c2\u5bdf\u51fa\u5927\u5c40\u7684\u5e55\u540e\u539f \u56e0\uff0c\u8fd8\u96be\u9884\u6599\u3002\u4f46\u72ec\u5b50\u4e4b\u6b7b\uff0c\u4f7f\u4ed6\u54c0\u75db\u6b32\u7edd\uff0c\u4e8b\u6240\u5fc5\u7136\uff0c\u77ed\u77ed\u7684\u4e09\u4e2a\u6708\uff0c\u5f88\u96be\u6709\u7597\u4f24\u6b62\u75bc\u7684 \u6548\u7528\u3002\u6211\u62c5\u5fc3\u63d0\u7763\u5e9c\u4e2d\uff0c\u5df2\u6f5c\u4f0f\u4e86\u654c\u4eba\u7684\u8033\u76ee\uff0c\u4fdf\u673a\u717d\u98ce\u70b9\u706b\uff0c\u4e00\u52a8\u624b\u5c31\u9020\u6210\u5927\u9519\uff0c\u90a3\u65f6\uff0c \u7bad\u5728\u5f26\u4e0a\uff0c\u5f13\u62c9\u6ee1\u6708\uff0c\u9a6c\u63d0\u7763\u5df2\u8eab\u4e0d\u7531\u5df1\uff0c\u4e5f\u4e0d\u5f97\u4e0d\u6740\u4e86\uff01\u201d \u3000\u3000\u201c\u4e00\u65e6\u6740\u622e\u5f00\u59cb\uff0c\u5c31\u6536\u624b\u4e0d\u53ca\u3002\u201d\u5c0f\u96c5\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u8981\u9632\u60a3\u672a\u7136\uff0c\u5148\u6e05\u9664\u9a6c\u63d0\u7763\u8eab\u4fa7\u7684 \u53ef\u7591\u4eba\u7269\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8c01\u662f\u53ef\u7591\u4eba\u7269\u5462\uff1f\u4ed6\u8eab\u4fa7\u7684\u6587\u6b66\u5e55\u50da\uff0c\u4e0d\u4e0b\u767e\u4eba\uff0c\u4ece\u536b\u4ea6\u6709\u767e\u4eba\u4e4b\u4f17\uff0c\u5982 \u65e0\u76f8\u5f53\u7684\u65f6\u95f4\u89c2\u5bdf\uff0c\u54ea\u91cc\u80fd\u67e5\u5f97\u6e05\u695a\uff1f\u201d \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u662f\u6709\u70b9\u56f0\u96be\uff0c\u4f46\u4ea6\u975e\u5168\u65e0\u7740\u624b\u4e4b\u5904\uff0c\u4eba\u6570\u867d\u591a\uff0c\u4f46\u80fd\u53c2\u4e0e\u4ed6\u5bb6\u52a1\u7684\u4eb2\u4fe1\u4eba\u7269\uff0c \u5e94\u8be5\u6709\u9650\uff0c\u5148\u5728\u90a3\u4e9b\u4eba\u8eab\u4e0a\u7740\u624b\uff0c\u4e0d\u8fc7\u5b9a\u662f\u8ffd\u968f\u4ed6\u591a\u5e74\u7684\u4eba\u3002\u96be\u9053\u8bf4\uff0c\u9634\u8c0b\u5b89\u6392\u5728\u5341\u5e74\u4e4b\u524d\uff0c \u90a3\u5e55\u540e\u4eba\uff0c\u5c82\u4e0d\u662f\u795e\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u5934\u7b11\u9053\uff1a\u201c\u4f60\u662f\u8d8a\u6765\u8d8a\u7cbe\u660e\u4e86\uff0c\u4e0d\u8fc7\uff0c\u9a6c\u63d0\u7763\u80fd\u7acb\u4e0b\u8d6b\u8d6b\u6218\u529f\uff0c\u5fc5\u662f\u4f4d\u667a\u52c7\u517c \u5907\u7684\u4eba\u7269\uff0c\u66f4\u96be\u5f97\u662f\u4ed6\u5175\u56f0\u91d1\u6c0f\u7236\u5973\u540e\uff0c\u53c8\u53ca\u65f6\u60ac\u5d16\u52d2\u9a6c\uff0c\u5374\u628a\u4e00\u6869\u56f0\u60d1\u4eba\u7684\u6848\u5b50\uff0c\u5957\u5728\u4e86 \u738b\u77e5\u5e9c\u7684\u5934\u4e0a\uff0c\u8fd9\u8bf4\u660e\u4e86\u9a6c\u63d0\u7763\u6781\u5177\u5fc3\u673a\uff0c\u8fd9\u6837\u4e00\u4e2a\u4eba\u7269\uff0c\u5c0f\u5c0f\u4f24\u5bb3\uff0c\u52a8\u4e0d\u4e86\u4ed6\uff0c\u4ed6\u5723\u7737\u6b63 \u9686\uff0c\u5982\u65e5\u5f53\u4e2d\uff0c\u540d\u5229\u4e5f\u5957\u4e0d\u4f4f\u4ed6\u2026\u2026\u201d \u3000\u3000\u5c0f\u96c5\u63a5\u9053\uff1a\u201c\u53ef\u4ee5\u7528\u7f8e\u4eba\u8ba1\u5440\uff01\u771f\u8981\u5982\u6b64\uff0c\u6211\u4eec\u53cd\u800c\u597d\u4e0b\u624b\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u4ed6\u624b\u63e1\u519b\u6743\uff0c\u5a01\u9547\u6c5f\u5357\uff0c\u626c\u5dde\u57ce\u53c8\u662f\u7f8e\u5973\u4f1a\u96c6\u4e4b\u5730\uff0c\u60dc\u7389\u53ef\u4ee5\u4e3a\u8bc1\uff0c\u9a6c \u63d0\u7763\u60f3\u7f8e\u5973\uff0c\u8fd8\u4e0d\u662f\u624b\u5230\u64d2\u6765\uff0c\u7f8e\u4eba\u8ba1\u4e5f\u884c\u4e0d\u901a\uff0c\u6240\u4ee5\uff0c\u5c31\u9009\u4ed6\u6700\u75db\u7684\u5730\u65b9\u4e0b\u624b\u4e86\uff0c\u624d\u80fd\u6fc0 \u8d77\u4ed6\u7684\u6012\u706b\uff0c\u9a6c\u656c\u6587\u662f\u4ed6\u7684\u72ec\u5b50\uff0c\u53c8\u662f\u4e2a\u54c1\u5b66\u517c\u4f18\u7684\u624d\u5b50\uff0c\u4e5f\u662f\u4ed6\u4e00\u751f\u7684\u5e0c\u671b\u6240\u5bc4\uff0c\u4e00\u65e6\u88ab \u4eba\u6740\u6b7b\uff0c\u4f55\u81f3\u662f\u809d\u80a0\u75db\u65ad\uff0c\u90a3\u80a1\u60b2\u5fff\u4e4b\u60c5\uff0c\u5f53\u5982\u6012\u706b\u711a\u80f8\u3002\u8c03\u52a8\u5927\u519b\uff0c\u56f4\u6740\u91d1\u6c0f\u7236\u5973\uff0c\u4e5f\u662f \u60c5\u96be\u81ea\u7981\u4e86\u3002\u201d \u3000\u3000\u201c\u60f3\u60f3\u4e5f\u662f\uff0c\u201d\u5c0f\u96c5\u9053\uff1a\u201c\u4f46\u4ed6\u8fd8\u80fd\u60ac\u5d16\u52d2\u9a6c\uff0c\u4e5f\u7b97\u662f\u4e2a\u597d\u5b98\u4e86\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u6211\u4eec\u8981\u4fdd\u62a4\u4ed6\uff0c\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u65b0\u623f\u547d\u6848\u4e00\u5b9a\u8981\u7834\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff01\u63d0\u7763\u5e9c\u4e2d\u5978\u4eba\u4f5c\u795f\uff0c\u201d\u5c0f\u96c5\u9053\uff1a\u201c\u6709\u5982\u706b\u79cd\u672a\u7184\uff0c\u968f\u65f6\u4f1a\u9020\u6210\u71ce\u539f\u5927\u7978\u5440\uff01\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u4ea4\u7ed9\u675c\u671b\u6708\u529e\uff0c\u7531\u6c5f\u5357\u5206\u6355\u53f8\u4e2d\uff0c\u9009\u4e00\u4e2a\u667a\u52c7\u517c\u5907\u7684\u4eba\uff0c\u6df7\u5165\u63d0\u7763\u5e9c\u53bb\uff0c\u6700\u597d \u80fd\u63a5\u8fd1\u9a6c\u63d0\u7763\u7684\u8eab\u4fa7\uff0c\u4e00\u65e6\u60c5\u52bf\u6709\u53d8\uff0c\u6562\u4e8e\u633a\u8eab\u8c0f\u8a00\uff0c\u8bf4\u660e\u5229\u5bb3\uff0c\u63ed\u53d1\u9634\u8c0b\u3002\u4ee5\u9a6c\u63d0\u7763\u7684\u667a \u6167\uff0c\u5f53\u53ef\u5206\u8fa8\u5229\u5bb3\u662f\u975e\uff0c\u6216\u80fd\u963b\u6b62\u4e00\u573a\u4e0d\u6d4b\u4e4b\u7978\u3002\u5f53\u7136\uff0c\u6211\u4eec\u5148\u628a\u8bf4\u8bcd\uff0c\u8f6c\u544a\u7ed9\u4ed6\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6ca1\u6709\u4eba\u80fd\u591f\u9884\u6d4b\u5341\u5e74\u540e\u7684\u53d8\u5316\uff0c\u6240\u4ee5\uff0c\u4e5f\u4e0d\u53ef\u80fd\u5728\u5341\u5e74\u524d\u5c31\u6d3e\u4e2a\u5978\u7ec6\uff0c\u9884 \u4f0f\u5728\u9a6c\u63d0\u7763\u7684\u8eab\u4fa7\uff0c\u4e0d\u8fc7\uff0c\u53ef\u4ee5\u7528\u94b1\u8d4f\u5440\uff01\u4e5f\u53ef\u4ee5\u7528\u4f60\u8bf4\u7684\u7f8e\u4eba\u8ba1\u8bf1\u4ed6\u5165\u5f40\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u8fd8\u662f\u5c0f\u59d0\u806a\u660e\uff0c\u5c0f\u96c5\u5c31\u6ca1\u6709\u60f3\u5230\u8fd9\u4e2a\u6cd5\u5b50\u3002\u73b0\u5728\uff0c\u54b1\u4eec\uff0c\u5e94\u8be5\u4f5c\u4e9b\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u8ffd\u67e5\u6848\u60c5\uff01\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5148\u627e\u675c\u671b\u6708\u8981\u4ed6\u5b89\u6392\u4eba\u624b\uff0c\u5165\u591c\u540e\u518d\u63a2\u67e5\u611a\u516c\u56ed\u6797\uff0c\u5e0c\u671b \u80fd\u65e9\u4e00\u4e9b\u9047\u4e0a\u68a6\u5e7b\u4e4b\u5200\uff0c\u770b\u770b\u8fd9\u4e2a\u6c5f\u5357\u7b2c\u4e00\u9b54\u5934\uff0c\u6709\u4ec0\u4e48\u60ca\u4eba\u7684\u6280\u827a\u3002\u201d \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u5165\u591c\u518d\u8d70\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e0d\uff01\u73b0\u5728\u8d70\uff01\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u53bb\u6539\u626e\u4e00\u4e0b\uff0c\u6211\u4eec\u8981\u5148\u719f\u6089\u4e00\u4e0b\u626c\u5dde\u5f62\u52bf\uff0c\u987a\u4fbf\u770b\u770b \u56db\u51e4\u697c\u3002\u201d \u3000\u3000\u5c0f\u96c5\u7b11\u9053\uff1a\u201c\u901b\u8857\u5440\uff01\u597d\u6781\u5566\uff01\u201d\u76ee\u5149\u51dd\u6ce8\u5728\u7a0b\u5c0f\u8776\u7684\u8138\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8fd9\u6837\u5b50\u4e0d \u597d\u770b\u54ea\uff0c\u8981\u4e0d\u8981\u6362\u4e2a\u6837\u5b50\uff0c\u56db\u51e4\u697c\u7684\u59d1\u5a18\uff0c\u90fd\u662f\u4e07\u4e2d\u9009\u4e00\u7684\u7f8e\u5973\uff0c\u59d0\u513f\u7231\u4fcf\u561b\uff0c\u5979\u4eec\u4e0d\u4f1a\u6b22 \u8fce\u4f60\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u592a\u597d\u770b\uff0c\u6613\u60f9\u9ebb\u70e6\uff0c\u4f55\u51b5\uff0c\u4eca\u5929\u4e0d\u5165\u697c\u3002\u201d \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u8fd9\u6837\u554a\uff01\u53ea\u5728\u697c\u5916\u770b\u770b\uff0c\u884c\uff0c\u4e2b\u5934\u561b\uff01\u603b\u662f\u542c\u5c0f\u59d0\u7684\u3002\u201d\u6613\u4e86\u88c5\uff0c\u79bb\u5f00\u5e9c\u8859\u3002 \u3000\u3000------------- \u3000\u3000\u5b64\u5251\u751f  \u626b\u63cf\u3000  \u695a\u5929\u4fa0\u5f71  OCR  \uff0c\u65e7\u96e8\u697c\u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u5341\u4e8c\u7ae0 \u3000\u3000\u91d1\u5c0f\u7709\u771f\u7684\u662f\u559c\u6b22\u60dc\u7389\uff0c\u5165\u4e86\u91d1\u5bb6\u5b85\u9662\uff0c\u5c31\u4e0d\u653e\u60dc\u7389\u79bb\u5f00\u5bf8\u6b65\u3002\u540c\u684c\u8fdb\u9910\u540e\uff0c\u7adf\u7136\u7559\u60dc \u7389\u4f4f\u5728\u5979\u7684\u95fa\u623f\u91cc\u540c\u69bb\u800c\u7720\uff0c\u60dc\u7389\u8868\u73b0\u7684\u5f88\u6e29\u67d4\uff0c\u4e5f\u5f88\u6709\u8010\u5fc3\uff0c\u7edd\u53e3\u4e0d\u63d0\u9a6c\u516c\u5b50\u7684\u4e8b\u3002 \u3000\u3000\u53cd\u5012\u662f\u91d1\u5c0f\u7709\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u4e3b\u52a8\u7684\u63d0\u5230\u4e86\u9a6c\u516c\u5b50\uff0c\u800c\u4e14\u628a\u4e2b\u5934\u3001\u4f7f\u5973\uff0c\u5168\u90fd\u9063\u51fa\u95fa\u623f\u3002 \u3000\u3000\u660e\u77e5\u95fa\u623f\u4e2d\u518d\u65e0\u4ed6\u4eba\uff0c\u91d1\u5c0f\u7709\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u56db\u4e0b\u77a7\u77a7\uff0c\u624d\u4f4e\u58f0\u9053\uff1a\u201c\u60dc\u7389\u59d0\u59d0\uff0c\u6211\u5fc3\u4e2d\u6709 \u4e00\u4ef6\u60f3\u4e0d\u901a\u7684\u79d8\u5bc6\uff0c\u4e00\u76f4\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u618b\u7684\u597d\u96be\u8fc7\uff0c\u597d\u75db\u82e6\u2026\u2026\u201d \u3000\u3000\u60dc\u7389\u63a5\u9053\uff1a\u201c\u65e2\u7136\u662f\u79d8\u5bc6\uff0c\u5c31\u4e0d\u8981\u8bf4\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u4e0d\uff01\u6211\u8981\u544a\u8bc9\u59d0\u59d0\uff0c\u9a6c\u516c\u5b50\u597d\u50cf\u662f\u6211\u6740\u7684\uff01\u201d \u3000\u3000\u60dc\u7389\u5fc3\u5934\u5fae\u9707\uff0c\u5401\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5c0f\u7709\u59d0\uff0c\u8fd9\u4e0d\u80fd\u4e71\u8bf4\u554a\uff01\u6740\u4eba\u662f\u8981\u507f\u547d\u7684\uff01\u201d \u3000\u3000\u201c\u6211\u4e5f\u89c9\u7740\u5947\u602a\uff0c\u6211\u54ea\u91cc\u6709\u6c14\u529b\uff0c\u4e00\u5200\u80fd\u628a\u4e00\u4e2a\u4eba\u6740\u6b7b\uff01\u201d\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u4f46\u8fd9\u51e0\u5929\u7cbe\u795e \u6e10\u590d\uff0c\u6740\u4eba\u7684\u7ecf\u8fc7\uff0c\u5728\u6211\u7684\u56de\u5fc6\u91cc\uff0c\u4e5f\u9010\u6e10\u5730\u6e05\u6670\uff0c\u6211\u8bb0\u5f97\u62ff\u8d77\u4e86\u5c0f\u5200\uff0c\u4e00\u5200\u523a\u4e86\u4e0b\u53bb\u2026\u2026\u201d \u7a81\u7136\u53cc\u624b\u8499\u8138\uff0c\u54ed\u4e86\u8d77\u6765\uff0c\u201c\u6211\u8981\u600e\u4e48\u529e\u554a\uff1f\u7adf\u7136\u4e00\u5200\u6740\u4e86\u6211\u7684\u4e08\u592b\uff0c\u4ed6\u662f\u6211\u6709\u751f\u4ee5\u6765\uff0c\u6700 \u4eb2\u7231\u7684\u4eba\uff0c\u6211\u5fc3\u4e2d\u597d\u4e0d\u5b89\uff0c\u597d\u75db\u82e6\u554a\uff0c\u6211\u662f\u4e2a\u6740\u592b\u7684\u51f6\u624b\uff01\u201d \u3000\u3000\u60dc\u7389\u5fc3\u4e2d\u5fd6\u9053\uff1a\u603b\u6355\u5934\u771f\u5389\u5bb3\uff0c\u60f3\u662f\u65e9\u5df2\u77a7\u51fa\u4e86\u91d1\u5c0f\u7709\u5fc3\u7406\u72b6\u51b5\u5df2\u4e0d\u7a33\u5b9a\uff0c\u624d\u8ba9\u6211\u966a\u5979 \u5230\u91d1\u5e9c\u4e2d\u6765\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u5374\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5c0f\u7709\uff0c\u51b7\u9759\u70b9\uff0c\u5982\u662f\u8bf4\u51fa\u6765\uff0c\u80fd\u4f7f\u4f60\u5fc3\u4e2d\u5e73\u5b89\u4e00\u4e9b\uff0c\u90a3 \u5c31\u8bf4\u51fa\u6765\u5427\uff01\u4e0d\u8fc7\uff0c\u8981\u8bf4\u51fa\u771f\u6b63\u7684\u611f\u89c9\uff01\u4e00\u4e2a\u4eba\u5fc3\u4e2d\u6709\u6068\uff0c\u624d\u4f1a\u6709\u6740\u4eba\u7684\u52a8\u673a\uff0c\u4ed6\u4f5c\u4e86\u4ec0\u4e48 \u8ba9\u4f60\u6068\u4ed6\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u771f\u5fc3\u559c\u7231\u4ed6\uff0c\u54ea\u91cc\u4f1a\u6068\u4ed6\uff1f\u201d\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u6211\u4e5f\u4ed4\u7ec6\u5730\u60f3\u8fc7\uff0c\u4ed6\u7559\u7ed9\u6211\u7684\uff0c\u5168\u662f \u7f8e\u597d\u56de\u5fc6\uff0c\u6ca1\u6709\u4e00\u70b9\u70b9\u8ba9\u6211\u538c\u6068\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u6740\u4e86\u4ed6\uff0c\u201d\u60dc\u7389\u8f7b\u8f7b\u63e1\u7740\u91d1\u5c0f\u7709\u7684\u624b\uff0c\u65e0\u9650\u6e29\u67d4\u5730\u8bf4\uff1a\u201c\u597d\u597d\u5730\u60f3\uff0c\u6162\u6162 \u5730\u8bf4\uff0c\u600e\u4e48\u4f1a\u53d1\u751f\u8fd9\u79cd\u5947\u602a\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u6211\u60f3\u4e0d\u901a\u7684\u79d8\u5bc6\u3002\u6211\u8fde\u4e00\u53ea\u5c0f\u9e1f\u4e5f\u4e0d\u6562\u4f24\u5bb3\uff0c\u600e\u4f1a\u6740\u4eba\uff1f\u201d\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u53ef\u662f\u7559 \u5728\u8111\u5b50\u91cc\u7684\u5370\u8c61\u660e\u6670\uff0c\u60f3\u4e86\u53c8\u60f3\uff0c\u8fd8\u662f\u4e00\u6837\u2026\u2026\u201d \u3000\u3000\u201c\u80fd\u4e0d\u80fd\u8bf4\u51fa\u7ecf\u8fc7\u7684\u60c5\u5f62\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u5f53\u65f6\uff0c\u662f\u4e0d\u662f\u7cbe\u795e\u604d\u60da\uff1f\u201d \u3000\u3000\u201c\u5f53\u65f6\u60c5\u666f\uff0c\u4f3c\u662f\u88ab\u4e00\u79cd\u65e0\u5f62\u7684\u529b\u91cf\u4fc3\u4f7f\u7740\u6211\uff0c\u8ff7\u8ff7\u7cca\u7cca\u7684\uff0c\u62ff\u8d77\u5c0f\u5200\u5c31\u523a\u4e86\u4e0b\u53bb\u3002\u201d \u91d1\u5c0f\u7709\u9053\uff1a\u201c\u4f46\u5728\u6211\u7684\u56de\u5fc6\u4e2d\uff0c\u5374\u5f88\u660e\u6717\u3001\u6e05\u695a\u3002\u201d \u3000\u3000\u60dc\u7389\u660e\u767d\u518d\u95ee\u4e0b\u53bb\u4e5f\u6ca1\u6709\u7ed3\u679c\uff0c\u8fd9\u662f\u68a6\u5e7b\u7684\u4e00\u5200\u3002\u4e00\u79cd\u795e\u79d8\u7684\u529b\u91cf\uff0c\u4fc3\u4f7f\u5979\u6210\u4e86\u6740\u4eba\u7684 \u51f6\u624b\u3002 \u3000\u3000\u201c\u60dc\u7389\u59d0\u59d0\uff0c\u4f60\u8981\u4e0d\u8981\u544a\u8bc9\u4f60\u59e8\u4e08\u738b\u5927\u4eba\uff1f\u201d \u3000\u3000\u201c\u4f60\u8bf4\u5462\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5f88\u6e05\u9192\uff0c\u544a\u8bc9\u6211\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u8bf4\u7ed9\u4ed6\u542c\uff0c\u201d\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u5ead\u5ba1\u65f6\uff0c\u6211\u5df2\u60f3\u62db\u8ba4\uff0c\u4f46\u6211\u7236\u4eb2\u5728\u4fa7\uff0c\u6211\u4e0d\u60f3\u592a\u4f24\u4ed6\u7684 \u5fc3\u3002\u201d \u3000\u3000\u60dc\u7389\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u4f1a\u60f3\u4e00\u60f3\uff0c\u518d\u4f5c\u51b3\u5b9a\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u7a81\u7136\u8bdd\u9898\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u60dc\u7389\u59d0\u59d0\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u6b66\u529f\uff1f\u201d \u3000\u3000\u201c\u4f1a\uff01\u4f60\u600e\u4e48\u5ffd\u7136\u60f3\u5230\u8fd9\u4ef6\u4e8b\u5462\uff1f\u201d \u3000\u3000\u201c\u8d3e\u82f1\u5077\u5077\u544a\u8bc9\u6211\uff0c\u8bf4\u4f60\u6b66\u529f\u5f88\u597d\uff0c\u8981\u6211\u5c0f\u5fc3\u5e94\u4ed8\u4f60\u3002\u201d\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u4f60\u63a5\u8fd1\u6211\u662f\u522b\u6709 \u7528\u5fc3\uff0c\u6211\u60f3\uff0c\u6700\u5927\u7684\u76ee\u7684\uff0c\u5c31\u662f\u60f3\u77e5\u9053\u6211\u662f\u4e0d\u662f\u51f6\u624b\uff1f\u6211\u544a\u8bc9\u4f60\u8fd9\u4e9b\u4e8b\uff0c\u4e3b\u8981\u7684\u539f\u56e0\uff0c\u662f\u6211 \u826f\u5fc3\u4e0a\u8d1f\u62c5\u592a\u91cd\uff0c\u6211\u4e0d\u80fd\u539f\u8c05\u81ea\u5df1\uff0c\u5c31\u7b97\u6211\u56e0\u6b64\u88ab\u5224\u5904\u65a9\u5211\uff0c\u4f60\u4e5f\u4e0d\u7528\u6127\u548e\uff0c\u6740\u4eba\u507f\u547d\u561b\uff01 \u6211\u5fc3\u7518\u60c5\u613f\u4e3a\u656c\u6587\u507f\u547d\uff0c\u6211\u73b0\u5728\u6d3b\u7684\u5f88\u75db\u82e6\uff0c\u60dc\u7389\u59d0\uff0c\u751f\u4e0d\u5982\u6b7b\u554a\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u5509\uff01\u5584\u826f\u7684\u5c0f\u7709\u59d0\u59d0\uff0c\u8fd9\u4e2d\u95f4\u7684\u5173\u952e\uff0c\u5c31\u5728\u90a3\u80a1\u4fc3\u4f7f\u4f60\u6740\u4eba\u7684\u795e\u79d8\u529b\u91cf\uff0c\u7531 \u4f55\u800c\u6765\uff0c\u6211\u4eec\u4f1a\u5168\u529b\u67e5\u4e2a\u660e\u767d\u3002\u5c0f\u7709\u59d0\uff0c\u4f60\u4e0d\u662f\u771f\u6b63\u7684\u51f6\u624b\uff0c\u771f\u51f6\u662f\u90a3\u80a1\u4fc3\u4f7f\u4f60\u6740\u4eba\u7684\u529b\u91cf\uff0c \u4f60\u597d\u597d\u5730\u6d3b\u4e0b\u53bb\uff0c\u770b\u6211\u4eec\u627e\u51fa\u771f\u51f6\uff0c\u66ff\u9a6c\u516c\u5b50\u62a5\u4ec7\uff0c\u4e5f\u66ff\u4f60\u6d17\u96ea\u51a4\u5c48\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u6211\u6068\u81ea\u5df1\u4e0d\u80fd\u63d0\u51fa\u6709\u7528\u7684\u7ebf\u7d22\uff0c\u201d\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u4e00\u5207\u5982\u540c\u505a\u68a6\uff0c\u9192\u6765\u68a6\u5883\u6210\u6068\u4e8b\u3002 \u60dc\u7389\u59d0\uff0c\u771f\u76f8\u5df2\u660e\uff0c\u4f60\u65e9\u4e9b\u56de\u53bb\u5427\uff01\u8d3e\u82f1\u5f88\u5389\u5bb3\uff0c\u4e00\u65e6\u4ed6\u77e5\u9053\u6211\u8bf4\u4e86\u5185\u60c5\uff0c\u4ed6\u4e3a\u4e86\u4fdd\u62a4\u6211\uff0c \u53ef\u80fd\u4f1a\u5bf9\u4f60\u4e0d\u5229\u3002\u201d \u3000\u3000\u60dc\u7389\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6b32\u8a00\u53c8\u6b62\uff0c\u91d1\u5c0f\u7709\u5f88\u806a\u660e\uff0c\u6709\u7740\u4e3e\u4e00\u53cd\u4e09\u7684\u80fd\u529b\uff0c\u4e0d\u80fd\u8bf4\u51fa\u5fc3\u4e2d\u4e4b\u7591\uff0c \u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u5c0f\u59b9\u8fd9\u5c31\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8d70\u5c31\u8d70\uff0c\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u91d1\u5c0f\u7709\u7a81\u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u6211\u9001\u4f60\uff0c\u6211\u4eec\u7275\u7740\u624b\u6162\u6162\u8d70\uff0c\u522b\u4eba\u5c31\u77a7\u4e0d\u51fa\u6765\uff0c\u4f60\u662f\u8981\u79bb \u5f00\u8fd9\u91cc\u4e86\u3002\u201d \u3000\u3000\u60dc\u7389\u77e5\u5979\u8981\u4fdd\u62a4\u81ea\u5df1\uff0c\u5012\u662f\u4e0d\u5fcd\u62d2\u7edd\u5979\u4e86\uff0c\u5c31\u8fd9\u6837\u5c0f\u624b\u7275\u5c0f\u624b\u5730\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u60dc\u7389\u76ee\u5149\u9510\u5229\uff0c\u53d1\u89c9\u82b1\u6811\u4e1b\u4e2d\uff0c\u9690\u4f0f\u7740\u4e0d\u5c11\u4eba\uff0c\u5728\u6ce8\u89c6\u7740\u5979\u4eec\uff0c\u4f46\u5374\u65e0\u4eba\u73b0\u8eab\u62e6\u8def\u3002\u770b \u5230\u88c5\u4f5c\u6ca1\u770b\u5230\uff0c\u91d1\u5c0f\u7709\u5374\u61f5\u65e0\u6240\u77e5\uff0c\u4f46\u5979\u5374\u88c5\u7684\u5f88\u81ea\u7136\uff0c\u8fb9\u8d70\u8fb9\u8bf4\u7b11\u3002\u9001\u60dc\u7389\u79bb\u5f00\u4e86\u91d1\u5bb6\u5b85 \u9662\uff0c\u4eba\u8fd8\u7ad9\u5728\u95e8\u53e3\u770b\u7740\u60dc\u7389\u8fdc\u8d70\u7684\u80cc\u666f\u6d88\u5931\uff0c\u624d\u9eef\u7136\u8f6c\u56de\u95fa\u623f\u3002 \u3000\u3000\u60dc\u7389\u8131\u79bb\u4e86\u91d1\u5c0f\u7709\u7684\u89c6\u7ebf\u4e4b\u540e\uff0c\u6545\u610f\u653e\u6162\u4e86\u811a\u6b65\uff0c\u5fc3\u4e2d\u5012\u5e0c\u671b\u6709\u4eba\u8ffd\u4e0a\u6765\uff0c\u597d\u597d\u5730\u6253\u4e00 \u67b6\u3002\u800c\u4e14\uff0c\u5e0c\u671b\u8ffd\u6765\u7684\u4eba\uff0c\u6700\u597d\u662f\u8d3e\u82f1\u3002\u65e2\u53ef\u4f1a\u4e00\u4f1a\u603b\u6355\u5934\u53e3\u4e2d\u7684\u9ad8\u624b\uff0c\u4e5f\u53ef\u4ee5\u85c9\u673a\u8bd5\u63a2\u4ed6 \u7684\u53cd\u5e94\u3002 \u3000\u3000\u4f46\u4e8b\u5b9e\u603b\u662f\u548c\u5fc3\u4e2d\u60f3\u6cd5\u4e0d\u540c\uff0c\u60dc\u7389\u8d70\u7684\u867d\u6162\uff0c\u4f46\u4ecd\u7136\u5e73\u5b89\u5730\u56de\u5230\u4e86\u626c\u5dde\u5e9c\u8859\uff0c\u6ca1\u6709\u4eba\u8ffd \u4e0a\u6765\uff0c\u4e5f\u6ca1\u4eba\u73b0\u8eab\u62e6\u8def\u3002 \u3000\u3000\u5c0f\u6587\u5feb\u6b65\u8fce\u4e0a\u6765\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u60dc\u7389\uff0c\u56de\u6765\u7684\u597d\u5feb\u5440\uff01\u662f\u4e0d\u662f\u51fa\u4e86\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u201c\u662f\u88ab\u91d1\u5c0f\u7709\u64b5\u56de\u6765\u7684\uff0c\u201d\u60dc\u7389\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5e78\u672a\u8fb1\u547d\uff0c\u91d1\u5c0f\u7709\u4eb2\u53e3\u62db\u8ba4\u4e86\u51f6 \u624b\u662f\u5979\uff0c\u6015\u79d8\u5bc6\u6cc4\u6f0f\uff0c\u8d3e\u82f1\u4f1a\u6740\u6211\u706d\u53e3\uff0c\u6240\u4ee5\u903c\u6211\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u5c0f\u6587\u9053\uff1a\u201c\u5927\u4e8b\u60c5\u554a\uff01\u53ef\u60dc\u5c0f\u59d0\u4e0d\u5728\uff0c\u8981\u4e0d\u8981\u544a\u8bc9\u738b\u5927\u4eba\u3002\u201d \u3000\u3000\u201c\u6682\u65f6\u4e0d\u5fc5\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u5979\u6301\u5200\u884c\u51f6\uff0c\u975e\u51fa\u672c\u610f\uff0c\u672c\u8eab\u4e5f\u786e\u5b9e\u6ca1\u6709\u6740\u4eba\u7684\u80fd\u529b\uff0c\u90a3\u662f \u68a6\u5e7b\u7684\u4e00\u5200\uff0c\u5509\uff01\u4e00\u5200\u53d6\u547d\u554a\uff01\u201d \u3000\u3000\u5c0f\u6587\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5148\u544a\u8bc9\u5c0f\u59d0\uff0c\u8be5\u4e0d\u8be5\u544a\u8bc9\u738b\u5927\u4eba\uff0c\u7531\u5979\u51b3\u5b9a\u3002\u201d \u3000\u3000\u201c\u5c0f\u6587\uff0c\u5c31\u7b97\u771f\u6709\u68a6\u5e7b\u4e4b\u5200\u8fd9\u4e2a\u4eba\uff0c\u4e5f\u7ec3\u6210\u4e86\u5f79\u4eba\u51fa\u5200\u7684\u5947\u6280\uff0c\u4f46\u4ed6\u4e00\u5b9a\u8981\u5728\u91d1\u5c0f\u7709\u7684 \u5de6\u53f3\u624d\u884c\u3002\u201d\u60dc\u7389\u9053\uff1a\u201c\u5982\u679c\u4e0d\u8bc6\u91d1\u5c0f\u7709\uff0c\u4eba\u5728\u6570\u91cc\u5916\uff0c\u5c31\u80fd\u5f79\u4f7f\u91d1\u5c0f\u7709\u51fa\u5200\uff0c\u5c82\u4e0d\u662f\u592a\u53ef \u6015\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u76f8\u4fe1\uff0c\u4e00\u4e2a\u4eba\u80fd\u7ec3\u6210\u8fd9\u79cd\u532a\u5937\u6240\u601d\u7684\u795e\u6280\uff0c\u80fd\u5728\u5f88\u9065\u8fdc\u7684\u5730\u65b9\uff0c\u5f79\u4f7f\u4e00\u4e2a\u7d20\u4e0d \u76f8\u8bc6\u7684\u4eba\u51fa\u5200\u6740\u4eba\uff01\u201d\u5c0f\u6587\u9053\uff1a\u201c\u68a6\u5e7b\u4e4b\u5200\u4e5f\u4e0d\u80fd\uff0c\u9664\u975e\u4ed6\u5df2\u5230\u4e86\u626c\u5dde\u2026\u2026\u201d \u3000\u3000\u201c\u800c\u4e14\uff0c\u8fd8\u8981\u5728\u91d1\u5c0f\u7709\u7684\u5de6\u53f3\u3002\u201d\u60dc\u7389\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u6709\u4e00\u79cd\u53ef\u4f7f\u4eba\u5fc3\u795e\u8ff7\u4e71\u7684\u836f\u7269\uff0c\u4f1a \u4f7f\u4eba\u5931\u53bb\u610f\u8bc6\u80fd\u529b\uff0c\u63a5\u53d7\u4e0b\u836f\u4eba\u7684\u6307\u4ee4\uff0c\u4f5c\u51fa\u4e00\u4e9b\u4e56\u5f20\u53cd\u5e38\u7684\u884c\u4e3a\u3002\u201d \u3000\u3000\u5c0f\u6587\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4e56\u4e56\uff0c\u4f60\u61c2\u8fd9\u4e9b\u836f\u7269\u554a\uff1f\u201d \u3000\u3000\u201c\u5148\u7236\u53f7\u79f0\u51b7\u9762\u795e\u533b\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u5c0f\u59b9\u53c8\u662f\u4ece\u5c0f\u5403\u836f\u957f\u5927\u7684\uff0c\u5bf9\u836f\u7269\u81ea\u7136\u662f\u7565\u77e5\u4e00\u4e8c \u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u8fd8\u53eb\u7565\u77e5\u4e00\u4e8c\u5440\uff01\u7b80\u76f4\u662f\u836f\u529b\u4e71\u795e\u4e86\uff0c\u201d\u5c0f\u6587\u9053\uff1a\u201c\u4f60\u8fd9\u8eab\u827a\u517c\u5357\u5317\u7684\u7edd\u6280\uff0c\u53ef\u90fd \u662f\u5403\u836f\u5403\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u5509\uff01\u5982\u65e0\u836f\u7269\u8f85\u52a9\uff0c\u5c0f\u59b9\u518d\u7ec3\u4e0a\u4e8c\u5341\u5e74\uff0c\u4e5f\u5f88\u96be\u8fbe\u6b64\u5883\u754c\u3002\u201d\u60dc\u7389\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4f60\u653e \u5fc3\uff0c\u5c0f\u59b9\u662f\u79c0\u624d\u9020\u53cd\uff0c\u4e09\u5e74\u4e0d\u6210\uff0c\u5148\u7236\u544a\u8bc9\u6211\u5f88\u591a\u836f\u7269\u5947\u5f02\u4e8b\u8ff9\uff0c\u53ef\u60dc\uff0c\u5374\u6ca1\u6559\u8fc7\u6211\u5982\u4f55\u914d \u5236\uff0c\u6211\u77e5\u9053\u5f88\u591a\u533b\u75c5\u7684\u836f\u65b9\u5b50\uff0c\u4f46\u5374\u4e0d\u77e5\u9053\u836f\u6750\u751f\u7684\u4ec0\u4e48\u6837\u5b50\uff0c\u5509\uff01\u5168\u662f\u7eb8\u4e0a\u8c08\u5175\u554a\uff01\u201d \u3000\u3000\u5c0f\u6587\u5bf9\u60dc\u7389\u591a\u4e86\u4e00\u4e9b\u4e86\u89e3\uff0c\u4e5f\u589e\u591a\u4e86\u51e0\u5206\u611f\u60c5\uff0c\u4f46\u5374\u5ead\u9662\u7a7a\u5730\u4e0a\u4e0d\u80af\u79fb\u52a8\uff0c\u60dc\u7389\u611f\u89c9\u5947 \u602a\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u6587\uff0c\u6211\u77e5\u9053\u836f\u7269\u7684\u6545\u4e8b\uff0c\u54b1\u4eec\u5230\u623f\u91cc\u8bf4\u5427\uff01\u7ad9\u5728\u8fd9\u91cc\u4e0d\u597d\u770b\u5566\uff0c\u4f60\u8fd9\u4e2a\u5c0f \u7537\u4eba\u4e0d\u5bb3\u7f9e\uff0c\u6211\u53ef\u662f\u8868\u5c0f\u59d0\u7684\u8eab\u5206\uff0c\u7ad9\u8fd9\u91cc\u4efb\u51ed\u98ce\u5439\u65e5\u6652\u7684\uff0c\u54ea\u91cc\u8fd8\u50cf\u6dd1\u5973\u554a\uff1f\u201d \u3000\u3000\u201c\u6709\u5973\u6000\u6625\uff0c\u5409\u58eb\u8bf1\u4e4b\uff0c\u6211\u8fd9\u4e2a\u5c0f\u7537\u4eba\u957f\u7684\u4fcf\u554a\uff01\u201d\u5c0f\u6587\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u6dd1\u5973\uff0c\u662f\u4e0d\u662f\u6709 \u70b9\u52a8\u5fc3\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd8\u541b\u660e\u73e0\u53cc\u5782\u6cea\uff0c\u6068\u4e0d\u76f8\u9022\u4e00\u6708\u524d\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u76f8\u9022\u6068\u665a\u5566\uff01\u201d \u3000\u3000\u201c\u5f88\u60f3\u8fdb\u95fa\u623f\uff0c\u548c\u4f60\u591a\u8c08\u8c08\uff0c\u53ef\u672c\u516c\u5b50\u91cd\u4efb\u5728\u8eab\uff0c\u4e0d\u80fd\u56e0\u79c1\u5e9f\u516c\u3002\u201d\u5c0f\u6587\u9053\uff1a\u201c\u603b\u6355\u5934 \u8c15\u4ee4\u68ee\u4e25\uff0c\u738b\u5927\u4eba\u771f\u8981\u53d7\u5230\u4e00\u70b9\u4f24\u5bb3\uff0c\u6211\u4e0d\u6b7b\u4e5f\u5f97\u8131\u4e00\u5c42\u76ae\u3002\u201d \u3000\u3000\u60dc\u7389\u6123\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4f60\u548c\u5979\u60c5\u540c\u59ca\u59b9\uff0c\u603b\u6355\u600e\u5fcd\u5fc3\u7ed9\u4f60\u90a3\u4e48\u60b2\u60e8\u7684\u60e9\u7f5a\u3002\u201d \u3000\u3000\u201c\u5176\u5b9e\uff0c\u6211\u548c\u5c0f\u96c5\u90fd\u662f\u5979\u7684\u4e2b\u5934\uff0c\u6253\u4e5f\u6253\u5f97\uff0c\u9a82\u4e5f\u9a82\u5f97\uff0c\u4f46\u5979\u5bf9\u5f85\u6211\u4eec\u5374\u5982\u540c\u59ca\u59b9\uff0c\u201d \u5c0f\u6587\u9053\uff1a\u201c\u6709\u4e86\u8fc7\u5931\uff0c\u771f\u88ab\u5979\u6253\u4e00\u987f\uff0c\u9a82\u51e0\u53e5\uff0c\u90a3\u5c31\u597d\u8fc7\u4e86\uff0c\u4f46\u5979\u4e0d\u4f1a\uff0c\u5979\u4f1a\u8eb2\u5728\u623f\u91cc\u81ea\u5df1 \u4f24\u5fc3\u96be\u8fc7\uff0c\u90a3\u6a21\u6837\u771f\u53eb\u4eba\u5fc3\u75bc\u5982\u7ede\uff0c\u6068\u4e0d\u5f97\u81ea\u7edd\u8c22\u7f6a\u2026\u2026\u201d \u3000\u3000\u201c\u6162\u6162\u6162\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u5982\u662f\u4f60\u529b\u4e0d\u4ece\u5fc3\uff0c\u6709\u6240\u95ea\u5931\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u53c8\u4e0d\u540c\uff01\u201d\u5c0f\u6587\u9053\uff1a\u201c\u5168\u529b\u4ee5\u8d74\uff0c\u4ecd\u96be\u56de\u5929\uff0c\u6280\u4e0d\u5982\u4eba\uff0c\u9519\u4e0d\u5728\u6211\uff0c\u5c0f\u59d0\u81ea\u4f1a\u8ba4\u547d\uff0c \u6211\u6240\u8c13\u7684\u8fc7\u5931\uff0c\u662f\u7c97\u5fc3\u5927\u610f\uff0c\u5077\u61d2\u758f\u5ffd\u2026\u2026\u201d \u3000\u3000\u201c\u5c0f\u6587\uff0c\u4e0d\u8981\u592a\u62c5\u5fc3\uff0c\u6211\u4f1a\u5168\u529b\u5e2e\u52a9\u4f60\u3002\u201d\u60dc\u7389\u9053\uff1a\u201c\u7559\u6211\u5728\u5e9c\u8859\uff0c\u4e5f\u8bb8\u5c31\u662f\u4e5f\u8981\u6211\u5c3d \u4efd\u5fc3\u529b\u3002\u201d \u3000\u3000\u5c0f\u6587\u9053\uff1a\u201c\u6211\u60f3\u4e5f\u6709\u8fd9\u4e2a\u7528\u5fc3\uff0c\u8fd9\u6b21\u5c0f\u59d0\u5343\u91cc\u4e0b\u626c\u5dde\uff0c\u6709\u4e24\u4ef6\u5927\u4e8b\uff0c\u4e00\u662f\u7ef4\u62a4\u738b\u5927\u4eba\uff0c \u4e3a\u5927\u660e\u671d\u7559\u4e00\u4ee3\u8d24\u76f8\u4eba\u9009\u3002\u5f53\u7136\uff0c\u4e5f\u8981\u5148\u770b\u770b\u4ed6\u662f\u4e0d\u662f\u680b\u6881\u4e4b\u6750\u2026\u2026\u201d \u3000\u3000\u201c\u4e25\u547d\u4f60\u4fdd\u62a4\u4ed6\u5bf8\u6b65\u4e0d\u79bb\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u53ef\u8868\u793a\u738b\u5927\u4eba\u5df2\u5165\u4e86\u5c0f\u59d0\u7684\u6cd5\u773c\u3002\u201d \u3000\u3000\u5c0f\u6587\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u8bf4\u51fa\u53bb\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u6050\u6015\u8fde\u56db\u65b9\u5927\u6355\u5934\uff0c\u90fd\u8fd8\u4e0d\u77e5\u5e95\u7ec6\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u6211\u660e\u767d\uff0c\u8c22\u8c22\u5c0f\u6587\u59d0\u7684\u539a\u7231\uff0c\u840d\u6c34\u76f8\u9022\uff0c\u80fd\u89c6\u6211\u6709\u5982\u77e5\u5df1\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u7231\u561b\uff01\u79c0\u5916\u6167\u4e2d\uff0c\u53c8\u6709\u70b9\u72e1\u9ee0\uff0c\u201d\u5c0f\u6587\u9053\uff1a\u201c\u548c\u5c0f\u96c5\u4e00\u6837\uff0c\u673a\u667a\u591a\u53d8\uff0c\u624d\u80fd\u53ef\u5f53 \u5927\u4efb\uff0c\u5c0f\u59d0\u4e5f\u770b\u4e0a\u4f60\u5566\uff01\u7834\u683c\u91cd\u7528\u554a\uff01\u4e5f\u96be\u602a\u4e09\u65b9\u5927\u6355\u5934\uff0c\u5fc3\u4e2d\u90fd\u6709\u70b9\u4e0d\u670d\u6c14\uff0c\u8981\u8003\u9a8c\u4e00\u4e0b \u4f60\u7684\u6b66\u529f\u4e86\uff0c\u9700\u77e5\u4e00\u65b9\u526f\u53f8\u4e3b\uff0c\u662f\u6b63\u4e94\u54c1\u7684\u5b98\u7ea7\u5440\uff01\u201d \u3000\u3000\u201c\u6211\u624d\u4e0d\u5e0c\u7f55\u4ec0\u4e48\u5b98\u54c1\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u5c0f\u59b9\u662f\u96c5\u4eba\uff0c\u8f7b\u6de1\u540d\u5229\u5440\uff01\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u53ea\u6155\u9e33\u9e2f\u4e0d\u7fa1\u5b98\u5566\uff01\u201d\u5c0f\u6587\u9053\uff1a\u201c\u675c\u671b\u6708\u51e0\u4e16\u4fee\u6765\u7684\u798f\u4efd\uff0c\u80fd\u5a36\u5230\u4f60\u8fd9\u4e2a\u53c8\u7f8e \u53c8\u80fd\uff0c\u6587\u6b66\u5168\u624d\u7684\u5c0f\u5ab3\u5987\uff01\u201d \u3000\u3000\u201c\u4e0d\u8ddf\u4f60\u80e1\u626f\u5566\uff01\u6211\u53bb\u6362\u8863\u670d\uff0c\u5148\u52d8\u67e5\u4e00\u4e0b\u5e9c\u8859\u7684\u5f62\u52bf\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u4e00\u65e6\u6709\u4e8b\uff0c\u624d\u53ef \u5e94\u53d8\u3002\u201d \u3000\u3000\u201c\u5148\u597d\u597d\u4f11\u606f\u4e00\u4e0b\uff0c\u4f60\u5e26\u56de\u7684\u6d88\u606f\uff0c\u80fd\u5e2e\u52a9\u603b\u6355\u5934\u6709\u6240\u51b3\u65ad\uff0c\u662f\u5927\u529f\u4e00\u4ef6\u3002\u201d\u5c0f\u6587\u9053\uff1a \u201c\u5176\u5b9e\uff0c\u5e9c\u8859\u4e2d\u4e5f\u6709\u9ad8\u624b\uff0c\u5316\u8eab\u8859\u5f79\uff0c\u5728\u6697\u4e2d\u4fdd\u62a4\u738b\u5927\u4eba\uff0c\u4ed6\u867d\u672a\u62d2\u7edd\u5c0f\u59d0\u63a8\u8350\u6211\u4fdd\u62a4\u4ed6\u7684 \u5de5\u4f5c\uff0c\u4f46\u4ed6\u5fc3\u4e2d\u5374\u4e0d\u4ee5\u4e3a\u7136\uff0c\u89c9\u7740\u6211\u5a07\u5c0f\u7626\u5f31\uff0c\u5982\u4f55\u80fd\u5f53\u5927\u4efb\u3002\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u8fd8\u4e0d\u77e5\u9053\u81ea\u5df1\u5904 \u5883\u7684\u5371\u9669\uff0c\u4e00\u65e6\u6709\u4eba\u5165\u5e9c\u884c\u523a\uff0c\u90a3\u53ef\u662f\u6c5f\u6e56\u4e0a\u4e00\u6d41\u6280\u827a\u7684\u6740\u624b\uff0c\u6211\u62c5\u5fc3\u662f\u6765\u4eba\u592a\u591a\u4e86\uff0c\u4e00\u4e2a \u4eba\u7167\u987e\u4e0d\u5468\u3002\u201d \u3000\u3000\u201c\u4e0d\u8981\u62c5\u5fc3\uff0c\u8fd8\u6709\u60dc\u7389\u5728\u6b64\uff0c\u54b1\u4eec\u597d\u597d\u8868\u73b0\u4e00\u4e0b\uff0c\u8ba9\u738b\u5927\u4eba\u89c1\u8bc6\u4e00\u756a\uff0c\u770b\u4ed6\u4ee5\u540e\u8fd8\u6562\u4e0d \u6562\u5c0f\u770b\u5973\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u6587\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6709\u4f60\u5e2e\u52a9\uff0c\u6211\u662f\u653e\u5fc3\u591a\u4e86\uff0c\u7b2c\u4e8c\u4ef6\u4e8b\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u7834\u4e86\u8fd9\u65b0\u623f\u547d\u6848\uff0c\u5f25\u7978\u65e0\u5f62\uff0c\u201d\u60dc\u7389\u63a5\u9053\uff1a\u201c\u633d\u6551\u8fd9\u5ea7\u540d\u57ce\uff0c\u514d\u6ca6\u5165\u4e00\u573a\u52ab\u6740\u4e2d\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u85c9\u673a\u6d88\u51cf\u6c5f\u5357\u9053\u4e0a\u8fd9\u7fa4\u76ee\u65e0\u6cd5\u7eaa\u7684\u6740\u624b\uff0c\u201d\u5c0f\u6587\u9053\uff1a\u201c\u4ed6\u4eec\u592a\u56a3\u5f20\u4e86\uff0c\u8ba1\u4ef7\u53d6\u547d\uff0c \u6740\u4eba\u6536\u94b1\u3002\u603b\u6355\u5934\u5f97\u5230\u7684\u8baf\u606f\u8bf4\uff0c\u4ed6\u4eec\u8fd1\u65e5\u4e2d\u6b63\u51c6\u5907\u56f4\u6740\u4e86\u675c\u671b\u6708\uff0c\u4ee5\u4fbf\u660e\u76ee\u5f20\u80c6\u5730\u4e88\u53d6\u4e88 \u6c42\u3002\u201d \u3000\u3000\u60dc\u7389\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u53ef\u6076\u554a\uff01\u4e0d\u80fd\u9976\u6055\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u9664\u6076\u52a1\u5c3d\uff0c\u6240\u4ee5\uff0c\u4e00\u51fa\u624b\u5c31\u4e0d\u7528\u5fc3\u5b58\u4ec1\u6148\uff0c\u56de\u623f\u4f11\u606f\u53bb\u5427\uff01\u201d\u5c0f\u6587\u9053\uff1a\u201c\u6211\u53bb\u770b \u770b\u738b\u5927\u4eba\u3002\u201d\u70b9\u5934\u4e00\u7b11\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u60dc\u7389\u539f\u672c\u4e00\u8154\u6b22\u60a6\uff0c\u5374\u88ab\u5c0f\u6587\u4e00\u53e5\u8bdd\uff0c\u53d8\u6210\u6ee1\u8179\u5fe7\u6101\uff0c\u62c5\u5fc3\u675c\u671b\u6708\u771f\u8981\u88ab\u6740\uff0c\u81ea\u5df1\u5c82\u4e0d \u6210\u671b\u95e8\u5c0f\u5be1\u5987\uff0c\u771f\u662f\u672a\u7ecf\u4eba\u4e8b\u5148\u4e27\u592b\uff0c\u7a7a\u6765\u4eba\u95f4\u8d70\u4e00\u906d\u3002 \u3000\u3000\u4f46\u5979\u81ea\u5c0f\u5c31\u72ec\u7acb\u751f\u6d3b\u60ef\u4e86\uff0c\u517b\u6210\u575a\u5fcd\u7684\u6027\u683c\uff0c\u60f3\u5230\u6355\u5934\u751f\u6d3b\uff0c\u672c\u5c31\u662f\u5200\u5934\u8214\u8840\u7684\u751f\u6daf\uff0c \u65e2\u5165\u6b64\u884c\uff0c\u4e5f\u4e0d\u80fd\u6028\u5929\u5c24\u4eba\u4e86\uff0c\u5df2\u77e5\u6b64\u4e8b\uff0c\u653e\u5728\u5fc3\u4e0a\uff0c\u6697\u4e2d\u591a\u6ce8\u610f\u4ed6\u7684\u5b89\u5168\uff0c\u627e\u5230\u673a\u4f1a\uff0c\u70b9 \u4ed6\u4e00\u70b9\u3002 \u3000\u3000\u7a81\u7136\u60f3\u5230\u4e86\u603b\u6355\u5934\u4e00\u518d\u5730\u8981\u6c42\u4ed6\u4eec\u56db\u65b9\u5927\u6355\u5934\uff0c\u7d27\u5bc6\u8fde\u7cfb\uff0c\u8d70\u5728\u4e00\u8d77\uff0c\u5929\u4e0b\u80fd\u6709\u51e0\u4eba\uff0c \u6297\u5f97\u4f4f\u8fd9\u56db\u5927\u540d\u6355\u8054\u624b\u4e4b\u529b\uff0c\u59d1\u5a18\u554a\uff01\u4f60\u771f\u7684\u7231\u62a4\u5c5e\u4e0b\uff0c\u5374\u53c8\u4e0d\u7740\u75d5\u8ff9\uff0c\u9ad8\u660e\u554a\uff01\u53ef\u656c\u554a\uff01 \u3000\u3000\u675c\u671b\u6708\u5462\uff1f\u6b63\u5750\u4e00\u682a\u5927\u6811\u4e0b\u9762\uff0c\u80cc\u501a\u5728\u6811\u5e72\u4e0a\u4f11\u606f\uff0c\u7a7f\u4e00\u8eab\u9ed1\u5e03\u886b\u88e4\uff0c\u7834\u635f\u5904\u5904\uff0c\u8fd8\u6253 \u4e86\u51e0\u4e2a\u5927\u8865\u4e01\uff0c\u5927\u6811\u4e0b\u8fd8\u653e\u4e86\u526f\u7a7a\u4e86\u7684\u7af9\u7ba9\u7b50\u3002\u767d\u7699\u7684\u76ae\u80a4\uff0c\u4e5f\u53d8\u9ed1\u4e86\uff0c\u770b\u4e0a\u53bb\u6709\u70b9\u50cf\u4e5e\u4e10\u3002 \u4f46\u6241\u62c5\u516b\u5c3a\u957f\uff0c\u653e\u7f6e\u5728\u8eab\u65c1\uff0c\u8fd9\u5c31\u8868\u660e\u4e86\uff0c\u662f\u9760\u6c14\u529b\u5403\u996d\u7684\u62c5\u592b\uff0c\u4f46\u5982\u65cb\u5f00\u6241\u62c5\u770b\uff0c\u90a3\u5c31\u5168 \u7a7f\u6886\u4e86\uff0c\u91cc\u9762\u662f\u540d\u6ee1\u6c5f\u5357\u7684\u4e03\u661f\u5251\u5566\uff01 \u3000\u3000\u4e8e\u627f\u5fd7\u4e5f\u5750\u5728\u5bf9\u9762\u4e0b\u4e0d\u8fdc\u53e6\u4e00\u68f5\u5927\u6811\u4e0b\u9762\uff0c\u4e5f\u6709\u6241\u62c5\u7af9\u7ba9\u7b50\uff0c\u8863\u7740\u4e5f\u76f8\u540c\uff0c\u4e00\u773c\u5c31\u53ef\u770b \u660e\u767d\uff0c\u4e24\u4eba\u662f\u540c\u884c\uff0c\u5077\u5f97\u6d6e\u751f\u534a\u65e5\u95f2\uff0c\u5750\u5728\u8fd9\u6761\u5c0f\u5f84\u65c1\u4eba\u8ff9\u6781\u5c11\u7684\u6811\u6797\u4e2d\u4f11\u606f\u3002 \u3000\u3000\u4e09\u4e08\u5916\u8fd8\u6709\u4e24\u4e2a\u4eba\uff0c\u662f\u5c91\u5578\u864e\u548c\u5434\u94c1\u5cf0\uff0c\u9ed1\u8272\u77ed\u886b\uff0c\u9ed1\u957f\u88e4\uff0c\u8170\u91cc\u8fd8\u675f\u7740\u4e00\u6761\u56db\u6307\u5bbd\u767d \u8170\u5e26\uff0c\u4e0a\u9762\u8fd8\u6cbe\u6709\u4e0d\u5c11\u6cb9\u6c61\uff0c\u8eab\u65c1\u505c\u653e\u4e00\u8f86\u72ec\u8f6e\u8f66\u3002\u5c91\u5578\u864e\u4e00\u8138\u5927\u80e1\u5b50\uff0c\u626e\u4e2a\u8f66\u592b\u6700\u76f8\u5f53\uff0c \u8f66\u4e0a\u653e\u7740\u7b80\u5355\u7684\u884c\u56ca\uff0c\u548c\u4e00\u4e2a\u957f\u88ab\u5377\u513f\uff0c\u91cc\u9762\u88f9\u7684\u662f\u56db\u5c3a\u957f\u7684\u65a9\u9a6c\u5200\uff0c\u5434\u94c1\u5cf0\u548c\u4ed6\u8863\u7740\u76f8\u540c\uff0c \u8868\u660e\u4e86\u662f\u5408\u63a8\u72ec\u8f6e\u8f66\u7684\u4f19\u8ba1\u3002 \u3000\u3000\u56db\u65b9\u5927\u6355\u5934\uff0c\u6539\u626e\u7684\u8eab\u4efd\uff0c\u5f88\u5c11\u4f1a\u88ab\u4eba\u77a7\u51fa\u7834\u7efd\uff0c\u5c31\u662f\u4ed6\u4eec\u80af\u727a\u7272\u5f62\u50cf\uff0c\u9009\u5f97\u884c\u4e1a\u9002\u5408\uff0c \u88c5\u5f97\u4f20\u795e\uff0c\u51e0\u4e4e\u662f\u65e0\u5f80\u4e0d\u5229\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u5915\u9633\u5c06\u5c3d\uff0c\u665a\u971e\u6ee1\u5929\u7684\u65f6\u5206\uff0c\u5929\u8272\u5feb\u8981\u9ed1\u4e86\u3002 \u3000\u3000\u4eba\u8ff9\u6781\u5c11\uff0c\u5e76\u4e0d\u8868\u793a\u6ca1\u6709\u4eba\uff0c\u73b0\u5728\u5c31\u6709\u4e24\u4e2a\u7070\u8863\u5927\u6c49\u8d70\u8fc7\u6765\uff0c\u8863\u6446\u4e0b\u9732\u51fa\u4e86\u6570\u5bf8\u957f\u7684\u5200 \u9798\uff0c\u4ed6\u4eec\u867d\u65e0\u610f\u5f20\u626c\u8eab\u4e0a\u4f69\u5200\uff0c\u4f46\u5374\u4e5f\u65e0\u523b\u610f\u63a9\u9970\u7684\u7528\u5fc3\u3002 \u3000\u3000\u7ecf\u8fc7\u56db\u4e2a\u4eba\u65f6\uff0c\u867d\u672a\u505c\u4e0b\u6765\uff0c\u5374\u653e\u6162\u4e86\u811a\u6b65\uff0c\u56db\u9053\u70af\u70af\u7684\u76ee\u5149\uff0c\u4e0d\u505c\u5730\u6253\u91cf\uff0c\u4f46\u600e\u4e48\u770b\uff0c \u4e5f\u770b\u4e0d\u51fa\u4e00\u70b9\u53ef\u7591\u7834\u7efd\uff0c\u56db\u5927\u6355\u5934\u88c5\u5f97\u50cf\u554a\uff01\u552f\u4e00\u53ef\u7591\u7684\u5730\u65b9\uff0c\u662f\u9009\u62e9\u7684\u5730\u65b9\u4e0d\u5bf9\uff0c\u8fd9\u91cc\u4e0d \u662f\u5927\u8def\u5b98\u9053\uff0c\u800c\u4e14\u6ca1\u6709\u4f4f\u6237\uff0c\u53ea\u6709\u4e00\u5e62\u7a7a\u4e86\u5f88\u4e45\u7684\u5927\u623f\u5b50\u3002 \u3000\u3000\u662f\u7684\uff0c\u7a7f\u8fc7\u8fd9\u7247\u6811\u6797\uff0c\u5c31\u662f\u626c\u5dde\u8457\u540d\u7684\u9b3c\u5c4b\uff0c\u611a\u516c\u56ed\u6797\uff0c\u90a3\u662f\u4e2a\u5f88\u591a\u5e74\u6ca1\u6709\u4eba\u53bb\u7684\u6240\u5728\u3002 \u3000\u3000\u5c91\u5578\u864e\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u5434\u554a\uff0c\u90a3\u4e24\u4e2a\u5154\u5d3d\u5b50\uff0c\u4f3c\u662f\u5bf9\u6211\u4eec\u52a8\u4e86\u6000\u7591\uff0c\u56db\u53ea\u8d3c\u773c\u5230\u5904\u77a7\uff0c\u83ab \u975e\u662f\u6211\u4eec\u7684\u626e\u76f8\u4e0a\u6709\u4e86\u7834\u7efd\uff1f\u201d \u3000\u3000\u201c\u88c5\u626e\u65e0\u7834\u7efd\uff0c\u53ea\u662f\u9009\u7684\u6b47\u811a\u5730\u65b9\u4e0d\u5bf9\u5934\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u8fd9\u91cc\u5c11\u6709\u4eba\u8ff9\uff0c\u6211\u4eec\u63a8\u8f66\u7684\uff0c \u62c5\u62c5\u7684\uff0c\u4e0d\u4f1a\u8def\u8fc7\u8fd9\u5730\u65b9\u3002\u5c0f\u4e0d\u5fcd\u5219\u4e71\u5927\u8c0b\uff0c\u73b0\u5728\u54b1\u4eec\u8fd8\u4e0d\u80fd\u8d70\uff0c\u4e00\u8d70\u5c31\u8bc1\u5b9e\u4e86\u4ed6\u4eec\u6000\u7591\uff0c \u5c31\u53ef\u80fd\u53bb\u9886\u529f\uff0c\u62a5\u544a\u7ed9\u4ed6\u4eec\u7684\u4e3b\u5b50\uff0c\u4eca\u591c\u6697\u63a2\u611a\u516c\u56ed\u6797\u8ba1\u5212\uff0c\u5c31\u7b97\u5439\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u8be5\u600e\u4e48\u529e\uff1f\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u4e24\u4e2a\u5154\u5d3d\u5b50\u8d3c\u5934\u8d3c\u8111\uff0c\u6211\u60f3\u8fd8\u4f1a\u6298\u8f6c\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u53ea\u597d\u6740\u4e86\uff01\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4e0d\u80fd\u7559\u4e0b\u7978\u5bb3\u3002\u201d \u3000\u3000\u201c\u8981\u4e0d\u8981\u901a\u77e5\u5c0f\u4e8e\u548c\u5c0f\u675c\u4e00\u58f0\u3002\u201d \u3000\u3000\u4e24\u4eba\u8c08\u8bdd\u7684\u58f0\u97f3\u5f88\u4f4e\uff0c\u5b9e\u5728\u4e0d\u80fd\u786e\u5b9a\u4e8e\u627f\u5fd7\u3001\u675c\u671b\u6708\u662f\u5426\u542c\u5230\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u5c0f\u4e8e\u3001\u5c0f\u675c\uff0c\u90fd\u662f\u7cbe\u660e\u4eba\u7269\uff0c\u56de\u5934\u8def\u8981\u5148\u7ecf\u8fc7\u4ed6\u4eec\uff0c\u8bf4\u4e0d\u5b9a\u7528\u4e0d \u5230\u6211\u4eec\u51fa\u624b\u3002\u201d \u3000\u3000\u731c\u7684\u679c\u7136\u5bf9\uff0c\u4e24\u4e2a\u7070\u8863\u5927\u6c49\u771f\u7684\u6298\u8f6c\u56de\u6765\uff0c\u4f46\u8fd9\u6b21\u4e24\u4eba\u4e5f\u6709\u4e86\u8b66\u89c9\uff0c\u62ff\u51fa\u4e86\u8863\u670d\u5185\u4f69\u7684 \u5355\u5200\uff0c\u53ea\u4e0d\u8fc7\u5200\u672a\u51fa\u9798\uff0c\u63d0\u5728\u624b\u91cc\u8d70\u56de\u6765\uff0c\u884c\u7ecf\u4e8e\u3001\u675c\u4f11\u606f\u5904\uff0c\u505c\u4e0b\u4e86\u811a\u6b65\uff0c\u8fd9\u662f\u6446\u660e\u4e86\u8981 \u627e\u9ebb\u70e6\u3002 \u3000\u3000\u4e8e\u627f\u5fd7\u548c\u675c\u671b\u6708\u7a81\u7136\u8dc3\u8d77\uff0c\u5feb\u7684\u50cf\u6012\u7bad\u79bb\u5f26\uff0c\u4e24\u4e2a\u7070\u8863\u5927\u6c49\uff0c\u5200\u8fd8\u672a\u53ca\u51fa\u9798\uff0c\u4eba\u5df2\u88ab\u70b9 \u4e2d\u7a74\u9053\uff0c\u8eab\u4f53\u672a\u5012\u4e0b\uff0c\u53c8\u88ab\u4e00\u4eba\u4e00\u4e2a\uff0c\u62a2\u98de\u5230\u4e86\u6797\u4e2d\u5927\u6811\u4e0a\uff0c\u67b6\u5728\u679d\u53f6\u63a9\u906e\u7684\u6811\u6748\u4e0a\u3002 \u3000\u3000\u5c91\u5578\u864e\u53d6\u51fa\u65a9\u9a6c\u5200\uff0c\u5374\u628a\u72ec\u8f6e\u8f66\u63a8\u5165\u6797\u4e2d\u5f03\u7f6e\u3002 \u3000\u3000\u4e8e\u627f\u5fd7\u3001\u675c\u671b\u6708\u4e5f\u4e22\u5f03\u4e86\u7af9\u7ba9\u7b50\uff0c\u5feb\u6b65\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8fb0\u5149\u867d\u8fd8\u65e9\u4e00\u4e9b\uff0c\u4f46\u5df2\u8fd1\u591c\u5e55\u4f4e\u5782\uff0c\u8d70\u5427\uff01\u5148\u53bb\u77a7\u77a7\u611a\u516c\u5b85\u9662\u5916\u7684\u5f62\u52bf\uff0c \u518d\u51b3\u5b9a\u884c\u52a8\u65b9\u6cd5\u3002\u201d \u3000\u3000\u201c\u5c0f\u4e8e\u3001\u5c0f\u675c\uff0c\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u4f60\u4eec\u5982\u4f55\u5904\u7f6e\u90a3\u4e24\u4e2a\u8d3c\u4eba\uff1f\u201d \u3000\u3000\u201c\u70b9\u4e86\u6b7b\u7a74\uff0c\u85cf\u5728\u679d\u53f6\u5bc6\u8302\u7684\u6811\u6748\u4e0a\uff0c\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u60f3\u53d1\u73b0\u5c38\u4f53\uff0c\u81f3\u5c11\u8981\u4e24\u5929\u4ee5\u540e\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u8f6c\u8eab\u5e26\u5934\u8d70\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u7a7f\u6797\u800c\u8fc7\uff0c\u8ddd\u79bb\u5f88\u8fd1\uff0c\u4e0d\u8fc7\uff0c\u6811\u6797\u548c\u611a\u516c\u5b85\u9662\u4e4b\u95f4\uff0c \u9694\u4e86\u4e00\u9053\u5927\u6c34\u6c9f\uff0c\u5bbd\u8fc7\u56db\u4e08\u4ee5\u4e0a\uff0c\u8981\u4e00\u8dc3\u800c\u8fc7\uff0c\u4e0d\u592a\u5bb9\u6613\uff0c\u8981\u60f3\u4e2a\u8d8a\u6e21\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u56db\u4eba\u4e4b\u4e2d\uff0c\u4ee5\u675c\u671b\u6708\u7684\u8f7b\u529f\u6700\u597d\uff0c\u4ed6\u8bf4\u4e0d\u592a\u5bb9\u6613\uff0c\u4e09\u4eba\u5c31\u4e0d\u4e89\u8fa9\u4e86\u3002 \u3000\u3000\u201c\u54b1\u4eec\u56db\u4e2a\u4eba\uff0c\u6211\u7684\u5757\u5934\u6700\u5927\uff0c\u8f7b\u529f\u4e5f\u6700\u5dee\u52b2\u3002\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u5c0f\u675c\u53f7\u79f0\u8e0f\u96ea\u4e0d\u7559\u75d5\uff0c \u4ed6\u8bf4\u4e0d\u5bb9\u6613\uff0c\u4e00\u8dc3\u800c\u8fc7\uff0c\u6700\u597d\u662f\u4e0d\u8981\u5192\u9669\u3002\u201d \u3000\u3000\u201c\u6c34\u6c9f\u4e24\u5cb8\uff0c\u54ea\u4e00\u8fb9\u7684\u5730\u52bf\u9ad8\uff1f\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u7ed5\u9053\u884c\u7a0b\u6709\u591a\u8fdc\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u8fd9\u8fb9\u9ad8\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u7ad9\u5728\u5cb8\u8fb9\uff0c\u53ef\u770b\u5230\u6574\u5ea7\u7684\u5927\u5b85\u9662\uff0c\u4f46\u611a\u516c\u5b85\u9662\u4e2d\uff0c\u82b1\u6811 \u5f88\u591a\uff0c\u8352\u829c\u6570\u5341\u5e74\uff0c\u4e5f\u957f\u6ee1\u4e86\u4e00\u4eba\u591a\u9ad8\u7684\u6742\u8349\uff0c\u8ddd\u79bb\u8fc7\u767e\u4e08\uff0c\u53ea\u6015\u5f88\u96be\u770b\u5230\u4ec0\u4e48\u3002\u4f30\u7b97\u60c5\u51b5\uff0c \u8981\u8fdb\u5165\u5b85\u9662\u4e2d\u624d\u80fd\u770b\u51fa\u4e00\u4e9b\u540d\u5802\uff0c\u81f3\u4e8e\u7ed5\u9053\u800c\u8fc7\uff0c\u6050\u8981\u8d70\u4e0a\u5341\u51e0\u91cc\u8def\uff0c\u624d\u6709\u4e00\u5ea7\u8fc7\u6e21\u540a\u6865\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u65f6\u95f4\u5f88\u591a\uff0c\u770b\u8fc7\u5f62\u52bf\u540e\uff0c\u518d\u51b3\u5b9a\u5982\u4f55\u8d8a\u6e21\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u53ea\u662f\u957f\u6ee1\u4e86\u8fc7\u4eba\u7684\u6742 \u8349\uff0c\u624d\u662f\u9ebb\u70e6\u4e8b\u60c5\uff0c\u5982\u679c\u662f\u8349\u4e2d\u85cf\u57cb\u4f0f\uff0c\u7528\u5f29\u7bad\u3001\u6697\u5668\u65bd\u88ad\uff0c\u5c31\u5f88\u96be\u5bf9\u4ed8\u4e86\u3002\u201d \u3000\u3000\u201c\u6700\u6015\u7684\u662f\u4ed6\u4eec\u65bd\u7528\u6885\u82b1\u9488\u4e00\u7c7b\u7684\u7ec6\u5c0f\u6697\u5668\u3002\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u5728\u8349\u4e2d\u884c\u52a8\u5f71\u54cd\u542c\u529b\uff0c\u95ea \u907f\u3001\u51fb\u6253\uff0c\u90fd\u4e0d\u5bb9\u6613\uff0c\u5982\u662f\u518d\u6dec\u4e86\u5267\u6bd2\uff0c\u4e2d\u4e00\u679a\u5c31\u53ef\u80fd\u7acb\u523b\u5931\u53bb\u4e86\u6218\u6597\u80fd\u529b\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u8fd9\u4e00\u70b9\u4e0d\u80fd\u4e0d\u9632\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u683d\u5728\u94f6\u9488\u4e4b\u7c7b\u7684\u6dec\u6bd2\u5c0f\u6697\u5668\u4e0a\uff0c\u90a3\u53ef\u662f\u65e2\u5931\u8d25\uff0c \u53c8\u4e22\u4eba\u7684\u4e8b\u3002\u201d \u3000\u3000\u4ee5\u56db\u65b9\u5927\u6355\u5934\u7684\u6b66\u529f\uff0c\u5728\u6b63\u5e38\u7684\u73af\u5883\u4e0b\uff0c\u7edd\u4e0d\u754f\u60e7\u6697\u5668\u7684\u653b\u88ad\uff0c\u4f46\u5982\u5728\u9ed1\u6697\u7684\u591c\u8272\u4e4b\u4e0b\uff0c \u6d53\u5bc6\u7684\u6df1\u8349\u4e1b\u4e2d\uff0c\u76ee\u529b\u3001\u542c\u529b\uff0c\u90fd\u53d7\u4e86\u5f88\u5927\u7684\u5c40\u9650\uff0c\u6dec\u6bd2\u3001\u7ec6\u5c0f\u6697\u5668\u7684\u6740\u4f24\u80fd\u529b\uff0c\u5c31\u975e\u5e38\u53ef \u6015\u4e86\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\u9669\u4e0d\u80fd\u5192\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u54b1\u4eec\u52d8\u67e5\u8fc7\u5b85\u9662\u5f62\u52bf\u4e4b\u540e\uff0c\u9009\u4e24\u5904\u53ef\u80fd\u7528\u4f5c\u654c\u4eba\u9003\u751f \u7684\u5730\u65b9\u57cb\u4f0f\uff0c\u7136\u540e\uff0c\u653e\u628a\u706b\u70e7\u4e86\u611a\u516c\u56ed\u6797\u3002\u5b85\u9662\u5185\u4e00\u7247\u6742\u8349\uff0c\u6570\u5341\u5e74\u65e0\u4eba\u6574\u7406\uff0c\u4e2a\u4e2d\u6050\u5df2\u6f5c \u85cf\u4e86\u4e0d\u5c11\u86c7\u3001\u867a\u4e4b\u7c7b\u7684\u6bd2\u7269\uff0c\u8fd9\u628a\u706b\u70e7\u6b7b\u6bd2\u866b\uff0c\u4e5f\u9010\u51fa\u5b85\u4e2d\u6f5c\u4f0f\u9b54\u5934\u3001\u6740\u624b\uff0c\u54b1\u4eec\u57cb\u4f0f\u622a\u62e6\uff0c \u53ea\u8981\u80fd\u751f\u64d2\u4e24\u4e2a\uff0c\u5c31\u53ef\u4ee5\u95ee\u51fa\u4ed6\u4eec\u7684\u6765\u5386\u4e86\u3002\u201d \u3000\u3000\u201c\u529e\u6cd5\u867d\u975e\u4e0a\u597d\uff0c\u4f46\u5982\u522b\u65e0\u826f\u7b56\u53d6\u4ee3\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u53ea\u6709\u4ee5\u4e2d\u7b56\u5e94\u5bf9\u4e86\uff0c\u4f46\u4e0d\u77e5\u8fd9\u7fa4 \u8096\u5c0f\u4e4b\u4e2d\uff0c\u662f\u5426\u6709\u68a6\u5e7b\u4e4b\u5200\uff1f\u201d \u3000\u3000\u706b\u70e7\u611a\u516c\u56ed\u6797\uff0c\u88ab\u5434\u94c1\u5cf0\u8bc4\u4e3a\u4e2d\u7b56\u3002 \u3000\u3000\u201c\u5c91\u67d0\u4eba\u4e5f\u63d0\u4e00\u4e2a\u529e\u6cd5\uff0c\u5434\u5144\u8bc4\u65ad\u4e00\u4e0b\uff0c\u662f\u5426\u53ef\u884c\uff1f\u201d \u3000\u3000\u5434\u94c1\u5cf0\u7b11\u9053\uff1a\u201c\u8bf4\u8bf4\u770b\uff1f\u201d \u3000\u3000\u201c\u6742\u8349\u8fc7\u4eba\uff0c\u654c\u4eba\u53ef\u7528\u4f5c\u57cb\u4f0f\uff0c\u54b1\u4eec\u4f55\u5c1d\u4e0d\u53ef\u4ee5\u5229\u7528\uff1f\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u54b1\u4eec\u53ef\u4ee5\u5728\u5b85\u9662 \u4e4b\u5916\uff0c\u5750\u606f\u517b\u795e\uff0c\u4fdf\u9ece\u660e\u5c06\u8fd1\uff0c\u518d\u5165\u6797\u56ed\uff0c\u6f5c\u5165\u8349\u4e2d\u9690\u4f0f\uff0c\u7b49\u5929\u660e\u4e4b\u540e\uff0c\u518d\u4ed4\u7ec6\u89c2\u5bdf\uff0c\u6709\u4e00 \u5929\u7684\u65f6\u95f4\uff0c\u53ef\u4f9b\u6211\u4eec\u4ed4\u7ec6\u67e5\u770b\u3002\u4e86\u7136\u654c\u60c5\u540e\uff0c\u518d\u7814\u5546\u5e94\u5bf9\u4e4b\u7b56\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u529e\u6cd5\u8f9b\u82e6\u4e00\u4e9b\uff0c\u4e00\u5929\u95f7\u5728\u8349\u4e1b\u4e2d\uff0c\u53ea\u80fd\u4ee5\u5e26\u7684\u5e72\u7cae\u5145\u9965\u3002\u201d \u3000\u3000\u201c\u6700\u91cd\u8981\u7684\u662f\uff0c\u5148\u4e86\u89e3\u626c\u5dde\u57ce\u5185\u654c\u4eba\u5f62\u52bf\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u5426\u5df2\u52fe\u7ed3\u4e00\u5904\uff0c\u6216\u662f \u5404\u81ea\u4e3a\u6218\uff0c\u72ec\u884c\u5176\u662f\uff0c\u6f5c\u9690\u5728\u611a\u516c\u56ed\u6797\u4e2d\u8fd9\u4e00\u4f19\u4eba\uff0c\u5b9e\u529b\u5982\u4f55\uff1f\u771f\u6b63\u7684\u76ee\u7684\u53c8\u662f\u4ec0\u4e48\uff1f\u6bd4\u8d77 \u4e00\u628a\u706b\uff0c\u70e7\u6bc1\u6797\u56ed\uff0c\u903c\u4ed6\u4eec\u53e6\u627e\u85cf\u8eab\u4e4b\u5730\uff0c\u662f\u8981\u9ad8\u660e\u4e00\u4e9b\uff0c\u6bd5\u7adf\u8fd8\u672a\u5230\u5175\u5203\u76f8\u89c1\u7684\u65f6\u673a\u3002\u201d \u3000\u3000\u5c91\u80e1\u5b50\u770b\u4e0a\u53bb\u6027\u60c5\u706b\u66b4\uff0c\u5176\u5b9e\uff0c\u5374\u662f\u4e2a\u7c97\u4e2d\u6709\u7ec6\u7684\u4eba\u3002 \u3000\u3000\u201c\u5bf9\uff01\u4e86\u89e3\u654c\u60c5\uff0c\u63a2\u51fa\u4ed6\u4eec\u7684\u9996\u8111\u4eba\u7269\u662f\u8c01\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\u4f3c\u662f\u5df2\u628a\u68a6\u5e7b\u4e4b\u5200\uff0c \u5217\u4e3a\u53e6\u4e00\u5f3a\u654c\u3001\u9b54\u5934\uff0c\u4e0d\u77e5\u5979\u662f\u65e9\u6709\u6240\u89c9\u5462\uff1f\u8fd8\u662f\u53d7\u5230\u4f20\u8a00\u7684\u5f71\u54cd\uff0c\u4f46\u770b\u8d77\u6765\u8fd9\u4e2a\u51b3\u5b9a\u6ca1 \u9519\uff01\u201d \u3000\u3000\u201c\u5979\u7684\u5e74\u7eaa\u4e0d\u5927\uff0c\u4f46\u80f8\u7f57\u4e4b\u5e7f\uff0c\u5374\u53c8\u975e\u6211\u4eec\u8fd9\u4e9b\u8001\u6c5f\u6e56\u6240\u80fd\u53ca\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6709\u4e9b\u4e8b \u4e0d\u662f\u8bfb\u4e66\u80fd\u591f\u5f97\u5230\u7684\uff0c\u800c\u662f\u7ecf\u9a8c\u548c\u9605\u5386\u7684\u7d2f\u79ef\uff0c\u4f46\u603b\u6355\u5934\u597d\u50cf\u8d85\u8d8a\u4e86\u8fd9\u4e2a\u5b9a\u5f8b\uff0c\u4e3a\u4ec0\u4e48\u5462\uff1f \u8001\u5434\u5c31\u6709\u70b9\u60f3\u4e0d\u901a\u554a\uff01\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u6211\u5c91\u80e1\u5b50\u5c31\u8d8a\u6765\u8d8a\u4f69\u670d\u5979\u4e86\uff01\u4e0d\u8bba\u6b66\u529f\u3001\u673a\u53d8\uff0c\u90fd\u6709\u8fc7\u4eba\u4e4b\u5904\uff0c\u96be\u9053\u8fd9\u4e16 \u95f4\u771f\u6709\u9634\u9633\u516b\u5366\uff0c\u672a\u535c\u5148\u77e5\u7684\u5947\u672f\uff1f\u800c\u54b1\u4eec\u7684\u603b\u6355\u5934\uff0c\u53c8\u662f\u4e2a\u4e2d\u9ad8\u624b\uff1f\u201d \u3000\u3000\u201c\u4f20\u5979\u6280\u827a\u7684\u5434\u4e00\u8c14\uff0c\u542c\u8bf4\u662f\u4e00\u4e2a\u80f8\u7f57\u4e07\u6709\u7684\u5148\u77e5\u8005\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5b66\u4f1a\u4e86\u9884\u535c\u5409\u51f6 \u7684\u5947\u672f\uff0c\u4e5f\u662f\u5927\u6709\u53ef\u80fd\uff0c\u4f46\u66f4\u91cd\u8981\u662f\uff0c\u5979\u8981\u5929\u751f\u5177\u6709\u6167\u6839\uff0c\u7edd\u9ad8\u7684\u624d\u667a\uff0c\u624d\u80fd\u6709\u7a81\u7834\u5e38\u89c4\u7684 \u6210\u5c31\u3002\u4e8b\u5b9e\u4e0a\uff0c\u54b1\u4eec\u56db\u4e2a\u4eba\uff0c\u90fd\u975e\u5eb8\u788c\u4e4b\u8f88\uff0c\u5374\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e2d\uff0c\u88ab\u5979\u5f81\u670d\u4e86\uff0c\u60c5\u7518\u6548\u6b7b\uff0c\u5fe0 \u8bda\u4e0d\u4e8c\uff0c\u8fd9\u548c\u5f53\u5e74\u6768\u5c1a\u4e66\uff0c\u529d\u5316\u54b1\u4eec\u8fdb\u5165\u5211\u90e8\uff0c\u4ece\u4e8b\u6355\u5934\u751f\u6daf\uff0c\u6709\u5f88\u5927\u7684\u4e0d\u540c\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\uff01\u6768\u5c1a\u4e66\u662f\u4ee5\u5fb7\u670d\u4eba\uff0c\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u603b\u6355\u5934\u5374\u51ed\u85c9\u5979\u7684\u667a\u8c0b\u3001\u80c6\u6c14\u3001\u6b66\u529f\uff0c\u4f7f\u6211 \u4eec\u5fc3\u60a6\u8bda\u670d\uff0c\u5979\u65e2\u7136\u8981\u6211\u4eec\u63a2\u67e5\u5185\u60c5\uff0c\u5c31\u5e94\u8be5\u5148\u660e\u4e86\u8fd9\u91cc\u654c\u4eba\u7684\u8eab\u4efd\uff1f\u4e00\u628a\u706b\uff0c\u53ef\u80fd\u63d0\u524d\u628a \u6076\u6218\u5f15\u53d1\uff0c\u4e5f\u4f1a\u628a\u654c\u4eba\u70e7\u7684\u6563\u5165\u4e86\u626c\u5dde\u6c11\u5bb6\uff0c\u4ed6\u4eec\u90fd\u4f1a\u70b9\u6b66\u529f\uff0c\u4f1a\u9020\u6210\u4ec0\u4e48\u6837\u7684\u9ebb\u70e6\uff0c\u5c31\u5f88 \u96be\u8bf4\u4e86\u3002\u6240\u4ee5\uff0c\u6211\u8d5e\u6210\u5c91\u80e1\u5b50\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u884c\uff01\u7167\u5c91\u5144\u7684\u529e\u6cd5\u6765\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u603b\u6355\u5934\u4ea4\u4ee3\u4e86\u4e24\u53e5\u8bdd\uff0c\u8bf8\u4f4d\u53ef\u8fd8\u8bb0\u5f97\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u76f4\u622a\u4e86\u5f53\u5730\u8bf4\u51fa\u6765\u5427\uff01\u5356\u7684\u4ec0\u4e48\u5173\u5b50\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u90fd\u6ca1\u6709\u89c1\u8fc7\u68a6\u5e7b\u4e4b\u5200\uff0c\u53ef\u662f\u6709\u5173\u4ed6\u7684\u4f20\u8bf4\u5f88\u795e\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u603b\u6355\u5934\u8981\u6211\u4eec\u7d27 \u5bc6\u8fde\u7cfb\uff0c\u8d70\u5728\u4e00\u8d77\uff0c\u662f\u5426\u522b\u6709\u542b\u610f\u5462\uff1f\u201d \u3000\u3000\u201c\u6015\u6211\u4eec\u4e00\u4eba\u4e4b\u529b\uff0c\u975e\u4ed6\u4e4b\u654c\u3002\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u4e5f\u53ef\u80fd\u662f\u5979\u7684\u4e00\u79cd\u611f\u89c9\uff0c\u4e5f\u53ef\u80fd\u662f\u535c\u5366 \u535c\u51fa\u6765\u7684\u7ed3\u679c\u3002\u4e0d\u7ba1\u5982\u4f55\uff0c\u8fd9\u662f\u6211\u4eec\u529e\u6848\u7684\u7ecf\u5386\u4e2d\uff0c\u56db\u4e2a\u4eba\u7b2c\u4e00\u6b21\u8054\u8882\u5927\u884c\u52a8\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u5fc3\u4e2d\u672c\u6709\u4e9b\u4e0d\u5927\u670d\u6c14\uff0c\u4f46\u60f3\u5230\u82b1\u5385\u4e2d\u5168\u529b\u8fd0\u5200\uff0c\u8fde\u653b\u4e09\u767e\u5200\uff0c\u672a\u80fd\u4f24\u5230\u60dc\u7389\u4e00\u5bf8 \u8863\u89d2\uff0c\u5fc3\u4e2d\u987f\u7136\u4e00\u5bd2\uff0c\u95ed\u4e0a\u5634\u5df4\u4e0d\u8bb2\u4e86\u3002 \u3000\u3000\u201c\u4e0d\u7ba1\u662f\u4ec0\u4e48\u539f\u56e0\uff0c\u4f46\u5c0f\u5fc3\u4e00\u4e9b\u603b\u4e0d\u9519\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u8001\u5434\u4e3b\u5f20\uff0c\u54b1\u4eec\u8fdb\u5165\u5b85\u9662\u4e4b\u540e\uff0c \u5c3d\u91cf\u4f1a\u805a\u5728\u4e00\u5904\uff0c\u4e2a\u4eba\u7684\u884c\u52a8\u4e0d\u9002\u5b9c\u79bb\u7fa4\u592a\u8fdc\uff0c\u603b\u8981\u5728\u76ee\u5149\u53ef\u89c1\u7684\u8303\u56f4\u4e4b\u5185\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u4fe1\u5929\u4e0b\u771f\u6709\u4eba\u80fd\u6297\u62d2\u6211\u4eec\u56db\u4eba\u7684\u8054\u624b\u4e4b\u529b\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u68a6\u5e7b\u4e4b\u5200\u4e5f\u672a\u5fc5\u6709\u6b64 \u80fd\u8010\u3002\u201d \u3000\u3000\u201c\u9664\u53bb\u68a6\u5e7b\u4e4b\u5200\uff0c\u8fd8\u6709\u4e00\u79cd\u7ec4\u5408\u9635\u6cd5\uff0c\u6709\u5982\u5c11\u6797\u540d\u9a70\u5929\u4e0b\u7684\u7f57\u6c49\u9635\uff0c\u7ec4\u9635\u548c\u5c1a\u4e00\u4eba\u529f\u529b\uff0c \u672a\u5fc5\u53ef\u6015\uff0c\u4f46\u5982\u7ed3\u5408\u4e00\u5904\uff0c\u5c31\u5e03\u6210\u5929\u4e0b\u95fb\u540d\u7684\u7f57\u6c49\u9635\u4e86\uff01\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4e00\u4eba\u7684\u884c\u52a8\uff0c\u53ef\u80fd \u88ab\u4ed6\u4eec\u56f0\u4f4f\uff0c\u5982\u662f\u6211\u4eec\u56db\u4e2a\u4eba\u8d70\u5728\u4e00\u8d77\uff0c\u56f0\u6211\u4eec\u6545\u7136\u4e0d\u6613\u3002\u800c\u4e14\uff0c\u5408\u6211\u4eec\u56db\u4eba\u624d\u667a\uff0c\u4e5f\u8bb8\u8fd8 \u80fd\u627e\u51fa\u4e00\u4e2a\u51fb\u6e83\u4ed6\u4eec\u7684\u65b9\u6cd5\u51fa\u6765\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u6697\u53eb\u4e86\u4e24\u58f0\u60ed\u6127\uff0c\u63a5\u9053\uff1a\u201c\u5bf9\uff01\u6211\u5c31\u7ecf\u5386\u8fc7\u8fd9\u4e2a\u5371\u6025\uff0c\u4e00\u5bf9\u4e00\uff0c\u751a\u81f3\u4ed6\u4eec\u4e24\u4eba\u8054 \u4e4e\uff0c\u90fd\u4e0d\u582a\u4e00\u51fb\uff0c\u4f46\u4e09\u4eba\u7ed3\u5408\u4e00\u5904\uff0c\u80fd\u628a\u624b\u4e2d\u5175\u5203\u5408\u4e8e\u4e00\u4e2a\u62db\u5f0f\u4e2d\u65bd\u5c55\u51fa\u6765\uff0c\u5176\u52bf\u4e4b\u5f3a\uff0c\u5b9e \u662f\u5f88\u96be\u62b5\u5fa1\u3002\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u5947\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5343\u771f\u4e07\u786e\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4e0d\u4f46\u653b\u52bf\u51cc\u5389\uff0c\u9632\u5b88\u7684\u529b\u91cf\uff0c\u4e5f\u5341\u5206\u5f3a\u5927\uff0c\u5982\u662f\u6709\u56db\u7ec4\u4ee5 \u4e0a\u7684\u4eba\uff0c\u5408\u4f5c\u4e0a\u518d\u4e25\u5bc6\u4e00\u4e9b\uff0c\u628a\u4eba\u56f0\u4f4f\uff0c\u60f3\u7834\u56f4\u800c\u51fa\uff0c\u8fd8\u771f\u662f\u4ef6\u4e0d\u592a\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u6211\u60f3\u8fd9\u5c31\u662f\u603b\u6355\u5934\u8981\u6211\u4eec\u8d70\u5728\u4e00\u8d77\u7684\u539f\u56e0\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u770b\u4e00\u770b\uff0c\u5408\u6211\u4eec\u56db\u4eba\u4e4b\u529b\uff0c \u80fd\u4e0d\u80fd\u51fb\u7834\u8fd9\u6837\u4e00\u4e2a\u7ec4\u5408\u9635\u52bf\uff1f\u201d \u3000\u3000\u201c\u5c0f\u675c\uff0c\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u4ee5\u4f60\u770b\uff0c\u90a3\u4e9b\u4eba\u662f\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\u7684\u5c5e\u4e0b\uff1f\u201d \u3000\u3000\u201c\u5e94\u8be5\u4e0d\u662f\uff0c\u6c5f\u5357\u9053\u4e0a\uff0c\u6709\u4e00\u4e2a\u6740\u624b\u4e4b\u738b\uff0c\u540d\u53eb\u8840\u624b\u65b9\u8f6e\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u7684\u80fd \u529b\uff0c\u662f\u5728\u5f88\u77ed\u65f6\u95f4\u5185\uff0c\u80fd\u8bad\u7ec3\u51fa\u4e00\u6279\u7edd\u5bf9\u5fe0\u4e8e\u4ed6\u7684\u6740\u624b\uff0c\u4e0d\u662f\u7b2c\u4e00\u6d41\u7684\u51fa\u8272\u4eba\u7269\uff0c\u4f46\u5374\u957f\u4e8e \u5e03\u5c40\uff0c\u5175\u5203\u6697\u5668\uff0c\u7ec4\u5408\u6210\u5fc5\u6740\u4e4b\u5c40\uff0c\u6700\u53ef\u6015\u7684\u662f\u7f20\u4e0a\u4eba\u5c31\u6ca1\u5b8c\u6ca1\u4e86\uff0c\u4e0d\u8fbe\u76ee\u7684\uff0c\u8a93\u4e0d\u7f62\u4f11\uff0c \u8fd9\u4e9b\u7ec4\u5408\u6740\u624b\uff0c\u53ef\u80fd\u662f\u4ed6\u7684\u5c5e\u4e0b\u3002\u201d \u3000\u3000\u201c\u4f60\u89c1\u8fc7\u4ed6\u6ca1\u6709\uff1f\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u4ed6\u672c\u8eab\u7684\u6280\u827a\uff0c\u662f\u5426\u5341\u5206\u9ad8\u660e\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u8bf4\u6765\u60ed\u6127\uff0c\u6b64\u4eba\u95fb\u540d\u867d\u4e45\uff0c\u5374\u65e0\u7f18\u4e00\u4f1a\uff0c\u60f3\u4e0d\u5230\u4f1a\u5728\u9a6c\u516c\u5b50\u8fd9\u4ef6 \u65b0\u623f\u547d\u6848\u4e4b\u4e2d\uff0c\u6c5f\u5357\u9053\u4e0a\u7684\u6740\u624b\u3001\u9b54\u5934\u90fd\u6d6e\u73b0\u4e86\u51fa\u6765\uff0c\u68a6\u5e7b\u4e4b\u5200\u3001\u8840\u624b\u65b9\u8f6e\uff0c\u8fd8\u6709\u4e00\u4f4d\u795e\u79d8 \u83ab\u6d4b\u7684\u56db\u51e4\u697c\u4e3b\uff0c\u5168\u90fd\u4f1a\u805a\u5230\u626c\u5dde\u6765\u4e86\uff0c\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u4ec0\u4e48\u6837\u5b50\u7684\u4e00\u4e2a\u96c7\u4e3b\uff0c\u6709\u5982\u6b64\u5927 \u7684\u9b44\u529b\u3001\u8d22\u529b\uff1f\u201d \u3000\u3000\u201c\u4f1a\u4e0d\u4f1a\u662f\u4e00\u79cd\u5de7\u5408\uff1f\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u636e\u6211\u4e8e\u67d0\u6240\u77e5\uff0c\u6740\u624b\u884c\u4e1a\uff0c\u4ea6\u6709\u89c4\u8303\uff0c\u4e00\u4e2a\u96c7\u4e3b\uff0c \u4e0d\u53ef\u4e00\u6b21\u9080\u8bf7\u5982\u6b64\u4f17\u591a\u7684\u6740\u624b\u7ec4\u5408\uff0c\u53bb\u5bf9\u4ed8\u4e00\u4e2a\u76ee\u6807\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\u6709\u70b9\u51fa\u4eba\u610f\u6599\uff0c\u91d1\u767e\u5e74\u6536\u85cf\u4e09\u4ef6\u91cd\u5b9d\uff0c\u4e5f\u540c\u65f6\u51fa\u73b0\u6c5f\u6e56\uff0c\u6781\u6709 \u53ef\u80fd\u5f15\u8d77\u5927\u6279\u6c5f\u6e56\u9ad8\u624b\u7684\u89ca\u89ce\u800c\u6765\uff0c\u4e24\u4ef6\u5927\u4e8b\u6df7\u5728\u4e00\u8d77\uff0c\u5c31\u6709\u4e9b\u6df7\u6dc6\u4e0d\u6e05\u4e86\u3002\u201d \u3000\u3000\u201c\u5982\u82e5\u4ed6\u4eec\u8981\u5bf9\u4ed8\u7684\u4e0d\u662f\u4e00\u4e2a\u4eba\uff0c\u6216\u662f\u540c\u4e00\u4f19\u7684\u4eba\uff0c\u201d\u5434\u94c1\u5cf0\uff1a\u201c\u5c31\u53ef\u80fd\u96c7\u8bf7\u591a\u4e2a\u6740\u624b \u7ec4\u5408\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u600e\u4e48\u8bf4\u5462\uff1f\u6740\u624b\u4f1a\u805a\uff1f\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u603b\u4e0d\u80fd\u628a\u6211\u4eec\u56db\u4e2a\u4e5f\u7b97\u4f5c\u4ed6\u4eec\u88ad\u6740\u7684\u76ee\u6807 \u5427\uff1f\u201d \u3000\u3000\u201c\u6709\u4f55\u4e0d\u53ef\uff0c\u4ed6\u4eec\u4e5f\u8bb8\u6ca1\u60f3\u5230\u6211\u4eec\u4f1a\u6765\uff1f\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4f46\u5c0f\u675c\u548c\u603b\u6355\u5934\uff0c\u5c31\u53ef\u80fd\u5728\u4ed6 \u4eec\u7684\u9884\u8ba1\u4e4b\u4e2d\u4e86\u3002\u5f53\u7136\uff0c\u91d1\u767e\u5e74\u7684\u5f3a\u5927\u5b9e\u529b\uff0c\u738b\u77e5\u5e9c\u7684\u5b98\u5458\u8eab\u4efd\uff0c\u90fd\u662f\u5f88\u96be\u4e0b\u624b\u7684\u5bf9\u8c61\u3002\u8fd9 \u4e48\u60f3\u4e00\u60f3\uff0c\u6c5f\u5357\u6740\u624b\u7ec4\u5408\u5168\u52a8\u5458\uff0c\u4e5f\u5c31\u4e0d\u8db3\u4e3a\u5947\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u8d77\u6765\uff0c\u662f\u60ca\u5929\u52a8\u5730\u7684\u4e8b\u4ef6\uff0c\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u6211\u4eec\u5bf9\u6297\u7684\u662f\u6c5f\u5357\u6574\u4e2a\u7eff\u6797\u9053\u4e0a\u7684 \u4eba\u7269\uff0c\u548c\u5168\u6c5f\u5357\u7684\u6740\u624b\u7ec4\u5408\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u6240\u4ee5\u8981\u5c3d\u91cf\u5730\u77e5\u5df1\u77e5\u5f7c\u3002\u5c0f\u675c\uff0c\u8bf4\u4e00\u4e0b\uff0c\u56db\u51e4\u697c\u4e3b\u53c8\u4f1a\u662f\u4ec0\u4e48\u6837\u4eba\u7269\u548c\u7ec4\u5408\uff1f\u201d \u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4e8b\u5173\u91cd\u5927\uff0c\u8981\u5c3d\u5410\u4f60\u80f8\u4e2d\u6240\u77e5\uff01\u201d \u3000\u3000\u201c\u6211\u6ca1\u89c1\u8fc7\u56db\u51e4\u697c\u4e3b\uff0c\u60dc\u7389\u6df7\u8ff9\u5728\u56db\u51e4\u697c\u4e2d\u7684\u6587\u9601\u4e00\u5e74\u591a\uff0c\u4e5f\u6ca1\u89c1\u8fc7\u697c\u4e3b\u3002\u201d\u675c\u671b\u6708\u9053\uff1a \u201c\u90a3\u91cc\u8fd8\u6709\u4e00\u4e2a\u6b66\u9662\uff0c\u56db\u51e4\u697c\u4e3b\u4f3c\u662f\u4e00\u4e2a\u602a\u4eba\uff0c\u7528\u7684\u4eba\uff0c\u5927\u90fd\u662f\u4e2d\u5e74\u5987\u4eba\uff0c\u771f\u53eb\u4eba\u83ab\u6d4b\u9ad8 \u6df1\u2026\u2026\u201d \u3000\u3000\u201c\u6162\u6162\u6162\uff0c\u4f60\u662f\u8bf4\u60dc\u7389\u59d1\u5a18\uff0c\u662f\u56db\u51e4\u697c\u4e3b\u7684\u95e8\u4e0b\u5f1f\u5b50\uff0c\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u90a3\u4e5f\u662f\u4e2a\u5973\u4eba \u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u662f\u56db\u51e4\u697c\u4e3b\u7684\u5f1f\u5b50\uff0c\u800c\u662f\u56db\u51e4\u697c\u4e3b\u7684\u4ec7\u4eba\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5979\u6000\u7591\u56db\u51e4\u697c\u4e3b\u662f\u5bb3\u6b7b \u5979\u7236\u4eb2\u7684\u51f6\u624b\uff0c\u624d\u60f3\u529e\u6cd5\u6df7\u5165\u56db\u51e4\u697c\u4e0b\u7684\u6587\u9601\u4e2d\uff0c\u4e00\u5e74\u591a\uff0c\u5374\u6ca1\u673a\u4f1a\u89c1\u5230\u697c\u4e3b\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u662f \u7537\u662f\u5973\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u201d \u3000\u3000\u82b1\u5385\u6bd4\u6b66\uff0c\u60dc\u7389\u7559\u7ed9\u4ed6\u592a\u6df1\u7684\u5370\u8c61\uff0c\u51e1\u662f\u7275\u626f\u4e0a\u60dc\u7389\u7684\u4e8b\uff0c\u90fd\u5f15\u8d77\u4ed6\u4e00\u9635\u7684\u7d27\u5f20\u3002 \u3000\u3000\u201c\u679c\u7136\u90fd\u4e0d\u662f\u7b80\u5355\u4eba\u7269\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u5c31\u51ed\u90a3\u4efd\u795e\u79d8\uff0c\u5c31\u975e\u5e38\u4eba\u80fd\u53ca\uff0c\u4e5f\u4fdd\u62a4\u7740\u4ed6\u4eec \u4e00\u76f4\u9690\u533f\u5728\u6697\u4e2d\u884c\u52a8\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u697c\u4e3b\u867d\u5982\u96fe\u4e2d\u795e\u9f99\uff0c\u4f46\u56db\u51e4\u697c\u5374\u5728\u626c\u5dde\u57ce\u4e2d\u3002\u6587\u9601\u3001\u6b66\u9662\u867d\u6709\u8bf8\u591a\u9650\u5236\uff0c \u4f46\u5982\u5b58\u5fc3\u8981\u8fdb\u53bb\u77a7\u77a7\uff0c\u4e5f\u975e\u96be\u4e8b\u3002\u201d \u3000\u3000\u201c\u8bdd\u662f\u4e0d\u9519\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u627e\u4e0a\u56db\u51e4\u697c\uff0c\u4f46\u697c\u4e3b\u6781\u53ef\u80fd\u53ea\u662f\u4e00\u4e2a\u66ff\u8eab\uff01\u8fd9\u6863\u5b50\u4e8b\uff0c\u770b \u4f3c\u5bb9\u6613\uff0c\u5b9e\u5219\u96be\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u6c89\u9ed8\u4e0d\u8bed\uff0c\u6ca1\u6709\u89c1\u8fc7\u56db\u51e4\u697c\u4e3b\uff0c\u53c8\u5982\u4f55\u5206\u8fa8\u771f\u4f2a\uff1f \u3000\u3000\u201c\u4e00\u6b65\u4e00\u6b65\u6765\u5427\uff01\u603b\u6355\u5934\u5165\u626c\u5dde\u9690\u540d\u4e0d\u626c\uff0c\u5c82\u4e0d\u662f\u65e9\u51c6\u5907\u4ee5\u6697\u5bf9\u6697\u4e86\uff1f\u201d\u4e8e\u627f\u5fd7\u9053\uff1a \u201c\u5148\u8fdb\u5165\u611a\u516c\u56ed\u6797\u770b\u770b\uff0c\u5982\u662f\u8fd0\u6c14\u597d\uff0c\u4e5f\u8bb8\u4e00\u4e0b\u5b50\u5c31\u649e\u4e0a\u4e86\u68a6\u5e7b\u4e4b\u5200\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u5e0c\u671b\u4e0d\u5927\uff0c\u6211\u542c\u8bf4\u7684\u68a6\u5e7b\u4e4b\u5200\uff0c\u4e00\u5411\u662f\u72ec\u6765\u72ec\u5f80\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5929\u67ab\u9053\u957f\u7387\u9886 \u7740\u516d\u4e2a\u6700\u64c5\u8ffd\u8e2a\u7684\u9ad8\u624b\uff0c\u8ffd\u89c5\u4e86\u516d\u5929\u516d\u591c\uff0c\u65e0\u529f\u800c\u8fd8\u3002\u201d \u3000\u3000\u201c\u611a\u516c\u56ed\u6797\u4e2d\uff0c\u4f3c\u662f\u85cf\u4eba\u4e0d\u5c11\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u662f\u6740\u624b\u4e4b\u738b\u3001\u8840\u624b\u65b9\u8f6e\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u675c\u67d0\u4e5f\u662f\u8fd9\u6837\u60f3\uff0c\u4f46\u5fc3\u4e2d\u5168\u65e0\u628a\u63e1\uff0c\u5c31\u4e0d\u4fbf\u8f7b\u6613\u5730\u8bf4\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u8fd8\u8981\u7b49\u4ec0\u4e48\uff1f\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u5929\u5df2\u5165\u591c\uff0c\u54b1\u4eec\u5c31\u5f00\u59cb\u884c\u52a8\u5427\uff01\u5148\u6e21\u8fc7\u90a3\u6761\u5927\u6c34\u6c9f\uff0c \u518d\u627e\u4e2a\u9002\u5408\u7684\u5730\u65b9\u4f11\u606f\uff0c\u501f\u9ece\u660e\u524d\u90a3\u4e00\u9635\u9ed1\u6697\uff0c\u6f5c\u5165\u5b85\u9662\u4e2d\u6df1\u8349\u4e1b\u5185\uff0c\u4e24\u4e2a\u5de1\u903b\u7684\u8d3c\u5d3d\u5b50\u903e \u65f6\u4e0d\u5f52\uff0c\u53ef\u80fd\u4f1a\u5f15\u8d77\u4ed6\u4eec\u2026\u2026\u201d\u7a81\u7136\uff0c\u4f4f\u53e3\u4e0d\u8bf4\u4e86\uff0c\u77aa\u7740\u773c\u775b\u60f3\u5fc3\u4e8b\u3002 \u3000\u3000\u201c\u5c91\u80e1\u5b50\uff0c\u4f60\u7684\u996d\u91cf\u5927\uff0c\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u770b\u770b\u5e26\u7684\u5e72\u7cae\uff0c\u591f\u4e0d\u591f\u4e00\u65e5\u4e4b\u9700\uff0c\u8981\u8865\u8db3\uff0c\u73b0 \u5728\u8fd8\u6765\u5f97\u53ca\uff0c\u522b\u8981\u8be5\u52a8\u624b\u65f6\uff0c\u997f\u7684\u6ca1\u6709\u52b2\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u5728\u60f3\u554a\uff01\u201d\u5c91\u5578\u864e\u53f3\u624b\u6309\u5728\u5934\u9876\u4e0a\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u5de1\u903b\u4eba\u624b\uff0c\u662f\u4e0d\u662f\u7531\u611a\u516c\u56ed\u6797\u4e2d\u6d3e \u51fa\u6765\u7684\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u5bf9\uff01\u5e94\u8be5\u6709\u4e00\u4e2a\u53ef\u8f7b\u6613\u8d8a\u6e21\u7684\u5730\u65b9\uff0c\u800c\u4e14\uff0c\u5c31\u5728\u9644\u8fd1\u4e0d\u8fdc\u5904\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u54b1\u4eec\u53bb \u627e\u4e00\u627e\u3002\u201d \u3000\u3000\u201c\u5403\u7684\u3001\u559d\u7684\u4e0d\u7528\u6101\uff0c\u611a\u516c\u56ed\u6797\u4e2d\u6709\u7684\u662f\uff0c\u800c\u4e14\uff0c\u8fd8\u90fd\u662f\u5927\u9c7c\u5927\u8089\u3002\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u8981 \u7d27\u7684\u662f\u8981\u627e\u5230\u6e21\u6cb3\u4e4b\u8def\u3002\u201d\u8d77\u8eab\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5df2\u662f\u591c\u5e55\u4f4e\u5782\uff0c\u666f\u7269\u8ff7\u8499\uff0c\u89c6\u7ebf\u5df2\u4e0d\u592a\u6e05\u695a\u4e86\u3002 \u3000\u3000\u4f46\u56db\u4eba\u4e2a\u4e2a\u5185\u529f\u7cbe\u6df1\uff0c\u76ee\u529b\u8fc7\u4eba\uff0c\u4ed4\u7ec6\u5bfb\u89c5\u4e4b\u4e0b\uff0c\u679c\u7136\uff0c\u627e\u5230\u8d8a\u6e21\u6377\u5f84\u3002\u4e00\u6761\u77f3\u6865\uff0c\u76f4 \u901a\u5bf9\u5cb8\uff0c\u8bf4\u662f\u6865\uff0c\u4e8b\u5b9e\u4e0a\u662f\u575a\u7acb\u7684\u77f3\u693f\uff0c\u800c\u4e14\u9690\u5728\u6c34\u4e2d\u5bf8\u8bb8\uff0c\u4e0d\u7528\u5fc3\u627e\uff0c\u5c31\u5f88\u96be\u53d1\u73b0\u3002 \u3000\u3000\u6e21\u4e0a\u5bf9\u5cb8\uff0c\u9009\u4e00\u5904\u53ef\u4ee5\u85cf\u8eab\u6240\u5728\uff0c\u76d8\u5750\u8c03\u606f\u3002 \u3000\u3000\u611a\u516c\u56ed\u6797\u6709\u4e00\u5835\u56f4\u5899\uff0c\u4f46\u5df2\u5e74\u4e45\u5931\u4fee\uff0c\u7834\u635f\u591a\u5904\uff0c\u53ef\u6e05\u695a\u5730\u770b\u5230\u56f4\u5899\u5185\u9ad8\u53ef\u8fc7\u4eba\u7684\u4e71\u8349\uff0c \u4e00\u9635\u591c\u98ce\u5439\u8fc7\uff0c\u4e71\u8349\u5043\u4f0f\u3002\u53ef\u89c1\u5230\u706f\u706b\u4e00\u5904\uff0c\u706f\u5149\u4e0d\u662f\u5f88\u660e\u4eae\uff0c\u4f46\u5df2\u8db3\u53ef\u89c1\u7269\uff0c\u60f3\u662f\u6015\u5149\u4eae \u592a\u5f3a\uff0c\u6cc4\u51fa\u56f4\u5899\u3002 \u3000\u3000\u786e\u5b9a\u4e86\u53e4\u5b85\u4e2d\u6709\u4eba\uff0c\u56db\u5927\u540d\u6355\uff0c\u5168\u5b89\u4e0b\u5fc3\u6765\uff0c\u4eca\u591c\u4e0d\u4f1a\u767d\u8dd1\u4e86\u3002 \u3000\u3000\u7531\u8c03\u606f\u4e2d\u9192\u6765\uff0c\u5df2\u662f\u4e94\u66f4\u65f6\u5206\uff0c\u56db\u4eba\u7684\u7cbe\u795e\u4f53\u80fd\uff0c\u90fd\u5df2\u8c03\u81f3\u5230\u6700\u4f73\u72b6\u51b5\uff0c\u4e92\u76f8\u9894\u9996\u4e00\u7b11\uff0c \u56db\u6761\u4eba\u5f71\u98de\u8d77\uff0c\u8d8a\u8fc7\u56f4\u5899\uff0c\u9690\u6ca1\u4e8e\u5b85\u9662\u5185\u6df1\u8349\u4e1b\u4e2d\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u975e\u5e38\u7f13\u6162\u5730\u79fb\u52a8\u8eab\u8eaf\uff0c\u675c\u671b\u6708\u5f62\u52bf\u8f83\u719f\uff0c\u76f4\u5954\u6b63\u897f\uff0c\u4eba\u51fa\u8349\u4e1b\uff0c\u5230\u4e86\u4e00\u5e62\u623f\u820d\u95e8 \u53e3\u3002 \u3000\u3000\u8fd9\u662f\u6574\u5ea7\u611a\u516c\u56ed\u6797\u5185\u5341\u4f59\u5e62\u623f\u820d\u4e2d\uff0c\u8f83\u597d\u51e0\u5e62\u4e4b\u4e00\uff0c\u4e5f\u662f\u70b9\u6709\u706f\u706b\u7684\u4e00\u5e62\u3002 \u3000\u3000\u675c\u671b\u6708\u9000\u540e\u6570\u5c3a\uff0c\u628a\u8eab\u5b50\u9690\u5165\u8349\u4e2d\uff0c\u53ea\u9732\u51fa\u4e00\u4e2a\u8111\u888b\u67e5\u770b\u3002 \u3000\u3000\u4e00\u53ea\u706b\u70db\uff0c\u653e\u7f6e\u5728\u5927\u5385\u4e2d\u4e00\u5f20\u516b\u4ed9\u684c\u4e0a\uff0c\u4e00\u4e2a\u6000\u4e2d\u62b1\u7740\u9b3c\u5934\u5200\u7684\u5927\u6c49\uff0c\u5750\u5728\u4e00\u5f20\u592a\u5e08\u6905 \u4e0a\uff0c\u659c\u4f9d\u7740\u684c\u5b50\u4f11\u606f\u3002 \u3000\u3000\u5927\u5385\u4e24\u9762\u7a7a\u65f7\uff0c\u53e6\u4e24\u9762\u5404\u6709\u56db\u4e2a\u623f\u95f4\uff0c\u623f\u95f4\u6728\u95e8\u7d27\u95ed\uff0c\u60f3\u662f\u90fd\u4f4f\u4e86\u4eba\u3002 \u3000\u3000\u770b\u6e05\u9b3c\u5934\u5200\u7684\u5f62\u5f0f\uff0c\u675c\u671b\u6708\u624d\u660e\u767d\u8fd9\u91cc\u6f5c\u9690\u7684\u4eba\uff0c\u6b63\u662f\u5728\u7626\u897f\u6e56\u65c1\u88ad\u51fb\u4ed6\u7684\u6740\u624b\u3002 \u3000\u3000\u7ec6\u60f3\u5f53\u65e5\u7ecf\u8fc7\uff0c\u90a3\u4e9b\u6740\u624b\uff0c\u53ea\u4f1a\u4e24\u62db\u5389\u5bb3\u7684\u6b66\u529f\uff0c\u653b\u654c\u3001\u9632\u5b88\u5404\u4e00\u5f0f\uff0c\u867d\u53ea\u4e24\u62db\uff0c\u4f46\u5374 \u5341\u5206\u53ef\u6015\u3002\u4ed6\u4e5f\u66fe\u60f3\u8fc7\u90a3\u4e24\u62db\u53d8\u5316\u8be1\u5947\u7684\u6b66\u529f\uff0c\u5947\u5728\u4f55\u5904\uff1f\u53ea\u662f\u60f3\u4e00\u4e0b\uff0c\u6ca1\u6709\u6df1\u601d\u4e0b\u53bb\uff0c\u73b0 \u5728\u4ed4\u7ec6\u60f3\u6765\uff0c\u5c31\u8d8a\u60f3\u8d8a\u590d\u6742\u4e86\u3002\u4e00\u4e2a\u654c\u4eba\u4e0d\u582a\u4e00\u51fb\uff0c\u4e24\u4e2a\u654c\u4eba\u5408\u8d77\u6765\uff0c\u4e5f\u975e\u4ed6\u7684\u654c\u624b\uff0c\u4e09\u4e2a \u654c\u4eba\u52a0\u8d77\u6765\uff0c\u5e94\u8be5\u4e5f\u4e0d\u4f1a\u662f\u4ed6\u654c\u624b\u624d\u5bf9\uff0c\u9664\u529f\u529b\u4e4b\u5916\uff0c\u62db\u672f\u7684\u53d8\u5316\u4e0a\uff0c\u4e09\u4e2a\u4eba\u4e5f\u96be\u53ca\u4ed6\uff0c\u4f46 \u90a3\u4e00\u51fb\u53d1\u51fa\u7684\u529b\u91cf\u4e4b\u5f3a\uff0c\u8d85\u8fc7\u4e86\u4e09\u4e2a\u4eba\u52a0\u8d77\u7684\u603b\u548c\u529b\u91cf\uff0c\u6570\u500d\u4e4b\u591a\uff0c\u51e0\u4e4e\u4f7f\u4ed6\u624b\u4e2d\u7684\u4e03\u661f\u5b9d \u5251\u8131\u624b\uff0c\u600e\u4f1a\u5982\u6b64\u5462\uff1f \u3000\u3000\u8fd9\u4ef6\u4e8b\u6709\u5fc5\u8981\u63d0\u51fa\u6765\u548c\u603b\u6355\u5934\u53ca\u4e09\u4f4d\u5927\u6355\u5934\u7814\u7a76\uff0c\u4e5f\u5e0c\u671b\u4ed6\u4eec\u5728\u4eca\u591c\u4e2d\u80fd\u9047\u4e0a\u90a3\u79cd\u4e09\u4eba \u5408\u4e00\u7684\u9635\u52bf\uff0c\u81ea\u5df1\u7ecf\u5386\u4f53\u4f1a\u4e00\u756a\uff0c\u5c31\u6bd4\u542c\u4eba\u8ff0\u8bf4\u7684\u611f\u53d7\u771f\u5b9e\u4e86\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\u6709\u70b9\u4e0d\u53ef\u601d\u8bae\uff0c\u4f46\u675c\u671b\u6708\u5374\u60f3\u4e0d\u51fa\u5947\u5728\u54ea\u91cc\u4e86\uff1f \u3000\u3000\u5ffd\u7136\u60f3\u5230\u654c\u4eba\u5728\u653b\u51fb\u4e4b\u524d\uff0c\u6765\u56de\u7a7f\u63d2\u8d70\u52a8\uff0c\u5408\u6210\u4e00\u5b9a\u7684\u9635\u52bf\uff0c\u4e5f\u628a\u5175\u5203\u7ec4\u5408\u6210\u4e00\u5b9a\u7684\u683c \u5c40\uff0c\u624d\u53d1\u52a8\u653b\u51fb\u3002\u83ab\u975e\u6bdb\u75c5\uff0c\u5c31\u51fa\u5728\u90a3\u4e2a\u5730\u65b9\uff1f\u90a3\u6446\u51fa\u7684\u59ff\u6001\uff0c\u80fd\u628a\u4e00\u4e2a\u4eba\u7684\u6f5c\u529b\u53d1\u6325\u51fa\u6765\uff0c \u5175\u5203\u7ec4\u5408\u7684\u683c\u5c40\uff0c\u80fd\u4f7f\u4e0d\u540c\u7684\u5175\u5668\uff0c\u5b8c\u5168\u7ed3\u5408\u6210\u4e00\u4f53\uff0c\u8d85\u8d8a\u4e86\u4e00\u822c\u7684\u6b66\u529f\u6240\u80fd\u2026\u2026 \u3000\u3000\u60f3\u662f\u60f3\u5230\u4e86\uff0c\u4f46\u7a76\u7adf\u662f\u4ec0\u4e48\u539f\u56e0\uff0c\u8fd8\u662f\u65e0\u6cd5\u901a\u8fbe\u3002\u5f53\u4eca\u4e4b\u4e16\uff0c\u662f\u5426\u771f\u6709\u8fd9\u79cd\u80fd\u5316\u8150\u673d\u4e3a \u795e\u5947\u7684\u6b66\u529f\u5462\uff1f \u3000\u3000\u4e8b\u51fa\u5e38\u60c5\u554a\uff01\u4e5f\u5c31\u4e0d\u662f\u4e00\u822c\u63a8\u5224\u4e8b\u7406\u7684\u667a\u6167\u3001\u4e30\u5bcc\u7684\u6c5f\u6e56\u9605\u5386\u53ef\u4ee5\u8fce\u5203\u800c\u89e3\u7684\u4e86\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u4e0d\u8fdc\u5904\u7684\u8349\u4e1b\u4e2d\uff0c\u4f20\u51fa\u4e86\u4e00\u9635\u6fc0\u70c8\u7684\u6447\u52a8\uff0c\u65ad\u8349\u5c04\u98de\u7684\u58f0\u97f3\u3002\u6709\u4e00\u622a\u957f\u7ea6\u8fd1\u5c3a\uff0c \u5f62\u82e5\u67af\u679d\u4e4b\u7269\uff0c\u98de\u51fa\u8349\u4e1b\uff0c\u76f4\u843d\u5165\u5927\u5385\u95e8\u5185\uff0c\u800c\u4e14\uff0c\u8fd8\u5728\u4e0d\u505c\u5730\u626d\u52a8\u3002 \u3000\u3000\u6562\u60c5\u662f\u534a\u622a\u86c7\u8eab\uff0c\u60f3\u662f\u6bd2\u86c7\u7a81\u88ad\uff0c\u88ab\u4eba\u4e00\u5200\u8170\u65a9\uff0c\u53ea\u662f\u51fa\u5200\u5f88\u731b\uff0c\u628a\u4e00\u622a\u86c7\u8eab\u4e5f\u7529\u51fa\u8349 \u4e1b\u3002\u4f46\u5374\u65e0\u6cd5\u8ba4\u5b9a\u662f\u4e09\u65b9\u5927\u6355\u5934\u4e2d\uff0c\u54ea\u4e00\u4e2a\u6325\u5200\u65a9\u86c7\u3002 \u3000\u3000\u90a3\u62b1\u5200\u501a\u684c\u4f11\u606f\u7684\u5927\u6c49\uff0c\u7a81\u7136\u88ab\u60ca\u9192\u8fc7\u6765\u3002 \u3000\u3000\u770b\u770b\u626d\u52a8\u7684\u86c7\u8eab\uff0c\u53c8\u770b\u770b\u5929\u8272\uff0c\u9a82\u9053\uff1a\u201c\u9f9f\u513f\u5b50\uff0c\u4e0d\u957f\u773c\u775b\u770b\uff0c\u628a\u957f\u866b\u4e22\u5230\u5927\u5385\u4e2d\u6765\u3002\u201d \u4e00\u6325\u624b\u4e2d\u9b3c\u5934\u5200\uff0c\u6311\u8d77\u534a\u622a\u86c7\u8eab\uff0c\u53c8\u7529\u4e86\u51fa\u6765\u3002 \u3000\u3000\u51dd\u76ee\u601d\u7d22\u4e00\u9635\uff0c\u7a81\u7136\u56de\u8eab\uff0c\u5439\u7184\u4e86\u6848\u4e0a\u70db\u706b\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u8b66\u89c9\u4e4b\u5fc3\u5f88\u9ad8\uff0c\u867d\u7136\u53cd\u5e94\u7684\u7a0d\u6162\u4e00\u4e9b\u3002 \u3000\u3000\u675c\u671b\u6708\u51dd\u805a\u76ee\u529b\u671b\u53bb\uff0c\u679c\u89c1\u4e00\u4e2a\u8111\u888b\uff0c\u63a2\u51fa\u5385\u95e8\u5916\uff0c\u5411\u56db\u4e0b\u5de1\u89c6\u4e00\u9635\uff0c\u8f7b\u6b65\u884c\u51fa\u5385\u5916\uff0c \u5200\u6a2a\u524d\u80f8\uff0c\u4e00\u526f\u6212\u5907\u68ee\u4e25\u7684\u6837\u5b50\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u591c\u8272\u6e10\u9000\uff0c\u5df2\u662f\u7834\u6653\u65f6\u5206\uff0c\u666f\u7269\u6e10\u89c1\u6e05\u6670\u3002 \u3000\u3000\u675c\u671b\u6708\u7a81\u7136\u53d1\u89c9\uff0c\u9690\u8eab\u7684\u8349\u4e1b\u4e2d\uff0c\u6709\u4e00\u5904\u5f88\u5927\u7684\u7a7a\u9699\uff0c\u5982\u679c\u7559\u5fc3\u4e00\u4e9b\uff0c\u76ee\u529b\u53c8\u5f88\u597d\u7684\u4eba\uff0c \u53ef\u80fd\u770b\u5230\u4ed6\u6f5c\u85cf\u7684\u8eab\u5b50\u3002 \u3000\u3000\u4f46\u654c\u4eba\u5c31\u5728\u4e08\u4f59\u5916\u5904\uff0c\u5b9e\u4e0d\u4fbf\u79fb\u52a8\u8eab\u8eaf\uff0c\u53ea\u597d\u9759\u4f0f\u4e0d\u52a8\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u9635\u6b65\u5c65\u58f0\u54cd\uff0c\u4e00\u7fa4\u7070\u8863\u5927\u6c49\uff0c\u5feb\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u5c31\u5728\u8fd9\u5ea7\u5927\u5385\u95e8\u53e3\uff0c\u6392\u6210\u961f\u5f62\u3002 \u3000\u3000\u675c\u671b\u6708\u85c9\u8fd9\u4e00\u9635\u6df7\u4e71\uff0c\u628a\u8eab\u4f53\u9690\u597d\uff0c\u624d\u5411\u5916\u4ed4\u7ec6\u770b\u53bb\u3002\u90a3\u5757\u7a7a\u5730\uff0c\u4f3c\u662f\u65b0\u7ecf\u6574\u50fb\uff0c\u5730\u65b9 \u4e0d\u5927\uff0c\u597d\u50cf\u5feb\u8981\u7ad9\u6ee1\u4e86\u4eba\u3002 \u3000\u3000\u6697\u4e2d\u6570\u8ba1\u8fd9\u7fa4\u7070\u8863\u5927\u6c49\uff0c\u7ade\u6709\u4e8c\u5341\u56db\u4eba\u4e4b\u591a\uff0c\u9752\u4e00\u8272\u7684\u4f69\u7740\u9b3c\u5934\u5200\uff0c\u5200\u85cf\u5728\u8863\u895f\u5185\u7684\u957f \u53e3\u888b\u4e2d\u3002 \u3000\u3000\u539f\u6765\uff0c\u8fd9\u4e9b\u7070\u8863\u5927\u6c49\u7a7f\u7684\u957f\u886b\uff0c\u90fd\u662f\u7279\u522b\u5b9a\u5236\u7684\u8863\u670d\uff0c\u6709\u4e00\u5904\uff0c\u4e13\u95e8\u85cf\u5200\u7684\u888b\u5b50\uff0c\u4eae\u51fa \u4e86\u9b3c\u5934\u5200\uff0c\u957f\u886b\u4e0b\u6446\u4e00\u6536\uff0c\u675f\u5728\u8170\u4e0a\uff0c\u5c31\u5f62\u540c\u4e00\u4ef6\u77ed\u886b\u4e86\uff0c\u5f53\u7136\uff0c\u4e5f\u53ef\u4ee5\u8131\u53bb\u957f\u886b\uff0c\u91cc\u9762\u5c31 \u662f\u4e00\u5957\u8d34\u817f\u957f\u88e4\uff0c\u5bc6\u6263\u52b2\u88c5\uff0c\u7528\u540c\u4e00\u989c\u8272\u7684\u7070\u5e03\u5236\u6210\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u73b0\u573a\u6307\u6325\uff0c\u4e8c\u5341\u56db\u4e2a\u4eba\u81ea\u884c\u90fd\u5728\u5927\u5385\u524d\u9762\u7ad9\u597d\uff0c\u8fd9\u8868\u793a\u5df2\u662f\u8bad\u7ec3\u6709\u7d20\u7684\u6740\u624b\uff0c\u53ef \u4ee5\u81ea\u52a8\u8ba4\u5b9a\u4f4d\u7f6e\u4e86\u3002 \u3000\u3000\u8fd9\u4e9b\u7070\u8863\u6740\u624b\uff0c\u4f3c\u5404\u6709\u4e00\u5b9a\u7684\u804c\u53f8\uff0c\u90a3\u539f\u6765\u5b88\u536b\u5927\u5385\u7684\u4eba\uff0c\u6b64\u523b\u5374\u6084\u7136\u9000\u56de\u5385\u4e2d\u3002 \u3000\u3000\u4f46\u53e6\u4e00\u884c\u7070\u8863\u4eba\uff0c\u53c8\u9c7c\u8d2f\u884c\u4e86\u8fc7\u6765\uff0c\u4f7f\u539f\u5df2\u6392\u597d\u7684\u961f\u5f62\uff0c\u6709\u4e86\u5f88\u5927\u7684\u6539\u53d8\u3002\u4e09\u5341\u516d\u4e2a\u4eba\uff0c \u5206\u6210\u4e86\u5341\u4e8c\u4e2a\u5c0f\u7ec4\uff0c\u90fd\u7a7f\u7740\u4e00\u6837\u7684\u8863\u670d\u3002\u5206\u522b\u5728\u5175\u5203\u4e0a\uff0c\u540e\u6765\u7684\u5341\u4e8c\u4e2a\u4eba\uff0c\u4f69\u5e26\u7684\u662f\u4e00\u79cd\u5947 \u5f62\u5175\u5668\u5434\u94a9\u5251\u3002 \u3000\u3000\u675c\u671b\u6708\u770b\u660e\u767d\u4e86\uff0c\u5341\u4e8c\u7ec4\u6740\u624b\u7684\u4eba\u9009\uff0c\u65e9\u5df2\u7ecf\u7f16\u7ec4\u5b8c\u6210\uff0c\u5341\u4e8c\u53ea\u5434\u94a9\u5251\u9886\u961f\uff0c\u6bcf\u53ea\u5251\u914d \u4e86\u4e24\u628a\u9b3c\u5934\u5200\uff0c\u4f3c\u4e4e\u662f\u5373\u5c06\u51fa\u52a8\u3002 \u3000\u3000\u4e09\u5341\u516d\u4e2a\u6740\u624b\uff0c\u90fd\u628a\u5e26\u5728\u8eab\u4e0a\u7684\u5175\u5203\uff0c\u7ec6\u5fc3\u5730\u6574\u597d\uff0c\u4f3c\u662f\u6709\u7740\u4e00\u79cd\u7528\u5fc3\uff0c\u5200\u672a\u51fa\u9798\u3001\u6740 \u622e\u672a\u5f00\u4e4b\u524d\uff0c\u5c3d\u91cf\u4e0d\u8ba9\u4eba\u53d1\u89c9\u4ed6\u4eec\u7684\u8eab\u4efd\u3002 \u3000\u3000\u675c\u671b\u6708\u5fc3\u4e2d\u5fd6\u601d\uff1a\u8fd9\u53ef\u80fd\u662f\u4e00\u6b21\u767d\u663c\u5927\u56f4\u6740\uff0c\u6240\u4ee5\uff0c\u51fa\u52a8\u4e86\u5341\u4e8c\u7ec4\u6740\u624b\u3002 \u3000\u3000\u51e0\u4e4e\u5df2\u53ef\u80af\u5b9a\uff0c\u8fd9\u91cc\u662f\u6740\u624b\u4e4b\u738b\u3001\u8840\u624b\u65b9\u8f6e\u6f5c\u9690\u7684\u5730\u65b9\u3002 \u3000\u3000\u4f46\u5374\u65e0\u6cd5\u786e\u5b9a\u65b9\u8f6e\u662f\u5426\u4e5f\u5728\u8fd9\u91cc\uff1f \u3000\u3000\u8fd9\u91cc\u867d\u5df2\u7834\u8d25\uff0c\u4f46\u5730\u65b9\u5f88\u5927\uff0c\u5c31\u7b97\u4f4f\u4e0a\u4e24\u767e\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u6709\u4eba\u591a\u7684\u611f\u89c9\u3002 \u3000\u3000------------- \u3000\u3000\u5b64\u5251\u751f  \u626b\u63cf\u3000  \u695a\u5929\u4fa0\u5f71  OCR  \uff0c\u65e7\u96e8\u697c\u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u5341\u4e09\u7ae0 \u3000\u3000\u4e0d\u77e5\u4f55\u65f6\uff0c\u5927\u5385\u95e8\u53e3\u5df2\u7ad9\u7740\u4e00\u4e2a\u9ed1\u888d\u4eba\uff0c\u4ed6\u6765\u7684\u65e0\u58f0\u65e0\u606f\uff0c\u8fde\u6392\u5728\u5385\u524d\u7684\u4e09\u5341\u516d\u540d\u6740\u624b\uff0c \u4e5f\u672a\u53d1\u89c9\u4ed6\u4f55\u65f6\u5230\u6765\uff0c\u675c\u671b\u6708\u4e5f\u672a\u53d1\u89c9\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u4e3e\u624b\u4e00\u62db\uff0c\u5341\u4e8c\u4e2a\u4f69\u5e26\u5434\u94a9\u5251\u7684\u9886\u961f\uff0c\u5feb\u6b65\u884c\u4e86\u8fc7\u53bb\uff0c\u4e00\u9f50\u8e72\u4e0b\uff0c\u5e03\u6210\u4e86\u4e00\u4e2a\u534a \u6708\u5f62\u5f0f\u3002 \u3000\u3000\u4e24\u4e2a\u9752\u8863\u59d1\u5a18\uff0c\u884c\u51fa\u5385\u5916\uff0c\u4e00\u4e2a\u6367\u7740\u4e00\u5e45\u5377\u56fe\uff0c\u4e00\u4e2a\u642c\u7740\u4e00\u4e2a\u5c0f\u677f\u51f3\uff0c\u5c0f\u677f\u51f3\u5728\u9ed1\u8863\u4eba \u8eab\u540e\u653e\u4e0b\uff0c\u90a3\u5e45\u5377\u56fe\u5df2\u5728\u9ed1\u888d\u4eba\u9762\u524d\u5c55\u5f00\uff0c\u8fd9\u4e9b\u52a8\u4f5c\u914d\u5408\u7684\u5341\u5206\u5feb\u901f\uff0c\u597d\u50cf\u662f\u9ed1\u888d\u4eba\u65e9\u5df3\u5750 \u5728\u4e86\u90a3\u91cc\uff0c\u6b63\u5728\u6b23\u8d4f\u94fa\u5728\u9762\u524d\u7684\u4e00\u5f20\u56fe\u753b\u3002 \u3000\u3000\u9ed1\u888d\u4eba\u6307\u7740\u5730\u4e0a\u5377\u56fe\uff0c\u4f3c\u5728\u8bb2\u89e3\u5185\u5bb9\uff0c\u5341\u4e8c\u4e2a\u8e72\u7740\u7684\u7070\u8863\u4eba\u4e0d\u505c\u5730\u70b9\u5934\u3002 \u3000\u3000\u8fd1\u5728\u4e0d\u8db3\u4e24\u4e08\u7684\u675c\u671b\u6708\uff0c\u51dd\u795e\u503e\u542c\uff0c\u53ea\u542c\u5230\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u4f46\u5374\u542c\u4e0d\u61c2\u8bf4\u7684\u90fd\u662f\u4ec0\u4e48\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u9ed1\u888d\u4eba\u7ad9\u8d77\u4e86\u8eab\u5b50\uff0c\u5341\u4e8c\u4e2a\u7070\u888d\u4eba\uff0c\u4e5f\u9000\u5165\u961f\u4e2d\uff0c\u6bd4\u51fa\u4e00\u4e2a\u624b\u52bf\uff0c\u4e09\u5341\u516d\u4e2a \u6740\u624b\uff0c\u7acb\u523b\u8f6c\u8eab\u884c\u52a8\uff0c\u8d70\u7684\u6084\u7136\u8fc5\u5feb\uff0c\u7728\u773c\u95f4\uff0c\u4eba\u5df2\u4e0d\u89c1\u3002 \u3000\u3000\u675c\u671b\u6708\u751f\u51fa\u4e00\u4e2a\u53ef\u6015\u7684\u611f\u89c9\uff0c\u8fd9\u6279\u4eba\u884c\u52a8\u7684\u901f\u5ea6\uff0c\u8868\u73b0\u51fa\u7684\u6b66\u529f\uff0c\u4f3c\u4e4e\u8981\u6bd4\u7626\u897f\u6e56\u62e6\u622a \u4ed6\u7684\u4eba\uff0c\u9ad8\u51fa\u5f88\u591a\u2026\u2026\u3002 \u3000\u3000\u4e24\u4e2a\u9752\u8863\u59d1\u5a18\u4e0d\u89c1\u4e86\uff0c\u5c0f\u6728\u51f3\u548c\u5377\u56fe\u4e5f\u88ab\u6536\u8d70\uff0c\u4f46\u90a3\u9ed1\u888d\u4eba\u4ecd\u7ad9\u5728\u5385\u95e8\u53e3\u5904\uff0c\u62ac\u5934\u671b\u7740 \u6668\u66e6\uff0c\u6df1\u957f\u5730\u5401\u4e86\u4e24\u53e3\u6c14\u3002 \u3000\u3000\u675c\u671b\u6708\u5168\u795e\u51dd\u6ce8\u4ed6\uff0c\u8fd9\u4e2a\u4eba\uff0c\u5f88\u53ef\u80fd\u5c31\u662f\u8840\u624b\u65b9\u8f6e\uff0c\u6c5f\u5357\u9053\u4e0a\u7684\u6740\u624b\u4e4b\u738b\uff0c\u5fc5\u987b\u8981\u770b\u4e2a \u6e05\u695a\uff0c\u6df1\u7559\u8111\u9645\u3002 \u3000\u3000\u4f46\u89c1\u4ed6\u8138\u8272\u82cd\u767d\uff0c\u4f3c\u662f\u6b7b\u4e86\u4e00\u4e2a\u6708\u7684\u4eba\uff0c\u4e0d\u89c1\u4e00\u70b9\u8840\u8272\uff0c\u8fd9\u4e2a\u8138\u5e76\u4e0d\u96be\u770b\uff0c\u5c31\u662f\u4e0d\u50cf\u4e00 \u5f20\u6d3b\u4eba\u7684\u8138\uff0c\u4e5f\u627e\u4e0d\u51fa\u4e00\u70b9\u53ef\u4f9b\u4eba\u8bb0\u5fc6\u7684\u5730\u65b9\u3002 \u3000\u3000\u675c\u671b\u6708\u7acb\u523b\u660e\u767d\uff0c\u8fd9\u662f\u4e00\u5f20\u5236\u4f5c\u7cbe\u5de7\u7684\u4eba\u76ae\u9762\u5177\uff0c\u63a9\u906e\u4e86\u672c\u6765\u9762\u76ee\uff0c\u4e5f\u63a9\u53bb\u4e86\u4e00\u4e2a\u4eba\u7684 \u7279\u8272\uff0c\u7ed9\u4eba\u7684\u611f\u89c9\u5c31\u662f\u6728\u96d5\u6ce5\u5851\u4e00\u822c\u7684\u6b7b\u4eba\u8138\uff0c\u770b\u4e0a\u4e00\u5929\uff0c\u4e5f\u627e\u4e0d\u51fa\u4e00\u70b9\u53ef\u4f9b\u8bb0\u5fc6\u7684\u5730\u65b9\u3002 \u3000\u3000\u804c\u4e1a\u4e0a\u7684\u672c\u80fd\uff0c\u675c\u671b\u6708\u5f00\u59cb\u6ce8\u610f\u4ed6\u7684\u8eab\u6750\uff0c\u56db\u65b9\u5927\u6355\u5934\u7684\u5389\u5bb3\u4e4b\u5904\uff0c\u5c31\u662f\u4e0e\u6848\u60c5\u6709\u5173\u8fde \u7684\u4eba\uff0c\u53ea\u8981\u8ba9\u4ed6\u4eec\u89c1\u5230\u4e00\u6b21\uff0c\u5c31\u4f1a\u6c38\u7559\u8111\u9645\u3002 \u3000\u3000\u4f46\u8fd9\u4e00\u6b21\u5374\u8ba9\u675c\u671b\u6708\u5931\u671b\u6781\u4e86\uff0c\u4e0d\u4f46\u627e\u4e0d\u5230\u4ed6\u9762\u8c8c\u4e0a\u53ef\u5bb9\u8bb0\u5fc6\u4e4b\u5904\uff0c\u5c31\u662f\u8eab\u6750\u4e0a\u4e5f\u627e\u4e0d \u51fa\u4e00\u70b9\u53ef\u4f9b\u5bb9\u7559\u7684\u8bb0\u5fc6\uff0c\u4e00\u4ef6\u7a0d\u663e\u5bbd\u5927\u7684\u9ed1\u888d\uff0c\u63a9\u906e\u4e86\u5168\u8eab\uff0c\u4f60\u53ea\u80fd\u8bf4\u662f\u4e2a\u4e0d\u9ad8\u4e0d\u77ee\uff0c\u4e0d\u80d6 \u4e0d\u7626\u7684\u4eba\uff0c\u626c\u5dde\u57ce\u4e2d\u5341\u4e07\u7537\u4eba\uff0c\u4e00\u5927\u534a\u90fd\u662f\u8fd9\u4e2a\u6837\u5b50\u3002 \u3000\u3000\u675c\u671b\u6708\u6709\u80a1\u5f3a\u70c8\u7684\u51b2\u52a8\uff0c\u5f88\u60f3\u51b2\u51fa\u53bb\uff0c\u62c9\u4e0b\u4ed6\u8138\u4e0a\u7684\u4eba\u76ae\u9762\u5177\uff0c\u8131\u53bb\u4ed6\u8eab\u4e0a\u7684\u9ed1\u888d\uff0c\u770b \u4e2a\u6e05\u695a\u2026\u2026\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u56db\u4e2a\u9690\u5728\u8349\u4e2d\u89c2\u5bdf\u9ed1\u888d\u4eba\u4e00\u4e3e\u4e00\u52a8\u7684\u56db\u65b9\u5927\u6355\u5934\uff0c\u90fd\u6709\u7740\u76f8\u540c\u611f\u53d7\uff0c\u4f46\u90fd\u987e\u53ca \u5230\u53e6\u5916\u4e09\u4e2a\u4eba\u53d7\u5230\u62d6\u7d2f\uff0c\u8bf4\u4ed6\u6c89\u4e0d\u4f4f\u6c14\uff0c\u5fcd\u4e0b\u672a\u52a8\u3002 \u3000\u3000\u9ed1\u888d\u4eba\u7ad9\u4e86\u7247\u523b\uff0c\u8f6c\u8eab\u5165\u5385\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5df2\u662f\u5929\u8272\u5927\u4eae\uff0c\u65ed\u65e5\u521d\u5347\u7684\u6668\u5149\u3002\u611a\u516c\u56ed\u6797\u4e2d\u7684\u666f\u7269\uff0c\u5df2\u660e\u6717\u53ef\u89c1\u3002 \u3000\u3000\u4f46\u6df1\u8349\u8fc7\u4eba\uff0c\u5bc6\u5ea6\u4ea6\u9ad8\uff0c\u4eba\u5728\u5176\u4e2d\uff0c\u80fd\u591f\u770b\u5230\u4e00\u4e2a\u65b9\u4f4d\uff0c\u5c31\u5f97\u7528\u5c3d\u5fc3\u673a\uff0c\u5206\u62e8\u8349\u4e1b\uff0c\u53c8 \u4e0d\u80fd\u8ba9\u654c\u4eba\u53d1\u89c9\uff0c\u8981\u60f3\u76d1\u770b\u56db\u65b9\uff0c\u662f\u5b8c\u5168\u4e0d\u53ef\u80fd\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u4e2a\u95ee\u9898\uff0c\u6d6e\u73b0\u8111\u9645\uff0c\u675c\u671b\u6708\u5f00\u59cb\u63a8\u60f3\uff0c\u5927\u767d\u5929\u51fa\u52a8\u4e00\u5927\u6279\u6740\u624b\uff0c\u800c\u4e14\u53c8\u662f\u8f83\u9ad8\u7b49\u7ea7 \u7684\u7cbe\u9510\uff0c\u4f1a\u53bb\u5bf9\u4ed8\u4ec0\u4e48\u4eba\u5462\uff1f \u3000\u3000\u8840\u624b\u65b9\u8f6e\uff0c\u64c5\u957f\u5e03\u5c40\uff0c\u521a\u624d\u53ec\u5341\u4e8c\u4e2a\u9886\u961f\uff0c\u5f88\u53ef\u80fd\u8c08\u7684\u662f\u5e03\u5c40\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u5175\u5203\u85cf\u7684\u5f88\u5bc6\uff0c\u4f1a\u5f71\u54cd\u51fa\u5200\u7684\u901f\u5ea6\uff0c\u76ee\u7684\u662f\u5728\u51fa\u5200\u4e4b\u524d\uff0c\u907f\u514d\u88ab\u4eba\u53d1\u89c9\uff0c\u8fd9\u4e00\u4e2a\u88ab \u56f4\u6740\u7684\u5730\u65b9\uff0c\u53ef\u80fd\u5c31\u5728\u626c\u5dde\u57ce\u4e2d\u2026\u2026\u3002 \u3000\u3000\u4e0d\u4f1a\u662f\u5341\u5927\u5bb6\u65cf\uff0c\u4f1a\u662f\u8c01\u5462\uff1f\u96be\u9053\u662f\u626c\u5dde\u5e9c\u8859\u7684\u738b\u5927\u4eba\u2026\u2026\u3002 \u3000\u3000\u601d\u5ff5\u53ca\u6b64\uff0c\u5fc3\u4e2d\u609a\u7136\u4e00\u60ca\uff0c\u7d27\u5f20\u7684\u53cc\u624b\u4e00\u4f38\uff0c\u62e8\u7684\u8eab\u4fa7\u957f\u8349\u6447\u98a4\u3002 \u3000\u3000\u5e78\u597d\u6709\u4eba\u53ca\u65f6\uff0c\u6276\u4f4f\u4e86\u98a4\u52a8\u957f\u8349\u3002 \u3000\u3000\u662f\u6709\u4eba\uff0c\u800c\u4e14\u662f\u4e09\u4e2a\u4eba\uff0c\u5434\u94c1\u5cf0\u3001\u5c91\u5578\u864e\u3001\u4e8e\u627f\u5fd7\uff0c\u5168\u90fd\u5230\u4e86\u675c\u671b\u6708\u7684\u8eab\u8fb9\u3002 \u3000\u3000\u201c\u5c0f\u675c\uff0c\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u60f3\u5230\u4e86\u4ec0\u4e48\u53ef\u6015\u7684\u4e8b\uff0c\u7d27\u5f20\u5230\u5fd8\u5176\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u675c\u671b\u6708\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u662f\u6709\u4e9b\u7d27\u5f20\uff0c\u4e0d\u77e5\u4e09\u4f4d\u662f\u5426\u770b\u5230\u4e86\uff0c\u65b9\u8f6e\u5df2\u51fa\u52a8\u4e86\u5927\u6279\u6740 \u624b\uff1f\u201d \u3000\u3000\u5434\u94c1\u5cf0\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u770b\u5230\u4e86\uff0c\u6211\u4eec\u5c31\u85cf\u5728\u4f60\u7684\u8eab\u4fa7\u4e0d\u8fdc\u5904\uff0c\u4f46\u4f60\u8fd9\u91cc\u89c6\u754c\u5bbd\u5e7f\uff0c\u4e5f\u770b \u7684\u6e05\u695a\uff0c\u6240\u4ee5\uff0c\u5c31\u770b\u7684\u5168\u795e\u8d2f\u6ce8\u4e86\uff0c\u6211\u4eec\u4e5f\u770b\u5230\u4e86\u5927\u6982\u60c5\u5f62\uff0c\u4f46\u5374\u4e0d\u80fd\u80af\u5b9a\u4ed6\u4eec\u884c\u52a8\u7684\u76ee\u7684\u3002 \u6240\u4ee5\uff0c\u6765\u627e\u4f60\u5546\u91cf\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u5401\u4e00\u53e3\u957f\u6c14\uff0c\u9053\uff1a\u201c\u4e09\u4f4d\uff0c\u662f\u5426\u5df2\u5fc3\u4e2d\u6709\u4e2a\u8c31\uff1f\u201d \u3000\u3000\u201c\u4f1a\u4e0d\u4f1a\u662f\u626c\u5dde\u5e9c\u8859\uff1f\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u770b\u4ed6\u4eec\u884c\u52a8\u7684\u8c28\u614e\uff0c\u597d\u50cf\u53bb\u505a\u4e00\u4ef6\u60ca\u5929\u52a8\u5730\u7684\u5927 \u4e8b\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e5f\u6b63\u662f\u6211\u62c5\u5fc3\u7684\u4e8b\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5982\u679c\uff0c\u603b\u6355\u5934\u5e26\u8d70\u4e86\u5c0f\u6587\u3001\u5c0f\u96c5\uff0c\u626c\u5dde\u5e9c\u8859\u867d \u7136\u8fd8\u6697\u4e2d\u57cb\u4f0f\u4e86\u4e24\u4f4d\u9ad8\u624b\uff0c\u4f46\u4ed6\u7edd\u65e0\u80fd\u529b\u963b\u6321\u5341\u4e8c\u7ec4\u6740\u624b\u7684\u56f4\u88ad\u3002\u201d \u3000\u3000\u201c\u603b\u6355\u5934\u601d\u8651\u5468\u5bc6\uff0c\u5c0f\u6587\u3001\u5c0f\u7a1a\uff0c\u81f3\u5c11\u4f1a\u7559\u4e0b\u4e00\u4e2a\u3002\u201d\u5434\u94c1\u5cf0\u8bf4\u3002 \u3000\u3000\u201c\u5c31\u662f\u7559\u4e0b\u4e00\u4e2a\uff0c\u4e5f\u4e8e\u4e8b\u65e0\u8865\uff0c\u5c0f\u6587\u4e5f\u597d\uff0c\u5c0f\u96c5\u4e5f\u7f62\uff0c\u4e00\u4e2a\u4eba\u53ea\u80fd\u963b\u6321\u4e00\u7ec4\u6740\u624b\u3002\u201d\u675c \u671b\u6708\u9053\uff1a\u201c\u4ed6\u4eec\u53bb\u4e86\u5341\u4e8c\u7ec4\uff0c\u4e09\u5341\u516d\u4e2a\u4eba\uff0c\u4e5f\u7b49\u4e8e\u5341\u4e8c\u4e2a\u5f3a\u8fc7\u6211\u675c\u67d0\u4eba\u7684\u9ad8\u624b\u2026\u2026\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e0d\u5f97\u4e86\uff0c\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u6211\u4eec\u5f97\u5feb\u4e9b\u8d76\u56de\u53bb\uff0c\u665a\u5c31\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u521a\u624d\u89c1\u5230\u7684\u9ed1\u8863\u4eba\uff0c\u662f\u4e0d\u662f\u6740\u624b\u4e4b\u738b\u65b9\u8f6e\uff1f\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u5e0c\u671b\u4e0d\u662f\u4ed6\u624d\u597d\uff01\u201d \u3000\u3000\u201c\u6050\u6015\u8981\u5434\u5144\u5931\u671b\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u90a3\u4e2a\u4eba\u516b\u6210\u662f\u4ed6\uff0c\u624d\u4f1a\u4fdd\u62a4\u7684\u5929\u8863\u65e0\u7f1d\uff0c\u6211\u770b\u7684\u5f88 \u4ed4\u7ec6\uff0c\u4f46\u662f\u5374\u8bb0\u4e0d\u8d77\u4ed6\u4efb\u4f55\u4e00\u70b9\u7279\u5f81\uff0c\u4ed6\u53ea\u8981\u4e0d\u53d6\u4e0b\u4eba\u76ae\u9762\u5177\uff0c\u8131\u53bb\u5bbd\u5927\u7684\u9ed1\u888d\uff0c\u4e0d\u7ba1\u5728\u54ea \u91cc\uff0c\u89c1\u5230\u4ed6\u90fd\u8ba4\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u8840\u624b\u65b9\u8f6e\u5982\u6b64\u7cbe\u660e\u3001\u5c0f\u5fc3\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u53ef\u80fd\u4e5f\u53d1\u73b0\u4e86\u6211\u4eec\u2026\u2026\u201d \u3000\u3000\u201c\u5feb\u8d70\uff01\u201d\u5c91\u5578\u864e\u4e00\u98de\u51b2\u5929\uff0c\u62d4\u8d77\u4e86\u4e24\u4e08\u591a\u9ad8\uff0c\u5411\u5916\u98de\u53bb\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u3001\u4e8e\u627f\u5fd7\u3001\u675c\u671b\u6708\u7d27\u968f\u7740\u8fde\u7fe9\u800c\u8d77\uff0c\u98de\u8dc3\u51fa\u611a\u516c\u56ed\u6797\u3002 \u3000\u3000\u60c5\u6025\u4e4b\u4e0b\uff0c\u987e\u4e0d\u5f97\u5728\u8fd9\u6700\u540e\u4e00\u523b\uff0c\u66b4\u9732\u884c\u8e2a\u4e86\u3002 \u3000\u3000\u5c91\u5578\u864e\u505c\u4e0b\u811a\u6b65\uff0c\u7b11\u9053\uff1a\u201c\u662f\u4e0d\u662f\u6740\u624b\u4e4b\u738b\uff0c\u628a\u6240\u6709\u7684\u6740\u624b\uff0c\u5168\u90fd\u6d3e\u51fa\u53bb\u4e86\uff0c\u8fd9\u5012\u662f\u4e00 \u4e2a\u597d\u673a\u4f1a\u5440\uff1f\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u4ec0\u4e48\u597d\u673a\u4f1a\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5ea7\u6797\u56ed\u4e2d\u53ea\u4f59\u4e0b\u8840\u624b\u65b9\u8f6e\uff0c\u54b1\u4eec\u53ef\u4ee5\u751f\u64d2\u4e86\u4ed6\u3002\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u5408\u54b1\u4eec\u56db\u4eba\u4e4b\u529b\uff0c \u5e94\u8be5\u4e0d\u662f\u96be\u4e8b\uff1f\u201d \u3000\u3000\u201c\u7109\u77e5\u4e0d\u662f\u4e00\u4e2a\u5e03\u5c40\uff0c\u8bf1\u4eba\u5165\u4f0f\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6740\u624b\u4e4b\u738b\u65e2\u662f\u4e2a\u957f\u4e8e\u5e03\u5c40\u7684\u4eba\uff0c\u600e\u4e48 \u5982\u6b64\u5927\u610f\u3002\u201d \u3000\u3000\u201c\u5c31\u7b97\u662f\u4e2a\u5e03\u5c40\u5427\uff01\u4ee5\u4f60\u5434\u5144\u7684\u91d1\u9556\u3001\u5c91\u67d0\u7684\u98de\u65a7\u3001\u4e8e\u5144\u7684\u5feb\u5200\u3001\u5c0f\u675c\u7684\u5229\u5251\uff0c\u5408\u6211\u4eec \u56db\u4eba\u4e4b\u529b\uff0c\u4e5f\u80fd\u6740\u51fa\u4e00\u6761\u8840\u8def\uff0c\u5982\u80fd\u751f\u64d2\u4e86\u6740\u624b\u4e4b\u738b\uff0c\u53ef\u80fd\u4f7f\u5927\u5c40\u9006\u8f6c\u2026\u2026\u201d \u3000\u3000\u201c\u6709\u70b9\u5f02\u60f3\u5929\u5f00\uff0c\u5374\u4e0d\u65e0\u9053\u7406\uff0c\u5175\u4e0d\u538c\u8bc8\uff0c\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u4e5f\u8bb8\u80fd\u6740\u4ed6\u4e2a\u63aa\u624b\u4e0d\u53ca\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u5e76\u4e0d\u540c\u610f\u5c91\u5578\u864e\u7684\u60f3\u6cd5\uff0c\u4ed6\u6025\u4e8e\u8d76\u56de\u626c\u5dde\u5e9c\u8859\uff0c\u9664\u4e86\u6302\u5ff5\u738b\u77e5\u5e9c\u7684\u5b89\u5371\u4e4b\u5916\uff0c\u8fd8 \u591a\u4e86\u4e00\u5206\u5bf9\u60dc\u7389\u7684\u5173\u6000\u3002\u4f46\u89c1\u4e8e\u627f\u5fd7\u540c\u610f\u4e86\u5c91\u5578\u864e\u7684\u4e3b\u610f\uff0c\u4e5f\u5c31\u95ed\u53e3\u4e0d\u8a00\u4e86\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u7b11\u7b11\uff0c\u4e5f\u672a\u518d\u51fa\u8a00\u53cd\u9a73\uff0c\u5fc3\u4e2d\u4e5f\u5f88\u60f3\u4e86\u89e3\u4e00\u4e0b\uff0c\u56db\u65b9\u5927\u6355\u5934\u8054\u624b\u62d2\u654c\uff0c\u80fd\u6709\u591a\u5927 \u7684\u5a01\u529b\uff0c\u6740\u624b\u4e4b\u738b\u7684\u5408\u51fb\u5947\u9635\uff0c\u53c8\u662f\u600e\u4e48\u4e00\u4e2a\u5389\u5bb3\u6cd5\uff1f \u3000\u3000\u5c91\u5578\u864e\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u675c\u9ed8\u8ba4\u4e86\uff0c\u5434\u5144\u4e5f\u4e0d\u518d\u53cd\u5bf9\uff0c\u54b1\u4eec\u8fd8\u5728\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u98de\u8eab\u4e00\u8dc3\u91cd\u56de\u611a\u516c\u56ed\u6797\u4e2d\uff0c\u76f4\u5954\u5927\u5385\uff0c\u51c6\u5907\u76f4\u6363\u9ec4\u9f99\u4e86\u3002 \u3000\u3000\u4eba\u5df2\u81f3\u5927\u5385\u95e8\u5916\uff0c\u5374\u542c\u675c\u671b\u6708\u5927\u58f0\u559d\u9053\uff1a\u201c\u5c91\u5144\u7559\u6b65\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u6536\u4f4f\u811a\u6b65\uff0c\u7b11\u9053\uff1a\u201c\u5385\u95e8\u5927\u5f00\uff0c\u65e0\u4eba\u9632\u5b88\uff0c\u8fd8\u8981\u7b49\u4ec0\u4e48\u5440\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u6025\u884c\u4e24\u6b65\uff0c\u8ffd\u5230\u95e8\u5916\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c91\u80e1\u5b50\uff0c\u6740\u624b\u4e4b\u738b\uff0c\u51b7\u9177\u51f6\u6b8b\uff0c\u6bcf\u4e00\u6b21\u56f4\u6740\u5f3a \u654c\uff0c\u5c31\u4e0d\u60dc\u727a\u7272\u5927\u91cf\u95e8\u4e0b\u5f1f\u5b50\uff0c\u53ea\u6c42\u80dc\u7b97\uff0c\u4ee5\u5176\u5fc3\u6027\u4e4b\u6bd2\uff0c\u5fc5\u4f1a\u8bbe\u4e0b\u5389\u5bb3\u7684\u57cb\u4f0f\u2026\u2026\u201d \u3000\u3000\u5c91\u5578\u864e\u65e9\u5df2\u4ed4\u7ec6\u5730\u6253\u91cf\u5385\u5185\u5f62\u52bf\uff0c\u7b11\u4e00\u7b11\u63a5\u9053\uff1a\u201c\u5982\u6709\u57cb\u4f0f\uff0c\u4e5f\u5728\u90a3\u6247\u6728\u95e8\u7d27\u95ed\u7684\u623f\u4e2d\uff0c \u6211\u8981\u5148\u8d4f\u4ed6\u4e00\u8bb0\u98de\u65a7\uff0c\u4ee5\u63a2\u7a76\u7adf\u3002\u201d \u3000\u3000\u98de\u65a7\u5c60\u72ee\u864e\uff0c\u5c91\u80e1\u5b50\u7684\u98de\u65a7\u5389\u5bb3\uff0c\u65e9\u5df2\u540d\u9707\u5173\u4e1c\u3002\u4f46\u5434\u3001\u4e8e\u3001\u675c\u4e09\u4eba\uff0c\u90fd\u672a\u89c1\u9047\u4ed6\u98de\u65a7 \u7684\u5a01\u529b\uff0c\u7adf\u7136\u4e00\u9f50\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5bf9\uff01\u98de\u65a7\u7834\u95e8\uff0c\u7eb5\u6709\u57cb\u4f0f\uff0c\u4ea6\u53ef\u65e0\u5fe7\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u5927\u4e0d\u4e86\u635f\u5931\u4e00\u628a\u98de\u65a7\u3002\u201d\u5c91\u5578\u864e\u4e00\u9762\u7b54\u8bdd\uff0c\u53f3\u624b\u5df2\u63e1\u4e86\u4e00\u67c4\u98de\u65a7\u3002 \u3000\u3000\u6b64\u65a7\u5203\u5bbd\u516d\u5bf8\uff0c\u5f62\u5982\u534a\u6708\uff0c\u4f3c\u5df2\u5931\u53bb\u4e86\u6697\u5668\u7684\u8f7b\u5de7\u6697\u88ad\u4e4b\u4fbf\uff0c\u662f\u51ed\u85c9\u7740\u5a01\u529b\u6151\u4eba\u7684\u5de8\u578b \u4e4b\u7269\u3002 \u3000\u3000\u679c\u7136\uff0c\u98de\u65a7\u51fa\u624b\uff0c\u6709\u5982\u8f6e\u6708\u65cb\u98de\uff0c\u631f\u7740\u4e00\u9635\u7834\u7a7a\u91d1\u98ce\uff0c\u649e\u5411\u5385\u4e2d\u4e00\u6247\u7d27\u95ed\u7684\u6728\u95e8\u3002\u679c\u7136 \u8f7b\u9707\u58f0\u4e2d\uff0c\u98de\u65a7\u5df2\u7834\u95e8\u800c\u5165\u3002 \u3000\u3000\u8fd8\u672a\u542c\u95fb\u5230\u5ba4\u5185\u7684\u53cd\u5e94\uff0c\u5434\u94c1\u5cf0\u5df2\u6c89\u58f0\u559d\u9053\uff1a\u201c\u9000\uff01\u201d\u5f53\u5148\u8dc3\u9000\u516b\u5c3a\u3002 \u3000\u3000\u56db\u5927\u540d\u6355\u4e2a\u4e2a\u76ee\u5149\u9510\u5229\uff0c\u5434\u94c1\u5cf0\u559d\u58f0\u4e2d\uff0c\u4e8e\u627f\u5fd7\u3001\u675c\u671b\u6708\uff0c\u5df2\u77a7\u51fa\u90a3\u6d1e\u5f00\u7684\u6728\u95e8\u5185\uff0c\u6709 \u4e00\u80a1\u6de1\u7ea2\u7684\u70df\u96fe\uff0c\u5192\u4e86\u51fa\u6765\uff0c\u8fc5\u5feb\u5730\u5728\u5385\u5185\u6269\u5c55\u3002 \u3000\u3000\u5c91\u5578\u864e\u4e5f\u770b\u5230\u4e86\uff0c\u4e09\u4e2a\u4eba\u8fde\u8882\u800c\u9000\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u90fd\u907f\u5f00\u4e86\u9000\u5165\u8349\u4e1b\u4e2d\uff0c\u8840\u624b\u65b9\u8f6e\uff0c\u5584\u4e8e\u5e03\u5c40\uff0c\u5f88\u53ef\u80fd\u53c8\u4f1a\u5728\u8349\u4e1b\u4e2d\u5e03\u4e0b\u57cb\u4f0f\u3002 \u3000\u3000\u4f46\u8fd9\u7247\u9664\u4e86\u8349\u7684\u7a7a\u5730\u4e0d\u5927\uff0c\u4e09\u4eba\u540e\u9000\u7684\u5f62\u52bf\uff0c\u5df2\u5230\u4e86\u53e6\u4e00\u5e62\u623f\u5c4b\u524d\u9762\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u56de\u76ee\u56db\u4e0b\u4e00\u770b\uff0c\u53d1\u89c9\u56db\u4eba\u80cc\u4e34\u7684\u623f\u820d\uff0c\u662f\u4e00\u9762\u5c71\u5899\uff0c\u65e0\u7a97\u65e0\u95e8\uff0c\u624d\u653e\u4e0b\u5fc3\u6765\uff0c\u9053\uff1a \u201c\u623f\u5185\u5192\u51fa\u7684\u6de1\u7ea2\u70df\u96fe\uff0c\u53ef\u80fd\u662f\u4f20\u8bf4\u4e2d\u4e03\u715e\u6843\u82b1\u7634\uff0c\u8001\u5434\u4e0d\u61c2\u836f\u7406\uff0c\u8bf4\u4e0d\u51fa\u5982\u4f55\u914d\u5236\u6210\u6b64\u5947 \u6bd2\uff0c\u53ea\u77e5\u4ed6\u4eba\u4e2d\u6bd2\u4e4b\u540e\uff0c\u4e0d\u4f1a\u7acb\u523b\u6b7b\u4ea1\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5c31\u7b97\u4e0d\u4e0a\u4ec0\u4e48\u5947\u6bd2\u4e86\u3002\u201d\u5c91\u5578\u864e\u63a5\u9053\uff1a\u201c\u53ea\u8981\u6709\u5341\u4e8c\u4e2a\u65f6\u8fb0\u7684\u65f6\u95f4\uff0c\u5c31\u8be5\u80fd\u627e\u5f97\u5230 \u7597\u6bd2\u5927\u592b\u3002\u201d \u3000\u3000\u201c\u4e2d\u4e86\u4e03\u715e\u6843\u82b1\u7634\uff0c\u6bd2\u6027\u53d1\u4f5c\u540e\u7684\u6050\u6016\uff0c\u6bd4\u6b7b\u4ea1\u8fd8\u8981\u53ef\u6015\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u8fd9\u4e16\u4e0a\u662f\u5426 \u6709\u89e3\u6b64\u6bd2\u7684\u5927\u592b\uff0c\u6211\u4e0d\u77e5\u9053\uff0c\u4f46\u6c74\u6881\u57ce\u4e2d\u5341\u4e8c\u4f4d\u540d\u533b\uff0c\u4e2a\u4e2a\u662f\u675f\u624b\u65e0\u7b56\uff0c\u770b\u7740\u4ed6\u75c5\u52bf\u53d1\u4f5c\uff0c \u75af\u72c2\u5bb3\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u6700\u540e\u5462\uff1f\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u8be5\u6709\u4e2a\u7ed3\u5c40\u5427\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6b7b\u4e86\uff01\u201d \u3000\u3000\u201c\u6bd2\u53d1\u800c\u6b7b\uff0c\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u6b7b\u7684\u662f\u5f88\u75db\u82e6\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u88ab\u4eba\u4e00\u638c\u6253\u6b7b\u4e86\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u90a3\u4e2a\u51fa\u624b\u6740\u4ed6\u7684\u5c31\u662f\u533a\u533a\u5728\u4e0b\u3002\u201d \u3000\u3000\u201c\u4ed6\u6bd2\u53d1\u5931\u63a7\uff0c\u7f6a\u4e0d\u81f3\u6b7b\u554a\uff01\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u6740\u4ed6\u4e8e\u5fc3\u4f55\u5fcd\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5c31\u662f\u4e03\u715e\u6843\u82b1\u7634\u7684\u53ef\u6016\u5904\u4e86\uff0c\u6bd2\u53d1\u4e4b\u540e\uff0c\u4ed6\u4f3c\u4e4e\u662f\u53ea\u80fd\u5206\u8fa8\u51fa\u7537\u3001\u5973\u4e24\u79cd\u4eba\uff0c\u81f3\u4e8e \u90a3\u4e2a\u5973\u4eba\u662f\u4ed6\u7684\u4ec0\u4e48\u4eba\uff1f\u4f3c\u5df2\u65e0\u80fd\u5206\u8fa8\uff0c\u89c1\u5230\u5973\u4eba\uff0c\u5c31\u5f3a\u884c\u4fb5\u72af\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4e0d\u7ba1\u5bf9\u65b9 \u7684\u8eab\u5206\u4e3a\u4f55\uff0c\u4eb2\u5982\u5144\u59b9\u3001\u6bcd\u5b50\uff0c\u4ea6\u96be\u5e78\u514d\u3002\u201d \u3000\u3000\u4e09\u5927\u6355\u5934\u5168\u542c\u50bb\u4e86\uff0c\u534a\u664c\u4e4b\u540e\uff0c\u5c91\u5578\u864e\u624d\u5401\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8001\u5434\uff0c\u8fd9\u4ef6\u4e8b\u662f\u542c\u4eba\u8bf4\u7684\uff0c\u8fd8 \u662f\u4f60\u4eb2\u773c\u770b\u5230\u3002\u201d \u3000\u3000\u201c\u4eb2\u773c\u770b\u5230\uff0c\u6240\u4ee5\uff0c\u6211\u4e0d\u80fd\u5fcd\u53d7\uff0c\u624d\u51fa\u624b\u70b9\u4e86\u4ed6\u7684\u6b7b\u7a74\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4f46\u6211\u8fd8\u662f\u51fa\u624b \u665a\u4e86\u4e00\u4e9b\uff0c\u51e0\u4e4e\u5bb3\u4e86\u4ed6\u7684\u59b9\u59b9\u2026\u2026\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u6740\u7684\u597d\uff01\u7f6a\u4e0d\u53ef\u6055\u554a\u3002\u201d \u3000\u3000\u201c\u5176\u5b9e\uff0c\u9519\u4e0d\u5728\u4ed6\uff0c\u4efb\u4f55\u4eba\u4e2d\u4e86\u4e03\u715e\u6843\u82b1\u7634\uff0c\u90fd\u4f1a\u548c\u4ed6\u4e00\u6837\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u9664\u975e\u6709\u89e3 \u6bd2\u836f\u7269\uff0c\u5426\u5219\uff0c\u53ea\u6709\u6b7b\u8def\u4e00\u6761\uff0c\u5341\u4e4b\u516b\u4e5d\uff0c\u8fd8\u8981\u6b7b\u5728\u81f3\u4eb2\u597d\u53cb\u7684\u624b\u4e0b\uff01\u201d \u3000\u3000\u201c\u5728\u6bd2\u6027\u53d1\u4f5c\u7684\u60c5\u51b5\u4e4b\u4e0b\uff0c\u4e5f\u53ea\u6709\u81f3\u4eb2\u597d\u53cb\u5728\u573a\u3002\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u6b64\u6bd2\u5fc5\u9664\uff0c\u4e0d\u80fd\u7559\u7740 \u5b83\u5728\u4e16\u4e0a\u5bb3\u4eba\u3002\u8d70\uff01\u54b1\u4eec\u5305\u4e0a\u53e3\u9f3b\uff0c\u51b2\u8fc7\u53bb\uff0c\u8054\u624b\u62a2\u653b\uff0c\u6740\u4ed6\u4e2a\u5e94\u53d8\u4e0d\u53ca\u2026\u2026\u201d \u3000\u3000\u4f46\u95fb\u4e00\u9635\u91d1\u5203\u7a7f\u58c1\u4e4b\u58f0\uff0c\u5341\u4f59\u652f\u94c1\u67aa\uff0c\u7531\u8eab\u540e\u7684\u5899\u58c1\u4e0a\u7a7f\u523a\u8fc7\u6765\u3002 \u3000\u3000\u800c\u4e14\uff0c\u8ba4\u4f4d\u5f88\u51c6\uff0c\u675c\u671b\u6708\u52a8\u4f5c\u8fc5\u5feb\uff0c\u95fb\u8b66\u95ea\u5f00\uff0c\u53ea\u88ab\u523a\u7834\u4e86\u8863\u670d\uff0c\u4e8e\u627f\u5fd7\u548c\u5c91\u5578\u864e\uff0c\u90fd \u4f24\u5230\u4e86\u76ae\u8089\uff0c\u5192\u51fa\u9c9c\u8840\u3002 \u3000\u3000\u53ea\u6709\u5434\u94c1\u5cf0\u4e00\u4eba\uff0c\u672a\u53d7\u5230\u4efb\u4f55\u4f24\u5bb3\uff0c\u4ed6\u4e00\u76f4\u4fdd\u6301\u7740\u9ad8\u5ea6\u7684\u8b66\u89c9\uff0c\u4e5f\u7ad9\u7684\u79bb\u5899\u58c1\u8f83\u8fdc\u3002 \u3000\u3000\u675c\u671b\u6708\u4f4e\u58f0\u9053\uff1a\u201c\u5411\u540e\u9000\uff0c\u4ed6\u4eec\u53ef\u80fd\u51b2\u51fa\u6765\uff01\u201d \u3000\u3000\u4f46\u95fb\u5f13\u5f26\u58f0\u97f3\u54cd\u52a8\uff0c\u4e00\u6392\u957f\u7bad\uff0c\u7531\u6570\u4e08\u5916\u4e00\u5e62\u5c4b\u810a\u540e\u5c04\u4e86\u8fc7\u6765\uff0c\u4e00\u6392\u6709\u516b\u652f\u4e4b\u591a\u3002 \u3000\u3000\u8fd9\u662f\u5f3a\u5f13\u957f\u7bad\uff0c\u53ef\u5c04\u5230\u5341\u4e08\u5de6\u53f3\uff0c\u6bcf\u4eba\u4e00\u6b21\uff0c\u53ea\u80fd\u5c04\u51fa\u4e00\u652f\uff0c\u516b\u652f\u7bad\uff0c\u8bf4\u660e\u4e86\u90a3\u91cc\u57cb\u4f0f \u7684\u5f13\u7bad\u624b\uff0c\u6700\u5c11\u6709\u516b\u4e2a\u4eba\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\u957f\u4e8e\u5e03\u5c40\uff0c\u679c\u7136\u4e0d\u9519\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u57cb\u4f0f\u4f0e\u4fe9\uff0c\u7edd\u4e0d\u81f3\u6b64\u3002\u6b64\u5730\u4e0d\u5b9c\u4e45\u7559\uff0c \u6211\u4eec\u9000\u51fa\u53bb\u5427\uff01\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u3001\u5c91\u5578\u864e\u867d\u7136\u662f\u76ae\u8089\u4e4b\u4f24\uff0c\u552f\u6050\u5bf9\u65b9\u5175\u5203\u4e4b\u4e0a\u6709\u6bd2\uff0c\u884c\u8e2a\u5df2\u9732\uff0c\u65e0\u6cd5\u518d\u6536\u5947\u88ad\u4e4b \u6548\uff0c\u9000\u51fa\u611a\u516c\u56ed\u6797\uff0c\u6b64\u5176\u65f6\u4e5f\u3002 \u3000\u3000\u4f46\u5c91\u5578\u864e\u4e34\u53bb\u4e4b\u9645\uff0c\u5374\u5e4c\u71c3\u4e86\u4e00\u679a\u706b\u6298\u5b50\uff0c\u6295\u5165\u4e86\u6df1\u8349\u4e1b\u4e2d\u3002 \u3000\u3000\u7acb\u523b\u95f4\u706b\u7130\u5347\u8d77\uff0c\u6d53\u70df\u5bc6\u5e03\uff0c\u56db\u4eba\u5728\u6d53\u70df\u4e2d\u79bb\u5f00\u4e86\u611a\u516c\u56ed\u6797\u3002 \u3000\u3000\u9000\u51fa\u6797\u56ed\uff0c\u7acb\u523b\u6e21\u56de\u5bf9\u5cb8\uff0c\u56de\u5934\u770b\u6797\u56ed\uff0c\u666f\u7269\u5982\u65e7\uff0c\u5c91\u5578\u864e\u4e00\u628a\u706b\uff0c\u5e76\u672a\u5f15\u8d77\u706b\u707e\uff0c\u4f3c \u5df2\u88ab\u4eba\u53ca\u65f6\u6251\u7184\u3002 \u3000\u3000\u201c\u6740\u624b\u4e4b\u738b\uff0c\u679c\u7136\u96be\u7f20\u3002\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u5c0f\u675c\uff0c\u8fd9\u4e2a\u4eba\u7559\u4e0d\u5f97\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u5728\u4e0b\u4f1a\u5411\u603b\u6355\u5934\u9648\u660e\u5229\u5bb3\uff0c\u5fc5\u987b\u628a\u6740\u624b\u4e4b\u738b\uff0c\u7ef3\u4e4b\u4e8e\u6cd5\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5c4a\u65f6\uff0c \u8fd8\u8981\u4e09\u4f4d\u7f8e\u8a00\u652f\u6301\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u4f1a\u7559\u4e0b\u6765\u5e2e\u4f60\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u8840\u624b\u65b9\u8f6e\u662f\u4e2a\u5927\u5bb3\uff0c\u5982\u4e0d\u80fd\u65e9\u65e5\u9664\u53bb\uff0c\u4e09\u4e94\u5e74\u5185\uff0c \u8fd9\u6279\u6740\u624b\uff0c\u5c31\u53ef\u4fb5\u5165\u4e2d\u5dde\u3001\u957f\u5b89\u7684\u8f96\u5883\u3002\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u3001\u5c91\u5578\u864e\u8fde\u8fde\u70b9\u5934\uff0c\u4e5f\u7b97\u662f\u627f\u8bfa\u4e86\u7559\u4e0b\u6765\u5bf9\u4ed8\u65b9\u8f6e\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u7a81\u7136\u8f6c\u8eab\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u4eca\u5929\u4e0d\u7ecf\u8fc7\u4e00\u756a\u8840\u6218\uff0c\u53ea\u6015\u662f\u5f88\u96be\u79bb\u5f00\u8fd9\u5ea7 \u611a\u516c\u56ed\u6797\u4e86\uff1f\u201d \u3000\u3000\u7528\u4e0d\u7740\u518d\u4f5c\u89e3\u8bf4\uff0c\u6811\u6797\u4e0e\u6e20\u5cb8\u4e4b\u95f4\u4e00\u7247\u7a7a\u5730\u4e0a\uff0c\u5df2\u7136\u6446\u597d\u4e86\u516d\u7ec4\u9635\u52bf\uff0c\u6bcf\u7ec4\u4e09\u4eba\uff0c\u9886\u961f \u7684\u4eba\uff0c\u4e0d\u7528\u5434\u94a9\u5251\uff0c\u800c\u662f\u9752\u94dc\u6708\u8f6e\u3002 \u3000\u3000\u675c\u671b\u6708\u5fd6\u9053\uff1a\u6bcf\u4e00\u7ec4\u4eba\u4e2d\uff0c\u9886\u961f\u4e4b\u4eba\uff0c\u4e0d\u662f\u5434\u94a9\u5251\uff0c\u5c31\u662f\u9752\u94dc\u8f6e\uff0c\u4f3c\u662f\u4e13\u505a\u9501\u62ff\u5bf9\u624b\u7684 \u5175\u5203\u4e4b\u7528\uff0c\u4e00\u65e6\u5f97\u624b\uff0c\u4e24\u67c4\u624b\u6267\u9b3c\u5934\u5200\u7684\u526f\u624b\uff0c\u5f53\u6709\u5fc5\u6740\u7684\u653b\u52bf\uff0c\u90a3\u65e5\u7626\u897f\u6e56\u7554\u4e00\u6218\uff0c\u654c\u4eba \u672a\u80fd\u9501\u62ff\u7740\u81ea\u5df1\u7684\u5175\u5203\uff0c\u6240\u4ee5\uff0c\u6ca1\u6709\u53d1\u6325\uff0c\u8fd9\u4e00\u70b9\u4ea6\u662f\u9700\u5f97\u7559\u5fc3\u4e4b\u5904\u3002 \u3000\u3000\u9664\u4e86\u7a7f\u7740\u7070\u8863\uff0c\u5e03\u9635\u5f85\u654c\u7684\u516d\u7ec4\u5341\u516b\u4e2a\u4e4b\u5916\uff0c\u8fd8\u6709\u56db\u4e2a\u7a7f\u7740\u9ed1\u8863\u7684\u4eba\uff0c\u624b\u4e2d\u5175\u5203\u65e2\u975e\u5434 \u94a9\u5251\uff0c\u4e5f\u975e\u9752\u94dc\u8f6e\u3002 \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u4eba\uff0c\u5e74\u7eaa\u90fd\u5728\u56db\u5341\u5f00\u5916\uff0c\u7528\u7684\u90fd\u662f\u4e00\u822c\u7684\u5200\u5251\uff0c\u53ea\u6709\u4e00\u4e2a\u957f\u9aef\u4eba\uff0c\u7528\u7684\u662f\u4e00\u652f \u674e\u516c\u62d0\u3002 \u3000\u3000\u201c\u5c0f\u675c\uff0c\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u8fd9\u56db\u4e2a\u7a7f\u7740\u9ed1\u8863\u7684\u4eba\uff0c\u53c8\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5927\u6982\u662f\u6740\u624b\u4e2d\u7684\u6740\u624b\u5427\uff01\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u6280\u827a\u7cbe\u6e5b\uff0c\u53ef\u80fd\u5404\u6709\u6240\u957f\uff0c\u4e5f\u6709\u72ec\u81ea \u884c\u52a8\u7684\u80fd\u529b\u3002\u201d \u3000\u3000\u201c\u6740\u624b\u4e4b\u738b\u7684\u624b\u4e0b\uff0c\u7a76\u7adf\u6709\u591a\u5c11\u6740\u624b\uff1f\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u5171\u5206\u4e3a\u51e0\u4e2a\u7b49\u7ea7\uff1f\u201d \u3000\u3000\u201c\u5f88\u60ed\u6127\uff01\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5144\u5f1f\u5bf9\u4ed6\u7684\u4e86\u89e3\uff0c\u5b9e\u5728\u5f88\u5c11\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u56db\u4e2a\u9ed1\u8863\u4eba\u90fd\u6234\u4e86\u9762 \u5177\uff0c\u4e5f\u8bb8\u4ed6\u4eec\u662f\u6c5f\u5357\u7eff\u6797\u9053\u4e0a\u7684\u6709\u540d\u4eba\u7269\uff0c\u624d\u4e0d\u613f\u4ee5\u672c\u6765\u9762\u76ee\u89c1\u4eba\uff1f\u201d \u3000\u3000\u5434\u94c1\u5cf0\u70b9\u70b9\u5934\uff0c\u76ee\u5149\u4e00\u63a0\u56db\u4e2a\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u56db\u4f4d\u65e2\u7136\u4e0d\u613f\u4ee5\u771f\u6b63\u7684\u9762\u76ee\u89c1\u4eba\uff0c\u54b1\u4eec\u4e5f\u4e0d \u514d\u5f3a\uff0c\u4f46\u56db\u4f4d\u6b64\u6765\u7684\u7528\u5fc3\uff0c\u53ef\u5426\u8bf4\u4e2a\u660e\u767d\u5462\uff1f\u201d \u3000\u3000\u90a3\u624b\u6267\u674e\u516c\u62d0\u3001\u7559\u7740\u957f\u9aef\u7684\u9ed1\u8863\u4eba\uff0c\u9053\uff1a\u201c\u56db\u4f4d\u5927\u6355\u5934\uff0c\u4e0d\u4f46\u662f\u76ee\u529b\u8fc7\u4eba\uff0c\u800c\u4e14\u4e5f\u5f88\u5584 \u7b97\u8ba1\uff0c\u675c\u671b\u6708\u8bf4\u7684\u4e0d\u9519\uff0c\u4ed6\u4eec\u90fd\u6234\u4e86\u4eba\u76ae\u9762\u5177\uff0c\u4e5f\u662f\u6c5f\u5357\u9053\u4e0a\u5c0f\u6709\u540d\u6c14\u7684\u4eba\u7269\uff0c\u6b64\u756a\u8d76\u6765\uff0c \u81ea\u4e5f\u6709\u9886\u6559\u4e00\u4e0b\u56db\u4f4d\u540d\u6355\u6b66\u529f\u7684\u7528\u5fc3\u3002\u4e0d\u8fc7\uff0c\u6211\u4eec\u4e0d\u7528\u548c\u4f60\u4eec\u5355\u6253\u72ec\u6597\uff0c\u6211\u4eec\u662f\u5584\u4e8e\u5229\u7528\u81ea \u5df1\u4f18\u52bf\u7684\u4eba\uff0c\u56e0\u4e3a\uff0c\u6211\u4eec\u662f\u6740\u624b\uff0c\u4e0d\u8981\u865a\u540d\uff0c\u4e0d\u8bb2\u60c5\u9762\u2026\u2026\u201d \u3000\u3000\u675c\u671b\u6708\u5fc3\u5934\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u8fd9\u56db\u4e2a\u9ed1\u8863\u4eba\u7684\u6280\u827a\uff0c\u5e94\u8be5\u662f\u5f3a\u8fc7\u90a3\u4e9b\u7070\u8863\u4eba\u5f88\u591a\uff0c\u5982\u82e5\u548c\u90a3 \u6279\u7070\u8863\u4eba\uff0c\u914d\u5408\u7684\u5f88\u5b8c\u7f8e\uff0c\u5fc5\u5c06\u5a01\u529b\u500d\u589e\uff0c\u4eca\u65e5\u80fd\u4e0d\u80fd\u51b2\u51fa\u8fd9\u756a\u622a\u51fb\uff0c\u5c31\u5f88\u96be\u9884\u6599\u4e86\uff0c\u5fc5\u987b \u8981\u5728\u56db\u4eba\u8fd8\u672a\u878d\u5408\u4e4b\u524d\uff0c\u91cd\u521b\u4e86\u4ed6\u4eec\u3002 \u3000\u3000\u56db\u4eba\u4e4b\u4e2d\uff0c\u53ea\u6709\u4ed6\u675c\u671b\u6708\u7ecf\u5386\u8fc7\u7ec4\u5408\u9635\u52bf\u7684\u56f4\u653b\uff0c\u77e5\u5176\u5389\u5bb3\uff0c\u6b64\u523b\u65f6\u673a\u6025\u8feb\uff0c\u65e0\u6687\u8ff0\u8bf4 \u6e05\u695a\uff0c\u53ea\u597d\u5927\u58f0\u559d\u9053\uff1a\u201c\u60f3\u8981\u8054\u624b\u5408\u51fb\uff0c\u90a3\u5c31\u7559\u4f60\u4eec\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u559d\u58f0\u4e2d\uff0c\u4eba\u5251\u5408\u4e00\uff0c\u76f4\u5411\u5de6\u4fa7\u4e00\u4e2a\u9ed1\u8863\u4eba\u51b2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\uff0c\u4e2a\u4e2a\u5fc3\u601d\u7075\u654f\uff0c\u675c\u671b\u6708\u90a3\u58f0\u5927\u559d\uff0c\u5b9e\u5df2\u6697\u793a\u4e86\u56db\u4eba\u4e0d\u53ef\u7559\uff0c\u4e2a\u4e2d\u5fc5\u6709\u5185\u60c5\uff0c \u4e09\u5927\u6355\u5934\uff0c\u4e5f\u7acb\u523b\u53d1\u52a8\u5feb\u653b\uff0c\u62a2\u5728\u56db\u4e2a\u9ed1\u8863\u4eba\u4e4b\u524d\u51fa\u624b\u4e86\u3002 \u3000\u3000\u800c\u4e14\uff0c\u4e00\u51fa\u624b\u5c31\u7528\u51fa\u6740\u7740\u3002 \u3000\u3000\u5c91\u5578\u864e\u4e00\u6325\u65a9\u9a6c\u5200\uff0c\u51b2\u4e86\u4e0a\u53bb\uff0c\u4f46\u53f3\u624b\u5374\u6084\u65e0\u58f0\u606f\u5730\u6253\u51fa\u4e00\u67c4\u98de\u65a7\uff0c\u624b\u6cd5\u5341\u5206\u5947\u5f02\uff0c\u7adf \u662f\u6253\u5411\u8eab\u540e\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u76ee\u7779\u4e03\u715e\u6843\u82b1\u7634\uff0c\u5728\u6b64\u5730\u51fa\u73b0\uff0c\u5371\u9669\u610f\u8bc6\uff0c\u5341\u5206\u5f3a\u70c8\uff0c\u8f7b\u6613\u4e0d\u7528\u7684\u593a\u547d\u91d1\u9556\uff0c\u7adf \u81ea\u5148\u884c\u51fa\u624b\uff0c\u4e00\u9053\u91d1\u5149\uff0c\u76f4\u5411\u90a3\u624b\u6267\u674e\u516c\u62d0\u7684\u9ed1\u8863\u4eba\u6253\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u957f\u9aef\u9ed1\u8863\u4eba\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u96d5\u866b\u5c0f\u6280\uff0c\u4e5f\u6562\u5356\u5f04\u3002\u201d\u6325\u62d0\u51fb\u51fa\u3002 \u3000\u3000\u51fa\u624b\u4e4b\u5feb\uff0c\u8ba4\u4f4d\u4e4b\u51c6\uff0c\u5b9e\u5b9e\u4e00\u4f4d\u975e\u540c\u5c0f\u53ef\u7684\u9ad8\u624b\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u58f0\u91d1\u94c1\u4ea4\u9e23\uff0c\u94c1\u62d0\u6b63\u51fb\u5728\u98de\u6765\u7684\u91d1\u9556\u4e4b\u4e0a\uff0c\u751f\u751f\u628a\u91d1\u9556\u51fb\u4f5c\u4e24\u65ad\u3002 \u3000\u3000\u4f46\u90a3\u65ad\u4f5c\u4e24\u622a\u7684\u91d1\u9556\uff0c\u4e00\u622a\u843d\u5730\uff0c\u4e00\u622a\u5374\u66b4\u5c04\u51fa\u4e00\u70b9\u5bd2\u661f\uff0c\u6d41\u77e2\u4e00\u822c\u7684\u5c04\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u957f\u9aef\u9ed1\u8863\u4eba\u8fd8\u672a\u6765\u5f97\u53ca\u770b\u6e05\u695a\uff0c\u5bd2\u661f\u5df2\u5c04\u5165\u54bd\u5589\u3002 \u3000\u3000\u8fd9\u4e2a\u957f\u9aef\u9ed1\u8863\u4eba\u662f\u4e8c\u5341\u4e8c\u4e2a\u6740\u624b\u7684\u9996\u8111\uff0c\u4e00\u8eab\u6280\u827a\u7cbe\u6e5b\u975e\u51e1\uff0c\u53ef\u601c\u5c1a\u672a\u6709\u6240\u53d1\u6325\uff0c\u5c31\u6b7b \u5728\u4e86\u5434\u94c1\u5cf0\u7684\u593a\u547d\u91d1\u9556\u4e4b\u4e0b\u3002 \u3000\u3000\u4e2d\u5dde\u540d\u6355\uff0c\u53f7\u79f0\u8fce\u95e8\u4e09\u4e0d\u8fc7\uff0c\u90a3\u662f\u8bf4\u6ca1\u6709\u4eba\u80fd\u8eb2\u8fc7\u4ed6\u7684\u8fde\u73af\u4e09\u9556\uff0c\u9664\u4e86\u53d1\u9556\u6709\u529b\uff0c\u8ba4\u4f4d \u5f88\u51c6\u4e4b\u5916\uff0c\u9556\u4e2d\u6697\u85cf\u7384\u673a\uff0c\u624d\u662f\u593a\u547d\u8ffd\u9b42\u7684\u673a\u5de7\u3002 \u3000\u3000\u5c91\u5578\u864e\u7684\u65a9\u9a6c\u5200\u653b\u51fa\u4e09\u62db\uff0c\u8fce\u6218\u4ed6\u7684\u9ed1\u8863\u4eba\uff0c\u7528\u7684\u539a\u80cc\u5f00\u5c71\u5200\uff0c\u4e5f\u662f\u4e00\u628a\u91cd\u5175\u5203\uff0c\u6325\u5200 \u786c\u63a5\uff0c\u7adf\u548c\u5c91\u5578\u864e\u8fde\u62da\u4e09\u5200\u3002 \u3000\u3000\u91d1\u94c1\u4ea4\u9e23\u58f0\u4e2d\uff0c\u8fdb\u98de\u51fa\u8fde\u4e32\u706b\u661f\uff0c\u8fd9\u4e09\u5200\u662f\u786c\u6253\u786c\u63a5\uff0c\u5c91\u5578\u864e\u7adf\u7136\u672a\u53d6\u5f97\u534a\u70b9\u4f18\u52bf\uff0c\u5fc3 \u4e2d\u6697\u6697\u5403\u60ca\uff0c\u5fd6\u9053\uff1a\u8fd9\u4e9b\u4eba\u540d\u4e0d\u89c1\u7ecf\u4f20\uff0c\u529f\u529b\u786e\u662f\u4e0d\u5f31\u3002\u770b\u6765\uff0c\u8fd9\u6c5f\u5357\u9053\u4e0a\u7684\u9ed1\u9053\u4eba\u7269\uff0c\u6bd4 \u8d77\u5173\u4e1c\uff0c\u96be\u7f20\u591a\u4e86\u3002\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u4eba\u5374\u5411\u4e00\u4fa7\u9000\u53bb\u3002 \u3000\u3000\u76ee\u7779\u5c91\u5578\u864e\u7684\u653b\u52bf\u51cc\u5389\uff0c\u9ed1\u8863\u4eba\u5df2\u5168\u91c7\u5b88\u52bf\uff0c\u4f46\u5c91\u5578\u864e\u9000\u5411\u4e00\u4fa7\uff0c\u8bf1\u52a8\u4ed6\u6539\u91c7\u653b\u52bf\uff0c\u6325 \u5200\u4e00\u62db\u201c\u6cf0\u5c71\u538b\u9876\u201d\u5168\u529b\u5288\u4e0b\uff0c\u5e0c\u671b\u5728\u8fd9\u4e00\u62db\u4e2d\uff0c\u53d6\u5f97\u5148\u673a\u3002 \u3000\u3000\u5c91\u5578\u864e\u679c\u7136\u626c\u5200\u786c\u63a5\uff0c\u76ee\u5149\u5374\u770b\u5411\u8eab\u540e\u7a7a\u4e2d\u3002 \u3000\u3000\u5c31\u5728\u53cc\u5200\u5c06\u8981\u63a5\u5b9e\u4e4b\u9645\uff0c\u4e00\u56e2\u5bd2\u8292\uff0c\u7a81\u7136\u75be\u843d\u800c\u4e0b\uff0c\u8840\u8089\u6e85\u98de\u4e2d\uff0c\u5288\u53bb\u4e86\u9ed1\u8863\u4eba\u534a\u4e2a\u8111 \u888b\u3002 \u3000\u3000\u662f\u7684\uff0c\u98de\u65a7\u5c60\u72ee\u864e\uff0c\u5c91\u80e1\u5b50\u6295\u5411\u8eab\u540e\u7684\u4e00\u65a7\uff0c\u7ed5\u5411\u534a\u7a7a\uff0c\u5728\u4ed6\u8ba1\u7b97\u597d\u7684\u4f4d\u7f6e\uff0c\u5288\u65a9\u4e0b\u6765\uff0c \u6709\u5982\u5929\u5916\u6765\u4e91\uff0c\u5b8c\u5168\u51fa\u4eba\u610f\u6599\uff0c\u9ed1\u8863\u4eba\u867d\u7136\u4e00\u8eab\u4e0d\u9519\u7684\u6b66\u529f\uff0c\u5374\u4e5f\u62b1\u61be\u800c\u7ec8\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u7684\u201c\u4e00\u5251\u5316\u4e03\u661f\u201d\u523a\u5165\u4e86\u654c\u4eba\u7684\u54bd\u5589\uff0c\u4e8e\u627f\u5fd7\u7684\u79cb\u98ce\u5341\u516b\u65a9\uff0c\u7528\u5230\u4e86\u5341\u4e00\u65a9\uff0c\u624d \u65a9\u4e0b\u4e86\u5bf9\u65b9\u7684\u8111\u888b\u3002 \u3000\u3000\u80dc\u4e4b\u4e0d\u6613\u5440\uff01\u5168\u90fd\u7528\u51fa\u4e86\u538b\u7bb1\u5e95\u7684\u672c\u9886\u3002 \u3000\u3000\u4f46\u90a3\u6392\u6210\u4e86\u516d\u5ea7\u9635\u52bf\u7684\u6740\u624b\uff0c\u867d\u7136\u76ee\u51fb\u5df1\u65b9\u7684\u603b\u9886\u961f\u548c\u4e00\u7ea7\u6740\u624b\uff0c\u77ac\u606f\u95f4\uff0c\u88ab\u4eba\u65a9\u6740\u73b0 \u573a\uff0c\u4f46\u5374\u5168\u65e0\u60ca\u60e7\u4e4b\u8272\uff0c\u4f3c\u4e4e\u5bf9\u751f\u6b7b\u4e4b\u4e8b\uff0c\u5168\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u675c\u671b\u6708\u770b\u4e86\u770b\u56db\u5177\u5c38\u4f53\uff0c\u5bf9\u5434\u94c1\u5cf0\u548c\u5c91\u5578\u864e\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u593a\u547d\u91d1\u9556\u3001\u8ffd\u9b42\u98de\u65a7\uff0c\u679c\u7136 \u662f\u540d\u4e0d\u865a\u4f20\uff0c\u4e00\u6325\u624b\uff0c\u5c31\u53d6\u654c\u6027\u547d\uff0c\u4f69\u670d\u554a\uff0c\u4f69\u670d\uff01\u201d \u3000\u3000\u201c\u5c11\u7ed9\u6211\u8ddf\u5c91\u80e1\u5b50\u8138\u4e0a\u8d34\u91d1\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6211\u4eec\u662f\u4ee5\u5de7\u53d6\u80dc\uff0c\u54ea\u80fd\u6bd4\u4e24\u4f4d\uff0c\u771f\u5200\u771f\u67aa \u7684\u62da\u6740\uff0c\u4e5f\u4e0d\u8fc7\u4e09\u4e94\u62db\uff0c\u5c31\u53d6\u654c\u6027\u547d\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e9b\u4eba\uff0c\u4e0d\u80fd\u7559\uff0c\u4f46\u6211\u8fd8\u662f\u672a\u6599\u5230\u4ed6\u4eec\u7684\u6280\u827a\u5982\u6b64\u4e4b\u5f3a\uff0c\u770b\u4f3c\u6740\u7684\u8f7b\u677e\uff0c\u5b9e\u5219\u675c\u67d0\u4eba \u5df2\u7528\u51fa\u4e86\u6700\u5389\u5bb3\u7684\u5251\u62db\uff0c\u5509\uff01\u5982\u82e5\u8ba9\u8fd9\u56db\u4e2a\u4eba\uff0c\u878d\u5165\u90a3\u4e09\u4eba\u7ec4\u6210\u7684\u5408\u51fb\u4e4b\u9635\uff0c\u4eca\u65e5\u80fd\u5426\u5168\u8eab \u800c\u9000\uff0c\u5c31\u5f88\u96be\u8bf4\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u770b\u5c06\u8d77\u6765\uff0c\u6740\u624b\u4e4b\u738b\uff0c\u4f3c\u5df2\u7ecf\u51fa\u52a8\u5168\u529b\u5bf9\u4ed8\u6211\u4eec\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e9b\u9ed1\u8863\u4eba\uff0c\u6b66\u529f\u786e\u5b9e\u4e0d\u9519\uff0c\u771f\u8981\u662f\u6253\u8d77\u6765\uff0c\u6050\u8981\u4e24\u767e\u62db\u4ee5\u4e0a\uff0c\u624d\u80fd\u5206\u51fa\u80dc\u8d1f\u3002\u201d\u5c91 \u5578\u864e\u9053\uff1a\u201c\u4f46\u4ed6\u4eec\u65e0\u540d\u65e0\u59d3\uff0c\u4e0d\u89c1\u7ecf\u4f20\uff0c\u60f3\u4e00\u60f3\u5341\u5206\u53ef\u6015\u5440\uff01\u2026\u2026\u201d \u3000\u3000\u201c\u5c91\u5144\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u7b49\u4f60\u548c\u8fd9\u4e9b\u6740\u624b\u7ec4\u6210\u7684\u9635\u52bf\uff0c\u4ea4\u8fc7\u624b\uff0c\u4f60\u624d\u771f\u6b63\u77e5\u9053\u4ed6\u4eec\u7684\u53ef \u6015\uff0c\u5e0c\u671b\u4f60\u7684\u98de\u65a7\u7edd\u6280\uff0c\u80fd\u5bf9\u4ed6\u9020\u6210\u4e25\u91cd\u7684\u5a01\u80c1\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u8fd9\u4e9b\u9635\u52bf\uff0c\u6bd4\u9ed1\u8863\u4eba\u8fd8\u96be\u5bf9\u4ed8\u4e86\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u7684\u4e2a\u4eba\u6280\u827a\u4e0d\u582a\u4e00\u51fb\uff0c\u4e24\u4e2a\u5408\u4f5c\uff0c\u4e5f\u4e0d\u662f\u6211\u4e09\u62db\u4e4b\u654c\uff0c\u4f46\u5982\u4e09\u4e2a\u4eba\u5408\u4e8e\u4e00\u5904\uff0c\u4ea7 \u751f\u51fa\u7684\u5f3a\u5927\u529b\u9053\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5374\u53c8\u975e\u6211\u6240\u80fd\u53ca\u4e86\u3002\u201d \u3000\u3000\u201c\u600e\u4f1a\u5982\u6b64\uff1f\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u5f88\u96be\u4f7f\u4eba\u76f8\u4fe1\u5566\uff01\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4e2a\u4e2d\u9053\u7406\uff0c\u6211\u4e5f\u60f3\u4e0d\u660e\u767d\uff0c\u8fd8\u8981\u5c91\u5144\u7b49\u4e09\u4f4d\u591a\u6307\u70b9\u6307\u70b9\u3002\u201d \u3000\u3000\u201c\u5c0f\u675c\uff0c\u4f60\u7684\u610f\u601d\u662f\u8981\u6211\u5c91\u80e1\u5b50\u8bd5\u8bd5\u4e86\uff1f\u201d \u3000\u3000\u201c\u5e0c\u671b\u5434\u5144\u548c\u4e8e\u5144\uff0c\u4e5f\u7ecf\u5386\u4e00\u756a\uff0c\u624d\u4f1a\u5370\u8c61\u6df1\u523b\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u7136\u540e\uff0c\u54b1\u4eec\u518d\u7814\u5546\uff0c \u770b\u770b\u80fd\u4e0d\u80fd\u627e\u51fa\u4e00\u4e2a\u65b9\u6cd5\u7834\u9664\u8fd9\u7b49\u4e09\u4eba\u5408\u51fb\u4e4b\u672f\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u76ee\u5149\u4e00\u63a0\u5434\u94c1\u5cf0\u3001\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u8001\u5434\u3001\u5c0f\u4e8e\uff0c\u4f60\u4eec\u542c\u5230\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u542c\u5f97\u5f88\u6e05\u695a\uff01\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6211\u5728\u52a8\u624b\u4e4b\u524d\uff0c\u65e0\u51fb\u4f24\u4ed6\u4eec\u4e00\u4e2a\uff0c\u4e09\u4eba\u5408\u51fb\u4e4b\u9635\uff0c\u5c31\u4e0d \u653b\u81ea\u7834\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u5012\u662f\u4e2a\u5f88\u7b80\u5355\u7684\u65b9\u6cd5\uff0c\u60f3\u6765\u4e5f\u5f88\u6709\u6548\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8981\u5728\u9635\u52bf\u8fd8\u672a\u5e03\u6210\u4e4b \u524d\uff0c\u4e00\u65e6\u6210\u5f62\uff0c\u4e24\u5200\u4e00\u8f6e\uff0c\u7ed3\u5408\u51fa\u624b\uff0c\u90a3\u5c31\u5a01\u529b\u65e0\u7a77\u4e86\u3002\u201d \u3000\u3000\u4e09\u4eba\u8c08\u8bdd\uff0c\u58f0\u97f3\u4e0d\u4f4e\uff0c\u90a3\u4e9b\u7070\u8863\u4eba\u5e94\u8be5\u53ef\u4ee5\u542c\u5230\uff0c\u4f46\u7ec6\u770b\u4ed6\u4eec\u7684\u795e\u60c5\uff0c\u4f3c\u662f\u6839\u672c\u6ca1\u6709\u5728 \u542c\u56db\u4eba\u7684\u8c08\u8bdd\uff0c\u5bf9\u540c\u4f34\u4e4b\u6b7b\uff0c\u4e5f\u611f\u89c9\u4e0d\u51fa\u6709\u6240\u54c0\u4f24\uff0c\u4f3c\u4e4e\u662f\u6d1e\u7a7f\u4e86\u751f\u6b7b\u7684\u7384\u79d8\uff0c\u5168\u65e0\u754f\u60e7\u548c \u4e0d\u5b89\u4e4b\u611f\u3002 \u3000\u3000\u201c\u8fd9\u4e9b\u4eba\u90fd\u5f88\u51b7\u9177\uff0c\u662f\u4e00\u7fa4\u65a9\u60c5\u706d\u6027\uff0c\u65ad\u4e49\u7edd\u4eb2\u7684\u4eba\u7269\uff0c\u8840\u624b\u65b9\u8f6e\uff0c\u80fd\u628a\u4ed6\u4eec\u8bad\u7ec3\u5230\u5982 \u6b64\u5883\u754c\uff0c\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u662f\u4e00\u4f4d\u5f88\u53ef\u6015\u7684\u4eba\u7269\u4e86\u3002\u201d \u3000\u3000\u201c\u68a6\u5e7b\u4e4b\u5200\uff0c\u4efd\u91cf\u592a\u91cd\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u63a9\u906e\u4e86\u8840\u624b\u65b9\u8f6e\u548c\u56db\u51e4\u697c\u4e3b\u7684\u6076\u540d\u2026\u2026\u201d \u3000\u3000\u201c\u6162\u6162\u6162\uff0c\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u8fd8\u6709\u4e2a\u56db\u51e4\u697c\u4e3b\uff0c\u53c8\u662f\u4e2a\u4ec0\u4e48\u6837\u5b50\u7684\u4eba\u7269\uff1f\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u4e0d\u5982\u8840\u624b\u65b9\u8f6e\u7684\u58f0\u540d\u54cd\u4eae\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4f46\u9634\u9669\u3001\u51f6\u6076\u6050\u72b9\u9047\u4e4b\uff0c\u5f53\u4e16\u4e09\u5927\u795e \u533b\u4e4b\u4e00\uff0c\u51b7\u9762\u795e\u533b\u8c2d\u6267\u4e2d\uff0c\u53ef\u80fd\u5c31\u88ab\u4ed6\u6240\u5bb3\u3002\u60dc\u7389\u4e3a\u62a5\u7236\u4ec7\uff0c\u6295\u5165\u4e86\u56db\u51e4\u697c\u4e0b\uff0c\u8fd1\u4e00\u5e74\u5149\u666f\uff0c \u7adf\u7136\u672a\u89c1\u5230\u56db\u51e4\u697c\u4e3b\u2026\u2026\u201d \u3000\u3000\u201c\u4e5f\u662f\u4e2a\u884c\u8e2a\u8be1\u79d8\uff0c\u540d\u73b0\u4eba\u9690\u7684\u8be1\u79d8\u4eba\u7269\u4e86\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u60dc\u7389\u6df7\u5165\u56db\u51e4\u697c\u4e2d\u751a\u4e45\uff0c \u603b\u4fa6\u77e5\u4e00\u70b9\u86db\u4e1d\u9a6c\u8ff9\u4e86\u3002\u201d\u4e00\u9762\u95ee\u8bdd\uff0c\u4e00\u9762\u76ee\u5149\u5fae\u5fae\u8f6c\u52a8\uff0c\u770b\u516d\u7ec4\u6740\u624b\uff0c\u7adf\u7136\u4e5f\u5728\u4f4e\u58f0\u4ea4\u8c08\uff0c \u5b8c\u5168\u4e0d\u62c5\u5fc3\u56db\u4eba\u9003\u8d70\u7684\u4e8b\u3002\u8fd9\u662f\u4ece\u672a\u9047\u8fc7\u7684\u4e8b\uff0c\u5434\u94c1\u5cf0\u5fc3\u4e2d\u7eb3\u95f7\uff0c\u4e5f\u6709\u4e9b\u60f3\u4e0d\u901a\uff0c\u8fd9\u4e9b\u4eba\u662f \u767d\u75f4\uff0c\u8fd8\u662f\u75af\u5b50\uff1f\u82b1\u4e86\u4e0d\u5c11\u65f6\u95f4\uff0c\u7b97\u8ba1\u3001\u57cb\u4f0f\uff0c\u4e00\u65e6\u654c\u6211\u76f8\u5bf9\u65f6\uff0c\u5374\u4e0d\u80af\u884c\u52a8\uff0c\u770b\u770b\u540c\u6765\u7684 \u56db\u5927\u9ad8\u624b\u88ab\u6740\uff0c\u4e5f\u4e0d\u4e88\u63f4\u624b\uff0c\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\u554a\uff01 \u3000\u3000\u4ed6\u4eec\u662f\u5728\u4e89\u53d6\u65f6\u95f4\uff0c\u4ee5\u5f85\u540e\u63f4\uff0c\u8fd8\u662f\u7b49\u5f85\u7740\u4e00\u4e2a\u7279\u5b9a\u8baf\u53f7\uff0c\u518d\u5c55\u5f00\u653b\u52bf\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u5fc3\u4e2d\u7591\u8651\uff0c\u4e5f\u6b63\u662f\u4e1c\u3001\u897f\u3001\u5357\uff0c\u4e09\u5927\u6355\u5934\u5fc3\u4e2d\u7684\u7591\u8651\u3002\u4f46\u76ee\u524d\u7684\u60c5\u51b5\uff0c\u5bf9\u56db\u5927\u6355 \u5934\u65e0\u5bb3\uff0c\u4ed6\u4eec\u90fd\u5bf9\u81ea\u8eab\u6709\u7740\u5145\u5206\u4fe1\u5fc3\u3002\u5c31\u7b97\u4e0d\u80fd\u4e00\u4e3e\u5236\u654c\uff0c\u4f46\u64a4\u8d70\u80fd\u529b\uff0c\u7edd\u5bf9\u53ef\u4ee5\u3002\u4f55\u51b5\uff0c \u8fd8\u662f\u56db\u4eba\u76f8\u4e92\u63a9\u62a4\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u56db\u4f4d\u5927\u6355\u5934\u4e5f\u4e0d\u6025\u3002 \u3000\u3000\u201c\u8fd9\u4e9b\u6740\u624b\uff0c\u53ef\u80fd\u548c\u56db\u51e4\u697c\u4e3b\uff0c\u4e5f\u6709\u4e00\u4e9b\u5173\u7cfb\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u4e0a\u6b21\u5728\u7626\u897f\u6e56\u5904 \u7684\u622a\u6740\uff0c\u662f\u53d7\u4e86\u56db\u51e4\u697c\u4e3b\u7684\u9080\u7ea6\u3002\u53ea\u662f\u65e0\u6cd5\u77e5\u6653\u4ed6\u4eec\u7684\u5173\u7cfb\uff0c\u662f\u96c7\u4e3b\u4e0e\u96c7\u5ba2\uff0c\u6216\u662f\u5408\u4f5c\u4f19 \u4f34\uff1f\u201d \u3000\u3000\u201c\u8fd9\u90fd\u4e0d\u91cd\u8981\u4e86\uff0c\u201d\u5434\u94c1\u5cf0\u62ac\u5934\u671b\u671b\u5929\u8272\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u54b1\u4eec\u8c08\u8c08\u7834\u9635\u4e4b\u6cd5\u3002\u5c0f\u675c\uff0c\u4f60 \u548c\u4ed6\u4eec\u4ea4\u8fc7\u624b\uff0c\u4f60\u5148\u8bf4\u3002\u201d \u3000\u3000\u201c\u6ca1\u6709\u7834\u9635\u4e4b\u6cd5\uff01\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u6839\u672c\u770b\u4e0d\u51fa\u9635\u52bf\u5982\u4f55\u53d8\u5316\uff0c\u4e09\u4e2a\u4eba\u5c31\u653b\u4e86\u4e0a\u6765\uff0c\u4e24 \u5200\u4e00\u8f6e\u5408\u6210\u4e00\u56e2\u5149\u5e55\uff0c\u4e5f\u65e0\u6cd5\u5206\u8fa8\u51fa\u5175\u5203\u5f62\u72b6\u2026\u2026\u201d \u3000\u3000\u201c\u7ed3\u5408\u7684\u5982\u6b64\u4e25\u5bc6\uff0c\u5012\u662f\u5c11\u6240\u542c\u95fb\uff0c\u201d\u5434\u94c1\u5cf0\u63a5\u9053\uff1a\u201c\u4f46\u770b\u4e0a\u53bb\u8fd9\u4e9b\u4eba\u5374\u4e0d\u50cf\u64c5\u53d8\u591a\u5de7 \u7684\u4eba\uff0c\u600e\u4e48\u7ec3\u6210\u8fd9\u7b49\u5947\u5de7\u53d8\u5316\u7684\u6b66\u529f\uff1f\u201d \u3000\u3000\u201c\u5434\u5144\uff0c\u4e00\u8a00\u9053\u7834\u4e2a\u4e2d\u79d8\u5bc6\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u5df2\u60f3\u4e86\u5f88\u4e45\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5374\u60f3\u4e0d\u901a\u673a\u5de7\u4f55 \u5728\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u4e0d\u7528\u60f3\u4e86\uff0c\u6240\u8c13\u611a\u8005\u5343\u5931\u5fc5\u6709\u4e00\u5f97\uff0c\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u8fd8\u662f\u5148\u8bd5\u8bd5\u4ed6\u4eec\u7684\u6280\u827a\u6210\u5c31\uff0c \u4e5f\u8bb8\u90a3\u4e00\u65e5\uff0c\u4f60\u9047\u4e0a\u7684\u662f\u4ed6\u771f\u6b63\u7684\u9ad8\u624b\uff0c\u4f46\u8fd9\u4e9b\u4eba\uff0c\u5374\u53c8\u4e0d\u662f\u3002\u201d \u3000\u3000\u663e\u7136\uff0c\u4ed6\u5bf9\u675c\u671b\u6708\u65e0\u6cd5\u81ea\u5706\u5176\u8bf4\u7684\u8bdd\uff0c\u6709\u4e9b\u4e0d\u4fe1\uff0c\u4f46\u53c8\u4e0d\u4fbf\u9762\u65a5\u675c\u671b\u6708\u80e1\u8bf4\u516b\u9053\uff0c\u8bdd\u867d \u8bf4\u7684\u5a49\u8f6c\uff0c\u4f46\u7528\u610f\u5df2\u660e\u3002 \u3000\u3000\u675c\u671b\u6708\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u662f\uff0c\u5c91\u5144\u5148\u884c\u51fa\u624b\u3002\u201d\u5fc3\u4e2d\u5374\u88ab\u611a\u8005\u5fc5\u6709\u4e00\u5f97\u7684\u4e00\u53e5\uff0c\u89e6\u52a8 \u4e86\u7075\u673a\u3002 \u3000\u3000\u4f46\u89c1\u5c91\u80e1\u5b50\u56db\u5c3a\u516b\u5bf8\u7684\u65a9\u9a6c\u5200\uff0c\u6709\u5982\u4e00\u9053\u51b7\u950b\u626b\u53bb\uff0c\u631f\u7740\u7834\u7a7a\u91d1\u98ce\u3002 \u3000\u3000\u8fd9\u4e00\u5200\uff0c\u4ed6\u84c4\u52bf\u800c\u53bb\uff0c\u7528\u51fa\u4e86\u516b\u6210\u529f\u529b\uff0c\u5a01\u52bf\u975e\u540c\u5c0f\u53ef\uff0c\u770b\u8fd9\u4e00\u5200\u7684\u5a01\u52bf\uff0c\u4e00\u4e0b\u5b50\u628a\u4e09 \u4e2a\u4eba\u4e00\u9f50\u8170\u65a9\uff0c\u5e76\u975e\u96be\u4e8b\u3002 \u3000\u3000\u4f46\u89c1\u5149\u5f71\u8ff7\u79bb\uff0c\u4e00\u8f6e\u53cc\u5200\uff0c\u4e00\u8d77\u98de\u52a8\uff0c\u5df2\u77a7\u4e0d\u51fa\u5200\u4e0e\u8f6e\u7684\u5206\u9645\uff0c\u5175\u5203\u4e0e\u5175\u5203\u95f4\u7684\u7ed3\u5408\u4e4b \u5bc6\uff0c\u6c5f\u6e56\u4e0a\u751a\u5c11\u89c1\u95fb\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u98de\u51fa\u4e86\u4e00\u4e32\u706b\u661f\uff0c\u5c91\u5578\u864e\u7adf\u89c9\u624b\u81c2\u53d1\u9ebb\uff0c\u624b\u4e2d\u7684\u65a9\u9a6c\u5200\uff0c\u4e5f\u51e0 \u4e4e\u88ab\u9707\u8131\u624b\u3002 \u3000\u3000\u8fd9\u53ef\u662f\u4ece\u672a\u6709\u8fc7\u7684\u4e8b\uff0c\u4ed6\u4e00\u5411\u81ea\u8d1f\u529b\u5927\u5200\u91cd\uff0c\u5f53\u4e16\u82f1\u96c4\u6562\u548c\u4ed6\u786c\u63a5\u786c\u78b0\u7684\uff0c\u6781\u5c11\u9047\u5230\uff0c \u60f3\u4e0d\u5230\u8fd9\u4e09\u4e2a\u5168\u4e0d\u8d77\u773c\u7684\u4eba\uff0c\u5408\u624b\u4e00\u51fb\u7684\u53cd\u9707\u4e4b\u529b\uff0c\u7adf\u7136\u51e0\u4e4e\u4f7f\u4ed6\u7684\u5175\u5203\u8131\u624b\uff0c\u5fc3\u4e2d\u9707\u60ca\u6781 \u4e86\u3002 \u3000\u3000\u5fc3\u4e2d\u60ca\u7591\u672a\u5b9a\uff0c\u8f6e\u5200\u5408\u624b\u7684\u653b\u52bf\uff0c\u5df2\u5f53\u5934\u7f69\u4e0b\u3002 \u3000\u3000\u5c91\u5578\u864e\u5fc3\u4e2d\u867d\u5df2\u627f\u8ba4\uff0c\u4e09\u4eba\u7684\u5408\u51fb\u4e4b\u672f\uff0c\u6709\u4e9b\u602a\u5f02\uff0c\u4f46\u5fc3\u4e2d\u5b9e\u672a\u771f\u670d\uff0c\u9632\u536b\u7684\u529b\u91cf\uff0c\u5f3a \u5927\u8fc7\u4eba\uff0c\u4f46\u653b\u51fb\u529b\u662f\u5426\u4e5f\u5f88\u5f3a\u5927\u5462\uff1f \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u4eba\u5df2\u4e3e\u5200\u5411\u4e0a\u8fce\u53bb\u3002 \u3000\u3000\u6709\u8fc7\u4e00\u6b21\u6559\u8bad\uff0c\u8fd9\u6b21\u4e0d\u6562\u5168\u529b\u65bd\u4e3a\uff0c\u7528\u4e86\u4e03\u6210\u52b2\u529b\u3002 \u3000\u3000\u4f46\u4ecd\u88ab\u9707\u9000\u4e86\u4e94\u6b65\uff0c\u624d\u7ad9\u7a33\u53cc\u8db3\u3002 \u3000\u3000\u5e78\u662f\u65e9\u6709\u9632\u5907\uff0c\u4eba\u672a\u53d7\u5230\u4f24\u5bb3\uff0c\u672a\u7528\u5168\u529b\uff0c\u4f46\u5fc3\u4e2d\u5df2\u5b8c\u5168\u660e\u767d\uff0c\u4e0d\u4fe1\u90aa\u662f\u4e0d\u884c\u4e86\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\u5df2\u4e0d\u7528\u5c91\u5578\u864e\u89e3\u8bf4\u4ec0\u4e48\uff0c\u4ee5\u5434\u94c1\u5cf0\u548c\u4e8e\u627f\u5fd7\u7684\u6c5f\u6e56\u9605\u5386\uff0c\u770b\u7684\u5b8c\u5168\u660e\u767d\u4e86\u3002 \u3000\u3000\u201c\u662f\u4e0d\u662f\u548c\u5c0f\u675c\u8bf4\u7684\u4e00\u6837\uff1f\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u8fd9\u4e2d\u95f4\u6709\u8e4a\u8df7\uff0c\u4e0d\u53ef\u529b\u654c\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u5df2\u75be\u9000\u4e94\u6b65\uff0c\u5434\u94c1\u5cf0\u4e5f\u4eae\u51fa\u4e86\u6587\u660c\u7b14\uff0c\u4e8e\u627f\u5fd7\u4e5f\u6a2a\u5200\u800c\u7acb\uff0c\u4e09\u4e2a\u4eba\u7ad9\u6210\u4e00\u6392\uff0c\u4f3c \u662f\u8981\u51c6\u5907\u5408\u529b\u5e94\u654c\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u672c\u60f3\u963b\u6b62\u4e09\u4eba\uff0c\u53e6\u8c0b\u5bf9\u654c\u4e4b\u6cd5\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u8fd8\u672a\u66fe\u6709\u8fc7\u5408\u624b\u7684\u7ecf\u9a8c\uff0c\u4e09\u4eba\u6b66\u529f \u9ad8\uff0c\u5e94\u8be5\u4e0d\u4f1a\u6709\u4ec0\u4e48\u5371\u9669\u624d\u5bf9\uff0c\u5c31\u9000\u5230\u4e00\u4fa7\uff0c\u5168\u795e\u89c2\u6218\u4e86\uff0c\u5e0c\u671b\u80fd\u5728\u8fd9\u4e00\u62db\u4ea4\u63a5\u4e2d\uff0c\u770b\u51fa\u4e00 \u70b9\u86db\u4e1d\u9a6c\u8ff9\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u58f0\u5927\u543c\uff0c\u4e09\u4e2a\u5175\u5203\u5408\u6210\u4e00\u56e2\u5149\u5e55\uff0c\u5df2\u51b2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4e09\u5927\u540d\u6355\u66f4\u662f\u52a8\u5982\u95ea\u7535\uff0c\u53cc\u5200\u548c\u6587\u660c\u7b14\u5404\u81ea\u51dd\u805a\u6210\u4e00\u70b9\u5bd2\u8292\uff0c\u96c6\u4e2d\u5343\u94a7\u4e4b\u529b\u4e8e\u4e00\u70b9\uff0c\u70b9 \u5411\u653b\u6765\u7684\u5149\u5e55\uff0c\u4e09\u4e2a\u7070\u8863\u7684\u5175\u5203\u7ed3\u5408\u4e4b\u5bc6\uff0c\u65e9\u5df2\u65e0\u6cd5\u770b\u51fa\uff0c\u662f\u5200\u662f\u8f6e\u3002 \u3000\u3000\u4f46\u4e09\u5927\u540d\u6355\u7684\u5175\u5203\uff0c\u7740\u529b\u70b9\u5206\u4f5c\u4e09\u5904\uff0c\u5206\u6563\u4e86\u4e09\u4eba\u51dd\u4e8e\u4e00\u7684\u52b2\u9053\u3002 \u3000\u3000\u91d1\u94c1\u4ea4\u9e23\u58f0\u4e2d\uff0c\u7adf\u7136\u5e73\u5206\u79cb\u8272\u3002 \u3000\u3000\u201c\u770b\u51fa\u95e8\u9053\u4e86\uff01\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5982\u6709\u591a\u4ef6\u5175\u5203\u540c\u65f6\u63a5\u89e6\uff0c\u4ed6\u4eec\u8fd8\u65e0\u80fd\u628a\u5175\u5203\u51dd\u7ed3\u7684\u529b\u9053 \u5206\u5f00\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4ed6\u4eec\u6709\u516d\u7ec4\u4eba\u624b\uff0c\u6211\u4eec\u53ea\u6709\u56db\u4e2a\u4eba\uff0c\u4ed6\u4eec\u53ef\u4ee5\u5206\u2026\u2026\u201d \u3000\u3000\u5206\u4ec0\u4e48\uff1f\u5434\u94c1\u5cf0\u672a\u518d\u8bf4\u4e0b\u53bb\uff0c\u751f\u6050\u9732\u51fa\u53e3\u98ce\uff0c\u53cd\u800c\u63d0\u9192\u4e86\u8fd9\u4e9b\u4eba\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u6700\u5927\u7684\u7f3a\u70b9\uff0c\u5c31\u662f\u53cd\u5e94\u4e0d\u591f\u7075\u654f\uff0c\u4f46\u667a\u8005\u6709\u6240\u5931\uff0c\u611a\u8005\u6709\u6240\u5f97\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a \u201c\u8fd9\u4e5f\u53ef\u80fd\u662f\u4ed6\u6781\u5feb\u901f\u7ec3\u6210\u8fd9\u79cd\u6b66\u529f\u7684\u539f\u56e0\u3002\u8fd9\u79cd\u4eba\u904d\u5730\u7686\u662f\uff0c\u5f88\u597d\u7f57\u81f4\uff0c\u6240\u4ee5\uff0c\u6740\u624b\u4e4b\u738b\uff0c \u4e5f\u4e0d\u5728\u4e4e\u4ed6\u4eec\u7684\u6b7b\u4f24\u4e86\u3002\u201d \u3000\u3000\u201c\u6740\u624b\u6613\u5f97\uff0c\u4fef\u4ef0\u7686\u662f\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u53ef\u6015\u5440\uff01\u53ef\u6015\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\uff0c\u5148\u8981\u5b89\u6392\u4e2a\u5bf9\u654c\u4e4b\u6cd5\uff0c\u6211\u4eec\u4e09\u4e2a\u4eba\u8054\u624b\u53ef\u6321\u4e00\u51fb\u3002\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u4e0d\u77e5\u4e24\u4e2a\u4eba\uff0c \u662f\u5426\u4e5f\u6709\u8fd9\u79cd\u80fd\u529b\uff1f\u201d \u3000\u3000\u201c\u5982\u6709\u9700\u8981\uff0c\u4e0d\u59a8\u4e00\u8bd5\u3002\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u4f46\u53e6\u5916\u4e24\u4f4d\u5982\u4f55\u4f5c\u4e3a\uff0c\u8981\u7cbe\u5bc6\u8ba1\u5212\uff0c\u52a1\u6c42\u4e00\u51fb \u6210\u529f\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e5f\u4e0d\u80fd\u8981\u6c42\u592a\u591a\u4e86\uff0c\u5404\u4eba\u5c3d\u5fc3\u5427\uff01\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5434\u5144\u3001\u5c91\u5144\u3001\u675c\u67d0\u4e09\u4eba\u5408\u529b\u63a5\u4e0b \u4e00\u51fb\uff0c\u4e8e\u5144\u4ee5\u5feb\u5200\u6025\u653b\u4e4b\uff0c\u627e\u4e2a\u4e0b\u624b\u5904\uff0c\u5c3d\u5168\u529b\u8fd0\u5200\u4f24\u654c\uff0c\u4e0d\u7528\u987e\u53ca\u6211\u4eec\u4e09\u4eba\u3002\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u70b9\u70b9\u5934\uff0c\u7a81\u7136\u5411\u540e\u9000\u4e86\u4e94\u6b65\u3002 \u3000\u3000\u8fd9\u4e94\u6b65\u540e\u9000\uff0c\u5374\u5f15\u52a8\u4e86\u53e6\u51e0\u7ec4\u4eba\u7684\u6000\u7591\u3002\u7a81\u7136\u95f4\u4e5f\u5f00\u59cb\u884c\u52a8\uff0c\u5f00\u59cb\u5411\u4e0a\u56f4\u62e2\uff0c\u4f3c\u662f\u51c6\u5907 \u51fa\u624b\u5408\u51fb\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u51e0\u4e2a\u9635\u52bf\u5408\u62e2\u8d77\u6765\u3002\u201d\u5f53\u5148\u53d1\u52a8\uff0c\u4e03\u661f\u5251\u4e00\u62db\u201c\u8fce \u98ce\u65ad\u8349\u201d\u65a9\u5411\u4e00\u4e2a\u7070\u8863\u4eba\u3002 \u3000\u3000\u4ed6\u60c5\u6025\u51fa\u5251\uff0c\u4e5f\u6ca1\u6709\u7279\u5b9a\u7684\u76ee\u6807\uff0c\u4f46\u662f\u51fa\u62db\u8fc5\u8fa3\uff0c\u6709\u5982\u7535\u5149\u77f3\u706b\u3002\u4f46\u95fb\u4e00\u58f0\u60e8\u53eb\uff0c\u4e00\u4e2a \u7070\u8863\u4eba\u53f3\u81c2\u65a9\u65ad\uff0c\u75db\u7684\u5927\u58f0\u547c\u53eb\uff0c\u9b3c\u5934\u5200\u8dcc\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u53cd\u5e94\u8fdf\u949d\u4e00\u4e9b\uff0c\u4f46\u8fd8\u662f\u77e5\u9053\u75bc\u75db\u3002\u201d\u5434\u94c1\u5cf0\u6587\u660c\u7b14\u4e00\u62db\u201c\u76f4\u6363\u9ec4\u9f99\u201d\u523a\u5165\u4e00\u4e2a\u5927 \u6c49\u524d\u80f8\u3002 \u3000\u3000\u8fd9\u53ef\u662f\u81f4\u547d\u5730\u65b9\uff0c\u5927\u6c49\u7acb\u523b\u5012\u4e86\u4e0b\u53bb\uff0c\u90a3\u5927\u6c49\u624b\u4e2d\u63e1\u7740\u4e00\u652f\u9752\u94dc\u94a2\u8f6e\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u4e00\u6536\u6587\u660c\u7b14\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u5c0f\u675c\u8bf4\u7684\u5bf9\uff0c\u4ed6\u4eec\u6ca1\u6709\u5408\u56f4\u4e4b\u524d\uff0c\u975e\u5e38\u7684\u8106\u5f31\uff0c\u4e00\u51fb \u53ef\u6740\uff0c\u5408\u56f4\u4e4b\u540e\uff0c\u5374\u53c8\u5f3a\u5927\u65e0\u6bd4\uff0c\u8fd9\u4e2d\u95f4\uff0c\u53ea\u6709\u5728\u4ed6\u4eec\u5408\u56f4\u51fa\u624b\u4e4b\u524d\uff0c\u975e\u5e38\u77ed\u6682\u7684\u4e00\u5239\u90a3\u95ee \u51fa\u624b\u4f24\u654c\u2026\u2026\u201d \u3000\u3000\u4ed6\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u6587\u660c\u7b14\u5de6\u70b9\u53f3\u523a\uff0c\u53c8\u4f24\u4e86\u4e24\u4e2a\u7070\u8863\u5927\u6c49\u3002 \u3000\u3000\u675c\u671b\u6708\u4e5f\u523a\u4f24\u4e86\u4e24\u4e2a\u4eba\uff0c\u4ed6\u4f24\u4eba\u7684\u624b\u6bb5\u5f88\u6709\u5fc3\u673a\uff0c\u4e24\u4e2a\u4eba\u5206\u5c5e\u4e24\u7ec4\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4e8e\u627f\u5fd7\u3001\u5c91\u5578\u864e\u4e5f\u5404\u4f24\u4e86\u4e00\u4eba\u3002 \u3000\u3000\u516d\u4e2a\u653b\u51fb\u5c0f\u7ec4\uff0c\u7ecf\u8fd9\u756a\u6740\u622e\uff0c\u6218\u529b\u5df2\u96be\u518d\u7ec4\u5408\u3002 \u3000\u3000\u4f46\u675c\u671b\u6708\u5e76\u672a\u7ee7\u7eed\u6740\u622e\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5434\u5144\uff0c\u54b1\u4eec\u64a4\u56de\u5e9c\u8859\u5427\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u5e94\u4e86\u4e00\u58f0\uff0c\u56db\u5927\u540d\u6355\u540c\u65f6\u98de\u8eab\u800c\u8d77\uff0c\u79bb\u5f00\u4e86\u611a\u516c\u56ed\u6797\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u56db\u4e2a\u4eba\u90fd\u6709\u7740\u5f3a\u70c8\u7684\u7275\u6302\uff0c\u738b\u77e5\u5e9c\u7684\u5b89\u5371\uff0c\u624d\u662f\u4ed6\u4eec\u6700\u62c5\u5fc3\u7684\u4e8b\u3002 \u3000\u3000\u626c\u5dde\u5e9c\u8859\uff0c\u5374\u662f\u6b63\u9677\u5165\u7d27\u5f20\u6050\u6016\u7684\u65f6\u523b\u3002\u4f46\u5728\u5e9c\u8859\u4e4b\u5916\uff0c\u5374\u611f\u53d7\u4e0d\u5230\u4efb\u4f55\u7d27\u5f20\u6c14\u6c1b\uff0c\u884c \u4eba\u5f80\u6765\u5982\u5e38\uff0c\u56db\u4e2a\u5f53\u73ed\u7684\u8859\u5f79\uff0c\u6267\u7740\u7ea2\u7f28\u67aa\uff0c\u6602\u9996\u633a\u7acb\uff0c\u7ad9\u5728\u5e9c\u8859\u4e24\u4fa7\uff0c\u770b\u4e0d\u51fa\u4e00\u70b9\u51fa\u4e8b\u7684 \u6837\u5b50\u3002 \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u4e0d\u50cf\u51fa\u4e86\u4e8b\u60c5\u554a\uff1f\u201d \u3000\u3000\u201c\u5982\u662f\u4e00\u773c\u5c31\u80fd\u770b\u51fa\u51fa\u4e86\u4e8b\u60c5\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u9a6c\u63d0\u7763\u7684\u4e00\u90e8\u6c34\u5e08\uff0c\u5c31\u9a7b\u624e\u9644\u8fd1\uff0c\u65e9\u5c31 \u8d76\u6765\u9a70\u63f4\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5b88\u8857\u7684\u8f66\u5175\uff0c\u6267\u67aa\u884c\u4e86\u8fc7\u6765\uff0c\u56db\u4eba\u4ecd\u7136\u7a7f\u7740\u6613\u5bb9\u540e\u7684\u7834\u70c2\u8863\u670d\u3002 \u3000\u3000\u675c\u671b\u6708\u8d39\u4e86\u4e00\u756a\u53e3\u820c\uff0c\u89e3\u8bf4\u6e05\u695a\uff0c\u56db\u4eba\u624d\u8fdb\u4e86\u77e5\u5e9c\u8859\u95e8\u3002 \u3000\u3000\u201c\u54b1\u4eec\u662f\u5426\u5f97\u6362\u4ef6\u8863\u670d\uff0c\u518d\u53bb\u4e8c\u5802\u664b\u89c1\u5927\u4eba\uff1f\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u8fd9\u4e48\u4e2a\u908b\u9062\u6cd5\uff0c\u4e5f\u4e0d\u592a\u793c \u8c8c\u2026\u2026\u201d \u3000\u3000\u201c\u73b0\u5728\u793c\u8c8c\u5df2\u4e0d\u592a\u91cd\u8981\uff0c\u89e3\u6551\u5927\u4eba\u7684\u5371\u96be\u8981\u7d27\u2026\u2026\u201d \u3000\u3000\u201c\u60dc\u7389\uff0c\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u675c\u671b\u6708\u6025\u6025\u63a5\u9053\u3002 \u3000\u3000\u201c\u5927\u4eba\u548c\u5211\u6848\u5f20\u5e08\u7237\uff0c\u90fd\u88ab\u56f4\u56f0\u5728\u4e8c\u5802\u5185\u3002\u201d\u60dc\u7389\u7f13\u6b65\u7ed5\u9047\u4e00\u4e2a\u5899\u89d2\uff0c\u884c\u4e86\u8fc7\u6765\uff0c\u4eba\u4e5f \u6362\u7a7f\u4e86\u75be\u670d\u52b2\u88c5\uff0c\u624b\u4e2d\u4e5f\u591a\u4e86\u4e00\u628a\u957f\u5251\uff0c\u8863\u670d\u8d34\u8eab\uff0c\u7a88\u7a95\u7684\u8eab\u6750\uff0c\u4e5f\u66b4\u9732\u65e0\u9057\u3002\u675c\u671b\u6708\u4ece\u672a \u89c1\u8fc7\u5979\u7a7f\u7740\u8fd9\u7b49\u88c5\u675f\uff0c\u4e0d\u7981\u770b\u5f97\u4e00\u5446\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u60dc\u7389\u59d1\u5a18\uff0c\u4e8c\u5802\u7684\u60c5\u52bf\u73b0\u5728\u5982\u4f55\uff1f\u738b\u5927\u4eba\u5904\u5883\u5371\u9669\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e8c\u5802\u60c5\u52bf\u7d27\u5f20\uff0c\u738b\u5927\u4eba\u7684\u5904\u5883\u5371\u800c\u4e0d\u9669\uff01\u201d\u60dc\u7389\u9053\uff1a\u201c\u4f46\u6211\u770b\u5f97\u51fa\u4ed6\u5fc3\u4e2d\u662f\u60ca\u6012\u4ea4\u96c6\uff0c \u4f60\u4eec\u65e9\u5230\u4e00\u523b\uff0c\u53ef\u4f7f\u4ed6\u65e9\u4e9b\u5b89\u5fc3\uff01\u201d \u3000\u3000\u201c\u8bf4\u8bf4\u770b\uff0c\u4ec0\u4e48\u6837\u5b50\u5371\u800c\u4e0d\u9669\u7684\u5904\u5883\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u60c5\u52bf\u662f\u5426\u968f\u65f6\u4f1a\u53d8\uff1f\u201d \u3000\u3000\u201c\u4e09\u5341\u516d\u4e2a\u7070\u8863\u5927\u6c49\uff0c\u5206\u6210\u5341\u4e8c\u7ec4\u628a\u4e8c\u5802\u56f4\u56f0\u8d77\u6765\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u800c\u4e14\u63a9\u4e0a\u4e86\u4e8c\u5802\u5927\u95e8\uff0c \u56db\u4e2a\u9ed1\u8863\u4eba\uff0c\u5b88\u5728\u4e8c\u5802\u95e8\u53e3\uff0c\u8fd9\u5c31\u5b8c\u5168\u9694\u7edd\u4e86\u5185\u5916\u7684\u8baf\u606f\u2026\u2026\u201d \u3000\u3000\u201c\u4e8c\u5802\u5185\u4e5f\u6709\u503c\u73ed\u7684\u8859\u5f79\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u6ca1\u6709\u4e00\u4e2a\u4eba\u4f20\u8baf\u51fa\u6765\uff1f\u201d \u3000\u3000\u201c\u654c\u4eba\u6765\u7684\u5f88\u7a81\u7136\uff0c\u800c\u4e14\u5206\u7531\u4e09\u9762\u8d8a\u5899\u800c\u5165\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u4eba\u5bb6\u65e9\u5c31\u6478\u6e05\u4e86\u4e8c\u5802\u5f62\u52bf\uff0c\u201d \u60dc\u7389\u9053\uff1a\u201c\u884c\u52a8\u5feb\u901f\u6709\u5e8f\uff0c\u7070\u8863\u5927\u6c49\uff0c\u76f4\u6251\u4e8c\u5802\uff0c\u628a\u7559\u5728\u4e8c\u5802\u4e2d\u7684\u5927\u90e8\u8859\u5f79\u3001\u9ad8\u624b\uff0c\u5438\u5f15\u5230 \u4e86\u4e8c\u5802\u56db\u5468\uff0c\u5e03\u5b88\u9632\u62a4\uff0c\u4ee5\u4fdd\u62a4\u5927\u4eba\u7684\u5b89\u5168\uff0c\u654c\u65b9\u7684\u7b2c\u4e09\u6279\u9ad8\u624b\uff0c\u9646\u7eed\u8dc3\u5165\u4e8c\u5802\uff0c\u5206\u522b\u5c60\u6740 \u4e86\u5176\u4ed6\u5730\u65b9\u5c11\u6570\u5b88\u536b\uff0c\u63a7\u5236\u4e86\u4e8c\u5802\u5ead\u9662\u7b49\u5927\u90e8\u5730\u65b9\u2026\u2026\u201d \u3000\u3000\u675c\u671b\u6708\u63a5\u9053\uff1a\u201c\u738b\u5927\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u5927\u4eba\u73b0\u5728\u4e8c\u5802\u4e66\u623f\uff0c\u7531\u5211\u6848\u5f20\u5e08\u7237\u966a\u7740\u4ed6\uff01\u201d\u60dc\u7389\u9053\uff1a\u201c\u8859\u5f79\u519b\u5175\uff0c\u8981\u5927\u91cf\u6295\u5165\uff0c\u624d \u6709\u519b\u5a01\u6548\u7528\uff0c\u8ba9\u4ed6\u4eec\u5355\u6253\u72ec\u6597\uff0c\u662f\u8ba9\u4ed6\u4eec\u9001\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u6162\uff0c\u6162\uff0c\u6162\uff0c\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u4f60\u79bb\u5f00\u4e86\uff0c\u8c01\u5728\u4fdd\u62a4\u5927\u4eba\uff1f\u201d \u3000\u3000\u201c\u5c0f\u6587\u59d1\u5a18\uff0c\u8fd8\u6709\u526f\u603b\u6355\u5934\u738b\u575a\u3001\u4f55\u5927\u5149\u548c\u96c6\u4e2d\u5728\u4e8c\u5802\u5916\u9762\u7684\u5341\u51e0\u4f4d\u8859\u5f79\u3002\u201d\u60dc\u7389\u9053\uff1a \u201c\u4e0d\u8fc7\uff0c\u6709\u4e24\u4f4d\u7528\u5251\u7684\u4eba\uff0c\u53ef\u4ee5\u770b\u51fa\u662f\u9ad8\u624b\uff0c\u6df7\u5728\u8859\u5f79\u4e2d\uff0c\u5206\u5b88\u5728\u524d\u5802\u4e24\u6247\u7a97\u5b50\u5916\u9762\u3002\u201d \u3000\u3000\u201c\u4eba\u529b\u5f88\u5355\u8584\u5440\uff01\u4f60\u600e\u4e48\u53ef\u4ee5\u8dd1\u51fa\u6765\uff1f\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u4f60\u662f\u53ef\u6218\u7684\u5927\u5c06\u554a\uff1f\u201d \u3000\u3000\u60dc\u7389\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u6587\u59d0\u8981\u6211\u51fa\u6765\u770b\u770b\uff0c\u5927\u6982\u662f\u8bd5\u8bd5\u6211\u80c6\u91cf\u5982\u4f55\uff0c\u6211\u80fd\u4e0d\u542c\u5417\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u4eec\u5982\u4f55\u653b\u8fdb\u53bb\uff0c\u4ee5\u89e3\u4e8c\u5802\u4e4b\u5371\uff1f\u201d \u3000\u3000\u539f\u6765\uff0c\u5927\u5802\u548c\u4e8c\u5802\u4e4b\u95f4\uff0c\u8fd8\u6709\u4e00\u6bb5\u76f8\u5f53\u7684\u8ddd\u79bb\uff0c\u5173\u4e0a\u76f8\u901a\u7684\u95e8\u6237\uff0c\u6709\u5982\u4e24\u4e2a\u4e92\u4e0d\u8fde\u63a5\u7684 \u5ead\u9662\uff0c\u770b\u4e0d\u5230\u4e5f\u542c\u4e0d\u5230\u4efb\u4f55\u58f0\u606f\u3002 \u3000\u3000\u201c\u4e8c\u5802\u5ead\u9662\uff0c\u9664\u4e86\u4e09\u5341\u516d\u4e2a\u7070\u8863\u4eba\uff0c\u5200\u5df2\u5728\u624b\uff0c\u5251\u4e5f\u51fa\u9798\uff0c\u628a\u4e8c\u5802\u56e2\u56e2\u56f4\u4f4f\uff0c\u5c1a\u672a\u51fa\u624b \u62a2\u653b\uff0c\u4e8c\u5341\u591a\u4e2a\u7a7f\u7740\u9ed1\u8863\u7684\u4eba\uff0c\u5374\u51fa\u624b\u51cc\u5389\uff0c\u6b7c\u6740\u4e86\u4e8c\u5802\u5ead\u9662\u4e2d\u8859\u5f79\uff0c\u7136\u540e\u5f00\u59cb\u5728\u4e8c\u5802\u5916\u9762 \u96c6\u4e2d\uff0c\u4f46\u4e5f\u672a\u5c55\u5f00\u653b\u52bf\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u52a8\u624b\u5462\uff1f\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u60dc\u7389\u59d1\u5a18\u6709\u4f55\u770b\u6cd5\uff1f\u201d \u3000\u3000\u201c\u7b49\u4eba\uff01\u201d\u60dc\u7389\u9053\uff1a\u201c\u8d31\u59be\u611a\u89c1\uff0c\u4ed6\u4eec\u7b49\u4e00\u6279\u66f4\u9ad8\u660e\u7684\u6740\u624b\u3002\u8fd9\u6b21\u767d\u663c\u56f4\u6740\uff0c\u521d\u671f\u76f8\u5f53 \u6210\u529f\uff0c\u5c0f\u6587\u59d1\u5a18\u3001\u738b\u526f\u603b\u6355\u770b\u6cd5\u4e00\u81f4\uff0c\u4ee5\u4fdd\u62a4\u5927\u4eba\u7684\u5b89\u5168\u4e3a\u7b2c\u4e00\u8981\u52a1\u3002\u6240\u4ee5\uff0c\u8fde\u5ead\u9662\u4e2d\u8859\u5f79 \u88ab\u6740\uff0c\u4e5f\u4e0d\u51fa\u4e4e\u62a2\u6551\uff0c\u6211\u4eec\u4eba\u624b\u592a\u5c11\uff0c\u5b9e\u4e5f\u7ecf\u4e0d\u8d77\u635f\u4f24\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u5c31\u8fd9\u6837\u50f5\u6301\u4e0b\u53bb\uff0c\u4f1a\u5bf9\u54ea\u65b9\u6709\u5229\uff1f\u201d \u3000\u3000\u201c\u53cc\u65b9\u90fd\u6709\u5229\uff0c\u624d\u80fd\u6709\u8fd9\u6837\u4e00\u822c\u5e73\u9759\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u4ed6\u4eec\u7b49\u63f4\u624b\uff0c\u6211\u4eec\u4e5f\u5728\u7b49\u4f60\u4eec\u5f52\u6765\uff0c \u770b\u6765\uff0c\u6211\u4eec\u662f\u5148\u7b49\u5230\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u628a\u4e8c\u5802\u91cc\u5ead\u9662\u5f62\u52bf\u8bf4\u7684\u660e\u767d\u4e9b\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u4eec\u8981\u9009\u62e9\u4e00\u4e2a\u8fdb\u653b\u7684\u65b9\u6cd5\uff0c\u4e00\u4e3e \u74e6\u89e3\u4ed6\u4eec\u5728\u5ead\u9662\u4e2d\u7684\u5b9e\u529b\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u5982\u5148\u89c1\u738b\u5927\u4eba\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u770b\u5230\u56db\u4f4d\uff0c\u4ed6\u5fc3\u4e2d\u81ea\u4f1a\u591a\u4e00\u4e9b\u5b89\u5168\u611f\u3002\u8001\u5b9e\u8bf4\uff0c\u4ed6\u53e3 \u867d\u672a\u8a00\uff0c\u4f46\u5fc3\u4e2d\u5bf9\u6211\u548c\u5c0f\u6587\u59d1\u5a18\u5168\u65e0\u4fe1\u4efb\u611f\uff0c\u5012\u662f\u628a\u5e0c\u671b\u5bc4\u6258\u5728\u4f55\u5927\u5149\u548c\u738b\u526f\u603b\u6355\u5934\u7684\u8eab\u4e0a\uff0c \u4ed6\u5fc3\u4e2d\u754f\u60e7\u751a\u6df1\uff0c\u4f46\u8868\u9762\u4e0a\u8fd8\u6491\u5f97\u4f4f\uff0c\u8fd9\u4e5f\u662f\u5c0f\u6587\u59d1\u5a18\u5168\u91c7\u5b88\u52bf\u7684\u539f\u56e0\uff0c\u4e0d\u613f\u6740\u622e\u60e8\u72b6\uff0c\u60ca \u5413\u5230\u4ed6\u3002\u201d \u3000\u3000\u201c\u654c\u4eba\u5e94\u8be5\u5728\u4e94\u5341\u4e2a\u4ee5\u4e0a\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u5982\u518d\u6709\u63f4\u624b\u5230\u6765\uff0c\u6050\u5c06\u516b\u5341\u4e4b\u6570\uff0c\u90a3\u4e9b\u624b\u6267 \u9b3c\u5934\u5200\uff0c\u4e09\u4eba\u4e00\u7ec4\u7684\u5408\u51fb\u9635\u52bf\uff0c\u76f8\u5f53\u5389\u5bb3\u3002\u4eca\u5929\u4e00\u65e9\uff0c\u6211\u4eec\u5df2\u540c\u4ed6\u4eec\u6740\u4e86\u4e00\u9635\uff0c\u5c0f\u6587\u59d1\u5a18\u7684 \u7a33\u5065\u4f5c\u6cd5\u4e0d\u9519\uff0c\u5982\u662f\u5df2\u7ecf\u52a8\u624b\uff0c\u53ea\u8981\u4ed6\u4eec\u628a\u4f60\u4eec\u4e24\u4f4d\u59d1\u5a18\u56f0\u4f4f\uff0c\u4e8c\u5802\u60c5\u52bf\u6050\u6015\u5c31\u5f88\u53ef\u6015\u4e86\u3002\u201d \u3000\u3000\u70b9\u5230\u4e3a\u6b62\uff0c\u4e5f\u672a\u628a\u4e8b\u60c5\u8bf4\u7684\u5f88\u6e05\u695a\u3002 \u3000\u3000\u201c\u4e8c\u5802\u60c5\u52bf\uff0c\u968f\u65f6\u53ef\u4ee5\u52a8\u4e0a\u624b\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u56db\u4f4d\u5c31\u65e9\u70b9\u8fc7\u53bb\u5427\uff01\u201d \u3000\u3000\u201c\u60dc\u7389\uff0c\u5982\u82e5\u773c\u4e0b\u7684\u5e73\u9759\uff0c\u80fd\u4fdd\u6301\u4e0b\u53bb\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u8981\u7834\u574f\u5b83\uff0c\u6211\u4eec\u5148\u586b\u4e00 \u4e0b\u809a\u5b50\u3002\u201d \u3000\u3000\u201c\u5c0f\u6587\u59d1\u5a18\u679c\u662f\u601d\u8651\u7684\u5468\u5bc6\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u65e9\u5df3\u5728\u4e8c\u5802\u4e2d\u5907\u4e0b\u4e86\u9152\u83dc\u548c\u98df\u7528\u4e4b\u7269\uff0c\u56db\u4f4d \u5230\u4e8c\u5802\u5403\u5427\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e09\u4f4d\u5144\u5f1f\uff0c\u54b1\u4eec\u8fdb\u5165\u4e8c\u5802\uff0c\u5c3d\u91cf\u4fdd\u6301\u9547\u9759\uff0c\u80fd\u4e0d\u7834\u574f\u73b0\u5728\u76f8\u6301\u5c40 \u52bf\u6700\u597d\u3002\u5c0f\u6587\u59d1\u5a18\u4e0d\u662f\u5728\u7b49\u6211\u4eec\uff0c\u800c\u662f\u5728\u7b49\u603b\u6355\u5934\uff0c\u7b49\u5230\u4e86\u6211\u4eec\u662f\u610f\u5916\uff0c\u6211\u60f3\u603b\u6355\u5934\u79bb\u5f00\u65f6\uff0c \u4e00\u5b9a\u5bf9\u5c0f\u6587\u6709\u6240\u5429\u5490\u3002\u201d \u3000\u3000\u201c\u7b49\u5230\u8bf8\u4f4d\uff0c\u662f\u610f\u5916\u4e4b\u559c\uff0c\u5c0f\u6587\u4e00\u5b9a\u4f1a\u9ad8\u5174\u7684\u8df3\u8d77\u6765\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u603b\u6355\u5934\u786e\u66fe\u662f\u4ea4\u4ee3 \u8fc7\u5c0f\u6587\uff0c\u5929\u4eae\u540e\u4e00\u5b9a\u56de\u6765\uff0c\u5982\u4eca\u5df2\u5feb\u8fd1\u4e2d\u5348\uff0c\u8fd8\u4e0d\u89c1\u884c\u8e2a\uff0c\u6050\u6015\u662f\u9047\u4e0a\u963b\u788d\u4e86\uff1f\u201d \u3000\u3000\u201c\u8fd9\u8eab\u8863\u670d\uff0c\u5b9e\u5728\u662f\u4e0d\u6210\u6837\u5b50\uff1f\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u5927\u4eba\u770b\u5230\u4e86\uff0c\u5b9a\u4f1a\u89c9\u7740\u6211\u4eec\u592a\u968f\u4fbf\u3002\u201d \u3000\u3000\u201c\u4f1a\u66f4\u656c\u91cd\u51e0\u4f4d\uff0c\u56db\u65b9\u5927\u6355\u5934\uff0c\u5fe0\u4e8e\u804c\u5b88\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u80fd\u653e\u4e0b\u8eab\u6bb5\uff0c\u4e0d\u60dc\u5de5\u672c\u5730\u6295\u5165 \u5de5\u4f5c\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5927\u5802\u4e0a\u7684\u503c\u73ed\u5b98\u540f\uff0c\u5927\u6982\u4e5f\u8b66\u89c9\u5230\u51fa\u4e86\u4e8b\u60c5\uff0c\u8dd1\u8fc7\u6765\u8bf7\u793a\u675c\u671b\u6708\uff0c\u8981\u4e0d\u8981\u901a\u77e5\u6c34 \u519b\u63d0\u7763\u5e9c\uff0c\u6d3e\u5175\u9a70\u63f4\u5e9c\u8859\u3002 \u3000\u3000\u8fd9\u91cc\u7684\u6c34\u5e08\u6700\u7cbe\u9510\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5148\u628a\u5e9c\u4e2d\u8859\u5f79\uff0c\u4eb2\u5175\u96c6\u4e2d\u5927\u5802\u5f85\u547d\uff0c\u6682\u4e0d\u7528\u901a\u77e5\u4ed6\u5904\u6d3e\u5175\u63a5\u5e94\uff0c\u8fd9\u8981\u5927\u4eba \u81ea\u5df1\u51b3\u5b9a\u3002\u201d \u3000\u3000\u6ca1\u6709\u53e9\u95e8\uff0c\u4e94\u4e2a\u4eba\u4e00\u4e2a\u98de\u817e\uff0c\u8d8a\u4e0a\u5c4b\u9762\uff0c\u8fdb\u5165\u4e86\u4e8c\u5802\u3002 \u3000\u3000\u5f62\u52bf\u679c\u5982\u60dc\u7389\u6240\u8a00\uff0c\u53cc\u65b9\u4eba\u624b\uff0c\u5927\u90e8\u5206\u96c6\u4e2d\u5728\u4e8c\u5802\u524d\u9762\uff0c\u5eca\u6cbf\u7a97\u5916\uff0c\u7ad9\u7740\u5e9c\u8859\u4e2d\u4eba\uff0c\u56db \u7ec4\u7070\u8863\u4eba\uff0c\u5200\u5251\u51fa\u9798\uff0c\u6321\u5728\u4e8c\u5802\u524d\u9762\uff0c\u4ee5\u4e8c\u5802\u7684\u5385\u5802\u4e3a\u4e3b\uff0c\u7070\u8863\u4eba\u7531\u6b63\u9762\u6563\u5e03\u81f3\u4e24\u8fb9\uff0c\u4e09\u5341 \u516d\u4e2a\u4eba\uff0c\u5206\u6210\u5341\u4e8c\u7ec4\uff0c\u6574\u4e2a\u628a\u4e8c\u5802\u56f0\u4f4f\u4e86\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u6697\u4e2d\u8ba1\u6570\uff0c\u5341\u4e8c\u4e2a\u9ed1\u8863\u4eba\u6563\u5e03\u5728\u4e8c\u5802\u6b63\u9762\uff0c\u9884\u4f30\u662f\u7c7b\u4f3c\u65e9\u4e0a\u52a8\u624b\u7684\u9ed1\u8863\u4eba\u540c\u7ea7\u7684 \u6740\u624b\uff0c\u53ea\u4e0d\u8fc7\u591a\u4e86\u4e09\u500d\u3002 \u3000\u3000\u4e94\u4eba\u7684\u884c\u52a8\uff0c\u53ea\u5f15\u52a8\u9ed1\u8863\u4eba\u7684\u76ee\u5149\u770b\u8fc7\u6765\uff0c\u5e76\u672a\u51c6\u5907\u62e6\u622a\u3002 \u3000\u3000\u60dc\u7389\u5982\u4f55\u51fa\u6b64\u56f4\u56f0\uff0c\u675c\u671b\u6708\u7b49\u6ca1\u6709\u591a\u95ee\uff0c\u60dc\u7389\u4e5f\u672a\u7ec6\u8bf4\uff0c\u73b0\u5728\uff0c\u60dc\u7389\u5374\u662f\u5f53\u5148\u5e26\u8def\uff0c\u76f4 \u5165\u4e8c\u5802\u3002 \u3000\u3000\u675c\u671b\u6708\u6015\u5979\u6709\u5931\uff0c\u7d27\u8ffd\u4e24\u6b65\uff0c\u76f4\u903c\u60dc\u7389\u8eab\u540e\u3002 \u3000\u3000\u60dc\u7389\u56de\u5934\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u522b\u6446\u51fa\u52a8\u6b66\u7684\u67b6\u5b50\uff0c\u6211\u4eec\u8fd8\u6709\u4e00\u534a\u673a\u4f1a\uff0c\u5e73\u5b89\u5730\u8fdb\u5165\u4e8c\u5802\u3002\u201d \u3000\u3000\u201c\u660e\u767d\u4e86\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4f46\u9632\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u65e0\uff0c\u4f60\u8981\u5c0f\u5fc3\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u8ddf\u6211\u540e\u9762\u8d70\uff0c\u8981\u6709\u5c65\u9669\u5982\u5937\u7684\u6c89\u7740\u3002\u201d\u83b2\u6b65\u59d7\u59d7\uff0c\u518d\u52a0\u4e86\u884c\u901f\u3002 \u3000\u3000\u7a7f\u8fc7\u7070\u8863\u4eba\u7684\u7ec4\u9635\uff0c\u7adf\u662f\u65e0\u4eba\u51fa\u624b\u963b\u62e6\uff0c\u76f4\u5165\u4e8c\u5802\u3002 \u3000\u3000\u5c0f\u6587\u624b\u6367\u957f\u5251\u7ad9\u5728\u95e8\u5de6\uff0c\u738b\u575a\u624b\u63e1\u5355\u5200\uff0c\u7ad9\u5728\u95e8\u53f3\uff0c\u738b\u5c11\u537f\u3001\u5f20\u5e08\u7237\uff0c\u5206\u5bbe\u4e3b\u5750\u5728\u4e8c\u5802 \u4e2d\u95f4\uff0c\u4f55\u5927\u5149\u7ad9\u5728\u738b\u5927\u4eba\u8eab\u540e\uff0c\u4e8c\u4e2a\u5e74\u8f7b\u7684\u8859\u5f79\uff0c\u7167\u987e\u8336\u6c34\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u4e8c\u5802\u4e2d\u518d\u65e0\u4ed6\u4eba\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u8138\u8272\u7d27\u7ef7\uff0c\u76f4\u5230\u770b\u6e05\u695a\u4e86\u675c\u671b\u6708\u7b49\u51e0\u4eba\u8eab\u5206\uff0c\u624d\u6709\u4e86\u4e00\u4e1d\u7b11\u5bb9\uff0c\u8d77\u8eab\u8bf4\u9053\uff1a\u201c\u56db \u4f4d\u8f9b\u82e6\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\u53d7\u60ca\u4e86\uff0c\u532a\u60a3\u95ef\u5165\u4e86\u77e5\u5e9c\u8859\u95e8\uff0c\u662f\u6211\u7b49\u65e0\u80fd\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u8fd8\u8bf7\u5927\u4eba\u6055\u7f6a\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u597d\u50cf\u602a\u4e0d\u5230\u8bf8\u4f4d\u5934\u4e0a\uff0c\u51b0\u51bb\u4e09\u5c3a\uff0c\u975e\u4e00\u65e5\u4e4b\u5bd2\uff0c\u6211\u6cbb\u7406\u626c\u5dde\u5e9c\uff0c\u5df2\u4e09\u5e74\u671f \u6ee1\uff0c\u7adf\u7136\u628a\u76d7\u532a\u6cbb\u5165\u8859\u95e8\u4e2d\u6765\uff0c\u4e0d\u53ea\u662f\u53ef\u7b11\uff0c\u7b80\u76f4\u662f\u65e0\u80fd\uff0c\u8fd9\u6279\u4eba\u51b3\u4e0d\u80fd\u9976\u6055\u3002\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u8981\u7ef3\u4e4b\u4e8e\u6cd5\u2026\u2026\u201d \u3000\u3000\u738b\u77e5\u5e9c\u7a81\u7136\u7b11\u4e86\uff0c\u8bdd\u9898\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u6b64\u65f6\u6b64\u60c5\uff0c\u8c08\u8fd9\u4e9b\u662f\u4e0d\u662f\u6709\u70b9\u767d\u65e5\u8bf4\u68a6\u3002\u770b\u56db\u4f4d\u795e \u60c5\uff0c\u4e0d\u53ea\u662f\u4e00\u591c\u672a\u7720\uff0c\u53ef\u80fd\u5df2\u4e45\u65f6\u672a\u8fdb\u98df\u7269\uff0c\u8bf7\u5165\u623f\u4e2d\u8fdb\u9910\u5427\uff01\u201d \u3000\u3000------------- \u3000\u3000\u5b64\u5251\u751f  \u626b\u63cf\u3000  \u695a\u5929\u4fa0\u5f71  OCR  \uff0c\u65e7\u96e8\u697c\u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u5341\u56db\u7ae0 \u3000\u3000\u8fd9\u4e8c\u5802\u6ca1\u6709\u4e13\u8bbe\u9910\u5385\uff0c\u5c31\u5728\u4f1a\u5ba2\u7684\u5730\u65b9\u67b6\u5f20\u684c\u5b50\u4f5c\u9910\u5385\u4e86\u3002 \u3000\u3000\u9152\u98df\u65e9\u5df2\u6446\u597d\uff0c\u56db\u5927\u6355\u5934\u5750\u4e0b\u5c31\u5403\uff0c\u738b\u77e5\u5e9c\u7adf\u7136\u4e5f\u8ddf\u4e86\u8fdb\u6765\uff0c\u7d27\u968f\u5728\u4ed6\u8eab\u540e\u7684\u4f55\u5927\u5149\u3001 \u5c0f\u6587\u59d1\u5a18\u5c31\u53d7\u5c3d\u59d4\u5c48\u4e86\uff0c\u8ddf\u5728\u8eab\u540e\uff0c\u7ad9\u5728\u4f1a\u5ba2\u5ba4\u7684\u95e8\u53e3\uff0c\u5e78\u597d\u60dc\u7389\u7d27\u884c\u4e86\u51e0\u6b65\uff0c\u548c\u5c0f\u6587\u7ad9\u8d77 \u4e00\u8d77\u4f4e\u58f0\u4ea4\u8c08\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u56de\u987e\u4e86\u675c\u671b\u6708\u4e00\u773c\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u597d\u4fee\u517b\uff0c\u65e2\u4e0d\u89c1\u6012\u8272\uff0c\u4e5f\u4e0d\u89c1\u59d4\u5c48\u7684\u8868\u60c5\u3002\u201d \u3000\u3000\u4ed6\u7a81\u7136\u5192\u51fa\u8fd9\u4e24\u53e5\u8bdd\uff0c\u56db\u5927\u6355\u5934\u90fd\u77e5\u9053\u4ed6\u662f\u66ff\u5c0f\u6587\u53eb\u5c48\uff0c\u4f46\u738b\u77e5\u5e9c\u4ecd\u7136\u82e5\u65e0\u6240\u77e5\u4f3c\u7684\uff0c \u4f4e\u58f0\u9053\uff1a\u201c\u675c\u53f8\u4e3b\uff0c\u603b\u6355\u5934\u51e0\u65f6\u5f52\u6765\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u67e5\u529e\u5211\u6848\uff0c\u5f88\u96be\u8ba2\u51fa\u4e00\u4e2a\u5f52\u671f\uff0c\u5c31\u62ff\u6211\u4eec\u56db\u4e2a\u6765\u8bf4\u5427\uff01\u6628\u5929\u51fa\u52a8\uff0c \u5c31\u662f\u8ffd\u67e5\u8fd9\u4e9b\u6740\u624b\u7684\u843d\u811a\u5730\u65b9\uff0c\u6211\u4eec\u662f\u627e\u5230\u4e86\uff0c\u6740\u4e86\u534a\u591c\u548c\u4e00\u4e2a\u4e0a\u5348\uff0c\u4ed6\u4eec\u4ecd\u7136\u6709\u80fd\u529b\u5206\u51fa \u5927\u6279\u6740\u624b\uff0c\u6478\u5165\u5e9c\u8859\u4e2d\u6765\uff0c\u5e78\u597d\uff0c\u6211\u4eec\u53ca\u65f6\u8d76\u56de\u6765\uff0c\u51c6\u5907\u5148\u5411\u603b\u6355\u5934\u7980\u660e\u5185\u60c5\uff0c\u518d\u4f5c\u5904\u7f6e\uff0c \u5374\u8d76\u4e0a\u4e86\u4ed6\u4eec\u8fdb\u653b\u5e9c\u8859\u7684\u884c\u52a8\u3002\u539f\u5b9a\u4eca\u65e9\u5f52\u6765\u7684\u603b\u6355\u5934\uff0c\u5374\u56e0\u6848\u60c5\u53d8\u5316\uff0c\u8bef\u4e86\u5f52\u671f\uff0c\u8fd9\u4e00\u4e9b \u4e8b\u60c5\u90fd\u4e0d\u5728\u9884\u6599\u4e4b\u5185\uff0c\u4f46\u603b\u6355\u5934\u5374\u4e5f\u505a\u4e86\u4e00\u4e9b\u5e94\u53d8\u7684\u5b89\u6392\u3002\u201d \u3000\u3000\u738b\u5927\u4eba\u9053\uff1a\u201c\u65e2\u5728\u9884\u6599\u4e4b\u5916\uff0c\u5374\u53c8\u6709\u5e94\u53d8\u5b89\u6392\uff0c\u4f60\u5012\u8bf4\u8bf4\u770b\uff0c\u4ec0\u4e48\u5b89\u6392\u5440\uff1f\u201d \u3000\u3000\u201c\u7b2c\u4e00\u662f\u7559\u4e0b\u4e86\u5c0f\u6587\u59d1\u5a18\uff0c\u5979\u53ef\u662f\u603b\u6355\u5934\u8eab\u4fa7\u7684\u52c7\u731b\u6218\u5c06\uff0c\u5251\u672f\u7edd\u4e16\uff0c\u52c7\u51a0\u4e09\u519b\uff0c\u53c8\u5a07 \u5c0f\u7f8e\u4e3d\uff0c\u8ba8\u4eba\u559c\u7231\uff0c\u7559\u4e0b\u6765\u4fdd\u62a4\u4f60\uff0c\u662f\u4f55\u7b49\u91cd\u89c6\u5927\u4eba\u2026\u2026\u201d \u3000\u3000\u738b\u5c11\u537f\u5fc3\u4e2d\u4e0d\u670d\uff0c\u63a5\u9053\uff1a\u201c\u6162\u6162\u6162\uff0c\u8981\u8bf4\uff0c\u5c31\u628a\u8bdd\u8bf4\u4e2a\u6e05\u695a\uff0c\u4f60\u8bf4\u5c0f\u6587\u59d1\u5a18\u7f8e\u8c8c\u53ef\u7231\uff0c \u6211\u662f\u7edd\u5bf9\u8bc1\u6210\uff0c\u8bf4\u5979\u52c7\u51a0\u4e09\u519b\uff0c\u662f\u5426\u6709\u70b9\u5938\u5f20\u5462\u3002\u6211\u4e0d\u5fcd\u770b\u7740\u5979\u4e3a\u4e86\u4fdd\u62a4\u6211\uff0c\u88ab\u4eba\u6740\u4f24\uff0c\u624d \u51cf\u5c11\u5979\u7684\u8d1f\u91cd\uff0c\u79bb\u6211\u8fdc\u4e00\u4e9b\uff0c\u4e00\u65e6\u60c5\u52bf\u6709\u53d8\uff0c\u4e0d\u5229\u6211\u4eec\u65f6\uff0c\u5c0f\u6587\u59d1\u5a18\u5982\u6709\u72ec\u81ea\u9003\u751f\u7684\u673a\u4f1a\uff0c \u5c31\u53ef\u4ee5\u81ea\u5df1\u8d70\u4e86\uff0c\u4e0d\u7528\u4e3a\u6211\u6240\u7d2f\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u7b49\u8f6c\u5f2f\u6478\u89d2\u7684\u7528\u5fc3\uff0c\u522b\u4eba\u5982\u4f55\u63e3\u6d4b\u5f97\u900f\uff0c\u4f46\u8bf4\u51fa\u6765\u5012\u4e5f\u662f\u8a00\u81f3\u6210\u7406\uff0c\u8fdc\u79bb\u5979\uff0c\u662f\u4e3a \u4e86\u4fdd\u62a4\u5979\u3002 \u3000\u3000\u8bf4\u8bdd\u7684\u58f0\u97f3\u867d\u7136\u4e0d\u5927\uff0c\u4f46\u7ad9\u5728\u95e8\u53e3\u7684\u5c0f\u6587\u3001\u60dc\u7389\u90fd\u542c\u5f97\u5341\u5206\u6e05\u695a\uff0c\u5c0f\u6587\u7684\u53cd\u5e94\u662f\u82e6\u7b11\u4e00 \u4e0b\uff0c\u60dc\u7389\u5374\u6447\u6447\u5934\uff0c\u4e00\u526f\u4e0d\u4ee5\u4e3a\u7136\u7684\u6837\u5b50\u3002 \u3000\u3000\u201c\u8bfb\u4e66\u4eba\u7684\u5fc3\u773c\u591a\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u4e9b\u6b66\u592b\uff0c\u679c\u7136\u662f\u5f88\u96be\u63e3\u6d4b\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u4eba\uff0c\u5c91\u80e1\u5b50\u4e00\u5411\u8bf4\u8bdd\uff0c\u90fd\u662f\u76f4\u6765\u76f4\u5f80\uff0c\u4e0d\u4f1a\u8f6c\u5f2f\u5b50\uff0c\u4f60\u628a\u5c0f\u6587\u59d1\u5a18\u9063\u5b88\u8fdc\u5904\uff0c\u662f\u6015 \u5979\u65e0\u80fd\u4fdd\u62a4\u4f60\uff0c\u5bf9\u5979\u6ca1\u6709\u4fe1\u5fc3\uff0c\u53ef\u662f\u4f60\u9519\u4e86\uff0c\u76ee\u4e0b\u5728\u8fd9\u626c\u5dde\u5e9c\u8859\u4e2d\u4eba\uff0c\u5982\u8bba\u6280\u827a\u4e4b\u5f3a\u3001\u6b66\u529f \u4e4b\u9ad8\uff0c\u5c0f\u6587\u5e94\u5c45\u9996\u4f4d\uff0c\u5509\uff01\u4e00\u6587\u4e00\u6b66\uff0c\u770b\u6cd5\u4e0d\u540c\uff0c\u89c1\u8bc6\u4e0a\u4e5f\u5c31\u65e0\u6cd5\u4e00\u81f4\u4e86\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u4e2d\uff0c\u542b\u6709\u5632\u7b11\u610f\u5473\uff0c\u662f\u8bf4\u4ed6\u6709\u773c\u4e0d\u8bc6\u6cf0\u5c71\u7684\u610f\u601d\u4e86\u3002 \u3000\u3000\u738b\u5c11\u537f\u8138\u8272\u4e00\u53d8\uff0c\u4f3c\u60f3\u53d1\u4f5c\uff0c\u4f46\u5ffd\u7136\u60f3\u5230\u8fd9\u56db\u65b9\u5927\u6355\u5934\u7684\u54c1\u7ea7\u548c\u4ed6\u76f8\u82e5\uff0c\u4e0d\u4e00\u5b9a\u628a\u4ed6\u5f53 \u4f5c\u4e0a\u53f8\u770b\u5f85\uff0c\u4e00\u65e6\u5f15\u8d77\u51b2\u7a81\uff0c\u6b64\u60c5\u6b64\u666f\uff0c\u5c31\u5f88\u96be\u5904\u7f6e\u4e86\u3002\u5f53\u4e0b\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\uff0c \u4e0b\u5b98\u8bfb\u4e66\u3001\u5e94\u8003\uff0c\u5168\u5c5e\u6587\u4e8b\uff0c\u54ea\u4f1a\u6709\u80fd\u529b\u770b\u51fa\u4e00\u4e2a\u4eba\u6b66\u529f\u6df1\u6d45\uff1f\u56db\u4f4d\u65e2\u56de\u5e9c\u8859\uff0c\u626c\u5dde\u5e9c\u8fd9\u573a \u52ab\u96be\uff0c\u5f53\u53ef\u5e73\u5b89\u5ea6\u8fc7\u4e86\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u4eba\u653e\u5fc3\uff0c\u56db\u5927\u6355\u5934\u53ea\u8981\u6709\u4e00\u4e2a\u4eba\u6d3b\u7740\uff0c\u7edd\u4e0d\u8ba9\u738b\u5927\u4eba\u53d7\u5230\u4f24 \u5bb3\u3002\u201d \u3000\u3000\u201c\u4e0b\u5b98\u611f\u6fc0\uff0c\u201d\u738b\u5c11\u537f\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u5411\u5916\u884c\u53bb\uff0c\u4f55\u5927\u5149\u7d27\u968f\u5176\u540e\u3002 \u3000\u3000\u5c0f\u6587\u59d1\u5a18\u671b\u671b\u56db\u5927\u6355\u5934\uff0c\u7d27\u8ffd\u5728\u738b\u5927\u4eba\u8eab\u540e\u8ddf\u53bb\u3002 \u3000\u3000\u60dc\u7389\u4f3c\u662f\u6015\u5c0f\u6587\u5b64\u72ec\uff0c\u7d27\u8ffd\u5c0f\u6587\u5e76\u80a9\u800c\u7acb\u3002 \u3000\u3000\u201c\u5feb\u4e9b\u5403\u996d\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6211\u770b\u4e8c\u5802\u60c5\u52bf\u6b63\u5728\u53d8\u52a8\uff0c\u5bf9\u9635\u640f\u6597\u5c31\u5feb\u53d1\u751f\u4e86\uff0c\u654c\u4eba\u7684\u9ad8 \u660e\u63f4\u624b\uff0c\u4e5f\u8be5\u5230\u4e86\u5e9c\u8859\uff0c\u5012\u5e0c\u671b\u603b\u6355\u5934\uff0c\u4e5f\u80fd\u8d76\u56de\u6765\uff0c\u5148\u5728\u626c\u5dde\u5e9c\u8859\u5c55\u5f00\u4e00\u573a\u5bf9\u51b3\uff0c\u4e5f\u8bb8\u80fd \u6253\u51fa\u4e00\u70b9\u7709\u76ee\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u53d7\u5230\u5c91\u5578\u864e\u4e00\u987f\u8bbd\u5632\uff0c\u5fc3\u4e2d\u4e5f\u618b\u4e86\u4e00\u8154\u6012\u6c14\uff0c\u4eba\u5374\u53d8\u7684\u80c6\u5927\u4e86\uff0c\u7adf\u7136\u76f4\u884c\u5230\u4e8c\u5802 \u95e8\u53e3\uff0c\u53ec\u8fc7\u8859\u5f79\uff0c\u642c\u4e86\u4e00\u5f20\u6728\u6905\uff0c\u5728\u95e8\u53e3\u5750\u4e0b\u4e86\u3002 \u3000\u3000\u4f55\u5927\u5149\u548c\u5c0f\u6587\u4e00\u5de6\u4e00\u53f3\u5730\u5206\u7ad9\u5728\u738b\u77e5\u5e9c\u7684\u4e24\u4fa7\u3002 \u3000\u3000\u8fd9\u5730\u65b9\uff0c\u5b8c\u5168\u66b4\u9732\u5728\u654c\u4eba\u7684\u653b\u51fb\u4e4b\u4e0b\uff0c\u5305\u62ec\u4e86\u5f13\u7bad\u548c\u6697\u5668\u7684\u88ad\u51fb\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u529d\u963b\u738b\u5927\u4eba\u7684\u884c\u52a8\uff0c\u4f46\u5374\u5728\u5c3d\u5fc3\u5730\u4fdd\u62a4\u4ed6\uff0c\u8fde\u5b88\u62a4\u524d\u5eca\u7a97\u5b50\u65c1\u7684\u5341\u4e2a\u8859\u5f79\uff0c\u4e5f\u5411 \u4e2d\u95f4\u9760\u8fd1\uff0c\u4ee5\u4fbf\u63a5\u8fd1\u738b\u5927\u4eba\u4e00\u70b9\uff0c\u8fd1\u4e00\u70b9\uff0c\u4fdd\u62a4\u4e0a\u81ea\u7136\u65b9\u4fbf\u4e00\u70b9\u3002\u4ed6\u662f\u4e2a\u597d\u5b98\uff0c\u656c\u7231\u4ed6\u7684\u4eba\uff0c \u90fd\u51fa\u81ea\u5185\u5fc3\u7684\u8bda\u610f\uff0c\u770b\u4ed6\u5750\u5728\u4e8c\u5802\u95e8\u53e3\u5bdf\u770b\u654c\u60c5\uff0c\u66f4\u89c9\u7740\u4ed6\u6e05\u6b63\u4e4b\u5916\uff0c\u4e5f\u5f88\u52c7\u6562\uff0c\u4eb2\u5192\u51f6\u9669\uff0c \u6fc0\u52b1\u58eb\u6c14\uff0c\u5927\u6982\u4e5f\u662f\u8fd9\u4e2a\u6837\u5b50\uff01\u8fd9\u65f6\uff0c\u56db\u65b9\u5927\u6355\u5934\u5df2\u6084\u7136\u5730\u6362\u4e0a\u4e86\u8859\u5f79\u7684\u8863\u7740\uff0c\u884c\u51fa\u4e8c\u5802\u95e8 \u5916\u3002 \u3000\u3000\u662f\u7684\uff01\u5bf9\u6297\u7684\u51c6\u5907\u5df2\u7ecf\u5b8c\u6210\u3002 \u3000\u3000\u201c\u6b63\u9762\u6709\u56db\u7ec4\u7070\u8863\u6740\u624b\uff0c\u54b1\u4eec\u5404\u81ea\u9009\u62e9\u4e00\u7ec4\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1b\u201c\u9009\u62e9\u6700\u6709\u628a\u63e1\u7684\u65f6\u673a\uff0c\u6482 \u5012\u4e00\u4e2a\u3002\u7136\u540e\uff0c\u987a\u52bf\u5411\u5916\u6269\u5c55\uff0c\u5341\u4e8c\u7ec4\u6740\u624b\uff0c\u6211\u4eec\u53ea\u8981\u653e\u5012\u5341\u4e8c\u4eba\uff0c\u4f59\u4e0b\u788c\u788c\u5c31\u4e0d\u8db3\u754f\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4eec\u4ea4\u8c08\u7684\u5730\u65b9\uff0c\u5c31\u5728\u5c0f\u6587\u8eab\u540e\u3002\u5f53\u7136\uff0c\u5c0f\u6587\u4e5f\u542c\u5230\u4e86\u4ed6\u4eec\u8c08\u8bdd\uff0c\u56de\u5934\u8bf4\u9053\uff1a\u201c\u600e\u4e48\uff1f \u90a3\u4e9b\u7070\u8863\u4eba\u624d\u662f\u96be\u7f20\u7684\u654c\u4eba\uff1f\u201d \u3000\u3000\u201c\u7070\u8863\u4eba\u5e03\u6210\u4e09\u4eba\u9635\u52bf\uff0c\u624d\u662f\u5f88\u96be\u5bf9\u4ed8\u7684\u529b\u91cf\u3002\u4e00\u4eba\u4e0d\u8db3\u754f\uff0c\u4e24\u4eba\u4e5f\u4e0d\u6015\uff0c\u4f46\u4e09\u4eba\u5408\u4e8e \u4e00\u5904\uff0c\u4e00\u51fb\u4e4b\u4e0b\uff0c\u786e\u5b9e\u6709\u4e07\u592b\u4e0d\u5f53\u4e4b\u52c7\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6211\u4eec\u5df2\u7ecf\u591a\u6b21\u8bd5\u8fc7\uff0c\u800c\u4e14\u5c61\u8bd5\u4e0d \u723d\uff01\u201d \u3000\u3000\u201c\u6709\u4e9b\u5927\u80cc\u5e38\u60c5\u554a\uff01\u201d\u5c0f\u6587\u9053\uff1a\u201c\u56db\u4f4d\u7ecf\u9a8c\u4e4b\u4e30\uff0c\u96be\u9053\u77a7\u4e0d\u51fa\u8e4a\u8df7\u4e48\uff1f\u201d \u3000\u3000\u201c\u95ee\u9898\u53ef\u80fd\u5728\u4e09\u4eba\u51fa\u5200\u4e4b\u524d\uff0c\u961f\u5f62\u7ec4\u5408\u4e0a\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u4eec\u7684\u624d\u667a\u6167\u80fd\uff0c\u8fd8 \u65e0\u6cd5\u77a7\u51fa\u539f\u56e0\uff0c\u8fd9\u4ef6\u4e8b\u6050\u8981\u603b\u6355\u5934\u8d39\u5fc3\u601d\u4e86\u3002\u4f46\u6211\u4eec\u627e\u51fa\u4e00\u4e2a\u6700\u7b80\u4fbf\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u5728\u4e09\u4eba\u5408 \u51fb\u4e4b\u524d\uff0c\u5148\u6740\u6b7b\u4e09\u4eba\u4e4b\u4e2d\u7684\u4e00\u4e2a\uff0c\u4f7f\u4ed6\u4eec\u7684\u5408\u4f5c\u89e3\u4f53\uff0c\u5c31\u5bb9\u6613\u5bf9\u4ed8\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e9b\u9ed1\u8863\u4eba\u5462\uff1f\u201d\u5c0f\u6587\u9053\uff1a\u201c\u548c\u7070\u8863\u4eba\u6709\u7740\u660e\u663e\u7684\u4e0d\u540c\uff0c\u4f3c\u975e\u4e00\u4e2a\u6740\u624b\u7b49\u7ea7\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u7684\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u8138\u4e0a\u90fd\u6234\u7740\u4eba\u76ae\u9762\u5177\uff0c\u6216\u662f\u7ecf\u8fc7\u6613\u5bb9\uff0c\u56e0\u4e3a\uff0c\u4ed6\u4eec\u4e0d\u5c11 \u662f\u6c5f\u5357\u9053\u4e0a\u7684\u6709\u540d\u4eba\u7269\uff0c\u4e5f\u53ef\u80fd\u662f\u4e00\u65b9\u9738\u4e3b\u7684\u8eab\u4efd\uff0c\u88ab\u65b9\u8f6e\u6536\u5165\u4e86\u6740\u624b\u9635\u4e2d\uff0c\u4e3a\u4e86\u4fdd\u62a4\u4ee5\u540e \u6d3b\u52a8\u7684\u8eab\u4efd\uff0c\u4e0d\u5f97\u4e0d\u6613\u5bb9\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u8d77\u6765\uff0c\u8840\u624b\u65b9\u8f6e\uff0c\u662f\u5927\u6076\u9b54\u4e86\uff1f\u201d\u5c0f\u6587\u9053\uff1a\u201c\u6211\u8981\u6740\u4e86\u4ed6\uff0c\u4ed6\u6765\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u6211\u8fd8\u6709\u89c1\u8fc7\u4ed6\uff0c\u6240\u4ee5\uff0c\u4e5f\u65e0\u6cd5\u786e\u5b9a\u4ed6\u662f\u5426\u5df2\u5165\u5e9c\u8859\uff0c\u8fd9\u4e9b\u4eba\u56f4\u800c\u4e0d\u6218\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a \u201c\u53ef\u80fd\u522b\u6709\u6240\u5f85\u3002\u201d \u3000\u3000\u201c\u662f\u7b49\u65b9\u8f6e\u672c\u4eba\uff0c\u8fd8\u662f\u66f4\u9ad8\u4e00\u7ea7\u7684\u6740\u624b\uff1f\u201d\u5c0f\u6587\u9053\uff1a\u201c\u7528\u5fc3\u4f55\u5728\u5462\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u795e\u60c5\u8083\u7136\u5730\u8bf4\u9053\uff1a\u201c\u5fc5\u8fbe\u76ee\u7684\uff0c\u6740\u6b7b\u76ee\u6807\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u5fc3\u5934\u8df3\u52a8\u4e86\u4e00\u4e0b\uff0c\u5374\u5fcd\u4e0b\u672a\u8a00\u3002 \u3000\u3000\u201c\u76ee\u6807\u662f\u8c01\uff1f\u201d\u5c0f\u6587\u9053\uff1a\u201c\u4e0d\u8981\u987e\u5fcc\u4ec0\u4e48\uff0c\u8bf4\u51fa\u6765\uff0c\u770b\u770b\u4f60\u7814\u5224\u7684\u7ed3\u679c\uff0c\u662f\u5426\u6b63\u786e\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u7814\u8ba8\u8fc7\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u90a3\u5fc5\u6740\u7684\u76ee\u6807\u2026\u2026\u201d\u7a81\u7136\u653e\u4f4e\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u53ef \u80fd\u662f\u738b\u5927\u4eba\uff01\u201d \u3000\u3000\u201c\u5bf9\uff01\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u5f97\u5148\u6740\u4e86\u6211\u3002\u201d\u5c0f\u6587\u9053\uff1a\u201c\u603b\u6355\u5934\u7ed9\u6211\u7684\u4ee4\u8c15\u662f\u6b7b\u5728\u738b\u5927\u4eba\u7684\u524d\u9762\u3002\u201d \u3000\u3000\u201c\u59d1\u5a18\u5df2\u660e\u786e\u5730\u4f20\u8fbe\u4e86\u603b\u6355\u7684\u4ee4\u8c15\uff0c\u738b\u77e5\u5e9c\u5982\u6709\u4e0d\u6d4b\uff0c\u56db\u5927\u6355\u5934\u9f50\u805a\u4e8e\u65af\uff0c\u8fde\u4e00\u4e2a\u4eba\u90fd \u4fdd\u62a4\u4e0d\u4e86\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u8fd8\u6709\u4f55\u989c\u751f\u4e8e\u4eba\u4e16\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u611f\u52a8\u6781\u4e86\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u56db\u5927\u6355\u5934\uff0c\u770b\u4e0a\u53bb\u6211\u884c\u6211\u7d20\uff0c\u4e00\u526f\u5929\u584c\u4e86\u4e5f\u4e0d\u5728\u4e4e\u7684\u6837\u5b50\uff0c \u4f46\u4ed6\u4eec\u81ea\u5f8b\u6807\u51c6\u4e4b\u9ad8\uff0c\u6709\u751a\u4e8e\u738b\u6cd5\u5f8b\u6761\uff0c\u8fd9\u4e9b\u4eba\u5916\u5f62\u6d12\u8131\uff0c\u5185\u5728\u5374\u662f\u6ee1\u8154\u5fe0\u4e49\uff0c\u662f\u53ef\u4ee5\u4fe1\u6258 \u7684\u670b\u53cb\uff0c\u662f\u53ef\u501a\u6258\u5927\u4e8b\u7684\u5fe0\u81e3\u826f\u5c06\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u5c0f\u6587\u8eab\u5b50\u4e00\u4fa7\uff0c\u5012\u5165\u4e86\u738b\u77e5\u5e9c\u7684\u6000\u4e2d\uff0c\u5de6\u624b\u626c\u8d77\uff0c\u63a5\u4f4f\u4e86\u4e00\u652f\u957f\u7bad\uff0c\u597d\u5feb\u7684\u624b \u811a\u3002 \u3000\u3000\u4f55\u5927\u5149\u548c\u738b\u575a\uff0c\u90fd\u77e5\u9053\u5c0f\u6587\u662f\u5973\u513f\u4e4b\u8eab\uff0c\u800c\u4e14\u662f\u5982\u82b1\u4f3c\u7389\u7684\u7f8e\u5973\uff0c\u5927\u5385\u5e7f\u4f17\uff0c\u4f17\u76ee\u777d\u777d \u4e4b\u4e0b\uff0c\u738b\u5927\u4eba\u5c31\u50cf\u88ab\u704c\u4e0b\u4e86\u4e24\u74f6\u8001\u9152\uff0c\u8fde\u4e24\u53ea\u8033\u6735\u90fd\u7ea2\u4e86\u8d77\u6765\u3002\u201c\u5927\u4eba\uff0c\u201d\u5c0f\u6587\u633a\u8eab\u800c\u8d77\uff0c \u9053\uff1a\u201c\u60c5\u52bf\u7d27\u5f20\uff0c\u4e2d\u95f4\u53c8\u6709\u4eb2\u5175\u963b\u9694\uff0c\u79fb\u4f4d\u4e0d\u6613\uff0c\u53ea\u597d\u5192\u72af\u5927\u4eba\u4e86\u3002\u201d \u3000\u3000\u770b\u5230\u5c0f\u6587\u624b\u4e2d\u957f\u7bad\uff0c\u95ea\u95ea\u751f\u5149\u7684\u7bad\u955e\uff0c\u738b\u77e5\u5e9c\u624d\u77e5\u9053\u5c0f\u6587\u6551\u4e86\u4ed6\u4e00\u547d\uff0c\u5fd6\u9053\uff1a\u7bad\u6765\u5982\u95ea \u7535\uff0c\u5c0f\u6587\u5374\u4e00\u624b\u628a\u7bad\u63a5\u4f4f\u4e86\uff0c\u4eba\u7684\u52a8\u4f5c\u600e\u80fd\u90a3\u6837\u4e00\u4e2a\u5feb\u6cd5\uff0c\u6bd4\u7bad\u8fd8\u5feb\u5440\uff01\u8fd9\u4e2b\u5934\u662f\u771f\u6b63\u9ad8\u624b \u554a\uff01\u6211\u6545\u610f\u51b7\u843d\u5979\uff0c\u4ee5\u6392\u6cc4\u5bf9\u7a0b\u603b\u6355\u5934\u7684\u4e0d\u6ee1\uff0c\u5b9e\u5728\u592a\u8fc7\u5c0f\u5bb6\u5b50\u6c14\u4e86\u2026\u2026\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u5374 \u8bf4\u9053\uff1a\u201c\u4e0b\u5b98\u5df2\u77e5\u603b\u6355\u5934\u7684\u5fc3\u610f\uff0c\u611f\u6fc0\u4e0d\u5c3d\uff0c\u5c0f\u6587\u59d1\u5a18\u7684\u6551\u547d\u4e4b\u60c5\uff0c\u66f4\u662f\u6c38\u94ed\u80ba\u8151\u3002\u201d \u3000\u3000\u5c0f\u6587\u9053\uff1a\u201c\u5927\u4eba\u5e0c\u671b\u76ee\u7779\u8fd9\u573a\u62da\u6218\uff0c\u6240\u4ee5\u624d\u4eb2\u5192\u77f3\u77e2\u4e4b\u9669\uff0c\u5c0f\u6587\u4e0d\u4fbf\u591a\u529d\uff0c\u6709\u5927\u4eba\u5750\u9547 \u73b0\u573a\uff0c\u4e5f\u53ef\u6fc0\u52b1\u58eb\u6c14\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u6709\u4e0d\u5c11\u4e2a\u9ed1\u8863\u4eba\u7f13\u6b65\u5411\u4e8c\u5802\u524d\u9762\u884c\u8fc7\u6765\u3002 \u3000\u3000\u60dc\u7389\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u4e9b\u9ed1\u8863\u4eba\u597d\u50cf\u8981\u6709\u6240\u884c\u52a8\u4e86\uff0c\u4f46\u90a3\u4e9b\u7070\u8863\u4eba\u5374\u4ecd\u7136\u5b88\u5728\u539f\u5730\u4e0d\u52a8\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u4eec\u7684\u5206\u5de5\u5f88\u7ec6\uff0c\u7070\u8863\u4eba\u4f3c\u4e4e\u662f\u4e13\u4ee5\u963b\u7edd\u63f4\u519b\u3001\u9632\u4eba\u7a81\u56f4\uff0c\u60f3\u662f\u4ee5\u9632\u5b88\u4e3a\u4e3b\u3002\u201d\u5c0f\u6587 \u9053\uff1a\u201c\u9ed1\u8863\u4eba\u5374\u4ee5\u5c60\u6740\u3001\u653b\u51fb\u4e3a\u4e3b\uff0c\u7b49\u4ed6\u4eec\u8fdb\u5165\u4e09\u4e08\u4e4b\u5185\uff0c\u54b1\u4eec\u5c31\u5f00\u59cb\u53cd\u51fb\u3002\u201d \u3000\u3000\u201c\u5c0f\u6587\u59d0\uff0c\u8981\u4e0d\u8981\u628a\u5de5\u4f5c\u5206\u914d\u4e00\u4e0b\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u514d\u5f97\u5e94\u53d8\u4e0d\u53ca\u3002\u201d \u3000\u3000\u201c\u4fdd\u62a4\u5927\u4eba\u7684\u4e8b\u7531\u4f60\u4ee3\u6211\u4e3a\u4e3b\uff0c\u518d\u52a0\u4e0a\u4f55\u5927\u5149\u548c\u738b\u575a\uff0c\u201d\u5c0f\u6587\u9053\uff1a\u201c\u6211\u4ee5\u6740\u654c\u4e3a\u4e3b\u3002\u5f53 \u7136\uff0c\u6700\u91cd\u8981\u7684\u662f\u4e0d\u8ba9\u4eba\u63a5\u8fd1\u738b\u5927\u4eba\uff0c\u5b8c\u5168\u6ca1\u4e0b\u624b\u884c\u523a\u7684\u673a\u4f1a\u3002\u201d\u60dc\u7389\u9053\uff1a\u201c\u56db\u4f4d\u5927\u6355\u5934\u5462\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e00\u5411\u72ec\u7acb\u529e\u6848\uff0c\u5224\u4e8b\u7684\u80fd\u529b\uff0c\u6211\u4eec\u4e0d\u5982\uff0c\u201d\u5c0f\u6587\u9053\uff1a\u201c\u4e0d\u7528\u7ba1\u4ed6\u4eec\u4e86\uff0c\u7531\u4ed6\u4eec\u81ea \u884c\u4f5c\u4e3b\u5427\uff1f\u201d \u3000\u3000\u60dc\u7389\u70b9\u70b9\u5934\u672a\u518d\u591a\u8a00\uff0c\u7d27\u508d\u5c0f\u6587\u8eab\u4fa7\u800c\u7acb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u7070\u8863\u4eba\u7ec4\u6210\u7684\u56db\u7ec4\u9635\u52bf\uff0c\u4e5f\u7f13\u7f13\u5411\u524d\u63a8\u8fdb\u4e86\u4e00\u4e08\u5de6\u53f3\uff0c\u53c8\u505c\u4e86\u4e0b\u6765\u3002\u4e5f\u5c31\u662f\u53cc\u65b9 \u4e4b\u95f4\u7684\u8ddd\u79bb\uff0c\u53c8\u7f29\u77ed\u4e86\u4e00\u4e08\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u56db\u65b9\u6355\u5934\uff0c\u7a7f\u7740\u8859\u5f79\u670d\u88c5\uff0c\u9c7c\u8d2f\u884c\u51fa\u4e8c\u5802\uff0c\u675c\u671b\u6708\u7ad9\u5728\u4e8c\u5802\u95e8\u53e3\u53f3\u4fa7\uff0c\u5434\u94c1\u5cf0\u7ad9 \u5728\u5de6\u4fa7\uff0c\u5c91\u5578\u864e\u3001\u4e8e\u627f\u5fd7\u5374\u6cbf\u8d70\u5eca\u5411\u4e24\u4fa7\u4f38\u5c55\uff0c\u5230\u4e24\u6247\u5927\u7a97\u53e3\u7684\u524d\u9762\u3002\u7a97\u524d\u672c\u6709\u4e94\u4e2a\u8859\u5f79\u5b88 \u62a4\uff0c\u73b0\u5728\u8868\u9762\u53c8\u591a\u4e86\u4e00\u4e2a\u8859\u5f79\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\u51fa\u5165\u5e9c\u8859\u591a\u6b21\uff0c\u5f88\u591a\u4eba\u90fd\u8ba4\u8bc6\u4ed6\u4eec\uff0c\u5c31\u7b97\u4e0d\u8ba4\u8bc6\uff0c\u4e5f\u53ef\u4ece\u5175\u5203\u4e0a\u77a7\u51fa\u4e00\u70b9\u82d7\u5934\u3002 \u3000\u3000\u5c91\u5578\u864e\u548c\u4e8e\u627f\u5fd7\uff0c\u76ee\u5149\u9510\u5229\uff0c\u4e00\u773c\u4e4b\u4e0b\uff0c\u5c31\u8ba4\u51fa\u4e86\u8fd9\u4e2a\u6b66\u5f53\u5f1f\u5b50\u7684\u8eab\u5206\uff0c\u4ed6\u4eec\u4e5f\u7a7f\u7740\u8859 \u5f79\u670d\u8272\uff0c\u4f46\u6c14\u52bf\u5374\u5927\u4e0d\u76f8\u540c\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u533a\u533a\u5173\u4e1c\u5c91\u5578\u864e\uff01\u201d \u3000\u3000\u201c\u4e45\u4ef0\u4e86\uff0c\u5317\u65b9\u5927\u6355\u5934\uff0c\u5728\u4e0b\u662f\u6b66\u5f53\u5f1f\u5b50\u77f3\u742a\uff0c\u5e94\u94c1\u7fce\u4e4b\u9080\uff0c\u5ba2\u5c45\u626c\u5dde\u5e9c\u7684\u6355\u5feb\u3002\u201d \u3000\u3000\u201c\u96be\u4e3a\u4f60\u4e86\uff0c\u77f3\u5144\u5f1f\uff0c\u4eca\u5929\u8fd9\u4e2a\u573a\u9762\u5f88\u58ee\u89c2\uff0c\u4f46\u4e5f\u5f88\u8270\u82e6\uff0c\u6211\u4eec\u4e0d\u4f46\u8981\u4ee5\u5be1\u51fb\u4f17\uff0c\u90a3\u7070 \u8863\u4eba\u7684\u7ec4\u5408\u9635\u52bf\uff0c\u5a01\u529b\u5341\u5206\u5f3a\u5927\uff0c\u4f46\u4ed6\u4eec\u4e00\u65e6\u5206\u5f00\uff0c\u5c31\u5bb9\u6613\u5bf9\u4ed8\u4e86\uff0c\u4e2a\u4e2d\u7684\u9053\u7406\uff0c\u4f60\u53bb\u60f3\u5427\uff01 \u81f4\u4e8e\u90a3\u4e9b\u9ed1\u8863\u4eba\uff0c\u4e5f\u7b97\u9ad8\u624b\uff0c\u4e0d\u8981\u501a\u671b\u90a3\u4e9b\u8859\u5f79\u80fd\u5e2e\u4f60\uff0c\u8fd9\u91cc\u80fd\u591f\u653e\u624b\u4e00\u6218\u7684\uff0c\u53ef\u80fd\u53ea\u6709\u6211 \u4eec\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u77f3\u742a\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5df2\u7ecf\u5f88\u597d\u4e86\uff0c\u5c91\u5927\u6355\u5934\u672a\u6765\u4e4b\u524d\uff0c\u53ea\u6709\u6211\u4e00\u4e2a\u4eba\u5566\uff01\u73b0\u5728\u589e\u52a0\u4e86\u4e00 \u500d\u7684\u751f\u529b\u519b\uff0c\u54b1\u4eec\u5e76\u80a9\u4e00\u6218\u5427\uff01\u201d\u7a81\u7136\u4fa7\u5934\u5bf9\u56db\u4e2a\u8859\u5f79\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u53bb\u4fdd\u62a4\u5927\u4eba\u5427\uff0c\u8fd9\u91cc\u4e0d \u7528\u4f60\u4eec\u5e2e\u5fd9\u4e86\uff01\u201d \u3000\u3000\u56db\u4eba\u7565\u4e00\u6c89\u541f\uff0c\u884c\u56de\u4e8c\u5802\u3002 \u3000\u3000\u5927\u7ea6\u5de6\u9762\u7684\u4e8e\u627f\u5fd7\u4e5f\u548c\u5f20\u9e9f\u8c08\u8fc7\uff0c\u4e5f\u628a\u56db\u4e2a\u8859\u5f79\u9063\u56de\u4e8c\u5802\u3002 \u3000\u3000\u8fd9\u5ea7\u4e8c\u5802\u7684\u6b63\u9762\u5f88\u5bbd\u5927\uff0c\u9664\u4e86\u4e00\u6247\u5927\u95e8\u4e4b\u5916\uff0c\u4e24\u65c1\u8fd8\u6709\u4e24\u6247\u5927\u7a97\u5b50\uff0c\u5899\u58c1\u662f\u7528\u5927\u9752\u7816\u780c \u6210\uff0c\u76f8\u5f53\u7262\u56fa\uff0c\u53ea\u8981\u5b88\u7d27\u7a97\u6237\u3001\u5927\u95e8\uff0c\u8981\u7834\u5899\u800c\u5165\uff0c\u5c31\u5f97\u8d39\u756a\u5de5\u592b\u4e86\u3002\u516b\u4e2a\u8859\u5f79\u56de\u5230\u738b\u575a\u8eab \u8fb9\uff0c\u738b\u526f\u603b\u6355\u5934\uff0c\u8ba4\u8bc6\u8fd9\u4e9b\uff0c\u662f\u4e00\u767e\u591a\u4e2a\u8859\u5f79\u4e2d\u9009\u51fa\u7684\u9ad8\u624b\uff0c\u4ed6\u4eec\u957f\u67aa\u5408\u523a\u672f\uff0c\u662f\u64d2\u8d3c\u7684\u4e00 \u7edd\u3002\u7acb\u523b\u628a\u516b\u4eba\u5b89\u6392\u5230\u738b\u77e5\u5e9c\u7684\u4e24\u4fa7\uff0c\u516b\u6839\u4e08\u4e8c\u957f\u7684\u7ea2\u7f28\u67aa\u95ea\u95ea\u751f\u5149\u3002 \u3000\u3000\u770b\u4e0a\u53bb\u5a01\u98ce\u591a\u4e86\uff0c\u4e5f\u597d\u50cf\u5728\u4fdd\u62a4\u4e0a\uff0c\u4e25\u5bc6\u5f88\u591a\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u3001\u675c\u671b\u6708\u7f13\u7f13\u79fb\u52a8\u8eab\u5b50\uff0c\u628a\u4f4d\u7f6e\u5b9a\u5728\u7a97\u6237\u4e0e\u95e8\u53e3\u4e4b\u95f4\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u5802\u95e8\u548c\u7a97\u53e3\uff0c\u4e5f\u4e0d\u8fc7\u4e00\u4e08\u5de6\u53f3\uff0c\u7ad9\u5728\u4e2d\u95f4\uff0c\u5404\u7ea6\u4e94\u5c3a\u8ddd\u79bb\uff0c\u662f\u53ef\u4ee5\u51fa\u624b\u4e24\u8fb9\u9a70\u63f4 \u7684\u4f4d\u7f6e\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u5728\u7070\u8863\u4eba\u7684\u9635\u52bf\u5b9a\u4e0b\u4e4b\u540e\uff0c\u53c8\u5f00\u59cb\u5411\u524d\u79fb\u52a8\uff0c\u53cc\u65b9\u90fd\u9009\u5b9a\u6700\u4f73\u7684\u653b\u9632\u4f4d\u7f6e\u3002 \u3000\u3000\u5f85\u53cc\u65b9\u5168\u90fd\u7a33\u5b9a\u4e0b\u6765\uff0c\u5f7c\u6b64\u7684\u8ddd\u79bb\u5df2\u5269\u4e0b\u4e00\u4e08\u4e94\u5c3a\u5de6\u53f3\u4e86\u3002 \u3000\u3000\u738b\u575a\u3001\u4f55\u5927\u5149\uff0c\u4e5f\u90fd\u6084\u7136\u5411\u524d\u79fb\u52a8\u4e86\u4e24\u5c3a\uff0c\u8d85\u8d8a\u738b\u5927\u4eba\u4e00\u5c3a\u6709\u4f59\u3002 \u3000\u3000\u5c0f\u6587\u3001\u60dc\u7389\u4e5f\u7f13\u7f13\u5411\u524d\u79fb\u52a8\u3002 \u3000\u3000\u7a81\u7136\u95ee\uff0c\u4eba\u5f71\u4e00\u95ea\uff0c\u63a5\u7740\u54cd\u8d77\u4e86\u4e00\u58f0\u60e8\u53eb\uff0c\u4e00\u4e2a\u7ad9\u5728\u6700\u524d\u9762\u7684\u9ed1\u8863\u4eba\uff0c\u5ffd\u7136\u5012\u4e86\u4e0b\u53bb\uff0c \u5c38\u4f53\u7740\u5730\uff0c\u80f8\u524d\u7684\u9c9c\u8840\u55b7\u51fa\u4e09\u5c3a\u591a\u9ad8\u3002 \u3000\u3000\u5c0f\u6587\u5c31\u7ad9\u5728\u90a3\u5177\u5c38\u4f53\u524d\u9762\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u770b\u6e05\u695a\u5979\u51e0\u65f6\u51b2\u4e86\u8fc7\u53bb\uff0c\u4e5f\u6ca1\u6709\u4eba\u770b\u5230\u5979\u62d4\u5251\u51fa\u9798\uff0c\u770b\u5230\u7684\u53ea\u662f\u7ed3\u679c\u3002\u5c0f\u6587\u7684\u4e2a \u5b50\u5f88\u77ee\uff0c\u9762\u5bf9\u7684\u654c\u4eba\uff0c\u6bcf\u4e2a\u4eba\u90fd\u6bd4\u4ed6\u9ad8\u4e00\u4e2a\u5934\uff0c\u4f46\u5979\u7684\u8eab\u4e0a\u4f3c\u662f\u6563\u53d1\u4e00\u79cd\u5f3a\u70c8\u7684\u6740\u6c14\uff0c\u534a\u664c \u65f6\u95f4\uff0c\u7adf\u65e0\u4eba\u548c\u5979\u7b54\u8bdd\u3002 \u3000\u3000\u8fd8\u662f\u5c0f\u6587\u5f00\u4e86\u53e3\uff0c\u51b7\u7684\u50cf\u4e5d\u5e7d\u5730\u72f1\u4e2d\u5439\u51fa\u6765\u7684\u5bd2\u98ce\uff0c\u9053\uff1a\u201c\u5e0c\u671b\u4f60\u4eec\u80fd\u6297\u62d2\u4f4f\u6211\u7684\u5251\u62db\uff0c \u6211\u4ece\u6765\u6ca1\u6709\u8fc7\u5982\u6b64\u5f3a\u70c8\u7684\u6740\u4eba\u613f\u671b\uff0c\u4f60\u4eec\u662f\u6536\u94b1\u6740\u4eba\u7684\u6740\u624b\uff0c\u6bcf\u4e2a\u4eba\u90fd\u53cc\u624b\u8840\u8165\uff0c\u6740\u4f60\u4eec\u4e0d \u662f\u7f6a\u6076\uff0c\u6211\u8981\u66ff\u90a3\u4e9b\u5343\u767e\u4f4d\u65e0\u8f9c\u7684\u6b7b\u8005\u62a5\u4ec7\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u5c0f\u675c\uff0c\u5c0f\u6587\u600e\u4e48\u4e86\uff1f\u5df2\u7ecf\u5200\u5934\u67d3\u8840\u4e86\uff0c\u8fd8\u8981\u8bf4\u6559\u4e00\u756a\u3002\u201d \u3000\u3000\u201c\u5979\u8981\u5927\u5927\u5f00\u6740\u6212\u5440\uff01\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5fc3\u4e2d\u4e0d\u5b89\u7684\u5f88\uff0c\u6240\u4ee5\uff0c\u5148\u8981\u4e3a\u81ea\u5df1\u627e\u4e2a\u7406\u7531\uff0c\u4ee5 \u6c42\u5b89\u5fc3\u5566\u2026\u2026\u201d\u58f0\u97f3\u7a81\u8f6c\u4f4e\u5f31\uff0c\u9053\uff1a\u201c\u4eca\u5929\uff0c\u6211\u4eec\u5c06\u770b\u5230\u5c0f\u6587\u771f\u6b63\u7684\u6280\u827a\u4e86\uff0c\u9664\u975e\u5979\u9047\u4e0a\u4e86 \u5371\u9669\uff0c\u4e0d\u7528\u51fa\u624b\u5e2e\u5979\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u70b9\u70b9\u5934\uff0c\u867d\u65e9\u5df2\u77e5\u6653\u5c0f\u6587\u3001\u5c0f\u96c5\u5251\u827a\u5353\u7edd\uff0c\u4f46\u9ad8\u5230\u4f55\u7b49\u7a0b\u5ea6\uff0c\u5374\u65e0\u6cd5\u9884\u6d4b\uff0c\u4eca\u5929 \u5012\u8981\u770b\u4e2a\u4ed4\u7ec6\u4e86\u3002 \u3000\u3000\u5c0f\u6587\u53f3\u624b\u7d27\u63e1\u7740\u5251\u628a\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6740\uff01\u201d\u4e00\u9053\u5251\u5149\u98de\u8d77\uff0c\u51b2\u5165\u4e86\u654c\u65b9\u4eba\u7fa4\u4e4b\u4e2d\u3002 \u3000\u3000\u4f46\u89c1\u5bd2\u5149\u95ea\u8f6c\uff0c\u9635\u9635\u60e8\u53eb\u54cd\u8d77\uff0c\u65ad\u81c2\u5171\u5200\u5251\u9f50\u98de\uff0c\u770b\u7684\u738b\u77e5\u5e9c\u5fc3\u90fd\u51c9\u4e86\uff0c\u547c\u5438\u4e5f\u6025\u4fc3\u8d77 \u6765\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u4e00\u9897\u4eba\u5934\u98de\u6765\uff0c\u76f4\u5411\u738b\u5927\u4eba\u649e\u53bb\uff0c\u4f55\u5927\u5149\u6325\u5200\u4e00\u6321\uff0c\u5374\u628a\u4eba\u5934\u5288\u6210\u4e24\u534a\uff0c\u4e00\u534a \u843d\u5730\uff0c\u4e00\u534a\u64e6\u5200\u800c\u8fc7\u3002 \u3000\u3000\u60dc\u7389\u52a8\u4f5c\u5feb\uff0c\u4e00\u4f38\u624b\uff0c\u63a5\u4f4f\u4e86\u534a\u4e2a\u4eba\u5934\u3002\u4eba\u5934\u662f\u63a5\u4f4f\u4e86\uff0c\u4f46\u6e85\u98de\u7684\u8840\u73e0\uff0c\u5374\u6709\u4e0d\u5c11\u6e85\u5728 \u738b\u5927\u4eba\u7684\u8138\u4e0a\u3002 \u3000\u3000\u738b\u5927\u4eba\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u5410\u4e86\u8d77\u6765\u3002\u3000\u5c0f\u6587\u5251\u6cd5\u592a\u5feb\uff0c\u4e24\u5ea7\u7070\u8863\u4eba\u6392\u6210\u7684 \u9635\u52bf\uff0c\u8fd8\u672a\u6765\u5f97\u53ca\u5e94\u53d8\uff0c\u5df2\u88ab\u5c0f\u6587\u65a9\u6740\u4e86\u56db\u4e2a\uff0c\u903c\u8fd1\u4e8c\u5802\u95e8\u53e3\u7684\u9ed1\u8863\u4eba\uff0c\u4e00\u5171\u6709\u5341\u4e2a\u4eba\uff0c\u66f4 \u662f\u65e0\u4eba\u5e78\u514d\u3002 \u3000\u3000\u201c\u5c0f\u675c\uff0c\u8fd9\u662f\u4ec0\u4e48\u5251\u6cd5\u5440\uff1f\u201d\u5c91\u5578\u864e\u773c\u90fd\u770b\u82b1\u4e86\uff0c\u8fd8\u662f\u770b\u4e0d\u51fa\u4e00\u70b9\u540d\u5802\u3002\u675c\u671b\u6708\u8eab\u7ecf\u767e \u6218\uff0c\u4e5f\u6ca1\u6709\u89c1\u8fc7\u5982\u6b64\u6050\u6016\u7684\u5c60\u622e\uff0c\u82e6\u4e60\u5251\u6cd5\u4e8c\u5341\u5e74\uff0c\u4e5f\u6ca1\u6709\u89c1\u8fc7\u5982\u6b64\u51f6\u5389\u7684\u5251\u6cd5\uff0c\u4e0d\u77e5\u5979\u5982 \u4f55\u9009\u62e9\u51fa\u5251\u7684\u89d2\u5ea6\uff0c\u80fd\u5251\u5251\u53d6\u547d\u3001\u5251\u5251\u67d3\u8840\u3002\u60dc\u7389\u62ed\u53bb\u624b\u4e0a\u7684\u8840\u8ff9\uff0c\u8f7b\u8f7b\u5728\u738b\u5927\u4eba\u80cc\u4e0a\u62cd\u7740\uff0c \u4f4e\u58f0\u9053\uff1a\u201c\u5927\u4eba\uff0c\u6211\u6276\u4f60\u56de\u5385\u5185\u4f11\u606f\u3002\u201d \u3000\u3000\u201c\u4e0d\uff01\u201d\u738b\u5927\u4eba\u9053\uff1a\u201c\u6211\u8981\u770b\u4e2a\u7ed3\u679c\uff0c\u5c0f\u6587\u5251\u5251\u53d6\u547d\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u80fd\u628a\u8fd9\u4e9b\u4eba\u9010\u8d70\uff01\u201d \u3000\u3000\u201c\u56e0\u4e3a\uff0c\u4ed6\u4eec\u662f\u6740\u624b\uff0c\u53ef\u4ee5\u88ab\u6740\uff0c\u4e0d\u53ef\u80fd\u9000\u8d70\uff01\u9664\u975e\uff0c\u6709\u4eba\u53ec\u5524\u4ed6\u4eec\u64a4\u9000\u3002\u201d\u5c0f\u6587\u7a81\u7136 \u98de\u8dc3\u800c\u56de\uff0c\u4eba\u53c8\u7ad9\u5230\u4e86\u738b\u5927\u4eba\u7684\u8eab\u524d\uff0c\u624b\u4e2d\u7684\u957f\u5251\uff0c\u4ecd\u7136\u5411\u4e0b\u6ef4\u7740\u9c9c\u8840\uff0c\u4e00\u53cc\u5927\u773c\u775b\u4e2d\uff0c\u5145 \u6ee1\u7740\u8840\u4e1d\u3002\u5979\u5728\u5f88\u77ed\u65f6\u95f4\u5185\uff0c\u6740\u4e86\u5341\u51e0\u53e3\u4eba\uff0c\u51f6\u6b8b\u9738\u9053\uff0c\u517c\u800c\u6709\u4e4b\uff0c\u4e5f\u597d\u50cf\u53d1\u6cc4\u4e86\u5fc3\u4e2d\u7684\u4e00 \u4e9b\u79ef\u5fff\uff0c\u4f46\u5374\u6ca1\u6709\u80dc\u5229\u7684\u5feb\u4e50\u3002 \u3000\u3000\u5979\u795e\u60c5\u51b7\u8083\uff0c\u539f\u672c\u79c0\u4e3d\u751c\u7f8e\u7684\u8138\u5e9e\uff0c\u4e5f\u56e0\u6d53\u91cd\u7684\u6740\u6c14\u53d8\u7684\u6709\u4e9b\u51b7\u9177\u3002 \u3000\u3000\u738b\u5c11\u537f\u7a81\u7136\u6253\u4e86\u4e00\u4e2a\u51b7\u98a4\uff0c\u53ea\u89c9\u5c0f\u6587\u4e24\u9053\u76ee\u5149\uff0c\u9510\u5229\u5982\u5251\uff0c\u76f4\u523a\u5411\u4ed6\u7684\u5185\u5fc3\u3002\u662f\u7684\uff01\u5c0f \u6587\u53d7\u4e86\u4e0d\u5c11\u8f7b\u85d0\u3001\u5c48\u8fb1\uff0c\u5fc3\u4e2d\u4e00\u76f4\u5bf9\u738b\u5927\u4eba\u6709\u51e0\u5206\u6de1\u6de1\u7684\u6068\u610f\uff0c\u4f46\u5982\u4eca\u8fd9\u4e9b\u6068\u610f\uff0c\u5f00\u59cb\u53d1\u9175\uff0c \u65e0\u6cd5\u63a7\u5236\uff0c\u5c31\u81ea\u7136\u6d41\u73b0\u51fa\u6765\u4e86\uff0c\u4e5f\u53d8\u7684\u5f3a\u70c8\u4e86\uff0c\u4f3c\u662f\u5df2\u52a8\u4e86\u6325\u5251\u6c42\u4e00\u5feb\u7684\u6740\u673a\u3002 \u3000\u3000\u60f3\u5230\u5c0f\u6587\u6740\u4eba\u5982\u5207\u83dc\uff0c\u738b\u5927\u4eba\u8fde\u58f0\u97f3\u4e5f\u6709\u70b9\u6296\u4e86\uff0c\u9053\uff1a\u201c\u5c0f\u6587\u2026\u2026\u5c0f\u6587\u2026\u2026\u6587\u59d1\u5a18\uff0c\u6709 \u8bdd\u2026\u2026\u597d\u597d\u2026\u2026\u8bf4\u3002\u201d \u3000\u3000\u60dc\u7389\u4e5f\u89c9\u7740\u4e0d\u5bf9\u4e86\uff0c\u6b64\u523b\u7684\u5c0f\u6587\u59d1\u5a18\uff0c\u4f3c\u662f\u6740\u7ea2\u4e86\u773c\u775b\uff0c\u6709\u7740\u89c1\u4eba\u5c31\u60f3\u6740\u7684\u51b2\u52a8\uff0c\u5c24\u5176 \u662f\u5979\u5fc3\u4e2d\u6709\u70b9\u8bb0\u6068\u7684\u4eba\u3002 \u3000\u3000\u675c\u671b\u6708\u3001\u5434\u94c1\u5cf0\uff0c\u90fd\u662f\u8b66\u89c9\u4e4b\u5fc3\u5947\u9ad8\u7684\u4eba\uff0c\u611f\u89c9\u5230\u4e0d\u5bf9\uff0c\u7acb\u523b\u5411\u4e2d\u95f4\u9760\u6765\u3002\u201c\u671b\u6708\uff0c\u5c0f \u6587\u59d0\u6709\u4e9b\u4e0d\u5bf9\u52b2\u554a\uff01\u201d\u60dc\u7389\u9053\uff1a\u201c\u597d\u50cf\u6709\u70b9\u75af\u4e86\uff01\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u7684\uff01\u5c0f\u6587\u59d1\u5a18\u5185\u529f\u7cbe\u6e5b\uff0c\u600e\u4e48\u7a81\u7136\u75af\u4e86\u5462\uff1f\u201d\u675c\u671b\u6708\u4e00\u9762\u7559\u5fc3\u5c0f\u6587\u795e\u60c5\u7684\u53d8\u5316\uff0c \u4e00\u9762\u8fd8\u8981\u517c\u987e\u654c\u60c5\uff0c\u770b\u5bf9\u65b9\u662f\u5426\u6709\u6240\u884c\u52a8\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u675c\u671b\u6708\u548c\u5434\u94c1\u5cf0\uff0c\u4e5f\u4ece\u5c0f\u6587\u4e24\u9053\u76ee\u5149\u4e2d\u53d1\u89c9\u4e86\u6d53\u91cd\u7684\u6740\u673a\uff0c\u4f3c\u4e4e\u662f\u968f\u65f6\u90fd\u53ef\u4ee5\u51fa \u624b\uff0c\u6740\u4e86\u738b\u5c11\u537f\u3002\u56e0\u4e3a\uff0c\u5c0f\u6587\u90a3\u4e00\u53cc\u9ed1\u767d\u5206\u660e\u7684\u5927\u773c\u775b\u4e2d\uff0c\u66b4\u5c04\u51fa\u4e86\u6bd2\u86c7\u4e00\u822c\u7684\u5149\u8292\uff0c\u4e00\u76f4 \u672a\u79bb\u5f00\u8fc7\u738b\u5c11\u537f\uff0c\u4e0d\u4f46\u770b\u7684\u738b\u5c11\u537f\u4e00\u8eab\u51b7\u6c57\uff0c\u675c\u671b\u6708\u548c\u5434\u94c1\u5cf0\u4e5f\u88ab\u5c0f\u6587\u7684\u4e3e\u6b62\u5413\u51fa\u4e86\u4e00\u8eab\u51b7 \u6c57\u3002 \u3000\u3000\u5982\u82e5\u5c0f\u6587\u7a81\u7136\u51fa\u624b\uff0c\u4ee5\u5979\u7684\u5251\u62db\u4e4b\u5feb\uff0c\u53d8\u5316\u4e4b\u5947\uff0c\u675c\u671b\u6708\u3001\u5434\u94c1\u5cf0\u90fd\u6ca1\u6709\u628a\u63e1\u80fd\u4e00\u5251\u62e6 \u4f4f\u5c0f\u6587\u5251\u52bf\u3002 \u3000\u3000\u4e00\u4e2a\u975e\u5e38\u53ef\u6015\u7684\u5ff5\u5934\uff0c\u5728\u675c\u671b\u6708\u7684\u8111\u9645\u95ea\u8fc7\uff0c\u201c\u68a6\u5e7b\u4e4b\u5200\u201d\uff0c\u8fd9\u662f\u4e0d\u662f\u201c\u68a6\u5e7b\u4e4b\u5200\u201d\u7684 \u9b45\u529b\uff0c\u5728\u5f79\u65bd\u7740\u5c0f\u6587\u3002 \u3000\u3000\u4e0d\u7ba1\u5982\u4f55\uff0c\u5f97\u5148\u60f3\u4e2a\u529e\u6cd5\uff0c\u5236\u4f4f\u5c0f\u6587\uff0c\u4e0d\u80fd\u8ba9\u5979\u6740\u5bb3\u4e86\u738b\u5927\u4eba\u3002 \u3000\u3000\u90a3\u5c06\u662f\u5c3d\u503e\u4e5d\u6c5f\u4e4b\u6c34\uff0c\u4e5f\u65e0\u6cd5\u6d17\u6e05\u7684\u51a4\u5c48\uff0c\u7a0b\u5c0f\u8776\u4e5f\u62c5\u4e0d\u8d77\uff0c\u4f55\u51b5\uff0c\u505a\u6355\u5934\u7684\u6740\u4e86\u4ed6\u4eec \u5168\u529b\u4fdd\u62a4\u7684\u4eba\uff0c\u53ef\u662f\u5343\u53e4\u5211\u6848\u7684\u5927\u7b11\u8bdd\u3002 \u3000\u3000\u675c\u671b\u6708\u51b3\u5fc3\u4e00\u8bd5\u4e86\uff0c\u4e24\u5bb3\u76f8\u6743\u53d6\u5176\u8f7b\uff0c\u5b81\u53ef\u4e2a\u4eba\u5931\u8d25\u635f\u547d\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u6574\u4e2a\u5211\u6355\u53f8\u53d7\u5230\u4f24 \u5bb3\u3002 \u3000\u3000\u4e8b\u60c5\u7a81\u7136\u7684\u5927\u8f6c\u5f2f\uff0c\u4f7f\u5f97\u675c\u671b\u6708\u7684\u5fc3\u60c5\u6c89\u91cd\u6781\u4e86\uff0c\u65e2\u4e0d\u80fd\u8bf4\uff0c\u4e5f\u4e0d\u80fd\u6709\u5f02\u6837\u7684\u8868\u793a\uff0c\u7f13 \u6b65\u884c\u8fd1\u5c0f\u6587\uff0c\u4e00\u9762\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u6587\u59d1\u5a18\uff0c\u603b\u6355\u5934\u53ef\u6709\u5429\u5490\uff0c\u8981\u6211\u4eec\u5982\u4f55\u5e94\u4ed8\u60c5\u5f62\u7684\u53d8\u5316\u3002\u201d \u3000\u3000\u5c0f\u6587\u7684\u4eba\u4f3c\u5728\u53d1\u6296\uff0c\u76ee\u5149\u4e2d\u7684\u6076\u6bd2\u4e4b\u5149\u4e5f\u66f4\u52a0\u5389\u70c8\u3002 \u3000\u3000\u4e00\u76f4\u7559\u5fc3\u7740\u675c\u671b\u6708\u884c\u52a8\u7684\u60dc\u7389\uff0c\u4e5f\u5df2\u770b\u51fa\u4e86\u60c5\u52bf\u6b63\u5728\u5267\u70c8\u7684\u53d8\u5316\u4e2d\uff01\u4f46\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u4e5f \u628a\u4e0d\u4f4f\u65b9\u5411\uff0c\u6b63\u60f3\u547c\u53eb\u675c\u671b\u6708\uff0c\u4f46\u5374\u88ab\u5584\u89c2\u53d8\u5316\u3001\u8001\u4e8e\u4e16\u6545\u7684\u5434\u94c1\u5cf0\u963b\u6b62\u4e86\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u60dc \u7389\u59d1\u5a18\uff0c\u5168\u529b\u4fdd\u62a4\u738b\u5927\u4eba\uff01\u201d\u60dc\u7389\u5401\u53e3\u6c14\uff0c\u70b9\u70b9\u5934\u3002 \u3000\u3000\u201c\u603b\u6355\u5934\uff0c\u201d\u5c0f\u6587\u832b\u7136\u5e94\u4e86\u4e00\u53e5\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e0d\u662f\u56de\u6765\u4e86\u5417\uff1f\u201d\u675c\u671b\u6708\u4f38\u624b\u5411\u5c0f\u6587\u53f3\u540e\u4fa7\u6307\u53bb\u3002 \u3000\u3000\u5c0f\u6587\u4e00\u56de\u5934\uff0c\u675c\u671b\u6708\u6c89\u8155\u4e00\u6307\uff0c\u70b9\u4e2d\u4e86\u5c0f\u6587\u7684\u201c\u8f6f\u9ebb\u7a74\u201d\u3002 \u3000\u3000\u786e\u5b9a\u4e86\u7a74\u9053\u70b9\u4e2d\uff0c\u5c0f\u6587\u4eba\u4e5f\u6655\u4e86\u8fc7\u53bb\uff0c\u675c\u671b\u6708\u6ee1\u5934\u7684\u5927\u6c57\uff0c\u624d\u6eda\u6eda\u843d\u4e0b\uff0c\u62b1\u7740\u5c0f\u6587\uff0c\u5feb \u901f\u5730\u95ea\u5165\u4e8c\u5802\u3002 \u3000\u3000\u738b\u5927\u4eba\u4e5f\u8ddf\u4e86\u8fdb\u6765\uff0c\u9053\uff1a\u201c\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f\u5c0f\u6587\u59d1\u5a18\uff0c\u597d\u50cf\u5931\u53bb\u4e86\u63a7\u5236\u81ea\u5df1\u7684\u80fd\u529b\uff01\u201d \u3000\u3000\u4f55\u5927\u5149\u662f\u738b\u5c11\u537f\u7ea6\u8bf7\u7684\u4fdd\u9556\uff0c\u672c\u60f3\u8ddf\u7740\u738b\u5c11\u537f\u4e00\u8d77\u8fdb\u5165\u4e8c\u5802\uff0c\u5374\u88ab\u738b\u575a\u4e00\u628a\u62d6\u4f4f\uff0c\u9053\uff1a \u201c\u4f55\u5144\u5f1f\uff0c\u7559\u8fd9\u91cc\u5e2e\u5e2e\u6211\u3002\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4f55\u5927\u5149\u4e5f\u5df2\u611f\u89c9\u5230\u8fd9\u4e9b\u4eba\u4e2d\uff0c\u4e2a\u4e2a\u6b66\u529f\u9ad8\u4ed6\u5f88\u591a\uff0c\u8fd1\u8eab\u4fdd\u62a4\u738b\u5927\u4eba\u7684\u4e8b\uff0c\u81ea\u5df1 \u5b9e\u5df2\u4e0d\u9002\u4efb\u4e86\u3002 \u3000\u3000\u60dc\u7389\u5df2\u8ddf\u4e86\u8fdb\u6765\uff0c\u7531\u675c\u671b\u6708\u624b\u4e2d\u63a5\u8fc7\u4e86\u5c0f\u6587\u59d1\u5a18\u3002 \u3000\u3000\u201c\u5927\u4eba\u4e5f\u53d1\u89c9\u4e86\u5c0f\u6587\u59d1\u5a18\u7684\u4e00\u4e9b\u8f6c\u53d8\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5927\u4eba\u6709\u4f55\u770b\u6cd5\u5462\uff1f\u201d \u3000\u3000\u201c\u6709\u75c5\uff0c\u4e00\u79cd\u5f88\u5947\u602a\u7684\u75c5\u3002\u201d\u738b\u5927\u4eba\u9053\uff1a\u201c\u4f46\u5c0f\u6587\u59d1\u5a18\u4e5f\u8868\u73b0\u4e86\u7cbe\u5947\u7edd\u4f26\u7684\u6b66\u529f\uff0c\u770b\u4e0a \u53bb\u6050\u6016\u6781\u4e86\uff0c\u4f3c\u662f\u654c\u4eba\u90fd\u5728\u4f38\u7740\u8111\u888b\uff0c\u7b49\u5979\u53bb\u6740\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\u7684\u5b66\u8bc6\u6e0a\u535a\uff0c\u770b\u51fa\u4e86\u75c5\u60c5\u5f81\u5019\u6ca1\u6709\uff1f\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u75c5\u60c5\u6765\u7684\u65e2\u7a81\u7136\uff0c\u53c8\u975e\u5e38\u5947 \u602a\u2026\u2026\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u597d\u50cf\u4e0b\u5b98\u4e5f\u5f71\u54cd\u4e86\u5c0f\u6587\u59d1\u5a18\u7684\u75c5\u60c5\uff0c\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u4e0b\u5b98\u5012\u662f\u660e\u767d\uff0c\u8fd1 \u65e5\u4e4b\u4e2d\uff0c\u6211\u786e\u6709\u5931\u793c\u4e4b\u5904\uff0c\u4f7f\u5c0f\u6587\u59d1\u5a18\uff0c\u5fc3\u4e2d\u53d7\u5230\u4e86\u4e00\u70b9\u632b\u4f24\uff0c\u4f46\u56e0\u6b64\u75c5\u5012\uff0c\u5374\u53c8\u2026\u2026\u201d \u3000\u3000\u201c\u5c0f\u6587\u59d1\u5a18\u7ecf\u53d7\u5927\u4eba\u8f7b\u85d0\uff0c\u4f24\u4e86\u5979\u4e00\u7247\u5fe0\u8bda\u81ea\u5c0a\uff0c\u4e5f\u4e0d\u81f4\u56e0\u6b64\u53d1\u75c5\uff0c\u6211\u6000\u7591\u2026\u2026\u201d\u675c\u671b \u6708\u9053\uff1a\u201c\u662f\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\u7684\u672f\u6cd5\u5f15\u8d77\uff1f\u201d \u3000\u3000\u8fd9\u7684\u786e\u662f\u4e2a\u53eb\u4eba\u9707\u60ca\u7684\u8baf\u606f\uff0c\u5434\u94c1\u5cf0\u6574\u4e2a\u4eba\u90fd\u542c\u5f97\u5446\u4f4f\u4e86\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u5012\u662f\u5927\u6709\u53ef \u80fd\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u5c0f\u6587\u59d1\u5a18\u8fd9\u6b21\u65bd\u5c55\u7684\u5251\u6cd5\uff0c\u4e5f\u90aa\u6c14\u7684\u5389\u5bb3\uff0c\u5434\u67d0\u5927\u534a\u751f\u5728\u6c5f\u6e56\u884c\u811a\uff0c\u53ea\u89c1\u8fc7 \u4e00\u6b21\u5982\u6b64\u90aa\u6076\u7684\u5251\u6cd5\uff0c\u5c0f\u6587\u59d1\u5a18\u4eca\u53e3\u65bd\u5c55\u7684\u5251\u62db\uff0c\u9887\u4f3c\u90a3\u5957\u5251\u6cd5\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u5c31\u5feb\u8bf7\u8bf4\u51fa\u6765\uff0c\u770b\u770b\u80fd\u4e0d\u80fd\u7ed9\u675c\u67d0\u4e00\u70b9\u542f\u793a\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u5c0f\u6587\u59d0\uff0c\u597d\u50cf\u670d\u98df\u4e86\u4e00\u79cd\u836f\u7269\uff1f\u201d\u4e00\u76f4\u5728\u4e0d\u505c\u67e5\u770b\u5c0f\u6587\u8033\u3001\u9f3b\u3001\u53e3\u3001\u820c\u3001\u773c\u7684\u60dc\u7389\uff0c \u7a81\u7136\u63d2\u4e86\u4e00\u53e5\u3002 \u3000\u3000\u8fd9\u4e00\u53e5\u8bdd\uff0c\u53c8\u5982\u5de8\u77f3\u6295\u6e56\uff0c\u9707\u8361\u8d77\u5de8\u5927\u7684\u6ce2\u6d6a\uff0c\u675c\u671b\u6708\u9053\uff1a\u201c\u836f\u7269\uff0c\u662f\u4e00\u79cd\u8ff7\u5931\u672c\u6027\u7684 \u836f\u7269\u4e86\uff0c\u4f46\u4f60\u548c\u5c0f\u6587\u59d1\u5a18\uff0c\u65e5\u5e38\u751f\u6d3b\u4e00\u5904\uff0c\u600e\u4f1a\u8ba9\u5979\u4e00\u4e2a\u541e\u4e0b\u836f\u7269\uff1f\u201d \u3000\u3000\u5434\u94c1\u5cf0\u5fc3\u4e2d\u5fd6\u9053\uff1a\u53ea\u662f\u4e0d\u5c0f\u5fc3\u5403\u4e0b\u836f\u7269\uff0c\u90a3\u5012\u5bb9\u6613\u9632\u8303\uff0c\u8981\u662f\u201c\u68a6\u5e7b\u4e4b\u5200\u201d\u7684\u5f79\u5200\u672f\u6cd5\uff0c \u6709\u6b64\u9b45\u529b\uff0c\u90a3\u5c31\u5927\u5927\u7684\u9ebb\u70e6\u4e86\u3002 \u3000\u3000\u201c\u5c0f\u6587\u59d1\u5a18\u7684\u8336\u6c34\u9910\u98df\uff0c\u90fd\u548c\u4e0b\u5b98\u5728\u4e00\u8d77\uff0c\u5982\u662f\u6709\u4eba\u4e0b\u6bd2\uff0c\u600e\u4f1a\u53ea\u6bd2\u4e86\u5c0f\u6587\u59d1\u5a18\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u795e\u60c5\u8083\u7136\u7684\uff0c\u9053\uff1a\u201c\u8fd9\u8981\u4ed4\u7ec6\u5730\u67e5\u4e2a\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u662f\u7684\uff0c\u80fd\u5728\u996e\u98df\u8336\u6c34\u4e2d\u4e0b\u6bd2\uff0c\u6bd2\u4f24\u4e86\u5c0f\u6587\uff0c\u4e5f\u53ef\u4ee5\u6bd2\u4e86\u4ed6\u738b\u5c11\u537f\u554a\uff01 \u3000\u3000\u201c\u4e0b\u6bd2\u7684\u65b9\u6cd5\u5f88\u591a\uff0c\u4e0d\u4e00\u5b9a\u8981\u4e0b\u5728\u996e\u98df\u4e4b\u4e2d\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u5c0f\u6587\u59d0\u4e2d\u6bd2\u7684\u6bd2\u6027\uff0c\u5f88\u6d45\uff0c \u4e0d\u6613\u53d1\u4f5c\uff0c\u5982\u975e\u6709\u4e00\u79cd\u5916\u529b\u8bf1\u53d1\uff0c\u4e5f\u53ef\u80fd\u538b\u5236\u4e0b\u53bb\uff0c\u4e0d\u4f1a\u53d1\u4f5c\uff0c\u4f46\u4ec0\u4e48\u529b\u91cf\u80fd\u8bf1\u4f7f\u5c0f\u6587\u7684\u6bd2 \u6027\u53d1\u4f5c\u5462\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u662f\u2018\u68a6\u5e7b\u4e4b\u5200\u2019\u7684\u5f79\u5200\u672f\u7684\u9b45\u529b\uff0c\u8bf1\u53d1\u4e86\u6bd2\u6027\uff1f\u201d \u3000\u3000\u201c\u8fd9\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u6211\u53ea\u61c2\u4e00\u4e9b\u836f\u7406\uff0c\u770b\u5f97\u51fa\u4e00\u4e9b\u5f81\u50cf\uff0c\u81f3\u4e8e\u5982\u4f55\u8bf1\u53d1\u6bd2 \u6027\uff0c\u5c31\u975e\u6211\u4e4b\u529b\u6240\u53ca\u4e86\u3002\u201d \u3000\u3000\u201c\u5c0f\u6587\u59d1\u5a18\u65bd\u5c55\u51fa\u7684\u90aa\u6076\u5251\u672f\uff0c\u4e5f\u53ef\u80fd\u662f\u8bf1\u4f7f\u5979\u9690\u85cf\u6bd2\u6027\u7684\u539f\u56e0\u4e86\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u90a3 \u5957\u5251\u6cd5\u7684\u51f6\u6b8b\uff0c\u5b9e\u5df2\u5165\u90aa\u6076\u4e4b\u5883\uff0c\u597d\u597d\u7684\u4eba\uff0c\u4e5f\u4f1a\u88ab\u7ec3\u51fa\u6765\u6f5c\u9690\u7684\u90aa\u6076\u672c\u8d28\u3002\u201d \u3000\u3000\u201c\u7ec3\u6b66\u529f\u4e5f\u6709\u5982\u6b64\u7981\u5fcc\uff1f\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u8fd9\u5c31\u548c\u770b\u4e66\u4e00\u6837\u7684\u4e86\uff0c\u8981\u6709\u6240\u9009\u62e9\uff0c\u5c3d\u8bfb\u4e9b\u7b26 \u5492\u6cd5\u5f55\u7684\u602a\u4e66\uff0c\u4eba\u4e5f\u4f1a\u9677\u5165\u5f79\u9b3c\u9063\u795e\u7684\u8ff7\u79bb\u4e4b\u5883\uff0c\u6e3a\u4e0d\u53ef\u671f\u7684\u5947\u6cd5\u5f02\u672f\u4e4b\u4e2d\uff0c\u5509\uff01\u4f46\u65c1\u95e8\u5de6 \u9053\u7684\u602a\u4e66\uff0c\u53c8\u5177\u6709\u7740\u5f3a\u5927\u7684\u5438\u5f15\u4e4b\u529b\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u5fc3\u4e2d\u5fd6\u601d\uff0c\u8fd9\u4e2a\u738b\u5927\u4eba\u54ea\uff01\u4e0d\u4f46\u662f\u8bfb\u901a\u4e86\u4e94\u7ecf\u56db\u4e66\uff0c\u4f3c\u662f\u4e5f\u8bfb\u4e86\u6742\u4e66\u4e0d\u5c11\uff0c\u662f\u4e2a \u80f8\u7f57\u4e07\u6709\u7684\u4eba\u7269\u2026\u2026\u3002 \u3000\u3000\u201c\u5c0f\u6587\u59d1\u5a18\u4ece\u54ea\u91cc\u5b66\u5230\u8fd9\u5957\u5251\u6cd5\u5462\uff1f\u201d\u5434\u94c1\u5cf0\u795e\u60c5\u5fe7\u82e6\u5730\u9053\uff1a\u201c\u5c0f\u6587\u4f1a\u4e86\uff1f\u5c0f\u96c5\u5462\uff1f\u603b \u6355\u5934\u5462\uff1f\u201d \u3000\u3000\u201c\u5434\u5144\u5bf9\u8fd9\u5957\u5251\u6cd5\u4f3c\u662f\u975e\u5e38\u91cd\u89c6\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u8bf4\u7684\u6e05\u9192\u4e00\u4e9b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u80fd\uff01\u56e0\u4e3a\uff0c\u6211\u5b8c\u5168\u4e0d\u4e86\u89e3\u8fd9\u5957\u5251\u6cd5\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6211\u53ea\u662f\u89c1\u4eba\u65bd\u5c55\u8fc7\u4e00\u6b21\u2026\u2026\u201d \u7a81\u7136\u95f4\u9690\u5165\u6c89\u601d\uff0c\u51dd\u795e\u4e0d\u8bed\u3002 \u3000\u3000\u53ea\u542c\u738b\u575a\u526f\u603b\u6355\u5934\u5927\u58f0\u559d\u9053\uff1a\u201c\u5927\u80c6\u532a\u5f92\uff0c\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\uff0c\u7adf\u6562\u6301\u68b0\u884c\u51f6\uff0c\u2026\u2026\u201d \u3000\u3000\u675c\u671b\u6708\u63a5\u9053\uff1a\u201c\u4fdd\u62a4\u5927\u4eba\u3002\u201d\u53f3\u624b\u8fde\u6325\uff0c\u53c8\u70b9\u4e86\u5c0f\u6587\u4e24\u5904\u7a74\u9053\uff0c\u4eba\u5df2\u75be\u5feb\u51b2\u5230\u95e8\u5916\u3002 \u3000\u3000\u5df2\u6709\u4e24\u4e2a\u9ed1\u8863\u4eba\u6740\u5230\u4e86\u4e8c\u5802\u95e8\u53e3\u3002 \u3000\u3000\u738b\u575a\u3001\u4f55\u5927\u5149\uff0c\u6b63\u548c\u4e24\u4eba\u6740\u7684\u96be\u5206\u96be\u89e3\uff0c\u516b\u4e2a\u624b\u6267\u7ea2\u7f28\u67aa\u7684\u4eb2\u5175\uff0c\u5df2\u9000\u5230\u4e8c\u5802\u95e8\u5185\uff0c\u53cc \u624b\u6301\u67aa\uff0c\u7b49\u673a\u4f1a\u51fa\u624b\u3002 \u3000\u3000\u738b\u575a\u548c\u4f55\u5927\u5149\u7684\u6b66\u529f\uff0c\u4f3c\u662f\u5728\u4f2f\u3001\u4ef2\u4e4b\u95f4\uff0c\u4e24\u4e2a\u4eba\u90fd\u5728\u62da\u5c3d\u5168\u529b\u652f\u6301\u7740\u4e0d\u80af\u540e\u9000\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u7528\u7684\u5200\uff0c\u91d1\u5149\u95ea\u95ea\uff0c\u5c31\u7b97\u4e0d\u662f\u5168\u7528\u9ec4\u91d1\u94f8\u6210\u7684\u5200\uff0c\u4f46\u4e5f\u9540\u4e0a\u4e86\u4e00\u5c42\u5f88\u539a\u7684\u91d1\u672b\uff0c \u770b\u4e0a\u53bb\u5200\u52bf\u5341\u5206\u6c89\u91cd\u3002 \u3000\u3000\u675c\u671b\u6708\u7684\u4e03\u661f\u5251\u3001\u5434\u94c1\u5cf0\u7684\u6587\u660c\u7b14\uff0c\u53ca\u65f6\u800c\u51fa\uff0c\u67b6\u5f00\u4e86\u4e24\u67c4\u91d1\u5200\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u738b\u575a\u548c\u4f55\u5927\u5149\uff0c\u5df2\u63a5\u4e0b\u4e86\u5bf9\u65b9\u4e94\u5200\uff0c\u518d\u63a5\u8fd9\u7b2c\u516d\u5200\u5df2\u6709\u7740\u529b\u96be\u4ece\u5fc3\u4e4b\u611f\uff0c\u4f46\u53c8 \u4e0d\u80fd\u9000\u5440\uff01\u4e00\u9000\u5c31\u8ba9\u51fa\u4e86\u4e8c\u5802\u5927\u95e8\uff0c\u5982\u8ba9\u654c\u4eba\u51b2\u5165\u4e8c\u5802\uff0c\u4f1a\u662f\u4e2a\u4ec0\u4e48\u6837\u5b50\u7684\u540e\u679c\uff0c\u5c31\u5f88\u96be\u9884 \u6599\u4e86\u3002\u5f53\u7136\uff0c\u6700\u5927\u7684\u51f6\u4e8b\uff0c\u662f\u6709\u53ef\u80fd\u6740\u5bb3\u4e86\u738b\u5927\u4eba\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u4eec\u62da\u547d\u62d2\u654c\uff0c\u51c6\u5907\u6218\u6b7b\uff0c\u4e5f\u4e0d\u540e\u9000\u3002 \u3000\u3000\u5e78\u597d\uff0c\u675c\u671b\u6708\u3001\u5434\u94c1\u5cf0\u53ca\u65f6\u5730\u5e2e\u4ed6\u4eec\u63a5\u4e0b\u4e24\u5200\u3002 \u3000\u3000\u8fd9\u65f6\u4e00\u6279\u65b0\u6765\u7684\u9ed1\u8863\u4eba\uff0c\u552f\u4e00\u7684\u4e0d\u540c\u5904\u662f\u4ed6\u4eec\u624b\u4e2d\u7684\u5175\u5203\u662f\u91d1\u8272\u4e4b\u5200\u3002 \u3000\u3000\u675c\u671b\u6708\u63a5\u4e0b\u4e00\u5200\uff0c\u7acb\u523b\u53cd\u51fb\uff0c\u5251\u52bf\u8fc5\u82e5\u77f3\u706b\u5730\u56de\u653b\u91d1\u5200\uff0c\u4f46\u7acb\u523b\u611f\u89c9\u5230\u8fd9\u6279\u65b0\u6765\u9ed1\u8863\u4eba \u7684\u6b66\u529f\uff0c\u76f8\u5f53\u9ad8\u660e\uff0c\u4e03\u661f\u5251\u53cd\u51fb\uff0c\u5e76\u672a\u628a\u654c\u4eba\u903c\u9000\u4e00\u6b65\u3002 \u3000\u3000\u4f46\u9ed1\u8863\u4eba\u7740\u7740\u903c\u8fdb\u7684\u653b\u52bf\uff0c\u4e5f\u88ab\u963b\u4e8e\u4e8c\u5802\u95e8\u5916\uff0c\u96be\u518d\u903c\u8fdb\u4e00\u6b65\u3002 \u3000\u3000\u675c\u671b\u6708\u5f88\u5feb\u4e86\u7136\u5230\u8fd9\u4e9b\u91d1\u5200\u4eba\u7684\u6b66\u529f\uff0c\u5e94\u5c5e\u6c5f\u6e56\u4e2d\u4e00\u6d41\u9ad8\u624b\uff0c\u548c\u90a3\u4e9b\u7070\u8863\u6740\u624b\u4e0d\u540c\uff0c\u548c \u5148\u524d\u51fa\u73b0\u7684\u9ed1\u8863\u4eba\u4e5f\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4ee5\u5200\u6cd5\u5b9a\u54c1\u7ea7\uff0c\u8fd9\u5e94\u8be5\u662f\u7b2c\u4e00\u6d41\u7684\u6740\u624b\u3002\u5982\u6b64\u6b66\u529f\uff0c\u4e5f\u4e0d\u662f\u77ed\u671f\u80fd\u6709\u7684\u6210\u5c31\uff0c\u8fd9 \u4e9b\u6740\u624b\u4e5f\u53ef\u660e\u663e\u5730\u5206\u51fa\u7b49\u7ea7\u3002\u7070\u8863\u7684\uff0c\u662f\u7ec4\u5408\u7ea7\u7684\u6740\u624b\uff0c\u4e2a\u4eba\u884c\u52a8\uff0c\u4e0d\u582a\u4e00\u51fb\uff0c\u968f\u65f6\u90fd\u8981\u51fa \u52a8\u7ec4\u5408\u9635\u52bf\uff0c\u624d\u80fd\u6709\u5f3a\u5927\u7684\u6297\u654c\u80fd\u529b\u3002\u7a7f\u9ed1\u8863\u7684\uff0c\u6709\u4e2a\u4eba\u884c\u52a8\u7684\u80fd\u529b\uff0c\u4f46\u6b66\u529f\u5374\u6709\u7740\u5f88\u5927\u7684 \u533a\u522b\u3002\u91d1\u5200\u6740\u624b\u5f88\u9ad8\u660e\uff0c\u4f46\u8fd8\u65e0\u6cd5\u5224\u5b9a\u4ed6\u4eec\u662f\u5426\u662f\u6740\u624b\u4e4b\u738b\u8840\u624b\u65b9\u8f6e\u624b\u4e0b\u6700\u9ad8\u7ea7\u7684\u6740\u624b\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u7edd\u975e\u65b9\u8f6e\u8bad\u7ec3\u51fa\u6765\u7684\u4eba\uff0c\u4ed6\u4eec\u53ef\u80fd\u6765\u81ea\u6c5f\u6e56\u4e0a\u5404\u5927\u95e8\u6d3e\u3001\u5e2e\u4f1a\u9ad8\u624b\u3001\u7eff\u6797\u5927\u76d7\uff0c \u65bd\u7528\u7edf\u4e00\u5b9a\u5236\u7684\u91d1\u5200\uff0c\u53ef\u4ee5\u63a9\u9970\u53bb\u4ed6\u4eec\u7684\u51fa\u8eab\u3001\u6765\u5386\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u5e94\u8be5\u662f\u975e\u5e38\u4e0d\u597d\u5bf9\u4ed8\u7684\u4eba\uff0c\u65b9\u8f6e\u7528\u4ec0\u4e48\u65b9\u6cd5\uff0c\u80fd\u628a\u4ed6\u4eec\u7f57\u81f4\u5728\u624b\u4e0b\u3002\u8fd9\u4e9b\u4eba\u672c\u8eab \u5c31\u4f1a\u4e25\u9632\u8eab\u4efd\u6cc4\u6f0f\uff0c\u6613\u5bb9\u662f\u5fc5\u7ecf\u7684\u624b\u7eed\uff0c\u4ed6\u4eec\u4e5f\u6bd4\u65b9\u8f6e\u66f4\u91cd\u89c6\u4fdd\u5bc6\u81ea\u5df1\u7684\u8eab\u4efd\u3002 \u3000\u3000\u6700\u91cd\u8981\u662f\uff0c\u8840\u624b\u65b9\u8f6e\u5982\u4f55\u624d\u80fd\u8981\u4ed6\u4eec\u7518\u613f\u6548\u547d\uff0c\u662f\u80c1\u8feb\uff1f\u8fd8\u662f\u8bf1\u63a7\uff1f\u4e00\u5b9a\u6709\u4e00\u79cd\u51fa\u4eba\u610f \u6599\u7684\u65b9\u6cd5\uff0c\u624d\u80fd\u5927\u89c4\u6a21\u7f57\u81f4\u8fd9\u4e9b\u9ad8\u624b\u3002 \u3000\u3000\u76ee\u5149\u8f6c\u52a8\uff0c\u6697\u6570\u5df2\u88ab\u5c0f\u6587\u59d1\u5a18\u6740\u6b7b\u4e86\u5341\u4f59\u4e2a\u7684\u9ed1\u8863\u4eba\u4ecd\u7136\u4f0f\u5c38\u73b0\u573a\uff0c\u4f46\u770b\u4e0a\u53bb\u9ed1\u8863\u4eba\u7684 \u6570\u91cf\uff0c\u5e76\u672a\u51cf\u5c11\uff0c\u90a3\u662f\u8bf4\uff0c\u81f3\u5c11\u6709\u5341\u4e2a\u4ee5\u4e0a\u7684\u9ed1\u8863\u4eba\u8865\u5145\u4e86\u8fdb\u6765\uff0c\u8fd9\u4e9b\u4eba\u8863\u7740\u989c\u8272\u76f8\u540c\uff0c\u751a \u81f3\u8fde\u753b\u51fa\u7684\u9762\u5b54\u4e5f\u5dee\u4e0d\u591a\uff0c\u8fd9\u4e0d\u662f\u7cbe\u81f4\u7684\u6613\u5bb9\u672f\uff0c\u76ee\u7684\u53ea\u662f\u4e0d\u8ba9\u4eba\u77a7\u51fa\u81ea\u5df1\u662f\u8c01\u3002 \u3000\u3000\u5927\u6279\u4eba\u624b\u6df7\u5728\u4e00\u5904\uff0c\u8863\u7740\u5f62\u8c8c\u76f8\u4f3c\uff0c\u4e5f\u5c31\u66f4\u96be\u5206\u8fa8\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u5fc3\u4e2d\u5fd6\u9053\uff1a\u6211\u4eec\u65e0\u6cd5\u5206\u8fa8\u51fa\u4ed6\u4eec\u7684\u8eab\u4efd\uff0c\u8981\u7b49\u5230\u4eae\u51fa\u5175\u5203\uff0c\u624d\u80fd\u770b\u51fa\u4ed6\u662f\u4e0d\u662f\u91d1 \u5200\u54c1\u7ea7\u7684\u6740\u624b\uff0c\u4f46\u4ed6\u4eec\u81ea\u5df1\u4eba\u5462\uff1f\u96be\u9053\u4e5f\u8981\u7b49\u4eae\u51fa\u5175\u5203\u624d\u80fd\u8ba4\u51fa\u4ed6\u7684\u8eab\u4efd\u4e48\uff1f \u3000\u3000\u5f88\u53ef\u80fd\u6709\u4e00\u79cd\u4e00\u773c\u5c31\u53ef\u4ee5\u770b\u51fa\u8eab\u5206\u7684\u6807\u5e1c\uff0c\u9690\u85cf\u5728\u522b\u4eba\u4e0d\u6ce8\u610f\u7684\u5730\u65b9\uff0c\u81ea\u5df1\u4eba\u65e9\u5df2\u77e5\u6653\uff0c \u4e00\u773c\u5c31\u8fa8\u522b\u51fa\u4ed6\u4eec\u7684\u8eab\u4efd\u3002 \u3000\u3000\u56db\u65b9\u5927\u6355\u5934\u548c\u4e00\u822c\u4eba\u4e0d\u540c\u7684\u5730\u65b9\uff0c\u5c31\u662f\u4ed6\u4eec\u5904\u5904\u90fd\u5728\u7559\u5fc3\u5468\u56f4\u7684\u53d8\u5316\uff0c\u4e00\u65e6\u53d1\u89c9\u4e86\u95ee\u9898\uff0c \u5fc5\u987b\u5148\u5c3d\u529b\u5bfb\u627e\u89e3\u7b54\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u675c\u671b\u6708\u6682\u65f6\u653e\u5f03\u4e86\u6c42\u80dc\u7684\u6253\u7b97\uff0c\u5168\u529b\u5728\u91d1\u5200\u6740\u624b\u8eab\u4e0a\u6253\u91cf\uff0c\u5e0c\u671b\u80fd\u591f\u77a7\u51fa\u4ed6\u4eec\u8eab \u4e0a\u7684\u6807\u5e1c\u3002 \u3000\u3000\u8fd9\u7b49\u4e0d\u80dc\u4e0d\u8d25\u5c40\u9762\uff0c\u53e6\u4e00\u597d\u5904\uff0c\u662f\u5200\u5149\u5251\u5f71\u628a\u4e8c\u5802\u7684\u5927\u95e8\u5c01\u6b7b\uff0c\u53cc\u65b9\u6355\u6740\uff0c\u770b\u4e0a\u53bb\u53c8\u5341 \u5206\u5267\u70c8\u3002\u6b63\u56e0\u4e3a\u662f\u53cc\u65b9\u4eba\u5728\u5bf9\u6740\uff0c\u53cc\u65b9\u90fd\u6709\u987e\u5fcc\uff0c\u4e00\u65b9\u7eb5\u6709\u6076\u6bd2\u7684\u65b9\u6cd5\u3001\u624b\u6bb5\uff0c\u4e5f\u4e0d\u4fbf\u65bd\u5c55\u3002 \u6240\u4ee5\uff0c\u4e8c\u5802\u5916\u9762\u867d\u7136\u805a\u96c6\u4e86\u5341\u4f59\u4e2a\u91d1\u5200\u6b66\u58eb\uff0c\u5374\u65e0\u6cd5\u5411\u4e8c\u5802\u51b2\u51fb\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u5f88\u5feb\u53d1\u89c9\u4e86\u8fd9\u5b97\u5999\u5904\uff0c\u6587\u660c\u7b14\u6cd5\u4e00\u53d8\u5b88\u591a\u653b\u5c11\uff0c\u4ed6\u7ad9\u7684\u5730\u65b9\u7a7a\u95f4\u8f83\u5927\uff0c\u4e5f\u591a\u4e86\u4e00 \u4e2a\u654c\u4eba\uff0c\u662f\u4ee5\u4e00\u62b5\u4e8c\u7684\u5c40\u9762\uff0c\u4f46\u5434\u94c1\u5cf0\u4ecd\u7136\u63a7\u5236\u7740\u5c40\u9762\uff0c\u800c\u4e14\u6709\u80fd\u529b\u5f88\u5feb\u6740\u6b7b\u4e24\u4eba\u3002\u4f46\u4ed6\u53d1 \u89c9\u6740\u4e86\u4e24\u4eba\u4e4b\u540e\uff0c\u5c31\u4f1a\u6709\u53e6\u5916\u4e24\u4e2a\u751f\u529b\u519b\u52a0\u5165\u8fdb\u6765\uff0c\u53cd\u6b63\u8981\u6253\u4e0b\u53bb\uff0c\u548c\u4e00\u4e2a\u4e45\u6218\u529b\u75b2\u7684\u4eba\u6253\uff0c \u7701\u529b\u591a\u4e86\u3002\u6240\u4ee5\uff0c\u5434\u94c1\u5cf0\u4e5f\u8ba9\u5c40\u9762\u80f6\u7740\uff0c\u6253\u4e2a\u4e0d\u80dc\u4e0d\u8d25\uff0c\u4f46\u5374\u5c3d\u91cf\u4f7f\u5200\u6c14\u51cc\u5389\uff0c\u5bd2\u8292\u95ea\u8dc3\uff0c \u4f7f\u4e00\u4e08\u5185\u4e0d\u80fd\u8fd1\u4eba\u3002 \u3000\u3000\u4f46\u5c91\u5578\u864e\u548c\u4e8e\u627f\u5fd7\u5374\u662f\u5168\u529b\u8fd0\u5200\uff0c\u4e24\u4eba\u5200\u6cd5\u7ef5\u5bc6\uff0c\u62db\u672f\u7cbe\u5947\uff0c\u5df2\u7ecf\u4f24\u4e86\u4e94\u516d\u4e2a\u654c\u4eba\uff0c\u4ecd \u5728\u4ee5\u4e00\u62b5\u4e8c\uff0c\u548c\u56db\u4e2a\u91d1\u5200\u9ed1\u8863\u6740\u624b\uff0c\u6253\u7684\u96be\u89e3\u96be\u5206\u3002 \u3000\u3000\u8fd9\u4e2a\u60c5\u5f62\uff0c\u770b\u4f3c\u5371\u6025\uff0c\u654c\u4eba\u4f17\u591a\uff0c\u56f4\u4e86\u4e8c\u5802\uff0c\u4fdd\u62a4\u5e9c\u8859\u7684\u4eba\u53c8\u592a\u5c11\uff0c\u56db\u5927\u6355\u5934\u4e4b\u5916\uff0c\u53ea \u6709\u738b\u575a\u3001\u60dc\u7389\u3001\u4f55\u5927\u5149\uff0c\u548c\u51e0\u4e2a\u4eb2\u5175\u8859\u5f79\uff0c\u52a0\u8d77\u6765\uff0c\u5341\u51e0\u4e2a\u800c\u5df2\u3002\u4f46\u675c\u671b\u6708\u548c\u738b\u77e5\u5e9c\u5fc3\u4e2d\u5f88 \u660e\u767d\u626c\u5dde\u603b\u5175\u3001\u6c34\u519b\u63d0\u7763\uff0c\u90fd\u5e94\u8be5\u5df2\u5f97\u5230\u8baf\u606f\uff0c\u4e5f\u8bb8\u5927\u519b\u5df2\u7ecf\u51fa\u52a8\uff0c\u6b63\u5728\u5411\u5e9c\u8859\u9a70\u63f4\u4e2d\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u4e8c\u5802\u7684\u60c5\u51b5\uff0c\u5371\u800c\u4e0d\u9669\uff0c\u5f3a\u654c\u4e2d\u7684\u9ad8\u624b\uff0c\u5df2\u88ab\u5c0f\u6587\u59d1\u5a18\u6740\u4f24\u8fd1\u534a\uff0c\u6218\u6597\u529b\u8017\u635f\u6781 \u5927\uff0c\u9a70\u63f4\u800c\u6765\u7684\u91d1\u5200\u6740\u624b\uff0c\u4eba\u6570\u867d\u4e5f\u4e0d\u5c11\uff0c\u4f46\u56db\u65b9\u5927\u6355\u5934\uff0c\u4e2a\u4e2a\u6280\u827a\u7cbe\u7edd\uff0c\u63a7\u5236\u4e86\u5c40\u9762\u540e\uff0c \u5374\u53c8\u4e0d\u6025\u4e8e\u6c42\u80dc\uff0c\u4f7f\u6218\u60c5\u4e4e\u7a33\u4e0d\u53d8\uff0c\u4f7f\u654c\u4eba\u7684\u4f18\u52bf\u6218\u6597\u529b\uff0c\u5b8c\u5168\u65e0\u6cd5\u53d1\u6325\u51fa\u6765\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u76f8\u5f53\u806a\u660e\uff0c\u867d\u7136\u4e0d\u4f1a\u6b66\u529f\uff0c\u4f46\u89c2\u5bdf\u4e00\u9635\u4e4b\u540e\uff0c\u4e5f\u53d1\u89c9\u4e86\u56db\u4f4d\u5927\u6355\u5934\uff0c\u672a\u51fa\u5168\u529b\uff0c \u662f\u6709\u610f\u5730\u9020\u6210\u76ee\u524d\u7684\u76f8\u6301\u60c5\u52bf\uff0c\u4e2a\u4eba\u7684\u5904\u5883\uff0c\u770b\u4e0a\u53bb\uff0c\u867d\u7136\u5728\u654c\u4eba\u7684\u91cd\u91cd\u5305\u56f4\u4e4b\u4e0b\uff0c\u4f46\u5374\u662f \u5b89\u5168\u7684\u5f88\u3002 \u3000\u3000\u679c\u7136\uff0c\u9a6c\u63d0\u7763\u7684\u6c34\u5e08\u7cbe\u9510\u3001\u4e94\u767e\u540d\u9a81\u5200\u624b\uff0c\u548c\u4e00\u767e\u540d\u914d\u5408\u8fd1\u6218\u7684\u5f13\u7bad\u624b\uff0c\u5f53\u5148\u51b2\u5165\u4e86\u4e8c \u5802\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u6b66\u529f\u4e0d\u9ad8\uff0c\u4f46\u5200\u3001\u7bad\u914d\u5408\u7684\u6218\u6cd5\uff0c\u5374\u6709\u7740\u60ca\u4eba\u7684\u6740\u4f24\u80fd\u529b\uff0c\u90a3\u4e9b\u7070\u8863\u4eba\u7684\u7ec4\u5408\u9635 \u52bf\uff0c\u5728\u7bad\u5982\u5bc6\u96e8\u4e4b\u4e0b\uff0c\u5df2\u65e0\u6cd5\u518d\u7ec4\u5408\u653b\u51fb\u4e4b\u9635\uff0c\u56db\u5927\u6355\u5934\u4e5f\u5c55\u5f00\u4e86\u5f3a\u529b\u7684\u53cd\u51fb\uff0c\u53ea\u4e0d\u8fc7\u7247\u523b \u5de5\u592b\uff0c\u654c\u52bf\u5df2\u5b8c\u5168\u5d29\u6e83\u3002 \u3000\u3000\u5230\u4e86\u9003\u547d\u65f6\u523b\uff0c\u5c31\u770b\u51fa\u672c\u8eab\u529f\u529b\u7684\u6df1\u6d45\u4e86\uff0c\u91d1\u5200\u6b66\u58eb\u9003\u8d70\u5927\u534a\uff0c\u90a3\u4e9b\u7070\u8863\u4eba\u51e0\u4e4e\u662f\u5168\u519b \u8986\u6ca1\uff0c\u5341\u4e8c\u7ec4\u9635\u52bf\uff0c\u4e09\u5341\u516d\u4e2a\u4eba\uff0c\u6b7b\u4e86\u4e09\u5341\u4e2a\uff0c\u516d\u4e2a\u4eba\u53d7\u4e86\u4f24\uff0c\u88ab\u968f\u540e\u51b2\u5165\u7684\u5927\u6279\u8859\u5f79\u751f\u64d2 \u4e86\u4e0b\u5165\u76d1\u7262\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u4e5f\u8fdb\u5165\u4e86\u5e9c\u8859\uff0c\u4f46\u53ea\u5728\u4e8c\u5802\u5916\u9762\u9065\u5bf9\u738b\u5927\u4eba\u62b1\u62f3\u4e00\u793c\uff0c\u9053\uff1a\u201c\u9a6c\u67d0\u6551\u5e94\u6765\u8fdf\uff0c\u5e9c \u53f0\u5927\u4eba\u53d7\u60ca\u4e86\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u6709\u52b3\u9a6c\u5144\u4e86\uff0c\u6c34\u5e08\u7cbe\u9510\uff0c\u679c\u975e\u5c0f\u53ef\uff0c\u7247\u523b\u51fb\u9000\u4e86\u5f3a\u654c\u2026\u2026\u201d \u3000\u3000\u201c\u8a00\u91cd\u4e86\uff0c\u5144\u5f1f\u8fd8\u5728\u7b49\u5f85\u5e9c\u53f0\u5927\u4eba\uff0c\u4e3a\u72ac\u5b50\u6d17\u96ea\u6c89\u51a4\uff0c\u201d\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u9a6c\u67d0\u544a\u8f9e\u4e86\u3002\u201d \u8f6c\u8eab\u626c\u957f\u800c\u53bb\uff0c\u6c34\u5e08\u9a81\u5200\u624b\u7d27\u968f\u7740\u64a4\u8d70\u3002 \u3000\u3000\u675c\u671b\u6708\u4f4e\u58f0\u9053\uff1a\u201c\u9a6c\u63d0\u7763\u7684\u4eba\u5f88\u9a84\u50b2\u3002\u201d \u3000\u3000\u201c\u6218\u529f\u5f6a\u70b3\uff0c\u5e1d\u7737\u6b63\u9686\u561b\uff01\u201d\u738b\u5927\u4eba\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u65b0\u623f\u547d\u6848\uff0c\u8fd8\u9020\u6210\u5f7c\u6b64\u95f4\u4e00\u70b9\u8bef\u4f1a\u3002\u201d \u3000\u3000\u201c\u770b\u6765\uff0c\u8fd9\u4e2a\u6848\u5b50\u7684\u538b\u529b\u5f88\u5927\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4ee5\u5927\u4eba\u4e4b\u624d\uff0c\u662f\u5426\u5df2\u63a8\u60f3\u51fa\u4e00\u70b9\u7709\u76ee\u5462\uff1f \u63d0\u51fa\u6765\uff0c\u4e5f\u597d\u4f9b\u6211\u7b49\u53c2\u914c\u53c2\u914c\u2026\u2026\u201d \u3000\u3000\u201c\u675c\u53f8\u4e3b\uff0c\u8fd9\u4e0d\u662f\u53eb\u4e0b\u5b98\u73ed\u95e8\u5f04\u65a7\u4e48\uff1f\u201d\u738b\u5c11\u537f\u7b11\u9053\uff1a\u201c\u7709\u76ee\u672a\u60f3\u51fa\u6765\uff0c\u4f46\u5374\u9047\u4e0a\u4e86\u4e24 \u4e2a\u60f3\u4e0d\u901a\u7684\u95ee\u9898\uff0c\u5b83\u4e0d\u662f\u4e66\u672c\u4e0a\u53ef\u4ee5\u89e3\u51b3\u7684\u4e8b\u3002\u672c\u6765\u5462\uff0c\u60f3\u60f3\u4e5f\u5c31\u7b97\u4e86\uff0c\u4f46\u5c0f\u6587\u59d1\u5a18\u7a81\u7136\u6027 \u683c\u5927\u53d8\uff0c\u53c8\u4f7f\u6211\u611f\u89c9\u5230\uff0c\u90a3\u4e9b\u95ee\u9898\uff0c\u5e94\u8be5\u6709\u63d0\u51fa\u6765\u8ba8\u8bba\u4e00\u4e0b\u7684\u4ef7\u503c\u3002\u201d \u3000\u3000\u201c\u5c0f\u6587\u59d1\u5a18\u4e00\u5411\u6e29\u67d4\u3001\u6df3\u539a\uff0c\u4eca\u65e5\u4e4b\u53d8\uff0c\u5927\u5927\u51fa\u4eba\u610f\u5916\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u8fd9\u4e2d\u95f4\u53ef\u80fd\u522b \u6709\u5185\u60c5\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u4e8c\u5802\u5185\u5916\uff0c\u90fd\u5df2\u7ecf\u6574\u626b\u6e05\u6d01\uff0c\u6b7b\u4f24\u4e4b\u4eba\uff0c\u4e5f\u6709\u5f20\u5e08\u7237\u6307\u793a\uff0c\u4f5c\u9a8c\u5c38\u5217\u5377\u540e\uff0c\u6e05\u51fa \u63a9\u57cb\u3002 \u3000\u3000\u5f20\u5b9d\u5584\u529e\u4e8b\u5feb\u901f\uff0c\u7acb\u523b\u79fb\u5730\u5f00\u5ba1\uff0c\u628a\u751f\u64d2\u7684\u6740\u624b\uff0c\u4e8c\u5802\u8baf\u843d\u6848\u3002 \u3000\u3000\u738b\u5c11\u537f\u5374\u5e26\u7740\u56db\u5927\u6355\u5934\u548c\u738b\u575a\uff0c\u5728\u4e8c\u5802\u4e66\u623f\u4e2d\u8336\u53d9\u3002\u5c0f\u6587\u4e5f\u5728\u60dc\u7389\u7684\u7167\u987e\u4e0b\uff0c\u5750\u5728\u4e66\u623f \u4e00\u5f20\u5927\u85e4\u6905\u4e0a\u4f11\u606f\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5979\u7a74\u9053\u672a\u89e3\uff0c\u4eba\u8fd8\u5728\u6655\u8ff7\u4e2d\uff0c\u4f46\u56db\u5927\u6355\u5934\u4e0d\u653e\u5fc3\u628a\u5979\u4e00\u4e2a\u4eba\u653e\u5165\u5367\u5ba4\u4e2d\u4f11\u606f\uff0c \u62c5\u5fc3\u88ab\u91cd\u6765\u7684\u6740\u624b\u52a0\u5bb3\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u4e5f\u4e0d\u653e\u5fc3\u5c0f\u6587\u4e00\u4e2a\u4eba\u5728\u623f\u4e2d\u4f11\u606f\uff0c\u4ed6\u62c5\u5fc3\u5c0f\u6587\u7684\u4eba\u7a81\u7136\u9192\u4e86\u8fc7\u6765\uff0c\u4f46\u795e\u5fd7\u5c1a\u672a\u5168 \u590d\uff0c\u6325\u5251\u5c31\u6740\uff0c\u51e0\u4e4e\u6ca1\u6709\u4eba\u80fd\u591f\u963b\u62e6\u4f4f\u5979\uff0c\u4e0d\u6446\u5728\u56db\u5927\u6355\u5934\u7684\u773c\u76ae\u4e0b\u9762\uff0c\u5982\u4f55\u80fd\u591f\u5b89\u5fc3\uff0c\u56e0 \u4e3a\uff0c\u5c0f\u6587\u5bf9\u4ed6\u5fc3\u6709\u79ef\u5fff\uff0c\u4e0b\u4e00\u4e2a\u88ab\u6740\u7684\u4eba\uff0c\u5f88\u53ef\u80fd\u4f1a\u9009\u4e2d\u4ed6\u738b\u5927\u4eba\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u4e5f\u4e3b\u5f20\u628a\u5c0f\u6587\u59d1\u5a18\u5b89\u7f6e\u5728\u4e66\u623f\u4e2d\uff0c\u660e\u662f\u4e25\u5bc6\u4fdd\u62a4\uff0c\u9aa8\u5b50\u91cc\u662f\u9632\u5979\u9192\u6765\u4f5c\u602a\u3002 \u3000\u3000\u675c\u671b\u6708\u559d\u4e86\u4e24\u53e3\u8336\uff0c\u7b11\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u5148\u8c08\u6848\u60c5\uff0c\u4f60\u60f3\u5230\u4e86\u4e24\u4e2a\u4ec0\u4e48\u6837\u5b50\u7684\u95ee\u9898\uff1f\u4ee5\u5927 \u4eba\u80f8\u7f57\u4e4b\u535a\uff0c\u7adf\u4e5f\u60f3\u4e0d\u901a\u5b83\uff1f\u201d \u3000\u3000\u738b\u77e5\u5e9c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0b\u5b98\u4e00\u4e0d\u4f1a\u6b66\u529f\uff0c\u4e8c\u65e0\u4fa6\u529e\u5211\u6848\u7684\u7ecf\u9a8c\uff0c\u5728\u8bf8\u4f4d\u5927\u6355\u5934\u773c\u4e2d\uff0c\u4e5f \u8bb8\u662f\u4e0d\u503c\u4e00\u54c2\u7684\u5c0f\u4e8b\uff0c\u4f46\u4e0b\u5b98\u5c31\u60f3\u7684\u5f88\u8f9b\u82e6\u4e86\uff01\u201d \u3000\u3000\u201c\u662f\u662f\u662f\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5927\u4eba\u8bf7\u8bf4\u5185\u60c5\uff0c\u6211\u7b49\u662f\u77e5\u65e0\u4e0d\u8a00\u3002\u201d \u3000\u3000------------- \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u5341\u4e94\u7ae0 \u3000\u3000\u626c\u5dde\u5e9c\u4e5f\u7b97\u6c5f\u5357\u5211\u6355\u53f8\u7684\u8f96\u533a\uff0c\u548c\u5f53\u5730\u9996\u957f\u5546\u8c08\u6848\u60c5\uff0c\u867d\u8bf4\u662f\u4ea4\u6362\u5fc3\u5f97\uff0c\u4ee5\u5229\u7834\u6848\uff0c\u4f46 \u4e5f\u9690\u9690\u95f4\u6709\u4e00\u79cd\u4e92\u901e\u5fc3\u673a\u7684\u5473\u9053\uff0c\u522b\u4eba\u5c31\u4e0d\u597d\u63d2\u53e3\uff0c\u53ea\u6709\u675c\u671b\u6708\u51fa\u9762\u63a5\u4e0b\u4e86\u3002 \u3000\u3000\u201c\u8fd9\u4e16\u4e0a\uff0c\u662f\u5426\u6709\u4e00\u79cd\u6b66\u529f\uff0c\u539f\u529b\uff0c\u5728\u4e00\u6bb5\u9002\u5f53\u7684\u8ddd\u79bb\u4e2d\uff0c\u80fd\u591f\u5f79\u4f7f\u5200\u3001\u5251\u6740\u4eba\uff0c\u201d\u738b \u77e5\u5e9c\u9053\uff1a\u201c\u4e0d\u7528\u4eb2\u4e34\u73b0\u573a\uff0c\u4ee5\u9632\u7559\u4e0b\u75d5\u8ff9\uff1f\u201d \u3000\u3000\u201c\u6709\u7684\uff0c\u4f46\u53ea\u6b62\u4e8e\u4f20\u95fb\u3002\u201d\u675c\u671b\u6708\uff1a\u201c\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u6211\u8fd8\u6ca1\u6709\u9047\u4e0a\u8fc7\u6709\u6b64\u80fd\u8010\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u56de\u7b54\u7684\u6a21\u68f1\u4e24\u53ef\uff0c\u4f46\u8a00\u8bcd\u9063\u53e5\uff0c\u5374\u53c8\u8868\u73b0\u7684\u5341\u5206\u8bda\u5b9e\u3002 \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u5200\u81ea\u4f24\u4eba\u3001\u5251\u81ea\u98de\uff0c\u4e0d\u662f\u602a\u529b\u4e71\u795e\u7684\u4f20\u8bf4\u4e86\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u662f\uff01\u5927\u4eba\u7684\u7b2c\u4e8c\u4e2a\u60f3\u4e0d\u901a\u7684\u95ee\u9898\uff0c\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u4e00\u4e2a\u4eba\uff0c\u4f1a\u4e0d\u4f1a\u5728\u68a6\u6e38\u4e2d\u6740\u4eba\uff1f\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u4f20\u8bf4\u4e2d\u7684\u795e\u6848\u3001\u5deb\u5e08\uff0c\u6709\u5e03\u65bd\u969c\u773c\u6cd5 \u7684\u5947\u6280\uff0c\u4f7f\u4e00\u4e9b\u6ce5\u5851\u6728\u96d5\u7684\u5f62\u50cf\u88ab\u4eba\u884c\u6cd5\u4e4b\u540e\uff0c\u8d77\u800c\u6740\u4eba\u3002\u8fd9\u4e9b\u4e8b\uff0c\u4e0b\u5b98\u672c\u6765\u4e0d\u4fe1\uff0c\u4f46\u5c0f\u6587 \u59d1\u5a18\u5374\u662f\u4e2a\u53d1\u4eba\u6df1\u7701\uff0c\u53c8\u4e0d\u53ef\u601d\u8bae\u7684\u4f8b\u5b50\u3002\u5982\u82e5\uff0c\u8fd9\u6837\u7684\u4e8b\u4ef6\uff0c\u771f\u7684\u53d1\u751f\u5728\u91d1\u5c0f\u7709\u7684\u8eab\u4e0a\uff0c \u8fd9\u91d1\u5c0f\u7709\u7b97\u4e0d\u7b97\u51f6\u624b\u5462\uff1f\u201d \u3000\u3000\u56db\u5927\u6355\u5934\uff0c\u90fd\u88ab\u738b\u77e5\u5e9c\u4e00\u756a\u8bdd\u95ee\u7684\u6123\u4f4f\u4e86\u3002 \u3000\u3000\u4f46\u6700\u4e3a\u5403\u60ca\u7684\uff0c\u5374\u662f\u60dc\u7389\u59d1\u5a18\uff0c\u91d1\u5c0f\u7709\u66fe\u4eb2\u53e3\u544a\u8bc9\u5979\uff0c\u5f88\u53ef\u80fd\u662f\u6740\u6b7b\u4e08\u592b\u7684\u51f6\u624b\uff0c\u597d\u50cf \u662f\u5728\u5168\u65e0\u610f\u8bc6\u4e0b\u64cd\u5200\u884c\u51f6\u3002 \u3000\u3000\u4e8b\u5b9e\u7ecf\u5386\u5982\u5e7b\u68a6\uff0c\u68a6\u5883\u9192\u6765\u8fa8\u4f9d\u7a00\u3002 \u3000\u3000\u60dc\u7389\u51c6\u5907\u628a\u5f97\u5230\u7684\u673a\u5bc6\uff0c\u544a\u8bc9\u603b\u6355\u5934\uff0c\u4f46\u5374\u60f3\u4e0d\u5230\u4f1a\u9047\u4e0a\u6740\u624b\u7684\u5e03\u5c40\uff0c\u88ad\u51fb\u5e9c\u8859\u3002\u5c0f\u6587 \u6012\u6b7c\u7fa4\u51f6\uff0c\u7adf\u7136\u53c8\u6740\u51fa\u4e86\u795e\u5fd7\u5931\u5e38\u7684\u6bdb\u75c5\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u53ea\u662f\u4e00\u4e2a\u8bfb\u4e66\u4eba\uff0c\u4f46\u63a8\u8bba\u6848\u60c5\uff0c\u7adf\u7136\u4e3e\u4e00\u53cd\u4e09\uff0c\u7531\u5c0f\u6587\u7684\u75c5\u6001\uff0c\u60f3\u5230\u91d1\u5c0f\u7709\u884c\u51f6 \u6740\u592b\uff0c\u8fd9\u4e2a\u4eba\u53ea\u662f\u4e2a\u79c0\u624d\u51fa\u8eab\u7684\u5b98\u5458\uff0c\u4e0d\u4f1a\u6b66\u529f\uff0c\u4e0d\u61c2\u6c5f\u6e56\uff0c\u4e0d\u4fee\u533b\u9053\uff0c\u4e5f\u65e0\u7ecf\u9a8c\uff0c\u4f46\u5374\u80fd \u76f4\u65ad\u6848\u60c5\uff0c\u6709\u5982\u76ee\u7779\uff0c\u8fd9\u8981\u591a\u5c11\u79cd\u4e0d\u540c\u7684\u5b66\u95ee\uff0c\u624d\u80fd\u57f9\u517b\u51fa\u4ed6\u8fd9\u7b49\u8d85\u8d8a\u7684\u667a\u6167\u3002 \u3000\u3000\u5c3d\u7ba1\u60dc\u7389\u59d1\u5a18\u7684\u5fc3\u4e8b\u6f6e\u6d8c\uff0c\u4f46\u5979\u8fd8\u662f\u5fcd\u4f4f\u4e86\uff0c\u4e00\u8bed\u4e0d\u53d1\uff0c\u8fd9\u4ef6\u4e8b\u51b3\u4e0d\u80fd\u544a\u8bc9\u522b\u4eba\uff0c\u8fde\u56db \u5927\u6355\u5934\uff0c\u548c\u5df2\u5728\u5fc3\u4e2d\u5b9a\u4f4d\u7684\u4e08\u592b\uff0c\u4e5f\u4e0d\u80fd\u900f\u9732\uff0c\u552f\u4e00\u80fd\u8bf4\u7684\u662f\u603b\u6355\u5934\u3002\u4f46\u95fb\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u5927 \u4eba\uff0c\u5c0f\u6587\u59d1\u5a18\u7684\u75c5\u6001\uff0c\u6765\u7684\u7a81\u7136\uff0c\u770b\u4f3c\u65e0\u56e0\uff0c\u5b9e\u5728\u4ea6\u975e\u65e0\u8ff9\u53ef\u5bfb\u2026\u2026\u201d \u3000\u3000\u201c\u597d\uff01\u597d\u6781\u4e86\uff0c\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u5434\u5144\u8bf7\u8bf4\u5185\u60c5\uff0c\u4ee5\u89e3\u6211\u5fc3\u4e2d\u4e4b\u60d1\uff0c\u5c11\u537f\u6d17\u8033\u606d\u542c\u4e86\u3002\u201d \u3000\u3000\u4f55\u6b62\u738b\u77e5\u5e9c\u6025\u4e8e\u4e00\u95fb\u5185\u60c5\uff0c\u5c31\u662f\u4e09\u4f4d\u5927\u6355\u5934\u3001\u60dc\u7389\u59d1\u5a18\uff0c\u8fde\u540c\u5b88\u5728\u4e66\u623f\u5916\u9762\u5927\u5385\u7684\u738b\u575a \u548c\u4f55\u5927\u5149\uff0c\u5168\u90fd\u51dd\u805a\u5168\u795e\uff0c\u503e\u542c\u4e0b\u6587\u4e86\uff0c\u56e0\u4e3a\uff0c\u5c0f\u6587\u5c60\u622e\u7684\u5389\u70c8\uff0c\u7559\u7ed9\u4e86\u4ed6\u4eec\u5f88\u6df1\u7684\u5370\u8c61\u3002 \u3000\u3000\u201c\u4e0d\u662f\u5185\u60c5\uff0c\u6211\u8bf4\u7684\u53ea\u662f\u4e00\u4ef6\u4e8b\u5b9e\uff0c\u4e00\u4ef6\u76ee\u7779\u7684\u5947\u5f02\u7ecf\u8fc7\uff0c\u5434\u67d0\u4eba\u5df2\u7ecf\u60f3\u4e86\u5f88\u591a\u5e74\uff0c\u4f46 \u8fd8\u662f\u60f3\u4e0d\u900f\u5f7b\uff0c\u6211\u8bf4\u51fa\u8fd9\u4ef6\u4e8b\uff0c\u4e5f\u8bb8\u6709\u70b9\u5f92\u4e71\u4eba\u610f\uff0c\u4f46\u5374\u548c\u5c0f\u6587\u59d1\u5a18\u7684\u75c5\u6001\uff0c\u6709\u4e9b\u7275\u8fde\u3002\u201d \u3000\u3000\u60dc\u7389\u80f8\u4e2d\u719f\u8bb0\u4e86\u5f88\u591a\u533b\u836f\u77e5\u8bc6\uff0c\u4e5f\u719f\u8bb0\u4e86\u5f88\u591a\u91cd\u8981\u7684\u836f\u65b9\uff0c\u5f53\u5e74\u8c2d\u795e\u533b\u628a\u836f\u7406\u77e5\u8bc6\uff0c\u5c3d \u91cf\u5730\u4f20\u6388\u4e86\u5973\u513f\uff0c\u4f46\u60dc\u7389\u5374\u672a\u7528\u5fc3\u63e3\u6478\uff0c\u53ea\u662f\u51ed\u4ed7\u7740\u5929\u8d4b\u624d\u6167\uff0c\u628a\u5b83\u8bb0\u5b58\u8111\u9645\uff0c\u73b0\u5728\u5374\u53d1\u73b0\uff0c \u533b\u836f\u77e5\u8bc6\u5bf9\u4eba\u751f\u975e\u5e38\u7684\u91cd\u8981\u3002\u5f00\u59cb\u601d\u7d22\u80f8\u4e2d\u8bb0\u5fc6\uff0c\u5982\u4f55\u5e94\u7528\u4e8e\u6551\u4eba\u4e4b\u4e0a\uff0c\u4e5f\u5f00\u59cb\u6ce8\u610f\u5230\u6b66\u529f \u548c\u533b\u836f\u95f4\u7684\u76f8\u4e92\u5173\u7cfb\u3002 \u3000\u3000\u5979\u60f3\u7406\u4e00\u4e2a\u836f\u65b9\u51fa\u6765\uff0c\u80fd\u627c\u6b62\u4f4f\u5c0f\u6587\u7684\u75c5\u60c5\u3002 \u3000\u3000\u201c\u90a3\u662f\u4e2a\u65ed\u65e5\u521d\u5347\u7684\u65e9\u6668\uff0c\u6211\u5728\u592a\u884c\u5c71\u4e2d\u76ee\u7779\u4e86\u4e00\u573a\u6740\u622e\u4e4b\u6218\u3002\u201d\u5434\u94c1\u5cf0\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c \u9053\uff1a\u201c\u5341\u5e74\u524d\u7eb5\u6a2a\u4e2d\u539f\u7684\u592a\u884c\u5341\u516b\u9a91\uff0c\u5c31\u5728\u90a3\u4e00\u6218\u4e2d\uff0c\u5168\u90e8\u6bc1\u706d\uff0c\u6ca1\u7559\u4e0b\u4e00\u4e2a\u6d3b\u53e3\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u548c\u5c0f\u6587\u59d1\u5a18\u7684\u75c5\u6001\uff0c\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\uff1f\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u5341\u5e74\u524d\uff0c\u5c0f\u6587\u8fd8\u662f\u4e2a\u5341\u6765\u5c81\u7684 \u5c0f\u59d1\u5a18\u5427\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u95ee\u9898\u662f\u90a3\u5957\u5251\u6cd5\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6740\u4eba\u7684\u5168\u8fc7\u7a0b\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u65e0\u6cd5\u770b\u7684\u5f88\u6e05\u695a\uff0c\u4f46\u51fa \u5251\u7684\u59ff\u6001\uff0c\u5374\u548c\u5c0f\u6587\u59d1\u5a18\u7684\u51fa\u5251\u59ff\u6001\uff0c\u6709\u4e9b\u96f7\u540c\u3002\u201d \u3000\u3000\u201c\u8001\u5434\uff0c\u5341\u5e74\u5566\uff01\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u8bb0\u5fc6\u4f1a\u5426\u6709\u8bef\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f1a\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u90a3\u662f\u4e00\u6869\u7ec8\u751f\u96be\u5fd8\u7684\u5927\u4e8b\uff0c\u6211\u548c\u592a\u884c\u5341\u516b\u9a91\uff0c\u4ea4\u8fc7\u4e86\u4e09\u6b21\u624b\uff0c \u77e5\u9053\u4ed6\u4eec\u7684\u6b66\u529f\uff0c\u4e00\u5bf9\u4e00\uff0c\u6211\u6709\u80dc\u7b97\uff0c\u4e00\u5bf9\u4e8c\u6211\u53ef\u4ee5\u4fdd\u4f4f\u6027\u547d\uff0c\u4e00\u5bf9\u4e09\uff0c\u6211\u5c31\u96be\u6491\u4e00\u767e\u4e2a\u56de \u5408\u3002\u4f46\u4ed6\u662f\u4e00\u5bf9\u5341\u516b\uff0c\u6211\u60f3\u4e0d\u51fa\u5f53\u4eca\u4e4b\u4e16\uff0c\u4ec0\u4e48\u4eba\u4f1a\u6709\u8fd9\u6837\u7684\u529f\u529b\uff0c\u5f53\u4ed6\u4eae\u51fa\u957f\u5251\u4e4b\u540e\uff0c\u6211 \u5fc3\u4e2d\u8fd8\u66ff\u4ed6\u5ff1\u60dc\uff0c\u4e00\u4e2a\u4eba\uff0c\u9762\u5bf9\u592a\u884c\u5341\u516b\u9a91\uff0c\u662f\u5b8c\u5168\u6ca1\u6709\u751f\u5b58\u673a\u4f1a\u3002\u4f46\u6211\u9519\u4e86\uff0c\u53cc\u65b9\u52a8\u4e0a\u624b\uff0c \u4f46\u89c1\u5251\u5149\u98de\u95ea\uff0c\u592a\u884c\u5341\u516b\u9a91\uff0c\u5b8c\u5168\u6ca1\u6709\u8fd8\u624b\u7684\u673a\u4f1a\uff0c\u751a\u81f3\u6211\u65e0\u6cd5\u786e\u5b9a\u4ed6\u51e0\u65f6\u628a\u5341\u516b\u9a91\u5b8c\u5168\u6740 \u6b7b\uff0c\u90a3\u4e0d\u53eb\u6218\u6597\uff0c\u5e94\u8be5\u6539\u79f0\u5c60\u6740\uff0c\u4eba\u90fd\u6740\u5149\u4e86\uff0c\u98de\u821e\u7684\u5251\u5149\uff0c\u8fd8\u6ca1\u6709\u9759\u6b62\u4e0b\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u662f\u9690\u8eab\u5077\u89d1\uff0c\u5982\u82e5\u4ed6\u77e5\u9053\u4f60\u5728\u5077\u770b\uff0c\u4e5f\u8bb8\u4e0d\u4f1a\u653e\u8fc7 \u4f60\uff1f\u201d \u3000\u3000\u201c\u5bf9\uff01\u5f53\u65f6\uff0c\u6211\u5c31\u60f3\u5230\u8fd9\u4e00\u70b9\uff0c\u6211\u8eb2\u5728\u4e00\u4e2a\u9ad8\u5d16\u4e0a\uff0c\u501f\u6b65\u6b65\u9ad8\u5347\u7684\u65e5\u5149\uff0c\u628a\u53d1\u751f\u7684\u4e8b\uff0c \u770b\u7684\u76f8\u5f53\u6e05\u695a\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6211\u867d\u7136\u628a\u81ea\u5df1\u85cf\u7684\u66f4\u5bc6\u4e86\uff0c\u4f46\u4ecd\u7136\u5fcd\u4e0d\u4f4f\u63a2\u9996\u5f20\u671b\uff0c\u6ca1\u6709\u4e86 \u8840\u8089\u98de\u6e85\u7684\u6df7\u6dc6\uff0c\u770b\u7684\u66f4\u6e05\u695a\u4e86\uff0c\u5927\u6982\uff0c\u4ed6\u8981\u628a\u90a3\u5957\u5251\u6cd5\u7ec3\u5b8c\uff0c\u8db3\u8db3\u8fc7\u4e86\u4e00\u523b\u5de5\u592b\u624d\u6536\u4f4f\u5251 \u52bf\u3002\u201d \u3000\u3000\u201c\u770b\u7684\u66f4\u6e05\u695a\u4e86\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u770b\u51fa\u4e86\u4ec0\u4e48\u5fc3\u5f97\uff1f\u201d \u3000\u3000\u201c\u90a3\u5957\u5251\u6cd5\uff0c\u6709\u4e00\u80a1\u90aa\u6c14\uff0c\u7531\u7ef5\u8fde\u4e0d\u7edd\u7684\u5251\u52bf\u4e2d\uff0c\u6563\u53d1\u51fa\u6765\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4e0d\u77e5\u662f\u5982 \u4f55\u7b97\u8ba1\u51fa\u6765\u7684\uff0c\u5251\u5149\u6307\u88ad\u4e4b\u5904\uff0c\u654c\u4eba\u521a\u597d\u8d76\u5230\uff0c\u649e\u5728\u4e86\u5251\u4e0a\uff0c\u8fd9\u4e00\u70b9\u662f\u4e0d\u662f\u90aa\u6c14\u5462\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u53cc\u76ee\u4e2d\u95ea\u52a8\u7740\u7075\u6167\u4e4b\u5149\uff0c\u4f46\u5374\u5f3a\u5fcd\u7740\u672a\u53d1\u4e00\u8a00\u3002 \u3000\u3000\u201c\u4e5f\u4e0d\u77e5\u9053\u90a3\u4e00\u5957\u5251\u6cd5\u7684\u540d\u5b57\u4e86\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u770b\u6765\u4e5f\u65e0\u6cd5\u8c08\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u5c0f\u675c\uff0c\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u4e0d\u7528\u5728\u5c0f\u6587\u8eab\u4e0a\u591a\u8d39\u5fc3\u673a\u4e86\uff0c\u8fd9\u4e9b\u4e8b\u603b\u6355\u5934\u5e94\u8be5\u5fc3\u4e2d\u6709\u6570\uff0c\u89c1 \u4e86\u5979\u4e4b\u540e\uff0c\u90fd\u4f1a\u8fce\u5203\u800c\u89e3\u7684\uff0c\u5012\u662f\u603b\u6355\u5934\u548c\u5c0f\u96c5\u903e\u65f6\u4e0d\u5f52\uff0c\u4f1a\u4e0d\u4f1a\u51fa\u4e86\u610f\u5916\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u6709\u4e86\u610f\u5916\uff0c\u4e0d\u8fc7\uff0c\u6211\u76f8\u4fe1\u603b\u6355\u5934\u6709\u8db3\u591f\u4fdd\u62a4\u81ea\u5df1\u7684\u80fd\u529b\uff0c\u6211\u4eec\u4e0d\u7528 \u4e3a\u5979\u7684\u5b89\u5371\u62c5\u5fe7\u3002\u201d \u3000\u3000\u867d\u7136\u662f\u5bbd\u6170\u7684\u8bdd\uff0c\u4f46\u4e5f\u662f\u4e8b\u5b9e\uff0c\u7a0b\u5c0f\u8776\u6280\u827a\u5353\u7edd\uff0c\u662f\u56db\u4f4d\u5927\u6355\u5934\u5fc3\u670d\u53e3\u4e5f\u670d\u7684\u4e00\u4eba\uff0c\u8fd9 \u4e16\u4e0a\u80fd\u591f\u4f24\u5bb3\u5979\u7684\u4eba\uff0c\u5b9e\u5728\u4e0d\u591a\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5f20\u5b9d\u5584\u6367\u7740\u6848\u5377\uff0c\u884c\u4e86\u8fdb\u6765\uff0c\u7b11\u9053\uff1a\u201c\u539f\u60f3\u8fd9\u4e9b\u4eba\u987d\u5f3a\u56fa\u6267\uff0c\u95ee\u53e3\u4f9b\uff0c\u53ea\u6015\u662f\u4e00 \u693f\u5f88\u96be\u7684\u4e8b\uff0c\u5374\u4e0d\u6599\u51fa\u4eba\u610f\u6599\u7684\u987a\u5229\u2026\u2026\u201d \u3000\u3000\u738b\u5c11\u537f\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u62db\u4f9b\u4e86\uff1f\u90fd\u4f9b\u51fa\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u662f\u6740\u624b\u4e4b\u738b\u3001\u8840\u624b\u65b9\u8f6e\u8bad\u7ec3\u51fa\u6765\u7684\u6740\u624b\u3002\u201d\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u8bad\u7ec3\u4ed6\u4eec\u7684\u5730\u65b9\uff0c\u5206\u8bbe \u591a\u5904\uff0c\u4f46\u8305\u5c71\u548c\u5929\u53f0\u5c71\uff0c\u662f\u4e24\u5904\u6700\u91cd\u8981\u7684\u8bad\u7ec3\u8425\u5730\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8f9b\u82e6\u4e86\uff0c\u4f60\u56de\u5bb6\u4f11\u606f\u4e24\u5929\uff0c\u660e\u5929\u4e5f\u4e0d\u7528\u6765\u8859\u95e8\u529e\u4e8b\uff0c\u6211\u4e5f\u8981\u4f11\u606f \u4e24\u5929\u624d\u884c\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u5927\u4eba\u4f53\u60dc\u3002\u201d\u5f20\u5b9d\u5584\u8d77\u8eab\u4e00\u63d6\uff0c\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u201c\u5c31\u7b97\u5211\u6c42\u903c\u4f9b\uff0c\u53ea\u6015\u4e5f\u95ee\u4e0d\u51fa\u4ec0\u4e48\u5185\u5bb9\u3002\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u6740\u624b\uff0c\u4e0d\u662f\u666e\u901a\u4eba\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\u9ad8\u660e\uff01\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4ed6\u4eec\u4e5f\u53ea\u77e5\u9053\u8fd9\u4e9b\uff0c\u518d\u95ee\u4e0b\u53bb\uff0c\u4ed6\u4eec\u662f\u771f\u7684\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u56de\u987e\u4e86\u5c0f\u6587\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5b89\u7f6e\u597d\u5c0f\u6587\u59d1\u5a18\uff0c\u4e5f\u53ef\u4ee5\u597d\u597d\u4f11\u606f\u4e24\u5929\u4e86\u3002\u201d \u3000\u3000\u201c\u53ea\u6015\u5927\u4eba\u96be\u5f97\u6709\u8fd9\u4efd\u6e05\u95f2\u7684\u798f\u6c14\uff0c\u201d\u5434\u94c1\u5cf0\u62ac\u5934\u671b\u4e86\u671b\u5929\u8272\uff0c\u9053\uff1a\u201c\u7531\u4eca\u591c\u8d77\uff0c\u5927\u4eba \u6050\u6015\u4e00\u76f4\u4f1a\u9677\u5165\u523a\u5ba2\u7684\u88ad\u6740\u4e4b\u4e2d\u2026\u2026\u201d \u3000\u3000\u201c\u96be\u9053\u4ed6\u4eec\u8fd8\u4f1a\u518d\u6765\u4e00\u6b21\u591c\u6df1\u4fb5\u5e9c\u8859\uff0c\u5e03\u5c40\u56f4\u6740\uff1f\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u6709\u7834\u89e3\u4e4b\u9053\uff1f\u201d \u3000\u3000\u201c\u518d\u51fa\u52a8\u5927\u6279\u6740\u624b\uff0c\u88ad\u51fb\u5e9c\u8859\uff0c\u662f\u4e0d\u592a\u53ef\u80fd\u4e86\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4f46\u51fa\u52a8\u5c11\u6570\u7684\u9ad8\u7ea7\u6740\u624b\uff0c \u8bbe\u5c40\u5077\u88ad\uff0c\u5374\u662f\u5fc5\u7136\u7684\u4e8b\u3002\u6240\u4ee5\uff0c\u7531\u6b64\u523b\u8d77\uff0c\u5927\u4eba\u7684\u4e00\u5207\u884c\u52a8\uff0c\u90fd\u8981\u5148\u7ecf\u8ba1\u5212\uff0c\u4ed6\u4eec\u53ef\u80fd\u626e \u6210\u6700\u5bb9\u6613\u63a5\u8fd1\u4f60\u7684\u4eba\uff0c\u7a81\u7136\u51fa\u5200\u65bd\u88ad\uff0c\u5c31\u7b97\u6211\u7b49\u5728\u4fa7\uff0c\u6050\u4e5f\u662f\u9632\u4e0d\u80dc\u9632\u3002\u201d \u3000\u3000\u201c\u8fd9\uff0c\u8fd9\u662f\u54ea\u91cc\u8bf4\u8d77\u5440\uff01\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u81ea\u53e4\u81f3\u4eca\uff0c\u6050\u6015\u6ca1\u6709\u4e00\u4e2a\u56db\u54c1\u7687\u5802\u7684\u77e5\u5e9c\uff0c\u505a \u7684\u50cf\u6211\u738b\u67d0\u4eba\u8fd9\u6837\u7684\u8f9b\u82e6\u4e86\uff1f\u201d \u3000\u3000\u201c\u80fd\u8005\u591a\u52b3\uff0c\u8d24\u8005\u906d\u5992\uff0c\u81ea\u53e4\u7686\u7136\u3002\u201d\u675c\u671b\u6708\u7b11\u9053\uff1a\u201c\u4e5f\u6ca1\u6709\u4e00\u4e2a\u56db\u54c1\u7687\u5802\u7684\u77e5\u5e9c\uff0c\u8ba9 \u5211\u90e8\u52a8\u5458\u4e86\u5929\u4e0b\u6700\u7cbe\u9510\u7684\u6355\u5934\uff0c\u96c6\u4e2d\u4e00\u5730\uff0c\u4fdd\u62a4\u4ed6\u7684\u5b89\u5168\u4e86\u3002\u201d\u738b\u5c11\u537f\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u60f3 \u5230\u6b64\u5904\uff0c\u4e0b\u5b98\u4e4b\u5e78\u4e5f\uff01\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u5927\u4eba\u8981\u4e3a\u56fd\u73cd\u60dc\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4e5f\u5e0c\u671b\u80fd\u548c\u6211\u7b49\u591a\u65b9\u914d\u5408\uff0c\u8bf1\u6355\u90a3\u4e9b\u6740\u624b\u3001 \u523a\u5ba2\uff0c\u67e5\u6e05\u695a\u8840\u624b\u65b9\u8f6e\u7684\u5e95\u7ec6\u3002\u7136\u540e\uff0c\u4e00\u7f51\u6253\u5c3d\uff0c\u5f7b\u5e95\u6d88\u9664\u8fd9\u4e2a\u4e3a\u5bb3\u6c5f\u6e56\u7684\u7ec4\u5408\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u7684\u884c\u5f84\uff0c\u5b9e\u5df2\u8d85\u8d8a\u4e86\u6c5f\u6e56\u7684\u89c4\u8303\uff0c\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u4e0b\u5b98\u613f\u534f\u8c03\u6c34\u5e08\u3001\u603b\u9547\uff0c\u8c03\u52a8 \u5927\u519b\uff0c\u52a9\u4f60\u4eec\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u201c\u76db\u60c5\u53ef\u611f\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4f46\u6b64\u4e8b\u5f97\u8bf7\u793a\u603b\u6355\u5934\u540e\uff0c\u624d\u80fd\u51b3\u5b9a\u3002\u73b0\u5728\uff0c\u5148\u7814\u7a76\u51fa\u4e00\u5957 \u4fdd\u62a4\u5927\u4eba\u7684\u65b9\u6cd5\uff0c\u65e2\u8981\u4e25\u5bc6\uff0c\u4e5f\u8981\u6545\u7559\u7834\u7efd\uff0c\u8bf1\u654c\u4eba\u5165\u4f0f\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u8981\u4e0b\u5b98\u2026\u2026\u548c\u8bf8\u4f4d\u914d\u5408\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u672c\u60f3\u8bf4\uff0c\u8fd9\u662f\u8981\u4e0b\u5b98\u4f5c\u8bf1\u654c\u4e4b\u9975\u4e86\uff0c\u8bdd\u5230\u53e3\u8fb9\uff0c\u53c8\u6539\u6210\u4e86\u8bf8\u4f4d\u914d\u5408\u4e4b\u53e5\uff0c\u8868\u73b0\u51fa\u4e86\u5927 \u53ef\u5bb9\u7269\u7684\u6c14\u5ea6\u3002 \u3000\u3000\u56db\u5927\u540d\u6355\u7814\u5546\u51fa\u4e00\u5957\u4fdd\u62a4\u738b\u5c11\u537f\u7684\u8ba1\u5212\uff0c\u8fde\u738b\u77e5\u5e9c\u4e5f\u542c\u7684\u9891\u9891\u70b9\u5934\u3002 \u3000\u3000\u738b\u592b\u4eba\uff0c\u8fd4\u56de\u539f\u7c4d\u63a2\u4eb2\u672a\u5f52\uff0c\u8fd9\u5c31\u8ba9\u738b\u77e5\u5e9c\u7684\u987e\u8651\u51cf\u53bb\u4e86\u4e0d\u5c11\uff0c\u4e8c\u5802\u4e2d\u6709\u95f4\u5ba2\u5ba4\uff0c\u539f\u662f \u63a5\u5f85\u8d35\u5bbe\u7684\u5730\u65b9\uff0c\u5982\u4eca\u6682\u4f5c\u4e86\u738b\u5927\u4eba\u7684\u5367\u5ba4\u3002\u4e8c\u5802\u623f\u820d\u575a\u7262\uff0c\u56db\u5468\u5ead\u9662\u5e7f\u9614\uff0c\u662f\u4e2a\u6613\u4e8e\u9632\u5b88\uff0c \u4e5f\u53ef\u4ee5\u7531\u591a\u65b9\u4f4d\u65bd\u63f4\u7684\u6240\u5728\u3002 \u3000\u3000\u73b0\u5728\uff0c\u738b\u77e5\u5e9c\u6b63\u5750\u5728\u5367\u623f\u4e2d\u770b\u4e66\u3002 \u3000\u3000\u4eca\u591c\u65e0\u6708\uff0c\u5ba4\u5916\u4e00\u7247\u5e7d\u6697\uff0c\u4f46\u5ba4\u5185\u7ea2\u70db\u9ad8\u70e7\uff0c\u7167\u7684\u6ee1\u5ba4\u901a\u660e\u3002 \u3000\u3000\u5df2\u8fd1\u4e8c\u66f4\u65f6\u5206\uff0c\u5ba4\u5916\u7a81\u7136\u54cd\u8d77\u4e86\u53e9\u95e8\u4e4b\u58f0\u3002 \u3000\u3000\u8fd9\u4e8c\u5802\u8868\u9762\u4e0a\u770b\u4e0d\u51fa\u6709\u4eba\u9632\u536b\uff0c\u4f46\u738b\u77e5\u5e9c\u5fc3\u7533\u660e\u767d\uff0c\u5165\u591c\u4e4b\u540e\uff0c\u4e8c\u5802\u5916\u9762\uff0c\u81f3\u5c11\u5341\u4e8c\u4e2a \u8859\u5f79\u5728\u503c\u73ed\u9632\u5b88\uff0c\u738b\u575a\u526f\u603b\u6355\u5934\u548c\u4f55\u5927\u5149\u62c5\u8d1f\u5de1\u67e5\u4e4b\u804c\uff0c\u80fd\u591f\u98de\u5165\u4e00\u53ea\u9e1f\u7684\u5730\u65b9\uff0c\u5927\u6982\u90fd\u6709 \u4eba\u5b88\u536b\u3002 \u3000\u3000\u5982\u6b64\u4e00\u4e2a\u68ee\u4e25\u7684\u9632\u5b88\uff0c\u600e\u80fd\u5bb9\u8bb8\u5916\u4eba\u8fdb\u5165\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u8fd8\u6709\u56db\u5927\u6355\u5934\u548c\u60dc\u7389\u59d1\u5a18\uff0c\u4e5f\u5728\u4e8c\u5802\u5185\u5916\u5de1\u89c6\u3002\u3000\u8fd9\u5730\u65b9\u5e94\u8be5\u662f\u5341\u5206\u5b89\u5168\u3002 \u3000\u3000\u4f46\u738b\u77e5\u5e9c\u4ecd\u6781\u4e3a\u5c0f\u5fc3\uff0c\u5728\u5367\u5ba4\u95e8\u5185\u52a0\u4e86\u6728\u6813\u3002 \u3000\u3000\u6253\u5f00\u6728\u95e8\u65f6\uff0c\u5148\u95fb\u5230\u4e00\u80a1\u5f15\u53d1\u98df\u6b32\u7684\u9152\u83dc\u9999\u6c14\uff0c\u4e00\u4e2a\u9752\u8863\u5973\u5a62\uff0c\u624b\u6367\u6728\u76d8\uff0c\u5f53\u95e8\u800c\u7acb\u3002 \u3000\u3000\u738b\u5c11\u537f\u75be\u9000\u4e24\u6b65\uff0c\u51c6\u5907\u5173\u4e0a\u6728\u95e8\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u4e0d\u8ba4\u8bc6\u8fd9\u4e2a\u5973\u5a62\uff0c\u738b\u592b\u4eba\u5f52\u5b81\u4e4b\u540e\uff0c\u5e9c\u8859\u4e4b\u4e2d\uff0c\u8fd8\u7559\u6709\u4e24\u4e2a\u4e2b\u5934\uff0c\u4f46\u738b\u5927\u4eba\u90fd \u89c1\u8fc7\u9762\uff0c\u8fd9\u4e2a\u59d1\u5a18\uff0c\u7edd\u4e0d\u662f\u4e24\u4e2a\u5973\u5a62\u4e4b\u4e00\uff0c\u6700\u91cd\u8981\u7684\u539f\u56e0\uff0c\u662f\u5979\u957f\u7684\u592a\u7f8e\u4e86\u3002 \u3000\u3000\u770b\u4e0a\u53bb\uff0c\u90a3\u9752\u8863\u5973\u5a62\u5f88\u6587\u79c0\uff0c\u6587\u79c0\u7684\u6709\u70b9\u513f\u7626\u5f31\uff0c\u52a8\u4f5c\u4e5f\u5f88\u65af\u6587\uff0c\u4f46\u884c\u52a8\u5374\u5f88\u5feb\u901f\uff0c\u5feb \u7684\u738b\u5927\u4eba\u6765\u4e0d\u53ca\u5173\u4e0a\u6728\u95e8\uff0c\u5979\u5df2\u884c\u5165\u4e86\u5367\u5ba4\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u4eba\u8fd9\u51e0\u5929\u592a\u5fd9\u4e86\uff0c\u5403\u4e0d\u597d\uff0c\u4e5f\u7761 \u4e0d\u5b89\uff0c\u5c0f\u5a62\u7096\u4e86\u4e00\u7897\u4eba\u53c2\u9e21\u6c64\u6765\uff0c\u66ff\u5927\u4eba\u8865\u8865\u8eab\u5b50\u3002\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u4eba\u5374\u76f4\u5411\u4e66\u6848\u8fb9\u884c\u53bb\uff0c\u770b \u6837\u5b50\uff0c\u662f\u51c6\u5907\u7559\u4e0b\u6765\u4e86\uff0c\u81f3\u5c11\u8981\u7b49\u738b\u5927\u4eba\u5403\u5b8c\u53c2\u6c64\u518d\u8d70\uff01 \u3000\u3000\u738b\u5927\u4eba\u5fc3\u4e2d\u6697\u6697\u5403\u60ca\uff0c\u5fd6\u9053\uff1a\u738b\u575a\u5462\uff1f\u4f55\u5927\u5149\u5462\uff1f\u8fd9\u5ba4\u95e8\u88ab\u53e9\u4e86\u4e0d\u5c11\u8fb0\u5149\uff0c\u600e\u4e48\u4f1a\u5168\u65e0 \u53cd\u5e94\uff0c\u8fde\u56db\u5927\u6355\u5934\u548c\u60dc\u7389\u4e5f\u662f\u4e0d\u95fb\u8baf\u606f\uff0c\u4e0d\u95fb\u56de\u58f0\uff0c\u96be\u9053\u4ed6\u4eec\u90fd\u53d7\u4e86\u6697\u7b97\u4e0d\u6210\u3002 \u3000\u3000\u5fc3\u4e2d\u867d\u7136\u5df2\u6709\u4e9b\u614c\u4e71\uff1f\u4f46\u4ed6\u8868\u9762\u4e0a\u8fd8\u6c89\u5f97\u4f4f\u6c14\uff0c\u90a3\u9752\u8863\u5973\u5a62\u767b\u5802\u5165\u5ba4\uff0c\u884c\u5411\u4e66\u6848\u3002 \u3000\u3000\u4f46\u738b\u77e5\u5e9c\u5374\u53cd\u4e3b\u4e3a\u5bbe\uff0c\u5feb\u6b65\u5411\u95e8\u5916\u884c\u53bb\u3002 \u3000\u3000\u201c\u5927\u4eba\uff01\u201d\u9752\u8863\u5973\u5a62\u653e\u4e0b\u53c2\u6c64\uff0c\u4e00\u56de\u5934\uff0c\u738b\u5c11\u537f\u5df2\u8d70\u5230\u4e86\u623f\u95e8\u53e3\u5904\u3002 \u3000\u3000\u4ed6\u5df2\u89c1\u8bc6\u8fc7\u5c0f\u6587\u7684\u6b66\u529f\uff0c\u4e0d\u80fd\u5c0f\u770b\u5f31\u4e0d\u80dc\u529b\u7684\u5c0f\u59d1\u5a18\uff0c\u4e00\u65e6\u53cd\u8138\u51fa\u5200\uff0c\u6740\u4eba\u5c31\u50cf\u780d\u74dc\u5207 \u83dc\u4e00\u6837\uff01\u6240\u4ee5\uff0c\u738b\u77e5\u5e9c\u4e0d\u4f1a\u56e0\u5979\u751f\u7684\u5a07\u5c0f\u7f8e\u4e3d\uff0c\u6709\u6240\u758f\u5ffd\u3002 \u3000\u3000\u9752\u8863\u5973\u5a62\u6765\u7684\u597d\u5feb\uff0c\u4e00\u4e2a\u5927\u8f6c\u8eab\uff0c\u4eba\u5c31\u5230\u4e86\u5367\u5ba4\u95e8\u53e3\uff0c\u6321\u4f4f\u738b\u5927\u4eba\u7684\u53bb\u8def\u3002\u201c\u59d1\u5a18\uff0c\u4f60 \u8fd9\u662f\u5e72\u4ec0\u4e48\u5440\uff1f\u201d \u3000\u3000\u201c\u5927\u4eba\uff0c\u53c8\u60f3\u5e72\u4ec0\u4e48\u5462\uff1f\u201d\u9752\u8863\u5973\u5a62\u7b54\u975e\u6240\u95ee\u5730\u9053\uff1a\u201c\u591c\u8272\u5e7d\u6697\uff0c\u5927\u4eba\u6700\u597d\u662f\u5b88\u5728\u623f\u4e2d\uff0c \u9700\u77e5\u6697\u5904\u6709\u9b3c\u5440\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u7684\u80c6\u5b50\uff0c\u7a81\u7136\u5927\u4e86\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u201c\u5bf9\uff01\u591c\u6697\u6709\u9b3c\uff0c\u4f46\u4e0d\u77e5\u662f\u7537\u9b3c\uff0c\u8fd8\u662f\u5973\u9b3c\uff1f\u4e0d \u8fc7\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8fc7\u600e\u6837\uff1f\u201d \u3000\u3000\u201c\u7537\u9b3c\u4e5f\u7f62\uff0c\u5973\u9b3c\u4e5f\u597d\uff0c\u51e1\u662f\u9b3c\u90fd\u4f1a\u5bb3\u4eba\u3002\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u59d1\u5a18\u80fd\u591c\u5165\u5e9c\u8859\uff0c\u8db3\u89c1\u9ad8\u660e\uff0c \u6709\u4ec0\u4e48\u76ee\u7684\uff0c\u73b0\u5728\uff0c\u53ef\u4ee5\u8bf4\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u5509\uff01\u5f88\u96be\u542f\u9f7f\u554a\u3002\u201d\u9752\u8863\u5973\u5a62\u5a07\u7f9e\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf4\u51fa\u6765\uff0c\u5927\u4eba\u5982\u4e0d\u540c\u610f\uff0c\u53eb\u6211\u4e2a\u5973\u5b69 \u5bb6\u5982\u4f55\u4e0b\u53f0\uff1f\u201d \u3000\u3000\u8a00\u8bcd\u66a7\u6627\uff0c\u7f9e\u6001\u66f4\u662f\u52a8\u4eba\uff0c\u738b\u5927\u4eba\u867d\u5df2\u660e\u77e5\u8eab\u5728\u9669\u5883\u4e4b\u4e2d\uff0c\u4f46\u4ecd\u770b\u7684\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a \u201c\u8bf4\u8bf4\u770b\u5427\uff1f\u4e5f\u8bb8\u4e0b\u5b98\u4e00\u65f6\u8ff7\u7cca\uff0c\u5c31\u7b54\u5e94\u4e86\u5462\uff01\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u6211\u60f3\u501f\u4f60\u4e00\u6761\u624b\u81c2\uff0c\u201d\u9752\u8863\u5973\u5b50\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u53ea\u6709\u4e00\u4e2a\u8111\u888b\uff0c\u4f46\u5374\u6709\u4e24\u6761 \u624b\u81c2\uff0c\u4f46\u7ed9\u6211\u4e00\u6761\uff0c\u6700\u591a\u662f\u75db\u82e6\u4e00\u9635\uff0c\u7edd\u5bf9\u662f\u4e0d\u4f1a\u6b7b\u7684\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u6c5f\u6e56\u4e2d\u4eba\uff0c\u591a\u7ec3\u6210\u6b8b\u9177\u6027\u683c\uff0c\u6740\u4eba\u6b8b\u80a2\u7684\u5927\u4e8b\uff0c\u5728\u4e00\u4e2a\u5982\u6b64\u7f8e\u4e3d\u59d1\u5a18 \u53e3\u4e2d\u8bf4\u51fa\u6765\uff0c\u7adf\u7136\u662f\u5982\u53d9\u5bb6\u5e38\uff0c\u8bf4\u7684\u5e73\u6de1\u65e0\u5947\u3002\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u4eba\u5374\u6447\u6447\u5934\u82e6\u7b11\u9053\uff1a\u201c\u679c\u7136\u662f\u4eba \u5982\u82b1\u822c\u5a07\uff0c\u5fc3\u4f3c\u86c7\u3001\u874e\u6bd2\uff0c\u65e2\u662f\u501f\uff0c\u4e0b\u5b98\u662f\u53ef\u4ee5\u62d2\u7edd\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u53ef\u4ee5\uff0c\u5c0f\u59b9\u65e2\u7136\u5f00\u4e86\u53e3\uff0c\u4e5f\u4e0d\u5bb9\u8bb8\u522b\u4eba\u62d2\u7edd\u3002\u201d\u9752\u8863\u5c11\u5973\u7b11\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u662f\u8981\u4f60\u6027 \u547d\uff0c\u53ea\u662f\u501f\u4e00\u6761\u624b\u81c2\u6765\u73a9\u73a9\uff0c\u4f60\u600e\u80fd\u5bf9\u4e00\u4e2a\u5c0f\u5973\u5b69\u5982\u6b64\u541d\u60dc\uff1f\u201d\u7a81\u7136\u4e00\u626c\u624b\uff0c\u4e00\u628a\u5bd2\u8292\u68ee\u68ee \u7684\u5229\u5203\uff0c\u5df2\u642d\u5728\u738b\u77e5\u5e9c\u5de6\u80a9\u4e4b\u4e0a\u3002 \u3000\u3000\u5979\u672c\u662f\u53cc\u624b\u7a7a\u7a7a\uff0c\u4e0d\u89c1\u5175\u5203\uff0c\u4f46\u4e00\u626c\u624b\u95f4\uff0c\u7adf\u591a\u51fa\u4e00\u628a\u51b7\u68ee\u68ee\u7684\u725b\u8033\u5c16\u5200\u3002\u738b\u5c11\u537f\u5fc3\u5934 \u4e00\u9635\u5267\u70c8\u7684\u8df3\u52a8\uff0c\u5fd6\u9053\uff1a\u600e\u4e48\u56de\u4e8b\u554a\uff01\u5200\u90fd\u67b6\u5728\u80a9\u8180\u4e0a\u4e86\uff0c\u8fd8\u4e0d\u89c1\u4eba\u6765\u62a4\u9a7e\uff0c\u8fd9\u4e0d\u6b62\u8ba9\u6211\u4f5c \u8bf1\u9975\uff0c\u7b80\u76f4\u662f\u62ff\u6211\u7684\u6027\u547d\u5f00\u73a9\u7b11\u4e86\u3002\u770b\u8fd9\u9752\u8863\u5c11\u5973\uff0c\u51fa\u5200\u50cf\u53d8\u620f\u6cd5\u4e00\u6837\u5feb\u901f\uff0c\u53ea\u8981\u4e00\u52a8\u624b\u8155\uff0c \u5c31\u4f1a\u5207\u4e0b\u6211\u4e00\u6761\u5de6\u81c2\uff0c\u73b0\u5728\uff0c\u5c31\u7b97\u56db\u5927\u6355\u5934\u51fa\u9762\uff0c\u53ea\u6015\u4e5f\u6765\u4e0d\u53ca\u62a2\u6551\u6211\u8fd9\u6761\u624b\u81c2\u4e86\uff0c\u771f\u662f\u4e00 \u5ff5\u8f7b\u8bfa\u5343\u53e4\u6068\uff0c\u518d\u56de\u9996\u5df2\u767e\u5e74\u8eab\u3002\u9752\u8863\u5c11\u5973\u5fae\u7b11\u5982\u82b1\u5730\u9053\uff1a\u201c\u77e5\u5e9c\u5927\u4eba\uff0c\u4f60\u8fd9\u5e9c\u4e2d\u7684\u9632\u536b\u592a \u677e\u61c8\u4e86\uff0c\u6211\u6765\u7684\u5f88\u5c0f\u5fc3\uff0c\u4f46\u5374\u51fa\u5947\u7684\u987a\u5229\u2026\u2026\u201d \u3000\u3000\u5f3a\u5fcd\u4e0b\u5fc3\u4e2d\u7684\u60ca\u6050\u548c\u6124\u6012\uff0c\u6545\u4f5c\u8f7b\u677e\u5730\u7b11\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u501f\u624b\u81c2\u5c31\u4e0d\u5982\u501f\u8111\u888b\u4e86\u3002\u201d\u738b \u5c11\u537f\u7adf\u7136\u8f6c\u8fc7\u8eab\u5b50\uff0c\u5411\u4e66\u6848\u524d\u884c\u53bb\uff0c\u53cd\u6b63\u662f\u8d70\u4e0d\u4e86\u5566\uff01\u8ba9\u5200\u67b6\u5728\u8116\u5b50\u4e0a\uff0c\u65e2\u6050\u6016\u53c8\u96be\u8fc7\uff0c\u65e2 \u7136\u662f\u65e0\u80fd\u963b\u6b62\u7684\u4e8b\uff0c\u53ea\u6709\u542c\u5929\u7531\u547d\uff0c\u4f38\u5934\u662f\u4e00\u5200\uff0c\u7f29\u5934\u4e5f\u4e00\u5200\uff0c\u4f55\u4e0d\u6b7b\u7684\u82f1\u96c4\u4e00\u70b9\uff01 \u3000\u3000\u60f3\u7684\u662f\u4e0d\u9519\uff0c\u4f46\u8eab\u5b50\u98a4\u6296\uff0c\u8fc8\u6b65\u7ef4\u8270\uff0c\u4f3c\u4e4e\u4e24\u6761\u817f\u5168\u90fd\u4e0d\u542c\u4f7f\u5524\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u786c\u7740\u5934\u76ae\u5f3a\u6491\u7740\uff0c\u8170\u5e72\u771f\u7684\u50cf\u6728\u7aff\uff0c\u5f3a\u8feb\u7740\u53cc\u817f\u5411\u524d\u8d70\uff01\u8d70\u7684\u867d\u7136\u5f88\u8f9b\u82e6\uff0c\u4f46\u8fd8 \u662f\u8d70\u5230\u4e66\u6848\u524d\u9762\uff0c\u5750\u5728\u6848\u524d\u9762\u7684\u6728\u6905\u4e0a\u3002 \u3000\u3000\u6848\u4e0a\u7ea2\u70db\u878d\u878d\uff0c\u706f\u5149\u66f4\u89c9\u660e\u4eae\uff0c\u7ecf\u8fc7\u8fd9\u4e00\u9635\u54ac\u7259\u786c\u6491\uff0c\u597d\u50cf\u628a\u738b\u77e5\u5e9c\u7684\u80c6\u5b50\u4e5f\u6491\u5927\u4e86\uff0c \u817f\u4e5f\u53ef\u4ee5\u6253\u5f2f\u4e86\uff0c\u8eab\u5b50\u4e5f\u4e0d\u518d\u98a4\u6296\uff0c\u601d\u8651\u6e05\u660e\uff0c\u6c14\u606f\u5e73\u987a\uff0c\u597d\u50cf\u5ffd\u7136\u95f4\u628a\u751f\u6b7b\u5927\u4e8b\u770b\u5f00\u4e86\u3002 \u3000\u3000\u9752\u8863\u5c11\u5973\u7f13\u6b65\u903c\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u4e0d\u501f\u624b\u81c2\u4e5f\u884c\uff0c\u4f46\u8981\u8ddf\u6211\u8d70\uff01\u6211\u76f8\u4fe1\u4f60\u6709\u80fd\u529b\u65a5\u9000\u62e6\u622a \u6211\u4eec\u7684\u6355\u5934\u3001\u8859\u5f79\uff0c\u8fd9\u5c31\u7686\u5927\u6b22\u559c\u4e86\uff0c\u4f60\u53ef\u4ee5\u6beb\u53d1\u65e0\u4f24\uff0c\u6211\u4e5f\u4e0d\u7528\u62da\u547d\u7a81\u56f4\uff0c\u8d70\u7684\u6f47\u6d12\uff0c\u4e5f \u4e0d\u5e26\u4e00\u70b9\u8840\u8165\u6c14\u3002\u201d \u3000\u3000\u201c\u55ef\uff01\u8bf4\u7684\u6709\u7406\uff0c\u4f46\u5230\u54ea\u91cc\u53bb\u5462\uff1f\u201d\u738b\u5927\u4eba\u9053\uff1a\u201c\u6c34\u5e08\u7cbe\u9510\u3001\u603b\u9547\u9a81\u9a91\uff0c\u5927\u6279\u519b\u9a6c\u4f1a\u7d27 \u8ffd\u4e0d\u820d\uff0c\u6211\u4eec\u8d70\u4e0d\u8fdc\u7684\u3002\u201d \u3000\u3000\u201c\u5e78\u597d\uff0c\u6211\u4eec\u53bb\u7684\u5730\u65b9\u4e5f\u4e0d\u8fdc\uff0c\u201d\u9752\u8863\u5c11\u5973\u9053\uff1a\u201c\u6709\u6211\u966a\u7740\u4f60\uff0c\u5f53\u7136\u4e5f\u5f88\u5b89\u5168\u2026\u2026\u201d \u3000\u3000\u738b\u77e5\u5e9c\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u4f1a\u5c31\u5728\u626c\u5dde\u57ce\u4e2d\u5427\uff01\u4f55\u51b5\uff0c\u76ee\u4e0b\u7684\u626c\u5dde\u57ce\uff0c\u4e5f\u662f\u6c5f\u6e56\u98ce\u96e8\u6b63 \u5728\u7d27\u6025\uff0c\u54b1\u4eec\u5f88\u53ef\u80fd\u4f1a\u9047\u4e0a\u53e6\u4e00\u6279\u6c5f\u6e56\u4eba\u7269\u7684\u622a\u6740\uff0c\u5c82\u4e0d\u662f\u5200\u5ba2\u9047\u4e0a\u622a\u8def\u7684\uff0c\u9020\u4e00\u573a\u81ea\u76f8\u6b8b \u6740\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u90a3\u4e48\u5de7\u5427\uff01\u201d\u9752\u8863\u5c11\u5973\u9053\uff1a\u201c\u5c31\u7b97\u9047\u4e0a\u4e86\uff0c\u6211\u4e5f\u6709\u5e94\u4ed8\u4e4b\u7b56\u3002\u4e0d\u8fc7\uff0c\u65f6\u95f4\u4e0d\u5b9c\u518d \u62d6\u5ef6\u4e0b\u4e0b\u53bb\uff0c\u4f60\u8981\u7acb\u523b\u51b3\u5b9a\u3002\u201d \u3000\u3000\u738b\u5927\u4eba\u5fc3\u4e2d\u5f88\u706b\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u6211\u62ff\u7740\u6027\u547d\u5728\u62d6\u5ef6\u8fb0\u5149\uff0c\u547d\u90fd\u73a9\u53bb\u4e86\u4e00\u5927\u534a\uff0c\u5168\u4e0d\u89c1\u4e00\u70b9 \u53cd\u5e94\uff0c\u5929\u4e0b\u56db\u5927\u540d\u6355\u4f1a\u5546\u51fa\u7684\u4fdd\u62a4\u529e\u6cd5\uff0c\u542c\u8d77\u6765\u5f88\u52a8\u4eba\uff0c\u600e\u4e48\u6267\u884c\u8d77\u6765\uff0c\u5168\u4e0d\u662f\u8fd9\u4e48\u56de\u4e8b\u554a\uff1f \u3000\u3000\u4f46\u95fb\u6728\u95e8\u5440\u7136\uff0c\u53c8\u4e00\u4e2a\u9752\u8863\u5973\u5a62\u7aef\u7740\u6728\u76d8\u51b2\u8fdb\u4e86\u5367\u5ba4\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u4e00\u773c\u5c31\u770b\u51fa\uff0c\u6765\u4eba\u662f\u60dc\u7389\u59d1\u5a18\uff0c\u4e0d\u8fc7\uff0c\u90a3\u8eab\u8863\u670d\u5374\u662f\u4e2b\u73af\u7a7f\u7740\u4e4b\u7269\uff0c\u770b\u8d77\u6765\uff0c\u5c31 \u4e0d\u9732\u7834\u7efd\u4e86\u3002 \u3000\u3000\u60dc\u7389\u597d\u50cf\u6ca1\u6709\u770b\u5230\u90a3\u9752\u8863\u5c11\u5973\uff0c\u53e3\u4e2d\u53eb\u9053\uff1a\u201c\u5927\u4eba\uff0c\u4f60\u7684\u9e21\u4e1d\u9762\uff0c\u201d\u5c31\u662f\u4e00\u53e5\u8bdd\uff0c\u4eba\u5df2 \u51b2\u5230\u4e86\u738b\u5c11\u537f\u7684\u9762\u524d\uff0c\u653e\u4e0b\u6728\u76d8\uff0c\u4e00\u4e2a\u56de\u8eab\u8f6c\uff0c\u6321\u5728\u4e86\u738b\u5927\u4eba\u7684\u8eab\u524d\uff0c\u548c\u90a3\u9752\u8863\u5c11\u5973\u6210\u4e86\u9762 \u5bf9\u9762\u7684\u5f62\u52bf\u3002 \u3000\u3000\u738b\u5927\u4eba\u5fc3\u4e2d\u5fd6\u9053\uff1a\u673a\u4f36\u554a\uff01\u4e00\u53e5\u9e21\u4e1d\u9762\uff0c\u9020\u6210\u4e86\u4e00\u70b9\u9519\u89c9\uff0c\u5c31\u5728\u4eba\u4e00\u95ea\u795e\u95f4\uff0c\u5df2\u7136\u5b8c\u6210 \u4e86\u62a4\u4eba\u3001\u5bf9\u654c\u7684\u5e03\u7f72\u3002 \u3000\u3000\u201c\u662f\u884c\u5bb6\u4e5f\u662f\u9ad8\u624b\uff0c\u738b\u5e9c\u4e2d\u7684\u4e2b\u5934\uff0c\u600e\u4f1a\u6709\u5982\u6b64\u51fa\u8272\u7684\u4eba\u7269\uff1f\u201d\u9752\u8863\u5c11\u5973\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c \u4f60\u867d\u7136\u5f88\u4e0d\u9519\uff0c\u4f46\u5374\u672a\u5fc5\u80fd\u4fdd\u4f4f\u4ed6\u4e0d\u53d7\u4f24\u5bb3\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u5e0c\u671b\u4f60\u5192\u9669\u4e00\u8bd5\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u5728\u6781\u7aef\u5371\u6076\u7684\u73af\u5883\u8feb\u903c\u4e4b\u4e0b\uff0c\u6211\u4f1a\u5168\u529b\u53cd\u51fb\uff0c\u53cd \u51fb\u7684\u540e\u679c\uff0c\u5c31\u4e0d\u662f\u6211\u6240\u80fd\u63a7\u5236\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u5c11\u5973\u51b7\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u4f60\u4f1a\u4f24\u4e86\u6211\uff0c\u662f\u5427\uff1f\u201d \u3000\u3000\u201c\u8c01\u77e5\u9053\u5462\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u62da\u5c3d\u5168\u529b\u7684\u4e00\u51fb\uff0c\u5927\u90fd\u662f\u65e0\u6cd5\u63a7\u5236\u7684\u5c40\u9762\uff0c\u4e0d\u662f\u4f60\u6b7b\uff0c\u5c31\u662f \u6211\u4ea1\u3002\u4f46\u5728\u672a\u51fa\u624b\u4e4b\u524d\uff0c\u4f55\u59a8\u5148\u628a\u4e00\u5207\u53ef\u60b2\u7684\u540e\u679c\uff0c\u8bf4\u4e2a\u6e05\u695a\uff1f\u201d \u3000\u3000\u9752\u8863\u5c11\u5973\u53cc\u76ee\u76ef\u4f4f\u5728\u60dc\u7389\u7684\u8138\u4e0a\uff0c\u77a7\u4e86\u597d\u4e00\u9635\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u8bdd\u53e3\u6c14\uff0c\u4f3c\u6709\u70b9\u60b2\u5929\u60af\u4eba \u7684\u5473\u9053\uff0c\u4f46\u6211\u5374\u77a7\u4e0d\u51fa\u4f60\u4f1a\u6709\u4ec0\u4e48\u80fd\u529b\u4fdd\u62a4\u4ed6\u4e0d\u53d7\u4f24\u5bb3\u2026\u2026\u201d\u7a81\u7136\u4e00\u6643\u53cc\u80a9\uff0c\u4eba\u5f71\u95ea\u52a8\uff0c\u6251 \u5411\u738b\u5927\u4eba\u3002 \u3000\u3000\u51e0\u4e4e\u662f\u5728\u540c\u65f6\uff0c\u60dc\u7389\u4e5f\u79fb\u52a8\u4e86\u8eab\u8eaf\u8fce\u4e0a\u53bb\u3002 \u3000\u3000\u738b\u5927\u4eba\u77aa\u7740\u4e24\u53ea\u773c\u775b\u770b\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u5367\u5ba4\u5730\u65b9\u6709\u9650\uff0c\u8eb2\u4e5f\u65e0\u5904\u53ef\u8eb2\uff0c\u53ea\u6709\u5750\u5f85\u7ed3\u679c\u4e86\u3002 \u3000\u3000\u611f\u89c9\u4e2d\uff0c\u90a3\u9752\u8863\u5c11\u5973\uff0c\u4f3c\u4e4e\u5e7b\u5316\u51fa\u56db\u4e94\u4e2a\u8eab\u5f71\uff0c\u4e00\u9f50\u5411\u4ed6\u6251\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4f46\u95fb\u4e24\u58f0\u5a07\u53f1\uff0c\u5bd2\u8292\u95ea\u8f6c\uff0c\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u4e4b\u540e\uff0c\u9759\u6b62\u4e0b\u6765\u3002 \u3000\u3000\u9752\u8863\u5c11\u5973\uff0c\u624b\u4e2d\u591a\u4e86\u4e00\u628a\u725b\u8033\u5c16\u5200\uff0c\u60dc\u7389\u59d1\u5a18\u53f3\u624b\u4e2d\uff0c\u4e5f\u63e1\u4e86\u4e00\u628a\u5c3a\u8bb8\u957f\u77ed\u7684\u950b\u5229\u5315\u9996\u3002 \u5315\u9996\u4f3c\u662f\u8fde\u5728\u4e00\u4e2a\u94f6\u8272\u7684\u624b\u5957\u4e0a\uff0c\u94f6\u8272\u7684\u624b\u5957\u5f88\u957f\uff0c\u76f4\u5165\u8896\u4e2d\u3002 \u3000\u3000\u9752\u8863\u5c11\u5973\u7684\u63e1\u5200\u7684\u53f3\u8155\u4e0a\uff0c\u5168\u4e3a\u9c9c\u8840\u63a9\u906e\uff0c\u770b\u4e0d\u51fa\u6709\u591a\u5c11\u4f24\u53e3\uff0c\u8138\u8272\u4e5f\u5fae\u73b0\u82cd\u767d\uff0c\u795e\u60c5 \u75b2\u840e\uff0c\u548c\u521a\u624d\u795e\u6c14\u6d3b\u73b0\u7684\u60c5\u6001\u5927\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u201c\u6211\u8bf4\u8fc7\u6211\u5982\u5168\u529b\u53cd\u51fb\uff0c\u662f\u65e0\u6cd5\u63a7\u5236\u7684\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u4f60\u7684\u8fd0\u6c14\u4e0d\u9519\uff0c\u53ea\u4f24\u5728\u53f3\u8155\u4e0a\uff0c \u800c\u4e14\u672a\u52a8\u5230\u7b4b\u9aa8\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u624b\u4e0b\u7559\u60c5\uff0c\u4f46\u6211\u4e00\u70b9\u4e5f\u4e0d\u4f1a\u611f\u6fc0\u4f60\uff01\u201d\u9752\u8863\u5c11\u5973\u9053\uff1a\u201c\u4f60\u8fd9\u4e00\u5200\u4e0d\u4f46\u632b\u8d25\u4e86 \u6211\u7684\u6b66\u529f\uff0c\u4e5f\u51fb\u6e83\u4e86\u6211\u7684\u4fe1\u5fc3\u3002\u73b0\u5728\u6211\u53ea\u60f3\u77e5\u9053\uff0c\u4f60\u624b\u4e2d\u7684\u5175\u5203\uff0c\u662f\u4e0d\u662f\u4f20\u8bf4\u4e2d\u94f6\u624b\u5200\uff1f\u201d \u3000\u3000\u60dc\u7389\u70b9\u70b9\u5934\uff0c\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8840\u624b\u65b9\u8f6e\u7684\u624b\u4e0b\uff1f\u201d \u3000\u3000\u9752\u8863\u5c11\u5973\u9053\uff1a\u201c\u662f\uff01\u6740\u624b\u4e4b\u738b\u624b\u4e0b\u6709\u6697\u9999\u3001\u6101\u4e91\u4e24\u4e2a\u5973\u6740\u624b\uff0c\u73b0\u5728\u53ea\u5269\u4e0b\u4e00\u4e2a\u4e86\u2026\u2026\u201d \u7a81\u7136\u4ef0\u8eab\u6454\u5012\u3002 \u3000\u3000\u738b\u5927\u4eba\u5401\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u59d1\u5a18\u662f\u4e0d\u662f\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u5979\u53e3\u4e2d\u542b\u6709\u6bd2\u836f\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u54ac\u7834\u836f\u7269\uff0c\u7acb\u523b\u6bd2\u53d1\u800c\u6b7b\uff01\u201d \u3000\u3000\u201c\u4f60\u65e9\u5c31\u77e5\u9053\u5979\u4f1a\u541e\u836f\u800c\u6b7b\uff0c\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u6551\u5979\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u80fd\u6551\uff01\u201d\u60dc\u7389\u9053\uff1a\u201c\u6211\u5982\u6551\u5979\uff0c\u53ef\u80fd\u6b7b\u7684\u4e0d\u662f\u5979\uff0c\u800c\u662f\u6211\u4e86\uff01\u201d\u7a81\u7136\u6325\u52a8\u94f6\u5200\uff0c\u6311 \u5f00\u9752\u8863\u5c11\u5973\u7684\u5de6\u624b\uff0c\u679c\u89c1\u4e00\u679a\u4e09\u5bf8\u957f\u77ed\u7684\u6bd2\u9488\uff0c\u63e1\u5728\u624b\u4e2d\u3002 \u3000\u3000\u738b\u5c11\u537f\u5401\u53e3\u6c14\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u679c\u7136\u9669\u8bc8\uff01\u201d\u76ee\u5149\u76ef\u5728\u60dc\u7389\u53f3\u624b\u4e0a\u9053\uff1a\u201c\u8fd9\u628a\u5200\u600e\u4e48\u4f1a\u53eb\u94f6 \u624b\u5200\uff1f\u201d \u3000\u3000\u60dc\u7389\u5fae\u5fae\u4e00\u7b11\uff0c\u5fd6\u9053\uff1a\u8fd9\u4f4d\u738b\u5927\u4eba\u597d\u5947\u7684\u5f88\uff0c\u4e3e\u8d77\u53f3\u624b\u8f6c\u4e86\u4e24\u8f6c\uff0c\u90a3\u5957\u5728\u8155\u4e0a\u7684\u624b\u5957\u7a81 \u7136\u4e0d\u89c1\u4e86\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u94f6\u624b\u5200\uff0c\u6307\u7684\u90a3\u4e2a\u94f6\u8272\u7684\u624b\u5957\uff0c\u6211\u4e0d\u5f88\u4e86\u89e3\u5b83\u7684\u6765\u5386\uff0c\u542c\u8bf4\u662f\u5929\u8695 \u4e4b\u4e1d\uff0c\u5408\u4ee5\u94f6\u4e1d\u5236\u6210\uff0c\u5200\u3001\u5251\u96be\u4f24\u3002\u5148\u7236\u4f20\u7ed9\u6211\uff0c\u6211\u7ec3\u4e86\u5341\u5e74\u4ee5\u4e0a\uff0c\u8fd0\u7528\u7684\u975e\u5e38\u7eaf\u719f\uff0c\u4e00\u628a \u950b\u5229\u7684\u94f6\u5200\u8fde\u5728\u624b\u5957\u4e0a\uff0c\u6c5f\u6e56\u4e0a\u5c31\u79f0\u5b83\u4e3a\u94f6\u624b\u5200\u3002\u201d \u3000\u3000\u201c\u4e0d\u6d89\u6c5f\u6e56\u4e8b\uff0c\u600e\u77e5\u6c5f\u6e56\u5b66\u95ee\u6df1\u3002\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u672c\u5e9c\u8fd8\u6709\u5f88\u591a\u4e0d\u89e3\u4e4b\u5904\uff0c\u60f3\u8bf7\u6559\u59d1 \u5a18\u3002\u201d \u3000\u3000\u60dc\u7389\u5fd6\u9053\uff1a\u65e2\u4e0d\u80fd\u62d2\u7edd\u4ed6\u8bf7\u6559\uff0c\u53ea\u597d\u8ba9\u4ed6\u95ee\u4e86\uff0c\u4e0d\u80fd\u56de\u7b54\u7684\u63a8\u5230\u51e0\u4f4d\u5927\u6355\u5934\u8eab\u4e0a\u5c31\u662f\uff0c \u70b9\u70b9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u597d\u5427\uff01\u6211\u8bd5\u8bd5\u770b\u80fd\u4e0d\u80fd\u4e3a\u5927\u4eba\u89e3\u8bf4\u660e\u767d\uff1f\u201d \u3000\u3000\u201c\u4eca\u591c\u6211\u8eab\u9677\u51f6\u5371\uff0c\u751f\u6b7b\u4e00\u53d1\uff0c\u4e0b\u5b98\u4e0d\u660e\u767d\u7684\u662f\uff0c\u56db\u4f4d\u5927\u6355\u5934\u662f\u6545\u610f\u5f04\u9669\uff0c\u8bd5\u8bd5\u4e0b\u5b98\u7684 \u80c6\u91cf\uff0c\u8fd8\u662f\u8ba1\u5212\u4e0d\u5bc6\uff0c\u6f0f\u6d1e\u51fa\u73b0\uff1f\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u8bf4\u51fa\u4e86\u5fc3\u4e2d\u7684\u4e0d\u60a6\uff0c\u4e5f\u6709\u7740\u6d53\u539a\u7684\u8d23\u5907\u610f\u5473\u3002 \u3000\u3000\u60dc\u7389\u6697\u6697\u5401\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8ba9\u5927\u4eba\u53d7\u60ca\u4e86\uff0c\u662f\u6211\u4eec\u7684\u9519\uff0c\u4f46\u4eca\u591c\u53d8\u5316\u51fa\u4eba\u610f\u5916\uff0c\u56db\u5927\u6355\u5934 \u90fd\u88ab\u8bf1\u51fa\u5e9c\u5916\uff0c\u6b63\u5728\u8fdb\u884c\u4e00\u573a\u751f\u6b7b\u4e4b\u6218\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u738b\u575a\u3001\u4f55\u5927\u5149\u5462\uff1f\u4e3a\u4ec0\u4e48\u4e5f\u90fd\u4eba\u5f71\u4e0d\u89c1\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u5f80\u597d\u5904\u8bf4\uff0c\u4ed6\u4eec\u8eab\u9677\u56f0\u5883\uff0c\u65e0\u6cd5\u62bd\u8eab\u3002\u574f\u5904\u8bf4\uff0c\u53ef\u80fd\u8eab\u53d7\u91cd\u4f24\uff0c\u6216\u5df2\u7ecf\u4e3a\u4eba \u727a\u7272\u4e86\u6027\u547d\uff01\u201d \u3000\u3000\u738b\u77e5\u5e9c\u5fc3\u5934\u9707\u52a8\uff0c\u5927\u4e3a\u5403\u60ca\uff0c\u9053\uff1a\u201c\u8fd9\u6279\u6740\u624b\uff0c\u4f3c\u4e4e\u662f\u5fc5\u6b32\u7f6e\u6211\u4e8e\u6b7b\u5730\uff0c\u800c\u540e\u7518\u5fc3\u2026\u2026\u201d \u3000\u3000\u201c\u770b\u6765\uff0c\u786e\u5b9e\u5982\u6b64\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u4eca\u5929\u6765\u4e86\u4e09\u6279\u6740\u624b\uff0c\u7edd\u4e0d\u662f\u6765\u81ea\u4e00\u5904\u3002\u5c0f\u5973\u5b50\u4e5f\u6709\u4e9b \u60f3\u4e0d\u901a\uff0c\u7a76\u7adf\u662f\u8c01\u8981\u6740\u4f60\uff0c\u7adf\u7136\u80fd\u52a8\u5458\u4e86\u6c5f\u5357\u9053\u4e0a\u7684\u6740\u624b\u7cbe\u82f1\u3002\u4e00\u4e2a\u8840\u624b\u65b9\u8f6e\uff0c\u4e0d\u53ef\u80fd\u6709\u5982 \u6b64\u96c4\u539a\u7684\u52bf\u529b\uff0c\u5927\u4eba\u5fc3\u4e2d\u603b\u8be5\u6709\u4e2a\u6982\u5ff5\u5427\uff1f\u201d \u3000\u3000\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u5211\u90e8\u7684\u7a0b\u603b\u6355\u5934\u5462\uff1f\u5979\u624d\u662f\u4e86\u89e3\u6765\u9f99\u53bb\u8109\u7684\u4eba\uff0c\u672c\u5e9c\u867d\u7136\u662f\u751f\u6d3b\u5728\u4e00\u65e5\u6570 \u60ca\u7684\u9669\u6076\u73af\u5883\u4e4b\u4e2d\uff0c\u4f46\u5374\u60f3\u4e0d\u51fa\u8c01\u4f1a\u8ddf\u6211\u6709\u5982\u6b64\u7684\u6df1\u4ec7\u5927\u6068\uff0c\u975e\u5f97\u6740\u4e86\u6211\u624d\u80fd\u5e73\u590d\u4ed6\u80f8\u4e2d\u4e4b \u6c14\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u662f\u4ec7\u6068\u7d2f\u79ef\u7684\u5fff\u6012\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u6211\u8eab\u8d1f\u6740\u7236\u4e4b\u4ec7\uff0c\u5fff\u6068\u6df1\u5207\uff0c\u4e00\u65e6\u660e\u4e86\u4ec7\u4eba\u8eab \u4efd\uff0c\u4e5f\u8bb8\u4f1a\u4e0d\u987e\u4e00\u5207\u627e\u4e0a\u95e8\u53bb\uff0c\u51b3\u4e00\u6b7b\u6218\uff0c\u4f46\u662f\u8fd9\u548c\u4f60\u7684\u60c5\u51b5\u5168\u7136\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u738b\u5927\u4eba\u5ffd\u7136\u53d1\u89c9\u4e86\u60dc\u7389\u59d1\u5a18\u8fc7\u4eba\u7684\u624d\u6167\uff0c\u6790\u4e8b\u4e4b\u80fd\uff0c\u4e0d\u5728\u4ed6\u738b\u5c11\u537f\u4e4b\u4e0b\uff0c\u5fc3\u4e2d\u6709\u70b9\u6298\u670d \u4e86\uff0c\u8fde\u8a00\u8bed\u884c\u4e8b\uff0c\u4e5f\u53d8\u7684\u5ba2\u6c14\u8d77\u6765\uff0c\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u9ad8\u8bba\u3001\u9ad8\u8bba\uff0c\u8bf7\u5750\u5427\uff01\u5982\u82e5\u80fd\u548c\u4e0b\u5b98 \u591a\u8c08\u4e00\u4f1a\uff0c\u4e5f\u8bb8\u80fd\u542f\u793a\u6211\u4e00\u62b9\u7075\u667a\u2026\u2026\u201d \u3000\u3000\u8bdd\u8bf4\u7684\u542b\u84c4\uff0c\u4f46\u4e5f\u660e\u663e\u5730\u8868\u8fbe\u51fa\u4e00\u4e2a\u8baf\u606f\uff0c\u5982\u82e5\u771f\u80fd\u8bf4\u670d\u6211\uff0c\u6211\u4e5f\u4f1a\u8bf4\u51fa\u5fc3\u4e2d\u7684\u53ef\u7591\u4eba \u7269\u2026\u2026 \u3000\u3000\u770b\u8d77\u6765\uff0c\u8fd9\u4ef6\u5927\u4e8b\uff0c\u738b\u77e5\u5e9c\u5fc3\u4e2d\u53ef\u80fd\u5df2\u7ecf\u6709\u8c31\uff0c\u53ea\u662f\u4e0d\u80af\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u60dc\u7389\u5fc3\u4e2d\u4ea6\u662f\u5927\u611f\u9707\u52a8\uff0c\u771f\u662f\u65e0\u5fc3\u63d2\u67f3\u67f3\u6210\u836b\uff0c\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u4eba\u5374\u6545\u4f5c\u8f7b\u677e\u5730\u8bf4\u9053\uff1a\u201c\u5927 \u4eba\u624d\u9ad8\u516b\u6597\uff0c\u5b66\u5bcc\u4e94\u8f66\uff0c\u80f8\u7f57\u9526\u7ee3\uff0c\u535a\u53e4\u901a\u4eca\uff0c\u662f\u5f53\u4ee3\u5b98\u573a\u4e2d\u624d\u5b50\uff0c\u603b\u6355\u5934\u6bcf\u6b21\u63d0\u5230\u5927\u4eba\uff0c \u90fd\u8d5e\u8bb8\u5927\u4eba\u662f\u4e00\u4ee3\u597d\u5b98\uff0c\u5c0f\u5973\u5b50\u80e1\u8bf4\u516b\u9053\u2026\u2026\u201d \u3000\u3000\u738b\u77e5\u5e9c\u7b11\u4e86\uff0c\u7b11\u7684\u5f88\u5f00\u5fc3\uff0c\u63a5\u7740\u9053\uff1a\u201c\u603b\u6355\u5934\u9519\u7231\u4e86\uff0c\u672c\u5e9c\u867d\u7136\u5df2\u5c3d\u529b\u7f57\u81f4\u6587\u3001\u6b66\u9ad8\u624b \u8944\u52a9\u6211\u6cbb\u7406\u626c\u5dde\uff0c\u4e00\u5219\u662f\u79ef\u5f0a\u592a\u6df1\uff0c\u6e05\u9664\u4e0d\u6613\uff0c\u4e8c\u5219\u662f\u4e00\u5730\u77e5\u5e9c\uff0c\u6743\u52bf\u6709\u9650\uff0c\u5f88\u96be\u8bf1\u5230\u771f\u6b63 \u7684\u9ad8\u4eba\u51fa\u5c71\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u4f4f\u5634\uff0c\u5401\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u626f\u8fdc\u4e86\uff0c\u626f\u8fdc\u4e86\uff0c\u4f60\u60dc\u7389\u59d1\u5a18\u4e0d\u662f\u80e1\u8bf4\u516b\u9053\uff0c\u800c\u662f\u8a00\u4e4b \u6709\u7406\uff0c\u6a2a\u770b\u6210\u5cad\uff0c\u4fa7\u6210\u5cf0\uff0c\u662f\u5cfb\u51fa\u7fa4\u5c71\u7684\u9ad8\u89c1\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u6055\u6211\u653e\u8086\u76f4\u8a00\u4e86\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u4f60\u738b\u5927\u4eba\u4e0d\u662f\u6043\u624d\u50b2\u7269\uff0c\u6063\u610f\u5e99\u5802\u7684\u96c5\u58eb\uff0c\u4e5f\u4e0d \u662f\u7eb5\u60c5\u6c5f\u6e56\u7684\u72c2\u751f\uff0c\u800c\u662f\u4e00\u4f4d\u6301\u91cd\u7a33\u5065\u7684\u597d\u5b98\uff0c\u662f\u5e99\u5802\u4e2d\u7684\u680b\u6881\u4e4b\u624d\uff0c\u52a0\u5bb3\u4f60\uff0c\u53ef\u80fd\u662f\u9752\u4e91 \u8def\u7a84\uff0c\u5bb9\u4e0d\u5f97\u53cc\u9a6c\u5e76\u8f94\uff0c\u4e5f\u53ef\u80fd\u4e3a\u9632\u60a3\u672a\u7136\uff0c\u4e0d\u5141\u4f60\u638c\u6743\u6cbb\u56fd\uff0c\u8fd9\u53ef\u80fd\u662f\u51fa\u4e8e\u4e00\u4e8c\u4eba\u7684\u7b56\u8c0b\uff0c \u4f46\u4e5f\u53ef\u80fd\u662f\u4e00\u7fa4\u4e00\u515a\u7684\u4f17\u610f\uff0c\u8fd9\u79cd\u8c0b\u53d6\u56f4\u6740\u7684\u8ba1\u5212\uff0c\u4e0d\u4f46\u9700\u8981\u52bf\u529b\u5f3a\u5927\uff0c\u65e0\u5c3d\u7684\u8d22\u5bcc\u652f\u6491\uff0c \u800c\u4e14\u4f1a\u7ef5\u8fde\u4e0d\u7edd\uff0c\u4e0d\u8fbe\u76ee\u7684\uff0c\u5f88\u96be\u4f11\u6b62\uff0c\u5927\u4eba\u5fc3\u4e2d\u8be5\u6709\u4e2a\u6570\u4e86\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u8138\u8272\u6570\u53d8\uff0c\u8db3\u8db3\u6709\u4e00\u76cf\u8336\u5de5\u592b\u4e4b\u4e45\uff0c\u624d\u6062\u590d\u4e86\u5e73\u9759\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u63d0\u793a\uff0c\u4e0b\u5b98\u83b7\u76ca \u826f\u591a\uff0c\u8fd9\u4ef6\u4e8b\u5bb9\u6211\u4ed4\u7ec6\u5730\u60f3\u4e00\u60f3\uff0c\u7406\u51fa\u4e00\u4e2a\u5934\u7eea\uff0c\u518d\u5411\u59d1\u5a18\u8bf7\u6559\u3002\u201d\u8bdd\u662f\u8bf4\u7684\u5ba2\u6c14\u6781\u4e86\uff0c\u4f46 \u8fd8\u662f\u672a\u6cc4\u6f0f\u51fa\u5fc3\u4e2d\u4e00\u70b9\u9690\u79d8\u3002 \u3000\u3000\u60dc\u7389\u5fc3\u4e2d\u5fd6\u9053\uff1a\u505a\u5b98\u7684\u4eba\uff0c\u5b9e\u5728\u96be\u7f20\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5949\u547d\u4fdd\u62a4\u5c0f\u6587\u59d1\u5a18\uff0c\u4f46\u60c5\u52bf\u8feb \u4eba\uff0c\u53ea\u597d\u5148\u4ee5\u62ef\u6551\u5927\u4eba\u4e3a\u4e3b\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5c0f\u6587\u59d1\u5a18\u5462\uff1f\u201d\u738b\u5927\u4eba\u9053\uff1a\u201c\u4f60\u56e0\u6551\u4e0b\u5b98\uff0c\u800c\u8bef\u4e86\u5c0f\u6587\u59d1\u5a18\uff0c\u90a3\u5c31\u662f\u672c\u5e9c\u7ec8\u751f\u7684\u5927\u6068 \u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u9a70\u63f4\u65f6\uff0c\u6211\u5df2\u628a\u5c0f\u6587\u59d1\u5a18\u9690\u85cf\u8d77\u6765\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u4e0d\u82b1\u8d39\u4e00\u756a\u529f\u592b\uff0c\u5e94\u8be5\u4e0d\u6613\u627e\u5230\u3002 \u73b0\u5728\u5c0f\u5973\u5b50\u6700\u5927\u7684\u4e3a\u96be\u662f\uff0c\u65e0\u6cd5\u5b89\u6392\u5927\u4eba\uff0c\u6211\u53bb\u5b88\u62a4\u5c0f\u6587\u59d1\u5a18\uff0c\u5927\u4eba\u53c8\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u7b11\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u8d70\uff01\u54b1\u4eec\u4e00\u8d77\u53bb\u5b88\u62a4\u5c0f\u6587\u59d1\u5a18\uff0c\u5927\u5fd9\u5e2e\u4e0d\u4e0a\uff0c\u5012\u676f\u6c34\u3001\u62ff\u4e2a\u624b\u5dfe \u7684\u5c0f\u4e8b\uff0c\u6211\u8fd8\u80fd\u505a\u2026\u2026\u201d \u3000\u3000\u201c\u600e\u4e48\u80fd\u5982\u6b64\u59d4\u5c48\u5927\u4eba\uff0c\u201d\u675c\u671b\u6708\u9053\u3002\u5feb\u6b65\u884c\u5165\u5367\u5ba4\uff0c\u76ee\u5149\u4e00\u63a0\u9752\u8863\u5973\u5a62\u7684\u5c38\u4f53\uff0c\u9053\uff1a \u201c\u5509\uff01\u770b\u6765\u5927\u4eba\u4e5f\u9047\u4e0a\u4e86\u4e00\u573a\u51f6\u9669\u2026\u2026\u201d \u3000\u3000\u6b64\u523b\u7684\u738b\u77e5\u5e9c\uff0c\u5fc3\u4e2d\u7684\u6c14\u607c\u65e9\u6d88\uff0c\u63a5\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u8fd8\u597d\uff0c\u60dc\u7389\u59d1\u5a18\u6765\u7684\u53ca\u65f6\uff0c\u672c\u5e9c\u53ea\u53d7 \u4e86\u4e00\u573a\u865a\u60ca\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u3001\u4e8e\u627f\u5fd7\u3001\u5434\u94c1\u5cf0\u9c7c\u8d2f\u884c\u5165\u5367\u5ba4\uff0c\u516d\u9053\u773c\u795e\u4e0d\u7ea6\u800c\u540c\uff0c\u90fd\u6295\u6ce8\u5728\u90a3\u5177\u5c38\u4f53\u4e4b\u4e0a\u3002 \u3000\u3000\u201c\u60dc\u7389\uff0c\u8fd9\u4e2a\u9752\u8863\u59d1\u5a18\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u53f3\u8155\u4e0a\u51e0\u5904\u8840\u8ff9\uff0c\u4e0d\u8be5\u8db3\u81f4\u547d\uff1f\u201d \u3000\u3000\u201c\u54ac\u788e\u4e86\u53e3\u4e2d\u9884\u85cf\u7684\u6bd2\u836f\uff0c\u7acb\u523b\u5c31\u6bd2\u53d1\u8eab\u4ea1\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u662f\u90a3\u79cd\u4ee5\u547d\u640f\u547d\u7684\u6740\u624b\uff0c\u4e0d \u6210\u529f\uff0c\u5b81\u53ef\u6b7b\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u5401\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5c0f\u6587\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u6682\u65f6\u628a\u5979\u85cf\u8d77\u6765\u4e86\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u5e94\u8be5\u5f88\u5b89\u5168\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4f55\u5927\u5149\u3001\u738b\u575a\u5462\uff1f\u96be\u9053\u4e24\u4e2a\u4eba\u90fd\u6218\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u6b7b\uff0c\u4f46\u53d7\u4f24\u5f88\u91cd\u3002\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u8fd9\u6b21\u73b0\u8eab\u7684\u654c\u4eba\u4e0d\u591a\uff0c\u4f46\u5374\u4ee5\u6280\u827a\u7cbe\u6e5b\u3001\u5251\u6cd5 \u4e4b\u5947\uff0c\u53ef\u6bd4\u62df\u5c0f\u6587\u59d1\u5a18\u3002\u738b\u575a\u7684\u80a9\u80db\u88ab\u5251\u52bf\u6d1e\u7a7f\uff0c\u4f55\u5927\u5149\u88ab\u9f50\u8098\u5904\uff0c\u65a9\u65ad\u4e86\u5de6\u5c0f\u81c2\uff0c\u5df2\u88ab\u8859 \u5f79\u4eec\u9001\u5165\u4e86\u9f99\u864e\u56de\u6625\u5802\u533b\u6cbb\u53bb\u4e86\u3002\u201d \u3000\u3000\u738b\u5927\u4eba\u70b9\u70b9\u5934\uff0c\u5fc3\u4e2d\u6697\u6697\u5fd6\u9053\uff1a\u201c\u6628\u65e5\u4e4b\u6218\uff0c\u591a\u4e8f\u5c0f\u6587\uff0c\u4ee5\u5be1\u51fb\u4f17\uff0c\u6b7c\u6740\u4e86\u654c\u4eba\u9ad8\u624b\u591a \u540d\uff0c\u56db\u5927\u6355\u5934\u624d\u80fd\u5408\u529b\u51fb\u9000\u4e86\u5f3a\u654c\u3002\u4eca\u591c\u4e00\u5f79\uff0c\u56db\u5927\u6355\u5934\u4e5f\u662f\u5408\u529b\u62d2\u654c\uff0c\u7adf\u7136\u4f7f\u9884\u5b9a\u7684\u8ba1\u5212 \u5927\u4e71\uff0c\u5982\u975e\u60dc\u7389\u89c1\u673a\u5e94\u53d8\uff0c\u53ca\u65f6\u51fa\u73b0\uff0c\u4ee5\u4e00\u67c4\u8fde\u4e0a\u4e00\u53ea\u624b\u5957\u7684\u602a\u5200\uff0c\u4e00\u62db\u4f24\u654c\uff0c\u6b64\u65f6\u6a2a\u5c38\u4e8c \u5802\u7684\u4e0d\u662f\u8fd9\u4f4d\u9752\u8863\u59d1\u5a18\uff0c\u53ea\u80fd\u662f\u6211\u8fd9\u5e9c\u53f0\u5927\u4eba\u4e86\u3002 \u3000\u3000\u6211\u6240\u4ed7\u6301\u7684\u738b\u575a\u548c\u4f55\u5927\u5149\uff0c\u7adf\u662f\u4e0d\u582a\u4e00\u51fb\uff0c\u4f46\u770b\u4e0a\u5a07\u5f31\u65e0\u529b\u3001\u4e0d\u8db3\u62a4\u6211\u7684\u5c0f\u6587\u59d1\u5a18\uff0c\u5374 \u5728\u4fdd\u62a4\u6211\u7684\u8fc7\u7a0b\u4e2d\uff0c\u5c55\u73b0\u51fa\u4e86\u7edd\u4e16\u6b66\u529f\uff0c\u5a01\u98ce\u516b\u9762\uff0c\u5c82\u771f\u662f\u6211\u7684\u53cc\u773c\u660f\u82b1\u4e86\uff0c\u770b\u4eba\u6599\u4e8b\uff0c\u600e \u4f1a\u6709\u5982\u6b64\u5927\u7684\u5dee\u8ddd\u5462\uff1f\u770b\u4f3c\u52c7\u6b66\u4e4b\u58eb\uff0c\u4e0d\u582a\u4e00\u51fb\uff0c\u82b1\u679d\u4eba\u6837\u7684\u5c0f\u59d1\u5a18\uff0c\u5374\u53c8\u5177\u6709\u4e86\u51fa\u4eba\u60f3\u50cf \u7684\u6280\u827a\u3001\u6b66\u529f\uff0c\u4e3a\u4ec0\u4e48\u5916\u5f62\u4e0a\uff0c\u4e00\u70b9\u4e5f\u77a7\u4e0d\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u773c\u770b\u738b\u5927\u4eba\u4f4e\u7740\u5934\uff0c\u53ea\u7ba1\u60f3\u81ea\u5df1\u5fc3\u4e8b\uff0c\u675c\u671b\u6708\u53ea\u597d\u7b11\u4e00\u7b11\uff0c\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u5927\u4eba\uff0c \u4eca\u591c\u6765\u4eba\u4f3c\u975e\u540c\u51fa\u4e00\u6e90\uff0c\u4ed6\u4eec\u7528\u7684\u65b9\u6cd5\u4e0d\u540c\uff0c\u4f46\u624b\u6bb5\u5374\u6781\u5176\u6bd2\uff0c\u8fd9\u4e2a\u60c5\u51b5\u2026\u2026\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u6765\u81ea\u56db\u9762\u516b\u65b9\u7684\u7684\u56f4\u6740\uff0c\u5fc5\u6b32\u7f6e\u4e0b\u5b98\u4e8e\u6b7b\u5730\u800c\u540e\u5feb\u3002\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u8fd9\u5c31\u8981\u501f \u91cd\u56db\u4f4d\u6355\u5934\u7684\u5927\u529b\u4e86\uff0c\u67e5\u4e00\u67e5\u4ed6\u4eec\u662f\u6765\u81ea\u4f55\u65b9\u7684\u795e\u5723\uff1f\u201d \u3000\u3000\u4e00\u53e5\u8bdd\uff0c\u5835\u5f97\u56db\u5927\u6355\u5934\u4e2a\u4e2a\u8138\u8272\u5fae\u7ea2\uff0c\u54d1\u53e3\u65e0\u8a00\u3002 \u3000\u3000\u4f46\u6c89\u7a33\u7684\u5434\u94c1\u5cf0\u4e0d\u5403\u8fd9\u4e2a\uff0c\u8f7b\u54b3\u4e86\u4e24\u58f0\uff0c\u9053\uff1a\u201c\u4eca\u591c\u5bf9\u654c\u4e4b\u65f6\uff0c\u5434\u67d0\u53d1\u73b0\u4e86\u4e00\u79cd\u6781\u4e3a\u5c11 \u89c1\u7684\u6697\u5668\u2026\u2026\u201d \u3000\u3000\u201c\u60f3\u5fc5\u662f\u6dec\u6709\u5267\u6bd2\uff0c\u4e2d\u4eba\u5fc5\u6b7b\u3002\u201d\u738b\u5c11\u537f\u4f3c\u662f\u6709\u610f\uff0c\u8981\u5835\u4e0a\u56db\u5927\u6355\u5934\u7684\u5634\u5df4\uff01\u597d\u50cf\u4e0d\u613f \u518d\u542c\u4ed6\u4eec\u63d0\u51fa\u7684\u5efa\u8a00\u4e86\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u7684\u8001\u7ec3\u5904\uff0c\u5c31\u5728\u4ed6\u80fd\u62e9\u5584\u56fa\u6267\uff0c\u4e0d\u7ba1\u4f60\u542c\u4e0d\u542c\uff0c\u4ed6\u90fd\u8981\u8868\u8fbe\u51fa\u6765\uff0c\u800c\u4e14\u662f\u591a\u9762\u8fdb \u8a00\uff0c\u5c3d\u91cf\u8bf4\u7684\u751f\u52a8\u4f20\u795e\u3002 \u3000\u3000\u201c\u5bf9\uff01\u4f46\u66f4\u53ef\u6015\u7684\u662f\uff0c\u5b83\u53ef\u80fd\u5c04\u51fa\u5f88\u8fdc\uff0c\u4ee5\u5176\u5f62\u4f53\u89c2\u5bdf\uff0c\u5c31\u53ef\u4ee5\u77a7\u51fa\u6765\u662f\u4e00\u79cd\u4e13\u4e3a\u5c04\u8fdc \u800c\u8bbe\u8ba1\u51fa\u6765\u7684\u6697\u5668\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4f46\u4e00\u822c\u6c5f\u6e56\u4eba\u7269\uff0c\u90fd\u4e0d\u4f1a\u8d39\u5c3d\u5fc3\u601d\uff0c\u8bbe\u8ba1\u51fa\u8fd9\u7c7b\u7cbe\u5de7\u3001 \u8d39\u529f\uff0c\u5374\u53c8\u4e0d\u662f\u5f88\u5b9e\u7528\u7684\u6697\u5668\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u52a8\u5bb9\u4e86\uff0c\u5fd6\u9053\uff1a\u96be\u9053\u662f\u4e13\u95e8\u4e3a\u4e86\u6740\u6211\u800c\u8bbe\u8ba1\u51fa\u7684\u4e00\u79cd\u6697\u5668\uff0c\u5012\u8981\u770b\u770b\uff0c\u8fd9\u6697\u5668\u7684 \u673a\u5de7\u4f55\u5728\uff1f\u63a5\u9053\uff1a\u201c\u5434\u5927\u4eba\u89c1\u8fc7\u8fd9\u79cd\u6697\u5668\uff1f\u8fd8\u662f\u624b\u4e2d\u5df2\u6301\u6709\u4e86\u4e00\u4ef6\uff0c\u4ee5\u8d44\u4f50\u8bc1\uff1f\u201d \u3000\u3000\u201c\u5434\u67d0\u4eba\u5982\u662f\u62ff\u4e0d\u51fa\u771f\u51ed\u5b9e\u636e\uff0c\u53c8\u600e\u4e48\u6562\u5728\u5e9c\u53f0\u5927\u4eba\u5f53\u9762\u8f7b\u8a00\u4e71\u8bed\uff0c\u81ea\u53d6\u5176\u8fb1\u5462\uff1f\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u5c31\u6709\u70b9\u53cd\u51fb\u7684\u610f\u5473\u4e86\uff0c\u738b\u5c11\u537f\u4e5f\u7a81\u7136\u8b66\u89c9\u5230\uff0c\u56db\u5927\u6355\u5934\u7684\u5b98\u7ea7\u54c1\u5e26\uff0c\u4e0d\u5728\u4ed6\u738b\u5c11 \u537f\u8fd9\u4e2a\u56db\u54c1\u7687\u5802\u4e4b\u4e0b\uff0c\u521a\u624d\uff0c\u4ee5\u8a00\u8bed\u9876\u649e\u56de\u6765\uff0c\u4ed6\u738b\u5c11\u537f\u53c8\u6709\u4ec0\u4e48\u529e\u6cd5\u5462\uff1f\u7a0b\u603b\u6355\u5934\u53ea\u8d23\u6210 \u5c0f\u6587\u59d1\u5a18\u5168\u529b\u4fdd\u62a4\u4ed6\uff0c\u8fd9\u53ef\u548c\u56db\u5927\u6355\u5934\u65e0\u5173\uff0c\u4ed6\u4eec\u820d\u6b7b\u5fd8\u751f\u5730\u51fb\u9000\u4e86\u523a\u5ba2\u3001\u5f3a\u654c\uff0c\u4fdd\u62a4\u4ed6\u7684 \u5b89\u5168\uff0c\u7eaf\u51fa\u4e8e\u4e00\u79cd\u5927\u6355\u5934\u7684\u8d23\u4efb\u611f\uff0c\u53ef\u4e0d\u662f\u6015\u4ed6\u738b\u5c11\u537f\u554a\uff01 \u3000\u3000\u4eba\u8d35\u81ea\u77e5\uff0c\u60f3\u901a\u4e2a\u4e2d\u9053\u7406\uff0c\u738b\u77e5\u5e9c\u7acb\u523b\u6001\u5ea6\u5927\u53d8\uff0c\u7b11\u9053\uff1a\u201c\u8bf4\u7b11\u4e86\uff0c\u8bf4\u7b11\u4e86\uff0c\u56db\u4f4d\u4e3a\u7ef4 \u62a4\u626c\u5dde\u5e9c\u7684\u5b89\u5b81\uff0c\u51fa\u529b\u6d89\u9669\uff0c\u672c\u5e9c\u4f1a\u7262\u8bb0\u5fc3\u4e2d\uff0c\u65e5\u540e\u81ea\u6709\u56de\u62a5\u2026\u2026\u201d \u3000\u3000\u5434\u94c1\u5cf0\u501f\u9636\u4e0b\u53f0\uff0c\u53d6\u51fa\u4e00\u679a\u770b\u4e0a\u53bb\u84dd\u8292\u95ea\u95ea\u7684\u6697\u5668\uff0c\u9053\uff1a\u201c\u6b63\u5982\u5927\u4eba\u6240\u8a00\uff0c\u6b64\u7269\u6dec\u6709\u5267 \u6bd2\uff0c\u5927\u4eba\u89c2\u5bdf\u65f6\uff0c\u8981\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u6697\u5668\u950b\u8292\u7280\u5229\uff0c\u522b\u8ba9\u5b83\u523a\u4f24\u4e86\u624b\u6307\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u8fd8\u771f\u7684\u5bb3\u6015\uff0c\u53d6\u51fa\u4e86\u4e00\u65b9\u7ee2\u5e15\uff0c\u624d\u63a5\u8fc7\u6697\u5668\uff0c\u4ed4\u7ec6\u77a7\u770b\uff0c\u53d1\u89c9\u5176\u7269\u957f\u903e\u4e09\u5bf8\uff0c\u5934 \u6709\u4e09\u5904\u5149\u8292\uff0c\u4e24\u4fa7\u6709\u7ffc\uff0c\u5c3e\u6709\u5206\u53c9\uff0c\u679c\u7136\uff0c\u7ed9\u4eba\u4e00\u79cd\uff0c\u53ef\u4ee5\u98de\u5c04\u5f88\u8fdc\u7684\u611f\u89c9\u3002 \u3000\u3000\u201c\u6253\u9020\u7684\u4f3c\u5f88\u7cbe\u5de7\uff0c\u5934\u4e0a\u4e09\u5904\u5149\u8292\uff0c\u4f3c\u662f\u4e2d\u7a7a\uff0c\u5176\u5185\u84c4\u6709\u6bd2\u6c34\uff0c\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u679c\u7136\u662f \u82b1\u8d39\u4e86\u6781\u5927\u5de5\u592b\u6253\u9020\u7684\u6697\u5668\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e0d\u662f\u6c5f\u6e56\u4eba\u65bd\u7528\u4e4b\u7269\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4e00\u662f\u9020\u4ef7\u6602\u8d35\uff0c\u4e8c\u662f\u5de5\u827a\u592a\u7cbe\uff0c\u5355\u662f\u6ce8\u5165\u6bd2 \u6c34\u4e00\u9879\uff0c\u5c31\u4e0d\u662f\u4e00\u822c\u673a\u5177\u6240\u80fd\u5b8c\u6210\uff0c\u5fc5\u987b\u8981\u7279\u5236\u7684\u6ce8\u5165\u5c04\u7ba1\u3002\u6c5f\u6e56\u4eba\u7269\u6253\u9020\u4e86\u4e00\u79cd\u8fd9\u6837\u7684\u6697 \u5668\uff0c\u5c82\u4e0d\u662f\u81ea\u627e\u9ebb\u70e6\uff1f\u4f55\u51b5\uff0c\u643a\u5e26\u4ea6\u4e0d\u65b9\u4fbf\u3002\u6240\u4ee5\uff0c\u6211\u80af\u5b9a\u5b83\u4e0d\u662f\u51fa\u81ea\u6c5f\u6e56\u4eba\u7269\u7684\u8bbe\u8ba1\uff0c\u518d \u8bb2\u7a76\u7684\u6c5f\u6e56\u4eba\u7269\uff0c\u4e5f\u4e0d\u4f1a\u7528\u5b83\uff0c\u5236\u9020\u65e2\u56f0\u96be\uff0c\u65bd\u7528\u53c8\u4e0d\u65b9\u4fbf\u3002\u201d \u3000\u3000\u201c\u90a3\uff01\u5434\u5927\u6355\u5934\u89c9\u7740\u5b83\u6765\u81ea\u4f55\u5904\u5462\uff1f\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u770b\u624b\u5de5\u4e4b\u7ec6\uff0c\u6253\u9020\u4e4b\u5de7\uff0c\u80fd\u591f\u6253\u9020 \u51fa\u8fd9\u79cd\u6697\u5668\u7684\u5de5\u5320\uff0c\u53ea\u6709\u5317\u4eac\u548c\u626c\u5dde\u4e24\u5904\u624d\u6709\u3002\u201d \u3000\u3000\u201c\u591a\u8c22\u5927\u4eba\u63d0\u793a\uff0c\u5434\u67d0\u4eba\u4e5f\u662f\u521d\u6b21\u89c1\u5230\u8fd9\u79cd\u6697\u5668\uff0c\u65e0\u6cd5\u8bf4\u51fa\u5b83\u7684\u540d\u79f0\uff0c\u4e5f\u4e0d\u77e5\u4e2d\u85cf\u6bd2\u6027 \u7684\u4f5c\u7528\uff0c\u8fd9\u4e00\u70b9\u8981\u8d39\u4e00\u756a\u5de5\u592b\uff0c\u624d\u80fd\u4e86\u89e3\u3002\u201d \u3000\u3000\u738b\u77e5\u5e9c\u8fde\u81ea\u5df1\u7684\u624b\u5e15\u5e26\u6697\u5668\uff0c\u4ea4\u7ed9\u5434\u94c1\u5cf0\uff0c\u9053\uff1a\u201c\u5434\u5927\u6355\u5934\u8d39\u5fc3\u4e86\uff0c\u4e0d\u8fc7\uff0c\u773c\u4e0b\u5c31\u6709\u4e00 \u4e2a\u77e5\u836f\u7684\u884c\u5bb6\uff0c\u4f55\u4e0d\u8bf7\u5979\u9274\u8bc6\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u201c\u5c11\u4e0d\u4e86\u8981\u52b3\u52a8\u60dc\u7389\u59d1\u5a18\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4f46\u675c\u8001\u5f1f\u8fd8\u6709\u7d27\u8981\u5927\u4e8b\uff0c\u8bf7\u793a\u5927\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u8bf7\u793a\u5c31\u4e0d\u6562\u5f53\u4e86\u3002\u201d\u738b\u5c11\u537f\u8bf4\u9053\uff1a\u201c\u675c\u5144\u6709\u8bdd\u53ea\u7ba1\u8bf7\u8bf4\uff0c\u4e0b\u5b98\u77e5\u9053\u7684\u8a00\u65e0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u672c\u6765\u6709\u70b9\u50f5\u786c\u7684\u5c40\u9762\uff0c\u5df2\u7ecf\u5b8c\u5168\u8f6f\u5316\u4e0b\u6765\u3002 \u3000\u3000\u201c\u5927\u4eba\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4eca\u665a\u51fa\u73b0\u4e24\u4f4d\u523a\u5ba2\uff0c\u5251\u6cd5\u4e4b\u5947\uff0c\u675c\u67d0\u662f\u521d\u6b21\u9047\u5230\uff0c\u738b\u526f\u603b\u6355\u548c \u4f55\u5927\u5149\uff0c\u5728\u4ea4\u624b\u4e24\u4e2a\u56de\u5408\u4e2d\uff0c\u5168\u88ab\u6482\u5012\uff0c\u675c\u67d0\u548c\u627f\u5fd7\u5144\uff0c\u8054\u624b\u51fa\u51fb\uff0c\u4e5f\u53ea\u80fd\u6253\u6210\u4e00\u4e2a\u5e73\u624b\uff0c \u4e25\u683c\u70b9\u8bf4\uff0c\u4e3b\u52a8\u53d8\u5316\uff0c\u5168\u64cd\u8bf8\u654c\u4eba\u4e4b\u624b\uff0c\u6211\u548c\u4e8e\u5144\uff0c\u5168\u51ed\u640f\u6740\u7684\u7ecf\u9a8c\uff0c\u7ef4\u6301\u4e2a\u6ca1\u6709\u843d\u8d25\u7684\u5c40 \u9762\uff0c\u8fd9\u662f\u675c\u67d0\u4eba\u8fdb\u5165\u5211\u90e8\u4e4b\u540e\uff0c\u4ece\u672a\u9047\u8fc7\u7684\u5f3a\u654c\uff0c\u5c91\u5144\u548c\u5434\u5144\uff0c\u4e5f\u662f\u4ee5\u4e8c\u5bf9\u4e00\uff0c\u4ea6\u672a\u53d6\u5f97\u4f18 \u52bf\u3002\u201d \u3000\u3000\u201c\u5982\u6b64\u5f3a\u608d\u7684\u6740\u624b\uff0c\u662f\u4ec0\u4e48\u4e00\u4e2a\u6765\u5934\u5462\uff1f\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u9700\u8981\u672c\u5e9c\u5982\u4f55\u914d\u5408\uff0c\u53ea\u7ba1\u8bf7 \u8bf4\uff1f\u201d \u3000\u3000\u201c\u5bf9\u6297\u5982\u6b64\u5f3a\u654c\uff0c\u5e9c\u4e2d\u5012\u6709\u4e00\u4f4d\u5251\u9053\u9ad8\u624b\u2026\u2026\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u5c0f\u6587\u59d1\u5a18\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5bf9\uff01\u5c0f\u6587\u59d1\u5a18\u5982\u82e5\u51fa\u624b\uff0c\u5e94\u8be5\u6709\u6740\u654c\u628a\u63e1\u3002\u201d \u3000\u3000\u201c\u597d\u554a\uff01\u90a3\u5c31\u5feb\u89e3\u5f00\u5c0f\u6587\u59d1\u5a18\u7a74\u9053\uff0c\u8ba9\u5979\u8c03\u606f\u597d\u4f53\u80fd\u3002\u201d\u738b\u77e5\u5e9c\u9053\uff1a\u201c\u51c6\u5907\u5bf9\u4ed8\u5f3a\u654c\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\uff0c\u201d\u675c\u671b\u6708\u6709\u70b9\u4e3a\u96be\u5730\u9053\uff1a\u201c\u5c0f\u6587\u7684\u795e\u5fd7\uff0c\u4e0d\u77e5\u662f\u5426\u5df2\u7ecf\u590d\u5e38\uff0c\u5728\u5979\u8ff7\u5931\u7684\u8fc7\u7a0b \u4e2d\uff0c\u597d\u50cf\u5bf9\u5927\u4eba\u6709\u4e9b\u6210\u89c1\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u8fd9\u65b9\u9762\u6211\u4e5f\u611f\u89c9\u5230\u4e86\uff0c\u4f46\u8981\u672c\u5e9c\u600e\u4e48\u505a\u5462\uff1f\u4e8b\u7531\u672c\u5e9c\u8d77\uff0c\u5e94\u8be5\u5982\u4f55\uff0c\u672c \u5e9c\u7edd\u4e0d\u63a8\u8f9e\u3002\u201d \u3000\u3000\u201c\u5927\u4eba\u6700\u597d\u8fd8\u662f\u56de\u907f\u4e00\u4e0b\uff0c\u90a3\u5957\u90aa\u6076\u7684\u5251\u6cd5\uff0c\u5bf9\u4eba\u7684\u6027\u683c\u5f71\u54cd\u5f88\u5927\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u7b49 \u5c0f\u6587\u795e\u5fd7\u6062\u590d\u540e\uff0c\u518d\u548c\u5979\u89c1\u9762\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u4e5f\u62c5\u5fc3\uff0c\u5979\u5fc3\u4e2d\u662f\u5426\u8bb0\u6068\u6211\u70b9\u5979\u7a74\u9053\u7684\u4e8b\uff1f\u5982\u662f\u5979\u5370\u8c61\u6df1\u523b\uff0c\u5f88\u96be\u5fd8\u6000\uff0c \u89e3\u5979\u7a74\u9053\uff0c\u4e5f\u53ef\u80fd\u5bf9\u6211\u6784\u6210\u4e00\u79cd\u5a01\u80c1\u3002\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5e94\u8be5\u600e\u4e48\u529e\u5462\uff1f\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u7a0b\u603b\u6355\u662f\u5426\u6709\u80fd\u529b\u89e3\u53bb\u5979\u8fd9\u4e9b\u6302\u788d\uff1f\u201d \u3000\u3000\u201c\u80fd\uff01\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u4eec\u56db\u4eba\u7814\u8ba8\u8fc7\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u603b\u6355\u5934\u4e0d\u4f1a\u662f\u56e0\u4e8b\u52a1\u7f20\u8eab\uff0c\u8bef\u4e86\u5f52 \u671f\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u9047\u4e0a\u610f\u5916\u4e86\uff1f\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u662f\u5426\u8be5\u4f20\u5448\u5211\u90e8\uff0c\u8bf4\u660e\u5185\u60c5\u5462\uff1f\u201d \u3000\u3000\u201c\u6740\u6b7b\u603b\u6355\u5934\u7684\u673a\u4f1a\u4e0d\u5927\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5ef6\u8bef\u5979\u5f52\u6765\u7684\uff0c\u53ea\u6709\u4e24\u4e2a\u539f\u56e0\uff0c\u4e00\u662f\u9677\u5165\u4e86 \u4e00\u79cd\u673a\u5173\u56da\u5ba4\u4e4b\u5185\uff0c\u6682\u65f6\u65e0\u6cd5\u8131\u56f0\u3002\u4e8c\u662f\uff0c\u53d1\u73b0\u4e86\u68a6\u5e7b\u4e4b\u5200\uff0c\u5f7c\u6b64\u9677\u5165\u4e86\u4e00\u573a\u6597\u667a\u3001\u89d2\u529b\u7684 \u73af\u5883\u4e4b\u4e2d\uff0c\u65e0\u6cd5\u6446\u8131\uff0c\u4f46\u6211\u4eec\u76f8\u4fe1\uff0c\u518d\u6709\u4e09\u4e94\u5929\uff0c\u5979\u4e00\u5b9a\u80fd\u591f\u5f52\u6765\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u5fc3\u4e2d\u6709\u4e9b\u4e0d\u670d\u6c14\uff0c\u4f46\u4ed6\u4e0d\u60f3\u6597\u6c14\u4e86\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8981\u672c\u5e9c\u8eb2\u907f\u5728\u4ec0\u4e48\u5730\u65b9\u5462\uff1f\u201d \u3000\u3000\u201c\u56de\u907f\u7684\u610f\u601d\uff0c\u4e0d\u662f\u8ba9\u5927\u4eba\u8eb2\u5728\u54ea\u91cc\u3002\u8001\u5b9e\u8bf4\uff0c\u6211\u4eec\u4e0d\u5b88\u5728\u5927\u4eba\u7684\u8eab\u4fa7\uff0c\u4e5f\u4e0d\u592a\u653e\u5fc3\uff0c\u201d \u5434\u94c1\u5cf0\u9053\uff1a\u201c\u53ea\u662f\u628a\u5927\u4eba\u7684\u5f62\u8c8c\u6539\u6362\u4e00\u4e0b\u2026\u2026\u201d \u3000\u3000\u201c\u597d\uff01\u201d\u738b\u5c11\u537f\u7b11\u9053\uff1a\u201c\u6539\u88c5\u6613\u5bb9\u7684\u4e8b\uff0c\u7531\u4f60\u4eec\u52a8\u624b\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u975e\u5e38\u5e0c\u671b\u80fd\u76ee\u7779\u8fd9\u573a \u640f\u6740\u7ecf\u8fc7\uff0c\u8fd9\u51e0\u65e5\u9047\u4e0a\u4e0d\u5c11\u51f6\u9669\uff0c\u4f46\u4e5f\u628a\u6211\u7684\u80c6\u5b50\u7ec3\u5927\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u6765\u5e2e\u5927\u4eba\u6539\u53d8\u4e00\u4e0b\u5f62\u8c8c\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u7792\u8fc7\u795e\u5fd7\u4e0d\u6e05\u7684\u5c0f\u6587\uff0c\u5e94\u8be5\u4e0d\u662f\u96be\u4e8b\u3002\u201d \u3000\u3000\u6682\u4e14\u628a\u77e5\u5e9c\u5927\u4eba\uff0c\u6539\u626e\u6210\u4e00\u4e2a\u8859\u5f79\uff0c\u548c\u7a7f\u7740\u8859\u5f79\u670d\u7684\u5f20\u9e9f\u3001\u77f3\u742a\u8d70\u5728\u4e00\u8d77\u3002\u5f53\u7136\uff0c\u8fd9\u4e24 \u4e2a\u6b66\u5f53\u5251\u5ba2\uff0c\u4e5f\u62c5\u8d1f\u4e86\u4fdd\u62a4\u738b\u5927\u4eba\u5b89\u5168\u7684\u91cd\u4efb\u3002 \u3000\u3000------------- \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u5341\u516d\u7ae0 \u3000\u3000\u60dc\u7389\u628a\u5c0f\u6587\u62b1\u5165\u4e86\u738b\u5927\u4eba\u7684\u5367\u5ba4\uff0c\u90a3\u9752\u8863\u5c11\u5973\u7684\u5c38\u4f53\u5df1\u65e9\u7ecf\u79fb\u8d70\uff0c\u60dc\u7389\u5c31\u628a\u5c0f\u6587\u653e\u5728\u738b \u5927\u4eba\u7684\u5e8a\u4e0a\u3002 \u3000\u3000\u675c\u671b\u6708\u5f00\u59cb\u63a8\u6d3b\u5c0f\u6587\u59d1\u5a18\u7684\u7a74\u9053\uff0c\u4e8e\u627f\u5fd7\u3001\u5434\u94c1\u5cf0\u5206\u7ad9\u675c\u671b\u6708\u7684\u4e24\u4fa7\uff0c\u4e24\u4e2a\u4eba\u7d27\u5f20\u5730\u8fd0 \u6c14\u6212\u5907\uff0c\u4ee5\u9632\u4e0d\u6d4b\u4e4b\u53d8\u3002 \u3000\u3000\u5c0f\u6587\u5f88\u5feb\u9192\u4e86\u8fc7\u6765\uff0c\u633a\u8eab\u5750\u8d77\uff0c\u76ee\u5149\u6295\u6ce8\u5728\u675c\u671b\u6708\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u662f\u4f60\u675c\u5927\u6355\u5934\uff0c\u70b9\u4e86 \u6211\u7684\u7a74\u9053\uff0c\u4e5f\u662f\u4f60\u63a8\u6d3b\u4e86\u6211\u7684\u7a74\u9053\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\uff01\u89e3\u94c3\u8fd8\u662f\u7cfb\u94c3\u4eba\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5c0f\u6587\u59d1\u5a18\u5982\u6709\u4ec0\u4e48\u4e0d\u60a6\uff0c\u675c\u67d0\u7518\u613f\u9886\u7f5a\u3002\u201d \u3000\u3000\u201c\u5c0f\u6587\u59d0\uff0c\u4ed6\u662f\u597d\u610f\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u5f53\u65f6\uff0c\u5c0f\u6587\u59d0\u53cc\u76ee\u4e2d\u6740\u673a\u6d53\u91cd\uff0c\u53ea\u77e5\u4f60\u5fc3\u6709\u6068\uff0c\u5374 \u4e0d\u77e5\u4f60\u5fc3\u4e2d\u6068\u8c01\uff1f\u201d \u3000\u3000\u5c0f\u6587\u62cd\u62cd\u60dc\u7389\u7684\u624b\uff0c\u9053\uff1a\u201c\u653e\u5fc3\u5427\uff01\u60dc\u7389\uff0c\u6211\u4e0d\u4f1a\u548c\u675c\u6355\u5934\u8ba1\u8f83\u6b64\u4e8b\u7684\u2026\u2026\u201d \u3000\u3000\u201c\u5c0f\u6587\u59d1\u5a18\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4f60\u662f\u5426\u77e5\u9053\u4f60\u7ec3\u6210\u4e86\u4e00\u5957\u975e\u5e38\u51f6\u6b8b\u7684\u5251\u6cd5\uff0c\u5251\u51fa\u5982\u95ea\u7535\uff0c \u6740\u4e86\u4e0d\u5c11\u7684\u4eba\uff1f\u201d \u3000\u3000\u5c0f\u6587\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u77e5\u9053\uff0c\u5251\u51fa\u5929\u8272\u53d8\uff0c\u8840\u67d3\u5927\u5730\u7ea2\u3002\u201d \u3000\u3000\u201c\u8fd9\u5957\u5251\u6cd5\u662f\u5426\u6709\u4e9b\u602a\u5f02\u3001\u90aa\u6076\uff1f\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u5bf9\u7528\u5251\u4eba\u7684\u6027\u683c\u5fc3\u7406\uff0c\u4f1a\u5426\u6709\u6240\u5f71 \u54cd\uff1f\u201d \u3000\u3000\u5c0f\u6587\u7b11\u4e86\uff0c\u4e00\u62ac\u817f\uff0c\u4eba\u5df2\u843d\u5728\u5e8a\u4e0b\uff0c\u9053\uff1a\u201c\u5251\u6cd5\u662f\u6709\u4e9b\u602a\u5f02\uff0c\u662f\u5426\u90aa\u6076\uff0c\u89c1\u4ec1\u89c1\u667a\u4e86\uff0c \u4f46\u5b83\u7edd\u4e0d\u662f\u6b63\u5b97\u5251\u6cd5\uff0c\u603b\u6355\u5934\u66fe\u7ecf\u544a\u8bc9\u8fc7\u6211\uff0c\u4e0d\u53ef\u4e60\u7ec3\uff0c\u53ea\u53ef\u60dc\u5979\u8bb2\u7684\u665a\u4e86\u534a\u4e2a\u6708\uff0c\u6211\u5df2\u7ec3 \u4f1a\u8fc7\u534a\u2026\u2026\u201d \u3000\u3000\u201c\u4e3a\u5b83\u7684\u7cbe\u5947\u53d8\u5316\u5438\u5f15\uff0c\u6240\u4ee5\u65e0\u6cd5\u6b47\u6b62\uff0c\u201d\u675c\u671b\u6708\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5c31\u7ee7\u7eed\u7ec3\u4e86\u4e0b\u6765\uff1f\u201d \u3000\u3000\u5c0f\u6587\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u6697\u4e2d\u628a\u8fd9\u5957\u5251\u6cd5\u7ec3\u6210\uff0c\u540e\u9762\u7684\u90e8\u5206\u90fd\u662f\u5728\u5750\u606f\u65f6\uff0c\u51a5\u601d\u63a2\u7d22\u4e2d\u60f3 \u51fa\u7684\u5251\u8def\uff0c\u7528\u4e8e\u5bf9\u654c\uff0c\u8fd8\u662f\u7b2c\u4e00\u6b21\u3002\u201d \u3000\u3000\u201c\u6628\u591c\u95ef\u8fdb\u4e86\u5e9c\u8859\u6765\u7684\u4e09\u4e2a\u523a\u5ba2\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4e8c\u7537\u4e00\u5973\uff0c\u5973\u7684\u4f24\u5728\u4e86\u60dc\u7389\u624b\u4e2d\uff0c\u541e \u6bd2\u800c\u6b7b\u2026\u2026\u201d \u3000\u3000\u201c\u60f3\u4e0d\u5230\u6c5f\u5357\u7684\u6740\u624b\uff0c\u4e2a\u4e2a\u90fd\u5982\u6b64\u987d\u5f3a\uff0c\u4e0d\u6210\u529f\uff0c\u5c31\u4ee5\u8eab\u6b89\u3002\u201d\u5c0f\u6587\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u7537\u7684 \u5462\uff1f\u4e5f\u6b7b\u5728\u4f60\u4eec\u56db\u5927\u6355\u5934\u7684\u624b\u4e0b\u4e86\uff1f\u201d \u3000\u3000\u201c\u8bf4\u8d77\u6765\uff0c\u6709\u70b9\u60ed\u6127\uff0c\u5c91\u67d0\u4eba\u4ece\u4e8b\u5211\u6355\u751f\u6daf\u4ee5\u6765\uff0c\u8fd8\u662f\u7b2c\u4e00\u6b21\u9047\u4e0a\u5251\u6cd5\u5982\u6b64\u7cbe\u5947\u7684\u9ad8\u4eba\uff0c \u6211\u4eec\u56db\u4e2a\u4eba\uff0c\u5168\u62da\u4e0a\u4e86\uff0c\u4e8c\u5bf9\u4e00\uff0c\u4e0d\u4f46\u88ab\u4eba\u4ece\u5bb9\u9000\u8d70\uff0c\u800c\u4e14\uff0c\u6355\u6740\u8fc7\u7a0b\u4e2d\uff0c\u5927\u534a\u7531\u654c\u4eba\u64cd\u63a7 \u4e3b\u52a8\uff0c\u771f\u662f\u4e22\u4eba\u7684\u5f88\u5566\uff01\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u867d\u7136\u6709\u4e9b\u7c97\u8c6a\uff0c\u4f46\u8bf4\u8bdd\u5012\u662f\u76f4\u7387\u5766\u767d\u7684\u5f88\u3002 \u3000\u3000\u201c\u6740\u624b\u5175\u5203\uff0c\u4ee5\u51cc\u5389\u51f6\u9738\u4e3a\u4e3b\uff0c\u7528\u5251\u5012\u662f\u4e0d\u591a\u3002\u201d\u5c0f\u6587\u76ee\u5149\u5982\u7535\uff0c\u7531\u56db\u5927\u6355\u5934\u7684\u8138\u4e0a\u4e00 \u626b\u8fc7\uff0c\u63a5\u9053\uff1a\u201c\u56db\u4f4d\u672a\u7b49\u5230\u603b\u6355\u5934\u56de\u6765\uff0c\u5c31\u89e3\u5f00\u4e86\u6211\u7684\u7a74\u9053\uff0c\u603b\u975e\u65e0\u56e0\u5427\uff1f\u201d \u3000\u3000\u201c\u662f\u6709\u4e8b\u8981\u548c\u59d1\u5a18\u5546\u91cf\uff0c\u201d\u675c\u671b\u6708\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6628\u591c\u4e00\u6218\u8bc1\u5b9e\uff0c\u6211\u4eec\u5df2\u81ea\u77e5\u65e0\u9010\u9000\u7528 \u5251\u6740\u624b\u7684\u6280\u827a\u548c\u80fd\u529b\uff0c\u4e5f\u6ca1\u6709\u628a\u63e1\u4fdd\u62a4\u5e9c\u53f0\u738b\u5927\u4eba\u7684\u5b89\u5168\u2026\u2026\u201d \u3000\u3000\u201c\u603b\u6355\u5934\u4e25\u4ee4\u6211\u4fdd\u62a4\u738b\u5927\u4eba\uff0c\u81ea\u90a3\u65f6\u8d77\uff0c\u738b\u77e5\u5e9c\u7684\u5b89\u5371\uff0c\u5728\u6211\u5c0f\u6587\u7684\u5fc3\u76ee\u4e2d\uff0c\u5b9e\u5df2\u8d85\u8fc7 \u4e86\u6211\u7684\u751f\u547d\uff0c\u4f46\u4ed6\u8f7b\u85d0\u6211\uff0c\u800c\u662f\u53d1\u81ea\u5185\u5fc3\u7684\u8f7b\u85d0\uff0c\u6211\u7684\u5fc3\u4e2d\u75db\u6781\u4e86\uff0c\u4e5f\u96be\u8fc7\u6781\u4e86\u3002\u6211\u5168\u5fc3\u5168 \u610f\u5730\u5173\u5fc3\u4ed6\uff0c\u9664\u4e86\u5f88\u77ed\u7684\u5750\u606f\u65f6\u95f4\u4e4b\u5916\uff0c\u6211\u4e00\u76f4\u6216\u660e\u6216\u6697\u5730\u5b88\u5019\u5728\u4ed6\u7684\u8eab\u4fa7\uff0c\u5c31\u7b97\u662f\u6253\u5750\u8c03 \u606f\u5427\uff01\u4e5f\u90fd\u7528\u4e86\u4e00\u4e9b\u5fc3\u673a\u5b89\u6392\u3002\u81ea\u4ece\u53d7\u547d\u4ee5\u6765\uff0c\u6211\u6ca1\u6709\u7761\u8fc7\u4e00\u5929\u597d\u89c9\uff0c\u4f46\u6211\u6362\u5230\u7684\u662f\uff0c\u738b\u5927 \u4eba\u90a3\u4e0d\u5c51\u4e00\u987e\u7684\u773c\u795e\u2026\u2026\u201d \u3000\u3000\u626e\u4f5c\u8859\u5f79\u7684\u738b\u77e5\u5e9c\uff0c\u7a81\u7136\u7f13\u7f13\u8f6c\u8fc7\u8eab\u53bb\uff0c\u52a8\u4f5c\u5f88\u6162\uff0c\u6162\u7684\u6ca1\u6709\u4eba\u611f\u89c9\u5230\u4ed6\u5728\u8f6c\u52a8\u8eab\u4e86\u3002 \u3000\u3000\u4f46\u8fd9\uff0c\u7792\u4e0d\u8fc7\u5434\u94c1\u5cf0\u7684\u773c\u775b\u3002 \u3000\u3000\u4ed6\u53d1\u89c9\u4e86\u738b\u77e5\u5e9c\u5728\u6697\u4e2d\u62ed\u6cea\uff0c\u663e\u7136\u5fc3\u4e2d\u4e5f\u6709\u7740\u4e00\u4e9b\u75db\u6094\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u5ffd\u7565\u4e86\u5c0f\u6587\u7528\u5c3d\u5fc3\u673a\u5730\u4fdd\u62a4\u4ed6\u7684\u60c5\u610f\uff0c\u4e5f\u62d2\u7edd\u4e86\u4e00\u4efd\u5c11\u5973\u7684\u5173\u6000\uff0c\u66f4\u574f\u7684\u662f\uff0c\u628a \u4e00\u4e2a\u8eab\u6000\u7edd\u6280\u7684\u9ad8\u624b\uff0c\u8bef\u8ba4\u4f5c\u88c5\u9970\u7684\u82b1\u74f6\u3002 \u3000\u3000\u5c11\u5973\u60c5\u6000\u603b\u662f\u8bd7\uff0c\u5bb9\u4e0d\u4e0b\u8f7b\u85d0\u548c\u8bef\u89e3\uff0c\u90a3\u6bd4\u5f53\u9762\u6253\u9a82\u5979\u4e00\u987f\u8fd8\u96be\u5fcd\u53d7\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u662f\u80f8\u7f57\u9526\u7ee3\u7684\u597d\u5b98\uff0c\u4e5f\u662f\u8bfb\u4e66\u4e07\u5377\u7684\u624d\u5b50\uff0c\u4ed6\u4e86\u89e3\u8fd9\u4e9b\uff0c\u53ea\u662f\u8eab\u5904\u5728\u60ca\u98ce\u9a87\u6d6a\u4e2d\uff0c \u6ca1\u6709\u4ed4\u7ec6\u89c2\u5bdf\uff0c\u4e5f\u6ca1\u6709\u7528\u5fc3\u53bb\u4f53\u4f1a\uff0c\u7f3a\u5c11\u4e86\u4e00\u4efd\u7985\u610f\u7684\u4fee\u517b\uff0c\u7ed9\u4e86\u5c0f\u6587\u59d1\u5a18\u4e0d\u5c11\u7684\u4f24\u5bb3\u3002 \u3000\u3000\u4f46\u542c\u5c0f\u6587\u8bf4\u9053\uff1a\u201c\u6211\u65bd\u5c55\u51fa\u603b\u6355\u5934\u4e25\u7981\u6211\u4e60\u7ec3\u7684\u5251\u6cd5\uff0c\u662f\u6211\u53d1\u89c9\u4e86\u654c\u4eba\u6108\u6765\u6108\u591a\uff0c\u6b66\u529f \u4e5f\u6108\u89c1\u9ad8\u5f3a\uff0c\u6211\u6015\u4ed6\u4eec\u4e00\u62e5\u800c\u4e0a\u7684\u6df7\u6218\uff0c\u4f7f\u6211\u4eec\u4e00\u65f6\u95f4\u5e94\u53d8\u4e0d\u53ca\uff0c\u8ba9\u738b\u5927\u4eba\u53d7\u5230\u4f24\u5bb3\uff0c\u4ed6\u5b8c \u5168\u6ca1\u6709\u4fdd\u62a4\u81ea\u5df1\u7684\u80fd\u529b\uff0c\u53ea\u8981\u53d7\u5230\u4e00\u70b9\u4f24\uff0c\u6211\u90fd\u65e0\u6cd5\u5411\u603b\u6355\u5934\u4ea4\u4ee3\u3002\u5979\u6d3e\u6211\u62c5\u5f53\u91cd\u4efb\uff0c\u662f\u4fe1 \u4efb\u6211\u7684\u51b3\u5fc3\u548c\u6b66\u529f\uff0c\u6211\u600e\u4e48\u80fd\u8ba9\u5979\u5931\u671b\u3002\u4e3a\u4e86\u9632\u60a3\u4e8e\u672a\u7136\uff0c\u6211\u7528\u51fa\u4e86\u90a3\u5957\u2018\u5251\u6d77\u6d74\u9b42\u2019\u7684\u5251 \u6cd5\uff0c\u6740\u6b7b\u90e8\u5206\u6740\u624b\uff0c\u5c31\u7b97\u53d7\u5230\u603b\u6355\u5934\u4e00\u987f\u8d23\u9a82\uff0c\u4e5f\u662f\u5fc3\u7518\u60c5\u613f\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u5251\u6d77\u6d74\u9b42\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4e0d\u50cf\u662f\u4e00\u5957\u5251\u6cd5\u7684\u540d\u79f0\u554a\uff01\u201d \u3000\u3000\u201c\u662f\u8868\u793a\u8fd9\u5957\u5251\u6cd5\u7684\u591a\u53d8\u4e0e\u51f6\u6b8b\uff0c\u4e5f\u4ee3\u8868\u4e86\u4e60\u5251\u4eba\u7684\u75db\u82e6\uff0c\u4e60\u7ec3\u8fd9\u5957\u5251\u6cd5\uff0c\u5c24\u5982\u5728\u5251\u6d77 \u4e2d\u6c90\u6d74\uff0c\u8eab\u5fc3\u5168\u6295\u5165\u4e86\u5251\u52bf\u7684\u8be1\u53d8\u4e2d\u3002\u201d\u5c0f\u6587\u9053\uff1a\u201c\u8fd9\u5957\u5251\u6cd5\u6709\u4e00\u4e2a\u7279\u8272\uff0c\u5b83\u4e0d\u80fd\u5f00\u5c71\u3001\u7acb \u5be8\uff0c\u56e0\u4e3a\u5b83\u6ca1\u6709\u5960\u57fa\u7684\u529f\u8bfe\uff0c\u65e0\u6cd5\u6309\u6b65\u5c31\u73ed\u5730\u53bb\u4f20\u6388\u6280\u827a\uff0c\u4e00\u5f00\u59cb\u5c31\u662f\u5251\u62db\u7684\u53d8\u5316\u3002\u800c\u4e14\uff0c \u5f88\u591a\u53d8\u5316\u548c\u6b66\u529f\u8fdb\u5c55\u662f\u9006\u52bf\u800c\u884c\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\uff0c\u9700\u8981\u6709\u76f8\u5f53\u7684\u6b66\u529f\u57fa\u7840\u7684\u4eba\u624d\u80fd\u4e60\u7ec3\uff0c\u6240\u4ee5\uff0c\u4ed6\u4eec\u4e0d\u80fd\u6210\u4e3a\u4e00\u95e8\u5b97\u6d3e\u3002\u201d\u675c \u671b\u6708\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u4ed6\u4eec\u6ca1\u6709\u529e\u6cd5\u53ec\u6536\u5f1f\u5b50\uff0c\u4ece\u5934\u5f00\u59cb\u4f20\u6388\u6280\u827a\u3002\u201d \u3000\u3000\u201c\u4e8b\u5b9e\u4e0a\u8fd9\u662f\u6574\u5957\u5251\u6cd5\uff0c\u4e5f\u662f\u4e00\u79cd\u79fb\u82b1\u63a5\u6728\u7684\u7ec4\u5408\uff0c\u5b83\u662f\u628a\u5251\u672f\u4e2d\u6700\u51f6\u72e0\u7684\u6740\u7740\uff0c\u7528\u6b63 \u5947\u76f8\u548c\u76f8\u514b\u7684\u65b9\u6cd5\uff0c\u628a\u5b83\u7ec4\u5408\u8d77\u6765\uff0c\u7531\u51fa\u4eba\u610f\u5916\u7684\u89d2\u5ea6\u4e2d\u51fa\u5251\uff0c\u6740\u4f24\u529b\u5947\u5927\u3002\u8fd9\u4e00\u70b9\u770b\u4f3c\u5bb9 \u6613\uff0c\u5176\u5b9e\u6700\u4e3a\u8270\u56f0\uff0c\u6709\u5982\u8ba9\u51b0\u70ad\u540c\u7089\uff0c\u975e\u7cbe\u719f\u5404\u5bb6\u5251\u6cd5\uff0c\u5f88\u96be\u529e\u5230\u3002\u201d\u5c0f\u6587\u9053\uff1a\u201c\u521b\u51fa\u8fd9\u5957 \u5251\u6cd5\u7684\u4eba\uff0c\u80af\u5b9a\u662f\u4e00\u4ee3\u5251\u672f\u5927\u5bb6\uff0c\u8fd9\u662f\u6211\u628a\u6574\u5957\u5251\u6cd5\u5b66\u4f1a\u4e4b\u540e\u7684\u5fc3\u5f97\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8fd9\u5957\u5251\u6cd5\uff0c\u6211\u4eec\u56db\u4e2a\u4eba\u4e5f\u53ef\u4ee5\u7ec3\u4e86\uff1f\u201d \u3000\u3000\u5c0f\u6587\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u56db\u5927\u6355\u5934\u4e2d\uff0c\u6050\u6015\u53ea\u6709\u4f60\u4e00\u4e2a\u4eba\u80fd\u7ec3\u2026\u2026\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u6211\u4eec\u56db\u4e2a\u4eba\u529f\u529b\u603b\u6709\u76f8\u5dee\uff0c\u4e5f\u6781\u6709\u9650\uff0c\u5c0f\u675c\u80fd\u8d85\u8d8a\u6211\u5c91\u67d0\u7684\uff0c\u53ea\u6709\u8f7b\u529f\u4e00 \u9879\u2026\u2026\u201d \u3000\u3000\u201c\u65e0\u5173\u529f\u529b\uff0c\u201d\u5c0f\u6587\u9053\uff1a\u201c\u56e0\u4e3a\u4e60\u7ec3\u8fd9\u5957\u5251\u6cd5\uff0c\u5fc5\u987b\u5148\u8981\u6709\u76f8\u5f53\u7684\u5251\u672f\u9020\u8be3\u3002\u4f60\u5c91\u5927\u6355 \u5934\u7684\u6b66\u529f\u867d\u597d\uff0c\u53ef\u60dc\u7ec3\u7684\u4e0d\u662f\u5251\u6cd5\uff0c\u800c\u8fd9\u5957\u5251\u6d77\u6d74\u9b42\uff0c\u5168\u7531\u5251\u672f\u4e2d\u6740\u7740\u7ec4\u6210\u3002\u201d \u3000\u3000\u201c\u8fd9\u5957\u5251\u6cd5\uff0c\u53ef\u4ee5\u7ec3\u51fa\u4e00\u8eab\u6740\u673a\uff0c\u4e00\u8eab\u5251\u5203\uff0c\u4e5f\u628a\u7075\u9b42\u7ec3\u5165\u5251\u4e2d\u3002\u6240\u4ee5\uff0c\u624d\u80fd\u4e00\u51fa\u5251\u5c31 \u53d6\u4eba\u6027\u547d\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u7ec3\u5230\u4e86\u4e00\u5b9a\u7684\u706b\u5019\uff0c\u4eba\u6027\u4e5f\u968f\u7740\u5251\u6cd5\u53d8\u4e86\uff0c\u8fd9\u4e16\u4e0a\u4e0d\u518d\u6709\u7f8e\u4e3d\u3001 \u53ef\u7231\u7684\u5c0f\u6587\u59d1\u5a18\uff0c\u5251\u6d77\u6d74\u9b42\uff0c\u6d74\u51fa\u4e00\u4e2a\u6740\u4eba\u7684\u673a\u68b0\u3002\u201d \u3000\u3000\u5c0f\u6587\u59d1\u5a18\u542c\u5446\u4e86\uff0c\u4e8b\u5b9e\u4e0a\u4e09\u5927\u6355\u5934\uff0c\u4e5f\u542c\u5f97\u5fc3\u5934\u9707\u52a8\u3002\u4ed6\u4eec\u4e60\u6b66\u6709\u6210\uff0c\u77e5\u9053\u4e0a\u4e58\u6b66\u529f\uff0c \u4e0d\u4f46\u8981\u6295\u5165\u4f53\u80fd\uff0c\u4e5f\u8981\u6295\u5165\u5fc3\u667a\uff0c\u5728\u6f2b\u957f\u7684\u4e60\u6b66\u8fc7\u7a0b\u4e2d\uff0c\u4eba\u6027\u5c31\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e2d\uff0c\u968f\u7740\u6280\u827a\u7684 \u8fdb\u5c55\u6539\u53d8\u4e86\u3002 \u3000\u3000\u5c0f\u6587\u51dd\u601d\u4e86\u597d\u4e00\u9635\uff0c\u624d\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5434\u5927\u6355\u5934\u8bf4\u7684\u5f88\u5bf9\uff0c\u8fd9\u5251\u6cd5\u771f\u7684\u6709\u70b9\u90aa\u6076\uff0c\u770b\u6837 \u5b50\u662f\u4e0d\u5b9c\u518d\u7ec3\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u4f46\u5b83\u5374\u53ef\u80fd\u662f\u5251\u6cd5\u4e4b\u6700\uff0c\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u6740\u654c\u7684\u950b\u5203\uff0c\u9664\u4e86\u8fd9\u5957\u5251\u6cd5\u4e4b\u5916\uff0c\u6211\u4eec\u8fd8\u8981 \u4ed7\u51ed\u4ec0\u4e48\u53bb\u5bf9\u4ed8\u6740\u4eba\u4e4b\u738b\u3001\u68a6\u5e7b\u4e4b\u5200\u3002\u56db\u5927\u6355\u5934\uff0c\u90fd\u6709\u4e9b\u81ea\u8d1f\uff0c\u867d\u7136\u4e0d\u66fe\u81ea\u5618\u8fc7\u6b66\u529f\u662f\u5929\u4e0b \u6700\u597d\u7684\u4eba\uff0c\u6211\u4eec\u662f\u4e8c\u3001\u4e09\u3001\u56db\u3001\u4e94\uff0c\u8c01\u662f\u7b2c\u4e00\u5462\uff1f\u9664\u4e86\u4e2a\u603b\u6355\u5934\u4e4b\u5916\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u4eec\u8fd8\u672a \u9047\u5230\u5f3a\u8fc7\u6211\u4eec\u7684\u654c\u624b\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u53e3\u9f7f\u542f\u52a8\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u201c\u4f46\u73b0\u5728\u9047\u5230\u4e86\u3002\u201d\u675c\u671b\u6708\u63a5\u9053\uff1a\u201c\u6628\u591c\u51fa\u73b0\u5e9c\u8859\u7684\u5251\u624b\uff0c\u5251\u6cd5\u4e4b\u9ad8\uff0c\u6211\u662f\u7b2c\u4e00\u6b21\u9047\u4e0a\uff0c \u5982\u662f\u5355\u6253\u72ec\u6597\uff0c\u533a\u533a\u81ea\u77e5\u975e\u4eba\u654c\u624b\uff0c\u6211\u8bf4\u5355\u6253\u72ec\u6597\u7684\u610f\u601d\u662f\u5404\u51ed\u6240\u5b66\uff0c\u628a\u81ea\u5df1\u7684\u6280\u827a\u5b8c\u5168\u53d1 \u6325\u51fa\u6765\u3002\u201d \u3000\u3000\u4e09\u5927\u6355\u5934\u6ca1\u4eba\u63a5\u53e3\uff0c\u60dc\u7389\u5fae\u7fd8\u5c0f\u5634\u5df4\uff0c\u4e5f\u672a\u591a\u8a00\u3002 \u3000\u3000\u5c0f\u6587\u76ee\u5149\u8f6c\u52a8\uff0c\u626b\u63a0\u8fc7\u5434\u3001\u4e8e\u3001\u5c91\u4e09\u5927\u6355\u5934\uff0c\u9053\uff1a\u201c\u4e09\u4f4d\u6709\u4f55\u9ad8\u89c1\uff1f\u8bf7\u8bf4\u51fa\u6765\u5440\uff01\u201d \u3000\u3000\u201c\u8bf4\u4ec0\u4e48\uff1f\u5c0f\u675c\u5df2\u7ecf\u8bf4\u7684\u5f88\u6e05\u695a\u4e86\uff0c\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u8fd9\u79cd\u4e22\u4eba\u73b0\u773c\u7684\u4e8b\uff0c\u8bf4\u591a\u4e86\u8138\u4f1a\u53d1 \u70ed\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u6362\u4e00\u79cd\u5251\u6cd5\u5bf9\u6297\u90a3\u4e9b\u4eba\uff0c\u6709\u51e0\u79cd\u80dc\u7b97\uff1f\u201d\u5c0f\u6587\u9053\uff1a\u201c\u4f46\u5982\u65bd\u5c55\u5251\u6d77\u6d74\u9b42\uff0c\u80dc \u7b97\u6781\u5927\uff0c\u90a3\u65e5\u5bf9\u654c\uff0c\u6211\u53ea\u4e0d\u8fc7\uff0c\u7528\u51fa\u4e03\u6210\u6280\u827a\u2026\u2026\u201d \u3000\u3000\u201c\u4f46\u4eba\u5df2\u7ecf\u53d8\u7684\u516d\u4eb2\u4e0d\u8ba4\uff0c\u8fde\u5e9c\u53f0\u5927\u4eba\u4e5f\u60f3\u6740\u5566\uff01\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6740\u75af\u4e86\u5fc3\uff0c\u8fde\u6211\u4eec\u56db \u5927\u6355\u5934\u4e5f\u5c06\u4f5c\u4f60\u7684\u5251\u4e0b\u4ea1\u9b42\u4e86\u3002\u201d \u3000\u3000\u201c\u6709\u8fd9\u7b49\u4e8b\uff01\u600e\u4e48\u53ef\u80fd\u5462\uff1f\u6211\u5949\u547d\u4fdd\u62a4\u4ed6\uff0c\u4e0d\u51c6\u522b\u4eba\u4f24\u5bb3\u4ed6\u3002\u6211\u6539\u626e\u4f5c\u4ed6\u7684\u4e66\u7ae5\u2026\u2026\u201d \u3000\u3000\u201c\u73b0\u5728\uff0c\u4f60\u6e05\u9192\u4e00\u51fa\u5251\u4e4b\u540e\uff0c\u6740\u4e86\u51e0\u4e2a\u4eba\uff0c\u90a3\u90aa\u6076\u7684\u5251\u6cd5\uff0c\u7275\u52a8\u4f60\u5fc3\u4e2d\u90a3\u70b9\u6302\u6068\uff0c\u4eba\u5c31 \u5168\u53d8\u4e86\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff0c\u4f60\u53cc\u76ee\u4e2d\u6d41\u9732\u51fa\u6765\u7684\u6740\u673a\uff0c\u6211\u4eec\u56db\u4e2a\u4eba\u90fd\u6709\u4e9b\u5bb3\u6015\uff0c\u6240 \u4ee5\uff0c\u5c0f\u675c\u624d\u5192\u7740\u6027\u547d\u7684\u5371\u9669\uff0c\u7528\u8bc8\u672f\u70b9\u4e86\u4f60\u7684\u7a74\u9053\u3002\u56e0\u4e3a\uff0c\u6211\u4eec\u90fd\u4e0d\u77e5\u9053\u70b9\u7a74\u4e0d\u4e2d\u7684\u540e\u679c\uff0c \u4f60\u4f1a\u4e0d\u4f1a\u4e00\u5251\u6740\u4e86\u5c0f\u675c\uff1f\u201d \u3000\u3000\u201c\u6211\u600e\u4e48\u53d8\u7684\u5982\u6b64\u80e1\u95f9\uff0c\u5c0f\u59d0\u77e5\u9053\u4e86\uff0c\u4e0d\u6740\u6211\uff0c\u4e5f\u5f97\u6252\u6211\u4e00\u5c42\u76ae\u4e86\uff01\u201d \u3000\u3000\u56db\u5927\u6355\u5934\u4e5f\u90fd\u542c\u5f97\u4e00\u6014\uff0c\u770b\u4e0a\u53bb\u5982\u82b1\u89e3\u8bed\u7684\u603b\u6355\u5934\uff0c\u7adf\u4f1a\u6709\u5982\u6b64\u68ee\u4e25\u4e00\u9762\uff0c\u5c0f\u6587\u662f\u771f\u7684 \u6015\uff0c\u6015\u7684\u8eab\u5b50\u4e5f\u6709\u70b9\u53d1\u6296\u4e86\u3002 \u3000\u3000\u201c\u5c0f\u6587\u59d1\u5a18\uff0c\u6d88\u53bb\u4e86\u5fc3\u5934\u90a3\u70b9\u6302\u788d\uff0c\u90aa\u6076\u4e4b\u5251\u5c31\u4e0d\u4f1a\u7275\u52a8\u4f60\u5fc3\u4e2d\u6068\u610f\uff01\u201d\u5434\u94c1\u5cf0\u9053\uff1a \u201c\u6740\u8fc7\u8fd9\u6279\u6740\u624b\u4e4b\u540e\uff0c\u5c31\u4ece\u6b64\u4e0d\u518d\u65bd\u7528\u8fd9\u5957\u5251\u6cd5\u3002\u201d \u3000\u3000\u5c0f\u6587\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u7adf\u7136\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u738b\u5927\u4eba\u5462\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8eb2\u8d77\u6765\u4e86\uff0c\u6211\u4eec\u56db\u4e2a\u5408\u8ba1\u8fc7\uff0c\u8054\u624b\u8fce\u6218\uff0c\u4e5f\u672a\u5fc5\u80fd\u4fdd\u62a4\u4f4f\u5927\u4eba\u7684\u5b89\u5168\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u5c0f\u6587\u53d8\u6210\u5c0f\u9b54\u5973\u4e86\uff1f\u603b\u6355\u5934\u56de\u6765\uff0c\u6211\u4e00\u5b9a\u8981\u636e\u5b9e\u7980\u660e\uff0c\u7531\u5979\u5904\u7f6e\uff0c\u780d\u4e86\u6211\u4e00\u53ea\u624b\uff0c \u6211\u4e5f\u8ba4\u547d\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u4f1a\u66ff\u4f60\u4e89\u4e2a\u516c\u9053\uff0c\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u529d\u8c0f\u4e0d\u542c\uff0c\u6211\u5c31\u8f9e\u804c\u5f52\u7c4d\uff0c\u559d\u6211\u7684\u4e8c\u9505\u5934\u53bb\u3002 \u5e72\u4e86\u8fd9\u4e9b\u5e74\u4e00\u65b9\u6355\u5934\uff0c\u8fde\u9152\u90fd\u6212\u4e86\uff0c\u4ece\u4e0d\u6562\u653e\u5f00\u91cf\u559d\u8fc7\u4e00\u6b21\uff0c\u751f\u6015\u9152\u9189\u8bef\u4e8b\u3002\u201d \u3000\u3000\u201c\u5bf9\uff01\u5c0f\u6587\u59d1\u5a18\u65bd\u5c55\u2018\u5251\u6d77\u6d74\u9b42\u2019\uff0c\u662f\u4e3a\u4e86\u89e3\u6551\u626c\u5dde\u5e9c\u8859\u4e4b\u5371\uff0c\u4e5f\u4e3a\u6551\u6211\u56db\u5927\u6355\u5934\u4e4b\u547d\uff0c \u62da\u4e0a\u4e00\u8eab\u9876\u5e26\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u5c0f\u6587\u59d1\u5a18\u53d7\u5230\u59d4\u5c48\u3002\u201d\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u5c91\u80e1\u5b50\uff0c\u6211\u652f\u6301\u4f60\u7684\u4f5c\u6cd5\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8981\u80e1\u8bf4\u516b\u9053\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u603b\u6355\u5934\u5c82\u662f\u4e0d\u901a\u60c5\u7406\u7684\u4eba\uff0c\u4f55\u51b5\uff0c\u8fd8\u6709\u5e9c\u53f0\u5927\u4eba\u66ff\u6211 \u4eec\u4f5c\u4e3b\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u662f\uff0c\u4e0b\u5b98\u4f1a\u636e\u7406\u529b\u4e89\uff0c\u62c5\u4fdd\u5c0f\u6587\u59d1\u5a18\u5e73\u5b89\u65e0\u4e8b\u3002\u201d\u738b\u5927\u4eba\u5b9e\u5728\u618b\u4e0d\u4f4f\u4e86\uff0c\u4e5f\u5c31\u987e \u4e0d\u5f97\u4e00\u8eab\u8859\u5f79\u88c5\u626e\uff0c\u5f00\u4e86\u8154\u3002 \u3000\u3000\u5c0f\u6587\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u4eba\uff0c\u59d4\u5c48\u4f60\u4e86\uff0c\u6211\u5e74\u7eaa\u8f7b\uff0c\u4fee\u517b\u8fd8\u4e0d\u591f\uff0c\u63a7\u5236\u4e0d\u597d\u81ea\u5df1\uff0c\u5f00\u7f6a \u5927\u4eba\u7684\u5730\u65b9\uff0c\u53ef\u4e0d\u8981\u653e\u5728\u5fc3\u4e0a\uff0c\u6211\u8fd9\u53a2\u8bf7\u7f6a\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8bf7\u7f6a\uff0c\u7adf\u7136\u771f\u7684\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u738b\u5c11\u537f\u614c\u7684\u76f4\u8df3\u811a\uff0c\u9053\uff1a\u201c\u5feb\u5feb\u5feb\uff0c\u5feb\u628a\u5c0f\u6587\u59d1\u5a18\u6276\u8d77\u6765\uff0c\u60dc\u7389\u4f84\u5973\u513f\u554a\uff0c\u5e2e\u59e8\u4e08\u4e00\u4e2a \u5fd9\u554a\uff01\u201d \u3000\u3000\u60dc\u7389\u4e00\u6b20\u8eab\uff0c\u9053\uff1a\u201c\u4f84\u5973\u9075\u547d\u4e86\uff01\u201d\u4e00\u628a\u62b1\u8d77\u5c0f\u6587\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u5982\u60f3\u738b\u5927\u4eba\u62b1\u62b1\u4f60\uff0c \u5c31\u6492\u8d56\u76ae\uff0c\u522b\u7ad9\u8d77\u6765\u3002\u201d \u3000\u3000\u8fd8\u771f\u7075\uff0c\u5c0f\u6587\u4e00\u4e0b\u5b50\u7ad9\u76f4\u4e86\u8eab\u5b50\u3002 \u3000\u3000\u738b\u5c11\u537f\u5feb\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u5982\u4f55\u80fd\u602a\u7f6a\u59d1\u5a18\uff0c\u8fd9\u5168\u662f\u5251\u6cd5\u4f5c\u7684\u602a\uff0c\u602a\u529b\u4e71\u795e\u561b\uff01\u201d \u3000\u3000\u201c\u770b\u6837\u5b50\uff0c\u4e00\u70b9\u5c0f\u8bef\u4f1a\u5168\u5df2\u5316\u89e3\u4e86\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u603b\u6355\u5934\u4ecd\u672a\u5f52\u6765\uff0c\u6211\u76f8\u4fe1\u4eca\u5929\u665a\u4e0a\uff0c \u5b9a\u4f1a\u9ad8\u624b\u5168\u51fa\uff0c\u8fd9\u4e00\u6218\u4e8b\u5173\u91cd\u5927\uff0c\u5c0f\u6587\u59d1\u5a18\u90aa\u6076\u7684\u5251\u6cd5\uff0c\u53ea\u6015\u5f88\u96be\u8fc7\u5173\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u662f\u90aa\u6076\u7684\u5251\u6cd5\uff0c\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u6280\u827a\u65e0\u6b63\u90aa\uff0c\u7528\u6765\u6b7c\u622e\u6740\u624b\uff0c\u662f\u6b63\uff0c\u5982\u7528\u6765\u6297\u62d2 \u6211\u4eec\u8fd9\u4e9b\u6267\u6cd5\u6355\u5feb\uff0c\u662f\u90aa\u3002\u8001\u5434\u554a\uff01\u4f60\u4e0d\u8981\u518d\u5413\u552c\u5c0f\u6587\u59d1\u5a18\u4e86\uff0c\u8ba9\u5979\u754f\u9996\u754f\u5c3e\uff0c\u62da\u547d\u4e5f\u62da\u7684 \u4e0d\u8212\u670d\u3002\u201d \u3000\u3000\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u4e8b\u6709\u7f13\u6025\uff0c\u5c0f\u6587\u59d1\u5a18\uff0c\u603b\u6355\u5934\u5c1a\u672a\u56de\u6765\uff0c\u5c31\u5f97\u5168\u770b\u4f60\u7684\u4e86\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a \u201c\u5c91\u80e1\u5b50\u5df2\u6c14\u7684\u8981\u7ffb\u8138\u4e86\uff0c\u770b\u6765\u51fa\u4e0a\u4efb\u4f55\u4e00\u70b9\u5dee\u9519\uff0c\u90fd\u4f1a\u8bb0\u5728\u5434\u67d0\u7684\u5934\u4e0a\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u4fdd\u62a4\u738b\u5927\u4eba\uff0c\u7edd\u4e0d\u8bb8\u4efb\u4f55\u4eba\u4fb5\u72af\u5230\u4ed6\u2026\u2026\u201d \u3000\u3000\u201c\u4e8b\u60c5\u662f\u4e0d\u9519\uff0c\u4f46\u5de5\u4f5c\u5206\u914d\u4e0d\u5bf9\uff01\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5f20\u9e9f\u3001\u77f3\u742a\uff0c\u662f\u4e13\u53f8\u4fdd\u62a4\u5927\u4eba\u7684\uff0c\u6211 \u4eec\u56db\u4e2a\u4eba\u548c\u4f60\u5c0f\u6587\u59d1\u5a18\u5408\u6210\u4e00\u7ec4\uff0c\u65e2\u6297\u62d2\u5f3a\u654c\uff0c\u4e5f\u4fdd\u62a4\u5927\u4eba\uff0c\u552f\u4e00\u7684\u4e0d\u540c\u662f\uff0c\u4f60\u662f\u4ee5\u6740\u654c\u4e3a \u4e3b\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u5b8c\u5168\u660e\u767d\u4e86\uff0c\u201d\u76ee\u5149\u4e00\u63a0\u7a7f\u7740\u8859\u5f79\u670d\u7684\u5f20\u9e9f\u3001\u77f3\u742a\uff0c\u63a5\u9053\uff1a\u201c\u4f55\u5927\u5149\u5462\uff1f\u738b\u526f\u603b\u6355 \u5934\u5462\uff1f\u201d \u3000\u3000\u201c\u90fd\u53d7\u4e86\u4f24\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u4f55\u5927\u5149\u65ad\u53bb\u4e86\u4e00\u81c2\uff0c\u738b\u526f\u603b\u6355\u5934\u8eab\u4e0a\u6709\u56db\u5904\u5251\u4f24\u3002\u201d \u3000\u3000\u201c\u90fd\u662f\u4e3a\u4e86\u4fdd\u62a4\u6211\uff01\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u60ed\u6127\u5440\uff01\u60ed\u6127\u3002\u201d \u3000\u3000\u5c0f\u6587\u9053\uff1a\u201c\u60ed\u6127\u7684\u662f\u6211\u4eec\uff0c\u5927\u4eba\uff0c\u603b\u6355\u5934\u5343\u91cc\u4e0b\u626c\u5dde\uff0c\u56db\u5927\u65b9\u6355\u5934\u5949\u547d\u4f1a\u805a\u5230\u6b64\uff0c\u4e5f\u662f \u4e3a\u4e86\u4fdd\u62a4\u5927\u4eba\u7684\u5b89\u5168\uff0c\u8fd9\u662f\u6211\u4eec\u7b2c\u4e00\u8981\u52a1\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u5fae\u5fae\u7684\u4e00\u6014\uff0c\u5fd6\u9053\uff1a\u4fdd\u62a4\u6211\u662f\u7b2c\u4e00\u8981\u52a1\uff0c\u96be\u9053\u6bd4\u7834\u83b7\u65b0\u623f\u547d\u6848\uff0c\u8fd8\u8981\u91cd\u8981\uff0c\u8fd9 \u662f\u2026\u2026\u5fc3\u4e2d\u52a8\u7591\u4e86\uff0c\u4f46\u5374\u672a\u8ffd\u95ee\u3002 \u3000\u3000\u201c\u6740\u4f24\u6355\u5934\uff0c\u5192\u72af\u5b98\u5a01\uff0c\u8be5\u6b7b\u554a\uff01\u201d\u4e00\u62b9\u51f6\u5389\u7684\u795e\u8292\uff0c\u7531\u5c0f\u6587\u7684\u773c\u4e2d\u95ea\u8fc7\uff0c\u56de\u8eab\u4e00\u8dc3\u4e0a \u5e8a\uff0c\u76d8\u819d\u5750\u606f\u8d77\u6765\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u8f7b\u8f7b\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u8ba9\u5979\u597d\u597d\u4f11\u606f\uff0c\u5979\u662f\u771f\u7684\u7d2f\u4e86\uff0c\u6211\u4eec\u5230\u5385\u4e2d\u5750\u53bb\u2026\u2026\u201d \u3000\u3000\u4eba\u5374\u9760\u8fd1\u675c\u671b\u6708\uff0c\u5e76\u80a9\u51fa\u5385\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u675c\uff0c\u770b\u5230\u6ca1\u6709\uff0c\u7a0d\u4e00\u52a8\u6c14\uff0c\u5c31\u76ee\u95ea\u51f6\u8292\uff0c\u8fd9 \u5957\u90aa\u6076\u4e60\u7684\u5251\u6cd5\uff0c\u6050\u6015\u5df2\u4fb5\u5165\u4e86\u5979\u7684\u5fc3\u4e2d\uff0c\u5f88\u96be\u63a7\u5236\u4e86\uff0c\u6ca1\u6709\u529e\u6cd5\u80fd\u9884\u6d4b\u5979\u4f1a\u505a\u51fa\u4ec0\u4e48\u4e8b \u60c5\uff1f\u201d \u3000\u3000\u201c\u90a3\u8981\u518d\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u80fd\u91cd\u65bd\u6545\u6280\u4e86\uff0c\u5979\u7684\u5fc3\u7406\u4e0a\u5df2\u6709\u9632\u5907\uff0c\u5434\u67d0\u771f\u7684\u4e0d\u6562\u9884\u6d4b\uff0c\u6740\u5149\u5f3a\u654c\uff0c\u4e5f\u53ef\u80fd\u6740\u4e86 \u4f60\u675c\u671b\u6708\u548c\u738b\u5927\u4eba\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u4e3a\u96be\u4e86\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u8981\u5979\u5bf9\u4ed8\u6740\u624b\uff0c\u4f46\u5979\u672c\u8eab\u5374\u53c8\u662f\u6700\u96be\u9884\u9632\u7684\u6740\u624b\u3002\u5509\uff01 \u96be\u3001\u96be\u3001\u96be\u554a\uff01\u201d \u3000\u3000\u201c\u4e3a\u4eca\u4e4b\u8ba1\uff0c\u53ea\u6709\u5c3d\u4eba\u4e8b\uff0c\u800c\u542c\u5929\u547d\u4e86\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u8fd8\u4e0d\u80fd\u544a\u8bc9\u5c91\u80e1\u5b50\uff0c \u53ea\u8981\u4ed6\u4e00\u56b7\u51fa\u6765\uff0c\u5c31\u5168\u7838\u9505\u4e86\u3002\u201d \u3000\u3000\u201c\u8bf4\u5427\uff01\u600e\u4e48\u4e00\u4e2a\u5b89\u6392\u6cd5\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u6211\u5c31\u60f3\u4e0d\u51fa\u4f1a\u6709\u4ec0\u4e48\u6cd5\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u8981\u968f\u65f6\u6ce8\u610f\u5230\uff0c\u5c0f\u6587\u548c\u738b\u5927\u4eba\u7684\u65b9\u4f4d\u8ddd\u79bb\uff0c\u6211\u4eec\u5c45\u5176\u95f4\uff0c\u4e00\u65e6\u6709\u53d8\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a \u201c\u5c31\u4e0d\u4f1a\u5e94\u53d8\u4e0d\u53ca\u4e86\uff01\u201d \u3000\u3000\u201c\u597d\uff01\u522b\u65e0\u826f\u7b56\uff0c\u53ea\u597d\u62ff\u547d\u53bb\u8d4c\u4e86\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u4f30\u8ba1\u53ef\u4ee5\u6321\u4e0b\u7b2c\u4e00\u5251\uff0c\u7b2c\u4e8c\u5251\u5c31 \u662f\u51b2\u7740\u6211\u4eec\u6765\u4e86\uff0c\u5f88\u96be\u8bf4\u8fd8\u80fd\u4e0d\u80fd\u63a5\u5f97\u4e0b\u6765\uff1f\u201d \u3000\u3000\u201c\u671b\u6708\uff0c\u8fd9\u4e0d\u662f\u62ff\u6027\u547d\u5f00\u73a9\u7b11\u5417\uff01\u4e0d\u884c\uff0c\u4e0d\u884c\uff0c\u201d\u60dc\u7389\u4f4e\u58f0\u9053\uff1a\u201c\u53d1\u89c9\u60c5\u52bf\u4e0d\u5bf9\uff0c\u53ef\u4ee5 \u7528\u836f\u7269\u8ff7\u5012\u5979\u3002\u201d \u3000\u3000\u201c\u4e0a\u7b56\u4e5f\uff01\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u53ef\u662f\u8c01\u4eba\u6709\u6b64\u672c\u9886\u5462\uff1f\u8981\u5b88\u5728\u5979\u8eab\u4fa7\uff0c\u89c1\u673a\u884c\u4e8b\uff0c\u8fd8\u8981\u51fa\u624b \u5982\u7535\u2026\u2026\u201d \u3000\u3000\u201c\u6211\uff01\u201d\u60dc\u7389\u9053\uff1a\u201c\u5c0f\u5973\u5b50\u8eab\u624b\u4e0d\u9519\uff0c\u76f8\u4fe1\u53ef\u5f53\u6b64\u5927\u4efb\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u5446\u4e86\u5446\uff0c\u9053\uff1a\u201c\u4f60\u4f1a\u65bd\u7528\u8ff7\u836f\u2026\u2026\u201d \u3000\u3000\u201c\u8fc7\u53bb\u4e0d\u4f1a\uff0c\u73b0\u5728\u4f1a\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e0d\u662f\u5f00\u73a9\u7b11\u554a\uff01\u4e00\u51fb\u4e0d\u4e2d\uff0c\u5c31\u4e0d\u4f1a\u518d\u6709\u7b2c\u4e8c\u6b21\u673a\u4f1a\u4e86\u3002\u201d\u675c\u671b\u6708\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4e0d\u80fd \u901e\u5f3a\u597d\u80dc\u3002\u201d \u3000\u3000\u770b\u5230\u675c\u671b\u6708\u7684\u5173\u5fc3\u3001\u7126\u8651\uff0c\u60dc\u7389\u5f88\u5f00\u5fc3\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u628a\u63e1\uff0c\u6211\u600e\u4e48\u6562\u4f5c\u6b64\u627f\u8bfa\u3002 \u6211\u660e\u767d\uff0c\u8fd9\u4e0d\u662f\u6211\u4e00\u4e2a\u4eba\u7684\u751f\u6b7b\uff0c\u8fd8\u5173\u7cfb\u5230\u4f60\u4eec\u51e0\u4f4d\u5927\u6355\u5934\u548c\u738b\u5927\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u5c31\u8fd9\u4e48\u529e\u4e86\uff0c\u8a00\u591a\u6709\u5931\uff0c\u518d\u8c08\u4e0b\u53bb\uff0c\u96be\u514d\u8981\u6cc4\u6f0f\u51fa\u80f8\u4e2d\u4e4b\u79d8\u3002\u201d\u5434\u94c1\u5cf0\u770b\u7740\u675c\u671b\u6708\u63a5 \u9053\uff1a\u201c\u672a\u5fc5\u5c31\u4e00\u5b9a\u80fd\u6210\u529f\uff0c\u4f46\u6709\u8ba1\u603b\u6bd4\u65e0\u8ba1\u597d\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\u5168\u90fd\u51c6\u5907\u62da\u547d\u4e86\uff0c\u603b\u4e0d\u80fd\u529d\u60dc\u7389\u82df\u5b89\u8fdc\u79bb\uff0c\u4ee5\u4fdd\u5168\u6027\u547d\uff0c\u4ee5\u60dc\u7389\u7684\u521a\u70c8\uff0c\u529d\u4e86 \u4e5f\u4e0d\u4f1a\u7b54\u5e94\u3002\u4f55\u51b5\uff0c\u8fd9\u4e9b\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u53e3\uff0c\u53ea\u597d\u5fcd\u4e0b\u4e86\uff0c\u771f\u662f\u75bc\u5728\u5fc3\u5934\u53e3\u96be\u5f00\u5440\uff01 \u3000\u3000\u679c\u7136\u5c91\u5578\u864e\u5f00\u4e86\u8154\uff0c\u9053\uff1a\u201c\u8001\u5434\uff0c\u4f60\u548c\u5c0f\u675c\u5546\u91cf\u51fa\u4e00\u4e2a\u529e\u6cd5\u6ca1\u6709\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u76f4\u6765\u76f4\u5f80\uff0c\u5374\u504f\u53c8\u6709\u7740\u5f88\u673a\u8b66\u7684\u53cd\u5e94\u3002 \u3000\u3000\u201c\u529e\u6cd5\u5012\u6709\uff0c\u4f46\u96be\u79f0\u4e07\u5168\uff0c\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4e00\u65e6\u548c\u654c\u4eba\u63a5\u89e6\uff0c\u628a\u738b\u5927\u4eba\u5b89\u6392\u5728\u575a\u58c1\u5899\u89d2 \u4e4b\u4e0b\uff0c\u7531\u4e24\u4f4d\u6b66\u5f53\u5251\u5ba2\uff0c\u7d27\u5b88\u5728\u5927\u4eba\u8eab\u65c1\uff0c\u548c\u56db\u4e2a\u6355\u5feb\u78b0\u5934\uff0c\u5e03\u6210\u4e00\u4e2a\u5706\u9635\uff0c\u6211\u4eec\u56db\u4e2a\u5728\u5916 \u56f4\u5835\u51fb\uff0c\u5c3d\u91cf\u62d2\u654c\u4e8e\u4e09\u4e08\u4e4b\u5916\uff0c\u518d\u7531\u5c0f\u6587\u59d1\u5a18\u4e3b\u9635\u6740\u654c\u3002\u201d\u8fd9\u4e0d\u662f\u5434\u94c1\u5cf0\u771f\u6b63\u7684\u987e\u8651\uff0c\u4f46\u4ed6 \u5728\u5e03\u7f72\u4e4b\u4e2d\uff0c\u5df2\u628a\u56db\u5927\u6355\u5934\u6392\u4e86\u5b9a\u4f4d\uff0c\u53ef\u62d2\u6740\u624b\u4e8e\u4e09\u4e08\u4e4b\u5916\uff0c\u5f53\u7136\u4e5f\u53ef\u4ee5\u62d2\u5c0f\u6587\u4e8e\u4e09\u4e08\u4ee5\u5916\u3002 \u53ea\u662f\u8fd9\u4e00\u9635\u8054\u624b\u6297\u62d2\uff0c\u80fd\u53d1\u751f\u591a\u5927\u6548\u7528\uff0c\u5c31\u5f88\u96be\u9884\u671f\u4e86\u3002\u56db\u5927\u6355\u5934\u4e2d\uff0c\u4ee5\u5434\u94c1\u5cf0\u5bf9\u8fd9\u5957\u5251\u6cd5 \u4e86\u89e3\u6700\u6df1\uff0c\u5fc3\u4e2d\u4e5f\u662f\u6700\u6050\u60e7\u3002 \u3000\u3000\u4ed6\u671f\u671b\u4eca\u591c\u6740\u624b\u4e0d\u8981\u6765\uff0c\u603b\u6355\u5934\u65e9\u4e9b\u5f52\u6765\uff0c\u4f46\u4e8b\u5b9e\u603b\u96be\u5982\u4eba\u610f\u3002 \u3000\u3000\u8d77\u66f4\u4e4b\u540e\uff0c\u5929\u8272\u5ffd\u53d8\uff0c\u4e00\u9635\u72c2\u98ce\u522e\u8fc7\uff0c\u5927\u96e8\u503e\u76c6\u800c\u4e0b\u3002 \u3000\u3000\u60dc\u7389\u7a81\u7136\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u53bb\u770b\u770b\u5c0f\u6587\u59d1\u5a18\u3002\u201d\u8f6c\u8eab\u6025\u6b65\u800c\u53bb\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u9635\u98ce\u6025\u9aa4\u96e8\uff0c\u60ca\u96f7\u95ea\u7535\uff0c\u5f88\u53ef\u80fd\u60ca\u9192\u4e86\u5c0f\u6587\u59d1\u5a18\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u60dc\u7389\u673a\u8b66\uff0c\u5904 \u7f6e\u5f97\u5f53\uff0c\u4e5f\u5e0c\u671b\u8fd9\u4e00\u9635\u98ce\u96e8\u80fd\u6301\u7eed\u5230\u5929\u8272\u5927\u4eae\u2026\u2026\u201d \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\uff0c\u96e8\u52bf\u7a81\u7136\u5c0f\u6b47\uff0c\u5374\u4f20\u8fc7\u6765\u4e00\u4e2a\u51b7\u51b7\u7684\u58f0\u97f3\uff0c\u63a5\u9053\uff1a\u201c\u53ef\u53f9\u5929\u4e0d\u4ece\u4eba\u613f\uff0c\u6025\u96e8 \u505c\u6b47\uff0c\u5c31\u53d8\u6210\u6708\u9ed1\u98ce\u9ad8\u591c\u4e86\u2026\u2026\u201d \u3000\u3000\u5434\u94c1\u5cf0\u5403\u4e86\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u597d\u7cbe\u6df1\u7684\u5185\u529f\uff0c\u6211\u8bf4\u8bdd\u7684\u58f0\u97f3\u4e0d\u5927\uff0c\u5927\u98ce\u547c\u5578\u4e2d\uff0c\u4ecd\u80fd\u542c\u5f97\u5982 \u6b64\u6e05\u695a\uff0c\u662f\u771f\u6b63\u7684\u9ad8\u624b\u3002 \u3000\u3000\u201c\u6708\u9ed1\u98ce\u9ad8\u6740\u4eba\u591c\uff0c\u4f46\u80fd\u4e0d\u80fd\u6740\u5f97\u4e86\u4eba\uff0c\u201d\u5c91\u5578\u864e\u63a5\u9053\uff1a\u201c\u8fd8\u8981\u51ed\u85c9\u70b9\u771f\u5b9e\u672c\u9886\u4e86\u3002\u201d \u3000\u3000\u201c\u5584\u8005\u4e0d\u6765\uff0c\u6765\u8005\u4e0d\u5584\uff0c\u4e1c\u3001\u5357\u3001\u4e2d\u3001\u897f\u56db\u540d\u6355\uff0c\u5df2\u7ecf\u8000\u6b66\u626c\u5a01\u5c06\u8fd1\u4e94\u5e74\uff0c\u201d\u4ecd\u662f\u90a3\u51b7 \u51b7\u7684\u58f0\u97f3\uff0c\u63a5\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\uff0c\u6709\u4e0d\u5c11\u683d\u5728\u4e86\u56db\u4f4d\u624b\u4e2d\uff0c\u4eca\u5bb5\u8981\u4ee5\u56db\u4f4d\u8eab\u4e0a\u7684\u9c9c\u8840\uff0c\u5960 \u6170\u4ed6\u4eec\u5728\u5929\u4e4b\u7075\u3002\u201d \u3000\u3000\u53e3\u6c14\u7279\u5927\uff0c\u8bf4\u660e\u4e86\u8981\u4e3a\u56db\u5927\u6355\u5934\u8ffd\u6740\u3001\u7f09\u6355\u7684\u6c5f\u6e56\u670b\u53cb\u4eec\uff0c\u8ba8\u53d6\u8fd9\u7b14\u8840\u503a\u3002 \u3000\u3000\u8fd9\u5c31\u4e0d\u50cf\u662f\u6c5f\u5357\u4e00\u5730\u7eff\u6797\u9053\u4e0a\u7684\u4eba\u7269\u4e86\u3002 \u3000\u3000\u201c\u5f88\u597d\uff0c\u6562\u81ea\u5618\u80fd\u4ee3\u8868\u6c5f\u6e56\u9053\u4e0a\u4eba\uff0c\u8ba8\u53d6\u8fd9\u7b14\u8840\u503a\u7684\uff0c\u7edd\u975e\u65e0\u540d\u4e4b\u8f88\uff0c\u533a\u533a\u957f\u5b89\u4e8e\u627f\u5fd7\uff0c \u8bf7\u6559\u9601\u4e0b\u7684\u4e0a\u59d3\u5927\u540d\uff1f\u201d \u3000\u3000\u4e00\u6539\u6740\u624b\u4e0d\u7559\u59d3\u540d\u3001\u5f62\u8c8c\u7684\u4f5c\u98ce\uff0c\u6765\u4eba\u7adf\u7136\u7b54\u4e86\u8bdd\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u59d3\u540d\u4e0d\u89c1\u4eba\uff0c\u5f7c\u6b64\u4e4b\u95f4\uff0c \u8fd8\u7559\u6709\u4f38\u7f29\u4f59\u5730\uff0c\u4e00\u7167\u9762\uff0c\u6216\u662f\u901a\u4e86\u59d3\u540d\uff0c\u90a3\u5c31\u662f\u4e00\u4e2a\u4e0d\u6b7b\u4e0d\u4f11\u7684\u5c40\u9762\u4e86\u3002\u201d \u3000\u3000\u201c\u7bad\u5728\u5f26\u4e0a\uff0c\u4e8b\u5230\u4e34\u5934\uff0c\u4e5f\u5c31\u7528\u4e0d\u7740\u5047\u60fa\u60fa\u4e86\u3002\u201d\u5c91\u5578\u864e\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u56db\u5927\u6355\u5934\uff0c\u786e\u5b9e \u6293\u4e86\u4e0d\u5c11\u6076\u4eba\uff0c\u6211\u4eec\u6267\u6cd5\u65f6\uff0c\u4e5f\u5c3d\u91cf\u6392\u9664\u5c60\u6740\u7684\u624b\u6bb5\uff0c\u4f46\u6076\u5f92\u62d2\u6355\u987d\u6297\uff0c\u90a3\u5c31\u5404\u51ed\u6b66\u529f\u4e00\u51b3 \u751f\u6b7b\u4e86\uff0c\u4f60\u670b\u53cb\u5982\u80af\u62a5\u4e0a\u59d3\u540d\uff0c\u73b0\u8eab\u76f8\u89c1\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u7528\u8bf4\u4e0b\u53bb\u4e86\uff0c\u6211\u901a\u540d\u62a5\u59d3\uff0c\u4e5f\u4ee5\u771f\u9762\u548c\u4f60\u4eec\u76f8\u89c1\uff0c\u4f60\u4eec\u56db\u5927\u6355\u5934\u662f\u4e00\u8d77\u4e0a\u5462\uff1f\u8fd8\u662f \u4e00\u4e2a\u4e2a\u6765\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u53e3\u6c14\uff0c\u5927\u7684\u79bb\u8c31\uff0c\u56db\u4e2a\u4eba\u5168\u542c\u5f97\u4e0a\u4e86\u706b\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\u4e0d\u52b3\u8d39\u5fc3\u4e86\uff0c\u4e00\u4e2a\u6253\u4e0d\u8fc7\uff0c\u6211\u4eec\u81ea\u4f1a\u4e0a\u4e24\u4e2a\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u9601\u4e0b\u7684\u6280\u827a\uff0c\u771f\u5982 \u53e3\u6c14\u4e00\u822c\u7684\u5927\u6cd5\uff0c\u4e5f\u53ef\u80fd\u56db\u4e2a\u4e00\u8d77\u4e0a\u4e86\u3002\u201d \u3000\u3000\u201c\u6700\u597d\u662f\u56db\u4e2a\u4eba\u4e00\u8d77\u4e0a\u4e86\u3002\u201d\u5bf9\u9762\u4e08\u4f59\u5904\uff0c\u6d53\u6d53\u591c\u8272\u4e4b\u4e2d\uff0c\u7ad9\u7740\u4e2a\u5168\u8eab\u9ed1\u8863\u7684\u4eba\uff0c\u8863\u8882 \u5728\u98ce\u4e2d\u98d8\u52a8\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\uff0c\u4e2a\u4e2a\u795e\u76ee\u5982\u7535\uff0c\u5c31\u662f\u6ca1\u6709\u770b\u6e05\u695a\uff0c\u90a3\u9ed1\u8863\u4eba\u51e0\u65f6\u51fa\u73b0\u7684\uff0c\u4f3c\u662f\u5f88\u4e45\u4e4b\u524d\uff0c\u4ed6 \u5c31\u7ad9\u5728\u90a3\u91cc\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u89c1\u591a\u8bc6\u5e7f\uff0c\u7acb\u523b\u611f\u89c9\u5230\u9047\u4e0a\u4e86\u4ece\u672a\u9047\u8fc7\u7684\u52b2\u654c\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u8fd9\u4eba\u6b66\u529f\u4e4b\u9ad8\uff0c\u5f53\u975e \u6211\u7b49\u80fd\u654c\uff0c\u5728\u6211\u6c5f\u5357\u9053\u7684\u5730\u76d8\u4e0a\uff0c\u5192\u9669\u72af\u96be\u7684\u4e8b\uff0c\u820d\u6211\u5176\u8c01\uff0c\u5f53\u4e0b\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6c5f\u5357\u9053 \u4e0a\uff0c\u6f5c\u9690\u4e86\u9601\u4e0b\u8fd9\u4e48\u4e00\u4f4d\u9ad8\u4eba\uff0c\u675c\u67d0\u7adf\u65e0\u6240\u77e5\uff0c\u5b9e\u5728\u60ed\u6127\u2026\u2026\u201d \u3000\u3000\u201c\u60ed\u6127\u4e8e\u4e8b\u65e0\u8865\uff0c\u201d\u9ed1\u8863\u4eba\u51b7\u51b7\u5730\u63a5\u9053\uff1a\u201c\u91cd\u8981\u7684\u662f\u8981\u4ed8\u51fa\u4ee3\u4ef7\uff0c\u660e\u5e74\u4eca\u65e5\uff0c\u5c31\u662f\u4f60\u4eec \u56db\u5927\u6355\u5934\u7684\u5468\u5e74\u796d\u65e5\uff0c\u56db\u4f4d\u540c\u65f6\u6210\u4e3a\u540d\u6355\uff0c\u4e5f\u540c\u65f6\u6bd5\u547d\u4e8e\u6b64\uff0c\u751f\u4e0d\u540c\u5e74\uff0c\u6b7b\u540c\u65e5\uff0c\u4e5f\u7b97\u662f\u6355 \u5934\u884c\u4e1a\u4e2d\u4e00\u6bb5\u4f73\u8bdd\u4e86\u3002\u201d \u3000\u3000\u201c\u591c\u98ce\u5982\u526a\uff0c\u4e5f\u4e0d\u6015\u526a\u4e86\u4f60\u7684\u820c\u5934\u3002\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u56db\u5927\u6355\u5934\u4fa6\u529e\u5211\u6848\u591a\u5e74\uff0c\u9047\u4e0a\u4e86\u4e0d \u5c11\u7684\u5978\u72e1\u51f6\u6b8b\u4e4b\u5f92\uff0c\u4f46\u6211\u4eec\u4f9d\u7136\u5065\u5728\uff0c\u81f3\u4e8e\u90a3\u5a01\u5413\u6050\u5413\u4e4b\u8a00\uff0c\u6211\u4eec\u4e5f\u542c\u5f97\u591a\u4e86\uff0c\u56db\u5927\u6355\u5934\u4e5f \u4e0d\u7b97\u662f\u5211\u6355\u53f8\u4e2d\u7684\u9ad8\u624b\u3002\u8001\u5b9e\u8bf4\uff0c\u4f60\u4eec\u51fa\u52a8\u7684\u8fd9\u6279\u6740\u624b\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u6280\u827a\u4e4b\u7cbe\uff0c\u5927\u51fa\u4e86\u6211\u4eec \u7684\u610f\u6599\u4e4b\u5916\uff0c\u4f46\u4f60\u4eec\u4f4e\u4f30\u4e86\u5211\u6355\u53f8\u7684\u5b9e\u529b\uff0c\u5374\u662f\u4f60\u4eec\u81f4\u547d\u7684\u4f24\u5bb3\uff0c\u4f60\u4eec\u4f1a\u5728\u4eca\u591c\u7684\u6355\u6740\u4e2d\uff0c \u5927\u5f00\u4e00\u6b21\u773c\u754c\uff0c\u89c1\u8bc6\u5230\u5251\u6cd5\u4e4b\u6700\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4e0d\u4e45\u524d\uff0c\u4f60\u4eec\u5df2\u7ecf\u9047\u4e0a\u8fc7\u4e00\u6b21\uff0c\u56de\u5fc6\u524d\u60c5\uff0c\u6216\u662f\u63a2\u8be2\u4e00\u4e0b\u4f60\u4eec\u7684\u670b\u53cb\uff0c\u5f53\u77e5\u543e \u8a00\u975e\u865a\u3002\u90a3\u591c\u6700\u540e\u51fa\u73b0\u7684\u91d1\u5200\u6740\u624b\uff0c\u76f8\u4fe1\u4e5f\u662f\u6740\u624b\u4e4b\u738b\u3001\u8840\u624b\u65b9\u8f6e\u624b\u4e0b\u6700\u7cbe\u5389\u7684\u6740\u624b\u7fa4\u4e86\u3002 \u4f46\u4ed6\u4eec\u5462\uff1f\u5374\u4f24\u60e8\u91cd\uff0c\u953b\u7fbd\u800c\u5f52\u3002\u201d \u3000\u3000\u8fd9\u7b49\u6709\u51ed\u6709\u636e\u7684\u8bf4\u6cd5\uff0c\u663e\u975e\u606b\u5413\u4e4b\u8bcd\uff0c\u8be1\u5f02\u96be\u6d4b\u7684\u9ed1\u8863\u4eba\uff0c\u4f3c\u662f\u4e5f\u6709\u70b9\u9707\u52a8\u4e86\uff0c\u6c89\u541f\u4e86 \u4e00\u9635\uff0c\u624d\u7f13\u7f13\u63a5\u9053\uff1a\u201c\u5e0c\u671b\u4ed6\u4ecd\u5728\u626c\u5dde\u7684\u5e9c\u8859\u4e2d\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4ed6\u73b0\u4ecd\u5728\u6b64\u5730\uff0c\u968f\u65f6\u53ef\u4ee5\u548c\u9601\u4e0b\u7167\u9762\u3002\u201d\u5434\u94c1\u5cf0\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u54b1\u4eec \u8fd8\u662f\u4e0d\u592a\u76f8\u4fe1\u9601\u4e0b\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u5f3a\u6309\u4e0b\u4e00\u8154\u6012\u706b\u9053\uff1a\u201c\u9601\u4e0b\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u8bd5\u8bd5\u9601\u4e0b\u7684\u624b\u6bb5\uff0c\u662f\u5426\u5982\u9601\u4e0b\u6240\u8a00\u4e00\u822c\u2026\u2026\u201d \u3000\u3000\u201c\u597d\uff01\u4f60\u51fa\u624b\u5427\uff01\u201d\u9ed1\u8863\u4eba\u5fae\u4e00\u8de8\u6b65\uff0c\u8eab\u5b50\u7a81\u7136\u98d8\u4e86\u8d77\u6765\uff0c\u867d\u7136\u5728\u9ed1\u591c\u4e4b\u4e2d\uff0c\u4ecd\u53ef\u770b\u5230 \u4ed6\u811a\u4e0d\u7740\u5730\uff0c\u5c31\u90a3\u4e48\u5728\u7a7a\u4e2d\u505c\u4e0b\u4e86\u3002 \u3000\u3000\u56db\u4eba\u6355\u5934\u4e5f\u90fd\u51dd\u805a\u4e86\u5168\u795e\uff0c\u6ce8\u89c6\u7740\u8fd9\u9ed1\u8863\u4eba\u7684\u4e3e\u52a8\uff0c\u800c\u4e14\uff0c\u5434\u3001\u4e8e\u3001\u5c91\u4e09\u4eba\u4e5f\u7f13\u7f13\u5411\u675c \u671b\u6708\u7684\u8eab\u4fa7\u96c6\u4e2d\u3002 \u3000\u3000\u8fd9\u4e2a\u9ed1\u8863\u4eba\u7684\u53e3\u6c14\u592a\u5927\u4e86\uff0c\u5927\u5230\u8fde\u56db\u5927\u6355\u5934\u4e5f\u611f\u89c9\u5230\u4e00\u80a1\u5f3a\u5927\u7684\u538b\u529b\u548c\u5a01\u80c1\u3002\u5fc3\u6709\u6240\u60e7\uff0c \u5c31\u81ea\u7136\u60f3\u5230\u4e86\u8054\u624b\u62d2\u654c\u7684\u4e8b\uff0c\u56db\u5927\u6355\u5934\u53ef\u4ee5\u786c\u7740\u5934\u76ae\u6218\u6b7b\uff0c\u5374\u4e0d\u80fd\u8eb2\u907f\u654c\u52bf\uff0c\u4e00\u8d70\u4e86\u4e4b\u3002 \u3000\u3000\u4e00\u76cf\u719f\u8336\u7684\u5de5\u592b\u8fc7\u53bb\u4e86\uff0c\u4f46\u90a3\u9ed1\u8863\u4eba\u4ecd\u7136\u7a33\u7a33\u5730\u60ac\u7a7a\u7ad9\u7740\u3002\u597d\u50cf\u6709\u4e00\u6839\u76ee\u529b\u96be\u89c1\u7684\u7ef3\u5b50 \u540a\u7740\u4ed6\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u4e0d\u53ef\u80fd\u4e0d\u51ed\u85c9\u4efb\u4f55\u7269\u529b\u7684\u5e2e\u52a9\uff0c\u60ac\u7a7a\u800c\u7acb\u3002\u800c\u4e14\uff0c\u8fd8\u80fd\u8bf4\u8bdd\uff0c\u624b\u8db3 \u4ea6\u53ef\u884c\u52a8\uff0c\u5386\u65f6\u751a\u4e45\u3002 \u3000\u3000\u53ea\u6b64\u4e00\u6869\uff0c\u56db\u5927\u6355\u5934\u90fd\u5df2\u611f\u89c9\u5230\u5bf9\u65b9\u6280\u827a\uff0c\u9ad8\u8fc7\u56db\u4eba\u5f88\u591a\u3002\u8fd9\u7b49\u60ac\u7a7a\u800c\u7acb\u7684\u6280\u5de7\uff0c\u5982\u975e \u662f\u4e00\u79cd\u7279\u6b8a\u7684\u6c14\u529f\uff0c\u7ec3\u6210\u6b64\u6280\uff0c\u90a3\u5c31\u66f4\u4e3a\u53ef\u6016\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u6700\u5148\u51fa\u624b\uff0c\u8fce\u9762\u4e00\u5251\u523a\u5411\u524d\u80f8\uff0c\u8c08\u4e0d\u4e0a\u5947\u5e7b\u53d8\u5316\uff0c\u4f46\u5374\u6df1\u5f97\u4e00\u4e2a\u5feb\u5b57\u3002\u60ac\u7acb\u5728\u7a7a \u4e2d\u7684\u9ed1\u8863\u4eba\uff0c\u8eab\u5982\u6768\u82b1\u98de\u7d6e\uff0c\u5728\u7a7a\u4e2d\u98d8\u8361\u8d77\u6765\uff0c\u968f\u7740\u5251\u52bf\u6e38\u52a8\u3002\u675c\u671b\u6708\u52a0\u523a\u4e86\u5341\u4f59\u5251\uff0c\u7adf\u7136 \u65e0\u4e00\u5251\u523a\u4e2d\u9ed1\u8863\u4eba\uff0c\u5fc3\u4e2d\u9707\u9a87\u6781\u4e86\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5434\u94c1\u5cf0\u3001\u4e8e\u627f\u5fd7\u3001\u5c91\u5578\u864e\u5168\u90fd\u770b\u89c1\u4e86\u3002 \u3000\u3000\u539f\u6765\u591c\u6697\u5982\u58a8\uff0c\u56db\u5927\u6355\u5934\u76ee\u529b\u867d\u5f3a\uff0c\u4e5f\u770b\u7684\u4e0d\u751a\u6e05\u695a\u3002\u4f46\u675c\u671b\u6708\u5251\u5982\u6d41\u661f\uff0c\u5feb\u901f\u523a\u70b9\uff0c \u5c31\u501f\u90a3\u5251\u4e0a\u7684\u5fae\u5f31\u5149\u8292\uff0c\u770b\u6e05\u4e86\u8fd9\u573a\u640f\u51fb\u3002\u675c\u671b\u6708\u5251\u5982\u95ea\u8f6c\u7684\u661f\u5149\uff0c\u9ed1\u8863\u4eba\u5374\u4f3c\u5251\u4e0a\u8709\u8763\uff0c \u4eba\u968f\u5251\u98d8\uff0c\u6beb\u53d1\u65e0\u4f24\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u706b\u5149\u95ea\u52a8\uff0c\u5e7d\u6697\u7684\u591c\u8272\u4e2d\uff0c\u4eae\u8d77\u4e00\u652f\u706b\u628a\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u98d8\u6d6e\u7684\u8eab\u5b50\uff0c\u4e5f\u540c\u65f6\u843d\u7740\u5b9e\u5730\uff0c\u94ee\u94ee\u4e24\u58f0\u91d1\u94c1\u4ea4\u9e23\uff0c\u9ed1\u8863\u4eba\u624b\u4e2d\u591a\u4e86\u4e00\u628a\u5251\uff0c\u675c \u671b\u6708\u4e5f\u88ab\u8fd9\u4e24\u5251\u9707\u9000\u4e86\u4e94\u516d\u5c3a\u8fdc\u3002 \u3000\u3000\u706b\u628a\u8000\u7167\u4e0b\u4eae\u5982\u767d\u663c\uff0c\u56db\u5468\u666f\u7269\u5df2\u6e05\u6670\u53ef\u89c1\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\u76ee\u5149\u4e00\u63a0\u56db\u4e0b\u7684\u5f62\u52bf\uff0c\u4e0d\u7981\u4e3a\u4e4b\u4e00\u5446\u3002 \u3000\u3000\u4e0d\u77e5\u4f55\u65f6\uff0c\u5341\u4f59\u4f4d\u9ed1\u8863\u5251\u624b\uff0c\u5df2\u628a\u4e8c\u5802\u5916\u9762\u5168\u56f4\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5728\u706b\u628a\u4eae\u8d77\u7684\u540c\u65f6\uff0c\u4ed6\u4eec\u624b\u4e2d\u7684\u5b9d\u5251\uff0c\u4e5f\u51fa\u9798\u5f85\u654c\u3002 \u3000\u3000\u738b\u5927\u4eba\u3001\u77f3\u742a\u3001\u5f20\u9e9f\u548c\u56db\u5927\u6355\u5934\uff0c\u5168\u90fd\u9677\u5165\u4e86\u8fd9\u6279\u5251\u624b\u7684\u5305\u56f4\u4e4b\u4e2d\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u626f\u53bb\u4e86\u63a9\u906e\u5728\u8138\u4e0a\u4e00\u7247\u9ed1\u5e03\uff0c\u9732\u4e86\u4e94\u7efa\u767d\u8272\u957f\u9aef\uff0c\u5728\u591c\u98ce\u4e2d\u98d8\u52a8\uff0c \u9053\uff1a\u201c\u8bf8\u4f4d\u4e00\u76f4\u60f3\u89c1\u8001\u592b\u7684\u771f\u6b63\u9762\u76ee\uff0c\u73b0\u5728\uff0c\u8001\u592b\u5b8c\u5168\u4ee5\u771f\u9762\u76ee\u548c\u8bf8\u4f4d\u89c1\u9762\uff0c\u5982\u6709\u8bc6\u6211\u4e4b\u4eba\uff0c \u5f53\u5df2\u77e5\u8001\u592b\u662f\u8c01\u4e86\uff1f\u201d \u3000\u3000\u56db\u5927\u6355\u5934\u4e2a\u4e2a\u51dd\u795e\u9759\u601d\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u610f\u7136\u65e0\u4eba\u51fa\u58f0\u3002 \u3000\u3000\u663e\u7136\u662f\uff0c\u56db\u4e2a\u4eba\u90fd\u65e0\u6cd5\u8ba4\u5f97\u51fa\u4ed6\u7684\u8eab\u4efd\u3002 \u3000\u3000\u767d\u9aef\u8001\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u4eec\u4e4b\u95f4\u5dee\u8ddd\u4e86\uff0c\u6211\u5bf9\u4f60\u4eec\uff0c\u4e86\u5982\u6307\u638c\uff0c\u5c24\u5176\u662f\u5728\u8fd9 \u6bb5\u65f6\u65e5\u4e2d\uff0c\u4f60\u4eec\u7684\u4e3e\u52a8\uff0c\u5927\u90e8\u90fd\u5728\u6211\u4eec\u7684\u76d1\u63a7\u4e4b\u4e2d\u3002\u539f\u8ba4\u4e3a\u7a0b\u5c0f\u8776\u8d76\u6765\u626c\u5dde\uff0c\u4f1a\u5408\u4f60\u4eec\u56db\u5927 \u6355\u5934\uff0c\u5e94\u8be5\u6709\u4e9b\u4f5c\u4e3a\uff0c\u6240\u4ee5\uff0c\u6211\u4e00\u76f4\u7b49\u5f85\uff0c\u73b0\u5728\u770b\u6765\uff0c\u7a0b\u5c0f\u8776\u7684\u5ffd\u9690\u5ffd\u73b0\uff0c\u53ea\u662f\u6545\u4f5c\u795e\u79d8\uff0c \u4f5c\u4e3a\u4e0d\u8fc7\u4e86\u4e86\u3002\u6240\u4ee5\uff0c\u6211\u4eec\u4e5f\u4e0d\u60f3\u518d\u7b49\u5979\u4e86\uff0c\u8bc4\u4f30\u6574\u4e2a\u4e8b\u4ef6\u7684\u7ed3\u679c\uff0c\u6211\u4eec\u51b3\u5b9a\u6309\u7167\u81ea\u5df1\u8ba1\u5212 \u884c\u52a8\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u7684\u8ba1\u5212\u662f\u2026\u2026\u201d\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4e0d\u80fd\u8bf4\uff0c\u8fd8\u662f\u4e0d\u6562\u8bf4\uff0c\u6089\u51ed\u5c0a\u4fbf\u2026\u2026\u201d \u3000\u3000\u201c\u7528\u4e0d\u7740\u518d\u7528\u6fc0\u5c06\u6cd5\uff0c\u201d\u767d\u9aef\u8001\u4eba\u9053\uff1a\u201c\u8001\u592b\u4f1a\u8bf4\uff0c\u800c\u4e14\u8bf4\u7684\u5f88\u6e05\u695a\uff0c\u5c31\u51ed\u4f60\u65e0\u6cd5\u8ba4\u5b9a \u8001\u592b\u8eab\u4efd\u4e00\u9879\u800c\u8a00\uff0c\u4f60\u4eec\u90fd\u662f\u4e00\u7fa4\u8822\u624d\uff0c\u4e0d\u8db3\u4e3a\u654c\u3002\u8fc7\u53bb\uff0c\u8001\u592b\u6709\u4e9b\u9ad8\u4f30\u4f60\u4eec\u4e86\uff0c\u6240\u4ee5\uff0c\u6211 \u4eec\u51b3\u5b9a\uff0c\u65e9\u4e00\u4e9b\u7ed3\u675f\u8fd9\u573a\u6e38\u620f\u2026\u2026\u201d \u3000\u3000\u62ac\u5934\u671b\u671b\u5929\u8272\uff0c\u9053\uff1a\u201c\u4e09\u66f4\u65f6\u5206\uff0c\u5929\u4eae\u4e4b\u524d\uff0c\u8fd9\u91cc\u5c06\u4f1a\u53d1\u751f\u4e00\u573a\u5de8\u53d8\uff0c\u626c\u5dde\u77e5\u5e9c\u738b\u5c11\u537f \u4eca\u591c\u5fc5\u6b7b\uff0c\u7531\u4f60\u4e2d\u3001\u897f\u3001\u5357\u3001\u5317\uff0c\u56db\u4f4d\u5927\u6355\u5934\u4e3a\u4ed6\u6b89\u846c\uff0c\u8fd9\u4efd\u8363\u8000\uff0c\u4e5f\u4e0d\u662f\u5e38\u4eba\u80fd\u5f97\uff0c\u738b\u5c11 \u537f\u4e5f\u662f\u6b7b\u800c\u65e0\u61be\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u8c01\u8bf4\u6211\u6b7b\u800c\u65e0\u61be\uff0c\u201d\u5728\u91cd\u91cd\u62a4\u536b\u4e4b\u4e0b\uff0c\u7a7f\u7740\u8859\u5f79\u88c5\u7684\u738b\u5c11\u537f\uff0c\u7a81\u7136\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u6211 \u9057\u61be\u7684\u5f88\u554a\uff01\u6211\u4e0d\u77e5\u9053\u4f60\u662f\u8c01\uff1f\u53ea\u77e5\u662f\u4e2a\u767d\u9aef\u5982\u4e91\u7684\u8001\u51f6\u624b\uff0c\u4e5f\u4e0d\u77e5\u4f60\u4eec\u53d7\u4f55\u4eba\u4e4b\u547d\u6765\u6740\u6211\uff0c \u4f46\u6211\u77e5\u9053\uff0c\u7edd\u975e\u51fa\u4e8e\u4f60\u4eec\u7684\u672c\u610f\u3002\u6211\u6b7b\u7684\u5982\u6b64\u6df7\u6dc6\u4e0d\u6e05\uff0c\u4e0d\u660e\u4e0d\u767d\uff0c\u5c82\u4e0d\u662f\u4e00\u5927\u61be\u4e8b\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e0d\u80fd\u602a\u5230\u8001\u592b\u7684\u5934\u4e0a\uff0c\u201d\u767d\u9aef\u8001\u4eba\u9053\uff1a\u201c\u51b0\u51bb\u4e09\u5c3a\uff0c\u5c82\u662f\u4e00\u65e5\u4e4b\u5bd2\uff1f\u4f60\u8fde\u8c01\u8981\u53d6\u4f60 \u6027\u547d\uff0c\u90fd\u61f5\u65e0\u6240\u77e5\uff0c\u8bf4\u4f60\u662f\u4e00\u4f4d\u80fd\u540f\uff0c\u672a\u514d\u6709\u4e9b\u8bbd\u523a\u4e86\u3002\u7b2c\u4e8c\u4e2a\u8981\u602a\u7684\u662f\u5211\u90e8\u603b\u6355\u5934\u548c\u773c\u4e0b \u7684\u56db\u4f4d\u5927\u6355\u5934\uff0c\u4ed6\u4eec\u53f7\u79f0\u529e\u6848\u80fd\u624b\uff0c\u5374\u67e5\u4e0d\u51fa\u4efb\u4f55\u7ebf\u7d22\uff0c\u6211\u5bf9\u4ed6\u4eec\u7684\u65e0\u80fd\u975e\u5e38\u5931\u671b\uff0c\u6240\u4ee5\uff0c \u4e0d\u613f\u518d\u548c\u4ed6\u4eec\u73a9\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u4eca\u591c\uff0c\u5c31\u8981\u7ed3\u675f\u8fd9\u4e2a\u6e38\u620f\uff0c\u4f60\u738b\u77e5\u5e9c\uff0c\u548c\u56db\u5927\u6355\u5934\uff0c\u4e5f\u90fd\u5c06\u5728\u4eca\u591c\u4e2d\u6b65\u5165\u6b7b\u4ea1\u3002\u5929\u4eae\u4e4b \u540e\uff0c\u662f\u53e6\u4e00\u4e2a\u65b0\u7684\u5f00\u62d3\uff0c\u626c\u5dde\u77e5\u5e9c\u6709\u65b0\u4eba\u53d6\u4ee3\uff0c\u56db\u5927\u6355\u5934\u4e5f\u5c06\u6210\u660e\u767d\u9ec4\u82b1\uff0c\u56db\u4e2a\u540d\u4eba\u4e0d\u5728\uff0c \u7559\u4e0b\u9752\u51a2\u5411\u9ec4\u660f\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u6ca1\u6709\u5f00\u53e3\uff0c\u9002\u624d\uff0c\u5bf9\u4e86\u4e24\u5251\uff0c\u5df2\u4f7f\u675c\u671b\u6708\u6709\u7740\u5b8c\u5168\u7684\u5931\u8d25\u611f\u89c9\uff0c\u4e5f\u65e0\u6cd5\u5728\u53e3\u820c\u4e0a \u901e\u5f3a\u4e86\uff0c\u52a8\u624b\u4e09\u4e94\u56de\u5408\uff0c\u5bf9\u65b9\u5c31\u6709\u53d6\u4ed6\u6027\u547d\u7684\u80fd\u529b\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u4e5f\u6709\u7740\u54d1\u53e3\u65e0\u8a00\u7684\u611f\u89c9\uff0c\u5408\u56db\u5927\u6355\u5934\u4e4b\u529b\uff0c\u8ba4\u4e0d\u51fa\u5bf9\u65b9\u8eab\u4efd\u3002\u540c\u65f6\uff0c\u4e5f\u770b\u51fa\u4e86\u675c \u671b\u6708\u88ab\u90a3\u4e24\u5251\u4ea4\u63a5\uff0c\u9707\u4f24\u5185\u8151\uff0c\u8fd9\u4e00\u9635\u8c03\u606f\uff0c\u662f\u5426\u5df2\u6062\u590d\u4e86\u518d\u6218\u7684\u80fd\u529b\uff0c\u8fd8\u65e0\u6cd5\u80af\u5b9a\u3002\u4ed6\u4e0d \u77e5\u5982\u4f55\u5f00\u53e3\uff0c\u8bf4\u4e9b\u4ec0\u4e48\uff1f \u3000\u3000\u56db\u5468\u5305\u56f4\u7684\u9ed1\u8863\u5251\u624b\uff0c\u5df2\u4eae\u51fa\u4e86\u957f\u5251\uff0c\u53ea\u770b\u90a3\u5305\u56f4\u5f62\u52bf\uff0c\u5df2\u77e5\u662f\u5168\u9762\u7684\u88ad\u6740\uff0c\u4e00\u53d1\u52a8\uff0c \u573a\u4e2d\u6240\u6709\u7684\u4eba\uff0c\u90fd\u5c06\u9762\u5bf9\u5251\u624b\u7684\u653b\u88ad\u3002\u5982\u4f55\u4e00\u79cd\u5e03\u7f72\uff0c\u80fd\u5728\u62d2\u5f53\u654c\u4eba\u5168\u9762\u653b\u52bf\u4e2d\uff0c\u53c8\u4fdd\u62a4\u4e86 \u738b\u5927\u4eba\u7684\u5b89\u5168\uff0c\u662f\u76ee\u4e0b\u56db\u5927\u6355\u5934\u5fc3\u4e2d\u6700\u7d27\u8981\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u201c\u56db\u4f4d\u5927\u6355\u5934\u65e2\u5df2\u8ba4\u660e\u4e86\u76ee\u4e0b\u60c5\u52bf\uff0c\u5f53\u77e5\u53cd\u6297\u65e0\u76ca\uff0c\u201d\u767d\u9aef\u8001\u4eba\u9053\uff1a\u201c\u56db\u4f4d\u5982\u5206\u5934\u9003\u547d\uff0c \u4e5f\u8bb8\u8fd8\u6709\u4e00\u4f4d\u80fd\u4fdd\u4f4f\u6027\u547d\u2026\u2026\u54c8\u54c8\uff0c\u8001\u592b\u4e4b\u8a00\u2026\u2026\u201d \u3000\u3000\u201c\u4f4f\u53e3\uff0c\u56db\u5927\u6355\u5934\uff0c\u5c82\u662f\u8d2a\u751f\u6015\u6b7b\u4e4b\u8f88\uff0c\u9601\u4e0b\u770b\u4eba\u5f53\u771f\u662f\u6709\u773c\u65e0\u73e0\u4e86\u3002\u201d\u60dc\u7389\u6276\u7740\u5c0f\u6587 \u7f13\u6b65\u7531\u4e8c\u5802\u4e2d\u884c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u767d\u9aef\u8001\u4eba\u7f13\u7f13\u5730\u628a\u76ee\u5149\u79fb\u6ce8\u5230\u60dc\u7389\u7684\u8138\u4e0a\uff0c\u8eab\u8eaf\u5fae\u5fae\u9707\u52a8\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u51b7\u9762\u795e\u533b\u8c2d\u6267\u4e2d\u7684\u9057\u5973\uff0c\u8c2d\u7389\u51e4\uff0c\u4eca\u540d\u60dc\u7389\u3002\u201d \u3000\u3000\u201c\u60dc\u7389\uff0c\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u7684\u827a\u540d\uff0c\u4e5f\u662f\u6587\u9601\u7684\u9152\u5973\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4e0e\u8001\u592b\u4f55\u5173\uff0c\u8001\u592b\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u56db\u51e4\u697c\u4e3b\uff0c\u201d\u60dc\u7389\u63a5\u9053\uff1a\u201c\u4e5f\u662f\u5148\u7236\u7684\u5927\u5e08\u5144\uff0c\u4e0d\u8001\u4e66\u751f\u547c\u5ef6\u8fdc\uff01\u795e\u533b\u4e2d\u4e00\u4ee3\u533b\u5b66\u5929 \u624d\u3001\u9ad8\u624b\uff0c\u4e5f\u662f\u65e0\u7236\u6700\u656c\u670d\u7684\u4eba\u2026\u2026\u201d \u3000\u3000\u767d\u9aef\u8001\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\u554a\uff01\u4f60\u5728\u968f\u53e3\u7f16\u6545\u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u672c\u6765\uff0c\u6211\u4e5f\u4e0d\u80fd\u80af\u5b9a\uff0c\u8001\u5b9e\u8bf4\uff0c\u5f53\u5e74\u6211\u8fd8\u5e74\u7eaa\u5c0f\uff0c\u513f\u65f6\u8bb0\u5fc6\u8fa8\u4f9d\u7a00\uff0c\u4f46\u4f60\u89c1\u5230\u6211\u65f6\uff0c \u8eab\u8eaf\u5fae\u5fae\u9707\u52a8\u4e00\u4e0b\u3002\u201d\u60dc\u7389\u9053\uff1a\u201c\u60f3\u662f\u8fd8\u6709\u513f\u65f6\u7684\u5370\u8c61\uff0c\u4e5f\u542f\u53d1\u4e86\u6211\u7684\u8bb0\u5fc6\u80fd\u529b\uff0c\u672c\u6765\u6a21\u7cca \u7684\u8bb0\u5fc6\uff0c\u4e5f\u9661\u7136\u6e05\u6670\u8d77\u6765\uff0c\u4f60\u58a8\u53d1\u3001\u96ea\u9aef\uff0c\u5374\u662f\u672c\u6765\u9762\u76ee\uff0c\u53ea\u795e\u533b\u95e8\u4e2d\u5206\u8001\u672f\uff0c\u5fc5\u987b\u85c9\u836f\u529b \u7684\u5e2e\u52a9\uff0c\u624d\u80fd\u628a\u8001\u8fc8\u5206\u5f00\u79fb\u7f6e\uff0c\u5927\u5e08\u4f2f\uff0c\u4f84\u5973\u53ef\u662f\u8bf4\u9519\u4e86\uff1f\u201d \u3000\u3000\u767d\u9aef\u8001\u4eba\u9053\uff1a\u201c\u4f60\u7239\u8fd8\u544a\u8bc9\u4e86\u4f60\u795e\u533b\u95e8\u4e2d\u591a\u5c11\u4e0d\u4f20\u4e4b\u79d8\uff1f\u201d \u3000\u3000\u201c\u6211\u7239\u77e5\u9053\u7684\uff0c\u6211\u90fd\u5df2\u77e5\u9053\uff0c\u5f53\u7136\u4e0d\u5982\u5927\u5e08\u4f2f\u90a3\u4e48\u6280\u827a\u7cbe\u6e5b\u3002\u201d\u60dc\u7389\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f84\u5973 \u6709\u4e00\u4ef6\u4e8b\uff0c\u8981\u5f53\u9762\u8bf7\u6559\uff0c\u4ee5\u5927\u5e08\u4f2f\u5a01\u671b\u3001\u8eab\u4efd\uff0c\u5b9a\u7136\u4e0d\u8c0e\u8a00\u6b3a\u7792\u4f84\u5973\u4e86\uff1f\u201d \u3000\u3000\u547c\u5ef6\u8fdc\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\uff01\u4f60\u8bf4\u5427\uff01\u201d \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u53e5\u8bdd\uff0c\u4e5f\u627f\u8ba4\u4e86\u4ed6\u5316\u8eab\u56db\u51e4\u697c\u4e3b\u7684\u8eab\u4efd\u3002 \u3000\u3000\u201c\u5148\u7236\u662f\u600e\u4e48\u6b7b\u7684\uff0c\u662f\u4e0d\u662f\u88ab\u5927\u5e08\u4f2f\u6349\u53bb\u5408\u836f\u4e86\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u8fd8\u671b\u5927\u5e08\u4f2f\u7ed9\u4f84\u5973\u4e00\u4e2a \u7b54\u590d\u3002\u201d \u3000\u3000\u547c\u5ef6\u8fdc\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u7239\u53ef\u662f\u628a\u5408\u836f\u795e\u672f\u4e5f\u4f20\u7ed9\u4f60\u4e86\uff1f\u201d \u3000\u3000\u201c\u5927\u5e08\u4f2f\u7684\u770b\u6cd5\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u90a3\u662f\u795e\u533b\u95e8\u7684\u7edd\u5bf9\u673a\u5bc6\uff0c\u4f60\u7239\u7adf\u4f20\u6388\u6b64\u672f\u7ed9\u4f60\u3002\u201d\u547c\u5ef6\u8fdc\u9053\uff1a\u201c\u8001\u592b\u62ff\u4ed6\u5408\u836f\uff0c\u5fc3\u4e2d \u8fd8\u6709\u4e00\u4efd\u6127\u759a\uff0c\u4f46\u73b0\u5728\u60f3\u6765\uff0c\u8001\u592b\u662f\u5fc3\u5b89\u7406\u5f97\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u7239\u6ca1\u6709\u4f20\u7ed9\u6211\u4ee5\u4eba\u5408\u836f\u4e4b\u672f\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u5927\u5e08\u4f2f\u51a4\u6789\u4e86\u6211\u7239\u2026\u2026\u201d \u3000\u3000\u547c\u5ef6\u8fdc\u63a5\u9053\uff1a\u201c\u4f60\u806a\u660e\u4f36\u4fd0\uff0c\u662f\u627f\u7ee7\u6211\u8863\u94b5\u7684\u4eba\u9009\uff0c\u4f46\u4f60\u77e5\u9053\u7684\u592a\u591a\u4e86\uff0c\u975e\u6b7b\u4e0d\u53ef\u3002\u4e2b \u5934\uff0c\u795e\u533b\u95e8\uff0c\u4e0d\u4f46\u662f\u533b\u672f\u5982\u795e\uff0c\u5251\u672f\u4e5f\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u8fd9\u5957\u5251\u6cd5\uff0c\u4e0d\u662f\u795e\u533b\u95e8\u6d41\u4f20\u4e0b\u6765\u7684\uff0c\u800c \u662f\u5927\u5e08\u4f2f\u82b1\u8d39\u4e86\u6570\u5341\u5e74\u7684\u5de5\u592b\uff0c\u96c6\u5929\u4e0b\u5251\u672f\u4e4b\u5927\u6210\uff0c\u521b\u51fa\u7684\u5947\u6280\u2026\u2026\u201d \u3000\u3000\u81ea\u73b0\u8eab\u5c31\u672b\u5f00\u53e3\u7684\u5c0f\u6587\uff0c\u7a81\u7136\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u62fe\u4eba\u7259\u6167\uff0c\u5c31\u8c08\u4e0d\u4e0a\u5947\u6280\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u6e29\u6545\u77e5\u65b0\uff0c\u8001\u592b\u628a\u5251\u672f\u4e2d\u6700\u6076\u6bd2\u3001\u6700\u51cc\u5389\u7684\u5251\u62db\u7ec4\u5408\u8d77\u6765\uff0c\u521b\u51fa\u5341\u4e09\u62db\u6740\u4eba\u5251\u6cd5\uff0c\u53eb \u4f5c\u5fc5\u6740\u5341\u4e09\u5251\uff0c\u201d\u547c\u5ef6\u8fdc\u9053\uff1a\u201c\u6ca1\u6709\u4eba\u80fd\u9003\u8fc7\u8fd9\u5341\u4e09\u62db\u5251\u6cd5\u7684\u8ffd\u6740\u2026\u2026\u201d \u3000\u3000\u201c\u5e94\u8be5\u53eb\u6742\u788e\u5341\u4e09\u5251\uff0c\u201d\u5c0f\u6587\u9053\uff1a\u201c\u5168\u662f\u5077\u4eba\u5bb6\u7684\u4e1c\u897f\uff0c\u59d1\u5a18\u4eca\u591c\u8ba9\u4f60\u4eec\u5f00\u5f00\u773c\u754c\uff0c\u89c1 \u8bc6\u4e00\u4e0b\u5251\u6cd5\u4e4b\u6700\u3002\u201d \u3000\u3000\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u4eba\u4e5f\u7f13\u6b65\u5411\u524d\u79fb\u52a8\uff0c\u884c\u51fa\u4e86\u4e00\u4e08\u591a\u8fdc\uff0c\u624d\u505c\u4e0b\u811a\u6b65\uff0c\u5251\u4e5f\u51fa\u4e86\u9798\uff0c\u659c\u6307\u53f3\u4e0a \u65b9\uff0c\u548c\u4e00\u822c\u5251\u6cd5\uff0c\u5927\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\u90fd\u662f\u8fbe\u4eba\uff0c\u4e89\u5343\u79cb\uff0c\u4e0d\u4e89\u4e00\u65f6\uff0c\u7acb\u65f6\u5411\u5c0f\u6587\u8eab\u540e\u96c6\u4e2d\uff0c\u7f29\u5c0f\u4e86\u5bf9\u654c\u7684\u6b63\u9762\uff0c\u5bf9 \u738b\u5c11\u537f\u7684\u4fdd\u62a4\uff0c\u4e5f\u5c31\u66f4\u4e3a\u4e25\u5bc6\u4e86\u3002 \u3000\u3000\u547c\u5ef6\u8fdc\u626b\u63a0\u4e86\u4e00\u773c\u5e03\u5b88\u5728\u56db\u5468\u7684\u9ed1\u8863\u5251\u624b\uff0c\u90fd\u5df2\u6a2a\u5251\u800c\u7acb\uff0c\u5c31\u7b49\u7740\u547d\u4ee4\u51fa\u624b\u4e86\u3002\u4f30\u7b97\u5f62 \u52bf\uff0c\u867d\u5df2\u7a0d\u6709\u53d8\u5316\uff0c\u4f46\u5927\u4f53\u800c\u8a00\uff0c\u4ecd\u5728\u4ed6\u5e03\u5c40\u7684\u63a7\u5236\u8303\u56f4\u4e4b\u5185\uff0c\u800c\u4e14\u8fb0\u5149\u8fd8\u65e9\uff0c\u4e5f\u4e0d\u6025\u4e8e\u51fa \u624b\uff0c\u4e24\u9053\u76ee\u5149\uff0c\u8f6c\u6ce8\u5230\u5c0f\u6587\u7684\u8eab\u4e0a\uff0c\u5f88\u4ed4\u7ec6\u5730\u770b\u5c06\u8d77\u6765\u3002\u5c0f\u6587\u5f88\u6c89\u7740\uff0c\u6446\u51fa\u5e94\u53d8\u7684\u59ff\u6001\u540e\uff0c \u5c31\u4efb\u51ed\u5bf9\u65b9\u6253\u91cf\u4e86\u3002 \u3000\u3000\u5979\u660e\u767d\uff0c\u4e89\u53d6\u4e00\u4e9b\u65f6\u95f4\uff0c\u5bf9\u56db\u5927\u6355\u5934\u5f88\u6709\u5229\uff0c\u8ba9\u4ed6\u4eec\u8c03\u6574\u4e00\u4e2a\u65b0\u7684\u8fce\u654c\u9635\u52bf\u3002\u5c0f\u6587\u4e5f\u5728 \u8fd9\u4e34\u9635\u7684\u65f6\u523b\uff0c\u601d\u7d22\u5982\u4f55\u62d2\u6321\u56db\u9762\u516b\u65b9\u4e00\u9f50\u5377\u88ad\u8fc7\u6765\u7684\u653b\u52bf\uff0c\u5251\u6cd5\u7684\u6269\u5f20\u6781\u9650\uff0c\u80fd\u62d2\u5b88\u591a\u5927 \u4e00\u4e2a\u6b63\u9762\uff0c\u5feb\u901f\u7684\u6740\u654c\u624b\u6cd5\uff0c\u9700\u591a\u5c11\u65f6\u95f4\u624d\u80fd\u628a\u654c\u4eba\u653b\u51fb\u529b\u91cf\u6d88\u9664\u3002 \u3000\u3000\u547c\u5ef6\u8fdc\u770b\u4e86\u53c8\u770b\uff0c\u5728\u706b\u628a\u7684\u7167\u5c04\u4e4b\u4e0b\uff0c\u628a\u5c0f\u6587\u59d1\u5a18\u770b\u7684\u5341\u5206\u6e05\u695a\u4e86\u3002 \u3000\u3000\u4ed6\u662f\u533b\u5b66\u5927\u5bb6\uff0c\u89c2\u5bdf\u7684\u80fd\u529b\uff0c\u975e\u5e38\u4eba\u80fd\u53ca\uff0c\u53ea\u53ef\u60dc\u53cc\u65b9\u7684\u8ddd\u79bb\u8fdc\u4e86\u4e00\u4e9b\uff0c\u53c8\u4e0d\u80fd\u95fb\u3001\u95ee\u3001 \u5207\uff0c\u53ea\u80fd\u4ee5\u4e00\u4e2a\u671b\u5b57\u53d6\u51b3\uff0c\u4f46\u4ecd\u7136\u88ab\u4ed6\u770b\u51fa\u4e86\u4e00\u70b9\u95e8\u9053\u3002 \u3000\u3000\u5c0f\u6587\u4e60\u7684\u662f\u4e00\u79cd\u6781\u9ad8\u7684\u5185\u529f\uff0c\u5728\u548c\u4eba\u52a8\u624b\u65f6\uff0c\u5185\u5bb6\u5143\u6c14\uff0c\u4ea6\u5728\u8fd0\u8f6c\u4e0d\u606f\uff0c\u53ef\u4e45\u6218\u4e0d\u75b2\uff0c \u5982\u60f3\u7528\u4e00\u9635\u5f3a\u731b\u7684\u8fde\u73af\u653b\u52bf\uff0c\u8feb\u4f7f\u5979\u6c14\u529b\u5c3d\u7edd\uff0c\u5c06\u4f1a\u5927\u5931\u6240\u671b\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u518d\u4e5f\u77a7\u4e0d\u51fa\u5c0f\u6587 \u59d1\u5a18\u6709\u4ec0\u4e48\u7279\u6b8a\u4e4b\u5904\uff0c\u767d\u767d\u7684\u76ae\u80a4\uff0c\u79c0\u4e3d\u7684\u8f6e\u5ed3\uff0c\u5f2f\u6708\u7709\u513f\uff0c\u6768\u67f3\u8170\uff0c\u662f\u4e2a\u60f9\u4eba\u7231\u601c\u7684\u5c0f\u59d1 \u5a18\uff0c\u624b\u4e2d\u7684\u5b9d\u5251\uff0c\u4f3c\u4e3a\u7279\u5236\uff0c\u6bd4\u4e00\u822c\u7684\u5b9d\u5251\u7a0d\u7a0d\u77ed\u4e86\u4e24\u5bf8\u3002 \u3000\u3000\u547c\u5ef6\u8fdc\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e45\u95fb\u7a0b\u5c0f\u8776\u8eab\u4fa7\u6709\u4e24\u4e2a\u7f8e\u4e3d\u7684\u5973\u5a62\uff0c\u4f60\u59d1\u5a18\u60f3\u662f\u5176\u4e2d\u4e4b\u4e00 \u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u5e08\u4f2f\uff0c\u4f60\u5931\u8a00\u4e86\uff0c\u201d\u60dc\u7389\u9053\uff1a\u201c\u5979\u4eec\u662f\u6bd4\u53bf\u957f\u8fd8\u8981\u5927\u4e24\u7ea7\u7684\u5211\u6355\u53f8\u5e26\u5200\u6355\u5feb\uff0c\u4e0d\u662f \u5a62\u5973\u3001\u4e2b\u5934\u3002\u201d \u3000\u3000\u60dc\u7389\u59d1\u5a18\u597d\u4fee\u517b\uff0c\u9762\u5bf9\u7740\u8ffd\u89c5\u591a\u5e74\u7684\u6740\u7236\u4ec7\u4eba\uff0c\u4ecd\u7136\u80fd\u4fdd\u6301\u9547\u9759\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u53c8\u4eae\u8d77\u4e86\u4e00\u652f\u706b\u628a\u3002 \u3000\u3000\u7d27\u63a5\u7740\u706b\u5149\u95ea\u52a8\uff0c\u4e00\u8fde\u4eae\u8d77\u4e86\u5341\u51e0\u652f\u706b\u628a\u3002 \u3000\u3000\u8fd9\u4e8c\u5802\u56db\u5468\uff0c\u9a7b\u5b88\u4e86\u6570\u5341\u540d\u8859\u5f79\u3001\u6355\u5feb\uff0c\u4f46\u675c\u671b\u6708\u8ba9\u4ed6\u4eec\u5b88\u5019\u5f85\u547d\uff0c\u4e0d\u51c6\u5377\u5165\u4e8c\u5802\u7684\u9632 \u5b88\u57cb\u4f0f\u4e4b\u4e2d\u3002 \u3000\u3000\u8fd9\u4e9b\u8859\u5f79\u3001\u6355\u5feb\uff0c\u4e5f\u660e\u767d\u8fd9\u6b21\u662f\u7b2c\u4e00\u6d41\u7684\u9ad8\u624b\u5bf9\u51b3\uff0c\u672c\u4eba\u6280\u827a\u4e0d\u5165\u6d41\uff0c\u52a0\u5165\u8fdb\u6765\u53ea\u662f\u767d \u9001\u6027\u547d\uff0c\u4e0e\u4e8b\u65e0\u8865\uff0c\u800c\u4e14\uff0c\u8fd8\u4f1a\u59a8\u788d\u5230\u56db\u5927\u6355\u5934\u7684\u884c\u52a8\u3002 \u3000\u3000\u5927\u96f7\u96e8\u540e\u4e00\u9635\u60ca\u6270\uff0c\u8fd9\u4e9b\u4eba\u5168\u60ca\u9192\u4e86\uff0c\u9690\u4f0f\u6697\u5f71\u56db\u5468\uff0c\u9ed8\u5bdf\u53d8\u5316\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u4e0d\u80fd\u53c2\u4e0e\u51b3\u6218\uff0c\u4f46\u71c3\u706f\u3001\u70b9\u4eae\u3001\u63d2\u706b\u628a\uff0c\u5374\u662f\u5185\u884c\u7684\u5f88\u3002\u7247\u523b\u4e4b\u95f4\uff0c\u5341\u51e0\u652f\u5927\u706b \u628a\uff0c\u904d\u5e03\u5728\u4e8c\u5802\u4fa7\u95e8\u5916\u9762\u7684\u9662\u843d\u4e2d\uff0c\u706b\u5149\u718a\u718a\uff0c\u628a\u4e8c\u5802\u4fa7\u95e8\u5916\u7684\u4e00\u5927\u7247\u7a7a\u5730\uff0c\u7167\u5f97\u4e00\u7247\u901a\u660e\u3002 \u3000\u3000\u706b\u628a\u63d2\u5728\u5730\u4e0a\uff0c\u4e5f\u6709\u4e9b\u7ed1\u5728\u6811\u4e0a\uff0c\u4eba\u5374\u9690\u5165\u6697\u5f71\u4e2d\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u56db\u51e4\u697c\u4e3b\u547c\u5ef6\u8fdc\u7a81\u7136\u653e\u58f0\u4e00\u9635\u5927\u7b11\uff0c\u9053\uff1a\u201c\u672c\u662f\u6697\u591c\u88ad\u6740\uff0c\u73b0\u5728\uff0c\u5374\u53d8\u6210\u660e\u706b\u6267\u4ed7\u7684\u62da \u6218\u4e86\uff0c\u4e8b\u60c5\u7684\u8f6c\u53d8\uff0c\u7adf\u662f\u5982\u6b64\u7684\u96be\u4ee5\u9884\u6599\u3002\u201d \u3000\u3000\u201c\u5927\u5e08\u4f2f\u6df1\u5f97\u795e\u533b\u95e8\u7684\u771f\u4f20\uff0c\u533b\u672f\u3001\u6b66\u529f\uff0c\u90fd\u5df2\u5165\u5316\u5883\uff0c\u540d\u6210\u5229\u5c31\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e0d\u6ee1\u8db3 \u7684\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u7518\u4f5c\u6740\u624b\uff0c\u8ba1\u4ef7\u53d6\u547d\uff0c\u8d5a\u53d6\u8fd9\u4e9b\u8840\u8165\u94b1\u5462\uff1f\u201d \u3000\u3000\u201c\u597d\u53ef\u7231\u7684\u4e2b\u5934\uff0c\u5206\u6790\u4e8b\u7269\uff0c\u4ea6\u80fd\u8a00\u4e4b\u6210\u7406\uff0c\u53ea\u53ef\u60dc\u4f60\u548c\u5927\u5e08\u4f2f\u6ca1\u6709\u7f18\u4efd\u3002\u201d\u62ac\u5934\u770b\u770b \u5929\u8272\uff0c\u8bed\u58f0\u7a81\u7136\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u65f6\u8fb0\u5df2\u5230\uff0c\u4f60\u4eec\u8fd9\u4e9b\u4eba\uff0c\u90fd\u4e0d\u80fd\u770b\u5230\u660e\u5929\u7684\u592a\u9633\uff0c\u7ed9\u6211\u6740\uff01\u201d \u3000\u3000\u5c0f\u6587\u65e9\u5df2\u6697\u4e2d\u6570\u8ba1\uff0c\u5206\u5e03\u5728\u56db\u5468\u7684\u9ed1\u8863\u5251\u624b\uff0c\u4e00\u5171\u662f\u5341\u4e00\u4e2a\u3002\u4e3a\u4ec0\u4e48\u52a8\u5458\u4e86\u5341\u4e00\u4e2a\u56f4\u6740 \u5251\u624b\uff0c\u5c0f\u6587\u6ca1\u6709\u7ec6\u60f3\uff0c\u6218\u573a\u4e0a\u60c5\u52bf\u591a\u53d8\uff0c\u5c0f\u6587\u4e5f\u65e0\u6687\u53bb\u60f3\uff0c\u60f3\u7684\u662f\u4ec0\u4e48\u6837\u4e00\u62db\u5251\u6cd5\uff0c\u624d\u80fd\u628a \u653b\u51fb\u7684\u654c\u4eba\u62e6\u591a\u4e00\u4e9b\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\u4e5f\u90fd\u5168\u795e\u51dd\u6ce8\uff0c\u51c6\u5907\u8fce\u6218\u5f3a\u654c\u3002\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u7684\u91cd\u70b9\uff0c\u662f\u4e24\u7ffc\u4fa7\u9762\uff0c\u5c0f\u6587\u6b66\u529f\u518d \u597d\uff0c\u4e5f\u65e0\u6cd5\u4e00\u5251\u5c01\u4f4f\u4e09\u9762\u654c\u52bf\uff0c\u4e24\u7ffc\u5c31\u6210\u4e86\u6700\u5927\u7684\u6f0f\u6d1e\u4e86\u3002 \u3000\u3000\u60dc\u7389\u7b97\u662f\u7b2c\u4e00\u6b21\u6b63\u5f0f\u5730\u51c6\u5907\u548c\u4eba\u52a8\u624b\uff0c\u5c31\u9047\u4e0a\u5982\u6b64\u91cd\u5927\u7684\u573a\u9762\uff0c\u5fc3\u4e2d\u65e2\u7d27\u5f20\u3001\u53c8\u5174\u594b\uff0c \u521d\u751f\u4e4b\u728a\u4e0d\u754f\u864e\uff0c\u5fc3\u4e2d\u6ca1\u6709\u53bb\u60f3\u80dc\u8d25\u7684\u4e8b\uff0c\u60f3\u7684\u662f\u5982\u4f55\u5728\u8fd9\u4e00\u6218\u4e2d\uff0c\u5c3d\u51fa\u516b\u5b9e\uff0c\u514b\u654c\u81f4\u80dc\u3002 \u3000\u3000\u5979\u7ad9\u5728\u5c0f\u6587\u7684\u5de6\u4fa7\uff0c\u76f8\u8ddd\u4e94\u5c3a\uff0c\u53ea\u89c1\u5979\u53cc\u624b\u7d27\u63e1\uff0c\u5374\u4e0d\u77e5\u9053\u5979\u624b\u4e2d\u6293\u7684\u4ec0\u4e48\u4e1c\u897f\u3002 \u3000\u3000\u795e\u533b\u95e8\u7684\u8be1\u5947\u533b\u672f\uff0c\u51b7\u9762\u795e\u533b\u8c2d\u6267\u4e2d\u5728\u6c5f\u6e56\u9053\u4e0a\u7684\u5a01\u671b\uff0c\u518d\u52a0\u4e0a\u4eca\u591c\u7684\u9547\u9759\u8868\u73b0\uff0c\u4e00\u4e0b \u5b50\u628a\u60dc\u7389\u59d1\u5a18\uff0c\u4e5f\u62ab\u4e0a\u4e86\u4e00\u5c42\u795e\u79d8\u5916\u8863\u3002 \u3000\u3000\u738b\u77e5\u5e9c\u53ea\u611f\u89c9\u4e00\u79cd\u7d27\u5f20\u7684\u6c14\u6c1b\uff0c\u5728\u7a7a\u6c14\u4e2d\u6563\u64ad\uff0c\u4eba\u4e5f\u4e0d\u81ea\u89c9\u5730\u7d27\u5f20\u8d77\u6765\u3002\u706b\u628a\u7684\u8000\u7167\u4e0b\uff0c \u7a81\u7136\u95ea\u8d77\u4e86\u8fde\u4e32\u98de\u8679\uff0c\u9ed1\u8863\u6740\u624b\u5c55\u5f00\u4e86\u88ad\u6740\u7684\u884c\u52a8\uff0c\u5341\u4e00\u9053\u95ea\u52a8\u7684\u5251\u5149\uff0c\u5206\u5411\u738b\u5c11\u537f\u548c\u56db\u5927 \u6355\u5934\u98de\u5c04\u800c\u6765\u3002 \u3000\u3000\u5c0f\u6587\u98de\u8eab\u800c\u8d77\uff0c\u8fce\u4e86\u4e0a\u53bb\u3002\u624b\u4e2d\u5b9d\u5251\u753b\u51fa\u4e00\u9053\u5149\u7130\uff0c\u6709\u5982\u6d41\u661f\uff0c\u4e00\u5251\u62e6\u4f4f\u4e86\u4e03\u4e2a\u9ed1\u8863\u5251 \u624b\uff0c\u5251\u4e0a\u7684\u5149\u7130\uff0c\u62d6\u51fa\u4e86\u4e00\u4e08\u591a\u957f\u3002 \u3000\u3000\u4f46\u4ecd\u6709\u56db\u4e2a\u4eba\u907f\u5f00\u5c0f\u6587\u7684\u62e6\u622a\uff0c\u5206\u7531\u4e24\u4fa7\uff0c\u6478\u5411\u738b\u5c11\u537f\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\u4e5f\u5206\u6210\u4e24\u8def\uff0c\u8fce\u5411\u56db\u4e2a\u5251\u624b\u3002 \u3000\u3000\u706b\u628a\u8000\u7167\u4e0b\uff0c\u53ea\u89c1\u5230\u56db\u5927\u6355\u5934\u548c\u56db\u4e2a\u5251\u624b\u5728\u7a7a\u4e2d\u649e\u5728\u4e86\u4e00\u8d77\uff0c\u4e5f\u770b\u5230\u5175\u5203\u95ea\u52a8\u7684\u51b7\u8292\u76f8 \u4e92\u649e\u51fb\uff0c\u8033\u9645\u95f4\u4e5f\u542c\u5230\u91d1\u94c1\u649e\u51fb\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4f46\u770b\u5f97\u7a0d\u4e3a\u6e05\u695a\u4e00\u4e9b\uff0c\u662f\u4e24\u4e2a\u5b88\u5728\u738b\u5c11\u537f\u8eab\u4fa7\u7684\u6b66\u5f53\u5251\u5ba2\uff0c\u77f3\u742a\u3001\u5f20\u9e9f\u3002\u4ed6\u4eec\u770b\u5230\u53cc\u65b9 \u624b\u4e2d\u7684\u5175\u5203\uff0c\u5728\u7a7a\u4e2d\u4ea4\u63a5\u4e86\u6570\u6b21\uff0c\u7a81\u7136\u5206\u5f00\u843d\u7740\u4e86\u5b9e\u5730\u3002 \u3000\u3000\u770b\u7684\u53ea\u662f\u7a0d\u4e3a\u6e05\u695a\u4e00\u4e9b\uff0c\u5e76\u4e0d\u662f\u5f88\u6e05\u695a\uff0c\u76f4\u5230\u53cc\u65b9\u843d\u7740\u5b9e\u5730\uff0c\u4e24\u4eba\u624d\u770b\u51fa\u56db\u5927\u6355\u5934\u53d7\u4e86 \u4f24\u3002 \u3000\u3000\u770b\u4e0d\u5230\u4f24\u5728\u4f55\u5904\uff0c\u4f46\u56db\u5927\u6355\u5934\u7684\u8eab\u4e0a\uff0c\u90fd\u6709\u9c9c\u8840\u4e0d\u505c\u5730\u6ef4\u4e0b\u6765\u3002 \u3000\u3000\u4e0d\u505c\u5730\u6d41\u51fa\u9c9c\u8840\uff0c\u90a3\u8bf4\u660e\u56db\u5927\u6355\u5934\u90fd\u4f24\u7684\u4e0d\u8f7b\u3002 \u3000\u3000\u4f46\u56db\u4eba\u5168\u4e0d\u987e\u4f24\u52bf\u7684\u6076\u5316\uff0c\u65e0\u4eba\u5305\u624e\u6b62\u8840\uff0c\u624b\u4e2d\u6a2a\u7740\u5175\u5203\uff0c\u4e00\u526f\u575a\u51b7\u5982\u94c1\u7684\u795e\u8272\u3002 \u3000\u3000\u60dc\u7389\u7684\u773c\u4e2d\u6d41\u51fa\u6cea\u6c34\uff0c\u592b\u90ce\u8840\u6d12\u6218\u573a\uff0c\u5c82\u80fd\u65e0\u52a8\u4e8e\u8877\uff0c\u4f46\u5979\u4e0d\u6562\u4e3a\u675c\u671b\u6708\u5305\u624e\u4f24\u52bf\uff0c\u751a \u81f3\u4e0d\u6562\u60ca\u6270\u4e8e\u4ed6\u3002\u5979\u7cbe\u4e8e\u533b\u672f\uff0c\u4e5f\u660e\u767d\u56db\u5927\u6355\u5934\u51dd\u805a\u5168\u8eab\u529f\u529b\u4e0d\u6563\uff0c\u662f\u51c6\u5907\u8fce\u63a5\u56db\u4e2a\u5251\u624b\u518d \u5ea6\u653b\u51fb\u3002 \u3000\u3000\u5982\u82e5\u6b64\u523b\u60ca\u6270\u5230\u4ed6\u4eec\uff0c\u4ed6\u4eec\u6050\u65e0\u518d\u4e00\u6b21\u51dd\u805a\u529f\u529b\u7684\u4f53\u80fd\u4e86\u3002 \u3000\u3000\u8c01\u6562\u5728\u8fd9\u5b58\u4ea1\u5173\u952e\u7684\u65f6\u523b\uff0c\u5192\u6b64\u5927\u9669\uff0c\u60dc\u7389\u4e5f\u4e0d\u6562\u3002\u53ea\u6709\u628a\u5e0c\u671b\u5bc4\u6258\u5728\u5c0f\u6587\u8eab\u4e0a\u4e86\u3002 \u3000\u3000------------- \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u5341\u4e03\u7ae0 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5c0f\u6587\u6b63\u5728\u548c\u4e03\u5927\u5251\u624b\uff0c\u5c55\u5f00\u640f\u547d\u7684\u6740\u622e\uff0c\u5c0f\u6587\u5251\u5982\u9b3c\u9b45\u7684\u5316\u8eab\uff0c\u7531\u7edd\u4e0d\u53ef\u80fd\u51fa \u5251\u7684\u89d2\u5ea6\uff0c\u653b\u51fa\u4f24\u654c\u3002 \u3000\u3000\u4f46\u5fc5\u6740\u5341\u4e09\u5251\uff0c\u7684\u786e\u4e5f\u662f\u5251\u6cd5\u4e2d\u6bd2\u5b66\u5947\u62db\uff0c\u5c0f\u6587\u5df2\u6740\u6b7b\u56db\u4e2a\u9ed1\u8863\u5251\u624b\uff0c\u4f46\u5979\u4e5f\u8eab\u4e2d\u56db\u5251\uff0c \u9c9c\u8840\u62ab\u6d12\uff0c\u4f3c\u5df2\u5f71\u54cd\u5230\u5979\u51fa\u5251\u901f\u5ea6\u548c\u53d8\u5316\uff0c\u4f46\u5979\u4ecd\u5728\u548c\u4e09\u4e2a\u9ed1\u8863\u5251\u624b\u93d6\u6218\u3002 \u3000\u3000\u8fd9\u7b49\u5251\u5feb\u5982\u95ea\u7535\uff0c\u9c9c\u8840\u6dcb\u6f13\u7684\u6740\u6cd5\uff0c\u770b\u5f97\u4eba\u76f4\u5192\u51b7\u6c57\u4e86\u3002 \u3000\u3000\u738b\u5927\u4eba\u770b\u7684\u76ee\u4e0d\u8f6c\u775b\uff0c\u4f46\u8eab\u4e0a\u5192\u51fa\u7684\u51b7\u6c57\uff0c\u5374\u5df2\u6e7f\u900f\u4e86\u8863\u88e4\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u6ca1\u6709\u770b\u5f97\u5f88\u6e05\u695a\uff0c\u5bd2\u8292\u5165\u8089\uff0c\u51b7\u5203\u65ad\u9aa8\uff0c\u4ed6\u770b\u4e0d\u6e05\u695a\uff0c\u53ea\u89c1\u5230\u4e00\u4e2a\u7ed3\u679c\uff0c\u4f46 \u90a3\u5df2\u7ecf\u4f7f\u4ed6\u624b\u8db3\u51b0\u51b7\uff0c\u5fc3\u8df3\u52a0\u901f\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u4e00\u67c4\u957f\u5251\uff0c\u523a\u5165\u4e86\u5c0f\u6587\u7684\u5de6\u80a9\uff0c\u5251\u5c16\u7531\u524d\u80a9\u80db\uff0c\u76f4\u900f\u540e\u80a9\uff0c\u9c9c\u8840\u968f\u7740\u900f\u7a7f\u7684\u5251\u5c16\uff0c \u9556\u5c04\u51fa\u8001\u8fdc\u3002 \u3000\u3000\u4f46\u5c0f\u6587\u7684\u4e00\u5251\u6a2a\u5207\uff0c\u65a9\u4e0b\u4e86\u4e09\u9897\u4eba\u5934\uff0c\u4eba\u5934\u88ab\u6fc0\u5c04\u7684\u9c9c\u8840\u51b2\u98de\u8d77\u4e03\u516b\u5c3a\u9ad8\uff0c\u98de\u843d\u5728\u4e08\u4f59 \u5f00\u5916\u3002 \u3000\u3000\u597d\u51cc\u5389\u7684\u4e00\u5251\uff0c\u597d\u60b2\u60e8\u7684\u5c60\u622e\u3002 \u3000\u3000\u4f46\u5c0f\u6587\u4e24\u9053\u51b7\u5389\u7684\u76ee\u5149\uff0c\u5374\u8f6c\u6ce8\u5230\u547c\u5ef6\u8fdc\u7684\u8eab\u4e0a\uff0c\u67f3\u8170\u4e00\u633a\uff0c\u4eba\u5251\u6b32\u98de\u3002\u56db\u51e4\u697c\u4e3b\u7a81\u7136 \u5927\u559d\u4e00\u58f0\uff1a\u201c\u5251\u6d77\u6d74\u9b42\u3002\u201d\u8f6c\u8eab\u75be\u5954\u800c\u53bb\u3002 \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u5251\u624b\uff0c\u7d27\u968f\u8eab\u540e\uff0c\u53bb\u5982\u98de\u9e1f\uff0c\u4e00\u77ac\u95f4\uff0c\u6d88\u5931\u4e8e\u591c\u7a7a\u4e4b\u4e2d\u3002\u6562\u60c5\u4ed6\u8ba4\u51fa\u4e86\u8fd9\u5957\u6740 \u622e\u6050\u6016\u7684\u5251\u6cd5\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\uff0c\u7a81\u7136\u5401\u4e00\u53e3\u6c14\uff0c\u8dcc\u5750\u5728\u5b9e\u5730\u4e0a\u3002 \u3000\u3000\u77f3\u742a\u3001\u5f20\u9e9f\uff0c\u548c\u9690\u5728\u6697\u5f71\u4e2d\u7684\u8859\u5f79\uff0c\u5168\u90fd\u8dd1\u4e86\u51fa\u6765\uff0c\u6276\u8d77\u56db\u5927\u6355\u5934\uff0c\u66ff\u4ed6\u4eec\u88f9\u4f24\u5305\u624e\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\uff0c\u90fd\u6709\u4e24\u5904\u5251\u4f24\uff0c\u4f46\u5374\u975e\u8981\u5bb3\uff0c\u53ea\u662f\u53d7\u4f24\u540e\uff0c\u6ca1\u6709\u5305\u624e\uff0c\u5931\u8840\u5f88\u591a\uff0c\u771f\u6c14\u5df2\u6563\uff0c \u90fd\u6709\u4e9b\u840e\u9761\u4e0d\u632f\u4e86\u3002 \u3000\u3000\u738b\u5927\u4eba\u4e5f\u8dd1\u4e86\u8fc7\u6765\uff0c\u4eca\u591c\u4e4b\u6218\uff0c\u5168\u662f\u9ad8\u624b\u5bf9\u51b3\uff0c\u8859\u5f79\u6355\u5feb\uff0c\u65e0\u4e00\u4eba\u53d7\u5230\u4f24\u5bb3\uff0c\u8fd9\u4e9b\u4eba\u56fa \u7136\u5fc3\u5b58\u611f\u6fc0\uff0c\u738b\u5927\u4eba\u4e5f\u611f\u52a8\u83ab\u540d\u3002\u56db\u5927\u6355\u5934\uff0c\u4e0d\u4f46\u6b66\u529f\u9ad8\u5f3a\uff0c\u673a\u667a\u8fc7\u4eba\uff0c\u800c\u4e14\u90fd\u6709\u7740\u4e00\u9897\u820d \u5df1\u4e3a\u4eba\u7684\u5fc3\uff0c\u5bf9\u4ed6\u738b\u5927\u4eba\u7684\u4fdd\u62a4\uff0c\u66f4\u662f\u4e0d\u60dc\u4ee5\u8eab\u76f8\u6b89\u3002 \u3000\u3000\u675c\u671b\u6708\u4e00\u7741\u773c\uff0c\u5c31\u53d1\u89c9\u4e86\u8eab\u4e0a\u5e26\u5251\u3001\u9c9c\u8840\u6dcb\u6dcb\u7684\u5c0f\u6587\u7ad9\u5728\u9762\u524d\uff0c\u4e24\u9053\u5e26\u7740\u51f6\u5149\u7684\u773c\u775b\uff0c \u76ef\u6ce8\u5728\u738b\u5927\u4eba\u8eab\u4e0a\uff0c\u4f3c\u4e4e\u53c8\u5df2\u5931\u53bb\u7406\u6027\u2026\u2026\u3002 \u3000\u3000\u8fd9\u662f\u975e\u5e38\u5371\u9669\u7684\u4e00\u523b\uff0c\u5f3a\u654c\u9000\u53bb\uff0c\u5374\u9762\u4e34\u4e86\u53e6\u4e00\u4e2a\u66f4\u5927\u5371\u9669\uff0c\u5982\u662f\u738b\u5927\u4eba\u53d7\u5230\u4f24\u5bb3\uff0c\u8fd9 \u4e00\u5207\u7684\u52aa\u529b\uff0c\u90fd\u663e\u5f97\u6beb\u65e0\u610f\u4e49\u4e86\u3002 \u3000\u3000\u5c0f\u6587\u7684\u4f24\u52bf\u5904\u5904\uff0c\u8840\u900f\u8863\u886b\uff0c\u8eab\u4e0a\u8fd8\u624e\u7740\u4e00\u628a\u5251\uff0c\u770b\u4e0a\u53bb\uff0c\u53ef\u6015\u6781\u4e86\u3002 \u3000\u3000\u4f46\u66f4\u53ef\u6015\u7684\u662f\u5979\u4e24\u9053\u51f6\u72e0\u7684\u76ee\u5149\uff0c\u7adf\u7136\u4f7f\u4eba\u9000\u907f\u4e09\u820d\uff0c\u65e0\u4eba\u6562\u8fd1\u5979\u4e4b\u8eab\uff0c\u66ff\u5979\u5305\u88f9\u4f24\u53e3\u3002 \u3000\u3000\u60dc\u7389\u6084\u7136\u884c\u4e86\u8fc7\u6765\uff0c\u95ea\u5728\u5c0f\u6587\u8eab\u540e\u4e94\u5c3a\u4e4b\u5904\uff0c\u90a3\u662f\u53ef\u4ee5\u907f\u5f00\u5c0f\u6587\u6325\u5251\u4e00\u51fb\u7684\u8ddd\u79bb\uff0c\u505c\u8eab \u5904\uff0c\u5df2\u907f\u5f00\u4e86\u5c0f\u6587\u7684\u89c6\u7ebf\u3002 \u3000\u3000\u738b\u5c11\u537f\u4e5f\u53d1\u89c9\u4e86\uff0c\u5c0f\u6587\u76ee\u5149\u4e2d\u900f\u51fa\u6068\u610f\uff0c\u4e0d\u7981\u5fc3\u795e\u9707\u98a4\uff0c\u4ed6\u770b\u5230\u4e86\u5c0f\u6587\u51fa\u5251\u7684\u5feb\u901f\uff0c\u53ea \u8981\u5979\u6740\u673a\u4e00\u52a8\uff0c\u7acb\u523b\u5c06\u8111\u888b\u642c\u5bb6\u3002 \u3000\u3000\u5e78\u597d\uff0c\u60dc\u7389\u51fa\u624b\u4e86\uff0c\u5c48\u6307\u4e00\u5f39\uff0c\u4e00\u70b9\u767d\u70df\uff0c\u88ad\u5411\u5c0f\u6587\u3002 \u3000\u3000\u90a3\u662f\u4e00\u79cd\u5f3a\u70c8\u7684\u8ff7\u9b42\u836f\u7269\uff0c\u5c0f\u6587\u95fb\u5230\u4e00\u80a1\u5f02\u9999\uff0c\u7acb\u523b\u5411\u5730\u4e0a\u5012\u53bb\u3002 \u3000\u3000\u60dc\u7389\u884c\u52a8\u5982\u98ce\uff0c\u4e00\u628a\u6293\u4f4f\u4e86\u5c0f\u6587\u7684\u8eab\u8eaf\uff0c\u987a\u52bf\u62d4\u51fa\u5979\u7a7f\u5728\u80a9\u80db\u4e0a\u7684\u957f\u5251\uff0c\u6295\u7f6e\u4e8e\u5730\u3002 \u3000\u3000\u56db\u4e2a\u8859\u5f79\u8dd1\u8fc7\u6765\uff0c\u4e00\u9f50\u52a8\u624b\uff0c\u66ff\u5c0f\u6587\u59d1\u5a18\u5305\u624e\u4f24\u52bf\uff0c\u4f46\u5374\u88ab\u60dc\u7389\u62e6\u4f4f\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u62ac \u4e00\u5f20\u8f6f\u69bb\u6765\uff0c\u4e00\u5757\u6728\u677f\u4e5f\u884c\uff0c\u6253\u4e24\u76c6\u70ed\u6c34\uff0c\u5979\u4f24\u5904\u592a\u591a\uff0c\u4f24\u7684\u4e5f\u91cd\uff0c\u8ba9\u6211\u66ff\u5979\u6577\u836f\u88f9\u4f24\u3002\u201d \u3000\u3000\u628a\u5c0f\u6587\u62ac\u5165\u4e86\u5367\u5ba4\uff0c\u60dc\u7389\u624d\u4ed4\u7ec6\u5730\u66ff\u5979\u6577\u836f\u548c\u5305\u624e\u4f24\u52bf\u3002 \u3000\u3000\u738b\u5927\u4eba\u9759\u9759\u5730\u7ad9\u5728\u6728\u69bb\u524d\u9762\uff0c\u76f4\u5f85\u60dc\u7389\u6599\u7406\u597d\u5c0f\u6587\u4f24\u52bf\uff0c\u624d\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8c2d\u59d1\u5a18\uff0c\u5c0f\u6587 \u59d1\u5a18\u4e0d\u4f1a\u6b8b\u5e9f\u5427\uff1f\u201d \u3000\u3000\u60dc\u7389\u6d41\u4e0b\u6cea\u6765\uff0c\u9053\uff1a\u201c\u5979\u5982\u4e0d\u6025\u4e8e\u6c42\u529f\uff0c\u4e0d\u4f1a\u53d7\u8fd9\u4e48\u591a\u4f24\uff0c\u53ef\u662f\u4e3a\u4e86\u6551\u6211\u4eec\uff0c\u624d\u5192\u9669\u8e81 \u8fdb\uff0c\u5fcd\u6b8b\u8eab\u8eaf\u4e4b\u82e6\uff0c\u53ea\u6c42\u901f\u6740\u654c\u4eba\u2026\u2026\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u201d\u738b\u5927\u4eba\u4e5f\u6d41\u4e0b\u4e86\u6cea\u6c34\uff0c\u9053\uff1a\u201c\u5c0f\u6587\u59d1\u5a18\u6b8b\u5e9f\u4e86\uff1f\u4f60\u8981\u60f3\u529e\u6cd5\u6551\u5979\u5440\uff01\u8fd9\u4e48\u4e2a \u52c7\u6b66\u7684\u5c0f\u59d1\u5a18\uff0c\u5982\u4f55\u80fd\u8ba9\u5979\u6b8b\u5e9f\uff0c\u6211\u4eec\u8981\u6551\u5979\uff0c\u4f60\u8bf4\uff0c\u8981\u4ec0\u4e48\u6837\u7684\u836f\u7269\uff1f\u2026\u2026\u201d \u3000\u3000\u60dc\u7389\u62ed\u53bb\u8138\u4e0a\u6cea\u6c34\uff0c\u63a5\u9053\uff1a\u201c\u5927\u4eba\u8bda\u5fc3\uff0c\u53ef\u611f\u5929\u5730\uff0c\u6211\u4f1a\u5168\u529b\u533b\u6cbb\u5979\uff0c\u5c0f\u6587\u59d0\u5728\u5927\u4eba\u7684 \u8bda\u610f\u7948\u7977\u4e0b\uff0c\u6216\u53ef\u4fdd\u4f4f\u8eab\u8eaf\u4e0d\u6b8b\u3002\u201d \u3000\u3000\u201c\u5bf9\u5bf9\u5bf9\uff0c\u6211\u8981\u8bda\u5fc3\u7948\u7977\uff0c\u62dc\u4f5b\u6c42\u795e\u90fd\u884c\u3002\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u53ea\u8981\u80fd\u4fdd\u4f51\u5c0f\u6587\u59d1\u5a18\u65e0\u4e8b\uff0c \u4e0b\u5b98\uff0c\u6211\u53ef\u4ee5\u658b\u6212\u4e09\u6708\uff0c\u4e0d\u52a8\u8364\u8165\u3002\u201d \u3000\u3000\u60dc\u7389\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5927\u4eba\uff0c\u4f60\u662f\u4e0d\u662f\u5f88\u559c\u6b22\u6211\u7684\u5c0f\u6587\u59d0\u59d0\u4e86\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u201d\u738b\u5c11\u537f\u9053\uff1a\u201c\u4e0b\u6b21\u89c1\u5230\u7a0b\u603b\u6355\u5934\u65f6\uff0c\u4e0b\u5b98\u8981\u6c42\u5979\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u60dc\u7389\u5403\u4e86\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u7cdf\u5566\uff01\u8981\u6c42\u5c0f\u6587\u59d0\u59d0\u4f5c\u5c0f\uff0c\u5c82\u4e0d\u662f\u8981\u603b\u6355\u5934\u4e3a\u96be\u4e48\uff1f\u5fc3\u4e2d\u8e66\u8e66\u8df3\uff0c \u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u6c42\u4ec0\u4e48\u5440\uff01\u201d \u3000\u3000\u201c\u6c42\u5979\u5141\u51c6\u628a\u5c0f\u6587\u59d1\u5a18\u8ba4\u6211\u819d\u4e0b\uff0c\u4f5c\u4e3a\u4e49\u5973\uff0c\u4e0b\u5b98\u5c31\u53ef\u4ee5\u597d\u597d\u7167\u987e\u5979\u4e86\u3002\u201d \u3000\u3000\u60dc\u7389\u5401\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u6211\u4eec\u51fa\u53bb\u5427\uff01\u8ba9\u5979\u597d\u597d\u5730\u4f11\u606f\uff0c\u6700\u597d\u80fd\u6d3e\u4e2a\u4e2b\u5934\u6765\u8fd9\u91cc \u7167\u987e\u5979\u3002\u201d \u3000\u3000\u201c\u4e0b\u5b98\u8fd9\u5c31\u53bb\u529e\u3002\u201d\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u60dc\u7389\u5954\u5165\u5ead\u9662\u4e2d\uff0c\u56db\u5927\u6355\u5934\uff0c\u90fd\u5df2\u5305\u597d\u4f24\u52bf\uff0c\u6b63\u5728\u76d8\u5750\u8c03\u606f\u3002 \u3000\u3000\u706b\u628a\u5df2\u5927\u90e8\u7184\u53bb\uff0c\u4f46\u6570\u5341\u4e2a\u8859\u5f79\u3001\u6355\u5feb\uff0c\u90fd\u81ea\u52a8\u5730\u6563\u5e03\u5728\u56db\u5468\u8b66\u6212\u3002 \u3000\u3000\u626c\u5dde\u5e9c\u8fde\u756a\u906d\u53d7\u88ad\u51fb\uff0c\u663e\u7136\uff0c\u738b\u77e5\u5e9c\u5e76\u672a\u5f20\u626c\uff0c\u4f46\u6d88\u606f\u4ecd\u7136\u4f20\u626c\u51fa\u53bb\u3002 \u3000\u3000\u6c34\u5e08\u63d0\u7763\u9a6c\u957f\u5c71\uff0c\u4eb2\u6765\u6170\u95ee\uff0c\u63d0\u8bae\u6d3e\u9063\u4e09\u767e\u540d\u7cbe\u9510\u9a81\u5200\u624b\uff0c\u9a7b\u624e\u5728\u626c\u5dde\u5e9c\uff0c\u4ee5\u4fdd\u5b89\u5168\u3002 \u4f46\u738b\u5c11\u537f\u5374\u5a49\u62d2\u597d\u610f\uff0c\u800c\u4e14\u6084\u7136\u5730\u544a\u8bc9\u9a6c\u957f\u5c71\uff0c\u8981\u4ee5\u8eab\u4f5c\u9975\uff0c\u8bf1\u4f7f\u51f6\u624b\u51fa\u73b0\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u6709\u70b9\u611f\u52a8\uff0c\u7adf\u7136\u81ea\u52a8\u677e\u53e3\uff0c\u7f09\u51f6\u9650\u671f\uff0c\u53ef\u4ee5\u5c55\u5ef6\uff0c\u4e5f\u5bf9\u738b\u77e5\u5e9c\u98ce\u9aa8\u3001\u6c14\u5ea6\uff0c\u6697\u4e3a \u5fc3\u6298\u3002 \u3000\u3000\u94c1\u7fce\u4e5f\u5306\u5306\u8d76\u56de\u4e86\u5e9c\u8859\uff0c\u5e26\u6765\u4e86\u91d1\u767e\u5e74\u7684\u70ed\u8877\u5173\u5fc3\uff0c\u4e5f\u5e26\u6765\u4e86\u91d1\u767e\u5e74\u7684\u5177\u4f53\u7684\u652f\u63f4\u3002 \u3000\u3000\u94c1\u7fce\u5e26\u4e86\u5feb\u5200\u738b\u526a\u3001\u94c1\u62f3\u4e25\u65b9\uff0c\u800c\u4e14\uff0c\u53e6\u7531\u8d3e\u82f1\u7edf\u7387\u7684\u4e00\u6279\u7cbe\u9510\u4eba\u624b\uff0c\u968f\u65f6\u5f85\u547d\uff0c\u53ea\u8981 \u4e00\u89c1\u5230\u4fe1\u53f7\uff0c\u7acb\u65f6\u8d76\u6765\u652f\u63f4\u3002 \u3000\u3000\u867d\u7136\u5e9c\u8859\u9047\u88ad\u7684\u4e8b\uff0c\u5df2\u4f20\u626c\u5f00\u53bb\uff0c\u4f46\u8be6\u7ec6\u7684\u60c5\u5f62\uff0c\u5374\u662f\u5f88\u5c11\u4eba\u77e5\u6653\uff0c\u94c1\u7fce\u4f1a\u89c1\u8fc7\u56db\u5927\u6355 \u5934\uff0c\u624d\u53d1\u89c9\u4e8b\u6001\u4e25\u91cd\u5230\u5b8c\u5168\u51fa\u4e4e\u4e86\u4ed6\u7684\u610f\u6599\u3002 \u3000\u3000\u4f46\u675c\u671b\u6708\u575a\u6301\u4fdd\u5bc6\uff0c\u4e0d\u8ba9\u94c1\u7fce\u628a\u771f\u76f8\u5ba3\u626c\u51fa\u53bb\uff0c\u8fde\u738b\u526a\u3001\u4e25\u65b9\u4e5f\u8981\u5b88\u5bc6\u3002\u56e0\u4e3a\uff0c\u8fd9\u4e24\u4e2a \u4eba\u77e5\u9053\u4e86\u5185\u60c5\uff0c\u7acb\u523b\u4f1a\u900f\u9732\u7ed9\u8d3e\u82f1\u77e5\u6653\u3002 \u3000\u3000\u94c1\u7fce\u4e5f\u662f\u8001\u6c5f\u6e56\u4e86\uff0c\u7acb\u523b\u9886\u609f\u5230\uff0c\u8d3e\u82f1\u8eab\u4efd\u8be1\u79d8\uff0c\u8fd8\u662f\u4e2a\u53ef\u7591\u7684\u4eba\u7269\u3002 \u3000\u3000\u53bb\u63a2\u89c6\u91cd\u4f24\u5367\u5e8a\u7684\u738b\u575a\u548c\u4f55\u5927\u5149\u4e4b\u540e\uff0c\u4e86\u89e3\u4e86\u66f4\u591a\u7684\u8be6\u60c5\uff0c\u4e5f\u610f\u8bc6\u5230\u6740\u624b\u96c6\u56e2\u4e4b\u5f3a\u3002 \u3000\u3000\u4e0d\u4f46\u626c\u5dde\u5e9c\u7684\u5b9e\u529b\u96be\u671b\u9879\u80cc\uff0c\u5c31\u7b97\u52a0\u4e0a\u56db\u5927\u6355\u5934\uff0c\u4e5f\u96be\u627c\u4f4f\u5bf9\u65b9\u7684\u6c14\u7130\u3002\u4e24\u4eba\u4e5f\u540c\u65f6\u5411 \u94c1\u7fce\u63d0\u51fa\u8f9e\u6218\u8981\u6c42\uff0c\u94c1\u7fce\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u5f53\u9762\u5141\u51c6\u3002 \u3000\u3000\u4ece\u5df2\u7ecf\u4e86\u89e3\u5230\u7684\u60c5\u51b5\u770b\uff0c\u738b\u3001\u4f55\u4e8c\u4eba\u5c31\u7b97\u5e76\u672b\u53d7\u4f24\uff0c\u5728\u5373\u5c06\u5f00\u59cb\u7684\u65b0\u4e00\u8f6e\u8840\u6218\u4e2d\uff0c\u5df2\u5b9e \u5728\u6d3e\u4e0d\u4e0a\u592a\u5927\u7684\u7528\u573a\uff0c\u5c31\u8bf4\u94c1\u7fce\u81ea\u5df1\u4e5f\u5fc3\u77e5\u4ece\u6b66\u529f\u4e0a\u672c\u8bf4\uff0c\u8fdc\u8fdc\u4e0d\u662f\u90a3\u4e9b\u9ad8\u7ea7\u6740\u624b\u7684\u654c\u624b\u3002 \u3000\u3000\u73b0\u5728\uff0c\u53ea\u80fd\u5c06\u5e0c\u671b\u653e\u5728\u5211\u90e8\u603b\u6355\u5934\u7a0b\u5c0f\u8776\u8eab\u4e0a\u4e86\uff0c\u5982\u679c\u5979\u80fd\u8bbe\u6cd5\u5c3d\u5feb\u67e5\u51fa\u6740\u624b\u96c6\u56e2\u7684\u4e3b \u8c0b\uff0c\u5e76\u5c3d\u8d77\u5211\u90e8\u603b\u6355\u53f8\u53ca\u4e2d\u3001\u897f\u3001\u5357\u3001\u4e1c\u56db\u5927\u5206\u53f8\u6240\u8f96\u7cbe\u9510\u529b\u91cf\uff0c\u6216\u53ef\u4e0e\u6740\u624b\u96c6\u56e2\u51b3\u4e00\u96c4\u96cc\u3002 \u3000\u3000\u4f46\u603b\u6355\u5934\u7a0b\u5c0f\u8776\u81ea\u4e24\u5929\u524d\u4e0e\u5c0f\u96c5\u4e00\u8d77\u6613\u5bb9\u79bb\u5f00\u5e9c\u8859\u540e\uff0c\u4e00\u76f4\u5230\u4eca\u5929\u8fd8\u672a\u8fd4\u56de\uff0c\u4e5f\u4e0d\u77e5\u662f \u53d1\u73b0\u4e86\u6740\u624b\u96c6\u56e2\u4e3b\u8c0b\u4e4b\u4eba\u7684\u7ebf\u7d22\uff0c\u5c31\u6b64\u8ffd\u67e5\u4e0b\u53bb\u4e86\uff0c\u8fd8\u662f\u9047\u4e0a\u4e86\u9669\u60c5\uff0c\u88ab\u56f0\u96be\u4ee5\u8131\u8eab\uff0c\u751a\u81f3 \u5df2\u7ecf\u4ee5\u8eab\u6b89\u804c\u4e86\u3002 \u3000\u3000\u65e0\u8bba\u5982\u4f55\uff0c\u4e24\u5929\u591a\u65f6\u95f4\u6ca1\u6709\u534a\u70b9\u6d88\u606f\u4f20\u56de\uff0c\u603b\u662f\u4e0d\u592a\u6b63\u5e38\u7684\u60c5\u51b5\u3002 \u3000\u3000\u94c1\u7fce\u672c\u60f3\u4eb2\u81ea\u5230\u57ce\u4e2d\u56db\u5904\u6253\u63a2\u4e00\u4e0b\uff0c\u770b\u80fd\u4e0d\u80fd\u627e\u51fa\u603b\u6355\u5934\u53ef\u80fd\u7559\u4e0b\u7684\u4fe1\u53f7\uff0c\u4f46\u4e00\u6765\u4ed6\u5bf9 \u603b\u6355\u5934\u4e0e\u56db\u5927\u6355\u5934\u4e4b\u95f4\u7684\u8054\u7cfb\u65b9\u6cd5\u5e76\u4e0d\u6e05\u695a\uff0c\u4e8c\u6765\u53c8\u5fc3\u7cfb\u5e9c\u8859\u7684\u9632\u536b\uff0c\u8131\u4e0d\u5f00\u8eab\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\u53d7\u4f24\uff0c\u5c0f\u6587\u91cd\u4f24\uff0c\u5e9c\u8859\u5185\u7684\u9632\u536b\u529b\u91cf\u5b9e\u5728\u5df2\u5f88\u5355\u8584\uff0c\u867d\u8bf4\u91d1\u5e9c\u6d3e\u6765\u4e86\u94c1\u62f3\u4e25\u65b9\uff0c \u5feb\u5200\u738b\u526a\u4e8c\u4eba\uff0c\u52a0\u4e0a\u5e9c\u4e2d\u539f\u6709\u7684\u6b66\u5f53\u5f1f\u5b50\u77f3\u742a\u3001\u5f20\u9e9f\uff0c\u94c1\u7fce\u8fd8\u662f\u5f88\u4e0d\u653e\u5fc3\u3002 \u3000\u3000\u201c\u8fd9\u4e9b\u6740\u624b\u5b9e\u5728\u592a\u53ef\u6076\u3001\u592a\u51f6\u6b8b\uff0c\u4e5f\u592a\u53ef\u6068\u4e86\uff01\u738b\u5927\u4eba\u8fd9\u6837\u4e00\u4e2a\u597d\u5b98\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e00\u5b9a \u8981\u5c06\u4ed6\u7f6e\u4e8e\u6b7b\u4ed6\u800c\u540e\u5feb\u5462\uff01\u201d\u94c1\u7fce\u60f3\u4e0d\u901a\u3002\u4ed6\u53ea\u611f\u89c9\u5230\u8981\u4fdd\u62a4\u738b\u5927\u4eba\uff0c\u5e76\u4e14\u4e0d\u80fd\u8ba9\u626c\u5dde\u8fd9\u5ea7 \u7e41\u534e\u540d\u57ce\u6bc1\u4e8e\u8fd9\u4e9b\u51f6\u5f92\u4e4b\u624b\uff0c\u8fd9\u526f\u62c5\u5b50\u5b9e\u5728\u662f\u592a\u91cd\u4e86\u3002\u4ed6\u94c1\u7fce\u6839\u672c\u625b\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u80fd\u625b\u8d77\u8fd9\u526f\u62c5\u5b50\u7684\uff0c\u73b0\u5728\u770b\u8d77\u6765\u53ea\u6709\u7a0b\u5c0f\u8776\u3002 \u3000\u3000\u53ef\u7a0b\u5c0f\u8776\u5230\u5e95\u5230\u54ea\u91cc\u53bb\u4e86\u5462\uff1f \u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1 \u3000\u3000\u7a0b\u5c0f\u8776\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u73b0\u5728\u8eab\u5904\u4f55\u5904\uff0c\u4e0d\u8fc7\u5979\u76f8\u4fe1\uff0c\u5f88\u5feb\u5979\u5c31\u4f1a\u5f04\u6e05\u695a\u4e86\u3002\u8fd9\u662f\u4e00\u95f4 \u4e0d\u7b97\u5927\u7684\u623f\u95f4\uff0c\u5f88\u6e05\u9759\uff0c\u5e03\u7f6e\u7684\u4e5f\u5f88\u96c5\u81f4\uff0c\u4e00\u5f20\u7d2b\u6a80\u6728\u7684\u5706\u684c\u6446\u5728\u623f\u95f4\u6b63\u4e2d\uff0c\u684c\u8fb9\u6709\u56db\u628a\u96d5 \u5de5\u7cbe\u7ec6\u7684\u7d2b\u6a80\u6728\u6905\uff0c\u9760\u897f\u6446\u7740\u4e00\u5f20\u96d5\u82b1\u5927\u5e8a\uff0c\u78a7\u7f57\u7eb1\u5e10\u8f7b\u5782\u5e73\u62d6\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5750\u5728\u5e8a\u8fb9\uff0c\u770b\u7740\u5140\u81ea\u5728\u5e8a\u4e0a\u660f\u7761\u4e0d\u9192\u7684\u5c0f\u96c5\uff0c\u4e0d\u7981\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u4f38\u51fa\u4e00\u53cc\u767d\u7389\u822c \u5254\u900f\u7684\u5c0f\u624b\uff0c\u63e1\u4f4f\u5c0f\u96c5\u7684\u5de6\u53f3\u8155\u8109\uff0c\u5168\u529b\u8fd0\u52a8\uff0c\u4e00\u80a1\u5f3a\u52b2\u7684\u5185\u6c14\u76f4\u900f\u5c0f\u96c5\u7684\u4f53\u5185\u3002 \u3000\u3000\u5c0f\u96c5\u6162\u6162\u7741\u5f00\u773c\u775b\uff0c\u8ff7\u8ff7\u7cca\u7cca\u5730\u770b\u7740\u7a0b\u5c0f\u8776\uff0c\u5ffd\u7136\u4e00\u633a\u8eab\u5750\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u54b1\u4eec \u8fd9\u662f\u5728\u54ea\u513f\uff1f\u662f\u5728\u5e9c\u8859\u91cc\u5417\uff1f\u5c0f\u59d0\u4ec0\u4e48\u65f6\u5019\u5e26\u6211\u56de\u6765\u7684\uff1f\u90a3\u4e2a\u8499\u9762\u4eba\u5462\uff1f\u662f\u88ab\u5c0f\u59d0\u6740\u4e86\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u6709\u4e9b\u65e0\u5948\u5730\u9053\uff1a\u201c\u5c0f\u96c5\uff0c\u4f60\u7684\u8001\u6bdb\u75c5\u603b\u4e5f\u6539\u4e0d\u4e86\uff0c\u4e00\u4e0b\u5b50\u95ee\u8fd9\u4e48\u591a\u95ee\u9898\uff0c \u8ba9\u6211\u600e\u4e48\u56de\u7b54\uff1f\u201d \u3000\u3000\u5c0f\u96c5\u4e5f\u89c9\u5f97\u597d\u7b11\u4e86\uff0c\u5410\u4e86\u5410\u820c\u5934\uff0c\u9053\uff1a\u201c\u662f\u5c0f\u96c5\u4e0d\u5bf9\uff0c\u5c0f\u59d0\uff0c\u4f60\u5148\u544a\u8bc9\u6211\uff0c\u6211\u4eec\u8fd9\u662f\u5728 \u54ea\u513f\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5c0f\u96c5\u5446\u4f4f\u4e86\uff0c\u6014\u6014\u5730\u770b\u7740\u7a0b\u5c0f\u8776\uff0c\u5403\u5403\u5730\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4f60\u7684\u610f\u601d\u662f\u2026\u2026\u201d\u7a0b\u5c0f\u8776\u53c8\u53f9\u4e86 \u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53d1\u751f\u7684\u4e8b\uff0c\u4f60\u8fd8\u8bb0\u5f97\u5417\uff1f\u201d \u3000\u3000\u5c0f\u96c5\u7728\u4e86\u7728\u773c\u775b\uff0c\u8138\u4e0a\u6162\u6162\u6d6e\u8d77\u53c8\u60ca\u6012\u53c8\u6050\u614c\u7684\u795e\u60c5\u6765\u3002 \u3000\u3000\u5979\u60f3\u8d77\u6765\u4e86\uff0c\u56e0\u4e3a\u6240\u53d1\u751f\u7684\u4e8b\u7559\u7ed9\u5979\u7684\u5370\u8c61\u592a\u6df1\u4e86\uff0c\u5979\u53c8\u600e\u4e48\u4f1a\u5fd8\u8bb0\u5462\uff1f\u521a\u4e00\u9192\u6765\u65f6\uff0c \u5979\u6ca1\u6709\u7acb\u523b\u60f3\u8d77\u6765\uff0c\u662f\u56e0\u4e3a\u5979\u6240\u4e2d\u7684\u8ff7\u9999\u836f\u6027\u592a\u5f3a\u70c8\u4e86\u3002 \u3000\u3000\u4e24\u5929\u524d\u53d1\u751f\u7684\u4e00\u5207\u53c8\u6e05\u6670\u5730\u6d6e\u8d77\u5728\u5979\u7684\u773c\u524d\u3002 \u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1 \u3000\u3000\u90a3\u5929\u7a0b\u5c0f\u8776\u548c\u5c0f\u96c5\u6613\u5bb9\u6539\u5986\u6210\u4e00\u4f4d\u4e2d\u5e74\u4eba\u548c\u4e00\u4f4d\u5c11\u5e74\u4ec6\u4ece\uff0c\u81ea\u5e9c\u8859\u4fa7\u95e8\u6e9c\u51fa\u6765\u540e\uff0c\u6162\u6162 \u5730\u5f80\u56db\u51e4\u697c\u65b9\u5411\u8d70\u53bb\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7684\u672c\u610f\u5c31\u662f\u5148\u719f\u6089\u4e00\u4e0b\u626c\u5dde\u57ce\u7684\u5f62\u52bf\u3001\u9053\u8def\uff0c\u6240\u4ee5\u5979\u4eec\u5e76\u672a\u76f4\u63a5\u53bb\u56db\u51e4\u697c\uff0c\u800c\u662f \u5728\u5927\u8857\u5c0f\u5df7\u56db\u5904\u968f\u610f\u5730\u6f2b\u6b65\u3002 \u3000\u3000\u8fd9\u4e0b\u5c0f\u96c5\u53ef\u9ad8\u5174\u5566\uff01 \u3000\u3000\u5c0f\u59d1\u5a18\u867d\u662f\u65e9\u5df2\u542c\u8bf4\u8fc7\u626c\u5dde\u7684\u70ed\u95f9\u3001\u7e41\u534e\u3001\u5bcc\u8d35\u6e29\u67d4\u666f\u50cf\uff0c\u4f46\u8033\u95fb\u4e0d\u5982\u773c\u89c1\u3002\u518d\u8bf4\uff0c\u4eac \u57ce\u4e5f\u662f\u7e41\u534e\u4e4b\u5730\uff0c\u5728\u5c0f\u96c5\u59d1\u5a18\u5fc3\u4e2d\u60f3\u6765\uff0c\u626c\u5dde\u518d\u7e41\u534e\uff0c\u53c8\u771f\u80fd\u6bd4\u5f97\u8fc7\u4eac\u57ce\u53bb\uff1f \u3000\u3000\u4eca\u5929\u4eb2\u773c\u6240\u89c1\uff0c\u5c0f\u59d1\u5a18\u624d\u7b97\u662f\u771f\u771f\u88ab\u626c\u5dde\u57ce\u7684\u666f\u50cf\u6240\u6298\u670d\uff0c\u867d\u8bf4\u5f97\u6c14\u6d3e\u4e4b\u5927\uff0c\u3000\u626c\u5dde\u4e0d \u5982\u4eac\u57ce\uff0c\u4f46\u7e41\u534e\u4e4b\u6001\uff0c\u6bd4\u4e4b\u4eac\u57ce\u53ef\u8c13\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d\u53ca\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u8def\u6162\u6162\u884c\u6765\uff0c\u4e00\u76f4\u5f88\u7ec6\u5fc3\u5730\u582a\u67e5\u7740\u6240\u7ecf\u4e4b\u5904\u7684\u5730\u52bf\uff0c\u66f4\u662f\u7559\u5fc3\u662f\u5426\u6709\u4eba\u5728\u6697\u4e2d \u8ddf\u8e2a\u3002 \u3000\u3000\u4f46\u5c0f\u96c5\u53ef\u5c31\u4e0d\u4e00\u6837\u4e86\uff0c\u5979\u4e00\u53cc\u773c\u775b\u5de6\u770b\u53f3\u770b\u4e00\u76f4\u6ca1\u95f2\u7740\uff0c\u8fd8\u76f4\u6068\u7239\u5988\u4e3a\u4ec0\u4e48\u53ea\u7ed9\u5979\u751f\u4e86 \u4e24\u53ea\u773c\u775b\uff0c\u8ba9\u5979\u5f88\u6709\u4e00\u79cd\u76ee\u4e0d\u6687\u63a5\u4e4b\u61be\u3002 \u3000\u3000\u4f60\u770b\u5979\uff0c\u4e00\u4f1a\u513f\u8dd1\u5230\u4e2a\u7ef8\u7f0e\u5e84\u524d\u770b\u4e0a\u51e0\u773c\uff0c\u4e00\u4f1a\u53c8\u6328\u8fd1\u4e2a\u8102\u7c89\u80ed\u8102\u94fa\u5b50\u731b\u770b\u4e00\u6c14\uff0c\u89c1\u4e86 \u4e2a\u5356\u82b1\u7684\u5c0f\u59d1\u5a18\uff0c\u53c8\u5fcd\u4e0d\u4f4f\u4e70\u4e0a\u4e00\u675f\u9c9c\u82b1\uff0c\u634f\u5728\u624b\u4e2d\u4e0d\u4f4f\u5730\u73a9\u3002\u5230\u540e\u6765\uff0c\u7a0b\u5c0f\u8776\u4e0d\u5f97\u4e0d\u5f88\u8ba4 \u771f\u5730\u544a\u8beb\u5979\uff0c\u4e0d\u8981\u5fd8\u4e86\u81ea\u5df1\u73b0\u5728\u7684\u88c5\u626e\u662f\u4e2a\u5c11\u5e74\u4ec6\u4ece\u7684\u6253\u626e\u3002\u4e5f\u96be\u602a\uff0c\u5c0f\u96c5\u867d\u8bf4\u5b98\u53f8\u4e94\u54c1\u5e26 \u5200\u5211\u6355\uff0c\u4f46\u5230\u5e95\u662f\u4e2a\u5c11\u5973\uff0c\u53c8\u6709\u54ea\u4e2a\u5c11\u5973\u4e0d\u559c\u6b22\u4e2a\u82b1\u82b1\u8349\u8349\u3001\u80ed\u8102\u7ef8\u7f0e\u5462\uff1f \u3000\u3000\u8d70\u8fc7\u4e00\u5ea7\u77f3\u62f1\u6865\u65f6\uff0c\u7a0b\u5c0f\u8776\u5ffd\u7136\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u96c5\uff0c\u6211\u4eec\u88ab\u4eba\u8ddf\u8e2a\u4e86\uff01\u201d \u3000\u3000\u5c0f\u96c5\u4e00\u60ca\uff0c\u6b63\u60f3\u8f6c\u5934\u53bb\u770b\uff0c\u7a0b\u5c0f\u8776\u5df2\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u56de\u5934\uff0c\u4e5f\u4e0d\u8981\u5de6\u987e\u53f3\u76fc\uff0c\u90a3\u4e24\u4e2a\u4eba\u5c31 \u5728\u6211\u4eec\u8eab\u540e\u4e0d\u8fdc\u5904\uff0c\u662f\u4e24\u4e2a\u8eab\u7a7f\u9752\u8272\u957f\u888d\u7684\u4e2d\u5e74\u4eba\u3002\u201d \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u6211\u4eec\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u65ad\u7136\u9053\uff1a\u201c\u8fc7\u4e86\u6865\uff0c\u5411\u53f3\u8f6c\uff0c\u4e00\u76f4\u5411\u524d\u8d70\u3002\u201d \u3000\u3000\u5c0f\u96c5\u98de\u5feb\u5730\u5411\u53f3\u8fb9\u770b\u4e86\u4e00\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u90a3\u8fb9\u770b\u8d77\u6765\u597d\u50cf\u5f88\u51b7\u6e05\uff0c\u54b1\u4eec\u4e3a\u4ec0\u4e48\u4e0d \u5f80\u4eba\u591a\u5904\u8d70\uff0c\u8bbe\u6cd5\u7529\u5f00\u4ed6\u4eec\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u8ddf\u8e2a\u6211\u4eec\uff0c\u6211\u8fd8\u60f3\u4ece\u4ed6\u4eec\u8eab\u4e0a\u627e\u51fa\u70b9\u7ebf\u7d22\u6765\u5462\uff01\u201d\u5c0f\u96c5\u4e0d\u89c9 \u5174\u594b\u8d77\u6765\uff0c\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u8d70\u8fc7\u77f3\u62f1\u6865\uff0c\u7a0b\u5c0f\u8776\u548c\u5c0f\u96c5\u7a81\u7136\u52a0\u5feb\u4e86\u811a\u6b65\u3002\u4e0d\u7528\u56de\u5934\u770b\u5c31\u77e5\u9053\uff0c\u5979\u4eec\u8eab\u540e\u90a3\u4e24\u540d\u7a7f\u7740 \u957f\u888d\u7684\u4e2d\u5e74\u4eba\u4e5f\u52a0\u5feb\u4e86\u6b65\u4f10\u3002 \u3000\u3000\u8d8a\u5f80\u524d\u8d70\uff0c\u679c\u7136\u8d8a\u662f\u4eba\u8ff9\u7a00\u5c11\uff0c\u518d\u5f80\u524d\uff0c\u8def\u8fb9\u6709\u4e00\u6bb5\u5df2\u7ecf\u6709\u4e9b\u6b8b\u7834\u7684\u56f4\u5899\uff0c\u4f3c\u4e4e\u662f\u4e00\u5904 \u5e9f\u5f03\u5df2\u4e45\u7684\u56ed\u6797\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u62c9\u5c0f\u96c5\u7684\u8863\u8896\uff0c\u9053\uff1a\u201c\u8fdb\u56ed\u5b50\u91cc\u53bb\uff01\u201d\u4fe9\u4eba\u4e00\u95ea\u8eab\uff0c\u5df2\u81ea\u4e00\u5904\u7f3a\u53e3\u5904\u63a0\u8fc7\u56f4\u5899\u3002 \u3000\u3000\u5899\u5185\u7684\u6742\u8349\u8db3\u6709\u534a\u4eba\u9ad8\uff0c\u4e8c\u4eba\u65b9\u4e00\u843d\u811a\uff0c\u7acb\u5373\u6a2a\u5411\u8dc3\u51fa\u4e00\u4e08\u6709\u4f59\uff0c\u4f0f\u8eab\u9690\u8fdb\u6742\u8349\u4e1b\u4e2d\u3002 \u3000\u3000\u5f88\u5feb\uff0c\u4e24\u540d\u9752\u888d\u4e2d\u5e74\u4eba\u4e5f\u63a0\u8fc7\u4e86\u56f4\u5899\u3002\u5947\u602a\u7684\u662f\u4ed6\u4eec\u843d\u5730\u4e4b\u540e\uff0c\u5e76\u672a\u6025\u7740\u56db\u4e0b\u641c\u5bfb\uff0c\u53cd \u800c\u5bf9\u89c6\u4e00\u773c\uff0c\u540c\u65f6\u653e\u58f0\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5fc3\u4e2d\u4e00\u60ca\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u83ab\u975e\u8fd9\u662f\u4e00\u4e2a\u5708\u5957\uff0c\u76ee\u7684\u5c31\u662f\u5f15\u6211\u4e8c\u4eba\u6765\u6b64\u5730\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u5979\u5df2\u81ea\u8349\u4e1b\u4e2d\u7ad9\u8d77\u8eab\u6765\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4e8c\u4f4d\u4e00\u76f4\u8ddf\u8e2a\u672c\u4eba\uff0c\u5230\u5e95\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e24\u540d\u9752\u888d\u4eba\u5374\u4e0d\u7b54\u8bdd\uff0c\u5404\u81ea\u4f38\u624b\uff0c\u81ea\u957f\u888d\u5185\u62bd\u51fa\u4e86\u5175\u5668\u3002 \u3000\u3000\u4e8c\u4eba\u4f7f\u7684\u90fd\u662f\u8f6f\u5175\u5668\uff0c\u4e00\u6761\u7ec3\u5b50\u67aa\uff0c\u4e00\u6761\u662f\u4e5d\u8282\u97ad\uff0c\u6162\u6162\u5411\u7a0b\u5c0f\u8776\u903c\u8fd1\u8fc7\u6765\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u51b7\u9053\uff1a\u201c\u4e8c\u4f4d\u663e\u7136\u662f\u7ecf\u8fc7\u6613\u5bb9\u6539\u626e\u3002\u600e\u4e48\uff0c\u4e0d\u6562\u4ee5\u771f\u9762\u76ee\u793a\u4eba\u5417\uff1f\u201d \u3000\u3000\u7a81\u7136\uff0c\u5979\u8eab\u540e\u54cd\u8d77\u4e86\u4e00\u9635\u72c2\u7b11\u58f0\uff0c\u7b11\u58f0\u65b9\u505c\uff0c\u4fbf\u542c\u89c1\u4e00\u4e2a\u7565\u663e\u82cd\u8001\u7684\u58f0\u97f3\u9053\uff1a\u201c\u5c0a\u9a7e\u4e0d \u8981\u8ba5\u7b11\u522b\u4eba\uff0c\u5c0a\u9a7e\u81ea\u5df1\u4e0d\u4e5f\u662f\u85cf\u5934\u9732\u5c3e\uff0c\u4e0d\u6b32\u4ee5\u771f\u9762\u76ee\u793a\u4eba\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5927\u5403\u4e00\u60ca\uff0c\u56de\u5934\u4e00\u770b\uff0c\u8eab\u540e\u7ea6\u4e09\u4e08\u5916\uff0c\u4e0d\u77e5\u4f55\u65f6\u591a\u51fa\u4e00\u4e2a\u9ed1\u8863\u8499\u9762\u4eba\uff0c\u4ed6\u8eab\u5de6\u53f3 \u4e24\u4fa7\uff0c\u9f50\u5237\u5237\u7ad9\u7740\u4e0d\u4e0b\u4e8c\u5341\u540d\u624b\u6301\u5200\u5251\u7684\u7070\u8863\u5927\u6c49\u3002 \u3000\u3000\u8fd9\u9ed1\u8863\u8499\u9762\u4eba\u7684\u8eab\u6750\u5341\u5206\u9ad8\u5927\u9b41\u68a7\uff0c\u4ed6\u7684\u8499\u9762\u9ed1\u5e03\u8fb9\u9690\u9690\u9732\u51fa\u4e00\u4e1d\u82b1\u767d\u5934\u53d1\uff0c\u663e\u7136\u5e74\u9f84 \u5df2\u5728\u4e94\u5341\u5f00\u5916\u3002 \u3000\u3000\u731b\u4e00\u542c\u8fd9\u4eba\u7684\u58f0\u97f3\u4f3c\u4e4e\u6709\u4e9b\u8033\u719f\uff0c\u7a0b\u5c0f\u8776\u5fc3\u4e2d\u6697\u5fd6\uff0c\u518d\u4ed4\u7ec6\u4e00\u60f3\uff0c\u5374\u53c8\u5341\u5206\u964c\u751f\uff0c\u8fd9\u662f \u600e\u4e48\u56de\u4e8b\u5462\uff1f \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u5475\u5475\u7b11\u9053\uff1a\u201c\u4e45\u95fb\u5211\u90e8\u603b\u6355\u5934\u7a0b\u5c0f\u8776\u7684\u5927\u540d\uff0c\u4e0d\u6599\u4eca\u65e5\u4e00\u89c1\uff0c\u4ea6\u4e0d\u8fc7\u5c14\u5c14\u3002 \u7a0b\u5927\u6355\u5934\uff0c\u8001\u592b\u65e2\u7136\u5df2\u77e5\u4f60\u7684\u8eab\u5206\uff0c\u4f60\u8fd8\u4e0d\u9732\u51fa\u4f60\u7684\u5e90\u5c71\u771f\u9762\u76ee\u6765\uff01\u201d \u3000\u3000\u201c\u8001\u5339\u592b\u4e0d\u8981\u5f20\u72c2\uff0c\u770b\u672c\u59d1\u5a18\u4e0d\u6740\u4e86\u4f60\uff01\u201d\u7a0b\u5c0f\u8776\u5c1a\u672a\u7b54\u8bdd\uff0c\u5c0f\u96c5\u5df2\u633a\u8eab\u800c\u51fa\u4e86\uff0c\u62bd\u51fa \u5b9d\u5251\u4fbf\u6b32\u51fa\u62db\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4f38\u624b\u62c9\u4f4f\u5979\uff0c\u9053\uff1a\u201c\u5c0f\u96c5\uff01\u4e0d\u8981\u4e2d\u4e86\u4ed6\u7684\u6fc0\u5c06\u4e4b\u8ba1\uff01\u201d\u5979\u63ed\u4e0b\u8138\u4e0a\u7684\u4eba\u76ae\u9762\u5177\uff0c \u5c55\u989c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u672c\u59d1\u5a18\u6b63\u662f\u5211\u90e8\u603b\u6355\u5934\u7a0b\u5c0f\u8776\uff0c\u4f60\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u53ef\u4ee5\u4e0d\u56de\u7b54\u5417\uff1f\u201d\u542c\u8d77\u6765\u8499\u9762\u4eba\u4f3c\u4e4e\u5f88\u6709\u4e0e\u5979\u95f2\u804a\u51e0\u53e5\u7684\u96c5\u5174\u3002 \u3000\u3000\u201c\u4e0d\u53ef\u4ee5\u3002\u201d\u7a0b\u5c0f\u8776\u51b7\u51b7\u9053\u3002 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u8499\u9762\u4eba\u7684\u53e3\u6c14\u542c\u4e0a\u5374\u9887\u6709\u4e9b\u8bb6\u7136\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7a81\u7136\u6c89\u4e0b\u8138\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u56e0\u4e3a\u672c\u59d1\u5a18\u73b0\u5728\u662f\u4ee5\u5211\u90e8\u603b\u6355\u5934\u7684\u8eab\u5206\u5728\u4e0e\u4f60\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u201c\u5211\u90e8\u603b\u6355\u5934\uff01\u5475\u5475\uff0c\u597d\u5927\u7684\u5b98\u8854\u5440\uff01\u201d\u8499\u9762\u4eba\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u8001\u592b\u4e0d\u6015\u4f60\u7684\u5b98\u8854\uff0c\u4f60\u60f3 \u77e5\u9053\u8001\u592b\u662f\u8c01\uff0c\u770b\u6837\u5b50\u53ea\u597d\u53bb\u95ee\u960e\u738b\u7237\u55bd\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u773c\u4e2d\u51b7\u5149\u5982\u7535\uff0c\u76f4\u89c6\u7740\u8499\u9762\u4eba\u7684\u53cc\u773c\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u60f3\u6740\u6211\uff1f\u201d \u3000\u3000\u8499\u9762\u4eba\u9053\uff1a\u201c\u4e0d\u4ec5\u60f3\u60f3\u800c\u5df2\uff0c\u800c\u4e14\u5177\u6709\u6740\u4f60\u7684\u5b9e\u529b\uff01\u660e\u5e74\u7684\u4eca\u65e5\uff0c\u4fbf\u662f\u4f60\u7684\u5fcc\u65e5\uff01\u201d \u3000\u3000\u5c0f\u96c5\u4e3e\u5251\u6012\u53f1\u9053\uff1a\u201c\u80e1\u5439\u5927\u6c14\uff01\u4e5f\u4e0d\u6015\u98ce\u5927\u95ea\u4e86\u4f60\u7684\u820c\u5934\uff01\u201d \u3000\u3000\u8499\u9762\u4eba\u9053\uff1a\u201c\u5c0f\u59d1\u5a18\u4e0d\u7528\u6025\uff0c\u4f60\u4eec\u603b\u6355\u5934\u5f53\u7136\u4f1a\u5e26\u4e0a\u4f60\uff0c\u4f60\u4eec\u653e\u5fc3\uff0c\u9ec4\u6cc9\u8def\u4e0a\u4e0d\u5bc2\u5bde\uff0c \u77e5\u5e9c\u738b\u5c11\u537f\uff0c\u540d\u632f\u5929\u4e0b\u7684\u56db\u5927\u6355\u5934\u4e5f\u4f1a\u966a\u7740\u4f60\u4eec\u4e00\u8d77\u8d70\u7684\uff01\u201d \u3000\u3000\u8bdd\u97f3\u672a\u843d\uff0c\u4e24\u540d\u9752\u888d\u4eba\u5df2\u7136\u5411\u7a0b\u5c0f\u8776\u548c\u5c0f\u96c5\u53d1\u52a8\u4e86\u653b\u51fb\u3002 \u3000\u3000\u4e24\u4ef6\u8f6f\u5175\u5668\u65cb\u98de\u800c\u81f3\uff0c\u5e26\u8d77\u4e00\u9635\u9510\u6025\u7684\u5578\u58f0\u3002\u7ec3\u5b50\u67aa\u76f4\u624e\u7a0b\u5c0f\u8776\u9762\u95e8\uff0c\u4e5d\u8282\u97ad\u6a2a\u626b\u5411\u5c0f \u96c5\u7684\u67f3\u8170\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u53f1\u4e00\u58f0\uff1a\u201c\u72c2\u5f92\u6562\u5c14\uff01\u201d\u5de6\u624b\u4f38\u51fa\uff0c\u5982\u4e00\u6735\u6d01\u767d\u7684\u5e7d\u5170\uff0c\u8f7b\u8f7b\u4e00\u62c8\uff0c\u5df2\u634f\u4f4f\u7ec3 \u5b50\u67aa\u950b\u9510\u7684\u67aa\u5934\u3002 \u3000\u3000\u9752\u888d\u4eba\u663e\u7136\u6ca1\u60f3\u5230\u81ea\u5df1\u5168\u529b\u653b\u51fa\u7684\u5175\u5668\u5982\u6b64\u8f7b\u6613\u5730\u843d\u5165\u4e86\u654c\u4eba\u4e4b\u624b\u4e2d\uff0c\u53cc\u81c2\u7528\u529b\uff0c\u60f3\u5c06 \u67aa\u5934\u593a\u56de\uff0c\u4f46\u6323\u7684\u6ee1\u9762\u901a\u7ea2\uff0c\u8116\u5b50\u4e0a\u9752\u7b4b\u4e71\u8e66\uff0c\u7ec3\u5b50\u67aa\u5374\u5728\u7a0b\u5c0f\u8776\u624b\u4e2d\u7eb9\u4e1d\u4e0d\u52a8\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7a81\u7136\u95ea\u8eab\u98de\u4e0a\uff0c\u53f3\u638c\u51fb\u51fa\uff0c\u6b63\u4e2d\u9752\u888d\u4eba\u7684\u80f8\u53e3\uff0c\u9752\u888d\u4eba\u5927\u543c\u4e00\u58f0\uff0c\u53e3\u4e2d\u9c9c\u8840\u72c2\u55b7\uff0c \u5012\u5730\u6323\u624e\u51e0\u4e0b\uff0c\u5c31\u6b64\u6bd9\u547d\u3002 \u3000\u3000\u53e6\u4e00\u9752\u888d\u4eba\u4e5f\u4e0d\u6bd4\u4ed6\u5e78\u8fd0\u3002 \u3000\u3000\u5c0f\u96c5\u4e00\u626d\u5c0f\u86ee\u8170\uff0c\u95ea\u8fc7\u4e5d\u8282\u97ad\u7684\u626b\u51fb\uff0c\u53f3\u811a\u8e0f\u4e0a\u4e00\u6b65\uff0c\u624b\u4e2d\u957f\u5251\u5df2\u81ea\u9752\u888d\u4eba\u80f8\u524d\u523a\u5165\uff0c \u76f4\u900f\u540e\u5fc3\uff01 \u3000\u3000\u201c\u597d\u72e0\u6bd2\u7684\u5c0f\u5a18\u4eec\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u548c\u5c0f\u96c5\u53ea\u542c\u89c1\u8499\u9762\u4eba\u4e00\u58f0\u547c\u543c\uff0c\u8f6c\u8fc7\u8eab\u65f6\uff0c\u5374\u5df2\u4e0d\u89c1\u8499\u9762\u4eba\u7684\u8eab\u5f71\uff0c\u8fde\u4ed6\u8eab\u8fb9\u7684 \u6570\u5341\u540d\u7070\u8863\u58ee\u6c49\u4e5f\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u73b0\u5728\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u65ad\u7136\u9053\uff1a\u201c\u90a3\u4e2a\u8499\u9762\u4eba\u4e00\u5b9a\u662f\u6740\u624b\u96c6\u56e2\u7684\u9996\u8111\u7ea7\u4eba\u7269\uff0c\u8fd9\u6761\u7ebf\u7d22\u7edd\u4e0d\u80fd\u653e\u8fc7\uff01\u5c0f \u96c5\uff0c\u54b1\u4eec\u8ffd\uff01\u201d \u3000\u3000\u4e8c\u4eba\u80cc\u9760\u7740\u80cc\uff0c\u4ee5\u9632\u8349\u4e1b\u4e2d\u4f0f\u6709\u6740\u624b\u7a81\u7136\u8dc3\u51fa\u5077\u88ad\uff0c\u6162\u6162\u5730\u5411\u524d\u641c\u7d22\u524d\u8fdb\uff0c\u8d70\u51fa\u4e8c\u4e09\u5341 \u4e08\u8fdc\uff0c\u773c\u524d\u51fa\u73b0\u4e86\u4e00\u5ea7\u7cbe\u7f8e\u7684\u697c\u9601\uff0c\u9601\u524d\u7adf\u7136\u662f\u4e00\u7247\u9752\u77f3\u94fa\u5c31\u7684\u7a7a\u5730\uff0c\u8fd8\u6709\u4e03\u516b\u5ea7\u592a\u6e56\u77f3\u5792 \u5c31\u7684\u5047\u5c71\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u548c\u5c0f\u96c5\u521a\u521a\u8d70\u51fa\u6742\u8349\u4e1b\uff0c\u81ea\u6bcf\u4e00\u5ea7\u5047\u5c71\u4e4b\u540e\u90fd\u95ea\u51fa\u4e86\u4e09\u540d\u7070\u8863\u4eba\uff0c\u4e24\u4eba\u6301\u9b3c\u5934\u5927 \u5200\uff0c\u4e00\u4eba\u6301\u9752\u94a2\u8f6e\u3002\u516b\u7ec4\u7070\u8863\u6740\u624b\u4e00\u8a00\u4e0d\u53d1\uff0c\u6162\u6162\u5411\u4e24\u8fb9\u6563\u5f00\uff0c\u5c06\u4e8c\u4eba\u56f4\u5728\u4e86\u6b63\u4e2d\u3002 \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u770b\u6837\u5b50\u4ed6\u4eec\u5c31\u662f\u675c\u53f8\u4e3b\u6240\u8bf4\u7684\u7070\u8863\u6740\u624b\u7ec4\u5408\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u81ea\u8896\u4e2d\u62bd\u51fa\u4e00\u6761\u4e09\u4e08\u6709\u4f59\u9c9c\u7ea2\u7684\u7ec6\u7d22\uff0c\u56e2\u5728\u53f3\u624b\u4e2d\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u662f\u7684\uff0c\u5c31\u662f\u4ed6\u4eec\u3002 \u5728\u7626\u897f\u6e56\u8fb9\u6211\u5df2\u89c1\u8fc7\u4ed6\u4eec\u7ec4\u5408\u4e4b\u540e\u7684\u5a01\u529b\uff0c\u5341\u5206\u60ca\u4eba\uff0c\u5c0f\u96c5\uff0c\u4f60\u8981\u5c0f\u5fc3\u5bf9\u654c\u624d\u662f\uff01\u201d \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u5c0f\u59d0\u4e00\u5b9a\u5df2\u7ecf\u60f3\u51fa\u4ed6\u4eec\u7ec4\u5408\u7684\u5173\u952e\u6240\u5728\u4e86\u5427\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u79cd\u7ec4\u5408\u5341\u5206\u5947\u5999\uff0c\u6211\u4e5f\u4e00\u76f4\u672a\u80fd\u60f3\u900f\u5176\u4e2d\u5173\u952e\uff0c\u4f46\u662f\u6211\u5df2\u60f3\u51fa \u4e86\u4e00\u79cd\u5bf9\u6297\u4ed6\u4eec\u7684\u65b9\u6cd5\uff0c\u53ea\u8981\u5c0f\u96c5\u4f60\u4e0e\u6211\u914d\u5408\u5f97\u5f53\uff0c\u4e00\u5b9a\u80fd\u591f\u83b7\u80dc\u3002\u201d \u3000\u3000\u5c0f\u96c5\u9ad8\u5174\u5730\u9053\uff1a\u201c\u5c0f\u59d0\u4f60\u5feb\u544a\u8bc9\u6211\u5440\uff01\u201d \u3000\u3000\u4e8c\u4eba\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u516b\u7ec4\u7070\u8863\u6740\u624b\u5df2\u7ecf\u903c\u8fd1\u4e86\u4e00\u4e08\u6709\u4f59\uff0c\u79bb\u5979\u4eec\u4e0d\u8fc7\u53ea\u6709\u56db\u4e94\u4e08\u8fdc\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u56db\u4e0b\u91cc\u626b\u89c6\u4e86\u4e00\u773c\uff0c\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u5f00\u59cb\u7528\u4f20\u97f3\u5165\u79d8\u7684\u529f\u592b\u5411\u5c0f\u96c5\u5bc6\u6388\u673a\u5b9c\u3002 \u3000\u3000\u5728\u73b0\u5728\u8fd9\u79cd\u5f62\u52bf\u4e0b\uff0c\u5979\u4e0d\u5f97\u4e0d\u8fd9\u6837\u505a\uff0c\u56e0\u4e3a\u6740\u624b\u4eec\u9760\u5f97\u5df2\u7ecf\u5f88\u8fd1\u4e86\uff0c\u5982\u679c\u8ba9\u4ed6\u4eec\u4e5f\u542c\u89c1 \u4e86\u5979\u7528\u6765\u5bf9\u4ed8\u4ed6\u4eec\u7684\u65b9\u6cd5\uff0c\u90a3\u5c31\u592a\u7cdf\u4e86\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u521a\u521a\u5c06\u5979\u7684\u65b9\u6cd5\u8bf4\u5b8c\uff0c\u7070\u8863\u6740\u624b\u4eec\u53c8\u5df2\u903c\u8fd1\u4e86\u4e00\u4e08\uff0c\u79bb\u5979\u4eec\u4e0d\u8fc7\u4e09\u4e08\u591a\u8fdc\u4e86\u3002 \u3000\u3000\u4e09\u4e08\u8fdc\uff0c\u5bf9\u4e8e\u5979\u4eec\u8fd9\u6837\u7684\u9ad8\u624b\u6765\u8bf4\uff0c\u51e0\u4e4e\u5df2\u53ef\u7b97\u662f\u6700\u540e\u7684\u5b89\u5168\u8ddd\u79bb\u3002 \u3000\u3000\u6740\u624b\u4eec\u9f50\u58f0\u4f4e\u543c\uff0c\u624b\u4e2d\u7684\u9b3c\u5934\u5200\u548c\u9752\u94a2\u8f6e\u5728\u9633\u5149\u4e0b\u95ea\u52a8\u7740\u4eae\u95ea\u95ea\u7684\u51b7\u5149\uff0c\u5411\u7a0b\u5c0f\u8776\u548c\u5c0f \u96c5\u76f4\u6251\u4e0a\u6765\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u53f3\u624b\u4e00\u6325\uff0c\u4e09\u4e08\u7ea2\u7d22\u5ffd\u7136\u6296\u8d77\u51e0\u4e2a\u98de\u65cb\u7684\u5708\u5b50\uff0c\u4e00\u7728\u773c\u95f4\uff0c\u7ea2\u7d22\u7684\u4e00\u7aef\u5df2\u7f20\u5728\u4e86 \u4e00\u540d\u624b\u821e\u9752\u94a2\u8f6e\u7684\u7070\u8863\u6740\u624b\u5c0f\u817f\u4e0a\u3002 \u3000\u3000\u8fd9\u540d\u7070\u8863\u6740\u624b\u88ab\u7ea2\u7d22\u4e00\u7f20\u4e00\u7eca\uff0c\u7acb\u523b\u6454\u5012\u5728\u5730\uff0c\u5c0f\u96c5\u65e9\u5df2\u98de\u8eab\u51b2\u4e0a\uff0c\u624b\u4e2d\u5b9d\u5251\u653e\u51fa\u6570\u5341 \u6761\u8000\u773c\u7684\u5251\u5149\uff0c\u4e00\u9635\u8840\u96e8\u6d12\u5f00\uff0c\u4e09\u9897\u4eba\u5934\u843d\u5730\u3002 \u3000\u3000\u201c\u6740\u5f97\u597d\uff01\u201d\u7a0b\u5c0f\u8776\u7b11\u9053\uff0c\u53f3\u624b\u75be\u6325\uff0c\u7ea2\u7d22\u98de\u5feb\u5730\u65cb\u52a8\u7740\uff0c\u62bd\u51fb\u7740\uff0c\u6bcf\u4e00\u6b21\u65cb\u52a8\uff0c\u6bcf\u4e00 \u6b21\u62bd\u51fb\uff0c\u90fd\u4f1a\u6709\u4e00\u540d\u4f7f\u9752\u94a2\u8f6e\u7684\u7070\u8863\u4eba\u88ab\u626f\u7ffb\u5728\u5730\uff0c\u800c\u7070\u8863\u6740\u624b\u4eec\u7684\u4e09\u4eba\u7ec4\u5408\u4e5f\u5c31\u5c31\u6b64\u74e6\u89e3\u3002 \u3000\u3000\u5931\u53bb\u4e86\u7ec4\u5408\u5a01\u529b\u7684\u7070\u8863\u6740\u624b\u54ea\u91cc\u662f\u5c0f\u96c5\u7684\u654c\u624b\u5462\uff01\u51e0\u4e4e\u662f\u4e00\u8f6c\u773c\u95f4\uff0c\u5730\u4e0a\u5df2\u8eba\u5012\u4e86\u4e8c\u5341 \u5177\u5c38\u4f53\uff0c\u5c1a\u6709\u56db\u4eba\u4e5f\u662f\u5012\u5730\u4e0d\u8d77\uff0c\u5c31\u7b97\u80fd\u4fdd\u4e00\u6761\u6027\u547d\uff0c\u770b\u6765\u540e\u534a\u8f88\u5b50\u4e5f\u53ea\u662f\u4e2a\u5e9f\u4eba\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u770b\u4e86\u770b\u5c0f\u96c5\u6cbe\u6ee1\u9c9c\u8840\u7684\u5b9d\u5251\uff0c\u53c8\u770b\u770b\u5730\u4e0a\u8eba\u5012\u7684\u7070\u8863\u6740\u624b\u4eec\uff0c\u8138\u4e0a\u4e0d\u7981\u95ea\u51fa\u4e86\u4e00 \u4e1d\u601c\u60af\uff0c\u4e00\u4e1d\u4e0d\u5fcd\u3002 \u3000\u3000\u770b\u6765\uff0c\u5979\u662f\u52a8\u4e86\u6148\u60b2\u4e4b\u5fc3\u4e86\u3002 \u3000\u3000\u4f46\u662f\u59d1\u5a18\u554a\uff0c\u4f60\u7684\u6148\u60b2\u4e4b\u5fc3\u662f\u53ef\u8d35\u7684\uff0c\u4e5f\u662f\u53ef\u656c\u7684\uff0c\u53ea\u662f\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\uff0c\u5bf9\u654c\u4eba\u7684\u6148\u60b2\uff0c \u4e5f\u5c31\u662f\u5bf9\u81ea\u5df1\u7684\u6b8b\u5fcd\u554a\uff01 \u3000\u3000\u56e0\u4e3a\u4f60\u4e0d\u6740\u4ed6\u4eec\uff0c\u4ed6\u4eec\u53ef\u662f\u4f1a\u6beb\u4e0d\u624b\u8f6f\u5730\u6740\u6389\u4f60\u554a\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u4e24\u9053\u660e\u4eae\u7684\u76ee\u5149\u76f4\u5c04\u5411\u90a3\u5ea7\u697c\u9601\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u9601\u4e0b\u4e5f\u8be5\u73b0\u8eab\u4e86\u5427\uff01\u201d\u51e0\u58f0\u96f6\u843d\u7684 \u638c\u58f0\u54cd\u8d77\uff0c\u9ed1\u8863\u8499\u9762\u4eba\u7f13\u6b65\u81ea\u697c\u9601\u4e2d\u8d70\u51fa\uff0c\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\u679c\u7136\u6709\u4e24\u4e0b\u5b50\uff0c\u540d\u4e0d\u865a\u4f20\u554a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u58f0\u9053\uff1a\u201c\u4f60\u660e\u77e5\u9053\u4ed6\u4eec\u4e0d\u662f\u6211\u4eec\u7684\u5bf9\u624b\uff0c\u4e3a\u4ec0\u4e48\u8981\u8ba9\u4ed6\u4eec\u6765\u9001\u6b7b\uff1f\u4f60\u60f3\u6740\u6211\uff0c \u4e3a\u4ec0\u4e48\u4e0d\u81ea\u5df1\u52a8\u624b\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u7684\u6027\u547d\u672c\u5c31\u5f88\u4e0d\u503c\u94b1\uff0c\u591a\u6b7b\u51e0\u4e2a\u4e5f\u6ca1\u4ec0\u4e48\u3002\u4ed6\u4eec\u4e8c\u5341\u51e0\u6761 \u547d\u6362\u5f97\u8001\u592b\u6709\u4e00\u7779\u7a0b\u603b\u6355\u5934\u6b66\u529f\u673a\u667a\u7684\u673a\u4f1a\uff0c\u4e5f\u7b97\u662f\u6b7b\u5f97\u5f88\u6709\u4ef7\u503c\u4e86\uff01\u201d\u5c0f\u96c5\u6012\u53f1\u9053\uff1a\u201c\u4f4f \u53e3\uff01\u4f60\u7adf\u7136\u8bf4\u51fa\u8fd9\u7b49\u6b8b\u5fcd\u7684\u8bdd\u6765\uff0c\u4f60\u8fd8\u662f\u4e0d\u662f\u4eba\uff01\u201d \u3000\u3000\u9ed1\u8863\u4eba\u7b11\u9053\uff1a\u201c\u5475\u5475\uff01\u53ef\u7b11\u554a\uff01\u6740\u6b7b\u8fd9\u4e9b\u4eba\u7684\u672c\u662f\u5c0f\u59d1\u5a18\u4f60\uff0c\u8001\u592b\u53c8\u4f55\u6b8b\u5fcd\u4e4b\u6709\uff1f\u201d \u3000\u3000\u5c0f\u96c5\u66f4\u60ca\u6012\uff0c\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u5de7\u8a00\u72e1\u8fa8\uff0c\u4e0d\u8981\u8138\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u662f\u6740\u624b\u4e4b\u738b\u3001\u8840\u624b\u65b9\u8f6e\u5417\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u8fd9\u79cd\u6740\u624b\u7ec4\u5408\u662f\u65b9\u8f6e\u7cbe\u5fc3\u8bad\u7ec3\u800c\u6210\uff0c\u4ed6\u4eec\u88ab\u4f60\u4eec\u6740\u4e86\uff0c\u65b9\u8f6e\u5e94\u8be5\u5f88\u89c9 \u5f97\u53ef\u60dc\u624d\u5bf9\uff0c\u4f60\u770b\u8001\u592b\u6709\u534a\u70b9\u53ef\u60dc\u7684\u610f\u601d\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u4f60\u4e0d\u662f\u65b9\u8f6e\u4e86\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u662f\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u90a3\u4f60\u5230\u5e95\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\u771f\u60f3\u77e5\u9053\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u8001\u592b\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u4f46\u9996\u5148\u5f97\u770b\u4f60\u591f\u4e0d\u591f\u4efd\u91cf\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6de1\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8981\u600e\u6837\u4f60\u624d\u8ba4\u4e3a\u6211\u591f\u4efd\u91cf\u5462\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u4f60\u6211\u4e4b\u95f4\uff0c\u76f8\u8ddd\u4e0d\u8fc7\u5341\u4e08\uff0c\u5982\u679c\u7a0b\u603b\u6355\u5934\u80fd\u5728\u8001\u592b\u81ea\u4e00\u6570\u5230\u4e09\u5341\u4e4b\u6570 \u95f4\u8d70\u5230\u8001\u592b\u8eab\u524d\u4e03\u5c3a\u5904\uff0c\u8001\u592b\u81ea\u4f1a\u5c06\u771f\u5b9e\u8eab\u4efd\u544a\u8bc9\u7a0b\u603b\u6355\u5934\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u5475\u5475\uff01\u7a0b\u603b\u6355\u5934\u8ba4\u4e3a\u8001\u592b\u6709\u9a97\u4f60\u7684\u5fc5\u8981\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u8f7b\u8f7b\u4e00\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u51c6\u5907\u597d\u4e86\u5417\uff1f\u8001\u592b\u8fd9\u5c31\u5f00\u59cb\u8ba1\u6570\u4e86\uff01\u201d \u3000\u3000\u8bdd\u97f3\u521a\u843d\uff0c\u4ed6\u5c31\u201c\u4e00\u3001\u4e8c\u3001\u4e09\u2026\u2026\u201d\u5730\u6570\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u6570\u7684\u5e76\u4e0d\u5feb\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u5f88\u6162\uff0c\u4f46\u662f\u65e0\u8bba\u4ed6\u6570\u5f97\u591a\u6162\uff0c\u4ece\u4e00\u5230\u4e09\u5341\u4e5f\u5b9e\u5728\u82b1\u4e0d\u4e86\u591a\u957f \u65f6\u95f4\u3002 \u3000\u3000\u5bf9\u7a0b\u5c0f\u8776\u8fd9\u6837\u7684\u6b66\u529f\u9ad8\u624b\u6765\u8bf4\uff0c\u5341\u4e08\u4e5f\u4e0d\u662f\u4e00\u4e2a\u5f88\u8fdc\u7684\u8ddd\u79bb\uff0c\u5982\u679c\u8bf4\u6709\u4efb\u4f55\u963b\u788d\uff0c\u53ea\u8981 \u7728\u773c\u4e4b\u95f4\uff0c\u5979\u5c31\u80fd\u4e00\u63a0\u800c\u8fc7\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u5f53\u7136\u4e0d\u4f1a\u8ba9\u5979\u5982\u6b64\u8f7b\u677e\u5730\u63a0\u8fc7\u8fd9\u6bb5\u8ddd\u79bb\u3002 \u3000\u3000\u5728\u4ed6\u5f00\u59cb\u6570\u6570\u65f6\uff0c\u4ed6\u4e0e\u7a0b\u5c0f\u8776\u4e4b\u95f4\u7684\u786e\u6ca1\u6709\u4efb\u4f55\u963b\u788d\uff0c\u4f46\u7a0b\u5c0f\u8776\u521a\u4e00\u4e3e\u6b65\uff0c\u5979\u9762\u524d\u5c31\u95ea \u51fa\u4e86\u4e24\u9053\u7070\u5f71\u3002 \u3000\u3000\u7070\u5f71\u7adf\u662f\u4e24\u540d\u91cd\u4f24\u5012\u5730\u7684\u7070\u8863\u6740\u624b\u3002 \u3000\u3000\u770b\u4ed6\u4eec\u7684\u6837\u5b50\uff0c\u6b66\u529f\u4f3c\u4e4e\u6bd4\u53d7\u4f24\u524d\u8fd8\u8981\u9ad8\u51fa\u6570\u500d\uff0c\u4e24\u628a\u9b3c\u5934\u5200\u547c\u5578\u7740\u76f4\u780d\u5411\u7a0b\u5c0f\u8776\u53cc\u80a9\uff0c \u7adf\u597d\u50cf\u8574\u6709\u5343\u65a4\u4e4b\u529b\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5fc3\u4e0d\u7981\u4e00\u60ca\uff0c\u53cc\u638c\u4e00\u5206\uff0c\u540c\u65f6\u51fb\u51fa\uff0c\u5c06\u4e24\u540d\u7070\u8863\u4eba\u51fb\u5f97\u5012\u98de\u6570\u4e08\uff0c\u91cd\u91cd\u5730\u6389\u5728\u5730 \u4e0a\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u9ed1\u8863\u8499\u9762\u4eba\u5df2\u6570\u5230\u201c\u516b\u201d\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u6e05\u5578\u4e00\u58f0\uff0c\u5ffd\u5730\u62d4\u5730\u800c\u8d77\uff0c\u8eab\u5b50\u5df2\u6a2a\u98de\u5728\u7a7a\u4e2d\uff0c\u5934\u524d\u811a\u540e\uff0c\u6574\u4e2a\u4eba\u5c31\u50cf\u662f\u4e00\u679d\u79bb \u5f26\u4e4b\u7bad\uff0c\u76f4\u5c04\u5411\u9ed1\u8863\u8499\u9762\u4eba\u3002 \u3000\u3000\u7a81\u542c\u5c0f\u96c5\u53eb\u9053\uff1a\u201c\u5c0f\u59d0\u5c0f\u5fc3\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53ea\u89c9\u5f97\u56db\u5468\u54cd\u8d77\u4e00\u9635\u5200\u950b\u7834\u7a7a\u4e4b\u58f0\uff0c\u56db\u5904\u6563\u843d\u5728\u5730\u7684\u5341\u51e0\u67c4\u9b3c\u5934\u5200\u548c\u516b\u4e2a\u9752\u94a2\u8f6e \u5ffd\u7136\u95f4\u4e00\u9f50\u5411\u5979\u76f4\u98de\u8fc7\u6765\u3002 \u3000\u3000\u534a\u7a7a\u4e2d\u5ffd\u7136\u95ea\u51fa\u4e00\u9053\u9c9c\u7ea2\u7684\u5149\u5e55\uff0c\u539f\u6765\u662f\u7a0b\u5c0f\u8776\u6296\u5f00\u4e86\u7ea2\u7d22\uff0c\u5728\u5468\u8eab\u5e03\u4e0b\u4e86\u5bc6\u4e0d\u900f\u98ce\u7684 \u91cd\u91cd\u7d22\u5f71\u3002 \u3000\u3000\u98de\u5c04\u800c\u81f3\u7684\u9b3c\u5934\u5200\u3001\u9752\u94a2\u8f6e\u4e00\u78d5\u4e0a\u8fd9\u9053\u9c9c\u7ea2\u7684\u5149\u5e55\uff0c\u4fbf\u88ab\u5f39\u5f00\u6765\u53bb\uff0c\u4f46\u88ab\u5f39\u5f00\u7684\u5200\uff0c\u8f6c \u773c\u95f4\u53c8\u98de\u4e86\u56de\u6765\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u53e3\u771f\u6c14\u65e0\u6cd5\u6301\u4e45\uff0c\u81ea\u534a\u7a7a\u4e2d\u843d\u5230\u4e86\u5730\u4e0a\u3002 \u3000\u3000\u5979\u843d\u811a\u4e4b\u5730\u79bb\u9ed1\u8863\u8499\u9762\u4eba\u5c1a\u6709\u4e94\u4e08\u4e4b\u9065\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u9ed1\u8863\u8499\u9762\u4eba\u5df2\u6570\u5230\u4e86\u201c\u5341\u4e5d\u201d\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u843d\u5730\uff0c\u9b3c\u5934\u5200\u548c\u9752\u94a2\u8f6e\u53c8\u90fd\u98de\u65cb\u7740\u5411\u5979\u5c04\u8fc7\u6765\u3002\u5979\u594b\u529b\u821e\u52a8\u7740\u7ea2\u7d22\uff0c\u4f0f\u8eab\u5411\u524d \u75be\u5954\uff0c\u4f46\u6bcf\u5411\u524d\u8fc8\u51fa\u4e00\u6b65\uff0c\u90fd\u662f\u90a3\u4e48\u7684\u56f0\u96be\u3002 \u3000\u3000\u5c0f\u96c5\u5728\u4e00\u65c1\u5e72\u7740\u6025\uff0c\u5374\u5e2e\u4e0d\u4e0a\u5fd9\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u5df2\u6570\u5230\u201c\u4e8c\u5341\u4e8c\u201d\u3002 \u3000\u3000\u4ed6\u7adf\u7136\u6570\u5f97\u8d8a\u6765\u8d8a\u6162\u4e86\uff0c\u50cf\u662f\u5f88\u60f3\u7a0b\u5c0f\u8776\u80fd\u591f\u5728\u201c\u4e09\u5341\u201d\u4e4b\u6570\u5185\u5230\u8fbe\u76ee\u7684\u5730\u4f3c\u7684\u3002 \u3000\u3000\u5c0f\u96c5\u4e00\u8fb9\u5e72\u7740\u6025\uff0c\u4e00\u8fb9\u6697\u81ea\u5fc3\u60ca\u3002 \u3000\u3000\u5979\u60f3\u4e0d\u901a\u90a3\u4e9b\u5200\u3001\u8f6e\u600e\u4e48\u4f1a\u5168\u90fd\u98de\u8d77\u6765\u7684\uff0c\u56e0\u4e3a\u9ed1\u8863\u8499\u9762\u4eba\u4e00\u76f4\u5c06\u53cc\u624b\u62e2\u5728\u5927\u8896\u4e4b\u4e2d\uff0c \u800c\u4e14\u4ed6\u7684\u888d\u3001\u8896\u4e00\u70b9\u4e5f\u6ca1\u6709\u9f13\u80c0\u8d77\u6765\uff0c\u663e\u7136\u4ed6\u5e76\u4e0d\u662f\u5728\u7528\u5185\u529b\u9a71\u52a8\u8fd9\u4e9b\u5200\u548c\u8f6e\u3002 \u3000\u3000\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u7a0b\u5c0f\u8776\u53c8\u8270\u96be\u5730\u5411\u524d\u51b2\u51fa\u4e86\u4e24\u4e08\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u5df2\u6570\u5230\u201c\u4e8c\u5341\u516d\u201d\u3002 \u3000\u3000\u7a81\u7136\uff0c\u7a0b\u5c0f\u8776\u7ea2\u7d22\u4e00\u5708\u3001\u4e00\u6536\uff0c\u5df2\u5c06\u6f2b\u5929\u98de\u81f3\u7684\u5200\u548c\u8f6e\u5168\u6570\u6346\u4f4f\u3002 \u3000\u3000\u5c0f\u96c5\u4e0d\u7981\u62cd\u624b\u7b11\u9053\uff1a\u201c\u597d\u554a\uff01\u5c0f\u59d0\u52a0\u6cb9\u554a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u98de\u8eab\u4e00\u8dc3\uff0c\u79bb\u9ed1\u8863\u8499\u9762\u4eba\u53ea\u6709\u4e00\u4e08\u8fdc\u4e86\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u5df2\u6570\u5230\u201c\u4e8c\u5341\u516b\u201d\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u79bb\u7a0b\u5c0f\u8776\u6700\u8fd1\u7684\u4e24\u5ea7\u5047\u5c71\u7a81\u7136\u6a2a\u98de\u8d77\u6765\uff0c\u6321\u5728\u4e86\u5979\u8eab\u524d\u3002 \u3000\u3000\u201c\u9b3c\u5200\uff0c\u53c8\u79f0\u68a6\u5e7b\u4e4b\u5200\uff0c\u80fd\u5f79\u4f7f\u4eba\u3001\u8349\u3001\u82b1\u3001\u77f3\u4e3a\u4ed6\u6240\u7528\u2026\u2026\u201d\u8fd9\u4e2a\u5ff5\u5934\u5ffd\u7136\u81ea\u5c0f\u96c5\u8111 \u4e2d\u95ea\u8fc7\u3002\u5c0f\u96c5\u8131\u53e3\u53eb\u9053\uff1a\u201c\u5c0f\u59d0\u5c0f\u5fc3\uff0c\u4ed6\u5c31\u662f\u68a6\u5e7b\u4e4b\u5200\uff01\u201d \u3000\u3000\u9ed1\u8863\u4eba\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u7528\u51b7\u51b0\u51b0\u7684\u58f0\u97f3\u6570\u9053\uff1a\u201c\u4e8c\u5341\u4e5d\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53cc\u638c\u75be\u6325\uff0c\u62cd\u5f00\u4e24\u5ea7\u5047\u5c71\uff0c\u5411\u524d\u8de8\u51fa\u4e00\u6b65\uff01 \u3000\u3000\u4e03\u5c3a\uff01 \u3000\u3000\u5979\u79bb\u8499\u9762\u9ed1\u8863\u4eba\u5df2\u53ea\u6709\u4e03\u5c3a\uff01 \u3000\u3000\u5979\u6210\u529f\u4e86\uff01 \u3000\u3000\u5c0f\u96c5\u4e0d\u7981\u6b22\u547c\u9053\uff1a\u201c\u5c0f\u59d0\u4f60\u6210\u529f\u4e86\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u76ee\u5149\u5982\u7535\uff0c\u76ef\u7740\u9ed1\u8863\u8499\u9762\u4eba\uff0c\u9053\uff1a\u201c\u4f60\u53ea\u6570\u5230\u4e8c\u5341\u4e5d\uff0c\u6211\u79bb\u4f60\u5df2\u53ea\u6709\u4e03\u5c3a\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8bf7\u4f60\u9075\u5b88\u8bfa\u8a00\uff0c\u63ed\u5f00\u8499\u9762\u9ed1\u5e03\u5427\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u8fd9\u4e2a\u5fc5\u8981\u5417\uff1f\u5c0f\u96c5\u59d1\u5a18\u4e0d\u662f\u5df2\u7ecf\u8ba4\u51fa\u8001\u592b\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u662f\u68a6\u5e7b\u4e4b\u5200\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u6b63\u662f\u8001\u592b\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u8fd8\u662f\u8981\u8bf7\u4f60\u63ed\u5f00\u9762\u5e55\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u7a0b\u603b\u6355\u5934\u4e0d\u4fe1\u8001\u592b\u7684\u8bdd\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u76ef\u7740\u4ed6\u770b\u4e86\u7247\u523b\uff0c\u9053\uff1a\u201c\u975e\u4e0d\u4fe1\u4e5f\uff0c\u53ea\u662f\u6211\u4e5f\u4e45\u6b32\u4e00\u7779\u68a6\u5e7b\u4e4b\u5200\u7684\u771f\u9762\u76ee\u3002\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u4e24\u624b\u6162\u6162\u81ea\u8896\u4e2d\u62bd\u51fa\u6765\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u8c01\u53eb\u8001\u592b\u8f93\u4e86\u5462\uff01\u201d \u3000\u3000\u8bdd\u97f3\u521a\u843d\uff0c\u4ed6\u4e24\u624b\u5ffd\u5730\u5f80\u524d\u4e00\u4f38\uff0c\u4e24\u53ea\u8896\u53e3\u4e2d\u90fd\u55b7\u51fa\u4e00\u80a1\u6d53\u6d53\u7684\u767d\u70df\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u6014\u4e4b\u4e0b\uff0c\u8fd8\u6ca1\u53cd\u5e94\u8fc7\u6765\uff0c\u767d\u70df\u5df2\u7ecf\u6251\u9762\u800c\u81f3\uff0c\u4e00\u7f15\u9999\u6c14\u5165\u9f3b\uff0c\u4eba\u5df2\u8f6f\u5012\u5728\u5730\u3002 \u3000\u3000\u5341\u4e08\u4e4b\u5916\u7684\u5c0f\u96c5\u4e5f\u88ab\u4ece\u4e00\u5ea7\u5047\u5c71\u4e4b\u540e\u5c04\u51fa\u7684\u4e00\u9635\u767d\u70df\u8ff7\u5012\u4e86\u3002 \u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u53e3 \u3000\u3000\u5c0f\u96c5\u51fa\u4e86\u534a\u5929\u795e\uff0c\u4e00\u6392\u788e\u7389\u822c\u6d01\u767d\u7684\u7259\u9f7f\u4e00\u76f4\u54ac\u7740\u9c9c\u7ea2\u7684\u6a31\u5507\uff0c\u90fd\u5feb\u54ac\u51fa\u8840\u6765\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5c0f\u96c5\uff01\u4f60\u5728\u60f3\u4ec0\u4e48\uff01\u201d \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u90a3\u68a6\u5e7b\u4e4b\u5200\u771f\u53ef\u6068\uff01\u5982\u679c\u4ed6\u4e0d\u5229\u7528\u8ff7\u9999\uff0c\u4e00\u5b9a\u4e0d\u662f\u5c0f\u59d0\u7684\u5bf9\u624b\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u7b11\u5bb9\u663e\u5f97\u6709\u4e9b\u82e6\u6da9\uff0c\u6162\u6162\u5730\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u5c31\u7b97\u4ed6\u4e0d\u7528\u8ff7\u836f\uff0c\u5355\u8bba\u6b66\u529f\uff0c \u6211\u4e5f\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff01\u201d \u3000\u3000\u5c0f\u96c5\u5403\u60ca\u5730\u77aa\u5927\u4e86\u773c\u775b\uff0c\u9053\uff1a\u201c\u5c0f\u59d0\u4f60\u8bf4\u4ec0\u4e48\uff1f\u5c0f\u59d0\u7684\u6b66\u529f\u5df2\u8fbe\u5316\u5883\uff0c\u68a6\u5e7b\u4e4b\u5200\u7684\u6b66\u529f \u600e\u4e48\u53ef\u80fd\u9ad8\u8fc7\u4f60\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5c0f\u96c5\uff0c\u6211\u544a\u8bc9\u4f60\uff0c\u6b66\u529f\u662f\u6ca1\u6709\u6b62\u5883\uff0c\u6b63\u6240\u8c13\u5f3a\u4e2d\u81ea\u6709\u5f3a\u4e2d\u624b\uff0c\u4e00\u5c71\u8fd8\u6bd4\u4e00 \u5c71\u9ad8\u554a\uff0c\u518d\u8bf4\uff0c\u90a3\u4e2a\u4eba\u4e5f\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\uff01\u201d \u3000\u3000\u5c0f\u96c5\u66f4\u662f\u60ca\u8bb6\u4e86\uff0c\u5f20\u5927\u4e86\u5634\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\uff1f\u5c0f\u59d0\u4f60\u77e5\u9053\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u82e6\u7b11\uff0c\u6447\u5934\u3002 \u3000\u3000\u5c0f\u96c5\u7d27\u903c\u7740\u95ee\uff1a\u201c\u90a3\u5c0f\u59d0\u600e\u4e48\u77e5\u9053\u4ed6\u4e00\u5b9a\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\uff1f\u5982\u679c\u4ed6\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\uff0c\u53c8\u600e\u4e48 \u80fd\u9a71\u52a8\u5200\u3001\u8f6e\u3001\u5047\u5c71\u6f2b\u5929\u98de\u821e\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5c0f\u96c5\uff0c\u4f60\u6ce8\u610f\u5230\u6ca1\u6709\uff0c\u90a3\u4e9b\u5200\u3001\u8f6e\u90fd\u662f\u88ab\u4eba\u7528\u5185\u529b\u63a7\u5236\uff0c\u624d\u5411\u6211\u98de\u5c04\u800c\u6765 \u7684\u3002\u201d \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u53ef\u6211\u770b\u5f97\u5f88\u6e05\u695a\uff0c\u90a3\u4e2a\u9ed1\u8863\u8499\u9762\u4eba\u5e76\u6ca1\u6709\u53d1\u52a8\u5185\u529f\u554a\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u53d1\u52a8\u5185\u529f\u63a7\u5236\u5200\u3001\u8f6e\u7684\u4eba\u90fd\u8eb2\u85cf\u5728\u5047\u5c71\u91cc\u3002\u201d \u3000\u3000\u5c0f\u96c5\u5410\u4e86\u5410\u820c\u5934\uff0c\u604d\u7136\u7b11\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u539f\u6765\u5c0f\u59d0\u65e9\u5c31\u770b\u51fa\u6765\u4e86\uff0c\u53ef\u5c31\u7b97\u8fd9\u6837\uff0c\u4e5f\u4e0d \u80fd\u8bc1\u660e\u4ed6\u5c31\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\u554a\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u770b\u7740\u5979\uff0c\u4e00\u4ed8\u6b32\u8a00\u53c8\u6b62\u7684\u6837\u5b50\u3002 \u3000\u3000\u5c0f\u96c5\u6709\u70b9\u4e0d\u9ad8\u5174\u4e86\uff0c\u6485\u8d77\u5c0f\u5634\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4f60\u4e0d\u4fe1\u4efb\u6211\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u8d70\u5230\u95e8\u8fb9\uff0c\u628a\u8033\u6735\u8d34\u5728\u95e8\u4e0a\u4ed4\u7ec6\u5730\u542c\u7740\uff0c\u663e\u7136\u662f\u5728\u67e5\u770b\u623f\u5b50\u5916\u662f\u5426\u6709\u4eba\u5728\u76d1\u542c\u3002 \u7136\u540e\u8d70\u56de\u5c0f\u96c5\u8eab\u8fb9\uff0c\u4f38\u624b\u62c9\u8d77\u5c0f\u96c5\u7684\u5c0f\u624b\uff0c\u9053\uff1a\u201c\u5c0f\u96c5\uff0c\u6211\u7edd\u4e0d\u662f\u4e0d\u4fe1\u4efb\u4f60\uff0c\u53ea\u662f\uff0c\u8fd9\u662f\u4e00 \u4e2a\u5927\u79d8\u5bc6\uff0c\u5176\u95f4\u66f4\u7275\u626f\u5230\u4e00\u4e2a\u5929\u5927\u7684\u9634\u8c0b\uff0c\u771f\u5b9e\u60c5\u51b5\u5230\u5e95\u5982\u4f55\uff0c\u6211\u4e5f\u4e0d\u662f\u5341\u5206\u6e05\u695a\uff0c\u53cd\u6b63\u6211 \u4eec\u8fd9\u6b21\u4e0d\u8fdc\u6570\u5343\u91cc\u6765\u5230\u626c\u5dde\uff0c\u76ee\u7684\u5c31\u662f\u8981\u4fdd\u62a4\u738b\u5927\u4eba\u7684\u5b89\u5168\u3002\u8fd9\u6837\u8ddf\u4f60\u8bf4\u5427\uff0c\u53ea\u8981\u4fdd\u62a4\u597d\u738b \u5927\u4eba\uff0c\u8fd9\u4e2a\u9634\u8c0b\u5f88\u5feb\u4e5f\u5c31\u4f1a\u81ea\u5df1\u66b4\u9732\u51fa\u6765\u7684\uff0c\u5c0f\u96c5\uff0c\u4f60\u61c2\u4e86\u5417\uff1f\u201d \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u96be\u602a\u5c0f\u59d0\u8981\u5c0f\u6587\u59d0\u7528\u6027\u547d\u4fdd\u62a4\u738b\u5927\u4eba\uff01\u5c0f\u96c5\u61c2\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u8f7b\u8f7b\u62cd\u4e86\u62cd\u5c0f\u96c5\u767d\u5ae9\u7684\u5c0f\u8138\uff0c\u5c55\u989c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u96c5\u5b9e\u662f\u4e2a\u597d\u59d1\u5a18\u3002\u201d \u3000\u3000\u5979\u8fd9\u4e00\u7b11\uff0c\u663e\u7136\u7b11\u5f97\u5f88\u8212\u5fc3\uff0c\u66f4\u663e\u5f97\u5979\u5bb9\u989c\u7684\u5a07\u7f8e\uff0c\u5c0f\u96c5\u4f3c\u5df2\u770b\u75f4\u4e86\uff0c\u75f4\u75f4\u5730\u9053\uff1a\u201c\u5c0f \u59d0\uff0c\u4f60\u771f\u7f8e\uff0c\u96be\u602a\u675c\u53f8\u4e3b\u8981\u8bf4\u5c0f\u59d0\u662f\u5c0f\u8c2a\u4eba\u95f4\u7684\u4ed9\u5973\u5462\uff0c\u4f9d\u5c0f\u96c5\u770b\u554a\uff0c\u5c31\u7b97\u662f\u4ed9\u5973\u4e5f\u6bd4\u4e0d\u4e0a \u5c0f\u59d0\u7f8e\u4e3d\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u767d\u7389\u822c\u7684\u8138\u988a\u4e0a\u5ffd\u7136\u5347\u8d77\u4e24\u6735\u6de1\u6de1\u7684\u7ea2\u6655\uff0c\u5979\u5b9a\u4e86\u5b9a\u795e\uff0c\u9053\uff1a\u201c\u5c0f\u96c5\uff0c\u636e\u6211\u63a8\u7b97\uff0c \u90a3\u4e2a\u9ed1\u8863\u8499\u9762\u4eba\u5f88\u5feb\u5c31\u4f1a\u73b0\u8eab\u4e86\uff0c\u4f60\u6015\u4e0d\u6015\uff1f\u201d \u3000\u3000\u5c0f\u96c5\u4e00\u633a\u5a07\u5c0f\u79c0\u7f8e\u7684\u5c0f\u80f8\u812f\uff0c\u9053\uff1a\u201c\u8ddf\u5c0f\u59d0\u5728\u4e00\u8d77\uff0c\u5c0f\u96c5\u4ec0\u4e48\u90fd\u4e0d\u6015\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u63a8\u7b97\u7684\u4e00\u70b9\u4e0d\u5dee\uff0c\u4e0d\u4e00\u4f1a\u513f\uff0c\u4e00\u9635\u811a\u6b65\u58f0\u7531\u8fdc\u53ca\u8fd1\uff0c\u201c\u5431\u5440\u201d\u4e00\u58f0\uff0c\u623f\u95f4\u88ab\u63a8\u5f00\uff0c \u8d70\u8fdb\u6765\u7684\uff0c\u4e0d\u662f\u9ed1\u8863\u8499\u9762\u4eba\uff0c\u53c8\u662f\u8c01\uff1f \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u4e00\u773c\u770b\u89c1\u7a0b\u5c0f\u8776\u548c\u5c0f\u96c5\u90fd\u5df2\u5750\u8d77\uff0c\u800c\u4e14\u795e\u60c5\u770b\u4e0a\u53bb\u5341\u5206\u6e05\u9192\uff0c\u4e0b\u610f\u6014\u4e86\u4e00\u6014\uff0c \u9053\uff1a\u201c\u770b\u6765\uff0c\u8001\u592b\u8fd8\u662f\u6709\u4e9b\u4f4e\u4f30\u4e86\u7a0b\u603b\u6355\u5934\u548c\u5c0f\u96c5\u59d1\u5a18\uff0c\u539f\u6765\u4f60\u4eec\u65e9\u5df2\u9192\u6765\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e0d\u5b88\u8bfa\u8a00\uff0c\u7a81\u65bd\u8ff7\u9999\uff0c\u8fd9\u822c\u4e0b\u4e09\u6ee5\u7684\u884c\u5f84\uff0c\u7adf\u7136\u8fd8\u6709\u8138\u5728\u8fd9\u91cc\u5927\u8a00 \u4e0d\u60ed\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\u4e0d\u8981\u8bef\u4f1a\uff0c\u8001\u592b\u5176\u5b9e\u5e76\u65e0\u52a0\u5bb3\u7a0b\u603b\u6355\u5934\u4e4b\u610f\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u90a3\u4f60\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u8001\u592b\u60f3\u8bf7\u7a0b\u603b\u6355\u5934\u7ed9\u4e00\u4e2a\u4eba\u5e26\u53e5\u8bdd\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u7ed9\u4ec0\u4e48\u4eba\u5e26\u8bdd\uff1f\u5e26\u7684\u53c8\u662f\u600e\u6837\u4e00\u53e5\u8bdd\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u8bf7\u8ba9\u91d1\u767e\u5e74\u4ea4\u51fa\u4e09\u5b9d\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e09\u5b9d\u867d\u7136\u73cd\u5947\uff0c\u53ef\u4e5f\u4e0d\u503c\u5f97\u4f60\u8d39\u5c3d\u5982\u6b64\u5fc3\u673a\u5427\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u4e09\u5b9d\u672c\u5c5e\u8001\u592b\u6240\u6709\uff0c\u6614\u5e74\u88ab\u91d1\u767e\u5e74\u4ee5\u6b3a\u8bc8\u624b\u6bb5\u593a\u8d70\uff0c\u8001\u592b\u9886\u53d6\u56de\u539f\u5c5e \u81ea\u5df1\u7684\u4e1c\u897f\uff0c\u53c8\u6709\u4f55\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u679c\u5982\u4f60\u6240\u8a00\uff0c\u5f53\u7136\u6ca1\u4ec0\u4e48\u4e0d\u5bf9\uff0c\u53ef\u4f60\u4e3a\u4ec0\u4e48\u8981\u5f79\u4f7f\u91d1\u5c0f\u7709\u523a\u6740\u9a6c\u656c\u6587\uff1f\u53c8 \u4e3a\u4ec0\u4e48\u610f\u56fe\u52a0\u5bb3\u738b\u77e5\u5e9c\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u5927\u7b11\u51e0\u58f0\uff0c\u9053\uff1a\u201c\u8001\u592b\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u53ea\u8981\u91d1\u767e\u5e74\u4e0d\u4ea4\u51fa\u4e09\u5b9d\uff0c\u8001\u592b\u8981\u6740\u7684\u53ef \u5c31\u4e0d\u6b62\u738b\u77e5\u5e9c\u4e00\u4eba\u4e86\u3002\u201d\u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u58f0\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u6740\u8c01\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u8001\u592b\u4f1a\u5c60\u5c3d\u626c\u5dde\u5341\u5927\u5bcc\u6237\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5f88\u4e0d\u5c51\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6709\u8fd9\u4e2a\u5b9e\u529b\u5417\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\u5df2\u7ecf\u89c1\u8bc6\u8fc7\u8001\u592b\u68a6\u5e7b\u4e4b\u5200\u7684\u5389\u5bb3\u4e86\uff0c\u4f60\u8bf4\u8001\u592b\u6709\u6ca1\u6709\u8fd9\u4e2a\u5b9e \u529b\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8981\u662f\u6211\u4e0d\u7ed9\u4f60\u5e26\u8bdd\u5462\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u90a3\u7a0b\u603b\u6355\u5934\u5c31\u4e0d\u8981\u602a\u8001\u592b\u5fc3\u72e0\u624b\u8fa3\u4e86\uff01\u201d \u3000\u3000\u8bdd\u97f3\u672a\u843d\uff0c\u9ed1\u8863\u8499\u9762\u4eba\u62e2\u5728\u8896\u4e2d\u7684\u53cc\u624b\u5df2\u7136\u4f38\u51fa\uff0c\u5de6\u624b\u5448\u864e\u722a\uff0c\u53f3\u624b\u53d8\u9e70\u722a\uff0c\u540c\u65f6\u5411\u7a0b \u5c0f\u8776\u80f8\u524d\u75be\u653b\u8fc7\u6765\uff0c\u5e26\u8d77\u4e00\u9635\u68ee\u51b7\u7684\u9634\u98ce\u3002 \u3000\u3000\u201c\u767e\u53d8\u795e\u722a\uff01\u201d\u7a0b\u5c0f\u8776\u60ca\u53f1\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u679c\u7136\u662f\u68a6\u5e7b\u4e4b\u5200\u7684\u6210\u540d\u7edd\u6280\uff01\u201d\u67f3\u8170\u4e00\u95ea\uff0c\u5df2 \u8eb2\u5f00\u53cc\u722a\u7684\u5408\u51fb\uff0c\u6325\u638c\u8fce\u654c\u3002 \u3000\u3000\u5c0f\u96c5\u4e5f\u62bd\u51fa\u5e8a\u8fb9\u7684\u5b9d\u5251\uff0c\u4e0a\u524d\u5939\u653b\u3002 \u3000\u3000\u4ee5\u4e8c\u654c\u4e00\uff0c\u8db3\u8db3\u6253\u8fc7\u4e86\u4e09\u5341\u62db\uff0c\u9ed1\u8863\u8499\u9762\u4eba\u5374\u4ecd\u7136\u8fdb\u9000\u81ea\u5982\uff0c\u4e0d\u7531\u5f97\u5c0f\u96c5\u4e0d\u5fc3\u60ca\u3002 \u3000\u3000\u5c0f\u59d0\u8bf4\u7684\u6ca1\u6709\u9519\uff0c\u8fd9\u4eba\u7684\u6b66\u529f\u679c\u7136\u5947\u9ad8\uff01\u5c0f\u96c5\u5fd6\u9053\uff0c\u5fc3\u5ff5\u5ffd\u95ea\uff0c\u5251\u6cd5\u5df2\u53d8\u5f97\u5947\u5e7b\u7075\u6167\uff0c \u7adf\u50cf\u662f\u5145\u6ee1\u4e86\u9b3c\u6c14\u3001\u9634\u6c14\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u4f3c\u4e4e\u6709\u4e9b\u5403\u60ca\uff0c\u786c\u63a5\u4e86\u51e0\u62db\u540e\uff0c\u8131\u53e3\u7b11\u9053\uff1a\u201c\u5c0f\u59d1\u5a18\u597d\u4fca\u7684\u5251\u6cd5\uff01\u770b\u8001\u592b\u5982 \u4f55\u5bf9\u4ed8\u4f60\uff01\u201d \u3000\u3000\u4ed6\u7684\u62db\u6570\u4e5f\u53d8\u4e86\uff0c\u5ffd\u7136\u4f7f\u51fa\u4e00\u5957\u5c11\u6797\u4f0f\u864e\u7f57\u6c49\u62f3\uff0c\u62db\u62db\u529b\u5927\u65e0\u7a77\uff0c\u6c14\u52bf\u96c4\u4f1f\uff0c\u76f4\u903c\u5f97\u7a0b \u5c0f\u8776\u548c\u5c0f\u96c5\u8fde\u8fde\u540e\u9000\u3002 \u3000\u3000\u6b63\u5728\u8fd9\u5371\u6025\u65f6\u523b\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u51e0\u58f0\u60e8\u53eb\uff0c\u4e00\u9635\u60ca\u547c\u76f4\u568e\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u4e00\u60ca\u56de\u5934\uff0c\u53ea\u89c1\u5899\u5916\u7adf\u7136\u706b\u5149\u51b2\u5929\uff01 \u3000\u3000\u4eba\u5f71\u8fde\u95ea\uff0c\u95e8\u5916\u51b2\u8fdb\u56db\u6761\u9ed1\u8863\u5927\u6c49\uff0c\u624b\u4e2d\u5404\u6301\u91d1\u80cc\u5927\u780d\u5200\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u6709\u5f3a\u654c\u6765 \u88ad\uff0c\u653e\u706b\u70e7\u4e86\u9601\u697c\uff0c\u5f1f\u5144\u4eec\u5df2\u6b7b\u4f24\u5341\u51e0\u4e2a\u4eba\u4e86\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u4e00\u6307\u7a0b\u5c0f\u8776\u548c\u5c0f\u96c5\uff0c\u9053\uff1a\u201c\u4e0d\u80fd\u8ba9\u5979\u4eec\u8dd1\u4e86\uff01\u201d\u4e00\u8f6c\u8eab\uff0c\u98de\u8eab\u8dc3\u51fa\u95e8\u5916\uff0c\u6d88 \u5931\u4e0d\u89c1\u3002 \u3000\u3000\u770b\u6765\uff0c\u4ed6\u662f\u60f3\u5148\u51fb\u9000\u5916\u6765\u4e4b\u654c\uff0c\u518d\u6765\u5bf9\u4ed8\u7a0b\u5c0f\u8776\u548c\u5c0f\u96c5\u3002 \u3000\u3000\u56db\u6761\u9ed1\u8863\u5927\u6c49\u56db\u9762\u4e00\u5206\uff0c\u7adf\u7136\u7ec4\u6210\u4e86\u4e00\u4e2a\u56db\u8c61\u5200\u9635\uff0c\u5404\u6325\u91d1\u80cc\u5927\u5200\uff0c\u5411\u7a0b\u5c0f\u8776\u548c\u5c0f\u96c5\u731b \u653b\u4e0a\u6765\u3002 \u3000\u3000\u8fd9\u56db\u540d\u9ed1\u8863\u5927\u6c49\u5404\u81ea\u7684\u6b66\u529f\u7adf\u7136\u90fd\u5f88\u9ad8\uff0c\u582a\u79f0\u4e00\u6d41\u9ad8\u624b\uff0c\u518d\u52a0\u4e0a\u56db\u8c61\u5200\u9635\u4e4b\u5a01\u529b\uff0c\u7a0b\u5c0f \u8776\u548c\u5c0f\u96c5\u867d\u7136\u5168\u529b\u53cd\u6251\uff0c\u7adf\u7136\u4e00\u65f6\u4e5f\u62ff\u4ed6\u4eec\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u201c\u547c\u201d\uff0c\u201c\u547c\u201d\u4e24\u58f0\u5de8\u54cd\uff0c\u897f\u9762\u7a97\u6237\u88ab\u649e\u5f97\u7c89\u788e\uff0c\u968f\u7740\u56db\u6563\u7684\u788e\u6728\u5c51\uff0c\u51b2\u8fdb \u4e24\u4e2a\u5f6a\u5f62\u5927\u6c49\u6765\uff01 \u3000\u3000\u201c\u963f\u6a2a\uff01\u963f\u4fdd\uff01\u201d\u5c0f\u96c5\u4e00\u9762\u6325\u5251\u653b\u654c\uff0c\u4e00\u8fb9\u5174\u594b\u5730\u53eb\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u4eec\uff01\u4f60\u4eec\u6765\u5f97\u592a\u597d \u4e86\uff01\u201d \u3000\u3000\u963f\u6a2a\u3001\u963f\u4fdd\u51b2\u5979\u7b11\u4e86\u4e00\u7b11\uff0c\u56db\u53ea\u9524\u5934\u5927\u7684\u94c1\u62f3\u5de6\u6253\u53f3\u51fb\uff0c\u4e09\u62db\u4e24\u5f0f\u4fbf\u5c06\u4e24\u540d\u9ed1\u8863\u5200\u624b\u6253 \u5f97\u808b\u9aa8\u5bf8\u65ad\uff0c\u53e3\u5410\u9c9c\u8840\u800c\u4ea1\u3002 \u3000\u3000\u5c0f\u96c5\u5251\u6c14\u5927\u76db\uff0c\u4e00\u7728\u773c\u95f4\u4e5f\u5c06\u53e6\u5916\u4e24\u540d\u5200\u624b\u523a\u7ffb\u5728\u5730\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u963f\u6a2a\u3001\u963f\u4fdd\uff0c\u4f60\u4eec\u600e\u4e48\u77e5\u9053\u6211\u4eec\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u963f\u6a2a\u8eac\u8eab\u9053\uff1a\u201c\u5c5e\u4e0b\u6628\u65e5\u4e00\u76f4\u6697\u4e2d\u8ddf\u5728\u603b\u6355\u5934\u540e\u9762\uff0c\u53d1\u73b0\u6709\u4eba\u8ddf\u8e2a\u603b\u6355\u5934\uff0c\u5c31\u4e00\u8def\u8ddf\u4e86 \u8fc7\u6765\uff0c\u540e\u6765\u89c1\u5230\u603b\u6355\u5934\u9047\u9669\uff0c\u4fbf\u8fd4\u56de\u53eb\u963f\u4fdd\u5144\u5f1f\uff0c\u4e00\u9f50\u8d76\u6765\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e8c\u4eba\u90fd\u6765\u4e86\uff0c\u8ba9\u4f60\u4eec\u4fdd\u62a4\u7684\u4eba\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u963f\u4fdd\u9053\uff1a\u201c\u5c5e\u4e0b\u89c1\u90a3\u91cc\u6212\u5907\u68ee\u4e25\uff0c\u6740\u624b\u5c31\u7b97\u6709\u6240\u56fe\uff0c\u53ea\u6015\u4e5f\u5f88\u96be\u5f97\u624b\u7684\u3002\u201d\u7a0b\u5c0f\u8776\u9053\uff1a \u201c\u6740\u624b\u4eec\u7684\u5b9e\u529b\u8fdc\u8fdc\u8d85\u8fc7\u6211\u7684\u9884\u60f3\uff0c\u624b\u6bb5\u4e5f\u5341\u5206\u8be1\u79d8\u6bd2\u8fa3\uff0c\u4f60\u4eec\u8fd8\u662f\u8981\u5c0f\u5fc3\u8c28\u614e\u4e3a\u4e0a\uff0c\u4e0d\u53ef \u5927\u610f\uff01\u201d \u3000\u3000\u963f\u6a2a\u3001\u963f\u4fdd\u9f50\u58f0\u9053\uff1a\u201c\u5c5e\u4e0b\u9075\u547d\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6b64\u5730\u4e0d\u5b9c\u4e45\u7559\uff0c\u6211\u4eec\u5feb\u8d70\u5427\uff01\u201d \u3000\u3000\u65b9\u624d\u98de\u8eab\u63a0\u8d77\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u56db\u5927\u6355\u5934\u53bb\u611a\u516c\u56ed\u6797\u67e5\u8bbf\uff0c\u4e0d\u77e5\u7ed3\u679c\u5982\u4f55\uff1f\u201d\u963f\u6a2a\u9053\uff1a\u201c\u56db \u5927\u6355\u5934\u5728\u611a\u516c\u56ed\u6797\u4e0e\u8840\u624b\u65b9\u8f6e\u624b\u4e0b\u4e00\u9635\u8840\u6218\uff0c\u83b7\u80dc\u540e\u8d76\u56de\u5e9c\u8859\uff0c\u4e0d\u6599\u4f17\u591a\u6740\u624b\u6b63\u56f4\u653b\u5e9c\u8859\u4e8c \u5802\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u60ca\uff0c\u6025\u6025\u9053\uff1a\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u4ed6\u4eec\u7adf\u6562\u516c\u7136\u56f4\u653b\u5e9c\u8859\uff1f\u738b\u5927\u4eba\u6ca1\u6709\u51fa\u610f\u5916\u5427\uff1f\u201d \u3000\u3000\u963f\u4fdd\u9053\uff1a\u201c\u5177\u4f53\u8be6\u60c5\u5c5e\u4e0b\u7b49\u4e5f\u4e0d\u592a\u6e05\u695a\uff0c\u4e0d\u8fc7\u738b\u5927\u4eba\u5b89\u7136\u65e0\u6059\uff0c\u53ef\u4ee5\u786e\u5b9a\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u76b1\u7709\u9053\uff1a\u201c\u771f\u6ca1\u60f3\u5230\u77ed\u77ed\u4e24\u5929\u65f6\u95f4\uff0c\u7adf\u51fa\u4e86\u8fd9\u4e48\u5927\u53d8\u6545\uff01\u963f\u6a2a\u3001\u963f\u4fdd\uff0c\u4f60\u4eec\u5c3d\u5feb \u8d76\u56de\u539f\u5730\uff0c\u4e25\u5bc6\u9632\u62a4\uff0c\u5c0f\u96c5\uff0c\u6211\u4eec\u5feb\u56de\u5e9c\u8859\u53bb\uff01\u201d \u3000\u3000\u56db\u4eba\u540c\u65f6\u98de\u8eab\u800c\u8d77\uff0c\u5206\u522b\u81ea\u95e8\u7a97\u63a0\u51fa\uff0c\u6d88\u5931\u5728\u534a\u7a7a\u4e2d\u3002 \u3000\u3000------------- \u3000\u3000\u5b64\u5251\u751f  \u626b\u63cf\u3000  cndy001 \u6731\u836b\u9636  OCR  \uff0c\u65e7\u96e8\u697c\u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u5341\u516b\u7ae0 \u3000\u3000\u56db\u5927\u6355\u5934\u7684\u4f24\u90fd\u4e0d\u662f\u5f88\u4e25\u91cd\uff0c\u56e0\u4e3a\u4ed6\u4eec\u6240\u53d7\u7684\u90fd\u662f\u76ae\u8089\u4e4b\u4f24\uff0c\u5185\u529f\u5e76\u672a\u53d7\u635f\uff0c\u53ea\u662f\u672a\u80fd \u53ca\u65f6\u5305\u624e\u6b62\u8840\uff0c\u6d41\u8840\u8fc7\u591a\uff0c\u4eba\u663e\u5f97\u6709\u6c14\u65e0\u529b\uff0c\u8eab\u4f53\u865a\u5f31\u800c\u5df2\u3002 \u3000\u3000\u7ecf\u8fc7\u4e00\u5929\u7684\u4f11\u517b\uff0c\u4ed6\u4eec\u7684\u4f53\u80fd\u5927\u90fd\u5df2\u7ecf\u6062\u590d\u3002 \u3000\u3000\u4f46\u662f\u5c0f\u6587\u7684\u4f24\u5374\u6781\u4e25\u91cd\uff0c\u4e0d\u4ec5\u5de6\u80a9\u88ab\u957f\u5251\u7a7f\u900f\uff0c\u770b\u6837\u5b50\u8fd8\u53d7\u4e86\u4e25\u91cd\u7684\u5185\u4f24\u3002\u5982\u679c\u80fd\u591f\u7528 \u89e3\u836f\u5316\u5f00\u60dc\u7389\u5bf9\u5979\u65bd\u653e\u7684\u8ff7\u836f\u4e4b\u6548\uff0c\u7531\u5979\u81ea\u5df1\u8fd0\u529f\u8c03\u606f\uff0c\u5979\u7684\u4f24\u53ea\u6015\u4f1a\u597d\u7684\u5feb\u4e00\u4e9b\uff0c\u53ea\u662f\u60dc \u7389\u53ca\u56db\u5927\u6355\u5934\u90fd\u62c5\u5fc3\u5979\u9192\u6765\u540e\u53c8\u8981\u5bf9\u738b\u5c11\u537f\u884c\u51f6\uff0c\u4e0d\u6562\u7528\u89e3\u836f\u5c06\u5979\u7684\u8ff7\u836f\u89e3\u5f00\u3002 \u3000\u3000\u5c0f\u6587\u6240\u656c\u670d\u7684\uff0c\u771f\u6b63\u8bf4\u8d77\u6765\u53ea\u6709\u603b\u6355\u5934\u4e00\u4eba\uff0c\u770b\u6837\u5b50\uff0c\u53ea\u6709\u603b\u6355\u5934\u56de\u6765\u4e86\uff0c\u624d\u80fd\u6ca1\u6cd5\u5316 \u89e3\u5979\u5fc3\u4e2d\u7684\u51f6\u608d\u4e4b\u6c14\u3002 \u3000\u3000\u201c\u5c0f\u6587\u5185\u5fc3\u7684\u51f6\u608d\u4e4b\u6c14\u771f\u7684\u662f\u56e0\u4e3a\u5979\u6240\u7ec3\u7684\u2018\u5251\u6d77\u6d74\u9b42\u2019\u5251\u6cd5\u5417\uff1f\u201d\u60dc\u7389\u4e00\u76f4\u5728\u60f3\u8fd9\u4e2a \u95ee\u9898\u3002 \u3000\u3000\u5979\u89c9\u5f97\u4e0e\u201c\u5251\u6d77\u6d74\u9b42\u201d\u5251\u6cd5\u4f1a\u6709\u4e00\u5b9a\u7684\u5173\u7cfb\uff0c\u4f46\u7edd\u975e\u4e3b\u8981\u539f\u56e0\u3002 \u3000\u3000\u770b\u5c0f\u6587\u7684\u60c5\u5f62\uff0c\u5e94\u8be5\u662f\u4e2d\u4e86\u67d0\u79cd\u5947\u5f02\u7684\u7981\u5236\uff0c\u6216\u662f\u8ff7\u836f\uff0c\u6216\u662f\u67d0\u79cd\u90aa\u672f\uff0c\u4f46\u60dc\u7389\u4e0d\u6562\u80af \u5b9a\u3002 \u3000\u3000\u867d\u7136\u5979\u662f\u51b7\u9762\u795e\u533b\u8c2d\u6267\u4e2d\u4e4b\u5973\uff0c\u795e\u533b\u95e8\u7684\u7edd\u6280\u4e5f\u90fd\u70c2\u719f\u4e8e\u5fc3\uff0c\u4f46\u5230\u5e95\u4ec5\u662f\u7eb8\u4e0a\u8c08\u5175\uff0c\u6ca1 \u6709\u4efb\u4f55\u5b9e\u9645\u7684\u7ecf\u9a8c\u3002 \u3000\u3000\u4e00\u5929\u6765\uff0c\u60dc\u7389\u4e00\u76f4\u5750\u5728\u5e8a\u8fb9\uff0c\u966a\u7740\u660f\u8ff7\u4e0d\u9192\u7684\u5c0f\u6587\uff0c\u5979\u4e00\u76f4\u5728\u8111\u5b50\u91cc\u5bfb\u601d\u7740\u4ee5\u524d\u6240\u5b66\u8fc7 \u7684\u79cd\u79cd\u836f\u7406\u533b\u9053\uff0c\u4f46\u4e00\u65f6\u95f4\u8fd8\u662f\u60f3\u4e0d\u51fa\u4e00\u4e2a\u6551\u5c0f\u6587\u7684\u529e\u6cd5\u3002 \u3000\u3000\u77e5\u5e9c\u738b\u5c11\u537f\u73b0\u5728\u7684\u4f4f\u5904\u5c31\u5728\u5c0f\u6587\u9694\u58c1\u7684\u4e00\u95f4\u623f\u95f4\uff0c\u4ed6\u6bcf\u8fc7\u4e00\u4f1a\u513f\u5c31\u4f1a\u8fc7\u6765\u770b\u4e00\u770b\u5c0f\u6587\uff0c \u6025\u5f97\u76f4\u53f9\u6c14\u3002 \u3000\u3000\u738b\u5c11\u537f\u4e00\u76f4\u8ba4\u4e3a\u662f\u56e0\u4e3a\u81ea\u5df1\u5bf9\u5c0f\u6587\u5b58\u6709\u8f7b\u89c6\u4e4b\u5fc3\uff0c\u624d\u5f15\u53d1\u4e86\u5c0f\u6587\u7684\u51f6\u608d\u4e4b\u6c14\uff0c\u6240\u4ee5\u5fc3\u91cc \u4e00\u76f4\u5f88\u5185\u759a\u3002 \u3000\u3000\u201c\u738b\u5927\u4eba\u771f\u662f\u4e00\u4e2a\u5fc3\u5730\u6df3\u539a\u7684\u541b\u5b50\u554a\uff01\u201d\u60dc\u7389\u60f3\uff0c\u201c\u8fd9\u4e48\u4e00\u4e2a\u597d\u5b98\u3001\u597d\u4eba\uff0c\u4e00\u5b9a\u4e0d\u80fd\u8ba9 \u4ed6\u88ab\u6740\u624b\u4f24\u5bb3\uff01\u867d\u7136\u5c0f\u6587\u59d0\u53d7\u4f24\u4e86\uff0c\u4f46\u6211\u62fc\u4e86\u8fd9\u6761\u547d\uff0c\u4e5f\u8981\u4e0e\u675c\u5927\u54e5\u4ed6\u4eec\u4e00\u8d77\u4fdd\u62a4\u4ed6\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u53c8\u8d70\u8fdb\u4e86\u623f\u95f4\uff0c\u4e00\u76f4\u8d70\u5230\u5e8a\u8fb9\uff0c\u770b\u7740\u5c0f\u6587\u82cd\u767d\u7684\u5c0f\u8138\uff0c\u95ee\u9053\uff1a\u201c\u5979\u8fd8\u4e0d\u89c1\u597d\u8f6c \u5417\uff1f\u201d \u3000\u3000\u60dc\u7389\u5782\u9996\u9053\uff1a\u201c\u662f\u7684\uff0c\u5927\u4eba\u653e\u5fc3\uff0c\u60dc\u7389\u6b63\u5728\u60f3\u529e\u6cd5\u6551\u5979\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u53f9\u53f9\u6c14\u9053\uff1a\u201c\u5509\uff01\u603b\u6355\u5934\u4e00\u53bb\u4e24\u5929\u6ca1\u6709\u6d88\u606f\uff0c\u4e5f\u4e0d\u77e5\u662f\u5426\u9047\u4e0a\u4e86\u610f\u5916\u60c5\u51b5\uff0c\u5982\u679c \u6709\u5979\u5728\uff0c\u5c0f\u6587\u4e5f\u4e0d\u4f1a\u2026\u2026\u201d \u3000\u3000\u60dc\u7389\u773c\u5708\u5df2\u6709\u70b9\u7ea2\u4e86\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u5927\u4eba\u4e5f\u8be5\u4fdd\u91cd\u8eab\u4f53\u5440\uff01\u8fd9\u91cc\u6709\u6211\u7167\u770b\uff0c\u5927\u4eba\u4f11\u606f\u4e00\u4f1a \u53bb\u5427\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u53f9\u6c14\u9053\uff1a\u201c\u7761\u4e0d\u7740\u554a\uff01\u60dc\u7389\u59d1\u5a18\uff0c\u4f60\u4e5f\u53bb\u4f11\u606f\u4f11\u606f\u5427\uff01\u675c\u53f8\u4e3b\u4f24\u8fd8\u672a\u75ca\u6108\uff0c\u4f60\u4e5f \u8be5\u53bb\u770b\u770b\uff0c\u6211\u5728\u8fd9\u91cc\u966a\u5c0f\u6587\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u5927\u4eba\uff0c\u8fd9\u6837\u6015\u4e0d\u59a5\u5427\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u59a5\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u4e07\u4e00\u5c0f\u6587\u59d0\u9192\u6765\uff0c\u53c8\u8981\u5bf9\u5927\u4eba\u6709\u6240\u4e0d\u5229\u2026\u2026\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u89e3\u94c3\u8fd8\u987b\u7cfb\u94c3\u4eba\uff0c\u6b64\u4e8b\u56e0\u6211\u800c\u8d77\uff0c\u4e07\u4e00\u5c0f\u6587\u59d1\u5a18\u9192\u6765\uff0c\u738b\u67d0\u81ea\u4f1a\u4ee5\u8bda\u5fc3\u5316 \u89e3\u5979\u5fc3\u4e2d\u4e4b\u4e0d\u5e73\u3002\u201d \u3000\u3000\u60dc\u7389\u611f\u52a8\u5730\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5927\u4eba\u7559\u5728\u8fd9\u91cc\u5427\uff0c\u60dc\u7389\u544a\u9000\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u51fa\u95e8\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u91cc\u662f\u5e9c\u8859\u7684\u5185\u5802\u540e\u9662\uff0c\u73b0\u5728\uff0c\u94c1\u7fce\u5df2\u5c06\u5e9c\u8859\u5185\u6240\u6709\u80fd\u8c03\u96c6\u7684\u4eba\u624b\u5168\u90fd\u96c6\u4e2d\u5230\u4e86\u8fd9\u91cc\u3002 \u3000\u3000\u94c1\u7fce\u7684\u60f3\u6cd5\u662f\uff0c\u5e9c\u8859\u592a\u5927\u4e86\uff0c\u8981\u60f3\u5168\u9762\u9632\u5b88\uff0c\u4eba\u624b\u80af\u5b9a\u662f\u4e0d\u591f\u7684\uff0c\u4e0d\u5982\u5168\u90e8\u9000\u5165\u5185\u5802\uff0c \u96c6\u4e2d\u4eba\u529b\uff0c\u4ee5\u6c42\u4e0e\u6740\u624b\u4eec\u4e00\u640f\u3002 \u3000\u3000\u77e5\u5e9c\u738b\u5c11\u537f\u4f4f\u623f\u4e24\u4fa7\u7684\u53a2\u623f\u91cc\uff0c\u5206\u522b\u7531\u4f53\u529b\u5df2\u6709\u6240\u6062\u590d\u7684\u675c\u671b\u6708\u7b49\u56db\u5927\u6355\u5934\u628a\u5b88\uff0c\u6b66\u5f53 \u5f1f\u5b50\u77f3\u742a\u3001\u5f20\u9e9f\u5206\u5b88\u738b\u77e5\u5e9c\u4f4f\u623f\u7684\u95e8\u7a97\uff0c\u91d1\u5e9c\u6d3e\u6765\u652f\u63f4\u7684\u5feb\u5200\u738b\u526a\u3001\u94c1\u62f3\u4e25\u65b9\u628a\u5b88\u540e\u9662\u9662\u95e8\uff0c \u94c1\u7fce\u4eb2\u7387\u6570\u5341\u540d\u8859\u5f79\u5175\u4e01\uff0c\u5728\u540e\u9662\u56db\u5468\u5e03\u9632\u3001\u5de1\u903b\uff0c\u5c06\u540e\u9662\u56f4\u4e86\u4e2a\u6c34\u6cc4\u4e0d\u901a\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u548c\u5c0f\u96c5\u4e00\u8fdb\u5e9c\u8859\uff0c\u4fbf\u77e5\u9053\u5f62\u52bf\u4e4b\u4e25\u91cd\uff0c\u6bd4\u5979\u7684\u9884\u60f3\u72b9\u6709\u8fc7\u4e4b\u3002 \u3000\u3000\u82e5\u5927\u4e00\u4e2a\u5e9c\u8859\uff0c\u7adf\u7136\u7a7a\u7a7a\u8361\u8361\u7684\uff0c\u4e0d\u95fb\u4e00\u70b9\u4eba\u58f0\u3002\u770b\u6765\u771f\u662f\u51fa\u5927\u4e8b\u4e86\u3002 \u3000\u3000\u8d70\u8fc7\u4e8c\u5802\uff0c\u7a0b\u5c0f\u8776\u8fce\u9762\u649e\u4e0a\u4e86\u6b63\u6301\u5200\u673a\u8b66\u5730\u56db\u4e0b\u5de1\u89c6\u7684\u94c1\u7fce\u3002 \u3000\u3000\u94c1\u7fce\u4e00\u89c1\u7a0b\u5c0f\u8776\uff0c\u987f\u65f6\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u75be\u6b65\u8fce\u4e86\u4e0a\u6765\uff0c\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u4f60\u4eec\u53ef\u56de\u6765\u4e86\uff01 \u94c1\u67d0\u653e\u5fc3\u4e86\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u653e\u773c\u56db\u4e0b\u4e00\u770b\uff0c\u9053\uff1a\u201c\u542c\u8bf4\u5e9c\u8859\u5185\u6709\u6740\u624b\u5927\u4e3e\u95ef\u5165\uff0c\u738b\u5927\u4eba\u5462\uff1f\u6ca1\u51fa\u4e8b\u5427\uff1f\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u738b\u5927\u4eba\u6b63\u5728\u540e\u9662\u76f8\u5019\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e3e\u8d77\u6b65\u5c31\u5411\u540e\u9662\u8d70\u53bb\u3002 \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u4f60\u6700\u597d\u4e0d\u8981\u4ece\u9662\u95e8\u8fdb\u53bb\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u56e0\u4e3a\u9632\u5b88\u9662\u95e8\u7684\uff0c\u662f\u91d1\u767e\u5e74\u6d3e\u6765\u652f\u63f4\u7684\u5feb\u5200\u738b\u526a\u3001\u94c1\u62f3\u4e25\u65b9\uff0c\u94c1\u67d0\u6015\u6cc4\u9732\u4e86 \u603b\u6355\u5934\u7684\u8eab\u5206\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6447\u4e00\u6447\u5934\uff0c\u9053\uff1a\u201c\u5df2\u7ecf\u6ca1\u4ec0\u4e48\u5173\u7cfb\u4e86\uff0c\u6211\u4eec\u7684\u8eab\u5206\u65e9\u5df2\u6cc4\u9732\uff0c\u884c\u8e2a\u4e5f\u4e00\u76f4\u5728\u654c\u4eba \u638c\u63e1\u4e4b\u4e2d\u5462\uff01\u201d \u3000\u3000\u94c1\u7fce\u5927\u5403\u4e00\u60ca\uff0c\u201c\u8fd9\u53ef\u80fd\u5417\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u95ee\uff0c\u4e09\u4eba\u5df2\u5230\u540e\u9662\u9662\u95e8\u5916\u3002 \u3000\u3000\u5feb\u5200\u738b\u526a\u3001\u94c1\u62f3\u4e25\u65b9\u770b\u89c1\u94c1\u7fce\u966a\u7740\u4e24\u4f4d\u8c8c\u7f8e\u5982\u82b1\u7684\u5e74\u8f7b\u5973\u5b50\u4e00\u8d77\u8d70\u8fd1\uff0c\u4e0d\u89c9\u90fd\u662f\u4e00\u6014\uff0c \u773c\u4e2d\u4e5f\u6709\u4e00\u62b9\u7591\u60d1\u4e4b\u8272\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5411\u94c1\u7fce\u4f7f\u4e86\u4e2a\u773c\u8272\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u662f\uff1f\u201d \u3000\u3000\u94c1\u7fce\u5fc3\u9886\u795e\u4f1a\uff0c\u5fd9\u9053\uff1a\u201c\u4e24\u4f4d\u4ec1\u5144\uff0c\u8fd9\u4f4d\u5c31\u662f\u5211\u90e8\u603b\u6355\u53f8\u6355\u5934\u7a0b\u5927\u4eba\u7a0b\u603b\u6355\u5934\uff0c\u4ed6\u4eec\u662f \u5feb\u5200\u738b\u526a\u548c\u94c1\u62f3\u4e25\u65b9\uff0c\u662f\u91d1\u5927\u4eba\u7279\u610f\u6d3e\u6765\u4fdd\u62a4\u738b\u5927\u4eba\u7684\u3002\u201d \u3000\u3000\u5feb\u5200\u738b\u526a\u3001\u94c1\u62f3\u4e25\u65b9\u4e00\u9f50\u62f1\u624b\u4e3a\u793c\uff0c\u9053\uff1a\u201c\u89c1\u8fc7\u7a0b\u603b\u6355\u5934\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e24\u4f4d\u4e49\u58eb\u4e0d\u5fc5\u591a\u793c\uff0c\u4f60\u4eec\u90fd\u968f\u6211\u6765\u5427\uff01\u201d \u3000\u3000\u738b\u3001\u94c1\u4e8c\u4eba\u8ddf\u5728\u7a0b\u5c0f\u8776\u3001\u5c0f\u96c5\u548c\u94c1\u7fce\u8eab\u540e\uff0c\u4e00\u9f50\u8d70\u8fdb\u540e\u9662\uff0c\u60dc\u7389\u65e9\u5df2\u51b2\u4e86\u4e0a\u6765\uff0c\u7ea2\u7740\u773c \u5708\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u5c0f\u6587\u59d0\u5979\u2026\u2026\u5979\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5c0f\u6587\u600e\u4e48\u5566\uff1f\u5c0f\u96c5\uff0c\u5feb\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u4e0d\u7b49\u5979\u7684\u8bdd\u8bf4\u5b8c\uff0c\u5c0f\u96c5\u65e9\u5df2\u62c9\u7740\u60dc\u7389\uff0c\u5982\u98de\u5954\u53bb\u3002 \u3000\u3000\u738b\u5c11\u537f\u95fb\u58f0\u8d76\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u4f60\u53ef\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u4ed6\u4e00\u773c\u770b\u89c1\u7a0b\u5c0f\u8776\u8eab\u540e\u7684\u5feb\u5200\u738b\u526a\u548c\u94c1\u62f3\u4e25\u65b9\uff0c\u8138\u8272\u4e0d\u7981\u5fae\u5fae\u4e00\u53d8\uff0c\u4f46\u5f88\u5feb\u5730\u53c8\u6062\u590d\u4e86 \u5e38\u6001\u3002 \u3000\u3000\u201c\u597d\u4e00\u4e2a\u738b\u5927\u4eba\uff0c\u517b\u6c14\u4e4b\u529f\u679c\u7136\u6df1\u539a\uff0c\u80fd\u5904\u53d8\u4e0d\u60ca\uff0c\u679c\u7136\u662f\u4e00\u4e2a\u4eba\u624d\u554a\uff01\u96be\u5f97\u7684\u4eba\u624d \u554a\uff01\u201d\u7a0b\u5c0f\u8776\u5fc3\u91cc\u6697\u5fd6\uff0c\u53e3\u4e2d\u5374\u6de1\u6de1\u5730\u9053\uff1a\u201c\u738b\u5927\u4eba\u53d7\u60ca\u4e86\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u738b\u67d0\u5012\u662f\u6ca1\u4ec0\u4e48\uff0c\u53ea\u662f\u5c0f\u6587\u59d1\u5a18\u4e3a\u4e86\u4fdd\u62a4\u6211\uff0c\u4e0e\u654c\u4eba\u5c55\u5f00\u6b8a\u6b7b\u640f\u6740\uff0c\u4e0d\u5e78 \u8eab\u53d7\u91cd\u4f24\u554a\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u770b\u7740\u4ed6\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e00\u5207\u6709\u6211\uff0c\u738b\u5927\u4eba\u4e0d\u5fc5\u62c5\u5fc3\u3002\u201d \u3000\u3000\u5211\u623f\u5e08\u7237\u5f20\u5b9d\u5584\u62a2\u4e0a\u4e24\u6b65\uff0c\u9053\uff1a\u201c\u8bf7\u7a0b\u603b\u6355\u5934\u5385\u5185\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4e0d\u9519\u4e0d\u9519\uff0c\u738b\u67d0\u90fd\u6025\u7cca\u6d82\u4e86\uff0c\u7a0b\u603b\u6355\u5934\u5343\u4e07\u4e0d\u8981\u89c1\u602a\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u839e\u5c14\u4e00\u7b11\uff0c\u4e3e\u6b65\u8d70\u8fdb\u540e\u9662\u7684\u6b63\u5385\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\u6b63\u5728\u5385\u4e2d\u5c0f\u5750\u8bae\u4e8b\uff0c\u89c1\u5230\u603b\u6355\u5934\u5b89\u7136\u8fd4\u56de\uff0c\u81ea\u7136\u6781\u4e3a\u9ad8\u5174\uff0c\u53ea\u662f\u89c1\u5230\u5feb\u5200\u738b\u526a\u3001 \u94c1\u62f3\u4e25\u65b9\u4e5f\u8ddf\u4e86\u8fdb\u6765\uff0c\u56db\u4eba\u90fd\u6709\u4e9b\u5403\u60ca\uff0c\u4f46\u4e5f\u6781\u5feb\u5730\u6062\u590d\u4e86\u5e38\u6001\uff0c\u53ea\u6709\u6700\u4e3a\u5403\u60ca\u7684\u675c\u671b\u6708\u72e0 \u72e0\u76ef\u4e86\u94c1\u7fce\u4e00\u773c\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u66fe\u544a\u8beb\u8fc7\u94c1\u7fce\uff0c\u51b3\u4e0d\u80fd\u8ba9\u738b\u3001\u94c1\u4e8c\u4eba\u77e5\u9053\u5211\u90e8\u603b\u6355\u5934\u548c\u56db\u5927\u6355\u5934\u5df2\u9f50\u4f1a\u626c\u5dde\u529e\u4e8b \u7684\u6d88\u606f\u554a\uff01 \u3000\u3000\u94c1\u7fce\u6084\u6084\u4e00\u6307\u7a0b\u5c0f\u8776\uff0c\u8138\u4e0a\u505a\u4e86\u4e2a\u5f88\u65e0\u5948\u7684\u8868\u60c5\u3002 \u3000\u3000\u675c\u671b\u6708\u8fd9\u624d\u660e\u767d\u8fd9\u662f\u603b\u6355\u5934\u7684\u610f\u601d\uff0c\u9762\u4e0a\u4e25\u5389\u7684\u8868\u60c5\u624d\u7f13\u548c\u4e0b\u6765\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u81ea\u987e\u8d70\u5230\u4e00\u5f20\u6905\u5b50\u4e0a\u5750\u4e0b\uff0c\u5f00\u53e3\u4fbf\u9053\uff1a\u201c\u6211\u548c\u5c0f\u96c5\u9047\u4e0a\u4e86\u68a6\u5e7b\u4e4b\u5200\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c \u56db\u5ea7\u7686\u60ca\u3002 \u3000\u3000\u4f46\u56db\u5927\u6355\u5934\uff0c\u738b\u77e5\u5e9c\u3001\u5f20\u5b9d\u5584\u548c\u94c1\u7fce\u7684\u6db5\u517b\u90fd\u5f88\u597d\uff0c\u800c\u5feb\u5200\u3001\u94c1\u62f3\u4e8c\u4eba\u7684\u795e\u60c5\u987f\u65f6\u5927\u53d8 \u8d77\u6765\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u76ee\u5149\u56db\u4e0b\u73af\u987e\u7740\uff0c\u81ea\u6bcf\u4e00\u4e2a\u4eba\u7684\u8138\u4e0a\u626b\u8fc7\uff0c\u53c8\u9053\uff1a\u201c\u8bf7\u8fd9\u4e8c\u4f4d\u4e49\u58eb\u901f\u56de\u91d1\u5e9c\uff0c\u8f6c \u544a\u4f60\u5bb6\u4e1c\u4e3b\uff0c\u5c31\u8bf4\u5211\u90e8\u603b\u6355\u5934\u611f\u8c22\u4ed6\u7684\u9ad8\u60c5\u539a\u610f\uff0c\u8fd8\u8981\u544a\u8bc9\u4ed6\uff0c\u68a6\u5e7b\u4e4b\u5200\u626c\u8a00\u4e00\u5b9a\u8981\u593a\u53d6\u4e09 \u5b9d\uff0c\u6240\u4ee5\u8ba9\u4f60\u5bb6\u4e1c\u4e3b\u8fd1\u671f\u4e00\u5b9a\u8981\u5728\u5e9c\u4e2d\u4e25\u52a0\u9632\u5907\uff0c\u4f60\u4eec\u4e5f\u56de\u91d1\u5e9c\u53bb\u52a9\u4e1c\u4e3b\u4e00\u81c2\u4e4b\u529b\u5427\uff01\u201d \u3000\u3000\u5feb\u5200\u738b\u526a\u3001\u94c1\u62f3\u4e25\u65b9\u5bf9\u89c6\u4e86\u4e00\u773c\uff0c\u9762\u4e0a\u60ca\u60e7\u4e4b\u8272\u66f4\u6d53\uff0c\u4e00\u9f50\u8eac\u8eab\u8bf4\u9053\uff1a\u201c\u8c22\u603b\u6355\u5934\u3002\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u4e8c\u4eba\u8f6c\u8eab\u98de\u5feb\u5730\u79bb\u53bb\u3002 \u3000\u3000\u4e8c\u4eba\u80cc\u5f71\u521a\u4e00\u6d88\u5931\uff0c\u675c\u671b\u6708\u5c31\u6025\u7740\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4e3a\u4ec0\u4e48\u8981\u8ba9\u8fd9\u4e8c\u4eba\u77e5\u9053\u4f60\u7684\u8eab\u5206\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u521a\u624d\u6211\u5df2\u7ecf\u5bf9\u94c1\u6355\u5934\u8bf4\u8fc7\u4e86\uff0c\u6211\u4eec\u7684\u8eab\u5206\u65e9\u5df2\u66b4\u9732\uff0c\u800c\u4e14\u884c\u52a8\u4e5f\u4e00\u76f4\u5728\u654c \u65b9\u638c\u63e1\u4e4b\u4e2d\u3002\u4ece\u73b0\u5728\u8d77\uff0c\u6211\u4eec\u5fc5\u987b\u4ece\u6697\u5904\u8d70\u5230\u660e\u5904\u6765\u4e86\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u771f\u7684\u9047\u4e0a\u4e86\u68a6\u5e7b\u4e4b\u5200\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u4e5f\u6025\u9053\uff1a\u201c\u4ed6\u7684\u6b66\u529f\u771f\u7684\u6709\u4f20\u8bf4\u4e2d\u90a3\u6837\u795e\u4e4e\u5176\u795e\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5446\u4e00\u4f1a\u513f\u518d\u8bf4\u3002\u6211\u8981\u5148\u53bb\u770b\u770b\u5c0f\u6587\u7684\u4f24\u52bf\uff0c\u7136\u540e\u4f60\u4eec\u518d\u7ed9 \u6211\u8be6\u7ec6\u4ecb\u7ecd\u4e00\u4e0b\u8fd9\u4e24\u5929\u5e9c\u8859\u91cc\u53d1\u751f\u7684\u79cd\u79cd\u60c5\u51b5\u548c\u6765\u4eba\u7684\u8eab\u5206\u3002\u201d\u5979\u7ad9\u8d77\u8eab\uff0c\u5f80\u5916\u8d70\u4e86\u51e0\u6b65\uff0c \u5ffd\u53c8\u505c\u4f4f\uff0c\u5173\u5207\u5730\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u53d7\u4e86\u4f24\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u7b11\u9053\uff1a\u201c\u4e00\u70b9\u76ae\u8089\u4e4b\u4f24\uff0c\u6ca1\u4ec0\u4e48\u3002\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u4e5f\u7b11\u9053\uff1a\u201c\u5c31\u662f\uff0c\u51f6\u624b\u4eec\u6562\u518d\u6765\uff0c\u4e8e\u67d0\u8fd8\u80fd\u6740\u4ed6\u4e2a\u5c38\u6a2a\u904d\u5730\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u4e0d\u53ef\u592a\u5927\u610f\u4e86\uff0c\u65e2\u7136\u53d7\u4e86\u4f24\uff0c\u5c31\u8981\u597d\u597d\u8c03\u6cbb\u624d\u662f\u3002\u6211\u6765\u7ed9\u4f60\u4eec \u770b\u770b\u3002\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u4f24\u5728\u5de6\u80c1\uff0c\u5c91\u5578\u864e\u4f24\u5728\u5c0f\u817f\uff0c\u5434\u94c1\u5cf0\u4f24\u5728\u80a9\u80db\u4e4b\u4e0b\uff0c\u4f24\u52bf\u679c\u7136\u90fd\u4e0d\u662f\u592a\u91cd\uff0c\u8840\u5df2 \u7ecf\u6b62\u4f4f\uff0c\u4f24\u53e3\u4e5f\u5df2\u5f00\u59cb\u6108\u5408\u3002 \u3000\u3000\u4f24\u6700\u91cd\u7684\u662f\u675c\u671b\u6708\uff0c\u4e00\u6761\u957f\u7ea6\u4e09\u5bf8\uff0c\u6df1\u8fbe\u56db\u5206\u7684\u5200\u53e3\u81ea\u4ed6\u7684\u9888\u4fa7\u4e00\u76f4\u62c9\u5230\u4e86\u53f3\u80a9\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u63ed\u5f00\u88f9\u4f24\u767d\u5e03\uff0c\u7528\u7ea4\u79c0\u767d\u5ae9\u7684\u624b\u6307\u8f7b\u8f7b\u78b0\u4e86\u78b0\u4f24\u53e3\u9644\u8fd1\uff0c\u9053\uff1a\u201c\u8fd8\u75db\u4e0d\u75db\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u75db\u662f\u75db\uff0c\u5374\u4e5f\u4e0d\u59a8\u788d\u6211\u6325\u5251\u6740\u654c\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u65f6\uff0c\u4ed6\u8f7b\u4e86\u8f6c\u5934\uff0c\u770b\u7740\u7a0b\u5c0f\u8776\u3002 \u3000\u3000\u56db\u76ee\u76f8\u63a5\uff0c\u4e00\u65f6\u7adf\u4f3c\u96be\u4ee5\u5206\u5f00\u3002 \u3000\u3000\u65e0\u610f\u95f4\uff0c\u7a0b\u5c0f\u8776\u67d4\u7f8e\u7684\u5c0f\u624b\u8d34\u5230\u4e86\u675c\u671b\u6708\u7684\u8138\u988a\u3002 \u3000\u3000\u675c\u671b\u6708\u76ee\u5149\u4e0d\u7981\u4e00\u98a4\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7684\u8138\u7adf\u662f\u5fae\u5fae\u4e00\u7ea2\u3002 \u3000\u3000\u5ffd\u7136\u542c\u5230\u5c91\u5578\u864e\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u60dc\u7389\u59d1\u5a18\uff0c\u5c0f\u6587\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5fd9\u79fb\u5f00\u76ee\u5149\uff0c\u6536\u56de\u624b\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u675c\u53f8\u4e3b\u7684\u4f24\u53e3\u6108\u5408\u7684\u4e5f\u5f88\u597d\uff0c\u8fd8\u662f\u8981\u591a\u6ce8\u610f\u8c03 \u517b\u554a\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5979\u4e00\u53cc\u7b11\u76ee\u53c8\u779f\u4e86\u675c\u671b\u6708\u4e00\u773c\u3002 \u3000\u3000\u675c\u671b\u6708\u5fae\u7b11\u9053\uff1a\u201c\u8c22\u603b\u6355\u5934\u5173\u5fc3\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u8f6c\u8fc7\u8eab\uff0c\u9053\uff1a\u201c\u60dc\u7389\uff0c\u5c0f\u6587\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u5df2\u7ecf\u9192\u4e86\uff0c\u6b63\u56b7\u56b7\u7740\u8981\u89c1\u603b\u6355\u5934\u5462\u3002\u201d \u3000\u3000\u542c\u5979\u7684\u53e3\u6c14\u91cc\uff0c\u5f88\u6709\u4e00\u70b9\u8001\u9648\u918b\u7684\u9178\u5473\u3002 \u3000\u3000\u8bf4\u6765\u4e5f\u662f\uff0c\u4e0e\u7a0b\u5c0f\u8776\u76f8\u6bd4\uff0c\u5979\u672c\u5c31\u81ea\u6127\u4e0d\u5982\uff0c\u4e00\u76f4\u62c5\u5fc3\u675c\u671b\u6708\u4e00\u9897\u5fc3\u4f1a\u5728\u7a0b\u5c0f\u8776\u8eab\u4e0a\uff0c \u73b0\u5728\u4eb2\u773c\u89c1\u5230\u7a0b\u5c0f\u8776\u5bf9\u675c\u671b\u6708\u5173\u5fc3\u4e4b\u6001\uff0c\u5979\u5fc3\u91cc\u80fd\u4e0d\u9178\u561b\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u60dc\u7389\uff0c\u6211\u521a\u624d\u68c0\u67e5\u8fc7\u4e86\uff0c\u56db\u5927\u6355\u5934\u4e2d\uff0c\u675c\u53f8\u4e3b\u7684\u4f24\u6700\u91cd\uff0c\u4f60\u53ef\u8981\u597d \u597d\u66ff\u4ed6\u8c03\u517b\u3001\u6cbb\u7597\u5440\u3002\u201d \u3000\u3000\u60dc\u7389\u8138\u4e0a\u4e00\u7ea2\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u8f6c\u773c\u770b\u4e86\u770b\u5c91\u5578\u864e\u4e09\u4eba\uff0c\u9053\uff1a\u201c\u6211\u4eec\u4e00\u8d77\u53bb\u770b\u770b\u5c0f\u6587\u53bb\u5427\u3002\u201d \u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1 \u3000\u3000\u7a0b\u5c0f\u8776\u3001\u738b\u5c11\u537f\u4e94\u4eba\u521a\u51fa\u5385\u95e8\uff0c\u60dc\u7389\u5c31\u51b2\u5230\u4e86\u675c\u671b\u6708\u8eab\u8fb9\uff0c\u4e24\u773c\u7d27\u76ef\u7740\u4ed6\uff0c\u50cf\u662f\u60f3\u4ece\u4ed6 \u8138\u4e0a\u770b\u51fa\u4e9b\u4ec0\u4e48\u6765\u3002 \u3000\u3000\u675c\u671b\u6708\u7b11\u9053\uff1a\u201c\u770b\u4ec0\u4e48\uff0c\u662f\u4e0d\u662f\u89c9\u5f97\u4f60\u672a\u6765\u7684\u90ce\u541b\u5b9e\u5728\u592a\u6709\u9b45\u529b\u4e86\uff1f\u201d \u3000\u3000\u60dc\u7389\u7a81\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8c01\u7a00\u7f55\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u4e0d\u7a00\u7f55\uff0c\u522b\u4eba\u53ef\u62a2\u7740\u5ac1\u6211\u675c\u67d0\u4eba\u5462\uff01\u201d \u3000\u3000\u60dc\u7389\u7a81\u7136\u6ee1\u8138\u901a\u7ea2\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u60ca\u8bb6\u9053\uff1a\u201c\u53bb\u54ea\u91cc\uff1f\u201d \u3000\u3000\u60dc\u7389\u4e0d\u56de\u7b54\uff0c\u76f4\u5f80\u524d\u8d70\u3002 \u3000\u3000\u4fe9\u4eba\u4e00\u76f4\u8d70\u8fdb\u60dc\u7389\u7684\u623f\u95f4\uff0c\u60dc\u7389\u6813\u4e0a\u95e8\uff0c\u653e\u4e0b\u7a97\u5e18\uff0c\u4e00\u8f6c\u8eab\u6251\u8fdb\u675c\u671b\u6708\u6000\u91cc\u3002\u5979\u6251\u5f97\u592a \u731b\u4e86\uff0c\u4e00\u4e0b\u5b50\u5c06\u675c\u671b\u6708\u6251\u5012\u5728\u4e86\u5e8a\u4e0a\u3002 \u3000\u3000\u675c\u671b\u6708\u7a81\u7136\uff1a\u201c\u54ce\u54df\u201d\u4e00\u58f0\u3002 \u3000\u3000\u60dc\u7389\u5fd9\u9053\uff1a\u201c\u78b0\u7740\u4f60\u4f24\u53e3\u4e86\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u4f7f\u52b2\u62bd\u4e86\u4e24\u4e0b\u9f3b\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u597d\u9999\uff0c\u95fb\u7740\u8fd9\u9999\u6c14\uff0c\u4f24\u53e3\u4e5f\u4e0d\u75bc\u4e86\u3002\u201d \u3000\u3000\u60dc\u7389\u62b1\u7d27\u4ed6\uff0c\u5a07\u58f0\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u559c\u4e0d\u559c\u6b22\u6211\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4e0d\u559c\u6b22\u6211\u600e\u4f1a\u60f3\u8981\u4f60\u5462\uff1f\u201d \u3000\u3000\u60dc\u7389\u76b1\u4e86\u76b1\u9f3b\u5b50\uff0c\u5e7d\u5e7d\u5730\u9053\uff1a\u201c\u53ef\u4f60\u66f4\u559c\u6b22\u603b\u6355\u5934\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u5fc3\u91cc\u4e00\u98a4\uff0c\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u6211\u6ca1\u6709\u80e1\u8bf4\uff0c\u6211\u4e5f\u770b\u51fa\u6765\uff0c\u603b\u6355\u5934\u4e5f\u559c\u6b22\u4f60\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u5fc3\u91cc\u6709\u4e9b\u53d1\u9178\u3001\u53d1\u75db\uff0c\u5ffd\u7136\u7528\u529b\u62b1\u4f4f\u60dc\u7389\u67d4\u8f6f\u7684\u7ec6\u8170\uff0c\u6df1\u6df1\u5411\u5979\u5a07\u7b11\u7684\u5c0f\u5634\u4e0a\u543b \u53bb\u3002 \u3000\u3000\u60dc\u7389\u9f3b\u5b50\u91cc\u653e\u51fa\u5a07\u58f0\uff0c\u5a07\u8eaf\u5267\u70c8\u5730\u6296\u52a8\u8d77\u6765\u3002 \u3000\u3000\u800c\u4e14\u7a81\u7136\u53d8\u5f97\u706b\u4e00\u822c\u5730\u70ed\u3002 \u3000\u3000\u53d1\u70eb\u7684\u5a07\u8eaf\u4e0d\u4f4f\u98a4\u6296\u3002 \u3000\u3000\u4f46\u5979\u7684\u6a31\u5507\u5374\u662f\u67d4\u8f6f\u53c8\u51b0\u51c9\u3002 \u3000\u3000\u675c\u671b\u6708\u4e00\u543b\u4e4b\u4e0b\uff0c\u4e0d\u89c9\u60c5\u6000\u5927\u52a8\uff0c\u4e00\u53cc\u5927\u624b\u4e0d\u89c9\u95f4\u5df2\u63d2\u8fdb\u60dc\u7389\u7684\u8863\u895f\uff0c\u6478\u5230\u4e86\u5979\u817b\u5982\u51dd \u8102\u7684\u53c8\u67d4\u8f6f\u53c8\u6e29\u6da6\u7684\u808c\u80a4\u3002 \u3000\u3000\u60dc\u7389\u98a4\u6296\u7740\uff0c\u5a07\u5583\u7ec6\u7ec6\u5730\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u8bb0\u4e0d\u8bb0\u5f97\u60dc\u7389\u8bf4\u8fc7\u7684\u8bdd\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u600e\u4f1a\u4e0d\u8bb0\u5f97\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u60dc\u7389\u8bf4\u8fc7\u8981\u628a\u8fd9\u4e2a\u6e05\u767d\u8eab\u5b50\u4ea4\u7ed9\u5927\u54e5\u7684\uff0c\u5927\u54e5\u8981\u4e0d\u8981\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u73b0\u5728\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u73b0\u5728\u3002\u5f62\u52bf\u8fd9\u6837\u51f6\u9669\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4f1a\u53d1\u751f\u4ec0\u4e48\u610f\u5916\uff0c\u518d\u8bf4\uff0c\u60dc\u7389\u628a\u8eab\u5b50\u4ea4\u7ed9 \u4f60\uff0c\u4f60\u77e5\u9053\u4e86\u60dc\u7389\u7684\u597d\uff0c\u5c31\u4e0d\u4f1a\u518d\u53bb\u559c\u6b22\u603b\u6355\u5934\u4e86\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u5fc3\u4e2d\u4e00\u75db\uff0c\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u771f\u662f\u4e2a\u75f4\u60c5\u7684\u59d1\u5a18\u554a\uff01\u201d \u3000\u3000\u60dc\u7389\u5a07\u58f0\u9053\uff1a\u201c\u5c31\u662f\uff0c\u60dc\u7389\u5c31\u662f\u4e00\u4e2a\u75f4\u60c5\u7684\u5973\u5b50\uff0c\u53ea\u5bf9\u5927\u54e5\u4e00\u4e2a\u4eba\u597d\uff0c\u53ea\u5bf9\u5927\u54e5\u4e00\u4e2a\u4eba \u75f4\u60c5\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u53c8\u5f20\u5f00\u5634\uff0c\u7d27\u7d27\u543b\u4f4f\u4e86\u5979\u7684\u6a31\u5507\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u4e0d\u5fcd\u518d\u542c\u5979\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u7684\u786e\u4e00\u76f4\u5728\u5fc3\u91cc\u6697\u6697\u7231\u7740\u7a0b\u5c0f\u8776\uff0c\u4f46\u53ea\u662f\u4e00\u4efd\u65e0\u671b\u7684\u7231\u3002 \u3000\u3000\u4f46\u5c31\u5728\u521a\u624d\uff0c\u4ed6\u81ea\u7a0b\u5c0f\u8776\u7684\u79c0\u7f8e\u7684\u773c\u7738\u4e2d\u770b\u51fa\u4e86\u5979\u5bf9\u4ed6\u4e5f\u6709\u4e00\u4efd\u60c5\u610f\uff0c\u53ea\u662f\u8fd9\u4efd\u60c5\u610f\u85cf \u5f97\u5f88\u6df1\uff0c\u57cb\u85cf\u5728\u4e00\u4efd\u65e0\u5948\u540e\u9762\uff0c\u8fd8\u5305\u542b\u6709\u4e00\u4efd\u51c4\u82e6\u3002 \u3000\u3000\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u7a0b\u5c0f\u8776\u5fc3\u91cc\u5230\u5e95\u662f\u600e\u4e48\u60f3\u7684\uff1f \u3000\u3000\u675c\u671b\u6708\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u4f46\u4ed6\u77e5\u9053\uff0c\u7a0b\u5c0f\u8776\u7684\u65e0\u5948\u4e0d\u662f\u56e0\u4e3a\u60dc\u7389\u3002 \u3000\u3000\u60dc\u7389\u7684\u5a07\u8eaf\u66f4\u70ed\u3002 \u3000\u3000\u5979\u5df2\u5f00\u59cb\u5728\u4ed6\u8eab\u4e0a\u626d\u52a8\u3002 \u3000\u3000\u60dc\u7389\u7a81\u7136\u79fb\u5f00\u5634\uff0c\u773c\u5149\u8ff7\u6f13\uff0c\u5a07\u6ef4\u7ec6\u7ec6\u5730\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u600e\u4e48\u8fd8\u4e0d\u8981\u6211\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5927\u54e5\u5df2\u662f\u91cd\u4f24\u5728\u8eab\uff0c\u4f60\u8fd9\u6837\uff0c\u4e5f\u592a\u4e0d\u5fc3\u75bc\u5927\u54e5\u4e86\u5427\uff01\u201d \u3000\u3000\u60dc\u7389\u5a07\u7f9e\u5730\u9053\uff1a\u201c\u597d\uff0c\u4f60\u73b0\u5728\u4e0d\u8981\uff0c\u7b49\u4ee5\u540e\u2026\u2026\u4ee5\u540e\u6d1e\u623f\u82b1\u70db\uff0c\u60dc\u7389\u8fd8\u4e0d\u8ba9\u4f60\u2026\u2026\u4e0d\u8ba9 \u4f60\u2026\u2026\u201d \u3000\u3000\u675c\u671b\u6708\u4e00\u7ffb\u8eab\u538b\u4f4f\u5979\uff0c\u4e24\u624b\u6025\u901f\u5730\u626f\u7740\u5979\u7684\u8863\u895f\uff0c\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8ba9\u6211\u2026\u2026\u600e\u4e48\u66ff\u6211 \u751f\u5c0f\u5b69\u5b50\uff1f\u201d \u3000\u3000\u60dc\u7389\u201c\u54fc\u54fc\u201d\u4e00\u58f0\uff0c\u4f38\u624b\u6293\u8fc7\u4e00\u65b9\u7f57\u5e15\uff0c\u6342\u5728\u8138\u4e0a\uff0c\u4efb\u4ed6\u6240\u4e3a\u4e86\u3002 \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u5c0f\u96c5\u7684\u58f0\u97f3\u5728\u9662\u4e2d\u53eb\u9053\uff1a\u201c\u60dc\u7389\u59d0\uff0c\u60dc\u7389\u59d0\uff0c\u4f60\u5728\u54ea\u513f\uff1f\u603b\u6355\u5934\u8981\u627e\u4f60 \u8bf4\u8bdd\u5462\uff0c\u60dc\u7389\u59d0\u3002\u201d \u3000\u3000\u60dc\u7389\u7ffb\u8eab\u5750\u8d77\uff0c\u6ee1\u9762\u6f6e\u7ea2\uff0c\u4f38\u624b\u5fd9\u4e71\u5730\u6574\u7406\u7740\u8863\u895f\uff0c\u53c8\u5306\u5306\u7406\u4e86\u7406\u6563\u4e71\u7684\u76d8\u53d1\uff0c\u5e94\u58f0\u9053\uff1a \u201c\u6211\u5728\u8fd9\u91cc\uff0c\u9a6c\u4e0a\u5c31\u8fc7\u6765\uff01\u201d\u5979\u542b\u7740\u7b11\u5730\u779f\u4e86\u675c\u671b\u6708\u4e00\u773c\uff0c\u6251\u4e0a\u6765\u6402\u7740\u4ed6\u7684\u8116\u5b50\u5728\u4ed6\u5634\u4e0a\u91cd \u91cd\u6765\u4e86\u4e00\u53e3\uff0c\u5a07\u58f0\u9053\uff1a\u201c\u90fd\u662f\u4f60\uff01\u4f60\u8fd9\u4e2a\u5927\u574f\u86cb\uff01\u201d \u3000\u3000\u4e00\u8fb9\u8bf4\u7740\uff0c\u4e00\u8fb9\u4f38\u51fa\u53c8\u767d\u53c8\u5ae9\u7684\u5c0f\u624b\u5728\u675c\u671b\u6708\u989d\u4e0a\u4f7f\u52b2\u70b9\u4e86\u4e00\u70b9\u3002 \u3000\u3000\u675c\u671b\u6708\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u771f\u662f\u7684\uff0c\u627e\u4eba\u4e5f\u4e0d\u770b\u4e2a\u65f6\u5019\uff01\u201d \u3000\u3000\u60dc\u7389\u8d70\u5230\u95e8\u8fb9\uff0c\u626d\u5934\u4e00\u7b11\uff0c\u5a9a\u58f0\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u653e\u5fc3\uff0c\u6709\u4f60\u2026\u2026\u6709\u4f60\u5982\u613f\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u5979\u62c9\u5f00\u95e8\uff0c\u4e00\u6e9c\u70df\u8dd1\u4e86\u3002\u3000\u675c\u671b\u6708\u53f9\u4e86\u53e3\u6c14\uff0c\u4ef0\u9762\u8eba\u5012\u5728\u60dc\u7389\u5e8a\u4e0a\uff0c\u5446\u5446\u60f3\u8d77\u4e86 \u5fc3\u4e8b\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u4e5f\u5947\u602a\u81ea\u5df1\u60f3\u7684\u7adf\u4e0d\u662f\u521a\u521a\u8fd8\u808c\u80a4\u76f8\u4eb2\u8fc7\u7684\u60dc\u7389\u3002 \u3000\u3000\u4ed6\u60f3\u7684\u662f\u7a0b\u5c0f\u8776\u3002 \u3000\u3000\u60f3\u5979\u90a3\u5bf9\u5e7d\u5e7d\u660e\u7738\u4e2d\u6240\u6d41\u9732\u51fa\u7684\u4e00\u5207\u3002 \u3000\u3000\u5979\u771f\u7684\u5bf9\u4ed6\u6000\u6709\u4e00\u4efd\u60c5\u610f\u5417\uff1f \u3000\u3000\u5f53\u7136\u662f\u771f\u7684\u3002 \u3000\u3000\u675c\u671b\u6708\u7edd\u975e\u4e0d\u89e3\u98ce\u60c5\u7684\u7537\u5b50\uff0c\u4ed6\u5f53\u7136\u80fd\u770b\u51fa\u6765\u3002 \u3000\u3000\u53ea\u662f\uff0c\u4e3a\u4ec0\u4e48\u5979\u2026\u2026 \u3000\u3000\u4e0d\u80fd\u518d\u80e1\u601d\u4e71\u60f3\u4e0b\u53bb\u4e86\uff0c\u675c\u671b\u6708\u544a\u8bc9\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u675c\u671b\u6708\u5440\u675c\u671b\u6708\uff0c\u4f60\u53ef\u771f\u662f\u4e2a\u7cca\u6d82\u866b\u554a\uff01\u5728\u73b0\u5728\u8fd9\u79cd\u5f62\u52bf\u4e4b\u4e0b\uff0c\u4e0d\u60f3 \u5982\u4f55\u534f\u52a9\u603b\u6355\u5934\u4fdd\u62a4\u738b\u77e5\u5e9c\uff0c\u7f09\u51f6\u7834\u6848\uff0c\u5374\u4e00\u5473\u6c89\u6eba\u4e8e\u513f\u5973\u79c1\u60c5\u4e4b\u4e2d\uff0c\u4e0d\u80fd\u81ea\u62d4\uff0c\u4f60\u771f\u8be5\u6b7b \u554a\uff01\u201d \u3000\u3000\u81ea\u8d23\u4e00\u756a\u540e\uff0c\u4ed6\u5ffd\u7136\u60f3\u8d77\u603b\u6355\u5934\u627e\u60dc\u7389\u4e00\u5b9a\u662f\u56e0\u4e3a\u5c0f\u6587\u7684\u4f24\u52bf\uff0c\u800c\u4e14\u603b\u6355\u5934\u4e5f\u4e00\u5b9a\u4f1a\u53ec \u96c6\u4ed6\u4e0e\u5434\u94c1\u5cf0\u7b49\u56db\u4eba\u4e86\u89e3\u51e0\u5929\u6765\u5e9c\u8859\u53d1\u751f\u7684\u60c5\u51b5\uff0c\u5546\u5b9a\u4ee5\u540e\u7684\u5bf9\u654c\u4e4b\u7b56\u3002\u4e8e\u662f\u4ed6\u4e00\u633a\u8eab\u81ea\u5e8a \u4e0a\u8df3\u4e86\u8d77\u6765\uff0c\u8d70\u51fa\u623f\u95e8\uff0c\u5411\u5927\u5385\u8d70\u53bb\u3002 \u3000\u3000\u679c\u7136\uff0c\u4e09\u5927\u6355\u5934\u3001\u60dc\u7389\u548c\u7a0b\u5c0f\u8776\u5df2\u7ecf\u5750\u5728\u82b1\u5385\u91cc\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u770b\u89c1\u4ed6\u8d70\u8fdb\u5927\u5385\uff0c\u9053\uff1a\u201c\u675c\u53f8\u4e3b\u6765\u7684\u6b63\u597d\uff0c\u5e9c\u8859\u906d\u6740\u624b\u88ad\u51fb\u4e4b\u540e\uff0c\u4f60\u4eec\u505a\u4e86\u4ec0\u4e48 \u65b0\u7684\u5e03\u7f6e\u4e86\u5417\uff1f\u201d \u3000\u3000\u5979\u4e0d\u95ee\u5434\u94c1\u5cf0\u4e09\u4eba\uff0c\u53ea\u95ee\u675c\u671b\u6708\uff0c\u4e43\u662f\u56e0\u4e3a\u626c\u5dde\u4e43\u662f\u6c5f\u5357\u5211\u6355\u5206\u53f8\u6240\u7ba1\u8f96\u4e4b\u8303\u56f4\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u98de\u9e3d\u4f20\u4e66\u7ed9\u6c5f\u5357\u5211\u6355\u5206\u53f8\u53f8\u6848\u6797\u4e0d\u51e1\uff0c\u706b\u901f\u8c03\u96c6\u5206\u53f8\u4e2d\u6240\u6709\u597d\u624b\uff0c \u79d8\u5bc6\u6f5c\u6765\u626c\u5dde\uff0c\u6765\u5e9c\u8859\u6c47\u5408\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\uff01\u8fd9\u4e2a\u51b3\u5b9a\u505a\u5f97\u5bf9\uff0c\u6740\u624b\u96c6\u56e2\u7684\u5b9e\u529b\u4e4b\u5f3a\uff0c\u8fdc\u8fdc\u8d85\u51fa\u6211\u4eec\u7684\u9884\u6599\uff0c\u5982\u80fd \u6709\u4e00\u652f\u751f\u529b\u519b\u6765\u63f4\uff0c\u5f53\u53ef\u89e3\u71c3\u7709\u4e4b\u6025\uff01\u675c\u53f8\u4e3b\uff0c\u4f60\u770b\u6c5f\u5357\u5206\u53f8\u80fd\u53ec\u96c6\u591a\u5c11\u597d\u624b\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u6211\u5728\u98de\u9e3d\u4f20\u4e66\u4e2d\u5df2\u7ecf\u544a\u8bc9\u6797\u53f8\u6848\uff0c\u6b64\u884c\u53ea\u8981\u5206\u53f8\u4e4b\u4e2d\u5c5e\u4e00\u6d41\u597d\u624b \u7684\u4eba\uff0c\u6211\u60f3\uff0c\u9664\u4e86\u5916\u51fa\u529e\u6848\u7684\u4eba\u5458\u4e4b\u5916\uff0c\u4e09\u5341\u591a\u4f4d\u5e94\u8be5\u6ca1\u6709\u95ee\u9898\u3002\u201d\u7a0b\u5c0f\u8776\u6ee1\u610f\u5730\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u675c\u671b\u6708\u56db\u4e0b\u91cc\u770b\u4e86\u770b\uff0c\u95ee\u9053\uff1a\u201c\u738b\u5927\u4eba\u5462\uff1f\u4ed6\u600e\u4e48\u4e0d\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u738b\u5927\u4eba\u548c\u5c0f\u96c5\u5df2\u5728\u966a\u5c0f\u6587\u8c08\u5fc3\u5462\uff0c\u6211\u60f3\uff0c\u8fd9\u6837\u6216\u8bb8\u80fd\u5316\u89e3\u6389\u4e00\u4e9b\u5c0f\u6587\u5fc3\u4e2d \u7684\u51f6\u608d\u4e4b\u6c14\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u603b\u6355\u5934\uff0c\u5c0f\u6587\u5230\u5e95\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u771f\u7684\u662f\u56e0\u4e3a\u5979\u6240\u7ec3\u7684\u90a3\u5957\u5251\u6cd5\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u662f\uff0c\u4e5f\u4e0d\u5168\u662f\u3002\u6211\u548c\u60dc\u7389\u90fd\u8ba4\u4e3a\uff0c\u6700\u4e3b\u8981\u7684\u539f\u56e0\u662f\u5979\u4e2d\u4e86\u4e00\u79cd\u5947 \u5f02\u7684\u6bd2\u836f\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4e2d\u6bd2\uff1f\u5c0f\u6587\u4e00\u76f4\u5446\u5728\u5e9c\u8859\u4e2d\uff0c\u96be\u9053\u8fd9\u91cc\u6709\u6f5c\u4f0f\u7684\u5978\u7ec6\uff1f\u201d\u7a0b\u5c0f\u8776\u9053\uff1a \u201c\u8fd9\u4e2a\u6211\u4ed4\u7ec6\u89c2\u5bdf\u8fc7\uff0c\u5e9c\u8859\u4e4b\u4e2d\u662f\u4e0d\u53ef\u80fd\u6709\u5978\u7ec6\u7684\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u90a3\u5c31\u5947\u602a\u4e86\uff0c\u662f\u4ec0\u4e48\u4eba\u80fd\u6709\u673a\u4f1a\u63a5\u8fd1\u5c0f\u6587\u4e0b\u6bd2\u5462\uff1f\u5c0f\u6587\u6ca1\u6709\u89c1\u8fc7\u5916\u4eba\u5427\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u89c1\u8fc7\uff0c\u800c\u4e14\u8fd8\u4e0d\u6b62\u4e00\u4e2a\uff0c\u90a3\u5929\u738b\u77e5\u5e9c\u5728\u82b1\u5385\u4f1a\u5ba1\u91d1\u5c0f\u7709\uff0c\u91d1\u5e9c\u4e00\u4e0b\u5b50\u5c31\u6765 \u4e86\u5341\u51e0\u4e2a\u4eba\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u5403\u60ca\u9053\uff1a\u201c\u96be\u9053\u91d1\u5e9c\u4e4b\u4e2d\u6f5c\u4f0f\u6709\u6740\u624b\u4e4b\u738b\u6216\u68a6\u5e7b\u4e4b\u5200\u7684\u4eba\uff1f\u8fd9\u4e2a\u4eba\u4e00\u5b9a\u5c31\u662f\u8d3e\u82f1\uff0c \u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6de1\u6de1\u4e00\u7b11\uff0c\u4e0d\u7f6e\u53ef\u5426\uff0c\u8f6c\u800c\u5bf9\u5434\u94c1\u5cf0\u7b49\u4eba\u9053\uff1a\u201c\u8fd9\u51e0\u5929\u5e9c\u8859\u53d1\u751f\u7684\u4e8b\u4f60\u4eec\u5df2\u7ecf\u8bb2 \u8fc7\u4e86\uff0c\u73b0\u5728\u8bb2\u4e00\u8bb2\u8bf8\u4f4d\u6709\u4ec0\u4e48\u6253\u7b97\u5427\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u5927\u58f0\u9053\uff1a\u201c\u4f9d\u6211\u770b\uff0c\u5e94\u8be5\u8bf7\u63d0\u7763\u8c03\u6d3e\u6c34\u5e08\u7cbe\u9510\uff0c\u91cd\u5175\u5728\u5e9c\u8859\u5e03\u9632\u3002\u8001\u5c91\u867d\u8bf4\u5411\u6765 \u80c6\u5b50\u4e0d\u5c0f\uff0c\u4f46\u8fd9\u4e00\u6b21\uff0c\u8001\u5b9e\u8bf4\uff0c\u51ed\u6211\u7b49\u51e0\u4eba\uff0c\u5b9e\u5728\u662f\u4e0d\u80fd\u591f\u4e0e\u6740\u624b\u96c6\u56e2\u76f8\u6297\u8861\u3002\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u53ef\u738b\u77e5\u5e9c\u4e00\u5b9a\u8981\u4ee5\u81ea\u5df1\u4e3a\u9975\uff0c\u8bf1\u4f7f\u51f6\u624b\u73b0\u8eab\uff0c\u575a\u51b3\u4e0d\u540c\u610f\u8c03\u6c34\u5e08\u5e03\u9632\u554a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u738b\u5927\u4eba\u4e0d\u987e\u4e2a\u4eba\u5b89\u5371\uff0c\u4e00\u5fc3\u8981\u6349\u7f09\u51f6\uff0c\u771f\u662f\u5b98\u573a\u4e2d\u96be\u5f97\u4e00\u89c1\u7684\u4eba\u624d\u554a\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u6211\u5c31\u4e0d\u540c\u610f\uff0c\u73b0\u5728\u8fd9\u79cd\u5f62\u52bf\u4e0b\uff0c\u5c31\u7b97\u8bf1\u4f7f\u51f6\u624b\u73b0\u8eab\u4e86\uff0c\u6211\u4eec\u53ea\u6015\u4e5f\u62ff\u4e0d\u4f4f \u4ed6\uff0c\u4f55\u76ca\u4e4b\u6709\uff1f\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u8981\u60f3\u5728\u6700\u77ed\u7684\u65f6\u95f4\u6269\u5145\u6211\u4eec\u7684\u5b9e\u529b\uff0c\u53ea\u6709\u4e00\u6761\u8def\u53ef\u884c\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5434\u53f8\u4e3b\u6709\u4ec0\u4e48\u9ad8\u89c1\u5417\uff1f\u5feb\u8bf4\u51fa\u6765\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u8bf7\u626c\u5dde\u5341\u5927\u5bcc\u6237\u5404\u6d3e\u95e8\u4e0b\u9ad8\u624b\uff0c\u7ec4\u6210\u4e00\u652f\u5f3a\u6709\u529b\u7684\u5feb\u901f\u6253\u51fb\u529b\u91cf\uff0c\u4e00\u65e6\u51f6 \u624b\u73b0\u8eab\uff0c\u7acb\u5373\u6295\u5165\u8fd9\u4e9b\u529b\u91cf\uff0c\u4e0e\u4ed6\u4eec\u51b3\u6218\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u8fd8\u672a\u5f00\u53e3\uff0c\u675c\u671b\u6708\u5df2\u9053\uff1a\u201c\u5341\u5927\u5bcc\u6237\u95e8\u4e0b\u9ad8\u624b\uff0c\u53ea\u6015\u6709\u4e00\u591a\u534a\u90fd\u662f\u81ea\u9ed1\u9053\u7f51\u7edc\u800c \u6765\uff0c\u4ed6\u4eec\u4f1a\u8bda\u5fc3\u5e2e\u6211\u4eec\u5417\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u6025\u9053\uff1a\u201c\u8fd9\u53c8\u4e0d\u884c\uff0c\u90a3\u53c8\u4e0d\u884c\uff0c\u5230\u5e95\u8be5\u5982\u4f55\u65b9\u597d\u5462\uff1f\u201d \u3000\u3000\u5ffd\u89c1\u94c1\u7fce\u5306\u5306\u8d70\u8fdb\u5385\u6765\uff0c\u9053\uff1a\u201c\u675c\u53f8\u4e3b\uff0c\u6c5f\u5357\u5206\u53f8\u6797\u53f8\u6848\u6c42\u89c1\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u60ca\u559c\u9053\uff1a\u201c\u4ed6\u6765\u7684\u597d\u5feb\u5440\uff01\u5feb\u8bf7\u4ed6\u8fdb\u6765\u3002\u201d \u3000\u3000\u6797\u4e0d\u51e1\u662f\u4e00\u4e2a\u5341\u5206\u7cbe\u608d\u7684\u4e2d\u5e74\u4eba\uff0c\u4ed6\u5927\u6b65\u8d70\u8fdb\u5385\u6765\uff0c\u4e00\u773c\u89c1\u5230\u7a0b\u5c0f\u8776\uff0c\u4e0d\u7981\u6014\u4e86\u4e00\u6014\uff0c \u624d\u62f1\u624b\u9053\uff1a\u201c\u539f\u6765\u603b\u6355\u5934\u4e5f\u5728\u8fd9\u91cc\uff0c\u5c5e\u4e0b\u6797\u4e0d\u51e1\uff0c\u89c1\u8fc7\u7a0b\u603b\u6355\u5934\u3002\u201d\u7a0b\u5c0f\u8776\u90d1\u91cd\u5730\u70b9\u70b9\u5934\uff0c \u9053\uff1a\u201c\u6797\u53f8\u6848\u884c\u52a8\u8fc5\u901f\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u4f60\u4e00\u5171\u5e26\u6765\u4e86\u591a\u5c11\u4eba\uff1f\u201d \u3000\u3000\u6797\u4e0d\u51e1\u9053\uff1a\u201c\u56de\u603b\u6355\u5934\u7684\u8bdd\uff0c\u5c5e\u4e0b\u4e00\u5171\u5e26\u6765\u4e09\u5341\u4e03\u4eba\uff0c\u5168\u90fd\u662f\u6c5f\u5357\u5206\u53f8\u4e2d\u7684\u4e00\u6d41\u9ad8\u624b\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4ed6\u4eec\u4eba\u5462\uff1f\u201d \u3000\u3000\u6797\u4e0d\u51e1\u9053\uff1a\u201c\u4ed6\u4eec\u6613\u5bb9\u6539\u626e\u6210\u5404\u8272\u4eba\u7b49\uff0c\u5df2\u5728\u5e9c\u8859\u56db\u5468\u57cb\u4f0f\u4e0b\u6765\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u65ad\u7136\u9053\uff1a\u201c\u4e0d\u7528\u8fd9\u6837\u4e86\uff0c\u901a\u77e5\u4ed6\u4eec\uff0c\u5168\u90e8\u90fd\u4ece\u6b63\u95e8\u8fdb\u5165\u5e9c\u8859\u3002\u201d \u3000\u3000\u6797\u4e0d\u51e1\u9053\uff1a\u201c\u662f\uff0c\u5c5e\u4e0b\u8fd9\u5c31\u53bb\u529e\uff01\u201d \u3000\u3000\u8bf4\u6bd5\uff0c\u4e00\u4e2a\u95ea\u8eab\u5df2\u6d88\u5931\u5728\u9662\u5916\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u53c8\u8d5e\u8bb8\u5730\u70b9\u70b9\u5934\uff0c\u5bf9\u94c1\u7fce\u9053\uff1a\u201c\u94c1\u6355\u5934\uff0c\u8fd8\u8981\u8bf7\u4f60\u5b89\u6392\u4e00\u4e0b\u4ed6\u4eec\u4e09\u5341\u4e03\u4eba\u7684\u9632\u5907\uff0c \u6709\u52b3\u4e86\u3002\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u8fd9\u662f\u5c5e\u4e0b\u5e94\u5c3d\u4e4b\u8d23\u3002\u201d \u3000\u3000\u4ed6\u4e5f\u5306\u5306\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u8bf4\u4f60\u548c\u5c0f\u96c5\u9047\u4e0a\u4e86\u68a6\u5e7b\u4e4b\u5200\uff0c\u662f\u4e0d\u662f\u4ea4\u8fc7\u624b\uff1f\u80dc\u8d1f\u5982\u4f55\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4ed4\u7ec6\u542c\u4e86\u542c\u56db\u4e0b\u7684\u52a8\u9759\uff0c\u538b\u4f4e\u58f0\u97f3\u9053\uff1a\u201c\u5e94\u8be5\u8bf4\u662f\u4ed6\u81ea\u79f0\u662f\u68a6\u5e7b\u4e4b\u5200\u3002\u56e0\u4e3a\u4ed6\u9ed1 \u8863\u8499\u9762\uff0c\u6211\u65e0\u6cd5\u770b\u89c1\u4ed6\u7684\u76f8\u8c8c\u3002\u90a3\u4eba\u6b66\u529f\u7684\u786e\u5f88\u9ad8\uff0c\u6211\u4e0e\u5c0f\u96c5\u8054\u624b\uff0c\u4e5f\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u5403\u60ca\u5730\u5f20\u5927\u4e86\u5634\uff0c\u5012\u5438\u4e86\u4e00\u53e3\u51b7\u6c14\uff0c\u9053\uff1a\u201c\u6211\u7684\u8001\u5929\u7237\uff01\u8fde\u603b\u6355\u5934\u4e5f\u4e0d\u662f\u4ed6\u7684\u5bf9 \u624b\uff0c\u68a6\u5e7b\u4e4b\u5200\u679c\u7136\u5389\u5bb3\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5434\u5144\u542c\u9519\u4e86\uff0c\u603b\u6355\u5934\u662f\u8bf4\u90a3\u4eba\u81ea\u79f0\u68a6\u5e7b\u4e4b\u5200\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u90a3\u4eba\u5176\u5b9e\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\uff1f\u603b\u6355\u5934\u8ba4\u8bc6\u771f\u6b63\u7684\u68a6\u5e7b\u4e4b\u5200\uff0c \u662f\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5e76\u4e0d\u91cd\u8981\uff0c\u4e0d\u7ba1\u90a3\u4eba\u662f\u8c01\uff0c\u4ed6\u80af\u5b9a\u662f\u51f6\u624b\u96c6\u56e2\u9996\u8111\u7ea7\u4eba\u7269\u65e0\u7591\uff0c\u800c \u4e14\u5730\u4f4d\u7edd\u5bf9\u5728\u8840\u624b\u65b9\u8f6e\u548c\u56db\u51e4\u697c\u4e3b\u4e0d\u8001\u4e66\u751f\u547c\u5ef6\u8fdc\u4e4b\u4e0a\u3002 \u3000\u3000\u73b0\u5728\u6211\u53ea\u80fd\u544a\u8bc9\u4f60\u4eec\uff0c\u6211\u4eec\u9700\u4fa6\u7834\u7684\uff0c\u662f\u4e00\u4e2a\u60ca\u4eba\u7684\u5927\u9634\u8c0b\uff0c\u5177\u4f53\u8be6\u60c5\uff0c\u73b0\u5728\u8fd8\u4e0d\u80fd\u900f \u9732\uff0c\u56e0\u4e3a\u6211\u4e5f\u4e0d\u662f\u5341\u5206\u6e05\u695a\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u603b\u6355\u5934\u4e0b\u626c\u5dde\uff0c\u5e76\u4e0d\u662f\u4e3a\u4e86\u91d1\u5e9c\u65b0\u623f\u8840\u6848\uff0c\u4e5f\u4e0d\u5168\u662f\u4e3a\u4e86\u4fdd\u62a4\u738b \u5927\u4eba\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u4f60\u9519\u4e86\uff0c\u8fd9\u4e09\u4ef6\u4e8b\u5176\u5b9e\u662f\u4e00\u4ef6\u4e8b\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u4e00\u6014\uff0c\u56db\u5927\u6355\u5934\u4e2a\u4e2a\u5bf9\u89c6\u4e00\u773c\uff0c\u90fd\u6697\u81ea\u601d\u91cf\u8d77\u6765\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u8f6c\u800c\u5bf9\u60dc\u7389\u9053\uff1a\u201c\u4e0d\u8001\u4e66\u751f\u547c\u5ef6\u8fdc\u662f\u4f60\u7684\u5927\u5e08\u4f2f\uff0c\u4f60\u5165\u56db\u51e4\u697c\u5c31\u662f\u4e3a\u4e86\u627e\u4ed6\u62a5\u6740 \u7236\u4e4b\u4ec7\uff1f\u201d \u3000\u3000\u60dc\u7389\u54ac\u7259\u9053\uff1a\u201c\u662f\u7684\uff0c\u60dc\u7389\u4e00\u5b9a\u8981\u4eb2\u624b\u6740\u4e86\u4ed6\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u62c9\u8d77\u5979\u7684\u5c0f\u624b\uff0c\u6e29\u8a00\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u4e0d\u7528\u7740\u6025\uff0c\u6211\u4fdd\u8bc1\uff0c\u4f60\u4e00\u5b9a\u80fd\u4eb2\u624b\u62a5\u6740\u7236\u5927 \u4ec7\uff01\u201d \u3000\u3000\u60dc\u7389\u773c\u5708\u7ea2\u4e86\uff0c\u7528\u529b\u5730\u70b9\u70b9\u5934\u3002 \u3000\u3000\u521a\u51fa\u53bb\u4e0d\u591a\u4e00\u4f1a\u7684\u94c1\u7fce\u5ffd\u7136\u53c8\u51fa\u73b0\u5728\u9662\u4e2d\uff0c\u8001\u8fdc\u5c31\u9ad8\u58f0\u9053\uff1a\u201c\u91d1\u5e9c\u603b\u7ba1\u8d3e\u82f1\uff0c\u6c42\u89c1\u7a0b\u603b \u6355\u5934\uff01\u201d \u3000\u3000\u201c\u94c1\u7fce\u4e5f\u662f\u4e00\u4e2a\u5f88\u673a\u8b66\u7684\u4eba\u5462\uff01\u201d\u7a0b\u5c0f\u8776\u5fc3\u4e2d\u6697\u5fd6\u9053\uff1a\u201c\u4ed6\u77e5\u9053\u6211\u4eec\u5df2\u5546\u8bae\u8981\u4e8b\uff0c\u6240\u4ee5 \u6545\u610f\u9ad8\u58f0\u901a\u62a5\u3002\u770b\u6765\uff0c\u738b\u77e5\u5e9c\u6cbb\u7406\u626c\u5dde\u7684\u529f\u52b3\uff0c\u8fd9\u4f4d\u94c1\u6355\u5934\u8981\u5360\u4e0a\u4e00\u5c0f\u534a\u4e86\uff01\u201d \u3000\u3000\u5fc3\u4e2d\u8f6c\u5ff5\uff0c\u53e3\u4e2d\u5df2\u9053\uff1a\u201c\u6709\u8bf7\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u4e0d\u7981\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u8d3e\u82f1\uff0c\u4ed6\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u94c1\u7fce\u966a\u7740\u8d3e\u82f1\u4e00\u540c\u8fdb\u6765\uff0c\u8d3e\u82f1\u56db\u4e0b\u56e2\u56e2\u4e00\u62b1\u62f3\uff0c\u5bf9\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u8d3e\u82f1\uff0c\u4e43\u91d1\u5e9c\u603b \u7ba1\uff0c\u4eca\u65e5\u7279\u6765\u6c42\u89c1\u7a0b\u603b\u6355\u5934\uff0c\u4e00\u6765\u53d7\u655d\u4e1c\u4e3b\u4e4b\u6258\uff0c\u611f\u8c22\u603b\u6355\u5934\u5bf9\u91d1\u5e9c\u7684\u5173\u5fc3\uff0c\u4e8c\u6765\u5c1a\u6709\u4e00\u4e0d \u60c5\u4e4b\u8bf7\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8bf7\u8bb2\u3002\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u655d\u4e1c\u4e3b\u5f97\u77e5\u68a6\u5e7b\u4e4b\u5200\u5c06\u5927\u4e3e\u8fdb\u72af\uff0c\u593a\u53d6\u4e09\u5b9d\uff0c\u5df2\u5c3d\u51fa\u5e9c\u4e2d\u5b9e\u529b\uff0c\u4e25\u4ee5\u5f85\u654c\uff0c \u53ea\u662f\u4e00\u65e6\u51b2\u7a81\uff0c\u5fc5\u7136\u4f1a\u6709\u6b7b\u4f24\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u655d\u4e1c\u4e3b\u4ecd\u60f3\u8bf7\u603b\u6355\u5934\u6069\u51c6\uff0c\u6d3e\u9063\u94c1\u6355\u5934\u81f3\u91d1\u5e9c\u4e3b\u6301\u5927\u5c40\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\uff0c\u4f60\u4eec\u662f\u6015\u6740\u4e86\u4eba\uff0c\u5b98\u5e9c\u4f1a\u8ffd\u7a76\u8d23\u4efb\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5ffd\u7136\u4e0d\u8bf4\u8bdd\u4e86\uff0c\u4f3c\u4e4e\u662f\u5728\u8003\u8651\u8ba9\u4e0d\u8ba9\u94c1\u7fce\u53bb\u3002 \u3000\u3000\u8d3e\u82f1\u4e5f\u4e0d\u8bf4\u8bdd\uff0c\u4ed6\u7684\u4e24\u773c\u4e00\u76f4\u4f4e\u5782\u7740\u770b\u7740\u5730\u9762\uff0c\u770b\u8d77\u6765\u5c31\u50cf\u662f\u5728\u5f88\u8ba4\u771f\u5730\u542c\u4ec0\u4e48\u4eba\u8bf4\u8bdd \u4f3c\u7684\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\u3001\u60dc\u7389\u3001\u94c1\u7fce\u6b63\u611f\u5947\u602a\uff0c\u53ea\u89c1\u7a0b\u5c0f\u8776\u53cc\u7709\u4e00\u5c55\uff0c\u65ad\u7136\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u8ba9\u94c1\u7fce\u53bb\u91d1 \u5e9c\uff0c\u800c\u4e14\uff0c\u8fd8\u8981\u8bf7\u8d3e\u603b\u7ba1\u7ed9\u8d35\u4e1c\u4e3b\u5e26\u53e5\u8bdd\u3002\u201d \u3000\u3000\u8d3e\u82f1\u9ad8\u58f0\u9053\uff1a\u201c\u603b\u6355\u5934\u8bf7\u8bb2\uff0c\u8d3e\u82f1\u4e00\u5b9a\u4e00\u5b57\u4e0d\u6f0f\u5730\u5e26\u5230\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6c89\u58f0\u9053\uff1a\u201c\u68a6\u5e7b\u4e4b\u5200\u6b63\u662f\u65b0\u623f\u8840\u6848\u7684\u771f\u51f6\uff0c\u53c8\u662f\u5984\u56fe\u52a0\u5bb3\u738b\u77e5\u5e9c\u7684\u6740\u624b\u96c6\u56e2\u7684\u9996 \u8111\uff0c\u8d35\u4e1c\u4e3b\u5982\u80fd\u591f\u5168\u529b\u6355\u6740\u6b64\u4eba\uff0c\u5b98\u5e9c\u4e0d\u4f46\u4e0d\u4f1a\u8ffd\u7a76\u6740\u4eba\u4e4b\u8d23\u4efb\uff0c\u6211\uff0c\u5211\u90e8\u603b\u6355\u5934\u8fd8\u4f1a\u66ff\u4ed6 \u5411\u671d\u5ef7\u8bf7\u529f\uff01\u201d \u3000\u3000\u8d3e\u82f1\u6df1\u6df1\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u8c22\u603b\u6355\u5934\uff01\u201d \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u5c0f\u96c5\u51b2\u4e86\u8fdb\u6765\uff0c\u6025\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4e0d\u597d\u4e86\uff0c\u5c0f\u6587\u59d0\u53c8\u5f00\u59cb\u76ee\u9732\u51f6\u5149\uff0c\u77f3\u742a\u3001\u5f20 \u9e9f\u4e8c\u4eba\u6b63\u5728\u8bbe\u6cd5\u7a33\u4f4f\u5979\u3002\u5c0f\u59d0\uff0c\u4f60\u548c\u60dc\u7389\u59d0\u5feb\u53bb\u770b\u770b\u5427\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7ad9\u8d77\u8eab\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u6709\u8981\u4e8b\uff0c\u8d3e\u603b\u7ba1\u8bf7\u56de\u5e9c\u590d\u547d\u53bb\u5427\u3002\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u542c\u8fd9\u4f4d\u59d1\u5a18\u7684\u8bdd\uff0c\u5e9c\u8859\u4e2d\u662f\u5426\u51fa\u4e86\u4ec0\u4e48\u610f\u5916\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e5f\u6ca1\u4ec0\u4e48\u5927\u4e8b\uff0c\u6211\u90e8\u4e0b\u7684\u5c0f\u6587\u4f3c\u4e4e\u53d7\u4e86\u4e00\u79cd\u5947\u6bd2\u7684\u63a7\u5236\uff0c\u795e\u667a\u4e0a\u6709\u4e9b\u4e0d\u592a \u6e05\u695a\u3002\u201d \u3000\u3000\u8d3e\u82f1\u773c\u4e2d\u5ffd\u7136\u95ea\u51fa\u4e86\u7cbe\u5149\uff0c\u9053\uff1a\u201c\u5947\u6bd2\uff1f\u80fd\u63a7\u5236\u4eba\u795e\u667a\u7684\u5947\u6bd2\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u8d3e\u82f1\u6c89\u9ed8\u4e86\u4e00\u4f1a\u513f\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u8d3e\u82f1\u8fd9\u5c31\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u4e0d\u7b49\u7a0b\u5c0f\u8776\u4f17\u4eba\u6709\u6240\u8868\u793a\uff0c\u4ed6\u4e00\u4e2a\u8f6c\u8eab\uff0c\u5df2\u98d8\u7136\u5411\u9662\u5916\u800c\u53bb\u3002 \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6211\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u4e0d\u660e\u767d\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u8fd9\u8d3e\u82f1\u5f88\u663e\u7136\u662f\u4e2a\u53ef\u7591\u4e4b\u4eba\uff0c\u603b\u6355\u5934\u4e3a\u4ec0\u4e48\u8981\u5bf9\u4ed6\u8bf4\u90a3\u4e48\u591a\u60c5\u51b5\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5230\u65f6\u5019\u4f60\u5c31\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u8bf4\u6bd5\uff0c\u5979\u5306\u5306\u5411\u5c0f\u6587\u4f4f\u7684\u623f\u95f4\u8d70\u53bb\u3002 \u3000\u3000\u5979\u8d76\u5230\u7684\u53ca\u65f6\uff0c\u56e0\u4e3a\u5c0f\u6587\u7adf\u7136\u5df2\u4ece\u5e8a\u4e0a\u722c\u4e86\u8d77\u6765\uff0c\u6b63\u6076\u72e0\u72e0\u5730\u903c\u89c6\u7740\u88ab\u77f3\u742a\u3001\u5f20\u9e9f\u63a9\u5728 \u8eab\u540e\u7684\u738b\u5c11\u537f\u3002 \u3000\u3000\u77f3\u742a\u3001\u5f20\u9e9f\u4e8c\u4eba\u957f\u5251\u90fd\u5df2\u51fa\u9798\uff0c\u4ed6\u4eec\u8138\u4e0a\u7684\u795e\u60c5\u5341\u5206\u590d\u6742\uff0c\u65e2\u6709\u75db\u60dc\u4e4b\u60c5\uff0c\u4e5f\u6709\u601c\u60af\u4e4b \u610f\uff0c\u8fd8\u6709\u4e09\u5206\u6050\u60f6\u548c\u60f6\u60d1\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u75be\u6b65\u8d70\u5230\u5c0f\u6587\u9762\u524d\uff0c\u9053\uff1a\u201c\u5c0f\u6587\uff0c\u4f60\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u5c0f\u6587\u4f38\u624b\u6293\u8d77\u4e86\u5e8a\u8fb9\u7684\u5b9d\u5251\uff0c\u77aa\u7740\u53cc\u773c\uff0c\u6ee1\u9762\u6740\u6c14\u5730\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4e3a\u4ec0\u4e48\u8981\u6321\u5728\u6211\u9762 \u524d\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53f9\u4e86\u53e3\u6c14\uff0c\u52aa\u529b\u5730\u7b11\u7740\uff0c\u9053\uff1a\u201c\u6211\u662f\u603b\u6355\u5934\u7a0b\u5c0f\u8776\uff0c\u662f\u4f60\u6700\u91cd\u89c6\u7684\u5c0f\u59d0\u554a\u3002\u201d \u3000\u3000\u5c0f\u6587\u773c\u4e2d\u7684\u51f6\u5149\u66f4\u5f3a\u70c8\u4e86\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u603b\u6355\u5934\uff0c\u4ec0\u4e48\u5c0f\u59d0\uff0c\u6211\u4e0d\u77e5\u9053\uff0c\u5feb\u5feb\u8ba9\u5f00\u8def\uff0c \u6211\u8981\u6740\u4eba\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u8981\u6740\u8c01\uff1f\u201d \u3000\u3000\u201c\u4ed6\uff01\u201d\u5c0f\u6587\u4e00\u6307\u738b\u5c11\u537f\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4e0d\u7981\u8138\u8272\u5927\u53d8\uff0c\u4f38\u51fa\u7ea4\u624b\u8f7b\u8f7b\u4e00\u6325\uff0c\u5c0f\u6587\u53c8\u660f\u8ff7\u8fc7\u53bb\uff0c\u6454\u5012\u5728\u5e8a\u4e0a\u4e86\u3002\u5c0f\u96c5\u6025\u5f97 \u90fd\u5feb\u54ed\u51fa\u6765\u4e86\uff0c\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4f60\u770b\u600e\u4e48\u529e\u624d\u597d\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u56de\u8fc7\u5934\uff0c\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u6709\u4ec0\u4e48\u597d\u529e\u6cd5\uff1f\u201d \u3000\u3000\u60dc\u7389\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u8fd9\u51e0\u5929\uff0c\u60dc\u7389\u4e00\u76f4\u5728\u6e29\u4e60\u5148\u7236\u6559\u7ed9\u6211\u7684\u795e\u672f\uff0c\u4f46\u4e00\u65f6\u95f4\u4e5f\u6ca1\u80fd\u60f3\u51fa \u8be5\u5982\u4f55\u65b9\u80fd\u89e3\u6551\u5c0f\u6587\uff0c\u6709\u4e00\u4e2a\u529e\u6cd5\u53ef\u5148\u8bd5\u4e00\u8bd5\uff0c\u53ea\u662f\u6211\u4e5f\u6ca1\u6709\u628a\u63e1\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4ec0\u4e48\u529e\u6cd5\uff1f\u4e3a\u4ec0\u4e48\u6ca1\u6709\u628a\u63e1\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u60dc\u7389\u59d1\u5a18\uff0c\u4e0d\u7ba1\u6709\u65e0\u628a\u63e1\uff0c\u603b\u8981\u8bd5\u4e00\u8bd5\u65b9\u597d\uff0c\u5c31\u7b97\u662f\u738b\u67d0\u6c42\u4f60\uff0c\u4e00\u5b9a\u8981\u6cbb \u597d\u5c0f\u6587\u59d1\u5a18\u3002\u4e0d\u7136\uff0c\u738b\u67d0\u5fc5\u5c06\u5185\u759a\u4e00\u751f\u554a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u770b\u7740\u738b\u5c11\u537f\uff0c\u6697\u6697\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u6211\u65e0\u6cd5\u914d\u5236\u89e3\u836f\uff0c\u4e3b\u8981\u662f\u56e0\u4e3a\u4e0d\u77e5\u9053\u5c0f\u6587\u6240\u4e2d\u4e4b\u6bd2\u7684\u4e3b\u8981\u6210\u4efd\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8981\u600e\u6837\u624d\u80fd\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u5fc5\u9700\u4e00\u4f4d\u5185\u529f\u7cbe\u6df1\u7684\u4eba\u914d\u5408\u6211\u67e5\u89c6\u5c0f\u6587\u7684\u5185\u606f\u60c5\u51b5\u548c\u5947\u7ecf\u516b\u8109\u7684\u60c5\u51b5\uff0c\u624d\u80fd \u786e\u5b9a\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u770b\u6211\u884c\u4e0d\u884c\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u603b\u6355\u5934\u5185\u529f\u5f53\u7136\u6781\u662f\u7cbe\u6df1\uff0c\u53ea\u662f\uff0c\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u5979\u50cf\u662f\u6709\u8bdd\u4e0d\u597d\u76f4\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u7b11\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\uff0c\u60dc\u7389\u4f60\u662f\u62c5\u5fc3\u4e07\u4e00\u6211\u5185\u529f\u5dee\u4e86\u90a3\u4e48\u4e00\u5206\u534a\u5206\uff0c\u4f1a\u5bf9\u5c0f\u6587 \u6709\u635f\u4f24\u3002\u201d \u3000\u3000\u60dc\u7389\u5f88\u4e25\u8083\u5730\u9053\uff1a\u201c\u4e0d\u4ec5\u5bf9\u5c0f\u6587\u6709\u635f\u4f24\uff0c\u800c\u4e14\u5c0f\u6587\u4f53\u5185\u7684\u5947\u6bd2\u4e5f\u4f1a\u9006\u6d41\u5165\u603b\u6355\u5934\u7684\u4f53 \u5185\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u806a\u660e\u8fc7\u4eba\uff0c\u4e00\u542c\u6b64\u8a00\u4e0d\u7981\u4e5f\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u5c0f\u6587\u4e2d\u7684\u5947\u6bd2\u8fd9\u6837\u5389\u5bb3\uff0c\u5b9e\u5728\u662f\u5979\u4e5f\u96be\u4ee5\u60f3\u50cf\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u5ffd\u5730\uff0c\u5979\u76d8\u817f\u5750\u5728\u5e8a\u4e0a\uff0c\u6276\u8d77\u4e86\u5c0f\u6587\uff0c\u65ad\u7136\u9053\uff1a\u201c\u60dc\u7389\uff0c\u6211\u4eec\u5f00\u59cb\u5427\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u53eb\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4e0d\u884c\u554a\uff0c\u592a\u5371\u9669\u4e86\uff0c\u4e07\u4e00\u2026\u2026\u201d \u3000\u3000\u5434\u94c1\u5cf0\u3001\u675c\u671b\u6708\u3001\u4e8e\u627f\u5fd7\u4e5f\u90fd\u53eb\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4e0d\u80fd\u5192\u8fd9\u4e2a\u9669\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5c0f\u6587\u4e0e\u6211\u60c5\u540c\u59d0\u59b9\uff0c\u6211\u4e00\u5b9a\u8981\u6551\u5979\uff01\u201d \u3000\u3000\u6240\u6709\u7684\u4eba\u90fd\u65e0\u8bdd\u53ef\u8bf4\u4e86\uff0c\u4e5f\u90fd\u6709\u4e9b\u4e0d\u77e5\u6240\u63aa\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u65ad\u7136\u9053\uff1a\u201c\u60dc\u7389\u7559\u4e0b\uff0c\u5176\u4f59\u7684\u4eba\uff0c\u6211\u547d\u4ee4\u4f60\u4eec\uff0c\u9000\u51fa\u8fd9\u95f4\u623f\u95f4\uff0c\u4fdd\u62a4\u597d\u738b\u5927\u4eba\u3002\u201d \u3000\u3000\u56db\u5927\u6355\u5934\u5f3a\u5fcd\u773c\u4e2d\u95ea\u52a8\u7684\u6cea\u5149\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u5c5e\u4e0b\u9075\u547d\uff01\u201d \u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1 \u3000\u3000\u8db3\u8db3\u4e00\u4e2a\u65f6\u8fb0\u8fc7\u53bb\u4e86\uff0c\u5c0f\u6587\u623f\u95f4\u7684\u95e8\u8fd8\u6ca1\u6709\u6253\u5f00\u3002\u4e5f\u6ca1\u6709\u4e00\u4e1d\u58f0\u606f\u4f20\u51fa\u6765\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\u548c\u77e5\u5e9c\u738b\u5c11\u537f\uff0c\u5211\u623f\u5e08\u7237\u5f20\u5b9d\u5584\u5750\u5728\u5927\u5385\u91cc\uff0c\u6bcf\u4e2a\u4eba\u8138\u4e0a\u90fd\u6ee1\u662f\u7126\u8651\u4e0d\u5b89\u7684\u795e \u60c5\u3002 \u3000\u3000\u4ed6\u4eec\u4e0d\u80fd\u4e0d\u62c5\u5fc3\u554a\u3002 \u3000\u3000\u66f4\u4e0d\u80fd\u4e0d\u4e3a\u7a0b\u603b\u6355\u5934\u7684\u4fa0\u4e49\u5fc3\u80f8\u800c\u611f\u52a8\u3002 \u3000\u3000\u675c\u671b\u6708\u8ba9\u77f3\u742a\u3001\u5f20\u9e9f\u4e8c\u4eba\u7387\u9886\u6797\u4e0d\u51e1\u90e8\u4e0b\u6c5f\u5357\u5206\u53f8\u7684\u5341\u516b\u540d\u597d\u624b\u4e25\u5bc6\u5730\u5b88\u536b\u5728\u5c0f\u6587\u623f\u95f4 \u56db\u5468\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u90fd\u77e5\u9053\uff0c\u7528\u5185\u529f\u66ff\u4eba\u7597\u4f24\u65f6\uff0c\u662f\u6700\u6015\u53d7\u5230\u5916\u754c\u7684\u5e72\u6270\u7684\u3002 \u3000\u3000\u5982\u679c\u8fd9\u4e2a\u65f6\u5019\u6709\u6740\u624b\u95ef\u8fdb\u6765\uff0c\u90a3\u5c31\u5371\u9669\u4e4b\u6781\u4e86\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4e5f\u4e0d\u77e5\u9053\u60c5\u51b5\u600e\u4e48\u6837\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u53f9\u6c14\u9053\uff1a\u201c\u95ee\u9898\u662f\u6211\u4eec\u4e5f\u4e0d\u80fd\u8fdb\u53bb\u770b\u770b\uff0c\u771f\u6025\u4eba\u554a\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8bf8\u4f4d\u90fd\u4e0d\u5fc5\u7740\u6025\uff0c\u603b\u6355\u5934\u65e2\u7136\u4e0b\u4e86\u51b3\u5fc3\uff0c\u81f3\u5c11\u4e5f\u6709\u4e5d\u6210\u628a\u63e1\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u4f60\u4e0d\u7740\u6025\uff0c\u4f60\u4e0d\u7740\u6025\u4e3a\u4ec0\u4e48\u4f1a\u5728\u90a3\u91cc\u4e0d\u52a8\uff0c\u8fd8\u4e00\u5934\u6c57\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u82e6\u7b11\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u4e0d\u53ef\u80fd\u4e0d\u7740\u6025\u3002\u4e0d\u4ec5\u66ff\u603b\u6355\u5934\u7740\u6025\uff0c\u5c0f\u6587\u7740\u6025\uff0c\u4e5f\u66f4\u4e3a\u60dc\u7389\u62c5\u5fc3\u3002 \u3000\u3000\u60dc\u7389\u554a\uff0c\u4f60\u80fd\u67e5\u51fa\u5c0f\u6587\u4e2d\u7684\u662f\u4ec0\u4e48\u6bd2\u5417\uff1f \u3000\u3000\u5434\u94c1\u5cf0\u5ffd\u7136\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\uff0c\u6211\u4e00\u76f4\u89c9\u5f97\u5f88\u5947\u602a\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u603b\u6355\u5934\u8fd9\u6b21\u56de\u6765\u540e\uff0c\u597d\u50cf\u53d8\u5f97\u5f88\u795e\u79d8\u4e86\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u5c91\u4e5f\u6709\u8fd9\u79cd\u611f\u89c9\u3002\u5c0f\u675c\uff0c\u4f60\u5462\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\u4e0d\u662f\u5df2\u7ecf\u8bf4\u8fc7\u4e86\uff0c\u5230\u65f6\u5019\u5927\u5bb6\u81ea\u4f1a\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u8981\u60f3\u77e5\u9053\u603b\u6355\u5934\u9047\u4e0a\u68a6\u5e7b\u4e4b\u5200\u7684\u8be6\u60c5\uff0c\u5f88\u5bb9\u6613\u554a\uff0c\u53eb\u5c0f\u96c5\u6765\u95ee\u4e00\u95ee\uff0c\u4e0d\u5c31 \u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5c0f\u96c5\u73b0\u5728\u628a\u5b88\u9662\u95e8\uff0c\u804c\u8d23\u91cd\u5927\uff0c\u8fd8\u662f\u4e0d\u8981\u53eb\u4e86\u5427\u3002\u6211\u76f8\u4fe1\uff0c\u603b\u6355\u5934\u5df2\u7ecf\u6709 \u4e86\u5979\u7684\u5168\u76d8\u8ba1\u5212\uff0c\u6211\u4eec\u53ea\u8981\u5949\u547d\u884c\u4e8b\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u5c0f\u675c\u7684\u8bdd\u5f88\u6709\u9053\u7406\u3002\u738b\u5927\u4eba\uff0c\u4f60\u770b\u5462\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u6309\u603b\u6355\u5934\u6240\u8bf4\uff0c\u68a6\u5e7b\u4e4b\u5200\u6b63\u662f\u65b0\u623f\u8840\u6848\u7684\u771f\u51f6\uff0c\u8840\u6848\u53ef\u4ee5\u8bf4\u5df2\u7ecf\u7834\u4e86\uff0c\u5269 \u4e0b\u7684\u95ee\u9898\u4fbf\u662f\u8be5\u5982\u4f55\u7f09\u62ff\u51f6\u72af\u3002\u53ea\u662f\u738b\u67d0\u4e00\u76f4\u60f3\u4e0d\u901a\u68a6\u5e7b\u4e4b\u5200\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u6740\u6211\uff0c\u800c\u4e14\u8fd8\u6709 \u56db\u51e4\u697c\u4e3b\u548c\u8840\u624b\u65b9\u8f6e\u8fd9\u7b49\u608d\u532a\u53c2\u4e0e\u5176\u4e8b\u3002\u4e0d\u8fc7\u8fd9\u6837\u4e5f\u597d\uff0c\u738b\u67d0\u6b63\u53ef\u4ee5\u4ee5\u8eab\u4e3a\u8bf1\u9975\uff0c\u8bf1\u4ed6\u4eec\u6765 \u5e9c\u8859\uff01\u201d \u3000\u3000\u56db\u5927\u6355\u5934\u5fc3\u91cc\u611f\u52a8\uff0c\u4e00\u65f6\u95f4\u90fd\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u4e48\u597d\u3002 \u3000\u3000\u5211\u623f\u5e08\u7237\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u56db\u4f4d\u53f8\u4e3b\uff0c\u5927\u4eba\uff0c\u4f9d\u6211\u770b\uff0c\u8fd9\u4ef6\u6848\u5b50\u53ef\u80fd\u7275\u8fde\u6781\u5e7f\u3001\u6781\u5927\uff0c\u6740\u5b98 \u5c31\u662f\u9020\u53cd\u3002\u5982\u679c\u6ca1\u6709\u6765\u5934\u6781\u5927\u4e4b\u4eba\u5728\u540e\u6491\u8170\uff0c\u8840\u624b\u65b9\u8f6e\u3001\u68a6\u5e7b\u4e4b\u5200\u7b49\u4eba\u53c8\u5c82\u6562\u5982\u6b64\u56a3\u5f20\uff01\u7a0b \u603b\u6355\u5934\u5343\u91cc\u8fe2\u8fe2\u4e0b\u626c\u5dde\uff0c\u4e13\u4e3a\u4fdd\u62a4\u738b\u5927\u4eba\u800c\u6765\uff0c\u8bf4\u660e\u5979\u5bf9\u6b64\u6848\u5185\u60c5\u65e9\u6709\u4e00\u5b9a\u4e4b\u4e86\u89e3\uff0c\u53ea\u4e0d\u8fc7 \u56e0\u4e3a\u65e9\u8bf4\u51fa\u6765\uff0c\u6015\u6253\u4e71\u4e86\u5168\u76d8\u8ba1\u5212\u800c\u5df2\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u8d5e\u53f9\u9053\uff1a\u201c\u5f20\u5e08\u7237\u9ad8\u89c1\uff0c\u5434\u67d0\u5341\u5206\u53f9\u670d\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u8bf4\u5f97\u5bf9\uff01\u8bf4\u5f97\u597d\uff01\u54b1\u4eec\u53ea\u8981\u9f50\u5fc3\u8ddf\u7740\u603b\u6355\u5934\uff0c\u6309\u8ba1\u884c\u4e8b\u5c31\u884c\u4e86\uff0c\u4e0d\u7528\u518d\u80e1 \u601d\u4e71\u60f3\u4e86\uff01\u201d \u3000\u3000------------- \u3000\u3000\u5b64\u5251\u751f  \u626b\u63cf\u3000  cndy001 zhuyj(\u6731\u836b\u9636)  OCR  \uff0c\u65e7\u96e8\u697c\u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u5341\u4e5d\u7ae0 \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u7a0b\u5c0f\u8776\u548c\u60dc\u7389\u51fa\u73b0\u5728\u5927\u5385\u5916\u3002 \u3000\u3000\u738b\u5c11\u537f\u5fd9\u8fce\u4e0a\u53bb\uff0c\u95ee\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u5c0f\u6587\u59d1\u5a18\u60c5\u51b5\u5982\u4f55\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5979\u88ab\u6211\u70b9\u4e86\u660f\u7761\u7a74\uff0c\u5927\u6982\u4f1a\u7761\u4e0a\u4e2a\u4e00\u4e24\u5929\u3002\u201d \u3000\u3000\u4e00\u8fb9\u8bf4\uff0c\u4e00\u8fb9\u8d70\u8fdb\u5927\u5385\uff0c\u5728\u4e00\u5f20\u6905\u4e0a\u5750\u4e0b\u3002 \u3000\u3000\u675c\u671b\u6708\u95ee\u60dc\u7389\u9053\uff1a\u201c\u60dc\u7389\uff0c\u67e5\u51fa\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u60dc\u7389\u70b9\u70b9\u5934\uff0c\u5f88\u4e25\u8083\u5730\u9053\uff1a\u201c\u67e5\u51fa\u6765\u4e86\uff0c\u6bd2\u836f\u662f\u56db\u51e4\u697c\u4e3b\u914d\u7684\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u5403\u60ca\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u56e0\u4e3a\u53ea\u6709\u795e\u533b\u95e8\u4e2d\u4eba\uff0c\u624d\u80fd\u914d\u51fa\u8fd9\u6837\u590d\u6742\u5389\u5bb3\u7684\u6bd2\u836f\u6765\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u8fd8\u672a\u8bf4\u8bdd\uff0c\u5c91\u5578\u864e\u5df2\u559c\u9053\uff1a\u201c\u5c0f\u6587\u6709\u6551\u4e86\uff01\u60dc\u7389\u59d1\u5a18\u4e0d\u6b63\u662f\u795e\u533b\u4eec\u7684\u4f20\u4eba\u561b\uff01\u4f60 \u5927\u5e08\u4f2f\u4e0d\u8001\u4e66\u751f\u547c\u5ef6\u8fdc\u914d\u7684\u836f\uff0c\u4f60\u4e00\u5b9a\u80fd\u914d\u51fa\u89e3\u836f\u6765\u5427\uff01\u201d \u3000\u3000\u60dc\u7389\u5446\u5446\u5730\u60f3\u4e86\u597d\u4e00\u9635\u5b50\uff0c\u767d\u7389\u822c\u7684\u8138\u4e0a\u5ffd\u7136\u6709\u70b9\u7ea2\uff0c\u9053\uff1a\u201c\u89e3\u836f\u53ea\u80fd\u4ece\u547c\u5ef6\u8fdc\u624b\u4e0a\u8981 \u624d\u884c\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u914d\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u6025\u9053\uff1a\u201c\u60dc\u7389\u59d1\u5a18\uff0c\u4e0d\u7ba1\u591a\u4e48\u73cd\u5947\u7684\u836f\u6750\uff0c\u53ea\u8981\u4f60\u8bf4\u51fa\u6765\uff0c\u738b\u67d0\u4e00\u5b9a\u7aed\u5c3d\u5168\u529b\uff0c \u627e\u5230\u5b83\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u4e0e\u836f\u6750\u65e0\u5173\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u5230\u5e95\u8981\u4ec0\u4e48\uff0c\u4f60\u5feb\u8bf4\u561b\uff0c\u83ab\u4e0d\u662f\u60f3\u6025\u6b7b\u8001\u5c91\uff01\u201d \u3000\u3000\u60dc\u7389\u54ac\u4e86\u54ac\u7259\uff0c\u53c8\u770b\u4e86\u770b\u7a0b\u5c0f\u8776\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u8981\u4eba\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u5413\u4e86\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u8981\u4eba\uff1f\u8981\u4ec0\u4e48\u4eba\uff1f\u8981\u4eba\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u5ffd\u7136\u60f3\u8d77\u5728\u56db\u51e4\u697c\u7684\u906d\u9047\uff0c\u95ee\u9053\uff1a\u201c\u60dc\u7389\uff0c\u662f\u4e0d\u662f\u5fc5\u987b\u7528\u4eba\u6765\u5408\u836f\uff0c\u624d\u884c\uff1f\u201d \u3000\u3000\u60dc\u7389\u7528\u529b\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u675c\u8001\u7237\uff0c\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u90a3\u5929\u6211\u5728\u56db\u51e4\u697c\uff0c\u5c31\u5dee\u70b9\u88ab\u697c\u4e3b\u6293\u53bb\u5408\u836f\u53bb\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u96be\u602a\u3002\u603b\u6355\u5934\uff0c\u5e9c\u8859\u5185\u6293\u6709\u51e0\u540d\u6740\u624b\uff0c\u53cd\u6b63\u8fd9\u4e9b\u4eba\u8be5\u6b7b\uff0c\u5c31\u8ba9\u60dc\u7389\u59d1\u5a18\u4ece \u4e2d\u6311\u4e00\u4e2a\uff0c\u4e3a\u5c0f\u6587\u5408\u836f\u5427\uff01\u201d \u3000\u3000\u60dc\u7389\u53f9\u6c14\u9053\uff1a\u201c\u4e0d\u884c\u3002\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7ec8\u4e8e\u5f00\u53e3\u4e86\uff0c\u800c\u4e14\u8bdd\u4e00\u8bf4\u51fa\u53e3\uff0c\u5c31\u8ba9\u5927\u5bb6\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u4f60\u4eec\u60f3\u4e00\u60f3\uff0c\u5c0f\u6587\u4e3a\u4ec0\u4e48 \u51f6\u6027\u53d1\u4f5c\u65f6\uff0c\u9996\u5148\u603b\u662f\u8981\u60f3\u6740\u738b\u77e5\u5e9c\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u5f88\u60ed\u6127\u5730\u9053\uff1a\u201c\u5f53\u7136\u662f\u56e0\u4e3a\u738b\u67d0\u4e00\u5f00\u59cb\u6709\u4e9b\u770b\u4e0d\u8d77\u5979\uff0c\u4f24\u4e86\u5979\u7684\u81ea\u5c0a\u5fc3\u7684\u7f18\u6545\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5bf9\uff0c\u5927\u5bb6\u518d\u60f3\u4e00\u60f3\uff0c\u4e3a\u4ec0\u4e48\u6f5c\u4f0f\u5728\u91d1\u5e9c\u7684\u51f6\u624b\u4e13\u7ed9\u5c0f\u6587\u4e0b\u6bd2\u5462\uff1f\u90a3\u5929\u5728\u82b1 \u5385\u4f1a\u5ba1\u91d1\u5c0f\u7709\uff0c\u6211\u3001\u5c0f\u96c5\u3001\u60dc\u7389\uff0c\u5c0f\u6587\u53ef\u90fd\u5728\u573a\u554a\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u60f3\u4e86\u4e00\u60f3\uff0c\u4fbf\u9053\uff1a\u201c\u83ab\u975e\u662f\u51f6\u624b\u5f53\u65f6\u5df2\u7ecf\u770b\u51fa\u4e86\u738b\u5927\u4eba\u4f24\u4e86\u5c0f\u6587\u7684\u81ea\u5c0a\u5fc3\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u675c\u53f8\u4e3b\u806a\u660e\u8fc7\u4eba\uff0c\u4e00\u8bed\u4e2d\u7684\u3002\u6211\u4eec\u51e0\u4e2a\u7684\u884c\u8e2a\u5176\u5b9e\u65e9\u5728\u6740\u624b\u96c6\u56e2\u638c\u63e1\u4e4b\u4e2d\uff0c \u4ed6\u4eec\u4e5f\u4e00\u5b9a\u65e9\u5c31\u77e5\u9053\u6211\u8eab\u8fb9\u6709\u5c0f\u6587\u3001\u5c0f\u96c5\u4e8c\u4eba\u3002\u90a3\u5929\u4f1a\u5ba1\u65f6\uff0c\u6211\u8ba9\u5c0f\u6587\u5973\u626e\u7537\u88c5\uff0c\u7ad9\u5728\u738b\u5927 \u4eba\u8eab\u8fb9\uff0c\u51f6\u624b\u4e00\u5b9a\u770b\u51fa\u6765\u4e86\uff0c\u800c\u4e14\u8fd8\u770b\u51fa\u6211\u8fd9\u6837\u505a\u662f\u4e3a\u4e86\u4fdd\u62a4\u738b\u5927\u4eba\u3002\u5f53\u65f6\uff0c\u738b\u5927\u4eba\u80af\u5b9a\u5df2 \u7ecf\u8ba4\u4e3a\u5c0f\u6587\u8fd9\u6837\u4e00\u4e2a\u5a07\u5f31\u7684\u5c0f\u5973\u5b50\u4e0d\u53ef\u80fd\u6709\u80fd\u529b\u4fdd\u62a4\u4ed6\uff0c\u6240\u4ee5\u795e\u8272\u4e4b\u95f4\u591a\u5c11\u6709\u4e9b\u53cd\u5e94\u3002\u800c\u5c0f \u6587\u77e5\u9053\u738b\u5927\u4eba\u7684\u60f3\u6cd5\u540e\uff0c\u795e\u8272\u4e4b\u95f4\u4e5f\u4f1a\u6d41\u9732\u51fa\u5c11\u8bb8\u6068\u610f\uff0c\u6240\u4ee5\u51f6\u624b\u624d\u9009\u5979\u4e3a\u4e0b\u6bd2\u76ee\u6807\u7684\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u8d5e\u53f9\u9053\uff1a\u201c\u603b\u6355\u5934\u771f\u662f\u7edd\u9876\u806a\u660e\uff0c\u5206\u6790\u7684\u5934\u5934\u662f\u9053\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u7684\u610f\u601d\u662f\u4e0d\u662f\u8bf4\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u4ed6\u6709\u610f\u65e0\u610f\u770b\u4e86\u738b\u5c11\u537f\u4e00\u773c\u3002 \u3000\u3000\u8fd9\u4e00\u773c\u770b\u7684\u98de\u5feb\u3002 \u3000\u3000\u5927\u5385\u4e4b\u4e2d\u9664\u4e86\u7a0b\u5c0f\u8776\uff0c\u6ca1\u6709\u5176\u4ed6\u4efb\u4f55\u4eba\u53d1\u73b0\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u675c\u671b\u6708\u53c8\u8f6c\u773c\u53bb\u770b\u60dc\u7389\u3002 \u3000\u3000\u60dc\u7389\u4e5f\u6781\u5feb\u6781\u8f7b\u5730\u70b9\u4e86\u4e00\u4e0b\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6240\u4ee5\u8bf4\uff0c\u89e3\u836f\u53ea\u80fd\u5411\u56db\u51e4\u697c\u4e3b\u4e0d\u8001\u4e66\u751f\u547c\u5ef6\u8fdc \u53bb\u8981\u4e86\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u4e5f\u60f3\u660e\u767d\u4e86\uff0c\u4ed6\u53ef\u662f\u4e2a\u76f4\u6027\u5b50\uff0c\u5927\u58f0\u9053\uff1a\u201c\u60dc\u7389\u59d1\u5a18\uff0c\u603b\u6355\u5934\uff0c\u4f60\u4eec\u7684\u610f\u601d\u662f\u8bf4 \u5fc5\u987b\u62ff\u738b\u5927\u4eba\u5408\u836f\u624d\u884c\uff1f\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u5176\u4ed6\u4eba\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u738b\u5c11\u537f\u3001\u5f20\u5b9d\u5584\u4ee5\u53ca\u4e8e\u627f\u5fd7\u3001\u5434\u94c1\u5cf0\u662f\u56e0\u4ed6\u8bf4\u7684\u8bdd\u5403\u60ca\uff0c\u800c\u7a0b\u5c0f\u8776\u3001\u60dc\u7389\u3001\u675c\u671b\u6708\u662f\u5403 \u60ca\u4ed6\u4e0d\u77e5\u8f7b\u91cd\u5730\u5c06\u8fd9\u8bdd\u53e3\u6ca1\u906e\u62e6\u5730\u76f4\u901a\u901a\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u738b\u5c11\u537f\u9762\u8272\u4e00\u53d8\uff0c\u53d8\u5f97\u5f88\u82cd\u767d\u3002 \u3000\u3000\u4ed6\u51db\u7136\u5730\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u60dc\u7389\u59d1\u5a18\uff0c\u53ea\u8981\u80fd\u6551\u5c0f\u6587\u59d1\u5a18\uff0c\u5c31\u7b97\u662f\u8981\u5272\u4e0b\u738b\u67d0\u8eab\u4e0a\u4e00\u4e24\u5757 \u8089\uff0c\u738b\u67d0\u4e5f\u4e0d\u5728\u4e4e\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u5fc3\u91cc\u771f\u662f\u592a\u611f\u52a8\u4e86\u3002 \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u60dc\u7389\u59d1\u5a18\uff0c\u4f60\u8bf4\u8981\u54ea\u91cc\u7684\u8089\uff0c\u53ea\u7ba1\u4e0b\u624b\u5272\u53bb\uff01\u201d \u3000\u3000\u60dc\u7389\u8138\u8272\u5fae\u53d8\uff0c\u5148\u662f\u53d8\u5f97\u6709\u4e9b\u7ea2\uff0c\u7136\u540e\u53c8\u53d8\u5f97\u82cd\u767d\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u738b\u5927\u4eba\u4e0d\u7528\u518d\u575a\u6301\u4e86\uff0c\u56db\u51e4\u697c\u4e3b\u7edd\u975e\u603b\u6355\u5934\u4e4b\u654c\u624b\uff01\u6211\u4eec\u4eca\u591c\u5c31\u53bb\u56db\u51e4\u697c\uff0c \u593a\u56de\u89e3\u836f\u6765\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\uff01\u5c31\u8fd9\u6837\u505a\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u5982\u679c\u6211\u4eec\u53bb\u56db\u51e4\u697c\uff0c\u5e9c\u8859\u7684\u9632\u5907\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u8981\u4e0d\u5148\u8bf7\u9a6c\u63d0\u7763\u8c03\u6765\u6c34\u5e08\u7cbe\u9510\uff0c\u5148\u5b88\u536b\u4e00\u665a\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u6837\u4e00\u6765\uff0c\u654c\u4eba\u4e00\u5b9a\u4f1a\u5bdf\u89c9\u5230\u6211\u4eec\u5c06\u51fa\u5e9c\u8859\u6709\u6240\u884c\u52a8\uff0c\u4e0d\u884c\u3002\u201d\u5c91\u5578\u864e\u9053\uff1a \u201c\u90a3\u8be5\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u4eec\u5206\u5175\u4e24\u8def\uff0c\u6211\u3001\u60dc\u7389\u3001\u675c\u53f8\u4e3b\u7387\u6c5f\u5357\u5206\u53f8\u5341\u540d\u597d\u624b\u591c\u88ad\u56db\u51e4\u697c\uff0c\u5c0f\u96c5 \u548c\u4e09\u5927\u6355\u5934\uff0c\u6797\u4e0d\u51e1\u548c\u53e6\u5916\u4e8c\u5341\u4e03\u540d\u597d\u624b\u7559\u5b88\u5e9c\u8859\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8fd9\u6837\u5f88\u597d\u3002\u603b\u6355\u5934\uff0c\u4f60\u548c\u5c0f\u96c5\u9047\u4e0a\u68a6\u5e7b\u4e4b\u5200\u65f6\uff0c\u662f\u5426\u8fd8\u6709\u8840\u624b\u65b9\u8f6e\u7684\u7070\u8863 \u6740\u624b\u7ec4\u5408\u5728\u573a\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u6b63\u8981\u544a\u8bc9\u4f60\u4eec\uff0c\u6211\u5df2\u7ecf\u627e\u51fa\u4e86\u8fd9\u4e2a\u7ec4\u5408\u7684\u5f31\u70b9\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u559c\u51fa\u671b\u5916\uff0c\u9053\uff1a\u201c\u592a\u597d\u4e86\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u7070\u8863\u6740\u624b\u7ec4\u5408\u7684\u5173\u952e\u4e4b\u5904\u5728\u4ed6\u4eec\u7684\u6b65\u6cd5\uff0c\u800c\u4e14\u4e09\u4eba\u4e2d\u7684\u9996\u8981\u4e4b\u4eba\u662f\u624b\u6301\u9752 \u94a2\u8f6e\u5c45\u4e2d\u7684\u90a3\u4e2a\u3002\u54b1\u4eec\u53ef\u4ee5\u8ba9\u6797\u4e0d\u51e1\u7387\u4ed6\u90e8\u4e0b\u4e09\u5341\u4e03\u540d\u4e00\u6d41\u597d\u624b\u5206\u6210\u5341\u4e5d\u7ec4\uff0c\u4e24\u4eba\u4e00\u7ec4\uff0c\u4e00 \u4eba\u4e13\u95e8\u7528\u94c1\u7ec3\u8bbe\u6cd5\u7eca\u4f4f\u624b\u6301\u7740\u94a2\u8f6e\u7684\u7070\u8863\u6740\u624b\uff0c\u7ec4\u5408\u5c31\u7834\u4e86\uff0c\u53e6\u4e00\u4eba\u5c31\u53ef\u51b2\u4e0a\u6740\u654c\u3002\u4f60\u4eec\u4e5f \u90fd\u77e5\u9053\uff0c\u90a3\u4e9b\u7070\u8863\u4eba\u672c\u8eab\u7684\u4e2a\u4eba\u6b66\u529f\u662f\u4e0d\u9ad8\u7684\uff0c\u6740\u6389\u4ed6\u4eec\u5f88\u5bb9\u6613\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u5927\u559c\u9053\uff1a\u201c\u771f\u662f\u592a\u597d\u4e86\uff01\u8981\u77e5\u9053\uff0c\u4f7f\u7528\u94c1\u7ec3\u7f20\u4eba\uff0c\u672c\u662f\u6355\u5feb\u7684\u57fa\u672c\u6b66\u529f\u4e4b\u4e00\uff0c\u73b0 \u5728\u6d3e\u4e0a\u5927\u7528\u573a\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u867d\u7136\u5982\u6b64\uff0c\u54b1\u4eec\u7684\u884c\u52a8\u8fd8\u662f\u8981\u4fdd\u5bc6\uff0c\u8981\u505a\u51fa\u4e00\u79cd\u6211\u4eec\u6240\u6709\u4eba\u90fd\u5728\u5e9c\u8859\u4e4b\u4e2d \u7684\u5047\u50cf\u3002\u800c\u4e14\uff0c\u6211\u4eec\u5728\u56db\u51e4\u697c\u7684\u884c\u52a8\u4e5f\u8981\u5feb\uff0c\u6740\u8fdb\u53bb\uff0c\u627e\u5230\u89e3\u836f\u5c31\u7528\u6700\u5feb\u7684\u901f\u5ea6\u64a4\u51fa\u6218\u6597\uff0c \u8fd4\u56de\u5e9c\u8859\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u611f\u52a8\u5730\u9053\uff1a\u201c\u603b\u6355\u5934\u548c\u4f17\u4f4d\u6355\u5934\u4e3a\u4e86\u738b\u67d0\u4e0d\u60dc\u4e0e\u51f6\u624b\u4ee5\u6027\u547d\u76f8\u6355\u6740\uff0c\u738b\u67d0\u771f\u662f\u4e0d \u77e5\u8be5\u8bf4\u4ec0\u4e48\u624d\u597d\u3002\u603b\u6355\u5934\uff0c\u5982\u679c\u56db\u51e4\u697c\u5b9e\u529b\u5f3a\u5927\uff0c\u5f62\u52bf\u592a\u51f6\u9669\uff0c\u4f60\u4eec\u5c31\u4e0d\u8981\u8fdb\u53bb\u4e86\uff0c\u8fd8\u662f\u53d6 \u6211\u8eab\u4e0a\u4e4b\u8840\u8089\uff0c\u4e3a\u5c0f\u6587\u59d1\u5a18\u5408\u836f\u5427\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6c89\u541f\u4e00\u9635\uff0c\u770b\u4e86\u60dc\u7389\u4e00\u773c\uff0c\u767d\u5ae9\u7684\u9762\u5e9e\u4e5f\u5ffd\u7136\u53d8\u5f97\u7eef\u7ea2\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u738b\u5927\u4eba\u4e0d\u7528 \u62c5\u5fc3\uff0c\u5982\u679c\u6b64\u884c\u4e0d\u80fd\u6210\u529f\uff0c\u4e5f\u53ea\u80fd\u518d\u6c42\u5927\u4eba\u4e86\uff0c\u5230\u65f6\u5019\u518d\u51b3\u5b9a\u5427\uff01\u201d \u3000\u3000\u5929\u9ed1\u900f\u4e86\u4e4b\u540e\uff0c\u7a0b\u5c0f\u8776\u4e0e\u60dc\u7389\u3001\u675c\u671b\u6708\u53ca\u5341\u4f4d\u6c5f\u5357\u5206\u53f8\u597d\u624b\u88c5\u675f\u505c\u5f53\uff0c\u6f5c\u51fa\u4e86\u5e9c\u8859\u3002\u4f46 \u4ed6\u4eec\u5e76\u6ca1\u6709\u76f4\u5954\u56db\u51e4\u697c\uff0c\u800c\u662f\u5728\u5e9c\u8859\u56db\u5468\u57cb\u4f0f\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u67e5\u660e\u7684\u786e\u6ca1\u6709\u4efb\u4f55\u4eba\u6ce8\u610f\u5230\u4ed6\u4eec \u7684\u884c\u8e2a\uff0c\u624d\u5f00\u59cb\u884c\u52a8\u3002 \u3000\u3000\u4ed6\u4eec\u5341\u4e09\u4eba\u5230\u8fbe\u56db\u51e4\u697c\u5916\u65f6\uff0c\u591c\u5df2\u7ecf\u5f88\u6df1\u4e86\uff0c\u57ce\u4e2d\u4e00\u7247\u6f06\u9ed1\uff0c\u56db\u51e4\u697c\u4e2d\u4e5f\u662f\u4e00\u904d\u6f06\u9ed1\uff0c \u53ea\u5728\u5ead\u9662\u6df1\u5904\uff0c\u9690\u9690\u900f\u51fa\u4e00\u4e1d\u4eae\u5149\u3002 \u3000\u3000\u60dc\u7389\u4f4e\u58f0\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u90a3\u4eae\u706f\u4e4b\u5904\uff0c\u5e94\u8be5\u5c31\u662f\u4e0d\u8001\u4e66\u751f\u547c\u5ef6\u8fdc\u7684\u4f4f\u5904\uff01\u201d\u7a0b\u5c0f\u8776\u51dd\u76ee \u89c2\u671b\u4e86\u597d\u4e00\u9635\u5b50\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5ead\u9662\u91cc\u600e\u4e48\u5982\u6b64\u5b89\u9759\uff1f\u8fd9\u60c5\u51b5\u53ef\u662f\u5f88\u6709\u4e9b\u4e0d\u6b63\u5e38\u554a\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u8fd9\u91cc\u7684\u60c5\u51b5\u6211\u6bd4\u8f83\u719f\uff0c\u60dc\u7389\u66f4\u719f\uff0c\u4e00\u8fdb\u5ead\u9662\uff0c\u56db\u4e0b\u91cc\u90fd\u6709\u6697\u54e8\uff0c \u6211\u548c\u60dc\u7389\u5148\u53bb\u5c06\u6697\u54e8\u5904\u7406\u6389\u5427\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u770b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u53bb\u5427\uff0c\u4e00\u5b9a\u4e0d\u8981\u6253\u8349\u60ca\u86c7\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53c8\u62c9\u7740\u60dc\u7389\uff0c\u9053\uff1a\u201c\u60dc\u7389\uff0c\u8981\u5c0f\u5fc3\uff0c\u4f60\u7684\u6b66\u529f\u6bd4\u675c\u53f8\u4e3b\u9ad8\uff0c\u4e00\u5b9a\u8981\u7167\u987e\u597d\u4ed6\u554a\u3002\u201d \u3000\u3000\u8bdd\u8bed\u4e2d\u6d41\u9732\u51fa\u660e\u663e\u7684\u5173\u5fc3\u4e4b\u610f\u3002 \u3000\u3000\u60dc\u7389\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\uff0c\u603b\u6355\u5934\u3002\u201d \u3000\u3000\u5979\u5fc3\u91cc\u4e5f\u4e0d\u77e5\u662f\u751c\uff0c\u662f\u9178\u3002 \u3000\u3000\u4eca\u591c\u4e0e\u675c\u90ce\u5e76\u80a9\u4f5c\u6218\uff0c\u5b9e\u5728\u662f\u4e00\u4ef6\u4ee4\u5979\u9ad8\u5174\u7684\u4e8b\uff0c\u53ef\u603b\u6355\u5934\u5bf9\u675c\u671b\u6708\u660e\u663e\u7684\u5173\u5fc3\uff0c\u53c8\u8ba9 \u5979\u6709\u4e9b\u918b\u610f\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u80fd\u542c\u51fa\u6765\uff0c\u603b\u6355\u5934\u7684\u5173\u5fc3\u5df2\u7ecf\u8d85\u51fa\u4e86\u5bf9\u666e\u901a\u4e0b\u5c5e\u7684\u754c\u9650\u3002 \u3000\u3000\u675c\u671b\u6708\u5411\u5979\u62db\u4e86\u62db\u624b\uff0c\u4e24\u4eba\u4e00\u8dc3\u800c\u8d77\uff0c\u8054\u8882\u8dc3\u8fdb\u4e86\u56f4\u5899\uff0c\u5728\u4e00\u4e1b\u82b1\u6811\u540e\u9690\u8eab\u4e0b\u6765\u3002 \u3000\u3000\u675c\u671b\u6708\u5c3d\u5c55\u76ee\u529b\uff0c\u56db\u4e0b\u63a2\u89c6\uff0c\u5f88\u5feb\u5c31\u53d1\u73b0\u4e86\u56db\u5904\u6697\u54e8\u3002 \u3000\u3000\u770b\u4ed6\u4f0f\u8eab\u673a\u8b66\u89c2\u5bdf\u7684\u6837\u5b50\uff0c\u771f\u662f\u6d51\u8eab\u90fd\u5145\u6ee1\u4e86\u667a\u6167\u4e0e\u529b\u91cf\u3002 \u3000\u3000\u201c\u675c\u90ce\u554a\uff0c\u4f60\u771f\u662f\u4e2a\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff01\u201d\u60dc\u7389\u5fd6\u9053\uff1a\u201c\u6211\u662f\u591a\u4e48\u5730\u7231\u4f60\u5440\uff01\u201d\u4e00\u65f6\u95f4\u5979\u5ffd \u7136\u6709\u4e86\u4e00\u79cd\u60f3\u4f0f\u8fdb\u675c\u671b\u6708\u6000\u91cc\uff0c\u62b1\u7d27\u4ed6\u5f3a\u6709\u529b\u7684\u808c\u8089\u5f3a\u5065\u7684\u8170\u80cc\u7684\u6b32\u671b\u3002 \u3000\u3000\u5979\u4e0d\u7981\u60f3\u8d77\u4e86\u767d\u5929\u675c\u671b\u6708\u90a3\u4e00\u53cc\u5927\u624b\u6709\u529b\u800c\u6e29\u67d4\u5730\u5728\u5979\u808c\u80a4\u4e0a\u629a\u6478\u65f6\u90a3\u79cd\u706b\u70ed\u7684\u6ecb\u5473\uff0c \u7f8e\u5999\u7684\u6ecb\u5473\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u95f4\uff0c\u5979\u5df2\u51d1\u8fd1\u4e86\u675c\u671b\u6708\uff0c\u5c06\u8eab\u4f53\u6328\u5728\u4ed6\u80a9\u4e0a\u3002 \u3000\u3000\u675c\u671b\u6708\u56de\u8fc7\u5934\uff0c\u770b\u89c1\u60dc\u7389\u6b63\u75f4\u75f4\u5730\u671b\u7740\u81ea\u5df1\uff0c\u6a31\u5507\u534a\u5f00\uff0c\u53e3\u4e2d\u6c14\u5982\u5e7d\u5170\u3002\u4ed6\u51d1\u8fc7\u5934\u53bb\u5728 \u5979\u6a31\u5507\u4e0a\u543b\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u60dc\u7389\u4f60\u770b\uff0c\u524d\u8fb9\u5047\u5c71\u82b1\u6728\u540e\u9762\uff0c\u5de6\u53f3\u5404\u6709\u4e24\u4e2a\u6697\u54e8\uff0c\u4f60\u8d1f\u8d23\u5de6\u8fb9\uff0c \u6211\u53bb\u53f3\u8fb9\u3002\u201d \u3000\u3000\u60dc\u7389\u4e5f\u543b\u4e86\u4ed6\u4e00\u4e0b\uff0c\u6709\u4e9b\u604b\u604b\u4e0d\u820d\u5730\u6084\u7136\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u597d\u5427\u3002\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u5979\u5df2\u626d\u8eab\u4e00\u6e9c\uff0c\u5f88\u5feb\u63a5\u8fd1\u4e86\u90a3\u4e24\u4e2a\u6697\u54e8\u3002 \u3000\u3000\u4e24\u4e2a\u6697\u54e8\u90fd\u662f\u624b\u63d0\u94a2\u5200\u7684\u9ed1\u8863\u4eba\uff0c\u770b\u6765\u4e5f\u90fd\u6709\u4e9b\u56f0\u5026\u4e86\uff0c\u6b63\u4f4e\u7740\u5934\u6253\u76f9\u3002\u60dc\u7389\u5feb\u901f\u51b2\u4e0a \u53bb\uff0c\u4e24\u624b\u5206\u5f00\u4e00\u5f39\uff0c\u5f39\u51fa\u4e24\u80a1\u767d\u70df\u3002 \u3000\u3000\u4e24\u540d\u9ed1\u8863\u4eba\u4e00\u58f0\u4e5f\u6ca1\u53d1\u51fa\uff0c\u8f6f\u8f6f\u5012\u5730\u3002 \u3000\u3000\u60dc\u7389\u773c\u660e\u624b\u5feb\uff0c\u62a2\u8fc7\u4e86\u4e8c\u4eba\u624b\u4e0a\u7684\u94a2\u5200\uff0c\u4ee5\u514d\u94a2\u5200\u843d\u5730\uff0c\u5f04\u51fa\u58f0\u54cd\u6765\u3002 \u3000\u3000\u518d\u8bf4\u675c\u671b\u6708\uff0c\u4ed6\u6478\u8fc7\u53bb\u65f6\uff0c\u4e24\u540d\u6697\u54e8\u6b63\u8e72\u5728\u5730\u4e0a\uff0c\u9760\u7740\u4e00\u5904\u5047\u5c71\uff0c\u51d1\u5728\u4e00\u8d77\u804a\u5929\u5462\u3002 \u3000\u3000\u675c\u671b\u6708\u8f7b\u8f7b\u62bd\u51fa\u4e03\u661f\u5b9d\u5251\uff0c\u731b\u5730\u4e00\u6325\uff0c\u5df2\u780d\u5f00\u4e00\u4eba\u7684\u5589\u5499\uff0c\u53e6\u4e00\u4eba\u6b63\u8981\u53eb\uff0c\u675c\u671b\u6708\u7684\u5de6 \u624b\u94a2\u94a9\u822c\u7684\u4e94\u6307\u5df2\u5361\u4f4f\u4e86\u4ed6\u7684\u8116\u5b50\uff0c\u50cf\u62ce\u5c0f\u9e21\u4e00\u6837\u5c06\u4ed6\u62ce\u4e86\u8d77\u6765\u3002\u90a3\u4eba\u88ab\u4ed6\u5413\u5f97\u4e24\u773c\u76f4\u7ffb\u767d\u3002 \u3000\u3000\u675c\u671b\u6708\u6084\u58f0\u9053\uff1a\u201c\u4e0d\u8bb8\u53eb\uff0c\u4e0d\u7136\u6211\u5361\u6b7b\u4f60\uff01\u201d \u3000\u3000\u90a3\u4eba\u76f4\u70b9\u5934\u3002 \u3000\u3000\u675c\u671b\u6708\u7a0d\u7a0d\u677e\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u547c\u5ef6\u8fdc\u5728\u4e0d\u5728\uff1f\u201d \u3000\u3000\u201c\u5728\u201d \u3000\u3000\u201c\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5728\u540e\u9662\u9601\u697c\u4e0a\u3002\u201d \u3000\u3000\u201c\u56db\u51e4\u697c\u4e2d\u90fd\u6709\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u697c\u4e3b\u6b63\u4e0e\u6740\u624b\u4e4b\u738b\u8bae\u4e8b\u3002\u201d \u3000\u3000\u201c\u68a6\u5e7b\u4e4b\u5200\u5728\u5417\uff1f\u201d \u3000\u3000\u201c\u5728\u3002\u201d \u3000\u3000\u201c\u540e\u9662\u6709\u591a\u5c11\u4eba\u628a\u5b88\uff1f\u201d \u3000\u3000\u201c\u6709\u2026\u2026\u6709\u56db\u5341\u591a\u4e2a\u3002\u201d \u3000\u3000\u201c\u662f\u8c01\u7684\u624b\u4e0b\uff1f\u201d \u3000\u3000\u201c\u6709\u697c\u4e3b\u624b\u4e0b\u7684\u7fe0\u4e91\u3001\u601c\u82b1\uff0c\u8fd8\u6709\u65b9\u8f6e\u624b\u4e0b\u7684\u6740\u624b\u3002\u201d \u3000\u3000\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u68a6\u5e7b\u4e4b\u5200\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u4e0d\u2026\u2026\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u770b\u6765\u4ed6\u662f\u771f\u7684\u4e0d\u77e5\u9053\uff0c\u56e0\u4e3a\u4ed6\u4e00\u76f4\u5f88\u8001\u5b9e\uff0c\u6709\u95ee\u5fc5\u7b54\u3002 \u3000\u3000\u4ed6\u8fd9\u79cd\u8eab\u5206\u7684\u5c0f\u89d2\u8272\uff0c\u7684\u786e\u4e5f\u4e0d\u53ef\u80fd\u77e5\u9053\u9996\u8111\u4eec\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u60dc\u7389\u5df2\u63a0\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u90a3\u4eba\u7adf\u7136\u8ba4\u8bc6\u60dc\u7389\uff0c\u6c42\u9976\u9053\uff1a\u201c\u59d1\u2026\u2026\u59d1\u5a18\uff0c\u9976\u2026\u2026\u9976\u547d\u554a\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u60dc\u7389\uff0c\u8fd9\u4eba\u8fd8\u5f88\u8001\u5b9e\uff0c\u9976\u4e86\u4ed6\u5427\uff01\u201d \u3000\u3000\u60dc\u7389\u54ac\u7259\u9053\uff1a\u201c\u547c\u5ef6\u8fdc\u624b\u4e0b\u4e4b\u4eba\uff0c\u6211\u8981\u89c1\u4e00\u4e2a\u6740\u4e00\u4e2a\u3002\u201d \u3000\u3000\u4e00\u638c\u62cd\u51fa\uff0c\u6b63\u4e2d\u90a3\u4eba\u8111\u95e8\u3002 \u3000\u3000\u90a3\u4eba\u4e24\u773c\u4e00\u7ffb\uff0c\u987f\u65f6\u4e86\u7ed3\u3002 \u3000\u3000\u675c\u671b\u6708\u4e0d\u7981\u6697\u81ea\u53f9\u6c14\u3002\u4ed6\u77e5\u9053\u60dc\u7389\u5bf9\u547c\u5ef6\u8fdc\u6000\u6709\u523b\u9aa8\u4ec7\u6068\uff0c\u4f46\u4ed6\u4e5f\u4e0d\u613f\u60dc\u7389\u56e0\u6b64\u800c\u6210\u4e3a \u4e00\u4e2a\u55dc\u6740\u6210\u6027\u4e4b\u4eba\u3002 \u3000\u3000\u201c\u6162\u6162\u5730\u7528\u67d4\u60c5\u53bb\u5316\u89e3\u5979\u5fc3\u4e2d\u7684\u4ec7\u6740\u4e4b\u5fc3\u5427\uff01\u201d\u675c\u671b\u6708\u6697\u5fd6\u9053\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u611f\u5230\u4e86\u81ea\u5df1\u5bf9\u60dc\u7389\u5e94\u8d1f\u7684\u8d23\u4efb\u4e86\u3002 \u3000\u3000\u60dc\u7389\u4e00\u638c\u6253\u6b7b\u9ed1\u8863\u6697\u54e8\uff0c\u72b9\u4e0d\u89e3\u6068\uff0c\u53c8\u5728\u90a3\u4eba\u5c38\u4f53\u4e0a\u8e22\u4e86\u51e0\u811a\uff0c\u65b9\u9053\uff1a\u201c\u675c\u5927\u54e5\uff0c\u4e0d\u77e5 \u9644\u8fd1\u8fd8\u6709\u6ca1\u6709\u6697\u54e8\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5927\u6982\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u6211\u53bb\u901a\u77e5\u603b\u6355\u5934\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u597d\u3002\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u60dc\u7389\u4e00\u4e2a\u98de\u8eab\uff0c\u63a0\u51fa\u4e86\u5899\u5916\u3002 \u3000\u3000\u7728\u773c\u95f4\uff0c\u7a0b\u5c0f\u8776\u548c\u60dc\u7389\u5e26\u7740\u6c5f\u5357\u5206\u53f8\u5341\u540d\u597d\u624b\u9646\u7eed\u63a0\u5230\u675c\u671b\u6708\u8eab\u8fb9\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u542c\u4e86\u675c\u671b\u6708\u7684\u4ecb\u7ecd\uff0c\u4e00\u6325\u624b\u9053\uff1a\u201c\u53bb\u540e\u9662\uff0c\u884c\u52a8\u4e00\u5b9a\u8981\u8f7b\u3002\u201d \u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1 \u3000\u3000\u540e\u9662\u9601\u697c\u4e0a\u867d\u7136\u70b9\u7740\u706f\uff0c\u4f46\u706f\u5149\u5e76\u4e0d\u4eae\uff0c\u770b\u53bb\u6655\u6697\u5f97\u5f88\uff0c\u50cf\u662f\u4e00\u70b9\u9b3c\u706b\u3002\u540e\u9662\u5e94\u8be5\u6709\u5f88 \u591a\u4eba\u7684\uff0c\u5374\u6781\u662f\u5b89\u9759\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7acb\u5373\u611f\u5230\u4e86\u5371\u9669\u3002 \u3000\u3000\u8fd9\u91cc\u4e5f\u7684\u786e\u5f88\u5371\u9669\uff0c\u9ed1\u4e4e\u4e4e\u7684\u5ead\u9662\u770b\u4e0a\u53bb\u6740\u6c14\u68ee\u68ee\uff0c\u53ef\u4ee5\u76f8\u4fe1\uff0c\u56db\u51e4\u697c\u4e3b\u7684\u4e2d\u67a2\u91cd\u5730\u4e00 \u5b9a\u662f\u673a\u5173\u91cd\u91cd\uff0c\u9632\u62a4\u4e25\u5bc6\u3002 \u3000\u3000\u53ef\u8c13\u6b65\u6b65\u662f\u9677\u9631\uff0c\u5904\u5904\u6709\u6740\u673a\u554a\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u697c\u4e0a\u4eae\u706f\u7684\u623f\u95f4\u91cc\u9690\u9690\u98d8\u6765\u4e00\u4e24\u53e5\u6a21\u7cca\u7684\u4eba\u58f0\u3002 \u3000\u3000\u770b\u6765\uff0c\u90a3\u540d\u9ed1\u8863\u4eba\u6ca1\u6709\u6492\u8c0e\uff0c\u56db\u51e4\u697c\u4e3b\u548c\u8840\u624b\u65b9\u8f6e\u3001\u68a6\u5e7b\u4e4b\u5200\u6b63\u5728\u5546\u8bae\u4e8b\u60c5\u3002\u60dc\u7389\u9053\uff1a \u201c\u603b\u6355\u5934\uff0c\u54b1\u4eec\u8fd9\u5c31\u51b2\u4e0a\u53bb\uff0c\u6740\u4e86\u4e0d\u8001\u4e66\u751f\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u5fc3\u91cc\u4e00\u5927\u8df3\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u60dc\u7389\u554a\uff0c\u4f60\u771f\u662f\u88ab\u4ec7\u6068\u51b2\u660f\u4e86\u5934\u8111\u4e86\uff0c\u8fd9\u6837\u53ef\u662f\u8981\u574f\u5927 \u4e8b\u7684\u5440\uff01\u201d \u3000\u3000\u4ed6\u5fd9\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u4e0d\u8981\u5fd8\u4e86\u4eca\u5929\u665a\u4e0a\u7684\u76ee\u7684\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u6740\u4e86\u547c\u5ef6\u8fdc\uff0c\u4e0d\u662f\u80fd\u66f4\u65b9\u4fbf\u627e\u5230\u89e3\u836f\u5417\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4f60\u5fd8\u5566\uff0c\u68a6\u5e7b\u4e4b\u5200\u73b0\u5728\u4e5f\u5728\u8fd9\u91cc\u5462\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u675c\u53f8\u4e3b\u8bf4\u5f97\u5bf9\uff0c\u68a6\u5e7b\u4e4b\u5200\u6b66\u529f\u4e4b\u9ad8\uff0c\u6211\u4e5f\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u4e00\u65e6\u6740\u5c06\u8d77\u6765\uff0c \u540e\u679c\u4e0d\u582a\u8bbe\u60f3\u3002\u201d \u3000\u3000\u60dc\u7389\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u538b\u4e0b\u5fc3\u4e2d\u4ec7\u6068\u4e4b\u706b\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u8bf4\u8be5\u600e\u4e48\u529e\uff1f\u201d\u7a0b\u5c0f\u8776\u9053\uff1a \u201c\u7b49\u3002\u201d \u3000\u3000\u201c\u7b49\uff1f\u7b49\u4ec0\u4e48\uff1f\u7b49\u5230\u4ec0\u4e48\u65f6\u5019\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u8981\u77e5\u9053\uff0c\u5728\u6ca1\u6709\u529e\u6cd5\u4e4b\u65f6\uff0c\u9759\u4ee5\u89c2\u53d8\uff0c\u662f\u6700\u597d\u7684\u4e5f\u6700\u6709\u7528\u7684\u5bf9\u7b56 \u4e4b\u4e00\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u660e\u767d\u4e86\uff0c\u603b\u6355\u5934\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u4f38\u957f\u8116\u5b50\uff0c\u76f4\u52fe\u52fe\u770b\u7740\u9601\u697c\uff0c\u773c\u4e2d\u7cbe\u5149\u95ea\u52a8\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u675c\u53f8\u4e3b\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u68a6\u5e7b\u4e4b\u5200\u5c31\u5728\u697c\u4e0a\uff0c\u8981\u662f\u80fd\u4e0a\u53bb\u770b\u770b\u4ed6\u7684\u771f\u9762\u76ee\uff0c\u8be5\u6709\u591a\u597d\u554a\uff01\u603b\u6355\u5934\uff0c \u4f60\u8bf4\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u4e5f\u6709\u8fd9\u4e2a\u60f3\u6cd5\uff0c\u53ea\u662f\u4e0d\u592a\u884c\u5f97\u901a\u3002\u201d \u3000\u3000\u6797\u671b\u6708\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u68a6\u5e7b\u4e4b\u5200\u6b66\u529f\u5df2\u8fbe\u5316\u5883\uff0c\u5982\u679c\u6211\u4eec\u9760\u5f97\u592a\u8fd1\u4e86\uff0c\u52bf\u5fc5\u4f1a\u88ab\u4ed6\u53d1\u73b0\u7684\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u4eec\u5c0f\u5fc3\u4e00\u70b9\u8fd8\u4e0d\u884c\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5f88\u4e25\u8083\u5730\u9053\uff1a\u201c\u6b66\u529f\u4e00\u9053\uff0c\u6beb\u5398\u4e4b\u5dee\u90fd\u4f1a\u51b3\u5b9a\u80dc\u8d25\uff0c\u5728\u8fd9\u65b9\u9762\uff0c\u5c24\u5176\u662f\u4e8b\u5173\u5927\u5c40 \u4e4b\u65f6\uff0c\u53ef\u4e0d\u80fd\u5fc3\u5b58\u534a\u5206\u4fa5\u5e78\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u4f4e\u58f0\u9053\uff1a\u201c\u603b\u6355\u5934\u6559\u8bad\u5f97\u5bf9\uff0c\u5c5e\u4e0b\u4e00\u5b9a\u7262\u8bb0\u5728\u5fc3\uff01\u201d \u3000\u3000\u4ed6\u4eec\u5341\u4e09\u4eba\u5404\u81ea\u627e\u5230\u9690\u85cf\u4e4b\u5730\u57cb\u4f0f\u4e86\u8d77\u6765\uff0c\u4e00\u76f4\u7b49\u4e86\u5c06\u8fd1\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5ffd\u542c\u5f97\u697c\u4e0a\u6709\u5f00\u623f \u95e8\u7684\u58f0\u97f3\uff0c\u7136\u540e\u4fbf\u89c1\u5230\u4e24\u6761\u4eba\u5f71\u81ea\u534a\u7a7a\u98de\u63a0\u800c\u53bb\uff0c\u75be\u5982\u6d41\u661f\u3002 \u3000\u3000\u603b\u6355\u5934\u5206\u6790\u7684\u679c\u7136\u5f88\u6b63\u786e\uff01\u675c\u671b\u6708\u6697\u5fd6\u9053\uff0c\u4ece\u8f7b\u529f\u4e0a\u6765\u770b\uff0c\u4e0d\u4ec5\u68a6\u5e7b\u4e4b\u5200\u5185\u529f\u5df2\u8fbe\u5316\u5883\uff0c \u5c31\u8fde\u6740\u624b\u4e4b\u738b\u8840\u624b\u65b9\u8f6e\u4e5f\u662f\u4e00\u4f4d\u6b66\u529f\u60ca\u4eba\u7684\u5927\u9ad8\u624b\uff01 \u3000\u3000\u60f3\u5230\u81ea\u5df1\u5982\u679c\u521a\u624d\u4e00\u5b9a\u8981\u6f5c\u8fdb\u9601\u697c\u7684\u540e\u679c\uff0c\u4e0d\u7981\u6d51\u8eab\u51b7\u6c57\u5982\u6cc9\u6d8c\uff01 \u3000\u3000\u53c8\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u7a0b\u5c0f\u8776\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u63a8\u7b97\u8840\u624b\u65b9\u8f6e\u3001\u68a6\u5e7b\u4e4b\u5200\u5df2\u7ecf\u8d70\u8fdc\u4e86\uff0c\u56db\u51e4\u697c\u4e3b\u4eca \u591c\u5373\u7136\u6ca1\u6709\u884c\u52a8\u4e4b\u610f\uff0c\u73b0\u5728\u4e00\u5b9a\u5df2\u7ecf\u51c6\u5907\u4f11\u606f\u4e86\uff0c\u6b63\u662f\u6211\u4eec\u52a8\u624b\u7684\u597d\u65f6\u673a\u5440\uff01\u201d \u3000\u3000\u5979\u7ad9\u8d77\u8eab\uff0c\u5bf9\u675c\u671b\u6708\u9053\uff1a\u201c\u675c\u53f8\u4e3b\uff0c\u4f60\u7387\u9886\u5341\u540d\u597d\u624b\u5728\u697c\u4e0b\u4e25\u9635\u4ee5\u5f85\uff0c\u6211\u4e0e\u60dc\u7389\u8fdb\u697c\u4e0a \u53d6\u836f\u53bb\uff01\u201d \u3000\u3000\u8bf4\u6bd5\uff0c\u4e00\u62c9\u60dc\u7389\uff0c\u4e8c\u4eba\u98de\u8eab\u63a0\u8fdb\u4e86\u697c\u4e0b\u5927\u5385\u3002 \u3000\u3000\u5927\u5385\u5185\u4e00\u7247\u9ed1\u6697\uff0c\u4f46\u7a0b\u5c0f\u8776\u548c\u60dc\u7389\u7686\u6709\u8fc7\u4eba\u4e4b\u76ee\u529b\uff0c\u6240\u4ee5\u5385\u5185\u4e00\u5207\uff0c\u5c3d\u5728\u5979\u4fe9\u773c\u4e2d\u3002 \u3000\u3000\u60dc\u7389\u5de6\u53f3\u89c2\u770b\u4e00\u756a\uff0c\u6084\u58f0\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u5982\u679c\u60dc\u7389\u6ca1\u770b\u9519\uff0c\u4ece\u8fd9\u8fb9\u8d70\uff0c\u5e94\u8be5\u5c31\u662f\u4e0d\u8001\u4e66 \u751f\u5408\u836f\u7684\u79d8\u5ba4\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u95ee\u9053\uff1a\u201c\u79d8\u5ba4\u5185\u6709\u4eba\u628a\u5b88\u5417\uff1f\u6b66\u529f\u5982\u4f55\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u672c\u6765\u662f\u7531\u4e00\u4e2a\u5510\u5b37\u5b37\u628a\u5b88\uff0c\u4f46\u5979\u6570\u5929\u4e4b\u524d\u88ab\u6211\u4e49\u7236\u91cd\u521b\uff0c\u73b0\u5728\u6362\u4e86\u8c01\uff0c\u6211\u53ef \u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5979\u59d3\u5510\uff1f\u662f\u56db\u5ddd\u5510\u95e8\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53f9\u9053\uff1a\u201c\u8fde\u56db\u5ddd\u5510\u95e8\u4e5f\u6709\u4eba\u7275\u8fde\u5728\u5176\u4e2d\uff0c\u96be\u602a\u6740\u624b\u96c6\u56e2\u5982\u6b64\u4e4b\u5f3a\u3002\u201d\u4e8c\u4eba\u653e\u8f7b\u811a \u6b65\uff0c\u5feb\u901f\u901a\u8fc7\u4e00\u6761\u8fc7\u9053\uff0c\u7ed5\u8fc7\u4e00\u5904\u56de\u5eca\uff0c\u5ffd\u7136\u770b\u89c1\u4e24\u540d\u9752\u8863\u5973\u5a62\u6b63\u5411\u8fd9\u8fb9\u8d70\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u4e24\u540d\u9752\u8863\u5973\u5a62\u4e00\u4e2a\u62ce\u7740\u4e00\u76cf\u5c0f\u706f\u7b3c\uff0c\u53e6\u4e00\u4e2a\u62ce\u7740\u4e00\u4e2a\u98df\u76c6\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u62c9\u60dc\u7389\uff0c\u4e24\u4eba\u95ea\u8eab\u8eb2\u5230\u5eca\u67f1\u4e4b\u540e\u3002\u4f46\u6253\u7740\u706f\u7b3c\u7684\u5a62\u5973\u773c\u775b\u53d1\u5149\uff0c\u5df2\u770b\u89c1\u524d\u9762\u4eba \u5f71\u4e00\u95ea\uff0c\u7acb\u523b\u5f00\u53e3\u95ee\u9053\uff1a\u201c\u524d\u9762\u662f\u8c01\u5440\uff1f\u201d \u3000\u3000\u542c\u5979\u7684\u8bed\u6c14\uff0c\u7adf\u4e0d\u662f\u5f88\u5403\u60ca\u3002 \u3000\u3000\u5927\u6982\u5979\u5df2\u770b\u51fa\u8eb2\u8d77\u6765\u7684\u662f\u4e24\u4e2a\u5c11\u5973\uff0c\u8fd8\u4ee5\u4e3a\u662f\u5973\u4f34\u8ddf\u5979\u5f00\u73a9\u7b11\u5462\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u95ea\u8eab\u800c\u51fa\uff0c\u98de\u8eab\u6251\u4e0a\uff0c\u4f38\u6307\u4e24\u70b9\uff0c\u5df2\u5c01\u4f4f\u4e86\u5979\u4eec\u7684\u8f6f\u9ebb\u7a74\u3002 \u3000\u3000\u4e24\u540d\u9752\u8863\u5a62\u5973\u76ee\u80fd\u89c6\uff0c\u8033\u80fd\u542c\uff0c\u5374\u662f\u53e3\u4e0d\u80fd\u8a00\uff0c\u6d51\u8eab\u9178\u8f6f\uff0c\u773c\u7741\u7741\u770b\u7740\u60dc\u7389\u548c\u7a0b\u5c0f\u8776\u4e8c \u4eba\u76f4\u5954\u5408\u836f\u5bc6\u5ba4\u800c\u53bb\uff0c\u5374\u4e0d\u80fd\u51fa\u58f0\u793a\u8b66\uff0c\u771f\u662f\u82e6\u4e5f\uff0c\u6025\u715e\uff01 \u3000\u3000\u60dc\u7389\u548c\u7a0b\u5c0f\u8776\u6f5c\u8eab\u5230\u79d8\u5ba4\u4e4b\u5916\uff0c\u5374\u6ca1\u6709\u89c1\u5230\u6709\u4eba\u628a\u5b88\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6697\u559c\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7528\u624b\u8f7b\u8f7b\u4e00\u63a8\uff0c\u95e8\u5374\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u770b\u6765\u8fd9\u9053\u95e8\u662f\u7531\u67d0\u5904\u673a\u5173\u6240\u63a7\u5236\u7684\uff0c\u4fbf\u4fef\u4e0b\u8eab\u6765\uff0c \u5728\u95e8\u56db\u5468\u4ed4\u7ec6\u5bfb\u627e\u673a\u5173\u6240\u5728\u3002 \u3000\u3000\u60dc\u7389\u5728\u4e00\u65c1\u66ff\u5979\u671b\u98ce\u3002 \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u4e00\u4eba\u9053\uff1a\u201c\u8fd9\u5e2e\u5c0f\u8e44\u5b50\uff0c\u8001\u5a46\u5b50\u809a\u4e2d\u9965\u6e34\u96be\u5fcd\uff0c\u8ba9\u5979\u4eec\u7ed9\u5f04\u70b9\u5403\u7684\uff0c\u5230 \u73b0\u5728\u4e5f\u4e0d\u6765\uff0c\u770b\u660e\u5929\u8001\u5a46\u5b50\u4e0d\u7980\u660e\u697c\u4e3b\uff0c\u6253\u65ad\u8fd9\u5c0f\u8e44\u5b50\u7684\u9a9a\u817f\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u548c\u60dc\u7389\u4e00\u60ca\u4e4b\u4e0b\uff0c\u6b63\u60f3\u95ea\u907f\uff0c\u4f46\u8fd9\u4eba\u6765\u7684\u597d\u5feb\uff0c\u5df2\u7ecf\u770b\u89c1\u5979\u4eec\u4e86\u3002\u6765\u4eba\u662f\u4e2a\u767d\u53d1 \u82cd\u82cd\u7684\u8001\u592a\u5a46\uff0c\u5979\u4e00\u770b\u89c1\u60dc\u7389\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u4fbf\u5c16\u58f0\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u4f60\u662f\u60dc\u7389\uff01\u6765\u4eba\u5566\uff01\u53db\u5f92\u60dc \u7389\u5e26\u4eba\u95ef\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u626c\u624b\u4e00\u6325\uff0c\u9c9c\u7ea2\u7684\u7ec6\u7d22\u76f4\u98de\u51fa\u53bb\uff0c\u7f20\u4e0a\u4e86\u8001\u592a\u5a46\u7684\u8116\u5b50\uff0c\u518d\u4e00\u62c9\uff0c\u5979\u5c31\u4ece\u6b64\u518d\u4e5f \u4e0d\u80fd\u8bf4\u51fa\u4e00\u4e2a\u5b57\u4e86\uff01 \u3000\u3000\u4f46\u8001\u5a46\u5b50\u7684\u53eb\u58f0\u8fd8\u662f\u60ca\u8d77\u4e86\u697c\u4e2d\u6240\u6709\u7684\u4eba\uff0c\u4e00\u65f6\u95f4\uff0c\u4f46\u95fb\u56db\u4e0b\u91cc\u4eba\u58f0\u5608\u6742\uff0c\u811a\u6b65\u6025\u4fc3\uff0c \u7d27\u63a5\u7740\uff0c\u697c\u5916\u54cd\u8d77\u4e86\u51e0\u58f0\u60e8\u53eb\u58f0\u3002 \u3000\u3000\u201c\u4e00\u5b9a\u662f\u675c\u53f8\u4e3b\u4ed6\u4eec\u4e0e\u654c\u4eba\u4ea4\u4e0a\u624b\u4e86\uff01\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u60dc\u7389\uff0c\u54b1\u4eec\u4e00\u5b9a\u8981\u5feb\u624d\u662f\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u53ef\u8fd9\u95e8\u6253\u4e0d\u5f00\u5440\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5f88\u9547\u5b9a\u5730\u7b11\u4e00\u7b11\uff0c\u4f38\u624b\u81ea\u8896\u4e2d\u62bd\u51fa\u4e86\u4e00\u67c4\u7cbe\u5149\u56db\u5c04\u7684\u77ed\u5251\u3002 \u3000\u3000\u4f38\u624b\u4e00\u523a\u3001\u4e00\u5212\uff0c\u5df2\u5728\u95e8\u4e0a\u523a\u51fa\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u518d\u52a0\u4e0a\u4e00\u811a\uff0c\u95e8\u201c\u7830\u201d\u5730\u4e00\u58f0\u655e\u5f00\u6765\u3002 \u3000\u3000\u79d8\u5ba4\u5185\u6b63\u4e2d\u592e\u6709\u4e00\u5f20\u7855\u5927\u7684\u767d\u6749\u6728\u957f\u684c\uff0c\u684c\u4e0a\u6446\u7740\u74f6\u74f6\u7f50\u7f50\uff0c\u5200\u526a\u5668\u5177\uff0c\u56db\u58c1\u5168\u662f\u836f\u67dc\u3002 \u3000\u3000\u60dc\u7389\u98de\u5feb\u5730\u5c06\u76ee\u5149\u5728\u56db\u9762\u836f\u67dc\u4e0a\u4e00\u626b\uff0c\u76f4\u51b2\u5411\u4e00\u9762\u67dc\u5b50\uff0c\u4f38\u624b\u62c9\u5f00\u4e00\u4e2a\u62bd\u5323\u3002\u5323\u4e2d\u662f\u7a7a \u7684\uff01 \u3000\u3000\u60dc\u7389\u8138\u4e0a\u8272\u53d8\uff0c\u9053\uff1a\u201c\u4e0d\u597d\uff01\u89e3\u836f\u7adf\u7136\u4e0d\u89c1\u4e86\uff01\u201d \u3000\u3000\u5ffd\u542c\u95e8\u5916\u4e00\u9635\u5927\u7b11\uff0c\u4e00\u4e2a\u4eba\u6717\u58f0\u9053\uff1a\u201c\u5e78\u4e8f\u8001\u592b\u65e9\u6709\u9632\u5907\uff0c\u4e0d\u7136\uff0c\u662f\u975e\u8ba9\u5c14\u7b49\u5f97\u4e86\u624b \u53bb\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u548c\u60dc\u7389\u5e76\u80a9\u51b2\u51fa\u4e86\u79d8\u5ba4\uff0c\u53ea\u89c1\u4e0d\u8001\u4e66\u751f\u547c\u5ef6\u8fdc\u6b63\u7ad9\u5728\u56de\u5eca\u4e4b\u4e0a\uff0c\u8eab\u540e\u7ad9\u7740\u6570\u5341\u540d \u9ed1\u8863\u7070\u8863\u6740\u624b\uff0c\u8fd8\u6709\u51e0\u4f4d\u4e2d\u5e74\u5987\u4eba\u3002 \u3000\u3000\u4e0d\u8001\u4e66\u751f\u547c\u5ef6\u8fdc\u5927\u7b11\u9053\uff1a\u201c\u60dc\u7389\uff0c\u5931\u671b\u4e86\u5427\uff1f\u8001\u592b\u65e9\u5df2\u7b97\u5b9a\u51ed\u4f60\u5b66\u7684\u90a3\u70b9\u795e\u533b\u95e8\u7684\u76ae\u6bdb \u4e4b\u6280\u914d\u4e0d\u51fa\u89e3\u836f\u6765\uff01\u8fdf\u65e9\u4f1a\u5230\u56db\u51e4\u697c\u6765\u5077\u89e3\u836f\uff01\u201d \u3000\u3000\u60dc\u7389\u5389\u58f0\u9053\uff1a\u201c\u5927\u5e08\u4f2f\uff0c\u53ea\u8981\u4f60\u4ea4\u51fa\u89e3\u836f\uff0c\u6211\u53ef\u9976\u4f60\u4e0d\u6b7b\uff01\u201d \u3000\u3000\u4e3a\u4e86\u6551\u6cbb\u5c0f\u6587\u59d1\u5a18\uff0c\u60dc\u7389\u7adf\u7136\u6b32\u7f6e\u6740\u7236\u4e4b\u4ec7\u4e8e\u8111\u540e\u4e86\uff01 \u3000\u3000\u8fd9\u662f\u4f55\u7b49\u7684\u80f8\u895f\u554a\uff01 \u3000\u3000\u8fd9\u8bf4\u660e\u4e86\u5979\u4e0e\u5c0f\u6587\u4e4b\u95f4\u7684\u59d0\u59b9\u4e4b\u60c5\u771f\u662f\u592a\u6df1\u539a\u4e86\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u5fc3\u91cc\u5341\u5206\u6fc0\u52a8\u3002 \u3000\u3000\u4e0d\u8001\u4e66\u751f\u547c\u5ef6\u8fdc\u72c2\u7b11\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u7684\u53e3\u6c14\u4e5f\u592a\u5927\u4e86\uff0c\u4eca\u591c\u8001\u592b\u5c31\u8981\u5c06\u4f60\u65a9\u6740\u4e8e\u2018\u5fc5\u6740 \u5341\u4e09\u5251\u2019\u7684\u5251\u4e0b\u3002\u4f60\u7adf\u6562\u5938\u53e3\u8bf4\u9976\u6211\u4e0d\u6b7b\uff0c\u8fd8\u662f\u5feb\u6c42\u6211\u9976\u4f60\u4e0d\u6b7b\u5427\uff01\u201d\u60dc\u7389\u94f6\u7259\u7d27\u54ac\uff0c\u53cc\u76ee \u55b7\u5c04\u51fa\u51b2\u5929\u7684\u6012\u706b\uff0c\u53cc\u81c2\u4e00\u62db\uff0c\u5c31\u8981\u5411\u547c\u5ef6\u8fdc\u51b2\u8fc7\u53bb\u3002\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4e14\u6162\uff01\u201d \u3000\u3000\u5979\u8e0f\u4e0a\u4e24\u6b65\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u4e0d\u8001\u4e66\u751f\u6709\u591a\u5927\u80fd\u8010\uff0c\u539f\u6765\u5168\u51ed\u51e0\u62db\u4e0d\u600e\u6837\u7684\u5251\u6cd5\uff01\u201d \u3000\u3000\u4e0d\u8001\u4e66\u751f\u547c\u5ef6\u8fdc\u770b\u7740\u5979\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u662f\u5211\u90e8\u603b\u6355\u5934\u7a0b\u5c0f\u8776\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u4e0d\u8001\u4e66\u751f\u547c\u5ef6\u8fdc\u4e24\u53ea\u8001\u773c\u5728\u7a0b\u5c0f\u8776\u5468\u8eab\u4e0a\u4e0a\u4e0b\u4e0b\u731b\u770b\u4e00\u756a\uff0c\u9053\uff1a\u201c\u597d\u4eba\u624d\uff0c\u597d\u4eba\u624d\uff0c\u6b63 \u53ef\u4ee5\u62ff\u6765\u4e3a\u8001\u592b\u5408\u836f\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53f1\u9053\uff1a\u201c\u4f11\u5f97\u53e3\u51fa\u72c2\u8a00\uff01\u50cf\u5c14\u7b49\u4e27\u5fc3\u75c5\u72c2\u4e4b\u4eba\uff0c\u4eba\u4eba\u53ef\u5f97\u800c\u6740\u4e4b\uff01\u6765\u6765\u6765\uff0c\u8ba9\u672c \u603b\u6355\u5934\u89c1\u8bc6\u89c1\u8bc6\u4f60\u7684\u5fc5\u6740\u5341\u4e09\u5251\u5427\uff01\u201d \u3000\u3000\u547c\u5ef6\u8fdc\u4e0d\u7b54\u8bdd\uff0c\u62bd\u51fa\u4e00\u67c4\u5b9d\u5251\uff0c\u53d1\u8db3\u51b2\u4e0a\uff0c\u5b9d\u5251\u95ea\u8d77\u6570\u5341\u9053\u8000\u76ee\u7684\u5149\u5f69\uff0c\u76f4\u5411\u7a0b\u5c0f\u8776\u5f53 \u5934\u7f69\u4e0b\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u6beb\u4e0d\u754f\u60e7\uff0c\u6e05\u53f1\u4e00\u58f0\uff1a\u201c\u8001\u5339\u592b\u53d7\u6b7b\uff01\u201d \u3000\u3000\u6325\u52a8\u53cc\u638c\uff0c\u8fce\u4e86\u4e0a\u53bb\uff01 \u3000\u3000\u4e8c\u4eba\u987f\u65f6\u6218\u4f5c\u4e00\u56e2\u3002 \u3000\u3000\u547c\u5ef6\u8fdc\u7684\u5fc5\u6740\u5341\u4e09\u5251\u679c\u7136\u6709\u5176\u7cbe\u5999\u4e4b\u5904\uff0c\u5251\u5149\u56e2\u56e2\u56f4\u56f0\u4f4f\u4e86\u7a0b\u5c0f\u8776\uff0c\u6bcf\u4e00\u62db\u90fd\u76f4\u523a\u5411\u5979 \u7684\u8981\u5bb3\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4ee5\u4e00\u53cc\u767d\u7389\u822c\u7684\u624b\u638c\u7a7a\u624b\u5bf9\u654c\uff0c\u7adf\u4e5f\u662f\u4e1d\u6beb\u4e0d\u843d\u4e0b\u98ce\u3002 \u3000\u3000\u53ea\u89c1\u5979\u7ea4\u79c0\u767d\u7699\u7684\u4e24\u53ea\u5c0f\u624b\u5982\u4e24\u53ea\u7075\u5de7\u7684\u8774\u8776\u4e00\u822c\u5728\u56e2\u56e2\u5251\u5149\u4e2d\u5de6\u53f3\u7ffb\u98de\uff0c\u4e0a\u4e0b\u4ea4\u653b\uff0c \u5979\u7684\u6b65\u6cd5\u3001\u8eab\u6cd5\u4e5f\u5341\u5206\u7075\u6d3b\uff0c\u7adf\u7136\u80fd\u5728\u5251\u5f71\u4e4b\u95f4\u7684\u7f1d\u9699\u76f4\u6325\u800c\u4e0a\u3002\u547c\u5ef6\u8fdc\u5df2\u4f7f\u51fa\u4e86\u538b\u7bb1\u5e95\u7684 \u7edd\u62db\uff0c\u5374\u672a\u80fd\u5360\u5230\u7a0b\u5c0f\u8776\u534a\u70b9\u4fbf\u5b9c\u3002 \u3000\u3000\u518d\u6597\u4e0a\u4e09\u5341\u62db\uff0c\u547c\u5ef6\u8fdc\u5c31\u6709\u4e9b\u624b\u5fd9\u811a\u4e71\uff0c\u5251\u62db\u4e5f\u4e0d\u4f3c\u4e00\u5f00\u59cb\u65f6\u90a3\u822c\u51cc\u5389\u4e86\u3002\u547c\u5ef6\u8fdc\u7684\u989d \u5934\u4e0a\u5df2\u9690\u9690\u89c1\u6c57\uff01 \u3000\u3000\u96be\u602a\u5979\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u4fbf\u80fd\u5750\u4e0a\u5211\u90e8\u603b\u6355\u5934\u8fd9\u6837\u7684\u9ad8\u4f4d\uff0c\u624b\u5e95\u4e0b\u679c\u7136\u5f88\u662f\u5389\u5bb3\uff01\u547c\u5ef6\u8fdc\u4e00\u9762 \u594b\u529b\u51fa\u5251\uff0c\u4e00\u8fb9\u5728\u5fc3\u91cc\u543c\u9053\uff1a\u770b\u8d77\u6765\uff0c\u5355\u51ed\u5fc5\u6740\u5341\u4e09\u5251\u662f\u5236\u4e0d\u4f4f\u5979\u4e86\uff01\u5fc3\u5ff5\u8f6c\u52a8\u95f4\uff0c\u4ed6\u7684\u5251 \u62db\u5df2\u53d8\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u4e5f\u7acb\u523b\u770b\u51fa\u547c\u5ef6\u8fdc\u7684\u5251\u6cd5\u53d8\u4e86\uff0c\u4f7f\u7684\u662f\u6bd4\u5fc5\u6740\u5341\u4e09\u5251\u66f4\u51cc\u5389\u3001\u4e5f\u66f4\u6b8b\u6bd2\u7684\u4e00\u79cd\u5251 \u6cd5\u3002 \u3000\u3000\u8fd9\u79cd\u5251\u6cd5\u5979\u7adf\u662f\u4ece\u672a\u89c1\u8fc7\u3002 \u3000\u3000\u5bf9\u65b9\u65e2\u7136\u5df2\u7ecf\u53d8\u62db\uff0c\u5979\u81ea\u7136\u4e5f\u8981\u6539\u53d8\u5e94\u5bf9\u4e4b\u7b56\u3002\u4f46\u5c31\u5728\u5979\u6b63\u60f3\u7740\u7528\u4ec0\u4e48\u529e\u6cd5\u624d\u80fd\u51fb\u8d25\u8fd9 \u79cd\u8be1\u5f02\u72e0\u8fa3\u7684\u5251\u6cd5\u65f6\uff0c\u5979\u5df2\u88ab\u547c\u5ef6\u8fdc\u903c\u9000\u4e86\u4e03\u516b\u6b65\u3002 \u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1 \u3000\u3000\u7a0b\u5c0f\u8776\u9677\u5165\u82e6\u6218\u65f6\uff0c\u60dc\u7389\u4e5f\u5df2\u5904\u5728\u6740\u624b\u4eec\u7684\u91cd\u91cd\u5305\u56f4\u4e4b\u4e2d\u3002 \u3000\u3000\u60dc\u7389\u7684\u624b\u4e2d\u4e5f\u6ca1\u6709\u5175\u5668\uff0c\u4f46\u5979\u5374\u80fd\u51ed\u9760\u8f7b\u7075\u5947\u5e7b\u7684\u201c\u8774\u8776\u7a7f\u82b1\u6b65\u201d\u4e00\u6b21\u53c8\u4e00\u6b21\u907f\u5f00\u6740\u624b \u4eec\u72c2\u91ce\u7684\u653b\u51fb\u3002 \u3000\u3000\u56f4\u7740\u5979\u7684\u6740\u624b\u4e0d\u4e0b\u4e8c\u5341\u4eba\uff0c\u53ef\u4ed6\u4eec\u56f4\u6740\u4e86\u534a\u5929\uff0c\u4e5f\u6ca1\u80fd\u4f24\u7740\u5979\u4e00\u5206\u76ae\u8089\uff0c\u53cd\u800c\u88ab\u60dc\u7389\u4e0d \u65f6\u5f39\u51fa\u7684\u4e00\u80a1\u80a1\u8ff7\u6bd2\u653e\u5230\u4e86\u56db\u4e94\u4e2a\u4eba\u3002 \u3000\u3000\u4f46\u65e2\u4f7f\u8fd9\u6837\uff0c\u60dc\u7389\u8fd8\u662f\u611f\u5230\u627f\u53d7\u7684\u538b\u529b\u8d8a\u6765\u8d8a\u5927\u3002 \u3000\u3000\u5979\u975e\u5e38\u5e0c\u671b\u603b\u6355\u5934\u80fd\u5c3d\u5feb\u51fb\u8d25\u4e0d\u8001\u4e66\u751f\uff0c\u6765\u52a9\u5979\u4e00\u81c2\u4e4b\u529b\u3002\u4f46\u4e00\u8f6c\u773c\uff0c\u5374\u770b\u89c1\u603b\u6355\u5934\u6b63 \u88ab\u547c\u5ef6\u8fdc\u7528\u795e\u533b\u95e8\u7684\u7edd\u5b66\u5929\u7f61\u96f7\u9706\u5251\u903c\u5f97\u5411\u5979\u8fd9\u8fb9\u8282\u8282\u540e\u9000\uff01 \u3000\u3000\u675c\u5927\u54e5\u5462\uff1f\u4ed6\u4eec\u662f\u5426\u4e5f\u5df2\u9677\u5165\u5305\u56f4\u4e4b\u4e2d\u4e86\uff1f\u60dc\u7389\u5fc3\u91cc\u5341\u5206\u7126\u6025\u3002 \u3000\u3000\u5979\u662f\u591a\u4e48\u5e0c\u671b\u675c\u671b\u6708\u5728\u8fd9\u5371\u6025\u65f6\u5206\u80fd\u7387\u4eba\u6740\u5c06\u8fdb\u6765\u554a\uff01 \u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1 \u3000\u3000\u90a3\u4e2a\u8001\u592a\u5a46\u7684\u53eb\u58f0\u521a\u4e00\u54cd\u8d77\uff0c\u675c\u671b\u6708\u5fc3\u91cc\u5c31\u731b\u5730\u5927\u8df3\u4e86\u4e00\u4e0b\uff0c\u5931\u58f0\u9053\uff1a\u201c\u54ce\u5440\uff01\u4e0d\u597d\uff01\u201d \u3000\u3000\u4ed6\u77e5\u9053\uff0c\u603b\u6355\u5934\u548c\u60dc\u7389\u7684\u884c\u8e2a\u88ab\u53d1\u73b0\u4e86\u3002 \u3000\u3000\u7728\u773c\u95f4\uff0c\u521a\u624d\u8fd8\u9ed1\u6f06\u6f06\u7684\u5ead\u9662\u91cc\u4e00\u4e0b\u4eae\u8d77\u4e86\u6570\u5341\u76cf\u706f\u7b3c\uff0c\u9601\u697c\u4e4b\u4e2d\u7acb\u523b\u4e5f\u53d8\u5f97\u706f\u706b\u901a\u660e\u3002 \u3000\u3000\u675c\u671b\u6708\u81ea\u9690\u8eab\u4e4b\u5904\u4e00\u8dc3\u800c\u51fa\uff0c\u9ad8\u4e3e\u4e03\u661f\u5b9d\u5251\uff0c\u76f4\u6307\u7740\u697c\u95e8\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5f1f\u5144\u4eec\uff0c\u968f\u6211 \u6740\u8fdb\u53bb\uff01\u201d \u3000\u3000\u6c5f\u5357\u5206\u53f8\u7684\u5341\u540d\u597d\u624b\u521a\u521a\u805a\u96c6\u5230\u4ed6\u8eab\u8fb9\uff0c\u9601\u697c\u4e2d\u548c\u9662\u5916\u5404\u6d8c\u8fdb\u4e00\u6279\u6740\u624b\uff0c\u5c06\u4ed6\u4eec\u5341\u4e00\u4eba \u7d27\u7d27\u56f4\u5728\u5f53\u4e2d\uff01 \u3000\u3000\u8fd9\u4e9b\u6740\u624b\u4eec\u884c\u52a8\u8fc5\u901f\uff0c\u8eab\u6cd5\u4e5f\u5e72\u51c0\u5229\u843d\uff0c\u4e00\u770b\u5c31\u77e5\u9053\u7ecf\u8fc7\u5341\u5206\u4e25\u683c\u7684\u8bad\u7ec3\u3002\u675c\u671b\u6708\u56db\u4e0b \u73af\u987e\u4e00\u773c\uff0c\u7acb\u523b\u610f\u8bc6\u5230\u6740\u624b\u4eec\u7684\u7ea7\u522b\u6bd4\u90a3\u4e9b\u9760\u7ec4\u5408\u53d6\u80dc\u7684\u7070\u8863\u6740\u624b\u4eec\u8981\u9ad8\u5f97\u591a\u3002 \u3000\u3000\u6740\u624b\u4eec\u7684\u5305\u56f4\u5708\u4e00\u5f62\u6210\uff0c\u4fbf\u6709\u654c\u4eba\u5404\u6325\u5175\u5203\uff0c\u5411\u4ed6\u4eec\u731b\u6251\u8fc7\u6765\uff0c\u800c\u7edd\u5927\u90e8\u5206\u6740\u624b\u5374\u53ea\u662f \u5c06\u5175\u5203\u4e3e\u5728\u80f8\u524d\uff0c\u7ad9\u7acb\u4e0d\u52a8\u3002 \u3000\u3000\u4ec5\u4ece\u8fd9\u4e00\u70b9\u4e5f\u53ef\u770b\u51fa\u8fd9\u4e9b\u6740\u624b\u4eec\u7d20\u8d28\u4e4b\u9ad8\uff0c\u56e0\u4e3a\u4ed6\u4eec\u663e\u7136\u61c2\u5f97\uff0c\u5728\u4ee5\u591a\u6253\u5c11\u7684\u65f6\u5019\uff0c\u5982 \u679c\u5168\u90fd\u4e00\u6d8c\u800c\u4e0a\uff0c\u6781\u53ef\u80fd\u53cd\u88ab\u5bf9\u65b9\u4e58\u4e71\u7a81\u56f4\u3002 \u3000\u3000\u6240\u8c13\u4e71\u4e2d\u53d6\u80dc\uff0c\u8bf4\u7684\u6b63\u662f\u8fd9\u4e2a\u9053\u7406\uff0c\u800c\u6d51\u6c34\u6478\u9c7c\uff0c\u4e5f\u4ece\u53cd\u9762\u4e00\u65b9\u8bf4\u660e\u4e86\u8fd9\u4e2a\u9053\u7406\u3002 \u3000\u3000\u675c\u671b\u6708\u4e00\u770b\u773c\u524d\u5f62\u52bf\uff0c\u5c31\u77e5\u9053\u4eca\u591c\u91cc\u5f97\u9760\u5b9e\u529b\u786c\u62fc\u4e86\uff0c\u60f3\u6295\u673a\u53d6\u5de7\u6218\u80dc\u6740\u624b\u4eec\u5df2\u662f\u4e0d\u53ef \u80fd\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u6df1\u6df1\u5730\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u4e03\u661f\u5b9d\u5251\u98de\u901f\u523a\u51fa\uff0c\u5ffd\u4e0a\u5ffd\u4e0b\uff0c\u5ffd\u5de6\u5ffd\u53f3\uff0c\u4f46\u89c1\u5251\u82b1\u6735\u6735\uff0c\u4ee4\u6740 \u624b\u773c\u82b1\u7f2d\u4e71\uff0c\u5e94\u63a5\u4e0d\u6687\u3002 \u3000\u3000\u7acb\u523b\uff0c\u96ea\u4eae\u7684\u5251\u82b1\u91cc\u95ea\u51fa\u4e86\u8840\u5149\u3002 \u3000\u3000\u6740\u624b\u4eec\u60e8\u53eb\u8fde\u58f0\u3002 \u3000\u3000\u4e00\u540d\u6740\u624b\u4e22\u5f00\u5175\u5203\uff0c\u4e24\u624b\u6342\u4f4f\u9762\u90e8\uff0c\u811a\u4e0b\u4e71\u7a9c\u51e0\u6b65\uff0c\u5012\u4e0b\u4e86\u3002 \u3000\u3000\u53e6\u4e00\u540d\u6740\u624b\u957f\u58f0\u60e8\u53eb\uff0c\u4f38\u624b\u5728\u534a\u7a7a\u4e71\u6293\u3002 \u3000\u3000\u7b2c\u4e09\u4e2a\u51b2\u4e0a\u6765\u7684\u6740\u624b\u4e00\u58f0\u4e5f\u6ca1\u53eb\u51fa\uff0c\u5589\u5934\u5904\u55b7\u51fa\u4e00\u80a1\u9c9c\u8840\uff0c\u7830\u7136\u5012\u5730\u3002 \u3000\u3000\u675c\u671b\u6708\u7b2c\u4e00\u8f6e\u653b\u51fb\uff0c\u4fbf\u683c\u6740\u4e09\u4eba\uff0c\u4e0d\u89c9\u7cbe\u795e\u4e3a\u4e4b\u4e00\u632f\u3002 \u3000\u3000\u6c5f\u5357\u5206\u53f8\u7684\u5341\u540d\u597d\u624b\u4e5f\u88ab\u4ed6\u9ad8\u5f3a\u7684\u6b66\u529f\u5251\u6cd5\u6240\u6fc0\u52b1\uff0c\u9f50\u58f0\u5927\u543c\u7740\uff0c\u76f4\u51b2\u5305\u56f4\u4ed6\u4eec\u7684\u6740\u624b \u5927\u9635\u3002 \u3000\u3000\u6740\u624b\u4eec\u518d\u4e5f\u7a33\u4e0d\u4f4f\u9635\u811a\u4e86\u3002 \u3000\u3000\u6df7\u6218\u7acb\u8d77\u3002 \u3000\u3000\u60e8\u53eb\u4e00\u58f0\u63a5\u7740\u4e00\u58f0\u3002 \u3000\u3000\u7a7a\u6c14\u4e2d\u7684\u8840\u8165\u5473\u4e5f\u8d8a\u6765\u8d8a\u6d53\u70c8\u3002 \u3000\u3000\u597d\u60e8\u70c8\u7684\u4e00\u6218\uff01 \u3000\u3000\u597d\u60ca\u5fc3\u52a8\u9b44\u7684\u4e00\u6218\uff01 \u3000\u3000\u675c\u671b\u6708\u5df2\u5c06\u6b66\u529f\u5251\u6cd5\u5145\u5206\u53d1\u6325\u51fa\u6765\uff0c\u811a\u4e0b\u6b65\u6cd5\u7075\u52a8\uff0c\u8eab\u5f62\u98d8\u5ffd\u5982\u9b3c\u9b45\uff0c\u4e00\u4f1a\u513f\u51fa\u73b0\u5728\u8fd9 \u8fb9\uff0c\u4e00\u4f1a\u513f\u51fa\u73b0\u5728\u90a3\u91cc\u3002 \u3000\u3000\u53ea\u8981\u4ed6\u51fa\u73b0\u7684\u5730\u65b9\uff0c\u7acb\u5373\u5c31\u4f1a\u95ea\u51fa\u593a\u76ee\u7684\u5251\u5149\uff0c\u6563\u5f00\u6f2b\u5929\u7684\u8840\u96e8\uff0c\u53d1\u51fa\u6151\u4eba\u7684\u60e8\u568e\u548c\u5c06 \u6b7b\u7684\u547b\u541f\u3002 \u3000\u3000\u5728\u4ed6\u7684\u5251\u4e0b\uff0c\u5df2\u5012\u4e0b\u5341\u4e03\u540d\u6740\u624b\u3002 \u3000\u3000\u6c5f\u5357\u5206\u53f8\u7684\u5341\u540d\u597d\u624b\u4eec\u4e5f\u594b\u529b\u6740\u6b7b\u4e86\u5341\u4e09\u540d\u6740\u624b\uff0c\u4f46\u4e5f\u6709\u4e09\u4eba\u5df2\u7ecf\u9635\u4ea1\uff0c\u56db\u4eba\u53d7\u4e86\u7a0b\u5ea6 \u4e0d\u7b49\u7684\u4f24\u3002 \u3000\u3000\u5269\u4e0b\u7684\u4e94\u516d\u540d\u6740\u624b\u4f3c\u662f\u88ab\u675c\u671b\u6708\u7684\u6740\u6c14\u6240\u6151\u4f0f\uff0c\u4e00\u4e2a\u4e2a\u4e22\u4e0b\u624b\u91cc\u7684\u5175\u5668\uff0c\u62b1\u5934\u56db\u4e0b\u9003\u7a9c\uff01 \u3000\u3000\u675c\u671b\u6708\u5c06\u4e03\u540d\u5f1f\u5144\u805a\u96c6\u8d77\u6765\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\u5df2\u9677\u8eab\u697c\u4e2d\uff0c\u6211\u4eec\u4e00\u5b9a\u8981\u6740\u8fdb\u53bb\uff0c\u548c\u5979\u5e76\u80a9 \u4f5c\u6218\uff0c\u5f1f\u5144\u4eec\uff0c\u4f60\u4eec\u6709\u6ca1\u6709\u53d6\u80dc\u7684\u4fe1\u5fc3\uff1f\u201d \u3000\u3000\u4e03\u4eba\u9ad8\u58f0\u9f50\u547c\uff1a\u201c\u6709\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u8c6a\u6c14\u5343\u4e91\uff0c\u5927\u559d\u9053\uff1a\u201c\u597d\uff01\u597d\u5144\u5f1f\uff01\u6765\u5440\uff0c\u8ddf\u6211\u51b2\u8fdb\u53bb\uff0c\u6740\uff01\u201d \u3000\u3000\u201c\u6740\uff01\u6740\uff01\uff01\u201d \u3000\u3000\u4e03\u4eba\u540c\u58f0\u547c\u201c\u6740\u201d\uff0c\u7d27\u968f\u5728\u675c\u671b\u6708\u8eab\u540e\uff0c\u76f4\u6251\u9601\u697c\u4e2d\u53bb\uff01 \u3000\u3000\u4ed6\u4eec\u8d76\u5230\u7684\u6b63\u53ca\u65f6\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u548c\u60dc\u7389\u5df2\u88ab\u903c\u5728\u4e86\u4e00\u8d77\uff0c\u4e8c\u4eba\u6b63\u80cc\u9760\u7740\u80cc\uff0c\u8054\u624b\u6297\u51fb\u547c\u5ef6\u8fdc\u548c\u6570\u5341\u6740\u624b\u7684\u5408\u51fb\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5df2\u7ecf\u4f7f\u51fa\u4e86\u7ec6\u7d22\uff0c\u5374\u4ecd\u53ea\u80fd\u4e0e\u4e0d\u8001\u4e66\u751f\u6253\u4e2a\u5e73\u624b\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u5979\u4f3c\u4e4e\u4ecd\u6709\u4f59\u529b\uff0c\u56e0\u4e3a\u5979\u4e0d\u65f6\u8fd8\u80fd\u62bd\u7a7a\u5e2e\u4e00\u4e0b\u60dc\u7389\u3002 \u3000\u3000\u51b2\u5f97\u79bb\u60dc\u7389\u6700\u8fd1\u7684\u51e0\u540d\u6740\u624b\u90fd\u88ab\u7a0b\u5c0f\u8776\u7a81\u7136\u98de\u81f3\u7684\u7ec6\u7d22\u62e7\u65ad\u4e86\u8116\u5b50\u3002 \u3000\u3000\u675c\u671b\u6708\u4e03\u4eba\u7acb\u5373\u76f4\u51b2\u8fc7\u6765\uff0c\u594b\u52c7\u6740\u5f00\u4e86\u6740\u624b\u4eec\u7684\u5305\u56f4\u3002 \u3000\u3000\u6218\u6597\u66f4\u6fc0\u70c8\u3001\u66f4\u6b8b\u9177\uff0c\u4e5f\u66f4\u8840\u8165\u4e86\u3002 \u3000\u3000\u4e0d\u8001\u4e66\u751f\u7684\u8138\u8272\u5df2\u53d8\u5f97\u5341\u5206\u96be\u770b\u3002 \u3000\u3000\u4ed6\u77e5\u9053\uff0c\u4eca\u591c\u8981\u60f3\u53d6\u80dc\u5927\u6982\u4e0d\u592a\u53ef\u80fd\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u5728\u575a\u6301\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u770b\u51fa\u4e86\u7a0b\u5c0f\u8776\u4f3c\u4e4e\u4e5f\u4e0d\u8f7b\u677e\u3002 \u3000\u3000\u5982\u679c\u80fd\u8bbe\u6cd5\u51fb\u4f24\u7a0b\u5c0f\u8776\uff0c\u5176\u4f59\u7684\u4eba\u81ea\u662f\u4e0d\u5728\u8bdd\u4e0b\uff01\u547c\u5ef6\u8fdc\u60f3\u5230\u8fd9\u91cc\uff0c\u632f\u4f5c\u8d77\u7cbe\u795e\uff0c\u5c06\u5168 \u90e8\u5341\u4e8c\u6210\u5185\u529b\u5168\u90fd\u8fd0\u5230\u4e86\u624b\u4e2d\u7684\u5b9d\u5251\u4e0a\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u8eab\u6cd5\u4e00\u53d8\uff0c\u518d\u53d8\uff0c\u7a81\u7136\u95f4\u4e00\u626d\u8eab\uff0c\u4ece\u4e00\u4e2a\u4e0d\u53ef\u601d\u8bae\u7684\u89d2\u5ea6\u8d34\u8fd1\u4e86\u4e0d\u8001\u4e66\u751f\uff0c\u5de6\u624b \u6325\u821e\u7ec6\u7d22\uff0c\u6321\u5f00\u4ed6\u7684\u5b9d\u5251\uff0c\u53f3\u624b\u5728\u8170\u95f4\u4e00\u6325\uff0c\u62bd\u51fa\u4e00\u67c4\u77ed\u5251\u76f4\u523a\u4e0d\u8001\u4e66\u751f\u524d\u80f8\u3002\u4e0d\u8001\u4e66\u751f\u5927 \u60ca\u4e4b\u4e0b\uff0c\u95ea\u8eab\u540e\u9000\u3002 \u3000\u3000\u4ed6\u9000\u7684\u5feb\uff0c\u7a0b\u5c0f\u8776\u8fdb\u7684\u66f4\u5feb\u3002 \u3000\u3000\u77ed\u5251\u5df2\u7531\u76f4\u523a\u53d8\u6a2a\u524a\u3002 \u3000\u3000\u76f4\u524a\u4ed6\u7684\u54bd\u5589\uff01 \u3000\u3000\u4e0d\u8001\u4e66\u751f\u594b\u529b\u6325\u5251\uff0c\u8fce\u5411\u7a0b\u5c0f\u8776\u7684\u77ed\u5251\u3002 \u3000\u3000\u201c\u5f53\u5577\u201d\u4e00\u58f0\uff0c\u5b9d\u5251\u88ab\u952f\u6210\u4e86\u4e24\u622a\u3002 \u3000\u3000\u4e0d\u8001\u4e66\u751f\u60ca\u9669\u5df2\u6781\u5730\u77aa\u7740\u7a0b\u5c0f\u8776\uff0c\u4f3c\u662f\u4e0d\u76f8\u4fe1\u5979\u624b\u4e2d\u7684\u77ed\u5251\u80fd\u524a\u65ad\u4ed6\u7684\u5b9d\u5251\uff01\u8fd9\u628a\u5b9d\u5251 \u5df2\u8ddf\u968f\u4ed6\u5927\u534a\u751f\uff0c\u6740\u4eba\u65e0\u6570\uff0c\u867d\u4e0d\u88ab\u79f0\u795e\u5175\uff0c\u4e5f\u7edd\u5bf9\u53ef\u5f53\u5229\u5668\u4e4b\u79f0\uff0c\u662f\u4e0d\u8001\u4e66\u751f\u89c6\u4e3a\u4e0e\u6027\u547d \u4e00\u822c\u73cd\u8d35\u4e4b\u7269\uff01 \u3000\u3000\u6b63\u60ca\u9a87\u95f4\uff0c\u4ed6\u5ffd\u7136\u53c8\u611f\u5230\u988c\u4e0b\u6709\u70b9\u53d1\u51c9\uff0c\u5b9a\u775b\u4e00\u770b\uff0c\u53ea\u89c1\u6570\u5341\u6839\u65ad\u4e1d\u5df2\u98d8\u843d\u5730\u4e0a\uff01 \u3000\u3000\u539f\u6765\u7a0b\u5c0f\u8776\u521a\u624d\u4e00\u5251\u6a2a\u524a\uff0c\u4e0d\u4ec5\u780d\u65ad\u4e86\u4ed6\u7684\u5b9d\u5251\uff0c\u4e5f\u524a\u4e0b\u4e86\u4ed6\u7684\u4e00\u7f15\u80e1\u987b\uff01\u4e0d\u8001\u4e66\u751f\u547c \u5ef6\u8fdc\u4e0d\u7981\u6c14\u4e3a\u4e4b\u593a\uff0c\u5fc3\u4e3a\u4e4b\u5bd2\u3002 \u3000\u3000\u4ed6\u518d\u4e5f\u65e0\u5fc3\u604b\u6218\uff0c\u5927\u543c\u4e00\u58f0\uff1a\u201c\u5feb\u64a4\uff01\u201d\u8f6c\u8eab\u5982\u98de\u63a0\u8d70\u3002 \u3000\u3000\u867d\u7136\u6240\u6709\u7684\u6740\u624b\u90fd\u5df2\u542c\u89c1\u4e86\u4ed6\u201c\u64a4\u201d\u7684\u547d\u4ee4\uff0c\u53ef\u8fd8\u662f\u6709\u5341\u6765\u4e2a\u4eba\u65e0\u6cd5\u201c\u64a4\u201d\u8d70\u4e86\uff0c\u4ed6\u4eec \u90fd\u5c06\u6027\u547d\u6c38\u8fdc\u5730\u7559\u5728\u4e86\u56db\u51e4\u697c\u4e2d\u3002 \u3000\u3000\u60dc\u7389\u987f\u8db3\u9053\uff1a\u201c\u54ce\u5440\uff01\u600e\u4e48\u8ba9\u4ed6\u8dd1\u4e86\uff01\u201d\u4fbf\u60f3\u5f80\u524d\u8ffd\u8d76\u3002\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8ffd\u4e5f\u8ffd\u4e0d\u4e0a\u4e86\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u53ef\u5c0f\u6587\u59d0\u7684\u7684\u89e3\u836f\u8fd8\u6ca1\u6709\u8981\u5230\u624b\u5462\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53bb\u697c\u4e0a\u627e\u4e00\u627e\u5427\uff0c\u82e5\u771f\u7684\u627e\u4e0d\u5230\uff0c\u4e5f\u53ea\u6709\u60f3\u522b\u7684\u529e\u6cd5\u4e86\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u4eec\u6765\u5230\u697c\u4e0a\u521a\u624d\u4eae\u7740\u706f\u7684\u90a3\u95f4\u5c4b\u5b50\uff0c\u5374\u89c1\u5c4b\u5185\u9664\u4e86\u4e00\u5f20\u56f4\u684c\uff0c\u51e0\u628a\u6905\u5b50\u5916\uff0c\u4ec0 \u4e48\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u60dc\u7389\u770b\u7740\u7a7a\u7a7a\u8361\u8361\u7684\u623f\u95f4\uff0c\u8138\u8272\u53d8\u5f97\u60e8\u767d\uff0c\u5446\u5446\u5730\u9053\uff1a\u201c\u727a\u7272\u4e86\u6c5f\u5357\u5206\u53f8\u4e2d\u4e09\u540d\u597d\u624b\uff0c \u5374\u4ec0\u4e48\u4e5f\u6ca1\u627e\u5230\uff0c\u771f\u662f\u4e0d\u503c\u5f97\u554a\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u9519\u4e86\uff0c\u6211\u4eec\u662f\u4e09\u6b7b\u56db\u4f24\uff0c\u4f46\u662f\u6211\u4eec\u6740\u6b7b\u4e86\u56db\u5341\u591a\u540d\u9ad8\u7ea7\u6740\u624b\uff0c\u603b \u6355\u5934\u53c8\u51fb\u8d25\u4e86\u547c\u5ef6\u8fdc\uff0c\u8fd9\u53ef\u662f\u5bf9\u6740\u624b\u96c6\u56e2\u7684\u4e00\u6b21\u6c89\u91cd\u7684\u6253\u51fb\u5440\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u675c\u53f8\u4e3b\u7684\u8bdd\u6709\u9053\u7406\uff0c\u60dc\u7389\u4f60\u4e0d\u8981\u96be\u8fc7\uff0c\u6211\u4eec\u7684\u727a\u7272\u662f\u503c\u5f97\u7684\uff01\u6b64\u5730\u4e0d\u5b9c\u4e45 \u7559\uff0c\u4e0d\u8001\u4e66\u751f\u65e2\u7136\u9003\u8d70\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u7518\u5fc3\uff0c\u6216\u8005\u4f1a\u53ec\u96c6\u4eba\u9a6c\uff0c\u5feb\u901f\u53cd\u6251\uff0c\u6216\u8005\u662f\u901a\u77e5\u8840\u624b\u65b9\u8f6e \u548c\u68a6\u5e7b\u4e4b\u5200\uff0c\u4e58\u5e9c\u8859\u7a7a\u865a\uff0c\u53bb\u523a\u6740\u738b\u5927\u4eba\uff0c\u6211\u4eec\u5feb\u8d76\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u60dc\u7389\u561f\u7740\u5c0f\u5634\uff0c\u6068\u6068\u5730\u9053\uff1a\u201c\u6068\u4e0d\u5f97\u70b9\u4e00\u628a\u706b\uff0c\u70e7\u4e86\u8fd9\u4e2a\u56db\u51e4\u697c\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u53ef\uff0c\u5982\u4eca\u6df1\u591c\uff0c\u56db\u51e4\u697c\u4e00\u8d77\u706b\uff0c\u5fc5\u7136\u5f15\u8d77\u57ce\u4e2d\u6050\u614c\uff0c\u4f1a\u8fde\u7d2f\u57ce\u4e2d\u767e\u59d3 \u7684\uff01\u201d"}),showOnHomepage:!0},{name:"\u7b2c\u4e8c\u5341\u7ae0",content:(0,O.jsx)(O.Fragment,{children:"\u8bf4\u8fd9\u53e5\u8bdd\u65f6\uff0c\u5979\u7684\u795e\u60c5\u5df2\u7ecf\u5f88\u4e25\u8083\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u6697\u6697\u4e00\u626f\u60dc\u7389\u7684\u8896\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u60dc\u7389\u4e5f\u53ea\u662f\u8bf4\u8bf4\u800c\u5df2\u7684\uff0c\u4e0d\u4f1a\u771f\u7684\u8fd9\u6837 \u505a\uff0c\u5bf9\u5427\uff1f\u60dc\u7389\u3002\u201d \u3000\u3000\u60dc\u7389\u770b\u4e86\u7a0b\u5c0f\u8776\u4e00\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u60f3\u5230\u675c\u671b\u6708\u8fd9\u662f\u5f53\u7740\u7a0b\u5c0f\u8776\u7684\u9762\u516c\u7136\u56de\u62a4\u5979\uff0c\u5fc3\u91cc\u4e0d\u7981\u53c8\u662f\u4e00\u9635\u751c\u871c\uff01 \u3000\u3000\u5728\u7a0b\u5c0f\u8776\u6307\u6325\u4e0b\uff0c\u4ed6\u4eec\u4e00\u884c\u5341\u4eba\u5f88\u5feb\u6d88\u5931\u5728\u832b\u832b\u591c\u8272\u4e4b\u4e2d\u3002 \u3000\u3000\u56db\u51e4\u697c\u53c8\u6062\u590d\u4e86\u4e00\u7247\u6b7b\u5bc2\uff01 \u3000\u3000\u591c\uff0c\u66f4\u6df1\u4e86\uff01 \u3000\u3000\u4e0e\u5e9c\u8859\u8fde\u756a\u906d\u53d7\u88ad\u51fb\u6bd4\u8d77\u6765\uff0c\u91d1\u5e9c\u8fd9\u51e0\u65e5\u7684\u5e73\u5b89\u65e0\u4e8b\u603b\u663e\u5f97\u6709\u70b9\u4e0d\u6b63\u5e38\u3002\u94c1\u7fce\u4e00\u76f4\u5728\u60f3 \u8fd9\u4e2a\u95ee\u9898\uff0c\u4f46\u4e00\u76f4\u6ca1\u60f3\u51fa\u4e2a\u5934\u7eea\u6765\u3002 \u3000\u3000\u8fd9\u5929\u4e0a\u5348\uff0c\u94c1\u7fce\u6b63\u4e0e\u8d3e\u82f1\u5728\u623f\u4e2d\u8bae\u4e8b\uff0c\u5929\u67ab\u9053\u957f\u5306\u5306\u8d70\u8fdb\u6765\uff0c\u5bf9\u8d3e\u82f1\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\uff0c\u4e1c \u4e3b\u6709\u8981\u4e8b\uff0c\u8bf7\u4f60\u53bb\u3002\u201d \u3000\u3000\u8d3e\u82f1\u7ad9\u8d77\u8eab\u5b50\uff0c\u95ee\u9053\uff1a\u201c\u4e1c\u4e3b\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4e1c\u4e3b\u5728\u89c2\u9e64\u697c\u7b49\u4f60\uff0c\u8bf7\u8d3e\u603b\u7ba1\u5feb\u53bb\u3002\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u6b63\u4e0e\u94c1\u6355\u5934\u5546\u8bae\u5e9c\u4e2d\u9632\u536b\u7684\u5e03\u7f6e\u548c\u4eca\u540e\u7684\u884c\u52a8\u8ba1\u5212\uff0c\u5929\u67ab\u9053\u957f\u6765\u7684 \u6b63\u597d\uff0c\u5c31\u8bf7\u9053\u957f\u4e0e\u94c1\u6355\u5934\u5408\u8ba1\u4e00\u4e0b\u5427\u3002\u201d \u3000\u3000\u4ed6\u5bf9\u94c1\u7fce\u4e00\u62f1\u624b\uff0c\u9053\uff1a\u201c\u5c11\u966a\u3002\u201d \u3000\u3000\u94c1\u7fce\u8fd8\u793c\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\u8bf7\u3002\u201d \u3000\u3000\u8d3e\u82f1\u8f6c\u8eab\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u51fa\u795e\u5730\u770b\u7740\u8d3e\u82f1\u7684\u80cc\u5f71\uff0c\u597d\u4e00\u4f1a\u513f\u624d\u5bf9\u94c1\u7fce\u9053\uff1a\u201c\u94c1\u5144\uff0c\u4f60\u4e0d\u89c9\u5f97\u5947\u602a\u5417\uff1f\u201d \u3000\u3000\u94c1\u7fce\u5fc3\u91cc\u4e00\u8df3\uff0c\u6697\u5fd6\u9053\uff1a\u83ab\u975e\u5929\u67ab\u9053\u957f\u4e5f\u6709\u4e9b\u6000\u7591\u8d3e\u82f1\uff1f\u53e3\u4e2d\u5374\u5e94\u9053\uff1a\u201c\u4ec0\u4e48\u5947\u602a\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u6682\u65f6\u8fd8\u4e0d\u60f3\u5c06\u81ea\u5df1\u7684\u60f3\u6cd5\u900f\u9732\u7ed9\u5929\u67ab\u9053\u957f\uff0c\u867d\u8bf4\u5929\u67ab\u672c\u662f\u4ed6\u7684\u670b\u53cb\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u51e0\u65e5\u6765\uff0c\u5e9c\u4e2d\u7adf\u7136\u4ec0\u4e48\u610f\u5916\u60c5\u51b5\u4e5f\u6ca1\u53d1\u751f\uff0c\u8fd9\u8fd8\u4e0d\u5947\u602a\u5417\uff1f\u96be\u9053\u68a6\u5e7b\u4e4b \u5200\u4e0d\u60f3\u8981\u4e09\u5b9d\u4e86\uff1f\u201d \u3000\u3000\u539f\u6765\u4ed6\u662f\u8bf4\u8fd9\u4ef6\u4e8b\uff01\u94c1\u7fce\u5fc3\u9053\uff0c\u53e3\u4e2d\u7b54\u9053\uff1a\u201c\u5927\u6982\u662f\u56e0\u4e3a\u68a6\u5e7b\u4e4b\u5200\u4e5f\u77e5\u9053\u91d1\u5e9c\u5b9e\u529b\u4e4b\u5f3a\uff0c \u96be\u4ee5\u4e0b\u624b\u5427\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u8fd9\u6837\u60f3\u3002\u94c1\u5144\uff0c\u6709\u4e00\u4ef6\u4e8b\u4e0d\u77e5\u4f60\u60f3\u8fc7\u6ca1\u6709\uff0c\u6740\u624b\u96c6\u56e2\u8fde\u756a\u88ad\u51fb\u5e9c \u8859\uff0c\u662f\u4e0d\u662f\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\uff1f\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u9053\u5144\u7684\u610f\u601d\u662f\u8bf4\uff0c\u68a6\u5e7b\u4e4b\u5200\u56e0\u91d1\u5e9c\u5b9e\u529b\u592a\u5f3a\uff0c\u9632\u5b88\u592a\u4e25\u5bc6\u800c\u96be\u4ee5\u4e0b\u624b\u593a\u53d6\u4e09 \u5b9d\uff0c\u53c8\u63a2\u77e5\u91d1\u5e9c\u548c\u738b\u5927\u4eba\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u6240\u4ee5\u5c31\u8fdb\u653b\u5e9c\u8859\uff0c\u505a\u51fa\u8981\u6740\u738b\u5927\u4eba\u7684\u5047\u50cf\uff0c\u8bf1\u4f7f\u91d1\u5e9c \u6d3e\u51fa\u5927\u6279\u9ad8\u624b\u9632\u5b88\u5e9c\u8859\uff0c\u4ed6\u5c31\u53ef\u4ee5\u6765\u8fd9\u91cc\u593a\u53d6\u4e09\u5b9d\u4e86\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u76b1\u7709\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u6b63\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f9d\u9053\u5144\u4e4b\u89c1\uff0c\u6211\u4eec\u8be5\u600e\u6837\u505a\u5462\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u6211\u5012\u6709\u4e00\u4e2a\u8ba1\u7b56\uff0c\u53ea\u4e0d\u77e5\u94c1\u5144\u4ee5\u4e3a\u7136\u5426\u3002\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u9053\u5144\u7684\u8ba1\u7b56\uff0c\u603b\u662f\u597d\u7684\uff0c\u5feb\u5feb\u8bb2\u6765\u542c\u542c\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u6211\u60f3\uff0c\u5e72\u8106\u81ea\u91d1\u5e9c\u62bd\u8c03\u4e00\u6279\u9ad8\u624b\uff0c\u6545\u610f\u505a\u51fa\u5f88\u673a\u5bc6\u7684\u6837\u5b50\uff0c\u6084\u6084\u6f5c\u5165\u5e9c \u8859\u3002 \u3000\u3000\u6211\u60f3\uff0c\u68a6\u5e7b\u4e4b\u5200\u8fd1\u671f\u5185\u4e00\u5b9a\u4e00\u518d\u5bf9\u91d1\u5e9c\u4e25\u5bc6\u76d1\u89c6\uff0c\u770b\u89c1\u91d1\u5e9c\u6d3e\u8d70\u4e86\u5927\u6279\u9ad8\u624b\uff0c\u4e00\u5b9a\u4f1a\u73b0 \u8eab\u593a\u5b9d\uff0c\u8fd9\u6837\uff0c\u54b1\u4eec\u5c31\u53ef\u4ee5\u8bbe\u4f0f\u64d2\u4f4f\u4ed6\u4e86\uff01\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u679c\u7136\u597d\u8ba1\uff0c\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u59a5\u4e4b\u5904\u5417\uff1f\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u65e2\u7136\u68a6\u5e7b\u4e4b\u5200\u4e00\u5b9a\u4f1a\u4e25\u5bc6\u76d1\u89c6\u91d1\u5e9c\uff0c\u5e9c\u4e2d\u6d3e\u51fa\u5927\u6279\u9ad8\u624b\u540e\uff0c\u68a6\u5e7b\u4e4b\u5200\u5c31\u7b97\u73b0 \u8eab\uff0c\u53ea\u6015\u6211\u4eec\u4e5f\u65e0\u5b9e\u529b\u64d2\u4f4f\u4ed6\u54e9\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u6240\u4ee5\u6211\u60f3\u8ba9\u94c1\u5144\u5728\u603b\u6355\u5934\u9762\u524d\u8fdb\u4e00\u8a00\uff0c\u8bf7\u5979\u7387\u540c\u90e8\u4e0b\u51e0\u4f4d\u7edd\u9876\u9ad8\u624b\uff0c\u5148 \u671f\u57cb\u4f0f\u5728\u91d1\u5e9c\u9644\u8fd1\uff0c\u53ea\u5f85\u68a6\u5e7b\u4e4b\u5200\u4e00\u73b0\u8eab\uff0c\u6211\u4f1a\u7acb\u523b\u6253\u51fa\u4fe1\u53f7\uff0c\u7a0b\u603b\u6355\u5934\u4fbf\u53ef\u5e26\u4eba\u76f4\u51b2\u8fdb\u91d1 \u5e9c\uff0c\u64d2\u6740\u68a6\u5e7b\u4e4b\u5200\uff01\u201d \u3000\u3000\u8ba1\u7684\u786e\u7b97\u662f\u597d\u8ba1\uff0c\u53ea\u662f\u603b\u6355\u5934\u5df2\u7ecf\u8bf4\u8fc7\uff0c\u6740\u624b\u4eec\u7684\u76ee\u6807\u6b63\u662f\u738b\u5927\u4eba\uff0c\u800c\u91d1\u5e9c\u8d3e\u82f1\u7684\u8eab\u4efd \u53c8\u5f88\u8be1\u79d8\uff0c\u96be\u4fdd\u5e9c\u4e2d\u9ad8\u624b\u91cc\u4e5f\u8fd8\u6709\u5176\u4ed6\u4eba\u662f\u6740\u624b\u96c6\u56e2\u7684\u5978\u7ec6\uff0c\u6d3e\u4f60\u4eec\u53bb\u5e9c\u8859\uff0c\u6211\u4eec\u81ea\u662f\u4e0d\u80fd \u653e\u5fc3\uff01 \u3000\u3000\u94c1\u7fce\u5fc3\u91cc\u98de\u5feb\u5730\u60f3\u7740\uff0c\u53e3\u4e2d\u5374\u7b11\u9053\uff1a\u201c\u5e9c\u8859\u4e2d\u4e00\u5207\u6709\u5173\u9632\u536b\u7684\u4e8b\u60c5\u90fd\u5fc5\u987b\u7531\u7a0b\u603b\u6355\u5934\u5b9a \u593a\uff0c\u94c1\u67d0\u53ef\u4ee5\u5c06\u9053\u5144\u4e4b\u8ba1\u8f6c\u544a\u5979\uff0c\u81f3\u4e8e\u603b\u6355\u5934\u662f\u5426\u8ba4\u53ef\uff0c\u94c1\u67d0\u53ef\u5c31\u4e0d\u6562\u62c5\u4fdd\u4e86\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u90a3\u662f\u5f53\u7136\u4e86\uff0c\u8fd9\u4e2a\u6211\u660e\u767d\uff0c\u8fd8\u8bf7\u94c1\u5144\u5728\u603b\u6355\u5934\u9762\u524d\u4e00\u529b\u5448\u53d9\u5229\u5bb3\uff0c\u4fc3\u6210 \u6b64\u8ba1\u5b9e\u65bd\u4e3a\u597d\u3002\u201d \u3000\u3000\u94c1\u7fce\u60f3\u4e86\u60f3\uff0c\u5f88\u8ba4\u771f\u5730\u770b\u4e86\u770b\u5929\u67ab\u9053\u957f\uff0c\u9053\uff1a\u201c\u597d\uff0c\u94c1\u67d0\u8fd9\u5c31\u56de\u5e9c\u53bb\uff01\u201d\u5929\u67ab\u9053\u957f\u9053\uff1a \u201c\u56de\u5e9c\u7684\u8def\u4e0a\u5f88\u53ef\u80fd\u4f1a\u6709\u6740\u624b\u57cb\u4f0f\uff0c\u5bf9\u94c1\u5144\u4e0d\u5229\uff0c\u8fd9\u6837\u5427\uff0c\u6211\u8ba9\u9a6c\u6c0f\u5144\u5f1f\u3001\u7f57\u6d6e\u4e09\u5251\uff0c\u966a\u94c1 \u5144\u4e00\u540c\u524d\u5f80\uff01\u201d \u3000\u3000\u94c1\u7fce\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9053\u5144\u9ad8\u60c5\u539a\u610f\uff0c\u771f\u8ba9\u94c1\u67d0\u611f\u52a8\u554a\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u4e5f\u7b11\u9053\uff1a\u201c\u94c1\u5144\u8bf4\u8fd9\u8bdd\uff0c\u53ef\u5c31\u89c1\u5916\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u5929\u67ab\u9053\u957f\u6d3e\u4eba\u53eb\u6765\u9a6c\u6c0f\u5144\u5f1f\u3001\u7f57\u6d6e\u4e09\u5251\uff0c\u5429\u5490\u4ed6\u4eec\u4e00\u5b9a\u8981\u6ce8\u610f\u4fdd\u62a4\u597d\u94c1\u7fce\uff0c\u4e00\u76f4 \u9001\u5230\u5e9c\u95e8\u5916\uff0c\u65b9\u624d\u9a7b\u8db3\u3002 \u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1 \u3000\u3000\u8d3e\u82f1\u5306\u5306\u6765\u5230\u89c2\u9e64\u697c\uff0c\u53ea\u89c1\u91d1\u767e\u5e74\u6b63\u5728\u7b49\u4ed6\u3002\u867d\u8bf4\u5929\u67ab\u9053\u957f\u8bf4\u91d1\u767e\u5e74\u627e\u4ed6\u662f\u6709\u5f88\u91cd\u8981\u7684 \u4e8b\uff0c\u4f46\u4ed6\u5728\u91d1\u767e\u5e74\u7684\u8138\u4e0a\u5374\u6ca1\u770b\u51fa\u4ec0\u4e48\u5f02\u5e38\u7684\u8868\u60c5\u3002 \u3000\u3000\u91d1\u767e\u5e74\u4e5f\u4e0d\u5ba2\u6c14\uff0c\u8d3e\u82f1\u4e00\u8fdb\u95e8\uff0c\u4ed6\u5c31\u5f00\u53e3\u95ee\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\uff0c\u5e9c\u5185\u7684\u9632\u5907\u4f60\u770b\u4f1a\u4e0d\u4f1a\u6709\u4ec0 \u4e48\u6f0f\u6d1e\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u636e\u8d3e\u67d0\u770b\uff0c\u53ef\u79f0\u4e25\u5bc6\u4e4b\u6781\uff0c\u6c34\u6cc4\u4e0d\u901a\u3002\u201d \u3000\u3000\u91d1\u767d\u5e74\u9053\uff1a\u201c\u90a3\u5c31\u597d\uff01\u90a3\u5c31\u597d\uff01\u8001\u592b\u4e00\u76f4\u76fc\u7740\u65e9\u65e5\u6293\u4f4f\u771f\u51f6\uff0c\u6d17\u5237\u5c0f\u5973\u8eab\u4e0a\u7684\u51a4\u60c5\uff01\u4f60 \u6628\u5929\u53bb\u5e9c\u8859\u8bf7\u94c1\u6355\u5934\u65f6\uff0c\u89c1\u5230\u738b\u5927\u4eba\u4e86\u5417\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u89c1\u5230\u4e86\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u738b\u5927\u4eba\u8bf4\u6ca1\u8bf4\u6848\u60c5\u6709\u4f55\u65b0\u7684\u8fdb\u5c55\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u5e9c\u8859\u906d\u88ad\uff0c\u738b\u5927\u4eba\u7684\u5b89\u5168\u4ea6\u53d7\u5230\u4e25\u91cd\u7684\u5a01\u80c1\uff0c\u53ea\u6015\u5f88\u96be\u96c6\u4e2d\u7cbe\u529b\u529e\u6848\u4e86\uff0c\u53ea \u4e0d\u8fc7\u636e\u8bf4\u9a6c\u63d0\u7763\u5df2\u677e\u53e3\u4e86\uff0c\u8bf4\u7834\u6848\u671f\u9650\u53ef\u4ee5\u5ef6\u5c55\uff0c\u5927\u6982\u8fd1\u671f\u5185\u4e0d\u4f1a\u518d\u6765\u627e\u4e1c\u4e3b\u7684\u9ebb\u70e6\uff0c\u903c\u4e1c \u4e3b\u4ea4\u51fa\u5c0f\u7709\u5c0f\u59d0\u4e86\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u8001\u592b\u7834\u6848\u4e4b\u5fc3\u60c5\uff0c\u6bd4\u9a6c\u63d0\u7763\u66f4\u6025\u8feb\u554a\uff01\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u662f\uff0c\u8d3e\u82f1\u77e5\u9053\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u7ad9\u8d77\u8eab\u5b50\uff0c\u5728\u5ba4\u5185\u7f13\u6162\u5730\u8e31\u7740\u65b9\u6b65\uff0c\u50cf\u662f\u6709\u4ec0\u4e48\u8bdd\u8981\u5bf9\u8d3e\u82f1\u8bf4\uff0c\u53ef\u4e00\u65f6\u53c8\u6709\u4e9b\u62ff \u4e0d\u5b9a\u4e3b\u610f\u3002\u8d3e\u82f1\u5782\u624b\u7ad9\u7acb\u4e00\u65c1\uff0c\u4e0d\u8bf4\u8bdd\uff0c\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u91d1\u767e\u5e74\u8e31\u4e86\u51e0\u5708\uff0c\u505c\u5728\u4e86\u8d3e\u82f1\u7684\u9762\u524d\uff0c\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\uff0c\u4f60\u6765\u672c\u5e9c\u4e5f\u6709\u4e03\u516b\u5e74\u4e86\u5427\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u662f\u7684\u3002\u8d3e\u82f1\u4e3a\u4e1c\u4e3b\u6548\u529b\uff0c\u5df2\u6709\u4e03\u5e74\u4e5d\u4e2a\u6708\u96f6\u4e8c\u5341\u4e09\u5929\u4e86\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u8001\u592b\u5f85\u4f60\u5982\u4f55\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u6069\u91cd\u5982\u5c71\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u53c8\u70b9\u70b9\u5934\uff0c\u76f4\u89c6\u7740\u8d3e\u82f1\u7684\u53cc\u773c\uff0c\u9053\uff1a\u201c\u51e0\u5e74\u6765\uff0c\u5e9c\u4e2d\u5927\u5c0f\u4e8b\u52a1\uff0c\u7686\u7531\u8d3e\u603b\u7ba1\u4e00\u624b \u5b89\u6392\uff0c\u4f7f\u8001\u592b\u7701\u4e86\u4e0d\u5c11\u5fc3\u795e\uff0c\u8001\u592b\u5bf9\u4f60\u4e5f\u5341\u5206\u611f\u6fc0\u5440\uff01\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e1c\u4e3b\u8a00\u91cd\u4e86\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u770b\u7740\u4ed6\uff0c\u795e\u60c5\u5ffd\u7136\u53d8\u5f97\u5f88\u4e25\u8083\uff0c\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\uff0c\u4f60\u4e5f\u77e5\u9053\uff0c\u626c\u5dde\u5341\u5927\u5bcc\u8c6a\uff0c\u591a\u591a \u5c11\u5c11\u624b\u4e2d\u90fd\u6709\u4e00\u70b9\u4e0d\u592a\u660e\u767d\u7684\u751f\u610f\uff0c\u91d1\u67d0\u5f53\u7136\u4e5f\u4e0d\u4f8b\u5916\u3002\u8fd9\u4e9b\u751f\u610f\u53ef\u90fd\u662f\u4e0d\u5408\u5b98\u5e9c\u4e4b\u6cd5\u7684\uff0c \u6240\u4ee5\u4e00\u65e6\u88ab\u63ed\u9732\uff0c\u5f88\u53ef\u80fd\u4e0e\u5b98\u5e9c\u4e4b\u95f4\u4f1a\u6709\u4e00\u70b9\u5c0f\u9ebb\u70e6\uff0c\u5230\u90a3\u65f6\u5019\uff0c\u8d3e\u603b\u7ba1\u4f1a\u7ad9\u5728\u54ea\u4e00\u8fb9\u5462\uff1f \u4f60\u8bf4\uff01\u201d \u3000\u3000\u8d3e\u82f1\u6beb\u4e0d\u8fdf\u7591\uff0c\u65ad\u7136\u9053\uff1a\u201c\u8d3e\u82f1\u672c\u662f\u4e00\u6c5f\u6e56\u6d6a\u5b50\uff0c\u8499\u4e1c\u4e3b\u9752\u773c\u6709\u52a0\uff0c\u5bf9\u8d3e\u82f1\u6069\u91cd\u5982\u5c71\uff0c \u8d3e\u82f1\u5fc3\u4e2d\u53ea\u6709\u4e1c\u4e3b\uff0c\u522b\u65e0\u5176\u5b83\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u6de1\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff01\u8001\u592b\u679c\u7136\u6ca1\u6709\u770b\u9519\u4eba\uff01\u8d3e\u603b\u7ba1\uff0c\u90a3\u5929\u738b\u5927\u4eba\u906d\u523a\u5ba2\u57cb\u4f0f\uff0c \u4f60\u8d76\u53bb\u89e3\u6551\u4e4b\u65f6\uff0c\u662f\u4e0d\u662f\u770b\u6e05\u523a\u5ba2\u7684\u76f8\u8c8c\u4e86\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u662f\uff0c\u770b\u6e05\u4e86\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4f60\u8ba4\u8bc6\u4ed6\u5417\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u7b11\u4e00\u7b11\uff0c\u5374\u4e0d\u56de\u7b54\u3002 \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u56de\u5e9c\u4e4b\u540e\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u7acb\u523b\u5bf9\u6211\u8bf4\u5462\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u4e1c\u4e3b\uff0c\u5982\u4f55\u77e5\u9053\u6211\u8ba4\u51fa\u4ed6\u4e86\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u770b\u7740\u4ed6\u597d\u4e00\u4f1a\u513f\uff0c\u5ffd\u7136\uff0c\u8f6c\u8eab\u5f80\u4e00\u9ad8\u95e8\u91cc\u8d70\u53bb\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u4f60\u8ddf\u8001\u592b\u8d70\u3002\u201d \u3000\u3000\u8d3e\u82f1\u5fc3\u91cc\u4e0d\u7981\u4e00\u9635\u6fc0\u52a8\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\uff0c\u4ece\u8fd9\u6247\u95e8\u8d70\u8fdb\u53bb\uff0c\u6b63\u901a\u5f80\u653e\u9e64\u53f0\u3002 \u3000\u3000\u653e\u9e64\u53f0\u662f\u91d1\u5e9c\u4e2d\u6700\u6700\u91cd\u8981\u7684\u673a\u5bc6\u4e4b\u5730\uff0c\u91cc\u9762\u673a\u5173\u91cd\u91cd\uff0c\u800c\u6240\u6709\u673a\u5173\u53ea\u6709\u91d1\u767e\u5e74\u4e00\u4eba\u624d\u80fd \u63a7\u5236\u3002 \u3000\u3000\u8d3e\u82f1\u867d\u8bf4\u8eab\u4e3a\u91d1\u5e9c\u603b\u7ba1\uff0c\u6765\u91d1\u5e9c\u4e5f\u5df2\u5c06\u8fd1\u516b\u5e74\uff0c\u4f46\u8fd9\u8fd8\u662f\u7b2c\u4e00\u6b21\u88ab\u91d1\u767e\u5e74\u5141\u8bb8\u8fdb\u5165\u653e\u9e64 \u53f0\u3002\u4ed6\u80fd\u4e0d\u6fc0\u52a8\u5417\uff1f \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u4e00\u70b9\u4e5f\u6ca1\u6709\u8ba9\u81ea\u5df1\u6fc0\u52a8\u7684\u5fc3\u60c5\u6d41\u9732\u51fa\u534a\u5206\u3002 \u3000\u3000\u770b\u4ed6\u7684\u6837\u5b50\uff0c\u5c31\u50cf\u662f\u6b63\u8d70\u5411\u81ea\u5df1\u7684\u623f\u95f4\u4e00\u6837\u5730\u5e73\u9759\u3002 \u3000\u3000\u6574\u4e2a\u91d1\u5e9c\u4e2d\uff0c\u8d3e\u82f1\u662f\u9664\u91d1\u767e\u5e74\u4e4b\u5916\uff0c\u7b2c\u4e8c\u4e2a\u80fd\u8fdb\u5165\u653e\u9e64\u53f0\u7684\u4eba\u3002\u4ed6\u77e5\u9053\uff0c\u8fd9\u8868\u793a\u91d1\u767e\u5e74 \u5df2\u5b8c\u5168\u5c06\u4ed6\u89c6\u4e3a\u6700\u91cd\u8981\u7684\u5fc3\u8179\u4e4b\u4eba\uff0c\u91d1\u5e9c\u91cc\u6240\u6709\u7684\u79d8\u5bc6\uff0c\u7acb\u5373\u90fd\u5c06\u5728\u4ed6\u773c\u524d\u6253\u5f00\uff01 \u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1 \u3000\u3000\u7a0b\u5c0f\u8776\u542c\u4e86\u94c1\u7fce\u62a5\u544a\u7684\u5929\u67ab\u9053\u957f\u7684\u884c\u52a8\u8ba1\u5212\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5929\u67ab\u9053\u957f\u679c\u7136\u5f88\u6709\u5fc3\u673a\uff0c \u4f46\u662f\uff0c\u6211\u4e0d\u51c6\u5907\u91c7\u7eb3\u4ed6\u7684\u5efa\u8bae\u3002\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u662f\u56e0\u4e3a\u8d3e\u82f1\u8fd9\u4e2a\u4eba\u8eab\u5206\u53ef\u7591\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\uff0c\u4e5f\u4e0d\u5168\u662f\uff0c\u6211\u60f3\uff0c\u53cd\u6b63\u6211\u4e0e\u56db\u5927\u6355\u5934\u90fd\u5df2\u7531\u6697\u8f6c\u660e\uff0c\u5e72\u8106\u5c31\u4e0e\u6740\u624b\u96c6 \u56e2\u786c\u78b0\u786c\u5e72\u4e0a\u4e00\u573a\uff0c\u56e0\u4e3a\u6211\u4eec\u53ef\u4e0d\u80fd\u957f\u51f6\u624b\u7684\u9510\u6c14\uff0c\u706d\u5b98\u5e9c\u7684\u5a01\u98ce\uff01\u201d \u3000\u3000\u542c\u603b\u6355\u5934\u7684\u8bdd\uff0c\u597d\u50cf\u5f88\u6709\u4e9b\u8a00\u4e0d\u7531\u8877\u554a\uff01\u83ab\u975e\u5979\u8fde\u6211\u4e5f\u4e0d\u662f\u5341\u5206\u4fe1\u4efb\uff1f \u3000\u3000\u94c1\u7fce\u6697\u5fd6\u9053\uff0c\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a\u201c\u82e5\u603b\u6355\u5934\u4e0d\u540c\u610f\uff0c\u94c1\u67d0\u8fd9\u5c31\u53bb\u91d1\u5e9c\uff0c\u544a\u8bc9\u5929\u67ab\u9053\u957f\u4e00\u58f0\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u522b\u5fd8\u4e86\u4ee3\u6211\u611f\u8c22\u4ed6\u7684\u597d\u610f\u3002\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u8d70\u51fa\u4e86\u51e0\u6b65\uff0c\u5feb\u5230\u623f\u95e8\u8fb9\u4e86\uff0c\u94c1\u7fce\u5ffd\u7136\u53c8\u7ad9\u4f4f\uff0c\u8fdf\u7591\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u94c1\u67d0\u4e00\u76f4\u89c9\u5f97\u56db\u51e4 \u697c\u6b66\u9662\u9662\u4e3b\u7684\u7684\u58f0\u97f3\u6709\u70b9\u719f\uff0c\u5e94\u8be5\u662f\u4e00\u4e2a\u94c1\u67d0\u8ba4\u8bc6\u7684\u4eba\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5f88\u8ba4\u771f\u5730\u542c\u7740\uff0c\u9053\uff1a\u201c\u94c1\u6355\u5934\u8ba4\u4e3a\u90a3\u4e2a\u4eba\u4f1a\u662f\u5e9c\u8859\u4e2d\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\u3002\u94c1\u6355\u5934\uff0c\u6211\u77e5\u9053\u4f60\u5fc3\u4e2d\u867d\u6709\u6000\u7591\uff0c\u53ef\u662f\u53c8\u4e0d\u6562\u80af\u5b9a\uff0c\u6240\u4ee5\u60f3\u6697 \u4e2d\u67e5\u8bbf\u8bd5\u63a2\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u94c1\u67d0\u6b63\u662f\u8fd9\u6837\u60f3\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\uff01\u4e0d\u8fc7\u94c1\u6355\u5934\u4e00\u5b9a\u8981\u5c0f\u5fc3\uff0c\u65e2\u4fbf\u6709\u6240\u53d1\u73b0\uff0c\u4e5f\u4e0d\u8981\u8f7b\u4e3e\u5984\u52a8\uff0c\u66f4\u4e0d\u80fd\u8ba9 \u90a3\u4eba\u67e5\u89c9\u4f60\u7684\u5185\u5fc3\u60f3\u6cd5\uff0c\u4ee5\u514d\u6253\u8349\u60ca\u86c7\uff0c\u66f4\u574f\u7684\u60c5\u51b5\u662f\u4ed6\u4e00\u5b9a\u4f1a\u4e0d\u62e9\u624b\u6bb5\u6765\u5bf9\u4ed8\u4f60\u3002\u94c1\u6355\u5934\uff0c \u4f60\u4e00\u5b9a\u4e00\u5b9a\u8981\u8c28\u614e\u884c\u4e8b\uff0c\u4e00\u6709\u53d1\u73b0\uff0c\u7acb\u523b\u6765\u5411\u6211\u901a\u62a5\uff01\u201d\u94c1\u7fce\u9053\uff1a\u201c\u662f\u3002\u603b\u6355\u5934\u653e\u5fc3\u53ef\u4e5f\uff01\u201d \u3000\u3000\u8bf4\u6bd5\uff0c\u4ed6\u8d70\u5230\u9662\u4e2d\uff0c\u53eb\u4e0a\u91d1\u5e9c\u7684\u9a6c\u6c0f\u5144\u5f1f\u3001\u7f57\u6d6e\u4e09\u5251\u4e00\u540c\u56de\u91d1\u5e9c\u53bb\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u770b\u7740\u94c1\u7fce\u7684\u80cc\u5f71\uff0c\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\u3002\u53ef\u5979\u7684\u8138\u4e0a\u53c8\u6d6e\u8d77\u4e00\u4e1d\u62c5\u5fe7\u4e4b\u8272\u3002 \u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1 \u3000\u3000\u7a0b\u5c0f\u8776\u548c\u60dc\u7389\u3001\u675c\u671b\u6708\u53ca\u6c5f\u5357\u5206\u53f8\u5341\u540d\u597d\u624b\u591c\u95ef\u56db\u51e4\u697c\uff0c\u867d\u8bf4\u51fb\u8d25\u4e0d\u8001\u4e66\u751f\u547c\u5ef6\u8fdc\uff0c\u683c \u6740\u56db\u5341\u4f59\u540d\u9ad8\u7ea7\u6740\u624b\uff0c\u4f46\u81ea\u5df1\u8fd9\u4e00\u65b9\u4e5f\u662f\u4e09\u6b7b\u56db\u4f24\uff0c\u800c\u4e14\u6ca1\u80fd\u627e\u5230\u89e3\u6551\u5c0f\u6587\u7684\u89e3\u836f\uff0c\u6240\u4ee5\u5e9c \u8859\u5185\u7684\u6c14\u6c1b\u6709\u4e9b\u6c89\u91cd\u3002 \u3000\u3000\u56e0\u4e3a\u62c5\u5fc3\u6740\u624b\u4eec\u4f1a\u518d\u5ea6\u6765\u88ad\uff0c\u867d\u7136\u738b\u5c11\u537f\u575a\u6301\u8981\u7167\u5e38\u529e\u7406\u5e9c\u8859\u7684\u516c\u52a1\uff0c\u4f46\u7a0b\u5c0f\u8776\u575a\u51b3\u4e0d \u540c\u610f\u3002 \u3000\u3000\u548c\u524d\u51e0\u5929\u4e00\u6837\uff0c\u5e9c\u8859\u5185\u6240\u6709\u7684\u4eba\u90fd\u5b88\u5185\u5802\u540e\u9662\uff0c\u7f29\u5c0f\u4e86\u9632\u5b88\u8303\u56f4\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5927\u5bb6\u6700\u62c5\u5fc3\u7684\u5c31\u662f\u5c0f\u6587\u59d1\u5a18\u3002 \u3000\u3000\u867d\u7136\u60dc\u7389\u60f3\u5c3d\u4e86\u4e00\u5207\u529e\u6cd5\u4e3a\u5979\u6cbb\u4f24\uff0c\u4f46\u56e0\u4e3a\u4e0d\u80fd\u89e3\u5f00\u5979\u7684\u660f\u7761\u7a74\uff0c\u5979\u5c31\u65e0\u6cd5\u81ea\u5df1\u8fd0\u52a8\u5185 \u606f\u7597\u4f24\uff0c\u6240\u4ee5\u5979\u7684\u4f24\u52bf\u597d\u5f97\u5f88\u7f13\u6162\u3002 \u3000\u3000\u738b\u5c11\u537f\u53c8\u4e0d\u6b62\u4e00\u6b21\u63d0\u51fa\u53ef\u4ee5\u53d6\u81ea\u5df1\u8eab\u4e0a\u8840\u8089\u5408\u836f\uff0c\u4f46\u7a0b\u5c0f\u8776\u575a\u51b3\u4e0d\u540c\u610f\u3002\u76ee\u9001\u94c1\u7fce\u9886\u7740 \u91d1\u5e9c\u7684\u4e94\u540d\u9ad8\u624b\u79bb\u5f00\u540e\uff0c\u7a0b\u5c0f\u8776\u8ba9\u5c0f\u96c5\u5c06\u56db\u5927\u6355\u5934\u548c\u60dc\u7389\u53eb\u5230\u4e86\u81ea\u5df1\u7684\u623f\u95f4\u6765\u3002 \u3000\u3000\u4e94\u4eba\u521a\u4e00\u8fdb\u95e8\uff0c\u8fd8\u6ca1\u5750\u7a33\uff0c\u675c\u671b\u6708\u5c31\u95ee\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u94c1\u7fce\u662f\u4e0d\u662f\u5e26\u6765\u4e86\u65b0\u7684\u60c5\u51b5\uff1f\u68a6 \u5e7b\u4e4b\u5200\u53bb\u91d1\u5e9c\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u94c1\u6355\u5934\u56de\u5e9c\u8859\u662f\u4e0e\u6211\u5546\u8bae\u53e6\u5916\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4e8b\uff0c\u603b\u6355\u5934\u80fd\u544a\u8bc9\u6211\u4eec\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4ed6\u5e26\u6765\u4e86\u91d1\u767e\u5e74\u7684\u4e00\u4e2a\u5efa\u8bae\u3002\u4f46\u6211\u6ca1\u6709\u540c\u610f\uff0c\u4e5f\u5c31\u4e0d\u7528\u63d0\u5b83\u4e86\u3002\u6211\u53eb\u4f60\u4eec \u6765\uff0c\u662f\u60f3\u95ee\u95ee\u4f60\u4eec\u5bf9\u5f53\u524d\u7684\u5f62\u52bf\u6709\u4ec0\u4e48\u770b\u6cd5\uff0c\u6709\u4ec0\u4e48\u9ad8\u89c1\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u770b\u6cd5\u5012\u6709\u4e00\u70b9\uff0c\u53ea\u662f\u8bf4\u4e0d\u4e0a\u4ec0\u4e48\u9ad8\u89c1\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8bf7\u8bb2\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6211\u8ba4\u4e3a\uff0c\u73b0\u5728\u7684\u5f53\u52a1\u4e4b\u6025\u662f\u6551\u5c0f\u6587\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u7406\u7531\u5462\uff1f\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u56e0\u6211\u89c9\u5f97\u4e0e\u6740\u624b\u96c6\u56e2\u7684\u51b3\u6218\u5df2\u8feb\u5728\u7709\u776b\uff0c\u800c\u5c0f\u6587\u7684\u2018\u5251\u6d77\u6d74\u9b42\u2019\u5251\u6cd5\u5728\u6740 \u654c\u5236\u80dc\u4e0a\uff0c\u6709\u5f88\u5927\u7684\u5a01\u529b\uff01\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff0c\u201c\u5434\u5144\u8bf4\u7684\u6ca1\u9519\uff0c\u90a3\u5929\u665a\u4e0a\uff0c\u5979\u4e00\u4e3e\u51fb\u6740\u4e0d\u8001\u4e66\u751f\u90e8\u4e0b\u4e03\u540d\u975e\u5e38\u5389\u5bb3\u7684\u9ed1\u8863 \u5251\u624b\uff0c\u4e0d\u8001\u4e66\u751f\u5c31\u662f\u56e0\u4e3a\u8ba4\u51fa\u4e86\u2018\u5251\u6d77\u6d74\u9b42\u2019\u5251\u6cd5\uff0c\u624d\u88ab\u60ca\u9000\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4ee5\u524d\uff0c\u6211\u66fe\u544a\u8beb\u5979\u4e0d\u8981\u7ec3\u8fd9\u79cd\u5251\u6cd5\uff0c\u56e0\u4e3a\u8fd9\u79cd\u5251\u6cd5\u5c31\u597d\u50cf\u53cc\u950b\u5b9d \u5251\uff0c\u80fd\u6740\u654c\uff0c\u4e5f\u4f1a\u4f24\u53ca\u81ea\u5df1\u2026\u2026\u5509\uff01\u6ca1\u6709\u62a2\u56de\u89e3\u836f\uff0c\u53c8\u8be5\u5982\u4f55\u6551\u5979\u5462\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u603b\u6355\u5934\u5bf9\u4e8e\u2018\u6210\u5927\u4e8b\u8005\uff0c\u4e0d\u62d8\u5c0f\u8282\u2019\u8fd9\u53e5\u8bdd\u600e\u6837\u770b\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u8ba4\u4e3a\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u90a3\u4e48\u603b\u6355\u5934\u4e3a\u4ec0\u4e48\u4e0d\u540c\u610f\u738b\u5927\u4eba\u7684\u8bf7\u6c42\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u7b11\u5bb9\u770b\u4e0a\u53bb\u6709\u4e9b\u52c9\u5f3a\uff0c\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u8bf4\u7ed9\u4ed6\u4eec\u542c\u5427\u3002\u201d\u60dc\u7389\u8138\u6709\u4e9b\u7ea2\uff0c \u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u8ba9\u6211\u5982\u4f55\u5f00\u53e3\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u662f\u795e\u533b\u95e8\u4e0b\u51b7\u9762\u795e\u533b\u8c2d\u6267\u4e2d\u7684\u5973\u513f\uff0c\u4e5f\u662f\u4ed6\u7684\u4f20\u4eba\u3002\u5176\u5b9e\uff0c\u4f60\u4e5f\u662f \u4e00\u4e2a\u533b\u8005\uff0c\u533b\u8005\u7236\u6bcd\u5fc3\uff0c\u6709\u4ec0\u4e48\u8bdd\u4e0d\u90fd\u53ef\u4ee5\u76f4\u8bf4\u5417\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u5bf9\u5bf9\u5bf9\uff0c\u518d\u8bf4\uff0c\u738b\u5927\u4eba\u81ea\u5df1\u4e5f\u8981\u6c42\u732e\u51fa\u81ea\u5df1\u4e00\u5757\u8840\u8089\uff0c\u60dc\u7389\u59d1\u5a18\u53c8\u6709\u4ec0\u4e48 \u4e0d\u597d\u5f00\u53e3\u7684\u5462\uff1f\u201d \u3000\u3000\u60dc\u7389\u7684\u8138\u66f4\u7ea2\uff0c\u58f0\u97f3\u4e5f\u4f4e\u4e86\u597d\u591a\uff0c\u9053\uff1a\u201c\u53ea\u56e0\u2026\u2026\u53ea\u56e0\u2026\u2026\u5408\u836f\u9700\u8981\u7684\u5e76\u4e0d\u662f\u2026\u2026\u4e0d\u662f \u738b\u5927\u4eba\u7684\u8840\u8089\u2026\u2026\u201d \u3000\u3000\u5434\u94c1\u5cf0\u5403\u60ca\u9053\uff1a\u201c\u90a3\u8fd8\u8981\u4ec0\u4e48\uff1f\u8981\u4ed6\u7684\u6574\u4e2a\u4eba\u624d\u884c\uff1f\u96be\u9053\u8bf4\u914d\u4e00\u70b9\u89e3\u836f\u8fd8\u8981\u7528\u4e0a\u4e00\u6574\u4e2a \u5927\u6d3b\u4eba\uff1f\u201d \u3000\u3000\u60dc\u7389\u7684\u58f0\u97f3\u66f4\u5c0f\u4e86\uff1a\u201c\u4e0d\u2026\u2026\u4e0d\u662f\u7684\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u90a3\u8fd8\u80fd\u8981\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u60dc\u7389\u7528\u4e00\u6392\u788e\u7389\u822c\u7684\u7693\u9f7f\u542e\u7740\u9c9c\u7ea2\u7684\u6a31\u5507\uff0c\u5ffd\u7136\u4f0f\u5230\u675c\u671b\u6708\u8033\u8fb9\uff0c\u8f7b\u58f0\u8bf4\u4e86\u51e0\u4e2a\u5b57\u3002 \u3000\u3000\u675c\u671b\u6708\u4e00\u6014\uff0c\u7136\u540e\u5927\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u60dc\u7389\u6068\u6068\u5730\u7528\u8f7b\u62f3\u6253\u4e86\u4ed6\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4eba\u5bb6\u90fd\u7f9e\u6b7b\u4e86\uff0c\u4f60\u8fd8\u7b11\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u7b11\u9053\uff1a\u201c\u4e5f\u96be\u602a\u4f60\u4eec\u4e0d\u597d\u51fa\u53e3\uff0c\u6211\u6765\u8bf4\u5427\uff0c\u914d\u836f\u9700\u8981\u738b\u5927\u4eba\u7684\u7cbe\u8840\u3002\u201d\u4e09\u5927\u6355\u5934 \u4e00\u60ca\uff0c\u4e5f\u90fd\u6709\u70b9\u4e0d\u81ea\u5728\u4e86\u3002 \u3000\u3000\u5c91\u5578\u864e\u5fc3\u76f4\u53e3\u5feb\uff0c\u9053\uff1a\u201c\u600e\u4e48\u4e00\u5b9a\u8981\u90a3\u4e1c\u897f\u5462\uff1f\u90a3\u73a9\u610f\u513f\u80fd\u5403\u5417\uff1f\u201d \u3000\u3000\u5434\u94c1\u5cf0\u4e5f\u9053\uff1a\u201c\u662f\u554a\uff0c\u60dc\u7389\u59d1\u5a18\uff0c\u8fd9\u795e\u533b\u95e8\u7684\u836f\u53ef\u591f\u90aa\u6027\u7684\u3002\u201d \u3000\u3000\u60dc\u7389\u6da8\u7ea2\u4e86\u8138\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u89e3\u836f\u90aa\u6027\uff0c\u800c\u662f\u4e0d\u8001\u4e66\u751f\u4e0d\u5b88\u795e\u533b\u95e8\u95e8\u89c4\uff0c\u64c5\u81ea\u4f7f\u7528\u4e86\u90a3\u79cd \u795e\u836f\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u6127\u7b11\u9053\uff1a\u201c\u662f\u5434\u67d0\u8bf4\u9519\u4e86\uff0c\u60dc\u7389\u59d1\u5a18\u4e0d\u8981\u89c1\u602a\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u7b11\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u5427\uff0c\u4e0d\u7ba1\u90aa\u4e0d\u90aa\uff0c\u5148\u914d\u51fa\u4e86\u836f\u518d\u8bf4\uff0c\u6211\u53bb\u95ee\u738b\u5927\u4eba\u63d0\u51fa\u6765\u5427\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5c31\u7ad9\u8d77\u8eab\uff0c\u5927\u6b65\u5411\u5916\u8d70\u3002 \u3000\u3000\u60dc\u7389\u7740\u6025\u9053\uff1a\u201c\u675c\u5927\u54e5\u4e14\u6162\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u4e3a\u4e86\u6551\u4eba\uff0c\u738b\u5927\u4eba\u4e0d\u4f1a\u89c1\u602a\u7684\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u5c31\u662f\uff0c\u603b\u6bd4\u786c\u5250\u4e0b\u4e00\u5757\u8089\u6765\u8981\u5bb9\u6613\u7684\u591a\u4e86\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u5509\uff0c\u4f60\u4eec\u4e0d\u77e5\u9053\uff0c\u836f\u662f\u836f\uff0c\u90a3\u4e2a\u2026\u2026\u90a3\u4e2a\u662f\u90a3\u4e2a\u2026\u2026\u90a3\u4e2a\u53ef\u4e0d\u662f\u548c\u836f\u4e00\u8d77\u5403 \u7684\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u8f6c\u8fc7\u8eab\uff0c\u5403\u60ca\u5730\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u539f\u6765\u662f\u8981\u5728\u5403\u836f\u540e\uff0c\u8981\u738b\u5927\u4eba\u4e0e\u5c0f\u6587\u2026\u2026\u8fd9 \u4e2a\u2026\u2026\u90a3\u4e2a\u2026\u2026\u7537\u5973\u4ea4\u5408\uff1f\u201d \u3000\u3000\u60dc\u7389\u70b9\u70b9\u5934\uff0c\u8138\u4e00\u76f4\u7ea2\u5230\u4e86\u8116\u5b50\u6839\u4e0a\uff0c\u7528\u624b\u6342\u4f4f\u8138\uff0c\u7f9e\u5f97\u76f4\u8dfa\u811a\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5782\u76ee\u770b\u7740\u5730\u4e0a\uff0c\u4e5f\u662f\u7c89\u8138\u901a\u7ea2\uff0c\u4e00\u526f\u5a07\u7f9e\u4e4b\u6001\u3002 \u3000\u3000\u96be\u602a\u554a\uff0c\u5979\u548c\u60dc\u7389\u90fd\u662f\u9ec4\u82b1\u5904\u5b50\uff0c\u5f53\u7740\u56db\u4e2a\u5927\u7537\u4eba\u7684\u9762\u8bf4\u8fd9\u79cd\u4e8b\uff0c\u80fd\u4e0d\u7f9e\u5417\uff1f\u675c\u671b\u6708\u7b11 \u4e86\u7b11\uff0c\u5bf9\u60dc\u7389\u9053\uff1a\u201c\u602a\u9053\u90a3\u5929\u4f60\u4e00\u5b9a\u4e0d\u8ba9\u6587\u9601\u9601\u4e3b\u62c9\u6211\u53bb\u5408\u836f\uff0c\u539f\u6765\u2026\u2026\u539f\u6765\u4f60\u662f\u4e0d\u820d\u5f97 \u6211\u3002\u201d \u3000\u3000\u60dc\u7389\u6251\u4e0a\u53bb\uff0c\u634f\u8d77\u7c89\u62f3\uff0c\u5728\u4ed6\u80f8\u524d\u4e00\u9635\u7684\u4e71\u6253\uff0c\u5a07\u58f0\u9053\uff1a\u201c\u4f60\u6b3a\u8d1f\u6211\uff01\u4f60\u6b3a\u8d1f\u6211\uff01\u60dc\u7389 \u4e0d\u7ba1\u5566\uff01\u201d \u3000\u3000\u4e09\u5927\u6355\u5934\u7eb5\u58f0\u5927\u7b11\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4e5f\u5f00\u989c\u4e00\u7b11\uff0c\u4e00\u53cc\u6c34\u7075\u7075\u7684\u773c\u775b\u779f\u5411\u675c\u671b\u6708\u3002 \u3000\u3000\u5979\u7684\u76ee\u5149\u5341\u5206\u590d\u6742\u3002 \u3000\u3000\u5fc3\u60c5\u5f53\u7136\u66f4\u590d\u6742\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u4e0d\u8981\u95f9\u4e86\uff0c\u8c08\u6b63\u4e8b\u5427\uff0c\u8fd9\u6837\u8ba9\u4eba\u89c1\u7b11\uff01\u201d \u3000\u3000\u60dc\u7389\u53c8\u201c\u5478\u201d\u4e86\u4e00\u53e3\uff0c\u65b9\u624d\u4f4f\u624b\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u8fd9\u4e8b\u96be\u529e\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53f9\u6c14\u9053\uff1a\u201c\u8c01\u8bf4\u4e0d\u662f\u5462\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u4e8b\u6025\u4ece\u6743\u561b\uff0c\u6ca1\u6709\u4ec0\u4e48\u96be\u529e\u7684\uff0c\u5c31\u8ba9\u4ed6\u4eec\u2026\u2026\u90a3\u4e2a\u4e00\u6b21\u4e0d\u5c31\u5b8c\u4e86\u561b\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u603b\u6355\u5934\uff0c\u5c0f\u6587\u5e94\u8be5\u8fd8\u662f\u5904\u5b50\u4e4b\u8eab\u5427\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7c89\u8138\u7f9e\u7ea2\uff0c\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u5982\u679c\u738b\u5927\u4eba\u5c1a\u672a\u5a36\u59bb\u2026\u2026\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u90a3\u6709\u4ec0\u4e48\uff0c\u5c31\u8ba9\u4ed6\u6536\u5c0f\u6587\u505a\u4e2a\u5c0f\u5457\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8bf4\u662f\u8fd9\u6837\u8bf4\uff0c\u53ef\u4e00\u6015\u5c0f\u6587\u89c9\u5f97\u59d4\u5c48\uff0c\u4e8c\u6015\u738b\u5927\u4eba\u592b\u59bb\u60c5\u6df1\uff0c\u4e0d\u613f\u7eb3\u59be\uff0c\u4e0d \u5c31\u96be\u4e86\uff01\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u5ffd\u7136\u9053\uff1a\u201c\u4e0d\u77e5\u738b\u592b\u4eba\u53ef\u66fe\u751f\u80b2\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u4e00\u62cd\u5927\u817f\uff0c\u9053\uff1a\u201c\u7740\u54c7\uff01\u8001\u4e8e\uff0c\u771f\u6709\u4f60\u7684\uff0c\u5173\u952e\u65f6\u5019\uff0c\u603b\u80fd\u4e00\u8bed\u70b9\u7834\u5929\u673a\uff01\u201d \u3000\u3000\u4ed6\u8f6c\u800c\u5bf9\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6211\u770b\u8fd9\u4e8b\u4e5f\u53ea\u80fd\u8fd9\u6837\u529e\u4e86\u3002\u738b\u5927\u4eba\u4eba\u5230\u4e2d\u5e74\uff0c\u592b\u4eba\u53c8\u4e00 \u76f4\u6ca1\u6709\u751f\u80b2\uff0c\u7eb3\u59be\u4e4b\u4e3e\uff0c\u5b9e\u5c5e\u5e73\u5e38\u3002\u5c0f\u6587\u90a3\u8fb9\u7684\u5de5\u4f5c\uff0c\u5f97\u7531\u603b\u6355\u5934\u4f60\u6765\u505a\uff0c\u738b\u5927\u4eba\u8fd9\u8fb9\uff0c\u5305 \u5728\u6211\u4eec\u56db\u5927\u6355\u5934\u8eab\u4e0a\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u5934\u9053\uff1a\u201c\u597d\u5427\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u5927\u58f0\u5439\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5509\uff01\u5211\u90e8\u7684\u6355\u5934\u53ef\u771f\u4e0d\u597d\u5e72\u554a\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u5c91\u5144\u4f55\u51fa\u6b64\u8a00\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u7b11\u9053\uff1a\u201c\u4f60\u770b\u4f60\u770b\uff0c\u6211\u4eec\u53c8\u8981\u7834\u6848\uff0c\u53c8\u8981\u7ba1\u7f09\u51f6\uff0c\u53c8\u8981\u7ba1\u4eba\u5bb6\u6709\u65e0\u540e\u7ee7\u9999\u706b\uff0c\u53c8 \u8981\u7ba1\u4eba\u5bb6\u7eb3\u59be\u53d6\u5c0f\uff0c\u8fd9\u6837\u7684\u4e8b\uff0c\u5e72\u8d77\u6765\u5bb9\u6613\u5417\uff1f\u771f\u4e0d\u5982\u56de\u5bb6\u79cd\u5730\u53bb\uff01\u201d\u623f\u5185\u516d\u4eba\u4e00\u8d77\u5927\u7b11\u8d77 \u6765\u3002 \u3000\u3000\u597d\u4e00\u9635\u5b50\uff0c\u516d\u4eba\u624d\u6b62\u4f4f\u7b11\uff0c\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u73b0\u5728\u5c31\u53bb\u914d\u836f\uff0c\u6211\u53bb\u70b9\u9192\u5c0f\u6587\uff0c\u770b\u5979 \u662f\u5426\u613f\u610f\u3002\u675c\u53f8\u4e3b\uff0c\u738b\u5927\u4eba\u90a3\u8fb9\uff0c\u53ea\u80fd\u8bf7\u4f60\u548c\u4e09\u4f4d\u5927\u6355\u5934\u591a\u8d39\u53e3\u820c\u4e86\uff01\u201d\u5c91\u5578\u864e\u9053\uff1a\u201c\u603b\u6355 \u5934\u653e\u5fc3\uff0c\u6211\u4eec\u8fd9\u8fb9\u6ca1\u95ee\u9898\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5947\u602a\u5730\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd9\u822c\u80af\u5b9a\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u7b11\u9053\uff1a\u201c\u5c0f\u6587\u53c8\u6f02\u4eae\uff0c\u6b66\u529f\u53c8\u597d\uff0c\u5b98\u804c\u53c8\u4e0d\u4f4e\uff0c\u80fd\u8981\u5230\u8fd9\u6837\u7684\u5973\u5b50\uff0c\u771f\u662f\u4e0a\u8f88\u5b50 \u4fee\u6765\u7684\uff0c\u522b\u8bf4\u505a\u5c0f\uff0c\u5c31\u7b97\u662f\u6b63\u5ba4\uff0c\u738b\u5927\u4eba\u53ea\u6015\u4e5f\u4f1a\u540c\u610f\u5462\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u201c\u54c8\u201d\u5730\u4e00\u7b11\uff0c\u53c8\u6b63\u8272\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u7b11\u9053\uff1a\u201c\u8001\u5c91\u5c31\u559c\u6b22\u80e1\u8bf4\uff0c\u8d70\u8d70\u8d70\uff0c\u54b1\u4eec\u5feb\u53bb\u529e\u6b63\u4e8b\u53bb\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u5ffd\u7136\u9053\uff1a\u201c\u6162\u6162\u6162\uff0c\u6211\u7a81\u7136\u60f3\u8d77\u4e00\u4e8b\uff0c\u8981\u6c42\u603b\u6355\u5934\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u60dc\u7389\u5fc3\u91cc\u987f\u65f6\u76f4\u8df3\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u4e0d\u4f1a\u662f\u60f3\u4e58\u6b64\u673a\u4f1a\uff0c\u6c42\u603b\u6355\u5934\u5ac1\u7ed9\u4ed6\uff1f\u201d\u5fc3\u91cc\u7acb\u523b\u9178\u5f97 \u6781\u96be\u53d7\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5c0f\u6587\u7684\u5251\u6cd5\uff0c\u4e0d\u77e5\u662f\u4e0d\u662f\u603b\u6355\u5934\u6559\u7684\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u662f\u3002\u6211\u4e5f\u4e0d\u4f1a\uff0c\u4e0d\u8fc7\u6211\u624b\u91cc\u6709\u5251\u8c31\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8bf7\u603b\u6355\u5934\u51c6\u8bb8\u5c5e\u4e0b\u4e60\u7ec3\u2018\u5251\u6d77\u6d74\u9b42\u2019\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u4f60\u5728\u8bf4\u4ec0\u4e48\uff1f\u201d\u7a0b\u5c0f\u8776\u5a07\u8eaf\u5267\u9707\uff0c\u663e\u7136\u5185\u5fc3\u5341\u5206\u9707\u52a8\u3002 \u3000\u3000\u800c\u5979\u5bf9\u675c\u671b\u6708\u7684\u5173\u5207\u4e4b\u60c5\uff0c\u4e5f\u7531\u6b64\u6d41\u9732\u51fa\u6765\u3002 \u3000\u3000\u4e09\u5927\u6355\u5934\u548c\u60dc\u7389\u4e5f\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u60dc\u7389\u542c\u8bf4\u675c\u671b\u6708\u8981\u6c42\u603b\u6355\u5934\u7684\u662f\u60f3\u5b66\u201c\u5251\u6d77\u6d74\u9b42\u201d\u5251\u6cd5\uff0c\u5fc3\u4e2d\u9178\u610f\u987f\u6d88\uff0c\u4f46\u53c8\u7acb\u523b\u4e3a\u675c \u671b\u6708\u62c5\u5fc3\u4e86\u3002 \u3000\u3000\u5979\u80fd\u4e0d\u62c5\u5fc3\u5417\uff1f \u3000\u3000\u7ec3\u4e86\u8fd9\u79cd\u5145\u6ee1\u90aa\u6c14\u7684\u5251\u6cd5\uff0c\u675c\u671b\u6708\u53d8\u574f\u4e86\uff0c\u8be5\u600e\u4e48\u529e\u4e86\uff01 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u90a3\u5929\u6211\u5df2\u7ecf\u548c\u5c0f\u6587\u8c08\u4e86\u4e00\u8c08\uff0c\u5c0f\u6587\u4e5f\u8bf4\u8fd9\u5251\u6cd5\u5728\u56db\u5927\u6355\u5934\u4e2d\u53ea\u6709\u6211\u80fd\u7ec3\uff0c \u56e0\u4e3a\u53ea\u6709\u6211\u4e00\u4eba\u662f\u4f7f\u5251\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u7ec3\u8fd9\u79cd\u90aa\u6076\u7684\u5251\u6cd5\u5462\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u6b63\u8272\u9053\uff1a\u201c\u4e3a\u4e86\u589e\u5f3a\u6211\u65b9\u7684\u6218\u6597\u529b\uff01\u4e3a\u4e86\u7f09\u62ff\u771f\u51f6\uff01\u4e3a\u4e86\u66f4\u597d\u5730\u4fdd\u62a4\u738b\u5927\u4eba\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u53ef\u8fd9\u662f\u5f88\u5371\u8138\u7684\uff0c\u5c0f\u6587\u5c31\u662f\u4e00\u4e2a\u6d3b\u7684\u4f8b\u5b50\u554a\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u8ba4\u4e3a\uff0c\u90aa\u4e0e\u4e0d\u90aa\u4e4b\u5206\uff0c\u5728\u4e8e\u4eba\u800c\u4e0d\u5728\u4e8e\u5251\u6cd5\uff0c\u53ea\u8981\u81ea\u5df1\u5185\u5fc3\u5145\u6ee1\u6b63\u4e49\u4e4b \u6c14\uff0c\u6ca1\u6709\u79c1\u5fc3\uff0c\u5c31\u4e0d\u4f1a\u88ab\u5251\u6cd5\u4e2d\u7684\u90aa\u6c14\u63a7\u5236\uff01\u6211\u867d\u7136\u6b66\u529f\u4e0d\u7b97\u7edd\u9ad8\uff0c\u4f46\u81ea\u4fe1\u6709\u4e00\u8154\u6b63\u6c14\uff0c\u6709 \u6ee1\u5fc3\u6b63\u4e49\uff0c\u6211\u81ea\u4fe1\uff0c\u5fc5\u5b9a\u80fd\u4ee5\u4eba\u9a6d\u5251\uff0c\u800c\u4e0d\u4f1a\u4e3a\u5251\u6240\u9a6d\uff01\u201d\u7a0b\u5c0f\u8776\u5341\u5206\u6fc0\u52a8\uff0c\u4e00\u65f6\u8bf4\u4e0d\u51fa\u8bdd \u6765\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8bf7\u603b\u6355\u5934\u6069\u51c6\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u8fd8\u662f\u4e0d\u8bf4\u8bdd\uff01 \u3000\u3000\u5979\u5185\u5fc3\u6b63\u6fc0\u70c8\u5730\u7ffb\u817e\u7740\uff01 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u90aa\u4e0d\u538b\u6b63\uff0c\u603b\u6355\u5934\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u4e5f\u9053\uff1a\u201c\u5c0f\u675c\u7684\u81ea\u63a7\u80fd\u529b\uff0c\u6211\u662f\u5f88\u6e05\u695a\u7684\uff0c\u603b\u6355\u5934\uff0c\u4f60\u5c31\u540c\u610f\u5427\uff01\u201d\u7a0b\u5c0f\u8776\u5782\u9996 \u65e0\u8bed\uff0c\u957f\u957f\u7684\u773c\u776b\u6bdb\u4e0d\u4f4f\u5730\u5ffd\u6247\u7740\u3002 \u3000\u3000\u4e8e\u627f\u5fd7\u3001\u5c91\u5578\u864e\u4e5f\u5f00\u53e3\u4e86\uff0c\u201c\u662f\u7684\uff0c\u6211\u4eec\u4e5f\u5e0c\u671b\u603b\u6355\u5934\u80fd\u7b54\u5e94\u5c0f\u675c\u7684\u8bf7\u6c42\uff01\u201d\u60dc\u7389\u7a81\u7136 \u9053\uff1a\u201c\u6211\u4e0d\u540c\u610f\uff01\u603b\u6355\u5934\uff0c\u60dc\u7389\u4e0d\u540c\u610f\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u4eec\u8fd9\u9879\u4efb\u52a1\u6709\u591a\u8270\u5de8\uff1f\u6211\u4eec\u9762\u5bf9\u7684\uff0c\u662f\u5927\u9b54\u5934\u68a6\u5e7b\u4e4b \u5200\uff0c\u6b66\u529f\u3001\u90aa\u672f\u66fe\u4e3a\u4e0a\u4e58\u7684\u4e0d\u8001\u4e66\u751f\u547c\u5ef6\u8fdc\uff0c\u624b\u4e0b\u62e5\u6709\u8fd1\u5343\u591a\u4ee5\u547d\u76f8\u968f\u7684\u6740\u624b\u4e4b\u738b\u8840\u624b\u65b9\u8f6e\uff0c \u679c\u5982\u603b\u6355\u5934\u6240\u8bf4\u6b64\u4e8b\u7275\u626f\u53ca\u5e7f\uff0c\u5219\u4ed6\u4eec\u80cc\u540e\u5fc5\u7136\u8fd8\u4f1a\u6709\u66f4\u5927\u6765\u5934\uff0c\u66f4\u5389\u5bb3\u7684\u4eba\uff0c\u5982\u679c\u56db\u5927\u6355 \u5934\u4e2d\u552f\u4e00\u80fd\u7ec3\u2018\u5251\u6d77\u6d74\u9b42\u2019\u7684\u6211\u4e0d\u7ec3\u8fd9\u79cd\u5251\u6cd5\uff0c\u6211\u4eec\u5982\u4f55\u5bf9\u6297\u4ed6\u4eec\uff1f\u66f4\u4e0d\u7528\u8bf4\u7834\u6848\u7f09\u51f6\u4e86\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u8138\u8272\u6709\u70b9\u82cd\u767d\u5730\u9053\uff1a\u201c\u597d\uff01\u6211\u540c\u610f\uff01\u675c\u53f8\u4e3b\uff0c\u4eca\u5929\u6211\u5c31\u628a\u5251\u8c31\u4ea4\u7ed9\u4f60\uff0c\u800c\u4e14\u4f1a\u5c06 \u6211\u5bf9\u8fd9\u79cd\u5251\u6cd5\u7684\u7406\u89e3\u544a\u8bc9\u4f60\uff0c\u4ee5\u4fbf\u4f60\u80fd\u5c3d\u5feb\u5730\u638c\u63e1\u5b83\u7684\u51b3\u7a8d\uff0c\u5728\u6700\u77ed\u7684\u65f6\u95f4\u5185\u7ec3\u6210\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u4e25\u8083\u5730\u9053\uff1a\u201c\u8c22\u603b\u6355\u5934\uff01\u201d \u3000\u3000\u4ed6\u53c8\u95ee\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u770b\u51ed\u6211\u7684\u6c14\u8d28\u548c\u5185\u529f\u57fa\u7840\uff0c\u6700\u5feb\u9700\u8981\u591a\u957f\u65f6\u95f4\u624d\u80fd\u7ec3\u6210\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u8fd9\u6837\u5427\uff0c\u7531\u6211\u548c\u60dc\u7389\u4e00\u8d77\u7528\u5185\u529b\u52a9\u4f60\uff0c\u6211\u770b\uff0c\u4e09\u5929\u540e\uff0c\u4f60\u5c31\u80fd\u8fbe \u5230\u2018\u5251\u6d77\u6d74\u9b42\u2019\u7684\u4e03\u6210\u706b\u5019\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u771f\u662f\u592a\u597d\u4e86\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u675c\u5927\u54e5\u7ec3\u5b83\uff0c\u771f\u7684\u6ca1\u6709\u5371\u9669\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u65ad\u7136\u9053\uff1a\u201c\u80af\u5b9a\u4f1a\u6709\uff0c\u4f46\u6211\u76f8\u4fe1\uff0c\u4ed6\u4e00\u5b9a\u80fd\u9760\u81ea\u5df1\u7684\u6bc5\u529b\u548c\u6b63\u4e49\u4e4b\u5fc3\uff0c\u514b\u670d\u5371\u9669\uff0c \u518d\u8bf4\uff0c\u6709\u60dc\u7389\u4f1a\u5e2e\u4ed6\u554a\u3002\u201d \u3000\u3000\u60dc\u7389\u4f4e\u58f0\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u4e0d\u4f1a\u602a\u6211\u79c1\u5fc3\u592a\u91cd\u5427\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u4f1a\u7684\uff0c\u6362\u4e86\u6211\u662f\u4f60\uff0c\u6211\u4e5f\u4e0d\u4f1a\u540c\u610f\u7684\u3002\u201d \u3000\u3000\u8bdd\u4e00\u8bf4\u51fa\u53e3\uff0c\u5979\u7684\u8138\u5c31\u7ea2\u4e86\uff01 \u3000\u3000\u59d1\u5a18\u554a\uff0c\u4f60\u8fd9\u53e5\u8bdd\u8bf4\u7684\u592a\u76f4\u9732\uff0c\u592a\u5510\u8352\uff0c\u592a\u4e0d\u6ce8\u610f\u5566\uff01 \u3000\u3000\u675c\u671b\u6708\u5fc3\u91cc\u4e00\u52a8\uff0c\u4e24\u9053\u76ee\u5149\u7d27\u7d27\u76ef\u7740\u7a0b\u5c0f\u8776\uff0c\u60f3\u6355\u6349\u4f4f\u5979\u7684\u76ee\u5149\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5fae\u5fae\u4e00\u4f4e\u5934\uff0c\u8eb2\u907f\u7740\u4ed6\u7684\u76ee\u5149\u3002 \u3000\u3000\u8fd9\u6b21\uff0c\u60dc\u7389\u53ef\u6ca1\u6ce8\u610f\u5230\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u7684\u5fc3\u8fd8\u662f\u4e71\u7684\u3002 \u3000\u3000\u4e3a\u4ec0\u4e48\u800c\u5fc3\u4e71\u554a\uff01 \u3000\u3000\u76f8\u7231\u4e2d\u7684\u5c11\u5973\uff0c\u5728\u5fc3\u91cc\u603b\u662f\u4f1a\u628a\u60c5\u90ce\u653e\u5728\u7b2c\u4e00\u4f4d\u7684\u554a\uff01 \u3000\u3000\u51e0\u4eba\u6b63\u51c6\u5907\u5206\u5934\u884c\u52a8\uff0c\u7a81\u7136\u53d1\u751f\u4e86\u4e00\u4ef6\u610f\u60f3\u4e0d\u5230\u7684\u60c5\u51b5\u3002 \u3000\u3000\u91d1\u767e\u5e74\u5230\u5e9c\u8859\u6765\u4e86\u3002 \u3000\u3000\u966a\u540c\u4ed6\u524d\u6765\u7684\uff0c\u6b63\u662f\u8d3e\u82f1\u3002 \u3000\u3000\u91d1\u767e\u5e74\u6307\u660e\u8981\u62dc\u4f1a\u7a0b\u603b\u6355\u5934\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u8ba9\u56db\u5927\u6355\u5934\u3001\u60dc\u7389\u5168\u90fd\u85cf\u8eab\u5728\u5927\u5385\u540e\u7684\u7a7f\u5802\u91cc\uff0c\u81ea\u5df1\u5355\u72ec\u63a5\u89c1\u91d1\u767e\u5e74\u3002 \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u5211\u90e8\u603b\u6355\u5934\u65e5\u7406\u4e07\u6848\uff0c\u5374\u56e0\u6d17\u5237\u5c0f\u5973\u51a4\u60c5\uff0c\u5343\u91cc\u8fe2\u8fe2\uff0c\u6765\u5230\u626c\u5dde\uff0c\u91d1\u67d0\u672a \u80fd\u65e9\u6765\u53e9\u89c1\uff0c\u771f\u662f\u4e07\u4e07\u4e0d\u8be5\u554a\uff01\u91d1\u67d0\u5982\u6709\u4e0d\u5f53\u4e4b\u5904\uff0c\u5c1a\u8bf7\u603b\u6355\u5934\u6d77\u6db5\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6de1\u6de1\u9053\uff1a\u201c\u91d1\u8001\u592a\u5ba2\u6c14\u4e86\u3002\u6211\u6765\u5230\u626c\u5dde\uff0c\u5e76\u4e0d\u5355\u4e3a\u4e86\u6d17\u5237\u91d1\u5c0f\u59d0\u51a4\u60c5\uff0c\u66f4\u91cd\u8981\u7684 \u662f\u8981\u7834\u6848\u7f09\u51f6\uff0c\u4e3a\u9a6c\u63d0\u7763\u7684\u516c\u5b50\u62a5\u4ec7\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u91d1\u67d0\u4e5f\u5e0c\u671b\u603b\u6355\u5934\u80fd\u65e9\u65e5\u7834\u6848\uff0c\u64d2\u62ff\u771f\u51f6\uff0c\u4e3a\u9a6c\u516c\u5b50\u62a5\u4ec7\uff01\u81ea\u9a6c \u516c\u5b50\u88ab\u5bb3\u540e\uff0c\u5c0f\u5973\u4e00\u76f4\u5fc3\u6000\u60b2\u75db\uff0c\u8336\u996d\u4e0d\u601d\uff0c\u7cbe\u795e\u65e5\u89c1\u840e\u987f\u554a\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u8001\u653e\u5fc3\uff0c\u6211\u4eec\u624b\u4e2d\u5df2\u638c\u63e1\u5927\u91cf\u7ebf\u7d22\uff0c\u6848\u5b50\u6307\u65e5\u53ef\u7834\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u9ad8\u5174\u5730\u9053\uff1a\u201c\u90a3\u5c31\u597d\uff01\u90a3\u5c31\u597d\u554a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4ed4\u7ec6\u5730\u6ce8\u89c6\u4e86\u91d1\u767e\u5e74\u4e00\u4f1a\u513f\uff0c\u9053\uff1a\u201c\u91d1\u8001\u7279\u610f\u6765\u5e9c\u8859\uff0c\u4e0d\u5355\u662f\u4e3a\u4e86\u5411\u6211\u8bf4\u51e0\u53e5\u5ba2 \u6c14\u8bdd\u5427\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u662f\uff0c\u603b\u6355\u5934\u8bf4\u7b11\u8bdd\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u90a3\u91d1\u8001\u662f\u6709\u4e8b\u5427\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u987f\u4e86\u4e00\u987f\uff0c\u624d\u9053\uff1a\u201c\u5982\u679c\u91d1\u8001\u60f3\u91cd\u63d0\u94c1\u6355\u5934\u5df2\u7ecf\u8f6c\u544a\u6211\u4eec\u7684\u90a3\u4e2a\u5efa\u8bae\uff0c\u6211\u60f3\u5c31\u6ca1 \u6709\u8fd9\u4e2a\u5fc5\u8981\u4e86\u3002\u56e0\u4e3a\u6211\u4e0d\u60f3\u5192\u9669\uff0c\u66f4\u4e0d\u60f3\u56e0\u91d1\u5e9c\u9632\u536b\u524a\u5f31\uff0c\u800c\u88ab\u68a6\u5e7b\u4e4b\u5200\u593a\u53bb\u4e09\u5b9d\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u591a\u8c22\u603b\u6355\u5934\u5bf9\u672c\u5e9c\u7684\u5173\u5fc3\u548c\u7231\u62a4\uff0c\u91d1\u67d0\u4eca\u65e5\u6765\uff0c\u662f\u53e6\u6709\u8981\u4e8b\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8bf7\u8bb2\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u91d1\u67d0\u542c\u8d3e\u603b\u7ba1\u8bf4\u53ca\u603b\u6355\u5934\u5c5e\u4e0b\u6709\u4e00\u4eba\u4e2d\u4e86\u4e00\u79cd\u5947\u6bd2\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u56de\u7b54\u65f6\uff0c\u76ef\u4e86\u8d3e\u82f1\u4e00\u773c\u3002 \u3000\u3000\u8d3e\u82f1\u5782\u624b\u7ad9\u5728\u91d1\u767e\u5e74\u8eab\u540e\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u4ed6\u5c31\u50cf\u662f\u4e2a\u6728\u5934\u4eba\u4e00\u822c\u3002 \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4e0d\u77e5\u4e2d\u7684\u4f55\u6bd2\u836f\uff1f\u91d1\u67d0\u5e9c\u4e2d\u9887\u6709\u51e0\u4f4d\u5947\u4eba\u5f02\u58eb\uff0c\u800c\u4e14\u5e9c\u4e2d\u4e5f\u5e38\u6709\u5927\u91cf\u5947\u73cd \u836f\u6750\uff0c\u5982\u679c\u6709\u91d1\u67d0\u80fd\u6548\u529b\u4e4b\u5904\uff0c\u8bf7\u603b\u6355\u5934\u76f4\u8a00\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5979\u4e2d\u7684\u6bd2\u7279\u522b\u602a\u5f02\uff0c\u6211\u4eec\u6839\u672c\u67e5\u4e0d\u51fa\u662f\u4f55\u6bd2\u6027\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u662f\u8fd9\u6837\u5417\uff1f\u2026\u2026\u603b\u6355\u5934\uff0c\u626c\u5dde\u5341\u5927\u5bcc\u8c6a\u4e2d\uff0c\u6709\u4e00\u59da\u987a\u5929\uff0c \u5176\u4eba\u95e8\u4e0b\u6709\u4e00\u4e13\u7814\u6bd2\u836f\u7684\u7528\u6bd2\u5c0f\u7ec4\uff0c\u4e0d\u77e5\u4ed6\u4eec\u662f\u5426\u77e5\u9053\u6b64\u79cd\u6bd2\u836f\uff1f\u603b\u6355\u5934\uff0c\u4f60\u4e0d\u59a8\u8bf4\u4e00\u8bf4\u4e2d \u6bd2\u4e4b\u540e\u7684\u75c7\u72b6\uff0c\u8ba9\u91d1\u67d0\u60f3\u4e00\u60f3\u662f\u5426\u89c1\u8fc7\u8fd9\u79cd\u60c5\u51b5\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u5979\u4e2d\u6bd2\u4e4b\u540e\uff0c\u8fde\u6211\u90fd\u8ba4\u4e0d\u51fa\u6765\u4e86\uff0c\u6709\u597d\u51e0\u6b21\u8fd8\u4e3e\u5251\u8981\u6740\u6211\u5462\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u54e6\uff1f\u771f\u5947\u602a\u5440\uff01\u8d3e\u603b\u7ba1\uff0c\u4f60\u6709\u4ec0\u4e48\u60f3\u6cd5\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u5c5e\u4e0b\u4e0d\u6562\u8bf4\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u56e0\u4e3a\u6b64\u4e8b\u4e8b\u5173\u91cd\u5927\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u91d1\u8001\uff0c\u4f60\u5e9c\u4e0a\u8fd9\u4f4d\u603b\u7ba1\u7684\u5634\u5f88\u7d27\uff0c\u4eba\u5f88\u5c0f\u5fc3\u8c28\u614e\u554a\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u8d3e\u82f1\uff0c\u6709\u8bdd\u5c31\u76f4\u8bf4\u561b\uff01\u5e72\u5417\u541e\u541e\u5410\u5410\u7684\uff01\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u8fd9\u79cd\u5947\u5f02\u7684\u6bd2\u836f\uff0c\u53ea\u6709\u4e00\u4e2a\u4eba\u53ef\u80fd\u62e5\u6709\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u59da\u987a\u5929\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4ed6\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u5927\u5403\u4e00\u60ca\u5730\u9053\uff1a\u201c\u4e0d\u4f1a\u5427\uff1f\u59da\u987a\u5929\u4e3a\u4ec0\u4e48\u8981\u5bb3\u603b\u6355\u5934\u7684\u5c5e\u4e0b\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u6211\u6ca1\u6709\u8bf4\u59da\u987a\u5929\u52a0\u5bb3\u603b\u6355\u5934\u90e8\u4e0b\uff0c\u53ea\u662f\u8bf4\u4ed6\u80af\u5b9a\u6709\u8fd9\u795e\u836f\u7684\u3002\u201d\u7a0b\u5c0f\u8776\u9053\uff1a \u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u79cd\u590d\u6742\u5947\u7279\u7684\u6bd2\u836f\u4e0d\u53ef\u80fd\u662f\u4e00\u4e2a\u4eba\u6240\u80fd\u914d\u5236\u51fa\u6765\u7684\uff0c\u53ea\u53ef\u80fd\u662f\u591a\u4eba\u4e00 \u8d77\u7814\u7a76\u7684\u7ed3\u679c\uff0c\u800c\u626c\u5dde\u9644\u8fd1\u4e00\u5e26\uff0c\u53ea\u6709\u59da\u5e9c\u6709\u4e00\u4e2a\u7cbe\u7814\u6bd2\u836f\u7684\u7528\u6bd2\u4e13\u5bb6\u5c0f\u7ec4\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u59da\u5e9c\u6709\u4e00\u4e2a\u7528\u6bd2\u4e13\u5bb6\u5c0f\u7ec4\uff0c\u4e25\u683c\u5730\u8bf4\u8d77\u6765\u5e76\u672a\u89e6\u72af\u5211\u5f8b\uff0c\u6211\u53c8\u600e\u4e48\u80fd\u56e0\u6b64 \u8ffd\u7a76\u4ed6\u5462\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u603b\u6355\u5934\u8bf4\u7684\u5f88\u5bf9\uff0c\u91d1\u67d0\u4e0e\u59da\u987a\u5929\u76f8\u8bc6\u591a\u5e74\uff0c\u4e5f\u5e76\u672a\u5bdf\u89c9\u4ed6\u66fe\u6709\u89e6\u72af\u5f8b\u6cd5\u4e4b \u4e3e\uff0c\u8d3e\u603b\u7ba1\u592a\u5192\u5931\u4e86\u3002\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u8d3e\u67d0\u5e76\u672a\u8ba9\u603b\u6355\u5934\u8ffd\u7a76\u59da\u987a\u5929\uff0c\u53ea\u4e0d\u8fc7\u8bf7\u4ed6\u5e9c\u4e0a\u7684\u4e13\u5bb6\u5c0f\u7ec4\u4e3a\u8d35\u5c5e\u4e0b\u8bca\u6cbb\u4e00 \u4e0b\uff0c\u5e76\u4e3a\u5979\u89e3\u6bd2\uff0c\u5e94\u8be5\u662f\u4e0d\u6210\u95ee\u9898\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u6837\u505a\u884c\u5417\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u8981\u4e0d\u7136\uff0c\u5c31\u8ba9\u91d1\u67d0\u5e9c\u4e0a\u7684\u8d3e\u603b\u7ba1\u548c\u5929\u67ab\u9053\u957f\u4ee5\u91d1\u5e9c\u7684\u540d\u4e49\u53bb\u8bf7\u6c42\u4ed6\u3002\u6211\u60f3\uff0c \u59da\u987a\u5929\u5e94\u8be5\u4e0d\u4f1a\u4e0d\u7ed9\u91d1\u67d0\u4eba\u8fd9\u4e2a\u9762\u5b50\uff0c\u5230\u5e95\u662f\u5341\u51e0\u5e74\u7684\u8001\u76f8\u8bc6\u4e86\u561b\uff01\u201d\u7a0b\u5c0f\u8776\u60f3\u4e86\u60f3\uff0c\u9053\uff1a \u201c\u597d\u5427\uff0c\u6709\u52b3\u91d1\u8001\u4e86\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u80fd\u66ff\u603b\u6355\u5934\u5c3d\u5fae\u8584\u4e4b\u529b\uff0c\u91d1\u67d0\u4e0d\u80dc\u8363\u5e78\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e5f\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u91d1\u8001\u592a\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u6211\u4eec\u4ec0\u4e48\u65f6\u5019\u53bb\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u5f53\u7136\u662f\u8d8a\u5feb\u8d8a\u597d\uff0c\u56e0\u4e3a\u5979\u7684\u60c5\u51b5\u5df2\u7ecf\u5f88\u574f\uff0c\u4e0d\u80fd\u518d\u62d6\u4e0b\u53bb\u4e86\u3002\u4e0d \u7136\uff0c\u4f1a\u6709\u6027\u547d\u4e4b\u5fe7\u554a\uff01\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u8fd9\u6837\u5427\uff0c\u8d3e\u67d0\u5148\u62a4\u9001\u4e1c\u4e3b\u56de\u53bb\uff0c\u518d\u4e0e\u5929\u67ab\u9053\u957f\u4e00\u540c\u524d\u6765\u5e9c\u8859\uff0c\u542c\u5019\u7a0b\u603b\u6355\u5934 \u8c03\u9063\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\u5427\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u7ad9\u8d77\u8eab\uff0c\u9053\uff1a\u201c\u91d1\u67d0\u544a\u8f9e\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u8001\u4e00\u8def\u5c0f\u5fc3\uff0c\u6211\u5c31\u4e0d\u9001\u4f60\u4e86\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u548c\u8d3e\u82f1\u4e00\u8d70\uff0c\u56db\u5927\u6355\u5934\u548c\u60dc\u7389\u5c31\u4ece\u7a7f\u5802\u91cc\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u8fd9\u4e2a\u8d3e\u82f1\u5728\u641e\u4ec0\u4e48\u9b3c\uff1f\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u5947\u602a\uff0c\u8fd9\u6bd2\u660e\u660e\u662f\u4e0d\u8001\u4e66\u751f\u624d\u62e5\u6709\uff0c\u600e\u4e48\u53c8\u4f1a\u626f\u4e0a\u59da\u987a\u5929\u8fd9\u4e2a\u4eba\u4e86\u5462\uff1f\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u5f88\u53ef\u80fd\u91d1\u767e\u5e74\u662f\u60f3\u501f\u6b64\u673a\u4f1a\u6574\u4e00\u6574\u59da\u987a\u5929\uff0c\u8981\u77e5\u9053\uff0c\u5341\u5927\u5bcc\u8c6a\u4e4b\u95f4\u662f\u52fe\u5fc3 \u6597\u89d2\u7684\u5173\u7cfb\u5462\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u83ab\u975e\u59da\u987a\u5929\u5e9c\u91cc\u4e5f\u9690\u6709\u795e\u533b\u95e8\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u7528\u778e\u731c\u4e86\uff0c\u53bb\u770b\u770b\u4e0d\u5c31\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u771f\u8981\u53bb\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5fae\u5fae\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u53bb\uff0c\u5f88\u53ef\u80fd\u4e8b\u60c5\u4f1a\u5c31\u6b64\u6709\u8f6c\u673a\u4e86\u5462\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u5f88\u542b\u7cca\uff0c\u56db\u5927\u6355\u5934\u90fd\u6ca1\u6709\u5341\u5206\u660e\u767d\uff0c\u5374\u53c8\u4e0d\u597d\u518d\u95ee\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\u51c6\u5907\u548c\u8c01\u4e00\u8d77\u53bb\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6de1\u6de1\u9053\uff1a\u201c\u53c8\u4e0d\u662f\u53bb\u59da\u5e9c\u6253\u67b6\uff0c\u5e26\u4eba\u5e72\u4ec0\u4e48\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u60f3\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u5434\u53f8\u4e3b\u548c\u60dc\u7389\u966a\u6211\u53bb\u5427\uff0c\u60dc\u7389\u6df1\u901a\u836f\u7406\uff0c\u53ef\u4ee5\u501f\u673a\u770b\u4e00\u770b\u59da\u5e9c\u7528 \u6bd2\u5c0f\u7ec4\u7684\u5b9e\u529b\uff0c\u5434\u53f8\u4e3b\u561b\uff0c\u5f53\u7136\u662f\u8981\u2018\u4ee5\u9632\u4e07\u4e00\u2019\u55bd\uff01\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u5927\u5bb6\u90fd\u4e3a\u4e4b\u4e00\u7b11\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u603b\u89c9\u5f97\u8d3e\u82f1\u6b64\u4eba\u53ef\u80fd\u662f\u654c\u4eba\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u60c5\u51b5\u672a\u660e\u6717\u4e4b\u524d\uff0c\u8c01\u90fd\u53ef\u80fd\u662f\u654c\u4eba\uff0c\u4e5f\u8c01\u90fd\u53ef\u80fd\u662f\u670b\u53cb\u3002\u201d\u8fd9\u53e5\u8bdd\u53c8\u8bf4\u7684 \u5f88\u542b\u7cca\uff0c\u4e5f\u53ef\u4ee5\u8bf4\u6709\u70b9\u7384\u554a\uff0c\u542c\u5f97\u675c\u671b\u6708\u4e00\u6014\uff0c\u60f3\u518d\u8bf4\u4ec0\u4e48\uff0c\u5374\u53c8\u6ca1\u6709\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u8d3e\u82f1\u7684\u53ef\u7591\u662f\u663e\u800c\u6613\u89c1\u7684\uff0c\u4f46\u7a0b\u5c0f\u8776\u8fd9\u6837\u8bf4\uff0c\u6709\u4ec0\u4e48\u7279\u522b\u6df1\u5c42\u7684\u542b\u4e49\u5417\uff1f\u7a0b\u5c0f\u8776\u548c\u60dc\u7389\u3001 \u5434\u94c1\u5cf0\u3001\u8d3e\u82f1\u3001\u5929\u67ab\u9053\u957f\u4e00\u884c\u4e94\u4eba\u8d70\u5230\u59da\u987a\u5929\u5e9c\u95e8\u524d\u65f6\uff0c\u5929\u5df2\u7ecf\u9ed1\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u59da\u5e9c\u5927\u95e8\u7d27\u95ed\uff0c\u5927\u95e8\u4e0a\u7684\u51e0\u76cf\u706f\u7b3c\u7adf\u7136\u8fd8\u6ca1\u6709\u70b9\u4eae\u3002 \u3000\u3000\u8d3e\u82f1\u4e0a\u524d\u53e9\u51fb\u7740\u95e8\u4e0a\u7684\u5927\u94dc\u73af\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u91d1\u767e\u5e74\u91d1\u5e9c\u603b\u7ba1\u8d3e\u82f1\uff0c\u6c42\u89c1\u59da\u5148\u751f\uff0c\u6709\u8981 \u4e8b\u76f8\u5546\uff01\u201d \u3000\u3000\u95e8\u91cc\u6ca1\u6709\u4eba\u7b54\u5e94\u3002 \u3000\u3000\u8d3e\u82f1\u56de\u5934\u770b\u4e86\u7a0b\u5c0f\u8776\u4e00\u773c\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u518d\u6572\u3002\u201d \u3000\u3000\u8d3e\u82f1\u53c8\u53e9\u4e86\u53e3\u4e0b\u94dc\u73af\uff0c\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u6709\u4eba\u5417\uff1f\u70e6\u8bf7\u8fdb\u53bb\u901a\u62a5\u4e00\u58f0\uff0c\u91d1\u5e9c\u603b\u7ba1\u8d3e\u82f1 \u6c42\u89c1\u59da\u5148\u751f\uff01\u201d \u3000\u3000\u95e8\u91cc\u4e00\u70b9\u58f0\u606f\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u8d3e\u82f1\u7b49\u4e86\u4e00\u7b49\uff0c\u53c8\u9053\uff1a\u201c\u8d3e\u67d0\u6b64\u884c\u4e43\u966a\u540c\u5211\u90e8\u603b\u6355\u5934\u524d\u6765\u67e5\u6848\uff0c\u91cc\u9762\u7684\u4eba\u542c\u7740\uff0c\u5feb\u5feb\u524d \u6765\u5f00\u95e8\uff01\u201d \u3000\u3000\u95e8\u5185\u58f0\u606f\u5168\u65e0\uff01 \u3000\u3000------------- \u3000\u3000\u5b64\u5251\u751f  \u626b\u63cf\u3000  cndy001 zhuyj(\u6731\u836b\u9636)  OCR  \uff0c\u65e7\u96e8\u697c\u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u4e8c\u5341\u4e00\u7ae0 \u3000\u3000\u602a\u4e86\uff01 \u3000\u3000\u59da\u5e9c\u7684\u4eba\u7adf\u7136\u8fde\u5211\u90e8\u603b\u6355\u4e5f\u6562\u62d2\u4e4b\u95e8\u5916\uff0c\u9664\u975e\u662f\u5403\u4e86\u718a\u5fc3\u8c79\u5b50\u80c6\u4e86\uff01 \u3000\u3000\u5c31\u7b97\u771f\u5403\u4e86\u718a\u5fc3\u8c79\u5b50\u80c6\uff0c\u6562\u8fd9\u6837\u505a\u7684\u4eba\uff0c\u4e5f\u5f88\u96be\u627e\u51fa\u51e0\u4e2a\u6765\uff01 \u3000\u3000\u8d3e\u82f1\u5f88\u5403\u60ca\uff0c\u4e5f\u5f88\u751f\u6c14\u3002 \u3000\u3000\u4ed6\u53c8\u4f38\u624b\u53bb\u62cd\u95e8\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u7528\u7684\u52b2\u5927\u4e86\u4e00\u4e9b\uff0c\u5374\u89c1\u95e8\u5e94\u638c\u800c\u5f00\u95ed\uff0c\u95e8\u5f00\u4e86\u4e00\u6761\u5c0f\u7f1d\u3002 \u3000\u3000\u95e8\u7adf\u7136\u6ca1\u6709\u6813\u4e0a\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u628a\u63a8\u5f00\u95e8\uff0c\u76f4\u51b2\u8fdb\u95e8\u91cc\u3002 \u3000\u3000\u770b\u6765\uff0c\u59da\u5e9c\u91cc\u51fa\u4e8b\u4e86\uff01 \u3000\u3000\u82e5\u5927\u4e00\u4e2a\u524d\u9662\uff0c\u7adf\u7136\u662f\u9ed1\u6c89\u6c89\u7684\uff0c\u8fde\u4e00\u70b9\u706f\u706b\u4e5f\u6ca1\u6709\uff0c\u800c\u4e14\u9662\u4e2d\u5b89\u9759\u6781\u4e86\uff0c\u9664\u4e86\u98ce\u5439\u6811 \u53f6\u58f0\u5916\uff0c\u5176\u5b83\u4ec0\u4e48\u58f0\u97f3\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u5929\u67ab\u9053\u957f\u60ca\u7591\u4e0d\u5b9a\u5730\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u59da\u5e9c\u91cc\u600e\u4e48\u4f1a\u4e00\u4e2a\u4eba\u4e5f\u6ca1\u6709\u4e86\uff1f\u4f1a\u4e0d\u4f1a\u662f\u68a6\u5e7b\u4e4b \u5200\u77e5\u9053\u91d1\u5e9c\u9632\u536b\u68ee\u4e25\uff0c\u65e0\u6cd5\u4e0b\u624b\uff0c\u4fbf\u5148\u62ff\u59da\u5e9c\u5f00\u5200\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u6837\u5427\uff0c\u6211\u4eec\u51e0\u4eba\u5206\u5934\u53bb\u627e\u4e00\u627e\uff0c\u770b\u770b\u5e9c\u91cc\u8fd8\u6709\u6ca1\u6709\u4eba\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u8001\u9053\u58eb\u548c\u8d3e\u603b\u7ba1\u4e00\u8def\u4ece\u5de6\u8fb9\u8fdb\u53bb\uff0c\u8bf7\u7a0b\u603b\u6355\u5934\u4ece\u53f3\u8fb9\u8fdb\u53bb\uff0c\u4f60\u770b\u8fd9\u6837\u884c \u4e0d\u884c\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\u5427\u3002\u201d \u3000\u3000\u4e94\u4eba\u5206\u6210\u4e24\u8def\uff0c\u4e00\u8def\u5f80\u91cc\u53bb\u3002 \u3000\u3000\u627e\u4e86\u4e24\u8fdb\u9662\u5b50\uff0c\u8fd8\u662f\u4e00\u4e2a\u4eba\u5f71\u4e5f\u6ca1\u6709\u78b0\u4e0a\u3002\u60dc\u7389\u4ece\u4e00\u95f4\u7a7a\u623f\u91cc\u627e\u51fa\u4e00\u76cf\u706f\u7b3c\u70b9\u4e0a\uff0c\u548c\u7a0b \u5c0f\u8776\u3001\u5434\u94c1\u5cf0\u9010\u4e2a\u623f\u95f4\u627e\u8fc7\u53bb\u3002 \u3000\u3000\u4ece\u623f\u95f4\u91cc\u7684\u60c5\u5f62\u770b\uff0c\u5e76\u4e0d\u662f\u6709\u5916\u654c\u5165\u4fb5\u7684\u6837\u5b50\uff0c\u56e0\u4e3a\u623f\u91cc\u4e0d\u4ec5\u4e1d\u6beb\u4e0d\u4e71\uff0c\u66f4\u6ca1\u6709\u534a\u70b9\u66fe \u6253\u6597\u8fc7\u7684\u75d5\u8ff9\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff0c\u201c\u603b\u6355\u5934\uff0c\u8fd9\u6837\u627e\u8981\u627e\u5230\u4ec0\u4e48\u65f6\u5019\uff0c\u4e0d\u5982\u6211\u4eec\u4e09\u4eba\u4e5f\u5206\u5f00\u641c\u5427\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u53ef\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u603b\u6355\u5934\u662f\u62c5\u5fc3\u59da\u5e9c\u91cc\u8fd8\u6709\u51e0\u624b\u57cb\u4f0f\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u773c\u4e2d\u95ea\u52a8\u7740\u660e\u4eae\u7684\u76ee\u5149\uff0c\u4ed4\u7ec6\u5730\u5411\u56db\u4e0b\u91cc\u770b\u7740\uff0c\u6162\u6162\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u5434\u94c1\u7b14\u9053\uff1a\u201c\u90a3\u603b\u6355\u5934\u8fd8\u62c5\u5fc3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u662f\u62c5\u5fc3\uff0c\u800c\u662f\u6ca1\u6709\u518d\u627e\u7684\u5fc5\u8981\u4e86\u3002\u201d \u3000\u3000\u5434\u94c1\u534e\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u60dc\u7389\u4e5f\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u662f\u4e0d\u662f\u5df2\u7ecf\u53d1\u73b0\u4e86\u4ec0\u4e48\u60c5\u51b5\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u4eec\u8bf4\u8bdd\u65f6\uff0c\u6b63\u7ad9\u5728\u59da\u5e9c\u5185\u5802\u7684\u4e00\u5904\u82b1\u5385\u91cc\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u8d70\u5230\u684c\u8fb9\u4e00\u5f20\u6905\u5b50\u4e0a\u5750\u4e0b\uff0c\u4e0d\u7d27\u4e0d\u6162\u5730\u9053\uff1a\u201c\u4f60\u4eec\u770b\uff0c\u5e9c\u91cc\u51e0\u4e4e\u6240\u6709\u7684\u623f\u95f4\u90fd\u6536 \u62fe\u7684\u5341\u5206\u5e72\u51c0\uff0c\u6240\u6709\u4e1c\u897f\u4e1d\u6beb\u4e0d\u4e71\uff0c\u8fd9\u5c31\u8bf4\u660e\uff0c\u59da\u5e9c\u7684\u4eba\u4e0d\u53ef\u80fd\u662f\u88ab\u68a6\u5e7b\u4e4b\u5200\u7b49\u6740\u624b\u96c6\u56e2\u7ed1 \u67b6\u4e86\uff0c\u5e9c\u91cc\u4e5f\u6ca1\u6709\u4efb\u4f55\u66fe\u7ecf\u6218\u6597\u8fc7\u7684\u75d5\u8ff9\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5979\u4f38\u51fa\u4e00\u6839\u7ea4\u79c0\u767d\u7699\u7684\u624b\u6307\u5728\u684c\u9762\u4e0a\u8f7b\u8f7b\u4e00\u62b9\uff0c\u4e3e\u8d77\u6307\u5934\uff0c\u5bf9\u5434\u94c1\u5cf0\u548c\u60dc\u7389\u9053\uff1a \u201c\u4f60\u4eec\u770b\uff0c\u684c\u4e0a\u7ea4\u5c18\u4e0d\u67d3\uff0c\u8bf4\u660e\u4eca\u5929\u8fd8\u6709\u4eba\u64e6\u62b9\u8fc7\uff0c\u4f60\u4eec\u518d\u770b\u5730\u4e0a\uff0c\u5f88\u663e\u7136\u4eca\u5929\u4e5f\u6709\u4eba\u626b\u8fc7\uff0c \u8fd9\u8bf4\u660e\u81f3\u5c11\u5728\u4eca\u5929\u4e0a\u5348\uff0c\u59da\u5e9c\u7684\u751f\u6d3b\u79e9\u5e8f\u8fd8\u662f\u5341\u5206\u6b63\u5e38\u7684\u3002\u201d \u3000\u3000\u5979\u987f\u4e86\u987f\uff0c\u4f38\u624b\u6361\u8d77\u684c\u4e0a\u7684\u4e00\u7b95\u8336\u58f6\uff0c\u62ff\u8d77\u58f6\u65c1\u7684\u4e00\u4e2a\u676f\u5b50\uff0c\u5012\u4e86\u534a\u676f\u8336\uff0c\u4ecd\u7136\u4e0d\u7d27\u4e0d \u6162\u5730\u9053\uff1a\u201c\u4f60\u4eec\u770b\uff0c\u8336\u8fd8\u662f\u6e29\u70ed\u7684\uff0c\u8bf4\u660e\u81f3\u591a\u534a\u4e2a\u65f6\u8fb0\u524d\uff0c\u8fd9\u623f\u95f4\u91cc\u8fd8\u6709\u4eba\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u603b\u6355\u5934\u7684\u610f\u601d\u662f\u8bf4\uff0c\u59da\u5e9c\u7684\u4eba\u662f\u2026\u2026\u662f\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u59da\u5e9c\u7684\u4eba\u662f\u81ea\u5df1\u64a4\u8d70\u7684\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8981\u64a4\u8d70\uff1f\u96be\u9053\u4ed6\u4eec\u65e9\u77e5\u9053\u603b\u6355\u5934\u8981\u6765\uff0c\u6240\u4ee5\u624d\u2026\u2026\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u80af\u5b9a\u662f\u8fd9\u6837\u4e86\uff0c\u4e0d\u4f1a\u518d\u6709\u522b\u7684\u53ef\u80fd\u4e86\u3002\u59da\u5e9c\u8fd9\u6837\u7684\u5bcc\u8c6a\u4e4b\u5bb6\uff0c\u4e0a\u4e0b\u4eba\u7b49\u7edd \u4e0d\u4f1a\u5c11\u4e8e\u56db\u4e94\u767e\u4eba\uff0c\u8fd9\u4e48\u591a\u7684\u4eba\u4e00\u8d77\u884c\u52a8\uff0c\u53ef\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\uff0c\u8981\u7ed1\u67b6\u4ed6\u4eec\uff0c\u53ea\u6015\u68a6\u5e7b\u4e4b \u5200\u6740\u624b\u96c6\u56e2\u4e5f\u6ca1\u6709\u8fd9\u4e2a\u5b9e\u529b\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u51fa\u4e86\u4e00\u4f1a\u513f\u795e\uff0c\u9053\uff1a\u201c\u662f\u7684\uff0c\u4ed6\u4eec\u53ea\u53ef\u80fd\u662f\u60f3\u907f\u5f00\u6211\u4eec\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff0c\u201c\u8fd9\u8bf4\u660e\u4ed6\u4eec\u5fc3\u91cc\u6709\u9b3c\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u83ab\u975e\u2026\u2026\u83ab\u975e\u59da\u987a\u5929\u4e5f\u662f\u68a6\u5e7b\u4e4b\u5200\u7684\u90e8\u4e0b\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6709\u8fd9\u4e2a\u53ef\u80fd\uff0c\u4e0d\u4ec5\u59da\u987a\u5929\uff0c\u79c1\u84c4\u5b9e\u529b\u7684\u626c\u5dde\u5341\u5927\u5bcc\u8c6a\uff0c\u6bcf\u4e00\u5bb6\u90fd\u6709\u53ef\u80fd\u4f1a \u662f\u68a6\u5e7b\u4e4b\u5200\u7684\u90e8\u4e0b\uff01\u201d \u3000\u3000\u6b63\u8bf4\u7740\uff0c\u8d3e\u82f1\u6025\u6025\u8d70\u4e86\u8fdb\u6765\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u5e9c\u91cc\u679c\u7136\u4e00\u4e2a\u4eba\u4e5f\u6ca1\u6709\uff0c\u800c\u4e14\u636e\u8d3e\u67d0\u63a8\u6d4b\uff0c \u4ed6\u4eec\u662f\u81ea\u5df1\u4e3b\u52a8\u64a4\u8d70\u7684\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u8fd9\u6837\u60f3\u3002\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5f53\u65f6\u65b0\u623f\u8840\u6848\u6848\u53d1\u4e4b\u540e\uff0c\u655d\u4e1c\u4e3b\u8054\u5408\u5341\u5927\u5bcc\u8c6a\u5408\u5175\u4e00\u5904\uff0c\u4ee5\u5bf9\u6297\u68a6\u5e7b \u4e4b\u5200\uff0c\u53ea\u6709\u59da\u987a\u5929\u516c\u7136\u8868\u793a\u4e0d\u540c\u610f\u5462\uff0c\u770b\u6765\uff0c\u4ed6\u4e0e\u68a6\u5e7b\u4e4b\u5200\u672c\u662f\u4e00\u5904\u9f3b\u5b54\u51fa\u6c14\u5440\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u8fd9\u6837\u4e00\u6765\uff0c\u68a6\u5e7b\u4e4b\u5200\u7684\u5b9e\u529b\u6bd4\u6211\u4eec\u9884\u6599\u7684\u66f4\u5f3a\u5f97\u591a\u4e86\uff0c\u603b\u6355\u5934\uff0c\u4f60\u770b\u6211\u4eec\u8be5 \u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u8fd9\u662f\u4e00\u4ef6\u597d\u4e8b\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u603b\u6355\u5934\u4f55\u51fa\u6b64\u8a00\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u654c\u4eba\u7684\u5b9e\u529b\u66b4\u9732\u7684\u8d8a\u65e9\u3001\u8d8a\u591a\uff0c\u5bf9\u6211\u4eec\u5c31\u8d8a\u6709\u5229\u5440\uff0c\u660e\u67aa\u597d\u8eb2\uff0c\u5591\u7bad\u96be\u9632 \u561b\uff01\u201d \u3000\u3000\u5979\u987f\u4e86\u4e00\u987f\uff0c\u5ffd\u7136\u60f3\u8d77\u4e00\u4ef6\u4e8b\uff0c\u4fbf\u5bf9\u60dc\u7389\u9053\uff1a\u201c\u60dc\u7389\uff0c\u54b1\u4eec\u53bb\u627e\u627e\u770b\u59da\u5e9c\u7684\u7528\u6bd2\u5c0f\u7ec4\u64a4 \u8d70\u65f6\u4f1a\u4e0d\u4f1a\u7559\u4e0b\u70b9\u4e1c\u897f\uff0c\u5f88\u53ef\u80fd\u4ed6\u4eec\u64a4\u5f97\u5306\u5fd9\uff0c\u4e00\u4e9b\u836f\u5fd8\u4e86\u5e26\u5462\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u597d\u7684\uff0c\u60dc\u7389\u966a\u603b\u6355\u5934\u4e00\u8d77\u53bb\u627e\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u5c5e\u4e0b\u4e5f\u53bb\u5427\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u7528\uff0c\u5434\u53f8\u4e3b\uff0c\u4f60\u5c31\u7559\u5728\u8fd9\u91cc\u966a\u7740\u8d3e\u603b\u7ba1\u5427\u3002\u201d \u3000\u3000\u5434\u94c1\u534e\u5bf9\u5979\u7684\u8bdd\u4e2d\u4e4b\u610f\u5fc3\u9886\u795e\u4f1a\u3002 \u3000\u3000\u660e\u91cc\u542c\u8d77\u6765\u5979\u662f\u8ba9\u5434\u94c1\u5cf0\u4e0d\u7528\u966a\u5979\u548c\u60dc\u7389\u4e00\u8d77\u53bb\u627e\u89e3\u836f\uff0c\u53ef\u5b9e\u9645\u4e0a\uff0c\u5979\u4e5f\u8868\u8fbe\u51fa\u4e86\u8ba9\u8d3e \u82f1\u5c31\u7b49\u5728\u8fd9\u4e2a\u5927\u5385\u91cc\uff0c\u4e0d\u6dee\u56db\u4e0b\u4e71\u8d70\u7684\u610f\u601d\u3002 \u3000\u3000\u5979\u7559\u4e0b\u5434\u94c1\u5cf0\uff0c\u6b63\u662f\u8981\u76d1\u89c6\u8d3e\u82f1\u3002 \u3000\u3000\u8d3e\u82f1\u5f53\u7136\u4e5f\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u600e\u4f1a\u542c\u4e0d\u51fa\u5979\u8bdd\u4e2d\u6709\u8bdd\uff0c\u53ea\u662f\u4ed6\u7d20\u6765\u5fc3\u673a\u6df1\u6c89\uff0c\u6240\u4ee5\u8868\u60c5\u4e00\u70b9 \u4e5f\u6ca1\u6709\u6539\u53d8\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5bf9\u60dc\u7389\u9053\uff1a\u201c\u60dc\u7389\uff0c\u6211\u4eec\u8d70\u5427\u3002\u201d\u6b63\u8981\u51fa\u95e8\uff0c\u5929\u67ab\u9053\u957f\u795e\u8272\u6fc0\u52a8\u5730\u8dd1\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u624b\u4e2d\u62ce\u7740\u4e00\u4e2a\u84dd\u5e03\u5c0f\u5305\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5929\u67ab\u9053\u957f\uff0c\u4f60\u53d1\u73b0\u4e86\u4ec0\u4e48\u4e1c\u897f\u4e86\u5417\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u6211\u627e\u5230\u4e86\u4e00\u5904\u79d8\u5ba4\uff0c\u91cc\u9762\u6709\u4e0d\u5c11\u836f\uff0c\u6211\u5bf9\u836f\u7269\u4e00\u5411\u4e0d\u592a\u5728\u884c\uff0c\u6240\u4ee5\u5c31\u5168 \u62ff\u6765\u4e86\uff0c\u4e5f\u4e0d\u77e5\u9053\u6709\u6ca1\u6709\u7528\uff1f\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u5c06\u5305\u88b1\u653e\u5728\u684c\u4e0a\uff0c\u6253\u5f00\u3002 \u3000\u3000\u5305\u91cc\u679c\u7136\u6709\u51e0\u5341\u4e2a\u5c0f\u74f6\u5b50\uff0c\u5c0f\u76d2\u5b50\uff0c\u91cc\u9762\u88c5\u7740\u5404\u79cd\u836f\u4e38\u3001\u836f\u7c89\u3001\u836f\u818f\u3002 \u3000\u3000\u60dc\u7389\u8d70\u8fc7\u53bb\uff0c\u4e00\u6837\u6837\u770b\u7740\u3002 \u3000\u3000\u5979\u770b\u7684\u975e\u5e38\u4ed4\u7ec6\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u5f88\u7d27\u5f20\u5730\u9053\uff1a\u201c\u60dc\u7389\u59d1\u5a18\uff0c\u8fd9\u91cc\u9762\u6709\u89e3\u836f\u5417\uff1f\u201d \u3000\u3000\u60dc\u7389\u62ff\u8d77\u4e00\u4e2a\u767d\u7389\u5c0f\u76d2\uff0c\u5c0f\u5fc3\u5730\u5f00\u4e86\u95fb\uff0c\u53c8\u7528\u6307\u7532\u6311\u51fa\u4e00\u70b9\u513f\u836f\u7c89\uff0c\u4ed4\u7ec6\u770b\u4e86\u770b\uff0c\u9053\uff1a \u201c\u6709\uff0c\u8fd9\u5c31\u662f\u89e3\u836f\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9ad8\u5174\u5730\u9053\uff1a\u201c\u597d\u4e86\uff0c\u5c0f\u6587\u6709\u6551\u4e86\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u8fd9\u4e5f\u8bf4\u660e\uff0c\u59da\u987a\u5929\u679c\u7136\u662f\u4e0e\u68a6\u5e7b\u4e4b\u5200\u4e00\u4f19\u7684\uff0c\u59da\u987a\u5929\u5728\u626c\u5dde\u5df2\u7ecf\u5446\u4e86\u5341\u597d \u51e0\u5e74\u4e86\u5427\uff0c\u8fd9\u8bf4\u660e\uff0c\u68a6\u5e7b\u4e4b\u5200\u65e9\u5df2\u5f00\u59cb\u5728\u626c\u5dde\u57f9\u690d\u81ea\u5df1\u7684\u52bf\u529b\u4e86\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u68a6\u5e7b\u4e4b\u5200\u679c\u7136\u5389\u5bb3\uff0c\u626c\u5dde\u662f\u5929\u4e0b\u8d22\u5bcc\u805a\u96c6\u4e4b\u5730\xb7\u4ed6\u80fd\u60f3\u5230\u5728\u626c\u5dde\u57f9\u690d\u4eba\u529b\uff0c \u8bf4\u660e\u4ed6\u7684\u91ce\u5fc3\u662f\u5f88\u5927\u7684\u3002\u201d \u3000\u3000\u5979\u770b\u4e86\u6bcf\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u597d\u4e86\uff0c\u89e3\u836f\u4e5f\u627e\u5230\u4e86\uff0c\u53c8\u5f04\u6e05\u4e86\u59da\u987a\u5929\u662f\u68a6\u5e7b\u4e4b\u5200\u7684\u90e8\u4e0b\uff0c\u4eca \u5929\u8fd8\u662f\u5f88\u6709\u6536\u83b7\u7684\uff0c\u54b1\u4eec\u56de\u5e9c\u8859\u53bb\u5427\u3002\u201d \u3000\u3000\u56de\u5230\u5e9c\u8859\uff0c\u8d3e\u82f1\u548c\u5929\u67ab\u9053\u957f\u4fbf\u544a\u8f9e\u56de\u91d1\u5e9c\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u4e8c\u4eba\u8d70\u4e86\u4e4b\u540e\uff0c\u60dc\u7389\u4fbf\u5bf9\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u8fd9\u89e3\u836f\u6ca1\u7528\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u548c\u4e09\u5927\u6355\u5934\u90fd\u662f\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u600e\u4e48\u6ca1\u7528\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5374\u662f\u4e1d\u6beb\u4e5f\u4e0d\u610f\u5916\uff0c\u9053\uff1a\u201c\u6211\u60f3\u4e5f\u6ca1\u7528\uff0c\u65e2\u7136\u4ed6\u4eec\u64a4\u8d70\u65f6\uff0c\u4e00\u70b9\u4e5f\u4e0d\u614c\u4e71\uff0c\u50cf\u89e3 \u836f\u8fd9\u6837\u91cd\u8981\u7684\u4e1c\u897f\u5f53\u7136\u4e0d\u53ef\u80fd\u7559\u4e0b\u6765\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u53ef\u60dc\u7389\u4e0d\u662f\u8bf4\u8fd9\u5c31\u662f\u89e3\u836f\u5417\uff1f\u201d \u3000\u3000\u60dc\u7389\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u7684\u786e\u662f\u89e3\u836f\uff0c\u53ea\u662f\u8fd9\u79cd\u836f\u60dc\u7389\u4e5f\u80fd\u914d\u51fa\u6765\uff0c\u4e5f\u5c31\u662f\u8bf4\u8fd9\u91cc\u9762\u5dee\u4e00\u5473\u836f \u5f15\u5b50\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u8fd9\u89e3\u836f\u5e76\u4e0d\u662f\u7528\u4eba\u5408\u6210\u7684\uff0c\u6240\u4ee5\u5c0f\u6587\u670d\u4e0b\u8fd9\u836f\u540e\uff0c\u8fd8\u662f\u8981\u4e0e\u738b \u5927\u4eba\u2026\u2026\u8fd9\u4e2a\u2026\u2026\u624d\u80fd\u5f7b\u5e95\u6839\u6cbb\u5979\u4f53\u5185\u4e4b\u6bd2\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u7b11\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u4f60\u4eec\u53bb\u59da\u5e9c\u7684\u8fc7\u7a0b\u4e2d\uff0c\u6211\u5df2\u7ecf\u79c1\u4e0b\u548c\u738b\u5927\u4eba\u8c08\u8fc7\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u738b\u5927\u4eba\u7684\u6001\u5ea6\u5982\u4f55\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4ed6\u5634\u4e0a\u5f53\u7136\u662f\u6ca1\u6709\u540c\u610f\uff0c\u4f46\u4f9d\u6211\u770b\uff0c\u5fc3\u601d\u662f\u6709\u4e9b\u52a8\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u7684\u5462\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u56e0\u4e3a\u738b\u5927\u4eba\u53ea\u8bf4\uff0c\u6015\u59d4\u5c48\u4e86\u5c0f\u6587\u59d1\u5a18\uff0c\u66f4\u6015\u8bef\u4e86\u5979\u7684\u7ec8\u8eab\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u7b11\u9053\uff1a\u201c\u597d\uff01\u516b\u6210\u4e86\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u4e5f\u7b11\u9053\uff1a\u201c\u8001\u5c91\u5c31\u8bf4\u561b\uff0c\u5f88\u96be\u6709\u4eba\u80fd\u4e0d\u88ab\u5c0f\u6587\u59d1\u5a18\u7684\u7f8e\u8272\u6240\u7740\u8ff7\u7684\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\u4e86\uff0c\u597d\u4e86\uff0c\u4e0d\u8c08\u8fd9\u4e2a\u4e86\uff0c\u53cd\u6b63\u5c0f\u6587\u90a3\u8fb9\uff0c\u6211\u548c\u60dc\u7389\u53bb\u529d\u5979\uff0c\u5e94\u8be5\u6ca1\u6709\u95ee \u9898\u3002\u6211\u4eec\u8fd8\u662f\u8c08\u4e00\u8c08\u6b63\u4e8b\u5427\u3002\u201d \u3000\u3000\u6b63\u4e8b\u5f53\u7136\u5c31\u662f\u5f53\u524d\u7684\u5f62\u52bf\u3002 \u3000\u3000\u542c\u5b8c\u7a0b\u5c0f\u8776\u5bf9\u59da\u5e9c\u4e4b\u884c\u60c5\u51b5\u7684\u4ecb\u7ecd\uff0c\u675c\u671b\u6708\u9996\u5148\u9053\uff1a\u201c\u770b\u6765\uff0c\u6740\u624b\u96c6\u56e2\u5f88\u5feb\u5c31\u4f1a\u6709\u65b0\u7684 \u52a8\u4f5c\u4e86\uff0c\u6211\u4eec\u8981\u505a\u597d\u8fce\u63a5\u4ed6\u4eec\u65b0\u4e00\u8f6e\u66f4\u51f6\u731b\u7684\u8fdb\u653b\u7684\u51c6\u5907\u5440\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\uff0c\u60c5\u51b5\u9010\u6e10\u5f00\u59cb\u660e\u6717\u4e86\uff0c\u6740\u624b\u96c6\u56e2\u4e5f\u5f00\u59cb\u4ece\u6697\u5904\u6e10\u6e10\u8d70\u5411\u660e\u5904\u6765\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u654c\u6211\u53cc\u65b9\u65e2\u7136\u90fd\u5df2\u8d70\u5411\u660e\u5904\uff0c\u5269\u4e0b\u7684\u4e8b\u5c31\u8981\u770b\u5b9e\u529b\u624d\u80fd\u89e3\u51b3\u3002\u53ef\u662f\uff0c\u603b\u6355 \u5934\uff0c\u6211\u65b9\u7684\u5b9e\u529b\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u675c\u53f8\u4e3b\uff0c\u4f60\u653e\u5fc3\uff0c\u636e\u6211\u6240\u77e5\uff0c\u5211\u90e8\u4e5f\u5df2\u6d3e\u51fa\u7edd\u4e16\u9ad8\u624b\u6765\u4e3a\u6211\u4eec\u52a9\u9635\u4e86\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u90a3\u5c31\u592a\u597d\u4e86\uff01\u603b\u6355\u5934\uff0c\u80fd\u544a\u8bc9\u6211\u4eec\u6d3e\u6765\u7684\u662f\u4ec0\u4e48\u4eba\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u662f\u5211\u90e8\u5c5e\u4e0b\u7684\u4e00\u652f\u5947\u5175\uff0c\u5230\u65f6\u5019\u5927\u5bb6\u5c31\u660e\u767d\u4e86\u3002\u675c\u53f8\u4e3b\uff0c\u4ece\u4eca\u665a\u5f00\u59cb\uff0c \u4f60\u5c31\u5f00\u8def\u6f5c\u5fc3\u4e60\u7ec3\u2018\u5251\u6d77\u6d74\u9b42\u2019\uff0c\u6211\u73b0\u5728\u5c31\u5c06\u5251\u8c31\u4ea4\u7ed9\u4f60\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u5f88\u9ad8\u5174\uff0c\u53c8\u4e0d\u89c9\u6709\u4e9b\u7d27\u5f20\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u675c\u53f8\u4e3b\u4e0d\u7528\u7740\u6025\uff0c\u4e5f\u4e0d\u7528\u62c5\u5fc3\uff0c\u6211\u548c\u60dc\u7389\u4f1a\u7528\u672c\u8eab\u5185\u529f\u5e2e\u52a9\u4f60\u7684\uff0c\u4f46\u73b0\u5728\uff0c \u6700\u5148\u8981\u505a\u7684\uff0c\u662f\u6cbb\u597d\u5c0f\u6587\uff0c\u56e0\u4e3a\u5c0f\u6587\u6bd5\u7adf\u5df2\u7ecf\u8fbe\u5230\u4e86\u2018\u5251\u6d77\u6d74\u9b42\u2019\u4e5d\u6210\u7684\u706b\u5019\uff0c\u662f\u4e00\u652f\u751f\u529b \u519b\uff0c\u518d\u8bf4\uff0c\u6cbb\u597d\u4e86\u5979\uff0c\u5979\u4e5f\u53ef\u4ee5\u5c06\u81ea\u5df1\u4e60\u7ec3\u5251\u6cd5\u7684\u5fc3\u5f97\u544a\u8bc9\u675c\u53f8\u4e3b\uff0c\u4f60\u7ec3\u8d77\u6765\u5371\u9669\u5c31\u5c0f\u5f97\u591a\uff0c \u6210\u529f\u5f97\u4e5f\u5c31\u66f4\u5feb\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u597d\uff0c\u5434\u5144\u3001\u5c91\u5144\u3001\u4e8e\u5144\uff0c\u54b1\u4eec\u8fd9\u5c31\u53bb\u627e\u738b\u5927\u4eba\u3002\u603b\u6355\u5934\uff0c\u4f60\u548c\u60dc\u7389\u5feb\u53bb\u627e \u5c0f\u6587\uff0c\u5148\u8ba9\u5979\u5c06\u89e3\u836f\u670d\u4e0b\uff0c\u9020\u6210\u5f00\u5f13\u6ca1\u6709\u56de\u5934\u7bad\u4e4b\u52bf\uff0c\u5219\u738b\u5927\u4eba\u548c\u5c0f\u6587\u4e8c\u4eba\u5c31\u90fd\u6ca1\u4ec0\u4e48\u53ef\u8bf4 \u7684\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\u5427\u3002\u201d \u3000\u3000\u60dc\u7389\u4f3c\u55d4\u975e\u55d4\u5730\u770b\u4e86\u675c\u671b\u6708\u4e00\u773c\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u5c31\u4f60\u9b3c\u5fc3\u773c\u513f\u591a\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u4e00\u7b11\uff0c\u548c\u4e09\u5927\u6355\u5934\u542f\u7a0b\u627e\u738b\u5c11\u537f\u53bb\u4e86\u3002 \u3000\u3000\u738b\u5c11\u537f\u8138\u6da8\u5f97\u901a\u7ea2\uff0c\u5c31\u662f\u4e0d\u540c\u610f\u3002 \u3000\u3000\u675c\u671b\u6708\u56db\u4eba\u786c\u5c06\u4ed6\u62d6\u5230\u5c0f\u6587\u623f\u95f4\u5916\u9762\uff0c\u4e0d\u8bf4\u4ec0\u4e48\uff0c\u738b\u5c11\u537f\u4e5f\u4e0d\u8fdb\u53bb\u3002 \u3000\u3000\u5211\u623f\u5e08\u7237\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u5927\u4eba\uff0c\u4f60\u8fd8\u662f\u540c\u610f\u4e86\u5427\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u8bef\u4e86\u5c0f\u6587\u59d1\u5a18\u7684\u7ec8\u8eab\u554a\uff01\u201d \u3000\u3000\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u5927\u4eba\uff0c\u5c0f\u6587\u59d1\u5a18\u5df2\u670d\u4e0b\u4e86\u89e3\u836f\uff0c\u5927\u4eba\u5982\u679c\u4e0d\u540c\u610f\uff0c\u5c31\u662f\u5bb3\u6b7b\u4e86\u5979\u4e86\u3002\u518d\u8bf4\uff0c \u8fd9\u4ef6\u4e8b\u5df2\u7ecf\u4e0d\u662f\u5927\u4eba\u4e00\u4e2a\u4eba\u7684\u79c1\u4e8b\u4e86\uff0c\u4e5f\u4e8b\u5173\u7834\u6848\u7f09\u51f6\u554a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u548c\u60dc\u7389\u4ece\u5c0f\u6587\u623f\u95f4\u91cc\u8d70\u4e86\u51fa\u6765\uff0c\u4e00\u529b\u76f8\u529d\u3002 \u3000\u3000\u738b\u5c11\u537f\u8fd8\u662f\u4e0d\u540c\u610f\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u5c0f\u6587\u7684\u58f0\u97f3\u5728\u623f\u95f4\u5bf9\u5916\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u4f60\u662f\u56fd\u5bb6\u680b\u589a\u4e4b\u6750\uff0c\u5c0f\u6587\u5f97\u6258\u7ec8\u8eab\uff0c\u4e5f\u7b97 \u662f\u5e78\u8fd0\u4e4b\u4e8b\uff0c\u6211\u4e00\u4e2a\u5973\u5b69\u5b50\u5bb6\u90fd\u5148\u5f00\u4e86\u53e3\u4e86\uff0c\u738b\u5927\u4eba\u53c8\u6709\u4ec0\u4e48\u597d\u72b9\u8c6b\u5462\uff1f\u518d\u8bf4\uff0c\u675c\u53f8\u4e3b\u4e3a\u4e86 \u7f09\u51f6\u4e4b\u4e8b\uff0c\u4e5f\u51c6\u5907\u4e60\u7ec3\u2018\u5251\u6d77\u6d74\u9b42\u2019\uff0c\u5982\u679c\u6ca1\u6709\u5c0f\u6587\u7684\u6307\u70b9\uff0c\u4ed6\u5f88\u53ef\u80fd\u4f1a\u7ec3\u51fa\u504f\u5dee\uff0c\u5219\u738b\u5927 \u4eba\u5c31\u662f\u5bb3\u4e86\u4e24\u4e2a\u4eba\u7684\u6027\u547d\u4e86\u3002\u51b5\u4e14\uff0c\u5c0f\u6587\u6b64\u8eab\u4e00\u65e6\u6258\u4ed8\u7ed9\u738b\u5927\u4eba\uff0c\u4e5f\u80fd\u66f4\u597d\u5730\u4fdd\u62a4\u5927\u4eba\u554a\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u6123\u5728\u5f53\u573a\u3002 \u3000\u3000\u5c0f\u6587\u7adf\u7136\u4e3b\u52a8\u5f00\u53e3\u4e86\uff0c\u5b9e\u5728\u662f\u4ed6\u6240\u6ca1\u6709\u6599\u60f3\u5230\u7684\u3002 \u3000\u3000\u60dc\u7389\u8d70\u5230\u4ed6\u8eab\u8fb9\uff0c\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u4f60\u4e0d\u662f\u4e5f\u8bf4\u8fc7\u4f60\u5f88\u559c\u6b22\u6211\u5c0f\u6587\u59d0\u5417\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u6025\u9053\uff1a\u201c\u662f\u8bf4\u8fc7\uff0c\u53ef\u662f\u2026\u2026\u53ef\u662f\u6211\u53ef\u4e0d\u662f\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u60dc\u7389\u7b11\u9053\uff0c\u201c\u90ce\u6709\u60c5\u59be\u6709\u610f\uff0c\u4e0d\u5c31\u884c\u4e86\uff01\u201d\u4f38\u624b\u5728\u738b\u5c11\u537f\u540e\u80cc\u8f7b\u8f7b\u4e00\u63a8\uff0c\u5df2\u5c06\u4ed6\u63a8\u8fdb\u4e86 \u5c0f\u6587\u7684\u623f\u95f4\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u53cc\u7ea4\u7ea4\u7389\u624b\u4f38\u51fa\u6765\uff0c\u5c06\u623f\u95e8\u649e\u4e0a\u4e86\uff0c\u7d27\u63a5\u7740\uff0c\u623f\u95f4\u91cc\u7684\u706f\u706b\u88ab\u5439\u706d\u4e86\u3002 \u3000\u3000\u5f20\u5b9d\u5584\u5927\u58f0\u9053\uff1a\u201c\u606d\u559c\u5927\u4eba\u548c\u5c0f\u6587\u59d1\u5a18\uff0c\u6211\u4eec\u8981\u53bb\u4f11\u606f\u4e86\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u7b11\u9053\uff1a\u201c\u4eca\u665a\u6211\u4eec\u5c31\u4e0d\u95f9\u65b0\u623f\u4e86\uff0c\u660e\u5929\u4e8c\u4f4d\u53ef\u5f97\u8865\u8bf7\u559c\u9152\u554a\uff01\u201d \u3000\u3000\u60dc\u7389\u6084\u6084\u4e00\u62c9\u675c\u671b\u6708\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u6765\uff01\u201d \u3000\u3000\u5979\u867d\u7136\u6ca1\u6709\u8bf4\u8981\u53bb\u54ea\u91cc\uff0c\u53ef\u662f\u675c\u671b\u6708\u53c8\u5c82\u80fd\u4e0d\u660e\u767d\uff0c\u60dc\u7389\u662f\u8981\u5c65\u884c\u5979\u8bb8\u4e0b\u7684\u8bfa\u8a00\u4e86\u3002 \u3000\u3000\u5f88\u5feb\uff0c\u540e\u9662\u91cc\u5f7b\u5e95\u5b89\u9759\u4e86\u4e0b\u6765\uff0c\u706f\u706b\u4e5f\u4e00\u70b9\u63a5\u4e00\u70b9\u5730\u7184\u706d\u4e86\u3002 \u3000\u3000\u591a\u5c11\u6e29\u67d4\u76ee\u5149\uff0c\u90fd\u9690\u5728\u4e86\u8fd9\u6c89\u6c89\u7684\u9ed1\u6697\u4e4b\u4e2d\uff01 \u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1 \u3000\u3000\u8d3e\u82f1\u548c\u5929\u67ab\u9053\u957f\u56de\u5230\u91d1\u5e9c\uff0c\u53d1\u73b0\u91d1\u767e\u5e74\u6b63\u6709\u4e9b\u7740\u6025\u5730\u5728\u7b49\u7740\u4ed6\u4eec\u3002 \u3000\u3000\u770b\u89c1\u4ed6\u4eec\u4e8c\u4eba\uff0c\u91d1\u767e\u5e74\u4fbf\u95ee\uff1a\u201c\u60c5\u51b5\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u59da\u5e9c\u4e0a\u4e0b\uff0c\u4e00\u4e2a\u4eba\u5f71\u4e5f\u6ca1\u6709\uff0c\u90fd\u4e0d\u77e5\u53bb\u54ea\u91cc\u4e86\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u5f88\u662f\u5403\u60ca\u5730\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u59da\u5e9c\u91cc\u4e00\u4e2a\u4eba\u90fd\u6ca1\u6709\u4e86\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u662f\u7684\uff0c\u770b\u5e9c\u91cc\u7684\u6837\u5b50\uff0c\u4ed6\u4eec\u662f\u81ea\u5df1\u5f88\u6709\u79e9\u5e8f\u5730\u64a4\u51fa\u53bb\u7684\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u6c89\u541f\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u8f6c\u53d8\u4e86\u8bdd\u9898\uff0c\u5bf9\u8d3e\u82f1\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\uff0c\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97 \u4e0a\u6b21\u4fb5\u5165\u5230\u89c2\u9e64\u697c\u4e0b\uff0c\u88ab\u4f60\u51fb\u8d25\u7684\u90a3\u4e2a\u94f6\u8863\u4eba\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u8bb0\u5f97\u3002\u6211\u4e00\u76f4\u60f3\u4ece\u4ed6\u53e3\u4e2d\u95ee\u51fa\u4e00\u4e9b\u60c5\u51b5\u6765\uff0c\u53ef\u662f\u56e0\u4e3a\u5e9c\u4e2d\u7684\u9632\u536b\u4e4b\u4e8b\uff0c\u4e00\u76f4 \u6ca1\u80fd\u62bd\u51fa\u65f6\u95f4\u53bb\u95ee\u4ed6\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u8ba9\u5bd2\u5c71\u56db\u5200\u903c\u95ee\u8fc7\u4e86\u3002\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u4e1c\u4e3b\uff0c\u4ed6\u4eec\u95ee\u51fa\u4ec0\u4e48\u53e3\u4f9b\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u5bd2\u5c71\u56db\u5200\u5c06\u4ed6\u4eec\u80fd\u60f3\u5230\u7684\u903c\u4f9b\u7684\u624b\u6bb5\u90fd\u7528\u4e0a\u4e86\uff0c\u53ef\u90a3\u4e2a\u4eba\u5c31\u662f \u4e0d\u5f00\u53e3\u3002\u201d \u3000\u3000\u8d3e\u82f1\u6de1\u6de1\u9053\uff1a\u201c\u4e1c\u4e3b\uff0c\u903c\u4f9b\u53ef\u662f\u4e00\u95e8\u5927\u5b66\u95ee\uff0c\u5bd2\u5c71\u56db\u5200\u663e\u7136\u5e76\u6ca1\u6709\u638c\u63e1\u5176\u4e2d\u7684\u8bc0\u7a8d\uff0c\u95ee \u4e0d\u51fa\u53e3\u4f9b\uff0c\u662f\u5f88\u6b63\u5e38\u7684\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\u77e5\u9053\u903c\u4f9b\u7684\u8bc0\u7a8d\u5417\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u6211\u81f3\u5c11\u77e5\u9053\u4e03\u5341\u79cd\u8ba9\u6700\u575a\u5f3a\u7684\u4eba\u4e0d\u5f97\u4e0d\u5f00\u53e3\u7684\u529e\u6cd5\uff0c\u6211\u770b\uff0c\u5728\u90a3\u4e2a\u94f6\u8863\u4eba\u8eab \u4e0a\uff0c\u6700\u591a\u53ea\u8981\u7528\u4e0a\u4e2a\u4e09\u4e24\u79cd\uff0c\u4ed6\u5c31\u4f1a\u8001\u8001\u5b9e\u5b9e\u5730\u5f00\u53e3\u4e86\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u6325\u4e86\u6325\u624b\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u6709\u52b3\u8d3e\u603b\u7ba1\u4e86\u3002\u5929\u67ab\u9053\u957f\uff0c\u4f60\u4e5f\u53bb\u5427\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u5176\u5b9e\u8001\u9053\u4e5f\u61c2\u5f97\u51e0\u79cd\u8ba9\u4eba\u5f00\u53e3\u7684\u529e\u6cd5\uff0c\u4f46\u65e2\u7136\u8d3e\u603b\u7ba1\u662f\u4f4d\u5927\u9ad8\u624b\uff0c\u6211\u4e5f \u5c31\u4e0d\u5728\u5173\u516c\u9762\u524d\u800d\u5927\u5200\u4e86\u5427\u3002\u201d \u3000\u3000\u8d3e\u82f1\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u9053\u957f\u592a\u5ba2\u6c14\u4e86\uff0c\u8fd8\u662f\u4e00\u8d77\u53bb\u5427\uff0c\u4fdd\u4e0d\u6dee\u8fd9\u94f6\u8863\u4eba\u662f\u4e2a\u7279\u522b\u786c\u7684\u81ed\u77f3 \u5934\uff0c\u8d3e\u67d0\u7684\u529e\u6cd5\u5168\u90fd\u4e0d\u7ba1\u7528\u5462\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\u8bf4\u7684\u6709\u9053\u7406\uff0c\u4f60\u4eec\u4e00\u8d77\u53bb\u5427\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u5bf9\u91d1\u767e\u5e74\u9053\uff1a\u201c\u8981\u4e0d\uff0c\u4f60\u4e5f\u4e00\u8d77\u53bb\u770b\u770b\u6211\u4eec\u5ba1\u95ee\u7684\u60c5\u5f62\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u6c89\u541f\u7740\uff0c\u4e00\u65f6\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u8d3e\u82f1\u5ffd\u7136\u9053\uff1a\u201c\u8fd9\u51e0\u5929\u6765\uff0c\u5927\u5c0f\u59d0\u7684\u60c5\u51b5\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5c0f\u7709\u8fd9\u5b69\u5b50\uff0c\u60b2\u4f24\u8fc7\u5ea6\u554a\uff01\u5230\u4e86\u4eca\u5929\uff0c\u4e5f\u8fd8\u662f\u8336\u996d\u4e0d\u601d\uff0c\u5a62\u5973 \u4eec\u4e00\u518d\u76f8\u529d\uff0c\u4e5f\u53ea\u662f\u5c11\u8bb8\u8fdb\u98df\uff0c\u4eba\u90fd\u7626\u5f97\u4e0d\u6210\u6837\u5b50\u4e86\uff01\u201d \u3000\u3000\u4ed6\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u5bf9\u8d3e\u82f1\u548c\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4f60\u4eec\u53bb\u5427\uff0c\u6211\u8fd8\u53bb\u529d\u529d\u5c0f\u7709\u8fd9\u5b69\u5b50\uff0c\u7167\u8fd9\u6837\u4e0b \u53bb\uff0c\u4f24\u4e86\u8eab\u5b50\uff0c\u53ef\u600e\u4e48\u529e\uff01\u8001\u592b\u53ef\u5c31\u53ea\u6709\u5979\u4e00\u4e2a\u5973\u513f\u554a\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u7ad9\u8d77\u8eab\uff0c\u4e00\u8fb9\u53f9\u6c14\uff0c\u4e00\u8fb9\u5411\u5185\u9662\u8d70\u53bb\u3002 \u3000\u3000\u8d3e\u82f1\u548c\u5929\u67ab\u9053\u957f\u8d70\u51fa\u5927\u5385\uff0c\u6765\u5230\u5e9c\u5185\u7684\u7262\u623f\u3002 \u3000\u3000\u91d1\u5e9c\u7684\u7262\u623f\u5176\u5b9e\u662f\u4e00\u5ea7\u7528\u5de8\u5927\u7684\u82b1\u5c97\u77f3\u780c\u6210\u7684\u5730\u4e0b\u5ba4\uff0c\u672c\u8eab\u5c31\u5341\u5206\u575a\u56fa\uff0c\u518d\u52a0\u4e0a\u5e74\u5916\u8fd8 \u6709\u5927\u6279\u536b\u58eb\u628a\u5b88\uff0c\u6240\u4ee5\u4eba\u53ea\u8981\u4e00\u5165\u7262\u623f\uff0c\u90a3\u5c31\u771f\u53ef\u8c13\u662f\u63d2\u7fc5\u4e5f\u96be\u98de\u8d70\u4e86\uff01 \u3000\u3000\u5b88\u7262\u7684\u536b\u58eb\u4eec\u4e00\u89c1\u8d3e\u82f1\uff0c\u4fbf\u4e00\u8d77\u8eac\u8eab\u9053\uff1a\u201c\u53c2\u89c1\u603b\u7ba1\uff01\u201d \u3000\u3000\u8d3e\u82f1\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u4e0d\u7528\u591a\u793c\uff0c\u90a3\u4e2a\u94f6\u8863\u4eba\u7684\u60c5\u51b5\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u536b\u58eb\u9053\uff1a\u201c\u4e0d\u8a00\u4e0d\u8bed\uff0c\u53ea\u4e0d\u8fc7\u996d\u7167\u5403\uff0c\u6c34\u7167\u559d\uff0c\u8fd8\u771f\u77e5\u9053\u4e0d\u4e8f\u5f85\u81ea\u5df1\u3002\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u536b\u58eb\u9053\uff1a\u201c\u6628\u5929\u4ed6\u8fd8\u5927\u53eb\u7740\u8981\u9152\u559d\uff0c\u8ba9\u5f1f\u5144\u4eec\u4e00\u987f\u597d\u9a82\uff0c\u624d\u4e0d\u53eb\u4e86\u3002\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u4ee5\u540e\uff0c\u4ed6\u8981\u9152\u559d\u4f60\u4eec\u5c31\u7ed9\u4ed6\uff0c\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u536b\u58eb\u9053\uff1a\u201c\u662f\uff0c\u5c5e\u4e0b\u9075\u547d\u3002\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u6253\u5f00\u7262\u95e8\uff0c\u6211\u548c\u5929\u67ab\u9053\u957f\u8981\u8fdb\u53bb\u95ee\u4ed6\u51e0\u53e5\u8bdd\u3002\u201d \u3000\u3000\u536b\u58eb\u6253\u5f00\u95e8\u4e0a\u7684\u5927\u94c1\u9501\uff0c\u63a8\u5f00\u6c89\u91cd\u7684\u94c1\u95e8\uff0c\u4f46\u89c9\u5e74\u5185\u4e00\u80a1\u9634\u68ee\u68ee\u7684\u6f6e\u6c14\u6251\u9762\u800c\u51fa\u3002 \u3000\u3000\u8d3e\u82f1\u8ba9\u4e94\u540d\u536b\u58eb\u70b9\u4e0a\u4e00\u76cf\u706f\u7b3c\uff0c\u548c\u5929\u67ab\u9053\u957f\u4e00\u524d\u4e00\u540e\u8d70\u8fdb\u4e86\u7262\u95e8\u3002 \u3000\u3000\u7262\u95e8\u91cc\u4fbf\u662f\u53c8\u9661\u53c8\u7a84\u7684\u51e0\u5341\u7ea7\u53f0\u9636\uff0c\u4e0b\u5230\u5730\u4e0b\u5ba4\u91cc\uff0c\u773c\u524d\u662f\u4e00\u6761\u77f3\u780c\u7684\u901a\u9053\uff0c\u901a\u9053\u4e24\u8fb9\uff0c \u5168\u90fd\u662f\u4e00\u95f4\u7d27\u6328\u7740\u4e00\u95f4\u7684\u5355\u4eba\u7262\u623f\u3002 \u3000\u3000\u94f6\u8863\u4eba\u88ab\u5173\u5728\u6700\u91cc\u9762\u7684\u90a3\u4e00\u95f4\u7262\u623f\u91cc\u3002 \u3000\u3000\u5730\u4e0b\u5ba4\u91cc\u9664\u4e86\u6570\u5341\u95f4\u7262\u623f\u5916\uff0c\u8fd8\u6709\u4e00\u95f4\u5f88\u5927\u7684\u623f\u95f4\uff0c\u623f\u95f4\u91cc\u653e\u6ee1\u4e86\u5404\u79cd\u5211\u5177\uff0c\u6b63\u4e2d\u95f4\u662f \u4e00\u5f20\u5927\u684c\u5b50\uff0c\u684c\u5b50\u540e\u9762\u548c\u4e24\u8fb9\uff0c\u5404\u6709\u4e00\u628a\u6905\u5b50\u3002 \u3000\u3000\u8d3e\u82f1\u548c\u5929\u67ab\u9053\u957f\u6765\u5230\u8fd9\u95f4\u884c\u5211\u5ba4\uff0c\u8d3e\u82f1\u5728\u684c\u5b50\u540e\u7684\u6905\u5b50\u4e0a\u5750\u4e0b\uff0c\u8ba9\u5929\u67ab\u9053\u957f\u5750\u5728\u684c\u8fb9\u7684 \u6905\u5b50\u4e0a\uff0c\u7acb\u523b\u5c31\u6709\u536b\u58eb\u66ff\u4ed6\u4eec\u9012\u6765\u4e86\u521a\u6ce1\u597d\u7684\u70ed\u8336\u3002 \u3000\u3000\u8d3e\u82f1\u559d\u4e86\u4e00\u53e3\u8336\uff0c\u8fd9\u624d\u9053\uff1a\u201c\u6765\u4eba\uff0c\u53bb\u628a\u90a3\u4e2a\u94f6\u8863\u4eba\u5e26\u5230\u8fd9\u91cc\u6765\uff01\u201d \u3000\u3000\u536b\u58eb\u4eec\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4e0d\u4e00\u4f1a\u513f\uff0c\u4e24\u540d\u536b\u58eb\u4fbf\u62b1\u7740\u6ee1\u8eab\u4f24\u75d5\u7684\u94f6\u8863\u4eba\u6765\u5230\u884c\u5211\u5ba4\uff0c\u91cd\u91cd\u5730\u5c06\u94f6\u8863\u4eba\u6454\u5728\u684c\u5b50\u524d \u9762\u6f6e\u6e7f\u7684\u77f3\u677f\u5730\u4e0a\u3002 \u3000\u3000\u8d3e\u82f1\u770b\u4e86\u770b\u94f6\u8863\u4eba\uff0c\u95ee\u536b\u58eb\u4eec\uff1a\u201c\u4ed6\u817f\u4e0a\u548c\u624b\u4e0a\u7684\u7a74\u9053\u90fd\u6ca1\u6709\u89e3\u5f00\u5427\uff1f\u201d \u3000\u3000\u536b\u58eb\u9053\uff1a\u201c\u6ca1\u6709\u603b\u7ba1\u7684\u547d\u4ee4\uff0c\u5c5e\u4e0b\u4e0d\u6562\u81ea\u4f5c\u4e3b\u5f20\uff01\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u597d\uff01\u4f60\u4eec\u505a\u5f97\u5f88\u5bf9\uff0c\u8fd9\u4e2a\u94f6\u8863\u4eba\u7684\u6b66\u529f\u4e4b\u9ad8\uff0c\u5176\u5b9e\u4e5f\u5e76\u4e0d\u5728\u8d3e\u67d0\u4e4b\u4e0b\uff0c\u5982\u679c \u89e3\u5f00\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u5c31\u5f88\u5371\u9669\u4e86\uff01\u201d \u3000\u3000\u536b\u58eb\u9053\uff1a\u201c\u5c5e\u4e0b\u4e0d\u4f1a\u5fd8\u8bb0\u603b\u7ba1\u7684\u8bdd\u3002\u201d \u3000\u3000\u88ab\u6454\u5728\u5730\u4e0a\u7684\u94f6\u8863\u4eba\u6323\u624e\u7740\u62ac\u8d77\u5934\u6765\uff0c\u6012\u89c6\u7740\u8d3e\u82f1\uff0c\u53cc\u76ee\u4e4b\u4e2d\uff0c\u7adf\u50cf\u662f\u8981\u55b7\u51fa\u706b\u6765\u3002 \u3000\u3000\u536b\u58eb\u559d\u53f1\u9053\uff1a\u201c\u77aa\u7740\u4f60\u7684\u72d7\u773c\u770b\u4ec0\u4e48\uff01\u8fd9\u662f\u8d3e\u603b\u7ba1\uff0c\u4f60\u6700\u597d\u653e\u5c0a\u656c\u70b9\uff0c\u4e0d\u7136\uff0c\u770b\u8001\u5b50\u4eec \u4e0d\u6253\u6b7b\u4f60\uff01\u201d \u3000\u3000\u94f6\u8863\u4eba\u8f6c\u5934\u77aa\u7740\u536b\u58eb\uff0c\u58f0\u97f3\u4f4e\u6c89\u5730\u9053\uff1a\u201c\u5982\u679c\u8001\u5b50\u4e0d\u662f\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u50cf\u4f60\u7b49\u8fd9\u79cd\u5c0f\u6742\u79cd \u8001\u5b50\u4e00\u638c\u5c31\u80fd\u6253\u6b7b\u4e09\u56db\u4e2a\uff01\u5c11\u5728\u8001\u5b50\u9762\u524d\u800d\u5a01\u98ce\uff0c\u6709\u672c\u4e8b\u653e\u5f00\u8001\u5b50\uff0c\u54b1\u4eec\u5728\u62f3\u811a\u4e0a\u89c1\u4e2a\u771f\u5b9e\uff0c \u5206\u4e2a\u9ad8\u4e0b\uff01\u201d \u3000\u3000\u536b\u58eb\u5927\u6012\uff0c\u62ac\u811a\u5c31\u8e22\uff0c\u9a82\u9053\uff1a\u201c\u4ed6\u5988\u7684\uff01\u771f\u662f\u9e2d\u5b50\u6b7b\u4e86\u5634\u58f3\u5b50\u786c\uff01\u90fd\u6b7b\u5230\u4e34\u5934\u4e86\uff0c\u8fd8\u6562 \u5439\u725b\uff01\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u4e0d\u8981\u6253\u4ed6\uff01\u4f60\u4eec\u9000\u5230\u4e00\u8fb9\xb7\u6211\u6709\u8bdd\u8981\u95ee\u4ed6\uff01\u201d \u3000\u3000\u536b\u58eb\u4eec\u5230\u5411\u540e\u9000\u53bb\uff0c\u4f46\u4e00\u4e2a\u4e2a\u4ecd\u7136\u5bf9\u94f6\u8863\u4eba\u864e\u89c6\u803d\u803d\uff0c\u5355\u7b49\u8d3e\u82f1\u4e00\u58f0\u4ee4\u4e0b\uff0c\u4fbf\u4f1a\u4e00\u6d8c\u800c \u4e0a\uff0c\u52a8\u624b\u75db\u6253\u3002 \u3000\u3000\u94f6\u8863\u4eba\u54fc\u4e86\u4e00\u58f0\uff0c\u6162\u6162\u722c\u4e86\u8d77\u6765\uff0c\u5f20\u5f00\u53cc\u817f\u5750\u5728\u5730\u4e0a\uff0c\u53cc\u773c\u76f4\u5728\u8d3e\u82f1\u548c\u5929\u67ab\u9053\u957f\u8eab\u4e0a\u6253 \u8f6c\u8f6c\uff0c\u8138\u4e0a\u7adf\u662f\u770b\u4e0d\u51fa\u534a\u70b9\u754f\u60e7\u4e4b\u8272\u3002 \u3000\u3000\u8d3e\u82f1\u4e5f\u76f4\u89c6\u7740\u4ed6\uff0c\u76ee\u5149\u9634\u68ee\u68ee\u5730\uff0c\u50cf\u662f\u4e24\u9053\u9510\u5229\u7684\u5251\u5149\u3002 \u3000\u3000\u94f6\u8863\u4eba\u6beb\u4e0d\u9000\u7f29\u3002 \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u8fd9\u4f4d\u4ec1\u5144\uff0c\u4f60\u5230\u5e95\u662f\u4f55\u4eba\u95e8\u4e0b\uff1f\u591c\u95ef\u91d1\u5e9c\uff0c\u6240\u6765\u4f55\u4e3a\uff1f\u201d \u3000\u3000\u94f6\u8863\u4eba\u9053\uff1a\u201c\u8bf4\u8fd9\u4e9b\u6ca1\u7528\u7684\u5e72\u4ec0\u4e48\uff1f\u8001\u5b50\u4e0d\u65e9\u5c31\u8bf4\u8fc7\u4e86\u561b\uff0c\u8001\u5b50\u662f\u6765\u95ee\u91d1\u767e\u5e74\u8981\u4e09\u5b9d \u7684\u3002\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u4e09\u5b9d\u4e43\u91d1\u5e9c\u4e4b\u7269\uff0c\u4e3a\u4ec0\u4e48\u8981\u7ed9\u4f60\uff1f\u4f60\u6709\u4ec0\u4e48\u6743\u529b\u6765\u8981\u5b83\u4eec\uff1f\u201d \u3000\u3000\u94f6\u8863\u4eba\u9053\uff1a\u201c\u653e\u5c41\uff01\u4e09\u5b9d\u672c\u662f\u8001\u5b50\u7684\u4e1c\u897f\uff0c\u662f\u91d1\u8001\u5339\u592b\u4ece\u8001\u5b50\u624b\u4e0a\u9a97\u53bb\u7684\uff01\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u6211\u770b\u4f60\u624d\u662f\u653e\u5c41\u54e9\uff01\u4e09\u5b9d\u4e43\u6211\u4eec\u4e1c\u4e3b\u82b1\u91cd\u91d1\u8d2d\u5f97\uff0c\u600e\u4e48\u4f1a\u662f\u9a97\u5230\u624b\u7684\uff01\u800c\u4e14\uff0c \u636e\u8d3e\u67d0\u6240\u77e5\uff0c\u90a3\u4e2a\u5356\u51fa\u7684\u4eba\u4e5f\u4e0d\u662f\u4f60\uff01\u201d \u3000\u3000\u94f6\u8863\u4eba\u95ed\u4e0a\u4e86\u5634\uff0c\u773c\u775b\u4e5f\u95ed\u4e0a\u4e86\uff0c\u4f3c\u4e4e\u4e0d\u5c51\u4e0e\u8d3e\u82f1\u8bf4\u8bdd\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\u5728\u95ee\u4f60\u8bdd\uff0c\u8fd8\u4e0d\u5feb\u56de\u7b54\uff01\u201d \u3000\u3000\u94f6\u8863\u4eba\u626b\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8ddf\u4f60\u4eec\u8fd9\u5e2e\u5974\u624d\u8bf4\u6709\u4ec0\u4e48\u7528\u5904\uff01\u5feb\u53eb\u91d1\u767e\u5e74\u6765\uff0c\u8001\u5b50\u548c\u4ed6\u5bf9 \u8d28\uff01\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u655d\u4e1c\u4e3b\u5df2\u5c06\u4ec1\u5144\u4f60\u4ea4\u7ed9\u4e86\u8d3e\u67d0\uff0c\u6709\u4ec0\u4e48\u8bdd\u5c31\u5bf9\u8d3e\u67d0\u8bf4\u5427\uff0c\u655d\u4e1c\u4e3b\u5f88\u5fd9\uff0c\u54ea\u91cc \u6709\u529f\u592b\u89c1\u4f60\u8fd9\u6837\u7684\u4eba\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u5ffd\u7136\u4fef\u5728\u8d3e\u67d0\u8033\u8fb9\uff0c\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u8d3e\u82f1\u542c\u5f97\u76f4\u70b9\u5934\u3002 \u3000\u3000\u94f6\u8863\u4eba\u9a82\u9053\uff1a\u201c\u4e24\u4e2a\u72d7\u8d3c\uff0c\u53c8\u5728\u60f3\u4ec0\u4e48\u529e\u6cd5\u6765\u6574\u6cbb\u8001\u5b50\uff1f\u8001\u5b50\u544a\u8bc9\u4f60\u4eec\uff0c\u6709\u4ec0\u4e48\u624b\u6bb5\u5c31 \u4e00\u8d77\u4f7f\u51fa\u6765\u5427\uff01\u8001\u5b50\u4e0d\u6015\uff01\u201d \u3000\u3000\u8d3e\u82f1\u770b\u4e86\u5929\u67ab\u9053\u957f\u4e00\u773c\uff0c\u7b11\u7b11\uff0c\u9053\uff1a\u201c\u8d3e\u67d0\u77e5\u9053\u4ec1\u5144\u662f\u4f4d\u786c\u6c49\uff0c\u4e5f\u662f\u4f4d\u597d\u6c49\uff0c\u8fd9\u6837\u5427\uff0c \u8d3e\u67d0\u95ee\u4f60\u4e00\u4e2a\u95ee\u9898\uff0c\u5c3a\u8981\u4f60\u636e\u5b9e\u56de\u7b54\uff0c\u4e70\u67d0\u51b3\u4e0d\u4e3a\u96be\u4f60\uff0c\u800c\u4e14\u4f1a\u4e0e\u5929\u67ab\u9053\u957f\u4e00\u8d77\u53bb\u5411\u4e1c\u4e3b\u6c42 \u60c5\uff0c\u653e\u4e86\u4f60\uff01\u201d \u3000\u3000\u94f6\u8863\u4eba\u6c89\u541f\u7740\uff0c\u663e\u7136\u662f\u5728\u601d\u8003\u8d3e\u67d0\u8bf4\u7684\u662f\u4e0d\u662f\u5b9e\u8bdd\uff0c\u4e5f\u5728\u60f3\u5e94\u5bf9\u4e4b\u7b56\u3002 \u3000\u3000\u8d3e\u82f1\u8ffd\u95ee\u9053\uff1a\u201c\u4ec1\u5144\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u94f6\u8863\u4eba\u9053\uff1a\u201c\u4f60\u95ee\u5427\uff01\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u597d\uff01\u4f60\u662f\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\u7684\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u94f6\u8863\u4eba\u4e00\u6014\uff0c\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u8fd9\u53ef\u662f\u4ec1\u5144\u4f60\u552f\u4e00\u6d3b\u547d\u7684\u673a\u4f1a\u4e86\uff0c\u4e5f\u662f\u6700\u540e\u7684\u673a\u4f1a\uff0c\u4f60\u8fd8\u662f\u8bf4\u5b9e\u8bdd\u5427\uff01\u201d \u3000\u3000\u94f6\u8863\u4eba\u76ee\u5149\u5728\u8d3e\u82f1\u548c\u5929\u67ab\u9053\u957f\u8138\u4e0a\u8f6c\u6765\u8f6c\u53bb\uff0c\u7a81\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8001\u5b50\u8981\u559d\u9152\uff01\u201d \u3000\u3000\u8d3e\u82f1\u548c\u5929\u67ab\u9053\u957f\u90fd\u662f\u4e00\u6014\uff0c\u4ed6\u4eec\u8c01\u4e5f\u6ca1\u60f3\u5230\u94f6\u8863\u4eba\u5728\u8fd9\u79cd\u65f6\u5019\u8fd8\u4f1a\u63d0\u51fa\u7684\u8fd9\u6837\u7684\u8981\u6c42\u6765\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u56de\u7b54\u4e86\u8d3e\u603b\u7ba1\u7684\u95ee\u9898\uff0c\u5c31\u7ed9\u9152\u559d\uff01\u201d \u3000\u3000\u8d3e\u5176\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5982\u679c\u4f60\u8bf4\u7684\u662f\u5b9e\u8bdd\uff0c\u4e0d\u4ec5\u6709\u9152\u559d\uff0c\u8d3e\u67d0\u8fd8\u4f1a\u5949\u4e0a\u8d64\u91d1\u5343\u4e24\uff0c\u606d\u9001\u4ec1\u5144 \u51fa\u91d1\u5e9c\u3002\u201d \u3000\u3000\u94f6\u8863\u4eba\u9053\uff1a\u201c\u8001\u5b50\u8981\u559d\u9152\uff0c\u6ca1\u6709\u9152\uff0c\u8001\u5b50\u5c31\u4e0d\u56de\u7b54\u4efb\u4f55\u95ee\u9898\uff0c\u544a\u8bc9\u4f60\u4eec\uff0c\u8001\u5b50\u4e0d\u662f\u4e09\u5c81 \u5c0f\u5b69\uff0c\u90a3\u4e48\u597d\u9a97\uff01\u201d \u3000\u3000\u8d3e\u82f1\u770b\u7740\u5929\u67ab\u9053\u957f\uff0c\u9053\uff1a\u201c\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u813e\u6c14\u8fd8\u771f\u662f\u53c8\u81ed\u53c8\u786c\uff0c\u5509\uff0c\u6ca1\u529e\u6cd5\uff0c\u5c31\u7ed9\u4ed6\u9152\u5427\uff0c\u603b \u7ba1\uff0c\u4f60\u770b\u5462\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u65e0\u5948\u5730\u5bf9\u4e00\u540d\u536b\u58eb\u9053\uff1a\u201c\u53bb\uff0c\u62ff\u4e00\u575b\u597d\u9152\u6765\uff01\u201d \u3000\u3000\u94f6\u8863\u4eba\u9053\uff1a\u201c\u8fd8\u8981\u51e0\u6837\u4e0b\u9152\u83dc\uff01\u201d \u3000\u3000\u8d3e\u82f1\u54ed\u7b11\u4e0d\u5f97\uff1a\u201c\u8001\u5144\uff0c\u4f60\u89c1\u597d\u5c31\u6536\u5427\uff0c\u4e0d\u8981\u592a\u8fc7\u4efd\u4e86\uff01\u201d \u3000\u3000\u94f6\u8863\u4eba\u51b7\u51b7\u5730\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9152\u8981\u662f\u4e0d\u597d\uff0c\u83dc\u8981\u662f\u4e0d\u5408\u53e3\u5473\uff0c\u8001\u5b50\u5c31\u4e0d\u56de\u7b54\u4f60\u4eec \u7684\u95ee\u9898\uff01\u201d \u3000\u3000\u8d3e\u82f1\u548c\u5929\u67ab\u9053\u957f\u76f8\u89c6\u82e6\u7b11\u3002 \u3000\u3000\u94f6\u8863\u4eba\u8fd9\u6837\u7684\u4eba\uff0c\u4ed6\u4eec\u4ee5\u524d\u8fd8\u771f\u6ca1\u89c1\u8fc7\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u4ed4\u7ec6\u5411\u94f6\u8863\u4eba\u6253\u91cf\u4e86\u4e00\u756a\uff0c\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\uff0c\u8fd9\u4eba\u8eab\u4e0a\u7684\u4f24\u75d5\u867d\u7136\u5f88\u591a\uff0c\u4f46\u597d\u50cf \u90fd\u4e0d\u5f88\u91cd\uff0c\u90fd\u662f\u4e9b\u76ae\u8089\u4e4b\u4f24\u561b\uff01\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u662f\u554a\uff0c\u6240\u4ee5\u8d3e\u679c\u8bf4\u4ed6\u7684\u6b66\u529f\u4e0d\u5728\u6211\u4e4b\u4e0b\uff0c\u5373\u5728\u591c\u95f4\uff0c\u8d3e\u67d0\u5df2\u7528\u5c3d\u4e86\u7edd\u6280\uff0c\u4e5f \u65e0\u6cd5\u5f7b\u5e95\u51fb\u8d25\u4ed6\uff0c\u5982\u679c\u4e0d\u662f\u4ed6\u56e0\u4e3a\u4f24\u53e3\u6d41\u8840\u8fc7\u591a\uff0c\u5bfc\u81f4\u4f53\u80fd\u4e0b\u964d\uff0c\u8d3e\u67d0\u8fd8\u771f\u62ff\u4e0d\u4f4f\u4ed6\uff01\u4e0d\u8fc7\uff0c \u73b0\u5728\u6ca1\u5173\u4fdd\u4e86\uff0c\u56e0\u4e3a\u5982\u679c\u5df2\u70b9\u4e86\u4ed6\u624b\u81c2\u4e4b\u4e0a\u548c\u4e24\u817f\u4e0a\u7684\u8981\u7a74\uff0c\u4ed6\u518d\u6709\u5929\u5927\u7684\u672c\u4e8b\uff0c\u4e5f\u9003\u8131\u4e0d \u51fa\u4e86\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u6c89\u541f\u9053\uff1b\u201c\u8fd9\u4eba\u7684\u6b66\u529f\u5982\u6b64\u4e4b\u9ad8\uff0c\u770b\u6765\u5fc5\u5b9a\u662f\u68a6\u5e7b\u4e4b\u5200\u95e8\u4e0b\u65e0\u7591\uff01\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u53cd\u6b63\u4ed6\u5feb\u5f00\u53e3\u56de\u7b54\u95ee\u9898\u4e86\uff0c\u771f\u76f8\u5982\u4f55\uff0c\u5f88\u5feb\u5c31\u4f1a\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u4e0d\u4e00\u4f1a\u513f\uff0c\u9152\u62ff\u6765\u4e86\uff0c\u83dc\u4e5f\u7aef\u4e0a\u6765\u4e86\u3002 \u3000\u3000\u94f6\u8863\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u5c01\u4e86\u8001\u5b50\u624b\u81c2\u4e0a\u7684\u7a74\u9053\uff0c\u53eb\u8001\u5b50\u5982\u4f55\u559d\u9152\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u6709\u8d3e\u603b\u7ba1\u5728\u6b64\uff0c\u8c05\u8fd9\u4eba\u4e5f\u8dd1\u4e0d\u4e86\uff0c\u4e0d\u5982\u5148\u89e3\u5f00\u4ed6\u7684\u7a74\u9053\u5427\u3002\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u4e0d\u53ef\uff01\u6b64\u4eba\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u4e00\u65e6\u6709\u6240\u95ea\u5931\uff0c\u54b1\u4eec\u5c31\u65e0\u6cd5\u5411\u4e1c\u4e3b\u4ea4\u5f85\u4e86\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\u8bf4\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u65e0\u53ef\u5948\u4f55\u5730\u9053\uff1a\u201c\u53ea\u597d\u7531\u8d3e\u67d0\u53bb\u5582\u4ed6\u559d\u4e86\uff01\u201d \u3000\u3000\u4ed6\u7ad9\u8d77\u8eab\u5b50\uff0c\u8d70\u5230\u94f6\u8863\u4eba\u9762\u524d\uff0c\u6367\u8d77\u4e86\u9152\u575b\u5b50\uff0c\u6ee1\u6ee1\u5730\u5012\u4e86\u4e00\u5927\u7897\u9152\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u4ec1\u5144\uff0c \u4f60\u5148\u59d4\u5c48\u4e00\u4e0b\uff0c\u7531\u8d3e\u67d0\u5582\u4f60\u559d\u9152\u5403\u83dc\uff0c\u53ea\u8981\u613f\u8bf4\u5b9e\u8bdd\uff0c\u8d3e\u67d0\u81ea\u4f1a\u8fd8\u4f60\u81ea\u7531\uff01\u201d \u3000\u3000\u94f6\u8863\u4eba\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u7c97\u58f0\u7c97\u6c14\u5730\u9053\uff1a\u201c\u8bf4\u5582\u8001\u5b50\uff0c\u624b\u4e0a\u53c8\u4e0d\u52a8\uff0c\u4e0d\u662f\u8981\u60f3\u998b\u6b7b\u8001\u5b50\u561b\uff01 \u5feb\uff0c\u628a\u9152\u9012\u8fc7\u6765\uff01\u201d \u3000\u3000\u8d3e\u82f1\u5c06\u9152\u7897\u9012\u5230\u94f6\u8863\u4eba\u5634\u8fb9\uff0c\u53ea\u89c1\u4ed6\u6df1\u5438\u4e00\u53e3\u6c14\uff0c\u7897\u91cc\u7684\u9152\u5316\u4f5c\u4e00\u9053\u9152\u67f1\uff0c\u5168\u90fd\u88ab\u5438\u8fdb \u4ed6\u53e3\u4e2d\u53bb\u4e86\u3002 \u3000\u3000\u8d3e\u82f1\u56de\u8fc7\u5934\u770b\u7740\u5929\u67ab\u9053\u957f\uff0c\u9762\u4e0a\u5f88\u6709\u4e9b\u5403\u60ca\u53d8\u8272\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u4e5f\u975e\u5e38\u5403\u60ca\u3002 \u3000\u3000\u8fd9\u94f6\u8863\u4eba\u5185\u529f\u5b9e\u5728\u662f\u592a\u60ca\u4eba\u4e86\u3002 \u3000\u3000\u8d3e\u82f1\u80fd\u51fb\u8d25\u8fd9\u94f6\u8863\u4eba\uff0c\u4ed6\u7684\u5185\u529f\u5c82\u975e\u66f4\u662f\u975e\u540c\u51e1\u54cd\uff01 \u3000\u3000\u4e00\u7728\u95f4\u7684\u529f\u592b\uff0c\u94f6\u8863\u4eba\u559d\u4e86\u4e03\u516b\u7897\u9152\uff0c\u5c06\u4e09\u4e2a\u789f\u5b50\u91cc\u7684\u4e0b\u9152\u83dc\u66f4\u662f\u4e00\u626b\u800c\u5149\uff0c\u8fd9\u624d\u5482\u4e86 \u5482\u5634\uff0c\u5927\u7b11\u9053\uff1a\u201c\u75db\u5feb\uff01\u75db\u5feb\u5440\uff01\u80fd\u8ba9\u5802\u5802\u91d1\u5e9c\u603b\u7ba1\u5582\u8001\u5b50\u559d\u9152\uff0c\u8001\u5b50\u7684\u9762\u5b50\u8fd8\u771f\u662f\u5927\u7684\u5f88 \u5462\uff01\u201d \u3000\u3000\u8d3e\u82f1\u8d70\u56de\u684c\u5b50\u540e\u9762\u5750\u4e0b\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8fd9\u4f4d\u4ec1\u5144\uff0c\u559d\u4e5f\u559d\u4e86\uff0c\u83dc\u4e5f\u5403\u4e86\uff0c\u9762\u5b50\u4f60\u4e5f\u6323\u8db3\u4e86\uff0c \u8be5\u56de\u7b54\u95ee\u9898\u4e86\u5417\uff1f\u201d \u3000\u3000\u94f6\u8863\u4eba\u7b11\u9053\uff1a\u201c\u8001\u5b50\u7684\u8bb0\u6027\u4e00\u5411\u4e0d\u592a\u597d\uff0c\u8d3e\u603b\u7ba1\u65b9\u624d\u95ee\u4e86\u4e2a\u4ec0\u4e48\u95ee\u9898\uff1f\u8001\u5b50\u559d\u9152\u559d\u5f97\u9ad8 \u5174\uff0c\u4e5f\u559d\u591a\u4e86\u4e00\u70b9\uff0c\u5934\u91cc\u9762\u6655\u5f97\u5f88\uff0c\u628a\u90a3\u4e2a\u95ee\u9898\u7ed9\u5fd8\u8bb0\u6389\u4e86\uff01\u201d \u3000\u3000\u8d3e\u82f1\u5927\u6012\uff0c\u52c9\u5f3a\u5730\u5236\u7740\u81ea\u5df1\u7684\u6012\u706b\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u94f6\u8863\u4eba\u7b11\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\uff0c\u4f60\u662f\u8981\u542c\u771f\u8bdd\uff0c\u8fd8\u662f\u8981\u542c\u5047\u8bdd\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u6012\u9053\uff1a\u201c\u5f53\u7136\u771f\u8bdd\uff01\u201d \u3000\u3000\u94f6\u8863\u4eba\u53c8\u9053\uff1a\u201c\u6211\u5982\u679c\u662f\u68a6\u5e7b\u4e4b\u5200\u95e8\u4e0b\uff0c\u4f60\u4eec\u4f1a\u628a\u6211\u600e\u4e48\u6837\uff1f\u5982\u679c\u6211\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\u95e8\u4e0b\uff0c \u4f60\u4eec\u53c8\u4f1a\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u201c\u547c\u201d\u5730\u4e00\u58f0\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u94f6\u8863\u4eba\u5927\u7b11\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\uff0c\u4e0d\u8981\u53d1\u706b\u561b\uff0c\u6c14\u5927\u4f24\u8eab\u7684\uff01\u201d \u3000\u3000\u8d3e\u82f1\u9762\u4e0a\u9634\u4e91\u5bc6\u5e03\uff0c\u54ac\u4e86\u54ac\u7259\uff0c\u53c8\u5750\u5728\u4e86\u6905\u5b50\u4e0a\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u5982\u679c\u4f60\u662f\u68a6\u5e7b\u4e4b\u5200\u95e8\u4e0b\uff0c\u6211\u4eec\u4f1a\u653e\u4f60\u56de\u53bb\uff0c\u8bf7\u4f60\u7ed9\u68a6\u5e7b\u4e4b\u5200\u5e26\u4e2a\u8bdd\uff0c\u5c31 \u8bf4\u8981\u60f3\u4e09\u5b9d\uff0c\u8ba9\u4ed6\u81ea\u5df1\u6765\u62ff\uff0c\u5982\u679c\u4f60\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\u95e8\u4e0b\uff0c\u8bf7\u4f60\u8bf4\u51fa\u4f60\u5230\u5e95\u662f\u4ec0\u4e48\u8eab\u4efd\uff0c\u6211\u4eec \u4e5f\u4f1a\u653e\u4e86\u4f60\uff0c\u81ea\u4f1a\u627e\u4f60\u95e8\u4e2d\u5e08\u957f\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u94f6\u8863\u4eba\u7b11\u7b11\u9053\uff1a\u201c\u770b\u6765\uff0c\u4e0d\u8bba\u6211\u662f\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\u95e8\u4e0b\uff0c\u4f60\u4eec\u90fd\u4f1a\u653e\u8fc7\u8001\u5b50\u7684\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u51a4\u6709\u5934\uff0c\u503a\u6709\u4e3b\uff0c\u6211\u4eec\u662f\u4e0d\u4f1a\u4e3a\u96be\u4f60\u3002\u201d \u3000\u3000\u94f6\u8863\u4eba\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8981\u662f\u8001\u5b50\u4e00\u5b9a\u4e0d\u8bf4\u5462\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4f60\u600e\u53ef\u8a00\u800c\u65e0\u4fe1\uff1f\u201d \u3000\u3000\u94f6\u8863\u4eba\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u6012\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff01\u201d \u3000\u3000\u94f6\u8863\u4eba\u5927\u7b11\u9053\uff1a\u201c\u548c\u4f60\u4eec\u8fd9\u6837\u7684\u4eba\uff0c\u6709\u4ec0\u4e48\u4fe1\u4e49\u53ef\u8c08\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u66f4\u6012\u4e86\uff0c\u9053\uff1a\u201c\u90a3\u4f60\u521a\u624d\u4e3a\u4ec0\u4e48\u8981\u7b54\u5e94\u6211\u4eec\uff0c\u8bf4\u4f60\u4f1a\u56de\u7b54\u6211\u4eec\u7684\u95ee\u9898\u5462\uff1f\u201d \u3000\u3000\u94f6\u8863\u4eba\u5f97\u610f\u5730\u9053\uff1a\u201c\u8001\u5b50\u5b9e\u8bdd\u544a\u8bc9\u4f60\u5427\uff0c\u5446\u5728\u8fd9\u4e2a\u53c8\u51b7\u53c8\u6f6e\u6e7f\u7684\u7834\u5730\u7262\u91cc\uff0c\u8001\u5b50\u96be\u53d7\u5f97 \u5f88\uff0c\u6240\u4ee5\u5c31\u9a97\u4f60\u4eec\u51e0\u53e3\u9152\u559d\uff0c\u9a71\u4e00\u9a71\u5bd2\u6c14\uff01\u201d \u3000\u3000\u8d3e\u82f1\u5927\u559d\u4e00\u58f0\uff0c\u62cd\u6848\u9053\uff1a\u201c\u72c2\u5f92\u7adf\u6562\u620f\u5f04\u4e8e\u6211\uff01\u201d \u3000\u3000\u94f6\u8863\u4eba\u53f9\u6c14\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\uff0c\u4f60\u4e5f\u4e0d\u8981\u751f\u6c14\uff0c\u5929\u67ab\u9053\u957f\uff0c\u4f60\u4e5f\u4e0d\u8981\u602a\u6211\uff0c\u8981\u602a\uff0c\u4f60\u4eec\u5c31\u602a \u5b88\u7262\u7684\u8fd9\u4e9b\u536b\u58eb\u4eec\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u94f6\u8863\u4eba\u9053\uff1a\u201c\u5982\u679c\u8001\u5b50\u6628\u5929\u8981\u9152\u559d\u65f6\uff0c\u4ed6\u4eec\u5c31\u7ed9\u8001\u5b50\u62ff\u6765\u4e86\uff0c\u8001\u5b50\u4f55\u82e6\u53c8\u8f6c\u8fd9\u4e48\u5927\u4e00\u4e2a \u5f2f\u5b50\u6765\u9a97\u4f60\u4eec\u5462\uff01\u201d \u3000\u3000\u8d3e\u82f1\u4e00\u62cd\u684c\u5b50\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u8fd8\u662f\u4e0d\u8bf4\uff1f\u201d \u3000\u3000\u94f6\u8863\u4eba\u4e00\u6897\u8116\u5b50\uff0c\u9053\uff1a\u201c\u4e0d\u8bf4\uff01\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u4f60\u4e0d\u6015\u6b7b\uff1f\u201d \u3000\u3000\u94f6\u8863\u4eba\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\u8fd9\u8bdd\u95ee\u5f97\u771f\u662f\u6ca1\u6709\u6c34\u5e73\uff01\u201d \u3000\u3000\u8d3e\u82f1\u601d\u9053\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u8bdd\u97f3\u672a\u843d\uff0c\u4ed6\u5df2\u95ea\u8eab\u5230\u4e86\u94f6\u8863\u4eba\u8eab\u8fb9\uff0c\u4e3e\u638c\u62cd\u4e0b\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u5927\u60ca\uff0c\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\u606f\u6012\uff0c\u5343\u4e07\u4e0d\u53ef\u6740\u4e86\u4ed6\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u4ed6\u5df2\u95ea\u8eab\u62a2\u5230\uff0c\u5c31\u4f38\u624b\u53bb\u62c9\u4f4f\u8d3e\u82f1\u3002 \u3000\u3000\u5df2\u7ecf\u8fdf\u4e86\uff01 \u3000\u3000\u8d3e\u82f1\u7684\u53f3\u638c\u5df2\u7ecf\u62cd\u5728\u94f6\u8863\u4eba\u80f8\u524d\uff01 \u3000\u3000\u94f6\u8863\u4eba\u6b63\u95ed\u76ee\u7b49\u6b7b\uff0c\u8d3e\u82f1\u4e00\u638c\u62cd\u4e0b\uff0c\u4ed6\u5ffd\u5730\u7741\u5f00\u4e86\u773c\u775b\uff0c\u7b11\u9053\uff1a\u201c\u54c8\u54c8\uff01\u591a\u8c22\u8d3e\u603b\u7ba1\uff01 \u4f60\u4e00\u5b9a\u662f\u77e5\u9053\u8001\u5b50\u8eab\u4e0a\u53d7\u4e86\u6f6e\u6c14\uff0c\u75d2\u5f97\u5f88\uff0c\u7279\u6765\u66ff\u8001\u5b50\u6320\u75d2\u75d2\uff01\u201d \u3000\u3000\u4ed6\u5927\u7b11\u51e0\u58f0\uff0c\u63a5\u7740\u9053\uff1a\u201c\u53ef\u60dc\u6320\u5f97\u592a\u8f7b\uff0c\u518d\u91cd\u4e00\u70b9\u624d\u597d\uff01\u8001\u9053\u957f\uff0c\u4f60\u4e5f\u4e00\u8d77\u6765\u5e2e\u5e2e\u5fd9\u5427\uff0c \u8001\u5b50\u7684\u80cc\u4e5f\u75d2\u75d2\u5f97\u5f88\u5462\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u4e00\u6014\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u4e0d\u56de\u7b54\uff0c\u624b\u638c\u4e00\u76f4\u6309\u5728\u94f6\u8863\u4eba\u80f8\u53e3\uff0c\u6ca1\u6709\u62ff\u5f00\u3002 \u3000\u3000\u94f6\u8863\u4eba\u53c8\u7b11\u4e86\u51e0\u58f0\uff0c\u5ffd\u7136\u4e0d\u7b11\u4e86\uff0c\u8138\u4e0a\u7684\u808c\u8089\u626d\u66f2\u8d77\u6765\uff0c\u5927\u53eb\u9053\uff1a\u201c\u54ce\u54df\uff0c\u75bc\u6b7b\u8001\u5b50\u4e86\uff01 \u8d3e\u603b\u7ba1\uff0c\u4f60\u8fd8\u662f\u4e0d\u662f\u4eba\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u8fd8\u4e0d\u77e5\u9053\u8d3e\u82f1\u662f\u5728\u4ee5\u4e00\u79cd\u5f88\u7279\u6b8a\u7684\u5185\u529f\u624b\u6cd5\u5728\u6298\u78e8\u94f6\u8863\u4eba\uff0c\u603b\u7b97\u662f\u677e\u4e86\u4e00\u53e3\u6c14\uff0c \u9053\uff1a\u201c\u5413\u4e86\u6211\u4e00\u8df3\uff01\u201d \u3000\u3000\u8d3e\u82f1\u51b7\u51b7\u9053\uff1a\u201c\u4ed6\u7684\u610f\u601d\u672c\u662f\u60f3\u6fc0\u6012\u6211\u4eec\uff0c\u8ba9\u6211\u4eec\u4e00\u4e0b\u5b50\u6740\u4e86\u4ed6\uff01\u6211\u4e0d\u4f1a\u8ba9\u4ed6\u6b7b\u5f97\u592a\u75db \u5feb\uff01\u54fc\u54fc\uff01\u8ba9\u4f60\u5634\u786c\uff0c\u770b\u4f60\u80fd\u5426\u786c\u5f97\u8fc7\u8d3e\u67d0\u4eba\u7684\u641c\u9b42\u5927\u6cd5\uff01\u201d \u3000\u3000\u94f6\u8863\u4eba\u663e\u7136\u5df2\u7ecf\u53d7\u4e0d\u4e86\u4e86\uff0c\u76f4\u7740\u8116\u5b50\uff0c\u5927\u58f0\u76f4\u53eb\u5524\uff01 \u3000\u3000\u641c\u9b42\u5927\u6cd5\u7684\u6b8b\u9177\u4e4b\u5904\uff0c\u4fbf\u662f\u80fd\u8ba9\u4eba\u6c42\u751f\u4e0d\u5f97\uff0c\u6c42\u6b7b\u4e0d\u80fd\u3002 \u3000\u3000\u8d3e\u82f1\u72de\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u4e0d\u8bf4\uff1f\u201d \u3000\u3000\u94f6\u8863\u4eba\u6ee1\u5934\u5927\u6c57\uff0c\u9762\u90e8\u808c\u8089\u5df2\u626d\u6210\u4e00\u56e2\uff0c\u4ecd\u7136\u9053\uff1a\u201c\u4e0d\u8bf4\uff01\u201d \u3000\u3000\u8d3e\u82f1\u559d\u9053\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u5f88\u663e\u7136\uff0c\u4ed6\u624b\u4e0a\u7684\u529f\u529b\u53c8\u52a0\u91cd\u4e86\u4e00\u5c42\u3002 \u3000\u3000\u94f6\u8863\u4eba\u53c8\u652f\u6491\u4e86\u4e00\u4f1a\u513f\uff0c\u770b\u6837\u5b50\u5b9e\u5728\u662f\u71ac\u4e0d\u4e0b\u53bb\u4e86\uff0c\u4e24\u773c\u76f4\u7ffb\u767d\uff0c\u5598\u606f\u7740\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\uff0c \u6211\u2026\u2026\u6211\u8bf4\uff01\u201d \u3000\u3000\u8d3e\u82f1\u72de\u7b11\u9053\uff1a\u201c\u8c05\u4f60\u4e5f\u633a\u4e0d\u8fc7\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5c06\u53f3\u638c\u64a4\u5f00\u4e24\u5bf8\u3002 \u3000\u3000\u94f6\u8863\u4eba\u95ed\u7740\u773c\u775b\uff0c\u5927\u53e3\u5927\u53e3\u5730\u5598\u6c14\u3002 \u3000\u3000\u8d3e\u82f1\u559d\u9053\uff1a\u201c\u5feb\u8bf4\uff01\u201d \u3000\u3000\u94f6\u8863\u4eba\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\uff0c\u8fd9\u6b21\u6211\u4f1a\u8bf4\u7684\uff0c\u4f60\u5c31\u5148\u8ba9\u6211\u5598\u4e0a\u4e24\u53e3\u6c14\u5427\uff01\u201d \u3000\u3000\u8d3e\u82f1\u54ac\u4e86\u54ac\u7259\uff0c\u9053\uff1a\u201c\u597d\uff01\u4f60\u5598\u5427\u3002\u201d \u3000\u3000\u94f6\u8863\u4eba\u5598\u4e86\u534a\u5929\u6c14\uff0c\u547c\u5438\u5df2\u5e73\u7a33\u591a\u4e86\u3002 \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u73b0\u5728\u80fd\u8bf4\u4e86\u5427\uff1f\u201d \u3000\u3000\u94f6\u8863\u4eba\u7741\u5f00\u773c\u775b\u770b\u7740\u4ed6\uff0c\u7a81\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u521a\u624d\uff0c\u53ea\u8981\u8d3e\u603b\u7ba1\u518d\u7565\u65bd\u52a0\u4e00\u4f1a\u513f\u4f60\u7684\u641c \u9b42\u5927\u6cd5\uff0c\u54ea\u6015\u53ea\u591a\u4e00\u5c0f\u4f1a\u513f\uff0c\u8001\u5b50\u5f88\u53ef\u80fd\u5c31\u4f1a\u8bf4\u5b9e\u8bdd\u4e86\uff01\u201d \u3000\u3000\u8d3e\u82f1\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u94f6\u8863\u4eba\u7b11\u9053\uff1a\u201c\u5598\u6c14\u7684\u65f6\u5019\uff0c\u8001\u5b50\u4ed4\u7ec6\u60f3\u4e86\u60f3\uff0c\u8fd8\u662f\u4e0d\u80fd\u8bf4\uff01\u201d \u3000\u3000\u8d3e\u82f1\u7684\u8138\u4e00\u4e0b\u6c14\u5f97\u8840\u7ea2\uff0c\u8f6c\u773c\u53c8\u53d8\u5f97\u94c1\u9752\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u770b\u51fa\u6765\u4ed6\u662f\u52a8\u4e86\u6740\u5fc3\u4e86\uff01 \u3000\u3000\u679c\u7136\uff0c\u8d3e\u82f1\u9634\u68ee\u68ee\u5730\u9053\uff1a\u201c\u4f60\u60f3\u6b7b\uff0c\u5bb9\u6613\u5f97\u5f88\uff01\u201d \u3000\u3000\u4ed6\u7684\u53f3\u638c\u91cd\u91cd\u5411\u524d\u4e00\u9001\uff01 \u3000\u3000\u5929\u67ab\u9053\u957f\u4e00\u628a\u62c9\u5f00\u4ed6\uff0c\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\uff0c\u4f60\u51b7\u9759\u4e00\u4e9b\uff01\u201d \u3000\u3000\u8d3e\u82f1\u54ac\u7259\u5207\u9f7f\u5730\u76ef\u7740\u94f6\u8863\u4eba\u3002 \u3000\u3000\u94f6\u8863\u4eba\u5374\u662f\u6ee1\u9762\u7b11\u5bb9\uff0c\u6ee1\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u8d3e\u67d0\u6df1\u6df1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u7ad9\u8d77\u8eab\uff0c\u9053\uff1a\u201c\u6765\u4eba\uff0c\u628a\u4ed6\u62d6\u56de\u7262\u623f\u53bb\uff0c\u4e09\u5929\u4e0d\u7ed9\u4ed6\u996d\u5403\uff0c\u4e5f\u4e0d \u8bb8\u7ed9\u4ed6\u6c34\u559d\uff01\u201d \u3000\u3000\u536b\u58eb\u4eec\u9f50\u58f0\u7b54\u5e94\uff0c\u5c06\u94f6\u8863\u4eba\u67b6\u8d77\u6765\uff0c\u50cf\u62d6\u4e00\u6761\u6b7b\u72d7\u4f3c\u5730\u62d6\u8d70\u4e86\u3002 \u3000\u3000\u8d3e\u57fa\u6014\u6014\u5730\u770b\u7740\u5730\u4e0a\uff0c\u597d\u534a\u5929\u90fd\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u53f9\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\uff0c\u8fd9\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u5176\u5b9e\u4ed6\u4e5f\u4e0d\u662f\u9aa8\u5934\u786c\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u4f1a\u800d\u65e0\u8d56\uff0c\u53cd \u800c\u4e0d\u597d\u5bf9\u4ed8\u3002\u201d \u3000\u3000\u8d3e\u82f1\u82e6\u7b11\u9053\uff1a\u201c\u6562\u62ff\u81ea\u5df1\u7684\u6027\u547d\u800d\u65e0\u8d56\uff0c\u4e5f\u662f\u5f88\u8981\u4e00\u70b9\u52c7\u6c14\u7684\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4e5f\u4e0d\u80fd\u7b97\u662f\u52c7\u6c14\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e00\u80a1\u6cfc\u76ae\u72e0\u52b2\u3002\u201d \u3000\u3000\u8d3e\u82f1\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u9053\u957f\uff0c\u4f60\u4e5f\u4e0d\u7528\u5b89\u6170\u6211\uff0c\u8fc7\u4e0a\u4e2a\u4e09\u56db\u5929\uff0c\u7b49\u4ed6\u997f\u5f97\u5feb\u6655\u4e86\uff0c\u6211\u518d \u6765\u5ba1\u4ed6\uff0c\u6211\u5c31\u4e0d\u4fe1\uff0c\u5ba1\u4e0d\u51fa\u4ed6\u7684\u5b9e\u8bdd\u6765\uff01\u201d \u3000\u3000\u4e8c\u4eba\u4e00\u540c\u8d70\u51fa\u4e86\u7262\u623f\uff0c\u6765\u5230\u5927\u5385\uff0c\u5374\u6ca1\u6709\u89c1\u5230\u91d1\u767e\u5e74\uff0c\u53ea\u6709\u7f57\u6d6e\u4e09\u5251\u6b63\u5728\u5385\u5916\u5b88\u536b\u3002 \u3000\u3000\u8d3e\u82f1\u95ee\u9053\uff1a\u201c\u4e1c\u4e3b\u5462\uff1f\u4ed6\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u8d3e\u91c7\u548c\u5929\u67ab\u9053\u957f\u6709\u8981\u4e8b\u5411\u4ed6\u62a5\u544a\u3002\u201d \u3000\u3000\u7f57\u6d6e\u4e09\u5251\u4e4b\u4e00\u9053\uff1a\u201c\u4e1c\u4e3b\u5728\u5927\u5c0f\u59d0\u90a3\u91cc\uff0c\u4ed6\u8bf4\uff0c\u8981\u662f\u8d3e\u603b\u7ba1\u548c\u5929\u67ab\u9053\u957f\u5ba1\u95ee\u5b8c\u4e86\uff0c\u5c31\u53bb \u540e\u9662\u89c1\u4ed6\u3002\u201d \u3000\u3000\u8d3e\u82f1\u548c\u5929\u67ab\u9053\u957f\u521a\u8d70\u5230\u540e\u9662\u95e8\u5916\uff0c\u5374\u89c1\u91d1\u767e\u5e74\u6b63\u4ece\u91cc\u9762\u51fa\u6765\uff0c\u6ee1\u8138\u62c5\u5fe7\u4e4b\u8272\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u5927\u5c0f\u59d0\u8fd8\u662f\u8336\u996d\u4e0d\u601d\u5417\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u53f9\u6c14\u9053\uff1a\u201c\u5509\uff01\u8fd9\u4e2a\u4e2b\u5934\uff0c\u8001\u592b\u529d\u4e86\u534a\u5929\uff0c\u5979\u4e5f\u53ea\u559d\u4e86\u4e00\u5c0f\u53e3\u53c2\u6c64\u3002\u201d \u3000\u3000\u4ed6\u6446\u4e86\u6446\u624b\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u5148\u4e0d\u63d0\u5b83\uff0c\u4f60\u4eec\u5ba1\u51fa\u4e2a\u7ed3\u679c\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u771f\u662f\u60ed\u6127\u5f97\u5f88\uff0c\u94f6\u8863\u4eba\u7684\u9aa8\u5934\u5f88\u786c\uff0c\u813e\u6c14\u5f88\u81ed\uff0c\u5c31\u662f\u4e0d\u8bf4\u5b9e\u8bdd\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\u5df2\u7528\u4e0a\u4e86\u641c\u9b42\u5927\u6cd5\uff0c\u4e5f\u6ca1\u80fd\u964d\u670d\u4e8e\u4ed6\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u5403\u60ca\u5730\u9053\uff1a\u201c\u8fd9\u4eba\u53ef\u771f\u5389\u5bb3\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4ed6\u4e0d\u8bf4\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u53ea\u662f\u53e3\u53e3\u58f0\u58f0\u8bf4\u6765\u8981\u56de\u539f\u672c\u5c5e\u4e8e\u4ed6\u4eec\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u76b1\u7709\u9053\uff1a\u201c\u4ed6\u8bf4\u4ec0\u4e48\uff1f\u4ed6\u7684\u610f\u601d\u662f\u4e09\u5b9d\u662f\u4ed6\u4eec\u7684\u4e1c\u897f\uff1f\u771f\u662f\u7b11\u8bdd\uff01\u4e09\u5b9d\u53ef\u662f\u8001\u592b \u4e0d\u60dc\u91cd\u91d1\u6c42\u8d2d\u5f97\u6765\uff0c\u548c\u4ed6\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u5df2\u7ecf\u4e0d\u7528\u518d\u95ee\u4e86\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u4e09\u5b9d\u662f\u68a6\u5e7b\u4e4b\u5200\u60f3\u8981\u4e4b\u7269\uff0c\u94f6\u8863\u4eba\u6b64\u884c\u4e5f\u662f\u8981\u7d22\u53d6\u4e09\u5b9d\uff0c\u53ef\u4ee5\u80af\u5b9a\uff0c\u4ed6\u80af\u5b9a \u548c\u68a6\u5e7b\u4e4b\u5200\u6709\u5173\u7cfb\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u4ed4\u7ec6\u770b\u4e86\u4ed6\u51e0\u773c\uff0c\u9053\uff1a\u201c\u6709\u9053\u7406\uff0c\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u4eba\u67ab\u9053\u957f\u9053\uff1a\u201c\u68a6\u5e7b\u4e4b\u5200\u8fdf\u8fdf\u4e0d\u73b0\u8eab\uff0c\u6211\u4eec\u53c8\u627e\u4e0d\u5230\u4ed6\u7684\u884c\u8e2a\uff0c\u8be5\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u662f\u554a\uff0c\u627e\u4e0d\u5230\u68a6\u5e7b\u4e4b\u5200\uff0c\u4e8b\u60c5\u603b\u662f\u6709\u4e9b\u4e3a\u96be\uff0c\u4e8c\u4f4d\u4e5f\u7d2f\u4e86\uff0c\u6211\u4eec\u53bb\u82b1\u5385\uff0c \u53eb\u4eba\u5907\u4e9b\u9152\u83dc\uff0c\u8fb9\u5403\u8fb9\u8c08\u5427\u3002\u201d \u3000\u3000\u4e09\u4eba\u5728\u82b1\u5385\u996e\u9152\uff0c\u4e00\u8fb9\u4f4e\u58f0\u5546\u8bae\u7740\u3002 \u3000\u3000\u9152\u81f3\u534a\u9163\uff0c\u5ffd\u89c1\u94c1\u62f3\u4e25\u65b9\u614c\u5fd9\u6025\u706b\u5730\u51b2\u4e86\u8fdb\u6765\uff0c\u9762\u4e0a\u795e\u8272\u5341\u5206\u7d27\u5f20\u3002 \u3000\u3000\u8d3e\u82f1\u6c89\u58f0\u9053\uff1a\u201c\u51fa\u4e86\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d \u3000\u3000\u4e25\u65b9\u9053\uff1a\u201c\u4e1c\u4e3b\uff0c\u603b\u7ba1\uff0c\u90a3\u4e2a\u94f6\u8863\u4eba\u4e0d\u77e5\u5982\u4f55\u9003\u8131\u51fa\u4e86\u5730\u7262\uff0c\u5df2\u7ecf\u4f24\u4e86\u5341\u51e0\u540d\u5f1f\u5144\u4e86\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u4eba\u5403\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u73b0\u5728\u60c5\u51b5\u600e\u4e48\u6837\u4e86\uff1f\u6293\u4f4f\u4ed6\u4e86\u5417\uff1f\u201d \u3000\u3000\u4e25\u65b9\u9053\uff1a\u201c\u5bd2\u5c71\u56db\u5200\u548c\u7f57\u6d6e\u4e09\u5251\u5df2\u5728\u56f4\u653b\u4ed6\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u4f3c\u4e4e\u4e5f\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u5feb\u5200\u738b \u526a\u548c\u9a6c\u6c0f\u5144\u5f1f\u6b63\u5728\u4e00\u65c1\u52a9\u9635\uff01\u201d \u3000\u3000\u8d3e\u82f1\u7ad9\u8d77\u8eab\uff0c\u9053\uff1a\u201c\u4e1c\u4e3b\uff0c\u6211\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u5fd9\u9053\uff1a\u201c\u6211\u4e5f\u53bb\u3002\u201d \u3000\u3000\u4ed6\u5bf9\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u9053\u957f\uff0c\u54b1\u4eec\u4e00\u8d77\u53bb\u3002\u201d \u3000\u3000------------- \u3000\u3000\u5b64\u5251\u751f  \u626b\u63cf\u3000  cndy001 zhuyj(\u6731\u836b\u9636)  OCR  \uff0c\u65e7\u96e8\u697c\u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u4e8c\u5341\u4e8c\u7ae0 \u3000\u3000\u56db\u4eba\u8d76\u5230\u5ead\u9662\u4e4b\u4e2d\u65f6\uff0c\u5374\u89c1\u5730\u4e0a\u5df2\u8eba\u5012\u4e86\u8fd1\u5341\u540d\u536b\u58eb\uff0c\u94f6\u8863\u4eba\u6b63\u5728\u5bd2\u5c71\u56db\u5200\u548c\u7f57\u6d6e\u4e09\u5251 \u7684\u5305\u56f4\u4e4b\u4e2d\u5de6\u51b2\u53f3\u7a81\uff01 \u3000\u3000\u8d3e\u82f1\u649e\u5f00\u94c1\u6247\uff0c\u98de\u8eab\u76f4\u51b2\u8fc7\u53bb\uff0c\u5927\u559d\u9053\uff1a\u201c\u90fd\u9000\u4e0b\uff0c\u770b\u8d3e\u67d0\u6765\u5bf9\u4ed8\u4ed6\uff01\u201d \u3000\u3000\u94f6\u8863\u4eba\u4e00\u56de\u5934\u770b\u89c1\u4e86\u8d3e\u82f1\uff0c\u957f\u7b11\u4e00\u58f0\uff0c\u5df2\u65bd\u8eab\u98de\u8d77\uff0c\u5728\u534a\u7a7a\u4e2d\u4e00\u626c\u624b\uff0c\u6253\u51fa\u4e00\u5927\u628a\u94f6\u5149 \u95ea\u95ea\u7684\u7ec6\u9488\uff01 \u3000\u3000\u201c\u6ee1\u5929\u82b1\u96e8\u843d\u56db\u65b9\uff01\u201d\u8d3e\u82f1\u94c1\u6298\u6247\u6325\u51fa\u4e00\u56e2\u6247\u5f71\uff0c\u62a4\u4f4f\u4e86\u5468\u8eab\u8981\u5bb3\uff0c\u5927\u559d\u9053\uff1a\u201c\u6ce8\u610f\u4fdd \u62a4\u4e1c\u4e3b\uff01\u201d \u3000\u3000\u90a3\u4e00\u5927\u628a\u7ec6\u94f6\u9488\u6709\u4e00\u5927\u534a\u90fd\u662f\u76f4\u5954\u91d1\u767e\u5e74\u800c\u53bb\u7684\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u62bd\u51fa\u957f\u5251\uff0c\u4f46\u89c1\u5251\u82b1\u6735\u6735\uff0c\u5728\u91d1\u77f3\u5e74\u56db\u5468\u95ea\u52a8\uff0c\u5c06\u4f55\u6765\u7684\u94f6\u9488\u5c3d\u51fb\u6563\u98de\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u4e00\u5fd9\u4e71\u95ee\uff0c\u94f6\u8863\u4eba\u7684\u8eab\u5f62\u5728\u534a\u7a7a\u4e00\u5c55\uff0c\u5bbd\u5927\u7684\u94f6\u888d\u5728\u534a\u7a7a\u4e2d\u5c55\u5f00\uff0c\u6574\u4e2a\u4eba\u4fbf\u5982 \u4e00\u53ea\u94f6\u8272\u7684\u5927\u9e4f\uff0c\u98de\u51fa\u4e86\u91d1\u5e9c\u7684\u56f4\u5899\u3002 \u3000\u3000\u9a6c\u6c0f\u5144\u5f1f\uff0c\u5feb\u5200\u738b\u526a\u52c3\u8eab\u800c\u8d77\uff0c\u7728\u773c\u95f4\u5df2\u63a0\u4e0a\u56f4\u5899\uff0c\u60f3\u5411\u5916\u8ffd\u53bb\u3002 \u3000\u3000\u8d3e\u82f1\u53f9\u6c14\u9053\uff1a\u201c\u6162\u6162\uff01\u4e0d\u7528\u8ffd\u4e86\uff0c\u8ffd\u4e0a\u4e86\u4e5f\u6ca1\u6709\u7528\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u5012\u662f\u5f88\u9547\u5b9a\uff0c\u8d70\u8fc7\u6765\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\uff0c\u4f60\u6ca1\u6709\u8ba9\u94f6\u9488\u4f24\u7740\u5427\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u6ca1\u6709\u3002\u591a\u8c22\u4e1c\u4e3b\u5173\u5fc3\u3002\u201d \u3000\u3000\u4ed6\u626b\u89c6\u4e86\u4f17\u4eba\u4e00\u773c\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u53ea\u662f\u6211\u5f88\u60f3\u4e0d\u901a\uff0c\u8fd9\u94f6\u8863\u4eba\u5982\u4f55\u80fd\u81ea\u5b88\u536b\u91cd\u91cd\u7684\u5730\u7262\u91cc \u9003\u8131\u51fa\u6765\u7684\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u770b\u6765\uff0c\u5e9c\u5185\u6709\u5185\u5978\uff01\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u4e0d\u9519\uff01\u4e1c\u4e3b\uff0c\u54b1\u4eec\u4e00\u5b9a\u8981\u4e25\u52a0\u8ffd\u67e5\uff0c\u627e\u51fa\u5185\u5978\u6765\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u70b9\u70b9\u5934\uff0c\u53c8\u53f9\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4f55\u65f6\u53d1\u73b0\u8fd9\u94f6\u8863\u4eba\u7684\uff1f\u201d \u3000\u3000\u5bd2\u5c71\u56db\u5200\u9053\uff1a\u201c\u6211\u4eec\u6b63\u5728\u5e9c\u4e2d\u5de1\u903b\uff0c\u7a81\u7136\u89c1\u5230\u94f6\u8863\u4eba\u4ece\u89c2\u9e64\u697c\u65b9\u5411\u8dd1\u6765\u2026\u2026\u201d \u3000\u3000\u8d3e\u82f1\u9762\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u7b49\u4e00\u7b49\uff0c\u4f60\u4eec\u8bf4\u4ed6\u662f\u4ece\u54ea\u4e2a\u65b9\u5411\u8dd1\u6765\u7684\uff1f\u201d \u3000\u3000\u5bd2\u5c71\u56db\u5200\u9053\uff1a\u201c\u89c2\u9e64\u697c\u554a\u3002\u201d \u3000\u3000\u4e70\u82f1\u9053\uff1a\u201c\u4e0d\u597d\uff01\u4e1c\u4e3b\uff0c\u54b1\u4eec\u5feb\u53bb\u89c2\u9e64\u697c\u770b\u770b\uff0c\u83ab\u8981\u88ab\u8fd9\u94f6\u8863\u4eba\u8d81\u673a\u7a83\u53d6\u4e86\u4e09\u5b9d\u2026\u2026\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u4ed6\u4e0e\u5929\u67ab\u9053\u957f\uff0c\u5feb\u5200\u738b\u526a\u5df2\u98de\u8eab\u63a0\u8d77\uff0c\u8054\u8882\u5411\u89c2\u9e64\u697c\u6251\u53bb\u3002 \u3000\u3000\u91d1\u767e\u5e74\u4e5f\u5f88\u5feb\u8d76\u5230\u4e86\u89c2\u9e64\u697c\uff0c\u53ea\u89c1\u8d3e\u82f1\u7b49\u4eba\u5df2\u8be2\u95ee\u8d1f\u8d23\u89c2\u9e64\u697c\u9632\u52a1\u7684\u536b\u58eb\u4eec\u3002 \u3000\u3000\u94c1\u7fce\u4e5f\u5306\u5306\u8d76\u6765\u4e86\u3002 \u3000\u3000\u636e\u89c2\u9e64\u697c\u56db\u5468\u7684\u536b\u58eb\u4eec\u8bf4\uff0c\u5e76\u672a\u53d1\u73b0\u6709\u4efb\u4f55\u5f02\u5e38\u60c5\u51b5\uff0c\u91d1\u767e\u5e74\u3001\u8d3e\u82f1\u3001\u5929\u67ab\u9053\u957f\u7b49\u4eba\u624d \u7b97\u662f\u677e\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u94c1\u7fce\u95ee\u660e\u4e86\u53d1\u751f\u7684\u4e8b\u540e\uff0c\u9053\uff1a\u201c\u8fd8\u662f\u4e0a\u53bb\u770b\u4e00\u770b\u4e09\u5b9d\u662f\u5426\u8fd8\u5728\u624d\u597d\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u94c1\u6355\u5934\u4f55\u51fa\u6b64\u8a00\uff1f\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u636e\u94c1\u67d0\u5206\u6790\uff0c\u8fd9\u4e2a\u94f6\u8863\u4eba\u6781\u6709\u53ef\u80fd\u5c31\u662f\u68a6\u5e7b\u4e4b\u5200\u672c\u4eba\uff0c\u4ed6\u6545\u610f\u88c5\u4f5c\u6b66\u529f\u4e0d\u5982 \u8d3e\u603b\u7ba1\uff0c\u6218\u8d25\u88ab\u64d2\uff0c\u662f\u60f3\u501f\u6b64\u7559\u5728\u91d1\u5e9c\uff0c\u8d81\u673a\u7a83\u53d6\u4e09\u5b9d\uff0c\u8bf8\u4f4d\u8bf7\u60f3\u4e00\u60f3\uff0c\u4ed6\u65e2\u80fd\u5728\u5b88\u536b\u68ee\u4e25 \u7684\u5730\u7262\u8131\u8eab\uff0c\u5b88\u697c\u7684\u536b\u58eb\u53c8\u600e\u4e48\u4f1a\u53d1\u73b0\u4ed6\u5df2\u6e9c\u8fdb\u697c\u4e2d\u5462\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u94c1\u6355\u5934\u8bf4\u7684\u5f88\u6709\u9053\u7406\uff0c\u8001\u592b\u8fd9\u5c31\u4e0a\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u4ed6\u6025\u5fd9\u5fd9\u4e0a\u4e86\u697c\uff0c\u4e0d\u4e00\u4f1a\u513f\uff0c\u4f17\u4eba\u4fbf\u542c\u89c1\u4ed6\u4e00\u58f0\u60ca\u547c\uff0c\u7d27\u63a5\u7740\u770b\u89c1\u4ed6\u6025\u5306\u5306\u8dd1\u4e0b\u505a\u6765\uff0c \u9762\u8272\u82cd\u767d\u5730\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u4e09\u5b9d\u5df2\u7ecf\u4e0d\u89c1\u4e86\uff01\u201d \u3000\u3000\u4f17\u4eba\u90fd\u662f\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u8d3e\u82f1\u7a81\u7136\u8dea\u4e0b\u4e86\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e09\u5b9d\u88ab\u7a83\uff0c\u662f\u8d3e\u67d0\u5931\u804c\uff0c\u8bf7\u4e1c\u4e3b\u5904\u7f5a\uff0c\u8d3e\u82f1\u5c31\u662f\u6b7b\uff0c\u4e5f\u65e0 \u51a4\u6068\u4e1c\u4e3b\u4e4b\u610f\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9762\u4e0a\u9634\u6674\u4e0d\u5b9a\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\u5feb\u8bf7\u8d77\u6765\uff0c\u6240\u8c13\u667a\u8005\u5343\u8651\uff0c\u5fc5\u6709\u4e00\u5931\uff0c\u8001\u592b \u53c8\u600e\u4f1a\u602a\u4f60\uff01\u94c1\u6355\u5934\u5206\u6790\u7684\u5bf9\uff0c\u94f6\u8863\u4eba\u80af\u5b9a\u5c31\u662f\u68a6\u5e7b\u4e4b\u5200\u672c\u4eba\uff0c\u6211\u4eec\u90fd\u88ab\u4ed6\u9a97\u4e86\uff01\u201d \u3000\u3000\u8d3e\u82f1\u7ad9\u8d77\u8eab\uff0c\u60ed\u6127\u5730\u9053\uff1a\u201c\u4e1c\u4e3b\u867d\u7136\u4e0d\u602a\uff0c\u53ef\u8d3e\u82f1\u5fc3\u91cc\u5f88\u662f\u4e0d\u5b89\u3002\u4e1c\u4e3b\uff0c\u8bf7\u4f60\u7ed9\u6211\u4e00\u4e2a \u6708\u7684\u65f6\u95f4\uff0c\u5982\u679c\u8d3e\u67d0\u627e\u4e0d\u5230\u68a6\u5e7b\u4e4b\u5200\uff0c\u593a\u56de\u4e09\u5b9d\uff0c\u81ea\u4f1a\u8ba9\u4eba\u5c06\u8d3e\u67d0\u7684\u4eba\u5934\u9001\u56de\u91d1\u5e9c\uff0c\u5411\u4e1c\u4e3b \u8c22\u7f6a\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u4e00\u5446\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff0c\u8d3e\u603b\u7ba1\u8981\u8d70\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u662f\u7684\uff0c\u8bf7\u4e1c\u4e3b\u7b54\u5e94\u8d3e\u67d0\u5728\u5e9c\u4e2d\u6311\u9009\u516d\u540d\u9ad8\u624b\uff0c\u8ffd\u5bfb\u68a6\u5e7b\u4e4b\u5200\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\u4e0d\u8981\u610f\u6c14\u7528\u4e8b\uff0c\u4f60\u8d70\u4e86\uff0c\u91d1\u5e9c\u7684\u9632\u536b\u7531\u8c01\u6765\u5b89\u6392\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u94c1\u7fce\u5ffd\u7136\u6b63\u8272\u9053\uff1a\u201c\u68a6\u5e7b\u4e4b\u5200\u66fe\u7559\u4e0b\u56db\u53e5\u8bd7\u8c12\uff0c\u73b0\u5728\uff0c\u4ed6\u4e09\u5b9d\u5df2\u7ecf\u5f97\u624b\uff0c\u770b\u6765\uff0c\u4ed6\u662f\u8981 \u5bf9\u5341\u5927\u5bcc\u8c6a\u4e0b\u624b\u4e86\uff01\u94c1\u67d0\u5f97\u8d76\u56de\u5e9c\u8859\u4e00\u8d9f\uff0c\u628a\u8fd9\u4e2a\u6d88\u606f\u7980\u544a\u738b\u5927\u4eba\u548c\u7a0b\u603b\u6355\u5934\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u94c1\u6355\u5934\uff0c\u8001\u592b\u73b0\u5728\u5fc3\u4e71\u5982\u9ebb\uff0c\u4e5f\u4e0d\u77e5\u8be5\u5982\u4f55\u662f\u597d\uff0c\u4f60\u56de\u5e9c\u8859\u7980\u544a\u5b8c\u4e86\u4e4b\u540e\uff0c \u6700\u597d\u80fd\u5c3d\u5feb\u8d76\u56de\u6765\uff0c\u8001\u592b\u5c1a\u6709\u8bf8\u591a\u4e8b\u60c5\u8981\u4e0e\u4f60\u5546\u8bae\u3002\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u94c1\u67d0\u4e00\u5b9a\u5c3d\u5feb\u8d76\u56de\uff01\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u5982\u98de\u79bb\u53bb\u3002 \u3000\u3000\u91d1\u767e\u5e74\u770b\u7740\u94c1\u7fce\u8fdc\u53bb\u7684\u80cc\u5f71\uff0c\u5bf9\u8d3e\u82f1\u9053\uff1a\u201c\u8ffd\u5bfb\u68a6\u5e7b\u4e4b\u5200\u7684\u4efb\u52a1\uff0c\u5c31\u4ea4\u7ed9\u5929\u67ab\u9053\u957f\u5427\u3002 \u8d3e\u603b\u7ba1\u8fd8\u662f\u7559\u5728\u5e9c\u4e2d\uff0c\u8d1f\u8d23\u9632\u5b88\u3002\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u662f\uff0c\u8d3e\u82f1\u542c\u4ece\u4e1c\u4e3b\u5b89\u6392\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u68a6\u5e7b\u4e4b\u5200\u4e09\u5b9d\u5230\u624b\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u518d\u5728\u626c\u5dde\u4e45\u7559\uff0c\u8001\u9053\u8fd9\u5c31\u53bb \u4e86\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u70b9\u5934\u9053\uff1a\u201c\u5e9c\u4e2d\u6240\u6709\u4eba\u624b\uff0c\u4efb\u9053\u957f\u6311\u9009\uff0c\u4e00\u5b9a\u8981\u627e\u51fa\u68a6\u5e7b\u4e4b\u5200\u6765\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4e1c\u4e3b\u653e\u5fc3\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u53f9\u6c14\u9053\uff1a\u201c\u68a6\u5e7b\u4e4b\u5200\u679c\u7136\u5389\u5bb3\uff0c\u8fd9\u4e00\u6b7b\u4e2d\u6c42\u6d3b\u4e4b\u8ba1\uff0c\u7adf\u7136\u628a\u6211\u4eec\u5168\u90fd\u9a97\u4f4f\u4e86\uff01\u53ea \u662f\u585e\u7fc1\u5931\u9a6c\uff0c\u7109\u77e5\u975e\u798f\uff0c\u4ed6\u62ff\u8d70\u4e86\u4e09\u5b9d\uff0c\u5fc5\u7136\u53c8\u4f1a\u6000\u74a7\u5176\u7f6a\uff0c\u88ab\u5176\u4ed6\u6c5f\u6e56\u4eba\u58eb\u8ffd\u6740\u2026\u2026\u201d \u3000\u3000\u4ed6\u6253\u4f4f\u8bdd\u5934\uff0c\u5bf9\u8d3e\u82f1\u9053\uff1a\u201c\u8001\u592b\u4e5f\u6709\u4e00\u4e2a\u8ba1\u5212\uff0c\u8d3e\u603b\u7ba1\uff0c\u54b1\u4eec\u4e00\u8d77\u5546\u8bae\u4e00\u4e0b\uff0c\u770b\u5176\u95f4\u662f \u5426\u4f1a\u6709\u4ec0\u4e48\u6f0f\u6d1e\u3002\u201d \u3000\u3000\u8d3e\u82f1\u606d\u58f0\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1 \u3000\u3000\u867d\u8bf4\u5f53\u524d\u7684\u5f62\u52bf\u5341\u5206\u51f6\u9669\uff0c\u4f46\u5e9c\u8859\u5185\u8fd8\u663e\u9887\u6709\u4e9b\u559c\u6c14\u6d0b\u6d0b\u7684\u3002 \u3000\u3000\u4e00\u5927\u6e05\u65e9\uff0c\u5c0f\u6587\u7684\u623f\u95e8\u6253\u5f00\u4e86\u3002 \u3000\u3000\u524d\u51e0\u5929\u4e00\u76f4\u660f\u8ff7\u7684\u5c0f\u6587\u795e\u6e05\u6c14\u723d\u5730\u8d70\u51fa\u6765\uff0c\u663e\u7136\u89e3\u836f\u5341\u5206\u594f\u6548\uff0c\u4f53\u5185\u7684\u6bd2\u836f\u5df2\u7ecf\u5168\u90e8\u88ab \u5316\u89e3\u4e86\u3002 \u3000\u3000\u5979\u7acb\u523b\u53bb\u89c1\u7a0b\u5c0f\u8776\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u6b63\u4e0e\u5c0f\u96c5\u5728\u623f\u5185\u5f00\u804a\uff0c\u89c1\u5c0f\u6587\u8d70\u8fdb\u6765\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u5c0f\u6587\uff0c\u4f60\u597d\u4e86\uff1f\u201d \u3000\u3000\u5c0f\u96c5\u7b11\u563b\u563b\u5730\u9053\uff1a\u201c\u5c0f\u6587\u59d0\uff0c\u606d\u559c\u4f60\u5440\u3002\u201d \u3000\u3000\u5c0f\u6587\u7ea2\u4e86\u8138\uff0c\u9053\uff1a\u201c\u4f55\u559c\u4e4b\u6709\uff1f\u201d \u3000\u3000\u5c0f\u96c5\u7b11\u9053\uff1a\u201c\u4f60\u6bd2\u4f24\u5c3d\u597d\uff0c\u8fd9\u662f\u4e00\u559c\uff0c\u73b0\u5728\u4f60\u53c8\u5df2\u7ecf\u662f\u738b\u77e5\u5e9c\u7684\u5982\u592b\u4eba\uff0c\u66f4\u662f\u5927\u559c\uff0c\u600e \u4e48\u80fd\u4e0d\u606d\u559c\u4f60\u5462\uff1f\u201d \u3000\u3000\u5c0f\u6587\u8138\u66f4\u7ea2\uff0c\u5a07\u7f9e\u5730\u9053\uff1a\u201c\u5c0f\u96c5\uff0c\u4f60\u4e5f\u8981\u53d6\u7b11\u6211\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6b63\u58f0\u9053\uff1a\u201c\u4e0d\u8981\u53d6\u7b11\uff0c\u7684\u786e\u503c\u5f97\u606d\u559c\u554a\u3002\u738b\u5927\u4eba\u4ee5\u540e\u5e73\u6b65\u9752\u4e91\uff0c\u4f60\u8ddf\u7740\u4ed6\uff0c\u771f\u7684 \u4f1a\u6709\u505a\u8bf0\u547d\u592b\u4eba\u7684\u4e00\u5929\u5462\u3002\u201d \u3000\u3000\u5c0f\u6587\u773c\u5708\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u5c0f\u6587\u6c38\u8fdc\u90fd\u662f\u5c0f\u59d0\u7684\u4f8d\u5973\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e5f\u6709\u4e9b\u611f\u4f24\uff0c\u9053\uff1a\u201c\u5c0f\u6587\uff0c\u4f60\u7684\u5fc3\u6211\u660e\u767d\u3002\u4e0d\u8fc7\uff0c\u4ece\u4eca\u65e5\u8d77\uff0c\u4f60\u5df2\u4e3a\u4eba\u4e4b\u59bb\uff0c\u4ee5 \u540e\u4e0d\u8981\u518d\u8bf4\u8fd9\u4e9b\u50bb\u8bdd\u4e86\u3002\u201d \u3000\u3000\u5c0f\u6587\u770b\u4e86\u5979\u4e00\u773c\uff0c\u4e0d\u8bf4\u8bdd\uff0c\u6241\u4e86\u6241\u5c0f\u5634\uff0c\u7adf\u7136\u8981\u54ed\u51fa\u6765\u3002 \u3000\u3000\u5c0f\u96c5\u62c9\u8d77\u5c0f\u6587\u7684\u5c0f\u624b\uff0c\u51d1\u5728\u5979\u8033\u8fb9\u95ee\u9053\uff1a\u201c\u5c0f\u6587\u59d0\uff0c\u6628\u5929\u591c\u91cc\uff0c\u4f60\u4eec\u2026\u2026\uff1f\u201d \u3000\u3000\u5c0f\u6587\u7ea2\u7740\u8138\u767d\u4e86\u5979\u4e00\u773c\uff0c\u5a07\u7f9e\u65e0\u529b\u5730\u6253\u4e86\u5c0f\u96c5\u4e00\u4e0b\uff0c\u54ac\u4e86\u54ac\u9c9c\u7ea2\u7684\u6a31\u5507\uff0c\u4f4e\u7b11\u9053\uff1a\u201c\u7b49 \u4f60\u5ac1\u4eba\u65f6\uff0c\u4f60\u5c31\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u5c0f\u96c5\u4e0d\u7981\u4e5f\u7ea2\u4e86\u8138\uff0c\u201c\u5478\u201d\u4e86\u4e00\u53e3\uff0c\u8dd1\u5230\u7a0b\u5c0f\u8776\u8eab\u8fb9\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u5ac1\u4eba\uff0c\u6211\u4e00\u8f88\u5b50\u8ddf\u7740 \u5c0f\u59d0\uff01\u4e0d\u5ac1\u4eba\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u7740\u63a8\u4e86\u5979\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5c0f\u96c5\uff0c\u4f60\u4e5f\u4e0d\u8981\u5634\u786c\uff0c\u4f60\u4e5f\u603b\u6709\u60f3\u5ac1\u4eba\u7684\u90a3\u4e00\u5929\u3002\u201d \u3000\u3000\u5c0f\u96c5\u7ea2\u7740\u8138\uff0c\u6b6a\u7740\u5934\u770b\u7740\u7a0b\u5c0f\u8776\uff0c\u9053\uff1a\u201c\u5c0f\u59d0\u4e5f\u4e00\u5b9a\u6709\u5ac1\u4eba\u7684\u90a3\u4e00\u5929\u55bd\uff1f\u5c0f\u59d0\u8fd9\u4e48\u7f8e\uff0c \u771f\u4e0d\u77e5\u54ea\u4e2a\u7537\u4eba\u6709\u6b64\u8273\u798f\u4e86\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u5c06\u76ee\u5149\u8f6c\u5f00\uff0c\u5e7d\u5e7d\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u5c0f\u6587\u548c\u5c0f\u96c5\u90fd\u5947\u602a\u4e86\uff1a\u201c\u5c0f\u59d0\u4eca\u513f\u662f\u600e\u4e48\u5566\uff01\u7adf\u597d\u50cf\u6709\u6781\u91cd\u7684\u5fc3\u4e8b\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u51fa\u4e86\u4e00\u4f1a\u795e\uff0c\u5ffd\u7136\u8138\u4e0a\u5347\u8d77\u4e24\u6735\u7684\u6de1\u6de1\u7684\u7ea2\u6655\uff0c\u770b\u7740\u5c0f\u6587\u9053\uff1a\u201c\u5c0f\u6587\uff0c\u6211\u8ddf\u4f60\u8bf4\uff0c \u7b49\u4f1a\u513f\u4e0e\u56db\u5927\u6355\u5934\u76f8\u89c1\uff0c\u4ed6\u4eec\u4e5f\u4e00\u5b9a\u4f1a\u606d\u559c\u4f60\u3002\u4ed6\u4eec\u90a3\u51e0\u5f20\u5634\uff0c\u5411\u6765\u53e3\u6ca1\u906e\u62e6\uff0c\u4f46\u4ed6\u4eec\u80af\u5b9a \u90fd\u662f\u597d\u5fc3\uff0c\u66ff\u4f60\u9ad8\u5174\uff0c\u53ea\u662f\u4ed6\u4eec\u5f88\u7231\u5f00\u73a9\u7b11\uff0c\u4f60\u53ef\u4e0d\u8981\u4e0d\u9ad8\u5174\u554a\uff01\u201d \u3000\u3000\u5c0f\u6587\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u6211\u61c2\u7684\uff0c\u56db\u5927\u6355\u5934\u4e00\u5411\u89c6\u6211\u4eec\u4e3a\u59d0\u59b9\uff0c\u662f\u4e00\u5bb6\u4eba\u3002\u4ed6\u4eec\u7231\u5f00\u73a9\u7b11\uff0c\u90a3 \u662f\u4ed6\u4eec\u559c\u7231\u6211\u4eec\u4e4b\u6545\uff0c\u6211\u53ea\u4f1a\u9ad8\u5174\uff0c\u4e0d\u4f1a\u751f\u6c14\u7684\u3002\u201d \u3000\u3000\u770b\u6765\uff0c\u5c0f\u6587\u5728\u4e00\u591c\u4e4b\u95f4\uff0c\u5df2\u7ecf\u53d8\u5f97\u6210\u719f\u591a\u4e86\u3002 \u3000\u3000\u8fd9\u5927\u6982\u5c31\u662f\u5c11\u5973\u6210\u4e3a\u5c11\u5987\u540e\u7684\u5fc5\u7136\u53d8\u5316\u5427\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5ffd\u7136\u60f3\u8d77\u4e00\u4e8b\uff0c\u95ee\u5c0f\u6587\u9053\uff1a\u201c\u5c0f\u6587\uff0c\u4f60\u660f\u8ff7\u4e86\u5f88\u591a\u5929\uff0c\u4e00\u76f4\u6ca1\u6709\u5403\u4e1c\u897f\uff0c\u4f53\u529b\u4e0a \u4e00\u5b9a\u6709\u5f88\u5927\u7684\u8017\u8d39\uff0c\u5e94\u8be5\u597d\u597d\u5403\u4e00\u987f\uff0c\u8865\u5145\u8865\u5145\u4f53\u529b\u3002\u201d \u3000\u3000\u5c0f\u6587\u7b11\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4f60\u8fd9\u4e00\u8bf4\uff0c\u6211\u8fd8\u771f\u997f\u4e86\u3002\u201d \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u6211\u4e5f\u997f\u4e86\u3002\u54b1\u4eec\u53bb\u5927\u5385\u91cc\u5403\u65e9\u996d\u53bb\u5427\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u7b11\u9053\uff1a\u201c\u998b\u5634\u7684\u4e2b\u5934\uff01\u4f60\u653e\u5fc3\uff0c\u4eca\u513f\u65e9\u4e0a\u4e00\u5b9a\u6709\u4e9b\u7cbe\u7f8e\u7684\u626c\u5dde\u7ec6\u70b9\u53ef\u5403\u7684\uff01\u201d \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4f60\u4e5f\u6ca1\u51fa\u623f\u95e8\uff0c\u600e\u4e48\u4f1a\u77e5\u9053\u65e9\u4e0a\u6709\u597d\u5403\u7684\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u770b\u7740\u5c0f\u6587\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u60f3\u60f3\uff0c\u738b\u5927\u4eba\u600e\u4e48\u4f1a\u60f3\u4e0d\u5230\u5c0f\u6587\u4f53\u529b\u5f88\u865a\u5f31\uff0c\u8981\u5403 \u4e9b\u597d\u4e1c\u897f\u8fdb\u8865\u5462\uff01\u201d \u3000\u3000\u5c0f\u6587\u7684\u8138\u53c8\u7ea2\u4e86\uff0c\u5374\u662f\u7ea2\u5f97\u5341\u5206\u7684\u751c\u7f8e\uff0c\u5f53\u7136\u8fd8\u6709\u51e0\u5206\u7f9e\u6da9\u3002 \u3000\u3000\u8981\u77e5\u9053\uff0c\u5979\u5230\u5e95\u8fd8\u662f\u521d\u4e3a\u4eba\u59bb\u554a\u3002 \u3000\u3000\u4e09\u4eba\u8bf4\u8bf4\u7b11\u7b11\uff0c\u7f13\u6b65\u8d70\u5411\u5927\u5385\uff0c\u79bb\u5385\u95e8\u8fd8\u6709\u5341\u6765\u6b65\u8fdc\uff0c\u4fbf\u542c\u89c1\u5385\u4e2d\u7b11\u8bed\u55a7\u54d7\uff0c\u6b63\u662f\u5434\u94c1 \u5cf0\u3001\u5c91\u5578\u864e\u3001\u4e8e\u627f\u5fd7\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4e00\u89c1\u5c0f\u6587\uff0c\u4e09\u4eba\u4fbf\u90fd\u8fce\u4e0a\u6765\uff0c\u540c\u58f0\u7b11\u9053\uff1a\u201c\u606d\u559c\uff0c\u606d\u559c\u5440\uff01\u201d \u3000\u3000\u5c0f\u6587\u5fae\u5fae\u7b11\u7740\u9053\uff1a\u201c\u8c22\u8c22\u4e09\u4f4d\u5927\u6355\u5934\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u54df\uff0c\u5230\u5e95\u662f\u8fdb\u4e86\u738b\u5bb6\u95e8\u7684\u4eba\u4e86\uff0c\u8fd9\u5c31\u77e5\u9053\u8ddf\u54b1\u4eec\u5ba2\u6c14\u8d77\u6765\u4e86\uff0c\u5c0f\u6587\uff0c\u4f60\u4e0d \u89c9\u5f97\u8fd9\u6837\u5ba2\u6c14\u8d77\u6765\uff0c\u5f88\u6709\u4e9b\u751f\u5206\u7684\u611f\u89c9\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u8c01\u8ba9\u4f60\u4eec\u4e00\u5927\u65e9\u8d77\u5c31\u8d77\u6253\u8da3\u4eba\u5bb6\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u8fd9\u53ef\u5c31\u662f\u51a4\u6789\u6211\u4eec\u4e86\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u5c31\u662f\uff0c\u6211\u4eec\u662f\u770b\u89c1\u5c0f\u6587\u59d1\u5a18\u2026\u2026\u5662\uff0c\u4e0d\u5bf9\uff0c\u5e94\u8be5\u79f0\u738b\u592b\u4eba\u4e86\uff0c\u563f\u563f\u2026\u2026\u6211 \u4eec\u662f\u770b\u89c1\u738b\u592b\u4eba\u8eab\u4f53\u5eb7\u590d\uff0c\u5fc3\u91cc\u66ff\u5979\u9ad8\u5174\u561b\uff01\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u7b11\u7740\u9053\uff1a\u201c\u518d\u8bf4\uff0c\u5c0f\u6587\u7ec8\u8eab\u6709\u6258\uff0c\u4e0d\u662f\u559c\u4e8b\u4e00\u4ef6\u5417\uff1f\u96be\u9053\u6211\u4eec\u4e0d\u5e94\u8be5\u606d\u559c\u5979\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u597d\u597d\u597d\uff0c\u6211\u8bf4\u4e0d\u8fc7\u4f60\u4eec\uff0c\u4f60\u4eec\u90fd\u5bf9\uff0c\u662f\u6211\u9519\u4e86\uff0c\u884c\u4e86\u5427\uff1f\u201d \u3000\u3000\u8bf4\u7b11\u95f4\uff0c\u738b\u5c11\u537f\u548c\u5211\u623f\u5e08\u7237\u5f20\u5b9d\u5584\u4e5f\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u521a\u4e00\u8fdb\u5385\uff0c\u4e09\u5927\u6355\u5934\u4fbf\u90fd\u9f50\u8bf4\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u5927\u559c\u554a\uff0c\u4ec0\u4e48\u65f6\u5019\u8bf7\u6211\u4eec\u559d\u559c\u9152\u54c7\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u6ee1\u9762\u559c\u98ce\uff0c\u7b11\u772f\u772f\u5730\u56e2\u56e2\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u4eca\u5929\uff0c\u4eca\u5929\u4e00\u5b9a\u8bf7\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u7b11\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u4e00\u8a00\u4e3a\u5b9a\uff0c\u4e0d\u53ef\u8d56\u8d26\u54e6\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u5c91\u53f8\u4e3b\u4e5f\u771f\u662f\uff0c\u738b\u5927\u4eba\u5802\u5802\u626c\u5dde\u77e5\u5e9c\uff0c\u600e\u4e48\u4f1a\u8bf7\u4e0d\u8d77\u4e00\u987f\u9152\uff0c\u4e0d\u81f3\u4e8e\u8d56 \u8d26\u561b\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u5f20\u7740\u5634\u53ea\u662f\u7b11\uff0c\u8fde\u58f0\u9053\uff1a\u201c\u4e0d\u4f1a\u8d56\u8d26\uff0c\u4e09\u4f4d\u653e\u5fc3\uff0c\u51b3\u4e0d\u4f1a\u8d56\u8d26\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u8bf4\u5f52\u8bf4\uff0c\u7b11\u5f52\u7b11\uff0c\u738b\u5927\u4eba\uff0c\u6709\u4e00\u53e5\u8bdd\uff0c\u6211\u4e0d\u80fd\u4e0d\u8bf4\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u5434\u53f8\u4e3b\u6709\u8bdd\u4f46\u8bb2\u4e0d\u59a8\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u5c0f\u6587\u59d1\u5a18\u867d\u8bf4\u4e0e\u6211\u4eec\u51e0\u4eba\u975e\u4eb2\u975e\u6545\uff0c\u4f46\u6211\u4eec\u4e00\u76f4\u89c6\u5979\u4e3a\u59b9\u59b9\uff0c\u738b\u5927\u4eba\uff0c\u4f60 \u53ef\u4e0d\u8981\u4e8f\u5f85\u4e86\u5979\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u6b63\u8272\u9053\uff1a\u201c\u8bf8\u4f4d\u653e\u5fc3\uff0c\u738b\u5c11\u537f\u4eca\u540e\u82e5\u59d4\u5c48\u5c0f\u6587\u59d1\u5a18\uff0c\u6709\u5982\u6b64\u7b77\uff01\u201d \u3000\u3000\u4ed6\u634f\u8d77\u684c\u4e0a\u7684\u4e00\u53cc\u7af9\u7b77\uff0c\u4e00\u6298\u4e24\u65ad\uff01 \u3000\u3000\u5c0f\u6587\u5fc3\u91cc\u5341\u5206\u9707\u52a8\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5434\u6355\u5934\u4e5f\u5c31\u662f\u8fd9\u4e48\u8bf4\u8bf4\u4e48\uff0c\u738b\u2026\u2026\u4f60\u4f55\u82e6\u53d1\u8fd9\u6837\u7684\u6bd2\u8a93 \u9ebb\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u7b11\u9053\uff1a\u201c\u602a\u9053\u5973\u5927\u4e0d\u4e2d\u7559\uff0c\u5973\u751f\u5916\u5411\uff0c\u8fd9\u624d\u521a\u8fc7\u95e8\uff0c\u5c31\u77e5\u9053\u5e2e\u7740\u592b\u5bb6\u8bf4\u8bdd\u4e86\uff01\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u53c8\u5728\u5385\u4e2d\u5f15\u8d77\u4e86\u4e00\u9635\u5927\u7b11\u58f0\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u56db\u4e0b\u770b\u4e86\u770b\uff0c\u9053\uff1a\u201c\u54a6\uff0c\u5947\u602a\uff0c\u5c0f\u675c\u600e\u4e48\u8fd8\u6ca1\u6709\u8fc7\u6765\uff1f\u201d \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u60dc\u7389\u59d0\u4e5f\u8fd8\u6ca1\u6765\uff0c\u4e00\u5b9a\u662f\u8eb2\u7740\u7761\u61d2\u89c9\u5462\uff01\u6211\u53bb\u53eb\u5979\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6de1\u6de1\u5730\u9053\uff1a\u201c\u8ba9\u5979\u591a\u7761\u4e00\u4f1a\u513f\u5427\uff0c\u8fd9\u51e0\u5929\u6765\uff0c\u5979\u4e5f\u591f\u7d2f\u7684\u4e86\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u5c0f\u675c\u5462\uff1f\u83ab\u975e\u4e5f\u5728\u7761\u61d2\u89c9\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u7740\u7684\u4ec0\u4e48\u6025\uff0c\u8be5\u6765\u65f6\uff0c\u4ed6\u81ea\u7136\u5c31\u4f1a\u6765\u7684\u3002\u201d \u3000\u3000\u5927\u5bb6\u90fd\u628a\u6ce8\u610f\u529b\u653e\u5728\u5c0f\u6587\u548c\u738b\u5927\u4eba\u8eab\u4e0a\uff0c\u6240\u4ee5\u6ca1\u6709\u4eba\u80fd\u542c\u51fa\u7a0b\u5c0f\u8776\u8bdd\u4e2d\u7684\u90a3\u4e00\u8d34\u6de1\u6de1\u7684 \u9178\u5473\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u7b11\u9053\uff1a\u201c\u4e0d\u7ba1\u5c0f\u675c\u4e86\uff0c\u738b\u5927\u4eba\uff0c\u6211\u4eec\u53ef\u90fd\u997f\u4e86\uff0c\u5148\u4e0a\u4e9b\u70b9\u5fc3\u8ba9\u6211\u4eec\u5145\u9965\u5427\uff0c\u8fd9\u6837 \u7a7a\u7740\u809a\u5b50\u5e72\u7b49\uff0c\u4e0d\u5230\u5403\u4f60\u4eec\u7684\u559c\u9152\uff0c\u65e9\u5c31\u997f\u6b7b\u4e86\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u7b11\u9053\uff1a\u201c\u9a6c\u4e0a\u5c31\u6765\uff0c\u65e9\u996d\u9a6c\u4e0a\u5c31\u6765\u3002\u201d \u3000\u3000\u6b63\u8bf4\u7740\uff0c\u51e0\u540d\u8859\u5f79\u7aef\u7740\u51e0\u4e2a\u5927\u98df\u76d2\u8fdb\u6765\u4e86\uff0c\u684c\u5b50\u4e0a\u4e00\u4f1a\u513f\u5c31\u6446\u6ee1\u4e86\u7cbe\u7f8e\u7684\u7ec6\u70b9\uff0c\u8272\u3001\u9999\u3001 \u5473\u4ff1\u4f73\u7684\u5c0f\u83dc\u548c\u70ed\u817e\u817e\u3001\u9999\u55b7\u55b7\u7684\u7cb3\u7c73\u7ca5\u3002 \u3000\u3000\u4f17\u4eba\u56f4\u7740\u684c\u5b50\u5750\u4e0b\uff0c\u4e00\u8fb9\u804a\u7740\u5929\uff0c\u4e00\u8fb9\u5403\u4e86\u8d77\u6765\u3002\u738b\u5c11\u537f\u4e00\u8f6c\u8eab\uff0c\u4ece\u4e00\u4e2a\u98df\u76d2\u91cc\u6367\u51fa\u4e00 \u76cf\u9752\u82b1\u7684\u7ec6\u74f7\u676f\uff0c\u653e\u5230\u5c0f\u6587\u9762\u524d\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u6211\u8ba9\u4ed6\u4eec\u7ed9\u4f60\u716e\u7684\u7ea2\u67a3\u94f6\u8033\u4eba\u53c2\u6c64\uff0c\u8d81\u70ed\u559d \u4e86\uff0c\u8865\u8865\u8eab\u5b50\u5427\u3002\u201d \u3000\u3000\u5c0f\u6587\u7ea2\u7740\u8138\uff0c\u7ec6\u58f0\u7ec6\u6c14\u5730\u9053\uff1a\u201c\u8c22\u8c22\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u4f3c\u6a21\u4f3c\u6837\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8001\u5434\u6d3b\u4e86\u8fd9\u4e00\u5927\u628a\u5e74\u7eaa\uff0c\u76f4\u5230\u4eca\u5929\uff0c\u624d\u7b97\u662f\u771f\u6b63 \u660e\u767d\u4e86\u2018\u76f8\u656c\u5982\u5bbe\u2019\u3001\u2018\u4e3e\u6848\u9f50\u7709\u2019\u662f\u4ec0\u4e48\u610f\u601d\u554a\u3002\u201d \u3000\u3000\u4f17\u4eba\u53c8\u662f\u4f1a\u5fc3\u7684\u4e00\u7b11\u3002 \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u675c\u671b\u6708\u548c\u60dc\u7389\u4e00\u524d\u4e00\u540e\u4e5f\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u597d\u54c7\uff0c\u6709\u597d\u5403\u7684\uff0c\u5c31\u4e0d\u7b49\u6211\u4eec\u4e86\u3002\u201d \u3000\u3000\u5c0f\u96c5\u7b11\u9053\uff1a\u201c\u6211\u4eec\uff1f\u6211\u4eec\u662f\u8c01\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u4e00\u6014\uff0c\u60dc\u7389\u5374\u65e9\u7ea2\u4e86\u8138\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u96c5\uff0c\u4f60\u7684\u5634\u53ef\u771f\u5389\u5bb3\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6de1\u6de1\u9053\uff1a\u201c\u4e8c\u4f4d\u5750\u4e0b\u6765\u5403\u996d\u5427\uff0c\u5403\u5b8c\u4e86\u996d\uff0c\u6211\u8fd8\u6709\u91cd\u8981\u7684\u4e8b\u60c5\u8981\u544a\u8bc9\u5927\u5bb6\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6240\u8bf4\u7684\u201c\u91cd\u8981\u7684\u4e8b\u201d\uff0c\u5f53\u7136\u5c31\u662f\u6307\u6628\u5929\u591c\u91cc\u91d1\u5e9c\u91cc\u53d1\u751f\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4e0d\u5bf9\u5440\uff0c\u603b\u6355\u5934\uff0c\u5982\u679c\u8bf4\u94f6\u8863\u4eba\u5c31\u662f\u68a6\u5e7b\u4e4b\u5200\uff0c\u8fd9\u4e9b\u5929\u6765\u4ed6\u4e00\u76f4\u5173\u5728\u91d1\u5e9c \u5730\u7262\u91cc\uff0c\u53c8\u600e\u4e48\u4f1a\u4e0e\u603b\u6355\u5934\u78b0\u4e0a\u9762\u7684\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u636e\u94c1\u6355\u5934\u8bf4\uff0c\u91d1\u767e\u5e74\u3001\u8d3e\u82f1\u548c\u5929\u67ab\u9053\u957f\u90fd\u8ba4\u5b9a\u94f6\u8863\u4eba\u5c31\u662f\u68a6\u5e7b\u4e4b\u5200\uff0c\u800c\u4e09 \u5b9d\u4e5f\u7684\u786e\u88ab\u94f6\u8863\u4eba\u7a83\u8d70\u4e86\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u4e16\u4e0a\u4f1a\u6709\u4e24\u4e2a\u68a6\u5e7b\u4e4b\u5200\u55bd\uff1f\u8fd9\u6015\u662f\u4e0d\u53ef\u80fd\u5427\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5f88\u663e\u7136\uff0c\u8fd9\u4e24\u4e2a\u68a6\u5e7b\u4e4b\u5200\u4e2d\uff0c\u6709\u4e00\u4e2a\u662f\u522b\u4eba\u5047\u5192\u7684\uff0c\u53ea\u662f\uff0c\u54ea\u4e00\u4e2a\u662f\u771f\uff0c \u54ea\u4e00\u4e2a\u662f\u5047\u5462\uff1f\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u4ece\u5fd7\u5728\u4e09\u5b9d\u8fd9\u4e2a\u60c5\u51b5\u6765\u770b\uff0c\u94f6\u8863\u4eba\u5e94\u8be5\u662f\u771f\uff0c\u53ef\u51ed\u68a6\u5e7b\u4e4b\u5200\u7684\u6b66\u529f\u3001\u5b9e\u529b \u548c\u4ed6\u624b\u4e0b\u7684\u52bf\u529b\uff0c\u4ed6\u4f1a\u7528\u5982\u6b64\u5192\u9669\u7684\u624b\u6cd5\u6765\u53d6\u5f97\u4e09\u5b9d\u5417\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u5982\u679c\u94f6\u8863\u4eba\u662f\u68a6\u5e7b\u4e4b\u5200\uff0c\u90a3\u4e48\u60f3\u6740\u672c\u5e9c\u7684\u4eba\u5e94\u8be5\u5c31\u4e0d\u662f\u4ed6\u4e86\uff0c\u90a3\u4e48\u6740\u624b\u96c6 \u56e2\u7684\u9996\u8111\u96be\u9053\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\uff1f\u53c8\u4f1a\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u60c5\u51b5\u8d8a\u6765\u8d8a\u590d\u6742\u4e86\uff0c\u5982\u679c\u6211\u7684\u63a8\u60f3\u6ca1\u6709\u9519\uff0c\u5c31\u5728\u8fd9\u4e24\u5929\u5185\uff0c\u68a6\u5e7b\u4e4b\u5200\u80af\u5b9a \u4f1a\u4eb2\u81ea\u7387\u9886\u6740\u624b\uff0c\u518d\u5ea6\u88ad\u51fb\u5e9c\u8859\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u4e24\u773c\u4eae\u4e86\u4e00\u4e0b\uff0c\u95ee\u9053\uff1a\u201c\u54ea\u4e2a\u68a6\u5e7b\u4e4b\u5200\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5fae\u7b11\u7740\u9053\uff1a\u201c\u675c\u53f8\u4e3b\u8fd9\u4e2a\u95ee\u9898\u95ee\u5f97\u597d\uff0c\u4e0d\u8fc7\uff0c\u6682\u65f6\u6211\u4eec\u4e0d\u53bb\u60f3\u5b83\u3002\u675c\u53f8\u4e3b\uff0c\u4ee5\u4eca \u5929\u5f00\u59cb\uff0c\u4f60\u5c31\u8981\u5728\u5c0f\u6587\u7684\u6307\u5bfc\u4e0b\u4e60\u7ec3\u2018\u5251\u6d77\u6d74\u9b42\u2019\uff0c\u5230\u4e86\u5173\u952e\u65f6\u5019\uff0c\u6211\u4f1a\u548c\u60dc\u7389\u7528\u5185\u529b\u5e2e\u52a9 \u4f60\u7684\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u662f\u3002\u5c0f\u6587\u59d1\u5a18\uff0c\u6211\u4eec\u8d70\u5427\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u770b\u7740\u7a0b\u5c0f\u8776\uff0c\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u738b\u67d0\u6709\u4e00\u53e5\u8bdd\uff0c\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u738b\u5927\u4eba\u8bf4\u54ea\u91cc\u8bdd\uff0c\u6709\u8bdd\u5c31\u8bb2\u561b\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u738b\u67d0\u603b\u89c9\u5f97\u603b\u6355\u5934\u5bf9\u6848\u60c5\u7684\u4e86\u89e3\u6bd4\u738b\u67d0\u6df1\u5f97\u591a\uff0c\u4e0d\u77e5\u80fd\u5426\u900f\u9732\u4e00\u4e8c\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u610f\u5473\u6df1\u957f\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u6848\u60c5\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u5f53\u7136\u662f\u65b0\u623f\u8840\u6848\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u738b\u5927\u4eba\u80fd\u4e0d\u80fd\u5148\u8bf4\u8bf4\u4f60\u7684\u770b\u6cd5\u5462\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u65b0\u623f\u8840\u6848\u7684\u6848\u60c5\u770b\u4f3c\u7b80\u5355\uff0c\u4f46\u4ed4\u7ec6\u4e00\u60f3\uff0c\u7591\u70b9\u5374\u53c8\u592a\u591a\uff0c\u5982\u679c\u8bf4\u68a6\u5e7b\u4e4b\u5200 \u5c31\u662f\u51f6\u624b\uff0c\u90a3\u4ed6\u7684\u76ee\u7684\u4f55\u5728\u51b5\uff1f\u5728\u4e09\u5b9d\uff1f\u51ed\u4ed6\u7684\u6b66\u529f\uff0c\u81ea\u91d1\u5e9c\u53d6\u5f97\u4e09\u5b9d\u5e76\u4e0d\u662f\u4e00\u4ef6\u96be\u4e8b\uff0c\u4ed6 \u4e3a\u4ec0\u4e48\u6307\u4f7f\u91d1\u5c0f\u7709\u6740\u9a6c\u656c\u6587\u5462\uff1f\u8fd9\u6837\u4e00\u6765\uff0c\u4e0d\u662f\u5f04\u5de7\u6210\u62d9\uff0c\u8fc7\u65e9\u5f15\u8d77\u4e86\u5927\u5bb6\u7684\u6ce8\u610f\u4e86\u5417\uff1f\u6b64 \u7591\u70b9\u4e00\uff1b\u5982\u679c\u68a6\u5e7b\u4e4b\u5200\u662f\u51f6\u624b\uff0c\u90a3\u4e48\u4ed6\u53c8\u4e3a\u4f55\u5c61\u6b21\u5bf9\u738b\u67d0\u4e0b\u624b\u5462\uff1f\u6740\u5b98\u9020\u53cd\uff0c\u53ef\u662f\u682a\u8fde\u4e5d\u65cf \u7684\u5927\u7f6a\uff0c\u4ed6\u5982\u4f55\u8981\u8fd9\u6837\u505a\uff1f\u5982\u679c\u8bf4\u662f\u4e3a\u4e86\u4e09\u5b9d\uff0c\u663e\u7136\u8bf4\u4e0d\u901a\uff01\u6b64\u7591\u70b9\u4e4b\u4e8c\uff1b\u679c\u7136\u5982\u603b\u6355\u5934\u6240 \u8bf4\uff0c\u4ed6\u5236\u9020\u8840\u6848\uff0c\u662f\u4e3a\u4e86\u5f15\u8d77\u91d1\u767e\u5e74\u4e0e\u9a6c\u957f\u5c71\u706b\u62fc\uff0c\u6d89\u53ca\u738b\u67d0\uff0c\u5219\u8fd9\u6837\u5bf9\u4ed6\u4e00\u4e2a\u6c5f\u6e56\u4eba\u53c8\u6709 \u4ec0\u4e48\u597d\u5904\u5462\uff1f\u6b64\u7591\u70b9\u4e4b\u4e09\u3002\u6709\u6b64\u4e09\u70b9\uff0c\u738b\u67d0\u60f3\u6765\u6b64\u6848\u5f88\u53ef\u80fd\u4e0d\u4ec5\u4ec5\u662f\u6c5f\u6e56\u4eba\u6240\u4e3a\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u5934\u9053\uff1a\u201c\u738b\u5927\u4eba\u679c\u7136\u601d\u8651\u7f1c\u5bc6\uff0c\u4f60\u8bf4\u7684\u8fd9\u4e09\u4e2a\u7591\u70b9\uff0c\u5f52\u5230\u4e00\u5904\uff0c\u5176\u5b9e\u5df2\u6709\u4e0b\u4e00 \u4e2a\u7ed3\u8bba\u4e86\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4ec0\u4e48\u7ed3\u8bba\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u51f6\u624b\u5e76\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u4e00\u6014\uff0c\u7acb\u5373\u604d\u7136\u5927\u609f\u9053\uff1a\u201c\u7684\u786e\uff01\u53ea\u6709\u8fd9\u6837\uff0c\u5f88\u591a\u4e8b\u60c5\u624d\u8bb2\u5f97\u901a\uff0c\u53ef\u4e3a\u4ec0\u4e48\u51f6\u624b \u4e00\u518d\u5c06\u6211\u4eec\u7684\u6000\u7591\u5f15\u5230\u68a6\u5e7b\u4e4b\u5200\u8eab\u4e0a\u53bb\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u53ea\u6709\u4e00\u4e2a\u53ef\u80fd\uff0c\u90a3\u5c31\u662f\u5ac1\u7978\u4e8e\u4eba\u4e4b\u8ba1\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u8feb\uff1a\u201c\u68a6\u5e7b\u4e4b\u5200\u5e76\u975e\u6613\u4e0e\u4e4b\u8f88\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u4efb\u4eba\u5ac1\u7978\uff0c\u4e14\u53c8\u4e0d\u51fa\u9762\u8fa9\u767d\u5462\uff1f\u201d \u3000\u3000\u7a06\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u60c5\u51b5\u5f88\u5feb\u5c31\u4f1a\u660e\u6717\u4e86\uff0c\u4f60\u4e0d\u7528\u5fc3\u6025\uff0c\u54b1\u4eec\u73b0\u5728\u53ea\u8981\u9759\u89c2 \u53d8\u5316\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u603b\u6355\u5934\u5df2\u6709\u81ea\u5df1\u7684\u8ba1\u5212\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53ea\u662f\u8fd8\u6709\u51e0\u5904\u91cd\u8981\u5173\u8282\u6ca1\u6709\u53c2\u900f\uff0c\u6240\u4ee5\u6211\u4eec\u8fd8\u662f\u8981\u7b49\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4e0b\u5b98\u552f\u7a0b\u603b\u6355\u5934\u9a6c\u9996\u662f\u77bb\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\uff01\u5927\u4eba\u73b0\u5728\u5c31\u53bb\u4f11\u606f\u5427\u3002\u4ece\u4eca\u5929\u5f00\u59cb\uff0c\u54b1\u4eec\u8981\u767d\u5929\u4f11\u606f\uff0c\u591c\u95f4\u5c31\u5f97\u63d0\u9ad8 \u8b66\u60d5\uff0c\u4e25\u9632\u6740\u624b\u96c6\u56e2\u591c\u88ad\uff01\u201d \u3000\u3000\u5979\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u8865\u5145\u9053\uff1a\u201c\u5982\u679c\u6740\u624b\u96c6\u56e2\u6765\u5077\u88ad\uff0c\u4ed6\u4eec\u7684\u9996\u9886\u4e00\u5b9a\u4f1a\u81ea\u79f0\u68a6\u5e7b\u4e4b\u5200\uff0c\u5230 \u90a3\u65f6\uff0c\u5927\u5bb6\u7edd\u4e0d\u53ef\u8868\u73b0\u51fa\u5bf9\u4ed6\u7684\u8bdd\u6709\u6240\u6000\u7591\uff0c\u4ee5\u514d\u6253\u8349\u60ca\u86c7\uff01\u201d \u3000\u3000\u4e09\u5927\u6355\u5934\u3001\u5c0f\u96c5\u3001\u60dc\u7389\u3001\u738b\u5c11\u537f\u4e00\u9f50\u9053\uff1a\u201c\u603b\u6355\u5934\u653e\u5fc3\u5427\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u5206\u5934\u53bb\u51c6\u5907\u4e00\u4e0b\u5427\uff0c\u5c06\u9632\u52a1\u5e03\u7f6e\u597d\uff0c\u4fbf\u6293\u7d27\u65f6\u95f4\u517b\u7cbe\u84c4\u9510\u3002 \u5c0f\u96c5\uff0c\u4f60\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u7a0b\u5c0f\u8776\u5e26\u7740\u5c0f\u96c5\u8fdb\u4e86\u81ea\u5df1\u7684\u623f\u95ee\u3002 \u3000\u3000\u738b\u5c11\u537f\u548c\u5211\u623f\u5e08\u5f20\u5b9d\u5584\u8fdb\u4e86\u4ed6\u7684\u4e66\u623f\u3002 \u3000\u3000\u867d\u8bf4\u8fd9\u4e9b\u5929\u91cc\u5e9c\u8859\u4e0d\u80fd\u6b63\u5e38\u529e\u516c\uff0c\u4f46\u738b\u5c11\u537f\u4ecd\u7136\u5229\u7528\u4e00\u5207\u53ef\u4ee5\u5229\u7528\u7684\u65f6\u95f4\uff0c\u5904\u7406\u4e00\u4e9b\u79ef \u538b\u4e0b\u6765\u7684\u516c\u52a1\u3002 \u3000\u3000\u5c0f\u96c5\u5173\u4e0a\u4e86\u623f\u95e8\uff0c\u8f6c\u8eab\u5bf9\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u6211\u4eec\u662f\u4e0d\u662f\u4e5f\u8981\u4f11\u606f\u4e00\u4e0b\uff0c\u517b\u7cbe\u84c4\u9510\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8981\u51fa\u53bb\u3002\u201d \u3000\u3000\u5c0f\u96c5\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u51fa\u53bb\uff1f\u53bb\u54ea\u91cc\uff1f\u53bb\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u5230\u4e86\u4f60\u5c31\u77e5\u9053\u4e86\u3002\u73b0\u5728\uff0c\u4f60\u6765\u5e2e\u6211\u6539\u88c5\uff0c\u54b1\u4eec\u6e9c\u51fa\u53bb\u65f6\uff0c\u4e00\u5b9a\u4e0d\u80fd\u8ba9\u5e9c \u8859\u91cc\u7684\u4efb\u4f55\u4eba\u53d1\u73b0\uff01\u201d \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u90a3\u5e9c\u8859\u7684\u9632\u52a1\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5929\u9ed1\u524d\u54b1\u4eec\u5c31\u56de\u6765\uff0c\u6211\u53ef\u4ee5\u80af\u5b9a\uff0c\u6740\u624b\u96c6\u56e2\u518d\u4e5f\u4e0d\u4f1a\u5728\u767d\u5929\u88ad\u51fb\u5e9c\u8859\u4e86\u3002\u201d \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u513f\u529f\u592b\uff0c\u4e8c\u4eba\u5df2\u88c5\u626e\u5b8c\u6bd5\uff0c\u5bf9\u89c6\u4e00\u773c\u540e\uff0c\u4e0d\u7981\u90fd\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5979\u4eec\u76f8\u4fe1\uff0c\u73b0\u5728\u5c31\u7b97\u5c0f\u6587\u8d70\u8fdb\u6765\uff0c\u4e5f\u4e0d\u53ef\u80fd\u8ba4\u51fa\u5979\u4eec\u662f\u8c01\u4e86\u3002\u56e0\u4e3a\u7edd\u4e0d\u4f1a\u6709\u4eba\u80fd\u60f3\u5f97\u5230\uff0c \u8fd9\u4e24\u4f4d\u8c8c\u7f8e\u7684\u5982\u82b1\u7684\u5a07\u6ef4\u6ef4\u7684\u5c11\u5973\u80fd\u88c5\u626e\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u613f\u610f\u88c5\u626e\u6210\u8fd9\u4e2a\u6837\u5b50\u3002 \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u4e2a\u6837\u5b50\uff0c\u80af\u5b9a\u6ca1\u4eba\u80fd\u8ba4\u51fa\u6765\u4e86\uff0c\u53ef\u54b1\u4eec\u600e\u4e48\u51fa\u53bb\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5c0f\u96c5\uff0c\u4f60\u6ce8\u610f\u8fc7\u6ca1\u6709\u4ece\u540e\u9662\u89d2\u95e8\u51fa\u53bb\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u5c0f\u96c5\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u662f\u5e9c\u8859\u7684\u53a8\u623f\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4ece\u53a8\u623f\u7684\u540e\u95e8\u8d70\u51fa\u6211\u4eec\u8fd9\u6837\u4e24\u4e2a\u4eba\uff0c\u4f60\u8ba4\u4e3a\u4f1a\u5f15\u4eba\u6000\u7591\u5417\uff1f\u201d \u3000\u3000\u5c0f\u96c5\u7b11\u9053\uff1a\u201c\u5982\u679c\u6709\u4eba\u6000\u7591\uff0c\u90a3\u4eba\u4e00\u5b9a\u6709\u4e9b\u4e0d\u6b63\u5e38\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8d70\uff01\u201d \u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1 \u3000\u3000\u5929\u8272\u6e10\u6e10\u9ed1\u4e0b\u6765\u4e86\uff0c\u5e9c\u8859\u540e\u9662\u91cc\u7684\u706f\u706b\u4e00\u76cf\u76cf\u4eae\u4e86\u8d77\u6765\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u548c\u5c0f\u96c5\u62c9\u5f00\u623f\u95e8\uff0c\u4ece\u623f\u95f4\u91cc\u8d70\u51fa\u6765\u65f6\uff0c\u6ca1\u6709\u4efb\u4f55\u4eba\u770b\u51fa\u6765\uff0c\u4e5f\u6ca1\u6709\u4eba\u6000\u7591\u5979\u4eec\u66fe \u7ecf\u6539\u88c5\u6084\u6084\u6e9c\u51fa\u53bb\u8fc7\u3002 \u3000\u3000\u5403\u8fc7\u665a\u996d\uff0c\u5927\u5bb6\u90fd\u805a\u5728\u4e86\u5927\u90e6\u91cc\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u95f4\u9053\uff1a\u201c\u675c\u53f8\u4e3b\uff0c\u2018\u5251\u6d77\u6d74\u9b42\u2019\u7684\u7cbe\u8981\u4e4b\u5904\uff0c\u4f60\u80fd\u9886\u4f1a\u5417\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5728\u5c0f\u6587\u59d1\u5a18\u7684\u70b9\u62e8\u4e0b\uff0c\u90fd\u9886\u609f\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u5934\u9053\uff1a\u201c\u90a3\u5c31\u597d\uff01\u675c\u53f8\u4e3b\uff0c\u4f60\u53ef\u8981\u8bb0\u4f4f\uff0c\u5728\u7ec3\u5230\u4e03\u6210\u706b\u5019\u4e4b\u524d\uff0c\u4f60\u7edd\u4e0d\u53ef\u4f7f\u7528 \u5b83\uff0c\u4e0d\u7136\u4f1a\u8d70\u706b\u5165\u9b54\u7684\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5c5e\u4e0b\u8bb0\u4f4f\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u987f\u4e86\u4e00\u987f\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u4eec\u6765\u5b89\u6392\u4e00\u4e0b\u540e\u9662\u7684\u9632\u536b\u4efb\u52a1\u3002\u201d \u3000\u3000\u5979\u7684\u5b89\u6392\u662f\u8fd9\u6837\u7684\uff1a \u3000\u3000\u5c0f\u6587\u548c\u738b\u77e5\u5e9c\u7559\u5728\u4ed6\u4eec\u4e8c\u4e2a\u7684\u5367\u5ba4\u4e4b\u5185\uff0c\u5982\u679c\u6740\u624b\u96c6\u56e2\u4e0d\u9732\u9762\uff0c\u5c31\u4e0d\u5fc5\u51fa\u6765\u3002 \u3000\u3000\u73b0\u5728\u5c31\u53ef\u4ee5\u770b\u51fa\u5c0f\u6587\u5ac1\u7ed9\u738b\u5c11\u537f\u7684\u6709\u5229\u4e00\u9762\u4e86\uff0c\u56e0\u4e3a\u73b0\u5728\u5979\u53ef\u4ee5\u4e0d\u5206\u65e5\u591c\u5730\u5bf9\u4ed6\u8fdb\u884c\u8d34 \u8eab\u4fdd\u62a4\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u548c\u5c0f\u96c5\u5206\u522b\u5b88\u5728\u738b\u77e5\u5e9c\u5367\u5ba4\u4e24\u8fb9\u7684\u623f\u95f4\u3002 \u3000\u3000\u675c\u671b\u6708\u548c\u60dc\u7389\u8d1f\u8d23\u5185\u9662\u540e\u9762\u7684\u9632\u52a1\uff0c\u5b88\u4f4f\u5185\u9662\u901a\u5f80\u5e9c\u8859\u540e\u95e8\u7684\u90a3\u6761\u901a\u9053\u3002 \u3000\u3000\u5434\u94c1\u534e\u548c\u77f3\u742a\u3001\u5f20\u9e9f\u4e09\u4eba\u5c31\u5728\u540e\u9662\u5185\u62e9\u5730\u57cb\u4f0f\u3002 \u3000\u3000\u4e8e\u627f\u5fd7\u5b88\u4f4f\u540e\u9662\u524d\u95e8\u3002 \u3000\u3000\u5c91\u5578\u864e\u6307\u6325\u8859\u5f79\u4e4b\u4e2d\u516b\u540d\u7528\u957f\u67aa\u7684\u597d\u624b\uff0c\u5728\u540e\u9662\u5916\u9762\u5de1\u903b\u3002 \u3000\u3000\u6797\u4e0d\u51e1\u4e0e\u4e09\u5341\u56db\u540d\u6c5f\u5357\u5206\u53f8\u7684\u597d\u624b\u4e00\u8d77\u57cb\u4f0f\u5728\u5927\u5385\u548c\u5c4b\u9876\u4e0a\u3002 \u3000\u3000\u5176\u4f59\u7684\u8859\u5f79\u5168\u90e8\u8eb2\u5728\u6697\u4e2d\uff0c\u5907\u597d\u706f\u7b3c\u706b\u628a\uff0c\u53ea\u8981\u6740\u624b\u96c6\u56e2\u4e00\u5230\uff0c\u4fbf\u56db\u9762\u70b9\u8d77\u706f\u706b\u6c34\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u53ea\u662f\u7b2c\u4e00\u5957\u65b9\u6848\uff0c\u540c\u65f6\uff0c\u7a0b\u5c0f\u8776\u8fd8\u5236\u5b9a\u4e86\u7b2c\u4e8c\u65b9\u6848\uff0c\u4ee5\u5907\u60c5\u51b5\u7d27\u6025\u4e4b\u65f6\uff0c\u53d8\u5316 \u56e0\u5e94\u3002 \u3000\u3000\u5b89\u6392\u597d\u4e4b\u540e\uff0c\u5e9c\u8859\u5185\u7684\u706b\u5149\u4e00\u70b9\u63a5\u7740\u4e00\u70b9\uff0c\u6e10\u6b21\u7184\u706d\u4e86\u3002 \u3000\u3000\u5728\u5916\u4eba\u770b\u8d77\u6765\uff0c\u5e9c\u8859\u5185\u4e0e\u5f80\u5e38\u6ca1\u6709\u4ec0\u4e48\u4e24\u6837\uff0c\u4ecd\u7136\u9075\u5b88\u7740\u6b63\u5e38\u7684\u4f5c\u606f\u65f6\u95ee\u3002 \u3000\u3000\u591c\uff0c\u6e10\u6e10\u5730\u6df1\u4e86\u3002 \u3000\u3000\u5c31\u5728\u5e9c\u8859\u91cc\u6b63\u6781\u79ef\u5907\u6218\u7684\u540c\u65f6\uff0c\u8d3e\u82f1\u4e5f\u8c03\u6574\u4e86\u91d1\u5e9c\u7684\u9632\u52a1\u3002 \u3000\u3000\u4ed6\u5c06\u91d1\u5e9c\u6240\u6709\u9ad8\u624b\u90fd\u8c03\u96c6\u5230\u4e86\u89c2\u9e64\u697c\u56db\u8fc7\uff0c\u5e76\u8bf7\u94c1\u7fce\u505a\u9632\u5b88\u7684\u603b\u6307\u6325\u3002 \u3000\u3000\u91d1\u767e\u5e74\u548c\u91d1\u5e9c\u5185\u7737\u5168\u90fd\u8fc1\u8fdb\u4e86\u89c2\u9e64\u697c\u4e4b\u5185\uff0c\u7531\u8d3e\u82f1\u7387\u9886\u5bd2\u5c71\u56db\u5200\u3001\u7f57\u6d6e\u4e09\u5251\u4fdd\u62a4\u3002 \u3000\u3000\u5728\u94c1\u7fce\u770b\u6765\uff0c\u8fd9\u79cd\u63aa\u65bd\u662f\u6ca1\u6709\u592a\u592a\u7684\u5fc5\u8981\u7684\uff0c\u56e0\u4e3a\u68a6\u5e7b\u4e4b\u5200\u4e09\u5b9d\u73b0\u5df2\u5230\u624b\uff0c\u7d27\u63a5\u7740\u5e94\u8be5 \u5bf9\u5341\u5927\u5bcc\u8499\u5927\u4e3e\u8fdb\u88ad\u4e86\u3002\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u91d1\u5e9c\u5e94\u8be5\u505a\u7684\u662f\u5728\u5e9c\u5185\u8bbe\u4e0b\u57cb\u4f0f\uff0c\u5229\u7528\u5e9c\u5185\u5e7f\u9614\u7684 \u7a7a\u95f4\u4e0e\u654c\u4eba\u5c55\u5f00\u6218\u6597\u3002\u8fd9\u6837\u4e00\u6536\u7f29\u9632\u5b88\u5708\u5b50\uff0c\u53cd\u800c\u4f1a\u7f29\u624b\u7f29\u811a\uff0c\u88ab\u52a8\u6328\u6253\u3002 \u3000\u3000\u4f46\u636e\u8d3e\u82f1\u8bf4\uff0c\u8fd9\u6837\u7684\u5b89\u6392\u662f\u91d1\u767e\u5e74\u4eb2\u81ea\u51b3\u5b9a\u7684\u3002 \u3000\u3000\u5927\u6982\u662f\u56e0\u4e3a\u94f6\u8863\u4eba\u8f7b\u6613\u7a83\u8d70\u4e09\u5b9d\uff0c\u91d1\u767e\u5e74\u5fc3\u91cc\u5f88\u662f\u5bb3\u6015\u4e86\u5427\u3002\u94c1\u7fce\u4e00\u8fb9\u5bc6\u5207\u6ce8\u89c6\u7740\u56db\u4e0b \u7684\u52a8\u9759\uff0c\u4e00\u8fb9\u60f3\u9053\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u4e3b\u52a8\u8fdb\u653b\uff0c\u624d\u662f\u6700\u597d\u7684\u9632\u5b88\uff0c\u91d1\u767e\u5e74\u73b0\u5728\u5e94\u8be5\u8054\u7edc\u5341\u5927\u5bcc\u8c6a\uff0c\u5408\u95e8\u4e0b\u9ad8\u624b\u4e8e\u4e00\u5904\uff0c \u8054\u624b\u4e0e\u68a6\u5e7b\u4e4b\u5200\u4e00\u640f\uff0c\u8fd9\u6837\u5404\u81ea\u4e3a\u9635\uff0c\u53cd\u800c\u5bb9\u6613\u88ab\u68a6\u5e7b\u4e4b\u5200\u5404\u4e2a\u51fb\u7834\u3002 \u3000\u3000\u591c\uff0c\u8d8a\u6765\u8d8a\u6df1\u4e86\u3002 \u3000\u3000\u56db\u4e0b\u91cc\u4e00\u70b9\u52a8\u9759\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u89c2\u9e64\u697c\u4e2d\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u94c1\u7fce\u6709\u4e9b\u5f00\u59cb\u62c5\u5fc3\u4e86\u3002\u4e3a\u5e9c\u8859\u7684\u5b89\u5168\u62c5\u5fc3\u3002 \u3000\u3000\u5e9c\u8859\u91cc\u7684\u60c5\u51b5\u600e\u4e48\u6837\u4e86\uff1f \u3000\u3000\u4f1a\u4e0d\u4f1a\u53c8\u6709\u6740\u624b\u88ad\u51fb\uff1f \u3000\u3000\u5c31\u5728\u94c1\u7fce\u4e3a\u5e9c\u8859\u7684\u5b89\u5168\u62c5\u5fc3\u65f6\uff0c\u7a0b\u5c0f\u8776\u5df2\u542c\u89c1\u5e9c\u8859\u4e8c\u5802\u9644\u8fd1\u54cd\u8d77\u4e86\u4e00\u9635\u8863\u631f\u5e26\u98ce\u4e4b\u58f0\u3002 \u3000\u3000\u201c\u6709\u4eba\u6765\u4e86\uff01\u201d\u7a0b\u5c0f\u8776\u4e00\u95ea\u8eab\u51fa\u4e86\u623f\u95f4\uff0c\u53d1\u51fa\u4e86\u6697\u53f7\uff0c\u901a\u77e5\u57cb\u4f0f\u7684\u4f17\u4eba\u3002 \u3000\u3000\u5c0f\u6587\u5728\u5367\u5ba4\u95e8\u540e\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u6211\u4eec\u73b0\u5728\u8981\u4e0d\u8981\u51fa\u6765\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u7528\uff0c\u7b49\u706f\u5149\u5927\u4eae\u65f6\uff0c\u4f60\u518d\u4fdd\u62a4\u738b\u5927\u4eba\u51fa\u6765\uff01\u201d \u3000\u3000\u5c0f\u6587\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u95ea\u5230\u5c0f\u96c5\u623f\u95f4\u5916\uff0c\u5c3a\u89c1\u623f\u95f4\u6084\u65e0\u58f0\u606f\u5730\u6253\u5f00\u4e86\u3002 \u3000\u3000\u5c0f\u96c5\u4e00\u8eab\u52b2\u7267\uff0c\u624b\u6301\u957f\u5251\uff0c\u8f7b\u624b\u8f7b\u811a\u5730\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u96c5\uff0c\u628a\u5251\u5148\u6536\u8d77\u6765\uff01\u201d \u3000\u3000\u5c0f\u96c5\u5947\u602a\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u654c\u4eba\u8fdb\u5165\u9662\u5185\u4e4b\u524d\uff0c\u6211\u4eec\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u53d1\u73b0\u6211\u4eec\u5df2\u6709\u9632\u5907\uff01\u5251\u8eab\u7684\u53cd\u5149\u53ef\u80fd \u4f1a\u8ba9\u4ed6\u4eec\u60ca\u89c9\u7684\uff01\u201d \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u6162\u6162\u5c06\u957f\u5251\u63d2\u5165\u4e86\u5251\u9798\u3002 \u3000\u3000\u4e8c\u4eba\u95ea\u8eab\u5230\u4e86\u4e00\u6839\u67f1\u5b50\u540e\u9762\uff0c\u4ed4\u7ec6\u542c\u7740\u8d8a\u6765\u8d8a\u8fd1\u7684\u6c99\u6c99\u7684\u811a\u6b65\u58f0\u3002 \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4e0d\u51fa\u4f60\u610f\u6599\uff0c\u6765\u4e86\u4e0d\u5c11\u4eba\u5462\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u55ef\uff0c\u4e0d\u4e0b\u516b\u5341\u4eba\uff01\u201d \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u542c\u4ed6\u4eec\u7684\u811a\u6b65\u58f0\u8fd9\u6837\u8f7b\uff0c\u597d\u50cf\u90fd\u662f\u597d\u624b\u554a\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6765\u7684\u5e94\u8be5\u90fd\u662f\u6740\u624b\u96c6\u56e2\u7684\u7cbe\u82f1\u4eba\u7269\uff0c\u800c\u4e14\u5176\u4e2d\u6709\u4e24\u4e2a\u4eba\u7684\u6b66\u529f\u7279\u522b \u4e4b\u9ad8\u3002\u201d \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4f60\u770b\u90a3\u4e2a\u68a6\u5e7b\u4e4b\u5200\u4f1a\u4e0d\u4f1a\u4e5f\u6765\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e24\u5927\u9ad8\u624b\u4e2d\uff0c\u6709\u4e00\u4e2a\u80af\u5b9a\u662f\u4ed6\u3002\u201d \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u4e0d\u7528\u8bf4\uff0c\u53e6\u4e00\u4e2a\u4e00\u5b9a\u662f\u6740\u624b\u4e4b\u738b\u8840\u624b\u65b9\u8f6e\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u4e86\u70b9\u5979\u7684\u989d\u5934\uff0c\u8f7b\u58f0\u7b11\u9053\uff1a\u201c\u9b3c\u4e2b\u5934\u771f\u806a\u660e\uff01\u201d \u3000\u3000\u5c0f\u96c5\u4e5f\u65e0\u58f0\u4e00\u7b11\uff0c\u6b63\u60f3\u8bf4\u4ec0\u4e48\uff0c\u7a0b\u5c0f\u8776\u7ad6\u8d77\u4e00\u6839\u624b\u6307\uff0c\u9053\uff1a\u201c\u5664\u58f0\uff01\u201d \u3000\u3000\u7684\u786e\u4e0d\u80fd\u518d\u8bf4\u8bdd\u4e86\uff0c\u56e0\u4e3a\u6740\u624b\u4eec\u5df2\u4e00\u4e2a\u63a5\u4e00\u4e2a\u7531\u56f4\u5899\u63a0\u8fdb\u4e86\u540e\u9662\u9662\u5185\u3002 \u3000\u3000\u7b2c\u4e00\u6279\u6740\u624b\u4eec\u5927\u7ea6\u6709\u4e09\u5341\u591a\uff0c\u4ed6\u4eec\u521a\u4e00\u843d\u5730\uff0c\u8fd8\u6ca1\u7ad9\u7a33\uff0c\u7a0b\u5c0f\u8776\u5ffd\u7136\u53d1\u51fa\u4e86\u4e00\u58f0\u6e05\u5578\u3002 \u3000\u3000\u5578\u58f0\u65b9\u8d77\uff0c\u56db\u9762\u5c31\u54cd\u8d77\u4e86\u5229\u7bad\u7834\u7a7a\u4e4b\u58f0\uff0c\u6574\u9f50\u5212\u4e00\u7684\u5f13\u5f26\u5c04\u53d1\u58f0\u4e2d\uff0c\u8fd8\u5939\u6742\u7740\u6697\u5668\u98de\u51fa \u7684\u58f0\u97f3\u3002 \u3000\u3000\u4e00\u9635\u60e8\u53eb\uff0c\u7b2c\u4e00\u6279\u6740\u624b\u5012\u4e0b\u4e86\u4e8c\u5341\u591a\u4e2a\uff01 \u3000\u3000\u4f46\u540c\u65f6\uff0c\u66f4\u591a\u7684\u6740\u624b\u53c8\u6d8c\u4e86\u8fdb\u6765\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u81ea\u5728\u540e\u51fa\u73b0\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u4e3e\u706f\uff01\u73b0\u8eab\uff0c\u56f4\u4f4f\u4ed6\u4eec\uff01\u201d \u3000\u3000\u706f\u5149\u7167\u89c6\u4e86\u5728\u5927\u5385\u524d\u4e00\u5b57\u6392\u5f00\u7684\u6c5f\u5357\u5206\u53f8\u7684\u4e09\u5341\u4f59\u540d\u597d\u624b\uff0c\u4e5f\u7167\u4eae\u4e86\u5404\u4e2a\u57cb\u4f0f\u70b9\u51b2\u51fa\u6765 \u7684\u56db\u5927\u6355\u5934\u548c\u624b\u6301\u957f\u67aa\u3001\u5f13\u7bad\u7684\u6570\u5341\u540d\u5e9c\u8859\u91cc\u6311\u51fa\u7684\u597d\u624b\u3002 \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u738b\u77e5\u5e9c\u7684\u5367\u5ba4\u95e8\u5927\u5f00\uff0c\u738b\u5c11\u537f\u80cc\u7740\u53cc\u624b\uff0c\u8fc8\u7740\u65b9\u6b65\uff0c\u6162\u6162\u5730\u8e31\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5c0f\u6587\u5b9d\u5251\u5728\u624b\uff0c\u7d27\u8ddf\u5728\u4ed6\u8eab\u4fa7\u3002 \u3000\u3000\u8fd9\u4e2a\u5f88\u7a81\u7136\u7684\u53d8\u5316\u663e\u7136\u51fa\u4e8e\u6740\u624b\u4eec\u7684\u9884\u6599\u4e4b\u5916\uff0c\u6240\u4ee5\u4ed6\u4eec\u4e00\u65f6\u95f4\u663e\u5f97\u5f88\u6709\u4e9b\u614c\u4e71\u3002 \u3000\u3000\u4f46\u662f\uff0c\u5f88\u5feb\u4fbf\u542c\u5f97\u9662\u5916\u54cd\u8d77\u4e00\u4e2a\u5f88\u5a01\u4e25\u7684\u58f0\u97f3\uff1a\u201c\u4e0d\u8981\u614c\uff01\u6211\u4eec\u7684\u5b9e\u529b\u5728\u4ed6\u4eec\u4e4b\u4e0a\uff01\u6392 \u597d\u9635\u5f62\uff01\u201d \u3000\u3000\u614c\u4e71\u7684\u6740\u624b\u4eec\u4e00\u7728\u773c\u95f4\u4fbf\u9547\u5b9a\u4e0b\u6765\u4e86\uff0c\u53ea\u89c1\u4eba\u5f71\u95ea\u52a8\uff0c\u811a\u6b65\u7eb7\u4e71\u58f0\u4e2d\uff0c\u6570\u5341\u540d\u9ed1\u8863\u8499\u9762 \u6740\u624b\u5df2\u7ec4\u6210\u4e00\u4e2a\u4e09\u89d2\u5f62\u7684\u9635\u5f62\uff0c\u7ad9\u5728\u5916\u9762\u7684\u6740\u624b\u4eec\u624b\u4e0a\uff0c\u90fd\u4e3e\u8d77\u4e86\u4e00\u9762\u5706\u76fe\u3002 \u3000\u3000\u8fd9\u76fe\u724c\u663e\u7136\u662f\u4e13\u4e3a\u9632\u5229\u7bad\u6697\u5668\u800c\u7528\u7684\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4e0d\u7981\u9887\u4e3a\u8d5e\u8bb8\u5730\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u8fd9\u4e9b\u6740\u624b\u4eec\u679c\u7136\u662f\u8bad\u7ec3\u6709\u7d20\u3002 \u3000\u3000\u5728\u73b0\u5728\u7684\u5f62\u52bf\u4e0b\u8fd8\u80fd\u4fdd\u6301\u9547\u5b9a\uff0c\u5904\u60ca\u4e0d\u4e71\uff0c\u8db3\u4ee5\u8bf4\u660e\u6740\u624b\u4eec\u7684\u5b9e\u529b\u4e86\u3002 \u3000\u3000\u53ef\u4ee5\u80af\u5b9a\uff0c\u4eca\u591c\u5c06\u662f\u4e00\u573a\u8840\u6218\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u77e5\u9662\u5916\u662f\u54ea\u4e00\u4f4d\u9ad8\u4eba\uff1f\u65e2\u7136\u6765\u4e86\uff0c\u4e3a\u4f55\u8fd8\u4e0d\u73b0\u8eab\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u4e00\u58f0\u5927\u7b11\uff0c\u4e24\u6761\u9ed1\u5f71\u8054\u6289\u81ea\u56f4\u5899\u4e4b\u4e0a\u98d8\u8eab\u800c\u8fdb\uff0c\u843d\u5728\u6740\u624b\u4eec\u7ec4\u6210\u7684\u4e09\u89d2\u9635\u5f62\u524d\u4e09 \u56db\u6b65\u8fdc\u7684\u5730\u65b9\u3002 \u3000\u3000\u4ed6\u4eec\u9762\u5bf9\u7740\u7a0b\u5c0f\u8776\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4f3c\u662f\u6014\u4e86\u4e00\u6014\uff0c\u624d\u9053\uff1a\u201c\u4f60\u662f\u68a6\u5e7b\u4e4b\u5200\uff1f\u201d \u3000\u3000\u5de6\u9996\u4e00\u4e2a\u9ed1\u8863\u8499\u9762\u4eba\u7b11\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\u5bf9\u4eba\u7684\u58f0\u97f3\u80fd\u8fc7\u8033\u4e0d\u5fd8\uff0c\u771f\u4ee4\u8001\u592b\u4f69\u670d\uff01\u4e0d\u9519\uff0c \u8001\u592b\u6b63\u662f\u66fe\u4e0e\u7a0b\u603b\u6355\u5934\u6253\u8fc7\u4ea4\u9053\u7684\u68a6\u5e7b\u4e4b\u5200\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u6307\u4ed6\u8eab\u8fb9\u90a3\u4eba\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u8eab\u6750\u9ad8\u5927\uff0c\u7070\u8863\u8499\u9762\uff0c\u58f0\u97f3\u5f88\u4f4e\u6c89\u5730\u9053\uff1a\u201c\u4e45\u95fb\u7a0b\u603b\u6355\u5934\u806a\u660e\u8fc7\u4eba\uff0c\u4f60\u53ef\u4ee5\u53d1\u6325\u4f60 \u7684\u806a\u660e\u624d\u667a\uff0c\u731c\u4e00\u731c\u8001\u592b\u662f\u8c01\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8981\u4e0d\u662f\u6740\u624b\u4e4b\u738b\u3001\u8840\u624b\u65b9\u8f6e\uff0c\u6211\u4e09\u5929\u4e0d\u5403\u996d\uff01\u201d \u3000\u3000\u53f3\u8fb9\u8499\u9762\u4eba\u4f3c\u4e4e\u4e00\u5446\uff0c\u65cb\u5373\u5927\u7b11\u9053\uff1a\u201c\u597d\uff01\u7a0b\u603b\u6355\u5934\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u65b9\u67d0\u80fd\u627e\u5230\u4f60\u8fd9\u4e00 \u4f4d\u5bf9\u624b\uff0c\u5b9e\u5728\u5f88\u9ad8\u5174\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6de1\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u542c\u4f60\u7684\u610f\u601d\uff0c\u4f60\u591c\u95ef\u5e9c\u8859\uff0c\u53ea\u4e0d\u8fc7\u662f\u60f3\u4e0e\u6211\u4e00\u51b3\u9ad8\u4e0b\u5417\uff1f\u5982\u679c \u662f\u8fd9\u6837\u7684\u8bdd\uff0c\u6211\u4e00\u76f4\u5728\u56db\u5904\u529e\u6848\uff0c\u884c\u8e2a\u4e5f\u5e76\u4e0d\u96be\u5bfb\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e00\u76f4\u8981\u7b49\u6211\u5230\u4e86\u626c\u5dde\uff0c\u624d\u627e\u4e0a \u6211\u5462\uff1f\u201d \u3000\u3000\u65b9\u8f6e\u6014\u4e86\u4e00\u6014\uff0c\u4e0d\u77e5\u5982\u4f55\u56de\u7b54\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u7b11\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\u679c\u7136\u8a00\u8f9e\u5c16\u9510\uff0c\u4f46\u4eca\u591c\u4e4b\u52bf\uff0c\u80dc\u8d1f\u9760\u7684\u662f\u6b66\u529f\uff0c\u662f\u5b9e\u529b\uff0c \u5355\u901e\u53e3\u820c\u4e4b\u5229\uff0c\u662f\u4e8e\u4e8b\u65e0\u8865\u7684\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u7b11\u9053\uff1a\u201c\u4e00\u7167\u9762\u5c31\u88ab\u6211\u4eec\u51fb\u6740\u4e86\u4e8c\u5341\u591a\u4eba\uff0c\u8fd8\u81ea\u4ee5\u4e3a\u6709\u4ec0\u4e48\u5b9e\u529b\uff01\u6211\u770b\u4f60\u4eec\u53ea \u4e0d\u8fc7\u662f\u4e00\u7fa4\u4e4c\u5408\u4e4b\u4f17\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u7b11\u9053\uff1a\u201c\u8fde\u533a\u533a\u51e0\u679d\u7834\u7bad\uff0c\u4e00\u9635\u6697\u5668\u90fd\u8eb2\u4e0d\u5f00\uff0c\u53ea\u8bf4\u660e\u4ed6\u4eec\u8be5\u6b7b\uff01\u4ed6\u4eec\u6b7b\u4e86 \u66f4\u597d\uff0c\u6211\u65b9\u4eba\u624b\u53ea\u5c11\u4e86\u4e00\u4e9b\uff0c\u4f46\u4e5f\u8bc1\u660e\u4f59\u4e0b\u7684\u5168\u90e8\u662f\u7cbe\u82f1\u4eba\u7269\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5f88\u4e0d\u5c51\u5730\u4ece\u9f3b\u5b50\u91cc\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5982\u679c\u4f60\u548c\u65b9\u8f6e\u8ba4\u4e3a\u90a3\u53ea\u662f\u533a\u533a\u51e0\u679a\u7834\u7bad\uff0c\u4e3a \u4ec0\u4e48\u4f60\u4eec\u5148\u8eb2\u5728\u9662\u5916\uff0c\u4e0d\u7b2c\u4e00\u4e2a\u8fdb\u6765\u5462\uff1f\u53ea\u6015\u662f\u4f60\u4eec\u4e5f\u81ea\u8ba4\u8eb2\u4e0d\u5f00\uff0c\u6240\u4ee5\u5148\u8ba9\u8fd9\u4e9b\u4eba\u8fdb\u6765\u5f15 \u53d1\u6211\u65b9\u7684\u57cb\u4f0f\u5427\uff01\u201d \u3000\u3000\u65b9\u8f6e\u6012\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u88ab\u6211\u8bf4\u4e2d\u5fc3\u601d\uff0c\u607c\u7f9e\u6210\u6012\u4e86\u5427\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u4f60\u5982\u679c\u60f3\u7528\u4f60\u5c16\u9510\u7684\u8a00\u8f9e\u6fc0\u6012\u8001\u592b\u548c\u8840\u624b\u65b9\u8f6e\uff0c\u5f15\u8d77\u6211\u4eec \u5fc3\u6d6e\u6c14\u71e5\uff0c\u4f60\u4fbf\u53ef\u4ee5\u8d81\u673a\u51fb\u8d25\u6211\u4eec\uff0c\u90a3\u4f60\u5c31\u9519\u4e86\uff01\u5927\u9519\u7279\u9519\u4e86\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53c8\u4e0d\u5c51\u5730\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u68a6\u5e7b\u4e4b\u5200\uff0c\u4f60\u521a\u624d\u4e5f\u8bf4\u8fc7\u4e86\uff0c\u80dc\u8d1f\u5168\u51ed\u5b9e\u529b\uff0c\u6211\u6839 \u672c\u7528\u4e0d\u7740\u5fc3\u7406\u6218\uff01\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u4f60\u4eec\u7684\u76ee\u7684\u662f\u4e0d\u53ef\u80fd\u5b9e\u73b0\u7684\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u6307\u7740\u738b\u5c11\u537f\uff0c\u9053\uff1a\u201c\u6211\u4eec\u7684\u76ee\u7684\u5c31\u662f\u8981\u6740\u4e86\u738b\u77e5\u5e9c\uff01\u4eca\u591c\uff0c\u8fd9\u4e2a\u76ee\u7684\u5c31\u53ef\u8fbe \u5230\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u626c\u58f0\u9053\uff1a\u201c\u68a6\u5e7b\u4e4b\u5200\uff0c\u8840\u624b\u65b9\u8f6e\uff0c\u4f60\u4eec\u4e00\u518d\u88ad\u51fb\u5e9c\u8859\uff0c\u60f3\u6740\u672c\u5e9c\uff0c\u7a76\u7adf\u60f3\u5e72\u4ec0\u4e48\uff1f \u6709\u4ec0\u4e48\u56fe\u8c0b\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u771f\u662f\u4e2a\u4e66\u5446\u5b50\uff01\u544a\u8bc9\u4f60\u4f60\u4e5f\u60f3\u4e0d\u901a\u7684\uff0c\u8fd8\u662f\u4e56\u4e56\u5730\u7b49\u6b7b\u5427\uff01\u201d \u3000\u3000\u5c0f\u6587\u4e00\u633a\u5b9d\u5251\uff0c\u9053\uff1a\u201c\u6709\u79cd\u7684\u4f60\u5c31\u7acb\u9a6c\u8fc7\u6765\uff0c\u770b\u770b\u6b7b\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u770b\u4e86\u5979\u4e00\u773c\uff0c\u8f6c\u800c\u5411\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u8001\u592b\u4e0e\u4f60\u8c08\u4e00\u7b14\u751f\u610f\u5982\u4f55\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u4f60\u5c06\u738b\u5c11\u537f\u4ea4\u7ed9\u6211\u4eec\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u90a3\u6211\u53c8\u6709\u4ec0\u4e48\u597d\u5904\u5462\uff1f\u8981\u77e5\u9053\uff0c\u6211\u662f\u5211\u90e8\u603b\u6355\u5934\uff0c\u6211\u4eb2\u81ea\u51fa\u9a6c\u6765\u626c\u5dde\u7834 \u6848\uff0c\u6848\u5b50\u6ca1\u6709\u7834\uff0c\u53cd\u800c\u8ba9\u6740\u624b\u6740\u4e86\u77e5\u5e9c\u5927\u4eba\uff0c\u6211\u600e\u4e48\u5411\u5211\u90e8\u4ea4\u5f85\uff0c\u53c8\u600e\u4e48\u5411\u671d\u5ef7\u4ea4\u5f85\u5462\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u8fd9\u4e2a\u5f88\u5bb9\u6613\uff0c\u8001\u592b\u81ea\u6709\u5b89\u6392\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8bf7\u5c06\u4f60\u7684\u5b89\u6392\u8be6\u7ec6\u5730\u8bf4\u7ed9\u6211\u542c\u542c\uff0c\u6211\u770b\u770b\u8fd9\u7b14\u751f\u610f\u5212\u7b97\u4e0d\u5212\u7b97\uff0c\u518d\u51b3\u5b9a\u662f \u5426\u4e0e\u4f60\u4ea4\u6613\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u4f60\u8ba9\u8001\u592b\u6740\u4e86\u738b\u5c11\u537f\uff0c\u8001\u592b\u4fdd\u8bc1\u53ef\u4ee5\u6740\u4e86\u73b0\u573a\u5176\u4ed6\u6240\u6709\u4eba\uff0c\u7136\u540e\u4ea4\u51fa \u51e0\u4e2a\u6c5f\u6e56\u4e0a\u81ed\u540d\u662d\u8457\u7684\u9ed1\u9053\u4eba\u7269\u3002\u7a0b\u603b\u6355\u5934\u5c06\u4ed6\u4eec\u6740\u6b7b\u540e\uff0c\u4fbf\u53ef\u5c06\u6848\u5b50\u63a8\u5230\u4ed6\u4eec\u5934\u4e0a\uff0c\u5e76\u4e14 \u53ef\u4ee5\u5411\u5211\u90e8\u4e0a\u62a5\u8bf4\u662f\u4ed6\u4eec\u6740\u4e86\u738b\u5c11\u537f\u548c\u5176\u4ed6\u4eba\uff0c\u800c\u4f60\u7a0b\u603b\u6355\u5934\u5374\u51ed\u501f\u6b66\u529f\u5c06\u4ed6\u4eec\u4e5f\u5c3d\u6570\u6740\u6b7b \u4e86\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u671d\u5ef7\u4e0d\u4ec5\u4e0d\u4f1a\u8ffd\u67e5\u4f60\u7684\u8d23\u4efb\uff0c\u53ea\u6015\u53cd\u800c\u4f1a\u52a0\u4f60\u7684\u5b98\u804c\u5462\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u8fd9\u8ba1\u5212\u770b\u4f3c\u4e0d\u9519\uff0c\u4f46\u660e\u773c\u4eba\u4e00\u770b\u4fbf\u53ef\u77e5\u5176\u4e2d\u7684\u6f0f\u6d1e\uff0c\u671d\u5ef7\u4e4b\u4e0a\u80fd \u4eba\u5e72\u5c06\u6bd4\u6bd4\u7686\u662f\uff0c\u53c8\u5982\u4f55\u80fd\u7792\u5f97\u8fc7\u4ed6\u4eec\uff01\u201d \u3000\u3000\u8840\u624b\u65b9\u8f6e\u7a81\u9053\uff1a\u201c\u8001\u592b\u7b49\u53ef\u4ee5\u4fdd\u8bc1\u671d\u5ef7\u51b3\u4e0d\u4f1a\u8ffd\u7a76\u7ec6\u8282\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u773c\u775b\u4e00\u4eae\uff0c\u8ffd\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u671d\u5ef7\u4e0d\u4f1a\u8ffd\u7a76\uff1f\u4f60\u80fd\u4fdd\u8bc1\uff1f\u4f60\u53ea\u4e0d\u8fc7\u662f\u6c5f\u6e56\u4e0a \u7684\u6740\u624b\u4e4b\u738b\uff0c\u4e0e\u671d\u5ef7\u6709\u4ec0\u4e48\u5173\u4fdd\uff1f\u96be\u9053\u4f60\u4eec\u7684\u5e55\u540e\u7279\u4f7f\u662f\u671d\u5ef7\u4e2d\u7684\u4e00\u4e2a\u5927\u4eba\u7269\uff1f\u65b9\u8f6e\uff1f\u4f60\u8bf4 \u5b9e\u8bdd\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u72e0\u72e0\u5730\u77aa\u4e86\u65b9\u8f6e\u4e00\u773c\u3002 \u3000\u3000\u65b9\u8f6e\u4f3c\u4e4e\u4e5f\u77e5\u9053\u81ea\u5df1\u8bf4\u9519\u8bdd\u4e86\uff0c\u4f4e\u5934\u4e0d\u518d\u56de\u7b54\u3002 \u3000\u3000\u8499\u9762\u9ed1\u8863\u4eba\u5e72\u7b11\u4e86\u51e0\u58f0\uff0c\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u4f60\u4e0d\u7528\u95ee\u8fd9\u4e48\u591a\uff0c\u53ea\u8bf4\u4f60\u5e72\u8fd8\u662f\u4e0d\u5e72\u5427\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u68a6\u5e7b\u4e4b\u5200\uff0c\u4f60\u771f\u662f\u6789\u79f0\u6c5f\u6e56\u5947\u4eba\uff0c\u4f60\u4e5f\u4e0d\u60f3\u60f3\uff0c\u6211\u7a0b\u5c0f\u8776\u662f\u8fd9 \u6837\u5bb9\u6613\u6536\u4e70\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u8499\u9762\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u672c\u6765\u6211\u4eec\u7684\u76ee\u6807\u53ea\u662f\u738b\u5c11\u537f\u4e00\u4eba\uff0c\u4f46\u73b0\u5728\uff0c\u4eca\u591c\u5728\u573a\u6240\u6709 \u7684\u4eba\uff0c\u5305\u62ec\u4f60\u7a0b\u603b\u6355\u5934\u7686\u5df2\u6210\u4e3a\u6211\u4eec\u6355\u6740\u7684\u76ee\u6807\u4e86\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u662f\u4e0d\u662f\u56e0\u4e3a\u65b9\u8f6e\u8bf4\u6f0f\u5634\uff0c\u9053\u51fa\u4e86\u4e00\u4e9b\u5b9e\u8bdd\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff01\u800c\u8fd9\u4e9b\u6070\u6070\u662f\u4f60\u4eec\u4e0d\u8be5\u542c\u5230\u7684\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u51b7\u9053\uff1a\u201c\u90a3\u4f60\u4eec\u4e0d\u59a8\u653e\u624b\u4e00\u8bd5\uff0c\u770b\u770b\u80fd\u5426\u80dc\u5f97\u4e86\u6211\u4eec\u5427\u3002\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u5e72\u7b11\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u4f60\u4e0d\u8981\u5634\u786c\uff0c\u4f60\u672c\u662f\u6211\u624b\u4e0b\u8d25\u5c06\u3002\u518d\u8bf4\uff0c\u8001\u592b\u8981\u6740\u738b \u5c11\u537f\uff0c\u6839\u672c\u7528\u4e0d\u7740\u81ea\u5df1\u52a8\u624b\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u7b11\u9053\uff1a\u201c\u6211\u770b\u4f60\u9664\u4e86\u5439\u725b\uff0c\u6ca1\u522b\u7684\u672c\u4e8b\u3002\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u7b11\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\u4e0d\u4f1a\u5fd8\u4e86\u6211\u662f\u8c01\u5427\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5f88\u4e0d\u5c51\u5730\u9053\uff1a\u201c\u4f60\u4e0d\u5c31\u662f\u68a6\u5e7b\u4e4b\u5200\u5417\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u4f60\u5927\u6982\u4e5f\u4e0d\u4f1a\u5fd8\u8bb0\u6211\u68a6\u5e7b\u4e4b\u5200\u6700\u5389\u5bb3\u7684\u529f\u592b\u662f\u4ec0\u4e48\u5427\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u5df2\u89c1\u8bc6\u8fc7\u4e86\uff0c\u5f79\u4eba\u51fa\u5200\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8f6c\u800c\u5bf9\u7740\u5c0f\u6587\u9053\uff1a\u201c\u4f60\u662f\u5c0f\u6587\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u5c0f\u6587\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u4f60\u8ba4\u8bc6\u4f60\u8eab\u8fb9\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u5c0f\u6587\u770b\u4e86\u770b\u7a0b\u5c0f\u8776\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u4e0d\u8981\u770b\u5979\uff0c\u770b\u7740\u6211\u3002\u201d \u3000\u3000\u5c0f\u6587\u5c31\u770b\u7740\u4ed6\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u56de\u7b54\u6211\uff0c\u4f60\u8ba4\u8bc6\u4f60\u8eab\u8fb9\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u5c0f\u6587\u9053\uff1a\u201c\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u5c0f\u6587\u9053\uff1a\u201c\u4ed6\u53eb\u738b\u5c11\u537f\uff0c\u662f\u626c\u5dde\u77e5\u5e9c\u3002\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6740\u4e86\u4ed6\uff01\u201d \u3000\u3000------------- \u3000\u3000\u5b64\u5251\u751f  \u626b\u63cf\u3000  cndy001 zhuyj(\u6731\u836b\u9636)  OCR  \uff0c\u65e7\u96e8\u697c\u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u4e8c\u5341\u4e09\u7ae0 \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u7686\u60ca\u3002 \u3000\u3000\u5c0f\u6587\u8f6c\u8138\u770b\u7740\u738b\u5c11\u537f\uff0c\u7adf\u7136\u53d8\u5f97\u6ee1\u9762\u51f6\u6c14\u773c\u4e2d\u6740\u673a\u9690\u73b0\u3002\u738b\u5c11\u537f\u4e0d\u7981\u540e\u9000\u4e86\u4e00\u6b65\uff0c\u9053\uff1a \u201c\u5c0f\u6587\uff0c\u4f60\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u5b9e\u5728\u60f3\u4e0d\u901a\uff0c\u5c0f\u6587\u4e0d\u662f\u5df2\u7ecf\u670d\u7528\u4e86\u89e3\u836f\uff0c\u4f59\u6bd2\u5c3d\u9664\u4e86\u5417\uff1f\u83ab\u975e\u8fd9\u68a6\u5e7b\u4e4b\u5200\u771f\u5982\u4f20\u8bf4\u4e2d \u4e00\u6837\uff0c\u6709\u5f79\u4eba\u51fa\u5200\u4e4b\u80fd\uff1f \u3000\u3000\u5c0f\u6587\u5df2\u4e3e\u8d77\u4e86\u5b9d\u5251\uff01 \u3000\u3000\u5251\u5149\u76f4\u6307\u5411\u738b\u5c11\u537f\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u559d\u9053\uff1a\u201c\u5c0f\u6587\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u4e0d\u8981\u542c\u5176\u4ed6\u4eba\u7684\u8bdd\uff0c\u5feb\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u5c0f\u6587\u9053\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u8bdd\u97f3\u672a\u843d\uff0c\u5b9d\u5251\u5df2\u5e7b\u8d77\u4e00\u7247\u51b0\u96ea\u822c\u523a\u76ee\u7684\u5251\u5149\uff01 \u3000\u3000\u5b9d\u5251\u523a\u51fa\uff0c\u5e26\u8d77\u4e00\u80a1\u9510\u5229\u7684\u98ce\u58f0\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u5979\u523a\u7684\u4e0d\u662f\u738b\u5c11\u537f\uff0c\u800c\u662f\u9ed1\u8863\u8499\u9762\u4eba\uff01 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u5927\u5403\u4e00\u60ca\uff01 \u3000\u3000\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u7728\u773c\u95f4\uff0c\u5251\u5149\u5df2\u523a\u5230\u4ed6\u9762\u524d\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u987f\u65f6\u6709\u4e9b\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u5c0f\u6587\u6325\u52a8\u5b9d\u5251\uff0c\u5251\u5149\u5982\u72c2\u98ce\u66b4\u96e8\u4e00\u822c\uff0c\u4e00\u4e0b\u5c06\u9ed1\u8863\u8499\u9762\u4eba\u541e\u6ca1\u4e86\uff0c\u591c\u7a7a\u4e2d\u53ea\u542c\u5f97\u5c0f\u6587\u5c16 \u5389\u5730\u5927\u53eb\u58f0\uff1a\u201c\u6211\u6740\u4e86\u4f60\uff01\u201d \u3000\u3000\u8840\u624b\u65b9\u8f6e\u6b63\u60f3\u4e0a\u524d\u5939\u653b\uff0c\u5c0f\u96c5\u5df2\u98de\u8eab\u800c\u4e0a\uff0c\u633a\u5251\u67b6\u4f4f\u4e86\u4ed6\u7684\u957f\u5200\u3002 \u3000\u3000\u4e8c\u4eba\u987f\u65f6\u6597\u5728\u4e00\u8d77\u3002 \u3000\u3000\u5200\u5251\u76f8\u4ea4\uff0c\u66b4\u8d77\u4e00\u70b9\u70b9\u706b\u82b1\u3002 \u3000\u3000\u8000\u773c\u7684\u706b\u82b1\u3002 \u3000\u3000\u94a2\u94c1\u4ea4\u51fb\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\u3002 \u3000\u3000\u60dc\u7389\u4e0d\u77e5\u4f55\u65f6\u5df1\u7ad9\u5728\u738b\u5c11\u537f\u8eab\u8fb9\uff0c\u7b11\u9053\uff1a\u201c\u738b\u5927\u4eba\u53d7\u60ca\u4e86\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u5598\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u5c0f\u6587\u4e0d\u4f1a\u518d\u4f24\u6211\uff0c\u6211\u5bf9\u5979\u6709\u4fe1\u5fc3\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u60dc\u7389\uff0c\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u8fd9\u662f\u6211\u548c\u5c0f\u6587\u59d0\u8ba2\u7684\u4e00\u4e2a\u8ba1\u7b56\uff0c\u6ca1\u60f3\u5230\u8fd8\u771f\u6d3e\u4e0a\u7528\u573a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e0d\u7981\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c31\u4f60\u4eec\u9b3c\u70b9\u5b50\u591a\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4e24\u773c\u8f6c\u8fc7\u53bb\u770b\u7740\u6218\u5c40\u3002 \u3000\u3000\u53ea\u89c1\u9ed1\u8863\u8499\u9762\u4eba\u5df2\u8eb2\u8fc7\u4e86\u5c0f\u6587\u4e00\u8f6e\u51cc\u5389\u7684\u5251\u62db\u3002 \u3000\u3000\u4ed6\u4f38\u624b\u4e00\u64a9\u9ed1\u8863\u957f\u888d\uff0c\u53f3\u624b\u5728\u8170\u95f4\u4e00\u626f\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u628a\u7cbe\u5149\u8000\u76ee\u7684\u77ed\u5200\u3002 \u3000\u3000\u77ed\u5200\u5e7b\u8d77\u6ee1\u5929\u5200\u5f71\uff0c\u4e0e\u5c0f\u6587\u7684\u957f\u5251\u7f20\u6597\u5728\u4e00\u8d77\u3002 \u3000\u3000\u770b\u6765\uff0c\u8fd9\u9ed1\u8863\u8499\u9762\u4eba\u679c\u7136\u6b66\u529f\u8d85\u7edd\uff0c\u5c0f\u6587\u5df2\u7ecf\u7ec3\u5230\u4e5d\u6210\u706b\u5019\u7684\u201c\u5251\u6d77\u6d74\u9b42\u201d\u7adf\u7136\u62b5\u6321\u4e0d \u4e86\u4ed6\u77ed\u5200\u7684\u8fdb\u653b\u3002 \u3000\u3000\u518d\u770b\u5c0f\u96c5\uff0c\u5728\u8840\u624b\u65b9\u8f6e\u957f\u5200\u6d51\u539a\u6709\u529b\u7684\u8fdb\u653b\u4e4b\u4e0b\uff0c\u4f3c\u4e4e\u4e5f\u5904\u4e8e\u4e0b\u98ce\u4e86\uff01 \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u9ed1\u8863\u8499\u9762\u4eba\u5927\u53eb\u9053\uff1a\u201c\u5e76\u80a9\u800c\u4e0a\uff0c\u628a\u4ed6\u4eec\u5168\u6740\u5149\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u53bb\u5e2e\u5c0f\u96c5\u5939\u653b\u65b9\u8f6e\uff0c\u6211\u53bb\u52a9\u5c0f\u6587\uff01\u56db\u5927\u6355\u5934\u3001\u6797\u53f8\u6848\uff01\u4f60\u4eec\u5e26\u4eba \u6321\u4f4f\u6740\u624b\u4eec\uff0c\u77f3\u742a\u3001\u5f20\u9e9f\u4fdd\u62a4\u738b\u5927\u4eba\uff01\u201d \u3000\u3000\u7a0e\u5b8c\uff0c\u5979\u5df2\u62bd\u51fa\u77ed\u5251\uff0c\u98de\u8eab\u6251\u5411\u9ed1\u8863\u8499\u9762\u4eba\u3002 \u3000\u3000\u5979\u7684\u77ed\u5251\u4e0e\u5c0f\u6587\u7684\u5b9d\u5251\u4e00\u957f\u4e00\u77ed\uff0c\u914d\u5408\u5f97\u76f8\u5f97\u76ca\u5f70\uff0c\u987f\u65f6\u5df2\u5c06\u5c40\u9762\u6273\u5e73\u3002 \u3000\u3000\u5c0f\u96c5\u6709\u60dc\u7389\u4e0a\u524d\u52a9\u9635\uff0c\u60c5\u51b5\u4e5f\u6709\u8f6c\u673a\u4e86\u3002 \u3000\u3000\u6392\u6210\u4e09\u89d2\u9635\u6cd5\u7684\u51f6\u624b\u4eec\u4e00\u9f50\u53d1\u8db3\u5411\u524d\u731b\u51b2\uff0c\u4f46\u675c\u671b\u6708\u3001\u5434\u94c1\u5cf0\u3001\u5c91\u5578\u864e\u3001\u4e8e\u627f\u5fd7\u56db\u4eba\u5206 \u522b\u4ece\u56db\u9762\u731b\u51b2\u8fc7\u53bb\uff0c\u987f\u65f6\u5c06\u51f6\u624b\u4eec\u7d27\u5bc6\u7684\u9635\u6cd5\u51b2\u6563\u4e86\u3002 \u3000\u3000\u6797\u4e0d\u51e1\u6307\u6325\u7740\u4e09\u5341\u56db\u540d\u6c5f\u5357\u5206\u53f8\u597d\u624b\uff0c\u6325\u821e\u7740\u5200\u3001\u5251\u3001\u94c1\u7ec3\u3001\u4e00\u9f50\u6d8c\u4e0a\uff0c\u4e0e\u6740\u624b\u4eec\u76f8\u5bf9 \u731b\u6597\u8d77\u6765\u3002 \u3000\u3000\u77f3\u742a\u3001\u5f20\u9e9f\u5e26\u7740\u516b\u540d\u957f\u67aa\u624b\uff0c\u7ec4\u6210\u534a\u4e2a\u5706\u5708\uff0c\u5c06\u738b\u5c11\u537f\u56f4\u5728\u4e86\u5eca\u67f1\u4e4b\u540e\uff0c\u867d\u7136\u6709\u4e24\u540d\u6740 \u624b\u51b2\u8fd1\u4e86\u67f1\u5b50\uff0c\u4f46\u4e0d\u8fc7\u4e24\u4e09\u62db\u95f4\uff0c\u5c31\u88ab\u77f3\u3001\u5f20\u4e8c\u4eba\u4e00\u5251\u4e00\u4e2a\uff0c\u523a\u6740\u4e8e\u5730\u3002 \u3000\u3000\u5c0f\u6587\u957f\u5251\u8fd0\u8f6c\u5982\u98ce\uff0c\u8eab\u5f62\u7075\u6d3b\u5730\u95ea\u52a8\u7740\uff0c\u6bcf\u4e00\u5251\u90fd\u5168\u529b\u653b\u5411\u4e86\u9ed1\u8863\u8499\u9762\u4eba\u7684\u8981\u5bb3\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5979\u5df2\u662f\u53ea\u653b\u4e0d\u5b88\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u8499\u9762\u4eba\u67b6\u5f00\u5979\u7684\u957f\u5251\uff0c\u60f3\u8981\u53cd\u51fb\u4e4b\u65f6\uff0c\u7a0b\u5c0f\u8776\u5c31\u4f1a\u6325\u52a8\u77ed\u5251\uff0c\u51b2\u4e0a\u7a81\u88ad\u3002 \u3000\u3000\u201c\u4e00\u5bf8\u957f\uff0c\u4e00\u5bf8\u5f3a\uff0c\u4e00\u5bf8\u77ed\uff0c\u4e00\u5bf8\u9669\u201d\uff0c\u7a0b\u5c0f\u8776\u624b\u4e2d\u7684\u77ed\u5251\u7528\u5f97\u90fd\u662f\u8d34\u8eab\u8fd1\u6218\u4e4b\u62db\u6570\uff0c \u6740\u5f97\u8499\u9762\u9ed1\u8863\u4eba\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u5f88\u663e\u7136\uff0c\u4ed6\u5bf9\u8fd9\u4e00\u957f\u4e00\u77ed\u4e24\u67c4\u5251\u7684\u8054\u624b\u5408\u51fb\u6ca1\u6709\u5fc3\u7406\u51c6\u5907\uff0c\u6240\u4ee5\uff0c\u4e00\u65f6\u4e4b\u95f4\u627e\u4e0d\u51fa\u7834\u89e3 \u4e4b\u6cd5\uff0c\u53ea\u80fd\u4e00\u8fb9\u7528\u77ed\u5200\u62b5\u6321\uff0c\u4e00\u8fb9\u4e0d\u65ad\u5730\u53d8\u5316\u7740\u8eab\u5f62\u6b65\u6cd5\uff0c\u60f3\u7528\u95ea\u8eb2\u672f\u907f\u5f00\u5c0f\u6587\u548c\u7a0b\u5c0f\u8776\u51cc \u5389\u4e4b\u6781\u7684\u8fdb\u653b\uff0c\u4ee5\u6c42\u53d6\u5f97\u4e00\u70b9\u5598\u606f\u4e4b\u673a\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u9ed1\u8863\u8499\u9762\u4eba\u62d4\u5730\u800c\u8d77\uff0c\u4e0d\u987e\u5df2\u523a\u5230\u8eab\u8fb9\u7684\u4e24\u5251\uff0c\u5934\u524d\u811a\u540e\uff0c\u77ed\u5200\u76f4\u4e3e\uff0c\u98de\u5411\u738b\u5c11 \u537f\u3002 \u3000\u3000\u5c0f\u6587\u5927\u60ca\uff0c\u4e5f\u98de\u8eab\u524d\u53bb\u5835\u622a\u3002 \u3000\u3000\u8fd9\u6837\uff0c\u957f\u77ed\u5251\u6cd5\u7684\u914d\u5408\u653b\u51fb\u5c31\u65e0\u6cd5\u518d\u884c\u5b9e\u65bd\u4e86\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u8eab\u5728\u534a\u7a7a\uff0c\u5de6\u624b\u4e2d\u5ffd\u7136\u53c8\u591a\u51fa\u4e00\u628a\u957f\u5200\uff0c\u843d\u4e0b\u5730\u6765\uff0c\u957f\u5200\u5bf9\u957f\u5251\u3001\u77ed\u5200\u5bf9\u77ed \u5251\uff0c\u53c8\u4e0e\u7a0b\u5c0f\u8776\u548c\u5c0f\u6587\u6253\u4e86\u4e2a\u5e73\u624b\u3002 \u3000\u3000\u539f\u6765\uff0c\u4ed6\u6251\u5411\u738b\u5c11\u537f\uff0c\u53ea\u662f\u865a\u62db\uff0c\u662f\u7f13\u5175\u4e4b\u8ba1\uff0c\u53ea\u4e0d\u8fc7\u8d81\u5f15\u5f00\u5c0f\u6587\u4e4b\u673a\uff0c\u62bd\u51fa\u4e86\u957f\u5200\u3002 \u3000\u3000\u5355\u5200\u53d8\u53cc\u5200\uff0c\u5f88\u5feb\uff0c\u7a0b\u5c0f\u8776\u548c\u5c0f\u6587\u5c31\u6709\u4e9b\u843d\u4e8e\u4e0b\u98ce\u4e86\u3002 \u3000\u3000\u8840\u624b\u65b9\u8f6e\u5374\u5df2\u9677\u5165\u4e86\u82e6\u6218\u3002 \u3000\u3000\u867d\u7136\u4ed6\u529b\u5927\u65e0\u7a77\uff0c\u4e00\u628a\u957f\u5200\u6325\u821e\u51fa\u4e86\u6f2b\u5929\u5200\u5f71\uff0c\u6fc0\u8d77\u7684\u5200\u98ce\u5c06\u60dc\u7389\u548c\u5c0f\u96c4\u7684\u5934\u9aeb\u90fd\u5439\u6563 \u5f00\u6765\uff0c\u4f46\u662f\u60dc\u7389\u548c\u5c0f\u96c5\u5374\u662f\u8282\u8282\u8fdb\u903c\u3002 \u3000\u3000\u4e8c\u5341\u62db\u8fc7\u540e\uff0c\u5c0f\u96c5\u67f3\u8170\u4e00\u95ea\uff0c\u4f7f\u51fa\u4e00\u62db\u201c\u897f\u98ce\u7626\u9a6c\u201d\uff0c\u76f4\u523a\u65b9\u8f6e\u5de6\u817f\u3002 \u3000\u3000\u65b9\u8f6e\u5df2\u7ecf\u7528\u5200\u78d5\u5f00\u957f\u5251\uff0c\u5374\u89c1\u60dc\u7389\u4e00\u53cc\u767d\u7389\u822c\u79c0\u7f8e\u7684\u5c0f\u624b\u5df2\u7ecf\u653b\u5230\u4e86\u81ea\u5df1\u7684\u80f8\u524d\u3002 \u3000\u3000\u522b\u770b\u8fd9\u624b\u7ea4\u5de7\u7f8e\u4e3d\uff0c\u80fd\u5f15\u4eba\u6687\u601d\uff0c\u53ef\u649e\u4e0a\u4e00\u4e0b\uff0c\u5f53\u573a\u5c31\u5f97\u5410\u8840\uff01 \u3000\u3000\u65b9\u8f6e\u65e0\u5948\u4e4b\u4e0b\uff0c\u6b63\u60f3\u95ea\u8eab\u9519\u5f00\uff0c\u8eb2\u907f\u8fd9\u4e00\u4e0a\u4e0b\u80fd\u624b\u6740\u7740\uff0c\u53ef\u5c0f\u96c5\u7684\u957f\u5251\u901f\u5ea6\u7a81\u7136\u52a0\u5feb\u4e86 \u4e00\u534a\u3002 \u3000\u3000\u5251\u5149\u4e00\u95ea\uff0c\u8840\u5149\u6d8c\u73b0\u3002 \u3000\u3000\u6740\u624b\u4e4b\u738b\uff0c\u8840\u624b\u65b9\u8f6e\u7684\u5de6\u817f\u4e4b\u4e0a\u5df2\u88ab\u5272\u5f00\u4e86\u4e00\u4e2a\u5927\u53e3\u5b50\u3002 \u3000\u3000\u9c9c\u8840\u55b7\u6d8c\u3002 \u3000\u3000\u65b9\u8f6e\u5389\u53eb\u4e00\u58f0\uff1a\u201c\u8001\u5927\u6740\u4e86\u4f60\u8fd9\u5c0f\u8d31\u4eba\uff01\u201d \u3000\u3000\u957f\u5200\u6325\u52a8\u7684\u66f4\u75af\u72c2\u4e86\uff0c\u5200\u52bf\u4e5f\u66f4\u60ca\u4eba\u3002 \u3000\u3000\u4f46\u60dc\u7389\u548c\u5c0f\u96c5\u5374\u4e0d\u518d\u4e0e\u4ed6\u786c\u633a\u3002 \u3000\u3000\u53ea\u89c1\u4e8c\u4eba\u8eab\u6cd5\u7075\u6d3b\u673a\u52a8\uff0c\u56f4\u7740\u65b9\u8f6e\u6253\u8f6c\u8f6c\uff0c\u65b9\u8f93\u7528\u5c3d\u4e86\u5168\u8eab\u529b\u6c14\u62a1\u51fa\u7684\u4e8c\u5341\u591a\u5200\u5374\u5168\u90e8 \u62a1\u7a7a\u4e86\u3002 \u3000\u3000\u8fde\u4e8c\u4eba\u7684\u8863\u89d2\u4e5f\u6ca1\u635e\u7740\u4e00\u7247\u3002 \u3000\u3000\u65b9\u8f6e\u62a1\u51fa\u4e8c\u5341\u591a\u5200\uff0c\u987f\u65f6\u89c9\u5f97\u5fc3\u6d6e\u6c14\u71e5\uff0c\u4e24\u81c2\u53d1\u9178\uff0c\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u6b63\u60f3\u8c03\u6574\u4e00\u4e0b\u81ea\u5df1\u7684 \u5185\u606f\uff0c\u60dc\u7389\u548c\u5c0f\u96c5\u53c8\u5feb\u901f\u8054\u624b\u5411\u4ed6\u653b\u51fb\u51cc\u5389\u7684\u4e00\u62db\u3002 \u3000\u3000\u65b9\u8f6e\u5c3a\u5f97\u53c8\u6325\u5200\u5e94\u6218\u3002 \u3000\u3000\u4ed6\u5f88\u6e05\u695a\uff0c\u8fd9\u4e24\u4e2a\u5c0f\u8d31\u4eba\u662f\u60f3\u5c31\u6b64\u7f20\u4f4f\u4ed6\uff0c\u4e0d\u8ba9\u4ed6\u817e\u51fa\u624b\u6765\u70b9\u7a74\u6b62\u8840\uff0c\u4e0d\u8ba9\u4ed6\u6709\u5598\u606f\u4e4b \u673a\uff0c\u60f3\u8ba9\u4ed6\u6d41\u8840\u6d41\u6b7b\u3001\u7d2f\u6b7b\u3002 \u3000\u3000\u6e05\u695a\u5f52\u6e05\u695a\uff0c\u4ed6\u53c8\u5b9e\u5728\u627e\u4e0d\u51fa\u89e3\u51b3\u95ee\u9898\u7684\u529e\u6cd5\u6765\u3002 \u3000\u3000\u4ed6\u53ea\u80fd\u4e00\u5200\u4e00\u5200\u5730\u4e0e\u60dc\u7389\u548c\u5c0f\u96c5\u786c\u62fc\u3002 \u3000\u3000\u65b9\u8f6e\u5df2\u7ecf\u611f\u89c9\u5230\u81ea\u5df1\u7684\u4f53\u529b\u5728\u98de\u5feb\u5730\u6d88\u5931\uff0c\u5de6\u817f\u4e0a\u6e7f\u4e4e\u4e4e\u5730\u6cbe\u6ee1\u4e86\u9c9c\u8840\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u6709\u4e9b\u5fc3\u614c\u6c14\u77ed\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u5185\u606f\u4e5f\u5f00\u59cb\u7d0a\u4e71\u3002 \u3000\u3000\u8fd9\u6837\u4e0b\u53bb\u53ef\u4e0d\u884c\uff0c\u8981\u5c06\u8001\u547d\u4e22\u5728\u8fd9\u91cc\u4e86\uff01 \u3000\u3000\u65b9\u8f6e\u6697\u5fd6\u9053\uff0c\u95ea\u52a8\u7740\u76ee\u5149\uff0c\u5bfb\u627e\u8131\u56f0\u7684\u673a\u4f1a\u3002 \u3000\u3000\u4ed6\u975e\u5e38\u671f\u671b\u6709\u4eba\u80fd\u6765\u5e2e\u52a9\u4e00\u628a\uff0c\u53ef\u56db\u4e0b\u4e00\u770b\uff0c\u6740\u624b\u4eec\u6b63\u4e0e\u56db\u5927\u6355\u5934\u7684\u90e8\u4e0b\u8840\u6218\u6b63\u9163\uff0c\u4e0d \u53ef\u80fd\u6709\u4eba\u80fd\u6765\u5e2e\u4ed6\u3002 \u3000\u3000\u60dc\u7389\u548c\u5c0f\u96c5\u53c8\u5f00\u59cb\u8fdb\u653b\u4e86\u3002 \u3000\u3000\u65b9\u8f6e\u5ffd\u7136\u6a2a\u4e0b\u4e00\u6761\u5fc3\uff0c\u6697\u9053\uff1b\u201c\u597d\u5427\uff0c\u5c31\u7b97\u6b7b\uff0c\u8001\u592b\u4e5f\u8981\u627e\u4e00\u4e2a\u57ab\u80cc\u7684\uff01\u201d \u3000\u3000\u6740\u624b\u4eec\u5728\u4eba\u6570\u4e0a\u6bd4\u675c\u671b\u6708\u4ed6\u4eec\u8981\u591a\u5f97\u591a\uff0c\u800c\u4e14\u8fd9\u6279\u6740\u624b\u7684\u6b66\u529f\u4e5f\u5f88\u597d\uff0c\u90fd\u4e0d\u6bd4\u4e0a\u6b21\u6765\u8fc7 \u7684\u90a3\u4e9b\u91d1\u5200\u6740\u624b\u5dee\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\u6709\u7684\u4ee5\u4e00\u654c\u4e8c\uff0c\u6709\u7684\u4ee5\u4e00\u654c\u4e09\u3002\u867d\u7136\u6740\u624b\u4eec\u65e0\u6cd5\u8131\u8eab\u6740\u738b\u5c11\u537f\uff0c\u53ef\u56db\u5927\u6355\u5934\u4e5f \u90fd\u9677\u5165\u4e86\u82e6\u6218\u3002 \u3000\u3000\u6797\u4e0d\u51e1\u548c\u4ed6\u624b\u4e0b\u6c5f\u5357\u5206\u53f8\u7684\u4e09\u5341\u56db\u540d\u597d\u624b\u4e5f\u5728\u82e6\u6218\u3002 \u3000\u3000\u60e8\u53eb\u4e00\u58f0\u63a5\u7740\u4e00\u58f0\u3002 \u3000\u3000\u8840\u5149\u4e00\u9635\u63a5\u4e00\u9635\u5730\u6d8c\u73b0\u3002 \u3000\u3000\u5730\u4e0a\u5df2\u8eba\u5012\u4e86\u4e0d\u4e0b\u56db\u5341\u4eba\uff0c\u8fd9\u5176\u4e2d\u6709\u6740\u624b\uff0c\u4e5f\u6709\u6c5f\u5357\u5206\u53f8\u7684\u597d\u624b\u3002 \u3000\u3000\u771f\u662f\u6740\u654c\u4e00\u4e07\uff0c\u81ea\u635f\u516b\u5343\u554a\uff01 \u3000\u3000\u675c\u671b\u6708\u529b\u654c\u4e09\u4eba\uff0c\u597d\u4e0d\u5bb9\u6613\u6740\u6389\u4e86\u4e00\u4e2a\uff0c\u957f\u5251\u5c1a\u672a\u4ece\u654c\u4eba\u80f8\u524d\u62bd\u51fa\u6765\uff0c\u8eab\u540e\u53c8\u6709\u4e09\u6837\u5175 \u5668\u88ad\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u52a8\u4e86\u8bd5\u7528\u201c\u5251\u6d77\u6d74\u9b42\u201d\u7684\u5ff5\u5934\uff0c\u4f46\u7a0b\u5c0f\u8776\u7684\u544a\u8beb\u4e00\u76f4\u5728\u4ed6\u8eab\u8fb9\uff0c\u4ed6\u4e0d\u6562\u5192\u8fd9\u4e2a\u9669\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u53ea\u6709\u62fc\u5c3d\u5168\u529b\uff0c\u6325\u5251\u8840\u6218\u3002 \u3000\u3000\u5c0f\u96c5\u548c\u60dc\u7389\u540c\u65f6\u653b\u4e0a\u65f6\uff0c\u65b9\u8f6e\u7adf\u7136\u4e22\u5f00\u5c0f\u96c5\u4e8e\u4e0d\u987e\uff0c\u4e3e\u8d77\u5927\u5200\uff0c\u76f4\u780d\u60dc\u7389\u3002 \u3000\u3000\u60dc\u7389\u8eb2\u8fc7\u4e24\u5200\uff0c\u60f3\u7740\u65b9\u8f6e\u8be5\u56de\u5200\u53bb\u67b6\u5c0f\u96c5\u7684\u957f\u5251\u4e86\uff0c\u5374\u6ca1\u60f3\u5230\u7b2c\u4e09\u5200\u4ecd\u7136\u5411\u5979\u780d\u6765\u3002 \u3000\u3000\u5c0f\u96c5\u7684\u957f\u5251\u6beb\u4e0d\u963b\u62e6\u5730\u523a\u5165\u4e86\u65b9\u8f6e\u7684\u540e\u80cc\uff0c\u8db3\u6709\u56db\u5206\u6df1\u6d45\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u5e94\u8be5\u662f\u5f88\u5389\u5bb3\u7684\u4e00\u5251\u4e86\uff0c\u6ca1\u60f3\u5230\u65b9\u8f6e\u7adf\u7136\u6ee1\u4e0d\u5728\u4e4e\u4f3c\u5730\uff0c\u4ecd\u76f4\u5411\u60dc\u7389\u6251\u53bb\u3002 \u3000\u3000\u60dc\u7389\u5927\u60ca\u4e4b\u4e0b\uff0c\u4e00\u4e2a\u95ea\u8eab\uff0c\u52c9\u5f3a\u8eb2\u5f00\u4e86\u7b2c\u4e09\u5200\u3002 \u3000\u3000\u65b9\u8f6e\u4e00\u5200\u51fb\u7a7a\uff0c\u5e76\u4e0d\u505c\u624b\uff0c\u5de6\u624b\u6a2a\u7740\u5c01\u5c71\uff0c\u963b\u4f4f\u4e86\u60dc\u7389\u7684\u53bb\u8def\uff0c\u53f3\u624b\u594b\u529b\u5411\u56de\u4e00\u9012\uff0c\u5927 \u5200\u7684\u5200\u67c4\u6b63\u51fb\u4e2d\u4e86\u60dc\u7389\u7684\u80f8\u53e3\u3002 \u3000\u3000\u60dc\u7389\u60e8\u53eb\u4e00\u58f0\uff0c\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff01 \u3000\u3000\u675c\u671b\u6708\u542c\u89c1\u60dc\u7389\u60e8\u53eb\uff0c\u60ca\u5f97\u4e00\u56de\u5934\uff0c\u6b63\u770b\u89c1\u60dc\u7389\u5012\u5728\u4e86\u5730\u4e0a\u3002 \u3000\u3000\u4ed6\u5fc3\u6025\u5982\u711a\uff0c\u60f3\u53bb\u6551\u63f4\uff0c\u5374\u54ea\u91cc\u8131\u5f97\u5f00\u8eab\u53bb\uff01 \u3000\u3000\u201c\u60dc\u7389\uff0c\u60dc\u7389\uff0c\u4f60\u8981\u575a\u6301\u4f4f\uff01\u201d\u675c\u671b\u6708\u4e00\u9762\u6325\u5251\u594b\u6218\uff0c\u4e00\u9762\u72c2\u547c\uff1a\u201c\u60dc\u7389\u4f60\u4e0d\u80fd\u6b7b\u554a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u542c\u89c1\u4e86\u675c\u671b\u6708\u7684\u72c2\u53eb\u58f0\uff0c\u56de\u773c\u4e00\u770b\uff0c\u770b\u89c1\u60dc\u7389\u5df2\u53d7\u4e86\u91cd\u4f24\uff0c\u800c\u65b9\u8f6e\u56e0\u4e3a\u62fc\u547d\u5f97\u624b\uff0c \u53c8\u6fc0\u8d77\u4e86\u51f6\u6b8b\u7684\u6740\u6027\uff0c\u6b63\u821e\u5200\u76f4\u903c\u5c0f\u96c5\uff01 \u3000\u3000\u5c0f\u96c5\u7adf\u7136\u8282\u8282\u8d25\u9000\u4e86\uff01 \u3000\u3000\u8fd9\u6837\u4e0b\u53bb\uff0c\u5c31\u8981\u51fa\u5927\u4e8b\u4e86\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u5ba1\u89c6\u4e86\u4e00\u4e0b\u6218\u5c40\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u77f3\u742a\uff0c\u5feb\u53bb\u5e2e\u5c0f\u96c5\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u8d81\u7740\u9ed1\u8863\u8499\u9762\u4eba\u6b63\u5168\u529b\u62b5\u6321\u5c0f\u6587\u4e00\u8f6e\u75af\u72c2\u7684\u5251\u62db\u8fdb\u653b\uff0c\u5de6\u624b\u5df2\u638f\u51fa\u7ec6\u7d22\uff0c\u624b\u81c2 \u4e00\u626c\uff0c\u4e00\u9053\u7ea2\u5f71\u76f4\u98de\u65b9\u8f6e\uff01 \u3000\u3000\u7ec6\u7d22\u51c6\u786e\u5730\u51fb\u4e2d\u4e86\u65b9\u8f6e\u7684\u624b\u8155\u3002 \u3000\u3000\u201c\u5f53\u5577\u201d\u4e00\u58f0\uff0c\u65b9\u8f6e\u5927\u5200\u8131\u624b\uff0c\u843d\u5728\u4e86\u5730\u4e0a\u3002 \u3000\u3000\u4e24\u624b\u7a7a\u7a7a\u7684\u65b9\u8f6e\u987f\u65f6\u53d1\u73b0\u81ea\u5df1\u8981\u9762\u5bf9\u7684\uff0c\u7531\u4e00\u67c4\u957f\u5251\u53d8\u6210\u4e86\u4e24\u67c4\u957f\u5251\u3002 \u3000\u3000\u4ed6\u7acb\u523b\u6740\u5fc3\u987f\u6d88\uff0c\u6050\u60e7\u4e4b\u5fc3\u5df2\u751f\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u6536\u56de\u7ec6\u7d22\uff0c\u7ea2\u5f71\u4e00\u95ea\uff0c\u53c8\u7f20\u5411\u9ed1\u8863\u8499\u9762\u4eba\uff0c\u5bf9\u5c0f\u6587\u9053\uff1a\u201c\u5c0f\u6587\uff0c\u4f60\u53bb\u5bf9\u4ed8\u90a3\u5e2e\u6740 \u624b\uff0c\u8fd9\u4e2a\u4ea4\u7ed9\u6211\u3002\u201d \u3000\u3000\u8fd9\u662f\u4e00\u4e2a\u5341\u5206\u5192\u9669\uff0c\u4f46\u4e5f\u5341\u5206\u806a\u660e\u7684\u51b3\u5b9a\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u81ea\u4fe1\u80fd\u5355\u72ec\u4e0e\u9ed1\u8863\u8499\u9762\u4eba\u6597\u4e0a\u4e09\u4e94\u5341\u62db\uff0c\u800c\u5728\u8fd9\u4e2a\u8fc7\u7a0b\u4e2d\uff0c\u5c0f\u6587\u5219\u53ef\u653e\u624b\u75db\u5bb0\u90a3 \u4e9b\u6740\u624b\u3002 \u3000\u3000\u5c0f\u6587\u7684\u201c\u5251\u6d77\u6d74\u9b42\u201d\u867d\u8bf4\u5bf9\u9ed1\u8863\u8499\u9762\u4eba\u4e0d\u8d77\u592a\u592a\u7684\u4f5c\u7528\uff0c\u4f46\u5bf9\u4ed8\u90a3\u4e9b\u6740\u624b\u4eec\uff0c\u5a01\u529b\u53ef\u5c31 \u60ca\u4eba\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5c0f\u6587\u5389\u53eb\u4e00\u58f0\uff1a\u201c\u5251\u6d77\u6d74\u9b42\uff01\u6321\u6211\u8005\u6b7b\uff01\u201d\u6574\u4e2a\u4eba\u5df2\u9690\u8eab\u4e8e\u4e00\u5708\u6151\u4eba\u7684\u5251\u5149\u4e4b\u4e2d\u3002 \u3000\u3000\u5251\u5149\u6240\u5230\u4e4b\u5904\uff0c\u6740\u624b\u4eec\u7eb7\u7eb7\u5012\u5730\uff0c\u60e8\u547c\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\u3002 \u3000\u3000\u4f46\u89c1\u4eba\u5934\u6eda\u6eda\uff0c\u8840\u7bad\u6a2a\u98de\u3002 \u3000\u3000\u7b49\u5230\u5c0f\u6587\u505c\u4e0b\u7b2c\u4e00\u6ce2\u653b\u51fb\uff0c\u573a\u4e2d\u6740\u624b\u5df2\u5c3a\u5269\u4e0b\u4e86\u5341\u6765\u540d\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u867d\u8bf4\u6e10\u5360\u4e0a\u98ce\uff0c\u5df2\u5c06\u7a0b\u5c0f\u8776\u903c\u5f97\u5de6\u652f\u53f3\u9000\uff0c\u4f46\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u65b9\u8f6e\u5927\u58f0\u547c\u53eb \u9053\uff1a\u201c\u8001\u5927\uff01\u6211\u652f\u6301\u4e0d\u4f4f\u5566\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u56db\u9762\u4e00\u770b\uff0c\u5df2\u77e5\u4eca\u591c\u8d25\u5c40\u5df2\u5b9a\uff0c\u8dfa\u4e86\u8dfa\u811a\uff0c\u9053\uff1a\u201c\u64a4\uff01\u201d \u3000\u3000\u6740\u624b\u4eec\u867d\u8d25\uff0c\u4f46\u9003\u8d77\u547d\u6765\u5374\u4e0d\u6162\uff0c\u9ed1\u8863\u8499\u9762\u4eba\u64a4\u5b57\u65b9\u51fa\u53e3\uff0c\u9662\u4e2d\u4ec5\u5269\u7684\u5341\u6570\u540d\u6740\u624b\u5df2\u98de \u8eab\u63a0\u4e0a\u4e86\u56f4\u5899\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u62a2\u5230\u65b9\u8f6e\u8eab\u8fb9\uff0c\u6276\u4f4f\u4ed6\uff0c\u4e24\u811a\u4e00\u8e6c\u5730\uff0c\u4e8c\u4eba\u5df2\u817e\u8eab\u800c\u8d77\uff0c\u8f6c\u773c\u95f4\u6d88\u5931\u5728\u56f4\u5899 \u4e4b\u5916\u3002 \u3000\u3000\u4f46\u542c\u832b\u832b\u591c\u8272\u4e4b\u4e2d\uff0c\u9ed1\u8863\u8499\u9762\u4eba\u5389\u53eb\u9053\uff1a\u201c\u738b\u5c11\u537f\uff0c\u7a0b\u5c0f\u8776\uff0c\u8001\u592b\u7edd\u4e0d\u4f1a\u653e\u8fc7\u4f60\u4eec\uff01\u201d \u3000\u3000\u6740\u624b\u4eec\u521a\u4e00\u5f00\u59cb\u64a4\u9000\u65f6\uff0c\u56db\u5927\u6355\u5934\u4eec\u8fd8\u60f3\u8ffd\u4e0a\u53bb\uff0c\u4f46\u5374\u662f\u6c57\u900f\u8eab\u8863\uff0c\u65e0\u529b\u518d\u8ffd\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u4e22\u5f00\u4e03\u661f\u5b9d\u5251\uff0c\u6251\u5411\u8eba\u5012\u5728\u5730\u7684\u60dc\u7389\uff0c\u5636\u58f0\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u6ca1\u4e8b\u5427\uff1f\u4f60\u53ef\u4e0d\u80fd\u6b7b \u554a\uff01\u6211\u4e0d\u4f1a\u8ba9\u4f60\u6b7b\u7684\uff01\u201d \u3000\u3000\u60dc\u7389\u7d27\u95ed\u7684\u53cc\u773c\u6162\u6162\u5730\u7741\u5f00\u4e86\uff0c\u7528\u5fae\u5f31\u7684\u58f0\u97f3\u9053\uff1a\u201c\u675c\u90ce\uff0c\u4f60\u4e0d\u8981\u4f24\u5fc3\uff0c\u60dc\u7389\u4e0d\u4f1a\u6b7b \u7684\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u5fc3\u91cc\u4e00\u9178\uff0c\u5fcd\u4e0d\u4f4f\u53cc\u76ee\u4e2d\u843d\u4e0b\u6ce2\u6765\uff0c\u54d1\u58f0\u5730\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u9192\u4e86\uff01\u592a\u597d\u4e86\uff01\u201d \u3000\u3000\u60dc\u7389\u7684\u58f0\u97f3\u8d8a\u6765\u8d8a\u5fae\u5f31\uff0c\u65ad\u65ad\u7eed\u7eed\u5730\u9053\uff1a\u201c\u675c\u90ce\uff0c\u60dc\u7389\u4e0d\u4f1a\u6b7b\uff0c\u4e5f\u4e0d\u60f3\u6b7b\u2026\u2026\u60dc\u7389\u6b7b\u4e86\uff0c \u8fd8\u600e\u4e48\u7ed9\u675c\u90ce\u751f\u2026\u2026\u751f\u5c0f\u5b9d\u5b9d\u5462\u2026\u2026\u201d \u3000\u3000\u4e24\u773c\u7f13\u7f13\u5f00\u4e0a\uff0c\u6655\u8ff7\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u62b1\u4f4f\u5979\u54ed\u8d77\u6765\u3002 \u3000\u3000\u771f\u662f\u7537\u513f\u6709\u6cea\u4e0d\u8f7b\u5f39\uff0c\u53ea\u662f\u672a\u5230\u4f24\u5fc3\u5904\u554a\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u8d70\u8fc7\u53bb\uff0c\u4f38\u624b\u6309\u4f4f\u60dc\u7389\u7684\u8109\u95e8\uff0c\u597d\u4e00\u4f1a\u513f\uff0c\u9053\uff1a\u201c\u675c\u53f8\u4e3b\uff0c\u4f60\u4e0d\u7528\u7740\u6025\uff0c\u60dc\u7389\u7684 \u4f24\u5f88\u91cd\uff0c\u4f46\u8fd8\u6709\u6551\uff0c\u4e0d\u4f1a\u6b7b\u7684\uff0c\u5c0f\u6587\u3001\u5c0f\u96c5\uff0c\u4f60\u4eec\u6765\uff0c\u628a\u60dc\u7389\u62ac\u5230\u6211\u623f\u91cc\u53bb\uff0c\u6211\u6765\u66ff\u5979\u7528\u5185 \u529f\u7597\u4f24\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u54d1\u58f0\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u5979\u771f\u7684\u6ca1\u4e8b\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u80af\u5b9a\u5730\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u4f1a\u5c3d\u5168\u529b\u6cbb\u597d\u5979\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8c22\u603b\u6355\u5934\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u79fb\u5f00\u4e86\u76ee\u5149\uff0c\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u53ef\u8c22\u7684\uff0c\u60dc\u7389\u4e0e\u6211\u60c5\u540c\u59d0\u59b9\uff0c\u6551\u5979\u662f\u5e94\u8be5\u7684\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5979\u548c\u5c0f\u6587\u3001\u5c0f\u96c5\u4e00\u8d77\u5c06\u60dc\u7389\u62ac\u8fdb\u4e86\u623f\u91cc\uff0c\u5173\u4e0a\u4e86\u623f\u95e8\u3002 \u3000\u3000\u675c\u671b\u6708\u75f4\u75f4\u5730\u770b\u7740\u623f\u95e8\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u5c91\u5578\u864e\u8d70\u8fc7\u6765\uff0c\u4f4e\u58f0\u529d\u9053\uff1a\u201c\u675c\u8001\u5f1f\uff0c\u6709\u603b\u6355\u5934\u4eb2\u81ea\u51fa\u624b\u65bd\u6551\uff0c\u60dc\u7389\u7684\u4f24\u4f1a\u597d\u7684\uff0c\u4f60\u4e5f \u5f88\u7d2f\u4e86\uff0c\u4f11\u606f\u4e00\u4f1a\u53bb\u5427\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u575a\u51b3\u5730\u9053\uff1a\u201c\u6211\u4e0d\u8d70\uff0c\u66f4\u4e0d\u80fd\u53bb\u4f11\u606f\uff0c\u6211\u8981\u5728\u8fd9\u91cc\u7b49\u7740\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u7528\u5185\u529f\u66ff\u4eba\u7597\u4f24\uff0c\u662f\u5341\u5206\u8017\u8d39\u5185\u529b\u7684\uff0c\u603b\u6355\u5934\u521a\u521a\u4e0e\u68a6\u5e7b\u4e4b\u5200 \u82e6\u6597\u4e86\u4e00\u756a\uff0c\u53ea\u6015\u2026\u2026\u201d \u3000\u3000\u675c\u671b\u6708\u987f\u65f6\u60ca\u9192\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u5148\u7ed9\u60dc\u7389\u4e00\u70b9\u4f24\u836f\uff0c\u7a33\u4f4f\u5979\u7684\u4f24\u52bf\uff0c\u660e\u5929\u518d\u7528\u529f \u66ff\u5979\u7597\u4f24\u5427\uff01\u201d \u3000\u3000\u623f\u95e8\u5f00\u4e86\u4e00\u6761\u7f1d\uff0c\u5c0f\u96c5\u94bb\u4e86\u51fa\u6765\uff0c\u767d\u4e86\u675c\u671b\u6708\u4e00\u773c\uff0c\u9053\uff1a\u201c\u73b0\u5728\u624d\u60f3\u8d77\u6765\uff1f\u665a\u5566\uff01\u603b\u6355 \u5934\u5df2\u7ecf\u5f00\u59cb\u8fd0\u529f\u66ff\u60dc\u7389\u59d0\u7597\u4f24\u4e86\uff01\u4f60\u4eec\u8fd8\u4e0d\u5feb\u56db\u4e0b\u8b66\u6212\uff0c\u4ee5\u9632\u6740\u624b\u4eec\u6765\u4e2a\u56de\u9a6c\u67aa\uff0c\u90a3\u6837\u60c5\u51b5 \u5c31\u7cdf\u4e86\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u987f\u8db3\u9053\uff1a\u201c\u5509\uff0c\u6211\u600e\u4e48\u5c31\u6ca1\u6709\u60f3\u5230\u8fd9\u4e00\u5c42\u5462\uff01\u201d \u3000\u3000\u56db\u5927\u6355\u5934\u5206\u6563\u5f00\u6765\uff0c\u5b88\u62a4\u5728\u623f\u95f4\u56db\u5468\u3002 \u3000\u3000\u6797\u4e0d\u51e1\u3001\u77f3\u742a\u3001\u5f20\u9e9f\u5f00\u59cb\u8fa8\u8ba4\u9635\u4ea1\u540e\u7684\u6c5f\u5357\u5206\u53f8\u7684\u597d\u624b\u3002 \u3000\u3000\u4e09\u5341\u56db\u540d\u597d\u624b\uff0c\u7ecf\u8fc7\u8fd9\u4e00\u6218\u540e\uff0c\u53ea\u5269\u4e0b\u4e86\u4e8c\u5341\u4e00\u4eba\u3002 \u3000\u3000\u597d\u60e8\u70c8\u7684\u4e00\u6218\u554a\uff01 \u3000\u3000\u8859\u5f79\u4eec\u4e5f\u90fd\u6253\u7740\u706f\u7b3c\u8fc7\u6765\u4e86\uff0c\u5e2e\u7740\u4ed6\u4eec\u4e00\u8d77\u6253\u626b\u6218\u573a\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u79bb\u675c\u671b\u6708\u5f88\u8fd1\uff0c\u95f4\u4ed6\u9053\uff1a\u201c\u5c0f\u675c\uff0c\u4ece\u8fd9\u4e00\u6218\u770b\uff0c\u6740\u624b\u96c6\u56e2\u7684\u5b9e\u529b\u5e76\u672a\u5c3d\u51fa\uff0c\u800c\u6211 \u65b9\u5374\u5df1\u635f\u5931\u60e8\u91cd\uff0c\u4f60\u770b\u8be5\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u5982\u4eca\u4e4b\u8ba1\u6709\u4e24\u4e2a\u529e\u6cd5\u3002\u5176\u4e00\uff0c\u6211\u52a0\u7d27\u7ec3\u4e60\u5251\u6d77\u6d74\u9b42\uff0c\u5176\u4e8c\uff0c\u53ea\u597d \u70e6\u8bf7\u5434\u5144\u81ea\u4e2d\u5dde\u5206\u53f8\u8c03\u4e00\u6279\u5f97\u529b\u4eba\u624b\u6765\u626c\u5dde\uff0c\u8865\u5145\u5b9e\u529b\u4e86\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u70b9\u5934\u9053\uff1a\u201c\u7684\u786e\u53ea\u80fd\u5982\u6b64\uff0c\u56e0\u4e3a\u56db\u5927\u5206\u53f8\u4e2d\uff0c\u53ea\u6709\u4e2d\u5dde\u5206\u53f8\u79bb\u8fd9\u91cc\u6700\u8fd1\u4e86\u3002\u4e0d\u7792 \u8001\u5f1f\uff0c\u6211\u4e24\u5929\u524d\u5df2\u7ecf\u98de\u9e3d\u4f20\u4e66\u7ed9\u4e2d\u5dde\u5206\u53f8\uff0c\u8ba9\u4ed6\u4eec\u5c3d\u8d77\u7cbe\u82f1\u826f\u5c06\uff0c\u8d76\u6765\u626c\u5dde\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u7b11\u9053\uff1a\u201c\u5434\u5144\uff0c\u771f\u6709\u4f60\u7684\uff0c\u8fd9\u4e48\u8bf4\uff0c\u4ed6\u4eec\u5e94\u8be5\u5feb\u5230\u4e86\uff1f\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6700\u665a\u660e\u65e5\u9ec4\u660f\uff0c\u7edd\u5bf9\u53ef\u4ee5\u62b5\u8fbe\u626c\u5dde\u57ce\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u4e0d\u89c9\u7cbe\u795e\u4e3a\u4e4b\u4e00\u632f\uff0c\u9053\uff1a\u201c\u592a\u597d\u4e86\uff01\u603b\u6355\u5934\u82e5\u77e5\u9053\u4e86\uff0c\u4e00\u5b9a\u4f1a\u975e\u5e38\u9ad8\u5174\u7684\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u4e0d\u7981\u53c8\u56de\u8fc7\u6765\uff0c\u770b\u4e86\u7d27\u95ed\u7684\u623f\u95e8\u4e00\u773c\u3002 \u3000\u3000\u623f\u95f4\u5185\u6084\u7136\u65e0\u58f0\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u6b63\u8272\u9053\uff1a\u201c\u5c0f\u675c\uff0c\u6709\u4e00\u53e5\u8bdd\uff0c\u6211\u4e00\u76f4\u60f3\u627e\u673a\u4f1a\u5bf9\u4f60\u8bf4\uff0c\u8fd9\u8bdd\u53ef\u80fd\u4e0d\u592a\u4e2d\u542c\uff0c\u4f46\u662f \u6211\u7684\u80ba\u8151\u4e4b\u8a00\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5434\u5144\uff0c\u4f60\u6211\u60c5\u540c\u624b\u8db3\uff0c\u6709\u8bdd\u4f60\u5c31\u8bf4\u5427\uff0c\u5fe0\u8a00\u9006\u8033\uff0c\u6211\u77e5\u9053\u5434\u5144\u662f\u4e3a\u6211\u597d\uff0c \u518d\u4e0d\u4e2d\u542c\u7684\u8bdd\uff0c\u6211\u4e5f\u4f1a\u542c\u7684\uff0c\u4e0d\u4ec5\u4f1a\u542c\uff0c\u8fd8\u4f1a\u6309\u5434\u5144\u8bf4\u5f97\u53bb\u505a\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u597d\uff01\u8fd9\u51e0\u5929\u6765\uff0c\u6211\u603b\u89c9\u5f97\u4f60\u5185\u5fc3\u6df1\u5904\u6709\u4e9b\u4e0d\u592a\u5bf9\u52b2\u3002\u5c0f\u675c\uff0c\u73b0\u5728\u5f62\u52bf\u5341\u5206 \u51f6\u9669\uff0c\u6211\u5e0c\u671b\u4f60\u80fd\u6682\u65f6\u4e22\u5f00\u513f\u5973\u79c1\u60c5\uff0c\u96c6\u4e2d\u5168\u90e8\u7cbe\u529b\u4e60\u7ec3\u2018\u5251\u6d77\u6d74\u9b42\u2019\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u4f1a\u7684\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u8bf4\u662f\u5f88\u5bb9\u6613\u7684\uff0c\u505a\u8d77\u6765\u5c31\u96be\u4e86\u3002\u5c0f\u675c\uff0c\u4f60\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u8ba9\u4f60\u5c3d\u5feb\u7ec3\u6210\u5251\u6cd5 \u8682\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5434\u5144\u8bf7\u8bb2\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4ece\u73b0\u5728\u5df2\u638c\u63e1\u7684\u60c5\u51b5\u770b\uff0c\u6740\u624b\u96c6\u56e2\u5171\u6709\u4e09\u5927\u9996\u8111\uff0c\u68a6\u5e7b\u4e4b\u5200\u3001\u8840\u624b\u65b9\u8f6e\u548c \u4e0d\u8001\u4e66\u751f\uff0c\u800c\u5728\u8fd9\u4e09\u4eba\u4e2d\uff0c\u68a6\u5e7b\u4e4b\u5200\u7684\u6b66\u529f\u663e\u7136\u9ad8\u51fa\u603b\u6355\u5934\u4e0d\u6b62\u4e00\u7b79\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u8fd9\u4e0b\uff0c\u5982 \u679c\u4e24\u65b9\u51b3\u6218\uff0c\u6211\u4eec\u83b7\u80dc\u7684\u5e0c\u671b\u7b80\u76f4\u592a\u5c0f\u4e86\uff0c\u8840\u624b\u65b9\u8f6e\u867d\u7136\u4eca\u591c\u53d7\u4e86\u4f24\uff0c\u4f46\u51ed\u4ed6\u7684\u6b66\u529f\uff0c\u4e0d\u65e5 \u5c31\u53ef\u6062\u590d\uff0c\u6211\u60f3\uff0c\u53ea\u6709\u5728\u51b3\u6218\u4e4b\u524d\uff0c\u4f60\u7ec3\u6210\u4e86\u2018\u5251\u6d77\u6d74\u9b42\u2019\u5251\u6cd5\uff0c\u6218\u6597\u4e2d\uff0c\u4fbf\u53ef\u7531\u5c0f\u6587\u3001\u603b \u6355\u5934\u5408\u529b\u7f20\u6597\u68a6\u5e7b\u4e4b\u5200\uff0c\u5c0f\u96c5\u3001\u60dc\u7389\u7f20\u6597\u8840\u624b\u65b9\u8f6e\uff0c\u4e0d\u8001\u4e66\u751f\u660e\u663e\u5bb3\u6015\u2018\u5251\u6d77\u6d74\u9b42\u2019\uff0c\u4f60\u53ef \u4ee5\u4e0a\u524d\u6311\u6218\u4ed6\uff0c\u4f46\u4e0d\u5148\u7528\u2018\u5251\u6d77\u6d74\u9b42\u2019\uff0c\u5f85\u6597\u5230\u5206\u9645\uff0c\u518d\u7a81\u7136\u4f7f\u51fa\uff0c\u5e94\u8be5\u80fd\u5c06\u4ed6\u51fb\u62ff\uff0c\u5728\u8fd9 \u79cd\u60c5\u51b5\u4e0b\uff0c\u4f60\u518d\u4e0e\u6211\u4eec\u4e09\u4eba\u5408\u529b\u6349\u62ff\u6740\u624b\u96c6\u56e2\u7684\u4e8c\u6d41\u4eba\u7269\uff0c\u5c06\u4ed6\u4eec\u6e05\u9664\u540e\uff0c\u518d\u56de\u624b\u53bb\u52a9\u603b\u6355 \u5934\u7b49\u4eba\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u559c\u9053\uff1a\u201c\u5434\u5144\u679c\u7136\u7cbe\u660e\u8fc7\u4eba\uff0c\u4e5f\u53ea\u6709\u8fd9\u6837\uff0c\u6211\u4eec\u624d\u6709\u53ef\u80fd\u83b7\u80dc\u3002\u201d \u3000\u3000\u81ed\u94c1\u5cf0\u9053\uff1a\u201c\u4f46\u5173\u952e\u5728\u4e8e\u4f60\uff0c\u5982\u679c\u4f60\u4e00\u5473\u88ab\u60c5\u4e1d\u7275\u60f9\u2026\u2026\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u4e0e\u60dc\u7389\u5df2\u8ba2\u7ec8\u8eab\u4e4b\u7ea6\uff0c\u4e0d\u4f1a\u518d\u6709\u60c5\u4e1d\u7275\u60f9\u4e86\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5c0f\u675c\uff0c\u6211\u76f8\u4fe1\u4f60\u65e9\u5df2\u770b\u51fa\u6765\uff0c\u6211\u4e5f\u770b\u51fa\u6765\uff0c\u603b\u6355\u5934\u5bf9\u4f60\u2026\u2026\u201d \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u201c\u54ce\u5440\u201d\u4e00\u58f0\uff0c\u623f\u95f4\u6253\u5f00\uff0c\u5c0f\u6587\u8d70\u51fa\u6765\u9053\uff1a\u201c\u5feb\u53bb\u6253\u4e24\u76c6\u70ed\u6c34\u6765\uff0c\u60dc\u7389 \u59d0\u5410\u4e86\u4e24\u5927\u53e3\u9ed1\u8840\uff0c\u4f24\u5df2\u597d\u4e86\u3002\u201d \u3000\u3000\u5979\u770b\u4e86\u675c\u671b\u6708\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5feb\u8fdb\u6765\u770b\u770b\u5427\uff0c\u603b\u6355\u5934\u77e5\u9053\u4f60\u4e00\u5b9a\u7b49\u6025\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u8d70\u8fdb\u623f\u95f4\uff0c\u4f46\u89c1\u60dc\u7389\u6a2a\u8eba\u5728\u5e8a\u4e0a\uff0c\u8138\u8272\u82cd\u767d\uff0c\u5634\u89d2\u6709\u4e00\u4e1d\u8840\u8ff9\uff0c\u4f46\u547c\u5438\u5df1\u5f88\u5e73\u7a33\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5750\u5728\u684c\u8fb9\u5598\u6c14\u9053\uff1a\u201c\u60dc\u7389\u5df2\u7ecf\u6ca1\u4e8b\u4e86\uff0c\u8ba9\u5979\u7761\u4e00\u89c9\uff0c\u660e\u5929\u5c31\u4f1a\u597d\u3002\u201d \u3000\u3000\u5979\u7684\u8138\u8272\u770b\u4e0a\u53bb\u7adf\u6bd4\u60dc\u7389\u7684\u8fd8\u8981\u82cd\u767d\u3002 \u3000\u3000\u5267\u6597\u4e4b\u540e\uff0c\u8fd8\u7528\u5185\u529f\u66ff\u60dc\u7389\u7597\u4f24\uff0c\u5185\u529b\u4f53\u529b\u7684\u635f\u8017\uff0c\u662f\u53ef\u60f3\u800c\u77e5\u7684\u3002 \u3000\u3000\u675c\u671b\u6708\u5634\u5507\u98a4\u6296\u7740\uff0c\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4e00\u65f6\u53c8\u8bf4\u4e0d\u51fa\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u65e0\u529b\u5730\u6325\u4e86\u6325\u624b\uff0c\u4f4e\u58f0\u5730\u9053\uff1a\u201c\u675c\u53f8\u4e3b\uff0c\u4f60\u4ec0\u4e48\u4e5f\u4e0d\u7528\u8bf4\u4e86\uff0c\u4f60\u4f11\u606f\u53bb\u5427\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u6df1\u6df1\u4f5c\u4e86\u4e00\u4e2a\u63d6\uff0c\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1 \u3000\u3000\u7b2c\u4e8c\u5929\u6e05\u6668\uff0c\u675c\u671b\u6708\u6765\u5230\u5927\u5385\uff0c\u53d1\u73b0\u5176\u4ed6\u4eba\u90fd\u5df2\u5728\u5ea7\u4e86\u3002 \u3000\u3000\u60dc\u7389\u5f53\u7136\u4e5f\u5728\uff0c\u5979\u5c31\u5750\u5728\u7a0b\u5c0f\u8776\u8eab\u8fb9\uff0c\u867d\u7136\u8138\u8272\u8fd8\u7565\u663e\u82cd\u767d\uff0c\u4f46\u663e\u7136\u5185\u4f24\u5df2\u75ca\u6108\uff0c\u529f\u529b \u4e5f\u5b8c\u5168\u6062\u590d\u4e86\u3002 \u3000\u3000\u4f17\u4eba\u6b63\u5728\u4e00\u8d77\u7528\u65e9\u70b9\u3002 \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u5c0f\u675c\uff0c\u600e\u4e48\u4eca\u5929\u53c8\u662f\u4f60\u6700\u665a\u5230\uff1f\u4e00\u5b9a\u662f\u62c5\u5fc3\u60dc\u7389\u7684\u4f24\u52bf\uff0c\u4e00\u591c\u6ca1\u6709\u7761\u7740\uff0c \u5929\u4eae\u65f6\u624d\u8fc7\u4e86\u4e00\u89c9\u5427\uff1f\u201d \u3000\u3000\u60dc\u7389\u82cd\u767d\u7684\u8138\u4e0a\u5347\u8d77\u4e24\u6735\u6d45\u6de1\u7684\u7ea2\u6655\u3002 \u3000\u3000\u675c\u671b\u6708\u7b11\u9052\uff1a\u201c\u6211\u771f\u662f\u4e00\u591c\u672a\u7720\u3002\u201d \u3000\u3000\u60dc\u7389\u7684\u8138\u66f4\u7ea2\u4e86\uff0c\u773c\u5708\u4e5f\u7ea2\u4e86\u3002 \u3000\u3000\u90ce\u541b\u5982\u6b64\u4e3a\u5979\u7684\u4f24\u52bf\u62c5\u5fe7\uff0c\u771f\u662f\u8ba9\u5979\u611f\u52a8\u5f97\u6f78\u7136\u6b32\u6cea\u4e86\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u5c0f\u675c\uff0c\u4e0d\u7761\u89c9\u517b\u7cbe\u84c4\u9510\u600e\u4e48\u884c\u5462\uff0c\u4f60\u8fd9\u4e48\u5feb\u96be\u9053\u5c31\u5fd8\u4e86\u6211\u8bf4\u8fc7\u7684\u8bdd\u4e86\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u5750\u4e0b\u6765\uff0c\u631f\u4e86\u4e00\u7b77\u5b50\u83dc\u5341\u5206\u9999\u751c\u5730\u5403\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u7761\u89c9\u53ef\u6ca1\u6709\u80e1\u601d\u4e71\u60f3\u3002\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u54ea\u4f60\u5e72\u4ec0\u4e48\u4e86\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u6b63\u8272\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u7ecf\u8fc7\u6628\u591c\u89c9\u609f\u548c\u7ec3\u4e60\uff0c\u6211\u81ea\u4fe1\u5df2\u8fbe\u5230\u2018\u5251\u6d77\u6b32\u9b42\u2019\u4e94\u6210\u706b\u5019\uff0c \u5982\u679c\u80fd\u5f97\u5230\u5916\u529b\u5e2e\u52a9\uff0c\u4eca\u5929\u5e94\u8be5\u5c31\u80fd\u8fbe\u5230\u4e03\u6210\u706b\u5019\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5f88\u597d\uff0c\u60dc\u7389\u91cd\u4f24\u521a\u597d\uff0c\u4e0d\u80fd\u5984\u52a8\u771f\u529b\u3002\u8fd9\u6837\u5427\uff0c\u4f60\u4e0a\u5348\u5148\u597d\u597d\u4f11\u606f\u4e00\u4e0b\uff0c \u5403\u8fc7\u5348\u996d\uff0c\u6211\u548c\u5c0f\u6587\u3001\u5c0f\u96c5\u4e00\u8d77\u7528\u5185\u529f\u5e2e\u52a9\u4f60\u8fc7\u5173\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u7b11\u9053\uff1a\u201c\u8c22\u603b\u6355\u5934\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6de1\u6de1\u9053\uff1a\u201c\u5148\u5403\u996d\u5427\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u5446\u4e86\u4e00\u5446\uff0c\u4f3c\u4e4e\u5bf9\u7a0b\u5c0f\u8776\u7684\u6001\u5ea6\u9887\u611f\u5947\u602a\u3002 \u3000\u3000\u4ed6\u770b\u4e86\u7a0b\u5c0f\u8776\u4e00\u773c\uff0c\u57cb\u5934\u5403\u8d77\u996d\u6765\u3002 \u3000\u3000\u5403\u5b8c\u65e9\u996d\uff0c\u7a0b\u5c0f\u8776\u4e0b\u4ee4\u8ba9\u6797\u4e0d\u51e1\u7387\u9886\u6c5f\u5357\u5206\u53f8\u7684\u4f17\u597d\u624b\u5728\u540e\u9662\u56db\u5468\u4e25\u5bc6\u5e03\u9632\uff0c\u7136\u540e\u5c06\u4f17 \u4eba\u53ec\u96c6\u5230\u4e86\u5927\u5385\u91cc\u3002 \u3000\u3000\u738b\u5c11\u537f\u9996\u5148\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u4e0b\u5b98\u6709\u4e00\u4e2a\u95ee\u9898\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8bf7\u8bb2\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u6628\u591c\u95f4\uff0c\u542c\u90a3\u8840\u624b\u65b9\u8f6e\u4e4b\u610f\uff0c\u60f3\u6740\u4e0b\u5b98\u7684\uff0c\u4f3c\u4e4e\u662f\u671d\u5ef7\u4e2d\u7684\u5927\u4eba\u7269\uff0c\u4e0d\u77e5 \u8fd9\u4e0e\u65b0\u623f\u8840\u6848\u6709\u65e0\u5173\u6761\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u80af\u5b9a\u9053\uff1a\u201c\u6709\uff0c\u738b\u5927\u4eba\uff0c\u4f60\u4e0d\u8981\u7740\u6025\uff0c\u4eca\u5929\u6211\u8ba9\u5927\u5bb6\u6765\uff0c\u5c31\u662f\u60f3\u8c08\u4e00\u8c08\u8fd9\u4ef6\u4e8b\u60c5\u3002 \u5176\u5b9e\uff0c\u65b0\u623f\u8840\u6848\u53ea\u4e0d\u8fc7\u662f\u4e00\u79cd\u624b\u6bb5\uff0c\u6740\u624b\u5236\u9020\u8fd9\u4ef6\u8840\u6848\u7684\u76ee\u7684\uff0c\u6b63\u662f\u60f3\u5bf9\u4ed8\u738b\u5927\u4eba\u548c\u6c34\u5e08\u63d0 \u7763\u9a6c\u957f\u5c71\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u8fd9\u662f\u4e00\u4e2a\u5927\u9634\u8c0b\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\uff0c\u800c\u4e14\u662f\u4e00\u4e2a\u5929\u5927\u7684\u9634\u8c0b\uff0c\u56e0\u4e3a\u8fd9\u4ef6\u4e8b\u60c5\u5173\u4fdd\u5230\u5927\u660e\u671d\u7684\u6c5f\u5c71\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u56db\u5ea7\u9707\u52a8\uff01 \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u603b\u6355\u5934\u80fd\u8bf4\u7684\u8be6\u7ec6\u4e00\u70b9\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u8be6\u7ec6\u7684\u60c5\u51b5\u6211\u4e5f\u4e0d\u592a\u6e05\u695a\u3002\u6211\u8fd9\u6b21\u4f1a\u540c\u56db\u5927\u6355\u5934\u6765\u626c\u5dde\uff0c\u662f\u56e0\u4e3a \u63a5\u5230\u4e86\u4e00\u4e2a\u547d\u4ee4\uff0c\u53eb\u6211\u6765\u626c\u5dde\u4fdd\u62a4\u738b\u5927\u4eba\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\u7ed9\u603b\u6355\u5934\u4e0b\u7684\u547d\u4ee4\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u8fd9\u8bdd\u95ee\u7684\uff01\u80fd\u7ed9\u603b\u6355\u5934\u4e0b\u547d\u4ee4\u7684\u4eba\u80fd\u6709\u51e0\u4e2a\uff1f\u4e0d\u662f\u5211\u90e8\u5c1a\u4e66\uff0c\u5c31\u662f\u7687\u4e0a \u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5c91\u53f8\u4e3b\u9519\u4e86\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u90a3\u8fd8\u4f1a\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u4e0b\u547d\u4ee4\u7684\u4eba\u662f\u8c01\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u6211\u53ea\u77e5\u9053\u4ed6\u73b0\u5728\u4e5f\u5728\u626c\u5dde\u9644\u8fd1\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u603b\u6355\u5934\u4e5f\u4e0d\u77e5\u9053\u8fd9\u4e2a\u9634\u8c0b\u7684\u4e3b\u8c0b\u662f\u8c01\uff0c\u5230\u5e95\u53c8\u6709\u4f55\u76ee\u7684\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u7684\u786e\u4e0d\u662f\u5f88\u6e05\u695a\uff0c\u4f46\u5728\u626c\u5dde\u7ecf\u8fc7\u4e86\u8fd9\u4e00\u7cfb\u5217\u4e8b\u4ef6\uff0c\u6211\u4e5f\u731c\u5230\u4e86\u4e03\u516b\u5206\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\u80fd\u8bf4\u4e00\u8bf4\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u53ef\u4ee5\u3002\u8bf8\u4f4d\u8bf7\u60f3\u4e00\u60f3\uff0c\u738b\u5927\u4eba\u4e3a\u671d\u5ef7\u547d\u5bab\uff0c\u6740\u5bb3\u671d\u5ef7\u547d\u5bab\uff0c\u662f\u4ec0\u4e48\u7f6a\u540d\uff1f\u201d \u3000\u3000\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u5f53\u7136\u662f\u8c0b\u53cd\u7684\u7f6a\u540d\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5bf9\uff01\u5982\u679c\u4e00\u4e2a\u4eba\u60f3\u8c0b\u53cd\uff0c\u90a3\u4e48\u4ed6\u9700\u8981\u7684\uff0c\u662f\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u4e00\u662f\u94b1\uff0c\u56e0\u4e3a\u8c0b\u53cd\u8fd9\u6837\u5927\u7684\u884c\u52a8\u9700\u8981\u5927\u91cf\u7684\u8d44\u91d1\u3002\u4e8c\u662f\u519b\u961f\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5f88\u5bf9\uff0c\u8bf7\u95ee\u8bf8\u4f4d\uff0c\u5929\u4e0b\u8d22\u5bcc\u6700\u591a\u7684\u5730\u65b9\uff0c\u662f\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u5f53\u7136\u662f\u626c\u5dde\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u63a5\u7740\u9053\uff1a\u201c\u6b63\u662f\u626c\u5dde\uff0c\u800c\u4e14\u6c34\u5e08\u63d0\u7763\u9a6c\u957f\u5c71\u7684\u6570\u4e07\u6c34\u5e08\u7cbe\u9510\uff0c\u6b63\u9a7b\u624e\u626c\u5dde\u9644\u8fd1\uff0c \u4e5f\u5c31\u662f\u8bf4\uff0c\u53ea\u8981\u4ed6\u4eec\u5728\u626c\u5dde\u7684\u9634\u8c0b\u5f97\u624b\uff0c\u5219\u8d44\u91d1\u4e0e\u519b\u961f\uff0c\u4e8c\u8005\u4fbf\u90fd\u53ef\u6293\u5728\u624b\u4e2d\u4e86\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u604d\u7136\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u4eec\u5236\u9020\u65b0\u623f\u8840\u6848\uff0c\u8bf1\u4f7f\u9a6c\u957f\u5c71\u91d1\u767e\u5e74\u706b\u62fc\uff0c\u518d\u501f\u6b64\u6765\u8feb\u9a6c\u957f\u5c71 \u4e0e\u4ed6\u4eec\u5408\u4f5c\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u738b\u5927\u4eba\u8bf4\u7684\u5f88\u5bf9\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u53ef\u8fd9\u4ef6\u4e8b\u5e94\u8be5\u4e0e\u738b\u67d0\u6ca1\u6709\u4ec0\u4e48\u5173\u8054\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u5b9a\u8981\u5c06\u738b\u67d0\u7f6e\u4e8e\u6b7b\u5730\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u53ea\u56e0\u4e3a\u738b\u5927\u4eba\u592a\u80fd\u5e72\u4e86\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u603b\u6355\u5934\u8fd9\u8bdd\u738b\u67d0\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u738b\u5927\u4eba\u5728\u626c\u5dde\u4e3a\u5b98\u4e09\u5e74\uff0c\u67e5\u529e\u4e86\u4e00\u5927\u6279\u4f5c\u5978\u72af\u79d1\u8005\uff0c\u8fd9\u4e9b\u4eba\u5f53\u4e2d\uff0c\u5c31\u6709\u4ed6 \u4eec\u6d3e\u6765\u626c\u5dde\u655b\u8d22\u7684\u722a\u7259\uff0c\u6240\u4ee5\uff0c\u4ed6\u4eec\u4e0d\u9664\u6389\u738b\u5927\u4eba\uff0c\u5c31\u5f88\u96be\u653e\u624b\u5728\u626c\u5dde\u655b\u8d22\u5440\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u53ef\u662f\u738b\u67d0\u4efb\u671f\u5df2\u6ee1\uff0c\u5373\u5c06\u8c03\u79bb\uff0c\u4ed6\u4eec\u5b8c\u5168\u6ca1\u6709\u5fc5\u8981\u6025\u7740\u6740\u6211\u561b\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u738b\u5927\u4eba\u4efb\u671f\u5df2\u8fc7\u4e86\u591a\u5c11\u5929\u4e86\uff1f\u6309\u6b63\u5e38\u60c5\u51b5\uff0c\u671d\u5ef7\u65e9\u8be5\u4e0b\u547d\u8c03\u738b\u5927 \u4eba\u8fdb\u4eac\u4e86\u5427\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u7684\u786e\uff0c\u8fd9\u4e8b\u5f88\u5947\u602a\u4e86\uff0c\u540f\u90e8\u600e\u4e48\u8fd8\u6ca1\u6709\u6d3e\u4eba\u6765\u63a5\u66ff\u738b\u67d0\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6b63\u8272\u9053\uff1a\u201c\u6709\u4e00\u6761\u6d88\u606f\uff0c\u786e\u5207\u4e0e\u5426\u6211\u4e0d\u6562\u80af\u5b9a\uff0c\u8bf4\u51fa\u6765\uff0c\u5927\u5bb6\u59d1\u4e14\u542c\u4e4b\uff0c\u4ee5\u4f9b\u53c2 \u8003\u3002\u5c31\u5728\u65b0\u623f\u8840\u6848\u524d\u7ea6\u4e00\u4e2a\u6708\uff0c\u4eac\u91cc\u98ce\u4f20\u56e0\u4e3a\u738b\u5927\u4eba\u5728\u626c\u5dde\u653f\u7ee9\u751a\u4f73\uff0c\u6240\u4ee5\u7687\u4e0a\u6709\u610f\u8ba9\u738b\u5927 \u4eba\u7559\u4efb\u626c\u5dde\uff0c\u518d\u5e72\u4e09\u5e74\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u60ca\u671b\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u9634\u8c0b\u7684\u4e3b\u8c0b\u4eba\u5df2\u7b49\u4e0d\u53ca\u4e09\u5e74\u4e86\uff0c\u6240\u4ee5\u4e0b\u51b3\u5fc3\u6740\u6389\u738b\u5927\u4eba\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6de1\u6de1\u9053\uff1a\u201c\u4e0d\u4ec5\u9634\u8c0b\u4e4b\u4e3b\u8c0b\u4eba\u7b49\u4e0d\u53ca\u4e86\uff0c\u5b9e\u9645\u60c5\u51b5\u4e5f\u4e0d\u5141\u8bb8\u4ed6\u518d\u7b49\u4e0b\u53bb\u2026\u2026\u201d \u3000\u3000\u8bed\u53ea\u8bf4\u4e86\u4e00\u534a\uff0c\u5979\u5374\u54bd\u4f4f\u4e86\u8bdd\u5934\u3002 \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u542c\u603b\u6355\u5934\u7684\u8bdd\u4e2d\u4e4b\u610f\uff0c\u5e94\u8be5\u662f\u77e5\u9053\u4e3b\u8c0b\u4e4b\u4eba\u662f\u8c01\u7684\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6b64\u4e8b\u7275\u626f\u592a\u592a\uff0c\u7275\u8fde\u592a\u5e7f\uff0c\u5728\u6ca1\u6709\u786e\u51ff\u7684\u8bc1\u636e\uff0c\u6211\u4e5f\u4e0d\u6562\u80e1\u731c\u4e71\u7591\u3002\u738b\u5927 \u4eba\uff0c\u6211\u53ea\u5e0c\u671b\u4f60\u4ee5\u540e\u80fd\u4e0d\u5fd8\u5728\u626c\u5dde\u8fd9\u4e00\u6bb5\u7ecf\u5386\uff0c\u5c3d\u5fc3\u5c3d\u529b\u591a\u4e3a\u56fd\u5bb6\u505a\u597d\u4e8b\uff0c\u4e5f\u5c31\u7b97\u662f\u6211\u7b49\u4e0d \u6789\u8d39\u8fd9\u756a\u5fc3\u8840\uff0c\u6b7b\u4e8e\u6740\u624b\u96c6\u56e2\u4e4b\u624b\u7684\u90a3\u4e9b\u6c5f\u5357\u5206\u53f8\u7684\u5144\u5f1f\u4eec\u6ca1\u6709\u767d\u6b7b\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u7ad9\u8d77\u8eab\uff0c\u957f\u63d6\u9053\uff1a\u201c\u603b\u6355\u5934\u6b64\u8a00\uff0c\u738b\u67d0\u7ec8\u6b64\u4e00\u751f\uff0c\u7edd\u4e0d\u4f1a\u5fd8\u8bb0\uff01\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u6797\u4e0d\u51e1\u5306\u5306\u8d70\u8fdb\u5927\u5385\uff0c\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\u3001\u738b\u5927\u4eba\uff0c\u94c1\u6355\u5934\u4e0e\u91d1\u5e9c\u5929\u67ab\u9053\u957f\u6c42 \u89c1\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u76b1\u4e00\u76b1\u7709\uff0c\u9053\uff1a\u201c\u5feb\u8bf7\u4ed6\u4eec\u8fdb\u6765\u3002\u201d \u3000\u3000\u94c1\u7fce\u548c\u5929\u67ab\u9053\u957f\u5f88\u5feb\u5c31\u51fa\u73b0\u5728\u4f17\u4eba\u9762\u524d\u3002 \u3000\u3000\u7a0b\u5c0f\u789f\u9053\uff1a\u201c\u94c1\u6355\u5934\u91d1\u5e9c\u8fd9\u51e0\u5929\u60c5\u51b5\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u5341\u5206\u5e73\u9759\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6ca1\u6709\u6740\u624b\u96c6\u56e2\u7684\u4eba\u53bb\u5077\u88ad\u91d1\u5e9c\u5417\uff1f\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u68a6\u5e7b\u4e4b\u5200\u5462\uff1f\u4ed6\u4e5f\u6ca1\u6709\u518d\u9732\u9762\uff1f\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u5c5e\u4e0b\u4eca\u65e5\u56de\u5e9c\uff0c\u5c31\u662f\u6709\u6709\u5173\u68a6\u5e7b\u4e4b\u5200\u7684\u60c5\u51b5\u8981\u7980\u544a\u603b\u6355\u5934\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9762\u8272\u53d8\u4e86\u53d8\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u6d88\u606f\uff1f\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u5929\u67ab\u9053\u957f\u81ea\u68a6\u5e7b\u4e4b\u5200\u7a83\u8d70\u4e09\u5b9d\u540e\uff0c\u4fbf\u7387\u9886\u91d1\u5e9c\u6570\u540d\u9ad8\u624b\u5728\u626c\u5dde\u9644\u8fd1\u6697\u4e2d\u67e5\u8bbf\uff0c \u679c\u7136\u8ba9\u4ed6\u67e5\u51fa\u4e86\u68a6\u5e7b\u4e4b\u5200\u7684\u884c\u8e2a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u4e0b\u7ad9\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u68a6\u5e7b\u4e4b\u5200\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u6211\u662f\u6628\u5929\u591c\u95f4\u53d1\u73b0\u4ed6\u7684\uff0c\u5f53\u65f6\uff0c\u4ed6\u4e00\u8eab\u9ed1\u8863\uff0c\u9ed1\u5dfe\u8499\u9762\uff0c\u6025\u5306\u5306\u5730\u5411\u7626 \u897f\u6e56\u65b9\u5411\u8d70\u53bb\u3002\u8001\u9053\u89c9\u5f97\u6b64\u4eba\u53ef\u7591\uff0c\u4fbf\u8ddf\u4e86\u4e0b\u53bb\u3002\u8ddf\u5230\u534a\u9014\uff0c\u5374\u5c06\u4ed6\u8ddf\u4e22\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u76ef\u7740\u4ed6\uff0c\u6162\u6162\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u9053\u957f\u53ea\u662f\u53d1\u73b0\u4e86\u4e00\u4e2a\u53ef\u7591\u4e4b\u4eba\uff0c\u4e0d\u80fd\u786e\u5b9a\u4ed6\u662f\u5426\u662f \u68a6\u5e7b\u4e4b\u5200\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4e0d\u3002\u540e\u6765\uff0c\u6211\u5c31\u8ba9\u91d1\u5e9c\u4e2d\u7684\u516d\u540d\u9ad8\u624b\u6563\u5f00\uff0c\u5728\u7626\u897f\u6e56\u9644\u8fd1\u53ef\u7591\u4e4b\u5904\u4ed4\u7ec6 \u641c\u5bfb\uff0c\u7ed3\u679c\u5728\u4e00\u6e56\u7554\u9690\u853d\u4e4b\u5904\uff0c\u53d1\u73b0\u4e86\u4e00\u5ea7\u7cbe\u542b\uff0c\u8001\u9053\u4e0d\u6562\u8fc7\u4e8e\u903c\u8fd1\uff0c\u53ea\u80fd\u8fdc\u8fdc\u89c2\u770b\uff0c\u7ed3\u679c\uff0c \u4ece\u4e00\u6247\u5f00\u7740\u7684\u7a97\u6237\u770b\u89c1\u4e86\u91d1\u5e9c\u7a83\u8d70\u4e09\u5b9d\u7684\u90a3\u4e2a\u94f6\u8863\u4eba\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u795e\u60c5\u5fae\u5fae\u4e00\u9707\uff0c\u9053\uff1a\u201c\u9053\u957f\u5f53\u65f6\u600e\u4e48\u505a\u7684\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u6211\u77e5\u9053\u7edd\u975e\u4ed6\u7684\u654c\u624b\uff0c\u4fbf\u7559\u4e0b\u4e86\u4e09\u540d\u9ad8\u624b\u5728\u9644\u8fd1\u76d1\u89c6\uff0c\u5306\u5306\u8d76\u56de\uff0c\u5411\u91d1 \u5e9c\u548c\u94c1\u6355\u5934\u62a5\u544a\u6b64\u4e8b\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4f3c\u4e4e\u5598\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u6837\u5c31\u597d\uff0c\u5982\u679c\u9053\u957f\u5f53\u65f6\u6709\u6240\u884c\u52a8\uff0c\u5f15\u8d77\u4ed6\u7684\u60ca\u89c9\uff0c\u5c31\u4e0d \u597d\u529e\u4e86\u3002\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u91d1\u767e\u5e74\u4e0b\u4e86\u51b3\u5fc3\u8981\u6355\u6740\u6b64\u4eba\uff0c\u4f46\u662f\u4ed6\u60f3\u8bf7\u603b\u6355\u5934\u53bb\u4e3b\u6301\u6b64\u4e8b\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u4eec\u51c6\u5907\u4ec0\u4e48\u65f6\u5019\u884c\u52a8\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u91d1\u5e9c\u4eba\u624b\u5df2\u7ecf\u505a\u597d\u4e86\u51c6\u5907\uff0c\u53ea\u5f85\u603b\u6355\u5934\u4e00\u58f0\u4ee4\u4e0b\uff0c\u968f\u65f6\u7686\u53ef\u884c\u52a9\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u8f6c\u5934\u5411\u56db\u5927\u6355\u5934\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u52a8\u624b\u65f6\u673a\u4e3a\u6700\u4f73\uff0c\u4f60\u4eec\u8bf4\u8bf4\u770b\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u6700\u597d\u968f\u540e\u5c31\u70b9\u9f50\u4eba\u9a6c\uff0c\u6740\u5c06\u8fc7\u53bb\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u6628\u591c\u7ecf\u8fc7\u4e00\u756a\u6fc0\u6597\uff0c\u4f53\u529b\u80af\u5b9a\u4e5f\u6709\u635f\u8017\uff0c\u5982\u679c\u53bb\u665a\u4e86\uff0c\u8ba9\u4ed6\u6709\u65f6\u95f4 \u6062\u590d\u4f53\u529b\uff0c\u5236\u670d\u4ed6\u5c31\u4e0d\u5bb9\u6613\u4e86\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4f60\u53ea\u77e5\u9053\u4ed6\u7684\u4f53\u529b\u6709\u635f\u8017\uff0c\u600e\u4e48\u4e0d\u60f3\u60f3\uff0c\u6211\u4eec\u81ea\u5df1\u5462\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u53ef\u662f\u6709\u91d1\u5e9c\u4e2d\u7684\u9ad8\u624b\u53c2\u4e0e\u6b64\u4e8b\uff0c\u4ed6\u4eec\u8fd9\u4e9b\u5929\u6765\u53ef\u662f\u4e00\u76f4\u5728\u517b\u7cbe\u84c4\u9510\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u5ffd\u7136\u9053\uff1a\u201c\u6211\u8ba4\u4e3a\u5e94\u8be5\u591c\u95f4\u518d\u53bb\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u51e0\u6b21\u6740\u624b\u7a81\u88ad\u5e9c\u8859\uff0c\u90fd\u662f\u5728\u591c\u95f4\uff0c\u6240\u4ee5\u68a6\u5e7b\u4e4b\u5200\u7edd\u5bf9\u60f3\u4e0d\u5230\u6211\u4eec\u4f1a \u5728\u591c\u95f4\u4e3b\u52a8\u51fa\u51fb\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u55ef\uff0c\u6709\u9053\u7406\uff0c\u5929\u67ab\u9053\u957f\uff0c\u8bf7\u4f60\u56de\u53bb\u544a\u8bc9\u91d1\u767e\u5e74\uff0c\u6211\u51b3\u5b9a\u4eca\u591c\u884c\u52a8\u3002\u4eca\u591c\u5b50 \u65f6\u4e4b\u524d\uff0c\u6211\u4f1a\u8d76\u5230\u91d1\u5e9c\u524d\u4e0e\u8bf8\u4f4d\u4f1a\u5408\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u591a\u8c22\u7a0b\u603b\u6355\u5934\u63f4\u624b\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u9053\u957f\u8bf4\u54ea\u91cc\u8bdd\uff0c\u627e\u51fa\u68a6\u5e7b\u4e4b\u5200\uff0c\u5c31\u662f\u627e\u5230\u4e86\u7834\u6848\u7684\u5173\u952e\uff0c\u6211\u8fd8\u8981\u8c22\u8c22\u9053 \u957f\u624d\u5bf9\u5440\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u8d2b\u9053\u544a\u8f9e\u4e86\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9001\u3002\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\uff0c\u7a0b\u5c0f\u8776\u53c8\u9053\uff1a\u201c\u94c1\u6355\u5934\uff0c\u4f60\u5148\u7559\u5728\u5e9c\u8859\u91cc\uff0c\u591c\u95f4\u518d\u4e0e\u6211\u4e00\u8d77\u53bb\u91d1\u5e9c\u5427\u3002\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u662f\u3002\u201d\u505c\u4f4f\u4e86\u811a\u6b65\uff0c\u62f1\u624b\u5bf9\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u8fd8\u8981\u4e00\u8def\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u8fd8\u4e86\u4e00\u793c\uff0c\u51fa\u95e8\u800c\u53bb\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u94c1\u6355\u5934\uff0c\u6628\u591c\u53c8\u6709\u6740\u624b\u591c\u88ad\u5e9c\u8859\uff0c\u800c\u4e14\u9886\u5934\u7684\u81ea\u79f0\u68a6\u5e7b\u4e4b\u5200\uff0c\u8fd9\u4ef6\u4e8b\u91d1\u5e9c \u91cc\u6709\u4eba\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u94c1\u6355\u5934\u4e5f\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f46\u4f60\u521a\u624d\u542c\u5c91\u53f8\u4e3b\u8bf4\u53ca\u6b64\u4e13\u65f6\uff0c\u5e76\u6ca1\u6709\u5403\u60ca\u7684\u8868\u60c5\uff0c\u4e5f\u6ca1\u6709\u95ee\u662f\u600e\u4e48\u56de\u4e8b \u5440\u3002\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u56e0\u4e3a\u521a\u624d\u5929\u67ab\u9053\u957f\u5728\u6b64\uff0c\u94c1\u67d0\u89c9\u5f97\u4e0d\u95ee\u4e3a\u597d\u3002\u675c\u53f8\u4e3b\u4e00\u76f4\u6000\u7591\u91d1\u5e9c\u5185\u6f5c\u6709\u6740 \u624b\u7684\u5367\u5e95\uff0c\u6240\u4ee5\uff0c\u5e9c\u8859\u5185\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u94c1\u67d0\u4e5f\u8ba4\u4e3a\u4e0d\u8ba9\u91d1\u5e9c\u7684\u4efb\u4f55\u4eba\u77e5\u9053\u4e3a\u597d\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6ee1\u610f\u5730\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u94c1\u6355\u5934\u679c\u7136\u673a\u8b66\u8fc7\u4eba\u3002\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u4e0d\u6562\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u4e0e\u5929\u67ab\u9053\u957f\u662f\u8001\u670b\u53cb\u4e86\uff1f\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u53ef\u4ee5\u8fd9\u6837\u8bf4\uff0c\u4f46\u8fd1\u5341\u5e74\u6765\uff0c\u51e0\u4e4e\u6ca1\u6709\u542c\u5230\u4ed6\u7684\u4efb\u4f55\u6d88\u606f\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4ed6\u662f\u5728\u91d1\u5e9c\u91cc\u7a81\u7136\u51fa\u73b0\u7684\u55bd\uff1f\u201d \u3000\u3000\u94c1\u7fce\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u5e94\u8be5\u662f\u8fd9\u6837\u3002\u56e0\u4e3a\u738b\u5927\u4eba\u5728\u626c\u5dde\u4e09\u5e74\uff0c\u6211\u4e5f\u6ca1\u5c11\u4e0e\u91d1\u5e9c\u6253\u4ea4\u9053\uff0c\u53ef \u76f4\u5230\u8840\u6848\u53d1\u751f\u540e\uff0c\u624d\u89c1\u5230\u5929\u67ab\u9053\u957f\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u94c1\u6355\u5934\uff0c\u4f60\u60f3\u8d77\u6b66\u9662\u9662\u4e3b\u7684\u58f0\u97f3\u662f\u8c01\u7684\u4e86\u5417\uff1f\u201d \u3000\u3000\u94c1\u7fce\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u7684\u786e\u6709\u4e9b\u719f\uff0c\u4f46\u518d\u600e\u6837\u60f3\uff0c\u5374\u4e5f\u60f3\u4e0d\u51fa\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6709\u5f88\u591a\u4e8b\uff0c\u4e00\u5fc3\u53bb\u627e\uff0c\u5374\u603b\u662f\u53d1\u73b0\u4e0d\u4e86\uff0c\u53ef\u65e0\u610f\u95ee\uff0c\u7a81\u7136\u5c31\u4f1a\u5728\u773c\u524d\u53d1\u73b0\uff0c \u6b63\u6240\u8c13\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u529f\u592b\u3002\u94c1\u6355\u5934\uff0c\u6211\u770b\u4f60\u8fd1\u6765\u795e\u7ecf\u592a\u7d27\u5f20\u4e86\uff0c\u4eca\u5929\u5c31\u5728\u5e9c \u8859\u597d\u597d\u4f11\u606f\u4e00\u5929\u5427\uff0c\u6b63\u597d\u4e5f\u53bb\u770b\u4e00\u770b\u738b\u575a\u548c\u4f55\u5927\u5149\uff0c\u4ed6\u4eec\u7684\u4f24\u52bf\u5df2\u7ecf\u597d\u591a\u4e86\u3002\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u662f\uff0c\u8c22\u603b\u6355\u5934\u5173\u5fc3\u3002\u201d \u3000\u3000------------- \u3000\u3000\u5b64\u5251\u751f  \u626b\u63cf\u3000  cndy001 zhuyj(\u6731\u836b\u9636)  OCR  \uff0c\u65e7\u96e8\u697c\u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u4e8c\u5341\u56db\u7ae0 \u3000\u3000\u8bf4\u5b8c\uff0c\u8f6c\u8eab\u8d70\u51fa\u4e86\u5927\u5385\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u8f6c\u800c\u5bf9\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u5434\u53f8\u4e3b\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e3b\u5f20\u665a\u95f4\u884c\u52a8\uff1f\u201d \u3000\u3000\u5434\u94c1\u5cf0\u7b11\u9053\uff1a\u201c\u6211\u662f\u4e3a\u5e9c\u8859\u7684\u5b89\u5168\u8003\u8651\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5947\u602a\u5730\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u603b\u6355\u5934\u4e0d\u5728\u5e9c\u8859\u671f\u95f4\uff0c\u5982\u679c\u68a6\u5e7b\u4e4b\u5200\u7a81\u7136\u6765\u88ad\uff0c\u6211\u4eec\u5982\u4f55\u8ddf\u5176\u5bf9\u6297\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u90a3\u591c\u95f4\u5462\uff0c\u5434\u53f8\u4e3b\u5c31\u4e0d\u62c5\u5fc3\u4e86\uff1f\u201d \u3000\u3000\u5434\u94c1\u5cf0\u7b11\u9053\uff1a\u201c\u56e0\u4e3a\u9ec4\u660f\u65f6\u5206\uff0c\u5c06\u4f1a\u6709\u4e00\u652f\u751f\u529b\u519b\u8d76\u6765\u63f4\u52a9\u6211\u4eec\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7f8e\u4e3d\u7684\u5927\u773c\u775b\u5ffd\u95ea\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4e00\u5b9a\u662f\u4e2d\u5dde\u5206\u53f8\u7684\u7cbe\u9510\u4eba\u9a6c\uff01\u5434\u53f8\u4e3b\uff0c \u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u5434\u94c1\u5cf0\u4e00\u7fd8\u5927\u62c7\u6307\uff0c\u5927\u7b11\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u603b\u662f\u4ec0\u4e48\u4e5f\u7792\u4e0d\u4f4f\u4f60\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e5f\u7b11\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u6211\u81ea\u5df1\u4e5f\u65e9\u51b3\u5b9a\u4e00\u5b9a\u8981\u5230\u665a\u95f4\u624d\u884c\u52a8\uff0c\u4e0d\u8fc7\u5e9c\u8859\u7684\u9632\u5907\u6211\u5e76\u4e0d \u62c5\u5fc3\uff0c\u800c\u662f\u56e0\u4e3a\u53e6\u4e00\u4e2a\u539f\u56e0\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6709\u4e9b\u8c03\u76ae\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u73b0\u5728\u662f\u4e0d\u80fd\u8bf4\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u603b\u6355\u5934\u4e3a\u4ec0\u4e48\u4e0d\u4e3a\u5e9c\u8859\u7684\u9632\u5907\u62c5\u5fc3\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6709\u4e24\u4e2a\u4eba\u4e00\u5b9a\u4f1a\u5f88\u4f24\u5fc3\uff0c\u5982\u679c\u4ed6\u4eec\u77e5\u9053\u4e86\u8fd9\u4e9b\u5929\u6765\u4f60\u4eec\u7adf\u7136\u4e00 \u76f4\u90fd\u6ca1\u6709\u60f3\u8d77\u8fc7\u4ed6\u4eec\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u5446\u4e86\u5446\uff0c\u5927\u53eb\u9053\uff1a\u201c\u54c8\uff01\u6211\u771f\u7b28\uff0c\u539f\u6765\u662f\u963f\u6a2a\u548c\u963f\u4fdd\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u4ed6\u4eec\u73b0\u5728\u5728\u54ea\u91cc\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5f88\u5feb\u4f60\u4eec\u5c31\u4f1a\u89c1\u5230\u4ed6\u4eec\u7684\u3002\u201d \u3000\u3000\u5979\u8f6c\u9762\u5bf9\u675c\u671b\u6708\u9053\uff1a\u201c\u675c\u53f8\u4e3b\uff0c\u4f60\u8be5\u7ec3\u4e60\u5251\u6cd5\u4e86\uff0c\u8ba9\u5c0f\u6587\u5148\u5e2e\u52a9\u4f60\uff0c\u6211\u548c\u5c0f\u96c5\u4e00\u4f1a\u5c31\u4f1a \u6765\u7528\u5185\u529b\u52a9\u4f60\u8fc7\u5173\u3002\u201d\u8bf4\u5b8c\uff0c\u5979\u53eb\u4e0a\u5c0f\u96c5\uff0c\u56de\u5230\u4e86\u623f\u95f4\u91cc\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\u513f\uff0c\u4e00\u53ea\u7070\u8272\u7684\u9e3d\u5b50\u81ea\u5c4b\u9876\u4e0a\u98de\u4e86\u8d77\u6765\uff0c\u5728\u5e9c\u8859\u4e0a\u7a7a\u6253\u4e86\u65cb\uff0c\u6251\u6247\u7740\u7fc5\u8180\uff0c\u98de\u8fdc \u4e86\u3002 \u3000\u3000\u9ec4\u660f\u65f6\u5206\uff0c\u4e2d\u5dde\u5206\u53f8\u56db\u5341\u540d\u597d\u624b\u98ce\u5c18\u4ec6\u4ec6\u6765\u5230\u4e86\u5e9c\u8859\u3002 \u3000\u3000\u675c\u671b\u6708\u4e5f\u5df2\u5b8c\u5168\u9886\u609f\u4e86\u201c\u5251\u6d77\u6d74\u9b42\u201d\u7684\u7cbe\u4e49\u6240\u5728\uff0c\u5728\u7a0b\u5c0f\u8776\u3001\u5c0f\u96c5\u3001\u5c0f\u6587\u7528\u5185\u529f\u5e2e\u52a9\u4e4b \u4e0b\uff0c\u5df2\u7ecf\u8fbe\u5230\u4e86\u516b\u6210\u706b\u5019\uff0c\u6210\u7ee9\u5c24\u5728\u4f17\u4eba\u671f\u671b\u4e4b\u4e0a\uff01 \u3000\u3000\u53ea\u662f\uff0c\u5403\u8fc7\u665a\u996d\uff0c\u8fd8\u662f\u4e0d\u89c1\u963f\u6a2a\u963f\u4fdd\u7684\u8e2a\u5f71\u3002 \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u963f\u6a2a\u3001\u963f\u4fdd\u4e8c\u4eba\u5462\uff1f\u603b\u6355\u5934\uff0c\u4f60\u4e0d\u662f\u8bf4\u4ed6\u4eec\u4e5f\u4f1a\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u4f1a\u51fa\u4ec0\u4e48\u610f\u5916\u5427\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u591a\u5e74\u6765\uff0c\u4f60\u4eec\u89c1\u8fc7\u4ed6\u4eec\u66fe\u51fa\u8fc7\u610f\u5916\u5417\uff1f\u201d \u3000\u3000\u5979\u63a5\u7740\u9053\uff1a\u201c\u8ba9\u4f60\u4eec\u4e0d\u7528\u6025\uff0c\u5c31\u4e0d\u8981\u7740\u6025\u561b\uff0c\u5f88\u5feb\u4f60\u4eec\u5c31\u4f1a\u770b\u89c1\u4ed6\u4eec\u4e86\u3002\u201d \u3000\u3000\u65e2\u7136\u603b\u6355\u5934\u8fd9\u6837\u8bf4\u4e86\uff0c\u56db\u5927\u6355\u5934\u4e5f\u5c31\u4e0d\u518d\u95ee\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4eca\u591c\u4e4b\u884c\uff0c\u53ef\u80fd\u5341\u5206\u5371\u9669\uff0c\u6211\u8ddf\u4f60\u4e00\u8d77\u53bb\u5427\uff0c\u4e5f\u597d\u8ba9\u6211\u8bd5\u4e00\u8bd5\u65b0 \u5b66\u7684\u5251\u6cd5\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u591a\u8c22\u675c\u53f8\u4e3b\u7684\u5173\u5fc3\uff0c\u6709\u963f\u6a2a\u3001\u963f\u4fdd\u5728\u6697\u4e2d\u76f8\u968f\uff0c\u4e0d\u4f1a\u6709\u4efb\u4f55\u5371\u9669\u7684\u3002\u201d \u3000\u3000\u5b89\u6392\u597d\u5e9c\u8859\u7684\u9632\u5907\uff0c\u591c\u5df1\u6e10\u6df1\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7ad9\u8d77\u8eab\uff0c\u9053\uff1a\u201c\u94c1\u6355\u5934\uff0c\u54b1\u4eec\u8be5\u53bb\u91d1\u5e9c\u4e86\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u6025\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u963f\u6a2a\u963f\u4fdd\u8fd8\u6ca1\u6765\u5462\uff0c\u4f60\u4e0d\u7b49\u4ed6\u4eec\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u56db\u5927\u6355\u5934\u771f\u7684\u8fd9\u6837\u6025\u5207\u5730\u60f3\u89c1\u4ed6\u4eec\u5417\uff1f\u201d \u3000\u3000\u56db\u4eba\u4e00\u9f50\u9053\uff1a\u201c\u5f53\u7136\u662f\u771f\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u4f60\u4eec\u5c31\u9001\u9001\u6211\u5427\u3002\u201d \u3000\u3000\u56db\u4eba\u9001\u7a0b\u5c0f\u8776\u548c\u94c1\u7fce\u51fa\u5e9c\u8859\u4fa7\u95e8\uff0c\u5374\u770b\u89c1\u4e24\u4e2a\u94c1\u5854\u822c\u7684\u5927\u6c49\u6b63\u7ad9\u5728\u95e8\u5916\uff0c\u4e0d\u662f\u963f\u6a2a\u3001\u963f \u4fdd\uff0c\u53c8\u662f\u4f55\u4eba\uff1f\u56db\u4eba\u4e00\u9f50\u51b2\u4e0a\u524d\uff0c\u56f4\u7740\u963f\u6a2a\u3001\u963f\u4fdd\u597d\u4e00\u756a\u4eb2\u70ed\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u597d\u5566\uff0c\u54b1\u4eec\u8be5\u8d70\u5566\uff01\u201d \u3000\u3000\u963f\u6a2a\u3001\u963f\u4fdd\u4e5f\u9053\uff1a\u201c\u8fd9\u6b21\u4efb\u52a1\u5b8c\u6210\u4e4b\u540e\uff0c\u518d\u597d\u597d\u805a\u4e00\u805a\u5427\u3002\u201d \u3000\u3000\u4e8c\u4eba\u5411\u56db\u5927\u6355\u5934\u62f1\u624b\u60dc\u522b\uff0c\u8ddf\u5728\u7a0b\u5c0f\u8776\u548c\u94c1\u7fce\u8eab\u540e\uff0c\u5f88\u5feb\u5730\u8d70\u8fdb\u4e86\u832b\u832b\u591c\u8272\u4e2d\uff0c\u6d88\u5931\u4e0d \u89c1\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u5446\u5446\u770b\u7740\u4ed6\u4eec\u6d88\u5931\u7684\u65b9\u5411\uff0c\u9053\uff1a\u201c\u5e0c\u671b\u603b\u6355\u5934\u6b64\u884c\u987a\u5229\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\uff0c\u6211\u5f88\u662f\u5947\u602a\uff0c\u4e3a\u4ec0\u4e48\u4e09\u5b9d\u6ca1\u6709\u4e22\u5931\u4e4b\u524d\uff0c\u5929\u67ab\u9053\u957f\u6839\u672c\u627e\u4e0d\u5230 \u68a6\u5e7b\u4e4b\u5200\u7684\u884c\u8e2a\uff0c\u800c\u4e09\u5b9d\u4e22\u5931\u6ca1\u51e0\u5929\uff0c\u4ed6\u5c31\u627e\u5230\u4e86\u5462\uff1f\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u53ef\u80fd\u4e09\u5b9d\u5f97\u624b\u4e4b\u540e\uff0c\u68a6\u5e7b\u4e4b\u5200\u4e5f\u653e\u677e\u4e86\u8b66\u60d5\u4e4b\u5fc3\u5427\uff1f\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4e0d\u77e5\u4f60\u4eec\u6ce8\u610f\u6ca1\u6709\uff0c\u5929\u67ab\u9053\u957f\u8bf4\u4ed6\u5148\u770b\u662f\u4e00\u4e2a\u9ed1\u8863\u8499\u9762\u4eba\uff0c\u4f46\u8ddf\u4e22\u4e86\uff0c\u540e \u6765\u624d\u5728\u4e00\u5ea7\u7cbe\u542b\u5185\u53d1\u73b0\u4e86\u94f6\u8863\u4eba\u3002\u90a3\u4e48\uff0c\u8fd9\u4e24\u4eba\u4f1a\u662f\u4e00\u4e2a\u4eba\u5417\uff1f\u4ed6\u4eec\u54ea\u4e00\u4e2a\u624d\u662f\u771f\u7684\u68a6\u5e7b\u4e4b \u5200\u5462\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u53f9\u6c14\u9053\uff1a\u201c\u771f\u662f\u7591\u4e91\u91cd\u91cd\u554a\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6740\u624b\u96c6\u56e2\u7684\u9996\u8111\u7ea7\u4eba\u7269\u90fd\u5df2\u9732\u9762\uff0c\u6309\u7406\u8bf4\u5f62\u52bf\u5e94\u8be5\u5f88\u660e\u6717\u4e86\uff0c\u4f46\u76ee\u524d\u7684\u5f62 \u52bf\u5374\u662f\u8d8a\u6765\u8d8a\u51f6\u9669\uff0c\u8fd9\u7edd\u5bf9\u4e0d\u662f\u4ec0\u4e48\u597d\u73b0\u50cf\uff0c\u770b\u6765\uff0c\u5176\u4e2d\u53e6\u6709\u9690\u60c5\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u603b\u6355\u5934\u4e0d\u662f\u8bf4\u4e86\u6709\u4e00\u4e2a\u4e3b\u8c0b\u4e4b\u4eba\u5417\uff1f\u4f60\u4eec\u4e14\u770b\u8fd9\u4e3b\u8c0b\u4e4b\u4eba\u4f1a\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u8fd9\u6869\u9634\u8c0b\u65e2\u7136\u9700\u8981\u7528\u5230\u9a6c\u957f\u5c71\u7684\u6570\u4e07\u6c34\u5e08\uff0c\u770b\u6765\u4e3b\u8c0b\u4e4b\u4eba\u6765\u5934\u7edd \u4e0d\u4f1a\u5c0f\u3002\u6562\u4e8e\u8058\u8bf7\u51f6\u624b\uff0c\u8c0b\u5bb3\u671d\u5ef7\u547d\u5bab\u7684\uff0c\u4f1a\u662f\u4ec0\u4e48\u4eba\u5462\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u6211\u770b\uff0c\u4e00\u5b9a\u662f\u738b\u5b57\u7ea7\u7684\u4eba\u7269\uff01\u201d \u3000\u3000\u5931\u94c1\u5cf0\u9053\uff1a\u201c\u597d\u4e86\uff0c\u4e0d\u8981\u778e\u731c\u4e86\uff0c\u6211\u4eec\u8be5\u56de\u53bb\u5e03\u9632\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u56db\u4eba\u8f6c\u8eab\u56de\u5230\u4e86\u5e9c\u8859\u91cc\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u548c\u94c1\u7fce\u5feb\u5230\u91d1\u5e9c\u65f6\uff0c\u94c1\u7fce\u5076\u7136\u4e00\u56de\u5934\uff0c\u5374\u53d1\u73b0\u4e86\u963f\u6a2a\u548c\u963f\u4fdd\u4e8c\u4eba\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u4e0d \u5728\u540e\u9762\u4e86\u3002 \u3000\u3000\u201c\u603b\u6355\u5934\u624b\u4e0b\u679c\u7136\u5c3d\u662f\u80fd\u4eba\u5e72\u5458\uff0c\u963f\u6a2a\u3001\u963f\u4fdd\u4e8c\u4eba\u6709\u7740\u94c1\u5854\u822c\u9ad8\u5927\u9b41\u68a7\u7684\u8eab\u6750\uff0c\u884c\u52a8\u5374 \u53c8\u80fd\u5982\u6b64\u8f7b\u6377\uff0c\u5982\u6b64\u795e\u51fa\u9b3c\u6ca1\uff0c\u6b66\u529f\u7edd\u5bf9\u662f\u8d85\u4e00\u6d41\u7684\u4e86\u3002\u201d \u3000\u3000\u94c1\u7fce\u5fc3\u91cc\u6697\u5fd6\u9053\uff0c\u5bf9\u7a0b\u5c0f\u8776\u4e5f\u5c31\u66f4\u94a6\u4f69\uff0c\u66f4\u656c\u754f\u4e86\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u6b63\u5728\u91d1\u5e9c\u95e8\u524d\u7b49\u7740\u3002 \u3000\u3000\u770b\u6837\u5b50\uff0c\u4ed6\u5df2\u6709\u4e9b\u7740\u6025\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u9053\u957f\u7b49\u6025\u4e86\u5427\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u603b\u6355\u5934\u8bf4\u54ea\u91cc\u8bdd\uff0c\u5b50\u65f6\u8fd8\u6ca1\u6709\u5230\u5462\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u76ee\u5149\u56db\u4e0b\u4e00\u8f6c\uff0c\u6709\u4e9b\u60ca\u8bb6\u5730\u9053\uff1a\u201c\u600e\u4e48\uff0c\u53ea\u6709\u9053\u957f\u4e00\u4eba\uff1f\u91d1\u5e9c\u7684\u90a3\u4e9b\u9ad8\u624b\u4eec\u5462\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u56e0\u4e3a\u62c5\u5fc3\u68a6\u5e7b\u4e4b\u5200\u6e9c\u9003\uff0c\u6240\u4ee5\u6211\u8ba9\u4ed6\u4eec\u5148\u884c\u57cb\u4f0f\u53bb\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5f88\u597d\uff0c\u53ea\u662f\u4ed6\u4eec\u7684\u884c\u52a8\u5e94\u8be5\u7279\u522b\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u4e0d\u8981\u60ca\u52a8\u4e86\u68a6\u5e7b\u4e4b\u5200\u624d\u597d\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u603b\u6355\u5934\u653e\u5fc3\uff0c\u8fd9\u4e9b\u6d3e\u53bb\u7684\u90fd\u662f\u8001\u6c5f\u6e56\u4e86\uff0c\u5341\u5206\u7684\u673a\u8b66\uff0c\u8eab\u624b\u4e5f\u90fd\u662f\u4e00\u7b49 \u4e00\u7684\uff0c\u4e0d\u4f1a\u51fa\u5dee\u5b50\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u6015\u4e00\u4e07\uff0c\u5c31\u6015\u4e07\u4e00\u5440\uff0c\u6211\u4eec\u8fd8\u662f\u5feb\u53bb\u5427\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u6211\u7ed9\u4e8c\u4f4d\u5e26\u8def\u3002\u603b\u6355\u5934\u653e\u5fc3\uff0c\u4eba\u662f\u5929\u9ed1\u900f\u4e86\u4e4b\u540e\u624d\u6d3e\u51fa\u53bb\u7684\uff0c\u800c\u4e14\u6211\u4e00 \u518d\u544a\u8beb\u4ed6\u4eec\uff0c\u884c\u52a8\u4e00\u5b9a\u8981\u5c0f\u5fc3\uff0c\u7edd\u5bf9\u4e0d\u80fd\u592a\u9760\u8fd1\u90a3\u5ea7\u7cbe\u542b\uff0c\u53ea\u8981\u8fdc\u8fdc\u5730\u56f4\u4f4f\u5b83\uff0c\u5c01\u9501\u4f4f\u4e00\u5207 \u53ef\u80fd\u9003\u8dd1\u7684\u8def\u5f84\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u8fb9\u8d70\uff0c\u4e00\u8fb9\u9053\uff1a\u201c\u9053\u957f\uff0c\u8fd9\u6b21\u5982\u80fd\u64d2\u4f4f\u68a6\u5e7b\u4e4b\u5200\uff0c\u6293\u83b7\u8840\u6848\u4e4b\u51f6\uff0c\u9053\u957f\u5f53\u8bba\u5934 \u529f\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u662f\u51fa\u5bb6\u4e4b\u4eba\uff0c\u672c\u65e0\u529f\u540d\u4e4b\u5fc3\uff0c\u8fd9\u6b21\u53c2\u4e0e\u64d2\u62ff\u68a6\u5e7b\u4e4b\u5200\uff0c\u4e00\u662f \u56e0\u4e3a\u91d1\u767e\u5e74\u4e4b\u76db\u60c5\u96be\u5374\uff0c\u4e8c\u662f\u56e0\u4e3a\u6211\u4e0e\u94c1\u6355\u5934\u672c\u662f\u6781\u597d\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u4e09\u4eba\u5df2\u884c\u81f3\u7626\u897f\u6e56\u5de6\u8fb9\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4ece\u73b0\u5728\u8d77\uff0c\u6211\u4eec\u7684\u884c\u52a8\u8981\u6781\u8f7b\u624d\u884c\u3002\u7531\u6b64\u5411\u540e\uff0c\u6cbf\u6e56\u8fb9\u800c\u884c\uff0c \u5927\u7ea6\u534a\u91cc\uff0c\u5c31\u8981\u5230\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u70b9\u5934\uff0c\u548c\u94c1\u7fce\u4e00\u8d77\u653e\u8f7b\u811a\u6b65\uff0c\u7d27\u8ddf\u5728\u5929\u67ab\u9053\u957f\u8eab\u540e\uff0c\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u8d70\u4e86\u7ea6\u6478\u534a\u91cc\u8fdc\uff0c\u5ffd\u542c\u8def\u8fb9\u6742\u8349\u4e1b\u4e2d\u4e00\u9635\u8f7b\u5fae\u7684\u54cd\u58f0\u3002 \u3000\u3000\u4e00\u6761\u9ed1\u5f71\u95ea\u8eab\u800c\u51fa\uff0c\u62e6\u4f4f\u4e86\u53bb\u8def\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u60c5\u51b5\u5982\u4f55\uff1f\u201d \u3000\u3000\u9ed1\u5f71\u4f4e\u58f0\u9053\uff1a\u201c\u5f88\u5e73\u9759\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u6ca1\u6709\u4eba\u8f7b\u4e3e\u5984\u52a8\u5427\uff1f\u201d \u3000\u3000\u9ed1\u5f71\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u90a3\u5c31\u597d\uff01\u4f60\u4ecd\u7136\u57cb\u4f0f\u5728\u8fd9\u91cc\u3002\u4ece\u73b0\u5728\u8d77\uff0c\u53ea\u8981\u6709\u964c\u751f\u7684\u4eba\u4ece\u8fd9\u91cc\u9003\u8dd1\uff0c \u4e00\u5b9a\u8981\u64d2\u4f4f\uff0c\u5426\u5219\uff0c\u552f\u4f60\u662f\u95ee\uff01\u201d \u3000\u3000\u9ed1\u5f71\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u94c1\u5144\uff0c\u6211\u4eec\u8d70\u5427\uff0c\u9a6c\u4e0a\u5c31\u5230\u4e86\u3002\u201d \u3000\u3000\u53c8\u5411\u524d\u8d70\u4e86\u4e00\u4f1a\u513f\uff0c\u7ecf\u8fc7\u4e4b\u5904\uff0c\u597d\u51e0\u6761\u9ed1\u5f71\u62a2\u4e0a\u6765\u6c47\u62a5\u60c5\u51b5\uff0c\u90fd\u8bf4\u7cbe\u820d\u4e4b\u5185\u5341\u5206\u5e73\u9759\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4eca\u5929\uff0c\u68a6\u5e7b\u4e4b\u5200\u662f\u63d2\u7fc5\u4e5f\u96be\u98de\u4e86\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u7cbe\u820d\u5df2\u7ecf\u5728\u671b\u3002 \u3000\u3000\u4f46\u89c1\u5ead\u9662\u5185\u4e00\u70b9\u706f\u5149\u4e5f\u6ca1\u6709\uff0c\u4e5f\u4e0d\u77e5\u9053\u9ed1\u6c89\u6c89\u7684\u5c4b\u5b50\u91cc\u662f\u5426\u6709\u4eba\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u4e3e\u8d77\u624b\u638c\uff0c\u6781\u8f7b\u5730\u62cd\u4e86\u4e24\u4e0b\u3002\u6ca1\u6709\u4eba\u56de\u5e94\u3002\u4ed6\u53c8\u62cd\u4e86\u4e24\u4e0b\u3002\u8fd8\u662f\u6ca1\u6709\u4eba\u56de\u5e94\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u9762\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u603b\u6355\u5934\uff0c\u770b\u6837\u5b50\u662f\u51fa\u4e86\u4ec0\u4e48\u610f\u5916\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e00\u8def\u4e0a\u57cb\u4f0f\u7684\u4eba\u90fd\u8bf4\u5341\u5206\u5e73\u9759\uff0c\u600e\u4e48\u4f1a\u51fa\u610f\u5916\u5462\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u90a3\u4e9b\u4eba\u90fd\u662f\u5916\u56f4\u57cb\u4f0f\u7684\u4eba\uff0c\u5728\u8fd9\u9644\u8fd1\uff0c\u6211\u4eb2\u81ea\u5b89\u6392\u4e86\u56db\u540d\u9ad8\u624b\uff0c\u4f5c\u4e3a\u64d2 \u62ff\u68a6\u5e7b\u4e4b\u5200\u7684\u4e3b\u529b\u519b\uff0c\u53ef\u6211\u521a\u624d\u53d1\u51fa\u4fe1\u53f7\uff0c\u4ed6\u4eec\u5374\u6ca1\u6709\u56de\u5e94\u3002\u201d \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u94c1\u7fce\u538b\u4f4e\u4e86\u58f0\u97f3\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u9053\u957f\uff0c\u4f60\u4eec\u5feb\u6765\u770b\uff01\u201d \u3000\u3000\u5728\u4ed6\u4eec\u8eab\u4fa7\u7ea6\u4e24\u6587\u4e4b\u5916\u7684\u4e00\u682a\u5927\u6930\u6811\u540e\u9762\uff0c\u8eba\u5012\u7740\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u4e00\u770b\uff0c\u4fbf\u5012\u62bd\u4e86\u4e00\u65e5\u51b7\u6c14\uff0c\u9053\uff1a\u201c\u679c\u7136\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u91d1\u5e9c\u7684\u4eba\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u6211\u4eb2\u81ea\u5b89\u6392\u7684\u56db\u4e2a\u4eba\u4e4b\u4e00\uff0c\u6b64\u4eba\u6b66\u529f\u4e4b\u9ad8\uff0c\u8fde\u8d3e\u603b\u7ba1\u4e5f\u5341\u5206 \u8d5e\u8d4f\uff0c\u600e\u4e48\u4f1a\u6084\u65e0\u58f0\u606f\u5730\u88ab\u4eba\u6740\u4e86\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u770b\u6765\uff0c\u80fd\u6740\u4ed6\u7684\u4eba\u53ea\u53ef\u80fd\u662f\u68a6\u5e7b\u4e4b\u5200\u4e86\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e5f\u5c3a\u6709\u68a6\u5e7b\u4e4b\u5200\u624d\u6709\u8fd9\u6837\u7684\u8eab\u624b\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u4eec\u4e09\u4eba\u53c8\u5f80\u65c1\u8fb9\u627e\u53bb\uff0c\u4e0d\u4e00\u4f1a\u513f\uff0c\u4fbf\u627e\u7740\u4e86\u53e6\u5916\u4e09\u4e2a\u4eba\u3002\u8fd9\u4e09\u4e2a\u4eba\u4e2d\uff0c\u4e24\u540d\u5df2 \u6b7b\uff0c\u8fd8\u6709\u4e00\u4eba\u4e5f\u53ea\u5269\u4e0b\u4e86\u534a\u53e3\u6c14\u4e86\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u5f80\u8fd9\u4eba\u53e3\u4e2d\u585e\u4e86\u4e00\u9897\u4fdd\u547d\u795e\u4e39\uff0c\u6643\u7740\u4ed6\u7684\u80a9\u8180\uff0c\u9053\uff1a\u201c\u5feb\u8bf4\uff0c\u662f\u4ec0\u4e48\u4eba\u4e0b\u7684\u624b\uff1f \u4f60\u770b\u89c1\u4e86\u5417\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u65e0\u529b\u5730\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u6ca1\u2026\u2026\u6ca1\u6709\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u68a6\u5e7b\u4e4b\u5200\u8fd8\u5728\u5417\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u7684\u58f0\u97f3\u66f4\u4f4e\u4e86\uff1a\u201c\u4e0d\u2026\u2026\u4e0d\u77e5\u2026\u2026\u9053\u2026\u2026\u201d\u8bf4\u7740\uff0c\u5934\u4e00\u6b6a\uff0c\u6b7b\u4e86\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u5446\u4e86\u4e00\u4f1a\u513f\uff0c\u5bf9\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u770b\u73b0\u5728\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u68a6\u5e7b\u4e4b\u5200\u5df2\u9003\u8131\u4e86\u3002\u8fd9\u6837\u5427\uff0c\u8bf7\u9053\u957f\u653e\u8d77\u4fe1\u53f7\uff0c\u8ba9\u6240\u6709\u57cb \u4f0f\u7684\u4eba\u4e00\u9f50\u5411\u7cbe\u820d\u9760\u62e2\uff0c\u5e76\u6cbf\u8def\u4ed4\u7ec6\u641c\u7d22\uff0c\u4e00\u6709\u53d1\u73b0\uff0c\u7acb\u5373\u51fa\u58f0\u793a\u8b66\uff0c\u6211\u4eec\u5148\u8fdb\u7cbe\u820d\u53bb\u770b\u4e00 \u770b\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u603b\u6355\u5934\u662f\u60f3\u770b\u770b\u80fd\u5426\u5728\u7cbe\u820d\u5185\u53d1\u73b0\u4e00\u4e9b\u80fd\u63a8\u65ad\u4ed6\u7684\u53bb\u5411\u7684\u7ebf\u7d22\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u8bdd\u97f3\u672a\u843d\uff0c\u4eba\u5df2\u98de\u8eab\u63a0\u8d77\uff0c\u76f4\u6251\u5411\u7cbe\u820d\u3002\u94c1\u7fce\u548c\u5929\u67ab\u9053\u957f\u4e5f\u7d27\u968f\u6251\u53bb\u3002 \u3000\u3000\u7cbe\u820d\u5185\u679c\u7136\u7a7a\u65e0\u4e00\u4eba\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u4eae\u4e86\u684c\u4e0a\u7684\u4e00\u76cf\u6cb9\u706f\uff0c\u660f\u9ec4\u7684\u706f\u5149\u7167\u4eae\u4e86\u5c4b\u5b50\u91cc\u5806\u7740\u7684\u6742\u7269\u3002\u4e00\u770b\u4fbf\u77e5\u9053\u8fd9\u91cc \u66fe\u53d1\u751f\u8fc7\u6218\u6597\u3002\u56e0\u4e3a\u7a97\u6237\u6905\u5b50\u90fd\u5df2\u88ab\u51fb\u788e\u4e86\uff0c\u6ee1\u5730\u90fd\u662f\u6454\u7834\u7684\u7897\u3001\u789f\u4ec0\u4e48\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u5403\u60ca\u5730\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5929\u67ab\u9053\u957f\uff0c\u4f60\u6562\u80af\u5b9a\u91d1\u5e9c\u7684\u4eba\u6ca1\u6709\u5148\u884c\u52a8\u624b\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u6211\u6562\u62ff\u6027\u547d\u62c5\u4fdd\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u770b\u4e86\u770b\u4ed6\uff0c\u5fae\u5fae\u4e00\u76b1\u7709\uff0c\u56db\u4e0b\u641c\u7d22\u8d77\u6765\u3002 \u3000\u3000\u5f88\u5feb\uff0c\u5979\u5c31\u6709\u6240\u53d1\u73b0\u4e86\u3002\u5979\u53d1\u73b0\u4e86\u51e0\u6ef4\u9c9c\u8840\u3002 \u3000\u3000\u770b\u6837\u5b50\uff0c\u9c9c\u8840\u6d41\u51fa\u6765\u7684\u65f6\u95f4\u5e76\u4e0d\u957f\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u8bf7\u4f60\u5728\u6b64\u5750\u9547\u6307\u6325\uff0c\u6211\u6cbf\u7740\u8fd9\u4e9b\u8840\u8ff9\u8ffd\u8fc7\u53bb\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u5934\u9053\uff1a\u201c\u597d\u5427\uff0c\u4e5f\u53ea\u597d\u5982\u6b64\u4e86\u3002\u201d \u3000\u3000\u4e00\u4e2a\u95ea\u8eab\uff0c\u65e0\u67ab\u9053\u957f\u5df2\u6251\u51fa\u4e86\u7cbe\u820d\u3002 \u3000\u3000\u94c1\u7fce\u76b1\u7740\u7709\u5934\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u8fd9\u4e8b\u5f88\u6709\u4e9b\u5947\u602a\uff0c\u7cbe\u820d\u4e4b\u4e2d\u53d1\u751f\u8fc7\u6218\u6597\uff0c\u91d1\u5e9c\u5728\u5916\u57cb\u4f0f\u7684 \u56db\u540d\u9ad8\u624b\u600e\u4e48\u4f1a\u4e0d\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u5f88\u5947\u602a\uff0c\u4e0d\u8fc7\uff0c\u4e5f\u6709\u53ef\u80fd\u6765\u4eba\u5148\u6740\u4e86\u90a3\u56db\u540d\u9ad8\u624b\uff0c\u518d\u95ef\u8fdb\u4e86\u7cbe\u820d\u3002\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u662f\u90a3\u56db\u540d\u9ad8\u624b\u7acb\u529f\u5fc3\u5207\uff0c\u64c5\u81ea\u95ef\u8fdb\u6765\u5148\u884c\u52a8\u624b\u4e86\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u51fa\u4e86\u4e00\u4f1a\u513f\u795e\uff0c\u9053\uff1a\u201c\u7684\u786e\u6709\u8fd9\u4e2a\u53ef\u80fd\uff0c\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u53ea\u662f\u4ec0\u4e48\uff0c\u5979\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u9664\u4e86\u6211\u4eec\u4e4b\u5916\uff0c\u8fd8\u6709\u522b\u7684\u52bf\u529b\u4e5f\u60f3\u6740\u6389\u68a6\u5e7b\u4e4b\u5200\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u70b9\u5934\uff0c\u6c89\u541f\u7740\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6211\u4e5f\u8981\u8ffd\u8fc7\u53bb\uff0c\u987a\u4fbf\u544a\u8bc9\u5929\u67ab\u9053\u957f\u603b\u6355\u5934\u7684\u65b0\u53d1\u73b0\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\u5427\uff0c\u4e0d\u8fc7\uff0c\u4f60\u4e00\u5b9a\u8981\u5c0f\u5fc3\uff0c\u6ce8\u610f\u81ea\u5df1\u7684\u5b89\u5168\uff01\u201d \u3000\u3000\u94c1\u7fce\u9053\uff1a\u201c\u8c22\u603b\u6355\u5934\u5173\u5fc3\uff01\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u4ed6\u4e5f\u95ea\u8eab\u6d88\u5931\u5728\u9ed1\u591c\u4e4b\u4e2d\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5728\u684c\u8fb9\u5750\u4e0b\uff0c\u7ea4\u624b\u6258\u7740\u9999\u816e\uff0c\u5446\u5446\u5730\u770b\u7740\u706f\u706b\uff0c\u50cf\u662f\u5728\u51fa\u795e\u3002\u5176\u5b9e\uff0c\u5979\u662f\u4ed4\u7ec6\u5730 \u8fa8\u542c\u7740\u56db\u4e0b\u7684\u52a8\u9759\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u5979\u6162\u6162\u7ad9\u8d77\u8eab\u6765\uff0c\u6de1\u6de1\u5730\u9053\uff1a\u201c\u9601\u4e0b\u65e2\u7136\u6765\u4e86\uff0c\u4e3a\u4f55\u4e0d\u8fdb\u6765\uff1f\u201d \u3000\u3000\u968f\u7740\u4e00\u58f0\u5927\u7b11\uff0c\u9ed1\u8863\u8499\u9762\u4eba\u8d70\u8fdb\u4e86\u7cbe\u820d\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u68a6\u5e7b\u4e4b\u5200\uff0c\u539f\u6765\u4f60\u6ca1\u6709\u8d70\u3002\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u6700\u5371\u9669\u7684\u5730\u65b9\uff0c\u5f80\u5f80\u6b63\u662f\u6700\u5b89\u5168\u7684\u5730\u65b9\uff0c\u73b0\u5728\uff0c\u91d1\u5e9c\u7684\u4eba\u6b63\u56db\u5904\u641c\u7d22\uff0c \u7edd\u4e0d\u4f1a\u6709\u4eba\u60f3\u5230\u8001\u592b\u4f1a\u51fa\u73b0\u5728\u7cbe\u820d\u91cc\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u505a\uff0c\u76ee\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u7b11\u9053\uff1a\u201c\u5f53\u7136\u662f\u4e3a\u4e86\u6545\u5e03\u7591\u9635\uff0c\u5f15\u8bf1\u603b\u6355\u5934\u6765\u8fd9\u91cc\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u60f3\u6740\u6211\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u9664\u5f00\u6b66\u529f\u4e0d\u8c08\uff0c\u5728\u624d\u667a\u3001\u5fc3\u673a\uff0c\u548c\u5224\u65ad\u95ee\u9898\u7684\u80fd\u529b\u4e0a\uff0c\u4f60\u53ef \u7b97\u662f\u8001\u592b\u6b64\u751f\u552f\u4e00\u7684\u52b2\u654c\uff0c\u6211\u672c\u4e0d\u60f3\u8fd9\u6837\u5feb\u5c31\u6740\u4e86\u4f60\uff0c\u53ea\u662f\u5f62\u52bf\u6240\u8feb\uff0c\u8001\u592b\u5df2\u4e0d\u80fd\u518d\u966a\u603b\u6355 \u5934\u73a9\u8fd9\u4e2a\u6e38\u620f\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4ec0\u4e48\u6e38\u620f\uff1f\u4f60\u80fd\u8bf4\u6e05\u695a\u4e00\u70b9\u5417\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u5927\u7b11\u9053\uff1a\u201c\u4f60\u771f\u4e0d\u77e5\u9053\uff1f\u8fd8\u662f\u5728\u88c5\u7cca\u6d82\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u95ee\u6211\u77e5\u4e0d\u77e5\u9053\uff0c\u6211\u53ea\u95ee\u4f60\u613f\u4e0d\u613f\u610f\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5047\u5982\u4f60\u771f\u4e0d\u77e5\u9053\uff0c\u6211\u73b0\u5728\u544a\u8bc9\u4f60\uff0c\u4f60\u4e5f\u662f\u56de\u5929\u4e4f\u672f\uff0c\u5e73\u7a7a\u589e\u6dfb\u4e00\u4e9b\u70e6\u607c\uff0c \u5982\u679c\u4f60\u65e9\u5df2\u77e5\u9053\uff0c\u6211\u53c8\u6709\u4ec0\u4e48\u5fc5\u8981\u518d\u8bf4\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u73b0\u5728\u5c31\u8981\u52a8\u624b\u6740\u6211\u4e86\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff01\u7a0b\u603b\u6355\u5934\u5a07\u8273\u53ef\u4eba\uff0c\u8001\u592b\u672c\u820d\u4e0d\u5f97\u4e0b\u624b\uff0c\u53ef\u60dc\u5440\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4f60\u81ea\u8ba4\u6709\u6740\u6211\u7684\u5b9e\u529b\u5417\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5355\u6253\u72ec\u6597\uff0c\u4f60\u6839\u672c\u4e0d\u662f\u6211\u7684\u5bf9\u624b\uff0c\u4eca\u591c\u4f60\u53c8\u6ca1\u6709\u5e26\u4e0a\u5c0f\u6587\u3001\u5c0f\u96c5\u4e24\u4e2a\u4e2b\u5934\uff0c \u8001\u592b\u6740\u4f60\uff0c\u6613\u5982\u53cd\u638c\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5de7\u7b11\u4f9d\u7136\uff0c\u7f8e\u76ee\u6d41\u76fc\uff0c\u9053\uff1a\u201c\u662f\u5417\uff1f\u4f60\u771f\u6709\u628a\u63e1\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u8db3\u6709\u5341\u4e8c\u6210\u628a\u63e1\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6c89\u4e0b\u8138\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u56de\u5934\u770b\u4e00\u770b\uff0c\u4f60\u8eab\u540e\u662f\u4ec0\u4e48\u4eba\u3002\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u4e00\u56de\u5934\uff0c\u7acb\u523b\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u4ed6\u7adf\u7136\u6839\u672c\u6ca1\u53d1\u73b0\uff0c\u95e8\u5916\u4ec0\u4e48\u65f6\u5019\u7a81\u7136\u51fa\u73b0\u4e86\u4e24\u540d\u94c1\u5854\u822c\u7684\u5927\u6c49\u3002 \u3000\u3000\u201c\u963f\u6a2a\u3001\u963f\u4fdd\uff01\u201d\u9ed1\u8863\u8499\u9762\u4eba\u60ca\u53eb\u9053\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4e5f\u6709\u4e9b\u5403\u60ca\uff1a\u201c\u539f\u6765\u4f60\u77e5\u9053\u4ed6\u4eec\u3002\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e00\u7740\u7b97\u9519\uff0c\u6ee1\u76d8\u7686\u8f93\uff0c\u603b\u6355\u5934\uff0c\u5982\u679c\u4f60\u4eec\u4e09\u4eba\u8054\u624b\uff0c\u8001\u592b \u81ea\u8ba4\u4e0d\u662f\u654c\u624b\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u5f62\u52bf\u53d8\u5316\u5f97\u5f88\u5feb\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u7a81\u7136\u9053\uff1a\u201c\u4f46\u8001\u592b\u4e5f\u4e0d\u4f1a\u675f\u624b\u5c31\u64d2\uff01\u201d \u3000\u3000\u8bdd\u97f3\u672a\u843d\uff0c\u4ed6\u7684\u8eab\u5b50\u5df2\u7ecf\u6a2a\u7a7a\u98de\u8d77\uff0c\u4ece\u7a97\u6237\u76f4\u7a7f\u51fa\u53bb\u3002 \u3000\u3000\u963f\u6a2a\u3001\u963f\u4fdd\u4e00\u9f50\u6251\u4e0a\uff0c\u5374\u53ea\u626f\u4e0b\u4e86\u4ed6\u888d\u895f\u4e0a\u4e00\u5757\u9ed1\u5e03\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4e5f\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u673a\u53d8\u767e\u51fa\uff0c\u6b66\u529f\u60ca\u4eba\uff0c\u6b64\u4eba\u7684\u786e\u53ef\u79f0\u662f\u4e00\u4ee3\u67ad\u96c4\u4e86\uff01\u53ef\u60dc\u5440\uff0c \u537f\u672c\u4f73\u4eba\uff0c\u5948\u4f55\u505a\u8d3c\u5462\uff1f\u201d \u3000\u3000\u963f\u6a2a\u963f\u4fdd\u8fdb\u4e86\u623f\u95f4\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u8ba9\u4ed6\u8dd1\u6389\u4e86\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6ca1\u5173\u7cfb\uff0c\u53cd\u6b63\u4ed6\u8fdf\u65e9\u4f1a\u9732\u51fa\u771f\u9762\u76ee\u6765\u7684\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u7136\u4e0d\u8fdc\u5904\u4f20\u6765\u94c1\u7fce\u7684\u5389\u559d\u58f0\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u4e0b\u5b50\u7ad9\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4e0d\u597d\uff01\u94c1\u6355\u5934\u8ffd\u4e0a\u5f3a\u654c\u4e86\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5979\u4e0e\u963f\u6a2a\u963f\u4fdd\u5df2\u98de\u5feb\u5730\u63a0\u51fa\u4e86\u7cbe\u820d\u3002 \u3000\u3000\u53ea\u542c\u94c1\u7fce\u4e00\u58f0\u60e8\u547c\uff0c\u5636\u54d1\u7684\u58f0\u97f3\u5927\u547c\u9053\uff1a\u201c\u539f\u6765\u2026\u2026\u539f\u6765\u662f\u4f60\uff01\u4f60\u662f\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e09\u4eba\u8d76\u5230\u65f6\uff0c\u94c1\u7fce\u5df2\u7ecf\u6b7b\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u80f8\u524d\u7684\u80c1\u9aa8\u5df2\u88ab\u5168\u90e8\u6253\u65ad\uff0c\u663e\u7136\u662f\u88ab\u521a\u731b\u7684\u5916\u90e8\u638c\u529b\u731b\u51fb\u4e86\u4e00\u638c\u3002 \u3000\u3000\u94c1\u7fce\u7684\u4e24\u53ea\u773c\u775b\u5706\u7741\u7740\uff0c\u6ee1\u662f\u60ca\u7591\uff0c\u5df2\u500d\u52a0\u6012\u706b\uff01\u4ed6\u6b7b\u4e0d\u7791\u76ee\u554a\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u5fc3\u4e2d\u4e00\u9178\uff0c\u773c\u6de1\u50cf\u65ad\u4e86\u7ebf\u7684\u73cd\u73e0\u4e00\u822c\u7eb7\u7eb7\u843d\u4e0b\u3002 \u3000\u3000\u5979\u54ed\u6ce3\u9053\uff1a\u201c\u94c1\u6355\u5934\uff0c\u4f60\u4e0d\u8be5\u6b7b\u554a\uff01\u662f\u6211\u592a\u592a\u610f\u4e86\uff01\u201d \u3000\u3000\u963f\u6a2a\u529d\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u4e5f\u4e0d\u8981\u592a\u4f24\u5fc3\u3001\u592a\u81ea\u8d23\u4e86\uff0c\u6211\u4eec\u4e00\u5b9a\u4f1a\u6293\u4f4d\u51f6\u624b\uff0c\u66ff\u94c1\u6355\u5934\u62a5 \u6b64\u8840\u4ec7\uff01\u201d \u3000\u3000\u963f\u4fdd\u7a81\u7136\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u4eba\u5f71\u4e00\u95ea\uff0c\u76f4\u51b2\u8fc7\u6765\uff0c\u539f\u6765\u662f\u5929\u67ab\u9053\u957f\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u4e00\u770b\u89c1\u8eba\u5012\u5728\u5730\u7684\u94c1\u7fce\uff0c\u987f\u65f6\u6251\u4e86\u4e0a\u53bb\uff0c\u6276\u5c38\u5927\u54ed\u8d77\u6765\u3002 \u3000\u3000\u94c1\u7fce\u7684\u60e8\u6b7b\uff0c\u9707\u60ca\u4e86\u5e9c\u8859\u4e4b\u5185\u6240\u6709\u7684\u4eba\u3002 \u3000\u3000\u738b\u5c11\u537f\u54ed\u5f97\u660f\u4e86\u8fc7\u53bb\uff0c\u4e24\u53ea\u624b\u4e00\u76f4\u6293\u7740\u94c1\u7fce\u65e9\u5df2\u51b0\u51c9\u7684\u624b\u81c2\uff0c\u4e0d\u80af\u677e\u5f00\u3002 \u3000\u3000\u5230\u6700\u540e\uff0c\u7a0b\u5c0f\u8776\u4e0d\u5f97\u4e0d\u4e0b\u4ee4\u8ba9\u5c0f\u6587\u5c06\u738b\u5c11\u537f\u5f3a\u884c\u67b6\u5f00\u4e86\u623f\u95ee\u3002 \u3000\u3000\u5f20\u5b9d\u5584\u4e5f\u5341\u5206\u60b2\u75db\uff0c\u4f46\u4ed6\u663e\u5f97\u5f88\u51b7\u9759\uff0c\u5f88\u80fd\u514b\u5236\u81ea\u5df1\uff0c\u5728\u5e9c\u8859\u91cc\u9ed8\u9ed8\u5730\u8d70\u6765\u8d70\u53bb\uff0c\u5b89\u6392 \u94c1\u7fce\u7684\u540e\u4e8b\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\u5f53\u7136\u5fc3\u91cc\u90fd\u5341\u5206\u96be\u8fc7\uff0c\u4f46\u94c1\u7fce\u7684\u60e8\u6b7b\uff0c\u4e5f\u66f4\u6fc0\u8d77\u4e86\u4ed6\u4eec\u8a93\u64d2\u51f6\u624b\u7684\u51b3\u5fc3\u3002 \u3000\u3000\u4f24\u52bf\u8fd8\u6ca1\u6709\u5168\u7136\u75ca\u6108\u7684\u738b\u575a\u548c\u4f55\u5927\u5149\u4e5f\u6323\u624e\u7740\u4ece\u623f\u95f4\u91cc\u51b2\u4e86\u51fa\u6765\uff0c\u8dea\u5728\u94c1\u7fce\u7684\u5c38\u4f53\u8fb9\uff0c \u8981\u6c42\u7a0b\u5c0f\u8776\u540c\u610f\u4ed6\u4eec\u4e5f\u52a0\u5165\u7834\u6848\u7f09\u51f6\u7684\u884c\u52a8\uff01 \u3000\u3000\u4ed6\u4eec\u4e0e\u94c1\u7fce\u662f\u591a\u5e74\u7684\u8001\u670b\u53cb\uff0c\u773c\u770b\u7740\u8001\u53cb\u60e8\u906d\u6740\u5bb3\uff0c\u4ed6\u4eec\u4e0d\u80fd\u4e0d\u75db\u60dc\uff0c\u4e0d\u80fd\u4e0d\u60b2\u6124\uff0c\u4e0d \u80fd\u4e0d\u6012\u706b\u4e2d\u70e7\u3002 \u3000\u3000\u91d1\u767e\u5e74\u5728\u8d3e\u82f1\u3001\u5929\u67ab\u9053\u957f\u548c\u4f17\u591a\u9ad8\u624b\u62a4\u536b\u7684\u4fdd\u62a4\u4e0b\uff0c\u4eb2\u6765\u5e9c\u8859\u796d\u5960\u94c1\u7fce\u3002 \u3000\u3000\u53ef\u4ee5\u8bf4\uff0c\u94c1\u7fce\u662f\u4e3a\u91d1\u5e9c\u800c\u6b7b\u7684\u3002 \u3000\u3000\u91d1\u767e\u5e74\u8bb8\u8bfa\uff0c\u94c1\u7fce\u8eab\u540e\u4e4b\u4e8b\u7684\u4e00\u5207\u8d39\u7528\uff0c\u90fd\u7531\u91d1\u5e9c\u627f\u62c5\uff0c\u5e76\u8d60\u4e0e\u94c1\u7fce\u9057\u5b64\u4e00\u5927\u7b14\u94f6\u4e24\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u4e5f\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u6765\u796d\u5960\u94c1\u7fce\uff0c\u4e5f\u662f\u6765\u544a\u8bc9\u5211\u90e8\u603b\u6355\u5934\u7a0b\u5c0f\u8776\uff0c\u4e00\u65e6\u5728\u7f09\u51f6\u65f6\u9700\u8981\u5e2e\u52a9\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u8c03\u96c6 \u90e8\u4e0b\u6c34\u5e08\u4e2d\u6700\u7cbe\u9510\u7684\u5175\u5c06\u524d\u6765\u52a9\u9635\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7279\u610f\u5b89\u6392\u4e0e\u9a6c\u957f\u5c71\u79c1\u4e0b\u79d8\u8c08\u4e86\u4e00\u6b21\uff0c\u81f3\u4e8e\u4ed6\u4eec\u4e4b\u95f4\u5230\u5e95\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\uff0c \u53ea\u662f\u9a6c\u957f\u5c71\u4ece\u623f\u95f4\u91cc\u51fa\u6765\u65f6\uff0c\u8138\u4e0a\u7684\u8868\u60c5\u770b\u4e0a\u53bb\u5341\u5206\u5730\u9707\u60ca\u3002 \u3000\u3000\u9686\u91cd\u5730\u57cb\u846c\u4e86\u94c1\u7fce\u540e\uff0c\u5e9c\u8859\u7ec8\u4e8e\u53c8\u5e73\u9759\u4e0b\u6765\u4e86\u3002\u5e73\u9759\u7684\u6c14\u6c1b\u4e2d\u53c8\u900f\u7740\u6c89\u91cd\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7684\u773c\u5708\u4e5f\u662f\u7ea2\u7ea2\u7684\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u94c1\u6355\u5934\u4e34\u6b7b\u524d\uff0c\u66fe\u9ad8\u58f0\u547c\u53eb\u9053\u2018\u539f\u6765\u662f\u4f60\u2026\u2026\u2019 \u8fd9\u8bf4\u660e\uff0c\u6740\u6b7b\u4ed6\u7684\uff0c\u4e00\u5b9a\u662f\u4ed6\u8ba4\u8bc6\u7684\uff0c\u800c\u4e14\u5f88\u719f\u6089\u7684\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u5f53\u65f6\u5728\u573a\u7684\u4eba\uff0c\u9664\u4e86\u90a3\u4f4d\u68a6\u5e7b\u4e4b\u5200\u5916\uff0c\u5e94\u8be5\u5168\u90fd\u662f\u91d1\u5e9c\u4e2d\u7684\u4eba\uff0c \u5bf9\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5f53\u65f6\uff0c\u90a3\u4e2a\u9ed1\u8863\u8499\u9762\u4eba\u521a\u521a\u63a0\u51fa\u7cbe\u820d\u4e0d\u4e45\uff0c\u6740\u94c1\u6355\u5934\u7684\u5e94\u8be5\u662f\u4ed6\u3002\u800c\u4e14\uff0c \u4ece\u94c1\u6355\u5934\u6240\u53d7\u7684\u638c\u529b\u6765\u770b\uff0c\u4e5f\u53ea\u6709\u9ed1\u8863\u8499\u9762\u4eba\u624d\u6709\u5982\u6b64\u6df1\u539a\u7684\u529f\u529b\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u94c1\u6355\u5934\u8ba4\u51fa\u4ed6\u662f\u8c01\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u4eba\u4f1a\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u6b63\u662f\u6574\u4e2a\u6848\u60c5\u7684\u5173\u952e\u4e4b\u5904\uff0c\u53ea\u8981\u5f04\u6e05\u9ed1\u8863\u8499\u9762\u4eba\u7684\u771f\u5b9e\u8eab\u4efd\uff0c\u6240\u6709\u7684\u60c5 \u51b5\u5c31\u4f1a\u6c34\u843d\u77f3\u51fa\u4e86\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u94c1\u7fce\u8ba4\u8bc6\uff0c\u800c\u4e14\u6bd4\u8f83\u719f\u6089\uff0c\u9664\u4e86\u4ed6\u5728\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u4e4b\u5916\uff0c\u5c31\u5c3a\u6709\u626c\u5dde\u4e00\u5e26 \u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u4f46\u51ed\u94c1\u7fce\u7684\u6b66\u529f\uff0c\u4ed6\u5728\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u4e2d\uff0c\u7edd\u4e0d\u4f1a\u6709\u7edd\u9876\u9ad8\u624b\uff0c\u6bd4\u5982\u8bf4\u5929\u67ab \u9053\u957f\u7684\u6b66\u529f\u5c31\u4e0d\u7b97\u5f88\u9ad8\uff01\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u9ed1\u8863\u8499\u9762\u4eba\u5e94\u8be5\u5c31\u662f\u626c\u5dde\u57ce\u91cc\u7684\u67d0\u4e00\u4e2a\u4eba\uff0c\u800c\u4e14\u2026\u2026\u800c\u4e14\uff0c\u5728 \u5e73\u65e5\u91cc\u4ed6\u4e00\u76f4\u6709\u610f\u9690\u85cf\u81ea\u5df1\u8eab\u5177\u7edd\u9876\u6b66\u529f\u8fd9\u6837\u4e00\u4e2a\u4e8b\u5b9e\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\u3002\u5176\u5b9e\uff0c\u94c1\u6355\u5934\u7684\u6b7b\u5f88\u503c\u5f97\uff0c\u56e0\u4e3a\u4ed6\u7528\u6027\u547d\u4e3a\u6211\u4eec\u627e\u51fa\u4e86\u6848\u60c5\u7684\u5173\u952e \u4e4b\u5904\uff0c\u4f7f\u6848\u60c5\u6709\u4e86\u5f88\u5927\u7684\u7a81\u7834\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6211\u6709\u4e00\u4e2a\u63a8\u65ad\uff0c\u53ea\u662f\u4e0d\u77e5\u9053\u5bf9\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u9ed1\u8863\u8499\u9762\u4eba\u5e94\u8be5\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u7684\u786e\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u5929\u67ab\u9053\u957f\u5c31\u662f\u57cb\u4f0f\u5728\u91d1\u5e9c\u5185\u7684\u5367\u5e95\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u7686\u60ca\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u675c\u53f8\u4e3b\uff0c\u4f60\u4e0d\u8981\u7740\u6025\uff0c\u628a\u4f60\u7684\u60f3\u6cd5\u8be6\u7ec6\u5730\u8bf4\u51fa\u6765\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5929\u67ab\u4e00\u76f4\u81ea\u79f0\u5728\u627e\u68a6\u5e7b\u4e4b\u5200\u7684\u884c\u8e2a\uff0c\u7ed3\u679c\u627e\u51fa\u68a6\u5e7b\u4e4b\u5200\u4f4f\u5728\u7cbe\u820d\u4e4b\u5185\uff0c\u4f46 \u91d1\u5e9c\u57cb\u4f0f\u7684\u4eba\u4e00\u70b9\u52a8\u9759\u4e5f\u6ca1\u6709\u542c\u5230\uff0c\u7cbe\u820d\u5185\u5374\u53c8\u53d1\u751f\u4e86\u6218\u6597\uff0c\u8fd9\u8fd8\u4e0d\u53ef\u7591\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u5f88\u53ef\u7591\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u7136\u540e\u5929\u67ab\u53bb\u8ffd\u68a6\u5e7b\u4e4b\u5200\uff0c\u7559\u4e0b\u603b\u6355\u5934\u4e00\u4eba\u5728\u7cbe\u820d\uff0c\u6070\u5de7\u5728\u6b64\u65f6\uff0c\u9ed1\u8863\u8499\u9762 \u4eba\u51fa\u73b0\u4e86\uff0c\u8fd9\u4e5f\u592a\u5de7\u5408\u4e86\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u675c\u53f8\u4e3b\uff0c\u4f60\u8ba4\u4e3a\u8fd9\u662f\u4e00\u4e2a\u5708\u5957\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4ed6\u4eec\u8bbe\u5957\u7684\u76ee\u7684\u5462\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6740\u603b\u6355\u5934\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5f88\u6709\u9053\u7406\uff0c\u53ef\u5982\u679c\u6211\u53bb\u65f6\u5e26\u4e0a\u5c0f\u6587\u3001\u5c0f\u96c5\uff0c\u800c\u4e0d\u662f\u8ba9\u963f\u6a2a\u963f\u4fdd\u5728\u6697\u4e2d\u76f8\u968f\uff0c \u76f8\u673a\u800c\u52a8\u5462\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u90a3\u4e48\u5e9c\u8859\u7684\u9632\u5907\u5fc5\u5b9a\u7a7a\u865a\uff0c\u6211\u60f3\u4ed6\u4eec\u5c31\u4f1a\u7acb\u5373\u6267\u884c\u7b2c\u4e8c\u65b9\u6848\uff0c\u6d3e\u4eba\u7a81\u88ad\u5e9c \u8859\uff0c\u6740\u738b\u5927\u4eba\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u4ed6\u4eec\u505a\u4e86\u4e24\u624b\u51c6\u5907\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5bf9\uff0c\u800c\u4e14\u8fd9\u4e2a\u8ba1\u5212\u7684\u5173\u952e\u4e4b\u5904\uff0c\u4fbf\u662f\u8981\u5c06\u603b\u6355\u5934\u4ece\u5e9c\u8859\u5f15\u5f00\uff0c\u4ee5\u4fbf\u5404\u4e2a\u51fb \u7834\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u5929\u67ab\u9053\u957f\u6839\u672c\u6ca1\u6709\u53d1\u73b0\u68a6\u5e7b\u4e4b\u5200\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5e94\u8be5\u662f\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u90a3\u4e48\u7cbe\u820d\u9644\u8fd1\u91d1\u5e9c\u57cb\u4f0f\u7684\u4eba\u662f\u88ab\u8c01\u6740\u6b7b\u7684\uff1f\u7cbe\u820d\u5185\u7684\u8840\u8ff9\u53c8\u662f\u600e\u4e48\u4e00\u56de\u4e8b \u5462\uff1f\u4f60\u8bf4\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u60f3\u4e86\u60f3\uff0c\u76b1\u7709\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u4e0d\u901a\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u96be\u9053\u68a6\u5e7b\u4e4b\u5200\u771f\u7684\u4f4f\u5728\u7cbe\u820d\u4e4b\u5185\uff1f\u201d \u3000\u3000\u5434\u94c1\u62f3\u9053\uff1a\u201c\u5f88\u53ef\u80fd\u9ed1\u8863\u8499\u9762\u4eba\u6765\u4e5f\u60f3\u5bf9\u4ed8\u68a6\u5e7b\u4e4b\u5200\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u4eec\u8bf4\u5bf9\u4e86\uff0c\u68a6\u5e7b\u4e4b\u5200\u7684\u786e\u4f4f\u5728\u7cbe\u820d\u4e4b\u5185\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u5403\u60ca\u5730\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6709\u4e9b\u60c5\u51b5\uff0c\u6211\u60f3\u73b0\u5728\u5e94\u8be5\u544a\u8bc9\u4f60\u4eec\u4e86\u3002\u68a6\u5e7b\u4e4b\u5200\u7684\u786e\u5728\u626c\u5dde\uff0c\u800c\u4e14\u4e00\u76f4\u4f4f \u5728\u90a3\u5ea7\u7cbe\u820d\u4e4b\u5185\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u5e76\u4e0d\u662f\u6740\u624b\u96c6\u56e2\u7684\u9996\u8111\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u5929\u67ab\u9053\u957f\u8bf4\u7684\u662f\u5b9e\u8bdd\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u90a3\u9ed1\u8863\u8499\u9762\u4eba\u4e3a\u4ec0\u4e48\u53c8\u8981\u4e00\u76f4\u81ea\u79f0\u68a6\u5e7b\u4e4b\u5200\uff0c\u800c\u4e14\u53c8\u6070\u5de7\u5728\u7cbe\u820d\u4e4b\u5185\u73b0\u8eab \u4e86\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4e0d\u4ec5\u60f3\u6740\u6211\uff0c\u4e5f\u60f3\u6740\u68a6\u5e7b\u4e4b\u5200\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6740\u6b7b\u4e86\u68a6\u5e7b\u4e4b\u5200\uff0c\u4ed6\u4eec\u4fbf\u53ef\u5c06\u6240\u6709\u8d23\u4efb\u63a8\u5230\u4ed6\u7684\u5934\u4e0a\uff0c\u4ed6\u4eec\u7684\u8eab\u5206\u5c31\u4e0d\u4f1a \u66b4\u9732\u4e86\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u8fd9\u4e5f\u662f\u4ed6\u4eec\u9634\u8c0b\u7684\u4e00\u90e8\u5206\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\u3002\u4f60\u4eec\u60f3\u4e00\u60f3\uff0c\u5982\u679c\u738b\u5927\u4eba\uff0c\u751a\u81f3\u8fd8\u6709\u9a6c\u957f\u5c71\u88ab\u8c0b\u6740\uff0c\u671d\u5ef7\u4f1a\u4e0d\u4f1a\u6d3e \u4eba\u4e00\u529b\u8ffd\u67e5\uff0c\u800c\u5982\u679c\u51f6\u624b\u5df2\u7ecf\u88ab\u6740\u4e86\uff0c\u671d\u5ef7\u8fd8\u4f1a\u8ffd\u67e5\u5417\uff1f\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u51f6\u624b\u4e5f\u88ab\u6740\uff0c\u6848\u5b50\u5f53\u7136\u4e5f\u5c31\u7b49\u4e8e\u7834\u4e86\uff0c\u671d\u5ef7\u5f53\u7136\u4e0d\u4f1a\u518d\u8ffd\u67e5\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u6837\u4e00\u6765\uff0c\u4ed6\u4eec\u5c31\u80fd\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u5730\u5728\u626c\u5dde\u655b\u8d22\uff0c\u5e76\u5c06\u6c34\u5e08\u7684\u5175\u6743\u4e5f\u6293\u5230 \u624b\u4e2d\u4e86\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u8fd9\u5230\u5e95\u662f\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u9634\u8c0b\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u672a\u67e5\u51fa\u6740\u624b\u96c6\u56e2\u9996\u8111\u4eba\u7269\u4e4b\u524d\uff0c\u6240\u6709\u7684\u63a8\u7406\u90fd\u53ea\u80fd\u662f\u5047\u8bbe\uff0c\u6240\u4ee5\u73b0\u5728\u6211\u8fd8 \u4e0d\u80fd\u8bf4\uff0c\u56e0\u4e3a\u6211\u624b\u4e2d\u4e5f\u6ca1\u6709\u786e\u5207\u7684\u8bc1\u636e\uff0c\u5c31\u7b97\u4e0a\u9762\u7ed9\u6211\u4e0b\u547d\u4ee4\u7684\u4eba\uff0c\u4e5f\u6ca1\u6709\u786e\u5207\u7684\u8bc1\u636e\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u53ef\u600e\u6837\u624d\u80fd\u627e\u5230\u8bc1\u636e\u5462\uff1f\u627e\u4ec0\u4e48\u6837\u7684\u8bc1\u636e\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4eec\u5728\u626c\u5dde\u6240\u505a\u7684\u4e00\u5207\uff0c\u7686\u53ef\u7b97\u4f5c\u8bc1\u636e\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u660e\u767d\u4e86\u5c31\u597d\uff0c\u6211\u77e5\u9053\u4f60\u4eec\u6025\u4e8e\u4e86\u89e3\u771f\u76f8\uff0c\u4f46\u6211\u5374\u4e0d\u80fd\u5c06\u6211\u6240\u77e5\u9053 \u7684\u4e00\u4e9b\u4e8b\u60c5\u544a\u8bc9\u4f60\u4eec\uff0c\u56e0\u4e3a\u8fd9\u4e9b\u4e8b\u60c5\u90fd\u8fd8\u6ca1\u6709\u5f97\u5230\u8bc1\u5b9e\uff0c\u73b0\u5728\u8bf4\u51fa\u6765\uff0c\u53ea\u80fd\u5f71\u54cd\u4f60\u4eec\u81ea\u5df1\u5bf9 \u4e8b\u60c5\u7684\u5224\u65ad\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u4e0d\u7528\u8bf4\u4e86\uff0c\u6211\u4eec\u90fd\u61c2\u5f97\u8fd9\u4e2a\u9053\u7406\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u90a3\u5c31\u597d\uff01\u201d \u3000\u3000\u5979\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u5728\u6ca1\u6709\u65b0\u7684\u60c5\u51b5\u53d1\u751f\u4e4b\u524d\uff0c\u6211\u4eec\u6240\u80fd\u505a\u7684\u5c31\u662f\u52a0\u5f3a\u5e9c\u8859\u7684\u9632\u5b88\uff0c \u4fdd\u62a4\u597d\u738b\u5927\u4eba\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u53ef\u4ec0\u4e48\u65f6\u5019\u624d\u4f1a\u6709\u65b0\u7684\u60c5\u51b5\u53d1\u751f\u5462\uff1f\u771f\u6025\u4eba\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5c91\u53f8\u4e3b\uff0c\u4f60\u60f3\u8fc7\u6ca1\u6709\uff0c\u6211\u4eec\u7740\u6025\uff0c\u9634\u8c0b\u7684\u4e3b\u8c0b\u4e4b\u4eba\u53ea\u4f1a\u6bd4\u6211\u4eec\u66f4\u6025\uff0c\u4e00\u5b9a \u4f1a\u50ac\u4fc3\u6740\u624b\u96c6\u56e2\u5c3d\u5feb\u5b8c\u6210\u4efb\u52a1\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u604d\u7136\u9053\uff1a\u201c\u679c\u7136\u662f\u8fd9\u4e2a\u9053\u7406\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u4eec\u53ea\u8981\u4ee5\u4e0d\u53d8\u5e94\u4e07\u53d8\uff0c\u6309\u5175\u4e0d\u52a8\uff0c\u6740\u624b\u96c6\u56e2\u4e00\u5b9a\u4f1a\u4e3b\u52a8\u51fa\u51fb\u7684\u3002\u201d \u3000\u3000------------- \u3000\u3000\u5b64\u5251\u751f  \u626b\u63cf\u3000  zhuyj(\u6731\u836b\u9636)  OCR  \uff0c\u65e7\u96e8\u697c\u72ec\u5bb6\u8fde\u8f7d \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u4e8c\u5341\u4e94\u7ae0 \u3000\u3000\u6b63\u8bf4\u7740\u8bdd\uff0c\u5ffd\u89c1\u6797\u4e0d\u51e1\u6765\u901a\u62a5\uff0c\u8bf4\u662f\u6709\u4e00\u4e2a\u5e74\u8f7b\u5c0f\u59d0\u795e\u8272\u614c\u5f20\u5730\u8dd1\u8fdb\u4e86\u5e9c\u8859\uff0c\u53e3\u53e3\u58f0\u58f0 \u8981\u89c1\u738b\u5927\u4eba\u548c\u7a0b\u603b\u6355\u5934\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u76b1\u7709\u9053\uff1a\u201c\u5e74\u8f7b\u5c0f\u59d0\uff1f\u957f\u7684\u4ec0\u4e48\u6837\u5b50\uff1f\u201d \u3000\u3000\u6797\u4e0d\u51e1\u9053\uff1a\u201c\u5f88\u7f8e\u4e3d\uff0c\u4f46\u5f88\u6194\u60b4\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u8138\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u60dc\u7389\u3001\u5c0f\u6587\u3001\u5c0f\u96c5\uff0c\u5feb\u53bb\u628a\u5979\u5e26\u5230\u540e\u9662\u6765\uff0c\u4e00\u8def\u4e0a\u8981\u6ce8\u610f \u4fdd\u62a4\u5979\uff0c\u8c28\u9632\u6709\u4eba\u7528\u6697\u5668\u5bf9\u5979\u88ad\u51fb\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u8bf4\u6bd5\uff0c\u548c\u5c0f\u6587\u3001\u5c0f\u96c5\u4e00\u4e2a\u95ea\u8eab\u51fa\u4e86\u5927\u5385\uff0c\u5411\u9662\u5916\u5954\u53bb\u3002 \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u6765\u7684\u4f1a\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u91d1\u5c0f\u7709\u3002\u201d \u3000\u3000\u56db\u5927\u6355\u5934\u5927\u5403\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u91d1\u5c0f\u7709\uff1f\u5979\u6765\u5e9c\u8859\u5e72\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6025\u4ec0\u4e48\uff0c\u7b49\u5979\u6765\u4e86\uff0c\u4e0d\u5c31\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5728\u60dc\u7389\u3001\u5c0f\u6587\u548c\u5c0f\u96c5\u4e09\u4eba\u4fdd\u62a4\u4e0b\u6765\u5230\u540e\u9662\u5927\u5385\u7684\uff0c\u679c\u7136\u6b63\u662f\u91d1\u5c0f\u7709\u3002 \u3000\u3000\u91d1\u5c0f\u7709\u6bd4\u4e0a\u4e00\u6b21\u6765\u5e9c\u8859\u82b1\u5385\u53d7\u5ba1\u65f6\uff0c\u53c8\u7626\u5f31\u4e86\u5f88\u591a\u3002 \u3000\u3000\u539f\u6765\u975e\u5e38\u597d\u770b\u7684\u4e24\u9053\u7ea4\u7ec6\u7684\u79c0\u7709\u7d27\u7d27\u5730\u76b1\u5728\u4e00\u8d77\uff0c\u5728\u7709\u5fc3\u6253\u6210\u4e86\u4e00\u4e2a\u7ed3\u3002 \u3000\u3000\u5979\u90a3\u53cc\u6c34\u7075\u7075\u7684\u773c\u775b\u91cc\u5c04\u51fa\u7684\u662f\u6709\u4e9b\u75f4\u5446\u7684\u3001\u75db\u82e6\u7684\u76ee\u5149\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u59d1\u5a18\uff0c\u4f60\u662f\u4e00\u4e2a\u4eba\u6765\u7684\u5417\uff1f\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u5e7d\u5e7d\u5730\u9053\uff1a\u201c\u6211\u662f\u4e00\u4e2a\u4eba\u6765\u7684\uff0c\u662f\u4ece\u5bb6\u91cc\u5077\u5077\u5730\u6e9c\u5230\u5e9c\u8859\u6765\u7684\u3002\u6211\u6765\u627e\u738b\u5927\u4eba\u548c \u7a0b\u603b\u6355\u5934\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u5c31\u662f\u5211\u90e8\u603b\u6355\u5934\u7a0b\u5c0f\u8776\uff0c\u6709\u4ec0\u4e48\u8bdd\u4f60\u5c31\u8bf4\u5427\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u770b\u7740\u5979\uff0c\u8868\u60c5\u5f88\u60ca\u8bb6\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff0c\u91d1\u59d1\u5a18\u89c9\u5f97\u6211\u4e0d\u592a\u50cf\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u5782\u9996\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\u8a00\u91cd\u4e86\uff0c\u738b\u5927\u4eba\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u53bb\u53eb\u738b\u5927\u4eba\u6765\uff0c\u5c31\u8bf4\u91d1\u59d1\u5a18\u6c42\u89c1\u3002\u201d \u3000\u3000\u60dc\u7389\u7b54\u5e94\u4e00\u58f0\uff0c\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u626c\u5dde\u77e5\u5e9c\u738b\u5c11\u537f\u5306\u5306\u8d70\u8fdb\u5927\u5385\u3002 \u3000\u3000\u91d1\u5c0f\u7709\u4e00\u89c1\u738b\u5c11\u537f\uff0c\u4fbf\u8dea\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u6211\u6709\u7f6a\uff0c\u4f60\u628a\u6211\u6293\u8d77\u6765\uff0c\u6740\u4e86\u6211\u5427\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u5413\u4e86\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u91d1\u59d1\u5a18\u5feb\u5feb\u8bf7\u8d77\uff0c\u6709\u4ec0\u4e48\u8bdd\u6162\u6162\u8bf4\u3002\u201d \u3000\u3000\u60dc\u7389\u6276\u8d77\u91d1\u5c0f\u7709\u3002 \u3000\u3000\u91d1\u5c0f\u7709\u5374\u4f0f\u5728\u60dc\u7389\u80a9\u5934\uff0c\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5c91\u5578\u864e\u4e0d\u7981\u6709\u4e9b\u6101\u6005\uff0c\u5583\u5583\u9053\uff1a\u201c\u5c0f\u59d1\u5a18\u771f\u9ebb\u70e6\uff0c\u597d\u597d\u7684\u54ed\u4e2a\u4ec0\u4e48\u52b2\uff01\u6709\u8bdd\u5c31\u5feb\u8bf4\u561b\uff0c \u8fd9\u4e0d\u662f\u60f3\u6025\u6b7b\u4eba\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u8f6c\u5934\u76ef\u4e86\u4ed6\u4e00\u773c\u3002 \u3000\u3000\u5c91\u5578\u864e\u7acb\u5373\u4f4f\u53e3\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u59d1\u5a18\uff0c\u5fc3\u91cc\u6709\u4ec0\u4e48\u59d4\u5c48\u5c31\u54ed\u51fa\u6765\u5427\uff0c\u54ed\u4e00\u4f1a\u513f\uff0c\u5fc3\u91cc\u4f1a\u597d\u53d7\u4e00\u70b9\u3002\u201d \u3000\u3000\u5979\u8fd9\u4e00\u8bf4\uff0c\u91d1\u5c0f\u7709\u5374\u4e0d\u54ed\u4e86\u3002 \u3000\u3000\u91d1\u5c0f\u7709\u5bf9\u60dc\u7389\u9053\uff1a\u201c\u60dc\u7389\u59d0\u59d0\uff0c\u4f60\u8fd8\u6ca1\u6709\u544a\u8bc9\u603b\u6355\u5934\uff1f\u201d \u3000\u3000\u60dc\u7389\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u8bf4\u4e86\u5440\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u9a6c\u656c\u6587\u662f\u6211\u6740\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6740\u4ed6\u5462\uff1f\u4f60\u6068\u4ed6\u5417\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u7231\u4ed6\uff01\u53ea\u7231\u4ed6\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u53ef\u4f60\u5374\u6740\u4e86\u4ed6\uff01\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u662f\u7684\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff0c\u53cd\u6b63\u4e8b\u540e\u6211\u60f3\u6765\u60f3\u53bb\uff0c\u597d\u50cf\u80fd\u770b\u89c1\u81ea\u5df1 \u63e1\u7740\u5200\uff0c\u76f4\u624e\u8fdb\u4e86\u4ed6\u7684\u2026\u2026\u4ed6\u7684\u80f8\u53e3\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5728\u8fd9\u4e4b\u524d\u7684\u4e8b\u4f60\u8fd8\u60f3\u5f97\u8d77\u6765\u5417\uff1f\u603b\u6709\u4e00\u70b9\u6a21\u7cca\u7684\u5370\u8c61\u5427\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4e00\u70b9\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u60dc\u7389\u5ffd\u7136\u9053\uff1a\u201c\u5373\u5728\u8fdb\u65b0\u623f\u524d\uff0c\u4f60\u5403\u4e86\u4ec0\u4e48\u4e1c\u897f\u6ca1\u6709\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u90a3\u5929\u6211\u5f88\u6fc0\u52a8\uff0c\u6ca1\u6709\u80c3\u53e3\uff0c\u53ea\u5403\u4e86\u4e00\u4e2a\u716e\u9e21\u86cb\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u662f\u8c01\u7ed9\u4f60\u716e\u7684\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u662f\u6211\u7684\u8d34\u8eab\u4e2b\u73af\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u4e5f\u662f\u5979\u7aef\u7ed9\u4f60\u5403\u7684\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u60dc\u7389\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u4e2b\u73af\u8ddf\u4e86\u4f60\u591a\u957f\u65f6\u95f4\u4e86\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u516d\u4e03\u5e74\u4e86\uff0c\u6211\u4eec\u81ea\u5c0f\u4e00\u8d77\u957f\u5927\u7684\u3002\u201d \u3000\u3000\u60dc\u7389\u5bf9\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4e0b\u6bd2\u7684\u4eba\u5e94\u8be5\u4e0d\u662f\u90a3\u4e2a\u4e2b\u73af\u3002\u201d \u3000\u3000\u548c\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\u3002\u5979\u6ca1\u6709\u7406\u7531\u5bb3\u5979\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u5403\u60ca\u9053\uff1a\u201c\u4e0b\u6bd2\uff1f\u60dc\u7389\u59d0\uff0c\u4f60\u662f\u8bf4\u6709\u4eba\u8981\u5bb3\u6211\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u662f\u7684\uff0c\u4e0d\u4ec5\u8981\u5bb3\u4f60\uff0c\u8fd8\u60f3\u501f\u6b64\u5bb3\u738b\u5927\u4eba\u3001\u9a6c\u63d0\u7763\u548c\u4f60\u7239\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u66f4\u5403\u60ca\u4e86\uff0c\u5403\u60ca\u5730\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u8fd9\u6837\u574f\uff1f\u7a0b\u603b\u6355\u5934\uff0c\u738b\u5927\u4eba\uff0c\u4f60\u4eec\u4e00\u5b9a\u8981\u4e3a\u6211\u4f5c \u4e3b\uff0c\u6293\u4f4f\u8fd9\u4e2a\u4eba\uff0c\u4e3a\u656c\u6587\u62a5\u4ec7\uff0c\u4e5f\u4e3a\u94c1\u6355\u5934\u62a5\u4ec7\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u94c1\u6355\u5934\u7684\u4e8b\u4f60\u5df2\u7ecf\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u7239\u544a\u8bc9\u4f60\u7684\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u662f\u8d3e\u603b\u7ba1\u5bf9\u6211\u8bf4\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ed6\u4e3a\u4ec0\u4e48\u8981\u544a\u8bc9\u4f60\u5417\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u4e24\u5929\uff0c\u6211\u5728\u5e9c\u4e2d\u6ca1\u6709\u770b\u5230\u94c1\u6355\u5934\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5979\u7684\u773c\u6cea\u53c8\u6d41\u4e86\u4e0b\u6765\uff0c\u54fd\u54bd\u9053\uff1a\u201c\u94c1\u6355\u5934\u662f\u4e3a\u4e86\u7f09\u62ff\u51f6\u624b\uff0c\u4e3a\u6211\u6d17\u5237\u51a4\u60c5\u624d\u6b7b\u7684\uff0c \u5982\u679c\u6211\u65e9\u8ba4\u4e86\u81ea\u5df1\u662f\u51f6\u624b\uff0c\u4ed6\u4e5f\u5c31\u4e0d\u4f1a\u6b7b\u4e86\uff0c\u603b\u6355\u5934\uff0c\u4f60\u4eec\u628a\u6211\u6293\u8d77\u6765\u5427\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u9a6c\u656c\u6587\u867d\u7136\u662f\u4f60\u6740\u7684\uff0c\u4f46\u4f60\u5e76\u4e0d\u662f\u81ea\u5df1\u771f\u5fc3\u60f3\u6740\u4ed6\uff0c\u800c\u662f\u53d7\u4e86\u8ff7\u836f\u7684\u63a7\u5236\uff0c \u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u5728\u505a\u4ec0\u4e48\u3002\u6211\u4eec\u5982\u679c\u628a\u4f60\u5f53\u51f6\u624b\u6293\u4e86\u8d77\u6765\uff0c\u6740\u4eba\u53ef\u662f\u6b7b\u7f6a\u5440\uff0c\u4f60\u96be\u9053\u4e0d\u6015 \u6b7b\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u51c4\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6b7b\uff1f\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\uff0c\u53cd\u6b63\u656c\u6587\u6b7b\u4e86\uff0c\u6211\u6d3b\u7740\u4e5f\u6ca1\u6709\u4ec0\u4e48\u8da3\u5473\u4e86\uff0c \u8fd8\u4e0d\u5982\u65e9\u70b9\u6b7b\u4e86\uff0c\u53bb\u5730\u4e0b\u548c\u656c\u6587\u76f8\u4f1a\uff0c\u518d\u7ed3\u592b\u59bb\u5462\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u95ee\u9898\u662f\u6293\u4e86\u4f60\uff0c\u771f\u51f6\u5c31\u9ad8\u5174\u4e86\uff0c\u5c31\u900d\u9065\u6cd5\u5916\u4e86\uff0c\u4f60\u96be\u9053\u4e0d\u60f3\u627e\u51fa\u771f\u51f6\uff0c\u4e3a \u9a6c\u656c\u6587\u62a5\u4ec7\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u6211\u4e00\u4e2a\u5f31\u5973\u5b50\uff0c\u5982\u4f55\u80fd\u627e\u51fa\u771f\u51f6\u6765\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u5e2e\u52a9\u6211\u4eec\u554a\uff01\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u600e\u4e48\u5e2e\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5c3d\u91cf\u628a\u4e8b\u60c5\u53d1\u751f\u4e4b\u524d\u7684\u6240\u6709\u60c5\u51b5\u90fd\u56de\u60f3\u8d77\u6765\uff0c\u544a\u8bc9\u6211\u4eec\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u90a3\u4e9b\u60c5\u51b5\u5bf9\u7834\u6848\u6709\u7528\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5f53\u7136\u6709\u7528\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u53ef\u662f\uff0c\u53ef\u662f\u6211\u5c31\u662f\u60f3\u4e0d\u8d77\u6765\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u80fd\u6025\uff0c\u8d8a\u6025\u8d8a\u60f3\u4e0d\u8d77\u6765\u3002\u8fd9\u6837\u5427\uff0c\u6211\u8ba9\u60dc\u7389\u5e2e\u4f60\uff0c\u597d\u4e0d\u597d\uff1f\u201d\u8f6c\u800c\u5bf9\u60dc \u7389\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u966a\u91d1\u59d1\u5a18\u53bb\u4f60\u623f\u95f4\uff0c\u8ba9\u5979\u5148\u597d\u597d\u4f11\u606f\u4e00\u4e0b\uff0c\u518d\u6162\u6162\u56de\u5fc6\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u662f\u3002\u201d\u4f38\u51fa\u5c0f\u624b\u62c9\u8d77\u91d1\u5c0f\u7709\u7684\u5c0f\u624b\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8d70\u5427\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u7b49\u4e00\u7b49\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u603b\u6355\u5934\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8981\u95ee\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u4f60\u544a\u8bc9\u8fc7\u4f60\u7239\u7239\u6ca1\u6709\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u51c4\u7136\u9053\uff1a\u201c\u51e0\u6b21\u90fd\u60f3\u8bf4\uff0c\u53ef\u6211\u53c8\u6015\u4f24\u4e86\u4ed6\u7684\u5fc3\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\uff0c\u4f60\u53bb\u4f11\u606f\u53bb\u5427\u3002\u201d \u3000\u3000\u60dc\u7389\u548c\u91d1\u5c0f\u7709\u4e00\u51fa\u5927\u5385\uff0c\u5c91\u5578\u864e\u4fbf\u8bf4\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u8fd9\u7b97\u4e0d\u7b97\u662f\u65b0\u51fa\u73b0\u7684\u60c5\u51b5\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5f53\u7136\u7b97\u3002\u91d1\u5c0f\u7709\u663e\u7136\u662f\u88ab\u836f\u7269\u63a7\u5236\u4e86\uff0c\u624d\u505a\u4e86\u6740\u4eba\u7684\u5de5\u5177\uff0c\u6309\u5979\u8bf4\u7684\u60c5\u5f62\uff0c \u7684\u786e\u4f1a\u8ba9\u4eba\u7b2c\u4e00\u4e2a\u60f3\u5217\u63a7\u5236\u5979\u7684\u4eba\u662f\u68a6\u5e7b\u4e4b\u5200\u3002\u4f46\u6070\u5de7\u6211\u65e9\u5df2\u77e5\u9053\u68a6\u5e7b\u4e4b\u5200\u4e0d\u53ef\u80fd\u662f\u51f6\u624b\uff0c \u6240\u4ee5\u6211\u8ba9\u60dc\u7389\u53bb\u6162\u6162\u56de\u5fc6\uff0c\u5e2e\u5979\u56de\u5fc6\u8d77\u6240\u6709\u60c5\u51b5\u3002 \u3000\u3000\u800c\u4e14\uff0c\u91d1\u5c0f\u7709\u6765\u5e9c\u8859\uff0c\u6740\u624b\u4eec\u8fdf\u65e9\u4f1a\u53d1\u89c9\u7684\uff0c\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u62c5\u5fc3\u6211\u4eec\u80fd\u591f\u8ba9\u91d1\u5c0f\u7709\u56de\u5fc6\u51fa \u771f\u5b9e\u7684\u60c5\u51b5\u6765\u3002\u90a3\u6837\uff0c\u4ed6\u4eec\u5c06\u65e0\u6cd5\u5ac1\u7978\u7ed9\u68a6\u5e7b\u4e4b\u5200\u4e86\u3002\u6240\u4ee5\uff0c\u6740\u624b\u4eec\u4e00\u5b9a\u4f1a\u5728\u6700\u77ed\u7684\u65f6\u95f4\u5185 \u4f5c\u51fa\u53cd\u5e94\u6765\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\u662f\u62c5\u5fc3\u6740\u624b\u96c6\u56e2\u4f1a\u6740\u4eba\u706d\u53e3\uff0c\u6740\u4e86\u91d1\u5c0f\u7709\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5f88\u62c5\u5fc3\uff0c\u6240\u4ee5\u6211\u4eec\u8981\u50cf\u4fdd\u62a4\u738b\u5927\u4eba\u4e00\u6837\u4fdd\u62a4\u5979\u4e0d\u53d7\u5230\u4f24\u5bb3\u3002\u800c\u4e14\uff0c \u6740\u624b\u4eec\u8981\u6740\u4eba\u706d\u53e3\uff0c\u5e76\u4e0d\u4ec5\u4ec5\u662f\u8981\u6740\u91d1\u5c0f\u7709\u4e00\u4eba\uff0c\u5176\u5b9e\uff0c\u73b0\u5728\u6211\u4eec\u6bcf\u4e00\u4e2a\u4eba\u90fd\u5df2\u6210\u4e86\u4ed6\u4eec\u7684 \u76ee\u6807\u4e86\uff01\u201d \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5e9c\u8859\u5185\u54cd\u8d77\u4e00\u9635\u5608\u6742\u7684\u4eba\u58f0\u3002 \u3000\u3000\u675c\u671b\u6708\u9762\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u6740\u624b\u4eec\u6765\u7684\u597d\u5feb\uff01\u603b\u6355\u5934\uff0c\u6211\u53bb\u51fb\u9000\u4ed6\u4eec\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u548c\u5c0f\u6587\u3001\u5c0f\u96c5\u53bb\u9000\u654c\uff0c\u4f60\u4eec\u56db\u4eba\u548c\u60dc\u7389\u4fdd\u62a4\u597d\u738b\u5927\u4eba\u548c\u91d1\u5c0f\u7709\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u7a0b\u5c0f\u8776\u548c\u5c0f\u6587\u3001\u5c0f\u96c5\u51b2\u51fa\u4e86\u5927\u5385\u3002 \u3000\u3000\u4e00\u51fa\u5385\u95e8\uff0c\u5979\u4eec\u5c31\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u539f\u6765\u51b2\u8fdb\u5e9c\u8859\u7684\u4e0d\u662f\u6740\u624b\u4eec\uff0c\u800c\u662f\u91d1\u767e\u5e74\u5e9c\u4e2d\u7684\u5341\u51e0\u4f4d\u9ad8\u624b\u3002 \u3000\u3000\u53ea\u89c1\u6797\u4e0d\u51e1\u7387\u9886\u7740\u6c5f\u5357\u5206\u53f8\u548c\u4e2d\u5dde\u5206\u53f8\u7684\u6570\u5341\u597d\u624b\u6392\u6210\u4e86\u4eba\u5899\uff0c\u5c3d\u529b\u5c06\u91d1\u5e9c\u7684\u4eba\u6321\u5728\u9662 \u95e8\u5916\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9ad8\u58f0\u9053\uff1a\u201c\u6797\u53f8\u6848\uff0c\u653e\u4ed6\u4eec\u8fdb\u6765\uff01\u201d \u3000\u3000\u6797\u4e0d\u51e1\u6307\u6325\u4f17\u4eba\u6563\u5f00\uff0c\u8ba9\u51fa\u9662\u95e8\uff0c\u53ea\u89c1\u91d1\u767e\u5e74\u6ee1\u9762\u60f6\u6025\u4e4b\u8272\uff0c\u75be\u6b65\u51b2\u8fdb\u9662\u5b50\u3002\u4ed6\u7684\u8eab\u540e\uff0c \u7d27\u8ddf\u7740\u8d3e\u82f1\u3001\u5929\u67ab\u9053\u754f\u3001\u5bd2\u5c71\u56db\u5200\u548c\u7f57\u6d6e\u4e09\u5251\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u6de1\u6de1\u9053\uff1a\u201c\u91d1\u8001\uff0c\u4f60\u5e26\u7740\u4f17\u591a\u9ad8\u624b\u5f3a\u95ef\u5e9c\u8859\uff0c\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u5b9e\u5728\u5bf9\u4e0d\u4f4f\uff0c\u603b\u6355\u5934\uff0c\u738b\u5927\u4eba\uff0c\u5c0f\u5973\u662f\u4e0d\u662f\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u91d1\u5c0f\u7709\u662f\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u6025\u9053\uff1a\u201c\u8bf7\u603b\u6355\u5934\u548c\u738b\u5927\u4eba\u6069\u51c6\u8001\u592b\u5c06\u5730\u5e26\u56de\u5e9c\u53bb\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e0d\u7b54\u8bdd\uff0c\u53ea\u770b\u7740\u738b\u5c11\u537f\u3002 \u3000\u3000\u738b\u5c11\u537f\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u5df2\u77e5\u5979\u7684\u610f\u601d\u3002 \u3000\u3000\u4ed6\u8d70\u4e0a\u4e00\u6b65\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u6055\u4e0b\u5b98\u4e0d\u80fd\u4ece\u547d\u4e86\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u8138\u8272\u5927\u53d8\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u91d1\u5c0f\u59d0\u81ea\u5df1\u81ea\u613f\u5230\u5e9c\u8859\u6765\uff0c\u53c8\u662f\u81ea\u613f\u8981\u7559\u5728\u8fd9\u91cc\uff0c\u4e0b\u5b98\u53c8\u600e\u597d\u8bf4\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u8131\u53e3\u9053\uff1a\u201c\u5c0f\u5973\u662f\u4e0d\u662f\u8bf4\u4e86\u4ec0\u4e48\u4e86\uff1f\u603b\u6355\u5934\uff0c\u738b\u5927\u4eba\uff0c\u5982\u679c\u8fd9\u5b69\u5b50\u80e1\u8bf4\u4e9b\u4ec0\u4e48\uff0c \u4e24\u4f4d\u53ef\u5343\u4e07\u4e0d\u80fd\u8f7b\u4fe1\u554a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u8001\u4e3a\u4ec0\u4e48\u8fd9\u6837\u7d27\u5f20\uff1f\u96be\u9053\u4f60\u5df2\u7ecf\u77e5\u9053\u6740\u9a6c\u656c\u6587\u7684\u4eba\u6b63\u662f\u91d1\u5c0f\u7709\u5417\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u4e00\u5446\uff0c\u9053\uff1a\u201c\u6211\uff1f\u6211\u600e\u4e48\u4f1a\u77e5\u9053\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u8e0f\u4e0a\u4e00\u6b65\uff0c\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u738b\u5927\u4eba\uff0c\u4ece\u6848\u60c5\u6765\u770b\uff0c\u65b0\u623f\u5185\u53ea\u6709\u4e8c\u4eba\uff0c\u800c\u4e14\u51f6\u5668\u4e5f \u7684\u786e\u5728\u91d1\u5c0f\u59d0\u624b\u4e2d\uff0c\u4f46\u738b\u5927\u4eba\u5df2\u7ecf\u77e5\u9053\uff0c\u91d1\u5c0f\u59d0\u662f\u88ab\u68a6\u5e7b\u4e4b\u5200\u5f79\u4f7f\u51fa\u5200\uff0c\u6240\u4ee5\u5979\u4e0d\u662f\u771f\u51f6 \u554a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u6ca1\u6709\u8bf4\u5979\u662f\u771f\u51f6\u554a\uff01\u53ea\u662f\u91d1\u5c0f\u7709\u6765\u5e9c\u8859\u770b\u770b\u5979\u7684\u670b\u53cb\u60dc\u7389\u59d1\u5a18\uff0c\u8fd9 \u672c\u662f\u4e00\u4ef6\u5f88\u5e73\u5e38\u7684\u4e8b\u60c5\uff0c\u91d1\u8001\u5374\u4e3a\u6b64\u5f15\u5f97\u5341\u6570\u9ad8\u624b\uff0c\u5f3a\u95ef\u5e9c\u8859\uff0c\u8fd9\u662f\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u54d1\u53e3\u65e0\u8a00\uff0c\u989d\u5934\u4e0a\u5df2\u5192\u51fa\u4e00\u5c42\u8c46\u5927\u7684\u6c57\u73e0\u3002 \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u53ea\u56e0\u8fd1\u6765\u91d1\u5c0f\u59d0\u54c0\u4f24\u8fc7\u5ea6\uff0c\u795e\u601d\u604d\u60da\uff0c\u655d\u4e1c\u4e3b\u62c5\u5fc3\u5979\u56e0\u6b64\u800c\u9519\u8ba4\u81ea\u5df1\u4fbf\u662f\u51f6 \u624b\uff0c\u524d\u6765\u5e9c\u8859\u6295\u6848\u554a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u91d1\u8001\u7231\u5973\u5fc3\u5207\uff0c\u6240\u4ee5\u4e3e\u6b62\u624d\u4f1a\u5931\u63aa\uff0c\u5012\u662f\u6211\u9519\u602a\u4f60\u4eec \u4e86\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u90a3\u5c0f\u5973\u53ef\u4ee5\u8ddf\u6211\u56de\u53bb\u4e86\u5427\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6050\u6015\u4e0d\u884c\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u53c8\u7740\u6025\u4e86\uff0c\u9053\uff1a\u201c\u8fd9\u53c8\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u5c0f\u7709\u662f\u7834\u6848\u7684\u5173\u952e\u4eba\u7269\uff0c\u66f4\u662f\u6b64\u6848\u7684\u91cd\u8981\u4eba\u8bc1\uff0c\u6211\u8981\u5c06\u5979\u7559\u5728\u5e9c\u8859\u4e2d\uff0c \u8bf1\u5979\u534f\u52a9\u7834\u6848\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u7684\u8138\u8272\u4e0d\u7981\u6709\u4e9b\u96be\u770b\u4e86\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u5979\u662f\u4eba\u8bc1\uff0c\u4e5f\u6ca1\u6709\u5c06\u8bc1\u4eba\u5173\u8d77\u6765\u7684\u9053\u7406\u554a\uff01\u201d \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u91d1\u5c0f\u7709\u5df2\u8d70\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u7239\uff0c\u4e0d\u7528\u8bf4\u4e86\uff0c\u6211\u4e0d\u4f1a\u56de\u53bb\u7684\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u5c0f\u7709\uff0c\u597d\u5b69\u5b50\uff0c\u4f60\u4e0d\u8981\u56fa\u6267\uff0c\u7f09\u62ff\u771f\u51f6\u4e4b\u4e8b\uff0c\u7239\u81ea\u4f1a\u5c3d\u529b\u5e2e\u52a9\u738b\u5927\u4eba\u548c\u603b \u6355\u5934\uff0c\u4f60\u4e5f\u5e2e\u4e0d\u4e0a\u4ec0\u4e48\u5fd9\uff0c\u7559\u5728\u8fd9\u91cc\uff0c\u53ea\u4f1a\u7ed9\u4ed6\u4eec\u6dfb\u9ebb\u70e6\uff01\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u7239\uff0c\u5982\u679c\u4f60\u4e00\u5b9a\u8981\u903c\u6211\u56de\u53bb\uff0c\u6211\u5c31\u627f\u8ba4\u81ea\u5df1\u662f\u51f6\u624b\uff0c\u8ba9\u738b\u5927\u4eba\u628a\u6211\u6293\u8d77\u6765\uff0c \u5173\u8fdb\u5927\u7262\u53bb\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u7a81\u9053\uff1a\u201c\u80e1\u95f9\uff0c\u6765\u4eba\u5566\uff01\u201d \u3000\u3000\u8d3e\u82f1\u8eac\u8eab\u9053\uff1a\u201c\u5c5e\u4e0b\u5728\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u628a\u8fd9\u4e2a\u4e0d\u61c2\u4e8b\u7684\u4e2b\u5934\u7ed1\u8d77\u6765\uff0c\u5e26\u56de\u5e9c\u53bb\uff01\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u5c31\u8981\u5f80\u524d\u8d70\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u4e2a\u95ea\u8eab\uff0c\u62e6\u5728\u4ed6\u9762\u524d\uff0c\u9053\uff1a\u201c\u91d1\u8001\uff0c\u8d3e\u603b\u7ba1\uff0c\u4f60\u4eec\u53ef\u8981\u60f3\u6e05\u695a\u4e86\uff0c\u8fd9\u91cc\u662f\u4ec0\u4e48 \u5730\u65b9\uff0c\u6211\u662f\u4ec0\u4e48\u4eba\uff01\u201d \u3000\u3000\u8d3e\u82f1\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8d3e\u67d0\u53ea\u542c\u4e1c\u4e3b\u7684\u547d\u4ee4\uff0c\u5176\u5b83\u4e00\u6982\u4e0d\u7ba1\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6c89\u4e0b\u4e86\u8138\uff0c\u9053\uff1a\u201c\u91d1\u8001\uff0c\u91d1\u5c0f\u7709\u7559\u5728\u5e9c\u8859\uff0c\u6709\u6211\u4eec\u4fdd\u62a4\u5979\uff0c\u53c8\u6709\u4ec0\u4e48\u4e0d\u597d\u5462\uff1f\u5982 \u679c\u4f60\u4e00\u5b9a\u8981\u5f3a\u884c\u5e26\u5979\u56de\u53bb\uff0c\u5fc5\u7136\u4f1a\u843d\u4e2a\u7ea0\u96c6\u5e2e\u51f6\uff0c\u5f3a\u95ef\u5e9c\u8859\u7684\u7f6a\u540d\uff0c\u503c\u5f97\u5417\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u91d1\u67d0\u4e0e\u4f60\u4eec\u4e00\u76f4\u662f\u5f88\u5408\u4f5c\u7684\uff0c\u53ea\u662f\u8fd9\u4e2b\u5934\u795e\u601d\u604d\u60da\uff0c \u8ba4\u5b9a\u81ea\u5df1\u662f\u51f6\u624b\uff0c\u8fd9\u2026\u2026\u8fd9\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u8001\u653e\u5fc3\uff0c\u662f\u975e\u66f2\u76f4\uff0c\u81ea\u6709\u738b\u5927\u4eba\u7980\u516c\u800c\u65ad\uff01\u6211\u4eec\u77e5\u9053\u91d1\u5c0f\u7709\u662f\u53d7\u68a6\u5e7b\u4e4b \u5200\u5f79\u4f7f\uff0c\u81ea\u7136\u4e0d\u4f1a\u5c06\u5979\u5f53\u505a\u51f6\u624b\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u8981\u662f\u5979\u4e00\u53e3\u54ac\u6b7b\u51f6\u624b\u5c31\u662f\u5979\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u53ea\u8981\u6293\u4f4f\u68a6\u5e7b\u4e4b\u5200\uff0c\u6848\u60c5\u81ea\u7136\u5927\u767d\u5929\u4e0b\uff0c\u9a6c\u516c\u5b50\u5927\u4ec7\u5f97\u62a5\uff0c\u91d1\u5c0f\u7709\u53c8\u4e3a\u4ec0 \u4e48\u8fd8\u8ba4\u5b9a\u81ea\u5df1\u662f\u51f6\u624b\u5462\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u5446\u4e86\u534a\u5929\uff0c\u8dfa\u4e86\u8dfa\u811a\uff0c\u9053\uff1a\u201c\u738b\u5927\u4eba\u3001\u7a0b\u603b\u6355\u5934\uff0c\u91d1\u67d0\u6765\u5f97\u9c81\u83bd\uff0c\u4f46\u7edd\u65e0\u5b83\u610f\uff0c \u671b\u4e8c\u4f4d\u5927\u4eba\u4e0d\u8981\u89c1\u602a\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b54\u9053\uff1a\u201c\u91d1\u8001\u592a\u5ba2\u6c14\u4e86\u3002\u4f60\u7231\u5973\u5fc3\u5207\uff0c\u624d\u4f1a\u5982\u6b64\uff0c\u6211\u4eec\u53c8\u600e\u4f1a\u602a\u4f60\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u770b\u7740\u91d1\u5c0f\u7709\uff0c\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u4f60\u5c31\u5728\u8fd9\u91cc\u4f4f\u51e0\u5929\u5427\uff0c\u5343\u4e07\u4e0d\u8981\u80e1\u601d\u4e71\u60f3\u4e86\uff0c\u7b49\u6293 \u5230\u4e86\u771f\u51f6\uff0c\u4e3a\u7236\u518d\u6765\u63a5\u4f60\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u7239\uff0c\u4f60\u653e\u5fc3\u56de\u53bb\u5427\u3002\u80fd\u4e3a\u7834\u6848\u5e2e\u4e00\u4e0a\u70b9\u5fd9\uff0c\u5973\u513f\u5fc3\u91cc\u53ef\u662f\u9ad8\u5174\u5f97\u5f88\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u7684\u773c\u775b\u91cc\u5ffd\u7136\u6d8c\u51fa\u51e0\u6ef4\u773c\u6cea\uff0c\u4e00\u8f6c\u8eab\uff0c\u5feb\u6b65\u5411\u9662\u5916\u8d70\u53bb\u3002 \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u91d1\u8001\u8d70\u597d\uff0c\u4e0b\u5b98\u5c31\u4e0d\u9001\u4e86\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u5728\u9662\u95e8\u5904\u56de\u8fc7\u8eab\uff0c\u4e3e\u624b\u9065\u9065\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u8bf7\u4e8c\u4f4d\u5927\u4eba\u5343\u4e07\u4e0d\u8981\u89c1\u602a\uff0c\u91d1\u67d0\u5c31\u6b64\u544a \u8f9e\u4e86\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u5c0f\u7709\u59d0\uff0c\u4f60\u7239\u5bf9\u4f60\u53ef\u771f\u597d\uff01\u201d \u3000\u3000\u91d1\u5c0f\u7709\u5782\u6cea\u9053\uff1a\u201c\u662f\u554a\uff0c\u5982\u679c\u4e0d\u662f\u6709\u8fd9\u6837\u597d\u7684\u7239\u7239\uff0c\u656c\u6587\u6b7b\u4e86\uff0c\u6211\u662f\u7edd\u4e0d\u4f1a\u6d3b\u5230\u73b0\u5728 \u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u59d1\u5a18\uff0c\u4f60\u5343\u4e07\u4e0d\u53ef\u8fd9\u6837\u60f3\uff0c\u4f60\u8fd8\u5f88\u5e74\u8f7b\uff0c\u53c8\u7f8e\u4e3d\uff0c\u8fd8\u6709\u5f88\u7f8e\u597d\u7684\u751f\u6d3b\u5728 \u7b49\u7740\u4f60\u5462\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u5c0f\u7709\u59d0\uff0c\u603b\u6355\u5934\u7684\u8bdd\u5f88\u5bf9\uff0c\u4f60\u4e0d\u80fd\u518d\u80e1\u601d\u4e71\u60f3\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u5e7d\u5e7d\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u8fd9\u573a\u98ce\u6ce2\u6765\u5f97\u7a81\u7136\uff0c\u5e73\u606f\u7684\u4e5f\u5feb\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u771f\u62c5\u5fc3\u91d1\u767e\u5e74\u4f1a\u4e00\u610f\u5b64\u884c\uff0c\u90a3\u6837\u7684\u8bdd\uff0c\u4e8b\u60c5\u5c31\u771f\u8981\u7cdf\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6df1\u9501\u53cc\u7709\uff0c\u4e0d\u53d1\u4e00\u8a00\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u770b\u7740\u5979\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u662f\u62c5\u5fc3\u91d1\u5e9c\u4e0d\u4f1a\u5c31\u6b64\u7f62\u4f11\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6709\u610f\u65e0\u610f\u5730\u770b\u4e86\u91d1\u5c0f\u7709\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u7acb\u5373\u660e\u767d\u4e86\uff0c\u6539\u53e3\u9053\uff1a\u201c\u6211\u60f3\u91d1\u767e\u5e74\u4e5f\u4e0d\u4f1a\u56e0\u6b64\u800c\u5bf9\u603b\u6355\u5934\u548c\u738b\u5927\u4eba\u5fc3\u6709\u82a5\u8482\uff0c \u91d1\u5c0f\u59d0\u5728\u5e9c\u8859\uff0c\u4ed6\u5e94\u8be5\u8fd8\u662f\u5f88\u653e\u5fc3\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u52c9\u5f3a\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\u554a\uff0c\u53ea\u662f\u5982\u679c\u8ba9\u53e6\u4e00\u4e2a\u4eba\u77e5\u9053\u91d1\u5c0f\u7709\u6765\u5e9c\u8859\u6295\u6848\u7684\u6d88\u606f\uff0c \u4e8b\u60c5\u53ea\u6015\u662f\u771f\u8981\u7cdf\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\u662f\u6307\u68a6\u5e7b\u4e4b\u5200\uff1f\u201d \u3000\u3000\u4ed6\u6240\u8bf4\u7684\u68a6\u5e7b\u4e4b\u5200\uff0c\u6307\u7684\u5f53\u7136\u662f\u90a3\u4f4d\u9ed1\u8863\u8499\u9762\u4eba\uff0c\u4e5f\u5c31\u662f\u6740\u624b\u96c6\u56e2\u7684\u9996\u8111\u3002\u7a0b\u5c0f\u8776\u9053\uff1a \u201c\u68a6\u5e7b\u4e4b\u5200\u6765\u4e86\u5c82\u4e0d\u66f4\u597d\uff0c\u5927\u5bb6\u53ef\u5728\u5200\u67aa\u5b9e\u529b\u4e0a\u89c1\u4e2a\u771f\u5047\uff0c\u6ca1\u6709\u4ec0\u4e48\u592a\u8ba9\u4eba\u4e3a\u96be\u7684\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\uff0c\u603b\u6355\u5934\u662f\u62c5\u5fc3\u9a6c\u957f\u5c71\u4f1a\u6765\u8981\u4eba\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u80af\u5b9a\u5730\u70b9\u5934\u3002 \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u91d1\u5c0f\u7709\u6765\u5e9c\u8859\u7684\u4e8b\uff0c\u9a6c\u957f\u5c71\u5e94\u8be5\u4e0d\u4f1a\u5f88\u5feb\u77e5\u9053\u5427\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u65e2\u7136\u91d1\u767e\u5e74\u80fd\u5982\u6b64\u8fc5\u901f\u5730\u77e5\u9053\u8fd9\u4e2a\u6d88\u606f\uff0c\u9a6c\u957f\u5c71\u4e3a\u4ec0\u4e48\u4e0d\u4f1a\u77e5\u9053 \u5462\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u5446\u4e86\u4e00\u5446\uff0c\u548c\u4e09\u5927\u6355\u5934\u5404\u5404\u5bf9\u89c6\u4e00\u773c\uff0c\u4f3c\u6709\u6240\u609f\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u7a0b\u5c0f\u8776\u521a\u521a\u5c55\u5f00\u7684\u7709\u5934\u53c8\u7ed3\u7d27\u4e86\uff0c\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u62c5\u5fc3\u7684\u4e8b\u60c5\u7ec8\u4e8e\u8fd8\u662f\u53d1\u751f \u4e86\uff0c\u4f60\u4eec\u542c\uff01\u201d \u3000\u3000\u4f17\u4eba\u4e00\u9f50\u51dd\u795e\u7ec6\u542c\u5916\u9762\u7684\u52a8\u9759\u3002 \u3000\u3000\u4e00\u5f00\u59cb\uff0c\u9664\u4e86\u5e9c\u8859\u5916\u9690\u9690\u4f20\u6765\u7684\u4eba\u58f0\uff0c\u4ec0\u4e48\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u4f46\u4e0d\u4e00\u4f1a\u513f\uff0c\u4ed6\u4eec\u5c31\u542c\u89c1\u4e00\u9635\u6c89\u91cd\u7684\u3001\u8f7b\u5fae\u7684\u3001\u8f70\u9686\u9686\u7684\u58f0\u97f3\u3002 \u3000\u3000\u58f0\u97f3\u8d8a\u6765\u8d8a\u5927\uff0c\u73b0\u5728\u5df2\u80fd\u542c\u51fa\u90a3\u662f\u4e00\u9635\u5bc6\u5982\u66b4\u96e8\u822c\u7684\u9a6c\u8e44\u58f0\u3002 \u3000\u3000\u7136\u540e\u4ed6\u4eec\u53c8\u542c\u89c1\u4e86\u5927\u8857\u4e0a\u4eba\u4eec\u7684\u60ca\u547c\u58f0\u3002 \u3000\u3000\u663e\u7136\uff0c\u884c\u4eba\u4eec\u5df2\u558a\u53eb\u7740\u8eb2\u907f\u5927\u8857\u4e0a\u6a2a\u51b2\u76f4\u649e\u7684\u9a6c\u961f\u3002 \u3000\u3000\u9a6c\u961f\u76f4\u51b2\u5e9c\u8859\u800c\u6765\u3002 \u3000\u3000\u53ea\u542c\u9a6c\u8e44\u58f0\uff0c\u4fbf\u53ef\u63a8\u6d4b\u6765\u7684\u4e0d\u4f1a\u5c11\u4e8e\u4e94\u767e\u540d\u9a91\u5175\u3002 \u3000\u3000\u9686\u9686\u7684\u9a6c\u8e44\u58f0\u4f3c\u4e4e\u9707\u5f97\u4f17\u4eba\u811a\u4e0b\u7684\u5730\u9762\u90fd\u98a4\u6296\u8d77\u6765\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u9762\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u9a6c\u957f\u5c71\u8fd8\u771f\u6765\u4e86\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u4ed6\u6765\u7684\u597d\u5feb\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4ed6\u5e26\u6765\u7684\u7edd\u4e0d\u4e0b\u4e94\u767e\u9a91\u5175\uff0c\u770b\u6765\u662f\u4e0d\u8fbe\u76ee\u7684\uff0c\u52bf\u4e0d\u7f62\u4f11\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u6211\u8ba4\u4e3a\u9a6c\u957f\u5c71\u8fd8\u662f\u4e00\u4e2a\u5f88\u8bb2\u9053\u7406\u7684\u4eba\uff0c\u53ea\u8981\u6653\u4e4b\u4ee5\u7406\uff0c\u52a8\u4e4b\u4ee5\u60c5\uff0c\u4ed6\u672a\u5fc5 \u4f1a\u52a8\u624b\u3002\u518d\u8bf4\uff0c\u5e26\u5175\u56f4\u653b\u5e9c\u8859\uff0c\u7f6a\u540d\u4e4b\u5927\uff0c\u540e\u679c\u4e4b\u4e0d\u582a\u8bbe\u60f3\uff0c\u6211\u60f3\u4ed6\u5fc3\u91cc\u8db3\u6709\u6570\u7684\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u9a6c\u957f\u5c71\u5728\u6570\u5341\u540d\u88c5\u7532\u9c9c\u660e\u3001\u63d0\u67aa\u643a\u5200\u7684\u4eb2\u5175\u7c07\u62e5\u4e4b\u4e0b\uff0c\u5927\u6b65\u51b2\u8fdb\u4e86\u9662\u95e8\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4f4e\u58f0\u9053\uff1a\u201c\u60dc\u7389\uff0c\u5e26\u91d1\u5c0f\u7709\u56de\u623f\u95f4\u53bb\uff0c\u5c0f\u6587\u3001\u5c0f\u96c5\uff0c\u5b88\u4f4f\u623f\u95f4\uff0c\u4efb\u4f55\u4eba\u60f3\u8fdb\u53bb\u6293 \u4eba\uff0c\u683c\u6740\u52ff\u8bba\uff01\u201d \u3000\u3000\u4e09\u4eba\u7acb\u5373\u884c\u52a8\u8d77\u6765\u3002 \u3000\u3000\u60dc\u7389\u6276\u7740\u91d1\u5c0f\u7709\u5411\u540e\u75be\u9000\uff0c\u5c0f\u6587\u3001\u5c0f\u96c5\u62bd\u51fa\u5b9d\u5251\uff0c\u4e00\u5de6\u4e00\u53f3\u62a4\u7740\u5979\u4eec\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u4e00\u89c1\u91d1\u5c0f\u7709\uff0c\u773c\u775b\u5c31\u7ea2\u4e86\uff0c\u5927\u559d\u9053\uff1a\u201c\u51f6\u624b\uff01\u5c0f\u8d31\u4eba\uff01\u4e0d\u8981\u8d70\uff01\u201d\u7a0b\u5c0f\u8776\u8fce\u4e0a\u53bb \u62e6\u4f4f\u4e86\u4ed6\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u4e00\u770b\u5211\u90e8\u603b\u6355\u5934\u6321\u4f4f\u53bb\u8def\uff0c\u867d\u7136\u6012\u6c14\u66f4\u76db\uff0c\u5374\u4e5f\u4e0d\u6562\u53d1\u4f5c\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u9a6c\u63d0\u7763\uff0c\u4f60\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u6ca1\u597d\u6c14\u5730\u9053\uff1a\u201c\u6765\u6293\u4eba\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6293\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\u4e3a\u4f55\u660e\u77e5\u6545\u95ee\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u8bf4\u9053\uff1a\u201c\u6211\u600e\u660e\u77e5\u6545\u95ee\u4e86\uff0c\u9a6c\u63d0\u7763\u662f\u8981\u6293\u91d1\u5c0f\u7709\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u5bf9\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6293\u5979\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u56e0\u4e3a\u5979\u662f\u6740\u4e86\u6211\u513f\u5b50\u7684\u51f6\u624b\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u9519\u4e86\uff01\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u9519\u4e86\uff01\u91d1\u5c0f\u7709\u4e0d\u662f\u51f6\u624b\uff01\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u4e0d\u662f\u51f6\u624b\uff0c\u5979\u4e3a\u4ec0\u4e48\u8981\u6765\u6295\u6848\u81ea\u9996\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u5c0f\u7709\u5e76\u975e\u6765\u6295\u6848\u81ea\u9996\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u6012\u6781\u53cd\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u6765\u6295\u6848\u81ea\u9996\uff0c\u96be\u9053\u662f\u6765\u8d70\u4eb2\u621a\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6b63\u8272\u9053\uff1a\u201c\u9a6c\u63d0\u7763\u8fd9\u8bdd\u6709\u4e9b\u8fc7\u706b\u4e86\u5427\uff1f\u91d1\u5c0f\u7709\u662f\u4e3b\u52a8\u6765\u5e9c\u8859\u4e3a\u5e2e\u6211\u4eec\u7834\u6848\u63d0\u501f\u7ebf \u7d22\u7684\uff01\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u5979\u80fd\u63d0\u4f9b\u4ec0\u4e48\u7ebf\u7d22\uff1f\u5982\u679c\u5979\u80fd\u63d0\u4f9b\u7ebf\u7d22\uff0c\u4e3a\u4ec0\u4e48\u65e9\u4e0d\u63d0\u4f9b\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u53ea\u56e0\u8d35\u516c\u5b50\u4e0d\u5e78\u9047\u5bb3\u540e\uff0c\u91d1\u5c0f\u7709\u4e00\u76f4\u795e\u601d\u604d\u60da\u2026\u2026\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u90a3\u662f\u5979\u6545\u610f\u505a\u51fa\u7684\u5047\u50cf\uff01\u7a0b\u603b\u6355\u5934\uff0c\u4f60\u4e5f\u4e0d\u7528\u4e3a\u5979\u906e\u63a9\u4e86\uff0c\u5feb\u628a\u4eba\u4ea4\u51fa\u6765 \u5427\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u9a6c\u63d0\u7763\u4e0d\u4f1a\u4e0d\u77e5\u9053\u8fd9\u91cc\u662f\u4ec0\u4e48\u5730\u65b9\u5427\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u626c\u5dde\u5e9c\u8859\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u4e16\u9053\uff1a\u201c\u9a6c\u63d0\u7763\u8eab\u4e3a\u671d\u5ef7\u547d\u5b98\uff0c\u4e0d\u4f1a\u4e0d\u61c2\u671d\u5ef7\u5f8b\u6cd5\uff0c\u4e3a\u4f55\u8981\u660e\u77e5\u6545\u72af\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u4e3a\u4e86\u7ed9\u6211\u513f\u5b50\u62a5\u4ec7\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u56db\u4e0b\u91cc\u770b\u4e86\u770b\uff0c\u9053\uff1a\u201c\u9a6c\u63d0\u7763\u5927\u6982\u5e26\u6765\u4e86\u4e94\u767e\u9a91\u5175\uff0c\u5982\u679c\u6211\u575a\u51b3\u4e0d\u4ea4\u51fa\u91d1\u5c0f\u7709\uff0c \u9a6c\u63d0\u7763\u53c8\u5c06\u5982\u4f55\uff1f\u9a71\u5175\u5927\u8fdb\uff0c\u56f4\u653b\u626c\u5dde\u5e9c\u8859\u5417\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u5446\u4e86\u4e00\u5446\uff0c\u770b\u7740\u7a0b\u5c0f\u8776\uff0c\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u6211\u771f\u60f3\u4e0d\u901a\uff0c\u4f60\u4eec\u4e3a\u4f55\u8981\u4e00\u529b\u62a4\u7740\u91d1 \u5c0f\u7709\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u53ea\u56e0\u5979\u7edd\u4e0d\u662f\u771f\u51f6\uff0c\u4e5f\u56e0\u4e3a\u5979\u672c\u662f\u4e2a\u5f88\u53ef\u601c\u7684\u5b69\u5b50\uff01\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u5982\u679c\u5979\u4e0d\u662f\u771f\u51f6\uff0c\u4e3a\u4ec0\u4e48\u8981\u6765\u6295\u6848\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u9a6c\u957f\u5c71\u5df2\u7ecf\u8bf4\u8fc7\u4e00\u904d\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u773c\u4e2d\u7cbe\u5149\u4e00\u95ea\uff0c\u9053\uff1a\u201c\u9a6c\u63d0\u7763\uff0c\u4f60\u53c8\u5982\u4f55\u77e5\u9053\u91d1\u5c0f\u7709\u6765\u5e9c\u8859\u6295\u6848\u8fd9\u4ef6\u4e8b\u7684\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u5fae\u5fae\u4e00\u5f81\uff0c\u968f\u5373\u9053\uff1a\u201c\u8fd9\u4e2a\u4e0d\u52b3\u7a0b\u603b\u6355\u5934\u64cd\u5fc3\uff0c\u9a6c\u67d0\u81ea\u6709\u6d88\u606f\u6765\u6e90\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u738b\u5927\u4eba\u5df2\u7ecf\u7b54\u5e94\u8fc7\u9a6c\u63d0\u7763\uff0c\u6b64\u4e8b\u5305\u5728\u4ed6\u8eab\u4e0a\u3002\u73b0\u5728\uff0c\u6211\u4e5f\u4e0d\u53ef\u5411 \u9a6c\u63d0\u7763\u4fdd\u8bc1\uff0c\u5982\u679c\u5728\u4f60\u89c4\u5b9a\u7684\u671f\u9650\u4e4b\u5185\u6211\u4eec\u7834\u4e0d\u4e86\u6848\uff0c\u6293\u4e0d\u5230\u771f\u51f6\uff0c\u81ea\u4f1a\u653e\u624b\u7531\u9a6c\u63d0\u7763\u4e3a\u6240 \u6b32\u4e3a\uff0c\u800c\u4e14\uff0c\u6211\u672c\u4eba\u8fd8\u4f1a\u8fd8\u4f60\u4e00\u4e2a\u4ea4\u5f85\uff0c\u9a6c\u63d0\u7763\uff0c\u4f60\u770b\u5982\u4f55\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u6c89\u601d\u7740\uff0c\u770b\u6837\u5b50\u662f\u4e00\u65f6\u62ff\u4e0d\u5b9a\u4e3b\u610f\u3002\u4f46\u5f88\u660e\u663e\uff0c\u4ed6\u5df2\u88ab\u7a0b\u5c0f\u8776\u771f\u8bda\u7684\u6001\u5ea6\u6253\u52a8\u4e86\u3002 \u3000\u3000\u6b63\u5728\u8fd9\u6837\u7684\u7d27\u8981\u5173\u5934\uff0c\u4e8b\u60c5\u7a81\u7136\u8d77\u4e86\u610f\u60f3\u4e0d\u5230\u7684\u53d8\u5316\u3002 \u3000\u3000\u540e\u9662\u5916\u7a81\u7136\u4eba\u58f0\u5927\u8d77\uff0c\u53ea\u542c\u91d1\u767e\u5e74\u7684\u58f0\u97f3\u5927\u53eb\u9053\uff1a\u201c\u8ba9\u91d1\u67d0\u8fdb\u53bb\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5927\u5403\u4e00\u60ca\uff0c\u4e00\u95ea\u8eab\u5230\u4e86\u9a6c\u957f\u5c71\u8eab\u8fb9\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u9a6c\u63d0\u7763\uff0c\u4f60\u662f\u624b\u63e1\u91cd\u5175\u7684\u671d\u5ef7\u8981 \u5458\uff0c\u4e00\u5207\u8981\u4ee5\u5927\u5c40\u4e3a\u91cd\uff0c\u5343\u4e07\u4e0d\u8981\u51b2\u52a8\uff0c\u6b64\u6848\u4e4b\u4e2d\uff0c\u53e6\u6709\u9690\u60c5\uff0c\u627e\u673a\u4f1a\u6211\u518d\u4e0e\u4f60\u8be6\u8c08\uff0c\u597d\u4e0d \u597d\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u770b\u7740\u5979\uff0c\u7a0d\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u597d\uff01\u770b\u5728\u603b\u6355\u5934\u7684\u9762\u5b50\u4e0a\uff0c\u4eca\u5929\u6211\u4e0d\u4f1a\u8ddf\u91d1\u767e\u5e74\u8ba1\u8f83 \u4e0b\u53bb\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u591a\u8c22\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u9662\u5916\u6b63\u54cd\u8d77\u4e86\u6e05\u8106\u7684\u5175\u5668\u51fa\u9798\u4e4b\u58f0\uff0c\u770b\u6765\uff0c\u91d1\u767e\u5e74\u7684\u624b\u4e0b\u4e0e\u9a6c\u957f\u5c71\u7684\u9a91\u5175\u5c31\u8981\u52a8 \u624b\u5f00\u4ed7\u4e86\uff01 \u3000\u3000\u53ea\u542c\u6797\u4e0d\u51e1\u60f6\u6025\u7684\u58f0\u97f3\u5927\u547c\u9053\uff1a\u201c\u4e0d\u53ef\u5982\u6b64\uff01\u4f60\u4eec\u4e0d\u53ef\u5982\u6b64\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u626c\u58f0\u9053\uff1a\u201c\u6797\u53f8\u6848\uff0c\u8ba9\u91d1\u8001\u4ed6\u4eec\u8fdb\u6765\uff0c\u4e0d\u8981\u963b\u62e6\uff01\u201d \u3000\u3000\u5979\u53c8\u4f4e\u58f0\u5bf9\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u8ba9\u4f60\u7684\u4eba\u9000\u5f00\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u70b9\u70b9\u5934\uff0c\u5bf9\u8eab\u8fb9\u4e00\u540d\u4eb2\u5175\u6821\u5c09\u9053\uff1a\u201c\u53bb\uff0c\u8ba9\u4ed6\u4eec\u9000\u81f3\u4e8c\u5802\u9662\u5916\uff01\u201d\u6821\u5c09\u5954\u51fa\u4e86\u9662 \u95e8\u3002 \u3000\u3000\u7d27\u63a5\u7740\uff0c\u9662\u5916\u54cd\u8d77\u6574\u9f50\u7684\u9a6c\u8e44\u58f0\uff0c\u800c\u91d1\u767e\u5e74\u4e00\u884c\u5df2\u8d70\u8fdb\u9662\u6765\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u6de1\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u91d1\u8001\u4e3a\u4f55\u53bb\u800c\u590d\u8fd4\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u8001\u592b\u4e0d\u80fd\u8ba9\u5c0f\u5973\u4efb\u4eba\u5bb0\u5272\uff0c\u8896\u624b\u65c1\u89c2\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u8001\u7684\u610f\u601d\u662f\uff0c\u5728\u8fd9\u5e9c\u8859\u4e4b\u5185\uff0c\u6709\u4eba\u5bf9\u91d1\u5c0f\u7709\u5fc3\u6709\u6076\u610f\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u4e00\u6307\u9a6c\u957f\u5c71\uff0c\u9053\uff1a\u201c\u5c31\u662f\u4ed6\uff01\u201d \u3000\u3000\u9a6c\u957f\u5c71\u53cc\u7709\u4e00\u76b1\uff0c\u65ad\u559d\u9053\uff1a\u201c\u653e\u8086\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u4f60\u662f\u8c01\uff0c\u53ea\u8981\u6562\u52a8\u4e00\u52a8\u5c0f\u5973\uff0c\u6211\u5c31\u4e0e\u4f60\u62fc\u4e86\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6de1\u6de1\u9053\uff1a\u201c\u770b\u6765\uff0c\u91d1\u8001\u662f\u60f3\u628a\u8fd9\u5e9c\u8859\u91cd\u5730\u4f5c\u4e3a\u6218\u573a\u4e86\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u5446\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u53ea\u56e0\u4e3a\u4ed6\u9a6c\u957f\u5c71\u5148\u5e26\u5175\u524d\u6765\uff0c\u8001\u592b\u4e0d\u5f97\u5df2\uff0c\u624d\u8d76\u8fc7\u6765\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u8001\u5f53\u7136\u662f\u4e3a\u4e86\u6765\u4fdd\u62a4\u91d1\u5c0f\u7709\uff0c\u800c\u522b\u65e0\u5b83\u610f\uff0c\u5bf9\u5427\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u91d1\u8001\u662f\u4e0d\u76f8\u4fe1\u738b\u5927\u4eba\u548c\u6211\u7a0b\u5c0f\u8776\uff0c\u5929\u4e0b\u95fb\u540d\u7684\u56db\u5927\u540d\u6355\u6709\u80fd\u529b\u4fdd \u62a4\u8eab\u5728\u5e9c\u8859\u4e4b\u5185\u7684\u91d1\u5c0f\u7709\u5417\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u2026\u2026\u201d \u3000\u3000\u8d3e\u82f1\u62f1\u624b\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u655d\u4e1c\u4e3b\u7edd\u6ca1\u6709\u8fd9\u4e2a\u610f\u601d\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u4ed6\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u8d3e\u603b\u7ba1\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u5982\u679c\u4e0d\u662f\u9a6c\u63d0\u7763\u8c03\u96c6\u91cd\u5175\uff0c\u76f4\u51b2\u5e9c\u8859\u800c\u6765\uff0c\u655d\u4e1c\u4e3b\u4e5f\u662f\u4e0d\u4f1a\u518d\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\uff01\u5982\u679c\u9a6c\u63d0\u7763\u73b0\u5728\u5c31\u52d2\u5175\u9000\u8d70\u5462\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u770b\u4e86\u91d1\u767e\u5e74\u4e00\u773c\u3002 \u3000\u3000\u91d1\u767e\u5e74\u6781\u8f7b\u5fae\uff0c\u800c\u4e14\u6781\u5feb\u5730\u70b9\u4e86\u4e00\u4e0b\u5934\u3002 \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u9a6c\u63d0\u7763\u624b\u63e1\u91cd\u5175\uff0c\u968f\u65f6\u53ef\u80fd\u524d\u6765\u5f3a\u884c\u6293\u4eba\uff0c\u655d\u4e1c\u4e3b\u5982\u4f55\u80fd\u653e\u5fc3\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u5ffd\u7136\u9053\uff1a\u201c\u542c\u8d3e\u603b\u7ba1\u7684\u610f\u601d\uff0c\u9a6c\u63d0\u7763\u4e0d\u5e94\u8be5\u6765\u5e9c\u8859\u5417\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8d3e\u67d0\u60f3\u4e0d\u51fa\u9664\u4e86\u5f3a\u884c\u6293\u4eba\uff0c\u4ed6\u8fd8\u80fd\u6765\u5e72\u4ec0\u4e48\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u51b7\u51b7\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\uff0c\u4f60\u5927\u6982\u5fd8\u4e86\uff0c\u9a6c\u63d0\u7763\u6b63\u662f\u65b0\u623f\u8840\u6848\u7684\u82e6\u4e3b\uff0c\u82e6\u4e3b\u6765\u5e9c\u8859\u95ee\u4e00 \u95ee\u6848\u60c5\u8fdb\u5c55\u4e5f\u4e0d\u884c\u5417\uff1f\u5929\u4e0b\u54ea\u6709\u8fd9\u6837\u7684\u9053\u7406\uff01\u201d \u3000\u3000\u8d3e\u82f1\u4e0d\u7981\u5f20\u53e3\u7ed3\u820c\uff01\u4f46\u5f88\u5feb\uff0c\u4ed6\u53c8\u56de\u8fc7\u795e\u6765\uff0c\u9053\uff1a\u201c\u6765\u95ee\u6848\u60c5\u5f53\u7136\u662f\u4ed6\u7684\u6743\u5229\uff0c\u53ef\u6709\u5fc5 \u8981\u5e26\u4e0a\u6570\u767e\u540d\u7cbe\u9510\u9a91\u5175\u5417\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\uff0c\u4f60\u5927\u6982\u53c8\u5fd8\u4e86\u9a6c\u63d0\u7763\u662f\u4ec0\u4e48\u4eba\u4e86\uff0c\u4ed6\u662f\u6c34\u5e08\u63d0\u7763\uff0c\u662f\u624b\u63e1\u96c4\u5175\u8fd1 \u5341\u4e07\u4e4b\u6570\u7684\u671d\u5ef7\u4e00\u65b9\u5927\u5458\uff0c\u4f60\u8d3e\u603b\u7ba1\u662f\u4ec0\u4e48\u4eba\uff0c\u6709\u4ec0\u4e48\u8d44\u683c\u7ba1\u9a6c\u63d0\u7763\u5e26\u591a\u5c11\u4eba\u6765\u5462\uff1f\u518d\u8bf4\uff0c \u4f60\u4eec\u4e0d\u4e5f\u6709\u5341\u6570\u540d\u6b66\u6797\u9ad8\u624b\u968f\u884c\uff0c\u800c\u4e14\u8eab\u5e26\u5175\u5668\u561b\uff1f\u201d\u4e00\u756a\u8bdd\uff0c\u8bf4\u5f97\u8d3e\u82f1\u54d1\u53e3\u65e0\u8a00\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u4e00\u62f1\u624b\uff0c\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u7a0b\u603b\u6355\u5934\uff0c\u9a6c\u67d0\u5bf9\u6848\u60c5\u8fdb\u5c55\u5341\u5206\u6ee1\u610f\uff0c\u4e0d\u591a\u6253\u6270\uff0c\u5c31\u6b64 \u544a\u8f9e\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u4e5f\u8fd8\u793c\u9053\uff1a\u201c\u957f\u5c71\u5144\uff0c\u8bf7\u6055\u4e0b\u5b98\u516c\u52a1\u5728\u8eab\uff0c\u4e0d\u9001\u4e86\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u8bf4\u8d70\u5c31\u8d70\uff0c\u628a\u91d1\u767e\u5e74\u4e00\u884c\u4eba\u667e\u5728\u4e86\u9662\u4e2d\u3002 \u3000\u3000\u91d1\u767e\u5e74\u5c34\u5c2c\u5730\u7b11\u4e86\u7b11\uff0c\u521a\u60f3\u8bf4\u4ec0\u4e48\uff0c\u738b\u5c11\u537f\u5df2\u9053\uff1a\u201c\u91d1\u8001\uff0c\u5982\u679c\u6ca1\u6709\u522b\u7684\u4e8b\uff0c\u4e5f\u8bf7\u56de \u5427\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u53c8\u9053\uff1a\u201c\u800c\u4e14\uff0c\u4eca\u540e\u738b\u67d0\u4e0d\u5e0c\u671b\u770b\u5230\u518d\u53d1\u751f\u50cf\u4eca\u5929\u8fd9\u6837\u7684\u4e8b\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u7b11\u9053\uff1a\u201c\u738b\u5927\u4eba\u653e\u5fc3\uff0c\u4e0d\u4f1a\u4e86\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u4e86\u3002\u201d \u3000\u3000\u5f88\u5feb\uff0c\u5e9c\u8859\u91cc\u53c8\u6062\u590d\u4e86\u5e73\u9759\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u770b\u4e86\u738b\u5c11\u537f\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u738b\u5927\u4eba\u521a\u624d\u597d\u5927\u7684\u5b98\u5a01\u554a\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u4e5f\u7b11\u9053\uff1a\u201c\u4e0d\u4e3a\u522b\u7684\uff0c\u53ea\u662f\u8d3e\u82f1\u8fd9\u4e2a\u4eba\u5b9e\u5728\u662f\u592a\u8ba9\u4eba\u8ba8\u538c\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u800c\u4e14\uff0c\u5728\u6211\u770b\u6765\uff0c\u4ed6\u7684\u8eab\u4efd\u8d8a\u6765\u8d8a\u53ef\u7591\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u53e6\u4e00\u4e2a\u4eba\u66f4\u53ef\u7591\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u8fd8\u7528\u95ee\uff1f\u5f53\u7136\u662f\u5929\u67ab\u9053\u957f\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u8840\u6848\u53d1\u751f\u7684\u5f53\u65e5\uff0c\u91d1\u5e9c\u4e2d\u662f\u8c01\u53d1\u73b0\u7684\u68a6\u5e7b\u4e4b\u5200\u7559\u4e0b\u7684\u6807\u8bb0\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u662f\u5929\u67ab\u9053\u957f\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u8fd9\u4e0d\u660e\u786e\uff0c\u65e2\u7136\u771f\u51f6\u4e0d\u662f\u68a6\u5e7b\u4e4b\u5200\uff0c\u4ed6\u53c8\u4f55\u4ee5\u53d1\u73b0\u68a6\u5e7b\u4e4b\u5200\u7684\u6807\u8bb0\u5462\uff1f\u6240 \u4ee5\uff0c\u8fd9\u662f\u5ac1\u7978\u7684\u7b2c\u4e00\u6b65\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u81ea\u4ece\u53d1\u73b0\u6807\u8bb0\u540e\uff0c\u738b\u67d0\u5c31\u8ba4\u5b9a\u771f\u51f6\u662f\u68a6\u5e7b\u4e4b\u5200\u4e86\u3002\u201d\u675c\u671b\u6708\u9053\uff1a \u201c\u603b\u6355\u5934\uff0c\u6211\u4eec\u8981\u4e0d\u8981\u4f20\u5524\u5929\u67ab\u9053\u957f\u6765\u5e9c\u8859\u53d7\u5ba1\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u6211\u4eec\u53ea\u7528\u9759\u4ee5\u89c2\u53d8\uff0c\u4ed6\u4eec\u7684\u72d0\u72f8\u5c3e\u5df4\u8fdf\u65e9\u4f1a\u81ea\u5df1\u9732\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u5ffd\u7136\u5f00\u53e3\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4e00\u5b9a\u6709\u4eba\u7279\u610f\u5c06\u91d1\u5c0f\u7709\u6765\u5e9c\u8859\u7684\u6d88\u606f\u544a\u8bc9\u4e86\u9a6c\u957f\u5c71\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\uff0c\u800c\u4e14\uff0c\u8fd9\u4e2a\u4eba\u4e00\u5b9a\u662f\u6740\u624b\u96c6\u56e2\u7684\u4eba\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u60f3\u6311\u8d77\u7eb7\u4e89\uff0c\u4ee5\u4fbf\u6d51\u6c34\u6478\u9c7c\uff0c\u4f46\u73b0\u5728\uff0c\u4ed6\u4eec\u4e00\u5b9a\u5df2\u7ecf\u77e5\u9053\u81ea\u5df1\u7684\u7b97 \u76d8\u6253\u7a7a\u4e86\uff0c\u4f1a\u4e0d\u4f1a\u5f3a\u884c\u5bf9\u5e9c\u8859\u53d1\u8d77\u8fdb\u653b\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6709\u4e9b\u62c5\u5fc3\u5730\u9053\uff1a\u201c\u6211\u60f3\uff0c\u5e9c\u8859\u6682\u65f6\u8fd8\u4e0d\u662f\u4ed6\u4eec\u7684\u9996\u8981\u76ee\u6807\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u90a3\u4ed6\u4eec\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u770b\u6765\uff0c\u4ed6\u4eec\u5f88\u53ef\u80fd\u8981\u5148\u5bf9\u9a6c\u957f\u5c71\u4e0b\u624b\u4e86\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\u7684\u63a8\u6d4b\u5f88\u6709\u9053\u7406\uff0c\u4ed6\u4eec\u5148\u5bf9\u9a6c\u63d0\u7763\u4e0b\u624b\uff0c\u5982\u679c\u6210\u529f\uff0c\u4fbf\u53ef\u4ee5\u7528\u81ea \u5df1\u4eba\u53d6\u4ee3\u6c34\u5e08\u63d0\u7763\u4e4b\u4f4d\uff0c\u5c06\u8fd9\u8fd1\u5341\u4e07\u7cbe\u5175\u6293\u5728\u624b\u4e2d\uff0c\u65e2\u4f7f\u4e0d\u6210\u529f\uff0c\u4e5f\u53ef\u5c06\u5f62\u52bf\u6405\u5f97\u66f4\u7eb7\u4e71\uff0c \u4ed6\u4eec\u5c31\u53ef\u4ece\u4e2d\u5f97\u5229\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u53ef\u4ee5\u80af\u5b9a\uff0c\u4ed6\u4eec\u8fd8\u662f\u4f1a\u4ee5\u68a6\u5e7b\u4e4b\u5200\u7684\u540d\u4e49\uff0c\u5bf9\u9a6c\u63d0\u7763\u4e0b\u624b\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6211\u4eec\u662f\u4e0d\u662f\u5e94\u8be5\u6d3e\u4eba\u53bb\u63d0\u9192\u9a6c\u63d0\u7763\uff0c\u6700\u597d\u80fd\u6d3e\u51e0\u4f4d\u9ad8\u624b\u53bb\u4fdd\u62a4\u4ed6 \u7684\u5b89\u5168\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u653e\u5fc3\u5427\uff0c\u6211\u65e9\u5c31\u5b89\u6392\u597d\u4e86\u3002\u201d \u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1 \u3000\u3000\u9a6c\u957f\u5c71\u672c\u6765\u5e76\u4e0d\u662f\u4e00\u4e2a\u559c\u597d\u5973\u8272\u7684\u4eba\uff0c\u4ed6\u5f88\u5c11\u51fa\u5916\u5bfb\u82b1\u95ee\u67f3\uff0c\u8eab\u8fb9\u8fde\u4e00\u4e2a\u5c0f\u59be\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u624b\u4e0b\u7684\u90e8\u5c06\uff0c\u751a\u81f3\u4ed6\u7684\u592b\u4eba\u4e5f\u66fe\u529d\u8fc7\u4ed6\u7eb3\u4e00\u4e2a\u5c0f\u59be\uff0c\u56e0\u4e3a\u4ed6\u7684\u5e74\u7eaa\u4e5f\u6bd4\u8f83\u5927\u4e86\uff0c\u53c8\u5e38 \u5e74\u5728\u5916\u5f81\u6218\uff0c\u6709\u4e00\u4e2a\u5c0f\u59be\uff0c\u53ef\u4ee5\u968f\u519b\u7167\u987e\u4ed6\u7684\u751f\u6d3b\u8d77\u5c45\u3002 \u3000\u3000\u4f46\u6bcf\u4e00\u6b21\uff0c\u9a6c\u957f\u5c71\u90fd\u575a\u51b3\u5730\u4e0d\u540c\u610f\u3002 \u3000\u3000\u81ea\u4ed6\u7684\u72ec\u5b50\u9a6c\u656c\u6587\u88ab\u6740\u4e4b\u540e\uff0c\u9a6c\u957f\u5c71\u5f00\u59cb\u8003\u8651\u662f\u4e0d\u662f\u7eb3\u4e00\u4e2a\u59be\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e0d\u60f3\u8ba9\u9a6c\u5bb6\u7684\u9999\u706b\u6ca1\u6709\u4eba\u7ee7\u627f\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u624b\u4e0b\u7684\u5c06\u9886\u4eec\u81ea\u7136\u5f88\u5feb\u5c31\u9886\u609f\u4e86\u4ed6\u7684\u5fc3\u601d\uff0c\u4e8e\u662f\u56db\u5904\u66ff\u4ed6\u7269\u8272\u9002\u5408\u7684\u4eba\u9009\u3002\u5802\u5802\u6c34\u5e08 \u63d0\u7763\u8981\u7eb3\u59be\uff0c\u5bb9\u8c8c\u5f53\u7136\u8981\u6f02\u4eae\u624d\u884c\uff0c\u800c\u4e14\u8981\u5e74\u8f7b\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u8fdb\u9a6c\u5bb6\u95e8\u7684\u9996\u8981\u4efb\u52a1\u662f\u8981\u80fd\u66ff\u9a6c\u957f\u5c71\u751f\u4e00\u4e2a\u513f\u5b50\uff0c\u4e3a\u9a6c\u5bb6\u4f20\u5b97\u63a5\u4ee3\u3002\u5c31\u5728\u4eca\u5929\uff0c \u9a6c\u957f\u5c71\u6c14\u51b2\u51b2\u5730\u5e26\u7740\u4e94\u767e\u7cbe\u5175\uff0c\u4ece\u5e9c\u8859\u56de\u6765\u7684\u4e0d\u4e45\uff0c\u4ed6\u624b\u4e0b\u7684\u51e0\u5458\u90e8\u5c06\u4fbf\u6765\u5411\u4ed6\u7980\u544a\uff0c\u8bf4\u662f \u627e\u5230\u4e86\u4e00\u4e2a\u53c8\u5e74\u8f7b\u53c8\u6f02\u4eae\u7684\u5973\u5b50\u3002 \u3000\u3000\u800c\u4e14\u8fd9\u4e2a\u5973\u5b50\u7684\u5bb6\u4e5f\u8fd8\u4e0d\u9519\uff0c\u662f\u626c\u5dde\u57ce\u91cc\u4e00\u6237\u5c0f\u6237\u4eba\u5bb6\u7684\u5c0f\u5bb6\u78a7\u7389\uff0c\u636e\u8bf4\u8fd8\u8bfb\u8fc7\u51e0\u672c\u4e66\uff0c \u8bc6\u6587\u65ad\u5b57\uff0c\u624b\u5de5\u5973\u7ea2\u5f53\u7136\u662f\u4e00\u5e94\u4ff1\u4f1a\u3002 \u3000\u3000\u6700\u8ba9\u9a6c\u957f\u5c71\u611f\u5230\u6ee1\u610f\u7684\u662f\uff0c\u636e\u8bf4\u8fd9\u4f4d\u5c0f\u5bb6\u78a7\u7389\u8fd8\u70e7\u5f97\u4e00\u624b\u7cbe\u81f4\u7684\u6dee\u626c\u83dc\u3002\u9a6c\u957f\u5c71\u957f\u5e74\u5728 \u5916\u5f81\u6218\uff0c\u5b9e\u5728\u6ca1\u4ec0\u4e48\u5341\u5206\u53ef\u53e3\u7684\u996d\u83dc\u53ef\u5403\uff0c\u5982\u679c\u4ece\u4eca\u540e\u8eab\u8fb9\u591a\u51fa\u8fd9\u6837\u4e00\u4e2a\u4e0d\u7528\u4ed8\u5de5\u94b1\u7684\u53a8\u5b50 \u517c\u5c0f\u59be\uff0c\u4ed6\u5f53\u7136\u6ca1\u6709\u4efb\u4f55\u4e0d\u6ee1\u610f\u7684\u9053\u7406\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u4eba\u4f60\u4eec\u90fd\u662f\u89c1\u8fc7\u4e86\uff1f\u201d \u3000\u3000\u51e0\u4f4d\u90e8\u5c06\u9053\uff1a\u201c\u89c1\u8fc7\u4e86\uff0c\u867d\u4e0d\u80fd\u7b97\u5f97\u4e0a\u56fd\u8272\u5929\u9999\uff0c\u4f46\u8fd8\u662f\u5341\u5206\u6f02\u4eae\u7684\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u5c0f\u6237\u4eba\u5bb6\u603b\u662f\u5c06\u81ea\u5df1\u7684\u95fa\u5973\u770b\u5f97\u66f4\u91cd\uff0c\u7ed9\u6211\u9a6c\u957f\u5c71\u505a\u59be\uff0c\u4ed6\u4eec\u613f\u610f\u5417\uff1f\u201d \u3000\u3000\u90e8\u5c06\u9053\uff1a\u201c\u542c\u8bf4\u662f\u7ed9\u9a6c\u5927\u4eba\u505a\u59be\uff0c\u5979\u7684\u7236\u6bcd\u5c31\u90fd\u6ca1\u6709\u610f\u89c1\u4e86\uff0c\u53ea\u662f\u60f3\u8bf7\u9a6c\u5927\u4eba\u4ee5\u540e\u4e0d\u8981 \u4e8f\u5f85\u4e86\u4ed6\u4eec\u7684\u5b9d\u8d1d\u5973\u513f\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u5927\u7b11\u9053\uff1a\u201c\u53ea\u8981\u5979\u80fd\u7ed9\u6211\u751f\u4e0a\u51e0\u4e2a\u513f\u5b50\uff0c\u6211\u53c8\u600e\u4f1a\u4e8f\u5f85\u4e8e\u5979\uff01\u55ef\uff0c\u5979\u4eec\u5bb6\u8bf4\u6ca1\u8bf4 \u8981\u4ec0\u4e48\u8058\u793c\u4e4b\u7c7b\u7684\uff1f\u201d \u3000\u3000\u90e8\u5c06\u9053\uff1a\u201c\u90a3\u5bb6\u4eba\u770b\u4e0a\u53bb\u5341\u5206\u8001\u5b9e\u672c\u4efd\uff0c\u6839\u672c\u6ca1\u6709\u63d0\u8fd9\u4e00\u7c7b\u7684\u8981\u6c42\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u7b11\u9053\uff1a\u201c\u597d\uff0c\u679c\u7136\u662f\u8001\u5b9e\u4eba\uff0c\u8fd9\u6837\u5427\uff0c\u6211\u4e5f\u4e0d\u80fd\u767d\u8981\u4ed6\u4eec\u7684\u5973\u513f\uff0c\u4f60\u4eec\u53bb\uff0c\u7ed9\u4ed6 \u4eec\u9001\u53bb\u9ec4\u91d1\u4e94\u5341\u4e24\uff0c\u767d\u94f6\u4e94\u767e\u4e24\uff0c\u7ef8\u7f0e\u4e94\u5341\u5339\uff0c\u5c31\u7b97\u662f\u7ed9\u4ed6\u5bb6\u5973\u513f\u7684\u89c1\u9762\u793c\u5427\u3002\u201d \u3000\u3000\u90e8\u5c06\u9053\uff1a\u201c\u5927\u4eba\uff0c\u4ec0\u4e48\u65f6\u5019\u628a\u4eba\u63a5\u8fc7\u6765\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u95ee\u7684\u4ec0\u4e48\u5e9f\u8bdd\uff0c\u5f53\u7136\u662f\u8d8a\u65e9\u8d8a\u597d\u3002\u201d \u3000\u3000\u90e8\u5c06\u7b11\u9053\uff1a\u201c\u662f\uff0c\u5c5e\u4e0b\u8fd9\u5c31\u53bb\u529e\uff0c\u4eca\u5929\u665a\u4e0a\u5c31\u628a\u4eba\u63a5\u6765\u5427\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u5927\u7b11\u9053\uff1a\u201c\u597d\uff01\u597d\uff01\u4f60\u53bb\u529e\u5427\uff0c\u5feb\u53bb\u5feb\u56de\u3002\u201d \u3000\u3000\u8981\u60f3\u65e9\u6536\u83b7\uff0c\u5c31\u8981\u65e9\u64ad\u79cd\uff0c\u8fd9\u4e2a\u9053\u7406\uff0c\u9a6c\u957f\u5c71\u5f88\u65e9\u5c31\u61c2\u5f97\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u5df2\u7ecf\u6709\u4e9b\u6025\u4e0d\u53ef\u8010\u4e86\uff0c\u76f4\u76fc\u7740\u591c\u665a\u65e9\u4e9b\u6765\u4e34\u3002\u4ed6\u5b9e\u5728\u662f\u592a\u60f3\u518d\u8981\u4e00\u4e2a\u513f\u5b50\u4e86\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u8fd9\u4e5f\u662f\u4eba\u4e4b\u5e38\u60c5\u561b\u3002 \u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1 \u3000\u3000\u5165\u591c\u540e\uff0c\u4e00\u4e58\u5c0f\u8f7f\uff0c\u62ac\u5165\u4e86\u6c34\u5e08\u63d0\u7763\u5e9c\u3002 \u3000\u3000\u63d0\u7763\u5e9c\u540e\u5802\uff0c\u7ea2\u70db\u9ad8\u7167\uff0c\u6620\u51fa\u6ee1\u5c4b\u5b50\u7684\u559c\u6c14\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u5750\u5728\u4e00\u5f20\u6905\u5b50\u4e0a\uff0c\u5c31\u7740\u684c\u4e0a\u7684\u51e0\u6837\u5c0f\u83dc\u81ea\u659f\u81ea\u996e\uff0c\u5634\u8fb9\u6302\u7740\u5fcd\u4e0d\u4f4f\u7684\u5fae\u7b11\u3002 \u3000\u3000\u5c0f\u8f7f\u4e00\u76f4\u62fe\u5165\u4e86\u540e\u5802\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u4e00\u4e0b\u5b50\u7ad9\u8d77\u8eab\u5b50\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4eba\u5462\uff1f\u63a5\u6765\u4e86\uff1f\u201d \u3000\u3000\u90e8\u5c06\u7b11\u563b\u563b\u5730\u9053\uff1a\u201c\u606d\u559c\u5927\u4eba\uff0c\u63a5\u6765\u4e86\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u5feb\uff0c\u5feb\u8bf7\u8fdb\u6765\u3002\u201d \u3000\u3000\u53ea\u89c1\u5728\u51e0\u4f4d\u90e8\u5c06\u7684\u7c07\u62e5\u4e4b\u4e2d\uff0c\u4e00\u4f4d\u8eab\u7740\u7c89\u7ea2\u8863\u886b\uff0c\u6ee1\u7740\u73e0\u7fe0\u7684\u5999\u9f84\u5973\u5b50\u8fc8\u7740\u5a07\u8f6f\u7684\u6b65\u5b50\uff0c \u7f13\u7f13\u8d70\u8fdb\u623f\u95f4\u91cc\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u987f\u65f6\u4e24\u773c\u653e\u51fa\u4e86\u4eae\u5149\u3002 \u3000\u3000\u201c\u4ec0\u4e48\u4e0d\u7b97\u56fd\u8272\u5929\u9999\uff01\u8fd9\u5e2e\u6709\u773c\u65e0\u73e0\u7684\u5bb6\u4f19\uff0c\u4f9d\u6211\u770b\u56fd\u8272\u5929\u9999\u4e5f\u6bd4\u4e0d\u4e86\u5979\uff01\u201d\u9a6c\u957f\u5c71\u5fc3 \u4e2d\u6697\u559c\uff0c\u4e24\u53ea\u773c\u775b\u76ee\u4e0d\u8f6c\u775b\u5730\u76ef\u4f4f\u5999\u9f84\u5973\u5b50\u7c89\u5ae9\u7684\u5a07\u8138\u731b\u770b\u3002 \u3000\u3000\u90e8\u5c06\u4eec\u90fd\u5077\u5077\u5730\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u77aa\u4e86\u4ed6\u4eec\u4e00\u773c\uff0c\u9053\uff1a\u201c\u7b11\u4ec0\u4e48\uff01\u6ca1\u4f60\u4eec\u4ec0\u4e48\u4e8b\u4e86\uff0c\u8d70\u8d70\u8d70\uff0c\u90fd\u8d70\uff0c\u8001\u8001\u5b9e\u5b9e\u7761\u5927 \u89c9\u53bb\uff01\u4e0d\u8981\u5728\u8fd9\u91cc\u70e6\u6211\u3002\u201d \u3000\u3000\u63d0\u7763\u5927\u4eba\u53d1\u4e86\u8bdd\uff0c\u90e8\u5c06\u4eec\u54ea\u6562\u518d\u8d56\u7740\u4e0d\u8d70\uff0c\u4e00\u4e2a\u4e2a\u98de\u5feb\u5730\u8dd1\u51fa\u4e86\u623f\u95f4\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u5927\u58f0\u9053\uff1a\u201c\u660e\u5929\uff0c\u6211\u4f1a\u8bf7\u4f60\u4eec\u559d\u9152\u7684\uff0c\u4e0d\u8981\u4ee5\u4e3a\u6211\u5f88\u5c0f\u6c14\uff01\u201d \u3000\u3000\u4e00\u5458\u90e8\u5c06\u5e94\u9053\uff1a\u201c\u8c22\u5927\u4eba\u3002\u201d \u3000\u3000\u4ed6\u53cd\u624b\u5c06\u623f\u95e8\u5e26\u4e0a\u4e86\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u5fc3\u9053\uff1a\u201c\u8fd9\u5e2e\u5bb6\u4f19\u8fd8\u633a\u77e5\u8da3\u3002\u201d \u3000\u3000\u60f3\u7740\uff0c\u53c8\u8f6c\u8138\u770b\u90a3\u5999\u9f84\u5c11\u5973\uff0c\u8d8a\u770b\u5fc3\u4e2d\u8d8a\u559c\uff0c\u8d8a\u770b\u8d8a\u662f\u9ad8\u5174\u3002 \u3000\u3000\u4ed6\u771f\u662f\u6ca1\u6599\u60f3\u5230\uff0c\u8fd9\u6837\u4e00\u628a\u5e74\u7eaa\u4e86\uff0c\u8fd8\u80fd\u6709\u8fd9\u6837\u7684\u8273\u798f\uff01 \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u5999\u9f84\u5c11\u5973\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u53eb\u6101\u4e91\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u597d\u53ef\u601c\u7684\u540d\u5b57\uff0c\u4f60\u653e\u5fc3\uff0c\u8fdb\u4e86\u6211\u9a6c\u5bb6\u7684\u95e8\uff0c\u4f60\u53ef\u5c31\u4ec0\u4e48\u90fd\u4e0d\u7528\u6101\u4e86\uff01\u201d \u3000\u3000\u5999\u9f84\u5c11\u5973\u9053\uff1a\u201c\u8c22\u5927\u4eba\uff01\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u5341\u5206\u5a07\u5ae9\u8f6f\u6ed1\uff0c\u8bf4\u8fd9\u8bdd\u65f6\uff0c\u53c8\u7528\u4e00\u53cc\u542b\u60c5\u8109\u8109\u7684\u5927\u773c\u775b\u779f\u4e86\u9a6c\u957f\u5c71\u4e00\u773c\u3002 \u3000\u3000------------- \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u4e8c\u5341\u516d\u7ae0 \u3000\u3000\u8fd9\u4e00\u773c\uff0c\u987f\u65f6\u8ba9\u9a6c\u957f\u5c71\u5168\u8eab\u7684\u9aa8\u5934\u516b\u6709\u56db\u4e24\u91cd\u4e86\u3002 \u3000\u3000\u4ed6\u5750\u7740\u53bb\u62c9\u5c11\u5973\u7684\u624b\uff0c\u53e3\u4e2d\u9053\uff1a\u201c\u6101\u4e91\uff0c\u6765\u6765\uff0c\u6211\u4eec\u5148\u559d\u4e00\u676f\u4ea4\u676f\u9152\u3002\u201d \u3000\u3000\u6101\u4e91\u76ee\u5149\u4e00\u8f6c\uff0c\u770b\u4e86\u684c\u4e0a\u7684\u5c0f\u83dc\u4e00\u773c\uff0c\u5a07\u58f0\u9053\uff1a\u201c\u54df\uff0c\u5802\u5802\u63d0\u7763\u5927\u4eba\uff0c\u5c31\u5403\u8fd9\u4e2a\u5440\uff01\u201d \u3000\u3000\u9a6c\u957f\u5c71\u7b11\u9053\uff1a\u201c\u65f6\u95f4\u592a\u7d27\uff0c\u6765\u4e0d\u53ca\u7f6e\u5907\u597d\u83dc\uff0c\u4f60\u5c31\u5c06\u5c31\u4e00\u70b9\u5427\u3002\u201d \u3000\u3000\u6101\u4e91\u4e00\u53cc\u542b\u70df\u5e26\u96fe\u7684\u7f8e\u76ee\u53c8\u779f\u4e86\u9a6c\u957f\u5c71\u4e00\u773c\uff0c\u5a07\u58f0\u9053\uff1a\u201c\u5927\u4eba\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u59be\u8eab\u70e7\u5f97\u4e00 \u624b\u597d\u83dc\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u6574\u4e2a\u4eba\u90fd\u8981\u98d8\u8d77\u6765\u4e86\uff0c\u7b11\u9053\uff1a\u201c\u77e5\u9053\uff0c\u77e5\u9053\uff0c\u4e0d\u8fc7\uff0c\u4eca\u5929\u592a\u665a\u4e86\uff0c\u660e\u5929\u518d\u5c1d\u4e00\u5c1d \u4f60\u7684\u624b\u827a\u5427\u3002\u201d \u3000\u3000\u6101\u4e91\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u4eca\u5929\u665a\u4e0a\u8fd9\u6837\u5927\u559c\u7684\u65f6\u5019\uff0c\u8ba9\u5927\u4eba\u5403\u8fd9\u6837\u7684\u9152\u83dc\uff0c\u59be\u8eab\u5fc3\u91cc\u5982\u4f55\u8fc7\u5f97 \u53bb\uff0c\u5927\u4eba\uff0c\u59be\u5728\u5bb6\u91cc\u5df2\u7ecf\u70e7\u597d\u4e86\u51e0\u6837\u83dc\uff0c\u7279\u610f\u5e26\u6765\u4e86\uff0c\u5927\u4eba\u60f3\u4e0d\u60f3\u5c1d\u5c1d\u9c9c\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u5fc3\u4e2d\u66f4\u559c\uff0c\u4e00\u8fde\u58f0\u9053\uff1a\u201c\u5f53\u7136\u60f3\uff0c\u5f53\u7136\u60f3\uff0c\u4e0d\u4ec5\u60f3\u5c1d\u4f60\u70e7\u7684\u83dc\uff0c\u8fd8\u60f3\u2026\u2026\u563f \u563f\u2026\u2026\u201d \u3000\u3000\u6101\u4e91\u9762\u4e0a\u4e00\u7ea2\uff0c\u5a07\u7f9e\u65e0\u6bd4\u5730\u55d4\u9053\uff1a\u201c\u5927\u4eba\uff0c\u4f60\u771f\u574f\uff01\u201d\u8bf4\u7740\uff0c\u5e26\u7740\u5341\u4e8c\u5206\u8bf1\u60d1\u7684\u76ee\u5149\u53c8 \u779f\u5411\u4e86\u9a6c\u957f\u5c71\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u76f4\u89c9\u5f97\u6d51\u8eab\u71e5\u70ed\uff0c\u5feb\u8981\u6309\u637a\u4e0d\u4f4f\u4e86\u3002\u4ed6\u5df2\u6709\u5f88\u591a\u5e74\u6ca1\u6709\u8fd9\u79cd\u611f\u89c9\u4e86\u3002 \u3000\u3000\u53ea\u662f\u6101\u4e91\u626d\u7740\u7ea4\u8170\u8d70\u5230\u95e8\u8fb9\uff0c\u62fe\u8d77\u5730\u4e0a\u7684\u4e00\u4e2a\u6f06\u7af9\u98df\u76d2\uff0c\u6253\u5f00\u4e86\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u7acb\u5373\u95fb\u5230\u4e00\u80a1\u9189\u4eba\u7684\u9152\u83dc\u9999\u6c14\u3002 \u3000\u3000\u6101\u4e91\u4e00\u53cc\u767d\u5ae9\u7684\u5c0f\u624b\u684c\u4e0a\u684c\u4e0b\u5fd9\u4e4e\u4e86\u4e00\u9635\uff0c\u516d\u4e03\u6837\u8272\u9999\u4ff1\u4f73\u7684\u83dc\u80b4\u5df2\u7ecf\u5728\u684c\u5b50\u4e0a\u6574\u6574\u9f50 \u9f50\u5730\u6446\u597d\u3002 \u3000\u3000\u7136\u540e\uff0c\u5979\u53c8\u6367\u51fa\u4e86\u4e00\u74f6\u9152\u3002 \u3000\u3000\u74f6\u5b50\u4e00\u6253\u5f00\uff0c\u9a6c\u957f\u5c71\u5c31\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u9152\uff0c\u771f\u9999\u554a\u3002\u201d \u3000\u3000\u6101\u4e91\u66ff\u4ed6\u659f\u4e0a\u4e00\u676f\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u5973\u513f\u7ea2\uff0c\u8ba9\u59be\u4eb2\u624b\u659f\u7ed9\u90ce\u541b\u559d\u3002\u201d \u3000\u3000\u4e0d\u53eb\u5927\u4eba\u53eb\u90ce\u541b\uff0c\u9a6c\u957f\u5c71\u9152\u672a\u8fdb\u53e3\uff0c\u4eba\u5df2\u7ecf\u9189\u4e86\u3002 \u3000\u3000\u6101\u4e91\u5a07\u58f0\u50ac\u4fc3\u9053\uff1a\u201c\u5927\u4eba\uff0c\u4f60\u559d\u561b\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u559d\uff0c\u559d\uff0c\u6101\u4e91\uff0c\u4f60\u4e5f\u559d\u3002\u201d \u3000\u3000\u4e00\u676f\u9152\u559d\u4e0b\u53bb\uff0c\u6101\u4e91\u5a07\u7f8e\u7684\u8138\u4e0a\u987f\u6dfb\u4e00\u4e8c\u5206\u8273\u4e3d\uff0c\u5979\u90a3\u53cc\u6c34\u6c6a\u6c6a\u7684\u5927\u773c\u775b\u91cc\uff0c\u6d6e\u52a8\u7740\u6d53 \u6d53\u7684\u60c5\u610f\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u4f38\u624b\u634f\u7740\u5979\u67d4\u8f6f\u65e0\u9aa8\u7684\u5c0f\u624b\uff0c\u5de6\u4e00\u676f\u53f3\u4e00\u676f\uff0c\u4e5f\u4e0d\u7528\u5403\u83dc\u4e86\u3002 \u3000\u3000\u6101\u4e91\u5ffd\u7136\u9053\uff1a\u201c\u90ce\u541b\uff0c\u6211\u4ece\u6765\u4e0d\u559d\u9152\u7684\uff0c\u4eca\u5929\u966a\u90ce\u541b\u559d\u4e86\u4e00\u676f\uff0c\u8eab\u4e0a\u70ed\u5f97\u5f88\uff0c\u5934\u4e5f\u6655\u8d77 \u6765\u4e86\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u5fd9\u653e\u4e0b\u9152\u676f\uff0c\u4f38\u624b\u53bb\u63fd\u5979\u7ea4\u7ec6\u7684\u8170\u8eab\uff0c\u9053\uff1a\u201c\u6211\u6765\u6276\u4f60\u3002\u201d \u3000\u3000\u6101\u4e91\u7ea4\u8170\u4e00\u626d\uff0c\u8eb2\u5f00\u4e86\uff0c\u5403\u5403\u5a07\u7b11\u7740\uff0c\u6162\u6162\u4f38\u51fa\u7ea4\u624b\uff0c\u4e00\u7c92\u4e00\u7c92\u89e3\u5f00\u4e86\u8863\u670d\u4e0a\u7684\u7ebd\u6263\u3002 \u7c89\u7ea2\u8272\u7684\u957f\u886b\u91cc\uff0c\u662f\u7c89\u7ea2\u8272\u7684\u5c0f\u8863\u3002 \u3000\u3000\u5c0f\u8863\u663e\u7136\u592a\u5c0f\u4e86\u4e00\u70b9\uff0c\u4e8e\u662f\u9a6c\u957f\u5c71\u7acb\u523b\u88ab\u6101\u4e91\u4e30\u6ee1\u9999\u6da6\u7684\u80f3\u818a\u3001\u5927\u817f\u548c\u9971\u80c0\u7684\u80f8\u812f\u8ff7\u9189 \u4e86\u3002 \u3000\u3000\u6101\u4e91\u8f7b\u8f7b\u5730\u8d70\u5230\u5e8a\u524d\uff0c\u5a07\u6175\u5730\u659c\u8eba\u4e0b\uff0c\u5a07\u58f0\u9053\uff1a\u201c\u9a6c\u90ce\uff0c\u4f60\u6765\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u518d\u4e5f\u4e0d\u80fd\u6309\u637a\u4e86\uff0c\u98de\u5feb\u5730\u8131\u53bb\u4e86\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u6251\u5230\u4e86\u5e8a\u4e0a\u3002 \u3000\u3000\u6101\u4e91\u5a07\u5598\u7ec6\u7ec6\uff0c\u820c\u5410\u4e01\u9999\u9053\uff1a\u201c\u90ce\u541b\uff0c\u59be\u8eab\u8fd8\u662f\u4e2a\u9ec4\u82b1\u95fa\u5973\uff0c\u4f60\u53ef\u8981\u601c\u60dc\u59be\u8eab\uff0c\u4e0d\u53ef \u592a\u2026\u2026\u201d \u3000\u3000\u4e0b\u9762\u7684\u8bdd\u5979\u5df2\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u9a6c\u957f\u5c71\u5df2\u7ecf\u7528\u706b\u70eb\u7684\u5927\u5634\u5835\u4f4f\u4e86\u5979\u7684\u6a31\u6843\u5c0f\u53e3\u3002 \u3000\u3000\u6101\u4e91\u73b0\u5728\u53ea\u80fd\u7528\u9f3b\u5b50\u53d1\u51fa\u58f0\u97f3\u4e86\u3002 \u3000\u3000\u8fd9\u79cd\u58f0\u97f3\u4ee4\u9a6c\u957f\u5c71\u66f4\u52a0\u75af\u72c2\u3002 \u3000\u3000\u6101\u4e91\u76b1\u7740\u7709\uff0c\u50cf\u662f\u5fcd\u53d7\u7740\u75db\u82e6\uff0c\u5a07\u8eaf\u4e5f\u5728\u98a4\u6296\u7740\uff0c\u8fce\u5408\u7740\uff0c\u4f46\u662f\u5979\u7684\u5de6\u624b\u6162\u6162\u6478\u5230\u4e86\u9a6c \u957f\u5c71\u5149\u6e9c\u6e9c\u7684\u810a\u80cc\u4e0a\uff0c\u98df\u4e2d\u4e8c\u6307\u95f4\uff0c\u5939\u7740\u4e00\u6839\u6781\u7ec6\u7684\u77ed\u9488\u3002 \u3000\u3000\u77ed\u9488\u5728\u706f\u5149\u4e0b\u6cdb\u7740\u5e7d\u5e7d\u7684\u84dd\u5149\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u731b\u70c8\u5730\u52a8\u4f5c\u7740\u3002 \u3000\u3000\u6101\u4e91\u5a07\u547c\u9053\uff1a\u201c\u9a6c\u90ce\uff0c\u9a6c\u90ce\uff0c\u4f60\u771f\u5f3a\u58ee\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5979\u7684\u5de6\u624b\u5df2\u62ac\u8d77\uff0c\u9488\u5c16\u5df2\u5bf9\u7740\u9a6c\u957f\u5c71\u8d77\u4f0f\u7684\u810a\u6881\uff0c\u7528\u529b\u5411\u4e0b\u624e\u53bb\u3002\u773c\u770b\u9a6c\u957f\u5c71\u5c31 \u8981\u6b7b\u4e8e\u8fd9\u4f4d\u6740\u624b\u4e4b\u738b\u624b\u4e0b\u8457\u540d\u7684\u5973\u6740\u624b\u9488\u4e0b\uff0c\u53ea\u542c\u201c\u5657\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u80a1\u4e4c\u5149\u51fb\u7834\u7a97\u7eb8\uff0c\u76f4\u98de \u8fdb\u6765\uff0c\u6b63\u4e2d\u6101\u4e91\u5de6\u624b\u624b\u8155\u7684\u8109\u95e8\u3002 \u3000\u3000\u6101\u4e91\u7acb\u523b\u60e8\u53eb\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u5927\u5403\u4e00\u60ca\uff0c\u7ffb\u8eab\u8df3\u4e86\u8d77\u6765\uff0c\u4fbf\u770b\u89c1\u6101\u4e91\u7684\u624b\u6307\u95ee\u5939\u7740\u7684\u6bd2\u9488\u3002 \u3000\u3000\u4ed6\u4ec0\u4e48\u90fd\u660e\u767d\u4e86\uff0c\u987e\u4e0d\u4e0a\u7a7f\u8863\u670d\uff0c\u6251\u5230\u5899\u8fb9\uff0c\u62bd\u51fa\u4e86\u6302\u5728\u5899\u4e0a\u7684\u5b9d\u5251\u3002 \u3000\u3000\u6101\u4e91\u4e5f\u7ffb\u8eab\u5750\u8d77\uff0c\u63a9\u4e0a\u8863\u895f\u3002 \u3000\u3000\u5979\u7684\u5de6\u624b\u5df2\u88ab\u98de\u8fdb\u7a97\u6765\u7684\u4e00\u679a\u94c1\u83b2\u5b50\u6253\u65ad\u4e86\uff0c\u4f46\u5979\u7684\u53f3\u624b\u8fd8\u80fd\u52a8\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u6012\u543c\u9053\uff1a\u201c\u6211\u6740\u4e86\u4f60\u8fd9\u4e2a\u5c0f\u8d31\u4eba\uff01\u201d \u3000\u3000\u4e00\u5251\u523a\u5411\u6101\u4e91\u3002 \u3000\u3000\u6101\u4e91\u4e00\u626d\u8eab\uff0c\u8eb2\u5f00\u4e86\u8fd9\u4e00\u5251\uff0c\u53f3\u624b\u5728\u540e\u8170\u5904\u4e00\u63a2\uff0c\u624b\u4e2d\u5df2\u591a\u51fa\u4e86\u4e00\u67c4\u7cbe\u5149\u56db\u5c04\u7684\u77ed\u5200\u3002 \u3000\u3000\u5979\u6325\u52a8\u77ed\u5200\uff0c\u5411\u9a6c\u957f\u5c71\u76f4\u6251\u8fc7\u53bb\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u54ea\u91cc\u662f\u5979\u7684\u5bf9\u624b\uff0c\u4e0d\u8fc7\u4e09\u62db\uff0c\u5df2\u88ab\u903c\u5f97\u9669\u50cf\u73af\u751f\uff0c\u76f4\u9000\u5230\u4e86\u5899\u8fb9\u3002 \u3000\u3000\u6101\u4e91\u5389\u58f0\u9053\uff1a\u201c\u6211\u6740\u4e86\u4f60\uff0c\u81ea\u5df1\u4e5f\u6d3b\u4e0d\u6210\u4e86\uff0c\u56e0\u4e3a\u5916\u9762\u663e\u7136\u6709\u9ad8\u4eba\u6765\u6551\u4f60\u4e86\u3002\u9a6c\u957f\u5c71\uff0c \u8fd8\u662f\u8ba9\u4f60\u5360\u4e86\u4fbf\u5b9c\uff0c\u4e34\u6b7b\u524d\u8fd8\u98ce\u6d41\u5feb\u6d3b\u4e86\u4e00\u4e0b\uff0c\u6b7b\u540e\u8fd8\u6709\u6211\u8fd9\u6837\u7f8e\u4e3d\u7684\u5973\u4eba\u966a\u7740\u4f60\uff01\u201d \u3000\u3000\u9a6c\u957f\u5c71\u957f\u5251\u76f4\u523a\uff0c\u53ef\u6101\u4e91\u4e00\u6325\u77ed\u5200\uff0c\u5df2\u5c06\u4ed6\u7684\u864e\u53e3\u9707\u9ebb\uff0c\u5b9d\u5251\u201c\u5f53\u5577\u201d\u4e00\u58f0\u843d\u5728\u4e86\u5730\u4e0a\u3002 \u3000\u3000\u770b\u6837\u5b50\u9664\u4e86\u95ed\u76ee\u7b49\u6b7b\u4e4b\u5916\uff0c\u4ed6\u5df2\u6ca1\u6709\u522b\u7684\u529e\u6cd5\u53ef\u60f3\u3002 \u3000\u3000\u6101\u4e91\u4e3e\u8d77\u4e86\u77ed\u5200\u3002 \u3000\u3000\u77ed\u5200\u5411\u9a6c\u957f\u5c71\u731b\u523a\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u957f\u53f9\u4e00\u58f0\uff0c\u675f\u624b\u5f85\u6bd9\u3002 \u3000\u3000\u7a81\u7136\uff0c\u6101\u4e91\u7684\u624b\u505c\u5728\u534a\u7a7a\u4e2d\u3002 \u3000\u3000\u5979\u7684\u4e24\u773c\u4e00\u4e0b\u7a81\u4e86\u51fa\u6765\uff0c\u5634\u91cc\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u55b7\u4e86\u9a6c\u957f\u5c71\u4e00\u8eab\u3002 \u3000\u3000\u7136\u540e\uff0c\u5979\u7684\u8eab\u4f53\u5c31\u8f6f\u8f6f\u5730\u4f8b\u5728\u4e86\u5730\u4e0a\u3002 \u3000\u3000\u5979\u6b7b\u4e86\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u8fd9\u624d\u770b\u89c1\u6101\u4e91\u8eab\u540e\u4e0d\u77e5\u4f55\u65f6\u591a\u4e86\u4e00\u6761\u94c1\u5854\u822c\u7684\u58ee\u6c49\u3002 \u3000\u3000\u4ed6\u60ca\u9b42\u672a\u5b9a\u5730\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u58ee\u6c49\u9053\uff1a\u201c\u6211\u53eb\u963f\u6a2a\uff0c\u7a0b\u603b\u6355\u5934\u8ba9\u6211\u548c\u963f\u4fdd\u6f5c\u4f0f\u5728\u5927\u4eba\u7684\u9644\u8fd1\uff0c\u5168\u529b\u5730\u4fdd\u62a4\u5927\u4eba\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u5598\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u548c\u963f\u4fdd\u90fd\u662f\u5211\u90e8\u7684\u6355\u5934\uff1f\u201d \u3000\u3000\u963f\u6a2a\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u4ed6\u4f38\u624b\u626f\u8fc7\u9a6c\u957f\u5c71\u7684\u8863\u670d\uff0c\u6254\u7ed9\u4ed6\uff0c\u9053\uff1a\u201c\u9a6c\u5927\u4eba\uff0c\u5148\u7a7f\u4e0a\u8863\u670d\u5427\uff0c\u6211\u4eec\u8fd8\u6709\u91cd\u8981\u7684\u4e8b \u60c5\u8981\u505a\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u4e00\u8fb9\u7a7f\u8863\uff0c\u4e00\u8fb9\u9053\uff1a\u201c\u963f\u6a2a\u5144\u5f1f\uff0c\u963f\u4fdd\u5144\u5f1f\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u963f\u6a2a\u9053\uff1a\u201c\u5927\u4eba\u8bf7\u542c\u5916\u9762\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u8fd9\u624d\u542c\u89c1\u5916\u9762\u6b63\u54cd\u7740\u6fc0\u70c8\u7684\u94c1\u5668\u76f8\u649e\u7684\u58f0\u97f3\u3002 \u3000\u3000\u963f\u6a2a\u9053\uff1a\u201c\u6740\u624b\u4e00\u5171\u6709\u4e94\u4eba\uff0c\u8fd9\u540d\u53eb\u6101\u4e91\u7684\uff0c\u662f\u6740\u624b\u4e4b\u571f\u8840\u624b\u65b9\u8f6e\u4e4e\u4e0b\u7684\u8457\u540d\u5973\u6740\u624b\u3002 \u53e6\u5916\u56db\u4eba\u5219\u7ad9\u5728\u95e8\u5916\uff0c\u663e\u7136\u662f\u9632\u6b62\u5927\u4eba\u7684\u90e8\u4e0b\u542c\u89c1\u5927\u4eba\u7684\u547c\u6551\uff0c\u8d76\u6765\u6551\u63f4\u7684\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u5403\u60ca\u9053\uff1a\u201c\u5916\u9762\u8fd8\u6709\u56db\u4e2a\u4eba\uff1f\u963f\u4fdd\u4e00\u4e2a\u4eba\u80fd\u884c\u5417\uff1f\u4f60\u5feb\u53bb\u5e2e\u52a9\u4ed6\u5427\u3002\u201d \u3000\u3000\u963f\u6a2a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5982\u679c\u628a\u4ed6\u56db\u4e2a\u4eba\u5168\u90fd\u6d3b\u6349\uff0c\u7684\u786e\u5f88\u56f0\u96be\uff0c\u4f46\u6211\u5df2\u7ecf\u4e0e\u963f\u4fdd\u5546\u91cf\u597d\u4e86\uff0c \u53ea\u8981\u4e00\u4e2a\u6d3b\u53e3\uff0c\u90a3\u4e48\uff0c\u963f\u4fdd\u4e00\u4e2a\u4eba\u80af\u5b9a\u80fd\u5bf9\u4ed8\u5f97\u4e86\u3002\u201d \u3000\u3000\u5ffd\u542c\u51e0\u58f0\u60e8\u547c\uff0c\u7136\u540e\u662f\u963f\u4fdd\u7684\u58f0\u97f3\u9053\uff1a\u201c\u9a6c\u5927\u4eba\uff0c\u963f\u6a2a\uff0c\u4f60\u4eec\u53ef\u4ee5\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u963f\u4fdd\u7ad9\u5728\u5ead\u9662\u4e2d\uff0c\u8eab\u8fb9\u8eba\u5012\u7740\u4e09\u540d\u9ed1\u8863\u8499\u9762\u6740\u624b\uff0c\u4ed6\u624b\u4e2d\u8fd8\u6a2a\u63d0\u7740\u4e00\u4e2a\u3002\u8fd9\u540d\u6740\u624b\u8fd8\u5728 \u547b\u541f\u3002 \u3000\u3000\u963f\u6a2a\u9053\uff1a\u201c\u7559\u4e0b\u4e00\u4e2a\u6d3b\u53e3\u5c31\u597d\u529e\u591a\u4e86\uff0c\u963f\u4fdd\uff0c\u4f60\u8981\u5f53\u5fc3\u4ed6\u54ac\u7834\u53e3\u4e2d\u7684\u6bd2\u836f\u4e38\u81ea\u6740\u3002\u201d \u3000\u3000\u963f\u4fdd\u7b11\u9053\uff1a\u201c\u963f\u6a2a\uff0c\u4f60\u653e\u5fc3\u5427\uff0c\u6211\u5df2\u7ecf\u628a\u4ed6\u5634\u91cc\u7684\u836f\u4e38\u6316\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u644a\u5f00\u624b\u638c\u4eae\u4e86\u4e00\u4eae\u3002 \u3000\u3000\u4ed6\u624b\u5fc3\u91cc\u679c\u7136\u6709\u4e00\u5c0f\u9897\u6731\u7ea2\u8272\u7684\u836f\u4e38\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u5feb\u95ee\u95ee\u662f\u8c01\u6d3e\u4ed6\u4eec\u6765\u7684\u3002\u201d \u3000\u3000\u963f\u4fdd\u9053\uff1a\u201c\u9a6c\u5927\u4eba\uff0c\u4e0d\u7528\u7740\u6025\uff0c\u8fd8\u662f\u5148\u95ee\u95ee\u90a3\u4e2a\u5973\u5b50\u662f\u4ece\u54ea\u4e00\u5bb6\u627e\u6765\u7684\u5427\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5bf9\u5bf9\uff0c\u6211\u600e\u4e48\u6ca1\u60f3\u8d77\u6765\uff0c\u771f\u662f\u60f3\u4e0d\u5230\uff0c\u6211\u7684\u90e8\u5c06\u4e2d\u7adf\u7136\u4e5f\u6709\u5978\u7ec6\uff01\u201d \u3000\u3000\u963f\u6a2a\u9053\uff1a\u201c\u9a6c\u5927\u4eba\uff0c\u4f60\u7684\u90e8\u5c06\u4e2d\u6ca1\u6709\u5978\u7ec6\uff0c\u6211\u4eec\u5df2\u7ecf\u6697\u4e2d\u8c03\u67e5\u8fc7\u4e86\uff0c\u4ed6\u4eec\u53ea\u662f\u4e2d\u4e86\u522b\u4eba \u7684\u5708\u5957\u3002\u201d \u3000\u3000\u76f4\u5230\u8fd9\u65f6\uff0c\u624d\u542c\u89c1\u4e00\u9635\u4e71\u54c4\u54c4\u7684\u811a\u6b65\u58f0\uff0c\u51e0\u5458\u90e8\u5c06\u8863\u5e3d\u4e0d\u6574\uff0c\u795e\u8272\u614c\u5f20\u5730\u8dd1\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u6012\u9053\uff1a\u201c\u770b\u770b\u4f60\u4eec\u8fd9\u4e2a\u6837\u5b50\uff01\u4e8f\u5f85\u8001\u5b50\u8fd9\u4e9b\u5e74\u4e00\u76f4\u5728\u7cbe\u5fc3\u8c03\u7406\u4f60\u4eec\uff01\u8981\u7b49\u4f60\u4eec\u6765 \u6551\uff0c\u8001\u5b50\u8fd8\u4e0d\u65e9\u5c31\u4e00\u547d\u5f52\u897f\u4e86\uff01\u201d \u3000\u3000\u90e8\u5c06\u4eec\u770b\u7740\u8eba\u5728\u5730\u4e0a\u7684\u6740\u624b\u4eec\uff0c\u4e00\u4e2a\u4e2a\u4f4e\u5934\u4e0d\u8bed\uff0c\u5664\u82e5\u5bd2\u8749\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u867d\u7136\u662f\u4e2a\u884c\u4f0d\u4e4b\u4eba\uff0c\u4f46\u5e73\u65f6\u6781\u5c11\u8bf4\u7c97\u8bdd\uff0c\u73b0\u5728\u4ed6\u6ee1\u53e3\u81ea\u79f0\u201c\u8001\u5b50\u201d\uff0c\u60f3\u662f\u5fc3\u4e2d\u5df2 \u6124\u6012\u5230\u4e86\u6781\u70b9\u4e86\u3002 \u3000\u3000\u963f\u6a2a\u9053\uff1a\u201c\u9a6c\u5927\u4eba\uff0c\u4e0d\u8981\u9a82\u4ed6\u4eec\u4e86\uff0c\u6211\u4eec\u8d76\u5feb\u53bb\u770b\u770b\u81ea\u79f0\u662f\u8fd9\u5973\u6740\u624b\u7236\u6bcd\u7684\u4eba\u5427\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u4e00\u62cd\u989d\u5934\uff0c\u9053\uff1a\u201c\u5bf9\u5bf9\u5bf9\uff01\u4ed6\u4eec\u53ef\u662f\u4e00\u6761\u91cd\u8981\u7684\u7ebf\u7d22\uff01\u4f60\u4eec\u51e0\u4e2a\uff0c\u8fd8\u4e0d\u70b9\u9f50\u4eba\u9a6c\uff0c \u5e26\u6211\u4eec\u53bb\u627e\u4ed6\u4eec\uff01\u201d \u3000\u3000\u963f\u4fdd\u9053\uff1a\u201c\u5927\u4eba\uff0c\u53ea\u8981\u5e26\u4e8c\u5341\u540d\u4eb2\u5175\u5373\u53ef\uff0c\u4eba\u591a\u4e86\uff0c\u884c\u52a8\u53cd\u800c\u4e0d\u4fbf\uff0c\u5982\u6b64\u6df1\u591c\uff0c\u60ca\u5fe7\u4e86 \u767e\u59d3\u4e5f\u4e0d\u592a\u597d\uff01\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u4e0d\u9519\u4e0d\u9519\uff01\u4f60\u4eec\u770b\uff0c\u8001\u5b50\u90fd\u88ab\u4f60\u4eec\u6c14\u7cca\u6d82\u4e86\uff01\u8fd8\u6123\u7740\u5e72\u4ec0\u4e48\uff0c\u8d70\u54c7\uff01\u201d \u3000\u3000\u4e00\u884c\u4eba\u5728\u90e8\u5c06\u4eec\u7684\u6307\u5f15\u4e0b\uff0c\u98de\u5feb\u5730\u6740\u5230\u5973\u6740\u624b\u7684\u201c\u5bb6\u201d\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u5e62\u5f88\u5c0f\u7684\u6c11\u5b85\uff0c\u5927\u95e8\u7d27\u95ed\uff0c\u5c4b\u91cc\u4e00\u70b9\u706f\u5149\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u963f\u6a2a\u9053\uff1a\u201c\u963f\u4fdd\uff0c\u4f60\u6ce8\u610f\u4fdd\u62a4\u9a6c\u5927\u4eba\uff0c\u6211\u5148\u8fdb\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u4e00\u95ea\u8eab\u5230\u4e86\u95e8\u524d\uff0c\u4e3e\u624b\u8f7b\u8f7b\u4e00\u63a8\uff0c\u5927\u95e8\u5e94\u624b\u800c\u5f00\u3002 \u3000\u3000\u963f\u6a2a\u98de\u5feb\u5730\u95ea\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\uff0c\u4ed6\u4e3e\u7740\u4e00\u76cf\u6cb9\u706f\u56de\u5230\u95e8\u8fb9\uff0c\u9053\uff1a\u201c\u5927\u4eba\uff0c\u4f60\u4eec\u8fdb\u6765\u5427\u3002\u201d \u3000\u3000\u4e00\u8fdb\u95e8\uff0c\u5c31\u95fb\u5230\u5c4b\u91cc\u6709\u4e00\u80a1\u6d53\u6d53\u7684\u8840\u8165\u5473\u3002 \u3000\u3000\u5730\u4e0a\u8eba\u7740\u4e00\u7537\u4e00\u5973\u4e24\u4e2a\u4e2d\u5e74\u4eba\u3002 \u3000\u3000\u90e8\u5c06\u9053\uff1a\u201c\u5c31\u662f\u4ed6\u4eec\uff0c\u5c31\u662f\u4ed6\u4eec\u8bf4\u613f\u610f\u628a\u5973\u513f\u7ed9\u5927\u4eba\u505a\u5c0f\u59be\u7684\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u4ed4\u7ec6\u770b\u4e86\u4e00\u773c\uff0c\u5931\u671b\u5730\u9053\uff1a\u201c\u6b7b\u4e86\uff01\u8fd9\u4e0b\u5b8c\u5566\uff0c\u7ebf\u7d22\u53c8\u65ad\u4e86\uff01\u963f\u6a2a\u3001\u963f\u4fdd\uff0c\u4f60\u4eec \u8bf4\u73b0\u5728\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u963f\u6a2a\u9053\uff1a\u201c\u5927\u4eba\uff0c\u54b1\u4eec\u624b\u4e2d\u8fd8\u6709\u4e00\u4e2a\u6d3b\u53e3\uff0c\u8fd9\u6837\u5427\uff0c\u8bf8\u5927\u4eba\u8ddf\u6211\u4eec\u4e00\u8d77\u53bb\u5e9c\u8859\uff0c\u591c\u5ba1\u6b64 \u4eba\uff0c\u770b\u80fd\u5426\u4ece\u4ed6\u53e3\u4e2d\u95ee\u51fa\u4e9b\u7ebf\u7d22\u6765\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u4e5f\u53ea\u597d\u5982\u6b64\u4e86\u3002\u201d \u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1 \u3000\u3000\u6740\u624b\u96c6\u56e2\u884c\u52a8\u5982\u6b64\u8fc5\u901f\uff0c\u8ba1\u5212\u5982\u6b64\u5468\u5bc6\uff0c\u5b9e\u5728\u8ba9\u7a0b\u5c0f\u8776\u548c\u56db\u5927\u6355\u5934\u5927\u4e3a\u5403\u60ca\u3002\u738b\u5c11\u537f\u9053\uff1a \u201c\u4e8f\u5f97\u603b\u6355\u5934\u65e9\u6709\u9632\u5907\uff0c\u4e0d\u7136\uff0c\u9a6c\u957f\u5c71\u5148\u5371\u9669\u4e86\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u60ed\u6127\u5730\u9053\uff1a\u201c\u81ea\u656c\u6587\u6b7b\u540e\uff0c\u6211\u4eec\u5e38\u6025\u5207\u5730\u60f3\u518d\u8981\u4e00\u4e2a\u513f\u5b50\uff0c\u4e0d\u60f3\uff0c\u6b63\u597d\u88ab\u6740\u624b\u4eec \u94bb\u4e86\u8fd9\u4e2a\u7a7a\u5b50\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u770b\u7740\u8dea\u5728\u5730\u4e0a\u7684\u8499\u9762\u6740\u624b\uff0c\u5bf9\u963f\u4fdd\u9053\uff1a\u201c\u963f\u4fdd\uff0c\u5e72\u5f97\u597d\uff01\u73b0\u5728\uff0c\u8ba9\u6211\u4eec\u770b\u770b\u4ed6\u7684 \u771f\u9762\u76ee\u5427\uff01\u201d \u3000\u3000\u8499\u9762\u5e03\u4e00\u63ed\u5f00\uff0c\u539f\u6765\u662f\u4e00\u4e2a\u7626\u524a\u7684\u4e2d\u5e74\u4eba\u3002 \u3000\u3000\u738b\u5c11\u537f\u4ed4\u7ec6\u770b\u7740\u4ed6\uff0c\u76b1\u8d77\u4e86\u7709\u5934\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u8fd9\u4eba\u6709\u4e9b\u9762\u719f\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u662f\u8c01\u6d3e\u4f60\u4eec\u4eba\u523a\u6740\u9a6c\u5927\u4eba\u7684\uff1f\u201d \u3000\u3000\u6740\u624b\u4e0d\u56de\u7b54\u3002 \u3000\u3000\u963f\u6a2a\u4e00\u811a\u8e22\u53bb\uff0c\u5c06\u4ed6\u8e22\u5012\u5728\u5730\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\u95ee\u4f60\u8bdd\u5462\uff01\u5feb\u56de\u7b54\u3002\u201d \u3000\u3000\u6740\u624b\u4e0d\u7406\u4ed6\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u51b7\u9053\uff1a\u201c\u4ed6\u9aa8\u5934\u8fd8\u633a\u786c\uff01\u201d \u3000\u3000\u963f\u6a2a\u9053\uff1a\u201c\u770b\u662f\u4ed6\u9aa8\u5934\u786c\uff0c\u8fd8\u662f\u6211\u963f\u6a2a\u7684\u624b\u6bb5\u72e0\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u5728\u6740\u624b\u8eab\u8fb9\u8e72\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u5144\uff0c\u6211\u77e5\u9053\u4f60\u4e0d\u6015\u6b7b\uff0c\u56e0\u4e3a\u4f60\u53e3\u4e2d\u5df2\u51c6\u5907 \u597d\u4e86\u6bd2\u836f\uff0c\u53ea\u53ef\u60dc\u73b0\u5728\u6bd2\u836f\u5df2\u88ab\u963f\u4fdd\u53d6\u51fa\uff0c\u4f60\u60f3\u6b7b\u4e5f\u6b7b\u4e0d\u4e86\uff01\u201d\u6740\u624b\u5e72\u8106\u95ed\u4e0a\u4e86\u773c\u775b\u3002 \u3000\u3000\u770b\u4ed6\u7684\u6837\u5b50\uff0c\u5bf9\u963f\u6a2a\u5f88\u662f\u6709\u70b9\u4e0d\u5c51\u4e00\u987e\u3002 \u3000\u3000\u963f\u6a2a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8001\u5144\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u6b7b\uff0c\u662f\u5f88\u75db\u5feb\u7684\uff0c\u60f3\u6b7b\u6b7b\u4e0d\u4e86\uff0c\u5374\u8981\u6d3b\u53d7\u7f6a\uff0c \u90a3\u5c31\u60e8\u4e86\uff01\u4f60\u8001\u5b9e\u5730\u8bf4\u51fa\u5b9e\u60c5\uff0c\u603b\u6355\u5934\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u653e\u4f60\u4e00\u6761\u751f\u8def\uff01\u201d\u6740\u624b\u50cf\u662f\u804b\u4e86\u4e00\u822c\uff0c\u5bf9 \u4ed6\u7684\u8bdd\u7adf\u662f\u5145\u8033\u4e0d\u95fb\u3002 \u3000\u3000\u963f\u6a2a\u9053\uff1a\u201c\u597d\uff0c\u8001\u5144\uff0c\u4f60\u4e00\u5b9a\u8981\u8bd5\u4e00\u8bd5\u6211\u963f\u6a2a\u7684\u4efd\u91cf\uff0c\u6211\u53ef\u5c31\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d\u8bf4\u7740\uff0c\u4ed6\u4f38 \u51fa\u53f3\u624b\u98df\u4e2d\u4e8c\u6307\uff0c\u5939\u4f4f\u6740\u624b\u7684\u4e00\u6839\u624b\u6307\uff0c\u6162\u6162\u5730\u7528\u529b\u4e00\u5939\u3002 \u3000\u3000\u6740\u624b\u4e24\u773c\u7ffb\u767d\uff0c\u6740\u732a\u822c\u568e\u53eb\u8d77\u6765\u3002 \u3000\u3000\u96be\u602a\u4ed6\u4f1a\u53eb\u558a\uff0c\u5341\u6307\u8fde\u5fc3\u75db\u554a\u3002 \u3000\u3000\u963f\u6a2a\u7b11\u7b11\u9053\uff1a\u201c\u600e\u4e48\u6837\uff0c\u8bf4\u4e0d\u8bf4\uff1f\u201d \u3000\u3000\u6740\u624b\u5598\u606f\u7740\u9053\uff1a\u201c\u4f60\u6740\u4e86\u6211\u5427\uff01\u201d \u3000\u3000\u963f\u6a2a\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u6740\u4f60\uff1f\u201d \u3000\u3000\u6740\u624b\u773c\u5df4\u5df4\u5730\u770b\u7740\u4ed6\uff0c\u6073\u6c42\u9053\uff1a\u201c\u4f60\u6740\u4e86\u6211\uff0c\u6211\u4f1a\u611f\u6fc0\u4f60\u7684\u3002\u201d \u3000\u3000\u963f\u6a2a\u9053\uff1a\u201c\u6211\u4e0d\u8981\u4f60\u611f\u6fc0\uff0c\u53ea\u8981\u4f60\u8bf4\u51fa\u771f\u60c5\u6765\uff01\u201d \u3000\u3000\u6740\u624b\u5598\u606f\u7740\uff0c\u773c\u5149\u95ea\u70c1\u4e0d\u5b9a\uff0c\u663e\u7136\u662f\u5728\u8003\u8651\u662f\u62db\u8fd8\u662f\u786c\u633a\u4e0b\u53bb\u3002 \u3000\u3000\u738b\u5c11\u537f\u5ffd\u7136\u9053\uff1a\u201c\u957f\u5c71\u5144\uff0c\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\u6848\u53d1\u5f53\u5929\uff0c\u4f60\u5e26\u5175\u53bb\u6293\u91d1\u5c0f\u7709\uff0c\u91d1\u767e\u5e74\u4e0d\u8ba9\u4f60 \u8fdb\u53bb\uff0c\u5f53\u65f6\uff0c\u91d1\u767e\u5e74\u8eab\u8fb9\u6709\u56db\u4e2a\u4f7f\u86c7\u6756\u7684\u4eba\u2026\u2026\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u5bf9\u5bf9\u5bf9\uff01\u738b\u5927\u4eba\uff0c\u4f60\u8fd9\u4e00\u8bf4\uff0c\u6211\u53ef\u60f3\u8d77\u6765\u4e86\uff0c\u8fd9\u4e2a\u6740\u624b\u6b63\u662f\u90a3\u56db\u540d\u4f7f\u86c7\u6756 \u7684\u4eba\u4e4b\u4e2d\u7684\u4e00\u4e2a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u771f\u662f\u91d1\u5e9c\u7684\u4eba\uff0c\u8bf4\uff0c\u662f\u4e0d\u662f\u91d1\u767e\u5e74\u8ba9\u4f60\u53bb\u8c0b\u5bb3\u9a6c\u5927\u4eba\u7684\uff01\u201d \u3000\u3000\u6740\u624b\u7684\u8138\u8272\u987f\u65f6\u53d8\u4e86\uff0c\u4f0f\u5728\u5730\u4e0a\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u8bf7\u4f60\u653e\u6211\u4e00\u9a6c\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8bf4\u5b9e\u8bdd\uff0c\u6211\u5c31\u9976\u4f60\u4e0d\u6b7b\uff01\u201d \u3000\u3000\u6740\u624b\u9053\uff1a\u201c\u597d\uff0c\u6211\u8bf4\uff0c\u662f\u5929\u67ab\u9053\u957f\u8ba9\u6211\u4eec\u53bb\u6740\u9a6c\u5927\u4eba\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u539f\u6765\u662f\u4ed6\uff01\u91d1\u767e\u5e74\u77e5\u9053\u5929\u67ab\u4e0e\u8840\u624b\u65b9\u8f6e\u7684\u5173\u7cfb\u5417\uff1f\u201d \u3000\u3000\u6740\u624b\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u6211\u4eec\u56db\u4eba\u662f\u53d7\u8840\u624b\u65b9\u8f6e\u4e4b\u547d\uff0c\u53bb\u91d1\u5e9c\u5367\u5e95\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u65b9\u8f6e\u4e5f\u8981\u5bf9\u91d1\u767e\u5e74\u4e0b\u624b\u5417\uff1f\u201d \u3000\u3000\u6740\u624b\u9053\uff1a\u201c\u626c\u5dde\u5341\u5927\u8c6a\u5bcc\u95e8\u4e2d\uff0c\u90fd\u6709\u6211\u4eec\u7684\u4eba\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u5341\u5927\u8c6a\u5bcc\u90fd\u662f\u65b9\u8f6e\u523a\u6740\u7684\u76ee\u6807\u55bd\uff1f\u636e\u6211\u6240\u77e5\uff0c\u65b9\u8f6e\u5982\u6709\u884c\u52a8\uff0c \u4e00\u5b9a\u662f\u6709\u4eba\u82b1\u94b1\u8bf7\u4ed6\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u96c7\u4e3b\u662f\u8c01\uff1f\u201d \u3000\u3000\u6740\u624b\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u6211\u4eec\u53ea\u662f\u5949\u547d\u884c\u4e8b\uff0c\u54ea\u6562\u591a\u95ee\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u4e86\u70b9\u5934\uff0c\u53c8\u9053\uff1a\u201c\u5929\u67ab\u8bf4\u6ca1\u8bf4\u4e3a\u4ec0\u4e48\u8981\u6740\u9a6c\u5927\u4eba\uff1f\u201d \u3000\u3000\u6740\u624b\u9053\uff1a\u201c\u6ca1\u6709\u8bf4\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u773c\u4e2d\u95ea\u52a8\u7740\u806a\u6167\u7684\u5149\uff0c\u7a81\u7136\u95ee\u9053\uff1a\u201c\u90a3\u5929\u5728\u67f3\u5824\u4e4b\u4e0a\u523a\u6740\u738b\u5927\u4eba\u7684\uff0c\u662f\u4e0d\u662f\u5929\u67ab \u9053\u957f\uff1f\u201d \u3000\u3000\u6740\u624b\u4e00\u6014\uff0c\u8138\u4e0a\u9732\u51fa\u94a6\u4f69\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u6b63\u662f\u4ed6\u672c\u4eba\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u975e\u5e38\u5403\u60ca\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u6709\u6211\u7684\u6d88\u606f\u6765\u6e90\u3002\u201d\u8bf4\u7740\uff0c\u5979\u8f6c\u5411\u6740\u624b\uff0c\u9053\uff1a\u201c\u4f60\u6562\u4e0e\u5929\u67ab\u9053 \u957f\u5f53\u9762\u5bf9\u8d28\u5417\uff1f\u201d \u3000\u3000\u6740\u624b\u9053\uff1a\u201c\u5f53\u7136\u6562\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\uff01\u738b\u5927\u4eba\uff0c\u9a6c\u5927\u4eba\uff0c\u6211\u4eec\u73b0\u5728\u5c31\u53bb\u91d1\u5e9c\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u6df1\u591c\u53bb\u91d1\u5e9c\uff0c\u662f\u5426\u4e0d\u592a\u597d\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u7b49\u5929\u67ab\u53d1\u89c9\u6740\u624b\u4eec\u6ca1\u6709\u5f97\u624b\uff0c\u4e00\u5b9a\u4f1a\u505a\u51fa\u76f8\u5e94\u7684\u5bf9\u7b56\u6765\uff01\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u9700\u8981\u9a6c\u67d0\u8c03\u96c6\u4e00\u961f\u7cbe\u5175\u6765\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u7528\u5982\u6b64\u52b3\u5e08\u52a8\u4f17\uff0c\u6709\u5c0f\u6587\u3001\u5c0f\u96c5\u3001\u60dc\u7389\u548c\u56db\u5927\u6355\u5934\uff0c\u518d\u5e26\u4e0a\u6c5f\u5357\u3001\u4e2d\u5dde \u4e24\u5927\u5206\u53f8\u4e8c\u4e09\u5341\u540d\u597d\u624b\uff0c\u4eba\u624b\u7edd\u5bf9\u5df2\u8db3\u591f\u3002\u963f\u6a2a\u3001\u963f\u4fdd\u548c\u9a6c\u5927\u4eba\u624b\u4e0b\u7684\u5c06\u5b98\uff0c\u4eb2\u5175\u4ee5\u53ca\u6797\u53f8 \u6848\uff0c\u5c31\u7559\u5728\u5e9c\u8859\u4fdd\u62a4\u4e8c\u4f4d\u5927\u4eba\u5427\u3002\u201d \u3000\u3000\u60f3\u4e86\u60f3\uff0c\u53c8\u9053\uff1a\u201c\u60dc\u7389\u8fd8\u662f\u7559\u4e0b\u5427\uff0c\u4f60\u966a\u7740\u91d1\u5c0f\u7709\uff0c\u770b\u770b\u5979\u80fd\u4e0d\u80fd\u60f3\u8d77\u6709\u5173\u5929\u67ab\u9053\u957f\u7684 \u4e00\u4e9b\u4e8b\u60c5\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u8fd8\u662f\u7b49\u5929\u4eae\u4e86\u518d\u53bb\u5427\uff0c\u4ece\u5e9c\u8859\u5230\u91d1\u5e9c\uff0c\u4e00\u8def\u4e4b\u4e0a\u6709\u5f88\u591a\u50fb\u9759\u4e4b\u5904\uff0c \u5982\u679c\u6740\u624b\u4eec\u6709\u57cb\u4f0f\uff0c\u5c31\u574f\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u738b\u5927\u4eba\u653e\u5fc3\uff0c\u4e00\u6765\u6740\u624b\u4eec\u4e0d\u4f1a\u60f3\u5230\u6211\u4eec\u7684\u884c\u52a8\u4f1a\u8fd9\u6837\u5feb\uff0c\u4e8c\u6765\uff0c\u4ed6\u4eec\u4e5f\u4e0d \u77e5\u9053\u6211\u4eec\u624b\u4e2d\u6709\u4e00\u4e2a\u6d3b\u53e3\u3002\u5982\u679c\u7b49\u5230\u5929\u4eae\uff0c\u6740\u624b\u4eec\u5f97\u5230\u4e86\u786e\u5207\u7684\u6d88\u606f\uff0c\u4e8b\u60c5\u53cd\u800c\u96be\u529e\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u611f\u52a8\u5730\u9053\uff1a\u201c\u603b\u6355\u5934\u4e3a\u4e86\u7834\u6848\uff0c\u4e0d\u987e\u4e2a\u4eba\u5b89\u5371\uff0c\u771f\u8ba9\u738b\u67d0\u94a6\u4f69\u3002\u201d\u7a0b\u5c0f\u8776\u4e00\u7b11\uff0c \u9053\uff1a\u201c\u738b\u5927\u4eba\u8fc7\u5956\u4e86\u3002\u201d \u3000\u3000\u5979\u7ad9\u8d77\u8eab\u6765\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8d70\u5427\u3002\u201d \u3000\u3000\u53c8\u5bf9\u56db\u5927\u6355\u5934\u9053\uff1a\u201c\u6d3e\u4e24\u4e2a\u4eba\u627e\u4e00\u4f4d\u5c0f\u8f7f\uff0c\u5c06\u8fd9\u4e2a\u6d3b\u53e3\u62ac\u5728\u8f7f\u5185\uff0c\u4e00\u8def\u4e4b\u4e0a\u53ef\u4e0d\u80fd\u8ba9\u6740 \u624b\u4eec\u7684\u773c\u7ebf\u53d1\u73b0\u4e86\u4ed6\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u662f\uff0c\u5c5e\u4e0b\u8fd9\u5c31\u53bb\u529e\uff01\u201d \u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1\u3000\u3000\u3000\u3000\u3000\u3000\u25a1\u25a1 \u3000\u3000\u91d1\u767e\u5e74\u5927\u6982\u662f\u505a\u68a6\u4e5f\u6ca1\u6709\u60f3\u5230\uff0c\u5211\u90e8\u603b\u6355\u5934\u548c\u540d\u9707\u5929\u4e0b\u7684\u56db\u65b9\u540d\u6355\u4f1a\u5728\u6df1\u591c\u91cc\u5bf9\u4ed6\u8fdb\u884c \u767b\u95e8\u9020\u8bbf\u3002 \u3000\u3000\u5c24\u5176\u8ba9\u91d1\u767e\u5e74\u5927\u611f\u5947\u602a\u7684\u662f\uff0c\u603b\u6355\u5934\u4e00\u884c\u4eba\u7adf\u7136\u8fd8\u5e26\u6765\u4e86\u4e00\u4e58\u5c0f\u8f7f\u3002 \u3000\u3000\u4e00\u5f00\u59cb\uff0c\u91d1\u767e\u5e74\u8fd8\u4ee5\u4e3a\u8f7f\u4e2d\u4e4b\u4eba\u4f1a\u662f\u91d1\u5c0f\u7709\uff0c\u603b\u6355\u5934\u4e00\u884c\u4eba\u5b9a\u662f\u9001\u4ed6\u5973\u513f\u56de\u6765\u4e86\u3002 \u3000\u3000\u4f46\u5f88\u5feb\uff0c\u4ed6\u5c31\u660e\u767d\u4e86\u4e0d\u662f\u8fd9\u6837\u4e00\u56de\u4e8b\u3002 \u3000\u3000\u56e0\u4e3a\u5982\u679c\u8f7f\u91cc\u5750\u7684\u662f\u91d1\u5c0f\u7709\uff0c\u4e00\u770b\u662f\u4ed6\uff0c\u53ea\u6015\u65e9\u4f1a\u5927\u53eb\u4e00\u58f0\u201c\u7239\u7239\u201d\uff0c\u6251\u8fdb\u4ed6\u7684\u6000\u4e2d\u6765 \u4e86\u3002 \u3000\u3000\u65e2\u7136\u4ed6\u4eec\u4e0d\u662f\u9001\u91d1\u5c0f\u7709\u56de\u91d1\u5e9c\uff0c\u5728\u8fd9\u6f06\u9ed1\u7684\u6df1\u591c\u91cc\uff0c\u4ece\u5e9c\u8859\u6765\u91d1\u5e9c\u5e72\u4ec0\u4e48\u5462\uff1f\u91d1\u767e\u5e74\u5fc3 \u91cc\u6b63\u60f3\u7740\uff0c\u7a0b\u5c0f\u8776\u5df2\u7ecf\u5148\u5f00\u53e3\u4e86\uff0c\u9053\uff1a\u201c\u6211\u7b49\u6df1\u591c\u9020\u8bbf\uff0c\u91d1\u8001\u662f\u5426\u5f88\u662f\u610f\u5916\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u5f88\u610f\u5916\uff0c\u4f46\u603b\u6355\u5934\u5927\u9a7e\u5149\u4e34\uff0c\u5bd2\u820d\u5012\u662f\u84ec\u6479\u751f\u8f89\u5440\u3002\u8bf7\uff0c\u8bf7\uff0c\u603b \u6355\u5934\uff0c\u8bf7\u81f3\u524d\u5385\u5c0f\u5750\uff0c\u5bb9\u91d1\u67d0\u5949\u8336\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u8001\u592a\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u56db\u5927\u6355\u5934\uff0c\u5c0f\u6587\u3001\u5c0f\u96c5\u966a\u7740\u7a0b\u5c0f\u8776\u4e00\u8d77\u8d70\u8fdb\u4e86\u91d1\u5e9c\u524d\u5385\u3002 \u3000\u3000\u4f17\u4eba\u5750\u5b9a\u540e\uff0c\u91d1\u767e\u5e74\u8ba9\u4eba\u6367\u4e0a\u8336\u70b9\uff0c\u65b9\u624d\u5c0f\u5fc3\u5730\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u662f\u4e0d\u662f\u51fa\u4ec0\u4e48\u4e8b\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5355\u5200\u76f4\u5165\uff0c\u9053\uff1a\u201c\u662f\u51fa\u4e8b\u4e86\uff0c\u800c\u4e14\u662f\u5927\u4e8b\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u5230\u5e95\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5c31\u5728\u521a\u624d\uff0c\u6709\u4e94\u540d\u6740\u624b\u6f5c\u5165\u4e86\u6c34\u5e08\u63d0\u7763\u5e9c\uff0c\u523a\u6740\u9a6c\u957f\u5c71\u63d0\u7763\uff0c\u4e8f\u5f97\u6211\u65e9\u6709 \u51c6\u5907\uff0c\u6d3e\u4e86\u51e0\u4f4d\u9ad8\u624b\u5728\u6697\u4e2d\u4fdd\u62a4\uff0c\u624d\u5c06\u523a\u5ba2\u64d2\u4e0b\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u8138\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u542c\u4f60\u7684\u610f\u601d\uff0c\u50cf\u662f\u6709\u4e9b\u6000\u7591\u91d1\u67d0\u55bd\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u91d1\u8001\u8a00\u91cd\u4e86\u3002\u4e0d\u8fc7\uff0c\u8fd9\u4ef6\u4e8b\u7684\u786e\u4e0e\u91d1\u8001\u6709\u4e00\u4e9b\u5173\u7cfb\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u8fd9\u8bdd\u6211\u53ef\u62c5\u5f53\u4e0d\u8d77\u5440\u3002\u91d1\u67d0\u867d\u7136\u4e0e\u9a6c\u63d0\u7763\u6709\u8fc7\u4e24\u6b21\u51b2\u7a81\uff0c\u53ef\u90a3 \u662f\u56e0\u4e3a\u4ed6\u60f3\u5f3a\u884c\u5e26\u8d70\u5c0f\u5973\uff0c\u91d1\u67d0\u867d\u8bf4\u53ea\u662f\u4e00\u4e2a\u5546\u4eba\uff0c\u4f46\u4e5f\u7a0d\u77e5\u56fd\u5bb6\u5211\u5f8b\uff0c\u8fd9\u8c0b\u5bb3\u671d\u5ef7\u8981\u5458\uff0c \u53ef\u662f\u6284\u5bb6\u6740\u5934\u7684\u7f6a\u540d\uff0c\u91d1\u67d0\u5bb6\u5927\u4e1a\u5927\uff0c\u53ef\u4e0d\u4f1a\u505a\u8fd9\u79cd\u4e8b\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u91d1\u8001\u4e0d\u5fc5\u5982\u6b64\u7d27\u5f20\uff0c\u6211\u4e5f\u6ca1\u8bf4\u8fd9\u4e8b\u662f\u91d1\u8001\u6307\u4f7f\u5440\uff0c\u6211\u53ea\u662f\u8bf4\u8fd9\u4e8b \u4e0e\u91d1\u8001\u6709\u4e00\u4e9b\u5173\u7cfb\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u4f3c\u4e4e\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u597d\uff0c\u90a3\u5c31\u597d\uff0c\u53ea\u4e0d\u77e5\u5230\u5e95\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d\u7a0b\u5c0f\u8776\u56db \u4e0b\u91cc\u770b\u770b\uff0c\u9053\uff1a\u201c\u5929\u67ab\u9053\u957f\u5462\uff1f\u4ed6\u4e0d\u662f\u4e00\u76f4\u8ddf\u5728\u91d1\u8001\u8eab\u8fb9\u5417\uff1f\u201d\u91d1\u767e\u5e74\u9053\uff1a\u201c\u6211\u4e00\u81ea\u5e9c\u8859\u56de \u6765\u4e4b\u540e\uff0c\u5929\u67ab\u9053\u957f\u4fbf\u5e26\u4e86\u5e9c\u4e2d\u56db\u4f4d\u9ad8\u624b\uff0c\u51fa\u53bb\u67e5\u8bbf\u68a6\u5e7b\u4e4b\u5200\u7684\u884c\u8e2a\u53bb\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4ed6\u5e26\u8d70\u7684\uff0c\u662f\u4e0d\u662f\u5e9c\u4e2d\u56db\u4f4d\u4f7f\u86c7\u6756\u7684\u9ad8\u624b\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6b63\u662f\u3002\u603b\u6355\u5934\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u56e0\u4e3a\u4eca\u591c\u610f\u56fe\u884c\u524c\u9a6c\u5927\u4eba\u7684\uff0c\u6b63\u662f\u90a3\u56db\u4f4d\u4f7f\u86c7\u6756\u7684\u9ad8\u624b\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u5927\u5403\u4e00\u60ca\uff0c\u5927\u5f20\u7740\u5634\uff0c\u77aa\u76f4\u4e86\u53cc\u773c\uff0c\u4e00\u4e2a\u5b57\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002\u770b\u4ed6\u7684\u6837\u5b50\uff0c\u4e0d\u50cf\u662f\u5047 \u505a\u51fa\u6765\u7684\u3002 \u3000\u3000\u8d3e\u82f1\u5728\u4e00\u65c1\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u5343\u4e07\u4e0d\u8981\u8bef\u4f1a\uff0c\u8fd9\u56db\u4eba\u867d\u8bf4\u5728\u91d1\u5e9c\u4e5f\u6709\u56db\u4e94\u5e74\u4e86\uff0c\u4f46\u771f\u5b9e \u8eab\u5206\u6211\u4eec\u4e5f\u4e00\u76f4\u6ca1\u6709\u6478\u5f97\u592a\u6e05\u695a\uff0c\u5176\u5b9e\uff0c\u4ed6\u4eec\u5728\u5e9c\u4e2d\u4e5f\u4e00\u76f4\u672a\u5f97\u91cd\u7528\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\u4e5f\u4e0d\u7528\u89e3\u91ca\u4e86\uff0c\u6211\u7684\u786e\u6ca1\u6709\u6000\u7591\u91d1\u8001\u7684\u610f\u601d\uff0c\u53ea\u662f\u60f3\u6765\u627e\u5929\u67ab\u9053\u957f\uff0c \u4e0e\u88ab\u64d2\u7684\u6740\u624b\u5bf9\u8d28\u3002\u91d1\u8001\uff0c\u4f60\u6709\u529e\u6cd5\u5c3d\u5feb\u627e\u5230\u5929\u67ab\u9053\u957f\u5417\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u6740\u624b\u8bf4\u662f\u53d7\u5929\u67ab\u9053\u957f\u7684\u6307\u4f7f\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u770b\u4e86\u8d3e\u82f1\u4e00\u773c\uff0c\u60ca\u7591\u4e0d\u5b9a\u5730\u9053\uff1a\u201c\u4e0d\u4f1a\u5427\uff1f\u5929\u67ab\u9053\u957f\u4e00\u76f4\u5728\u5168\u529b\u8ffd\u67e5\u68a6\u5e7b\u4e4b\u5200\u7684 \u884c\u8e2a\uff0c\u4e3a\u7834\u6848\u51fa\u529b\uff0c\u4ed6\u600e\u4e48\u4f1a\u6307\u4f7f\u6740\u624b\u53bb\u523a\u6740\u9a6c\u5927\u4eba\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u8001\uff0c\u4f60\u80fd\u66ff\u5929\u67ab\u9053\u957f\u62c5\u4fdd\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u4e1c\u4e3b\uff0c\u4f60\u4e0d\u80fd\u66ff\u4ed6\u62c5\u4fdd\uff0c\u77e5\u4eba\u77e5\u9762\u4e0d\u77e5\u5fc3\u554a\uff0c\u4eba\u5fc3\u9694\u809a\u76ae\uff0c\u4f60\u600e\u4e48\u77e5\u9053\u5929\u67ab \u9053\u957f\u5fc3\u91cc\u5230\u5e95\u600e\u4e48\u60f3\u5462\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u9053\u7406\u662f\u8fd9\u4e2a\u9053\u7406\uff0c\u4f46\u4e3a\u4e86\u66ff\u5c0f\u5973\u6d17\u5237\u51a4\u60c5\uff0c\u9053\u957f\u771f\u53ef\u8c13\u662f\u65e5\u591c\u64cd\u52b3\uff0c\u600e\u4e48 \u8bf4\uff0c\u91d1\u67d0\u4e5f\u4e0d\u76f8\u4fe1\u4ed6\u4f1a\u4e0e\u6740\u624b\u96c6\u56e2\u6709\u5173\u7cfb\u3002\u201d \u3000\u3000\u91d1\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u4e2a\u597d\u529e\uff0c\u6211\u4eec\u5c31\u5728\u8fd9\u91cc\u5750\u7b49\u4e00\u4f1a\uff0c\u8bf7\u91d1\u8001\u6d3e\u4eba\u53bb\u5c06\u5929\u67ab\u9053\u957f\u627e\u56de\u6765\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u884c\u3002\u201d \u3000\u3000\u8d3e\u82f1\u7acb\u523b\u9053\uff1a\u201c\u4e1c\u4e3b\uff0c\u8d3e\u82f1\u53bb\u5427\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u6700\u719f\u6089\u5929\u67ab\u9053\u957f\u884c\u4e8b\u4e60\u60ef\u7684\uff0c\u83ab\u8fc7\u4e8e\u9a6c\u6c0f\u5144\u5f1f\uff0c \u8fd8\u662f\u8ba9\u4ed6\u4eec\u53bb\u5427\u3002\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u662f\uff0c\u6211\u7acb\u523b\u5c31\u53bb\u5b89\u6392\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u7b49\u4e00\u7b49\uff0c\u8d3e\u603b\u7ba1\uff0c\u8bf7\u4f60\u5c06\u9a6c\u6c0f\u5144\u5f1f\u62db\u5230\u5927\u5385\u6765\uff0c\u7531\u6211\u4eb2\u81ea\u5bf9\u4ed6\u4eec\u8bf4\u3002\u201d \u3000\u3000\u8d3e\u82f1\u4e00\u6014\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\u4e0d\u4fe1\u4efb\u8d3e\u67d0\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6de1\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\u591a\u5fc3\u4e86\u3002\u201d \u3000\u3000\u8d3e\u82f1\u54ac\u4e86\u54ac\u7259\uff0c\u5927\u58f0\u5bf9\u5385\u5916\u9053\uff1a\u201c\u6765\u4eba\uff0c\u4f20\u4ee4\u4e0b\u53bb\uff0c\u53eb\u9a6c\u6c0f\u5144\u5f1f\u6765\u89c1\u4e1c\u4e3b\uff01\u201d \u3000\u3000\u5385\u5916\u6709\u4eba\u5e94\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\uff0c\u9a6c\u6c0f\u5144\u5f1f\u6025\u5306\u5306\u8d70\u4e86\u8fdb\u6765\uff0c\u62f1\u624b\u9053\uff1a\u201c\u4e1c\u4e3b\u4f20\u5524\u5728\u4e0b\u5144\u5f1f\uff0c\u4e0d\u77e5\u6709\u4ec0\u4e48\u4e8b\u8981 \u6211\u4eec\u53bb\u529e\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u5211\u90e8\u603b\u6355\u5934\u7a0b\u5927\u4eba\u3002\u201d \u3000\u3000\u9a6c\u6c0f\u5144\u5f1f\u4e00\u9f50\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u89c1\u8fc7\u603b\u6355\u5934\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6beb\u4e0d\u5ba2\u6c14\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u548c\u5929\u67ab\u9053\u957f\u5f88\u719f\uff1f\u201d \u3000\u3000\u9a6c\u6c0f\u5144\u5f1f\u9053\uff1a\u201c\u8c08\u4e0d\u4e0a\u5f88\u719f\uff0c\u4e0d\u8fc7\u4e00\u8d77\u51fa\u53bb\u66ff\u4e1c\u4e3b\u529e\u4e8b\u7684\u673a\u4f1a\u6bd4\u8f83\u591a\u800c\u5df2\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u90a3\u597d\uff0c\u4f60\u4eec\u77e5\u4e0d\u77e5\u9053\u73b0\u5728\u5728\u54ea\u91cc\u624d\u80fd\u627e\u5230\u4ed6\uff1f\u201d \u3000\u3000\u9a6c\u4fee\u9053\uff1a\u201c\u9053\u957f\u51fa\u53bb\u8ffd\u67e5\u68a6\u5e7b\u4e4b\u5200\u7684\u884c\u8e2a\u53bb\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u5df2\u7ecf\u77e5\u9053\u4e86\uff0c\u6211\u53ea\u95ee\u4f60\u80fd\u5426\u5c3d\u5feb\u627e\u5230\u4ed6\u3002\u201d \u3000\u3000\u9a6c\u4fee\u8fdf\u7591\u5730\u9053\uff1a\u201c\u5929\u67ab\u9053\u957f\uff0c\u884c\u8e2a\u4e00\u5411\u98d8\u5ffd\u4e0d\u5b9a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u8d3e\u82f1\u5ffd\u7136\u8f7b\u8f7b\u54b3\u55fd\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u9a6c\u5f3a\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u77e5\u9053\u4e00\u79cd\u5c3d\u5feb\u4e0e\u4ed6\u8054\u7cfb\u7684\u65b9\u6cd5\uff0c\u603b\u6355\u5934\u53ef\u662f\u8ba9\u6211\u4eec\u5144\u5f1f\u53bb\u627e\u4ed6\u56de\u5e9c \u6765\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u9a6c\u5f3a\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u5c31\u53bb\u627e\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u987f\u4e86\u987f\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u679c\u5929\u67ab\u9053\u957f\u4e0d\u80af\u8ddf\u4f60\u4eec\u4e00\u8d77\u56de\u6765\uff0c\u4f60\u4eec\u51c6\u5907\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u9a6c\u5f3a\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u90a3\u5c31\u597d\uff0c\u4f60\u4eec\u53bb\u5427\u3002\u201d \u3000\u3000\u9a6c\u6c0f\u5144\u5f1f\u524d\u811a\u51fa\u95e8\uff0c\u8d3e\u82f1\u540e\u811a\u5c31\u60f3\u5f80\u5927\u5385\u5916\u9762\u8d70\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8d3e\u603b\u7ba1\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u6d3e\u51fa\u4e86\u9a6c\u6c0f\u5144\u5f1f\uff0c\u5e9c\u4e2d\u5357\u533a\u7684\u9632\u52a1\u5fc5\u5b9a\u7a7a\u865a\uff0c\u8d3e\u67d0\u53ea\u662f\u60f3\u53bb\u8c03\u6574\u4e00\u4e0b\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u5c31\u5728\u8fd9\u91cc\u4e0b\u4ee4\u8c03\u6d3e\u4eba\u624b\u5427\u3002\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\u5f97\u5f88\uff0c\u91d1\u8001\uff0c\u8d3e\u603b\u7ba1\uff0c\u5728\u5929\u67ab\u9053\u957f\u56de\u5230\u6b64\u5730\u4e0e\u6740\u624b\u5bf9\u8d28\u4e4b\u524d\uff0c\u4efb\u4f55 \u4eba\u90fd\u4e0d\u8bb8\u79bb\u5f00\u5927\u5385\uff01\u201d \u3000\u3000\u8d3e\u82f1\u7684\u773c\u775b\u91cc\u7a81\u7136\u9732\u51fa\u4e00\u4e1d\u51f6\u5149\uff0c\u4f46\u5f88\u5feb\u53c8\u6d88\u5931\u4e86\uff0c\u4ed6\u5f88\u5e73\u9759\u5730\u9053\uff1a\u201c\u8d3e\u67d0\u9075\u547d\u4fbf\u662f\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u5c31\u7ad9\u5728\u5927\u5385\u95e8\u8fb9\uff0c\u5411\u95e8\u5916\u7684\u4eba\u4e0b\u8fbe\u547d\u4ee4\uff0c\u53e6\u6d3e\u56db\u4f4d\u9ad8\u624b\u62a4\u536b\u53bb\u5357\u533a\u5e03\u9632\uff0c\u63a5\u66ff \u9a6c\u6c0f\u5144\u5f1f\u3002 \u3000\u3000\u91d1\u767e\u5e74\u7684\u76ee\u5149\u4e00\u76f4\u60ca\u7591\u4e0d\u5b9a\u5730\u770b\u7740\u7a0b\u5c0f\u8776\uff0c\u8fd9\u65f6\u8bf4\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u4f60\u4eb2\u81ea\u6765\u5bd2\u820d\uff0c\u8fd8 \u5e26\u6765\u4e86\u56db\u5927\u6355\u5934\u548c\u51e0\u5341\u540d\u597d\u624b\uff0c\u5e9c\u8859\u90a3\u8fb9\u600e\u4e48\u529e\uff1f\u738b\u5927\u4eba\u7684\u5b89\u5168\u7531\u8c01\u4fdd\u8bc1\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u91d1\u8001\u653e\u5fc3\uff0c\u6211\u5df2\u8ba9\u9a6c\u63d0\u7763\u8c03\u96c6\u4e09\u5343\u7cbe\u5175\uff0c\u9a7b\u5b88\u5e9c\u8859\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u90a3\u5c31\u597d\uff0c\u90a3\u5c31\u597d\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5bbe\u4e3b\u4e4b\u95f4\u5c31\u627e\u4e0d\u51fa\u8bdd\u597d\u8bf4\u4e86\u3002 \u3000\u3000\u5927\u5385\u91cc\u7684\u6c14\u6c1b\uff0c\u5341\u5206\u5c34\u5c2c\u3002 \u3000\u3000\u91d1\u767e\u5e74\u5f88\u662f\u6709\u70b9\u5750\u7acb\u4e0d\u5b89\uff0c\u76ee\u5149\u4e0d\u4f4f\u5730\u6295\u5411\u95e8\u5916\u3002 \u3000\u3000\u8d3e\u82f1\u5374\u4e00\u76f4\u5f88\u9547\u5b9a\u3002 \u3000\u3000\u4ed6\u5782\u7740\u624b\uff0c\u7ad9\u5728\u91d1\u767e\u5e74\u8eab\u540e\uff0c\u4e00\u52a8\u4e0d\u52a8\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u91d1\u8001\u600e\u4e48\u4e0d\u95ee\u95ee\u4ee4\u5a9b\u7684\u60c5\u51b5\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u662f\u5f88\u60f3\u95ee\uff0c\u53ea\u662f\u4e00\u76f4\u4e0d\u592a\u597d\u5f00\u53e3\u554a\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u5f97\u66f4\u4eb2\u5207\uff0c\u9053\uff1a\u201c\u91d1\u8001\u8a00\u91cd\u4e86\uff0c\u91d1\u5c0f\u7709\u53bb\u5e9c\u8859\u662f\u505a\u5ba2\uff0c\u53c8\u4e0d\u662f\u4eba\u72af\uff0c\u6709\u4ec0\u4e48\u4e0d \u597d\u95ee\u7684\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u6de1\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u60f3\uff0c\u5979\u4e00\u5b9a\u5f97\u5230\u4e86\u8bf8\u4f4d\u6781\u597d\u7684\u7167\u987e\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\uff0c\u60dc\u7389\u4e00\u76f4\u5728\u966a\u7740\u5979\uff0c\u5e2e\u52a9\u5979\u56de\u5fc6\u8840\u6848\u4e4b\u524d\u6240\u53d1\u751f\u7684\u4e00\u4e9b\u4e8b\u60c5\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u54e6\uff1f\u8fd9\u5bf9\u7834\u6848\u6709\u4ec0\u4e48\u7528\u5904\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5f53\u7136\u6709\uff0c\u800c\u4e14\u662f\u975e\u5e38\u91cd\u8981\u7684\u7ebf\u7d22\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u4e00\u76f4\u4e0d\u601d\u8336\u996d\uff0c\u6574\u5929\u6c89\u6d78\u5728\u8fc7\u5ea6\u7684\u60b2\u4f24\u4e4b\u4e2d\uff0c\u4eba\u4e5f\u65e5\u89c1\u6194\u60b4\uff0c\u7cbe\u795e \u604d\u60da\uff0c\u53ea\u6015\u5f88\u96be\u56de\u5fc6\u8d77\u4ec0\u4e48\u6765\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\uff0c\u5982\u679c\u5979\u4e00\u76f4\u4e0d\u601d\u8336\u996d\uff0c\u8eab\u4f53\u3001\u7cbe\u795e\u4e0a\u65e5\u89c1\u8870\u5f31\uff0c\u5f53\u7136\u56de\u5fc6\u4e0b\u8d77\u4ec0\u4e48 \u6765\uff0c\u4f46\u81ea\u4ece\u5230\u4e86\u5e9c\u8859\u4e4b\u540e\uff0c\u91d1\u5c0f\u7709\u996d\u4e5f\u5403\u5f97\u4e0b\uff0c\u89c9\u4e5f\u7761\u5f97\u7740\uff0c\u77ed\u77ed\u4e00\u5929\u4e0d\u5230\u7684\u65f6\u95f4\uff0c\u7cbe\u795e\u5df2 \u7ecf\u5065\u65fa\u4e86\u5f88\u591a\u4e86\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u60ca\u559c\u5730\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u8001\u592b\u771f\u662f\u8981\u8c22\u8c22\u603b\u6355\u5934\u3001\u738b\u5927\u4eba\u548c\u5e9c\u8859\u91cc\u7684\u8bf8\u4f4d\u5bf9\u5c0f\u5973\u7684 \u5173\u7231\u4e4b\u5fc3\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u91d1\u8001\u592a\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e00\u6bb5\u8bdd\u8bf4\u8fc7\uff0c\u5bbe\u4e3b\u4e4b\u95f4\u53c8\u6ca1\u6709\u4e86\u8bdd\u9898\u53ef\u8bf4\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u524d\u5385\u7684\u6c14\u6c1b\u5df2\u4e0d\u50cf\u521a\u624d\u90a3\u6837\u5c34\u5c2c\u4e86\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\u5fc3\u91cc\u4e0d\u7981\u6697\u81ea\u655b\u4f69\u603b\u6355\u5934\u7684\u5fc3\u673a\uff0c\u77ed\u77ed\u51e0\u53e5\u8bdd\u5c31\u80fd\u6253\u6d88\u91d1\u767e\u5e74\u5fc3\u4e2d\u7684\u654c\u610f\uff0c\u5b9e \u5728\u5f88\u96be\u5f97\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u957f\u4e00\u6bb5\u65f6\u95f4\u3002 \u3000\u3000\u524d\u5385\u91cc\u867d\u8bf4\u96c6\u4e0b\u4e09\u5341\u4eba\uff0c\u5374\u662f\u4e00\u70b9\u58f0\u97f3\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4ee4\u4eba\u96be\u8010\u7684\u5bc2\u9759\u4e2d\uff0c\u5385\u5916\u4f20\u6765\u4e86\u811a\u6b65\u58f0\u3002 \u3000\u3000\u4eba\u5f71\u4e00\u95ea\uff0c\u5929\u67ab\u9053\u957f\u6b63\u51fa\u73b0\u5728\u5385\u95e8\u5916\u3002 \u3000\u3000\u4ed6\u4e00\u770b\u5385\u4e2d\u7684\u9635\u52bf\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u65cb\u5373\u6062\u590d\u4e86\u5e38\u6001\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\u6025\u62db\u8001\u9053\u6765\u6b64\uff0c\u4e0d\u77e5\u6709 \u4f55\u8981\u4e8b\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e0d\u56de\u7b54\uff0c\u5bf9\u5c0f\u6587\u3001\u5c0f\u96c5\u9053\uff1a\u201c\u53bb\uff0c\u5b88\u4f4f\u5385\u95e8\uff0c\u4efb\u4f55\u4eba\u4e5f\u4e0d\u8bb8\u653e\u8d70\uff0c\u6709\u64c5\u81ea\u95ef\u8fdb\uff0c \u95ef\u5165\u8005\uff0c\u6740\u4e0d\u8d66\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u5403\u60ca\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u51b7\u5730\u9053\uff1a\u201c\u5929\u67ab\u9053\u957f\uff0c\u4f60\u4eca\u5929\u51fa\u53bb\u5e72\u4ec0\u4e48\u4e86\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u53bb\u8ffd\u67e5\u68a6\u5e7b\u4e4b\u5200\u7684\u884c\u8e2a\u53bb\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u4e00\u4e2a\u4eba\u53bb\u7684\u5427\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u9053\u5e26\u4e86\u91d1\u5e9c\u91cc\u56db\u4f4d\u9ad8\u624b\u4e00\u8d77\u53bb\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u90a3\u56db\u4f4d\u9ad8\u624b\u7684\u5175\u5668\u662f\u4e0d\u662f\u86c7\u6756\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4ed6\u4eec\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u8001\u9053\u8ba9\u4ed6\u4eec\u5206\u5934\u8ffd\u67e5\u53bb\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6765\u4eba\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5c5e\u4e0b\u5728\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u53bb\uff0c\u628a\u8f7f\u4e2d\u4eba\u8bf7\u51fa\u6765\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u8fd9\u624d\u53d1\u73b0\u5728\u5385\u95e8\u540e\u653e\u7740\u4e00\u4e58\u5c0f\u8f7f\u3002 \u3000\u3000\u675c\u671b\u6708\u95ea\u8eab\u8fc7\u53bb\uff0c\u6380\u5f00\u8f7f\u5e18\uff0c\u4f38\u624b\u5c06\u9ed1\u8863\u6740\u624b\u62d6\u4e86\u51fa\u6765\uff0c\u4e00\u76f4\u62d6\u5230\u5929\u67ab\u9053\u957f\u8eab\u8fb9\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u51b7\u9053\uff1a\u201c\u5929\u67ab\u9053\u957f\uff0c\u8fd9\u4eba\u662f\u4e0d\u662f\u90a3\u56db\u540d\u9ad8\u624b\u4e4b\u4e00\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u7684\u8138\u987f\u65f6\u53d8\u5f97\u6709\u4e9b\u82cd\u767d\uff0c\u4f46\u4ed6\u4ecd\u7136\u5f88\u8dcb\u6248\u5730\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u4eec\u662f\u5728\u54ea\u91cc\u6293\u4f4f\u4ed6\u7684\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4ed6\u4eec\u56db\u4eba\u8fde\u540c\u8840\u624b\u65b9\u8f6e\u624b\u4e0b\u4e00\u540d\u5973\u6740\u624b\u4eca\u591c\u6f5c\u5165\u63d0\u7763\u5e9c\uff0c\u610f\u6b32\u884c\u523a\u9a6c\u5927\u4eba\uff0c \u88ab\u6211\u5f53\u573a\u51fb\u6bd9\u56db\u4eba\uff0c\u7559\u4e0b\u4e86\u8fd9\u4e00\u4e2a\u6d3b\u53e3\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u8fd9\u8ddf\u6211\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u58f0\u9053\uff1a\u201c\u8fd9\u540d\u6740\u624b\u51c6\u5907\u670d\u6bd2\u836f\u81ea\u6740\uff0c\u5374\u88ab\u6211\u8bc6\u7834\uff0c\u53d6\u51fa\u4e86\u4ed6\u7684\u6bd2\u836f\u8721\u4e38\uff0c\u56e0\u6b64\uff0c \u5df2\u5c06\u5b9e\u60c5\u5c3d\u5410\uff0c\u5929\u67ab\uff0c\u4f60\u4e0d\u8981\u518d\u62b5\u8d56\u4e86\uff01\u201d \u3000\u3000\u5979\u5bf9\u675c\u671b\u6708\u9053\uff1a\u201c\u675c\u53f8\u4e3b\uff0c\u89e3\u5f00\u4ed6\u7684\u7a74\u9053\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u4f38\u624b\u5728\u9ed1\u8863\u6740\u624b\u80cc\u4e0a\u7528\u529b\u70b9\u4e86\u4e24\u4e0b\uff0c\u6740\u624b\u987f\u65f6\u5f00\u53e3\u53eb\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u5c31\u662f\u4ed6\u8ba9\u6211 \u4eec\u53bb\u7684\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u8dfa\u4e86\u8dfa\u811a\uff0c\u4f38\u624b\u62e8\u51fa\u4e86\u5b9d\u5251\uff0c\u5927\u559d\u9053\uff1a\u201c\u6ca1\u9aa8\u6c14\u7684\u8d56\u76ae\u72d7\uff01\u201d \u3000\u3000\u6740\u624b\u9053\uff1a\u201c\u9053\u957f\uff0c\u4f60\u4e5f\u4e0d\u8981\u602a\u6211\uff0c\u6b7b\u6211\u4e0d\u6015\uff0c\u53ef\u6d3b\u7f6a\u6211\u53d7\u4e0d\u4e86\u554a\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u4e00\u5251\u5411\u4ed6\u523a\u4e86\u8fc7\u53bb\uff01 \u3000\u3000\u675c\u671b\u6708\u65e9\u5df2\u62e8\u51fa\u4e03\u661f\u5b9d\u5251\uff0c\u67b6\u5f00\u5929\u67ab\u9053\u957f\u7684\u957f\u5251\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u6740\u4eba\u706d\u53e3\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u5ffd\u7136\u5c06\u957f\u5251\u5782\u4e86\u4e0b\u6765\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u602a\u4f60\uff01\u53ea\u6068\u4e00\u7740\u5931\u8bef\uff0c\u529f\u8d25\u5782\u6210 \u4e86\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u5927\u559d\u9053\uff1a\u201c\u5929\u67ab\u9053\u957f\uff0c\u4f60\u771f\u7684\u2026\u2026\u4f60\u771f\u7684\u662f\u8840\u624b\u65b9\u8f6e\u7684\u624b\u4e0b\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4f60\u5230\u91d1\u67d0\u5e9c\u4e2d\u6765\uff0c\u610f\u6b32\u4f55\u4e3a\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u6740\u4f60\uff01\u5229\u7528\u4f60\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u6c14\u5f97\u76f4\u54c6\u55e6\uff0c\u4e00\u4e2a\u5b57\u4e5f\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u8001\uff0c\u4f60\u8fd8\u8981\u66ff\u4ed6\u62c5\u4fdd\u5417\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u6211\u771f\u662f\u6709\u773c\u65e0\u73e0\uff0c\u600e\u4e48\u4f1a\u5982\u6b64\u76f8\u4fe1\u4ed6\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u6c89\u58f0\u9053\uff1a\u201c\u5929\u67ab\uff0c\u4f60\u5df2\u7ecf\u5931\u8d25\u4e86\uff01\u8fd8\u4e0d\u4e22\u4e0b\u5251\uff0c\u675f\u624b\u5c31\u7f1a\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u4e22\u5251\uff1f\u4f60\u4eec\u56db\u5927\u6355\u5934\u867d\u7136\u540d\u9707\u56db\u6d77\uff0c\u4f46\u8981\u8bba\u6b66\u529f\uff0c\u5927\u6982 \u8fd8\u4e0d\u662f\u6211\u7684\u5bf9\u624b\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u9053\u957f\uff0c\u4f60\u539f\u672c\u5578\u50b2\u5c71\u6797\uff0c\u8fc7\u7740\u95f2\u4e91\u91ce\u9e64\u4e00\u822c\u7684\u65e5\u5b50\uff0c\u53c8\u662f\u4f55 \u7b49\u7684\u8212\u5fc3\u5feb\u610f\uff01\u4e3a\u4f55\u8fd8\u770b\u4e0d\u5f00\u529f\u540d\u5229\u7984\u56db\u4e2a\u5b57\uff0c\u4e00\u5fc3\u5815\u5165\u6740\u624b\u96c6\u56e2\u5462\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u529f\u540d\u5229\u7984\uff0c\u8fd9\u4e16\u4e0a\u53c8\u6709\u51e0\u4eba\u80fd\u770b\u5f00\uff01\u4f60\u7a0b\u603b\u6355\u5934\u80fd\u770b\u5f00\u5417\uff1f\u675c\u671b\u6708\uff0c\u4f60 \u4eec\u56db\u5927\u6355\u5934\u80fd\u770b\u5f00\u5417\uff1f\u5982\u679c\u4f60\u4eec\u80fd\u770b\u5f97\u5f00\uff0c\u90a3\u4e48\uff0c\u5343\u91cc\u5954\u6ce2\u6765\u626c\u5dde\uff0c\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u8bba\u5230\u5e95\uff0c \u8fd8\u4e0d\u662f\u4e3a\u4e86\u540d\uff0c\u4e3a\u4e86\u5229\uff0c\u4e3a\u4e86\u671d\u5ef7\u8d4f\u8d50\u7684\u4e00\u5b98\u534a\u804c\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u6c89\u58f0\u9053\uff1a\u201c\u9053\u957f\uff0c\u4f60\u9519\u4e86\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u4e0d\u5c51\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u8bf4\u770b\uff0c\u6211\u600e\u4e48\u9519\u4e86\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u8f9e\u8f9b\u52b3\uff0c\u662f\u4e3a\u4e86\u4f38\u5f20\u6b63\u4e49\uff0c\u60e9\u5904\u4f5c\u5978\u72af\u79d1\u8005\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4f60\u8bf4\u4f60\u4eec\u4e3a\u5f97\u662f\u4f38\u5f20\u6b63\u4e49\uff0c\u8bf7\u95ee\u4ec0\u4e48\u624d\u662f\u6b63\u4e49\u5462\uff1f\u6211\u8001\u9053\u4e5f\u81ea\u8ba4\u4e3a\u81ea\u5df1 \u7684\u6240\u505a\u6240\u4e3a\uff0c\u624d\u662f\u4ee3\u8868\u4e86\u6b63\u4e49\u5462\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u53f1\u9053\uff1a\u201c\u4e00\u6d3e\u80e1\u8a00\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u8ddf\u4f60\u4eec\u8fd9\u79cd\u5c0f\u4eba\u7269\u8bf4\u4e0d\u6e05\u695a\uff0c\u6b63\u6240\u8c13\uff0c\u71d5\u96c0\u5b89\u77e5\u9e3f\u9e44\u4e4b\u5fd7\u54c9\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u80fd\u95ee\u4e00\u95ee\u9053\u957f\u7684\u9e3f\u9e44\u4e4b\u5fd7\u662f\u4ec0\u4e48\u5417\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u5ffd\u7136\u95ed\u4e0a\u4e86\u5634\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u600e\u4e48\uff0c\u5728\u9053\u957f\u773c\u91cc\uff0c\u6211\u5802\u5802\u5211\u90e8\u603b\u6355\u5934\u4e5f\u53ea\u662f\u4e00\u4e2a\u5c0f\u4eba\u7684\u5417\uff1f\u90a3 \u4e48\u4ec0\u4e48\u4eba\u624d\u662f\u5927\u4eba\u7269\u5462\uff1f\u5728\u4f60\u80cc\u540e\u7684\uff0c\u53c8\u662f\u600e\u6837\u4e00\u4f4d\u5927\u4eba\u7269\u5462\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u65e0\u53ef\u5949\u544a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u8bf4\u51fa\u6765\uff0c\u6211\u9976\u4f60\u4e0d\u6b7b\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u7b11\u9053\uff1a\u201c\u8001\u9053\u65e9\u5df2\u5c06\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u603b\u6355\u5934\u5948\u4f55\u4ee5\u6b7b\u60e7\u6211\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u9053\u957f\u7684\u786e\u81ea\u4ee5\u4e3a\u8fdb\u884c\u7684\u662f\u6b63\u4e49\u7684\u4e8b\u4e1a\u4e86\uff0c\u4e0d\u77e5\u9053\u4f60\u80fd\u5426\u8c08\u4e00\u8c08\uff0c \u662f\u4e00\u4ef6\u600e\u6837\u7684\u6b63\u4e49\u4e8b\u4e1a\u5462\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd8\u662f\u521a\u624d\u90a3\u53e5\u8bdd\uff0c\u65e0\u53ef\u5949\u544a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u7b11\u9053\uff1a\u201c\u770b\u6765\uff0c\u9053\u957f\u662f\u4e00\u5fc3\u6c42\u6b7b\u4e86\uff0c\u53ea\u662f\uff0c\u9053\u957f\u4e0d\u60f3\u4f60\u6b7b\u5f97\u6709\u4e9b\u4e0d\u503c\u5417\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4f60\u7684\u8bdd\u6211\u542c\u4e0d\u61c2\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u73b0\u5728\u6b7b\u4e86\uff0c\u5c31\u770b\u4e0d\u5230\u4f60\u6240\u8c13\u6b63\u4e49\u7684\u4e8b\u4e1a\u5230\u5e95\u80fd\u5426\u6210\u529f\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\uff0c\u6211\u5f88\u653e\u5fc3\uff0c\u56e0\u4e3a\u6211\u867d\u7136\u6b7b\u4e86\uff0c\u4f46\u4e0e\u6211\u5fd7\u540c\u9053\u5408\u7684\u90a3\u4e9b\u5fd7\u58eb\u4eec\u4e00 \u5b9a\u4f1a\u5c06\u8fd9\u9879\u4e8b\u4e1a\u8fdb\u884c\u4e0b\u53bb\u7684\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u9053\u957f\u5982\u6b64\u56fa\u6267\uff0c\u6211\u4e5f\u5c31\u4e0d\u518d\u8ffd\u95ee\u4e86\u3002\u53ea\u662f\u6709\u51e0\u4e2a\u65e0\u5173\u5927\u5c40\u7684\u95ee \u9898\uff0c\u60f3\u8bf7\u9053\u957f\u6307\u70b9\u3002\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u56db\u51e4\u697c\u6b66\u9662\u9662\u4e3b\u662f\u4e0d\u662f\u4f60\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6211\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u94c1\u7fce\u4e00\u76f4\u89c9\u5f97\u4ed6\u5f88\u719f\u6089\u6b66\u9662\u9662\u4e3b\u7684\u58f0\u97f3\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u6b63\u662f\u56e0\u4e3a\u6211\u5df2\u770b\u51fa\u4ed6\u5bf9\u6211\u5fc3\u5b58\u6000\u7591\uff0c\u624d\u4f1a\u6740\u4e86\u4ed6\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u94c1\u7fce\u662f\u4f60\u6740\u7684\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u8001\u9053\u53ef\u6ca1\u6709\u90a3\u6837\u6df1\u7684\u529f\u529b\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u90a3\u662f\u8c01\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u5f53\u7136\u662f\u68a6\u5e7b\u4e4b\u5200\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u68a6\u5e7b\u4e4b\u5200\u7684\u786e\u662f\u6740\u624b\u96c6\u56e2\u7684\u9996\u8111\u4e86\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u5c06\u4ed6\u7684\u4f4f\u5904\u900f\u9732\u7ed9\u6211\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u90a3\u53ea\u662f\u4e00\u4e2a\u5708\u5957\uff0c\u76ee\u7684\u662f\u4e3a\u4e86\u6740\u4f60\u548c\u94c1\u7fce\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u4e00\u5f00\u59cb\u5c31\u8bf4\u81ea\u5df1\u53d1\u73b0\u4e86\u65b0\u623f\u8840\u6848\u662f\u68a6\u5e7b\u4e4b\u5200\u6240\u4e3a\uff0c\u662f\u60f3\u5e72\u4ec0\u4e48\u5462\uff1f\u8fd9\u5c82 \u4e0d\u662f\u81ea\u5df1\u66b4\u9732\u81ea\u5df1\u5417\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u8fd9\u6b63\u662f\u68a6\u5e7b\u4e4b\u5200\u7684\u672c\u610f\uff0c\u4ed6\u672c\u5c31\u60f3\u8bd5\u4e00\u8bd5\u4f60\u4eec\u6709\u591a\u5927\u80fd\u8010\uff0c\u662f\u4e0d\u662f\u80fd\u6293 \u4f4f\u4ed6\u3002\u770b\u6765\uff0c\u4f60\u603b\u6355\u5934\u548c\u56db\u5927\u6355\u5934\u4e5f\u53ea\u662f\u6d6a\u5f97\u865a\u540d\u4e4b\u8f88\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u6211\u4eec\u662f\u4e0d\u662f\u6d6a\u5f97\u865a\u540d\uff0c\u5f88\u5feb\u5c31\u4f1a\u89c1\u5206\u6653\uff0c\u53ea\u53ef\u60dc\uff0c\u4f60\u5929\u67ab\u9053\u957f\u662f\u770b\u4e0d\u5230 \u7ed3\u679c\u4e86\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u603b\u6355\u5934\u8981\u6740\u6211\uff0c\u4fbf\u8bf7\u52a8\u624b\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u8fd8\u672a\u7b54\u8bdd\uff0c\u91d1\u767e\u5e74\u7a81\u7136\u5f00\u53e3\u9053\uff1a\u201c\u5929\u67ab\u9053\u957f\uff0c\u8001\u592b\u5f85\u4f60\u4e0d\u8584\uff0c\u4f60\u4e3a\u4f55\u8981\u52fe\u7ed3\u68a6\u5e7b \u4e4b\u5200\uff0c\u52a0\u5bb3\u5c0f\u5973\uff1f\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u9053\uff1a\u201c\u8981\u6210\u5927\u4e8b\uff0c\u5fc5\u987b\u6709\u6240\u727a\u7272\u3002\u6211\u8fde\u6700\u597d\u7684\u670b\u53cb\u94c1\u7fce\u90fd\u80fd\u727a\u7272\u6389\uff0c\u53c8\u600e\u4f1a\u5728 \u4e4e\u91d1\u5c0f\u7709\u5462\uff1f\u518d\u8bf4\uff0c\u65b0\u623f\u8840\u6848\u7684\u91d1\u5c0f\u7709\u4e0d\u662f\u8fd8\u6d3b\u5f97\u597d\u597d\u7684\u561b\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u6c14\u5f97\u76f4\u54c6\u55e6\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u53ef\u662f\uff0c\u5c0f\u7709\u8fd9\u5b69\u5b50\u88ab\u89c6\u4e3a\u51f6\u624b\uff0c\u4e0d\u80fd\u6d17\u5237\u51a4\u5c48\uff0c \u4f60\u8fd8\u662f\u5bb3\u4e86\u5979\uff01\u201d \u3000\u3000\u5929\u67ab\u9053\u957f\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u5355\u9a6c\u656c\u6587\u4e00\u6b7b\uff0c\u53ea\u6015\u91d1\u5c0f\u7709\u6d3b\u7740\u4e5f\u65e0\u8da3\u5473\uff0c\u8fd8\u4e0d\u5982\u65e9\u6b7b\u4e86\u597d\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u5927\u9a82\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff01\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u4ed6\u731b\u5730\u7ad9\u8d77\u8eab\u5b50\uff0c\u4e3e\u7740\u53cc\u624b\u5411\u5929\u67ab\u9053\u957f\u6251\u8fc7\u53bb\uff0c\u663e\u7136\u662f\u56e0\u4e3a\u9707\u6012\u4e4b\u4e0b\uff0c\u60f3\u6251\u4e0a\u53bb\u75db\u6253\u5929 \u67ab\u4e00\u987f\u3002 \u3000\u3000\u8d3e\u82f1\u5927\u60ca\uff0c\u9053\uff1a\u201c\u4e1c\u4e3b\uff0c\u4e0d\u53ef\uff01\u201d \u3000\u3000\u559d\u58f0\u4e2d\uff0c\u4ed6\u95ea\u8eab\u53bb\u6293\u91d1\u767e\u5e74\u3002 \u3000\u3000\u675c\u671b\u6708\u4e00\u6014\u4e4b\u4e0b\uff0c\u4e5f\u4e00\u95ea\u8eab\uff0c\u5c06\u957f\u5251\u6a2a\u5728\u91d1\u767e\u5e74\u548c\u5929\u67ab\u4e4b\u95f4\uff0c\u6015\u5929\u67ab\u51fa\u5251\u6740\u4e86\u91d1\u767e\u5e74\u3002 \u3000\u3000------------- \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u4e8c\u5341\u4e03\u7ae0 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5385\u4e2d\u54cd\u8d77\u4e86\u4e00\u58f0\u60e8\u53eb\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u7684\u5251\u5df2\u7ecf\u523a\u6740\u4e86\u9ed1\u8863\u6740\u624b\u7684\u8eab\u4f53\u3002 \u3000\u3000\u675c\u671b\u6708\u5927\u6012\uff0c\u6325\u5251\u6251\u4e0a\uff0c\u76f4\u524c\u5929\u67ab\u9053\u957f\u524d\u80f8\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u6ca1\u6599\u5230\u5929\u67ab\u9053\u957f\u7adf\u7136\u6293\u4f4f\u4e86\u8fd9\u4e2a\u673a\u4f1a\uff0c\u6740\u4e86\u4ed6\u552f\u4e00\u7684\u6d3b\u53e3\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u633a\u5251\u76f8\u8fce\u3002 \u3000\u3000\u4ed6\u771f\u5b9e\u7684\u6b66\u529f\u7adf\u7136\u6bd4\u4ed6\u4e4e\u65f6\u6240\u8868\u73b0\u51fa\u6765\u7684\u8981\u9ad8\u5f97\u591a\uff0c\u4e09\u56db\u62db\u4e00\u8fc7\uff0c\u7adf\u5c06\u675c\u671b\u6708\u903c\u9000\u4e86\u4e00 \u6b65\u3002 \u3000\u3000\u675c\u671b\u6708\u540e\u9000\u51e0\u6b65\uff0c\u6df1\u6df1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u773c\u4e2d\u987f\u65f6\u66b4\u5c04\u51fa\u6151\u4eba\u7684\u7cbe\u5149\u3002 \u3000\u3000\u5f88\u663e\u7136\uff0c\u4ed6\u8981\u4f7f\u7528\u201c\u5251\u6d77\u6d74\u9b42\u201d\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u6025\u9053\uff1a\u201c\u4e0d\u53ef\uff01\u675c\u53f8\u4e3b\uff0c\u8981\u6d3b\u53e3\uff01\u201d \u3000\u3000\u5929\u67ab\u72c2\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u8981\u5931\u671b\u4e86\uff01\u201d\u8bf4\u7740\uff0c\u6a2a\u8fc7\u5b9d\u5251\uff0c\u67b6\u5728\u8116\u5b50\u4e0a\uff0c\u53cd\u624b\u4e00 \u6325\uff01 \u3000\u3000\u4e00\u9053\u8840\u7bad\u5c04\u51fa\uff0c\u5728\u5730\u4e0a\u6e85\u5f00\u4e86\u4e00\u5927\u6735\u8840\u82b1\u3002 \u3000\u3000\u5929\u67ab\u9053\u957f\u53cc\u76ee\u5706\u7741\uff0c\u6162\u6162\u5012\u5728\u4e86\u5730\u4e0a\u3002 \u3000\u3000\u4ed6\u6b7b\u4e86\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4ed6\u7684\u786e\u4e0d\u5931\u4e3a\u4e00\u6761\u786c\u6c49\u5b50\uff0c\u53ea\u53ef\u60dc\u8d70\u9519\u4e86\u8def\u4e86\u3002\u201d \u3000\u3000\u770b\u4e86\u770b\u91d1\u767e\u5e74\uff0c\u53c8\u9053\uff1a\u201c\u91d1\u8001\uff0c\u6df1\u591c\u6253\u6270\uff0c\u5b9e\u5728\u662f\u4e0d\u597d\u610f\u601d\uff0c\u8bf7\u91d1\u8001\u89c1\u8c05\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u51b2\u4e0a\u53bb\u72e0\u72e0\u8e22\u4e86\u5929\u67ab\u4e24\u811a\uff0c\u624d\u9053\uff1a\u201c\u603b\u6355\u5934\u66ff\u8001\u592b\u94f2\u9664\u4e86\u5e9c\u4e2d\u5185\u5978\uff0c\u8001\u592b\u611f\u6fc0\u8fd8 \u6765\u4e0d\u53ca\u5462\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6765\u4eba\uff0c\u5e26\u4e0a\u4e24\u4eba\u7684\u5c38\u4f53\uff0c\u6211\u4eec\u8d70\u3002\u201d \u3000\u3000\u8f6c\u9762\u5bf9\u91d1\u767e\u5e74\u9053\uff1a\u201c\u91d1\u8001\uff0c\u6211\u4eec\u544a\u8f9e\u4e86\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u957f\u63d6\u9053\uff1a\u201c\u591a\u8c22\u603b\u6355\u5934\uff0c\u8bf7\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u3001\u56db\u4eba\u6355\u5934\u3001\u5c0f\u6587\u3001\u5c0f\u96c5\u4e00\u884c\u4eba\u56de\u5230\u5e9c\u8859\u4e0d\u4e45\uff0c\u5929\u8272\u5c31\u5927\u4eae\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u6765\u5230\u540e\u9662\u65f6\uff0c\u53ea\u89c1\u9662\u4e2d\u706f\u706b\u901a\u660e\uff0c\u9a6c\u957f\u5c71\u624b\u4e0b\u7684\u90e8\u5c06\u3001\u4eb2\u5175\u548c\u6797\u4e0d\u51e1\u7387\u9886\u7684\u6c5f\u5357\u3001 \u4e2d\u5dde\u4e24\u5927\u5206\u53f8\u7684\u6570\u5341\u597d\u624b\u6b63\u4f0f\u5728\u540e\u9662\u56db\u5468\uff0c\u4e25\u9635\u4ee5\u5f85\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u548c\u738b\u5c11\u537f\u6b63\u5728\u5927\u5385\u91cc\u7126\u6025\u5730\u7b49\u5f85\u7740\uff0c\u5f20\u5b9d\u5584\u4e5f\u5728\u4e00\u8fb9\u966a\u7740\u4ed6\u4eec\uff0c\u548c\u4ed6\u4eec\u8bf4\u4e00\u4e9b\u95f2 \u8bdd\uff0c\u89e3\u89e3\u70e6\u95f7\u3002 \u3000\u3000\u53ea\u6709\u60dc\u7389\u5728\u5385\u524d\u7684\u957f\u5eca\u4e0a\u6162\u6162\u5730\u8d70\u8fc7\u6765\uff0c\u8d70\u8fc7\u53bb\uff0c\u50cf\u662f\u6709\u4ec0\u4e48\u5fc3\u4e8b\u3002 \u3000\u3000\u4e00\u89c1\u7a0b\u5c0f\u8776\uff0c\u738b\u5c11\u537f\u3001\u9a6c\u957f\u5c71\u7acb\u523b\u5c31\u7ad9\u4e86\u8d77\u6765\uff0c\u8fce\u4e0a\u6765\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6b64\u884c\u987a\u5229\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u52c9\u5f3a\u7b97\u5f97\u4e0a\u987a\u5229\uff0c\u4f46\u7ed3\u679c\u4e0d\u80fd\u4ee4\u4eba\u6ee1\u610f\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u4e00\u6014\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff0c\u5929\u67ab\u9053\u957f\u9003\u8d70\u4e86\uff1f\u6ca1\u6709\u6293\u4f4f\u4ed6\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4ed6\u6ca1\u6709\u8dd1\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u662f\u4ed6\u4e0d\u613f\u610f\u62db\u4f9b\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e5f\u62db\u4f9b\u4e86\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u90a3\u8fd8\u6709\u4ec0\u4e48\u4e0d\u6ee1\u610f\u7684\uff1f\u6709\u5929\u67ab\u9053\u957f\u8fd9\u6837\u4e00\u4e2a\u91cd\u8981\u7684\u8bc1\u636e\uff0c\u6740\u624b\u96c6\u56e2\u7b49\u4e8e\u5df2 \u7ecf\u5931\u8d25\uff0c\u8840\u6848\u4e5f\u7834\u4e86\u561b\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4ed6\u8bf4\u51fa\u7684\u90fd\u662f\u6211\u4eec\u5df2\u7ecf\u638c\u63e1\u4e86\u7684\u60c5\u51b5\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u5927\u58f0\u9053\uff1a\u201c\u518d\u5bf9\u4ed6\u8fdb\u884c\u4e25\u5211\u903c\u4f9b\uff01\u6211\u5c31\u4e0d\u4fe1\u6253\u4e0d\u51fa\u4ed6\u7684\u5b9e\u8bdd\u6765\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u662f\u6ca1\u8fd9\u4e2a\u672c\u4e8b\u4e86\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u8bf7\u603b\u6355\u5934\u628a\u4ed6\u4ea4\u7ed9\u6211\uff0c\u6211\u4e00\u5b9a\u4f1a\u8ba9\u4ed6\u5c3d\u5410\u5b9e\u60c5\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u56de\u5934\u9053\uff1a\u201c\u6765\u4eba\uff0c\u5c06\u5929\u67ab\u62ac\u4e0a\u6765\uff01\u201d \u3000\u3000\u9a6c\u957f\u5c71\u4e00\u770b\u5929\u67ab\u9053\u957f\u65e9\u5df2\u6b7b\u53bb\uff0c\u4e0d\u7981\u82e6\u7b11\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u4f60\u8fd9\u4e2a\u73a9\u7b11\u53ef\u5f00\u5927\u4e86\uff01\u55ef\uff0c \u770b\u6837\u5b50\u4ed6\u662f\u81ea\u6740\u7684\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\uff0c\u4ed6\u5148\u6740\u4e86\u90a3\u540d\u9ed1\u8863\u6740\u624b\uff0c\u7136\u540e\u624d\u81ea\u6740\u8eab\u4ea1\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u54a6\uff0c\u4ed6\u8fd8\u633a\u6709\u9aa8\u6c14\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u4e5f\u8bf4\u660e\u6740\u624b\u96c6\u56e2\u7684\u9996\u8111\u662f\u4e2a\u591a\u4e48\u5389\u5bb3\u7684\u4eba\u7269\uff0c\u80fd\u8ba9\u5929\u67ab\u9053\u957f\u8fd9\u6837\u7684\u6c5f\u6e56 \u540d\u4eba\u7518\u613f\u4e3a\u4ed6\u6240\u7528\uff0c\u4f9b\u4ed6\u9a71\u7b56\uff0c\u751a\u81f3\u5b81\u6b7b\u4e5f\u4e0d\u613f\u62db\u51fa\u4ed6\u6765\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u5927\u58f0\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u5929\u67ab\u9053\u957f\u6b7b\u524d\u53e3\u53e3\u58f0\u58f0\u8bf4\u4ed6\u8fdb\u884c\u7684\u662f\u6b63\u4e49\u7684\u4e8b\u4e1a\uff0c\u4e0d\u77e5\u662f\u4ec0 \u4e48\u610f\u601d\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6240\u6709\u7684\u9634\u8c0b\u5bb6\u90fd\u4f1a\u4e3a\u81ea\u5df1\u6240\u8fdb\u884c\u7684\u9634\u8c0b\u62ab\u4e0a\u4e00\u5c42\u6b63\u4e49\u7684\u5e4c\u5b50\uff0c\u4e0d \u7136\u7684\u8bdd\uff0c\u53c8\u600e\u4f1a\u6709\u4eba\u7518\u5fc3\u66ff\u4ed6\u5356\u547d\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u95ee\u5929\u67ab\u662f\u4e0d\u662f\u4ed6\u5728\u67f3\u5824\u4e0a\u57cb\u4f0f\uff0c\u60f3\u523a\u6740\u738b\u5927\u4eba\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u9ed1\u8863\u6740\u624b\u5df2\u7ecf\u8bf4\u4e86\uff0c\u6211\u4e3a\u4ec0\u4e48\u8fd8\u8981\u95ee\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u56e0\u4e3a\u8d3e\u82f1\uff0c\u5982\u679c\u884c\u523a\u738b\u5927\u4eba\u7684\u7684\u786e\u5c31\u662f\u5929\u67ab\uff0c\u90a3\u4e48\u8d3e\u82f1\u80af\u5b9a\u548c\u4ed6\u662f\u4e00\u4f19\u7684\uff0c \u56e0\u4e3a\u4ed6\u5f53\u65f6\u5df2\u770b\u89c1\u4e86\u523a\u5ba2\u7684\u8138\uff0c\u5374\u4e0d\u8bf4\u51fa\u6765\u523a\u5ba2\u662f\u8c01\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u675c\u53f8\u4e3b\u679c\u7136\u660e\u5bdf\u79cb\u6beb\uff0c\u53ea\u662f\uff0c\u8fd9\u4ef6\u4e8b\u5df2\u7ecf\u6ca1\u6709\u518d\u9700\u8ffd\u4e0b\u53bb\u7684\u5fc5\u8981\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u6211\u4eec\u7684\u4e3b\u8981\u76ee\u7684\u662f\u7834\u83b7\u90a3\u4e2a\u5927\u9634\u8c0b\u3002\u201d \u3000\u3000\u8fd9\u53e5\u89e3\u91ca\u4f3c\u4e4e\u6709\u4e9b\u6587\u4e0d\u5bf9\u9898\uff0c\u4f46\u675c\u671b\u6708\u6ca1\u6709\u518d\u8ffd\u95ee\u4e0b\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\uff0c\u4ee5\u603b\u6355\u5934\u7684\u806a\u660e\u624d\u667a\uff0c\u4e00\u5b9a\u4e5f\u60f3\u5230\u4e86\u8fd9\u4e00\u70b9\uff0c\u90a3\u4e48\u5979\u5c06\u4e4b\u6545\u610f\u820d\u53bb\u4e0d\u63d0\uff0c \u81ea\u6709\u5176\u6df1\u610f\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5bf9\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u9a6c\u5927\u4eba\uff0c\u8bf7\u4f60\u4e0b\u4ee4\u8c03\u96c6\u4e00\u5343\u7cbe\u9510\u6c34\u5e08\u524d\u6765\u5e9c\u8859\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u4e00\u6014\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5bf9\uff0c\u73b0\u5728\uff0c\u800c\u4e14\u662f\u8d8a\u5feb\u8d8a\u597d\uff01\u201d \u3000\u3000\u9a6c\u957f\u5c71\u7ad9\u8d77\u8eab\uff0c\u9053\uff1a\u201c\u597d\uff0c\u9a6c\u67d0\u8fd9\u5c31\u53bb\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u9a6c\u5927\u4eba\u5c31\u7559\u5728\u5e9c\u8859\u91cc\u5427\uff0c\u53ea\u8981\u5199\u4e00\u7eb8\u547d\u4ee4\uff0c\u4ea4\u5f85\u963f\u6a2a\u3001\u963f\u4fdd\u53bb\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u603b\u6355\u5934\u662f\u62c5\u5fc3\u6740\u624b\u4eec\u4f1a\u5728\u534a\u8def\u88ad\u51fb\u9a6c\u67d0\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u662f\u539f\u56e0\u4e4b\u4e00\uff0c\u7b2c\u4e8c\u4e2a\u539f\u56e0\u662f\uff0c\u7531\u963f\u6a2a\u3001\u963f\u4fdd\u53bb\uff0c\u901f\u5ea6\u4e5f\u4f1a\u5feb\u5f97\u591a\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u597d\u5427\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u8ba9\u5f20\u5b9d\u5584\u6367\u4e0a\u6587\u623f\u56db\u5b9e\uff0c\u5306\u5306\u5199\u4e86\u51e0\u53e5\u8bdd\uff0c\u638f\u51fa\u6000\u4e2d\u4e00\u65b9\u77f3\u5370\u5728\u7eb8\u4e0a\u76d6\u4e86\u4e00\u4e2a\u5370 \u51fd\uff0c\u4ea4\u7ed9\u4e86\u963f\u6a2a\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u963f\u6a2a\uff0c\u5feb\u53bb\u5feb\u56de\uff01\u201d \u3000\u3000\u963f\u6a2a\u9053\uff1a\u201c\u603b\u6355\u5934\u653e\u5fc3\uff01\u201d \u3000\u3000\u62c9\u7740\u963f\u4fdd\uff0c\u5982\u98de\u5954\u51fa\u3002 \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4e3a\u4ec0\u4e48\u8981\u8c03\u8fd9\u4e48\u591a\u6c34\u5e08\u6765\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u4e24\u5929\u662f\u6700\u6700\u5173\u952e\u7684\u4e24\u5929\uff0c\u800c\u4e14\uff0c\u6211\u89c9\u5f97\u6709\u5fc5\u8981\u5c06\u4e00\u4e9b\u60c5\u51b5\u5bf9\u8bf8\u4f4d\u505a \u4e00\u4e2a\u4ecb\u7ecd\uff0c\u8c03\u6c34\u5e08\u6765\uff0c\u5f53\u7136\u662f\u8981\u5c06\u5e9c\u8859\u56f4\u4e2a\u6c34\u6cc4\u4e0d\u901a\uff0c\u4ee5\u9632\u6cc4\u5bc6\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u603b\u6355\u5934\u7ec8\u4e8e\u8981\u5c06\u4e8b\u60c5\u548c\u76d8\u6258\u51fa\u5566\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u7b11\u9053\uff1a\u201c\u662f\u7684\uff0c\u5c91\u53f8\u4e3b\uff0c\u8fd9\u4e48\u591a\u5929\u90fd\u7b49\u4e86\uff0c\u53ea\u8fd9\u4e00\u523b\uff0c\u4f60\u4e0d\u4f1a\u7b49\u4e0d\u53ca\u4e86\u5427\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u5927\u7b11\u9053\uff1a\u201c\u7b49\u5f97\u53ca\uff0c\u7b49\u5f97\u53ca\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u8d70\u5230\u60dc\u7389\u8eab\u8fb9\uff0c\u9053\uff1a\u201c\u60dc\u7389\uff0c\u91d1\u5c0f\u7709\u7684\u60c5\u51b5\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u6628\u665a\uff0c\u6211\u4eec\u4e00\u76f4\u8c08\u5230\u540e\u534a\u591c\uff0c\u5979\u624d\u7761\uff0c\u73b0\u5728\u7761\u5f97\u6b63\u9999\u5462\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5f88\u597d\uff0c\u7b49\u4e00\u4f1a\u513f\u6c34\u5e08\u6765\u4e86\uff0c\u4f60\u53bb\u70b9\u4e86\u5979\u7684\u660f\u7761\u7a74\uff0c\u8ba9\u5979\u518d\u591a\u7761\u534a\u5929\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u662f\u3002\u53ef\u662f\uff0c\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\u5462\uff1f\u96be\u9053\u2026\u2026\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u4e0d\u8981\u4e71\u731c\uff0c\u53ea\u4e0d\u8fc7\u6709\u4e9b\u8bdd\u662f\u4e0d\u80fd\u8ba9\u5979\u542c\u89c1\u7684\u3002\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\uff0c\u5979\u53c8\u95ee\u9053\uff1a\u201c\u91d1\u5c0f\u7709\u56de\u5fc6\u8d77\u4ec0\u4e48\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u6211\u4e00\u76f4\u5728\u7528\u795e\u533b\u95e8\u7edd\u5b66\u8c03\u52a8\u5979\u7684\u8bb0\u5fc6\u529b\uff0c\u636e\u5979\u56de\u5fc6\uff0c\u5728\u8840\u6848\u53d1\u751f\u7684\u524d\u51e0\u5929\uff0c \u4e00\u76f4\u6ca1\u6709\u964c\u751f\u4eba\u63a5\u8fd1\u8fc7\u5979\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5929\u67ab\u9053\u957f\u5462\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u597d\u50cf\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u60dc\u9053\uff1a\u201c\u4e5f\u5c31\u662f\u8bf4\u9664\u4e86\u5979\u7684\u8d34\u8eab\u4f8d\u5973\u3001\u4e2b\u73af\u5a46\u5b50\u4e4b\u5916\uff0c\u53ea\u6709\u91d1\u767e\u5e74\u4e00\u4eba\u63a5\u8fd1\u8fc7\u5979\uff0c \u5bf9\u5417\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u597d\u50cf\u662f\u7684\uff0c\u4e0d\u8fc7\uff0c\u5979\u8bf4\u5c31\u5728\u8840\u6848\u7684\u5934\u4e00\u5929\uff0c\u91d1\u5e9c\u66fe\u8bf7\u4e86\u4e00\u4e2a\u90ce\u4e2d\u6765\u7ed9\u91d1\u767e\u5e74 \u6cbb\u75c5\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e24\u773c\u4e8c\u5144\uff0c\u9053\uff1a\u201c\u90ce\u4e2d\uff1f\u4ec0\u4e48\u6837\u7684\u90ce\u4e2d\uff1f\u91d1\u5c0f\u7709\u89c1\u8fc7\u90a3\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u91d1\u5c0f\u7709\u8bf4\uff0c\u90a3\u5929\u5979\u53bb\u91d1\u767e\u5e74\u4e66\u623f\u60f3\u4e0e\u91d1\u767e\u5e74\u8bf4\u8bf4\u8bdd\uff0c\u8d70\u5230\u95e8\u8fb9\uff0c\u770b\u89c1 \u90ce\u4e2d\u6b63\u66ff\u91d1\u767e\u5e74\u6cbb\u75c5\uff0c\u5c31\u56de\u81ea\u5df1\u623f\u95f4\u53bb\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u7b49\u4e00\u4f1a\u4f60\u518d\u8ba9\u5979\u597d\u597d\u56de\u5fc6\u4e00\u4e0b\u90a3\u4e2a\u90ce\u4e2d\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u963f\u6a2a\u3001\u963f\u4fdd\u5927\u6b65\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u4e24\u4f4d\u597d\u5feb\u5440\uff0c\u5175\u8c03\u6765\u4e86\uff1f\u201d \u3000\u3000\u963f\u6a2a\u9053\uff1a\u201c\u6b63\u5728\u8def\u4e0a\uff0c\u9a6c\u4e0a\u5c31\u5230\u3002\u201d \u3000\u3000\u963f\u4fdd\u9053\uff1a\u201c\u6211\u4eec\u62c5\u5fc3\u5e9c\u8859\u6709\u53d8\uff0c\u5148\u8d76\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\uff01\u9a6c\u5927\u4eba\uff0c\u8bf7\u4f60\u4f20\u547d\u4f60\u7684\u90e8\u5c06\uff0c\u6c34\u5e08\u4e00\u5230\uff0c\u7acb\u523b\u8ba9\u4ed6\u4eec\u5e26\u5175\u5c06\u4e8c\u5802\u5230\u540e \u9662\u8fd9\u4e00\u5e26\u7d27\u7d27\u56f4\u4f4f\uff0c\u5907\u597d\u5f3a\u5f13\u786c\u5f29\uff0c\u4efb\u4f55\u4eba\u6562\u786c\u95ef\u5e9c\u8859\uff0c\u7acb\u5373\u683c\u6740\uff0c\u4e0d\u7528\u7980\u544a\uff01\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u9a6c\u67d0\u8fd9\u5c31\u53bb\u529e\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f20\u4e0b\u4ee4\u540e\uff0c\u8bf7\u9a6c\u5927\u4eba\u8d76\u56de\u5927\u5385\uff0c\u6709\u8981\u4e8b\u76f8\u544a\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u7b11\u9053\uff1a\u201c\u603b\u6355\u5934\u653e\u5fc3\uff0c\u9a6c\u67d0\u675c\u5c91\u53f8\u4e3b\u53ea\u6015\u66f4\u7740\u6025\u5462\uff01\u201d \u3000\u3000\u8fd9\u8bdd\u8bf4\u5f97\u4f17\u4eba\u90fd\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\u513f\uff0c\u4e00\u5343\u540d\u7cbe\u9510\u6c34\u5e08\u5df2\u7ecf\u5f00\u8fdb\u4e86\u5e9c\u8859\uff0c\u5728\u9a6c\u957f\u5c71\u7684\u6307\u6325\u4e4b\u4e0b\uff0c\u5f88\u5feb\u5c31\u6392\u597d\u4e86\u9635\u5f62\uff0c \u505a\u597d\u4e86\u968f\u65f6\u6218\u6597\u7684\u51c6\u5907\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u3001\u738b\u5c11\u537f\u3001\u9a6c\u957f\u5c71\u3001\u56db\u5927\u6355\u5934\u3001\u5c0f\u6587\u3001\u5c0f\u96c5\u3001\u60dc\u7389\u3001\u963f\u6a2a\u3001\u963f\u4fdd\u3001\u6797\u4e0d\u51e1\uff0c\u5168\u90fd \u96c6\u4e2d\u5750\u5728\u5927\u5385\u91cc\uff0c\u5c0f\u6587\u548c\u5c0f\u96c5\u5f00\u4e0a\u4e86\u5385\u95e8\uff0c\u4e00\u5de6\u4e00\u53f3\u5b88\u5728\u95e8\u540e\u3002 \u3000\u3000\u4e00\u60f3\u5230\u591a\u65e5\u7684\u7591\u56e2\u5c31\u8981\u88ab\u89e3\u5f00\uff0c\u4f17\u4eba\u5fc3\u91cc\u90fd\u4e0d\u7981\u6709\u4e9b\u7d27\u5f20\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u9996\u5148\u58f0\u660e\u4e00\u70b9\uff0c\u5173\u4e8e\u8fd9\u4e2a\u7d27\u8981\u7684\u8be6\u60c5\u3001\u5185\u60c5\uff0c\u6211\u5e76\u4e0d\u662f\u4e00\u5f00\u59cb\u5c31\u77e5 \u9053\uff0c\u5373\u4fbf\u5230\u4e86\u73b0\u5728\uff0c\u6211\u4e5f\u4e0d\u662f\u5341\u5206\u6e05\u695a\uff0c\u4e0d\u8fc7\uff0c\u6211\u4e0d\u4e45\u524d\u5012\u63a5\u5230\u4e86\u65b0\u7684\u547d\u4ee4\uff0c\u6240\u4ee5\u624d\u5bf9\u5185\u60c5 \u6709\u4e86\u8fdb\u4e00\u6b65\u7684\u4e86\u89e3\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u7b11\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u5c31\u5feb\u8bf4\u5427\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u4f1a\u8bf4\u7684\uff0c\u4e0d\u8fc7\uff0c\u6211\u60f3\u5148\u542c\u542c\u8bf8\u4f4d\u5bf9\u8fd9\u4e2a\u6848\u5b50\u7684\u770b\u6cd5\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u4e00\u5f00\u59cb\u738b\u67d0\u8ba4\u4e3a\u65b0\u623f\u8840\u6848\u53ea\u662f\u4e00\u4e2a\u666e\u901a\u7684\u884c\u51f6\u6740\u4eba\u6848\uff0c\u76f4\u5230\u5929\u67ab \u9053\u957f\u60f3\u884c\u523a\u738b\u67d0\uff0c\u6211\u624d\u60f3\u8d77\u5c31\u5728\u65b0\u623f\u8840\u6848\u53d1\u751f\u7684\u5934\u4e00\u5929\u665a\u4e0a\uff0c\u5df2\u7ecf\u6709\u591c\u884c\u4eba\u6f5c\u884c\u5e9c\u8859\uff0c\u610f\u56fe \u5bf9\u6211\u884c\u523a\u4e86\uff0c\u8fd9\u65f6\uff0c\u6211\u624d\u611f\u89c9\u5230\u8fd9\u4ef6\u6848\u5b50\u5e76\u4e0d\u7b80\u5355\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f46\u738b\u5927\u4eba\u8fd8\u662f\u6ca1\u6709\u60f3\u5230\u8840\u6848\u4f1a\u4e0e\u4f60\u6709\u76f4\u63a5\u7684\u5173\u7cfb\uff0c\u5bf9\u5427\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7684\u786e\u6ca1\u6709\u60f3\u5230\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u540e\u6765\u5462\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u540e\u6765\uff0c\u7ecf\u603b\u6355\u5934\u70b9\u9192\uff01\u8fd9\u624d\u660e\u767d\u8840\u6848\u662f\u4eba\u4e3a\u5236\u9020\u7684\uff0c\u5176\u76ee\u7684\u662f\u60f3\u5f15\u8d77\u9a6c\u5927 \u4eba\u4e0e\u91d1\u767e\u5e74\u706b\u62fc\uff0c\u7978\u53ca\u672c\u5b98\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u4e14\u6162\uff0c\u603b\u6355\u5934\uff0c\u8fd9\u9a6c\u67d0\u5c31\u4e0d\u61c2\u4e86\uff0c\u9a6c\u67d0\u4e0e\u91d1\u767e\u5e74\u706b\u62fc\uff0c\u5982\u4f55\u4f1a\u7d2f\u53ca\u738b\u5927\u4eba \u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u626c\u5dde\u5341\u5927\u8c6a\u5bcc\uff0c\u5728\u4eac\u91cc\u90fd\u6709\u5f88\u786c\u7684\u540e\u53f0\uff0c\u800c\u4e14\u4ea4\u7ed3\u6781\u5e7f\uff0c\u4e00\u65e6\u9a6c\u5927\u4eba\u538b\u4e0d \u4e0b\u6012\u706b\uff0c\u901e\u4e00\u65f6\u4e4b\u6c14\uff0c\u4e0e\u91d1\u767e\u5e74\u52a8\u4e0a\u4e86\u624b\uff0c\u671d\u5ef7\u4e00\u5b9a\u4f1a\u5bf9\u9a6c\u5927\u4eba\u4e25\u52a0\u60e9\u5904\u7684\uff0c\u4f46\u9a6c\u5927\u4eba\u81ea\u8ba4 \u662f\u4e3a\u5b50\u62a5\u4ec7\uff0c\u5e76\u6ca1\u6709\u9519\uff0c\u5bf9\u671d\u5ef7\u7684\u60e9\u7f5a\u4e00\u5b9a\u4f1a\u5fc3\u6709\u4e0d\u670d\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u5f88\u53ef\u80fd\u4f1a\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5982\u679c\u90a3\u65f6\u6709\u4eba\u6311\u5506\u9a6c\u5927\u4eba\u8c0b\u53cd\uff0c\u9a6c\u5927\u4eba\u4f1a\u662f\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u670d\u5f53\u7136\u4e0d\u670d\uff0c\u4f46\u8c0b\u53cd\u4e4b\u4e8b\uff0c\u9a6c\u67d0\u51b3\u4e0d\u4f1a\u5e72\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u9a6c\u5927\u4eba\uff0c\u5982\u679c\u6311\u5506\u4f60\u7684\u4eba\u4e5f\u662f\u671d\u5ef7\u91cd\u81e3\uff0c\u738b\u516c\u4faf\u5b50\u5462\uff1f\u5e76\u4e14\u4ed6\u8bb8\u8bfa\u4e00\u65e6 \u4ed6\u5750\u4e0a\u9f99\u5ef7\uff0c\u4e00\u5b9a\u4f1a\u91cd\u8d4f\u9a6c\u5927\u4eba\u5462\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u6014\u4e86\u4e00\u6014\uff0c\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u8fd9\u8bdd\u5168\u662f\u63a8\u6d4b\uff0c\u800c\u4e14\uff0c\u4eca\u5929\u6211\u4eec\u5728\u8fd9\u91cc\u8bf4\u7684\u8bdd\u4e00\u51fa\u6b64\u95e8\uff0c\u5927\u5bb6\u90fd\u5fc5\u987b\u5fd8 \u8bb0\u3002\u9a6c\u5927\u4eba\uff0c\u6211\u4eec\u53ea\u8bf4\u53ef\u80fd\uff0c\u679c\u771f\u6709\u8fd9\u79cd\u60c5\u51b5\uff0c\u9a6c\u5927\u4eba\u53cd\u4e0d\u53cd\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u4ece\u5e38\u7406\u4e0a\u770b\uff0c\u5f53\u7136\u6709\u53ef\u80fd\uff0c\u53ea\u662f\u9a6c\u67d0\u5bf9\u7687\u4e0a\u4e00\u7247\u5fe0\u5fc3\uff0c\u6307\u65e5\u53ef\u8868\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u597d\u5566\uff0c\u9a6c\u5927\u4eba\uff0c\u4e0d\u7528\u8868\u4e86\uff0c\u4f60\u5bf9\u7687\u4e0a\u7684\u5fe0\u5fc3\uff0c\u5927\u5bb6\u90fd\u77e5\u9053\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u4e0d\u7981\u4e5f\u6709\u4e9b\u597d\u7b11\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u63a5\u7740\u9053\uff1a\u201c\u9a6c\u5927\u4eba\u4e00\u53cd\uff0c\u738b\u5927\u4eba\u8eab\u4e3a\u626c\u5dde\u77e5\u5e9c\uff0c\u53ea\u6709\u4e24\u6761\u8def\u53ef\u8d70\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e00\u662f\u6b7b\uff0c\u4e00\u662f\u964d\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u70b9\u5934\u9053\uff1a\u201c\u9a6c\u67d0\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u6740\u624b\u96c6\u56e2\u540e\u6765\u7adf\u516c\u7136\u591c\u88ad\u5e9c\u8859\uff0c\u5fc5\u987b\u7f6e\u738b\u67d0\u4e8e\u6b7b\u5730\uff0c\u6628\u591c\u66f4\u662f\u610f\u6b32 \u884c\u523a\u957f\u5c71\u5144\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u60f3\uff0c\u8fd9\u5df2\u662f\u4ed6\u4eec\u7684\u7b2c\u4e8c\u65b9\u6848\uff0c\u56e0\u4e3a\u60c5\u51b5\u53d1\u751f\u4e86\u6839\u672c\u7684\u53d8\u5316\uff0c\u4ed6\u4eec\u53ea\u80fd\u6267 \u884c\u7b2c\u4e8c\u65b9\u6848\u4e86\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4ec0\u4e48\u6839\u672c\u7684\u53d8\u5316\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6c89\u541f\u4e86\u4e00\u4f1a\u513f\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u5148\u4e0d\u8bf4\uff0c\u56db\u5927\u6355\u5934\uff0c\u4f60\u4eec\u4e5f\u8bf4\u4e00\u8bf4\u561b\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u8ba4\u4e3a\uff0c\u6b64\u4e8b\u7684\u5173\u952e\u5728\u4e8e\u90a3\u4e2a\u5047\u68a6\u5e7b\u4e4b\u5200\u7684\u771f\u5b9e\u8eab\u5206\uff0c\u4ee5\u53ca\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e00 \u5b9a\u8981\u5ac1\u7978\u7ed9\u68a6\u5e7b\u4e4b\u5200\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u90a3\u53ea\u662f\u56e0\u4e3a\u68a6\u5e7b\u4e4b\u5200\u5728\u5341\u5e74\u4e4b\u524d\u540d\u6ee1\u6c5f\u6e56\uff0c\u800c\u4e14\u5df2\u6210\u4e3a\u4e00\u4e2a\u4e0d\u8d25\u7684\u795e\u8bdd\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u6837\u4e00\u4ef6\u5927\u4e8b\uff0c\u4e5f\u7684\u786e\u53ea\u6709\u5ac1\u7978\u7ed9\u68a6\u5e7b\u4e4b\u5200\u8fd9\u6837\u7684\u7edd\u9876\u9ad8\u624b\uff0c\u624d\u80fd \u8ba9\u4eba\u4fe1\u670d\u3002\u95ee\u9898\u662f\uff0c\u68a6\u5e7b\u4e4b\u5200\u4e3a\u4ec0\u4e48\u4e0d\u51fa\u6765\u8fa8\u89e3\u5462\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u65e9\u5df2\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u4e0d\u4f1a\uff0c\u5982\u679c\u4ed6\u65e9\u6b7b\u4e86\uff0c\u53c8\u5982\u4f55\u5ac1\u7978\u4e00\u4e2a\u6b7b\u4eba\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5bf9\uff01\u68a6\u5e7b\u4e4b\u5200\u6ca1\u6709\u6b7b\uff0c\u53ea\u4e0d\u8fc7\uff0c\u5341\u5e74\u524d\u5728\u4e00\u573a\u529b\u91cf\u60ac\u6b8a\u7684\u51b3\u6218\u4e4b\u4e2d\uff0c\u4ed6\u88ab \u4eba\u7528\u5978\u8ba1\u52a0\u5bb3\uff0c\u5df2\u7ecf\u6b66\u529f\u5168\u5931\u4e86\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u8fd9\u4e2a\u60c5\u51b5\u7a0b\u603b\u6355\u5934\u662f\u5982\u4f55\u77e5\u9053\u7684\uff1f\u4ec0\u4e48\u65f6\u5019\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u5341\u5e74\u524d\u5c31\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u4e00\u5f00\u59cb\u5c31\u77e5\u9053\u90a3\u4e2a\u9ed1\u8863\u8499\u9762\u4eba\u4e0d\u662f\u771f\u6b63\u7684\u68a6\u5e7b\u4e4b\u5200\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u5929\u67ab\u9053\u957f\u6ca1\u6709\u8bf4\u8c0e\uff0c\u6e56\u8fb9\u7cbe\u820d\u5185\u7684\u786e\u4f4f\u7740\u68a6\u5e7b\u4e4b\u5200\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5bf9\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u68a6\u5e7b\u4e4b\u5200\u6b66\u529f\u5df2\u5c3d\u5931\uff0c\u90a3\u4e48\u5929\u67ab\u9053\u957f\u624b\u4e0b\u90a3\u56db\u540d\u9ad8\u624b\u53c8\u662f\u8c01\u6740\u7684\u5462\uff1f\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u5c91\u5144\uff0c\u4f60\u5fd8\u4e86\u90a3\u4e2a\u94f6\u8863\u4eba\u4e86\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u4e00\u62cd\u5927\u817f\uff0c\u9053\uff1a\u201c\u554a\u5440\uff01\u770b\u6211\u8fd9\u4e2a\u8bb0\u6027\uff0c\u603b\u6355\u5934\uff0c\u90a3\u4e2a\u94f6\u8863\u4eba\u662f\u68a6\u5e7b\u4e4b\u5200\u7684\u4ec0\u4e48 \u4eba\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u4ed6\u7684\u4e8c\u5f1f\u5b50\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u539f\u6765\u6740\u624b\u96c6\u56e2\u662f\u771f\u7684\u4e00\u76f4\u5728\u627e\u68a6\u5e7b\u4e4b\u5200\uff0c\u56e0\u4e3a\u4ed6\u4eec\u53ea\u6709\u5c06\u4ed6\u5148\u6293\u5230\u624b\uff0c\u624d \u80fd\u5728\u4e8b\u6210\u4e4b\u540e\uff0c\u5c06\u4ed6\u629b\u51fa\u6765\u505a\u66ff\u7f6a\u7f94\u7f8a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u8fc7\uff0c\u91d1\u5e9c\u90a3\u56db\u540d\u9ad8\u624b\u53ef\u4e0d\u662f\u94f6\u8863\u4eba\u6740\u7684\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u90a3\u4f1a\u662f\u8c01\uff1f\u201d \u3000\u3000\u963f\u6a2a\u3001\u963f\u4fdd\u7b11\u9053\uff1a\u201c\u662f\u6211\u4eec\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u53c8\u662f\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u4eec\uff1f\u4f60\u4eec\u6740\u7684\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u65e9\u5c31\u5728\u6000\u7591\u5929\u67ab\u4e86\uff0c\u6240\u4ee5\u5f53\u4ed6\u4e00\u5b9a\u8981\u6211\u4eb2\u53bb\u4e3b\u6301\u884c\u52a8\u65f6\uff0c\u6211\u5df2\u731c \u5230\u4ed6\u5fc5\u5b9a\u4f1a\u8ba9\u4eba\u5148\u52a8\u624b\u6740\u4e86\u68a6\u5e7b\u4e4b\u5200\uff0c\u6240\u4ee5\u5c31\u98de\u9e3d\u4f20\u4e66\uff0c\u8ba9\u963f\u6a2a\u963f\u4fdd\u4e8c\u4eba\u53bb\u6551\u68a6\u5e7b\u4e4b\u5200\u548c\u94f6 \u8863\u4eba\uff01\u201d \u3000\u3000\u963f\u6a2a\u9053\uff1a\u201c\u6211\u4eec\u8d76\u53bb\u65f6\uff0c\u91d1\u5e9c\u56db\u5927\u9ad8\u624b\u5df2\u7ecf\u95ef\u8fdb\u4e86\u7cbe\u820d\uff0c\u94f6\u8863\u4eba\u6b63\u6d74\u8840\u82e6\u6218\uff0c\u5df2\u7ecf\u53d7\u4e86 \u4e09\u5904\u5251\u4f24\uff0c\u4e00\u89c1\u6211\u4eec\u8fdb\u53bb\uff0c\u56db\u4eba\u4e5f\u60f3\u6740\u4e86\u6211\u4eec\u706d\u53e3\uff0c\u6211\u4eec\u4fbf\u6253\u53d1\u4ed6\u4eec\u4e0a\u8def\u4e86\u3002\u201d \u3000\u3000\u53ef\u4ee5\u60f3\u50cf\u5f53\u65f6\u7684\u60c5\u666f\u4e00\u5b9a\u662f\u975e\u5e38\u5371\u9a8c\u7684\uff0c\u4f46\u5728\u963f\u6a2a\u53e3\u4e2d\u8bf4\u6765\uff0c\u5374\u662f\u5341\u5206\u7684\u5e73\u6de1\u65e0\u5947\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u96be\u9053\u68a6\u5e7b\u4e4b\u5200\u662f\u7ad9\u5728\u6211\u4eec\u8fd9\u4e00\u8fb9\u7684\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4fbf\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u4e00\u76f4\u662f\u5728\u5229\u7528\u4ed6\u7684\u624d\u667a\u548c\u4e30\u5bcc\u7684\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u6307\u5bfc\u6211\u7834\u6848\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u73b0\u5728\u4ed6\u4eec\u5728\u54ea\u91cc\uff1f\u8981\u4e0d\u8981\u8ba9\u4ed6\u4eec\u4e5f\u5230\u5e9c\u8859\u6765\uff0c\u4ee5\u514d\u518d\u88ab\u6740\u624b\u96c6\u56e2\u53d1\u73b0\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u5c06\u4ed6\u4eec\u5b89\u6392\u5728\u4e00\u4e2a\u975e\u5e38\u5b89\u5168\u7684\u5730\u65b9\uff0c\u800c\u4e14\uff0c\u5c31\u662f\u73b0\u5728\u6740\u624b\u96c6\u56e2\u627e \u5230\u4ed6\u4eec\uff0c\u4e5f\u53ea\u4f1a\u662f\u81ea\u52a8\u53bb\u9001\u6b7b\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u96be\u9053\u68a6\u5e7b\u4e4b\u5200\u7684\u6b66\u529f\u53c8\u795e\u5947\u5730\u6062\u590d\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u542b\u7b11\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u65e2\u7136\u73b0\u5728\u4e8b\u60c5\u5df2\u7ecf\u660e\u4e86\uff0c\u6211\u4eec\u662f\u4e0d\u662f\u8be5\u7834\u6848\u7f09\u51f6\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u884c\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4eec\u5236\u9020\u8fd9\u4e2a\u8840\u6848\uff0c\u662f\u4e3a\u90a3\u4e2a\u5927\u9634\u8c0b\u5236\u9020\u673a\u4f1a\uff0c\u53ea\u6709\u5f7b\u5e95\u63ed\u9732\u90a3\u4e2a\u5927 \u9634\u8c0b\uff0c\u6211\u4eec\u624d\u7b97\u5b8c\u6210\u4e86\u4efb\u52a1\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u5230\u5e95\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u9634\u8c0b\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u8981\u8bf4\u8d77\u6765\uff0c\u53ef\u5c31\u957f\u4e86\uff0c\u8bf8\u4f4d\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\u4e24\u5e74\u4e4b\u524d\uff0c\u4eac\u57ce\u91cc\u53d1\u751f\u4e86\u4e00\u4ef6\u5927 \u4e8b\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u603b\u6355\u5934\u6307\u7684\u662f\u5e9f\u592a\u5b50\u7684\u4e8b\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5bf9\uff0c\u5f53\u65f6\uff0c\u6709\u4e03\u516b\u4f4d\u671d\u5ef7\u91cd\u81e3\u548c\u4e00\u6279\u5ba6\u5b98\uff0c\u5728\u4e0d\u5230\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\u5185\u4e00\u5171\u7ed9 \u7687\u5e1d\u4e0a\u4e86\u4e03\u5341\u591a\u9053\u594f\u6298\uff0c\u5185\u5bb9\u90fd\u662f\u8bf4\u592a\u5b50\u751f\u6d3b\u8352\u6deb\u65e0\u5ea6\uff0c\u6ca1\u6709\u8eab\u4e3a\u4eba\u541b\u7684\u624d\u80fd\uff0c\u8981\u6c42\u5e9f\u592a\u5b50 \u800c\u53e6\u7acb\u5176\u4ed6\u7687\u5b50\u4e3a\u592a\u5b50\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u9a6c\u67d0\u4e5f\u77e5\u9053\uff0c\u540e\u6765\u597d\u50cf\u662f\u4e0d\u4e86\u4e86\u4e4b\u4e86\u5427\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\uff0c\u7687\u4e0a\u770b\u4e86\u594f\u6298\u540e\uff0c\u5341\u5206\u9707\u6012\uff0c\u5c06\u592a\u5b50\u72e0\u72e0\u53f1\u8d23\u4e00\u756a\uff0c\u5e76\u6d3e\u4eba\u67e5\u8bbf\u592a \u5b50\u7684\u52a3\u8ff9\uff0c\u4f46\u67e5\u6765\u67e5\u53bb\uff0c\u4ec0\u4e48\u4e5f\u6ca1\u6709\u67e5\u51fa\u6765\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u662f\u90a3\u4e9b\u91cd\u81e3\u548c\u5ba6\u5b98\u60f3\u9677\u5bb3\u592a\u5b50\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u7687\u4e0a\u4e5f\u5f88\u5947\u602a\uff0c\u56e0\u4e3a\u8fd9\u4e9b\u91cd\u81e3\u548c\u5ba6\u5b98\u4e00\u5411\u90fd\u662f\u5f88\u803f\u76f4\u7684\u4eba\uff0c\u7edd\u4e0d\u4f1a\u65e0\u7f18\u65e0 \u6545\u9677\u5bb3\u592a\u5b50\uff0c\u4fbf\u53ec\u96c6\u8fd9\u4e9b\u4eba\uff0c\u95ee\u4ed6\u4eec\u594f\u6298\u4e0a\u6240\u5199\u7684\u592a\u5b50\u7684\u8bf8\u822c\u52a3\u8ff9\u662f\u4e0d\u662f\u4ed6\u4eec\u773c\u775b\u6240\u89c1\uff0c\u4eb2 \u8033\u6240\u95fb\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u7ed3\u679c\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u7ed3\u679c\uff0c\u4ed6\u4eec\u90fd\u8bf4\u662f\u6536\u5230\u4e86\u5341\u51e0\u5c01\u533f\u540d\u7684\u4fe1\u4ef6\uff0c\u592a\u5b50\u7684\u6240\u8c13\u52a3\u8ff9\uff0c\u5168\u90fd\u662f\u8fd9 \u4e9b\u4fe1\u4ef6\u4e0a\u5199\u7684\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u8fd9\u4e9b\u5927\u81e3\uff0c\u4e3a\u5b98\u4e5f\u592a\u7cca\u6d82\u4e86\uff0c\u50cf\u8fd9\u79cd\u4e8b\u60c5\uff0c\u4e0d\u4e8b\u5148\u67e5\u4e00\u67e5\uff0c\u600e\u4e48\u884c\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u51e0\u4f4d\u91cd\u81e3\u8fd8\u771f\u6697\u4e2d\u6d3e\u4eba\u67e5\u8fc7\uff0c\u53d1\u73b0\u4fe1\u4e2d\u6240\u8bf4\u7684\u7adf\u7136\u90fd\u786e\u6709\u5176\u4e8b\uff0c\u53ea\u662f\u4e3a\u6076 \u4e4b\u4eba\u5f53\u65f6\u90fd\u662f\u8499\u7740\u9762\uff0c\u6240\u4ee5\u672c\u4e3b\u5e76\u4e0d\u77e5\u9053\u52a0\u5bb3\u4ed6\u4eec\u7684\u5230\u5e95\u662f\u4e9b\u4ec0\u4e48\u4eba\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u540e\u6765\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u540e\u6765\uff0c\u7687\u4e0a\u4e5f\u6ca1\u529e\u6cd5\uff0c\u53ea\u597d\u5c06\u8fd9\u4ef6\u4e8b\u4ea4\u7ed9\u4e86\u5211\u90e8\u3002\u592a\u5b50\u5f53\u7136\u4e5f\u6ca1\u6709\u88ab\u5e9f\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u6848\u5b50\u5230\u4e86\u5211\u90e8\uff0c\u63a5\u624b\u7684\u4e00\u5b9a\u5c31\u662f\u7a0b\u603b\u6355\u5934\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7ecf\u8fc7\u4e24\u5e74\u7684\u660e\u67e5\u6697\u8bbf\uff0c\u6211\u7ec8\u4e8e\u53d1\u73b0\u4e86\u4e00\u4e2a\u9634\u8c0b\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4ec0\u4e48\u9634\u8c0b\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6709\u4eba\u60f3\u53d6\u592a\u5b50\u800c\u4ee3\u4e4b\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u56db\u5ea7\u7686\u60ca\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8bf8\u4f4d\uff0c\u6211\u518d\u6b21\u58f0\u660e\uff0c\u4eca\u5929\u5728\u8fd9\u91cc\u89e3\u8bf4\u7684\u8bdd\uff0c\u5168\u90fd\u662f\u63a8\u6d4b\uff0c\u5728\u6ca1\u6709\u62ff\u5230\u771f\u51ed \u5b9e\u636e\u4e4b\u524d\uff0c\u6211\u662f\u59d1\u5984\u8a00\u4e4b\uff0c\u8bf8\u4f4d\u4e14\u5219\u8981\u59d1\u5984\u542c\u4e4b\uff0c\u4e00\u51fa\u6b64\u95e8\uff0c\u5c31\u5fc5\u987b\u5f97\u5fd8\u6389\uff01\u201d \u3000\u3000\u4f17\u4eba\u7eb7\u7eb7\u9053\uff1a\u201c\u603b\u6355\u5934\u653e\u5fc3\u5427\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\u7684\u610f\u601d\u662f\u8bf4\uff0c\u8fd9\u4f4d\u60f3\u53d6\u592a\u5b50\u800c\u4ee3\u4e4b\u7684\u4eba\uff0c\u6211\u4eec\u6682\u65f6\u79f0\u4ed6\u4e3a\u67d0\u738b\u7237\u5427\uff0c \u67d0\u738b\u7237\u4e00\u5b9a\u662f\u4e00\u8ba1\u4e0d\u6210\uff0c\u53c8\u751f\u4e00\u8ba1\uff0c\u60f3\u4eff\u6548\u5f53\u5e74\u6210\u7956\u7687\u5e1d\u4e4b\u4e3e\uff0c\u8d77\u5175\u593a\u4f4d\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\uff0c\u6240\u4ee5\u67d0\u738b\u7237\u624d\u770b\u4e2d\u4e86\u626c\u5dde\u8fd9\u5757\u5bcc\u7532\u5929\u4e0b\u4e4b\u5730\uff0c\u800c\u4e14\u770b\u4e2d\u4e86\u6c34\u5e08\u63d0\u7763 \u624b\u4e2d\u7684\u8fd1\u5341\u4e07\u7cbe\u5175\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u770b\u6765\uff0c\u8fd9\u4e2a\u9634\u8c0b\u4e00\u76f4\u5728\u6697\u4e2d\u8fdb\u884c\u7740\uff0c\u53ef\u4e3a\u4ec0\u4e48\u4ed6\u4eec\u7adf\u7136\u52a0\u5feb\u4e86\u901f\u5ea6\uff0c\u4e0d\u60dc \u7531\u6697\u8f6c\u660e\u4e86\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u53ea\u56e0\u2026\u2026\u53ea\u56e0\u7687\u4e0a\u7684\u8eab\u4f53\u5df2\u65e5\u89c1\u8870\u5f31\uff0c\u592a\u533b\u8bf4\uff0c\u53ef\u80fd \u662f\u5929\u9650\u5df2\u5230\u4e86\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u6240\u4ee5\u67d0\u738b\u7237\u60f3\u4e58\u7687\u4e0a\u9a7e\u5d29\uff0c\u65b0\u541b\u7ee7\u4f4d\uff0c\u653f\u5c40\u4e0d\u7a33\u4e4b\u65f6\uff0c\u8d77\u5175\u593a \u4f4d\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5bf9\uff01\u4f46\u8981\u60f3\u8d77\u5175\uff0c\u5fc5\u987b\u5148\u6293\u5175\u6743\u3002\u6240\u4ee5\uff0c\u4ed6\u4eec\u5c31\u60f3\u6740\u9a6c\u5927\u4eba\u593a\u6c34\u5e08\u63d0\u7763\u4e4b \u804c\uff0c\u593a\u738b\u5927\u4eba\uff0c\u4ee5\u53d6\u5f97\u626c\u5dde\u8fd9\u5757\u5bcc\u7532\u5929\u4e0b\u4e4b\u5730\u4f5c\u4e3a\u6839\u636e\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8fd9\u4e2a\u9634\u8c0b\u65e2\u7136\u5df2\u8fdb\u884c\u4e86\u6570\u5e74\uff0c\u5219\u4ed6\u4eec\u624b\u4e2d\u4e00\u5b9a\u5df2\u7ecf\u805a\u96c6\u4e86\u4e0d\u5c0f\u7684\u5b9e\u529b\uff0c\u800c \u4e14\u626c\u5dde\u5341\u5927\u8c6a\u5bcc\u4e4b\u4e2d\uff0c\u4e00\u5b9a\u6709\u4ed6\u4eec\u7684\u4eba\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u675c\u53f8\u4e3b\u5206\u6790\u5f97\u5f88\u5bf9\uff0c\u6240\u4ee5\uff0c\u6211\u8bf4\u8fd9\u4e24\u5929\u662f\u6700\u4e3a\u5173\u952e\u7684\u65f6\u5019\uff0c\u6740\u624b\u96c6\u56e2\u80af\u5b9a \u5df2\u7ecf\u77e5\u9053\u6211\u4eec\u5df2\u7ecf\u638c\u63e1\u4e86\u5f88\u591a\u60c5\u51b5\uff0c\u800c\u4e14\uff0c\u65f6\u95f4\u4e5f\u4e0d\u5141\u8bb8\u4ed6\u4eec\u518d\u62d6\u5ef6\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u4e00\u518d\u5f3a\u8c03\u8fd9\u4e24\u5929\u7279\u522b\u5173\u952e\uff0c\u597d\u50cf\u7ecf\u8fc7\u4e86\u8fd9\u4e24\u5929\uff0c\u60c5\u51b5\u4f1a\u53d1\u751f\u5f88 \u5927\u7684\u53d8\u5316\u4f3c\u7684\uff0c\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u8bf7\u95ee\u9700\u7834\u83b7\u8fd9\u4e2a\u5927\u9634\u8c0b\uff0c\u6700\u91cd\u8981\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u5f53\u7136\u662f\u67d0\u738b\u7237\u4e0e\u6740\u624b\u96c6\u56e2\u52fe\u7ed3\u7684\u786e\u51ff\u8bc1\u636e\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u5934\u7b11\u9053\uff1a\u201c\u6b63\u662f\u3002\u636e\u6211\u6240\u77e5\uff0c\u8fd9\u8bc1\u636e\u5c31\u5728\u8fd1\u51e0\u5929\u5185\uff0c\u80fd\u62ff\u5230\u6211\u4eec\u7684\u4eba\u624b\u4e2d\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u542c\u603b\u6355\u5934\u7684\u610f\u601d\uff0c\u6740\u624b\u96c6\u56e2\u5185\u90e8\u5df2\u7ecf\u6709\u603b\u6355\u5934\u5b89\u6392\u7684\u5367\u5e95\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u7740\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u5979\u7b11\u563b\u563b\u5730\u53c8\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u738b\u5927\u4eba\uff0c\u4f60\u53ef\u8981\u505a\u597d\u8bf7\u6211\u4eec\u559d\u559c\u9152\u7684\u51c6\u5907\u54df\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u4e00\u6014\uff0c\u4e0d\u660e\u767d\u5979\u4e3a\u4ec0\u4e48\u7adf\u7136\u8bf4\u51fa\u8fd9\u6837\u4e00\u53e5\u8bdd\u6765\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\u5566\uff0c\u4e0d\u7528\u591a\u60f3\uff0c\u4e5f\u4e0d\u7528\u4e71\u731c\uff0c\u660e\u5929\uff0c\u5c31\u4ec0\u4e48\u90fd\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u770b\u8fd8\u6709\u4ec0\u4e48\u9700\u8981\u9a6c\u67d0\u505a\u7684\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u73b0\u5728\u9a6c\u5927\u4eba\u8981\u505a\u7684\uff0c\u548c\u738b\u5927\u4eba\u8981\u505a\u7684\u4e00\u6837\uff0c\u5c31\u662f\u4e00\u5b9a\u8981\u6ce8\u610f\u81ea\u5df1\u7684\u5b89\u5168\uff0c \u56e0\u4e3a\u6740\u624b\u96c6\u56e2\u6709\u89c1\u7f1d\u63d2\u9488\u7684\u672c\u9886\uff0c\u7a0d\u4e00\u758f\u6f0f\uff0c\u4ed6\u4eec\u5c31\u4f1a\u52a0\u4ee5\u5229\u7528\uff0c\u5c55\u5f00\u8fc5\u731b\u7684\u884c\u52a8\u3002\u4e24\u4f4d\u5927 \u4eba\uff0c\u5c24\u5176\u662f\u9a6c\u5927\u4eba\u4e00\u65e6\u51fa\u4ec0\u4e48\u610f\u5916\uff0c\u6211\u53ef\u4ee5\u80af\u5b9a\uff0c\u7ee7\u4efb\u626c\u5dde\u77e5\u5e9c\u548c\u6c34\u5e08\u63d0\u7763\u7684\u4eba\u9009\uff0c\u4ed6\u4eec\u65e9 \u5df2\u5b89\u6392\u597d\u4e86\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u6211\u4f1a\u6ce8\u610f\u7684\uff0c\u7761\u89c9\u4e5f\u4f1a\u7741\u5f00\u4e00\u53ea\u773c\u775b\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8981\u4e0d\u7136\u8fd9\u6837\u597d\u4e86\uff0c\u9a6c\u5927\u4eba\u4e0d\u59a8\u5c06\u6c34\u5e08\u63d0\u7763\u5e9c\u7684\u516c\u4e8b\u6682\u65f6\u79fb\u5230\u5e9c\u8859\u6765\u529e\uff0c\u8fd9 \u6837\u54b1\u4eec\u5408\u5175\u4e00\u5904\uff0c\u5b9e\u529b\u5c31\u66f4\u4e3a\u5f3a\u5927\uff0c\u6740\u624b\u96c6\u56e2\u5c31\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\u4e86\uff01\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u4ece\u603b\u6355\u5934\u521a\u624d\u4ecb\u7ecd\u7684\u4e00\u4e9b\u60c5\u51b5\u6765\u770b\uff0c\u6740\u624b\u96c6\u56e2\u662f\u6025\u4e8e\u60f3\u5f97\u624b\uff0c\u770b\u6765\u4e0d\u7ba1\u6211 \u4eec\u5982\u4f55\u9632\u5907\uff0c\u4ed6\u4eec\u4e5f\u4f1a\u5c3d\u51fa\u5b9e\u529b\uff0c\u4e0e\u6211\u4eec\u4e00\u51b3\u80dc\u8d1f\uff0c\u9a6c\u67d0\u603b\u4e0d\u80fd\u5728\u5e9c\u8859\u5446\u4e00\u8f88\u5b50\u5427\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u6700\u591a\u4e24\u5929\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u4f4f\u4e24\u5929\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u65e2\u7136\u9a6c\u5927\u4eba\u7b54\u5e94\u5728\u6b64\u4f4f\u4e0b\uff0c\u6700\u597d\u518d\u8c03\u4e94\u767e\u7cbe\u5175\u6765\uff0c\u5c06\u519b\u58eb\u4eec\u5206\u6210\u4e09\u73ed\uff0c\u8f6e \u73ed\u9632\u536b\uff0c\u56db\u5927\u6355\u5934\u548c\u6797\u53f8\u6848\uff0c\u6c5f\u5357\u3001\u4e2d\u5dde\u5206\u53f8\u7684\u597d\u624b\u4eec\u4e5f\u5206\u73ed\u9632\u536b\uff0c\u4ee5\u4fbf\u5927\u5bb6\u90fd\u80fd\u597d\u597d\u4f11\u606f \u4e00\u9635\uff0c\u517b\u8db3\u7cbe\u795e\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u53ef\u662f\u6709\u5927\u4ed7\u8981\u6253\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u800c\u4e14\u662f\u786c\u4ed7\u3002\u6211\u60f3\uff0c\u6740\u624b\u96c6\u56e2\u8fd9\u6b21\u4f1a\u5c3d\u5b9e\u529b\uff0c\u56e0\u4e3a\u8fd9\u6b63\u662f\u4ed6\u4eec\u6700\u540e \u7684\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u9a6c\u67d0\u624b\u4e0b\u6709\u4e94\u767e\u4eb2\u5175\uff0c\u4e2a\u4e2a\u90fd\u662f\u4e45\u7ecf\u9635\u6218\u7684\u8001\u5175\u6cb9\u5b50\uff0c\u6211\u5c31\u5c06\u4ed6\u4eec\u8c03\u6765 \u5427\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u592a\u597d\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u9a6c\u957f\u5c71\u5e26\u7740\u963f\u6a2a\u3001\u963f\u4fdd\u548c\u4e24\u767e\u540d\u7cbe\u9510\u6c34\u5e08\uff0c\u53bb\u6c34\u5e08\u63d0\u7763\u8c03\u96c6\u4eb2\u5175\u53bb\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7684\u672c\u610f\u662f\u8ba9\u4ed6\u8fd8\u662f\u4e0b\u4e00\u7eb8\u547d\u4ee4\uff0c\u4f46\u9a6c\u957f\u5c71\u89e3\u91ca\u8bf4\uff0c\u8fd9\u4e9b\u4eb2\u5175\u7686\u53ef\u7b97\u4ed6\u7684\u5b50\u5f1f\u5175\uff0c \u975e\u4ed6\u672c\u4eba\u7684\u4eb2\u53e3\u547d\u4ee4\uff0c\u7edd\u4e0d\u4f1a\u6267\u884c\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u8fd8\u6709\u51e0\u4e2a\u7591\u70b9\uff0c\u4e0d\u641e\u6e05\u695a\uff0c\u6211\u5fc3\u91cc\u603b\u6709\u4e9b\u4e0d\u8e0f\u5b9e\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u77e5\u9053\u675c\u53f8\u4e3b\u7684\u813e\u6c14\uff0c\u8bf7\u628a\u4f60\u7684\u7591\u95ee\u63d0\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u90a3\u4e2a\u81ea\u79f0\u68a6\u5e7b\u4e4b\u5200\u7684\u9ed1\u8863\u8499\u9762\u4eba\u7a76\u7adf\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u6211\u867d\u7136\u5f88\u6709\u4e9b\u6000\u7591\u4e00\u4e2a\u4eba\uff0c\u4f46\u6682\u65f6\u8fd8\u6ca1\u6709\u771f\u51ed\u5b9e\u636e\uff0c\u7684\u786e\u4e0d\u597d\u5984 \u52a0\u5224\u65ad\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u662f\u59da\u987a\u5929\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u675c\u53f8\u4e3b\u4e3a\u4ec0\u4e48\u4f1a\u6000\u7591\u4ed6\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u56e0\u4e3a\u5929\u67ab\u9053\u957f\u66fe\u7ecf\u5728\u4ed6\u7684\u5e9c\u4e2d\u627e\u5230\u4e86\u5c0f\u6587\u7684\u89e3\u836f\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8ba4\u4e3a\u662f\u5929\u67ab\u9053\u957f\u6709\u610f\u5ac1\u7978\u7ed9\u59da\u987a\u5929\u5462\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u56e0\u4e3a\u59da\u5e9c\u4e0a\u4e0b\u6240\u6709\u7684\u4eba\u90fd\u5931\u8e2a\u4e86\uff0c\u800c\u4e14\u663e\u7136\u4e0d\u662f\u88ab\u6740\uff0c\u88ab\u7ed1\u67b6\uff0c\u800c\u662f\u81ea\u52a8 \u64a4\u8d70\u7684\uff0c\u66f4\u53ef\u7591\u7684\u662f\uff0c\u4ed6\u4eec\u5c31\u6b64\u4e00\u53bb\u65e0\u6d88\u606f\uff0c\u51ed\u7a7a\u6d88\u5931\u4e86\u4e00\u822c\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6c89\u541f\u7740\uff0c\u4e00\u65f6\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u636e\u6211\u60f3\u6765\uff0c\u5047\u68a6\u5e7b\u4e4b\u5200\u4e00\u5b9a\u662f\u626c\u5dde\u5341\u5927\u8c6a\u5bcc\u4e2d\u7684\u67d0\u4e00\u4e2a\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8bf4\u8bf4\u4f60\u63a8\u65ad\u7684\u6839\u636e\u3002\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u662f\u6740\u624b\u96c6\u56e2\u7684\u9996\u8111\uff0c\u5728\u626c\u5dde\u8fdb\u884c\u7684\u4e00\u5207\u90fd\u4e14\u7531\u4ed6\u5b89\u6392\uff0c\u51b3\u5b9a\uff0c\u663e\u7136 \u662f\u4e00\u4e2a\u5bf9\u626c\u5dde\u5404\u65b9\u9762\u60c5\u51b5\u90fd\u5f88\u719f\u6089\u7684\u4eba\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\u3002\u6211\u60f3\uff0c\u5927\u5bb6\u8fd8\u662f\u4f11\u606f\u53bb\u5427\uff0c\u4e8b\u60c5\u5f88\u5feb\u5c31\u4f1a\u6c34\u843d\u77f3\u51fa\u4e86\uff0c\u4e0d\u7528\u5728\u8fd9\u91cc \u6697\u8d39\u5fc3\u601d\u3002\u6211\u8fd8\u8981\u544a\u8beb\u5927\u5bb6\u4e00\u6b21\uff0c\u4e0d\u5230\u6700\u540e\u7684\u65f6\u523b\uff0c\u51b3\u4e0d\u8981\u8f7b\u6613\u65ad\u5b9a\u8c01\u662f\u654c\u4eba\uff0c\u8c01\u662f\u670b\u53cb\u3002\u201d \u3000\u3000\u56db\u5927\u6355\u5934\u9f50\u58f0\u9053\uff1a\u201c\u662f\uff0c\u5c5e\u4e0b\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5bf9\u60dc\u7389\u548c\u5c0f\u96c5\u9053\uff1a\u201c\u4eca\u5929\uff0c\u4e00\u76f4\u5230\u660e\u5929\uff0c\u4f60\u4eec\u6700\u597d\u4e0d\u8981\u79bb\u5f00\u91d1\u5c0f\u7709\u5de6\u53f3\u3002\u60dc\u7389\uff0c \u4f60\u4ecd\u7136\u8981\u7ee7\u7eed\u5e2e\u52a9\u5979\u56de\u5fc6\u3002\u5c0f\u96c5\uff0c\u4f60\u7684\u4e3b\u8981\u4efb\u52a1\u662f\u4fdd\u62a4\u5979\u7684\u5b89\u5168\u3002\u201d \u3000\u3000\u60dc\u7389\u3001\u5c0f\u96c5\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u8f7b\u5411\u5c0f\u6587\u9053\uff1a\u201c\u8fd9\u51e0\u5929\u5c06\u662f\u6211\u4eec\u6765\u626c\u5dde\u7684\u6700\u5371\u9669\u7684\u51e0\u5929\uff0c\u5f88\u53ef\u80fd\u4e5f\u5c06\u662f\u6211\u4eec\u5211\u6355 \u751f\u6daf\u4e2d\u6700\u5371\u9669\u7684\u51e0\u5929\uff0c\u6240\u4ee5\uff0c\u5c0f\u6587\u4f60\u4e00\u5b9a\u8981\u534a\u6b65\u4e0d\u79bb\u738b\u5927\u4eba\uff01\u201d \u3000\u3000\u5c0f\u6587\u9053\uff1a\u201c\u603b\u6355\u5934\u653e\u5fc3\uff0c\u5c0f\u6587\u4f1a\u7528\u6027\u547d\u4fdd\u62a4\u4ed6\uff01\u201d \u3000\u3000\u9a6c\u957f\u5c71\u5e26\u7740\u4ed6\u7684\u4e94\u767e\u4eb2\u5175\u8d76\u5230\u5e9c\u8859\u6765\u4e86\uff0c\u5f88\u5feb\uff0c\u6240\u6709\u7684\u4eba\u5728\u7a0b\u5c0f\u8776\u7684\u5b89\u6392\u548c\u9a6c\u957f\u5c71\u7684\u6307 \u6325\u3001\u8c03\u5ea6\u4e0b\uff0c\u8fdb\u5165\u4e86\u5404\u81ea\u7684\u9632\u5b88\u533a\u57df\u3002 \u3000\u3000\u73b0\u5728\uff0c\u6574\u4e2a\u5e9c\u8859\u5df2\u7ecf\u6210\u4e86\u4e00\u5ea7\u5175\u8425\u3002 \u3000\u3000\u7262\u4e0d\u53ef\u7834\u7684\u5175\u8425\u3002 \u3000\u3000\u5e9c\u8859\u7684\u56f4\u5899\u540e\uff0c\u5230\u5904\u90fd\u4f0f\u6709\u6c34\u5e08\u4e2d\u767e\u53d1\u767e\u4e2d\u7684\u795e\u7bad\u624b\u3002 \u3000\u3000\u53ea\u8981\u6709\u4eba\u6562\u5f3a\u95ef\u5e9c\u8859\uff0c\u4e00\u7728\u773c\u95f4\uff0c\u5c31\u4f1a\u6709\u4e0a\u5343\u652f\u5229\u7bad\u4e00\u8d77\u5c04\u5411\u4ed6\u3002 \u3000\u3000\u6765\u4eba\u7acb\u523b\u4f1a\u53d8\u6210\u4e00\u53ea\u5927\u523a\u732c\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u56db\u5904\u5de1\u89c6\u4e86\u4e00\u756a\u540e\uff0c\u7ec8\u4e8e\u653e\u5fc3\u5730\u56de\u623f\u95f4\u4f11\u606f\u53bb\u4e86\u3002 \u3000\u3000\u5979\u76f8\u4fe1\uff0c\u73b0\u5728\uff0c\u5982\u679c\u6709\u4eba\u60f3\u653b\u8fdb\u5e9c\u8859\uff0c\u9664\u975e\u4ed6\u624b\u4e2d\u63e1\u6709\u6570\u5343\u96c4\u5175\u3002 \u3000\u3000\u800c\u8fd9\u4e00\u70b9\uff0c\u6740\u624b\u96c6\u56e2\u5374\u662f\u4e0d\u53ef\u80fd\u505a\u5230\u7684\u3002 \u3000\u3000\u4e0d\u4f46\u662f\u6740\u624b\u96c6\u56e2\uff0c\u5c31\u662f\u8fd9\u4e2a\u5927\u9634\u8c0b\u7684\u4e3b\u8c0b\u4e4b\u4eba\uff0c\u4e5f\u6ca1\u6709\u8fd9\u4e2a\u5b9e\u529b\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u5982\u679c\u4ed6\u624b\u4e2d\u638c\u63e1\u6709\u4e00\u652f\u519b\u961f\uff0c\u5219\u4ed6\u80af\u5b9a\u65e9\u5df2\u52a8\u624b\u4e86\uff0c\u54ea\u91cc\u8fd8\u4f1a\u5229\u7528\u6740\u624b\u96c6\u56e2\u5728\u626c \u5dde\u641e\u51fa\u73b0\u5728\u8fd9\u6837\u4e00\u4e2a\u5c40\u9762\u5462\uff1f \u3000\u3000\u51e0\u5929\u6765\uff0c\u675c\u671b\u6708\u5b9e\u5728\u662f\u592a\u7d2f\u4e86\uff0c\u7cbe\u795e\u4e5f\u4e00\u76f4\u592a\u7d27\u5f20\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u5f53\u5c91\u5578\u864e\u6765\u66ff\u6362\u4ed6\u65f6\uff0c\u4ed6\u4e5f\u4e0b\u518d\u5ba2\u6c14\uff0c\u56de\u5230\u81ea\u5df1\u7684\u623f\u95f4\u91cc\uff0c\u5012\u5934\u4fbf\u7761\u4e0b\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\u513f\uff0c\u4ed6\u5c31\u8fdb\u5165\u4e86\u68a6\u4e61\u3002 \u3000\u3000\u8fd9\u4e00\u89c9\u7761\u5f97\u5f88\u6c89\uff0c\u8fde\u6709\u4eba\u6765\u5230\u4e86\u4ed6\u7684\u623f\u95f4\uff0c\u4ed6\u90fd\u6ca1\u6709\u9192\u8fc7\u6765\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4e5f\u56e0\u4e3a\u6765\u4eba\u7684\u884c\u52a8\u975e\u5e38\u4e4b\u8f7b\uff0c\u975e\u5e38\u4e4b\u5c0f\u5fc3\u3002 \u3000\u3000\u6765\u4eba\u8fdb\u4e86\u623f\u95f4\u540e\uff0c\u4fbf\u4fe1\u624b\u63a9\u4e0a\u4e86\u95e8\uff0c\u8fd8\u8f7b\u8f7b\u62f4\u4e0a\u4e86\u95e8\u95e9\u3002 \u3000\u3000\u7136\u540e\u6765\u4eba\u5c31\u5728\u5e8a\u524d\u7684\u4e00\u5f20\u6905\u5b50\u4e0a\u5750\u4e0b\uff0c\u770b\u7740\u719f\u7761\u4e2d\u7684\u675c\u671b\u6708\u3002 \u3000\u3000\u5f88\u5feb\uff0c\u675c\u671b\u6708\u5c31\u9192\u4e86\u3002 \u3000\u3000\u6b66\u529f\u5230\u4e86\u4ed6\u8fd9\u79cd\u706b\u5019\u7684\u4eba\uff0c\u603b\u662f\u5f88\u8b66\u89c9\u7684\u3002 \u3000\u3000\u623f\u95f4\u8944\u5f88\u9ed1\uff0c\u56e0\u4e3a\u6b63\u662f\u6df1\u591c\u3002 \u3000\u3000\u5c4b\u91cc\u6ca1\u6709\u706f\u3002 \u3000\u3000\u675c\u671b\u6708\u8fd8\u6ca1\u7741\u773c\uff0c\u5df2\u611f\u89c9\u5230\u5e8a\u8fb9\u5750\u7740\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u6765\u4eba\u4e0d\u662f\u654c\u4eba\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u95fb\u5230\u4e86\u4e00\u80a1\u5e76\u4e0d\u964c\u751f\u7684\u9999\u6c14\u3002 \u3000\u3000\u8fd9\u662f\u5c11\u5973\u6de1\u6de1\u7684\u4f53\u9999\u3002 \u3000\u3000\u675c\u671b\u6708\u7ffb\u4e86\u4e2a\u8eab\uff0c\u4f38\u624b\u53bb\u62c9\u6765\u4eba\u653e\u5728\u5e8a\u8fb9\u7684\u4e00\u53ea\u5c0f\u624b\uff0c\u53e3\u4e2d\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u6765\u4e86\uff1f\u4f60\u4e0d \u53bb\u4fdd\u62a4\u91d1\u5c0f\u7709\uff0c\u7559\u5c0f\u96c5\u4e00\u4e2a\u4eba\u5728\u90a3\u91cc\uff0c\u80fd\u884c\u5417\uff1f\u60dc\u2026\u2026\u201d \u3000\u3000\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u5c31\u65ad\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u62c9\u4f4f\u4e86\u6765\u4eba\u7684\u5c0f\u624b\u3002 \u3000\u3000\u51ed\u611f\u89c9\uff0c\u4ed6\u62c9\u7684\u8fd9\u53ea\u624b\u51b3\u4e0d\u662f\u60dc\u7389\u7684\u5c0f\u624b\u3002 \u3000\u3000\u800c\u4e14\u4ed6\u4e5f\u95fb\u51fa\u4e86\u8fd9\u80a1\u6de1\u6de1\u7684\u9999\u5473\u4e0e\u60dc\u7389\u8eab\u4e0a\u7684\u4f53\u9999\u4e0d\u540c\u3002 \u3000\u3000\u675c\u671b\u6708\u4e0d\u7981\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u5750\u8d77\u6765\u5c31\u8981\u70b9\u706f\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u542c\u89c1\u6765\u4eba\u7528\u6781\u4f4e\u7684\u58f0\u97f3\u9053\uff1a\u201c\u675c\u5927\u54e5\uff0c\u4e0d\u8981\u70b9\u706f\uff0c\u662f\u6211\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u5403\u4e86\u4e00\u60ca\uff0c\u4e00\u4e0b\u5b50\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u77e5\u9053\u6765\u4eba\u662f\u8c01\u4e86\uff0c\u56e0\u4e3a\u4ed6\u7edd\u4e0d\u4f1a\u542c\u4e0d\u51fa\u5979\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5979\u5c31\u662f\u7a0b\u5c0f\u8776\u3002 \u3000\u3000\u5211\u90e8\u603b\u6355\u5934\u7a0b\u5c0f\u8776\u3002 \u3000\u3000\u675c\u671b\u6708\u4e00\u65f6\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u529e\u3002 \u3000\u3000\u4ed6\u6014\u6014\u5730\u95ee\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u627e\u6211\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d \u3000\u3000\u5176\u5b9e\u4ed6\u77e5\u9053\u7a0b\u5c0f\u8776\u4e3a\u4ec0\u4e48\u4f1a\u5728\u6df1\u591c\u91cc\u5230\u4ed6\u7684\u623f\u95f4\u91cc\u6765\u627e\u4ed6\u3002 \u3000\u3000\u56e0\u4e3a\u7a0b\u5c0f\u8776\u521a\u624d\u7684\u4e00\u58f0\u201c\u675c\u5927\u54e5\u201d\u5df2\u7ecf\u5c06\u5979\u7684\u5fc3\u601d\u5b8c\u5168\u5766\u9732\u65e0\u7591\u4e86\u3002 \u3000\u3000\u4e0d\u53eb\u201c\u675c\u53f8\u4e3b\u201d\u800c\u53eb\u201c\u675c\u5927\u54e5\u201d\uff0c\u8fd9\u610f\u5473\u7740\u4ec0\u4e48\uff0c\u675c\u671b\u6708\u8fd8\u80fd\u4e0d\u660e\u767d\u5417\uff1f \u3000\u3000\u4e00\u65f6\u95f4\uff0c\u4ed6\u5f88\u7d27\u5f20\uff0c\u4e5f\u5f88\u5174\u594b\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u6709\u70b9\u5598\u4e0d\u4e0a\u6c14\u4e86\u3002 \u3000\u3000\u8fd9\u4f1a\u662f\u771f\u7684\u5417\uff1f \u3000\u3000\u675c\u671b\u6708\u6697\u5fd6\u9053\uff1a\u6211\u4f1a\u4e0d\u4f1a\u662f\u5728\u505a\u68a6\uff1f \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u7a0b\u5c0f\u8776\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u5979\u7684\u58f0\u97f3\u4ecd\u7136\u5f88\u4f4e\uff0c\u4f4e\u4f4e\u7684\u58f0\u97f3\u4e2d\u4f3c\u4e4e\u8fd8\u5e26\u7740\u4e00\u4e1d\u98a4\u6296\u3002 \u3000\u3000\u5188\u538b\u6291\u4e0d\u4f4f\u7684\u6fc0\u60c5\u800c\u5f15\u53d1\u7684\u98a4\u6296\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u8981\u53eb\u6211\u603b\u6355\u5934\u2026\u2026\u81f3\u5c11\uff0c\u4eca\u5929\u591c\u91cc\u4e0d\u8981\u53eb\u2026\u2026\u675c\u5927\u54e5\u2026\u2026\u81f3\u5c11\u73b0\u5728\u4e0d\u8981 \u53eb\u603b\u6355\u5934\u3002\u201d \u3000\u3000\u5979\u505c\u4e86\u4e00\u505c\uff0c\u53c8\u63a5\u7740\u9053\uff1a\u201c\u675c\u5927\u54e5\uff0c\u53eb\u6211\u5c0f\u8776\u5427\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u60ca\u5446\u4e86\u3002 \u3000\u3000\u4ed6\u771f\u662f\u6000\u7591\u81ea\u5df1\u662f\u5728\u505a\u68a6\u4e86\u3002 \u3000\u3000\u8001\u5b9e\u8bf4\uff0c\u773c\u524d\u8fd9\u79cd\u60c5\u51b5\uff0c\u4ee5\u524d\u662f\u66fe\u7ecf\u5728\u4ed6\u7684\u68a6\u4e2d\u51fa\u73b0\u8fc7\u7684\u3002 \u3000\u3000\u4f46\u81ea\u4ece\u60dc\u7389\u5c06\u6e05\u767d\u7684\u5904\u5973\u4e4b\u8eab\u5b8c\u5168\u4ea4\u7ed9\u4ed6\u540e\uff0c\u4ed6\u5df2\u6ca1\u6709\u518d\u505a\u8fc7\u8fd9\u6837\u7684\u68a6\u3002 \u3000\u3000\u8fd9\u68a6\u867d\u7136\u662f\u751c\u871c\u7684\uff0c\u4f46\u4e5f\u662f\u8352\u5510\u7684\u3002 \u3000\u3000\u675c\u671b\u6708\u6293\u8d77\u7a0b\u5c0f\u8776\u7684\u5c0f\u624b\uff0c\u8f7b\u8f7b\u629a\u6478\u7740\uff0c\u9053\uff1a\u201c\u5c0f\u8776\uff0c\u6211\u662f\u4e0d\u662f\u5728\u505a\u68a6\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u675c\u5927\u54e5\uff0c\u8fd9\u4e0d\u662f\u68a6\uff0c\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u2026\u2026\u6211\u4e5f\u4e00\u76f4\u5728\u68a6\u60f3\u7740\u4f1a\u6709\u8fd9\u4e00\u5929\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u53c8\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5c0f\u8776\uff0c\u4f60\u8bf4\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u675c\u5927\u54e5\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u4e00\u76f4\u5728\u5fc3\u4e2d\u6697\u6697\u5730\u7231\u7740\u4f60\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u60c5\u4e0d\u81ea\u7981\u5730\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u4e5f\u4e00\u76f4\u5728\u7231\u7740\u4f60\u5440\uff0c\u5c0f\u8776\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5e7d\u7136\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u70b9\u8bf4\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u65e0\u8a00\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u675c\u5927\u54e5\u4f60\u4e00\u76f4\u628a\u6211\u770b\u505a\u662f\u5c0f\u8c2a\u4eba\u95f4\u7684\u4ed9\u5973\uff0c\u4f46\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u6211\u867d \u7136\u662f\u5211\u90e8\u7684\u603b\u6355\u5934\uff0c\u4f46\u66f4\u662f\u4e00\u4e2a\u5973\u4eba\uff0c\u800c\u4e14\u662f\u4e00\u4e2a\u6b63\u5e38\u7684\u5973\u4eba\uff0c\u4e5f\u6709\u7740\u5176\u4ed6\u6b63\u5e38\u5973\u4eba\u4e00\u6837\u7684 \u9700\u8981\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u53ef\u662f\uff0c\u5c0f\u8776\uff0c\u73b0\u5728\u5df2\u7ecf\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4f60\u5df2\u548c\u60dc\u7389\u6709\u767d\u9996\u4e4b\u7ea6\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4e0d\u4ec5\u5982\u6b64\uff0c\u6211\u4eec\u5df2\u7ecf\u2026\u2026\u5df2\u7ecf\u6709\u4e86\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\uff0c\u4f60\u4eec\u5df2\u7ecf\u6709\u4e86\u592b\u59bb\u4e4b\u5b9e\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u60dc\u7389\u4e0d\u662f\u4e00\u4e2a\u5e73\u51e1\u7684\u5973\u4eba\uff0c\u6211\u5f88\u7fa1\u6155\u5979\uff0c\u66f4\u4f69\u670d\u5979\u3002\u201d \u3000\u3000\u5979\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u7740\u9053\uff1a\u201c\u6211\u7fa1\u6155\u5979\uff0c\u662f\u56e0\u4e3a\u5979\u5bf9\u675c\u5927\u54e5\u4f60\u5df2\u7ecf\u6377\u8db3\u5c22\u767b\uff0c\u6211\u4f69\u670d\u5979\uff0c\u662f \u56e0\u4e3a\u5979\u6562\u4e8e\u6b63\u89c6\u81ea\u5df1\u7684\u611f\u60c5\uff0c\u4e00\u65e6\u7231\u4e0a\u4e00\u4e2a\u4eba\uff0c\u4fbf\u6562\u4e8e\u5927\u80c6\u5730\u8868\u767d\u51fa\u6765\uff0c\u5927\u80c6\u5730\u884c\u52a8\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u65e0\u8a00\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4e5f\u65e0\u8a00\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5c0f\u8776\uff0c\u6211\u4eec\u4eca\u751f\u5df2\u7ecf\u65e0\u7f18\uff0c\u4f46\u6c42\u6765\u4e16\u5427\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\uff0c\u4eca\u591c\u6211\u5c31\u8981\u628a\u81ea\u5df1\u4ea4\u2026\u2026\u4ea4\u7ed9\u4f60\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u5927\u60ca\uff0c\u9053\uff1a\u201c\u5c0f\u8776\uff0c\u8fd9\u672c\u6765\u4e00\u76f4\u662f\u6211\u5fc3\u4e2d\u552f\u4e00\u5fc3\u613f\uff0c\u4f46\u662f\u2026\u2026\u4f46\u662f\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5fc3\u91cc\u5728\u60f3\u4ec0\u4e48\uff0c\u675c\u5927\u54e5\uff0c\u4f60\u653e\u5fc3\uff0c\u6211\u4e0d\u4f1a\u4e0e\u60dc\u7389\u4e89\u7684\uff0c \u3000\u3000\u66f4\u4e0d\u8981\u6c42\u5728\u4f60\u4eec\u751f\u6d3b\u4e2d\u5360\u6709\u91cd\u8981\u7684\u4e00\u5e2d\u4e4b\u5730\uff0c\u6211\u53ea\u662f\u2026\u2026\u53ea\u662f\u60f3\u5bf9\u81ea\u5df1\u7684\u8fd9\u4efd\u611f\u60c5\uff0c\u4e5f \u5bf9\u675c\u5927\u54e5\u5bf9\u6211\u7684\u7231\u6709\u4e00\u4e2a\u4ea4\u5f85\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5979\u4ece\u6905\u5b50\u4e0a\u7ad9\u8d77\u8eab\u5b50\uff0c\u4e00\u4e0b\u5b50\u6251\u8fdb\u675c\u671b\u6708\u7684\u6000\u4e2d\u3002 \u3000\u3000\u8f7b\u7389\u6e29\u9999\u62b1\u6ee1\u6000\uff0c\u675c\u671b\u6708\u5fc3\u4e2d\u53c8\u559c\u53c8\u60ca\uff0c\u4e00\u65f6\u4e0d\u77e5\u600e\u6837\u624d\u597d\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u706b\u70ed\u7684\u6a31\u5507\u5df2\u7ecf\u8d34\u5728\u4e86\u4ed6\u7684\u80f8\u53e3\u4e4b\u4e0a\uff0c\u70ed\u70c8\u5730\u543b\u7740\u3002 \u3000\u3000\u675c\u671b\u6708\u80fd\u611f\u89c9\u5230\u5979\u7684\u771f\u60c5\uff0c\u5979\u7684\u6e34\u671b\uff0c\u4f46\u4ed6\u52aa\u529b\u514b\u5236\u7740\u81ea\u5df1\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e0d\u60f3\u5bf9\u4e0d\u8d77\u60dc\u7389\uff0c\u66f4\u4e0d\u60f3\u5bf9\u4e0d\u8d77\u7a0b\u5c0f\u8776\u3002 \u3000\u3000\u4e00\u5931\u8db3\u6210\u5343\u53e4\u6068\u554a\uff01 \u3000\u3000\u6709\u65f6\u5019\uff0c\u5931\u8db3\u4e0e\u5426\uff0c\u4ec5\u5728\u4f60\u4e00\u5ff5\u4e4b\u95f4\u3002 \u3000\u3000\u89c1\u675c\u671b\u6708\u4e00\u76f4\u6ca1\u6709\u53cd\u5e94\uff0c\u7a0b\u5c0f\u8776\u70ed\u60c5\u7684\u52a8\u4f5c\u7adf\u7136\u505c\u987f\u4e0b\u6765\u3002 \u3000\u3000\u5979\u4f0f\u5728\u675c\u671b\u6708\u6000\u91cc\uff0c\u5e7d\u5e7d\u5730\u9053\uff1a\u201c\u675c\u5927\u54e5\uff0c\u4f60\u662f\u4e0d\u662f\u6709\u4e9b\u770b\u4e0d\u8d77\u6211\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u5462\uff1f\u5c0f\u8776\uff0c\u6211\u53ea\u4e0d\u8fc7\u2026\u2026\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u675c\u5927\u54e5\u662f\u4e0d\u60f3\u5bb3\u4e86\u6211\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5982\u679c\u4f60\u4e0d\u7b54\u5e94\uff0c\u624d\u662f\u5bb3\u4e86\u6211\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u662f\u6211\u552f\u4e00\u6240\u7231\u7684\u7537\u4eba\uff0c\u7ec8\u6211\u4e00\u751f\uff0c\u6211\u662f\u7edd\u4e0d\u4f1a\u518d\u7231\u522b\u7684\u7537\u4eba\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u5f3a\u5fcd\u5fc3\u4e2d\u7684\u9178\u75db\uff0c\u9053\uff1a\u201c\u5c0f\u8776\uff0c\u4f60\u8fd8\u5f88\u5e74\u8f7b\uff0c\u957f\u5f97\u53c8\u7f8e\u4e3d\u52a8\u4eba\uff0c\u6b66\u529f\u53c8\u9ad8\u5f3a\uff0c\u66f4 \u9ad8\u5c45\u5211\u90e8\u603b\u6355\u5934\u4e4b\u4f4d\uff0c\u4f1a\u6709\u5f88\u591a\u4eba\u7231\u4f60\u7684\uff0c\u4f60\u4e00\u5b9a\u80fd\u591f\u627e\u5230\u4e00\u4e2a\u5982\u610f\u90ce\u541b\u7684\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u675c\u90ce\uff0c\u6211\u53ea\u7231\u4f60\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u5fc3\u4e2d\u66f4\u75db\uff0c\u9053\uff1a\u201c\u5c0f\u8776\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u675c\u90ce\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u7687\u4e0a\u4e3a\u4ec0\u4e48\u8981\u5c06\u8fd9\u4e2a\u6848\u5b50\u4ea4\u7ed9\u6211\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u662f\u5211\u90e8\u603b\u6355\u5934\u554a\uff1f\u201d \u3000\u3000\u4ed6\u5b9e\u5728\u4e0d\u660e\u767d\u73b0\u5728\u7a0b\u5c0f\u8776\u600e\u4e48\u4f1a\u7a81\u7136\u60f3\u8d77\u8fd9\u4ef6\u4e8b\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u9634\u8c0b\u9006\u53cd\uff0c\u662f\u4f55\u7b49\u5927\u6848\uff0c\u6309\u60ef\u4f8b\uff0c\u8fd9\u79cd\u6848\u5b50\u53ea\u4f1a\u4ea4\u7ed9\u4e1c\u5382\u548c\u9526\u8863\u536b\uff0c\u51b3\u4e0d \u4f1a\u7ed9\u5211\u90e8\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u4f60\u662f\u7687\u4e0a\u7684\u5e72\u5973\u513f\u5427\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u731c\u5bf9\u4e86\u4e00\u534a\uff0c\u6211\u518d\u95ee\u4f60\uff0c\u7687\u4e0a\u4e3a\u4ec0\u4e48\u8981\u8ba4\u6211\u505a\u5e72\u5973\u513f\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u60f3\u4e86\u60f3\uff0c\u5ffd\u7136\u60f3\u8d77\u4e86\u7687\u4e0a\u66fe\u60f3\u7eb3\u7a0b\u5c0f\u8776\u4e3a\u5983\u7684\u4f20\u95fb\uff0c\u4f46\u8fd9\u8bdd\u73b0\u5728\u4ed6\u53ef\u4e0d\u80fd\u8bf4\u51fa\u6765\uff0c \u4fbf\u9053\uff1a\u201c\u90a3\u5f53\u7136\u662f\u56e0\u4e3a\u4f60\u806a\u660e\u8fc7\u4eba\uff0c\u6b66\u529f\u9ad8\u5f3a\uff0c\u79c0\u5916\u6167\u4e2d\uff0c\u60f9\u4eba\u7231\u601c\u4e4b\u6545\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u5bf9\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u90a3\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u56e0\u4e3a\u592a\u5b50\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u5927\u5403\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u96be\u9053\u592a\u5b50\u5f88\u7231\u4f60\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\u3002\u4f46\u662f\u6211\u4e0d\u7231\u4ed6\uff0c\u6211\u5bf9\u4ed6\u53ea\u6709\u5fe0\u5fc3\uff0c\u6ca1\u6709\u7537\u5973\u4e4b\u60c5\uff0c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u592a\u5b50\u4e00\u767b\u57fa\uff0c\u662f\u4e0d\u662f\u5c31\u4f1a\u7eb3\u4f60\u4e3a\u5983\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u5979\u8f7b\u8f7b\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u7687\u4e0a\u5df2\u7ecf\u8c15\u65e8\uff0c\u592a\u5b50\u5373\u5c06\u767b\u57fa\uff0c\u800c\u4e14\u5df2\u7ecf\u505a\u597d\u4e86\u767b\u57fa\u7684\u4e00\u5207\u51c6\u5907\uff0c \u4ed6\u4e00\u65e6\u6210\u4e3a\u7687\u5e1d\uff0c\u6211\u2026\u2026\u6211\u5c31\u4e0d\u4f1a\u518d\u662f\u5211\u90e8\u603b\u6355\u5934\uff0c\u4e5f\u5c31\u6ca1\u6709\u673a\u4f1a\u518d\u89c1\u5230\u675c\u5927\u54e5\u4e86\u3002\u675c\u5927\u54e5\uff0c \u4f60\u73b0\u5728\u660e\u767d\u4e86\u6211\u4e3a\u4ec0\u4e48\u2026\u2026\u4e3a\u4ec0\u4e48\u4e86\u5427\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u662f\u7684\uff0c\u6211\u660e\u767d\u4e86\u3002\u4f46\u6b63\u56e0\u4e3a\u6211\u660e\u767d\u4e86\uff0c\u5c0f\u8776\uff0c\u6211\u5c31\u66f4\u4e0d\u80fd\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7a81\u7136\u6d41\u4e0b\u6cea\u6765\uff0c\u9053\uff1a\u201c\u675c\u5927\u54e5\uff0c\u96be\u9053\u4f60\u5fcd\u5fc3\u8ba9\u5c0f\u8776\u9057\u61be\u7ec8\u751f\u5417\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u5fc3\u5982\u5200\u7ede\u4e00\u822c\u5730\u75db\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u660e\u5929\u2026\u2026\u660e\u5929\uff0c\u5c31\u4f1a\u6709\u53d8\u5316\uff0c\u675c\u5927\u54e5\uff0c\u4eca\u591c\u5df2\u7ecf\u662f\u6211\u4eec\u6709\u53ef\u80fd\u5728\u4e00\u8d77\u7684\u6700 \u540e\u7684\u65f6\u95f4\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u53cc\u624b\u4e00\u7d27\uff0c\u62b1\u7d27\u4e86\u5979\u7684\u7ea4\u67d4\u7684\u8170\u8eab\uff0c\u6df1\u6df1\u5730\u543b\u4f4f\u4e86\u5979\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u201c\u5624\u549b\u201d\u4e00\u58f0\uff0c\u5a49\u8f6c\u76f8\u5c31\u3002 \u3000\u3000\u4e8c\u4eba\u5747\u5df2\u6697\u604b\u5bf9\u65b9\u5f88\u4e45\u4e86\uff0c\u5374\u4e00\u76f4\u90fd\u6ca1\u6709\u8bf4\u51fa\u53e3\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4e8c\u4eba\u90fd\u5df2\u660e\u4e86\u5bf9\u65b9\u7684\u611f\u60c5\uff0c\u65f6\u95f4\u5374\u53c8\u53ea\u5269\u4e0b\u77ed\u77ed\u7684\u4e00\u591c\u3002 \u3000\u3000\u8fd9\u5230\u5e95\u8be5\u7b97\u662f\u6700\u7f8e\u597d\u7684\u6e29\u67d4\u98ce\u5149\uff0c\u8fd8\u662f\u8be5\u7b97\u662f\u4eba\u95f4\u60e8\u5267\u5462\uff1f \u3000\u3000\u675c\u671b\u6708\u4e0d\u7981\u5728\u5fc3\u4e2d\u6697\u6697\u8d28\u95ee\uff1a\u201c\u8001\u5929\u7237\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u4f5c\u5f04\u4eba\u5462\uff1f\u201d \u3000\u3000\u5176\u5b9e\uff0c\u9053\u5316\u5f04\u4eba\uff0c\u7531\u6765\u5df2\u4e45\uff0c\u53ea\u662f\u4e0d\u5230\u81ea\u5df1\u5934\u4e0a\uff0c\u4e0d\u4f1a\u60f3\u7f62\u4e86\u3002 \u3000\u3000------------- \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u4e8c\u5341\u516b\u7ae0 \u3000\u3000\u7b2c\u4e8c\u5929\u4e2d\u5348\u65f6\u5206\uff0c\u56db\u5339\u5feb\u9a6c\u51b2\u5165\u4e86\u626c\u5dde\u5e9c\u8859\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5f53\u65f6\u65e9\u5df2\u77e5\u9053\u4f1a\u6709\u8fd9\u6837\u51e0\u4e2a\u4eba\u6765\uff0c\u6240\u4ee5\u7acb\u523b\u4f1a\u89c1\u4e86\u4ed6\u4eec\u3002 \u3000\u3000\u8fd9\u56db\u4eba\u4e2d\uff0c\u6709\u4e24\u540d\u592a\u76d1\uff0c\u4e24\u540d\u5211\u90e8\u7684\u53f8\u5b98\u3002 \u3000\u3000\u592a\u76d1\u5e26\u6765\u4e86\u5723\u65e8\uff0c\u53f8\u5b98\u5e26\u6765\u4e86\u5211\u90e8\u7684\u4ee4\u8c15\u3002 \u3000\u3000\u5723\u65e8\u548c\u5211\u90e8\u4ee4\u8c15\u7684\u5185\u5bb9\uff0c\u7686\u5927\u51fa\u8bf8\u4eba\u7684\u9884\u6599\uff0c\u53ea\u6709\u7a0b\u5c0f\u8776\u4e00\u4e2a\u4eba\u6ca1\u6709\u663e\u51fa\u534a\u70b9\u5f88\u610f\u5916\u7684 \u8868\u60c5\u3002 \u3000\u3000\u5c06\u4e24\u540d\u592a\u76d1\u548c\u4e24\u540d\u5211\u90e8\u53f8\u5b98\u8bf7\u81f3\u5185\u5802\u4f11\u606f\u7528\u996d\u540e\uff0c\u738b\u5c11\u537f\u6367\u7740\u5723\u65e8\u53c8\u770b\u4e86\u4e00\u904d\uff0c\u95ee\u7a0b\u5c0f \u8776\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u539f\u6765\uff0c\u5723\u65e8\u662f\u8c03\u738b\u5c11\u537f\u7acb\u523b\u8d74\u4eac\u7684\uff0c\u5e76\u4e14\u5df2\u5c06\u4ed6\u7684\u5b98\u804c\u52a0\u81f3\u5211\u90e8\u4f8d\u90ce\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u73b0\u5728\uff0c\u5927\u4eba\u5df2\u662f\u6211\u4eec\u4e0a\u53f8\uff0c\u4f46\u6709\u6240\u8a00\uff0c\u6211\u4e00\u5b9a\u77e5\u65e0\u4e0d\u8a00\uff0c\u8a00\u65e0\u4e0d \u5c3d\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u603b\u6355\u5934\u6628\u5929\u8ba9\u6211\u51c6\u5907\u559c\u9152\uff0c\u96be\u9053\u5c31\u662f\u8fd9\u4e2a\u610f\u601d\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u603b\u6355\u5934\u4e0d\u662f\u8bf4\u8fc7\uff0c\u4eac\u4e2d\u76db\u4f20\u8981\u6211\u518d\u5728\u626c\u5dde\u7559\u4efb\u4e09\u5e74\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u662f\u4f20\u95fb\uff0c\u7687\u4e0a\u539f\u672c\u7684\u786e\u6253\u7b97\u8ba9\u5927\u4eba\u8981\u7559\u4efb\u626c\u5dde\u77e5\u5e9c\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u53ef\u7687\u4e0a\u4e3a\u4ec0\u4e48\u7a81\u7136\u6539\u53d8\u4e86\u4e3b\u610f\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5f53\u7136\u662f\u56e0\u4e3a\u626c\u5dde\u7684\u60c5\u51b5\u4e5f\u5728\u53d8\u5316\uff0c\u4eac\u91cc\u7684\u60c5\u51b5\u540c\u65f6\u4e5f\u5728\u53d8\u5316\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u662f\u7687\u4e0a\u63a5\u5230\u4e86\u603b\u6355\u5934\u5173\u4e8e\u626c\u5dde\u5f62\u52bf\u7684\u79d8\u594f\uff0c\u624d\u6539\u53d8\u4e3b\u610f\u7684\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u987f\u4e86\u987f\uff0c\u9053\uff1a\u201c\u95ee\u9898\u662f\uff0c\u738b\u67d0\u79bb\u5f00\u626c\u5dde\u4e4b\u540e\uff0c\u5c06\u7531\u8c01\u6765\u7ee7\u4efb\u626c\u5dde\u77e5\u5e9c\u5462\uff1f\u8fd9\u4eba\u4e0d \u662f\u4e5f\u4f1a\u6210\u4e3a\u6740\u624b\u96c6\u56e2\u7684\u76ee\u6807\u5417\uff1f\u201d \u3000\u3000\u6211\u679c\u7136\u6ca1\u6709\u770b\u9519\u4eba\u3002\u5728\u73b0\u5728\u8fd9\u79cd\u60c5\u51b5\u4e4b\u4e0b\uff0c\u4ed6\u60f3\u7684\u8fd8\u662f\u56fd\u5bb6\u4e4b\u4e8b\uff0c\u66f4\u4e3a\u7ee7\u4efb\u8005\u7684\u5b89\u5168\u62c5 \u5fc3\uff0c\u8fd9\u662f\u4f55\u7b49\u7684\u80f8\u895f\u554a\uff01\u7a0b\u5c0f\u8776\u5fc3\u4e2d\u5fd6\u9053\uff0c\u53e3\u4e2d\u5374\u7b54\u9053\uff1a\u201c\u5927\u4eba\u653e\u5fc3\uff0c\u6740\u624b\u96c6\u56e2\u5df2\u4e0d\u53ef\u80fd\u5c06 \u76ee\u5149\u76ef\u51c6\u626c\u5dde\u4e86\uff0c\u800c\u4e14\uff0c\u5c31\u7b97\u4ed6\u4eec\u4ecd\u7136\u4e00\u610f\u5b64\u884c\uff0c\u4e5f\u6ca1\u6709\u7528\u5904\u4e86\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u56e0\u4e3a\u7687\u4e0a\u8fd8\u6709\u4e00\u9053\u53e3\u8c15\uff0c\u662f\u5bf9\u5723\u65e8\u5185\u5bb9\u7684\u8865\u5145\uff01\u201d \u3000\u3000\u5979\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u6c34\u5e08\u63d0\u7763\u9a6c\u957f\u5c71\u63a5\u65e8\uff01\u201d \u3000\u3000\u9a6c\u957f\u5c71\u4e00\u6014\uff0c\u8dea\u5728\u5730\u4e0a\uff0c\u9053\uff1a\u201c\u81e3\u5728\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u7740\u4ee4\u6c34\u5e08\u63d0\u7763\u9a6c\u957f\u5c71\u6682\u7406\u626c\u5dde\uff0c\u5728\u65b0\u4efb\u77e5\u5e9c\u5230\u8fbe\u626c\u5dde\u524d\uff0c\u9a6c\u957f\u5c71\u5fc5\u987b\u514b\u5c3d \u804c\u5b88\uff0c\u4e0d\u5f97\u6709\u8bef\uff01\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u81e3\u9886\u65e8\uff0c\u8c22\u6069\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5750\u4e0b\u4e86\uff0c\u9053\uff1a\u201c\u9a6c\u5927\u4eba\u8bf7\u8d77\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6211\u53ef\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u9a6c\u5927\u4eba\u8bf7\u8bb2\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u7687\u4e0a\u4e3a\u4ec0\u4e48\u8981\u505a\u8fd9\u6837\u7684\u51b3\u5b9a\u5462\uff1f\u6211\u53ea\u662f\u4e00\u533a\u533a\u6b66\u58eb\uff0c\u53c8\u600e\u80fd\u7ba1\u7406\u5f97\u4e86\u626c\u5dde \u7684\u653f\u52a1\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u6211\u60f3\u8bf7\u4f60\u7559\u4e0b\u5211\u623f\u5e08\u7237\u5f20\u5b9d\u5584\uff0c\u4e0d\u77e5\u4f60\u662f\u5426\u613f\u610f\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u600e\u6562\u4e0d\u613f\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u9a6c\u5927\u4eba\uff0c\u5f20\u5e08\u7237\u719f\u901a\u540f\u9053\uff0c\u6709\u4ed6\u534f\u52a9\uff0c\u8fd8\u6101\u6709\u529e\u4e0d\u4e86\u7684\u4e8b\u561b\uff01\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u8c22\u738b\u5927\u4eba\uff0c\u8c22\u603b\u6355\u5934\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u5211\u90e8\u7684\u4ee4\u8c15\u53c8\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u5462\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u8bdd\u7684\u53e3\u6c14\u5f88\u6709\u4e9b\u4e0d\u81ea\u7136\uff0c\u53ea\u662f\u5176\u4ed6\u4eba\u6ca1\u6709\u542c\u51fa\u6765\u7f62\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5f53\u7136\u542c\u51fa\u6765\u4e86\uff0c\u5979\u6de1\u7136\u4e00\u7b11\uff0c\u770b\u4e86\u675c\u671b\u6708\u4e00\u773c\uff0c\u9053\uff1a\u201c\u675c\u53f8\u4e3b\u662f\u60f3\u4e0d\u901a\u5211\u90e8\u4e3a\u4ec0 \u4e48\u8981\u8ba9\u6211\u4eec\u5c06\u91d1\u5c0f\u7709\u5e26\u81f3\u4eac\u57ce\u53d7\u5ba1\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u5979\u7684\u8bed\u6c14\u548c\u4ee5\u524d\u6ca1\u4ec0\u4e48\u4e24\u6837\u3002 \u3000\u3000\u675c\u671b\u6708\u4e0d\u7981\u6000\u7591\u6628\u591c\u662f\u4e0d\u662f\u771f\u7684\u505a\u4e86\u4e00\u573a\u6625\u68a6\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u5f88\u6e05\u695a\uff0c\u90a3\u4e0d\u662f\u505a\u68a6\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u8fd9\u6837\u505a\uff0c\u662f\u4e3a\u4e86\u4fdd\u62a4\u4ed6\u4eec\u4e4b\u95f4\u90a3\u4efd\u611f\u60c5\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u662f\u7684\uff0c\u5c5e\u4e0b\u7684\u786e\u60f3\u4e0d\u901a\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u539f\u56e0\u5f88\u7b80\u5355\uff0c\u91d1\u5c0f\u7709\u662f\u4e00\u4e2a\u5f88\u91cd\u8981\u7684\u8bc1\u4eba\uff0c\u800c\u4e14\uff0c\u6211\u4eec\u8981\u5c3d\u5feb\u5c06\u91d1\u5c0f\u7709\u5373 \u5c06\u88ab\u9001\u8fdb\u4eac\u7684\u6d88\u606f\u4f20\u51fa\u53bb\uff0c\u5e76\u4e8e\u6b64\u540c\u65f6\uff0c\u5047\u653e\u8a00\u91d1\u5c0f\u7709\u5df2\u7ecf\u56de\u5fc6\u8d77\u4e86\u4e00\u6761\u975e\u5e38\u91cd\u8981\u7684\u7ebf\u7d22\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\u7684\u610f\u601d\u662f\uff0c\u5c06\u6740\u624b\u4eec\u5438\u5f15\u5230\u91d1\u5c0f\u7709\u8fd9\u8fb9\u6765\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u95ee\u9898\u662f\uff0c\u91d1\u5c0f\u7709\u7531\u8c01\u6765\u4fdd\u62a4\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u4eec\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u90a3\u738b\u5927\u4eba\u7531\u8c01\u6765\u4fdd\u62a4\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e5f\u662f\u6211\u4eec\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u603b\u6355\u5934\u6b64\u4e3e\u5fd7\u5728\u5438\u5f15\u6740\u624b\u96c6\u56e2\u7684\u9996\u8111\u73b0\u8eab\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4e0d\u4ec5\u8981\u5438\u5f15\u4ed6\u4eec\u7684\u9996\u8111\u73b0\u8eab\uff0c\u800c\u4e14\u8981\u5c06\u6740\u624b\u96c6\u56e2\u6240\u6709\u7684\u529b\u91cf\u5168\u90e8\u5438 \u5f15\u51fa\u6765\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5f15\u8bf1\u4ed6\u4eec\u4e0e\u6211\u4eec\u51b3\u6218\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u53ef\u6211\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u5728\u626c\u5dde\u4e0e\u4ed6\u4eec\u51b3\u6218\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5f62\u52bf\u5bf9\u6740\u624b\u96c6\u56e2\u548c\u9634\u8c0b\u7684\u7b56\u5212\u4eba\u5df2\u7ecf\u975e\u5e38\u4e0d\u5229\uff0c\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u6293\u4f4f\u8fd9\u6700\u540e\u4e00 \u6b21\u673a\u4f1a\uff0c\u6240\u4ee5\uff0c\u4e00\u5b9a\u4f1a\u5c3d\u51fa\u5b9e\u529b\u3002\u5982\u679c\u5728\u626c\u5dde\u57ce\u91cc\u51b3\u6218\uff0c\u52bf\u5fc5\u4f1a\u7978\u53ca\u767e\u59d3\uff0c\u6211\u4eec\u53ef\u4e0d\u80fd\u8ba9\u8fd9 \u6837\u4e00\u5ea7\u540d\u57ce\u6bc1\u5728\u6211\u4eec\u624b\u91cc\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u603b\u6355\u5934\u8981\u8bf1\u4f7f\u4ed6\u4eec\u5728\u8fdb\u4eac\u9014\u4e2d\u4e0e\u6211\u4eec\u51b3\u6218\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u53ef\u6211\u4eec\u7684\u5b9e\u529b\u548c\u4ed6\u4eec\u6bd4\u8d77\u6765\uff0c\u4f1a\u4e0d\u4f1a\u5dee\u4e0a\u4e00\u7b79\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u8fd9\u4e2a\u597d\u529e\uff0c\u9a6c\u67d0\u6d3e\u4e94\u5343\u6c34\u5e08\uff0c\u62a4\u9001\u4f60\u4eec\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u53ef\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u9a6c\u63d0\u7763\u624b\u4e0b\u6c34\u5e08\u662f\u4e00\u652f\u5168\u56fd\u95fb\u540d\u7684\u7cbe\u9510\u90e8\u961f\uff0c\u7531\u4f60\u4eec\u62a4\u9001\uff0c\u6740\u624b\u4eec\u5c31\u4f1a\u77e5 \u96be\u800c\u9000\uff0c\u5168\u90e8\u8f6c\u5165\u6697\u4e2d\uff0c\u5f85\u673a\u800c\u52a8\uff0c\u5219\u6211\u4eec\u5c31\u8fbe\u4e0d\u5230\u5f7b\u5e95\u94f2\u9664\u4ed6\u4eec\u7684\u76ee\u7684\u4e86\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u90a3\u662f\u603b\u6355\u5934\u51c6\u5907\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u7531\u6211\u3001\u5c0f\u6587\u3001\u5c0f\u96c5\u3001\u60dc\u7389\u3001\u56db\u5927\u6355\u5934\u3001\u6797\u53f8\u6848\u548c\u6c5f\u5357\u3001\u4e2d\u5dde\u56db\u5206\u53f8\u7684\u6570\u5341 \u597d\u624b\uff0c\u62a4\u9001\u5927\u4eba\u548c\u91d1\u5c0f\u7709\u8fdb\u4eac\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u8fd9\u6837\u592a\u5371\u9669\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u963f\u6a2a\u548c\u963f\u4fdd\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4ed6\u4e8c\u4eba\u7559\u5728\u626c\u5dde\uff0c\u8d1f\u8d23\u4fdd\u62a4\u9a6c\u5927\u4eba\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u8fd9\u4e0d\u884c\uff0c\u4f60\u4eec\u7684\u5b9e\u529b\uff0c\u672c\u6765\u5c31\u5f88\u6709\u4e9b\u4e0d\u591f\uff0c\u518d\u7559\u4e0b\u963f\u6a2a\u3001\u963f\u4fdd\u4e24\u5458\u608d\u5c06\uff0c \u5b9e\u529b\u66f4\u662f\u53d7\u635f\u3002\u603b\u6355\u5934\uff0c\u9a6c\u67d0\u9ebe\u4e0b\u96c4\u5175\u6570\u4e07\uff0c\u6218\u5c06\u4e91\u96c6\uff0c\u4e0d\u7528\u7559\u4e0b\u4ed6\u4e8c\u4eba\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u9a6c\u5927\u4eba\u4e0d\u7528\u5fc3\u6025\uff0c\u542c\u6211\u628a\u8bdd\u8bf4\u5b8c\u3002\u6211\u4eec\u6b64\u884c\uff0c\u662f\u4e3a\u4e86\u8bf1\u654c\uff0c\u5b9e\u529b\u592a\u5f3a\uff0c\u53cd \u800c\u8fbe\u4e0d\u5230\u76ee\u7684\uff0c\u6740\u624b\u4eec\u7684\u6b66\u529f\u6781\u9ad8\uff0c\u5982\u679c\u771f\u7684\u6709\u4eba\u6765\u884c\u523a\u9a6c\u5927\u4eba\uff0c\u5219\u6c34\u5e08\u5175\u5c06\u5f88\u96be\u8d77\u5230\u592a\u5927 \u7684\u4f5c\u7528\uff0c\u6240\u4ee5\u6211\u624d\u7559\u4e0b\u963f\u6a2a\u3001\u963f\u4fdd\uff0c\u800c\u4e14\uff0c\u6211\u51c6\u5907\u2026\u2026\u201d \u3000\u3000\u5979\u538b\u4f4e\u58f0\u97f3\uff0c\u5982\u6b64\u8fd9\u822c\u8bf4\u4e86\u4e00\u901a\uff0c\u542c\u5f97\u9a6c\u957f\u5c71\u3001\u738b\u5c11\u537f\u4e0d\u4f4f\u70b9\u5934\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e8c\u4f4d\u5927\u4eba\u653e\u5fc3\u4e86\u5427\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u7684\u786e\u662f\u597d\u8ba1\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u8fd9\u6837\uff0c\u5e94\u8be5\u5c31\u4e07\u65e0\u4e00\u5931\u4e86\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u4e86\u70b9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u65e2\u7136\u4e8c\u4f4d\u5927\u4eba\u90fd\u540c\u610f\u4e86\u6211\u7684\u8ba1\u5212\uff0c\u6211\u4eec\u73b0\u5728\u5c31\u8981\u5f00\u59cb\u884c\u52a8 \u4e86\u3002\u201d \u3000\u3000\u5979\u5bf9\u60dc\u7389\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u548c\u5c0f\u6587\u3001\u5c0f\u96c5\u53bb\u91d1\u5e9c\uff0c\u8ba9\u91d1\u767e\u5e74\u4e3a\u91d1\u5c0f\u7709\u51c6\u5907\u4e00\u4e9b\u7b80\u5355\u7684\u751f\u6d3b \u7528\u5177\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u5e26\u7740\u5c0f\u6587\u3001\u5c0f\u96c5\u5306\u5306\u79bb\u5f00\u4e86\u5e9c\u8859\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u8bf7\u4f60\u53bb\u544a\u8bc9\u5f20\u5b9d\u5584\uff0c\u8ba9\u4ed6\u53bb\u8f66\u884c\u8ba2\u51e0\u8f86\u7ed3\u5b9e\u7684\u9a6c\u8f66\uff0c\u800c\u4e14\u4e00\u5b9a\u8981 \u5c06\u5927\u4eba\u5373\u5c06\u81f3\u4eac\u57ce\u4e0a\u4efb\u7684\u6d88\u606f\u4f20\u51fa\u53bb\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u603b\u6355\u5934\u653e\u5fc3\uff0c\u5f20\u5e08\u7237\u505a\u8d77\u8fd9\u4e9b\u4e8b\u6765\uff0c\u4e00\u5411\u5341\u5206\u7a33\u59a5\u3002\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u4ed6\u53bb\u627e\u5f20\u5b9d\u5584\u53bb\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5bf9\u56db\u5927\u6355\u5934\u9053\uff1a\u201c\u4f60\u4eec\u4e5f\u90fd\u5404\u81ea\u53bb\u51c6\u5907\u4e00\u4e0b\u5427\uff0c\u6700\u8fdf\u660e\u65e5\u5929\u4eae\uff0c\u6211\u4eec\u5c31\u5c06\u52a8\u8eab \u4e86\u3002\u201d \u3000\u3000\u56db\u5927\u6355\u5934\u9886\u547d\u800c\u53bb\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u9a6c\u67d0\u4e5f\u8981\u53bb\u51c6\u5907\u4e00\u4e0b\uff0c\u5728\u8425\u4e2d\u6311\u9009\u4e00\u6279\u7cbe\u608d\u7684\u58eb\u5175\u548c\u52c7\u731b\u7684\u5927 \u5c06\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\uff0c\u4e0d\u8fc7\u8981\u8ba9\u963f\u6a2a\u3001\u963f\u4fdd\u966a\u5927\u4eba\u4e00\u8d77\u53bb\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u62f1\u624b\u9053\uff1a\u201c\u8c22\u603b\u6355\u5934\u5173\u5fc3\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8bf7\u5927\u4eba\u4e00\u5b9a\u8981\u6ce8\u610f\u4fdd\u5bc6\uff0c\u9664\u4e86\u5fc3\u8179\u5c06\u9886\uff0c\u4e0d\u5f97\u8d70\u6f0f\u534a\u70b9\u98ce\u58f0\uff0c\u81f3\u4e8e\u519b\u58eb\u561b\uff0c \u4e0d\u59a8\u5148\u544a\u8bc9\u4ed6\u4eec\u592a\u6e56\u4e00\u5e26\u53c8\u51fa\u73b0\u4e86\u6c34\u76d7\uff0c\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u603b\u6355\u5934\u653e\u5fc3\u597d\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u70b9\uff0c\u9a6c\u5927\u4eba\u4e00\u5b9a\u8981\u7279\u522b\u6ce8\u610f\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u603b\u6355\u5934\u8bf7\u8bb2\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u65f6\u95f4\u3002\u9a6c\u5927\u4eba\u4e00\u5b9a\u8981\u6ce8\u610f\u65f6\u95f4\uff0c\u4e0d\u80fd\u65e9\uff0c\u4e5f\u4e0d\u80fd\u665a\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u4e2a\u9a6c\u67d0\u77e5\u9053\uff0c\u5c06\u519b\u51b3\u80dc\u5343\u91cc\uff0c\u6700\u91cd\u8981\u7684\uff0c\u5c31\u662f\u65f6\u673a\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u6211\u6709\u70b9\u73ed\u95e8\u5f04\u65a7\u4e4b\u5acc\u4e86\uff0c\u9a6c\u5927\u4eba\u4e0d\u8981\u89c1\u602a\u54e6\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u4e5f\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u54ea\u91cc\uff0c\u9a6c\u67d0\u5148\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8d70\u4e86\u6ca1\u4e00\u4f1a\u513f\uff0c\u60dc\u7389\u3001\u5c0f\u6587\u3001\u5c0f\u96c5\u56de\u6765\u4e86\uff0c\u60dc\u7389\u624b\u4e2d\u63d0\u4e86\u4e00\u4e2a\u5927\u5305\u88b1\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u95ee\u9053\uff1a\u201c\u91d1\u767e\u5e74\u6ca1\u6709\u8bf4\u4ec0\u4e48\u5417\uff1f\u201d\u60dc\u7389\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u90a3\u5c31\u597d\u3002\u60dc\u7389\uff0c\u4f60\u53bb\u5e2e\u91d1\u5c0f\u7709\u51c6\u5907\u4e00\u4e0b\uff0c\u5c0f\u6587\u3001\u5c0f\u96c5\uff0c\u4f60\u4eec\u4e5f\u8981\u51c6\u5907\u5145\u5206 \u624d\u597d\u3002\u201d \u3000\u3000\u5c0f\u6587\uff0c\u5c0f\u96c5\u7b11\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u6211\u4eec\u8ddf\u7740\u4f60\u8fd9\u4e48\u591a\u5e74\u4e86\uff0c\u4f60\u8fd8\u4e0d\u653e\u5fc3\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e25\u8083\u5730\u9053\uff1a\u201c\u4e0d\u662f\u4e0d\u653e\u5fc3\uff0c\u53ea\u662f\u8fd9\u4e00\u6b21\u7684\u884c\u52a8\u4e8b\u5173\u91cd\u5927\uff0c\u6211\u4e0d\u80fd\u4e0d\u5c0f\u5fc3\u518d\u5c0f\u5fc3\uff0c \u8c28\u614e\u53c8\u8c28\u614e\u624d\u884c\u554a\uff01\u201d \u3000\u3000\u60dc\u7389\u3001\u5c0f\u6587\u3001\u5c0f\u96c5\u9053\uff1a\u201c\u6211\u4eec\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u6b63\u8bf4\u7740\u8bdd\uff0c\u738b\u5c11\u537f\u548c\u5f20\u5b9d\u5584\u8fdb\u6765\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5bf9\u4e09\u5973\u6325\u4e86\u6325\u624b\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u53bb\u5427\uff01\u201d \u3000\u3000\u8f6c\u800c\u95ee\u5f20\u5b9d\u5584\uff1a\u201c\u4e8b\u60c5\u90fd\u59a5\u4e86\uff1f\u201d \u3000\u3000\u5f20\u5b9d\u5584\u70b9\u5934\u9053\uff1a\u201c\u90fd\u59a5\u4e86\u3002\u4e00\u5171\u8ba2\u4e86\u4e09\u8f86\u9a6c\u8f66\u3002\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u55ef\uff0c\u4e09\u8f86\u9a6c\u8f66\u5df2\u7ecf\u591f\u7528 \u4e86\u3002\u738b\u5927\u4eba\u5373\u5c06\u5347\u4efb\u5211\u90e8\u4f8d\u90ce\u7684\u6d88\u606f\u5462\uff1f\u4f20\u51fa\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u662f\u3002\u5df2\u7ecf\u4f20\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5916\u9762\u767e\u59d3\u7684\u53cd\u5e94\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u767e\u59d3\u4eec\u90fd\u820d\u4e0d\u5f97\u738b\u5927\u4eba\u8d70\uff0c\u6b63\u5546\u91cf\u7740\u51c6\u5907\u9009\u51fa\u4ee3\u8868\uff0c\u7559\u4e0b\u738b\u5927\u4eba\u5462\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u6700\u62c5\u5fc3\u7684\uff0c\u5c31\u662f\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u8f66\u884c\u8001\u677f\u548c\u597d\u51e0\u4e2a\u5546\u4eba\u4e00\u518d\u8ffd\u7a76\u95ee\u6211\u5927\u4eba\u4ec0\u4e48\u65f6\u5019\u52a8\u8eab\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u600e\u6837\u8bf4\u7684\uff1f\u201d \u3000\u3000\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u6211\u544a\u8bc9\u4ed6\u4eec\uff0c\u81f3\u5c11\u8981\u7b49\u534a\u4e2a\u6708\uff0c\u65b0\u4efb\u77e5\u5e9c\u6765\u4e86\uff0c\u5927\u4eba\u624d\u4f1a\u8d70\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8bf4\u5f97\u597d\uff0c\u5f20\u5e08\u7237\uff0c\u4f60\u5f88\u6709\u624d\u5e72\u554a\uff01\u201d \u3000\u3000\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u603b\u6355\u5934\u5938\u5956\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u7559\u4f60\u5728\u626c\u5dde\u534f\u52a9\u9a6c\u5927\u4eba\u4e00\u6bb5\u65f6\u95f4\uff0c\u4f60\u4e0d\u4f1a\u602a\u6211\u5427\uff1f\u201d \u3000\u3000\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u603b\u6355\u5934\u8a00\u91cd\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u653e\u5fc3\u597d\u4e86\uff0c\u7b49\u4e8b\u6001\u4e00\u5e73\u606f\uff0c\u6211\u4f1a\u8ba9\u738b\u5927\u4eba\u8bf7\u4f60\u53bb\u4eac\u57ce\u7684\u3002\u201d \u3000\u3000\u5f20\u5b9d\u5584\u5927\u559c\uff0c\u9053\uff1a\u201c\u591a\u8c22\u603b\u6355\u5934\u7f8e\u610f\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u4e5f\u53bb\u5e2e\u7740\u738b\u5927\u4eba\u6536\u62fe\u4e00\u4e0b\u7ec6\u8f6f\u5427\uff01\u201d \u3000\u3000\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4ed6\u521a\u8d70\u51fa\u95e8\uff0c\u5374\u53c8\u8fd4\u4e86\u56de\u6765\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u95ee\u9053\uff1a\u201c\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5f20\u5b9d\u5584\u9053\uff1a\u201c\u738b\u575a\u548c\u4f55\u5927\u5149\u8981\u6c42\u89c1\u603b\u6355\u5934\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5feb\u8bf7\u3002\u201d \u3000\u3000\u738b\u3001\u4f55\u4e8c\u4eba\u4e00\u8fdb\u5927\u5385\uff0c\u5c31\u8dea\u4e0b\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u773c\u4e2d\uff0c\u90fd\u542b\u7740\u6cea\u5149\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e8c\u4f4d\u5feb\u8bf7\u8d77\u6765\uff0c\u6709\u4ec0\u4e48\u8bdd\uff0c\u8bf7\u76f4\u8bf4\uff0c\u53ea\u8981\u6211\u80fd\u505a\u5230\u7684\uff0c\u4e00\u5b9a\u66ff\u4e8c\u4f4d\u529e\u5230\uff0c \u51b3\u4e0d\u98df\u8a00\u3002\u201d \u3000\u3000\u738b\u575a\u53cc\u6cea\u4ea4\u6d41\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6211\u4eec\u4e5f\u8981\u968f\u738b\u5927\u4eba\u4e00\u540c\u4e0a\u4eac\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u4eec\u7684\u4f24\u90fd\u597d\u4e86\uff01\u201d \u3000\u3000\u4f55\u5927\u5149\u9053\uff1a\u201c\u597d\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6b64\u884c\u5341\u5206\u51f6\u9669\u2026\u2026\u201d \u3000\u3000\u4f55\u5927\u5149\u5927\u58f0\u9053\uff1a\u201c\u6b63\u56e0\u4e3a\u6b64\u884c\u5341\u5206\u51f6\u9669\uff0c\u6211\u4eec\u624d\u8981\u4e00\u8d77\u53bb\uff0c\u597d\u4fdd\u62a4\u5927\u4eba\uff0c\u518d\u8bf4\u2026\u2026\u201d \u3000\u3000\u4ed6\u54fd\u54bd\u7740\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u6765\u3002 \u3000\u3000\u738b\u575a\u9053\uff1a\u201c\u518d\u8bf4\uff0c\u8fd9\u6b21\u6740\u624b\u96c6\u56e2\u7684\u9996\u8111\u4e00\u5b9a\u4f1a\u51fa\u73b0\u7684\uff0c\u6211\u548c\u4f55\u5144\u8981\u4e3a\u94c1\u7fce\u5144\u62a5\u4ec7\uff01\u201d \u3000\u3000\u63d0\u5230\u94c1\u7fce\uff0c\u7a0b\u5c0f\u8776\u5fc3\u91cc\u4e0d\u7981\u4e5f\u662f\u4e00\u9635\u96be\u8fc7\uff0c\u770b\u4e86\u738b\u5c11\u537f\u4e00\u773c\uff0c\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u4f60\u770b\u5462\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4ed6\u4e8c\u4eba\u8ddf\u968f\u738b\u67d0\u4eba\u591a\u5e74\uff0c\u800c\u4e14\uff0c\u4e5f\u662f\u94c1\u7fce\u7684\u597d\u670b\u53cb\uff0c\u5c31\u8ba9\u4ed6\u4eec\u53bb\u5427\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\u5427\u3002\u4e0d\u8fc7\uff0c\u4e8c\u4f4d\u4e00\u5b9a\u8981\u6ce8\u610f\u4fdd\u62a4\u81ea\u5df1\uff0c\u4e0d\u8981\u62a5\u4ec7\u5fc3\u5207\uff0c\u5fd8\u4e86\u81ea\u8eab\u7684\u5b89\u5168\u3002 \u738b\u5927\u4eba\u5df2\u5347\u4efb\u5211\u90e8\u4f8d\u90ce\uff0c\u4ee5\u4e8c\u4f4d\u7684\u6b66\u529f\uff0c\u4ee5\u540e\u738b\u5927\u4eba\u80af\u5b9a\u8fd8\u6709\u6025\u9700\u4e8c\u4f4d\u7684\u5730\u65b9\uff0c\u518d\u8bf4\u5211\u90e8\u603b \u6355\u53f8\u4e5f\u5f88\u9700\u8981\u4e8c\u4f4d\u8fd9\u6837\u7684\u4eba\u624d\u3002\u201d \u3000\u3000\u738b\u575a\u3001\u4f55\u5927\u5149\u53e9\u9996\u9053\uff1a\u201c\u8c22\u603b\u6355\u5934\uff0c\u8c22\u738b\u5927\u4eba\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u53bb\u505a\u4e9b\u51c6\u5907\u5427\uff01\u201d \u3000\u3000\u738b\u575a\u9053\uff1a\u201c\u6211\u4eec\u672c\u662f\u6c5f\u6e56\u4e2d\u4eba\uff0c\u5b51\u7136\u4e00\u8eab\uff0c\u6ca1\u4ec0\u4e48\u597d\u51c6\u5907\u7684\u3002\u201d \u3000\u3000\u4f55\u5927\u5149\u9053\uff1a\u201c\u662f\u7684\uff0c\u53ea\u8981\u603b\u6355\u5934\u53f7\u4ee4\u4e00\u58f0\uff0c\u6211\u4eec\u968f\u65f6\u90fd\u53ef\u62d4\u811a\u800c\u8d70\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5f88\u597d\uff0c\u90a3\u4f60\u4eec\u5c31\u53bb\u4f11\u606f\uff0c\u4e00\u5b9a\u8981\u517b\u597d\u4f53\u529b\uff0c\u624d\u80fd\u62a5\u4ec7\uff01\u201d \u3000\u3000\u4e8c\u4eba\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u770b\u7740\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u603b\u6355\u5934\u4e5f\u8be5\u4f11\u606f\u4e00\u4e0b\u4e86\uff0c\u8fde\u65e5\u52b3\u7d2f\uff0c\u4f60\u5df2\u6d88\u7626\u4e86\u4e00\u4e9b\uff0c\u773c\u5708\u4e5f \u6709\u4e9b\u53d1\u9ed1\uff0c\u662f\u4e0d\u662f\u6628\u665a\u6ca1\u6709\u7761\u597d\uff1f\u201d \u3000\u3000\u83ab\u975e\u738b\u5927\u4eba\u770b\u7834\u4e86\u6211\u548c\u675c\u671b\u6708\u2026\u2026\u7a0b\u5c0f\u8776\u5fc3\u91cc\u4e0d\u7981\u4e00\u52a8\uff0c\u8138\u988a\u5ffd\u7136\u6709\u4e9b\u7ea2\u6655\u3002 \u3000\u3000\u738b\u5c11\u537f\u5f88\u6709\u4e9b\u5947\u602a\uff0c\u600e\u4e48\u6211\u8fd9\u4e00\u53e5\u8bdd\u4f7f\u5f97\u5979\u8138\u7ea2\u4e86\uff1f\u662f\u4e0d\u662f\u8bdd\u8bf4\u5f97\u592a\u5510\u7a81\uff1f \u3000\u3000\u7a0b\u5c0f\u8776\u7ad9\u8d77\u8eab\u5b50\uff0c\u61d2\u6563\u5730\u9053\uff1a\u201c\u5c5e\u4e0b\u8c22\u5927\u4eba\u5173\u7231\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u5413\u4e86\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\u4e3a\u4f55\u5982\u6b64\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5927\u4eba\u5df2\u5347\u4efb\u5211\u90e8\u4f8d\u90e8\uff0c\u6211\u81ea\u7136\u5df2\u662f\u5927\u4eba\u7684\u4e0b\u5c5e\u4e86\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u738b\u67d0\u8fd9\u6761\u547d\u5982\u679c\u4e0d\u662f\u4f60\u8d76\u6765\u626c\u5dde\uff0c\u53ea\u6015\u65e9\u5df2\u4e22\u6389\u4e86\u3002\u518d\u8bf4\uff0c\u4e3a\u4e86 \u738b\u67d0\u7684\u5b89\u5168\uff0c\u603b\u6355\u5934\u66f4\u5c06\u5c0f\u6587\u59d1\u5a18\u7ed9\u4e86\u738b\u67d0\uff0c\u738b\u67d0\u6b64\u751f\u7edd\u4e0d\u4f1a\u5fd8\u603b\u6355\u5934\u7684\u6069\u60c5\u3002\u6240\u4ee5\uff0c\u603b\u6355 \u5934\u4ee5\u540e\u4e0d\u8981\u8fd9\u6837\u624d\u597d\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u591a\u8c22\u5927\u4eba\u3002\u201d \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u675c\u671b\u6708\u4e0b\u8f66\u63a0\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u91d1\u767e\u5e74\u6c42\u89c1\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u73b0\u541f\u9053\uff1a\u201c\u4ed6\u5e26\u4e86\u591a\u5c11\u4eba\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u7ba1\u8d3e\u82f1\u53ca\u7f57\u6d6e\u4e09\u5251\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6709\u8bf7\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u7b54\u5e94\u4e00\u58f0\uff0c\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5927\u4eba\uff0c\u8bf7\u4f60\u5230\u540e\u5802\u6682\u907f\u4e00\u4e0b\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u767e\u5e74\u6b64\u6765\uff0c\u80af\u5b9a\u662f\u4e3a\u4e86\u91d1\u5c0f\u7709\uff0c\u6211\u6015\u6709\u4e9b\u8bdd\u5927\u4eba\u4e0d\u597d\u8bf4\uff0c\u6709\u4e9b\u4e3a\u96be\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u70b9\u4e86\u70b9\u5934\uff0c\u8f6c\u8eab\u8d70\u5230\u4e86\u5c4f\u98ce\u540e\u7684\u540e\u5802\u8d70\u4e86\u3002 \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u513f\uff0c\u91d1\u767e\u5e74\u548c\u8d3e\u82f1\u884c\u8fdb\u4e86\u5927\u5385\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u91d1\u8001\uff0c\u4f60\u6765\u662f\u60f3\u770b\u770b\u4ee4\u7231\u7684\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u662f\u554a\uff0c\u603b\u6355\u5934\uff0c\u8001\u592b\u5c31\u5c0f\u7709\u4e00\u4e2a\u5b69\u5b50\uff0c\u8fd9\u4e00\u4e0b\u8bf4\u8d70\u5c31\u8d70\uff0c\u771f\u6709\u4e9b\u820d\u4e0d\u5f97\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e5f\u4e0d\u662f\u5f88\u5feb\u5c31\u8d70\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4e0d\u77e5\u738b\u5927\u4eba\u4ec0\u4e48\u65f6\u5019\u52a8\u8eab\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u738b\u5927\u4eba\u6015\u767e\u59d3\u76f8\u7559\uff0c\u5bf9\u5916\u5ba3\u79f0\u534a\u4e2a\u6708\u540e\u52a8\u8eab\u3002\u5176\u5b9e\uff0c\u6211\u4eec\u51c6\u5907\u4e09\u5929\u540e\u5c31\u8d70\uff0c \u53ef\u4ee5\u514d\u53bb\u5f88\u591a\u9ebb\u70e6\u3002\u91d1\u8001\uff0c\u56e0\u4e3a\u6709\u4ee4\u5a9b\u968f\u884c\uff0c\u6211\u624d\u5bf9\u4f60\u8bf4\u51fa\u4e86\u5b9e\u60c5\uff0c\u4f60\u53ef\u4e0d\u80fd\u6cc4\u9732\u6d88\u606f\u54df\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u603b\u6355\u5934\u8bf7\u653e\u5fc3\u3002\u201d \u3000\u3000\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u50cf\u662f\u6709\u4ec0\u4e48\u8bdd\uff0c\u5374\u53c8\u4e0d\u597d\u51fa\u53e3\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u8001\uff0c\u6709\u8bdd\u76f4\u7ba1\u8bf4\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u5c0f\u5973\u7684\u786e\u4e0d\u662f\u771f\u51f6\u3002\u8fd9\u4e00\u70b9\uff0c\u603b\u6355\u5934\u548c\u738b\u5927\u4eba\u4e5f\u6e05\u695a\uff0c\u4e3a\u4ec0\u4e48\u5211\u90e8\u8981\u62db\u5979 \u4e0a\u4eac\u53bb\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u91d1\u8001\u9519\u4e86\uff0c\u4e0d\u662f\u62db\u5979\u4e0a\u4eac\uff0c\u800c\u662f\u8bf7\u5979\u4e0a\u4eac\uff0c\u4f5c\u4e3a\u4e00\u4e2a\u91cd\u8981\u7684\u8bc1\u4eba\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u6b64\u6848\u5df2\u7ecf\u6848\u60c5\u5927\u767d\uff0c\u8fd9\u6837\u505a\u6709\u4ec0\u4e48\u5fc5\u8981\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u8001\u4e0d\u8981\u89c1\u602a\uff0c\u56e0\u4e3a\u6b64\u6848\u6848\u60c5\u602a\u5f02\uff0c\u5df2\u7ecf\u60ca\u52a8\u4e86\u5211\u90e8\uff0c\u6240\u4ee5\u738b\u5927\u4eba\u5df2\u7ecf\u625b \u4e0d\u4e0b\u6765\u4e86\u3002\u4e0d\u8fc7\uff0c\u91d1\u8001\u8bf7\u653e\u5fc3\uff0c\u738b\u5927\u4eba\u5347\u4efb\u5211\u90e8\u4f8d\u90ce\uff0c\u5c31\u66f4\u80fd\u66ff\u4ee4\u5a9b\u8bf4\u8bdd\u4e86\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u91d1\u8001\uff0c\u5b9e\u8bdd\u5bf9\u4f60\u8bf4\u4e86\u5427\uff0c\u5e26\u91d1\u5c0f\u7709\u4e0a\u4eac\uff0c\u5176\u5b9e\u662f\u4e00\u79cd\u4fdd\u62a4\u5979\u7684\u624b \u6bb5\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u8001\u5e94\u8be5\u77e5\u9053\uff0c\u671d\u5ef7\u60ef\u4f8b\uff0c\u5982\u65b0\u5b98\u6ca1\u6709\u5230\u5e9c\uff0c\u524d\u4efb\u662f\u4e0d\u80fd\u8d70\u7684\uff0c\u800c\u738b\u5927\u4eba \u4e09\u5929\u540e\u5c31\u8981\u52a8\u8eab\uff0c\u91d1\u8001\u53ef\u77e5\u9053\u8fd9\u4e48\u505a\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u83ab\u975e\uff0c\u7ee7\u4efb\u8005\u5c31\u662f\u9644\u8fd1\u67d0\u4e00\u5dde\u53bf\u7684\u5b98\u5458\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u7684\u786e\u662f\u9644\u8fd1\u7684\u671d\u5ef7\u8981\u5458\uff0c\u53ea\u4e0d\u8fc7\u4e0d\u662f\u67d0\u4e00\u5dde\u7684\u5b98\u5458\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u4e00\u6014\uff0c\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u83ab\u4e0d\u662f\u2026\u2026\u83ab\u4e0d\u662f\u9a6c\u957f\u5c71\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4ed6\u662f\u6c34\u5e08\u63d0\u7763\uff0c\u671d\u5ef7\u600e\u4e48\u4f1a\u8ba9\u4e00\u5458\u6b66\u5c06\u6765\u5f53\u626c\u5dde\u77e5\u5e9c\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u56e0\u4e3a\u671d\u5ef7\u6025\u7740\u8c03\u7528\u738b\u5927\u4eba\uff0c\u4e00\u65f6\u53c8\u627e\u4e0d\u5230\u5408\u9002\u7684\u7ee7\u4efb\u8005\uff0c\u6240\u4ee5\u8ba9\u9a6c\u63d0\u7763\u6682 \u65f6\u4ee3\u7406\u626c\u5dde\u5e9c\u3002\u91d1\u8001\uff0c\u4f60\u60f3\uff0c\u5982\u679c\u91d1\u5c0f\u7709\u4e0d\u548c\u738b\u5927\u4eba\u4e00\u540c\u4e0a\u4eac\uff0c\u7559\u5728\u626c\u5dde\u5e9c\uff0c\u843d\u5230\u9a6c\u5927\u4eba\u624b \u4e2d\uff0c\u4f1a\u662f\u4e00\u4e2a\u4ec0\u4e48\u7ed3\u679c\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u604d\u7136\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u591a\u8c22\u603b\u6355\u5934\u5bf9\u5c0f\u5973\u7684\u539a\u7231\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u91d1\u8001\u53ef\u4e0d\u8981\u8c22\u9519\u4e86\u4eba\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u4e00\u6014\uff0c\u9053\uff1a\u201c\u90a3\u8be5\u8c22\u8c01\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5f53\u7136\u662f\u738b\u5927\u4eba\u3002\u91d1\u8001\u77e5\u4e0d\u77e5\u9053\uff0c\u738b\u5927\u4eba\u5df2\u6709\u610f\u5c06\u91d1\u5c0f\u7709\u8ba4\u4f5c\u4e49\u5973\u5462\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u7b11\u9053\uff1a\u201c\u90a3\u592a\u597d\u4e86\uff0c\u91d1\u67d0\u53ef\u662f\u4e0d\u6562\u9ad8\u6500\u554a\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u4e0b\u91d1\u8001\u653e\u5fc3\u4e86\u5427\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u592a\u653e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u8ba9\u4eba\u8bf7\u4ee4\u5a9b\u6765\uff0c\u4f60\u4eec\u7236\u5973\u89c1\u4e00\u9762\uff0c\u5982\u4f55\uff1f\u5176\u5b9e\uff0c\u4e5f\u4e0d\u7528\u7740\u6025\uff0c\u53cd\u6b63\u738b\u5927 \u4eba\u4e09\u5929\u540e\u624d\u52a8\u8eab\uff0c\u8fd9\u51e0\u5929\u91cc\uff0c\u91d1\u8001\u968f\u65f6\u53ef\u4ee5\u6765\u770b\u4ee4\u5a9b\u561b\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\u3002\u55ef\uff0c\u6211\u5dee\u70b9\u5fd8\u4e86\uff0c\u8fd9\u91cc\u6709\u51e0\u5c01\u4fe1\uff0c\u8bf7\u603b\u6355\u5934\u4ea4\u7ed9\u5c0f\u7709\u3002\u8001\u592b \u5728\u4eac\u91cc\u4e5f\u6709\u51e0\u53f7\u751f\u610f\uff0c\u51e0\u4f4d\u670b\u53cb\uff0c\u6240\u4ee5\u7279\u610f\u5e26\u4fe1\u7ed9\u4ed6\u4eec\uff0c\u8bf7\u4ed6\u4eec\u7167\u987e\u4e00\u4e0b\u5c0f\u5973\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u8001\u653e\u5fc3\uff0c\u6211\u4e00\u5b9a\u5e26\u5230\u3002\u201d \u3000\u3000\u5979\u987f\u4e86\u987f\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u91d1\u8001\uff0c\u8fd9\u51e0\u5929\u91cc\u68a6\u5e7b\u4e4b\u5200\u53ef\u66fe\u53bb\u8fc7\u91d1\u5e9c\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u770b\u4e86\u8d3e\u82f1\u4e00\u773c\u3002 \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u6211\u4eec\u7684\u9632\u5b88\u4e5f\u5f88\u68ee\u4e25\uff0c\u53ef\u80fd\u662f\u4e00\u65f6\u6ca1\u6709\u4e0b\u624b\u7684\u673a\u4f1a\uff0c\u6240\u4ee5\u68a6\u5e7b\u4e4b\u5200\u4e00\u76f4\u6ca1\u6709 \u73b0\u8eab\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e5f\u53ef\u80fd\u4ed6\u4e09\u5b9d\u5230\u624b\u4e4b\u540e\uff0c\u5df2\u7ecf\u79bb\u5f00\u626c\u5dde\u4e86\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u8001\u592b\u4e5f\u6709\u8fd9\u4e2a\u63a8\u60f3\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8bdd\u662f\u8fd9\u6837\u8bf4\uff0c\u53ef\u91d1\u8001\u8fd8\u662f\u8981\u6ce8\u610f\u9632\u536b\u624d\u597d\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u8c22\u603b\u6355\u5934\u5173\u5fc3\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53c8\u9053\uff1a\u201c\u6700\u91cd\u8981\u7684\u662f\uff0c\u91d1\u8001\u4e00\u5b9a\u8981\u6ce8\u610f\u5e9c\u4e2d\u662f\u5426\u6709\u4eba\u8eab\u5206\u53ef\u7591\uff0c\u5982\u679c\u518d\u6709\u4e00\u4e2a\u5929 \u67ab\u9053\u957f\u90a3\u6837\u7684\u5185\u5978\uff0c\u5c31\u5371\u9669\u4e86\u3002\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u51e0\u5929\u6765\uff0c\u8d3e\u67d0\u5df2\u5c06\u5e9c\u4e2d\u536b\u58eb\u7684\u6765\u5386\u53c8\u666e\u67e5\u4e86\u4e00\u904d\uff0c\u53ea\u6d3e\u6700\u53ef\u9760\u7684\u4eba\u8d34\u8eab\u4fdd\u62a4 \u655d\u4e1c\u4e3b\uff0c\u603b\u6355\u5934\u653e\u5fc3\u5427\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u5934\u9053\uff1a\u201c\u90a3\u5c31\u597d\uff0c\u90a3\u5c31\u597d\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u56db\u4e0b\u770b\u4e86\u770b\uff0c\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u89c1\u738b\u5927\u4eba\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u738b\u5927\u4eba\u5728\u6293\u7d27\u65f6\u95f4\u5904\u7406\u79ef\u538b\u7684\u4e00\u5806\u516c\u4e8b\uff0c\u4e5f\u505a\u4e00\u70b9\u51c6\u5907\u5de5\u4f5c\uff0c\u6240\u4ee5\u5f88\u5fd9\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u538b\u4f4e\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u738b\u5927\u4eba\u5347\u4e3a\u5211\u90e8\u4f8d\u90ce\u4e86\uff0c\u5c31\u662f\u6211\u4eec\u7684\u9876\u5934\u4e0a\u53f8\uff0c\u73b0\u5728\uff0c\u4ed6\u7684\u4e8b \u6211\u4e5f\u4e0d\u597d\u8fc7\u591a\u5730\u5e72\u9884\u4e86\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u90a3\u4e48\uff0c\u8fd9\u6837\uff0c\u91d1\u67d0\u5c31\u544a\u8f9e\u4e86\uff0c\u738b\u5927\u4eba\u9762\u524d\uff0c\u8bf7\u603b\u6355\u5934\u8f6c\u8fbe\u8001\u592b\u7684\u606d \u8d3a\u4e4b\u610f\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u4e5f\u4e0d\u7559\u91d1\u8001\uff0c\u4f60\u653e\u5fc3\uff0c\u7b49\u6293\u4f4f\u4e86\u68a6\u5e7b\u4e4b\u5200\uff0c\u6211\u4eb2\u81ea\u5c06\u91d1\u5c0f\u7709\u9001\u56de \u91d1\u5e9c\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u4f9b\u624b\u9053\uff1a\u201c\u603b\u6355\u5934\u8bf7\u7559\u6b65\u3002\u201d \u3000\u3000\u9001\u8d70\u4e86\u91d1\u767e\u5e74\uff0c\u7a0b\u5c0f\u8776\u624d\u5f7b\u5e95\u677e\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u5979\u5b9e\u5728\u662f\u6709\u4e9b\u7d2f\u4e86\uff0c\u4e8e\u662f\uff0c\u5979\u6162\u6162\u8d70\u56de\u4e86\u81ea\u5df1\u7684\u623f\u95f4\uff0c\u6b6a\u5012\u5728\u5e8a\u4e0a\uff0c\u60f3\u5c0f\u7761\u4e00\u4f1a\u513f\u3002 \u3000\u3000\u4f46\u5979\u7761\u4e0d\u7740\u3002 \u3000\u3000\u53ea\u8981\u4e00\u95ed\u4e0a\u773c\u775b\uff0c\u5979\u5c31\u4f1a\u60f3\u8d77\u6628\u5929\u665a\u4e0a\u7684\u60c5\u666f\u3002 \u3000\u3000\u90a3\u60c5\u666f\u4ee4\u5979\u8138\u7ea2\uff0c\u4ee4\u5979\u5fc3\u8df3\uff0c\u4ee4\u5730\u6d51\u8eab\u53d1\u70ed\u3001\u53d1\u8f6f\u3002 \u3000\u3000\u5979\u4f3c\u4e4e\u53c8\u60f3\u5230\u4e86\u675c\u671b\u6708\u90a3\u4e00\u53cc\u5f3a\u6709\u529b\u7684\u81c2\u8180\u7684\u62e5\u62b1\uff0c\u60f3\u5230\u4e86\u4ed6\u90a3\u53cc\u5927\u624b\u5728\u5979\u51dd\u8102\u4e00\u822c\u7684 \u808c\u80a4\u4e0a\u6e38\u8d70\u65f6\u6240\u5e26\u7ed9\u5979\u7684\u90a3\u79cd\u5174\u594b\u7684\u611f\u89c9\u3002 \u3000\u3000\u90a3\u79cd\u611f\u89c9\u771f\u662f\u592a\u597d\u4e86\u3002 \u3000\u3000\u5979\u771f\u5e0c\u671b\u81ea\u5df1\u80fd\u6c38\u8fdc\u501a\u5728\u675c\u671b\u6708\u6000\u4e2d\uff0c\u88ab\u4ed6\u7231\uff0c\u88ab\u4ed6\u601c\u60dc\uff0c\u88ab\u4ed6\u51b2\u51fb\u3002 \u3000\u3000\u4e00\u60f3\u8d77\u90a3\u79cd\u575a\u5f3a\u3001\u594b\u529b\u800c\u53c8\u6e29\u67d4\u7684\u51b2\u51fb\uff0c\u5979\u7684\u5fc3\u91cc\u53c8\u6d8c\u8d77\u4e00\u80a1\u70ed\u6d41\u3002 \u3000\u3000\u5979\u53ea\u89c9\u5f97\u81ea\u5df1\u53c8\u5c06\u88ab\u8fd9\u80a1\u70ed\u6eb6\u5316\uff01 \u3000\u3000\u4f46\u5979\u5f88\u5feb\u5c31\u6e05\u9192\u8fc7\u6765\u3002 \u3000\u3000\u5979\u63a7\u5236\u4f4f\u4e86\u81ea\u5df1\u7684\u5fc3\u733f\u610f\u9a6c\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u77e5\u9053\uff0c\u90a3\u662f\u5979\u4e0e\u675c\u671b\u6708\u4e4b\u95f4\u7684\u7b2c\u4e00\u6b21\uff0c\u4e5f\u5c06\u662f\u6700\u540e\u4e00\u6b21\u3002 \u3000\u3000\u65e2\u4f7f\u53ea\u6709\u4e00\u6b21\uff0c\u5979\u4e5f\u5df2\u5fc3\u6ee1\u610f\u8db3\uff01 \u3000\u3000\u56e0\u4e3a\u5979\u5df2\u5c06\u81ea\u5df1\u4ea4\u7ed9\u4e86\u81ea\u5df1\u771f\u6b63\u7231\u7684\u7537\u4eba\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5728\u5e8a\u4e0a\u5750\u4e86\u8d77\u6765\uff0c\u505a\u7740\u6df1\u547c\u5438\uff0c\u8ba9\u81ea\u5df1\u7684\u5fc3\u7eea\u53d8\u5f97\u5e73\u9759\u4e0b\u6765\u3002 \u3000\u3000\u5979\u5f00\u59cb\u60f3\u5373\u5c06\u8fdb\u884c\u7684\u8ba1\u5212\u3002\u201c\u8fd9\u4e2a\u8ba1\u5212\u4f1a\u4e0d\u4f1a\u6709\u4ec0\u4e48\u6f0f\u6d1e\u5462\uff1f\u201d \u3000\u3000\u5979\u60f3\u4e86\u534a\u5929\uff0c\u5f97\u51fa\u7684\u7ed3\u8bba\u662f\uff1a\u6ca1\u6709\u6f0f\u6d1e\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u5979\u6253\u5f00\u623f\u95f4\uff0c\u5411\u524d\u5385\u8d70\u53bb\u3002 \u3000\u3000\u5979\u77e5\u9053\u81ea\u5df1\u5fc5\u987b\u627e\u4e00\u4e9b\u4e8b\u60c5\u6765\u505a\uff0c\u56e0\u4e3a\u4e00\u95f2\u4e0b\u6765\uff0c\u5979\u5c31\u4f1a\u60f3\u8d77\u675c\u671b\u6708\u3002 \u3000\u3000\u60f3\u8d77\u4ed6\u548c\u5979\u4e4b\u95f4\u90a3\u5982\u706b\u7684\u70ed\u60c5\u3002 \u3000\u3000\u90a3\u70ed\u60c5\u4ee4\u5979\u6c89\u8ff7\uff0c\u4ee4\u5979\u5982\u75f4\u5982\u9189\uff0c\u4ee4\u5979\u4e0d\u80fd\u6b63\u5e38\u601d\u8003\u3002 \u3000\u3000\u8d70\u8fdb\u5927\u5385\uff0c\u7a0b\u5c0f\u8776\u53d1\u73b0\u5927\u5bb6\u90fd\u5728\u5927\u5385\u91cc\u7b49\u7740\u5979\u3002 \u3000\u3000\u91d1\u5c0f\u7709\u4e5f\u5728\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u638f\u51fa\u90a3\u51e0\u5c01\u4fe1\u9012\u7ed9\u5979\uff0c\u9053\uff1a\u201c\u5c0f\u7709\uff0c\u4f60\u7239\u6765\u8fc7\u4e86\uff0c\u8fd9\u51e0\u5c01\u4fe1\u662f\u4ed6\u5199\u7ed9\u4eac\u91cc\u7684\u51e0\u4f4d \u670b\u53cb\u7684\uff0c\u6258\u4ed6\u4eec\u7167\u987e\u4f60\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u63a5\u8fc7\u4e86\u4fe1\uff0c\u95ee\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6211\u4eec\u4ec0\u4e48\u65f6\u5019\u52a8\u8eab\uff1f\u6211\u8fd8\u80fd\u591f\u89c1\u4e00\u89c1\u6211\u7239\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8d70\u7684\u65f6\u5019\u4f1a\u544a\u8bc9\u4f60\u7684\u3002\u5c0f\u7709\u59d1\u5a18\uff0c\u8fdb\u4eac\u53bb\u4f60\u4e0d\u6015\u5427\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u53ea\u8981\u80fd\u5bf9\u7834\u6848\u6709\u5e2e\u52a9\uff0c\u53bb\u54ea\u91cc\u6211\u90fd\u4e0d\u6015\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5c0f\u7709\uff0c\u4f60\u5f88\u575a\u5f3a\u3002\u6211\u5e0c\u671b\u4f60\u80fd\u66f4\u575a\u5f3a\u4e00\u4e9b\uff0c\u56e0\u4e3a\u8fd8\u6709\u5f88\u591a\u610f\u60f3\u4e0d\u5230\u7684\u56f0\u96be \u5728\u7b49\u7740\u6211\u4eec\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6211\u77e5\u9053\u5728\u8fdb\u4eac\u7684\u8def\u9014\u4e2d\u4f1a\u6709\u5f88\u591a\u5371\u9669\uff0c\u4f46\u662f\uff0c\u6211\u4e0d\u4f1a\u5bb3\u6015\u7684\uff0c\u4f60 \u653e\u5fc3\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8fd9\u6837\u6211\u5c31\u653e\u5fc3\u4e86\u3002\u6211\u4eec\u968f\u65f6\u90fd\u6709\u53ef\u80fd\u52a8\u8eab\uff0c\u4f60\u5148\u56de\u623f\u597d\u597d\u7761\u4e00\u89c9 \u5427\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u5979\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u770b\u7740\u5979\u7684\u80cc\u5f71\uff0c\u5bf9\u60dc\u7389\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u53bb\u966a\u5979\u4e00\u4f1a\u513f\uff0c\u7b49\u5979\u7761\u4e0b\uff0c\u70b9\u5979\u7684\u7761\u7a74\uff0c \u6211\u4eec\u6709\u91cd\u8981\u7684\u4e8b\u8981\u5546\u8bae\u3002\u201d \u3000\u3000\u60dc\u7389\u4e5f\u8d70\u4e86\u51fa\u53bb\u3002\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u5979\u53c8\u5306\u5306\u56de\u5230\u4e86\u5927\u5385\uff0c\u51b2\u7a0b\u5c0f\u8776\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u963f\u6a2a\u3001\u963f\u4fdd\u3002\u201d \u3000\u3000\u4e8c\u4eba\u7ad9\u8d77\u8eab\u9053\uff1a\u201c\u5c5e\u4e0b\u5728\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u4e8c\u4eba\u8ddf\u7740\u9a6c\u5927\u4eba\uff0c\u5177\u4f53\u8ba1\u5212\u4f60\u4eec\u90fd\u6e05\u695a\u4e86\uff1f\u201d \u3000\u3000\u4e8c\u4eba\u9053\uff1a\u201c\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6ee1\u610f\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5f20\u5e08\u7237\u4e00\u5171\u8ba2\u4e86\u4e09\u8f86\u9a6c\u8f66\u3002\u60dc\u7389\u3001\u91d1\u5c0f\u7709\u540c\u4e58\u4e00\u8f66\uff0c\u60dc \u7389\u3001\u91d1\u5c0f\u7709\u4e5f\u662f\u6740\u624b\u4eec\u7684\u91cd\u8981\u76ee\u6807\uff0c\u4f60\u4e00\u5b9a\u8981\u968f\u65f6\u6ce8\u610f\u610f\u5916\u60c5\u51b5\u53d1\u751f\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u738b\u5927\u4eba\u548c\u5c0f\u6587\u4e24\u4eba\u540c\u4e58\u4e00\u8f66\uff0c\u6211\u548c\u5c0f\u96c5\u540c\u4e58\u4e00\u8f66\uff0c\u5c91\u53f8\u4e3b\u548c\u4e8e\u53f8\u4e3b\u5206\u522b\u5b88 \u5728\u738b\u5927\u4eba\u9a6c\u8f66\u7684\u5de6\u53f3\u4e24\u4fa7\uff0c\u675c\u53f8\u4e3b\u7387\u9886\u6c5f\u5357\u5206\u53f8\u7684\u597d\u624b\u5728\u524d\uff0c\u4e2d\u5dde\u5206\u53f8\u7684\u597d\u624b\u6bbf\u540e\uff0c\u8bf7\u9a6c\u5927 \u4eba\u8c03\u62e8\u4e94\u5341\u591a\u540d\u4f1a\u7528\u5f2f\u5f13\u7684\u7cbe\u9510\u6c34\u5e08\u9a81\u5200\u624b\u5206\u5b88\u5de6\u53f3\uff0c\u6bcf\u4eba\u5e26\u4e0a\u4e24\u5323\u5f3a\u5f29\u94c1\u7bad\uff0c\u4e00\u65e6\u51fa\u73b0\u610f \u5916\u60c5\u51b5\uff0c\u7acb\u5373\u5f3a\u5f29\u9f50\u5c04\u3002\u4f46\u654c\u4eba\u51b2\u8fd1\u540e\uff0c\u4ed6\u4eec\u4e0d\u53ef\u4e0a\u524d\u8fce\u654c\uff0c\u53ea\u53ef\u5b88\u62a4\u5728\u9a6c\u8f66\u56db\u5468\uff0c\u8bf8\u4f4d\u90fd \u6e05\u695a\u4e86\u5417\uff1f\u201d \u3000\u3000\u4f17\u4eba\u5e94\u9053\uff1a\u201c\u90fd\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u987f\u4e86\u987f\uff0c\u9053\uff1a\u201c\u90a3\u4e94\u5341\u540d\u7bad\u624b\u7531\u5c91\u3001\u4e8e\u4e8c\u4f4d\u53f8\u4e3b\u6307\u6325\uff0c\u7279\u522b\u8981\u6ce8\u610f\u7684\u662f\uff0c\u5728\u654c\u4eba \u524d\u8fd1\u540e\uff0c\u4e00\u5b9a\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u76f4\u63a5\u9762\u5bf9\u654c\u4eba\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u6211\u660e\u767d\uff0c\u6211\u4f1a\u8ba9\u5206\u53f8\u4e2d\u7684\u597d\u624b\u7ad9\u5728\u4ed6\u4eec\u524d\u9762\uff0c\u4e00\u9762\u8fce\u654c\uff0c\u4e00\u9762\u4fdd\u62a4\u7740\u4ed6\u4eec \u5411\u5f3a\u5f29\u5323\u4e2d\u88c5\u9510\u7bad\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u592a\u5bf9\u4e86\uff01\u6211\u5c31\u662f\u8fd9\u4e2a\u610f\u601d\uff01\u201d \u3000\u3000\u665a\u996d\u8fc7\u540e\uff0c\u7a0b\u5c0f\u8776\u5f53\u4f17\u5ba3\u5e03\uff0c\u738b\u5c11\u537f\u4e00\u884c\u4e8e\u4e8c\u66f4\u65f6\u5206\u8d77\u7a0b\u3002 \u3000\u3000\u56e0\u4e3a\u6240\u6709\u7684\u4eba\u90fd\u5df2\u505a\u597d\u4e86\u52a8\u8eab\u7684\u4e00\u5207\u51c6\u5907\uff0c\u6240\u4ee5\u5305\u62ec\u4e86\u9a6c\u957f\u5c71\u624b\u4e0b\u8c03\u5f52\u7a0b\u5c0f\u8776\u6307\u6325\u7684\u90a3 \u4e94\u5341\u540d\u5f29\u7bad\u624b\u4e5f\u6ca1\u6709\u5bf9\u8fd9\u4e2a\u51b3\u5b9a\u611f\u5230\u610f\u5916\u3002 \u3000\u3000\u53ea\u6709\u4e00\u4e2a\u4eba\u9664\u5916\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5c31\u662f\u91d1\u5c0f\u7709\u3002 \u3000\u3000\u91d1\u5c0f\u7709\u4e00\u76f4\u4ee5\u4e3a\u8981\u597d\u51e0\u5929\u4e4b\u540e\u624d\u4f1a\u8d77\u7a0b\uff0c\u6240\u4ee5\uff0c\u5979\u8fd8\u60f3\u5728\u79bb\u5f00\u626c\u5dde\u524d\u89c1\u8001\u7236\u4e00\u9762\u3002 \u3000\u3000\u867d\u7136\u610f\u5916\uff0c\u4f46\u5979\u4e5f\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u603b\u6355\u5934\u505a\u7684\u51b3\u5b9a\u662f\u5f88\u96be\u66f4\u6539\u7684\uff0c\u800c\u4e14\uff0c\u8fd9\u4e00 \u6b21\u4e0a\u4eac\u7684\u4e3b\u89d2\u662f\u738b\u77e5\u5e9c\uff0c\u5979\u53ea\u662f\u591a\u540d\u968f\u884c\u4e4b\u4eba\u4e2d\u7684\u4e00\u4eba\uff0c\u5f53\u7136\u6ca1\u6709\u6743\u5229\u63d0\u51fa\u7279\u6b8a\u7684\u8981\u6c42\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u95ee\u9053\uff1a\u201c\u91d1\u5c0f\u7709\uff0c\u4f60\u662f\u4e0d\u662f\u611f\u5230\u6709\u4e9b\u610f\u5916\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u541e\u541e\u5410\u5410\u5730\u9053\uff1a\u201c\u662f\u7684\uff0c\u603b\u6355\u5934\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5f88\u60f3\u89c1\u4ee4\u5c0a\u4e00\u9762\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8bf7\u4f60\u4e0d\u8981\u89c1\u602a\uff0c\u8fd9\u4e5f\u662f\u6ca1\u6709\u529e\u6cd5\u7684\u4e8b\uff0c\u6211\u4e4b\u6240\u4ee5\u51b3\u5b9a\u7acb\u523b\u52a8\u8eab\uff0c \u662f\u4e3a\u4e86\u738b\u5927\u4eba\u548c\u4f60\u7684\u5b89\u5168\u7740\u60f3\u3002\u5c0f\u7709\uff0c\u7b49\u5927\u4e8b\u4e00\u4e86\uff0c\u4f60\u4f1a\u518d\u89c1\u5230\u4f60\u7236\u4eb2\u7684\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u70b9\u70b9\u5934\uff0c\u4e0d\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u9a6c\u5927\u4eba\uff0c\u5317\u95e8\u90a3\u8fb9\uff0c\u662f\u4e0d\u662f\u5df2\u7ecf\u5b89\u6392\u597d\u4e86\uff1f\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u603b\u6355\u5934\u653e\u5fc3\uff0c\u6211\u5df2\u6d3e\u51fa\u624b\u4e0b\u6700\u5f97\u529b\u7684\u4e24\u540d\u90e8\u5c06\u5e26\u9886\u4e8c\u5341\u540d\u9a91\u5175\uff0c\u88c5\u626e\u6210\u767e \u59d3\uff0c\u57cb\u4f0f\u5728\u5317\u95e8\u9644\u8fd1\uff0c\u53ea\u8981\u4e00\u63a5\u5230\u4fe1\u53f7\uff0c\u4ed6\u4eec\u5c31\u4f1a\u7acb\u523b\u6253\u5f00\u57ce\u95e8\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\uff0c\u9a6c\u5927\u4eba\uff0c\u4f60\u7559\u5728\u626c\u5dde\uff0c\u4e5f\u8981\u4e07\u4e8b\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u6211\u4f1a\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5bf9\u56db\u5927\u6355\u5934\u9053\uff1a\u201c\u4f60\u4eec\u5206\u522b\u5e26\u4eba\u5b88\u4f4f\u5e9c\u8859\u56db\u65b9\uff0c\u5982\u679c\u53d1\u73b0\u6709\u4eba\u6765\u63a2\uff0c\u4e0d\u8981\u60ca\u52a8\u4ed6\uff0c \u8ba9\u4ed6\u770b\u4e00\u770b\u5e9c\u8859\u7684\u60c5\u51b5\u597d\u4e86\u3002\u201d \u3000\u3000\u56db\u5927\u6355\u5934\u9886\u547d\u800c\u51fa\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u6c89\u541f\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u9664\u4e86\u5927\u5385\u4e0a\uff0c\u5176\u4f59\u5404\u5904\u623f\u95f4\u4e00\u5f8b\u4e0d\u8bb8\u70b9\u706f\uff0c\u5c0f\u6587\u3001\u5c0f\u96c5\u3001\u60dc\u738b\u3001 \u963f\u6a2a\u3001\u963f\u4fdd\u5206\u522b\u5728\u5927\u5385\u56db\u5468\u8b66\u6212\uff0c\u6211\u548c\u738b\u5927\u4eba\uff0c\u9a6c\u5927\u4eba\u5c31\u5750\u5728\u5927\u5385\u91cc\uff0c\u4e00\u662f\u8981\u7ed9\u6740\u624b\u96c6\u56e2\u4e00 \u4e2a\u6211\u4eec\u4ecd\u7136\u8981\u575a\u5b88\u5e9c\u8859\u7684\u5047\u50cf\u3002\u201d \u3000\u3000\u5f88\u5feb\uff0c\u5e9c\u8859\u6c89\u5165\u4e86\u591c\u8272\u4e4b\u4e2d\uff0c\u53ea\u6709\u5927\u5385\u91cc\u70b9\u7740\u51e0\u652f\u8721\u70db\u3002 \u3000\u3000\u8d77\u66f4\u540e\uff0c\u675c\u671b\u6708\u7684\u8eab\u5f71\u8fdb\u4e86\u5927\u5385\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u95ee\u9053\uff1a\u201c\u675c\u53f8\u4e3b\uff0c\u662f\u4e0d\u662f\u6709\u4eba\u6765\u8fc7\u4e86\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6765\u4eba\u6b66\u529f\u5982\u4f55\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4e00\u6d41\u8eab\u624b\uff0c\u867d\u7136\u4e0d\u662f\u9ed1\u8863\u8499\u9762\u4eba\u3001\u4e0d\u8001\u4e66\u751f\u6216\u8840\u624b\u65b9\u8f6e\u7684\u4eba\uff0c\u4f46\u4e5f\u5f88\u60ca\u4eba \u4e86\uff0c\u770b\u6765\u5728\u6740\u624b\u96c6\u56e2\u4e2d\u7684\u53f8\u804c\u7edd\u4e0d\u4f1a\u4f4e\u3002\u4ed6\u4f0f\u8eab\u5728\u5927\u5802\u9876\u4e0a\uff0c\u56db\u4e0b\u91cc\u770b\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u624d\u98de\u8eab \u63a0\u8d70\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u7b11\u9053\uff1a\u201c\u679c\u4e0d\u51fa\u6211\u7684\u610f\u6599\uff0c\u675c\u53f8\u4e3b\uff0c\u4f60\u4eec\u6ca1\u6709\u60ca\u52a8\u4ed6\u5427\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u90a3\u5c31\u597d\uff0c\u675c\u53f8\u4e3b\uff0c\u901a\u77e5\u4ed6\u4eec\uff0c\u7acb\u5373\u8d77\u7a0b\uff01\u201d \u3000\u3000\u4e00\u884c\u4eba\u7a7f\u8fc7\u51e0\u6761\u50fb\u9759\u7684\u5c0f\u5df7\uff0c\u7a7f\u8fc7\u51e0\u6761\u51b7\u9759\u7684\u8857\u9053\uff0c\u6084\u65e0\u58f0\u606f\u5730\u7a7f\u8fc7\u4e86\u534a\u4e2a\u626c\u5dde\u57ce\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5f88\u6ee1\u610f\uff0c\u5317\u95e8\u679c\u7136\u5df2\u7ecf\u6253\u5f00\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u624b\u4e0b\u7684\u4e24\u4e2a\u90e8\u5c06\u4e00\u5de6\u4e00\u53f3\u5730\u7ad9\u5728\u95e8\u8fb9\uff0c\u606d\u9001\u738b\u5c11\u537f\u548c\u7a0b\u5c0f\u8776\u4e00\u884c\u3002\u7b49\u6240\u6709\u4eba\u90fd\u51fa \u4e86\u5317\u95e8\u540e\uff0c\u7a0b\u5c0f\u8776\u624d\u5bf9\u8fd9\u4e9b\u5c06\u9886\u9053\uff1a\u201c\u4f60\u4eec\u5173\u4e0a\u57ce\u95e8\u540e\uff0c\u5c3d\u5feb\u8d76\u56de\u5e9c\u8859\uff0c\u8981\u4e0d\u60dc\u4e00\u5207\u4ee3\u4ef7\u4fdd \u62a4\u9a6c\u5927\u4eba\u7684\u5b89\u5168\u3002\u8fd8\u6709\uff0c\u63d0\u9192\u9a6c\u5927\u4eba\u5343\u4e07\u4e0d\u8981\u5fd8\u4e86\u6211\u4e0e\u4ed6\u4e4b\u95f4\u7684\u8c08\u8bdd\u3002\u201d \u3000\u3000\u4e24\u4f4d\u90e8\u5c06\u9053\uff1a\u201c\u662f\uff0c\u603b\u6355\u5934\u653e\u5fc3\u5427\u3002\u201d \u3000\u3000\u7b49\u57ce\u95e8\u5173\u4e0a\u540e\uff0c\u7a0b\u5c0f\u8776\u4fbf\u4e0b\u4ee4\u8ba9\u4f17\u4eba\u4ee5\u6700\u5feb\u7684\u901f\u5ea6\u5411\u5317\u8fdb\u53d1\u3002 \u3000\u3000------------- \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u4e8c\u5341\u4e5d\u7ae0 \u3000\u3000\u9a6c\u8e44\u7ffb\u98de\uff0c\u8f66\u8f6e\u6eda\u6eda\uff0c\u4e0d\u4e00\u4f1a\u513f\uff0c\u4e00\u884c\u4eba\u5df2\u7ecf\u884c\u8fdb\u4e86\u8fd1\u4e09\u5341\u91cc\u8def\u3002 \u3000\u3000\u8fd9\u4e00\u8def\u4e0a\uff0c\u4e00\u76f4\u5230\u73b0\u5728\u90fd\u5341\u5206\u5e73\u9759\uff0c\u6ca1\u6709\u53d1\u73b0\u5f02\u5e38\u60c5\u51b5\u3002 \u3000\u3000\u5c91\u5578\u864e\u9a91\u7740\u9a6c\uff0c\u8d76\u5230\u7a0b\u5c0f\u8776\u4e58\u5750\u7684\u9a6c\u8f66\u524d\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6211\u4eec\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u6025\u7740\u5f80\u524d \u9762\u8d76\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u81ea\u8f7f\u91cc\u4f38\u51fa\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u56e0\u4e3a\u5728\u6740\u624b\u96c6\u56e2\u53d1\u73b0\u6211\u4eec\u79bb\u5f00\u626c\u5dde\u4e4b\u524d\uff0c\u6211\u4eec\u79bb\u626c\u5dde \u8d8a\u8fdc\u8d8a\u597d\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u592a\u8fdc\u4e86\uff0c\u4ed6\u4eec\u4e0d\u5c31\u8d76\u4e0d\u4e0a\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5c91\u53f8\u4e3b\u653e\u5fc3\uff0c\u5c31\u7b97\u8dd1\u65ad\u4e24\u6761\u817f\uff0c\u4ed6\u4eec\u4e5f\u4f1a\u62fc\u547d\u8ffd\u4e0a\u6211\u4eec\u7684\uff0c\u56e0\u4e3a \u4ed6\u4eec\u77e5\u9053\u8ffd\u4e0d\u4e0a\u6211\u4eec\u7684\u540e\u679c\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u603b\u6355\u5934\u51c6\u5907\u4ee5\u9038\u5f85\u52b3\uff0c\u6253\u4e00\u4e2a\u6f02\u4eae\u7684\u6b7c\u706d\u6218\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u4e5f\u51d1\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u65e2\u7136\u4f60\u4eec\u6709\u5fc3\u5f15\u8bf1\u4ed6\u4eec\u6765\u8ffd\uff0c\u4e3a\u4ec0\u4e48\u4f60\u53c8\u8981\u505a\u5f97\u5982 \u6b64\u79d8\u5bc6\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u62c5\u5fc3\u4ed6\u4eec\u4e0d\u77e5\u9053\u6211\u4eec\u5df2\u7ecf\u51fa\u57ce\u4e86\uff1f\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u7528\u4e0d\u7740\u7b49\u5230\u5929\u4eae\uff0c\u4f60\u5c31\u4f1a\u4e0d\u62c5\u5fc3\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u96be\u9053\u6740\u624b\u96c6\u56e2\u5df2\u7ecf\u63a2\u5230\u6211\u4eec\u7684\u884c\u8e2a\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u53ef\u4ee5\u8fd9\u6837\u8bf4\u3002\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u603b\u4e0d\u4f1a\u662f\u5e9c\u8859\u5185\u4e5f\u6709\u4ed6\u4eec\u7684\u5978\u7ec6\u5427\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u4e8e\u53f8\u4e3b\u4e0d\u7528\u6000\u7591\uff0c\u7edd\u5bf9\u6ca1\u6709\u3002\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u53ef\u6211\u4eec\u7684\u884c\u52a8\u4e00\u76f4\u662f\u4fdd\u5bc6\u7684\uff0c\u5e94\u8be5\u4e0d\u4f1a\u8d70\u6f0f\u98ce\u58f0\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6b63\u56e0\u4e3a\u6211\u4eec\u7684\u884c\u52a8\u4e00\u76f4\u5f88\u795e\u79d8\uff0c\u4ed6\u4eec\u624d\u66f4\u8981\u6000\u7591\uff0c\u800c\u51ed\u6740\u624b\u96c6\u56e2\u773c\u7ebf\u4e4b\u5e7f\uff0c \u4e00\u5b9a\u65e9\u5df2\u7ecf\u5728\u5e9c\u8859\u56db\u5468\u4f0f\u4e0b\u65e0\u6570\u7684\u63a2\u5b50\uff0c\u518d\u8bf4\uff0c\u5047\u68a6\u5e7b\u4e4b\u5200\u3001\u4e0d\u8001\u4e66\u751f\u3001\u8840\u624b\u65b9\u8f6e\u4e5f\u90fd\u662f\u6781 \u4e3a\u7cbe\u660e\u7684\u4eba\u3002\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u60f3\u4e86\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u8fd9\u4e00\u6b21\u771f\u53ef\u8c13\u662f\u68cb\u9022\u5bf9\u624b\uff0c\u5c06\u9047\u826f\u6750\u4e86\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u4e5f\u53ea\u6709\u8fd9\u6837\u7684\u5bf9\u624b\uff0c\u6253\u8d77\u6765\u591f\u5473\uff01\u201d \u3000\u3000\u4e09\u4eba\u8fd8\u8bf4\u7740\u8bdd\uff0c\u5ffd\u542c\u62c9\u7684\u4e5d\u5339\u9a6c\u4e00\u58f0\u63a5\u4e00\u58f0\u5406\u5636\u8d77\u6765\u3002 \u3000\u3000\u7d27\u63a5\u7740\uff0c\u9a6c\u8f66\u7684\u901f\u5ea6\u7a81\u7136\u53d8\u6162\u4e86\u3002 \u3000\u3000\u5c91\u5578\u864e\u5403\u60ca\u5730\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4ece\u8f66\u91cc\u8df3\u51fa\u6765\uff0c\u9053\uff1a\u201c\u5c91\u53f8\u4e3b\uff0c\u53bb\u901a\u77e5\u675c\u53f8\u4e3b\uff0c\u6240\u6709\u4eba\u6682\u65f6\u505c\u4e0b\u6765\u3002\u201d \u3000\u3000\u5979\u8f6c\u800c\u5bf9\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u8ba9\u8f66\u91cc\u7684\u4eba\u90fd\u51fa\u6765\uff0c\u5728\u6211\u8fd9\u8f86\u8f66\u65c1\u96c6\u4e2d\uff01\u201d \u3000\u3000\u4e8c\u4eba\u9886\u547d\uff0c\u8f6c\u8eab\u800c\u7acb\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\u513f\uff0c\u8fd9\u4e00\u5c0f\u961f\u4eba\u9a6c\u505c\u4e0b\u6765\uff0c\u800c\u4e14\u56f4\u6210\u4e86\u4e00\u4e2a\u5706\u5f62\u7684\u6218\u9635\u3002 \u3000\u3000\u738b\u5c11\u537f\u3001\u5c0f\u6587\u3001\u5c0f\u96c5\u3001\u60dc\u7389\u3001\u91d1\u5c0f\u7709\u3001\u56db\u5927\u6355\u5934\u90fd\u6765\u5230\u4e86\u7a0b\u5c0f\u8776\u8eab\u8fb9\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u70b9\u8d77\u706f\u7b3c\u6765\u3002\u201d \u3000\u3000\u56db\u5927\u6355\u5934\u7acb\u5373\u5404\u81ea\u70b9\u8d77\u4e86\u4e00\u53ea\u706f\u7b3c\u3002 \u3000\u3000\u5728\u56db\u53ea\u706f\u7b3c\u7684\u5149\u7167\u4e0b\uff0c\u5927\u5bb6\u90fd\u770b\u6e05\u695a\u4e86\uff0c\u62c9\u8f66\u7684\u9a6c\u5339\u5634\u8fb9\u76f4\u5411\u5916\u5192\u7740\u767d\u6cab\uff0c\u800c\u4e14\uff0c\u9a6c\u817f \u5728\u4e0d\u4f4f\u5730\u8f7b\u6296\u7740\uff0c\u8116\u5b50\u4e00\u4e0b\u63a5\u4e00\u4e0b\u5730\u5f2f\u66f2\uff0c\u50cf\u662f\u8981\u5012\u4e0b\u6765\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u7acb\u523b\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u79bb\u9a6c\u8f66\u8fdc\u4e00\u70b9\uff01\u201d \u3000\u3000\u8bdd\u97f3\u672a\u8bed\uff0c\u53ea\u89c1\u4e09\u5339\u9a6c\u5df2\u5012\u5728\u5730\u4e0a\uff0c\u8fd8\u5e26\u7740\u9a6c\u8f66\u4e00\u8d77\u7ffb\u5012\u5728\u5730\uff0c\u201c\u8f70\u9686\u201d\u4e00\u58f0\u5927\u54cd\u3002 \u3000\u3000\u8fd9\u54cd\u58f0\u8fd8\u6ca1\u6709\u505c\uff0c\u63a5\u7740\u53c8\u662f\u201c\u8f70\u9686\u201d\u4e00\u58f0\u5927\u54cd\u3002 \u3000\u3000\u738b\u5c11\u537f\u548c\u91d1\u5c0f\u7709\u4e58\u5750\u7684\u90a3\u8f86\u8f66\u4e5f\u7ffb\u5012\u4e86\u3002 \u3000\u3000\u60dc\u7389\u4e0a\u524d\u4f4e\u4e0b\u8eab\uff0c\u4ed4\u7ec6\u5730\u68c0\u67e5\u4e86\u4e00\u4e0b\u5df2\u7ecf\u6b7b\u53bb\u7684\u9a6c\u5339\uff0c\u62ac\u5934\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u8fd9\u4e9b\u9a6c\u90fd\u4e2d \u4e86\u6bd2\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u80fd\u770b\u51fa\u4e2d\u7684\u662f\u4ec0\u4e48\u6bd2\u5417\uff1f\u201d \u3000\u3000\u60dc\u7389\u53c8\u770b\u4e86\u51e0\u773c\uff0c\u9053\uff1a\u201c\u80af\u5b9a\u662f\u4e0d\u8001\u4e66\u751f\u7684\u6bd2\u836f\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u5927\u5bb6\u5fc3\u91cc\u4e0d\u7981\u4e00\u60ca\u3002 \u3000\u3000\u600e\u4e48\u8bf4\uff0c\u4e0d\u8001\u4e66\u751f\u5df2\u7ecf\u5728\u9644\u8fd1\u4e86\u3002\u4ed6\u5982\u679c\u6765\u4e86\uff0c\u6740\u624b\u96c6\u56e2\u7684\u4eba\u80af\u5b9a\u4e5f\u5df2\u7ecf\u6765\u4e86\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u6c89\u541f\u4e00\u4f1a\u513f\uff0c\u9053\uff1a\u201c\u5927\u5bb6\u4e0d\u8981\u4e71\uff01\u60dc\u7389\uff0c\u4f60\u770b\u8fd9\u79cd\u6bd2\u662f\u53ef\u4ee5\u4e0b\u5728\u98ce\u4e2d\uff0c\u968f\u98ce\u4f20\u64ad \u7684\u5417\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u5c31\u7b97\u662f\uff0c\u4e5f\u6ca1\u5173\u7cfb\uff0c\u56e0\u4e3a\u6211\u4eec\u73b0\u5728\u8fd8\u5728\u4e0a\u98ce\u53e3\uff0c\u4e0d\u8001\u4e66\u751f\u6ca1\u6cd5\u5bf9\u6211\u4eec \u7528\u6bd2\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u8fd9\u62c9\u8f66\u7684\u4e5d\u5339\u9a6c\u90fd\u662f\u56e0\u5403\u7684\u8349\u6599\uff0c\u6216\u8005\u662f\u996d\u6c34\u4e2d\u6709\u6bd2\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5e94\u8be5\u662f\u6162\u6027\u6bd2\u836f\u624d\u5bf9\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u636e\u6211\u7684\u5224\u65ad\uff0c\u4e00\u5b9a\u662f\u6162\u6027\u6bd2\u836f\uff0c\u603b\u6355\u5934\uff0c\u539f\u6765\u4f60\u5bf9\u7528\u6bd2\u4e00\u9053\u4e5f\u5341\u5206\u7684 \u7cbe\u901a\u5440\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u54ea\u662f\u61c2\u4ec0\u4e48\u6bd2\u836f\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u90a3\u4f60\u662f\u5982\u4f55\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u4ece\u4e8b\u7406\u4e2d\u63a8\u51fa\u6765\u7684\u3002\u60dc\u7389\u4f60\u60f3\uff0c\u8fd9\u4e09\u8f86\u9a6c\u8f66\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u662f\u5728\u8f66\u884c\u8ba2\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u9a6c\u8f66\u5230\u5e9c\u8859\u540e\uff0c\u5e76\u6ca1\u6709\u4eba\u7ed9\u9a6c\u518d\u5582\u6599\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u8fd9\u4e9b\u9a6c\u5728\u5230 \u5e9c\u8859\u524d\u5c31\u5df2\u4e2d\u6bd2\uff0c\u53ef\u6bd2\u6027\u5230\u73b0\u5728\u624d\u53d1\u4f5c\uff0c\u5f53\u7136\u53ea\u53ef\u80fd\u662f\u6162\u6027\u6bd2\u836f\u624d\u5bf9\u3002\u201d \u3000\u3000\u60dc\u7389\u7b11\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u771f\u806a\u660e\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e5f\u4e00\u7b11\uff0c\u53ef\u8868\u60c5\u7acb\u523b\u53c8\u4e25\u8083\u8d77\u6765\uff0c\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u5f20\u5e08\u7237\u4e0d\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5b9d\u5584\u4e00\u5b9a\u77e5\u9053\u90a3\u5bb6\u8f66\u884c\u662f\u8c01\u7684\u4ea7\u4e1a\uff0c\u8fd9\u6837\uff0c\u6211\u4eec\u5c31\u80fd\u77e5\u9053\u5341\u5927\u5bcc\u8c6a \u4e4b\u4e2d\uff0c\u5230\u5e95\u8c01\u662f\u6740\u624b\u96c6\u56e2\u7684\u4eba\u4e86\uff01\u201d \u3000\u3000\u91d1\u5c0f\u7709\u5ffd\u7136\u9053\uff1a\u201c\u8fd9\u662f\u53ef\u4ee5\u770b\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u600e\u4e48\u770b\u5462\uff1f\u8bf7\u91d1\u59d1\u5a18\u6307\u70b9\uff01\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u8f66\u548c\u9a6c\u8eab\u4e0a\u4e00\u76f4\u5168\u6709\u6807\u8bb0\uff0c\u6bd4\u5982\u8bf4\u5982\u679c\u662f\u6211\u5bb6\u7684\u8f66\u884c\uff0c\u5728\u9a6c\u817f\u4e0a\u548c\u8f66\u8f95\u4e0a\uff0c \u5b9a\u4f1a\u70d9\u4e0a\u2018\u91d1\u8bb0\u2019\u4e24\u4e2a\u5b57\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u95fb\u8a00\u7acb\u5373\u4e3e\u8d77\u706f\u7b3c\u5728\u9a6c\u548c\u8f66\u8eab\u4e0a\u4e0a\u4e0b\u4e0b\u7167\u4e86\u4e00\u756a\uff0c\u9053\uff1a\u201c\u679c\u7136\u6709\u6807\u8bb0\uff0c\u603b\u6355\u5934\uff0c \u662f\u2018\u59da\u8bb0\u2019\uff0c\u4e24\u4e2a\u5b57\uff01\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u5341\u5927\u5bcc\u8c6a\uff0c\u53ea\u6709\u4e00\u5bb6\u59d3\u59da\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u59da\u987a\u5929\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u59da\u987a\u5929\u4e00\u5bb6\u4eba\u4e0d\u662f\u90fd\u8d70\u4e86\u5417\uff1f\u4ed6\u7684\u4ea7\u4e1a\u4e00\u5b9a\u8ba9\u5176\u4ed6\u4eba\u517c\u5e76\u4e86\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u91d1\u59d1\u5a18\uff0c\u4f60\u77e5\u9053\u662f\u54ea\u4e00\u5bb6\u517c\u5e76\u4e86\u59da\u5bb6\u4ea7\u4e1a\u5417\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u7684\u786e\uff0c\u5979\u6765\u672c\u5c31\u662f\u4e00\u4e2a\u517b\u5728\u6df1\u95fa\u7684\u5927\u5c0f\u59d0\uff0c\u518d\u52a0\u4e0a\u8fd1\u6765\u4e00\u76f4\u60b2\u4f24\u4e8e\u5fc3\uff0c\u795e\u601d\u604d\u60da\uff0c\u81ea\u7136 \u4e0d\u4f1a\u6ce8\u610f\u8fd9\u4e9b\u4e8b\u60c5\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u6c89\u601d\u8d77\u6765\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6211\u4eec\u73b0\u5728\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u53ea\u6709\u59d4\u5c48\u738b\u5927\u4eba\u548c\u91d1\u59d1\u5a18\u4e86\u3002\u738b\u5927\u4eba\u5e94\u8be5\u80fd\u9a91\u9a6c\uff0c\u91d1\u59d1\u5a18\u5462\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u6211\u4e5f\u80fd\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5927\u5bb6\u5c31\u90fd\u5f03\u8f66\u9a91\u9a6c\uff0c\u5c3d\u53ef\u80fd\u5feb\u5730\u5f80\u524d\u8d76\uff0c\u6211\u60f3\uff0c\u5728\u524d\u9762\u5c0f\u8def\u4e0a\uff0c\u5e94 \u8be5\u80fd\u627e\u5230\u9a6c\u8f66\uff01\u201d \u3000\u3000\u7ecf\u8fc7\u8fd9\u4e00\u610f\u5916\u505c\u987f\u4e4b\u540e\uff0c\u8fd9\u4e00\u5c0f\u961f\u4eba\u9a6c\u53c8\u5feb\u901f\u5730\u5f80\u524d\u8fdb\u53d1\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u521a\u624d\uff0c\u8981\u662f\u4e0d\u8001\u4e66\u751f\u4f0f\u5728\u6697\u4e2d\u8fce\u98ce\u4e0b\u836f\uff0c\u54b1\u4eec\u7684\u60c5\u51b5\u5c31\u9669\u4e86\u3002\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u62c5\u5fc3\uff0c\u8fd9\u4e9b\u5929\u6211\u5df2\u7528\u5e9c\u8859\u4e2d\u73cd\u85cf\u7684\u836f\u6750\u5408\u6210\u4e86\u795e\u533b\u95e8\u7684\u4e0d\u4f20\u4e4b\u79d8\u2014\u2014 \u89e3\u6bd2\u7075\u4e39\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4e0d\u8001\u4e66\u751f\u4f1a\u7528\u4ec0\u4e48\u6bd2\uff1f\u5982\u679c\u836f\u4e0d\u5bf9\uff0c\u4e5f\u80fd\u89e3\u6bd2\u5417\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u8fd9\u79cd\u914d\u65b9\u662f\u6211\u524d\u4e0d\u4e45\u624d\u7814\u5236\u51fa\u6765\u7684\uff0c\u53ea\u8981\u662f\u6309\u795e\u533b\u95e8\u7684\u836f\u7406\u914d\u7684\u6bd2\u836f\uff0c\u90fd\u80fd \u89e3\u5f00\u3002\u6211\u60f3\uff0c\u4e0d\u8001\u4e66\u751f\u4e00\u5b9a\u4e0d\u77e5\u9053\u8fd9\u4e2a\u914d\u65b9\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u90a3\u592a\u597d\u4e86\uff01\u60dc\u7389\uff0c\u4f60\u5c06\u89e3\u836f\u5148\u53d1\u7ed9\u5927\u5bb6\uff0c\u4e00\u65e6\u4e0d\u8001\u4e66\u751f\u7528\u6bd2\uff0c\u4fbf\u8ba9\u4ed6\u4eec\u706b \u901f\u670d\u4e0b\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u638f\u51fa\u4e24\u4e2a\u5c0f\u5305\uff0c\u9012\u7ed9\u7a0b\u5c0f\u8776\u4e00\u4e2a\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4e2d\u961f\u548c\u540e\u961f\u7684\u836f\u4f60\u6765\u53d1\u5427\uff0c\u6211\u53bb\u524d \u961f\u3002\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u4e24\u811a\u4e00\u8e6c\u9a6c\u8179\uff0c\u98de\u5954\u5411\u524d\u800c\u53bb\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u4e86\u7b11\uff0c\u779f\u4e86\u675c\u671b\u6708\u4e00\u773c\uff0c\u9053\uff1a\u201c\u675c\u53f8\u4e3b\uff0c\u4f60\u771f\u662f\u597d\u798f\u6c14\u5440\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u5fc3\u91cc\u4e5f\u4e0d\u77e5\u662f\u9178\u662f\u751c\u662f\u82e6\u662f\u75db\uff0c\u53ea\u5f97\u6de1\u6de1\u7b11\u4e86\u4e00\u7b11\uff0c\u6ca1\u6709\u7b54\u8bdd\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\u513f\uff0c\u60dc\u7389\u5df2\u7ecf\u53d1\u5b8c\u836f\uff0c\u53c8\u9a70\u4e86\u56de\u6765\u3002 \u3000\u3000\u884c\u8d70\u4e4b\u95f4\uff0c\u5979\u6709\u610f\u65e0\u610f\u5730\u603b\u662f\u63a7\u7740\u9a6c\uff0c\u60f3\u79bb\u675c\u671b\u6708\u66f4\u8fd1\u4e00\u4e9b\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u53c8\u98de\u5feb\u5730\u779f\u4e86\u675c\u671b\u6708\u4e00\u773c\uff0c\u62bf\u5634\u4e00\u7b11\u3002 \u3000\u3000\u675c\u671b\u6708\u65e0\u58f0\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8f6c\u5f00\u4e86\u5934\u3002 \u3000\u3000\u53c8\u5411\u524d\u8d70\u4e86\u7ea6\u4e09\u5341\u91cc\uff0c\u591c\u66f4\u6df1\u4e86\uff0c\u591c\u98ce\u4e5f\u8d8a\u6765\u8d8a\u5927\uff0c\u73b0\u5728\u8eab\u4e0a\u5f88\u6709\u4e9b\u51c9\u610f\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u626c\u5dde\u8fd9\u4e00\u5e26\u5927\u4eba\u5e94\u8be5\u6bd4\u719f\u4e9b\uff0c\u4e0d\u77e5\u8fd9\u9644\u8fd1\u6700\u8fd1\u7684\u5c0f\u6751\u9547\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u524d\u9762\u5341\u6765\u91cc\u5730\uff0c\u5e94\u8be5\u6709\u4e00\u6751\u5b50\uff0c\u597d\u50cf\u53eb\u5c0f\u6768\u6751\uff0c\u8fd8\u633a\u5927\u7684\uff0c\u5927\u7ea6 \u6709\u8fd1\u4e8c\u767e\u6237\u4eba\u5bb6\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\uff0c\u6211\u4eec\u5c31\u8d76\u5230\u5c0f\u6768\u6751\u6b47\u811a\uff0c\u7b49\u5929\u4eae\u518d\u5f80\u524d\u8d70\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u5f80\u524d\u518d\u8d76\u4e00\u4e9b\u5462\uff1f\u5c0f\u6768\u6751\u5f80\u524d\u7ea6\u56db\u5341\u91cc\uff0c\u6709\u4e00\u4e2a\u6bd4\u8f83\u5927\u7684\u9547\u5b50\uff0c \u53eb\u8427\u4f2f\u9547\uff0c\u5728\u90a3\u91cc\u5e94\u8be5\u80fd\u5f04\u5230\u9a6c\u8f66\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u62c5\u5fc3\u6740\u624b\u96c6\u56e2\u65e9\u5df2\u7ecf\u505a\u597d\u4e86\u51c6\u5907\uff0c\u800c\u4e14\u4ed6\u4eec\u7684\u8ba1\u5212\u4e0d\u662f\u968f\u540e\u5c3e\u8ffd\uff0c\u800c\u662f \u5728\u6cbf\u9014\u5b89\u6392\u4eba\u624b\uff0c\u5bf9\u6211\u4eec\u8fdb\u884c\u57cb\u4f0f\u548c\u622a\u6740\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u5403\u60ca\u5730\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u53cd\u5e94\u80fd\u6709\u8fd9\u4e48\u5feb\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5f88\u53ef\u80fd\u5f20\u5e08\u7237\u53bb\u8ba2\u9a6c\u8f66\u65f6\uff0c\u4ed6\u4eec\u5df2\u7ecf\u7b97\u51c6\u6211\u4eec\u4f1a\u8fde\u591c\u8d77\u7a0b\uff0c\u6240\u4ee5\u5148\u884c\u505a\u597d \u4e86\u51c6\u5907\u5de5\u4f5c\u3002\u201d \u3000\u3000\u50cf\u662f\u8981\u6025\u4e8e\u8bc1\u660e\u7a0b\u5c0f\u8776\u7684\u5224\u65ad\u662f\u6b63\u786e\u7684\uff0c\u5979\u7684\u8bdd\u624d\u521a\u521a\u8bf4\u5b8c\uff0c\u4fbf\u542c\u89c1\u662f\u7531\u6797\u4e0d\u51e1\u9886\u519b\u7684 \u524d\u961f\u4f20\u6765\u51e0\u58f0\u60ca\u53eb\u3002 \u3000\u3000\u675c\u671b\u6708\u9762\u8272\u4e00\u53d8\uff0c\u5927\u60ca\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u6ce8\u610f\u4fdd\u62a4\u597d\u738b\u5927\u4eba\u548c\u91d1\u5c0f\u7709\uff0c\u6211\u4eec\u5feb\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u4e94\u4eba\u75be\u9a70\u5230\u524d\u961f\uff0c\u53d1\u73b0\u524d\u961f\u5df2\u7ecf\u505c\u4e86\u4e0b\u6765\uff0c\u675c\u671b\u6708\u6b63\u5728\u66ff\u4e24\u4f4d\u6c5f\u5357\u5206\u53f8\u7684\u597d\u624b\u88f9\u4f24\uff0c\u4ed6 \u7684\u8eab\u8fb9\uff0c\u8eba\u7740\u4e24\u5177\u5c38\u4f53\u3002 \u3000\u3000\u8fd9\u4e8c\u4eba\u4e5f\u662f\u6c5f\u5357\u5206\u53f8\u4e2d\u7684\u597d\u624b\uff0c\u4ed6\u4eec\u90fd\u662f\u88ab\u957f\u7bad\u5c04\u6b7b\u7684\u3002 \u3000\u3000\u53d7\u4e86\u4f24\u7684\u4e94\u4e2a\u4eba\u4e5f\u662f\u88ab\u7a81\u5982\u5176\u6765\u7684\u5229\u7bad\u5c04\u5012\u7684\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u8df3\u4e0b\u9a6c\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6ce8\u610f\u9632\u5907\u6697\u7bad\uff0c\u505a\u597d\u6218\u6597\u51c6\u5907\uff01\u201d \u3000\u3000\u8bed\u8a00\u672a\u843d\uff0c\u53c8\u6709\u4e00\u9635\u7bad\u96e8\u5c04\u6765\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u4e00\u6b21\u6ca1\u6709\u4eba\u53d7\u4f24\u3002 \u3000\u3000\u56e0\u4e3a\u5df2\u6709\u9632\u5907\uff0c\u6240\u4ee5\u5927\u5bb6\u5404\u6325\u91d1\u5200\uff0c\u5c06\u98de\u6765\u7684\u5229\u7bad\u62e8\u5f00\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u6361\u8d77\u4e00\u6839\u5229\u7bad\u770b\u4e86\u770b\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u8fd9\u4e9b\u5f13\u624b\u662f\u8840\u624b\u65b9\u8f6e\u7684\u624b\u4e0b\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u5934\u9053\uff1a\u201c\u675c\u53f8\u4e3b\uff0c\u8fd8\u8bb0\u5f97\u5728\u7626\u897f\u6e56\u8fb9\u4f60\u4e0e\u8840\u624b\u65b9\u8f6e\u624b\u4e0b\u90a3\u4e00\u6218\u5427\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8bb0\u5f97\uff0c\u90a3\u5929\u5982\u679c\u4e0d\u662f\u603b\u6355\u5934\u548c\u963f\u6a2a\u3001\u963f\u4fdd\u8d76\u6765\u76f8\u52a9\uff0c\u6211\u548c\u60dc\u7389\u53ea\u6015\u65e9\u5df2\u6b7b \u6389\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5728\u6e56\u8fb9\u90a3\u4e00\u6218\u4e2d\uff0c\u8840\u624b\u65b9\u8f6e\u624b\u4e0b\u7684\u5f13\u7bad\u624b\u662f\u4e0e\u90a3\u4e9b\u6392\u6210\u7ec4\u5408\u4e4b\u52bf\u7684\u7070\u8863\u624b \u4eec\u8054\u5408\u4f5c\u6218\u7684\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u603b\u6355\u5934\u662f\u8bf4\uff0c\u7070\u8863\u6740\u624b\u4eec\u7acb\u523b\u5c31\u4f1a\u73b0\u8eab\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5979\u8f6c\u5934\u5bf9\u5c91\u5578\u864e\u548c\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u53bb\u5c06\u90a3\u4e94\u5341\u540d\u5f13\u5f29\u624b\u8c03\u4e0a\u6765\u3002\u201d \u3000\u3000\u4e8c\u4eba\u98de\u8eab\u5411\u540e\u63a0\u53bb\uff01 \u3000\u3000\u53c8\u662f\u4e00\u9635\u7bad\u96e8\u98de\u6765\uff01 \u3000\u3000\u8fd9\u4e00\u9635\u7bad\u96e8\u6bd4\u4e0a\u4e24\u6b21\u7684\u66f4\u5bc6\u96c6\uff0c\u529b\u9053\u4e5f\u66f4\u5f3a\u52b2\uff0c\u6240\u4ee5\u5927\u5bb6\u867d\u7136\u65e9\u6709\u51c6\u5907\uff0c\u4f46\u4ecd\u6709\u4eba\u88ab\u5c04 \u4e2d\u4e86\u3002 \u3000\u3000\u4e24\u6b7b\u4e94\u4f24\uff01 \u3000\u3000\u4ece\u5229\u7bad\u5c04\u6765\u7684\u529b\u9053\u770b\uff0c\u5f13\u7bad\u624b\u4eec\u5df2\u6bd4\u521a\u624d\u5411\u524d\u8ffd\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u8fd9\u6837\u88ab\u52a8\u8ffd\u6253\u53ef\u4e0d\u662f\u4e2a\u529e\u6cd5\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u53d7\u4f24\u7684\u4eba\u5f80\u540e\u9000\uff0c\u6ca1\u6709\u53d7\u4f24\u7684\u4e24\u4e2a\u4e00\u7ec4\uff0c\u6309\u6211\u5b9a\u7684\u529e\u6cd5\uff0c\u4e00\u4e2a\u4f7f\u9524\u5b50\uff0c\u4e00 \u4e2a\u7528\u8170\u5200\uff0c\u7070\u8863\u6740\u624b\u4eec\u5c31\u5feb\u51b2\u4e0a\u6765\u4e86\uff01\u201d \u3000\u3000\u53ea\u89c1\u5927\u9053\u65c1\u8fb9\u7684\u8349\u4e1b\u4e2d\u7a81\u7136\u6d8c\u51fa\u4e86\u4e00\u6279\u7070\u8863\u6740\u624b\uff0c\u76f4\u6d8c\u5230\u5927\u9053\u6b63\u4e2d\uff0c\u62e6\u4f4f\u4e86\u4f17\u4eba\u7684\u53bb\u8def\u3002 \u3000\u3000\u7070\u8863\u6740\u624b\u4e00\u5171\u5206\u6210\u4e8c\u5341\u7ec4\uff0c\u4e5f\u5c31\u662f\u516d\u5341\u4e2a\u4eba\u3002\u4ed6\u4eec\u5df2\u7ecf\u5b8c\u6210\u4e86\u7ec4\u5408\u3002 \u3000\u3000\u5927\u9053\u4e24\u8fb9\uff0c\u8fd8\u6574\u9f50\u5730\u6392\u884c\u7740\u4e24\u961f\u5f13\u7bad\u624b\uff0c\u5f3a\u5f13\u5df2\u62c9\u6ee1\u4e86\uff0c\u5229\u7bad\u5df2\u5728\u5f26\u3002 \u3000\u3000\u6b63\u5728\u8fd9\u5371\u6025\u65f6\u523b\uff0c\u5c91\u5578\u864e\u548c\u4e8e\u627f\u5fd7\u5e26\u7740\u4e94\u5341\u540d\u5f13\u5f29\u624b\u8d76\u4e0a\u6765\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5f13\u5f29\u624b\u5bf9\u5de6\u8fb9\u7684\u5f13\u624b\u9f50\u5c04\uff0c\u5c91\u53f8\u4e3b\u3001\u4e8e\u53f8\u4e3b\u548c\u6797\u53f8\u6848\u7387\u9886\u524d\u961f\u4eba\u9a6c\u653b\u51fb\u90a3 \u4e9b\u7070\u8863\u6740\u624b\u3002\u675c\u53f8\u4e3b\uff0c\u6211\u4eec\u53bb\u5bf9\u4ed8\u53f3\u8fb9\u7684\u90a3\u4e00\u961f\u5f13\u7bad\u624b\uff01\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u5979\u5df2\u6446\u8eab\u63a0\u8d77\uff0c\u8eab\u5728\u7a7a\u4e2d\uff0c\u4f3c\u662f\u4e00\u4f4d\u51cc\u7a7a\u98de\u8d77\u7684\u4ed9\u5b50\u4e00\u822c\uff0c\u5de6\u624b\u6325\u51fa\uff0c\u9c9c\u7ea2\u7684\u7ec6 \u7d22\u5df2\u7ecf\u51fa\u624b\uff0c\u53f3\u624b\u4e2d\u4e5f\u5df2\u63e1\u4f4f\u4e86\u4e00\u67c4\u7cbe\u5149\u56db\u5c04\u7684\u77ed\u7bad\u3002 \u3000\u3000\u5f13\u7bad\u624b\u4eec\u4e00\u9f50\u653e\u7bad\u3002 \u3000\u3000\u8fd9\u9635\u7bad\u96e8\u5168\u90e8\u98de\u5411\u7a0b\u5c0f\u8776\u3002 \u3000\u3000\u675c\u671b\u6708\u817e\u8eab\u518d\u8d77\uff0c\u4e00\u56e2\u5251\u5149\u62a4\u4f4f\u4e86\u5468\u8eab\u8981\u5bb3\uff0c\u76f4\u6251\u5411\u5f13\u7bad\u624b\u4eec\uff0c\u53e3\u4e2d\u5927\u53eb\u9053\uff1a\u201c\u603b\u6355\u5934 \u5c0f\u5fc3\uff01\u201d \u3000\u3000\u90a3\u9635\u7bad\u96e8\u5c04\u5230\u65f6\uff0c\u7a0b\u5c0f\u8776\u624b\u4e2d\u7684\u7ec6\u7d22\u65e9\u5df1\u5728\u5468\u8eab\u5e03\u4e0b\u4e00\u9053\u7ea2\u5f71\u3002 \u3000\u3000\u5c04\u5230\u8fd9\u9053\u7ea2\u5f71\u4e0a\u7684\u7bad\u7eb7\u7eb7\u843d\u5730\u3002 \u3000\u3000\u675c\u671b\u6708\u5df2\u6251\u8fdb\u5f13\u7bad\u624b\u4e4b\u4e2d\u3002 \u3000\u3000\u5251\u5149\u98de\u821e\uff0c\u5982\u4e00\u9053\u9053\u5212\u7834\u591c\u7a7a\u7684\u95ea\u7535\u3002 \u3000\u3000\u8840\u98de\u6e85\u3002\u6b8b\u7834\u7684\u8131\u4f53\u56db\u4e0b\u98de\u843d\u3002\u60e8\u53eb\u58f0\u6c47\u6210\u4e00\u7247\uff0c\u5341\u5206\u6151\u4eba\u3002 \u3000\u3000\u4e8c\u5341\u591a\u540d\u7bad\u624b\u5728\u7a0b\u5c0f\u8776\u6251\u5230\u65f6\uff0c\u5df2\u5168\u90e8\u88ab\u6740\u6b7b\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u773c\u4e2d\u95ea\u7740\u51b7\u5389\u7684\u5149\u8292\uff0c\u5c06\u624b\u4e2d\u7684\u5b9d\u5251\u5728\u4e00\u540d\u5f13\u7bad\u624b\u5c38\u4f53\u4e0a\u62ed\u4e86\u62ed\uff0c\u62b9\u53bb\u4e0a\u9762\u7684\u9c9c \u8840\uff0c\u4ef0\u5929\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u6084\u6084\u5c06\u5b9d\u5251\u63d2\u5165\u9798\u4e2d\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u8d70\u5230\u4ed6\u8eab\u8fb9\uff0c\u8f7b\u8f7b\u4e00\u78b0\u4ed6\u7684\u5927\u624b\uff0c\u9053\uff1a\u201c\u8fd9\u5251\u6cd5\u771f\u6b8b\u9177\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u662f\u554a\uff0c\u4f46\u7528\u6765\u5bf9\u4ed8\u8fd9\u4e9b\u6b8b\u9177\u7684\u6740\u624b\uff0c\u662f\u518d\u597d\u4e0d\u8fc7\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u675c\u53f8\u4e3b\uff0c\u606d\u559c\u4f60\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u5df2\u5c3d\u5f97\u2018\u5251\u6d77\u6d74\u9b42\u2019\u7684\u7cbe\u5999\u4e4b\u5904\uff0c\u800c\u4e14\uff0c\u4f60\u80fd\u4e0d\u4e3a\u5251\u6cd5\u5f79\uff0c\u73b0\u5728\u4f60 \u5728\u5251\u4e0a\u7684\u9020\u8be3\u5df2\u7ecf\u8d85\u8fc7\u4e86\u5c0f\u6587\uff0c\u5df2\u8fbe\u5230\u4e86\u5341\u4e8c\u5c42\uff0c\u5929\u4e0b\u4f7f\u5251\u7684\u9ad8\u624b\u4e2d\uff0c\u53ea\u6015\u5df2\u5f88\u96be\u627e\u5230\u4f60\u7684 \u5bf9\u624b\u4e86\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u7b11\u4e86\u7b11\uff0c\u6df1\u60c5\u5730\u770b\u4e86\u5979\u4e00\u773c\u3002 \u3000\u3000\u867d\u7136\u4ed6\u4ec0\u4e48\u8bdd\u4e5f\u6ca1\u6709\u8bf4\uff0c\u4f46\u4ed6\u5df2\u7528\u6df1\u60c5\u7684\u76ee\u5149\u544a\u8bc9\u7a0b\u5c0f\u8776\uff0c\u4ed6\u51b3\u4e0d\u4f1a\u5fd8\u8bb0\u5979\u5bf9\u4ed6\u7684\u5e2e\u52a9\uff0c \u5f53\u7136\u66f4\u4e0d\u4f1a\u5fd8\u8bb0\u5979\u5bf9\u4ed6\u7684\u7231\u60c5\u3002 \u3000\u3000\u800c\u8fd9\u5957\u201c\u5251\u6d77\u6d74\u9b42\u201d\u5728\u675c\u671b\u6708\u8eab\u4e0a\u5b8c\u5584\u7684\u4f53\u73b0\uff0c\u5df2\u662f\u4ed6\u4eec\u4e8c\u4eba\u7231\u60c5\u7684\u7ed3\u6676\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u8fce\u7740\u4ed6\u7684\u76ee\u5149\uff0c\u5df2\u6709\u4e9b\u75f4\u4e86\u3002 \u3000\u3000\u5979\u52aa\u529b\u63a7\u5236\u4f4f\u81ea\u5df1\uff0c\u8f6c\u5f00\u76ee\u5149\uff0c\u9053\uff1a\u201c\u6211\u4eec\u5feb\u53bb\u5e2e\u4ed6\u4eec\u3002\u201d \u3000\u3000\u5c31\u5728\u675c\u671b\u6708\u75db\u6740\u5de6\u8fb9\u4e00\u961f\u5f13\u7bad\u624b\u65f6\uff0c\u4e94\u5341\u540d\u5f13\u624b\u4e2d\u7684\u5f29\u7bad\u4e5f\u5df2\u629b\u53d1\u3002\u8fd9\u4e00\u6b21\u5c31\u53d1\u51fa\u53bb\u4e94 \u767e\u679d\u94c1\u7bad\u3002 \u3000\u3000\u5de6\u8fb9\u4e00\u961f\u5f13\u7bad\u624b\u7acb\u5373\u5168\u90e8\u88ab\u5c04\u6b7b\uff0c\u4e00\u4e2a\u4e5f\u6ca1\u6d3b\u4e0b\u6765\u3002 \u3000\u3000\u4f46\u5728\u88ab\u5c04\u4e2d\u524d\uff0c\u4ed6\u4eec\u4e5f\u53d1\u51fa\u4e86\u624b\u4e2d\u7684\u5229\u7bad\uff01 \u3000\u3000\u7bad\u5982\u98de\u8757\u3002 \u3000\u3000\u4f46\u8fd9\u6b21\u5374\u6ca1\u6709\u4e00\u4e2a\u4eba\u4e2d\u7bad\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4e9b\u5f13\u7bad\u624b\u8fd9\u6b21\u7684\u76ee\u6807\u4e0d\u662f\u4eba\uff0c\u800c\u662f\u9a6c\u3002 \u3000\u3000\u6797\u4e0d\u51e1\u6240\u7387\u9886\u7684\u524d\u961f\u4eba\u9a6c\u7684\u9a6c\u5339\uff0c\u5728\u8fd9\u4e00\u9635\u7bad\u96e8\u540e\uff0c\u53ea\u5269\u4e0b\u4e86\u4e09\u56db\u5339\u8fd8\u6d3b\u7740\u3002 \u3000\u3000\u4f46\u8fd9\u51e0\u5339\u6d3b\u7740\u7684\u9a6c\u4e5f\u88ab\u60ca\u5f97\u5411\u8352\u91ce\u4e4b\u4e2d\u98de\u5954\u800c\u53bb\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7acb\u523b\u660e\u767d\u8fc7\u6765\u6740\u624b\u96c6\u56e2\u7684\u76ee\u7684\u4e86\u3002 \u3000\u3000\u8fd9\u4e9b\u5f13\u7bad\u624b\u548c\u7070\u8863\u6740\u624b\u884c\u52a8\u7684\u76ee\u6807\u5c31\u662f\u9a6c\u5339\u3002 \u3000\u3000\u6740\u6b7b\u4ed6\u4eec\u7684\u9a6c\u5339\uff0c\u4ed6\u4eec\u884c\u8fdb\u7684\u901f\u5ea6\u5c31\u65e0\u6cd5\u52a0\u5feb\uff0c\u5219\u6740\u624b\u96c6\u56e2\u5c31\u4f1a\u6709\u5145\u8db3\u7684\u65f6\u95f4\u6765\u5b89\u6392\u5982 \u4f55\u5bf9\u4ed8\u4ed6\u4eec\u3002 \u3000\u3000\u518d\u4e00\u770b\u90a3\u4e9b\u7070\u8863\u6740\u624b\u4eec\uff0c\u5728\u5c91\u5578\u864e\u3001\u4e8e\u627f\u5fd7\u3001\u6797\u4e0d\u51e1\u548c\u6c5f\u5357\u3001\u4e2d\u5dde\u5206\u53f8\u597d\u624b\u7684\u51fb\u6740\u4e4b\u4e0b\uff0c \u5df2\u7ecf\u88ab\u6d88\u706d\u4e86\u4e00\u534a\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5bf9\u675c\u671b\u6708\u9053\uff1a\u201c\u4f60\u53bb\u5e2e\u4ed6\u4eec\uff0c\u4e00\u5b9a\u8981\u5c06\u8fd9\u6279\u6740\u624b\u4e00\u4e2a\u4e0d\u7559\u5730\u6740\u6389\uff0c\u6211\u53bb\u544a\u8bc9\u540e\u8fb9 \u7684\u4eba\uff0c\u6ce8\u610f\u4fdd\u62a4\u9a6c\u5339\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5979\u98de\u8eab\u5f80\u540e\u63a0\u53bb\u3002 \u3000\u3000\u675c\u671b\u6708\u6df1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u62bd\u51fa\u4e03\u661f\u5b9d\u5251\uff0c\u5927\u543c\u4e00\u58f0\uff1a\u201c\u6321\u6211\u8005\u6b7b\uff01\u201d\u821e\u8d77\u4e00\u56e2\u5251\u5149\uff0c\u5411\u524d \u76f4\u51b2\u3002 \u3000\u3000\u96ea\u4eae\u7684\u5251\u5149\u51b2\u5165\u7070\u8863\u6740\u624b\u4e1b\u4e2d\uff0c\u7acb\u523b\u5e26\u8d77\u4e00\u9053\u8840\u8165\u7684\u8840\u5e55\u3002 \u3000\u3000\u5251\u5149\u6240\u5230\u4e4b\u5904\uff0c\u8840\u8089\u6a2a\u98de\u3002 \u3000\u3000\u5f88\u5feb\uff0c\u7070\u8863\u6740\u624b\u4e00\u4e2a\u4e0d\u5269\u5730\u5168\u90fd\u6bd9\u547d\u5728\u4ed6\u7684\u5251\u4e0b\uff01 \u3000\u3000\u675c\u671b\u6708\u5bf9\u5403\u60ca\u4e0d\u5df2\u5730\u770b\u7740\u4ed6\u7684\u5c91\u5578\u864e\u548c\u4e8e\u627f\u5fd7\u7565\u663e\u88ab\u52a8\u5730\u7b11\u4e86\u7b11\uff0c\u62b9\u53bb\u5251\u4e0a\u6dcc\u6d41\u7684\u9c9c\u8840\uff0c \u5f52\u5251\u5165\u9798\u3002 \u3000\u3000\u5728\u4ed6\u5b8c\u6210\u201c\u5251\u6d77\u6d74\u9b42\u201d\u4e4b\u540e\uff0c\u6bcf\u6b21\u6740\u8fc7\u4eba\uff0c\u4ed6\u90fd\u4f1a\u5c06\u5251\u63d2\u5165\u9798\u4e2d\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u79cd\u4e60\u60ef\uff0c\u4e5f\u662f\u4e00\u79cd\u533a\u522b\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u73b0\u5728\uff0c\u4ed6\u7684\u5251\u53ea\u8981\u51fa\u9798\uff0c\u5fc5\u5b9a\u4f1a\u67d3\u6ce1\u9c9c\u8840\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u5fc5\u987b\u514b\u5236\u4f4f\u8fd9\u628a\u5251\u4e0a\u6240\u900f\u9732\u51fa\u6765\u7684\u6740\u673a\uff0c\u4e0d\u5230\u5fc5\u8981\u65f6\u523b\uff0c\u51b3\u4e0d\u80fd\u8f7b\u6613\u5730\u5c06\u5251\u62d4\u51fa \u6765\u3002 \u3000\u3000\u5c0f\u6587\u6b63\u662f\u56e0\u4e3a\u6ca1\u6709\u8fd9\u79cd\u514b\u5236\u529b\uff0c\u6240\u4ee5\u624d\u5168\u88ab\u4e0d\u8001\u4e66\u751f\u7684\u6bd2\u836f\u63a7\u5236\u4f4f\u5fc3\u667a\u3002 \u3000\u3000\u675c\u671b\u6708\u4e09\u4eba\u5f00\u59cb\u6e05\u7406\u6218\u573a\u3002 \u3000\u3000\u521a\u521a\u6e05\u7406\u5b8c\uff0c\u7a0b\u5c0f\u8776\u548c\u4e2d\u961f\u3001\u540e\u961f\u5df2\u8d76\u4e0a\u6765\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u95ee\u9053\uff1a\u201c\u6740\u624b\u4eec\u6ca1\u8dd1\u6389\u4e86\u5427\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u4e8e\u627f\u5fd7\u9053\uff1a\u201c\u5168\u90e8\u5728\u8fd9\u513f\u8eba\u7740\u5462\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5609\u8bb8\u5730\u5bf9\u675c\u671b\u6708\u70b9\u4e86\u70b9\u5934\uff0c\u53c8\u9053\uff1a\u201c\u6211\u4eec\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u6797\u4e0d\u51e1\u9053\uff1a\u201c\u6c5f\u5357\u3001\u4e2d\u5dde\u5206\u53f8\u7684\u597d\u624b\u4eec\u5171\u635f\u5931\u4e86\u4e8c\u5341\u4eba\uff0c\u5341\u6b7b\u5341\u4f24\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u628a\u6b7b\u96be\u7684\u5f1f\u5144\u62d6\u5230\u8349\u4e1b\u4e2d\u653e\u597d\uff0c\u6211\u4eec\u8d76\u8def\u8981\u7d27\uff0c\u4f1a\u6709\u4eba\u6765\u4e3a\u4ed6\u4eec\u5904\u7406\u540e\u4e8b \u7684\u3002\u201d \u3000\u3000\u6797\u4e0d\u51e1\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6ce8\u610f\uff0c\u5728\u5b89\u7f6e\u4ed6\u4eec\u7684\u5730\u65b9\u505a\u4e00\u4e2a\u6807\u5fd7\u3002\u201d \u3000\u3000\u6797\u4e0d\u51e1\u9053\uff1a\u201c\u603b\u6355\u5934\u653e\u5fc3\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\uff01\u6211\u4eec\u7ee7\u7eed\u8d76\u8def\uff01\u201d \u3000\u3000\u4e00\u884c\u4eba\u8d76\u5230\u5c0f\u6768\u6751\u65f6\uff0c\u5929\u5df2\u7ecf\u8499\u8499\u4eae\u4e86\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\u53e9\u5f00\u4e86\u51e0\u6237\u6751\u6c11\u7684\u95e8\uff0c\u501f\u4ed6\u4eec\u7684\u9505\u7076\u751f\u706b\u505a\u996d\u3002 \u3000\u3000\u5f85\u5927\u5bb6\u90fd\u5403\u8fc7\u996d\uff0c\u5929\u5df2\u7ecf\u5927\u4eae\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u4eec\u8d76\u5feb\u4e0a\u8def\u5427\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6628\u591c\u4e00\u9635\u640f\u6740\uff0c\u5927\u5bb6\u4f53\u529b\u6d88\u8017\u5f88\u5927\uff0c\u5df2\u6709\u4f24\u53f7\uff0c\u8fd8\u662f\u5728\u5c0f\u6768\u6751\u591a \u6b47\u4e00\u4f1a\u513f\u5427\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5927\u4eba\u6709\u6240\u4e0d\u77e5\uff0c\u6740\u624b\u96c6\u56e2\u4eec\u80af\u5b9a\u662f\u4f1a\u5728\u6709\u4eba\u5c45\u4f4f\u7684\u5730\u65b9\u5bf9\u6211\u4eec\u52a8\u624b\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e00\u6765\u4ed6\u4eec\u53ef\u4ee5\u7528\u767e\u59d3\u6765\u505a\u6321\u7bad\u724c\uff0c\u4e8c\u6765\u6211\u4eec\u56e0\u4e3a\u6015\u4f24\u53ca\u767e\u59d3\uff0c\u52a8\u8d77\u624b\u6765\u603b \u6709\u4e9b\u2026\u2026\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u8bf4\u4e86\uff0c\u6211\u4eec\u5feb\u8d70\u5427\u3002\u201d \u3000\u3000\u4e8e\u662f\u5927\u5bb6\u53c8\u5306\u5306\u4e0a\u8def\u3002 \u3000\u3000\u738b\u5c11\u537f\u9a71\u9a6c\u8d70\u5728\u7a0b\u5c0f\u8776\u8eab\u65c1\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u5bf9\u51b3\u6218\u4f60\u771f\u7684\u6709\u628a\u63e1\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u5927\u4eba\u4f55\u6545\u6709\u6b64\u4e00\u95ee\u5462\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u6628\u591c\u62e6\u622a\u6211\u4eec\u7684\u4e0d\u8fc7\u662f\u6740\u624b\u96c6\u56e2\u4e2d\u7684\u672b\u6d41\u89d2\u8272\u5427\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u53ef\u6211\u4eec\u8fd9\u8fb9\u5df2\u7ecf\u635f\u5931\u4e86\u4e8c\u5341\u4e2a\u4eba\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f46\u6211\u7684\u56db\u5927\u6355\u5934\u3001\u5c0f\u6587\u3001\u5c0f\u96c5\u3001\u60dc\u7389\u3001\u6797\u53f8\u6848\u672a\u53d7\u4f24\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u95ee\u9898\u662f\u6740\u624b\u96c6\u56e2\u7684\u9ad8\u7ea7\u4eba\u7269\u4e5f\u6ca1\u6709\u73b0\u8eab\u554a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5927\u4eba\u53ef\u662f\u5bf9\u5c5e\u4e0b\u6ca1\u6709\u4fe1\u5fc3\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u5fd9\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6211\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5c31\u7b97\u662f\uff0c\u4e5f\u6ca1\u6709\u5173\u7cfb\uff0c\u56e0\u4e3a\u6211\u4f1a\u4ee5\u4e8b\u5b9e\u5411\u5927\u4eba\u8bc1\u660e\uff0c\u6211\u4eec\u7684\u8ba1\u5212\u662f\u53ef\u884c\u7684\uff0c \u662f\u5b8c\u5584\u7684\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u90a3\u5c31\u597d\uff0c\u90a3\u5c31\u597d\u3002\u201d \u3000\u3000\u4ed6\u4eec\u8d70\u7684\u662f\u6cbf\u8fd0\u6cb3\u7684\u4e00\u6761\u9053\uff0c\u4e00\u8def\u884c\u6765\uff0c\u5374\u89c1\u5927\u9053\u8fb9\u7684\u597d\u591a\u7530\u5730\u90fd\u8352\u829c\u4e86\uff0c\u6709\u4e9b\u7530\u4e2d\u5df2 \u662f\u6742\u8349\u4e1b\u751f\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4e0d\u7981\u5947\u602a\uff0c\u4fbf\u95ee\u738b\u5c11\u537f\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u8fd9\u4e9b\u7530\u96be\u9053\u90fd\u6ca1\u6709\u4eba\u79cd\u5417\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u662f\u554a\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u6ca1\u4eba\u79cd\u5462\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u53ea\u56e0\u4e3a\u626c\u5dde\u592a\u7e41\u534e\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u5927\u4eba\u8fd9\u8bdd\uff0c\u6211\u53ef\u542c\u4e0d\u61c2\u4e86\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u56e0\u4e3a\u626c\u5dde\u592a\u7e41\u534e\uff0c\u6709\u4e9b\u4eba\u5c31\u8ba4\u4e3a\u626c\u5dde\u904d\u5730\u90fd\u662f\u91d1\u94b1\uff0c\u5728\u90a3\u91cc\u8d5a\u94b1\u8981\u6bd4\u79cd\u5730 \u6765\u5f97\u8f7b\u677e\uff0c\u4e8e\u662f\u90fd\u629b\u4e0b\u7530\u5730\uff0c\u8dd1\u5230\u57ce\u91cc\u53bb\u8d5a\u94b1\u7cca\u53e3\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u53ef\u662f\u79cd\u7530\u4eba\u9664\u4e86\u79cd\u7530\u53c8\u6ca1\u6709\u522b\u7684\u672c\u4e8b\uff0c\u8dd1\u5230\u626c\u5dde\u57ce\u53bb\uff0c\u9760\u4ec0\u4e48\u8d5a\u94b1\u5462\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u9760\u529b\u6c14\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\uff0c\u4ed6\u4eec\u662f\u53bb\u7ed9\u90a3\u4e9b\u6709\u94b1\u4eba\u5f53\u4f63\u4eba\u3001\u5f53\u4ec6\u4eba\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e3a\u4e86\u8d5a\u94b1\uff0c\u5c31\u7518\u613f\u53bb\u505a\u4e0b\u4eba\uff0c\u8fd8\u4e0d\u5982\u5728\u5bb6\u79cd\u7530\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u90a3\u4e9b\u4eba\u8981\u50cf\u603b\u6355\u5934\u8fd9\u6837\u8bf4\u7684\u61c2\u5f97\u8fd9\u4e2a\u9053\u7406\uff0c\u5c31\u597d\u4e86\u3002\u8fd9\u4e9b\u5e74\u6765\uff0c\u8fdb\u57ce\u5e2e\u5de5 \u5e2e\u4f63\u7684\u4eba\u8d8a\u6765\u8d8a\u591a\uff0c\u7530\u5730\u56e0\u6b64\u8352\u5e9f\u7684\u4e5f\u8d8a\u591a\uff0c\u6bcf\u5e74\u751f\u4ea7\u7684\u7a3b\u7c73\u4e5f\u8d8a\u6765\u8d8a\u5c11\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6c89\u541f\u7740\uff0c\u4e5f\u4e0d\u77e5\u5979\u5728\u60f3\u4ec0\u4e48\u3002 \u3000\u3000\u738b\u5c11\u537f\u5728\u9a6c\u4e0a\u633a\u4e86\u633a\u8170\uff0c\u4f38\u957f\u8116\u5b50\u5f80\u53f3\u8fb9\u770b\u4e86\u770b\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u65e9\u77e5\u9053\u6740\u624b\u96c6\u56e2\u4f1a\u6d3e \u4eba\u6740\u9a6c\uff0c\u6211\u4eec\u8fd8\u4e0d\u5982\u4e00\u5f00\u59cb\u5c31\u5750\u8239\uff0c\u6cbf\u8fd0\u6cb3\u5317\u4e0a\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u7684\u786e\u662f\u8fd9\u6837\uff0c\u4e0d\u8fc7\uff0c\u5728\u9646\u5730\u4e0a\uff0c\u9a6c\u6b7b\u4e86\uff0c\u6211\u4eec\u8fd8\u80fd\u9760\u4e24\u53ea\u811a\u8d70\uff0c\u8981\u662f\u5728\u6c34 \u4e0a\uff0c\u5982\u679c\u88ab\u6740\u624b\u4eec\u6f5c\u5165\u6c34\u4e2d\uff0c\u51ff\u6f0f\u4e86\u8239\uff0c\u90a3\u53ef\u5c31\u9ebb\u70e6\u4e86\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u771f\u662f\u51e1\u4e8b\u6709\u4e00\u5229\u5fc5\u4e00\u5f0a\uff0c\u5594\uff01\u4e5f\u4e0d\u77e5\u957f\u5c71\u73b0\u5728\u600e\u4e48\u6837\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u9a6c\u5927\u4eba\u90e8\u4e0b\u96c4\u5175\u6570\u4e07\uff0c\u53c8\u6709\u963f\u6a2a\u3001\u963f\u4fdd\u7d27\u968f\u8eab\u8fb9\uff0c\u4e0d\u4f1a\u51fa\u4efb\u4f55\u610f\u5916\u7684\uff0c\u5927 \u4eba\u5c3d\u7ba1\u653e\u5fc3\u3002\u201d \u3000\u3000\u4e8c\u4eba\u4e00\u8def\u8bf4\u7740\uff0c\u7a0b\u5c0f\u8776\u5374\u4e0d\u4f4f\u5730\u5c06\u76ee\u5149\u6295\u5411\u8fd0\u6cb3\u4e4b\u4e0a\u3002 \u3000\u3000\u738b\u5c11\u537f\u53ea\u7ba1\u4e0e\u5979\u8bf4\u7740\u8bdd\uff0c\u5e76\u6ca1\u6709\u6ce8\u610f\u5230\uff0c\u4f46\u8ddf\u5728\u4ed6\u4eec\u540e\u9762\u7684\u56db\u5927\u6355\u5934\u5374\u65e9\u5df1\u6ce8\u610f\u5230\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u731c\u60f3\u7a0b\u5c0f\u8776\u662f\u53d1\u73b0\u4e86\u8fd0\u6cb3\u4e0a\u6709\u4ec0\u4e48\u53ef\u7591\u4e4b\u5904\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4eec\u4e5f\u5f00\u59cb\u89c2\u5bdf\u6cb3\u4e0a\u6765\u5f80\u7684\u8239\u53ea\u3002 \u3000\u3000\u6cb3\u4e0a\u767d\u5e06\u70b9\u70b9\uff0c\u8239\u53ea\u53ef\u4e0d\u5c11\uff0c\u4f46\u770b\u4e86\u534a\u5929\uff0c\u5374\u6ca1\u6709\u53d1\u73b0\u4ec0\u4e48\u4e0d\u592a\u6b63\u5e38\u7684\u60c5\u51b5\u3002 \u3000\u3000\u524d\u9762\uff0c\u8fdc\u8fdc\u5730\u51fa\u73b0\u4e86\u4e00\u62b9\u6de1\u6de1\u7684\u9752\u7070\u8272\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4e3e\u8d77\u9a6c\u97ad\uff0c\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u524d\u9762\u662f\u4e0d\u662f\u8427\u4f2f\u9547\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u5e94\u8be5\u662f\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u4eec\u5c31\u8d76\u5230\u8427\u4f2f\u9547\u6b47\u811a\uff0c\u65f6\u95f4\u4e5f\u5feb\u5230\u5348\u996d\u7684\u996d\u70b9\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u5ffd\u7136\u5927\u543c\u9053\uff1a\u201c\u738b\u5927\u4eba\u5c0f\u5fc3\uff01\u201d \u3000\u3000\u81ea\u9a6c\u4e0a\u98de\u8eab\u63a0\u8d77\uff0c\u5f20\u5f00\u53cc\u81c2\uff0c\u62b1\u7740\u738b\u5c11\u537f\u81ea\u9a6c\u4e0a\u6eda\u843d\u5728\u5730\u3002 \u3000\u3000\u738b\u5c11\u537f\u7684\u5750\u9a91\u957f\u5636\u4e00\u58f0\uff0c\u56db\u8e44\u4e00\u8f6f\uff0c\u5df2\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u9a6c\u7684\u81c0\u90e8\u5df2\u624e\u7740\u4e00\u628a\u98de\u5200\u3002 \u3000\u3000\u5200\u8eab\u5df2\u5168\u90e8\u6ca1\u5165\u9a6c\u8eab\uff0c\u53ea\u7559\u4e0b\u9c9c\u7ea2\u7684\u5200\u67c4\u5728\u5916\u9762\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4e5f\u62d4\u51fa\u77ed\u5251\uff0c\u559d\u9053\uff1a\u201c\u5927\u5bb6\u5c0f\u5fc3\uff0c\u6cb3\u4e0a\u6709\u654c\u4eba\uff01\u201d \u3000\u3000\u53ea\u89c1\u56db\u53ea\u5927\u4e4c\u84ec\u8239\u5df2\u7ecf\u81ea\u6cb3\u5fc3\u51b2\u5230\u4e86\u5cb8\u8fb9\uff0c\u81ea\u8239\u8231\u5185\u6d8c\u51fa\u4e86\u8fd1\u767e\u540d\u6740\u624b\u3002 \u3000\u3000\u4ed6\u4eec\u4e2d\u6709\u4e00\u534a\u662f\u7070\u8863\u6740\u624b\uff0c\u53e6\u4e00\u534a\u9ed1\u8863\u6740\u624b\u7684\u624b\u4e2d\uff0c\u4e3e\u7740\u7684\u90fd\u662f\u91d1\u80cc\u5927\u5200\uff01 \u3000\u3000\u201c\u91d1\u5200\u7ea7\u6740\u624b\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u60ca\u547c\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u8fd9\u4e9b\u4eba\u6bd4\u7070\u8863\u6740\u624b\u8981\u5389\u5bb3\u5f97\u591a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u9759\u5730\u9053\uff1a\u201c\u4e0d\u8981\u4e71\uff0c\u6392\u597d\u9635\u5f62\uff0c\u5c06\u738b\u5927\u4eba\u548c\u91d1\u5c0f\u7709\u62a4\u5728\u9635\u4e2d\uff01\u5f13\u5f29\u624b\u94c1\u7bad\u51c6\u5907\uff0c \u56db\u5927\u6355\u5934\u7387\u4eba\u6740\u654c\uff01\u201d \u3000\u3000\u9635\u5f62\u521a\u5e03\u7f6e\u5b8c\u6bd5\uff0c\u6740\u624b\u4eec\u5df2\u51b2\u8fc7\u4e86\u6cb3\u6ee9\uff0c\u5411\u5927\u9053\u4e0a\u51b2\u6740\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u4e00\u9762\u51b2\uff0c\u4e00\u9762\u6325\u821e\u7740\u5200\u5251\uff0c\u5927\u58f0\u547c\u53eb\u7740\uff0c\u58f0\u52bf\u5341\u5206\u60ca\u4eba\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u65ad\u559d\u9053\uff1a\u201c\u653e\u7bad\uff01\u201d \u3000\u3000\u673a\u7c27\u6263\u54cd\u58f0\u540c\u65f6\u54cd\u8d77\uff01 \u3000\u3000\u4e94\u767e\u53ea\u94c1\u7bad\u7acb\u5373\u5982\u4e00\u9635\u74e2\u6cfc\u5927\u96e8\uff0c\u8fce\u5934\u7838\u5411\u6740\u624b\u4eec\u3002 \u3000\u3000\u60e8\u53eb\u8fde\u58f0\u3002 \u3000\u3000\u6740\u624b\u4eec\u5012\u4e0b\u4e86\u4e09\u5206\u4e4b\u4e00\u3002 \u3000\u3000\u4f46\u6ca1\u6709\u88ab\u5c04\u4e2d\u7684\u6740\u624b\u4eec\u5df2\u7ecf\u51b2\u4e0a\u4e86\u5927\u9053\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u6b63\u51c6\u5907\u4e0b\u4ee4\u653e\u7b2c\u4e8c\u9635\u5f29\u7bad\uff0c\u5374\u89c1\u524d\u9762\u5927\u9053\u4e0a\u5c18\u571f\u5927\u8d77\uff0c\u4f20\u6765\u4e00\u9635\u66b4\u96e8\u822c\u5bc6\u96c6\u7684\u9a6c \u8e44\u58f0\uff01 \u3000\u3000\u539f\u6765\u6740\u624b\u4eec\u51c6\u5907\u7684\u662f\u4e24\u9762\u5939\u51fb\u4e4b\u7b56\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u7acb\u523b\u5c06\u5f13\u5f29\u624b\u8c03\u5230\u4e86\u5de6\u4fa7\uff0c\u6b63\u5bf9\u7740\u5927\u9053\u4e0a\u75be\u9a70\u800c\u6765\u7684\u6740\u624b\u96c6\u56e2\u7684\u9a6c\u961f\uff0c \u3000\u3000\u7136\u540e\u77ed\u5251\u76f4\u6307\u5411\u6cb3\u4e2d\u51b2\u4e0a\u7684\u6740\u624b\u4eec\uff0c\u9053\uff1a\u201c\u56db\u5927\u6355\u5934\u3001\u5c0f\u6587\uff0c\u683c\u6740\u4ed6\u4eec\uff01\u201d \u3000\u3000\u5c0f\u6587\u7b54\u5e94\u4e00\u58f0\uff0c\u6574\u4e2a\u4eba\u7acb\u5373\u5316\u4f5c\u4e00\u56e2\u523a\u76ee\u7684\u5251\u5149\uff0c\u843d\u5165\u6740\u624b\u7fa4\u4e2d\u3002 \u3000\u3000\u7d27\u63a5\u7740\uff0c\u7b2c\u4e8c\u56e2\u66f4\u4e3a\u523a\u76ee\u7684\u5251\u5149\u4e5f\u843d\u4e86\u8fdb\u53bb\uff01 \u3000\u3000\u8fd9\u4eba\u5f53\u7136\u5c31\u662f\u675c\u671b\u6708\u3002 \u3000\u3000\u5c91\u5578\u864e\u3001\u4e8e\u627f\u5fd7\u5e26\u7740\u6c5f\u5357\u5206\u53f8\u7684\u5341\u6570\u540d\u597d\u624b\u4e5f\u5927\u547c\u7740\u76f4\u5377\u4e0a\u53bb\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u5e26\u7740\u4e2d\u5dde\u5206\u53f8\u7684\u597d\u624b\u4eec\u5728\u5f13\u5f29\u624b\u4e24\u4fa7\uff0c\u4f46\u7b49\u94c1\u7bad\u98de\u51fa\uff0c\u4fbf\u4f1a\u4e0e\u7a0b\u5c0f\u8776\u4e00\u8d77\u540c\u81ea\u7bad \u96e8\u4e0b\u9003\u5f97\u6027\u547d\u7684\u6740\u624b\u4eec\u8840\u6218\u3002 \u3000\u3000\u6740\u624b\u96c6\u56e2\u7684\u9a6c\u961f\u8d8a\u6765\u8d8a\u8fd1\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u653e\uff01\u201d \u3000\u3000\u7bad\u96e8\u98de\u51fa\u3002 \u3000\u3000\u7acb\u523b\u54cd\u8d77\u4e86\u4e00\u9635\u9a6c\u7684\u5636\u9e23\u58f0\u548c\u4eba\u7684\u60e8\u53eb\u58f0\u3002 \u3000\u3000\u52bf\u5982\u72c2\u98ce\u9aa4\u96e8\u7684\u9a6c\u961f\u7684\u51b2\u51fb\u88ab\u963b\u4f4f\u4e86\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u4e3e\u8d77\u6587\u660c\u7b14\uff0c\u5f53\u5148\u51b2\u4e0a\u3002 \u3000\u3000\u5927\u9053\u4e4b\u4e0a\uff0c\u7acb\u523b\u5c55\u5f00\u4e86\u6b8b\u9177\u7684\u640f\u6740\u3002 \u3000\u3000\u5c0f\u96c5\u548c\u60dc\u7389\u4e00\u4e2a\u7acb\u5728\u738b\u5c11\u537f\u8eab\u8fb9\uff0c\u4e00\u4e2a\u7acb\u5728\u91d1\u5c0f\u7709\u8eab\u8fb9\uff0c\u4e8c\u4eba\u90fd\u4e3e\u7740\u5175\u5668\uff0c\u7d27\u5f20\u5730\u6ce8\u89c6 \u7740\u773c\u524d\u6b8b\u70c8\u7684\u6218\u6597\u3002 \u3000\u3000\u5979\u4eec\u5341\u5206\u60f3\u51b2\u4e0a\u53bb\u4e00\u8d77\u6740\u654c\uff0c\u4f46\u5979\u4eec\u77e5\u9053\u73b0\u5728\u7684\u4efb\u52a1\u66f4\u91cd\u8981\u3002 \u3000\u3000\u675c\u671b\u6708\u8fd9\u4e00\u8fb9\uff0c\u6740\u624b\u4eec\u5df2\u7ecf\u8d25\u4e86\u3002 \u3000\u3000\u4f46\u662f\u6740\u624b\u4eec\u5374\u6ca1\u6709\u4e00\u4e2a\u540e\u9000\u3002 \u3000\u3000\u867d\u7136\u5c0f\u6587\u548c\u675c\u671b\u6708\u4e8c\u4eba\u53ea\u8981\u518d\u4e00\u5251\u6325\u51fa\uff0c\u5c31\u4f1a\u683c\u6740\u4e8c\u4e09\u540d\u6740\u624b\uff0c\u4f46\u662f\u90a3\u4e9b\u91d1\u5200\u6740\u624b\u4eec\u4ecd \u7136\u568e\u53eb\u7740\uff0c\u76f4\u5411\u524d\u51b2\u3002 \u3000\u3000\u51b2\u4e0a\u6765\u7684\u90fd\u88ab\u51cc\u5389\u7684\u5251\u6c14\u524c\u5012\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8001\u5c91\u3001\u8001\u4e8e\uff0c\u53bb\u5e2e\u603b\u6355\u5934\u90a3\u8fb9\uff0c\u8fd9\u91cc\u6709\u6211\u548c\u5c0f\u6587\u5c31\u591f\u4e86\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u4e00\u770b\u773c\u524d\u5f62\u52bf\uff0c\u7acb\u523b\u8f6c\u5934\u51b2\u5230\u4e86\u5927\u9053\u4e0a\u3002 \u3000\u3000\u867d\u8bf4\u91d1\u5200\u6740\u624b\u8fd8\u5269\u4e0b\u4e86\u4e8c\u5341\u591a\u4eba\uff0c\u4f46\u4ed6\u4eec\u5374\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u51b2\u7834\u675c\u671b\u6708\u548c\u5c0f\u6587\u53cc\u5251\u5408\u58c1\u5e03 \u4e0b\u7684\u5251\u7f51\uff01 \u3000\u3000\u4e8c\u4eba\u8eab\u8fb9\uff0c\u5df2\u8eba\u5012\u4e86\u4e0d\u4e0b\u56db\u5341\u5177\u5934\u88c2\u8179\u5f00\u7684\u5c38\u4f53\u3002 \u3000\u3000\u4e8e\u627f\u5fd7\u8fd8\u4e0d\u592a\u653e\u5fc3\uff0c\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u5c0f\u675c\uff0c\u884c\u4e0d\u884c\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u4e00\u5251\u6325\u51fa\uff0c\u4e09\u9897\u4eba\u5934\u6392\u5728\u4e86\u5176\u4e2d\u3002 \u3000\u3000\u4ed6\u5927\u7b11\u9053\uff1a\u201c\u4f60\u770b\u884c\u4e0d\u884c\uff1f\u201d \u3000\u3000\u4e8e\u627f\u5fd7\u653e\u5fc3\u4e86\uff0c\u6325\u5200\u671d\u524d\u5954\u53bb\uff0c\u4e00\u9762\u5bf9\u6797\u4e0d\u51e1\u7b49\u4eba\u8bf4\u9053\uff1a\u201c\u5f1f\u5144\u4eec\uff0c\u90fd\u5230\u8fd9\u8fb9\u6765\u3002\u201d \u3000\u3000\u4ed6\u4eec\u7684\u52a0\u5165\uff0c\u4f7f\u5927\u9053\u4e4b\u4e0a\u7684\u80dc\u8d25\u7acb\u523b\u5c31\u51b3\u5b9a\u4e86\u3002 \u3000\u3000\u8fd8\u4e0d\u5230\u4e00\u987f\u996d\u7684\u529f\u592b\uff0c\u4ece\u4e24\u65b9\u9762\u88ad\u6765\u7684\u6740\u624b\u5df2\u6b7b\u4f24\u6b86\u5c3d\u3002 \u3000\u3000\u4ee4\u4ed6\u4eec\u5403\u60ca\u7684\u662f\uff0c\u6ca1\u6709\u4e00\u4e2a\u6740\u624b\u4e3b\u52a8\u9003\u8dd1\uff0c\u8fd9\u4e5f\u8bf4\u660e\u4e86\u8840\u624b\u65b9\u8f6e\u662f\u4f55\u7b49\u5389\u5bb3\u7684\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u675c\u671b\u6708\u548c\u5c0f\u6587\u76f8\u89c6\u4e00\u7b11\uff0c\u8fd8\u5251\u5f52\u9798\uff0c\u8d70\u56de\u5230\u738b\u5c11\u537f\u8eab\u8fb9\u3002 \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u5c0f\u6587\uff0c\u4f60\u6ca1\u6709\u53d7\u4f24\u5427\uff1f\u201d \u3000\u3000\u5c0f\u6587\u751c\u751c\u5730\u4e00\u7b11\uff1a\u201c\u8c22\u8c22\u4f60\u7684\u5173\u5fc3\u3002\u201d \u3000\u3000\u60dc\u7389\u4e5f\u5f88\u5173\u5207\u5730\u671b\u7740\u675c\u671b\u6708\u3002 \u3000\u3000\u675c\u671b\u6708\u62b9\u4e86\u62b9\u8138\u988a\u4e0a\u7684\u6c57\u6c34\uff0c\u7b11\u9053\uff1a\u201c\u8fd8\u771f\u6709\u4e9b\u7d2f\uff01\u201d \u3000\u3000\u60dc\u7389\u653e\u5fc3\u5730\u7b11\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u56db\u4e0b\u770b\u4e86\u4e00\u773c\uff0c\u957f\u957f\u51fa\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u6765\u7684\u592a\u7a81\u7136\u4e86\uff0c\u771f\u6ca1\u60f3\u5230\u4ed6\u4eec\u4f1a\u5728 \u6c34\u8def\u4e0a\u6253\u4e3b\u610f\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u60f3\u60f3\u81ea\u5df1\u521a\u624d\u8fd8\u5728\u60f3\u53ef\u4ee5\u8d70\u6c34\u8def\uff0c\u4e0d\u7981\u6709\u4e9b\u540e\u6015\u3002 \u3000\u3000\u5982\u679c\u4ed6\u4eec\u771f\u7684\u5750\u8239\u81ea\u8fd0\u6cb3\u5317\u4e0a\uff0c\u53ef\u80fd\u73b0\u5728\u5df2\u7ecf\u906d\u4e86\u6740\u624b\u4eec\u7684\u6bd2\u624b\u3002 \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u675c\u53f8\u4e3b\uff0c\u4f60\u662f\u5982\u4f55\u53d1\u73b0\u6740\u624b\u4eec\u7684\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u91cc\u5e76\u4e0d\u662f\u6df1\u6c34\uff0c\u800c\u4e14\u5cb8\u8fb9\u7684\u6c34\u5f88\u6d45\uff0c\u6839\u672c\u4e0d\u9002\u5408\u8239\u9760\u5cb8\uff0c\u4f46\u90a3\u56db\u6761 \u8239\u5374\u76f4\u5411\u5cb8\u8fb9\u51b2\u6765\uff0c\u6240\u4ee5\u6211\u611f\u89c9\u5230\u60c5\u51b5\u4e0d\u592a\u5bf9\u5934\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u5982\u679c\u4e0d\u662f\u4f60\uff0c\u90a3\u628a\u98de\u5200\u4e00\u5b9a\u4f1a\u5c04\u4e2d\u6211\u7684\uff0c\u8c22\u8c22\u4f60\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5927\u4eba\u592a\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5bf9\u6797\u4e0d\u51e1\u9053\uff1a\u201c\u6211\u4eec\u7684\u635f\u5931\u5927\u5417\uff1f\u201d \u3000\u3000\u6797\u4e0d\u51e1\u7684\u8138\u8272\u5341\u5206\u6c89\u91cd\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e2d\u5dde\u5206\u53f8\u7684\u5f1f\u5144\u4ec5\u5269\u4e0b\u5341\u516d\u4eba\uff0c\u6c5f\u5357\u5206\u53f8\u7684\uff0c\u5df2\u53ea \u6709\u4e5d\u4e2a\u4eba\u4e86\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u5927\u5403\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u4e0d\u51e1\u9053\uff1a\u201c\u521a\u624d\u6740\u624b\u9a6c\u961f\u4e4b\u4e2d\uff0c\u6709\u516b\u540d\u9ed1\u8863\u5251\u624b\uff0c\u5251\u6cd5\u72e0\u6bd2\uff0c\u6b66\u529f\u9ad8\u5f3a\uff0c\u5982\u679c\u4e0d\u662f\u603b \u6355\u5934\u53ca\u65f6\u6740\u4e86\u4ed6\u4eec\uff0c\u6211\u4eec\u7684\u635f\u5931\u8fd8\u8981\u66f4\u5927\u4e00\u4e9b\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u9664\u4e86\u4e94\u5341\u540d\u5f13\u5f29\u624b\u4e4b\u5916\uff0c\u6211\u4eec\u7684\u6218\u6597\u4eba\u5458\u5df2\u53ea\u5269\u4e0b\u4e86\u4e09\u5341\u4e8c\u4eba\uff0c \u800c\u4e14\uff0c\u5c0f\u96c5\u548c\u60dc\u7389\u4e8c\u4eba\u8fd8\u4e0d\u80fd\u53c2\u52a0\u6218\u6597\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6740\u624b\u4eec\u6d3e\u51fa\u7684\u4eba\u8d8a\u6765\u8d8a\u5f3a\uff0c\u6211\u4eec\u80fd\u652f\u6491\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u76b1\u7740\u7709\u5934\uff0c\u663e\u7136\u662f\u5728\u5fc3\u91cc\u6697\u81ea\u7b97\u8ba1\u7740\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u9ed1\u8863\u5251\u624b\u662f\u4e0d\u8001\u4e66\u751f\u4eb2\u624b\u8bad\u7ec3\u7684\u6740\u624b\uff0c\u65e2\u7136\u4ed6\u4eec\u5df2\u7ecf\u5f00\u59cb\u53c2\u4e0e\u622a \u6740\uff0c\u8bf4\u660e\u65b9\u8f6e\u3001\u4e0d\u8001\u4e66\u751f\u3001\u9ed1\u8863\u8499\u9762\u4eba\u4e5f\u5feb\u8981\u73b0\u8eab\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4e5f\u662f\u8fd9\u6837\u63a8\u65ad\u7684\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5979\u81ea\u6000\u91cc\u638f\u51fa\u4e86\u4e00\u622a\u7af9\u7ba1\uff0c\u4e00\u626c\u624b\u7af9\u7ba1\u98de\u8d77\u5728\u534a\u7a7a\u4e2d\u3002 \u3000\u3000\u201c\u55d6\u201d\u5730\u4e00\u58f0\u5de8\u54cd\uff0c\u7af9\u7ba1\u5c3e\u90e8\u5192\u51fa\u4e00\u80a1\u6d53\u70df\uff0c\u63a5\u7740\u7af9\u7ba1\u5b50\u5411\u4e0a\u98de\u4e86\u4e00\u9635\u5b50\uff0c\u7136\u540e\uff0c\u7af9\u7ba1 \u7a81\u7136\u70b8\u5f00\u3002 \u3000\u3000\u7a7a\u4e2d\uff0c\u6563\u5f00\u4e00\u9635\u84dd\u8272\u7684\u70df\u96fe\u3002 \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u662f\u4e0d\u662f\u5df2\u51c6\u5907\u597d\u4e86\u540e\u5907\u4eba\u624b\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\uff0c\u6211\u4eec\u5c3d\u5feb\u8d76\u5230\u8427\u4f2f\u9547\u627e\u4e2a\u5730\u65b9\u597d\u597d\u4f11\u606f\u4e00\u4e0b\u3002\u201d \u3000\u3000\u4f17\u4eba\u5c06\u80fd\u9a6e\u91cd\u7684\u9a6c\u5339\u96c6\u4e2d\u8d77\u6765\uff0c\u52a0\u4e0a\u6740\u624b\u9a6c\u961f\u7684\u9a6c\uff0c\u8fd8\u6709\u4e09\u5341\u4f59\u5339\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8427\u4f2f\u9547\u5e76\u4e0d\u7b97\u5c0f\uff0c\u9547\u91cc\u5e94\u8be5\u6709\u9a6c\uff0c\u5927\u5bb6\u5148\u575a\u6301\u4e00\u9635\uff0c\u8d76\u5230\u90a3\u91cc\uff0c\u518d\u60f3\u529e \u6cd5\u3002\u201d \u3000\u3000------------- \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u4e09\u5341\u7ae0 \u3000\u3000\u4e00\u884c\u4eba\u6765\u5230\u9547\u5916\u65f6\uff0c\u5df2\u662f\u5348\u65f6\u3002 \u3000\u3000\u8427\u4f2f\u9547\u679c\u7136\u5f88\u5927\uff0c\u7b80\u76f4\u53ef\u4ee5\u7b97\u662f\u4e00\u5ea7\u5c0f\u57ce\u4e86\u3002 \u3000\u3000\u9547\u5b50\u5916\u9762\u6709\u4e00\u9053\u5f88\u9ad8\u7684\u56f4\u5899\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u884c\u4eba\u8fdb\u5165\u4e86\u9547\u5b50\u5357\u95e8\uff0c\u7acb\u523b\u5f15\u8d77\u4e86\u4eba\u4eec\u7684\u6ce8\u610f\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u8eab\u4e0a\u90fd\u6cbe\u6709\u9c9c\u8840\u3002 \u3000\u3000\u53ea\u89c1\u8def\u4e0a\u884c\u4eba\u7eb7\u7eb7\u8eb2\u907f\uff0c\u4e0d\u4e00\u4f1a\u513f\uff0c\u8857\u9053\u4e0a\u5df2\u662f\u7a7a\u65e0\u4e00\u4eba\u3002 \u3000\u3000\u8857\u4e24\u8fb9\u4eba\u5bb6\u7684\u5927\u95e8\u4e5f\u90fd\u5173\u7d27\u4e86\u3002 \u3000\u3000\u9547\u5b50\u91cc\u53ea\u6709\u4e00\u5bb6\u996d\u9986\u3002 \u3000\u3000\u996d\u9986\u5012\u662f\u6ca1\u6709\u5173\u95e8\uff0c\u76f8\u53cd\uff0c\u8001\u677f\u548c\u4e09\u56db\u4e2a\u4f19\u8ba1\u8fd8\u7b11\u563b\u563b\u5730\u8fce\u4e86\u51fa\u6765\uff0c\u8bf7\u5ba2\u4eba\u8fdb\u53bb\u5403\u996d\u3001 \u6b47\u606f\u3002 \u3000\u3000\u751f\u610f\u4eba\u53ea\u8981\u6709\u751f\u610f\u53ef\u505a\uff0c\u6709\u94b1\u53ef\u8d5a\uff0c\u80c6\u5b50\u4e00\u822c\u8fd8\u662f\u6bd4\u8f83\u5927\u7684\u3002 \u3000\u3000\u800c\u4e14\u90a3\u51e0\u5341\u540d\u5f13\u5f29\u624b\u8eab\u4e0a\u7a7f\u7740\u5b98\u519b\u7684\u5236\u670d\uff0c\u4e00\u770b\u5c31\u77e5\u9053\u4e0d\u4f1a\u662f\u5f3a\u76d7\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u548c\u56db\u5927\u6355\u5934\u4e00\u8d77\u8fdb\u540e\u95e8\uff0c\u623f\u91cc\u7684\u5ba2\u4eba\u5c31\u7eb7\u7eb7\u79bb\u5ea7\u7ed3\u8d26\uff0c\u4e00\u4f1a\u513f\u5c31\u5c06\u8bfa\u5927\u7684\u5e97\u5802\u7a7a \u4e86\u51fa\u6765\u3002 \u3000\u3000\u8001\u677f\u7b11\u563b\u563b\u5730\u9053\uff1a\u201c\u8d70\u4e86\u4e5f\u597d\uff0c\u6211\u6b63\u60f3\u8bf8\u4f4d\u5b98\u7237\u6709\u8fd9\u4e48\u591a\u4eba\uff0c\u5c0f\u5e97\u5750\u4e0d\u4e0b\u5462\uff1f\u8bf7\uff0c\u8bf7\uff0c \u5feb\u8bf7\u8fdb\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u8ba9\u4e94\u5341\u540d\u5f13\u5f29\u624b\u548c\u4e8c\u5341\u4e94\u540d\u6c5f\u5357\u3001\u4e2d\u5dde\u5206\u53f8\u7684\u597d\u624b\u5206\u522b\u5b88\u5728\u5e97\u5802\u7684\u540e\u95e8\u5916\uff0c\u8fd9\u624d \u8bf7\u738b\u5c11\u537f\u548c\u91d1\u5c0f\u7709\u8d70\u8fdb\u4e86\u5e97\u91cc\u3002 \u3000\u3000\u5979\u8ba9\u738b\u5c11\u537f\u3001\u91d1\u5c0f\u7709\u4e0e\u5c0f\u6587\u3001\u5c0f\u96c5\u3001\u60dc\u7389\u5750\u5728\u6b63\u4e2d\u95f4\u7684\u4e00\u5f20\u684c\u5b50\u4e0a\u3002 \u3000\u3000\u8fd9\u6837\u5b89\u6392\uff0c\u66f4\u4fbf\u4e8e\u4fdd\u62a4\u3002 \u3000\u3000\u51e0\u540d\u5e97\u4f19\u8ba1\u5f88\u5feb\u9001\u4e0a\u4e86\u70ed\u83dc\u3002 \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u513f\u529f\u592b\uff0c\u5341\u51e0\u5f20\u684c\u5b50\u4e0a\u90fd\u6446\u4e0a\u4e86\u56db\u4e94\u4e2a\u70ed\u83dc\uff0c\u4e00\u5927\u76c6\u7c73\u996d\uff0c\u4e00\u5927\u76c6\u70ed\u6c14\u817e\u817e\u7684 \u6c64\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u548c\u56db\u5927\u6355\u5934\u76f8\u5750\u4e00\u684c\u3002 \u3000\u3000\u4f17\u4eba\u90fd\u662f\u53c8\u7d2f\u3001\u53c8\u997f\u3001\u53c8\u6e34\uff0c\u6240\u4ee5\uff0c\u5927\u5bb6\u987e\u4e0d\u4e0a\u5403\u996d\uff0c\u90fd\u62a2\u7740\u559d\u8336\u3001\u559d\u6c64\u3002 \u3000\u3000\u8001\u677f\u7b11\u563b\u563b\u5730\u9053\uff1a\u201c\u8bf8\u4f4d\u5b98\u5175\uff0c\u60f3\u559d\u70b9\u9152\u5417\uff1f\u5c0f\u5e97\u6709\u2026\u2026\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u4e0d\u7528\u8bf4\u4e86\uff0c\u6211\u4eec\u4e0d\u559d\u9152\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u60dc\u7389\uff0c\u8bd5\u8bd5\u996d\u83dc\u91cc\u6709\u6ca1\u6709\u6bd2\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u8001\u677f\u4e0d\u9ad8\u5174\u4e86\uff0c\u9053\uff1a\u201c\u6211\u5728\u8427\u4f2f\u9547\u5f00\u4e86\u51e0\u5341\u5e74\u9986\u5b50\uff0c\u996d\u83dc\u91cc\u600e\u4e48\u4f1a\u6709\u6bd2\u5462\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u4e0b\u53bb\u5427\uff0c\u4e0d\u7528\u4f60\u4f3a\u5019\uff01\u201d \u3000\u3000\u8001\u677f\u8f6c\u8eab\u8d70\u5230\u67dc\u53f0\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u996d\u83dc\u91cc\u6ca1\u6709\u6bd2\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\u5566\uff0c\u5927\u5bb6\u5feb\u5403\u996d\u5427\u3002\u201d \u3000\u3000\u5403\u5b8c\u996d\uff0c\u7a0b\u5c0f\u8776\u5bf9\u5403\u8fc7\u996d\u7684\u4e8c\u5341\u4e94\u540d\u5f13\u5f29\u624b\u548c\u738b\u575a\u3001\u4f55\u5927\u5149\u9053\uff1a\u201c\u4f60\u4eec\u53bb\u6362\u524d\u95e8\u7684\u90a3\u4e9b \u5f1f\u5144\u6765\u5403\u996d\u3002\u201d \u3000\u3000\u738b\u575a\u3001\u4f55\u5927\u5149\u548c\u5f13\u5f29\u624b\u4eec\u7eb7\u7eb7\u7ad9\u8d77\u4e86\u8eab\u5b50\uff0c\u6b63\u60f3\u5f80\u5916\u8d70\uff0c\u7a81\u7136\u4e00\u4e2a\u4e2a\u90fd\u6447\u6643\u8d77\u6765\uff0c\u8138\u8272 \u4e5f\u5f00\u59cb\u53d1\u9752\u3002 \u3000\u3000\u60dc\u7389\u5927\u5403\u4e00\u60ca\uff0c\u5fd9\u9053\uff1a\u201c\u5feb\uff0c\u5feb\u5403\u89e3\u6bd2\u4e39\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u81ea\u5df1\u5df2\u5148\u670d\u4e0b\u89e3\u6bd2\u4e39\u3002 \u3000\u3000\u738b\u5c11\u537f\u3001\u91d1\u5c0f\u7709\u3001\u5c0f\u6587\u3001\u5c0f\u96c5\u3001\u7a0b\u5c0f\u8776\u548c\u56db\u5927\u6355\u5934\u4e5f\u90fd\u611f\u5230\u809a\u4e2d\u75db\u5982\u5200\u7ede\uff0c\u5fd9\u638f\u51fa\u89e3\u836f \u4e22\u8fdb\u4e86\u53e3\u4e2d\u3002 \u3000\u3000\u738b\u575a\u3001\u4f55\u5927\u5149\u7684\u52a8\u4f5c\u8fd8\u7b97\u5feb\uff0c\u4f46\u90a3\u4e8c\u5341\u4e94\u540d\u5f13\u5f29\u624b\u90fd\u90a3\u4e48\u521a\u638f\u51fa\u836f\u6765\uff0c\u4fbf\u4e00\u4e2a\u63a5\u4e00\u4e2a\u5012 \u5728\u4e86\u5730\u4e0a\u3002 \u3000\u3000\u60dc\u7389\u9762\u8272\u82cd\u767d\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u6211\u5df2\u7ecf\u67e5\u8fc7\u4e86\uff0c\u996d\u83dc\u4e2d\u7684\u786e\u6ca1\u6709\u6bd2\uff01\u201d \u3000\u3000\u53ea\u89c1\u90a3\u8001\u677f\u51fa\u67dc\u53f0\uff0c\u5927\u7b11\u9053\uff1a\u201c\u996d\u83dc\u91cc\u7684\u786e\u6ca1\u6709\u6bd2\uff0c\u4f46\u7897\u7b77\u4e0a\u6709\u6bd2\uff01\u4f60\u4eec\u8d70\u4e0d\u4e86\u5566\uff01\u544a \u8bc9\u4f60\u4eec\uff0c\u9a6c\u7684\u8349\u6599\u91cc\u4e5f\u4e0b\u4e86\u6bd2\uff0c\u73b0\u5728\u53ea\u6015\u5df2\u7ecf\u5168\u90fd\u6b7b\u6389\u4e86\uff01\u201d \u3000\u3000\u60dc\u7389\u6323\u624e\u7740\u7ad9\u8d77\u8eab\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u8001\u4e66\u751f\u95e8\u4e0b\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u5e97\u8001\u677f\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u60dc\u7389\u7a81\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u597d\u3002\u201d \u3000\u3000\u5e97\u8001\u677f\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u60dc\u7389\u6df1\u6df1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u4e0d\u8001\u4e66\u751f\u7684\u6bd2\u6211\u90fd\u80fd\u89e3\u5f00\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5979\u5df2\u6251\u5411\u5e97\u8001\u677f\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u628a\u957f\u5251\u3002 \u3000\u3000\u5e97\u8001\u677f\u5927\u5403\u4e00\u60ca\uff0c\u8f6c\u8eab\u60f3\u8dd1\uff0c\u5df2\u88ab\u957f\u5251\u523a\u7a7f\u800c\u8fc7\uff0c\u60e8\u53eb\u4e00\u58f0\uff0c\u5012\u5730\u6bd9\u547d\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5e97\u5916\u5df2\u54cd\u8d77\u4e86\u559d\u53f1\u58f0\uff0c\u6253\u95fb\u58f0\uff0c\u5175\u5203\u78b0\u62a2\u4e4b\u58f0\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u548c\u56db\u5927\u6355\u5934\u3001\u5c0f\u6587\u3001\u5c0f\u96c5\u3001\u60dc\u7389\u4e00\u8d77\u51b2\u51fa\u95e8\u5916\uff0c\u738b\u575a\u4e0e\u4f55\u5927\u5149\u62a4\u7740\u738b\u5c11\u537f\u548c\u91d1\u5c0f \u7709\u7d27\u8ddf\u7740\u4ed6\u4eec\u3002 \u3000\u3000\u5728\u95e8\u5916\u4e0e\u4e2d\u5dde\u3001\u6c5f\u5357\u5206\u53f8\u7684\u597d\u624b\u4ea4\u6218\u7684\u4eba\uff0c\u7adf\u7136\u662f\u521a\u624d\u7eb7\u7eb7\u79bb\u5e97\u7684\u90a3\u6279\u98df\u5ba2\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u7684\u8868\u60c5\u90fd\u5f88\u6728\u7136\uff0c\u8138\u4e0a\u663e\u7136\u6234\u4e86\u4eba\u76ae\u9762\u5177\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u7684\u6b66\u529f\u90fd\u5341\u5206\u4e4b\u9ad8\uff0c\u5206\u53f8\u7684\u597d\u624b\u4eec\u6839\u672c\u4e0d\u662f\u4ed6\u4eec\u7684\u5bf9\u624b\uff01 \u3000\u3000\u675c\u671b\u6708\u3001\u5c0f\u6587\u6325\u5251\u51b2\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u6765\u4fdd\u62a4\u738b\u5927\u4eba\u548c\u91d1\u5c0f\u7709\uff0c\u5c0f\u96c5\u3001\u60dc\u7389\uff0c\u4f60\u4eec\u53bb\u5e2e\u540e\u95e8\u5916\u7684\u5f13\u5f29\u624b\u4eec\uff0c\u738b \u575a\u3001\u4f55\u5927\u5149\uff0c\u5feb\u5c06\u5e97\u5802\u91cc\u6b7b\u53bb\u5f13\u5f29\u624b\u4eec\u8eab\u4e0a\u7684\u8fde\u5f29\u94c1\u7bad\u6536\u96c6\u4e00\u4e9b\uff0c\u5e26\u5728\u8eab\u4e0a\uff01\u201d \u3000\u3000\u738b\u575a\u3001\u4f55\u5927\u5149\u8f6c\u8eab\u53c8\u51b2\u5165\u4e86\u5e97\u5802\u3002 \u3000\u3000\u5c0f\u96c5\u548c\u60dc\u7389\u8d76\u5230\u7684\u6b63\u53ca\u65f6\uff0c\u53ea\u89c1\u540e\u95e8\u5916\uff0c\u56db\u540d\u9ed1\u8863\u5251\u624b\u6b63\u6325\u5251\u780d\u6740\u90a3\u4e9b\u5c06\u5f29\u7bad\u5df2\u7ecf\u65bd\u653e \u51fa\u53bb\u7684\u5f13\u5f29\u624b\u4eec\u3002 \u3000\u3000\u5f13\u5f29\u624b\u4eec\u867d\u7136\u4e5f\u62bd\u51fa\u4e86\u8170\u95f4\u7684\u4f69\u5200\uff0c\u62fc\u547d\u62b5\u6297\uff0c\u4f46\u4ed6\u4eec\u53c8\u600e\u4f1a\u662f\u9ed1\u8863\u5251\u624b\u7684\u5bf9\u624b\uff0c\u773c\u770b \u7740\u4e00\u4e2a\u4e2a\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5c0f\u96c5\u548c\u60dc\u7389\u6325\u5251\u51b2\u4e0a\uff0c\u5404\u81ea\u4ee5\u4e00\u654c\u4e8c\u3002 \u3000\u3000\u56db\u540d\u9ed1\u8863\u5251\u624b\u867d\u7136\u5f3a\u608d\uff0c\u4f46\u4ed6\u4eec\u7684\u5251\u672f\u6bd4\u8d77\u5c0f\u96c5\u548c\u60dc\u7389\u6765\uff0c\u90fd\u53c8\u76f8\u5f62\u89c1\u7ecc\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u5251\u5149\u8fde\u95ea\uff0c\u5728\u60dc\u7389\u548c\u5c0f\u96c5\u7684\u6e05\u53f1\u58f0\u4e2d\uff0c\u56db\u540d\u9ed1\u8863\u5251\u624b\u80f8\u524d\u9c9c\u8840\u55b7\u6d8c\uff0c\u60e8\u53eb\u7740\u6454\u5012\u5728 \u5730\u4e0a\u3002 \u3000\u3000\u5b88\u536b\u540e\u95e8\u7684\u4e8c\u5341\u4e94\u540d\u5f13\u5f29\u624b\uff0c\u53ea\u5269\u4e0b\u4e86\u4e94\u4e2a\u4eba\u3002 \u3000\u3000\u4f46\u662f\uff0c\u8fdb\u653b\u540e\u95e8\u7684\u5341\u540d\u9ed1\u8863\u5251\u624b\u4e5f\u88ab\u4ed6\u4eec\u5c04\u51fa\u7684\u8fde\u5f29\u94c1\u7bad\u5c04\u6740\u4e86\u516d\u4eba\u3002 \u3000\u3000\u518d\u8bf4\u524d\u95e8\u5916\u7684\u5f62\u52bf\u3002 \u3000\u3000\u675c\u671b\u6708\u548c\u5c0f\u6587\u4e00\u52a0\u5165\u6218\u6597\uff0c\u5f62\u52bf\u5c31\u53d1\u751f\u4e86\u9006\u8f6c\u3002 \u3000\u3000\u90a3\u4e9b\u8eab\u7a7f\u5404\u8272\u8863\u886b\uff0c\u90fd\u6234\u7740\u4eba\u76ae\u9762\u5177\u3001\u626e\u4f5c\u98df\u5ba2\u7684\u6740\u624b\u4eec\u5728\u4e00\u4e2a\u7167\u9762\u95f4\uff0c\u5df2\u6709\u4e03\u4eba\u5728\u675c \u671b\u6708\u548c\u5c0f\u6587\u5251\u4e0b\u4e27\u751f\u3002 \u3000\u3000\u5c91\u5578\u864e\u3001\u4e8e\u627f\u5fd7\u3001\u5434\u94c1\u5cf0\u7684\u6797\u4e0d\u51e1\u4e5f\u5404\u81ea\u6740\u4e86\u4e00\u4eba\u3002 \u3000\u3000\u7a81\u7136\uff0c\u6740\u624b\u4eec\u7eb7\u7eb7\u64a4\u51fa\u4e86\u6218\u8f9f\uff0c\u5411\u540e\u5012\u9000\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\u548c\u5c0f\u6587\u53d1\u8db3\u5c31\u8ffd\uff0c\u7a0b\u5c0f\u8776\u559d\u9053\uff1a\u201c\u4e14\u6162\uff0c\u4e0d\u8981\u8ffd\uff01\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u8ffd\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u4eec\u60f3\u4e00\u60f3\uff0c\u5728\u524d\u4e24\u6b21\u88ad\u51fb\u4e2d\uff0c\u6740\u624b\u4eec\u7684\u5b9e\u529b\u660e\u663e\u4e0d\u5982\u8fd9\u4e00\u6b21\uff0c\u5374\u662f\u6b7b\u6218 \u4e0d\u9000\uff0c\u8fd9\u4e00\u6b21\u4ed6\u4eec\u5176\u5b9e\u5e76\u672a\u4e00\u8d25\u6d82\u5730\uff0c\u4e3a\u4ec0\u4e48\u53cd\u800c\u8981\u9000\u8d70\u5462\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u7684\u610f\u601d\u662f\u8bf4\uff0c\u4ed6\u4eec\u8bbe\u6709\u57cb\u4f0f\uff0c\u662f\u60f3\u628a\u6211\u4eec\u8bf1\u5165\u4f0f\u51fb\u5708\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u800c\u4e14\uff0c\u5982\u679c\u6211\u731c\u7684\u4e0d\u9519\uff0c\u6307\u6325\u57cb\u4f0f\u7684\u4eba\u4e00\u5b9a\u5c31\u662f\u90a3\u4e2a\u68a6\u5e7b\u4e4b\u5200\uff01\u201d \u3000\u3000\u201c\u54c8\u54c8\u54c8\u54c8\uff01\u201d \u3000\u3000\u4e00\u9635\u72c2\u7b11\u58f0\u81ea\u8f6c\u89d2\u5904\u4f20\u6765\u3002 \u3000\u3000\u4f17\u4eba\u62ac\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u9ed1\u8863\u8499\u9762\u4eba\u795e\u95f2\u6c14\u5b9a\u5730\u81ea\u8f6c\u89d2\u5904\u8d70\u4e86\u51fa\u6765\uff0c\u4ed6\u7684\u8eab\u540e\uff0c\u8ddf\u7740\u6570\u5341\u540d \u91d1\u5200\u7ea7\u6740\u624b\uff0c\u767e\u591a\u540d\u8eab\u7740\u6742\u8272\u8863\u886b\u7684\u8499\u9762\u4eba\uff0c\u8fd8\u6709\u8fd1\u516d\u5341\u540d\u624b\u6267\u957f\u5251\u7684\u9ed1\u8863\u5251\u624b\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u4f60\u53ea\u731c\u5bf9\u4e86\u4e00\u534a\uff0c\u6307\u6325\u57cb\u4f0f\u7684\u4eba\u7684\u786e\u662f\u6211\u68a6\u5e7b\u4e4b\u5200\uff0c\u4f46\u7a0b \u603b\u6355\u5934\u6ca1\u6709\u731c\u5230\u7684\u662f\uff0c\u4f60\u4eec\u65e9\u5df2\u8fdb\u4e86\u6211\u4eec\u7684\u4f0f\u51fb\u5708\uff0c\u56e0\u4e3a\u6574\u4e2a\u8427\u4f2f\u9547\u90fd\u5df2\u5728\u8001\u592b\u7684\u638c\u63e1\u4e4b\u4e2d\uff0c \u7a0b\u603b\u6355\u5934\uff0c\u738b\u5927\u4eba\uff0c\u4f60\u4eec\u5b8c\u4e86\uff0c\u8fd8\u662f\u4e56\u4e56\u5730\u675f\u624b\u5c31\u64d2\u5427\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u9759\u5730\u9053\uff1a\u201c\u5c31\u51ed\u4f60\u548c\u4f60\u8eab\u540e\u8fd9\u4e8c\u767e\u591a\u53f7\u4eba\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4e0d\u591f\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\uff0c\u4e0d\u591f\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\u7684\u53e3\u6c14\u5b9e\u5728\u592a\u72c2\u4e86\u4e00\u70b9\u5427\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5c31\u7b97\u6211\u5f88\u72c2\uff0c\u4e5f\u662f\u56e0\u4e3a\u6211\u6709\u72c2\u7684\u5b9e\u529b\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u53c8\u662f\u4e00\u9635\u72c2\u7b11\uff0c\u9053\uff1a\u201c\u5c31\u51ed\u4f60\u548c\u4f60\u624b\u4e0b\u8fd9\u5341\u51e0\u4e8c\u5341\u53f7\u4eba\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7a81\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff0c\u201c\u5728\u626c\u5dde\u5e9c\u8859\u91cc\uff0c\u6211\u4eec\u4e5f\u53ea\u6709\u8fd9\u51e0\u53f7\u4eba\uff0c\u4f60\u4eec\u5c61\u6b21\u8fdb\u653b\uff0c\u4e0d\u4e5f \u6ca1\u6709\u5f97\u624b\u5417\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u6211\u5f88\u4f69\u670d\u4f60\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\u5462\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u5728\u73b0\u5728\u8fd9\u79cd\u60c5\u51b5\u4e4b\u4e0b\uff0c\u4f60\u8fd8\u8bf4\u5f97\u51fa\u5927\u8bdd\u6765\uff0c\u771f\u662f\u4e0d\u7b80\u5355\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u7b11\u4e00\u58f0\uff0c\u8f6c\u8fc7\u5934\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u738b\u575a\uff0c\u4f55\u5927\u5149\uff0c\u4f60\u4eec\u6536\u96c6\u4e86\u591a\u5c11\u8fde\u5f29\u94c1\u7bad\uff1f\u201d \u3000\u3000\u738b\u575a\uff0c\u4f55\u5927\u5149\u9053\uff1a\u201c\u4e00\u5171\u4e09\u5341\u5323\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4f4e\u58f0\u9053\uff1a\u201c\u597d\uff0c\u8ba9\u6240\u6709\u7684\u4eba\u90fd\u62ff\u4e0a\uff0c\u4e00\u4eba\u4e24\u5323\u94c1\u7bad\uff0c\u542c\u6211\u53f7\u4ee4\uff0c\u5411\u90a3\u8fb9\u9f50\u5c04\uff01\u201d \u3000\u3000\u738b\u575a\u3001\u4f55\u5927\u5149\u5206\u5934\u5c06\u7bad\u5323\u53d1\u5230\u5404\u4eba\u624b\u4e2d\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u5546\u91cf\u597d\u4e86\u5417\uff1f\u5e94\u505a\u51b3\u5b9a\u4e86\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4ec0\u4e48\u51b3\u5b9a\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u662f\u81ea\u89c9\u4e00\u70b9\uff0c\u675f\u624b\u5c31\u64d2\uff0c\u8fd8\u662f\u60f3\u8ba9\u8001\u592b\u7565\u8d39\u4e0a\u4e00\u70b9\u529b\u6c14\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u522b\u505a\u767d\u65e5\u68a6\u4e86\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u51b7\u58f0\u9053\uff1a\u201c\u6211\u770b\u4f60\u662f\u4e0d\u5230\u9ec4\u6cb3\u4e0d\u6b7b\u5fc3\u4e86\uff01\u7ed9\u6211\u4e0a\uff0c\u5250\u4e86\u4ed6\u4eec\uff01\u201d \u3000\u3000\u4ed6\u8eab\u540e\u7684\u6740\u624b\u4eec\u66b4\u559d\u4e00\u58f0\uff0c\u4e00\u8d77\u5411\u524d\u6d8c\u4e0a\u6765\uff0c \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u4fdd\u62a4\u738b\u5927\u4eba\uff0c\u60dc\u7389\u4fdd\u62a4\u91d1\u5c0f\u7709\uff0c\u738b\u575a\u3001\u4f55\u5927\u5149\u3001\u6797\u4e0d\u51e1\u4f60\u4eec\u4e09\u4eba\u6253\u5934\u9635\uff0c \u56db\u5927\u6355\u5934\u548c\u5c0f\u6587\u6bbf\u540e\uff0c\u6211\u4eec\u5f80\u90a3\u8fb9\u51b2\uff01\u201d \u3000\u3000\u5979\u6307\u7684\u662f\u4e0e\u6740\u624b\u4eec\u963b\u5f53\u7684\u53cd\u5411\u7684\u4e00\u6761\u8857\u9053\u3002 \u3000\u3000\u521a\u521a\u51b2\u5230\u4e0d\u8fc7\u4e09\u5341\u6765\u6b65\uff0c\u53ea\u89c1\u90a3\u6761\u539f\u672c\u662f\u7a7a\u65e0\u4e00\u4eba\u7684\u8857\u9053\u4e0a\uff0c\u7a81\u7136\u4e5f\u6d8c\u51fa\u4e86\u4e00\u6279\u4eba\u3002 \u3000\u3000\u9886\u5934\u7684\uff0c\u6b63\u662f\u4e0d\u8001\u4e66\u751f\uff01 \u3000\u3000\u4e0d\u8001\u4e66\u751f\u7684\u8eab\u540e\uff0c\u8ddf\u7740\u7684\u9ad8\u7ea7\u6740\u624b\u4e5f\u4e0d\u4e0b\u4e00\u767e\u4eba\uff01 \u3000\u3000\u800c\u4e14\u4ed6\u8eab\u8fb9\u7684\u9ed1\u8863\u5251\u624b\u5360\u4e86\u6740\u624b\u4eec\u4eba\u6570\u7684\u4e00\u5927\u534a\uff0c\u5927\u7ea6\u6709\u4e03\u5341\u540d\uff01 \u3000\u3000\u4e0d\u7ba1\u600e\u6837\u3002\u4e0d\u8001\u4e66\u751f\u6bd4\u9ed1\u8863\u8499\u9762\u4eba\u8fd8\u662f\u8981\u597d\u5bf9\u4ed8\u4e00\u4e9b\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u65ad\u559d\u9053\uff1a\u201c\u653e\u7bad\uff01\u201d \u3000\u3000\u4e09\u5341\u5323\u94c1\u7bad\uff0c\u5c04\u51fa\u4e86\u75be\u5982\u72c2\u98ce\u3001\u5bc6\u5982\u66b4\u96e8\u7684\u4e09\u767e\u679d\u9510\u5229\u7684\u94c1\u7bad\u3002 \u3000\u3000\u6740\u624b\u4eec\u987f\u65f6\u624b\u811a\u5927\u4e71\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u9635\u7bad\u96e8\u4e2d\uff0c\u4ed6\u4eec\u81f3\u5c11\u5012\u4e0b\u4e86\u4e8c\u5341\u4eba\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u675c\u53f8\u4e3b\u3001\u5c0f\u6587\uff0c\u53cc\u5251\u5408\u74a7\uff0c\u683c\u6740\u547c\u5ef6\u8fdc\uff01\u201d \u3000\u3000\u4e0d\u8001\u4e66\u751f\u6700\u6015\u7684\u4fbf\u662f\u5c0f\u6587\u7684\u201c\u5251\u6d77\u6d74\u9b42\u201d\uff0c\u4e00\u542c\u6b64\u97f3\uff0c\u4fbf\u6325\u624b\u9053\uff1a\u201c\u6563\u5f00\uff0c\u8ba9\u4ed6\u4eec\u8fc7 \u53bb\uff01\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\u53ef\u5927\u51fa\u7a0b\u5c0f\u8776\u7684\u610f\u6599\uff01 \u3000\u3000\u53ea\u542c\u540e\u9762\u9ed1\u8863\u8499\u9762\u4eba\u53eb\u9053\uff1a\u201c\u547c\u5ef6\u8868\u5f1f\uff0c\u4e0d\u80fd\u653e\uff0c\u5feb\u7ec4\u7ec7\u4eba\u624b\uff0c\u622a\u4f4f\u4ed6\u4eec\uff01\u201d \u3000\u3000\u4f46\u547c\u5ef6\u8fdc\u7adf\u662f\u5145\u8033\u4e0d\u95fb\uff0c\u5e26\u7740\u624b\u4e0b\u7684\u6740\u624b\u4eec\uff0c\u95ea\u8eab\u8fdb\u4e86\u8857\u9053\u4e24\u65c1\u7684\u6c11\u5c45\u540e\u9762\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7387\u4f17\u5f80\u8857\u7684\u90a3\u4e00\u5934\u76f4\u51b2\u3002 \u3000\u3000\u53c8\u51b2\u51fa\u4e94\u5341\u6765\u6b65\uff0c\u5979\u7a81\u7136\u505c\u4e86\u4e0b\u6765\uff01 \u3000\u3000\u5979\u611f\u89c9\u5230\u8fd9\u662f\u4e2d\u8bc8\uff01 \u3000\u3000\u4e0d\u8001\u4e66\u751f\uff0c\u600e\u4e48\u4f1a\u62d2\u4e0d\u6267\u884c\u9ed1\u8863\u8499\u9762\u4eba\u7684\u547d\u4ee4\u5462\uff1f \u3000\u3000\u8981\u77e5\u9053\uff0c\u9ed1\u8863\u8499\u9762\u4eba\u7edd\u5bf9\u662f\u6740\u624b\u96c6\u56e2\u7684\u9886\u8896\u4eba\u7269\u5440\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u60f3\u5230\u8fd9\u91cc\uff0c\u7acb\u523b\u65ad\u559d\u9053\uff1a\u201c\u5927\u5bb6\u4e14\u6162\uff01\u201d \u3000\u3000\u4f17\u4eba\u521a\u505c\u4e0b\uff0c\u9ed1\u8863\u8499\u9762\u4eba\u548c\u4e0d\u8001\u4e66\u751f\u7684\u5927\u7b11\u58f0\u53c8\u54cd\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8857\u9053\u4e24\u8fb9\u7684\u623f\u9876\u4e0a\uff0c\u7a81\u7136\u51fa\u73b0\u4e86\u4e09\u767e\u540d\u5f13\u7bad\u624b\u3002 \u3000\u3000\u5f13\u7bad\u624b\u5404\u5404\u5f2f\u5f13\u642d\u7bad\u3002 \u3000\u3000\u5f13\u5df2\u62c9\u6ee1\uff0c\u7bad\u5934\u5168\u90fd\u5bf9\u51c6\u4e86\u8857\u4e2d\u5fc3\u8fd9\u4e00\u7fa4\u4eba\uff01 \u3000\u3000\u5728\u6b63\u5348\u5f3a\u70c8\u7684\u9633\u5149\u4e0b\uff0c\u6240\u6709\u7684\u7bad\u5934\u90fd\u95ea\u52a8\u7740\u6697\u84dd\u8272\u7684\u5149\u8292\u3002 \u3000\u3000\u663e\u7136\uff0c\u7bad\u5934\u4e0a\u90fd\u6d82\u6709\u5267\u6bd2\uff01 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u8dd1\u554a\uff0c\u51b2\u554a\uff0c\u6740\u554a\uff01\u600e\u4e48\u90fd\u4e0d\u52a8\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53cc\u7709\u7d27\u76b1\uff0c\u989d\u5934\u4e0a\u5df2\u66b4\u51fa\u4e86\u51b7\u6c57\uff01 \u3000\u3000\u663e\u7136\uff0c\u5979\u7684\u5fc3\u60c5\u5341\u5206\u7d27\u5f20\uff01 \u3000\u3000\u675c\u671b\u6708\u548c\u5434\u94c1\u5cf0\u56db\u4eba\u4e0d\u89c9\u5fc3\u4e2d\u4e00\u5bd2\uff01 \u3000\u3000\u8ddf\u968f\u4e86\u7a0b\u5c0f\u8776\u591a\u5e74\u4e86\uff0c\u4ed6\u4eec\u8fd8\u4ece\u6ca1\u89c1\u8fc7\u603b\u6355\u5934\u6709\u8fc7\u5982\u6b64\u4e3e\u6b62\u5931\u63aa\u7684\u65f6\u5019\uff01 \u3000\u3000\u96be\u9053\u8bf4\uff0c\u4ed6\u4eec\u771f\u7684\u5df2\u7ecf\u9677\u5165\u4e86\u7edd\u5883\uff1f \u3000\u3000\u7a81\u7136\uff0c\u675c\u671b\u6708\u5fc3\u4e2d\u7075\u5149\u4e00\u95ea\u3002 \u3000\u3000\u4ed6\u60f3\u8d77\u4e86\u7a0b\u5c0f\u8776\u5728\u8fd0\u6cb3\u8fb9\u653e\u7684\u90a3\u53ea\u4fe1\u53f7\u70df\u82b1\u3002 \u3000\u3000\u6309\u7406\u8bf4\uff0c\u5e94\u8be5\u6709\u4eba\u6765\u6551\u63f4\u624d\u5bf9\uff01 \u3000\u3000\u53ef\u4e3a\u4ec0\u4e48\u6ca1\u6709\u4eba\u51fa\u73b0\u5462\uff1f \u3000\u3000\u96be\u9053\u7a0b\u5c0f\u8776\u57cb\u4f0f\u7684\u63f4\u519b\uff0c\u65e9\u5df2\u88ab\u6740\u624b\u96c6\u56e2\u89e3\u51b3\u6389\u4e86\uff1f \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u7a0b\u603b\u6355\u5934\uff0c\u738b\u5927\u4eba\uff0c\u600e\u4e48\u6837\uff0c\u60f3\u597d\u4e86\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u62ac\u5934\u5f80\u4e24\u8fb9\u770b\u4e86\u770b\uff0c\u9053\uff1a\u201c\u770b\u6837\u5b50\uff0c\u5982\u679c\u6211\u4eec\u4e0d\u675f\u624b\u5c31\u64d2\uff0c\u4f60\u5c31\u8981\u4e0b\u4ee4\u653e\u7bad\uff0c\u628a \u6211\u4eec\u5c04\u6210\u523a\u732c\u4e86\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0b\u4ee4\u653e\u7bad\u5427\uff01\xb7\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u4f3c\u4e4e\u6014\u4e86\u6014\uff0c\u6b63\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5374\u89c1\u6b63\u5728\u5c4b\u9876\u5f2f\u5f13\u642d\u7bad\u7684\u7bad\u624b\u4e00\u4e0b\u5b50\u5012\u4e0b\u4e86\u4e00\u5927 \u534a\u3002 \u3000\u3000\u5012\u4e0b\u7684\u5f13\u7bad\u624b\u7684\u80cc\u4e0a\uff0c\u5374\u9489\u7740\u4e00\u679d\u6216\u51e0\u679d\u957f\u7bad\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u957f\u7b11\u4e00\u58f0\uff0c\u6325\u5251\u51b2\uff0c\u53eb\u9053\uff1a\u201c\u5927\u4f19\u5f80\u5317\u51b2\uff0c\u51b2\u51fa\u8fd9\u4e2a\u9547\u5b50\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u6c14\u72e0\u72e0\u5730\u4e00\u8dfa\u811a\uff0c\u9053\uff1a\u201c\u5f1f\u5144\u4eec\uff0c\u5feb\u8ffd\uff01\u201d \u3000\u3000\u4ed6\u4e0e\u4e0d\u8001\u4e66\u751f\u547c\u5ef6\u8fdc\u5408\u5728\u4e00\u5904\uff0c\u8db3\u6709\u8fd1\u4e09\u767e\u4eba\uff0c\u5927\u547c\u5c0f\u53eb\u5730\u7d27\u8feb\u7a0b\u5c0f\u8776\u4e00\u884c\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7b49\u4eba\u51b2\u51fa\u8427\u4f2f\u9547\u5317\u95e8\uff0c\u987f\u65f6\u677e\u4e86\u4e00\u53e3\u6c14\uff01 \u3000\u3000\u4f17\u4eba\u4e5f\u90fd\u660e\u767d\u5c4b\u9876\u4e0a\u90a3\u4e9b\u5f13\u7bad\u624b\u662f\u88ab\u4ec0\u4e48\u4eba\u6740\u6b7b\u7684\u4e86\u3002 \u3000\u3000\u6740\u6b7b\u90a3\u4e9b\u4eba\u7684\uff0c\u662f\u9a6c\u957f\u5c71\u624b\u4e0b\u7684\u4e94\u767e\u540d\u6c34\u5e08\u7cbe\u9510\uff01 \u3000\u3000\u539f\u6765\uff0c\u7a0b\u5c0f\u8776\u653e\u7684\u90a3\u53ea\u4fe1\u53f7\uff0c\u662f\u901a\u77e5\u9a6c\u957f\u5c71\u624b\u4e0b\u7684\u6c34\u5e08\u6765\u6551\u63f4\u3002 \u3000\u3000\u4f46\u8fd9\u4e94\u767e\u540d\u6c34\u5e08\u7cbe\u9510\u600e\u4f1a\u51fa\u73b0\u5728\u8427\u4f2f\u9547\u9644\u8fd1\u5462\uff1f \u3000\u3000\u675c\u671b\u6708\u4e0d\u7981\u95ee\u7a0b\u5c0f\u8776\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u662f\u4e0d\u662f\u65e9\u4e0e\u9a6c\u5927\u4eba\u7ea6\u597d\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u8bf7\u9a6c\u5927\u4eba\u62e8\u51fa\u4e86\u4e94\u767e\u7cbe\u9510\u6c34\u5e08\uff0c\u6cbf\u8fd0\u6cb3\u5317\u4e0a\uff0c\u6697\u4e2d\u5c3e\u968f\u54b1\u4eec\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u201d \u3000\u3000\u4e8c\u4eba\u6b63\u8bf4\u7740\u8bf4\uff0c\u56db\u540d\u6c34\u5e08\u5c06\u9886\u9a71\u9a6c\u8fce\u4e86\u4e0a\u6765\uff0c\u4e00\u9f50\u5728\u9a6c\u4e0a\u8eac\u8eab\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u738b\u5927\u4eba\uff0c \u672b\u5c06\u7b49\u63a5\u5230\u4fe1\u53f7\uff0c\u4fbf\u8d76\u6765\u4e86\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u4eec\u6765\u7684\u6b63\u597d\uff0c\u6b63\u53ca\u65f6\uff0c\u56db\u4f4d\u5c06\u519b\uff0c\u6740\u624b\u4eec\u9a6c\u4e0a\u5c31\u8981\u51fa\u9547\u6765\u4e86\uff0c\u4f60\u4eec\u51c6\u5907 \u8fce\u654c\u5427\uff01\u201d \u3000\u3000\u56db\u540d\u5c06\u9886\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u53ea\u89c1\u5728\u56db\u4eba\u53f7\u4ee4\u58f0\u4e2d\uff0c\u4e94\u767e\u7cbe\u9510\u6c34\u5e08\u5217\u6210\u5341\u961f\u3002 \u3000\u3000\u58eb\u5175\u4eec\u5c06\u624b\u4e2d\u7684\u957f\u67aa\u6a2a\u6401\u5728\u9a6c\u80cc\u4e0a\uff0c\u4e00\u9f50\u4e3e\u8d77\u4e86\u5f13\u7bad\u3002 \u3000\u3000\u7bad\u4e0a\u5f26\uff01 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u76f4\u5230\u73b0\u5728\uff0c\u6740\u624b\u4eec\u7adf\u8fd8\u6ca1\u6709\u8ffd\u51fa\u6765\u3002 \u3000\u3000\u8d81\u8fd9\u4e2a\u7a7a\u6321\uff0c\u7a0b\u5c0f\u8776\u53c8\u5306\u5306\u6e05\u70b9\u4e86\u4e00\u4e0b\u90e8\u4e0b\u7684\u635f\u5931\u3002 \u3000\u3000\u635f\u5931\u5b9e\u5728\u662f\u592a\u60e8\u91cd\u4e86\uff01 \u3000\u3000\u5979\u60f3\u4e86\u60f3\uff0c\u4f38\u624b\u5165\u6000\uff0c\u53c8\u638f\u51fa\u4e00\u679d\u77ed\u77ed\u7684\u7af9\u7ba1\u3002 \u3000\u3000\u4e00\u626c\u624b\uff0c\u7af9\u7ba1\u98de\u4e0a\u4e86\u5929\u7a7a\u3002 \u3000\u3000\u8fd9\u6b21\uff0c\u7af9\u7ba1\u7206\u51fa\u7684\u662f\u7c89\u7ea2\u8272\u7684\u70df\u96fe\u3002 \u3000\u3000\u201c\u54c8\u54c8\uff01\u201d \u3000\u3000\u4e00\u9635\u5927\u7b11\u58f0\uff0c\u53ea\u89c1\u4e0d\u8001\u4e66\u751f\u548c\u9ed1\u8863\u8499\u9762\u4eba\u5df2\u5927\u6b65\u8d70\u51fa\u4e86\u8427\u4f2f\u9547\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u8d70\u51fa\u9547\u5b50\uff0c\u5c31\u6ca1\u6709\u518d\u5411\u524d\u903c\u8fd1\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u8eab\u540e\uff0c\u6740\u624b\u4eec\u6b63\u6e90\u6e90\u4e0d\u65ad\u5730\u6d8c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6740\u624b\u4eec\u4e5f\u6392\u6210\u4e86\u51e0\u4e2a\u65b9\u9635\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u8fd9\u6b21\u6d8c\u51fa\u7684\uff0c\u4ee5\u7070\u8863\u6740\u624b\u548c\u91d1\u5200\u7ea7\u7684\u9ed1\u8863\u6740\u624b\u4e3a\u591a\u3002 \u3000\u3000\u7070\u8863\u6740\u624b\u6392\u6210\u4e86\u4e09\u4e2a\u65b9\u9635\uff0c\u5217\u5728\u6700\u524d\u9762\u3002 \u3000\u3000\u675c\u671b\u6708\u7b49\u56db\u5927\u6355\u5934\u4e0d\u7981\u5012\u62bd\u4e86\u4e00\u53e3\u51b7\u6c14\u3002 \u3000\u3000\u6740\u624b\u96c6\u56e2\u7684\u5b9e\u529b\u7adf\u662f\u5982\u6b64\u4e4b\u5f3a\uff0c\u5b9e\u5728\u5927\u51fa\u4ed6\u4eec\u7684\u610f\u6599\u3002 \u3000\u3000\u4ed6\u4eec\u4e00\u76f4\u4ee5\u4e3a\uff0c\u5728\u4e0a\u4e24\u6b21\u7a81\u88ad\u4e2d\u88ab\u4ed6\u4eec\u6740\u6b7b\u7684\u7070\u8863\u6740\u624b\u5df2\u7ecf\u662f\u6740\u624b\u96c6\u56e2\u4e2d\u6240\u6709\u7684\u57fa\u7840\u529b \u91cf\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u521a\u521a\u5728\u8427\u4f2f\u9547\u4e2d\uff0c\u4ed6\u4eec\u6240\u89c1\u5230\u7684\u7070\u8863\u6740\u624b\uff0c\u4eba\u6570\u5df2\u7ecf\u8fdc\u8fdc\u5c11\u4e8e\u91d1\u5200\u7ea7\u7684\u9ad8\u7ea7\u6740\u624b\u548c \u90a3\u7fa4\u8eab\u7740\u6742\u8272\u8863\u886b\u7684\u8499\u9762\u4eba\u3002 \u3000\u3000\u5c31\u8fde\u4e0d\u8001\u4e66\u751f\u624b\u4e0b\u90a3\u4e9b\u6b66\u529f\u5251\u672f\u4e00\u6d41\u7684\u9ed1\u8863\u5251\u624b\uff0c\u4eba\u6570\u4e5f\u6ca1\u6709\u7070\u8863\u6740\u624b\u591a\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4e8b\u5b9e\u5c31\u5728\u773c\u524d\u3002 \u3000\u3000\u8fd9\u8bf4\u660e\uff0c\u4ed6\u4eec\u4f4e\u4f30\u4e86\u8840\u624b\u65b9\u8f6e\u7684\u80fd\u529b\u3002 \u3000\u3000\u770b\u6765\uff0c\u65b9\u8f6e\u7684\u6740\u624b\u8bad\u7ec3\u57fa\u5730\u51b3\u4e0d\u662f\u5e26\u5c71\u3001\u5929\u53f0\u4e24\u5904\u3002 \u3000\u3000\u6700\u4ee4\u4ed6\u4eec\u62c5\u5fc3\u7684\u662f\uff0c\u8840\u624b\u65b9\u8f6e\u672c\u4eba\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u73b0\u8eab\u3002 \u3000\u3000\u53ef\u4ee5\u60f3\u5230\uff0c\u65b9\u8f6e\u7684\u8eab\u8fb9\uff0c\u4e00\u5b9a\u4f1a\u5e26\u7740\u4eba\u6570\u4f17\u591a\u7684\u9ad8\u7ea7\u6740\u624b\uff01 \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u521a\u624d\u653e\u7684\u4fe1\u53f7\uff0c\u662f\u4e0d\u662f\u901a\u77e5\u4e8c\u6279\u63f4\u519b\u53ef\u4ee5\u9732\u9762\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\uff0c\u672c\u6765\u6211\u4e0d\u6253\u7b97\u8fd9\u4e48\u65e9\u5c31\u5c3d\u51fa\u5b9e\u529b\uff0c\u4f46\u5b9e\u5728\u6ca1\u60f3\u5230\u4ed6\u4eec\u4f1a\u9009\u62e9\u8427\u4f2f\u9547 \u4e3a\u51b3\u6218\u4e4b\u5730\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u603b\u6355\u5934\u539f\u6253\u7b97\u5728\u4ec0\u4e48\u5730\u65b9\u51b3\u6218\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5728\u524d\u9762\u7ea6\u4e8c\u5341\u91cc\u5904\u4e00\u5904\u8352\u91ce\u4e4b\u4e2d\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u6740\u624b\u4eec\u65e9\u5df2\u51c6\u5907\u597d\u5728\u8427\u4f2f\u9547\u51b3\u6218\uff0c\u5219\u5df2\u5360\u5730\u5229\uff0c\u6211\u4eec\u7edd\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u8f7b\u6613\u5982 \u613f\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u90a3\u4f60\u8bf4\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u5434\u94c1\u5cf0\u9053\uff1a\u201c\u8d70\uff01\u201d\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5c06\u4ed6\u4eec\u5f15\u5230\u90a3\u5904\u8352\u91ce\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u95ee\u9898\u662f\u6c34\u5e08\u7cbe\u9510\u5df2\u7ecf\u8d76\u6765\u4e86\uff0c\u5982\u679c\u73b0\u5728\u6211\u8bf4\u8981\u8d70\uff0c\u4e00\u662f\u4f1a\u635f\u53ca\u4ed6\u4eec\u7684\u58eb\u6c14\uff0c \u5175\u8d25\u5982\u5c71\u5012\uff0c\u58eb\u5175\u4eec\u7684\u5fc3\u7406\u7d20\u8d28\u53ef\u4e0d\u5982\u6211\u4eec\u8fd9\u4e9b\u4eba\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u4f4e\u58f0\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6211\u521a\u624d\u770b\u4e86\u4e00\u4e0b\uff0c\u6740\u624b\u4eec\u7684\u4eba\u6570\u6bd4\u6211\u4eec\u8fd8\u8981\u591a\uff0c\u5927\u6982\u6709\u516d\u767e \u4f59\u4eba\uff0c\u6211\u8bb0\u5f97\u94c1\u7fce\u66fe\u5bf9\u6211\u8bf4\u8fc7\uff0c\u5982\u679c\u6c34\u5e08\u4e0e\u6b66\u6797\u4eba\u58eb\u5bf9\u4ed8\uff0c\u5c31\u7b97\u6c34\u5e08\u4eba\u6570\u591a\u51fa\u516d\u4e03\u500d\uff0c\u4e5f\u4e0d \u4f1a\u80dc\u3002\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u94c1\u7fce\u8bf4\u7684\u7684\u786e\u4e0d\u9519\uff0c\u4f46\u73b0\u5728\u9664\u4e86\u6c34\u5e08\u4e4b\u5916\uff0c\u4e0d\u662f\u8fd8\u6709\u6211\u4eec\u5417\uff1f \u6211\u4eec\u53ef\u4ee5\u5c06\u9ad8\u7ea7\u6740\u624b\u7f20\u4f4f\uff0c\u800c\u8ba9\u6c34\u5e08\u4eec\u4e0e\u4f4e\u7ea7\u6740\u624b\u5bf9\u9635\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6c34\u5e08\u4eec\u73b0\u5728\u8d76\u6765\u4e86\uff0c\u4e0d\u6253\u4e00\u4ed7\uff0c\u603b\u4f1a\u5fc3\u6c14\u96be\u5e73\uff0c\u518d\u8bf4\uff0c\u4ed6\u4eec\u90fd\u662f\u9a91\u5175\uff0c\u4f7f \u7684\u90fd\u662f\u957f\u67aa\uff0c\u501f\u52a9\u9a6c\u5339\u7684\u51b2\u529b\uff0c\u672a\u5fc5\u4e0d\u80fd\u4e0e\u6740\u624b\u4eec\u4e00\u6218\uff01\u201d \u3000\u3000\u5bf9\u9762\uff0c\u6740\u624b\u4eec\u7684\u65b9\u9635\u5df2\u5f00\u59cb\u7f13\u6162\u5411\u524d\u903c\u8fdb\uff01 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u539f\u6765\u8fd9\u5c31\u662f\u4f60\u7684\u540e\u63f4\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u4f60\u4e0d\u89c9\u5f97\u4ed6\u4eec\u7684\u529b\u91cf\u592a\u5355\u8584\u4e00\u70b9\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd8\u6ca1\u5f00\u6253\uff0c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u4eec\u7684\u5389\u5bb3\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u7b11\u58f0\u6717\u6717\uff0c\u9053\uff1a\u201c\u5c31\u8bf4\u9a6c\u957f\u5c71\u624b\u4e0b\u8fd9\u51e0\u767e\u5c0f\u5352\uff0c\u8fd8\u4e0d\u591f\u8001\u592b\u624b\u4e0b\u4e00\u76cf\u8336\u65f6\u5206 \u6740\u7684\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6765\u5427\uff01\u201d \u3000\u3000\u5979\u8df3\u4e0a\u4e00\u5339\u9a6c\uff0c\u5bf9\u5c0f\u96c5\u9053\uff1a\u201c\u6211\u8981\u6325\u4e00\u6325\u5175\u5668\u4e86\uff01\u201d \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4ece\u80cc\u4e0a\u89e3\u4e0b\u4e00\u4e2a\u6241\u957f\u7684\u5305\u88b1\u3002 \u3000\u3000\u5305\u88b1\u6253\u5f00\uff0c\u91cc\u9762\u7684\u4e03\u516b\u6bb5\u4e8c\u5c3a\u5de6\u53f3\u7684\u94c1\u7ba1\uff0c\u548c\u4e00\u622a\u4e00\u5c3a\u4e03\u5bf8\u957f\u7684\u539a\u539a\u7684\u67aa\u5934\uff01 \u3000\u3000\u5c0f\u96c5\u4e24\u624b\u4e0d\u505c\uff0c\u7728\u773c\u95ee\uff0c\u4e00\u6761\u4e08\u516b\u957f\uff0c\u9152\u676f\u53e3\u7c97\u7684\u957f\u67aa\u5df2\u7ecf\u63a5\u597d\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4f38\u624b\u63a5\u8fc7\u4e86\u94c1\u67aa\uff0c\u9053\uff1a\u201c\u675c\u53f8\u4e3b\u3001\u5c0f\u6587\uff0c\u4f60\u4eec\u548c\u60dc\u7389\u3001\u5c0f\u96c5\u4e00\u8d77\u4fdd\u62a4\u738b\u5927\u4eba\u548c\u91d1 \u5c0f\u7709\uff0c\u4e0d\u8001\u4e66\u751f\u548c\u68a6\u5e7b\u4e4b\u5200\u4e0d\u51fa\u624b\uff0c\u675c\u53f8\u4e3b\u548c\u5c0f\u6587\u4e5f\u4e0d\u5f97\u51fa\u624b\uff0c\u542c\u6e05\u695a\u4e86\u5417\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u548c\u5c0f\u6587\u9053\uff1a\u201c\u542c\u6e05\u695a\u4e86\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u770b\u89c1\u7a0b\u5c0f\u8776\u624b\u4e2d\u7684\u94c1\u67aa\uff0c\u4f3c\u4e4e\u5168\u8eab\u90fd\u9707\u52a8\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u90a3\u59d3\u5434\u7684\u8001\u4e0d\u6b7b\u662f \u4f60\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u4f60\u603b\u7b97\u770b\u51fa\u6765\u4e86\uff0c\u4ed6\u6b63\u662f\u5bb6\u5e08\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u597d\uff01\u6211\u4eca\u5929\u6b63\u597d\u770b\u4e00\u770b\u5434\u8001\u602a\u7684\u2018\u9738\u738b\u65e0\u654c\u2019\u67aa\u6cd5\u4f60\u5b66\u5230\u4e86\u51e0\u6210\u706b \u5019\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u8981\u771f\u6562\u63a5\u6211\u7684\u2018\u9738\u738b\u65e0\u654c\u2019\u67aa\uff0c\u4e0d\u5982\u6211\u4eec\u4e8c\u4eba\u5148\u5355\u6311\u4e00\u9635\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u5927\u624b\u4e00\u6325\uff0c\u559d\u9053\uff1a\u201c\u4e0a\uff01\u201d \u3000\u3000\u7070\u8863\u6740\u624b\u548c\u9ed1\u8863\u6740\u624b\u7ec4\u6210\u7684\u56db\u4e2a\u65b9\u9635\u7acb\u523b\u5feb\u901f\u5411\u524d\u51b2\u6765\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u653e\u7bad\uff01\u201d \u3000\u3000\u4e94\u767e\u679d\u957f\u7bad\u5939\u6742\u7740\u4e09\u767e\u679d\u8fde\u5f29\u94c1\u7bad\u50cf\u4e00\u9635\u51f6\u731b\u7684\u98de\u8757\uff0c\u5212\u8fc7\u4e86\u7a7a\u4e2d\uff0c\u5c04\u5411\u4e86\u6740\u624b\u4eec\uff01 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u53eb\u9053\uff1a\u201c\u7528\u5175\u5668\u62e8\u5f00\u98de\u7bad\uff01\u201d \u3000\u3000\u5df2\u7ecf\u8fdf\u4e86\uff01 \u3000\u3000\u4ed6\u521a\u5927\u53eb\u51fa\u58f0\uff0c\u51b2\u5728\u6700\u524d\u9762\u7684\u4e00\u4e2a\u7070\u8863\u6740\u624b\u7684\u65b9\u9635\u5df2\u88ab\u7bad\u96e8\u541e\u6ca1\u4e86\u4e00\u4e0b\u534a\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6c34\u5e08\u9a91\u5175\u8ddf\u6211\u51b2\uff0c\u8e4b\u4e71\u4ed6\u4eec\u7684\u9635\u5f62\u3002\u5434\u53f8\u4e3b\uff0c\u4f60\u4eec\u968f\u540e\u51b2\u4e0a\uff0c\u53bb\u627e\u9ad8\u7ea7\u6740 \u624b\uff01\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u4e00\u4e3e\u94c1\u67aa\uff0c\u7eb5\u8eab\u5411\u524d\u731b\u51b2\u3002 \u3000\u3000\u4e94\u767e\u540d\u6c34\u5e08\u94c1\u9a91\u5c31\u50cf\u4e00\u9635\u9ed1\u8272\u7684\u98ce\u66b4\u4e00\u822c\u7d27\u968f\u5728\u5979\u8eab\u540e\uff0c\u5411\u524d\u98ce\u5377\u800c\u4e0a\u3002 \u3000\u3000\u7070\u8863\u6740\u624b\u548c\u9ed1\u8863\u6740\u624b\u4eec\u7684\u65b9\u9635\u7acb\u523b\u88ab\u51b2\u6563\u4e86\uff01 \u3000\u3000\u58eb\u5175\u4eec\u65e9\u5df2\u653e\u4e0b\u5f13\u7bad\uff0c\u62ce\u8d77\u4e86\u957f\u67aa\uff01 \u3000\u3000\u51b2\u6740\u9635\u4e2d\uff0c\u4ed6\u4eec\u4fbf\u957f\u67aa\u9f50\u53d1\uff0c\u89c1\u4eba\u5c31\u731b\u524c\u72ec\u6253\uff01 \u3000\u3000\u6740\u624b\u4eec\u663e\u7136\u8fd8\u6ca1\u7ecf\u8fc7\u8fd9\u79cd\u9635\u4ed7\uff0c\u987f\u65f6\u9635\u811a\u5927\u4e71\u3002 \u3000\u3000\u7728\u773c\u95f4\uff0c\u5728\u6c34\u5e08\u58eb\u5175\u7684\u957f\u67aa\u4e4b\u4e0b\uff0c\u5df2\u6709\u4e0d\u4e0b\u4e03\u5341\u540d\u6740\u624b\u4e27\u751f\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u624b\u4e2d\u7684\u957f\u67aa\u5df2\u4f3c\u6210\u4e00\u6761\u94f6\u767d\u8272\u7684\u60ca\u9f99\u3002 \u3000\u3000\u8ddf\u7740\u95ea\u6210\u4e00\u9053\u9053\u94f6\u767d\u8272\u7684\u5f27\u7ebf\u3002 \u3000\u3000\u7ebf\u53c8\u7ed3\u6210\u4e86\u7f51\u3002 \u3000\u3000\u5fc5\u6740\u4e4b\u7f51\uff01 \u3000\u3000\u6bcf\u4e00\u9053\u94f6\u7ebf\u5212\u8fc7\uff0c\u5c31\u4f1a\u6709\u51e0\u540d\u6740\u624b\u60e8\u53eb\u5012\u5730\uff0c\u819b\u5f00\u8179\u88c2\uff0c\u8840\u5149\u56db\u6e85\uff01 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u4e00\u6328\u624b\uff0c\u4ed6\u8eab\u540e\u7684\u91d1\u5200\u7ea7\u9ad8\u7ea7\u6740\u624b\u548c\u9ed1\u8863\u5251\u624b\u7acb\u523b\u6251\u4e0a\u3002 \u3000\u3000\u53ea\u6709\u90a3\u4e00\u7fa4\u6234\u7740\u4eba\u76ae\u5177\u7684\u4eba\u90fd\u4e0d\u52a8\u3002 \u3000\u3000\u4e0d\u4ec5\u6ca1\u6709\u52a8\uff0c\u4ed6\u4eec\u8fde\u5175\u5203\u90fd\u4e0d\u66fe\u62bd\u51fa\u6765\uff01 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u548c\u4e0d\u8001\u4e66\u751f\u4e5f\u6ca1\u6709\u51b2\u4e0a\u3002 \u3000\u3000\u91d1\u5200\u7ea7\u6740\u624b\u548c\u9ed1\u8863\u5251\u624b\u4e00\u5165\u6df7\u6218\uff0c\u5f62\u52bf\u5c31\u53d8\u5f97\u660e\u6717\u4e86\uff01 \u3000\u3000\u6c34\u5e08\u7cbe\u9510\u663e\u7136\u4e0d\u662f\u4ed6\u4eec\u7684\u5bf9\u624b\uff01 \u3000\u3000\u867d\u7136\u7a0b\u5c0f\u8776\u5728\u9635\u4e2d\u5de6\u51b2\u53f3\u51fb\uff0c\u6240\u5230\u4e4b\u5904\uff0c\u6740\u624b\u4eec\u65e0\u4e0d\u6bd9\u547d\u6216\u8eb2\u5f00\uff0c\u4f46\u6c34\u5e08\u8fd8\u662f\u6e10\u6e10\u8d25\u4e0b \u9635\u4e86\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u7b49\u4e09\u5927\u6355\u5934\u4ee5\u53ca\u6797\u4e0d\u51e1\u7b49\u4eba\u7684\u52a0\u5165\uff0c\u4e5f\u4e0d\u80fd\u626d\u8f6c\u6218\u5c40\u3002 \u3000\u3000\u4e09\u5927\u6355\u5934\u5728\u4e09\u540d\u9ed1\u8863\u5251\u624b\u7684\u5f3a\u653b\u4e4b\u4e0b\uff0c\u7adf\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6ca1\u6709\u8fd8\u624b\u4e4b\u529b\uff01 \u3000\u3000\u6c34\u5e08\u58eb\u5175\uff0c\u4e00\u4e2a\u63a5\u4e00\u4e2a\u5730\u88ab\u6740\u4e86\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u4eec\u5df2\u5269\u4e0b\u4e0d\u5230\u4e00\u767e\u4eba\uff01 \u3000\u3000\u7a81\u7136\uff0c\u5c0f\u6587\u548c\u5c0f\u96c5\u6e05\u53f1\u51fa\u58f0\uff0c\u98de\u62a2\u800c\u81f3\uff0c\u52a0\u5165\u4e86\u6218\u5708\u3002 \u3000\u3000\u4e24\u9053\u5251\u5149\uff0c\u56db\u4e0b\u76d8\u65cb\uff0c\u4e0d\u4e00\u4f1a\u513f\u5c31\u683c\u6740\u4e86\u6b63\u4e0e\u4e09\u5927\u6355\u5934\u5bf9\u9635\u7684\u9ed1\u8863\u5251\u624b\uff0c\u8fd8\u6740\u6b7b\u4e86\u4e8c\u5341 \u4f59\u540d\u91d1\u5200\u7ea7\u6740\u624b\uff01 \u3000\u3000\u4f46\u4e00\u76f4\u6ca1\u6709\u52a8\u7684\u9ed1\u8863\u8499\u9762\u4eba\u3001\u4e0d\u8001\u4e66\u751f\u548c\u4ed6\u4eec\u8eab\u540e\u90a3\u4e00\u7fa4\u8eab\u7740\u5f69\u8272\u8863\u670d\uff0c\u6234\u7740\u4eba\u76ae\u9762\u5177 \u7684\u6740\u624b\u4eec\u7a81\u7136\u53d1\u52a8\u4e86\uff01 \u3000\u3000\u4ed6\u4eec\u5e76\u6ca1\u6709\u52a0\u5165\u6218\u6597\uff0c\u800c\u662f\u5206\u6210\u4e24\u8def\uff0c\u81ea\u5de6\u53f3\u4e24\u4fa7\u4e92\u6251\u5411\u738b\u5c11\u537f\u548c\u91d1\u5c0f\u7709\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u5927\u60ca\uff0c\u7acb\u5373\u4e0e\u4e09\u5927\u6355\u5934\u3001\u6797\u4e0d\u51e1\u7b49\u4eba\u56de\u9a6c\u6551\u63f4\uff01 \u3000\u3000\u5c0f\u6587\u548c\u5c0f\u96c5\u4e5f\u9192\u609f\u8fc7\u6765\uff0c\u56de\u5934\u51b2\u3002 \u3000\u3000\u5979\u4eec\u60f3\u8d76\u5728\u6740\u624b\u4eec\u4e4b\u524d\uff0c\u51b2\u56de\u5230\u738b\u5c11\u537f\u8eab\u8fb9\uff01 \u3000\u3000\u4f46\u6740\u624b\u4eec\u8fd8\u662f\u5148\u5230\u4e00\u70b9\uff01 \u3000\u3000\u675c\u671b\u6708\u548c\u60dc\u7389\u6325\u5251\u800c\u4e0a\uff0c\u5206\u522b\u654c\u4f4f\u4e86\u9ed1\u8863\u8499\u9762\u4eba\u548c\u4e0d\u8001\u4e66\u751f\uff01 \u3000\u3000\u4f46\u4ed6\u4eec\u6321\u4e0d\u4f4f\u90a3\u4e9b\u51f6\u608d\u7684\u6740\u624b\u3002 \u3000\u3000\u4e8c\u5341\u4f59\u540d\u6740\u624b\u4e3e\u7740\u4eae\u95ea\u95ea\u7684\u5200\u5251\uff0c\u76f4\u6251\u5411\u738b\u5c11\u537f\u3002 \u3000\u3000\u675c\u671b\u6708\u4e00\u8fb9\u594b\u529b\u51fa\u5251\uff0c\u5251\u5149\u5982\u66b4\u96e8\u72c2\u98ce\u4e00\u822c\u7f20\u4f4f\u4e86\u9ed1\u8863\u8499\u9762\u4eba\uff0c\u4e00\u58f0\u5927\u547c\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c \u5feb\u70b9\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u5374\u6ca1\u6709\u6253\u9a6c\u9003\u8d70\uff01 \u3000\u3000\u4ed6\u53cd\u800c\u9a71\u9a6c\u5411\u524d\u8d70\u4e86\u51e0\u6b65\uff0c\u62a4\u5728\u91d1\u5c0f\u7709\u8eab\u524d\uff0c\u5927\u559d\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u4ee5\u6740\u4e86\u738b\u67d0\uff0c\u4f46\u7edd\u4e0d\u8981 \u4f24\u4e86\u91d1\u5c0f\u59d0\uff01\u201d \u3000\u3000\u5728\u8fd9\u79cd\u751f\u6b7b\u5173\u5934\uff0c\u4ed6\u60f3\u7684\u4e0d\u662f\u81ea\u5df1\uff0c\u800c\u4e14\u522b\u4eba\uff01 \u3000\u3000\u5df2\u7ecf\u51b2\u8fd1\u7684\u7a0b\u5c0f\u8776\u5fc3\u4e2d\u4e0d\u7981\u4e00\u70ed\uff01 \u3000\u3000\u6740\u624b\u4eec\u4f3c\u4e4e\u4e5f\u88ab\u4ed6\u7684\u6c14\u52bf\u6240\u6151\u4f4f\uff0c\u4e00\u65f6\u95f4\u5446\u4f4f\u4e86\uff01 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u53cc\u638c\u731b\u51fb\uff0c\u51fb\u9000\u4e86\u675c\u671b\u6708\uff0c\u5927\u543c\u9053\uff1a\u201c\u6123\u7740\u5e72\u4ec0\u4e48\uff01\u5feb\u6740\u4e86\u4ed6\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u817e\u8eab\u81ea\u9a6c\u80cc\u4e0a\u8dc3\u8d77\uff0c\u5de6\u624b\u4e00\u6325\uff0c\u7ec6\u7d22\u5728\u7a7a\u4e2d\u5212\u51fa\u4e00\u9053\u4f18\u7f8e\u7684\u7ea2\u5f71\u3002 \u3000\u3000\u957f\u957f\u7684\u7ec6\u7d22\u4e0a\u84c4\u96c6\u4e86\u5979\u7684\u5341\u4e8c\u6210\u529f\u529b\uff0c\u597d\u4e00\u9053\u5343\u65a4\u94c1\u7ec3\uff0c\u987f\u65f6\u5c06\u51b2\u8fd1\u738b\u5c11\u537f\u7684\u4e8c\u5341\u591a\u540d \u6740\u624b\u5168\u90e8\u6253\u5012\u5728\u5730\uff01 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u4e00\u4f38\u624b\uff0c\u81ea\u4e00\u540d\u6740\u624b\u624b\u4e2d\u63a5\u8fc7\u4e00\u67c4\u957f\u5251\uff0c\u8fde\u51fa\u4e24\u62db\uff0c\u5c06\u6251\u4e0a\u7684\u675c\u671b\u6708\u523a\u5012\u5728 \u5730\uff0c\u957f\u5251\u4e00\u8f6c\uff0c\u5df2\u523a\u5411\u738b\u5c11\u537f\u3002 \u3000\u3000\u738b\u5c11\u537f\u5f15\u9888\u5f85\u6b7b\uff01 \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u4e00\u9053\u4eba\u5f71\u6251\u4e86\u4e0a\u6765\uff0c\u6321\u5728\u738b\u5c11\u537f\u8eab\u524d\u3002 \u3000\u3000\u957f\u5251\u6beb\u65e0\u963b\u6321\u5730\u523a\u6740\u4e86\u9ed1\u5f71\uff0c\u53c8\u6536\u4e86\u56de\u53bb\u3002 \u3000\u3000\u9ed1\u5f71\u60e8\u547c\u4e00\u58f0\uff0c\u6251\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\u55b7\u51fa\u7684\u9c9c\u8840\uff0c\u6d12\u4e86\u738b\u5c11\u537f\u4e00\u5934\uff0c\u4e00\u8138\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u6b63\u60f3\u524c\u51fa\u7b2c\u4e8c\u5251\uff0c\u675c\u671b\u6708\u5df2\u7ecf\u518d\u6b21\u6251\u4e0a\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u4e5f\u5df2\u8d76\u5230\uff0c\u96ea\u4eae\u7684\u67aa\u5934\u7f69\u4f4f\u4e86\u9ed1\u8863\u8499\u9762\u4eba\u5468\u8eab\u8981\u5bb3\uff01 \u3000\u3000\u5c0f\u6587\u3001\u5c0f\u96c5\u4e5f\u5df2\u51b2\u56de\uff0c\u5bf9\u6740\u624b\u4eec\u75db\u793a\u6740\u5a01\uff01 \u3000\u3000\u4e00\u9635\u62fc\u6b7b\u8840\u6218\uff0c\u7adf\u7136\u5c06\u4e0d\u8001\u4e66\u751f\u548c\u9ed1\u8863\u8499\u9762\u4eba\u903c\u5f97\u5411\u540e\u9000\u8d70\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u6251\u5230\u90a3\u540d\u88ab\u8499\u9762\u9ed1\u8863\u4eba\u523a\u4e2d\u7684\u4eba\u8eab\u8fb9\uff0c\u5927\u547c\u9053\uff1a\u201c\u6797\u8d24\u5f1f\uff01\u6797\u8d24\u5f1f\uff01\u201d \u3000\u3000\u7528\u8eab\u4f53\u6321\u4f4f\u4e86\u523a\u5411\u738b\u5c11\u537f\u90a3\u4e00\u5251\u7684\uff0c\u6b63\u662f\u6797\u4e0d\u51e1\uff01 \u3000\u3000\u6797\u4e0d\u51e1\u52c9\u5f3a\u7741\u5f00\u4e86\u773c\u775b\uff0c\u5148\u770b\u4e86\u770b\u738b\u5c11\u537f\uff0c\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u6211\u4e0d\u80fd\u591f\u518d\u4fdd\u62a4\u4f60\u4e86\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u53cc\u76ee\u6cea\u6d41\uff0c\u4e00\u4e2a\u5b57\u4e5f\u8bf4\u4e0d\u51fa\uff01 \u3000\u3000\u4ed6\u53c8\u770b\u7740\u675c\u671b\u6708\uff0c\u9053\uff1a\u201c\u53f8\u4e3b\uff0c\u5c5e\u4e0b\u4e0d\u80fd\u518d\u8ffd\u968f\u2026\u2026\u5de6\u53f3\u4e86\uff0c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u8bdd\u6ca1\u8bf4\u5b8c\uff0c\u4ed6\u5934\u4e00\u70b9\uff0c\u6b7b\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u629a\u5c38\u75db\u54ed\uff01 \u3000\u3000\u4e09\u5927\u6355\u5934\u4e5f\u5fc3\u4e2d\u60e8\u7136\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u76ee\u89c6\u7740\u5c0f\u6587\u548c\u5c0f\u96c5\uff0c\u8bf4\u58f0\u9053\uff1a\u201c\u8c01\u8ba9\u4f60\u4eec\u53c2\u52a0\u6218\u6597\u7684\uff1f\u201d \u3000\u3000\u5c0f\u6587\u9053\uff1a\u201c\u6211\u4eec\u662f\u6015\u5c0f\u59d0\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u53ef\u4f60\u4eec\u7684\u4efb\u52a1\u662f\u4fdd\u62a4\u738b\u5927\u4eba\uff01\u5982\u679c\u4f60\u4eec\u4e0d\u64c5\u81ea\u884c\u52a8\uff0c\u6797\u53f8\u6848\u4e5f\u4e0d\u4f1a\u6b7b\uff01\u201d \u3000\u3000\u5c0f\u96c5\u9053\uff1a\u201c\u6211\u77e5\u9053\u9519\u4e86\uff0c\u4efb\u51ed\u5c0f\u59d0\u60e9\u7f5a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5148\u4e0d\u8bf4\u8fd9\u4e2a\uff0c\u6211\u4eec\u706b\u901f\u64a4\u79bb\u6b64\u5730\uff01\u201d \u3000\u3000\u6740\u624b\u4eec\u5df2\u7ecf\u51b2\u4e86\u4e0a\u6765\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u8df3\u4e0a\u9a6c\uff0c\u9053\uff1a\u201c\u5927\u5bb6\u5feb\u4e0a\u9a6c\uff0c\u5f80\u5317\u8d70\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u957f\u67aa\u4e00\u6a2a\uff0c\u626b\u4e2d\u4e86\u51b2\u4e0a\u6765\u7684\u56db\u540d\u6740\u624b\u3002 \u3000\u3000\u5979\u770b\u7740\u4eba\u90fd\u4e0a\u4e86\u9a6c\uff0c\u4e5f\u4e0d\u518d\u6218\uff0c\u6389\u8f6c\u9a6c\u5934\uff0c\u98de\u5954\u800c\u53bb\u3002 \u3000\u3000\u6740\u624b\u4eec\u53d1\u8db3\u75be\u8ffd\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u6ca1\u6709\u9a6c\uff0c\u4e24\u6761\u817f\u7ec8\u7a76\u8dd1\u4e0d\u8fc7\u56db\u6761\u817f\u3002 \u3000\u3000\u4e00\u53e3\u6c14\u72c2\u5954\u51fa\u4e8c\u5341\u4f59\u91cc\uff0c\u7a0b\u5c0f\u8776\u624d\u8ba9\u4f17\u4eba\u505c\u4e0b\u6765\u3002 \u3000\u3000\u738b\u5c11\u537f\u56db\u4e0b\u4e00\u770b\uff0c\u5fc3\u4e2d\u987f\u65f6\u51b0\u51b7\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u8fd9\u4e00\u884c\u4eba\u5df2\u7ecf\u53ea\u5269\u4e0b\u4e86\u5341\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u5341\u4e00\u4e2a\u4eba\u4e4b\u4e2d\uff0c\u738b\u575a\u3001\u4f55\u5927\u5149\u548c\u56db\u5927\u6355\u5934\u8fd8\u90fd\u53d7\u4e86\u7a0b\u5ea6\u4e0d\u7b49\u7684\u4f24\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5927\u5bb6\u4e0b\u9a6c\u4f11\u606f\u4e00\u4e0b\u5427\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6c5f\u5357\u5206\u53f8\u6570\u5341\u5404\u597d\u624b\u5df2\u6b7b\u4f24\u6b86\u5c3d\uff0c\u4e94\u5341\u540d\u6c34\u5e08\u5f13\u5f29\u624b\u4e5f\u5df2\u4e00\u4e2a\u4e0d \u5269\uff0c\u6211\u4eec\u5728\u8fd9\u91cc\u4f11\u606f\uff0c\u5c82\u4e0d\u662f\u7b49\u6b7b\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u738b\u5927\u4eba\u653e\u5fc3\uff0c\u5f88\u5feb\u5c31\u4f1a\u6709\u4eba\u8d76\u6765\u63f4\u52a9\u6211\u4eec\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6740\u624b\u96c6\u56e2\u7684\u529b\u91cf\u5b9e\u5728\u662f\u592a\u5f3a\u5927\u4e86\uff0c\u4ed6\u4eec\u7adf\u7136\u80fd\u5c06\u4e94\u767e\u540d\u6c34\u5e08 \u7cbe\u9510\u4e00\u4e3e\u6740\u6b7b\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u88f9\u597d\u4e86\u5de6\u81c2\u7684\u4f24\u53e3\uff0c\u9053\uff1a\u201c\u5927\u4eba\uff0c\u4ed6\u4eec\u5e76\u6ca1\u6709\u88ab\u5168\u90e8\u6740\u6b7b\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u636e\u6211\u89c2\u5bdf\uff0c\u5927\u7ea6\u6709\u4e00\u767e\u6765\u4eba\u9003\u8d70\u4e86\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u9003\u8d70\u4e86\u66f4\u597d\uff01\u4ed6\u4eec\u54ea\u91cc\u662f\u5982\u6b64\u51f6\u608d\u7684\u6740\u624b\u4eec\u7684\u5bf9\u624b\uff0c\u80fd\u591a\u9003\u51fa \u4e00\u4eba\uff0c\u738b\u67d0\u5fc3\u4e2d\u4e5f\u5c11\u4e00\u4efd\u5185\u759a\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5927\u4eba\uff0c\u4ed6\u4eec\u8fd8\u4f1a\u6765\u627e\u6211\u4eec\u7684\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u9a6c\u63d0\u7763\u6307\u6325\u6c34\u5e08\u5df2\u6709\u7ecf\u9a8c\uff0c\u8fd9\u4e94\u767e\u4eba\u663e\u7136\u90fd\u662f\u4ed6\u624b\u4e0b\u7684\u7cbe\u5175\uff0c\u9a6c\u6307\u7763\u6d3e\u4ed6 \u4eec\u6765\u65f6\uff0c\u4e00\u5b9a\u5411\u4ed6\u4eec\u4e0b\u4e86\u6b7b\u547d\u4ee4\uff0c\u4ed6\u4eec\u867d\u8bf4\u56e0\u4e00\u65f6\u62b5\u6297\u4e0d\u4f4f\uff0c\u56db\u4e0b\u9003\u6563\uff0c\u4f46\u53ea\u8981\u60f3\u5230\u519b\u4ee4\uff0c \u4ed6\u4eec\u8fd8\u4f1a\u518d\u6765\u7684\u3002\u201d \u3000\u3000\u679c\u7136\uff0c\u4e0d\u4e00\u4f1a\u513f\uff0c\u9003\u5f97\u6027\u547d\u7684\u9a91\u5175\u53c8\u4e09\u4e2a\u4e00\u7fa4\uff0c\u4e94\u4e2a\u4e00\u4f19\u5730\u96c6\u4e2d\u5f52\u6765\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u770b\u4e86\u770b\uff0c\u53d1\u73b0\u5f52\u6765\u7684\u5171\u6709\u516d\u5341\u4e03\u4eba\u3002 \u3000\u3000\u5979\u8ba9\u4ed6\u4eec\u4e0b\u9a6c\u4f11\u606f\uff0c\u5403\u4e9b\u5e72\u7cae\uff0c\u5e76\u5c06\u4ec5\u5269\u7684\u5341\u5323\u8fde\u5f29\u94c1\u7bad\u53d1\u7ed9\u4e86\u4ed6\u4eec\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6740\u624b\u4eec\u4e0d\u4e45\u5c31\u4f1a\u8ffd\u8fc7\u6765\uff0c\u5230\u90a3\u65f6\uff0c\u4f60\u4eec\u53ea\u987b\u8fdc\u8fdc\u5730\u7528\u8fde\u5f29\u94c1\u7bad\u88ad\u51fb\u4ed6\u4eec\uff0c \u5343\u4e07\u4e0d\u8981\u518d\u53c2\u52a0\u6218\u6597\uff0c\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u6c34\u5e08\u9a91\u5175\u4eec\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u603b\u6355\u5934\u6709\u4e00\u4ef6\u4e8b\uff0c\u6211\u4e00\u76f4\u60f3\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u51ed\u6740\u624b\u96c6\u56e2\u7684\u5b9e\u529b\uff0c\u5982\u5728\u626c\u5dde\u65f6\u4ed6\u4eec\u5c31\u5c3d\u51fa\u597d\u624b\u653b\u6253\u5e9c\u8859\uff0c\u6211\u4eec\u4e00\u5b9a\u65e9\u5df2 \u62b5\u6321\u4e0d\u4f4f\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4ed6\u4eec\u4e00\u76f4\u4e0d\u52a8\u624b\uff0c\u975e\u5f97\u5230\u73b0\u5728\u624d\u52a8\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u56e0\u4e3a\u90a3\u65f6\u4ed6\u4eec\u5fc3\u91cc\u8fd8\u62b1\u6709\u5e0c\u671b\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u4ed6\u4eec\u73b0\u5728\u8fd9\u6837\u505a\u4e5f\u662f\u4e3a\u5f62\u52bf\u6240\u8feb\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\uff0c\u4e0d\u628a\u4ed6\u4eec\u903c\u5230\u8fd9\u4e00\u6b65\uff0c\u4ed6\u4eec\u5c31\u4e0d\u4f1a\u5c3d\u51fa\u5b9e\u529b\uff0c\u800c\u6211\u4eec\u4e5f\u5c31\u65e0\u6cd5\u5c06\u4ed6 \u4eec\u4e00\u7f51\u6253\u5c3d\uff01\u518d\u8bf4\uff0c\u66f4\u91cd\u8981\u662f\u9636\u8981\u627e\u5230\u6740\u624b\u96c6\u56e2\u4e0e\u67d0\u738b\u7237\u52fe\u7ed3\u7684\u8bc1\u636e\uff0c\u5982\u679c\u4e0d\u5c06\u4ed6\u4eec\u7684\u7cbe\u9510 \u5f15\u51fa\u626c\u5dde\uff0c\u6211\u4eec\u5c31\u65e0\u6cd5\u8fdb\u5165\u6740\u624b\u96c6\u56e2\u7684\u5185\u90e8\uff0c\u53c8\u600e\u80fd\u627e\u5230\u8bc1\u636e\u5462\uff1f\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u542c\u603b\u6355\u5934\u7684\u610f\u601d\uff0c\u5f62\u52bf\u4ecd\u7136\u5728\u6211\u4eec\u63a7\u5236\u4e4b\u4e2d\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u6211\u4eec\u88ab\u4e00\u8def\u8ffd\u6740\uff0c\u635f\u5931\u4e86\u8fd1\u767e\u540d\u597d\u624b\u548c\u6570\u767e\u6c34\u5e08\u7cbe\u9510\uff0c\u4e3a\u5f97\u5c31\u662f\u5f15\u4ed6\u4eec\u8ffd \u6740\u6211\u4eec\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5982\u679c\u4e0d\u8ba9\u4ed6\u4eec\u611f\u89c9\u5230\u5f88\u5feb\u5c31\u80fd\u591f\u5f97\u624b\uff0c\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u51b7\u9759\u4e0b\u6765\uff0c\u4ed4\u7ec6 \u5730\u8003\u8651\u5168\u5c40\u7684\u5f97\u5931\uff0c\u90a3\u6837\u7684\u8bdd\uff0c\u6211\u4eec\u5728\u8fd9\u4e4b\u524d\u6240\u6709\u7684\u52aa\u529b\u5c31\u90fd\u767d\u8d39\u4e86\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u9053\uff1a\u201c\u53ef\u54b1\u4eec\u635f\u5931\u4e86\u90a3\u4e48\u591a\u4eba\uff0c\u503c\u5f97\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5c91\u53f8\u4e3b\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u5f53\u5e74\u6210\u7956\u7687\u5e1d\u8d77\u5175\u9756\u96be\uff0c\u5728\u56db\u5e74\u7684\u6218\u4e71\u4e2d\uff0c\u53c8\u6b7b\u4e86\u591a \u5c11\u519b\u961f\uff1f\u6740\u4e86\u591a\u5c11\u767e\u59d3\uff1f\u6240\u4ee5\uff0c\u5982\u679c\u635f\u5931\u8fd9\u4e9b\u4eba\u80fd\u591f\u5f15\u51fa\u6740\u624b\u96c6\u56e2\uff0c\u80fd\u591f\u8ba9\u6211\u4eec\u7684\u4eba\u627e\u5230\u6740 \u624b\u96c6\u56e2\u4e0e\u67d0\u738b\u7237\u52fe\u7ed3\u7684\u8bc1\u636e\uff0c\u5219\u67d0\u738b\u7237\u8d77\u5175\u593a\u4f4d\u7684\u9634\u8c0b\u5c31\u4e0d\u53ef\u5b9e\u65bd\u3002\u6211\u8ba4\u4e3a\uff0c\u65e2\u4f7f\u635f\u5931\u518d\u5927\uff0c \u4e5f\u503c\u5f97\u3002\u201d \u3000\u3000\u5c91\u5578\u864e\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u6211\u5f7b\u5e95\u60f3\u901a\u4e86\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u60f3\u901a\u4e86\u5c31\u597d\uff0c\u4f60\u597d\u597d\u4f11\u606f\u4e00\u4e0b\uff0c\u517b\u8db3\u6c14\u529b\uff0c\u88f9\u597d\u4f24\uff0c\u4e00\u4f1a\u89c1\u8fd8\u6709\u5927\u4ed7\u8981 \u6253\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u770b\u3002\u201d \u3000\u3000\u4ed6\u6307\u7740\u897f\u8fb9\u7684\u5929\u7a7a\u3002 \u3000\u3000\u8fdc\u8fdc\u5730\uff0c\u7a7a\u4e2d\u70b8\u5f00\u4e00\u56e2\u7ea2\u8272\u7684\u70df\u96fe\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u4e0b\u7ad9\u8d77\u8eab\u5b50\uff0c\u6fc0\u52a8\u5730\u9053\uff1a\u201c\u597d\uff0c\u63f4\u519b\u5c31\u5728\u9644\u8fd1\uff0c\u9a6c\u4e0a\u5c31\u4f1a\u8d76\u5230\u4e86\u3002\u201d\u5979\u6c89\u541f\u4e86 \u4e00\u4f1a\u513f\uff0c\u53c8\u9053\uff1a\u201c\u5982\u679c\u5feb\uff0c\u4ed6\u4eec\u80fd\u5728\u6740\u624b\u4eec\u8ffd\u6765\u4e4b\u524d\u8d76\u5230\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u8fd9\u6b21\u6765\u63f4\u662f\u4e0d\u662f\u957f\u5c71\u5144\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u90a3\u4f1a\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e00\u4f1a\u513f\u5927\u4eba\u5c31\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u5979\u770b\u7740\u738b\u5c11\u537f\uff0c\u7a81\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4eba\u4e0e\u738b\u5927\u4eba\u4e5f\u7b97\u662f\u719f\u4eba\uff0c\u4e00\u4f1a\u513f\uff0c\u5927\u4eba\u4e0d\u8981\u592a\u5403 \u60ca\u54e6\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u719f\u4eba\uff1f\u4f1a\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f1a\u4e0d\u4f1a\u662f\u94f6\u8863\u4eba\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u53c8\u662f\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u675c\u53f8\u4e3b\u867d\u7136\u673a\u667a\u8fc7\u4eba\uff0c\u4f46\u8fd9\u4e00\u6b21\u4e5f\u731c\u9519\u4e86\uff0c\u94f6\u8863\u4eba\u53e6\u6709\u91cd\u8981\u7684 \u4efb\u52a1\uff0c\u4e0d\u80fd\u8d76\u6765\uff0c\u4e3b\u8981\u770b\u4ed6\u80fd\u5426\u987a\u5229\u5b8c\u6210\u4efb\u52a1\uff0c\u6211\u4e5f\u4e0d\u6562\u80af\u5b9a\uff01\u201d \u3000\u3000------------- \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u4e09\u5341\u4e00\u7ae0 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u7a0b\u5c0f\u8776\u7684\u5224\u65ad\u5931\u8bef\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u6740\u624b\u4eec\u5df2\u7ecf\u51fa\u73b0\u4e86\uff0c\u800c\u63f4\u519b\u5374\u8fd8\u6ca1\u6709\u8d76\u5230\u3002 \u3000\u3000\u516d\u5341\u4e03\u540d\u6c34\u5e08\u7cbe\u9510\u7acb\u523b\u653e\u7bad\u3002 \u3000\u3000\u4f46\u5c04\u51fa\u7684\u7bad\u5b9e\u5728\u592a\u5c11\u4e86\uff0c\u9664\u4e86\u6709\u5341\u51e0\u540d\u7070\u8863\u6740\u624b\u8eb2\u907f\u4e0d\u53ca\uff0c\u88ab\u5c04\u6740\u5012\u5730\u5916\uff0c\u5176\u4f59\u7684\u90fd\u88ab \u6740\u624b\u4eec\u683c\u5f00\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u603b\u5171\u53ea\u5c04\u51fa\u4e86\u4e24\u6b21\u7bad\uff0c\u5c31\u6ca1\u6709\u529e\u6cd5\u518d\u5f15\u5f13\u653e\u7bad\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u6740\u624b\u4eec\u5df2\u7ecf\u51b2\u4e0a\uff0c\u4e0e\u7a0b\u5c0f\u8776\u7b49\u6df7\u6218\u5728\u4e00\u8d77\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u8ba9\u56db\u5927\u6355\u5934\u548c\u60dc\u7389\u56f4\u6210\u4e86\u4e00\u4e2a\u5c0f\u5708\u5b50\uff0c\u5c06\u738b\u5c11\u537f\u548c\u91d1\u5c0f\u7709\u56f4\u5728\u5176\u4e2d\uff0c\u738b\u575a\u3001\u4f55\u5927 \u5149\u4e8c\u4eba\u5728\u5708\u5916\u6e38\u51fb\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u548c\u4e0d\u8001\u4e66\u751f\u90fd\u4e0d\u52a8\u624b\uff0c\u53ea\u662f\u8fdc\u8fdc\u7ad9\u5728\u4e00\u65c1\u3002 \u3000\u3000\u770b\u4ed6\u4eec\u7684\u67b6\u52bf\uff0c\u662f\u8981\u7b49\u5230\u7a0b\u5c0f\u8776\u7b49\u4eba\u529b\u7aed\u4e4b\u540e\uff0c\u518d\u884c\u51fa\u624b\u4e00\u51fb\uff01 \u3000\u3000\u7684\u786e\uff0c\u4ed6\u4eec\u4e5f\u7528\u4e0d\u7740\u518d\u7740\u6025\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u548c\u4e8e\u627f\u5fd7\u3001\u5c0f\u96c5\u867d\u7136\u52c7\u72c2\uff0c\u53c8\u5982\u4f55\u80fd\u5bf9\u4ed8\u5f97\u4e86\u6570\u767e\u6740\u624b\u5462\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u821e\u52a8\u624b\u4e2d\u7684\u4e08\u516b\u94c1\u67aa\uff0c\u65b9\u5706\u4e8c\u4e08\u4e4b\u95f4\uff0c\u51b2\u4e0a\u7684\u6740\u624b\u4eec\u65e0\u4e00\u5e78\u514d\uff0c\u5168\u90e8\u88ab\u96ea\u4eae\u7684\u67aa \u5934\u7a7f\u80f8\u800c\u8fc7\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e8c\u4e08\u65b9\u5706\u4e4b\u5916\uff0c\u5de6\u8fb9\uff0c\u5c0f\u6587\u624b\u91cc\u7684\u957f\u5251\u95ea\u6210\u4e00\u56e2\u94f6\u5149\u3002\u4e5f\u8ba9\u6321\u8005\u7acb\u907f\uff0c\u53f3\u8fb9\uff0c\u5c0f \u96c5\u4e5f\u638f\u51fa\u4e86\u538b\u7bb1\u5e95\u7684\u7edd\u6280\uff0c\u4e0d\u8fc7\uff0c\u5979\u5df2\u683c\u6740\u4e86\u5341\u591a\u4eba\uff01\u4e5f\u6709\u5341\u6765\u540d\u6740\u624b\u907f\u8fc7\u4e86\u8fd9\u4e24\u5251\u4e00\u67aa\u7ec4 \u5408\u800c\u6210\u7684\u5fc5\u6740\u4e4b\u7f51\uff0c\u63a5\u8fd1\u4e86\u738b\u5c11\u537f\uff0c\u4f46\u5168\u90e8\u6b7b\u4e8e\u675c\u671b\u6708\u3001\u4e09\u5927\u6355\u53f8\u548c\u60dc\u7389\u4e4b\u624b\u3002 \u3000\u3000\u738b\u575a\u3001\u4f55\u5927\u5149\u4ec5\u80fd\u81ea\u4fdd\u800c\u5df2\u3002 \u3000\u3000\u60e8\u70c8\u7684\u640f\u6740\u53c8\u8fdb\u884c\u4e86\u4e00\u987f\u996d\u5de5\u592b\uff0c\u7a0b\u5c0f\u8776\u957f\u67aa\u821e\u8d77\u7684\u5708\u5b50\u5df2\u7ecf\u5f00\u59cb\u7f29\u5c0f\u4e86\u3002\u5c0f\u6587\u548c\u5c0f\u96c5 \u4e5f\u5f00\u59cb\u540e\u9000\u3002 \u3000\u3000\u5979\u4eec\u7684\u80f3\u818a\u5df2\u6e10\u611f\u9ebb\u6728\uff0c\u4f53\u529b\u4e5f\u5728\u98de\u5feb\u5730\u6d88\u8017\u7740\u3002 \u3000\u3000\u675c\u671b\u6708\u7b49\u4eba\u4e5f\u5df2\u6c57\u900f\u8863\u886b\uff01 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u548c\u4e0d\u8001\u4e66\u751f\u7b49\u7684\u663e\u7136\u5c31\u662f\u8fd9\u4e00\u523b\u3002 \u3000\u3000\u4ed6\u4eec\u4e8c\u4eba\u4e00\u8d77\u4e3e\u5251\uff0c\u6162\u6162\u903c\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u4e0d\u8001\u4e66\u751f\u5df2\u62bd\u51fa\u5b9d\u5251\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u7684\u4e24\u624b\u4e2d\uff0c\u6267\u7740\u4e24\u67c4\u5200\u3002 \u3000\u3000\u53f3\u624b\u957f\u5200\uff0c\u5de6\u624b\u77ed\u5200\u3002 \u3000\u3000\u6740\u624b\u4eec\u7acb\u523b\u5411\u4e24\u8fb9\u9000\u5f00\uff0c\u95ea\u51fa\u4e00\u6761\u901a\u9053\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u548c\u4e0d\u8001\u4e66\u751f\u8d70\u5f97\u90fd\u5f88\u6162\uff0c\u4e8c\u4eba\u7684\u56db\u53ea\u773c\u775b\u91cc\uff0c\u95ea\u52a8\u7740\u6151\u4eba\u7684\u5149\u3002\u56db\u53ea\u773c\u775b\u5168 \u90fd\u76ef\u7740\u7a0b\u5c0f\u8776\u3002 \u3000\u3000\u663e\u7136\uff0c\u4ed6\u4eec\u51c6\u5907\u5408\u529b\u4e00\u51fb\uff0c\u51fb\u6740\u7a0b\u5c0f\u8776\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u5f88\u6e05\u695a\uff0c\u4e0d\u6740\u6389\u7a0b\u5c0f\u8776\uff0c\u5c31\u4e0d\u53ef\u80fd\u6740\u4e86\u738b\u5c11\u537f\uff01 \u3000\u3000\u5c0f\u6587\u548c\u5c0f\u96c5\u547c\u5578\u7740\u6325\u5251\u51b2\u4e0a\uff0c\u5374\u5206\u522b\u88ab\u5341\u51e0\u540d\u6740\u624b\u7f20\u4f4f\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7d27\u63e1\u957f\u67aa\uff0c\u51b7\u51b7\u76ef\u7740\u8d8a\u903c\u8d8a\u8fd1\u7684\u4e24\u5927\u9ad8\u624b\u3002 \u3000\u3000\u5979\u77e5\u9053\uff0c\u51ed\u81ea\u5df1\u7684\u6b66\u529f\uff0c\u6839\u672c\u63a5\u4e0d\u4e0b\u4ed6\u4eec\u4e8c\u4eba\u7684\u8054\u624b\u4e00\u51fb\u3002 \u3000\u3000\u4f46\u5979\u5df2\u7ecf\u6ca1\u6709\u522b\u7684\u529e\u6cd5\u3002 \u3000\u3000\u5979\u751a\u81f3\u4e0d\u80fd\u7528\u6e38\u6597\u4e4b\u672f\u7f20\u4f4f\u4ed6\u4eec\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u4e0d\u80fd\u9000\u3002 \u3000\u3000\u5979\u7684\u8eab\u540e\uff0c\u5c31\u662f\u738b\u5c11\u537f\u3002 \u3000\u3000\u800c\u4e14\u5979\u77e5\u9053\uff0c\u56db\u5927\u6355\u5934\u4e5f\u5df2\u53d7\u4f24\uff0c\u800c\u4e14\u90fd\u5df2\u7cbe\u75b2\u529b\u7aed\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u5389\u543c\u4e00\u58f0\uff0c\u633a\u8d77\u4e08\u516b\u94c1\u67aa\uff0c\u523a\u5411\u9ed1\u8863\u8499\u9762\u4eba\u3002 \u3000\u3000\u5979\u60f3\u7528\u81ea\u5df1\u7aed\u5c3d\u5168\u529b\u7684\u8fd9\u4e00\u51fb\uff0c\u5148\u6740\u6389\u9ed1\u8863\u8499\u9762\u4eba\u8fd9\u4e2a\u6740\u624b\u96c6\u56e2\u7684\u9996\u9886\uff0c\u4e5f\u662f\u6740\u624b\u4e2d\u6b66 \u529f\u6700\u9ad8\u7684\u5927\u9ad8\u624b\uff01\u9ed1\u8863\u8499\u9762\u4eba\u4e00\u6446\u53cc\u5200\uff0c\u6a2a\u5411\u67b6\u5f00\u4e86\u8fd9\u4e00\u67aa\u3002 \u3000\u3000\u4ed6\u88ab\u67aa\u8eab\u4e0a\u4f20\u6765\u7684\u529b\u9053\u9707\u9000\u4e86\u4e94\u516d\u6b65\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u67aa\u52bf\u4e0d\u505c\uff0c\u6b63\u6b32\u523a\u51fa\u7b2c\u4e8c\u67aa\uff0c\u4e0d\u8001\u4e66\u751f\u7684\u957f\u5251\u95ea\u8d77\u4e94\u6735\u5251\u82b1\uff0c\u5206\u523a\u5979\u80f8\u524d\u4e94\u5904\u5927 \u7a74\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u53ea\u5f97\u56de\u67aa\u81ea\u6551\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u5927\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\uff01\u60f3\u4e0d\u5230\u7a0b\u603b\u6355\u5934\u7684\u5185\u529b\u5df2\u7ecf\u7ec3\u5230\u4e86\u8fd9\u4e2a\u706b\u5019\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u53cc\u5200\u4e00\u76f4\u653e\uff0c\u4e00\u6a2a\u63a2\uff0c\u53c8\u6251\u4e86\u4e0a\u6765\uff01 \u3000\u3000\u675c\u671b\u6708\u6025\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6211\u6765\u52a9\u4f60\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u6325\u5251\u4fbf\u8981\u6251\u4e0a\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u8270\u96be\u5730\u9053\uff1a\u201c\u4e0d\u53ef\uff01\u6211\u547d\u4ee4\u4f60\uff0c\u4fdd\u62a4\u738b\u5927\u4eba\uff0c\u5411\u897f\u64a4\u9000\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u4e00\u6014\uff0c\u4e0d\u52a8\u4e86\uff01 \u3000\u3000\u4ed6\u51b3\u4e0d\u60f3\u9000\u8d70\uff01 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e0d\u80fd\u773c\u7741\u7741\u770b\u7740\u7a0b\u5c0f\u8776\u6b7b\u4e8e\u8fd9\u4e24\u5927\u9ad8\u624b\u653b\u51fb\u4e4b\u4e0b\uff01 \u3000\u3000\u4f46\u4ed6\u4e5f\u4e0d\u80fd\u4e0a\u53bb\u5e2e\u5979\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u65e0\u6cd5\u8fdd\u80cc\u603b\u6355\u5934\u4e0b\u7684\u547d\u4ee4\uff01 \u3000\u3000\u6b63\u5728\u8fd9\u5371\u6025\u65f6\u523b\uff0c\u738b\u575a\u548c\u4f55\u5927\u5149\u5404\u4e3e\u5200\u3001\u5251\uff0c\u5636\u53eb\u7740\u51b2\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u51b2\u5411\u4e86\u9ed1\u8863\u8499\u9762\u4eba\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u5de6\u817f\u98de\u8d77\uff0c\u8fde\u8e22\u4e24\u811a\u3002 \u3000\u3000\u738b\u575a\u3001\u4f55\u5927\u5149\u51e0\u4e4e\u662f\u540c\u65f6\u88ab\u8e22\u4e2d\u4e86\u80f8\u53e3\u3002 \u3000\u3000\u4e8c\u4eba\u53e3\u5410\u9c9c\u8840\uff0c\u6454\u5012\u5728\u5730\uff01 \u3000\u3000\u675c\u671b\u6708\u5fc3\u4e2d\u5927\u6025\uff0c\u5374\u65e0\u6cd5\u53ef\u65bd\uff01 \u3000\u3000\u4ed6\u5f88\u60f3\u53bb\u770b\u770b\u8fd9\u4e8c\u4eba\u53d7\u4e86\u8fd9\u6c89\u91cd\u7684\u4e00\u51fb\uff0c\u662f\u4e0d\u662f\u5df2\u7ecf\u6bd9\u547d\uff0c\u4f46\u4ed6\u5374\u4e0d\u80fd\u53bb\uff01\u56e0\u4e3a\u4ed6\u5fc5\u987b \u4fdd\u62a4\u738b\u5c11\u537f\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u8d81\u738b\u3001\u4f55\u4e8c\u4eba\u88ab\u9ed1\u8863\u8499\u9762\u4eba\u8e22\u98de\u7684\u4e00\u5239\u90a3\uff0c\u594b\u8d77\u5168\u90e8\u5185\u529b\uff0c\u4f7f\u51fa\u4e86\u201c\u9738\u738b\u65e0\u654c\u201d \u67aa\u6cd5\u4e2d\u7684\u7edd\u6280\u2014\u2014\u201c\u9a7f\u8def\u68a8\u82b1\u201d\uff01 \u3000\u3000\u201c\u9a7f\u8def\u68a8\u82b1\u5904\u5904\u5f00\u201d\u2014\u2014\u8fd9\u4e00\u62db\u4f7f\u51fa\uff0c\u4f46\u89c1\u6f2b\u7a7a\u90fd\u662f\u96ea\u4eae\u7684\u67aa\uff0c\u5c31\u50cf\u6735\u6735\u68a8\u82b1\u4e00\u822c\uff0c\u5c06 \u9ed1\u8863\u8499\u9762\u4eba\u548c\u4e0d\u8001\u4e66\u751f\u5708\u5728\u7281\u82b1\u4e1b\u4e2d\uff01 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u5927\u60ca\u4e4b\u4e0b\uff0c\u548c\u4e0d\u8001\u4e66\u751f\u4e00\u8d77\u5012\u5730\u4e00\u6eda\uff0c\u6eda\u5f00\u4e86\u4e09\u56db\u4e08\u8fdc\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u7136\u8eb2\u5f00\u4e86\u8fd9\u4e00\u5fc5\u6740\u4e4b\u62db\uff0c\u4f46\u4e5f\u663e\u5f97\u5f02\u5e38\u72fc\u72c8\uff01 \u3000\u3000\u800c\u4e14\uff0c\u8fd9\u4e00\u62db\u4ed6\u4eec\u5341\u5206\u80c6\u5bd2\u3002 \u3000\u3000\u53ef\u4ee5\u60f3\uff0c\u5982\u679c\u521a\u624d\u4ed6\u4eec\u4e0d\u662f\u8054\u6740\u5bf9\u654c\uff0c\u90a3\u4e48\u5176\u4e2d\u4e00\u4eba\u80af\u5b9a\u5df2\u6b7b\u5728\u7a0b\u5c0f\u8776\u67aa\u4e0b\u3002\u662f\u6545\uff0c\u4e8c \u4eba\u4e00\u65f6\u4e0d\u6562\u518d\u4e0a\u653b\u51fb\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u67aa\u9010\u9000\u4e24\u540d\u5f3a\u654c\uff0c\u6b63\u6b32\u6325\u67aa\u518d\u62a2\u653b\u4e0a\u53bb\uff0c\u5ffd\u89c9\u80f8\u95f4\u4e00\u9635\u6c14\u8840\u7ffb\u6d8c\uff0c\u53cc\u817f\u7adf\u5df2\u4e0d \u80fd\u8fc8\u52a8\uff01 \u3000\u3000\u5979\u53f3\u624b\u63e1\u67aa\u6491\u5730\uff0c\u6491\u4f4f\u81ea\u5df1\u6447\u6447\u6643\u6643\u7684\u8eab\u5b50\uff0c\u5de6\u624b\u629a\u5728\u80f8\u524d\uff0c\u9762\u4e0a\u8868\u60c5\u5341\u5206\u75db\u82e6\uff01 \u3000\u3000\u7a81\u7136\uff0c\u5979\u4e00\u5f20\u53e3\uff0c\u5410\u51fa\u4e86\u4e00\u5927\u53e3\u9c9c\u7ea2\u7684\u8840\uff01 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u548c\u4e0d\u8001\u4e66\u751f\u5927\u559c\uff0c\u4e3e\u6b65\u53c8\u903c\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u4e0d\u6562\u8d70\u5f97\u592a\u5feb\uff01 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u77e5\u9053\u4ee5\u7a0b\u5c0f\u8776\u7684\u529f\u529b\uff0c\u90a3\u51dd\u6b7b\u7684\u4e00\u51fb\u5c06\u4f1a\u6709\u4f55\u7b49\u5a01\u529b\uff01 \u3000\u3000\u5c0f\u6587\u5c0f\u96c5\u4e00\u5de6\u4e00\u53f3\u6251\u4e0a\uff0c\u67b6\u8d77\u4e2b\u7a0b\u5c0f\u8776\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4f60\u600e\u4e48\u6837\u4e86\uff1f\u201d\u7a0b\u5c0f\u8776\u53c8\u5410 \u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5feb\uff0c\u5411\u897f\u9000\uff01\u201d \u3000\u3000\u60dc\u7389\u6276\u7740\u7a0b\u5c0f\u8776\uff0c\u56db\u5927\u6355\u5934\u62a4\u7740\u738b\u5c11\u537f\u548c\u91d1\u5c0f\u7709\uff0c\u5c0f\u6587\u5c0f\u96c5\u65ad\u540e\uff0c\u98de\u5feb\u5730\u5411\u897f\u9000\u53bb\u3002 \u3000\u3000\u897f\u9762\u4e0d\u8fdc\u5904\uff0c\u662f\u4e00\u5ea7\u4e0d\u9ad8\u7684\u91ce\u8349\u4e1b\u751f\u7684\u5c0f\u5c71\u5761\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u548c\u4e0d\u8001\u4e66\u751f\u6307\u6325\u6740\u624b\u4eec\uff0c\u6b65\u6b65\u7d27\u903c\u3002 \u3000\u3000\u6b63\u5728\u8fd9\u5e72\u94a7\u4e00\u53d1\u4e4b\u65f6\uff0c\u5c0f\u5c71\u5761\u4e0a\u7a81\u7136\u54cd\u8d77\u4e00\u9635\u8c6a\u8fc8\u7684\u5927\u7b11\u58f0\uff01 \u3000\u3000\u675c\u671b\u6708\u4e00\u60ca\u56de\u5934\uff0c\u770b\u89c1\u5c71\u5761\u4e0a\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u5192\u51fa\u4e86\u4e00\u5927\u7fa4\u8499\u9762\u4eba\u3002 \u3000\u3000\u8fd9\u7fa4\u8499\u9762\u4eba\u4e00\u5f8b\u9752\u5e03\u5305\u5934\uff0c\u9752\u8863\u9752\u88e4\uff0c\u811a\u4e0b\u53cc\u8033\u9ebb\u978b\uff0c\u6253\u7740\u5343\u5c42\u6eda\u7684\u7ed1\u817f\u3002\u4e3a\u9996\u4e00\u4eba\u7a7f \u7740\u4e00\u4ef6\u9752\u8272\u7684\u4e1d\u7ef8\u957f\u888d\u3002 \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4e0d\u597d\uff01\u8fd9\u91cc\u4e5f\u6709\u57cb\u4f0f\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u8981\u8f7b\u4e3e\u5984\u52a8\uff0c\u675c\u53f8\u4e3b\uff0c\u4ed6\u4eec\u662f\u81ea\u5df1\u4eba\uff01\u201d \u3000\u3000\u53ea\u89c1\u8fd9\u7fa4\u9752\u8863\u8499\u9762\u4eba\u5728\u4e3a\u9996\u4e4b\u4eba\u7684\u6307\u6325\u4e0b\uff0c\u5206\u6210\u4e24\u961f\uff0c\u9ad8\u4e3e\u5404\u822c\u5175\u5668\uff0c\u51b2\u4e0b\u5c71\u5761\uff0c\u51b2\u5165 \u4e86\u6740\u624b\u9635\u4e2d\uff01 \u3000\u3000\u675c\u671b\u6708\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u5c31\u5f7b\u5e95\u677e\u4e86\u53e3\u6c14\uff01 \u3000\u3000\u8fd9\u7fa4\u9752\u8863\u4eba\u8db3\u6709\u4e00\u767e\u4e8c\u5341\u4eba\uff0c\u800c\u4e14\uff0c\u4ed6\u4eec\u6bcf\u4e2a\u4eba\u7684\u6b66\u529f\uff0c\u90fd\u4e0e\u4e0d\u8001\u4e66\u751f\u624b\u4e0b\u7684\u9ed1\u8863\u5251\u624b \u76f8\u5f53\u3002 \u3000\u3000\u4ed6\u4eec\u4e2d\u6709\u5341\u6765\u4e2a\u7684\u6b66\u529f\u663e\u7136\u66f4\u8981\u9ad8\u51fa\u4e00\u7b79\uff0c\u56e0\u4e3a\u8fd9\u5341\u6765\u4eba\u5206\u6210\u4e24\u7ec4\uff0c\u5206\u522b\u56f4\u4f4f\u4e86\u9ed1\u8863\u8499 \u9762\u4eba\u548c\u4e0d\u8001\u4e66\u751f\uff0c\u800c\u4ed6\u4eec\u4e8c\u4eba\u4e00\u65f6\u88ab\u903c\u5f97\u624b\u5fd9\u811a\u4e71\uff01 \u3000\u3000\u8fd9\u4e9b\u9752\u8863\u4eba\u662f\u8c01\u5462\uff1f \u3000\u3000\u675c\u671b\u6708\u60f3\u4e86\u534a\u5929\uff0c\u4e5f\u60f3\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u9886\u5934\u7684\u90a3\u4e2a\u4eba\u7684\u58f0\u97f3\u4ed6\u5341\u5206\u964c\u751f\u3002 \u3000\u3000\u90a3\u4eba\u7684\u58f0\u97f3\u542c\u4e0a\u53bb\u6709\u4e9b\u82cd\u8001\uff0c\u663e\u7136\u5e74\u7eaa\u5df2\u7ecf\u5f88\u5927\u4e86\u3002 \u3000\u3000\u738b\u5c11\u537f\u4e5f\u5728\u60f3\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u90a3\u4eba\u7684\u58f0\u97f3\u4ed6\u5f88\u662f\u719f\u6089\uff0c\u4f46\u4e00\u65f6\u5374\u60f3\u4e0d\u8d77\u5230\u5e95\u662f\u8c01\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u670d\u4e0b\u5c0f\u6587\u9012\u4e0a\u7684\u4e00\u7c92\u6731\u7ea2\u8272\u7684\u836f\u4e38\uff0c\u4e0d\u4e00\u4f1a\u513f\uff0c\u4fbf\u6062\u590d\u4e86\u7cbe\u795e\u3002 \u3000\u3000\u867d\u7136\u5979\u7684\u5185\u4f24\u4e00\u65f6\u4e0d\u80fd\u75ca\u6108\uff0c\u6682\u65f6\u8fd8\u4e0d\u80fd\u53c2\u52a0\u6218\u9616\uff0c\u4f46\u5979\u5df2\u80fd\u7ad9\u8d77\u8eab\u5b50\u4e86\u3002\u7a0b\u5c0f\u8776\u3001\u56db \u5927\u6355\u5934\u3001\u5c0f\u6587\u3001\u5c0f\u96c5\u548c\u727f\u7389\u7ad9\u5728\u5c71\u5761\u4e0a\uff0c\u5c06\u738b\u5c11\u537f\u548c\u91d1\u5c0f\u7709\u56f4\u5728\u5f53\u4e2d\uff0c\u4f17\u4eba\u4e00\u8d77\u89c2\u770b\u6218\u5c40\u3002 \u3000\u3000\u53ea\u89c1\u6740\u624b\u4eec\u5f88\u5feb\u5c31\u7a33\u4f4f\u4e86\u9635\u811a\uff01 \u3000\u3000\u8fd9\u4e0b\u5c31\u80fd\u770b\u51fa\u6740\u624b\u4eec\u7684\u8bad\u7ec3\u6709\u7d20\u4e86\uff0c\u56e0\u4e3a\u4e0d\u8001\u4e66\u751f\u548c\u9ed1\u8863\u8499\u9762\u4eba\u5df2\u88ab\u5341\u51e0\u540d\u9752\u8863\u4eba\u56f4\u4f4f\uff0c \u9677\u5165\u82e6\u6218\u4e4b\u4e2d\uff0c\u4e0d\u80fd\u53d1\u53f7\u65bd\u4ee4\uff0c\u4f46\u6740\u624b\u4eec\u5374\u80fd\u81ea\u89c9\u5730\u4e09\u4e24\u4e2a\u4e00\u7ec4\u7ec4\u5408\u8d77\u6765\uff0c\u8054\u624b\u5bf9\u4ed8\u4e00\u540d\u9752 \u8863\u4eba\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6740\u624b\u4eec\u906d\u6b64\u7a81\u88ad\u800c\u80fd\u4e0d\u4e71\uff0c\u6211\u771f\u662f\u4e0d\u80fd\u4e0d\u4f69\u670d\u90a3\u4f4d\u9ed1\u8863\u7684\u8499\u9762\u4eba\u4e86\uff01\u201d \u3000\u3000\u6218\u6597\u6301\u7ee7\u4e86\u7ea6\u4e00\u987f\u996d\u529f\u592b\uff0c\u53cc\u65b9\u90fd\u5f00\u59cb\u540e\u9000\u4e86\u3002 \u3000\u3000\u6740\u624b\u4eec\u5728\u5c71\u5761\u524d\u4e22\u4e0b\u4e86\u7ea6\u767e\u4f59\u5177\u5c38\u4f53\uff0c\u800c\u9000\u4e0a\u5c71\u6765\u7684\u9752\u8863\u8499\u9762\u4eba\u4e5f\u635f\u5931\u4e86\u5c06\u8fd1\u4e00\u534a\u3002 \u3000\u3000\u4e3a\u9996\u7684\u9752\u8863\u8499\u9762\u4eba\u5728\u5c71\u5761\u524d\u5e03\u597d\u9632\u7ebf\uff0c\u81ea\u5df1\u5927\u6b65\u8d70\u4e0a\u5c71\u5761\uff0c\u5bf9\u738b\u5c11\u537f\u62db\u624b\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c \u59da\u67d0\u6765\u8fdf\u4e00\u6b65\uff0c\u5927\u4eba\u53d7\u60ca\u4e86\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u5927\u5403\u4e00\u60ca\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u662f\u59da\u987a\u5929\uff01\u201d \u3000\u3000\u9752\u8863\u8499\u9762\u4eba\u63ed\u5f00\u8138\u4e0a\u7684\u8499\u9762\u5e03\uff0c\u4e0d\u662f\u59da\u987a\u5929\uff0c\u53c8\u662f\u4f55\u4eba\uff1f \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u5931\u8e2a\u4e86\u5417\uff1f\u201d \u3000\u3000\u59da\u987a\u5929\u9053\uff1a\u201c\u662f\u554a\uff0c\u90a3\u662f\u56e0\u4e3a\u6211\u63a5\u5230\u4e86\u603b\u6355\u5934\u7684\u624b\u8c15\uff0c\u8ba9\u6211\u7387\u4eba\u6682\u65f6\u8f6c\u5165\u4e86\u6697\u4e2d\u884c\u52a8\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u56db\u5927\u6355\u5934\uff0c\u4f60\u4eec\u4e00\u5b9a\u90fd\u6ca1\u6709\u60f3\u5230\u5427\uff1f\u59da\u8001\u4e00\u76f4\u5c31\u662f\u5211\u90e8\u7684\u4eba\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5929\u67ab\u9053\u957f\u8981\u5ac1\u7978\u4e8e\u59da\u8001\u5462\u3002\u201d \u3000\u3000\u59da\u987a\u5929\u7b11\u9053\uff1a\u201c\u8fd9\u6b63\u662f\u806a\u660e\u53cd\u88ab\u806a\u660e\u8bef\uff0c\u4ed6\u8fd9\u4e00\u5ac1\u7978\uff0c\u59da\u67d0\u6b63\u597d\u8d81\u673a\u8f6c\u5165\u6697\u4e2d\uff0c\u800c\u4e14\u4ed6 \u81ea\u5df1\u4e5f\u5c31\u66b4\u9732\u4e86\u8eab\u4efd\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u60f3\u4e86\u60f3\uff0c\u8bd5\u63a2\u6027\u5730\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u5728\u738b\u67d0\u4e4b\u524d\u53ea\u5e72\u4e86\u4e00\u5e74\u534a\u7684\u90a3\u4f4d\u626c\u5dde\u77e5\u5e9c\uff0c\u662f \u4e0d\u662f\u90a3\u4e2a\u67d0\u738b\u7237\u7684\u4eba\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u738b\u5927\u4eba\u5f00\u59cb\u60f3\u660e\u767d\u4e86\u5417\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u662f\u7684\uff0c\u524d\u4efb\u77e5\u5e9c\u867d\u8bf4\u624d\u80fd\u6709\u9650\uff0c\u4f46\u770b\u8d77\u6765\u4e3a\u5b98\u4e5f\u7b97\u6e05\u5ec9\uff0c\u6700\u540e\u5374\u88ab\u641e\u5f97\u7070 \u5934\u7070\u8138\u5730\u4e0b\u4e86\u53f0\u3002\u6211\u60f3\uff0c\u4e00\u5b9a\u662f\u6709\u4eba\u8eb2\u5728\u6697\u4e2d\u6574\u6cbb\u4ed6\uff01\u201d \u3000\u3000\u59da\u987a\u5929\u542b\u7b11\u4e0d\u8bed\u3002 \u3000\u3000\u738b\u5c11\u537f\u770b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u90a3\u4e2a\u4eba\u5c31\u662f\u59da\u8001\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u59da\u987a\u5929\u7b54\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u59da\u67d0\u4e00\u76f4\u662f\u9075\u7167\u5211\u90e8\u548c\u603b\u6355\u5934\u7684\u547d\u4ee4\u884c\u4e8b\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u76db\u4f20\u524d\u4efb\u77e5\u5e9c\u540e\u9762\u6709\u4e24\u4f4d\u738b\u7237\u6491\u8170\uff0c\u90a3\u4e48\uff0c\u90a3\u4f4d\u67d0\u738b\u7237\u4e00\u5b9a\u662f\u8fd9\u4e24\u4f4d\u738b\u7237 \u5176\u4e2d\u4e4b\u4e00\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5927\u4eba\uff0c\u6ca1\u6709\u62ff\u5230\u771f\u51ed\u5b9e\u636e\u4e4b\u524d\uff0c\u6211\u4eec\u4e0d\u80fd\u4e71\u731c\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e5f\u7528\u4e0d\u7740\u4e71\u731c\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u59da\u8001\uff0c\u626c\u5dde\u90a3\u8fb9\u60c5\u51b5\u5982\u4f55\uff1f\u201d \u3000\u3000\u59da\u987a\u5929\u9053\uff1a\u201c\u5f88\u5e73\u9759\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u6211\u4eec\u8bf1\u654c\u51fa\u51fb\u7684\u8ba1\u5212\u5df2\u7ecf\u6210\u529f\u4e86\uff1f\u201d \u3000\u3000\u59da\u987a\u5929\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u5411\u5c71\u5761\u4e0b\u6740\u624b\u9635\u4e2d\u770b\u4e86\u770b\uff0c\u76b1\u7709\u9053\uff1a\u201c\u53ef\u662f\uff0c\u8840\u624b\u65b9\u8f6e\u8fd8\u6ca1\u6709\u51fa\u73b0\uff01\u201d \u3000\u3000\u59da\u987a\u5929\u9053\uff1a\u201c\u603b\u6355\u5934\u653e\u5fc3\uff0c\u59da\u67d0\u4e00\u73b0\u8eab\uff0c\u65b9\u8f6e\u5f88\u5feb\u5c31\u4f1a\u51fa\u73b0\u7684\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u59da\u987a\u5929\u9053\uff1a\u201c\u6740\u624b\u96c6\u56e2\u7684\u9996\u8111\u4eec\u4e5f\u7b97\u5230\u4e86\u603b\u6355\u5934\u4f1a\u4f0f\u4e0b\u4e00\u652f\u6216\u51e0\u652f\u63f4\u5175\uff0c\u4f46\u59da\u67d0\u4e00\u73b0\u8eab\uff0c \u4ed6\u4eec\u4f1a\u8ba4\u4e3a\u6211\u4eec\u7684\u63f4\u5175\u5df2\u7ecf\u5c3d\u51fa\uff0c\u4ed6\u4eec\u4e5f\u5c31\u7528\u4e0d\u7740\u7559\u4e00\u624b\u4e86\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u66f4\u91cd\u8981\u7684\u662f\uff0c\u65b9\u624d\u4e00\u6218\uff0c\u6740\u624b\u4eec\u5df2\u7ecf\u770b\u5230\u4e86\u59da\u8001\u90e8\u4e0b\u7684\u5b9e\u529b\uff0c\u5982\u679c\u4e0d\u8c03\u51fa \u65b9\u8f6e\uff0c\u4ed6\u4eec\u4e5f\u6ca1\u6709\u5fc5\u80dc\u7684\u628a\u63e1\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u6307\u7740\u5c71\u5761\u4e0b\uff0c\u9053\uff1a\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u4ed6\u4eec\u73b0\u5728\u6b63\u5546\u91cf\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5979\u6c89\u541f\u4e86\u4e00\u4f1a\u513f\uff0c\u9053\uff1a\u201c\u7b49\u6218\u4e8b\u518d\u8d77\uff0c\u59da\u8001\u548c\u56db\u5927\u6355\u5934\uff0c\u60dc\u7389\u6682\u4e14\u4e0d\u8981\u52a8\u624b\uff0c\u4ee5\u9632\u5907\u65b9 \u8f6e\u7a81\u7136\u51fa\u73b0\uff01\u201d \u3000\u3000\u59da\u987a\u5929\u9053\uff1a\u201c\u662f\uff0c\u5c5e\u4e0b\u542c\u5019\u603b\u6355\u5934\u8c03\u9063\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u8fdb\u653b\u5462\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5f88\u663e\u7136\uff0c\u4ed6\u4eec\u5728\u7b49\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u7b49\u65b9\u8f6e\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7b49\u65b9\u8f6e\uff0c\u540c\u65f6\uff0c\u4e5f\u5728\u7b49\u6211\u4eec\u662f\u4e0d\u662f\u8fd8\u6709\u63f4\u5175\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u4ed6\u4f55\u6709\u53ef\u80fd\u5f00\u59cb\u6000\u7591\u662f\u4e0d\u662f\u4e2d\u8ba1\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u76b1\u7709\u9053\uff1a\u201c\u5f88\u6709\u53ef\u80fd\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u5982\u679c\u4ed6\u4eec\u73b0\u5728\u9000\u8d70\uff0c\u6211\u4eec\u80fd\u7559\u4e0b\u4ed6\u4eec\u5417\uff1f\u201d \u3000\u3000\u59da\u987a\u5929\u9053\uff1a\u201c\u51ed\u6211\u4eec\u7684\u5b9e\u529b\uff0c\u6050\u6015\u5f88\u96be\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6211\u6709\u4e00\u5efa\u8bae\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u59da\u8001\u7684\u90e8\u4e0b\u8fdc\u9053\u800c\u6765\uff0c\u4f60\u4eec\u4e5f\u90fd\u53d7\u4e86\u4f24\uff0c\u6211\u7684\u5185\u529b\u4e00\u65f6\u8fd8\u4e0d\u53ef\u80fd\u6062\u590d\uff0c\u73b0 \u5728\u8fdb\u653b\u2026\u2026\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u53ea\u6709\u6211\u4eec\u4e3b\u52a8\u8fdb\u653b\uff0c\u624d\u80fd\u7559\u4f4f\u6740\u624b\u4eec\uff0c\u4f7f\u4ed6\u4eec\u4ee5\u4e3a\u8fd9\u5df2\u662f\u6211\u4eec\u6240\u6709\u7684\u5b9e\u529b\uff0c \u4ed6\u4eec\u624d\u4f1a\u8c03\u65b9\u8f6e\u51fa\u9762\u3002\u201d \u3000\u3000\u59da\u987a\u5929\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u675c\u53f8\u4e3b\u8bf4\u7684\u5f88\u6709\u9053\u7406\u5440\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u90a3\u597d\uff01\u201d \u3000\u3000\u60f3\u4e86\u60f3\uff0c\u53c8\u95ee\u59da\u987a\u5929\uff1a\u201c\u4ed6\u4eec\u8fd8\u9700\u591a\u957f\u65f6\u95f4\u624d\u80fd\u8d76\u5230\uff1f\u201d \u3000\u3000\u59da\u987a\u5929\u9053\uff1a\u201c\u6700\u591a\u534a\u4e2a\u65f6\u8fb0\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u90a3\u59da\u8001\u7684\u90e8\u4e0b\u635f\u5931\u53ef\u80fd\u4f1a\u5f88\u60e8\u91cd\u554a\uff01\u201d \u3000\u3000\u59da\u987a\u5929\u9053\uff1a\u201c\u517b\u5175\u5343\u65e5\uff0c\u7528\u5175\u4e00\u65f6\uff0c\u4ed6\u4eec\u90fd\u4e0d\u6015\u6b7b\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6df1\u6df1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u59da\u8001\uff0c\u4f60\u5e26\u5341\u540d\u597d\u624b\u7559\u5728\u6b64\u5730\uff0c\u5176\u4f59\u7684\u4eba\uff0c\u8ddf\u6211\u6765\uff01\u201d \u3000\u3000\u59da\u987a\u5929\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u4f60\u7684\u5185\u4f24\u2026\u2026\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u534a\u4e2a\u65f6\u8fb0\u8fd8\u662f\u80fd\u575a\u6301\u7684\uff0c\u800c\u4e14\u6211\u5df2\u7ecf\u670d\u4e0b\u4e00\u7c92\u5c11\u6797\u5927\u8fd8\u4e39\uff0c\u5185\u4f24 \u4e00\u65f6\u4e0d\u4f1a\u53d1\u4f5c\uff01\u201d\u5979\u5bf9\u5c0f\u6587\u9053\uff1a\u201c\u5c0f\u6587\uff0c\u4f60\u548c\u60dc\u7389\u4e5f\u7559\u4e0b\uff0c\u5c0f\u96c5\uff0c\u4f60\u8ddf\u6211\u6765\uff01\u201d \u3000\u3000\u5c0f\u96c5\u63d0\u7740\u5b9d\u5251\u4e00\u8dc3\u800c\u8d77\uff0c\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u548c\u5c0f\u96c5\u7387\u9886\u59da\u987a\u5929\u624b\u4e0b\u5341\u4f59\u540d\u9ad8\u624b\uff0c\u51b2\u4e0b\u5c71\u5761\uff0c\u76f4\u5411\u6740\u624b\u5927\u9635\u51b2\u53bb\u3002\u6740\u624b\u4eec\u6beb\u4e0d \u754f\u60e7\uff0c\u4e5f\u731b\u6251\u4e86\u4e0a\u6765\uff01 \u3000\u3000\u6df7\u6218\u7acb\u523b\u5f00\u59cb\uff01 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u5df2\u53ef\u79f0\u5f97\u4e0a\u662f\u4e00\u573a\u52bf\u5747\u529b\u654c\u7684\u6218\u6597\u4e86\u3002 \u3000\u3000\u6740\u624b\u4eec\u5728\u4eba\u6570\u4e0a\u591a\u51fa\u4e86\u4e94\u516d\u500d\uff0c\u4f46\u59da\u987a\u5929\u624b\u4e0b\u4e4b\u4eba\u7684\u6b66\u529f\u90fd\u5f88\u9ad8\u5f3a\u3002 \u3000\u3000\u4e00\u65f6\u95f4\uff0c\u8352\u91ce\u4e4b\u4e0a\u6740\u58f0\u9707\u5929\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u5728\u4e71\u519b\u4e4b\u4e2d\u6325\u67aa\u51b2\u6740\uff0c\u5f88\u60f3\u518d\u627e\u5230\u9ed1\u8863\u8499\u9762\u4eba\u548c\u4e0d\u8001\u4e66\u751f\uff0c\u56e0\u4e3a\u5979\u60f3\u5c3d\u529b\u7f20\u4f4f\u4ed6 \u4eec\uff0c\u5c3d\u91cf\u4fdd\u5b58\u4e00\u70b9\u59da\u987a\u5929\u624b\u4e0b\u7684\u9ad8\u624b\u3002 \u3000\u3000\u90a3\u4e9b\u4eba\u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u5982\u9047\u4e0a\u9ed1\u8863\u8499\u9762\u4eba\u6216\u4e0d\u8001\u4e66\u751f\uff0c\u5927\u6982\u5f88\u96be\u652f\u6491\u4e0a\u5341\u62db\uff01\u4f46\u5de6\u51b2\u53f3\u7a81\uff0c \u6218\u4e86\u534a\u5929\uff0c\u6b7b\u4e8e\u5979\u67aa\u4e0b\u7684\u6740\u624b\u5df2\u8fbe\u4e03\u5341\u4f59\u4eba\uff0c\u5979\u8fd8\u662f\u6ca1\u6709\u9047\u4e0a\u4e0d\u8001\u4e66\u751f\u548c\u9ed1\u8863\u8499\u9762\u4eba\uff01 \u3000\u3000\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u96be\u9053\u8fd9\u4e8c\u4eba\u5df2\u770b\u51fa\u60c5\u51b5\u4e0d\u5bf9\uff0c\u5e26\u7740\u5c11\u6570\u5fc3\u8179\u9003\u8d70\u4e86\uff1f \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u7a0b\u5c0f\u8776\u542c\u89c1\u8eab\u508d\u7684\u5c0f\u96c5\u60ca\u53eb\u4e86\u4e00\u58f0\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u56de\u5934\uff0c\u4e0d\u7981\u9762\u8272\u5927\u53d8\uff01 \u3000\u3000\u539f\u6765\u5979\u53d1\u73b0\uff0c\u9ed1\u8863\u8499\u9762\u4eba\u548c\u4e0d\u8001\u4e66\u751f\u5df2\u7ecf\u7387\u9886\u7740\u4e8c\u5341\u4f59\u540d\u9ed1\u8863\u5251\u624b\uff0c\u653b\u4e0a\u4e86\u5c71\u5761\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u79bb\u738b\u5c11\u537f\u7b49\u4eba\u53ea\u6709\u5341\u51e0\u6b65\u8fdc\u4e86\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u5927\u6025\u4e4b\u4e0b\uff0c\u4e00\u53e3\u9c9c\u8840\u5df2\u6d8c\u5230\u4e86\u55d3\u5b50\u773c\u3002 \u3000\u3000\u4f46\u5979\u53c8\u5c06\u9c9c\u8840\u786c\u751f\u751f\u54bd\u4e86\u56de\u53bb\uff01 \u3000\u3000\u5979\u6df1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u957f\u67aa\u5de6\u6311\u53f3\u523a\uff0c\u67aa\u6740\u4e86\u6d8c\u4e0a\u6765\u60f3\u7f20\u4f4f\u5979\u7684\u5341\u4f59\u540d\u6740\u624b\uff0c\u62cd\u9a6c\u5411\u5c71\u5761\u51b2 \u53bb\u3002 \u3000\u3000\u5c0f\u96c5\u98de\u8eab\u63a0\u8d77\uff0c\u7d27\u968f\u5176\u540e\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u7387\u4eba\u51b2\u4e0b\u5c71\u5761\u65f6\uff0c\u675c\u671b\u6708\u5df2\u7ecf\u60f3\u5230\u60c5\u51b5\u6709\u4e9b\u4e0d\u5bf9\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u770b\u89c1\u5728\u9ed1\u8863\u8499\u9762\u4eba\u548c\u4e0d\u8001\u4e66\u751f\u7684\u6307\u6325\u4e0b\uff0c\u6709\u4e8c\u5341\u4f59\u540d\u9ed1\u8863\u5251\u624b\u96c6\u4e2d\u5230\u4e86\u4ed6\u4eec\u8eab\u8fb9\uff0c \u800c\u5176\u4f59\u7684\u6740\u624b\u6b63\u6539\u53d8\u9635\u5f62\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u9635\u5f62\u7531\u65b9\u9635\u53d8\u6210\u4e00\u4e2a\u534a\u5706\u5f62\u3002 \u3000\u3000\u5f88\u663e\u7136\uff0c\u6740\u624b\u4eec\u662f\u60f3\u5c06\u7a0b\u5c0f\u8776\u7b49\u4eba\u56f4\u4f4f\u3002 \u3000\u3000\u90a3\u4e48\uff0c\u9ed1\u8863\u8499\u9762\u4eba\u548c\u4e0d\u8001\u4e66\u751f\u5728\u8eab\u8fb9\u96c6\u4e2d\u4e86\u4e8c\u5341\u4f59\u540d\u6740\u624b\u4e2d\u6700\u7cbe\u5f3a\u7684\u9ed1\u8863\u5251\u624b\uff0c\u662f\u6709\u4ec0 \u4e48\u4f01\u56fe\u5462\uff1f \u3000\u3000\u5f88\u5feb\uff0c\u4ed6\u5c31\u77e5\u9053\u7b54\u6848\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u7387\u4eba\u51b2\u5165\u6740\u624b\u9635\u4e2d\uff0c\u679c\u7136\u7acb\u523b\u88ab\u6740\u624b\u4eec\u56f4\u4f4f\u4e86\u3002 \u3000\u3000\u4e0d\u8001\u4e66\u751f\u548c\u9ed1\u8863\u8499\u9762\u4eba\u5374\u7387\u7740\u90a3\u4e8c\u5341\u4f59\u540d\u9ed1\u8863\u5251\u624b\uff0c\u98de\u901f\u5411\u5c71\u5761\u4e0a\u51b2\u6765\u3002\u675c\u671b\u6708\u9053\uff1a \u201c\u59da\u8001\uff0c\u8bf7\u4f60\u7387\u4eba\u4fdd\u62a4\u738b\u5927\u4eba\u3002\u5c0f\u6587\uff0c\u6211\u4eec\u53bb\u6321\u4f4f\u4ed6\u4eec\uff01\u201d \u3000\u3000\u59da\u987a\u5929\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u675c\u53f8\u4e3b\uff0c\u8fd8\u662f\u4f60\u4e0e\u5c0f\u6587\u59d1\u5a18\u4fdd\u62a4\u738b\u5927\u4eba\uff0c\u6211\u7387\u4eba\u6321\u4f4f\u4ed6\u4eec\uff01\u675c\u53f8 \u4e3b\uff0c\u4f60\u653e\u5fc3\uff0c\u5bf9\u4e0d\u8001\u4e66\u751f\u548c\u90a3\u4f4d\u9ed1\u8863\u8499\u9762\u4eba\u59da\u67d0\u6709\u4e00\u5b9a\u7684\u4e86\u89e3\uff0c\u6211\u81ea\u4fe1\uff0c\u80fd\u4e0e\u9ed1\u8863\u8499\u9762\u4eba\u7f20 \u9619\u767e\u62db\u800c\u4e0d\u8d25\uff0c\u800c\u6211\u624b\u4e0b\u8fd9\u5341\u4eba\u4e4b\u4e2d\uff0c\u53ea\u8981\u6d3e\u51fa\u4e24\u4eba\uff0c\u5e94\u8be5\u5c31\u80fd\u7f20\u4f4f\u4e0d\u8001\u4e66\u751f\uff0c\u4f59\u4e0b\u7684\u4eba\u53ef \u4ee5\u5168\u529b\u683c\u6740\u90a3\u4e8c\u5341\u4f59\u540d\u9ed1\u8863\u5251\u624b\u4e86\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u59da\u8001\u5bf9\u9ed1\u8863\u8499\u9762\u4eba\u6709\u4e00\u5b9a\u7684\u4e86\u89e3\uff1f\u201d \u3000\u3000\u59da\u987a\u5929\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u77e5\u9053\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u59da\u987a\u5929\u9053\uff1a\u201c\u6211\u5927\u81f4\u80fd\u731c\u5230\uff0c\u4f46\u8fd8\u4e0d\u6562\u80af\u5b9a\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u597d\u5427\uff0c\u59da\u8001\uff0c\u4f60\u8981\u5c0f\u5fc3\uff0c\u90a3\u9ed1\u8863\u8499\u9762\u4eba\u6b66\u529f\u53ef\u9ad8\u3002\u8fde\u603b\u6355\u5934\u4e5f\u4e0d\u662f\u4ed6\u7684\u5bf9 \u624b\u5462\uff01\u201d \u3000\u3000\u59da\u987a\u5929\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u4f1a\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u4f46\u662f\u6211\u4e0d\u6015\u4ed6\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u9ed1\u8863\u8499\u9762\u4eba\u548c\u4e0d\u8001\u4e66\u751f\u5df2\u7387\u4eba\u51b2\u4e0a\u4e86\u5c71\u5761\u3002 \u3000\u3000\u59da\u987a\u5929\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e8c\u4eba\uff0c\u7f20\u4f4f\u4e0d\u8001\u4e66\u751f\uff01\u4e0a\uff01\u201d \u3000\u3000\u4ed6\u5df2\u81ea\u8170\u95ee\u62bd\u51fa\u4e86\u4e00\u67c4\u957f\u5251\u76f4\u6251\u5411\u9ed1\u8863\u8499\u9762\u4eba\u3002 \u3000\u3000\u4ed6\u624b\u4e2d\u7684\u957f\u5251\u76f4\u523a\u8499\u9762\u4eba\u7684\u80f8\u53e3\uff01 \u3000\u3000\u8499\u9762\u4eba\u5de6\u624b\u77ed\u5200\u67b6\u4f4f\u957f\u5251\uff0c\u53f3\u624b\u957f\u5200\u81ea\u659c\u4e0b\u65b9\u5411\u4e0a\u6325\u8d77\uff0c\u523a\u5411\u59da\u987a\u5929\u817f\u90e8\uff01\u773c\u770b\u59da\u987a\u5929 \u4e00\u6761\u817f\u5373\u5c06\u4e0e\u8eab\u4f53\u5206\u5bb6\uff0c\u4ed6\u5374\u5728\u8fd9\u7535\u5149\u706b\u77f3\u95f4\uff0c\u4ece\u4e00\u4e2a\u770b\u4f3c\u7edd\u4e0d\u53ef\u80fd\u7684\u89d2\u5ea6\u626d\u52a8\u4e86\u4e00\u4e0b\u8eab\u5b50\uff0c \u8eb2\u8fc7\u4e86\u8fd9\u4e00\u5200\u3002 \u3000\u3000\u800c\u4e14\uff0c\u4ed6\u7684\u957f\u5251\u5df2\u6cbf\u7740\u8499\u9762\u4eba\u77ed\u5200\u7684\u5200\u8eab\u75be\u6ed1\u4e0b\u6765\uff0c\u76f4\u524a\u9ed1\u8863\u8499\u9762\u4eba\u7684\u624b\u8155\u3002\u9ed1\u8863\u8499\u9762 \u4eba\u5413\u4e86\u4e00\u8df3\u3002 \u3000\u3000\u59da\u987a\u5929\u6b66\u529f\u4e4b\u9ad8\uff0c\u663e\u7136\u4e0d\u51fa\u4ed6\u7684\u9884\u6599\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u5de6\u624b\u659c\u5212\uff0c\u53f3\u624b\u957f\u5200\u633d\u4e86\u4e2a\u5200\u82b1\uff0c\u524c\u5411\u59da\u987a\u5929\u80f8\u53e3\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u8fde\u524a\u5e26\u6253\uff0c\u5999\u6781\u3002 \u3000\u3000\u4f46\u59da\u987a\u5929\u4ecd\u7136\u8f7b\u677e\u907f\u8fc7\u4e86\uff0c\u800c\u4e14\u4ed6\u624b\u4e2d\u957f\u5251\u4e0d\u505c\uff0c\u8fde\u523a\u5411\u9ed1\u8863\u8499\u9762\u4eba\u7684\u5de6\u80a9\u5de6\u8155\u548c\u5de6\u9888 \u524a\u51fa\u4e09\u5251\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u5411\u540e\u8df3\u51fa\u4e09\u56db\u6b65\u8fdc\uff0c\u559d\u9053\uff1a\u201c\u597d\u4e00\u62db\u9633\u5173\u4e09\u53e0\uff01\u4f60\u4e0d\u662f\u59da\u987a\u5929\uff01\u201d \u3000\u3000\u59da\u987a\u5929\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5341\u4e8c\u5e74\u524d\uff0c\u8001\u592b\u662f\u7684\u786e\u4e0d\u53eb\u59da\u987a\u5929\uff01\u90a3\u4e48\uff0c\u4f60\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u8001\u592b\u662f\u68a6\u5e7b\u4e4b\u5200\uff01\u201d \u3000\u3000\u59da\u987a\u5929\u5927\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u8981\u662f\u68a6\u5e7b\u4e4b\u5200\uff0c\u6211\u5c31\u662f\u5c11\u6797\u638c\u95e8\uff0c\u6b66\u5f53\u638c\u6559\u4e86\uff01\u4f60\u4e0d\u662f\u68a6\u5e7b\u4e4b \u5200\uff01\u5728\u5341\u4e8c\u5e74\u524d\uff0c\u8001\u592b\u4e0e\u68a6\u5e7b\u4e4b\u5200\u4ea4\u8fc7\u624b\uff0c\u7ed3\u679c\uff0c\u4e0d\u8fc7\u4e00\u62db\u95f4\uff0c\u8001\u592b\u5c31\u5df2\u8d25\u9000\uff01\u4f60\u82e5\u662f\u68a6\u5e7b \u4e4b\u5200\uff0c\u53c8\u600e\u4f1a\u88ab\u8001\u592b\u8fd9\u51e0\u62db\u8fde\u8fde\u5f97\u624b\uff0c\u65e0\u6cd5\u62b5\u6321\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u559d\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u770b\u5200\uff01\u201d \u3000\u3000\u4e00\u957f\u4e00\u77ed\u4e24\u67c4\u5200\u5316\u505a\u4e24\u56e2\u5149\uff0c\u76f4\u7f69\u5411\u59da\u987a\u5929\uff01 \u3000\u3000\u8fd9\u8fb9\u6253\u5f97\u70ed\u95f9\uff0c\u4e0d\u8001\u4e66\u751f\u4e5f\u4e0d\u6e05\u95f2\u3002 \u3000\u3000\u59da\u987a\u5929\u624b\u4e0b\u4e24\u540d\u9752\u8863\u4eba\u4e00\u5200\u4e00\u68cd\uff0c\u5df2\u5c06\u4e0d\u8001\u4e66\u751f\u7d27\u7f20\u4f4f\u3002 \u3000\u3000\u4e0d\u8001\u4e66\u751f\u8fde\u53d8\u4e86\u4e09\u79cd\u5251\u6cd5\uff0c\u5374\u4ecd\u5360\u4e0d\u4e86\u4e0a\u98ce\uff0c\u5fc3\u4e2d\u4e00\u6025\uff0c\u53c8\u4f7f\u51fa\u4e86\u201c\u5fc5\u6740\u5341\u4e09\u5251\u201d\uff01 \u3000\u3000\u5251\u4e00\u51fa\u624b\uff0c\u5f62\u52bf\u7acb\u53d8\u3002 \u3000\u3000\u7b2c\u56db\u5251\u523a\u51fa\uff0c\u4f7f\u5200\u7684\u9752\u8863\u4eba\u624b\u4e2d\u5251\u3001\u5355\u5200\u843d\u5730\u3002 \u3000\u3000\u7b2c\u4e03\u5251\u523a\u51fa\uff0c\u4f7f\u68cd\u7684\u9752\u8863\u4eba\u7ffb\u5012\u5728\u5730\uff0c\u817f\u4e0a\u8840\u6d41\u5982\u6ce8\uff01 \u3000\u3000\u53e6\u516b\u540d\u9752\u8863\u4eba\u6b63\u4e0e\u4e8c\u5341\u4f59\u540d\u9ed1\u8863\u5251\u624b\u7f20\u95fb\uff0c\u53cc\u65b9\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u817e\u51fa\u624b\u6765\u3002\u4e0d\u8001\u4e66\u751f\u957f\u5251 \u9f50\u7709\uff0c\u75be\u51b2\u5411\u738b\u5c11\u537f\uff01 \u3000\u3000\u675c\u671b\u6708\u4e00\u6325\u624b\u4e2d\u4e03\u661f\u5b9d\u5251\uff0c\u8fce\u4f4f\u4e86\u4ed6\uff01 \u3000\u3000\u5341\u62db\u4e00\u8fc7\uff0c\u4e0d\u8001\u4e66\u751f\u5df2\u5904\u4e8e\u4e86\u4e0b\u98ce\u3002 \u3000\u3000\u675c\u671b\u6708\u4e00\u5f00\u59cb\u4fbf\u4f7f\u51fa\u4e86\u201c\u5251\u6d77\u6d74\u9b42\u201d\u5251\u6cd5\uff0c\u800c\u8fd9\u79cd\u5251\u6cd5\u6b63\u662f\u4e0d\u8001\u4e66\u751f\u7684\u514b\u661f\uff01\u4e0d\u8001\u4e66\u751f \u957f\u5251\u6025\u901f\u5730\u7ffb\u98de\uff0c\u5374\u6321\u4e0d\u4f4f\u675c\u671b\u6708\u653b\u4e0a\u7684\u91cd\u91cd\u5251\u5f71\u3002 \u3000\u3000\u597d\u51e0\u6b21\uff0c\u675c\u671b\u6708\u7684\u5251\u5c16\u5df2\u5dee\u4e00\u70b9\u5212\u7834\u4e86\u4ed6\u7684\u8863\u886b\u3002 \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u59da\u987a\u5929\u53d1\u51fa\u4e86\u4e00\u58f0\u60ca\u543c\u58f0\u3002 \u3000\u3000\u675c\u671b\u6708\u4e00\u62db\u9010\u9000\u4e0d\u8001\u4e66\u751f\uff0c\u5411\u90a3\u8fb9\u770b\u53bb\u3002 \u3000\u3000\u59da\u987a\u5929\u4e00\u6761\u817f\u8dea\u5728\u5730\u4e0a\uff0c\u53cc\u624b\u63e1\u5251\uff0c\u7aed\u529b\u6321\u67b6\u7740\u9ed1\u8863\u8499\u9762\u4eba\u53cc\u624b\u75af\u72c2\u7684\u8f6e\u756a\u780d\u6740\uff01\u4ed6\u7684 \u5de6\u817f\u4e4b\u4e0a\uff0c\u63d2\u7740\u4e00\u67c4\u95ea\u4eae\u7684\u98de\u5200\uff01 \u3000\u3000\u59da\u987a\u5929\u4e00\u8fb9\u6321\u67b6\uff0c\u4e00\u8fb9\u6012\u543c\u9053\uff1a\u201c\u4f60\u4f7f\u6697\u5668\uff01\u597d\u4e0d\u8981\u8138\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u72de\u7b11\u9053\uff1a\u201c\u4f60\u8981\u77e5\u9053\uff0c\u5386\u53f2\u662f\u80dc\u5229\u8005\u7684\uff0c\u53ea\u8981\u6211\u6740\u4e86\u4f60\uff0c\u53c8\u6709\u8c01\u8fd8\u77e5\u9053\u6211\u662f \u7528\u6697\u5668\u6740\u4f60\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u6012\u6c14\u52c3\u53d1\uff0c\u5927\u543c\u9053\uff1a\u201c\u6211\uff0c\u6211\u77e5\u9053\uff01\u4eca\u5929\u5728\u573a\u7684\u4eba\u90fd\u77e5\u9053\uff01\u201d \u3000\u3000\u4eba\u968f\u58f0\u5230\uff0c\u5251\u968f\u58f0\u5230\uff0c\u4e00\u9053\u96ea\u4eae\u7684\u5251\u5f71\u76f4\u523a\u9ed1\u8863\u8499\u9762\u4eba\uff01 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u4e0d\u5f97\u5df2\uff0c\u56de\u5200\u8fce\u654c\u3002 \u3000\u3000\u4e0d\u8001\u4e66\u751f\u5df2\u4e0e\u51b2\u4e0a\u7684\u5c0f\u6587\u6597\u5728\u4e00\u8d77\u3002 \u3000\u3000\u59da\u987a\u5929\u4f38\u51fa\u624b\uff0c\u4f7f\u52b2\u54ac\u4f4f\u4e86\u7259\u5173\uff0c\u60f3\u5c06\u817f\u4e0a\u7684\u98de\u5200\u62d4\u51fa\u6765\u3002 \u3000\u3000\u60dc\u7389\u5927\u53eb\u9053\uff1a\u201c\u59da\u8001\uff0c\u4e0d\u8981\u62d4\uff0c\u5200\u4e0a\u6709\u6bd2\uff0c\u5982\u8fd9\u6837\u62d4\u51fa\uff0c\u6bd2\u6c14\u5c31\u4f1a\u9006\u8840\u800c\u751f\uff0c\u540e\u679c\u5c06\u4e0d \u582a\u8bbe\u60f3\uff01\u201d \u3000\u3000\u59da\u987a\u5929\u4e00\u5446\uff0c\u9053\uff1a\u201c\u90a3\u600e\u4e48\u529e\uff1f\u4e0d\u62d4\u51fa\u98de\u5200\uff0c\u59da\u67d0\u65e0\u6cd5\u53c2\u52a0\u6218\u5f00\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u59da\u8001\uff0c\u4f60\u8fc7\u6765\uff0c\u6211\u6765\u66ff\u4f60\u5904\u7406\u5200\u4f24\uff01\u201d \u3000\u3000\u59da\u987a\u5929\u52aa\u529b\u7ad9\u8d77\u8eab\u5b50\uff0c\u6447\u6447\u6643\u6643\u8d70\u5411\u60dc\u7389\u90a3\u8fb9\u3002 \u3000\u3000\u521a\u8d70\u51fa\u51e0\u6b65\uff0c\u4ed6\u7a81\u7136\u89c9\u5f97\u80cc\u4e0a\u4e00\u75db\uff0c\u4e00\u51c9\u3002\u7136\u540e\uff0c\u4ed6\u542c\u89c1\u4e86\u7a0b\u5c0f\u8776\u7684\u53eb\u58f0\uff1a\u201c\u59da\u8001\uff0c\u5c0f \u5fc3\u8eab\u540e\uff01\u201d \u3000\u3000\u59da\u987a\u5929\u5c31\u5730\u4e00\u6eda\uff0c\u8fd4\u8eab\u4e00\u770b\uff0c\u5374\u662f\u4e24\u67c4\u957f\u5251\u5df2\u523a\u5230\u4ed6\u8eab\u4e0a\u3002 \u3000\u3000\u4ed6\u60f3\u51fa\u5251\u683c\u6321\uff0c\u4f46\u53f3\u81c2\u7adf\u5df2\u63d0\u4e0d\u8d77\u6765\uff01 \u3000\u3000\u4e24\u67c4\u5251\u53c8\u5df2\u903c\u8fd1\u4e86\u4e24\u5c3a\uff0c\u5251\u5149\u5df2\u9876\u7834\u4e86\u4ed6\u7684\u8863\u886b\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u8fd9\u4e24\u540d\u9ed1\u8863\u5251\u624b\u957f\u5251\u8131\u624b\uff0c\u4eba\u5374\u5df2\u98de\u5728\u4e86\u534a\u7a7a\u4e2d\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u5c31\u770b\u89c1\u4e86\u7a0b\u5c0f\u8776\u3002 \u3000\u3000\u90a3\u4e24\u540d\u9ed1\u8863\u5251\u624b\u662f\u88ab\u7a0b\u5c0f\u8776\u4e00\u67aa\u6311\u98de\u7684\uff01 \u3000\u3000\u5c0f\u96c5\u6251\u4e0a\u6765\u6276\u8d77\u4ed6\uff0c\u8d70\u5230\u4e86\u60dc\u7389\u8eab\u8fb9\u3002 \u3000\u3000\u60dc\u7389\u7acb\u523b\u5f00\u59cb\u66ff\u4ed6\u7597\u4f24\u3001\u89e3\u6bd2\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u957f\u5251\u523a\u51fa\uff0c\u63a5\u4f4f\u4e86\u9ed1\u8863\u8499\u9762\u4eba\u7684\u53cc\u5200\uff0c\u5bf9\u675c\u671b\u6708\u9053\uff1a\u201c\u5feb\u53bb\u5e2e\u4ed6\u4eec\uff01\u201d\u5979\u5de6\u624b\u4e00 \u6307\uff0c\u6307\u5411\u5df2\u4e0e\u4e8c\u5341\u4f59\u540d\u9ed1\u8863\u5251\u624b\u640f\u6740\u7684\u516b\u540d\u9752\u8863\u4eba\uff01 \u3000\u3000\u675c\u671b\u6708\u7b54\u5e94\u4e00\u58f0\uff0c\u98de\u8eab\u6251\u4e0a\uff01 \u3000\u3000\u5979\u4e00\u52a0\u5165\u6218\u677f\uff0c\u957f\u5251\u5c31\u523a\u7ffb\u4e86\u4e24\u540d\u9ed1\u8863\u5251\u624b\uff01 \u3000\u3000\u5c0f\u6587\u529b\u654c\u4e0d\u8001\u4e66\u751f\uff0c\u7a0b\u5c0f\u8776\u82e6\u6597\u9ed1\u8863\u8499\u9762\u4eba\uff0c\u867d\u8bf4\u90fd\u4e0d\u80fd\u53d6\u80dc\uff0c\u4f46\u4e00\u65f6\u4e4b\u95f4\u4e5f\u4e0d\u4f1a\u8d25\u4e0b \u9635\u6765\u3002\u5728\u8fd9\u5c71\u5761\u4e4b\u4e0a\uff0c\u5f62\u52bf\u53d8\u6210\u4e86\u50f5\u6301\u7684\u5c40\u9762\u3002 \u3000\u3000\u4f46\u5c71\u5761\u4e4b\u4e0b\uff0c\u59da\u987a\u5929\u90e8\u4e0b\u90a3\u51e0\u5341\u540d\u9752\u8863\u4eba\u5374\u5df2\u5728\u6570\u767e\u6740\u624b\u7684\u5305\u56f4\u3001\u653b\u6740\u3001\u903c\u8feb\u4e4b\u4e0b\uff0c\u6162 \u6162\u5411\u5c71\u5761\u8fd9\u8fb9\u9000\u8fc7\u6765\uff01\u4e00\u4f1a\u513f\uff0c\u9752\u8863\u4eba\u548c\u6740\u624b\u4eec\u90fd\u5df2\u805a\u5230\u4e86\u5c71\u5761\u4e4b\u4e0b\uff0c\u9752\u8863\u4eba\u867d\u8bf4\u5168\u90fd\u62fc\u6b7b \u640f\u6740\uff0c\u4f46\u4ecd\u963b\u6321\u4e0d\u4f4f\u6740\u624b\u4eec\u524d\u8fdb\u7684\u52bf\u5934\u3002\u7a0b\u5c0f\u8776\u5927\u547c\u9053\uff1a\u201c\u5feb\uff0c\u90fd\u9000\u4e0b\u6765\uff0c\u5f80\u5c71\u5761\u4e0b\u9000\uff01\u201d \u3000\u3000\u51cc\u7a7a\uff0c\u5979\u8fd0\u67aa\u5982\u98ce\uff0c\u4e00\u53e3\u6c14\u5411\u9ed1\u8863\u8499\u9762\u4eba\u523a\u51fa\u4e86\u5341\u4e5d\u67aa\u3002 \u3000\u3000\u8fd9\u4e5f\u662f\u201c\u9738\u738b\u65e0\u654c\u201d\u67aa\u6cd5\u4e2d\u7684\u7edd\u6280\u4e4b\u4e00\u201c\u5341\u4e5d\u8fde\u73af\u6740\u201d\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u987f\u65f6\u88ab\u903c\u5f97\u76f4\u5f80\u540e\u9000\uff01 \u3000\u3000\u4f46\u8fd9\u5341\u4e5d\u67aa\u4e5f\u6ca1\u6709\u4e00\u67aa\u80fd\u523a\u4e2d\u4ed6\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u594b\u529b\u523a\u51fa\u8fd9\u5341\u4e5d\u67aa\uff0c\u53e3\u4e2d\u53c8\u5410\u51fa\u4e86\u4e00\u5927\u80a1\u9c9c\u8840\uff01 \u3000\u3000\u5979\u672c\u5df2\u53d7\u4e86\u5185\u4f24\uff0c\u53c8\u5984\u52a8\u771f\u6c14\uff0c\u7ed3\u679c\uff0c\u5185\u4f24\u66f4\u4e25\u91cd\u4e86\uff01 \u3000\u3000\u4f46\u5979\u4e5f\u4e3a\u5927\u5bb6\u8d62\u5f97\u4e86\u64a4\u79bb\u6218\u5f00\u7684\u65f6\u95f4\u3002 \u3000\u3000\u9752\u8863\u4eba\u4eec\u6446\u8131\u4e86\u6740\u624b\u4eec\u7684\u7ea0\u7f20\uff0c\u4e00\u8d77\u9000\u81f3\u738b\u5c11\u537f\u56db\u5468\uff0c\u5e03\u597d\u4e86\u9632\u5b88\u9635\u5f62\u3002\u4ed6\u4eec\u8fc5\u901f\u4f46\u4e0d \u614c\u4e71\u5730\u5411\u5761\u9876\u4e0a\u9000\u53bb\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u6df1\u6df1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u770b\u7740\u8702\u62e5\u800c\u4e0a\u7684\u6740\u624b\u4eec\uff0c\u559d\u9053\uff1a\u201c\u653e\u7bad\uff01\u653e\u6697\u5668\uff01\u201d\u90a3\u516d\u5341\u4f59 \u540d\u6c34\u5e08\u9a91\u5175\u4e00\u76f4\u5728\u5761\u9876\u5f2f\u5f13\u5f85\u547d\uff0c\u6b64\u65f6\uff0c\u4e00\u9f50\u653e\u7bad\uff0c\u5e76\u9644\u4e0a\u5341\u5323\u8fde\u5f29\u94c1\u7bad\uff0c\u987f\u65f6\u5c06\u51b2\u5728\u6700\u524d \u9762\u7684\u5341\u51e0\u540d\u6740\u624b\u5c04\u5012\u5728\u5730\u3002 \u3000\u3000\u56db\u5927\u6355\u5934\u3001\u5c0f\u6587\u3001\u5c0f\u96c5\u3001\u59da\u987a\u5929\u548c\u4ed6\u624b\u4e0b\u7684\u9752\u8863\u4eba\u4e5f\u90fd\u5404\u629b\u51fa\u4e00\u628a\u6697\u5668\u3002\u6570\u767e\u4ef6\u5404\u79cd\u5404 \u6837\u7684\u6697\u5668\u5728\u5982\u6b64\u8fd1\u7684\u8ddd\u79bb\u7a81\u7136\u98de\u81f3\uff0c\u6740\u624b\u4eec\u663e\u7136\u65e0\u6cd5\u683c\u6321\uff0c\u800c\u4e14\u4ed6\u4eec\u56e0\u4e3a\u4eba\u6570\u592a\u591a\uff0c\u53c8\u6324\u5728 \u4e00\u8d77\uff0c\u5c31\u65e0\u6cd5\u56de\u907f\uff0c\u6240\u4ee5\u88ab\u8fd9\u9635\u6697\u5668\u5c04\u7ffb\u4e86\u4e03\u516b\u5341\u4eba\uff01\u7b49\u4e00\u9635\u6697\u5668\u4e4b\u96e8\u521a\u521a\u843d\u4e0b\uff0c\u7b2c\u4e8c\u9635\u6697 \u5668\u53c8\u98de\u51fa\u624b\uff01 \u3000\u3000\u6740\u624b\u4eec\u867d\u7136\u6709\u7684\u4e5f\u53d1\u51fa\u4e86\u6697\u5668\uff0c\u4f46\u56e0\u4e3a\u4ed6\u4eec\u5df2\u5904\u4e8e\u6df7\u4e71\u4e4b\u4e2d\uff0c\u800c\u4e14\u662f\u7531\u4e0b\u5411\u4e0a\u653b\uff0c\u5bf9\u7a0b \u5c0f\u8776\u7b49\u4eba\u6ca1\u6709\u6784\u6210\u4efb\u4f55\u5a01\u80c1\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u3001\u738b\u5c11\u537f\u4f17\u4eba\u9000\u5230\u5761\u9876\u65f6\uff0c\u6740\u624b\u4eec\u5df2\u7ecf\u4e0d\u518d\u5f80\u4e0a\u5f3a\u653b\u4e86\uff01 \u3000\u3000\u4f46\u662f\uff0c\u6740\u624b\u4eec\u56f4\u6210\u4e00\u4e2a\u5927\u7684\u5305\u56f4\u5708\uff0c\u5c06\u8fd9\u4e2a\u4e0d\u592a\u5927\u7684\u5c0f\u5c71\u5761\u56e2\u56e2\u56f4\u4f4f\uff01 \u3000\u3000\u60dc\u7389\u6293\u7d27\u65f6\u95f4\uff0c\u67e5\u51fa\u4e86\u59da\u987a\u5929\u6240\u4e2d\u98de\u5200\u4e0a\u6bd2\u836f\u7684\u6bd2\u6027\u3002 \u3000\u3000\u53ef\u4ee5\u80af\u5b9a\uff0c\u8fd9\u79cd\u6bd2\u662f\u4e0d\u8001\u4e66\u751f\u914d\u5236\u7684\u3002 \u3000\u3000\u60dc\u7389\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u5c31\u597d\u529e\u4e86\uff01\u201d\u8bf4\u7740\uff0c\u638f\u51fa\u4e00\u7c92\u89e3\u6bd2\u4e39\u836f\uff0c\u5582\u4ed6\u5403\u4e0b\u3002\u7136 \u540e\uff0c\u5979\u5c0f\u5fc3\u5730\u5c01\u4e86\u4f24\u53e3\u56db\u5904\u7684\u9053\uff0c\u8fd9\u624d\u7528\u529b\u62d4\u51fa\u4e86\u98de\u5200\u3002 \u3000\u3000\u968f\u7740\u5200\u62d4\u51fa\uff0c\u5251\u53e3\u5185\u55b7\u51fa\u4e00\u80a1\u6f06\u9ed1\u7684\u53c8\u8165\u53c8\u81ed\u7684\u9ed1\u8840\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\u513f\uff0c\u8840\u7684\u989c\u8272\u7531\u9ed1\u8f6c\u7ea2\u3002 \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u597d\u4e86\uff0c\u59da\u8001\uff0c\u4e0d\u8fc7\uff0c\u4f60\u4e0d\u80fd\u518d\u52a8\u7528\u5185\u529b\u4e86\u3002\u201d \u3000\u3000\u59da\u987a\u5929\u6025\u8981\uff1a\u201c\u9700\u8981\u591a\u957f\u65f6\u95f4\uff1f\u201d \u3000\u3000\u60dc\u7389\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u81f3\u5c11\u4e24\u4e2a\u65f6\u8fb0\uff01\u201d \u3000\u3000\u59da\u987a\u5929\u66f4\u7740\u6025\uff0c\u9053\uff1a\u201c\u654c\u4eba\u5c31\u5728\u773c\u524d\uff0c\u6211\u600e\u80fd\u4e0d\u52a8\u7528\u5185\u529b\u5462\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u59da\u8001\u653e\u5fc3\uff0c\u4f9d\u6211\u770b\uff0c\u4ed6\u4eec\u4ecd\u7136\u6ca1\u6709\u8bc6\u7834\u6211\u4eec\u7684\u8ba1\u5212\u3002\u201d \u3000\u3000\u59da\u987a\u5929\u9053\uff1a\u201c\u4f55\u4ee5\u89c1\u5f97\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4eec\u5e76\u6ca1\u6709\u9000\u8d70\uff0c\u800c\u662f\u56f4\u4f4f\u4e86\u6211\u4eec\uff0c\u8fd9\u8bf4\u660e\uff0c\u4ed6\u4eec\u51c6\u5907\u6253\u6301\u4e45\u6218\u4e86\uff01\u201d \u3000\u3000\u59da\u987a\u5929\u9053\uff1a\u201c\u6709\u9053\u7406\uff0c\u4e0d\u8fc7\uff0c\u8840\u624b\u65b9\u8f6e\u8fd8\u6ca1\u6709\u51fa\u73b0\uff0c\u8fd9\u53ef\u662f\u4e2a\u5341\u5206\u4e0d\u6b63\u5e38\u7684\u60c5\u51b5\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6211\u4eec\u4e0d\u59a8\u518d\u6fc0\u4ed6\u4eec\u4e00\u6fc0\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5982\u4f55\u6fc0\u6cd5\uff1f\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u60f3\uff0c\u603b\u6355\u5934\u5e94\u8be5\u6ce8\u610f\u7559\u4e0b\u4e86\u6700\u540e\u4e00\u652f\u51b3\u5b9a\u80dc\u8d1f\u7684\u4f18\u5175\uff0c\u4f60\u8bf4\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u9a6c\u63d0\u7763\u4eb2\u7387\u7684\u6c34\u5e08\u5927\u519b\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4f46\u8840\u624b\u65b9\u8f6e\u8fd8\u6ca1\u6709\u73b0\u8eab\uff0c\u6211\u4eec\u4e0d\u80fd\u8fc7\u65e9\u5c3d\u51fa\u5b9e\u529b\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u9053\uff1a\u201c\u6211\u7684\u610f\u601d\u662f\uff0c\u53ea\u51fa\u4e00\u90e8\u5206\uff0c\u800c\u4e14\u662f\u4e00\u5c0f\u90e8\u5206\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e00\u5c0f\u90e8\u5206\uff1f\u5982\u679c\u53ea\u6740\u51fa\u767e\u4f59\u540d\u6c34\u5e08\u6765\uff0c\u5bf9\u6218\u5c40\u5e76\u65e0\u592a\u5927\u5e2e\u52a9\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u7b11\u9053\uff1a\u201c\u9a6c\u5927\u4eba\u65e2\u7136\u5c31\u5728\u5de6\u8fd1\uff0c\u963f\u6a2a\u963f\u4fdd\u4e00\u5b9a\u4e5f\u6765\u4e86\u5427\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u679c\u7136\u5999\u8ba1\uff01\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u5979\u638f\u51fa\u4e86\u7b2c\u4e09\u652f\u7af9\u7ba1\uff0c\u5f39\u5411\u4e86\u534a\u7a7a\u4e2d\u3002 \u3000\u3000\u7af9\u7ba1\u70b8\u5f00\u4e00\u5c0f\u56e2\u84dd\u8272\u7684\u70df\u96fe\uff0c\u5c71\u4e0b\u7684\u6740\u624b\u4eec\u987f\u65f6\u5411\u5c71\u4e0a\u731b\u6251\u4e0a\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u653b\uff0c\u4ed6\u4eec\u6bcf\u4e2a\u4eba\u4e4b\u95f4\u79bb\u5f00\u6709\u4e00\u6bb5\u7a7a\u9699\uff0c\u6240\u4ee5\u80fd\u65bd\u5c55\u624b\u4e2d\u5175\u5203\uff0c\u683c\u5f00\u6697\u5668\u4e0e\u4e3a\u6570\u4e0d\u591a \u7684\u98de\u7bad\u3002\u4ed6\u4eec\u5f88\u5feb\u6251\u5230\u4e86\u534a\u5c71\u8170\u3002 \u3000\u3000\u59da\u987a\u5929\u624b\u4e0b\u7684\u9752\u8863\u4eba\u4eec\u4e5f\u90fd\u56db\u9762\u6251\u4e0b\uff0c\u594b\u52c7\u8fce\u654c\uff01 \u3000\u3000\u534a\u5c71\u8170\u5904\uff0c\u7acb\u523b\u53c8\u5c55\u5f00\u4e86\u8840\u6218\uff01 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u548c\u4e0d\u8001\u4e66\u751f\u5200\u5251\u8054\u624b\uff0c\u65e0\u4eba\u53ef\u6321\uff0c\u4ed6\u4eec\u4e8c\u4eba\u5f88\u5feb\u903c\u8fd1\u4e86\u5c71\u9876\u3002\u7a0b\u5c0f\u8776\u3001\u5c0f\u6587\u3001 \u5c0f\u96c5\u3001\u675c\u671b\u6708\u56db\u4eba\u4ee5\u4e8c\u5bf9\u4e00\uff0c\u594b\u529b\u4e0e\u4ed6\u4eec\u7f20\u6597\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u6740\u624b\u4eec\u4f3c\u4e4e\u4e0b\u4e86\u6b7b\u6218\u5230\u5e95\u7684\u51b3\u5fc3\uff0c\u6240\u4ee5\uff0c\u4ed6\u4eec\u4e0d\u987e\u4f24\u4ea1\uff0c\u5168\u529b\u524d\u51b2\u3002\u9ed1\u8863\u8499\u9762 \u4eba\u548c\u4e0d\u8001\u4e66\u751f\u663e\u7136\u4e5f\u638f\u51fa\u4e86\u538b\u7bb1\u5e95\u7684\u529f\u592b\uff0c\u770b\u6837\u5b50\uff0c\u662f\u60f3\u5c3d\u5feb\u89e3\u51b3\u6218\u6597\uff01\u6b63\u5728\u8fd9\u65f6\uff0c\u53ea\u89c1\u4e24 \u540d\u4eba\u5f71\u5982\u6d41\u661f\u822c\u98de\u63a0\u800c\u6765\uff0c\u8f6c\u773c\u95f4\u5df2\u5230\u5c71\u8170\u3002 \u3000\u3000\u6740\u624b\u4eec\u7eb7\u7eb7\u6012\u53f1\u51fa\u58f0\uff0c\u4e0a\u524d\u963b\u6321\u3002 \u3000\u3000\u4f46\u51b2\u4e0a\u53bb\u7684\u6740\u624b\u65e0\u4e00\u4f8b\u5916\u5730\u90fd\u88ab\u6740\u98de\u51fa\u4e86\u4e08\u4f59\u8fdc\u3002 \u3000\u3000\u800c\u4e14\u5012\u5730\u4e4b\u540e\uff0c\u4fbf\u53e3\u5410\u9c9c\u8840\uff0c\u518d\u4e5f\u6323\u624e\u4e0d\u8d77\uff01 \u3000\u3000\u675c\u671b\u6708\u60ca\u559c\u5730\u53eb\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u963f\u6a2a\u3001\u963f\u4fdd\u7ec8\u4e8e\u6765\u4e86\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u5c06\u53cc\u5200\u4f7f\u5f97\u522e\u98ce\u4e5f\u4f3c\uff0c\u4e00\u9762\u72de\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u53ea\u6709\u4e24\u4e2a\u4eba\u4e86\uff01\u4ed6\u4eec\u6765\u4e86\uff0c \u53c8\u80fd\u5982\u4f55\uff01\u201d\u7a0b\u5c0f\u8776\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u770b\u5427\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u548c\u4e0d\u8001\u4e66\u751f\u4e00\u770b\u4e4b\u4e0b\uff0c\u4e2a\u4e2a\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u963f\u6a2a\u3001\u963f\u4fdd\u4e8c\u4eba\u90a3\u79cd\u51f6\u608d\u7684\u6253\u6cd5\u548c\u9ad8\u6df1\u7684\u6b66\u529f\uff0c\u8ba9\u4ed6\u4eec\u4e0d\u5f97\u4e0d\u5403\u60ca\uff01 \u3000\u3000\u963f\u6a2a\u3001\u963f\u4fdd\u4e8c\u4eba\u90fd\u4e0d\u7528\u5175\u5668\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u5175\u5668\uff0c\u5c31\u662f\u4ed6\u4eec\u7684\u62f3\u5934\u3002 \u3000\u3000\u56db\u53ea\u94b5\u5934\u5927\u7684\u94c1\u62f3\u524d\u540e\u5de6\u53f3\u5feb\u901f\u7edd\u4f26\u5730\u51fb\u51fa\u3002 \u3000\u3000\u6bcf\u4e00\u51fb\uff0c\u5c31\u6709\u4e00\u540d\u6740\u624b\u98de\u8d77\u5728\u7a7a\u4e2d\u3002 \u3000\u3000\u7a7a\u4e2d\u5c31\u4f1a\u6d12\u4e0b\u4e00\u7247\u8840\u6cab\uff01 \u3000\u3000\u88ab\u51fb\u98de\u7684\u6740\u624b\u6389\u843d\u5728\u5730\u4e0a\u65f6\uff0c\u90fd\u5df2\u7ecf\u65ad\u4e86\u6c14\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u4e94\u810f\u516d\u8151\u5728\u62f3\u51fb\u4e4b\u4e0b\uff0c\u90fd\u788e\u88c2\u4e86\u3002 \u3000\u3000\u963f\u6a2a\u3001\u963f\u4fdd\u5728\u4eba\u4e1b\u4e2d\u5de6\u7a81\u53f3\u51b2\uff0c\u51b2\u5230\u54ea\u91cc\uff0c\u90a3\u91cc\u5c31\u4f1a\u6709\u6740\u624b\u63a5\u4e8c\u8fde\u4e09\u5730\u98de\u8d77\uff0c\u5012\u4e0b\uff0c\u6bd9 \u547d\u5f53\u573a\uff01\u8fd9\u4e8c\u4eba\u8eab\u4e0a\u90a3\u79cd\u5fc5\u6740\u7684\u51f6\u608d\u4e4b\u6c14\u987f\u65f6\u51fb\u6bc1\u4e86\u6740\u624b\u4eec\u7684\u81ea\u4fe1\u5fc3\u3002 \u3000\u3000\u6740\u624b\u4eec\u5f00\u59cb\u540e\u9000\u3002 \u3000\u3000\u800c\u4ed6\u4eec\u7684\u9635\u5f62\u5df2\u7ecf\u5f00\u59cb\u7eb7\u4e71\u3002 \u3000\u3000\u59da\u987a\u5929\u624b\u4e0b\u7684\u9752\u8863\u4eba\u7acb\u523b\u5f00\u59cb\u5927\u4e3e\u53cd\u653b\uff01 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u5ffd\u5730\u4e00\u4ef0\u5934\uff0c\u6253\u4e86\u4e00\u4e2a\u5c16\u9510\u54cd\u4eae\u7684\u553f\u54e8\u3002 \u3000\u3000\u54e8\u58f0\u65b9\u8d77\uff0c\u4fbf\u542c\u89c1\u56db\u9762\u8352\u91ce\u95f4\u53d1\u51fa\u4e00\u9635\u558a\u6740\u4e4b\u58f0\uff0c\u7a0b\u5c0f\u8776\u8f6c\u52a8\u76ee\u5149\uff0c\u56db\u4e0b\u4e00\u770b\uff0c\u4e0d\u7981\u5fc3 \u4e2d\u6697\u559c\uff01 \u3000\u3000\u8840\u624b\u65b9\u8f6e\u7ec8\u4e8e\u73b0\u8eab\u4e86\u3002 \u3000\u3000\u65b9\u8f6e\u5e26\u6765\u7684\u6740\u624b\u603b\u6570\u7ea6\u6709\u4e8c\u767e\u591a\u540d\uff0c\u4e00\u5f8b\u8eab\u7a7f\u7ea2\u8863\u886b\uff0c\u7ea2\u5e03\u7cfb\u9762\u3002 \u3000\u3000\u4ed6\u4eec\u6240\u7528\u7684\u5175\u5668\uff0c\u6e05\u4e00\u8272\u5168\u662f\u7684\u5f2f\u5200\uff01 \u3000\u3000\u7ea2\u8863\u6740\u624b\u4eec\u5982\u4e00\u9635\u7ea2\u8272\u7684\u98ce\u66b4\uff0c\u5239\u90a3\u95f4\u5df2\u5377\u4e0a\u4e86\u5c71\u5761\u3002 \u3000\u3000\u963f\u6a2a\u963f\u4fdd\u7acb\u523b\u7387\u4eba\u903c\u4e86\u4e0a\u53bb\uff01 \u3000\u3000\u65b9\u8f6e\u6325\u52a8\u7740\u957f\u5251\uff0c\u98de\u8eab\u5411\u5c71\u9876\u76f4\u63a0\u8fc7\u6765\u3002 \u3000\u3000\u51e0\u540d\u9752\u8863\u4eba\u60f3\u4e0a\u524d\u963b\u6321\uff0c\u4f46\u4e0d\u51fa\u4e09\u62db\uff0c\u4fbf\u90fd\u5df2\u6b7b\u4e8e\u4ed6\u7684\u5251\u4e0b\uff01 \u3000\u3000\u5c0f\u6587\u5bf9\u675c\u671b\u6708\u9053\uff1a\u201c\u675c\u53f8\u4e3b\uff0c\u4f60\u53bb\u5bf9\u4ed8\u65b9\u8f6e\uff0c\u4e0d\u8001\u4e66\u751f\u6709\u6211\u4e00\u4eba\u5c31\u884c\u4e86\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u867d\u7136\u5f88\u6709\u4e9b\u4e0d\u653e\u5fc3\uff0c\u4f46\u65b9\u8f6e\u7684\u5371\u5bb3\u66f4\u5927\uff0c\u4ed6\u8fc5\u901f\u53d1\u4e24\u5251\uff0c\u903c\u8fd1\u4e0d\u8001\u4e66\u751f\u8eab\u540e\uff0c\u98de \u8eab\u903c\u8fd1\u4e86\u6b63\u6325\u5251\u5927\u780d\u5927\u6740\u7684\u65b9\u8f6e\u3002 \u3000\u3000\u4e8c\u4eba\u4e00\u4ea4\u624b\uff0c\u675c\u671b\u6708\u4fbf\u77e5\u9053\u81ea\u5df1\u7684\u6b66\u529f\u4e0e\u65b9\u8f6e\u53ef\u8c13\u534a\u65a4\u516b\u4e24\uff0c\u8c01\u80dc\u4e0d\u8fc7\u8c01\u53bb\u3002\u5728\u8fd9\u79cd\u60c5 \u51b5\u4e4b\u4e0b\uff0c\u8981\u6bd4\u7684\u5c31\u662f\u4f53\u529b\u3001\u8010\u529b\u548c\u5fc3\u673a\u4e86\u3002 \u3000\u3000\u4f53\u529b\u4e0a\uff0c\u65b9\u8f6e\u660e\u663e\u5360\u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u662f\u517b\u7cbe\u84c4\u9510\uff0c\u521a\u52a0\u5165\u6218\u6597\uff0c\u800c\u675c\u671b\u6708\u5374\u5df2\u7ecf\u7ecf\u8fc7\u4e86\u4e09\u56db\u9635\u60e8\u70c8\u7684\u640f\u6740\u3002\u8010\u529b\u4e0a\uff0c \u675c\u671b\u6708\u5f53\u7136\u4e5f\u8981\u900a\u4ed6\u4e00\u7b79\u3002 \u3000\u3000\u4e8e\u662f\u675c\u671b\u6708\u56f4\u7740\u65b9\u8f6e\uff0c\u957f\u5251\u8fde\u73af\u76f4\u524c\uff0c\u4e0d\u7ed9\u4ed6\u5598\u606f\u4e4b\u673a\u3002 \u3000\u3000\u65b9\u8f6e\u867d\u7136\u6bcf\u4e00\u5251\u90fd\u5e94\u4ed8\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u800c\u4e14\uff0c\u4ed6\u4e5f\u6709\u4e9b\u624b\u5fd9\u811a\u4e71\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u8d81\u4ed6\u5fd9\u4e8e\u62db\u67b6\u4e4b\u673a\uff0c\u81ea\u5df1\u7a81\u7136\u540e\u64a4\u4e86\u4e09\u56db\u6b65\uff0c\u6293\u7d27\u65f6\u95f4\u8c03\u8fd0\u5185\u606f\u3002\u538b\u529b\u4e00\u51cf\uff0c\u65b9 \u8f6e\u4e5f\u60f3\u5c31\u8fd9\u4e2a\u673a\u4f1a\u5598\u4e0a\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u4f46\u675c\u671b\u6708\u53c8\u98de\u5feb\u5730\u6251\u4e0a\uff0c\u8fd9\u5251\u5982\u98ce\uff0c\u7d27\u7d27\u5730\u7f20\u4f4f\u4e86\u4ed6\u3002 \u3000\u3000\u8fd9\u6837\u51e0\u8f6e\u653b\u51fb\u4e0b\u6765\uff0c\u65b9\u8f6e\u987f\u611f\u5fc3\u6d6e\u6c14\u71e5\u8d77\u6765\u3002 \u3000\u3000\u5f53\u7136\uff0c\u675c\u671b\u6708\u8981\u60f3\u6218\u80dc\u4ed6\uff0c\u8fd8\u662f\u5f88\u4e0d\u5bb9\u6613\u7684\u3002 \u3000\u3000\u5c11\u4e86\u4e00\u4e2a\u675c\u671b\u6708\uff0c\u4e0d\u8001\u4e66\u751f\u7684\u5251\u6cd5\u5c31\u5bb9\u6613\u53d1\u6325\u5f97\u591a\u4e86\u3002 \u3000\u3000\u4ed6\u4f3c\u4e4e\u5df2\u7ecf\u627e\u5230\u4e86\u514b\u5236\u201c\u5251\u6d77\u6d74\u9b42\u201d\u7684\u65b9\u6cd5\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5f00\u59cb\u4ee5\u957f\u5251\u4f7f\u51fa\u4e86\u5355\u5200\u7684\u62db\u6570\uff0c\u4e00\u5251\u7d27\u63a5\u7740\u4e00\u5251\u5411\u5c0f\u6587\u5f53\u5934\u731b\u780d\uff01\u4ed6\u7684\u5251\u6cd5\u6ca1\u6709 \u4efb\u4f55\u53d8\u5316\uff0c\u4f46\u6bcf\u4e00\u5251\u90fd\u84c4\u96c6\u4e86\u5343\u94a7\u4e4b\u529b\uff0c\u957f\u5251\u5e26\u8d77\u7684\u5251\u98ce\u5df2\u5c06\u5c0f\u6587\u7684\u5934\u53d1\u522e\u5f97\u62ab\u6563\u5f00\u6765\u3002 \u3000\u3000\u6b63\u662f\u56e0\u4e3a\uff0c\u53ea\u51ed\u6c14\u529b\uff0c\u5c0f\u6587\u4e5f\u53ea\u6709\u786c\u5c01\u786c\u6321\uff0c\u201c\u5251\u6d77\u6d74\u9b42\u201d\u90a3\u4e9b\u597d\u7684\u6740\u7740\u4e00\u62db\u4e5f\u4f7f\u4e0d\u51fa \u6765\u4e86\u3002 \u3000\u3000\u5c0f\u6587\u7acb\u523b\u9677\u5165\u4e86\u9669\u5883\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u7684\u529b\u6c14\uff0c\u4f53\u529b\u660e\u663e\u6bd4\u4e0d\u4e0a\u4e0d\u8001\u4e66\u751f\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u548c\u5c0f\u96c5\u5408\u6597\u9ed1\u8863\u8499\u9762\u4eba\uff0c\u5f62\u52bf\u4e5f\u5f88\u5371\u9669\u3002 \u3000\u3000\u56e0\u4e3a\u7a0b\u5c0f\u8776\u53d7\u4e86\u4e25\u91cd\u7684\u5185\u4f24\uff0c\u6240\u4ee5\uff0c\u5979\u7684\u201c\u9738\u738b\u65e0\u654c\u201d\u67aa\u6cd5\u6ca1\u6cd5\u53d1\u6325\u51fa\u6700\u5927\u5a01\u529b\u6765\u3002 \u3000\u3000\u5c0f\u96c5\u867d\u7136\u594b\u529b\u51fa\u5251\uff0c\u4f46\u7ecf\u8fc7\u4e00\u6574\u5929\u7684\u6fc0\u6218\uff0c\u5979\u7684\u4f53\u529b\u4e5f\u5728\u98de\u901f\u5730\u4e0b\u964d\uff0c\u4f7f\u51fa\u7684\u5251\u62db\u5df2\u4e0d \u80fd\u5bf9\u9ed1\u8863\u8499\u9762\u4eba\u6784\u6210\u592a\u5927\u7684\u5a01\u80c1\uff01 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u5374\u662f\u8d8a\u6218\u8d8a\u52c7\uff0c\u624b\u4e2d\u53cc\u5200\u98ce\u4e5f\u4f3c\u5730\u4f7f\u5c06\u51fa\u6765\uff0c\u95ea\u51fa\u6ee1\u5929\u7684\u5200\u5f71\u3002\u4e00\u6735\u4e00\u6735\u96ea \u4eae\u7684\u5200\u82b1\u4e0d\u65ad\u5730\u5728\u7a0b\u5c0f\u8776\u548c\u5c0f\u96c5\u7684\u8981\u5bb3\u4e4b\u5904\u9760\u8fd1\u95ea\u73b0\u3002 \u3000\u3000------------- \u5367\u9f99\u751f\u300a\u68a6\u5e7b\u4e4b\u5200\u300b \u7b2c\u4e09\u5341\u4e8c\u7ae0 \u3000\u3000\u4e00\u58f0\u88c2\u5e1b\u54cd\u8d77\u3002 \u3000\u3000\u5c0f\u96c5\u7684\u5de6\u80a9\u5934\u88ab\u5212\u4e2d\u4e86\u4e00\u5200\u3002 \u3000\u3000\u8863\u886b\u5f39\u88c2\uff0c\u9c9c\u8840\u76f4\u6d41\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u594b\u529b\u6a2a\u5411\u4e00\u67aa\uff0c\u63a5\u4f4f\u9ed1\u8863\u8499\u9762\u4eba\u53cc\u5200\uff0c\u75be\u95ee\u9053\uff1a\u201c\u5c0f\u96c5\uff0c\u4f60\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5c0f\u96c5\u7259\u54ac\uff0c\u987e\u4e0d\u5f97\u5305\u624e\u4f24\u53e3\uff0c\u6325\u5251\u53c8\u6251\u4e86\u4e0a\u6765\uff01 \u3000\u3000\u60dc\u7389\u5927\u53eb\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6211\u6765\u5e2e\u4f60\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u6c89\u58f0\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u4f60\u7684\u4efb\u52a1\u662f\u4fdd\u62a4\u738b\u5927\u4eba\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u72de\u7b11\u9053\uff1a\u201c\u81ea\u5df1\u90fd\u5df2\u6b7b\u5230\u4e34\u5934\u4e86\uff0c\u8fd8\u80fd\u4fdd\u62a4\u4eba\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u957f\u5200\u75be\u6325\uff0c\u4e00\u5200\u62b5\u4f4f\u7a0b\u5c0f\u8776\u957f\u67aa\u548c\u5c0f\u96c5\u957f\u5251\u7684\u8054\u5408\u653b\u51fb\uff0c\u5411\u5de6\u4e00\u95ea\u8eab\uff0c\u5de6\u624b\u77ed\u5200 \u5df2\u7136\u5212\u51fa\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u5927\u60ca\uff0c\u60f3\u907f\uff0c\u4f46\u80f8\u95f4\u4e00\u9635\u6c14\u8840\u7ffb\u6d8c\uff0c\u53cc\u817f\u7adf\u7136\u6709\u4e9b\u53d1\u8f6f\u3002 \u3000\u3000\u5979\u6ca1\u6709\u95ea\u5f00\uff01 \u3000\u3000\u5c0f\u96c5\u3001\u60dc\u7389\u60ca\u547c\u4e00\u58f0\u3002\u201c\u603b\u6355\u5934\uff01\u201d \u3000\u3000\u77ed\u5200\u6df1\u6df1\u624e\u8fdb\u4e86\u7a0b\u5c0f\u8776\u7684\u5de6\u817f\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u94f6\u7259\u7d27\u54ac\uff0c\u7a81\u7136\u4e22\u5f00\u957f\u67aa\uff0c\u53cc\u624b\u7d27\u7d27\u63e1\u4f4f\u4e86\u9ed1\u8863\u8499\u9762\u4eba\u7684\u5de6\u624b\u8155\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u594b\u529b\u56de\u593a\uff0c\u60f3\u5c06\u77ed\u5200\u62d4\u51fa\u6765\uff0c\u4f46\u5de6\u624b\u5374\u5df2\u4f7f\u4e0d\u4e0a\u529b\u3002 \u3000\u3000\u4ed6\u5de6\u624b\u4e00\u7d27\uff0c\u957f\u5200\u5411\u7a0b\u5c0f\u8776\u5f53\u5934\u5288\u4e0b\uff01 \u3000\u3000\u201c\u5f53\u201d\u5730\u4e00\u58f0\u8106\u54cd\uff01 \u3000\u3000\u706b\u5149\u56db\u6e85\u3002 \u3000\u3000\u5c0f\u96c5\u4ee5\u957f\u5251\u67b6\u5f00\u4e86\u4ed6\u8fd9\u4e00\u5200\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u4e00\u4fa7\u8eab\uff0c\u4fa7\u8eba\u5728\u5730\uff0c\u53f3\u811a\u98de\u5feb\u5730\u8e22\u51fa\uff01 \u3000\u3000\u201c\u549a\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u53f3\u808b\u4e2d\u4e86\u4e00\u811a\uff01 \u3000\u3000\u4ed6\u4e0d\u5f97\u4e0d\u653e\u5f00\u4e86\u77ed\u5200\uff0c\u8e09\u8e09\u8dc6\u8231\u5730\u5411\u540e\u9000\u53bb\uff01 \u3000\u3000\u5c0f\u96c5\u5389\u53eb\u4e00\u58f0\uff0c\u6325\u5251\u51b2\u4e0a\uff01 \u3000\u3000\u65b9\u8f6e\u7387\u9886\u7684\u7ea2\u8863\u6740\u624b\u4eec\u7684\u52a0\u5165\uff0c\u4f7f\u6740\u624b\u4eec\u9f13\u8d77\u4e86\u518d\u6218\u7684\u52c7\u6c14\u3002 \u3000\u3000\u8fd9\u4e9b\u7ea2\u8863\u6740\u624b\u7684\u6b66\u529f\u90fd\u5f88\u9ad8\uff0c\u59da\u987a\u5929\u624b\u4e0b\u7684\u9752\u8863\u4eba\u4eec\u4ee5\u4e00\u5bf9\u4e00\uff0c\u4e5f\u53ea\u80fd\u6253\u4e2a\u5e73\u624b\uff01 \u3000\u3000\u5341\u51e0\u540d\u7ea2\u8863\u6740\u624b\u5206\u522b\u56f4\u4f4f\u4e86\u963f\u6a2a\u3001\u963f\u4fdd\uff0c\u4e8c\u4eba\u867d\u7136\u94c1\u62f3\u6bcf\u4e00\u51fb\u90fd\u80fd\u6253\u5012\u4e00\u4e2a\uff0c\u4f46\u7acb\u5373\u5c31 \u6709\u4e00\u4e2a\u8865\u5145\u4e0a\u6765\uff01 \u3000\u3000\u6740\u624b\u4eec\u518d\u6b21\u5360\u4e86\u4e0a\u98ce\uff01 \u3000\u3000\u6218\u9635\u53c8\u6e10\u6e10\u79fb\u5411\u4e86\u5c71\u9876\uff01 \u3000\u3000\u4e00\u76f4\u5728\u5c71\u9876\u7684\u516d\u5341\u4f59\u540d\u6c34\u5e08\u5200\u624b\u4eec\u518d\u4e5f\u4e0d\u613f\u5750\u89c6\u3002 \u3000\u3000\u4ed6\u4eec\u77e5\u9053\u51ed\u81ea\u5df1\u7684\u529f\u592b\u6839\u672c\u654c\u4e0d\u8fc7\u6740\u624b\u4eec\uff0c\u4f46\u4ed6\u4eec\u80fd\u62fc\u547d\uff01 \u3000\u3000\u53ea\u89c1\u5200\u624b\u4eec\u4e09\u4e2a\u4e00\u7ec4\uff0c\u4e94\u4e2a\u4e00\u7fa4\uff0c\u72c2\u547c\u4e71\u53eb\u7740\u6251\u8fdb\u6740\u624b\u4e1b\u4e2d\uff0c\u6325\u5200\u4e71\u78b0\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u7a81\u7136\u52a0\u5165\u663e\u7136\u5927\u51fa\u6740\u624b\u4eec\u7684\u9884\u6599\uff0c\u6240\u4ee5\u5728\u731d\u4e0d\u53ca\u9632\u4e0b\uff0c\u8fd8\u771f\u88ab\u4ed6\u4eec\u6740\u6b7b\u4e86\u5341\u51e0\u4e2a \u6740\u624b\u3002 \u3000\u3000\u4f46\u8f6c\u773c\u95f4\uff0c\u6740\u624b\u4eec\u5c31\u7a33\u4f4f\u4e86\u9635\u811a\u3002 \u3000\u3000\u4e0d\u8fc7\u76cf\u8336\u65f6\u5206\uff0c\u8fd9\u516d\u5341\u4f59\u540d\u6c34\u5e08\u58eb\u5175\u5df2\u5c3d\u6570\u88ab\u6740\uff01 \u3000\u3000\u675c\u671b\u6708\u4e0e\u8840\u624b\u65b9\u8f6e\u4ecd\u6253\u5f97\u96be\u5206\u96be\u89e3\uff0c\u8c01\u4e5f\u635e\u4e0d\u4e86\u8c01\u7684\u4fbf\u5b9c\uff01 \u3000\u3000\u5c0f\u96c5\u6325\u5251\u731b\u6251\u5411\u9ed1\u8863\u8499\u9762\u4eba\uff0c\u6218\u8fc7\u56db\u4e94\u62db\uff0c\u5979\u8eab\u4e0a\u53c8\u88ab\u523a\u4e2d\u4e86\u4e24\u5200\u3002 \u3000\u3000\u867d\u7136\u88ab\u523a\u7684\u5e76\u4e0d\u662f\u8981\u5bb3\uff0c\u4f46\u56e0\u6d41\u8840\u8fc7\u591a\uff0c\u5979\u523a\u51fa\u7684\u5251\u62db\u5df2\u6709\u6c14\u65e0\u529b\u4e86\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u72c2\u7b11\u4e00\u58f0\uff0c\u4e00\u5200\u6325\u51fa\u3002 \u3000\u3000\u5c0f\u96c5\u7684\u957f\u5251\u98de\u5728\u4e86\u534a\u7a7a\u4e2d\u3002 \u3000\u3000\u5200\u5149\u9006\u95ea\u3002 \u3000\u3000\u5c0f\u96c5\u8eab\u4e0a\u53c8\u6740\u4e86\u4e09\u5904\u5200\u4f24\uff0c\u53cc\u817f\u4e00\u8f6f\uff0c\u5012\u5728\u4e86\u5730\u4e0a\uff01 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u4e3e\u5200\u6a2a\u80f8\uff0c\u4e00\u6b65\u4e00\u6b65\u5411\u5c71\u9876\u8d70\u53bb\uff01 \u3000\u3000\u60dc\u7389\uff0c\u5434\u94c1\u5cf0\uff0c\u4e8e\u627f\u5fd7\uff0c\u5c91\u5578\u864e\uff0c\u738b\u5c11\u537f\u548c\u8eab\u53d7\u91cd\u4f24\u7684\u59da\u987a\u5929\u5c31\u5728\u5c71\u9876\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u81ea\u5730\u4e0a\u6361\u8d77\u957f\u67aa\uff0c\u8fd8\u672a\u523a\u51fa\uff0c\u8179\u90e8\u5df2\u53d7\u4e86\u9ed1\u8863\u8499\u9762\u4eba\u4e00\u811a\u3002 \u3000\u3000\u5979\u88ab\u8e22\u5f97\u6eda\u4e86\u6eda\uff0c\u8eba\u5012\u5728\u5730\uff0c\u6323\u624e\u4e86\u51e0\u4e0b\uff0c\u5374\u5df2\u6323\u624e\u4e0d\u8d77\uff01 \u3000\u3000\u5434\u94c1\u5cf0\u53cc\u624b\u8ff8\u6296\uff0c\u6253\u51fa\u56db\u679a\u91d1\u9488\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u53f3\u624b\u4e3e\u5200\u5e73\u80f8\uff0c\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u5de6\u624b\u4e0b\u8896\u4e00\u6325\uff0c\u4e00\u5377\uff0c\u56db\u679a\u91d1\u9488\u3002\u88ab\u5377\u4f4f\uff0c\u843d\u5728 \u5730\u4e0a\u3002 \u3000\u3000\u5c91\u5578\u864e\u864e\u543c\u4e00\u58f0\uff0c\u5e94\u8eab\u6251\u4e0a\u3002 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u53f3\u638c\u4e00\u6325\uff0c\u5c91\u5578\u864e\u60e8\u53eb\u4e00\u58f0\uff0c\u7ffb\u5012\u5728\u5730\uff0c\u53e3\u4e2d\u9c9c\u8840\u55b7\u6d8c\uff01 \u3000\u3000\u738b\u5c11\u537f\u5bf9\u60dc\u7389\u9053\uff1a\u201c\u60dc\u7389\uff0c\u4f60\u4fdd\u62a4\u91d1\u59d1\u5a18\u5feb\u8d70\u5427\uff01\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u4e0d\uff01\u6211\u7684\u4efb\u52a1\u662f\u4fdd\u62a4\u5927\u4eba\uff01\u201d \u3000\u3000\u5434\u94c1\u5cf0\u548c\u4e8e\u627f\u5fd7\u53cc\u53cc\u6251\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5200\u5149\u4e00\u95ea\u3002 \u3000\u3000\u518d\u95ea\u3002 \u3000\u3000\u4e8c\u4eba\u90fd\u60ca\u547c\u51fa\u58f0\uff0c\u88ab\u78b0\u5012\u4e86\u3002 \u3000\u3000\u5434\u94c1\u5cf0\u4f24\u5728\u5de6\u808b\uff0c\u4e8e\u627f\u5fd7\u4f24\u5728\u53f3\u80a9\uff01 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u4e00\u6b65\u4e00\u6b65\u5411\u738b\u5c11\u537f\u903c\u8fd1\uff01 \u3000\u3000\u60dc\u7389\u53f3\u81c2\u76f4\u4f38\uff0c\u957f\u5251\u5bf9\u7740\u9ed1\u8863\u8499\u9762\u4eba\uff0c\u5de6\u81c2\u5c06\u738b\u5c11\u537f\u548c\u91d1\u5c0f\u7709\u62e6\u5728\u8eab\u540e\uff0c\u4e00\u6b65\u4e00\u6b65\u5411\u540e \u9000\u3002 \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u6211\u5c31\u662f\u62fc\u4e86\u547d\uff0c\u4e5f\u8981\u523a\u4f60\u4e00\u5251\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u4f60\u6536\u5251\u79bb\u5f00\uff0c\u6211\u4fdd\u8bc1\u4e0d\u4f1a\u4f24\u4f60\u3002\u800c\u4e14\uff0c\u4f1a\u8ba9\u4f60\u6709\u4eab\u4e0d\u5c3d\u7684\u8363\u534e\u5bcc\u8d35\uff01\u201d \u3000\u3000\u60dc\u7389\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u6015\u6211\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u60dc\u7389\u9053\uff1a\u201c\u4f60\u8981\u662f\u81ea\u4fe1\u80fd\u6740\u6b7b\u6211\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u52a8\u624b\uff1f\u4e3a\u4ec0\u4e48\u8fd8\u8981\u7528\u8363\u534e\u5bcc\u8d35\u6765\u5f15\u8bf1\u6211\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u6012\u543c\u4e00\u58f0\uff0c\u6325\u5200\u6251\u4e0a\uff01 \u3000\u3000\u675c\u671b\u6708\u770b\u89c1\u5c71\u9876\u5904\u5f62\u52bf\u5371\u6025\uff0c\u60dc\u7389\u5b64\u8eab\u4e00\u4eba\u5f88\u96be\u963b\u4f4f\u9ed1\u8863\u8499\u9762\u4eba\uff0c\u800c\u81ea\u5df1\u53c8\u5fc5\u7ecf\u5168\u529b\u62fc \u6740\uff0c\u65b9\u80fd\u51fb\u5012\u8840\u624b\u65b9\u8f6e\u3002\u60c5\u6025\u4e4b\u4e0b\uff0c\u5927\u547c\u9053\uff1a\u201c\u963f\u6a2a\uff0c\u963f\u4fdd\uff0c\u5feb\u51b2\u4e0a\u53bb\uff0c\u6551\u738b\u5927\u4eba\uff01\u201d \u3000\u3000\u963f\u6a2a\u963f\u4fdd\u65e9\u5df2\u60f3\u51b2\u6740\u51fa\u6765\u4e86\uff0c\u53ea\u662f\u56f4\u7740\u4ed6\u4eec\u7684\u5341\u51e0\u540d\u7ea2\u8863\u6740\u624b\u6b66\u529f\u5b9e\u5728\u662f\u5f88\u5389\u5bb3\uff0c\u51b2\u4e86 \u51e0\u6b21\uff0c\u90fd\u4ee5\u5931\u8d25\u544a\u7ec8\u3002 \u3000\u3000\u4f46\u542c\u662f\u675c\u671b\u6708\u7684\u53eb\u558a\u58f0\uff0c\u4ed6\u4eec\u4e0d\u89c9\u4e5f\u662f\u5fc3\u4e2d\u5927\u6025\uff0c\u4e24\u81c2\u4e4b\u4e0a\u5ffd\u7136\u751f\u51fa\u4e00\u80a1\u795e\u529b\u6765\u3002 \u3000\u3000\u201c\u7830\u7830\u7830\uff0c\u547c\u547c\uff01\u201d \u3000\u3000\u56db\u6761\u94c1\u62f3\u9661\u7136\u95f4\u95ea\u7535\u4f3c\u5730\u62a1\u8f6c\u8d77\u6765\uff0c\u5c06\u56f4\u4f4f\u4ed6\u4eec\u7684\u7ea2\u8863\u6740\u624b\u4e00\u4e0b\u51fb\u5230\u516d\u4e03\u4e2a\uff0c\u518d\u914d\u4ee5\u795e \u5999\u7684\u6b65\u6cd5\u8eab\u6cd5\uff0c\u4ed6\u4eec\u5df2\u6446\u8131\u7ea0\u7f20\u3002 \u3000\u3000\u4e8c\u4eba\u7acb\u5373\u5411\u5c71\u9876\u731b\u51b2\uff01 \u3000\u3000\u59da\u987a\u5929\u624b\u4e0b\u7684\u9752\u8863\u4eba\u4eec\u4e5f\u90fd\u53d1\u73b0\u4e86\u5f62\u52bf\u7684\u5371\u6025\uff0c\u77e5\u9053\u662f\u5230\u4e86\u62fc\u8001\u547d\u7684\u65f6\u5019\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u6e10\u6e10\u5730\u805a\u96c6\u8d77\u6765\uff0c\u7ec4\u6210\u4e86\u4e00\u9053\u9632\u7ebf\uff0c\u7528\u76f8\u4e92\u4e4b\u95f4\u5de7\u5999\u7684\u914d\u5408\uff0c\u987d\u5f3a\u5730\u963b\u6321\u7740\u6740\u624b\u4eec\uff01 \u3000\u3000\u9752\u8863\u4eba\u7684\u4eba\u6570\u5728\u4e00\u9635\u9635\u7684\u60e8\u53eb\u58f0\u4e2d\uff0c\u4e0d\u65ad\u5730\u51cf\u5c11\u3002 \u3000\u3000\u4f46\u6740\u624b\u4eec\u7684\u635f\u5931\u66f4\u5927\uff01 \u3000\u3000\u6bcf\u5012\u4e0b\u4e00\u4e2a\u9752\u8863\u4eba\uff0c\u540c\u65f6\u4e5f\u4f1a\u5012\u4e0b\u4e09\u4e2a\u4ee5\u4e0a\u7684\u6740\u624b\uff01 \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u5c71\u9876\u4e0a\u54cd\u8d77\u4e00\u58f0\u523a\u8033\u7684\u60ca\u53eb\u58f0\uff01 \u3000\u3000\u60ca\u53eb\u58f0\u8ba9\u6240\u6709\u4eba\u542c\u4e86\u90fd\u662f\u5fc3\u5934\u4e00\u9707\u3002\u4e0d\u77e5\u4e0d\u89c9\u95f4\uff0c\u6740\u624b\u4eec\u4e00\u4e0b\u5b50\u505c\u6b62\u4e86\u653b\u51fb\uff0c\u800c\u9752\u8863\u4eba \u4eec\u4e5f\u4e0d\u518d\u51b2\u4e0a\u62fc\u6740\u3002 \u3000\u3000\u4f17\u4eba\u4e00\u8d77\u5411\u5c71\u9876\u770b\u53bb\u3002 \u3000\u3000\u675c\u671b\u6708\u548c\u4e0d\u8001\u4e66\u751f\u4e5f\u5206\u5f00\u4e86\uff0c\u5404\u81ea\u5411\u540e\u9000\u4e86\u4e94\u516d\u6b65\uff01 \u3000\u3000\u521a\u521a\u8fd8\u5145\u6ee1\u4e86\u60e8\u53eb\u58f0\u3001\u5598\u606f\u58f0\u3001\u547c\u558a\u58f0\u3001\u5175\u5203\u649e\u51fb\u58f0\u7684\u8352\u91ce\u4e0a\uff0c\u73b0\u5728\u5df2\u53d8\u5f97\u4e00\u7247\u5bc2\u9759\uff01 \u3000\u3000\u6b7b\u4e00\u822c\u7684\u5bc2\u9759\u4e2d\uff0c\u53ea\u6709\u91d1\u5c0f\u7709\u5c16\u5229\u7684\u58f0\u97f3\u5389\u53eb\u9053\uff1a\u201c\u7239\u7239\u2014\u2014\u600e\u4e48\u4f1a\u662f\u4f60\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u6325\u5200\u6251\u4e0a\u65f6\uff0c\u60dc\u7389\u5df2\u51c6\u5907\u633a\u5251\u5e94\u6218\uff0c\u4e00\u76f4\u534a\u8eba\u5728\u5730\u4e0a\u7684\u59da\u987a\u5929\u5374\u7a81\u7136\u81ea\u5730\u4e0a \u5f39\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u53cc\u81c2\u5f20\u5f00\uff0c\u5e73\u5e73\u5730\u6251\u5411\u4e86\u9ed1\u8863\u8499\u9762\u4eba\uff01 \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u624b\u4e2d\u7684\u957f\u5200\u65e0\u60c5\u5730\u523a\u4e2d\u4e86\u4ed6\u7684\u80f8\u53e3\u3002 \u3000\u3000\u81ea\u524d\u80f8\u4e00\u76f4\u523a\u7a7f\u5230\u540e\u80cc\uff01 \u3000\u3000\u59da\u987a\u5929\u7684\u4e00\u53cc\u9752\u7b4b\u6012\u5f20\u7684\u5927\u624b\u4e5f\u6293\u4f4f\u4e86\u4ed6\u7684\u53cc\u80a9\uff01 \u3000\u3000\u59da\u987a\u5929\u5598\u606f\u7740\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u53bb\u95ee\u960e\u738b\u5427\uff01\u201d \u3000\u3000\u59da\u987a\u5929\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u6211\u77e5\u9053\u4f60\u662f\u8c01\uff0c\u4e0d\u8fc7\uff0c\u6211\u4e0d\u592a\u76f8\u4fe1\u4f1a\u662f\u771f\u7684\u2026\u2026\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u9053\uff1a\u201c\u90a3\u53c8\u600e\u4e48\u6837\uff01\u201d \u3000\u3000\u59da\u987a\u5929\u7684\u53cc\u624b\u6e10\u6e10\u677e\u5f00\uff0c\u58f0\u97f3\u4e5f\u8d8a\u6765\u8d8a\u4f4e\uff1a\u201c\u6211\u2026\u2026\u2026\u6211\u8981\u773c\u2026\u2026\u770b\u7740\u2026\u2026\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\u51b7\u54fc\u4e00\u58f0\uff0c\u4f38\u51fa\u53f3\u638c\uff0c\u7528\u529b\u51fb\u5728\u59da\u987a\u5929\u7709\u5934\uff0c\u60f3\u628a\u4ed6\u63a8\u5f00\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u59da\u987a\u5929\u7a81\u7136\u5927\u543c\u9053\uff1a\u201c\u6211\u8981\u770b\u4f60\u7684\u8138\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u5df2\u65e0\u529b\u7684\u53cc\u624b\u731b\u5730\u626c\u4e86\u8d77\u6765\uff0c\u4e00\u4e0b\u6293\u4e0b\u4e86\u9ed1\u8863\u8499\u9762\u4eba\u88f9\u5f97\u7d27\u7d27\u7684\u8499\u9762\u9ed1\u5e03\uff01 \u3000\u3000\u91d1\u5c0f\u7709\u7684\u773c\u775b\u4e00\u4e0b\u77aa\u5927\u4e86\u3002 \u3000\u3000\u738b\u5c11\u537f\u6ee1\u8138\u7684\u5403\u60ca\uff0c\u6ee1\u8138\u7684\u4e0d\u4fe1\uff01 \u3000\u3000\u60dc\u7389\u4e5f\u5403\u60ca\u5730\u77aa\u5706\u4e86\u53cc\u773c\uff01 \u3000\u3000\u662f\u4ed6\uff01\u5c31\u662f\u4ed6\uff01\u539f\u6765\u4ed6\u5c31\u662f\u6740\u624b\u96c6\u56e2\u7684\u9996\u8111\uff01 \u3000\u3000\u91d1\u5c0f\u7709\u7a81\u7136\u5c16\u58f0\u5389\u53eb\u4e86\u8d77\u6765\u2014\u2014 \u3000\u3000\u201c\u7239\u7239\u2014\u2014\u600e\u4e48\u4f1a\u662f\u4f60\uff01\uff01\u201d \u3000\u3000\u9ed1\u8863\u8499\u9762\u4eba\uff0c\u4e00\u76f4\u81ea\u79f0\u662f\u68a6\u5e7b\u4e4b\u5200\u7684\u9ed1\u8863\u8499\u9762\u4eba\u7adf\u662f\u91d1\u767e\u5e74\uff01 \u3000\u3000\u91d1\u767e\u5e74\u4f1a\u662f\u6740\u624b\u96c6\u56e2\u7684\u9996\u8111\uff1f \u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u8981\u9677\u5bb3\u81ea\u5df1\u7684\u5973\u513f\u5462\uff1f \u3000\u3000\u91d1\u767e\u5e74\u6325\u8d77\u4e00\u638c\uff0c\u5c06\u5df2\u7ecf\u6b7b\u53bb\u7684\u59da\u987a\u5929\u6253\u98de\uff0c\u9759\u9759\u5730\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7709\u513f\uff0c\u6b63\u662f\u4e3a\u7236\uff01\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u7239\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6740\u738b\u5927\u4eba\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u7709\u513f\uff0c\u6709\u5f88\u591a\u4e8b\uff0c\u4f60\u90fd\u4e0d\u61c2\uff0c\u4ee5\u540e\uff0c\u4e3a\u7236\u4f1a\u6162\u6162\u5411\u4f60\u89e3\u91ca\u7684\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u6211\u73b0\u5728\u5c31\u8981\u4f60\u89e3\u91ca\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u90a3\u4f60\u95ee\u5427\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u675c\u671b\u6708\u3001\u963f\u6a2a\u3001\u963f\u4fdd\u5df2\u8d76\u5230\u738b\u5c11\u537f\u8eab\u8fb9\u3002 \u3000\u3000\u4e0d\u8001\u4e66\u751f\u4e5f\u8d70\u5230\u4e86\u91d1\u767e\u5e74\u8eab\u8fb9\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u3001\u5c0f\u96c5\u3001\u4e8e\u627f\u5fd7\u3001\u5434\u94c1\u5cf0\u3001\u5c91\u5578\u864e\u5728\u5c0f\u6587\u7684\u5e2e\u52a9\u4e0b\uff0c\u4e5f\u6323\u624e\u7740\u7ad9\u8d77\u6765\uff0c \u3000\u3000\u6162\u6162\u8d70\u56de\u5230\u738b\u5c11\u537f\u8eab\u8fb9\uff0c\u5404\u6a2a\u5175\u5203\uff0c\u4e25\u9635\u4ee5\u5f85\uff01 \u3000\u3000\u91d1\u5c0f\u7709\u4e24\u773c\u56db\u4e0b\u770b\u770b\uff0c\u7a81\u7136\uff0c\u5979\u76ef\u7740\u4e0d\u8001\u4e66\u751f\uff0c\u5389\u53eb\u9053\uff1a\u201c\u662f\u4ed6\uff01\u90a3\u5929\u53bb\u5e9c\u91cc\u66ff\u6211\u7239\u6cbb \u75c5\u7684\u533b\u751f\u5c31\u662f\u4ed6\uff01\u201d \u3000\u3000\u4e0d\u8001\u4e66\u751f\u9053\uff1a\u201c\u4e0d\u9519\uff01\u8001\u592b\u90a3\u5929\u662f\u4e13\u7a0b\u9001\u836f\u53bb\u7684\uff01\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u6211\u8ff7\u8ff7\u7cca\u7cca\u4e4b\u4e2d\uff0c\u6740\u4e86\u656c\u6587\uff0c\u5c31\u662f\u56e0\u88ab\u4f60\u7684\u836f\u63a7\u5236\u4e86\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u4e0d\u8001\u4e66\u751f\u9053\uff1a\u201c\u662f\u7684\uff01\u201d \u3000\u3000\u91d1\u5c0f\u80a9\u9053\uff1a\u201c\u662f\u8c01\u4e0b\u7684\u836f\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u662f\u6211\u3002\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u56e0\u4e3a\u65f6\u95f4\u4e0d\u5141\u8bb8\u6211\u4eec\u518d\u7b49\u4e0b\u53bb\u4e86\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u539f\u6765\uff0c\u4f60\u672c\u6253\u7b97\u4e0e\u9a6c\u63d0\u7763\u7ed3\u6210\u513f\u5973\u4eb2\u5bb6\uff0c\u7136\u540e\u518d\u6162\u6162\u5730\u505a\u4ed6\u7684\u5de5\u4f5c\uff0c\u62c9\u4ed6 \u4e0b\u6c34\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u53ef\u540e\u6765\uff0c\u4eac\u91cc\u4f20\u6765\u6d88\u606f\uff0c\u8bf4\u7687\u4e0a\u75c5\u5371\uff0c\u6240\u4ee5\u4f60\u4eec\u7b49\u4e0d\u53ca\u4e86\uff0c\u4fbf\u8bbe\u4e0b\u65b0\u623f\u8840 \u6848\uff0c\u6311\u52a8\u9a6c\u957f\u5c71\u4e0e\u4f60\u706b\u62fc\uff0c\u5c06\u4e8b\u95f9\u5927\uff0c\u597d\u903c\u4ed6\u8c0b\u53cd\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u662f\u7684\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u66fe\u8bf4\u8fc7\u8840\u6848\u524d\u4e00\u4e2a\u6708\uff0c\u4f60\u89c1\u8fc7\u4eac\u91cc\u6765\u7684\u4e24\u4f4d\u592b\u5b50\u5148\u751f\uff0c\u539f\u6765\uff0c\u4ed6\u4eec \u5c31\u662f\u6765\u5411\u4f60\u901a\u62a5\u60c5\u51b5\u7684\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u738b\u5927\u4eba\u73b0\u5728\u624d\u60f3\u901a\uff0c\u5b9e\u5728\u7b97\u4e0d\u4e0a\u806a\u660e\u554a\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u864e\u6bd2\u4e0d\u98df\u5b50\uff0c\u91d1\u767e\u5e74\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u5229\u7528\u81ea\u5df1\u7684\u5973\u513f\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u6210\u5927\u4e8b\u8005\uff0c\u4e0d\u62d8\u5c0f\u8282\uff0c\u518d\u8bf4\uff0c\u6211\u4e5f\u6ca1\u6709\u5bb3\u6211\u7684\u5973\u513f\uff0c\u5979\u4ecd\u7136\u80fd\u4eab\u5c3d\u8363\u534e\u5bcc \u8d35\uff01\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u7239\uff0c\u4f60\u6709\u4e07\u8d2f\u5bb6\u8d22\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u6ee1\u610f\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u53f9\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u4e0d\u61c2\uff0c\u7239\u7684\u4e07\u8d2f\u5bb6\u8d22\u662f\u600e\u4e48\u6765\u7684\uff0c\u5e72\u4ec0\u4e48\u7528\u7684\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u90a3\u4e9b\u94b1\u4e0d\u662f\u6211\u4e2a\u4eba\u7684\uff0c\u53ea\u662f\u6211\u4eec\u7684\u6d3b\u52a8\u7ecf\u8d39\uff01\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u53ef\u4f60\u8fd9\u6837\u505a\uff0c\u4e0d\u662f\u5bb3\u4e86\u5973\u513f\u5417\uff1f\u7239\uff0c\u4f60\u8fd8\u53ea\u6709\u6211\u4e00\u4e2a\u5973\u513f\u554a\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u5c0f\u7709\uff0c\u4e00\u4e2a\u9a6c\u656c\u6587\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff01\u53ea\u8981\u5927\u8ba1\u6210\u529f\uff0c\u738b\u5b59\u516c\u5b50\u4efb\u4f60\u6311\u9009\uff01\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u53ef\u6211\u53ea\u7231\u656c\u6587\u4e00\u4e2a\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u5c0f\u7709\uff0c\u4f60\u4e0d\u8981\u8fd9\u6837\u56fa\u6267\uff01\u201d \u3000\u3000\u91d1\u5c0f\u7709\u7a81\u7136\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u767e\u5e74\uff0c\u4f60\u544a\u8bc9\u6211\uff0c\u5230\u5e95\u662f\u8c01\u5728\u5e55\u540e\u7b56\u5212\u3001\u6307\u4f7f\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u544a\u8bc9\u4f60\u7684\uff0c\u73b0\u5728\u5c31\u7b97\u6211\u8bf4\u51fa\u6765\uff0c\u4f60\u53c8\u80fd\u600e\u6837\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u541f\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u4ee5\u4e3a\u4f60\u4eec\u5df2\u7ecf\u80dc\u5238\u5728\u63e1\u4e86\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u7a0b\u5c0f\u8776\uff0c\u4f60\u53ef\u8c13\u662f\u673a\u5173\u7b97\u5c3d\uff0c\u4f0f\u4e0b\u4e86\u4e09\u652f\u5947\u5175\uff0c\u53ef\u662f\u4f60\u5927\u6982\u7edd\u5bf9\u60f3\u4e0d\u5230\u6211 \u4eec\u6709\u8fd9\u4e48\u5f3a\u5927\u7684\u5b9e\u529b\uff01\u201d \u3000\u3000\u4ed6\u987f\u4e86\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u73b0\u5728\u5df2\u6b7b\u4f24\u6b86\u5c3d\u4e86\uff0c\u4ea4\u51fa\u738b\u5c11\u537f\uff0c\u6211\u53ef\u80fd\u8fd8\u4f1a\u7559\u7ed9\u4f60\u4eec\u4e00\u6761\u6d3b \u8def\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u767e\u5e74\uff0c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u6ca1\u6709\u7b2c\u56db\u652f\u4f0f\u5175\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u5927\u7b11\u9053\uff1a\u201c\u4f60\u624b\u4e0b\u90a3\u4e9b\u6c34\u5e08\u80fd\u80dc\u5f97\u8fc7\u6211\u4eec\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6211\u4eec\u90fd\u8fd8\u6d3b\u7740\uff0c\u8fd8\u80fd\u4e0e\u4f60\u4eec\u4e00\u6218\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u53c8\u5927\u7b11\u4e00\u9635\uff0c\u7a81\u7136\u9053\uff1a\u201c\u65b9\u8f6e\uff0c\u547c\u5ef6\u8fdc\uff01\u201d \u3000\u3000\u65b9\u8f6e\u548c\u4e0d\u8001\u4e66\u751f\u4e00\u9f50\u9053\uff1a\u201c\u5c5e\u4e0b\u5728\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4e0a\u53bb\u6740\u4e86\u4ed6\u4eec\uff01\u201d \u3000\u3000\u65b9\u8f6e\u3001\u4e0d\u8001\u4e66\u751f\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u4e8c\u4eba\u4e3e\u6b65\u5411\u524d\u8d70\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e00\u4e2a\u7ea2\u8863\u6740\u624b\u7a81\u7136\u95ef\u5230\u91d1\u767e\u5e74\u8eab\u8fb9\uff0c\u9053\uff1a\u201c\u4e1c\u4e3b\uff0c\u6740\u9e21\u7109\u7528\u725b\u5200\uff0c\u5c5e\u4e0b\u53bb\u5c31\u8db3\u591f \u4e86\uff1f\u201d \u3000\u3000\u738b\u5c11\u537f\u6012\u543c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u626f\u5f00\u4f60\u7684\u8499\u9762\u5e03\uff01\u201d \u3000\u3000\u7ea2\u8863\u6740\u624b\u5927\u7b11\u4e00\u58f0\uff0c\u626f\u5f00\u9762\u5e55\uff0c\u4e0d\u662f\u8d3e\u82f1\uff0c\u53c8\u662f\u4f55\u4eba\uff01 \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u679c\u7136\u662f\u4f60\uff0c\u96be\u602a\u90a3\u5929\u4f60\u653e\u8d70\u4e86\u60f3\u523a\u6740\u6211\u7684\u5929\u67ab\u9053\u957f\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u767e\u5e74\uff0c\u4f60\u4eec\u5df2\u7ecf\u9732\u51fa\u4e86\u9762\u76ee\uff0c\u4f60\u5e9c\u4e2d\u7684\u90a3\u4e9b\u4eba\u8fd8\u8499\u7740\u9762\u5e72\u4ec0\u4e48\u5462\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u8ba9\u4f60\u4eec\u505a\u4e2a\u660e\u767d\u9b3c\u7f62\uff01\u201d \u3000\u3000\u4ed6\u4e00\u6325\u624b\uff0c\u4ed6\u8eab\u540e\u7684\u4e00\u7fa4\u6740\u624b\u5168\u90fd\u5c06\u9762\u4e0a\u7684\u9762\u5e03\u548c\u4eba\u76ae\u9762\u5177\u63ed\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u7fa4\u4eba\u624b\uff0c\u5c31\u6709\u5bd2\u5c71\u56db\u5200\uff0c\u7f57\u6d6e\u4e09\u5251\uff0c\u94c1\u62f3\u4e25\u65b9\uff0c\u5feb\u5200\u738b\u526a\uff0c\u9a6c\u6c0f\u5144\u5f1f\uff01 \u3000\u3000\u91d1\u767e\u5e74\u7b11\u9053\uff1a\u201c\u6ee1\u610f\u4e86\u5427\uff1f\u201d\u56de\u5934\u9053\uff1a\u201c\u5c06\u4ed6\u4eec\u5168\u90e8\u683c\u6740\uff0c\u4e00\u4e2a\u4e0d\u7559\uff01\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u662f\uff0c\u4e1c\u4e3b\u653e\u5fc3\uff01\u201d \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u91d1\u5c0f\u7709\u7a81\u7136\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u7239\uff0c\u4f60\u4e5f\u8981\u6740\u6211\u5417\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5c0f\u7709\uff0c\u4e0d\u8981\u80e1\u8bf4\uff0c\u7239\u6b64\u751f\u53ea\u6709\u4f60\u4e00\u4e2a\u4eb2\u9aa8\u8089\uff0c\u600e\u4e48\u820d\u5f97\u6740\u4f60\uff01\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u90a3\u597d\uff0c\u4f60\u547d\u4ee4\u4f60\u7684\u4eba\u5168\u90fd\u9000\u4e0b\u5c71\u53bb\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4f60\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u653e\u738b\u5927\u4eba\u548c\u603b\u6355\u5934\u4ed6\u4eec\u8d70\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u4e0d\u884c\uff01\u201d \u3000\u3000\u91d1\u5c0f\u7709\u624b\u8155\u4e00\u7ffb\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u67c4\u950b\u5230\u7684\u5c0f\u5200\uff01 \u3000\u3000\u5200\u5c16\u5df2\u5bf9\u7740\u5979\u81ea\u5df1\u7684\u5589\u5499\u3002 \u3000\u3000\u91d1\u5c0f\u7709\u51b7\u7b11\u9053\uff1a\u201c\u7239\uff0c\u4f60\u4e0d\u653e\u4eba\uff0c\u6211\u5c31\u6b7b\u5728\u4f60\u9762\u524d\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u5927\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u7709\uff0c\u4e0d\u884c\u554a\uff01\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u90a3\u4f60\u4eec\u5c31\u8ba9\u5f00\uff01\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u91d1\u59d1\u5a18\uff0c\u4f60\u662f\u4e00\u4e2a\u597d\u5973\u5b69\u5b50\uff0c\u6211\u4eec\u90fd\u5f88\u8c22\u8c22\u4f60\uff0c\u4f60\u5230\u4f60\u7239\u90a3\u8fb9\u53bb\u5427\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u4f38\u624b\u593a\u5c0f\u7709\u624b\u4e2d\u7684\u5229\u5200\u3002 \u3000\u3000\u91d1\u5c0f\u7709\u5c16\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u52a8\uff0c\u8c01\u52a8\u6211\u90fd\u4f1a\u6740\u6b7b\u81ea\u5df1\u3002\u201d \u3000\u3000\u91d1\u767e\u5e74\u6ee1\u8138\u60f6\u6025\uff0c\u4e0d\u77e5\u6240\u63aa\uff01 \u3000\u3000\u8840\u624b\u65b9\u8f6e\u9053\uff1a\u201c\u8001\u5927\uff0c\u5feb\u5f53\u673a\u7acb\u65ad\u5427\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u8001\u54e5\u53ef\u53ea\u6709\u5979\u4e00\u4e2a\u5b69\u5b50\uff0c\u8981\u4e48\u2026\u2026\u201d \u3000\u3000\u4e0d\u8001\u4e66\u751f\u9053\uff1a\u201c\u91d1\u8001\uff0c\u603b\u4e0d\u80fd\u4e3a\u4e86\u4e00\u4e2a\u4eba\uff0c\u6bc1\u4e86\u5168\u5c40\u5427\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4eec\u5df2\u7ecf\u5168\u76d8\u5931\u8d25\u4e86\uff01\u201d \u3000\u3000\u8840\u624b\u65b9\u8f6e\u5927\u9a82\u9053\uff1a\u201c\u653e\u5c41\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u4e0d\u4fe1\uff1f\u4f60\u4eec\u56de\u5934\u770b\u4e00\u770b\u5c31\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u8840\u624b\u65b9\u8f6e\u4e00\u56de\u5934\uff0c\u7acb\u5373\u60ca\u53eb\u4e00\u58f0\u3002 \u3000\u3000\u4e0d\u8fdc\u5904\uff0c\u7a81\u7136\u51fa\u73b0\u4e86\u4e00\u4e2a\u5e9e\u5927\u800c\u6574\u9f50\u7684\u9a91\u5175\u65b9\u9635\uff0c\u9635\u4e2d\u9ad8\u4e3e\u4e00\u9762\u5927\u65d7\uff0c\u4e0a\u4e66\u4e00\u4e2a\u6597\u5927\u7684 \u201c\u9a6c\u201d\u5b57\uff01 \u3000\u3000\u9a6c\u957f\u5c71\u6b63\u9a91\u5728\u9a6c\u4e0a\uff0c\u5728\u9635\u524d\u6307\u6325\uff01 \u3000\u3000\u8fd9\u961f\u9a91\u5175\u8db3\u6709\u4e09\u5343\u4eba\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u91d1\u767e\u5e74\uff0c\u4f60\u4eec\u7684\u6b66\u529f\u7684\u786e\u5f88\u9ad8\uff0c\u4f46\u4f60\u4eec\u624b\u4e0b\u7684\u6740\u624b\u4eec\u62fc\u5f97\u8fc7\u8fd9\u4e09\u5343\u5168\u56fd\u6700 \u7cbe\u9510\u7684\u6c34\u5e08\u9a91\u5175\u7684\u5305\u56f4\u548c\u4e07\u7bad\u9f50\u5c04\u5417\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u53cc\u76ee\u5706\u7741\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5c0f\u7709\uff0c\u4f60\u4e0d\u8981\u56fa\u6267\uff0c\u4f60\u770b\uff0c\u4ed6\u4eec\u5c31\u8981\u6765\u8981\u4e3a\u7236\u7684\u6027\u547d\u4e86\uff01\u201d \u3000\u3000\u91d1\u5c0f\u7709\u9053\uff1a\u201c\u90a3\u662f\u4f60\u548e\u7531\u81ea\u53d6\uff01\u201d \u3000\u3000\u8840\u624b\u65b9\u8f6e\u9053\uff1a\u201c\u8001\u5927\uff0c\u4e0d\u8981\u518d\u72b9\u8c6b\u4e86\uff01\u5148\u6740\u4e86\u4ed6\u4eec\uff0c\u518d\u4ee5\u7a0b\u5c0f\u8776\u3001\u738b\u5c11\u537f\u4e3a\u4eba\u8d28\uff0c\u8981\u631f \u9a6c\u957f\u5c71\uff01\u201d \u3000\u3000\u4e0d\u8001\u4e66\u751f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9a6c\u957f\u5c71\u662f\u4e0d\u5f97\u4e0d\u6709\u6240\u987e\u5fcc\uff0c\u6211\u4eec\u5c31\u8fd8\u6709\u8f6c\u8d25\u4e3a\u80dc\u7684\u673a\u4f1a\u4e86\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u8fd8\u5728\u8fdf\u7591\u7740\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u5bb3\u6015\u91d1\u5c0f\u7709\u4f1a\u8bf4\u5230\u505a\u5230\uff0c\u81ea\u6740\u5728\u4ed6\u9762\u524d\uff01 \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u7a0b\u5c0f\u8776\u5ffd\u7a81\u7136\u9053\uff1a\u201c\u60dc\u7389\uff0c\u52a8\u624b\uff01\u201d \u3000\u3000\u60dc\u7389\u53cc\u624b\u8fde\u626c\uff0c\u4e00\u8fde\u4e32\u7206\u70b8\u58f0\u540e\uff0c\u5c71\u9876\u56db\u5468\u817e\u8d77\u4e00\u9635\u6d53\u6d53\u7684\u70df\u96fe\uff01 \u3000\u3000\u4e0d\u8001\u4e66\u751f\u60ca\u53eb\u9053\uff1a\u201c\u6709\u6bd2\uff0c\u70df\u91cc\u6709\u5267\u6bd2\uff01\u5feb\u8eb2\u5f00\uff0c\u5c4f\u4f4f\u547c\u5438\uff01\u201d \u3000\u3000\u8fd9\u662f\u60dc\u7389\u5728\u7a0b\u5c0f\u8776\u3001\u5c0f\u6587\u3001\u5c0f\u96c5\u7684\u5e2e\u52a9\u4e0b\uff0c\u5728\u5e9c\u8859\u91cc\u79d8\u5bc6\u7814\u5236\u7684\u6bd2\u5f39\uff0c\u73b0\u5728\u679c\u7136\u6392\u4e0a\u4e86 \u7528\u573a\u3002 \u3000\u3000\u6740\u624b\u4eec\u7eb7\u7eb7\u9000\u907f\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u4f4e\u58f0\u9053\uff1a\u201c\u4fdd\u62a4\u738b\u5927\u4eba\uff0c\u51b2\u4e0b\u5c71\u53bb\uff01\u201d \u3000\u3000\u51b2\u5230\u5c71\u4e0b\uff0c\u4e0e\u9a6c\u957f\u5c71\u6c47\u5408\u540e\uff0c\u4ed6\u4eec\u624d\u53d1\u73b0\u91d1\u5c0f\u7709\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u56de\u9996\u5c71\u4e0a\uff0c\u6bd2\u70df\u5df2\u7ecf\u6563\u5c3d\u3002 \u3000\u3000\u91d1\u5c0f\u7709\u4e3e\u5200\u6a2a\u5728\u8116\u5b50\u4e0a\uff0c\u4e3e\u773c\u671b\u5929\uff01 \u3000\u3000\u91d1\u767e\u5e74\u4e00\u770b\u7a0b\u5c0f\u8776\u7b49\u4eba\u8d81\u4e71\u7a81\u56f4\u4e86\uff0c\u4e0d\u7981\u6025\u6012\u653b\u5fc3\uff0c\u518d\u770b\u91d1\u5c0f\u7709\u4ecd\u6ca1\u6709\u653e\u4e0b\u5229\u5200\uff0c\u5fc3\u4e2d \u66f4\u6025\uff01 \u3000\u3000\u53ea\u542c\u91d1\u5c0f\u7709\u5927\u58f0\u547c\u558a\u9053\uff1a\u201c\u656c\u6587\u3001\u656c\u6587\uff0c\u4f60\u4ea1\u7075\u4e0d\u706d\uff0c\u7b49\u4e00\u7b49\u5c0f\u7709\uff0c\u6211\u5c31\u6765\u966a\u4f60\u4e86\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u53f3\u624b\u7528\u529b\u4e00\u62c9\uff0c\u77ac\u95f4\u9c9c\u8840\u55b7\u6d8c\uff0c\u4eba\u6162\u6162\u5730\u5012\u5728\u5730\u4e0a\uff01 \u3000\u3000\u91d1\u767e\u5e74\u60e8\u547c\u4e00\u58f0\uff0c\u4e24\u773c\u4e00\u7ffb\uff0c\u6655\u4e86\u8fc7\u53bb\uff01 \u3000\u3000\u4e0d\u8001\u4e66\u751f\u8fde\u5fd9\u5bf9\u4ed6\u8fdb\u884c\u65bd\u6551\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u5bf9\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u9a6c\u5927\u4eba\uff0c\u8bf7\u4e0b\u4ee4\u8fdb\u653b\u5427\uff01\u201d \u3000\u3000\u8fd9\u4e09\u5343\u540d\u9a91\u5175\u4e2d\uff0c\u6709\u4e00\u5343\u4eba\u90fd\u643a\u5e26\u6709\u8fde\u5f29\u94c1\u7bad\uff0c\u5728\u9a6c\u957f\u5c71\u7684\u6307\u6325\u4e0b\uff0c\u4e09\u5343\u9a91\u5175\u5df2\u5c06\u5c0f\u5c71 \u56f4\u5f97\u6c34\u6cc4\u4e0d\u901a\uff01 \u3000\u3000\u91d1\u767e\u5e74\u9192\u4e86\u8fc7\u6765\uff0c\u53cc\u76ee\u5c3d\u8d64\uff0c\u4e3e\u5200\u5927\u543c\u9053\uff1a\u201c\u5f1f\u5144\u4eec\uff0c\u542c\u6211\u53f7\u4ee4\uff0c\u51b2\u4e0b\u5c71\u5761\uff0c\u6740\u5149\u4ed6 \u4eec\uff01\u201d \u3000\u3000\u6740\u624b\u4eec\u8f70\u7136\u4e00\u58f0\uff0c\u201c\u6740\u5440\uff01\u201d\u5404\u4e3e\u5175\u5200\uff0c\u51b2\u4e0b\u5c71\u6765\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u653e\u7bad\uff01\u201d \u3000\u3000\u767e\u4f59\u679d\u98de\u7bad\u75be\u5c04\u800c\u51fa\uff01 \u3000\u3000\u6740\u624b\u4eec\u5012\u4e0b\u4e86\u4e00\u5927\u7247\uff01 \u3000\u3000\u9a6c\u957f\u5c71\u53c8\u9053\uff1a\u201c\u51b2\u4e0a\u53bb\uff01\u51b2\u6563\u4ed6\u4eec\uff01\u201d \u3000\u3000\u4e24\u5343\u540d\u9a91\u5175\u9ad8\u4e3e\u957f\u5200\uff0c\u7eb5\u9a6c\u51b2\u4e0a\uff0c\u7acb\u5373\u5c06\u6740\u624b\u4eec\u5206\u5272\u5305\u56f4\u8d77\u6765\u3002 \u3000\u3000\u4f46\u5269\u4e0b\u7684\u4e8c\u767e\u4f59\u540d\u6740\u624b\u90fd\u662f\u6740\u624b\u96c6\u56e2\u7684\u7cbe\u82f1\u4eba\u7269\uff0c\u4e2a\u4e2a\u6b66\u529f\u9ad8\u5f3a\uff0c\u53ea\u89c1\u4ed6\u4eec\u5728\u9a91\u5175\u9635\u4e2d \u5de6\u51b2\u53f3\u7a81\uff0c\u867d\u7136\u4e0d\u65f6\u4e5f\u6709\u4eba\u88ab\u9a91\u5175\u683c\u6740\uff0c\u4f46\u9a91\u5175\u7684\u4f24\u4ea1\u66f4\u5927\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u675c\u53f8\u4e3b\u3001\u5c0f\u6587\u3001\u60dc\u7389\u3001\u963f\u6a2a\u3001\u963f\u4fdd\uff0c\u4f60\u4eec\u4e0a\u53bb\uff0c\u5148\u5c06\u91d1\u767e\u5e74\u624b\u4e0b\u4e4b\u4eba\u4e00\u4e2a \u4e2a\u6d88\u706d\u6389\uff01\u201d \u3000\u3000\u4e94\u4eba\u9886\u547d\uff0c\u98de\u8eab\u51b2\u8fdb\u4e86\u6218\u9635\u3002 \u3000\u3000\u59da\u987a\u5929\u624b\u4e0b\u7684\u9752\u8863\u4eba\u4e5f\u90fd\u51b2\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u6740\u624b\u4eec\u5c31\u6e10\u6e10\u6e83\u8d25\u4e86\u3002 \u3000\u3000\u5df2\u7ecf\u6709\u6740\u624b\u5f00\u59cb\u5411\u56db\u9762\u9003\u4ea1\uff0c\u4f46\u9003\u4e0d\u4e86\u591a\u8fdc\uff0c\u7acb\u523b\u5c31\u4f1a\u88ab\u5c04\u6210\u523a\u732c\uff01 \u3000\u3000\u6fc0\u6218\u6301\u7eed\u4e86\u6574\u6574\u4e00\u4e2a\u65f6\u8fb0\uff0c\u7ec8\u4e8e\u6e10\u6e10\u5e73\u606f\u3002 \u3000\u3000\u6740\u624b\u96c6\u56e2\u53ea\u5269\u4e0b\u4e86\u91d1\u767e\u5e74\u3001\u8840\u624b\u65b9\u8f6e\u3001\u4e0d\u8001\u4e66\u751f\u3001\u7f57\u6d6e\u4e09\u5251\u548c\u5341\u51e0\u540d\u7ea2\u8863\u6740\u624b\u4e86\u3002 \u3000\u3000\u6c34\u5e08\u9a91\u5175\u7684\u635f\u5931\u5f88\u5927\uff0c\u8db3\u8db3\u4f24\u4ea1\u4e86\u6709\u5343\u4f59\u4eba\uff01 \u3000\u3000\u963f\u6a2a\u3001\u963f\u4fdd\u4e5f\u90fd\u53d7\u4e86\u4f24\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u88ab\u91d1\u767e\u5e74\u548c\u4e0d\u8001\u4e66\u751f\u51fb\u4f24\u7684\u3002 \u3000\u3000\u9a6c\u957f\u5c71\u9ad8\u58f0\u9053\uff1a\u201c\u91d1\u767e\u5e74\uff0c\u4f60\u5927\u52bf\u5df2\u53bb\uff0c\u8fd8\u4e0d\u675f\u624b\u5c31\u7f1a\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u72c2\u7b11\u9053\uff1a\u201c\u9a6c\u957f\u5c71\uff0c\u4f60\u4e0d\u8981\u5f97\u610f\uff01\u4f60\u7684\u4eba\u5458\u591a\uff0c\u4f46\u7559\u5f97\u4f4f\u6211\u4eec\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u56db\u4e0b\u4e00\u770b\uff0c\u4e0d\u7981\u6697\u6697\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u91d1\u767e\u5e74\u6ca1\u6709\u8bf4\u5927\u8bdd\uff0c\u5355\u51ed\u9a6c\u957f\u5c71\u624b\u4e0b\u7684\u8fd1\u4e24\u5343\u540d\u9a91\u5175\uff0c\u7684\u786e\u7559\u4e0d\u4f4f\u4ed6\u4eec\u3002 \u3000\u3000\u7a0b\u5c0f\u8776\u672c\u4e0d\u662f\u91d1\u767e\u5e74\u7684\u5bf9\u624b\uff0c\u73b0\u5728\u66f4\u662f\u91cd\u4f24\u5728\u8eab\uff0c\u65e0\u529b\u518d\u6218\uff0c\u963f\u6a2a\u3001\u963f\u4fdd\u3001\u5c0f\u96c5\uff0c\u4e5f\u90fd \u53d7\u4e86\u4f24\uff0c\u5434\u94c1\u5cf0\u4e09\u4eba\u5c31\u66f4\u4e0d\u7528\u63d0\u4e86\u3002 \u3000\u3000\u675c\u671b\u6708\u548c\u60dc\u7389\u7684\u60c5\u51b5\u8981\u7a0d\u597d\u4e00\u4e9b\uff0c\u4f46\u8981\u60f3\u80dc\u8fc7\u91d1\u767e\u5e74\u4e09\u4eba\uff0c\u662f\u7edd\u5bf9\u4e0d\u53ef\u80fd\u7684\u3002 \u3000\u3000\u518d\u8bf4\uff0c\u91d1\u767e\u5e74\u624b\u4e0b\u8fd8\u6709\u6570\u5341\u540d\u9ad8\u624b\u3002 \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u7559\u5f97\u9752\u5c71\u5728\uff0c\u4e0d\u6015\u6ca1\u67f4\u70e7\uff0c\u8fd9\u6b21\u6211\u4eec\u867d\u7136\u5931\u8d25\u4e86\uff0c\u4f46\u53ea\u8981\u91d1\u67d0\u4e0d\u6b7b\uff0c\u6709\u4f60 \u4eec\u53d7\u7f6a\u7684\u65f6\u5019\uff01\u201d \u3000\u3000\u9a6c\u957f\u5c71\u95ee\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u7b49\u3002\u6211\u4eec\u662f\u6ca1\u6cd5\u62d6\u4f4f\u4ed6\u4eec\uff01\u201d \u3000\u3000\u9a6c\u957f\u5c71\u9053\uff1a\u201c\u542c\u603b\u6355\u5934\u7684\u610f\u601d\uff0c\u8fd8\u6709\u9ad8\u624b\u4f1a\u8d76\u6765\u53c2\u6218\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u662f\u7684\uff0c\u6709\u4e00\u4e2a\u4eba\u5f88\u5feb\u5c31\u4f1a\u8d76\u6765\uff0c\u4e0e\u91d1\u767e\u5e74\u7b97\u4e00\u7b14\u65e7\u8d26\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u7b49\u4eba\u5df2\u5f00\u59cb\u6162\u6162\u5f80\u5357\u9000\u53bb\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u675c\u53f8\u4e3b\u3001\u60dc\u7389\uff01\u4e0a\u53bb\u7f20\u4f4f\u4ed6\u4eec\uff01\u4f46\u4e0d\u8981\u786c\u6597\uff01\u201d \u3000\u3000\u4e8c\u4eba\u98de\u8eab\u6251\u4e0a\uff0c\u53cc\u5251\u9f50\u51fa\uff0c\u523a\u5411\u4e0d\u8001\u4e66\u751f\uff01 \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u627e\u6b7b\uff01\u201d\u6389\u5200\u63a5\u4f4f\u4e86\u675c\u671b\u6708\u3002 \u3000\u3000\u4e0d\u8001\u4e66\u738b\u548c\u8840\u624b\u65b9\u8f6e\u4e00\u9f50\u653b\u5411\u60dc\u7389\uff01 \u3000\u3000\u963f\u6a2a\u3001\u963f\u4fdd\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u6211\u4eec\u8fd8\u6709\u4f59\u529b\u518d\u6218\uff01\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5feb\u4e0a\u5427\uff01\u201d \u3000\u3000\u4e8c\u4eba\u98de\u8eab\u800c\u4e0a\uff0c\u4e00\u4eba\u52a9\u60dc\u7389\uff0c\u4e00\u4eba\u4e0e\u7f57\u6d6e\u4e09\u5251\u7f20\u6597\uff01 \u3000\u3000\u7a81\u7136\uff0c\u5357\u9762\u6709\u4e24\u540d\u4eba\u5f71\u5982\u6d41\u661f\u5212\u8fc7\u591c\u7a7a\u822c\u75be\u63a0\u800c\u81f3\u3002 \u3000\u3000\u4e00\u4eba\u5927\u7b11\u9053\uff1a\u201c\u91d1\u80e1\u5b50\u3001\u91d1\u5927\u4e2d\uff01\u4f60\u8fd8\u8ba4\u8bc6\u8001\u592b\u5417\uff1f\u201d \u3000\u3000\u91d1\u767e\u5e74\u4e00\u6014\u56de\u5934\uff0c\u5927\u60ca\u9053\uff1a\u201c\u68a6\u5e7b\u4e4b\u5200\uff1f\u201d \u3000\u3000\u6765\u4eba\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u8001\u592b\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u53f3\u624b\u4e00\u6325\uff0c\u4e00\u67c4\u53c8\u7a84\u3001\u53c8\u8584\u7684\u4e8c\u5c3a\u77ed\u5200\u653b\u5411\u91d1\u767e\u5e74\uff01 \u3000\u3000\u675c\u671b\u6708\u7acb\u5373\u4e22\u4e0b\u91d1\u767e\u5e74\uff0c\u6325\u5251\u51b2\u5411\u90a3\u4e9b\u7ea2\u8863\u6740\u624b\uff01 \u3000\u3000\u4e0e\u68a6\u5e7b\u4e4b\u5200\u4e00\u540c\u8d76\u6765\u7684\uff0c\u6b63\u662f\u94f6\u8863\u4eba\uff01 \u3000\u3000\u94f6\u8863\u4eba\u4e00\u51b2\u51fb\u8fdb\u6218\u573a\uff0c\u957f\u5251\u4e00\u51fa\u9798\uff0c\u4fbf\u6709\u4e24\u540d\u6740\u624b\u5012\u5728\u4e86\u5730\u4e0a\uff01 \u3000\u3000\u4ed6\u7b2c\u4e09\u5251\u523a\u5411\u4e86\u4e0d\u8001\u4e66\u751f\u3002 \u3000\u3000\u60dc\u7389\u4e5f\u95ea\u8eab\u8ba9\u5f00\uff0c\u653b\u5411\u4e86\u7f57\u6d6e\u4e09\u5251\uff01 \u3000\u3000\u8fd9\u4e0b\u5f62\u52bf\u5c31\u660e\u786e\u4e86\uff0c\u5f88\u5feb\uff0c\u573a\u4e2d\u5c31\u53ea\u5269\u4e0b\u4e86\u91d1\u767e\u5e74\u3001\u4e0d\u8001\u4e66\u751f\u548c\u8840\u624b\u65b9\u8f6e\uff0c\u4ed6\u4eec\u624b\u4e0b\u7684 \u6740\u624b\u4eec\u5df2\u5c3d\u88ab\u6740\u3002 \u3000\u3000\u68a6\u5e7b\u4e4b\u5200\u7b11\u9053\uff1a\u201c\u91d1\u5927\u4e2d\uff0c\u6ca1\u60f3\u5230\u5427\uff0c\u8001\u592b\u906d\u4f60\u6697\u7b97\u4e4b\u540e\uff0c\u8fd8\u80fd\u6062\u590d\u6b66\u529f\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u9053\uff1a\u201c\u7a83\u8d70\u4e09\u5b9d\u7684\uff0c\u679c\u7136\u662f\u4f60\uff01\u201d \u3000\u3000\u68a6\u5e7b\u4e4b\u5200\u7b11\u9053\uff1a\u201c\u4f60\u9519\u4e86\uff0c\u53d6\u5f97\u4e09\u5b9d\u7684\uff0c\u662f\u6211\u8fd9\u4e2a\u5f92\u513f\u3002\u201d\u6307\u7684\u662f\u94f6\u8863\u4eba\u3002 \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u603b\u6355\u5934\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u5341\u5e74\u524d\uff0c\u91d1\u767e\u5e74\u662f\u6c5f\u6e56\u4e0a\u6076\u540d\u662d\u8457\u7684\u5927\u76d7\uff0c\u4ed6\u672c\u540d\u53eb\u91d1\u5927\u4e2d\uff0c\u4e3a\u4e86\u9003\u907f\u68a6 \u5e7b\u4e4b\u5200\u7684\u8ffd\u6355\uff0c\u4fbf\u7ea6\u4e86\u9ed1\u9053\u4e0a\u7684\u5e2e\u624b\uff0c\u8bbe\u8ba1\u6697\u7b97\uff0c\u68a6\u5e7b\u4e4b\u5200\u4e2d\u8ba1\uff0c\u4e00\u8eab\u6b66\u529f\u88ab\u5e9f\u3002\u5341\u5e74\u6765\uff0c \u68a6\u5e7b\u4e4b\u5200\u4e00\u76f4\u5728\u4eac\u5e08\u7684\u5e2e\u52a9\u4e4b\u4e0b\u6062\u590d\u6b66\u529f\uff0c\u4f46\u6700\u540e\u4e00\u5173\u5fc5\u987b\u8981\u5343\u5e74\u8001\u53c2\u548c\u6c34\u706b\u76f8\u6d4e\u77f3\u73e0\u5408\u836f\uff0c \u65b9\u80fd\u987a\u5229\u901a\u8fc7\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u90a3\u4e2a\u5230\u91d1\u5e9c\u593a\u8981\u4e09\u5b9d\u7684\u4eba\u662f\u6211\u4eec\u7684\u4eba\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u7b11\u9053\uff1a\u201c\u5c31\u662f\u94f6\u8863\u4eba\u6539\u626e\u7684\uff0c\u6211\u4eec\u4e00\u76f4\u6000\u7591\u626c\u5dde\u5341\u5927\u8c6a\u5bcc\u4e4b\u4e2d\u6709\u67d0\u738b\u7237\u7684\u4eba\uff0c\u4f46 \u4e0d\u77e5\u7a76\u7adf\u662f\u8c01\uff0c\u540e\u6765\u5f97\u77e5\u91d1\u5e9c\u6709\u4e09\u5b9d\uff0c\u4fbf\u5f00\u59cb\u6000\u7591\u91d1\u767e\u5e74\uff0c\u56e0\u4e3a\u4e09\u5b9d\u4e2d\u7684\u6c34\u706b\u76f8\u6d4e\u77f3\u73e0\u53ea\u5bf9 \u68a6\u5e7b\u4e4b\u5200\u6765\u8bf4\u6709\u7528\uff0c\u5bf9\u5e38\u4eba\u4e00\u65e0\u7528\u5904\uff0c\u800c\u91d1\u767e\u5e74\u6b63\u662f\u56e0\u4e3a\u77e5\u9053\u8fd9\u4e00\u70b9\uff0c\u6240\u4ee5\u624d\u5c06\u77f3\u73e0\u636e\u4e3a\u5df2 \u6709\uff0c\u6211\u4eec\u67e5\u51fa\u91d1\u767e\u5e74\u5c31\u662f\u5927\u76d7\u91d1\u5927\u4e2d\u540e\uff0c\u5c31\u66f4\u6000\u7591\u4ed6\u662f\u67d0\u738b\u7237\u7684\u722a\u7259\u4e86\u3002\u4e8e\u662f\u8ba9\u94f6\u8863\u4eba\u524d\u53bb \u8bd5\u63a2\uff0c\u91d1\u767e\u5e74\u679c\u7136\u4e0d\u80af\u4ea4\u51fa\u77f3\u73e0\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u4ed6\u4eec\u5ac1\u7978\u68a6\u5e7b\u4e4b\u5200\uff0c\u5c31\u662f\u56e0\u4e3a\u4ed6\u4eec\u77e5\u9053\u68a6\u5e7b\u4e4b\u5200\u6b66\u529f\u5df2\u5e9f\u3002\u800c\u4e14\uff0c\u56e0\u94f6\u8863 \u4eba\u7684\u73b0\u8eab\uff0c\u4e5f\u77e5\u9053\u68a6\u5e7b\u4e4b\u5200\u5c31\u5728\u626c\u5dde\u9644\u8fd1\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\uff0c\u53ea\u662f\u4ed6\u4eec\u6ca1\u60f3\u5230\u68a6\u5e7b\u4e4b\u5200\u662f\u4eac\u5e08\u591a\u5e74\u8001\u53cb\uff0c\u800c\u4e14\u4e00\u76f4\u5728\u4e3a\u5211\u90e8 \u5de5\u4f5c\uff0c\u6240\u4ee5\u9020\u4e00\u5ac1\u7978\uff0c\u53cd\u800c\u66b4\u9732\u4e86\u8eab\u5206\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u6c14\u6025\u8d25\u574f\u5730\u9053\uff1a\u201c\u597d\uff0c\u4e00\u62db\u7b97\u9519\uff0c\u6ee1\u76d8\u7686\u8f93\uff0c\u6211\u8ba4\u4e86\uff01\u4f46\u4f60\u68a6\u5e7b\u4e4b\u5200\u53c8\u80fd\u628a\u6211\u600e \u4e48\u6837\uff1f\u201d \u3000\u3000\u68a6\u5e7b\u4e4b\u5200\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u5462\uff1f\u201d \u3000\u3000\u8840\u624b\u65b9\u8f6e\u4e00\u51b2\u800c\u4e0a\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e1c\u897f\uff0c\u6211\u65b9\u8f6e\u4e0d\u6015\u4f60\uff01\u201d\u624b\u4e2d\u957f\u5251\u75be\u523a\u68a6\u5e7b\u4e4b\u5200\u3002 \u3000\u3000\u68a6\u5e7b\u4e4b\u5200\u8f7b\u8f7b\u4e00\u53f9\uff0c\u968f\u968f\u4fbf\u4fbf\u5730\u6325\u5200\u3002 \u3000\u3000\u7a7a\u4e2d\u95ea\u8fc7\u4e00\u7247\u9752\u8499\u8499\u7684\u5200\u5149\uff0c\u82e5\u6709\u82e5\u65e0\u3002 \u3000\u3000\u5200\u5149\u6d88\u5931\u4e86\u3002\u65b9\u8f6e\u5f20\u4e86\u5f20\u5634\uff0c\u50cf\u662f\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4f46\u5374\u6ca1\u54c9\u51fa\u6765\uff0c\u624b\u91cc\u957f\u5251\uff0c\u201c\u5f53\u5577\u201d\u4e00\u58f0 \u6389\u5728\u4e86\u5730\u4e0a\u3002 \u3000\u3000\u7a81\u7136\uff0c\u4ed6\u7684\u4e00\u9897\u5934\u76f4\u51b2\u4e0a\u534a\u7a7a\uff0c\u8116\u5b50\u91cc\u55b7\u51fa\u4e00\u80a1\u8840\u7bad\uff01 \u3000\u3000\u597d\u5feb\u7684\u5200\uff01 \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u68a6\u5e7b\u4e4b\u5200\u5f97\u540d\u7684\u7531\u6765\uff0c\u4e00\u5200\u6325\u51fa\uff0c\u82e5\u6709\u82e5\u65e0\uff0c\u4f7f\u5bf9\u624b\u65e0\u4ece\u62db\u67b6\uff0c\u5c31 \u50cf\u662f\u9677\u5165\u4e86\u68a6\u5883\u4e00\u822c\uff0c\u800c\u975e\u6c5f\u6e56\u4e0a\u6240\u4f20\u8bf4\u7684\u4ec0\u4e48\u80fd\u5f79\u4eba\u51fa\u5200\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u548c\u4e0d\u8001\u4e66\u751f\u89c1\u65b9\u8f6e\u4e00\u62db\u88ab\u6740\uff0c\u9f50\u58f0\u72c2\u543c\uff0c\u5200\u5251\u5408\u74a7\uff0c\u51b2\u5411\u68a6\u5e7b\u4e4b\u5200\uff01 \u3000\u3000\u4ed6\u4eec\u4e8c\u4eba\u8fd9\u5957\u5206\u8fdb\u5408\u51fb\u4e4b\u672f\u770b\u6765\u5df2\u914d\u5408\u4e86\u5f88\u4e45\uff0c\u4e00\u65f6\u95f4\u7adf\u903c\u5f97\u68a6\u5e7b\u4e4b\u5200\u5de6\u8eb2\u53f3\u95ea\u3002 \u3000\u3000\u68a6\u5e7b\u4e4b\u5200\u663e\u7136\u662f\u56e0\u4e3a\u521a\u6062\u590d\u529f\u529b\u4e0d\u4e45\uff0c\u4ed6\u7684\u8eab\u6cd5\u8fd8\u4e0d\u662f\u5f88\u7075\u6d3b\uff0c\u8eb2\u95ea\u4e4b\u95f4\uff0c\u5de6\u81c2\u4e0a\u5df2\u6328 \u4e86\u4e0d\u8001\u4e66\u751f\u4e00\u5251\uff01 \u3000\u3000\u91d1\u767e\u5e74\u72c2\u7b11\u9053\uff1a\u201c\u5982\u4f55\uff0c\u6211\u65e9\u6599\u5230\u59d3\u5448\u7684\u8001\u602a\u4f1a\u60f3\u529e\u6cd5\u8ba9\u4f60\u6062\u590d\u6b66\u529f\uff0c\u6240\u4ee5\u5341\u5e74\u6765\uff0c\u4e13 \u95e8\u4e0e\u547c\u5ef6\u8d24\u5f1f\u7ec3\u5c31\u4e86\u4e00\u5957\u4e13\u95e8\u5bf9\u4ed8\u4f60\u7684\u6b66\u529f\uff01\u201d \u3000\u3000\u94f6\u8863\u4eba\u6012\u53f1\u4e00\u58f0\uff0c\u6251\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u4e0d\u8fc7\u4e09\u62db\uff0c\u4ed6\u5df2\u88ab\u91d1\u767e\u5e74\u8e22\u7ffb\u5728\u5730\uff01 \u3000\u3000\u91d1\u767e\u5e74\u548c\u4e0d\u8001\u4e66\u751f\u76f4\u6251\u5411\u68a6\u5e7b\u4e4b\u5200\u3002 \u3000\u3000\u675c\u671b\u6708\u548c\u60dc\u7389\u4e00\u5de6\u4e00\u53f3\u75be\u51b2\u800c\u4e0a\uff0c\u594b\u529b\u7f20\u4f4f\u4e86\u4ed6\u4eec\uff01 \u3000\u3000\u91d1\u767e\u5e74\u773c\u770b\u68a6\u5e7b\u4e4b\u5200\u81ea\u5df1\u70b9\u7a74\u6b62\u4f4f\u4e86\u4f24\u53e3\u4e0a\u8fdb\u6d41\u7684\u9c9c\u8840\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u559d\u9053\uff1a\u201c\u5feb\u4e0a\u53bb\u6740 \u4e86\u4ed6\uff01\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u662f\uff01\u201d\u6325\u8d77\u957f\u5251\uff0c\u5411\u524d\u51b2\u53bb\uff01 \u3000\u3000\u4ed6\u957f\u5251\u75be\u523a\u800c\u51fa\u65f6\uff0c\u4eba\u7a81\u7136\u8f6c\u4e86\u4e00\u4e2a\u8eab\uff0c\u5251\u5149\u4ece\u675c\u671b\u6708\u548c\u60dc\u7389\u4e8c\u4eba\u7684\u7a7a\u95f4\u76f4\u523a\u8fc7\u53bb\uff0c\u6b63 \u4e2d\u91d1\u767e\u5e74\u5de6\u817f\uff01 \u3000\u3000\u91d1\u767e\u5e74\u5927\u60ca\u9053\uff1a\u201c\u8d3e\u82f1\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u65ad\u559d\u9053\uff1a\u201c\u6740\u4e86\u4f60\uff01\u201d \u3000\u3000\u675c\u671b\u6708\u548c\u60dc\u7389\u4e5f\u5927\u4e3a\u5947\u602a\uff0c\u4e0d\u7981\u505c\u4f4f\u4e86\u624b\u3002 \u3000\u3000\u91d1\u767e\u5e74\u60ca\u6012\u4ea4\u52a0\uff0c\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8d3e\u82f1\u9053\uff1a\u201c\u6211\u662f\u68a6\u5e7b\u4e4b\u5200\u7684\u5927\u5f1f\u5b50\uff01\u6211\u6295\u8eab\u5230\u91d1\u5e9c\uff0c\u5c31\u662f\u8981\u67e5\u6e05\u4f60\u662f\u4e0d\u662f\u7a81\u7136\u4ece\u9ed1\u9053\u4e0a \u6d88\u5931\u7684\u91d1\u5927\u4e2d\uff01\u201d \u3000\u3000\u91d1\u767e\u5e74\u4eba\u543c\u2014\u58f0\uff0c\u6325\u5200\u6251\u5411\u8d3e\u82f1\uff01\u633a\u5251\u63a5\u4f4f\uff0c\u4e1d\u6beb\u4e0d\u843d\u4e0b\u98ce\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5012\u5728\u5730\u4e0a\u7684\u94f6\u8863\u4eba\u731b\u6251\u4e0a\u6765\uff0c\u5f20\u5f00\u53cc\u81c2\uff0c\u62b1\u4f4f\u4e86\u91d1\u767e\u5e74\u7684\u53cc\u817f\u3002 \u3000\u3000\u68a6\u5e7b\u4e4b\u5200\u53c8\u5df2\u51fa\u5200\u3002 \u3000\u3000\u5200\u5149\u4e00\u95ea\u3002 \u3000\u3000\u91d1\u767e\u5e74\u5927\u5f20\u7740\u5634\uff0c\u4e00\u7f15\u9c9c\u8840\u6162\u6162\u81ea\u4ed6\u7684\u80f8\u53e3\u6e17\u51fa\u3002 \u3000\u3000\u4ed6\u6454\u5012\u5728\u5730\uff0c\u6323\u624e\u4e86\u51e0\u4e0b\uff0c\u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u4e0d\u8001\u4e66\u751f\u8fd4\u8eab\u60f3\u9003\uff0c\u7a81\u7136\u53d1\u73b0\u81ea\u5df1\u80f8\u524d\u7a81\u51fa\u4e86\u4e24\u622a\u5251\u5149\u3002 \u3000\u3000\u4ed6\u8270\u96be\u5730\u8f6c\u8fc7\u8eab\uff0c\u53d1\u73b0\u523a\u4e2d\u4ed6\u7684\u6b63\u662f\u60dc\u7389\u548c\u675c\u671b\u6708\u3002 \u3000\u3000\u4ed6\u5583\u5583\u5730\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u62a5\u5e94\uff01\u201d\u4fbf\u5012\u5728\u5730\u4e0a\u6b7b\u53bb\u4e86\uff01 \u3000\u3000\u738b\u5c11\u537f\u7a81\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\uff0c\u6211\u4eec\u8fd8\u662f\u672a\u80fd\u95ee\u51fa\u67d0\u738b\u7237\u5230\u5e95\u662f\u8c01\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u4f60\u77e5\u9053\u68a6\u5e7b\u4e4b\u5200\u4e3a\u4ec0\u4e48\u73b0\u5728\u624d\u8d76\u6765\u5417\uff1f\u4ed6\u5c31\u662f\u53bb\u91d1\u5e9c\u91cc\u627e\u8bc1\u636e\u53bb \u4e86\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u636e\u8bf4\uff0c\u91d1\u5e9c\u89c2\u9e64\u697c\u4e2d\u673a\u5173\u91cd\u91cd\uff0c\u975e\u91d1\u767e\u5e74\u672c\u4eba\uff0c\u4e0d\u80fd\u6253\u5f00\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u62bf\u5634\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u738b\u5927\u4eba\u5fd8\u4e86\uff1f\u8d3e\u82f1\u5728\u91d1\u5e9c\u516b\u5e74\uff0c\u91d1\u767e\u5e74\u5df2\u5c06\u4ed6\u89c6\u4e3a\u5fc3\u8179\u4e86\u3002\u201d \u3000\u3000\u738b\u5c11\u537f\u9053\uff1a\u201c\u662f\u554a\uff0c\u8d3e\u82f1\u771f\u6b63\u662f\u5fcd\u8fb1\u8d1f\u91cd\uff0c\u53ef\u662f\u6211\u4eec\u5374\u4e00\u76f4\u5728\u6000\u7591\u4ed6\uff0c\u771f\u4e0d\u5e94\u8be5\uff01\u201d \u3000\u3000\u68a6\u5e7b\u4e4b\u5200\u8d70\u4e0a\u524d\u6765\uff0c\u81ea\u6000\u91cc\u638f\u51fa\u4e00\u5377\u4fe1\u51fd\uff0c\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u603b\u6355\u5934\uff0c\u8bc1\u636e\u5c31\u5728\u8fd9\u91cc\u9762\u3002\u201d \u3000\u3000\u7a0b\u5c0f\u8776\u63a5\u8fc7\u4fe1\u51fd\uff0c\u9053\uff1a\u201c\u592a\u597d\u4e86\uff01\u9a6c\u5927\u4eba\uff0c\u8bf7\u4f60\u901f\u56de\u626c\u5dde\uff0c\u4e25\u9632\u6f0f\u7f51\u6740\u624b\u8d81\u673a\u95f9\u4e8b\uff0c\u6211 \u4eec\u8981\u706b\u901f\u8d76\u5f80\u4eac\u57ce\uff0c\u8bf7\u6c42\u7f09\u62ff\u4e3b\u8c0b\u4e4b\u4eba\uff01\u201d \u3000\u3000\u5915\u9633\u897f\u4e0b\uff0c\u738b\u5c11\u537f\u4e00\u884c\u4e0d\u987e\u4f24\u75db\uff0c\u4ecd\u7136\u575a\u6301\u8d76\u8def\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u4eec\u77e5\u9053\uff0c\u5728\u4eac\u57ce\uff0c\u8fd8\u6709\u66f4\u4e25\u5cfb\u7684\u6311\u6218\u5728\u7b49\u7740\u4ed6\u4eec\uff01 \u3000\u3000\u2014\u2014\u300a\u5168\u4e66\u5b8c\u300b\u2014\u2014"}),showOnHomepage:!0}]}}]);