"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[21637],{65073:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u51b0\u6cb3\u6d17\u5251\u5f55",content:(0,t.jsx)(t.Fragment,{children:"\u7b2c\u4e00\u56de\u3000\u516b\u5973\u540c\u6765\u751f\u5f02\u4e8b\u3000\u4e03\u5e74\u6d41\u843d\u5269\u6c89\u54c0 \u3000\u3000\u8717\u89d2\u6d6e\u751f\u6362\uff0c\u6005\u5e74\u6765\u8f66\u5c18\u9a6c\u8ff9\uff0c\u5929\u6daf\u671b\u65ad\u3002\u9752\u51a2\u5bd2\u9e26\u557c\u672a\u4e86\uff0c\u51c4\u7edd\u6b64\u60c5\u96be\u6d63\u3002\u66f4\u8fd8\u6709\u5e7d \u95fa\u65e7\u4f34\uff0c\u6b7b\u522b\u751f\u79bb\u540c\u4e00\u6068\uff0c\u68a6\u9b42\u60ca\uff0c\u72b9\u4f3c\u95fb\u4f4e\u5524\u3002\u6e05\u6cea\u6ef4\uff0c\u9e33\u6795\u7554\u3002 \u3000\u3000\u6df1\u60c5\u8d1f\u5c3d\u957f\u9057\u6028\uff0c\u6b64\u751f\u7f18\uff0c\u955c\u82b1\u6c34\u6708\uff0c\u90fd\u6210\u7a7a\u5e7b\u3002\u5f39\u5251\u72c2\u6b4c\u4e34\u7edd\u585e\uff0c\u4e91\u6d77\u82cd\u832b\u4eba\u8fdc\uff0c\u633d \u51b0\u6cb3\u6d17\u6da4\u5c18\u4e1d\u4e71\u3002\u5f80\u8005\u5982\u65af\u968f\u901d\u6c34\uff0c\u540e\u6765\u4eba\u5e94\u5f97\u5982\u5fc3\u613f\u3002\u6bb7\u52e4\u795d\uff0c\u5618\u5bd2\u6696\u3002 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u2014\u2014\u8c03\u5bc4\u91d1\u7f15\u8863 \u3000\u3000\u201c\u7ea2\u70db\u672a\u6b8b\u4eba\u5df2\u674f\uff0c\u60c5\u5929\u96be\u8865\u6068\u7ef5\u7ef5\u3002\u201d\u81ea\u4ece\u7ecf\u8fc7\u4e86\u90a3\u4e00\u573a\u60c5\u53d8\u4e4b\u540e\uff0c\u6c5f\u6e56\u4e0a\u5c31\u518d\u4e5f\u6ca1 \u6709\u4eba\u89c1\u8fc7\u91d1\u4e16\u9057\uff0c\u6625\u53bb\u6625\u6765\uff0c\u82b1\u5f00\u82b1\u843d\uff0c\u5230\u5982\u4eca\u5df2\u662f\u6574\u6574\u4e03\u5e74\u4e86\u3002 \u3000\u3000\u4ed6\u4e0e\u5389\u80dc\u7537\u7684\u54c0\u8273\u6545\u4e8b\u4f20\u904d\u4e86\u6b66\u6797\uff0c\u8bc6\u4e0e\u4e0d\u8bc6\uff0c\u90fd\u5728\u4e3a\u4ed6\u53f9\u606f\uff0c\u5f53\u7136\u5404\u4eba\u7684\u611f\u60f3\u6709\u6240\u4e0d \u540c\uff0c\u6709\u7684\u4eba\u4e00\u76f4\u618e\u6068\u5389\u80dc\u7537\uff0c\u8ba4\u4e3a\u662f\u5389\u80dc\u7537\u5bb3\u4e86\u91d1\u4e16\u9057\uff1b\u6709\u7684\u4eba\u5219\u5728\u5979\u6b7b\u540e\u539f\u8c05\u4e86\u5979\uff0c\u751a\u81f3 \u4e3a\u5979\u7684\u75f4\u60c5\u611f\u52a8\uff1b\u4e5f\u6709\u4e9b\u4eba\u662f\u77e5\u9053\u91d1\u4e16\u9057\u4e0e\u8c37\u4e4b\u534e\u66fe\u6709\u8fc7\u4e00\u6bb5\u604b\u60c5\u7684\uff0c\u4ed6\u4eec\u5374\u4e3a\u8c37\u4e4b\u534e\u800c\u611f \u5230\u4e0d\u503c\u3002\u5728\u4ed6\u4eec\u770b\u6765\uff0c\u91d1\u4e16\u9057\u548c\u8c37\u4e4b\u534e\u672c\u6765\u662f\u4e00\u5bf9\u6700\u7406\u60f3\u7684\u6b66\u6797\u4f73\u5076\uff0c\u90fd\u662f\u5389\u80dc\u7537\u7684\u4e0d\u597d\uff0c \u62c6\u6563\u4e86\u8fd9\u5bf9\u7f8e\u6ee1\u7684\u59fb\u7f18\u3002\u4ed6\u4eec\u628a\u5389\u80dc\u7537\u4e4b\u6b7b\u4e5f\u5f53\u4f5c\u662f\u5979\u201c\u5de5\u5b50\u5fc3\u8ba1\u201d\u7684\u8868\u73b0\uff0c\u4ed6\u4eec\u8ba4\u4e3a\uff1a\u5389 \u80dc\u7537\u81ea\u77e5\u5728\u60c5\u573a\u4e0a\u96be\u4e0e\u8c37\u4e4b\u534e\u89d2\u9010\uff0c\u6240\u4ee5\u624d\u7528\u6b7b\u6765\u8d62\u5f97\u5979\u6b7b\u540e\u7684\u7231\u60c5\u3002 \u3000\u3000\u662f\u662f\u975e\u975e\uff0c\u6069\u6069\u6028\u6028\uff0c\u8bae\u8bba\u7eb7\u575b\u3002\u4f46\u6709\u4e00\u70b9\u76f8\u540c\u7684\u662f\uff1a\u6b66\u6797\u4eba\u58eb\u5bf9\u91d1\u4e16\u9057\u7684\u770b\u6cd5\u90fd\u5df2\u53d8 \u4e86\uff0c\u6ca1\u6709\u4eba\u518d\u628a\u4ed6\u5f53\u4f5c\uff1a\u201c\u9b54\u5934\u201d\uff0c\u5927\u5bb6\u90fd\u5728\u6000\u5ff5\u7740\u4ed6\uff0c\u5e0c\u671b\u4ed6\u80fd\u591f\u632f\u4f5c\u8d77\u6765\uff0c\u505a\u51fa\u4e00\u756a\u4e8b \u4e1a\u3002 \u3000\u3000\u5728\u91d1\u4e16\u9057\u7684\u670b\u53cb\u4e4b\u4e2d\uff0c\u9664\u4e86\u8c37\u4e4b\u534e\u4e4b\u5916\uff0c\u60f3\u5ff5\u4ed6\u60f3\u5ff5\u5f97\u6700\u6df1\u7684\u4eba\uff0c\u4e43\u662f\u6c5f\u5357\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u662f\u4e00\u4e2a\u5929\u6717\u6c14\u6e05\u7684\u521d\u79cb\u4f73\u65e5\uff0c\u6c5f\u5357\u4e00\u65e9\u8d77\u6765\uff0c\u7167\u7740\u5f80\u65e5\u7684\u4e60\u60ef\uff0c\u5e26\u4ed6\u7684\u513f\u5b50\u5230\u82b1 \u56ed\u7ec3\u6b66\u3002\u4ed6\u7684\u513f\u5b50\u5c31\u662f\u5728\u91d1\u3001\u5389\u60c5\u52ab\u90a3\u4e00\u5e74\u751f\u7684\uff0c\u5982\u4eca\u4e5f\u5df2\u662f\u4e03\u5c81\u4e86\u3002\u6c5f\u5357\u81ea\u5e7c\u7ed9\u9648\u5929\u5b87\u7684 \u7236\u4eb2\u4e70\u4f5c\u4e66\u7ae5\uff0c\u4ed6\u672c\u6765\u59d3\u4ec0\u4e48\uff0c\u5df2\u4e0d\u77e5\u9053\uff0c\u4e00\u76f4\u88ab\u4eba\u5524\u4f5c\u201c\u6c5f\u5357\u201d\uff0c\u4ed6\u4e5f\u5c31\u4ee5\u201c\u6c5f\u201d\u4e3a\u59d3\uff0c \u7ed9\u4ed6\u513f\u5b50\u8d77\u4e86\u4e2a\u540d\u5b57\uff0c\u53eb\u505a\u6c5f\u6d77\u5929\u3002 \u3000\u3000\u6768\u67f3\u9752\u53ea\u6709\u4e00\u4e2a\u5973\u513f\uff0c\u820d\u4e0d\u5f97\u548c\u5973\u513f\u5206\u5f00\uff0c\u56e0\u6b64\u5c06\u6c5f\u5357\u62db\u8d58\u6765\u5bb6\uff0c\u8fd9\u4e2a\u5bb6\u4e5f\u5c31\u662f\u5979\u7684\u7236 \u4eb2\u2014\u2014\u5f53\u5e74\u540d\u9707\u5317\u4e94\u7701\u7684\u201c\u94c1\u638c\u795e\u5f39\u201d\u6768\u4ef2\u82f1\u7684\u6545\u5c45\u3002\u540e\u82b1\u56ed\u8fd9\u4e2a\u7ec3\u6b66\u573a\u4e5f\u662f\u6768\u4ef2\u82f1\u751f\u524d\u5e03 \u7f6e\u7684\uff0c\u4e00\u4e00\u5e94\u7ec3\u6b66\u5668\u68b0\uff0c\u6837\u6837\u4ff1\u5168\u3002\u5468\u56f4\u82b1\u6811\u56f4\u7ed5\uff0c\u80cc\u5c71\u9762\u6e56\uff0c\u98ce\u666f\u5e7d\u7f8e\u3002 \u3000\u3000\u6c5f\u5357\u770b\u513f\u5b50\u7ec3\u4e86\u4e00\u5957\u7334\u62f3\uff0c\u54a7\u5f00\u4e86\u5634\u4e50\u54c8\u54c8\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8fd9\u5a03\u5a03\u5c45\u7136\u6bd4\u7238\u7238\u8fd8\u806a\u660e\uff0c\u4e0d \u7528\u6211\u6559\u7b2c\u4e8c\u904d\u3002\u201d\u6c5f\u6d77\u5929\u4f53\u51fa\u4e00\u6839\u5c0f\u6307\u5934\uff0c\u5728\u4ed6\u8138\u4e0a\u4e00\u522e\uff0c\u6c5f\u5357\u9053\uff1a\u201c\u5413\uff0c\u4f60\u4e3a\u4ec0\u4e48\u7f9e\u8d77\u4f60 \u7238\u7238\u6765\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5988\u8bf4\u7684\u2026\u2026\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u54e6\uff0c\u6211\u77e5\u9053\uff0c\u4f60\u5988\u8001\u662f\u7231\u53d6\u7b11\u6211\uff0c\u8bf4\u6211\u6b22\u559c\u5439\u725b\u662f\u4e0d\u662f\uff1f\u4e0d\u8fc7\uff0c\u6211\u4eca\u5929\u662f\u5938 \u8d5e\u4f60\uff0c\u7b97\u4e0d\u5f97\u81ea\u5df1\u5439\u725b\u662f\u4e0d\u662f\uff1f\u54c8\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u4f60\u7238\u7238\u662f\u4ece\u6765\u672a\u66fe\u6b63\u5f0f\u6295\u8fc7\u5e08\uff0c\u4e60\u8fc7\u827a \u7684\uff0c\u6211\u7684\u6b66\u529f\u5440\uff0c\u90fd\u662f\u4e00\u70b9\u4e00\u6ef4\u4ece\u522b\u4eba\u90a3\u91cc\u5077\u6765\u7684\uff0c\u60f3\u5f53\u5e74\u4f60\u9648\u5927\u4f2f\u2026\u2026\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211 \u77e5\u9053\u4e86\u3002\u6211\u5df2\u542c\u4f60\u8bf4\u8fc7\u8bb8\u591a\u904d\u4e86\u3002\u5148\u662f\u8ddf\u9648\u5927\u4f2f\u5b66\uff0c\u540e\u6765\u8ddf\u8427\u516c\u516c\u5b66\uff0c\u518d\u540e\u6765\u561b\uff0c\u5c31\u8be5\u8bf4\u5230 \u91d1\u5927\u4fa0\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4e0d\u8bf4\u4e86\uff0c\u4e0d\u8bf4\u4e86\uff0c\u54b1\u4eec\u6b63\u6b63\u7ecf\u7ecf\u7ec3\u529f\u592b\u3002\u5b69\u5b50\u5440\uff0c\u4eca\u5929\u6211\u8981 \u6559\u4f60\u4e00\u6837\u5f88\u96be\u7ec3\u7684\u5de5\u592b---\u7ffb\u7b4b\u6597\uff01\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u7ffb\u7b4b\u6597\uff1f\u201d\u610f\u601d\u4f3c\u4e4e\u662f\u8981\u8bf4\uff1a\u201c\u6211\u5929\u5929 \u90fd\u5728\u7ffb\uff0c\u7528\u5f97\u7740\u7ec3\u5417\uff1f\u201d \u3000\u3000\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u4f60\u522b\u770b\u8f7b\u8fd9\u7ffb\u7b4b\u6597\u7684\u529f\u592b\uff0c\u8fd9\u8ddf\u4f60\u4eec\u5a03\u5a03\u4eec\u4e71\u7ffb\u4e00\u901a\u53ef\u4e0d\u540c\u5440\uff01\u8fd9\u662f\u91d1\u5927 \u4fa0\u6559\u6211\u7684\u5440\uff0c\u54c8\uff0c\u60f3\u5f53\u5e74\u2026\u2026\u201d\u5b69\u5b50\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u6c5f\u5357\u9053\uff1a\u201c\u597d\uff0c\u4e0d\u8bf4\u4e86\uff0c\u4e0d\u8bf4\u4e86\u3002\u5440\uff0c \u4e0d\u884c\uff0c\u4e0d\u884c\uff0c\u8fd9\u8bdd\u6211\u8fd8\u662f\u8981\u8bf4\u3002\u5b69\u5b50\u5440\uff0c\u4f60\u56fa\u7136\u6bd4\u6211\u806a\u660e\uff0c\u4f46\u4f60\u7684\u547d\u4e5f\u5b9e\u5728\u6bd4\u6211\u597d\u5f97\u5927\u591a \u4e86\uff0c\u4f60\u4e00\u751f\u51fa\u6765\u5c31\u6709\u4eba\u6559\uff0c\u5f85\u5230\u4f60\u5b66\u5b8c\u4e86\u4f60\u7238\u7684\u73a9\u827a\uff0c\u6211\u8fd8\u8981\u9001\u4f60\u5230\u91d1\u5927\u4fa0\u90a3\u91cc\u53bb\u5b66\uff01\u201d\u8fd9 \u8bdd\u5927\u7ea6\u662f\u6c5f\u5357\u7b2c\u4e00\u6b21\u5bf9\u513f\u5b50\u8bb2\u7684\uff0c\u5b69\u5b50\u767b\u65f6\u4e50\u5f97\u8e66\u8df3\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u7684\uff1f\u4f60\u53c8\u8bf4\u4e0d\u77e5\u9053\u91d1 \u5927\u4fa0\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u7239\uff0c\u4f60\u4e0d\u662f\u54c4\u6211\u7684\u5427\uff1f\u201d\u6c5f\u5357\u5927\u7b11\u9053\uff1a\u201c\u5230\u5e95\u9017\u5f97\u4f60\u8bf4\u8bdd\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u6c5f\u5357\u505a\u4e86\u7236\u4eb2\u4e4b\u540e\uff0c\u7231\u8bf4\u8bdd\u7684\u813e\u6c14\u4f9d\u7136\u672a\u6539\uff0c\u4ed6\u5929\u5929\u5bf9\u7740\u5b69\u5b50\uff0c\u5b69\u5b50\u53c8\u4e0d\u4f1a\u8ba8\u538c \u4ed6\uff0c\u4f46\u662f\uff0c\u4ed6\u8bf4\u8bdd\u4e00\u591a\uff0c\u5c31\u6ca1\u6709\u5b69\u5b50\u8bf4\u8bdd\u7684\u4efd\u513f\uff0c\u4e45\u800c\u4e45\u4e4b\uff0c\u53cd\u517b\u6210\u4e86\u5b69\u5b50\u6c89\u9ed8\u5be1\u8a00\u7684\u6027 \u683c\uff0c\u6070\u6070\u548c\u4ed6\u7236\u4eb2\u76f8\u53cd\u3002\u4f46\u5b69\u5b50\u7684\u5929\u6027\u6d3b\u6cfc\uff0c\u78b0\u5230\u4e86\u9ad8\u5174\u7684\u4e8b\u60c5\uff0c\u8fd8\u662f\u8981\u4e50\u5f97\u76f4\u56b7\u51fa\u6765\u7684\u3002 \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u7238\u7238\u51e0\u65f6\u54c4\u8fc7\u4f60\u6765\uff0c\u91d1\u5927\u4fa0\u7b54\u5e94\u8fc7\u6536\u4f60\u4e3a\u5f92\u7684\u3002\u4f60\u5728\u8941\u8913\u4e4b\u4e2d\uff0c\u4ed6\u66fe\u7ecf\u6765\u770b \u8fc7\u4f60\uff0c\u6478\u8fc7\u4f60\u7684\u9aa8\u683c\uff0c\u8bf4\u4f60\u662f\u4e00\u5757\u4e0a\u597d\u7684\u7ec3\u6b66\u6750\u6599\u54e9\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u4e2a\u4f60\u4e5f\u8bf4\u8fc7\u4e86\uff0c\u6211 \u8981\u95ee\u7684\u662f\uff0c\u91d1\u5927\u4fa0\uff0c\u4ed6\u2014\u2014\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u8981\u95ee\u7684\u662f\u91d1\u5927\u4fa0\u73b0\u5728\u4f55\u65b9\u662f\u4e0d\u662f\uff1f\u4f60\u4e0d\u8981\u62c5\u5fc3\uff0c\u91d1\u5927\u4fa0\u7684\u8bdd\u50cf\u91d1\u5b50\u4e00 \u822c\uff0c\u8bf4\u8fc7\u4e86\u5c31\u503c\u4ef7\uff0c\u51b3\u4e0d\u6709\u5047\u3002\u7eb5\u7136\u4eec\u627e\u4e0d\u7740\u4ed6\uff0c\u4f60\u957f\u5927\u4e86\u4ed6\u4e5f\u4f1a\u6765\u627e\u4f60\u7684\u3002\u4f60\u8fd9\u4e2a\u5e08\u662f\u62dc \u5b9a\u4e86\u3002\u597d\uff0c\u597d\uff0c\u597d\uff0c\u4e0d\u8bf4\u4e86\uff0c\u4e0d\u8bf4\u4e86\uff0c\u7ec3\u5427\uff0c\u7ec3\u5427\uff01\u6211\u5148\u7ffb\u7ed9\u4f60\u770b\uff01\u201d \u3000\u3000\u6c5f\u5357\u4e00\u4e2a\u7b4b\u6597\u7ffb\u8fc7\u53bb\uff0c\u84e6\u7136\u95f4\u201c\u5440\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u8d77\u6765\uff0c\u5c06\u513f\u5b50\u5413\u4e86\u4e00\u8df3\uff01 \u3000\u3000\u539f\u6765\u6c5f\u5357\u4e00\u4e2a\u7b4b\u6597\u7ffb\u8fc7\u53bb\uff0c\u5ffd\u89c1\u82b1\u6811\u4e1b\u4e2d\uff0c\u4f3c\u6709\u4eba\u5f71\u79fb\u52a8\uff0c\u5b9a\u775b\u770b\u65f6\uff0c\u7adf\u662f\u4e00\u4e2a\u5973\u5b50\u3002 \u3000\u3000\u6c5f\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u90a3\u5c11\u5973\u8138\u4e0a\u8499\u7740\u4e00\u5c42\u8f7b\u7eb1\uff0c\u7f13\u7f13\u7684\u4ece\u82b1\u4e1b\u4e2d\u8d70\u51fa \u6765\uff0c\u6b65\u6cd5\u5341\u5206\u53e4\u602a\uff0c\u8f7b\u76c8\u98d8\u5ffd\uff0c\u7adf\u4f3c\u811a\u4e0d\u6cbe\u5c18\uff0c\u50cf\u4e2a\u5e7d\u7075\u4e00\u822c\u3002 \u3000\u3000\u6c5f\u5357\u8fde\u95ee\u4e24\u58f0\uff0c\u90a3\u5973\u5b50\u90fd\u4e0d\u56de\u7b54\u3002\u6c5f\u6d77\u5929\u53eb\u9053\uff1a\u201c\u7239\uff0c\u8fd9\u8fb9\u4e5f\u6709\u4eba\u3002\u201d\u6c5f\u5357\u671b\u8fc7\u53bb\uff0c\u4e0d \u4f46\u4ed6\u513f\u5b50\u6240\u6307\u7684\u90a3\u4e2a\u65b9\u5411\u6709\u4eba\uff0c\u4e1c\u5357\u897f\u5317\u56db\u4e2a\u65b9\u5411\uff0c\u90fd\u51fa\u73b0\u4e86\u540c\u6837\u670d\u88c5\u7684\u5973\u5b50\u3002 \u3000\u3000\u6c5f\u5357\u4e5f\u662f\u66fe\u7ecf\u8fc7\u8bb8\u591a\u5927\u98ce\u5927\u6d6a\u7684\u4eba\uff0c\u60ca\u5fc3\u52a8\u9b44\u7684\u573a\u9762\u4e5f\u89c1\u8fc7\u4e0d\u5c11\uff0c\u800c\u4e14\u4ed6\u7684\u6b66\u529f\uff0c\u7ecf\u8fc7 \u4e86\u91d1\u4e16\u9057\u7684\u6307\u70b9\uff0c\u4e5f\u65e9\u5df2\u8fdb\u5165\u4e00\u6d41\u4e4b\u5217\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u5e73\u751f\u4e0e\u4eba\u65e0\u4ec7\uff0c\u6015\u5979\u4eec \u4f5c\u751a\uff1f\u201d\u4f46\u8bdd\u867d\u5982\u6b64\uff0c\u8fd9\u56db\u4e2a\u5973\u5b50\u6765\u5f97\u592a\u8fc7\u8be1\u5f02\uff0c\u6c5f\u5357\u5bf9\u7740\u5979\u4eec\uff0c\u7adf\u662f\u4e0d\u81ea\u7981\u7684\u6709\u70b9\u513f\u611f\u5230 \u5bb3\u6015\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u5973\u5b50\u8e0f\u8fdb\u4e86\u7ec3\u6b66\u573a\uff0c\u5404\u81ea\u5728\u4e00\u65b9\u7ad9\u5b9a\uff0c\u4ecd\u7136\u4e00\u58f0\u4e0d\u54cd\u3002\u6c5f\u5357\u9f13\u8d77\u52c7\u6c14\u95ee\u9053\uff1a \u201c\u5582\uff0c\u4f60\u4eec\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u662f\u6765\u627e\u8c01\u7684\uff1f\u6d77\u513f\uff0c\u53eb\u4f60\u5a46\u5a46\u548c\u6bcd\u4eb2\u51fa\u6765\u3002\u201d\u4ed6\u7684\u5cb3\u6bcd\u6768\u67f3\u9752\u662f \u6b66\u6797\u524d\u8f88\uff0c\u4e0e\u5404\u5927\u95e8\u6d3e\uff0c\u5dee\u4e0d\u591a\u90fd\u6709\u70b9\u4ea4\u60c5\uff0c\u8fd9\u56db\u4e2a\u5973\u5b50\u6c5f\u5357\u5168\u4e0d\u8ba4\u8bc6\uff0c\u56e0\u6b64\u60f3\u53eb\u5cb3\u6bcd\u51fa\u6765 \u770b\u770b\u3002 \u3000\u3000\u4e1c\u9996\u90a3\u4e2a\u5973\u5b50\u5ffd\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u662f\u6765\u627e\u4f60\u7684\uff0c\u5e76\u975e\u8981\u89c1\u4f60\u7684\u5cb3\u6bcd\u5927\u4eba\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u6211 \u4e0d\u8ba4\u8bc6\u4f60\u4eec\uff0c\u4f60\u4eec\u627e\u6211\u505a\u4ec0\u4e48\uff1f\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u4f60\u4e0d\u8ba4\u8bc6\u6211\u4eec\uff0c\u6211\u4eec\u5374\u8ba4\u8bc6\u4f60\u3002\u4eca\u5929\u5230\u6765\uff0c \u662f\u7279\u5730\u770b\u4f60\u7ec3\u6b66\u7684\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u591a\u8c22\uff0c\u591a\u8c22\uff0c\u60f3\u4e0d\u5230\u6211\u8fd9\u51e0\u624b\u4e0d\u50cf\u6837\u7684\u4e09\u811a\u732b\u529f\u592b\uff0c\u4e5f\u5c45\u7136\u6709\u4eba\u8d4f\u8bc6\u4e86\u3002\u53ea \u662f\uff0c\u4f60\u4eec\u8fd9\u6837\u6765\u6cd5\uff0c\u5374\u662f\u6709\u70b9\u53e4\u602a\u3002\u4e0d\u8fc7\uff0c\u6211\u7d20\u6765\u597d\u5ba2\uff0c\u4e0d\u7ba1\u8bc6\u4e0e\u4e0d\u8bc6\u90fd\u4e00\u6837\u6b22\u8fce\u3002\u4f46\u662f\u4e3b \u5ba2\u4e4b\u95f4\uff0c\u603b\u5f97\u901a\u4e2a\u540d\u59d3\u5440\u3002\u4f60\u4eec\u5148\u8fdb\u53bb\u559d\u4e00\u53e3\u8336\uff0c\u6b47\u4e00\u6b47\uff0c\u8c08\u4e00\u8c08\uff0c\u7136\u540e\u54b1\u4eec\u518d\u5230\u8fd9\u4e2a\u7ec3\u6b66 \u573a\u5b50\u5982\u4f55\uff1f\u201d \u3000\u3000\u897f\u9996\u90a3\u4e2a\u5973\u5b50\u7b11\u9053\uff1a\u201c\u4eba\u4eba\u90fd\u53eb\u4f60\u591a\u5634\u7684\u6c5f\u5357\uff0c\u679c\u7136\u4e0d\u9519\u3002\u54ea\u6765\u7684\u8fd9\u4e48\u4e9b\u5e9f\u8bdd\uff1f\u201d\u6c5f\u5357 \u9053\uff1a\u201c\u54ce\u5440\u5440\uff0c\u4fd7\u8bed\u9053\uff1a\u793c\u591a\u4eba\u4e0d\u602a\uff0c\u6211\u9756\u4f60\u4eec\u559d\u8336\uff0c\u53c8\u4e0d\u662f\u5f97\u7f6a\u4f60\u4eec\uff0c\u600e\u7684\u53cd\u60f9\u4f60\u4eec\u8ba8\u538c \u4e86\uff1f\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u662f\u8ba8\u538c\u4f60\uff0c\u53ea\u662f\u60f3\u5feb\u70b9\u770b\u4f60\u7ec3\u6b66\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u90a3\u4e5f\u5f97\u6211\u5fc3\u7518\u60c5 \u613f\u5440\u3002\u4e0e\u5973\u4eba\u6253\u4ea4\u9053\u662f\u6709\u70b9\u6709\u7406\u8bf4\u4e0d\u6e05\uff0c\u5440\uff0c\u6211\u8fd8\u662f\u53eb\u7edb\u971e\u6765\u966a\u4f60\u4eec\u804a\u4e00\u804a\u5427\u3002\u201d \u3000\u3000\u4e1c\u9996\u90a3\u4e2a\u5973\u5b50\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u59bb\u5b50\u548c\u5cb3\u6bcd\u4e48\uff0c\u6211\u4eec\u65e9\u5df2\u6709\u4eba\u8fdb\u53bb\u62dc\u89c1\u4e86\u3002\u4e0d\u7528\u4f60\u8bf7\u5979 \u4eec\u51fa\u6765\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u6768\u67f3\u9752\u7684\u58f0\u97f3\u5728\u91cc\u9762\u5927\u53eb\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff01\u4f60\u4eec\u662f\u4e9b\u4ec0\u4e48\u4eba\uff0c \u4e3a\u4ec0\u4e48\u4e71\u95ef\u8fdb\u6211\u7684\u5bb6\u6765\uff1f\u4f60\u4eec\u5f53\u6768\u5bb6\u662f\u597d\u6b3a\u8d1f\u7684\u4e48\uff1f\u201d \u3000\u3000\u4e1c\u9996\u90a3\u4e2a\u5973\u5b50\u7b11\u9053\uff0c\u201c\u4f60\u7684\u5cb3\u6bcd\u600e\u7684\u8fd9\u4e48\u51f6\u5440\uff0c\u6bd4\u4f60\u66f4\u96be\u8bf4\u8bdd\u3002\u201d\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u5a18\uff0c\u4f60 \u4eec\u5148\u522b\u6253\u67b6\uff0c\u95ee\u660e\u767d\u4e86\u518d\u8bf4\u5427\uff01\u201d \u3000\u3000\u53ea\u89c1\u6768\u67f3\u9752\u62ab\u5934\u6563\u53d1\uff0c\u6267\u7740\u5f39\u5f13\uff0c\u5df2\u662f\u8ffd\u4e86\u51fa\u6765\u3002\u90b9\u7edb\u971e\u4e5f\u4ed7\u5251\u76f8\u968f\u3002\u6768\u67f3\u9752\u51fa\u6765\u4e00 \u770b\uff0c\u89c1\u573a\u4e2d\u8fd8\u6709\u56db\u4e2a\u4e00\u5f0f\u6253\u626e\u7684\u5973\u5b50\u56f4\u7740\u5979\u7684\u5973\u5a7f\uff0c\u4e0d\u89c9\u4e00\u6014\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff0c\u8fd9\u4e9b\u4eba\u662f\u4ec0 \u4e48\u4eba\uff1f\u4f60\u8ba4\u8bc6\u5979\u4eec\u7684\u5417\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u5c31\u662f\u56e0\u4e3a\u6211\u4e0d\u8ba4\u8bc6\uff0c\u6240\u4ee5\u624d\u8981\u95ee\u5440\u3002\u201d\u6768\u67f3\u9752\u9053\uff1a \u201c\u771f\u662f\u7cca\u6d82\uff0c\u4f60\u4e0d\u8ba4\u8bc6\uff0c\u4e3a\u4ec0\u4e48\u653e\u5979\u4eec\u8fdb\u6765\uff1f\u201d \u3000\u3000\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u4e0d\u662f\u6211\u653e\u7684\u5440\uff0c\u5979\u4eec\u8bf4\u3001\u8bf4\u2026\u2026\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u6768\u67f3\u9752\u5df2\u62c9\u52a8\u5f13\u5f26\uff0c\u50fb\u50fb\u556a \u556a\uff0c\u4e00\u987f\u5f39\u5b50\u5411\u8fd9\u7fa4\u5973\u5b50\u6253\u53bb\u3002\u9a82\u9053\uff1a\u201c\u7cca\u6d82\uff0c\u7cca\u6d82\uff0c\u4f60\u53ef\u77e5\u9053\u5979\u4eec\u5728\u91cc\u9762\u5e72\u4e9b\u4ec0\u4e48\uff1f\u7b80\u76f4 \u5c31\u662f\u4e00\u7fa4\u5f3a\u76d7\uff01\u201d\u539f\u6765\u90a3\u56db\u4e2a\u5973\u5b50\u662f\u5728\u90b9\u7edb\u971e\u623f\u5185\u7ffb\u7bb1\u5012\u7b50\uff0c\u88ab\u6768\u67f3\u9752\u6bcd\u5973\u53d1\u73b0\uff0c\u8d76\u51fa\u6765\u7684\u3002 \u3000\u3000\u6768\u5bb6\u7684\u795e\u5f39\u7edd\u6280\u975e\u540c\u5c0f\u53ef\uff0c\u8fde\u73e0\u53d1\u51fa\uff0c\u6709\u5982\u51b0\u96f9\u4e71\u843d\uff0c\u6709\u4e2a\u5973\u5b50\u95ea\u907f\u7a0d\u6162\uff0c\u88ab\u5f39\u5b50\u64e6\u4f24 \u4e86\u989d\u89d2\uff0c\u8fd9\u5973\u5b50\u6012\u9053\uff1a\u201c\u8001\u8654\u5a46\uff0c\u4f60\u5f53\u6211\u4eec\u662f\u6015\u4f60\u4e48\uff1f\u201d\u8eab\u5f62\u4e00\u6643\uff0c\u4e00\u6e9c\u9ed1\u70df\u4f3c\u7684\u5ffd\u5730\u5411\u6768 \u67f3\u9752\u51b2\u53bb\u3002\u6768\u67f3\u9752\u7684\u7b2c\u4e00\u6279\u5f39\u5b50\u5df2\u7ecf\u53d1\u5c3d\uff0c\u6765\u4e0d\u53ca\u6362\uff0c\u5c55\u5f00\u5bb6\u4f20\u7684\u201c\u5168\u5f13\u5341\u516b\u6253\u201d\u6b66\u827a\uff0c\u5219 \u7684\u4e00\u58f0\uff0c\u5f13\u5f26\u4fbf\u5411\u90a3\u5973\u5b50\u7684\u624b\u8155\u62c9\u4e0b\uff0c\u8fd9\u4e00\u4e0b\u82e5\u7ed9\u62c9\u5b9e\uff0c\u90a3\u5973\u5b50\u7684\u8155\u8109\u4fbf\u8981\u7ed9\u5979\u5272\u65ad\uff0c\u6210\u4e3a \u6b8b\u5e9f\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u5973\u5b50\u7684\u8eab\u6cd5\u7adf\u662f\u5341\u5206\u602a\u5f02\uff0c\u4e00\u98d8\u4e00\u95ea\uff0c\u7adf\u7136\u76f4\u6b3a\u8ff8\u6768\u67f3\u9752\u7684\u6000\u4e2d\uff0c\u62e2\u6307\u4e00\u62c2\uff0c\u53ea \u542c\u5f97\u6768\u67f3\u9752\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u90a3\u628a\u94c1\u80ce\u5f13\u8fd8\u5728\u4f5c\u7740\u4e0b\u5288\u4e4b\u52bf\uff0c\u8eab\u8eaf\u5374\u4f3c\u4e00\u5ea7\u77f3\u50cf\u4e00\u822c\uff0c\u52a8\u4e5f\u4e0d \u4f1a\u52a8\u4e86\u3002\u5c31\u5728\u8fd9\u540c\u4e00\u7684\u65f6\u95f4\uff0c\u90b9\u7edb\u971e\u4e5f\u5df2\u7ed9\u53e6\u4e00\u4e2a\u5973\u5b50\u7528\u70b9\u7a74\u6cd5\u5236\u4f0f\u3002 \u3000\u3000\u6c5f\u5357\u7684\u6b66\u529f\u867d\u7136\u65e9\u5df2\u5230\u4e86\u7b2c\u4e00\u6d41\u5883\u754c\uff0c\u4f46\u4ed6\u5fc3\u6027\u548c\u5e73\uff0c\u672c\u6765\u5c31\u4e0d\u60f3\u4e0e\u8fd9\u73ed\u5973\u5b50\u52a8\u624b\u3002\u6b64 \u523b\u4ed6\u5f85\u8981\u52a8\u624b\uff0c\u4f46\u662f\u5cb3\u6bcd\u548c\u59bb\u5b50\u5df1\u7136\u843d\u5728\u654c\u4eba\u624b\u4e2d\uff0c\u4ed6\u6295\u9f20\u5fcc\u5668\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u65b9\u5bf8\u5927\u4e71\uff0c\u4e0d \u77e5\u5982\u4f55\u662f\u597d\u3002 \u3000\u3000\u4ed6\u7684\u513f\u5b50\u5374\u4e0d\u77e5\u4ec0\u4e48\u987e\u5fcc\uff0c\u5927\u53eb\u5927\u56b7\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u6b3a\u4fae\u6211\u5a18\uff01\u201d\u5411\u4ed6\u6bcd\u4eb2\u5954\u53bb\u3002\u6c5f \u5357\u6b63\u5728\u53eb\u9053\uff1a\u201c\u6d77\u513f\u56de\u6765\uff01\u5f85\u7239\u7239\u548c\u5979\u4eec\u8bf4\u3002\u201d\u4ed6\u7684\u513f\u5b50\u4e5f\u5df2\u7ed9\u53e6\u4e00\u4e2a\u5973\u5b50\u64d2\u7740\uff0c\u90a3\u5973\u5b50\u8f7b \u8f7b\u629a\u4ed6\u7684\u5934\u53d1\u7b11\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u6211\u4eec\u5e76\u65e0\u6076\u610f\uff0c\u4f60\u5a18\u597d\u597d\u7684\u6ca1\u6709\u635f\u4e86\u534a\u6839\u6beb\u6bdb\uff0c\u4f60\u653e\u5fc3\u3002\u6211 \u7ed9\u4f60\u7cd6\u5403\u3002\u201d\u6c5f\u6d77\u5927\u626d\u8f6c\u4e86\u8138\uff0c\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u5403\u4f60\u7684\u7cd6\uff0c\u4f60\u653e\u6211\u7684\u6bcd\u4eb2\u548c\u5a46\u5a46\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u65e2\u7136\u5e76\u65e0\u6076\u610f\uff0c\u4e3a\u4f55\u4e0d\u80af\u89e3\u5f00\u5979\u4eec\u7684\u7a74\u9053\uff1f\u201d\u4e1c\u9996\u90a3\u5973\u5b50\u9053\uff1a\u201c\u4f60 \u53c8\u4e0d\u662f\u4e0d\u77e5\u9053\u4f60\u5cb3\u6bcd\u7684\u813e\u6c14\uff0c\u89e3\u5f00\u5979\u7684\u7a74\u9053\uff0c\u54b1\u4eec\u8fd8\u5f97\u5b89\u9759\u5417\uff1f\u6211\u4eec\u7684\u70b9\u7a74\u6cd5\u5bf9\u5979\u5e76\u65e0\u4f24 \u5bb3\uff0c\u4f60\u4e0d\u7528\u4e3a\u5979\u62c5\u5fc3\u3002\u4f60\u5c06\u4f60\u7684\u770b\u5bb6\u672c\u9886\u597d\u597d\u7684\u7ec3\u4e00\u7ec3\u7ed9\u54b1\u4eec\u77a7\u5427\uff0c\u7ec3\u5f97\u597d\uff0c\u6211\u5c31\u653e\u5979\u3002\u201d \u3000\u3000\u6c5f\u5357\u867d\u662f\u5fc3\u6027\u548c\u5e73\uff0c\u5374\u4e5f\u4e0d\u7518\u4e3a\u4eba\u6240\u8fb1\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u6837\u8feb\u6211\u7ec3\u6b66\u7ed9\u4f60\u4eec\u77a7\uff0c\u8fd9\u4e0d\u662f \u5b58\u5fc3\u5c06\u6211\u5f53\u4f5c\u7334\u513f\u620f\u800d\u5417\uff1f\u201d\u5f53\u4e0b\u8e0c\u8e87\u83ab\u51b3\uff0c\u7ad9\u5728\u573a\u5fc3\uff0c\u795e\u60c5\u751a\u662f\u5c34\u5c2c\u3002 \u3000\u3000\u897f\u9996\u90a3\u9ed1\u8863\u5973\u5b50\u4f3c\u662f\u77e5\u9053\u4ed6\u7684\u5fc3\u610f\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4e00\u4e2a\u4eba\u4e0d\u597d\u610f\u601d\u7ec3\u4e48\uff1f\u597d\uff0c\u6211 \u966a\u4f60\u7ec3\uff0c\u7ed9\u4f60\u5582\u62db\u3002\u201d \u3000\u3000\u201c\u5582\u62db\u201d\u662f\u6b66\u6797\u672f\u8bed\uff0c\u5e7f\u4e49\u6765\u8bf4\uff0c\u662f\u6307\u540c\u4e00\u5bb6\u7684\u62db\u6570\u4e92\u76f8\u5207\u78cb\u7422\u78e8\uff1b\u72ed\u4e49\u6765\u8bf4\uff0c\u6839\u672c\u5c31 \u662f\u6307\u5e08\u5f92\u6216\u540c\u95e8\u5144\u5f1f\u7684\u7ec3\u4e60\u3002\u6c5f\u5357\u542c\u4e86\uff0c\u4e0b\u89c9\u53c8\u662f\u4e00\u6115\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u4e14\u770b\u4f60\u600e\u6837\u7ed9\u6211\u5582\u62db\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u5ff5\u672a\u5df2\uff0c\u90a3\u5973\u5b50\u4e00\u675f\u8170\u5e26\uff0c\u5ffd\u5730\u4e00\u4e2a\u7b4b\u6597\u5012\u7ffb\u8fc7\u6765\u3002\u867d\u8bf4\u4f1a\u6b66\u529f\u7684\u5973\u5b50\u6bd4\u8f83\u8c6a\u653e\uff0c \u4f46\u603b\u6709\u4e00\u4efd\u5c11\u5973\u7684\u77dc\u6301\uff0c\u6240\u4ee5\u201c\u6eda\u5730\u5802\u201d\u201c\u71d5\u9752\u5341\u516b\u7ffb\u201d\u4e4b\u7c7b\u7684\u529f\u592b\uff0c\u53ea\u6709\u7537\u4eba\u624d\u6562\u4f7f\u7528\uff0c \u4ee5\u5973\u5b50\u800c\u5927\u7ffb\u7b4b\u6597\uff0c\u4f11\u8bf4\u6c5f\u5357\u4ece\u672a\u89c1\u8fc7\uff0c\u8fde\u542c\u4e5f\u672a\u66fe\u542c\u8fc7\uff01 \u3000\u3000\u5c24\u5176\u5947\u602a\u7684\u662f\uff0c\u8fd9\u5973\u5b50\u5012\u7ffb\u7b4b\u6597\u7684\u8eab\u6cd5\uff0c\u7adf\u4e0e\u91d1\u4e16\u9057\u6388\u4e0e\u6c5f\u5357\u7684\u5927\u540c\u5c0f\u5f02\u3001\u5979\u7ffb\u7b4b\u6597\u7684 \u59ff\u52bf\u6bd4\u6c5f\u5357\u8fd8\u8981\u597d\u770b\uff0c\u5728\u534a\u7a7a\u4e2d\u63a5\u8fde\u4e24\u4e2a\u8f6c\u8eab\uff0c\u500f\u5730\u5c31\u7ffb\u5230\u4e86\u6c5f\u5357\u7684\u9762\u524d\uff0c\u800c\u4e14\u8fde\u88d9\u5b50\u4e5f\u672a \u98d8\u8d77\u3002 \u3000\u3000\u6c5f\u5357\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u53eb\u8d77\u6765\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e5f\u4f1a\u8fd9\u6837\u7ffb\u7b4b\u6597\uff0c\u5582\uff0c\u5582\uff0c\u662f\u8c01\u6559\u7ed9\u4f60\u7684\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u559d\u9053\uff1a\u201c\u63a5\u62db\u3002\u201d\u6839\u672c\u5c31\u4e0d\u7b54\u590d\u4ed6\u7684\u95ee\u8bdd\uff0c\u4e00\u4e2a\u7b4b\u6597\u7ffb\u5230\u4ed6\u7684\u9762\u524d\uff0c\u7acb\u5373\u53cc\u624b\u9f50 \u5f20\uff0c\u5341\u6307\u5982\u94a9\uff0c\u500f\u5730\u5411\u4ed6\u6293\u4e0b\u3002 \u3000\u3000\u6c5f\u5357\u5927\u4e3a\u60ca\u9a87\uff0c\u8fd9\u4e00\u6293\u6b63\u662f\u4e54\u5317\u6e9f\u6b66\u529f\u79d8\u590d\u4e2d\u201c\u9634\u9633\u6293\u201d\u7684\u529f\u592b\uff0c\u91d1\u4e16\u9057\u524d\u51e0\u5e74\u5230\u8fc7\u6c5f \u5357\u5bb6\u4e2d\u4e00\u6b21\uff0c\u66fe\u5c06\u79d8\u7c4d\u4e0a\u7684\u529f\u592b\uff0c\u62e3\u5bb9\u6613\u5b66\u7684\u6559\u8fc7\u4ed6\u5341\u591a\u5957\uff0c\u8fd9\u9634\u9633\u6293\u7684\u529f\u592b\u4e5f\u662f\u5176\u4e2d\u4e4b\u4e00\u3002 \u3000\u3000\u9ed1\u8863\u5973\u5b50\u8fd9\u4e00\u6293\u52b2\u9053\u5341\u8db3\uff0c\u53cc\u638c\u53d1\u51fa\u4e24\u80a1\u521a\u67d4\u4e0d\u540c\u7684\u529b\u9053\uff0c\u4e00\u51fa\u624b\u4fbf\u628a\u6c5f\u5357\u7684\u8eab\u5f62\u7b3c\u7f69 \u5728\u5341\u6307\u4e4b\u4e0b\uff0c\u82e5\u662f\u7ed9\u5979\u6293\u5b9e\uff0c\u4fbf\u6709\u6027\u547d\u4e4b\u5371\uff01\u6c5f\u5357\u60ca\u7591\u4e0d\u5b9a\uff0c\u4f46\u8fd9\u65f6\u5374\u5df2\u65e0\u6687\u591a\u95f4\uff0c\u6025\u5fd9\u4f7f \u51fa\u91d1\u4e16\u9057\u6559\u7ed9\u4ed6\u7684\u7834\u89e3\u4e4b\u6cd5\uff0c\u5de6\u624b\u4e94\u6307\u4e5f\u5411\u90a3\u5973\u5b50\u6293\u53bb\uff0c\u53f3\u624b\u5374\u4ece\u65f6\u5e95\u7a7f\u51fa\u6765\uff0c\u7fd8\u8d77\u4e2d\u6307\uff0c \u5f39\u90a3\u5973\u5b50\u7684\u66f2\u6c60\u7a74\u53f0 \u3000\u3000\u90a3\u5973\u5b50\u559d\u9053\uff1a\u201c\u597d\uff01\u201d\u8eab\u5f62\u4e00\u98d8\u4e00\u95ea\uff0c\u8e0f\u7684\u662f\u201c\u5929\u7f57\u6b65\u6cd5\u201d\u8fd9\u79cd\u6b65\u6cd5\u6c5f\u5357\u8fd8\u672a\u7ec3\u5f97\u5230 \u5bb6\uff0c\u4e00\u6293\u6293\u7a7a\uff0c\u90a3\u5973\u5b50\u5df2\u7ed5\u5230\u4ed6\u7684\u80cc\u540e\uff0c\u4f7f\u51fa\u201c\u5370\u638c\u201d\u7684\u529f\u592b\uff0c\u6309\u5230\u6c5f\u5357\u7684\u80cc\u5fc3\u3002 \u3000\u3000\u6c5f\u5357\u53cd\u624b\u4e00\u638c\uff0c\u5c06\u90a3\u5973\u5b50\u9707\u5f00\uff0c\u4ed6\u65e0\u610f\u4f24\u5bb3\u90a3\u4e2a\u5973\u5b50\uff0c\u53ea\u7528\u4e86\u4e94\u6210\u5185\u529b\uff0c\u53ef\u662f\u90a3\u5973\u5b50\u7684 \u62db\u6570\u5374\u6781\u4e3a\u72e0\u8fa3\uff0c\u4e00\u62db\u7d27\u4f3c\u4e00\u62db\uff0c\u6c5f\u5357\u88ab\u5979\u7f20\u5f97\u5fc3\u4e2d\u70e6\u607c\uff0c\u6697\u8fd0\u62a4\u4f53\u795e\u529f\uff0c\u6545\u610f\u5356\u4e2a\u7834\u7efd\uff0c \u90a3\u5973\u5b50\u4e00\u638c\u51fb\u4e2d\u4ed6\u7684\u80cc\u5fc3\uff0c\u767b\u65f6\u88ab\u4ed6\u53cd\u5f39\u51fa\u53bb\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u91cd\u91cd\u5730\u8dcc\u4e86\u4e00\u8de4\u3002 \u3000\u3000\u5357\u9762\u90a3\u4e2a\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e5f\u6765\u7ed9\u4f60\u5582\u62db\uff01\u201d\u6c5f\u5357\u5598\u606f\u672a\u5b9a\uff0c\u90a3\u5973\u5b50\u5df2\u7ecf\u6765\u5230\uff0c\u8863 \u8882\u98d8\u98d8\uff0c\u957f\u8896\u4e00\u62c2\uff0c\u7528\u7684\u7adf\u7136\u4e5f\u662f\u79d8\u7c4d\u4e2d\u7684\u94c1\u8896\u529f\u592b\u3002\u6c5f\u5357\u8bc6\u5f97\u5389\u5bb3\uff0c\u8fde\u5fd9\u4e00\u4e2a\u7b4b\u6597\u5012\u7ffb\u5f00 \u53bb\uff0c\u907f\u4e86\u5979\u8fd9\u4e00\u62c2\u3002 \u3000\u3000\u90a3\u5973\u5b50\u5982\u5f71\u968f\u5f62\uff0c\u8ddf\u8e2a\u8ffd\u5230\uff0c\u6c5f\u5357\u5728\u5730\u4e0a\u4e00\u4e2a\u76d8\u65cb\uff0c\u90a3\u5973\u5b50\u4e09\u62c2\u4e0d\u4e2d\uff0c\u6c5f\u5357\u66ae\u7136\u8dc3\u8d77\uff0c \u547c\u7684\u4e00\u58f0\uff0c\u4ece\u5979\u5934\u9876\u63a0\u8fc7\uff0c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u5582\uff0c\u4e14\u6162\uff0c\u4e14\u6162\uff0c\u4f60\u4eec\u7684\u529f\u592b\u7a76\u7adf\u662f\u8c01\u6559\u7684\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u4f60\u7ba1\u6211\u662f\u8c01\u6559\u7684\uff1f\u201d\u6c5f\u5357\u8eab\u5f62\u6b63\u8981\u843d\u5730\uff0c\u5979\u53cc\u638c\u4e00\u5708\uff0c\u53c8\u5df2\u662f\u4e00\u62db\u201c\u6491\u693d \u624b\u201d\u653b\u4e86\u4e0a\u6765\uff0c\u6c5f\u5357\u5fc3\u4e2d\u6709\u6c14\uff0c\u8fd9\u62db\u201c\u6491\u693d\u624b\u201d\u662f\u4ed6\u66fe\u7ecf\u5b66\u8fc7\u7684\uff0c\u5f53\u4e0b\u4e5f\u628a\u53cc\u638c\u4e00\u5708\uff0c\u5c06\u90a3 \u5973\u5b50\u7684\u53cc\u638c\u5f53\u4e2d\u5206\u5f00\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u7684\u529f\u592b\u662f\u5426\u91d1\u5927\u4fa0\u6559\u7684\uff0c\u82e5\u7136\u54b1\u4eec\u662f\u540c\u51fa\u4e00\u6e90\uff0c\u8fd8\u6bd4\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u53cc\u7709\u4e00\u7ad6\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u91d1\u5927\u4fa0\uff1f\u5728\u6211\u4eec\u7684\u773c\u4e2d\uff0c\u4ed6\u53ea\u662f\u4e2a\u5bb3\u4eba\u7684\u9b54\u5934\uff01\u201d\u5929\u4e0b \u6ca1\u6709\u5f92\u5f1f\u9a82\u5e08\u7236\u7684\u9053\u7406\uff0c\u5979\u8fd9\u4e48\u4e00\u9a82\uff0c\u5f53\u7136\u8868\u660e\u4e86\u5979\u4eec\u7684\u6b66\u529f\u5e76\u975e\u91d1\u4e16\u9057\u6240\u6388\u7684\u4e86\u3002 \u3000\u3000\u8fd9\u51e0\u5e74\u6765\uff0c\u6b66\u6797\u4e2d\u6b63\u6d3e\u4eba\u58eb\u90fd\u5df2\u628a\u91d1\u4e16\u9057\u5f53\u4f5c\u4e49\u4fa0\u540c\u9053\uff0c\u65e0\u4eba\u518d\u8bf4\u4ed6\u662f\u9b54\u5934\u3002\u5374\u4e0d\u6599\u8fd9 \u4e2a\u5973\u5b50\u4f9d\u7136\u8fd9\u6837\u9a82\u4ed6\uff0c\u6c5f\u5357\u4e00\u542c\uff0c\u6012\u706b\u4e0a\u51b2\uff0c\u559d\u9053\uff1a\u201c\u4f60\u80e1\u8bf4\uff0c\u4e0d\u770b\u4f60\u662f\u4e2a\u5973\u5b50\uff0c\u6211\u5c31\u6253\u4f60 \u8033\u5149\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u6211\u504f\u8981\u9a82\uff0c\u770b\u4f60\u5982\u4f55\uff1f\u4f60\u8fd9\u6837\u62a4\u4ed6\uff0c\u53ea\u6709\u81ea\u5df1\u5403\u4e8f\u3002\u201d\u8ffd\u4e0a\u524d\u6765\uff0c\u5411 \u6c5f\u5357\u7740\u7740\u62a2\u653b\uff0c\u62c6\u5230\u4e8c\u5341\u6765\u62db\uff0c\u6c5f\u5357\u6697\u8fd0\u5c0f\u5927\u661f\u638c\u529b\uff0c\u7c98\u7740\u4e86\u5979\u7684\u53cc\u638c\uff0c\u559d\u58f0\uff1a\u201c\u53bb\u5427\uff01\u201d \u638c\u529b\u4e00\u5410\uff0c\u767b\u65f6\u628a\u5979\u9707\u51fa\u4e09\u4e08\u5f00\u5916\u3002\u6c5f\u5357\u5230\u5e95\u662f\u5fc3\u5730\u5584\u826f\uff0c\u867d\u7136\u6c14\u607c\u5979\u8fb1\u9a82\u91d1\u4e16\u9057\uff0c\u5374\u4ecd\u7136 \u624b\u4e0b\u7559\u60c5\uff0c\u53ea\u662f\u4ee4\u5979\u53d7\u70b9\u75bc\u75db\uff0c\u8dcc\u4e86\u4e2a\u56db\u811a\u671d\u5929\u3002 \u3000\u3000\u7b2c\u4e09\u4e2a\u5973\u5b50\u8dc3\u8fdb\u573a\u4e2d\uff0c\u5979\u5728\u5175\u5668\u67b6\u4e0a\u53d6\u4e0b\u4e86\u4e24\u67c4\u957f\u5251\uff0c\u5c06\u4e00\u67c4\u629b\u7ed9\u6c5f\u5357\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6765 \u9886\u6559\u4f60\u7684\u5251\u6cd5\u3002\u201d\u4e0d\u5f85\u6c5f\u5357\u7b54\u8bdd\uff0c\u957f\u5251\u4e00\u6643\uff0c\u4fbf\u5373\u8fdb\u62db\u3002 \u3000\u3000\u6c5f\u5357\u7684\u5251\u6cd5\u5374\u4e0d\u662f\u91d1\u4e16\u9057\u6559\u7684\uff0c\u4ed6\u5b66\u8fc7\u7684\u6709\u8427\u9752\u5cf0\u6240\u6559\u7684\u9752\u57ce\u5251\u6cd5\uff0c\u6709\u9648\u5927\u5b57\u6240\u6559\u7684\u51b0 \u5ddd\u5251\u6cd5\uff0c\u4e0d\u8fc7\uff0c\u90fd\u672a\u5b66\u5168\uff0c\u4f46\u4ed6\u5f97\u91d1\u4e16\u9057\u6307\u70b9\uff0c\u5df2\u9886\u4f1a\u4e86\u4e0a\u4e58\u5251\u6cd5\u7684\u7cbe\u4e49\uff0c\u5c06\u8fd9\u4e9b\u96f6\u96f6\u788e\u788e \u7684\u5251\u62db\u8d2f\u4e32\u8d77\u6765\uff0c\u522b\u51fa\u5fc3\u88c1\uff0c\u5374\u4e5f\u5c45\u7136\u6210\u4e86\u4e00\u5bb6\u5251\u6cd5\u3002 \u3000\u3000\u90a3\u5973\u5b50\u7684\u5251\u6cd5\u751a\u4e3a\u5947\u8be1\uff0c\u53ef\u662f\u4e5f\u4f3c\u4e4e\u672a\u66fe\u5b66\u5168\uff0c\u62c6\u5230\u4e86\u4e09\u5341\u62db\u5de6\u53f3\uff0c\u88ab\u6c5f\u5357\u7528\u4e86\u4e00\u4e2a\u8bf1 \u7740\uff0c\u4e00\u5251\u524a\u65ad\u4e86\u5979\u7684\u8863\u8896\uff0c\u90a3\u5973\u5b50\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u4fbf\u5373\u9000\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u5251\u6cd5\u4e0d\u5fc5\u518d\u8bd5\u4ed6\u4e86\u3002 \u59d0\u59d0\uff0c\u4f60\u51fa\u53bb\u8f83\u91cf\u4ed6\u7684\u70b9\u7a74\u529f\u592b\u3002\u201d\u7b2c\u56db\u4e2a\u5973\u5b50\u5e94\u58f0\u800c\u51fa\uff0c\u4e00\u51fa\u624b\u4fbf\u662f\u4e94\u6307\u8fde\u5f39\uff0c\u5f39\u6307\u4e4b \u95f4\uff0c\u904d\u88ad\u6c5f\u5357\u7684\u5341\u4e09\u5904\u5927\u7a74\u3002 \u3000\u3000\u5728\u5f53\u4eca\u7684\u70b9\u7a74\u540d\u5bb6\u4e4b\u4e2d\uff0c\u672c\u9886\u6700\u9ad8\u7684\u4e5f\u53ea\u80fd\u5728\u4e00\u62db\u4e4b\u5185\u8fde\u70b9\u5bf9\u65b9\u4e03\u5904\u7a74\u9053\uff0c\u53ea\u6709\u4e54\u5317\u6e9f \u7684\u6b66\u529f\u79d8\u590d\u624d\u6709\u4e00\u62db\u8fde\u70b9\u5341\u4e09\u5927\u7a74\u7684\u4e0d\u4f20\u4e4b\u79d8\u3002\u8fd9\u5973\u5b50\u82e5\u662f\u5728\u4ec0\u4e48\u6b66\u6797\u5927\u4f1a\u4e4b\u4e2d\uff0c\u663e\u9732\u8fd9\u624b \u529f\u592b\uff0c\u5f53\u80fd\u9707\u4e16\u9a87\u4fd7\uff0c\u53ef\u662f\u7528\u6765\u5bf9\u4ed8\u6c5f\u5357\uff0c\u90a3\u5374\u662f\u7b49\u5b50\u5728\u5b54\u5b50\u8d2b\u524d\u5356\u6587\u7ae0\uff0c\u5728\u9c81\u73ed\u95e8\u524d\u5f04\u5927 \u65a7\u4e86\u3002 \u3000\u3000\u6c5f\u5357\u4ece\u91d1\u4e16\u9057\u90a3\u513f\u5b66\u4f1a\u4e86\u5341\u591a\u79cd\u529f\u592b\uff0c\u5176\u4ed6\u7684\u4e5f\u8fd8\u7f62\u4e86\uff0c\u8fd9\u70b9\u7a74\u529f\u592b\u4ed6\u5df2\u662f\u5c3d\u5f97\u4e86\u91d1\u4e16 \u9057\u7684\u771f\u4f20\uff0c\u91d1\u4e16\u9057\u4e0d\u4f46\u5c06\u79d8\u7c4d\u4e0a\u7684\u70b9\u7a74\u6cd5\u6559\u4e86\u4ed6\uff0c\u800c\u4e14\u8fd8\u6559\u4e86\u4ed6\u6bd2\u9f99\u5c0a\u8005\u7684\u72ec\u95e8\u70b9\u7a74\u624b\u6cd5\u3002 \u9664\u6b64\u4e4b\u5916\uff0c\u6c5f\u5357\u53c8\u66fe\u4ece\u9ec4\u77f3\u9053\u4eba\u5b66\u8fc7\u98a0\u5012\u7a74\u9053\u7684\u529f\u592b\uff0c\u5bf9\u70b9\u7a74\u4e0e\u9632\u5fa1\u70b9\u7a74\u7684\u8fd0\u7528\uff0c\u9664\u4e86\u91d1\u4e16 \u9057\u4e4b\u5916\uff0c\u53ef\u4ee5\u8bf4\u4ed6\u5df1\u662f\u6b66\u6797\u7684\u7b2c\u4e00\u4eba\u3002 \u3000\u3000\u6c5f\u5357\u6709\u610f\u5c06\u5979\u6349\u5f04\uff0c\u80a9\u5934\u4e00\u7f29\uff0c\u8ba9\u90a3\u5973\u5b50\u7684\u6307\u5c16\u70b9\u4e2d\u4ed6\u814b\u7a9d\u7684\u201c\u72c2\u7b11\u7a74\u201d\uff0c\u6c5f\u5357\u4e00\u4e2a\u7b4b \u6597\u7ffb\u5f00\uff0c\u683c\u683c\u7b11\u9053\uff1a\u201c\u5582\uff0c\u5582\uff0c\u4f60\u522b\u8fd9\u6837\uff01\u6211\u6700\u6015\u6293\u75d2\uff01\u201d \u3000\u3000\u8fd9\u201c\u72c2\u7b11\u7a74\u201d\u662f\u4eba\u8eab\u6b7b\u7a74\u4e4b\u4e00\uff0c\u4e00\u88ab\u70b9\u4e2d\uff0c\u5168\u8eab\u53d1\u8f6f\uff0c\u82e5\u7136\u4e0d\u5f97\u53ca\u65f6\u89e3\u6551\uff0c\u5c31\u8981\u72c2\u7b11\u81f3 \u6c14\u7edd\u800c\u4ea1\u3002\u73b0\u5728\u6c5f\u5357\u7b11\u662f\u7b11\u4e86\uff0c\u4f46\u5374\u5e76\u975e\u72c2\u7b11\uff0c\u800c\u4e14\u4ed6\u8fd8\u80fd\u591f\u63a5\u8fde\u7ffb\u4e24\u4e2a\u7b4b\u6597\uff0c\u8fd9\u5973\u5b50\u867d\u7136 \u8fd8\u672a\u7b97\u5f97\u662f\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u89c1\u6b64\u60c5\u5f62\uff0c\u4e5f\u77e5\u9053\u5979\u7684\u70b9\u7a74\u6cd5\u672a\u66fe\u751f\u6548\u4e86\u3002 \u3000\u3000\u90a3\u5973\u5b50\u6014\u4e86\u4e00\u6014\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u5f00\u4ec0\u4e48\u73a9\u7b11\uff1f\u201d\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u6015\u75d2\uff0c\u4f60\u504f\u8981\u6293\u6211 \u7684\u814b\u7a9d\uff0c\u6211\u4e0d\u8bf4\u4f60\u4e5f\u8fd8\u7f62\u4e86\uff0c\u4f60\u5374\u600e\u7684\u98a0\u5012\u8bf4\u6211\uff0c\u8fd9\u662f\u4f60\u548c\u6211\u5f00\u73a9\u7b11\u554a\uff01\u201d \u3000\u3000\u90a3\u5973\u5b50\u4e58\u4ed6\u4e0d\u5907\uff0c\u6479\u5730\u7528\u5929\u7f57\u6b65\u6cd5\u6b3a\u8fd1\u4ed6\u7684\u8eab\u524d\uff0c\u9a88\u6307\u4e00\u6233\uff0c\u6233\u5411\u4ed6\u80f8\u524d\u201c\u7487\u7391\u7a74\u201d\u3002 \u8fd9\u7487\u7391\u7a74\u4e5f\u662f\u6b7b\u7a74\u4e4b\u4e00\uff0c\u800c\u4e14\u6bd4\u201c\u72c2\u7b11\u7a74\u201d\u88ab\u70b9\u4e2d\u66f4\u4e3a\u5371\u9669\uff0c\u201c\u72c2\u7b11\u7a74\u201d\u88ab\u70b9\u4e2d\u4e0d\u81f3\u7acb\u5373\u6c14 \u7edd\uff0c\u800c\u201c\u7487\u7391\u7a74\u201d\u88ab\u70b9\u4e2d\u5374\u8981\u7acb\u523b\u8eab\u4ea1\u3002 \u3000\u3000\u90a3\u5973\u5b50\u672c\u6765\u65e0\u610f\u5c06\u6c5f\u5357\u7f6e\u4e8e\u6b7b\u5730\uff0c\u5979\u8fd9\u4e00\u62db\u53ea\u662f\u8bd5\u8bd5\u6c5f\u5357\uff0c\u770b\u4ed6\u5982\u4f55\u5e94\u4ed8\uff0c\u54ea\u77e5\u6c5f\u5357\u975e \u4f46\u4e0d\u8eb2\uff0c\u53cd\u800c\u633a\u80f8\u8fce\u4e0a\uff0c\u90a3\u5973\u5b50\u7f29\u624b\u4e0d\u53ca\uff0c\u201c\u535c\u201d\u7684\u4e00\u4e0b\uff0c\u6b63\u6b63\u70b9\u4e2d\u4e86\u4ed6\u7684\u201c\u7487\u7391\u7a74\u201d\uff0c\u6c5f \u5357\u5927\u53eb\u4e00\u58f0\uff0c\u6251\u901a\u4fbf\u5012\u3002 \u3000\u3000\u90a3\u5973\u5b50\u6b63\u5728\u540e\u6094\uff0c\u6c5f\u5357\u7a81\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u7ed9\u6211\u8eba\u4e0b\u5427\uff01\u201d\u4f38\u624b\u4e00\u70b9\uff0c\u90a3\u5973\u5b50 \u679c\u7136\u5e94\u58f0\u800c\u5012\u3002\u4e1c\u9996\u90a3\u4e2a\u5973\u5b50\u8dd1\u51fa\u6765\u6276\u8d77\u540c\u4f34\uff0c\u4f46\u5374\u65e0\u6cd5\u7ed9\u5979\u89e3\u7a74\uff0c\u60ca\u8d77\u6765\u9053\uff1a\u201c\u8bf4\u662f\u4e0e\u4f60 \u5582\u62db\uff0c\u4f60\u600e\u7684\u628a\u5979\u6740\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u8c01\u8bf4\u5979\u662f\u6b7b\u4e86\uff1f\u4f60\u77a7\uff01\u201d\u4ed6\u624b\u6307\u4e00\u5f39\uff0c\u4e00\u7c92\u77f3\u5b50\u98de\u51fa\uff0c\u90a3\u5973\u5b50\u7ed9\u4ed6\u5f39\u4e2d\uff0c \u767b\u65f6\u624b\u8db3\u6d3b\u52a8\u8fc7\u6765\u3002\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8fd9\u70b9\u7a74\u6cd5\u679c\u7136\u795e\u5999\uff0c\u590f\u59d0\u59d0\uff0c\u4f60\u53bb\u8bd5\u4ed6\u7684\u7ef5\u638c\u529f\u592b\u3002\u201d \u3000\u3000\u7b2c\u56db\u4e2a\u5973\u5b50\u53c8\u8d70\u8fdb\u573a\uff0c\u6c5f\u5357\u6c14\u9053\uff1a\u201c\u600e\u7684\u4f60\u4eec\u603b\u662f\u7ea0\u7f20\u4e0d\u4f11\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u65a5\u9053\uff1a\u201c\u4f11\u8bf4\u5e9f\u8bdd\uff0c\u770b\u638c\uff01\u201d\u8eab\u5f62\u5982\u7bad\uff0c\u500f\u5730\u4fbf\u5230\u4e86\u6c5f\u5357\u9762\u524d\uff0c\u4e00\u638c\u62cd\u4e0b\uff0c\u770b\u4f3c \u8f7b\u98d8\u98d8\u7684\uff0c\u4f46\u4e00\u80a1\u6f5c\u529b\u5374\u4f3c\u6697\u6d41\u6c79\u6d8c\uff0c\u7a81\u7136\u88ad\u6765\uff0c\u6b63\u662f\u201c\u7ef5\u638c\u51fb\u77f3\u5982\u7c89\u201d\u7684\u529f\u592b\u3002 \u3000\u3000\u6c5f\u5357\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u632f\u8d77\u7cbe\u795e\uff0c\u548c\u5979\u5bf9\u6253\u3002\u6c5f\u5357\u7684\u5185\u529f\u9020\u8be3\u6bd4\u5979\u9ad8\u6df1\uff0c\u62c6\u5230\u4e86\u4e09\u5341\u4e8c \u62db\uff0c\u6c5f\u5357\u4e00\u638c\u5c06\u5979\u9707\u9000\uff0c\u53ef\u662f\u6c5f\u5357\u4e5f\u5df2\u7ecf\u7d2f\u5f97\u6709\u70b9\u513f\u6c14\u5598\u4e86\u3002 \u3000\u3000\u8fd9\u7fa4\u5c11\u5973\u4e0d\u5f85\u4ed6\u6709\u6b47\u606f\u7684\u673a\u4f1a\uff0c\u7b2c\u4e94\u4e2a\u7b2c\u516d\u4e2a\u53c8\u63a5\u7eed\u800c\u6765\uff0c\u7b2c\u4e94\u4e2a\u5973\u5b50\u7528\u5c0f\u64d2\u62ff\u624b\u548c\u4ed6 \u5bf9\u6253\uff0c\u7b2c\u516d\u4e2a\u5973\u5b50\u5219\u5c06\u51e0\u79cd\u602a\u5f02\u7684\u6b66\u529f\u4ea4\u66ff\u6765\u7528\uff0c\u5176\u4e2d\u6709\u6c5f\u5357\u5b66\u8fc7\u7684\uff0c\u4e5f\u6709\u672a\u5b66\u8fc7\u7684\uff0c\u6c5f\u5357 \u5e94\u4ed8\u5f97\u975e\u5e38\u5403\u529b\uff0c\u4f46\u7ec8\u4e8e\u8fd8\u662f\u5c06\u5979\u4eec\u6253\u8d25\u4e86\u3002 \u3000\u3000\u6c5f\u5357\u8fde\u8d25\u4e86\u5979\u4eec\u516d\u4eba\uff0c\u53d1\u73b0\u5979\u4eec\u6bcf\u4eba\u90fd\u6709\u4e00\u6837\u4e13\u957f\uff0c\u6709\u4e9b\u529f\u592b\uff0c\u6c5f\u5357\u867d\u7136\u4e0d\u8bc6\uff0c\u5374\u77e5\u9053 \u662f\u51fa\u81ea\u4e00\u4e2a\u6e90\u6d41\uff0c\u90a3\u5c31\u662f\u4e54\u5317\u6e9f\u6b66\u529f\u79d8\u7c4d\u3002\u6c5f\u5357\u731b\u5730\u5fc3\u4e2d\u4e00\u52a8\uff0c\u53eb\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4eec\u7684\u6765\u5386 \u4e86\uff0c\u4f60\u4eec\u662f\uff0c\u662f\u2026\u2026\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u6700\u5148\u6765\u5230\u7684\u3001\u7ad9\u5728\u4e1c\u9996\u7684\u90a3\u4e2a\u9ed1\u8863\u5973\u5b50\u53c8\u5df2\u5230\u6765\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ec0 \u4e48\uff1f\u4f11\u5f97\u9976\u820c\uff01\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4f60\u6015\u6211\u63ed\u4f60\u4eec\u7684\u5e95\u7ec6\u4e0d\u662f\uff1f\u597d\u5427\uff0c\u6211\u77e5\u9053\u4e86\u4e5f\u4e0d\u8bf4\u5c31\u662f\u3002\u201d\u90a3 \u5973\u5b50\u51b7\u7b11\u4e00\u58f0\uff1a\u201c\u6211\u6015\u4f60\u4ec0\u4e48\uff1f\u6765\u5427\uff0c\u8fd9\u662f\u6700\u540e\u4e00\u573a\u4e86\uff0c\u4e14\u770b\u770b\u4f60\u7684\u5185\u529f\u5df2\u5230\u4e86\u4ec0\u4e48\u5883\u754c\uff1f \u5bf9\u4e0d\u4f4f\uff0c\u6211\u4eec\u53ef\u8981\u4e24\u4e2a\u4e00\u9f50\u4e0a\u5566\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u6b3a\u8fd1\u8eab\u524d\uff0c\u6479\u5730\u5c31\u662f\u4e00\u638c\uff0c\u51e0\u4e4e\u5c31\u5728\u8fd9\u540c\u4e00\u7684\u65f6\u523b\uff0c\u6c5f\u5357\u731b\u89c9\u52b2\u98ce\u98d2\u7136\uff0c\u53c8\u4e00\u4e2a \u5973\u5b50\u653b\u4e86\u5230\u6765\uff0c\u6a2a\u638c\u5411\u4ed6\u51fb\u4e0b\u3002\u8fd9\u5c11\u5973\u6765\u5f97\u5feb\u6781\uff0c\u6b66\u529f\u4f3c\u662f\u540c\u6d4e\u4e4b\u51a0\u3002 \u3000\u3000\u6c5f\u5357\u53cc\u638c\u4e00\u5206\uff0c\u5de6\u53f3\u62b5\u5fa1\uff0c\u53ea\u542c\u5f97\u201c\u556a\u556a\u201d\u4e24\u58f0\uff0c\u56db\u638c\u76f8\u4ea4\uff0c\u7adf\u91c9\u7740\u4e86\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u5973\u5b50\u540c\u65f6\u8fdb\u8feb\uff0c\u6c5f\u5357\u4f46\u89c9\u5979\u4eec\u7684\u624b\u638c\u5176\u51b7\u5982\u51b0\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765 \u5979\u4eec\u4e5f\u7ec3\u6210\u4e86\u4fee\u7f57\u9634\u715e\u529f\uff0c\u4f46\u4ee5\u6b64\u529f\u529b\u770b\u6765\uff0c\u5927\u7ea6\u6700\u591a\u662f\u7b2c\u4e94\u91cd\u7684\u5883\u754c\u3002\u201d\u91d1\u4e16\u9057\u56e0\u4e3a\u4fee\u7f57 \u9634\u715e\u529f\u592a\u8fc7\u9634\u6bd2\uff0c\u867d\u77e5\u5176\u6cd5\uff0c\u5374\u4e0d\u80af\u7ec3\uff0c\u6c5f\u5357\u5f53\u7136\u66f4\u4e0d\u4f1a\u4e86\u3002\u4f46\u662f\u4ed6\u66fe\u5f97\u5230\u91d1\u4e16\u9057\u4f20\u4ed6\u7684\u4e0a \u4e58\u5185\u529f\u5fc3\u6cd5\uff0c\u8fd9\u4e03\u5e74\u6765\u7528\u529b\u9887\u52e4\uff0c\u5bf9\u6b63\u90aa\u5408\u4e00\u7684\u5185\u529f\u9014\u5f84\uff0c\u5df2\u662f\u521d\u7aa5\u85e9\u7bf1\uff0c\u867d\u7136\u8fd8\u672a\u8c08\u5f97\u4e0a \u767b\u5802\u5165\u5ba4\uff0c\u5374\u8fd8\u53ef\u4ee5\u52c9\u5f3a\u5e94\u4ed8\u8fd9\u4e24\u4e2a\u5973\u5b50\u3002 \u3000\u3000\u53ef\u662f\uff0c\u65f6\u95f4\u4e00\u4e45\uff0c\u5bd2\u6c14\u4fb5\u5165\u4ed6\u7684\u8eab\u4f53\uff0c\u6e10\u6e10\u6269\u6563\u3002\u6c5f\u5357\u4f46\u89c9\u8840\u6db2\u90fd\u4f3c\u4e4e\u7094\u8981\u51dd\u7ed3\u8d77\u6765\uff0c \u53ea\u5f97\u5c3d\u5c55\u6240\u5b66\uff0c\u9ed8\u8fd0\u7384\u529f\uff0c\u4e0e\u5979\u4eec\u5bf9\u6297\u3002\u90a3\u4e24\u4e2a\u5973\u5b50\u4e5f\u602a\uff0c\u6bcf\u5f53\u5bdf\u89c9\u4ed6\u6709\u4e0d\u652f\u7684\u8ff9\u8c61\u4e4b\u65f6\uff0c \u4fbf\u653e\u677e\u4e00\u9635\uff0c\u7136\u540e\u52a0\u7d27\u65bd\u4e3a\uff0c\u5982\u662f\u8005\u6570\u6b21\u4e4b\u591a\uff0c\u8fc7\u4e86\u5927\u7ea6\u4e00\u4e2a\u65f6\u8fb0\uff0c\u6c5f\u5357\u6e10\u6e10\u6c14\u8870\u529b\u7aed\uff0c\u4e0d \u7531\u5f97\u6d51\u8eab\u98a4\u6296\u8d77\u6765\u3002 \u3000\u3000\u5de6\u9762\u90a3\u9ed1\u8863\u5973\u5b50\u7b11\u9053\uff1a\u201c\u8ba9\u4ed6\u5c0f\u75c5\u4e00\u573a\uff0c\u4f60\u770b\u8fd9\u60e9\u7f5a\u591f\u4e86\u5417\uff1f\u201d\u53f3\u9762\u90a3\u767d\u8863\u5973\u5b50\u9053\uff1a \u201c\u8bba\u7406\u6765\u8bf4\uff0c\u8fd9\u5c0f\u5b50\u4fae\u8fb1\u4e86\u54b1\u4eec\u7684\u6559\u4e3b\uff0c\u53ea\u53eb\u4ed6\u5c0f\u75c5\u4e00\u573a\uff0c\u60e9\u7f5a\u8fd8\u662f\u592a\u8f7b\u3002\u4e0d\u8fc7\uff0c\u5ff5\u5728\u4ed6\u4eca \u5929\u966a\u54b1\u4eec\u7ec3\u4e86\u8bb8\u591a\u573a\u529f\u592b\uff0c\u53c8\u6709\u59d0\u59d0\u4f60\u66ff\u4ed6\u8bf4\u60c5\uff0c\u90a3\u4e5f\u5c31\u7b97\u4e86\u5427\u3002\u201d\u4e24\u4e2a\u5973\u5b50\u540c\u65f6\u64a4\u638c\u62bd \u8eab\u3002\u6c5f\u5357\u6d51\u8eab\u4e4f\u529b\uff0c\u53cc\u817f\u4e00\u8f6f\uff0c\u4e0d\u7531\u5f97\u5750\u5728\u5730\u4e0a\u3002 \u3000\u3000\u90a3\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e00\u5411\u9976\u820c\uff0c\u54b1\u4eec\u5f97\u8981\u4ed6\u4e00\u4ef6\u62bc\u5934\u3002\u201d\u90a3\u9ed1\u8863\u5973\u5b50\u9053\uff1a\u201c\u4e0d\u9519\u3002 \u597d\uff0c\u4f60\u8fd9\u5c0f\u5b50\u542c\u7740\uff1a\u6211\u4eec\u8d70\u4e86\u4e4b\u540e\uff0c\u4f60\u53ef\u4e0d\u8bb8\u5c06\u8fd9\u4ef6\u4e8b\u5bf9\u522b\u4eba\u8bf4\u3002\u4f60\u82e5\u662f\u5230\u5904\u53bb\u80e1\u4e71\u6258\u4eba\uff0c \u8ffd\u67e5\u6211\u4eec\u7684\u5e95\u7ec6\uff0c\u90a3\u6211\u4eec\u53ef\u8981\u5bf9\u4f60\u4e0d\u5ba2\u6c14\u5566\u3002\u201d \u3000\u3000\u6c5f\u5357\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u7978\u4ece\u53e3\u51fa\uff0c\u4eca\u5929\u6211\u603b\u7b97\u77e5\u9053\u5566\u3002\u4ee5\u540e\u6211\u4ec0\u4e48\u4e5f\u4e0d\u8bf4\u4e86\u3002\u201d\u90a3\u9ed1\u8863\u5973\u5b50 \u9053\uff1a\u201c\u4f60\u8bdd\u662f\u5982\u6b64\uff0c\u6211\u5374\u4fe1\u4f60\u4e0d\u8fc7\u3002\u4f60\u7684\u513f\u5b50\uff0c\u6211\u4eec\u6682\u65f6\u5c06\u4ed6\u5e26\u53bb\uff0c\u8981\u662f\u6ca1\u4e8b\uff0c\u8fc7\u4e86\u51e0\u5e74\uff0c \u518d\u8fd8\u7ed9\u4f60\u3002\u201d \u3000\u3000\u6c5f\u5357\u5927\u60ca\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u4f7f\u5f97\uff1f\u5582\uff0c\u5582\uff0c\u7eb5\u7b97\u662f\u6211\u5f97\u7f6a\u4e86\u4f60\u4eec\uff0c\u5374\u5173\u6211\u513f\u5b50\u4ec0\u4e48\u4e8b\uff1f\u201d\u4ed6 \u6323\u624e\u7740\u8df3\u5c06\u8d77\u6765\uff0c\u53ef\u662f\u90a3\u7fa4\u5973\u5b50\u5df2\u7ecf\u547c\u5578\u800c\u53bb\uff0c\u4ed6\u7684\u513f\u5b50\u4e5f\u7ed9\u5e26\u8d70\u4e86\uff0c\u6c5f\u5357\u8981\u8d8a\u8fc7\u5899\u5934\u53bb \u8ffd\uff0c\u5374\u662f\u529b\u4e0d\u4ece\u5fc3\uff0c\u78b0\u7740\u56f4\u5899\u4fbf\u8dcc\u4e0b\u6765\uff0c\u9690\u9690\u8fd8\u542c\u5f97\u4ed6\u7684\u513f\u5b50\u5728\u53eb\u7740\u7239\u7239\u3002 \u3000\u3000\u6768\u67f3\u9752\u4e24\u6bcd\u5973\u7684\u7a74\u9053\u5c1a\u672a\u89e3\u5f00\uff0c\u6c5f\u5357\u76d8\u819d\u5750\u4e86\u4e00\u4f1a\uff0c\u7cbe\u795e\u7a0d\u7a0d\u6062\u590d\uff0c\u8d70\u8fc7\u53bb\u770b\uff0c\u5e78\u800c\u90a3 \u5973\u5b50\u7528\u7684\u4e0d\u662f\u91cd\u624b\u6cd5\u70b9\u7a74\uff0c\u800c\u6c5f\u5357\u53c9\u662f\u70b9\u7a74\u7684\u5927\u884c\u5bb6\uff0c\u5185\u529b\u867d\u672a\u6062\u590d\uff0c\u65f6\u95f4\u4e0d\u8fc7\u7a0d\u957f\u4e00\u4e9b\uff0c \u7ec8\u5b50\u4e5f\u7ed9\u5cb3\u6bcd\u548c\u59bb\u5b50\u89e3\u5f00\u4e86\u7a74\u9053\u3002 \u3000\u3000\u6768\u67f3\u9752\u7a74\u9053\u4e00\u89e3\uff0c\u7acb\u5373\u4fbf\u9a82\u4ed6\u9053\uff1a\u201c\u5bb3\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u6709\uff0c\u9632\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u65e0\u3002\u4f60\u5df2\u7ecf\u662f\u505a\u4e86 \u7238\u7238\u7684\u4eba\u4e86\uff0c\u5374\u600e\u7684\u8fd8\u662f\u8fd9\u6837\u7cca\u6d82\uff1f\u8fd9\u73ed\u5996\u5973\u4e0d\u7ea6\u800c\u6765\uff0c\u4f60\u5c31\u5e94\u8be5\u5148\u628a\u5979\u4eec\u64d2\u4e0b\uff0c\u5979\u4eec\u7684\u6b66 \u529f\u90fd\u4e0d\u662f\u4f60\u7684\u5bf9\u624b\uff0c\u4f60\u4e0d\u5f85\u5979\u4eec\u5408\u56f4\uff0c\u4fbf\u884c\u52a8\u624b\uff0c\u6700\u5c11\u4e5f\u53ef\u4ee5\u5148\u64d2\u83b7\u4e09\u4e24\u4e2a\u4f5c\u4e3a\u4eba\u8d28\uff0c\u5979\u4eec \u8fd8\u6562\u80e1\u6765\u5417\uff1f\u4f60\u5374\u4e00\u573a\u4e00\u573a\u7684\u4e0e\u5979\u4eec\u6bd4\u8bd5\u4ec0\u4e48\u529f\u592b\uff0c\u771f\u662f\u5929\u4e0b\u7b2c\u4e00\u7684\u5927\u50bb\u74dc\uff01\u597d\u5566\uff0c\u5982\u4eca\u4e22 \u4e86\u6211\u7684\u5916\u5b59\uff0c\u6211\u770b\u4f60\u5982\u4f55\u53bb\u6211\u56de\u6765\uff1f\u201d \u3000\u3000\u6c5f\u5357\u8eab\u4e0a\u6240\u4e2d\u7684\u9634\u715e\u5bd2\u6bd2\uff0c\u8fd8\u6765\u4e0d\u53ca\u8fd0\u529f\u9a71\u9664\uff0c\u7259\u5173\u5140\u81ea\u6253\u98a4\uff0c\u88ab\u4ed6\u5cb3\u6bcd\u4e00\u9a82\uff0c\u66f4\u662f\u6c14 \u6cae\u795e\u4f24\uff0c\u9762\u5982\u767d\u7eb8\u3002\u90b9\u7edb\u971e\u70ab\u7136\u6cea\u4e0b\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5a18\uff0c\u4e0d\u8981\u518d\u9a82\u4ed6\u5566\u3002\u4e8b\u5df2\u5982\u65af\uff0c\u9a82\u4e5f\u6ca1 \u7528\uff0c\u5496\u5f97\u60f3\u4e2a\u529e\u6cd5\u624d\u597d\u3002\u201d \u3000\u3000\u6768\u67f3\u9752\u770b\u5979\u5973\u5a7f\u53ef\u601c\uff0c\u6d88\u4e86\u6012\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u5973\u5b50\u662f\u4ec0\u4e48\u4eba\uff1f\u4e3a\u4f55\u5979\u4eec\u8bf4\u4f60\u4fae\u8fb1\u4e86 \u5979\u4eec\u7684\u6559\u4e3b\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4f9d\u6211\u770b\u6765\uff0c\u5979\u4eec\u4f3c\u4e4e\u5c31\u662f\u5f53\u5e74\u5389\u80dc\u7537\u5e26\u4e0a\u5929\u5c71\u7684\u90a3\u516b\u540d\u968f\u8eab\u4f8d \u5973\u3002\u5979\u4eec\u8bf4\u6211\u4fae\u8fb1\u4e86\u5979\u4eec\u7684\u6559\u4e3b\uff0c\u5927\u7ea6\u662f\u6307\u6211\u5f53\u65f6\u66fe\u9a82\u8fc7\u5389\u80dc\u7537\u3002\u201d \u3000\u3000\u90b9\u7edb\u971e\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6211\u770b\u5979\u4eec\u597d\u751f\u773c\u719f\uff0c\u539f\u6765\u662f\u5389\u80dc\u7537\u90a3\u516b\u4e2a\u4e2b\u5934\u3002\u539f\u6765 \u5389\u80dc\u7537\u751f\u524d\u8fd8\u66fe\u505a\u4e86\u4ec0\u4e48\u6559\u4e3b\u3002\u54fc\uff0c\u5979\u751f\u524d\u4f5c\u6076\u591a\u7aef\uff0c\u6b7b\u540e\u8fd8\u7559\u4e0b\u4e86\u8fd9\u7fa4\u5996\u5973\u8d3b\u7978\u4eba\u95f4\u3002\u5357 \u54e5\uff0c\u4f60\u662f\u53d7\u4e86\u5979\u4eec\u7684\u4f24\u5566\uff1f\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u4e0d\u6253\u7d27\uff0c\u7a0d\u7a0d\u53d7\u4e86\u70b9\u9634\u5bd2\u4e4b\u6c14\uff0c\u660e\u5929\u5c31\u6ca1\u4e8b\u4e86\u3002\u201d\u90b9\u7edb\u971e\u6276\u4ed6\u56de\u623f\u6b47\u606f\uff0c\u5bb6 \u4e2d\u6742\u7269\u4e22\u6ee1\u6ee1\u5730\uff0c\u4e00\u7247\u51cc\u4e71\u3002 \u3000\u3000\u6768\u67f3\u9752\u6c14\u6124\u672a\u6d88\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u54b1\u4eec\u7684\u5bb6\u90fd\u51e0\u4e4e\u7ed9\u8fd9\u7fa4\u5996\u5973\u6bc1\u4e86\u3002\u6768\u5bb6\u4ece\u672a\u66fe\u53d7\u8fc7\u8fd9 \u7b49\u803b\u8fb1\uff01\u6c5f\u5357\uff0c\u4f60\u8c03\u6cbb\u597d\u4e86\uff0c\u62ff\u6211\u7684\u4eb2\u7b14\u4e66\u4fe1\u4e0a\u5929\u5c71\u89c1\u5510\u6653\u6f9c\u53bb\uff01\u201d\u90b9\u7ec8\u971e\u9053\uff1a\u201c\u5982\u4f55\u5e94 \u4ed8\uff0c\u5f85\u660e\u5929\u6162\u6162\u5546\u91cf\u3002\u201d\u5979\u662f\u6015\u513f\u5b50\u843d\u5728\u5979\u4eec\u624b\u4e2d\uff0c\u82e5\u7136\u8bf7\u51fa\u6b66\u6797\u524d\u8f88\u5e72\u9884\uff0c\u53ea\u6015\u4f1a\u5bf9\u513f\u5b50 \u4e0d\u5229\u3002 \u3000\u3000\u6c5f\u5357\u5583\u5583\u8bf4\u9053\uff1a\u201c\u5979\u4eec\u56e0\u4e3a\u6211\u66fe\u9a82\u4e86\u5389\u80dc\u7537\uff0c\u8981\u4f5c\u5f04\u6211\uff0c\u8fd9\u4e5f\u7f62\u4e86\uff0c\u6211\u5374\u4e0d\u660e\u767d\u5979\u4eec\u4e3a \u4f55\u8981\u5230\u54b1\u4eec\u7684\u5bb6\u91cc\u6765\u6363\u4e71\u3002\u201d \u3000\u3000\u6768\u67f3\u9752\u56e0\u4e3a\u813e\u6c14\u66b4\u8e81\uff0c\u5979\u7684\u6b66\u529f\u7ec8\u751f\u90fd\u4e0d\u80fd\u8fdb\u5165\u4e00\u6d41\u5883\u754c\u3002\u4f46\u5979\u51fa\u8eab\u6b66\u529f\u4e16\u5bb6\uff0c\u89c1\u591a\u8bc6 \u5e7f\uff0c\u6c5f\u6e56\u4eba\u7269\u7684\u4f0e\u4fe9\u90fd\u7792\u4e0d\u8fc7\u5979\u3002\u5979\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u5947\u602a\uff1f\u6c5f\u5357\uff0c\u4f60\u4ee5\u4e3a\u5979\u4eec \u4e0e\u4f60\u6bd4\u6b66\uff0c\u4ec5\u4ec5\u662f\u8981\u6349\u5f04\u4f60\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u5a18\uff0c\u4f9d\u4f60\u4e4b\u89c1\u5982\u4f55\uff1f\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u5979\u4eec\u6bcf\u4eba\u90fd\u53ea\u64c5\u957f\u4e00\u6837\u529f\u592b\uff0c\u5927\u7ea6\u5389\u80dc \u7537\u4e5f\u672a\u66fe\u5c06\u4e54\u5317\u6e9f\u79d8\u590d\u4e0a\u7684\u529f\u592b\u90fd\u6559\u7ed9\u5979\u4eec\uff0c\u800c\u662f\u6bcf\u4eba\u53ea\u6559\u4e00\u6837\u3002\u5389\u80dc\u7537\u6b7b\u540e\uff0c\u5979\u4eec\u4e92\u76f8\u7422 \u78e8\uff0c\u4f46\u4e5f\u4ecd\u662f\u4e00\u9cde\u534a\u722a\uff0c\u96be\u7aa5\u5168\u8c79\u3002\u5979\u4eec\u4ee5\u4e3a\u4f60\u66fe\u5f97\u91d1\u4e16\u9057\u7684\u771f\u4f20\uff0c\u8bf4\u4e0d\u5b9a\u79d8\u7626\u4e5f\u5728\u4f60\u8fd9 \u91cc\uff0c\u6240\u4ee5\u624d\u6765\u641c\u7d22\u3002\u540e\u6765\u641c\u4e0d\u51fa\u4ec0\u4e48\u4e1c\u897f\uff0c\u53c8\u89c1\u4f60\u6240\u4f1a\u7684\u4e5f\u662f\u6709\u9650\uff0c\u8fd9\u624d\u7f62\u4e86\u3002\u4f9d\u6211\u770b\u6765\uff0c \u5979\u4eec\u4e0e\u4f60\u6bd4\u6b66\uff0c\u6b63\u662f\u8981\u5957\u53d6\u4f60\u7684\u529f\u592b\uff0c\u4ee5\u8865\u5145\u5979\u4eec\u7684\u4e0d\u8db3\u3002\u201d \u3000\u3000\u6768\u67f3\u9752\u8fd9\u756a\u63a8\u8bba\uff0c\u6c5f\u5357\u4e5f\u89c9\u5f97\u5408\u60c5\u5408\u7406\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u6837\u4e00\u6765\uff0c\u6c5f\u6e56\u4e0a\u5c82\u4e0d\u662f\u53c8\u8981\u6380 \u8d77\u98ce\u6ce2\uff1f\u82e5\u7136\u5979\u4eec\u4ed7\u6280\u80e1\u4e3a\uff0c\u6211\u6240\u4f1a\u7684\u529f\u592b\u90fd\u5df2\u7ed9\u5979\u4eec\u9a97\u53bb\uff0c\u6211\u4e5f\u6709\u8fc7\u9519\u4e86\u3002\u201d\u4ed6\u65e2\u4f24\u5fc3\u513f \u5b50\u7684\u5931\u53bb\uff0c\u53c8\u5fe7\u8651\u6b64\u4e8b\u7684\u540e\u679c\uff0c\u597d\u751f\u4e0d\u4e50\u3002\u90b9\u7edb\u971e\u529d\u6170\u4ed6\u9053\uff1a\u201c\u4f60\u8eab\u4f53\u8981\u7d27\uff0c\u5148\u5f97\u8c03\u517b\u597d \u4e86\uff0c\u7136\u540e\u624d\u6709\u529e\u6cd5\u53ef\u60f3\u3002\u201d \u3000\u3000\u6c5f\u5357\u7684\u5185\u529f\u5df1\u6709\u4e86\u5f88\u6df1\u7684\u9020\u8be3\uff0c\u90a3\u7fa4\u5973\u5b50\u4ee5\u4e3a\u4ed6\u6700\u5c11\u8981\u5c0f\u75c5\u4e00\u573a\u7684\uff0c\u7ed3\u679c\u4ed6\u9759\u5750\u8fd0\u529f\uff0c \u53ea\u662f\u8fc7\u4e86\u4e00\u4e2a\u665a\u4e0a\uff0c\u4fbf\u5df2\u5b8c\u5168\u6062\u590d\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u6768\u67f3\u9752\u6bcd\u5973\u4e0e\u4ed6\u5546\u91cf\uff0c\u6768\u67f3\u9752\u662f\u5b81\u6298\u4e0d\u5c48\u7684\u813e\u6c14\uff0c\u4e3b\u5f20\u6c5f\u5357\u4e0a\u5927\u5c71\u53bb\u8bf7\u5510\u6653\u6f9c\u51fa \u6765\u8ffd\u7a76\u6b64\u4e8b\uff0c\u90b9\u7edb\u971e\u5374\u6015\u4e8b\u60c5\u95f9\u5927\uff0c\u6253\u8349\u60ca\u86c7\uff0c\u53cd\u4e3a\u4e0d\u5999\u3002\u6c5f\u5357\u9053\uff1a\u201c\u6211\u4eec\u5f53\u7136\u4e0d\u80fd\u53d7\u5979\u4eec \u7684\u6050\u5413\uff0c\u513f\u5b50\u4e5f\u4e00\u5b9a\u8981\u627e\u56de\u6765\u3002\u4e0d\u8fc7\uff0c\u5728\u8fd8\u6709\u529e\u6cd5\u53ef\u60f3\u4e4b\u524d\uff0c\u5374\u4e0d\u5fc5\u53bb\u9ebb\u70e6\u5510\u5927\u4fa0\uff0c\u4ee4\u5230\u5929 \u4e0b\u6b66\u6797\u9707\u52a8\u3002\u201d \u3000\u3000\u6768\u67f3\u9752\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u6211\u770b\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6700\u597d\u8fd8\u662f\u8bf7\u91d1\u5927\u4fa0\u5e2e\u5fd9\u3002\u8fd9 \u51e0\u4e2a\u5973\u5b50\u4e43\u662f\u5389\u80dc\u7537\u7684\u4e2b\u5934\uff0c\u7528\u7684\u53c8\u662f\u4e54\u5317\u6e9f\u79d8\u7c4d\u4e0a\u7684\u529f\u592b\uff0c\u4e5f\u5373\u662f\u4e0e\u91d1\u5927\u4fa0\u6709\u4e9b\u5173\u8054\u3002\u7eb5 \u7136\u6487\u5f00\u6211\u4e0e\u4ed6\u7684\u4ea4\u60c5\u4e0d\u8c08\uff0c\u8fd9\u4e8b\u4ed6\u4e5f\u4e0d\u80fd\u4e0d\u7ba1\u3002\u201d \u3000\u3000\u90b9\u7edb\u971e\u76b1\u4e86\u76b1\u7709\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bdd\u867d\u8bf4\u5f97\u6709\u7406\uff0c\u5374\u600e\u77e5\u4f60\u7684\u91d1\u5927\u4fa0\u73b0\u5728\u4f55\u65b9\uff1f\u201d\u6c5f\u5357\u9053\uff1a \u201c\u6211\u60f3\u5148\u4e0a\u6c13\u5c71\u8bbf\u8c37\u4e4b\u534e\uff0c\u518d\u5230\u82cf\u5dde\u5bfb\u6211\u7684\u4e49\u5144\u9648\u516c\u5b50\uff0c\u4ed6\u4eec\u4e24\u4eba\u6216\u8bb8\u4f1a\u77e5\u9053\u91d1\u5927\u4fa0\u7684\u884c \u8e2a\u3002\u800c\u4e14\u5373\u7b97\u627e\u4e0d\u5230\u4e5f\u603b\u80dc\u4e8e\u4e0d\u53bb\u627e\u5440\u3002\u201d \u3000\u3000\u6768\u67f3\u9752\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4e5f\u4e0d\u59a8\u591a\u65b9\u8bbe\u6cd5\uff0c\u91d1\u4e16\u9057\u90a3\u513f\u4e5f\u662f\u4e00\u6761\u8def\u5b50\u3002\u82e5\u662f\u4f60\u6253 \u542c\u4e0d\u5230\u4ed6\u7684\u4e0b\u843d\uff0c\u4f60\u518d\u4e0a\u5929\u5c71\u53bb\u5427\u3002\u6211\u8fd9\u5c01\u4eb2\u7b14\u4e66\u4fe1\u5148\u4ea4\u7ed9\u4f60\uff0c\u4f60\u968f\u65f6\u53ef\u4ee5\u53bb\u89c1\u5510\u6653\u6f9c\u3002\u5176 \u5b9e\u4f9d\u6211\u770b\u6765\uff0c\u8bf7\u5510\u6653\u6f9c\u76f8\u52a9\uff0c\u90a3\u662f\u8e0f\u5b9e\u53ef\u9760\u5f97\u591a\u3002\u201d\u539f\u6765\u6768\u67f3\u9752\u66fe\u7ecf\u662f\u5510\u6653\u6f9c\u7684\u672a\u5a5a\u59bb\u5b50\uff0c \u540e\u6765\u867d\u7136\u5a5a\u4e8b\u4e0d\u6210\uff0c\u4ea4\u60c5\u4ecd\u5728\u3002\u73b0\u5728\u5510\u6653\u6f9c\u5df2\u6210\u4e3a\u8eab\u8d1f\u5929\u4e0b\u6b66\u6797\u91cd\u671b\u7684\u5927\u5b97\u5e08\uff0c\u5728\u6768\u67f3\u9752\u7684 \u5185\u5fc3\uff0c\u8fd8\u662f\u628a\u4ed6\u4f5c\u4e3a\u81ea\u5df1\u7684\u9a84\u50b2\uff0c\u8fd9\u79cd\u60c5\u7eea\uff0c\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u4fbf\u4f1a\u6d41\u9732\u51fa\u6765\u3002\u8ba1\u8bae\u5df1\u5b9a\uff0c\u6c5f\u5357 \u5f53\u65e5\u4fbf\u5373\u79bb\u5bb6\uff0c\u7ecf\u8fc7\u4e86\u4e94\u65e5\u7684\u65c5\u7a0b\uff0c\u5230\u4e86\u6c13\u5c71\u4e1c\u9762\u7684\u4e00\u4e2a\u5c0f\u9547\uff0c\u5730\u540d\u65b0\u5b89\uff0c\u79bb\u6c13\u5c71\u5c1a\u6709\u4e00\u767e \u591a\u91cc\uff0c\u6b63\u662f\u51e0\u5e74\u4e4b\u524d\uff0c\u4ed6\u548c\u9648\u5929\u5b87\u5728\u8fd9\u91cc\u9047\u89c1\u5389\u80dc\u7537\u7684\u5730\u65b9\u3002\u5176\u65f6\u5929\u8272\u5df2\u665a\uff0c\u6c5f\u5357\u5b58\u7740\u4e00\u4efd \u6000\u65e7\u7684\u611f\u60c5\uff0c\u627e\u5230\u4e86\u5f53\u5e74\u4ed6\u66fe\u4f4f\u8fc7\u7684\u90a3\u95f4\u5ba2\u5e97\u6295\u5bbf\u3002 \u3000\u3000\u5ba2\u5e97\u7684\u751f\u610f\u4ee5\u4e4e\u4e0d\u600e\u4e48\u597d\uff0c\u6709\u51e0\u95f4\u623f\u5b50\u7a7a\u7740\uff0c\u6c5f\u5357\u95ee\u4e86\u4e00\u95ee\uff0c\u4ed6\u4ee5\u524d\u5f80\u8fc7\u7684\u897f\u9996\u7684\u90a3\u95f4 \u53a2\u623f\u4e5f\u8fd8\u672a\u66fe\u79df\u51fa\uff0c\u4fbf\u8981\u4e86\u8fd9\u95f4\u623f\u5b50\u3002\u5e97\u5c0f\u4e8c\u5949\u627f\u4ed6\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u662f\u672c\u5e97\u7684\u8001\u5ba2\u4eba\u5566\uff0c\u8fd9\u662f \u672c\u5e97\u6700\u597d\u7684\u5ba2\u623f\u4e4b\u4e00\uff0c\u4e0d\u4e45\u4e4b\u524d\uff0c\u6709\u4e00\u4f4d\u5ba2\u4eba\u5230\u8fd9\u91cc\u6295\u5bbf\uff0c\u4e5f\u662f\u6307\u5b9a\u8981\u8fd9\u95f4\u623f\u5b50\u3002\u201d\u62ff\u4e86\u9501 \u5319\uff0c\u4fbf\u5e26\u6c5f\u5357\u53bb\u65a4\u8fd9\u95f4\u5ba2\u623f\u3002 \u3000\u3000\u6c5f\u5357\u5927\u611f\u5174\u8da3\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u662f\u4e00\u4f4d\u5f88\u9614\u6c14\u7684\u5b98\u592a\u592a\uff0c\u5750\u8f7f \u6765\u7684\u3002\u201d\u6c5f\u5357\u76f8\u8bc6\u7684\u4eba\u867d\u7136\u4e0d\u5c11\uff0c\u4f46\u5374\u6ca1\u6709\u5b98\u592a\u592a\u8eab\u4efd\u7684\u4eba\uff0c\u4e00\u542c\u4e4b\u540e\uff0c\u5174\u65fa\u7d22\u7136\uff0c\u5fc3\u4e2d\u60f3 \u9053\uff1a\u201c\u6216\u8005\u8fd9\u53ea\u662f\u5076\u7136\u7684\u5de7\u5408\uff0c\u4f55\u8db3\u4e3a\u5947\uff1f\u201d\u539f\u6765\u4ed6\u8fd8\u4ee5\u4e3a\u53ef\u80fd\u662f\u91d1\u4e16\u9057\u5462\uff1f\u4e00\u542c\u8bf4\u662f\u4f4d\u5b98 \u5927\u592a\uff0c\u4ed6\u8bb0\u4f4f\u59bb\u5b50\u53ee\u5631\u4ed6\u4e0d\u53ef\u591a\u8bdd\uff0c\u4fbf\u4e0d\u518d\u95ee\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u5e97\u5c0f\u4e8c\u72b9\u5728\u557c\u557c\u53e8\u53e8\uff0c\u8bf4\u90a3\u5b98\u4eba\u4eba\u5982\u4f55\u5982\u4f55\u9614\u6c14\uff0c\u53ea\u6253\u8d4f\u4fbf\u662f\u4e00\u952d\u6210\u8272\u5341\u8db3\u7684\u5927\u94f6\u3002\u6c5f \u5357\u6b63\u81fc\u542c\u4ed6\u8bf4\u8bdd\uff0c\u5ffd\u5730\u6709\u4e00\u4e2a\u4eba\u5306\u5306\u4ece\u8fc7\u9053\u90a3\u8fb9\u8d70\u6765\uff0c\u649e\u4e86\u6c5f\u5357\u4e00\u4e0b\uff0c\u54ce\u54df\u4e00\u58f0\uff0c\u5411\u540e\u9000\u4e86 \u51e0\u6b65\u3002\u6c5f\u5357\u5b9a\u775b\u4e00\u770b\uff0c\u53ea\u89c1\u662f\u4e2a\u5c0f\u53ae\u6a21\u6837\u7684\u5e74\u8f7b\u4eba\uff0c\u9752\u8863\u5c0f\u5e3d\uff0c\u8863\u88f3\u5012\u662f\u5149\u9c9c\uff0c\u6c5f\u5357\u6b63\u8981\u9053 \u6b49\uff0c\u5374\u89c1\u4e1c\u9996\u90a3\u95f4\u53a2\u623f\uff0c\u5df2\u51fa\u6765\u4e86\u4e00\u4e2a\u8863\u88f3\u534e\u4e3d\u7684\u5ba2\u4eba\uff0c\u9a82\u4ed6\u90a3\u4e2a\u5c0f\u53ae\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u9a82\u9053\uff1a\u201c\u5c0f\u4e09\u5b50\uff0c\u4f60\u600e\u7684\u8001\u662f\u8fd9\u6837\u83bd\u649e\uff0c\u8d70\u8def\u4e5f\u4e0d\u5e26\u773c\u775b\uff0c\u8fd8\u4e0d\u5feb\u5411\u8fd9\u4f4d\u5ba2\u5b98\u8d54 \u7f6a\u3002\u201d\u6c5f\u5357\u672c\u662f\u4e66\u7ae5\u51fa\u8eab\uff0c\u5bf9\u8fd9\u5c0f\u53ae\u9887\u4e3a\u540c\u60c5\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u4e9b\u987b\u5c0f\u4e8b\uff0c\u4f55\u8db3\u4ecb\u610f\uff0c\u55ef\uff0c\u5c0f \u5144\u5f1f\uff0c\u6ca1\u6709\u78b0\u4f24\u4f60\u5427\uff1f\u201d\u90a3\u5c0f\u53ae\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u6ca1\u6709\u3002\u5ba2\u5b98\u5440\uff0c\u4f60\u6c14\u529b\u597d\u5927\uff01\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u7b11\u9053\uff1a\u201c\u542c\u4f60\u8fd9\u53e3\u6c14\uff0c\u4f60\u597d\u50cf\u8fd8\u5728\u57cb\u6028\u4eba\u5bb6\u5462\u3002\u201d\u90a3\u5c0f\u53ae\u5fd9\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\u3002 \u5509\uff0c\u5176\u5b9e\u90fd\u662f\u4f60\u7684\u4e0d\u597d\u3002\u201d\u5e97\u5c0f\u4e8c\u8be7\u9053\uff1a\u201c\u4f60\u78b0\u7740\u4eba\u5bb6\uff0c\u600e\u4e48\u53cd\u63a8\u5230\u6211\u7684\u5934\u4e0a\u6765\u4e86\uff1f\u201d \u3000\u3000\u90a3\u5c0f\u53ae\u9053\uff1a\u201c\u516c\u5b50\u65e9\u5c31\u5429\u5490\u4f60\u4eec\u51c6\u5907\u665a\u996d\uff0c\u4f60\u5230\u73b0\u5728\u8fd8\u672a\u9001\u6765\uff0c\u516c\u5b50\u53eb\u6211\u53bb\u50ac\uff0c\u54b3\uff0c\u4f60 \u60f3\u60f3\uff0c\u82e5\u662f\u4f60\u65e9\u4e9b\u5f00\u996d\uff0c\u6211\u600e\u4f1a\u5fc3\u6025\u53bb\u50ac\uff0c\u6211\u82e5\u4e0d\u5fc3\u6025\uff0c\u53c8\u600e\u4f1a\u78b0\u4e86\u8fd9\u4f4d\u5ba2\u5b98\uff1f\u201d\u5e97\u5c0f\u4e8c\u7b11 \u9053\uff1a\u201c\u542c\u4f60\u8bf4\u7684\u3002\u5012\u597d\u50cf\u8fd8\u6709\u4e00\u756a\u6b6a\u7406\u5462\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u5c11\u5e74\u516c\u5b50\u65e9\u5df2\u56de\u5230\u81ea\u5df1\u7684\u623f\u4e2d\uff0c\u5e97\u5c0f\u4e8c\u5374\u606d\u606d\u656c\u656c\u7684\u5bf9\u7740\u4ed6\u7684\u623f\u95e8\u8bf4\u9053\uff1a\u201c\u7980 \u516c\u5b50\uff0c\u516c\u5b50\u5429\u5490\u7684\u90a3\u51e0\u6837\u5c0f\u83dc\uff0c\u5df2\u53eb\u53a8\u5b50\u5c0f\u5fc3\u53bb\u505a\u4e86\uff0c\u4e00\u65f6\u672a\u80fd\u5f04\u597d\uff0c\u8fd8\u671b\u516c\u5b50\u6055\u7f6a\uff0c\u5c31\u5feb \u8981\u9001\u6765\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u516c\u5b50\u5728\u623f\u5185\u5e94\u9053\uff1a\u201c\u77e5\u9053\u5566\u3002\u6211\u4e0d\u8fc7\u662f\u53eb\u5c0f\u53ae\u53bb\u770b\u770b\uff0c\u770b\u4f60\u4eec\u51c6\u5907\u5f97\u5982\u4f55\uff0c\u5e76\u975e \u7b49\u7740\u6765\u5403\uff0c\u662f\u4ed6\u81ea\u5df1\u5fc3\u6025\u3002\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u653e\u5fc3\uff0c\u6750\u6599\u90fd\u662f\u9009\u6700\u4e0a\u4e58\u7684\u3002\u201d \u3000\u3000\u90a3\u516c\u5b50\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5c0f\u4e09\u5b50\u4f60\u4e5f\u4e0d\u5fc5\u5230\u53a8\u623f\u53bb\u4e86\u3002\u56de\u6765\u5427\uff0c\u522b\u5520\u5520\u53e8\u53e8\u7684\uff0c\u53eb\u4eba\u9a82 \u4f60\u662f\u4e2a\u591a\u5634\u7684\u5c0f\u53ae\uff01\u201d\u6c5f\u5357\u542c\u4e86\uff0c\u5927\u4e0d\u8212\u670d\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5fc3\u9053\uff1a\u201c\u666e\u5929\u4e4b\u4e0b\uff0c\u505a\u516c\u5b50\u7237\u7684 \u4eba\uff0c\u5927\u7ea6\u90fd\u662f\u8fd9\u6837\u5bf9\u5f85\u4e0b\u4eba\u7684\uff0c\u52a8\u4e0d\u52a8\u5c31\u9a82\uff0c\u8bf4\u5f97\u4e0d\u597d\u8fd8\u8981\u6253\u5165\u5462\uff0c\u50cf\u6211\u7684\u4e49\u5144\u9648\u516c\u5b50\uff0c\u90a3 \u662f\u6781\u5c11\u6570\u7684\u4f8b\u5916\u3002\u568f\uff0c\u4ed6\u53c8\u4e0d\u77e5\u6211\u7684\u51fa\u8eab\uff0c\u4ed6\u9a82\u4ed6\u7684\u5c0f\u53ae\uff0c\u6211\u778e\u731c\u7591\u4f5c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53ef\u662f\u8fd9\u4e00\u4e2a\u7591\u5fc3\u521a\u521a\u6d88\u6563\uff0c\u53e6\u4e00\u4ef6\u66f4\u5927\u7684\u6000\u7591\u968f\u5373\u53c8\u6d8c\u5230\u5fc3\u5934\u3002\u6c5f\u5357\u867d\u8bf4\u662f\u80f8\u65e0\u57ce\u5e9c\uff0c \u5230\u5e95\u4e5f\u6709\u4e00\u4e9b\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u8fd9\u65f6\u4e0d\u7531\u5f97\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u6761\u8096\u9053\u7edd\u975e\u62e5\u6324\uff0c\u5c31\u53ea\u6709\u6211\u548c\u5e97\u5c0f\u4e8c\u4e24 \u4eba\uff0c\u8fd9\u4e2a\u5c0f\u53ae\u5c31\u7b97\u8d70\u8def\u4e0d\u5e26\u773c\u775b\uff0c\u4e5f\u4e0d\u8be5\u5c31\u78b0\u4e0a\u4e86\u6211\uff1f\u518d\u8bf4\uff0c\u6211\u662f\u4e2a\u7ec3\u8fc7\u6b66\u827a\u7684\u4eba\uff0c\u8033\u806a\u76ee \u660e\uff0c\u4eca\u756a\u600e\u7684\u7cca\u91cc\u7cca\u6d82\u7684\u5c31\u7ed9\u4ed6\u78b0\u4e0a\u4e86\uff0c\u771f\u662f\u5947\u602a\uff01\u201d \u3000\u3000\u4ed6\u56de\u60f3\u5f53\u65f6\u7684\u60c5\u666f\uff0c\u7a81\u7136\u53d1\u73b0\u90a3\u5c0f\u53ae\u649e\u5230\u8ddf\u524d\uff0c\u81ea\u5df1\u6b63\u8981\u95ea\u5f00\uff0c\u5374\u4ecd\u7136\u95ea\u4e0d\u8fc7\u4ed6\u8fd9\u4e00 \u649e\uff0c\u90a3\u5c0f\u53ae\u7684\u8eab\u6cd5\u7684\u786e\u6709\u70b9\u602a\u3002\u518d\u4e00\u56de\u60f3\uff0c\u90a3\u4e2a\u5c11\u5e74\u516c\u5b50\u5728\u5bf9\u4ed6\u7684\u5c0f\u53ae\u53d1\u8bdd\u7684\u65f6\u5019\uff0c\u4e24\u53ea\u773c \u775b\u5374\u671b\u7740\u81ea\u5df1\uff0c\u800c\u4ed6\u7684\u53cc\u773c\u4e5f\u662f\u7684\u7684\u6709\u795e\uff0c\u4ece\u4ed6\u773c\u795e\u770b\u6765\uff0c\u8fd9\u516c\u5b50\u4f3c\u4e4e\u4e5f\u662f\u7ec3\u8fc7\u6b66\u529f\u7684\u3002 \u3000\u3000\u5e97\u5c0f\u4e8c\u5f00\u4e86\u623f\u95e8\uff0c\u8bf7\u6c5f\u5357\u8fdb\u53bb\uff0c\u6c5f\u5357\u53d6\u51fa\u4e86\u4e00\u952d\u94f6\u5b50\uff0c\u8bf4\u9053\uff1b\u201c\u4f60\u968f\u4fbf\u7ed9\u6211\u5f04\u4e00\u4e24\u4e2a\u9152 \u83dc\uff0c\u591a\u4e86\u7684\u7ed9\u4f60\u3002\u201d\u8fd9\u952d\u94f6\u5b50\u8db3\u6709\u5341\u4e24\uff0c\u5e97\u5c0f\u4e8c\u7709\u5f00\u773c\u7b11\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u7ed9\u4f60\u8001\u5f04\u4e00 \u6837\u672c\u5e97\u6700\u62ff\u624b\u7684\u53eb\u5316\u9e21\uff0c\u4f60\u8001\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u6211\u98df\u91cf\u4e0d\u5927\uff0c\u6709\u4e00\u53ea\u53eb\u5316\u9e21\u5c3d\u591f\u4e86\u3002\u55ef\uff0c\u6211\u7d20\u6765\u6b22\u559c\u7ed3\u4ea4\u670b\u53cb\uff0c\u4f60\u53ef\u77e5\u9053\u90a3 \u516c\u5b50\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u90a3\u5c0f\u53ae\u79f0\u4ed6\u505a\u6587\u516c\u5b50\uff0c\u540d\u5b57\u4e48\u5374\u4e0d\u77e5\u9053\u3002\u770b\u6837\u5b50\u4ed6\u5bb6\u91cc\u5f88\u6709\u94b1\uff0c\u5927\u7ea6\u662f\u51fa \u6765\u6e38\u5b66\u7684\u3002\u54b1\u4eec\u5e97\u5b50\u91cc\u6709\u4e24\u4e2a\u6700\u597d\u7684\u623f\u95f4\uff0c\u4e00\u95f4\u5c31\u662f\u4f60\u8001\u8981\u7684\u8fd9\u4e2a\u623f\u95f4\uff0c\u53e6\u4e00\u95f4\u5c31\u662f\u4ed6\u4eec\u4e3b \u4ec6\u4e24\u4eba\u4f4f\u7684\u90a3\u4e2a\u4e1c\u53a2\u5957\u623f\u3002\u4f60\u731c\u4ed6\u662f\u600e\u4e48\u4ed8\u623f\u91d1\uff1f\u54c8\uff0c\u90a3\u624d\u771f\u662f\u53eb\u505a\u9614\u6c14\u5462\uff0c\u662f\u4e00\u9897\u91d1\u74dc \u5b50\uff01\u6700\u5c11\u4e5f\u503c\u5341\u4e24\u4ee5\u4e0a\u7684\u94f6\u5b50\u5462\uff01\u5bf9\u5566\uff0c\u4f60\u4eec\u4e24\u4f4d\u90fd\u662f\u9614\u6c14\u7684\u5c11\u7237\uff0c\u6b63\u8be5\u7ed3\u4ea4\u7ed3\u4ea4\uff0c\u6211\u7ed9\u4f60 \u4eec\u8bf4\u53bb\uff01\u201d \u3000\u3000\u8fd9\u5e97\u5c0f\u4e8c\u4e5f\u662f\u4e2a\u591a\u5634\u7684\u4eba\uff0c\u53ef\u662f\u4ed6\u9664\u4e86\u5938\u8d5e\u90a3\u6587\u516c\u5b50\u9614\u6c14\u4e4b\u5916\uff0c\u522b\u7684\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u6c5f\u5357 \u89c1\u6253\u542c\u4e0d\u5230\u4ec0\u4e48\u4e1c\u897f\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u4f60\u53bb\u8bf4\uff0c\u6211\u82e5\u662f\u8981\u548c\u4ed6\u8ba4\u8bc6\uff0c\u6211\u81ea\u5df1\u4f1a\u53bb\u62dc\u8bbf\u3002\u201d \u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u662f\uff0c\u662f\u3002\u4f60\u4eec\u662f\u540c\u7b49\u8eab\u4efd\uff0c\u4f60\u8001\u4e00\u6765\u5c31\u4eb2\u53bb\u62dc\u8bbf\uff0c\u90a3\u66f4\u663e\u5f97\u793c\u4eea\u5468\u5168\u3002\u201d\u5e97\u5c0f \u4e8c\u53d7\u4e86\u4ed6\u5341\u4e24\u94f6\u5b50\uff0c\u559c\u5f97\u7709\u5f00\u773c\u7b11\uff0c\u62cd\u4e86\u4e00\u987f\u9a6c\u5c41\uff0c\u624d\u53bb\u7ed9\u4ed6\u5907\u996d\u3002 \u3000\u3000\u6c5f\u5357\u5403\u8fc7\u4e86\u665a\u996d\uff0c\u60f3\u53bb\u62dc\u8bbf\u90a3\u6587\u516c\u5b50\uff0c\u8fdf\u7591\u4e86\u4e00\u9635\uff0c\u5fc3\u4e2d\u5374\u53c8\u60f3\u9053\uff1a\u201c\u6211\u81ea\u5df1\u6709\u4e8b\u5728 \u8eab\uff0c\u4f55\u5fc5\u591a\u627e\u4e9b\u95f2\u4e8b\u6765\u7406\uff0c\u4f55\u51b5\u8fd9\u6587\u516c\u5b50\u4e0e\u6211\u6c14\u5473\u53c8\u4e0d\u76f8\u6295\u3002\u201d\u4ed6\u72ec\u81ea\u4e00\u4eba\uff0c\u95f7\u5750\u65e0\u804a\uff0c\u8fc7 \u4e86\u4e00\u4f1a\uff0c\u4e0d\u77e5\u4e0d\u89c9\u7684\u53c8\u60f3\u8d77\u4e86\u90a3\u6587\u516c\u5b50\u4e3b\u4ec6\u4e8c\u4eba\u7684\u53ef\u7591\u4e4b\u70b9\uff0c\u7ec8\u5b50\u6291\u5236\u4e0d\u4f4f\u81ea\u5df1\u7684\u597d\u5947\u5fc3\uff0c \u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u53bb\u5077\u5077\u5f20\u671b\u4e00\u4e0b\uff0c\u603b\u4e0d\u788d\u4e8b\u5427\uff1f\u201d \u3000\u3000\u4e3b\u610f\u6253\u5b9a\uff0c\u8fc7\u4e86\u4e8c\u66f4\u65f6\u5206\uff0c\u6c5f\u5357\u6362\u4e86\u4e00\u8eab\u9ed1\u8272\u7684\u8863\u88f3\uff0c\u5077\u6109\u7684\u4ece\u7a97\u53e3\u51fa\u53bb\uff0c\u4ed6\u7684\u8f7b\u529f\uff0c \u867d\u7136\u8fd8\u4e0d\u7b97\u9876\u513f\u5c16\u513f\u7684\u89d2\u8272\uff0c\u4f46\u5728\u6c5f\u6e56\u4e0a\u4e5f\u662f\u5c11\u6709\u7684\u4e86\u3002\u4ed6\u4ece\u5c4b\u9876\u8fc7\u53bb\u3001\u8e0f\u7740\u74e6\u7247\uff0c\u74e6\u7247\u513f \u4e00\u70b9\u58f0\u54cd\u4e5f\u6ca1\u6709\uff0c\u6599\u60f3\u4e0d\u81f4\u88ab\u4eba\u53d1\u89c9\uff0c\u5927\u7740\u80c6\u513f\uff0c\u5230\u4e86\u6587\u516c\u5b50\u90a3\u95ee\u623f\u7684\u5c4b\u9876\uff0c\u4fbf\u7528\u4e00\u4e2a\u201c\u91d1 \u94a9\u5012\u5377\u5e18\u201d\u7684\u59ff\u52bf\uff0c\u53cc\u8db3\u52fe\u7740\u5c4b\u6a90\uff0c\u5077\u5077\u4ece\u540e\u7a97\u5f20\u671b\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u90a3\u6587\u516c\u5b50\u8bf4\u9053\uff1a\u201c\u5c0f\u4e09\u5b50\uff0c\u6211\u5fc3\u60ca\u8089\u8df3\uff0c\u53ea\u6015\u6709\u5c0f\u8d3c\u6765\u5077\u4e1c\u897f\uff0c\u4f60\u62ff\u90a3\u4e2a\u7bb1\u5b50\u7ed9 \u6211\u770b\u770b\uff0c\u770b\u4e1c\u897f\u8fd8\u5728\u4e0d\u5728\u91cc\u9762\uff1f\u201d\u90a3\u5c0f\u53ae\u9053\uff1a\u201c\u7bb1\u5b50\u8fd8\u5728\u6795\u5934\u5e95\u4e0b\uff0c\u516c\u5b50\uff0c\u4f60\u653e\u5fc3\u3002\u201d\u90a3\u6587 \u516c\u5b50\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u8981\u518d\u770b\u4e00\u770b\uff0c\u70b9\u4e00\u70b9\uff0c\u624d\u80fd\u5b89\u5fc3\u7761\u89c9\u3002\u201d \u3000\u3000\u90a3\u5c0f\u53ae\u5728\u6795\u5934\u5e95\u4e0b\u62ff\u51fa\u4e86\u4e00\u4e2a\u7ea2\u6f06\u6728\u7bb1\uff0c\u4e01\u65b9\u4e0d\u5230\u4e00\u5c3a\uff0c\u63d0\u5728\u624b\u4e2d\uff0c\u5374\u4f3c\u6c89\u7538\u7538\u7684\u3002\u90a3 \u6587\u516c\u5b50\u5c06\u7bb1\u5b50\u7f13\u7f13\u6253\u5f00\uff0c\u767b\u65f6\u5b9d\u6c14\u73e0\u5149\uff0c\u8000\u773c\u751f\u9897\uff0c\u628a\u4e00\u4e2a\u5728\u7a97\u5916\u5077\u770b\u7684\u6c5f\u5357\uff0c\u770b\u5f97\u819b\u76ee\u7ed3 \u820c\uff0c\u773c\u90fd\u82b1\u4e86\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u591a\u91d1\u5b63\u5b50\u8c01\u4eba\u8bc6\uff0c\u5374\u5411\u5c71\u4e1c\u9053\u4e0a\u6765\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff0c\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000--------------- \u3000\u3000\u626c\u5251\u8f69\u5c45\u58eb \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u56de\u3000\u795e\u5077\u5999\u624b\u77e5\u4f55\u5904\u3000\u5b9d\u6c14\u73e0\u5149\u52a8\u76d7\u5fc3 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e8c\u56de\u3000\u795e\u5077\u5999\u624b\u77e5\u4f55\u5904\u3000\u5b9d\u6c14\u73e0\u5149\u52a8\u76d7\u5fc3 \u3000\u3000\u53ea\u89c1\u90a3\u516c\u5b50\u5c06\u73e0\u5b9d\u4e00\u4ef6\u4ef6\u62ff\u51fa\u6765\u70b9\u6570\uff0c\u591c\u660e\u73e0\uff0c\u5b9d\u77f3\uff0c\u7fe1\u7fe0\u7b49\uff0c\u66f4\u96be\u5f97\u7684\u662f\u4e00\u67c4\u7eff\u7389\u5982 \u610f\uff0c\u901a\u4f53\u6676\u83b9\uff0c\u4e00\u770b\u5c31\u77e5\u662f\u4ef7\u503c\u8fde\u57ce\u7684\u5b9d\u8d1d\u3002 \u3000\u3000\u6c5f\u5357\u5012\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u5fc3\u9053\uff0c\u201c\u8fd9\u4e3b\u4ec6\u4e24\u4eba\u867d\u7136\u61c2\u5f97\u4e00\u70b9\u6b66\u529f\uff0c\u5374\u672a\u514d\u592a\u6ca1\u6709\u6c5f\u6e56\u7ecf\u9a8c \u4e86\uff01\u4fd7\u8bed\u8bf4\uff0c\u94b1\u8d22\u4e0d\u53ef\u9732\u773c\uff0c\u4f55\u51b5\u8fd9\u7b49\u7a00\u4e16\u5947\u73cd\uff1f\u201d\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4fbf\u60f3\u8fdb\u53bb\u529d\u544a\u4ed6\u3002 \u3000\u3000\u90a3\u5c0f\u53ae\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u53ef\u5f97\u591a\u52a0\u5c0f\u5fc3\u3002\u53ea\u6015\u8fd9\u5e97\u5b50\u91cc\u4fbf\u6709\u574f\u4eba\u3002\u201d\u6587\u516c\u5b50\u9053\uff1a\u201c\u4f60\u770b\u51fa \u53ef\u7591\u6765\u4e86\uff1f\uff0c\uff0c\u90a3\u5c0f\u53ae\u9053\uff1a\u201c\u4eca\u5929\u78b0\u4e86\u6211\u4e00\u4e0b\u7684\u90a3\u4e2a\u5ba2\u4eba\uff0c\u9b3c\u5934\u9b3c\u8111\u7684\uff0c\u4fbf\u50cf\u662f\u4e2a\u5c0f\u8d3c\u3002\u4f60 \u77a7\uff0c\u6211\u88ab\u4ed6\u78b0\u4e86\u4e00\u4e0b\uff0c\u51e0\u4e4e\u8dcc\u5012\uff0c\u5c82\u4e0d\u53ef\u7591\uff1f\u201d \u3000\u3000\u6587\u516c\u5b50\u9053\uff1a\u201c\u8c01\u8981\u4f60\u591a\u5634\uff0c\u6211\u81ea\u4f1a\u5c0f\u5fc3\uff01\u4f60\u8bf4\u7684\u90a3\u53ae\u867d\u7136\u50cf\u4e2a\u5c0f\u8d3c\uff0c\u4f46\u6211\u770b\u4ed6\u672c\u4e8b\u6709 \u9650\uff0c\u8981\u9632\u5907\u7684\u662f\u53e6\u4e00\u4e9b\u4eba\uff0c\u8fd9\u5c0f\u8d3c\u561b\uff0c\u5374\u4e0d\u5fc5\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u6c5f\u5357\u4e00\u542c\uff0c\u6c14\u5f80\u4e0a\u51b2\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u4e00\u756a\u597d\u610f\uff0c\u5012\u7ed9\u4f60\u4eec\u7591\u662f\u5c0f\u8d3c\uff0c\u771f\u662f\u5c82\u6709\u6b64\u7406\uff01\u597d \u5440\uff0c\u4f60\u7684\u4e1c\u897f\u5c31\u662f\u7ed9\u4eba\u5077\u5149\u4e86\uff0c\u4e5f\u4e0d\u7ba1\u6211\u7684\u4e8b\u3002\u53cd\u6b63\u4f60\u4eec\u6709\u94b1\uff0c\u6211\u5012\u5df4\u4e0d\u5f97\u4f60\u7ed9\u4eba\u5077\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u4e00\u6c14\u4e4b\u4e0b\uff0c\u7acb\u5373\u79bb\u5f00\uff0c\u5ffd\u542c\u5f97\u6709\u6781\u8f7b\u5fae\u7684\u6089\u7d22\u4e4b\u58f0\uff0c\u6c5f\u5357\u4e00\u542c\uff0c\u4fbf\u77e5\u662f\u6709\u8f7b\u529f\u6781\u9ad8 \u660e\u7684\u591c\u884c\u4eba\u57cb\u4f0f\u5728\u6697\u5904\u3002 \u3000\u3000\u6c5f\u5357\u867d\u7136\u51b3\u5b9a\u4e0d\u7ba1\uff0c\u4f46\u542c\u5230\u4e86\u8fd9\u4e2a\u58f0\u97f3\uff0c\u5374\u53c8\u66ff\u90a3\u6587\u516c\u5b50\u62c5\u5fe7\uff0c\u60f3\u9053\uff1a\u201c\u5177\u6709\u8fd9\u6837\u8f7b\u529f \u7684\u4eba\uff0c\u6b66\u529f\u4e5f\u5b9a\u7136\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u82e5\u7136\u53ea\u662f\u8981\u5077\u4e1c\u897f\uff0c\u6211\u53ef\u4ee5\u4e0d\u7ba1\uff1b\u4f46\u4ed6\u8bf4\u4e0d\u5b9a\u4f1a\u5200\u4f24\u4e8b\u4e3b\uff0c \u8fd9\u6211\u5c31\u4e0d\u80fd\u4e0d\u7ba1\u4e86\u3002\u4e0d\u5982\u53bb\u770b\u770b\u662f\u4ec0\u4e48\u4eba\uff0c\u8b66\u544a\u4ed6\u4e00\u58f0\uff0c\u529d\u4ed6\u53ea\u5077\u51e0\u9897\u73e0\u5b50\u4e5f\u5c31\u7b97\u4e86\u5427\u3002\u201d \u3000\u3000\u6c5f\u5357\u60f3\u5f97\u5929\u771f\uff0c\u4f46\u4ed6\u81ea\u5df1\u5374\u4ee5\u4e3a\u8fd9\u4e2a\u60f3\u6cd5\u5f88\u4e0d\u9519\uff0c\u4e3b\u610f\u6253\u5b9a\uff0c\u4fbf\u5faa\u58f0\u89c5\u8ff9\uff0c\u53bb\u627e\u90a3\u5728\u6697 \u4e2d\u57cb\u4f0f\u7684\u591c\u884c\u4eba\u3002 \u3000\u3000\u6726\u80e7\u7684\u6708\u5149\u4e0b\uff0c\u5ffd\u89c1\u6709\u4e24\u6761\u9ed1\u5f71\u7a9c\u4e86\u51fa\u6765\uff0c\u4e00\u770b\u5374\u662f\u4e24\u4e2a\u5149\u5934\uff0c\u6c5f\u5357\u6014\u4e86\u4e00\u6014\uff0c\u5b9a\u775b\u4e00 \u77a7\uff0c\u51e0\u4e4e\u60ca\u5f97\u5931\u58f0\u547c\u558a\uff01 \u3000\u3000\u90a3\u4e24\u4e2a\u548c\u5c1a\u89c1\u4e86\u6c5f\u5357\uff0c\u4e5f\u662f\u4e00\u6014\uff0c\u4ed6\u4eec\u7acb\u5373\u6447\u624b\u793a\u610f\uff0c\u53eb\u4ed6\u4e0d\u8981\u51fa\u58f0\uff0c\u968f\u5373\u4fbf\u8d70\u8fc7\u6765\u3002 \u3000\u3000\u4f60\u9053\u6c5f\u5357\u4f55\u4ee5\u5982\u6b64\u5403\u60ca\u3001\u539f\u6765\u8fd9\u4e24\u4e2a\u548c\u5c1a\u975e\u6bd4\u5bfb\u5e38\uff0c\u7adf\u662f\u5c11\u6797\u5bfa\u4e2d\u7684\u5927\u96c4\u3001\u5927\u60b2\u4e24\u4f4d\u63b8 \u5e08\uff0c\u8fd9\u4e24\u4f4d\u7985\u5e08\u540d\u5217\u5c11\u6797\u5bfa\u5341\u516b\u7f57\u6c49\u4e4b\u4e2d\uff0c\u6b66\u529f\u9ad8\u5f3a\uff0c\u90a3\u662f\u4e0d\u6d88\u8bf4\u4e86\uff0c\u4ed6\u4eec\u7684\u6212\u5f8b\u7cbe\u4e25\uff0c\u8a00 \u884c\u4e0d\u82df\uff0c\u4e5f\u662f\u51fa\u5bb6\u4eba\u6240\u94a6\u4f69\u7684\u3002\u8981\u4e0d\u7136\u4ed6\u4eec\u600e\u80fd\u53f7\u79f0\u201c\u7f57\u6c49\u201d\u3002\u6c5f\u5357\u8ba4\u51fa\u4ed6\u4eec\uff0c\u8fd9\u4efd\u60ca\u5947\u771f \u662f\u96be\u4ee5\u5f62\u5bb9\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u96be\u9053\u8fd9\u4e24\u4f4d\u9ad8\u50e7\uff0c\u7adf\u4e5f\u4f1a\u6765\u4f5c\u8d3c\uff1f\u201d \u3000\u3000\u5927\u96c4\u7985\u5e08\u6253\u4e86\u4e00\u4e2a\u624b\u52bf\uff0c\u6c5f\u5357\u6ee1\u8179\u7591\u56e2\uff0c\u5374\u4e0d\u80fd\u5f20\u5634\u8bf4\u8bdd\uff0c\u95f7\u5f97\u96be\u53d7\u3002 \u3000\u3000\u5927\u60b2\u7985\u5e08\u628a\u624b\u4e00\u62db\uff0c\u9662\u5b50\u91cc\u90a3\u682a\u68a7\u6850\u6811\u4e0a\uff0c\u5ffd\u5730\u53c8\u8df3\u4e0b\u4e00\u4e2a\u4eba\u6765\uff0c\u8fd9\u4eba\u7684\u8f7b\u529f\u751a\u662f\u9ad8 \u660e\uff0c\u4e25\u5982\u4e00\u53f6\u5760\u5730\uff0c\u843d\u5730\u65e0\u58f0\u3002\u6c5f\u5357\u4e00\u89c1\uff0c\u66f4\u4e3a\u60ca\u8be7\u3002 \u3000\u3000\u8fd9\u4eba\u4e0e\u6c5f\u5357\u4e0a\u4e0b\u5e74\u7eaa\uff0c\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u8427\u9752\u5cf0\u7684\u5927\u5f1f\u5b50\u5d14\u4e91\u4eae\u3002\u8427\u9752\u5cf0\u4ee5\u524d\u66fe\u5728\u9648\u5bb6\u6559 \u4e66\uff0c\u6c5f\u5357\u6700\u521d\u5b66\u6b66\uff0c\u5c31\u662f\u5f53\u8427\u9752\u5cf0\u6559\u9648\u5929\u5b87\u7684\u65f6\u5019\uff0c\u4ed6\u5728\u65c1\u8fb9\u89c2\u770b\uff0c\u5077\u5077\u5b66\u7684\uff0c\u6545\u6b64\u867d\u65e0\u5e08 \u5f92\u4e4b\u540d\uff0c\u5374\u6709\u5e08\u5f92\u4e4b\u5b9e\u3002\u8427\u9752\u5cf0\u8fd9\u4e00\u5e74\u6765\u9690\u5c45\u9752\u57ce\u5c71\u6388\u5f92\uff0c\u6c5f\u5357\u4e5f\u66fe\u53bb\u63a2\u671b\u8fc7\u4ed6\u51e0\u6b21\uff0c\u8427\u9752 \u5cf0\u7684\u5f92\u5f1f\uff0c\u4ed6\u90fd\u76f8\u719f\uff0c\u5c24\u5176\u4e0e\u5d14\u4e91\u4eae\u4ea4\u60c5\u66f4\u597d\uff0c\u5f7c\u6b64\u4e00\u5411\u4ee5\u5144\u5f1f\u76f8\u79f0\u3002 \u3000\u3000\u5d14\u4e91\u4eae\u8f7b\u8f7b\u62cd\u4e86\u6c5f\u5357\u4e00\u4e0b\uff0c\u7528\u624b\u4e00\u6307\uff0c\u6c5f\u5357\u4e00\u770b\uff0c\u4ed6\u624b\u6307\u65b9\u5411\u6b63\u662f\u81ea\u5df1\u6240\u4f4f\u7684\u90a3\u95f4\u623f \u95f4\uff0c\u6c5f\u5357\u767b\u65f6\u4f1a\u610f\uff0c\u548c\u5d14\u4e91\u4eae\u518d\u4e0a\u5c4b\u9876\uff0c\u4f46\u89c1\u8fdc\u5904\u9ed1\u5f71\u7ef0\u7ef0\u7684\uff0c\u4e00\u65f6\u95f4\u4e5f\u5206\u522b\u4e0d\u51fa\u6709\u51e0\u4e2a \u4eba\uff0c\u4f46\u4ee5\u6c5f\u5357\u7684\u6b66\u5b66\u9020\u8be3\uff0c\u5374\u5df2\u77e5\u9053\u4eca\u665a\u6765\u7684\u5c3d\u662f\u6b66\u6797\u9ad8\u624b\uff01 \u3000\u3000\u6c5f\u5357\u5e26\u5d14\u4e91\u4eae\u56de\u5230\u81ea\u5df1\u7684\u623f\u95f4\uff0c\u5173\u4e86\u7a97\u6237\uff0c\u7b11\u9053\uff1a\u201c\u73b0\u5728\u53ef\u4ee5\u8bf4\u8bdd\u4e86\u5427\uff1f\u5d14\u8001\u5f1f\uff0c\u8fd9\u662f \u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5d14\u4e91\u4eae\u5728\u4ed6\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u5c0f\u58f0\u70b9\u513f\uff0c\u4f60\u522b\u5fd9\u7740\u95ee\u6211\uff0c\u201d\u6211\u5148\u95ee\u4f60\uff0c\u4f60\u7aa5\u63a2\u4e86\u90a3\u4e2a\u59d3\u6587\u7684 \u623f\u95f4\uff0c\u770b\u89c1\u4ec0\u4e48\u6765\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u770b\u89c1\u4ed6\u6709\u6ee1\u7bb1\u73e0\u5b9d\uff0c\u6211\u773c\u90fd\u82b1\u4e86\uff0c\u53ea\u662f\u591c\u660e\u73e0\uff0c\u5c31\u6709\u51e0\u5341\u9897\uff01\u8fd8\u6709\u73ca\u745a\u6811\u548c \u7389\u5982\u610f\uff0c\u6211\u867d\u7136\u4e0d\u61c2\u73e0\u5b9d\uff0c\u4f46\u4f9d\u6211\u770b\u6765\uff0c\u62ff\u897f\u85cf\u571f\u738b\u7684\u8d21\u54c1\u4e0e\u5b83\u76f8\u6bd4\uff0c\u571f\u738b\u7684\u8d21\u54c1\u53ea\u80fd\u7b97\u662f \u4e00\u5806\u5783\u573e\uff01\u201d\u9648\u5929\u5b87\u7684\u7236\u4eb2\u9648\u5b9a\u57fa\u66fe\u505a\u8fc7\u8428\u8fe6\u5ba3\u6170\u4f7f\uff0c\u6240\u5c5e\u571f\u53f8\u548c\u85e9\u738b\u7684\u8d21\u54c1\u90fd\u7531\u4ed6\u63a5\u9001\u4e0a \u4eac\uff0c\u6545\u6b64\u6c5f\u5357\u4e5f\u66fe\u89c1\u8fc7\u90a3\u4e9b\u8d21\u54c1\u3002 \u3000\u3000\u5d14\u4e91\u4eae\u77e5\u9053\u6c5f\u5357\u559c\u6b22\u5439\u725b\uff0c\u4f46\u5373\u7b97\u62ff\u4ed6\u7684\u8bdd\u6253\u4e2a\u6298\u6263\uff0c\u90a3\u7bb1\u73e0\u5b9d\u4ea6\u5df2\u4ef7\u503c\u4e0d\u83f2\u3002\u8138\u4e0a\u73b0 \u51fa\u7b11\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5927\u7ea6\u6211\u4e0d\u4f1a\u865a\u6b64\u4e00\u884c\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u5357\u60ca\u5947\u4e4b\u6781\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u548c\u90a3\u4e24\u4f4d\u7985\u5e08\uff0c\u5f53\u771f\u662f\u4e3a\u4e86\u8fd9\u59d3\u6587\u7684\u73e0\u5b9d\u6765\u7684 \u4e48\uff1f\u4e5f\u597d\uff0c\u82e5\u662f\u4f60\u4eec\uff0c\u6211\u53ef\u4ee5\u653e\u5fc3\u4e86\u3002\u5c31\u4e0d\u77e5\u53e6\u5916\u7684\u90a3\u4e9b\u4eba\u542c\u4e0d\u542c\u4f60\u4eec\u7684\u8bdd\uff1f\u201d \u3000\u3000\u5d14\u4e91\u4eae\u542c\u4e86\u4ed6\u8fd9\u987f\u6ca1\u5934\u6ca1\u8111\u7684\u8bf4\u8bdd\uff0c\u6115\u4e86\u4e00\u4e0b\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u653e\u5fc3\u4e0d\u653e\u5fc3\u7684\uff1f\u6211\u53ef\u4e0d\u660e \u767d\u4f60\u7684\u8bdd\uff01\u201d\u6c5f\u5357\u9053\uff0c\u201c\u6211\u60f3\u4f60\u4eec\u6700\u591a\u662f\u8981\u5077\u4ed6\u7684\u73e0\u5b9d\uff0c\u51b3\u4e0d\u4f1a\u4f24\u4eba\uff0c\u662f\u4e48\uff1f\u201d\u5d14\u4e91\u4eae\u5f2f\u4e0b \u8170\u6765\uff0c\u63c9\u7740\u809a\u5b50\uff0c\u6781\u529b\u5fcd\u7740\uff0c\u8fd9\u624d\u6ca1\u6709\u7b11\u51fa\u58f0\u6765\u3002 \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u600e\u4e48\uff1f\u6211\u8bf4\u9519\u4e86\u4e48\uff1f\u4f60\u4e3a\u4f55\u5982\u6b64\u597d\u7b11\uff1f\u201d\u5d14\u4e91\u4eae\u7ad9\u76f4\u8eab\u5b50\uff0c\u6b47\u4e86\u4e00\u4f1a\uff0c\u7f13\u8fc7 \u6c14\u6765\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u7f6a\u8fc7\uff0c\u7f6a\u8fc7\uff01\u6c5f\u5927\u54e5\uff0c\u4f60\u6000\u7591\u5c0f\u5f1f\u4f5c\u8d3c\uff0c\u4e5f\u8fd8\u7f62\u4e86\uff0c\u600e\u7684\u4f1a\u7591\u5fc3\u5230\u5c11\u6797 \u5bfa\u90a3\u4e24\u4f4d\u9ad8\u50e7\uff0c\u4e5f\u662f\u8d2a\u56fe\u73e0\u5b9d\u7684\u8d3c\u4eba\uff1f\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u662f\u5440\uff0c\u6240\u4ee5\u6211\u624d\u89c9\u5f97\u5947\u602a\uff0c\u4f9d\u4f60\u4e4b\u8bf4\uff0c\u82e5\u7136\u4ed6\u4eec\u4e0d\u662f\u4e3a\u4e86\u73e0\u5b9d\uff0c\u5374\u5230\u8fd9\u5c0f\u5e97 \u6765\u4f5c\u4ec0\u4e48\uff0c\u8fd8\u6709\u90a3\u4e9b\u591c\u884c\u4eba\u5462\uff0c\u4ed6\u4eec\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\u6765\u7684\uff1f\u201d \u3000\u3000\u5d14\u4e91\u4eae\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u4f60\u662f\u806a\u660e\u4e00\u4e16\u5374\u7cca\u6d82\u4e00\u65f6\u4e86\uff0c\u5373\u7b97\u6211\u4eec\u8981\u62a2\u90a3\u5c11\u5e74\u7684\u73e0\u5b9d\uff0c\u7528\u5f97 \u4e86\u8fd9\u8bb8\u591a\u4eba\u5417\uff1f\u66f4\u4f55\u987b\u60ca\u52a8\u5c11\u6797\u5bfa\u7684\u9ad8\u50e7\u5462\uff1f\u201d \u3000\u3000\u6c5f\u5357\u8d4c\u6c14\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4f60\u809a\u5b50\u91cc\u7684\u86d4\u866b\uff0c\u600e\u77e5\u4f60\u8981\u5e72\u4ec0\u4e48\u52fe\u5f53\uff1f\u597d\u5566\uff0c\u4f60\u65e2\u7136\u7ed9\u6211\u8fd9\u4e2a \u95f7\u846b\u82a6\uff0c\u53ea\u6709\u8bf7\u4f60\u4e3a\u6211\u5256\u5f00\u4e86\u3002\u201d \u3000\u3000\u5d14\u4e91\u4eae\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u662f\u5916\u4eba\uff0c\u6211\u90fd\u544a\u8bc9\u4f60\u5427\uff0c\u7b49\u4e0b\u8fd8\u8981\u8bf7\u4f60\u5e2e\u5fd9\uff0c\u4f60\u53ef\u8bb0\u5f97\u76c2\u795e\u901a \u90a3\u4e2a\u5f1f\u5b50\u59ec\u6653\u98ce\uff1f\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u5929\u4e0b\u7b2c\u4e00\u795e\u5077\u59ec\u6653\u98ce\uff0c\u54c8\uff0c\u8fd9\u4e2a\u4eba\u6211\u600e\u4f1a\u4e0d\u8bb0\u5f97\uff1f\u4ed6\u7684\u5e08\u7236\u5b5f\u795e\u901a\u5728\u751f\u4e4b\u65f6 \u4eba\u4eba\u618e\u6068\uff0c\u53ef\u662f\u8fd9\u4e2a\u59ec\u6653\u98ce\u5374\u4f3c\u8fd8\u6709\u51e0\u5206\u53ef\u53d6\u3002\u201d \u3000\u3000\u5d14\u4e91\u4eae\u9053\uff1a\u201c\u5478\uff0c\u6709\u4ec0\u4e48\u53ef\u53d6\uff1f\u60f3\u4e0d\u5230\u4f60\u5bf9\u4ed6\u5012\u6709\u597d\u611f\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4ed6\u5077\u70b9\u4e1c\u897f\uff0c\u65e0 \u4f24\u5927\u96c5\uff0c\u5374\u7ed9\u4eba\u4eec\u589e\u6dfb\u4e86\u4e0d\u5c11\u8336\u4f59\u996d\u540e\u7684\u8c08\u8d44\uff0c\u8fd9\u4e0d\u4e5f\u5f88\u6709\u8da3\u4e48\uff1a\u4f55\u51b5\u4ed6\u53c8\u6ca1\u6709\u5077\u5230\u4f60\u7684\u5934 \u4e0a\uff0c\u4f60\u8fd9\u6837\u6068\u4ed6\u4f5c\u751a\uff1f\u201d \u3000\u3000\u5d14\u4e91\u4eae\u9053\uff1a\u201c\u82e5\u662f\u5077\u5230\u6211\u7684\u5934\u4e0a\u5462\uff1f\u4f60\u5e2e\u4e0d\u5e2e\u6211\uff1f\u201d\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u6211\u5f53\u7136\u5e2e\u4f60\u3002\u53ef\u662f\u4f60 \u6709\u4ec0\u4e48\u4e1c\u897f\u503c\u5f97\u59ec\u6653\u98ce\u6765\u5077\uff1f\u597d\u5566\uff0c\u95f2\u8bdd\u522b\u626f\u5f97\u592a\u8fdc\u5566\uff0c\u59ec\u6653\u98ce\u4e0e\u4f60\u4eec\u4eca\u665a\u7684\u884c\u52a8\u53c8\u6709\u4ec0\u4e48 \u5173\u7cfb\u5462\uff1f\u201d \u3000\u3000\u5d14\u4e91\u4eae\u9053\uff1a\u201c\u59ec\u6653\u98ce\u66fe\u5077\u4e86\u5c11\u6797\u5bfa\u7684\u4e09\u5377\u6b66\u5b66\u79d8\u7c4d\uff0c\u4f60\u53ef\u77e5\u9053\uff1f\u201d\u6c5f\u5357\u7b11\u9053\uff0c\u201c\u6211\u5f53\u7136 \u77e5\u9053\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u662f\u53d1\u751f\u5728\u5b5f\u795e\u901a\u4e0e\u5510\u5927\u4fa0\u5343\u5d82\u576a\u4e4b\u4f1a\u4e4b\u540e\uff0c\u5343\u5d82\u576a\u4e4b\u4f1a\uff0c\u6211\u4e5f\u6709\u53c2\u52a0\uff0c\u90a3\u65f6 \u4f60\u8fd8\u672a\u66fe\u51fa\u9053\u5462\u3002\u201d \u3000\u3000\u5d14\u4e91\u4eae\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u51e0\u5e74\u6765\u4f60\u5728\u5bb6\u91cc\u62b1\u513f\u5b50\u7eb3\u798f\uff0c\u5916\u9762\u7684\u4e8b\u60c5\u53ea\u6015\u5c31\u4e0d\u5927\u77e5\u9053\u4e86\u3002\u201d\u6c5f \u5357\u542c\u4ed6\u63d0\u8d77\u81ea\u5df1\u7684\u513f\u5b50\uff0c\u4e0d\u7531\u5f97\u4e00\u9635\u795e\u4f24\u3002\u4f46\u5d14\u4e91\u4eae\u6b63\u5728\u8bf4\u5230\u9898\u76ee\uff0c\u6c5f\u5357\u4e0d\u60f3\u6253\u65ad\u4ed6\u7684\u8bdd \u67c4\uff0c\u53ea\u597d\u5148\u628a\u81ea\u5df1\u7684\u4e8b\u60c5\u6401\u8d77\u6765\u3002 \u3000\u3000\u5d14\u4e91\u4eae\u9053\uff1a\u201c\u8fd9\u51e0\u5e74\u6765\u5c11\u6797\u5bfa\u5230\u5904\u6d3e\u4eba\u53bb\u641c\u67e5\u4ed6\u7684\u8e2a\u8ff9\uff0c\u5404\u5927\u95e8\u6d3e\u4e5f\u90fd\u7559\u610f\u4ed6\u7684\u6d88\u606f\uff0c \u53ef\u662f\u603b\u6ca1\u6cd5\u5b50\u6349\u5230\u4ed6\u3002\u8fd9\u4e5f\u8fd8\u7f62\u4e86\uff0c\u4e0d\u6599\u90a3\u59ec\u6653\u98ce\u5728\u5c11\u6797\u5bfa\u5f97\u624b\u4e4b\u540e\uff0c\u5077\u75db\u5927\u53d1\uff0c\u4f60\u4e0d\u627e \u4ed6\uff0c\u4ed6\u53cd\u6765\u627e\u4f60\uff01\u6700\u8fd1\u8fd9\u4e24\u4e09\u5e74\uff0c\u5404\u5927\u95e8\u6d3e\u51e0\u4e4e\u90fd\u66fe\u53d7\u5230\u4ed6\u7684\u5149\u987e\uff01\u201d \u3000\u3000\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u9752\u57ce\u6d3e\u4e5f\u53d7\u5230\u5149\u987e\u4e86\uff1f\u201d\u5d14\u4e91\u4eae\u9053\uff1a\u201c\u6b63\u662f\u90a3\u53ef\u6068\u7684\u59ec\u6653\u98ce\uff0c\u4ed6\u628a\u6211 \u4eec\u8f9b\u638c\u95e8\u4e00\u672c\u65b0\u8457\u7684\u5251\u8c31\u5077\u53bb\u4e86\u3002\u201d \u3000\u3000\u9752\u57ce\u6d3e\u53f7\u79f0\u4e2d\u539f\u56db\u5927\u5251\u6d3e\u4e4b\u4e00\uff0c\u73b0\u4efb\u638c\u95e8\u8f9b\u9690\u519c\u66f4\u662f\u4e2a\u6770\u51fa\u7684\u4eba\u6750\uff0c\u4ed6\u5c06\u672c\u6d3e\u5251\u8c31\u91cd\u65b0 \u6574\u7406\uff0c\u52a0\u4e0a\u81ea\u5df1\u7684\u5fc3\u5f97\uff0c\u5199\u6210\u4e86\u9752\u57ce\u5251\u6cd5\u4e00\u5341\u516b\u7bc7\uff0c\u60f3\u4e0d\u5230\u5728\u65b0\u8457\u6740\u9752\u4e4b\u65e5\uff0c\u5c31\u7ed9\u59ec\u6653\u98ce\u5077 \u53bb\uff0c\u59ec\u6653\u98ce\u8fd8\u7559\u4e0b\u201c\u501f\u5e16\u201d\uff0c\u516c\u7136\u7b7e\u4e0a\u4e86\u201c\u501f\u4e66\u4eba\u59ec\u6653\u98ce\u201d\u516d\u4e2a\u5927\u5b57\uff0c\u628a\u8f9b\u9690\u519c\u6c14\u5f97\u51e0\u4e4e\u7834 \u4e86\u809a\u76ae\uff0c\u56e0\u6b64\u6d3e\u51fa\u95e8\u4eba\uff0c\u534f\u540c\u5c11\u6797\u6d3e\u5230\u5904\u641c\u67e5\u59ec\u6653\u98ce\u7684\u8e2a\u8ff9\u3002 \u3000\u3000\u5d14\u4e91\u4eae\u53c8\u9053\uff1a\u201c\u8fd8\u6709\u534e\u5c71\u6d3e\u7684\u4e00\u672c\u4e94\u884c\u62f3\u62f3\u7ecf\uff0c\u5ce8\u5d4b\u6d3e\u7684\u4e00\u672c\u7ec3\u529f\u79d8\u7b56\uff0c\u5025\u4f97\u6d3e\u7684\u4e00\u672c \u5947\u95e8\u70b9\u7a74\u8bc0\uff0c\u90fd\u662f\u7ed9\u59ec\u6653\u98ce\u5077\u53bb\u7684\uff0c\u5176\u4ed6\u4e00\u4e9b\u4e0d\u5927\u91cd\u8981\u7684\u8fd8\u672a\u8ba1\u7b97\u5728\u5185\u3002\u56e0\u6b64\u73b0\u5728\u5404\u5927\u95e8\u6d3e \u90fd\u8054\u5408\u8d77\u6765\uff0c\u8981\u6349\u62ff\u8fd9\u4e2a\u80c6\u5927\u5984\u4e3a\u7684\u5077\u4e66\u8d3c\u3002\u201d \u3000\u3000\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u59ec\u6653\u98ce\u771f\u6709\u610f\u601d\uff0c\u636e\u6211\u6240\u77e5\uff0c\u7687\u5bab\u5927\u5185\u7684\u5b9d\u7269\u4ed6\u4e5f\u5077\u8fc7\u4e86\uff0c\u54c8\uff0c\u5982\u4eca \u4ed6\u7adf\u4ece\u7687\u5bab\u5927\u5185\u5077\u5230\u4e86\u5c11\u6797\u5bfa\u3001\u9752\u57ce\u5c71\u7b49\u5404\u5927\u95e8\u6d3e\u6765\uff0c\u4e0d\u6015\u7687\u5e1d\u8001\u5b50\uff0c\u4e5f\u4e0d\u6015\u5404\u6d3e\u7684\u6b66\u5b66\u5927 \u5e08\uff0c\u771f\u662f\u4e2a\u53e4\u5f80\u4eca\u6765\uff0c\u7edd\u65e0\u4ec5\u6709\u7684\u5999\u624b\u795e\u5077\u5440\uff01\u201d\u5d14\u4e91\u4eae\u6012\u9053\uff1a\u201c\u59ec\u6653\u98ce\u5df2\u60f9\u8d77\u4e86\u5404\u6d3e\u7684\u516c \u6124\uff0c\u504f\u504f\u4f60\u8fd8\u8d5e\u4ed6\uff01\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u6211\u4e0d\u662f\u504f\u8892\u4ed6\uff0c\u53ea\u662f\u6211\u89c9\u5f97\u4ed6\u8fd9\u4e2a\u8d3c\u4e0e\u4f17\u4e0d\u540c\uff0c\u5077\u4e1c\u897f\u4e5f\u5f88\u6709\u773c\u5149\u7f62\u4e86\u3002\u800c \u4e14\u4ed6\u7684\u6d88\u606f\u4e5f\u771f\u7075\u901a\uff0c\u6bd4\u5982\u6211\u5427\uff0c\u6211\u548c\u4f60\u4eec\u4ea4\u60c5\u8fd9\u4e48\u597d\uff0c\u6211\u5c31\u4e0d\u77e5\u9053\u4f60\u4eec\u7684\u8f9b\u638c\u95e8\u65b0\u8457\u4e86\u4e00 \u672c\u5251\u8c31\u3002\u201d\u5d14\u4e91\u4eae\u9053\uff1a\u201c\u8fd9\u8fd8\u4e0d\u662f\u8d5e\u4ed6\uff1f\u542c\u4f60\u8bf4\uff0c\u7adf\u662f\u8d8a\u6765\u8d8a\u4f69\u670d\u4ed6\u4e86\uff01\u201d \u3000\u3000\u6760\u5357\u7b11\u9053\uff1a\u201c\u4f69\u4e0d\u4f69\u670d\u662f\u53e6\u4e00\u56de\u4e8b\uff0c\u8981\u662f\u6211\u78b0\u4e0a\u4e86\u59ec\u6653\u98ce\uff0c\u6211\u8fd8\u662f\u8981\u5e2e\u4f60\u6349\u62ff\u4ed6\u7684\uff0c\u4e0d \u8fc7\u8bdd\u8bf4\u56de\u6765\uff0c\u4ed6\u5077\u4e00\u4e9b\u62f3\u7ecf\u5251\u8c31\uff0c\u5012\u8fd8\u7b97\u5f97\u662f\u4e2a\u8bc6\u8d27\u7684\u98ce\u96c5\u8d3c\uff0c\u5e76\u975e\u5341\u6076\u4e0d\u8d66\uff0c\u4e0e\u4ed6\u7684\u5e08\u7236 \u5b5f\u795e\u901a\u4e0d\u80fd\u540c\u4e00\u800c\u8bba\u3002\u6240\u4ee5\u6211\u8fd8\u662f\u5e0c\u671b\u4f60\u4eec\u53ea\u8981\u8ffd\u56de\u539f\u7269\u5c31\u7b97\uff0c\u4e0d\u53ef\u4f24\u4ed6\u6027\u547d\u3002\u201d \u3000\u3000\u5d14\u4e91\u4eae\u9053\uff1a\u201c\u8fd9\u4e2a\u4e0d\u7528\u4f60\u6765\u7ed9\u6211\u4eec\u51fa\u4e3b\u610f\uff0c\u6211\u4eec\u5404\u6d3e\u5df2\u7ecf\u5546\u8bae\u597d\u4e86\uff0c\u8981\u662f\u62ff\u5230\u4e86\u59ec\u6653 \u98ce\uff0c\u5c31\u628a\u4ed6\u56da\u7981\u5728\u5025\u4f97\u5c71\u7684\u9634\u98ce\u6d1e\u91cc\uff1b\u4e00\u4e16\u4e0d\u653e\u4ed6\u51fa\u6765\u3002\u201d \u3000\u3000\u6c5f\u5357\u4f38\u4f38\u820c\u5934\u9053\uff1a\u201c\u8fd9\u53ef\u6bd4\u6740\u4e86\u4ed6\u8fd8\u60e8\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u65e2\u7136\u662f\u4f60\u4eec\u516c\u8bae\u7684\uff0c\u6211\u4e5f\u5c31\u4e0d\u597d\u518d\u8bf4 \u4ec0\u4e48\u4e86\u3002\u5582\uff0c\u5582\uff0c\u54b1\u4eec\u8bf4\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u5bf9\u5566\uff0c\u5bf9\u5566\uff0c\u6211\u8981\u95ee\u4f60\u7684\u662f\uff0c\u59ec\u6653\u98ce\u548c\u4f60\u4eec\u4eca\u591c\u7684\u884c \u52a8\u6709\u4ec0\u4e48\u5173\u8054\uff1f\u96be\u9053\u90a3\u4e2a\u6587\u516c\u5b50\u662f\u59ec\u6653\u98ce\u7684\u540c\u515a\u4e48\uff1f\u201d \u3000\u3000\u5d14\u4e91\u4eae\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u806a\u660e\uff0c\u600e\u7684\u8fde\u8fd9\u4e00\u70b9\u4e5f\u731c\u60f3\u4e0d\u5230\u3002\u90a3\u59d3\u6587\u7684\u867d\u7136\u7a79\u59ec\u6653\u98ce\u65e0\u5173\uff0c\u6211 \u4eec\u5374\u8981\u4ece\u8fd9\u59d3\u6587\u7684\u8eab\u4e0a\u8ffd\u67e5\u51fa\u59ec\u6653\u98ce\u6765\uff01\u201d \u3000\u3000\u6c5f\u5357\u8be7\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u8bb2\uff0c\u65e2\u4e0e\u59ec\u6653\u98ce\u65e0\u5173\uff0c\u53c8\u600e\u4e48\u4ece\u4ed6\u8eab\u4e0a\u8ffd\u67e5\uff1f\u54ce\u5440\u5440\uff0c\u4f60\u53ef\u522b\u8d5e\u4f60 \u8fd9\u4e2a\u54e5\u54e5\u806a\u660e\uff0c\u4f60\u8d8a\u8bf4\u5440\uff0c\u6211\u53ef\u5c31\u8d8a\u7cca\u6d82\u4e86\u3002\u201d \u3000\u3000\u5d14\u4e91\u4eae\u9053\uff1a\u201c\u4f60\u662f\u88c5\u50bb\u8fd8\u662f\u771f\u7684\u731c\u60f3\u4e0d\u5230\uff1f\u597d\u5566\uff0c\u6211\u5c31\u5bf9\u4f60\u660e\u660e\u767d\u767d\u7684\u8bf4\u4e86\u5427\u3002\u90a3\u59d3\u6587 \u7684\u6709\u4e00\u7bb1\u73e0\u5b9d\uff0c\u6211\u4eec\u5c31\u8981\u4ece\u8fd9\u7bb1\u73e0\u5b9d\u4e0a\u5f15\u51fa\u59ec\u6653\u98ce\u6765\u3002\u201d \u3000\u3000\u6c5f\u5357\u4e00\u638c\u62cd\u4e0b\uff0c\u53eb\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff01\u201d\u5d14\u4e91\u4eae\u6025\u5fd9\u62c9\u7740\u4ed6\u7684\u624b\uff0c\u63a9\u7740\u4ed6\u7684\u5634\uff0c\u9053\uff1a\u201c\u4f60 \u80e1\u56b7\u4ec0\u4e48\uff0c\u63d0\u9632\u59ec\u6653\u98ce\u542c\u89c1\u4e86\uff0c\u4e0a\u4e86\u94a9\u7684\u9c7c\u513f\u53c8\u8981\u6e38\u8d70\u3002\u201d \u3000\u3000\u6c5f\u5357\u5c0f\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8981\u6349\u8d3c\u5374\u53c8\u6015\u7ed9\u8d3c\u4eba\u77e5\u9053\uff0c\u9b3c\u9b3c\u795f\u795f\u7684\u81ea\u5df1\u5012\u50cf\u4e2a\u8d3c\u4e86\u3002\u201d\u5d14\u4e91 \u4eae\u9053\uff1a\u201c\u4f60\u53c8\u4e0d\u662f\u4e0d\u77e5\u9053\u59ec\u6653\u98ce\u8fd9\u53ae\u6765\u53bb\u65e0\u8e2a\uff0c\u4e0d\u662f\u5e03\u4e0b\u5708\u5957\uff0c\u7109\u80fd\u4ee4\u4ed6\u843d\u7f51\uff1f\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u90a3\u59d3\u6587\u7684\u662f\u4f60\u4eec\u7684\u4eba\u5417\uff1f\u201d\u5d14\u4e91\u4eae\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u6211\u4eec\u54ea\u91cc\u6765\u7684\u90a3\u4e9b\u73e0\u5b9d\uff1f\u4e0d \u8fc7\uff0c\u636e\u6211\u4eec\u4f30\u8ba1\uff0c\u90a3\u59d3\u6587\u7684\u8eab\u6000\u91cd\u5b9d\uff0c\u4e1a\u5df2\u9732\u51fa\u98ce\u58f0\uff0c\u59ec\u6653\u98ce\u4e00\u5b9a\u4f1a\u89c1\u730e\u5fc3\u559c\uff0c\u8fdf\u65e9\u90fd\u8981\u4e0b \u624b\u5077\u5b83\u3002\u6211\u4eec\u8ddf\u5b9a\u4e86\u90a3\u59d3\u6587\u7684\uff0c\u53ea\u5f85\u59ec\u6653\u98ce\u51fa\u73b0\uff01\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5982\u6b64\u3002\u602a\u4e0d\u5f97\u5c11\u6797\u5bfa\u7684\u4e24\u4e2a\u9ad8\u618e\uff0c\u4e5f\u5230\u8fd9\u5c0f\u5ba2\u5e97\u91cc\u6765\u6253\u57cb\u4f0f\u4e86\u3002\u4f60 \u4eec\u662f\u8981\u501f\u8fd9\u59d3\u6587\u7684\u73e0\u5b9d\u5f53\u4f5c\u9493\u9c7c\u7684\u9975\uff0c\u5f15\u59ec\u6653\u98ce\u8fd9\u5c3e\u5927\u9c7c\u4e0a\u94a9\u3002\u4f46\u662f\uff0c\u8fd9\u59d3\u6587\u7684\u662f\u4ec0\u4e48\u4eba\uff0c \u4f60\u4eec\u53ef\u67e5\u5f97\u6e05\u695a\uff1f\u4ed6\u77e5\u4e0d\u77e5\u9053\u4f60\u4eec\u7684\u8ba1\u5212\uff1f\u518d\u8005\uff0c\u4ed6\u8eab\u6000\u91cd\u5b9d\uff0c\u65e2\u7136\u9732\u51fa\u98ce\u58f0\uff0c\u9ed1\u9053\u4e0a\u7684\u4eba \u7269\u53c8\u4f1a\u4e0d\u4f1a\u95fb\u98ce\u800c\u6765\uff0c\u641e\u4e71\u4e86\u4f60\u4eec\u7684\u8ba1\u5212\uff1f\u201d \u3000\u3000\u5d14\u4e91\u4eae\u9053\uff1a\u201c\u8fd9\u59d3\u6587\u7684\u6765\u9f99\u53bb\u8109\uff0c\u6211\u4eec\u5c1a\u672a\u67e5\u5f97\u6e05\u695a\uff0c\u53ea\u77e5\u9053\u4ed6\u662f\u4ece\u5357\u65b9\u6765\u7684\u3002\u8fdb\u5165\u5c71 \u4e1c\u5883\u5185\uff0c\u624d\u7ed9\u6211\u4eec\u7684\u4eba\u53d1\u89c9\u4ed6\u643a\u6709\u4ef7\u503c\u8fde\u57ce\u7684\u73e0\u5b9d\u3002\u90a3\u98ce\u58f0\u4e5f\u662f\u6211\u4eec\u653e\u51fa\u53bb\u7684\u3002\u81f3\u5b50\u9ed1\u9053\u4e0a \u7684\u4eba\u7269\uff0c\u6211\u4eec\u65e9\u5c31\u8bf7\u4e10\u5e2e\u7684\u4eba\u53bb\u6253\u8fc7\u62db\u547c\u4e86\uff0c\u5728\u672a\u5f15\u51fa\u59ec\u6653\u98ce\u4e4b\u524d\uff0c\u4e0d\u8bb8\u4ed6\u4eec\u4e0b\u624b\u3002\u5728\u6349\u5230 \u59ec\u6653\u98ce\u4e4b\u540e\uff0c\u4ed6\u4eec\u8981\u52ab\u5b9d\uff0c\u6211\u4eec\u4e0d\u7ba1\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u4eec\u5404\u5927\u95e8\u6d3e\uff0c\u8fd9\u8bb8\u591a\u4eba\uff0c\u90fd\u67e5\u4e0d\u5230\u8fd9\u59d3\u6587\u7684\u5e95\u7ec6\uff1f\u5175\u6cd5\u6709\u4e91\uff0c\u77e5\u5df1\u77e5 \u5f7c\uff0c\u65b9\u80fd\u767e\u6218\u767e\u80dc\uff0c\u4f60\u4eec\u8981\u501f\u91cd\u8fd9\u59d3\u6587\u7684\uff0c\u5374\u4e0d\u77e5\u9053\u4ed6\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u8fd9\u672a\u514d\u662f\u6709\u70b9\u5192\u9669 \u4e86\u3002\u201d\u6c5f\u5357\u81ea\u5e7c\u5931\u5b66\uff0c\u9760\u9648\u5929\u5b87\u7684\u5e2e\u52a9\uff0c\u59cb\u7c97\u901a\u6587\u58a8\uff1b\u56e0\u6b64\uff0c\u4ed6\u5728\u8bf4\u8bdd\u65f6\uff0c\u4fbf\u7279\u522b\u6b22\u559c\u5f15\u7528 \u4e00\u4e9b\u4ed6\u6240\u61c2\u5f97\u7684\u6216\u534a\u61c2\u4e0d\u61c2\u7684\u6210\u8bed\uff0c\u628a\u5d14\u4e91\u4eae\u5f04\u5f97\u557c\u7b11\u7686\u975e\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u542c\u6c5f\u5357\u8bf4\u5f97\u90d1\u91cd\uff0c\u4e5f\u4e0d\u89c9\u6014\u4e86\u4e00\u6014\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u521a\u624d\u66fe\u7aa5\u63a2\u8fc7\u4ed6\u7684\u623f\u95f4\uff0c\u53ef \u66fe\u53d1\u73b0\u4ed6\u8eab\u6000\u7edd\u6280\uff0c\u6b66\u529f\u975e\u6bd4\u5bfb\u5e38\uff1f\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1b\u201c\u8fd9\u59d3\u6587\u7684\u662f\u5426\u8eab\u6000\u7edd\u6280\uff0c\u6211\u5012\u672a\u66fe\u89c1\u5230\u3002\u53ea\u662f\u636e\u6211\u6240\u95fb\uff0c\u4ed6\u4e5f\u597d\u50cf\u5df2\u7ecf\u77e5\u9053 \u4f60\u4eec\u5728\u6697\u4e2d\u8ddf\u8e2a\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5d14\u4e91\u4eae\u6115\u4e86\u4e00\u6115\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d\u4f60\u542c\u89c1\u4ec0\u4e48\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u6211\u542c\u89c1\u4ed6\u5bf9\u4ed6\u90a3\u4e2a\u5c0f\u53ae \u8bf4\uff0c\u53eb\u4ed6\u7559\u610f\u63d0\u9632\u57cb\u4f0f\u5728\u5e97\u5b50\u91cc\u7684\u5176\u4ed6\u8d3c\u4eba\uff01\u201d\u5d14\u4e91\u4eae\u8be7\u9053\uff1a\u201c\u4ec0\u4e48\u5176\u4ed6\u8d3c\u4eba\uff1f\u201d\u6c5f\u5357\u7b11 \u9053\uff1a\u201c\u4ed6\u4eec\u6000\u7591\u6211\u4e5f\u662f\u4e2a\u5c0f\u8d3c\u5462\u3002\u201d\u5f53\u4e0b\u5c06\u81ea\u5df1\u600e\u6837\u6000\u7740\u4e00\u7247\u597d\u5fc3\uff0c\u60f3\u53bb\u529d\u90a3\u6587\u516c\u5b50\u4e0d\u53ef\u5c06 \u5b9d\u85cf\u5916\u9732\uff0c\u5374\u542c\u5230\u4ed6\u4eec\u4e3b\u4ec6\u79c1\u4e0b\u8c08\u8bdd\uff0c\u5c06\u4ed6\u4e5f\u6000\u7591\u4e0a\u4e86\u3002 \u3000\u3000\u5d14\u4e91\u4eae\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u5012\u662f\u6211\u4eec\u8d70\u773c\u4e86\u3002\u4eca\u665a\u5230\u6b64\u4e4b\u4eba\uff0c\u5747\u975e\u5eb8\u624b\u3002\u4ed6\u5c45\u7136\u80fd\u591f\u5bdf\u89c9\uff0c \u8fd9\u4efd\u672c\u9886\uff0c\u5df2\u975e\u6211\u4eec\u59cb\u6599\u6240\u53ca\u3002\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5e94\u8be5\u8bf4\u7ed9\u90a3\u4e24\u4f4d\u7985\u5e08\u77e5\u9053\u3002\u201d \u3000\u3000\u521a\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u542c\u5f97\u201c\u6251\u901a\u201d\u4e00\u58f0\uff0c\u4f3c\u662f\u6709\u4ec0\u4e48\u91cd\u7269\u7ed9\u6454\u4e86\u51fa\u6765\uff0c\u968f\u5373\u542c\u5f97\u6709\u4eba\u558a\u9053\uff1a \u201c\u778e\u4e86\u773c\u7684\u8d3c\u4eba\uff0c\u4e0d\u7ed9\u4f60\u4e00\u70b9\u989c\u8272\u77a7\u77a7\uff0c\u4f60\u4e5f\u4e0d\u77e5\u9053\u6211\u7684\u5389\u5bb3\uff01\u4f60\u8fd8\u6709\u51e0\u4e2a\u515a\u7fbd\uff1f\u6709\u79cd\u7684\u90fd \u7ad9\u51fa\u6765\uff01\u201d\u6b63\u662f\u90a3\u6587\u516c\u5b50\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5d14\u4e91\u4eae\u5927\u529b\u60ca\u8be7\uff0c\u63a8\u5f00\u7a97\u95e8\uff0c\u4e0e\u6c5f\u5357\u7acb\u5373\u98de\u8eab\u4e0a\u5c4b\uff0c\u4ed6\u4eec\u501f\u7740\u6a90\u89d2\u906e\u8eab\uff0c\u4fef\u5934\u671b\u4e0b\uff0c\u8fd9 \u4e00\u671b\u767b\u65f6\u5446\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u6700\u521d\u4ee5\u4e3a\u662f\u59ec\u6653\u98ce\u6765\u4e86\uff0c\u4f46\u53c8\u6b63\u5728\u6000\u7591\uff1a\u4ee5\u59ec\u6653\u98ce\u7684\u672c\u4e8b\uff0c\u65ad\u65e0\u4e00\u4e2a\u7167\u9762\uff0c\u4fbf\u7ed9\u4eba \u5bb6\u6454\u4e86\u51fa\u6765\u7684\u9053\u7406\uff0c\u54ea\u77e5\u8fd9\u4e2a\u867d\u7136\u4e0d\u662f\u59ec\u6653\u98ce\uff0c\u5374\u4e5f\u662f\u4ed6\u4eec\u7684\u719f\u4eba\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u4e00\u4e2a\u7626\u957f\u7684\u6c49\u5b50\uff0c\u5728\u5730\u4e0a\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\uff0c\u7ffb\u8d77\u8eab\u6765\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u6587\u516c\u5b50 \u5df2\u7136\u8ffd\u4e86\u51fa\u6765\uff0c\u51e0\u4e4e\u5c31\u5728\u8fd9\u540c\u4e00\u65f6\u95f4\uff0c\u89d2\u843d\u91cc\u8df3\u51fa\u4e24\u4e2a\u4eba\u6765\uff0c\u4e24\u67c4\u957f\u5251\u540c\u65f6\u523a\u5230\uff0c\u4e00\u662f\u4e2d\u5e74 \u9053\u58eb\uff0c\u53e6\u4e00\u4e2a\u5219\u662f\u4e2a\u7c97\u8c6a\u7684\u9ed1\u8138\u5c11\u5e74\uff01 \u3000\u3000\u8fd9\u4e09\u4e2a\u4eba\u5d14\u4e91\u4eae\u5168\u90fd\u8ba4\u5f97\uff0c\u7ed9\u7eca\u5012\u7684\u90a3\u4e2a\u7626\u957f\u6c49\u5b50\u540d\u53eb\u80e1\u4e7e\uff0c\u662f\u6b66\u5f53\u6d3e\u638c\u95e8\u96f7\u9707\u5b50\u7684\u9996 \u5f92\uff0c\u4ed6\u51fa\u9053\u6bd4\u5d14\u4e91\u4eae\u66f4\u65e9\uff0c\u4ee5\u8eab\u624b\u77eb\u6377\u9a70\u540d\u4e8e\u6c5f\u6e56\uff0c\u4eba\u79f0\u201c\u5c0f\u7075\u733f\u80e1\u4e7e\u201d\uff0c\u90a3\u9ed1\u8138\u5c11\u5e74\u4e5f\u662f \u96f7\u9707\u5b50\u7684\u5f1f\u5b50\uff0c\u540d\u53eb\u6210\u6ed4\uff0c\u4ed6\u6c14\u529b\u8fc7\u4eba\uff0c\u7ef0\u53f7\u201c\u5927\u529b\u795e\u201d\uff0c\u90a3\u4e2a\u4e2d\u5e74\u9053\u58eb\u5219\u662f\u4ed6\u4eec\u7684\u5e08\u53d4\u62b1 \u62d9\u9053\u4eba\u3002 \u3000\u3000\u5d14\u4e91\u4eae\u8ba4\u51fa\u4ed6\u4eec\uff0c\u60ca\u5947\u4e4b\u6781\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u672a\u66fe\u4e0e\u5927\u60b2\u7985\u5e08\u6253\u8fc7\u62db\u547c\uff1f\u4e0d\u77e5\u9053 \u6211\u4eec\u7684\u5b89\u6392\u5417\uff1f\u4f46\u5373\u4f7f\u4ed6\u4eec\u4e0d\u77e5\uff0c\u4e5f\u4e0d\u8be5\u5982\u6b64\u64c5\u81ea\u884c\u52a8\u5440\uff1f\u600e\u7684\u771f\u7684\u4e0b\u624b\u53bb\u5077\u8fd9\u59d3\u6587\u7684\u4e86\uff1f\u201d \u3000\u3000\u5d14\u4e91\u4eae\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u201c\u556a\u201d\u7684\u4e0a\u58f0\u54cd\uff0c\u5927\u529b\u795e\u6210\u6ed4\u5df2\u7740\u4e86\u90a3\u59d3\u6587\u5c11\u5e74\u4e00\u8bb0\u6e05\u8106\u7684\u8033 \u5149\uff0c\u6210\u6ed4\u5927\u9a82\u9053\uff1a\u201c\u5988\u5df4\u5b50\u7684\uff0c\u4f60\u8fd9\u6743\u95e8\u8d70\u72d7\uff0c\u8001\u5b50\u8981\u9a82\u4f60\u3002\u9a82\u4f60\u2026\u2026\u201d\u6210\u6ed4\u662f\u4e2a\u9c81\u83bd\u800c\u53c8 \u6897\u76f4\u7684\u5c11\u5e74\uff0c\u4e00\u6012\u4e4b\u4e0b\uff0c\u5dee\u70b9\u8981\u7528\u5bb6\u4e61\u7c97\u4fd7\u7684\u8bf4\u8bdd\u9a82\u4e86\u51fa\u6765\uff0c\u731b\u5730\u60f3\u8d77\u6709\u4e24\u4f4d\u524d\u8f88\u9ad8\u50e7\u53ef\u80fd \u5728\u573a\uff0c\u8fde\u8bf4\u4e86\u51e0\u58f0\uff1a\u201c\u9a82\u4f60\uff01\u201d\u820c\u5c16\u571f\u8bdd\u5410\u4e0d\u51fa\u6765\uff0c\u4e00\u65f6\u95f4\u5374\u53c8\u4e0d\u80fd\u6536\u53e3\uff0c\u6c14\u5f97\u6da8\u7ea2\u4e86\u8138\uff0c \u751a\u662f\u5c34\u5c2c\u3002 \u3000\u3000\u90a3\u59d3\u6587\u7684\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u6d51\u5c0f\u5b50\uff0c\u4f60\u9a82\u5427\u3002\u4f60\u518d\u9a82\uff0c\u6211\u5c31\u518d\u8d4f\u4f60\u4e00\u8033\u5149\uff01\u201d\u62b1\u62d9\u9053\u4eba\u559d \u9053\uff1a\u201c\u6210\u5e08\u4f84\uff0c\u4f60\u9000\u4e0b\uff01\u201d\u54a7\u7684\u4e00\u5251\u523a\u51fa\uff0c\u62b1\u62d9\u9053\u4eba\u662f\u6b66\u5f53\u6d3e\u7684\u6210\u540d\u4eba\u7269\uff0c\u4e00\u5251\u523a\u51fa\uff0c\u5251\u5c16 \u98a4\u52a8\uff0c\u55e1\u55e1\u6709\u58f0\uff0c\u7aef\u7684\u662f\u52b2\u9053\u5341\u8db3\uff0c\u51cc\u5389\u975e\u5e38\u3002 \u3000\u3000\u90a3\u59d3\u6587\u7684\u5c11\u5e74\u8d5e\u9053\uff1a\u201c\u597d\uff0c\u8fd8\u662f\u4f60\u8fd9\u725b\u9f3b\u5b50\u6709\u4e24\u4e0b\u5b50\u3002\u201d\u8eab\u5b50\u4e00\u98d8\u4e00\u95ea\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u907f \u5f00\u4e86\u62b1\u62d9\u9053\u4eba\u7684\u8fde\u73af\u4e09\u5251\u3002\u5f85\u5230\u7b2c\u56db\u5251\u523a\u6765\uff0c\u731b\u7684\u4e00\u58f0\u5927\u559d\u9053\uff1a\u201c\u6492\u624b\uff01\u201d\u4e0d\u77e5\u5982\u4f55\uff0c\u4ed6\u624b \u4e0a\u591a\u4e86\u4e00\u628a\u6298\u6247\uff0c\u62b1\u62d9\u9053\u4eba\u7684\u957f\u5251\u88ab\u4ed6\u7684\u6247\u5b50\u4e00\u642d\uff0c\u767b\u65f6\u597d\u50cf\u5f7c\u5343\u65a4\u91cd\u7269\u538b\u4f4f\u4e00\u822c\uff0c\u5251\u8eab\u5f2f \u66f2\uff0c\u53ef\u662f\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u4e5f\u672a\u66fe\u6492\u624b\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u57cb\u4f0f\u5728\u5c4b\u9876\u3001\u6811\u4e0a\u3001\u5899\u89d2\u6697\u5904\u7684\u5404\u6d3e\u9ad8\u624b\uff0c\u5df2\u6709\u4e8c\u5341\u4f59\u4eba\uff0c\u89c1\u6b64\u60c5\u5f62\uff0c\u65e0\u4e0d\u9707 \u9a87\uff0c\u4e0d\u4f46\u662f\u56e0\u8fd9\u5c11\u5e74\u7684\u6b66\u529f\u602a\u5f02\uff0c\u5927\u51fa\u4ed6\u4eec\u610f\u6599\u4e4b\u5916\uff0c\u800c\u4e14\u662f\u56e0\u4e3a\u6210\u6ed4\u9a82\u4ed6\u7684\u90a3\u53e5\u8bf4\u8bdd\uff0c\u4eba \u4eba\u90fd\u5728\u5fc3\u4e2d\u60f3\uff1a\u201c\u8fd9\u59d3\u6587\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\xb7\uff1f\u4e3a\u4f55\u6210\u6ed4\u9a82\u4ed6\u662f\u6743\u95e8\u8d70\u72d7\uff1f\u201d \u3000\u3000\u8fd9\u4e9b\u4eba\u90fd\u662f\u5728\u6b66\u6797\u4e2d\u6709\u76f8\u5f53\u8eab\u4efd\u7684\u4eba\uff0c\u800c\u4e14\u4ed6\u4eec\u672c\u6765\u7684\u76ee\u6807\u4e43\u662f\u59ec\u6653\u98ce\uff0c\u56e0\u6b64\u5728\u672a\u660e\u767d \u8fd9\u5c11\u5e74\u7684\u6765\u5386\u4e4b\u524d\uff0c\u8c01\u90fd\u4e0d\u613f\u51fa\u624b\u3002\u201c\u5c0f\u7075\u733f\u201d\u80e1\u4e7e\u672c\u6765\u5df2\u9000\u8fc7\u4e00\u8fb9\uff0c\u8fd9\u65f6\u89c1\u5e08\u53d4\u60c5\u5f62\u4e0d \u5999\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5bf9\u4ed8\u8fd9\u7b49\u6743\u95e8\u9e70\u72ac\uff0c\u4f55\u5fc5\u4e0e\u4ed6\u8bb2\u4ec0\u4e48\u6b66\u6797\u89c4\u77e9\uff1f\u201d\u633a\u5310\u518d\u4e0a\uff0c\u4ed6\u7684\u5251\u672f\u6bd4 \u5e08\u5f1f\u5927\u529b\u795e\u6210\u6ed4\u8981\u9ad8\u660e\u5f97\u591a\uff0c\u521a\u624d\u4ed6\u4e4b\u6240\u4ee5\u4e00\u8fdb\u623f\u95e8\u4fbf\u7ed9\u90a3\u5c11\u5e74\u6454\u4e86\u51fa\u6765\uff0c\u56fa\u7136\u662f\u7531\u5b50\u90a3\u5c11 \u5e74\u53c8\u8981\u6bd4\u4ed6\u9ad8\u660e\u4e00\u7b79\uff0c\u4f46\u53e6\u5916\u4e00\u534a\u539f\u56e0\uff0c\u5374\u4e5f\u662f\u7531\u5b50\u4ed6\u5bf9\u654c\u4eba\u4f30\u8ba1\u4e0d\u8db3\u7684\u7f18\u6545\u3002 \u3000\u3000\u6210\u6ed4\u89c1\u5e08\u5144\u52a8\u624b\uff0c\u4ed6\u4e5f\u5927\u53eb\u8c0a\uff1a\u201c\u5e08\u53d4\uff0c\u6211\u5b81\u613f\u53d7\u4f60\u8d23\u9a82\uff0c\u8fd9\u5154\u5d3d\u5b50\u6211\u662f\u975e\u6253\u4ed6\u4e0d \u53ef\uff01\u201d\u4ed6\u56e0\u4e3a\u6c14\u529b\u8fc7\u4eba\uff0c\u7528\u7684\u5251\u4e5f\u4e0e\u4f17\u4e0d\u540c\uff0c\u8db3\u6709\u56db\u5c3a\u6765\u957f\u3002\u6bd4\u5bfb\u5e38\u7684\u9752\u521a\u5251\u8981\u539a\u4e09\u500d\uff0c\u7adf \u4f3c\u51b2\u950b\u9677\u9635\u7528\u7684\u5927\u5200\u4e00\u822c\uff0c\u4e00\u5251\u5288\u4e0b\uff0c\u547c\u547c\u98ce\u54cd\u3002 \u3000\u3000\u6210\u6ed4\u7684\u5251\u91cd\u529b\u6c89\uff0c\u80e1\u4e7e\u7684\u5251\u8f7b\u7075\u7fd4\u52a8\uff0c\u540c\u65f6\u4f7f\u51fa\u6b66\u5f53\u6d3e\u7684\u8fde\u73af\u593a\u547d\u5251\u6cd5\uff0c\u76f8\u5f97\u76ca\u5f70\u3002\u90a3 \u59d3\u6587\u7684\u5c11\u5e74\u5728\u4e00\u65f6\u4e4b\u95f4\uff0c\u65e2\u672a\u80fd\u5c06\u62b1\u62d9\u9053\u4eba\u7684\u957f\u5251\u6253\u843d\uff0c\u53ea\u597d\u653e\u677e\u62b1\u62d9\u9053\u4eba\u3002\u4ed6\u7684\u8eab\u6cd5\u7aef\u7684 \u662f\u602a\u5f02\u4e4b\u6781\u3002 \u3000\u3000\u773c\u770b\u6210\u3001\u80e1\u4e8c\u4eba\u7684\u5175\u5203\u5df2\u5c06\u523a\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u500f\u7136\u95ee\u4ed6\u5df2\u5728\u53cc\u5251\u4ea4\u63d2\u7684\u7f1d\u4e2d\u94bb\u4e86\u51fa\u6765\uff0c\u53ea \u542c\u5f97\u201c\u5417\u201d\u7684\u4e00\u58f0\uff0c\u4ed6\u7684\u6298\u6247\u4e00\u6325\uff0c\u6210\u6ed4\u7684\u91cd\u94c1\u5251\u7adf\u7ed9\u4ed6\u8361\u5f97\u53cd\u5288\u8fc7\u53bb\uff0c\u4e0e\u80e1\u4e7e\u7684\u957f\u5251\u78b0\u4e2a \u6b63\u7740\uff0c\u80e1\u4e7e\u53d7\u4e0d\u8d77\u4ed6\u5e08\u5f1f\u90a3\u80a1\u5927\u529b\uff0c\u9669\u9669\u683d\u5012\uff0c\u5e78\u4e8f\u4ed6\u8eab\u6cd5\u8f7b\u7075\uff0c\u6025\u9000\u4e09\u6b65\uff0c\u6253\u4e86\u4e24\u4e2a\u76d8 \u65cb\uff0c\u8fd9\u624d\u7ad9\u7a33\u4e86\u811a\u6b65\u3002 \u3000\u3000\u62b1\u62d9\u9053\u4eba\u7ecf\u9a8c\u8001\u5230\uff0c\u6240\u53d7\u7684\u538b\u529b\u4e00\u677e\uff0c\u7acb\u5373\u62bd\u51fa\u957f\u5251\uff0c\u4e00\u62db\u201c\u4e34\u6c5f\u622a\u58c1\u201d\uff0c\u62e6\u5728\u6210\u6ed4\u7684 \u524d\u9762\uff0c\u4e0d\u8ba9\u90a3\u5c11\u5e74\u4e58\u673a\u88ad\u51fb\u4ed6\u8fd9\u4e2a\u9c81\u83bd\u7684\u5e08\u4f84\u3002\u80e1\u4e7e\u4e5f\u63c9\u8eab\u590d\u4e0a\uff0c\u7a81\u523a\u90a3\u5c11\u5e74\u80cc\u540e\u7684\u201c\u98ce\u5e9c \u7a74\u201d\uff0c\u4e24\u4eba\u524d\u540e\u5939\u653b\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u628a\u90a3\u5c11\u5e74\u7684\u653b\u52bf\u6321\u4f4f\u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6b66\u5f53\u6d3e\u957f\u5e7c\u4e24\u8f88\u7684\u6770\u51fa\u4eba\u624d\uff0c\u6587\u67d0\u4eca\u665a\u90fd\u9886\u6559\u4e86\uff0c\u679c\u7136\u9ad8\u660e\uff0c\u679c\u7136 \u9ad8\u660e\uff01\u201d \u3000\u3000\u62b1\u62d9\u9053\u4eba\u6c14\u5f97\u53cc\u7709\u5012\u7ad6\uff0c\u6012\u76ee\u5706\u7741\uff0c\u75be\u653b\u4e09\u5251\uff0c\u731b\u5730\u53eb\u9053\uff1a\u201c\u5404\u4f4d\u6b66\u6797\u540c\u9053\uff0c\u5e76\u975e\u6211\u4eec \u6b66\u5f53\u6d3e\u60f3\u6043\u4f17\u884c\u52a8\uff0c\u8fd9\u59d3\u6587\u7684\u5b9e\u5728\u662f\u5978\u76f8\u548c\u7837\u7684\u95e8\u5ba2\uff0c\u66ff\u4ed6\u62bc\u8fd0\u73e0\u5b9d\u8fdb\u4eac\u7684\uff0c\u4ed6\u8fd9\u7bb1\u73e0\u5b9d\u4e43 \u662f\u6c5f\u5357\u5404\u7701\u7763\u629a\u9001\u7ed9\u548c\u7837\u7684\u793c\u7269\uff0c\u6b64\u79cd\u4e0d\u4e49\u4e4b\u8d22\uff0c\u4eba\u4eba\u53ef\u53d6\uff0c\u6b64\u79cd\u4e0d\u4e49\u4e4b\u4eba\uff0c\u4eba\u4eba\u53ef\u8bdb\uff01\u201d \u3000\u3000\u548c\u7837\u662f\u5f53\u671d\u6700\u5f97\u5ba0\u7684\u5927\u81e3\uff0c\u636e\u8bf4\u672c\u662f\u4e7e\u9686\u7684\u8f7f\u592b\uff0c\u4e7e\u9686\u56e0\u4ed6\u76f8\u8c8c\u4e0e\u4e00\u4e2a\u6b7b\u53bb\u7684\u5ba0\u5983\u76f8 \u4f3c\uff0c\u9042\u52a0\u4ee5\u4e0d\u65ad\u5347\u64e2\u5347\u3002\u53e6\u4e00\u8bf4\u8c13\u4ed6\u672c\u6709\u70b9\u5c0f\u806a\u660e\uff0c\u6709\u4e00\u65e5\u4e7e\u9686\u5927\u9a7e\u5c06\u51fa\uff0c\u4ed3\u5352\u95f4\u6c42\u9ec4\u76d6\u4e0d \u5f97\uff0c\u4e7e\u9686\u8d23\u95ee\uff1a\u201c\u662f\u8c01\u4e4b\u8fc7\uff1f\u201d\u548c\u7837\u5728\u8f7f\u524d\u5e94\u58f0\u7b54\u9053\uff1a\u201c\u5178\u5b88\u8005\u4e0d\u5f97\u8f9e\u5176\u8d23\u3002\u201d\u4e7e\u9686\u89c1\u4ed6\u4eea \u5ea6\u4fca\u96c5\uff0c\u58f0\u97f3\u6e05\u4eae\uff0c\u8d5e\u9053\uff1a\u201c\u82e5\u8f88\u4e2d\u5b89\u5f97\u6b64\u89e3\u4eba\uff1f\u201d\u9042\u6d3e\u4ed6\u603b\u7ba1\u4eea\u4ed7\uff0c\u65cb\u5347\u4f8d\u536b\uff0c\u64e2\u5347\u526f\u90fd \u7edf\uff0c\u53c8\u8fc1\u4f8d\u90ce\uff0c\u4e00\u8def\u5347\u4e0a\u53bb\uff0c\u76f4\u505a\u81f3\u201c\u5927\u5b66\u58eb\u201d\u3002 \u3000\u3000\u6e05\u671d\u4e0d\u8bbe\u5bb0\u76f8\uff0c\u7531\u201c\u5927\u5b66\u58eb\u201d\u5206\u638c\u76f8\u6743\uff0c\u5b98\u573a\u4e2d\u5bf9\u4efb\u5927\u5b66\u675c\u804c\u8005\u4ea6\u5c0a\u79f0\u4e3a\u76f8\u540c\uff0c\u5173\u5b50\u548c \u7837\u51fa\u8eab\u6b64\u8bf4\uff0c\u89c1\u859b\u798f\u6210\u300a\u5eb8\u76d2\u7b14\u8bb0\u300b\u3002\u603b\u4e4b\uff0c\u4e0d\u8bba\u4ed6\u662f\u501f\u751a\u673a\u7f18\u5f97\u5230\u63d0\u5347\uff0c\u5728\u6709\u6e05\u4e00\u4ee3\uff0c\u8bba \u5230\u79c9\u653f\u63fd\u6743\uff0c\u5f97\u5230\u541b\u7687\u4fe1\u4efb\u4e4b\u4e13\uff0c\u6ca1\u6709\u4e00\u4e2a\u5927\u5b66\u58eb\u8db3\u4ee5\u4e0e\u4ed6\u6bd4\u62df\u3002\u4ed6\u4ece\u4e7e\u9686\u56db\u5341\u4e8c\u5e74\u51fa\u4efb\u5927 \u5b66\u58eb\u8d77\uff0c\u4e00\u76f4\u505a\u4e86\u5341\u51e0\u5e74\u7684\u592a\u5e73\u5bb0\u76f8\uff0c\u76f4\u5230\u4e7e\u9686\u6b7b\u540e\uff0c\u4ed6\u624d\u7ed9\u5609\u5e86\u6240\u6740\uff0c\u90a3\u662f\u540e\u8bdd\u3002 \u3000\u3000\u4e7e\u9686\u91cd\u7528\u548c\u547b\uff0c\u5230\u4e86\u665a\u5e74\uff0c\u501a\u754c\u76ca\u7b03\uff0c\u7adf\u51c6\u5176\u7236\u914d\u4eab\u592a\u5e99\uff0c\u5176\u5f1f\u548c\u7433\u91cd\u4efb\u8fb9\u7586\uff0c\u53c8\u5c06\u516c \u4e3b\u5ac1\u7ed9\u4ed6\u7684\u513f\u4e30\u7ec5\u6bb7\u5fb7\uff0c\u4e00\u5bb6\u5bcc\u8d35\uff0c\u4f4d\u6781\u4eba\u81e3\uff0c\u6743\u503e\u671d\u91ce\u3002\u8fbe\u5b98\u8d35\u4eba\uff0c\u54b8\u5954\u8d70\u5176\u95e8\uff0c\u89c6\u4e3a\u5347 \u5b98\u53d1\u8d22\u7684\u6377\u5f84\u3002 \u3000\u3000\u548c\u7837\u66f4\u662f\u5356\u5b98\u7a9c\u7235\uff0c\u62db\u6743\u7eb3\u8d3f\uff0c\u65e0\u6240\u4e0d\u4e3a\u3002\u65f6\u4eba\u6709\u8bd7\u4e91\uff1a\u201c\u7ee3\u8863\u6210\u5df7\u63a5\u516c\u8859\uff0c\u66f2\u66f2\u5f2f\u5f2f \u8def\u4e0d\u5dee\uff0c\u83ab\u7b11\u6b64\u95f4\u8857\u9053\u7a84\uff0c\u6709\u95e8\u80fd\u8fbe\u76f8\u516c\u5bb6\u3002\u201d\u5c31\u662f\u541f\u548f\u5f53\u65f6\u60c5\u666f\u7684\u3002 \u3000\u3000\u4e7e\u9686\u4e8c\u5341\u4e94\u5c81\u5373\u4f4d\uff0c\u8fd9\u65f6\u5df2\u7ecf\u505a\u4e86\u4e94\u5341\u4e03\u5e74\u7687\u5e1d\uff0c\u5df2\u7ecf\u662f\u516b\u5341\u4e8c\u5c81\u7684\u8001\u4eba\uff0c\u5065\u5eb7\u8fd8\u5f88\u4e0d \u9519\u3002\u4e0d\u8fc7\uff0c\u4ed6\u5728\u5373\u4f4d\u7684\u65f6\u5019\u4fbf\u66fe\u8bb8\u4e0b\u8a93\u613f\uff0c\u505a\u7687\u5e1d\u6700\u591a\u505a\u516d\u5341\u5e74\uff0c\u8868\u793a\u4e0d\u6562\u8d8a\u8fc7\u4ed6\u7684\u7956\u7236\uff0c \u4ed6\u7684\u7956\u7236\u5eb7\u7199\u505a\u4e86\u516d\u5341\u4e00\u5e74\u7687\u5e1d\u3002\u56e0\u6b64\u51c6\u5907\u518d\u8fc7\u4e09\u5e74\uff0c\u4fbf\u4f20\u4f4d\u7ed9\u592a\u5b50\uff0c\u81ea\u5df1\u9000\u4e3a\u201c\u592a\u4e0a\u7687\u201d\u3002 \u3000\u3000\u548c\u7837\u5f97\u4efb\u9ad8\u4f4d\uff0c\u5168\u9760\u4e7e\u9686\u7684\u5ba0\u7737\uff0c\u5f97\u77e5\u4e7e\u9686\u6709\u9000\u4f4d\u4e4b\u610f\uff0c\u5927\u4e3a\u7740\u6025\uff0c\u4ed6\u4e00\u9762\u7b3c\u7edc\u592a\u5b50\uff0c \u4e00\u9762\u57f9\u690d\u81ea\u5df1\u7684\u52bf\u529b\uff0c\u540c\u65f6\u52a0\u7d27\u805a\u6559\u3002\u4ed6\u7684\u8c6a\u5962\uff0c\u771f\u53ef\u8bf4\u5f97\u524d\u65e0\u53e4\u4eba\uff0c\u540e\u65e0\u6765\u8005\u3002 \u3000\u3000\u636e\u8bf4\u4ed6\u6bcf\u65e5\u6e05\u6668\uff0c\u90fd\u8981\u5403\u51e0\u9897\u73cd\u73e0\uff0c\u7531\u4e13\u5bb6\u66ff\u4ed6\u70f9\u8c03\uff0c\u4e91\u662f\uff1a\u98df\u73e0\u4e4b\u540e\uff0c\u5373\u5fc3\u7a8d\u7075\u660e\uff0c \u8fc7\u76ee\u5373\u8bb0\uff0c\u4e00\u65e5\u4e4b\u5185\uff0c\u867d\u8bf8\u52a1\u7eb7\u6c93\uff0c\u5176\u80f8\u4e2d\u4e86\u7136\u4e0d\u5fd8\u3002\u4ed6\u6240\u98df\u7684\u73cd\u73e0\uff0c\u51e1\u8272\u6cfd\u68a2\u5dee\u7684\u548c\u5df2\u7ecf \u7a7f\u8fc7\u7684\u4e0d\u7528\uff0c\u636e\u524d\u4eba\u7b14\u8bb0\u6240\u8f7d\uff0c\u4ed6\u6240\u98df\u7528\u7684\u73cd\u73e0\uff0c\u6700\u91cd\u8005\u4e00\u7c92\u4ef7\u503c\u4e8c\u5341\u4e07\uff0c\u8f7b\u8005\u4e00\u65b9\uff0c\u81f3\u8f7b \u8005\u4ea6\u503c\u516b\u5343\uff01\u4ed6\u6bcf\u65e5\u6240\u7528\u7684\u73cd\u73e0\uff0c\u6709\u90e8\u4efd\u4fbf\u662f\u5357\u65b9\u5404\u7701\u7763\u629a\u6240\u732e\u7684\u3002 \u3000\u3000\u5173\u4e8e\u548c\u7837\u7684\u95f2\u8bdd\u5e26\u8fc7\uff0c\u4e14\u8bf4\u57cb\u4f0f\u5728\u8fd9\u5ba2\u5e97\u7684\u5404\u6d3e\u9ad8\u624b\uff0c\u542c\u62b1\u62d9\u9053\u4eba\u8bf4\u8fd9\u59d3\u6587\u7684\u7adf\u662f\u548c\u7837 \u95e8\u5ba2\uff0c\u90a3\u7bb1\u73e0\u5b9d\uff0c\u5c31\u662f\u66ff\u548c\u7837\u62bc\u8fdb\u4eac\u7684\uff0c\u767b\u65f6\u9a9a\u52a8\u8d77\u6765\uff0c\u6709\u51e0\u4e2a\u4eba\u5df2\u4ece\u6697\u9edd\u4e4b\u5904\u8df3\u51fa\u3002 \u3000\u3000\u90a3\u59d3\u6587\u7684\u65e2\u4e0d\u627f\u8ba4\u4ea6\u4e0d\u5426\u8ba4\uff0c\u4ed6\u6298\u6247\u4e00\u6325\uff0c\u5c06\u62b1\u62d9\u9053\u4eba\u7684\u957f\u5251\u5c01\u51fa\u95e8\u5916\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u600e\u4e48\uff0c\u4f60\u4eec\u6b66\u5f53\u6d3e\u957f\u5e7c\u4e24\u8f88\uff0c\u8fd8\u5acc\u4eba\u624b\u4e0d\u591f\uff0c\u8981\u8bf7\u5728\u573a\u8bf8\u4f4d\u4e00\u9f50\u4e0a\u4e48\uff1f\u54c8\uff0c\u54c8\uff0c\u8fd9\u771f\u662f\u5927 \u62ac\u4e3e\u6211\u4e86\u3002\u6587\u67d0\u5f97\u5929\u4e0b\u82f1\u96c4\uff0c\u540c\u6765\u8d50\u6559\uff0c\u4f55\u5e78\u5982\u4e4b\u3002\u201d \u3000\u3000\u5728\u573a\u7684\u5341\u4e5d\u90fd\u662f\u6b66\u6797\u4e2d\u7684\u6210\u540d\u4eba\u7269\uff0c\u867d\u7136\u4ed6\u4eec\u75db\u6068\u548c\u7837\uff0c\u4e0e\u62b1\u62d9\u9053\u4eba\u4e5f\u6216\u591a\u6216\u5c11\u6709\u70b9\u4ea4 \u60c5\uff0c\u4f46\u59d3\u6587\u8fd9\u5c11\u5e74\u6b64\u8a00\u4e00\u51fa\uff0c\u65e0\u5f02\u7aef\u51fa\u4e86\u4e00\u9762\u6321\u7bad\u724c\uff0c\u767b\u65f6\u4ee4\u5f97\u7fa4\u96c4\u8e0c\u8e87\u4e0d\u524d\uff0c\u90a3\u51e0\u4e2a\u8df3\u4e86 \u51fa\u6765\u7684\u4eba\u53c8\u590d\u9000\u4e86\u56de\u53bb\u3002 \u3000\u3000\u90a3\u59d3\u6587\u7684\u5c11\u5e74\u6298\u6247\u8fde\u6325\uff0c\u628a\u62b1\u62d9\u9053\u4eba\u8feb\u5f97\u6b65\u6b65\u540e\u9000\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u53ea\u542c\u5f97\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u5927\u529b\u795e\u6210\u6ed4\u7684\u989d\u89d2\u88ab\u6247\u5b50\u72e0\u72e0\u5730\u6572\u4e86\u4e00\u8bb0\uff0c\u8840\u6d41\u5982\u6ce8\uff0c\u62b1 \u62d9\u9053\u4eba\u4e0e\u80e1\u4e7e\u53cc\u5251\u9f50\u51fa\uff0c\u4e00\u4e2a\u5728\u524d\u9762\u5c55\u5251\u523a\u4ed6\u80f8\u53e3\u7684\u201c\u7487\u7391\u7a74\u201d\uff0c\u4e00\u4e2a\u5728\u80cc\u540e\u523a\u4ed6\u7684\u201c\u98ce\u5e9c \u7a74\u201d\u3002\u8fd9\u4e24\u4eba\u662f\u6b66\u5f53\u6d3e\u6709\u6570\u7684\u5251\u672f\u597d\u624b\uff0c\u4ed6\u4eec\u4e3a\u4e86\u89e3\u6210\u6ed4\u4e4b\u5371\uff0c\u594b\u4e0d\u987e\u8eab\u7684\u6251\u4e0a\uff0c\u8fd0\u5251\u5982 \u98ce\uff0c\u5f53\u771f\u662f\u6027\u547d\u76f8\u640f\uff0c\u51cc\u5389\u975e\u5e38\uff01 \u3000\u3000\u597d\u4e2a\u5c11\u5e74\uff0c\u53ea\u89c1\u4ed6\u5728\u80cc\u8179\u53d7\u654c\uff0c\u53cc\u5251\u8fdb\u8feb\u4e4b\u4e0b\uff0c\u500f\u5730\u4e00\u4e2a\u76d8\u65cb\uff0c\u6298\u6247\u4e00\u5408\uff0c\u4fbf\u5411\u62b1\u62d9\u9053 \u4eba\u7684\u8155\u9aa8\u6572\u51fb\uff0c\u62b1\u62d9\u9053\u4eba\u201c\u6dae\u201d\u7684\u4e00\u5251\u4ece\u4ed6\u80c1\u4e0b\u7a7f\u8fc7\uff0c\u5374\u6ca1\u6709\u4f24\u7740\u4ed6\uff0c\u53cd\u800c\u88ab\u4ed6\u6b3a\u8eab\u53cd\u6251\uff0c \u8fde\u5fd9\u6643\u8eab\u75be\u95ea\u3002 \u3000\u3000\u54ea\u77e5\u59d3\u6587\u5c11\u5e74\u8fd9\u4e00\u62db\u53cd\u6251\uff0c\u770b\u4f3c\u9738\u9053\uff0c\u5b9e\u5728\u5374\u662f\u865a\u62db\uff0c\u62b1\u62d9\u9053\u4eba\u4e00\u65f6\u4e0d\u5bdf\uff0c\u88ab\u4ed6\u5413\u9000\uff0c \u8fd9\u5c11\u5e74\u51cf\u5c11\u4e86\u524d\u9762\u7684\u5a01\u80c1\uff0c\u9661\u5730\u53cd\u624b\u4e00\u6293\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4e5f\u7ed9\u6211\u8eba\u4e0b\u6765\u5427\uff01\u201d\u539f\u6765\u4ed6\u662f\u907f\u5f3a\u51fb \u5f31\uff0c\u5b9e\u9645\u7684\u76ee\u6807\u5374\u662f\u80e1\u4e7e\u3002 \u3000\u3000\u80e1\u4e7e\u672c\u6765\u4e5f\u4ee5\u8eab\u624b\u77eb\u6377\u89c1\u957f\uff0c\u53ef\u662f\u4e09\u4e2a\u4eba\u6bd4\u8d77\u6765\uff0c\u5374\u662f\u4ed6\u7a0d\u900a\u4e00\u7b79\uff0c\u4ed6\u7684\u5251\u5c16\u582a\u582a\u5c31\u8981 \u89e6\u5230\u90a3\u5c11\u5e74\u7684\u80cc\u5fc3\uff0c\u4e0d\u6599\u90a3\u5c11\u5e74\u7684\u8eab\u5f62\u4e00\u4e2a\u503e\u659c\u6ed1\u6b65\uff0c\u4ed6\u7684\u957f\u5251\u5df2\u7ecf\u523a\u6b6a\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6 \u5feb\uff0c\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u7684\u5239\u90a3\u4e4b\u95f4\uff0c\u90a3\u5c11\u5e74\u5df2\u662f\u58f0\u5230\u4eba\u5230\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u80e1\u4e7e\u7684\u8863\u670d \u88ab\u6495\u53bb\u4e86\u4e00\u5927\u5e45\uff0c\u9732\u51fa\u534a\u8fb9\u5149\u80cc\u810a\uff0c\u9690\u7136\u6ca1\u6709\u8eba\u4e0b\uff0c\u4ea6\u5df2\u72fc\u72c8\u975e\u5e38\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u7684\u672c\u4e8b\u8981\u6bd4\u8fd9\u5927\u4e2a\u5b50\u9ad8\u660e\u8bb8\u591a\uff0c\u4f60\u8981\u4e0d\u8981\u6b47\u6b47\uff0c\u7a7f\u597d\u4e86\u8863\u518d\u6765\uff1f\u201d \u3000\u3000\u5d14\u4e91\u4eae\u4e0e\u80e1\u4e7e\u4ea4\u60c5\u751a\u6df1\uff0c\u8fd9\u65f6\u5fcd\u4e0d\u4f4f\u62d4\u5251\u8df3\u4e0b\uff0c\u9053\uff1a\u201c\u62b1\u62d9\u9053\u957f\u8bf4\u7684\u5bf9\uff0c\u5bf9\u4ed8\u8fd9\u7b49\u9e70 \u72ac\uff0c\u4f55\u987b\u4e0e\u4ed6\u8bb2\u6b66\u6797\u89c4\u77e9\uff0c\u80e1\u5927\u54e5\uff0c\u6210\u5927\u54e5\uff0c\u8bf7\u8ba9\u5c0f\u5f1f\u4e5f\u6765\u4e0e\u4ed6\u4e00\u4f1a\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u51b7\u7b11\u9053\uff1a\u201c\u597d\u7684\uff0c\u6b66\u5f53\u6d3e\u4e0d\u884c\uff0c\u518d\u77a7\u77a7\u4f60\u9752\u57ce\u6d3e\u7684\uff0c\u4f60\u4eec\u8981\u4e00\u7a9d\u8702\u6765\u4e5f\u597d\uff0c\u8981\u8f66 \u8f6e\u6218\u4e5f\u597d\uff0c\u90fd\u542c\u968f\u4f60\u4eec\u7684\u4fbf\u3002\u201d\u4e0d\u4f46\u795e\u60c5\u50b2\u6162\uff0c\u800c\u4e14\u542c\u4ed6\u968f\u53e3\u9053\u6765\uff0c\u7adf\u4f3c\u5bf9\u5907\u4eba\u7684\u6765\u5386\u90fd\u77e5 \u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u80e1\u4e7e\u88ab\u6495\u6bc1\u4e86\u8863\u88f3\uff0c\u65e0\u989c\u518d\u6218\uff0c\u53ea\u597d\u62c9\u4e86\u4ed6\u7684\u5e08\u5f1f\u9000\u4e0b\uff0c\u62b1\u62d9\u9053\u4eba\u867d\u7136\u4ea6\u89c9\u9762\u4e0a\u65e0\u5149\uff0c \u4f46\u5f3a\u654c\u5f53\u524d\uff0c\u5d14\u4e91\u4eae\u65e2\u6765\u76f8\u52a9\uff0c\u81ea\u5df2\u600e\u5fcd\u8ba9\u4ed6\u4e00\u4eba\u72ec\u6218\uff1f\u56e0\u6b64\u53ea\u5f97\u5f3a\u632f\u7cbe\u795e\uff0c\u4ecd\u7136\u4e0e\u4ed6\u5411\u90a3 \u5c11\u5e74\u594b\u6218\u3002\u4f46\u4ed6\u4ee5\u6b66\u5f53\u524d\u8f88\u7684\u8eab\u4efd\uff0c\u4e0d\u4f46\u6218\u8fd9\u5c11\u5e74\u4e0d\u4e0b\uff0c\u53cd\u800c\u5c61\u6b21\u5403\u4e8f\uff0c\u4e5f\u65e9\u5df2\u6709\u4e9b\u6c14\u9981\u4e86\u3002 \u3000\u3000\u90a3\u5d14\u4e91\u4eae\u5374\u662f\u8840\u6c14\u65b9\u521a\uff1b\u6068\u8fd9\u5c11\u5e74\u50b2\u6162\uff0c\u9752\u94a2\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u7acb\u5373\u4e00\u62db\u201c\u957f\u8679\u7ecf\u5929\u201d\uff0c\u811a \u8e0f\u6d2a\u95e8\uff0c\u5411\u8fd9\u5c11\u5e74\u80f8\u53e3\u901e\u523a\u3002 \u3000\u3000\u5d14\u4e91\u4eae\u5df2\u5c3d\u5f97\u4e43\u5e08\u771f\u4f20\uff0c\u5251\u672f\u4e0a\u548c\u5185\u529f\u4e0a\u7684\u9020\u8be3\uff0c\u53c8\u8981\u6bd4\u96f7\u9707\u5b50\u90a3\u4e24\u4e2a\u5f92\u5f1f\u6df1\u539a\u8bb8\u591a\uff0c \u672c\u6765\u6b66\u5b66\u7684\u672f\u8bed\u6709\u4e91\uff1a\u201c\u5200\u8d70\u767d\uff0c\u5251\u8d70\u9ed1\u3002\u201d\u5373\u662f\u8bf4\u7528\u5200\u5b9c\u4e8e\u6b63\u9762\u4ea4\u950b\uff0c\u7528\u5251\u5219\u5b9c\u5b50\u4fa7\u88ad\uff0c \u50cf\u5d14\u4e91\u4eae\u8fd9\u6837\uff0c\u7b2c\u4e00\u62db\u5c31\u8e0f\u6b63\u6d2a\u95e8\uff0c\u4ece\u4e2d\u8def\u6025\u653b\uff0c\u90a3\u662f\u975e\u5e38\u5c11\u89c1\u3002\u90a3\u5c11\u5e74\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d \u5b57\uff0c\u6298\u6247\u4e00\u5e26\uff0c\u4f7f\u4e86\u4e2a\u201c\u5378\u201d\u5b57\u8bc0\uff0c\u5d14\u4e91\u4eae\u8fd9\u4e00\u5251\u7528\u8db3\u4e86\u6c14\u529b\uff0c\u7a81\u7136\u88ab\u4ed6\u7684\u6247\u5b50\u642d\u7740\u5251\u810a\uff0c \u987a\u624b\u4e00\u5e26\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u8eab\u5411\u524d\u503e\uff0c\u5e78\u5728\u4ed6\u5df2\u6709\u4e86\u76f8\u5f53\u529f\u529b\uff0c\u5dee\u4e0d\u591a\u5230\u4e86\u80fd\u53d1\u80fd\u6536\uff0c\u968f\u5fc3\u6240\u6b32 \u7684\u5883\u754c\uff0c\u811a\u6b65\u521a\u4e00\u8e09\u8dc4\uff0c\u7acb\u5373\u4fbf\u8d81\u52bf\u4ee5\u811a\u8ddf\u4f5c\u8f74\uff0c\u8f6c\u4e86\u534a\u4e2a\u5706\u5708\uff0c\u5251\u62db\u4ece\u201c\u957f\u8679\u7ecf\u5929\u201d\u4e00\u53d8 \u800c\u4e3a\u201c\u968f\u98ce\u6298\u67f3\u2019\uff0c\u4e0d\u4f46\u63a9\u9970\u4e86\u4ed6\u5931\u62db\u7a98\u6001\uff0c\u800c\u4e14\u53d8\u5316\u5f97\u975e\u5e38\u81ea\u7136\uff0c\u5018\u975e\u5251\u672f\u540d\u5bb6\uff0c\u7edd\u5bf9\u770b \u4e0d\u51fa\u6765\u3002 \u3000\u3000\u62b1\u62d9\u9053\u4eba\u89c1\u5d14\u4e91\u4eae\u5251\u672f\u4e86\u5f97\uff0c\u5b9e\u5728\u6bd4\u4ed6\u90a3\u4e24\u4e2a\u5e08\u4f84\u52a0\u8d77\u6765\u8fd8\u5f3a\u5f97\u591a\uff0c\u6218\u610f\u767b\u65f6\u590d\u76db\uff0c\u800c \u4e14\u4e3a\u4e86\u5d14\u4e91\u4eae\u662f\u9752\u57ce\u6d3e\u7684\uff0c\u4ed6\u66f4\u4e0d\u613f\u5728\u7fa4\u96c4\u9762\u524d\u5760\u4e86\u6b66\u5f53\u6d3e\u7684\u58f0\u5a01\uff0c\u8fd9\u4e00\u6765\uff0c\u4ed6\u4e0d\u4f46\u662f\u4e0e\u5d14 \u4e91\u4eae\u8054\u624b\u5bf9\u654c\uff0c\u800c\u4e14\u8fd8\u542b\u6709\u6697\u4e2d\u4e0e\u5d14\u4e91\u4eae\u201c\u6bd4\u8d5b\u201d\u7684\u5fc3\u610f\uff0c\u4e0d\u7531\u5f97\u4ed6\u4e0d\u628a\u5168\u526f\u672c\u9886\u5c3d\u90fd\u65bd\u5c55 \u51fa\u6765\uff0c\u5f53\u771f\u662f\u62fc\u4e86\u6027\u547d\u4e0e\u90a3\u59d3\u6587\u7684\u5c11\u5e74\u6076\u6218\u3002 \u3000\u3000\u62b1\u62d9\u9053\u4eba\u631f\u7740\u6570\u5341\u5e74\u529f\u529b\uff0c\u62fc\u547d\u6076\u6218\uff0c\u6bd4\u4e4b\u521a\u624d\u5927\u5927\u4e0d\u540c\uff0c\u4f46\u89c1\u4ed6\u628a\u6b66\u5f53\u6d3e\u7684\u4e03\u5341\u4e8c\u624b \u8fde\u73af\u5251\u6cd5\u970d\u970d\u5c55\u5f00\uff0c\u767b\u65f6\u56db\u9762\u516b\u65b9\uff0c\u90fd\u662f\u5251\u5149\u4eba\u5f71\u3002\u5d14\u4e91\u4eae\u521d\u9022\u5f3a\u654c\uff0c\u4e5f\u662f\u5168\u529b\u65bd\u4e3a\uff0c\u4e24\u4eba \u90fd\u5728\u594b\u52c7\u4e89\u5148\uff0c\u5e0c\u671b\u80fd\u6bd4\u540c\u4f34\u62a2\u5feb\u4e00\u6b65\uff0c\u5728\u90a3\u5c11\u5e74\u7684\u8eab\u4e0a\u523a\u4e2a\u900f\u660e\u7684\u7a9f\u7abf\u3002 \u3000\u3000\u4e0d\u6599\u90a3\u59d3\u6587\u7684\u5c11\u5e74\uff0c\u672c\u9886\u7adf\u662f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u654c\u4eba\u65b9\u9762\u52a0\u5f3a\uff0c\u4ed6\u7684\u672c\u9886\u4e5f\u4f3c\u4e4e\u7a81\u7136\u589e\u5f3a\u8d77 \u6765\uff0c\u4f46\u89c1\u4ed6\u5728\u5251\u5149\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u4f9d\u7136\u6c14\u5b9a\u795e\u95f2\uff0c\u4e00\u67c4\u6298\u6247\u5ffd\u5f20\u5ffd\u5408\uff0c\u5f20\u5f00\u6765\u65f6\uff0c\u5f53\u4f5c\u6298\u94c1\u5200 \u7528\uff0c\u5408\u8d77\u6765\u65f6\u5f53\u4f5c\u5224\u5b98\u7b14\u4f7f\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u62db\u6570\u5947\u8be1\u65e0\u4f26\uff01\u9976\u662f\u53cc\u5251\u592d\u77eb\uff0c\u7adf\u7136\u8fde \u4ed6\u7684\u8863\u89d2\u90fd\u672a\u66fe\u6cbe\u4e0a\u3002 \u3000\u3000\u6c5f\u5357\u770b\u5f97\u6697\u6697\u7740\u6025\uff0c\u6479\u7136\u95f4\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u62b1\u62d9\u9053\u4eba\u8bf4\u4ed6\u662f\u548c\u7837\u7684\u95e8\u5ba2\uff0c\u8fd9\u548c\u7837\u4e0d\u5c31\u662f\u5f53 \u5e74\u5bb3\u6211\u4e49\u4f2f\u7684\u90a3\u4e2a\u548c\u7837\u5417\uff1f\u6211\u4e49\u4f2f\u4e3a\u4ed6\u5403\u4e86\u5341\u5e74\u82e6\u5934\uff0c\u8fd9\u53ae\u662f\u7ed9\u548c\u7837\u62bc\u8fd0\u73e0\u5b9d\u7684\u3002\u54fc\u54fc\uff0c\u6211 \u867d\u4e0e\u8fd9\u5c11\u5e74\u65e0\u51a4\u65e0\u4ec7\uff0c\u4f46\u4e3a\u4e86\u7ed9\u4e49\u4f2f\u51fa\u53e3\u6028\u6c14\uff0c\u6211\u4e5f\u4e0d\u80fd\u4fbf\u5b9c\u4e86\u548c\u7837\u8fd9\u8001\u8d3c\uff01\u201d \u3000\u3000\u6c5f\u5357\u6240\u60f3\u8d77\u7684\u201c\u4e49\u4f2f\u201d\uff0c\u4fbf\u662f\u4ed6\u7ed3\u62dc\u5144\u5f1f\u9648\u5929\u5b87\u7684\u7236\u4eb2\uff0c\u4e5f\u5373\u662f\u4ed6\u7684\u65e7\u4e3b\u4eba\u9648\u5b9a\u57fa\u3002\u9648 \u5b9a\u57fa\u5c31\u662f\u56fa\u4e3a\u4e0a\u7ae0\u5f39\u52be\u548c\u7837\uff0c\u56e0\u800c\u88ab\u4e7e\u9686\u8d2c\u5230\u897f\u85cf\uff0c\u505a\u8428\u8fea\u5b97\u7684\u201c\u5ba3\u6170\u4f7f\u201d\u7684\uff0c\u4e00\u8d2c\u5341\u5e74\uff0c \u8fdc\u620d\u8fb9\u7586\uff0c\u51e0\u65e0\u751f\u8fd8\u4e4b\u671b\uff0c\u540e\u6765\u597d\u5728\u6709\u4fdd\u62a4\u201c\u91d1\u672c\u5df4\u74f6\u201d\u5165\u85cf\u4e4b\u529f\uff0c\u8fd9\u624d\u5f97\u88ab\u53ec\u56de\uff0c\u5b98\u5c45\u539f \u804c\uff0c\u4e0d\u4e45\u4ed6\u4e5f\u5c31\u544a\u8001\u9000\u4f11\u4e86\u3002\uff08\u4e8b\u8be6\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\uff09\u90a3\u65f6\uff0c\u6c5f\u5357\u662f\u9648\u5929\u5b87\u7684\u4e66\u7ae5\uff0c\u9648\u5b9a\u57fa \u5c31\u662f\u56e0\u4e3a\u6000\u5ff5\u6c5f\u5357\u6545\u4e61\uff0c\u624d\u7ed9\u4ed6\u8d77\u8fd9\u4e2a\u540d\u5b57\u7684\u3002 \u3000\u3000\u6c5f\u5357\u60f3\u8d77\u4e86\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u767b\u65f6\u6012\u6c14\u6697\u751f\uff0c\u5fc3\u9053\uff1a\u201c\u4fd7\u8bed\u8bf4\uff1a\u6253\u72d7\u8981\u770b\u4e3b\u4eba\u9762\u3002\u6211\u8fd9\u56de\u5374 \u662f\uff1a\u4e3a\u4e86\u4e3b\u4eba\u624d\u6253\u72d7\u3002\u59d3\u6587\u7684\u4e0e\u6211\u65e0\u4ec7\uff0c\u548c\u7837\u5374\u4e0e\u6211\u4e49\u4f2f\u6709\u4ec7\uff0c\u4e0d\u7ba1\u597d\u574f\uff0c\u6211\u4e5f\u5f97\u60e9\u6212\u60e9\u6212 \u8fd9\u4e2a\u5c0f\u5b50\u3002\u201d \u3000\u3000\u6c5f\u5357\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u5d14\u4e91\u4eae\u95f7\u54fc\u4e00\u58f0\uff0c\u6251\u901a\u4fbf\u5012\u3002\u539f\u6765\u662f\u7ed9\u90a3\u5c11\u5e74\u70b9\u4e2d\u4e86\u4ed6\u7684\u7a74\u9053\u3002 \u90a3\u5c11\u5e74\u70b9\u5012\u4e86\u5d14\u4e91\u4eae\uff0c\u671b\u4e5f\u4e0d\u671b\u4e00\u773c\uff0c\u6325\u6247\u4fbf\u5411\u62b1\u62d9\u9053\u4eba\u72c2\u653b\uff0c\u628a\u62b1\u62d9\u9053\u4eba\u8feb\u5f97\u5341\u5206\u72fc\u72c8\u3002 \u3000\u3000\u6c5f\u5357\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u597d\u5c0f\u5b50\u4f11\u5f97\u7316\u72c2\uff01\u201d\u53cc\u81c2\u4e00\u632f\uff0c\u4fbf\u4ece\u5c4b\u9876\u8df3\u4e86\u4e0b\u6765\uff0c\u6276\u8d77\u4e86\u5d14\u4e91\u4eae\u5411 \u65c1\u4e00\u63a8\uff0c\u53eb\u9053\uff1a\u201c\u5d14\u8001\u5f1f\uff0c\u4f60\u7b49\u7740\u77a7\uff0c\u505a\u5144\u5f1f\u7684\u66ff\u4f60\u51fa\u6c14\u3002\u201d \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u4ed3\u5577\u58f0\u54cd\uff0c\u62b1\u62d9\u9053\u4eba\u7684\u957f\u5251\u53c8\u5df2\u7ed9\u90a3\u5c11\u5e74\u6253\u843d\uff0c\u62b1\u62d9\u9053\u4eba\u662f\u6709\u8eab\u4efd\u7684 \u6210\u540d\u4eba\u7269\uff0c\u5b9d\u5251\u843d\u5730\uff0c\u65e0\u989c\u518d\u6218\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u62ac\u8d77\u5175\u5203\uff0c\u4fbf\u8df3\u51fa\u56f4\u5899\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u89c1\u5d14\u4e91\u4eae\u88ab\u6c5f\u5357\u4e00\u6276\u8d77\u6765\uff0c\u624b\u8db3\u4fbf\u53ef\u6d3b\u52a8\uff0c\u81ea\u884c\u9000\u5230\u5899\u8fb9\uff0c\u5305\u624e\u4f24\u53e3\uff0c\u4ecd\u7136\u501a\u5899 \u89c2\u6218\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u6709\u70b9\u60ca\u8be7\uff0c\u60f3\u9053\uff0c\u201c\u6709\u4eba\u8bf4\u8fd9\u5c0f\u5b50\u66fe\u5f97\u8fc7\u91d1\u4e16\u9057\u7684\u4f20\u6388\uff0c\u5982\u4eca\u770b\u6765\uff0c\u4ed6\u7adf \u7136\u80fd\u89e3\u5f00\u6211\u6240\u70b9\u7684\u7a74\u9053\uff0c\u53ea\u6015\u662f\u771f\u7684\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u867d\u7136\u77e5\u9053\u6c5f\u5357\u5e95\u7ec6\uff0c\u5374\u4e5f\u5e76\u4e0d\u754f\u60e7\uff0c\u5f53\u4e0b\u6298\u6247\u4e00\u6325\uff0c\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff0c\u539f \u6765\u662f\u4f60\u3002\u6211\u90a3\u5c0f\u53ae\u773c\u5149\u4e0d\u9519\uff0c\u4ed6\u65e9\u770b\u51fa\u4f60\u662f\u4e2a\u5c0f\u8d3c\u3002\u600e\u4e48\uff0c\u5c31\u51ed\u4f60\u4e00\u4e2a\u4eba\u4fbf\u60f3\u89ca\u89ce\u6211\u8fd9\u7bb1\u73e0 \u5b9d\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u968f\u4fbf\u4f60\u53eb\u6211\u4ec0\u4e48\uff0c\u6211\u662f\u5c0f\u8d3c\uff0c\u4f60\u7684\u4e3b\u4eba\u5c31\u662f\u5927\u8d3c\uff0c\u4f60\u66ff\u5927\u8d3c\u641c\u522e\u73e0\u5b9d\uff0c\u4f60\u4e5f \u662f\u4e2a\u5c0f\u8d3c\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u7740\u5411\u56db\u65b9\u4f5c\u4e86\u4e00\u4e2a\u7f57\u5708\u63d6\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u6c5f\u5357\u662f\u4e2a\u5c0f\u89d2\u8272\uff0c\u4e0d \u6015\u4ed6\u7b11\u6211\u8f66\u8f6e\u6218\uff0c\u4e5f\u4e0d\u6015\u4ed6\u9a82\u6211\u6043\u4f17\u4e3a\u5f3a\uff0c\u4e0d\u8fc7\u6211\u8fd9\u5c0f\u8d3c\u5012\u60f3\u5148\u770b\u770b\u4ed6\u8fd9\u5c0f\u8d3c\u7684\u672c\u9886\u3002\u5217\u4f4d \u8981\u662f\u770b\u6211\u4e0d\u6210\u4e86\uff0c\u90a3\u65f6\u518d\u8bf7\u6765\u5e2e\u5fd9\uff01\u5582\uff0c\u5582\uff0c\u5c0f\u8d3c\uff0c\u4f60\u77aa\u7740\u773c\u775b\u5e72\u5417\uff1f\u4fa0\u52a8\u624b\u5427\uff01\u201d \u3000\u3000\u6c5f\u5357\u8fd9\u756a\u8bdd\u8bf4\u5f97\u5999\u6781\uff0c\u4ed6\u8d8a\u662f\u8d2c\u4f4e\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u5c31\u8d8a\u663e\u5f97\u662f\u8f7b\u89c6\u5bf9\u65b9\uff0c\u800c\u4e14\u662f\u5355\u72ec\u4e00\u4eba \u5411\u8fd9\u59d3\u6587\u7684\u5c11\u5e74\u6311\u6218\u3002\u6697\u9edd\u5904\u6709\u51e0\u4e2a\u4eba\u7b11\u51fa\u58f0\u6765\uff0c\u8d5e\u9053\uff1a\u201c\u6c5f\u5357\u8fd9\u5c0f\u5b50\u5012\u771f\u6709\u79cd\u3002\u201d \u3000\u3000\u59d3\u6587\u7684\u5c11\u5e74\u6012\u6c14\u6697\u751f\uff0c\u6298\u6247\u4e00\u6307\uff0c\u9a82\u9053\uff1a\u201c\u6cb9\u5634\u6ed1\u820c\uff0c\u4f60\u518d\u80e1\u8bf4\u516b\u9053\uff0c\u770b\u6211\u6253\u4f60\u8033 \u5149\u3002\u201d\u6c5f\u752b\u7b11\u9053\uff1a\u201c\u6709\u672c\u9886\u4f60\u5c31\u6765\u6253\u5427\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u773c\u524d\u4eba\u5f71\u4e00\u6643\uff0c\u90a3\u5c11\u5e74\u500f\u5730\u5c31\u6251\u4e86\u8fc7 \u6765\uff0c\u58f0\u5230\u4eba\u5230\uff0c\u5f53\u771f\u662f\u5feb\u6377\u65e0\u4f26\uff0c\u773c\u770b\u6c5f\u5357\u5c31\u8981\u7ed9\u4ed6\u6293\u4f4f\uff0c\u5374\u4e0d\u77e5\u600e\u7684\uff0c\u5c31\u5728\u90a3\u95f4\u4e0d\u5bb9\u53d1\u4e4b \u9645\uff0c\u8fd9\u5c11\u5e74\u4e00\u638c\u62cd\u4e0b\uff0c\u7adf\u7136\u62cd\u4e86\u4e2a\u7a7a\uff0c\u6c5f\u5357\u4e00\u95ea\u5f00\uff0c\u53eb\u9053\uff1a\u201c\u54ce\u6674\uff0c\u597d\u5389\u5bb3\uff01\u4e5f\u8fd8\u6ca1\u6709\u6253 \u7740\uff01\u201d\u539f\u6765\u4ed6\u7528\u7684\u662f\u91d1\u4e16\u9057\u6240\u6559\u7684\u201c\u5929\u7f57\u6b65\u6cd5\u201d\uff0c\u8fd9\u79cd\u6b65\u6cd5\uff0c\u5584\u4e8e\u5de7\u5999\u907f\u653b\uff0c\u5bf9\u4ed8\u5f3a\u654c\u6700\u6709 \u7528\u5904\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u559d\u9053\uff1a\u201c\u672a\u66fe\u4ea4\u624b\uff0c\u4fbf\u60f3\u6e9c\u4e48\uff1f\u201d\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u6e9c\uff1f\u6211\u4e0d\u662f\u7ad9\u5728\u4f60\u7684\u9762\u524d \u4e48\uff1f\u5c0f\u8d3c\uff0c\u6211\u662f\u597d\u5fc3\u597d\u610f\u8ba9\u4f60\u4e00\u62db\uff0c\u4f60\u5f53\u6211\u662f\u6015\u4f60\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u6298\u6247\u4e00\u5f20\uff0c\u559d\u9053\uff1a\u201c\u597d\u6837\u7684\uff0c\u522b\u8dd1\uff01\u201d\u6298\u6247\u5411\u6c5f\u5357\u8fce\u9762\u4e00\u62e8\uff0c\u6c5f\u5357\u731b\u89c9\u4e00\u80a1\u52b2\u98ce \u88ad\u6765\uff0c\u6b63\u60f3\u7528\u5927\u7f57\u6b65\u6cd5\uff0c\u7ed5\u8fc7\u654c\u4eba\u7684\u80cc\u540e\uff0c\u653b\u4ed6\u4e2a\u63aa\u624b\u4e0d\u53ca\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8fd9\u59d3\u6587\u7684\u5c11 \u5e74\u5728\u6298\u6247\u4e00\u6325\u4e4b\u540e\uff0c\u8ddf\u7740\u53c8\u662f\u4e00\u8bb0\u5288\u7a7a\u638c\u62cd\u51fa\u3002 \u3000\u3000\u4e24\u80a1\u52b2\u529b\u4e00\u67d4\u4e00\u521a\uff0c\u767b\u65f6\u4f3c\u5377\u8d77\u4e00\u4e2a\u65e0\u5f62\u7684\u6f29\u6da1\uff0c\u6c5f\u5357\u4e0d\u7531\u5f97\u811a\u6b65\u4e00\u4e2a\u8dea\u8e09\uff0c\u53ea\u542c\u5f97 \u201c\u6251\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u5c11\u5e74\u7684\u6247\u67c4\uff0c\u5df2\u6233\u4e2d\u4e86\u6c5f\u5357\u80cc\u5fc3\u7684\u201c\u5927\u690e\u7a74\u201d\uff0c\u8fd9\u7a74\u9053\u662f\u4eba\u8eab\u6b7b\u7a74\u4e4b\u4e00\uff0c \u8eb2\u5728\u6697\u5904\u89c2\u6218\u7684\u5404\u6d3e\u9ad8\u624b\uff0c\u6709\u597d\u51e0\u4e2a\u4eba\u5413\u5f97\u9a87\u53eb\u5931\u58f0\u3002 \u3000\u3000\u5c31\u5728\u90a3\u4e00\u5239\u90a3\u95f4\uff0c\u7d27\u63a5\u7740\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u5c11\u5e74\u7684\u957f\u888d\u5df2\u7ed9\u6c5f\u5357\u6495\u53bb\u4e86\u534a\u8fb9\uff0c\u90a3 \u5c11\u5e74\u4e0d\u77e5\u6c5f\u5357\u6709\u201c\u98a0\u5012\u7a74\u9053\u201d\u7684\u529f\u592b\uff0c\u7adf\u7ed9\u4ed6\u653b\u5f97\u4e2a\u63aa\u624b\u4e0d\u53ca\u3002\u53ef\u662f\u6c5f\u5357\u88ab\u4ed6\u7528\u201c\u91cd\u624b\u6cd5\u6253 \u7a74\u201d\u51fb\u4e2d\uff0c\u7a74\u9053\u867d\u7136\u672a\u7ed9\u5c01\u95ed\uff0c\u5374\u4e5f\u75bc\u75db\u96be\u5f53\u3002 \u3000\u3000\u5b9e\u5728\u8bf4\u6765\uff0c\u8fd8\u662f\u6c5f\u5357\u5403\u7684\u4e8f\u8f83\u5927\uff0c\u4e0d\u8fc7\uff0c\u90a3\u5c11\u5e74\u7684\u957f\u888d\u88ab\u6495\u53bb\u4e86\u534a\u8fb9\uff0c\u8868\u9762\u770b\u6765\uff0c\u5374\u662f \u66f4\u4e3a\u72fc\u72c8\u3002\u90a3\u5c11\u5e74\u6012\u4e0d\u53ef\u904f\uff0c\u521d\u65f6\u4ed6\u672c\u65e0\u610f\u53d6\u6c5f\u5357\u7684\u6027\u547d\uff0c\u8fd9\u65f6\u5374\u662f\u6298\u6247\u72c2\u6325\uff0c\u4e0b\u624b\u7edd\u4e0d\u7559 \u60c5\u3002\u8fd9\u65f6\u3002\u90a3\u5c11\u5e74\u5df2\u77e5\u9053\u6c5f\u5357\u957f\u4e8e\u70b9\u7a74\uff0c\u4e8e\u662f\u907f\u654c\u4e4b\u957f\uff0c\u653b\u654c\u4e4b\u77ed\uff0c\u4e0d\u628a\u6298\u6247\u5f53\u4f5c\u5224\u5b98\u7b14 \u7528\uff0c\u5374\u7528\u5b83\u4f7f\u51fa\u5200\u5251\u7684\u8def\u6570\uff0c\u62db\u6570\u5e72\u8106\u4e4b\u6781\uff01\u6c5f\u5357\u5bf9\u5404\u5bb6\u5404\u6d3e\u7684\u6b66\u672f\u90fd\u7565\u6709\u6240\u77e5\uff0c\u4f46\u5374\u4e0d\u66fe \u89c1\u8fc7\u8fd9\u7b49\u7684\u8fd9\u8def\u6b66\u529f\uff0c\u800c\u4e14\u90a3\u5c11\u5e74\u7684\u529f\u529b\u4e5f\u8981\u6bd4\u4ed6\u80dc\u8fc7\u4e00\u7b79\uff0c\u56e0\u6b64\uff0c\u6c5f\u5357\u5c3d\u5c55\u5e73\u751f\u6240\u5b66\uff0c\u4e5f \u4ec5\u662f\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u53ea\u542c\u5f97\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u6c5f\u5357\u53c8\u88ab\u90a3\u5c11\u5e74\u7684\u6298\u6247\u72e0\u72e0\u6572\u4e86\u4e00\u8bb0\uff0c\u4f46\u6c5f\u5357\u968f\u5373\u4f7f\u51fa \u201c\u9634\u9633\u6293\u201d\u7684\u529f\u592b\uff0c\u4e5f\u628a\u90a3\u5c11\u5e74\u8863\u886b\u53c8\u6495\u53bb\u4e86\u4e00\u5e45\u3002\u8fd9\u65f6\uff0c\u4eba\u4eba\u90fd\u770b\u5f97\u51fa\u6765\uff0c\u6c5f\u5357\u7684\u6b66\u529f\uff0c \u4e0e\u90a3\u5c11\u5e74\u53ea\u662f\u76f8\u5dee\u4e00\u7ebf\uff0c\u82e5\u7136\u6709\u4eba\u76f8\u52a9\uff0c\u7acb\u5373\u4fbf\u53ef\u53cd\u8d25\u4e3a\u80dc\u3002\u4f46\u82e5\u65e0\u4eba\u76f8\u52a9\uff0c\u4ed6\u591a\u6328\u51e0\u4e0b\uff0c \u5fc5\u5b9a\u8981\u53d7\u5185\u4f24\u3002 \u3000\u3000\u5404\u6d3e\u9ad8\u624b\u90fd\u8981\u987e\u7740\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u6709\u51e0\u4e2a\u4eba\u610f\u6b32\u76f8\u52a9\uff0c\u4f46\u4ecd\u8e0c\u8e87\u3002\u5ffd\u5730\u6709\u4e2a\u866c\u9aef\u5927\u6c49\u4ece\u5899 \u5934\u8df3\u4e0b\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u4e49\u4e4b\u8d22\uff0c\u4eba\u4eba\u53ef\u53d6\u3002\u73b0\u5728\u4e8b\u60c5\u5df2\u7ecf\u95f9\u5f00\u4e86\uff0c\u6211\u8001\u5f20\u4e5f\u60f3\u63d2\u4e00\u63d2\u624b\uff0c \u8bf7\u8bf8\u4f4d\u4e0d\u8981\u89c1\u602a\uff01\u201d \u3000\u3000\u8fd9\u5927\u6c49\u662f\u5c71\u4e1c\u7684\u72ec\u811a\u5927\u76d7\u5f20\u94c1\u80a9\uff0c\u5927\u60b2\u7985\u5e08\u672c\u5df1\u6258\u4e10\u5e2e\u7684\u5411\u9ed1\u9053\u6253\u8fc7\u62db\u547c\uff0c\u8981\u4ed6\u4eec\u5f85\u59ec \u6653\u98ce\u51fa\u73b0\u4e4b\u540e\uff0c\u624d\u53ef\u4ee5\u52ab\u8fd9\u5c11\u5e74\u7684\u73e0\u5b9d\uff0c\u4f46\u90a3\u65f6\u4ed6\u4eec\u5c1a\u672a\u77e5\u9053\u8fd9\u5c11\u5e74\u7684\u8eab\u4efd\u4e0e\u73e0\u5b9d\u7684\u6765\u5386\uff0c \u73b0\u5728\u6b63\u6d3e\u4e2d\u4eba\uff0c\u5df2\u5148\u540e\u6709\u4e86\u62b1\u62d9\u9053\u957f\u3001\u5d14\u4e91\u4eae\u8bf8\u4eba\u4e0e\u90a3\u5c11\u5e74\u4ea4\u624b\uff0c\u786e\u662f\u5982\u5f20\u94c1\u80a9\u6240\u8bf4\u201c\u4e8b\u60c5 \u5df2\u7ecf\u95f9\u5f00\u201d\uff0c\u4f9d\u5e38\u60c5\u800c\u8bba\uff0c\u59ec\u6653\u98ce\u5f53\u4e5f\u4e0d\u4f1a\u518d\u6765\u4e0a\u94a9\u4e86\u3002\u56e0\u6b64\uff0c\u7167\u6c5f\u6e56\u89c4\u77e9\uff0c\u5c31\u6ca1\u6709\u7406\u7531\u518d \u7981\u6b62\u9ed1\u9053\u7684\u4eba\u7269\u63d2\u624b\u3002 \u3000\u3000\u5c11\u6797\u5bfa\u4e24\u4f4d\u7985\u5e08\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u7fa4\u96c4\u552f\u4ed6\u4eec\u9a6c\u9996\u662f\u77bb\uff0c\u4e5f\u5c31\u65e0\u4eba\u51fa\u58f0\u7981\u6b62\u3002 \u3000\u3000\u5f20\u94c1\u80a9\u56db\u65b9\u4e00\u63d6\uff0c\u89c1\u65e0\u4eba\u53d1\u8bdd\uff0c\u7acb\u5373\u5927\u559d\u4e00\u58f0\uff0c\u5411\u90a3\u5c11\u5e74\u6253\u53bb\u3002\u4ed6\u7684\u62db\u5f0f\u751a\u602a\uff0c\u4f4e\u4e0b\u4e86 \u5934\uff0c\u53cc\u624b\u63e1\u62f3\uff0c\u906e\u5728\u989d\u524d\uff0c\u597d\u4f3c\u4e00\u53ea\u725b\u89d2\uff0c\u800c\u4ed6\u7684\u59ff\u52bf\uff0c\u4e5f\u5c31\u6070\u4f3c\u6597\u725b\u4e00\u822c\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u86ee\u725b\u4e5f\u6562\u6765\u4e48\uff1f\u201d\u53cd\u624b\u4e00\u638c\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u6b63\u6b63\u51fb\u4e2d\u4ed6\u7684\u80a9\u5934\uff0c \u8fd9\u6c49\u5b50\u540d\u5524\u5f20\u94c1\u80a9\uff0c\u80a9\u818a\u7684\u786e\u662f\u4e25\u5982\u94c1\u677f\uff0c\u5c11\u5e74\u4e00\u638c\u51fb\u4e0b\uff0c\u7adf\u7ed9\u4ed6\u53cd\u9707\u5f97\u5012\u9000\u4e24\u6b65\uff0c\u638c\u5fc3\u7834 \u88c2\uff0c\u6c81\u51fa\u8840\u4e1d\u3002 \u3000\u3000\u5f20\u94c1\u80a9\u5927\u53eb\u9053\uff1a\u201c\u597d\u8d3c\u5b50\uff0c\u4f60\u6562\u6253\u4f60\u8001\u5b50\uff01\u201d\u539f\u6765\u4ed6\u7ed9\u8fd9\u5c11\u5e74\u7528\u201c\u7ef5\u638c\u788e\u77f3\u201d\u7684\u529f\u592b\u4e00 \u51fb\uff0c\u5df2\u6709\u4e24\u6839\u80a9\u80bf\u9aa8\u65ad\u4e86\uff0c\u4f46\u4f24\u5728\u91cc\u9762\uff0c\u4f17\u4eba\u5374\u672a\u80fd\u770b\u51fa\uff0c\u8fd8\u5728\u7ed9\u4ed6\u559d\u5f69\u3002 \u3000\u3000\u5f20\u94c1\u80a9\u53eb\u9053\uff1a\u201c\u5c0f\u54e5\uff0c\u4f60\u6293\u4ed6\u7684\u9762\u95e8\uff1b\u6211\u518d\u6765\u7ed9\u4ed6\u4e00\u4e0b\uff01\u201d\u4fef\u9996\u5f2f\u8170\uff0c\u4ecd\u4f9d\u524d\u5f0f\uff0c\u53cc\u80a9 \u53c8\u5411\u90a3\u5c11\u5e74\u731b\u649e\u3002\u6760\u5357\u4f9d\u8a00\u6293\u4ed6\u7684\u9762\u95e8\uff0c\u90a3\u5c11\u5e74\u8981\u95ea\u5f00\u8fd9\u4e00\u649e\u5bb9\u6613\u4e4b\u6781\uff0c\u53ef\u662f\u6c5f\u5357\u8fd9\u4e00\u6293\u6070 \u6070\u5c01\u6625\u4e86\u4ed6\u7684\u9000\u8def\uff0c\u4ee4\u4ed6\u4e0d\u80fd\u4e0d\u4e88\u62db\u67b6\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5f20\u94c1\u80a9\u5df2\u51b2\u4e86\u5230\u6765\uff0c\u773c\u770b\u5c31\u8981\u649e \u4e2a\u6b63\u7740\uff0c\u5374\u5ffd\u7136\u6d88\u5931\u4e86\u90a3\u5c11\u5e74\u7684\u5f71\u5b50\u3002 \u3000\u3000\u539f\u6765\u5f20\u94c1\u80a9\u731b\u649e\u8fc7\u6765\u7684\u65f6\u5019\uff0c\u53cc\u817f\u5b7d\u5f20\uff0c\u90a3\u5c11\u5e74\u65e0\u53ef\u8eb2\u907f\uff0c\u4e8b\u6025\u667a\u751f\uff0c\u5ffd\u7136\u4e00\u77ee\u8eab\u8eaf\uff0c \u5c31\u4ece\u4ed6\u7684\u80ef\u4e0b\u94bb\u8fc7\u3002\u5f20\u94c1\u80a9\u4e00\u6115\uff0c\u9661\u89c9\u80cc\u5fc3\u5267\u75db\uff0c\u81c0\u90e8\u4e5f\u4f3c\u7ed9\u94c1\u68cd\u51b2\u649e\u4e00\u822c\uff0c\u767b\u65f6\u5411\u524d\u8dc4\u8dc4 \u8e09\u8e09\u7684\u5954\u51fa\u51e0\u6b65\uff0c\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\u3002\u539f\u6765\u90a3\u5c11\u5e74\u4e00\u94bb\u8fc7\u53bb\uff0c\u7acb\u5373\u4fbf\u5728\u4ed6\u80cc \u5fc3\u51fb\u4e86\u4e00\u638c\uff0c\u53c8\u91cd\u91cd\u5730\u8e22\u4e86\u4ed6\u4e00\u811a\u3002 \u3000\u3000\u5f20\u94c1\u80a9\u7ec3\u6709\u91d1\u949f\u7f69\u94c1\u5e03\u886b\u7684\u529f\u592b\uff0c\u4e0d\u8fc7\u4ed6\u7684\u80cc\u5fc3\u5374\u6ca1\u6709\u53cc\u80a9\u7ec3\u5f97\u8fd9\u4e48\u94a2\u786c\uff0c\u5403\u4e86\u8fd9\u638c\uff0c \u51e0\u4e4e\u7981\u53d7\u4e0d\u8d77\u3002\u53ef\u662f\u5f20\u94c1\u80a9\u662f\u4e2a\u6709\u540d\u7684\u786c\u6c49\uff0c\u53e3\u55b7\u9c9c\u8840\uff0c\u5374\u53cd\u800c\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60 \u4ece\u6211\u80ef\u4e0b\u722c\u8fc7\u53bb\uff0c\u5c31\u60f3\u6211\u9976\u4e86\u4f60\u4e48\uff1f\u8fd9\u8fd8\u4e0d\u6210\uff0c\u5f97\u518d\u78d5\u4e09\u4e2a\u54cd\u5934\u624d\u884c\u3002\u201d \u3000\u3000\u88ab\u8feb\u4ece\u522b\u4eba\u80ef\u4e0b\u722c\u8fc7\uff0c\u81ea\u53e4\u4ee5\u6765\uff0c\u90fd\u8ba4\u4e3a\u662f\u5947\u803b\u5927\u8fb1\u3002\u6c49\u671d\u7684\u97e9\u4fe1\uff0c\u5728\u8d2b\u8d31\u7684\u65f6\u5019\uff0c\u5c31 \u662f\u56e0\u4e3a\u88ab\u65e0\u8d56\u5c11\u5e74\u8feb\u4ed6\u4ece\u80ef\u4e0b\u722c\u8fc7\uff0c\u56e0\u800c\u53d1\u6124\u7684\u3002\u8fd9\u59d3\u6587\u7684\u5c11\u5e74\u6ca1\u6709\u97e9\u4fe1\u7684\u5ea6\u91cf\uff0c\u53d7\u4e86\u201c\u80ef \u4e0b\u4e4b\u8fb1\u201d\uff0c\u867d\u7136\u7acb\u5373\u4fbf\u4e88\u4ee5\u62a5\u590d\uff0c\u6253\u4e86\u5f20\u94c1\u80a9\u4e00\u638c\uff0c\u53c8\u8e22\u4e86\u4ed6\u4e00\u811a\uff0c\u4f46\u5728\u4f17\u76ee\u8dcc\u77aa\u4e4b\u4e0b\uff0c\u7ec8 \u89c9\u7f9e\u6127\u96be\u5f53\u3002\u5927\u6012\u559d\u9053\uff1a\u201c\u6211\u5148\u628a\u4f60\u8fd9\u86ee\u725b\u7684\u773c\u775b\u6316\u4e86\u3002\u201d \u3000\u3000\u4ed6\u624b\u6325\u6298\u6247\u654c\u4f4f\u6c5f\u5357\uff0c\u53e6\u4e00\u53ea\u624b\u5374\u4f38\u5f00\u53cc\u6307\uff0c\u5411\u5f20\u94c1\u80a9\u7740\u7740\u8fdb\u8feb\uff0c\u53cc\u6307\u5ffd\u4f38\u5ffd\u7f29\uff0c\u76f4\u6307 \u4ed6\u7684\u9762\u95e8\uff0c\u5f53\u771f\u662f\u8981\u6316\u4ed6\u7684\u773c\u775b\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u53c8\u6709\u4e24\u4e2a\u6c49\u5b50\u7a9c\u4e86\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u5f20\u5927\u54e5\uff0c\u8fd9\u7897\u6c34\u54b1\u4eec\u5927\u5bb6\u559d\u5566\u3002\u6211\u4f0f\u864e\u5be8\u4e5f\u6765 \u4e00\u4efd\u3002\u201d\u8fd9\u4e24\u4e2a\u4eba\u662f\u4f0f\u864e\u5be8\u7684\u5f53\u5bb6\u6c99\u5bb6\u5144\u5f1f\uff0c\u5728\u5317\u4e94\u7701\u4e5f\u662f\u53eb\u5f97\u54cd\u5b57\u53f7\u7684\u7eff\u6797\u4eba\u7269\u3002 \u3000\u3000\u57cb\u4f0f\u5728\u8fd9\u5ba2\u5e97\u4e2d\u7684\uff0c\u9664\u4e86\u5404\u6b63\u6d3e\u9ad8\u624b\u4e4b\u513f\u8fd8\u6709\u8bb8\u591a\u6c5f\u6e56\u5927\u76d7\uff0c\u5f20\u94c1\u80a9\u4e00\u53d1\u96be\uff0c\u4ed6\u4eec\u5df2\u7ecf \u8dc3\u8dc3\u6b32\u8bd5\uff0c\u8fd9\u65f6\u6c99\u5bb6\u5144\u5f1f\u53c8\u5df2\u6765\u52a8\u624b\uff0c\u6240\u6709\u7684\u9ed1\u9053\u4eba\u7269\uff0c\u767b\u65f6\u90fd\u4e89\u5148\u6050\u540e\u5730\u8dd1\u4e86\u51fa\u6765\uff0c\u7eb7\u7eb7 \u8bf4\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\uff0c\u6709\u798f\u540c\u4eab\uff0c\u6709\u96be\u540c\u5f53\uff0c\u6709\u80a5\u7f8a\u5927\u5bb6\u540c\u5241\uff01\u8fd9\u7897\u6c34\u5927\u5bb6\u559d\u5566\uff01\u201d \u3000\u3000\u6c99\u8001\u5927\u53eb\u9053\uff1a\u201c\u5206\u4e00\u4e9b\u4eba\u8fdb\u623f\u53bb\u641c\uff0c\u4e0d\u53ef\u8ba9\u90a3\u5c0f\u53ae\u6f0f\u7f51\u3002\u201d\u4ed6\u4eec\u4eba\u6570\u4f17\u591a\uff0c\u5206\u5175\u4e4b\u540e\uff0c \u8fd8\u6709\u4e03\u516b\u4e2a\u6b66\u529f\u9ad8\u5f3a\u7684\u5927\u76d7\uff0c\u5411\u90a3\u5c11\u5e74\u56f4\u653b\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u6b66\u529f\u518d\u597d\uff0c\u4e5f\u654c\u4e0d\u4f4f\u8fd9\u8bb8\u591a\u4eba\u7684\u56f4\u653b\uff0c\u53ef\u662f\u4ed6\u4e5f\u72e1\u733e\u975e\u5e38\uff0c\u672a\u5f85\u7fa4\u76d7\u5408\u56f4\uff0c\u4ed6\u5df2 \u9000\u5230\u4e00\u5904\u5899\u89d2\u3002 \u3000\u3000\u4ed6\u80cc\u9760\u5899\u58c1\uff0c\u51cf\u5c11\u4e86\u540e\u65b9\u7684\u5a01\u80c1\uff0c\u6325\u6247\u51fa\u638c\uff0c\u529b\u654c\u7fa4\u76d7\uff0c\u6298\u6247\u7528\u7684\u662f\u5224\u5b98\u7b14\u62db\u6570\uff0c\u53e6\u4e00 \u53ea\u624b\u7528\u7684\u5374\u662f\u8fd1\u8eab\u640f\u6597\u7684\u5c0f\u64d2\u62ff\u624b\u529f\u592b\u3002\u6709\u4e24\u4e2a\u201c\u72ec\u811a\u5927\u76d7\u201d\u8feb\u5f97\u592a\u8fd1\uff0c\u4e00\u4e2a\u88ab\u4ed6\u70b9\u4e2d\u80c1\u4e0b \u7684\u201c\u6108\u6c14\u7a74\u201d\uff0c\u95f7\u54fc\u4e00\u58f0\uff0c\u767b\u65f6\u5012\u5730\uff0c\u53e6\u4e00\u4e2a\u5219\u88ab\u4ed6\u626d\u6298\u4e86\u624b\u8155\uff0c\u66f4\u662f\u75db\u5f97\u6740\u732a\u822c\u7684\u5927\u53eb\u3002 \u3000\u3000\u7fa4\u76d7\u53d1\u4e00\u58f0\u558a\uff0c\u6539\u7528\u957f\u5175\u5668\u6233\u4ed6\uff0c\u65a9\u4ed6\uff0c\u90a3\u5c11\u5e74\u7684\u6b66\u529f\u786e\u662f\u7cbe\u5947\u5965\u5999\uff0c\u4ed6\u624b\u4e2d\u53ea\u6709\u4e00\u628a \u4e0d\u5230\u4e8c\u5c3a\u957f\u7684\u6298\u6247\uff0c\u4f46\u4ed6\u7528\u8fd9\u628a\u884d\u6247\u8fd9\u8fb9\u4e00\u6572\uff0c\u90a3\u8fb9\u4e00\u62e8\uff0c\u7adf\u7136\u4f7f\u51fa\u4e0a\u4e58\u6b66\u529f\u7684\u501f\u529b\u6253\u529b\u4e4b \u6cd5\uff0c\u5c06\u7532\u5f3a\u76d7\u6233\u8fc7\u6765\u7684\u957f\u77db\u62e8\u8fc7\u53bb\u78b0\u4e59\u5f3a\u76d7\u780d\u6765\u7684\u5927\u5200\uff0c\u5c06\u4e19\u5f3a\u76d7\u98de\u8fc7\u6765\u7684\u6d41\u661f\u9524\u8361\u5f00\u53bb\u649e \u4e01\u5f3a\u76d7\u78d5\u6765\u7684\u9752\u94dc\u94dc\u3002\u56e0\u6b64\uff0c\u4ed6\u867d\u7136\u662f\u88ab\u56f4\u5f97\u5bc6\u4e0d\u901a\u98ce\uff0c\u7fa4\u76d7\u5728\u8feb\u5207\u4e4b\u95f4\uff0c\u5374\u4e5f\u5948\u4ed6\u4e0d\u5f97\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5165\u623f\u641c\u7d22\u7684\u5f3a\u76d7\u51fa\u6765\u62a5\u9053\uff1a\u201c\u623f\u95f4\u91cc\u90fd\u641c\u904d\u4e86\uff0c\u90a3\u5c0f\u53ae\u4e5f\u7f1a\u8d77\u6765\u4e86\uff0c\u90a3\u7bb1\u73e0 \u5b9d\u5374\u672a\u66fe\u53d1\u73b0\u3002\u201d\u6c99\u8001\u5927\u9053\uff1a\u201c\u90a3\u4e00\u5b9a\u662f\u5728\u8fd9\u5c0f\u8d3c\u7684\u8eab\u4e0a\u4e86\u3002\u597d\u5440\uff0c\u4f60\u82e5\u4e0d\u662f\u4e56\u4e56\u7684\u5c06\u73e0\u5b9d \u732e\u51fa\u6765\uff0c\u54b1\u4eec\u53ea\u6709\u628a\u4f60\u4e71\u5200\u5206\u5c38\u4e86\u3002\u201d\u7fa4\u76d7\u8f70\u7136\u559d\u9053\uff1a\u201c\u5bf9\uff0c\u8fd9\u5c0f\u5b50\u4e0d\u77e5\u597d\u574f\uff0c\u54b1\u4eec\u4e00\u9f50\u4e0a \u53bb\uff0c\u5c06\u4ed6\u4e71\u5200\u5bb0\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5357\u5fc3\u6027\u5584\u826f\uff0c\u89c1\u90a3\u5c11\u5e74\u53d7\u5230\u7fa4\u76d7\u56f4\u653b\uff0c\u53cd\u800c\u611f\u5230\u6709\u70b9\u4e0d\u5fcd\uff0c\u4ed6\u4e0d\u4f4f\u7684\u529d\u90a3\u5c11\u5e74\u9053\uff1a \u201c\u94b1\u8d22\u662f\u8eab\u5916\u4e4b\u7269\uff0c\u4f60\u5c31\u62ff\u51fa\u6765\u5427\uff01\u8fd9\u7bb1\u73e0\u5b9d\u662f\u548c\u7837\u8001\u8d3c\u7684\u4e1c\u897f\uff0c\u4ed6\u7684\u73e0\u5b9d\u591a\u7740\u5462\uff0c\u4f60\u4f55\u82e6 \u4e3a\u4ed6\u5356\u547d\uff1f\u8ba9\u6c5f\u6e56\u4e0a\u4e00\u73ed\u82e6\u54c8\u54c8\u7684\u5144\u5f1f\u5206\u4e86\uff0c\u4e5f\u7b97\u5f97\u662f\u4f60\u505a\u4e86\u4e00\u6869\u597d\u4e8b\u5440\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u4e00\u58f0\u4e0d\u53d1\uff0c\u6839\u672c\u5c31\u4e0d\u7406\u776c\u4ed6\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u4ecd\u7136\u4f7f\u5c3d\u6d51\u8eab\u89e3\u6570\uff0c\u529b\u654c\u7fa4\u76d7\u56f4\u653b\uff1b\u6c5f\u5357 \u62ff\u4ed6\u6ca1\u6cd5\uff0c\u53ea\u6709\u81ea\u5df1\u628a\u653b\u52bf\u7f13\u4e86\u4e0b\u6765\uff0c\u867d\u7136\u4ecd\u662f\u88c5\u6a21\u4f5c\u52bf\u5411\u90a3\u5c11\u5e74\u653b\u51fb\uff0c\u5176\u5b9e\u5374\u53ea\u662f\u865a\u4e0e\u59d4 \u86c7\uff0c\u4e3a\u4e86\u5f20\u94c1\u80a9\u4e3a\u4ed6\u53d7\u4f24\uff1a\u5728\u8fd9\u5171\u540c\u5bf9\u654c\u4e4b\u9645\uff0c\u4e0d\u597d\u610f\u601d\u9000\u51fa\u800c\u5df2\u3002\u4e5f\u53ea\u662f\u56e0\u6b64\uff0c\u6240\u4ee5\u90a3\u59d3 \u6587\u7684\u5c11\u5e74\uff0c\u624d\u80fd\u591f\u52c9\u5f3a\u5e94\u4ed8\u7fa4\u76d7\u7684\u8fdb\u653b\u3002 \u3000\u3000\u6c99\u8001\u5927\u77a7\u51fa\u51e0\u5206\uff0c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u5c0f\u5144\u5f1f\uff0c\u4e0d\u8981\u6cc4\u6c14\u5440\uff0c\u52a0\u628a\u52b2\u5427\uff01\u4e5f\u6709\u4f60\u7684\u4e00\u4efd\uff01\u201d\u6c5f \u5357\u9053\uff1a\u201c\u73e0\u5b9d\u6211\u662f\u4e0d\u8981\u7684\u3002\u201d\u6c99\u8001\u5927\u9053\uff1a\u201c\u73e0\u5b9d\u4e0d\u8981\uff0c\u4e49\u6c14\u4f60\u603b\u5f97\u8bb2\u5440\u3002\u54b1\u4eec\u90fd\u662f\u5e2e\u4f60\u6765 \u7684\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u662f\u5440\uff0c\u6240\u4ee5\u6211\u867d\u7136\u6253\u5f97\u7d2f\u4e86\uff0c\u4e5f\u8fd8\u662f\u548c\u4f60\u4eec\u4e00\u9f50\u6253\u5440\u3002\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u4ed6\u603b\u89c9 \u5f97\u8fd9\u6837\u4ee5\u4f17\u51cc\u5be1\uff0c\u5b9e\u5728\u4e0d\u5927\u5149\u5f69\uff0c\u7d22\u6027\u4f7f\u51fa\u4e00\u5957\u82b1\u62f3\u7ee3\u817f\uff0c\u8868\u9762\u597d\u770b\uff0c\u5b9e\u5219\u5bf9\u654c\u4eba\u5e76\u65e0\u5a01 \u80c1\u3002\u53ef\u662f\uff0c\u6c5f\u5357\u867d\u7136\u4e0d\u80af\u51fa\u529b\uff0c\u7fa4\u76d7\u5374\u662f\u5168\u529b\u56f4\u653b\u3002 \u3000\u3000\u4f17\u5be1\u60ac\u6b8a\uff0c\u59d3\u6587\u90a3\u5c11\u5e74\u867d\u662f\u4f7f\u5c3d\u6d51\u8eab\u89e3\u6570\uff0c\u82e6\u82e6\u652f\u6491\uff0c\u5140\u81ea\u611f\u5230\u5de6\u652f\u53f3\u7ee7\uff0c\u9669\u8c61\u73af\u751f\uff0c \u6fc0\u6218\u4e2d\uff0c\u4f0f\u864e\u5be8\u7684\u6c99\u8001\u4e8c\u4e00\u67c4\u957f\u77db\u63b7\u51fa\uff0c\u76f4\u63d2\u5165\u5899\u4e2d\uff0c\u8981\u4e0d\u662f\u4ed6\u8eb2\u95ea\u5f97\u5feb\uff0c\u9669\u4e9b\u5c31\u8981\u7ed9\u957f\u77db \u9489\u5728\u5899\u4e0a\u3002 \u3000\u3000\u773c\u770b\u90a3\u5c11\u5e74\u7684\u6027\u547d\uff0c\u5c31\u8981\u4e27\u5728\u6307\u987e\u4e4b\u95f4\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u4eba\u58f0\u5608\u6742\uff0c\u6709\u4eba\u53eb\u9053\uff1a\u201c\u67e5\u591c\u7684\u6765 \u5566\uff01\u201d\u53ea\u89c1\u4e00\u4e2a\u519b\u5b98\uff0c\u5e26\u9886\u7740\u56db\u4e2a\u5175\u4e01\uff0c\u5df2\u662f\u7834\u95e8\u800c\u5165\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u9662\u5b50\u91cc\u8fd9\u4e00\u573a\u6076\u6597\uff0c\u65e9\u5c31\u60ca\u9192\u4e86\u5ba2\u5e97\u91cc\u6240\u6709\u7684\u4eba\uff0c\u4eba\u4eba\u90fd\u7ed9\u5413\u5f97\u5fc3\u60ca\u80c6\u6218\uff0c\u4e2a\u4e2a \u5173\u7d27\u4e86\u623f\u95e8\u3001\u8eb2\u5728\u88ab\u7a9d\u91cc\u9762\uff0c\u4e0d\u6562\u51fa\u6765\u3002\u67e5\u591c\u7684\u516c\u4eba\u5c31\u662f\u56e0\u4e3a\u4e0d\u89c1\u5e97\u4e3b\u5f00\u95e8\uff0c\u8fd9\u624d\u6253\u70c2\u4e86\u5927 \u95e8\uff0c\u6025\u6025\u5fd9\u5fd9\u5730\u51b2\u8fdb\u6765\u7684\u3002 \u3000\u3000\u56f4\u653b\u59d3\u6587\u8fd9\u5c11\u5e74\u7684\u4eba\uff0c\u90fd\u662f\u6c5f\u6e56\u4e0a\u8457\u540d\u7684\u5927\u76d7\uff0c\u6839\u672c\u5c31\u4e0d\u628a\u51e0\u4e2a\u5b98\u5175\u653e\u5728\u773c\u5185\uff0c\u85cf\u5728\u6811 \u4e0a\u3001\u5899\u5934\u3001\u5c4b\u9876\u7684\u5404\u6d3e\u9ad8\u624b\uff0c\u86a4\u7136\u4e0d\u6b32\u95f9\u4e8b\uff0c\u4f46\u5374\u4e5f\u6ca1\u6709\u4e00\u4e2a\u4eba\u79bb\u5f00\u3002 \u3000\u3000\u90a3\u519b\u5b98\u5927\u559d\u9053\uff1a\u201c\u5582\uff0c\u5582\uff0c\u4f60\u4eec\u662f\u5e72\u4ec0\u4e48\u7684\uff0c\u4e3a\u4ec0\u4e48\u5728\u8fd9\u91cc\u6253\u67b6\uff1f\u201d\u90a3\u59d3\u6587\u7684\u5c11\u5e74\u53eb \u9053\uff1a\u201c\u4ec0\u4e48\u6253\u67b6\uff1f\u8fd9\u73ed\u5f3a\u76d7\u662f\u8981\u52ab\u8d22\u5bb3\u547d\uff01\u201d\u90a3\u519b\u5b98\u8fd9\u65f6\u5927\u7ea6\u662f\u5df2\u770b\u6e05\u695a\u4e86\u662f\u7fa4\u76d7\u56f4\u653b\u4e00 \u4eba\uff0c\u5927\u5927\u5403\u60ca\uff0c\u8fde\u5fd9\u558a\u9053\uff1a\u201c\u52ab\u8d22\u5bb3\u547d\uff0c\u8fd9\u8fd8\u4e86\u5f97\uff1f\u54fc\uff0c\u54fc\uff0c\u4f60\u4eec\u76ee\u4e2d\u8fd8\u6709\u738b\u6cd5\u5417\uff1f\u5feb\u5feb\u4f4f \u624b\uff0c\u5feb\u5feb\u4f4f\u624b\uff0c\u542c\u6211\u95ee\u8bdd\uff01\u201d \u3000\u3000\u7fa4\u76d7\u54ea\u91cc\u80af\u542c\u4ed6\u7684\u5429\u5490\uff0c\u519b\u5b98\u5728\u4e00\u65c1\u529b\u7aed\u58f0\u5636\u7684\u559d\u505c\uff0c\u4ed6\u4eec\u5374\u66f4\u52a0\u9ad8\u547c\u9163\u6597\uff0c\u6709\u4e9b\u4eba\u8fd8 \u5728\u7b11\u9a82\u9053\uff1a\u201c\u516c\u95e8\u7684\u9e70\u722a\u5b59\uff0c\u4f60\u5c31\u5c11\u7ba1\u4e9b\u95f2\u4e8b\u5427\uff0c\u518d\u5728\u8fd9\u91cc\u80e1\u5439\u4e71\u53eb\uff0c\u5c0f\u5fc3\u8fde\u4f60\u7684\u76ae\u4e5f\u5265\u4e86 \u53bb\u3002\u201d \u3000\u3000\u90a3\u519b\u5b98\u5927\u6012\u559d\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7fd8\uff01\u771f\u662f\u4e00\u73ed\u76ee\u65e0\u738b\u6cd5\u7684\u51f6\u5f92\uff01\u628a\u4ed6\u4eec\u90fd\u62ff\u5230\u8859\u95e8\u53bb\uff01\u201d \u3000\u3000\u90a3\u56db\u4e2a\u5175\u4e01\u53d1\u4e00\u58f0\u558a\uff0c\u51b2\u5165\u76d7\u7fa4\u4e4b\u4e2d\uff0c\u7fa4\u76d7\u867d\u7136\u4e0d\u60e7\uff0c\u5374\u4e5f\u6709\u70b9\u8be7\u5f02\uff0c\u5fc3\u4e2d\u90fd\u5728\u60f3\u9053\uff1a \u201c\u8fd9\u51e0\u4e2a\u9e70\u722a\u5b59\u80c6\u91cf\u5012\u662f\u4e0d\u5c0f\u3002\u201d\u6c99\u8001\u4e8c\u62d4\u51fa\u957f\u77db\uff0c\u6b63\u8981\u5411\u90a3\u5c11\u5e74\u518d\u63b7\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c \u4e00\u4e2a\u5175\u4e01\u5df1\u7a9c\u5230\u4ed6\u7684\u8eab\u8fb9\uff0c\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d\u6c99\u8001\u4e8c\u6012\u9053\uff1a\u201c\u6eda\u5f00\uff0c\u522b\u5728\u8fd9\u513f\u788d\u624b\u788d\u811a\uff01\u201d \u98de\u8d77\u4e00\u811a\uff0c\u6b63\u8981\u8e22\u90a3\u5175\u4e86\uff0c\u5ffd\u89c9\u624b\u8155\u6709\u5982\u52a0\u4e86\u4e00\u9053\u7d27\u94b3\uff0c\u90a3\u5175\u4e01\u5df2\u628a\u4ed6\u6293\u4e86\u8d77\u6765\uff0c\u6c99\u8001\u5927\u5927 \u60ca\uff0c\u8fde\u5fd9\u5728\u4ed6\u80cc\u540e\u8d77\u811a\uff0c\u4e0d\u6599\u53e6\u4e00\u4e2a\u5175\u4e01\u53c8\u5df2\u8d76\u5230\uff0c\u4e00\u4e0b\u5b50\u5c31\u6258\u7740\u4ed6\u7684\u811a\u8e51\uff0c\u559d\u4e00\u58f0\uff1a \u201c\u53bb\uff01\u201d\u7adf\u7136\u628a\u4ed6\u629b\u51fa\u4e86\u56f4\u5899\uff01\u5c31\u5728\u8fd9\u65f6\uff0c\u6293\u7740\u4e86\u6c99\u8001\u4e8c\u7684\u90a3\u4e2a\u5175\u4e01\uff0c\u4e5f\u628a\u4ed6\u6454\u51fa\u4e86\u95e8\u5916\uff01 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u6551\u5175\u5ffd\u5c14\u4ece\u5929\u964d\uff0c\u5c82\u662f\u516c\u95e8\u4e0b\u9aa1\u6750\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000--------------- \u3000\u3000\u626c\u5251\u8f69\u5c45\u58eb \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e09\u56de\u3000\u79d8\u7c4d\u5949\u8fd8\u6c42\u66f2\u8c05\u3000\u4f5b\u73e0\u7a7a\u63b7\u6124\u96be\u5e73 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e09\u56de\u3000\u79d8\u7c4d\u5949\u8fd8\u6c42\u66f2\u8c05\u3000\u4f5b\u73e0\u7a7a\u63b7\u6124\u96be\u5e73 \u3000\u3000\u8fd9\u4e00\u4e0b\u7a81\u5982\u5176\u6765\u7684\u53d8\u5316\uff0c\u767b\u65f6\u4ee4\u5f97\u5168\u573a\u9707\u52a8\u3002\u5728\u6b64\u4e4b\u524d\uff0c\u8c01\u90e8\u4ee5\u4e3a\u8fd9\u51e0\u4e2a\u5175\u58eb\uff0c\u4e0d\u8fc7\u662f \u4ed7\u7740\u5b98\u5a01\uff0c\u865a\u5f20\u58f0\u52bf\u800c\u5df2\uff0c\u54ea\u77e5\u4ed6\u4eec\u7adf\u7136\u4e2a\u4e2a\u90e8\u6709\u771f\u624d\u5b9e\u5b66\uff0c\u4f0f\u864e\u5be8\u7684\u6c99\u5bb6\u5144\u5f1f\u5728\u7eff\u6797\u4e2d\u662f \u54cd\u5f53\u5f53\u7684\u89d2\u8272\uff0c\u4e0d\u8fc7\u4e00\u4e2a\u7167\u9762\uff0c\u5c31\u7ed9\u4ed6\u4eec\u6454\u5012\uff0c\u8fd9\u4efd\u80fd\u4e3a\uff0c\u5b9e\u5728\u5df2\u591f\u5f97\u4e0a\u79f0\u4e3a\u6b66\u6797\u4e2d\u7684\u4e00\u6d41 \u9ad8\u624b\uff01 \u3000\u3000\u5f20\u94c1\u80a9\u5927\u543c\u4e00\u58f0\uff0c\u4e00\u4f4e\u5934\u5c31\u5411\u90a3\u4e2a\u6b3a\u5230\u4ed6\u8eab\u524d\u7684\u5175\u4e01\u731b\u649e\uff0c\u5f20\u94c1\u80a9\u7684\u6b66\u529f\u662f\u7fa4\u76d7\u4e4b\u51a0\uff0c \u53d7\u4f24\u4e4b\u540e\uff0c\u8fd9\u4e00\u649e\u4ecd\u662f\u5a01\u731b\u975e\u5e38\uff0c\u90a3\u5175\u4e01\u53cc\u811a\u4e00\u70b9\uff0c\u51cc\u7a7a\u98de\u8d77\uff0c\u6b63\u8981\u6293\u4ed6\uff0c\u90a3\u519b\u5b98\u5ffd\u5730\u7b11 \u9053\uff1a\u201c\u8fd9\u662f\u4e00\u6761\u597d\u6c49\u5b50\uff0c\u4e0d\u8981\u5c06\u4ed6\u96be\u4e3a\u4e86\u3002\u201d\u4ed6\u5ffd\u5730\u633a\u8eab\u800c\u4e0a\uff0c\u5f20\u94c1\u80a9\u53cc\u80a9\u4e00\u649e\uff0c\u6b63\u597d\u4e0e\u4ed6 \u78b0\u4e2a\u6b63\u7740\uff0c\u90a3\u519b\u5b98\u9053\uff1a\u201c\u7ad9\u7a33\u4e86\uff01\u201d\u4f38\u624b\u5c06\u4ed6\u6276\u4f4f\uff0c\u5f20\u94c1\u80a9\u649e\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u6709\u5982\u649e\u7740\u4e86\u4e00\u5806 \u68c9\u82b1\uff0c\u811a\u6b65\u865a\u6d6e\uff0c\u4e0d\u7531\u5f97\u8eab\u5411\u524d\u503e\uff0c\u5e78\u90a3\u519b\u5b98\u5c06\u4ed6\u6276\u4f4f\u3002 \u3000\u3000\u5f20\u94c1\u80a9\u53eb\u9053\uff1a\u201c\u597d\u672c\u4e8b\uff0c\u51b2\u7740\u4f60\u8fd9\u4e00\u4e0b\uff0c\u6211\u59d3\u5f20\u7684\u81ea\u613f\u4e0d\u8d9f\u8fd9\u8d9f\u6d51\u6c34\u4e86\uff0c\u5c31\u4fbf\u5b9c\u4e86\u90a3\u5c0f \u4e86\u5427\uff01\u201d\u90a3\u519b\u5b98\u9053\uff1a\u201c\u597d\uff0c\u6211\u9886\u4f60\u8fd9\u4e2a\u60c5\u3002\u201d\u624b\u4e00\u677e\u5f00\uff0c\u5f20\u94c1\u80a9\u4fbf\u8dd1\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5f20\u94c1\u80a9\u4e00\u8dd1\uff0c\u7fa4\u76d7\u65e0\u4e0d\u5927\u60ca\u5931\u8272\uff0c\u6c5f\u5357\u6b63\u8981\u4e0a\u524d\uff0c\u5ffd\u542c\u5f97\u8033\u8fb9\u4e00\u4e2a\u4f3c\u66fe\u76f8\u8bc6\u7684\u5362\u97f3\uff0c\u9634 \u58f0\u7ec6\u6c14\u4ed6\u8bf4\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u4f60\u8fd8\u591f\u670b\u53cb\uff0c\u8fd9\u4ef6\u4e8b\u4f60\u4e5f\u4e0d\u5fc5\u7ba1\u5566\uff0c\u4e8b\u60c5\u8fc7\u540e\uff0c\u6211\u518d\u8bf7\u4f60\u559d\u9152\u3002\u201d \u3000\u3000\u6c5f\u5357\u6014\u4e86\u4e00\u6014\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u597d\uff0c\u4eba\u751f\u4f55\u5904\u4e0d\u76f8\u9022\uff0c\u4f60\u770b\u5f97\u8d77\u6211\uff0c\u6211\u6c5f\u5357\u4e5f\u613f\u610f\u4ea4 \u4ea4\u4f60\u8fd9\u4e2a\u670b\u53cb\u3002\u4e0d\u8fc7\uff0c\u8fd9\u573a\u70ed\u95f9\uff0c\u6211\u8fd8\u60f3\u770b\u5230\u7ec8\u573a\u3002\u201d \u3000\u3000\u90a3\u4e2a\u58f0\u97f3\uff0c\u53ea\u6709\u6c5f\u5357\u4e00\u4eba\u542c\u5230\uff0c\u56e0\u6b64\uff0c\u5728\u65c1\u4eba\u542c\u6765\uff0c\u5c31\u4f3c\u6c5f\u5357\u81ea\u8a00\u81ea\u8bed\u4e00\u822c\uff0c\u5927\u5bb6\u90fd\u89c9 \u5f97\u83ab\u540d\u5176\u5999\uff01\u53ea\u89c1\u6c5f\u5357\u9000\u51fa\u4eba\u4e1b\uff0c\u4f46\u5374\u4e0d\u5f20\u94c1\u80a9\u90a3\u6837\u8dd1\u51fa\u95e8\u5916\uff0c\u800c\u662f\u8dc3\u4e0a\u74e6\u9762\uff0c\u4ecd\u7136\u5750\u5728\u5c4b \u810a\u4e0a\u770b\u70ed\u95f9\u3002 \u3000\u3000\u62b1\u62d9\u9053\u4eba\u8fc7\u6765\u95ee\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u8fd9\u4e00\u4f19\u4eba\u4f60\u8ba4\u8bc6\u7684\u4e48\uff1f\u4ed6\u4eec\u771f\u662f\u5b98\u5175\uff1f\u201d\u6c5f\u5357\u6447\u6447\u5934\u9053\uff1a \u201c\u4e0d\u8ba4\u8bc6\u3002\u4e0d\u8fc7\uff0c\u6211\u6c5f\u5357\u8d70\u5357\u95ef\u5317\uff0c\u7565\u7565\u6709\u70b9\u540d\u5934\uff0c\u6216\u8005\u4ed6\u4eec\u4e4b\u4e2d\uff0c\u6709\u4eba\u8ba4\u5f97\u6211\uff0c\u90a3\u4e5f\u8bf4\u4e0d \u5b9a\u3002\u201d\u62b1\u62d9\u9053\u4eba\u4e5f\u77e5\u9053\u6c5f\u5357\u7d20\u6765\u6b22\u559c\u5439\u725b\uff0c\u4f46\u5374\u4e0d\u4f1a\u8bf4\u8c0e\uff0c\u56e0\u6b64\uff0c\u5bf9\u8fd9\u51e0\u4e2a\u5b98\u5175\u66f4\u662f\u8d77\u7591\u3002 \u5176\u5b9e\u6c5f\u5357\u542c\u4e86\u90a3\u4e2a\u58f0\u97f3\uff0c\u5fc3\u4e2d\u5df2\u662f\u60f3\u5230\u4e86\u4e00\u4e2a\u4eba\uff0c\u4e0d\u8fc7\uff0c\u76f8\u8c8c\u5374\u5b8c\u5168\u4e0d\u662f\u4e00\u6837\uff0c\u56e0\u6b64\u4ed6\u4e5f\u4e0d \u6562\u65ad\u5b9a\u3002 \u3000\u3000\u5728\u573a\u7684\u5404\u6d3e\u9ad8\u624b\u90fd\u662f\u6b66\u5b66\u884c\u5bb6\uff0c\u4eba\u4eba\u90fd\u770b\u51fa\u4e86\u8fd9\u4e2a\u519b\u5b98\u975e\u6bd4\u5bfb\u5e38\uff0c\u597d\u5947\u4e4b\u5fc3\u5927\u8d77\uff0c\u56fa\u6b64 \u7fa4\u76d7\u867d\u7136\u6563\u4e86\u4e00\u534a\uff0c\u4ed6\u4eec\u5374\u662f\u4e0d\u80af\u79bb\u573a\u3002 \u3000\u3000\u7528\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u8fd9\u4e00\u73ed\u7eb5\u6a2a\u6c5f\u6e56\u7684\u5927\u76d7\uff0c\u5df2\u8d70\u5f97\u4e00\u4e2a\u4e0d\u7559\u3002\u8fd9\u65f6\uff0c\u4e0d\u4f46\u65c1\u89c2\u7684\u5404\u6d3e\u9ad8 \u624b\u8d77\u7591\uff0c\u8fde\u90a3\u5c11\u5e74\u4e5f\u89c9\u5f97\u53e4\u602a\uff0c\u6309\u8bf4\u4ee5\u90a3\u519b\u5b98\u548c\u4ed6\u624b\u4e0b\u7684\u672c\u9886\uff0c\u82e5\u8981\u6349\u62ff\u7fa4\u76d7\uff0c\u90a3\u662f\u624b\u5230\u64d2 \u6765\uff0c\u53ef\u662f\u4ed6\u5374\u53ea\u662f\u7565\u663e\u8eab\u624b\uff0c\u6216\u628a\u5f3a\u76d7\u6454\u51fa\u95e8\u5916\uff0c\u6216\u8005\u53ea\u662f\u4ee4\u4ed6\u4eec\u53d7\u70b9\u65e0\u5173\u91cd\u8981\u7684\u521b\u4f24\uff0c\u6559 \u4ed6\u4eec\u81ea\u5df1\u77e5\u96be\u800c\u9000\u3002\u7eb5\u65e0\u201c\u7eb5\u76d7\u201d\u4e4b\u5acc\uff0c\u6700\u5c11\u4e5f\u662f\u65e0\u5fc3\u529e\u6848\u3002 \u3000\u3000\u59d3\u6587\u90a3\u5c11\u5e74\u6536\u8d77\u6298\u6247\uff0c\u4e0a\u524d\u5411\u90a3\u519b\u5b98\u65bd\u793c\uff0c\u8c22\u9053\uff1a\u201c\u591a\u8499\u5927\u4eba\u76f8\u6551\uff0c\u6562\u95ee\u9ad8\u59d3\u5927\u540d\uff1f\u201d \u90a3\u519b\u5b98\u5411\u4ed6\u6253\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u4e5f\u4e0d\u89c1\u4ed6\u5f00\u53e3\uff0c\u90a3\u5c11\u5e74\u5df2\u542c\u5230\u4e00\u4e2a\u58f0\u97f3\u5728\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u5c0f\u5fc3\uff0c\u771f \u6b63\u7684\u5f3a\u76d7\u8fd8\u5728\u8fd9\u91cc\u5462\uff01\u8fd9\u4e00\u73ed\u4eba\u7684\u672c\u9886\u6bd4\u90a3\u73ed\u5f3a\u76d7\u5927\u5f97\u591a\uff0c\u5185\u4e2d\u8fd8\u6709\u6765\u53bb\u65e0\u8e2a\u7684\u5999\u624b\u795e\u5077\uff0c \u4f60\u7684\u96be\u5173\u672a\u8fc7\uff0c\u8fd8\u8981\u63d0\u9632\u3002\u201d \u3000\u3000\u8fd9\u59d3\u6587\u7684\u5c11\u5e74\u5f53\u7136\u77e5\u9053\u8fd8\u6709\u5404\u6d3e\u9ad8\u624b\u7aa5\u4f3a\u5728\u65c1\uff0c\u53ef\u662f\u4ed6\u4e5f\u77e5\u9053\u4eca\u665a\u5230\u6765\u7684\u9ad8\u624b\uff0c\u672c\u4e8b\u6700 \u9ad8\u662f\u5c11\u6797\u5bfa\u4e24\u4f4d\u63b8\u5e08\uff0c\u53e6\u5916\u8fd8\u6709\u4e00\u4f4d\u5ce8\u5d4b\u6d3e\u7684\u540d\u5bbf\uff0c\u8fd9\u4e09\u4f4d\u6b66\u5b66\u5927\u5e08\uff0c\u51b3\u4e0d\u4f1a\u81ea\u8d2c\u8eab\u4efd\uff0c\u6043 \u591a\u4e3a\u4f17\uff0c\u5411\u4ed6\u4e00\u4e2a\u665a\u8f88\u56f4\u653b\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u542c\u5230\u4e86\u519b\u5b98\u8fd9\u4e2a\u8b66\u544a\uff0c\u4ecd\u662f\u7981\u4e0d\u4f4f\u5fc3\u5934\u4e00\u51db\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5f53\u4eca\u5929\u4e0b\uff0c\u79f0\u5f97\u4e0a\u6765 \u53bb\u65e0\u8e2a\u7684\u5999\u624b\u795e\u5077\uff0c\u53ea\u6709\u59ec\u6653\u98ce\u4e00\u4eba\uff0c\u96be\u9053\u4ed6\u4e5f\u6765\u4e86\uff1f\u542c\u8bf4\u5404\u5927\u95e8\u6d3e\u90fd\u8981\u641c\u6355\u4ed6\uff0c\u96be\u9053\u4ed6\u6562 \u516c\u7136\u5728\u8fd9\u91cc\u51fa\u73b0\uff1f\u201d\u518d\u5219\u60f3\u9053\uff1a\u201c\u8fd9\u519b\u5b98\u80fd\u591f\u95ed\u53e3\u4f20\u58f0\uff0c\u4f3c\u662f\u4f20\u8bf4\u4e2d\u6709\u2018\u5929\u9041\u4f20\u97f3\u2019\u4e4b\u672f\uff0c \u600e\u7684\u5728\u5b5f\u795e\u901a\u6b7b\u540e\uff0c\u8fd8\u6709\u4eba\u61c2\u5f97\u8fd9\u95e8\u529f\u592b\uff1f\u201d \u3000\u3000\u90a3\u519b\u5b98\u5ffd\u5730\u638f\u51fa\u4e00\u7eb8\u516c\u6587\uff0c\u8e0f\u4e0a\u4e00\u6b65\uff0c\u9012\u4e0e\u90a3\u4e2a\u5c11\u5e74\uff0c\u5c11\u5e74\u4e00\u770b\uff0c\u5374\u662f\u4e00\u5f20\u59d4\u4efb\u7684\u6587 \u4e66\u3002\u539f\u6765\u8fd9\u4e2a\u519b\u5b98\u4e43\u662f\u5fa1\u6797\u519b\u7684\u519b\u5b98\uff0c\u4ed6\u638f\u51fa\u6765\u7ed9\u8fd9\u5c11\u5e74\u8fc7\u76ee\uff0c\u4e0d\u95ee\u53ef\u77e5\uff0c\u4e43\u662f\u8981\u5411\u5c11\u5e74\u8868 \u660e\u4ed6\u7684\u8eab\u4efd\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u604d\u7136\u5927\u609f\uff0c\u5fc3\u9053\uff1a\u201c\u6562\u60c5\u6b64\u4eba\u4e43\u662f\u5949\u4e86\u547d\u4ee4\uff0c\u6697\u4e2d\u7167\u987e\u6211\u7684\u3002\u60f3\u4e0d\u5230\u5fa1\u6797\u519b\u4e2d\uff0c \u7adf\u6709\u5982\u6b64\u9ad8\u624b\uff0c\u771f\u662f\u60ed\u6127\uff0c\u60ed\u6127\uff01\u201d\u8fd9\u5c11\u5e74\u672c\u60f3\u51ed\u7740\u4e00\u8eab\u6280\u4e1a\uff0c\u62bc\u8fd0\u4e86\u8fd9\u6279\u73e0\u5b9d\u4e4b\u540e\uff0c\u4fbf\u53ef \u4ee5\u5728\u4eac\u534e\u95ef\u8361\u6210\u540d\u3002\u5982\u4eca\u770b\u6765\uff0c\u53ea\u8fd9\u4e00\u4e2a\u519b\u5b98\u7684\u672c\u9886\u4fbf\u9ad8\u8fc7\u81ea\u5df1\uff0c\u5fa1\u6797\u519b\u4e2d\u8fd8\u4e0d\u77e5\u6709\u591a\u5c11\u4eba \u6750\uff1f\u600e\u4e0d\u53eb\u4ed6\u5fc3\u7070\u610f\u51b7\uff1f \u3000\u3000\u90a3\u5c11\u5e74\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u5730\u53c8\u542c\u5f97\u90a3\u519b\u5b98\u7684\u58f0\u97f3\u5728\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u5feb\u628a\u4f60\u90a3\u7bb1\u73e0\u5b9d\u7ed9\u6211\uff0c \u5feb\uff01\u201d\u90a3\u5c11\u5e74\u6014\u4e86\u4e00\u6014\uff0c\u90a3\u519b\u5b98\u7684\u58f0\u97f3\u53c8\u5df2\u5728\u4ed6\u8eab\u8fb9\u6025\u4fc3\u5730\u53eb\u9053\uff1a\u201c\u4f60\u90a3\u7bb1\u73e0\u5b9d\u5c31\u85cf\u5728\u90a3\u91cc \u662f\u4e0d\u662f\uff1f\u7cdf\u7cd5\uff0c\u7cdf\u7cd5\uff0c\u90a3\u4f4d\u5929\u4e0b\u7b2c\u4e00\u795e\u5077\u5df2\u7ecf\u6765\u4e86\uff0c\u4f60\u77a7\uff0c\u4ed6\u3001\u4ed6\u3001\u4ed6\u3001\u4ed6\u201d \u3000\u3000\u9976\u662f\u8fd9\u59d3\u6587\u7684\u5c11\u5e74\u7cbe\u660e\u80fd\u5e72\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u4e5f\u4e0d\u7531\u5f97\u9661\u7136\u5fc3\u60ca\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4ed6\u8eab\u5f62 \u65b9\u8d77\uff0c\u90a3\u519b\u5b98\u5df2\u7ecf\u6bd4\u4ed6\u62a2\u5feb\u4e86\u4e24\u6b65\uff0c\u5ffd\u5730\u4ece\u4e00\u4e2a\u6811\u7abf\u91cc\u638f\u51fa\u4e86\u4e00\u4e2a\u7bb1\u5b50\u6765\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9 \u7bb1\u73e0\u5b9d\uff0c\u4e0e\u5176\u9001\u7ed9\u548c\u7837\uff0c\u4e0d\u5982\u9001\u7ed9\u6211\u5427\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u5c11\u5e74\u5de5\u4e8e\u5fc3\u8ba1\uff0c\u9662\u5b50\u91cc\u6709\u51e0\u682a\u8001\u69d0\u6811\uff0c\u6bcf\u682a\u69d0\u6811\uff0c\u6811\u6839\u6811\u5e72\uff0c\u90fd\u6709\u866b\u8681\u6240\u81f3\u7684\u6811 \u7abf\uff0c\u4ed6\u5c06\u73e0\u5b9d\u85cf\u5728\u4e00\u4e2a\u6811\u7abf\u4e4b\u4e2d\uff0c\u7fa4\u76d7\u54ea\u91cc\u6599\u60f3\u5f97\u5230\uff0c\u4eba\u4eba\u90fd\u4ee5\u4e3a\u4ed6\u4e0d\u662f\u968f\u8eab\u643a\u5e26\uff0c\u4fbf\u662f\u85cf \u5728\u623f\u95f4\uff0c\u4ed6\u5373\u7b97\u5931\u624b\u88ab\u64d2\uff0c\u73e0\u5b9d\u4e5f\u4e0d\u4f1a\u5931\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u9053\u201c\u519b\u5b98\u201d\u5374\u4f1a\u7528\u201c\u865a\u58f0\u606b\u5413\u201d\u8fd9\u4e00\u7edd\u62db\uff0c\u4efb\u4f55\u7cbe\u660e\u7684\u4eba\u5728\u8fd9\u6837\u7d27\u5f20\u60ca\u60f6\u7684\u65f6\u523b\uff0c \u9996\u5148\u90fd\u4f1a\u6ce8\u610f\u81ea\u5df1\u6700\u5173\u5fc3\u3001\u6700\u8d35\u91cd\u7684\u4e1c\u897f\u3002\u90a3\u5c11\u5e74\u5f53\u7136\u4e5f\u4e0d\u4f8b\u5916\uff0c\u4ed6\u88ab\u90a3\u519b\u5b98\u4e00\u5413\uff0c\u773c\u5149\u4e0d \u7531\u5411\u90a3\u682a\u69d0\u6811\u671b\u53bb\uff0c\u8fd9\u519b\u5b98\u4f55\u7b49\u5389\u5bb3\uff0c\u7acb\u5373\u4fbf\u6709\u5982\u63a2\u56ca\u53d6\u7269\u4e00\u822c\u5c06\u90a3\u7bb1\u73e0\u5b9d\u624b\u5230\u62ff\u6765\uff01 \u3000\u3000\u5c11\u5e74\u5927\u5403\u4e00\u60ca\uff0c\u6b63\u5728\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u201d\u4ed6\u7684\u8bdd\u58f0\u5c1a\u672a\u51fa\u53e3\uff0c\u53ea\u542c\u5f97\u5df2\u6709\u4eba\u62a2\u5148 \u53eb\u9053\uff1a\u201c\u59ec\u6653\u98ce\uff01\u201d\u201c\u597d\u5440\uff0c\u4f60\u597d\u5927\u7684\u80c6\u5b50\uff01\u201d\u5c11\u6797\u5bfa\u4e24\u4f4d\u7985\u5e08\u540c\u65f6\u5954\u51fa\uff0c\u5927\u96c4\u7985\u5e08\u4e00\u6296\u624b \u5c06\u4e00\u767e\u96f6\u516b\u73e0\u90fd\u6563\u4e86\u5f00\u6765\uff0c\u4ee5\u201c\u5929\u5973\u6563\u82b1\u201d\u7684\u624b\u6cd5\uff0c\u5411\u90a3\u519b\u5b98\u6253\u53bb\uff01 \u3000\u3000\u4e0d\u9519\uff0c\u8fd9\u519b\u5b98\u6b63\u662f\u59ec\u6653\u98ce\uff0c\u4ed6\u90a3\u5f20\u59d4\u4efb\u72b6\uff0c\u662f\u4ee5\u524d\u4ed6\u5728\u5927\u5185\u603b\u7ba1\u5bc7\u65b9\u768b\u5bb6\u4e2d\uff0c\u4ece\u90a3\u4f4d\u524d \u4efb\u7684\u5fa1\u6797\u519b\u7edf\u9886\u53f8\u7a7a\u5316\u8eab\u4e0a\u5077\u6765\u7684\u3002\u90a3\u65f6\u53f8\u7a7a\u5316\u4e3a\u4e86\u4e0e\u5bc7\u65b9\u768b\u4f1a\u5546\u8bdb\u9504\u5929\u4e0b\u6b66\u6797\u4eba\u58eb\u4e4b\u4e8b\uff0c \u8981\u6311\u9009\u90e8\u4e0b\uff0c\u6545\u800c\u968f\u8eab\u5e26\u6709\u8bb8\u591a\u7a7a\u767d\u7684\u201c\u6298\u5b50\u201d\uff0c\u586b\u4e0a\u540d\u5b57\uff0c\u4fbf\u662f\u59d4\u4efb\u72b6\u3002\u59ec\u6653\u98ce\u5077\u4e86\u4e00 \u5f20\uff0c\u8fd9\u51e0\u5e74\u6765\u4ece\u672a\u7528\u8fc7\uff0c\u4eca\u665a\u662f\u7b2c\u4e00\u6b21\u4f7f\u7528\uff0c\u679c\u7136\u9a97\u8fc7\u4e86\u8fd9\u7cbe\u660e\u7684\u6587\u59d3\u5c11\u5e74\u3002\u81f3\u4e8e\u90a3\u56db\u4e2a\u5175 \u4e01\uff0c\u4e43\u662f\u4ed6\u7684\u5f92\u5f1f\u4e54\u88c5\u7684\u3002\u59ec\u6653\u98ce\u662f\u5929\u4e0b\u7b2c\u4e00\u795e\u5077\uff0c\u6613\u5bb9\u53d8\u8c8c\u4e4b\u672f\u7cbe\u5999\u65e0\u6bd4\uff0c\u6545\u4e4b\uff0c\u5373\u7b97\u662f \u5c11\u6797\u5bfa\u90a3\u4e24\u4f4d\u9ad8\u50e7\uff0c\u4e5f\u662f\u76f4\u5230\u6b64\u523b\uff0c\u770b\u6e05\u695a\u4e86\u4ed6\u7684\u8eab\u624b\uff0c\u624d\u6562\u786e\u5b9a\u662f\u4ed6\u3002 \u3000\u3000\u5927\u96c4\u63b8\u5e08\u4e00\u58f0\u53eb\u558a\uff0c\u6709\u5982\u6674\u5929\u54cd\u8d77\u4e86\u9739\u96f3\uff0c\u5404\u6d3e\u9ad8\u624b\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u90fd\u73b0\u51fa\u8eab\u6765\uff01\u8fd9\u65f6\uff0c \u4eba\u4eba\u90fd\u628a\u6ce8\u610f\u529b\u653e\u5230\u59ec\u6653\u98ce\u8eab\u4e0a\uff0c\u4e0d\u518d\u7ba1\u90a3\u59d3\u6587\u7684\u5c11\u5e74\u4e86\u3002 \u3000\u3000\u5927\u96c4\u7985\u5e08\u8fd9\u4e00\u624b\u201c\u5b9a\u73e0\u964d\u9b54\u201d\u795e\u529f\uff0c\u4e43\u662f\u51fa\u81ea\u5c11\u6797\u65b9\u4e08\u75db\u7985\u4e0a\u4eba\u7684\u771f\u4f20\uff0c\u5389\u5bb3\u65e0\u6bd4\uff0c\u4f46 \u89c1\u5ff5\u73e0\u7eb7\u98de\uff0c\u4ece\u56db\u9762\u516b\u65b9\u88ad\u5230\uff0c\u5c06\u59ec\u6653\u98ce\u7684\u8eab\u5f62\u5168\u90fd\u7f69\u4f4f\uff01 \u3000\u3000\u59ec\u6653\u98ce\u8d5e\u9053\uff1a\u201c\u4f5b\u95e8\u5927\u6cd5\uff0c\u679c\u7136\u662f\u975e\u540c\u5c0f\u53ef\u3002\u201d\u5ffd\u5730\u4e00\u58f0\u957f\u5578\uff0c\u5439\u6c14\u6210\u98ce\u3002\u8bf4\u4e5f\u5947\u602a\uff0c \u90a3\u4e00\u767e\u96f6\u516b\u9897\u5ff5\u73e0\uff0c\u5230\u4e86\u4ed6\u7684\u8ddf\u524d\uff0c\u5ffd\u4f3c\u53d7\u4e86\u4e00\u80a1\u65e0\u5f62\u7684\u963b\u529b\u4e00\u822c\uff0c\u6765\u52bf\u987f\u7f13\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c \u5947\u666f\u51fa\u73b0\uff0c\u90a3\u4e9b\u5ff5\u73e0\uff0c\u7adf\u4f3c\u6295\u5165\u6d77\u4e2d\u7684\u6c99\u77f3\uff0c\u53d7\u7740\u6697\u6d41\u6fc0\u8361\uff0c\u8f7d\u6d6e\u8f7d\u6c89\uff0c\u5728\u59ec\u6653\u98ce\u7684\u8eab\u8fb9\u6253 \u7740\u5708\u5708\uff0c\u5374\u53ea\u662f\u6253\u4e0d\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u518d\u8fc7\u7247\u523b\uff0c\u59ec\u6653\u98ce\u731b\u5730\u5927\u559d\u4e00\u58f0\uff0c\u90a3\u4e00\u767e\u96f6\u516b\u9897\u5ff5\u73e0\u604d\u5982 \u6d41\u661f\u70b9\u70b9\uff0c\u7eb7\u7eb7\u5760\u5730\u3002 \u3000\u3000\u5927\u96c4\u7985\u5e08\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u53ae\u7adf\u53c2\u900f\u4e86\u592a\u865a\u771f\u7ecf\u7684\u4e0a\u4e58\u5fc3\u6cd5\uff0c\u7ec3\u6210\u4e86\u9632\u8eab\u7684 \u6c14\u529f\uff01\u201d\u539f\u6765\u59ec\u6653\u98ce\u4ece\u5c11\u6797\u5bfa\u5077\u53bb\u4e09\u5377\u5185\u5bb6\u79d8\u5178\uff0c\u5176\u4e2d\u6709\u4e00\u5377\u4fbf\u662f\u7ec3\u6c14\u7684\u592a\u865a\u771f\u7ecf\uff0c\u82e5\u662f\u7ec3 \u5230\u6700\u9ad8\u5883\u754c\uff0c\u7aef\u7684\u53ef\u4ee5\u5200\u67aa\u4e0d\u5165\uff0c\u800c\u4e14\u6697\u5668\u4e00\u5230\u8ddf\u524d\uff0c\u4fbf\u8981\u7ed9\u65e0\u5f62\u7684\u7f61\u6c14\u9707\u843d\uff0c\u90a3\u662f\u6bd4\u6cbe\u8863 \u5341\u516b\u8dcc\u7684\u4e0a\u4e58\u5185\u529f\u8fd8\u8981\u5389\u5bb3\u5f97\u591a\u4e86\u3002\u73b0\u5728\u59ec\u6653\u98ce\u8fd8\u672a\u7ec3\u5230\u8fd9\u4e2a\u5883\u754c\uff0c\u53ef\u662f\u5927\u96c4\u7985\u5e08\u6240\u7528\u7684 \u201c\u5b9a\u73e0\u964d\u9b54\u795e\u529f\u201d\u4e5f\u8fd8\u6bd4\u4e0d\u4e0a\u4ed6\u7684\u5e08\u7236\u2014\u2014\u5c11\u6797\u5bfa\u65b9\u4e08\u75db\u7985\u4e0a\u4eba\u7684\u529f\u529b\uff0c\u6240\u4ee5\u4e0e\u59ec\u6653\u98ce\u8f83\u91cf \u8d77\u6765\uff0c\u4fbf\u76f8\u5f62\u89c1\u7ecc\u4e86\u3002 \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u6211\u7528\u4ece\u8d35\u6d3e\u5077\u6765\u7684\u529f\u592b\uff0c\u5f53\u771f\u662f\u73ed\u95e8\u5f04\u65a7\uff0c\u8d3b\u7b11\u5927\u65b9\u3002\u627f\u8499\u63b8\u5e08\u5ff5\u5728\u540c \u51fa\u4e00\u6e90\uff0c\u624b\u4e0b\u7559\u60c5\uff0c\u6211\u8fd9\u53a2\u6709\u793c\u4e86\u3002\u201d\u4ed6\u76f4\u627f\u8fd9\u662f\u4ece\u5c11\u6797\u5bfa\u5077\u6765\u7684\u529f\u592b\uff0c\u5927\u96c4\u7985\u5e08\u542c\u4e86\uff0c\u66f4 \u89c9\u5f97\u523a\u8033\u94bb\u5fc3\uff0c\u9976\u4ed6\u6db5\u517b\u518d\u597d\uff0c\u4e5f\u7981\u4e0d\u4f4f\u9a82\u9053\uff1a\u201c\u65e0\u803b\u5c0f\u8d3c\uff0c\u8c01\u4e0e\u4f60\u540c\u51fa\u4e00\u6e90\uff1f\u770b\u638c\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u7985\u5e08\u4e4b\u8a00\u5dee\u77e3\uff0c\u6211\u59ec\u67d0\u82e5\u7136\u53ea\u662f\u2018\u5c0f\u8d3c\u201d\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u8fd8\u6709\u8c01\u914d\u79f0 \u4e3a\u5927\u8d3c\uff1f\u201d\u542c\u4ed6\u7684\u8bed\u6c14\uff0c\u7adf\u662f\u4ee5\u4ed6\u7684\u795e\u5077\u7edd\u6280\u4e3a\u8363\uff0c\u5bf9\u5927\u96c4\u7985\u5e08\u9a82\u4ed6\u4f5c\u201c\u5c0f\u8d3c\u201d\uff0c\u53cd\u800c\u4e0d\u670d \u6c14\u4f3c\u7684\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4ed6\u8bdd\u58f0\u672a\u4e86\uff0c\u5927\u96c4\u7985\u5e08\u90a3\u4e00\u638c\u5df2\u7136\u6253\u4e86\u5230\u6765\uff0c\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u5927\u548c\u5c1a \u4e0d\u5fc5\u52a8\u6c14\uff0c\u54b1\u4eec\u627e\u4e2a\u5730\u65b9\u8bf4\u8bdd\u3002\u201d\u4ed6\u53cc\u80a9\u4e00\u6c89\uff0c\u5927\u96c4\u7985\u5e08\u4e00\u638c\u6253\u4e0b\uff0c\u52b2\u529b\u5df2\u88ab\u4ed6\u5378\u53bb\u4e86\u4e03\u516b \u5206\uff0c\u624b\u638c\u62cd\u7740\u4ed6\u7684\u80a9\u818a\uff0c\u4f46\u89c9\u6ed1\u4e0d\u7559\u624b\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u4fbf\u7ed9\u4ed6\u6e9c\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u5927\u60b2\u7985\u5e08\u9a82\u9053\uff1a\u201c\u597d\u8d3c\u5b50\uff0c\u5f80\u54ea\u91cc\u8d70\uff1f\u201d\u62c2\u5c18\u4e00\u6296\uff0c\u5411\u59ec\u6653\u98ce\u8fce\u9762\u626b\u53bb\uff0c\u59ec\u6653\u98ce\u7b11\u9053\uff1a \u201c\u597d\u8d3c\u7684\u79f0\u547c\u6bd4\u5c0f\u8d3c\u597d\u5f97\u591a\uff0c\u5927\u548c\u5c1a\uff0c\u591a\u627f\u4f60\u9752\u7750\u6709\u52a0\u4e86\uff01\u201d\u4e00\u53e3\u6c14\u5439\u5c06\u8fc7\u53bb\uff0c\u54ea\u77e5\u5927\u60b2\u7985 \u5e08\u4e43\u662f\u5c11\u6797\u5bfa\u5341\u516b\u7f57\u6c49\u4e4b\u9996\uff0c\u529f\u592b\u8981\u6bd4\u5927\u96c4\u63b8\u5e08\u53c8\u80dc\u4e00\u7b79\uff0c\u59ec\u6653\u98ce\u4e00\u53e3\u6c14\u7ec8\u867d\u7136\u628a\u62c2\u5c18\u5439\u5f97 \u6563\u5f00\uff0c\u4f46\u4ed6\u7684\u8863\u88f3\u88ab\u62c2\u5c18\u5c3e\u62c2\u4e2d\uff0c\u987f\u7136\u51fa\u73b0\u4e86\u5341\u51e0\u9053\u88c2\u7f1d\uff01 \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u6211\u6b63\u8ba8\u538c\u62ab\u7740\u8fd9\u4ef6\u8001\u864e\u76ae\uff0c\u591a\u8c22\u7985\u5e08\u6240\u8d50\u6b64\u4e00\u62db\uff0c\u6211\u4e5f\u597d\u8d81\u6b64\u8fd8\u6211\u672c\u6765 \u9762\u76ee\u3002\u201d\u4ed6\u6253\u4e86\u4e00\u4e2a\u76d8\u65cb\uff0c\u4ee5\u654f\u6377\u65e0\u6bd4\u7684\u624b\u6cd5\uff0c\u8131\u4e0b\u4e86\u5916\u9762\u7684\u8863\u88f3\uff0c\u5c31\u50cf\u53d8\u620f\u6cd5\u4e00\u822c\uff0c\u5f53\u4ed6 \u518d\u8f6c\u8fc7\u8eab\u6765\uff0c\u9762\u5411\u7740\u5927\u60b2\u7985\u5e08\u4e4b\u65f6\uff0c\u5df2\u662f\u5b8c\u5168\u6362\u4e86\u4e2a\u6a21\u6837\uff0c\u539f\u6765\u4ed6\u5907\u6709\u5404\u79cd\u4eba\u76ae\u9762\u5177\uff0c\u5316\u88c5 \u6210\u5404\u5f0f\u4eba\u7b49\uff0c\u7ef4\u5999\u7ef4\u8096\uff0c\u9762\u5177\u4e00\u5265\u4e0b\u6765\uff0c\u4fbf\u6062\u590d\u4e86\u672c\u6765\u9762\u76ee\u3002\u4f46\u4ed6\u7684\u624b\u6cd5\u5927\u5feb\uff0c\u4f17\u4eba\u53ea\u89c1\u4ed6 \u7684\u8131\u8863\u52a8\u4f5c\uff0c\u770b\u4e0d\u5230\u4ed6\u5265\u4e0b\u9762\u5177\u7684\u52a8\u4f5c\uff0c\u56e0\u6b64\uff0c\u7a81\u7136\u95f4\u89c1\u4ed6\u6362\u4e86\u4e2a\u6a21\u6837\uff0c\u90fd\u4e0d\u7981\u6115\u4e86\u4e00\u6115\u3002 \u3000\u3000\u59ec\u6653\u98ce\u4f7f\u51fa\u5929\u7f57\u6b65\u6cd5\uff0c\u8d81\u7740\u4f17\u4eba\u60ca\u6115\u4e4b\u9645\uff0c\u500f\u5730\u5c31\u7a7f\u8fc7\u4eba\u7fa4\uff0c\u5954\u51fa\u5927\u95e8\uff0c\u5927\u60b2\u7985\u5e08\u62c2\u5c18 \u518d\u5c55\uff0c\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6295\u6843\u62a5\u674e\uff0c\u6211\u9001\u8001\u7985\u5e08\u4e00\u4ef6\u4e1c\u897f\u3002\u201d\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u4ef6\u9ed1\u5ffd\u5ffd\u7684\u4e1c\u897f \u500f\u7136\u800c\u81f3\uff0c\u5c06\u5927\u60b2\u7985\u5e08\u7684\u62c2\u5c18\u675f\u4f4f\uff0c\u5374\u539f\u6765\u662f\u4ed6\u90a3\u4ef6\u7834\u8863\u3002 \u3000\u3000\u5b88\u5728\u95e8\u53e3\u7684\u4e24\u4e2a\u5025\u4f97\u6d3e\u7684\u5f1f\u5b50\uff0c\u4e00\u4e2a\u53cc\u638c\u7ffb\u98de\uff0c\u4e00\u4e2a\u8fd0\u5251\u5982\u98de\uff0c\u5835\u4f4f\u4ed6\u7684\u53bb\u8def\u3002\u59ec\u6653\u98ce \u9053\uff1a\u201c\u4f60\u7684\u91d1\u73af\u638c\u5b66\u5f97\u8fd8\u672a\u5230\u5bb6\u3002\u201d\u53cc\u638c\u4e00\u5708\uff0c\u5c06\u90a3\u5025\u4f97\u6d3e\u5f1f\u5b50\u7684\u53cc\u638c\u5c01\u51fa\u5916\u95e8\uff0c\u7d27\u63a5\u7740 \u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u53e6\u4e8c\u4e2a\u5025\u4f97\u6d3e\u5f1f\u5b50\u7684\u957f\u5251\u7adf\u7ed9\u4ed6\u5939\u65ad\uff01 \u3000\u3000\u8fd9\u6b63\u662f\u5025\u4f97\u6d3e\u9547\u5c71\u7edd\u6280\u201c\u91d1\u73af\u638c\u201d\u7684\u529f\u592b\uff0c\u529f\u592b\u7ec3\u5230\u6df1\u5904\uff0c\u53cc\u638c\u5408\u6210\u7684\u73af\u5f62\u5708\u5185\uff0c\u975e\u4f46 \u65e0\u61c8\u53ef\u51fb\uff0c\u800c\u4e14\u65e0\u575a\u4e0d\u6467\u3002\u8fd9\u4e24\u540d\u5025\u4f97\u6d3e\u5f1f\u5b50\u5927\u60ca\u5931\u8272\uff0c\u8fde\u5fd9\u9000\u5f00\u3002\u539f\u6765\u5728\u5025\u4f97\u6d3e\u4e2d\uff0c\u91d1\u73af \u638c\u529f\u592b\u80fd\u8fbe\u5230\u59ec\u6653\u98ce\u8fd9\u822c\u706b\u5019\uff0c\u53ef\u4ee5\u5c06\u5229\u5251\u5939\u65ad\u7684\uff0c\u4e5f\u53ea\u6709\u5e74\u7eaa\u6700\u8001\u7684\u957f\u8001\u4e4c\u5927\u6717\u4e00\u4eba\u800c \u5df2\u3002\u8fd9\u4e24\u4e2a\u5f1f\u5b50\u7109\u6562\u62db\u60f9\u3002 \u3000\u3000\u5ce8\u5d4b\u6d3e\u7684\u540d\u5bbf\u9752\u677e\u9053\u4eba\u559d\u9053\uff1a\u201c\u5077\u6765\u7684\u529f\u592b\uff0c\u4e5f\u6562\u5728\u4eba\u524d\u70ab\u8000\uff01\u201d\u8eab\u5f62\u4e00\u8d77\uff0c\u5410\u6c14\u626c \u58f0\uff0c\u638c\u4f3c\u5954\u96f7\uff0c\u7acb\u5411\u59ec\u6653\u98ce\u6253\u4e0b\u3002 \u3000\u3000\u8fd9\u9752\u677e\u9053\u4eba\u662f\u5ce8\u5d4b\u6d3e\u540d\u5bbf\u91d1\u5149\u5927\u5e08\u7684\u5927\u5f1f\u5b50\uff0c\u91d1\u5149\u5927\u5e08\u4e0e\u75db\u7985\u4e0a\u4eba\u5e76\u9a7e\u9f50\u540d\uff0c\u662f\u4e2d\u539f\u4e24 \u4f4d\u7855\u679c\u4ec5\u5b58\u7684\u524d\u8f88\u5b97\u5e08\uff0c\u9752\u677e\u9053\u4eba\u5f97\u4e43\u5e08\u771f\u4f20\uff0c\u201c\u592a\u6e05\u6c14\u529f\u201d\u5df2\u7ec3\u5230\u5e08\u7236\u7684\u4e94\u6210\u529f\u529b\u3002 \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u6b66\u5b66\u4e4b\u9053\uff0c\u672c\u5c31\u8be5\u535a\u91c7\u4f17\u957f\uff0c\u878d\u4f1a\u8d2f\u901a\uff0c\u5206\u4ec0\u4e48\u4f60\u7684\u6211\u7684\u3001\u5b66\u7684\u5077 \u7684\uff1f\u201d\u8f7b\u98d8\u98d8\u7684\u4e00\u638c\u62cd\u51fa\uff0c\u770b\u4f3c\u6beb\u4e0d\u7740\u529b\uff0c\u4f46\u9752\u677e\u9053\u4eba\u548c\u4ed6\u7684\u638c\u7f18\u4e00\u63a5\uff0c\u5374\u611f\u5230\u4e00\u80a1\u5341\u5206\u67d4 \u548c\u5374\u53c8\u5341\u5206\u6df1\u539a\u7684\u5185\u529b\u8feb\u6765\uff0c\u767b\u65f6\u4ee4\u4ed6\u4e0d\u80fd\u518d\u5411\u524d\u79fb\u52a8\u4e00\u6b65\u3002\u59ec\u6653\u98ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5077\u6765\u7684\u5982 \u4f55\uff1f\u201d\u7b11\u58f0\u4e00\u6536\uff0c\u64a4\u638c\u4fbf\u8dd1\u3002 \u3000\u3000\u9752\u677e\u9053\u4eba\u6b63\u5728\u4ee5\u5168\u529b\u4e0e\u4ed6\u76f8\u6297\uff0c\u59ec\u6653\u98ce\u7a81\u7136\u6536\u638c\uff0c\u4ed6\u51b7\u4e0d\u53ca\u9632\uff0c\u76f4\u5411\u524d\u5954\u51fa\u4e09\u6b65\uff0c\u624d\u7a33 \u5f97\u4f4f\u8eab\u5f62\uff0c\u4e0d\u7981\u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u7f9e\u6127\u3002\u539f\u6765\u59ec\u6653\u98ce\u7528\u7684\u4e5f\u6b63\u662f\u201c\u592a\u6e05\u6c14\u529f\u201d\uff0c\u529f\u529b\u867d\u7136\u5c1a\u4e0d \u53ca\u4ed6\u7684\u5e08\u7236\u91d1\u5149\u5927\u5e08\uff0c\u4f46\u5374\u5df2\u8981\u6bd4\u4ed6\u80dc\u8fc7\u8bb8\u591a\u4e86\u3002\u9752\u677e\u9053\u4eba\u5403\u60ca\u4e4b\u540e\uff0c\u53c8\u611f\u5230\u5927\u60d1\u4e0d\u89e3\uff0c\u5fc3 \u9053\uff1a\u201c\u8fd9\u592a\u6e05\u6c14\u529f\uff0c\u6700\u4e3a\u96be\u7ec3\uff0c\u59ec\u6653\u98ce\u8fd9\u53ae\u867d\u7136\u5077\u53bb\u4e86\u672c\u95e8\u7684\u79d8\u7c4d\uff0c\u4e0d\u8fc7\u4ec5\u4ec5\u51e0\u5e74\uff0c\u600e\u7684\u5c31 \u7ec3\u5230\u4e86\u8fd9\u822c\u5883\u754c\uff1f\u201d \u3000\u3000\u9752\u677e\u9053\u4eba\u6709\u6240\u4e0d\u77e5\uff0c\u539f\u6765\u59ec\u6653\u98ce\u662f\u5b5f\u795e\u901a\u6700\u5fc3\u7231\u7684\u5f1f\u5b50\uff0c\u5b5f\u795e\u901a\u5f97\u4e86\u4e54\u5317\u6e9f\u7684\u534a\u90e8\u6b66\u529f \u79d8\u53d1\u4e4b\u540e\uff0c\u53ea\u5c06\u4e00\u90e8\u4efd\u4f20\u7ed9\u4ed6\u7684\u5e08\u5f1f\u9633\u8d64\u7b26\u548c\u59ec\u6653\u98ce\uff0c\u800c\u59ec\u6653\u98ce\u6240\u5f97\u7684\u4f20\u6388\u6bd4\u4ed6\u7684\u5e08\u53d4\u8fd8\u8981 \u591a\u4e86\u597d\u4e9b\u3002\u4e54\u5317\u6e9f\u7684\u6b66\u529f\u79d8\u7626\u5f00\u8f9f\u4e86\u6b63\u90aa\u5408\u4e00\u7684\u7ec3\u6b66\u9014\u5f84\uff0c\u59ec\u6653\u98ce\u4eba\u53c8\u806a\u660e\uff0c\u867d\u7136\u672a\u5b66\u5f97\u5b8c \u5168\uff0c\u5374\u5df2\u53c2\u900f\u4e86\u4e0a\u4e58\u5fc3\u6cd5\u3002\u6545\u6b64\u4ed6\u5077\u6765\u4e86\u5404\u6d3e\u7684\u6b66\u5b66\u5178\u7c4d\u4e4b\u540e\uff0c\u7ec3\u8d77\u6765\u5c31\u4e8b\u534a\u529f\u500d\u4e86\u3002\u4ed6\u6b64 \u6b21\u5230\u6765\uff0c\u6000\u7740\u4e09\u4e2a\u76ee\u7684\uff0c\u5176\u4e2d\u4e00\u4e2a\uff0c\u5c31\u662f\u60f3\u7528\u5077\u6765\u7684\u5404\u6d3e\u529f\u592b\u3002\u4e0e\u5404\u6d3e\u9ad8\u624b\u8f83\u91cf\uff0c\u770b\u770b\u81ea\u5df1 \u5b66\u5f97\u5982\u4f55\uff0c\u662f\u5426\u80fd\u591f\u5b9e\u7528\uff1f \u3000\u3000\u59ec\u6653\u98ce\u8feb\u9000\u4e86\u9752\u677e\u9053\u4eba\uff0c\u51b2\u51fa\u5e97\u95e8\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8bf8\u4f4d\u53ef\u8fd8\u6709\u5174\u8da3\u966a\u6211\u8dd1\u8dd1\uff0c\u9001\u6211\u4e00\u7a0b \u4e48\uff1f\u201d\u5927\u60b2\u7985\u5e08\u6c89\u58f0\u8bf4\u4e86\u4e00\u4e2a\u5b57\uff1a\u201c\u8ffd\uff01\u201d\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u59ec\u6653\u98ce\u7684\u8eab\u5f62\u5df2\u5728\u5341\u6570\u4e08\u5916\u3002 \u3000\u3000\u5927\u60b2\u7985\u5e08\u4e0d\u662f\u4e0d\u77e5\u59ec\u6653\u98ce\u7684\u8f7b\u529f\u5353\u7edd\uff0c\u4f46\u4e00\u6765\u82e5\u662f\u6b64\u6b21\u8ba9\u4ed6\u9003\u8d70\uff0c\u4e0b\u6b21\u5c31\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019 \u624d\u80fd\u78b0\u4e0a\u4e86\uff1b\u4e8c\u6765\u4ed6\u89c1\u4e86\u59ec\u6653\u98ce\u4eca\u665a\u6240\u663e\u7684\u529f\u592b\u4e4b\u540e\uff0c\u5fc3\u4e2d\u66f4\u591a\u4e86\u4e00\u5c42\u9690\u5fe7\uff0c\u6b64\u65f6\u82e5\u4e0d\u9664 \u4ed6\uff0c\u53ea\u6015\u518d\u8fc7\u51e0\u5e74\uff0c\u4ed6\u5c06\u5077\u6765\u7684\u5404\u6d3e\u529f\u592b\uff0c\u878d\u4f1a\u8d2f\u901a\uff0c\u7ec3\u5230\u4e86\u767b\u5cf0\u9020\u6781\u4e4b\u65f6\uff0c\u50cf\u4ed6\u5e08\u7236\u90a3\u6837 \u6043\u5f3a\u4f5c\u6076\uff0c\u5c82\u975e\u53c8\u662f\u6b66\u6797\u5927\u60a3\uff1f\u6545\u6b64\u5373\u4f7f\u8ffd\u4e0d\u4e0a\u4ed6\uff0c\u4e5f\u8981\u53bb\u8ffd\uff0c \u3000\u3000\u90a3\u59d3\u6587\u7684\u5c11\u5e74\u5931\u53bb\u4e86\u73e0\u5b9d\uff0c\u6c14\u6cae\u795e\u4f24\uff0c\u5fc3\u610f\u8e87\u8e0c\u83ab\u51b3\uff0c\u4ed6\u4e00\u811a\u521a\u8de8\u51fa\u95e8\u5916\uff0c\u5ffd\u542c\u5f97\u6c5f\u5357 \u7b11\u9053\uff1a\u201c\u8fd9\u573a\u70ed\u95f9\uff0c\u6211\u662f\u975e\u770b\u5230\u6563\u573a\u4e0d\u53ef\uff0c\u4f60\u5462\uff0c\u6211\u770b\u662f\u77a7\u4e0d\u77a7\u4e5f\u7f62\u4e86\u3002\u201d\u90a3\u5c11\u5e74\u6479\u7136\u60ca \u9192\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u73ed\u4eba\u90fd\u662f\u4e0e\u6211\u4f5c\u5bf9\u7684\uff0c\u4f11\u8bf4\u6211\u8feb\u4e0d\u4e0a\u59ec\u6653\u98ce\uff0c\u8ffd\u4e0a\u4e86\uff0c\u4e5f\u51b3\u593a\u4e0d\u56de \u6765\u3002\u201d\u8fde\u5fd9\u5c06\u8de8\u51fa\u53bb\u7684\u811a\u6b65\u6536\u4e86\u56de\u6765\uff0c\u5927\u5bb6\u90fd\u53bb\u8ffd\u59ec\u6653\u98ce\u4e86\uff0c\u9664\u4e86\u6c5f\u5357\uff0c\u6ca1\u6709\u4eba\u518d\u7406\u4f1a\u4ed6\uff0c \u970e\u773c\u4e4b\u95f4\uff0c\u90fd\u8d70\u7684\u5e72\u5e72\u51c0\u51c0\uff0c\u90a3\u5c11\u5e74\u81ea\u884c\u56de\u623f\u89e3\u6551\u4ed6\u7684\u90a3\u4e2a\u5c0f\u53ae\uff0c\u6309\u4e0b\u540e\u8868\u3002 \u3000\u3000\u4e14\u8bf4\u6d3e\u9ad8\u624b\u4e00\u7a9d\u8702\u7684\u53bb\u8ffd\u59ec\u6653\u98ce\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u5c31\u8ffd\u51fa\u90ca\u5916\uff0c\u90a3\u59ec\u6653\u98ce\u4e5f\u602a\uff0c\u8dd1\u4e86\u4e00\u4f1a\uff0c \u5374\u5ffd\u5730\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u653e\u6162\u4e86\u811a\u6b65\uff0c\u5509\u58f0\u53f9\u6c14\u5730\u53eb\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u7cdf\u7cd5\uff01\u4f60\u4eec\u771f\u7684\u662f\u7a77\u8feb\u4e0d\u820d\uff1f \u6211\u53ef\u6709\u70b9\u7d2f\u4e86\uff0c\u7d2f\u4e86\uff01\u201d \u3000\u3000\u6709\u51e0\u4e2a\u4e0d\u77e5\u9ad8\u4e0b\u7684\u665a\u8f88\u5f1f\u5b50\uff0c\u89c1\u72b6\u5927\u4e3a\u6b22\u559c\uff0c\u53fd\u53fd\u55b3\u55b3\u7684\u8bae\u8bba\u9053\uff1a\u201c\u4eba\u4eba\u90fd\u8bf4\u59ec\u6653\u98ce\u7684 \u8f7b\u529f\u5982\u4f55\u4e86\u5f97\uff0c\u5374\u539f\u6765\u662f\u8a00\u8fc7\u5176\u5b9e\uff0c\u8033\u95fb\u662f\u5047\uff0c\u773c\u89c1\u662f\u771f\uff01\u201d\u4ed6\u4eec\u53d1\u4e00\u58f0\u558a\uff0c\u4e89\u5148\u6050\u540e\u7684\u4fbf \u62e5\u4e0a\u53bb\u3002 \u3000\u3000\u5927\u96c4\u7985\u5e08\u7709\u5934\u4e00\u76b1\uff0c\u4f20\u58f0\u558a\u9053\uff1a\u201c\u63d0\u9632\u6709\u8bc8\uff01\u201d\u5176\u4e2d\u6709\u4e00\u4e2a\u64c5\u4f7f\u8896\u7bad\u7684\u6b66\u5f53\u95e8\u4e0b\uff0c\u62a2\u5728 \u524d\u5934\uff0c\u770b\u770b\u5c31\u8981\u8ffd\u4e0a\uff0c\u542c\u5f97\u5927\u96c4\u7985\u5e08\u7684\u558a\u58f0\uff0c\u5fc3\u9053\uff1a\u201c\u7ba1\u4ed6\u6709\u8bc8\u6ca1\u8bc8\uff0c\u4e14\u5148\u8d4f\u4ed6\u4e24\u679d\u8896\u7bad\u518d \u8bf4\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u8111\u540e\u7adf\u4f3c\u957f\u6709\u773c\u775b\uff1b\u8fd9\u4e2a\u6b66\u5f53\u5f1f\u5b50\u7684\u8896\u7bad\u65b9\u53d1\uff0c\u4ed6\u84e6\u5730\u4fbf\u5411\u524d\u75be\u63a0\uff0c\u767e\u5fd9\u4e2d\u8fd8\u5411 \u540e\u9762\u62db\u624b\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u662f\u5b58\u5fc3\u8981\u8feb\u6211\u5feb\u8dd1\u4e86\uff0c\u53ea\u6015\u6211\u8fd9\u4e00\u8dd1\uff0c\u4f60\u4eec\u53c8\u5f97\u8d39\u597d\u5927\u7684\u6c14\u529b\u624d\u8ffd \u5f97\u4e0a\u4e86\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u8fd9\u4e00\u53d1\u529b\u75be\u8dd1\uff0c\u5f53\u771f\u662f\u5feb\u5982\u95ea\u7535\uff0c\u4f46\u542c\u5f97\u98d5\u98d5\u8fde\u58f0\uff0c\u8896\u7bad\u8ddd\u79bb\u4ed6\u7684\u8eab\u540e\u8fd8\u6709\u4e00\u4e08 \u591a\u8fdc\u4fbf\u5760\u4e0b\u5730\u6765\uff0c\u7adf\u662f\u8fde\u5c04\u7bad\u4e5f\u8ffd\u4e0d\u4e0a\u3002 \u3000\u3000\u62b1\u62d9\u9053\u4eba\u53d8\u4e86\u9762\u8272\uff0c\u8d23\u5907\u4ed6\u8fd9\u4e2a\u5e08\u4f84\u9053\uff1a\u201c\u4f60\u771f\u662f\u4e0d\u77e5\u5929\u9ad8\u539a\uff0c\u4e5f\u4e0d\u60f3\u60f3\uff0c\u6709\u8fd9\u8bb8\u591a\u524d \u8f88\u5728\u6b64\uff0c\u7528\u5f97\u7740\u4f60\u51fa\u624b\u5417\uff1f\u201d\u5927\u96c4\u7985\u5e08\u9053\uff1a\u201c\u8fd9\u53ae\u76ee\u5185\u65e0\u4eba\uff0c\u5b9e\u662f\u620f\u5f04\u6211\u4eec\u5927\u4f17\u3002\u8981\u662f\u8feb\u4e0d \u4e0a\u4ed6\uff0c\u5927\u5bb6\u90fd\u5931\u9762\u5b50\uff0c\u9053\u5144\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u5355\u8d23\u5907\u4ed6\u4e00\u4e2a\u4eba\u4e86\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u5ffd\u5feb\u5ffd\u6162\uff0c\u8d70\u4e86\u4e00\u4f1a\u53c8\u6b47\u4e00\u56de\uff0c\u628a\u4f17\u4eba\u90fd\u5f04\u5f97\u6c14\u607c\u96be\u582a\uff0c\u9752\u677e\u9053\u4eba\u9053\uff1a\u201c\u597d\uff0c\u8ba9 \u4ed6\u6682\u4e14\u9a84\u72c2\u5427\uff0c\u6211\u6b63\u662f\u60f3\u4ed6\u5982\u6b64\u3002\u4ed6\u8f7b\u529f\u867d\u597d\uff0c\u8bba\u5230\u5185\u529b\u60a0\u957f\uff0c\u6301\u4e45\u4e0d\u75b2\uff0c\u672a\u5fc5\u80dc\u5f97\u8fc7\u4e24\u4f4d \u7985\u5e08\u3002\u201d\u4ed6\u8fd9\u4e00\u60f3\u6cd5\u6b63\u4e0e\u4e24\u4f4d\u7985\u5e08\u7684\u5fc3\u610f\u76f8\u7b26\uff0c\u4f46\u4ed6\u4eec\u542c\u4e86\u8fd9\u8bdd\uff0c\u5374\u53c8\u4e0d\u7981\u6697\u547c\u201c\u60ed\u6127\u201d\uff0c \u539f\u6765\u4ed6\u4eec\u6240\u6253\u7684\u4e3b\u610f\uff0c\u6b63\u662f\u8981\u5f85\u59ec\u6653\u98ce\u75b2\u7d2f\u4e4b\u540e\uff0c\u4fbf\u8054\u5408\u9752\u677e\u9053\u4eba\uff0c\u4ee5\u4e09\u4eba\u4e4b\u529b\uff0c\u6599\u60f3\u53ef\u4ee5 \u5c06\u4ed6\u5236\u4f0f\u3002 \u3000\u3000\u6b64\u5954\u5f7c\u9010\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u5df2\u662f\u5929\u8272\u5927\u767d\uff0c\u59ec\u6653\u98ce\u603b\u662f\u548c\u4ed6\u4eec\u4fdd\u6301\u4e00\u6bb5\u8ddd\u79bb\u3002\u9003\u5230\u4e86\u65f7\u91ce\uff0c \u59ec\u6653\u98ce\u5ffd\u5730\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u53eb\u9053\uff1a\u201c\u54ce\u5440\u5440\uff0c\u6211\u771f\u662f\u652f\u6491\u4e0d\u4f4f\u5566\uff0c\u8bf7\u8bf8\u4f4d\u6055\u6211\u65e0\u793c\uff0c\u6211\u53ef\u8981\u6253 \u4e2a\u5428\u513f\u4e86\u3002\u201d\u501a\u7740\u4e00\u68f5\u5927\u6811\uff0c\u547c\u547c\u565c\u565c\u7684\uff0c\u679c\u7136\u6253\u8d77\u4e86\u778c\u7761\u6765\u3002 \u3000\u3000\u5404\u6d3e\u5f1f\u5b50\u8fd9\u6b21\u4e0d\u6562\u518d\u5192\u6627\u4e0a\u524d\uff0c\u5728\u5927\u6811\u5468\u56f4\u5c06\u4ed6\u56f4\u4f4f\uff0c\u5927\u60b2\u7985\u5e08\u8d70\u4e0a\u524d\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u59ec \u5c45\u58eb\uff0c\u4f60\u6e38\u620f\u98ce\u5c18\uff0c\u4e5f\u4e0d\u5b9c\u5927\u8fc7\u5206\u4e86\uff0c\u8001\u8896\u8fd9\u53a2\u6709\u793c\uff0c\u8fd8\u60f3\u518d\u5411\u4f60\u8ba8\u6559\u51e0\u624b\u9ad8\u62db\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u54c8\u54c8\u4e00\u7b11\uff0c\u633a\u8eab\u7ad9\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u656c\u6211\u4e00\u5c3a\uff0c\u6211\u656c\u4f60\u4e00\u4e08\uff0c\u8001\u7985\u5e08\uff0c\u59ec\u67d0\u8fd9\u53a2\u8fd8 \u793c\u4e86\u3002\u8bf7\u8001\u7985\u5e08\u522b\u5148\u602a\u6211\u4e86\uff0c\u6211\u7684\u8fd9\u756a\u505a\u4f5c\uff0c\u5b9e\u975e\u6709\u610f\u620f\u5f04\u5404\u4f4d\u9ad8\u4eba\uff0c\u4e5f\u975e\u60f3\u548c\u4f60\u4eec\u518d\u6b21\u4ea4 \u624b\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u65e0\u4e0d\u6115\u7136\uff0c\u6709\u4eba\u5600\u5600\u5495\u5495\u4ed6\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u8fd8\u8bf4\u4e0d\u662f\u620f\u5f04\uff1f\u201d\u201c\u96be\u9053\u4ed6\u8fd8 \u80fd\u5b58\u7740\u4ec0\u4e48\u597d\u5fc3\uff1f\u201d\u201c\u8001\u7985\u5e08\uff0c\u4e0d\u8981\u4e2d\u4ed6\u7684\u7f13\u5175\u4e4b\u8ba1\u3002\u201d \u3000\u3000\u5927\u60b2\u7985\u5e08\u662f\u5c11\u6797\u5bfa\u5341\u516b\u7f57\u6c49\u4e4b\u9996\uff0c\u4f17\u4eba\u552f\u4ed6\u9a6c\u9996\u662f\u77bb\uff0c\u5927\u96c4\u7985\u5e08\u4e0e\u9752\u677e\u9053\u4eba\u867d\u7136\u65e9\u6709\u8ba1 \u5212\uff0c\u51c6\u5907\u8054\u624b\u5bf9\u59ec\u6653\u98ce\u56f4\u653b\uff0c\u53ef\u662f\u4ed6\u4eec\u4e5f\u5f97\u987e\u4f4f\u8eab\u4efd\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e14\u770b\u4ed6\u4e0e\u5927\u60b2\u7985\u5e08\u5982\u4f55 \u8bf4\u6cd5\uff1f\u8981\u662f\u51b3\u88c2\u4e86\uff0c\u5927\u60b2\u7985\u5e08\u5148\u8017\u4ed6\u4e00\u573a\u4e5f\u597d\u3002\u201d\u4ed6\u4eec\u4f30\u8ba1\uff0c\u4ee5\u5927\u60b2\u7985\u5e08\u7684\u529f\u529b\uff0c\u7eb5\u4e0d\u80fd \u80dc\uff0c\u6700\u5c11\u4e5f\u53ef\u4ee5\u5c06\u59ec\u6653\u98ce\u7d2f\u4e2a\u534a\u6b7b\u3002 \u3000\u3000\u5927\u60b2\u7985\u5e08\u4e0d\u7406\u4f17\u4eba\u8bae\u8bba\uff0c\u548c\u989c\u60a6\u8272\u4ed6\u8bf4\u9053\uff1a\u201c\u5c45\u58eb\u6709\u4f55\u8bdd\u8bf4\uff0c\u8001\u7eb3\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u59ec\u67d0\u5c06\u5404\u4f4d\u5f15\u6765\uff0c\u5b9e\u662f\u56e0\u4e3a\u5ba2\u5e97\u4e4b\u4e2d\u95f2\u6742\u4eba\u591a\uff0c\u7ec8\u4e0d\u662f\u8bf4\u8bdd\u4e4b\u6240\u3002 \u6709\u4e9b\u4e1c\u897f\uff0c\u4e5f\u4e0d\u597d\u5728\u90a3\u91cc\u62ff\u51fa\u6765\u201d\u3002 \u3000\u3000\u5927\u60b2\u7985\u5e08\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u59ec\u5c45\u58eb\uff0c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8bf8\u4f4d\u5bf9\u59ec\u67d0\u7a77\u8feb\u4e0d\u820d\uff0c\u6599\u60f3\u662f\u56e0\u4e3a\u59ec\u67d0\u66fe\u4e0d\u544a\u800c\u53d6\uff0c\u62ff\u53bb\u4e86\u4f60\u4eec\u5404 \u6d3e\u7684\u6b66\u529f\u79d8\u5178\u4e86\uff1f\u662f\u4e48\uff1f\u201d \u3000\u3000\u4f17\u4eba\u8f70\u7136\u559d\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5c31\u597d\u4e86\uff01\u201d\u53c8\u6709\u4eba\u5bf9\u5927\u60b2\u7985\u5e08\u53eb\u9053\uff1a\u201c\u4e0d\u5fc5\u4e0e\u4ed6\u591a\u8bf4\uff0c\u628a\u4ed6\u5077 \u6765\u7684\u6b66\u529f\u5e9f\u4e86\uff0c\u514d\u5f97\u4ed6\u62ff\u53bb\u5bb3\u4eba\u3002\u201d\u5927\u60b2\u7985\u5e08\u6446\u4e86\u6446\u624b\uff0c\u5c06\u4f17\u4eba\u55a7\u56b7\u7684\u58f0\u97f3\u538b\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u59ec\u6653\u98ce\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u5077\u6765\u7684\u6b66\u529f\u6ca1\u6cd5\u5b50\u8fd8\u7ed9\u5927\u5bb6\uff0c\u5077\u6765\u7684\u4e66\u5374\u662f\u539f\u7269\u65e0\u635f\uff0c\u8c28\u6b64\u5949 \u8fd8\u3002\u201d \u3000\u3000\u8c01\u4e5f\u6ca1\u60f3\u5230\u59ec\u6653\u98ce\u7adf\u80af\u8fd9\u6837\u8f7b\u6613\u7684\u4fbf\u5c06\u4e66\u9001\u56de\uff0c\u987f\u65f6\u95f4\u5927\u5bb6\u90fd\u4e0d\u4f5c\u58f0\uff0c\u6014\u6014\u5730\u671b\u7740\u59ec\u6653 \u98ce\u3002\u59ec\u6653\u98ce\u53c8\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4e00\u53e5\u8bdd\uff0c\u6211\u5e08\u7236\u751f\u524d\u4e3a\u60a3\u6b66\u6797\uff0c\u591a\u5e74\u6765\u6211\u4fbf\u60f3\u4e3a\u4ed6\u8d4e\u7f6a\u3002\u6211\u5077\u4f60 \u4eec\u5404\u6d3e\u79d8\u5178\uff0c\u56fa\u7136\u662f\u56e0\u4e3a\u6211\u79ef\u4e60\u5df2\u6210\uff0c\u8d3c\u6027\u96be\u6539\u3002\u53e6\u4e00\u4e2a\u539f\u56e0\uff0c\u4e5f\u662f\u60f3\u501f\u6b64\u673a\u4f1a\uff0c\u5c06\u5404\u6d3e\u6b66 \u529f\u4e4b\u79d8\uff0c\u516c\u8bf8\u540c\u597d\u3002\u5728\u6211\u5077\u7684\u4e66\u4e0a\u90fd\u6dfb\u4e0a\u4e86\u8bfb\u540e\u7684\u611a\u89c1\uff0c\u4e66\u4e2d\u597d\u4e9b\u5730\u65b9\uff0c\u6211\u4e5f\u4e0d\u63e3\u5192\u6627\uff0c\u52a0 \u4e0a\u4e0b\u6ce8\u3002\u8fd9\u4e9b\u4e5f\u90fd\u662f\u4ece\u4f60\u4eec\u5404\u6d3e\u7684\u79d8\u5178\u4e2d\u53d6\u957f\u8865\u77ed\uff0c\u518d\u52a0\u4e0a\u6211\u7684\u6240\u5b66\uff0c\u8d21\u732e\u4e00\u5f97\u4e4b\u611a\uff0c\u597d\u4f5c \u6295\u6843\u4e4b\u62a5\u7684\u3002\u60f3\u4f60\u4eec\u4e0d\u81f3\u4e8e\u8d23\u602a\u6211\u591a\u4e8b\u5427\uff1f\u597d\uff0c\u73b0\u5728\u6211\u5c31\u4f9d\u6b21\u5949\u8fd8\u5927\u5bb6\uff0c\u8bf7\u8001\u7985\u5e08\u5148\u6536\u56de\u8d35 \u6d3e\u7684\u4e09\u5377\u79d8\u5178\u3002\u201d \u3000\u3000\u53ea\u89c1\u59ec\u6653\u98ce\u628a\u624b\u4e00\u626c\uff0c\u4e09\u672c\u4e66\u4ece\u4ed6\u8896\u4e2d\u98de\u51fa\uff0c\u5e73\u5e73\u7a33\u7a33\u7684\u5411\u5927\u60b2\u7985\u5e08\u98de\u6765\uff0c\u63a5\u7740\u53c8\u5c06\u6b66 \u5f53\u6d3e\u7684\u79d8\u5178\u8fd8\u7ed9\u62b1\u62d9\u9053\u4eba\uff0c\u5ce8\u5d4b\u7684\u8fd8\u7ed9\u9752\u677e\u9053\u4eba\u2026\u2026\u4e0d\u7ba1\u5404\u4eba\u6240\u7ad9\u7684\u8fdc\u8fd1\uff0c\u4ed6\u7684\u4e66\u629b\u51fa\u90fd\u6070 \u5230\u597d\u5904\uff0c\u5c31\u4f3c\u9012\u5230\u4e86\u4ed6\u4eec\u7684\u624b\u4e0a\u4e00\u822c\u3002 \u3000\u3000\u5927\u60b2\u7985\u5e08\u63a5\u8fc7\u4e86\u4ed6\u7684\u8fd8\u4e66\u3002\u968f\u624b\u63ed\u5f00\u4e00\u9875\uff0c\u8fd9\u4e00\u9875\u6b63\u662f\u201c\u592a\u865a\u771f\u7ecf\u201d\u4e2d\u8c08\u53ca\u201c\u4e09\u8c61\u5f52 \u5143\u201d\u5185\u529f\u5fc3\u6cd5\u7684\u7cbe\u4e49\u4e4b\u5904\uff0c\u53ea\u89c1\u59ec\u6653\u98ce\u7684\u201c\u7709\u6279\u201d\u5199\u9053\uff1a\u201c\u4e09\u8c61\u5f52\u65e0\uff0c\u6df7\u4e8e\u65e0\u6709\uff0c\u6c14\u8109\u7cbe \u795e\uff0c\u96be\u5206\u5148\u540e\uff0c\u5929\u4eba\u5408\u4e00\uff0c\u65b9\u6210\u4e0d\u673d\u3002\u201d\u5927\u60b2\u63b8\u5e08\u770b\u4e86\uff0c\u4e0d\u7981\u7fdf\u7136\u4e00\u60ca\u3002 \u3000\u3000\u539f\u6765\u6240\u8c13\u201c\u4e09\u8c61\u5f52\u5143\u201d\uff0c\u5373\u662f\u795e\u3001\u6c14\u3001\u8109\u4e09\u8005\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u4fee\u70bc\u5185\u529f\u7684\u4eba\uff0c\u6709\u7684\u4e3b\u5f20 \u201c\u795e\u4e0e\u8109\u5408\u201d\u6709\u7684\u4e3b\u5f20\u201c\u795e\u4e0e\u6c14\u5408\u201d\uff0c\u6709\u7684\u4e3b\u5f20\u201c\u820d\u6c14\u4ece\u8109\u201d\uff0c\u6709\u7684\u4e3b\u5f20\u201c\u6c14\u8109\u7cbe\u795e\uff0c\u5408\u800c \u4e3a\u4e00\u201d\u3002\u5bf9\u8fd9\u4e09\u8005\u7684\u5173\u7cfb\uff0c\u7531\u4e8e\u8f7b\u91cd\u5148\u540e\u7684\u4e0d\u540c\u4e3b\u5f20\uff0c\u800c\u5206\u6210\u8bb8\u591a\u4e0d\u540c\u7684\u7ec3\u529f\u6d41\u6d3e\uff0c\u8fd9\u662f\u6b66 \u5b66\u4e2d\u6700\u6df1\u5965\u7684\u7406\u8bba\u4e4b\u4e00\uff0c\u96be\u4ee5\u7ec6\u8868\u3002\u59ec\u6653\u98ce\u8fd9\u51e0\u53e5\u7709\u6279\uff0c\u63d0\u7eb2\u631a\u9886\uff0c\u9053\u51fa\u4e86\u4fee\u70bc\u5185\u529f\u7684\u4e0a\u4e58 \u5fc3\u6cd5\uff0c\u6bd4\u201c\u592a\u865a\u771f\u7ecf\u201d\u6240\u8bba\uff0c\u66f4\u4e3a\u7cbe\u8f9f\uff0c\u6545\u6b64\u5927\u60b2\u7985\u5e08\u770b\u4e86\uff0c\u5fc3\u4e2d\u4e5f\u6697\u6697\u4f69\u670d\u3002 \u3000\u3000\u5176\u4ed6\u5404\u4eba\u7684\u53cd\u5e94\u5404\u5404\u4e0d\u540c\uff0c\u6709\u7684\u6b22\u559c\uff0c\u6709\u7684\u607c\u6012\u3002\u8981\u77e5\u6b66\u6797\u4e2d\u7684\u95e8\u6237\u4e4b\u89c1\u751a\u4e25\uff0c\u5404\u5927\u95e8 \u6d3e\u90fd\u6709\u4ed6\u4eec\u672c\u6d3e\u7684\u4e0d\u4f20\u4e4b\u79d8\uff0c\u5373\u7b97\u6e0a\u6e90\u6781\u6df1\uff0c\u4ea4\u60c5\u6781\u597d\uff0c\u4e5f\u51b3\u4e0d\u80fd\u501f\u9605\u522b\u6d3e\u7684\u6b66\u529f\u79d8\u7c4d\u3002 \u3000\u3000\u5982\u4eca\u59ec\u6653\u98ce\u5728\u5404\u6d3e\u7684\u79d8\u590d\u4e0a\uff0c\u4e00\u4e00\u52a0\u4e0a\u4e86\u8bc4\u6ce8\uff0c\u6216\u501f\u5c11\u6797\u6d3e\u7684\u6765\u8865\u5145\u6b66\u5f53\u6d3e\u7684\uff0c\u6216\u7528\u9752 \u57ce\u6d3e\u7684\u6765\u8bc4\u5025\u4f97\u6d3e\u7684\uff0c\u6216\u63c9\u5408\u5907\u5bb6\u5b66\u8bf4\u800c\u72ec\u6292\u5df1\u89c1\uff0c\u6216\u5c06\u90aa\u6d3e\u7684\u7406\u8bba\u63ba\u6742\u4e8e\u6b63\u6d3e\u4e4b\u4e2d\uff0c\u6362\u8a00 \u4e4b\u4e5f\u5373\u662f\u7b49\u4e8e\u5c06\u5404\u6d3e\u6b66\u529f\u7684\u4e0d\u4f20\u4e4b\u79d8\u5411\u522b\u6d3e\u516c\u5f00\u4e86\u3002\u56e0\u6b64\uff0c\u6709\u4e9b\u6c14\u91cf\u72ed\u7a84\uff0c\u95e8\u6237\u4e4b\u89c1\u7279\u6df1\u7684 \u4eba\u5f53\u7136\u607c\u6012\uff0c\u6709\u4e9b\u8f83\u5c0f\u7684\u95e8\u6d3e\u4e2d\u4eba\uff0c\u501f\u6b64\u673a\u7f18\uff0c\u5f97\u7aa5\u5404\u5927\u95e8\u6d3e\u7684\u4e0a\u4e58\u5fc3\u6cd5\uff0c\u5fc3\u4e2d\u5374\u662f\u6697\u6697\u6b22 \u559c\u3002 \u3000\u3000\u59ec\u6653\u98ce\u5c06\u5077\u6765\u7684\u5404\u6d3e\u79d8\u7c4d\u5c3d\u90fd\u53d1\u8fd8\u4e4b\u540e\uff0c\u62cd\u62cd\u624b\u9053\uff1a\u201c\u539f\u7269\u5949\u8fd8\uff0c\u6709\u591a\u65e0\u5c11\uff0c\u8bf8\u4f4d\u53ef\u4ee5 \u653e\u59ec\u67d0\u8d70\u4e86\u5427\uff1f\u201d\u5927\u96c4\u63b8\u5e08\u662f\u5c11\u6797\u5bfa\u7684\u201c\u6267\u6cd5\u50e7\u201d\uff0c\u5ac9\u6076\u5982\u4ec7\uff0c\u5bf9\u4e8e\u90aa\u6b63\u4e4b\u522b\uff0c\u770b\u5f97\u751a\u4e3a\u91cd \u8981\uff0c\u59ec\u6653\u98ce\u4ece\u5c11\u6797\u5bfa\u5077\u4e66\uff0c\u4ed6\u65e9\u5df2\u8ba4\u4e3a\u5927\u635f\u672c\u6d3e\u7684\u5a01\u4e25\uff0c\u5982\u4eca\u59ec\u6653\u98ce\u8fd8\u4e66\u7ed9\u4ed6\u7684\u5e08\u5144\uff0c\u4ed6\u53c8 \u4e0d\u77e5\u9053\u59ec\u6653\u98ce\u5728\u4e66\u4e2d\u5199\u7684\u662f\u4e9b\u4ec0\u4e48\uff0c\u56e0\u6b64\u89c1\u59ec\u6653\u98ce\u8981\u8d70\uff0c\u4fbf\u52c3\u7136\u6012\u9053\uff1a\u201c\u4f60\u5c06\u5404\u6d3e\u79d8\u5178\uff0c\u4e88 \u53d6\u4e88\u643a\uff0c\u5982\u4eca\u62cd\u62cd\u624b\u4fbf\u60f3\u8d70\u4e86\u4e48\uff1f\u201d\u6b66\u5f53\u6d3e\u7684\u62b1\u62d9\u9053\u4eba\u53eb\u9053\uff1a\u201c\u5bf9\uff01\u82e5\u4e0d\u5c06\u4ed6\u6b66\u529f\u5e9f\u6389\uff0c\u7ed9 \u4ed6\u8981\u6765\u4fbf\u6765\uff0c\u8981\u53bb\u4fbf\u53bb\uff0c\u54b1\u4eec\u5404\u5927\u95e8\u6d3e\u7684\u989c\u9762\u4f55\u5b58\uff1f\u201d \u3000\u3000\u5927\u96c4\u7985\u5e08\u8131\u4e0b\u8888\u88df\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u8888\u88df\u5316\u6210\u4e86\u4e00\u6735\u7ea2\u4e91\uff0c\u5411\u59ec\u6653\u98ce\u5f53\u5934\u7f69\u4e0b\uff0c\u62b1\u62d9\u9053\u4eba\u5c55 \u5f00\u4e86\u8fde\u73af\u593a\u547d\u5251\u6cd5\uff0c\u4e5f\u8ddf\u8e2a\u6025\u523a\uff0c\u5ce8\u5d4b\u6d3e\u7684\u9752\u677e\u9053\u4eba\u4e0e\u5927\u96c4\u7985\u5e08\u65e9\u5df2\u7ea6\u597d\uff0c\u8981\u5408\u529b\u751f\u64d2\u59ec\u6653 \u98ce\uff0c\u8fd9\u65f6\uff0c\u4ed6\u5f97\u56de\u4e86\u672c\u6d3e\u79d8\u7c4d\uff0c\u7565\u7565\u7ffb\u9605\u4e86\u4e00\u4e0b\u59ec\u6653\u98ce\u7684\u6279\u6ce8\uff0c\u5fc3\u4e2d\u6709\u70b9\u8e0c\u8e87\u3002\u4f46\u5927\u96c4\u7985\u5e08 \u5df2\u7ecf\u53d1\u52a8\u3001\u4ed6\u4e5f\u53ea\u597d\u8ddf\u5728\u5927\u96c4\u7985\u5e08\u4e4b\u540e\uff0c\u5411\u59ec\u6653\u98ce\u5c55\u5f00\u653b\u51fb\uff01 \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u51fa\u5bb6\u4eba\u6148\u60b2\u4e3a\u6000\uff0c\u8001\u7985\u5e08\u4f60\u5374\u600e\u7684\u8fd9\u6837\u72e0\u6cd5\uff1f\u4fd7\u8bdd\u8bf4\u6349\u8d3c\u8ffd\u8d43\uff0c\u81ea\u9996\u51cf \u7f6a\u3002\u73b0\u5728\u6211\u4e0d\u4f46\u539f\u7269\u5949\u8fd8\uff0c\u8fd8\u52a0\u4e0a\u4e86\u5229\u606f\uff0c\u5373\u7b97\u4f60\u4eec\u5f53\u6211\u662f\u4e2a\u8d3c\uff0c\u4e5f\u8be5\u9976\u4e86\u6211\u4e86\uff01\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5927\u96c4\u7985\u5e08\u90a3\u9886\u8888\u88df\u5df1\u7f69\u4e86\u4e0b\u6765\uff0c\u59ec\u6653\u98ce\u770b\u4f3c\u5b09\u76ae\u7b11\u8138\uff0c\u5176\u5b9e\u5bf9\u8fd9\u4e09\u5927 \u9ad8\u624b\u5374\u662f\u4e0d\u6562\u8f7b\u89c6\uff0c\u4ed6\u62cd\u51fa\u4e86\u4e00\u638c\uff0c\u90a3\u9886\u8888\u88df\u547c\u7684\u4e00\u58f0\u5728\u4ed6\u5934\u9876\u6253\u4e86\u4e00\u5708\uff0c\u56db\u8fb9\u5782\u4e0b\uff0c\u4e2d\u95f4 \u90e8\u5206\u7a81\u8d77\u8d77\uff0c\u4f3c\u5145\u6ee1\u4e86\u6c14\u4f53\u4e00\u822c\uff0c\u5316\u6210\u4e86\u4e00\u53e3\u949f\u5f62\uff0c\u4ecd\u7136\u5982\u5f71\u968f\u5f62\uff0c\u7d27\u7d27\u7684\u7f69\u5b9a\u4e86\u59ec\u6653\u98ce\u3002 \u3000\u3000\u8981\u77e5\u9053\u59ec\u6653\u98ce\u7684\u6b66\u5b66\u9020\u8be3\u867d\u9ad8\uff0c\u4f46\u5185\u529f\u7684\u4fee\u70bc\u5374\u96be\u4ee5\u901f\u6210\uff0c\u4ed6\u867d\u7136\u5077\u5b66\u4e86\u5404\u6d3e\u7684\u4e0a\u4e58\u5fc3 \u6cd5\uff0c\u771f\u5b9e\u7684\u529f\u592b\u548c\u5c11\u6797\u5bfa\u8fd9\u4e24\u50e7\u76f8\u6bd4\uff0c\u4e0d\u8fc7\u662f\u5728\u4f2f\u4ef2\u4e4b\u95f4\u3002\u73b0\u5728\u5927\u96c4\u7985\u5e08\u4ee5\u5168\u529b\u65bd\u4e3a\uff0c\u59ec\u6653 \u98ce\u7684\u5288\u7a7a\u638c\u5c31\u53ea\u53ef\u4ee5\u51cf\u8f7b\u538b\u529b\uff0c\u5374\u7834\u4e0d\u4e86\u4ed6\u90a3\u9886\u8888\u88df\u3002 \u3000\u3000\u62b1\u62d9\u9053\u4eba\u7684\u957f\u5251\u4ea6\u5df2\u653b\u5230\uff0c\u8fd9\u4e00\u5251\u76f4\u53d6\u4ed6\u80cc\u540e\u7684\u98ce\u5e9c\u7a74\uff0c\u5251\u52bf\u51cc\u5389\u975e\u5e38\uff0c\u53ef\u662f\u5728\u8fd9\u4e09\u5927 \u9ad8\u624b\u4e4b\u4e2d\uff0c\u62b1\u62d9\u9053\u4eba\u7a76\u7adf\u662f\u6700\u5f31\u7684\u4e00\u73af\u3002\u59ec\u6653\u98ce\u5c31\u4f3c\u80cc\u540e\u957f\u7740\u773c\u775b\uff0c\u5ffd\u5730\u53cd\u624b\u4e00\u5f39\uff0c\u62b1\u62d9\u9053 \u4eba\u62ff\u5251\u7684\u624b\u8155\u7ed9\u4ed6\u5f39\u4e2d\uff0c\u957f\u5251\u4e0d\u7531\u81ea\u4e3b\u7684\u5411\u4e0a\u523a\u51fa\uff0c\u4e0e\u5927\u96c4\u7985\u5e08\u7684\u8888\u88df\u6b63\u597d\u78b0\u7740\uff0c\u94ff\u9535\u6709 \u58f0\uff0c\u7adf\u5982\u523a\u7740\u4e86\u94c1\u677f\u4e00\u822c\uff0c\u5251\u5c16\u7acb\u65f6\u6298\u4e86\u3002\u59ec\u6653\u98ce\u8f6c\u8fc7\u8eab\u6765\uff0c\u518d\u63a5\u4e86\u9752\u677e\u9053\u4eba\u7684\u4e00\u638c\u3002 \u3000\u3000\u62b1\u62d9\u9053\u4eba\u5927\u6012\uff0c\u8fd0\u5251\u518d\u523a\uff0c\u5927\u60b2\u7985\u5e08\u5ffd\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u7531\u4ed6\u53bb\u5427\u3002\u201d\u5927\u96c4\u7985\u5e08\u6014\u4e86\u4e00\u6014\uff0c \u90a3\u9886\u8888\u88df\u5728\u534a\u7a7a\u4e2d\u505c\u4f4f\uff0c\u9752\u677e\u9053\u4eba\u4e5f\u65e0\u610f\u4e0e\u59ec\u6653\u98ce\u62fc\u547d\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u7adf\u53d8\u6210\u4e86\u62b1\u62d9\u9053\u4eba\u4e0e\u59ec \u6653\u98ce\u6b63\u9762\u5bf9\u654c\u3002 \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u8001\u7985\u5e08\u8bf4\u5f97\u5bf9\uff0c\u5f97\u9976\u4eba\u5904\u4e14\u9976\u4eba\uff01\u201d\u8bdd\u662f\u5982\u6b64\uff0c\u5374\u5ffd\u7136\u4ee5\u5929\u7f57\u6b65\u6cd5\uff0c\u95ea\u7535 \u822c\u7684\u6b3a\u5230\u62b1\u62d9\u9053\u4eba\u8eab\u8fb9\uff0c\u5288\u624b\u5c31\u593a\u4e86\u4ed6\u7684\u957f\u5251\u3002 \u3000\u3000\u5927\u96c4\u7985\u5e08\u5927\u60ca\uff0c\u8888\u88df\u6025\u5fd9\u7f69\u4e0b\u3001\u59ec\u6653\u98ce\u4e00\u5251\u76f4\u523a\uff0c\u8fd9\u6b21\u5374\u4e0e\u7a7a\u624b\u4e0d\u540c\uff0c\u4ed6\u529b\u8d2f\u5251\u5c16\uff0c\u867d \u662f\u949d\u5251\uff0c\u4e5f\u628a\u8888\u88df\u523a\u7a7f\u4e86\u4e00\u4e2a\u5c0f\u5b54\u3002\u8888\u88df\u767b\u65f6\u4f3c\u6cc4\u4e86\u6c14\u7684\u5e03\u56ca\uff0c\u538b\u529b\u5927\u51cf\uff0c\u59ec\u6653\u98ce\u5012\u6301\u5251 \u67c4\uff0c\u5c31\u5728\u62b1\u62d9\u9053\u4eba\u60ca\u6115\u4e4b\u9645\uff0c\u5c06\u957f\u5251\u585e\u5230\u4e86\u4ed6\u7684\u624b\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u539f\u7269\u5949\u8fd8\uff0c\u6c42\u9053\u957f\u9ad8\u62ac\u8d35 \u624b\u3002\u201d \u3000\u3000\u4ed6\u8f7b\u529f\u4f55\u7b49\u9ad8\u660e\uff0c\u4e0d\u5f85\u5927\u96c4\u7985\u5e08\u518d\u8fd0\u7384\u529f\uff0c\u9f13\u8d77\u8888\u88df\uff0c\u4ed6\u5df2\u76f4\u5954\u51fa\u53bb\uff0c\u90a3\u4e48\u591a\u4eba\uff0c\u7adf\u81ea \u62e6\u4ed6\u4e0d\u4f4f\uff0c\u53ea\u89c1\u4ed6\u76f4\u5954\u4e0a\u5c71\u5934\uff0c\u8fb9\u8dd1\u8fb9\u56b7\u9053\uff1a\u201c\u5927\u96c4\u7985\u5e08\uff0c\u4f60\u4e0d\u9976\u6211\uff0c\u6211\u53ea\u6709\u6b7b\u7ed9\u4f60\u770b \u5566\uff01\u201d\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u8dd1\u5230\u4e86\u4e00\u5904\u60ac\u5d16\u65c1\u8fb9\uff0c\u5ffd\u7136\u5c31\u8dc3\u4e86\u4e0b\u53bb\uff0c\u4f17\u4eba\u867d\u7136\u77e5\u9053\u4ed6\u662f\u620f\u800d\uff0c\u4f46\u5728 \u90a3\u4fc4\u9877\u4e4b\u95f4\uff0c\u4e5f\u4e0d\u81ea\u7981\u7684\u6115\u7136\u60ca\u547c\uff01 \u3000\u3000\u6c5f\u5357\u5657\u55e4\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5999\u624b\u795e\u5077\u6709\u8da3\u5f97\u7d27\uff0c\u82e5\u7136\u771f\u4e2a\u6b7b\u4e86\uff0c\u5012\u662f\u53ef\u60dc\u3002\u201d\u62b1\u62d9\u9053 \u4eba\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u4ed6\u906d\u53d7\u593a\u5251\u4e4b\u8fb1\uff0c\u5bf9\u59ec\u6653\u98ce\u81ea\u662f\u6068\u4e4b\u5165\u9aa8\uff0c\u4f46\u4ed6\u4ee5\u957f\u8f88\u7684\u8eab\u4efd\uff0c\u5374\u4e5f\u4e0d\u4fbf\u5411 \u6c5f\u5357\u53d1\u4f5c\u3002 \u3000\u3000\u5927\u96c4\u7985\u5e08\u8f6c\u8fc7\u8eab\u6765\uff0c\u95ee\u9053\uff1a\u201c\u5e08\u5144\uff0c\u597d\u4e0d\u5bb9\u6613\u5c06\u8fd9\u53ae\u56f4\u4f4f\uff0c\u4e3a\u4f55\u5e08\u5144\u8981\u5c06\u4ed6\u653e\u4e86\u3002\u201d\u5927 \u60b2\u7985\u5e08\u9053\uff1a\u201c\u6211\u770b\u4ed6\u5e76\u65e0\u6076\u610f\uff0c\u800c\u4e14\uff0c\u8981\u662f\u4ed6\u60f3\u8dd1\u7684\u8bdd\uff0c\u54b1\u4eec\u65e9\u5df2\u8ffd\u4ed6\u4e0d\u4e0a\u4e86\u3002\u5927\u5bb6\u65e2\u7136\u5f97 \u56de\u5931\u53bb\u4e4b\u7269\uff0c\u5c31\u7b97\u4e86\u5427\u3002\u201d \u3000\u3000\u62b1\u62d9\u9053\u4eba\u6124\u6124\u4e0d\u5e73\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u7985\u5e08\u5fb7\u9ad8\u671b\u91cd\uff0c\u6211\u7b49\u5c82\u6562\u4e0d\u9075\uff1f\u53ea\u662f\u8fd9\u59ec\u6653\u98ce\u4e43\u662f\u5b5f\u795e \u901a\u7684\u5f1f\u5b50\uff0c\u4eca\u65e5\u8f7b\u6613\u5c06\u4ed6\u653e\u8fc7\uff0c\u5f85\u4ed6\u6280\u4e1a\u5927\u6210\u4e4b\u540e\uff0c\u82e5\u7136\u4ed6\u8981\u7ed9\u4e43\u5e08\u62a5\u4ec7\uff0c\u8fd9\u5982\u4f55\u662f\u597d\uff1f\u201d \u3000\u3000\u5927\u60b2\u7985\u5e08\u9053\uff1a\u201c\u8d2b\u50e7\u4e0d\u613f\u52a8\u624b\u8bdb\u4ed6\uff0c\u5c31\u662f\u56e0\u4e3a\u4ed6\u9664\u4e86\u5077\u5077\u4e1c\u897f\u4e4b\u5916\uff0c\u5c1a\u65e0\u4ec0\u4e48\u91cd\u5927\u7684\u7f6a \u884c\uff0c\u8981\u662f\u4ed6\u679c\u7136\u6076\u8ff9\u662d\u5f70\uff0c\u5c11\u6797\u6d3e\u51b3\u4e0d\u7f6e\u8eab\u4e8b\u5916\u3002\u201d \u3000\u3000\u62b1\u62d9\u9053\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53ea\u6015\u5230\u4e86\u90a3\u65f6\uff0c\u8001\u7985\u5e08\u8981\u60f3\u9664\u4ed6\uff0c\u5c31\u672a\u5fc5\u5bb9\u6613\u4e86\u3002\u201d \u3000\u3000\u5927\u60b2\u7985\u5e08\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u73b0\u5728\u60f3\u8981\u9664\u4ed6\uff0c\u4e5f\u672a\u5fc5\u5bb9\u6613\uff0c\u4e0d\u8fc7\uff0c\u8d2b\u50e7\u867d\u7136\u80fd\u529b\u4e0d\u591f\uff0c\u5c11\u6797\u5bfa \u8fd8\u6709\u5bb6\u5e08\u4e3b\u6301\uff0c\u59ec\u6653\u98ce\u82e5\u6562\u6043\u5f3a\u4f5c\u6076\uff0c\u4ed6\u8001\u4eba\u5bb6\u4e5f\u51b3\u4e0d\u4f1a\u5750\u89c6\u3002\u201d \u3000\u3000\u5927\u60b2\u7985\u5e08\u7684\u5e08\u7236\u5c31\u662f\u5c11\u6797\u5bfa\u7684\u65b9\u4e08\u75db\u7985\u4e0a\u4eba\uff0c\u8fd9\u65f6\u5df2\u5e74\u8fc7\u516b\u65ec\uff0c\u65e9\u4e0d\u7406\u4e8b\uff0c\u5927\u60b2\u7985\u5e08\u62ac \u51fa\u4ed6\u7684\u5e08\u7236\uff0c\u4f17\u4eba\u90fd\u4e0d\u6562\u591a\u8bdd\uff0c\u53ea\u62b1\u62d9\u9053\u4eba\u6124\u6c14\u96be\u6d88\uff0c\u4ecd\u7136\u5600\u5495\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u82e5\u8981\u60ca\u52a8\u5230\u4ed6 \u8001\u4eba\u5bb6\uff0c\u8fd9\u53ef\u662f\u4e00\u6869\u7f6a\u8fc7\u4e86\u3002\u201d \u3000\u3000\u5927\u60b2\u7985\u5e08\u7709\u5934\u4e00\u76b1\uff0c\u6168\u7136\u8bf4\u9053\uff1a\u201c\u8981\u662f\u771f\u7684\u6709\u90a3\u4e48\u4e00\u5929\uff0c\u8d2b\u50e7\u53ca\u4f17\u540c\u95e8\uff0c\u5b9a\u7136\u8feb\u968f\u9053\u957f \u4e4b\u540e\uff0c\u4e0e\u59ec\u6653\u98ce\u7b97\u5e10\u4fbf\u662f\u3002\u8981\u662f\u5168\u90fd\u8d25\u5728\u4ed6\u4e00\u4eba\u4e4b\u624b\uff0c\u90a3\u5c31\u65e0\u8bdd\u53ef\u8bf4\u3002\u8981\u4e0d\u7136\u4e5f\u8fd8\u65e0\u987b\u8001\u4eba \u5bb6\u4eb2\u81ea\u52a8\u624b\u3002\u201d \u3000\u3000\u5927\u60b2\u7985\u5e08\u662f\u5c11\u6797\u5bfa\u5341\u516b\u7f57\u6c49\u4e4b\u9996\uff0c\u8fd1\u5e74\u6765\u75db\u7985\u4e0a\u4eba\u53ea\u662f\u5728\u5bfa\u4e2d\u8bb2\u7ecf\uff0c\u4e0d\u7406\u4e8b\u52a1\uff0c\u6302\u540d \u201c\u65b9\u4e08\u201d\uff0c\u5b9e\u9645\u5df2\u7531\u5927\u60b2\u7985\u5e08\u4e3b\u6301\u3002\u5927\u60b2\u7985\u5e08\u8bdd\u8bf4\u81f3\u6b64\uff0c\u62b1\u62d9\u9053\u4eba\u4e5f\u4e0d\u6562\u5476\u5476\u4e0d\u4f11\u4e86\u3002 \u3000\u3000\u5f53\u4e0b\u5404\u6d3e\u5f1f\u5b50\u5206\u522b\u6563\u53bb\u3002\u6c5f\u5357\u4e0e\u9752\u57ce\u6d3e\u7684\u5d14\u4e91\u4eae\u4ea4\u60c5\u6df1\u539a\uff0c\u540c\u8d70\u4e00\u7a0b\uff0c\u5d14\u4e91\u4eae\u95ee\u9053\uff1a \u201c\u6c5f\u5927\u54e5\uff0c\u4f60\u4e0d\u5728\u5bb6\u91cc\u7eb3\u798f\uff0c\u5230\u6c5f\u6e56\u4e0a\u95ef\u8361\u4f5c\u4ec0\u4e48\uff1f\u6c5f\u5357\u76b1\u4e86\u76b1\u7709\uff0c\u6b32\u8a00\u53c8\u6b62\u3002\u5d14\u4e91\u4eae\u5947\u602a \u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u5f80\u65e5\u54b1\u4eec\u54e5\u513f\u4fe9\u76f8\u805a\uff0c\u53ea\u6709\u4f60\u8bf4\u7684\uff0c\u6ca1\u6211\u8bf4\u7684\uff0c\u600e\u7684\u4eca\u5929\u4f60\u5374\u53d8\u6210\u4e86\u952f\u5634\u846b \u82a6\u4e86\uff1f\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u83ab\u9053\u77e5\u4ea4\u60c5\u6027\u6539\uff0c\u5fc3\u4e2d\u6709\u4e8b\u53e3\u96be\u8a00\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000--------------- \u3000\u3000\u626c\u5251\u8f69\u5c45\u58eb \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u56db\u56de\u3000\u6bd2\u9152\u751c\u8a00\u6c42\u79d8\u7c4d\u3000\u795e\u5077\u5999\u6280\u620f\u5929\u9b54 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u56db\u56de\u3000\u6bd2\u9152\u751c\u8a00\u6c42\u79d8\u7c4d\u3000\u795e\u5077\u5999\u6280\u620f\u5929\u9b54 \u3000\u3000\u6c5f\u5357\u79bb\u5bb6\u7684\u65f6\u5019\uff0c\u5cb3\u6bcd\u548c\u59bb\u5b50\u66fe\u518d\u4e09\u5411\u4ed6\u53ee\u5631\uff0c\u5728\u8def\u4e0a\u4e0d\u53ef\u591a\u8bdd\uff0c\u5c24\u5176\u4e0d\u53ef\u5c06\u5bfb\u5b50\u8bbf\u53cb \u4e4b\u4e8b\uff0c\u5411\u522b\u4eba\u900f\u9732\uff0c\u4ee5\u514d\u6253\u8349\u60ca\u86c7\uff0c\u53cd\u589e\u6ce2\u6298\u3002\u6545\u6b64\uff0c\u6c5f\u5357\u867d\u7136\u6b22\u559c\u8bf4\u8bdd\uff0c\u4f46\u4e3a\u4e86\u513f\u5b50\u7684\u7f18 \u6545\uff0c\u4e5f\u53ea\u5f97\u5fcd\u4f4f\u3002\u4ed6\u65e9\u5df2\u6253\u5b9a\u4e3b\u610f\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u53ea\u80fd\u5bf9\u4e09\u4e2a\u4eba\u8bf4\uff0c\u4e00\u4e2a\u662f\u91d1\u4e16\u9057\uff0c\u4e00\u4e2a\u662f\u9648\u5929 \u5b87\uff0c\u4e00\u4e2a\u662f\u8c37\u4e4b\u534e\u3002\u5bf9\u5176\u4ed6\u7684\u4eba\u4ed6\u51b3\u8ba1\u4e0d\u9732\u51fa\u534a\u70b9\u98ce\u58f0\u3002 \u3000\u3000\u53ef\u662f\u6c5f\u5357\u4e0d\u60ef\u8bf4\u8c0e\uff0c\u5728\u5d14\u4e91\u4eae\u8ffd\u95ee\u4e4b\u4e0b\uff0c\u5f3a\u7b11\u63a9\u9970\u9053\uff1a\u201c\u6211\u5b9e\u662f\u56e0\u4e3a\u5728\u5bb6\u65e5\u4e45\uff0c\u4f4f\u5f97\u95f7 \u4e86\uff0c\u6240\u4ee5\u624d\u5230\u5916\u9762\u6e9c\u6e9c\u3002\u201d\u795e\u60c5\u8a00\u8bed\uff0c\u90fd\u663e\u5f97\u4e0d\u5927\u81ea\u7136\u3002 \u3000\u3000\u5d14\u4e91\u4eae\u76b1\u76b1\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u770b\u4f60\u4e00\u5b9a\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff0c\u54b1\u4eec\u60c5\u5982\u5144\u5f1f\uff0c\u4f60\u82e5\u6709\u4e3a\u96be\u4e4b\u5904\uff0c \u6211\u613f\u4e0e\u4f60\u5206\u5fe7\u3002\u201d \u3000\u3000\u6c5f\u5357\u5fc3\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4f60\u5c82\u80fd\u4e0e\u6211\u5206\u5fe7\uff1f\u90a3\u516b\u4e2a\u8499\u9762\u5973\u5b50\u7684\u6b66\u529f\uff0c\u4f11\u8bf4\u662f\u4f60\uff0c\u5373\u7b97\u5c11 \u6797\u5bfa\u90a3\u4e24\u4f4d\u9ad8\u50e7\u4e5f\u964d\u670d\u4e0d\u4e86\u5979\u4eec\u3002\u8bf4\u51fa\u6765\u5e72\u4e8b\u65e0\u8865\uff0c\u53cd\u800c\u6709\u5bb3\u3002\u201d\u5f53\u4e0b\u8f6c\u4e86\u4e2a\u8bdd\u9898\u8bf4\u9053\uff1a \u201c\u6211\u54ea\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff0c\u5d14\u5144\u5f1f\u4e0d\u8981\u80e1\u4e71\u731c\u7591\u3002\u53ea\u662f\u6211\u521a\u624d\u4e0e\u90a3\u59d3\u6587\u7684\u4ea4\u624b\uff0c\u5403\u4e86\u8d25\u4ed7\uff0c\u6709\u70b9\u4e0d \u8212\u670d\u7f62\u4e86\uff0c\u5d14\u5144\u5f1f\uff0c\u6211\u5012\u60f3\u5411\u4f60\u6253\u542c\u6253\u542c\uff0c\u201d\u6211\u4e49\u5144\u7684\u8fd1\u51b5\u5982\u4f55\uff1f\u4f60\u53ef\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u7684\u6b66\u5b66\u5f00\u8499\u4e1a\u5e08\u662f\u8427\u9752\u5cf0\uff0c\u548c\u5d14\u4e91\u4eae\u8c0a\u5c5e\u540c\u95e8\uff0c\u6545\u6b64\u6c5f\u5357\u6709\u6b64\u4e00\u95ee\u3002\u5d14\u4e91\u4eae\u9053\uff1a \u201c\u6211\u6b63\u60f3\u548c\u4f60\u8bf4\u5462\uff0c\u4f60\u4e49\u5144\u78b0\u5230\u4e86\u4e00\u4ef6\u602a\u4e8b\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4ec0\u4e48\u602a\u4e8b\uff1f\u201d\u5d14\u4e91\u4eae\u9053\uff1a\u201c\u5927\u7ea6 \u5728\u4e09\u6708\u4e4b\u524d\uff0c\u4ed6\u6b63\u5bb6\u91cc\u4f4f\u5f97\u597d\u597d\u7684\uff0c\u7a81\u7136\u6709\u4e24\u4e2a\u8499\u9762\u5973\u5b50\uff0c\u5230\u4ed6\u5bb6\u91cc\u6765\u95f9\u4e86\u4e00\u573a\u3002\u3002\u201d \u3000\u3000\u6c5f\u5357\u6014\u4e86\u4e00\u6014\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4ed6\u4e5f\u78b0\u5230\u4e86\u8fd9\u73ed\u8499\u9762\u7684\u5973\u5b50\uff1f\u201d \u3000\u3000\u5d14\u4e91\u4eae\u9053\uff1a\u201c\u542c\u4f60\u7684\u8bed\u6c14\uff0c\u6562\u60c5\u4f60\u77e5\u9053\u90a3\u4e24\u4e2a\u8499\u9762\u5973\u5b50\u7684\u6765\u5386\uff1f\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u4f60\u5148\u8bf4\u8bf4\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5d14\u4e91\u4eae\u9053\uff1a\u201c\u6709\u4e00\u665a\u4ed6\u4eec\u592b\u5987\u7761\u5f97\u6b63\u9163\uff0c\u5ffd\u88ab\u5f02\u58f0\u60ca\u9192\uff0c\u4e00\u62ac\u5934\uff0c\u53ea\u89c1\u4e24\u4e2a\u8499\u9762\u5973\u5b50\u7ad9 \u5728\u5e8a\u524d\uff0c\u6709\u4e00\u4e2a\u8fd8\u4eae\u8d77\u4e86\u706b\u6298\uff0c\u4fef\u4e0b\u4f3c\u662f\u6b63\u5728\u5bdf\u770b\u4ed6\u4eec\u7684\u9762\u8c8c\uff0c\u53e6\u4e00\u4e2a\u4f4e\u58f0\u8bf4\u9053\uff1a\u2018\u4e0d\u662f\uff0c \u4e0d\u662f\u3002\u2019\u9648\u5e08\u5144\u5927\u6012\uff0c\u7acb\u5373\u5c06\u60ac\u5728\u5e8a\u5934\u7684\u5b9d\u5251\u62d4\u4e86\u51fa\u6765\uff0c\u521a\u8981\u559d\u95ee\uff0c\u90a3\u4e24\u4e2a\u5973\u5b50\u5df2\u7184\u4e86\u706b \u6298\uff0c\u4ece\u7a97\u53e3\u8df3\u51fa\u53bb\u4e86\u3002\u6211\u5e08\u5ac2\u8ddf\u8e2a\u8ffd\u51fa\uff0c\u6253\u4e86\u5979\u4eec\u4e09\u9897\u51b0\u9b44\u795e\u5f39\uff0c\u51b0\u5f39\u5728\u5979\u4eec\u5934\u9876\u7206\u88c2\uff0c\u767d \u8499\u8499\u7684\u5bd2\u5149\u51b7\u6c14\uff0c\u5df1\u662f\u5c06\u5979\u4eec\u8eab\u5f62\u7f69\u4f4f\uff0c\u53ef\u662f\uff0c\u5979\u4eec\u7adf\u7136\u82e5\u65e0\u5176\u4e8b\u5730\u8dd1\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u5c31\u8fd9\u6837\u8dd1\u4e86\u5417\uff1f\u201d\u5d14\u4e91\u4eae\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u5417\uff1f\u4f60\u662f\u77e5\u9053\u7684\uff0c\u4f60\u7684\u4e49\u5144\u66fe\u670d\u8fc7\u51b0 \u5bab\u5f02\u679c\uff0c\u8f7b\u529f\u5353\u7edd\uff0c\u7eb5\u7136\u6bd4\u4e0d\u4e0a\u59ec\u6653\u98ce\uff0c\u5927\u7ea6\u4e5f\u76f8\u5dee\u4e0d\u8fdc\uff0c\u53ef\u662f\u7adf\u7136\u8ffd\u5979\u4eec\u4e0d\u4e0a\u3002\u4f60\u4e49\u5ac2\u7684 \u51b0\u9b44\u795e\u5f39\uff0c\u6b66\u529f\u5dee\u4e00\u70b9\u7684\u78b0\u4e0a\u4e86\u5c31\u8981\u51bb\u5f97\u534a\u6b7b\uff0c\u4f46\u5bf9\u5979\u4eec\u5374\u662f\u6beb\u65e0\u7528\u5904\uff0c\u66f4\u4ee4\u4eba\u83ab\u540d\u5176\u5999\u7684 \u662f\uff1a\u5979\u4eec\u8fd9\u6837\u7a81\u5982\u5176\u6765\uff0c\u5374\u53c8\u8bdd\u4e5f\u4e0d\u591a\u8bf4\u4e00\u53e5\u4fbf\u8dd1\u4e86\uff0c\u4f60\u8bf4\u602a\u4e5f\u4e0d\u602a\uff1f\u201d \u3000\u3000\u6c5f\u5357\u5374\u5e76\u4e0d\u611f\u5230\u5947\u602a\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u8499\u9762\u5973\u5b50\uff0c\u5927\u7ea6\u627e\u7684\u662f\u6211\u3002\u5979\u4eec\u4ee5\u4e3a\u6211\u8fd8\u662f \u4f4f\u5728\u4e49\u5144\u5bb6\u4e2d\uff0c\u5bfb\u6211\u4e0d\u89c1\uff0c\u540e\u6765\u624d\u77e5\u9053\u6211\u5df2\u642c\u4e86\u5bb6\uff0c\u548c\u5cb3\u6bcd\u540c\u4f4f\u4e86\uff0c\u55ef\uff0c\u5982\u6b64\u770b\u6765\uff0c\u5979\u4eec\u662f \u65e9\u5df2\u5904\u5fc3\u79ef\u8651\uff0c\u8981\u60f3\u6cd5\u5b50\u6765\u5077\u5b66\u91d1\u5927\u4fa0\u4f20\u7ed9\u6211\u7684\u6b66\u529f\u4e86\u3002\u201d \u3000\u3000\u5d14\u4e91\u4eae\u7eed\u9053\uff1a\u201c\u6211\u662f\u4e0a\u6708\u5230\u5e08\u5144\u5bb6\u4e2d\u4f5c\u5ba2\uff0c\u542c\u4ed6\u4eec\u8c08\u8d77\u8fd9\u4ef6\u4e8b\u60c5\u6765\u7684\u3002\u4ed6\u4eec\u672c\u6765\u60f3\u67e5\u4e2a \u6c34\u843d\u77f3\u51fa\uff0c\u53ef\u662f\u89c1\u5bb6\u4e2d\u65e2\u65e0\u635f\u5931\uff0c\u7236\u4eb2\u5e74\u7eaa\u53c8\u8001\uff0c\u592b\u59bb\u5546\u91cf\u4e4b\u540e\uff0c\u4e5f\u5c31\u4e0d\u613f\u751f\u4e8b\u4e86\u3002\u4ed6\u4eec\u77e5 \u9053\u6211\u6709\u5c71\u4e1c\u4e4b\u884c\uff0c\u8fd8\u53eb\u6211\u53bb\u627e\u4f60\uff0c\u8bf4\u662f\u5df2\u6709\u51e0\u5e74\u672a\u548c\u4f60\u89c1\u9762\uff0c\u5e0c\u671b\u4f60\u80fd\u591f\u5230\u4ed6\u4eec\u90a3\u91cc\u5c0f\u4f4f\u4e9b \u65f6\u5019\u5462\uff0c\u60f3\u4e0d\u5230\u6628\u665a\u5374\u5728\u5ba2\u5e97\u548c\u4f60\u5de7\u9047\u3002\u5582\uff0c\u4f60\u521a\u624d\u8bf4\u7684\u4ec0\u4e48\u2018\u4ed6\u4e5f\u78b0\u5230\u4e86\u8fd9\u73ed\u8499\u9762\u5973 \u5b50\uff1f\u2019\u5982\u6b64\u8bf4\u6cd5\uff0c\u83ab\u975e\u4f60\u4e5f\u78b0\u5230\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u5357\u5df2\u6cc4\u6f0f\u4e86\u53e3\u98ce\uff0c\u6ca1\u6709\u6cd5\u5b50\uff0c\u53ea\u597d\u5c06\u81ea\u5df1\u7684\u4e8b\u60c5\u544a\u8bc9\u4e86\u4ed6\uff0c\u8ddf\u7740\u5343\u53ee\u4e07\u5631\u4ed6\u8bf4\u9053\uff1a \u201c\u5d14\u5144\u5f1f\uff0c\u6211\u7684\u513f\u5b50\u5c1a\u8fd8\u5728\u5979\u4eec\u7684\u624b\u4e2d\uff0c\u4f60\u53ef\u4e0d\u8981\u6cc4\u9732\u7ed9\u522b\u4eba\u77e5\u9053\u3002\u8fd9\u4e8b\u60c5\u53ef\u4e0d\u80fd\u60ca\u5e08\u52a8\u4f17 \u7684\u5440\uff01\u201d \u3000\u3000\u5d14\u4e91\u4eae\u5927\u4e3a\u60ca\u8be7\uff0c\u8bf4\u9053\uff1a\u201c\u7adf\u6709\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u602a\u4e0d\u5f97\u4f60\u521a\u624d\u5bf9\u6211\u4e5f\u4e0d\u80af\u5b9e\u8bf4\u4e86\u3002\u4f60\u653e \u5fc3\uff0c\u6211\u591a\u5c11\u4e5f\u6709\u4e86\u51e0\u5e74\u6c5f\u6e56\u9605\u5386\uff0c\u5f53\u7136\u4e0d\u4f1a\u6253\u8349\u60ca\u86c7\uff0c\u5c06\u4f60\u7684\u4e8b\u60c5\u5ba3\u626c\u5f00\u53bb\u3002\u6211\u6697\u4e2d\u4e3a\u4f60\u7559 \u5fc3\u4fbf\u662f\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u82e6\u7b11\u9053\uff1a\u201c\u5d14\u5144\u5f1f\uff0c\u591a\u8c22\u4f60\u7684\u597d\u610f\u4e86\u3002\u6682\u65f6\u6211\u4e0d\u80fd\u53bb\u62dc\u8bbf\u4e49\u5144\uff0c\u4f60\u89c1\u5230\u4ed6\u65f6\uff0c\u8bf7\u4ee3 \u4e3a\u81f4\u610f\u3002\u201d\u4ed6\u672c\u6765\u4e0d\u60f3\u8bf4\u7684\uff0c\u7ec8\u4e8e\u8fd8\u662f\u8bf4\u4e86\u3002\u56e0\u6b64\u5fc3\u4e2d\u4e0d\u65e0\u540e\u6094\u3002\u4f46\u60f3\u5d14\u4e91\u4eae\u4eba\u5f88\u7a33\u91cd\uff0c\u4ed6 \u65e2\u7b54\u5e94\u81ea\u5df1\uff0c\u5f53\u4f1a\u5b88\u53e3\u5982\u74f6\u3002 \u3000\u3000\u4e24\u4eba\u5206\u624b\u4e4b\u540e\uff0c\u6c5f\u5357\u72ec\u81ea\u8d76\u8def\uff0c\u524d\u5f80\u6c13\u5c71\uff0c\u4ed6\u8d70\u4e86\u4e00\u4f1a\uff0c\u60f3\u8d77\u6765\u53c8\u540e\u6094\u4e00\u756a\u3002\u4ed6\u5e76\u975e\u4e0d \u4fe1\u4efb\u5d14\u4e91\u4eae\uff0c\u800c\u662f\u540e\u6094\u81ea\u5df1\u6ca1\u6709\u4f9d\u4ece\u59bb\u5b50\u7684\u5631\u5490\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8981\u662f\u6211\u56de\u5230\u5bb6\u4e2d\uff0c\u971e\u59b9\u95ee\u8d77 \u4e86\u6211\uff1a\u4f60\u5728\u8def\u4e0a\uff0c\u53ef\u6709\u5bf9\u522b\u4eba\u8bb2\u4e86\uff1f\u6211\u600e\u4e48\u56de\u7b54\u5462\uff1f\u5f53\u7136\u4e0d\u4f1a\u9a97\u5979\u3002\u5509\uff0c\u90a3\u5979\u4e00\u5b9a\u53c8\u8981\u8d23\u5907 \u6211\u2018\u6c5f\u5c71\u6613\u6539\uff0c\u672c\u6027\u96be\u79fb\u2019\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u6b63\u5728\u81ea\u6028\u81ea\u827e\uff0c\u5ffd\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u672a\u53ca\u56de\u5934\uff0c\u5df2\u7ed9\u4eba\u62cd\u4e86\u4e00\u4e0b\uff0c\u8033\u8fb9\u542c\u5f97\u4e00\u4e2a\u58f0\u97f3\u7b11 \u9053\uff1a\u201c\u50bb\u5c0f\u5b50\uff0c\u4f60\u81ea\u8a00\u81ea\u8bed\uff0c\u5728\u60f3\u4ec0\u4e48\u5fc3\u4e8b\uff1f\u201d \u3000\u3000\u6c5f\u5357\u5413\u4e86\u4e00\u8df3\uff0c\u672c\u80fd\u7684\u95ea\u8fc7\u4e00\u8fb9\uff0c\u56de\u5934\u770b\u65f6\uff0c\u53ef\u4e0d\u6b63\u662f\u59ec\u6653\u98ce\u3002 \u3000\u3000\u59ec\u6653\u98ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u6015\u4ec0\u4e48\uff1f\u5e72\u6211\u4eec\u8fd9\u884c\u7684\u4e5f\u8bb2\u4e49\u6c14\uff0c\u5077\u4e1c\u897f\u51b3\u4e0d\u4f1a\u5077\u5230\u597d\u670b\u53cb\u7684\u8eab \u4e0a\u3002\u201d \u3000\u3000\u6c5f\u5357\u672c\u6765\u95f7\u95f7\u4e0d\u4e50\uff0c\u7ed9\u4ed6\u9017\u5f97\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5df4\u671b\u4e0d\u5f97\u4f60\u6765\u5077\u6211\u5462\uff0c\u53ef\u60dc\u6211\u6ca1\u6709 \u4e1c\u897f\u503c\u5f97\u4f60\u5077\uff0c\u53ea\u80fd\u81ea\u6028\u798f\u8584\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u8be7\u9053\uff1a\u201c\u4f60\u8bf4\u8bdd\u597d\u602a\uff0c\u8fd9\u662f\u751a\u610f\u601d\uff1f\u201d \u3000\u3000\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u5982\u679c\u6211\u4e5f\u6709\u6b66\u529f\u79d8\u7c4d\u4e4b\u7c7b\uff0c\u4f60\u5077\u4e86\u53bb\uff0c\u52a0\u4e0a\u5229\u606f\u8fd8\u7ed9\u6211\uff0c\u6211\u5c82\u4e0d\u6b63\u662f\u5f97\u5176 \u6240\u54c9\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u5927\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u771f\u6709\u610f\u601d\u3002\u53ef\u60dc\u4ed6\u4eec\u5c31\u6ca1\u6709\u4f60\u7684\u89c1\u8bc6\uff0c\u5bf9\u4ed6\u4eec\u672c\u6765\u662f\u6709\u597d \u5904\u7684\uff0c\u4ed6\u4eec\u5374\u975e\u4f46\u4e0d\u9886\u60c5\uff0c\u53cd\u800c\u8981\u628a\u6211\u5f53\u4f5c\u6316\u4e86\u4ed6\u4eec\u7956\u575f\u7684\u4ec7\u4eba\u4f3c\u7684\u3002\u201c \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u6211\u53ef\u4ece\u6765\u6ca1\u6709\u5728\u80cc\u540e\u9a82\u8fc7\u4f60\u554a\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4f60\u548c\u4ed6\u4eec\u8bf4\u7684\u6d3b\u6211\u90fd\u542c\u5230\u4e86\uff0c\u6240\u4ee5\u6211\u624d\u60f3\u548c\u4f60\u4ea4\u670b\u53cb\u3002\u4f46\u4e0d\u77e5\u4f60\u53ef\u5acc\u5f03\u6211 \u662f\u4e2a\u5c0f\u5077\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u6211\u7684\u51fa\u8eab\u4e5f\u5e76\u4e0d\u6bd4\u4f60\u9ad8\u5f3a\uff0c\u4f60\u662f\u5c0f\u5077\u51fa\u8eab\uff0c\u6211\u662f\u5c0f\u53ae\u51fa\u8eab\u3002\u8981\u662f\u4f60\u6709\u5973\u513f \u7684\u8bdd\uff0c\u54b1\u4eec\u7ed3\u6210\u4eb2\u5bb6\uff0c\u5012\u662f\u95e8\u5f53\u6237\u5bf9\u3002\u201d\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u6211\u975e\u4f46\u6ca1\u6709\u5973\u513f\uff0c\u8fde\u8001\u5a46\u4e5f\u8fd8 \u6ca1\u6709\uff0c\u4e0d\u8fc7\u54b1\u4eec\u867d\u7136\u96be\u4ee5\u7ed3\u6210\u4eb2\u5bb6\uff0c\u5374\u53ef\u4ee5\u7ed3\u6210\u5144\u5f1f\uff0c\u4f60\u613f\u610f\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u5357\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u662f\u597d\uff0c\u4f46\u4f60\u7684\u5e74\u7eaa\u8981\u6bd4\u6211\u5927\u5f97\u591a\uff0c\u8f88\u5206\u4e5f\u9ad8\uff0c\u6211\u4e0e\u4f60\u7ed3\u4e3a\u5144 \u5f1f\uff0c\u4e0d\u662f\u6709\u70b9\u7c2a\u8d8a\u4e48\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4f60\u600e\u7684\u4fd7\u6c14\u8d77\u6765\u4e86\uff1f\u8fd9\u53ef\u4e0d\u50cf\u4f60\u7684\u4e3a\u4eba\u3002\u54e5\u54e5\u6bd4\u5144\u5f1f\u5927\u4e0a\u4e8c\u4e09\u5341\u5e74\u7684\u6709\u7684 \u662f\uff0c\u6211\u4eec\u5e08\u95e8\u4e0e\u4f60\u6beb\u65e0\u6e0a\u6e90\uff0c\u4e5f\u6392\u4e0d\u4e0a\u4ec0\u4e48\u8f88\u4efd\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u597d\uff0c\u627f\u4f60\u770b\u5f97\u8d77\u6211\uff0c\u6211\u5c31\u518d\u591a\u8ba4\u4e00\u4e2a\u4e49\u5144\u5427\uff01\u201d\u5f53\u4e0b\u64ae\u571f\u4e3a\u9999\uff0c\u4ea4\u4e92\u516b\u62dc\uff0c \u7ed3\u4e3a\u5f02\u59d3\u5144\u5f1f\u3002 \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u505a\u54e5\u54e5\u7684\u8981\u9001\u4f60\u4e00\u4efd\u89c1\u9762\u793c\uff0c\u4f60\u559c\u6b22\u4ec0\u4e48\uff1f\u81ea\u5df1\u62ff\u5427\uff01\u201d\u4ed6\u6253\u5f00\u4e86\u593a\u81ea\u59d3 \u6587\u90a3\u5c11\u5e74\u7684\u73e0\u5b9d\u7bb1\uff0c\u5b9d\u6c14\u73e0\u5149\uff0c\u8000\u773c\u751f\u82b1\u3002\u6c5f\u5357\u5374\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u4fbf\u628a\u7bb1\u5b50\u63a8\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9 \u4e1c\u897f\uff0c\u597d\u662f\u597d\uff0c\u53ef\u662f\u6211\u8981\u6765\u6709\u4ec0\u4e48\u7528\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u73e0\u5b9d\uff1f\u55ef\uff0c\u90a3\u4f60\u8981\u4ec0\u4e48\u4e1c\u897f\uff1f\u4f60\u8bf4\u5427\u3002\u9664\u4e86\u5929\u4e0a\u7684\u6708\u4eae\uff0c\u53ea\u8981\u662f\u4eba \u95f4\u7684\u4e1c\u897f\uff0c\u6211\u90fd\u6709\u6cd5\u5b50\u7ed9\u4f60\u53d6\u6765\uff01\u201d \u3000\u3000\u6c5f\u5357\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u6211\u53ea\u60f3\u5f97\u56de\u6211\u7684\u513f\u5b50\u3002\u201d\u4f46\u4ed6\u8bb0\u8d77\u4e86\u59bb\u5b50\u7684\u5429\u5490\uff0c\u8bdd\u513f\u5df2\u7ecf\u5728 \u820c\u5c16\u4e0a\u6253\u8f6c\uff0c\u5374\u7ec8\u4e8e\u6ca1\u6709\u5410\u51fa\u6765\u3002 \u3000\u3000\u8981\u77e5\u6c5f\u5357\u867d\u7136\u5bf9\u59ec\u6653\u98ce\u5e76\u65e0\u6076\u611f\uff0c\u751a\u81f3\u8fd8\u6709\u70b9\u4f69\u670d\u4ed6\uff0c\u4f46\u4e5f\u53ea\u4ec5\u6b62\u4e8e\u4f69\u670d\u800c\u5df2\uff0c\u5b9e\u5728\u8fd8 \u8c08\u4e0d\u4e0a\u6709\u4ec0\u4e48\u6df1\u539a\u7684\u4ea4\u60c5\u3002\u4ed6\u4e4b\u6240\u4ee5\u4e0e\u59ec\u6653\u98ce\u7ed3\u62dc\uff0c\u4e43\u662f\u56e0\u4e3a\u4ed6\u751f\u6027\u968f\u548c\uff0c\u4e0d\u613f\u62c2\u9006\u59ec\u6653\u98ce \u7684\u597d\u610f\u800c\u5df2\u3002\u5728\u4ed6\u7684\u5fc3\u4e0a\uff0c\u59ec\u6653\u98ce\u7684\u5730\u4f4d\uff0c\u5f53\u7136\u8fd8\u4e0d\u80fd\u4e0e\u91d1\u4e16\u9057\u3001\u8c37\u4e4b\u534e\uff0c\u9648\u5929\u5b87\u7b49\u4eba\u76f8\u63d0 \u5e76\u8bba\u3002 \u3000\u3000\u53ef\u662f\u59ec\u6653\u98ce\u5c31\u4e0d\u540c\u4e86\uff0c\u4ed6\u662f\u5c0f\u5077\u51fa\u8eab\uff0c\u7d20\u4e3a\u6b63\u6d3e\u4eba\u58eb\u6240\u4e0d\u9f7f\uff0c\u56e0\u6b64\u4e00\u65e6\u542c\u5f97\u6709\u4eba\u5728\u80cc\u540e \u66ff\u4ed6\u8fa9\u62a4\uff0c\u4fbf\u5c06\u8fd9\u4eba\u8ba4\u4e3a\u77e5\u5df1\u4e86\u3002\u8fd9\u5c31\u662f\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u987e\u5e74\u7eaa\u548c\u8f88\u4efd\uff0c\u8981\u548c\u6c5f\u5357\u7ed3\u62dc\u7684\u539f\u56e0\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4ed6\u89c1\u6c5f\u5357\u6c89\u541f\u4e0d\u8bed\uff0c\u4f5b\u7136\u8bf4\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u5acc\u6211\u7684\u4e1c\u897f\u4e0d\u5e72\u51c0\u4e48\uff1f\u4f60\u4e0d\u613f\u610f\u53d7\u6211 \u7684\u793c\u7269\uff0c\u90a3\u5c31\u662f\u770b\u4e0d\u8d77\u6211\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5357\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u54ea\u91cc\u7684\u8bdd\u6765\uff1f\u6211\u53ea\u662f\u60f3\u6211\u4e0d\u8fc7\u662f\u4e2a\u5c0f\u53ae\u51fa\u8eab\uff0c\u80fd\u6709\u4eca\u5929\uff0c\u4e5f\u5e94 \u8be5\u5fc3\u6ee1\u610f\u8db3\u4e86\uff0c\u6240\u4ee5\u4e0d\u6562\u5984\u6c42\u975e\u4efd\u3002\u5927\u54e5\uff0c\u4f60\u65e2\u7136\u76db\u610f\u62f3\u62f3\uff0c\u90a3\u6211\u5c31\u6c42\u4f60\u4e00\u4ef6\u4e8b\u60c5\u5427\u3002\u201d\u59ec \u6653\u98ce\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\uff01\u201d \u3000\u3000\u6c5f\u5357\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u4f60\u8981\u5c06\u73e0\u5b9d\u9001\u6211\uff0c\u53ef\u662f\u4efb\u6211\u8981\u4e48\uff1f\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u5f53\u7136\uff01\u201d\u6c5f\u5357\u9053\uff1a \u201c\u6211\u5168\u8981\u5462\uff1f\u4f60\u820d\u5f97\u4e48\uff1f\u201d\u59ec\u6653\u98ce\u597d\u751f\u5947\u602a\uff0c\u5fc3\u91cc\u5f88\u4e0d\u8212\u670d\uff0c\u60f3\u9053\uff1a\u201c\u600e\u7684\u4ed6\u7a81\u7136\u8d2a\u5fc3\u8d77\u6765 \u4e86\uff1f\u96be\u9053\u6211\u770b\u9519\u4e86\u4ed6\u7684\u4e3a\u4eba\uff1f\u201d\u4f46\u4ed6\u8bdd\u5df2\u51fa\u53e3\uff0c\u65ad\u65e0\u66f4\u6539\uff0c\u5f53\u4e0b\u4fbf\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u4e2a\u7bb1\u5b50\uff0c\u4f60 \u62ff\u53bb\u5427\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u662f\u8981\u4f60\u66ff\u6211\u7528\u8fd9\u7bb1\u73e0\u5b9d\uff0c\u7167\u6211\u7684\u610f\u601d\u505a\u3002\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4f60\u8981\u600e\u6837\u7528 \u6cd5\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u73e0\u5b9d\u5bf9\u6211\u6ca1\u6709\u7528\uff0c\u4f46\u5bf9\u997f\u809a\u76ae\u7684\u4eba\u5374\u6709\u7528\uff0c\u6211\u8981\u4f60\u62ff\u6765\u90fd\u6551\u6d4e\u4e86\u7a77\u4eba\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u771f\u4e0d\u6127\u662f\u6211\u7684\u5144\u5f1f\uff0c\u4f60\u7684\u60f3\u6cd5\u6b63\u548c\u6211\u4e00\u6837\u3002\u8001\u5b9e\u544a\u8bc9\u4f60\u5427\uff0c\u6211\u5077\u8fd9 \u7bb1\u73e0\u5b9d\uff0c\u4e5f\u4e0d\u662f\u6211\u81ea\u5df1\u8981\u7528\u7684\uff0c\u6700\u8fd1\u9ec4\u6cb3\u51b3\u5824\uff0c\u707e\u6c11\u65e0\u6570\uff0c\u6211\u662f\u8981\u62ff\u53bb\u53d8\u5356\uff0c\u4ea4\u7ed9\u53ef\u9760\u7684\u4eba \u53bb\u6551\u707e\u7684\u3002\u6211\u672c\u6765\u60f3\u8ba9\u4f60\u6311\u4e00\u4e24\u4ef6\u73e0\u5b9d\u505a\u4e2a\u7eaa\u5ff5\uff0c\u96be\u5f97\u4f60\u4e00\u6837\u90fd\u4e0d\u8981\u5417\uff1f\u201d\u6c5f\u5357\u5927\u559c\u9053\uff1a \u201c\u539f\u6765\u4f60\u8981\u62ff\u53bb\u6551\u707e\uff0c\u8fd9\u66f4\u662f\u529f\u5fb7\u65e0\u91cf\u3002\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u8fd9\u7bb1\u73e0\u5b9d\u662f\u4f60\u7684\u4e86\uff0c\u6709\u4ec0\u4e48\u529f\u5fb7\uff0c \u4e5f\u8be5\u8bb0\u5728\u4f60\u7684\u5e10\u4e0a\u3002\u4eba\u751f\u5f97\u4e00\u77e5\u5df1\uff0c\u6b7b\u53ef\u65e0\u61be\uff0c\u6765\uff0c\u6765\uff0c\u6765\uff0c\u4e3a\u5144\u7684\u8bf7\u4f60\u559d\u9152\u3002\u201d\u524d\u9762\u6b63\u6709 \u4e00\u95ee\u517c\u5356\u9152\u83dc\u7684\u8336\u4ead\uff0c\u59ec\u6653\u98ce\u4e0d\u7531\u5206\u8bf4\uff0c\u4fbf\u628a\u6c5f\u5357\u62c9\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u59ec\u6653\u98ce\u559d\u4e86\u51e0\u676f\uff0c\u610f\u5174\u66f4\u8c6a\uff0c\u6ed4\u6ed4\u4e0d\u7edd\u7684\u8c08\u4ed6\u751f\u5e73\u5f97\u610f\u4e4b\u4f5c\uff0c\u67d0\u5e74\u67d0\u6708\uff0c\u66fe\u6f5c\u5165\u5bab\u4e2d\uff0c \u76d7\u53bb\u4e86\u7687\u540e\u7684\u9999\u7f57\u6c57\u5dfe\uff0c\u5077\u5c1d\u4e86\u5fa1\u98df\u7f8e\u70b9\uff1b\u51e0\u65f6\u51e0\u65f6\uff0c\u5728\u6c13\u5c71\u4f1a\u4e0a\uff0c\u53c8\u66fe\u5077\u4e86\u5c11\u6797\u65b9\u4e08\u4e00\u9897 \u5ff5\u73e0\uff0c\u7a83\u8d70\u5025\u4f97\u957f\u8001\u7684\u7075\u4e39\u5999\u836f\u2026\u2026\u6240\u8c08\u7684\u90fd\u662f\u6781\u6709\u8da3\u7684\u5999\u4e8b\uff0c\u6c5f\u5357\u966a\u4ed6\u559d\u9152\uff0c\u542c\u4ed6\u8bf4\u8bdd\uff0c \u53cd\u800c\u4e00\u58f0\u4e0d\u54cd\u3002 \u3000\u3000\u59ec\u6653\u98ce\u653e\u4e0b\u9152\u676f\uff0c\u671b\u4e86\u6c5f\u5357\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u4e00\u5b9a\u6709\u4ec0\u5fc3\u4e8b\u3002\u201d\u6c5f\u5357\u5f3a\u7b11\u8bf4\u9053\uff1a \u201c\u4f60\u4ece\u4f55\u89c1\u5f97\uff1f\u201d\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u6211\u8bb0\u5f97\u4f60\u7684\u7ef0\u53f7\uff0c\u522b\u4eba\u4e0d\u662f\u53eb\u4f60\u505a\u2018\u591a\u5634\u7684\u6c5f\u5357\u2019\u5417\uff0c\u505a \u54e5\u54e5\u7684\u4eca\u6b21\u8bf7\u4f60\u559d\u9152\uff0c\u4f60\u5374\u4e3a\u4f55\u8bdd\u4e5f\u4e0d\u591a\u8bf4\u534a\u53e5\uff1f\u201d\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u6211\u662f\u5728\u542c\u4f60\u8bf4\u5440\uff01\u4f60\u8bf4\u5f97 \u6709\u8da3\uff0c\u6211\u82e5\u63d2\u5634\u8fdb\u53bb\uff0c\u6253\u65ad\u4e86\u4f60\u7684\u8bdd\u5934\uff0c\u5c82\u4e0d\u662f\u53d8\u6210\u4e86\u4e0d\u8bc6\u8da3\u4e86\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4f60\u4e5f\u8bf4\u5f97\u6709\u7406\uff0c\u563f\uff0c\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\uff0c\u8fd8\u662f\u4f60\u7684\u795e\u8272\u4e0d\u5bf9\uff0c\u4f60\u5f53\u771f\u6ca1\u6709 \u5fc3\u4e8b\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u5f53\u771f\u6ca1\u6709\uff01\u4f60\u8bf4\u6211\u795e\u8272\u4e0d\u5bf9\uff0c\u5927\u7ea6\u662f\u56e0\u4e3a\u6211\u4e0d\u80fd\u559d\u9152\u7684\u7f18\u6545\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u5ffd\u5730\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u5fc3\u4e8b\uff0c\u6211\u5012\u6709\u5fc3\u4e8b\uff01\u201d\u6c5f\u5357\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u72ec\u5f80\u72ec\u6765\uff0c \u65e0\u7275\u65e0\u6302\uff0c\u5374\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4f60\u662f\u77e5\u9053\u6211\u7684\u51fa\u8eab\u6765\u5386\u7684\uff0c\u6211\u505a\u5c0f\u5077\uff0c\u52ab\u5bcc\u6d4e\u8d2b\uff0c\u65c1\u4eba\u770b\u6211\u4e0d\u8d77\uff0c\u6211\u5374\u5e76 \u4e0d\u89c9\u5f97\u803b\u8fb1\u3002\u6211\u6700\u611f\u5230\u96be\u8fc7\u7684\uff0c\u662f\u66ff\u5e08\u7236\u8d4e\u7f6a\u3002\u6211\u5e08\u7236\u751f\u524d\u4f5c\u6076\u591a\u7aef\uff0c\u4f46\u5bf9\u6211\u5374\u771f\u4e0d\u9519\uff0c\u6240 \u91cc\u8d8a\u53d1\u4e0d\u5b89\uff0c\u82e5\u4e0d\u66ff\u4ed6\u8d4e\u7f6a\uff0c\u603b\u4f3c\u89c9\u5f97\u6b20\u4e86\u4e00\u7b14\u503a\u4f3c\u7684\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u505a\u4e86\u8bb8\u591a\u597d\u4e8b\uff0c\u4e5f\u7b97\u662f\u66ff\u5e08\u7236\u8d4e\u7f6a\u4e86\u3002\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4e0d\uff0c\u90a3\u8fd8\u4e0d \u591f\uff0c\u90a3\u8fd8\u4e0d\u591f\uff01\u6211\u7684\u5e08\u7236\u751f\u524d\u603b\u60f3\u5728\u6b66\u6797\u4e2d\u51fa\u4eba\u5934\u5730\uff0c\u5728\u6b66\u5b66\u4e0a\u4e5f\u7684\u786e\u66fe\u7528\u8fc7\u82e6\u529f\uff0c\u53ef\u6068\u4ed6 \u7684\u8def\u8d70\u9519\u4e86\uff0c\u7559\u4e0b\u7684\u5374\u662f\u6076\u540d\uff01\u6211\u8981\u7ee7\u627f\u4ed6\u6b66\u5b66\u7684\u9057\u5fd7\uff0c\u5374\u53cd\u5176\u9053\u800c\u884c\u4e4b\uff0c\u505a\u51fa\u4e00\u4e9b\u5bf9\u6b66\u6797 \u6709\u76ca\u4e4b\u4e8b\uff0c\u8ba9\u540e\u4e16\u4e4b\u4eba\uff0c\u8c08\u8d77\u6211\u5e08\u7236\u7684\u65f6\u5019\uff0c\u4e5f\u4f1a\u8bf4\u9053\uff1a\u5b5f\u795e\u901a\u867d\u662f\u4e2a\u4f5c\u6076\u591a\u7aef\u7684\u5927\u9b54\u5934\uff0c \u4f46\u4e5f\u6709\u4e00\u6837\u529f\u52b3\uff0c\u4ed6\u6559\u51fa\u4e86\u4e00\u4e2a\u597d\u5f1f\u5b50\uff01\u201d \u3000\u3000\u6c5f\u5357\u5bf9\u59ec\u6653\u98ce\u6e10\u6e10\u53d1\u751f\u656c\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u7684\u82e6\u5fc3\u53ef\u4f69\uff0c\u4ee5\u4f60\u7684\u806a\u660e\u624d\u667a\uff0c\u4ee5\u4f60\u73b0 \u6709\u7684\u6b66\u5b66\u9020\u8be3\uff0c\u76f8\u4fe1\u4f60\u7684\u5fd7\u613f\uff0c\u5b9a\u7136\u53ef\u4ee5\u8fbe\u5230\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u5c06\u58f6\u4e2d\u5269\u9152\u4e00\u53e3\u559d\u5c3d\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u5c31\u662f\u56e0\u4e3a\u609f\u6027\u592a\u5dee\uff0c\u6839\u57fa\u592a\u8584\uff0c\u6545\u6b64\u5e38\u611f \u5fc3\u6709\u4f59\u800c\u529b\u4e0d\u8db3\uff01\u6211\u82e5\u80fd\u5b8c\u6210\u5fc3\u613f\uff0c\u9664\u975e\u6709\u4e00\u4e2a\u4eba\u80af\u5e2e\u52a9\u6211\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u5462\uff1f\u201d\u59ec \u6653\u98ce\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u4e5f\u662f\u4f60\u7684\u597d\u670b\u53cb\uff0c\u4ed6\u5c31\u662f\u91d1\u5927\u4fa0\u2014\u2014\u91d1\u4e16\u9057\uff01\u666e\u5929\u4e4b\u4e0b\uff0c\u53ea\u6709\u4ed6\u80fd\u52a9\u6211\u5b8c \u6210\u5fc3\u613f\uff01\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u4f60\u8981\u91d1\u5927\u4fa0\u5982\u4f55\u52a9\u4f60\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u518d\u5524\u4e86\u4e00\u58f6\u9152\uff0c\u53c8\u559d\u4e86\u4e24\u676f\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u6211\u7684\u5fc3\u4e8b\u90fd\u5bf9\u4f60\u8bf4\u4e86\u5427\u3002\u4f60\u662f\u77e5 \u9053\u7684\uff0c\u6211\u5e08\u7236\u6bd5\u751f\u81f4\u529b\u7684\uff0c\u5c31\u662f\u8981\u628a\u6b63\u90aa\u5404\u6d3e\u7684\u6b66\u529f\u5408\u800c\u4e3a\u4e00\uff0c\u4ed6\u751f\u524d\u867d\u7136\u4f5c\u6076\u591a\u7aef\uff0c\u8fd9\u4e2a \u60f3\u6cd5\u5374\u662f\u4e0d\u9519\u3002\u4eba\u6709\u90aa\u6b63\u4e4b\u5206\uff0c\u6b66\u529f\u672c\u8eab\u5374\u4e0d\u5e94\u6709\u90aa\u6b63\u4e4b\u5206\uff0c\u5b83\u53ef\u4ee5\u7528\u6765\u5bb3\u4eba\uff0c\u4e5f\u53ef\u4ee5\u7528\u6765 \u6551\u4eba\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u518d\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u5f53\u521d\u6211\u5f80\u5c11\u6797\u5bfa\u5077\u4e66\u7684\u65f6\u5019\uff0c\u672c\u662f\u4e00\u65f6\u5174\u8d77\uff0c\u968f\u610f\u800c\u4e3a\u3002\u540e\u6765\u6211 \u8bfb\u8fd9\u7c7b\u6b66\u5b66\u79d8\u5178\uff0c\u8bfb\u4e0a\u4e86\u75db\uff0c\u4e5f\u5c31\u5077\u4e0a\u4e86\u763e\uff0c\u8bfb\u4e86\u5341\u591a\u672c\u4e4b\u540e\uff0c\u6211\u53d1\u89c9\u5404\u6d3e\u6b66\u529f\uff0c\u5927\u90fd\u6709\u8109 \u7edc\u53ef\u901a\u4e4b\u5904\uff0c\u8fd9\u624d\u5174\u8d77\u4e86\u7ee7\u627f\u5e08\u7236\u9057\u5fd7\u7684\u5ff5\u5934\uff0c\u53ef\u60dc\u6211\u6b66\u5b66\u7684\u5e95\u5b50\u592a\u5dee\uff0c\u609f\u6027\u4e5f\u4e0d\u591f\uff0c\u6709\u82e5 \u5e72\u6b66\u5b66\u4e0a\u7684\u96be\u9898\uff0c\u81f3\u4eca\u4ecd\u662f\u6478\u7d22\u96be\u901a\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u6211\u66fe\u542c\u91d1\u5927\u4fa0\u8a00\u9053\uff1a\u4e54\u5317\u6e9f\u7684\u6b66\u529f\u79d8\u7c4d\u4fbf\u662f\u5c06\u6b63\u90aa\u5404\u6d3e\u7684\u6b66\u529f\u7194\u4e8e\u4e00\u7089\u7684\uff0c \u6b66\u5b66\u4e0a\u7684\u96be\u9898\uff0c\u5728\u90a3\u672c\u79d8\u53d1\u4e2d\u5dee\u4e0d\u591a\u90fd\u5df2\u89e3\u51b3\u4e86\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u5c31\u662f\u5440\u3002\u6211\u8bfb\u4e86\u5341\u51e0\u672c\u5404\u6d3e\u7684\u79d8\u5178\u4e4b\u540e\uff0c\u89c9\u5f97\u90fd\u4e0d\u53ca\u4e54\u5317\u6e9f\u6b66\u529f\u79d8\u7b3c\u7684\u7cbe \u5fae\u5965\u5999\uff0c\u867d\u7136\u6211\u5bf9\u4e8e\u4e54\u5317\u6e9f\u7684\u6b66\u5b66\u4e5f\u4e0d\u8fc7\u662f\u4ec5\u5f97\u7aa5\u4e00\u9cde\u534a\u722a\u3002\u201d \u3000\u3000\u6c5f\u5357\u542c\u5230\u8fd9\u91cc\uff0c\u7981\u4e0d\u4f4f\u63d2\u5634\u9053\uff1a\u201c\u4f60\u8bf4\u5404\u6d3e\u7684\u6b66\u5b66\u90fd\u6bd4\u4e0d\u4e0a\u4e54\u5317\u6e9f\u7684\uff0c\u8fd9\u4e5f\u4e0d\u89c1\u5f97 \u5427\uff1f\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6211\u662f\u6307\u6211\u8bfb\u8fc7\u7684\u800c\u8a00\uff0c\u5929\u5c71\u6d3e\u7684\u5185\u529f\u5fc3\u6cd5\uff0c\u5c11\u6797\u6d3e\u7684\u6613\u7b4b\u6d17\u9ad3\u4e8c\u7ecf\uff0c\u90a3 \u90fd\u662f\u6700\u4e0a\u4e58\u7684\u6b66\u529f\uff0c\u6211\u4e0d\u6562\u53bb\u5077\uff0c\u672a\u66fe\u89c1\u8fc7\uff0c\u90a3\u4e5f\u5c31\u65e0\u4ece\u6bd4\u8f83\u4e86\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u987f\u4e86\u4e00\u987f\uff0c\u7eed\u9053\uff1a\u201c\u56e0\u6b64\uff0c\u6211\u60f3\u8d77\u4e86\u91d1\u5927\u4fa0\u6765\u3002\u5f53\u4eca\u53ea\u6709\u4ed6\u4e00\u4eba\u5bf9\u4e54\u5317\u6e9f\u7684\u6b66\u529f \u79d8\u7c4d\u5f97\u7aa5\u5168\u8c79\uff0c\u800c\u4ed6\u53c8\u662f\u61c2\u6b63\u5b97\u7684\u5185\u529f\u5fc3\u6cd5\u7684\u4eba\uff0c\u6240\u4ee5\u6211\u975e\u5e38\u76fc\u671b\u80fd\u89c1\u5f97\u5230\u4ed6\uff0c\u5c06\u6b66\u5b66\u96be\u9898 \u96be\u5411\u4ed6\u8bf7\u6559\u3002\u53ef\u60dc\u6211\u8d70\u904d\u56db\u65b9\uff0c\u5374\u65e0\u7f18\u4e0e\u4ed6\u4e00\u9762\u3002\u4f60\u53ef\u80fd\u627e\u5230\u4ed6\u5417\uff1f\u4ee5\u4f60\u4e0e\u4ed6\u7684\u4ea4\u60c5\uff0c\u4f60\u53ef \u613f\u610f\u4ee3\u6211\u8fdb\u8a00\uff0c\u8bf7\u4ed6\u53d7\u6211\u505a\u4e2a\u8bb0\u540d\u5f1f\u5b50\u5417\uff1f\u5b66\u65e0\u524d\u540e\uff0c\u8fbe\u8005\u4e3a\u5e08\uff0c\u6211\u5e74\u7eaa\u867d\u7136\u6bd4\u4ed6\u5927\uff0c\u5374\u662f \u7518\u5fc3\u60c5\u613f\u62dc\u4ed6\u4e3a\u5e08\u7684\u3002\u201d \u3000\u3000\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u662f\u8bbf\u5bfb\u91d1\u5927\u4fa0\u7684\u884c\u8e2a\uff0c\u5374\u8fd8\u6ca1\u6709\u529e\u6cd5\u5462\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4f60\u53c8\u6709\u4ec0\u4e48\u4e8b\u60c5\u6025\u4e8e\u8981\u627e\u4ed6\uff1f\u201d\u6c5f\u5357\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u5e76\u975e\u4ec0\u4e48\u8981 \u4e8b\uff0c\u4e0d\u8fc7\u591a\u5e74\u672a\u89c1\uff0c\u60f3\u4e0e\u4ed6\u53d9\u53d9\u7f62\u4e86\u3002\u201d\u59ec\u6653\u98ce\u76ef\u4ed6\u4e00\u773c\uff0c\u663e\u51fa\u4f3c\u4fe1\u975e\u4fe1\u7684\u795e\u60c5\u3002 \u3000\u3000\u6c5f\u5357\u6015\u4ed6\u8ffd\u95ee\uff0c\u5fd9\u628a\u8bdd\u9898\u5f15\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u81f3\u4e8e\u8bf4\u5230\u4f60\u8981\u62dc\u4ed6\u4e3a\u5e08\uff0c\u90a3\u662f\u592a\u8c26\u865a\u4e86\u3002\u636e\u6211 \u6240\u77e5\uff0c\u91d1\u5927\u4fa0\u867d\u7136\u517c\u6b63\u90aa\u5404\u6d3e\u4e4b\u957f\uff0c\u4f46\u5bf9\u4e8e\u5404\u6d3e\u7684\u79d8\u5178\uff0c\u4e5f\u8fd8\u672a\u66fe\u89c1\u8fc7\u3002\u4f60\u5077\u4e86\u8fd9\u8bb8\u591a\uff0c\u82e5 \u662f\u548c\u4ed6\u5207\u78cb\uff0c\u53ea\u6015\u5bf9\u4ed6\u4e5f\u6709\u597d\u5904\u3002\u6211\u8fd8\u77e5\u9053\u91d1\u5927\u4fa0\u4ed6\u4e5f\u662f\u60f3\u878d\u5408\u6b63\u90aa\u5404\u6d3e\u4e4b\u957f\uff0c\u5faa\u7740\u6b63\u6d3e\u6b66 \u529f\u7684\u9014\u5f84\uff0c\u5c06\u4e54\u5317\u6e9f\u7684\u79d8\u53d1\u5fc3\u6cd5\uff0c\u51b6\u4e8e\u4e00\u7089\uff0c\u53e6\u521b\u4e00\u95e8\u5149\u660e\u6b63\u5927\u7684\u6b66\u529f\u7684\u3002\u4f60\u4eec\u6b63\u8bf4\u5f97\u4e0a\u662f \u5fd7\u540c\u9053\u5408\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u8bf4\u662f\u5fd7\u540c\u9053\u5408\u5c1a\u53ef\uff0c\u8c08\u5230\u5207\u78cb\u4e8c\u5b57\uff0c\u6211\u53ef\u4e0d\u6562\u3002\u201d\u8ddf\u7740\u53c8\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u60f3 \u662f\u8fd9\u6837\u60f3\uff0c\u53ef\u662f\u600e\u4e48\u80fd\u89c1\u5f97\u7740\u4ed6\uff1f\u201d \u3000\u3000\u6c5f\u5357\u9ed8\u7136\u4e0d\u8bed\uff0c\u59ec\u6653\u98ce\u559d\u4e86\u4e00\u676f\uff0c\u5ffd\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u6628\u665a\u77a7\u4f60\u7684\u8eab\u624b\uff0c\u6562\u60c5\u4f60\u5f97\u8fc7\u91d1\u5927\u4fa0 \u7684\u6307\u6559\uff0c\u4e5f\u5b66\u4f1a\u4e86\u79d8\u7c4d\u4e0a\u7684\u4e00\u4e9b\u6b66\u529f\uff1f\u201d \u3000\u3000\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u70b9\u529f\u592b\uff0c\u5f53\u7136\u96be\u9003\u5927\u54e5\u7684\u6cd5\u773c\u3002\u4f60\u4e5f\u5b9a\u7136\u770b\u5f97\u51fa\u6765\uff0c\u6211\u6240\u61c2\u5f97\u7684\u79d8\u75ab \u529f\u592b\uff0c\u8fde\u4e00\u9cde\u534a\u722a\u4e5f\u8c08\u4e0d\u4e0a\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4f60\u4e5f\u603b\u7b97\u662f\u7565\u7aa5\u95e8\u5f84\u4e86\u3002\u8981\u662f\u65e0\u6cd5\u89c1\u5f97\u7740\u91d1\u5927\u4fa0\u7684\u8bdd\uff0c\u4f60\u53ef\u613f\u610f\u82b1\u5341\u5e74\u529f \u592b\uff0c\u548c\u6211\u4e00\u540c\u7422\u78e8\u4e0a\u4e58\u7684\u6b66\u529f\u5fc3\u6cd5\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u5357\u8e0c\u8e87\u9053\uff1a\u201c\u53ea\u6015\u6211\u914d\u4e0d\u4e0a\u548c\u5927\u54e5\u5207\u78cb\u3002\u8981\u662f\u5927\u54e5\u4e0d\u5acc\u5f03\u7684\u8bdd\uff0c\u4e09\u5e74\u4e4b\u540e\uff0c\u8bf7\u5230\u5bd2\u820d \u5982\u4f55\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u5f85\u4e09\u5e74\uff1f\u201d\u6c5f\u5357\u652f\u543e\u8bf4\u9053\uff1a\u201c\u6211\u662f\u60f3\u5728\u8fd9\u4e09\u5e74\u4e4b\u4e2d\uff0c\u5230\u5404\u5730\u62dc\u8bbf \u65e7\u65e5\u7684\u5e08\u53cb\u3002\u9664\u4e86\u91d1\u5927\u4fa0\u4e4b\u5916\uff0c\u8fd8\u6709\u8427\u9752\u5cf0\u4e0e\u6211\u7684\u4e49\u5144\u9648\u5929\u5b87\u7b49\u4eba\u3002\u201d\u5176\u5b9e\u4ed6\u662f\u60f3\u8bbf\u67e5\u81ea\u5df1 \u5b69\u5b50\u7684\u4e0b\u843d\u3002\u59d1\u4e14\u6682\u4ee5\u4e09\u5e74\u4e3a\u671f\u3002\u4f46\u4ed6\u4e0d\u60ef\u8bf4\u8c0e\uff0c\u6240\u4ee5\u8bf4\u6765\u603b\u662f\u4e0d\u5927\u81ea\u7136\u3002 \u3000\u3000\u59ec\u6653\u98ce\u5df2\u6709\u4e86\u516d\u4e03\u5206\u9152\u610f\uff0c\u95fb\u8a00\u7b11\u9053\uff1a\u201c\u4f60\u5012\u662f\u5f88\u91cd\u53cb\u60c5\uff0c\u4e0d\u6789\u6211\u4e0e\u4f60\u7eea\u4ea4\u3002\u201d\u987f\u4e86\u4e00 \u987f\uff0c\u5f80\u4e0b\u7eed\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u5fc5\u5927\u8fc7\u81ea\u8c26\uff0c\u4f60\u7684\u6b66\u529f\u867d\u672a\u5230\u4e0a\u4e58\u5883\u754c\uff0c\u4f46\u9664\u4e86\u91d1\u5927\u4fa0\u4e4b\u5916\uff0c\u53ef\u4ee5 \u4e0e\u6211\u5207\u78cb\u4e54\u5317\u6e9f\u6b66\u529f\u79d8\u590d\u7684\u5fc3\u6cd5\u7684\uff0c\u4e5f\u5c31\u53ea\u6709\u4f60\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u4e3a\u4e86\u627e\u5bfb\u5b69\u5b50\uff0c\u5fc3\u4e0a\u603b\u662f\u6709\u7740\u90a3\u516b\u4e2a\u8499\u9762\u5973\u5b50\u7684\u5f62\u8c61\uff0c\u8fd9\u65f6\u4ed6\u4e5f\u6709\u4e86\u4e09\u4e24\u5206\u9152\u610f\uff0c \u7981\u4e0d\u4f4f\u7a81\u7136\u51b2\u53e3\u800c\u51fa\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u89c1\u5f97\u7f62\uff0c\u9664\u4e86\u4f60\u6211\u4e4b\u5916\uff0c\u53ea\u6015\u8fd8\u6709\u4eba\u8bc6\u5f97\u90a3\u79d8\u7c4d\u4e0a\u7684\u6b66 \u529f\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u4e00\u6115\uff0c\u6479\u5730\u628a\u9152\u676f\u653e\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u7ec4\u6765\u5c71\u7684\u90a3\u4e5d\u4e2a\u5929\u9b54\u5973\u5417\uff1f\u55ef\uff0c\u4f60\u600e\u4e48 \u4e5f\u77e5\u9053\u5979\u4eec\uff1f\u201d\u59ec\u6653\u98ce\u6b64\u8a00\u4e00\u51fa\uff0c\u8f6e\u5230\u6c5f\u5357\u6bd4\u4ed6\u66f4\u4e3a\u60ca\u6115\u4e86\u3002 \u3000\u3000\u6c5f\u5357\u6309\u637a\u4e0d\u4f4f\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u600e\u4e48\uff0c\u5979\u4eec\u539f\u6765\u5171\u662f\u6709\u4e5d\u4e2a\u7684\u4e48\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u671b\u7740\u6c5f\u5357\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u5b9a\u7136\u662f\u6709\u4e8b\u60c5\u7792\u7740\u54e5\u54e5\uff0c\u770b\u6765\u5c31\u662f\u4e0e\u4e5d\u4e2a\u5929\u9b54 \u5973\u6709\u5173\uff0c\u662f\u4f60\u5076\u7136\u78b0\u7740\u5979\u4eec\uff0c\u8fd8\u662f\u5979\u4eec\u627e\u4f60\uff0c\u4f60\u5b9e\u8bf4\u4e86\u5427\uff1f\u201d \u3000\u3000\u6c5f\u5357\u5b9a\u4e86\u5b9a\u795e\uff0c\u7ecf\u8fc7\u4e86\u8fd9\u4e00\u756a\u957f\u8c08\uff0c\u4ed6\u5bf9\u59ec\u6653\u98ce\u53c8\u591a\u4e86\u51e0\u5206\u4fe1\u4efb\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u59ec\u5927\u54e5 \u6e38\u620f\u98ce\u5c18\uff0c\u5fc3\u80a0\u5374\u662f\u4e0e\u6211\u4e00\u822c\u826f\u5584\u3002\u4f55\u51b5\u81ea\u5df2\u4e0e\u4ed6\u7ed3\u62dc\uff0c\u5b9e\u5728\u4e5f\u4e0d\u8be5\u518d\u7792\u7740\u4ed6\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u5148\u5411\u4ed6\u544a\u4e86\u4e2a\u7f6a\uff0c\u8bf4\u9053\uff1a\u201c\u975e\u662f\u6211\u4fe1\u4e0d\u8fc7\u5927\u54e5\uff0c\u5b9e\u662f\u90a3\u516b\u4e2a\u5973\u5b50\u6b66\u529f\u592a\u5f3a\uff0c\u6211\u6015\u6cc4 \u6f0f\u4e86\u98ce\u58f0\uff0c\u6253\u8349\u60ca\u86c7\uff0c\u53cd\u800c\u4e0d\u5999\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff0c\u201c\u5979\u4eec\u6b66\u529f\u6df1\u6d45\uff0c\u6211\u5168\u90e8\u77e5\u9053\u3002\u4f60\u6709\u4ec0\u4e48\u628a\u67c4\u843d\u5728\u5979\u4eec\u624b\u4e0a\uff0c\u5982\u6b64\u987e\u5fcc\u3002 \u597d\uff0c\u4f60\u8bf4\uff0c\u5929\u5927\u7684\u4e8b\u60c5\uff0c\u5927\u54e5\u66ff\u4f60\u4f5c\u4e3b\u3002\u201d \u3000\u3000\u6c5f\u5357\u5c06\u90a3\u516b\u4e2a\u8499\u9762\u5973\u5b50\u4e0a\u95e8\u6bd4\u6b66\uff0c\u4ee5\u53ca\u7231\u5b50\u88ab\u593a\u7684\u4e8b\u60c5\uff0c\u539f\u539f\u672c\u672c\u5730\u544a\u8bc9\u4e86\u59ec\u6653\u98ce\u3002\u59ec \u6653\u98ce\u5c06\u9152\u676f\u91cd\u91cd\u7684\u4e00\u987f\uff0c\u8bf4\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u5979\u4eec\u9a97\u53d6\u4e86\u4f60\u7684\u6b66\u529f\uff0c\u8fd8\u5e26\u8d70\u4e86\u4f60\u7684\u513f\u5b50\uff0c\u5c31 \u7b97\u4f60\u4e0d\u662f\u6211\u7684\u5144\u5f1f\uff0c\u6211\u4e5f\u975e\u66ff\u4f60\u51fa\u5934\u4e0d\u53ef\u3002\u201d \u3000\u3000\u6c5f\u9053\uff1a\u201c\u591a\u8c22\u5927\u54e5\u3002\u53ea\u662f\u5927\u54e5\u8bf4\u5979\u4eec\u5171\u6709\u4e5d\u4e2a\uff0c\u6211\u5374\u53ea\u89c1\u5230\u516b\u4e2a\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u542c\u4f60\u7684\u60c5\u5f62\uff0c\u6700\u5389\u5bb3\u7684\u90a3\u4e2a\u5c1a\u672a\u9732\u9762\uff01\u201d \u3000\u3000\u6c5f\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5979\u4eec\u7aef\u7684\u662f\u4ec0\u4e48\u4eba\uff0c\u6700\u5389\u5bb3\u7684\u90a3\u4e2a\u5389\u5bb3\u5230\u4ec0\u4e48\u7a0b\u5ea6\uff1f\u201d\u59ec\u6653\u98ce \u9053\uff1a\u201c\u90a3\u516b\u4e2a\u8499\u9762\u5973\u5b50\u662f\u5389\u80dc\u7537\u7684\u4f8d\u5973\uff0c\u4f60\u731c\u4e2d\u7684\u4e86\u3002\u8fd8\u6709\u4e00\u4e2a\uff0c\u8fde\u6211\u4e5f\u4e0d\u77e5\u9053\u5979\u7684\u5e95\u7ec6\uff0c \u53ea\u77e5\u5979\u6781\u5176\u5389\u5bb3\uff0c\u8fd8\u4f1a\u4f7f\u6bd2\uff0c\u6211\u53ea\u53ef\u4ee5\u65ad\u5b9a\u5979\u5fc5\u7136\u5927\u6709\u6765\u5934\uff0c\u4e0d\u662f\u5389\u80dc\u7537\u7684\u4f8d\u5973\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u4f60\u5bf9\u5979\u7684\u6b66\u529f\u600e\u7684\u77e5\u5f97\u8fd9\u6837\u6e05\u695a\uff1f\u5979\u6bd4\u4f60\u5982\u4f55\uff1f\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6211\u548c\u5979\u4ea4\u8fc7 \u4e00\u6b21\u624b\uff0c\u6211\u662f\u65e0\u6cd5\u80dc\u5979\uff0c\u4f46\u5979\u8981\u60f3\u80dc\u6211\uff0c\u6015\u4e5f\u4e0d\u5bb9\u6613\u3002\u201d\u8fd9\u6837\u8bf4\u6cd5\uff0c\u5373\u662f\u81ea\u8ba4\u900a\u4e86\u4e00\u7b79\uff0c\u6c5f \u5357\u542c\u4e86\uff0c\u66f4\u4e3a\u60ca\u8be7\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u59ec\u6653\u98ce\u4fbf\u5c06\u548c\u90a3\u7fa4\u201c\u5929\u9b54\u5973\u201d\u53d1\u751f\u7ea0\u7eb7\u7684\u7ecf\u8fc7\uff0c\u544a\u8bc9\u6c5f\u5357\u3002 \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4e8b\u60c5\u53d1\u751f\u5728\u4e09\u5e74\u524d\u7684\u6e05\u660e\u65f6\u8282\uff0c\u6211\u5ffd\u7136\u5fc3\u8840\u6765\u6f6e\uff0c\u8dd1\u5230\u767e\u82b1\u8c37\u770b\u5389\u80dc\u7537\u7684 \u575f\u5893\uff0c\u90a3\u5ea7\u575f\u5893\uff0c\u5c31\u662f\u91d1\u5927\u4fa0\u7ed9\u5979\u7acb\u7684\uff0c\u4f60\u53ef\u77e5\u9053\u4e48\uff1f\u201d\u6c5f\u5357\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u5728 \u5979\u751f\u524d\u4e3a\u5979\u6240\u7d2f\uff0c\u5728\u5979\u6b7b\u540e\u4ecd\u4e3a\u5979\u6240\u8ff7\uff0c\u8fd9\u771f\u4ee4\u6211\u4e3a\u4ed6\u611f\u5230\u4e0d\u503c\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6211\u548c\u91d1\u5927\u4fa0\u76f8\u77e5\u4e0d\u6df1\uff0c\u4f46\u4ed6\u662f\u6211\u552f\u4e00\u656c\u4ef0\u7684\u4eba\uff0c\u5f53\u65f6\u6211\u4e5f\u662f\u8fd9\u6837\u60f3\u3002\u6b63\u5f53 \u6211\u62c2\u62ed\u5893\u7891\uff0c\u550f\u5618\u53f9\u606f\u7684\u65f6\u5019\uff0c\u5ffd\u7136\u6709\u4e24\u4e2a\u5c11\u5973\u8d70\u6765\uff0c\u8bf4\u9053\uff1a\u2018\u59ec\u5148\u751f\uff0c\u4f60\u8fd8\u8ba4\u5f97\u6211\u4eec\u4e48\uff1f \u54b1\u4eec\u7684\u5e08\u7236\u751f\u524d\u867d\u6709\u6df1\u4ec7\u5927\u6068\uff0c\u4f46\u73b0\u5728\u5df2\u662f\u4e00\u6b7b\u767e\u4e86\uff0c\u4f55\u51b5\u4ed6\u4eec\u7684\u6b66\u529f\u4e5f\u662f\u4e00\u8109\u76f8\u627f\uff0c\u60f3\u6765 \u4f60\u4e0d\u4f1a\u56e0\u4e3a\u5e08\u7236\u7684\u539f\u6545\uff0c\u800c\u628a\u6211\u4eec\u5f53\u4f5c\u4ec7\u4eba\u5427\uff1f\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u8ba4\u5f97\u5979\u4eec\u662f\u5389\u80dc\u7537\u7684\u4f8d\u5973\uff0c\u6211\u4e4b\u6240\u4ee5\u5728\u6e05\u660e\u65f6\u8282\uff0c\u6765\u770b\u5389\u80dc\u7537\u7684\u575f\u5893\uff0c\u5176\u5b9e\u5c31 \u662f\u4e3a\u4e86\u5979\u4eec\u3002\u56e0\u4e3a\u5f53\u65f6\u6211\u6b63\u662f\u5728\u6b66\u5b66\u4e0a\u4eff\u60f6\u63a2\u7d22\uff0c\u96be\u4ee5\u81ea\u901a\uff0c\u5f88\u60f3\u5f97\u4e2a\u5fd7\u540c\u9053\u5408\u7684\u670b\u53cb\uff0c\u5f7c \u6b64\u5207\u78cb\u3002\u91d1\u5927\u4fa0\u65e2\u7136\u4e0d\u77e5\u8e2a\u8ff9\uff0c\u6211\u4fbf\u81ea\u7136\u800c\u7136\u7684\u60f3\u8d77\u5389\u80dc\u7537\u5f53\u5e74\u90a3\u7fa4\u4f8d\u5973\u6765\u3002\u4f46\u4e00\u6765\u7537\u5973\u6709 \u522b\uff0c\u4e8c\u6765\u5e08\u95e8\u6709\u4ec7\uff0c\u4e09\u6765\u4e0d\u77e5\u9053\u5979\u4eec\u7684\u884c\u5f84\u5982\u4f55\uff1f\u56db\u6765\u4e5f\u4e0d\u77e5\u9053\u5979\u4eec\u5bf9\u79d8\u7c4d\u7684\u6b66\u529f\u61c2\u5f97\u591a \u5c11\uff1f\u6211\u731c\u60f3\u5979\u4eec\u5728\u6e05\u660e\u65f6\u8282\uff0c\u5b9a\u4f1a\u6765\u7ed9\u5979\u4eec\u7684\u4e3b\u4eba\u626b\u5893\uff0c\u56e0\u6b64\uff0c\u6211\u5c31\u6b63\u662f\u62b1\u7740\u4e00\u79cd\u8bd5\u63a2\u7684\u5fc3 \u60c5\uff0c\u6765\u548c\u5979\u4eec\u78b0\u5934\u7684\uff0c\u679c\u7136\u7ed9\u6211\u78b0\u4e0a\u4e86\u3002 \u3000\u3000\u201c\u4e8e\u662f\u6211\u5bf9\u5979\u4eec\u8bf4\u9053\uff1a\u2018\u4f60\u4eec\u6240\u8bf4\u7684\u6b63\u5c31\u662f\u6211\u60f3\u8bf4\u7684\u8bdd\uff0c\u4f46\u4e0d\u77e5\u4e24\u4f4d\u6b64\u6765\uff0c\u8fd8\u6709\u4f55\u6307 \u6559\uff0c\uff0c\u5979\u4eec\u8bf4\u9053\uff1a\u2018\u6211\u4eec\u8fd8\u6709\u51e0\u4e2a\u59d0\u59b9\uff0c\u60f3\u89c1\u89c1\u59ec\u5148\u751f\u3002\u4e0d\u77e5\u59ec\u5148\u751f\u53ef\u80af\u968f\u6211\u4eec\u524d\u5f80\u4e48\uff1f\u2019 \u6211\u7acb\u5373\u4fbf\u70b9\u5934\u7b54\u5141\u3002 \u3000\u3000\u201c\u5389\u80dc\u7537\u7684\u575f\u5893\u79bb\u7956\u6cab\u5c71\u4e0d\u8fdc\uff0c\u6211\u4eec\u8d70\u4e86\u534a\u5929\uff0c\u4fbf\u8e0f\u8fdb\u4e86\u7ec4\u6765\u5c71\u3002\u90a3\u4e24\u4e2a\u5973\u5b50\u5ffd\u9053\uff1a \u2018\u59ec\u5148\u751f\uff0c\u59d4\u5c48\u4f60\u4e00\u70b9\uff0c\u8bf7\u4f60\u7f1a\u4e0a\u773c\u775b\u3002\u2019\u8fd9\u672c\u6765\u662f\u9ed1\u9053\u4e0a\u7684\u89c4\u77e9\uff0c\u6211\u5fc3\u91cc\u4e0d\u5927\u9ad8\u5174\uff0c\u4f46\u8fd8 \u662f\u4f9d\u4ece\u5979\u4eec\uff0c\u4efb\u7531\u5979\u4eec\u7528\u539a\u5e03\u7f1a\u4e86\u773c\u775b\u4e86 \u3000\u3000\u201c\u6211\u8ddf\u968f\u4eec\uff0c\u8f6c\u4e86\u8bb8\u591a\u5927\u5f2f\u5c0f\u5f2f\uff0c\u8d70\u8fc7\u8bb8\u591a\u7f8a\u80a0\u66f2\u5f84\uff0c\u51ed\u7740\u6211\u542c\u58f0\u8fa8\u7269\u548c\u8f7b\u529f\u7684\u672c\u9886\uff0c \u8fd8\u6500\u767b\u4e86\u5f88\u591a\u5ced\u58c1\u659c\u5761\u3002\u6700\u540e\u5f53\u5979\u4eec\u89e3\u5f00\u6211\u773c\u775b\u7684\u675f\u7f1a\u7684\u65f6\u5019\uff0c\u6211\u53d1\u73b0\u81ea\u5df1\u7f6e\u8eab\u5728\u4e00\u95f4\u5bbd\u655e \u534e\u4e3d\u7684\u5927\u5385\u91cc\u9762\uff0c\u9664\u4e86\u90a3\u4e24\u4e2a\u5e26\u8def\u7684\u5973\u5b50\u4e4b\u5916\uff0c\u8fd8\u6709\u516d\u4e2a\u4e00\u5f0f\u88c5\u675f\u7684\u5973\u5b50\uff0c\u4e5f\u90fd\u5df2\u5728\u90a3\u91cc\u7b49 \u5019\u6211\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u626c\u53e3\u9053\uff1a\u201c\u55ef\uff0c\u90a3\u4e48\u5171\u662f\u516b\u4e2a\u5440\uff01\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5148\u89c1\u5230\u7684\u5c31\u662f\u5389\u80dc\u7537\u90a3 \u516b\u4e2a\u4f8d\u5973\uff0c\u5979\u4eec\u7684\u9996\u9886\u5c1a\u672a\u51fa\u6765\u3002\u4f60\u522b\u5fc3\u6025\uff0c\u542c\u6211\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5750\u5b9a\u4e4b\u540e\uff0c\u6211\u4fbf\u8bf7\u95ee\u5979\u4eec\u8bf7\u6211\u524d\u6765\u7684\u7528\u610f\uff0c\u4e00\u4e2a\u5e74\u957f\u7684\u9ed1\u8863\u5973\u5b50\u8bf4 \u9053\uff1a\u2018\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u4eec\u662f\u60f3\u8bf7\u59ec\u5148\u751f\u6765\u505a\u6211\u4eec\u7684\u526f\u6559\u4e3b\u3002\u2019\u6211\u95ee\u9053\uff1a\u2018\u4f60\u4eec\u662f\u4ec0\u4e48\u6559\uff1f\u6b63\u6559 \u4e3b\u53c8\u662f\u4f55\u4eba\uff1f\u201d\u5979\u4eec\u7b54\u9053\uff1a\u2018\u6211\u4eec\u521b\u7684\u6559\u540d\u53eb\u5929\u9b54\u6559\uff1f\u2019\u6211\u6014\u4e86\u4e00\u6014\uff0c\u4f60\u77e5\u9053\u6211\u8c61\u4f60\u4e00\u6837\uff0c \u5e73\u65f6\u968f\u4fbf\u8bf4\u8bdd\u662f\u8bf4\u60ef\u4e86\u7684\uff0c\u5f53\u65f6\u4e0d\u5047\u601d\u7d22\uff0c\u4fbf\u968f\u53e3\u8bf4\u9053\uff1a\u2018\u600e\u4e48\u53d6\u8fd9\u6837\u90aa\u6076\u7684\u6559\u540d\uff1f\u2019 \u3000\u3000\u90a3\u5973\u5b50\u54c8\u54c8\u7b11\u9053\uff1a\u2018\u4ec0\u4e48\u53eb\u505a\u90aa\u6076\uff1a\u5584\u6076\u968f\u4eba\uff0c\u9b54\u7531\u5fc3\u8d77\u3002\u662f\u9b54\uff1f\u975e\u9b54\uff1f\u662f\u9b54\uff1f\u4f55\u5fc5\u7406 \u4eba\u95f2\u8bdd\u591a\uff1f\u4f55\u51b5\u636e\u6211\u4eec\u6240\u77e5\uff0c\u4f60\u4e5f\u5e76\u4e0d\u662f\u4ec0\u4e48\u6b63\u6d3e\u4e2d\u4eba\u3002\u201d \u3000\u3000\u6211\u53ea\u597d\u8bf4\u9053\uff1a\u2018\u4e0d\u9519\uff0c\u6211\u7684\u5e08\u7236\u672c\u6765\u5c31\u662f\u4e2a\u5927\u9b54\u5934\uff0c\u6211\u662f\u4ed6\u7684\u5f1f\u5b50\uff0c\u5f53\u7136\u5e94\u8be5\u7b97\u662f\u90aa\u6d3e \u7684\u4eba\u7269\u3002\uff0c \u3000\u3000\u90a3\u5973\u5b50\u65b9\u59cb\u6b23\u7136\u8272\u559c\uff0c\u9ed1\u8863\u5973\u5b50\u8bf4\u9053\uff1a\uff0c\u59ec\u5148\u751f\uff0c\u4f60\u8fd9\u6837\u5c31\u8bf4\u5bf9\u4e86\u3002\u5176\u5b9e\uff0c\u8bf4\u5c06\u8d77\u6765\uff0c \u54b1\u4eec\u672c\u6765\u5c31\u662f\u4e00\u5bb6\u3002\u5927\u5bb6\u7684\u529f\u592b\u90fd\u4ece\u4e54\u7956\u5e08\u90a3\u513f\u4e00\u8109\u76f8\u627f\u7684\u3002\u4e54\u7956\u5e08\u662f\u6b66\u6797\u4e2d\u6709\u53f2\u4ee5\u6765\u7684\u5927 \u9b54\u5934\uff0c\u53ef\u60dc\u4ed6\u56f0\u6b7b\u8352\u5c9b\uff0c\u542b\u6068\u800c\u7ec8\u3002\u6211\u4eec\u7684\u5c0f\u59d0\u672c\u6765\u8981\u7ee7\u627f\u4ed6\u7684\u9057\u5fd7\uff0c\u53ef\u60dc\u53c8\u56e0\u60c5\u5b7d\u7275\u8fde\uff0c \u88ab\u90a3\u6740\u5343\u5200\u7684\u91d1\u4e16\u9057\u5bb3\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5357\u63d2\u5634\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u5979\u4eec\u7adf\u6562\u8fb1\u9a82\u91d1\u5927\u4fa0\uff1a\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u542c\u4e86\u4e5f\u4e0d\u8212\u670d\u3002\u53ef\u662f\uff0c\u6211\u60f3\u5230\u5979\u4eec\u662f\u5389\u80dc\u7537\u7684\u4f8d\u5973\uff0c\u4e5f\u5c31\u4e0d\u613f\u8fc7 \u4e8e\u602a\u8d23\u5979\u4eec\u4e86\u3002\u5f53\u4e0b\u6211\u95ee\u5979\u4eec\u9053\uff1a\u2018\u539f\u6765\u4f60\u4eec\u8981\u7ee7\u627f\u4e54\u5317\u6ee8\u548c\u4f60\u4eec\u5389\u59d1\u5a18\u7684\u9057\u5fd7\uff0c\u53ef\u4e0d\u77e5\u8fd9 \u5fd7\u5411\u5982\u4f55\uff1f\u2019 \u3000\u3000\u201c\u90a3\u9ed1\u8863\u5973\u5b50\u9053\uff1a\u2018\u8fd9\u8fd8\u7528\u95ee\u5417\uff1f\u4f60\u5e94\u8be5\u77e5\u9053\u7684\uff0c\u6211\u4eec\u5389\u59d1\u5a18\u7684\u9057\u5fd7\u4fbf\u662f\u8981\u5c06\u6240\u6709\u81ea\u79f0 \u6b63\u6d3e\u7684\u4eba\u7269\u538b\u670d\uff0c\u552f\u6211\u72ec\u5c0a\uff01\u2019 \u3000\u3000\u201c\u539f\u6765\u5982\u6b64\uff01\u5979\u4eec\u5e76\u975e\u5fd7\u5728\u53d1\u626c\u6b66\u5b66\uff0c\u800c\u662f\u8981\u79f0\u9738\u6b66\u6797\uff0c\u4e3a\u4e86\u5389\u80dc\u7537\u4e4b\u6b7b\uff0c\u5979\u4eec\u5bf9\u6b63\u6d3e \u7684\u6b66\u6797\u4eba\u7269\uff0c\u7adf\u662f\u6709\u7740\u5f88\u6df1\u7684\u6028\u6bd2\uff01 \u3000\u3000\u201c\u6211\u548c\u5979\u4eec\u8d8a\u8bf4\u8d8a\u4e0d\u6295\u673a\uff0c\u4f46\u6211\u4e5f\u8fd8\u4e0d\u613f\u5f00\u7f6a\u5979\u4eec\uff0c\u6211\u4fbf\u501f\u8f9e\u8bf4\u9053\uff1a\u2018\u59ec\u67d0\u4e0d\u8fc7\u662f\u4e2a\u5c0f \u5077\u51fa\u8eab\uff0c\u5b9e\u5728\u5e76\u65e0\u96c4\u5fc3\u58ee\u5409\uff0c\u4f55\u51b5\u6709\u6211\u5e08\u7236\u7684\u524d\u8f66\u4e4b\u9274\uff0c\u6211\u4e5f\u4e0d\u6562\u5984\u81ea\u80e1\u4e3a\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u518d\u4e09\u529d\u6211\uff0c\u53c8\u7528\u8bf4\u8bdd\u6fc0\u6211\uff0c\u8bf4\u6211\u80c6\u5c0f\u3001\u8bf4\u6211\u80cc\u53db\u5e08\u95e8\u3002\u7b49\u7b49\uff0c\u7b49\u7b49\uff0c\u6211\u90fd\u4e0d\u4e3a\u6240 \u52a8\u3002\u5979\u4eec\u89c1\u6211\u6267\u610f\u4e0d\u4ece\uff0c\u90a3\u9ed1\u8863\u5973\u5b50\u7acb\u5373\u4fbf\u6362\u4e86\u4e00\u526f\u989c\u8272\uff01\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u5979\u4eec\u7ffb\u4e86\u8138\u8981\u548c\u4f60\u52a8\u624b\u4e86\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u5979\u4eec\u7684\u9634\u72e0\u624b\u6bb5\uff0c\u4f60\u8fd8\u672a\u66fe\u89c1\u8bc6\u8fc7\u5462\uff01\u90a3\u9ed1\u8863\u5973\u5b50\u975e\u4f46\u5e76\u4e0d\u52a8\u6012\uff0c\u53cd\u800c \u548c\u989c\u60a6\u8272\u7684\u5bf9\u6211\u8bf4\u9053\uff1a\u2018\u4eba\u5404\u6709\u5fd7\uff0c\u59ec\u5148\u751f\u65e2\u7136\u4e0d\u5141\uff0c\u6211\u4eec\u4e5f\u4e0d\u6562\u52c9\u5f3a\u4e86\u3002\u4f46\u59ec\u5148\u751f\u8fdc\u9053\u800c \u6765\uff0c\u5c1a\u672a\u8fdb\u8fc7\u4e00\u676f\u6c34\u9152\uff0c\u8bf7\u5bb9\u8bb8\u6211\u4eec\u7a0d\u5c3d\u5730\u4e3b\u4e4b\u8c0a\u3002 \u3000\u3000\u201c\u6211\u534a\u5929\u672a\u8fdb\u996e\u98df\uff0c\u786e\u4e5f\u6709\u70b9\u513f\u9965\u6e34\uff0c\u800c\u4e14\u7167\u6c5f\u6e56\u7684\u89c4\u77e9\uff0c\u7eb5\u7136\u6240\u8bae\u4e0d\u6210\uff0c\u4e5f\u8be5\u597d\u805a\u597d \u6563\u3002\u4e8e\u662f\u6211\u4fbf\u8bf4\u9053\uff1a\u2018\u591a\u8c22\u4e3b\u4eba\u6bb7\u52e4\u62db\u5f85\uff0c\u6211\u59ec\u67d0\u672c\u6765\u662f\u8d70\u5343\u5bb6\u98df\u767e\u6237\u7684\u5c0f\u5077\uff0c\u5c31\u53e8\u6270\u4f60\u4eec \u4e00\u987f\u5427\u3002\u2019 \u3000\u3000\u201c\u90a3\u9ed1\u8863\u5973\u5b50\u7b11\u9053\uff1a\u2018\u59ec\u5148\u751f\u771f\u4f1a\u8bf4\u7b11\u8bdd\u3002\u2019\u7247\u523b\u4e4b\u95f4\uff0c\u4fbf\u8bbe\u597d\u7b75\u5e2d\uff0c\u90a3\u9ed1\u8863\u5973\u5b50\uff0c\u793c \u4eea\u5468\u5168\uff0c\u5148\u5411\u6211\u656c\u9152\u3002 \u3000\u3000\u201c\u54c8\u54c8\uff0c\u5979\u4eec\u4e5f\u5fd2\u628a\u6211\u770b\u5f97\u5c0f\u4e86\uff0c\u6211\u59ec\u6653\u98ce\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u65e9\u5c31\u770b\u51fa\u5979\u4eec\u795e\u8272\u6709\u5f02\u3002\u5f53\u4e0b \u6211\u4fbf\u8bf4\u9053\uff1a\u2018\u597d\uff0c\u8bf7\u5927\u5bb6\u90fd\u5e72\u4e86\u6b64\u676f\uff01\u2019\u6211\u4f5c\u4e86\u4e00\u4e2a\u8bf7\u5e72\u676f\u7684\u624b\u52bf\uff0c\u7565\u65bd\u624b\u6cd5\uff0c\u6697\u4e2d\u5c31\u628a\u90a3 \u676f\u9152\u6362\u4e86\uff0c\u6362\u7ed9\u5750\u6211\u5de6\u624b\u8fb9\u7684\u5973\u5b50\uff0c\u53ef\u7b11\u5979\u4eec\u516b\u4eba\u5341\u516d\u4e2a\u773c\u775b\uff0c\u7adf\u6ca1\u4e00\u4e2a\u773c\u775b\u77a7\u89c1\uff01\u201d \u3000\u3000\u6c5f\u5357\u542c\u4e86\u4e0d\u89c9\u9a87\u7136\uff0c\u51ed\u7740\u90a3\u516b\u4e2a\u5973\u5b50\u7684\u672c\u9886\uff0c\u59ec\u6653\u98ce\u5f53\u4f17\u6362\u9152\uff0c\u7adf\u80fd\u7792\u8fc7\u5979\u4eec\uff0c\u624b\u6cd5\u7684 \u8fc5\u901f\u5229\u843d\uff0c\u771f\u662f\u96be\u4ee5\u60f3\u8c61\uff01\u59ec\u6653\u98ce\u7eed\u9053\uff1a\u201c\u6211\u559d\u4e86\u8fd9\u676f\u9152\uff0c\u6545\u610f\u6253\u4e2a\u963f\u6b20\uff0c\u8bf4\u9053\uff1a\u2018\u597d\u9152\uff0c \u4eba\u53e3\u4e0d\u70c8\uff0c\u5374\u600e\u7684\u9152\u529b\u8fd9\u7b49\u5389\u5bb3\uff01\u54ce\u5440\uff0c\u6211\u53ef\u662f\u6709\u9189\u4e86\uff01\u2019 \u3000\u3000\u201c\u90a3\u9ed1\u8863\u5973\u5b50\u5ffd\u7136\u54c8\u54c8\u7b11\u9053\uff1a\u2018\u59ec\u5148\u751f\uff0c\u4f60\u4e2d\u8ba1\u4e86\uff0c\u8fd9\u662f\u4e00\u676f\u6bd2\u9152\uff0c\u4f60\u82e5\u60f3\u8981\u89e3\u836f\uff0c\u53ef \u5f97\u4f9d\u4ece\u6211\u4eec\u4e24\u4ef6\u4e8b\u60c5\uff01\u2019 \u3000\u3000\u201c\u6211\u4f5c\u51fa\u5927\u4e3a\u60ca\u6050\u7684\u6837\u5b50\uff0c\u8bf4\u9053\uff1a\u2018\u4ec0\u4e48\uff1f\u8fd9\u662f\u6bd2\u9152\uff1f\u4f60\u4eec\u8981\u6211\u4f9d\u4ece\u4ec0\u4e48\uff1f\u2019 \u3000\u3000\u201c\u90a3\u9ed1\u8863\u5973\u5b50\u9053\uff1a\u2018\u6211\u77e5\u9053\u4f60\u5077\u4e86\u5404\u5927\u95e8\u6d3e\u7684\u8bb8\u591a\u6b66\u529f\u79d8\u7c4d\uff0c\u7b2c\u4e00\u4ef6\uff0c\u4f60\u8981\u628a\u8fd9\u4e9b\u6b66\u529f \u79d8\u5ea6\u90fd\u4ea4\u51fa\u6765\uff1b\u7b2c\u4e8c\u4ef6\uff0c\u628a\u4f60\u6240\u77e5\u9053\u7684\u4e54\u7956\u5e08\u79d8\u7c4d\u4e0a\u7684\u6b66\u529f\u90fd\u5199\u51fa\u6765\uff0c\u4e0d\u8bb8\u6709\u534a\u70b9\u9690\u7792\uff01 \u54fc\uff0c\u54fc\uff0c\u4f60\u60f3\u9690\u7792\uff0c\u6211\u4eec\u4e5f\u6709\u529e\u6cd5\u8bd5\u5f97\u51fa\u6765\u3002\u597d\uff0c\u8fd9\u91cc\u7ed9\u4f60\u4e00\u9897\u836f\u4e38\uff0c\u53ef\u4ee4\u6bd2\u6027\u5ef6\u7f13\u4e09\u5929\uff0c \u8ba9\u4f60\u53bb\u53d6\u6b66\u529f\u79d8\u590d\u3002\u4e09\u5929\u4e0d\u6765\uff0c\u6bd2\u6027\u4e00\u53d1\uff0c\u5c31\u8981\u80a0\u7a7f\u809a\u70c2\u800c\u4ea1\uff01\u2019 \u3000\u3000\u201c\u5979\u7684\u8bdd\u521a\u521a\u8bf4\u5b8c\uff0c\u53ea\u542c\u5f97\u4e00\u58f0\u5c16\u53eb\uff0c\u5728\u6211\u5de6\u624b\u8fb9\u7684\u90a3\u4e2a\u5973\u5b50\u5df2\u662f\u5fcd\u4e0d\u7740\u6367\u8179\u547c\u75db\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u5927\u7b11\u9053\uff1a\u201c\u5c31\u5728\u5979\u4eec\u60ca\u6115\u4e4b\u4e2d\uff0c\u6211\u54c8\u54c8\u7b11\u9053\uff1a\u2018\u4f60\u4eec\u4e2d\u4e86\u6211\u7684\u8ba1\u4e86\uff01\u8fd9\u676f\u9152\u6211\u65e9 \u5df2\u6362\u4e86\uff01\u2019 \u3000\u3000\u201c\u6211\u6b64\u8a00\u4e00\u51fa\uff0c\u5979\u4eec\u53c8\u60ca\u53c8\u6012\uff0c\u7acb\u5373\u5411\u6211\u56f4\u653b\uff0c\u6211\u6709\u610f\u770b\u770b\u4eec\u7684\u672c\u9886\u5982\u4f55\uff0c\u540c\u65f6\uff0c\u6211\u4e5f \u662f\u4e0d\u60f3\u8ba9\u5979\u4eec\u4ece\u6211\u8fd9\u91cc\u5077\u5e08\uff0c\u4e8e\u662f\u6211\u4efb\u51ed\u5979\u4eec\u653b\u51fb\uff0c\u4e0d\u8fd8\u4e00\u62db\uff0c\u53ea\u662f\u7528\u5929\u7f57\u6b65\u6cd5\uff0c\u518d\u65bd\u5c55\u4e00 \u70b9\u8f7b\u5de7\u7684\u8f7b\u529f\uff0c\u5728\u5979\u4eec\u4e4b\u4e2d\u7a7f\u6765\u63d2\u53bb\uff0c\u6211\u8fde\u8863\u89d2\u90fd\u4e0d\u8ba9\u5979\u4eec\u6cbe\u7740\u3002 \u3000\u3000\u201c\u95f9\u4e86\u4e00\u4f1a\uff0c\u6211\u4e5f\u95f9\u5f97\u591f\u4e86\uff0c\u4e8e\u662f\u6211\u4fbf\u7b11\u8bf4\uff1a\u2018\u597d\u4e86\uff0c\u591a\u8c22\u4f60\u4eec\u7684\u597d\u610f\uff0c\u8981\u8bf7\u6211\u505a\u526f\u6559 \u4e3b\uff0c\u53ef\u60dc\u6211\u8fd9\u4e2a\u7a77\u9aa8\u5934\u4e0d\u53d7\u62ac\u4e3e\uff0c\u6211\u8981\u5931\u966a\u5566 \u3000\u3000\u201c\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u6781\u5176\u51b7\u5ced\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u2018\u59d3\u59ec\u7684\uff0c\u4f60\u592a\u5c0f\u89d1\u6211\u4eec\u5929\u9b54\u6559\uff0c\u6211\u770b \u4f60\u662f\u6765\u5f97\u53bb\u4e0d\u5f97\u4e86\uff01\u2019\u58f0\u5230\u5165\u5230\uff0c\u970e\u5ffd\u95f4\uff0c\u5728\u6211\u7684\u9762\u524d\uff0c\u5c31\u591a\u4e86\u4e00\u4e2a\u5973\u5b50\uff0c\u51ed\u7740\u6211\u7684\u773c\u529b\uff0c \u4e5f\u53ea\u662f\u89c1\u767d\u5f71\u4e00\u95ea\uff0c\u5979\u5c31\u7a81\u5982\u5176\u6765\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u8fd9\u4e2a\u5973\u5b50\uff0c\u8be5\u662f\u5979\u4eec\u7684\u6b63\u6559\u4e3b\u4e86\u5427\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u76f4\u5230\u8fd9\u4e2a\u65f6\u5019\uff0c\u6b63\u89d2\u513f\u65b9\u59cb\u767b\u573a\uff0c\u5979\u4e00\u51fa\u573a\u5c31\u5413\u4e86\u6211\u4e00\u8df3\uff01\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u6bd2\u9152\u751c\u8a00\u6c42\u79d8\u7c4d\uff0c\u5929\u9b54\u6559\u4e3b\u51fa\u573a\u6765\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000--------------- \u3000\u3000\u626c\u5251\u8f69\u5c45\u58eb \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e94\u56de\u3000\u5c45\u7136\u610f\u5916\u6467\u5f3a\u654c\u3000\u53c8\u89c1\u4eba\u95f4\u73b0\u4fa0\u8e2a \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e94\u56de\u3000\u5c45\u7136\u610f\u5916\u6467\u5f3a\u654c\u3000\u53c8\u89c1\u4eba\u95f4\u73b0\u4fa0\u8e2a \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u5c1a\u8fd8\u672a\u66fe\u4ea4\u624b\uff0c\u4f60\u5c31\u6015\u4e86\u5979\u4e48\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u884c\u5bb6\u4e00\u51fa\u624b\uff0c\u5c31\u77e5\u6709\u6ca1\u6709\u3002\u53ea\u51ed\u5979\u90a3\u51fa\u573a\u7684\u8eab\u624b\uff0c\u5c31\u8db3\u4ee5\u60ca\u4e16\u9a87\u4fd7\u3002\u4e0d \u8fc7\uff0c\u6211\u5012\u4e0d\u662f\u60e7\u6015\u5979\u90a3\u6765\u53bb\u65e0\u8e2a\u7684\u672c\u4e8b\uff0c\u800c\u662f\uff0c\u800c\u662f\u2014\u2014\u201d \u3000\u3000\u6c5f\u5357\u63d2\u53e3\u9053\uff1a\u201c\u8bba\u5230\u6765\u53bb\u65e0\u8e2a\u7684\u672c\u9886\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u8fd8\u6709\u8c01\u6bd4\u5f97\u4e0a\u5927\u54e5\uff1f\u662f\u4e86\uff0c\u6211\u660e\u767d \u4e86\uff0c\u4e00\u5b9a\u662f\u5979\u7684\u76f8\u8c8c\u751f\u5f97\u5947\u4e11\u65e0\u6bd4\uff0c\u56e0\u6b64\u5413\u574f\u4f60\u4e86\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4e0d\uff0c\u5979\u7684\u9762\u8272\u867d\u7136\u82cd\u767d\u4e4b\u6781\uff0c\u8bf4\u4e0d\u4e0a\u662f\u7f8e\uff0c\u4f46\u4e0d\u80fd\u8bf4\u662f\u4e11\u964b\u3002\u53ea\u662f\u5979\u9762\u4e0a \u51b7\u51b0\u51b0\u7684\u6beb\u65e0\u751f\u6c14\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u5c31\u6709\u4e2a\u611f\u89c9\uff0c\u597d\u50cf\u4e0d\u662f\u751f\u4eba\uff0c\u800c\u662f\u4ece\u575f\u5893\u4e2d\u8d70\u51fa\u7684\u5973\u9b3c\uff01\u800c \u5979\u5c45\u7136\u6709\u4e03\u516b\u5206\u4f3c\u5389\u80dc\u7537\uff01\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u602a\u4e0d\u5f97\u4f60\u5fc3\u91cc\u53d1\u6bdb\u3002\u82e5\u7136\u771f\u7684\u662f\u5389\u80dc\u7537\u7684\u8bdd\uff0c\u6599\u60f3\u5929\u5c71\u5510\u5927\u4fa0\u89c1 \u4e86\uff0c\u4e5f\u4f1a\u5927\u5403\u4e00\u60ca\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u7eed\u9053\uff1a\u201c\u5e72\u6211\u4eec\u8fd9\u884c\u7684\u4eba\uff0c\u51b3\u4e0d\u4fe1\u9b3c\u3002\u6211\u5b9a\u4e86\u5b9a\u795e\u3002\u8bc6\u7a7f\u5979\u7684\u4f0e\u4fe9\uff0c\u5979\u662f\u6545\u610f\u627e \u4e00\u4e2a\u4e0e\u5389\u80dc\u7537\u76f8\u4f3c\u7684\u4eba\u76ae\u9762\u5177\u6234\u4e86\u6765\u5413\u6211\u7684\u3002\u5f53\u4e0b\u6211\u4e00\u58f0\u51b7\u7b11\uff1a\u2018\u4f60\u4e0d\u8ba9\u6211\u8d70\uff0c\u6211\u5c31\u6293\u7834\u4f60 \u7684\u8138\u76ae\uff01\u2019\u6211\u4e0d\u5f85\u5979\u8d70\u6765\uff0c\u5148\u5df2\u7528\u5929\u7f57\u6b65\u6cd5\u8fce\u4e86\u4e0a\u53bb\uff0c\u8fc5\u5373\u53bb\u6293\u5979\u7684\u4eba\u76ae\u9762\u5177\uff01\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u5979\u7684\u5e90\u5c71\u771f\u8c8c\u5982\u4f55\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u5929\u9b54\u6559\u4e3b\u662f\u8fd9\u6837\u5bb9\u6613\u5bf9\u4ed8\u4e48\uff0c\u55ef\uff0c\u6211\u672c\u5df2\u770b\u51fa\u5979\u8eab\u624b\u4e0d\u51e1\uff0c\u5374\u8fd8\u662f \u4f30\u8ba1\u4e0d\u8db3\uff0c\u6211\u90a3\u4e00\u6293\u975e\u4f46\u6ca1\u6709\u6293\u4e0b\u5979\u7684\u9762\u5177\uff0c\u53cd\u800c\u9669\u4e9b\u5403\u4e86\u5927\u4e8f\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u5979\u7528\u4ec0\u4e48\u529f\u592b\u7834\u4f60\u7684\u9634\u9633\u6293\uff1f\u96be\u9053\u5979\u7684\u8f7b\u529f\u6bd4\u4f60\u66f4\u9ad8\uff1f\u201d\u8fd9\u9634\u9633\u6293\u7684\u529f\u592b\u4e43 \u662f\u4e54\u5317\u6e9f\u79d8\u9707\u4e2d\u7684\u7edd\u5b66\uff0c\u4e00\u7ecf\u4f7f\u5f00\uff0c\u4fbf\u5982\u5f71\u968f\u5f62\uff0c\u9664\u975e\u662f\u5bf9\u65b9\u5df2\u7ec3\u6210\u4e86\u91d1\u521a\u4e0d\u574f\u7684\u62a4\u4f53\u795e \u529f\uff0c\u6216\u8005\u662f\u8f7b\u529f\u8fdc\u80dc\u81ea\u5df1\uff0c\u5426\u5219\u51b3\u96be\u5316\u89e3\u3002\u6545\u6b64\u6c5f\u5357\u6709\u6b64\u4e00\u95ee\u3002 \u3000\u3000\u59ec\u6653\u98ce\u559d\u4e86\u4e00\u53e3\u9152\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u8fd9\u5929\u9b54\u6559\u4e3b\u597d\u4e0d\u5389\u5bb3\uff0c\u5979\u7adf\u7136\u5c06\u8ba1\u5c31\u8ba1\uff0c\u8ba9\u6211\u7684\u624b\u6307 \u6cbe\u4e0a\u4e86\u5979\u7684\u9762\u5177\uff0c\u82e5\u662f\u8840\u8089\u4e4b\u8eaf\uff0c\u6211\u8fd9\u4e00\u6307\u4f2f\u4e0d\u628a\u5979\u7684\u773c\u775b\u6316\u4e86\u3002\u53ef\u662f\u8fd9\u662f\u4e00\u5f20\u4eba\u76ae\u9762\u5177\uff0c \u9762\u5177\u6562\u60c5\u662f\u7528\u6bd2\u836f\u70bc\u8fc7\u7684\uff0c\u6211\u7684\u624b\u6307\u4e00\u6cbe\uff0c\u7a81\u7136\u597d\u4f3c\u7ed9\u706b\u70ad\u89e6\u7740\u4e00\u822c\uff0c\u624b\u6307\u767b\u65f6\u8d77\u6ce1\uff0c\u706b\u8fa3 \u8fa3\u4f5c\u75db\u3002\u6211\u5fd9\u4e0d\u8fed\u7684\u7f29\u624b\uff0c\u5c31\u5728\u90a3\u5239\u90a3\u95f4\uff0c\u5979\u5df2\u4f7f\u51fa\u4e54\u7956\u5e08\u79d8\u7c4d\u4e2d\u7684\u70b9\u7a74\u529f\u592b\uff0c\u4e00\u62db\u4e4b\u5185\uff0c \u8fde\u70b9\u6211\u5341\u4e09\u9053\u5927\u7a74\u3002 \u3000\u3000\u201c\u6211\u4e00\u4e2a\u7b4b\u6597\u5012\u7ffb\u51fa\u53bb\uff0c\u5c3e\u960e\u7684\u2018\u98ce\u773c\u7a74\u2019\u8fd8\u662f\u7ed9\u5979\u6233\u4e86\u4e00\u4e0b\u3002\u5e78\u800c\u6211\u5df2\u95ed\u4e86\u7a74\u9053\uff0c\u5e76 \u65e0\u635f\u4f24\u3002\u6211\u8f6c\u8fc7\u8eab\u6765\uff0c\u548c\u5979\u518d\u6597\uff0c\u7aed\u5c3d\u5e73\u751f\u6240\u5b66\uff0c\u548c\u5979\u6597\u4e86\u4e09\u767e\u591a\u62db\uff0c\u582a\u582a\u6253\u6210\u5e73\u624b\u3002\u201d \u3000\u3000\u6c5f\u5357\u95ee\u9053\uff1a\u201c\u4f60\u624b\u6307\u7684\u4f24\u52bf\u5982\u4f55\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6211\u5df2\u7528\u5185\u529f\u5c06\u6bd2\u8840\u8feb\u805a\u6307\u5c16\uff0c\u6682\u65f6\u4e0d\u4f1a\u53d1\u4f5c\u3002\u53ef\u662f\uff0c\u4e5f\u6b63\u56e0\u4e3a\u6211\u624b\u6307\u4e2d \u6bd2\uff0c\u597d\u4e9b\u5389\u5bb3\u7684\u529f\u592b\u65bd\u5c55\u4e0d\u51fa\u6765\uff0c\u81ea\u77e5\u4e45\u6218\u4e0b\u53bb\uff0c\u5fc5\u5b9a\u5403\u4e8f\uff0c\u56e0\u6b64\u8fc7\u4e86\u4e09\u767e\u62db\u4e4b\u540e\uff0c\u6211\u4fbf \u8bf4\uff1a\u2018\u6559\u4e3b\u6b66\u529f\u679c\u7136\u9ad8\u5f3a\uff0c\u4f60\u6211\u8981\u5206\u51fa\u80dc\u8d1f\uff0c\u53ea\u6015\u5f97\u5343\u62db\u5f00\u5916\u3002\u59ec\u67d0\u53ef\u6ca1\u6709\u8fd9\u8bb8\u591a\u95f2\u529f\u592b\uff0c \u8bf7\u6055\u6211\u5931\u966a\u4e86\uff01\u2019\u201d \u3000\u3000\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u597d\u5728\u4f60\u7684\u8f7b\u529f\u9ad8\u660e\uff0c\u6253\u4e0d\u8fc7\u4fbf\u8dd1\uff0c\u672c\u9886\u6bd4\u4f60\u518d\u9ad8\u7684\u4e5f\u5948\u4f60\u4e0d\u4f55\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u54ea\u6709\u8fd9\u6837\u5bb9\u6613\uff1f\u5389\u80dc\u7537\u90a3\u516b\u4e2a\u4f8d\u5973\u5206\u5e03\u516b\u65b9\uff0c\u6211\u9003\u5411\u54ea\u4e2a\u65b9\u5411\uff0c\u90fd\u6709\u4eba\u5835 \u622a\uff0c\u82e5\u662f\u7f8a\u6253\u72ec\u6597\uff0c\u90a3\u516b\u4e2a\u4f8d\u5973\u8c01\u90e8\u6321\u4e0d\u4e86\u6211\u957f\u62db\uff0c\u53ef\u662f\u5728\u5f53\u65f6\u7684\u60c5\u5f62\u4e4b\u4e0b\uff0c\u90a3\u5929\u9b54\u6559\u4e3b\u7684 \u8f7b\u529f\u4e0d\u8fc7\u6bd4\u6211\u7565\u900a\u4e00\u7b79\uff0c\u522b\u8bf4\u5341\u62db\uff0c\u53ea\u8981\u5979\u7684\u624b\u4e0b\u80fd\u6321\u4f4f\u6211\u4e24\u62db\uff0c\u5979\u6d6a\u4e0a\u4e86\u3002\u53c8\u518d\u7f20\u6597\u4e86\u534a \u4e2a\u65f6\u8fb0\uff0c\u6211\u7adf\u7136\u672a\u80fd\u8131\u8eab\uff01\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u59ec\u5927\u54e5\uff0c\u90a3\u4f60\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u5e78\u800c\u6211\u4eba\u6025\u667a\u751f\uff0c\u6597\u5230\u7d27\u5f20\u4e4b\u9645\uff0c\u6211\u7a81\u7136\u4f7f\u71d5\u5b50\u94bb\u4e91\u7684\u7edd\u9876\u8f7b\u529f\uff0c\u4ece\u6a2a \u6881\u98de\u8fc7\uff0c\u6a2a\u6881\u90a3\u8fb9\u7684\u4e00\u4e2a\u4f8d\u5973\uff0c\u5927\u56e0\u4e3a\u6211\u6765\u5f97\u5927\u5feb\uff0c\u4e0d\u53ca\u63d0\u9632\uff0c\u88ab\u6211\u4e00\u4e0b\u5b50\u5c31\u6293\u4e86\u8d77\u6765\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u7eed\u9053\uff1a\u201c\u6211\u64d2\u83b7\u4e86\u8fd9\u4e2a\u4f8d\u5973\uff0c\u5979\u7684\u540c\u4f34\u6295\u9f20\u5fcc\u5668\uff0c\u4e0d\u6562\u4e0a\u524d\u653b\u51fb\u3002\u5929\u9b54\u6559\u4e3b\u8865\u4e0a \u90a3\u4e2a\u7a7a\u4f4d\uff0c\u5bf9\u6211\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u59d3\u59ec\u7684\uff0c\u6562\u52a8\u5979\u4e00\u6839\u6beb\u6bdb\uff0c\u6211\u5c31\u8981\u4f60\u7684\u547d\uff01\u201d\u6211\u7b11\u9053\uff1a\u2018\u6211\u672c \u6765\u5c31\u4e0d\u60f3\u5bb3\u5979\uff0c\u5374\u662f\u4f60\u60f3\u8981\u6211\u7684\u547d\u3002\u597d\u5427\uff0c\u73b0\u5728\u54b1\u4eec\u53ef\u4ee5\u8c08\u8c08\u4e86\u5427\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u2018\u4f60\u6709 \u8bdd\u4fbf\u8bf4\uff01\u2019\u6211\u8bf4\u9053\uff1a\u2018\u4f60\u505a\u4f60\u7684\u6559\u4e3b\uff0c\u6211\u505a\u6211\u5077\u513f\uff0c\u6211\u65e2\u4e0d\u60f3\u52a0\u5165\u4f60\u4eec\u7684\u5929\u9b54\u6559\uff0c\u4e5f\u4e0d\u60f3\u6765 \u5077\u4f60\u4eec\u7684\u4e1c\u897f\uff0c\u6211\u4eec\u672c\u6765\u662f\u98ce\u9a6c\u725b\u4e0d\u76f8\u53ca\uff0c\u4f60\u53c8\u4f55\u5fc5\u82e6\u82e6\u76f8\u8feb\uff1f\u4e0d\u9519\uff0c\u4f60\u82e5\u4e0d\u653e\u6211\u8d70\uff0c\u6211\u4eca \u65e5\u662f\u96be\u4ee5\u8131\u8eab\uff0c\u4f46\u6211\u82e5\u5f53\u771f\u62fc\u8d77\u547d\u6765\uff0c\u53ea\u6015\u4f60\u4eec\u96be\u514d\u4e24\u8d25\u4ff1\u4f24\u3002\u201d \u3000\u3000\u201c\u5018\u975e\u8fd9\u7fa4\u4f8d\u5973\u76f8\u52a9\uff0c\u90a3\u5927\u9b54\u6559\u4e3b\u4e5f\u5b9e\u5728\u5e76\u65e0\u80dc\u6211\u7684\u628a\u63e1\u3002\u5979\u6234\u7740\u9762\u5177\uff0c\u6211\u7aa5\u5bdf\u4e0d\u5230\u5979 \u7684\u8138\u8272\uff0c\u4f46\u89c1\u5979\u7684\u773c\u775b\u95ea\u4e86\u51e0\u4e0b\uff0c\u7ec8\u4e8e\u8bf4\u9053\uff1a\u2018\u597d\uff0c\u53ea\u8981\u4f60\u4e0d\u4e0e\u6211\u4f5c\u5bf9\uff0c\u6211\u53ef\u4ee5\u653e\u4f60\u51fa\u53bb\u3002 \u4f46\u4f60\u8fd8\u5f97\u7531\u4e00\u4ef6\u4e8b\u60c5\u3002\u2019\u6211\u95ee\u5979\u4f55\u4e8b\uff1f\u5979\u8bf4\uff1a\u2018\u7f1a\u4e0a\u4f60\u7684\u773c\u775b\uff0c\u5c31\u7531\u5979\u4eec\u5e26\u4f60\u51fa\u53bb\u3002\u2019\u6211\u660e \u767d\u5979\u7684\u610f\u601d\uff0c\u8bf4\u9053\uff1a\u2018\u4f60\u653e\u5fc3\uff0c\u6211\u51b3\u4e0d\u4f1a\u6cc4\u6f0f\u4f60\u4eec\u7684\u884c\u85cf\uff0c\u4e5f\u4e0d\u613f\u7406\u4f1a\u4f60\u4eec\u7684\u4e8b\u60c5\u3002\u2019\u90a3\u5929 \u9b54\u6559\u4e3b\u51b7\u51b7\u8bf4\u9053\uff1a\u2018\u8fd9\u662f\u89c4\u77e9\uff0c\u96be\u9053\u4f60\u5bb3\u6015\u6211\u6697\u7b97\u4f60\u5417\uff1f\u54fc\uff0c\u54fc\uff0c\u4f60\u4e5f\u5fd2\u5c0f\u89d1\u6211\u5929\u9b54\u6559\u4e3b \u4e86\uff01\u2019 \u3000\u3000\u201c\u6211\u5fc3\u60f3\uff1a\u2018\u6211\u5c31\u662f\u7f1a\u4e86\u773c\u775b\uff0c\u4f60\u4e5f\u672a\u5fc5\u4fbf\u4f24\u5bb3\u5f97\u4e86\u6211\uff0c\u4f55\u51b5\u8fd8\u6709\u4fd8\u864f\u5728\u6211\u624b\u4e2d\u3002\u2019\u6211 \u6495\u4e0b\u4e86\u4e00\u5e45\u886b\u8896\uff0c\u81ea\u5df1\u7f1a\u4e86\u773c\u775b\uff0c\u5e26\u7740\u90a3\u4e2a\u4f8d\u5973\uff0c\u8ba9\u5979\u7ed9\u6211\u5f15\u8def\u3002 \u3000\u3000\u201c\u6211\u542c\u5f97\u80cc\u540e\u6709\u8f7b\u5fae\u7684\u817f\u6b65\u58f0\uff0c\u77e5\u9053\u662f\u90a3\u5929\u9b54\u6559\u4e3b\u8ddf\u5728\u540e\u9762\uff0c\u6211\u4e5f\u4e0d\u7406\u4f1a\u5979\u3002\u5979\u679c\u7136\u6ca1 \u6709\u5077\u65bd\u6697\u7b97\uff0c\u5230\u4e86\u8c37\u53e3\uff0c\u90a3\u5929\u9b54\u6559\u4e3b\u626c\u58f0\u8bf4\u9053\uff1a\u2018\u4f60\u53ef\u4ee5\u89e3\u5f00\u773c\u775b\u4e86\u3002\u2019\u6211\u653e\u8d70\u90a3\u4e2a\u4f8d\u5973\uff0c \u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u5929\u9b54\u6559\u4e3b\u5728\u5c71\u5761\u4e0a\u7a81\u7136\u628a\u624b\u4e00\u626c\uff0c\u8bf4\u9053\uff1a\u2018\u4eca\u540e\u4e0d\u8bb8\u4f60\u518d\u8e0f\u8fdb\u8c37\u4e2d\u534a\u6b65\uff01 \u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u521a\u624d\u6211\u4e0d\u53d6\u4f60\u6027\u547d\uff0c\u5b9e\u662f\u5ff5\u5728\u4f60\u6211\u540c\u51fa\u4e00\u6e90\uff0c\u4f60\u522b\u4ee5\u4e3a\u6211\u6ca1\u6709\u672c\u9886\u6740\u4f60\uff01\u201d \u3000\u3000\u201c\u5979\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u2018\u6ce2\uff0c\u7684\u4e00\u58f0\uff0c\u4e00\u56e2\u6d53\u70df\u70c8\u7130\uff0c\u5df2\u5728\u6211\u7684\u8eab\u8fb9\u6563\u5f00\uff1b\u5979\u7684\u5f71\u5b50\u4e5f \u5728\u6d53\u70df\u4e2d\u6d88\u5931\uff01 \u3000\u3000\u201c\u5f85\u70df\u6d88\u706b\u706d\u4e4b\u540e\uff0c\u6211\u5b9a\u775b\u4e00\u770b\uff0c\u4f46\u89c1\u65b9\u5706\u6570\u4e08\u4e4b\u5185\uff0c\u88ab\u70df\u55b7\u8fc7\u7684\u5730\u65b9\uff0c\u82b1\u8349\u5c3d\u90fd\u67af \u840e\uff0c\u70e7\u6389\u7684\u8fd8\u4e0d\u7b97\uff01\u201d \u3000\u3000\u6c5f\u5357\u53eb\u8d77\u6765\u9053\uff1a\u201c\u8fd9\u662f\u5389\u80dc\u7537\u751f\u524d\u60ef\u7528\u7684\u6697\u5668\uff0c\u540d\u53eb\u6bd2\u96fe\u91d1\u9488\u70c8\u7130\u5f39\uff01\u60f3\u4e0d\u5230\u5929\u9b54\u6559\u4e3b \u4e5f\u5f97\u4e86\u5979\u8fd9\u624b\u6b79\u6bd2\u7684\u6697\u5668\u529f\u592b\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u559d\u4e86\u6ee1\u6ee1\u4e00\u676f\uff0c\u7b11\u9053\uff1a\u201c\u6211\u5012\u4e0d\u9886\u5929\u9b54\u6559\u4e3b\u8fd9\u4e2a\u60c5\u3002\u6211\u624d\u4e0d\u76f8\u4fe1\u5979\u771f\u7684\u662f\u5ff5\u5728\u540c \u6e90\u5462\uff01\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u5979\u6700\u521d\u548c\u4f60\u52a8\u624b\u4e4b\u65f6\uff0c\u5927\u7ea6\u8fd8\u5e0c\u671b\u80fd\u628a\u4f60\u751f\u64d2\uff0c\u8feb\u4f60\u732e\u51fa\u6b66\u529f\u79d8\u7c4d\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u4e2a\u539f\u56e0\uff0c\u53e6\u4e00\u4e2a\u539f\u56e0\u4e5f\u4e0d\u96be\u731c\u5230\uff0c\u90a3\u662f\u56e0\u4e3a\u8fd9\u79cd\u6697\u5668\u6740\u4f24\u4e4b\u9762\u592a \u5e7f\uff0c\u5728\u7fa4\u6bb4\u4e4b\u65f6\uff0c\u5979\u4f7f\u51fa\u8fd9\u79cd\u6b79\u6bd2\u7684\u6697\u5668\uff0c\u53ea\u6015\u5979\u7684\u624b\u4e0b\u5148\u8981\u906d\u6b83\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u5728\u5979\u7b54\u5e94\u4e86\u653e\u4f60\u4e4b\u540e\uff0c\u4e0d\u7528\u8fd9\u79cd\u6697\u5668\u5077\u88ad\u4f60\uff0c\u603b\u7b97\u662f\u5979\u4fe1 \u5b88\u8bfa\u8a00\u3002\u5c24\u5176\u5728\u4f60\u5230\u4e86\u8c37\u53e3\uff0c\u5df2\u91ca\u653e\u5979\u7684\u4f8d\u5973\u4e4b\u540e\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u8bba\u4eba\u8bba\u4e8b\uff0c\u603b\u662f\u5148\u4ece\u5bf9\u65b9\u7684\u597d\u5904\u7740\u60f3\uff0c\u5fc3\u5730\u5fe0\u539a\uff0c\u786e\u662f\u4eba\u6240\u96be \u80fd\u3002\u602a\u4e0d\u5f97\u91d1\u5927\u4fa0\u8d4f\u8bc6\u4f60\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u8bb2\u5b8c\u4e86\u4f1a\u89c1\u5929\u9b54\u5973\u7684\u6545\u4e8b\u4e4b\u540e\uff0c\u6c5f\u5357\u60f3\u4e86\u4e00\u60f3\uff0c\u62c5\u5fe7\u95ee\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5927\u54e5\uff0c \u4f60\u600e\u597d\u8fd8\u518d\u8e0f\u8fdb\u7ec4\u6765\u5c71\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4e0d\u8bb8\u6211\u8e0f\u7ec4\u6765\u5c71\u4e00\u6b65\uff0c\u90a3\u662f\u5979\u7684\u81ea\u8bf4\u81ea\u8bdd\uff0c\u6211\u53ef\u5e76\u6ca1\u6709\u7b54\u5e94\u5979\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u8fd9\u5929\u9b54\u6559\u4e3b\u6b66\u529f\u9ad8\u5f3a\uff0c\u53c8\u4f1a\u4f7f\u6bd2\uff0c\u6211\u600e\u653e\u5fc3\u8ba9\u4f60\u518d\u53bb\u4e3a\u6211\u5192\u9669\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u6211\u518d\u53bb\u7684\u65f6\u5019\uff0c\u5f53\u7136\u4f1a\u52a0\u4e0a\u51e0\u5206\u5c0f\u5fc3\uff0c\u5979\u6b66\u529f\u7eb5\u7136\u80dc\u6211\uff0c\u4e5f\u672a\u5fc5\u4fbf\u80fd\u5c06 \u6211\u64d2\u4e86\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u597d\u5728\u6211\u8fd9\u884c\u7684\u90fd\u662f\u5728\u9ed1\u6697\u4e4b\u4e2d\u6765\u53bb\u81ea\u5982\u7684\uff0c\u5979\u5f53\u65f6\u4ee5\u4e3a\u7f1a \u4e86\u6211\u7684\u773c\u775b\uff0c\u6211\u5c31\u8ba4\u4e0d\u5f97\u8def\u4e86\uff0c\u54c8\u54c8\uff0c\u54ea\u77e5\u9053\u6211\u5df2\u8bb0\u5728\u5fc3\u4e2d\u3002\u201d \u3000\u3000\u6c5f\u5357\u4ecd\u662f\u4e0d\u653e\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7792\u5927\u54e5\uff0c\u6211\u8fd9\u6b21\u5b9e\u662f\u60f3\u4e0a\u6c13\u5c71\u3002\u6c42\u8c37\u5973\u4fa0\u6c42\u52a9\u7684\uff0c\u82e5\u7136\u4ece \u8c37\u5973\u4fa0\u90a3\u513f\u5f97\u77e5\u91d1\u5927\u4fa0\u7684\u6d88\u606f\uff0c\u90a3\u5c31\u597d\u4e86\u3002\u73b0\u5728\u5df2\u7ecf\u77e5\u9053\u4e86\u90a3\u7fa4\u5929\u9b54\u5973\u7684\u6240\u5728\uff0c\u4e0d\u5fc5\u6025\u5728\u4e00 \u65f6\u3002\u6b64\u5904\u79bb\u6c13\u5c71\u4e0d\u8fc7\u662f\u4e24\u5929\u8def\u7a0b\uff0c\u5927\u54e5\uff0c\u4f60\u5c31\u4e0e\u6211\u540c\u4e0a\u6c13\u5c71\uff0c\u518d\u4f5c\u6253\u7b97\u5982\u4f55\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6211\u4e0d\u65b9\u4fbf\u524d\u5f80\u6c13\u5c71\u3002\u201d \u3000\u3000\u6c5f\u5357\u8be7\u9053\uff1a\u201c\u8fd9\u5374\u4e3a\u4f55\uff1f\u201d\u5ffd\u5730\u60f3\u8d77\u4e00\u4e8b\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u4f60\u5077\u904d\u5404\u5927\u95e8\u6d3e\u7684\u6b66\u529f\u79d8 \u5178\uff0c\u5c31\u662f\u6ca1\u6709\u5077\u6c13\u5c71\u6d3e\u7684\uff0c\u662f\u56e0\u4e3a\u91d1\u5927\u4fa0\u7684\u7f18\u6545\u5417\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u662f\u3002\u54c8\u54c8\uff0c\u5c0f\u5144\u5f1f\uff0c\u4f60\u5012\u5fd8\u4e86\u8c37\u5973\u4fa0\u662f\u6211\u7684\u4ec0\u4e48\u4eba\u4e86\u4e48\uff1f\u201d\u6c5f\u5357 \u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u5440\uff0c\u4e0d\u9519\u3002\u4f60\u662f\u5b5f\u795e\u901a\u7684\u5f1f\u5b50\uff0c\u5979\u672c\u6765\u662f\u5b5f\u795e\u901a\u7684\u5973\u513f\u3002\u8bf4\u6765\uff0c\u5979\u8fd8\u662f \u4f60\u7684\u5e08\u59b9\u3002\u4e0d\u8fc7\uff0c\u5c31\u5728\u4f60\u7684\u5e08\u7236\u751f\u524d\uff0c\u5979\u65e9\u5df2\u4e0d\u8ba4\u8fd9\u4e2a\u7236\u4eb2\u4e86\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6211\u5e08\u7236\u53ea\u6709\u5979\u4e00\u4e2a\u5973\u513f\uff0c\u5979\u867d\u7136\u4e0d\u8ba4\u5979\u7684\u7236\u4eb2\uff0c\u6211\u5374\u4e0d\u80fd\u4e0d\u8ba4\u8fd9\u4e2a\u5e08 \u59b9\u3002\u201d\u4ed6\u7b11\u4e86\u4e00\u7b11\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u4f60\u8be5\u660e\u767d\u4e86\u5427\uff0c\u5e72\u6211\u4eec\u8fd9\u884c\u7684\uff0c\u7edd\u4e0d\u5077\u81ea\u5df1\u4eba\uff0c\u6240\u4ee5 \u4e2d\u539f\u7684\u5404\u5927\u95e8\u6d3e\u6211\u90fd\u2018\u5149\u987e\u2019\u4e86\uff0c\u5c31\u662f\u6ca1\u6709\u2018\u5149\u987e\u2019\u6c13\u5c71\u6d3e\uff0c\u4e5f\u6b63\u56e0\u4e3a\u5979\u4e0d\u8ba4\u6211\u8fd9\u4e2a\u5e08\u5144\uff0c \u6240\u4ee5\u6211\u4e5f\u4e0d\u65b9\u4fbf\u53bb\u89c1\u5979\uff0c\u514d\u5f97\u5979\u5fc6\u8d77\u5f80\u4e8b\uff0c\u5f92\u589e\u70e6\u607c\u3002\u201d \u3000\u3000\u6c5f\u5357\u8bf4\u9053\uff1a\u201c\u4f46\u6b64\u5730\u79bb\u6c13\u5c71\u5df2\u662f\u4e0d\u8fdc\uff0c\u6211\u5df2\u7136\u6765\u5230\u6b64\u95f4\uff0c\u548c\u8c37\u5973\u4fa0\u53c8\u662f\u591a\u5e74\u672a\u89c1\uff0c\u6211\u8fd8 \u662f\u60f3\u53bb\u62dc\u8bbf\u5979\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4f60\u53bb\u62dc\u8bbf\u5979\u662f\u5e94\u8be5\u7684\u3002\u4e0d\u8fc7\uff0c\u6211\u4e0d\u60f3\u4f60\u5411\u5979\u6c42\u6551\uff0c\u9664\u975e\u662f\u5f97\u77e5\u91d1\u5927\u4fa0\u7684\u6d88 \u606f\uff0c\u90a3\u53c8\u5f53\u522b\u8bba\u3002\u201d\u59ec\u6653\u98ce\u4f3c\u4e4e\u662f\u6015\u4ed6\u4e0d\u660e\u767d\u4ed6\u7684\u610f\u601d\uff0c\u63a5\u7740\u89e3\u91ca\u9053\uff1a\u201c\u4e00\u6765\u5979\u73b0\u5728\u5df2\u662f\u6c13 \u5c71\u6d3e\u7684\u638c\u4eec\uff0c\u7edd\u4e0d\u80fd\u4e00\u58f0\u4e0d\u54cd\u7684\u5355\u72ec\u79bb\u5c71\uff0c\u800c\u4e14\u5373\u4f7f\u5979\u4e3a\u4e86\u987e\u5ff5\u4e0e\u4f60\u7684\u4ea4\u60c5\uff0c\u613f\u610f\u5982\u6b64\u505a\uff0c \u90a3\u4e5f\u5fc5\u5c06\u60ca\u52a8\u6b66\u6797\uff0c\u53cd\u4e3a\u4e0d\u5999\uff1b\u4e8c\u6765\uff0c\u4ee5\u5979\u7684\u6b66\u529f\uff0c\u7eb5\u6216\u80fd\u80dc\u8fc7\u90a3\u4e5d\u4e2a\u5929\u9b54\u5973\uff0c\u6211\u4e5f\u4e0d\u613f\u5979 \u5192\u8fd9\u4e2a\u9669\u3002\u5f53\u7136\uff0c\u82e5\u662f\u91d1\u5927\u4fa0\u548c\u54b1\u4eec\u540c\u53bb\uff0c\u90a3\u53c8\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u4f60\u6240\u8bf4\u7684\u9053\u7406\u6211\u4e5f\u65e9\u5df2\u60f3\u5230\u4e86\u3002\u6211\u672c\u6765\u5c31\u53ea\u662f\u60f3\u5411\u5979\u63a2\u542c\u91d1\u5927\u4fa0\u7684\u6d88\u606f\uff0c\u5e76 \u975e\u6c42\u5979\u76f8\u52a9\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u975e\u4f46\u5979\u4e0d\u5b9c\u524d\u5f80\u7ec4\u6765\u5c71\uff0c\u4f60\u4e5f\u4e0d\u5b9c\u4e0e\u6211\u540c\u53bb\u3002\u201d \u3000\u3000\u6c5f\u5357\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u4fbf\u660e\u5176\u7406\uff0c\u8981\u77e5\u59ec\u6653\u98ce\u6b64\u53bb\u7ec4\u6765\u5c71\uff0c\u5e76\u975e\u7167\u7740\u6c5f\u6e56\u89c4\u77e9\uff0c\u767b\u95e8\u5411\u5927 \u9b54\u6559\u4e3b\u786c\u8ba8\u786c\u7d22\uff0c\u800c\u662f\u8981\u51ed\u7740\u4ed6\u7684\u795e\u5077\u5999\u6280\uff0c\u5c06\u6c5f\u5357\u7684\u513f\u5b50\u5077\u56de\u6765\u3002\u6c5f\u5357\u7684\u8f7b\u529f\u8fdc\u4e0d\u5982\u4ed6\uff0c \u82e5\u4e0e\u4ed6\u540c\u53bb\uff0c\u53cd\u5c06\u6210\u4e3a\u4ed6\u7684\u7d2f\u8d58\u3002 \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u5f85\u6211\u559d\u5b8c\u4e86\u8fd9\u58f6\u9152\uff0c\u54b1\u4eec\u4fbf\u5373\u5206\u9053\u626c\u9573\uff0c\u5341\u5929\u4e4b\u540e\uff0c\u4f60\u5728\u7956\u889c\u5c71\u4e0b\u7684\u76a4\u9f99 \u5c0f\u9547\u542c\u5019\u6211\u7684\u6d88\u606f\u3002\u55ef\uff0c\u8fd8\u6709\u4e00\u4ef6\u4e8b\u60c5\uff0c\u4f60\u89c1\u4e86\u8c37\u4e4b\u534e\uff0c\u53ef\u4ee5\u5c06\u5929\u9b54\u6559\u7684\u4e8b\u60c5\u544a\u8bc9\u5979\uff0c\u53eb\u5979 \u591a\u52a0\u5c0f\u5fc3\u3002\u8fd9\u7fa4\u5929\u9b54\u5973\u5949\u5389\u80dc\u7537\u4e3a\u6559\u7956\uff0c\u53ea\u6015\u5979\u4eec\u6280\u6210\u4e4b\u540e\uff0c\u4f1a\u5bf9\u4e4b\u534e\u4e0d\u5229\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u8fd9\u6211\u7406\u4f1a\u5f97\uff0c\u5927\u54e5\uff0c\u4f60\u7684\u9152\u559d\u591a\u4e86\u3002\u201d\u8fd9\u65f6\u59ec\u6653\u98ce\u5df2\u559d\u4e86\u4e94\u65a4\u6c7e\u9152\uff0c\u8bf4\u8bdd\u867d \u7136\u8fd8\u6709\u6761\u7406\uff0c\u4f46\u820c\u5934\u5df2\u4f3c\u6709\u70b9\u8f6c\u52a8\u4e0d\u7075\u3002\u6c5f\u5357\u60f3\u5230\u4ed6\u8fd8\u8981\u8d76\u8def\uff0c\u6240\u4ee5\u529d\u4ed6\u5c11\u996e\u3002 \u3000\u3000\u59ec\u6653\u98ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u9152\u9022\u77e5\u5df1\u5343\u676f\u5c11\uff0c\u5c0f\u5144\u5f1f\uff0c\u4e0d\u662f\u4f60\u8fd9\u4e48\u4e00\u8bf4\uff0c\u6211\u8fd8\u8981\u559d\u4ed6\u4e24\u58f6\u3002\u54ce \u5440\uff0c\u600e\u7684\u8fd9\u4e48\u5de7\uff0c\u53c8\u6709\u4e24\u4e2a\u597d\u670b\u53cb\u6765\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5357\u968f\u7740\u4ed6\u7684\u773c\u5149\u671b\u53bb\uff0c\u4e0d\u7531\u5f97\u6479\u5730\u4e00\u60ca\u3002\u53ea\u89c1\u662f\u4e24\u4e2a\u8eab\u6750\u9ad8\u5927\u7684\u756a\u50e7\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6 \u5019\uff0c\u4e1a\u5df2\u8d70\u8fdb\u4e86\u8336\u4ead\u3002 \u3000\u3000\u6c5f\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u4e24\u4e2a\u756a\u50e7\u7684\u76f8\u8c8c\u597d\u719f\uff01\u4ed6\u968f\u5373\u60f3\u8d77\uff0c\u8fd9\u4e24\u4e2a\u756a\u618e\u6b63\u662f\u5f53\u5e74\u5343\u969c\u576a\u4e4b\u4f1a \u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u66fe\u548c\u59ec\u6653\u98ce\u5230\u5c11\u6797\u5bfa\u4e3a\u5b5f\u795e\u901a\u4e0b\u4e66\uff0c\u540e\u6765\u53c8\u4e0e\u59ec\u6653\u98ce\u5230\u5c11\u6797\u5bfa\u5077\u76d7\u79d8\u7c4d\u7684\u90a3\u4e24 \u4e2a\u756a\u50e7\u2014\u2014\u7afa\u6cd5\u5170\u548c\u7afa\u6cd5\u4f11\u3002\u5f53\u65f6\u59ec\u6653\u98ce\u5f97\u624b\u4e4b\u540e\uff0c\u4fbf\u5373\u9003\u8d70\uff0c\u800c\u4ed6\u4eec\u4e24\u4eba\u5219\u5931\u624b\u88ab\u64d2\uff0c\u540e \u6765\u8fd8\u662f\u5c11\u6797\u5bfa\u7684\u65b9\u4e08\u75db\u63b8\u4e0a\u4eba\uff0c\u5ff5\u5728\u4ed6\u4eec\u662f\u4f5b\u95e8\u5f1f\u5b50\uff0c\u8fd9\u624d\u5c06\u4ed6\u4eec\u91ca\u653e\u7684\uff08\u4e8b\u8be6\u300a\u4e91\u6d77\u7389\u5f13 \u7f18\u300b\uff09\u3002 \u3000\u3000\u59ec\u6653\u98ce\u5df2\u81ea\u6709\u4e86\u51e0\u5206\u9189\u610f\uff0c\u4e00\u89c1\u4ed6\u4eec\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u98ce\u628a\u4f60\u4eec\u5439\u6765\u7684\uff1f\u6211\u53ea\u5f53\u4f60\u4eec\u5df2 \u56de\u5929\u7afa\u8001\u5bb6\u53bb\u4e86\u3002\u5f53\u65e5\u54b1\u4eec\u540c\u53bb\u76d7\u4e66\uff0c\u6211\u8dd1\u5f97\u5feb\uff0c\u4f60\u4eec\u8dd1\u5f97\u6162\uff0c\u8fd9\u53ef\u4e0d\u662f\u6211\u6709\u610f\u6487\u5f00\u4f60\u4eec \u7684\u3002\u4e0d\u8fc7\u4f60\u4eec\u5403\u4e86\u5c11\u6797\u5bfa\u548c\u5c1a\u7684\u4e8f\uff0c\u6211\u603b\u4e5f\u6709\u70b9\u7f6a\u8fc7\u7684\uff0c\u6ca1\u8bf4\u7684\uff0c\u6211\u5c31\u8bf7\u4f60\u4eec\u559d\u4e24\u676f\u9152\uff0c\u6743 \u5f53\u8d54\u7f6a\u5427\uff01\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u756a\u50e7\u8d70\u5230\u4e86\u4ed6\u4eec\u684c\u5b50\u524d\u9762\uff0c\u7afa\u6cd5\u5170\u77aa\u7740\u773c\u775b\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u662f\u5df4\u671b\u6211\u4eec\u4e0d\u518d\u6765 \u4e86\uff0c\u54fc\uff0c\u54fc\uff0c\u4e8b\u60c5\u54ea\u80fd\u8fd9\u6837\u5bb9\u6613\u4e86\u7ed3\uff0c\u6211\u4eec\u5df2\u627e\u4e86\u4f60\u597d\u51e0\u5e74\uff0c\u4eca\u65e5\u597d\u4e0d\u5bb9\u6613\u627e\u5230\u4e86\u4f60\uff0c\u5e9f\u8bdd \u4f11\u518d\u8bf4\uff0c\u628a\u6211\u4eec\u5e94\u5f97\u7684\u4e1c\u897f\u4ea4\u51fa\u6765\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u4f60\u4eec\u662f\u60f3\u5206\u8d43\uff1f\u7cdf\u7cd5\uff0c\u4f60\u4eec\u6765\u5f97\u592a\u4e0d\u5de7\u4e86\uff01\u201d\u7afa\u6cd5\u4f11\u6012\u9053\uff1a\u201c\u59ec\u6653 \u98ce\u4f60\u8bf4\u8fc7\u7684\u8bdd\u7b97\u4e0d\u7b97\u6570\uff1f\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u5f53\u7136\u7b97\u6570\uff0c\u4e8e\u6211\u4eec\u8fd9\u884c\u7684\uff0c\u8bb2\u7a76\u7684\u5c31\u662f\u4fe1\u4e49\u8fd9\u4e24\u4e2a \u5b57\uff01\u201d \u3000\u3000\u7afa\u6cd5\u5170\u9732\u51fa\u8ff7\u60d1\u7684\u795e\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u7740\u5440\uff0c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u63a8\u4e09\u963b\u56db\u7684\uff0c\u4e0d\u80af\u723d\u723d\u5feb\u5feb\u5730\u62ff \u51fa\u6765\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u5f53\u5e74\u5408\u4f19\u5230\u5c11\u6797\u5bfa\u53bb\u5077\u4e1c\u897f\u7684\u65f6\u5019\u662f\u66fe\u8bf4\u8fc7\u6709\u798f\u540c\u4eab\uff0c\u6709\u7978 \u540c\u5f53\uff0c\u5077\u6765\u7684\u4e1c\u897f\u5927\u5bb6\u5171\u4eab\u3002\u53ef\u662f\u4f60\u4eec\u5931\u624b\u906d\u64d2\uff0c\u53c8\u76f4\u5230\u4eca\u5929\u624d\u6765\u627e\u6211\uff1f\u6211\u6070\u5de7\u5c31\u5728\u4eca\u5929\u628a \u4e1c\u897f\u9000\u56de\u539f\u4e3b\u4e86\uff01\u201d \u3000\u3000\u7afa\u6cd5\u5170\u5927\u6012\u9053\uff1a\u201c\u4e00\u6d3e\u80e1\u8a00\uff0c\u5c11\u6797\u5bfa\u90a3\u4e09\u5377\u79d8\u7c4d\u4f60\u60f3\u72ec\u541e\u4e86\u4e0d\u6210\uff01\u4f11\u602a\u6211\u4eec\u4e0d\u7559\u60c5 \u9762\uff01\u201d\u58f0\u51fa\u638c\u53d1\uff0c\u201c\u4e53\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u90a3\u5f20\u684c\u5b50\u6253\u88c2\uff0c\u9152\u676f\u9152\u58f6\u90fd\u98de\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8336\u4ead\u4e3b\u4eba\u770b\u89c1\u8fd9\u4e24\u4e2a\u756a\u50e7\u51f6\u795e\u6076\u715e\u7684\u6a21\u6837\uff0c\u65e9\u5df2\u5413\u5f97\u614c\u4e86\uff0c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u5582\uff0c\u4f60\u4eec\u8981\u6253 \u67b6\u5230\u5916\u8fb9\u53bb\uff0c\u6211\u5b81\u613f\u4e0d\u8981\u9152\u94b1\u4e86\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u54ea\u80fd\u53eb\u4f60\u8001\u4eba\u5bb6\u5403\u4e8f\uff01\u201d\u4ed6\u629b\u4e0b\u4e86\u4e00\u952d\u5927\u94f6\uff0c\u9053\uff1a\u201c\u8fd9\u8001\u4eba\u5bb6\u8bf4\u5f97\u5bf9\uff0c \u4f60\u4eec\u8981\u7406\u8bba\u4e5f\u597d\uff0c\u8981\u6253\u67b6\u4e5f\u597d\uff0c\u90fd\u5e94\u5230\u5916\u9762\u627e\u4e2a\u5730\u65b9\uff0c\u522b\u628a\u4eba\u5bb6\u7684\u5e97\u5b50\u7838\u4e86\uff01\u4ed6\u8eab\u5f62\u4e00\u95ea\uff0c \u7acb\u5373\u51fa\u4ead\uff0c\u90a3\u4e24\u4e2a\u756a\u50e7\u614c\u5fd9\u8ddf\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u59ec\u6653\u98ce\u4f60\u8fd8\u60f3\u9003\u4e48\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u6211\u89c1\u4e86\u5931\u7269\u7684\u6b63\u4e3b\u513f\u5c1a\u4e14\u4e0d\u9003\uff0c\u4f55\u51b5\u4f60\u4eec\u53ea\u662f\u7ed9\u6211\u628a\u98ce\u7684\u4f19\u8ba1\uff1f\u201d\u8fd9\u4e24 \u4e2a\u5929\u7afa\u50e7\u4eba\u5728\u672c\u56fd\u7684\u8eab\u4efd\u751a\u9ad8\uff0c\u4ed6\u4eec\u5230\u4e2d\u56fd\u6765\u7684\u76ee\u7684\u4e4b\u4e00\uff0c\u5c31\u662f\u60f3\u53d6\u56de\u8fbe\u6469\u7956\u5e08\u4f20\u7ed9\u5c11\u6797\u5bfa \u7684\u6613\u7b4b\u6d17\u9ad3\u4e8c\u7ecf\u3002 \u3000\u3000\u8981\u77e5\u5c11\u6797\u5bfa\u7684\u59cb\u7956\u8fbe\u6469\u7985\u5e08\u672c\u662f\u5929\u7afa\uff08\u5373\u4eca\u5370\u5ea6\uff09\u9ad8\u50e7\uff0c\u4ed6\u7684\u6b66\u529f\u5728\u672c\u56fd\u4e5f\u6709\u4f20\u4eba\uff0c\u4e0d \u8fc7\u7ecf\u8fc7\u4e86\u5343\u591a\u5e74\u7684\u6f14\u53d8\uff0c\u5929\u7afa\u8fd9\u4e00\u652f\u7684\u6b66\u529f\u5df2\u8fdc\u4e0d\u53ca\u4e2d\u56fd\u5c11\u6797\u5bfa\u8fd9\u4e00\u652f\uff0c\u6240\u4ee5\u4ed6\u4eec\u624d\u4e0d\u62e9\u624b \u6bb5\uff0c\u53cd\u8f6c\u8fc7\u6765\u60f3\u5411\u5c11\u6797\u5bfa\u201c\u53d6\u7ecf\u201d\uff0c\u5728\u4ed6\u4eec\u7684\u5fc3\u76ee\u4e4b\u4e2d\uff0c\u5e76\u4e0d\u8ba4\u4e3a\u8fd9\u662f\u201c\u5077\u201d\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u672c\u6765\u5c31\u987e\u5fcc\u8fd9\u4e2a\u201c\u5077\u201d\u5b57\uff0c\u504f\u504f\u59ec\u6653\u98ce\u4e00\u51fa\u53e3\u5c31\u662f\u5077\u513f\u7684\u672f\u8bed\uff0c\u201c\u5206\u8d43\u201d\u5440\u201c\u628a \u98ce\u201d\u5440\uff0c\u56b7\u4e2a\u4e0d\u505c\uff0c\u5982\u4eca\u66f4\u81ea\u628a\u4ed6\u4eec\u5f53\u4f5c\u201c\u628a\u98ce\u201d\u7684\u5c0f\u4f19\u8ba1\uff0c\u8fd9\u4e24\u4e2a\u756a\u50e7\u5927\u6012\u9a82\u9053\uff1a\u201c\u6cb9\u5634 \u6ed1\u820c\u7684\u5c0f\u8d3c\uff0c\u4f60\u8981\u9003\u4e5f\u9003\u4e0d\u4e86\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff0c\u201c\u6211\u672c\u6765\u5c06\u4f60\u4eec\u5f53\u4f5c\u670b\u53cb\uff0c\u4f60\u4eec\u518d\u9a82\uff0c\u54b1\u4eec\u53ef\u6ca1\u6709\u670b\u53cb\u505a\u5566\uff01\u201d\u6b63\u8981\u52a0\u5feb\u811a \u6b65\uff0c\u5c06\u4ed6\u4eec\u620f\u5f04\u4e00\u4e0b\uff0c\u90a3\u4e24\u4e2a\u756a\u50e7\u56db\u624b\u9f50\u626c\uff0c\u5ffd\u5730\u5411\u524d\u9762\u865a\u6293\u4e00\u628a\uff0c\u6c5f\u5357\u4e5f\u6b63\u5728\u52a0\u5feb\u811a\u6b65\uff0c \u53ea\u89c9\u4e00\u80a1\u5927\u529b\u5c06\u4ed6\u5438\u4f4f\uff0c\u7adf\u4f3c\u771f\u7684\u7ed9\u4eba\u6293\u7740\u4e86\u4e00\u822c\uff0c\u4e3e\u6b65\u7ef4\u8270\uff01 \u3000\u3000\u59ec\u6653\u98ce\u867d\u7136\u4e0d\u81f3\u4e8e\u96be\u4ee5\u4e3e\u6b65\uff0c\u4f46\u901f\u5ea6\u5374\u4e5f\u4e0d\u7531\u81ea\u5df1\u7684\u7f13\u6162\u4e86\u597d\u591a\uff0c\u4f46\u56e0\u4ed6\u6b63\u5728\u53d1\u529b\u5954 \u8dd1\uff0c\u4e24\u76f8\u62b5\u6d88\uff0c\u8fd8\u662f\u6bd4\u539f\u6765\u7684\u52a0\u5feb\u4e86\u4e00\u70b9\u3002\u90a3\u4e24\u4e2a\u756a\u50e7\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u597d\u751f\u60ca\u8be7\u3002 \u3000\u3000\u59ec\u6653\u98ce\u88c5\u4f5c\u82e5\u65e0\u5176\u4e8b\u5730\u7b11\u9053\uff1a\u201c\u6211\u53ea\u662f\u4e3a\u4e86\u987e\u5168\u540c\u4f19\u7684\u4e49\u6c14\uff0c\u624d\u4e0d\u60f3\u9003\u8dd1\u7f62\u4e86\uff0c\u4f60\u4eec\u8981 \u662f\u4e0d\u5ba2\u6c14\uff0c\u6211\u5c31\u8981\u548c\u4f60\u4eec\u73a9\u73a9\u6349\u8ff7\u85cf\u7684\u6e38\u620f\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u756a\u50e7\u8fd9\u65f6\u4e5f\u7684\u786e\u5df2\u6709\u70b9\u6015\u4ed6\u9003\u8dd1\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u4f4f\u4e86\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u5c31\u627e\u4e2a \u5730\u65b9\u7406\u8bba\uff0c\u770b\u4f60\u8fd8\u6709\u4ec0\u4e48\u82b1\u8a00\u5de7\u8bed\uff1f\u201d \u3000\u3000\u4e00\u884c\u4eba\u79bb\u5f00\u5927\u8def\uff0c\u4e0d\u6d88\u591a\u4e45\uff0c\u5df2\u8dd1\u5230\u65f7\u91ce\u4e4b\u5904\uff0c\u59ec\u6653\u98ce\u500f\u5730\u6b62\u6b65\uff0c\u7b11\u9053\uff1a\u201c\u6211\u95ee\u5fc3\u65e0 \u6127\uff0c\u4f55\u987b\u5de7\u8bed\u82b1\u8a00\uff1f\u8001\u5b9e\u544a\u8bc9\u4f60\u4eec\u5427\uff0c\u6211\u4e0d\u4f46\u5077\u4e86\u5c11\u6797\u5bfa\u7684\u4e09\u5377\u771f\u7ecf\uff0c\u4e2d\u539f\u5404\u5927\u95e8\u6d3e\u7684\u6211\u90fd \u5077\u4e86\uff0c\u53ef\u662f\u89c9\u5f97\u5077\u4eba\u5bb6\u7684\u4e1c\u897f\u603b\u662f\u4e0d\u5bf9\uff0c\u6240\u4ee5\u6211\u51b3\u5fc3\u6539\u90aa\u5f52\u6b63\uff0c\u5c31\u5728\u4eca\u5929\u628a\u5077\u6765\u7684\u4e1c\u897f\uff0c\u90fd \u8fd8\u56de\u4e86\u5931\u4e3b\u5566\uff01\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u756a\u50e7\u54ea\u80af\u76f8\u4fe1\uff0c\u7afa\u6cd5\u5170\u9a82\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u4f60\u9a97\u9b3c\u4e5f\u9a97\u4e0d\u4e86\uff01\u201d\u7afa\u6cd5\u4f11\u4e5f\u9a82\u9053\uff1a \u201c\u4f60\u4f1a\u6539\u4e86\u8d3c\u6027\uff0c\u9664\u975e\u6c5f\u6c34\u5012\u6d41\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u5927\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u8bf4\u5f97\u5bf9\uff0c\u4e0d\u4e49\u4e4b\u8d22\u5927\u7ea6\u6211\u4eca\u540e\u8fd8\u662f\u8981\u53d6\u3002\u4f46\u6b66\u529f\u79d8\u7c4d\u4e4b\u7c7b\uff0c\u6211\u662f \u518d\u4e5f\u4e0d\u7528\u5077\u4e86\u3002\u201d\u7afa\u6cd5\u5170\u9a82\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u5077\u5f97\u591f\u4e86\uff0c\u8fd8\u7528\u5077\u5417\uff1f\u201d\u6012\u76ee\u5706\u7741\uff0c\u4fbf\u8981\u52a8\u624b\uff01 \u3000\u3000\u6c5f\u5357\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u5582\uff0c\u5582\uff0c\u6162\u70b9\u52a8\u624b\uff0c\u542c\u6211\u8bf4\u8bdd\uff01\u6211\u53ef\u4ee5\u505a\u8bc1\uff0c\u59ec\u5927\u54e5\u786e\u786e\u5b9e\u5b9e\u662f\u628a\u5077 \u6765\u7684\u5404\u6d3e\u79d8\u7c4d\uff0c\u5305\u62ec\u5c11\u6797\u5bfa\u90a3\u4e09\u5377\u7ecf\u4e66\u5728\u5185\uff0c\u90fd\u5f52\u8fd8\u539f\u4e3b\u5566\uff01\u4f60\u4eec\u82e5\u662f\u6709\u672c\u9886\u7684\u53ef\u5230\u5c11\u6797\u5bfa \u53bb\u8981\uff0c\u82e6\u82e6\u8feb\u6211\u7684\u5927\u54e5\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7afa\u6cd5\u5170\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u8981\u6211\u4fe1\u4f60\u7684\u8bdd\uff01\u201d\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u54ce\u5440\uff0c \u4f60\u4eec\u7adf\u4e0d\u4fe1\u6211\uff1f\u4f60\u4eec\u53ef\u4ee5\u53bb\u6253\u542c\u6253\u542c\uff0c\u6211\u53eb\u6c5f\u5357\uff0c\u4ece\u6765\u4e0d\u8bf4\u5047\u8bdd\uff01\u201d\u7afa\u6cd5\u4f11\u559d\u9053\uff1a\u201c\u8c01\u7406\u4f60 \u6c5f\u5317\u6c5f\u5357\u7684\uff0c\u6eda\u5f00\uff01\u201d\u98de\u8d77\u4e00\u811a\u8e22\u4ed6\uff0c\u6c5f\u5357\u8fc5\u5373\u4e00\u4e2a\u7b4b\u6597\u5012\u7ffb\u51fa\u53bb\uff0c\u53eb\u9053\uff1a\u201d\u6ca1\u542c\u8fc7\u8fde\u529d\u67b6 \u7684\u4eba\u4e5f\u8981\u6253\u7684\uff0c\u4f60\u4eec\u771f\u662f\u86ee\u4e0d\u8bb2\u7406\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u8fd9\u4e8b\u4e0d\u7528\u4f60\u7ba1\uff0c\u4f60\u8fd8\u662f\u8d70\u4e86\u7684\u597d\uff01\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u6211\u82e5\u8d70\u5f00\uff0c \u90a3\u8fd8\u7b97\u5f97\u662f\u4ec0\u4e48\u2018\u6709\u798f\u540c\u4eab\uff0c\u6709\u96be\u540c\u5f53\u2019\u7684\u5144\u5f1f\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u62ff\u4ed6\u6ca1\u6cd5\uff0c\u8f6c\u8fc7\u5934\u5bf9\u90a3\u4e24\u4e2a\u756a\u50e7\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u80af\u76f8\u4fe1\u6211\u7684\u8bdd\uff0c\u90a3\u4e5f\u6ca1\u6709\u529e\u6cd5\u3002 \u4f60\u4eec\u8981\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u7afa\u6cd5\u5170\u9a82\u9053\uff1a\u201c\u4f60\u4e0d\u62ff\u51fa\u6765\uff0c\u6211\u5c31\u62bd\u4f60\u7684\u7b4b\uff0c\u5265\u4f60\u7684\u76ae\uff01\u201d\u59ec\u6653\u98ce\u672c\u6765\u8fd8\u60f3\u4e0e\u4ed6\u4eec\u8bb2\u8bb2 \u4ea4\u60c5\uff0c\u8fd9\u4e00\u4e0b\u53ef\u6c14\u8d77\u6765\u4e86\uff0c\u6012\u6781\u6c14\u6781\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u5c31\u7b97\u6211\u5077\u6765\u7684\u4e1c\u897f\uff0c\u672a\u66fe\u8fd8\u7ed9\u5931 \u4e3b\uff0c\u4e5f\u4e0d\u7ed9\u4f60\uff01\u4f60\u4eec\u6709\u672c\u4e8b\u7684\uff0c\u81ea\u5df1\u53bb\u5077\uff01\u597d\u6ca1\u51fa\u606f\uff0c\u6015\u4e86\u5c11\u6797\u5bfa\uff0c\u5374\u6765\u8fd9\u91cc\u6b3a\u4fae\u4eba\uff01\u4f60\u4eec \u5984\u60f3\u5750\u4eab\u5176\u6210\uff0c\u6211\u504f\u504f\u4e0d\u5356\u4f60\u4eec\u7684\u5e10\uff01\u6211\u5012\u8981\u770b\u770b\uff0c\u4f60\u4eec\u7a76\u7adf\u8981\u600e\u6837\u6765\u62bd\u6211\u7684\u7b4b\uff0c\u5265\u6211\u7684 \u76ae\uff1f\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u7afa\u6cd5\u5170\u5927\u543c\u4e00\u58f0\uff0c\u604d\u5982\u9752\u5929\u8d77\u4e86\u4e00\u4e2a\u9739\u96f3\uff0c\u6c5f\u5357\u5835\u7740\u8033\u6735\uff0c\u5140\u81ea\u7ed9\u9707 \u5f97\u5fc3\u60ca\u80c6\u6218\u3002\u539f\u6765\u7afa\u6cd5\u5170\u7528\u7684\u662f\u4f5b\u95e8\u201c\u72ee\u5b50\u543c\u529f\u201d\uff0c\u5e78\u4e8f\u6c5f\u5357\u7684\u5185\u529f\u4ea6\u5df2\u6709\u4e86\u76f8\u5f53\u9020\u8be3\uff0c\u8fd9 \u624d\u4e0d\u81f4\u6655\u7729\u3002 \u3000\u3000\u59ec\u6653\u98ce\u867d\u7136\u7981\u53d7\u5f97\u8d77\uff0c\u8033\u9f13\u4ea6\u81ea\u55e1\u55e1\u4f5c\u54cd\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5f53\u5e74\u5c11\u6797\u5bfa\u7684\u5927\u60b2\u3001\u5927 \u667a\u4e24\u4f4d\u7985\u5e08\uff0c\u4e5f\u5728\u4ed6\u4eec\u7684\u8ddf\u524d\u683d\u4e86\u7b4b\u6597\uff0c\u8981\u51fa\u5230\u8fbe\u6469\u9662\u7684\u957f\u8001\u624d\u80fd\u5c06\u4ed6\u4eec\u964d\u4f0f\u3002\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7afa\u6cd5\u5170\u4e00\u58f0\u5927\u543c\uff0c\u7acb\u5373\u4fbf\u6251\u8fc7\u6765\uff0c\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u4f60\u7684\u9b3c\u53eb\u53ef\u5413\u4e0d\u4e86 \u4eba\uff01\u4f60\u4e5f\u77a7\u77a7\u6211\u7684\uff01\u201d\u8eab\u5f62\u4e00\u98d8\u4e00\u95ea\uff0c\u7afa\u6cd5\u5170\u53cc\u624b\u6293\u7a7a\uff0c\u59ec\u6653\u98ce\u559d\u4e00\u58f0\uff1a\u201c\u7740\uff01\u201d\u4f7f\u51fa\u9634\u9633 \u6293\u7684\u529f\u592b\uff0c\u4e00\u6293\u5c31\u6293\u7740\u4e86\u4ed6\u7684\u7435\u7436\u9aa8\u3002\u6c5f\u5357\u9ad8\u5174\u5f97\u559d\u8d77\u5f69\u6765\uff0c\u59ec\u6653\u98ce\u6ca1\u60f3\u5230\u5982\u6b64\u5bb9\u6613\u5c31\u6293\u4f4f \u4e86\u4ed6\uff0c\u4e5f\u89c9\u5f97\u5927\u51fa\u610f\u5916\uff01 \u3000\u3000\u7435\u7436\u9aa8\u662f\u624b\u5c11\u9633\u7ecf\u8109\u4ea4\u5e94\u4e4b\u5904\uff0c\u7435\u7436\u9aa8\u7684\u8f6f\u7b4b\u7ed9\u4eba\u6293\u4f4f\uff0c\u591a\u597d\u7684\u6b66\u529f\u4e5f\u8981\u5168\u8eab\u762b\u75ea\uff0c\u52a8 \u5f39\u4e0d\u5f97\u3002\u6240\u4ee5\u82e5\u8981\u5e9f\u6389\u522b\u4eba\u7684\u6b66\u529f\u3002\u53ea\u8981\u6311\u4e86\u4ed6\u7684\u7435\u7436\u9aa8\u4fbf\u6210\u3002\u59ec\u6653\u98ce\u4e00\u51fa\u624b\u5c31\u6293\u4f4f\u7afa\u6cd5\u5170 \u7684\u7435\u7436\u9aa8\uff0c\u81ea\u5df1\u4e5f\u89c9\u5f97\u80dc\u6765\u592a\u6613\u3002\u4ed6\u662f\u4e2a\u8001\u6c5f\u6e56\u4e86\uff0c\u7acb\u5373\u4fbf\u60f3\u5230\u654c\u4eba\u6709\u8bc8\u3002 \u3000\u3000\u679c\u7136\u5c31\u5728\u4ed6\u5fc3\u5ff5\u65b9\u52a8\u4e4b\u9645\uff0c\u5ffd\u89c9\u6240\u6293\u4e4b\u5904\uff0c\u67d4\u82e5\u65e0\u9aa8\uff0c\u7afa\u6cd5\u5170\u7684\u624b\u81c2\u7a81\u7136\u62d0\u4e86\u4e2a\u5f2f\uff0c \u201c\u556a\u201d\u7684\u4e00\u638c\uff0c\u5c31\u671d\u7740\u59ec\u6653\u98ce\u7684\u201c\u592a\u9633\u7a74\u201d\u62cd\u8fc7\u6765\uff01\u7435\u7436\u9aa8\u88ab\u6293\uff0c\u5c45\u7136\u8fd8\u80fd\u591f\u4f7f\u52b2\u53d1\u638c\uff0c\u59ec \u6653\u98ce\u867d\u5df2\u6599\u5230\u654c\u4eba\u6709\u8bc8\uff0c\u4e5f\u8fd8\u672a\u60f3\u5230\u4ed6\u7684\u6b66\u529f\u7adf\u662f\u5982\u6b64\u602a\u5f02\uff01 \u3000\u3000\u539f\u6765\u8fd9\u662f\u5370\u5ea6\u72ec\u7279\u7684\u745c\u5496\u529f\u592b\uff0c\u7ec3\u5230\u9ad8\u6df1\u7684\u5883\u754c\uff0c\u808c\u8089\u53ef\u4ee5\u968f\u610f\u626d\u66f2\u53d8\u5f62\uff0c\u968f\u7740\u654c\u4eba\u7684 \u6765\u52bf\u5378\u529b\u5316\u89e3\u3002\u59ec\u6653\u98ce\u901a\u6653\u4e2d\u56fd\u7684\u5404\u6d3e\u6b66\u529f\uff0c\u5bf9\u5370\u5ea6\u7684\u745c\u5496\u672f\u5374\u662f\u672a\u66fe\u5b66\u8fc7\u3002 \u3000\u3000\u5e78\u4e8f\u4ed6\u65e9\u6709\u63d0\u9632\uff0c\u4e00\u89c9\u4e0d\u5999\uff0c\u4e94\u6307\u4e00\u677e\uff0c\u7acb\u5373\u817e\u8eab\u98de\u8d77\uff0c\u7afa\u6cd5\u5170\u4e00\u638c\u62cd\u7a7a\uff0c\u59ec\u6653\u98ce\u5df2\u81ea \u5230\u4e86\u4ed6\u7684\u80cc\u540e\u3002 \u3000\u3000\u7afa\u6cd5\u5170\u542c\u5f97\u80cc\u540e\u98ce\u58f0\uff0c\u53cd\u624b\u4fbf\u662f\u4e00\u62f3\uff1a\u7528\u7684\u662f\u8fbe\u6469\u7956\u5e08\u6240\u4f20\u7684\u201c\u4e11\u884c\u62f3\u201d\u4e2d\u7684\u201c\u9f99 \u62f3\u201d\uff0c\u5728\u5404\u79cd\u62f3\u6cd5\u4e4b\u4e2d\u6700\u4e3a\u521a\u731b\u6709\u529b\uff01\u59ec\u6653\u98ce\u8bc6\u5f97\u5389\u5bb3\uff0c\u8d5e\u9053\uff1a\u201c\u521a\u67d4\u4e92\u6613\uff0c\u53d8\u5f97\u597d\u5feb\uff0c\u53ef \u662f\uff0c\u4e5f\u8fd8\u672a\u6765\u7684\u53ca\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u4e00\u62f3\u5df2\u51fb\u5230\u59ec\u6653\u98ce\u8eab\u4e0a\uff0c\u6c5f \u5357\u7684\u5f69\u58f0\u672a\u5df2\uff0c\u767b\u65f6\u5413\u5f97\u60ca\u53eb\u8d77\u6765\uff01 \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u793c\u5c1a\u5f80\u6765\uff0c\u4f60\u4e5f\u77a7\u6211\u7684\uff01\u201d\u7afa\u6cd5\u5170\u4e00\u62f3\u51fb\u4e0b\uff0c\u5982\u4e2d\u68c9\u7d6e\uff0c\u539f\u6765\u59ec\u6653\u98ce\u8fd0 \u7528\u4e0a\u4e58\u7684\u5185\u529f\uff0c\u4e5f\u662f\u5728\u5239\u90a3\u4e4b\u95f4\uff0c\u5c06\u6240\u53d7\u7684\u529b\u9053\uff0c\u5c3d\u90fd\u6d88\u53bb\u3002 \u3000\u3000\u59ec\u6653\u98ce\u8eab\u624b\u4f55\u7b49\u654f\u6377\uff0c\u5c31\u5728\u7afa\u6cd5\u5170\u4e00\u6014\u4e4b\u9645\uff0c\u53cc\u6307\u4e00\u4f38\uff0c\u95ea\u7535\u822c\u4fbf\u70b9\u4e2d\u4e86\u4ed6\u7684\u8109\u95e8\uff0c\u7afa \u6cd5\u5170\u5927\u53eb\u4e00\u58f0\uff0c\u767b\u65f6\u8dc4\u8dc4\u8e09\u8e09\u7684\u9000\u4e86\u51e0\u6b65\uff0c\u6447\u6447\u6b32\u5760\uff01 \u3000\u3000\u59ec\u6653\u98ce\u89c1\u4ed6\u8155\u8109\u88ab\u70b9\uff0c\u5c45\u7136\u5e76\u672a\u5012\u4e0b\uff0c\u4e5f\u662f\u6697\u6697\u4f69\u670d\u3002\u4f46\u81ea\u5df1\u62a2\u4e86\u5148\u624b\uff0c\u5360\u4e86\u4e0a\u98ce\uff0c\u8d81 \u7740\u7afa\u6cd5\u5170\u7acb\u8db3\u672a\u7a33\uff0c\u7acb\u5373\u8ddf\u4e0a\uff0c\u5411\u4ed6\u5c55\u5f00\u4e86\u66b4\u98ce\u9aa4\u96e8\u822c\u7684\u653b\u51fb\uff01 \u3000\u3000\u4e24\u4eba\u4ea4\u6362\u4e86\u8fd9\u51e0\u62db\uff0c\u5f7c\u6b64\u90fd\u5df2\u77e5\u9053\u5bf9\u65b9\u7684\u65a4\u4e24\u3002\u59ec\u6653\u98ce\u7684\u6b66\u529f\u4e0e\u7afa\u6cd5\u5170\u7684\u745c\u5496\u529f\u592b\uff0c\u5f02 \u66f2\u540c\u5de5\uff0c\u5b9e\u662f\u96be\u5206\u4e0a\u4e0b\u3002\u53ef\u662f\u59ec\u6653\u98ce\u8eab\u6cd5\u8f7b\u7075\uff0c\u5374\u5927\u5927\u7684\u5360\u4e86\u4fbf\u5b9c\u3002 \u3000\u3000\u7afa\u6cd5\u4f11\u89c1\u4ed6\u5e08\u5144\u8fde\u8fde\u5403\u4e8f\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u5ffd\u5730\u5c06\u8888\u88df\u8131\u4e0b\uff0c\u4fbf\u4e0a\u524d\u52a9\u653b\u3002 \u3000\u3000\u6c5f\u5357\u9a82\u9053\uff1a\u201c\u5582\uff0c\u5582\uff01\u4f60\u4eec\u61c2\u5f97\u6c5f\u6e56\u9053\u4e0a\u7684\u89c4\u77e9\u6ca1\u6709\uff1f\u4e24\u4e2a\u6253\u4e00\u4e2a\uff0c\u597d\u4e0d\u8981\u8138\uff01\u201d \u3000\u3000\u7afa\u6cd5\u4f11\u6012\u9053\uff1a\u201c\u4e0d\u670d\u6c14\u4f60\u5c31\u4e0a\u6765\uff01\u201d\u59ec\u6653\u98ce\u8fde\u5fd9\u7528\u5929\u9041\u4f20\u97f3\u5411\u4ed6\u8bf4\u9053\uff1a\u201c\u597d\u5144\u5f1f\uff0c\u6211\u9886 \u4f60\u7684\u60c5\u4fbf\u662f\uff0c\u4f60\u5343\u4e07\u4e0d\u53ef\u4e0a\u6765\uff01\u6211\u6253\u4ed6\u4eec\u4e0d\u8fc7\uff0c\u6211\u8fd8\u4f1a\u8dd1\u5462\uff0c\u4f60\u8981\u662f\u6253\u4e0d\u8fc7\uff0c\u4f60\u53ef\u6ca1\u6709\u6211\u4e48 \u5feb\u554a\uff01\u201d\u6c5f\u5357\u60f3\u60f3\u4e5f\u5bf9\uff0c\u4fbf\u9053\uff1a\u201c\u5c31\u8fd9\u4e48\u529e\uff0c\u6211\u5728\u8fd9\u91cc\u52a9\u9635\uff0c\u4f60\u4e0d\u8dd1\u6211\u4e5f\u662f\u4e0d\u8dd1\u7684\uff01\u201d \u3000\u3000\u6c5f\u5357\u4e0d\u61c2\u5929\u9041\u4f20\u97f3\u4e4b\u672f\uff0c\u4ed6\u8fd9\u51e0\u53e5\u8bdd\u5927\u58f0\u8bf4\u51fa\uff0c\u767b\u65f6\u5c31\u6cc4\u4e86\u5e95\uff0c\u7afa\u6cd5\u4f11\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d \u4e2a\u72e1\u733e\u7684\u5c0f\u8d3c\uff0c\u5c31\u60f3\u8dd1\u4e86\u4e48\uff1f\u201d\u8888\u88df\u4e00\u6643\uff0c\u604d\u5982\u4e00\u7247\u7ea2\u971e\uff0c\u6a2a\u7a7a\u5377\u5230\uff0c\u59ec\u6653\u98ce\u4e00\u638c\u62cd\u53bb\uff0c \u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u8888\u88df\u5c55\u4e86\u5f00\u6765\uff0c\u8981\u4e0d\u662f\u59ec\u6653\u98ce\u8eb2\u95ea\u5f97\u5feb\uff0c\u9669\u4e9b\u7ed9\u4ed6\u7f69\u4f4f\uff01 \u3000\u3000\u539f\u6765\u7afa\u6cd5\u4f11\u8fd9\u4ef6\u8888\u88df\u662f\u592d\u7afa\u91d1\u8695\u4e1d\u7ec7\u7684\uff0c\u666e\u901a\u5200\u5251\u5c1a\u4e14\u4e0d\u80fd\u6233\u7a7f\uff0c\u800c\u4e14\u8f7b\u8f6f\u4e4b\u6781\uff0c\u6beb\u4e0d \u53d7\u529b\uff0c\u59ec\u6653\u98ce\u53ea\u51ed\u7740\u4e00\u53cc\u8089\u638c\uff0c\u9976\u4ed6\u6b66\u529f\u591a\u597d\uff0c\u4e5f\u662f\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u756a\u50e7\u7684\u529f\u529b\u548c\u59ec\u6653\u98ce\u90fd\u5dee\u4e0d\u591a\uff0c\u521a\u624d\u59ec\u6653\u98ce\u4e0e\u7afa\u6cd5\u5170\u5355\u6253\u72ec\u6597\uff0c\u6240\u4ee5\u5360\u5f97\u4fbf\u5b9c\uff0c \u4e43\u662f\u56e0\u4e3a\u4ed6\u7684\u8f7b\u529f\u9ad8\u660e\u4e4b\u6545\u3002\u5982\u4eca\u7afa\u6cd5\u4f11\u4e0a\u6765\u52a9\u9635\uff0c\u5c06\u4ed6\u90a3\u4ef6\u5b9d\u8d1d\u8888\u88df\u6325\u821e\u8d77\u6765\uff0c\u5c31\u4f3c\u64a4\u4e0b \u4e00\u5f20\u5929\u7f57\u5730\u7f51\uff0c\u59ec\u6653\u98ce\u7684\u8eab\u6cd5\u4e0d\u8bba\u8d70\u5230\u54ea\u4e2a\u65b9\u4f4d\uff0c\u90fd\u7ed9\u4ed6\u7684\u8888\u88df\u6321\u4f4f\u3002\u7afa\u6cd5\u5170\u4e58\u52bf\u8ffd\u51fb\uff0c\u90a3 \u4ef6\u8888\u88df\u5bf9\u4ed6\u6765\u8bf4\uff0c\u5374\u4f3c\u4e00\u9762\u76fe\u724c\uff0c\u5bf9\u4ed6\u5927\u5927\u6709\u5229\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u6b64\u6d88\u5f7c\u957f\uff0c\u767b\u65f6\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u5360 \u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\uff0c\u59ec\u6653\u98ce\u4e2d\u4e86\u4e00\u8bb0\u201c\u5370\u638c\u201d\uff0c\u8fd9\u4e00\u8bb0\u201c\u5370\u638c\u201d\u7528\u7684\u7eaf\u662f\u9634\u67d4\u4e4b\u529b\uff0c\u59ec\u6653\u98ce\u7528\u4e86\u4e0a \u4e58\u5185\u529f\uff0c\u4e5f\u4e0d\u8fc7\u5316\u4e86\u4ed6\u4e03\u6210\u529b\u9053\uff0c\u4e94\u810f\u53d7\u5230\u9707\u52a8\uff0c\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u521a\u624d\u6240\u559d\u7684\u9152\u90fd\u5410\u4e86\u51fa \u6765\u3002 \u3000\u3000\u4ed6\u7684\u9152\u672c\u6765\u5c31\u559d\u5f97\u591a\u4e86\uff0c\u8fd9\u4e00\u5410\u771f\u5982\u55b7\u6cc9\u98de\u5c04\uff0c\u5c06\u90a3\u4ef6\u8888\u88df\u90fd\u55b7\u5f97\u6e7f\u6dcb\u6dcb\u7684\uff0c\u9152\u6c14\u7d20 \u4eba\u3002\u7afa\u6cd5\u4f11\u5927\u6012\u9a82\u9053\uff1a\u201c\u81ed\u8d3c\uff0c\u5c82\u6709\u6b64\u7406\uff0c\u5f04\u6c61\u4e86\u4f5b\u7237\u7684\u8888\u88df\uff01\u201d\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u4f60\u597d\u9999 \u4e48\uff1f\u4f60\u8eab\u4e0a\u90a3\u80a1\u81ed\u5473\u513f\u6bd4\u9152\u6c14\u90a3\u662f\u96be\u95fb\u5f97\u591a\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5357\u4e50\u5f97\u6367\u8179\u5927\u7b11\uff0c\u53ef\u662f\u53ea\u7b11\u4e86\u4e00\u4f1a\uff0c\u5c31\u518d\u4e5f\u7b11\u4e0d\u51fa\u6765\u3002\u59ec\u6653\u98ce\u8fd9\u80a1\u201c\u9152\u6d6a\u201d\u867d\u80fd\u6682\u65f6 \u89e3\u56f0\uff0c\u53ef\u662f\u8d8a\u53d1\u6fc0\u6012\u4e86\u7afa\u6cd5\u4f11\uff0c\u9152\u6d6a\u55b7\u8fc7\uff0c\u4ed6\u90a3\u4ef6\u8888\u88df\u66f4\u662f\u8d8a\u8feb\u8d8a\u8fd1\uff0c\u5708\u5b50\u4e5f\u8d8a\u6536\u8d8a\u5c0f\uff0c\u59ec \u6653\u98ce\u4e0e\u7afa\u6cd5\u5170\u5f62\u6210\u4e86\u8fd1\u8eab\u8089\u640f\u7684\u573a\u9762\uff0c\u6709\u597d\u51e0\u6b21\u4e3a\u4e86\u907f\u5f00\u8888\u88df\u7f69\u4f53\u4e4b\u707e\uff0c\u9669\u9669\u7ed9\u7afa\u6cd5\u5170\u6253 \u4e2d\uff01\u6c5f\u5357\u770b\u8fd9\u79cd\u60c5\u5f62\uff0c\u59ec\u6653\u98ce\u975e\u4f46\u5df2\u6ca1\u6709\u53d6\u80dc\u7684\u5e0c\u671b\uff0c\u8fde\u9003\u8d70\u4e5f\u4e0d\u5bb9\u6613\u4e86\uff01 \u3000\u3000\u6c5f\u5357\u6361\u8d77\u4e86\u51e0\u9897\u77f3\u5b50\uff0c\u4f7f\u7528\u8fde\u73e0\u5f39\u7684\u624b\u6cd5\uff0c\u5411\u7afa\u6cd5\u5170\u7684\u80cc\u5fc3\u6253\u53bb\uff0c\u540c\u65f6\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c \u4f60\u5feb\u8dd1\u5427\u3002\u201d \u3000\u3000\u6c5f\u5357\u7684\u6253\u7a74\u529f\u592b\u672c\u6765\u975e\u5e38\u5389\u5bb3\uff0c\u53ef\u60dc\u4ed6\u7684\u529f\u529b\u5c1a\u5acc\u4e0d\u8db3\uff0c\u53ea\u542c\u5f97\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u7afa\u6cd5\u5170 \u7684\u80cc\u5fc3\u5927\u7a74\u7ed9\u77f3\u5b50\u6253\u4e2d\uff0c\u5374\u53ea\u662f\u6643\u4e86\u4e24\u6643\uff0c\u672a\u66fe\u5012\u4e0b\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7afa\u6cd5\u4f11\u7684\u8888\u88df\u4e00 \u5c55\uff0c\u628a\u6253\u8fc7\u6765\u7684\u77f3\u5b50\u5168\u90e8\u5377\u53bb\uff01 \u3000\u3000\u4ee5\u7afa\u6cd5\u5170\u7684\u672c\u9886\uff0c\u672c\u6765\u4e0d\u5e94\u7ed9\u6c5f\u5357\u6253\u4e2d\uff0c\u53ea\u56e0\u4ed6\u538b\u6839\u513f\u4e0d\u628a\u6c5f\u5357\u653e\u5728\u5fc3\u4e0a\uff0c\u4e00\u65f6\u8f7b\u654c\uff0c \u4ee5\u81f4\u5403\u4e86\u70b9\u4e8f\uff0c\u867d\u662f\u5e76\u65e0\u5927\u788d\uff0c\u4f46\u662f\u4e2d\u7684\u4e43\u662f\u75d5\u75d2\u7a74\uff0c\u4e00\u9635\u9ebb\u75d2\u75d2\u7684\u611f\u89c9\uff0c\u4e5f\u662f\u751a\u4e3a\u96be\u53d7\uff0c \u767b\u65f6\u4ed6\u66b4\u8df3\u5982\u96f7\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u8d3c\uff0c\u8fde\u4f60\u4e5f\u7b97\u4e0a\u4e86\uff0c\u4f60\u65e2\u7136\u4e0e\u8fd9\u72d7\u8d3c\u5144\u5f1f\u76f8\u79f0\uff0c\u6709\u4e49\u6c14\u7684\u5c31\u4e00 \u9f50\u4e0a\u5427\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u8fde\u5fd9\u7528\u5929\u9041\u4f20\u97f3\u4e4b\u672f\u5411\u4ed6\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4e0d\u53ef\u9c81\u83bd\uff0c\u5feb\u8d70\uff01\u201d\u9ad8\u624b\u5bf9\u654c\uff0c\u54ea\u5bb9\u6709 \u534a\u70b9\u5206\u5fc3\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u7afa\u6cd5\u4f11\u8888\u88df\u4e00\u5c55\uff0c\u5c06\u521a\u624d\u6240\u5377\u7684\u77f3\u5b50\u90fd\u6253\u51fa\u6765\uff0c\u59ec\u6653\u98ce\u817e\u8eab\u95ea\u907f\uff0c\u5374 \u7ed9\u7afa\u6cd5\u5170\u4e58\u673a\u6253\u4e2d\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u59ec\u6653\u98ce\u7684\u4e0a\u8863\u88c2\u5f00\u4e00\u9053\u53e3\u5b50\uff0c\u80cc\u4e0a\u73b0\u51fa\u4e86\u8840\u75d5\u3002 \u3000\u3000\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u54b1\u4eec\u6709\u798f\u540c\u4eab\uff0c\u6709\u7978\u540c\u5f53\uff0c\u4f60\u4e0d\u5fc5\u529d\u6211\uff0c\u6211\u975e\u4e0a\u6765\u5e2e\u4f60\u4e0d\u53ef\uff01\u201d\u8eab \u5f62\u4e00\u8d77\uff0c\u767b\u65f6\u5982\u7bad\u79bb\u5f26\uff0c\u59ec\u6653\u98ce\u5927\u4e3a\u5403\u60ca\uff0c\u5374\u662f\u65e0\u6cd5\u963b\u62e6\uff01 \u3000\u3000\u7afa\u6cd5\u4f11\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0f\u8d3c\uff0c\u679c\u7136\u597d\u591f\u4e49\u6c14\uff01\u201d\u8888\u88df\u75be\u5c55\uff0c\u7acb\u5411\u6c5f\u5357\u7f69\u4e0b\uff0c\u6c5f\u5357\u53cc\u624b\u4e00 \u6491\uff0c\u6293\u7740\u4e86\u8888\u88df\u4fbf\u6495\uff0c\u54ea\u6495\u5f97\u52a8\uff0c\u773c\u89c1\u7ed9\u8888\u88df\u88f9\u4f4f\uff0c\u59ec\u6653\u98ce\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6492\u624b\uff01\u201d\u53cc\u6307\u75be\u70b9 \u7afa\u6cd5\u4f11\u8109\u95e8\u3002 \u3000\u3000\u4ed6\u7528\u7684\u662f\u201c\u7384\u9634\u6307\u201d\u7684\u529f\u592b\u3002\u4e0e\u4fee\u7f57\u9634\u715e\u529f\u5f02\u66f2\u540c\u5de5\uff0c\u867d\u7136\u6ca1\u6709\u4fee\u7f57\u9634\u715e\u529f\u7684\u5a01\u529b\uff0c\u4f46 \u52b2\u98ce\u75be\u5c04\uff0c\u53ea\u653b\u654c\u4eba\u7684\u4e00\u70b9\uff0c\u5374\u662f\u5bb9\u6613\u4f24\u4eba\uff0c\u59ec\u6653\u98ce\u672c\u6765\u4e0d\u6b32\u4f7f\u8fd9\u7b49\u9634\u6bd2\u7684\u529f\u592b\uff0c\u8fd9\u65f6\u4e3a\u4e86 \u6c5f\u5357\uff0c\u4e0d\u5f97\u5df2\u624d\u884c\u4f7f\u7528\u3002 \u3000\u3000\u7afa\u6cd5\u4f11\u84e6\u89c9\u5bd2\u98ce\u523a\u76ee\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u5c06\u8888\u88df\u6321\u4f4f\u9762\u95e8\uff0c\u53ef\u662f\u5df2\u7ecf\u7ea2\u80bf\uff0c\u6cea\u73e0\u6eda\u6eda\u5730\u6d41\u4e0b \u6765\u3002\u6c5f\u5357\u5927\u559c\u7b11\u9053\uff1a\u201c\u5927\u54e5\uff0c\u79c3\u9a74\u5df2\u7ecf\u54ed\u4e86\uff1f\u54b1\u4eec\u5c31\u9976\u4e86\u4ed6\u5427\uff01\u201d\u59ec\u6653\u98ce\u62d6\u7740\u4ed6\u7684\u624b\uff0c\u6b63\u8981 \u8d70\u65f6\uff0c\u7afa\u6cd5\u4f11\u5927\u6012\uff0c\u8888\u88df\u65cb\u98ce\u4e00\u821e\uff0c\u6c5f\u5357\u88ab\u90a3\u80a1\u5f3a\u529b\u4e00\u9707\uff0c\u767b\u65f6\u8e09\u8dc4\u7684\u5012\u9000\u51e0\u6b65\uff0c\u59ec\u6653\u98ce\u5927 \u60ca\uff0c\u6025\u5fd9\u8f6c\u8eab\u62c9\u4ed6\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7afa\u6cd5\u5170\u7684\u638c\u98ce\u53c8\u5df2\u5288\u9762\u6253\u5230\uff0c\u7afa\u6cd5\u4f11\u7684\u8888\u88df\u5f53\u4e2d\u4e00 \u62c2\uff0c\u767b\u65f6\u628a\u4ed6\u4eec\u4e24\u4eba\u9694\u65ad\uff01 \u3000\u3000\u59ec\u6653\u98ce\u987e\u4e0d\u53ca\u62b5\u6321\u7afa\u6cd5\u5170\u7684\u62f3\u5934\u3001\u53cd\u624b\u4e00\u638c\uff0c\u5148\u5411\u540e\u65b9\u62cd\u51fa\u3002 \u3000\u3000\u7afa\u6cd5\u4f11\u521a\u8981\u6293\u7740\u6c5f\u5357\uff0c\u59ec\u6653\u98ce\u4e00\u638c\u62cd\u53bb\uff0c\u628a\u6a2a\u5728\u4ed6\u4eec\u4e2d\u95f4\u7684\u90a3\u4ef6\u8888\u88df\u9707\u8361\u8d77\u6765\uff0c\u7afa\u6cd5\u4f11 \u7a81\u7136\u611f\u5230\u4e00\u80a1\u5bd2\u610f\u4ece\u8109\u95e8\u4e0a\u76f4\u900f\u4e0a\u6765\uff0c\u4e0d\u7531\u5f97\u5403\u4e86\u4e00\u60ca\uff0c\u539f\u6765\u59ec\u6653\u98ce\u7684\u4fee\u7f57\u9634\u715e\u529f\u4ea6\u5df2\u7ec3\u5230 \u4e86\u7b2c\u4e03\u91cd\u7684\u5883\u754c\uff0c\u53ef\u4ee5\u201c\u9694\u7269\u4f20\u529f\u201d\u4e86\u3002 \u3000\u3000\u4f46\u662f\u59ec\u6653\u98ce\u4e3a\u4e86\u63f4\u6551\u6c5f\u5357\uff0c\u5374\u53c8\u4e2d\u4e86\u7afa\u6cd5\u5170\u7684\u4e00\u62f3\uff0c\u4ed6\u4e0d\u80fd\u4e00\u5fc3\u4e8c\u7528\uff0c\u62a4\u8eab\u7684\u529f\u529b\u7531\u4e8e \u8981\u8fd0\u7528\u4fee\u7f57\u9634\u715e\u529f\u7684\u7f18\u6545\u800c\u5927\u5927\u51cf\u5f31\uff0c\u7ed9\u8fd9\u4e00\u62f3\u6253\u4e2d\uff0c\u75db\u5f97\u773c\u4e2d\u5192\u51fa\u91d1\u661f\uff0c\u51e0\u4e4e\u683d\u5012\uff01 \u3000\u3000\u7afa\u6cd5\u4f11\u88ab\u5929\u7f57\u9634\u715e\u529f\u6240\u88ad\uff0c\u5fc3\u5934\u4e00\u51db\uff0c\u90a3\u4e00\u6293\u5c31\u5931\u4e86\u51c6\u5934\uff0c\u6c5f\u5357\u6253\u4e86\u4e00\u4e2a\u76d8\u65cb\uff0c\u8fc5\u5373\u7528 \u5929\u7f57\u6b65\u6cd5\u4ece\u4ed6\u7684\u638c\u5e95\u94bb\u8fc7\uff0c\u59ec\u6653\u98ce\u5927\u53eb\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u5feb\u8d70\u5427\uff01\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4e34\u5371\u5f03\u53cb\uff0c\u5c82 \u5927\u4e08\u592b\u6240\u4e3a\uff1f\u597d\uff0c\u6211\u4eca\u5929\u5c31\u820d\u4e86\u6027\u547d\uff0c\u4e0e\u8fd9\u79c3\u9a74\u62fc\u5566\uff01\u201d \u3000\u3000\u7afa\u6cd5\u4f11\u51b7\u7b11\u9053\uff1a\u201c\u51ed\u4f60\u8fd9\u4e2a\u5c0f\u5b50\uff0c\u4e5f\u914d\u4e0e\u6211\u62fc\u547d\uff1f\u201d\u4ed6\u53f3\u624b\u6325\u52a8\u8888\u88df\uff0c\u52a9\u7afa\u6cd5\u5170\u6218\u59ec\u6653 \u98ce\uff0c\u53ea\u4ee5\u4e00\u53ea\u5de6\u624b\u4e0e\u6c5f\u5357\u53ae\u6597\u3002 \u3000\u3000\u6c5f\u5357\u7684\u6b66\u529f\u867d\u7136\u4e0d\u5f31\uff0c\u6bd4\u8d77\u7afa\u6cd5\u4f11\u5374\u8fd8\u5dee\u5f97\u5f88\u8fdc\uff0c\u6597\u4e86\u8fd8\u4e0d\u5230\u5341\u62db\uff0c\u5c31\u6709\u70b9\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u59ec\u6653\u98ce\u5148\u540e\u4e2d\u4e86\u4e24\u638c\uff0c\u529f\u529b\u5927\u51cf\uff0c\u4ed6\u7684\u4fee\u7f57\u9634\u715e\u529f\u4e5f\u8fd8\u672a\u5230\u968f\u5fc3\u6240\u6b32\u4fbf\u80fd\u53d1\u51fa\u7684\u5883\u754c\uff0c \u56e0\u4e4b\u5f62\u52bf\u66f4\u4e3a\u6076\u52a3\uff0c\u5f53\u771f\u662f\u81ea\u8eab\u96be\u4fdd\uff0c\u8981\u987e\u6c5f\u5357\u4e5f\u987e\u4e0d\u53ca\u4e86\u3002\u8fd9\u4e5f\u662f\u4ed6\u7684\u5931\u7b56\uff0c\u8981\u662f\u4ed6\u4e00\u4e0a \u6765\u5c31\u8fd0\u7528\u4fee\u7f57\u9634\u715e\u529f\u7684\u8bdd\uff0c\u7eb5\u4e0d\u80fd\u80dc\uff0c\u4ea6\u4e0d\u81f4\u843d\u8d25\u3002\u5230\u4e86\u6b64\u9645\uff0c\u8981\u60f3\u8fd0\u7528\uff0c\u5df2\u662f\u4e0d\u80fd\u3002 \u3000\u3000\u6c5f\u5357\u63a5\u8fde\u65bd\u7528\u4e86\u51e0\u6b21\u72ec\u95e8\u70b9\u7a74\u624b\u6cd5\uff0c\u90fd\u6ca1\u6709\u89e6\u53ca\u654c\u4eba\u7684\u8eab\u4f53\uff0c\u7afa\u6cd5\u4f11\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u4f60\u8981 \u5356\u5f04\u4f60\u7684\u70b9\u7a74\u529f\u592b\u4e48\uff1f\u597d\uff0c\u5c31\u8ba9\u4f60\u8bd5\u8bd5\uff01\u201d\u9661\u5730\u5927\u559d\u4e00\u58f0\uff1a\u201c\u7740\uff01\u201d\u6b3a\u5230\u6c5f\u5357\u8eab\u524d\uff0c\u4e00\u628a\u5c31 \u5c06\u4ed6\u6293\u7740\uff0c\u6c5f\u5357\u9a88\u6307\u4e00\u6233\uff0c\u5982\u89e6\u6728\u77f3\uff0c\u539f\u6765\u7afa\u6cd5\u4f11\u5df2\u8fd0\u8d77\u4e86\u62a4\u4f53\u795e\u529f\u3002 \u3000\u3000\u7afa\u6cd5\u4f11\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u8fd8\u8981\u62fc\u547d\u4e48\uff1f\u201d\u6c5f\u5357\u53f3\u624b\u88ab\u4ed6\u6293\u7740\uff0c\u8fd8\u80fd\u6d3b\u52a8\uff0c\u660e\u77e5\u70b9\u4ed6\u7684\u7a74 \u9053\u4e5f\u4e0d\u4e2d\u7528\uff0c\u4f46\u4e00\u4e2a\u4eba\u5230\u4e86\u5371\u9669\u65f6\u5019\u603b\u8981\u6323\u624e\uff0c\u4ed6\u51fa\u4e8e\u672c\u80fd\u7684\u53c8\u4f7f\u51fa\u770b\u5bb6\u7684\u672c\u9886\u6765\uff0c\u518d\u4e00\u6307 \u5411\u654c\u4eba\u6233\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u4e00\u6233\uff0c\u7adf\u7136\u751f\u51fa\u5947\u8ff9\uff0c\u53ea\u542c\u5f97\u7afa\u6cd5\u4f11\u5927\u53eb\u4e00\u58f0\uff0c\u5982\u4e2d\u4e86\u96f7\u7535\u4e00\u822c\uff0c\u5fd9\u4e0d\u8fed\u7684\u4fbf\u5411 \u540e\u9000\u3002\u7afa\u6cd5\u5170\u53eb\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6c5f\u5357\u88ab\u7afa\u6cd5\u4f11\u6454\u8131\uff0c\u6b63\u597d\u6eda\u5230\u7afa\u6cd5\u5170 \u7684\u811a\u4e0b\uff0c\u7afa\u6cd5\u5170\u63d0\u8d77\u811a\u8981\u8e0f\u4ed6\uff0c\u6c5f\u5357\u4e00\u6307\u6233\u53bb\uff0c\u53c8\u6b63\u597d\u6233\u4e2d\u4ed6\u811a\u5fc3\u201c\u6d8c\u6cc9\u7a74\u201d\uff01 \u3000\u3000\u7afa\u6cd5\u5170\u7684\u811a\u8ddf\u597d\u4f3c\u7ed9\u5229\u9488\u523a\u4e86\u4e00\u822c\uff0c\u767b\u65f6\u5927\u53eb\u4e00\u58f0\uff0c\u6cea\u5982\u6cc9\u6d8c\uff01 \u3000\u3000\u5176\u5b9e\u7afa\u6cd5\u5170\u4e5f\u65e9\u5df2\u77e5\u9053\u4e86\u6c5f\u5357\u7684\u70b9\u7a74\u672c\u9886\uff0c\u4ed6\u4e3e\u8db3\u5411\u6c5f\u5357\u8e0f\u53bb\u4e4b\u65f6\uff0c\u8db3\u90e8\u7684\u7a74\u9053\u90fd\u5df2\u5c01 \u95ed\u4e86\u7684\uff0c\u4f46\u4ed6\u5374\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u6c5f\u5357\u8fd9\u822c\u529f\u529b\uff0c\u4ed6\u8fd9\u4e00\u6233\u7adf\u6bd4\u91cd\u624b\u6cd5\u70b9\u7a74\u8fd8\u8981\u5389\u5bb3\uff0c\u975e\u4f46\u7a74\u4f4d \u6ca1\u6709\u95ed\u4f4f\uff0c\u8fde\u8db3\u5c11\u9633\u7ecf\u8109\u4e5f\u53d7\u4e86\u635f\u4f24\uff01\u8fd8\u5e78\u4e8f\u6d8c\u6cc9\u7a74\u4e0d\u662f\u6b7b\u7a74\uff0c\u59ec\u6653\u98ce\u8eab\u624b\u4f55\u7b49\u77eb\u6377\uff0c\u5c31\u5728 \u7afa\u6cd5\u5170\u4e3e\u8db3\u8df5\u8e0f\u6c5f\u5357\u4e4b\u65f6\uff0c\u4ed6\u5df2\u4e00\u638c\u62cd\u4e86\u8fc7\u6765\uff0c\u8fd9\u4e00\u638c\u6b63\u4e2d\u7afa\u6cd5\u5170\u7684\u80cc\u5fc3\uff01 \u3000\u3000\u201c\u6d8c\u6cc9\u7a74\u201d\u867d\u7136\u4e0d\u662f\u6b7b\u7a74\uff0c\u4f46\u88ab\u654c\u4eba\u6233\u4e2d\uff0c\u4fbf\u8981\u6cea\u5982\u6cc9\u6d8c\uff0c\u5168\u8eab\u75b2\u8f6f\u65e0\u529b\u3002\u6240\u4ee5\u59ec\u6653\u98ce \u867d\u662f\u53d7\u4f24\u529b\u5f31\uff0c\u8fd9\u4e00\u638c\u4ecd\u6253\u5f97\u7afa\u6cd5\u5170\u54c7\u54c7\u5927\u53eb\uff0c\u63a5\u8fde\u6454\u4e86\u4e09\u4e2a\u7b4b\u6597\u624d\u7a33\u5f97\u4f4f\u8eab\u5f62\u3002 \u3000\u3000\u8fd9\u65f6\u7afa\u6cd5\u5170\u54ea\u91cc\u8fd8\u6562\u518d\u6218\uff1f\u4ed6\u7684\u540c\u95e8\u5144\u5f1f\u7afa\u6cd5\u4f11\u5148\u5df2\u9003\u4e86\uff0c\u7afa\u6cd5\u5170\u4e00\u722c\u8d77\u6765\uff0c\u4e5f\u6025\u6025\u5fd9 \u5fd9\u9003\u8dd1\uff0c\u4ed6\u8fd8\u751f\u6015\u59ec\u6653\u51e4\u8ffd\u6765\uff0c\u53ea\u6068\u5c11\u4e86\u4e00\u53cc\u817f\u3002 \u3000\u3000\u6c5f\u5357\u770b\u4ed6\u9003\u5f97\u5982\u6b64\u72fc\u72c8\uff0c\u81ea\u5df1\u8eab\u4e0a\u7684\u75bc\u75db\u5168\u90fd\u5fd8\u4e86\uff0c\u4ed6\u62c9\u7740\u59ec\u6653\u98ce\u7684\u624b\uff0c\u4e24\u4e2a\u4eba\u5c31\u54c8\u54c8 \u5927\u7b11\u8d77\u6765\uff01 \u3000\u3000\u59ec\u6653\u98ce\u7b11\u8fc7\u4e4b\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u771f\u7684\u591a\u4e8f\u4f60\u4e86\u3002\u8981\u4e0d\u662f\u4f60\u70b9\u4e2d\u4ed6\u7684\u6d8c\u6cc9\u7a74\uff0c\u6211\u51b3\u4e0d\u80fd \u5c06\u4ed6\u6253\u8d25\u3002\u201d\u59ec\u6653\u98ce\u867d\u7136\u5df2\u7ecf\u662f\u4e2a\u5927\u884c\u5bb6\uff0c\u4f46\u8fd0\u6c14\u95ed\u7a74\u90a3\u662f\u4ece\u5916\u9762\u770b\u4e0d\u51fa\u6765\u7684\uff0c\u56e0\u6b64\uff0c\u4ed6\u53ea \u770b\u5f97\u51fa\u662f\u7afa\u6cd5\u5170\u201c\u6d8c\u6cc9\u7a74\u201d\u88ab\u6c5f\u5357\u70b9\u4e2d\uff0c\u5c1a\u8fd8\u672a\u77e5\u662f\u7afa\u6cd5\u5170\u5728\u5c01\u95ed\u4e86\u7a74\u9053\u4e4b\u540e\uff0c\u4ecd\u7136\u7ed9\u6c5f\u5357 \u6240\u4f24\u7684\u3002 \u3000\u3000\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u65f6\u78b0\u5de7\uff0c\u4e5f\u662f\u4ed6\u592a\u8f7b\u654c\u4e4b\u6545\u3002\u54c8\u54c8\uff0c\u4ed6\u4e0d\u77e5\u9053\u6211\u7684\u70b9\u7a74\u6cd5\u662f\u91d1\u5927\u4fa0 \u6240\u4f20\uff0c\u7adf\u6562\u6beb\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u90a3\u662f\u4ed6\u6d3b\u8be5\u5012\u9709\uff01\u201d\u539f\u6765\u8fde\u6c5f\u5357\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u7afa\u6cd5\u5170\u5df2\u7ecf\u5c01\u95ed\u4e86 \u7a74\u9053\u3002 \u3000\u3000\u59ec\u6653\u98ce\u53c8\u9053\uff1a\u201c\u975e\u4f46\u662f\u4ed6\uff0c\u8fde\u6211\u4e5f\u60f3\u4e0d\u5230\u4f60\u7684\u529f\u529b\u7adf\u662f\u5982\u6b64\u7cbe\u7eaf\u3002\u7afa\u6cd5\u5170\u4e3e\u8db3\u8e0f\u4f60\uff0c\u78b0 \u5de7\u88ab\u4f60\u70b9\u4e2d\u7a74\u9053\uff0c\u8fd9\u4e5f\u7f62\u4e86\uff0c\u90a3\u7afa\u6cd5\u4f11\u7684\u672c\u9886\u5c1a\u5728\u7afa\u6cd5\u5170\u4e4b\u4e0a\uff0c\u4ed6\u5df2\u7ecf\u5c06\u4f60\u6293\u7740\uff0c\u5982\u4f55\u4e5f\u7ed9 \u4f60\u4f24\u4e86\u5462\uff1f\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u90a3\u4e5f\u662f\u4ed6\u592a\u8f7b\u654c\u4e4b\u6545\uff0c\u4ed6\u8bf4\u8981\u8bd5\u8bd5\u6211\u7684\u70b9\u7a74\u529f\u592b\uff0c\u4ed6\u6293\u7740\u6211\uff0c\u65e2\u4e0d\u65bd\u5c55\u5206\u7b4b \u9519\u9aa8\u7684\u624b\u6cd5\uff0c\u53c8\u4e0d\u4fe1\u624b\u70b9\u6211\u7684\u7a74\u9053\uff0c\u6211\u5f53\u7136\u4e0d\u548c\u4ed6\u5ba2\u6c14\u4e86\uff0c\u54c8\u54c8\uff0c\u4e00\u70b9\u5c31\u6b63\u4e2d\u4ed6\u80f8\u53e3\u7684\u7487\u7391 \u7a74\uff01\u201d\u6c5f\u5357\u7d20\u6765\u6b22\u559c\u8bf4\u81ea\u5df1\u5f97\u610f\u7684\u4e8b\uff0c\u4ed6\u70b9\u4e2d\u7afa\u6cd5\u4f11\u7684\u7487\u7391\u7a74\u90a3\u662f\u7b2c\u4e8c\u6b21\u624d\u6210\u529f\u7684\uff0c\u7b2c\u4e00\u6b21 \u70b9\u4e2d\u7afa\u6cd5\u4f11\u7684\u65f6\u5019\uff0c\u4ed6\u81ea\u5df1\u7684\u624b\u6307\u7ed9\u78b0\u5f97\u51e0\u4e4e\u6298\u65ad\u4e4b\u4e8b\uff0c\u4ed6\u5c31\u4e0d\u63d0\u4e86\u3002 \u3000\u3000\u59ec\u6653\u51e4\u867d\u89c9\u5f97\uff0c\u7afa\u6cd5\u4f11\u8fd9\u6837\u5bb9\u6613\u7684\u4fbf\u7ed9\u6c5f\u5357\u6253\u8dd1\uff0c\u672a\u514d\u592a\u8fc7\u51fa\u5947\uff0c\u4f46\u6c5f\u5357\u90a3\u756a\u89e3\u91ca\uff0c\u5374 \u4e5f\u7684\u786e\u5408\u60c5\u5408\u7406\uff0c\u4e0d\u7531\u4ed6\u4e0d\u76f8\u4fe1\u3002\u4ed6\u7ed9\u6c5f\u5357\u90a3\u526f\u6ed1\u7a3d\u7684\u795e\u6001\u9017\u5f97\u54c8\u54c8\u5927\u7b11\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f \u5144\u5f1f\uff0c\u4f60\u771f\u662f\u4e00\u5458\u798f\u5c06\uff0c\u6709\u4f60\u5728\u573a\uff0c\u4fbf\u9022\u51f6\u5316\u5409\uff0c\u9047\u96be\u6210\u7965\uff01\u6211\u8fd8\u62c5\u5fc3\u8fde\u7d2f\u4e86\u4f60\u5462\uff0c\u54ea\u77e5\u5374 \u53cd\u800c\u662f\u4f60\u6551\u4e86\u505a\u54e5\u54e5\u7684\u8fd9\u6761\u6027\u547d\uff0c\u54c8\uff0c\u4e5f\u771f\u60f3\u4e0d\u5230\uff0c\u90a3\u7afa\u6cd5\u4f11\u7adf\u4f1a\u5982\u6b64\u8f7b\u654c\uff0c\u4ee5\u81f4\u8d25\u5728\u4f60\u7684 \u624b\u4e0b\u3002\u5c0f\u5144\u5f1f\uff0c\u505a\u5927\u54e5\u7684\u8fd9\u53a2\u591a\u8c22\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u8fde\u5fd9\u8fd8\u793c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u8fd9\u6837\u8bf4\u5c31\u4e0d\u5bf9\u4e86\u3002\u82e5\u7136\u6709\u9152\u8089\u4fbf\u6765\uff0c\u6709\u60a3\u96be\u4fbf\u8dd1\uff0c\u8fd9\u6837\u7684 \u5144\u5f1f\u8fd8\u6210\u4ec0\u4e48\u8bdd\uff0c\u533a\u533a\u5c0f\u4e8b\uff0c\u4e5f\u8981\u4e00\u8c22\u518d\u8c22\uff0c\u90a3\u4e48\uff0c\u4f60\u7518\u613f\u5192\u91cd\u89c1\u5929\u9b54\u6559\u4e3b\u4e4b\u9669\uff0c\u53bb\u6551\u6211\u7684 \u513f\u5b50\uff0c\u6211\u53c8\u8be5\u5982\u4f55\u591a\u8c22\u4f60\u5462\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u771f\u662f\u4e2a\u723d\u5feb\u4eba\uff01\u53ef\u662f\u6211\u5374\u662f\u4e2a\u597d\u5403\u597d\u559d\u7684\u4eba\uff0c\u8981\u662f\u4ed7\u4f60 \u7684\u798f\u6c14\uff0c\u6b64\u53bb\u7ec4\u6765\u5c71\uff0c\u6551\u5f97\u6211\u7684\u4f84\u513f\u6765\uff0c\u6211\u8fd8\u8981\u548c\u4f60\u75db\u996e\u4e00\u573a\u3002\u201d \u3000\u3000\u6c5f\u5357\u4e00\u672c\u6b63\u7ecf\u4ed6\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\uff0c\u4f46\u4f60\u53ef\u5f97\u8ba9\u6211\u4f5c\u4e1c\uff0c\u5230\u65f6\u5019\uff0c\u4e5f\u4e0d\u4f1a\u518d\u529d\u4f60\u5c11\u559d \u4e86\uff01\u201d\u8bf4\u7f62\u624d\u54c8\u54c8\u7b11\u8d77\u6765\u3002 \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u73b0\u5728\u5c31\u5206\u624b\u5427\uff0c\u8bb0\u4f4f\uff0c\u5341\u5929\u4e4b\u540e\uff0c\u5728\u897f\u9762\u87cb\u9f99\u9547\u7b49\u5019\u6211\u7684\u6d88 \u606f\uff01\u201d \u3000\u3000\u6c5f\u5357\u6700\u521d\u4e0e\u59ec\u6653\u98ce\u7ed3\u62dc\u4e4b\u65f6\uff0c\u8fd8\u5e76\u4e0d\u662f\u5341\u5206\u8bda\u5fc3\u7684\uff0c\u4e0d\u8fc7\u4ed6\u751f\u6027\u968f\u548c\uff0c\u4e0d\u613f\u62c2\u9006\u59ec\u6653\u98ce \u4e4b\u610f\u800c\u5df2\u3002\u4f46\u5230\u4e86\u6b64\u65f6\uff0c\u5374\u548c\u59ec\u6653\u98ce\u7684\u795e\u60c5\u751a\u4e3a\u6295\u5408\uff0c\u751a\u81f3\u6bd4\u8d77\u4ed6\u7684\u4e49\u5144\u9648\u5929\u5b87\u6765\uff0c\u8fd8\u66f4\u6709 \u4e00\u79cd\u4eb2\u5207\u4e4b\u611f\u3002\u56e0\u6b64\u4ed6\u53cd\u800c\u6709\u70b9\u501f\u522b\u4f9d\u4f9d\u4e4b\u611f\u4e86\u3002\u5f53\u4e0b\uff0c\u95ee\u9053\uff1a\u201c\u5927\u54e5\u3001\u4f60\u8fd8\u6709\u4ec0\u4e48\u5631\u5490 \u5417\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u4e0a\u6c13\u5c71\u89c1\u5230\u4e86\u8c37\u4e4b\u534e\uff0c\u4f60\u987a\u4fbf\u66ff\u6211\u95ee\u5019\u5979\u5427\u3002\u201d \u4ed6\u521a\u521a\u8bf4\u5b8c\uff0c\u5ffd\u53c8\u6447\u4e86\u6447\u5934\uff0c\u4e0d\u5f85\u4ed6\u7b54\u5e94\uff0c\u4fbf\u53c8\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u8fd9\u662f\u4e0d\u5fc5\u4e86\uff01\u4e0d\u5fc5\u518d\u5728\u5979\u7684\u9762 \u524d\u63d0\u6211\u7684\u540d\u5b57\u4e86\uff01\u201d\u8a00\u4e0b\u795e\u8272\u9eef\u7136\u3002 \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u660e\u767d\u4f60\u7684\u5fc3\u610f\u3002\u4f46\u6211\u4e5f\u660e\u767d\u8c37\u5973\u4fa0\u7684\u4e3a\u4eba\uff0c\u6211\u4e00\u5b9a\u8981\u5c06\u4f60\u6539\u90aa\u5f52\u6b63 \u7684\u4e8b\u60c5\u548c\u4f60\u7684\u5fc3\u613f\u8bf4\u7ed9\u5979\u542c\u3002\u6211\u76f8\u4fe1\u5979\u77e5\u9053\u5979\u6709\u8fd9\u6837\u4e00\u4e2a\u5e08\u5144\uff0c\u7edd\u4e0d\u4f1a\u611f\u5230\u7f9e\u803b\uff0c\u800c\u662f\u611f\u5230 \u9a84\u50b2\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u8138\u4e0a\u9732\u51fa\u7b11\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u77e5\u9053\u7981\u6b62\u4f60\u8bf4\u8bdd\uff0c\u6bd4\u4e0d\u6211\u559d\u9152\u8fd8\u96be\u3002\u201d\u4f60\u8981\u8bf4 \u90a3\u4e5f\u968f\u4f60\u5427\uff01\u201d \u3000\u3000\u4e24\u4eba\u5206\u624b\u4e4b\u540e\uff0c\u6c5f\u5357\u56de\u60f3\u8d77\u521a\u624d\u53cd\u8d25\u4e3a\u80dc\uff0c\u5c06\u90a3\u4e24\u4e2a\u756a\u50e7\u6253\u7684\u72fc\u72c8\u800c\u9003\u7684\u60c5\u5f62\uff0c\u8fd8\u662f\u5341 \u5206\u5f97\u610f\uff0c\u5c31\u50cf\u5c0f\u5b69\u5b50\u56de\u5473\u4ec0\u4e48\u503c\u5f97\u5938\u8000\u7684\u4e8b\u60c5\u4e00\u6837\u3002\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u59ec\u5927\u54e5\uff0c\u4f60\u7ed3\u8bc6\u7684\u8fd9\u4e2a \u5144\u5f1f\u867d\u7136\u672c\u4e8b\u4e0d\u6d4e\uff0c\u5374\u4e5f\u8fd8\u6709\u70b9\u7528\u5904\u3002\u201d\u4ed6\u81ea\u8a00\u81ea\u8bed\u4e86\u4e00\u4f1a\u513f\uff0c\u5ffd\u5730\u5fc3\u4e2d\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4e0d \u5bf9\uff01\u201d \u3000\u3000\u4f60\u9053\u6c5f\u5357\u4f55\u4ee5\u4f1a\u53d1\u73b0\u4e0d\u5bf9\uff0c\u539f\u6765\u4ed6\u867d\u7136\u6b22\u559c\u5439\u725b\uff0c\u5374\u4e5f\u6709\u7ec6\u77e5\u4e4b\u660e\u3002\u4ed6\u5728\u5f97\u610f\u5fd8\u5f62\u4e4b \u540e\uff0c\u7a81\u7136\u60f3\u8d77\u4e86\u59ec\u6653\u98ce\u548c\u4ed6\u8bf4\u8bdd\u65f6\uff0c\u8138\u4e0a\u7684\u7591\u60d1\u7684\u795e\u60c5\uff0c\u548c\u59ec\u6653\u98ce\u7684\u90a3\u4e00\u53e5\u8bdd\uff1a\u201c\u771f\u60f3\u4e0d\u5230 \u90a3\u7afa\u6cd5\u4f11\u7adf\u4f1a\u5982\u6b64\u8f7b\u654c\uff0c\u4ee5\u81f4\u8d25\u5728\u4f60\u7684\u624b\u4e0b\uff01\u201d\u4ed6\u4e0d\u7981\u5728\u5fc3\u91cc\u81ea\u5df1\u6697\u95ee\u81ea\u5df1\uff0c\u201c\u6211\u4e4b\u6240\u4ee5\u5f97 \u80dc\uff0c\u5f53\u771f\u662f\u5b8c\u5168\u7531\u4e8e\u5bf9\u65b9\u7684\u8f7b\u654c\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u5357\u5728\u6b66\u5b66\u4e0a\u4e5f\u5df2\u6709\u4e86\u76f8\u5f53\u9020\u8be3\uff0c\u4ed6\u4e00\u51b7\u9759\u4e0b\u6765\uff0c\u7acb\u5373\u4fbf\u53d1\u89c9\u8bb8\u591a\u7591\u7aa6\uff0c\u4ed6\u60f3\u8d77\u4e86\u81ea\u5df1 \u7ed9\u90a3\u7afa\u6cd5\u4f11\u6293\u7740\u4e4b\u65f6\uff0c\u534a\u8fb9\u8eab\u5b50\u9ebb\u8f6f\uff0c\u5df2\u662f\u4ec5\u80fd\u4f7f\u51fa\u4e94\u5206\u771f\u529b\uff1b\u4ed6\u60f3\u8d77\u81ea\u5df1\u51fa\u624b\u70b9\u90a3\u7afa\u6cd5\u4f11 \u7684\u7a74\u9053\uff0c\u7b2c\u4e00\u6b21\u70b9\u7a74\u7684\u60c5\u5f62\u548c\u7b2c\u4e8c\u6b21\u5927\u5927\u4e0d\u540c\uff0c\u7b2c\u4e00\u6b21\u5982\u89e6\u6728\u77f3\uff0c\u7b2c\u4e8c\u6b21\u5374\u4f3c\u89e6\u53ca\u4e00\u56e2\u70c2\u6ce5\u3002 \u3000\u3000\u4ed6\u53c8\u60f3\u8d77\u4e86\u540e\u6765\u70b9\u90a3\u7afa\u6cd5\u5170\u7684\u201c\u6d8c\u6cc9\u7a74\u201d\u7684\u65f6\u5019\uff0c\u4ed6\u660e\u660e\u662f\u611f\u89c9\u5230\u654c\u4eba\u662f\u5df2\u7ecf\u95ed\u4e86\u7a74\u9053 \u7684\uff0c\u4ed6\u53c8\u4e0d\u7981\u81ea\u5df1\u6697\u95ee\u81ea\u5df1\u9053\uff1a\u201c\u90a3\u7afa\u6cd5\u5170\u7684\u529f\u529b\u548c\u59ec\u5927\u54e5\u65d7\u9f13\u76f8\u5f53\uff0c\u600e\u7684\u4ed6\u95ed\u4e86\u7a74\u9053\uff0c\u4e5f \u4f1a\u4e3a\u6211\u6240\u4f24\uff1f\u54ce\u5440\uff0c\u6211\u53ea\u77e5\u9053\u81ea\u5df1\u5938\u8000\uff0c\u521a\u624d\u5374\u5fd8\u8bb0\u544a\u8bc9\u59ec\u5927\u54e5\u4e86\uff01\u7afa\u6cd5\u5170\u95ed\u4e86\u7a74\u9053\uff0c\u59ec\u5927 \u54e5\u662f\u7b2c\u4e09\u8005\uff0c\u5f53\u7136\u4e0d\u77e5\uff0c\u4f46\u6211\u5374\u662f\u611f\u89c9\u5f97\u5230\u7684\uff01\u201d \u3000\u3000\u6c5f\u5357\u53d1\u89c9\u4e86\u8fd9\u8bb8\u591a\u7591\u7aa6\u4e4b\u540e\uff0c\u7acb\u5373\u8054\u60f3\u8d77\u5341\u5e74\u524d\u7684\u4e00\u4ef6\u5f80\u4e8b\u3002\u90a3\u4e00\u5e74\u4ed6\u548c\u90b9\u7edb\u971e\u5c1a\u672a\u8ba2 \u5a5a\uff0c\u6709\u4e00\u6b21\u8def\u8fc7\u5979\u5bb6\uff0c\u6070\u5de7\u9047\u7740\u8bb8\u591a\u5389\u5bb3\u7684\u90aa\u6d3e\u4eba\u7269\uff0c\u5411\u90b9\u7edb\u971e\u7684\u6bcd\u4eb2\u6768\u67f3\u9752\u5bfb\u4ec7\uff0c\u4ed6\u8def\u89c1 \u4e0d\u5e73\uff0c\u62d4\u5200\u76f8\u52a9\uff0c\u7ed3\u679c\u8fde\u80dc\u51e0\u573a\uff0c\u66ff\u6768\u67f3\u9752\u89e3\u4e86\u5371\u96be\u3002\u6768\u67f3\u9752\u672c\u6765\u4e0d\u5927\u770b\u5f97\u8d77\u6c5f\u5357\uff0c\u4e5f\u662f\u7ecf \u8fc7\u4e86\u90a3\u4e00\u6b21\u4e4b\u540e\uff0c\u624d\u5bf9\u6c5f\u5357\u53e6\u773c\u76f8\u770b\uff0c\u613f\u610f\u628a\u5973\u513f\u8bb8\u914d\u4ed6\u7684\uff0c\u6c5f\u5357\u5f53\u65f6\u4e5f\u662f\u8d62\u5f97\u83ab\u540d\u5176\u5999\uff0c \u540e\u6765\u624d\u77e5\u9053\u662f\u7531\u4e8e\u91d1\u4e16\u9057\u7684\u6697\u52a9\u3002\uff08\u4e8b\u8be6\u300a\u4e91\u6d77\u7389\u5f13\u7f18\u300b\uff09 \u3000\u3000\u6c5f\u5357\u6709\u65f6\u867d\u7136\u50bb\u91cc\u50bb\u6c14\uff0c\u4eba\u53ef\u5e76\u4e0d\u7cca\u6d82\uff0c\u4ed6\u60f3\u81f3\u6b64\u5904\uff0c\u7acb\u5373\u604d\u7136\u5927\u609f\uff0c\u72c2\u559c\u53eb\u9053\u3002\u201c\u91d1 \u5927\u4fa0\uff0c\u6211\u6c5f\u5357\u627e\u5f97\u4f60\u597d\u82e6\uff0c\u539f\u6765\u4f60\u5c31\u5728\u8fd9\u91cc\uff0c\u4e0d\u8981\u518d\u548c\u6211\u5f00\u73a9\u7b11\u4e86\uff0c\u5feb\u5feb\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u539f\u91ce\u4e0a\u56db\u987e\u65e0\u4eba\uff0c\u4e00\u7247\u5bc2\u9759\uff0c\u53ea\u6709\u6c5f\u5357\u7684\u56de\u58f0\u3002\u6c5f\u5357\u8fde\u53eb\u4e86\u51e0\u904d\uff0c\u90fd\u4e0d\u89c1\u91d1\u4e16\u9057\u73b0\u5f62\u3002 \u6c5f\u5357\u5927\u4e3a\u7740\u6025\uff0c\u53c8\u518d\u53eb\u9053\uff1a\u201c\u5582\uff0c\u5582\uff0c\u4e0d\u5355\u662f\u6211\uff0c\u90a3\u795e\u5077\u59ec\u6653\u98ce\u4e5f\u5728\u627e\u4f60\u5462\uff01\u4f60\u8981\u4e0d\u8981\u542c\u4ed6 \u7684\u6545\u4e8b\uff1f\u201d\u4ecd\u7136\u4e0d\u89c1\u6709\u4eba\u56de\u7b54\u3002\u6c5f\u5357\u5fc3\u9053\uff1a\u201c\u96be\u9053\u6697\u52a9\u6211\u7684\u4eba\u4e0d\u662f\u91d1\u5927\u4fa0\uff1f\u55ef\uff0c\u90a3\u7edd\u4e0d\u53ef\u80fd \u4e0d\u662f\u4ed6\uff0c\u9664\u4e86\u4ed6\u8fd8\u6709\u8c01\u6709\u8fd9\u6837\u672c\u9886\uff1f\u201d \u3000\u3000\u6c5f\u5357\u77e5\u9053\u91d1\u4e16\u9057\u7684\u813e\u6c14\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u4e00\u91d1\u5927\u4fa0\u4ed6\u4e0d\u80af\u8fd9\u4e2a\u65f6\u5019\u89c1\u6211\uff0c\u60f3\u5fc5\u53e6\u6709\u539f\u6545\u3002\u6211 \u5230\u4e86\u6c13\u5c71\uff1b\u5fc5\u7136\u53ef\u4ee5\u89c1\u5f97\u7740\u4ed6\uff0c\u5509\uff0c\u53ef\u60dc\u59ec\u5927\u54e5\u5df2\u7ecf\u8d70\u8fdc\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5357\u867d\u7136\u6709\u70b9\u4e3a\u59ec\u6653\u51e4\u60cb\u60dc\uff0c\u4f46\u60f3\u5230\u81ea\u5df1\u4e0d\u4e45\u5c31\u53ef\u4ee5\u89c1\u7740\u91d1\u4e16\u9057\uff0c\u5fc3\u4e2d\u65e0\u9650\u5174\u594b\uff0c\u5f53\u4e0b \u4fbf\u517c\u7a0b\u8d76\u8def\uff0c\u524d\u5f80\u6c13\u5c71\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e2d\u5348\u65f6\u5206\uff0c\u6c5f\u5357\u4fbf\u5230\u4e86\u6c13\u5c71\u811a\u4e0b\u3002\u5c71\u811a\u6709\u4e00\u5ea7\u51c9\u4ead\uff0c\u6811\u9634\u6df1\u5904\uff0c\u51c9\u4ead\u91cc\u6709\u4e2a\u5356\u8336 \u7684\u8001\u4eba\uff0c\u89c1\u6c5f\u5357\u6ee1\u5934\u5927\u6c57\uff0c\u4fbf\u62db\u624b\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u4f60\u8d70\u5f97\u7d2f\u4e86\uff0c\u8fdb\u6765\u6b47\u6b47\u5427\u3002\u201d \u3000\u3000\u6c5f\u5357\u6b63\u81ea\u611f\u5230\u8179\u808c\u53e3\u6e34\uff0c\u8d70\u8fdb\u53bb\u95ee\u9053\uff1a\u201c\u53ef\u6709\u9152\u83dc\u5356\u4e48\uff1f\u201d\u90a3\u8001\u4eba\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u4e0d\u6bd4\u8def \u8fb9\u7684\u8336\u4ead\uff0c\u54ea\u6709\u4ec0\u4e48\u751f\u610f\u3002\u5c0f\u8001\u513f\u4e5f\u4e0d\u662f\u505a\u4e70\u5356\u7684\uff0c\u53ea\u56e0\u8fd9\u51e0\u5929\u662f\u5c71\u4e0a\u836f\u738b\u5e99\u7684\u9999\u671f\uff0c\u4e0a\u5c71 \u7684\u4eba\u591a\u591a\u4e9b\uff0c\u5c0f\u8001\u513f\u95f2\u7740\u6ca1\u4e8b\uff0c\u5c31\u5728\u8fd9\u91cc\u70e7\u8336\uff0c\u597d\u7ed9\u4e0a\u5c71\u7684\u9999\u5ba2\u89e3\u6e34\uff0c\u4efb\u968f\u5ba2\u4eba\u9ad8\u5174\uff0c\u65bd\u820d \u51e0\u6587\u3002\u201d \u3000\u3000\u6c5f\u5357\u89c1\u90a3\u8001\u4eba\u548c\u853c\u53ef\u4eb2\uff0c\u5750\u4e0b\u6765\u4fbf\u548c\u4ed6\u4e1c\u62c9\u897f\u626f\u7684\u95f2\u804a\uff0c\u95ee\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u5c71\u4e0a\u6709\u5ea7\u7384 \u5973\u89c2\uff0c\u5374\u539f\u6765\u8fd8\u6709\u4e00\u5ea7\u836f\u738b\u5e99\uff0c\u5012\u6ca1\u542c\u4eba\u8bf4\u8fc7\u3002\u201d\u90a3\u8001\u5934\u513f\u9053\uff1a\u201c\u8fd9\u836f\u738b\u5e99\u53ea\u662f\u4e00\u5ea7\u7834\u7834\u70c2 \u70c2\u7684\u5c0f\u5e99\uff0c\u5373\u662f\u5c71\u4e2d\u7684\u836f\u519c\u548c\u730e\u6237\u4f9b\u5949\u7684\uff0c\u8fd1\u5e74\u6765\u624d\u7a0d\u7a0d\u6539\u4ee5\u88c5\u4fee\uff0c\u600e\u6bd4\u5f97\u4e0a\u7384\u5973\u89c2\u3002\u4e0d \u8fc7\uff0c\u8fd9\u836f\u738b\u5e99\u548c\u7384\u5973\u89c2\u5374\u4e5f\u6709\u4e9b\u5173\u7cfb\u3002\u201d \u3000\u3000\u6c5f\u5357\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d\u90a3\u8001\u5934\u513f\u9053\uff1a\u201c\u73b0\u5728\u5728\u5e99\u91cc\u4f4f\u7684\u5c3c\u59d1\u672c\u6765\u662f\u7384\u5973\u89c2\u7684\uff0c\u7384\u5973 \u89c2\u6cbb\u4f24\u7684\u836f\u5f88\u7075\uff0c\u4ee5\u524d\u5728\u5c71\u91cc\u4f4f\u7684\u91ce\u517d\u54ac\u4f24\u4e5f\u5e38\u5230\u7384\u5973\u89c2\u53bb\u6c42\u836f\uff0c\u4f46\u7384\u5973\u89c2\u5728\u6c13\u5c71\u5c71\u9876\uff0c\u4e0a \u53bb\u4e0d\u65b9\u4fbf\u3002\u6240\u4ee5\uff0c\u7384\u5973\u89c2\u6d3e\u4e86\u4e24\u4e2a\u5c3c\u59d1\uff0c\u5c31\u7d22\u6027\u4f4f\u5728\u836f\u738b\u5e99\u91cc\uff0c\u6211\u4eec\u4e5f\u628a\u5979\u4eec\u5f53\u6210\u836f\u738b\u5e99\u7684 \u4e3b\u6301\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u836f\u738b\u7237\u7237\u662f\u4e2a\u7537\u7684\uff0c\u600e\u597d\u7528\u5c3c\u59d1\u4e3b\u6301\uff1f\u201d\u90a3\u8001\u4eba\u4e5f\u7b11\u9053\uff1a\u201c\u53c8\u4e0d\u662f\u4ec0\u4e48\u5e84 \u4e25\u53e4\u5239\u3001\u5341\u65b9\u4e1b\u6797\uff0c\u6211\u4eec\u53ea\u95ee\u836f\u7075\u4e0d\u7075\uff0c\u7ba1\u4ed6\u662f\u7537\u662f\u5973\uff1f\u201d \u3000\u3000\u6c5f\u5357\u559d\u4e86\u4e00\u7897\u8336\uff0c\u6b63\u60f3\u89e3\u5f00\u5e72\u7cae\u888b\uff0c\u90a3\u8001\u5934\u513f\u5ffd\u9053\uff1a\u201c\u6211\u6709\u8bdd\u5728\u5148\uff0c\u4e0d\u8981\u94b1\u7684\uff0c\u5c0f\u54e5 \u513f\uff0c\u4f60\u4e0d\u5fc5\u5ba2\u6c14\uff01\u201d \u3000\u3000\u6c5f\u5357\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u4fbf\u660e\u767d\u4e86\u4ed6\u8fd9\u6837\u8bf4\u8bdd\u7684\u539f\u56e0\uff0c\u539f\u6765\u6c5f\u5357\u4e3a\u4e86\u6025\u4e8e\u8d76\u8def\uff0c\u661f\u591c\u5954\u9a70\uff0c \u6628\u665a\u6839\u672c\u5c31\u4e0d\u662f\u5728\u5ba2\u5e97\u91cc\u4f4f\u7684\uff0c\u800c\u662f\u8dd1\u5f97\u5026\u4e86\uff0c\u5c31\u968f\u4fbf\u5728\u5730\u4e0a\u8eba\u4e00\u4f1a\uff0c\u56e0\u6b64\u4ed6\u7684\u8863\u88f3\u5934\u53d1\uff0c \u90fd\u6ee1\u662f\u6ce5\u788e\u8349\u5c51\uff0c\u800c\u4e14\u4ed6\u662f\u5728\u548c\u90a3\u4e24\u4e2a\u756a\u618e\u5927\u6253\u4e00\u573a\u4e4b\u540e\uff0c\u4fbf\u7acb\u5373\u8d76\u8def\u7684\uff0c\u8eab\u4e0a\u7a7f\u7684\u8863\u88f3\u4e5f \u8fd8\u672a\u6362\uff0c\u7537p\u4ef6\u8863\u88f3\u65e9\u5df2\u88ab\u7afa\u6cd5\u4f11\u6495\u5f97\u7834\u7834\u70c2\u70c2\u4e86\u3002\u6c5f\u5357\u5fc3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u4ed6\u4e00\u5b9a\u662f\u89c1\u6211\u8fd9\u4e2a \u6a21\u6837\uff0c\u56e0\u6b64\u4ee5\u4e3a\u6211\u662f\u4e2a\u7a77\u5149\u86cb\u4e86\u3002\u201d \u3000\u3000\u90a3\u8001\u4eba\u6539\u53e3\u79f0\u6c5f\u5357\u4e3a\u201c\u5c0f\u54e5\u513f\u201d\uff0c\u6c5f\u5357\u8d8a\u53d1\u89c9\u5f97\u4eb2\u5207\uff0c\u5f53\u4e0b\u4e5f\u4e0d\u8bf4\u660e\u771f\u76f8\uff0c\u63a5\u8fc7\u4ed6\u7684\u7092 \u7c73\u997c\u4fbf\u5927\u56bc\u7279\u56bc\uff0c\u7b11\u9053\uff1a\u201c\u8001\u516c\u516c\uff0c\u4f60\u7684\u5fc3\u5730\u771f\u597d\uff0c\u975e\u4f46\u4e0d\u8981\u6211\u65bd\u820d\u8336\u94b1\uff0c\u53cd\u800c\u5c06\u7092\u7c73\u997c\u65bd \u820d\u7ed9\u6211\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u89c1\u4e00\u4e58\u5c0f\u8f7f\uff0c\u62ac\u5230\u4e86\u51c9\u4ead\u524d\u9762\uff0c\u90a3\u4e58\u5c0f\u8f7f\u867d\u7136\u53ea\u662f\u4e24\u4eba\u62ac\u7684\u548c\u5b98\u5e9c\u5750\u7684\u516b \u4eba\u5927\u8f7f\u4e0d\u80fd\u76f8\u6bd4\uff0c\u4f46\u5374\u662f\u4e00\u9876\u7c07\u65b0\u7684\u63cf\u91d1\u523b\u82b1\u5c0f\u8f7f\uff0c\u534e\u8d35\u975e\u51e1\uff0c\u6c5f\u5357\u548c\u90a3\u8001\u4eba\u90fd\u751a\u4e3a\u60ca\u5f02\uff0c \u90a3\u9876\u5c0f\u8f7f\u505c\u4e86\u4e0b\u6765\u4e4b\u540e\uff0c\u53ea\u89c1\u4e00\u4e2a\u73e0\u5149\u5b9d\u6c14\u7684\u5987\u4eba\u8d70\u4e86\u51fa\u6765\uff0c\u90a3\u51c9\u4ead\u7684\u8001\u4eba\u7741\u5927\u4e86\u773c\u775b\uff0c\u770b \u5f97\u5446\u4e86\uff0c\u7adf\u7136\u5fd8\u8bb0\u4e86\u62db\u547c\uff01 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u7f55\u89c1\u8352\u5c71\u6765\u8d35\u5987\uff0c\u73e0\u5149\u5b9d\u6c14\u60f9\u7591\u731c\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000--------------- \u3000\u3000\u626c\u5251\u8f69\u5c45\u58eb \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u516d\u56de\u3000\u60c5\u5929\u62a5\u6068\u5e7d\u5170\u6028\u3000\u5996\u6c14\u5f25\u7a7a\u8d35\u5987\u6765 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u516d\u56de\u3000\u60c5\u5929\u62a5\u6068\u5e7d\u5170\u6028\u3000\u5996\u6c14\u5f25\u7a7a\u8d35\u5987\u6765 \u3000\u3000\u770b\u8fd9\u5987\u4eba\u7684\u8eab\u4efd\uff0c\u5f53\u662f\u5b98\u5bb6\u7684\u7737\u5c5e\u65e0\u7591\u3002\u5728\u6c13\u5c71\u5c71\u811a\u8fd9\u6837\u8352\u50fb\u7684\u5730\u65b9\uff0c\u6709\u4e00\u4e2a\u5b98\u592a\u592a\u5149 \u4e34\uff0c\u8fd9\u662f\u4ece\u6240\u672a\u6709\u4e4b\u4e8b\u3002\u90a3\u8001\u5934\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u96be\u9053\u4e5f\u662f\u6765\u8fdb\u9999\u7684\uff1f\u4f46\u90a3\u7834\u5e99\u4f9b\u5949\u7684\u836f\u738b\uff0c\u53ea \u6709\u5c71\u4e2d\u7684\u836f\u519c\u548c\u730e\u6237\u624d\u4f1a\u6765\u4e0a\u9999\u8bb8\u613f\uff0c\u600e\u7684\u4f1a\u60ca\u52a8\u8d77\u5b98\u592a\u592a\u6765\u4e86\uff1f\u800c\u4e14\u4e5f\u51b3\u6ca1\u6709\u5750\u8fd9\u6837\u7684\u8f7f \u5b50\u6765\u4e0a\u5c71\u8fdb\u9999\u7684\u9053\u7406\u3002 \u3000\u3000\u8981\u77e5\u5c71\u8def\u9669\u5cfb\uff0c\u62ac\u8d77\u8f7f\u5b50\uff0c\u66f4\u662f\u96be\u884c\uff0c\u5e73\u5e38\u5373\u7b97\u6709\u4e9b\u4e0a\u5c71\u6e38\u73a9\u7684\u4eba\uff0c\u8981\u5750\u8f7f\u5b50\uff0c\u6700\u591a\u4e5f \u662f\u5750\u7528\u7af9\u5b50\u7f16\u6210\u7684\u8f7b\u4fbf\u7684\u201c\u8fc7\u5c71\u515c\u201d\uff0c\u50cf\u8fd9\u6837\u534e\u7f8e\u7684\u8f7f\u5b50\uff0c\u8336\u4ead\u8001\u4eba\u6d3b\u4e86\u8fd9\u4e00\u5927\u628a\u5e74\u7eaa\uff0c\u8fd8 \u662f\u7b2c\u4e00\u6b21\u89c1\u5230\uff0c\u66f4\u52a0\u4e0a\u8fd9\u6837\u4e00\u4f4d\u73e0\u5149\u5b9d\u6c14\u7684\u8d35\u5987\u4eba\uff0c\u90a3\u5c31\u65e0\u602a\u4ed6\u5927\u4e3a\u60ca\u8be7\u4e86\uff01 \u3000\u3000\u90a3\u4e24\u4e2a\u8f7f\u592b\u653e\u4e0b\u8f7f\u5b50\u4fbf\u5927\u58f0\u53f1\u559d\u9053\uff1a\u201c\u7cdf\u8001\u5934\u513f\uff0c\u4f60\u5413\u50bb\u4e86\u4e48\uff1f\u592b\u4eba\u6765\u5230\uff0c\u8fd8\u4e0d\u8d76\u5feb\u62db \u547c\uff01\u201d\u8336\u4ead\u8001\u4eba\u5fd9\u9053\uff1a\u201c\u662f\u3001\u662f\u3001\u662f\uff01\u201d\u8d76\u7d27\u4fbf\u53bb\u5012\u8336\u3002 \u3000\u3000\u90a3\u5b98\u592a\u592a\u8f7b\u79fb\u83b2\u6b65\uff0c\u8d70\u8fdb\u51c9\u4ead\uff0c\u6c5f\u5357\u597d\u5947\u5fc3\u8d77\uff0c\u5f53\u7136\u4e5f\u514d\u4e0d\u4e86\u6ce8\u89c6\u5979\uff0c\u54ea\u77e5\u4e00\u770b\u4e4b\u4e0b\uff0c \u5374\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff01 \u3000\u3000\u6c5f\u5357\u5e76\u4e0d\u662f\u60ca\u5947\u4e8e\u5979\u4f69\u6234\u7684\u73cd\u5b9d\u9996\u9970\uff0c\u800c\u662f\u90a3\u5b98\u592a\u5927\u7684\u7709\u5fc3\u4e4b\u95f4\uff0c\u6709\u4e00\u56e2\u9690\u7ea6\u53ef\u8fa8\u7684\u6de1 \u6de1\u9ed1\u6c14\uff01 \u3000\u3000\u6c5f\u5357\u66fe\u542c\u91d1\u4e16\u9057\u8bf4\u8fc7\uff0c\u7709\u5fc3\u4e4b\u95f4\u6709\u8fd9\u6837\u9ed1\u6c14\u7684\u4eba\uff0c\u5fc5\u5b9a\u662f\u7ec3\u8fc7\u4e00\u79cd\u6781\u5389\u5bb3\u7684\u90aa\u6d3e\u9634\u6bd2\u6b66 \u529f\uff0c\u529f\u592b\u7ec3\u5f97\u8d8a\u6df1\uff0c\u9ed1\u6c14\u8d8a\u6de1\u3002\u7ec3\u5230\u4e86\u6700\u9ad8\u6df1\u7684\u5883\u754c\uff0c\u9ed1\u6c14\u5c31\u975e\u666e\u901a\u7684\u8089\u773c\u6240\u80fd\u770b\u89c1\uff0c\u800c\u8981 \u6b66\u5b66\u7684\u5927\u884c\u5bb6\u624d\u770b\u5f97\u51fa\u6765\u4e86\u3002\u73b0\u5728\u4ece\u8fd9\u4f4d\u5b98\u592a\u5927\u90a3\u56e2\u6de1\u6de1\u7684\u9ed1\u6c14\u770b\u6765\uff0c\u6c5f\u5357\u867d\u7136\u770b\u4e0d\u51fa\u5979\u7ec3 \u7684\u662f\u54ea\u4e00\u79cd\u9634\u6bd2\u6b66\u529f\uff0c\u4f46\u6700\u5c11\u4e5f\u5df2\u6709\u4e86\u4e03\u516b\u6210\u706b\u5019\u3002 \u3000\u3000\u6c5f\u5357\u60ca\u7591\u4e0d\u5b9a\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u96be\u9053\u8fd9\u4e2a\u5b98\u5927\u592a\u7adf\u662f\u90aa\u6d3e\u4e2d\u7684\u4e00\u4e2a\u5389\u5bb3\u4eba\u7269\uff1f\u8fd9\u5b9e\u5728\u96be\u4ee5 \u4ee4\u4eba\u76f8\u4fe1\uff01\u54c8\uff0c\u83ab\u975e\u5979\u662f\u60a3\u4e86\u9690\u75be\uff0c\u542c\u8bf4\u60a3\u4e86\u9690\u75be\u7684\u4eba\uff0c\u6709\u65f6\u7709\u5fc3\u4e0a\u4e5f\u4f1a\u5448\u73b0\u9ed1\u6c14\u7684\u3002\u201d \u3000\u3000\u6c5f\u5357\u6b63\u81ea\u6c89\u541f\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u559d\u9053\uff1a\u201c\u81ed\u6ce5\u817f\u5b50\uff0c\u6eda\u5f00\uff01\u201d\u539f\u6765\u662f\u90a3\u4e24\u4e2a\u8f7f\u592b\u8fc7\u6765\u8d76\u4ed6\uff0c \u6c5f\u5357\u6c14\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u559d\u8336\uff0c\u788d\u4e86\u4f60\u4eec\u4ec0\u4e48\u4e86\uff1f\u201d\u90a3\u8f7f\u592b\u559d\u9053\uff1a\u201c\u591a\u5634\uff0c\u6253\u4f60\u5634\u5df4\uff01\u201c\u58f0\u51fa \u638c\u53d1\uff0c\u679c\u7136\u4fbf\u4e00\u638c\u6253\u4e86\u8fc7\u6765\uff01 \u3000\u3000\u6c5f\u5357\u6012\u9053\uff1a\u201c\u8fd8\u672a\u89c1\u8fc7\u4f60\u8fd9\u6837\u6a2a\u86ee\u7684\u4eba\uff01\u201d\u4f46\u4ed6\u867d\u7136\u53d1\u6012\uff0c\u5374\u4e0d\u613f\u610f\u65e0\u7aef\u7aef\u7684\u548c\u8f7f\u592b\u6253 \u67b6\uff0c\u5f53\u4e0b\u8fc5\u5373\u7528\u5929\u7f57\u6b65\u6cd5\u4e00\u95ea\u95ea\u5f00\uff0c\u4f46\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u8f7f\u592b\u4e00\u638c\u51fb\u4e2d\u4e86\u6c5f\u5357\u6240\u5750\u7684\u53f3 \u51f3\uff0c\u7adf\u6253\u5f97\u77f3\u5c51\u7eb7\u98de\u3001\u663e\u7136\u662f\u5206\u725b\u638c\u7834\u7891\u624b\u8fd9\u7c7b\u6781\u4e3a\u521a\u5f3a\u7684\u638c\u529b\uff01 \u3000\u3000\u6c5f\u5357\u7981\u4e0d\u4f4f\u5fc3\u5934\u4e00\u51db\uff0c\u4ed6\u4e0d\u662f\u60e7\u6015\u90a3\u4e2a\u8f7f\u592b\uff0c\u90a3\u8f7f\u592b\u7684\u638c\u529b\u867d\u7136\u521a\u731b\uff0c\u5374\u4e5f\u8fd8\u672a\u5fc5\u80dc\u5f97 \u8fc7\u4ed6\u3002\u53ea\u662f\u8fd9\u8f7f\u592b\u5df2\u7136\u6709\u8fd9\u6837\u80fd\u4e3a\uff0c\u90a3\u5987\u4eba\u7684\u672c\u9886\u5c31\u66f4\u53ef\u60f3\u800c\u77e5\u3002\u8f7f\u592b\u8fd9\u4e00\u638c\u4e0d\u557b\u8bc1\u5b9e\u4e86\u6c5f \u5357\u7684\u63a8\u6d4b\uff1a\u8fd9\u5987\u4eba\u5fc5\u5b9a\u4e0d\u662f\u5bfb\u5e38\u7684\u5b98\u592a\u592a\uff0c\u800c\u662f\u90aa\u6d3e\u4e2d\u7684\u4e00\u4e2a\u5389\u5bb3\u4eba\u7269\uff01 \u3000\u3000\u90a3\u8f7f\u592b\u4e00\u638c\u6253\u4e2d\u77f3\u51f3\uff0c\u75db\u5f97\u4ed6\u624b\u8155\u51e0\u4e4e\u6298\u65ad\uff0c\u54c7\u54c7\u5927\u53eb\uff0c\u53e6\u4e00\u4e2a\u8f7f\u592b\u89c1\u540c\u4f34\u5931\u5229\uff0c\u6325\u52a8 \u62f3\u5934\uff0c\u4e5f\u6253\u8fc7\u6765\u3002 \u3000\u3000\u6c5f\u5357\u5728\u4ed6\u4eec\u4e24\u4eba\u5939\u653b\u4e4b\u4e0b\uff0c\u6479\u5730\u4e00\u4e2a\u7b4b\u6597\uff0c\u5012\u7ffb\u51fa\u53bb\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u518d\u6253\uff0c\u6211\u53ef\u4e0d\u548c\u4f60 \u4eec\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u90a3\u5b98\u592a\u592a\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u4f4f\u624b\uff01\xb7\u90a3\u4e24\u4e2a\u8f7f\u592b\u6014\u4e86\u4e00\u6014\uff0c\u4e0d\u6562\u4e0d\u4ece\uff0c\u56db\u53ea\u773c\u775b\u671b\u7740\u90a3\u5b98\u592a \u592a\uff0c\u4f3c\u4e4e\u9887\u89c9\u610f\u5916\u3002\u90a3\u5b98\u592a\u5927\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u51fa\u95e8\u4eba\u662f\u8be5\u4e0e\u4eba\u65b9\u4fbf\u3002\u5c31\u8ba9\u4ed6\u5728\u8fd9\u91cc\u559d\u8336\u5427\u3002\u201d \u90a3\u5927\u523a\u523a\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u8fd9\u8336\u4ead\u662f\u5979\u7684\u5730\u65b9\u4e00\u6837\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u8f7f\u592b\u5782\u4e0b\u624b\u6765\uff0c\u4ece\u6c5f\u5357\u8eab\u8fb9\u9000\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u4fbf\u5b9c\u4e86\u4f60\u8fd9\u5c0f\u5b50\uff0c\u8fd8\u4e0d\u8c22\u8fc7\u592a\u592a\u7684\u6069 \u5178\uff1f\u201d\u201d \u3000\u3000\u6c5f\u5357\u53ef\u4e0d\u80af\u9886\u8fd9\u4e2a\u60c5\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u4e0a\u6765\u6253\u6211\u7684\u65f6\u5019\uff0c\u4f60\u53c8\u4e0d\u559d\u6b62\u4ed6\u4eec\uff0c\u5206\u660e\u662f\u6709 \u610f\u8bd5\u770b\u6211\u7684\u529f\u592b\u3002\u8981\u4e0d\u662f\u6211\u8fd8\u6709\u4e24\u4e0b\u5b50\uff0c\u53c8\u6015\u4f60\u4eec\u8fd8\u6ca1\u6709\u8fd9\u6837\u6613\u76f8\u4e0e\u5462\uff01\u201d\u4e0d\u8fc7\u4ed6\u867d\u7136\u6012\u6c14 \u672a\u5e73\uff0c\u5374\u4e5f\u4e0d\u62db\u60f9\u8fd9\u4e9b\u4eba\uff0c\u5f53\u4e0b\u7d22\u6027\u7ed9\u4ed6\u4eec\u6765\u4e2a\u4e0d\u7406\u4e0d\u776c\u3002 \u3000\u3000\u90a3\u5b98\u5927\u5927\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u5fc5\u591a\u4e8b\u4e86\uff0c\u5c31\u7531\u4ed6\u53bb\u5427\u3002\u201d\u6c5f\u5357\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u62c2\u4e00\u62c2\u8eab\u4e0a\u7684\u7070 \u5c18\uff0c\u5fc3\u9053\uff0c\u201c\u4f60\u8981\u6211\u8d70\uff0c\u6211\u504f\u4e0d\u8d70\u3002\u201d\u53c8\u5927\u9a6c\u91d1\u5200\u7684\u53c8\u5750\u4e0b\u6765\u3002 \u3000\u3000\u8336\u4ead\u8001\u4eba\u672c\u6765\u8981\u7ed9\u90a3\u5b98\u592a\u592a\u5012\u8336\u7684\uff0c\u7ed9\u4ed6\u4eec\u8fd9\u6837\u4e00\u95f9\uff0c\u5413\u5f97\u614c\u4e86\uff0c\u8fd9\u65f6\u624d\u98a4\u5dcd\u5dcd\u7684\u5c06\u4e00 \u7897\u70ed\u817e\u817e\u7684\u8336\u6367\u8fc7\u6765\u3002 \u3000\u3000\u624b\u8155\u53d7\u4f24\u7684\u90a3\u4e2a\u8f7f\u592b\u4f3c\u4e4e\u8981\u501f\u8fd9\u8001\u4eba\u51fa\u6c14\uff0c\u5ffd\u5730\u8863\u8896\u4e00\u62c2\uff0c\u559d\u9053\uff1a\u201c\u8c01\u559d\u4f60\u8fd9\u4e2a\u8336\uff1f\u8fd9 \u8336\u53ea\u914d\u7ed9\u4e61\u4e0b\u4eba\u559d\u7684\uff01\u201d\u5f53\u5577\u58f0\u54cd\uff0c\u8336\u7897\u843d\u5730\uff0c\u788e\u6210\u516b\u5757\uff0c\u70ed\u8336\u6e85\u4e86\u90a3\u8001\u4eba\u6ee1\u5934\u6ee1\u9762\uff01 \u3000\u3000\u6c5f\u5357\u770b\u4e0d\u8fc7\u773c\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u8df3\u8d77\u6765\u9a82\u9053\uff1a\u201c\u4f60\u4eec\u4ed7\u52bf\u6b3a\u4fae\u4eba\u4e48\uff1f\u201d\u4e24\u4e2a\u8f7f\u592b\u5927\u6012\uff0c\u9f50\u58f0\u559d \u9053\uff1aJ\u4f60\u8fd9\u5c0f\u5b50\u662f\u4e0d\u662f\u8fd8\u60f3\u8ba8\u6253\uff1f\u201d \u3000\u3000\u5b98\u5927\u592a\u6446\u6446\u624b\u9053\uff1a\u201c\u7b97\u4e86\uff0c\u7b97\u4e86\u3002\u8fd9\u8001\u6c49\u4e0d\u77e5\u9053\u54b1\u4eec\u5e26\u6709\u8336\u53f6\uff0c\u602a\u4e0d\u5f97\u4ed6\u3002\u55ef\uff0c\u8001\u6c49\uff0c \u4f60\u53ea\u7ed9\u6211\u4eec\u4e00\u58f6\u5f00\u6c34\u4fbf\u884c\u4e86\u3002\u6211\u4eec\u81ea\u5907\u6709\u6d1e\u5ead\u7684\u78a7\u87ba\u6625\u3002\u201d \u3000\u3000\u8001\u4eba\u5fd9\u9053\uff1a\u201c\u6ca1\u70eb\u7740\uff0c\u6ca1\u70eb\u7740\u3002\u201d\u7ed9\u90a3\u5b98\u592a\u592a\u9001\u8fc7\u4e86\u5f00\u6c34\u4e4b\u540e\uff0c\u53c8\u8d76\u5fd9\u5230\u6c5f\u5357\u7684\u8eab\u8fb9\uff0c \u5411\u4ed6\u4f7f\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u8bf4\u9053\uff0c\u201c\u5c0f\u54e5\u513f\uff0c\u4f60\u4e0d\u662f\u8fd8\u8981\u8d76\u4e0a\u5c71\u4e48\uff1f\u65f6\u5019\u53ef\u4e0d\u65e9\u4e86\u554a\uff01\u201d \u3000\u3000\u6c5f\u5357\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u4fbf\u660e\u767d\u4e86\u8fd9\u8001\u4eba\u7684\u610f\u601d\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u4ed6\u662f\u6015\u6211\u5403\u4e8f\uff0c\u6240\u4ee5 \u53eb\u6211\u5feb\u8d70\uff0c\u6211\u867d\u7136\u4e0d\u60e7\uff0c\u4f46\u5374\u4e5f\u4e0d\u597d\u7d2f\u4ed6\u62c5\u60ca\u53d7\u5413\u3002\u201d\u6c5f\u5357\u662f\u4e2a\u5403\u8f6f\u4e0d\u5403\u786c\u7684\u4eba\uff0c\u867d\u662f\u6027\u60c5 \u968f\u548c\uff0c\u5374\u4e5f\u4e0d\u7518\u53d7\u8fb1\u3002\u4e3a\u4e86\u90a3\u4e24\u4e2a\u8f7f\u592b\u8981\u8d76\u4ed6\u51fa\u53bb\uff0c\u4ed6\u672c\u6765\u5b58\u5fc3\u6c14\u6c14\u4ed6\u4eec\uff0c\u504f\u504f\u4e0d\u8d70\u7684\u3002\u53ef \u662f\u73b0\u5728\u662f\u8fd9\u4e2a\u4e00\u7247\u597d\u5fc3\u7684\u8001\u4eba\uff0c\u7528\u6c42\u6073\u7684\u773c\u5149\u8bf7\u4ed6\u79bb\u5f00\uff0c\u4ed6\u5c31\u4e0d\u5fcd\u4ee4\u8fd9\u8001\u4eba\u96be\u582a\u4e86\uff0c\u5f53\u4e0b\u5fc3 \u91cc\u518d\u60f3\u9053\uff1a\u201c\u53cd\u6b63\u6211\u662f\u8981\u53bb\u89c1\u8c37\u5973\u4fa0\u7684\uff0c\u4f55\u82e6\u5728\u8fd9\u91cc\u548c\u4ed6\u4eec\u751f\u65e0\u8c13\u7684\u95f2\u6c14\u3002\u201d \u3000\u3000\u60f3\u81f3\u6b64\u5904\uff0c\u6c5f\u5357\u5df2\u662f\u5fc3\u5e73\u6c14\u548c\uff0c\u4fbf\u7ad9\u4e86\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u201c\u591a\u8c22\u8001\u4e08\u4f60\u63d0\u9192\u6211\uff0c\u4e5f\u591a\u8c22\u4f60\u9001 \u6211\u7684\u7092\u7c73\u997c\u3002\u8fd9\u70b9\u94f6\u5b50\u4e0d\u6562\u5f53\u4f5c\u8336\u94b1\uff0c\u53ea\u662f\u804a\u8868\u8c22\u610f\u800c\u5df2\uff0c\u671b\u4f60\u6536\u4e0b\u3002\u201d\u4ed6\u638f\u51fa\u4e00\u952d\u7eb9\u94f6\uff0c \u585e\u5230\u8001\u4eba\u7684\u624b\u91cc\uff0c\u90a3\u8001\u4eba\u5927\u4e3a\u60ca\u8be7\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u54e5\u513f\uff0c\u4f60\u53ea\u559d\u4e86\u4e00\u7897\u8336\uff0c\u6211\u600e\u597d\u6536\u4f60\u8fd9\u4e9b\u94f6 \u5b50\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u6211\u672c\u6765\u8bf4\u8fc7\u4e0d\u662f\u5f53\u4f5c\u8336\u94b1\u7684\uff0c\u4f60\u5018\u82e5\u4e0d\u6536\uff0c\u5c31\u662f\u770b\u4e0d\u8d77\u6211\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u4e0d\u5f85\u90a3\u8001\u4eba\u518d\u8bf4\uff0c\u4fbf\u80cc\u8d77\u5305\u88b1\u3002\u5927\u6b65\u8de8\u51fa\u8336\u4ead\uff0c\u4ed6\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u90a3\u4e24\u4e2a\u8f7f\u592b\u77aa\u7740 \u773c\u775b\u671b\u4ed6\uff0c\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u521a\u624d\u54b1\u4eec\u8fd8\u9a82\u4ed6\u662f\u7a77\u6ce5\u817f\u5b50\uff0c\u5012\u662f\u54b1\u4eec\u770b\u8d70\u773c\u4e86\u3002\u201d\u53e6\u4e00\u4e2a\u9053\uff1a \u201c\u516b\u6210\u662f\u4e2a\u4e0b\u4e09\u95e8\u7684\u5c0f\u8d3c\uff0c\u5077\u4e86\u51e0\u4e2a\u94f6\u5b50\uff0c\u5411\u4e61\u4e0b\u4eba\u6446\u9614\u6c14\u3002\u201d \u3000\u3000\u6c5f\u5357\u51b7\u7b11\u4e00\u58f0\uff0c\u5fc3\u91cc\u9a82\u9053\uff1a\u201c\u72d7\u773c\u770b\u4eba\u4f4e1\u201d\u4ed6\u5df2\u51b3\u5b9a\u4e0a\u5c71\uff0c\u4e0d\u613f\u518d\u548c\u8f7f\u592b\u6597\u5634\uff0c\u51b7\u7b11 \u4e00\u58f0\u4e4b\u540e\uff0c\u653e\u5f00\u5927\u6b65\u4fbf\u8d70\u3002 \u3000\u3000\u90a3\u836f\u738b\u5e99\u5728\u5c71\u5761\u4e0a\uff0c\u6c5f\u752b\u7ecf\u8fc7\u5e99\u95e8\uff0c\u89c1\u6709\u51e0\u4e2a\u8863\u886b\u69db\u697c\u7684\u6c49\u5b50\u6b63\u5728\u5e99\u91cc\u56f4\u7740\u4e00\u4e2a\u8001\u5c3c\u59d1 \u8bf4\u8bdd\uff0c\u90a3\u51e0\u4e2a\u6c49\u5b50\u7684\u80a9\u5934\u4e0a\u90fd\u6709\u6bb7\u7ea2\u7684\u8840\u6e0d\uff0c\u6c5f\u5357\u4e0d\u7981\u53c8\u5f15\u8d77\u4e86\u597d\u5947\u4e4b\u5fc3\uff0c\u60f3\u9053\uff1a\u201c\u600e\u7684\u8fd9 \u6837\u5de7\uff0c\u96be\u9053\u90fd\u662f\u7ed9\u8001\u864e\u54ac\u4f24\u4e86\u80a9\u818a\u7684\uff1f\u201d \u3000\u3000\u90a3\u8001\u5c3c\u59d1\u628a\u4e00\u5305\u5305\u7684\u8349\u836f\u5206\u7ed9\u4ed6\u4eec\uff0c\u6c5f\u5357\u8ba4\u5f97\u8fd9\u4e2a\u8001\u5c3c\u59d1\u662f\u548c\u8fc7\u4e16\u7684\u66f9\u9526\u513f\u540c\u4e00\u73ed\u8f88\u7684 \u6c13\u5c71\u6d3e\u5f1f\u5b50\uff0c\u90a3\u8001\u5c3c\u59d1\u4e13\u5fc3\u4e00\u5fd7\u66ff\u90a3\u4e9b\u4eba\u7597\u4f24\uff0c\u6c5f\u5357\u7684\u8863\u88f3\u53c8\u6bd4\u90a3\u4e9b\u4eba\u8fd8\u8981\u7834\u70c2\uff0c\u5979\u53ea\u5f53\u662f \u4e2a\u8fc7\u8def\u7684\u730e\u6237\uff0c\u6ca1\u6709\u7559\u610f\u4ed6\u3002 \u3000\u3000\u6c5f\u5357\u672c\u60f3\u8fdb\u53bb\u770b\u770b\uff0c\u4f46\u4e00\u770b\u65e5\u5934\u5df2\u7ecf\u8fc7\u5348\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u7684\u6027\u60c5\uff0c\u82e5\u7136\u8e0f\u8fdb\u5e99\u95e8\uff0c\u628a\u8bdd\u62c9 \u5f00\uff0c\u53c8\u4e0d\u77e5\u8981\u803d\u6401\u591a\u5c11\u65f6\u5019\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd8\u662f\u5148\u89c1\u4e86\u8c37\u5973\u4fa0\uff0c\u7136\u540e\u518d\u884c\u6253\u542c\u5427\u3002\u201d \u3000\u3000\u6c5f\u5357\u7ee7\u7eed\u767b\u5c71\uff0c\u518d\u8d70\u4e86\u534a\u4e2a\u65f6\u8fb0\u3002\u5c71\u8def\u8d8a\u6765\u8d8a\u9661\uff0c\u5df2\u7ecf\u5230\u4e86\u6ca1\u4eba\u70df\u7684\u9ad8\u5904\uff0c\u5ffd\u542c\u5f97\u80cc\u540e \u6709\u811a\u6b65\u7684\u58f0\u97f3\uff0c\u53c8\u6709\u4eba\u9a82\u9053\uff1a\u201c\u53c8\u78b0\u5230\u4e86\u8fd9\u4e2a\u6df7\u5e10\u7684\u5c0f\u5b50\uff01\u201d\u6c5f\u5357\u56de\u5934\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u5b98\u592a \u592a\u7684\u90a3\u4e58\u5c0f\u8f7f\u6765\u4e86\u3002 \u3000\u3000\u5c71\u8def\u5d0e\u5c96\uff0c\u666e\u901a\u7684\u4eba\u5f92\u624b\u8d70\u8def\uff0c\u5c1a\u4e14\u611f\u5230\u5403\u529b\uff0c\u90a3\u4e24\u4e2a\u8f7f\u592b\u62ac\u7740\u8f7f\u5b50\uff0c\u5374\u662f\u5065\u6b65\u5982\u98de\uff0c \u8f6c\u773c\u95f4\u4fbf\u5230\u4e86\u6c5f\u5357\u80cc\u540e\u3002\u6c5f\u5357\u5fcd\u4f4f\u6c14\u95ea\u8fc7\u4e00\u65c1\uff0c\u8ba9\u90a3\u4e58\u8f7f\u5b50\u8d70\u5728\u524d\u5934\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u8f7f\u592b\u54c8\u54c8\u5927\u7b11\uff0c\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u60f3\u662f\u6653\u5f97\u5389\u5bb3\uff0c\u8bc6\u76f8\u591a\u4e86\u3002\u201d\u53e6\u4e00\u4e2a\u9053\uff1a \u201c\u8fd9\u5c0f\u5b50\u6211\u4f3c\u4e4e\u5728\u54ea\u513f\u89c1\u8fc7\u7684\uff1f\u5bf9\u4e86\uff0c\u6211\u60f3\u8d77\u6765\u4e86\uff0c\u65b0\u5b89\u9547\u90a3\u665a\u7684\u4e8b\u60c5\uff0c\u4f3c\u4e4e\u4e5f\u6709\u8fd9\u5c0f\u5b50\u5728 \u5185\u3002\u201d \u3000\u3000\u6c5f\u5357\u542c\u4e86\u8fd9\u8bdd\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u6562\u60c5\u4ed6\u4eec\u524d\u665a\u4e5f\u66fe\u5230\u8fc7\u6211\u6240\u4f4f\u7684\u90a3\u5bb6\u5ba2\u5e97\uff0c\u610f\u6b32\u52ab \u593a\u90a3\u59d3\u6587\u5c11\u5e74\u7684\u73e0\u5b9d\u7684\uff1f\u90a3\u665a\u6765\u7684\u4eba\u592a\u591a\uff0c\u60f3\u662f\u4ed6\u4eec\u540e\u6765\u672a\u66fe\u51fa\u624b\uff0c\u6240\u4ee5\u4ed6\u8ba4\u5f97\u6211\uff0c\u6211\u5374\u4e0d \u8ba4\u5f97\u4ed6\u3002\uff1a \u3000\u3000\u6c5f\u5357\u7a0d\u5fae\u52a0\u5feb\u811a\u6b65\uff0c\u4e0e\u90a3\u4e58\u8f7f\u5b50\u4fdd\u6301\u7740\u5341\u6765\u4e08\u7684\u8ddd\u79bb\uff0c\u89c1\u90a3\u4e58\u8f7f\u5b50\u76f4\u5411\u5c71\u4e0a\u62ac\u53bb\uff0c\u4e0d\u7531 \u5f97\u53c8\u6697\u6697\u7eb3\u7f55\uff0c\u4ed6\u8d77\u521d\u4e5f\u4ee5\u4e3a\u8fd9\u201c\u5b98\u592a\u592a\u201d\u662f\u8981\u5230\u836f\u738b\u5e99\u8fdb\u9999\u7684\uff0c\u73b0\u5728\u624d\u77e5\u9053\u4e0d\u662f\u3002 \u3000\u3000\u6c5f\u5357\u8d8a\u60f3\u8d8a\u89c9\u5f97\u5947\u602a\uff0c\u7b2c\u4e00\u3001\u90a3\u201c\u5b98\u592a\u592a\u201d\u5df2\u53ef\u4ee5\u80af\u5b9a\u662f\u4e2a\u6709\u672c\u9886\u7684\u4eba\uff0c\u5979\u4e3a\u4ec0\u4e48\u8981\u5750 \u8f7f\u4e0a\u5c71\uff1f\u82e5\u8bf4\u662f\u6709\u610f\u6446\u6446\u9614\u6c14\uff0c\u5728\u6c13\u4e0a\u53c8\u6446\u7ed9\u8c01\u770b\uff1f\u7b2c\u4e8c\u3001\u90a3\u4e24\u4e2a\u8f7f\u592b\u4e3e\u6b62\u8a00\u8c08\uff0c\u8086\u65e0\u5fcc \u60ee\uff0c\u548c\u90a3\u201c\u5b98\u592a\u592a\u201d\u7684\u5173\u7cfb\uff0c\u4e5f\u4f3c\u4e4e\u4e0d\u662f\u4e0b\u4eba\u548c\u4e3b\u5b50\u7684\u5173\u7cfb\uff0c\u4ed6\u4eec\u7684\u6b66\u529f\u867d\u8fd8\u4e0d\u7b97\u600e\u6837\u4e86\u4e0d \u5f97\uff0c\u4f46\u6bd4\u8d77\u4e00\u822c\u7684\u6c5f\u6e56\u4eba\u7269\uff0c\u5374\u5df2\u662f\u5f3a\u5f97\u591a\u4e86\uff0c\u4f55\u4ee5\u4ed6\u4eec\u7518\u5fc3\u4e3a\u4e00\u4e2a\u5973\u4eba\u62ac\u8f7f\uff1f\u7b2c\u4e09\uff0c\u6700\u91cd \u8981\u7684\u662f\uff1a\u4ed6\u4eec\u4e0a\u6c13\u5c71\u6765\u4f5c\u4ec0\u4e48\uff1f\u6c5f\u5357\u7edd\u4e0d\u76f8\u4fe1\u4ed6\u4eec\u53ea\u662f\u4e3a\u4e86\u4e0a\u5c71\u6e38\u89c8\u6765\u7684\u3002 \u3000\u3000\u6c5f\u5357\u5ffd\u5730\u60f3\u8d77\u5728\u65b0\u5b89\u9547\u90a3\u95f4\u5ba2\u623f\u91cc\uff0c\u90a3\u5e97\u5c0f\u4e8c\u548c\u4ed6\u8bf4\u8fc7\u7684\u4e00\u4ef6\u4e8b\uff0c\u8bf4\u662f\u5728\u4ed6\u4f4f\u8fc7\u7684\u90a3\u95f4 \u623f\u5b50\uff0c\u6709\u4e00\u4e2a\u5b98\u592a\u5927\u6709\u4e00\u6b21\u524d\u6765\u6295\u5bbf\uff0c\u4e5f\u66fe\u7ecf\u6307\u5b9a\u8981\u4f4f\u90a3\u95f4\u623f\u5b50\uff0c\u800c\u90a3\u95f4\u623f\u5b50\uff0c\u5219\u662f\u5f88\u591a\u5e74 \u4ee5\u524d\u91d1\u4e16\u9057\u548c\u5389\u80dc\u7537\u90fd\u4f4f\u8fc7\u7684\u3002\u201c\u83ab\u975e\u8fd9\u4e2a\u5b98\u592a\u592a\u5c31\u662f\u90a3\u4e2a\u5b98\u592a\u592a\uff1f\u201d\u53ef\u662f\u6c5f\u5357\u867d\u7136\u628a\u8fd9\u4e24 \u4ef6\u4e8b\u60c5\u8054\u8d2f\u8d77\u6765\uff0c\u5374\u4e5f\u60f3\u4e0d\u51fa\u5176\u4e2d\u6709\u4f55\u9053\u7406\u3002 \u3000\u3000\u90a3\u4e58\u8f7f\u5b50\u59cb\u7ec8\u4e0e\u6c5f\u5357\u540c\u4e00\u6761\u8def\uff0c\u8d70\u5728\u4ed6\u7684\u524d\u9762\uff0c\u8d70\u4e86\u4e00\u4f1a\uff0c\u90a3\u4e58\u8f7f\u5b50\u5ffd\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u90a3 \u4e24\u4e2a\u8f7f\u592b\u56de\u8fc7\u5934\u6765\uff0c\u72e0\u72e0\u5730\u9a82\u9053\uff1a\u201c\u6df7\u5e10\u5c0f\u5b50\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8001\u662f\u8ddf\u7740\u6211\u4eec\u7684\u592a\u592a\uff1f\u201d \u3000\u3000\u6c5f\u5357\u7ed9\u4ed6\u4eec\u5de6\u4e00\u4e2a\u201c\u6df7\u5e10\u5c0f\u5b50\u201d\uff0c\u53f3\u4e00\u4e2a\u201c\u6df7\u5e10\u5c0f\u5b50\u201d\u9a82\u5f97\u4e5f\u53d1\u8d77\u4e86\u813e\u6c14\u6765\uff0c\u5f53\u4e0b\u4fbf \u201c\u56de\u656c\u201d\u8fc7\u53bb\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u6211\u8bf4\u4f60\u4eec\u624d\u662f\u6df7\u5e10\uff01\u8fd9\u6761\u8def\u662f\u4f60\u4eec\u7684\u4e48\uff1f\u8981\u8bf4\u662f\u8c01\u8ddf\u8c01\u5427\uff1f \u90a3\u4e5f\u662f\u6211\u5148\u8d70\u7684\uff0c\u6211\u4e0d\u8bf4\u4f60\u4eec\uff0c\u4f60\u4eec\u53cd\u800c\u8bf4\u6211\uff1f\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u8f7f\u592b\u9a82\u9053\uff1a\u201c\u597d\u4e2a\u7259\u5c16\u5634\u5229\u7684\u5c0f\u5b50\uff0c\u4f60\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u5357\u6012\u9053\uff1a\u201c\u4f60\u4eec\u60f3\u600e\u4e48\u6837\uff1f\u60f3\u6740\u6211\u4e48\uff1f\u201d\u524d\u9762\u90a3\u8f7f\u592b\u53d1\u51fa\u4e00\u58f0\u72de\u7b11\uff0c\u9634\u6c89\u6c89\u4ed6\u8bf4\u9053\uff1a \u201c\u6b64\u5730\u65e0\u4eba\uff0c\u628a\u8fd9\u5c0f\u5b50\u5e72\u4e86\u5427\uff01\u201d\u4f3c\u662f\u4e0e\u4ed6\u7684\u540c\u4f34\u5546\u91cf\uff0c\u53c8\u4f3c\u662f\u5411\u4ed6\u7684\u4e3b\u4eba\u7980\u544a\u3002 \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u55e4\u55e4\u58f0\u54cd\uff0c\u4e24\u679d\u77ed\u7bad\u5df2\u662f\u95ea\u7535\u822c\u7684\u5c04\u6765\uff0c\u6c5f\u5357\u559d\u9053\uff1a\u201c\u6697\u7bad\u4f24\u4eba\uff0c\u7b97 \u4ec0\u4e48\u672c\u9886\uff1f\u2014\u2014\u54fc\uff0c\u54fc\uff0c\u4e5f\u672a\u5fc5\u4fbf\u4f24\u5f97\u4e86\u6211\uff01\u201d\u4ed6\u4f7f\u51fa\u91d1\u4e16\u9057\u6240\u6559\u7684\u94c1\u6307\u7985\u529f\uff0c\u4f38\u6307\u75be\u5f39\uff0c \u5c06\u90a3\u4e24\u679d\u77ed\u7bad\u5f39\u5f00\uff0c\u6b63\u60f3\u518d\u9a82\uff0c\u5ffd\u89c9\u989d\u89d2\u4e00\u7247\u6c81\u51c9\uff0c\u7d27\u63a5\u7740\u53c8\u662f\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u6c5f\u5357\u5faa\u58f0\u6ce8 \u76ee\uff0c\u53ea\u89c1\u4e00\u679a\u94a2\u9489\uff0c\u5c31\u63d2\u5728\u81ea\u5df1\u8eab\u540e\u90a3\u68f5\u6811\u4e0a\uff0c\u4e0d\u95ee\u53ef\u77e5\uff0c\u81ea\u5df1\u7684\u989d\u89d2\u521a\u624d\u662f\u7ed9\u8fd9\u679a\u94a2\u9489\u64e6 \u8fc7\u4e86\u3002 \u3000\u3000\u6c5f\u5357\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u5206\u660e\u53ea\u770b\u89c1\u5c04\u6765\u7684\u662f\u4e24\u679d\u77ed\u7bad\uff0c\u73b0\u5728\u5374\u7a81\u7136\u591a\u4e86\u4e00\u679a\u94a2\u9489\uff0c\u4e0d \u77e5\u662f\u54ea\u91cc\u6765\u7684\uff1f\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u90a3\u5b98\u592a\u5927\u7684\u58f0\u97f3\u5728\u8f7f\u5b50\u91cc\u4f20\u51fa\u6765\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u8981\u518d\u7ed9\u6211\u60f9 \u4e8b\u4e86\uff0c\u8d70\u5427\uff01\u201d \u3000\u3000\u6c5f\u5357\u604d\u7136\u5927\u609f\uff0c\u8fd9\u679a\u94a2\u9489\uff0c\u6562\u60c5\u5c31\u662f\u8fd9\u5b98\u5927\u592a\u53d1\u7684\uff1f\u82e5\u7136\u5982\u6b64\uff0c\u5979\u8eb2\u5728\u8f7f\u5185\uff0c\u65bd\u653e\u6697 \u5668\uff0c\u6697\u5668\u5230\u4e86\u81ea\u5df1\u7684\u8ddf\u524d\uff0c\u81ea\u5df1\u5c1a\u672a\u77e5\u9053\uff0c\u5979\u7684\u624b\u6cd5\u4e5f\u672a\u514d\u5927\u795e\u5947\u4e86\uff01\u5fc3\u5ff5\u672a\u5df2\uff0c\u8033\u8fb9\u5ffd\u5730 \u4e5f\u4f3c\u6709\u4e2a\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8d70\u5427\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u521a\u597d\u662f\u90a3\u5b98\u592a\u592a\u8bf4\u4e86\u90a3\u53e5\u8bdd\uff0c\u201c\u8d70\u5427\u201d\u8fd9\u4e24\u4e2a\u5b57\u72b9\u4f59\u97f3\u8885\u8885\uff0c\u6c5f\u5357\u4e00\u65f6\u5012\u7cca\u6d82 \u4e86\uff0c\u4e0d\u77e5\u81ea\u5df1\u542c\u5230\u7684\u7a76\u7adf\u662f\u5979\u7684\u56de\u97f3\u8fd8\u662f\u53e6\u6709\u5176\u4eba\u3002 \u3000\u3000\u6c5f\u5357\u5fc3\u9053\uff1a\u201c\u7684\u786e\u72af\u4e0d\u7740\u518d\u62db\u60f9\u4ed6\u4eec\u4e86\uff0c\u6211\u8fd9\u6761\u5c0f\u547d\u8fd8\u8981\u7559\u7740\u89c1\u8c37\u5973\u4fa0\u5462\uff0c\u89c1\u4e86\u8c37\u5973\u4fa0 \u518d\u8bf4\u3002\u201d\u4ed6\u6253\u5b9a\u4e3b\u610f\uff0c\u7acb\u5373\u65bd\u5c55\u8f7b\u529f\uff0c\u4ece\u53e6\u4e00\u8fb9\u5ced\u58c1\u4e0a\u722c\u4e0a\u53bb\uff0c\u80cc\u540e\u72b9\u81ea\u542c\u5f97\u90a3\u4e24\u4e2a\u8f7f\u592b\u5495 \u5495\u565c\u565c\u7684\u9a82\u58f0\u3002\u6c5f\u5357\u53c8\u81ea\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u662f\u90a3\u5996\u5987\u6545\u610f\u5413\u5413\u6211\u5462\uff1f\u8fd8\u662f\u5979\u771f\u60f3\u53d6\u6211\u6027\u547d\uff0c\u5374\u7ed9 \u91d1\u5927\u4fa0\u5728\u6697\u4e2d\u5c06\u6211\u6551\u4e86\u5462\uff1f\u201d \u3000\u3000\u6c5f\u5357\u6284\u6377\u5f84\u767b\u5c71\uff0c\u4e00\u53e3\u6c14\u8dd1\u5230\u4e86\u7384\u5973\u89c2\u524d\uff0c\u56de\u5934\u4e00\u671b\uff0c\u5e76\u672a\u89c1\u6709\u90a3\u4e58\u8f7f\u5b50\uff0c\u4ed6\u5b9a\u4e86\u5b9a \u795e\uff0c\u5fc3\u91cc\u53c8\u60f3\u9053\uff1a\u201c\u6211\u7a7f\u7740\u8fd9\u8eab\u8863\u88f3\uff0c\u5374\u4e0d\u597d\u53bb\u89c1\u8c37\u5973\u4fa0\u3002\u201d \u3000\u3000\u4ed6\u672c\u6765\u5e26\u6709\u51e0\u4ef6\u968f\u8eab\u8863\u88f3\uff0c\u4e0d\u8fc7\u4e3a\u4e86\u6025\u4e8e\u8d76\u8def\uff0c\u6ca1\u6709\u6362\u4e0b\u7f62\u4e86\u3002\u8fd9\u65f6\u4ed6\u5df2\u5230\u4e86\u7384\u5973\u89c2 \u524d\uff0c\u81ea\u7136\u53ef\u4ee5\u677e\u4e00\u53e3\u6c14\uff0c\u4e8e\u662f\u4fbf\u8eb2\u5728\u8349\u4e1b\u4e2d\u6362\u8863\u3002 \u3000\u3000\u6c5f\u5357\u5c1a\u672a\u5c06\u65b0\u6362\u7684\u8863\u88f3\u7a7f\u597d\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u5b69\u5b50\u7684\u53e3\u97f3\u53eb\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u4f60\u4f60\u5feb\u6765\u770b\uff01\u563b \u563b\uff0c\u8fd9\u91cc\u6709\u4e2a\u5149\u5c41\u80a1\u7684\u4eba\uff01\u201d\u968f\u5373\u542c\u5f97\u4e00\u4e2a\u7c97\u8c6a\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u8eb2\u5728\u8fd9\u91cc\uff0c\u5feb\u6eda\u51fa \u6765\uff01\u201d \u3000\u3000\u6c5f\u5357\u8fde\u5fd9\u5c06\u88e4\u5b50\u62c9\u597d\uff0c\u94bb\u51fa\u8349\u4e1b\uff0c\u53ea\u89c1\u4e00\u6761\u5927\u6c49\u6a2a\u7709\u6012\u76ee\u7684\u5728\u4ed6\u7684\u9762\u524d\uff0c\u53e6\u5916\u4e00\u4e2a\u5973\u5b50 \u5219\u80cc\u5411\u7740\u4ed6\uff0c\u8fd8\u6709\u4e00\u4e2a\u5927\u7ea6\u516d\u4e03\u5973\u5b69\u5b50\uff0c\u4f38\u51fa\u6307\u5934\u4e5f\u5728\u4ed6\u7684\u9762\u524d\u7f9e\u4ed6\u3002 \u3000\u3000\u90a3\u5927\u6c49\u89c1\u4e86\u6c5f\u5357\uff0c\u5ffd\u5730\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u53eb\u8d77\u6765\u9053\uff1a\u201c\u4f60\u3001\u4f60\u3001\u662f\u6c5f\u5357\u5417\uff1f\u201d \u3000\u3000\u6c5f\u5357\u5b9a\u775b\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u4e2a\u76f8\u8bc6\u7684\u4eba\u2014\u2014\u8c37\u4e4b\u534e\u7684\u5e08\u5144\u8def\u82f1\u8c6a\u3002\u6c5f\u5357\u9762\u7ea2\u8fc7\u8033\uff0c\u8fde\u5fd9\u8bf4 \u9053\uff1a\u201c\u6211\u662f\u6765\u8c12\u89c1\u8d35\u6d3e\u638c\u95e8\u7684\uff0c\u4e0a\u5f97\u5c71\u6765\u6765\uff0c\u8863\u88f3\u7834\u4e86\uff0c\u6240\u4ee5\u6362\u8fc7\u4e00\u4ef6\u65b0\u8863\u3002\u771f\u662f\u5931\u793c\u4e86\uff01\u201d \u3000\u3000\u8def\u82f1\u8c6a\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u4f60\u8fc7\u6765\u5427\u3002\u8fd9\u4e0d\u662f\u5916\u4eba\uff0c\u8fd9\u662f\u6211\u7684\u597d\u53cb\u6c5f\u5357\u3002\u201d\u90a3\u5973 \u5b50\u8f6c\u8fc7\u8eab\u6765\uff0c\u5411\u6c5f\u5357\u201c\u798f\u201d\u4e86\u4e00\u201c\u798f\u201d\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u6c5f\u5357\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u8fd9\u4e48\u5927\u7684\u4eba \u4e86\uff0c\u8fd8\u50cf\u4e2a\u6d51\u5c0f\u5b50\uff01\u53bb\u62dc\u8bbf\u4eba\u5bb6\uff0c\u5374\u5230\u4e86\u4eba\u5bb6\u7684\u95e8\u524d\uff0c\u624d\u6362\u8863\u886b\uff01\u201d\u5979\u8138\u4e0a\u90a3\u526f\u5f3a\u5fcd\u7740\u7b11\u7684 \u795e\u6c14\uff0c\u4ee4\u5f97\u6c5f\u5357\u751a\u662f\u5c34\u5c2c\u3002 \u3000\u3000\u8def\u82f1\u8c6a\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u98ce\u628a\u4f60\u5439\u6765\u7684\uff1f\u771f\u662f\u7a00\u5ba2\uff0c\u6211\u966a\u4f60\u8fdb\u53bb\u5427\uff01\u201d\u90a3\u5c0f\u5973\u5b69\u5374\u9053\uff1a\u201c\u6211 \u4e0d\u548c\u5149\u5c41\u80a1\u7684\u7537\u4eba\u5728\u4e00\u8d77\uff0c\u6211\u8fd8\u8981\u548c\u59d1\u59d1\u53bb\u6458\u679c\u5b50\u3002\u201d\u8def\u82f1\u8c6a\u5fcd\u4fca\u4e0d\u7981\uff0c\u53c8\u7b11\u8d77\u6765\u9053\uff1a\u201c\u5c0f \u5b69\u5b50\u80e1\u4e71\u8bf4\u8bdd\uff0c\u6c5f\u5144\u83ab\u602a\uff01\u201d\u90a3\u5c0f\u5973\u5b69\u561f\u7740\u5634\u9053\uff1a\u201c\u6211\u624d\u4e0d\u662f\u4e71\u8bf4\u5462\uff0c\u6211\u521a\u624d\u89c1\u7740\u4ed6\u7684\u65f6 \u5019\uff0c\u4ed6\u662f\u5149\u7740\u5c41\u80a1\u7684\u5440\uff01\u201d\u6c5f\u5357\u81ea\u5df1\u4e5f\u5fcd\u4e0d\u4f4f\u7b11\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u521a\u624d\u53ea\u662f\u8fd9\u5c0f\u9b3c\u94bb\u8fdb \u8349\u4e1b\u91cc\u6765\uff0c\u8981\u662f\u7ed9\u90a3\u4e2a\u5973\u7684\u77a7\u89c1\u6211\u7684\u5149\u5c41\u80a1\uff0c\u90a3\u53ef\u5c31\u66f4\u5931\u793c\u4e86\u3002\u201d \u3000\u3000\u8def\u82f1\u8c6a\u901a\u62a5\u4e0a\u53bb\uff0c\u8c37\u4e4b\u534e\u542c\u8bf4\u6c5f\u5357\u5230\u6765\uff0c\u5341\u5206\u6b22\u559c\uff0c\u7acb\u523b\u8bf7\u4ed6\u5728\u4e66\u623f\u76f8\u89c1\uff0c\u6c5f\u5357\u4e00\u89c1\u4fbf \u53eb\u9053\uff1a\u201c\u54ce\u5440\uff0c\u8c37\u5973\u4fa0\u4f60\u8fd8\u662f\u5f53\u5e74\u7684\u6837\u5b50\uff0c\u4e00\u70b9\u4e5f\u6ca1\u6709\u53d8\uff0c\u597d\u4e0d\u6559\u4eba\u9ad8\u5174\uff01\u201d \u3000\u3000\u539f\u6765\u6c5f\u5357\u4ee5\u4e3a\u8c37\u4e4b\u534e\u5df2\u524a\u53d1\u4e3a\u5c3c\u4e86\u3002\u5982\u4eca\u89c1\u5979\u8fd8\u7559\u7740\u5934\u53d1\uff0c\u4fbf\u4e0d\u7531\u5f97\u60f3\u8d77\u5979\u548c\u91d1\u4e16\u9057\u7684 \u4e8b\u6765\u3002\u6c5f\u5357\u7684\u5fc3\u91cc\u662f\u5e0c\u671b\u4ed6\u4eec\u4e24\u4eba\u80fd\u591f\u590d\u5408\u7684\uff0c\u8c37\u4e4b\u534e\u8fd8\u7559\u7740\u5934\u53d1\uff0c\u4ed6\u5c31\u591a\u4e86\u4e00\u5206\u5e0c\u671b\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u5374\u4e0d\u77e5\u9053\u4ed6\u7684\u5fc3\u4e8b\uff0c\u56e0\u4e3a\u6c13\u5c71\u6d3e\u7684\u59cb\u7956\u72ec\u81c2\u795e\u5c3c\u662f\u4e2a\u5c3c\u59d1\uff0c\u5979\u53c8\u5728\u6c13\u5c71\u4e0a\u5efa\u4e86\u5ea7 \u7384\u5973\u89c2\uff0c\u6240\u4ee5\u90ae\u5c71\u6d3e\u7684\u5973\u5f1f\u5b50\u4e5f\u6709\u8bb8\u591a\u662f\u5c31\u5728\u89c2\u91cc\u505a\u4e86\u5c3c\u59d1\u7684\u3002\u4f46\u6c13\u5c71\u6d3e\u53c8\u662f\u4e00\u652f\u6b66\u6797\u7684\u5927 \u5b97\u6d3e\uff0c\u4fd7\u5bb6\u7684\u7537\u5973\u5f1f\u5b50\u66f4\u591a\uff0c\u6709\u4e00\u90e8\u4efd\u6563\u5c45\u5404\u5730\uff0c\u6709\u4e00\u90e8\u4efd\u5219\u5728\u9053\u89c2\u9644\u8fd1\u7ed3\u5e90\u805a\u5c45\uff0c\u7ed9\u7956\u5e08 \u5b88\u5893\uff0c\u540c\u65f6\u4e5f\u907f\u6e05\u5ef7\u7684\u641c\u6355\u3002\u6240\u4ee5\u51fa\u5165\u7384\u5973\u89c2\u7684\u56fa\u7136\u591a\u662f\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u5373\u5728\u7384\u5973\u89c2\u5c45\u4f4f\u7684\u4e5f\u5e76 \u975e\u4e2a\u4e2a\u90fd\u662f\u5c3c\u59d1\u3002\u8c37\u4e4b\u534e\u53ea\u662f\u81ea\u5df1\u4e0d\u6b22\u559c\u505a\u5c3c\u59d1\u800c\u5df2\uff0c\u5012\u5e76\u975e\u4e3a\u4e86\u91d1\u4e16\u9057\u7684\u7f18\u6545\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u542c\u4e86\u6c5f\u5357\u8fd9\u51e0\u53e5\u6ca1\u5934\u6ca1\u8111\u7684\u8bdd\uff0c\u4e5f\u7981\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u4f60\u4e5f\u4e00\u70b9\u6ca1\u6709\u53d8\uff0c\u8fd8\u662f \u50cf\u4ece\u524d\u90a3\u4e2a\u4f1a\u9017\u4eba\u5f00\u5fc3\u7684\u5c0f\u4f19\u5b50\u3002\u55ef\uff0c\u542c\u8bf4\u4f60\u505a\u4e86\u7236\u4eb2\u5566\uff0c\u6709\u591a\u5927\u4e86\uff1f\u600e\u4e48\u4e0d\u548c\u4f60\u7684\u5b69\u5b50\u4e00 \u540c\u6765\u73a9\u73a9\uff1f\u201d \u3000\u3000\u6c5f\u5357\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6b63\u662f\u4e3a\u4e86\u5b69\u5b50\u7684\u7f18\u6545\uff0c\u6211\u624d\u8dd1\u6765\u89c1\u4f60\u7684\u3002\u201d\u8c37\u4e4b\u534e\u6014\u4e86\u4e00\u6014\uff0c \u8bf4\u9053\uff1a\u201c\u8fd9\u5374\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u201d \u3000\u3000\u6c5f\u5357\u5fc3\u6025\u5982\u711a\uff0c\u6765\u4e0d\u53ca\u4ece\u5934\u7ec6\u8bf4\uff0c\u4fbf\u5148\u95ee\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u6765\u8fc7\u6ca1\u6709\uff1f\u201d\u4ed6\u524d\u8a00\u4e0d\u63a5\u540e\u8bed\uff0c \u800c\u4e14\u51e0\u4e4e\u662f\u4e0e\u8c37\u4e4b\u534e\u62a2\u7740\u8bf4\u8bdd\uff0c\u8fd8\u6ca1\u6709\u56de\u7b54\u8c37\u4e4b\u534e\u7684\u95ee\u8bdd\uff0c\u53c8\u95ee\u8d77\u8c37\u4e4b\u534e\u6765\u4e86\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u4e0d\u89c9\u53c8\u662f\u4e00\u6014\uff0c\u5fc3\u5934\u8df3\u4e86\u4e00\u4e0b\uff0c\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u4f60\u4e2d\u4e86\u90aa\u4e48\uff0c\u600e\u7684\u8001\u662f\u8bf4\u6ca1 \u5934\u6ca1\u8111\u7684\u8bdd\uff1f\u201d \u3000\u3000\u6c5f\u5357\u4e00\u672c\u6b63\u7ecf\u5730\u9053\uff1a\u201c\u5018\u82e5\u91d1\u5927\u4fa0\u8fd8\u672a\u5230\u8fc7\u8fd9\u91cc\uff0c\u6211\u5c31\u662f\u5f53\u771f\u662f\u4e2d\u90aa\u4e86\uff01\u8c37\u5973\u4fa0\uff0c\u4f60\u771f \u7684\u8fd8\u6ca1\u6709\u89c1\u8fc7\u4ed6\u4e48\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u81ea\u4ece\u90a3\u5e74\u4ed6\u7ed9\u6211\u9001\u6765\u89e3\u836f\u4e4b\u540e\uff0c\u6211\u5c31\u6ca1\u6709\u89c1\u8fc7\u4ed6\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5357\u6414\u4e86\u6414\u8111\u888b\uff0c\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u53ef\u5947\u602a\u4e86\u3002\u96be\u9053\u90a3\u4e2a\u6697\u4e2d\u52a9\u6211\u7684\u4eba\u4e0d\u662f\u4ed6\uff1f\u82e5\u7136\u662f \u4ed6\uff0c\u4ed6\u53c8\u600e\u7684\u4f1a\u4e0d\u5728\u8fd9\u91cc\uff1f\u4ed6\u53ef\u4ee5\u548c\u6211\u5f00\u73a9\u7b11\uff0c\u4f46\u4ed6\u5bf9\u4f60\u5374\u662f\u4ece\u6765\u4e0d\u5f00\u73a9\u7b11\u7684\u5440\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u7a76\u7adf\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff0c\u4f60\u4ece\u5934\u8bf4\u8d77\u5427\u3002\u201d \u3000\u3000\u6c5f\u5357\u82e6\u7b11\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\u3002\u4e0d\u8fc7\uff0c\u4e5f\u53ea\u6709\u4ece\u5934\u8bf4\u8d77\uff0c\u624d\u80fd\u4ee4\u4f60\u660e\u767d\u4e86\u3002\u6211\u7684\u5b69\u5b50\u88ab\u4eba\u62a2 \u53bb\u4e86\uff0c\u6211\u662f\u4e3a\u4e86\u60f3\u8bf7\u91d1\u5927\u4fa0\u5e2e\u5fd9\uff0c\u6240\u4ee5\u624d\u5230\u4f60\u8fd9\u91cc\u6765\uff0c\u60f3\u95ee\u4e00\u95ee\u4ed6\u7684\u6d88\u606f\u7684\u3002\u201d \u3000\u3000\u5f53\u4e0b\u4ed6\u5c31\u4ece\u90a3\u516b\u4e2a\u8499\u9762\u5973\u5b50\u5728\u4ed6\u7684\u5bb6\u91cc\u95f9\u4e8b\uff0c\u52ab\u8d70\u4e86\u4ed6\u7684\u513f\u5b50\u8bf4\u8d77\uff0c\u76f4\u8bf4\u81f3\u9014\u4e2d\u9047\u89c1\u59ec \u6653\u98ce\u7b49\u7b49\u4e8b\u60c5\u3002\u4ed6\u867d\u7136\u6025\u4e8e\u8981\u8bf4\u5230\u6b63\u9898\uff0c\u5374\u4e5f\u672a\u5fd8\u8bb0\u8981\u66ff\u59ec\u6653\u98ce\u8bf4\u51e0\u53e5\u597d\u8bdd\uff0c\u8bb2\u5b8c\u4e86\u59ec\u6653\u98ce \u52ab\u5b9d\uff0c\u8fd8\u4e66\u4ee5\u53ca\u548c\u4ed6\u7ed3\u62dc\u7b49\u51e0\u4ef6\u4e8b\u4e4b\u540e\uff0c\u4fbf\u95ee\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u59ec\u5927\u54e5\u4ed6\u4e0d\u6562\u5230\u6c13\u5c71\u89c1\u4f60\uff0c\u4e0d\u77e5 \u4f60\u53ef\u613f\u610f\u8ba4\u4ed6\u8fd9\u4e2a\u5e08\u5144\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u672c\u6765\u65e9\u5df2\u628a\u8fc7\u53bb\u5f53\u4f5c\u4e00\u573a\u5669\u68a6\uff0c\u4e0d\u60f3\u518d\u63d0\u8d77\u4e0e\u5979\u7236\u4eb2\u6709\u5173\u7684\u4eba\u4e86\u3002\u4f46\u5982\u4eca\u542c\u5f97\u59ec \u6653\u98ce\u6539\u90aa\u5f52\u6b63\uff0c\u5fc3\u4e2d\u5374\u4e5f\u6697\u6697\u559c\u6b22\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u59ec\u6653\u98ce\u5e76\u975e\u6c13\u5c71\u95e8\u4e0b\uff0c\u6211\u4f55\u6765\u8fd9\u4e2a \u5e08\u5144\uff1f\u4e0d\u8fc7\uff0c\u4ed6\u65e2\u7136\u662f\u4f60\u7684\u7ed3\u62dc\u4e49\u5144\uff0c\u6211\u770b\u5728\u4f60\u7684\u4efd\u4e0a\uff0c\u89c1\u4e86\u4ed6\u4e5f\u4f1a\u79f0\u4e00\u58f0\u59ec\u5927\u54e5\u7684\u3002\u201d \u3000\u3000\u6c5f\u5357\u63a5\u7740\u8bb2\u5230\u90a3\u4e24\u4e2a\u756a\u50e7\u4e0e\u59ec\u6653\u98ce\u4e89\u6597\uff0c\u4ee5\u53ca\u81ea\u5df1\u5f97\u4eba\u6697\u4e2d\u76f8\u52a9\uff0c\u6253\u8d25\u4e86\u90a3\u4e24\u4e2a\u756a\u50e7\u4e4b \u4e8b\u3002\u8c37\u4e4b\u534e\u4e5f\u89c9\u5f97\u5947\u602a\u8d77\u6765\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u83ab\u975e\u771f\u662f\u91d1\u4e16\u9057\u6765\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u5357\u63a5\u4e0b\u53bb\u6b63\u8981\u8bb2\u5230\u90a3\u4e2a\u73e0\u5149\u5b9d\u6c14\u7684\u8d35\u5987\u4eba\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u5730\u6709\u4e00\u4e2a\u5973\u5f1f\u5b50\u8fdb\u6765\u62a5 \u9053\uff1a\u201d\u542f\u7980\u638c\u95e8\uff0c\u6709\u4e2a\u5ba2\u4eba\u60f3\u6765\u89c1\u4f60\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u7684\u5ba2\u4eba\uff1f\u4f60\u8bf7\u767d\u5e08\u5144\u66ff\u6211\u5148\u62db\u547c\u5427\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u6211\u53cd\u6b63\u6ca1\u6709\u8fd9\u4e48 \u5feb\u8d70\uff0c\u4f60\u89c1\u8fc7\u4e86\u5ba2\u4eba\uff0c\u54b1\u4eec\u518d\u53d9\u3002\u201d \u3000\u3000\u90a3\u5973\u5f1f\u5b50\u9053\uff1a\u201c\u8fd9\u5ba2\u4eba\u6709\u70b9\u7279\u522b\uff0c\u5979\u662f\u5750\u7740\u8f7f\u5b50\u6765\u7684\uff0c\u6307\u540d\u8981\u89c1\u638c\u95e8\u5e08\u59ca\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u6c5f\u5357\u53eb\u8d77\u6765\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4e00\u4e2a\u6d51\u8eab\u73e0\u5149\u5b9d\u6c14\u7684\u5b98\u592a\u592a\uff1f\u201d\u90a3\u5973\u5f1f \u5b50\u5927\u4e3a\u60ca\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u5979\u7684\u8eab\u4efd\uff0c\u770b\u6837\u5b50\u4f3c\u4e4e\u662f\u4e2a\u5b98\u592a\u592a\uff0c\u600e\u4e48\uff0c\u4f60\u8ba4\u5f97\u5979\u7684\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u9664\u4e86\u6211\u7684\u4e49\u5144\u4e4b\u5916\uff0c\u6211\u54ea\u4f1a\u8ba4\u5f97\u4ec0\u4e48\u5b98\u5bb6\uff1f\u8fd9\u5973\u4eba\u6211\u662f\u5728\u8def\u4e0a\u78b0\u5230\u7684\uff0c\u8c37 \u5973\u4fa0\uff0c\u4f60\u53bb\u4f1a\u5979\uff0c\u53ef\u5f97\u5f53\u5fc3\u4e00\u4e9b\uff0c\u53ea\u6015\u5979\u7684\u6765\u8def\u4e0d\u6b63\uff0c\u6709\u70b9\u90aa\u95e8\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4f60\u7559\u5fc3\u770b\u770b\uff0c\u770b\u5979\u7684\u7709\u5fc3\u662f\u4e0d\u662f\u6709\u4e00\u56e2\u6de1\u6de1\u7684 \u9ed1\u6c14\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u4e5f\u89c9\u5f97\u4e8b\u60c5\u53e4\u602a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6e05\u5ef7\u5411\u6765\u654c\u89c6\u672c\u6d3e\uff0c\u600e\u7684\u4f1a\u6709\u4e2a\u5b98\u5bb6\u592a\u592a\u5230\u6765\u8bbf \u6211\uff1f\u201d\u5979\u597d\u5947\u5fc3\u8d77\uff0c\u5f53\u4e0b\u65e0\u6687\u518d\u5411\u6c5f\u5357\u67e5\u95ee\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u4e0d\u7ba1\u5979\u662f\u6b63\u6d3e\u90aa\u6d3e\uff0c\u5b98\u5bb6\u6c11 \u5bb6\uff0c\u4e14\u5148\u95f4\u95ee\u5979\u7684\u6765\u610f\u518d\u8bf4\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u8d70\u51fa\u5916\u9762\u5ba2\u5385\uff0c\u53ea\u89c1\u90a3\u4e58\u8f7f\u5b50\u5df2\u505c\u5728\u9662\u4e2d\uff0c\u90a3\u4e24\u4e2a\u8f7f\u592b\u53c9\u7740\u8170\u6746\uff1b\u7ad9\u5728\u8f7f\u65c1\uff0c\u795e \u60c5\u751a\u4e3a\u4e0d\u60a6\u3002\u8c37\u4e4b\u534e\u9063\u4e2a\u5973\u5f1f\u5b50\u63a5\u90a3\u5987\u4eba\u51fa\u6765\uff0c\u81f3\u4e8e\u90a3\u4e24\u4e2a\u8f7f\u592b\uff0c\u5219\u7531\u8def\u82f1\u8c6a\u4e0e\u767d\u82f1\u6770\u4e8c\u4eba \u62db\u5f85\uff0c\u5c06\u4ed6\u4eec\u8bf7\u5230\u53e6\u5916\u4e00\u5904\u3002\u90a3\u4e24\u4e2a\u8f7f\u592b\u4f3c\u4e4e\u77e5\u9053\u8def\u3001\u767d\u4e8c\u4eba\u7684\u8eab\u4efd\uff0c\u4ed6\u4eec\u672c\u6765\u56e0\u4e3a\u8c37\u4e4b\u534e \u8fdf\u8fdf\u4e0d\u4e88\u63a5\u89c1\u4ed6\u4eec\u7684\u4e3b\u4eba\uff0c\u56e0\u800c\u611f\u5230\u4e0d\u60a6\uff0c\u5982\u4eca\u5f97\u5230\u6c13\u5c71\u6d3e\u6709\u5934\u9762\u7684\u5f1f\u5b50\uff0c\u5c06\u4ed6\u4eec\u4e5f\u5f53\u4f5c\u5bbe \u5ba2\u62db\u5f85\uff0c\u8fd9\u624d\u8f6c\u6012\u4e3a\u559c\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u7559\u795e\u7ec6\u5bdf\uff0c\u679c\u7136\u53d1\u89c9\u8fd9\u4e2a\u73e0\u5149\u5b9d\u6c14\u7684\u5973\u4eba\uff0c\u7709\u5fc3\u662f\u6709\u4e00\u56e2\u6de1\u6de1\u7684\u9ed1\u6c14\u3002\u8c37\u4e4b\u534e\u662f \u6b63\u6d3e\u4e2d\u4eba\uff0c\u672c\u6765\u4e0d\u61c2\u5f97\u6709\u8fd9\u4e2a\u73b0\u8c61\u7684\u4eba\u4fbf\u662f\u7ec3\u6709\u9634\u6bd2\u6b66\u529f\u7684\uff0c\u597d\u5728\u5f97\u6c5f\u5357\u63d0\u9192\uff0c\u5fc3\u4e2d\u5148\u6709\u4e86 \u9632\u5907\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u867d\u7136\u5df2\u77e5\u9053\u8fd9\u5973\u4eba\u6709\u70b9\u90aa\u95e8\uff0c\u4f46\u4ecd\u7136\u4ee5\u793c\u76f8\u5f85\uff0c\u5750\u5b9a\u4e4b\u540e\uff0c\u4fbf\u95ee\u5979\u9053\uff1a\u201c\u4e0d\u77e5\u592b \u4eba\u9ad8\u59d3\u5927\u540d\uff0c\u83b2\u9a7e\u81f3\u6b64\uff0c\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u5ba2\u5385\u4e4b\u4e2d\u672c\u6765\u8fd8\u6709\u4e00\u4f4d\u90b1\u5c71\u6d3e\u7684\u5973\u5f1f\u5b50\u62c5\u4efb\u62db\u5f85\u4e4b\u804c\uff0c\u8fd9\u65f6\u6b63\u5c06\u4e00\u676f\u8336\u9001\u5230\u90a3\u201c\u5b98\u592a \u592a\u201d\u7684\u9762\u524d\uff0c\u90a3\u5b98\u592a\u592a\u671b\u4e86\u5979\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u8c37\u5973\u4fa0\uff0c\u6211\u7684\u4e8b\u60c5\u60f3\u4e0e\u4f60\u5355\u72ec\u8c08\u8c08\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u7709\u5934\u7565\u76b1\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u96be\u9053\u53c8\u662f\u5389\u80dc\u7537\u6545\u4e8b\u7684\u91cd\u6f14\uff1f\u201d\u5f53\u4e0b\u6325\u4e00\u6325\u624b\uff0c\u5bf9\u90a3\u5973 \u5f1f\u5b50\u9053\uff1a\u201c\u597d\u5427\uff0c\u8fd9\u91cc\u6ca1\u6709\u4f60\u7684\u4e8b\u4e86\uff0c\u79f0\u51fa\u53bb\u5427\uff0c\u4f60\u4f20\u8bdd\u51fa\u53bb\uff0c\u4e0d\u5f97\u6211\u7684\u5429\u5490\uff0c\u4efb\u4f55\u4eba\u90fd\u4e0d \u8bb8\u8fdb\u6765\u3002\u201d\u5f85\u90a3\u5973\u5f1f\u5b50\u8d70\u4e86\u51fa\u53bb\uff0c\u5e76\u4e14\u5173\u597d\u4e86\u95e8\u4e4b\u540e\uff0c\u8c37\u4e4b\u534e\u7136\u540e\u8bf4\u9053\uff1a\u201c\u592b\u4eba\uff0c\u4f60\u6709\u4f55\u4e8b \u60c5\uff0c\u73b0\u5728\u53ef\u4ee5\u653e\u5fc3\u8bf4\u4e86\u5427\uff1f\u201d \u3000\u3000\u90a3\u5b98\u592a\u592a\u88e3\u887d\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5a18\u5bb6\u59d3\u6842\uff0c\u5916\u5b50\u7f2a\u5357\u5ef7\uff0c\u5b98\u5c45\u6cb3\u5357\u63d0\u7763\u4e4b\u804c\u3002\u201d\u6c13\u5c71\u5c5e \u6cb3\u5357\u7701\u5883\uff0c\u63d0\u7763\u4e43\u662f\u4e00\u7701\u7684\u519b\u4e8b\u957f\u5b98\uff0c\u4e0e\u5de1\u629a\u5e73\u884c\u3002\u8c37\u4e4b\u534e\u9762\u8272\u5fae\u53d8\uff0c\u8fd8\u793c\u8bf4\u9053\uff0c\u201c\u5931\u656c\uff0c \u5931\u656c\uff0c\u539f\u6765\u662f\u7f2a\u519b\u95e8\u592b\u4eba\u3002\u6211\u662f\u4f60\u6cbb\u4e0b\u7684\u5c0f\u6c11\uff0c\u6709\u52b3\u592b\u4eba\u98ce\u9a7e\u4eb2\u4e0a\u8352\u5c71\uff0c\u5b9e\u662f\u4e0d\u80dc\u60f6\u6050\u4e4b \u81f3\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u7684\u8bdd\u6697\u542b\u8ba5\u523a\uff0c\u540c\u65f6\u5fc3\u91cc\u7684\u7591\u60d1\u53c8\u52a0\u91cd\u4e86\u4e00\u5c42\uff0c\u82e5\u7136\u8fd9\u5973\u4eba\u7684\u8bdd\u662f\u771f\uff0c\u4e00\u4e2a\u671d\u5ef7 \u4e8c\u54c1\u5927\u5458\u7684\u547d\u5987\uff0c\u7adf\u662f\u4e2a\u90aa\u6d3e\u5996\u4eba\uff0c\u90a3\u5c82\u975e\u4e0d\u53ef\u601d\u8bae\u4e4b\u4e8b\uff01\u800c\u8fd9\u4e2a\u671d\u5ef7\u547d\u5987\u5750\u8f7f\u5230\u6c13\u5c71\u6765\u62dc \u4f1a\u4e8e\u5979\uff0c\u800c\u5979\u53c8\u662f\u671d\u5ef7\u6240\u654c\u89c6\u7684\u6c13\u5c71\u6d3e\u638c\u95e8\uff0c\u8fd9\u66f4\u662f\u8352\u5510\u900f\u9876\uff0c\u96be\u4ee5\u89e3\u91ca\u4e86\u3002 \u3000\u3000\u90a3\u7f2a\u592b\u4eba\u4f3c\u4e4e\u731c\u5230\u4e86\u8c37\u4e4b\u534e\u7684\u5fc3\u610f\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u8c37\u638c\u95e8\u4e0d\u7528\u60ca\u7591\uff0c\u6211\u6b64\u6765\u5bf9\u8d35\u6d3e\u5e76\u65e0 \u4e0d\u5229\uff0c\u53ea\u662f\u6709\u4e00\u4ef6\u4e8b\u60c5\uff0c\u8981\u8bf7\u638c\u95e8\u6210\u5168\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u592b\u4eba\u8bf4\u7b11\u4e86\uff0c\u5c0a\u592b\u624b\u63e1\u91cd\u5175\uff0c\u5b98\u5c45\u65b9\u9762\uff0c\u4f55\u6c42\u4e0d\u5f97\uff1f\u600e\u7684\u8981\u6c42\u5230\u6211\u6765\uff1f\u201d \u7f2a\u592b\u4eba\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u8fd9\u4e8b\u60c5\u662f\u7792\u7740\u4e08\u592b\u7684\uff0c\u8c37\u638c\u95e8\u82e5\u7136\u4e0d\u5141\u6210\u5168\uff0c\u6211\u53ea\u6709\u62b1\u6068\u7ec8\u751f \u4e86\uff01\u201d \u3000\u3000\u5404\u4e4b\u534e\u89c1\u5979\u8bf4\u5f97\u5982\u6b64\u4e25\u91cd\uff0c\u53ea\u5f97\u5e94\u5141\u5979\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u592b\u4eba\u8bf7\u8bf4\u3002\u53ea\u8981\u4e0d\u8fdd\u60c5\u7406\uff0c\u5c0f \u5973\u5b50\u6709\u53ef\u4ee5\u6548\u52b3\u4e4b\u5904\uff0c\u81ea\u5f53\u7a0d\u5c3d\u7ef5\u529b\u3002\u201d \u3000\u3000\u90a3\u7f2a\u592b\u4eba\u626d\u634f\u4e00\u7b11\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e5f\u8bb8\u6b64\u4e8b\u6b63\u662f\u5927\u8fdd\u60c5\u7406\uff0c\u4e0d\u8fc7\u8c37\u638c\u95e8\u662f\u4e2a\u901a\u8fbe\u7684\u4eba\uff0c \u800c\u8fd9\u4e8b\u5bf9\u6211\u53c8\u6781\u4e3a\u91cd\u8981\uff0c\u6240\u4ee5\u6211\u4e5f\u4e0d\u6015\u8c37\u638c\u95e8\u89c1\u7b11\uff0c\u53ea\u597d\u5bf9\u4f60\u76f4\u8bf4\u4e86\uff01\u201d \u3000\u3000\u7f2a\u592b\u4eba\u5477\u4e86\u53e3\u8336\uff0c\u4e24\u988a\u5fae\u73b0\u7ea2\u6655\uff0c\u4ecd\u7136\u4f3c\u662f\u8033\u8bed\u4e00\u822c\uff0c\u5bf9\u8c37\u4e4b\u534e\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8 \u7792\uff0c\u6211\u5728\u672a\u5ac1\u4e4b\u524d\uff0c\u4e0e\u540c\u6751\u7684\u4e00\u4e2a\u4e66\u751f\u6709\u4e86\u79c1\u60c5\uff0c\u751f\u4e0b\u4e86\u4e00\u5bf9\u53cc\u80de\u80ce\uff0c\u4e43\u662f\u4e00\u7537\u4e00\u5973\uff0c\u4ea7\u4e0b \u4e4b\u540e\uff0c\u4ea4\u7ed9\u5976\u5988\u9690\u85cf\u8d77\u6765\u3002\u4e0d\u4e45\uff0c\u6211\u5c31\u8feb\u4e8e\u4e25\u7236\u4e4b\u547d\uff0c\u975e\u5ac1\u4e0d\u53ef\uff0c\u8fd9\u4e24\u4e2a\u5b69\u5b50\u5f53\u7136\u4e0d\u80fd\u5e26\u5230 \u592b\u5bb6\uff0c\u751a\u81f3\u4e5f\u4e0d\u80fd\u8ba9\u7236\u6bcd\u77e5\u9053\u3002\u6211\u7684\u5976\u8682\u6709\u4e2a\u513f\u5b50\uff0c\u662f\u5728\u5916\u4e61\u79cd\u7530\u7684\uff0c\u65e0\u53ef\u5948\u4f55\uff0c\u6211\u53ea\u5f97\u63a5 \u7eb3\u4e86\u5976\u5988\u4e4b\u8ba1\uff0c\u5c06\u8fd9\u5bf9\u5b69\u5b50\u6258\u4ed6\u7684\u513f\u5b50\u629a\u517b\uff0c\u8f6c\u773c\u81f3\u4eca\uff0c\u5df2\u6709\u4e03\u5e74\u4e86\u3002\u9aa8\u8089\u5206\u79bb\uff0c\u6bcf\u4e00\u5ff5 \u53ca\uff0c\u96be\u514d\u5fc3\u4f24\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u662f\u4e2a\u672a\u5ac1\u5973\u5b50\uff0c\u542c\u4e86\u6b64\u6d3b\uff0c\u4e0d\u7981\u9762\u7ea2\u8033\u8d64\uff0c\u5fc3\u60f3\uff1a\u201c\u602a\u4e0d\u5f97\u5979\u8981\u6211\u628a\u65c1\u4eba\u9063\u5f00\uff0c \u539f\u6765\u662f\u6709\u8fd9\u79cd\u79c1\u60c5\uff01\u4f46\u8fd9\u6837\u9690\u79d8\u7684\u5931\u5fb7\u4e4b\u4e8b\uff0c\u5979\u5bf9\u7236\u6bcd\u5c1a\u4e14\u4e0d\u4fbf\u542f\u9f7f\uff0c\u5374\u4e3a\u4f55\u5bf9\u6211\u6765\u8bb2\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u90a3\u7f2a\u592b\u4eba\u95ee\u9053\uff1a\u201c\u542c\u8bf4\u8c37\u5973\u4fa0\u6536\u517b\u4e86\u4e2a\u5b64\u5973\uff0c\u4e0d\u77e5\u6709\u6b64\u4e8b\u4e48\uff1f\u201d\u8c37\u4e4b \u534e\u542c\u4e86\uff0c\u5fc3\u5934\u4e00\u9707\uff0c\u8c2c\u592b\u4eba\u8fd9\u53e5\u95ee\u8bdd\uff0c\u4e0a\u89e6\u53ca\u4e86\u5979\u4e09\u5e74\u6765\u6240\u7591\u8651\u7684\u4e00\u4ef6\u4e8b\u60c5\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u4e16\u4e8b\u5c82\u771f\u591a\u5de7\u5408\uff0c\u5e08\u5f92\u547d\u8fd0\u4e00\u822c\u540c\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff0c\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000--------------- \u3000\u3000\u626c\u5251\u8f69\u5c45\u58eb \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e03\u56de\u3000\u5b64\u96cf\u8eab\u4e16\u8c1c\u96be\u89e3\u3000\u9b54\u5973\u6069\u4ec7\u6068\u672a\u5e73 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e03\u56de\u3000\u5b64\u96cf\u8eab\u4e16\u8c1c\u96be\u89e3\u3000\u9b54\u5973\u6069\u4ec7\u6068\u672a\u5e73 \u3000\u3000\u539f\u6765\u8c37\u4e4b\u534e\u7684\u786e\u6536\u517b\u6709\u4e00\u4e2a\u5b64\u5973\uff0c\u8fd9\u4e2a\u5b64\u5973\u7684\u6765\u5386\u5341\u5206\u53e4\u602a\uff0c\u76f4\u5230\u5982\u4eca\uff0c\u5979\u7684\u8eab\u4e16\u8fd8\u662f \u4e2a\u96be\u89e3\u4e4b\u8c1c\uff01 \u3000\u3000\u90a3\u662f\u4e09\u5e74\u524d\u7684\u4e8b\u60c5\u4e86\uff0c\u6709\u4e00\u5929\uff0c\u8c37\u4e4b\u534e\u7684\u5e08\u5144\uff0c\u5357\u4e10\u5e2e\u7684\u5e2e\u4e3b\u7ffc\u4ef2\u725f\u5e26\u4e86\u4e00\u4e2a\u5e74\u4ec5\u56db\u5c81 \u7684\u5973\u5b69\u5b50\u4e0a\u5c71\uff0c\u592e\u6c42\u8c37\u4e4b\u534e\u6536\u517b\uff0c\u5e76\u4e14\u8bf4\u51fa\u4e86\u4e00\u6bb5\u79bb\u5947\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u5180\u4ef2\u725f\u6709\u4e00\u4e2a\u4e0d\u5927\u76f8\u719f\u7684\u670b\u53cb\uff0c\u540d\u53eb\u4e18\u5ca9\uff0c\u6b64\u4eba\u662f\u6cb3\u5357\u4e2d\u725f\u53bf\u7684\u4e00\u4e2a\u5c0f\u5730\u4e3b\uff0c\u61c2\u5f97\u6b66 \u529f\uff0c\u4eba\u8fd8\u6b63\u6d3e\uff0c\u800c\u4e14\u5c11\u5e74\u65f6\u5019\uff0c\u8fd8\u66fe\u5077\u5370\u8fc7\u5415\u7559\u826f\u7684\u53cd\u6e05\u9057\u4f5c\uff0c\u6697\u5730\u91cc\u5206\u9001\u7ed9\u4eba\uff0c\u662f\u6545\u7ffc\u4ef2 \u725f\u4e0e\u4ed6\u7ed3\u7eb3\u3002\u540e\u6765\u6e05\u5ef7\u4e25\u5389\u67e5\u7a76\u5415\u7559\u826f\u7684\u9057\u515a\uff0c\u4ed6\u624d\u56de\u5230\u4e61\u4e0b\u505a\u8d77\u7ec5\u58eb\u6765\u3002 \u3000\u3000\u7ffc\u4ef2\u725f\u6d6a\u8ff9\u6c5f\u6e56\uff0c\u4e14\u53c8\u662f\u4e2a\u53eb\u5316\u4e8e\u7684\u8eab\u4efd\uff0c\u5f53\u7136\u4e0d\u5927\u65b9\u4fbf\u548c\u4ed6\u5f80\u6765\uff0c\u4e24\u5165\u5df2\u6709\u76f8\u8fd1\u5341\u5e74 \u672a\u89c1\u9762\u4e86\uff0c\u4e18\u5ca9\u5ffd\u7136\u8bf7\u4e2d\u725f\u53bf\u7684\u4e10\u5e2e\u5f1f\u5b50\u9001\u5c01\u6025\u4fe1\u7ed9\u4ed6\uff0c\u8bf7\u4ed6\u5230\u5bb6\u4e2d\u4e00\u53d9\u3002 \u3000\u3000\u7ffc\u4ef2\u725f\u662f\u4e2a\u91cd\u53cb\u60c5\u7684\u4eba\uff0c\u63a5\u5230\u8fd9\u5c01\u4fe1\uff0c\u867d\u7136\u6709\u70b9\u5947\u602a\uff0c\u4ecd\u4f9d\u7ea6\u5230\u4e86\u4e18\u5bb6\u3002\u4e18\u5ca9\u8bf7\u4ed6\u8fdb\u5165 \u5185\u623f\uff0c\u4e00\u5f00\u53e3\u4fbf\u592e\u6c42\u4ed6\u9053\uff1a\u201c\u6211\u6709\u4e2a\u517b\u5973\uff0c\u4ed6\u7684\u7236\u4eb2\u662f\u6211\u7684\u4f43\u6237\uff0c\u56e0\u75c5\u53bb\u4e16\uff0c\u7559\u6b64\u5b64\u5973\uff0c\u751a \u662f\u53ef\u601c\uff0c\u6211\u6ca1\u6709\u513f\u5973\uff0c\u6240\u4ee5\u6536\u517b\u4e86\u5979\uff0c\u4f5c\u4e3a\u81ea\u5df1\u7684\u513f\u5973\u770b\u5f85\u3002\u4f46\u6211\u73b0\u5728\u4e5f\u5e74\u8001\u591a\u75c5\uff0c\u53ea\u6015\u6d3b \u4e0d\u4e86\u591a\u4e45\u4e86\uff0c\u4f60\u53ef\u613f\u610f\u6536\u7559\u8fd9\u4e2a\u5b64\u5973\u4e48\uff1f \u3000\u3000\u7ffc\u4ef2\u725f\u751a\u611f\u4e3a\u96be\uff0c\u5f53\u4e0b\u53ea\u597d\u76f4\u8a00\u8bf4\u9053\uff1a\u201c\u4e10\u5e2e\u5411\u6765\u4e0d\u6536\u5973\u5f1f\u5b50\uff0c\u6211\u53c8\u662f\u4e2a\u5355\u8eab\u7684\u8001\u53eb \u5316\uff0c\u6211\u7eb5\u7136\u60f3\u628a\u5979\u5f53\u4f5c\u5973\u513f\uff0c\u5e26\u5728\u8eab\u8fb9\u4e5f\u4e0d\u65b9\u4fbf\u3002\u8bf4\u53e5\u7b11\u8bdd\uff0c\u96be\u9053\u53eb\u5979\u957f\u5927\u4e86\u8ddf\u6211\u5f53\u5973\u5316\u5b50 \u4e48\uff1f\u543e\u5144\u621a\u53cb\u4e4b\u4e2d\uff0c\u4e0d\u4e4f\u6709\u5bb6\u6709\u4e1a\u4e4b\u4eba\uff0c\u8bf7\u4e00\u4e2a\u6bb7\u5b9e\u7684\u4eba\u7167\u6599\u5979\uff0c\u5c82\u4e0d\u80dc\u4e8e\u6258\u6211\u8fd9\u4e2a\u8001\u53eb\u5316 \u767e\u500d\uff1f\u51b5\u4e14\u543e\u5144\u7cbe\u795e\u4e5f\u8fd8\u5065\u7855\uff0c\u80e1\u4e3a\u51fa\u6b64\u4e0d\u7965\u4e4b\u8a00\uff1f\u201d \u3000\u3000\u4e18\u5ca9\u89c1\u7ffc\u4ef2\u725f\u4e0d\u80af\u7b54\u5141\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u4f3c\u6709\u96be\u8a00\u4e4b\u9690\uff0c\u4f46\u5374\u4e5f\u4e0d\u518d\u8bf7\u6c42\u3002\u4ed6\u65e9\u5df2\u5728\u623f\u4e2d\uff0c \u6446\u597d\u4e86\u9152\u83dc\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u7ffc\u5e2e\u4e3b\u6709\u4e3a\u96be\u4e4b\u5904\uff0c\u8fd9\u4e8b\u5c31\u6682\u4e14\u6401\u8fc7\u4e00\u8fb9\uff0c\u54b1\u4eec\u591a\u5e74\u672a\u89c1\uff0c \u5148\u75db\u996e\u4e00\u573a\u518d\u8bf4\u3002\u201d \u3000\u3000\u9152\u8fc7\u4e09\u5de1\uff0c\u4e18\u5ca9\u53c8\u9053\uff1a\u201c\u7ffc\u5e2e\u4e3b\u867d\u7136\u4e0d\u613f\u6536\u7559\u5c0f\u5973\uff0c\u4f46\u8ba9\u5979\u51fa\u6765\u62dc\u89c1\uff0c\u603b\u53ef\u4ee5\u5427\uff1f\u201d\u7ffc \u4ef2\u725f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8001\u53eb\u5316\u53ea\u6101\u6ca1\u6709\u89c1\u9762\u793c\uff0c\u5bf9\u4e0d\u8d77\u4ee4\u5343\u91d1\u3002\u201d \u3000\u3000\u4e18\u5ca9\u5f97\u4ed6\u7b54\u5141\uff0c\u4fbf\u628a\u5973\u513f\u53eb\u4e86\u51fa\u6765\uff0c\u5374\u539f\u6765\u662f\u4e00\u4e2a\u5e74\u4ec5\u4e09\u56db\u5c81\u7684\u5c0f\u5b69\u5b50\uff0c\u76f8\u8c8c\u5f88\u6e05\u79c0\uff0c \u4f46\u7a7f\u7684\u8863\u670d\u5374\u6709\u70b9\u53e4\u602a\uff0c\u90a3\u662f\u4e00\u4ef6\u7ea2\u7f0e\u5b50\u5c0f\u68c9\u8884\uff0c\u6700\u591a\u53ea\u5408\u4e24\u5c81\u5927\u7684\u5b69\u5b50\u7a7f\uff0c\u8fd9\u5973\u5b69\u5b50\u867d\u7136 \u751f\u5f97\u7626\u5c0f\uff0c\u7a7f\u5728\u8eab\u4e0a\uff0c\u4e5f\u5acc\u6709\u70b9\u7a84\u4e86\u3002\u800c\u4e14\u90a3\u65f6\u65b9\u662f\u521d\u79cb\u65f6\u5206\uff0c\u7a7f\u4e0a\u68c9\u8884\uff0c\u4e5f\u5acc\u4e0d\u5408\u65f6\u5b9c\u3002 \u3000\u3000\u7ffc\u4ef2\u725f\u6b63\u81ea\u7eb3\u7f55\uff0c\u4e18\u5ca9\u5df2\u5012\u6ee1\u4e86\u4e24\u676f\u9152\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6211\u6b64\u4f1a\u4e4b\u540e\uff0c\u53ea\u6015\u96be\u4ee5\u518d\u89c1\u4e86\uff0c\u8bf7 \u5c3d\u6b64\u4e00\u676f\uff0c\u6211\u6709\u80ba\u8151\u4e4b\u8a00\uff0c\u8981\u5411\u5e2e\u4e3b\u8868\u767d\uff01\u201d \u3000\u3000\u7ffc\u4ef2\u725f\u60ca\u7591\u4e0d\u5b9a\u3001\u4e3e\u676f\u8bf4\u9053\uff1a\u201c\u4f60\u6211\u9053\u4e49\u4e4b\u4ea4\uff0c\u4e18\u5144\uff0c\u4f60\u82e5\u5f53\u771f\u662f\u6709\u4e3a\u96be\u4e4b\u4e8b\uff0c\u5c0f\u5f1f\u81ea \u662f\u4e0d\u80fd\u4e0d\u7ba1\uff01\u4f46\u8bf4\u65e0\u59a8\uff01\u201d \u3000\u3000\u4e18\u5ca9\u6101\u7709\u987f\u5c55\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u5c06\u676f\u4e2d\u9152\u4e00\u996e\u800c\u5c3d\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6b63\u662f\u8981\u5e2e\u4e3b\u8fd9\u53e5\u8bf4\u8bdd\uff01\u8bda\u5982 \u5e2e\u4e3b\u6240\u8a00\uff0c\u4f60\u6211\u867d\u975e\u6df1\u4ea4\uff0c\u4f46\u6211\u6df1\u77e5\u4f60\u662f\u809d\u80c6\u7167\u4eba\uff0c\u4e00\u8bfa\u5343\u91d1\u7684\u98ce\u5c18\u8c6a\u4fa0\uff01\u5c0f\u5f1f\u621a\u53cb\u867d\u7136\u4f17 \u591a\uff0c\u53ef\u582a\u4fe1\u8d56\u8005\u53ea\u552f\u5144\u53f0\u4e00\u4eba\u800c\u5df2\uff01\u6b64\u5973\u4e0d\u4f46\u8eab\u4e16\u53ef\u601c\uff0c\u53ea\u6015\u6765\u65e5\u5c1a\u591a\u707e\u96be\uff0c\u6211\u73b0\u5728\u5df1\u662f\u65e0 \u529b\u7167\u987e\u5979\u4e86\uff0c\u6240\u4ee5\u660e\u77e5\u662f\u4e0d\u60c5\u4e4b\u8bf7\uff0c\u8fd8\u671b\u5e2e\u4e3b\u770b\u5728\u4f60\u6211\u7684\u4ea4\u60c5\u4efd\u4e0a\uff0c\u629a\u6b64\u5b64\u513f\uff0c\u5979\u82e5\u5f97\u5230\u8d35 \u5e2e\u7684\u5e87\u62a4 \u3000\u3000\u4e18\u5ca9\u7684\u58f0\u97f3\u8d8a\u6765\u8d8a\u5f31\uff0c\u8138\u4e0a\u7684\u4e00\u56e2\u9ed1\u6c14\u4e5f\u8d8a\u6765\u8d8a\u6d53\uff0c\u7ffc\u4ef2\u725f\u5403\u4e86\u4e00\u60ca\uff0c\u89c1\u4ed6\u6447\u6447\u6b32\u5760\uff0c \u8fde\u5fd9\u5c06\u4ed6\u6276\u4f4f\u9053\uff1a\u201c\u4e18\u5144\uff0c\u4f60\u600e\u4e48\u4e86\uff1f\u7ffc\u4ef2\u725f\u7c97\u901a\u533b\u7406\uff0c\u89e6\u53ca\u4ed6\u7684\u8eab\u5b50\uff0c\u53ea\u89c9\u4ed6\u6d51\u8eab\u53d1\u70ed\uff0c \u518d\u770b\u4ed6\u8138\u8272\uff0c\u5206\u660e\u662f\u5df2\u4e2d\u4e86\u5267\u6bd2\uff01 \u3000\u3000\u7ffc\u4ef2\u725f\u53eb\u9053\uff1a\u201c\u4e18\u5144\uff0c\u6709\u8bdd\u597d\u8bf4\uff0c\u4f55\u5fc5\u5982\u6b64\uff1f\u89e3\u836f\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u4f60\u5feb\u8bf4\uff01\u201d\u4e18\u5ca9\u53ea\u662f\u6307 \u6307\u90a3\u4e2a\u5973\u5b69\uff0c\u518d\u6307\u6307\u81ea\u5df1\u7684\u5fc3\u53e3\uff0c\u8868\u793a\u4ed6\u7684\u5fc3\u613f\u5c31\u53ea\u662f\u8981\u7ffc\u4ef2\u725f\u5e87\u62a4\u8fd9\u4e2a\u5973\u5b69\u3002 \u3000\u3000\u7ffc\u4ef2\u725f\u5c06\u9152\u58f6\u4e00\u63b7\uff0c\u4f46\u542c\u5f97\u201c\u78b0\u201d\u7684\u4e00\u58f0\uff0c\u9152\u58f6\u788e\u88c2\uff0c\u7acb\u65f6\u6fc0\u8d77\u4e00\u6e9c\u706b\u82b1\uff0c\u58f6\u4e2d\u6240\u76db\u7684 \u7adf\u662f\u6bd2\u9152\uff01\u539f\u6765\u4e18\u5ca9\u7ed9\u81ea\u5df1\u659f\u7684\u662f\u6bd2\u9152\uff0c\u7ed9\u7ffc\u4ef2\u725f\u7684\u5219\u662f\u4ece\u53e6\u4e00\u4e2a\u9152\u58f6\u4e2d\u659f\u51fa\u6765\u7684\u3002 \u3000\u3000\u4e18\u5ca9\u4ee5\u6b7b\u6258\u5b64\uff0c\u7ffc\u4ef2\u725f\u81ea\u662f\u96be\u8fc7\u4e4b\u6781\uff0c\u8fd9\u65f6\u4e18\u5ca9\u7684\u8109\u606f\u5df2\u7edd\uff0c\u8eab\u4f53\u4e5f\u4ece\u9ad8\u70ed\u800c\u53d8\u4e3a\u51b0 \u51c9\uff0c\u6b64\u65f6\u7eb5\u6709\u89e3\u836f\uff0c\u4e5f\u96be\u4ee5\u6551\u6d3b\u4e86\u3002\u7ffc\u4ef2\u725f\u53ea\u597d\u5728\u4ed6\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u4e18\u5144\uff0c\u6211\u4e00\u5b9a\u4f1a\u597d\u597d\u5b89\u7f6e \u8fd9\u4e2a\u5973\u5b69\u5b50\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\uff0c\u8d76\u5feb\u8bf4\u5427\u3002\u201d \u3000\u3000\u4e18\u5ca9\u6781\u529b\u6323\u624e\uff0c\u65ad\u65ad\u7eed\u7eed\u4ed6\u8bf4\u9053\uff1a\u201c\u5979\u8eab\u4e0a\u7684\u68c9\u8884\uff0c\u662f\u5979\u6bcd\u4eb2\u7ed9\u5979\u7684\u3002\u5979\u8fd8\u6709\u4e00\u4e2a\u5144 \u5f1f\uff0c\u540c\u65f6\u51fa\u4e16\u7684\uff0c\u662f\u9648\u7559\u53bf\u53f6\u541b\u5c71\u6536\u517b\uff0c\u4e5f\u6709\u4e00\u4ef6\u540c\u6837\u5f0f\u6837\u7684\u68c9\u8884\uff0c\u5374\u662f\u9752\u7f0e\u9762\u7684\uff0c\u4f60\u5e26\u5979 \u53bb\u627e\u5979\u5927\u54e5\uff0c\u8fd9\u4e24\u4ef6\u68c9\u8884\uff0c\u5173\u7cfb\u91cd\u5927\uff0c\u4f60\u3001\u4f60\u3001\u4f60\u8981\u2026\u2026\u201d \u3000\u3000\u7ffc\u4ef2\u725f\u5927\u58f0\u9053\uff1a\u201c\u4f60\u8981\u6211\u505a\u4ec0\u4e48\uff1f\u201d\u53ef\u662f\u4e18\u5ca9\u8bf4\u5230\u6b64\u5904\uff0c\u5df2\u7136\u6c14\u7edd\uff0c\u4e0d\u80fd\u56de\u7b54\u8fd9\u53e5\u95ee\u8bdd \u4e86\uff01 \u3000\u3000\u7ffc\u4ef2\u725f\u4e00\u751f\u6d6a\u8ff9\u6c5f\u6e56\uff0c\u78b0\u5230\u7684\u610f\u5916\u4e4b\u4e8b\u4e0d\u77e5\u591a\u5c11\uff0c\u4f46\u5374\u6ca1\u4e00\u4ef6\u6bd4\u5f97\u4e0a\u8fd9\u4ef6\u4e8b\u7684\u53e4\u602a\u79bb\u5947\uff01 \u3000\u3000\u7b2c\u4e00\u4ef6\u4ee4\u7ffc\u4ef2\u725f\u731c\u60f3\u4e0d\u900f\u7684\u662f\u4e18\u5ca9\u4f55\u4ee5\u800c\u6b7b\uff1f\u82e5\u8bf4\u662f\u4e3a\u4ec7\u4eba\u5bfb\u4ec7\uff0c\u4e3a\u4f55\u4ed6\u89c1\u4e86\u7ffc\u4ef2\u725f\u4e4b \u540e\uff0c\u5374\u4e0d\u6c42\u63f4\uff0c\u800c\u4e14\u4e34\u6b7b\u4e5f\u4e0d\u8bf4\u660e\uff1f \u3000\u3000\u7b2c\u4e8c\u4ef6\u731c\u60f3\u4e0d\u900f\u7684\u662f\u90a3\u5973\u5b69\u7684\u8eab\u4e16\uff0c\u636e\u4e18\u5ca9\u6240\u8bf4\uff0c\u8fd9\u5973\u5b69\u5b50\u662f\u4ed6\u7684\u4f43\u6237\u7684\u5973\u513f\uff0c\u4f46\u4ece\u5979 \u90a3\u4ef6\u68c9\u8884\u770b\u6765\uff0c\u867d\u7136\u7ea2\u7f0e\u5b50\u68c9\u8884\u4e5f\u5e76\u975e\u5f88\u503c\u94b1\u7684\u4e1c\u897f\uff0c\u5374\u4e0d\u662f\u4e00\u4e2a\u4f43\u6237\u820d\u5f97\u4e70\u7684\u3002\u4e18\u5ca9\u4e34\u6b7b \u65f6\u53c8\u8bf4\u8fd9\u4ef6\u68c9\u8884\u5173\u7cfb\u91cd\u5927\uff0c\u90a3\u5c31\u66f4\u5947\u602a\u4e86\u3002\u7ffc\u4ef2\u725f\u662f\u4e2a\u8001\u6c5f\u6e56\u4e86\uff0c\u5f53\u7136\u60f3\u5230\u8fd9\u5973\u5b69\u662f\u53e6\u6709\u6765 \u5386\uff0c\u8bf4\u4e0d\u5b9a\u4fbf\u662f\u5979\u7ed9\u4e18\u5ca9\u62db\u60f9\u4e86\u4ec7\u5bb6\u3002\u53ef\u662f\uff0c\u82e5\u7136\u5982\u6b64\uff0c\u4e18\u5ca9\u4e3a\u4f55\u8981\u53e6\u7f16\u4e00\u5957\u5047\u8bdd\uff0c\u5374\u5c06\u5979 \u7684\u8eab\u4e16\u9690\u7792\u5462\uff1f \u3000\u3000\u4e18\u5bb6\u53ea\u6709\u4e00\u4e2a\u8001\u4ec6\u4eba\uff0c\u7ffc\u4ef2\u725f\u76d8\u95ee\u4ed6\uff0c\u90a3\u4ec6\u4eba\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u8001\u7237\u4e3a\u4f55\u8981\u6b7b\uff0c\u524d\u51e0\u65e5\u5bb6 \u91cc\u95f9\u8fc7\u4e00\u6b21\u8d3c\uff0c\u4ee5\u540e\u8001\u7237\u5c31\u95f7\u95f7\u4e0d\u4e50\u3002\u4ed6\u4eca\u65e9\u4ea4\u4e86\u4e00\u5c01\u4fe1\u7ed9\u6211\uff0c\u53eb\u6211\u5f85\u5ba2\u4eba\u8d70\u540e\u624d\u62c6\u5f00\u6765\u3002 \u6211\u4e5f\u6b63\u5728\u5947\u602a\u5462\uff01\u201d \u3000\u3000\u7ffc\u4ef2\u725f\u9053\uff1a\u201c\u5feb\u62c6\u5f00\u6765\u770b\uff0c\u8fd9\u5b9a\u662f\u9057\u4e66\u3002\u201d\u62c6\u5f00\u4e00\u770b\uff0c\u4e0d\u9519\uff0c\u786e\u662f\u9057\u4e66\uff0c\u4f46\u53ea\u662f\u5631\u6258\u90a3 \u8001\u4ec6\u4eba\u5c06\u4ed6\u7684\u5bb6\u4ea7\u5982\u4f55\u5206\u914d\u7684\uff0c\u5927\u90e8\u4efd\u4f5c\u5584\u4e8b\uff0c\u5c0f\u90e8\u4efd\u8d60\u7ed9\u8d2b\u7a77\u7684\u4e61\u4eb2\uff0c\u8fd8\u6709\u4e00\u5c0f\u90e8\u4efd\u5219\u662f \u7559\u7ed9\u90a3\u4ec6\u4eba\u517b\u8001\uff0c\u5e76\u65e0\u4e00\u8bed\u6d89\u53ca\u4ed6\u7684\u6b7b\u56e0\u548c\u90a3\u5973\u5b69\u8eab\u4e16\u4e4b\u8c1c\uff0c\u95ee\u90a3\u4ec6\u4eba\uff0c\u90a3\u4ec6\u4eba\u4e5f\u8bf4\u8fd9\u5973\u5b69 \u662f\u4e18\u5ca9\u4e09\u5e74\u524d\u4eb2\u81ea\u62b1\u56de\u6765\u7684\uff0c\u4ec0\u4e48\u6765\u5386\uff0c\u4ece\u672a\u5bf9\u4ed6\u8a00\u53ca\u3002 \u3000\u3000\u7ffc\u4ef2\u725f\u518d\u95ee\u53ca\u95f9\u8d3c\u4e4b\u4e8b\uff0c\u90a3\u4ec6\u4eba\u9053\uff1a\u201c\u6211\u542c\u5230\u58f0\u54cd\u65f6\uff0c\u90a3\u8d3c\u4eba\u5df2\u7ecf\u8d70\u4e86\u3002\u201d\u7ffc\u4ef2\u725f\u89c1\u95ee \u4e0d\u51fa\u6240\u4ee5\u7136\u6765\uff0c\u53ea\u597d\u6000\u7740\u6ee1\u8179\u7591\u56e2\uff0c\u5c06\u90a3\u5973\u5b69\u5e26\u8d70\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5947\u602a\u7684\u4e8b\u60c5\u8fd8\u672a\u7ec8\u7ed3\u3002\u7ffc\u4ef2\u725f\u4f9d\u4e18\u5ca9\u4e34\u6b7b\u7684\u5631\u6258\uff0c\u5230\u6cb3\u5357\u9648\u7559\u53bf\u53f6\u541b\u5c71\u5bb6\u53bb\u8bbf\u5bfb \u90a3\u5973\u5b69\u7684\u80de\u5144\uff0c\u53f6\u541b\u5c71\u4e43\u9752\u57ce\u540d\u5bbf\u6768\u5e84\u7684\u5f1f\u5b50\uff0c\u4e5f\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u5c31\u5728\u7ffc\u4ef2\u725f\u6765\u5230\u7684\u524d\u4e00\u665a\u66b4 \u6bd9\u800c\u4ea1\uff0c\u4ed6\u90a3\u62b1\u517b\u7684\u7537\u5b69\u5b50\u4e0d\u77e5\u53bb\u5411\u3002 \u3000\u3000\u7ffc\u4ef2\u725f\u662f\u4e10\u5e2e\u5e2e\u4e3b\uff0c\u4ed6\u89c9\u5f97\u8ba9\u4e00\u4e2a\u5973\u5b69\u5b50\u5728\u53eb\u5316\u7fa4\u4e2d\u53ae\u6df7\u3002\u603b\u662f\u4e0d\u59a5\uff0c\u56e0\u6b64\u4fbf\u5c06\u5979\u5e26\u4e0a \u6c13\u5c71\uff0c\u8bf7\u5e08\u59b9\u8c37\u4e4b\u534e\u6536\u7559\u5979\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u5341\u5206\u6b22\u559c\u8fd9\u4e2a\u5973\u5b69\uff0c\u540c\u65f6\u56e0\u8fd9\u5973\u5b69\u800c\u53c8\u60f3\u5230\u81ea\u5df1\u7684\u8eab\u4e16\uff0c\u5979\u81ea\u5df1\u4e5f\u662f\u88ab\u4eba\u6536\u517b\u7684 \u5b64\u513f\uff0c\u56e0\u5df1\u53ca\u4eba\uff0c\u5f53\u7136\u5c31\u66f4\u613f\u610f\u6536\u7559\u8fd9\u5b69\u5b50\u4e86\u3002 \u3000\u3000\u5979\u95ee\u8fd9\u5973\u5b69\u5b50\u7684\u540d\u5b57\uff0c\u8fd9\u5973\u5b69\u7684\u7b54\u8bdd\u4e5f\u5f88\u5947\u602a\uff0c\u5979\u8bf4\uff1a\u201c\u6211\u6ca1\u540d\u5b57\uff0c\u4e18\u7237\u7237\u5524\u6211\u4f5c\u83b2 \u513f\u3002\u4e18\u7237\u7237\u8bf4\u6211\u53ea\u662f\u6682\u65f6\u5bc4\u517b\u4ed6\u5bb6\uff0c\u4e0d\u613f\u610f\u6211\u6539\u540d\u6362\u59d3\uff0c\u6240\u4ee5\u6211\u4e5f\u4e0d\u59d3\u4e18\u3002\u201d\u8c37\u4e4b\u534e\u9053\uff1a \u201c\u6ca1\u6709\u59d3\u4e0d\u597d\u547c\u5524\u3002\u4f60\u8ddf\u6211\u59d3\u4e86\u5427\uff0c\u505a\u6211\u7684\u5973\u513f\u597d\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b69\u5b50\u4e0e\u8c37\u4e4b\u534e\u4e5f\u6781\u6295\u7f18\uff0c\u7acb\u5373\u4fbf\u8dea\u4e0b\u53bb\u78d5\u5934\u8ba4\u5979\u505a\u5988\u5988\uff0c\u8c37\u4e4b\u534e\u7ed9\u5979\u53d6\u4e2a\u540d\u5b57\u53eb\u505a \u8c37\u4e2d\u83b2\uff0c\u90a3\u662f\u56e0\u4e3a\u91d1\u4e16\u9057\u5f53\u5e74\u66fe\u5c06\u83b2\u4e0e\u5979\u4f5c\u6bd4\uff0c\u52c9\u52b1\u5979\u201c\u51fa\u6c61\u6ce5\u800c\u4e0d\u67d3\u201d\u7684\uff0c\u5979\u89c9\u5f97\u8fd9\u5973\u5b69 \u5b50\u7684\u8eab\u4e16\u4e0e\u5979\u76f8\u4f3c\uff0c\u56e0\u800c\u7ed9\u5979\u53d6\u4e86\u8fd9\u4e2a\u540d\u5b57\u3002\u4f46\u5979\u5374\u79c1\u5fc3\u7977\u544a\uff0c\u8fd9\u5973\u5b69\u5b50\u7684\u4e0d\u77e5\u540d\u7684\u751f\u8eab\u7236 \u6bcd\uff0c\u4e43\u662f\u6e05\u767d\u4eba\u5bb6\uff0c\u4f46\u613f\u8fd9\u5973\u5b69\u5b50\u4e0d\u8981\u548c\u5979\u6709\u76f8\u540c\u7684\u547d\u8fd0\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u53e6\u5916\u8fd8\u6709\u4e00\u4e2a\u5fc3\u613f\uff0c\u5979\u53d7\u4e49\u7236\u4e24\u6e56\u5927\u4fa0\u8c37\u6b63\u670b\u629a\u517b\uff0c\u65e0\u4ee5\u4e3a\u62a5\uff0c\u8c37\u6b63\u670b\u6ca1\u6709\u7559\u4e0b \u540e\u55e3\uff0c\u800c\u5979\u53c8\u662f\u4eca\u751f\u4eca\u4e16\u51b3\u4e0d\u7ed3\u5a5a\u7684\u4e86\uff0c\u56e0\u6b64\u5979\u624d\u60f3\u5230\u6536\u517b\u4e00\u4e2a\u5973\u513f\uff0c\u5f85\u5979\u957f\u5927\u6210\u4eba\uff0c\u62db\u8d58 \u4e00\u95e8\u4f73\u5a7f\uff0c\u4e5f\u597d\u7ee7\u627f\u8c37\u5bb6\u7684\u9999\u70df\u3002 \u3000\u3000\u8fd9\u51e0\u5e74\u6765\u5979\u4e00\u76f4\u4e3a\u7740\u5973\u513f\u7684\u795e\u79d8\u8eab\u4e16\u800c\u62c5\u5fe7\uff0c\u60f3\u4e0d\u5230\u8981\u6765\u7684\u7ec8\u4e8e\u6765\u4e86\uff01 \u3000\u3000\u5982\u4eca\u5979\u9762\u5bf9\u7740\u8fd9\u4e2a\u73e0\u5149\u5b9d\u6c14\u7684\u63d0\u7763\u5927\u592a\uff0c\u800c\u8fd9\u4e2a\u7f2a\u592b\u4eba\u5728\u9053\u51fa\u4e86\u5979\u7684\u79c1\u60c5\u4e4b\u540e\uff0c\u7acb\u5373\u4fbf \u5411\u5979\u95ee\u53ca\u6240\u6536\u517b\u7684\u5b64\u5973\u4e86\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u4e0d\u7531\u5f97\u5fc3\u5934\u4e00\u9707\uff0c\u53ea\u5f97\u7b54\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u6536\u517b\u4e86\u4e00\u5b64\u5973\uff0c\u4e0d\u77e5\u592b\u4eba\u4ece\u4f55\u5f97\u77e5\uff1f\u201d \u3000\u3000\u7f2a\u592b\u4eba\u559c\u5f62\u4e8e\u8272\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e3a\u4e86\u7275\u6302\u8fd9\u5bf9\u5b69\u5b50\uff0c\u65e5\u524d\u6d3e\u51fa\u5fc3\u8179\u4e4b\u4eba\uff0c\u5230\u4e2d\u725f\u53bf \u53bb\uff0c\u60f3\u63a5\u56de\u8fd9\u5bf9\u5b69\u5b50\uff0c\u53e6\u4f5c\u5b89\u7f6e\uff0c\u514d\u5f97\u4ed6\u4eec\u5728\u8d2b\u5bb6\u53d7\u82e6\u3002\u60f3\u4e0d\u5230\u6211\u90a3\u5976\u5988\u7684\u513f\u5b50\u5df2\u7ecf\u6b7b\u4e86\uff0c \u7ed9\u6211\u529e\u7406\u7684\u4eba\uff0c\u8d39\u5c3d\u5fc3\u529b\u67e5\u8bbf\uff0c\u624d\u67e5\u51fa\u8fd9\u4e2a\u8be5\u6b7b\u7684\u5976\u5988\u7684\u513f\u5b50\uff0c\u5df2\u5c06\u8fd9\u5bf9\u5b69\u5b50\u9001\u4e86\u7ed9\u4eba\uff0c\u513f \u5b50\u9001\u7ed9\u8c01\u5bb6\uff1f\u8fc4\u4eca\u5c1a\u672a\u67e5\u5f97\u51fa\u6765\uff0c\u53ea\u77e5\u5973\u513f\u5374\u662f\u9001\u7ed9\u4ed6\u7684\u7530\u4e3b\uff0c\u4e2d\u725f\u53bf\u7684\u4e18\u5ca9\u3002\u800c\u8fd9\u4e18\u5ca9\u53c8 \u5728\u4e09\u5e74\u524d\u6b7b\u4e86\u3002 \u3000\u3000\u636e\u4e18\u5ca9\u7684\u8001\u4ec6\u4eba\u8bf4\uff0c\u5728\u4ed6\u4e34\u6b7b\u4e4b\u65f6\uff0c\u53c8\u628a\u6211\u7684\u5973\u513f\u4ea4\u7ed9\u4e00\u4e2a\u8001\u53eb\u5316\u5e26\u8d70\u4e86\u3002\u8fd9\u4e2a\u8001\u53eb\u5316 \u662f\u5357\u4e10\u5e2e\u7684\u5e2e\u4e3b\u7ffc\u4ef2\u725f\u3002\u6211\u4e08\u592b\u662f\u4e2a\u6b66\u5b98\uff0c\u4e10\u5e2e\u662f\u4e0e\u671d\u5ef7\u4f5c\u5bf9\u7684\u6c5f\u6e56\u4e0a\u4e00\u5927\u5e2e\u6d3e\uff0c\u592b\u5987\u4e4b \u95f4\uff0c\u6709\u65f6\u4e5f\u4f1a\u8c08\u8d77\u4e10\u5e2e\u7684\u4e8b\u60c5\uff0c\u56e0\u6b64\u6211\u4e5f\u591a\u5c11\u77e5\u9053\u4e00\u70b9\u4e10\u5e2e\u7684\u89c4\u77e9\uff0c\u4e10\u5e2e\u662f\u4ece\u6765\u4e0d\u6536\u5973\u5f1f\u5b50 \u7684\uff0c\u7ffc\u5e2e\u4e3b\u662f\u4e2a\u5355\u8eab\u7684\u8001\u53eb\u5316\uff0c\u5e26\u7740\u4e00\u4e2a\u5973\u5b69\u5b50\u4e5f\u4e0d\u65b9\u4fbf\uff0c\u7ffc\u5e2e\u4e3b\u662f\u4f60\u8c37\u638c\u95e8\u7684\u5e08\u5144\uff0c\u56e0\u6b64 \u6211\u624d\u4f1a\u60f3\u5230\u6211\u8fd9\u4e2a\u5973\u513f\uff0c\u7ffc\u5e2e\u4e3b\u53ef\u80fd\u662f\u5df2\u6258\u4f60\u6536\u517b\u4e86\u3002\u8bf7\u4f60\u653e\u5fc3\uff0c\u6211\u4e0d\u7ba1\u4f60\u4eec\u662f\u5426\u53cd\u5bf9\u671d \u5ef7\uff0c\u6211\u53ea\u6c42\u5f97\u56de\u81ea\u5df1\u7684\u5b69\u5b50\u3002\u5982\u4eca\uff0c\u8c37\u638c\u95e8\u65e2\u7136\u627f\u8ba4\u4e86\u6709\u6b64\u4e00\u4e8b\uff0c\u8fd9\u4e2a\u5b64\u5973\uff0c\u4e00\u5b9a\u662f\u6211\u7684\u4eb2 \u8eab\u9aa8\u8089\u4e86\uff01\u6211\u8981\u91cd\u91cd\u7684\u62a5\u7b54\u8c37\u638c\u95e8\u6536\u517b\u6211\u5973\u4e4b\u6069\uff0c\u73b0\u5728\u5c31\u8bf7\u8c37\u638c\u95e8\u5c06\u6211\u7684\u5973\u513f\u4ea4\u56de\u7ed9\u6211\u5427\uff01\u201d \u3000\u3000\u7f2a\u592b\u4eba\u6240\u8bf4\u7684\u4e8b\u5b9e\uff0c\u4e0e\u7ffc\u4ef2\u725f\u4ece\u4e18\u5ca9\u90a3\u513f\u542c\u6765\u7684\u4e00\u4e00\u7b26\u5408\uff0c\u8c37\u4e4b\u534e\u542c\u5f97\u5bd2\u6c14\u76f4\u900f\u5fc3\u5934\uff0c \u6697\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u7f2a\u592b\u4eba\u8bb2\u5979\u81ea\u5df1\u7684\u4e8b\uff0c\u867d\u7136\u672a\u5fc5\u786e\u5b9e\uff0c\u4f46\u5bf9\u8fd9\u5b69\u5b50\u7684\u4e8b\uff0c\u5979\u8bb2\u7684\u5374\u662f\u65e0\u53ef\u7f6e \u7591\uff0c\u5509\uff0c\u96be\u9053\u8fd9\u5b69\u5b50\u5f53\u771f\u4e5f\u662f\u547d\u4e2d\u6ce8\u5b9a\uff0c\u4e0e\u6211\u4e00\u822c\uff1f\u201d\u5979\u518d\u671b\u4e86\u90a3\u7f2a\u592b\u4eba\u4e00\u773c\uff0c\u5979\u7709\u5fc3\u4e0a\u90a3 \u56e2\u6de1\u6de1\u7684\u9ed1\u6c14\uff0c\u8d8a\u53d1\u770b\u5f97\u6e05\u695a\u4e86\uff0c\u8c37\u4e4b\u534e\u671b\u591a\u4e24\u773c\uff0c\u5fc3\u4e2d\u4e0d\u7531\u81ea\u5df1\u7684\u8d77\u4e86\u618e\u6076\u4e4b\u611f\uff0c\u7981\u4e0d\u4f4f \u53c8\u60f3\u9053\uff1a\u201c\u8fd9\u5973\u4eba\u5996\u58f0\u5996\u6c14\uff0c\u7edd\u4e0d\u4f1a\u662f\u4e2a\u597d\u4eba\uff0c\u6211\u600e\u80fd\u8ba9\u83b2\u513f\u8ddf\u5979\uff1f\u201d \u3000\u3000\u90a3\u7f2a\u592b\u4eba\u4f3c\u662f\u731c\u5230\u4e86\u8c37\u4e4b\u534e\u7684\u5fc3\u610f\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u8c37\u638c\u95e8\u6562\u60c5\u662f\u820d\u4e0d\u5f97\u8fd9\u4e2a\u5b69\u5b50\u4e48\uff1f\u82e5 \u8499\u8d50\u60e0\uff0c\u9aa8\u8089\u56e2\u5706\uff0c\u6211\u4eec\u6bcd\u5973\u90fd\u4f1a\u6c38\u611f\u5927\u6069\uff0c\u51b3\u4e0d\u81f3\u4e8e\u5fd8\u8bb0\u4f60\u7684\u3002\u5b69\u5b50\u957f\u5927\u4e86\uff0c\u6211\u4e5f\u4f1a\u53eb\u5979 \u5e38\u5e38\u6765\u63a2\u671b\u4f60\u7684\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u542c\u5979\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u751a\u4e3a\u8bda\u6073\uff0c\u4e0d\u89c9\u88ab\u5979\u611f\u52a8\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5979\u7eb5\u7136\u662f\u90aa\u6d3e\u4e2d\u4eba\uff0c \u884c\u4e3a\u4e0d\u7aef\uff0c\u4f46\u5979\u7a76\u7adf\u662f\u83b2\u513f\u7684\u6bcd\u4eb2\uff0c\u6211\u5c82\u80fd\u963b\u6b62\u5979\u4eec\u6bcd\u5973\u76f8\u8ba4\uff1f\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u806a\u660e \u4f36\u4fd0\uff0c\u6211\u7684\u786e\u662f\u6709\u70b9\u820d\u4e0d\u5f97\u5979\u3002\u4f46\u5979\u5f97\u89c1\u751f\u8eab\u4e4b\u6bcd\uff0c\u6211\u4ecd\u7136\u662f\u66ff\u5979\u6b22\u559c\u7684\u3002\u592b\u4eba\uff0c\u4f60\u8bf7\u7a0d \u5019\u3002\u201d \u3000\u3000\u7f2a\u592b\u4eba\u9053\uff1a\u201c\u5982\u6b64\uff0c\u5c31\u8bf7\u4f60\u5e26\u8fd9\u5b69\u5b50\u51fa\u6765\u3002\u201d\u773c\u89d2\u7709\u68a2\u90fd\u5145\u6ee1\u4e86\u7b11\u610f\uff0c\u4f46\u90a3\u559c\u60a6\u4e4b\u60c5\uff0c \u5374\u663e\u5f97\u6709\u4e9b\u5f02\u6837\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5979\u7684\u7b11\u5bb9\uff0c\u7ed9\u8c37\u4e4b\u534e\u8fd9\u6837\u7684\u611f\u89c9\uff1a\u4e0d\u50cf\u662f\u53d1\u81ea\u5185\u5fc3\u7684\u3001\u60b2\u559c\u4ea4 \u96c6\u7684\u7684\u795e\u6001\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u592b\u4eba\uff0c\u4f60\u8fd9\u5b69\u5b50\u53ef\u6709\u4ec0\u4e48\u8bb0\u8ba4\u5417\uff1f\u201d\u592b\u4eba\u6014\u4e86\u4e00\u6014\uff0c \u9053\uff1a\u201c\u4ec0\u4e48\u8bb0\u8ba4\uff1f\u6211\u751f\u4e0b\u4e86\u8fd9\u5bf9\u5b69\u5b50\u4e4b\u540e\uff0c\u5c31\u4ea4\u7ed9\u5976\u5988\u4e86\u3002\u4ed6\u4eec\u8eab\u4e0a\uff0c\u5230\u5e95\u6709\u4f55\u7279\u70b9\uff0c\u6211\u5b9e \u5728\u672a\u66fe\u8be6\u5bdf\u3002\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u603b\u8be5\u7559\u4e0b\u4ec0\u4e48\u4fe1\u7269\uff0c\u7ed9\u90a3\u4e24\u4e2a\u5b69\u5b50\uff0c\u4ee5\u5f85\u5c06\u6765\u76f8\u8bc6\u4f5c \u4e3a\u51ed\u8bc1\u5427\uff1f\u201d \u3000\u3000\u7f2a\u592b\u4eba\u7b11\u9053\uff1a\u201c\u8c37\u638c\u95e8\uff0c\u4f60\u771f\u662f\u7ec6\u5fc3\uff0c\u6015\u6211\u5192\u8ba4\u5b69\u5b50\u4e48\uff1f\u4e5f\u597d\uff0c\u63d0\u9192\u6211\uff0c\u6211\u60f3\u8d77\u6765\u4e86\uff0c \u6211\u7ed9\u8fd9\u5bf9\u5b69\u5b50\u4eb2\u624b\u7f1d\u4e86\u4e24\u4ef6\u68c9\u8884\uff0c\u662f\u82cf\u7ee3\u7f0e\u9762\u7684\uff0c\u6211\u66fe\u90d1\u91cd\u5631\u6258\u5976\u5988\uff0c\u53eb\u5979\u5343\u4e07\u7ed9\u5b69\u5b50\u7559 \u7740\uff0c\u4e0d\u77e5\u4f60\u6536\u517b\u8fd9\u5b69\u5b50\u7684\u65f6\u5019\uff0c\u90a3\u4ef6\u68c9\u8884\u53ef\u8fd8\u5728\u4e48\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u5fc3\u5934\u4e00\u6c89\uff0c\u60f3\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u771f\u662f\u5979\u7684\u5b69\u5b50\u4e86\uff1f\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u592b\u4eba\u60f3\u8fd8 \u8bb0\u5f97\u8fd9\u4e24\u4ef6\u68c9\u8884\u662f\u4ec0\u4e48\u989c\u8272\u7684\u5427\uff1f\u201d\u7f2a\u592b\u4eba\u60f3\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u7537\u5b69\u5b50\u4f3c\u4e4e\u662f\u7ea2\u7f0e\u9762\u7684\uff0c\u5973 \u5b69\u5b50\u7684\u662f\u9752\u7f0e\u9762\u7684\uff0c\u53ea\u662f\u5e74\u6df1\u6708\u4e45\uff0c\u8bb0\u5f97\u4e0d\u5927\u6e05\u695a\uff0c\u53ea\u6015\u8bf4\u9519\u4e5f\u4e0d\u4e00\u5b9a\u3002\u201d\u7f2a\u592b\u4eba\u6240\u8bf4\uff0c\u6070 \u6070\u8981\u6389\u8f6c\u8fc7\u6765\u624d\u5bf9\u3002\u8c37\u4e4b\u534e\u767b\u65f6\u8d77\u4e86\u6000\u7591\uff0c\u5fc3\u9053\uff1a\u201c\u5979\u521a\u624d\u8bf4\u5f97\u90a3\u7b49\u90d1\u91cd\uff0c\u8981\u51ed\u8fd9\u4e24\u4ef6\u68c9\u8884 \u4f5c\u4e3a\u4fe1\u7269\uff0c\u600e\u6709\u4e0d\u6e05\u695a\u4e4b\u7406\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u7d27\u8ddf\u7740\u8ffd\u95f4\u9053\uff1a\u201c\u989c\u8272\u8bb0\u4e0d\u6e05\u695a\u8fd9\u662f\u5c0f\u4e8b\uff0c\u4f46\u90a3\u4ef6\u68c9\u8884\u8fd8\u6709\u4e9b\u7279\u522b\u4e4b\u5904\uff0c\u4e0d\u77e5\u592b \u4eba\u53ef\u8fd8\u8bb0\u5f97\uff1f\u201d \u3000\u3000\u7f2a\u592b\u4eba\u4e0d\u89c9\u7684\u9732\u51fa\u60f6\u60d1\u7684\u795e\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u68c9\u8884\u5c31\u662f\u68c9\u8884\uff0c\u4e0d\u7528\u5f97\u597d\u4e9b\u7f62\u4e86\uff0c\u6709\u4ec0\u4e48\u7279 \u522b\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u68c9\u8884\u65e2\u662f\u592b\u4eba\u4eb2\u624b\u88c1\u5236\u7684\uff0c\u592b\u4eba\u603b\u8be5\u60f3\u8d77\uff0c\u6bd4\u5982\u8bf4\u5b83\u7684\u5f0f\u6837\u5982\u4f55\uff0c \u6709\u51e0\u9897\u94ae\u6263\uff0c\u94ae\u6263\u7684\u8d28\u6599\u600e\u6837\u7b49\uff0c\u592b\u4eba\u4ed4\u7ec6\u60f3\u60f3\uff0c\u6216\u8005\u603b\u6709\u4e00\u70b9\u548c\u666e\u901a\u7684\u68c9\u8884\u4e0d\u540c\u5427\uff1f\u201d \u3000\u3000\u7f2a\u592b\u4eba\u9762\u8272\u767d\u91cc\u6cdb\u7ea2\uff0c\u6e29\u9053\uff1a\u201c\u8fd9\u4e9b\u7410\u5c51\u7684\u7269\u4e8b\uff0c\u6211\u54ea\u80fd\u8bb0\u5f97\u8fd9\u8bb8\u591a\uff1f\u8fde\u94ae\u6263\u6709\u51e0\u9897\u90fd \u8981\u95ee\u5230\uff0c\u8c37\u638c\u95e8\uff0c\u4f60\u4e0d\u662f\u5b58\u5fc3\u62ff\u6211\u6d88\u9063\u4e48\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u5fae\u5fae\u4e00\u7b11\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u90a3\u51e0\u9897\u94ae\u6263\uff0c\u662f\u65e0\u4ef7\u4e4b\u73cd\uff0c\u636e\u8bc6\u8d27\u7684\u4eba\u8bf4\uff0c\u7687\u5bab\u5927\u5185\uff0c \u53ea\u6015\u4e5f\u672a\u5fc5\u4f1a\u6709\uff01\u201d \u3000\u3000\u539f\u6765\u90a3\u4ef6\u68c9\u8884\u6709\u4e03\u9897\u94ae\u6263\uff0c\u4e4d\u773c\u770b\u53bb\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u4e4b\u5904\uff0c\u4f3c\u662f\u666e\u901a\u7684\u94dc\u8d28\u94ae\u6263\uff0c\u6709 \u4e00\u5929\uff0c\u8c37\u4e4b\u534e\u6015\u68c9\u8884\u5728\u7bb1\u5e95\u53d1\u9709\uff0c\u62ff\u51fa\u6765\u6652\uff0c\u9633\u5149\u5e95\u4e0b\uff0c\u5ffd\u5730\u53d1\u73b0\u94ae\u6263\u53cd\u5c04\u51fa\u5f02\u6837\u7684\u5149\u8f89\uff0c \u7ec6\u5fc3\u9274\u522b\uff0c\u975e\u94dc\u975e\u94c1\uff0c\u7adf\u4e0d\u77e5\u662f\u4ec0\u4e48\u91d1\u5c5e\uff0c\u800c\u4e14\u8fd8\u6709\u4e00\u6837\u7279\u522b\u4e4b\u5904\uff0c\u91d1\u5c5e\u7684\u4e1c\u897f\uff0c\u5728\u9633\u5149\u4e0b \u6652\u5f97\u4e45\u4e86\uff0c\u603b\u4f1a\u6709\u70b9\u53d1\u70ed\uff0c\u800c\u90a3\u51e0\u9897\u94ae\u6263\uff0c\u5374\u662f\u89e6\u624b\u51b0\u51c9\uff0c\u6bd4\u8d77\u672a\u6652\u4e4b\u524d\uff0c\u8fd8\u66f4\u4ee4\u4eba\u6709\u5bd2\u51b7 \u7684\u611f\u89c9\uff0c\u8c37\u4e4b\u534e\u8fd9\u624d\u5947\u602a\u8d77\u6765\u3002 \u3000\u3000\u4e5f\u6b63\u662f\u56e0\u6b64\uff0c\u8c37\u4e4b\u534e\u5bf9\u8fd9\u5b69\u5b50\u7684\u8eab\u4e16\u66f4\u589e\u52a0\u4e86\u795e\u79d8\u4e4b\u611f\uff0c\u5979\u66fe\u6697\u4e2d\u62ff\u8fd9\u4ef6\u68c9\u8884\u7ed9\u51e0\u4f4d\u89c1 \u591a\u8bc6\u5e7f\u7684\u524d\u8f88\u9274\u8d4f\uff0c\u540e\u6765\u78b0\u5230\u4e86\u6c5f\u5357\u533b\u9690\u53f6\u91ce\u9038\u624d\u8bc6\u5f97\u8fd9\u4e1c\u897f\u7684\u6765\u5386\u3002 \u3000\u3000\u539f\u6765\u8fd9\u51e0\u9897\u94ae\u6263\u5e76\u975e\u91d1\u5c5e\uff0c\u800c\u662f\u6606\u4ed1\u5c71\u7edd\u9876\u661f\u5bbf\u6d77\u6240\u7279\u6709\u7684\u201c\u5929\u5fc3\u77f3\u201d\uff0c\u8fd9\u79cd\u5947\u602a\u7684\u77f3 \u4e8e\u6781\u4e3a\u7a00\u7f55\uff0c\u4e14\u53c8\u6df7\u5728\u65e0\u6570\u6c99\u77f3\u4e4b\u4e2d\uff0c\u5373\u7b97\u5728\u57a6\u5bbf\u6d77\u8fb9\u4f4f\u4e0a\u4e00\u5e74\uff0c\u4e5f\u672a\u5fc5\u80fd\u627e\u5230\u4e00\u9897\u3002 \u3000\u3000\u636e\u53f6\u91ce\u9038\u6240\u85cf\u7684\u53e4\u4ee3\u533b\u5b66\u79d8\u672c\u4e0a\u8bf4\uff0c\u5929\u5fc3\u77f3\u82e5\u662f\u7814\u6210\u7c89\u672a\u3002\u7528\u70c8\u9152\u51b2\u670d\uff0c\u529f\u80fd\u80dc\u8fc7\u4efb\u4f55 \u8865\u836f\uff0c\u80fd\u957f\u7cbe\u795e\u3001\u65fa\u6c14\u8840\uff0c\u4f1a\u670d\u98df\u4e4b\u4eba\uff0c\u5f97\u5230\u4e00\u79cd\u8d85\u4e4e\u5bfb\u5e38\u7684\u529b\u91cf\uff0c\u53ef\u662f\u5929\u5fc3\u77f3\u53c8\u662f\u5929\u4e0b\u6700 \u71e5\u70ed\u7684\u836f\u7269\uff0c\u670d\u4e0b\u5c11\u8bb8\uff0c\u5c31\u53ef\u4ee5\u4ee4\u4eba\u5168\u8eab\u6eda\u70ed\u800c\u53d1\u72c2\uff01 \u3000\u3000\u56e0\u6b64\u5b83\u65e2\u662f\u529f\u80fd\u6781\u5927\u7684\u8865\u836f\u53c8\u662f\u5389\u5bb3\u975e\u5e38\u7684\u6bd2\u836f\uff0c\u5bfb\u5e38\u7684\u4eba\u5207\u4e0d\u53ef\u670d\u3002\u53ea\u6709\u5185\u529f\u5df2\u6709\u4e86 \u6df1\u539a\u7684\u57fa\u7840\u4e4b\u540e\uff0c\u624d\u53ef\u4ee5\u5229\u7528\u8fd9\u79cd\u836f\u7269\u6765\u7ec3\u8d85\u51e1\u5165\u5723\u7684\u6b66\u529f\uff0c\u6216\u8005\u5185\u529f\u7684\u6839\u5e95\u867d\u7136\u672a\u591f\uff0c\u4f46 \u80fd\u627e\u5230\u53e6\u4e00\u79cd\u836f\u7269\u53ef\u4ee5\u6d88\u9664\u5929\u5fc3\u77f3\u7684\u70ed\u6bd2\u7684\uff0c\u4e0e\u5b83\u540c\u670d\uff0c\u6700\u5c11\u4e5f\u53ef\u4ee5\u589e\u8fdb\u4e8c\u5341\u5e74\u529f\u529b\uff0c\u670d\u4e0b \u4e03\u9897\uff0c\u4fbf\u6210\u91d1\u521a\u4e0d\u574f\u4e4b\u8eab\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u8c37\u4e4b\u534e\u8bf4\u8fd9\u51e0\u9897\u94ae\u6263\u662f\u65e0\u4ef7\u4e4b\u73cd\uff0c\u786e\u662f\u4e1d\u6beb\u4e5f\u6ca1\u5938\u5927\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u79cd\u4ef7\u503c\uff0c\u90fd\u662f\u5bf9 \u6b66\u6797\u4e2d\u4eba\u624d\u6709\u7279\u6b8a\u610f\u4e49\uff0c\u8fd9\u70b9\u8c37\u5e7d\u7136\u4e0d\u4f1a\u5bf9\u5979\u660e\u8a00\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u6b64\u8a00\u4e00\u51fa\uff0c\u53ea\u89c1\u90a3\u7f2a\u592b\u4eba\u767b\u65f6\u4e00\u9707\uff0c\u9762\u8272\u5927\u53d8\uff0c\u7f2a\u592b\u4eba\u6124\u7136\u8bf4\u9053\uff1a\u201c\u8c37\u638c\u95e8\uff0c\u4f60 \u4e5f\u5fd2\u628a\u6211\u770b\u5f97\u5c0f\u4e86\u3002\u4f60\u5f53\u6211\u4e86\u89ca\u89ce\u73e0\u5b9d\u624d\u6765\u5192\u8ba4\u5b69\u5b50\u7684\u4e48\uff1f\u54fc\uff0c\u54fc\uff0c\u4f60\u82e5\u662f\u6709\u8fd9\u4e2a\u7591\u56e2\uff0c\u90a3 \u5c31\u8bf7\u628a\u94ae\u6263\u9664\u4e0b\uff0c\u7136\u540e\u518d\u628a\u5b69\u5b50\u7ed9\u6211\u9886\u56de\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u51b7\u7b11\u9053\uff1a\u201c\u592b\u4eba\u4e4b\u8a00\u5dee\u77e3\uff01\u592b\u4eba\uff0c\u4f60\u662f\u5927\u5bcc\u5927\u8d35\u7684\u5b98\u592a\u592a\uff0c\u6211\u5c82\u6562\u770b\u5c0f\u592b\u4eba\uff0c\u8bf4 \u4f60\u8d2a\u56fe\u73e0\u5b9d\uff1f\u518d\u8005\u90a3\u5b69\u5b50\u82e5\u662f\u4f60\u7684\uff0c\u5979\u68c9\u8884\u4e0a\u7684\u94ae\u6263\u5f53\u662f\u4f60\u5bb6\u4e4b\u7269\uff0c\u6211\u53c8\u600e\u80fd\u5c06\u5b83\u9664\u4e0b\uff1f\u8fd9 \u4e0d\u662f\u770b\u5c0f\u592b\u4eba\uff0c\u800c\u662f\u592b\u4eba\u770b\u5c0f\u6211\u4e86\uff01\u201d \u3000\u3000\u90a3\u7f2a\u592b\u4eba\u81ea\u77e5\u8bf4\u9519\u4e86\u8bdd\uff0c\u8fde\u5fd9\u4fbf\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u6211\u529d\u638c\u95e8\u5c06\u90a3\u51e0\u9897\u94ae\u6263\u9664\u4e0b\uff0c \u53ea\u4e0d\u8fc7\u662f\u60f3\u916c\u8c22\u8c37\u638c\u95e8\u629a\u517b\u5b69\u5b50\u7f62\u4e86\u3002\u8bf4\u8001\u5b9e\u8bdd\uff0c\u6211\u5f53\u65f6\u968f\u624b\u628a\u5148\u7236\u8d50\u7ed9\u6211\u7684\u51e0\u9897\u5b9d\u77f3\u53eb\u5320 \u4eba\u9576\u5d4c\u5728\u94ae\u6263\u4e0a\uff0c\u6211\u4e5f\u8fd8\u4e0d\u77e5\u9053\u90a3\u4e9b\u5b9d\u77f3\u7adf\u662f\u8fd9\u4e48\u540d\u8d35\uff1f\u201d\u7f2a\u592b\u4eba\u542c\u8c37\u4e4b\u534e\u8bf4\u7684\u662f\u201c\u7a00\u4e16\u4e4b \u73cd\u201d\u8fd9\u56db\u4e2a\u5b57\uff0c\u5979\u54ea\u91cc\u60f3\u662f\u6606\u4ed1\u5c71\u661f\u5bbf\u6d77\u7684\u201c\u5929\u5fc3\u77f3\u201d\uff0c\u53ea\u5f53\u4e0d\u662f\u73cd\u73e0\uff0c\u4fbf\u662f\u5b9d\u77f3\uff0c\u73e0\u662f\u4e0d \u80fd\u9576\u4f5c\u94ae\u6263\u7684\uff0c\u6240\u4ee5\u4fbf\u4ee5\u4e3a\u5b9a\u662f\u5b9d\u77f3\u65e0\u7591\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u7b11\u4e86\u4e00\u7b11\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u4e4b\u6240\u4ee5\u63d0\u51fa\u68c9\u8884\u4e0a\u7684\u94ae\u6263\uff0c\u4e3a\u4e86\u614e\u91cd\u8d77\u89c1\uff0c\u514d\u5f97\u592b\u4eba \u9519\u9886\u4e86\u522b\u5bb6\u7684\u5b69\u5b50\u3002\u73b0\u5728\u53ea\u6015\u8981\u8ba9\u592b\u4eba\u5931\u671b\u4e86\uff0c\u8fd9\u4e2a\u5b69\u5b50\uff0c\u6211\u5df2\u7ecf\u53ef\u4ee5\u65ad\u5b9a\u7edd\u4e0d\u662f\u4f60\u7684\u5b69\u5b50 \u4e86\uff01\u201d \u3000\u3000\u7f2a\u592b\u4eba\u53cc\u773c\u5706\u7741\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u51ed\u4ec0\u4e48\u65ad\u5b9a\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u5c31\u51ed\u90a3\u51e0\u9897\u94ae\u6263\u3002\u90a3\u51e0\u9897\u94ae\u6263\u5e76\u975e\u88c5\u9970\u7528\u7684\u5b9d\u4e5f\u5e76\u975e\u9576\u5d4c\u4e0a\u53bb\u7684\uff0c\u800c\u662f\u4e00 \u79cd\u751a\u4e3a\u53e4\u602a\u7684\u77f3\u5934\uff0c\u8981\u8bc6\u8d27\u7684\u4eba\u624d\u61c2\u5f97\u5b83\u7684\u7528\u5904\uff0c\u624d\u77e5\u9053\u5b83\u662f\u7a00\u4e16\u4e4b\u73cd\uff01\u201d \u3000\u3000\u7f2a\u592b\u4eba\u767b\u65f6\u77a0\u76ee\u7ed3\u820c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u53ea\u9053\u79d8\u5bc6\u662f\u5728\u68c9\u8884\u5185\uff0c\u5374\u662f\u5728\u94ae\u6263\u4e0a\uff01\u201d\u6b63\u5728\u82e6 \u601d\uff0c\u60f3\u7528\u5de7\u8a00\u642a\u585e\uff0c\u8c37\u4e4b\u534e\u53c8\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u592b\u4eba\u83ab\u975e\u8fd8\u6709\u6000\u7591\u4e48\uff1f\u90a3\u5c31\u8bf7\u4f60\u8bd5\u8bf4\u4e00\u8bf4\u770b\uff0c\u4f60 \u7eb5\u4f7f\u4e0d\u77e5\u9053\u90a3\u79cd\u77f3\u5934\u7684\u7528\u5904\uff0c\u6700\u5c11\u4e5f\u8be5\u77e5\u9053\u5b83\u7684\u8272\u6cfd\u548c\u6709\u4ec0\u4e48\u7279\u522b\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u7f2a\u592b\u4eba\u53ea\u6015\u8d8a\u8bf4\u8d8a\u9519\uff0c\u4e0d\u6562\u518d\u80e1\u4e71\u8bf4\u4e86\uff0c\u5f53\u4e0b\u5f3a\u884c\u8fa9\u9053\uff1a\u201c\u5979\u662f\u6cb3\u5357\u4e2d\u725f\u53bf\u4e18\u5ca9\u7684\u4f43\u6237 \u9001\u7ed9\u4e18\u5ca9\u7684\u4e0d\u662f\uff1f\u8fd9\u4e2a\u6211\u5df2\u7ecf\u8bf4\u5f97\u5bf9\u4e86\uff0c\u5f53\u7136\u5c31\u662f\u6211\u7684\u5b69\u5b50\uff01\u81f3\u4e8e\u90a3\u51e0\u9897\u94ae\u6263\uff0c\u6216\u8005\u662f\u4e18\u5ca9 \u540e\u6765\u6362\u4e86\u7684\uff0c\u6211\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4e18\u5ca9\u4e0d\u6b62\u4e00\u4e2a\u4f43\u6237\uff0c\u4f60\u53c8\u7109\u77e5\u4e0d\u662f\u7b2c\u4e8c\u4e2a\u4f43\u6237\u7684\u5b69\u5b50\uff1f\u603b\u4e4b\uff0c\u8bc1\u7269\u4e0d\u5bf9\uff0c \u6211\u600e\u53ef\u4ee5\u5c06\u5b69\u5b50\u7ed9\u4f60\u3002\u4f60\u4e5f\u4e0d\u5fc5\u8981\u522b\u5bb6\u7684\u5b69\u5b50\uff01\u201d \u3000\u3000\u7f2a\u592b\u4eba\u8001\u7f9e\u6210\u6012\uff0c\u201c\u4e52\u201d\u7684\u4e00\u58f0\uff0c\u5ffd\u5730\u62cd\u6848\u9a82\u9053\uff1a\u201c\u8c37\u638c\u95e8\uff0c\u4f60\u53ef\u662f\u6709\u610f\u8981\u5c06\u6211\u96be\u4e3a \u4e48\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9762\u8272\u4e00\u6c89\uff0c\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u592b\u4eba\uff0c\u8fd9\u91cc\u4e0d\u662f\u4f60\u7684\u8859\u95e8\uff0c\u4f60\u8981\u53d1\u813e\u6c14\uff0c\u56de\u5230\u4f60\u7684\u8859\u95e8 \u518d\u53d1\u5427\uff01\u201d\u8bf4\u7f62\uff0c\u7acb\u5373\u7aef\u8d77\u8336\u676f\uff0c\u8fd9\u662f\u8868\u793a\u9001\u5ba2\u7684\u610f\u601d\u3002 \u3000\u3000\u7f2a\u592b\u4eba\u6c14\u5f97\u8fde\u8116\u5b50\u4e5f\u901a\u7ea2\u4e86\uff0c\u7709\u5fc3\u7684\u9ed1\u6c14\u4e5f\u8d8a\u6765\u8d8a\u6d53\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u8c37\u638c\u95e8\uff0c\u4f60\u662f\u8981\u8d76 \u6211\u8d70\u4e48\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4f46\u4e8b\u60c5\u5df2\u7ecf\u5f04\u5f97\u660e\u660e\u767d\u767d\uff0c\u592b\u4eba\uff0c\u4f60\u7684\u5b69\u5b50\u4e0d\u5728\u6b64\u95f4\uff0c\u6211\u8fd9\u91cc\u8352 \u5c71\u5c0f\u5bfa\uff0c\u81ea\u662f\u4e0d\u6562\u591a\u7559\u8d35\u5ba2\uff01\u201d \u3000\u3000\u7f2a\u592b\u4eba\u6012\u9053\uff1a\u201c\u8c01\u8bf4\u4e8b\u60c5\u5df2\u7ecf\u660e\u767d\uff1f\u54fc\uff0c\u54fc\uff0c\u6211\u867d\u662f\u5b64\u8eab\u5230\u6b64\uff0c\u4f60\u8981\u8d76\u6211\uff0c\u53ea\u6015\u4e5f\u8fd8\u4e0d \u90a3\u4e48\u5bb9\u6613\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u8bc1\u7269\u4e0d\u7b26\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e0d\u660e\u767d\u7684\uff1f\u4f60\u4e00\u5b9a\u8981\u5728\u6211\u8fd9\u7384\u5973\u89c2\u95f9\u4e0b\u53bb\uff0c\u8fd9\u5c31\u4e0d\u662f \u6211\u96be\u4e3a\u4f60\uff0c\u800c\u662f\u4f60\u96be\u4e3a\u6211\u4e86\uff01\u201d \u3000\u3000\u7f2a\u592b\u4eba\u53c8\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7384\u5973\u89c2\u53c8\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u6db5\u517b\u518d\u597d\uff0c\u4e5f\u4e0d\u7981\u9732\u51fa\u4e86\u6012\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u51e1\u4e8b\u62ac\u4e0d\u8fc7\u4e00\u4e2a\u7406\u5b57\uff0c\u7384\u5973\u89c2\u5f53\u7136\u6bd4\u4e0d \u4e0a\u4f60\u7684\u63d0\u7763\u519b\u95e8\uff0c\u53ef\u662f\u4f60\u82e5\u8981\u4e0d\u8bb2\u7406\u4e48\uff0c\u7384\u5973\u89c2\u4e5f\u8fd8\u4e0d\u60e7\uff01\u201d \u3000\u3000\u7f2a\u592b\u4eba\u9762\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u7ea2\uff0c\u4f3c\u4e4e\u5c31\u8981\u53d1\u4f5c\uff0c\u5374\u53c8\u5ffd\u7136\u4f4e\u4e0b\u5934\u53bb\uff0c\u6362\u4e86\u4e00\u526f\u58f0\u8c03\u8bf4\u9053\uff1a \u201c\u8c37\u638c\u95e8\uff0c\u6211\u5bf9\u4f60\u662f\u63a8\u5fc3\u7f6e\u8179\uff0c\u5c06\u9690\u79c1\u4e5f\u548c\u4f60\u8bb2\u4e86\u3002\u4f60\u5f53\u7136\u77e5\u9053\uff0c\u6211\u5373\u4f7f\u53ef\u4ee5\u8c03\u52a8\u4e08\u592b\u7684\u5175 \u5c06\uff0c\u4e5f\u53ef\u4ee5\u4f60\u516c\u7136\u8981\u4eba\u3002\u6240\u4ee5\u8bf7\u4f60\u4e0d\u8981\u8bef\u4f1a\u6211\u662f\u4ed7\u52bf\u6b3a\u4eba\u3002\u6b63\u5982\u4f60\u6240\u8bf4\uff0c\u51e1\u4e8b\u62ac\u4e0d\u8fc7\u4e00\u4e2a\u7406 \u5b57\uff0c\u6211\u53ea\u662f\u8981\u548c\u4f60\u8bb2\u4e2a\u9053\u7406\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u592b\u4eba\u8981\u8bb2\u7684\u662f\u4ec0\u4e48\u9053\u7406\uff0c\u5c0f\u5973\u5b50\u6d17\u8033\u606d\u542c\uff01\u201d \u3000\u3000\u7f2a\u592b\u4eba\u9053\uff1a\u201c\u54b1\u4eec\u4e89\u6267\u7684\u662f\u8fd9\u4e2a\u5b69\u5b50\u3002\u90a3\u4e48\u4f60\u603b\u8be5\u8ba9\u6211\u89c1\u4e00\u89c1\u3002\u6211\u542c\u5f97\u5976\u5988\u8bf4\uff0c\u5979\u957f\u5f97 \u5f88\u4f3c\u6211\u3002\u8981\u662f\u6211\u89c1\u4e86\u4f60\u6536\u517b\u7684\u5b69\u5b50\uff0c\u5979\u4e0d\u4f3c\u6211\u7684\u8bdd\uff0c\u90a3\u6211\u5c31\u91ca\u7136\u4e8e\u6000\u4e86\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u8c37\u4e4b\u534e\u5fc3\u4e2d\u4e00\u52a8\uff0c\u89c9\u5f97\u4e8b\u60c5\u66f4\u4e3a\u660e\u6717\uff0c\u66f4\u53ef\u4ee5\u8ba4\u5b9a\u81ea\u5df1\u6240\u6536\u517b\u7684\u83b2\u513f\uff0c\u4e0d\u662f \u8fd9\u5987\u4eba\u7684\u9aa8\u8089\u4e86\u3002\u5fc3\u60f3\uff1a\u201c\u6211\u7684\u83b2\u513f\u7709\u76ee\u6e05\u79c0\uff0c\u9aa8\u9abc\u7aef\u5e84\uff0c\u54ea\u6709\u534a\u70b9\u548c\u4f60\u76f8\u4f3c\uff1f\u201d \u3000\u3000\u7f2a\u592b\u4eba\u6700\u521d\u8bf4\u8981\u8ba4\u56de\u5b69\u5b50\u7684\u65f6\u5019\uff0c\u8c37\u4e4b\u534e\u5df2\u7ecf\u6000\u7591\u9762\u8c8c\u4e0d\u540c\u8fd9\u70b9\u4e86\uff1b\u53ea\u56e0\u7236\u6bcd\u5b50\u5973\u7684\u9762 \u8c8c\u867d\u7136\u591a\u6570\u76f8\u4f3c\uff0c\u4e5f\u6709\u4e0d\u540c\u7684\uff0c\u6240\u4ee5\u8c37\u4e4b\u534e\u672a\u66fe\u636e\u6b64\u53cd\u9a73\uff0c\u5982\u4eca\u7531\u5979\u4eb2\u53e3\u63d0\u51fa\u6765\uff0c\u6b63\u5408\u8c37\u4e4b \u534e\u7684\u5fc3\u610f\u3002\u5f53\u4e0b\u4fbf\u7b54\u5e94\u5979\u9053\uff1a\u201c\u4f60\u8981\u89c1\u8fd9\u4e2a\u5b69\u5b50\uff0c\u6211\u53ef\u4ee5\u8fbe\u5230\u4f60\u7684\u5fc3\u613f\u3002\u4e0d\u8fc7\uff0c\u4f60\u53ef\u8981\u770b\u5f97 \u4ed4\u7ec6\u4e00\u70b9\uff0c\u5207\u4e0d\u8981\u4e00\u89c1\u9762\u5c31\u81ea\u79f0\u662f\u5979\u7684\u5988\u5988\u3002\u201d \u3000\u3000\u7f2a\u592b\u4eba\u9053\uff1a\u201c\u8fd9\u4e2a\u4f55\u52b3\u5631\u5490\uff0c\u6211\u4e5f\u51b3\u4e0d\u4f1a\u5192\u8ba4\u522b\u5bb6\u7684\u5b69\u5b50\u3002\u6211\u8fd8\u6709\u4e00\u4e8b\uff0c\u8981\u5411\u638c\u95e8\u8bf7 \u6c42\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u89c1\u5979\u6001\u5ea6\u5df2\u8f6c\u4e3a\u8c26\u606d\u6709\u793c\uff0c\u4fbf\u4e5f\u597d\u8a00\u7b54\u9053\uff1a\u201c\u592b\u4eba\u8bf7\u8bf4\uff0c\u5982\u80fd\u529e\u5230\uff0c\u81ea\u5f53\u5e94\u547d\u3002\u201d \u3000\u3000\u7f2a\u592b\u4eba\u9053\uff1a\u201c\u5b69\u5b50\u90a3\u4ef6\u68c9\u8884\uff0c\u662f\u6211\u4eb2\u624b\u88c1\u5236\u7684\uff0c\u4f46\u636e\u8c37\u638c\u95e8\u8bf4\uff0c\u94ae\u6263\u5374\u6709\u6240\u4e0d\u540c\uff0c\u4e0d\u77e5 \u53ef\u5426\u8d50\u6211\u4e00\u89c2\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u4f5b\u7136\u4e0d\u60a6\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4ef6\u68c9\u8884\u6211\u5f53\u7136\u4e5f\u8981\u62ff\u51fa\u6765\u8ba9\u592b\u4eba\u8fc7\u76ee\uff0c\u5426\u5219\u592b\u4eba\u96be\u514d\u4ee5\u4e3a \u6211\u662f\u4fe1\u53e3\u5f00\u6cb3\uff0c\u62b9\u6740\u8bc1\u636e\u4e86\u3002\u201d \u3000\u3000\u7f2a\u592b\u4eba\u9053\uff1a\u201c\u8c37\u638c\u95e8\u8a00\u91cd\u4e86\uff0c\u6211\u53ea\u662f\u4f46\u6c42\u5bf9\u8bc1\u4e00\u4e0b\u800c\u5df2\u3002\u201d\u5979\u7684\u7b11\u5bb9\uff0c\u4e00\u770b\u5c31\u77e5\u662f\u7b11\u5f97 \u975e\u5e38\u52c9\u5f3a\u3002\u8c37\u4e4b\u534e\u8d8a\u770b\u8d8a\u89c9\u5f97\u5979\u4f3c\u662f\u6709\u6240\u56fe\u800c\u6765\uff0c\u4f46\u968f\u5373\u60f3\u9053\uff1a\u201c\u5728\u6211\u9762\u524d\uff0c\u8c05\u5979\u4e5f\u4e0d\u80fd\u5c31 \u628a\u4e1c\u897f\u62a2\u53bb\u3002\u201d \u3000\u3000\u5f53\u4e0b\u8c37\u4e4b\u534e\u5c06\u4e00\u4e2a\u4f8d\u5973\u5524\u6765\uff0c\u5429\u5490\u5979\u9053\uff1a\u201c\u4f60\u8bf7\u6211\u7684\u4e8c\u5e08\u5ac2\u5c06\u83b2\u513f\u5e26\u51fa\u6765\uff0c\u8fd8\u6709\u90a3\u4ef6\u68c9 \u8884\u4e5f\u4e00\u8d77\u62ff\u6765\u5427\u3002\u68c9\u8884\u5728\u6211\u623f\u4e2d\u6700\u5e95\u7684\u90a3\u4e2a\u7bb1\u5b50\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u7684\u4e8c\u5e08\u5ac2\u5373\u662f\u524d\u4efb\u4e10\u5e2e\u5e2e\u4e3b\u94c1\u62d0\u4ed9\u7684\u59bb\u5b50\u8c22\u4e91\u771f\uff0c\u5979\u81ea\u4ece\u4e08\u592b\u53bb\u4e16\u4e4b\u540e\uff0c\u5373\u4e00\u76f4 \u5728\u7384\u5973\u89c2\u5c45\u4f4f\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4fbf\u542c\u5f97\u4e00\u4e2a\u5b69\u5b50\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u5a18\uff0c\u6211\u521a\u624d\u77a7\u89c1\u4e00\u4e2a\u5149\u7740\u5c41\u80a1\u7684\u4eba\uff0c\u8fd9\u4e2a\u4eba \u662f\u6765\u770b\u4f60\u7684\uff0c\u4f60\u89c1\u4e86\u4ed6\u6ca1\u6709\uff1f\u201d\u539f\u6765\u8fd9\u4e2a\u5973\u5b69\u4fbf\u662f\u6c5f\u5357\u6240\u78b0\u89c1\u7684\u90a3\u4e2a\u5973\u5b69\uff0c\u5979\u8fd8\u672a\u8e0f\u8fdb\u95e8 \u53e3\uff0c\u4fbf\u6025\u4e0d\u53ef\u5f85\u7684\u8981\u5c06\u8fd9\u4ef6\u201c\u597d\u73a9\u201d\u7684\u4e8b\u60c5\u544a\u8bc9\u8c37\u4e4b\u534e\u4e86\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u522b\u80e1\u8bf4\u516b\u9053\uff0c\u5feb\u6765\u89c1\u8fc7\u5ba2\u4eba\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u771f\u7684\u5462\uff0c\u4e0d\u4fe1\u4f60\u95ee\u8def\u5e08\u53d4\uff0c\u4ed6\u4e5f\u77a7\u89c1\u4e86\u3002\u201d\u8fd9\u65f6\u5979\u65b9\u59cb\u8e0f\u8fdb\u95e8\u6765\uff0c \u6b63\u597d\u4e0e\u90a3\u7f2a\u592b\u4eba\u7684\u773c\u5149\u76f8\u5bf9\uff0c\u5979\u90a3\u53cc\u6ef4\u6e9c\u6e9c\u7684\u5c0f\u773c\u775b\u7a81\u7136\u505c\u6b62\u4e86\u8f6c\u52a8\uff0c\u4f3c\u4e4e\u7ed9\u5413\u5f97\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u8c22\u4e91\u771f\u62c9\u7740\u5979\u7684\u5c0f\u624b\uff0c\u611f\u5230\u5979\u7684\u5c0f\u624b\u5fae\u5fae\u53d1\u6296\uff0c\u5fc3\u77e5\u6709\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u83b2\u513f\uff0c\u4f60\u600e\u4e48\u5566\uff0c \u5feb\u4e0a\u53bb\u5524\u58f0\u59d1\u59d1\u3002\u201d \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u90a3\u7f2a\u592b\u4eba\u5df2\u4e0a\u524d\u51e0\u6b65\uff0c\u9762\u5bf9\u7740\u8c37\u4e2d\u83b2\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5b69\u5b50\uff0c\u4f60\u77e5\u9053\u6211 \u662f\u8c01\u5417\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u7a81\u7136\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u8c01\uff0c\u6211\u8ba4\u5f97\u4f60\uff0c\u4f60\u5c31\u662f\u90a3\u665a\u7684\u90a3\u4e2a\u5973\u8d3c\uff01\u201d \u3000\u3000\u539f\u6765\u4e18\u5ca9\u5728\u81ea\u6740\u4e4b\u524d\u7684\u51e0\u5929\uff0c\u5bb6\u4e2d\u66fe\u95f9\u8fc7\u4e00\u6b21\u8d3c\uff0c\u90a3\u665a\u4e18\u5ca9\u7761\u5230\u534a\u591c\uff0c\u7a81\u7136\u7ed9\u5f02\u58f0\u60ca \u9192\uff0c\u90a3\u65f6\u8c37\u4e2d\u83b2\u8fd8\u53ea\u4e09\u5c81\u6709\u591a\uff0c\u4e18\u5ca9\u751a\u75bc\u7231\u5979\uff0c\u5c06\u5979\u6240\u7761\u7684\u5c0f\u5e8a\u5b89\u653e\u5728\u81ea\u5df1\u7684\u623f\u4e2d\uff0c\u4ee5\u4fbf\u7167 \u987e\uff0c\u4ed6\u4e00\u7ed9\u5f02\u58f0\u60ca\u9192\uff0c\u4fbf\u89c1\u4e00\u6761\u9ed1\u5f71\u6b63\u7ad9\u5728\u5b69\u5b50\u7684\u5e8a\u524d\uff0c\u521a\u521a\u4f38\u51fa\u624b\u81c2\uff0c\u4f3c\u662f\u8981\u5411\u5b69\u5b50\u6293\u4e0b\u3002 \u3000\u3000\u4e18\u5ca9\u7ec3\u6709\u6df7\u5143\u9739\u96f3\u638c\u7684\u529f\u592b\uff0c\u8fd9\u662f\u5916\u5bb6\u62f3\u4e2d\u4e00\u79cd\u6700\u521a\u731b\u7684\u638c\u529b\uff0c\u867d\u6bd4\u4e0d\u4e0a\u5c11\u6797\u6d3e\u7684\u91d1\u521a \u638c\uff0c\u4f46\u4e5f\u6709\u5f00\u7891\u88c2\u77f3\u4e4b\u80fd\uff0c\u4ed6\u731b\u7136\u60ca\u9192\uff0c\u89c1\u6b64\u60c5\u5f62\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u7acb\u5373\u4fbf\u8df3\u4e0b\u5e8a\u6765\uff0c\u5411\u90a3\u8d3c\u4eba \u7684\u540e\u5fc3\u4e00\u638c\u51fb\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u51fb\u4e2a\u6b63\u7740\uff0c\u4f46\u542c\u5f97\u90a3\u8d3c\u4eba\u201c\u54fc\u201d\u7684\u4e00\u58f0\uff0c\u7acb\u5373\u7834\u7a97\u800c\u51fa\uff0c\u4e18\u5ca9\u8ddf\u7740\u8ffd\u51fa\uff0c\u5f71\u5b50\u90fd \u4e0d\u89c1\u4e86\uff0c\u53ea\u89c9\u5f97\u8d3c\u4eba\u201c\u54fc\u201d\u90a3\u4e00\u58f0\uff0c\u4e0d\u50cf\u662f\u4e2a\u7537\u4eba\u3002\u7b2c\u4e8c\u5929\u4e18\u5ca9\u5c31\u611f\u5230\u4e94\u810f\u516d\u8151\u90fd\u9690\u9690\u4f5c \u75db\uff0c\u8fd9\u624d\u77e5\u9053\u662f\u53d7\u4e86\u5185\u4f24\u3002 \u3000\u3000\u4e18\u5bb6\u95f9\u8d3c\u7684\u4e8b\u60c5\uff0c\u7ffc\u4ef2\u725f\u66fe\u542c\u4e18\u5ca9\u7684\u8001\u4ec6\u4eba\u8bf4\u8fc7\uff0c\u6240\u4ee5\u8c37\u4e4b\u534e\u77e5\u9053\u8fd9\u4ef6\u4e8b\u60c5\u3002\u4f46\u90fd\u662f\u77e5 \u800c\u4e0d\u8be6\uff0c\u800c\u8fd9\u4ef6\u4e8b\u60c5\u4e5f\u6b63\u662f\u7591\u70b9\u4e4b\u4e00\uff0c\u56e0\u4e3a\u4e18\u5ca9\u5b81\u53ef\u81ea\u5c3d\uff0c\u5374\u59cb\u7ec8\u672a\u66fe\u5c06\u6b64\u4e8b\u5411\u7ffc\u4ef2\u725f\u900f \u9732\u3002\u5982\u4eca\uff0c\u8fd9\u5973\u5b69\u7a81\u7136\u4e00\u53e3\u9053\u7834\uff0c\u8bf4\u51fa\u4e86\u8fd9\u7f2a\u592b\u4eba\u5c31\u662f\u5f53\u5e74\u5077\u5165\u5979\u5bb6\u7684\u5973\u8d3c\uff01 \u3000\u3000\u8c37\u4e4b\u534e\u5927\u5403\u4e00\u60ca\uff0c\u521a\u521a\u8054\u60f3\u5230\u4e18\u5bb6\u95f9\u8d3c\u7684\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u90a3\u7f2a\u592b\u4eba\u9634\u58f0\u602a \u6c14\u5730\u53eb\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u4f60\u65e2\u8ba4\u51fa\u6211\uff0c\u5c31\u968f\u6211\u8d70\u5427\uff01\u201d\u58f0\u5230\u4eba\u5230\uff0c\u4e00\u624b\u5c31\u5411\u90a3\u5b69\u5b50\u5f53\u5934\u6293\u4e0b\uff01 \u3000\u3000\u8c37\u4e4b\u534e\u7684\u5e08\u5ac2\u8c22\u4e91\u771f\uff0c\u5916\u53f7\u201c\u8fa3\u624b\u4ed9\u5a18\u201d\uff0c\u4f55\u7b49\u5389\u5bb3\uff0c\u4f46\u89c1\u8eab\u5f62\u4e00\u95ea\uff0c\u7f2a\u592b\u4eba\u90a3\u4e00\u6293\u8fd8 \u672a\u66fe\u6293\u5b9e\uff0c\u5979\u5df2\u662f\u62d4\u5251\uff0c\u51fa\u9798\uff0c\u53d1\u62db\uff0c\u51e0\u4e2a\u52a8\u4f5c\uff0c\u4e00\u6c14\u5475\u6210\uff01 \u3000\u3000\u8fd9\u4e00\u5251\u76f4\u6307\u7f2a\u592b\u4eba\u7684\u5fc3\u623f\uff0c\u7f2a\u592b\u4eba\u4e43\u662f\u72c2\u5954\u800c\u6765\uff0c\u773c\u770b\u5c31\u8981\u957f\u5251\u6d1e\u7a7f\u5fc3\u80f8\uff0c\u5374\u5ffd\u542c\u5f97\u4e00 \u58f0\u5c16\u53eb\uff0c\u8fde\u8c37\u4e4b\u534e\u4e5f\u8fd8\u672a\u66fe\u770b\u695a\uff0c\u8fa3\u624b\u4ed9\u5a18\u8c22\u4e91\u771f\u7adf\u7136\u5148\u906d\u4e86\u5979\u7684\u8fa3\u624b\uff0c\u6251\u901a\u4fbf\u5012\uff01 \u3000\u3000\u8c37\u4e4b\u534e\u8981\u5979\u5e08\u5ac2\u5e26\u8fd9\u5b69\u5b50\u51fa\u6765\uff0c\u672c\u6765\u5c31\u662f\u4e3a\u4e86\u9632\u5907\u610f\u5916\uff0c\u8981\u4e00\u4e2a\u4eba\u6765\u4fdd\u62a4\u5b69\u5b50\u7684\uff0c\u54ea\u6599 \u5f97\u5230\u8c22\u4e91\u771f\u7adf\u662f\u4e00\u4e2a\u7167\u9762\u4fbf\u5012\u5730\u4e0d\u8d77\uff01\u8c37\u4e4b\u534e\u6025\u6012\u4ea4\u52a0\uff0c\u8eab\u5f62\u75be\u8d77\uff0c\u7acb\u5373\u5411\u90a3\u7f2a\u592b\u4eba\u6251\u53bb\uff0c \u90a3\u7f2a\u592b\u4eba\u51fb\u5012\u4e86\u8c22\u4e91\u771f\u4e4b\u540e\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u811a\u6b65\u7a81\u7136\u505c\u4e0b\uff0c\u90a3\u4ef6\u68c9\u8884\u672c\u662f\u5728\u8c37\u4e2d\u83b2\u8eab\u4e0a\u7684\uff0c\u8fd9 \u65f6\u4e5f\u5df2\u8dcc\u843d\u5730\u4e0a\uff0c\u7f2a\u592b\u4eba\u773c\u5149\u4e00\u77a5\u90a3\u4ef6\u68c9\u8884\uff0c\u5fc3\u5ff5\u65b9\u52a8\uff0c\u6b63\u8981\u4f38\u624b\u53bb\u6361\uff0c\u8c37\u4e4b\u534e\u7684\u971c\u534e\u5b9d\u5251 \u5df2\u7ecf\u5410\u51fa\u4e86\u78a7\u83b9\u83b9\u7684\u5bd2\u5149\uff0c\u6307\u5230\u4e86\u5979\u7684\u7709\u5fc3\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u8fd9\u51e0\u5e74\u6765\u82e6\u7ec3\u7384\u5973\u5251\u6cd5\uff0c\u6bd4\u8d77\u8c22\u4e91\u771f\u53c8\u66f4\u5389\u5bb3\u5f97\u591a\uff0c\u5b9d\u5251\u8f7b\u7075\u7fd4\u52a8\uff0c\u52bf\u6377\u529b\u5f3a\uff0c \u6781\u96be\u8eb2\u907f\uff0c\u90a3\u7f2a\u592b\u4eba\u597d\u751f\u4e86\u5f97\uff0c\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u8eab\u5b50\u7adf\u7136\u672a\u66fe\u632a\u540e\u534a\u6b65\uff0c\u7acb\u5373\u4f7f\u4e00\u6307 \u53cd\u5f39\u8fc7\u6765\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff1b\u6b63\u5f39\u4e2d\u4e86\u8c37\u4e4b\u534e\u7684\u5251\u810a\uff01 \u3000\u3000\u8c37\u4e4b\u534e\u4f46\u89c9\u4e00\u80a1\u52b2\u529b\u4f20\u6765\uff0c\u864e\u53e3\u7adf\u7136\u5fae\u5fae\u53d1\u70ed\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u51db\uff1a\u201c\u8fd9\u5996\u5987\u7ec3\u7684\u662f\u4ec0\u4e48 \u90aa\u95e8\u529f\u592b\uff1f\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u7f2a\u592b\u4eba\u4e00\u6307\u5f39\u5f00\u4e86\u8c37\u4e4b\u534e\u7684\u5b9d\u5251\uff0c\u8fc5\u5373\u4fbf\u53cd\u624b\u6293\u6765\uff0c\u77ac\u606f \u4e4b\u95f4\uff0c\u63a5\u8fde\u653b\u4e86\u4e09\u62db\uff01 \u3000\u3000\u7f2a\u592b\u4eba\u7684\u62db\u6570\u602a\u5f02\u90a3\u662f\u65e0\u987b\u8bf4\u4e86\uff0c\u66f4\u4ee4\u4eba\u6035\u76ee\u60ca\u5fc3\u7684\u662f\uff1a\u5979\u90a3\u53cc\u638c\u672c\u6765\u67d4\u8f6f\u7ea2\u6da6\u7684\uff0c\u8fd9 \u65f6\u5374\u597d\u50cf\u6d82\u4e0a\u4e86\u6d53\u58a8\u4e00\u822c\uff0c\u53d8\u4f5c\u4e86\u6f06\u9ed1\u4e00\u56e2\uff0c\u800c\u4e14\u53d1\u51fa\u7684\u638c\u98ce\uff0c\u9690\u9690\u5e26\u7740\u8840\u8165\u7684\u6c14\u5473\uff01\u5e78\u800c \u8c37\u4e4b\u534e\u7ec3\u7684\u662f\u6b63\u5b97\u7684\u7384\u95e8\u5185\u529f\uff0c\u4ea6\u65e9\u5df2\u5230\u4e86\u4e0a\u4e58\u5883\u754c\uff0c\u8fd0\u6c14\u4e09\u8f6c\uff0c\u628a\u80f8\u4e2d\u7684\u70e6\u95f7\u4e4b\u611f\uff0c\u5c3d\u90fd \u6d88\u9664\uff0c\u4ea6\u662f\u5728\u77ac\u606f\u4e4b\u95f4\uff0c\u63a5\u8fde\u8fd8\u4e86\u4e09\u62db\uff0c\u4ee4\u90a3\u7f2a\u592b\u4eba\u4e0d\u6562\u6b3a\u8eab\u8fdb\u8feb\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u7ed9\u5413\u5f97\u5446\u4e86\uff0c\u8fd9\u65f6\u65b9\u59cb\u201c\u54c7\u201d\u7684\u4e00\u58f0\u54ed\u5f97\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u5a18\uff0c\u8fd9\u5973\u8d3c\u8981\u6293\u6b7b\u6211\uff0c \u4f60\u5feb\u5feb\u628a\u5979\u8d76\u8dd1\uff0c\u6211\u3001\u6211\u6015\u6b7b\u5566\uff01\u201d \u3000\u3000\u7f2a\u592b\u4eba\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u6211\u5c31\u662f\u4f60\u7684\u4eb2\u751f\u6bcd\u4eb2\uff0c\u4f60\u4e0d\u7528\u5bb3\u6015\uff01\u201d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u53c8\u8981\u5411\u90a3\u5b69\u5b50\u6293 \u53bb\uff0c\u8c37\u4e4b\u534e\u54ea\u80fd\u8ba9\u5979\u5f97\u901e\uff0c\u5251\u5149\u4e00\u5c55\uff0c\u4e0d\u5bb9\u5979\u8d70\u8fd1\u5b69\u5b50\u7684\u8eab\u8fb9\uff0c\u65e9\u5df2\u628a\u5979\u7684\u8eab\u5f62\u7f69\u4f4f\uff01 \u3000\u3000\u8c37\u4e2d\u83b2\u53c8\u54ed\u53c8\u9a82\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u6211\u7684\u4eb2\u751f\u6bcd\u4eb2\u65e9\u5df2\u6b7b\u5566\uff0c\u4f60\u662f\u8981\u5bb3\u6211\u7684\u5973\u8d3c\uff0c\u4e0d\u662f\u6211 \u7684\u6bcd\u4eb2\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60ca\u8be7\u4e4b\u4e2d\u53c8\u611f\u5230\u4e86\u5feb\u6170\uff0c\u8fd9\u662f\u90a3\u5b69\u5b50\u7b2c\u4e00\u6b21\u8bf4\u51fa\u5979\u7684\u4eb2\u751f\u6bcd\u4eb2\u5df2\u7ecf\u6b7b \u4e86\uff0c\u540c\u65f6\uff0c\u7531\u4e8e\u90a3\u5b69\u5b50\u7684\u8bf4\u8bdd\uff0c\u4e5f\u63d0\u9192\u4e86\u8c37\u4e4b\u534e\uff0c\u4ee4\u5979\u60f3\u5230\u4e86\u8fd9\u4e00\u70b9\uff1a\u7167\u90a3\u7f2a\u592b\u4eba\u521a\u624d\u5411\u90a3 \u5b69\u5b50\u6293\u4e0b\u53bb\u7684\u51f6\u6076\u795e\u6001\uff0c\u4e00\u4e2a\u6bcd\u4eb2\uff0c\u51b3\u4e0d\u4f1a\u5bf9\u81ea\u5df1\u4eb2\u751f\u7684\u5b69\u5b50\uff0c\u8fd9\u6837\u52a8\u624b\u7684\uff0c\u5f53\u4e0b\u8c37\u4e4b\u534e\u4e00 \u58f0\u51b7\u7b11\uff0c\u65a5\u9053\uff1a\u201c\u597d\u4e2a\u72fc\u5fc3\u72d7\u80ba\u7684\u65e0\u803b\u5996\u5987\uff0c\u73b0\u5728\u8fd8\u6562\u5192\u8ba4\u662f\u5b69\u5b50\u7684\u6bcd\u4eb2\u5417\uff1f\u201d \u3000\u3000\u90a3\u7f2a\u592b\u4eba\u5927\u6012\u9053\uff1a\u201c\u8c37\u4e4b\u534e\uff0c\u6211\u6b64\u6765\u672c\u6765\u662f\u597d\u8a00\u597d\u8bed\u7684\u6765\u6c42\u4f60\uff0c\u4f60\u5374\u80e1\u8a00\u79fd\u8bed\u7684\u6765\u9a82 \u6211\uff0c\u4f60\u65e2\u65e0\u793c\uff0c\u4e5f\u4f11\u602a\u6211\u4e0d\u5ba2\u6c14\u4e86\uff01\u54fc\uff0c\u54fc\uff0c\u4f60\u60f9\u4e0a\u4e86\u6211\uff0c\u53ea\u6709\u4f60\u81ea\u5df1\u5012\u9709\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u4e5f\u6012\u9053\uff1a\u201c\u5373\u7b97\u4f60\u662f\u4e00\u54c1\u592b\u4eba\uff0c\u6b64\u523b\u4e5f\u8981\u4f60\u6eda\u4e0b\u5c71\u53bb\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u7f2a\u592b\u4eba\u7a81 \u7136\u4e00\u58f0\u602a\u53eb\uff0c\u9634\u607b\u607b\u5730\u53eb\u9053\uff1a\u201c\u8c37\u4e4b\u534e\u5440\u8c37\u4e4b\u534e\uff01\u597d\u8a00\u597d\u8bed\u4f60\u4e0d\u542c\uff0c\u6211\u8981\u4ee3\u5341\u6bbf\u960e\u738b\u53d1\u8bf7\u5e16 \u5566\uff01\u201d\u58f0\u97f3\u51c4\u5389\u5f02\u5e38\uff0c\u4e25\u5982\u6076\u6bd2\u7684\u5deb\u5987\u5728\u547c\u9b42\u5524\u9b44\uff0c\u9976\u662f\u8c37\u4e4b\u534e\u5b9a\u529b\u975e\u5e38\uff0c\u542c\u5230\u5979\u7684\u547c\u5524\uff0c \u5fc3\u5934\u4e5f\u4e0d\u7981\u5fae\u5fae\u53d1\u6296\u3002 \u3000\u3000\u90a3\u7f2a\u592b\u4eba\u602a\u5578\u4e4b\u540e\uff0c\u4fbf\u53cc\u638c\u9f50\u53d1\uff0c\u5341\u6307\u8fde\u5f39\uff0c\u5e26\u7740\u8165\u5473\u7684\u6307\u98ce\uff0c\u4f3c\u4e00\u679d\u679d\u51b7\u7bad\u4e00\u822c\uff0c\u5411 \u8c37\u4e4b\u534e\u5c04\u6765\uff0c\u8c37\u4e4b\u534e\u9ed8\u8fd0\u7384\u529f\uff0c\u5c55\u5f00\u7384\u5973\u5251\u6cd5\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u5316\u6210\u4e86\u4e00\u9053\u5149\u5e62\uff0c\u5c06\u8eab\u8eaf\u62a4\u4f4f\uff0c \u4e5f\u6321\u4e86\u90a3\u51b7\u7bad\u7684\u52b2\u98ce\u3002 \u3000\u3000\u8fd9\u51e0\u62db\u5feb\u4f3c\u7535\u5149\u77f3\u706b\uff0c\u53cc\u65b9\u90fd\u4f7f\u51fa\u4e86\u5e73\u751f\u6240\u5b66\uff0c\u529b\u4e89\u5148\u624b\uff0c\u8c37\u4e4b\u534e\u80dc\u5728\u5185\u529f\u6df1\u539a\uff0c\u5251\u672f \u7cbe\u5947\uff1b\u4f46\u90a3\u7f2a\u592b\u4eba\u6240\u7ec3\u7684\u90aa\u95e8\u529f\u592b\u975e\u5e38\u602a\u5f02\uff0c\u53cc\u65b9\u90fd\u6709\u6240\u987e\u5fcc\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u5c1a\u662f\u80dc\u8d1f\u96be\u5206\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u6b63\u5728\u5168\u795e\u8d2f\u6ce8\uff0c\u5e94\u4ed8\u90a3\u7f2a\u592b\u4eba\u7684\u653b\u51fb\uff0c\u5ffd\u542c\u5f97\u4f8d\u5973\u53eb\u9053\uff1a\u201c\u7980\u638c\u95e8\uff0c\u864e\u3001\u6797\u3001 \u5b59\uff0c\u7a0b\u513f\u4f4d\u5e08\u53d4\u7b49\u5019\u4f20\u89c1\u3002\u201d\u539f\u6765\u521a\u624d\u8c37\u4e4b\u534e\u56e0\u4e3a\u5e94\u7f2a\u592b\u4eba\u4e4b\u8bf7\uff0c\u66fe\u53d1\u51fa\u7981\u4ee4\uff0c\u4efb\u4f55\u4eba\u4e0d\u8bb8 \u8fdb\u5165\uff0c\u56e0\u6b64\u5728\u5916\u9762\u6253\u63a2\u52a8\u9759\u7684\u51e0\u4f4d\u5927\u5f1f\u5b50\uff0c\u867d\u7136\u542c\u5f97\u91cc\u9762\u5df2\u53d1\u751f\u6253\u6597\u7684\u58f0\u97f3\uff0c\u4ecd\u7136\u672a\u6562\u64c5\u81ea \u8fdb\u6765\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u5e94\u58f0\u7b54\u9053\uff1a\u201c\u7981\u4ee4\u89e3\u9664\uff0c\u8ba9\u4ed6\u4eec\u8fdb\u6765\uff01\u201d\u5979\u8bf4\u8bdd\u5206\u795e\uff0c\u5251\u62db\u7a0d\u7f13\uff0c\u7f2a\u592b\u4eba\u8d81\u6b64\u65f6 \u673a\uff0c\u6479\u5730\u4e00\u58f0\u602a\u5578\uff0c\u4e00\u9053\u7eff\u8272\u7684\u706b\u7130\uff0c\u4ece\u5979\u8896\u4e2d\u98de\u51fa\uff0c\u900f\u8fc7\u4e86\u5251\u5149\uff0c\u76f4\u5c04\u5230\u8c37\u4e4b\u534e\u7684\u9762\u95e8\uff01 \u3000\u3000\u8c37\u4e4b\u534e\u4e00\u53e3\u771f\u6c14\u5439\u51fa\uff0c\u90a3\u4e00\u6e9c\u706b\u5149\u767b\u65f6\u5411\u56db\u65b9\u758f\u6563\uff0c\u90a3\u7f2a\u592b\u4eba\u4f3c\u4e4e\u672a\u6599\u5230\u5979\u5df2\u7ec3\u6210\u4e86\u5185 \u5bb6\u7f61\u6c14\uff0c\u5fae\u5501\u4e00\u58f0\uff0c\u9000\u4e86\u4e24\u6b65\uff1b\u4f46\u90a3\u706b\u82b1\u5e26\u7740\u4e00\u79cd\u6781\u96be\u95fb\u7684\u8150\u81ed\u8165\u5473\uff0c\u663e\u7136\u662f\u4ec0\u4e48\u90aa\u6076\u7684\u836f \u7269\uff0c\u8c37\u4e4b\u534e\u5728\u5f20\u53e3\u5439\u6c14\u7684\u5f53\u513f\uff0c\u4e5f\u96be\u514d\u5438\u8fdb\u4e86\u5c11\u8bb8\uff0c\u4f46\u89c9\u5934\u6655\u76ee\u7729\uff0c\u5c31\u8981\u4f5c\u5455\u3002 \u3000\u3000\u8fd9\u65f6\u7981\u4ee4\u5df2\u89e3\uff0c\u6c13\u5c71\u6d3e\u7684\u51e0\u4e2a\u5927\u5f1f\u5b50\u8d70\u4e86\u8fdb\u6765\uff0c\u4ed6\u4eec\u867d\u975e\u9996\u5f53\u5176\u51b2\uff0c\u95fb\u5f97\u90a3\u80a1\u6c14\u5473\uff0c\u4e5f \u662f\u597d\u751f\u96be\u53d7\uff0c\u5176\u4e2d\u5362\u9053\u78f7\u7684\u6027\u5b50\u6700\u4e3a\u521a\u66b4\uff0c\u5927\u6012\u9a82\u9053\uff1a\u201c\u54ea\u91cc\u6765\u7684\u5996\u5987\uff0c\u80c6\u6562\u5728\u8fd9\u513f\u653e \u6bd2\uff01\u201d\u4ed6\u662f\u5f53\u5e74\u6c5f\u5357\u4e03\u4fa0\u4e2d\u66f9\u4ec1\u7236\u7684\u5ae1\u4f20\u5f1f\u5b50\uff0c\u4f7f\u7684\u662f\u4e00\u628a\u94c1\u7435\u7436\uff0c\u7435\u7436\u8179\u90e8\u4e2d\u7a7a\uff0c\u85cf\u6709\u4e13 \u6253\u654c\u4eba\u7a74\u9053\u7684\u900f\u9aa8\u9489\uff0c\u4ed6\u4e00\u6309\u7435\u7436\uff0c\u4e09\u679a\u900f\u9aa8\u9489\u7535\u5c04\u800c\u51fa\u3002 \u3000\u3000\u90a3\u7f2a\u592b\u4eba\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u7c73\u7c92\u4e4b\u73e0\uff1b\u4e5f\u653e\u5149\u534e\uff1f\u201d\u5979\u5728\u8c37\u4e4b\u534e\u5251\u5149\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c \u4f46\u542c\u5f97\u53ee\u53ee\u53ee\u4e09\u58f0\u6e05\u8106\u7684\u97f3\u54cd\uff0c\u4e09\u679a\u900f\u9aa8\u9489\u90fd\u6253\u5728\u5979\u7684\u8eab\u4f53\uff0c\u4f46\u5374\u597d\u50cf\u78b0\u7740\u4e86\u91d1\u5c5e\u4e00\u822c\uff0c\u4e00 \u89e6\u53ca\u5979\u7684\u8eab\u5b50\u4fbf\u90fd\u8dcc\u843d\uff01 \u3000\u3000\u7f2a\u592b\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u8c37\u4e4b\u534e\uff0c\u4f60\u4e0d\u5982\u4f20\u4ee4\u4e0b\u53bb\uff0c\u9e23\u949f\u51fb\u9f13\uff0c\u5c06\u4f60\u4eec\u6c13\u5c71\u6d3e\u7684\u4e0a\u4e0b\u4eba\u7b49\uff0c\u90fd \u53ec\u96c6\u6765\u7f62\uff0c\u4e5f\u7701\u5f97\u6211\u4e00\u4e2a\u4e2a\u7684\u6765\u5bf9\u4ed8\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u6012\u9053\uff1a\u201c\u6211\u6c13\u5c71\u6d3e\u4e0d\u6bd4\u4f60\u7684\u63d0\u7763\u8859\u95e8\uff0c\u51b3\u4e0d\u4ed7\u52bf\u6b3a\u4eba\uff0c\u4f46\u4f60\u4f24\u4e86\u6211\u6c13\u5c71\u6d3e\u7684\u4eba\uff0c \u6211\u8eab\u4efb\u638c\u95e8\uff0c\u4e5f\u51b3\u4e0d\u80fd\u8f7b\u6613\u9976\u4f60\uff01\u201d\u968f\u5373\u4fbf\u4f20\u4e0b\u547d\u4ee4\u9053\uff1a\u201c\u5362\u3001\u6797\u4e24\u4f4d\u5e08\u5144\uff0c\u4f60\u4eec\u5148\u6551\u6cbb\u8c22 \u5e08\u5ac2\uff0c\u770b\u5979\u662f\u53d7\u4e86\u4ec0\u4e48\u4f24\uff1f\u5b59\u5e08\u59d0\uff0c\u4f60\u628a\u83b2\u513f\u8d76\u5feb\u62b1\u51fa\u53bb\u3002\u201d\u8bf4\u8bdd\u7684\u610f\u601d\u5373\u662f\u8981\u5355\u72ec\u5bf9\u4ed8\u7f2a \u592b\u4eba\u3002\u5979\u8bdd\u58f0\u672a\u4e86\uff0c\u90a3\u7f2a\u592b\u4eba\u6479\u5730\u53c8\u662f\u4e00\u6293\u6293\u6765\uff0c\u9634\u607b\u607b\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u9976\u6211\uff0c\u53c8\u5f85\u5982\u4f55\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u5b9d\u5251\u4e00\u6307\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u4f60\u662f\u4ec0\u4e48\u4e00\u54c1\u592b\u4eba\u3001\u4e8c\u54c1\u592b\u4eba\uff0c\u4f60\u4f24\u5bb3\u4e86\u6c13\u5c71\u6d3e \u7684\u5f1f\u5b50\uff0c\u5c31\u5f97\u7ed9\u6211\u78d5\u5934\u8ba4\u9519\uff0c\u4ee5\u540e\u4e0d\u8bb8\u518d\u8e0f\u4e0a\u6c13\u5c71\u534a\u6b65\uff01\u201d \u3000\u3000\u90a3\u7f2a\u592b\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u597d\u5927\u7684\u53e3\u6c14\uff0c\u54fc\uff0c\u54fc\uff0c\u4f60\u593a\u4e86\u6211\u7684\u5973\u513f\uff0c\u8fd8\u7adf\u6562\u8fd9\u6837\u6a2a\u86ee\uff1f\u597d\u5440\uff0c \u4f60\u662f\u6c13\u5c71\u6d3e\u7684\u638c\u95e8\uff0c\u6211\u5c31\u6309\u7167\u6b66\u6797\u89c4\u77e9\uff0c\u4e0e\u4f60\u8f83\u91cf\u8f83\u91cf\u5427\uff01\u6211\u4e0d\u6015\u4f60\u6709\u5b9d\u5251\uff0c\u4f60\u80dc\u5f97\u4e86\u6211\uff0c \u6211\u5c31\u4e0d\u8981\u5973\u513f\uff0c\u7acb\u523b\u4e0b\u5c71\uff0c\u8981\u662f\u6211\u8d62\u4e86\u5462\uff0c\u4f60\u53ef\u5f97\u628a\u5973\u513f\u4ea4\u8fd8\u7ed9\u6211\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u5bf9\u8fd9\u5987\u4eba\u5b9e\u5728\u662f\u8ba8\u538c\u5230\u4e86\u6781\u70b9\uff0c\u6068\u4e0d\u5f97\u628a\u5979\u65e9\u65e9\u8d76\u8dd1\uff0c\u7acb\u5373\u7b54\u9053\uff1a\u201c\u4f9d\u4f60\u5c31 \u662f\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u7f2a\u592b\u4eba\u8eab\u5f62\u4e00\u6643\uff0c\u8d81\u7740\u8c37\u4e4b\u534e\u7684\u5251\u52bf\u5c1a\u672a\u5408\u6210\u5706\u5708\uff0c\u53cc\u624b\u9f50\u626c\uff0c\u5de6\u624b\u98de \u51fa\u4e86\u4e09\u679a\u6307\u73af\uff0c\u5206\u6253\u8c37\u4e4b\u534e\u7684\u7a74\u9053\uff0c\u53f3\u624b\u98de\u51fa\u4e09\u679d\u5e26\u7740\u9752\u5149\u7684\u6bd2\u7bad\u3002\u8c37\u4e4b\u534e\u5927\u6012\uff0c\u957f\u5251\u4e00 \u5708\uff0c\u540c\u65f6\u4e00\u638c\u62cd\u51fa\uff0c\u5979\u8fd9\u4e00\u638c\uff0c\u8fd0\u7684\u662f\u5415\u56db\u5a18\u8863\u94b5\u771f\u4f20\u7684\u5c11\u9633\u7384\u529f\uff0c\u4e09\u679d\u6bd2\u7bad\uff0c\u7ed9\u5979\u7684\u638c\u529b \u4e00\u538b\uff0c\u767b\u65f6\u54cd\u51fa\u4e00\u7247\u7206\u88c2\u4e4b\u58f0\uff0c\u788e\u6210\u4e86\u65e0\u6570\u5c0f\u7247\uff0c\u5251\u5149\u626b\u8fc7\uff0c\u90a3\u4e09\u679a\u6307\u73af\uff0c\u767b\u65f6\u53d8\u6210\u4e86\u516d\u7247 \u7834\u94dc\uff0c\u7f2a\u592b\u4eba\u53eb\u58f0\u201c\u597d\u5389\u5bb3\uff01\u4f46\u8981\u4f24\u6211\uff0c\u53ea\u6015\u8fd8\u672a\u80fd\uff01\u201d\u8eab\u5f62\u75be\u9000\uff0c\u8c37\u4e4b\u534e\u521a\u521a\u4e24\u5251\uff0c\u90fd\u6ca1 \u6709\u523a\u4e2d\u3002 \u3000\u3000\u5979\u90a3\u6bd2\u7bad\u867d\u7136\u7c89\u788e\uff0c\u9752\u5149\u5374\u56db\u5904\u6d41\u6563\uff0c\u90b1\u5c71\u6d3e\u516d\u5927\u5f1f\u5b50\u4e4b\u53c1\u6797\u7afa\uff0c\u4e0d\u5c0f\u5fc3\u7ed9\u9752\u5149\u6cbe\u7740\u4e86 \u8863\u88f3\uff0c\u7acb\u5373\u4fbf\u71c3\u70e7\u8d77\u6765\uff0c\u6797\u7afa\u6025\u5fd9\u7528\u529b\u5c06\u8863\u88f3\u6323\u7834\uff0c\u8131\u51fa\u8eab\u6765\uff0c\u4f46\u5df2\u7ed9\u70e7\u7126\u4e86\u4e00\u7247\u76ae\u8089\u3002\u811a \u6b65\u8e09\u8dc4\uff0c\u51e0\u4e4e\u7acb\u8db3\u4e0d\u7a33\uff0c\u539f\u6765\u90a3\u9752\u5149\u662f\u6709\u6bd2\u7684\u78f7\u706b\uff0c\u6240\u4ee5\u8fd9\u6837\u5389\u5bb3\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u89c1\u5979\u7684\u6709\u6bd2\u6697\u7bad\u5c42\u51fa\u4e0d\u7a77\uff0c\u4e5f\u81ea\u6709\u70b9\u987e\u5fcc\uff0c\u6025\u5fd9\u8bf4\uff1a\u201c\u4f17\u5e08\u5144\u90fd\u8bf7\u51fa\u53bb\u5427\uff0c\u5c0f\u5fc3 \u6212\u5907\uff0c\u4e25\u9632\u5979\u8fd8\u6709\u515a\u7fbd\u5230\u6765\u3002\u201d\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8981\u4e0d\u662f\u6211\u8fd9\u51e0\u5e74\u82e6\u7ec3\u5c11\u9633\u7384\u529f\uff0c\u53ea\u6015\u4e5f\u8981\u906d\u5979 \u6bd2\u624b\u3002\u201d \u3000\u3000\u5362\u9053\u78f7\u548c\u7a0b\u6d69\u5c06\u8c22\u4e91\u771f\u6276\u51fa\u5ead\u9662\uff0c\u53ea\u89c1\u5979\u9762\u8272\u9752\u4e2d\u6cdb\u9ed1\uff0c\u624b\u5fc3\u662f\u50f5\u51b7\u5982\u51b0\uff0c\u5e78\u559c\u9f3b\u7aef\u8fd8 \u6709\u6c14\u606f\uff0c\u5362\u9053\u78f7\u5927\u53eb\u9053\uff1a\u201c\u638c\u95e8\u5c0f\u5fc3\uff0c\u5207\u4e0d\u53ef\u7ed9\u8fd9\u5996\u5987\u7684\u6bd2\u638c\u51fb\u4e2d\uff01\u201d\u7a0b\u6d69\u4e5f\u53eb\u9053\uff1a\u201c\u638c \u95e8\uff0c\u5feb\u5c06\u5996\u5987\u5236\u4f0f\uff0c\u8feb\u5979\u62ff\u51fa\u89e3\u836f\u3002\u201d\u90a3\u8eb2\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8981\u6211\u62ff\u836f\u6551\u4eba\u5417\uff1f\u90a3\u5c31\u5feb\u5feb \u8ba4\u8f93\u4e86\u5427\uff0c\u514d\u5f97\u803d\u6401\u65f6\u5019\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u626c\u58f0\u95ee\u9053\uff1a\u201c\u8109\u606f\u5982\u4f55\uff1f\u201d\u5362\u9053\u78f7\u9053\uff1a\u201c\u8109\u606f\u5fae\u5f31\uff0c\u5c1a\u672a\u6c14\u7edd\uff01\u201d\u8c37\u4e4b\u534e\u4e00\u542c\uff0c \u7565\u7565\u5bbd\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u623f\u95f4\u91cc\u90a3\u7389\u5323\u4e4b\u4e2d\uff0c\u8fd8\u6709\u4e09\u7c92\u78a7\u7075\u4e39\uff0c\u4f60\u53eb\u4f8d\u5973\u627e\u51fa\u6765\uff0c\u5148\u7ed9\u5e08\u5ac2\u670d \u4e0b\u3002\u201d\u90a3\u7f2a\u592b\u4eba\u542c\u4e86\uff0c\u53c8\u662f\u54c8\u54c8\u5927\u7b11\u3002\u8c37\u4e4b\u534e\u4e00\u5251\u523a\u53bb\uff0c\u65a5\u9053\uff1a\u201c\u5996\u5987\uff0c\u4f60\u5f88\u5f97\u610f\u4e48\uff1f\u201d \u3000\u3000\u90a3\u7f2a\u592b\u4eba\u6325\u8896\u4e00\u62c2\uff0c\u5378\u5f00\u4e86\u8c37\u4e4b\u534e\u7684\u5251\u52bf\uff0c\u8bf4\u9053\uff1a\u201c\u8c37\u4e4b\u534e\uff0c\u6211\u7b11\u4f60\u7ed3\u4e86\u75ae\u75a4\u5fd8\u4e86\u75db\uff0c \u5389\u80dc\u7537\u7ed9\u4f60\u5403\u7684\u82e6\u5934\uff0c\u4f60\u53ef\u8fd8\u8bb0\u5f97\u4e48\uff1f\u201d\u8c37\u4e4b\u534e\u6014\u4e86\u4e00\u6014\uff0c\u53ea\u542c\u5f97\u90a3\u7f2a\u592b\u4eba\u53c8\u683c\u683c\u7b11\u9053\uff1a \u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u624b\u638c\u4e0a\u6d82\u7684\u6bd2\u836f\uff0c\u5c31\u6b63\u662f\u5389\u80dc\u7537\u5f53\u5e74\u4ee4\u4f60\u5403\u4e8f\u7684\u90a3\u79cd\u5947\u836f\uff0c\u4f60\u5e94\u8be5\u77e5\u9053\uff0c\u8fd9 \u79cd\u6bd2\u836f\u662f\u5426\u5929\u5c71\u96ea\u83b2\u6240\u80fd\u89e3\u6551\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u542c\u4e86\u8fd9\u8bdd\uff0c\u81ea\u662f\u5403\u4e86\u4e00\u60ca\uff0c\u4f46\u4e5f\u4e0d\u81f3\u4e8e\u50cf\u7f2a\u592b\u4eba\u6240\u6599\u7684\u90a3\u822c\u60ca\u60f6\u5931\u63aa\uff0c\u8981\u77e5\u8fd9\u79cd \u6bd2\u836f\u867d\u7136\u5389\u5bb3\u975e\u5e38\uff0c\u4f46\u5f53\u5e74\u8c37\u4e4b\u534e\u4e2d\u6bd2\u4e4b\u540e\uff0c\u4ed7\u7740\u674e\u6c81\u6885\u6240\u8d60\u7684\u5929\u5c71\u96ea\u83b2\uff0c\u4e5f\u6328\u8fc7\u4e86\u4e09\u5e74\u3002 \u5982\u4eca\u8c37\u4e4b\u534e\u5e08\u5ac2\u8c22\u4e91\u771f\u7684\u529f\u529b\uff0c\u51b3\u4e0d\u81f3\u5f31\u4e8e\u4e03\u5e74\u524d\u7684\u8c37\u4e4b\u534e\uff0c\u6240\u4ee5\u8c37\u4e4b\u534e\u5728\u4e00\u60ca\u4e4b\u540e\uff0c\u53cd\u800c \u653e\u4e0b\u4e86\u5fc3\u4e0a\u7684\u77f3\u5934\uff0c\u60f3\u9053\uff1a\u201c\u5018\u82e5\u771f\u662f\u8fd9\u79cd\u6bd2\u836f\u7684\u8bdd\uff0c\u6709\u5929\u5c71\u96ea\u83b2\u6240\u6ce1\u5236\u7684\u78a7\u7075\u4e39\uff0c\u77ed\u671f\u4e4b \u5185\uff0c\u6211\u5e08\u5ac2\u7684\u6027\u547d\uff0c\u5b9a\u53ef\u65e0\u5fe7\u3002\u201d \u3000\u3000\u4f46\u4ee4\u5f97\u8c37\u4e4b\u534e\u5403\u60ca\u7684\u4e0d\u5355\u662f\u8fd9\u79cd\u6bd2\u836f\uff0c\u800c\u662f\u5bf9\u65b9\u600e\u61c2\u5f97\u8fd9\u79cd\u6bd2\u836f\uff1f\u53c8\u600e\u77e5\u9053\u5389\u80dc\u7537\u5f53\u5e74 \u4f7f\u7528\u7684\u662f\u8fd9\u79cd\u6bd2\u836f\uff1f\u636e\u8c37\u4e4b\u534e\u6240\u77e5\uff0c\u5f53\u5e74\u5389\u80dc\u7537\u5728\u4e34\u6b7b\u4e4b\u524d\uff0c\u66fe\u628a\u4e54\u5317\u6ec7\u7684\u6b66\u529f\u79d8\u590d\u9001\u7ed9\u4e86 \u91d1\u4e16\u9057\uff0c\u4f46\u90a3\u90e8\u53d6\u81ea\u4e03\u9634\u6559\u7684\u767e\u6bd2\u771f\u7ecf\uff0c\u5728\u5389\u80dc\u7537\u6b7b\u540e\uff0c\u5374\u662f\u4e0d\u77e5\u4e0b\u843d\u3002 \u3000\u3000\u5982\u4eca\uff0c\u8c37\u4e4b\u534e\u542c\u5f97\u8fd9\u7f2a\u592b\u4eba\u63d0\u8d77\u4e86\u5389\u80dc\u7537\uff0c\u81ea\u4e0d\u514d\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u96be\u9053\u8fd9\u5996\u5987\u548c\u5389\u80dc\u7537\u7adf \u6709\u4ec0\u4e48\u5173\u8054\uff1f\u53c8\u96be\u9053\u90a3\u672c\u767e\u6bd2\u771f\u7ecf\uff0c\u7adf\u662f\u843d\u5230\u4e86\u5979\u7684\u624b\u4e0a\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u4e00\u751f\u5403\u5c3d\u4e86\u5389\u80dc\u7537\u7684\u82e6\u5934\uff0c\u4e00\u60f3\u5230\u7f2a\u592b\u4eba\u53ef\u80fd\u4e0e\u5389\u80dc\u7537\u6709\u751a\u6e0a\u6e90\uff0c\u7981\u4e0d\u4f4f\u66f4\u662f\u6012 \u6c14\u52c3\u53d1\uff0c\u767b\u65f6\u5168\u529b\u65bd\u4e3a\uff0c\u628a\u7384\u5973\u5251\u6cd5\u4e0e\u5c11\u9633\u7384\u529f\u90fd\u5c3d\u91cf\u65bd\u5c55\u51fa\u6765\uff0c\u4e00\u5251\u7d27\u4f3c\u4e00\u5251\uff0c\u628a\u7f2a\u592b\u4eba \u7d27\u7d27\u8feb\u7740\u3002 \u3000\u3000\u8fd9\u7f2a\u592b\u4eba\u6b66\u529f\u867d\u7136\u4e86\u5f97\uff0c\u4f46\u6bd4\u8d77\u5f53\u5e74\u7684\u5389\u80dc\u7537\uff0c\u5374\u8fd8\u5dee\u5f97\u9887\u8fdc\uff0c\u8c37\u4e4b\u534e\u4e0e\u5979\u6597\u4e86\u4e09\u5341\u6765 \u62db\uff0c\u5df2\u662f\u7565\u7565\u5360\u4e86\u4e0a\u98ce\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u53ea\u8981\u4e0d\u7ed9\u5979\u7684\u5582\u6bd2\u6697\u5668\u4e0e\u6bd2\u638c\u6253\u4e2d\uff0c\u6211\u603b\u53ef\u4ee5\u8feb\u5f97\u5979 \u4ea4\u51fa\u89e3\u836f\u3002\u201d \u3000\u3000\u6c13\u5c71\u6d3e\u7684\u5f1f\u5b50\u9000\u4e86\u51fa\u53bb\uff0c\u6c5f\u5357\u5374\u4e00\u76f4\u7ad9\u5728\u95e8\u53e3\uff0c\u63a2\u5934\u63a2\u8111\u7684\u5411\u91cc\u9762\u5f20\u671b\uff0c\u4ed6\u89c1\u4e86\u7f2a\u592b\u4eba \u7684\u672c\u9886\uff0c\u5fc3\u4e2d\u6697\u6697\u5403\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u597d\u9669\uff0c\u597d\u9669\uff0c\u521a\u624d\u5728\u90a3\u5c71\u8def\u4e0a\uff0c\u6211\u82e5\u4e0d\u662f\u6709\u4eba\u6697\u4e2d\u76f8\u52a9\uff0c \u5b9a\u7136\u96be\u9003\u5979\u7684\u6bd2\u624b\u4e86\u3002\u53ef\u662f\uff0c\u8fd9\u6697\u4e2d\u76f8\u52a9\u4e8e\u6211\u7684\uff0c\u5018\u975e\u91d1\u5927\u4fa0\uff0c\u5374\u53c8\u662f\u8c01\u5462\uff1f\u201d\u4ed6\u4e00\u9762\u5403 \u60ca\uff0c\u5f85\u770b\u5230\u8c37\u4e4b\u534e\u5360\u4e86\u4e0a\u98ce\uff0c\u53c8\u4e0d\u7981\u7709\u98de\u8272\u821e\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u679c\u7136\u4e0d\u51fa\u6211\u6c5f\u5357\u6240\u6599\uff0c\u8fd9\u5996\u5987 \u662f\u90aa\u6d3e\u574f\u4eba\u3002\u8c37\u5973\u4fa0\uff0c\u4f60\u4e00\u5b9a\u8981\u7ed9\u5979\u5403\u70b9\u82e6\u5934\uff0c\u5207\u4e0d\u53ef\u8f7b\u6613\u5c06\u5979\u9976\u4e86\u3002\u201d\u4ed6\u81ea\u8a00\u81ea\u8bed\u5730\u56b7\u4e86 \u4e00\u4f1a\uff0c\u5ffd\u5730\u8054\u60f3\u5230\u4e00\u4e2a\u795e\u79d8\u7684\u4eba\u7269\uff0c\u7981\u4e0d\u4f4f\u5fc3\u5934\u4e00\u98a4\uff01 \u3000\u3000\u4f60\u9053\u6c5f\u5357\u60f3\u8d77\u4e86\u8c01\uff1f\u539f\u6765\u662f\u60f3\u8d77\u4ed6\u90a3\u4e0d\u77e5\u540d\u7684\u795e\u79d8\u83ab\u6d4b\u7684\u5929\u9b54\u6559\u4e3b\u3002\u4ed6\u89c1\u8fd9\u7f2a\u592b\u4eba\u6240\u7528 \u7684\u90aa\u6d3e\u6b66\u529f\u5c42\u51fa\u4e0d\u7a77\uff0c\u800c\u4e14\u6709\u5404\u5f0f\u5404\u6837\u7684\u6bd2\u836f\u6697\u5668\uff0c\u5176\u4e2d\u6709\u4e00\u79cd\u80fd\u53d1\u706b\u7130\u7684\u6bd2\u7bad\uff0c\u4e0e\u59ec\u6653\u98ce \u6240\u8bf4\u7684\u5929\u9b54\u6559\u4e3b\u5411\u4ed6\u793a\u5a01\u6240\u53d1\u7684\u90a3\u79cd\u6bd2\u7bad\u53c8\u6b63\u76f8\u540c\uff0c\u6c5f\u5357\u4e0d\u4f4f\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u7f2a\u592b\u4eba\u5c31 \u662f\u5929\u9b54\u6559\u4e3b\uff1f\u201d\u4f46\u968f\u5373\u60f3\u5230\uff1a\u201c\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\uff01\u636e\u59ec\u5927\u54e5\u6240\u8bf4\uff0c\u90a3\u5929\u9b54\u6559\u4e3b\u867d\u7136\u8499\u4f4f\u9762\uff0c\u4f46\u5374 \u662f\u8eab\u6750\u70af\u5a1c\uff0c\u51ed\u4ed6\u7684\u7ecf\u9a8c\u770b\u6765\uff0c\u6700\u591a\u662f\u4e8c\u5341\u6765\u5c81\u7684\u5c11\u5973\uff0c\u800c\u8fd9\u4e2a\u6d51\u8eab\u73e0\u5149\u5b9d\u6c14\u7684\u7f2a\u592b\u4eba\uff0c\u5c3d \u7ba1\u5979\u6781\u529b\u4fee\u9970\uff0c\u5374\u5df2\u662f\u5fae\u5fae\u53d1\u80d6\uff0c\u773c\u89d2\u4e5f\u53ef\u4ee5\u770b\u51fa\u6709\u76b1\u7eb9\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u662f\u76f8\u4fe1\u59ec\u6653\u98ce\u7684\u89c2\u5bdf\u80fd\u529b\u7684\uff0c\u4e0d\u8fc7\uff0c\u59ec\u6653\u98ce\u5bf9\u90a3\u5929\u9b54\u6559\u4e3b\u7684\u5224\u65ad\uff0c\u4e5f\u53ea\u662f\u63a8\u6d4b\u4e4b \u8a00\uff0c\u4ed6\u5230\u5e95\u8fd8\u6ca1\u6709\u89c1\u8fc7\u5929\u9b54\u6559\u4e3b\u7684\u5e90\u5c71\u771f\u9762\u3002\u800c\u773c\u524d\u8fd9\u4e2a\u4e2d\u5e74\u53d1\u80d6\u7684\u7f2a\u592b\u4eba\uff0c\u5374\u6709\u51e0\u4e2a\u7279\u70b9 \u4e0e\u59ec\u6653\u98ce\u6240\u8bf4\u7684\u90a3\u4e2a\u5929\u9b54\u6559\u4e3b\u76f8\u540c\uff0c\u56e0\u6b64\u6c5f\u5357\u867d\u89c9\u5f97\u6709\u70b9\u201c\u4e0d\u5bf9\u201d\uff0c\u4ecd\u7136\u96be\u514d\u6000\u7591\u3002 \u3000\u3000\u6b63\u5728\u6c5f\u5357\u60ca\u7591\u4e0d\u5b9a\u3001\u6697\u5730\u91cc\u81ea\u8a00\u81ea\u8bed\u4e4b\u9645\uff0c\u5ffd\u89c1\u6709\u4e24\u4e2a\u4eba\u6025\u5954\u800c\u6765\uff0c\u6c5f\u5357\u521a\u8ba4\u51fa\u8dd1\u5728\u524d \u9762\u7684\u90a3\u4e2a\u662f\u8def\u82f1\u8c6a\uff0c\u4fbf\u542c\u5f97\u4ed6\u7c97\u7740\u55d3\u5b50\u56b7\u9053\uff1a\u201c\u7980\u638c\u95e8\uff0c\u8fd9\u5996\u5987\u7684\u6765\u5386\u6211\u4eec\u5df2\u7ecf\u67e5\u51fa\u5566\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u89e3\u5f00\u4e86\u7f2a\u592b\u4eba\u7684\u4e00\u62db\u653b\u51fb\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u5996\u5987\u7a76\u662f\u4f55\u4eba\uff1f\u201d\u5728\u8def\u82f1\u8c6a\u540e\u9762\u7684\u767d \u82f1\u6770\u7b54\u9053\uff1a\u201c\u5979\u662f\u5929\u9b54\u6559\u7684\u4e00\u4e2a\u91cd\u8981\u4eba\u7269\uff0c\u4e5d\u6210\u5c31\u662f\u5929\u9b54\u6559\u4e3b\uff01\u201d \u3000\u3000\u539f\u6765\u5728\u6c13\u5c71\u6d3e\u4f17\u5f1f\u5b50\u4e2d\uff0c\u767d\u82f1\u6770\u6700\u4e3a\u7cbe\u660e\u80fd\u5e72\uff0c\u6545\u6b64\u521a\u624d\u8c37\u4e4b\u534e\u6d3e\u4ed6\u548c\u8def\u82f1\u8c6a\u53bb\u62db\u5f85\u90a3 \u4e24\u4e2a\u8f7f\u592b\uff0c\u767d\u82f1\u6770\u4e0e\u8def\u82f1\u8c6a\u5546\u8ba1\u4e00\u4e0b\uff0c\u8bf7\u90a3\u4e24\u4e2a\u8f7f\u592b\u559d\u9152\uff0c\u6545\u610f\u6311\u9017\u4ed6\u4eec\u8c08\u8bba\u6b66\u529f\uff0c\u53c8\u6545\u610f \u62ff\u9ad8\u5e3d\u5b50\u7ed9\u4ed6\u4eec\u6234\uff0c\u79f0\u8d5e\u4ed6\u4eec\u7684\u6b66\u529f\u4e86\u5f97\u3002 \u3000\u3000\u767d\u3001\u8def\u4e8c\u4eba\u540d\u5217\u6c13\u5c71\u516d\u5927\u5f1f\u5b50\u4e4b\u4e2d\uff0c\u5730\u4f4d\u4ec5\u5728\u8c37\u4e4b\u534e\u3001\u7ffc\u4ef2\u725f\u4e0e\u7a0b\u6d69\u4e09\u4eba\u4e4b\u4e0b\uff0c\u4ed6\u4eec\u5728 \u6c13\u5c71\u6d3e\u7684\u5730\u4f4d\uff0c\u90a3\u4e24\u4e2a\u8f7f\u592b\u4e43\u662f\u77e5\u9053\u7684\uff0c\u6240\u4ee5\u4ed6\u4eec\u5f97\u5230\u81ea\u3001\u8def\u4e8c\u4eba\u4eb2\u81ea\u62db\u5f85\uff0c\u4fbf\u4e0d\u7531\u5f97\u6df1\u611f \u8363\u5e78\uff0c\u5927\u51fa\u610f\u5916\uff0c\u518d\u52a0\u4e0a\u51e0\u9876\u9ad8\u5e3d\u5b50\u4e8c\u6234\uff0c\u66f4\u662f\u9676\u9676\u7136\u4e86\u3002 \u3000\u3000\u559d\u4e86\u51e0\u676f\uff0c\u767d\u82f1\u6770\u5411\u4ed6\u4eec\u8bf7\u6559\u59d3\u540d\uff0c\u90a3\u4e24\u4e2a\u8f7f\u592b\u8e0c\u8e87\u7247\u523b\uff0c\u7ec8\u4e8e\u8bf4\u4e86\u51fa\u6765\uff0c\u539f\u6765\u8fd9\u4e24\u4e2a \u8f7f\u592b\u7adf\u662f\u6c5f\u5357\u4e24\u4e2a\u5c0f\u5e2e\u4f1a\u7684\u8235\u4e3b\u3002 \u3000\u3000\u767d\u3001\u8def\u4e8c\u4eba\u90fd\u662f\u6d77\u91cf\uff0c\u4e0d\u505c\u7684\u529d\u90a3\u4e24\u4e2a\u8f7f\u592b\u559d\u9152\uff0c\u770b\u4ed6\u4eec\u5df2\u6709\u4e86\u4e03\u516b\u5206\u9152\u610f\u4e4b\u65f6\uff0c\u767d\u82f1 \u6770\u5ffd\u5730\u5c06\u9152\u676f\u91cd\u91cd\u4e00\u987f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6709\u4e00\u53e5\u8bdd\uff0c\u4e0d\u77e5\u8be5\u4e0d\u8be5\u8bf4\uff1f\u4f46\u82e5\u4e0d\u8bf4\uff0c\u5374\u53c8\u597d\u4e0d\u95f7\u715e\u4eba \u4e5f\uff01\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u8f7f\u592b\u5df2\u996e\u81f3\u9152\u9163\u8033\u70ed\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u53eb\u8d77\u6765\u9053\uff1a\u201c\u767d\u5927\u4fa0\uff0c\u54b1\u4eec\u867d\u7136\u4eca\u65e5\u521d\u4ea4\uff0c\u4f46 \u5374\u662f\u4e00\u89c1\u5982\u6545\uff0c\u4f60\u6709\u4ec0\u4e48\u8bdd\u4f46\u8bf4\u65e0\u59a8\uff01\u82e5\u7136\u4e0d\u8bf4\uff0c\u90a3\u53cd\u800c\u662f\u89c1\u5916\u4e86\u3002\u201d \u3000\u3000\u767d\u82f1\u6770\u6545\u610f\u4f5c\u4e86\u4e2a\u4e3a\u96be\u7684\u795e\u6c14\uff0c\u7136\u540e\u8bf4\u9053\uff1a\u201c\u4e8c\u5144\u90fd\u662f\u723d\u5feb\u4e4b\u4eba\uff0c\u6211\u59d3\u767d\u7684\u4e5f\u662f\u4e2a\u723d\u76f4 \u7684\u813e\u6c14\uff0c\u5fc3\u6709\u6240\u7591\uff0c\u4fbf\u5982\u9aa8\u9cd7\u5728\u5589\uff0c\u4e0d\u5410\u4e0d\u5feb\uff01\u597d\uff0c\uff0c\u82e5\u662f\u6211\u8bf4\u9519\u4e86\uff0c\u8bf7\u4e24\u4f4d\u5144\u53f0\u6d77\u91cf\u5305 \u6db5\uff01\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u8f7f\u592b\u542c\u4ed6\u5de6\u4e00\u4e2a\u201c\u5144\u53f0\u201d\u3001\u53f3\u4e00\u4e2a\u201c\u5144\u53f0\u201d\u76f8\u79f0\uff0c\u65e9\u5df2\u98d8\u98d8\u7136\u4e86\uff0c\u4f46\u9152\u9189\u8fd8\u6709\u4e09 \u5206\u9192\uff0c\u4e0d\u7ea6\u800c\u540c\u4ed6\u8bf4\u9053\uff1a\u201c\u767d\u5927\u4fa0\u6240\u7591\u4f55\u4e8b\uff1f\u82e5\u662f\u6211\u4eec\u77e5\u9053\u7684\uff0c\u5b9a\u5f53\u5949\u544a\u3002\u201d\u4ed6\u4eec\u4e5f\u60f3\u5230\u4e86 \u767d\u82f1\u6770\u53ef\u80fd\u8981\u95ee\u5173\u4e8e\u7f2a\u592b\u4eba\u7684\u79d8\u5bc6\uff0c\u6240\u4ee5\u5148\u8bbe\u4e0b\u9041\u8f9e\uff0c\u82e5\u662f\u4e0d\u53ef\u4ee5\u8bf4\u7684\uff0c\u5c31\u63a8\u4f5c\u4e0d\u77e5\u3002 \u3000\u3000\u54ea\u77e5\u767d\u82f1\u6770\u5374\u5e76\u4e0d\u5148\u95ee\u7f2a\u592b\u4eba\uff0c\u800c\u662f\u6df1\u6df1\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u773c\u5149\u7d27\u7d27\u7684\u6ce8\u89c6\u7740\u4ed6\u4eec\u95f4\u9053\uff1a \u201c\u6211\u6709\u4e00\u4e8b\u4e0d\u660e\uff0c\u4ee5\u4e24\u4f4d\u5144\u53f0\u7684\u672c\u9886\u548c\u5730\u4f4d\uff0c\u5728\u6c5f\u7322\u4e0a\u4e5f\u5c3d\u53ef\u53f1\u54a4\u98ce\u4e91\u4e86\uff0c\u4f55\u4ee5\u7518\u4ee5\u8235\u4e3b\u4e4b \u5c0a\uff0c\u4e3a\u4eba\u53ae\u5f79\uff1f\u96be\u9053\u5c31\u56e0\u4e3a\u8d35\u4e3b\u4eba\u662f\u4e2a\u63d0\u7763\u592b\u4eba\uff0c\u4f60\u4eec\u5c31\u613f\u610f\u5c48\u819d\u5b98\u5e9c\u5417\uff1f\u542c\u4e24\u4f4d\u5144\u53f0\u7684\u8c6a \u8fc8\u8c08\u5410\uff0c\u5374\u53c8\u4e0d\u50cf\u662f\u8fd9\u7b49\u8d8b\u708e\u9644\u52bf\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u8f7f\u592b\u672c\u6765\u5c31\u4e0d\u5927\u7518\u5fc3\u7ed9\u7f2a\u592b\u4eba\u62ac\u8f7f\u7684\uff0c\u5982\u4eca\u518d\u7ed9\u767d\u82f1\u6770\u7528\u5c16\u523b\u7684\u8bf4\u8bdd\u4e00\u6fc0\uff0c\u7981\u4e0d \u4f4f\u9762\u7ea2\u8fc7\u8033\uff0c\u671f\u671f\u827e\u827e\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u867d\u6bd4\u4e0d\u4e0a\u767d\u5927\u4fa0\u540d\u9707\u6c5f\u6e56\uff0c\u4f46\u4e5f\u975e\u65e0\u540d\u4e4b\u8f88\uff0c\u83ab\u8bf4\u4e00 \u4e2a\u63d0\u7763\uff0c\u518d\u5927\u7684\u5b98\u513f\uff0c\u6211\u4eec\u4e5f\u638c\u4e0d\u4f1a\u542c\u4ed6\u5dee\u8feb\uff0c\u53ea\u662f\uff0c\u53ea\u662f----\u201d\u767d\u82f1\u6770\u9053\uff1a\u201c\u82e5\u6709\u82e6\u8877\uff0c \u4e0d\u8bf4\u4e5f\u7f62\uff01\u5f53\u7136\uff0c\u6211\u662f\u77e5\u9053\u4e24\u4f4d\u7684\uff0c\u522b\u4eba\u561b\uff0c\u90a3\u5c31\u4e0d\u6562\u8bf4\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u8f7f\u592b\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u7ec8\u4e8e\u56b7\u51fa\u6765\u9053\uff1a\u201c\u6211\u4eec\u7518\u5fc3\u7ed9\u5979\u62ac\u8f7f\u5b50\uff0c\u5e76\u975e\u56e0\u4e3a\u5979\u662f\u63d0\u7763 \u592b\u4eba\uff0c\u800c\u662f\u56e0\u4e3a\u5979\u7528\u5929\u9b54\u6559\u7684\u91d1\u724c\u5dee\u4f7f\uff0c\u6211\u4eec\u90fd\u662f\u65b0\u8fd1\u5165\u4e86\u5929\u9b54\u6559\uff0c\u5bf9\u672c\u6559\u91d1\u724c\uff0c\u52bf\u96be\u8fdd \u6297\u3002\u201d \u3000\u3000\u767d\u82f1\u6770\u89c1\u95fb\u6781\u5e7f\uff0c\u5929\u9b54\u6559\u867d\u7136\u9690\u79d8\uff0c\u4f46\u6700\u8fd1\u4e00\u5e74\uff0c\u5728\u6c5f\u7322\u4e0a\u591a\u6709\u6d3b\u52a8\uff0c\u767d\u82f1\u6770\u4e5f\u7565\u6709\u6240 \u77e5\uff0c\u5f53\u4e0b\u4fbf\u4f5c\u51fa\u60f6\u6050\u7684\u795e\u60c5\u8bf4\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u8bf7\u6055\u767d\u67d0\u65e0\u77e5\u3002\u4f46\u542c\u8bf4\u5929\u9b54\u6559\u90fd\u662f\u5973\u7684\uff0c\u73b0 \u5728\u6536\u7537\u6559\u5f92\u5417\uff1f\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u8f7f\u592b\u9053\uff1a\u201c\u5929\u9b54\u6559\u4e3b\u662f\u4e2a\u5973\u7684\uff0c\u53bb\u5e74\u542c\u8bf4\u591a\u4e86\u4e00\u4e2a\u7537\u526f\u5e2e\u4e3b\uff0c\u6240\u4ee5\u4e5f\u6709\u8bb8\u591a\u6c5f\u6e56 \u4eba\u7269\u8fdb\u4e86\u5929\u9b54\u6559\u4e86\u3002\u6211\u4eec\u56e0\u4e3a\u662f\u5c0f\u5e2e\u4f1a\uff0c\u4ee5\u4e3a\u8fdb\u4e86\u5929\u9b54\u6559\uff0c\u5927\u6811\u4e4b\u4e0b\u597d\u906e\u836b\uff0c\u54ea\u77e5\u5140\u662f\u88ab\u4eba \u6b3a\u8fb1\uff01\u201d \u3000\u3000\u767d\u82f1\u6770\u8ba9\u4ed6\u4eec\u53d1\u4e86\u4e00\u901a\u7262\u9a9a\uff0c\u518d\u95ee\u9053\uff1a\u201c\u8fd9\u7f2a\u592b\u4eba\u662f\u8d35\u6559\u7684\u5417\uff1f\u201d\u90a3\u4e24\u4e2a\u8f7f\u592b\u9053\uff1a\u201c\u6211 \u4eec\u5728\u5929\u9b54\u6559\u4e2d\uff0c\u53ea\u662f\u88ab\u4eba\u5dee\u5524\u7684\u5c0f\u5352\uff0c\u76f4\u5230\u5982\u4eca\uff0c\u8fd8\u672a\u8499\u6b63\u526f\u6559\u4e3b\u53ec\u89c1\u8fc7\u3002\u6b64\u6b21\u6211\u4eec\u5949\u4e86\u6559 \u4e3b\u4e4b\u547d\uff0c\u53bb\u670d\u4f8d\u8fd9\u7f2a\u592b\u4eba\uff0c\u5979\u4e5f\u672a\u66fe\u5411\u6211\u4eec\u8868\u9732\u8eab\u4efd\u3002\u4e0d\u77e5\u5230\u5e95\u662f\u6559\u4e3b\u8fd8\u662f\u771f\u7684\u63d0\u7763\u592b\u4eba\uff1f\u201d \u3000\u3000\u767d\u82f1\u6770\u5957\u4e0d\u51fa\u66f4\u591a\u7684\u8bf4\u8bdd\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u4e0d\u8fc7\u662f\u5929\u9b54\u6559\u7684\u5c0f\u811a\u8272\uff0c\u770b\u6765\uff0c\u4ed6\u4eec\u6240\u77e5\u9053 \u7684\u4e5f\u53ea\u662f\u8fd9\u4e48\u591a\u4e86\u3002\u201d\u4e8e\u662f\u8d76\u5feb\u7684\u5c06\u4e24\u4e2a\u8f7f\u592b\u704c\u9189\uff0c\u4fbf\u6025\u6025\u5fd9\u5fd9\u8d76\u6765\u5411\u8c37\u4e4b\u534e\u62a5\u544a\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u542c\u8bf4\u8fd9\u7f2a\u592b\u4eba\u5f88\u53ef\u80fd\u4fbf\u662f\u5929\u9b54\u6559\u4e3b\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u968f\u5373\u60f3\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u4eec\u8fd9\u79cd\u90aa\u6559 \u98ce\u9a6c\u725b\u7d20\u4e0d\u76f8\u6d89\uff0c\u4f60\u4e3a\u4f55\u5230\u6211\u7684\u6c13\u5c71\u65e0\u7406\u53d6\u95f9\u3002\u201d \u3000\u3000\u90a3\u7f2a\u592b\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u5bf9\u767d\u82f1\u6770\u6307\u5979\u662f\u5929\u9b54\u6559\u4e3b\u4e4b\u8a00\uff0c\u65e2\u4e0d\u627f\u8ba4\uff0c\u4ea6\u4e0d\u5426\u8ba4\uff0c\u5979\u602a\u7b11\u4e86\u4e00 \u9635\uff0c\u7a81\u7136\u53c8\u5411\u8c37\u4e4b\u534e\u5c55\u5f00\u4e86\u72c2\u98ce\u822c\u7684\u653b\u51fb\uff0c\u540c\u65f6\u9a82\u9053\uff1a\u201c\u4f60\u7adf\u6562\u8bf4\u6211\u4eec\u5929\u9b54\u6559\u662f\u90aa\u6559\uff0c\u5c31\u51ed \u8fd9\u4e00\u70b9\uff0c\u6211\u4e0e\u4f60\u4eec\u6c13\u5c71\u6d3e\u7684\u4ec7\u4fbf\u7ed3\u5b9a\u4e86\uff0c\u4f55\u51b5\u4f60\u8fd8\u5f3a\u5360\u6211\u7684\u5973\u513f\uff1f\u201d \u3000\u3000\u767d\u82f1\u6770\u529f\u529b\u8f83\u6df1\uff0c\u542c\u4e86\u7f2a\u592b\u4eba\u7684\u602a\u7b11\uff0c\u8fd8\u4e0d\u89c9\u5f97\u600e\u6837\uff0c\u8def\u82f1\u8c6a\u542c\u4e86\uff0c\u5374\u597d\u50cf\u7ed9\u4eba\u7528\u4e00\u6839 \u5229\u9488\u4ece\u8033\u9f13\u91cc\u523a\u8fdb\u53bb\u4e00\u822c\uff0c\u5341\u5206\u96be\u53d7\uff0c\u4ed6\u6027\u60c5\u66b4\u8e81\uff0c\u767b\u65f6\u62d4\u51fa\u8170\u5200\uff0c\u4fbf\u8981\u4e0a\u524d\u52a9\u6218\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u7684\u4f8d\u5973\u5fd9\u53eb\u9053\uff1a\u201c\u8def\u5e08\u53d4\uff0c\u8bf7\u9000\u4e0b\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u542c\u5f97\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c \u90a3\u7f2a\u592b\u4eba\u4ee5\u654f\u6377\u65e0\u4f26\u7684\u624b\u6cd5\u53d6\u51fa\u4e86\u4e00\u6761\u8f6f\u97ad\uff0c\u6dae\u7684\u4e00\u58f0\uff0c\u6b63\u6b63\u62bd\u4e2d\u4e86\u8def\u82f1\u8c6a\u7684\u624b\u80cc\uff0c\u8def\u82f1\u8c6a \u75db\u5f97\u5931\u58f0\u5927\u53eb\uff0c\u539f\u6765\u5979\u90a3\u6761\u8f6f\u97ad\u662f\u8638\u6ee1\u4e86\u874e\u5b50\u7c89\u7684\u6bd2\u97ad\uff0c\u97ad\u68a2\u53c8\u6709\u94a2\u523a\u5012\u987b\uff0c\u8def\u82f1\u8c6a\u7ed9\u5979\u4e00 \u97ad\u62bd\u4e2d\uff0c\u5982\u7740\u706b\u70e7\uff0c\u624b\u80cc\u4e0a\u7684\u76ae\u8089\u4e5f\u88ab\u6495\u53bb\u4e86\u4e00\u5927\u7247\u3002\u4f46\u662f\u7531\u4e8e\u8c37\u4e4b\u534e\u7684\u547d\u4ee4\uff0c\u4e0d\u8bb8\u522b\u4eba\u76f8 \u52a9\uff0c\u4ed6\u53ea\u5f97\u5fcd\u7740\u6124\u6012\uff0c\u9000\u51fa\u95e8\u5916\u3002 \u3000\u3000\u7f2a\u592b\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u6c13\u5c71\u6d3e\u65e2\u8981\u6043\u591a\u4e3a\u80dc\uff0c\u8bf7\u6055\u6211\u53ea\u597d\u53d6\u51fa\u5175\u5668\u5949\u966a\u4e86\u3002\u201d\u5176\u5b9e\u8def\u82f1 \u8c6a\u6839\u672c\u672a\u66fe\u51fa\u62db\uff0c\u4fbf\u5373\u53d7\u4f24\u9000\u4e0b\uff0c\u8bf4\u4e0d\u4e0a\u662f\u6c13\u5c71\u6d3e\u201c\u6043\u591a\u4e3a\u80dc\u201d\uff0c\u7f2a\u592b\u4eba\u4e0d\u8fc7\u662f\u56e0\u4e3a\u5df2\u5904\u5728 \u4e0b\u98ce\uff0c\u6240\u4ee5\u627e\u4e2a\u501f\u53e3\u800c\u5df2\u3002 \u3000\u3000\u5979\u6bd2\u97ad\u5728\u624b\uff0c\u5982\u864e\u6dfb\u7ffc\uff0c\u4e00\u8f6e\u72c2\u653b\u731b\u626b\uff0c\u679c\u7136\u628a\u52a3\u52bf\u6273\u8f6c\u8fc7\u6765\u3002 \u3000\u3000\u4e14\u8bf4\u6c5f\u5357\u6b63\u5728\u601d\u7591\u4e0d\u5b9a\uff0c\u5ffd\u542c\u5f97\u8def\u3001\u767d\u4e8c\u4eba\u6307\u8bc1\u8fd9\u7f2a\u592b\u4eba\u4fbf\u662f\u5929\u9b54\u6559\u4e3b\uff0c\u4e0d\u7531\u5f97\u5fc3\u5934\u4e00 \u9707\uff0c\u4ed6\u662f\u4e2a\u6027\u60c5\u7387\u76f4\uff0c\u4e0d\u8ba1\u5229\u5bb3\u7684\u4eba\uff0c\u660e\u77e5\u7f2a\u592b\u4eba\u7684\u672c\u9886\u9ad8\u51fa\u4ed6\u4e0d\u77e5\u51e0\u500d\uff0c\u4e5f\u8981\u594b\u4e0d\u987e\u8eab\u7684 \u4e0a\u524d\u4e0e\u5979\u5bf9\u654c\u4e86\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u89c1\u6c5f\u5357\u7a81\u7136\u6251\u8fdb\u95e8\u6765\uff0c\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u6211\u65e0\u987b\u4f60\u5e2e\u52a9\uff0c\u8d76\u5feb\u9000\u5f00\uff01\u201d \u3000\u3000\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u5979\u62a2\u4e86\u6211\u7684\u513f\u5b50\uff0c\u6211\u975e\u5f97\u548c\u5979\u62fc\u547d\u4e0d\u53ef\uff01\u6211\u4e0d\u662f\u4f60\u6c13\u5c71\u6d3e\u7684\u95e8\u4e0b\uff0c\u6211\u53ef\u4ee5 \u4e0d\u542c\u4f60\u7684\u547d\u4ee4\uff01\u201d \u3000\u3000\u7f2a\u592b\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u9a82\u9053\uff1a\u201c\u6d51\u5c0f\u5b50\uff0c\u4f60\u80e1\u8bf4\u516b\u9053\uff0c\u8c01\u5e0c\u7f55\u4f60\u7684\u513f\u5b50\uff1f\u597d\u5427\uff0c\u4f60\u8981\u62fc \u547d\uff0c\u6211\u4e5f\u5c31\u987a\u4fbf\u9001\u4f60\u4e00\u5f20\u960e\u738b\u5e16\u5b50\uff01\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u6c13\u5c71\u60ca\u89c1\u9b54\u6c1b\u7f69\uff0c\u6765\u5386\u5982\u4f55\u5c1a\u672a\u660e\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000--------------- \u3000\u3000\u626c\u5251\u8f69\u5c45\u58eb \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u516b\u56de\u3000\u7d22\u5973\u767b\u95e8\u8f83\u8eab\u624b\u3000\u98de\u676f\u88c2\u6848\u70ab\u795e\u529f \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u516b\u56de\u3000\u7d22\u5973\u767b\u95e8\u8f83\u8eab\u624b\u3000\u98de\u676f\u88c2\u6848\u70ab\u795e\u529f \u3000\u3000\u7f2a\u592b\u4eba\u90a3\u6761\u8f6f\u97ad\uff0c\u6709\u5982\u6bd2\u86c7\u5410\u4fe1\uff0c\u4f38\u7f29\u81ea\u5982\uff0c\u8212\u5c55\u5f00\u6765\uff0c\u53ef\u8fbe\u4e00\u4e08\u5f00\u5916\uff0c\u6c5f\u5357\u8fd8\u672a\u6251\u5230 \u5979\u7684\u8ddf\u524d\uff0c\u53ea\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u5979\u7684\u6bd2\u97ad\u5df2\u5148\u5377\u5230\uff01 \u3000\u3000\u8c37\u4e4b\u534e\u8fde\u5fd9\u4e00\u5251\u523a\u53bb\uff0c\u5251\u5149\u97ad\u5f71\u4e4b\u4e2d\uff0c\u53ea\u89c1\u6c5f\u5357\u53cc\u624b\u62b1\u7740\u5934\u9885\uff0c\u8eab\u8eaf\u5f2f\u66f2\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c \u84e6\u5730\u4e00\u4e2a\u7b4b\u6597\u4fbf\u7ffb\u8fc7\u53bb\uff01 \u3000\u3000\u8fd9\u662f\u91d1\u4e16\u9057\u6240\u6388\u7684\u5947\u88ad\u529f\u592b\uff0c\u9976\u662f\u8fd9\u7f2a\u592b\u4eba\u89c1\u591a\u8bc6\u5e7f\uff0c\u4e5f\u672a\u66fe\u89c1\u8fc7\u5982\u6b64\u53e4\u602a\u7684\u8eab\u6cd5\uff0c\u5979 \u90a3\u6761\u6bd2\u97ad\uff0c\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u51e0\u4e4e\u662f\u8d34\u7740\u6c5f\u5357\u7684\u80cc\u810a\u626b\u8fc7\uff0c\u5374\u672a\u66fe\u4f24\u7740\u6c5f\u5357\u3002 \u3000\u3000\u53ea\u542c\u5f97\u6c5f\u5357\u5927\u559d\u4e00\u58f0\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53cc\u6307\u5df2\u6233\u5230\u4e86\u7f2a\u592b\u4eba\u4e73\u4e0b\u7684\u201c\u7389\u6cc9\u7a74\u201d\uff0c\u5979\u90a3 \u6761\u8f6f\u97ad\u6b63\u8981\u62db\u67b6\u8c37\u4e4b\u534e\u7684\u5b9d\u5251\uff0c\u767e\u5fd9\u4e2d\u65e0\u6687\u64a4\u56de\uff0c\u6c5f\u5357\u7684\u70b9\u7a74\u8eab\u624b\u662f\u7b2c\u4e00\u6d41\u529f\u592b\uff0c\u5c31\u5728\u5979\u95ea \u8eab\u4e4b\u9645\uff0c\u53cc\u6307\u4e00\u52fe\uff0c\u201c\u5636\u201d\u7684\u4e00\u58f0\uff0c\u4fbf\u52fe\u70c2\u4e86\u5979\u80f8\u524d\u7684\u4e00\u7247\u8863\u88f3\u3002\u7d27\u63a5\u7740\u201c\u6251\u901a\u201d\u4e00\u58f0\uff0c\u6c5f \u5357\u4e5f\u6eda\u51fa\u4e86\u4e00\u4e08\u5f00\u5916\u3002\u539f\u6765\u5c31\u5728\u4ed6\u52fe\u70c2\u7f2a\u592b\u4eba\u8863\u88f3\u7684\u540c\u65f6\uff0c\u4ed6\u4e5f\u7ed9\u7f2a\u592b\u4eba\u4e00\u4e2a\u8098\u9524\uff0c\u649e\u4e2d\u4e86 \u4ed6\u80c1\u4e0b\u7684\u6108\u6c14\u7a74\u3002 \u3000\u3000\u7f2a\u592b\u4eba\u867d\u6ca1\u6709\u7ed9\u70b9\u6b63\u7a74\u9053\uff0c\u4f46\u8863\u88f3\u7834\u788e\uff0c\u5df2\u662f\u7f9e\u6127\u4e0d\u582a\uff0c\u5979\u6124\u706b\u4e2d\u70e7\uff0c\u201c\u6dae\u201d\u7684\u4e00\u97ad\uff0c \u53c8\u5411\u5df2\u7ecf\u8dcc\u5012\u4e86\u7684\u6c5f\u5357\u626b\u53bb\uff0c\u9a82\u9053\uff1a\u201c\u65e0\u793c\u5c0f\u5b50\uff0c\u518d\u5403\u4e00\u97ad\uff0c\u5230\u960e\u738b\u6bbf\u4e0a\u901e\u80fd\u53bb\u5427\uff01\u201d \u3000\u3000\u8fd9\u201c\u6108\u6c14\u7a74\u201d\u4e43\u662f\u4eba\u8eab\u6b7b\u7a74\u4e4b\u4e00\uff0c\u7f2a\u592b\u4eba\u7528\u65f6\u9524\u649e\u4e2d\u4e86\u4ed6\u7684\u201c\u6108\u6c14\u7a74\u201d\uff0c\u6599\u60f3\u4ed6\u7eb5\u7136\u672a \u7acb\u523b\u6bd9\u547d\uff0c\u4e5f\u5fc5\u5b9a\u4e0d\u80fd\u52a8\u5f39\uff0c\u54ea\u77e5\u6c5f\u5357\u5374\u6709\u201c\u98a0\u5012\u7a74\u9053\u201d\u7684\u672c\u9886\uff0c\u8fd9\u4e00\u56de\u672a\u5f85\u5979\u7684\u6bd2\u97ad\u6253 \u5230\uff0c\u5df2\u81ea\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u7ffb\u8d77\u8eab\u6765\uff0c\u5927\u58f0\u9a82\u9053\uff1a\u201c\u4f60\u62a2\u4e86\u6211\u7684\u513f\u5b50\uff0c\u8fd8\u8981\u6211\u548c\u4f60\u8bb2\u793c\u8c8c \u5417\uff1f\u54fc\uff0c\u54fc\uff0c\u6211\u8981\u548c\u4f60\u62fc\u547d\uff01\u201d \u3000\u3000\u6c5f\u5357\u7684\u6b66\u529f\u867d\u6bd4\u4e0d\u4e0a\u7f2a\u592b\u4eba\uff0c\u4f46\u8eab\u624b\u4e5f\u751a\u4e3a\u654f\u6377\uff0c\u4e00\u8df3\u8d77\u6765\uff0c\u957f\u5251\u4fbf\u5df2\u51fa\u9798\uff0c\u4e00\u62db\u201c\u6625 \u98ce\u89e3\u51bb\u201d\uff0c\u4fbf\u5411\u654c\u4eba\u523a\u53bb\u3002\uff08\u6c5f\u5357\u8fd9\u4e00\u62db\u5251\u5f0f\uff0c\u4e43\u662f\u201c\u51b0\u5ddd\u5251\u6cd5\u201d\u7684\u4e00\u62db\u7cbe\u5999\u62db\u6570\uff09\u5c3d\u7ba1\u4ed6 \u5b66\u5f97\u4e0d\u5168\uff0c\u4f46\u7a76\u7adf\u662f\u4e0a\u4e58\u5251\u6cd5\uff0c\u4e00\u9cde\u534a\u722a\uff0c\u4e5f\u8db3\u4ee5\u9707\u6151\u5bf9\u624b\u3002 \u3000\u3000\u7f2a\u592b\u4eba\u89c1\u6c5f\u5357\u7ed9\u649e\u4e2d\u4e86\u6108\u6c14\u7a74\uff0c\u5c45\u7136\u82e5\u65e0\u5176\u4e8b\uff0c\u800c\u4e14\u8fd8\u80fd\u7acb\u5373\u4f7f\u51fa\u5982\u6b64\u795e\u5999\u7684\u5251\u62db\uff0c\u4e0d \u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u662f\u6211\u770b\u8d70\u773c\u4e86\uff1f\u8fd9\u5c0f\u5b50\u4e5f\u5b66\u6210\u4e86\u91d1\u521a\u4e0d\u574f\u7684\u795e\u529f\uff1f\u201d\u5f53\u4e0b\u54ea\u91cc \u8fd8\u6562\u8f7b\u654c\uff0c\u7adf\u628a\u6c5f\u5357\u4e0e\u8c37\u4e4b\u534e\u540c\u7b49\u5bf9\u5f85\uff0c\u5206\u51fa\u4e86\u4e00\u534a\u529b\u91cf\u53bb\u5e94\u4ed8\u6c5f\u5357\uff0c\u6c5f\u5357\u7684\u771f\u5b9e\u529f\u592b\uff0c\u7a76 \u7adf\u4e0e\u7f2a\u592b\u4eba\u8ddd\u79bb\u5c1a\u8fdc\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u4e0d\u51fa\u4e8c\u5341\u62db\uff0c\u6c5f\u5357\u4fbf\u53c8\u663e\u5f97\u624b\u5fd9\u811a\u4e71\u4e86\uff01 \u3000\u3000\u5e78\u800c\u6c5f\u5357\u5df2\u5b66\u4f1a\u4e86\u5929\u7f57\u6b65\u6cd5\uff0c\u8c37\u4e4b\u534e\u7684\u7384\u5973\u5251\u6cd5\u53c8\u7cbe\u5999\u975e\u5e38\uff0c\u4ee4\u5f97\u90a3\u7f2a\u592b\u4eba\u4e0d\u6562\u653e\u624b\u5411 \u6c5f\u5357\u653b\u51fb\uff0c\u56e0\u6b64\u6c5f\u5357\u624d\u80fd\u591f\u63a5\u8fde\u6570\u6b21\uff0c\u5728\u6781\u4e3a\u5371\u9669\u7684\u60c5\u5f62\u4e0b\uff0c\u4fa5\u5e78\u9003\u8fc7\u4e86\u7f2a\u592b\u4eba\u7684\u6bd2\u624b\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u867d\u7136\u56e0\u4e3a\u8981\u7167\u987e\u6c5f\u5357\uff0c\u591a\u5c11\u6709\u70b9\u9677\u4e8e\u88ab\u52a8\uff0c\u4f46\u4ece\u53e6\u4e00\u65b9\u9762\u8bf4\u6765\uff0c\u6c5f\u5357\u6b64\u65f6\u7684\u6b66\u529f \u4e5f\u5df2\u4e0d\u7b97\u5e73\u5eb8\u4e4b\u8f88\uff0c\u66f4\u52a0\u4e0a\u4ed6\u90a3\u594b\u4e0d\u987e\u8eab\u7684\u6253\u6cd5\uff0c\u4ee4\u5f97\u7f2a\u592b\u4eba\u4e5f\u8981\u987e\u5fcc\u51e0\u5206\uff0c\u591a\u5c11\u4e5f\u5bf9\u8c37\u4e4b \u534e\u6709\u4e9b\u5e2e\u52a9\uff0c\u6240\u4ee5\uff0c\u603b\u7684\u8bf4\u6765\uff0c\u5229\u5bb3\u76f8\u6d88\uff0c\u8fd8\u662f\u5229\u591a\u5bb3\u5c11\u3002\u8c37\u4e4b\u534e\u7684\u771f\u5b9e\u672c\u9886\u672c\u6765\u5c31\u8981\u6bd4\u90a3 \u7f2a\u592b\u4eba\u7a0d\u80dc\u4e00\u7b79\uff0c\u6709\u4e86\u6c5f\u5357\u76f8\u52a9\uff0c\u5251\u6c14\u5982\u8679\uff0c\u653b\u52bf\u66f4\u76db\uff0c\u82e5\u975e\u56e0\u4e3a\u8981\u7167\u987e\u6c5f\u5357\uff0c\u5979\u65e9\u5c31\u53ef\u4ee5 \u5c06\u654c\u4eba\u4f24\u4e86\u3002 \u3000\u3000\u90a3\u7f2a\u592b\u4eba\u4e5f\u770b\u51fa\u4e86\u6c5f\u5357\u7684\u5f31\u70b9\uff0c\u6fc0\u6218\u4e2d\u5979\u5ffd\u5730\u4f7f\u51fa\u201c\u56de\u98ce\u62c2\u67f3\u201d\u7684\u97ad\u6cd5\uff0c\u5530\u5530\u5530\u63a5\u8fde\u4e09 \u97ad\uff0c\u4f5c\u52bf\u5411\u8c37\u4e4b\u534e\u731b\u653b\uff0c\u6c5f\u5357\u89c1\u6709\u673a\u53ef\u4e58\uff0c\u6325\u5251\u4fbf\u4e0a\uff0c\u7f2a\u592b\u4eba\u5356\u4e2a\u7834\u7efd\uff0c\u8ba9\u4ed6\u6b3a\u8fd1\u8eab\u524d\uff0c\u84e6 \u5730\u4e00\u53e3\u51b7\u6c14\u5439\u53bb\uff0c\u6c5f\u5357\u673a\u4f36\u4f36\u7684\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7f2a\u592b\u4eba\u83b2\u7fd8\u4e00\u6446\uff0c\u4e00\u811a\u8e22 \u4e2d\u4e86\u6c5f\u5357\uff01 \u3000\u3000\u8c37\u4e4b\u534e\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u4e00\u5251\u523a\u53bb\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u6c5f\u5357\u5927\u53eb\u4e00\u58f0\uff0c\u4e00\u4e2a\u7b4b\u6597\u7ffb\u5230\u4e86\u5899 \u8fb9\uff0c\u7d27\u63a5\u7740\u5374\u662f\u7f2a\u592b\u4eba\u4e5f\u53d1\u51fa\u4e86\u4e00\u58f0\u5c16\u53eb\uff0c\u811a\u6b65\u7a81\u7136\u8dc4\u8e09\uff0c\u8c37\u4e4b\u534e\u7684\u5b9d\u5251\u523a\u5230\uff0c\u5979\u7adf\u7136\u62db\u67b6 \u4e0d\u5f00\uff0c\u5de6\u81c2\u4e0a\u65b9\uff0c\u7ed9\u8c37\u4e4b\u534e\u4e00\u5251\u524a\u53bb\u4e86\u4e00\u5927\u7247\u76ae\u8089\u3002\u539f\u6765\u6c5f\u5357\u608d\u4e0d\u754f\u6b7b\uff1b\u4ed6\u5728\u7ed9\u7f2a\u592b\u4eba\u8e22\u4e2d \u7684\u65f6\u5019\uff0c\u7adf\u8fd8\u5f20\u5f00\u5927\u53e3\uff0c\u5728\u5979\u7684\u811a\u8e1d\u4e0a\u72e0\u72e0\u5730\u54ac\u4e86\u4e00\u65e5\u3002\u56e0\u6b64\u8c37\u4e4b\u534e\u8ddf\u7740\u8865\u4e0a\u7684\u8fd9\u4e00\u5251\uff0c\u624d \u80fd\u591f\u91cd\u91cd\u7684\u4f24\u4e86\u654c\u4eba\u3002 \u3000\u3000\u90a3\u7f2a\u592b\u4eba\u5148\u540e\u53d7\u4e86\u54ac\u4f24\u3001\u5251\u4f24\uff0c\u518d\u4e5f\u62b5\u6321\u4e0d\u4f4f\uff0c\u5c16\u53eb\u4e00\u58f0\uff0c\u593a\u95e8\u4fbf\u8dd1\u3002\u8c37\u4e4b\u534e\u65e0\u6687\u8ffd \u654c\uff0c\u5148\u884c\u95ee\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u4f60\u5feb\u53bb\u8ffd\u90a3\u5996\u5987\u5427\u3002\u201d\u53ef\u662f\u4ed6\u8981 \u6276\u7740\u5899\u58c1\uff0c\u624d\u80fd\u7ad9\u7acb\u8d77\u6765\uff0c\u663e\u89c1\u8fd9\u4e00\u8de4\u4e5f\u5b9e\u5728\u6454\u5f97\u4e0d\u8f7b\u3002 \u3000\u3000\u90a3\u7f2a\u592b\u4eba\u7684\u672c\u9886\u7aef\u7684\u975e\u51e1\uff0c\u91cd\u4f24\u4e4b\u540e\uff0c\u4e00\u8db3\u5fae\u7638\uff0c\u4ecd\u7136\u9003\u5f97\u975e\u5e38\u8fc5\u901f\uff0c\u5916\u9762\u672c\u6765\u6709\u8bb8\u591a \u6c13\u5c71\u6d3e\u7684\u5f1f\u5b50\uff0c\u5979\u4e00\u9003\u51fa\u6765\uff0c\u4e00\u626c\u624b\u4fbf\u662f\u4e00\u56e2\u6d53\u70df\u70c8\u706b\uff0c\u70df\u706b\u4e4b\u4e2d\u8fd8\u6742\u7740\u55e4\u55e4\u58f0\u54cd\uff0c\u767d\u82f1\u6770 \u8ba4\u5f97\u8fd9\u662f\u5389\u80dc\u7537\u5f53\u5e74\u7528\u8fc7\u7684\u201c\u6bd2\u96fe\u91d1\u9488\u70c8\u7130\u5f39\u201d\uff0c\u614c\u5fd9\u4e0e\u7a0b\u6d69\u540c\u65f6\u53d1\u638c\uff0c\u8fd9\u4e24\u4eba\u662f\u90ce\u5c71\u6d3e\u516d \u5927\u5f1f\u5b50\u4e4b\u9996\uff0c\u5288\u7a7a\u638c\u7684\u529f\u529b\u751a\u9ad8\uff0c\u53cc\u638c\u9f50\u53d1\uff0c\u638c\u98ce\u5c06\u6bd2\u7130\u5439\u4e0a\u4e0a\u7a7a\uff0c\u53ef\u662f\u4ecd\u7136\u6709\u51e0\u4e2a\u5f1f\u5b50\u53d7 \u4e86\u6bd2\u9488\u4e4b\u4f24\u3002 \u3000\u3000\u6c13\u5c71\u6d3e\u5f1f\u5b50\u90fd\u52a8\u4e86\u6012\uff0c\u5927\u58f0\u547c\u559d\uff0c\u6697\u5668\u7eb7\u7eb7\u51fa\u624b\uff0c\u96e8\u70b9\u822c\u7684\u5411\u7f2a\u592b\u4eba\u540e\u5fc3\u6253\u53bb\u3002 \u3000\u3000\u90a3\u7f2a\u592b\u4eba\u5728\u51b7\u7b11\u4e4b\u4e2d\u4f7f\u5f00\u4e86\u5979\u90a3\u6761\u8f6f\u97ad\uff0c\u5b9b\u5982\u4e00\u6761\u5f20\u7259\u821e\u722a\u7684\u6bd2\u9f99\u76d8\u7a7a\u98de\u821e\uff0c\u4f46\u89c1\u6ee1\u5929 \u6697\u5668\uff0c\u98de\u53bb\u98de\u56de\uff0c\u7a0d\u4e3a\u6c89\u91cd\u4e00\u4e9b\u7684\u6697\u5668\uff0c\u5982\u67f3\u53f6\u5200\u3001\u86fe\u7709\u523a\u3001\u4e09\u68f1\u9556\u3001\u6d41\u661f\u9524\u4e4b\u7c7b\uff0c\u90fd\u7ed9\u5979 \u7684\u6bd2\u97ad\u8361\u5411\u56db\u65b9\uff0c\u5176\u4ed6\u5982\u6885\u82b1\u9488\u3001\u900f\u9aa8\u9489\u3001\u94c1\u83b2\u5b50\u4e4b\u7c7b\u7684\u7ec6\u5c0f\u7792\u5668\uff0c\u5979\u7406\u90fd\u4e0d\u7406\uff0c\u53ea\u662f\u62a4\u7740 \u9762\u95e8\uff0c\u4efb\u51ed\u90a3\u4e9b\u6697\u5668\u6253\u6765\uff0c\u4f46\u542c\u5f97\u53ee\u53ee\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u90a3\u4e9b\u7ec6\u5c0f\u7684\u6697\u5668\u7eb5\u4f7f\u4e0d\u88ab\u5979\u7684\u97ad\u98ce \u626b\u5f00\uff0c\u6cbe\u8863\u5373\u843d\u3002 \u3000\u3000\u6643\u773c\u4e4b\u95f4\uff0c\u5979\u5df2\u9003\u51fa\u7b2c\u4e8c\u9053\u5c71\u95e8\uff0c\u5b88\u5728\u7b2c\u4e09\u9053\u95e8\u7684\u662f\u516d\u5927\u5f1f\u5b50\u4e2d\u7684\u7518\u4eba\u9f99\u4e0e\u6797\u7b19\u4e8c\u4eba\uff0c \u7518\u4eba\u9f99\u662f\u5f53\u5e74\u6c5f\u5357\u5927\u4fa0\u7518\u51e4\u6c60\u7684\u5f1f\u5b50\uff0c\u5f97\u4e86\u7518\u51e4\u6c60\u4eb2\u6388\u7684\u795e\u62f3\u529f\u592b\uff0c\u89c1\u5979\u95ef\u6765\uff0c\u7acb\u5373\u4e00\u62f3\u6253 \u53bb\uff0c\u540e\u9762\u8ffd\u6765\u7684\u767d\u82f1\u6770\u614c\u5fd9\u53eb\u9053\uff1a\u201c\u4e0d\u53ef\u7ed9\u5979\u7684\u6bd2\u638c\u78b0\u4e0a\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00 \u58f0\uff0c\u7f2a\u592b\u4eba\u4e00\u638c\u62cd\u51fa\uff1b\u5df2\u662f\u548c\u7518\u4eba\u9f99\u7684\u62f3\u5934\u78b0\u4e2a\u6b63\u7740\uff01 \u3000\u3000\u7518\u4eba\u9f99\u7684\u795e\u62f3\u6709\u6d1e\u7a7f\u725b\u8179\u4e4b\u80fd\uff0c\u7f2a\u592b\u4eba\u786c\u63a5\u4e86\u4ed6\u7684\u4e00\u638c\uff0c\u4e5f\u6709\u70b9\u6447\u6447\u6643\u6643\uff0c\u5979\u51b7\u7b11\u4e00 \u58f0\uff0c\u7b2c\u4e8c\u638c\u8ddf\u7740\u53c8\u62cd\u5230\u4e86\u4ed6\u7684\u5934\u9876\uff0c\u6797\u7b19\u4f7f\u7684\u662f\u4e00\u679d\u7389\u7b1b\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u5996\u5987\u4f11\u5f97\u653e\u8086\uff01\u201d \u624b\u6325\u7389\u7b1b\uff0c\u4e00\u70b9\u5c31\u70b9\u5230\u4e86\u5979\u7684\u8109\u95e8\uff01 \u3000\u3000\u6797\u7b19\u7684\u7389\u7b1b\u70b9\u7a74\u529f\u592b\u4e5f\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u7389\u7b1b\u662f\u77ed\u5175\u5668\uff0c\u8fd9\u65f6\u8fd1\u8eab\u8089\u640f\uff0c\u7f2a\u592b\u4eba\u7684\u6bd2\u97ad\u6765\u4e0d \u53ca\u5377\u56de\uff0c\u5fc3\u5934\u4e00\u51db\uff0c\u53ea\u5f97\u5feb\u6b65\u95ea\u5f00\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u7518\u4eba\u9f99\u5927\u53eb\u4e00\u58f0\uff0c\u6251\u901a\u4fbf\u5012\uff0c\u6797\u7b19\u548c \u767d\u82f1\u6770\u53ea\u5f97\u4efb\u51ed\u7f2a\u592b\u4eba\u4ece\u5bb9\u8d70\u51fa\u5c71\u95e8\uff0c\u8d76\u5fd9\u53bb\u5148\u628a\u7518\u4eba\u9f99\u6276\u8d77\u3002 \u3000\u3000\u53ea\u89c1\u7518\u4eba\u9f99\u9762\u8272\u7600\u9ed1\uff0c\u5df2\u662f\u660f\u8ff7\u8fc7\u53bb\u4e86\u3002\u4ed6\u6240\u4e2d\u6bd2\u7684\u60c5\u72b6\uff0c\u6b63\u4e0e\u8c22\u4e91\u771f\u76f8\u540c\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u770b\u771f\u4e86\u6c5f\u5357\u672a\u66fe\u53d7\u4f24\uff0c\u65b9\u59cb\u653e\u5fc3\u8ffd\u51fa\uff0c\u4f46\u5df2\u662f\u6162\u4e86\u4e00\u6b65\uff0c\u8fd9\u65f6\u7f2a\u592b\u4eba\u5df2\u95ef\u8fc7\u4e86\u4e09 \u9053\u5c71\u95e8\uff0c\u5230\u4e86\u5916\u95f4\u7684\u9662\u5b50\u4e86\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u559d\u9189\u4e86\u7684\u8f7f\u592b\uff0c\u542c\u5f97\u4eba\u58f0\u55a7\u95f9\uff0c\u731b\u7136\u60ca\u9192\uff0c\u614c\u5fd9\u8dd1\u51fa\u6765\uff0c\u4e00\u62ac\u5934\uff0c\u53ea\u89c1\u8fce\u9762\u8dd1\u6765 \u7684\u6b63\u662f\u4ed6\u4eec\u5949\u547d\u670d\u4f8d\u7684\u7f2a\u592b\u4eba\uff01 \u3000\u3000\u8fd9\u4e24\u4e2a\u8f7f\u592b\u8fd8\u4e0d\u77e5\u5df2\u662f\u95f9\u51fa\u4e86\u5927\u4e8b\uff0c\u9189\u9187\u9187\u5730\u95ee\u9053\uff1a\u201c\u592b\u4eba\uff0c\u4f60\u8981\u4e0b\u5c71\u4e86\u4e48\uff1f\u5f85\u6211\u4eec\u53bb \u62ac\u8f7f\u5b50\u3002\u201d\u7f2a\u592b\u4eba\u5ffd\u5730\u4e00\u58f0\u51b7\u7b11\uff0c\u65a5\u9053\uff1a\u201d\u90fd\u662f\u4f60\u4eec\u8fd9\u4e24\u4e2a\u8822\u6750\u6cc4\u4e86\u6211\u7684\u5e95\uff0c\u6211\u8fd8\u4f1a\u8981\u4f60\u4eec \u62ac\u8f7f\u5417\uff1f\u7ed9\u6211\u6eda\u56de\u8001\u5bb6\u53bb\u5427\uff01\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u9189\u5f97\u7cca\u6d82\u4e86\u7684\u8f7f\u592b\uff0c\u8fd8\u5f53\u662f\u7f2a\u592b\u4eba\u514d\u4e86\u4ed6\u4eec\u7684\u8d31\u5f79\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u8fd8\u672a\u66fe\u9053\u8c22\uff0c\u53ea \u542c\u5f97\u55d6\u6388\u4e24\u58f0\uff0c\u7f2a\u592b\u4eba\u5df2\u53d1\u51fa\u4e86\u4e24\u679d\u8896\u7bad\uff0c\u4e24\u679d\u8896\u7bad\u90fd\u662f\u7a7f\u5589\u800c\u8fc7\uff0c\u8fd9\u4e24\u4e2a\u8f7f\u592b\u4e0d\u660e\u4e0d\u767d\u5c31 \u505a\u4e86\u6789\u6b7b\u9b3c\uff01 \u3000\u3000\u7f2a\u592b\u4eba\u7684\u52a8\u4f5c\u5feb\u6781\uff0c\u5979\u5de6\u624b\u53d1\u7bad\uff0c\u5c04\u6b7b\u4e86\u4e24\u540d\u8f7f\u592b\uff0c\u770b\u4e5f\u4e0d\u770b\uff0c\u53f3\u624b\u7684\u8f6f\u97ad\uff0c\u201c\u556a\u54d2\u201d \u4e00\u58f0\uff0c\u5df2\u642d\u7740\u4e86\u5899\u5934\uff0c\u8eab\u5f62\u75be\u8d77\uff0c\u5728\u7a7a\u4e2d\u4e00\u4e2a\u9e5e\u5b50\u7ffb\u8eab\uff0c\u4fbf\u7ffb\u8fc7\u4e86\u5899\u5934\uff0c\u59ff\u52bf\u7f8e\u5999\u4e4b\u6781\uff0c\u800c \u4e14\u5728\u5979\u7ffb\u8fc7\u5899\u5934\u4e4b\u65f6\uff0c\u8fd8\u53d1\u51fa\u4e86\u4e00\u679a\u6bd2\u96fe\u91d1\u9488\u70c8\u7130\u5f39\u6765\u963b\u6321\u8ffd\u5175\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u53d1\u51fa\u5288\u7a7a\u638c\u5c06\u70df\u96fe\u8361\u5f00\uff0c\u767d\u82f1\u6770\u8dc3\u4e0a\u5899\u5934\u4e00\u770b\uff0c\u7f2a\u592b\u4eba\u5df2\u8d70\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002\u8c37\u4e4b\u534e \u9053\uff1a\u201c\u5979\u7ed9\u6c5f\u5357\u54ac\u4e86\u4e00\u53e3\uff0c\u53c8\u4e2d\u4e86\u6211\u7684\u4e00\u5251\uff0c\u521a\u624d\u7ffb\u8fc7\u5899\u5934\uff0c\u5df2\u8981\u501f\u52a9\u8f6f\u97ad\u4e4b\u529b\uff0c\u770b\u6765\u4e5f\u662f \u4f24\u5f97\u4e0d\u8f7b\u7684\u4e86\u3002\u5c31\u8ba9\u5979\u53bb\u5427\uff01\u201d \u3000\u3000\u8fd9\u4e00\u5f79\u6c13\u5c71\u7684\u5f1f\u5b50\u4f24\u7684\u4e0d\u5c11\uff0c\u9664\u4e86\u8c22\u4e91\u771f\u3001\u7518\u4eba\u9f99\u91cd\u4f24\u4e4b\u5916\uff0c\u8fd8\u6709\u4e94\u516d\u4e2a\u5f1f\u5b50\u4e2d\u4e86\u6bd2 \u9488\uff0c\u5c31\u662f\u6ca1\u6709\u8c37\u4e4b\u534e\u7684\u547d\u4ee4\uff0c\u5927\u5bb6\u4e5f\u5f97\u5148\u5fd9\u7740\u6599\u7406\u4f24\u8005\uff0c\u65e0\u6687\u53bb\u8ffd\u90a3\u7f2a\u592b\u4eba\u4e86\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u95f7\u95f7\u4e0d\u4e50\uff0c\u548c\u6c5f\u5357\u4e00\u8d77\uff0c\u5148\u53bb\u63a2\u671b\u8c22\u4e91\u771f\uff0c\u5979\u670d\u4e86\u78a7\u7075\u4e39\u4e4b\u540e\uff0c\u547c\u5438\u5df2\u5747\u5300\u4e86\u8bb8 \u591a\uff0c\u4f46\u8fd8\u5728\u660f\u8ff7\u7684\u72b6\u6001\u4e2d\u3002\u8c37\u4e4b\u534e\u7a0d\u7a0d\u653e\u5fc3\uff0c\u63a5\u7740\u4fbf\u4e0e\u6c5f\u5357\u53bb\u770b\u5979\u7684\u517b\u5973\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u4e00\u89c1\u6c5f\u5357\u4fbf\u56b7\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u4e0b\u4e00\u6b21\u5c31\u662f\u518d\u5149\u7740\u5c41\u80a1\uff0c\u6211\u4e5f\u4e0d\u4f1a\u53d6\u7b11\u4f60\u4e86\u3002\u4f60\u662f \u597d\u4eba\u3002\u201d \u3000\u3000\u6c5f\u5357\u5c34\u5c2c\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u9b3c\u5934\uff0c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u662f\u597d\u4eba\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4ed6\u4eec\u544a\u8bc9\u6211\uff0c\u662f\u4f60\u5e2e\u6211\u5a18\u5c06\u90a3\u4e2a\u5973\u8d3c\u6253\u8dd1\u4e86\u3002\u521a\u624d\u6211\u771f\u5bb3\u6015\uff0c\u8981\u662f\u7ed9\u5979\u6293 \u53bb\uff0c\u771f\u4e0d\u77e5\u5982\u4f55\u662f\u597d\uff1f\u201d \u3000\u3000\u6c5f\u5357\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u7684\u513f\u5b50\u5df2\u7ed9\u5979\u6293\u53bb\u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8fd9\u5973\u8d3c\u771f\u53ef\u6076\uff0c\u53d4\u53d4\uff0c\u4f60\u4e0d \u8981\u62c5\u5fc3\uff0c\u4f60\u8fd9\u6b21\u5e2e\u5fd9\u4e86\u6211\u5a18\uff0c\u6211\u4e5f\u8981\u5a18\u5e2e\u5fd9\u4f60\uff0c\u5c06\u4f60\u7684\u513f\u5b50\u8981\u56de\u6765\u3002\u4ed6\u6709\u591a\u5927\u4e86\uff0c\u6211\u4eca\u5e74\u662f \u4e03\u5c81\uff0c\u6211\u60f3\u77e5\u9053\uff0c\u6211\u5e94\u8be5\u53eb\u4ed6\u505a\u54e5\u54e5\u8fd8\u662f\u53eb\u4ed6\u505a\u5f1f\u5f1f\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u548c\u4f60\u4e00\u6837\uff0c\u4eca\u5e74\u4e5f\u6b63\u5de7\u662f\u4e03\u5c81\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u62cd\u62cd\u5c0f\u624b\u9053\uff1a\u201c\u597d\u5440\uff0c\u5a18\uff0c\u4f60\u5feb\u5e2e\u5fd9\u53d4\u53d4\u628a\u4ed6\u627e\u56de\u6765\u5427\uff0c\u4e5f\u597d\u4e0e\u6211\u4f5c\u4f34\u3002\u53d4\u53d4\uff0c \u4f60\u4e5f\u7559\u4e0b\u6765\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u672c\u6765\u5fc3\u4e2d\u70e6\u95f7\uff0c\u89c1\u5b69\u5b50\u8fd9\u6837\u53ef\u7231\uff0c\u4e5f\u4e0d\u7981\u5fae\u7b11\u8d77\u6765\uff0c\u9053\uff1a\u201c\u597d\u5440\uff0c\u8981\u662f\u6c5f\u53d4\u53d4\u613f \u610f\u8981\u4f60\uff0c\u6c5f\u5bb6\u54e5\u54e5\u627e\u4e86\u56de\u6765\uff0c\u6211\u5c31\u9001\u4f60\u7ed9\u4ed6\u505a\u5c0f\u5ab3\u5987\u513f\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5374\u4e0d\u61c2\u5f97\u4ec0\u4e48\u53eb\u201c\u5c0f\u5ab3\u5987\u513f\u201d\uff0c\u561f\u7740\u5634\u9053\uff1a\u201c\u6211\u53ea\u662f\u60f3\u8981\u4ed6\u505a\u4e2a\u4f34\u513f\uff0c\u6211\u53ef\u4e0d\u613f \u79bb\u5f00\u4f60\uff0c\u5a18\uff0c\u6211\u8fd9\u4ef6\u68c9\u8884\u4e5f\u7ed9\u90a3\u5973\u8d3c\u6293\u574f\u4e86\uff0c\u4f60\u77a7\uff0c\u4f60\u7ed9\u6211\u7f1d\u7f1d\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u63a5\u8fc7\u4e86\u8fd9\u4ef6\u68c9\u8884\uff0c\u4e0d\u89c9\u5fc3\u4e2d\u4e00\u52a8\u3002 \u3000\u3000\u5979\u60f3\u8d77\u521a\u624d\u7684\u4e00\u5e55\u60c5\u4e8b\uff1a\u90a3\u7f2a\u592b\u4eba\u5728\u8981\u6c42\u548c\u5b69\u5b50\u89c1\u9762\u4e4b\u65f6\uff0c\u66fe\u63d0\u51fa\u4e00\u4e2a\u9644\u5e26\u7684\u8981\u6c42\uff0c\u8981 \u5b69\u5b50\u62ab\u7740\u8fd9\u4ef6\u68c9\u8884\u51fa\u6765\u3002\u5f85\u5230\u5b69\u5b50\u51fa\u6765\uff0c\u5979\u5c31\u7acb\u5373\u5411\u5979\u6293\u53bb\uff01\u8c37\u4e4b\u534e\u5f53\u65f6\u66fe\u975e\u5e38\u7559\u5fc3\u7684\u6ce8 \u89c6\uff0c\u77a7\u5979\u51fa\u624b\u65f6\u7684\u51f6\u6076\u795e\u60c5\uff0c\u6839\u672c\u5c31\u4e0d\u7406\u4f1a\u662f\u5426\u53ef\u80fd\u4f24\u53ca\u5b69\u5b50\uff0c\u53ef\u4ee5\u65ad\u5b9a\uff1a\u4e0d\u4f46\u8fd9\u5b69\u5b50\u4e0d\u662f \u5979\u7684\u4eb2\u751f\u5973\u513f\uff0c\u800c\u4e14\u5979\u4e5f\u4e0d\u662f\u5fd7\u5728\u8981\u8fd9\u5b69\u5b50\uff0c\u800c\u662f\u8981\u8fd9\u68c9\u8884\u3002 \u3000\u3000\u68c9\u8884\u4e0a\u7684\u94ae\u6263\uff0c\u662f\u6b66\u6797\u4e2d\u4eba\u68a6\u5bd0\u4ee5\u6c42\u7684\u7a00\u4e16\u5947\u73cd\uff0c\u8fd9\u662f\u8c37\u4e4b\u65e9\u5df2\u77e5\u9053\u4e86\u7684\uff0c\u4f46\u90a3\u7f2a\u592b\u4eba \u5374\u672a\u77e5\u9053\u3002\u53ef\u89c1\u5979\u8981\u53d6\u8fd9\u68c9\u8884\uff0c\u5e76\u662f\u7531\u4e8e\u5df2\u7ecf\u77e5\u9053\u4e86\u94ae\u6263\u7684\u79d8\u5bc6\uff0c\u90a3\u4e48\uff0c\u9664\u4e86\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u68c9 \u8884\u4e2d\u83ab\u975e\u8fd8\u6709\u53e6\u4e00\u4e2a\u66f4\u5927\u7684\u79d8\u5bc6\uff1f \u3000\u3000\u8c37\u4e4b\u534e\u7591\u60d1\u4e0d\u5b9a\uff0c\u63a5\u8fc7\u4e86\u68c9\u8884\uff0c\u4e0d\u514d\u4ed4\u7ec6\u68c0\u89c6\u4e00\u756a\uff0c\u90a3\u68c9\u8884\u5df2\u7ed9\u7f2a\u592b\u4eba\u6293\u5f00\u4e86\u4e00\u6761\u88c2 \u7f1d\uff0c\u8c37\u4e4b\u534e\u5c06\u68c9\u8884\u62c6\u5f00\u5c11\u8bb8\uff0c\u5ffd\u89c1\u91cc\u9762\u4f3c\u6709\u4e00\u7247\u4e1c\u897f\uff0c\u62c9\u51fa\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u4e00\u5f20\u7cbe\u5de5\u5de7\u5236\u7684 \u7f8a\u76ae\u7eb8\uff0c\u666e\u901a\u7684\u7f8a\u76ae\u7eb8\u90fd\u662f\u6bd4\u4e00\u822c\u7684\u7eb8\u5f20\u539a\u7684\uff0c\u4f46\u8fd9\u5f20\u7f8a\u76ae\u7eb8\u5374\u8584\u5982\u8749\u7ffc\uff0c\u6478\u5230\u624b\u4e2d\uff0c\u624d\u77e5 \u9053\u5b83\u662f\u7f8a\u76ae\u3002 \u3000\u3000\u7eb8\u8d28\u7684\u5947\u602a\u4e5f\u8fd8\u7f62\u4e86\uff0c\u7eb8\u7247\u4e0a\u8fd8\u5199\u6ee1\u4e86\u6587\u5b57\uff0c\u5f2f\u5f2f\u66f2\u66f2\uff0c\u6709\u5982\u86af\u8693\uff01\u8c37\u4e4b\u534e\u4e00\u4e2a\u5b57\u90fd\u4e0d \u8ba4\u8bc6\u3002 \u3000\u3000\u6c5f\u5357\u5728\u65c1\u8fb9\u4e5f\u7741\u5927\u4e86\u773c\u775b\uff0c\u8c37\u4e4b\u534e\u5ffd\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u4f60\u5728\u897f\u85cf\u5446\u4e86\u5341\u5e74\uff0c\u53ef\u8ba4\u5f97\u85cf\u6587\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u7a0d\u5fae\u8ba4\u8bc6\u51e0\u4e2a\u3002\u201d\u4f46\u4ed6\u63a5\u8fc7\u4e86\u7eb8\u7247\uff0c\u770b\u4e86\u4e00\u770b\uff0c\u5374\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e0d \u662f\u85cf\u6587\u3002\u201d\u4ed6\u53c8\u9053\uff1a\u201c\u6211\u4ee5\u524d\u5728\u8428\u8fe6\u5ba3\u6170\u4f7f\u8859\u95e8\u7684\u65f6\u5019\uff0c\u6709\u65f6\u4e5f\u66ff\u4ed6\u4eec\u9001\u9001\u516c\u6587\uff0c\u8fd9\u7eb8\u4e0a\u7684 \u6587\u5b57\u4e0d\u662f\u85cf\u6587\uff0c\u4f46\u6211\u5374\u53c8\u4f3c\u66fe\u89c1\u8fc7\u8fd9\u79cd\u5b57\u4f53\uff0c\u53ea\u662f\u8bf4\u4e0d\u4e0a\u6765\u3002\u6211\u7684\u4e49\u5144\u9648\u5929\u5b87\u61c2\u5f97\u897f\u57df\u7684\u51e0 \u79cd\u6587\u5b57\uff0c\u5c06\u6765\u6211\u628a\u4ed6\u8bf7\u5230\u4f60\u8fd9\u513f\u6765\uff0c\u4f60\u53ef\u4ee5\u7ed9\u4ed6\u4e00\u770b\u3002\u201d \u3000\u3000\u65e2\u7136\u6c5f\u5357\u4e0d\u80fd\u8fa8\u8ba4\uff0c\u8c37\u4e4b\u534e\u4e5f\u53ea\u597d\u542c\u4ece\u4ed6\u8fd9\u4e2a\u4e3b\u610f\uff0c\u5f53\u4e0b\u5979\u5c06\u8fd9\u7247\u7f8a\u76ae\u7eb8\u518d\u7eb3\u5165\u68c9\u8884\u4e4b \u4e2d\uff0c\u7528\u9488\u7ebf\u91cd\u65b0\u7f1d\u597d\uff0c\u8c37\u4e2d\u83b2\u4e5f\u5728\u7528\u597d\u5947\u7684\u76ee\u5149\u770b\u5979\u7f1d\u8865\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u67d4\u58f0\u95ee\u9053\uff1a\u201c\u83b2\u513f\uff0c\u4f60\u8fd8\u8bb0\u5f97\u5c0f\u65f6\u5019\u7684\u4e8b\u60c5\u5417\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u90a3\u5bf9\u5706\u6e9c\u6e9c\u7684\u5c0f\u773c\u73e0\u8f6c\u6765\u8f6c\u53bb\uff0c\u4f3c\u4e4e\u6709\u70b9\u4e3a\u96be\u7684\u795e\u6c14\uff0c\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u83b2\u513f\uff0c\u4f60\u4e0d \u613f\u610f\u8bf4\u5c31\u4e0d\u7528\u8bf4\u4e86\uff0c\u6211\u4e5f\u4e0d\u60f3\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u662f\u4e18\u7237\u7237\u5429\u5490\u8fc7\u6211\uff0c\u53eb\u6211\u4e0d\u53ef\u5c06\u5c0f\u65f6\u7684\u4e8b\u60c5\u5bf9\u4eba\u8bf4\u7684\u3002\u4f46\u4f60\u662f\u6211\u7684\u6bcd\u4eb2\uff0c \u6211\u544a\u8bc9\u7ed9\u4f60\uff0c\u60f3\u6765\u4e18\u7237\u7237\u4e0d\u4f1a\u89c1\u602a\u3002\u53ea\u662f\u6211\u4e5f\u51e0\u4e4e\u662f\u4ec0\u4e48\u90fd\u8bb0\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u5c06\u5979\u8f7b\u8f7b\u7684\u63fd\u5165\u6000\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bb0\u5f97\u4ec0\u4e48\u5c31\u8bf4\u4ec0\u4e48\u3002\u201d\u8c37\u4e2d\u83b2\u4fa7\u7740\u5934\u513f\u60f3\u4e86\u4e00 \u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8bb0\u5f97\u6211\u5c0f\u65f6\u5019\u662f\u4f4f\u5728\u5e10\u5e55\u91cc\uff0c\u5f88\u5927\u5f88\u5927\u7684\u5e10\u5e55\uff0c\u91cc\u9762\u6709\u8bb8\u8bb8\u591a\u591a\u623f\u5b50\u7684\uff0c\u5e10 \u5e55\u5916\u6709\u5f88\u5927\u5f88\u5927\u7684\u8349\u5730\uff0c\u6709\u8bb8\u8bb8\u591a\u591a\u725b\u7f8a\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u542c\u5f97\u51fa\u4e86\u795e\uff0c\u5fc3\u60f3\uff1a\u201c\u5979\u4f4f\u5728\u8fd9\u6837\u7684\u5e10\u5e55\uff0c\u96be\u9053\u662f\u8499\u53e4\u7684\u738b\u516c\u8d35\u65cf\uff0c\u6216\u8005\u662f\u56de\u7586 \u4ec0\u4e48\u914b\u957f\u7684\u5973\u513f\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u6709\u8bb8\u8bb8\u591a\u591a\u4ec6\u4eba\uff0c\u6211\u8bb0\u5f97\u5e38\u5e38\u62b1\u6211\u7684\u4eba\u662f\u4e00\u4e2a\u5934\u53d1\u90fd\u767d\u4e86\u7684\u8001\u5988 \u5988\uff0c\u6709\u4e00\u6b21\u6211\u5728\u8349\u573a\u4e0a\u73a9\uff0c\u542c\u5f97\u6709\u4e00\u4e2a\u5b69\u5b50\u53eb\u4ed6\u7684\u7239\u5a18\uff0c\u6211\u624d\u77e5\u9053\u4e00\u4e2a\u4eba\u662f\u5e94\u8be5\u6709\u7239\u6709\u5a18 \u7684\uff0c\u6211\u56de\u6765\u95ee\u90a3\u8001\u5988\u5988\uff0c\u95ee\u5979\u662f\u4e0d\u662f\u6211\u7684\u5a18\uff1f\u5979\u8bf4\uff1a\u2018\u6211\u54ea\u6709\u8fd9\u6837\u7684\u798f\u6c14\uff1f\u6211\u53ea\u662f\u4e00\u4e2a\u7167\u6599 \u4f60\u7684\u8001\u5974\u5a62\u3002\u2019\u5979\u544a\u8bc9\u6211\uff0c\u5e10\u5e55\u91cc\u7684\u4eba\u90fd\u662f\u6211\u7684\u4ec6\u4eba\u3002\u4f46\u5374\u6ca1\u6709\u544a\u8bc9\u6211\uff0c\u6211\u7684\u7239\u5a18\u5728\u4ec0\u4e48\u5730 \u65b9\u3002\u90a3\u65f6\u6211\u4e5f\u4e0d\u61c2\u5f97\u591a\u95ee\uff0c\u6211\u4ee5\u4e3a\u6216\u8005\u6211\u662f\u4f8b\u5916\uff0c\u6ca1\u6709\u7239\u5a18\u7684\u3002\u4e0d\u4e45\uff0c\u4e0d\u4e45\u4e4b\u540e\uff0c\u6211\u5c31\u77e5\u9053 \u6211\u662f\u6709\u4e00\u4e2a\u6bcd\u4eb2\u7684\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6709\u4e00\u5929\u665a\u4e0a\uff0c\u6709\u4e2a\u5973\u4eba\u5230\u5e10\u5e55\u6765\u770b\u6211\uff0c\u5979\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u6211\u73b0\u5728\u5168\u4e0d\u8bb0\u5f97\u4e86\uff0c \u53ea\u8bb0\u5f97\u5979\u662f\u4e2a\u5f88\u597d\u770b\u7684\u5973\u4eba\u3002\u5979\u8d70\u4e86\u4e4b\u540e\uff0c\u90a3\u8001\u5988\u5988\u624d\u544a\u8bc9\u6211\u90a3\u5973\u4eba\u5c31\u662f\u6211\u7684\u6bcd\u4eb2\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u4f60\u7684\u4eb2\u5a18\u8fd8\u5728\u4e16\u4e0a\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4e0d\uff0c\u5979\u5df2\u7ecf\u6b7b\u4e86\u3002\u8fd9\u662f\u540e\u6765\u4e18\u7237\u7237\u544a\u8bc9\u6211\u7684\u3002\u6709\u4e00\u5929\uff0c\u8349\u539f\u4e0a\u4e0d\u77e5\u53d1\u751f\u4e86 \u4ec0\u4e48\u4e8b\u60c5\uff0c\u4eba\u4eec\u5230\u5904\u4e71\u8dd1\u4e71\u51b2\uff0c\u4e18\u7237\u7237\u5c06\u6211\u62b1\u7740\uff0c\u9a91\u7740\u9a6c\u8dd1\u4e86\u51e0\u5929\u51e0\u591c\uff0c\u540e\u6765\u6211\u5c31\u548c\u4e18\u7237\u7237 \u4f4f\u5728\u4e00\u8d77\u3002\u4e0d\uff0c\u6700\u521d\u8fd8\u4e0d\u662f\u548c\u4ed6\u540c\u4f4f\uff0c\u662f\u4f4f\u5728\u4e00\u95f4\u6ce5\u5c4b\u91cc\uff0c\u5927\u7ea6\u8fc7\u4e86\u51e0\u4e2a\u6708\uff0c\u4e18\u7237\u7237\u624d\u63a5\u6211 \u5230\u4ed6\u7684\u5927\u5c4b\u91cc\u7684\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u90a3\u6ce5\u5c4b\u91cc\u6709\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u59d3\u7533\u7684\u53d4\u53d4\uff0c\u540e\u6765\u6211\u624d\u77e5\u9053\u4ed6\u662f\u7ed9\u6211\u4e18\u7237\u7237\u79cd\u7530\u7684\u3002\u8fd9\u4f4d\u7533\u53d4\u53d4\u6559 \u6211\u548c\u4ed6\u7684\u5b69\u5b50\u4eec\u8bf4\u4e00\u6837\u7684\u8bdd\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u90a3\u4f60\u4ee5\u524d\u662f\u8bf4\u4ec0\u4e48\u8bdd\u7684\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u76b1\u7740\u7709\u5934\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u6211\u73b0\u5728\u662f\u4e00\u53e5\u90fd\u4e0d\u4f1a\u8bf4\u4e86\u3002\u201d \u3000\u3000\u8981\u77e5\u8c37\u4e2d\u83b2\u5230\u4e18\u5bb6\u7684\u65f6\u5019\uff0c\u53ea\u6709\u4e09\u5c81\uff0c\u4e09\u5c81\u7684\u5b69\u5b50\u8bb0\u5f97\u8fd9\u8bb8\u591a\u4e8b\u60c5\uff0c\u5df2\u7ecf\u662f\u5341\u5206\u96be\u5f97 \u4e86\u3002\u53ef\u662f\u8c37\u4e4b\u534e\u542c\u4e86\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u867d\u7136\u5df2\u6709\u70b9\u7ebf\u7d22\u53ef\u5bfb\uff0c\u4f46\u8fd9\u5b69\u5b50\u7684\u8eab\u4e16\u4e4b\u8c1c\uff0c\u8fd8\u662f\u6ca1\u6709\u63ed \u6653\uff0c\u800c\u4f3c\u4e4e\u66f4\u663e\u5f97\u795e\u79d8\u4e86\u3002 \u3000\u3000\u5728\u5979\u8c1c\u4e00\u6837\u7684\u8eab\u4e16\u4e4b\u4e2d\uff0c\u8fd8\u6709\u4e24\u70b9\u7279\u522b\u96be\u4ee5\u7d22\u89e3\u4e4b\u5904\uff0c\u7b2c\u4e00\uff0c\u5979\u7684\u7236\u6bcd\u4e3a\u4ec0\u4e48\u4e0d\u548c\u5979\u540c \u4f4f\uff1f\u800c\u5979\u7684\u6bcd\u4eb2\u8981\u5728\u665a\u4e0a\u5077\u5077\u53bb\u770b\u5979\uff0c\u8c37\u4e4b\u534e\u8d77\u521d\u731c\u60f3\uff0c\u5979\u6216\u8005\u662f\u8499\u53e4\u7684\u4ec0\u4e48\u738b\u516c\u8d35\u65cf\uff0c\u6216 \u56de\u7586\u914b\u957f\u7684\u5973\u513f\uff0c\u4e5f\u60f3\u5230\u4e86\u7f2a\u592b\u4eba\u6240\u7f16\u7684\u90a3\u4e2a\u6545\u4e8b\uff0c\u5373\u662f\u8bf4\u5979\u662f\u79c1\u751f\u5973\u7684\u8eab\u4efd\uff0c\u4f46\u82e5\u7136\u771f\u662f \u8fd9\u6837\u7684\u8bdd\uff0c\u90a3\u5c31\u66f4\u96be\u4ee5\u89e3\u91ca\u4e86\u3002 \u3000\u3000\u8981\u77e5\u897f\u5317\u7684\u6e38\u7267\u6c11\u65cf\u548c\u5728\u4e2d\u539f\u5b9a\u5c45\u7684\u6c49\u65cf\u5927\u5927\u4e0d\u540c\uff0c\u4ed6\u4eec\u4ee5\u4e00\u4e2a\u4e2a\u7684\u90e8\u843d\u4f5c\u4e3a\u5355\u4f4d\uff0c\u9010 \u6c34\u8349\u800c\u5c45\uff0c\u4eba\u6570\u4e5f\u4e0d\u4f1a\u5927\u591a\uff0c\u7ecf\u5e74\u7d2f\u6708\uff0c\u65cf\u4eba\u90fd\u662f\u805a\u5728\u4e00\u8d77\u7684\uff0c\u5f7c\u6b64\u4e92\u76f8\u719f\u6089\uff0c\u6709\u4ec0\u4e48\u79c1 \u4e8b\uff0c\u5f88\u96be\u9690\u7792\u3002\u6b64\u5176\u4e00\u3002\u518d\u8005\uff0c\u82e5\u8bf4\u8fd9\u5b69\u5b50\u662f\u7537\u65b9\u7684\u79c1\u751f\u5973\u7684\u8bdd\uff0c\u56de\u7586\u7684\u914b\u957f\u6216\u8499\u53e4\u7684\u738b \u516c\uff0c\u90fd\u6709\u5f88\u5927\u7684\u6743\u529b\uff0c\u4ed6\u65e0\u987b\u907f\u5fcc\uff0c\u82e5\u8bf4\u662f\u5973\u65b9\u7684\u79c1\u751f\u5973\u7684\u8bdd\uff0c\u5979\u53c8\u600e\u6562\u5c06\u5b69\u5b50\u5b89\u7f6e\u5728\u90a3\u6837 \u5b8f\u5927\u6c14\u6d3e\u7684\u5e10\u5e55\u91cc\uff1f\u53eb\u90a3\u8bb8\u591a\u4ec6\u4eba\u53bb\u7167\u6599\u5979\uff1f\u800c\u4e14\u8fd9\u5e10\u5e55\u53c8\u662f\u56fa\u5b9a\u5728\u4e00\u4e2a\u5730\u65b9\uff0c\u5e76\u4e0d\u79fb\u52a8 \u7684\uff1f\u5728\u4e00\u4e2a\u751f\u6d3b\u6bd4\u8f83\u7b80\u5355\uff0c\u4eba\u6570\u5e76\u4e0d\u5927\u591a\u7684\u6e38\u7267\u6c11\u65cf\u91cc\uff0c\u5979\u4e0d\u6015\u7ed9\u5979\u6709\u6743\u529b\u7684\u4e08\u592b\u53d1\u73b0\u5417\uff1f \u7b2c\u4e8c\uff0c\u4e18\u5ca9\u662f\u6cb3\u5357\u4e2d\u725f\u53bf\u4e00\u4e2a\u5c0f\u7ec5\u58eb\uff0c\u4ea4\u6e38\u4e5f\u4e0d\u7b97\u5f88\u5e7f\uff0c\u4ed6\u600e\u7684\u4f1a\u5230\u897f\u5317\u4e00\u4e2a\u8fbd\u8fdc\u5730\u65b9\u7684\u8349 \u539f\u4e0a\uff0c\u5c06\u8fd9\u4e2a\u5973\u5b69\u5b50\u62b1\u56de\u6765\uff0c\u800c\u4e14\u7518\u5fc3\u820d\u5f03\u4e86\u6027\u547d\uff0c\u4e5f\u8981\u4e3a\u5979\u4fdd\u5b88\u7740\u79d8\u5bc6\uff1f \u3000\u3000\u8c37\u4e4b\u534e\u6b63\u81ea\u82e6\u82e6\u601d\u7d22\uff0c\u5979\u7684\u4f8d\u5973\u8fdb\u6765\u62a5\u544a\uff0c\u8bf4\u662f\u767d\u82f1\u6770\u8981\u6765\u89c1\u5979\u3002 \u3000\u3000\u6c5f\u5357\u559c\u9053\uff1a\u201c\u767d\u5927\u54e5\u8db3\u667a\u591a\u8c0b\uff0c\u4e0d\u59a8\u548c\u4ed6\u5546\u8ba8\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u4e18\u5ca9\u81f3\u6b7b\u4e0d\u80af\u6cc4\u6f0f\u79d8\u5bc6\uff0c\u53c8\u66fe\u5429\u5490\u8fc7\u5979\uff0c\u4e0d\u8bb8\u5979\u5bf9\u4eba\u4e71\u8bf4\uff0c \u60f3\u6765\u8fd9\u4e2a\u79d8\u5bc6\u751a\u4e3a\u91cd\u8981\u3002\u767d\u5927\u54e5\u867d\u7136\u53ef\u9760\uff0c\u4f46\u6211\u60f3\u8fd8\u662f\u5c11\u4e00\u4e9b\u4eba\u77e5\u9053\u7684\u66f4\u597d\u3002\u6211\u8fd9\u6b21\u662f\u4e3a\u4e86 \u90a3\u5996\u5987\u786c\u8981\u5192\u9886\u5979\u7684\u7f18\u6545\uff0c\u8981\u4e0d\u7136\u6211\u4e5f\u4e0d\u4f1a\u95ee\u5979\u7684\u3002\u201d\u8bf4\u7f62\uff0c\u8fd8\u5bf9\u6c5f\u5357\u671b\u4e86\u4e00\u773c\uff0c\u4f3c\u4e4e\u8fd8\u6709 \u4ec0\u4e48\u6d3b\u8bed\u4e0d\u65b9\u4fbf\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u6c5f\u5357\u8fd8\u4e0d\u7b97\u592a\u7cca\u6d82\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u968f\u5373\u4f1a\u610f\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u8c37\u5973\u4fa0\u653e\u5fc3\uff0c\u6211\u8fd9\u6b21\u662f\u9002\u9022\u5176 \u4f1a\uff0c\u542c\u5230\u4e86\u8fd9\u8bb8\u591a\u4e8b\u60c5\uff0c\u6211\u51b3\u4e0d\u4f1a\u968f\u4fbf\u62ff\u53bb\u548c\u4eba\u8c08\u8bba\u3002\u6211\u53ef\u4ee5\u53d1\u8a93\uff0c\u8981\u662f\u6211\u6cc4\u6f0f\u51fa\u53bb\uff0c\u6211\u820c \u5934\u4e0a\u5c31\u957f\u4e2a\u5927\u7594\u75ae\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u4e0d\u7531\u5f97\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u6211\u76f8\u4fe1\u4f60\uff0c\u4f60\u4e0d\u5fc5\u4e71\u53d1\u6bd2\u8a93\u4e86\u3002\u201d\u968f\u5373\u53eb\u90a3 \u4f8d\u5973\u53bb\u8bf7\u767d\u82f1\u6770\u8fdb\u6765\u3002 \u3000\u3000\u767d\u82f1\u6770\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u90a3\u51e0\u4f4d\u4e2d\u4e86\u6bd2\u9488\u7684\u540c\u95e8\uff0c\u6bd2\u9488\u5df2\u7528\u5438\u94c1\u77f3\u5438\u51fa\u6765\u4e86\uff0c\u4ed6\u4eec\u4e2d\u7684\u6bd2\u5e78 \u800c\u8fd8\u4e0d\u7b97\u5389\u5bb3\uff0c\u670d\u4e86\u672c\u95e8\u7684\u89e3\u6bd2\u4e39\u5927\u81f4\u90fd\u53ef\u4ee5\u65e0\u4e8b\u4e86\u3002\u53ea\u6709\u7518\u5e08\u5f1f\u786c\u63a5\u4e86\u90a3\u5996\u5987\u7684\u6bd2\u638c\uff0c\u60c5 \u5f62\u5374\u662f\u6709\u70b9\u4e0d\u5999\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u5999\uff1f\u201d\u767d\u82f1\u6770\u9053\uff1a\u201c\u7518\u5e08\u5f1f\u670d\u4e0b\u4e86\u78a7\u7075\u4e39\uff0c\u4ecd\u7136\u660f\u8ff7 \u672a\u9192\uff0c\u521a\u624d\u8fd8\u5410\u4e86\u51e0\u53e3\u7600\u8840\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u8fd9\u662f\u56e0\u4e3a\u4ed6\u7684\u529f\u529b\u6bd4\u4e0d\u4e0a\u8c22\u5e08\u5ac2\uff0c\u6240\u4ee5\u75c5\u72b6\u4e5f\u663e\u5f97\u4e25\u91cd\u4e00\u4e9b\u3002\u4e0d\u8fc7\uff0c\u5410\u51fa \u7600\u8840\uff0c\u90a3\u5012\u65e0\u8db3\u4e3a\u8651\uff0c\u5410\u4e86\u51fa\u6765\uff0c\u6bd2\u6027\u53cd\u800c\u4f1a\u51cf\u8f7b\u4e00\u4e9b\u3002\u201d\u8c37\u4e4b\u534e\u66fe\u8eab\u53d7\u6b64\u6bd2\uff0c\u6545\u6b64\u6df1\u660e\u5229 \u5bb3\uff0c\u4f46\u78a7\u7075\u4e39\u53ea\u80fd\u6cbb\u6807\uff0c\u4e0d\u80fd\u6cbb\u672c\uff0c\u8c37\u4e4b\u534e\u60f3\u5230\u89e3\u836f\u96be\u6c42\uff0c\u4ea6\u662f\u5fc3\u4e2d\u70e6\u95f7\u3002 \u3000\u3000\u767d\u82f1\u6770\u53c8\u9053\uff1a\u201c\u90a3\u5996\u5987\u8fd8\u6709\u4e00\u6837\u7279\u522b\u4e4b\u5904\uff0c\u4e0d\u77e5\u638c\u95e8\u53ef\u66fe\u6ce8\u610f\uff1f\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4e0d\u77e5\u662f \u54ea\u4e00\u6837\uff1f\u201d\u767d\u82f1\u6770\u9053\uff1a\u201c\u5979\u7684\u5934\u53d1\u4e4b\u4e2d\u6709\u591a\u6839\u91d1\u53d1\uff0c\u770b\u6765\u4e0d\u50cf\u662f\u7eaf\u79cd\u6c49\u4eba\u3002\u201d\u6c5f\u5357\u56b7\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u6211\u6ce8\u610f\u5230\u4e86\uff0c\u8fd8\u6709\u5979\u7684\u773c\u775b\u4e5f\u662f\u78a7\u8272\uff0c\u516b\u6210\u662f\u80e1\u6c49\u76f8\u6742\u7684\u6df7\u8840\u513f\u3002\u201d\u8c37\u4e2d\u83b2\u4e0d\u61c2\u4ec0 \u4e48\u53eb\u201c\u7eaf\u79cd\u201d\u201c\u6742\u79cd\u201d\uff0c\u4e5f\u4e0d\u61c2\u5f97\u4ec0\u4e48\u53eb\u505a\u201c\u6df7\u8840\u513f\u201d\uff0c\u4f46\u542c\u4e86\u8fd9\u8bdd\uff0c\u5374\u5ffd\u7136\u56b7\u8d77\u6765\u9053\uff1a \u201c\u5988\u5988\uff0c\u6211\u7684\u5934\u53d1\u91cc\u6709\u51e0\u6839\u91d1\u9ec4\u8272\u7684\u5934\u53d1\uff0c\u4f60\u77a7\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u6bcf\u65e5\u7ed9\u5979\u68b3\u5934\uff0c\u65e9\u5df2\u6ce8\u610f\u5230\u4e86\uff0c\u8fd9\u65f6\u518d\u4ed4\u7ec6\u6ce8\u610f\u5979\u7684\u773c\u73e0\uff0c\u53d1\u73b0\u5979\u773c\u73e0\u7684\u8272\u6cfd\u4e5f \u6709\u4e9b\u5f02\u6837\uff0c\u867d\u7136\u4e0d\u4f3c\u7f2a\u592b\u4eba\u7684\u6df1\u78a7\uff0c\u5374\u4e5f\u5fae\u5e26\u68d5\u8272\uff0c\u8c37\u4e4b\u534e\u66f4\u589e\u7591\u8651\uff0c\u4f46\u968f\u5373\u60f3\u9053\uff1a\u201c\u5979\u4eec \u867d\u6709\u70b9\u76f8\u4f3c\uff0c\u4f46\u5c31\u51ed\u7740\u83b2\u513f\u6240\u8bf4\u7684\u8fd9\u4e9b\uff0c\u90a3\u5996\u5987\u4e5f\u51b3\u4e0d\u53ef\u80fd\u662f\u5979\u7684\u6bcd\u4eb2\u3002\u4e0d\u8fc7\uff0c\u53ef\u4ee5\u65ad\u5b9a\uff0c \u83b2\u513f\u5927\u7ea6\u4e5f\u662f\u4e2a\u6df7\u8840\u513f\u4e86\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u6bcf\u4e2a\u4eba\u7684\u76f8\u8c8c\u90fd\u4e0d\u76f8\u540c\uff0c\u5934\u53d1\u4e5f\u4e0d\u4f1a\u5b8c\u5168\u76f8\u540c \u7684\u3002\u83b2\u513f\uff0c\u6709\u51e0\u6839\u91d1\u8272\u7684\u5934\u53d1\uff0c\u8fd8\u66f4\u597d\u770b\u5462\u3002\u4f60\u4eca\u5929\u4e5f\u7d2f\u4e86\uff0c\u8fdb\u53bb\u7761\u4e2a\u89c9\u5427\u3002\u6211\u7b49\u4e0b\u518d\u6765\u966a \u4f60\u3002\u201d \u3000\u3000\u5f85\u4f8d\u5973\u9886\u4e86\u5b69\u5b50\u8fdb\u53bb\uff0c\u767d\u82f1\u6770\u4e5f\u8d70\u4e86\u4e4b\u540e\uff0c\u8c37\u4e4b\u534e\u518d\u95ee\u6c5f\u5357\uff1a\u201c\u6c5f\u5357\uff0c\u5728\u90a3\u5996\u5987\u672a\u6765\u4e4b \u524d\uff0c\u4f60\u4e0d\u662f\u8bf4\u5230\u548c\u90a3\u4e24\u4e2a\u756a\u50e7\u6076\u6597\uff0c\u6709\u4eba\u6697\u52a9\u4e4b\u4e8b\u5417\uff1f\u540e\u6765\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u540e\u6765\uff0c\u540e\u6765\u5c31\u662f\u78b0\u89c1\u8fd9\u5996\u5987\u4e86\u3002\u5148\u662f\u5979\u90a3\u4e24\u4e2a\u8f7f\u592b\u548c\u6211\u52a8\u624b\uff0c\u540e\u6765\u5979\u4e5f\u51fa\u624b \u5bb3\u6211\uff0c\u54c8\u54c8\uff0c\u5e78\u800c\u6211\u6c5f\u5357\u4e43\u662f\u5409\u4eba\u5929\u76f8\uff0c\u5904\u5904\u6709\u80fd\u4eba\u6697\u4e2d\u76f8\u52a9\u3002\u201d \u3000\u3000\u6c5f\u5357\u5c06\u7ecf\u8fc7\u7684\u60c5\u5f62\u8be6\u7ec6\u8bf4\u4e86\u4e00\u904d\uff0c\u8c37\u4e4b\u534e\u542c\u5f97\u751a\u4e3a\u7eb3\u7f55\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4e16\u9057\u4ed6 \u662f\u5df2\u7ecf\u5728\u6697\u4e2d\u7f00\u4e0a\u4e86\u8fd9\u5996\u5987\u4e86\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u9002\u624d\u8fd9\u5996\u5987\u5728\u6b64\u95f9\u4e8b\uff0c\u4ed6\u5374\u53c8\u4e3a\u4f55\u4e0d\u73b0\u51fa\u8eab\u6765\uff1f \u96be\u9053\u4ed6\u8fd8\u662f\u4e0d\u60f3\u89c1\u6211\u5417\uff1f\u201d\u5fc6\u8d77\u5f80\u4e8b\uff0c\u4e0d\u7981\u60d8\u7136\u3002 \u3000\u3000\u665a\u996d\u8fc7\u540e\uff0c\u8c37\u4e4b\u534e\u7763\u4fc3\u8c37\u4e2d\u83b2\u505a\u529f\u8bfe\uff0c\u6c5f\u5357\u5728\u65c1\u966a\u5979\u95f2\u8c08\uff0c\u6c5f\u5357\u770b\u7740\u8c37\u4e2d\u83b2\uff1b\u6b63\u81ea\u60f3\u8d77 \u81ea\u5df1\u7684\u5b69\u5b50\uff0c\u5ffd\u542c\u5f97\u949f\u58f0\u54e8\u54e8\uff0c\u8c37\u4e4b\u534e\u907d\u7136\u60ca\u8d77\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u54cd\u4eae\u7684\u7b11\u58f0\u4f20\u4e86\u8fdb \u6765\uff01 \u3000\u3000\u8fd9\u4e00\u9635\u7b11\u58f0\uff0c\u521d\u542c\u4e4b\u65f6\uff0c\u4f3c\u5728\u5c71\u95e8\u4e4b\u5916\uff0c\u500f\u5ffd\u4e4b\u95f4\uff0c\u4fbf\u4f3c\u5728\u8033\u8fb9\u54cd\u8d77\u4e0a\u822c\uff0c\u9707\u5f97\u6c5f\u5357\u7684 \u8033\u9f13\u90fd\u55e1\u55e1\u4f5c\u54cd\uff0c\u6c5f\u5357\u8df3\u8d77\u6765\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u8fd9\u5996\u5987\u53c8\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u4e5f\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u5996\u5987\u53d7\u4f24\u4e0d\u8f7b\uff0c\u65e5\u95f4\u9003\u8dd1\u4e4b\u65f6\uff0c\u8fd8\u8981\u501f\u52a9\u8f6f\u97ad \u4e4b\u529b\uff0c\u65b9\u80fd\u7ffb\u8fc7\u5899\u5934\uff0c\u600e\u4f1a\u597d\u5f97\u8fd9\u4e48\u5feb\uff0c\u800c\u4e14\u6765\u5f97\u5982\u6b64\u8fc5\u75be\uff1f\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u7b11\u58f0\u4e00\u6536\uff0c\u6765\u4eba\u5df2\u5728\u95e8\u5916\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5929\u9b54\u6559\u4e3b\uff0c\u8bf7\u89c1\u6c13\u5c71\u6d3e\u638c\u95e8\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u7ad9\u7acb\u8d77\u6765\uff0c\u53ea\u89c1\u95e8\u5185\u5df2\u7ad9\u7740\u4e09\u4e2a\u8499\u9762\u7684\u5973\u5b50\uff0c\u4e3a\u9996\u7684\u90a3\u4e2a\u5973\u5b50\uff0c\u4e14\u5df2\u5411\u5979\u88e3\u887d\u65bd \u793c\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u548c\u6c5f\u5357\u90fd\u6014\u4e86\u4e00\u6014\uff0c\u6e90\u6765\u8fd9\u5929\u9b54\u6559\u4e3b\u7684\u7b11\u58f0\u9177\u4f3c\u90a3\u7f2a\u592b\u4eba\uff0c\u8eab\u6750\u7684\u9ad8\u77ee\u4e5f\u5dee\u4e0d \u591a\uff0c\u4ed4\u7ec6\u770b\u65f6\uff0c\u624d\u53d1\u89c9\u5979\u662f\u67f3\u8170\u8885\u5a1c\uff0c\u8317\u6b65\u8f7b\u76c8\uff0c\u548c\u90a3\u7f2a\u592b\u4eba\u5927\u5927\u4e0d\u540c\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u8fd8\u4e86\u4e00\u793c\uff0c\u672a\u53ca\u95ee\u5979\uff0c\u4f46\u542c\u5f97\u811a\u6b65\u58f0\u547c\u559d\u58f0\u95f9\u5f97\u4e71\u54c4\u54c4\u7684\uff0c\u5362\u9053\u78f7\u3001\u767d\u82f1\u6770\u3001\u7a0b \u6d69\u8fd9\u4e00\u73ed\u4eba\u90fd\u5df2\u8d76\u6765\u3002\u7a0b\u6d69\u53eb\u9053\uff1a\u201c\u7980\u638c\u95e8\uff0c\u8fd9\u5996\u5987\u4e0a\u95e8\u95f9\u4e8b\uff0c\u5df2\u4f24\u4e86\u8bb8\u591a\u5f1f\u5b50\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u51e4\u773c\u542b\u55d4\uff0c\u4f46\u4ecd\u6309\u7740\u6b66\u6797\u7684\u89c4\u77e9\uff0c\u8fd8\u4e86\u4e00\u793c\uff0c\u7136\u540e\u95ee\u9053\uff1a\u201c\u539f\u6765\u662f\u5929\u9b54\u6559\u4e3b\u6765 \u4e86\uff0c\u5931\u8fce\uff0c\u5931\u8fce\uff01\u6211\u4e0e\u8d35\u6559\u7d20\u4e0d\u76f8\u6d89\uff0c\u4e0d\u77e5\u6559\u4e3b\u524d\u6765\uff0c\u6240\u4e3a\u4f55\u4e8b\uff1f\u4e00\u4e0a\u95e8\u4fbf\u51fa\u624b\u4f24\u4eba\uff0c\u53c8\u662f \u4f55\u7f18\u6545\uff1f\u201d \u3000\u3000\u90a3\u5929\u9b54\u6559\u4e3b\u7528\u8f7b\u7eb1\u8499\u9762\uff0c\u773c\u775b\u9732\u5728\u5916\u5934\uff0c\u53ea\u89c1\u5979\u7684\u773c\u73e0\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u795e\u8272\u81ea\u5982\uff0c\u5fae\u7b11\u8bf4 \u9053\uff1a\u201c\u7a0b\u5148\u751f\uff0c\u4f60\u8fd9\u8bdd\u672a\u514d\u662f\u5938\u5927\u4e86\uff0c\u6211\u54ea\u6709\u4f24\u53ca\u8d35\u6d3e\u5f1f\u5b50\uff0c\u53ea\u56e0\u4ed6\u4eec\u4e0d\u8bb8\u6211\u8fdb\u6765\uff0c\u6211\u53c8\u4e0d \u8010\u70e6\u4ed6\u4eec\u4e00\u91cd\u91cd\u7684\u901a\u62a5\uff0c\u6240\u4ee5\u8feb\u4e0d\u5f97\u5df2\uff0c\u624d\u70b9\u4e86\u4ed6\u4eec\u7a74\u9053\uff0c\u8fc7\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u4eec\u7684\u7a74\u9053\u81ea \u89e3\uff0c\u51b3\u65e0\u4f24\u635f\u3002\u4f60\u4eec\u53ef\u4ee5\u5b89\u5fc3\u3002\u54c8\u8d35\u6d3e\u9ad8\u624b\u5982\u4e91\uff0c\u96be\u9053\u8fde\u8fd9\u4e2a\u4e5f\u770b\u4e0d\u51fa\u6765\u5417\uff1f\u201d \u3000\u3000\u7a0b\u3001\u767d\u7b49\u4eba\u90fd\u662f\u9762\u7ea2\u8fc7\u8033\uff0c\u539f\u6765\u88ab\u8fd9\u5929\u9b54\u6559\u4e3b\u70b9\u5012\u7684\u5f1f\u5b50\u6709\u5341\u51e0\u540d\u4e4b\u591a\uff0c\u70b9\u5012\u4e4b\u540e\uff0c\u90fd \u662f\u5168\u8eab\u50f5\u786c\uff0c\u6c14\u606f\u5168\u65e0\uff0c\u4fe8\u5982\u6b7b\u4eba\u3002\u4efb\u4f55\u4e00\u6d3e\u7684\u70b9\u7a74\uff0c\u53d7\u5bb3\u4e4b\u4eba\u90fd\u4e0d\u4f1a\u6709\u8fd9\u6837\u8ff9\u8c61\uff0c\u6700\u5c11\u4e5f \u6709\u6c14\u606f\uff0c\u6240\u4ee5\u7a0b\u767d\u7b49\u4eba\u90fd\u4ee5\u4e3a\u8fd9\u4e9b\u5f1f\u4e8e\u662f\u4e2d\u4e86\u5267\u6bd2\u7684\uff0c\u6839\u672c\u5c31\u672a\u60f3\u5230\u662f\u53d7\u4e86\u70b9\u7a74\uff01\u5982\u4eca\u542c \u4e86\uff0c\u4e5f\u8fd8\u662f\u534a\u4fe1\u534a\u7591\u3002 \u3000\u3000\u6c13\u5c71\u6d3e\u662f\u6b66\u6797\u4e2d\u7684\u540d\u95e8\u5927\u6d3e\uff0c\u5982\u4eca\u7adf\u7ed9\u8fd9\u4e2a\u5929\u9b54\u6559\u4e3b\u95ef\u8fdb\u6765\uff0c\u800c\u4e14\u662f\u4f24\u4e86\u8bb8\u591a\u4eba\u4e4b\u540e\uff0c \u65b9\u624d\u53d1\u89c9\uff0c\u9e23\u949f\u62a5\u8b66\u3002\u5404\u5927\u5f1f\u5b50\u6df1\u611f\u9762\u4e0a\u65e0\u5149\uff0c\u53c8\u7f9e\u53c8\u607c\u3002\u4f46\u73b0\u5728\u5979\u5df2\u548c\u638c\u95e8\u89c1\u9762\uff0c\u8981\u662f\u4e00 \u62e5\u800c\u4e0a\uff0c\u90a3\u5c31\u66f4\u5931\u9762\u5b50\u3002\u56e0\u6b64\u5728\u7a0b\u6d69\u53d7\u4e86\u62a2\u767d\u4e4b\u540e\u8fd9\u4e00\u7247\u5608\u6742\u58f0\u97f3\u53cd\u800c\u9759\u4e86\u4e0b\u6765\uff0c\u5927\u5bb6\u90fd\u7b49 \u5f85\u8c37\u4e4b\u534e\u7684\u53d1\u843d\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u6d3e\u5f1f\u5b50\uff0c\u82e5\u7136\u793c\u4eea\u4e0d\u5468\uff0c\u6211\u81ea\u4f1a\u60e9\u7f5a\u4ed6\u4eec\uff0c\u4e0d\u52b3\u8d35\u6559\u4e3b\u4ee3\u4e3a\u7ba1 \u6559\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u539f\u6765\u8c37\u638c\u95e8\u4e5f\u80af\u548c\u4eba\u5bb6\u8bb2\u9053\u7406\u4e48\uff1f\u597d\uff0c\u6211\u70b9\u5012\u8d35\u6d3e\u7684\u5f1f\u5b50\uff0c\u8fd9\u4ef6 \u4e8b\u6211\u81ea\u8ba4\u7406\u4e8f\uff0c\u4e0d\u8fc7\uff0c\u597d\u5728\u4ed6\u4eec\u90fd\u672a\u53d7\u4f24\uff0c\u8c37\u638c\u95e8\u4e5f\u65e0\u987b\u52a8\u6012\u3002\u8fd8\u6709\u53e6\u4e00\u4ef6\u4e8b\uff0c\u6211\u5012\u8981\u548c\u8c37 \u638c\u95e8\u8bc4\u8bc4\u7406\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4f55\u4e8b\uff1f\u8bf7\u8bf4\uff01\u201d\u5929\u9b54\u6559\u4e3b\u8de8\u4e0a\u4e00\u6b65\uff0c\u76ee\u5149\u6ce8\u89c6\u8c37\u4e2d\u83b2\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b \u4e48\uff0c\u6211\u7684\u59d0\u59d0\u5df2\u7ecf\u548c\u8c37\u638c\u95e8\u8bf4\u8fc7\u4e86\uff0c\u5c31\u662f\u3002\u3002\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u5fc3\u4e2d\u4e00\u51db\uff0c\u622a\u7740\u5979\u7684\u8bdd\u9053\uff1a\u201c\u539f\u6765\u90a3\u4f4d\u7f2a\u592b\u4eba\u5c31\u662f\u4ee4\u59d0\uff1f\u201d\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u8f6c\u8eab \u5b50\u906e\u5728\u8c37\u4e2d\u83b2\u7684\u9762\u524d\uff0c\u5e76\u6325\u624b\u793a\u610f\uff0c\u53eb\u4f8d\u5973\u5e26\u8c37\u4e2d\u83b2\u8fdb\u53bb\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u51b7\u7b11\u9053\uff1a\u201c\u6211\u867d\u7136\u662f\u4e3a\u4e86\u7525\u513f\u4e4b\u4e8b\u800c\u6765\uff0c\u4f46\u4e5f\u51b3\u4e0d\u4f1a\u7528\u5f3a\u62a2\u593a\uff0c\u8c37\u638c\u95e8\uff0c\u4f60\u53ef \u4ee5\u653e\u5fc3\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u65e9\u5df2\u770b\u51fa\uff0c\u8fd9\u5929\u9b54\u6559\u4e3b\u867d\u8bf4\u662f\u90a3\u7f2a\u592b\u4eba\u7684\u59b9\u59b9\uff0c\u4f46\u5979\u7684\u6b66\u529f\uff0c\u5374\u5b9e\u662f\u8fdc\u5728\u90a3\u7f2a\u592b \u4eba\u4e4b\u4e0a\uff0c\u8c37\u4e4b\u534e\u7684\u786e\u662f\u6709\u70b9\u4e0d\u653e\u5fc3\u3002\u5979\u5f53\u7136\u4e5f\u542c\u5f97\u61c2\u5929\u9b54\u6559\u4e3b\u8fd9\u51e0\u53e5\u8bdd\u4e43\u662f\u8ba5\u8bbd\u5979\u5f3a\u5360\u8fd9\u5b69 \u5b50\u7684\uff0c\u4f46\u8fd9\u65f6\u5374\u65e0\u6687\u4e89\u8fa9\uff0c\u5979\u7d27\u63a5\u7740\u5929\u9b54\u6559\u4e3b\u7684\u8bdd\u4fbf\u9053\uff1a\u201c\u65e2\u7136\u6559\u4e3b\u613f\u610f\u8bb2\u7406\uff0c\u90a3\u662f\u6700\u597d\u4e0d \u8fc7\u3002\u83b2\u513f\uff0c\u4f60\u81ea\u5df1\u53bb\u505a\u529f\u8bfe\u5427\uff0c\u5a18\u6709\u5ba2\u4eba\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u521a\u521a\u5750\u5b9a\uff0c\u8c37\u4e4b\u534e\u6b63\u8981\u548c\u5979\u8bf4\u8bdd\uff0c\u6c5f\u5357\u5ffd\u5730\u5927\u53eb\u8d77\u6765\u9053\uff1a\u201c\u4f60\u613f\u610f\u8bb2\u7406\u4e48\uff0c \u597d\uff0c\u6211\u5c31\u5148\u548c\u4f60\u8bb2\u7406\uff01\u4f60\u8bf4\u4f60\u4e0d\u4f1a\u5f3a\u593a\u4eba\u5bb6\u7684\u5b69\u5b50\uff0c\u90a3\u4e48\uff0c\u4f60\u4e3a\u4ec0\u4e48\u53c8\u62a2\u4e86\u6211\u7684\u5b69\u513f\uff1f\u201d\u4ed6 \u8bb2\u5f97\u6fc0\u52a8\u8d77\u6765\uff0c\u6307\u624b\u5212\u811a\u7684\u5f84\u5411\u5929\u9b54\u6559\u4e3b\u5954\u53bb\uff01 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6d51\u5c0f\u5b50\uff0c\u4f60\u597d\u65e0\u793c\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u8863\u895f\u5e26\u98ce\u4e4b\u58f0\uff0c\u5929 \u9b54\u6559\u4e3b\u90a3\u4e24\u4e2a\u4f8d\u5973\u5df2\u62e6\u4f4f\u4e86\u6c5f\u5357\u7684\u53bb\u8def\u3002 \u3000\u3000\u6c5f\u5357\u8ba4\u5f97\u5979\u4eec\u5c31\u662f\u5f53\u65e5\u5728\u4ed6\u5bb6\u4e2d\u95f9\u4e8b\u7684\u8499\u9762\u5973\u90ce\uff0c\u800c\u4e14\u5176\u4e2d\u4e00\u4e2a\u9ed1\u8863\u5973\u5b50\u8fd8\u6b63\u662f\u63b3\u8d70\u4ed6 \u513f\u5b50\u7684\u4eba\uff0c\u6c5f\u5357\u4e0d\u7981\u6012\u4ece\u5fc3\u8d77\uff0c\u4e00\u624b\u5c31\u5411\u5979\u6293\u53bb\uff0c\u559d\u9053\uff1a\u201c\u8fd8\u6211\u513f\u6765\uff01\u201d \u3000\u3000\u90a3\u9ed1\u8863\u5973\u5b50\u67f3\u8170\u4e00\u5f2f\uff0c\u4e2d\u6307\u4e00\u4f38\uff0c\u5c31\u70b9\u5230\u4e86\u6c5f\u5357\u5c0f\u8179\uff0c\u6108\u6c14\u7a74\u201d\uff0c\u53e6\u4e00\u4e2a\u9ec4\u886b\u5973\u5b50\u53f3\u638c \u865a\u6643\uff0c\u5c06\u6c5f\u5357\u4e00\u5e26\uff0c\u5de6\u638c\u4e00\u7ffb\u4fbf\u6263\u7740\u4e86\u6c5f\u5357\u7684\u8109\u95e8\uff0c\u7a0b\u6d69\u548c\u767d\u82f1\u6770\u5927\u60ca\uff0c\u53cc\u53cc\u62a2\u4e0a\u3002 \u3000\u3000\u6c5f\u5357\u4f7f\u4e86\u4e2a\u201c\u91d1\u8749\u8131\u58f3\u201d\u7684\u89e3\u6570\uff0c\u6c89\u80a9\u7f29\u65f6\uff0c\u6323\u8131\u51fa\u6765\uff0c\u4f46\u89c9\u4e39\u7530\u548c\u8109\u95e8\uff0c\u90fd\u662f\u706b\u8fa3\u8fa3 \u7684\u9690\u9690\u4f5c\u75db\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u90a3\u5929\u9b54\u6559\u4e3b\u5df2\u5728\u559d\u4ee4\u90a3\u4e24\u4e2a\u4f8d\u5973\u4f4f\u624b\uff0c\u7a0b\u6d69\u548c\u767d\u82f1\u6770\u89c1\u5979\u4eec\u5df2\u7ecf\u4f4f \u624b\uff0c\u4e5f\u4fbf\u505c\u4e0b\u811a\u6b65\u3002 \u3000\u3000\u5e78\u800c\u6c5f\u5357\u6709\u98a0\u5012\u7a74\u9053\u7684\u529f\u592b\uff0c\u867d\u662f\u5403\u4e8f\uff0c\u5374\u65e0\u5927\u788d\uff0c\u4f46\u5df2\u4ee4\u4ed6\u5403\u60ca\u975e\u5c0f\uff0c\u539f\u6765\u8fd9\u4e24\u4e2a\u8499 \u9762\u5973\u5b50\u6240\u7528\u7684\u529f\u592b\uff0c\u5c31\u6b63\u662f\u4ece\u6c5f\u5357\u8fd9\u513f\u5077\u5e08\u7684\u3002\u90a3\u4e00\u6b21\u5979\u4eec\u8f6e\u6d41\u4e0e\u6c5f\u5357\u8f83\u91cf\u8eab\u624b\uff0c\u9a97\u53d6\u4e86\u91d1 \u4e16\u9057\u6240\u4f20\u7684\u529f\u592b\uff0c\u5982\u4eca\u7adf\u5df2\u662f\u9752\u51fa\u4e8e\u84dd\u4e86\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u559d\u4ee4\u505c\u624b\u4e4b\u540e\uff0c\u4fbf\u628a\u76ee\u5149\u8f6c\u5411\u6c5f\u5357\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6d51\u5c0f\u5b50\uff0c\u4f60\u8981\u52a8\u624b\uff0c\u53ea\u6709\u81ea \u5df1\u5403\u4e8f\uff1b\u4f60\u8981\u8bb2\u7406\u561b\uff0c\u6211\u5012\u53ef\u4ee5\u8fd8\u4f60\u4e00\u4e2a\u9053\u7406\u3002\u201d \u3000\u3000\u6c5f\u5357\u6012\u9053\uff1a\u201c\u4f60\u5c45\u7136\u8fd8\u6709\u9053\u7406\u53ef\u8bf4\u4e48\uff1f\u6211\u5012\u8981\u6d17\u8033\u606d\u542c\u4e86\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u6211\u7684\u4f8d\u5973\u4e0d\u662f\u5411\u4f60\u4ea4\u4ee3\u8fc7\u4e48\uff1f\u53eb\u4f60\u4e0d\u8bb8\u80e1\u4e71\u6258\u4eba\u8ffd\u67e5\u6211\u4eec\u7684\u5e95\u7ec6\uff0c\u4f60\u5374 \u5148\u5411\u90a3\u59ec\u6653\u98ce\u8bf4\u4e86\uff0c\u73b0\u5728\u53c8\u5230\u6c13\u5c71\u4e0a\u6765\u642c\u6551\u5175\uff0c\u4f60\u65e2\u8fdd\u80cc\u8bfa\u8a00\uff0c\u6211\u5c31\u53ea\u597d\u6682\u4e14\u6263\u7559\u4f60\u7684\u5b69\u5b50 \u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u53c8\u60ca\u53c8\u6012\uff0c\u60ca\u8005\u662f\u81ea\u5df1\u4e0e\u59ec\u6653\u98ce\u7684\u8c08\u8bdd\uff0c\u8fd9\u5929\u9b54\u6559\u4e3b\u7adf\u5df2\u77e5\u9053\uff0c\u6012\u8005\u662f\u5979\u58f0\u8a00\u8981\u6263 \u7559\u81ea\u5df1\u7684\u5b69\u5b50\u3002\u5f53\u4e0b\u4fbf\u5927\u58f0\u6297\u8bae\u9053\uff1a\u201c\u90a3\u662f\u4f60\u7684\u4f8d\u5973\u81ea\u8bf4\u81ea\u8bdd\uff0c\u6211\u4f55\u66fe\u5e94\u5141\u8fc7\u4ec0\u4e48\u8bfa\u8a00\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u542c\u6211\u4f8d\u5973\u7684\u5429\u5490\uff0c\u4f60\u5c31\u662f\u4e8f\u7406\u4e86\u3002\u563f\u563f\uff0c\u4f60\u8981\u662f\u4e0d\u670d\u6211\u8fd9\u9053\u7406\uff0c \u5c3d\u53ef\u9080\u8bf7\u4f60\u90a3\u4e9b\u9e21\u9e23\u72d7\u76d7\u7684\u670b\u53cb\uff0c\u5230\u7ec4\u6765\u5c71\u6765\uff0c\u6309\u6b66\u6797\u89c4\u77e9\u4e0e\u6211\u89c1\u4e2a\u9ad8\u4f4e\uff01\u6211\u7684\u9053\u7406\u5c31\u662f\u5982 \u6b64\uff0c\u73b0\u5728\u6211\u6709\u6b63\u7ecf\u4e8b\u8981\u4e0e\u8c37\u638c\u95e8\u5546\u8c08\uff0c\u4e0d\u8010\u70e6\u548c\u4f60\u518d\u8bf4\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u4f60\u653e\u5fc3\uff0c\u6211\u51b3\u4e0d\u8ba9\u4f60\u7ed9\u4eba\u6b3a\u8d1f\u3002\u5c31\u8ba9\u5979\u5148\u8c08\u4eca\u65e5\u4e0a\u5c71\u95f9\u4e8b\u4e4b \u2018\u7406\u2019\uff0c\u8981\u662f\u8fd8\u4e0d\u51fa\u9053\u7406\u6765\uff0c\u54b1\u4eec\u4e24\u4ef6\u4e8b\u60c5\u4e00\u540c\u4e86\u7ed3\uff01\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5012\u8981\u542c\u542c\u4f60\u7684\u9053\u7406\uff0c\u4f60\u51ed\u4ec0\u4e48\u9053\u7406\u5f3a\u593a\u6211\u7684\u7525\u513f\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u83b2\u513f\u6839\u672c\u5c31\u4e0d\u662f\u4f60\u59d0\u59d0\u6240\u751f\uff0c\u6211\u65e9\u5df2\u5bf9\u5979\u8bb2\u5f97\u6e05\u6e05\u695a\u695a\u4e86\uff0c\u96be\u9053\u4f60\u8fd8\u672a \u77e5\uff0c\u8981\u6211\u518d\u8bf4\u4e00\u904d\u4e48\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u6211\u53ea\u4fe1\u6211\u59d0\u59d0\u7684\u8bf4\u8bdd\uff0c\u5979\u8bf4\u5f97\u6709\u51ed\u6709\u636e\uff0c\u51b3\u4e0d\u4f1a\u5047\uff01\u201d\u8c37\u4e4b\u534e\u51b7\u7b11\u9053\uff1a \u201c\u4f60\u504f\u542c\u4e00\u9762\u4e4b\u8f9e\uff0c\u8fd9\u5c31\u6ca1\u6709\u9053\u7406\u53ef\u8bf4\u4e86\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u6211\u5c31\u518d\u7ed9\u4f60\u4e00\u4e2a\u8bc1\u636e\uff0c\u4f60\u8bf4\u6211\u59d0\u59d0\u4e0d\u77e5\u68c9\u8884\u4e0a\u94ae\u6263\u7684\u79d8\u5bc6\uff0c\u662f \u7684\uff0c\u8fd9\u79d8\u5bc6\u5979\u662f\u4e0d\u77e5\uff0c\u4f46\u5176\u4e2d\u5374\u6709\u4e00\u4e2a\u7f18\u6545\u3002\u90a3\u4e00\u6392\u94ae\u6263\u662f\u6211\u7ed9\u5979\u9489\u4e0a\u7684\uff0c\u90a3\u94ae\u6263\u662f\u661f\u5bbf\u6d77 \u7684\u5929\u5fc3\u77f3\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u5403\u4e86\u4e00\u60ca\uff0c\u968f\u5373\u4fbf\u53cd\u9a73\u9053\uff1a\u201c\u4f60\u8fd9\u7406\u7531\u4e5f\u8fd8\u662f\u6b20\u901a\uff0c\u4f60\u662f\u5979\u7684\u59b9\u59b9\uff0c\u4f60\u9489\u4e0a\u7684\u94ae \u6263\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u600e\u7684\u5979\u4e0d\u77e5\u9053\uff1f\u5373\u7b97\u4e8b\u5148\u4e0d\u77e5\uff0c\u4e8b\u540e\u4f60\u4e5f\u8be5\u544a\u8bc9\u5979\uff1b\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u544a\u4e0d\u544a\u8bc9\u5979\uff0c\u8fd9\u5c31\u662f\u6211\u7684\u4e8b\u4e86\u3002\u8fd9\u4e2a\u7406\u7531\u4e0e\u672c\u9898\u65e0\u5173\uff0c\u6211\u65e0\u987b\u544a\u8bc9\u4f60\uff01 \u6211\u80fd\u591f\u8bf4\u5f97\u51fa\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u8fd9\u4fbf\u662f\u6709\u529b\u7684\u8bc1\u636e\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u597d\uff0c\u5c31\u7b97\u8fd9\u4e2a\u4f60\u8bf4\u5f97\u5bf9\u4e86\uff0c\u68c9\u8884\u5185\u8fd8\u6709\u4ec0\u4e48\u5176\u4ed6\u7684\u79d8\u5bc6\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u79d8\u5bc6\uff0c\u4f60\u8bf4\u8bf4\u770b\u3002\u6211\u662f\u8bf4\u6ca1\u6709\u4e86\u7684\uff0c\u4f60\u82e5\u8bf4\u6709\uff0c\u5c31\u62ff\u51fa\u6765\u8ba9\u6211 \u77a7\u77a7\uff0c\u6211\u4e00\u5b9a\u8ba4\u8f93\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u5fc3\u5934\u4e00\u51db\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5979\u8fd9\u662f\u8bda\u5fc3\u8bd3\u9a97\u6211\u7684\u6ccc\u5bc6\uff0c\u90a3\u5f20\u7eb8\u7247\uff0c\u5b9a\u7136\u6781\u5173\u91cd\u8981\uff0c \u5c82\u53ef\u8ba9\u7ed9\u5979\u77a7\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u7136\u4e0d\u77e5\u53e6\u6709\u79d8\u5bc6\uff0c\u90a3\u5c31\u8db3\u8bc1\u4e0d\u662f\u4f60\u7684\u7525\u513f\uff01\u201d\u5929\u9b54\u6559\u4e3b \u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u62ff\u4e0d\u51fa\u6765\uff0c\u7109\u77e5\u4e0d\u662f\u4f60\u634f\u9020\u4e4b\u8f9e\uff01\u201d \u3000\u3000\u8fd9\u6837\u4e89\u8bba\uff0c\u5f53\u7136\u6beb\u65e0\u7ed3\u679c\u3002\u5929\u9b54\u6559\u4e3b\u7a81\u7136\u51b7\u51b7\u4e00\u7b11\uff0c\u5c06\u624b\u4e0a\u7684\u8336\u676f\u5728\u684c\u4e0a\u4e00\u987f\uff0c\u8bf4\u9053\uff1a \u201c\u65e2\u7136\u5404\u6267\u4e00\u8f9e\uff0c\u96be\u4ee5\u89e3\u51b3\uff0c\u90a3\u5c31\u53ea\u6709\u6309\u6c5f\u6e56\u89c4\u77e9\u6765\u529e\u4e8b\u4e86\uff0c\u6211\u4e0d\u81ea\u91cf\u529b\uff0c\u4e45\u95fb\u8c37\u638c\u95e8\u7684\u5185 \u529f\u5251\u6cd5\u4e24\u7686\u7cbe\u5999\uff0c\u6211\u8981\u5148\u5411\u8c37\u638c\u95e8\u9886\u6559\u5185\u529f\uff0c\u7136\u540e\u518d\u5411\u4f60\u5b66\u51e0\u62db\u5251\u6cd5\uff01\u201d \u3000\u3000\u90a3\u4e00\u676f\u8336\u662f\u8c37\u4e4b\u534e\u7684\u4f8d\u5973\u521a\u624d\u5012\u7ed9\u5979\u7684\uff0c\u5979\u8fd8\u6ca1\u6709\u559d\u8fc7\u534a\u70b9\uff0c\u90a3\u4e2a\u8336\u676f\u662f\u6c5f\u897f\u6709\u540d\u7684\u7cbe \u7f8e\u74f7\u5668\uff0c\u7ed9\u5979\u5728\u684c\u5b50\u4e0a\u4e00\u62cd\uff0c\u8336\u676f\u7adf\u7136\u9677\u5165\u684c\u5185\uff0c\u51e0\u4e4e\u4e0e\u684c\u9762\u76f8\u5e73\uff0c\u676f\u5185\u7684\u70ed\u8336\uff0c\u7adf\u7136\u4e5f\u6ca1 \u6709\u6e85\u51fa\u534a\u70b9\uff01 \u3000\u3000\u8fd9\u4e00\u624b\u529f\u592b\uff0c\u767b\u65f6\u4ee4\u5f97\u5728\u573a\u7684\u6c13\u5c71\u5f1f\u5b50\u90fd\u819b\u76ee\u7ed3\u820c\uff0c\u8c37\u4e4b\u534e\u4e5f\u6697\u6697\u60ca\u5fc3\u3002\u5979\u8fd9\u684c\u5b50\u662f\u575a \u5b9e\u7684\u7d2b\u6a80\u9999\u6728\u6240\u9020\uff0c\u5373\u7b97\u6709\u94c1\u7802\u638c\u7684\u529f\u592b\uff0c\u4e5f\u4e0d\u5bb9\u6613\u5c06\u5b83\u62cd\u88c2\uff0c\u4f55\u51b5\u8fd9\u5929\u9b54\u6559\u4e3b\u6240\u7528\u7684\u4ec5\u662f \u4e00\u4e2a\u8106\u8584\u6613\u788e\u7684\u8336\u676f\uff01\u8fd9\u624b\u529f\u592b\uff0c\u7b80\u76f4\u4e0e\u6700\u4e0a\u4e58\u7684\u201c\u6458\u53f6\u98de\u82b1\u3001\u4f24\u4eba\u7acb\u6b7b\u201d\u7684\u529f\u592b\u5f02\u66f2\u540c \u5de5\uff0c\u8c37\u4e4b\u534e\u73b0\u5728\u7684\u5185\u529f\u9020\u8be3\uff0c\u867d\u7136\u4ea6\u5df2\u5230\u4e86\u4e00\u6d41\u5883\u754c\uff0c\u4f46\u81ea\u95ee\u8fd8\u6ca1\u6709\u8fd9\u6837\u7684\u529f\u529b\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u6b63\u5728\u4e3a\u96be\uff0c\u5ffd\u5730\u5c4b\u89d2\u6709\u4e00\u4e2a\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7684\u638c\u95e8\u5c82\u662f\u8f7b\u6613\u4e0e\u4eba\u6bd4\u8bd5\u7684\u3002\u4f60\u8981 \u8f83\u91cf\u5185\u529f\uff0c\u8f83\u91cf\u5251\u6cd5\uff0c\u6211\u6765\u5949\u966a\uff0c\u4f60\u80dc\u5f97\u4e86\u6211\uff0c\u7136\u540e\u518d\u8bf4\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u7684\u76ee\u5149\uff0c\u90fd\u671d\u7740\u8fd9\u4eba\u770b\u53bb\uff0c\u8c03\u89c1\u662f\u4e00\u4e2a\u4e2d\u7b49\u8eab\u6750\u7684\u6c49\u5b50\uff0c\u8138\u4e0a\u6beb\u65e0\u8868 \u60c5\uff0c\u4e00\u773c\u770b\u53bb\uff0c\u7adf\u4e0d\u4f3c\u662f\u751f\u4eba\u7684\u8138\u5b54\uff0c\u4ee4\u4eba\u6709\u9b3c\u6c14\u9634\u6c89\u7684\u611f\u89c9\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u8c01\u90fd\u4e0d\u8ba4\u5f97\uff0c\u5929\u9b54\u6559\u4e3b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u201d\u8fd9\u4eba\u7684\u7b54\u8bdd\uff0c\u66f4\u4ee4\u6c13\u5c71\u6d3e\u4f17\u5f1f \u5b50\u5927\u5927\u60ca\u7591\u3002\u4f60\u9053\u4ed6\u8bf4\u4ec0\u4e48\uff1f\u4ed6\u8bf4\uff1a\u201c\u6211\u4e48\uff1f\u6211\u53ea\u4e0d\u8fc7\u662f\u6c13\u5c71\u6d3e\u7684\u4e00\u4e2a\u672a\u5b66\u5f1f\u5b50\uff01\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u6551\u5175\u5ffd\u5730\u4ece\u5929\u964d\uff0c\u53c8\u89c1\u4eba\u95f4\u73b0\u4fa0\u8e2a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000--------------- \u3000\u3000\u626c\u5251\u8f69\u5c45\u58eb \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e5d\u56de\u3000\u4e91\u5f00\u6708\u73b0\u5206\u771f\u5047\u3000\u5315\u9732\u56fe\u7a77\u8fa9\u53cb\u4ec7 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e5d\u56de\u3000\u4e91\u5f00\u6708\u73b0\u5206\u771f\u5047\u3000\u5315\u9732\u56fe\u7a77\u8fa9\u53cb\u4ec7 \u3000\u3000\u8fd9\u4eba\u81ea\u79f0\u662f\u6c13\u5c71\u6d3e\u7684\u5f1f\u5b50\uff0c\u6c13\u5c71\u6d3e\u7684\u5f1f\u5b50\u4e2a\u4e2a\u60ca\u7591\uff0c\u90fd\u5728\u9762\u9762\u76f8\u89d1\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a \u2018\u540c\u95e8\u2019\u662f\u4ece\u54ea\u91cc\u94bb\u51fa\u6765\u7684\uff1f\u201d\u8fd9\u4e2a\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u523a\u8033\u975e\u5e38\uff0c\u4f3c\u4e4e\u662f\u6545\u610f\u634f\u7740\u55d3\u5b50\u8bf4\u8bdd\uff01\u8c37 \u4e4b\u534e\u8d77\u521d\u4e5f\u975e\u5e38\u7eb3\u7f55\uff0c\u65cb\u5373\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u5230\u4e86\u4e00\u4e2a\u4eba\uff0c\u82b3\u5fc3\u5fd0\u5fd1\uff0c\u53c8\u559c\u53c8\u60ca\uff0c\u5979\u6781\u529b\u6291\u6b62\u4e86 \u6fc0\u52a8\u7684\u60c5\u7eea\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u4f60\u672a\u66fe\u51fa\u8fc7\u9053\uff0c\u5c31\u8ba9\u4f60\u501f\u8fd9\u4e2a\u673a\u4f1a\u78e8\u7ec3\u78e8\u7ec3\u5427\uff0c\u5c0f\u5fc3\u53bb\u5411 \u6559\u4e3b\u8ba8\u6559\u51e0\u62db\uff01\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u51b7\u7b11\u9053\uff1a\u201c\u8c37\u638c\u95e8\uff0c\u4f60\u4e5f\u5fd2\u5c0f\u89d1\u6211\u4e86\uff0c\u6211\u53ea\u914d\u5411\u8d35\u6d3e\u4e00\u4e2a\u672a\u51fa\u8fc7\u9053\u7684\u5f1f\u5b50\u53e8\u6559 \u4e48\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u4e0d\u5f85\u8c37\u4e4b\u534e\u7b54\u8bdd\uff0c\u7acb\u5373\u4e5f\u51b7\u7b11\u4e00\u58f0\uff0c\u7d27\u63a5\u7740\u5929\u9b54\u6559\u4e3b\u7684\u8bdd\u8bed\u8bf4\u9053\uff1a\u201c\u6559\u4e3b\uff0c\u4f60\u662f \u4e3a\u4e86\u66ff\u4ee4\u59d0\u51fa\u5934\uff0c\u6211\u662f\u4e3a\u638c\u95e8\u6548\u52b3\uff0c\u6709\u54ea\u70b9\u4e0d\u5408\u6b66\u6797\u89c4\u77e9\uff1f\u6211\u56fa\u7136\u672a\u66fe\u51fa\u8fc7\u9053\uff0c\u6559\u4e3b\u4f60\u4e5f\u4e0d \u89c1\u5f97\u6c5f\u6e56\u4e0a\u6709\u4ec0\u4e48\u540d\u5934\uff0c\u600e\u89c1\u5f97\u662f\u8fb1\u6ca1\u4f60\u4e86\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u8eab\u8d1f\u7edd\u6280\uff0c\u4f46\u5979\u5728\u6c5f\u6e56\u4e0a\u4ece\u672a\u9732\u9762\uff0c\u77e5\u9053\u5979\u7684\u7684\u786e\u4e5f\u8fd8\u4e0d\u591a\u3002\u8fd9\u4e2a\u4eba\u7684\u8bdd\u610f\u5373 \u662f\u8bf4\uff1a\u201c\u4f60\u4e5f\u4e0d\u8fc7\u662f\u4e2a\u521d\u51fa\u9053\u7684\u4eba\u3002\u201d\u5929\u9b54\u6559\u4e3b\u542c\u4e86\uff0c\u52c3\u7136\u5927\u6012\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u8981 \u66ff\u8d35\u6d3e\u638c\u95e8\u4e0b\u573a\uff0c\u6211\u5df2\u732e\u62d9\u8fc7\u4e86\uff0c\u4f60\u4e5f\u5f97\u5148\u9732\u51fa\u4e00\u624b\u77a7\u77a7\uff01\u201d \u3000\u3000\u8fd9\u7b49\u4e8e\u51fa\u4e86\u4e00\u9053\u96be\u9898\uff0c\u9664\u4e86\u8c37\u4e4b\u534e\u4e4b\u5916\uff0c\u6c13\u5c71\u6d3e\u7684\u4eba\u90fd\u5728\u4e3a\u8fd9\u6c49\u5b50\u53d1\u6101\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a \u201c\u5929\u9b54\u6559\u4e3b\u63b7\u676f\u88c2\u6848\uff0c\u8fd8\u6709\u4ec0\u4e48\u529f\u592b\u53ef\u4ee5\u5c06\u5979\u6bd4\u538b\u4e0b\u53bb\u5462\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u5374\u662f\u795e\u8272\u81ea\u5982\uff0c\u4e0d\u614c\u4e0d\u5fd9\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u672a\u5b66\u540e\u8fdb\uff0c\u6709\u4ec0\u4e48\u60ca\u4e16\u9a87\u4fd7\u7684\u529f\u592b\uff1f\u6211 \u5148\u4f3a\u4faf\u4f60\u559d\u4e00\u676f\u8336\u5427\uff01\u4f60\u8fdc\u6765\u662f\u5ba2\uff0c\u8336\u4e5f\u4e0d\u559d\u4e00\u676f\uff0c\u8fd9\u672a\u514d\u592a\u4e0d\u7ed9\u6211\u4eec\u9762\u5b50\u4e86\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u51b7\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u8fd9\u4e00\u676f\u5df2\u53d6\u4e0d\u51fa\u6765\u4e86\uff0c\u4f60\u518d\u5012\u4e00\u676f\u5427\uff01\u201d\u5979\u8bf4\u8fd9\u8bdd\uff0c\u4e00\u6765\u662f\u6316 \u82e6\u6c13\u5c71\u6d3e\u65e0\u4eba\u6709\u6b64\u80fd\u529b\uff0c\u4e8c\u6765\u662f\u60f3\u7f9e\u8fb1\u8fd9\u4e2a\u6c49\u5b50\uff0c\u5e76\u60f3\u5728\u4ed6\u7aef\u8336\u7684\u65f6\u5019\uff0c\u518d\u70ab\u795e\u529f\u3002 \u3000\u3000\u4e0d\u6599\u90a3\u6c49\u5b50\u7b11\u4e86\u4e00\u7b11\uff0c\u7d27\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4e00\u8336\u4e00\u996d\uff0c\u90fd\u5f53\u601d\u6765\u4e0d\u6613\uff0c\u5c82\u53ef\u66b4\u6b84\u5929\u7269\u3002\u6211\u8fd8 \u662f\u60f3\u501f\u8fd9\u676f\u8336\u5949\u656c\u8d35\u5ba2\uff01\u201d\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u4ed6\u624b\u81c2\u4e00\u62ac\uff0c\u5411\u90a3\u5f20\u684c\u5b50\u9065\u53d1\u4e00\u638c\uff0c\u4f46\u542c\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u5f20\u7d2b\u6a80\u6728\u684c \u5b50\uff0c\u767b\u65f6\u88c2\u5f00\uff0c\u90a3\u676f\u72b9\u81ea\u70ed\u6c14\u817e\u817e\u7684\u8336\u676f\u98de\u4e86\u8d77\u6765\uff0c\u90a3\u6c49\u5b50\u4ee5\u654f\u6377\u65e0\u4f26\u7684\u624b\u6cd5\uff0c\u5728\u8336\u676f\u8fb9\u7f18 \u4e00\u63a8\uff0e\u90a3\u4e2a\u8336\u676f\u5e73\u5e73\u7a33\u7a33\u7684\u5411\u5929\u9b54\u6559\u4e3b\u98de\u53bb\uff01 \u3000\u3000\u6240\u6709\u5728\u573a\u7684\u4eba\u90fd\u5413\u5f97\u5446\u4e86\uff0c\u7528\u5288\u7a7a\u638c\u51fb\u88c2\u684c\u5b50\uff0c\u8fd9\u5df2\u7ecf\u662f\u60ca\u4e16\u9a87\u4fd7\u7684\u529f\u592b\uff0c\u684c\u88c2\u800c\u676f\u4e0d \u788e\uff0c\u8336\u4e5f\u672a\u66fe\u6e85\u51fa\u4e00\u70b9\uff0c\u8fd9\u66f4\u662f\u96be\u4ee5\u601d\u8bae\u4e86\uff01\u8fd9\u624b\u529f\u592b\u6bd4\u4e4b\u90a3\u5929\u9b54\u6559\u4e3b\u7684\u63b7\u676f\u88c2\u6848\uff0c\u4e0d\u77e5\u9ad8 \u660e\u51e0\u500d\uff01 \u3000\u3000\u6c13\u5c71\u6d3e\u7684\u5f1f\u5b50\u4e4b\u4e2d\uff0c\u5982\u767d\u82f1\u6770\u3001\u7a0b\u6d69\u7b49\u4eba\u90fd\u662f\u7ecf\u5386\u8fc7\u8bb8\u591a\u5927\u573a\u9762\u7684\uff0c\u5f53\u5e74\u5343\u969c\u576a\u4e4b\u4f1a\uff0c \u5929\u5c71\u638c\u95e8\u5510\u6653\u6f9c\u6076\u6597\u5b5f\u795e\u901a\uff0c\u4ed6\u4eec\u4e5f\u66fe\u5728\u573a\uff0c\u5982\u4eca\u89c1\u4e86\u6b64\u4eba\u7684\u7edd\u9876\u795e\u529f\uff0c\u66f4\u662f\u60ca\u7591\u4ea4\u96c6\u3002\u5fc3 \u4e2d\u60f3\u9053\uff1a\u201c\u4ee5\u6b64\u80fd\u4e3a\uff0c\u53ea\u6015\u5510\u5927\u4fa0\u4e5f\u672a\u5fc5\u80fd\u591f\uff01\u4ed6\u5374\u4e3a\u4f55\u662f\u5192\u8ba4\u672c\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u5c31\u5728\u4f17\u4eba\u60ca\u7591\u9707\u64bc\u4e4b\u4e2d\uff0c\u53ea\u89c1\u90a3\u4e2a\u8336\u676f\u5df2\u98de\u5230\u4e86\u5929\u9b54\u6559\u4e3b\u9762\u524d\uff0c\u5929\u9b54\u6559\u4e3b\u628a\u624b\u4e00\u62db\uff0c\u8336 \u676f\u7684\u6765\u52bf\u7f13\u4e86\u4e00\u4e9b\uff0c\u5979\u53cc\u624b\u5e73\u4f38\uff0c\u628a\u676f\u63a5\u4e86\u4e0b\u6765\uff0c\u9053\u58f0\uff1a\u201c\u591a\u8c22\u8d50\u8336\uff01\u201d\u4e00\u53e3\u559d\u5c3d\u3002\u4f46\u5979\u867d \u7136\u63a5\u5f97\u8336\u676f\uff0c\u5374\u5df2\u8dc4\u8dc4\u8e09\u8e09\u7684\u5012\u9000\u4e86\u51e0\u6b65\uff01\u6c14\u7130\u767b\u65f6\u5927\u51cf\uff01 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5c06\u8336\u676f\u4e00\u6454\uff0c\u6c5f\u5357\u4e00\u8dc3\u4e0a\u524d\uff0c\u5c06\u8336\u676f\u63a5\u5230\u624b\u4e2d\uff0c\u8f7b\u8f7b\u653e\u4e0b\u3002\u7b11\u9053\uff1a\u201c\u4f60\u6bd4\u4e0d\u8fc7 \u4eba\u5bb6\uff0c\u4e5f\u4e0d\u5fc5\u52a8\u6012\u5440\uff01\u201d\u5e78\u800c\u5929\u9b54\u6559\u4e3b\u89c1\u90a3\u4eba\u7684\u5185\u529f\u8fdc\u80dc\u81ea\u5df1\uff0c\u4e0d\u6562\u518d\u884c\u5356\u5f04\uff0c\u6d88\u8017\u5185\u529b\uff0c \u8fd9\u4e00\u6454\u53ea\u662f\u56e0\u4e3a\u5fcd\u4e0d\u7740\u6c14\u6124\uff0c\u968f\u624b\u6454\u51fa\u7684\uff0c\u90a3\u4eba\u4e5f\u65e9\u5df2\u770b\u51fa\uff0c\u5979\u6454\u676f\u7528\u7684\u4e0d\u662f\u91cd\u624b\u6cd5\uff0c\u6240\u4ee5 \u653e\u5fc3\u7531\u6c5f\u5357\u53bb\u63a5\uff0c\u8ba9\u6c5f\u5357\u4e58\u673a\u6316\u82e6\u4e86\u5979\u51e0\u53e5\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5f53\u7136\u4e0d\u4f1a\u518d\u7406\u776c\u6c5f\u5357\u3001\u5979\u8336\u676f\u4e00\u6454\uff0c\u55d6\u7684\u4e00\u58f0\uff0c\u4f69\u5251\u7acb\u5373\u51fa\u9798\uff0c\u6307\u7740\u90a3\u6c49\u5b50 \u9053\uff1a\u201c\u9601\u4e0b\u7684\u5185\u529f\u9020\u8be3\u679c\u662f\u4e0d\u51e1\uff0c\u4e0d\u5fc5\u518d\u6bd4\u4e86\u3002\u4e0d\u77e5\u9601\u4e0b\u53ef\u8fd8\u613f\u610f\u66ff\u4ee3\u8d35\u6d3e\u638c\u95e8\uff0c\u8ba9\u6211\u518d\u53e8 \u6559\u4f60\u4eec\u6c13\u5c71\u6d3e\u7684\u51e0\u62db\u5251\u6cd5\u4e48\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u4ecd\u662f\u6728\u7136\u6beb\u65e0\u8868\u60c5\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6559\u4e3b\u4e0d\u5acc\u6211\u8fd9\u4e2a\u65e0\u540d\u5c0f\u5352\u4e0d\u914d\uff0c\u8ba9\u6211\u5f97\u4ee5\u9886\u6559 \u9ad8\u62db\uff0c\u5728\u6211\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u4e3b\u4e0d\u538b\u5ba2\uff0c\u4fbf\u8bf7\u8d50\u62db\u5427\uff01\u201d \u3000\u3000\u53ef\u662f\u90a3\u6c49\u4e8e\u53cc\u624b\u7a7a\u7a7a\uff0c\u968f\u8eab\u4e5f\u6ca1\u6709\u4f69\u5e26\u5200\u5251\u3002\u767d\u82f1\u6770\u9009\u4e86\u4e00\u67c4\u957f\u5251\uff0c\u8d70\u8fc7\u6765\u9053\uff0c\u201c\u5e08 \u5f1f\uff0c\u63a5\u5251\uff01\u201d\u767d\u82f1\u6770\u8fd9\u65f6\u4ea6\u5df2\u9690\u7ea6\u731c\u5230\u6b64\u4eba\u662f\u8c01\uff0c\u4ed6\u8fd9\u4e00\u58f0\u201c\u5e08\u5f1f\u201d\uff0c\u5b9e\u662f\u4e3a\u4e86\u514d\u90a3\u5929\u9b54\u6559 \u4e3b\u8d77\u7591\u7684\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u6446\u4e86\u6446\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6c14\u529b\u4e0d\u591f\uff0c\u7528\u4e0d\u4e86\u8fd9\u6837\u6c89\u91cd\u7684\u957f\u5251\u3002\u6211\u81ea\u5df1\u4f1a\u9009\u5408\u624b\u7684 \u7528\uff0c\u767d\u5e08\u5144\u4e0d\u5fc5\u4e3a\u6211\u64cd\u5fc3\u3002\u201d \u3000\u3000\u6c13\u5c71\u6d3e\u5f1f\u5b50\u4e2d\uff0c\u8def\u82f1\u8c6a\u662f\u4e2a\u6709\u5fc3\u773c\u513f\u7684\u4eba\uff0c\u4e00\u542c\u4ed6\u5982\u6b64\u8bf4\u6cd5\uff0c\u7acb\u5373\u56b7\u9053\uff1a\u201c\u5feb\u628a\u5175\u5668\u67b6 \u63a8\u8fc7\u6765\uff0c\u8ba9\u8fd9\u4f4d\u5e08\u5144\u9009\u62e9\u3002\u201d \u3000\u3000\u8bdd\u97f3\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u90a3\u6c49\u5b50\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u65e0\u987b\u591a\u4e8b\u4e86\uff0c\u6211\u5df2\u627e\u5230\u5408\u624b\u7684\u5175\u5668\u4e86\uff01\u6559\u4e3b\uff0c\u8bf7 \u6055\u5192\u6627\uff0c\u6211\u8981\u5411\u4f60\u501f\u4e00\u6837\u4e1c\u897f\uff01\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5fc3\u5934\u4e00\u51db\uff0c\u53cd\u5251\u4e00\u524a\uff0c\u4f46\u542c\u5f97\u5fae\u98ce\u98d2\u7136\uff0c\u4eba\u5f71\u4e00\u95ea\uff0c\u90a3\u6c49\u4e8e\u5df2\u63a0\u8fc7\u4e86\u5929\u9b54\u6559\u4e3b \u7684\u8eab\u8fb9\uff0c\u8def\u82f1\u8c6a\u5b9a\u775b\u77a7\u65f6\uff0c\u53ea\u89c1\u90a3\u4eba\u7684\u624b\u4e0a\u5df2\u591a\u4e86\u4e00\u4ef6\u4eae\u6676\u6676\u7684\u4e1c\u897f\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u90a3\u5929\u9b54\u6559 \u4e3b\u5df1\u662f\u4e00\u58f0\u6012\u559d\uff1a\u201c\u5c0f\u5b50\u65e0\u793c\uff0c\u6b3a\u6211\u592a\u751a\uff01\u201d\u767b\u65f6\u5251\u5149\u5982\u7ec3\uff0c\u5411\u90a3\u6c49\u5b50\u75be\u4e0b\u6740\u624b\uff01 \u3000\u3000\u8fd9\u65f6\u4f17\u4eba\u65b9\u59cb\u770b\u5f97\u5206\u660e\uff0c\u90a3\u6c49\u5b50\u53cc\u6307\u4e4b\u95f4\u631f\u7740\u7684\uff0c\u4e43\u662f\u4e00\u652f\u4e94\u5bf8\u6765\u957f\u7684\u78a7\u7389\u7c2a\u3002\u4ed6\u5c06\u7389 \u7c2a\u4e00\u6307\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u653e\u5fc3\uff0c\u6211\u4e0d\u4f1a\u635f\u574f\u4f60\u7684\uff0c\u6682\u501f\u4e00\u7528\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u201d\u539f\u6765\u8fd9\u652f\u7389\u7c2a\uff0c\u6b63 \u662f\u4ed6\u4ece\u5929\u9b54\u6559\u4e3b\u7684\u5934\u4e0a\u53d6\u4e0b\u6765\u7684\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7684\u5251\u6cd5\u6709\u5982\u66b4\u98ce\u9aa4\u96e8\uff0c\u5c31\u5728\u90a3\u6c49\u5b50\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5979\u5df2\u63a5\u8fde\u653b\u51fa\u4e86\u516d\u62db\uff0c\u6bcf\u4e00\u62db\u90fd \u662f\u6307\u5411\u5bf9\u65b9\u7684\u8981\u5bb3\u7a74\u9053\uff0c\u53ef\u662f\uff0c\u5947\u602a\u5f97\u5f88\uff0c\u4e5f\u4e0d\u89c1\u4ed6\u600e\u6837\u8eb2\u95ea\uff0c\u5728\u65c1\u4eba\u770b\u6765\uff0c\u5929\u9b54\u6559\u4e3b\u8fd9\u8fde \u73af\u4e03\u5251\uff0c\u6bcf\u4e00\u5251\u90fd\u4f3c\u4e4e\u5df2\u523a\u4e2d\u4e86\u4ed6\u7684\u8eab\u4f53\uff0c\u800c\u4ed6\u5374\u662f\u53d1\u6beb\u65e0\u4f24\uff01\u53ea\u6709\u529f\u529b\u6700\u9ad8\u7684\u51e0\u4e2a\u5927\u5f1f\u5b50 \u624d\u770b\u5f97\u51fa\uff0c\u6bcf\u5f53\u5251\u950b\u5c31\u8981\u6233\u4e2d\u4ed6\u7684\u8eab\u4f53\u4e4b\u65f6\uff0c\u4ed6\u4fbf\u5411\u65c1\u8fb9\u6ed1\u51fa\u5c11\u8bb8\uff0c\u4f46\u4e5f\u8981\u6781\u4e3a\u7ec6\u5fc3\u624d\u770b\u5f97 \u51fa\uff0c\u82e5\u51b7\u773c\u770b\u53bb\uff0c\u4fbf\u53ea\u89c1\u5251\u5149\u4ea4\u53c9\u7a7f\u63d2\uff0c\u5c31\u5982\u540c\u5728\u4ed6\u7684\u8eab\u4e0a\u6233\u8fc7\u4e00\u822c\u3002\u90a3\u6c49\u5b50\u5ffd\u5730\u201c\u566b\u201d\u4e86 \u4e00\u58f0\uff0c\u58f0\u97f3\u4e2d\u4f3c\u4e4e\u5145\u6ee1\u65e0\u9650\u8be7\u610f\uff01\u5c31\u5728\u8fd9\u65f6\uff0c\u90a3\u5929\u9b54\u6559\u4e3b\u4e5f\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u4f3c\u4e4e\u5df1\u8bc6\u5f97\u90a3\u4eba \u7684\u6765\u5386\uff0c\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u2026\u2026\u201d\u5c16\u5ced\u7684\u58f0\u97f3\u5728\u201c\u4f60\u201d\u5b57\u4e0a\u6253\u4e86\u4e00\u4e2a\u76d8\u65cb\uff0c\u5ffd\u5730\u8bdd\u8bed\u4e00\u6536\uff0c\u5251 \u6cd5\u7a81\u53d8\uff0c\u5251\u5149\u8d77\u5904\uff0c\u5c31\u4f3c\u8361\u8d77\u4e00\u5708\u957f\u8679\uff0c\u5c06\u90a3\u6c49\u5b50\u524d\u540e\u5de6\u53f3\u7684\u9000\u8def\u5168\u90fd\u5c01\u4f4f\uff01 \u3000\u3000\u767d\u82f1\u6770\u770b\u5f97\u60ca\u5947\u4e0d\u5df2\uff0c\u539f\u6765\u5728\u5510\u6653\u6f9c\u7684\u5f1f\u5b50\u949f\u5c55\u548c\u4ed6\u7684\u7525\u5973\u674e\u6c81\u6885\u7ed3\u5a5a\u7684\u65f6\u5019\uff0c\u767d\u82f1\u6770 \u662f\u4ee3\u8868\u6c13\u5c71\u6d3e\u524d\u5f80\u89c2\u793c\u7684\u4eba\uff0c\u5728\u90a3\u6b21\u5a5a\u793c\u4e4b\u4e2d\uff0c\u5389\u80dc\u7537\u66fe\u95ef\u6765\u6363\u4e71\uff0c\u4e0e\u5929\u5c71\u638c\u95e8\u5510\u6653\u6f9c\u6bd4\u8bd5 \u5251\u6cd5\uff0c\u767d\u82f1\u6770\u867d\u7136\u4e0d\u662f\u5251\u5b66\u540d\u5bb6\uff0c\u4f46\u5bf9\u5389\u80dc\u7537\u90a3\u624b\u5251\u6cd5\u8fd8\u6709\u4e9b\u5370\u8c61\uff0c\u5982\u4eca\u4ed6\u770b\u4e86\u8fd9\u5929\u9b54\u6559\u4e3b \u7684\u8fde\u73af\u4e03\u5251\u4e0d\u89c9\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4f3c\u66fe\u76f8\u8bc6\uff0c\u518d\u770b\u4e86\u5979\u8fd9\u4e00\u62db\u201c\u795e\u5149\u79bb\u5408\u201d\uff0c\u66f4\u662f\u53ef\u4ee5\u786e\u7136\u80af\u5b9a\uff1a \u90a3\u5c31\u662f\u5389\u80dc\u7537\u6240\u7528\u8fc7\u7684\u90a3\u5957\u5251\u6cd5\uff01\u4ed6\u8fd8\u8bb0\u5f97\uff0c\u5f53\u65f6\u5728\u5389\u80dc\u7537\u7528\u5230\u8fd9\u62db\u5251\u6cd5\u7684\u65f6\u5019\uff0c\u5510\u6653\u6f9c\u4e5f \u66fe\u8d5e\u8fc7\u4e00\u4e2a\u201c\u597d\u201d\uff01 \u3000\u3000\u8fd9\u6c49\u5b50\u672a\u66fe\u8d5e\u597d\uff0c\u4f46\u5374\u53c8\u5fae\u5fae\u7684\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u4f3c\u4e4e\u7a81\u7136\u5446\u4e86\u4e00\u5446\uff0c\u5929\u9b54\u6559\u4e3b\u8fd9\u4e00\u62db \u201c\u795e\u5149\u79bb\u5408\u201d\u4f55\u7b49\u5389\u5bb3\uff0c\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u90a3\u9053\u5149\u73af\u5df1\u5411\u4ed6\u5934\u9876\u7f69\u4e0b\u3002 \u3000\u3000\u597d\u4e9b\u6c13\u5c71\u5f1f\u5b50\u7981\u4e0d\u4f4f\u5931\u58f0\u60ca\u547c\uff0c\u53ef\u662f\uff0c\u5c31\u5728\u4ed6\u4eec\u60ca\u53eb\u58f0\u4e2d\uff0c\u53ea\u89c1\u90a3\u6c49\u5b50\u5c06\u7389\u7c2a\u4e00\u6307\uff0c\u4e00 \u7ebf\u78a7\u83b9\u83b9\u7684\u5149\u534e\uff0c\u7a81\u7136\u4ece\u5343\u91cd\u5251\u6c14\u4e2d\u900f\u51fa\uff0c\u523a\u5411\u5929\u9b54\u6559\u4e3b\u7684\u9762\u4e0a\u53cc\u775b\uff01 \u3000\u3000\u55a7\u54d7\u547c\u53eb\u4e4b\u58f0\u987f\u7136\u505c\u606f\uff0c\u8fd9\u65f6\u4e0d\u53ea\u662f\u767d\u82f1\u6770\uff0c\u6c13\u5c71\u6d3e\u7684\u5176\u4ed6\u5f1f\u5b50\u4e5f\u90fd\u60ca\u5f97\u5446\u4e86\uff0c\u4e0d\u4f46\u56e0 \u4e3a\u90a3\u6c49\u5b50\u7528\u7389\u7c2a\u4f7f\u51fa\u5251\u6cd5\uff0c\u5965\u5999\u795e\u5947\uff0c\u800c\u4e14\u56e0\u4e3a\u4ed6\u6240\u7528\u7684\u8fd9\u4e00\u62db\uff0c\u6b63\u662f\u6c13\u5c71\u6d3e\u7956\u5e08\u72ec\u81c2\u795e\u5c3c \u6240\u521b\u7684\u2014\u2014\u201c\u7384\u5973\u5251\u6cd5\u201d\u4e2d\u7684\u4e00\u62db\u201c\u7389\u5973\u7a7f\u9488\u201d\u3002 \u3000\u3000\u7384\u5973\u5251\u6cd5\u4e43\u662f\u72ec\u81c2\u795e\u5c3c\u91c7\u81ea\u53e4\u8c31\uff0c\u518d\u522b\u51fa\u5fc3\u88c1\uff0c\u6240\u521b\u9020\u7684\u4e00\u5957\u9002\u5b9c\u4e8e\u5973\u5b50\u6240\u5b66\u7684\u5251\u6cd5\uff0c \u56e0\u4e3a\u5973\u5b50\u6c14\u529b\u8f83\u5f31\uff0c\u6240\u4ee5\u7384\u5973\u5251\u6cd5\u4e0d\u4ee5\u521a\u731b\u89c1\u957f\uff0c\u4f46\u5374\u6781\u5f97\u8f7b\u7075\u7fd4\u52a8\u4e4b\u5999\uff0c\u6c13\u5c71\u6d3e\u4e2d\u5b66\u8fd9\u5957 \u5251\u6cd5\u7684\u5341\u4e5d\u90fd\u662f\u5973\u5f1f\u5b50\uff0c\u800c\u4ee5\u8c37\u4e4b\u534e\u7684\u9020\u8be3\u6700\u6df1\u3002 \u3000\u3000\u5982\u4eca\u8fd9\u6c49\u5b50\u7528\u7389\u7c2a\u4f7f\u51fa\u7384\u5973\u5251\u6cd5\uff0c\u6b63\u5408\u4e4e\u8fd9\u5957\u5251\u6cd5\u7684\u8def\u6570\uff0c\u59ff\u52bf\u7f8e\u5999\u4e4b\u6781\uff0c\u5f53\u771f\u5c31\u6709\u5982 \u5973\u5b50\u7ee3\u82b1\uff0c\u7a7f\u9488\u5f15\u7ebf\u4e00\u822c\uff0c\u7cbe\u5999\u4e4b\u5904\uff0c\u96be\u4ee5\u8a00\u5ba3\uff0c\u83ab\u8bf4\u6c13\u5c71\u6d3e\u7684\u7537\u5f1f\u5b50\u77a0\u76ee\u7ed3\u820c\uff0c\u6240\u6709\u7684\u5973 \u5f1f\u5b50\u4e5f\u90fd\u81ea\u6127\u4e0d\u5982\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5373\u4f7f\u662f\u7531\u8c37\u638c\u95e8\u4f7f\u51fa\u6b64\u62db\uff0c\u6700\u591a\u4e5f\u4e0d\u8fc7\u5982\u6b64\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u5fc3\u5f26\u98a4\u6296\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u4e00\u5b9a\u662f\u4ed6\uff0c\u4e00\u5b9a\u662f\u4ed6\u4e86\uff01\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5929\u9b54\u6559\u4e3b\u8499\u9762\u7684\u90a3\u5e45\u8f7b\u7eb1\uff0c\u5df2\u7ed9\u7389\u7c2a\u6311\u4e0b\uff01 \u3000\u3000\u987f\u7136\u95f4\uff0c\u4f17\u4eba\u7684\u773c\u775b\u4e00\u4eae\uff0c\u4f46\u89c1\u8fd9\u5929\u9b54\u6559\u4e3b\u7389\u8c8c\u82b1\u5bb9\uff0c\u80a4\u5149\u5982\u96ea\uff01\u9762\u90e8\u7684\u8f6e\u5eca\u867d\u6709\u51e0\u5206 \u4f3c\u90a3\u7f2a\u592b\u4eba\uff0c\u4f46\u5374\u4e0d\u77e5\u8981\u6bd4\u90a3\u7f2a\u592b\u4eba\u7f8e\u6570\u5341\u767e\u500d\uff01 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5929\u9b54\u6559\u4e3b\u4e00\u58f0\u53eb\u9053\uff1a\u201c\u597d\u5440\uff0c\u91d1\u4e16\u9057\uff0c\u4f60\u66ff\u4f60\u5fc3\u4e0a\u4eba\u6491\u8170\uff0c\u5e72\u8106\u628a\u6211\u6740\u4e86 \u5427\uff01\u201d \u3000\u3000\u90a3\u6c49\u5b50\u5446\u4e86\u4e00\u5446\uff0c\u53eb\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u662f\u5389\u59d1\u5a18\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u6012\u9053\uff1a\u201c\u4ec0\u4e48\u5389\u59d1\u5a18\uff1f\u4f60\u8fde\u59bb\u5b50\u4e5f\u4e0d\u8ba4\u4e86\u4e48\uff1f\u5389\u7956\u5e08\uff0c\u4f60\u6b7b\u5f97\u597d\u51a4\u6789\u5440\uff01\u201d \u3000\u3000\u90a3\u6c49\u5b50\u53eb\u9053\uff1a\u201c\u5582\uff0c\u5582\uff0c\u6211\u6709\u8bdd\u548c\u4f60\u8bf4\uff01\u201d\u5929\u9b54\u6559\u4e3b\u63a9\u9762\u800c\u6ce3\uff0c\u5df2\u81ea\u98de\u8eab\u62a2\u51fa\u5927\u95e8\uff0c\u90ca \u5c71\u6d3e\u5f1f\u5b50\u77e5\u9053\u662f\u91d1\u4e16\u9057\uff0c\u6bcf\u4e00\u4e2a\u4eba\u7684\u6ce8\u610f\u529b\u90fd\u653e\u5728\u91d1\u4e16\u9057\u8eab\u4e0a\uff0c\u8c01\u90fd\u6ca1\u6709\u62e6\u963b\u5979\u3002\u7728\u773c\u4e4b \u95f4\uff0c\u5df2\u542c\u5f97\u5929\u9b54\u6559\u4e3b\u7684\u58f0\u97f3\u5728\u5916\u9762\u8bf4\u9053\uff1a\u201c\u8981\u5417\uff0c\u4f60\u5c31\u6740\u6211\uff0c\u6211\u4e0d\u548c\u4f60\u8fd9\u8d1f\u5fc3\u6c49\u5b50\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u90a3\u6c49\u5b50\u8e0c\u8e87\u7247\u523b\uff0c\u4ed6\u770b\u4e86\u8c37\u4e4b\u534e\u4e00\u773c\uff0c\u4f3c\u4e4e\u5728\u611f\u5230\u53d6\u820d\u4e24\u96be\uff0c\u5374\u5ffd\u5730\u5927\u53eb\u4e00\u58f0\uff0c\u5f84\u5411\u95e8 \u5916\u8ffd\u53bb\uff0c\u5e76\u4e14\u558a\u9053\uff1a\u201c\u5582\u3001\u5582\uff01\u4f60\u8ba4\u9519\u4e86\u4eba\u5566\uff01\u4f60\u7684\u78a7\u7389\u7c2a\u6211\u8fd8\u7ed9\u4f60\uff01\u4f60\u542c\u6211\u8bf4\u5440\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u82b3\u5fc3\u5927\u75bc\uff0c\u91d1\u4e16\u9057\u7adf\u7136\u4e0d\u987e\u5979\u800c\u53bb\uff0c\u770b\u6765\u4ed6\u628a\u6b7b\u53bb\u7684\u5389\u80dc\u7537\u770b\u5f97\u6bd4\u81ea\u5df1\u8fd8\u91cd\u8981\uff0c \u53ea\u56e0\u4e3a\u8fd9\u5929\u9b54\u6559\u4e3b\u5949\u5389\u80dc\u7537\u4e3a\u7956\u5e08\uff0c\u4ed6\u5c31\u8981\u5411\u5979\u6c42\u9976\u8d54\u7f6a\uff01 \u3000\u3000\u6c13\u5c71\u6d3e\u5f1f\u5b50\u90fd\u6124\u6124\u4e0d\u5e73\uff0c\u8c01\u90fd\u4e0d\u53bb\u8ffd\u4ed6\uff0c\u53ea\u6709\u6c5f\u5357\u5927\u53eb\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u91d1\u5927\u4fa0\uff01\u56de\u6765 \u5440\uff01\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u6709\u4e2a\u723d\u6717\u7684\u58f0\u97f3\u7b11\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u6211\u4e0d\u662f\u5df2\u5728\u8fd9\u91cc\u4e86\u5417\uff1f\u201d\u968f\u5373\u542c\u5f97 \u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u6c49\u5b50\u7ed9\u9707\u5f97\u76f4\u9000\u56de\u6765\uff0c\u6709\u4e00\u4e2a\u4eba\u5982\u5f71\u968f\u5f62\u7684\u8ddf\u5728\u4ed6\u7684\u80cc\u540e\uff01 \u3000\u3000\u6c5f\u5357\u7741\u5927\u773c\u775b\uff0c\u5446\u82e5\u6728\u9e21\uff0c\u4ed6\u89c1\u5230\u4e86\u91d1\u4e16\u9057\uff0c\u53cd\u800c\u53eb\u4e0d\u51fa\u6765\u4e86\u3002\u539f\u6765\u8ddf\u7740\u90a3\u6c49\u5b50\u8fdb\u6765\u7684 \u4eba\u624d\u662f\u91d1\u4e16\u9057\uff01 \u3000\u3000\u8fd9\u771f\u662f\u5927\u5927\u51fa\u4e4e\u4f17\u4eba\u610f\u5916\u7684\u4e8b\u60c5\uff0c\u5728\u6b64\u4e4b\u524d\uff0c\u8c01\u90fd\u4ee5\u4e3a\u90a3\u6c49\u5b50\u5c31\u662f\u91d1\u4e16\u9057\uff0c\u54ea\u77e5\u7adf\u7136\u4e0d \u662f\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u7b11\u9053\uff1a\u201c\u91d1\u4e16\u9057\uff0c\u4f60\u6765\u4e86\u90a3\u5c31\u8be5\u8ba9\u6211\u8d70\u4e86\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u5374\u62e6\u4f4f\u4e86\u4ed6\uff0c\u8d28\u95ee\u4ed6\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u522b\u7684\u4e5f\u8fd8\u7f62\u4e86\uff0c\u4f60\u4e3a\u4ec0\u4e48\u5047\u5192\u6211\u7684\u540d\u5934\uff0c \u5077\u4e0a\u6c13\u5c71\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u6012\u9053\uff1a\u201c\u7b11\u8bdd\uff0c\u4f60\u91d1\u4e16\u9057\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\uff0c\u6211\u59d3\u6587\u7684\u8981\u5192\u4f60\u7684\u540d\u5934\uff1f\u4f60\u95ee\u95ee\u4ed6\u4eec \u770b\uff0c\u6211\u5192\u4e86\u4f60\u7684\u540d\u5934\u6ca1\u6709\uff1f\u6211\u5e2e\u4e86\u4f60\u7684\u670b\u53cb\u7684\u5927\u5fd9\uff0c\u4f60\u4e0d\u9053\u8c22\u4e5f\u8fd8\u7f62\u4e86\uff0c\u5c45\u7136\u8fd8\u51fa\u53e3\u4f24\u4eba\uff1f\u201d \u3000\u3000\u6c5f\u5357\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4ed6\u8bf4\u7684\u8bdd\u5012\u662f\u771f\u7684\uff0c\u8981\u4e0d\u662f\u4ed6\uff0c\u54b1\u4eec\u53ef\u90fd\u6253\u4e0d\u8fc7\u90a3\u4e2a\u5929\u9b54\u6559\u4e3b\u3002 \u4ed6\u4e5f\u6ca1\u6709\u8bf4\u8fc7\u4ed6\u662f\u8c01\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u6446\u4e86\u6446\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u4f60\u54ea\u91cc\u77e5\u9053\u4ed6\u7684\u9634\u72e0\u5389\u5bb3\uff01\u54fc\uff0c\u4f60\u5f53\u6211\u4e0d\u77e5\u9053\u4f60\u7684\u7528 \u610f\u5417\uff1f\u770b\u5251\uff01\u201d\u540e\u9762\u8fd9\u4e24\u53e5\u662f\u51b2\u7740\u90a3\u6c49\u5b50\u8bf4\u7684\uff0c\u8bdd\u58f0\u672a\u4e86\uff0c\u4f46\u89c1\u5251\u5149\u8000\u773c\uff0c\u5df2\u523a\u5230\u4e86\u90a3\u6c49\u5b50 \u7684\u9762\u95e8\uff01 \u3000\u3000\u90a3\u6c49\u5b50\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u91d1\u4e16\u9057\uff0c\u4f60\u6253\u4e0d\u8fc7\u6211\uff0c\u8981\u501f\u52a9\u5b9d\u5251\u4e4b\u529b\u4e48\uff1f\u597d\u5427\uff0c\u4f60\u4e0d\u6015\u5929\u4e0b\u82f1 \u96c4\u803b\u7b11\uff0c\u5c3d\u7ba1\u523a\u5427\uff01\u201d \u3000\u3000\u90a3\u6c49\u5b50\u53cc\u624b\u7a7a\u7a7a\uff0c\u91d1\u4e16\u9057\u7528\u7684\u5374\u662f\u5389\u80dc\u7537\u9057\u7559\u7ed9\u4ed6\u7684\u90a3\u628a\u88c1\u4e91\u5b9d\u5251\uff0c\u91d1\u4e16\u9057\u8fd9\u4e00\u62d4\u5251\uff0c \u8fde\u7a0b\u6d69\u3001\u767d\u82f1\u6770\u7b49\u4eba\u90fd\u89c9\u5f97\u6709\u4e9b\u8fc7\u4efd\uff0c\u5fc3\u91cc\u4ff1\u662f\u60f3\u9053\uff1a\u201c\u4ee5\u91d1\u4e16\u9057\u7684\u6b66\u529f\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u8fd8\u6709 \u8c01\u662f\u4ed6\u7684\u654c\u624b\uff1f\u4e3a\u4f55\u5374\u8fd8\u8981\u4e0d\u987e\u8eab\u4efd\uff0c\u7528\u5b9d\u5251\u6765\u5bf9\u4ed8\u8d64\u638c\u7a7a\u62f3\uff01\u201d \u3000\u3000\u4f17\u4eba\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u4ed3\u7136\u58f0\u54cd\uff0c\u5982\u88c2\u539a\u9769\uff0c\u91d1\u4e16\u9057\u5df2\u628a\u90a3\u88c1\u4e91\u5251\u63b7\u51fa\uff0c\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u4f60\u7684\u9762\u76ae\u5927\u539a\uff0c\u6211\u662f\u8981\u5212\u7834\u4f60\u7684\u9762\u76ae\uff0c\u8ba9\u5927\u5bb6\u77a7\u77a7\uff0c\u4e5f\u597d\u8ba9\u4ed6\u4eec\u8ba4\u8bc6\u4f60\uff0c\u4ee5\u540e\u4e5f\u6709\u4e2a\u63d0 \u9632\uff01\u201d \u3000\u3000\u5374\u539f\u6765\u91d1\u4e16\u9057\u8fd9\u4e00\u5251\u5e76\u6ca1\u6709\u4f24\u53ca\u90a3\u6c49\u5b50\u5206\u6beb\uff0c\u53ea\u4e0d\u8fc7\u5212\u7834\u4e86\u4ed6\u7684\u9762\u5177\u3002 \u3000\u3000\u6c5f\u5357\u731b\u5730\u4e00\u6014\uff0c\u8fd9\u6c49\u5b50\u7684\u76f8\u8c8c\u5f88\u50cf\u4ed6\u8ba4\u8bc6\u7684\u4e00\u4e2a\u4eba\uff0c\u518d\u4e00\u60f3\u4fbf\u60f3\u8d77\u6765\u4e86\uff0c\u662f\u50cf\u90a3\u4e2a\u66ff\u548c \u7837\u62bc\u8fd0\u73e0\u5b9d\uff0c\u4e2d\u9014\u9047\u76d7\u7684\u90a3\u6587\u516c\u5b50\u3002\u4e0d\u8fc7\u8fd9\u6c49\u5b50\u5e74\u7eaa\u8f83\u5927\uff0c\u988a\u4e0b\u6bd4\u90a3\u6587\u516c\u5b50\u591a\u4e86\u51e0\u6839\u987b\u5b50\u3002 \u3000\u3000\u8fd9\u6c49\u5b50\u7d20\u6765\u77e5\u9053\u91d1\u4e16\u9057\u662f\u4e2a\u81ea\u8d1f\u7684\u4eba\uff0c\u6240\u4ee5\u624d\u6562\u4f46\u7136\u65e0\u60e7\u7684\u4ee5\u8d64\u624b\u7a7a\u62f3\u9762\u5bf9\u4ed6\u7684\u5b9d\u5251\u3002 \u4ed6\u662f\u6599\u51c6\u4e86\u91d1\u4e16\u9057\u4e0d\u4f1a\u7528\u5b9d\u5251\u523a\u6765\u7684\uff0c\u5374\u60f3\u4e0d\u5230\u91d1\u4e16\u9057\u548c\u4ed6\u5f00\u4e86\u4e00\u4e2a\u4e0d\u5927\u4e0d\u5c0f\u7684\u73a9\u7b11\uff0c\u5e76\u672a \u4f24\u4ed6\uff0c\u5374\u5212\u7834\u4e86\u4ed6\u7684\u9762\u5177\u3002\u4ed6\u521a\u624d\u7528\u7389\u7c2a\u6311\u5f00\u5929\u9b54\u6559\u4e3b\u7684\u8499\u9762\u8f7b\u7eb1\uff0c\u5982\u4eca\u5374\u7ed9\u91d1\u4e16\u9057\u4ee5\u5176\u4eba \u4e4b\u9053\uff0c\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\uff0c\u4e5f\u63ed\u5f00\u4e86\u4ed6\u7684\u5e90\u5c71\u771f\u9762\uff0c\u4e0d\u7531\u5f97\u52c3\u7136\u5927\u6012\uff01 \u3000\u3000\u8fd9\u6c49\u5b50\u9762\u5982\u51a0\u7389\uff0c\u4e3e\u6b62\u6e29\u6587\uff0c\u770b\u6765\u5012\u4f3c\u4e2a\u5112\u96c5\u7684\u4e66\u751f\uff0c\u6240\u4ee5\u5f53\u4ed6\u73b0\u51fa\u4e86\u5e90\u5c71\u771f\u9762\u4e4b\u540e\uff0c \u8fde\u6c5f\u5357\u4e5f\u5bf9\u4ed6\u6709\u51e0\u5206\u597d\u611f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u6ca1\u5e26\u534a\u5206\u90aa\u6c14\uff0c\u600e\u7684\u91d1\u5927\u4fa0\u5374\u628a\u4ed6\u5f53\u4f5c\u574f\u4eba\uff1f\u201d \u3000\u3000\u6c5f\u5357\u6b63\u81ea\u5fc3\u91cc\u5600\u5495\uff0c\u5ffd\u89c1\u4ed6\u53cc\u773c\u4e00\u7741\uff0c\u6012\u6c14\u52c3\u53d1\uff0c\u5239\u65f6\u95f4\uff0c\u4ed6\u90a3\u4fca\u96c5\u7684\u4eea\u5bb9\u4e5f\u90fd\u53d8\u4e86\uff0c \u8138\u4e0a\u7f69\u7740\u4e00\u5c42\u9752\u6c14\uff0c\u4e24\u9053\u773c\u5149\u6709\u8bf4\u4e0d\u51fa\u7684\u72e0\u6bd2\uff0c\u4ed6\u6b63\u9762\u5bf9\u7740\u91d1\u4e16\u9057\uff0c\u4f46\u5728\u4ed6\u56db\u5468\u7684\u4eba\u4eec\uff0c\u4e5f \u90fd\u611f\u5230\u4ed6\u773c\u5149\u7684\u5a01\u80c1\uff0c\u6c5f\u5357\u66f4\u662f\u4e0d\u7531\u81ea\u5df1\u7684\u6253\u4e86\u4e00\u4e2a\u5bd2\u6218\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u6c49\u5b50\u9634\u607b\u607b\u5730\u7b11\u9053\uff1a\u201c\u91d1\u4e16\u9057\uff0c\u5f80\u6614\u4f60\u6765\u5230\u6211\u7684\u672a\u540d\u5c9b\uff0c\u6211\u603b\u7b97\u5c06\u4f60\u5f53\u4f5c\u5ba2\u4eba \u62db\u5f85\u4e86\uff1b\u5982\u4eca\u6211\u6765\u5230\u6c13\u5c71\uff0c\u4f60\u4e5f\u7b97\u5f97\u662f\u534a\u4e2a\u4e3b\u4eba\uff0c\u5c82\u53ef\u5982\u6b64\u65e0\u793c\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u5251\u7709\u5012\u7ad6\uff0c\u4e5f\u51b7\u7b11\u9053\uff1a\u201c\u6587\u5c9b\u4e3b\uff0c\u6211\u6b63\u8981\u62a5\u7b54\u4f60\u90a3\u756a\u9686\u60c5\uff0c\u52a8\u624b\u5427\uff01\u201d \u3000\u3000\u4f17\u4eba\u542c\u4ed6\u4eec\u7684\u5bf9\u7b54\uff0c\u90fd\u89c9\u5f97\u6709\u70b9\u83ab\u540d\u5176\u5999\uff0c\u5fc3\u4e2d\u5747\u662f\u60f3\u9053\uff1a\u201c\u65e2\u7136\u90a3\u6c49\u5b50\u4e0e\u91d1\u4e16\u9057\u6709\u8fc7 \u4e3b\u5ba2\u4e4b\u60c5\uff0c\u4f55\u4ee5\u91d1\u4e16\u9057\u53c8\u7a81\u7136\u4e0e\u4ed6\u52a8\u624b\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u65e2\u627f\u9080\u8bf7\uff0c\u6562\u4e0d\u5949\u966a\uff1f\u6211\u6b63\u8981\u770b\u4f60\u4e09\u5e74\u6765\u8fdb\u5883\u5982\u4f55\uff1f\u201d\u8bdd\u58f0\u4e00\u6536\uff0c\u500f\u5730\u4fbf \u5411\u91d1\u4e16\u9057\u6251\u53bb\uff01 \u3000\u3000\u4f17\u4eba\u4f46\u89c9\u773c\u775b\u4e00\u82b1\uff0c\u9661\u7136\u95f4\u53ea\u89c1\u56db\u9762\u516b\u65b9\u90fd\u662f\u90a3\u4eba\u7684\u5f71\u5b50\uff0c\u8c61\u4f1a\u9b54\u6cd5\u4f3c\u7684\uff0c\u5e7b\u51fa\u65e0\u6570\u5316 \u8eab\uff0c\u4ece\u5404\u4e2a\u4e0d\u540c\u7684\u65b9\u4f4d\uff0c\u5411\u91d1\u4e16\u9057\u5c55\u5f00\u653b\u51fb\uff01 \u3000\u3000\u8f7b\u529f\u7edd\u9876\u7684\u9ad8\u660e\u4e4b\u58eb\uff0c\u5728\u5c55\u5f00\u4e86\u6700\u8fc5\u6377\u7684\u8eab\u6cd5\u65f6\uff0c\u53ef\u80fd\u4f1a\u4ee5\u8fd9\u79cd\u5316\u8eab\u5e7b\u5f71\u7684\u73b0\u8c61\uff0c\u4f46\u8fd9 \u79cd\u901f\u5ea6\u662f\u8981\u8fde\u7eed\u7684\u52a8\u4f5c\u4e2d\u624d\u4f1a\u505a\u5230\u7684\uff0c\u5982\u4eca\u8fd9\u6c49\u5b50\u5374\u662f\u8eab\u5f62\u4e00\u6643\uff0c\u4e00\u51fa\u624b\u4fbf\u5e7b\u51fa\u65e0\u6570\u5316\u8eab\uff0c \u90a3\u771f\u662f\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\uff01 \u3000\u3000\u773c\u82b1\u7f2d\u4e71\u4e2d\uff0c\u53ea\u542c\u5f97\u91d1\u4e16\u9057\u4e00\u58f0\u957f\u5578\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u516b\u5366\u5947\u95e8\u6b65\u6cd5\uff0c\u679c\u662f\u9ad8\u660e\uff0c\u4f46\u6211\u8fd8 \u8981\u770b\u770b\u4f60\u7684\u771f\u624d\u5b9e\u5b66\uff01\u201d\u4f46\u89c1\u4ed6\u5728\u65e0\u6570\u5f71\u5b50\u7684\u653b\u51fb\u4e0b\u53cc\u638c\u4e00\u63a8\uff0c\u500f\u7136\u95f4\u90a3\u4e9b\u5982\u771f\u4f3c\u5e7b\u7684\u5f71\u5b50 \u5168\u90e8\u6d88\u5931\uff0c\u90a3\u6c49\u5b50\u201c\u54fc\u201d\u7684\u4e00\u58f0\uff0c\u5df2\u81ea\u79bb\u5f00\u4e86\u91d1\u4e16\u9057\u4e00\u4e08\u6709\u591a\uff0c\u5982\u540c\u96d5\u5851\u4e00\u822c\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002 \u91d1\u4e16\u9057\u4e5f\u5b9a\u4e86\u773c\u775b\uff0c\u5168\u795e\u8d2f\u6ce8\u7684\u76ef\u7740\u4ed6\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u7684\u80cc\u5fc3\u6709\u4e2a\u638c\u5370\uff0c\u91d1\u4e16\u9057\u7684\u8863\u886b\u4e5f\u7ed9\u4ed6\u6495\u70c2\u4e86\u51e0\u6761\u3002\u8fc7\u4e86\u534a\u664c\uff0c\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u4f60 \u6709\u91d1\u521a\u4e0d\u574f\u795e\u529f\uff0c\u6211\u4e5f\u6709\u4e09\u8c61\u5f52\u5143\u795e\u529f\uff0c\u54b1\u4eec\u518d\u6bd4\u4e00\u638c\uff01\u201d\u8bf4\u7f62\uff0c\u7f13\u7f13\u5730\u62ac\u8d77\u624b\u6765\uff0c\u82e5\u4e0d\u7ecf \u610f\u7684\u5411\u91d1\u4e16\u9057\u8f7b\u8f7b\u53d1\u51fa\u4e00\u638c\u3002 \u3000\u3000\u6c13\u5c71\u6d3e\u5f1f\u5b50\u5927\u90fd\u542c\u4e0d\u61c2\u8fd9\u6c49\u5b50\u7684\u8bf4\u8bdd\uff0c\u8c37\u4e4b\u534e\u542c\u4e86\uff0c\u5374\u662f\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u539f\u6765\u201c\u4e09\u8c61\u5f52\u5143\u201d\u4e43\u662f\u6b66\u5b66\u4e2d\u7684\u4e0a\u4e58\u5883\u754c\uff0c\u6307\u7cbe\u3001\u6c14\u3001\u795e\u51e0\u90e8\u53ef\u4ee5\u7ec3\u5f97\u5408\u800c\u4e3a\u4e00\uff0c\u636e\u8c37 \u4e4b\u534e\u6240\u77e5\uff0c\u5979\u7684\u5e08\u7236\u5415\u56db\u5a18\u751f\u524d\u66fe\u7ec3\u5230\u8fd9\u4e2a\u5883\u754c\uff0c\u81f3\u4e8e\u5f53\u4eca\u4e4b\u4e16\uff0c\u5219\u53ea\u6015\u53ea\u6709\u5510\u6653\u6f9c\u4e00\u4eba\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u6c49\u5b50\u8bf4\u8bdd\u7684\u65f6\u95f4\uff0c\u4ed6\u80cc\u4e0a\u7684\u90a3\u4e2a\u638c\u5370\u5df2\u7136\u6d88\u5931\u3002\u8c37\u4e4b\u534e\u66f4\u662f\u5403\u60ca\uff0c\u8981\u77e5\u91d1\u4e16\u9057\u521a \u624d\u51fb\u90a3\u6c49\u5b50\u4e00\u638c\uff0c\u65c1\u4eba\u770b\u4e0d\u51fa\u6765\uff0c\u8c37\u4e4b\u534e\u5374\u662f\u8bc6\u5f97\u7684\uff0c\u91d1\u4e16\u9057\u7528\u7684\u662f\u91d1\u521a\u638c\u91cd\u624b\u6cd5\uff0c\u8c37\u4e4b\u534e \u4e5f\u770b\u51fa\u90a3\u6c49\u5b50\u7a0d\u7a0d\u53d7\u4e86\u5185\u4f24\uff0c\u4f46\u5982\u4eca\u770b\u8fd9\u60c5\u5f62\uff0c\u4ed6\u6240\u53d7\u7684\u5185\u4f24\u5df2\u662f\u8fc5\u901f\u7ed9\u4ed6\u8fd0\u6c14\u6cbb\u6108\u3002\u80fd\u786c \u63a5\u91d1\u4e16\u9057\u8fd9\u4e00\u638c\uff0c\u5df2\u662f\u975e\u5e38\u96be\u80fd\u4e4b\u4e8b\uff0c\u800c\u4ed6\u8fd8\u80fd\u8fc5\u901f\u7597\u4f24\uff0c\u663e\u89c1\u529f\u529b\u4e4b\u9ad8\uff0c\u4e0e\u91d1\u4e16\u9057\u5b9e\u662f\u4e0d \u76f8\u4e0a\u4e0b\uff01 \u3000\u3000\u8fd9\u65f6\u4ed6\u7f13\u7f13\u5730\u62ac\u8d77\u638c\u6765\uff0c\u770b\u6765\u4f3c\u662f\u6f2b\u4e0d\u7ecf\u610f\u7684\u8f7b\u8f7b\u62cd\u51fa\uff0c\u4e1d\u6beb\u6ca1\u6709\u638c\u98ce\uff0c\u53ef\u662f\u5c31\u5728\u8fd9 \u65f6\uff0c\u56db\u5468\u56f4\u7684\u4eba\u5374\u7a81\u7136\u611f\u5230\u4e00\u80a1\u6f5c\u529b\u5411\u4ed6\u4eec\u538b\u6765\uff0c\u4eba\u4eba\u90fd\u662f\u8eab\u4e0d\u7531\u5df1\u7684\u5411\u540e\u76f4\u9000\uff0c\u6709\u51e0\u4e2a\u529f \u529b\u8f83\u5f31\u7684\u6c13\u5c71\u6d3e\u5f1f\u5b50\u8fd8\u8dcc\u5012\u5730\u4e0a\uff0c\u8f6c\u773c\u95f4\uff0c\u5728\u91d1\u4e16\u9057\u4e0e\u90a3\u6c49\u5b50\u7684\u65b9\u5706\u4e09\u4e08\u4e4b\u5185\uff0c\u5df2\u7ecf\u65e0\u4e00\u4eba \u7acb\u5f97\u4f4f\u8db3\uff01 \u3000\u3000\u4ed6\u7684\u638c\u529b\u662f\u5411\u7740\u91d1\u4e16\u9057\u6b63\u9762\u653b\u51fb\u7684\uff0c\u56db\u5468\u56f4\u7684\u65c1\u89c2\u8005\u5df2\u611f\u53d7\u5230\u5982\u6b64\u538b\u529b\uff0c\u6b63\u9762\u7684\u91d1\u4e16\u9057 \u53ef\u60f3\u800c\u77e5\uff0c\u4f46\u91d1\u4e16\u9057\u4ecd\u662f\u5140\u7acb\u5982\u5c71\uff0c\u7eb9\u4e1d\u4e0d\u52a8\u3002\u8c37\u4e4b\u534e\u548c\u6c5f\u5357\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5f85\u5bf9\u65b9\u7684\u638c\u529b\u53d1\u5c3d\uff0c\u65b9\u59cb\u7b11\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u4e14\u5f85\u6211\u4e5f\u8bd5\u8bd5\u4f60\u7684\u4e09\u8c61\u5f52 \u5143\uff0c\u8fbe\u5230\u4f55\u4f55\u5883\u754c\uff1f\u201d\u4ed6\u5c0f\u81c2\u5212\u4e86\u4e00\u4e2a\u5708\u5f27\uff0c\u4e5f\u662f\u8f7b\u8f7b\u7684\u4e00\u638c\u63a8\u51fa\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u4e5f\u662f\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u53ef\u662f\u4ed6\u7684\u8863\u88f3\u5374\u50cf\u88ab\u6625\u98ce\u5439\u76b1\u4e86\u7684\u6e56\u6c34\u4e00\u822c\uff0c\u8361\u8d77\u4e86\u4e00\u5708\u5708\u7684\u6ce2 \u7eb9\uff0c\u4f17\u4eba\u867d\u7136\u4e0d\u61c2\u5176\u4e2d\u5965\u5999\uff0c\u89c1\u6b64\u60c5\u5f62\uff0c\u4e5f\u77e5\u9053\u662f\u91d1\u4e16\u9057\u7684\u5185\u529f\u7a0d\u80dc\u4e00\u7b79\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u9762\u8272\u4e00\u53d8\uff0c\u5ffd\u5730\u8eab\u5f62\u75be\u8d77\uff0c\u7bad\u4e00\u822c\u5730\u98de\u5c04\u51fa\u53bb\uff0c\u56b7\u9053\uff1a\u201c\u9752\u5c71\u7eff\u6c34\uff0c\u540e\u4f1a\u6709\u671f\u3002 \u6587\u67d0\u4e09\u5bf8\u6c14\u5728\uff0c\u603b\u8fd8\u8981\u5411\u9601\u4e0b\u9886\u6559\uff01\u201d\u8bf4\u5230\u201c\u9886\u6559\u201d\u8fd9\u4e24\u4e2a\u5b57\uff0c\u58f0\u97f3\u5df2\u4f3c\u4ece\u5f88\u9065\u8fdc\u7684\u5730\u65b9\u4f20 \u6765\u3002 \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60\u4ec0\u4e48\u65f6\u5019\u6765\uff0c\u6211\u4fbf\u4ec0\u4e48\u65f6\u5019\u5949\u966a\uff01\u201d\u4ed6\u8bf4\u5b8c\u4e4b\u540e\uff0c\u6574\u4e86\u6574\u8863\u886b\uff0c\u8fd9\u624d\u4e0e \u8c37\u4e4b\u534e\u65bd\u793c\u76f8\u89c1\u3002\u6b49\u7136\u8bf4\u9053\uff1a\u201c\u90fd\u662f\u6211\u6765\u8fdf\u4e86\u4e00\u6b65\uff0c\u7d2f\u4f60\u4eec\u5403\u4e8f\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u95ee\u9053\uff1a\u201c\u8fd9\u59d3\u6587\u7684\u662f\u4ec0\u4e48\u4eba\uff0c\u5982\u6b64\u5389\u5bb3\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60\u5927\u7ea6\u5df2\u89c1\u8fc7\u4ed6\u7684\u4f84\u513f\u4e86\uff0c\u4ed6\u5c31\u662f\u66ff\u548c\u5764\u62a4\u5b9d\u90a3\u5c11\u5e74\u7684\u53d4\u7236\uff0c\u5357\u6d77\u672a\u540d\u5c9b \u7684\u5c9b\u4e3b\uff0c\u4e09\u5e74\u4e4b\u524d\uff0c\u6211\u4e5f\u66fe\u5403\u8fc7\u4ed6\u7684\u5927\u4e8f\u3002\u201d \u3000\u3000\u6c5f\u5357\u8be7\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u51ed\u4f60\u8fd9\u8d85\u51e1\u7edd\u4e16\u7684\u6b66\u529f\uff0c\u600e\u7684\u8fd8\u4f1a\u5403\u4eba\u5bb6\u7684\u4e8f\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4eba\u5fc3\u9669\u6076\uff0c\u5355\u51ed\u7740\u6b66\u529f\uff0c\u90a3\u8fd8\u662f\u5e94\u4ed8\u4e0d\u4e86\u7684\u3002\u5c0f\u5144\u5f1f\uff0c\u4f60\u5b85\u5fc3\u826f\u5584\uff0c\u6bd4\u6211 \u66f4\u5bb9\u6613\u4e0a\u5f53\u3002\u6211\u73b0\u5728\u5c31\u5c06\u6211\u7684\u906d\u9047\u8bf4\u7ed9\u4f60\u542c\uff0c\u8ba9\u4f60\u4e5f\u597d\u5f97\u4e2a\u6559\u8bad\uff0c\u4ee5\u540e\u4e34\u4e8b\u4e0d\u80fd\u5927\u610f\uff0c\u5bf9\u4eba \u987b\u8981\u63d0\u9632\u3002\u201d \u3000\u3000\u539f\u6765\u91d1\u4e16\u9057\u81ea\u7ecf\u8fc7\u90a3\u573a\u60c5\u573a\u60e8\u53d8\u4e4b\u540e\uff0c\u5373\u5fc3\u7070\u610f\u51b7\uff0c\u7eb5\u60c5\u5c71\u6c34\uff0c\u6f2b\u6e38\u5404\u5730\u3002\u4e09\u5e74\u4e4b\u524d\uff0c \u4ed6\u52a8\u4e86\u6d77\u5916\u6e38\u5174\uff0c\u72ec\u81ea\u9a7e\u821f\u51fa\u6d77\uff0c\u6d4f\u89c8\u5404\u5c9b\u98ce\u5149\u3002\u6709\u4e00\u5929\uff0c\u7ecf\u8fc7\u5357\u6d77\u7684\u4e00\u4e2a\u5c0f\u5c9b\uff0c\u5ffd\u89c1\u5c9b\u4e0a \u6709\u708a\u70df\u5347\u8d77\uff0c\u77e5\u6709\u4eba\u5bb6\uff0c\u4fbf\u820d\u821f\u767b\u9646\uff0c\u62dc\u8bbf\u5c9b\u4e3b\u3002 \u3000\u3000\u5c9b\u4e3b\u59d3\u6587\uff0c\u5bf9\u91d1\u4e16\u9057\u6bb7\u52e4\u62db\u5f85\uff0c\u6267\u793c\u751a\u606d\uff0c\u81ea\u8a00\u5148\u4ee3\u4e43\u662f\u524d\u5b8b\u7684\u5b98\u5ba6\u4eba\u5bb6\uff0c\u660e\u4ea1\u4e4b\u540e\uff0c \u4ed6\u4eec\u4e0d\u613f\u505a\u6ee1\u6e05\u987a\u6c11\uff0c\u56e0\u800c\u4e0e\u82e5\u5e72\u4eb2\u53cb\uff0c\u9003\u81f3\u6b64\u5c9b\u3002\u91d1\u4e16\u9057\u656c\u4ed6\u5fe0\u4e49\uff0c\u4e14\u89c1\u4ed6\u8c08\u5410\u4e0d\u4fd7\uff0c\u5f53 \u771f\u4e00\u89c1\u5982\u6545\u3002 \u3000\u3000\u91d1\u4e16\u9057\u662f\u4e2a\u6b66\u5b66\u5927\u884c\u5bb6\uff0c\u770b\u51fa\u4e86\u8fd9\u6587\u5c9b\u4e3b\u6b66\u529f\u6781\u6709\u9020\u8be3\uff0c\u4e0d\u4e45\u548c\u4ed6\u8c08\u8bba\u8d77\u6b66\u529f\u4e0a\u7684\u4e8b \u60c5\uff0c\u6587\u5c9b\u4e3b\u8bf4\uff0c\u4ed6\u7684\u6b66\u529f\u4e43\u662f\u51fa\u81ea\u5bb6\u4f20\uff1a\u636e\u5148\u7956\u6240\u8a00\uff0c\u5927\u534a\u662f\u4ece\u6613\u7ecf\u4e2d\u53c2\u609f\u7684\uff0c\u9003\u81f3\u6b64\u5c9b\u4e4b \u540e\uff0c\u53c8\u4e0e\u5c9b\u6c11\u94bb\u7814\uff0c\u9887\u6709\u589e\u6dfb\uff0c\u4f46\u56e0\u50fb\u5904\u8352\u5c9b\uff0c\u5b64\u964b\u5be1\u95fb\uff0c\u4e0d\u77e5\u662f\u5426\u4e0e\u54ea\u4f4d\u7684\u6b66\u5b66\u76f8\u5408\uff1f \u3000\u3000\u91d1\u4e16\u9057\u5bf9\u6b63\u90aa\u5404\u6d3e\u7684\u6b66\u5b66\uff0c\u51e0\u4e4e\u53ef\u4ee5\u8bf4\u5f97\u4e0a\u662f\u65e0\u6240\u4e0d\u7aa5\uff0c\u4f46\u4ece\u6613\u7ecf\u800c\u80fd\u53c2\u900f\u6b66\u5b66\uff0c\u8fd9\u5374 \u662f\u4ed6\u95fb\u6240\u672a\u95fb\uff0c\u8c08\u8bba\u4e4b\u4e0b\uff0c\u679c\u7136\u53d1\u89c9\u8fd9\u4e00\u5bb6\u7684\u6b66\u5b66\uff0c\u751a\u4e3a\u5947\u5965\uff0c\u5c24\u5176\u5728\u5185\u529f\u7684\u4fee\u4e60\u4e0a\uff0c\u66f4\u662f \u522b\u51fa\u5fc3\u88c1\uff0c\u53e6\u5f00\u9014\u5f84\uff0c\u4ee5\u91d1\u4e16\u9057\u7684\u89c1\u8bc6\uff0c\u4e5f\u4e0d\u80fd\u5224\u522b\u4ed6\u662f\u6b63\u662f\u90aa\uff1f \u3000\u3000\u8fd9\u6587\u5c9b\u4e3b\u4e5f\u5df2\u770b\u51fa\u4e86\u91d1\u4e16\u9057\u4e43\u662f\u5f02\u4eba\uff0c\u5f53\u665a\u76db\u7b75\u62db\u5f85\uff0c\u9152\u9163\u8033\u70ed\u4e4b\u9645\uff0c\u5c31\u9080\u4ed6\u5370\u8bc1\u6b66 \u529f\u3002\u91d1\u4e16\u9057\u6c89\u8ff7\u6b66\u5b66\uff0c\u55dc\u597d\u5982\u72c2\uff0c\u751f\u5e73\u6700\u6b22\u559c\u4e0e\u4eba\u6bd4\u8bd5\u6b66\u529f\uff0c\u5c11\u5e74\u65f6\u5019\uff0c\u4e5f\u66fe\u904d\u8bbf\u6b66\u6797\u540d \u5bb6\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u9ad8\u624b\uff0c\u66fe\u53d7\u8fc7\u4ed6\u7684\u6298\u8fb1\u3002\u5982\u4eca\u5230\u4e86\u4e2d\u5e74\uff0c\u867d\u7136\u72c2\u6027\u5df2\u655b\uff0c\u4f46\u5bf9\u4e0a\u4e58\u6b66\u5b66\uff0c\u8fd8 \u662f\u4e0d\u65ad\u8ffd\u6c42\uff0c\u96be\u5f97\u8fd9\u6587\u5c9b\u4e3b\u81ea\u52a8\u76f8\u9080\uff0c\u4ed6\u7acb\u5373\u6b23\u7136\u7b54\u5141\u3002 \u3000\u3000\u4e00\u8bd5\u4e4b\u4e0b\uff0c\u679c\u7136\u5927\u662f\u4e0d\u51e1\uff0c\u76f4\u6597\u4e86\u4e09\u4e2a\u65f6\u8fb0\uff0c\u91d1\u4e16\u9057\u624d\u80dc\u5f97\u4e00\u62db\u3002\u8fd9\u6b21\u6bd4\u8bd5\u8fc7\u540e\uff0c\u4e24\u4eba \u7684\u4ea4\u60c5\u66f4\u52a0\u597d\u4e86\u3002\u6587\u5c9b\u4e3b\u6bb7\u6bb7\u7559\u4f4f\uff0c\u91d1\u4e16\u9057\u4e5f\u820d\u4e0d\u5f97\u79bb\u5f00\u4ed6\uff0c\u4fbf\u5728\u4ed6\u5bb6\u4e2d\u4f5c\u5ba2\u3002 \u3000\u3000\u5982\u662f\u8005\u4e00\u8fde\u8c08\u8bba\u4e86\u51e0\u65e5\u7684\u6b66\u529f\uff0c\u6587\u5c9b\u4e3b\u5bf9\u4e2d\u571f\u7684\u5404\u6d3e\u6b66\u5b66\uff0c\u6709\u751a\u80fd\u4eba\u7b49\u7b49\uff0c\u90fd\u95ee\u5f97\u751a\u4e3a \u8be6\u7ec6\uff0c\u65e0\u610f\u4e2d\u4e5f\u9732\u51fa\u4e00\u4e9b\u6d88\u606f\uff0c\u542c\u6765\u4ed6\u5bf9\u4e2d\u58eb\u7684\u6b66\u6797\u60c5\u5f62\uff0c\u4e5f\u5e76\u975e\u5341\u5206\u9694\u819c\uff0c\u91d1\u4e16\u9057\u8fd9\u624d\u7a0d \u7a0d\u8d77\u7591\u3002 \u3000\u3000\u6587\u5c9b\u4e3b\u5927\u7ea6\u4e5f\u731c\u5230\u91d1\u4e16\u9057\u5df2\u8d77\u4e86\u7591\u5fc3\uff0c\u4e0d\u5f85\u4ed6\u63d0\u95ee\uff0c\u4fbf\u544a\u8bc9\u4ed6\uff0c\u539f\u6765\u5c9b\u4e3b\u6bcf\u9694\u4e24\u4e09\u5e74\u4fbf \u8981\u6d3e\u4eba\u5230\u6cbf\u6d77\u7684\u57ce\u5e02\u53bb\uff0c\u91c7\u8d2d\u5fc5\u987b\u7684\u7269\u54c1\uff0c\u6240\u4ee5\u4e0e\u5916\u95f4\u5e76\u975e\u5b8c\u5168\u65ad\u7edd\u6d88\u606f\u3002 \u3000\u3000\u8fc7\u4e86\u51e0\u5929\uff0c\u679c\u7136\u6709\u4e00\u8258\u8239\u53ea\u56de\u6765\uff0c\u8fd9\u6b21\u51fa\u6d77\u56de\u6765\u7684\u662f\u6587\u5c9b\u4e3b\u7684\u4f84\u513f\uff0c\u540d\u5524\u6587\u9053\u5e84\uff0c\u5c9b\u4e0a \u5f53\u665a\u5c31\u6446\u9152\u7ed9\u4ed6\u63a5\u98ce\uff0c\u91d1\u4e16\u9057\u5f53\u7136\u662f\u5e2d\u4e0a\u8d35\u5bbe\u3002\u5230\u4e86\u9152\u9163\u8033\u70ed\uff0c\u6587\u9053\u5e84\u5ffd\u7136\u63d0\u8d77\u4e00\u6869\u4e8b\u60c5\uff0c \u8bf4\u662f\u5f53\u671d\u7684\u9996\u76f8\u548c\u7837\uff0c\u6b63\u8981\u793c\u8058\u6b66\u529f\u9ad8\u5f3a\u4e4b\u571f\uff0c\u4ed6\u6709\u610f\u5230\u5317\u4eac\u53bb\u4e00\u8d9f\uff0c\u4f1a\u4f1a\u9ad8\u4eba\uff0c\u5f00\u5f00\u773c \u754c\uff0c\u95ee\u53d4\u7236\u610f\u89c1\u5982\u4f55\uff1f \u3000\u3000\u91d1\u4e16\u9057\u542c\u4e86\uff0c\u8fde\u5fd9\u544a\u8bc9\u4ed6\uff0c\u548c\u7837\u4e43\u662f\u4e2a\u201c\u56fd\u4eba\u7686\u66f0\u53ef\u6740\u201d\u7684\u5978\u76f8\uff0c\u82e5\u53ea\u662f\u62b1\u7740\u6e38\u620f\u4eba\u95f4 \u7684\u5fc3\u60c5\uff0c\u53bb\u5f00\u5f00\u773c\u754c\uff0c\u90a3\u8fd8\u53ef\u4ee5\uff0c\u4f46\u5207\u52ff\u53d7\u548c\u7837\u7684\u7b3c\u7edc\u3002 \u3000\u3000\u6587\u9053\u5e84\u542c\u4e86\u4ed6\u7684\u8c08\u8bba\uff0c\u4f3c\u4e4e\u6709\u70b9\u8be7\u5f02\uff0c\u4f46\u968f\u5373\u4fbf\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6211\u5bb6\u662f\u4e3a\u4e86\u9003\u907f\u6e05\u5175\uff0c \u624d\u6765\u81f3\u6b64\u5c9b\u7684\uff0c\u6211\u5c82\u4f1a\u505a\u6e05\u671d\u7684\u5b98\u513f\uff1f\u5f53\u7136\u53ea\u662f\u4e3a\u4e86\u501f\u4e2a\u673a\u4f1a\uff0c\u5230\u4e2d\u571f\u53bb\u5370\u8bc1\u5370\u8bc1\u6b66\u529f\u800c \u5df2\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u542c\u4ed6\u8bf4\u5f97\u723d\u5feb\uff0c\u5f53\u4e0b\u8fd8\u7b54\u5e94\u4ed6\uff0c\u613f\u610f\u643a\u4ed6\u540c\u56de\u4e2d\u571f\uff0c\u4ecb\u7ecd\u4ed6\u8ba4\u8bc6\u5404\u6d3e\u7684\u6b66\u5b66\u5b97 \u5e08\uff0c\u5e76\u4e14\u7b11\u9053\uff1a\u201c\u81f3\u4e8e\u548c\u7837\u90a3\u513f\uff0c\u8c05\u4ed6\u4e0d\u4f1a\u7f51\u7f57\u5230\u4ec0\u4e48\u9ad8\u4eba\uff0c\u4f60\u53bb\u4e0d\u53bb\u4e5f\u7f62\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u56e0\u4e3a\u81ea\u5df1\u7684\u540d\u5934\u5927\u54cd\uff0c\u5bf9\u964c\u751f\u4eba\u662f\u4ece\u4e0d\u8868\u9732\u81ea\u5df1\u7684\u8eab\u4efd\u7684\uff0c\u8fd9\u6b21\u4ed6\u6765\u5230\u6b64\u5c9b\uff0c\u867d \u7136\u4e0e\u6587\u5c9b\u4e3b\u8c08\u5f97\u751a\u8fc7\u6b22\u6d3d\uff0c\u4e5f\u8fd8\u6ca1\u6709\u900f\u9732\u81ea\u5df1\u7684\u5b9e\u59d3\u771f\u540d\u3002\u4f46\u8fd9\u665a\u4e5f\u591a\u559d\u4e86\u4e24\u676f\uff0c\u4e0d\u77e5\u4e0d\u89c9 \u4e4b\u95f4\uff0c\u8ba9\u4eba\u5bb6\u77e5\u9053\u4e86\u4ed6\u7684\u4ea4\u6e38\u6781\u5e7f\uff0c\u5927\u4e0d\u5bfb\u5e38\u3002 \u3000\u3000\u6587\u9053\u5e84\u63a5\u7740\u8c08\u8d77\u4e86\u4ed6\u8fd9\u6b21\u7684\u89c1\u95fb\uff0c\u8bdd\u9898\u7adf\u8bf4\u5230\u4e86\u91d1\u4e16\u9057\u7684\u8eab\u4e0a\uff0c\u539f\u6765\u4ed6\u4e5f\u77e5\u9053\u4e86\u91d1\u4e16 \u9057\u3001\u5389\u80dc\u7537\u3001\u5510\u6653\u6f9c\u3001\u5b5f\u795e\u901a\u7b49\u4eba\u7684\u90a3\u4e9b\u4e8b\u8ff9\uff0c\u4ed6\u8bb2\u4e86\u4ed6\u542c\u6765\u7684\u5389\u80dc\u7537\u4e0e\u5510\u6653\u6f9c\u6bd4\u6b66\u7684\u6545\u4e8b \u4e4b\u540e\uff0c\u9053\uff1a\u201c\u5982\u4eca\u5389\u80dc\u7537\u4e0e\u5b5f\u795e\u901a\u90fd\u5df1\u6b7b\u4e86\uff0c\u4ed6\u4eec\u7684\u6b66\u529f\u79d8\u7c4d\u90fd\u5df2\u5230\u4e86\u91d1\u4e16\u9057\u4e4b\u624b\uff0c\u53ea\u6015\u5510 \u6653\u6f9c\u4e5f\u8981\u900a\u4ed6\u4e00\u7b79\u3002\u5f53\u4eca\u4e4b\u4e16\uff0c\u8bba\u5230\u6b66\u529f\uff0c\u90a3\u662f\u8981\u63a8\u91d1\u4e16\u9057\u5750\u7b2c\u4e00\u628a\u4ea4\u6905\u4e86\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u5f53\u7136\u4e0d\u4f1a\u63d2\u8bdd\uff0c\u4f46\u4ed6\u542c\u5f97\u522b\u4eba\u63d0\u8d77\u81ea\u5df1\u7684\u6545\u4e8b\uff0c\u5c24\u5176\u662f\u63d0\u5230\u5389\u80dc\u7537\u90a3\u6bb5\u4e8b\u60c5\uff0c\u5374 \u96be\u514d\u6709\u6240\u611f\u4f24\uff0c\u8fde\u8fde\u559d\u9152\u3002\u90a3\u6587\u5c9b\u4e3b\u6709\u610f\u65e0\u610f\u4e4b\u95f4\u77a7\u4e86\u4ed6\u597d\u51e0\u773c\u3002 \u3000\u3000\u6587\u9053\u5e84\u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u73b0\u5728\u542c\u8bf4\u5389\u80dc\u7537\u4e5f\u6709\u4e86\u4f20\u4eba\u4e86\uff0c\u6709\u4eba\u7acb\u5979\u4e3a\u7956\u5e08\uff0c\u6210\u7acb\u4e86\u4e00\u4e2a\u5929 \u9b54\u6559\uff0c\u4e13\u7f51\u7f57\u90aa\u6d3e\u9ad8\u624b\uff0c\u636e\u8bf4\u4eba\u624d\u80da\u4e0d\u5c11\u54e9\uff01\u201d\u8fd9\u4e2a\u6d88\u606f\uff0c\u8fde\u91d1\u4e16\u9057\u4e5f\u8fd8\u662f\u7b2c\u4e00\u6b21\u542c\u5230\uff0c\u8fde \u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ece\u54ea\u513f\u542c\u6765\u7684\uff1f\u201d\u6587\u9053\u5e84\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u4e0d\u662f\u542c\u6765\u7684\u4e86\uff0c\u6211\u89c1\u8fc7\u5929\u9b54\u6559\u4e3b\u672c \u4eba\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u95ee\u9053\uff1a\u201c\u662f\u600e\u4e48\u6837\u7684\u4e00\u4e2a\u4eba\uff1f\u201d\u6587\u9053\u5e84\u9053\uff1a\u201c\u5979\u6234\u7740\u9762\u7eb1\uff0c\u6211\u770b\u4e0d\u6e05\u695a\uff0c\u50cf\u662f\u4e2a \u5e74\u8f7b\u7684\u5973\u5b50\u3002\u90a3\u5929\u6211\u5728\u6cc9\u5dde\u5356\u51fa\u73cd\u73e0\u767e\u8d27\uff0c\u94b1\u8d22\u9732\u773c\uff0c\u6709\u51e0\u4e2a\u5c0f\u8d3c\u4fbf\u6765\u6253\u6211\u7684\u4e3b\u610f\uff0c\u5f85\u6211\u51fa \u57ce\uff0c\u4fbf\u62e6\u9014\u622a\u52ab\uff0c\u6211\u548c\u4ed6\u4eec\u5f00\u4e86\u4e2a\u5c0f\u5c0f\u7684\u73a9\u7b11\uff0c\u5c06\u4ed6\u4eec\u90e1\u70b9\u4e86\u7b11\u8170\u7a74\uff0c\u8ba9\u4ed6\u4eec\u90fd\u7b11\u5f97\u5728\u5730\u4e0a \u6253\u6eda\uff0c\u722c\u4e0d\u8d77\u6765\u3002\u8981\u8fc7\u4e00\u4e2a\u65f6\u8fb0\u65b9\u80fd\u81ea\u89e3\u3002\u4e8b\u60c5\u8fc7\u540e\uff0c\u6211\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u54ea\u77e5\u94b1\u8d22\u4e0d\u53ef\u9732 \u773c\uff0c\u6b66\u529f\u4e5f\u4e0d\u53ef\u9732\u773c\uff0c\u6211\u8fd9\u4e48\u4e00\u9732\uff0c\u5c31\u60ca\u52a8\u4e86\u5929\u9b54\u6559\u4e3b\u3002\u90a3\u5929\u665a\u4e0a\uff0c\u6211\u5728\u4e00\u5ea7\u5bfa\u89c2\u501f\u5bbf\uff0c\u5348 \u591c\u65f6\u5206\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u5728\u5916\u9762\u7684\u7a97\u5b50\u5f39\u4e86\u51e0\u4e0b\uff0c\u53eb\u6211\u51fa\u6765\u3002 \u3000\u3000\u201c\u6211\u51fa\u6765\u4e00\u770b\uff0c\u89c1\u662f\u4e00\u4e2a\u8499\u9762\u5973\u5b50\uff0c\u6211\u6b63\u5728\u60ca\u8be7\uff0c\u5979\u5df2\u95ee\u6211\u9053\uff1a\u2018\u4eca\u65e5\u70b9\u4e86\u9752\u9633\u5e2e\u5e2e\u4f17 \u7b11\u7a74\u7684\uff0c\u53ef\u662f\u4f60\u4e48\uff1f\u2019\u6211\u4ee5\u4e3a\u5979\u662f\u9752\u9633\u5e2e\u7684\u5973\u9996\u9886\uff0c\u62b1\u7740\u606f\u4e8b\u5b81\u4eba\u7684\u6253\u7b97\uff0c\u4fbf\u5411\u5979\u8d54\u7f6a\uff0c\u5e76 \u5411\u5979\u7533\u8fa9\uff0c\u8bf4\u6211\u5e76\u4e0d\u77e5\u9053\u90a3\u4e9b\u4eba\u7684\u6765\u5386\u3002 \u3000\u3000\u201c\u54ea\u77e5\u5979\u4e0d\u5f85\u6211\u628a\u8bdd\u8bf4\u5b8c\uff0c\u4fbf\u51b7\u7b11\u9053\uff1a\u2018\u6211\u624d\u6ca1\u6709\u5de5\u592b\u53bb\u7406\u9752\u9633\u5e2e\u7684\u4e8b\u60c5\u5462\uff0c\u6211\u662f\u89c1\u4f60 \u6b66\u529f\u4e0d\u9519\uff0c\u610f\u6b32\u5c06\u4f60\u6536\u4e3a\u672c\u6559\u7684\u62a4\u6cd5\u5f1f\u5b50\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u597d\u5947\u5fc3\u8d77\uff0c\u95ee\u5979\u662f\u4ec0\u4e48\u6559\uff0c\u6559\u5b97\u662f\u8c01\uff1f\u662f\u505a\u4e9b\u4ec0\u4e48\uff1f\u8fd9\u624d\u77e5\u9053\u5979\u4eec\u7684\u6559\u540d\u53eb\u5929\u9b54 \u6559\uff0c\u8fd9\u8499\u9762\u5973\u5b50\u672c\u4eba\u4fbf\u662f\u6559\u4e3b\uff0c\u5979\u4eec\u6240\u5949\u7684\u7956\u5e08\u4fbf\u662f\u53bb\u4e16\u672a\u4e45\u3001\u9f0e\u9f0e\u5927\u540d\u7684\u5973\u9b54\u5934\u5389\u80dc\u7537\u3002 \u5979\u4eec\u6210\u7acb\u6b64\u6559\uff0c\u610f\u6b32\u4e0e\u81ea\u79f0\u6b63\u6d3e\u7684\u6b66\u6797\u4e2d\u4eba\u4e00\u51b3\u96cc\u96c4\uff0c\u636e\u8bf4\u4e0d\u4e45\u4fbf\u8981\u524d\u5f80\u6c13\u5c71\uff0c\u5148\u4e0e\u6c13\u5c71\u6d3e \u7684\u638c\u95e8\u4e00\u6597\u3002 \u3000\u3000\u201c\u6211\u60f3\u6240\u77e5\u9053\u7684\u90fd\u5df2\u77e5\u9053\u4e86\uff0c\u4e8e\u662f\u6211\u4fbf\u5bf9\u5979\u8bf4\uff1a\u2018\u591a\u8c22\u76db\u60c5\uff0c\u4f46\u6211\u53e6\u6709\u5b89\u8eab\u7acb\u547d\u4e4b\u6240\uff0c \u53ef\u5e76\u4e0d\u60f3\u52a0\u5165\u8d35\u6559\u3002\u201c\u90a3\u8499\u9762\u5973\u5b50\u542c\u4e86\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201d\u4f60\u4e0d\u60f3\u52a0\u5165\u4e5f\u884c\uff0c\u53ef\u662f\u4f9d\u7167\u672c\u6559\u89c4 \u77e9\uff0c\u77e5\u9053\u4e86\u672c\u6559\u79d8\u5bc6\u7684\u5916\u4eba\u5c31\u5f97\u4ea4\u51fa\u6027\u547d\u3002\u201d\u201c\u6211\u542c\u4e86\u5f53\u7136\u706b\u8d77\uff0c\u8bf4\u9053\uff1a\u2018\u53ea\u8981\u4f60\u6709\u672c\u9886\uff0c \u6211\u8fd9\u6761\u4e0d\u503c\u94b1\u7684\u6027\u547d\uff0c\u4f60\u53ea\u7ba1\u53d6\u53bb\u3002\u2019\u8bdd\u5df2\u8bf4\u50f5\uff0c\u4fbf\u5373\u52a8\u624b\u3002\u201c\u54ea\u77e5\u8fd9\u5929\u9b54\u6559\u4e3b\u679c\u7136\u5389\u5bb3\u975e \u5e38\uff0c\u6211\u52a8\u624b\u4e0d\u8fc7\u5341\u62db\uff0c\u4fbf\u53d7\u4e86\u91cd\u4f24\uff0c\u5e78\u800c\u6211\u61c2\u5f97\u95ed\u6c14\u65ad\u606f\u7684\u5185\u529f\uff0c\u8eba\u5728\u5730\u4e0a\u8bc8\u6b7b\uff0c\u9a97\u8fc7\u4e86 \u5979\u3002\u4e5f\u5e78\u800c\u5979\u968f\u8eab\u5e76\u65e0\u643a\u5e26\u5229\u5668\uff0c\u5979\u8e22\u4e86\u6211\u51e0\u4e0b\uff0c\u89c1\u6211\u8eab\u4f53\u5df2\u7ecf\u50f5\u786c\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u4fbf\u5f84\u81ea\u8d70 \u4e86\u3002\u201c\u6211\u8fd9\u6b21\u53d7\u4f24\uff0c\u76f4\u533b\u4e86\u4e24\u4e2a\u591a\u6708\uff0c\u624d\u5f97\u590d\u539f\u3002\u53d4\u53d4\uff0c\u8fd9\u4e5f\u5c31\u662f\u4e3a\u4ec0\u4e48\u6211\u8fdf\u4e86\u5f52\u671f\u7684\u7f18 \u6545\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u542c\u4e86\u4ed6\u7684\u6545\u4e8b\uff0c\u5185\u5fc3\u6697\u60ca\uff0c\u4f46\u540c\u65f6\u4e5f\u89c9\u5f97\u6709\u70b9\u7591\u7aa6\u3002 \u3000\u3000\u5403\u60ca\u7684\u662f\uff0c\u5929\u9b54\u6559\u4e3b\u5728\u5341\u62db\u4e4b\u5185\uff0c\u4fbf\u80fd\u4ee4\u6587\u9053\u5e84\u51e0\u4e4e\u4e27\u547d\uff0c\u5f53\u7136\uff0c\u6587\u9053\u5e84\u90a3\u70b9\u672c\u9886\uff0c\u5728 \u91d1\u4e16\u9057\u773c\u4e2d\u8fd8\u4e0d\u7b97\u5f97\u4ec0\u4e48\uff0c\u5929\u9b54\u6559\u6b66\u529f\u518d\u5f3a\u4e5f\u4e0d\u80fd\u5f3a\u8fc7\u5f53\u5e74\u7684\u5389\u80dc\u7537\uff0c\u91d1\u4e16\u9057\u5e76\u975e\u672c\u4eba\u60e7\u6015 \u4e8e\u5979\u4eec\uff0c\u800c\u662f\u56e0\u4e3a\u5929\u9b54\u6559\u4e3b\u626c\u8a00\u8981\u5411\u8c37\u4e4b\u534e\u5bfb\u4ec7\uff0c\u4ed6\u662f\u6015\u8c37\u4e4b\u534e\u5bf9\u4ed8\u4e0d\u4e86\u3002 \u3000\u3000\u6000\u7591\u7684\u662f\uff0c\u90a3\u5929\u9b54\u6559\u4e3b\u4e3a\u4f55\u8981\u5c06\u62df\u5411\u8c37\u4e4b\u534e\u5bfb\u4ec7\u4e4b\u4e8b\uff0c\u8bf4\u7ed9\u6587\u9053\u5e84\u77e5\u9053\uff0c\u6587\u9053\u5e84\u53c8\u672a\u66fe \u7b54\u5e94\u5165\u5979\u7684\u6559\uff0c\u6700\u591a\u5979\u5c06\u672c\u6559\u7684\u6765\u5386\u8bf4\u4e2a\u6e05\u695a\u4e5f\u5c31\u662f\u4e86\uff0c\u5374\u65e0\u9700\u5c06\u672c\u6559\u7684\u8ba1\u5212\u544a\u8bc9\u5916\u4eba\u3002 \u3000\u3000\u4f46\u7531\u4e8e\u91d1\u4e16\u9057\u5bf9\u6587\u5c9b\u4e3b\u53d4\u4f84\u9887\u6709\u597d\u611f\uff0c\u8fd9\u4e00\u70b9\u70b9\u6000\u7591\u5728\u5fc3\u4e0a\u4e00\u63a0\u5373\u8fc7\uff0c\u4ed6\u60f3\u4eba\u7684\u6027\u60c5\uff0c \u5404\u4e2a\u4e0d\u540c\uff0c\u6216\u8bb8\u90a3\u5929\u9b54\u6559\u4e3b\u662f\u50cf\u6c5f\u5357\u4e00\u6837\u591a\u8bdd\u7684\u5462\uff1f\u5374\u60f3\u4e0d\u5230\u8fd9\u4e00\u6bb5\u8bdd\u6b63\u662f\u6587\u9053\u5e84\u634f\u9020\u51fa \u6765\uff0c\u8bd5\u63a2\u4ed6\u7684\u3002\u4e0d\u8fc7\u4ed6\u7684\u634f\u9020\u4e5f\u5e76\u975e\u5168\u65e0\u6839\u636e\uff0c\u4ed6\u662f\u77e5\u9053\u4e86\u5929\u9b54\u6559\u4e3b\u7684\u6765\u5386\uff0c\u731c\u6d4b\u5979\u5c06\u6765\u8981 \u5982\u6b64\u7684\uff0c\u6240\u4ee5\u540e\u6765\u4ed6\u634f\u9020\u7684\u8bdd\u4e5f\u7adf\u6210\u4e86\u4e8b\u5b9e\u3002 \u3000\u3000\u91d1\u4e16\u9057\u65e2\u62c5\u5fc3\u8c37\u4e4b\u534e\u4f1a\u6709\u5371\u9669\uff0c\u540c\u65f6\u53c8\u60f3\u77e5\u9053\u8fd9\u5929\u9b54\u6559\u4e3b\u548c\u5389\u80dc\u7537\u6709\u4f55\u5173\u7cfb\uff0c\u56e0\u6b64\u542c\u5b8c \u4e86\u6587\u9053\u5e84\u7684\u53d9\u8ff0\u4e4b\u540e\uff0c\u4fbf\u4e0d\u7981\u5fc3\u4e8b\u91cd\u91cd\uff0c\u6068\u4e0d\u5f97\u65e9\u65e5\u5f52\u53bb\uff0c\u5f53\u4e0b\u4fbf\u5411\u6587\u5c9b\u4e3b\u8f9e\u884c\uff0c\u8bf4\u662f\u660e\u5929 \u4e00\u65e9\uff0c\u4fbf\u8981\u56de\u5bb6\uff0c\u5018\u82e5\u6587\u9053\u5e84\u6709\u610f\u7ed3\u8bc6\u4e2d\u4e0a\u7684\u6b66\u6797\u4eba\u7269\uff0c\u53ef\u4ee5\u4e0e\u4ed6\u540c\u884c\u3002 \u3000\u3000\u6587\u5c9b\u4e3b\u8fd8\u6545\u4f5c\u60ca\u8be7\uff0c\u95ee\u4ed6\u4f55\u4ee5\u7a81\u7136\u52a8\u4e86\u5f52\u5bb6\u4e4b\u5ff5\uff1f\u91d1\u4e16\u9057\u53ea\u597d\u63a8\u8bf4\u662f\u89c1\u6587\u9053\u5e84\u4ece\u4e2d\u571f\u56de \u6765\uff0c\u800c\u81ea\u5df1\u5df2\u51fa\u6d77\u591a\u5e74\uff0c\u56e0\u800c\u6709\u4e86\u4e61\u601d\u3002 \u3000\u3000\u6587\u5c9b\u4e3b\u633d\u7559\u4e0d\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u76f8\u805a\u6b63\u6b22\uff0c\u4fbf\u8981\u5206\u624b\uff0c\u60c5\u4f55\u4ee5\u582a\uff1f\u4f46\u662f\u4ec1\u5144\u5f52\u610f\u5df2\u51b3\uff0c\u6211\u4e5f \u96be\u4ee5\u5f3a\u7559\u3002\u4ec1\u5144\u613f\u643a\u540c\u820d\u4f84\u5f80\u4e2d\u571f\u5386\u7ec3\uff0c\u6b63\u662f\u6211\u6c42\u4e4b\u4e0d\u5f97\u7684\u4e8b\u60c5\u3002\u54c8\u54c8\uff0c\u6211\u6b64\u523b\u7aef\u7684\u662f\u5e94\u4e86 \u2018\u60b2\u559c\u4ea4\u96c6\u2019\uff0c\u8fd9\u53e5\u8bdd\u4e86\uff0c\u4e3a\u4ec1\u5144\u7684\u522b\u79bb\u800c\u4f24\uff1b\u4e3a\u820d\u4f84\u6709\u6240\u501a\u9760\u800c\u9ad8\u5174\uff01\u6765\uff0c\u6211\u6362\u8fc7\u4e00\u4e2a\u5927 \u676f\u5411\u4f60\u656c\u9152\uff0c\u7ed9\u4f60\u996f\u884c\uff0c\u8c22\u4f60\u5bf9\u820d\u4f84\u7684\u7167\u987e\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u4e5f\u6709\u70b9\u4f9d\u4f9d\u60dc\u522b\u4e4b\u611f\uff0c\u6beb\u4e0d\u63a8\u8f9e\u7684\u4fbf\u4e0e\u4ed6\u5e72\u4e86\u4e00\u676f\uff0c\u54ea\u77e5\u9152\u4e00\u4e0b\u809a\u4e4b\u540e\uff0c\u4fbf\u611f\u5230 \u6709\u4e9b\u5f02\u6837\uff0c\u91d1\u4e16\u9057\u521a\u521a\u5bdf\u89c9\uff0c\u90a3\u836f\u9152\u5df2\u7ecf\u53d1\u4f5c\uff0c\u4e00\u9635\u5730\u8f6c\u5929\u65cb\uff0c\u7cca\u91cc\u7cca\u6d82\u7684\u4fbf\u9189\u5012\u4e86\u3002 \u3000\u3000\u6726\u80e7\u4e2d\uff0c\u91d1\u4e16\u9057\u542c\u5f97\u6709\u4eba\u5927\u58f0\u53eb\u4ed6\u7684\u540d\u5b57\uff0c\u91d1\u4e16\u9057\u5e94\u4e86\u4e00\u58f0\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u53ea\u89c9\u5468\u56f4\u6f06 \u9ed1\uff0c\u7528\u624b\u4e00\u6478\uff0c\u56db\u9762\u90fd\u662f\u77f3\u58c1\uff0c\u91d1\u4e16\u9057\u65b9\u5728\u5947\u602a\uff0c\u4fbf\u542c\u5f97\u6587\u5c9b\u4e3b\u7684\u58f0\u97f3\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u91d1\u4e16 \u9057\uff0c\u4f60\u60f3\u8981\u4e0d\u627f\u8ba4\u4f60\u662f\u91d1\u4e16\u9057\u4e5f\u4e0d\u884c\u4e86\uff0c\u54c8\u54c8\uff0c\u6211\u62ff\u4f60\u5f53\u597d\u670b\u53cb\u770b\u5f85\uff0c\u4f60\u5374\u5bf9\u6211\u9690\u7792\u8eab\u4efd\uff0c \u4f60\u81ea\u5df1\u8bf4\u8bf4\uff0c\u8fd9\u8be5\u600e\u4e48\u5904\u7f5a\uff01\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u7edd\u4e16\u795e\u529f\u906d\u6697\u7b97\uff0c\u4eba\u5fc3\u9669\u6076\u6700\u96be\u9632\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000--------------- \u3000\u3000\u626c\u5251\u8f69\u5c45\u58eb \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u5341\u56de\u3000\u6df1\u5bb5\u4e4d\u542c\u91d1\u7334\u543c\u3000\u521d\u4f1a\u60ca\u9022\u7389\u5c3a\u5bd2 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u5341\u56de\u3000\u6df1\u5bb5\u4e4d\u542c\u91d1\u7334\u543c\u3000\u521d\u4f1a\u60ca\u9022\u7389\u5c3a\u5bd2 \u3000\u3000\u91d1\u4e16\u9057\u53eb\u9053\uff1a\u201c\u6211\u7eb5\u6709\u4e0d\u662f\uff0c\u6587\u5c9b\u4e3b\uff0c\u4f60\u8fd9\u4e2a\u73a9\u7b11\u4e5f\u672a\u514d\u5f00\u5f97\u5927\u5927\u4e86\uff01\u201d\u4ed6\u8fd8\u4ee5\u4e3a\u6587\u5c9b \u4e3b\u662f\u6545\u610f\u6349\u5f04\u4ed6\uff0c\u672a\u60f3\u5230\u6587\u5c9b\u4e3b\u5df2\u662f\u5fc3\u6000\u53f5\u6d4b\uff0c\u8981\u4e0e\u4ed6\u4e3a\u96be\u3002 \u3000\u3000\u8fd9\u65f6\u6587\u5c9b\u4e3b\u5df1\u6253\u5f00\u77f3\u7a9f\u7684\u94c1\u95e8\uff0c\u91d1\u4e16\u9057\u7741\u773c\u4e00\u77a7\uff0c\u53ea\u89c1\u4ed6\u6ee1\u9762\u6740\u6c14\uff0c\u4e0e\u5f80\u65e5\u7684\u6e29\u6587\u5112 \u96c5\uff0c\u5927\u4e0d\u76f8\u540c\u3002\u91d1\u4e16\u9057\u4e0d\u7531\u5f97\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u542c\u5f97\u4ed6\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u8c01\u4e0e\u4f60\u5f00\u73a9\u7b11\uff1f\u6211\u6700\u6068\u5bf9\u53cb \u65e0\u4e49\u4e4b\u4eba\uff0c\u975e\u60e9\u7f5a\u4f60\u4e0d\u53ef\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u6012\u6c14\u4e0a\u51b2\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7b80\u76f4\u662f\u5c0f\u9898\u5927\u505a\uff0c\u6211\u672a\u544a\u8bc9\u4f60\u6211\u7684\u771f\u540d\uff0c\u8fd9\u4e5f\u8bf4\u4e0d\u4e0a\u4ec0\u4e48 \u6709\u4e49\u65e0\u4e49\uff0c\u4f60\u5982\u6b64\u4f5c\u4e3a\uff0c\u624d\u771f\u6b63\u662f\u4e0d\u4ec1\u4e0d\u4e49\uff01\u6211\u662f\u778e\u4e86\u773c\u775b\uff0c\u8bc6\u9519\u4e86\u4f60\u4e86\uff01\u201d \u3000\u3000\u6587\u5c9b\u4e3b\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8bf4\u5f97\u4e0d\u9519\uff0c\u4f60\u73b0\u5728\u5df2\u662f\u6211\u7684\u56da\u5f92\u4e86\uff01\u4f60\u82e5\u4e0d\u4f9d\u4ece\u6211\u7684\u4e3b\u610f\uff0c \u4eca\u751f\u4eca\u4e16\uff0c\u53ef\u4f11\u60f3\u751f\u8fd8\u4e2d\u571f\u4e86\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u5fcd\u7740\u4e86\u6c14\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u5230\u5e95\u60f3\u8981\u4ec0\u4e48\uff1f\u201d\u6587\u5c9b\u4e3b\u9053\uff1a\u201c\u4e54\u5317\u6e9f\u7684\u6b66\u529f\u79d8\u7c4d\u843d\u5728\u4f60 \u7684\u624b\u4e0a\uff0c\u4f60\u7ed9\u6211\u4ea4\u51fa\u6765\uff01\u201d \u3000\u3000\u8fd9\u56de\u8f6e\u5230\u4e86\u91d1\u4e16\u9057\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u5df1\u641c\u8fc7\u4e86\u6211\u7684\u8eab\u5b50\u548c\u884c\u56ca\u4e48\uff1f\u54ea\u6709\u4ec0\u4e48\u6b66 \u529f\u79d8\u7c4d\uff1f\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u8fd9\u79d8\u7c4d\u7684\u786e\u662f\u843d\u5728\u6211\u7684\u624b\u4e0a\uff0c\u4f46\u6211\u65e9\u5df2\u628a\u5b83\u70e7\u4e86\u3002\u54c8\u54c8\uff0c\u5e78\u800c\u6211\u6709\u5148 \u89c1\u4e4b\u660e\uff0c\u514d\u5f97\u7559\u4e0b\u6765\u8ba9\u4f60\u4eec\u8fd9\u4e9b\u90aa\u9b54\u5996\u5b7d\u4f60\u4e89\u6211\u593a\uff01\u201d \u3000\u3000\u6587\u5c9b\u4e3b\u9762\u8272\u4e00\u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u770b\u5f97\u51fa\u4f60\u5df2\u7ec3\u4e0a\u4e86\u79d8\u7c4d\u4e0a\u7684\u6b66\u529f\uff0c\u70e7\u4e86\u4f60\u4e5f\u8981\u91cd\u5199\u51fa\u6765\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u5927\u7b11\u9053\uff1a\u201c\u4f60\u51ed\u4ec0\u4e48\u8981\u5429\u5490\u6211\uff1f\u201d \u3000\u3000\u6587\u5c9b\u4e3b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u53c8\u81ea\u5fd6\u4ec0\u4e48\uff1f\u4f60\u4ee5\u4e3a\u4f60\u7684\u6b66\u529f\u679c\u7136\u6bd4\u6211\u9ad8\u660e\u5417\uff1f\u524d\u65e5\u6211\u4e0d\u8fc7\u8ba9\u4f60 \u7f62\u4e86\u3002\u4e0d\u4fe1\uff0c\u4f60\u5c31\u518d\u6765\u8bd5\u8bd5\uff0c\u6211\u5c31\u8981\u51ed\u6211\u7684\u6b66\u529f\u6765\u6298\u670d\u4f60\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u9192\u6765\u4e4b\u540e\uff0c\u5df2\u8bd5\u8fc7\u81ea\u884c\u8fd0\u529f\uff0c\u771f\u6c14\u901a\u884c\u65e0\u963b\uff0c\u77e5\u9053\u672a\u66fe\u4e2d\u6bd2\uff0c\u5f53\u4e0b\u6709\u6043\u65e0\u6050\uff0c\u4fbf \u5728\u77f3\u7a9f\u4e2d\u4e0e\u90a3\u6587\u5c9b\u4e3b\u518d\u6597\u4e00\u573a\u3002 \u3000\u3000\u8fd9\u4e00\u756a\u6bd4\u62fc\uff0c\u5728\u91d1\u4e16\u9057\u8bf4\u6765\uff0c\u5df2\u4e0d\u662f\u4e0e\u4ed6\u5370\u8bc1\u6b66\u529f\uff0c\u800c\u662f\u8981\u4e0e\u4ed6\u62fc\u4e2a\u4f60\u6b7b\u6211\u6d3b\uff0c\u4f46\u90a3\u6587 \u5c9b\u4e3b\u53cd\u800c\u6c14\u5b9a\u795e\u95f2\uff0c\u4ecd\u7136\u4f3c\u662f\u4e0e\u597d\u670b\u53cb\u8fc7\u62db\u7422\u78e8\u4e00\u6837\uff0c\u6ee1\u4e0d\u5f53\u4f5c\u4e00\u56de\u4e8b\u3002\u8bf4\u4e5f\u5947\u602a\uff0c\u91d1\u4e16\u9057 \u660e\u660e\u770b\u51fa\u5bf9\u65b9\u7684\u6b66\u529f\u7a0d\u900a\u4e8e\u4ed6\uff0c\u4f46\u5230\u4e86\u7d27\u8981\u5173\u5934\uff0c\u62ff\u4e16\u9057\u5374\u6bcf\u6bcf\u529b\u4e0d\u4ece\u5fc3\uff0c\u7eb5\u6709\u8bb8\u591a\u5965\u5999\u7684 \u6b66\u529f\uff0c\u53ea\u56e0\u52b2\u529b\u7a0d\u5dee\u90a3\u4e48\u4e00\u70b9\uff0c\u5c31\u7ed9\u5bf9\u65b9\u4ece\u5bb9\u5316\u89e3\u4e86\u3002 \u3000\u3000\u6700\u521d\u51e0\u5341\u62db\u91d1\u4e16\u9057\u8fd8\u672a\u89c9\u5bdf\uff0c\u8d8a\u5230\u540e\u6765\uff0c\u5c31\u8d8a\u611f\u5230\u81ea\u5df1\u7684\u529f\u529b\u4e0d\u5982\u4ece\u524d\u3002\u5374\u539f\u6765\u91d1\u4e16\u9057 \u81ea\u5df1\u4ee5\u4e3a\u672a\u66fe\u4e2d\u6bd2\uff0c\u5176\u5b9e\u5df2\u662f\u4e2d\u4e86\u6bd2\u3002\u6587\u5c9b\u4e3b\u7ed9\u4ed6\u90a3\u676f\u836f\u9152\u843d\u6709\u4ed6\u6240\u79d8\u5236\u7684\u9165\u7b4b\u5316\u9aa8\u6563\uff0c\u8981 \u4e0d\u662f\u91d1\u4e16\u9057\u90a3\u65f6\u5df2\u5c06\u8fd1\u7ec3\u6210\u91d1\u521a\u4e0d\u574f\u4e4b\u8eab\uff0c\u559d\u4e86\u8fd9\u676f\u836f\u9152\uff0c\u4fbf\u4e0d\u80fd\u518d\u8fd0\u7528\u5185\u529f\u4e86\u3002 \u3000\u3000\u90a3\u9165\u7b4b\u5316\u9aa8\u6563\u7684\u836f\u529b\u662f\u6162\u6162\u53d1\u4f5c\u7684\uff0c\u91d1\u4e16\u9057\u7531\u4e8e\u529f\u529b\u6df1\u539a\uff0c\u6240\u53d7\u7684\u5f71\u54cd\u4ea6\u5fae\uff0c\u56e0\u6b64\u4e00\u65f6 \u95f4\u672a\u80fd\u89c9\u5bdf\u3002\u4f46\u6587\u5c9b\u4e3b\u4e0e\u4ed6\u7684\u529f\u529b\u672c\u6765\u76f8\u5dee\u6709\u9650\uff0c\u6b64\u6d88\u5f7c\u957f\uff0c\u7ed3\u679c\u5f53\u7136\u4fbf\u662f\u8d8a\u6597\u4e0b\u53bb\uff0c\u91d1\u4e16 \u9057\u8d8a\u663e\u5f97\u529b\u4e0d\u4ece\u5fc3\u3002 \u3000\u3000\u6597\u5230\u4e86\u4e00\u767e\u4e03\u5341\u516d\u62db\uff0c\u91d1\u4e16\u9057\u7ed9\u6587\u5c9b\u4e3b\u4e00\u638c\u51fb\u5012\uff0c\u6587\u5c9b\u4e3b\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u4f60\u670d\u4e86\u6211 \u4e48\uff1f\u201d\u91d1\u4e16\u9057\u8f93\u5f97\u83ab\u540d\u5176\u5999\uff0c\u5927\u6012\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u6740\u6211\u53ef\u4ee5\uff0c\u8981\u6211\u670d\u4f60\uff0c\u90a3\u662f\u51b3\u8ba1\u4e0d\u80fd\u7684\u3002\u4f60 \u7684\u6b66\u529f\uff0c\u54fc\u54fc\uff0c\u54fc\u54fc\u2026\u2026\u201d \u3000\u3000\u6587\u5c9b\u4e3b\u9053\uff1a\u201c\u6211\u7684\u6b66\u529f\u600e\u4e48\uff1f\u201d\u91d1\u4e16\u9057\u672c\u60f3\u6307\u51fa\u4ed6\u7684\u6b66\u529f\u6709\u4e9b\u5730\u65b9\u4e5f\u8fd8\u4e0d\u89c1\u5f97\u600e\u6837\u9ad8 \u660e\uff0c\u7ee7\u800c\u4e00\u60f3\uff0c\u81ea\u5df1\u5df2\u7136\u8f93\u4e86\uff0c\u867d\u7136\u6781\u4e0d\u670d\u6c14\uff0c\u4e5f\u65e0\u8c13\u591a\u8a00\u4e86\u3002 \u3000\u3000\u6587\u5c9b\u4e3b\u9274\u8c8c\u8fa8\u8272\uff0c\u7b11\u9053\uff1a\u201c\u6211\u6740\u4f60\u505a\u4ec0\u4e48\uff1f\u6211\u8fd8\u8981\u7559\u4f60\u6d88\u78e8\u5de5\u592b\uff01\u770b\u6765\uff0c\u4f60\u6562\u60c5\u8fd8\u662f\u6709 \u70b9\u513f\u4e0d\u670d\uff0c\u4e5f\u597d\uff0c\u8fc7\u4e24\u5929\u54b1\u4eec\u518d\u6bd4\uff01\u201d\u4ed6\u8d70\u51fa\u77f3\u7a9f\uff0c\u968f\u624b\u5173\u4e0a\u4e86\u94c1\u95e8\u3002 \u3000\u3000\u6587\u5c9b\u4e3b\u8d70\u540e\uff0c\u91d1\u4e16\u9057\u518d\u9759\u5750\u8fd0\u529f\uff0c\u8fd9\u624d\u53d1\u89c9\u771f\u6c14\u5728\u901a\u8fc7\u4e39\u7530\u6709\u7a0d\u7a0d\u963b\u6ede\u7684\u73b0\u8c61\uff0c\u8fd9\u624d\u77e5 \u9053\u662f\u7740\u4e86\u6587\u5c9b\u4e3b\u7684\u9053\u513f\u3002\u8fc7\u4e86\u4e00\u5929\uff0c\u6587\u5c9b\u4e3b\u518d\u6765\uff0c\u91d1\u4e16\u9057\u7834\u53e3\u5927\u9a82\uff0c\u7ed3\u679c\u518d\u6076\u6597\u4e00\u573a\uff0c\u5f53\u7136 \u8fd8\u662f\u91d1\u4e16\u9057\u8f93\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u542c\u91d1\u4e16\u9057\u8bf4\u5230\u8fd9\u91cc\uff0c\u63d2\u53e3\u8bf4\u9053\uff1a\u201c\u4e0d\u5999\u5440\u4e0d\u5999\uff0c\u91d1\u5927\u4fa0\uff0c\u4f60\u4e00\u800c\u518d\u3001\u518d\u800c\u4e09\u7684\u7740\u4e86 \u90a3\u59d3\u6587\u7684\u9053\u513f\u4e86\u3002\u4ed6\u662f\u89c1\u4f60\u4e0d\u80af\u5c06\u6b66\u529f\u79d8\u7c4d\u9ed8\u5199\u7ed9\u4ed6\uff0c\u6240\u4ee5\u624d\u60f3\u51fa\u8fd9\u4e2a\u6cd5\u5b50\u6765\u9a97\u4f60\u7684\u6b66 \u529f\u3002\u201d\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u6211\u4ee5\u4e3a\u4f60\u5168\u65e0\u673a\u5fc3\uff0c\u5374\u539f\u6765\u4f60\u4e5f\u8fd8\u6709\u5c0f\u806a\u660e\u3002\u54fc\uff0c\u54fc\uff0c\u6211\u4f55 \u5c1d\u770b\u4e0d\u51fa\u4ed6\u7684\u7528\u610f\u3002\u201d\u4ed6\u5374\u4e0d\u77e5\uff0c\u6c5f\u5357\u56e0\u4e3a\u4e0a\u8fc7\u5929\u9b54\u6559\u4e3b\u4f8d\u5973\u7684\u5f53\uff0c\u540c\u6837\u4ee5\u7422\u78e8\u6b66\u529f\u4e3a\u540d\uff0c \u5077\u4e86\u4ed6\u4e00\u4e9b\u672c\u9886\uff0c\u56e0\u6b64\u624d\u731c\u5230\u6587\u5c9b\u4e3b\u7684\u7528\u610f\u7684\u3002 \u3000\u3000\u91d1\u4e16\u9057\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4f46\u6211\u5f53\u65f6\u6b63\u5728\u706b\u6c14\u4e0a\u5934\uff0c\u4e5f\u5c31\u987e\u4e0d\u4e86\u8fd9\u4e9b\u4e86\u3002\u4e0d\u8fc7\u8bdd\u8bf4\u56de\u6765\uff0c\u4ed6\u56fa \u7136\u4ece\u6211\u8fd9\u513f\u5077\u5b66\u4e86\u4e00\u4e9b\u672c\u9886\uff0c\u6211\u4e5f\u770b\u51fa\u4e86\u4ed6\u8fd9\u95e8\u6b66\u529f\u7684\u4e00\u4e9b\u79d8\u5965\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u4f46\u603b\u662f\u4f60\u5403\u4e8f\u8f83\u5927\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7167\u5f53\u65f6\u7684\u60c5\u5f62\u6765\u8bf4\uff0c\u4e00\u6765\u6211\u5df2\u88ab\u4ed6\u56da\u7981\uff0c\u6211\u7684\u529f\u529b\u53c8\u672a\u66fe\u6062\u590d\uff0c\u867d \u7136\u5f7c\u6b64\u90fd\u4ece\u5bf9\u65b9\u5f97\u5230\u76ca\u5904\u3002\u800c\u6211\u5374\u5927\u5927\u5403\u4e8f\u4e86\u3002\u4f46\u90a3\u53ea\u662f\u6839\u636e\u5f53\u65f6\u7684\u60c5\u5f62\u800c\u8a00\uff0c\u73b0\u5728\u6765\u8bf4\uff0c \u5374\u662f\u56e0\u7978\u5f97\u798f\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u8bf4\u5462\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u548c\u4ed6\u6bd4\u8bd5\u4e86\u51e0\u6b21\uff0c\u4ed6\u7684\u79d8\u5965\u5df2\u61c2\u5f97\u4e86\u5341\u4e4b \u516d\u4e03\uff0c\u800c\u4ed6\u5462\uff0c\u636e\u6211\u770b\u6765\uff0c\u6700\u591a\u5f97\u4e86\u6211\u4e24\u4e09\u6210\u529f\u592b\u3002\u201d \u3000\u3000\u6c5f\u5357\u53c8\u9053\uff1a\u201c\u4f46\u4ed6\u662f\u4e2a\u574f\u4eba\uff0c\u4f60\u7ed9\u4ed6\u5077\u5b66\u4e86\u4e24\u4e09\u6210\u529f\u592b\uff0c\u4e5f\u589e\u52a0\u4ed6\u4f5c\u6076\u7684\u672c\u94b1\u4e86\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u7684\u56e0\u7978\u5f97\u798f\uff0c\u8fd8\u4e0d\u4ec5\u6b62\u662f\u4ece\u4ed6\u90a3\u513f\u5f97\u5230\u7684\u597d\u5904\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u7eed\u9053\uff1a\u201c\u4f60\u60f3\u5230\u7684\u6211\u5f53\u7136\u4e5f\u60f3\u5230\u4e86\uff0c\u90a3\u77f3\u7a9f\u7684\u94c1\u95e8\u662f\u91cc\u5916\u4e24\u9762\u90fd\u53ef\u4ee5\u5173\u9501\u7684\uff0c\u6211 \u548c\u4ed6\u8f83\u91cf\u51e0\u6b21\u5403\u4e8f\u4e4b\u540e\uff0c\u5c31\u7d22\u6027\u4ece\u91cc\u9762\u5173\u4e0a\u4e86\u95e8\uff0c\u62fc\u7740\u997f\u6b7b\uff0c\u4e5f\u4efb\u51ed\u4ed6\u767e\u822c\u8fb1\u9a82\uff0c\u90fd\u4e0d\u5f00\u95e8\u3002 \u3000\u3000\u201c\u90a3\u6587\u5c9b\u4e3b\u5927\u7ea6\u8fd8\u60f3\u4ece\u6211\u8fd9\u513f\u5077\u4e00\u4e9b\u529f\u592b\uff0c\u4e0d\u80af\u8ba9\u6211\u997f\u6b7b\u3002\u4ed6\u5728\u77f3\u7a9f\u4e0a\u65b9\u5f00\u4e86\u4e00\u4e2a\u5c0f \u6d1e\uff0c\u6bcf\u5929\u7528\u5c0f\u7bee\u5b50\u5c06\u98df\u7269\u540a\u4e0b\u6765\u3002\u201d \u3000\u3000\u6c5f\u5357\u56b7\u9053\uff0c\u201c\u4f60\u6709\u88c1\u4e91\u5b9d\u5251\uff0c\u5c82\u4e0d\u662f\u53ef\u4ee5\u628a\u6d1e\u53e3\u5f04\u5bbd\u4e86\u5c31\u9003\u51fa\u6765\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u90a3\u6587\u5c9b\u4e3b\u6bd4\u4f60\u66f4\u806a\u660e\uff0c\u4ed6\u5c82\u80af\u8ba9\u6211\u7559\u4e0b\u5b9d\u5251\uff1f\u65e9\u5728\u6211\u4e2d\u9152\u660f\u8ff7\u7684\u65f6\u5019\uff0c \u6211\u7684\u5b9d\u5251\u548c\u62a4\u8eab\u7389\u7532\u90fd\u5df2\u7ed9\u4ed6\u53d6\u53bb\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u540e\u6765\u662f\u8c01\u5c06\u4f60\u6551\u51fa\u77f3\u7a9f\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u5728\u8fd9\u5b64\u60ac\u6d77\u5916\u7684\u5c0f\u5c9b\u4e0a\u54ea\u6709\u4eba\u6765\u6551\u6211\uff1f\u662f\u6211\u81ea\u5df1\u60f3\u6cd5\u5b50\u9003\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u539f\u6765\u91d1\u4e16\u9057\u81ea\u5f97\u4e86\u4e54\u5317\u6e9f\u7684\u6b66\u529f\u79d8\u7c4d\u4e4b\u540e\uff0c\u5c31\u7acb\u5fd7\u8981\u878d\u4f1a\u5404\u5bb6\uff0c\u521b\u7acb\u4e00\u95e8\u6b63\u5927\u5149\u660e\u7cbe\u6df1 \u5e7f\u535a\u7684\u6b66\u529f\uff0c\u7ecf\u8fc7\u4e86\u51e0\u5e74\u7684\u94bb\u7814\uff0c\u5df2\u6e10\u6e10\u6709\u4e9b\u7709\u76ee\uff0c\u4f46\u8fd8\u6709\u51e0\u4e2a\u6b66\u5b66\u4e0a\u7684\u96be\u9898\uff0c\u5c1a\u672a\u80fd\u60f3\u5f97 \u901a\u900f\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5c31\u5229\u7528\u8fd9\u6bb5\u65f6\u95f4\uff0c\u5728\u77f3\u7a9f\u91cc\u6f5c\u601d\u9ed8\u7d22\uff0c\u65e5\u65e5\u7528\u529f\uff0c\u518d\u52a0\u4e0a\u4ed6\u4ece\u6587\u5c9b\u4e3b\u7684\u6b66\u5b66\u4e2d\u4e5f \u53c2\u609f\u4e86\u4e00\u4e9b\u9053\u7406\uff0c\u53ef\u4ee5\u4e0e\u4ed6\u4ee5\u524d\u6240\u5b66\u7684\u8109\u7d22\u76f8\u901a\uff0c\u5982\u662f\u8005\u8fc7\u4e86\u4e09\u4e2a\u6708\uff0c\u5728\u67d0\u4e00\u4e2a\u665a\u4e0a\uff0c\u4ed6\u7a81 \u7136\u8c41\u7136\u8d2f\u901a\uff0c\u4ee5\u524d\u8fd8\u672a\u60f3\u5f97\u901a\u900f\u7684\u96be\u9898\u90fd\u4e00\u4e00\u8fce\u5203\u800c\u89e3\uff01\u4ed6\u521b\u7acb\u4e86\u81ea\u5df1\u7684\u6b66\u5b66\uff0c\u90a3\u662f\u4ee5\u5929\u5c71 \u6d3e\u6b63\u5b97\u5185\u529f\u4e3a\u57fa\u77f3\uff0c\u4ee5\u4e54\u5317\u6e9f\u7684\u6b66\u529d\u79d8\u7c4d\u4e3a\u6881\u67f1\uff0c\u66f4\u52a0\u4e0a\u5176\u4ed6\u6b63\u90aa\u5404\u6d3e\u7684\u6b66\u529f\u4e3a\u5c4b\u74e6\u800c\u5efa\u7acb \u8d77\u6765\u7684\u3002\u4f46\u5df2\u4e0d\u540c\u4e8e\u4efb\u4f55\u4e00\u5bb6\uff0c\u800c\u662f\u771f\u771f\u6b63\u6b63\u5c5e\u4e8e\u91d1\u4e16\u9057\u81ea\u5df1\u7684\u6b66\u5b66\u4e86\u3002 \u3000\u3000\u5927\u529f\u544a\u6210\u4e4b\u540e\uff0c\u4ed6\u4e5f\u7ec3\u6210\u4e86\u91d1\u521a\u4e0d\u574f\u4e4b\u8eab\uff0c\u6240\u4e2d\u7684\u6bd2\uff0c\u4e5f\u5f88\u8f7b\u6613\u7684\u4fbf\u7ed9\u4ed6\u7528\u672c\u8eab\u7684\u771f\u706b \u70bc\u5316\u4e86\u3002\u529f\u529b\u4e0d\u4f46\u6062\u590d\uff0c\u800c\u4e14\u5927\u80dc\u4ece\u524d\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u4e2a\u665a\u4e0a\uff0c\u4ed6\u65bd\u5c55\u795e\u529f\uff0c\u6293\u88c2\u5ca9\u77f3\uff0c\u6253\u901a\u4e86\u4e00\u4e08\u591a\u6df1\u7684\u77f3\u58c1\uff0c\u9003\u51fa\u751f\u5929\uff01 \u3000\u3000\u6c5f\u5357\u542c\u5230\u8fd9\u91cc\uff0c\u5927\u559c\u53eb\u9053\uff1a\u201c\u90a3\u4f60\u8be5\u7ed9\u90a3\u6587\u5c9b\u4e3b\u82e6\u5934\u5403\u5403\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4f60\u4e0d\u5e9f\u53bb\u4ed6\u7684\u6b66 \u529f\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8bf4\u6211\u662f\u56e0\u7978\u5f97\u798f\u5417\uff1f\u8981\u4e0d\u662f\u4ed6\u5c06\u6211\u5173\u5728\u77f3\u7a9f\u91cc\uff0c\u6211\u8fd8\u6ca1\u6709\u8fd9\u4e48\u5feb \u7ec3\u6210\u5462\uff01\u800c\u4e14\u4ed6\u90a3\u65f6\u53ea\u662f\u5728\u6d77\u5c9b\u79f0\u96c4\uff0c\u5e76\u672a\u5230\u4e2d\u539f\u4f5c\u6076\uff0c\u6211\u53c8\u4f55\u5fc5\u8fc7\u4efd\u4e0e\u4ed6\u4e3a\u96be\uff01 \u3000\u3000\u201c\u6211\u51fa\u6765\u4e4b\u540e\uff0c\u518d\u4e0e\u4ed6\u6bd4\u6597\uff0c\u90a3\u53ae\u89c1\u6211\u80fd\u591f\u8131\u8eab\u800c\u51fa\uff0c\u65e9\u5df2\u5413\u5f97\u6015\u4e86\u3002\u4ed6\u7684\u6b66\u529f\u4e5f\u771f\u4e0d \u5f31\uff0c\u5c45\u7136\u8fd8\u80fd\u4e0e\u6211\u5468\u65cb\u4e86\u56db\u4e94\u5341\u62db\uff0c\u4f46\u7ec8\u4e8e\u7ed9\u6211\u628a\u4ed6\u6253\u5f97\u5927\u8d25\u3002 \u3000\u3000\u201c\u6211\u7d22\u56de\u4e86\u5b9d\u5251\u7389\u7532\uff0c\u53c8\u8981\u4e86\u4ed6\u4e00\u53ea\u88c5\u6ee1\u7cae\u98df\u7684\u5927\u8239\uff0c\u4fbf\u5373\u626c\u5e06\u5f52\u56fd\u3002\u201d \u3000\u3000\u6c5f\u5357\u56b7\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff0c\u4f60\u5bf9\u4ed6\u7684\u8d23\u7f5a\u771f\u662f\u592a\u8f7b\u4e86\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u90a3\u65f6\u6211\u8fd8\u4e0d\u77e5\u9053\u4ed6\u4eec\u53d4\u4f84\u540e\u6765\u7adf\u4f1a\u8d70\u5978\u76f8\u548c\u5764\u7684\u95e8\u8def\uff0c\u6765\u4e0e\u4e2d\u539f\u7684\u6b66\u6797\u4eba \u7269\u4e89\u96c4\u3002\u201d \u3000\u3000\u4ed6\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u5728\u56de\u6765\u7684\u6d77\u7a0b\u4e0a\u9047\u5230\u98ce\u66b4\uff0c\u53bb\u5e74\u6625\u521d\u624d\u56de\u5230\u4e2d\u571f\u3002\u90a3\u6587\u9053\u5e84\u5df2\u5148\u5230\u4e86 \u5317\u4eac\uff0c\u4ed6\u66ff\u548c\u7837\u62bc\u8fd0\u73e0\u5b9d\u7684\u4e8b\u60c5\u6211\u4e5f\u662f\u77e5\u9053\u7684\uff0c\u4e0d\u4e45\uff0c\u6211\u53c8\u63a2\u542c\u5f97\u4ed6\u7684\u53d4\u53d4\u4e5f\u6765\u5230\u4e86\u4e2d\u571f\uff0c \u6211\u4e00\u76f4\u5728\u6ce8\u610f\u4ed6\u4eec\u4e8c\u4eba\u7684\u884c\u8e2a\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u90a3\u4e48\u59ec\u6653\u98ce\u6025\u4e8e\u627e\u4f60\uff0c\u4f60\u53ef\u77e5\u9053\u5417\uff1f\u5c31\u662f\u56e0\u4e3a\u59ec\u6653\u98ce\u5077\u8d70\u6587\u9053\u5e84\u62bc\u8fd0\u7684\u73e0 \u5b9d\uff0c\u524d\u51e0\u5929\u5728\u65b0\u5b89\u9547\u4e0a\u95f9\u4e86\u4e00\u573a\u5927\u5927\u7684\u98ce\u6ce2\uff0c\u4e5f\u5c31\u662f\u5728\u90a3\u5929\u665a\u4e0a\uff0c\u6211\u548c\u59ec\u6653\u98ce\u518d\u6b21\u89c1\u9762\uff0c\u540e \u6765\u8fd8\u505a\u4e86\u62dc\u628a\u5144\u5f1f\u5462\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u8bf4\uff0c\u6211\u5168\u90fd\u77e5\u9053\u4e86\u3002\u53ea\u56e0\u90a3\u65f6\u6211\u5df2\u77e5\u9053\u5929\u9b54\u6559\u4e3b\u59ca\u59b9\u548c\u90a3\u6587\u5c9b\u4e3b \u90fd\u51c6\u5907\u5230\u6c13\u5c71\u6765\uff0c\u6211\u6ca1\u6709\u5de5\u592b\u548c\u59ec\u6653\u98ce\u89c1\u9762\u8be6\u8c08\uff0c\u5f85\u4ed6\u4ece\u7ec4\u6765\u5c71\u56de\u6765\u4e4b\u540e\uff0c\u6211\u81ea\u4f1a\u524d\u53bb\u89c1 \u4ed6\uff0c\u5e2e\u4ed6\u5b8c\u6210\u5fc3\u613f\u3002\u201d \u3000\u3000\u6c5f\u5357\u56e0\u4e3a\u5b69\u5b50\u843d\u5728\u5929\u9b54\u6559\u4e3b\u4e4b\u624b\uff0c\u6025\u4e8e\u77e5\u9053\u6709\u5173\u5929\u9b54\u6559\u4e3b\u4e4b\u4e8b\uff0c\u4fbf\u6487\u4e0b\u4e86\u90a3\u6587\u5c9b\u4e3b\u53d4 \u4f84\uff0c\u5148\u95ee\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60\u65e2\u77e5\u9053\u4e86\u5929\u9b54\u6559\u4e3b\u8981\u4e0e\u8c37\u5973\u4fa0\u4e3a\u96be\uff0c\u90a3\u4e48\u4f60\u56de\u6765\u4e4b\u540e\uff0c\u60f3\u5fc5\u66fe\u5230\u8fc7 \u7ec4\u6765\u5c71\u5979\u7684\u5de2\u7a74\u8bbf\u8fc7\u4e86\uff1f\u8fd9\u5929\u9b54\u6559\u4e3b\u7aef\u7684\u662f\u4ec0\u4e48\u6765\u5386\uff1f\u90a3\u7f2a\u592b\u4eba\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u8fd8\u672a\u5341\u5206\u6e05\u695a\uff0c\u4f46\u4ea6\u5df2\u7565\u77e5\u4e00\u4e8c\uff0c\u8bf4\u8d77\u6765\uff0c\u90a3\u5929\u9b54\u6559\u4e3b\u7684\u672c\u9886\u975e\u4f46\u4e0d\u662f \u5389\u80dc\u7537\u6240\u6388\uff0c\u5979\u4eec\u4e8c\u4eba\u7684\u4e0a\u4ee3\u8fd8\u662f\u4e16\u4ec7\u3002\u201d \u3000\u3000\u6c5f\u5357\u8be7\u9053\uff1a\u201c\u90a3\u4e48\u5979\u4e3a\u4ec0\u4e48\u53e3\u53e3\u58f0\u58f0\uff0c\u5949\u5389\u80dc\u7537\u4f5c\u5979\u4eec\u5927\u9b54\u6559\u7684\u7956\u5e08\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4e0e\u4e54\u5317\u6e9f\u3001\u970d\u5929\u90fd\u540c\u4e00\u4e2a\u65f6\u4ee3\u7684\uff0c\u8fd8\u6709\u4e00\u4e2a\u5f88\u5389\u5bb3\u7684\u5973\u9b54\u5934\uff0c\u5176\u5b9e\u8bf4\u662f\u5973 \u9b54\u5934\u4e5f\u4e0d\u5927\u9002\u5408\uff0c\u5979\u662f\u4e00\u4e2a\u4ecb\u4e4e\u90aa\u6b63\u4e4b\u95f4\u7684\u4eba\u7269\uff0c\u4e5f\u66fe\u521b\u7acb\u4e86\u4e00\u4e2a\u6559\uff0c\u540d\u53eb\u4e03\u9634\u6559\u4e3b\u3002\u8fd9\u4e03 \u9634\u6559\u4e3b\u6709\u4e2a\u5973\u513f\uff0c\u540d\u53eb\u9634\u79c0\u5170\uff0c\u4e54\u5317\u6e9f\u5f53\u5e74\u66fe\u4e3a\u4ed6\u7684\u513f\u5b50\u6c42\u5a5a\uff0c\u53d7\u5230\u9634\u79c0\u5170\u7684\u62d2\u7edd\uff0c\u4e54\u5317\u6e9f \u7684\u513f\u5b50\u628a\u5979\u786c\u62a2\u4e86\u53bb\uff0c\u540e\u6765\u5f97\u970d\u5929\u90fd\u592b\u5987\u6551\u56de\u3002\u90a3\u65f6\u5389\u80dc\u7537\u7684\u5148\u7956\u5389\u6297\u5929\u4e43\u662f\u4e54\u5317\u6e9f\u7684\u5fe0\u4ec6 \uff08\u62a2\u9634\u79c0\u5170\uff0c\u4ed6\u4e5f\u6709\u4efd\uff0c\u56e0\u6b64\u5389\u5bb6\u9634\u5bb6\u5b9e\u662f\u4e16\u4ec7\u3002\uff09 \u3000\u3000\u201c\u9634\u79c0\u5170\u540e\u6765\u53e6\u5916\u5ac1\u4e86\u4e00\u4e2a\u59d3\u5468\u7684\u5c11\u5e74\u82f1\u4fa0\uff0c\u59d3\u5468\u7684\u7236\u4eb2\u662f\u5f53\u65f6\u7684\u7eff\u6797\u9886\u8896\uff0c\u88ab\u5b98\u519b\u8ffd \u6355\uff0c\u9634\u79c0\u5170\u592b\u5987\u9003\u81f3\u585e\u5916\u897f\u57ce\u4e00\u4e2a\u5c0f\u56fd\u5b9a\u5c45\uff0c\u8fd9\u5929\u9b54\u6559\u4e3b\u4e43\u662f\u9634\u79c0\u5170\u7684\u540e\u88d4\uff0c\u4ed6\u4eec\u8fd9\u4e00\u5bb6\u56e0 \u4e3a\u4e16\u5c45\u897f\u57df\uff0c\u514d\u4e0d\u4e86\u548c\u80e1\u4eba\u901a\u5a5a\uff0c\u6545\u6b64\u8840\u7edf\u5f88\u6742\u3002 \u3000\u3000\u201c\u4e8b\u60c5\u62c9\u56de\u5341\u5e74\u4e4b\u524d\uff0c\u90a3\u65f6\u5389\u80dc\u7537\u56e0\u4e3a\u548c\u6211\u95f9\u7ffb\uff0c\u66fe\u6709\u4e00\u4e2a\u65f6\u671f\u72ec\u6e38\u585e\u5916\uff0c\u5927\u7ea6\u5c31\u662f\u90a3 \u4e2a\u65f6\u5019\uff0c\u5389\u80dc\u7537\u548c\u8fd9\u4f4d\u540e\u6765\u7684\u5929\u9b54\u6559\u4e3b\u7684\u5bb6\u4eba\u89c1\u4e86\u9762\uff0c\u5389\u80dc\u7537\u6069\u6028\u5206\u660e\uff0c\u53ef\u80fd\u662f\u56e0\u4e3a\u66ff\u7956\u5b97 \u8d4e\u7f6a\uff0c\u6545\u800c\u5c06\u539f\u6765\u5c5e\u4e8e\u9634\u5bb6\u7684\u4e00\u672c\u767e\u6bd2\u771f\u7ecf\u8fd8\u7ed9\u5979\u5bb6\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5979\u4eec\u7684\u6bd2\u836f\u6697\u5668\u5c42\u51fa\u4e0d\u7a77\uff0c\u539f\u6765\u90a3\u672c\u767e\u6bd2\u771f\u7ecf\u5df2\u662f\u843d\u5728\u5979\u4eec\u624b\u4e0a\u3002 \u55ef\uff0c\u4f60\u8bf4\u4e86\u8fd9\u534a\u5929\uff0c\u8fd8\u672a\u8bf4\u5230\u5979\u4eec\u53eb\u751a\u4e48\u540d\u5b57\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u5979\u4eec\u4e16\u5c45\u897f\u57df\uff0c\u5c5e\u4e8e\u9a6c\u8428\u513f\u4eba\u90e8\u843d\uff0c\u751f\u6d3b\u4e60\u60ef\u5dee\u4e0d\u591a\u4e0e\u80e1\u4eba\u540c\u5316\u4e86\u3002\u5979\u4eec \u7684\u540d\u5b57\uff0c\u6211\u662f\u4ece\u5979\u4eec\u65cf\u4eba\u90a3\u513f\u6253\u542c\u5230\u7684\u3002\u59d0\u59d0\u53eb\u5361\u5170\u59ae\uff0c\u59b9\u59b9\u53eb\u4f0a\u58c1\u73e0\u739b\u3002\u5979\u4eec\u8fd8\u6709\u6c49\u540d\uff0c \u4f46\u65cf\u4eba\u8bf4\u4e0d\u4e0a\u6765\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u5979\u4eec\u771f\u662f\u59d0\u59b9\u4e86\u3002\u90a3\u5361\u5170\u59ae\u771f\u7684\u662f\u4ec0\u4e48\u63d0\u7763\u592b\u4eba\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u8fd9\u4e2a\u5979\u5012\u6ca1\u6709\u8bf4\u8c0e\uff0c\u5979\u7684\u4e08\u592b\u540d\u53eb\u7f2a\u5357\u5ef7\uff0c\u4ee5\u524d\u505a\u8fc7\u4f0a\u7281\u5c06\u519b\uff0c\u73b0\u5728\u786e\u786e \u5b9e\u5b9e\u662f\u6cb3\u5357\u63d0\u7763\u3002\u5979\u7ed3\u5a5a\u90a3\u5e74\uff0c\u6b63\u662f\u5389\u80dc\u7537\u5728\u56de\u7586\u4e0e\u5979\u4eec\u89c1\u9762\u7684\u90a3\u4e00\u5e74\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u7eed\u9053\uff1a\u201c\u5389\u80dc\u7537\u4e0e\u5979\u4eec\u7684\u4ea4\u60c5\u5982\u4f55\uff0c\u6211\u4e0d\u77e5\u9053\uff0c\u4f46\u5979\u5c06\u767e\u6bd2\u771f\u7ecf\u8fd8\u7ed9\u5979\u4eec\uff0c\u60f3\u5fc5 \u65e9\u5df2\u5316\u654c\u4e3a\u53cb\u3002\u4e54\u5317\u6e9f\u79d8\u7c4d\u4e0a\u7684\u529f\u592b\uff0c\u5389\u80dc\u7537\u662f\u4e0d\u4f1a\u4f20\u7ed9\u5979\u4eec\u7684\uff0c\u4f46\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5979\u4eec\u5374\u53ef\u80fd \u77e5\u9053\u3002\u56e0\u6b64\u540e\u6765\u4f0a\u58c1\u73e0\u739b\u5c31\u5047\u501f\u5389\u80dc\u7537\u7684\u540d\u4e49\uff0c\u521b\u7acb\u4e86\u5929\u9b54\u6559\uff0c\u5c06\u5389\u80dc\u7537\u4ee5\u524d\u7684\u4f8d\u5973\u5168\u90fd\u7f51 \u7f57\u6559\u4e2d\u3002\u5979\u4eec\u6709\u5bb6\u4f20\u7684\u6b66\u529f\uff0c\u6536\u670d\u5389\u80dc\u7537\u90a3\u73ed\u4f8d\u5973\u4e4b\u540e\uff0c\u53c8\u5b66\u5230\u4e86\u4e00\u4e9b\u79d8\u7c4d\u4e0a\u7684\u672c\u9886\uff0c\u5f53\u7136 \u5c31\u66f4\u52a0\u5389\u5bb3\u4e86\u3002\u4e0d\u8fc7\u5979\u4eec\u59d0\u59b9\u540c\u51fa\u4e00\u6e90\uff0c\u59b9\u59b9\u7684\u6b66\u529f\u5374\u6bd4\u59d0\u59d0\u9ad8\u5341\u500d\uff0c\u4ec0\u4e48\u7f18\u6545\uff1f\u8fd9\u6211\u5374\u73b0 \u5728\u5c1a\u672a\u660e\u767d\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u5fc3\u4e2d\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u6211\u7ffc\u5e08\u5144\u6709\u4e2a\u670b\u53cb\uff0c\u662f\u4e2d\u725f\u53bf\u7684\u4e00\u4e2a\u5c0f\u5730\u4e3b\uff0c\u540d\u53eb\u4e18\u5ca9\uff0c\u4f60 \u53ef\u77e5\u9053\u8fd9\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f1a\u8fc7\u4e00\u9762\uff0c\u6b66\u529f\u4e0d\u7b97\u5f97\u597d\uff0c\u4e5f\u8fd8\u8fc7\u5f97\u53bb\u3002\u4f60\u4e3a\u4ec0\u4e48\u63d0\u8d77\u8fd9\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u6211\u60f3\u77e5\u9053\u4e18\u5ca9\u66fe\u5426\u5230\u8fc7\u56de\u7586\uff0c\u4e0e\u8fd9\u5bf9\u59d0\u59b9\u662f\u5426\u66fe\u7ecf\u76f8\u8bc6\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u5374\u4e0d\u77e5\u9053\u4e86\uff0c\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u6b63\u662f\u6709\u4ef6\u4e8b\u60c5\u4e0e\u4e18\u5ca9\u76f8\u5173\u7684\uff0c\u6162\u6162\u518d\u8bf4\u5427\u3002\u6211\u95ee\u4f60\uff0c\u5389\u80dc\u7537\u5f53\u5e74\u7ed9\u4f60\u7684\u90a3 \u74f6\u89e3\u836f\uff0c\u53ef\u8fd8\u5728\u4f60\u8eab\u4e0a\uff1f\u6211\u7684\u8c22\u5e08\u5ac2\u548c\u7518\u5e08\u5144\u90fd\u4e2d\u4e86\u90a3\u9b54\u5973\u7684\u6bd2\uff0c\u4e2d\u6bd2\u7684\u8ff9\u8c61\uff0c\u4e0e\u6211\u5f53\u5e74\u6240 \u53d7\u7684\u6bd2\u76f8\u540c\u3002\u201d \u3000\u3000\u6c5f\u5357\u57cb\u6028\u9053\uff1a\u201c\u8981\u662f\u4f60\u65e9\u6765\u4e00\u6b65\uff0c\u6211\u4eec\u5c31\u4e0d\u81f3\u4e8e\u5403\u90a3\u9b54\u5973\u7684\u82e6\u5934\u4e86\u3002\u6211\u4e0d\u660e\u767d\uff0c\u5728\u5c71\u8def \u4e0a\u6697\u4e2d\u52a9\u6211\uff0c\u5413\u9000\u4e86\u90a3\u7f2a\u592b\u4eba\u7684\u662f\u4e0d\u662f\u4f60\uff1f\u82e5\u7136\u662f\u4f60\uff0c\u4e3a\u4f55\u4f60\u53c8\u4e0d\u8ddf\u5979\u8fdb\u6765\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u4e8b\u5417\uff1f\u52a9\u4f60\u51fb\u8d25\u90a3\u4e24\u4e2a\u756a\u50e7\u7684\u662f\u6211\uff0c\u4f60\u5728\u5c71\u4e2d\u9047\u9669\uff0c \u6211\u5374\u4e0d\u77e5\uff01\u6211\u53ea\u6709\u4e00\u4e2a\u4eba\uff0c\u96be\u4ee5\u5206\u8eab\uff0c\u6211\u77e5\u9053\u5979\u4eec\u59ca\u59b9\u7684\u5389\u5bb3\uff0c\u4f46\u6599\u60f3\u4ee5\u4e4b\u534e\u7684\u672c\u9886\uff0c\u5927\u7ea6 \u8fd8\u4e0d\u81f3\u4e8e\u600e\u6837\u5403\u4e8f\uff0c\u6700\u5389\u5bb3\u7684\u662f\u6587\u5c9b\u4e3b\uff0c\u6240\u4ee5\u81ea\u4e0a\u6c13\u5c71\u4e4b\u540e\uff0c\u6211\u5c31\u4e00\u76f4\u5728\u6697\u4e2d\u7f00\u7740\u4ed6\u3002\u6211\u548c \u4ed6\u90fd\u662f\u5728\u5929\u9b54\u6559\u4e3b\u70ab\u9732\u63b7\u676f\u88c2\u6848\u7684\u529f\u592b\u4e4b\u65f6\u8fdb\u95e8\u7684\u3002\u4f60\u4eec\u6ca1\u6709\u53d1\u73b0\u90a3\u6587\u5c9b\u4e3b\uff0c\u90a3\u6587\u5c9b\u4e3b\u4e5f\u6ca1 \u6709\u53d1\u73b0\u6211\u3002\u201d \u3000\u3000\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u5c31\u771f\u5947\u602a\u4e86\uff01\u4f9d\u4f60\u8fd9\u4e48\u8bf4\uff0c\u8fd9\u4e2a\u6697\u52a9\u6211\u7684\u4eba\uff0c\u65e2\u4e0d\u662f\u4f60\uff0c\u4e5f\u4e0d\u662f\u90a3 \u59d3\u6587\u7684\u4e86\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4ed6\u662f\u5982\u4f55\u52a9\u4f60\uff1f\u201d\u6c5f\u5357\u5c06\u7ecf\u8fc7\u518d\u8bf4\u4e00\u904d\uff0c\u91d1\u4e16\u9057\u4e5f\u5927\u4e3a\u60ca\u8be7\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a \u201c\u8fd9\u4eba\u6709\u98de\u82b1\u6458\u53f6\u4e4b\u80fd\uff0c\u53c8\u61c2\u5929\u9041\u4f20\u97f3\u4e4b\u672f\uff0c\u8fd9\u53ef\u771f\u662f\u5947\u602a\u4e86\u3002\u96be\u9053\u53c8\u662f\u6587\u5c9b\u4e3b\u8fd9\u822c\u4eba\u7269\uff1f\u201d \u3000\u3000\u6c5f\u5357\u95ee\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u4f60\u5fc3\u76ee\u4e2d\u4ee5\u4e3a\u662f\u8c01\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u4e5f\u731c\u60f3\u4e0d\u5230\u3002\u770b\u6765\u8fd9\u4f4d\u670b\u53cb \u5927\u7ea6\u662f\u8981\u6765\u4f1a\u6211\u7684\uff0c\u7ec8\u987b\u6709\u4e2a\u6c34\u843d\u77f3\u51fa\u4e4b\u65e5\uff0c\u6682\u65f6\u4e14\u4e0d\u5fc5\u7406\u4ed6\u3002\u4e4b\u534e\uff0c\u4f60\u5148\u628a\u8fd9\u51e0\u9897\u89e3\u836f\u62ff \u53bb\u7ed9\u4f60\u7684\u5e08\u5ac2\u548c\u7518\u5e08\u5144\u5427\u3002\u5e78\u559c\u6211\u5e26\u5728\u8eab\u4e0a\uff0c\u5509\uff0c\u4e5f\u60f3\u4e0d\u5230\u4eca\u65e5\u8fd8\u8981\u7528\u5b83\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u63a5\u8fc7\u90a3\u534a\u74f6\u89e3\u836f\uff0c\u547d\u767d\u82f1\u6770\u9001\u53bb\uff0c\u5979\u4e0e\u91d1\u4e16\u9057\u90fd\u56e0\u4e3a\u8fd9\u74f6\u89e3\u836f\u800c\u60f3\u8d77\u4e86\u5389\u80dc\u7537\u7684 \u5f80\u4e8b\uff0c\u4e24\u4eba\u60f3\u6cd5\u4e0d\u540c\uff0c\u5374\u90fd\u662f\u9eef\u7136\u65e0\u8bed\u3002 \u3000\u3000\u6c5f\u5357\u95ee\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4f60\u521a\u624d\u8bf4\u5230\u5929\u9b54\u6559\u4e3b\uff0c\u4f60\u53ef\u66fe\u641c\u8fc7\u5979\u5728\u7ec4\u6765\u5c71\u7684\u5de2\u7a74\u4e48\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60\u7684\u513f\u5b50\u7ed9\u5979\u63b3\u53bb\uff0c\u8fd9\u4e8b\u60c5\u6211\u5df2\u77e5\u9053\u4e86\uff0c\u6211\u8fdb\u8fc7\u7ec4\u6765\u5c71\uff0c\u4e0d\u8fc7\uff0c\u90a3\u662f\u5728\u534a \u5e74\u4e4b\u524d\u3002\u6ca1\u6709\u89c1\u5230\u4f60\u7684\u513f\u5b50\u3002\u201d \u3000\u3000\u6c5f\u5357\u597d\u751f\u5931\u671b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7684\u513f\u5b50\u662f\u5728\u4e00\u4e2a\u6708\u524d\u7ed9\u5979\u7684\u4f8d\u5973\u63b3\u53bb\u7684\u3002\u59ec\u5927\u54e5\u5df2\u7ed9\u6211\u53bb\u627e \u4e86\uff0c\u53ea\u662f\u4ed6\u867d\u6709\u795e\u5077\u5999\u6280\uff0c\u5374\u4e0d\u662f\u5929\u9b54\u6559\u4e3b\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u59ec\u6653\u98ce\u662f\u795e\u884c\u592a\u4fdd\uff0c\u4ed6\u5230\u7ec4\u6765\u5c71\u7684\u65f6\u5019\uff0c\u5929\u9b54\u6559\u4e3b\u7a7a\u53ea\u6015\u8fd8\u672a\u80fd\u8d76\u56de\uff0c\u6b63 \u597d\u4e58\u865a\u800c\u5165\u3002\u4e0d\u8fc7\uff0c\u6c5f\u5357\uff0c\u4f60\u5374\u5c3d\u53ef\u653e\u5fc3\uff0c\u6211\u5df2\u7b54\u5e94\u6536\u4f60\u7684\u513f\u5b50\u505a\u5f92\u5f1f\uff0c\u6211\u5c31\u51b3\u4e0d\u80fd\u8ba9\u90a3\u5929 \u9b54\u6559\u4e3b\u5c06\u6211\u7684\u5f92\u513f\u63b3\u53bb\uff0c\u7eb5\u4f7f\u59ec\u6653\u98ce\u8981\u4e0d\u56de\u6765\uff0c\u4e5f\u5305\u5728\u6211\u8eab\u4e0a\u3002\u201d \u3000\u3000\u6c5f\u5357\u5f97\u4e86\u91d1\u4e16\u9057\u7684\u5141\u8bfa\uff0c\u5fc3\u4e0a\u6101\u4e91\u5c3d\u90fd\u6d88\u6563\u3002\u7b11\u9053\uff1a\u201c\u6709\u4f60\u7684\u4e00\u53e5\u8bdd\uff0c\u6bd4\u5929\u9b54\u6559\u4e3b\u66f4\u5389 \u5bb3\u5341\u500d\u7684\u654c\u4eba\uff0c\u6211\u4e5f\u4e0d\u4f1a\u5bb3\u6015\u4e86\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e8b\u672a\u660e\uff0c\u90a3\u6587\u5c9b\u4e3b\u65e2\u7136\u662f\u4e2a\u597d\u9669\u72e0\u6bd2\u4e4b\u5f92\uff0c\u4ed6\u53c8\u4e3a\u4f55\u52a9\u6211\u6597\u90a3\u5929\u9b54 \u6559\u4e3b\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4ed6\u7684\u7528\u610f\uff0c\u6211\u4e5f\u4e0d\u6562\u8bf4\u5b8c\u5168\u660e\u767d\u3002\u4e0d\u8fc7\uff0c\u636e\u6211\u770b\u6765\uff0c\u4ed6\u53ef\u80fd\u6709\u4e24\u4e2a\u76ee\u7684\uff0c \u4e00\u65b9\u9762\u662f\u8bd5\u8bd5\u90a3\u5929\u9b54\u6559\u4e3b\uff0c\u770b\u5979\u61c2\u5f97\u591a\u5c11\u79d8\u7c4d\u4e0a\u7684\u529f\u592b\uff1f\u53e6\u4e00\u65b9\u9762\u662f\u5411\u4f60\u793a\u60e0\uff0c\u4f01\u56fe\u9a97\u53d6\u4f60 \u7684\u5c11\u9633\u7384\u529f\u3002\u56e0\u4e3a\u5728\u90a3\u6d77\u5c9b\u4e0a\u65f6\uff0c\u4ed6\u66fe\u542c\u6211\u8bf4\u8fc7\uff0c\u77e5\u9053\u5929\u5c71\u6d3e\u7684\u5185\u529f\u5fc3\u6cd5\u548c\u4f60\u5e08\u7236\u6240\u7559\u4e0b\u7684 \u5c11\u9633\u7384\u529f\u4e09\u7bc7\uff0c\u4e43\u662f\u6700\u6df1\u5965\u7684\u6b63\u5b97\u5185\u529f\uff0c\u8981\u4e0d\u662f\u6211\u559d\u7834\u4ed6\uff0c\u4ed6\u53ef\u80fd\u771f\u4f1a\u5047\u5192\u6211\u7684\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u7b11\u9053\uff1a\u201c\u90a3\u65f6\uff0c\u6211\u5f53\u771f\u4ee5\u4e3a\u4ed6\u5c31\u662f\u4f60\u3002\u4e0d\u8fc7\uff0c\u4ed6\u82e5\u662f\u60f3\u9a97\u6211\uff0c\u4e3a\u4f55\u5728\u90a3\u5929\u9b54\u6559\u4e3b \u9003\u8d70\u4e4b\u65f6\uff0c\u4ed6\u53c8\u5306\u5306\u5fd9\u5fd9\u7684\u8ffd\u51fa\u53bb\u5462\uff0c\u90a3\u65f6\u6211\u8fd8\u4ee5\u4e3a\u662f\u4f60\u4e0d\u60f3\u7406\u776c\u6211\u5462\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u542c\u5979\u8bf4\u5f97\u67d4\u60c5\u8109\u8109\uff0c\u4e0d\u89c9\u5fc3\u4e2d\u4e00\u52a8\uff0c\u201c\u55ef\uff0c\u8fd9\u4e48\u591a\u5e74\u6765\uff0c\u5979\u5bf9\u6211\u8fd8\u672a\u5fd8\u6000\u3002\u201d\u773c \u5149\u4e00\u77a5\uff0c\u53ea\u89c1\u6c5f\u5357\u4f3c\u7b11\u975e\u7b11\u5730\u671b\u7740\u4ed6\u3002\u91d1\u4e16\u9057\u5b9a\u4e86\u5b9a\u795e\uff0c\u624d\u63a5\u4e0b\u6765\u8bf4\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u90a3\u65f6\u5df2\u53d1 \u73b0\u4e86\u6211\uff0c\u6216\u8005\u4ed6\u611f\u5230\u9a97\u4f60\u4e0d\u6613\uff0c\u5012\u4e0d\u5982\u5148\u53bb\u6253\u5929\u9b54\u6559\u4e3b\u7684\u4e3b\u610f\u3002\u201d \u3000\u3000\u8bf4\u5230\u6b64\u5904\uff0c\u767d\u82f1\u6770\u5df2\u7ecf\u56de\u6765\uff0c\u5411\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u8fd9\u89e3\u836f\u679c\u7136\u7075\u9a8c\u5df1\u6bd4\uff0c\u8c22\u5e08\u5ac2\u548c\u7518\u5e08\u5f1f\u90fd \u5df2\u9192\u8fc7\u6765\u4e86\uff0c\u7518\u5e08\u5f1f\u8fd8\u56b7\u7740\u809a\u5b50\u997f\u8981\u4e1c\u897f\u5462\u3002\u770b\u5149\u666f\u660e\u5929\u4fbf\u53ef\u4ee5\u590d\u539f\u4e86\uff0c\u4ed6\u4eec\u6258\u6211\u5411\u91d1\u5927\u4fa0 \u81f4\u8c22\u3002\u201d\u6c5f\u5357\u5ffd\u5730\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u767d\u5e08\u5144\uff0c\u4f60\u5e26\u6211\u53bb\u770b\u770b\u4ed6\u4eec\uff0c\u7518\u5e08\u5144\u66ff\u6211\u6321\u4e86\u4e00\u62db\uff0c\u7b49\u4e8e\u662f \u66ff\u6211\u53d7\u4e86\u4f24\uff0c\u6211\u5b9e\u5728\u8fc7\u610f\u4e0d\u53bb\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u4e5f\u60f3\u8bf4\u53bb\uff0c\u6c5f\u5357\u5df2\u5148\u8bf4\u9053\uff1a\u201c\u63a2\u75c5\u7684\u4eba\u4e0d\u5b9c\u5927\u591a\uff0c\u91d1\u5927\u4fa0\u4f60\u5750\u4f1a\u513f\uff0c\u6055\u6211\u5931\u966a \u4e86\u3002\u201d\u91d1\u4e16\u9057\u77e5\u4ed6\u5fc3\u610f\uff0c\u7b11\u4e86\u4e00\u7b11\uff0c\u4e5f\u4e0d\u518d\u8bf4\u4ec0\u4e48\uff0c\u4fbf\u7559\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u6c5f\u5357\u7b49\u4eba\u6258\u8f9e\u8d70\u5f00\uff0c\u623f\u95f4\u91cc\u4fbf\u53ea\u5269\u4e0b\u91d1\u4e16\u9057\u4e0e\u8c37\u4e4b\u534e\u5355\u72ec\u76f8\u5bf9\uff0c\u4e24\u4eba\u90fd\u611f\u5230\u4e07\u8bed\u5343\u8a00\uff0c \u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u8c37\u4e4b\u534e\u65b9\u54b3\u4e86\u4e00\u58f0\uff0c\u8f7b\u8f7b\u95ee\u9053\uff1a\u201c\u4e16\u9057\uff0c\u8fd9\u51e0\u5e74\u6765\u4f60\u53ef\u597d\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a \u201c\u597d\uff0c\u8fd9\u51e0\u5e74\u6765\u6211\u56db\u6d77\u4e3a\u5bb6\uff0c\u5012\u4e5f\u60ef\u4e86\u3002\u4f60\u770b\u6211\u6709\u4ec0\u4e48\u6539\u53d8\u6ca1\u6709\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u7b11\u9053\uff1a\u201c\u770b\u6765\u4f60\u662f\u6bd4\u4ee5\u524d\u8001\u6210\u591a\u4e86\u3002\u5927\u7ea6\u73b0\u5728\u4e0d\u50cf\u4ece\u524d\u90a3\u822c\u559c\u6b22\u6076\u4f5c\u5267\u4e86\u5427\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u6709\u65f6\u4e5f\u8fd8\u559c\u6b22\u6349\u5f04\u522b\u4eba\u7684\uff0c\u4e0d\u8fc7\u6124\u4e16\u5ac9\u4fd7\u7684\u5fc3\u60c5\u5374\u662f\u6ca1\u6709\u4e86\u3002\u4f60\u5462\uff0c\u8fd9 \u51e0\u5e74\u6765\u4f60\u4e5f\u597d\u4e48\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u6700\u521d\u505a\u638c\u95e8\u7684\u65f6\u5019\u611f\u5230\u4e0d\u60ef\uff0c\u73b0\u5728\u4e5f\u4e0d\u600e\u4e48\u4e86\u3002\u4f60\u4e5f\u770b\uff0c\u6211\u6709\u4ec0\u4e48\u6539\u53d8\u6ca1 \u6709\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60\u4e5f\u6bd4\u4ee5\u524d\u66f4\u6c89\u7740\u4e86\uff0c\u597d\u50cf\u4e8b\u4e8b\u90fd\u5f88\u6709\u4e3b\u610f\uff0c\u53eb\u4eba\u611f\u5230\u53ef\u4ee5\u4fe1\u8d56\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4ee5\u524d\u6211\u5bf9\u4e2a\u4eba\u7684\u4e8b\u60c5\u60f3\u5f97\u8f83\u591a\uff0c\u5728\u9047\u5230\u547d\u8fd0\u78e8\u6298\u7684\u65f6\u5019\uff0c\u5c31\u96be\u514d\u6d88\u6c89\u3002\u73b0 \u5728\u6211\u4ee5\u6211\u7684\u5e08\u7236\u4f5c\u4e3a\u5178\u8303\uff0c\u4e00\u5fc3\u4e00\u610f\u662f\u60f3\u5149\u5927\u672c\u95e8\uff0c\u57f9\u690d\u540e\u8f88\uff0c\u597d\u4e0e\u80e1\u864f\u5468\u65cb\uff0c\u529f\u6210\u4e0d\u5fc5\u5728 \u6211\uff0c\u603b\u6709\u4e00\u5929\uff0c\u53ef\u4ee5\u6062\u590d\u6c49\u5bb6\u65e7\u4e1a\u3002\u6211\u7684\u5fc3\u60c5\u6709\u4e86\u5bc4\u6258\uff0c\u4e5f\u5373\u662f\u5df2\u627e\u5230\u4e86\u5b89\u8eab\u7acb\u547d\u4e4b\u6240\u4e86\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u9690\u9690\u7684\u9053\u51fa\u4e86\u5979\u7684\u5fc3\u4e8b\uff0c\u90a3\u5373\u662f\u5979\u613f\u4f5c\u6c13\u5c71\u6d3e\u7684\u638c\u95e8\u4ee5\u7ec8\u8001\uff0c\u8fc7\u5f80\u7684\u60c5\u5b7d\uff0c\u90a3 \u5df2\u662f\u89c6\u5982\u8fc7\u773c\u4e91\u70df\uff0c\u4e1c\u6d41\u901d\u6c34\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5728\u5979\u9762\u524d\uff0c\u672c\u6765\u611f\u5230\u6709\u70b9\u513f\u5185\u759a\uff0c\u542c\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5fc3\u60c5\u8c41\u7136\u5f00\u6717\uff0c\u4e0d\u77e5\u4e0d\u89c9\u7684\u7d27 \u7d27\u6293\u4f4f\u4e86\u5979\u7684\u624b\u3002 \u3000\u3000\u8fd9\u662f\u51c0\u5316\u4e86\u7684\u611f\u60c5\uff0c\u5347\u534e\u4e86\u7684\u611f\u60c5\uff0c\u4e24\u4eba\u7d27\u7d27\u63e1\u7740\u624b\u513f\uff0c\u80f8\u4e2d\u6beb\u65e0\u6742\u5ff5\uff0c\u53ea\u662f\u6c89\u6d78\u5728\u5e78 \u798f\u7684\u611f\u89c9\u4e2d\uff0c\u90a3\u662f\u201c\u5f97\u4e00\u77e5\u5df1\uff0c\u4e00\u751f\u65e0\u61be\u201d\u7684\u5e78\u798f\u3002 \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4e4b\u534e\uff0c\u591a\u8c22\u4f60\u3002\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u591a\u8c22\u6211\u4ec0\u4e48\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u5728\u6d77\u5916\u98d8 \u6d41\uff0c\u5b64\u5355\u5355\u4e00\u4eba\uff0c\u6709\u65f6\u4e5f\u4f1a\u7a81\u7136\u611f\u5230\u60b2\u4ece\u4e2d\u6765\uff0c\u4e0d\u53ef\u65ad\u7edd\uff0c\u751f\u6d3b\u5728\u8fd9\u4e16\u754c\u4e0a\u4f3c\u4e4e\u6ca1\u6709\u4ec0\u4e48\u610f \u601d\uff0c\u6bcf\u5230\u8fd9\u79cd\u65f6\u523b\uff0c\u6211\u5c31\u4f1a\u60f3\u8d77\u4f60\u6765\uff0c\u4f60\u6bd4\u6211\u575a\u5f3a\u5f97\u591a\uff0c\u60f3\u8d77\u4f60\u6765\uff0c\u6211\u5c31\u4f1a\u575a\u5f3a\u4e86\u3002\u6211\u7ed9\u6587 \u5c9b\u4e3b\u5173\u5728\u77f3\u7a9f\u7684\u65f6\u5019\uff0c\u4e0e\u5176\u8bf4\u662f\u4ed6\u8feb\u6211\u7ec3\u6210\u6b66\u529f\uff0c\u4e0d\u5982\u8bf4\u662f\u56e0\u4e3a\u7531\u4e8e\u4f60\u7684\u9f13\u52b1\uff0c\u6211\u662f\u60f3\u8d77\u4e86 \u4f60\u5bf9\u6211\u7684\u671f\u671b\uff0c\u624d\u51b3\u5fc3\u7ec3\u6210\u6b66\u529f\uff0c\u6253\u7834\u7262\u7b3c\uff0c\u8fd8\u8981\u6d3b\u5728\u8fd9\u4e16\u754c\u4e0a\u505a\u4e00\u756a\u4e8b\u4e1a\u7684\u3002\u6240\u4ee5\uff0c\u4e4b \u534e\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u5728\u6d77\u5916\u98d8\u6d41\uff0c\u79bb\u5f00\u4f60\u4f3c\u4e4e\u5feb\u5f88\u8fdc\u5f88\u8fdc\uff0c\u4f46\u5b9e\u9645\u6765\u53c8\u662f\u5f88\u8fd1\u5f88\u8fd1\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u6211\u4e5f\u662f\u6bcf\u5929\u60e6\u8bb0\u4f60\u7684\uff0c\u6211\u62c5\u5fc3\u4ee5\u4f60\u90a3\u6837\u7684\u611f\u60c5\uff0c\u81ea\u5df1\u4e0d\u80fd\u63a7\u5236\u81ea\u5df1\uff0c\u78b0\u5230 \u91cd\u5927\u7684\u53d8\u6545\uff0c\u4f1a\u7a81\u7136\u50cf\u706b\u5c71\u822c\u7206\u53d1\u8d77\u6765\uff0c\u70e7\u6bc1\u4e86\u81ea\u5df1\u3002\u73b0\u5728\u6211\u53ef\u653e\u5fc3\u4e86\uff0c\u4f60\u5df2\u7ecf\u50cf\u5b69\u5b50\u957f\u5927 \u6210\u4eba\uff0c\u611f\u60c5\u4e5f\u6c89\u7a33\u575a\u539a\u4e86\uff0c\u770b\u5f97\u51fa\u4f60\u4e0d\u4f1a\u518d\u4efb\u6027\u800c\u4e3a\uff0c\u80e1\u95ef\u4e00\u901a\u3002\u4e16\u9057\uff0c\u6211\u606d\u8d3a\u4f60\u7ec3\u6210\u4e86\u7edd \u4e16\u6b66\u529f\uff0c\u679c\u7136\u4e0d\u8d1f\u6211\u5148\u5e08\u7684\u671f\u8bb8\u3002\u201d \u3000\u3000\u4e24\u4eba\u7ecf\u8fc7\u4e00\u756a\u503e\u5410\uff0c\u4f46\u89c9\u5f7c\u6b64\u5fc3\u610f\u76f8\u901a\uff0c\u611f\u60c5\u5230\u4e86\u66f4\u9ad8\u7684\u5883\u754c\u3002\u90a3\u662f\u6c5f\u5357\u6240\u60f3\u8c61\u4e0d\u5230\u7684 \u5883\u754c\uff0c\u6c5f\u5357\u662f\u5e0c\u671b\u4ed6\u4eec\u7834\u955c\u91cd\u5706\uff0c\u6210\u4e3a\u7231\u4fa3\u7684\uff0c\u800c\u73b0\u5728\u4ed6\u4eec\u7684\u611f\u60c5\u5df2\u662f\u51c0\u5316\u5347\u534e\uff0c\u8fdc\u8fdc\u8d85\u4e4e \u666e\u901a\u7231\u60c5\u4e4b\u4e0a\u3002\u8fd9\u79cd\u7ed3\u679c\uff0c\u6c5f\u5357\u77e5\u9053\u4e86\u6216\u8bb8\u4f1a\u5931\u671b\uff0c\u4f46\u8981\u662f\u4ed6\u80fd\u591f\u7406\u89e3\u7684\u8bdd\uff0c\u4ed6\u4e5f\u4f1a\u4e3a\u4ed6\u4eec \u611f\u5230\u5e78\u798f\u7684\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u5fc3\u91cc\u8f7b\u8f7b\u5ff5\u7740\u4e24\u53e5\u8bd7\uff1a\u201c\u4e2d\u5e74\u5fc3\u4e8b\u6d53\u5982\u9152\uff0c\u5c11\u5973\u60c5\u6000\u603b\u662f\u8bd7\u3002\u201d\u91d1\u4e16\u9057\u5df2\u8e0f\u8fdb\u4e2d \u5e74\uff0c\u800c\u5979\u4e5f\u5c06\u8fd1\u4e2d\u5e74\u4e86\uff0c\u5979\u6df1\u6df1\u7684\u611f\u89c9\u5230\uff0c\u91d1\u4e16\u9057\u5bf9\u5979\u7684\u611f\u60c5\u6bd4\u4ee5\u524d\u66f4\u4e3a\u6df1\u539a\uff0c\u50cf\u9152\u4e00\u6837\u7684 \u6d53\uff0c\u4e5f\u50cf\u9152\u4e00\u6837\u7684\u9187\uff01\u5982\u679c\u8bf4\u91d1\u4e16\u9057\u4ee5\u524d\u7684\u611f\u60c5\u4ee4\u5979\u6fc0\u52a8\u3001\u4ee4\u5979\u98a4\u6296\uff0c\u5982\u4eca\u5219\u662f\u4ee4\u5979\u611f\u5230\u9187 \u9152\u7684\u82b3\u9999\u4e86\u3002\u800c\u5979\u81ea\u5df1\u5462\uff0c\u4e5f\u79bb\u5f00\u4e86\u5c11\u5973\u7684\u65f6\u4ee3\u4e86\uff0c\u7f3a\u4e4f\u5c11\u5973\u90a3\u201c\u8bd7\u201d\u822c\u7684\u5e7b\u60f3\uff0c\u8c1c\u6837\u7684\u60c5 \u6000\uff0c\u4f46\u73b0\u5728\u5374\u662f\u628a\u63e1\u5f97\u4f4f\u7684\u611f\u60c5\uff0c\u90a3\u662f\u53e6\u4e00\u79cd\u201c\u7f8e\u5999\u201d\uff0c\u5e76\u4e0d\u900a\u4e8e\u4ee4\u4eba\u5fc3\u5f26\u98a4\u52a8\u7684\u8bd7\u7bc7\uff01 \u3000\u3000\u4e24\u4eba\u9ed8\u9ed8\u65e0\u8a00\uff0c\u76f8\u5bf9\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u91d1\u4e16\u9057\u8fd9\u624d\u60f3\u4e86\u8d77\u6765\uff0c\u95ee\u9053\uff0c\u201c\u4e4b\u534e\uff0c\u4f60\u521a\u624d\u95ee\u8d77\u4e2d \u725f\u53bf\u7684\u4e18\u5ca9\uff0c\u8bf4\u662f\u6709\u4e00\u4ef6\u5947\u602a\u7684\u4e8b\u60c5\u8981\u544a\u8bc9\u6211\uff0c\u90a3\u662f\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u6b63\u60f3\u7b54\u4ed6\uff0c\u5ffd\u542c\u5f97\u8c37\u4e2d\u83b2\u5728\u5185\u623f\u53eb\u9053\uff1a\u201c\u5988\u5988\uff0c\u5988\u5988\u3002\u201d\u539f\u6765\u5979\u5df2\u7ecf\u9192\u4e86\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u7b11\u9053\uff1a\u201c\u4e16\u9057\uff0c\u4f60\u5148\u89c1\u89c1\u6211\u7684\u5973\u513f\u5427\uff01\u201d\u91d1\u4e16\u9057\u8be7\u9053\uff1a\u201c\u4f60\u54ea\u91cc\u6765\u7684\u5973\u513f\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7ffc\u5e08\u5144\u4ece\u4e18\u5ca9\u5bb6\u4e2d\u5e26\u51fa\u6765\u7684\u4e00\u4f4d\u5b64\u5973\u3002\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f \u4f60\u7684\u517b\u5973\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u90a3\u5973\u5b69\u5b50\u5df2\u8d70\u4e86\u51fa\u6765\uff0c\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u83b2\u513f\uff0c\u5feb\u6765\u89c1\u8fc7\u91d1\u4f2f\u4f2f\u3002\u201d\u90a3\u5973\u5b69\u5b50\u7741 \u5927\u4e86\u773c\u775b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5c31\u662f\u91d1\u4e16\u9057\u4f2f\u4f2f\u5417\uff1f\u5988\u5988\u548c\u59d1\u59d1\u4eec\u5e38\u5e38\u63d0\u8d77\u4f60\uff0c\u4f60\u662f\u5929\u4e0b\u6700\u6709\u672c\u9886\u7684 \u4eba\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u5929\u5916\u6709\u5929\uff0c\u4eba\u5916\u6709\u4eba\u3002\u4efb\u51ed\u54ea\u4e00\u79cd\u672c\u9886\uff0c\u90fd\u6ca1\u6709\u8c01\u6562\u8bf4\u5929\u4e0b\u7b2c\u4e00\u7684\u3002\u201d \u4ed6\u8fb9\u7b11\u8fb9\u8bf4\uff0c\u76ee\u4e0d\u8f6c\u775b\u7684\u6ce8\u89c6\u7740\u90a3\u5973\u5b69\u5b50\uff0c\u773c\u5149\u4e2d\u5ffd\u7136\u9732\u51fa\u8be7\u5f02\u7684\u795e\u8272\uff0c\u8c37\u4e4b\u534e\u4e0d\u7531\u5f97\u611f\u5230 \u6709\u4e9b\u5947\u602a\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u83b2\u513f\uff0c\u4f60\u7ec3\u4e00\u5957\u7384\u5973\u638c\u7ed9\u91d1\u4f2f\u4f2f\u77a7\u77a7\u3002\u201d\u91d1\u4e16\u9057\u770b\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5973\u5b69\u4e8e \u662f\u5929\u751f\u7684\u7ec3\u6b66\u8d44\u8d28\uff0c\u6211\u9001\u5979\u4e00\u6837\u89c1\u9762\u793c\u5427\u3002\u201d\u8bf4\u7f62\uff0c\u62ff\u51fa\u4e86\u4e00\u672c\u8584\u8584\u7684\u5c0f\u518c\u5b50\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u6240\u53c2\u609f\u7684\u6b66\u5b66\uff0c\u5c1a\u672a\u66fe\u6574\u7406\u5c31\u7eea\uff0c\u4e5f\u672a\u6709\u5de5 \u592b\u90fd\u5199\u51fa\u6765\u3002\u4e0d\u8fc7\uff0c\u6253\u597d\u57fa\u7840\u7684\u5165\u95e8\u529f\u592b\u6211\u5df2\u5199\u597d\u4e24\u7ae0\u4e86\u3002\u8fd9\u662f\u878d\u4f1a\u4e86\u4e54\u5317\u6e9f\u79d8\u7c4d\u7684\u5965\u4e49\u548c \u5929\u5c71\u6d3e\u7684\u5185\u529f\u5fc3\u6cd5\u7684\uff0c\u4f60\u5e08\u7236\u7684\u5c11\u9633\u7384\u529f\u662f\u6700\u4e0a\u4e58\u7684\u6b63\u5b97\u5185\u529f\uff0c\u53ea\u662f\u5bf9\u4e8e\u521d\u5b66\u4e4b\u4eba\uff0c\u975e\u6709\u5341 \u5e74\u516b\u5e74\u7684\u529f\u592b\uff0c\u4e0d\u80fd\u767b\u5802\u4eba\u5ba4\u3002\u6211\u6240\u53c2\u609f\u7684\u6b66\u5b66\u4e0e\u4f60\u5e08\u7236\u7684\u5f02\u9014\u540c\u5f52\uff0c\u5bf9\u4e8e\u521d\u5b66\u4e4b\u4eba\uff0c\u4e5f\u8bb8 \u66f4\u6613\u5165\u624b\uff0c\u4fee\u4e60\u7684\u65f6\u95f4\u4e5f\u4f1a\u5feb\u4e9b\uff0c\u4e4b\u534e\uff0c\u8bf4\u8d77\u6765\u8fd9\u4e5f\u672c\u5e94\u662f\u4f60\u5bb6\u7684\u4e1c\u897f\uff0c\u5982\u4eca\u6211\u501f\u82b1\u732e\u4f5b\uff0c \u62ff\u6765\u7ed9\u4f60\uff0c\u4e5f\u5c31\u4f5c\u4e3a\u7ed9\u4f60\u5973\u513f\u7684\u89c1\u9762\u793c\u5427\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u8bf4\u201c\u8fd9\u672c\u5e94\u662f\u4f60\u5bb6\u7684\u4e1c\u897f\u201d\uff0c\u8fd9\u53e5\u8bdd\u6709\u4e2a\u7f18\u6545\uff0c\u56e0\u4e3a\u4e54\u5317\u6e9f\u7684\u6b66\u529f\u79d8\u7c4d\uff0c\u5f53\u521d\u6709 \u534a\u90e8\u843d\u5728\u8c37\u4e4b\u534e\u7684\u7236\u4eb2\u5b5f\u795e\u901a\u7684\u624b\u4e0a\uff0c\u540e\u6765\u7531\u5b5f\u795e\u901a\u7ed9\u4e86\u5973\u513f\uff0c\u8c37\u4e4b\u534e\u518d\u7ed9\u4e86\u91d1\u4e16\u9057\uff0c\u800c\u91d1 \u4e16\u9057\u7684\u6b66\u5b66\u5c31\u6709\u4e00\u5927\u90e8\u4efd\u662f\u4ece\u4e54\u5317\u6e9f\u7684\u6b66\u529f\u79d8\u7c4d\u518d\u53d1\u5c55\u51fa\u6765\u7684\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u542c\u4e86\u6b64\u8a00\uff0c\u4e0d\u65e0\u611f\u89e6\uff0c\u4f46\u8fd9\u662f\u7ed9\u5973\u513f\u7684\u89c1\u9762\u793c\uff0c\u56e0\u6b64\u4e5f\u5c31\u63a5\u4e0b\u4e86\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u91d1\u4f2f\u4f2f\uff0c\u4f60\u771f\u662f\u597d\u4eba\u3002\u6211\u542c\u767d\u5e08\u4f2f\u8bf4\uff0c\u4f60\u662f\u65e0\u5bb6\u53ef\u5f52\u3001\u5230\u5904\u6d6a\u8361\u7684\u3002\u4e0d\u5982 \u4f60\u4e5f\u548c\u6211\u4eec\u5728\u8fd9\u89c2\u4e2d\u4f4f\u4e0b\u6765\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u6211\u548c\u4f60\u7684\u5988\u5988\u662f\u597d\u670b\u53cb\uff0c\u5c31\u662f\u4e0d\u4f4f\u4e0b\u6765\uff0c\u6211\u4ee5\u540e\u4e5f\u4f1a\u5e38\u5e38\u6765\u770b\u4f60\u4eec\u7684\u3002\u201d \u3000\u3000\u6c5f\u5357\u5df2\u53bb\u4e86\u5927\u534a\u4e2a\u65f6\u8fb0\uff0c\u8fd8\u672a\u56de\u6765\u3002\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u83b2\u513f\uff0c\u4f60\u53bb\u7518\u5e08\u53d4\u90a3\u513f\u53eb\u6c5f\u53d4\u53d4\u56de \u6765\uff0c\u5dee\u4e0d\u591a\u662f\u5403\u996d\u7684\u65f6\u5019\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u8d70\u540e\uff0c\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4e16\u9057\uff0c\u4f60\u521a\u624d\u76ee\u4e0d\u8f6c\u775b\u7684\u77a7\u7740\u83b2\u513f\uff0c\u53ef\u662f\u89c9\u5f97\u6709\u4ec0\u4e48\u5f02\u6837\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u5979\u4e0d\u662f\u4e18\u5ca9\u7684\u4eb2\u751f\u5973\u513f\u5427\uff1f\u770b\u6765\u4e0d\u5927\u50cf\u662f\u6c49\u4eba\u7684\u5b69\u5b50\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4f60\u773c\u529b\u4e0d\u9519\uff0c\u77a7\u51fa\u6765\u4e86\u3002\u5979\u662f\u4e18\u5ca9\u4ece\u585e\u5916\u5e26\u56de\u6765\u7684\u4e00\u4e2a\u4e0d\u77e5\u6765\u5386\u7684\u5b64 \u5973\u3002\u201d\u91d1\u4e16\u9057\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u6c89\u541f\u4e0d\u8bed\uff0c\u4f3c\u4e4e\u8be7\u610f\u66f4\u6d53\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u4e5f\u4e0d\u7981\u8be7\u5f02\u8d77\u6765\uff0c\u5979\u7684\u8be7\u5f02\u5374\u6b63\u662f\u7531\u4e8e\u91d1\u4e16\u9057\u7684\u8be7\u5f02\u800c\u5f15\u8d77\u7684\u3002\u8981\u77e5\u8c37\u4e2d\u83b2\u5934\u6709 \u91d1\u53d1\uff0c\u773c\u73e0\u5fae\u78a7\uff0c\u53ea\u8981\u7559\u5fc3\u89c2\u5bdf\uff0c\u770b\u51fa\u5979\u5e76\u975e\u6c49\u4eba\u7684\u5b69\u5b50\uff0c\u8fd9\u5e76\u4e0d\u56f0\u96be\uff1b\u90a3\u4e48\uff0c\u4ece\u91d1\u4e16\u9057\u6df1 \u611f\u8be7\u5f02\u7684\u795e\u60c5\u770b\u6765\uff0c\u4ed6\u6240\u8be7\u5f02\u7684\u5f53\u4e0d\u53ea\u662f\u8fd9\u5b69\u5b50\u7684\u672c\u8eab\uff0c\u800c\u662f\u53e6\u6709\u5176\u4ed6\u539f\u56e0\u4e86\u3002\u90a3\u53c8\u662f\u4ec0\u4e48 \u5462\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60\u5148\u628a\u8fd9\u5b69\u5b50\u662f\u600e\u6837\u5f97\u6765\u7684\u7ecf\u8fc7\u544a\u8bc9\u6211\u5427\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u4f3c\u4e4e\u4ed6\u4e5f\u6709\u4e00\u4e9b \u4e8b\u60c5\u8981\u544a\u8bc9\u8c37\u4e4b\u534e\u3002 \u3000\u3000\u5f53\u4e0b\u8c37\u4e4b\u534e\u4fbf\u5c06\u7ffc\u4ef2\u725f\u600e\u6837\u8d74\u4e18\u5ca9\u4e4b\u7ea6\uff0c\u4e18\u5ca9\u600e\u6837\u81ea\u5c3d\u6258\u5b64\uff0c\u4ee5\u53ca\u7ffc\u4ef2\u725f\u56e0\u4e3a\u4e0d\u4fbf\u629a \u517b\uff0c\u6545\u800c\u5c06\u8fd9\u5b69\u5b50\u9001\u7ed9\u81ea\u5df1\u505a\u5973\u513f\u7b49\u7b49\u4e8b\u60c5\u8bf4\u4e86\uff0c\u5979\u56e0\u4e3a\u6025\u4e8e\u8981\u542c\u91d1\u4e16\u9057\u7684\uff0c\u6240\u4ee5\u8bf4\u7684\u53ea\u662f \u4e00\u4e2a\u5927\u6982\u7ecf\u8fc7\u7ec6\u8282\uff0c\u9057\u6f0f\u9887\u591a\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u662f\u4e0d\u662f\u8fd8\u6709\u4e00\u4e2a\u540c\u80de\u5144\u5f1f\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u5927\u611f\u60ca\u5947\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5fd8\u4e86\u544a\u8bc9\u4f60\u4e86\u3002\u5979\u662f\u6709\u4e00\u4e2a\u5b6a\u751f\u5144\u5f1f\uff0c\u7531\u9648\u7559 \u53bf\u7684\u53f6\u541b\u5c71\u6536\u517b\u3002\u54a6\uff0c\u4f60\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u90a3\u53f6\u541b\u5c71\u5462\uff1f\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u53f6\u541b\u5c71\u5df2\u7ed9\u4eba\u5bb3\u6b7b\u4e86\uff0c\u4ed6\u7684\u6b7b\u8fd8\u5728\u4e18\u5ca9\u4e4b\u524d \u51e0\u5929\uff0c\u51f6\u624b\u662f\u8c01\uff0c\u65e0\u4eba\u77e5\u6653\uff0c\u5b69\u5b50\u4e0b\u843d\uff0c\u4e5f\u4e0d\u77e5\u9053\u3002\u600e\u4e48\uff0c\u4f60\u6709\u6240\u77e5\u95fb\u4e48\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u6447\u6447\u5934\u9053\uff1a\u201c\u5bf9\u4e18\u5ca9\u3001\u53f6\u541b\u5c71\u4ee5\u53ca\u8fd9\u4e24\u4e2a\u5b69\u5b50\u7684\u4e8b\u60c5\uff0c\u6211\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002\u4f46\u542c\u4e86 \u4f60\u7684\u53d9\u8ff0\uff0c\u5374\u4ee4\u6211\u5fc6\u8d77\u4e00\u4ef6\u65e7\u95fb\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u90a3\u5e74\u6211\u56e0\u4e3a\u8bbf\u67e5\u5929\u9b54\u6559\u4e3b\u7684\u6765\u5386\uff0c\u66fe\u5728\u963f\u5c14\u6cf0\u5c71\u4e0b\u7684\u4e00\u4e2a\u5c0f\u56fd\u5bb6\u803d \u6401\u8fc7\u4e00\u4e9b\u65f6\u5019\uff0c\u90a3\u662f\u4e0e\u5929\u9b54\u6559\u4e3b\u540c\u4e00\u90e8\u65cf\u7684\u9a6c\u8428\u513f\u4eba\u6240\u5efa\u7acb\u7684\u4e00\u4e2a\u56fd\u5bb6\u3002\u542c\u5f97\u56fd\u4e2d\u4eba\u8bf4\uff0c\u4ed6 \u4eec\u7684\u56fd\u738b\u6b63\u5728\u8ffd\u67e5\u524d\u738b\u4e00\u5bf9\u513f\u5973\u7684\u4e0b\u843d\uff0c\u539f\u6765\u4ed6\u4eec\u7684\u56fd\u738b\u4e43\u662f\u524d\u738b\u5e73\u7ae0\uff08\u5b98\u540d\uff0c\u76f8\u7b49\u4e8e\u5bb0 \u76f8\uff09\uff0c\u56db\u5e74\u524d\u6740\u4e86\u56fd\u738b\u738b\u540e\uff0c\u7be1\u4f4d\u81ea\u7acb\u7684\uff0c\u4e3a\u4e86\u65a9\u8349\u9664\u6839\uff0c\u662f\u4ee5\u8ffd\u67e5\u524d\u738b\u7684\u513f\u5973\u3002\u6211\u53c8\u542c\u8fc7 \u4ed6\u4eec\u4e00\u4e9b\u7236\u8001\u7684\u79c1\u4e0b\u8c08\u8bdd\uff0c\u524d\u738b\u4f3c\u4e4e\u6bd4\u73b0\u5728\u7684\u56fd\u738b\uff0c\u8fdc\u5f97\u767e\u59d3\u7231\u6234\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u96be\u9053\u83b2\u513f\u7adf\u662f\u9a6c\u8428\u513f\u56fd\u7684\u516c\u4e3b\uff1f\u4f46\u6839\u636e\u5979\u7684\u8bb0\u5fc6\uff0c\u5979\u5c0f\u65f6\u5e76\u4e0d \u662f\u5728\u7687\u5bab\u4f4f\u7684\uff0c\u7236\u6bcd\u4e5f\u4e0d\u548c\u5979\u540c\u5728\u4e00\u8d77\uff0c\u5979\u7684\u6bcd\u4eb2\u53ea\u6765\u770b\u8fc7\u5979\u4e00\u6b21\uff0c\u8fd8\u662f\u665a\u4e0a\u5077\u5077\u5230\u5979\u6240\u4f4f \u7684\u5e10\u5e55\u6765\uff0c\u800c\u4e14\u8fd8\u4e0d\u6562\u8868\u9732\u8eab\u4efd\uff0c\u8fd9\u53c8\u662f\u4ec0\u4e48\u7f18\u6545\uff1f\u90a3\u65f6\u5979\u7684\u7236\u4eb2\u8fd8\u662f\u56fd\u738b\uff0c\u5978\u81e3\u8fd8\u672a\u66fe\u7be1 \u4f4d\u5440\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u5f53\u7136\u8fd8\u4e0d\u80fd\u65ad\u5b9a\u8fd9\u5b69\u5b50\u5c31\u662f\u9a6c\u8428\u513f\u56fd\u524d\u738b\u7684\u5973\u513f\uff0c\u6216\u8005\u8fd9\u53ea\u662f\u4e00\u4e2a\u5de7\u5408\uff0c \u4ed6\u6709\u4e00\u5bf9\u5b6a\u751f\u5b50\u5973\uff0c\u4f60\u7684\u5973\u513f\u4e5f\u6709\u4e2a\u5b6a\u751f\u7684\u54e5\u54e5\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u95ee\u9053\uff1a\u201c\u4f60\u521a\u624d\u8bf4\u90a3\u56fd\u738b\u662f\u5728\u56db\u5e74\u524d\u88ab\u6740\u5bb3\u7684\u4e48\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u8c37\u4e4b \u534e\u6c89\u541f\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u53c8\u662f\u4e00\u4e2a\u5de7\u5408\u4e86\uff0c\u6839\u636e\u83b2\u513f\u7684\u5fc6\u8ff0\uff0c\u4e5f\u662f\u5728\u56db\u5e74\u4e4b\u524d\uff0c\u90a3\u8349\u539f\u4e0a\u4f3c \u4e4e\u66fe\u53d1\u751f\u8fc7\u4e00\u573a\u4e48\u707e\u96be\uff0c\u5979\u5c31\u662f\u5728\u90a3\u4e00\u5e74\u88ab\u4e18\u5ca9\u4ece\u8349\u539f\u4e0a\u5e26\u8d70\u7684\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u5728\u897f\u57df\u8bf8\u79cd\u4eba\u4e2d\uff0c\u9a6c\u8428\u513f\u4eba\u8f83\u4f3c\u6c49\u4eba\uff0c\u4ed6\u4eec\u7684\u5b69\u5b50\u5927\u90fd\u957f\u5f97\u5f88\u79c0\u6c14\uff0c\u4f60\u7684 \u83b2\u513f\u662f\u6709\u70b9\u50cf\u9a6c\u8428\u513f\u4eba\u7684\u5b69\u5b50\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u7b11\u9053\uff1a\u201c\u542c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u7adf\u662f\u8d8a\u8bf4\u8d8a\u4f3c\u4e86\u3002\u8981\u662f\u83b2\u513f\u5f53\u771f\u662f\u4ec0\u4e48\u516c\u4e3b\uff0c\u6211\u53ef\u4e0d\u6562\u8981 \u5979\u505a\u5973\u513f\u4e86\u3002\u55ef\uff0c\u5173\u4e8e\u9a6c\u8428\u513f\u56fd\u90a3\u524d\u738b\uff0c\u4f60\u53ef\u8fd8\u77e5\u9053\u4e9b\u4ec0\u4e48\uff1f\u4ed6\u61c2\u4e0d\u61c2\u6b66\u529f\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u8fd9\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\uff0c\u4e0d\u8fc7\u8349\u539f\u4e0a\u7684\u5c45\u6c11\u90fd\u5584\u9a91\u5c04\uff0c\u914b\u957f\u751a\u81f3\u56fd\u738b\uff0c\u5728\u8fdb\u884c\u56f4 \u730e\u65f6\u4e5f\u662f\u4f17\u4eba\u7684\u9886\u8896\uff0c\u4e0d\u50cf\u4e2d\u56fd\u7684\u7687\u5e1d\u6df1\u5c45\u4e5d\u91cd\u7684\uff0c\u6240\u4ee5\u4ed6\u7684\u5f13\u9a6c\u529f\u592b\uff0c\u5927\u7ea6\u4e5f\u4e0d\u4f1a\u5dee\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u6211\u6240\u8bf4\u7684\u4e0d\u662f\u8fd9\u79cd\u5f13\u9a6c\u529f\u592b\uff0c\u662f\u54b1\u4eec\u6b66\u6797\u4eba\u7269\u7ec3\u7684\u8fd9\u79cd\u6b66\u529f\u3002\u201d\u91d1\u4e16\u9057\u95ee \u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u77e5\u9053\u8fd9\u4e00\u70b9\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u544a\u8bc9\u4ed6\uff0c\u90a3\u4ef6\u68c9\u8884\u4e0a\u7684\u94ae\u6263\uff0c\u4e43\u662f\u5bf9\u4fee\u4e60\u5185\u529f\u6700\u6709\u6548\u7684\u201c\u5929\u5fc3\u77f3\u201d\uff0c\u91d1\u4e16\u9057\u7acb\u523b \u660e\u767d\u5979\u7684\u610f\u601d\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5018\u82e5\u5728\u6b66\u5b66\u4e0a\u6709\u6df1\u6e5b\u7684\u9020\u8be3\uff0c\u800c\u4e14\u8fd8\u8981\u662f\u89c1\u95fb\u5e7f\u535a\u7684\u4eba\uff0c\u51b3 \u4e0d\u4f1a\u4e0d\u77e5\u9053\u8fd9\u5929\u5fc3\u77f3\u4e43\u662f\u5f02\u5b9d\uff0c\u4e5f\u51b3\u4e0d\u61c2\u5f97\u600e\u6837\u7528\u5b83\u3002\u4f9d\u6211\u60f3\u6765\uff0c\u90a3\u9a6c\u8428\u513f\u56fd\u7684\u524d\u738b\uff0c\u603b\u4e0d \u4f1a\u662f\u4f4d\u6b66\u5b66\u5927\u5e08\u5427\uff1f\u201d \u3000\u3000\u867d\u7136\u6709\u51e0\u4e2a\u7591\u70b9\uff0c\u4f46\u201c\u5de7\u5408\u201d\u4e4b\u5904\u4e5f\u591a\uff01\u8c37\u4e2d\u83b2\u662f\u5426\u9a6c\u8428\u513f\u56fd\u56fd\u738b\u7684\u5973\u513f\uff0c\u5b9e\u5728\u96be\u4ee5\u65ad \u5b9a\uff01\u8c37\u4e4b\u534e\u82e6\u7b11\u9053\uff1a\u201c\u5979\u82e5\u662f\u516c\u4e3b\uff0c\u8eab\u4efd\u867d\u7136\u9ad8\u8d35\uff0c\u9ebb\u70e6\u53ef\u5c31\u591a\u4e86\u3002\u4f46\u613f\u5979\u7684\u547d\u8fd0\u4e0d\u4f3c\u6211\u7684 \u574e\u5777\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u53ef\u60dc\u6211\u53ea\u7565\u61c2\u897f\u57df\u8bf8\u56fd\u7684\u8bed\u8a00\uff0c\u4e0d\u901a\u4ed6\u4eec\u7684\u6587\u5b57\u3002\u82e5\u8981\u786e\u5b9a\u4f60\u7684\u83b2\u513f\u7684\u8eab \u4efd\uff0c\u6050\u6015\u53ea\u6709\u7b49\u5f85\u9648\u5929\u5b87\u6765\uff0c\u8ba9\u4ed6\u770b\u4e00\u770b\u90a3\u4e00\u7eb8\u7f8a\u76ae\u4e66\u4e86\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u53c8\u9053\uff1a\u201c\u6211\u51c6\u5907\u5148\u53bb\u66ff\u6c5f\u5357\u8981\u56de\u5b69\u5b50\uff0c\u7136\u540e\u518d\u4e0e\u4ed6\u540c\u53bb\u627e\u9648\u5929\u5b87\u3002\u201d\u8bf4\u8d77\u6c5f\u5357\uff0c \u8c37\u4e4b\u534e\u770b\u4e86\u770b\u7a97\u5916\u7684\u5929\u8272\uff0c\u7b11\u9053\uff1a\u201c\u50bb\u5c0f\u5b50\u600e\u7684\u73b0\u5728\u8fd8\u4e0d\u56de\u6765\uff1f\u201d\u91d1\u4e16\u9057\u4e5f\u7b11\u9053\uff1a\u201c\u4f60\u522b\u602a \u4ed6\uff0c\u4ed6\u662f\u4e00\u7247\u597d\u5fc3\uff0c\u4ed6\u5927\u7ea6\u4ee5\u4e3a\u6211\u6709\u4e00\u5927\u8f66\u5b50\u7684\u79c1\u623f\u8bdd\u8981\u548c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u521a\u8bf4\u5230\u6b64\u5904\uff0c\u6c5f\u5357\u7684\u58f0\u97f3\u5df2\u4ece\u5916\u9762\u4f20\u6765\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u53c8\u6709\u4e00\u4ef6\u7a00\u5947\u7684\u4e8b\u60c5\u4e86\uff01\u201d\u63a5\u7740\u662f \u8c37\u4e2d\u83b2\u7684\u58f0\u97f3\u9053\uff1a\u201c\u5988\uff0c\u9759\u7f18\u59d1\u59d1\u6765\u770b\u4f60\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u4f60\u603b\u662f\u5927\u60ca\u5c0f\u602a\u7684\uff0c\u53c8\u6709\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u8c37\u4e2d\u83b2\u5df2\u8e66\u8e66 \u8df3\u8df3\u5730\u8d70\u8fdb\u5c4b\u5b50\uff0c\u540e\u9762\u662f\u4e00\u4e2a\u8001\u5c3c\u59d1\uff0c\u91d1\u4e16\u9057\u8ba4\u5f97\u5979\u662f\u8c37\u4e4b\u534e\u7684\u5e08\u59d0\u9759\u7f18\u3002 \u3000\u3000\u9759\u7f18\u5728\u534a\u5c71\u7684\u836f\u738b\u5e99\u5f53\u4e3b\u6301\uff0c\u8c37\u4e4b\u534e\u89c1\u5979\u5230\u6765\uff0c\u9887\u89c9\u610f\u5916\uff0c\u7b11\u9053\uff1a\u201c\u4eca\u5929\u4e0d\u662f\u836f\u738b\u8bde \u5417\uff1f\u4f60\u4e00\u5b9a\u5fd9\u4e86\u4e00\u6574\u5929\u4e86\uff0c\u5374\u600e\u7684\u8fd9\u4e48\u665a\u4e86\uff0c\u8fd8\u4e0a\u7384\u5973\u89c2\u6765\uff1a\u54c8\uff0c\u6c5f\u5357\uff0c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u8fd9\u5012 \u662f\u4ef6\u65b0\u9c9c\u4e8b\u513f\u3002\u201d \u3000\u3000\u9759\u7f18\u9053\uff1a\u201c\u6211\u6765\u4e86\u6709\u534a\u4e2a\u65f6\u8fb0\u4e86\uff0c\u5148\u53bb\u770b\u4e86\u8c22\u5e08\u5ac2\u548c\u7518\u5e08\u5f1f\u7684\u75c5\uff0c\u5509\uff0c\u60f3\u4e0d\u5230\u4f60\u4eec\u8fd9\u91cc \u4e5f\u53d1\u751f\u4e86\u610f\u5916\u7684\u4e8b\u60c5\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u836f\u738b\u5e99\u53d1\u751f\u4e86\u4ec0\u4e48\u610f\u5916\uff1f\u201d\u8fd9\u65f6\u6c5f\u5357\u548c\u767d\u82f1\u6770\u4ea6\u5df2\u8fdb\u4e86\u5c4b \u5b50\uff0c\u6c5f\u5357\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u8fd9\u53ef\u4e0d\u662f\u6211\u5927\u60ca\u5c0f\u602a\u4e86\u5427\uff1f\u201d \u3000\u3000\u9759\u7f18\u9053\uff1a\u201c\u836f\u738b\u5e99\u5012\u6ca1\u6709\u4ec0\u4e48\u610f\u5916\uff0c\u800c\u662f\u730e\u6237\u4eec\u78b0\u5230\u4e86\u610f\u5916\uff0c\u6709\u597d\u51e0\u4e2a\u730e\u6237\u88ab\u5927\u7329\u7329\u6293 \u4f24\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u5012\u5947\u4e86\uff0c\u6c13\u5c71\u54ea\u91cc\u6765\u7684\u5927\u7329\u7329\uff1f\u201d \u3000\u3000\u9759\u7f18\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6c13\u5c71\u662f\u4ece\u672a\u53d1\u73b0\u8fc7\u7329\u7329\u7684\uff0c\u8fd9\u4e24\u5934\u5927\u7329\u7329\u662f\u5916\u4eba\u5e26\u6765\u7684\u3002\u201d\u5f53\u4e0b\uff0c\u679c \u771f\u8bf4\u51fa\u4e86\u4e00\u4ef6\u7a00\u5947\u53e4\u602a\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u539f\u6765\u5728\u836f\u738b\u5e99\u5468\u56f4\u4f4f\u6709\u51e0\u5341\u5bb6\u730e\u6237\uff0c\u6628\u5929\u665a\u4e0a\uff0c\u5ffd\u7136\u542c\u5f97\u8001\u864e\u7684\u543c\u58f0\uff0c\u90ae\u5c71\u539f\u6709\u8001\u864e\uff0c \u4e0d\u8db3\u4e3a\u5947\uff0c\u5947\u602a\u7684\u662f\u6ee1\u5c71\u90fd\u662f\u8001\u864e\u7684\u543c\u58f0\uff0c\u542c\u6765\u603b\u6709\u767e\u6570\u5341\u5934\uff01\u8001\u864e\u7684\u4e60\u6027\u662f\u4e0d\u559c\u6b22\u6210\u7fa4\u7ed3 \u961f\u7684\uff0c\u800c\u4e14\u7531\u4e8e\u8fd9\u51e0\u5e74\u6765\u8001\u864e\u8d8a\u6253\u8d8a\u5c11\uff0c\u730e\u6237\u4eec\u8981\u5bfb\u89c5\u8001\u864e\u7684\u8e2a\u8ff9\u5df2\u5f88\u56f0\u96be\uff0c\u800c\u73b0\u5728\u5374\u4e00\u6765 \u5c31\u662f\u4e00\u5927\u7fa4\uff01 \u3000\u3000\u8fd9\u4e9b\u730e\u6237\u90fd\u662f\u4e16\u4ee3\u76f8\u4f20\u7684\u730e\u864e\u597d\u624b\uff0c\u767b\u65f6\u805a\u96c6\u8d77\u56db\u4e94\u5341\u4e2a\u7cbe\u58ee\u7537\u5b50\uff0c\u51c6\u5907\u4e86\u89c1\u8840\u5c01\u5589\u7684 \u6bd2\u7bad\uff0c\u950b\u5229\u7684\u523a\u864e\u53c9\uff0c\u5c31\u4e0a\u5c71\u730e\u864e\u3002 \u3000\u3000\u9759\u7f18\u8bf4\u9053\uff1a\u201c\u5947\u602a\u7684\u4e8b\u60c5\u6765\u4e86\uff0c\u90a3\u4e9b\u730e\u6237\u8fdb\u4e86\u6811\u6797\uff0c\u53ea\u89c1\u8001\u864e\u4e09\u4e94\u6210\u7fa4\uff0c\u6ee1\u5c71\u4e71\u7a9c\uff0c\u730e \u6237\u4eec\u5927\u7740\u80c6\u5b50\uff0c\u622a\u4f4f\u843d\u5355\u7684\u51e0\u53ea\u8001\u864e\uff0c\u94a2\u53c9\u6bd2\u7bad\uff0c\u4fbf\u98de\u8fc7\u53bb\uff0c\u5c04\u4f24\u4e86\u51e0\u53ea\u8001\u864e\u3002\u730e\u6237\u4eec\u6b63\u8981 \u53bb\u62d6\u67ef\u6765\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u517d\u543c\uff0c\u6709\u5982\u9752\u592d\u8d77\u4e86\u4e2a\u9739\u96f3\uff0c\u9707\u8033\u6b32\u804b\uff0c\u52a8\u6127\u60ca\u5fc3\uff0c\u6bd4\u8001\u864e\u7684\u543c\u58f0\u66f4 \u4e3a\u53ef\u6015\uff01 \u3000\u3000\u201c\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u65cb\u98ce\u822c\u7684\u6765\u4e86\u4e24\u53ea\u602a\u517d\u3002\u540e\u8e44\u76f4\u7acb\uff0c\u5176\u72b6\u5982\u4eba\uff0c\u6ee1\u8eab\u91d1\u6bdb\uff0c\u5b83\u543c\u7684\u58f0 \u4e00\u8d77\uff0c\u672a\u53d7\u4f24\u7684\u8001\u864e\u5c3d\u90fd\u530d\u4f0f\u3002 \u3000\u3000\u201c\u730e\u6237\u5927\u60ca\uff0c\u89c1\u8840\u5c01\u5589\u6bd2\u7bad\u7eb7\u7eb7\u5c04\u53bb\uff0c\u54ea\u77e5\u8fd9\u4e24\u4e2a\u602a\u517d\u7adf\u662f\u8eab\u575a\u5982\u94c1\uff0c\u5200\u7bad\u4e0d\u5165\uff0c\u6bd2\u7bad \u5c04\u4e2d\u5b83\u4eec\u7684\u8eab\u5b50\uff0c\u5168\u90fd\u53cd\u5c04\u56de\u6765\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u8fd9\u602a\u517d\u4e0d\u662f\u7329\u7329\uff0c\u5b83\u53eb\u505a\u91d1\u6bdb\u68ad\u3002\u201d\u9759\u7f18\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u89c1\u8fc7\u8fd9\u79cd\u602a\u517d\uff1f\u201d \u91d1\u4e16\u9057\u9053\uff1a\u201c\u5927\u7ea6\u662f\u5341\u5e74\u4e4b\u524d\uff0c\u6211\u5728\u4e00\u4e2a\u5c9b\u4e0a\u89c1\u8fc7\u4e24\u53ea\u91d1\u6bdb\u5506\u3002\u5b83\u4e13\u957f\u98df\u72ee\u864e\u7684\u8111\u5b50\uff0c\u6240\u4ee5 \u8001\u864e\u89c1\u4e86\u5b83\uff0c\u5c31\u50cf\u8001\u9f20\u89c1\u4e86\u732b\u4e00\u822c\u3002\u55ef\uff0c\u6c13\u5c71\u7adf\u6709\u91d1\u6bdb\u6d5a\u51fa\u73b0\uff0c\u8fd9\u771f\u7b97\u5f97\u4e0a\u662f\u5947\u602a\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u9759\u7f18\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u66f4\u5947\u602a\u7684\u5728\u540e\u5934\u5462\uff0c\u730e\u6237\u4eec\u89c1\u90a3\u4e24\u53ea\u602a\u517d\u5982\u6b64\u5389\u5bb3\uff0c\u8fde\u6bd2\u7bad\u4e5f\u5948 \u4f55\u5b83\u4eec\u4e0d\u5f97\uff0c\u8fd8\u7ed9\u53cd\u5c04\u56de\u6765\uff0c\u5c3d\u90fd\u614c\u4e86\uff0c\u5e78\u4e8f\u53cd\u5c04\u56de\u6765\u7684\u6bd2\u7bad\u6b20\u7f3a\u51c6\u5934\uff0c\u5426\u5219\u66f4\u4e0d\u582a\u8bbe\u60f3\u3002 \u3000\u3000\u201c\u730e\u6237\u4eec\u53d1\u4e00\u58f0\u558a\uff0c\u56db\u6563\u9003\u53bb\uff0c\u4ed6\u4eec\u7684\u8eab\u624b\u6bd4\u5e38\u4eba\u77eb\u6377\u5f97\u591a\uff0c\u4f46\u5374\u600e\u907f\u5f97\u5f00\u90a3\u4e24\u53ea\u884c\u52a8 \u5982\u98ce\u7684\u602a\u517d\uff1f\u90a3\u4e24\u53ea\u602a\u517d\u4f3c\u662f\u4e3a\u4ed6\u4eec\u6bd2\u7bad\u6240\u6fc0\u6012\uff0c\u53d1\u4e2a\u6012\u543c\uff0c\u89c1\u4eba\u5c31\u6293\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u5fc3\u6027\u4ec1\u6148\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u53ef\u6709\u730e\u6237\u9001\u4e86\u547d\u4e48\uff1f\u201d \u3000\u3000\u9759\u7f18\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u5c31\u5728\u90a3\u4e24\u53ea\u602a\u517d\u8086\u8650\u4e4b\u65f6\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u6709\u4eba\u559d\u9053\uff1a\u2018\u53ea\u51c6\u4f24 \u864e\uff0c\u4e0d\u51c6\u4f24\u4eba\uff01\u2019\u8bf4\u4e5f\u5947\u602a\uff0c\u90a3\u4e24\u53ea\u602a\u517d\u4fbf\u4f3c\u90a3\u4eba\u517b\u719f\u4e86\u7684\u5bb6\u755c\u4e00\u822c\uff0c\u61c2\u5f97\u4e3b\u4eba\u7684\u8a00\u8bed\uff0c\u542c \u5f97\u559d\u58f0\uff0c\u4fbf\u7acb\u5373\u505c\u4f4f\u4e86\u624b\u3002 \u3000\u3000\u91d1\u4e16\u9057\u95ee\u9053\uff1a\u201c\u90a3\u662f\u4e2a\u4ec0\u4e48\u6a21\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u9759\u7f18\u9053\uff1a\u201c\u4f17\u730e\u6237\u53ea\u95fb\u5176\u58f0\u800c\u4e0d\u89c1\u5176\u4eba\uff0c\u800c\u4e14\u5728\u90a3\u65f6\u5019\uff0c\u8c01\u90fd\u5fd9\u7740\u9003\u547d\uff0c\u54ea\u8fd8\u6709\u5de5\u592b\u627e \u4eba\u3002\u5e78\u800c\u90a3\u4eba\u51fa\u58f0\u963b\u6b62\uff0c\u6765\u5f97\u53ca\u65f6\uff0c\u65e0\u4eba\u4e27\u547d\uff0c\u4e0d\u8fc7\uff0c\u4e5f\u5df2\u6709\u5341\u591a\u4e2a\u4eba\uff0c\u4f24\u5728\u5b83\u4eec\u7684\u722a\u4e0b\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u95ee\u9053\uff1a\u201c\u90a3\u4e24\u53ea\u602a\u517d\u5462\uff1f\u201d \u3000\u3000\u9759\u7f18\u9053\uff1a\u201c\u90a3\u4e24\u53ea\u602a\u517d\u820d\u4e86\u730e\u4eba\uff0c\u518d\u53bb\u8ffd\u9010\u731b\u864e\uff0c\u5c06\u90a3\u4e9b\u4e09\u4e94\u6210\u7fa4\u3001\u6ee1\u5c71\u4e71\u7a9c\u7684\u731b\u864e\u90fd \u8d76\u5230\u4e00\u5904\uff0c\u5c31\u50cf\u62bc\u7740\u4fd8\u864f\u4e00\u6837\uff0c\u7ffb\u5c71\u8d8a\u5cad\u8dd1\u4e86\uff01\u201d \u3000\u3000\u4f17\u4eba\u542c\u4e86\uff0c\u65e0\u4e0d\u60ca\u9a87\uff0c\u767d\u82f1\u6770\u9053\uff1a\u201c\u90a3\u4eba\u867d\u4e0d\u77e5\u662f\u597d\u662f\u574f\uff0c\u4f46\u7ed9\u90a3\u4e24\u53ea\u602a\u517d\u85cf\u5728\u672c\u5c71\uff0c \u603b\u662f\u4e0d\u59a5\u3002\u638c\u95e8\uff0c\u4f60\u770b\u8be5\u5982\u4f55\u5bf9\u4ed8\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u5148\u6551\u4e86\u90a3\u4e9b\u730e\u6237\uff0c\u7136\u540e\u518d\u53bb\u641c\u67e5\u90a3\u4e00\u4eba\u4e8c\u517d\u7684\u884c\u8e2a\uff0c\u95ee\u660e\u4ed6\u7684\u6765\u610f\uff0c\u518d\u4f5c \u5b9a\u593a\u3002\u9759\u7f18\u5e08\u59d0\uff0c\u730e\u6237\u4eec\u53ef\u4f24\u5f97\u91cd\u4e48\uff1f\u201d \u3000\u3000\u9759\u7f18\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u4f24\u52bf\u4e2a\u4e2a\u76f8\u540c\uff0c\u90fd\u662f\u80a9\u4e0a\u7684\u7435\u7436\u9aa8\u7ed9\u6293\u788e\u4e86\u3002\u6211\u5df2\u7ed9\u4ed6\u4eec\u6577\u4e0a\u4e86\u91d1\u521b \u836f\uff0c\u6027\u547d\u5927\u7ea6\u65e0\u59a8\uff0c\u53ea\u662f\u6211\u90a3\u91cc\u6b20\u7f3a\u7eed\u7b4b\u9a73\u9aa8\u7684\u836f\uff0c\u56e0\u6b64\u987e\u4e0d\u5f97\u5929\u8272\u5df2\u665a\uff0c\u4e5f\u8981\u8d76\u6765\u7d22\u836f\u3002 \u5e76\u8bf7\u638c\u95e8\u591a\u6d3e\u51e0\u4f4d\u5e08\u5f1f\u5e08\u59b9\uff0c\u5e2e\u5fd9\u65bd\u672f\u3002\u201d \u3000\u3000\u7435\u7436\u9aa8\u6293\u65ad\uff0c\u82e5\u8fc7\u4e86\u4e00\u5929\u4e00\u591c\uff0c\u4fbf\u4e0d\u80fd\u9a73\u7eed\uff0c\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u6551\u4eba\u5982\u6551\u706b\uff0c\u767d\u5e08\u5144\uff0c\u4f60\u7acb \u5373\u5e26\u51e0\u4f4d\u61c2\u5f97\u7eed\u7b4b\u9a73\u9aa8\u624b\u672f\u7684\u5e08\u5f1f\uff0c\u62ff\u4e86\u836f\u968f\u9759\u7f18\u5e08\u59d0\u8d70\u5427\u3002\u730e\u6237\u5168\u9760\u6c14\u529b\u8c0b\u751f\uff0c\u53ef\u4e0d\u80fd\u8ba9 \u4ed6\u4eec\u6b8b\u5e9f\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u5600\u5600\u5495\u5495\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u6c5f\u5357\u89c1\u8fc7\u7684\u602a\u4e8b\u4e5f\u8fd8\u4e0d\u5c11\uff0c\u5374\u4ece\u672a\u542c\u8fc7\u755c\u751f\u4e5f\u4f1a\u6293\u4eba\u7684\u7435\u7436 \u9aa8\u7684\uff01\u5f53\u771f\u5982\u6b64\uff0c\u8fd9\u4e24\u53ea\u755c\u751f\u7b80\u76f4\u5c31\u662f\u6b66\u6797\u9ad8\u624b\u4e86\uff01\u7cdf\u7cd5\u5440\u7cdf\u7cd5\uff01\u5b83\u4eec\u5200\u67aa\u4e0d\u5165\uff0c\u53c8\u4f1a\u6b66 \u529f\uff0c\u6211\u4eec\u90fd\u662f\u8840\u8089\u4e4b\u8eaf\uff0c\u5374\u5982\u4f55\u62b5\u654c\u5f97\u4f4f\uff1f\u201d \u3000\u3000\u6c5f\u5357\u5600\u5600\u5495\u5495\uff0c\u672c\u662f\u60f3\u5f15\u91d1\u4e16\u9057\u8bf4\u8bdd\uff0c\u5374\u89c1\u91d1\u4e16\u9057\u671b\u51fa\u7a97\u5916\uff0c\u4e00\u6d3e\u832b\u7136\u7684\u795e\u6001\uff0c\u5bf9\u4ed6\u7684 \u8bf4\u8bdd\uff0c\u7adf\u4f3c\u662f\u542c\u800c\u4e0d\u95fb\u3002 \u3000\u3000\u539f\u6765\u91d1\u4e16\u9057\u60f3\u8d77\u4e86\u5341\u5e74\u524d\u7684\u5f80\u4e8b\u3002\u90a3\u4e00\u5e74\u4ed6\u548c\u5389\u80dc\u7537\u51fa\u6d77\u627e\u5bfb\u4e54\u5317\u6e9f\u6240\u85cf\u7684\u79d8\u7c4d\uff0c\u5c31\u5728 \u4e54\u5317\u6e9f\u6240\u4f4f\u8fc7\u7684\u90a3\u4e2a\u706b\u5c71\u5c9b\u4e0a\uff0c\u89c1\u7740\u5389\u80dc\u7537\u7684\u53d4\u53d4\u5389\u76fc\u5f52\uff0c\u4e5f\u7b2c\u4e00\u6b21\u89c1\u5230\u4e86\u91d1\u6bdb\u733f\u8fd9\u79cd\u602a \u517d\u3002\u90a3\u4e24\u53ea\u91d1\u6bdb\u68ad\u5c31\u662f\u5389\u76fc\u5f52\u517b\u7684\uff0c\u5389\u5bb3\u975e\u5e38\uff0c\u4e0e\u91d1\u4e16\u9057\u540c\u65f6\u6765\u5230\u90a3\u5c9b\u7684\u85cf\u8fb9\u56db\u5927\u9b54\u5934\u4e4b\u4e00 \u7684\u6851\u9752\u5a18\uff0c\u5c31\u662f\u6b7b\u5728\u90a3\u96c4\u91d1\u6bdb\u9a8f\u7684\u722a\u4e0b\u7684\u3002 \u3000\u3000\u91d1\u4e16\u9057\u4e0d\u7531\u5f97\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u91d1\u6bdb\u6d5a\u662f\u6781\u7f55\u89c1\u7684\u5f02\u517d\uff0c\u53ea\u751f\u957f\u5728\u70ed\u5e26\u591a\u96e8\u7684\u4e1b\u6797\u4e2d\uff0c\u5728\u8fd9 \u4e2d\u539f\u5730\u65b9\u90a3\u662f\u51b3\u8ba1\u4e0d\u4f1a\u6709\u7684\u3002\u91d1\u6bdb\u68ad\u5df2\u7136\u7f55\u89c1\uff0c\u4f1a\u6b66\u529f\u7684\u91d1\u6bdb\u6d5a\u60f3\u6765\u66f4\u662f\u4e16\u4e0a\u96be\u5bfb\uff0c\u83ab\u975e\u8fd9 \u4e24\u53ea\u91d1\u6bdb\u5506\u5c31\u662f\u5389\u80dc\u7537\u7684\u53d4\u53d4\u517b\u7684\u90a3\u4e24\u53ea\u91d1\u6bdb\u6d5a\uff1f\u4f46\u5374\u53c8\u662f\u8c01\u6709\u5982\u6b64\u80fd\u4e3a\uff0c\u7adf\u80fd\u628a\u5b83\u4eec\u964d \u4f0f\uff1f\u201d \u3000\u3000\u6c5f\u5357\u89c1\u91d1\u4e16\u9057\u4e0d\u63a5\u8bdd\u5934\uff0c\u7d22\u6027\u653e\u5f00\u4e86\u5589\u5499\u56b7\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u6562\u60c5\u4f60\u4e5f\u6015\u4e86\u90a3\u4e24\u53ea\u755c\u751f\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u6709\u5982\u5728\u68a6\u4e2d\u7ed9\u4eba\u559d\u9192\uff0c\u7b11\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u4f60\u5927\u53eb\u5927\u56b7\u505a\u751a\u4e48\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u6211\u662f\u5728 \u8bf4\u90a3\u4e24\u53ea\u755c\u751f\u5440\uff0c\u4f60\u53ef\u5f97\u60f3\u4e2a\u529e\u6cd5\u5bf9\u4ed8\u5b83\u4eec\u3002\u6c13\u5c71\u4e43\u662f\u6b66\u6797\u80dc\u5730\uff0c\u8981\u662f\u4f60\u4e5f\u6015\u4e86\u90a3\u4e24\u53ea\u755c \u751f\uff0c\u54b1\u4eec\u5c31\u53ea\u597d\u4efb\u51ed\u5b83\u4eec\u5728\u6c13\u5c71\u6492\u91ce\u4e86\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u600e\u4f1a\u5bb3\u6015\u5b83\u4eec\uff0c\u8bf4\u4e0d\u5b9a\u5b83\u4eec\u8fd8\u662f\u6211\u76f8\u8bc6\u7684\u670b\u53cb\u5462\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u4e5f\u65e9\u5df2\u89c9\u5bdf\u5230\u4e86\u91d1\u4e16\u9057\u795e\u6001\u6709\u5f02\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u4fbf\u7981\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4e16\u9057\uff0c\u4f60\u53ef\u662f\u77e5 \u9053\u90a3\u4e00\u4eba\u4e8c\u517d\u7684\u6765\u5386\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u4e0d\u60f3\u5728\u8c37\u4e4b\u534e\u9762\u524d\u591a\u63d0\u5389\u80dc\u7537\u7684\u5f80\u4e8b\uff0c\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u6bdb\u6d5a\u662f\u6781\u4e3a\u7f55\u89c1\u7684 \u5f02\u517d\uff0c\u6211\u4ee5\u524d\u5728\u6d77\u5916\u89c1\u8fc7\u4e24\u53ea\uff0c\u521a\u624d\u5ffd\u53d1\u5f02\u60f3\uff0c\u4f46\u613f\u8fd9\u4e24\u53ea\u5c31\u662f\u6211\u4ee5\u524d\u6240\u66fe\u89c1\u8fc7\u7684\u90a3\u4e24\u53ea\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u7b11\u9053\uff1a\u201c\u4e16\u4e0a\u54ea\u6709\u8fd9\u6837\u51d1\u5de7\u7684\u4e8b\u60c5\u3002\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4e4b\u534e\uff0c\u4f60\u653e\u5fc3\u3002\u4e0d\u7ba1\u8fd9\u4e24\u53ea \u91d1\u6bdb\u6d5a\u662f\u5426\u6211\u6240\u89c1\u8fc7\u7684\uff0c\u6211\u603b\u8981\u627e\u7740\u5b83\u4eec\uff0c\u51b3\u4e0d\u4f1a\u8ba9\u5b83\u4eec\u5728\u4f60\u7684\u6c13\u5c71\u4e0a\u6492\u91ce\u3002\u201d \u3000\u3000\u6c13\u5c71\u6d3e\u5f1f\u5b50\u542c\u5f97\u6709\u602a\u517d\u85cf\u533f\u672c\u5c71\uff0c\u5927\u5bb6\u90fd\u5728\u5c0f\u5fc3\u6212\u5907\u3002\u8c37\u4e4b\u534e\u4efd\u5c5e\u638c\u95e8\uff0c\u514d\u4e0d\u4e86\u8981\u7ed9\u4ed6 \u4eec\u5b89\u6392\u8b66\u6212\u7684\u4efb\u52a1\uff0c\u665a\u996d\u8fc7\u540e\uff0c\u5979\u5c31\u65e0\u6687\u4e0e\u91d1\u4e16\u9057\u518d\u53d9\u4e86\u3002 \u3000\u3000\u662f\u591c\u6c5f\u5357\u4e0e\u91d1\u4e16\u9057\u540c\u4f4f\u5728\u4e00\u623f\uff0c\u6c5f\u5357\u7ecf\u8fc7\u65e5\u95f4\u7684\u4e24\u573a\u6253\u6597\uff0c\u7cbe\u795e\u5df2\u662f\u75b2\u5026\u4e0d\u582a\uff0c\u8d77\u521d\u8fd8 \u5f3a\u81ea\u652f\u6301\uff0c\u5520\u5520\u53e8\u53e8\u7684\u4e0e\u91d1\u4e16\u9057\u8bf4\u4e2a\u4e0d\u4f11\uff0c\u540e\u6765\u5c31\u9891\u9891\u7684\u6253\u8d77\u5475\u6b20\u6765\uff0c\u4e0d\u6d88\u591a\u4e45\uff0c\u4fbf\u547c\u547c\u565c \u565c\u7684\u719f\u7761\u5982\u6ce5\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5374\u662f\u5fc3\u4e8b\u5982\u6f6e\uff0c\u8f97\u8f6c\u53cd\u4fa7\uff0c\u4e0d\u80fd\u5165\u7761\uff0c\u4ed6\u4ece\u91d1\u6bdb\u68ad\u60f3\u5230\u5389\u80dc\u7537\uff0c\u60f3\u5230\u4e86\u706b\u5c71\u5c9b\u4e0a \u90a3\u6bb5\u60c5\u7f18\uff0c\u60f3\u5230\u4e86\u5176\u540e\u7684\u60b2\u6b22\u79bb\u5408\uff0c\u8fd9\u4e00\u4e9b\u60c5\u4e8b\uff0c\u672c\u6765\u5df2\u968f\u7740\u5c81\u6708\u7684\u6d88\u901d\u800c\u6e10\u6e10\u6de1\u5fd8\uff0c\u5982\u4eca \u88ab\u8fd9\u4e24\u53ea\u91d1\u6bdb\u6d5a\u6311\u8d77\u4e86\u5c18\u5c01\u7684\u8bb0\u5fc6\uff0c\u6479\u7136\u95ee\u90fd\u4e0a\u5fc3\u5934\u3002 \u3000\u3000\u591c\u5df2\u4e09\u66f4\u3002\u6708\u5149\u5982\u6c34\uff0c\u900f\u8fc7\u7eb1\u7a97\uff0c\u91d1\u4e16\u9057\u795e\u601d\u660f\u660f\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u7d22\u6027\u62ab\u8863\u800c\u8d77\uff0c\u5728\u5c0f\u5ead \u91cc\u72ec\u81ea\u5f98\u5f8a\u3002 \u3000\u3000\u6708\u5149\u5728\u68a7\u6850\u6811\u4e0b\u201c\u753b\u201d\u51fa\u4e86\u91d1\u4e16\u9057\u7684\u5f71\u5b50\uff0c\u90a3\u5f71\u5b50\u53c8\u5e7b\u5316\u6210\u4e86\u5389\u80dc\u7537\u7684\u5f71\u5b50\uff0c\u91d1\u4e16\u9057\u671b \u7740\u81ea\u5df1\u7684\u5f71\u5b50\u53d1\u5446\uff0c\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u4ed6\u611f\u5230\u5389\u80dc\u7537\u53c8\u56de\u6765\u4e86\u3002\u5979\u867d\u7136\u6b7b\u4e86\uff0c\u4f46\u5979\u7684 \u5f71\u5b50\u8fd8\u5728\u8ffd\u968f\u7740\u4ed6\u3002 \u3000\u3000\u4e5f\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u8fdc\u5904\u4e00\u58f0\u602a\u5578\uff0c\u90a3\u6b63\u662f\u91d1\u4e16\u9057\u719f\u6089\u7684\u91d1\u6bdb\u6d5a\u7684\u53eb\u58f0\uff01 \u3000\u3000\u91d1\u4e16\u9057\u7fdf\u7136\u4e00\u60ca\uff0c\u767b\u65f6\u201c\u9192\u201d\u4e86\u8fc7\u6765\uff0c\u7acb\u5373\u5c55\u5f00\u7edd\u9876\u8f7b\u529f\uff0c\u51fa\u4e86\u7384\u5973\u89c2\uff0c\u5954\u4eba\u6797\u4e2d\uff0c\u5411 \u90a3\u58f0\u97f3\u7684\u6765\u5904\u5bfb\u627e\u3002\u5ffd\u53c8\u542c\u5f97\u4e00\u58f0\u5578\u58f0\uff0c\u4f3c\u662f\u91ce\u517d\u7684\u543c\u53eb\uff0c\u4f46\u4e0e\u90a3\u91d1\u6bdb\u68ad\u7684\u543c\u58f0\u5374\u53c8\u5e76\u4e0d\u4e00 \u6837\u3002 \u3000\u3000\u91d1\u4e16\u9057\u4e5f\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u51db\uff0c\u4ed6\u662f\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u8fd9\u65f6\u5df2\u542c\u51fa\u4e86\u90a3\u662f\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u4e0a\u4e58 \u5185\u529f\uff0c\u672c\u6765\u80fd\u591f\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u4e4b\u4eba\uff0c\u5185\u529f\u7684\u4fee\u517b\u5fc5\u7136\u63a5\u8fd1\u7089\u706b\u7eaf\u9752\u7684\u5883\u754c\uff0c\u6240\u53d1\u51fa\u7684\u58f0\u97f3\u4e5f \u5fc5\u662f\u67d4\u548c\u7684\uff0c\u4f46\u8fd9\u5578\u58f0\u5374\u662f\u5982\u6b64\u51c4\u5389\u53ef\u6016\uff0c\u663e\u51fa\u53d1\u5578\u4e4b\u4eba\uff0c\u51b3\u975e\u6b63\u6d3e\u4e2d\u4eba\uff0c\u800c\u662f\u7ec3\u6709\u6781\u5389\u5bb3 \u7684\u90aa\u6d3e\u5185\u529f\u7684\u3002\u91d1\u4e16\u9057\u5df2\u662f\u878d\u901a\u4e86\u6b63\u90aa\u5404\u6d3e\uff0c\u4f46\u4ece\u8fd9\u5578\u58f0\uff0c\u4e5f\u542c\u4e0d\u51fa\u90a3\u4eba\u7ec3\u7684\u662f\u54ea\u4e00\u6d3e\u90aa\u6d3e \u7684\u5185\u529f\u3002 \u3000\u3000\u5578\u58f0\u672a\u6b62\uff0c\u4fbf\u89c1\u91d1\u5149\u95ea\u95ea\uff0c\u5e73\u5730\u91cc\u5377\u8d77\u4e00\u9635\u65cb\u98ce\uff0c\u5374\u539f\u6765\u662f\u90a3\u4e24\u53ea\u91d1\u6bdb\u6d5a\u6765\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u731b\u7136\u7701\u609f\uff0c\u539f\u6765\u90a3\u4eba\u7684\u5578\u58f0\u6b63\u662f\u6307\u6325\u90a3\u4e24\u53ea\u91d1\u6bdb\u4fca\u6765\u6251\u51fb\u4ed6\u7684\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6 \u5feb\uff0c\u65cb\u98ce\u500f\u5730\u5377\u5230\u4e86\u4ed6\u7684\u8ddf\u524d\uff0c\u90a3\u4e24\u53ea\u91d1\u6bdb\u6d5a\u5df2\u4f38\u51fa\u6bdb\u8338\u8338\u7684\u957f\u81c2\u5411\u4ed6\u75be\u6293\uff01 \u3000\u3000\u91d1\u4e16\u9057\u7109\u80fd\u7ed9\u5b83\u4eec\u6293\u4e2d\uff0c\u53e6\u3002\u4e24\u53ea\u91d1\u6bdb\u6d5a\u95ea\u7535\u822c\u5411\u4ed6\u8fde\u7eed\u3002\u6293\u6765\uff0c\u91d1\u4e16\u9057\u4f7f\u51fa\u4e86\u5929\u7f57\u6b65 \u6cd5\uff0c\u4e5f\u662f\u95ea\u7535\u822c\u7684\u8fde\u7eed\u4e09\u6b21\u907f\u5f00\uff0c\u5728\u8fd9\u77ed\u4fc3\u7684\u65f6\u95f4\u4e2d\uff0c\u91d1\u4e16\u9057\u5df2\u8ba4\u51fa\u4e86\u5c31\u662f\u5389\u80dc\u7537\u7684\u53d4\u53d4\u6240 \u517b\u7684\u90a3\u4e24\u53ea\u91d1\u6bdb\u6d5a\u3002 \u3000\u3000\u91d1\u4e16\u9057\u8fde\u5fd9\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u8ba4\u5f97\u8001\u670b\u53cb\u4e86\u5417\uff1f\u201d\u90a3\u4e24\u53ea\u91d1\u6bdb\u68ad\u7b2c\u56db\u6b21\u6b63\u8981\u6293\u4e0b\uff0c\u5ffd\u5730\u957f\u81c2 \u4e0b\u5782\uff0c\u6446\u5c3e\u6447\u5934\uff0c\u53d1\u51fa\u545c\u545c\u7684\u53eb\u58f0\u3002\u5b83\u4eec\u4e00\u65f6\u95f4\u8ba4\u4e0d\u51fa\u91d1\u4e16\u9057\uff0c\u5982\u4eca\u542c\u51fa\u4e86\u662f\u8001\u670b\u53cb\u7684\u58f0 \u97f3\uff0c\u55c5\u5230\u4e86\u70ed\u6089\u7684\u6c14\u5473\uff0c\u767b\u65f6\u51f6\u6027\u5c3d\u655b\uff0c\u4e0e\u91d1\u4e16\u9057\u4eb2\u70ed\u8d77\u6765\u3002\u5c24\u5176\u90a3\u53ea\u96cc\u7684\uff0c\u56e0\u4e3a\u5b83\u5f53\u5e74\u66fe \u53d7\u8fc7\u5b5f\u795e\u901a\u6240\u4f24\uff0c\u800c\u8fd9\u4f24\u662f\u91d1\u4e16\u9057\u7ed9\u5b83\u6cbb\u597d\u7684\uff0c\u6240\u4ee5\u5bf9\u91d1\u4e16\u9057\u66f4\u662f\u7279\u522b\u4eb2\u70ed\uff0c\u4e0b\u4f0f\u5728\u91d1\u4e16\u9057 \u7684\u811a\u4e0b\u4e0e\u4ed6\u53ae\u78e8\u3002 \u3000\u3000\u5ffd\u5730\u58f0\u53c8\u8d77\uff0c\u90a3\u53ea\u96cc\u91d1\u6bdb\u68ad\u4f3c\u662f\u5403\u4e86\u4e00\u60ca\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u90a3\u53ea\u96c4\u7684\u7ed5\u7740\u91d1\u4e16\u9057\u8df3\u4e86\u4e00\u5708\uff0c \u545c\u545c\u5730\u53eb\u4e2a\u4e0d\u4f11\uff0c\u91d1\u4e16\u9057\u660e\u767d\u90a3\u5578\u58f0\u4e43\u662f\u6307\u6325\u8fd9\u4e24\u53ea\u91d1\u6bdb\u68ad\u4e0a\u524d\u6251\u51fb\u7684\uff0c\u4f46\u5b83\u4eec\u5df2\u628a\u91d1\u4e16\u9057 \u5f53\u4f5c\u670b\u53cb\uff0c\u54ea\u80af\u5411\u524d\uff1f \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u4e0d\u5fc5\u8d39\u795e\u4e86\uff0c\u6211\u548c\u5b83\u4eec\u76f8\u8bc6\uff0c\u4e5f\u8bb8\u8fd8\u5728\u4f60\u4e4b\u524d\u5462\u3002\u201d \u3000\u3000\u8bdd\u58f0\u752b\u6bd5\uff0c\u53ea\u89c1\u4e00\u6761\u9ed1\u5f71\u500f\u5730\u4ece\u6797\u5b50\u91cc\u51b2\u51fa\u6765\uff0c\u91d1\u4e16\u9057\u731b\u7684\u5fc3\u5934\u4e00\u9707\uff0c\u7981\u4e0d\u4f4f\u6d51\u8eab\u98a4 \u6296\uff0c\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u5413\u5f97\u51e0\u4e4e\u5446\u4e86\uff01\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u51e0\u4e4e\u4ee5\u4e3a\u662f\u5389\u80dc\u7537\u7684\u9b3c\u9b42\u51fa\u73b0\uff0c\u90a3\u4eba\u7a7f\u7740\u4e00 \u8eab\u9ed1\u8272\u7684\u8863\u88f3\uff0c\u957f\u53d1\u62ab\u80a9\uff0c\u8fdc\u8fdc\u770b\u53bb\uff0c\u6d3b\u8131\u5c31\u50cf\u5389\u80dc\u7537\u5f53\u5e74\u7684\u6a21\u6837\uff01 \u3000\u3000\u8f6c\u773c\u95f4\u90a3\u4e2a\u4eba\u5df2\u5230\u4e86\u8fd1\u5904\uff0c\u91d1\u4e16\u9057\u5b9a\u4e86\u5b9a\u795e\uff0c\u8fd9\u624d\u770b\u51fa\u5e76\u4e0d\u662f\u5973\u5b50\uff0c\u4f46\u4ed6\u7684\u76f8\u8c8c\u786e\u662f\u6709 \u51e0\u5206\u50cf\u5389\u80dc\u7537\uff0c\u4e14\u53c8\u7559\u7740\u957f\u957f\u7684\u5934\u53d1\uff0c\u6253\u626e\u5f97\u4e0d\u7537\u4e0d\u5973\uff0c\u8981\u4e0d\u662f\u91d1\u4e16\u9057\u542c\u5230\u4e86\u4ed6\u7684\u58f0\u97f3\uff0c\u5f53 \u771f\u4f1a\u4ee5\u4e3a\u662f\u5389\u80dc\u7537\u7a7f\u4e0a\u4e86\u7537\u5b50\u7684\u670d\u88c5\u5462\u3002 \u3000\u3000\u90a3\u4eba\u8d70\u5230\u4e86\u91d1\u4e16\u9057\u9762\u524d\uff0c\u628a\u624b\u4e00\u6325\uff0c\u90a3\u4e24\u53ea\u91d1\u6bdb\u5506\u5982\u5949\u5927\u8d66\uff0c\u8fde\u5fd9\u9000\u4e0b\uff0c\u8fdc\u8fdc\u8d70\u5f00\u3002\u90a3 \u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u5c31\u662f\u91d1\u4e16\u9057\u5417\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u7684\u58f0\u97f3\u628a\u91d1\u4e16\u9057\u53c8\u5413\u4e86\u4e00\u8df3\u3002\u4ed6\u7684\u58f0\u97f3\u9576\u632b\u523a\u8033\uff0c\u6709\u5982\u4e24\u7247\u91d1\u5c5e\u78e8\u64e6\u4e00\u822c\u3002\u91d1\u4e16\u9057 \u600e\u4e5f\u6599\u60f3\u4e0d\u5230\uff0c\u6b64\u4eba\u7709\u6e05\u76ee\u79c0\uff0c\u8c8c\u5982\u5973\u5b50\uff0c\u5374\u4f1a\u53d1\u51fa\u8fd9\u6837\u523a\u8033\u7684\u58f0\u97f3\uff01 \u3000\u3000\u91d1\u4e16\u9057\u76ee\u4e0d\u8f6c\u775b\u5730\u671b\u7740\u4ed6\uff0c\u5fc3\u4e2d\u7591\u4e91\u5927\u8d77\uff0c\u5f3a\u9547\u5fc3\u795e\uff0c\u7b54\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u91d1\u4e16\u9057\u3002 \u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u8d8a\u770b\u8d8a\u89c9\u5f97\u8fd9\u4eba\u4f3c\u5389\u80dc\u7537\uff0c\u4e0d\u8fc7\uff0c\u8d8a\u770b\u4e5f\u5c31\u8d8a\u53d1\u53ef\u4ee5\u80af\u5b9a\u662f\u4e2a\u7537\u5b50\u3002\u9664\u4e86\u58f0\u97f3\u3001 \u670d\u88c5\u4e4b\u5916\uff0c\u8fd9\u4eba\u6709\u7c97\u5927\u7684\u5589\u6838\uff0c\u8fd8\u6709\u7a00\u758f\u7684\u51e0\u6839\u77ed\u987b\uff0c\u8fd9\u90fd\u662f\u7537\u5b50\u7684\u7279\u5f81\uff0c\u8fd8\u6709\u4ed6\u90a3\u53cc\u5927 \u811a\uff0c\u4e5f\u51b3\u975e\u5389\u80dc\u7537\u90a3\u4e09\u5bf8\u91d1\u83b2\u53ef\u6bd4\u3002\u770b\u6765\u8fd9\u4e2a\u7537\u5b50\u5927\u7ea6\u5728\u4e8c\u5341\u56db\u4e94\u5c81\u4e4b\u95f4\u3002 \u3000\u3000\u8fd9\u4eba\u4e5f\u662f\u5728\u76ee\u4e0d\u8f6c\u775b\u5730\u671b\u7740\u91d1\u4e16\u9057\uff0c\u8fdf\u8fdf\u672a\u66fe\u56de\u7b54\u3002\u91d1\u4e16\u9057\u84e6\u5730\u5fc3\u5934\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u96be \u9053\u662f\u80dc\u7537\u7684\u5144\u5f1f\uff1f\u53ef\u662f\u8fd9\u662f\u51b3\u4e0d\u53ef\u80fd\u4e4b\u4e8b\uff0c\u5389\u80dc\u7537\u7684\u5168\u5bb6\uff0c\u9664\u4e86\u5979\u81ea\u5df1\u4e00\u4eba\u4e4b\u5916\uff0c\u65e9\u5df2\u88ab\u5b5f \u795e\u901a\u6740\u5f97\u9e21\u72ac\u4e0d\u7559\u4e86\uff0c\u8fd9\u4e16\u754c\u4e0a\u54ea\u91cc\u8fd8\u4f1a\u6709\u5389\u80dc\u7537\u7684\u5bb6\u4eba\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u65e7\u68a6\u5c18\u5c01\u4f11\u518d\u542f\uff0c\u5389\u5bb6\u5b64\u5b50\u53c8\u91cd\u6765\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000--------------- \u3000\u3000\u626c\u5251\u8f69\u5c45\u58eb \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u5341\u4e00\u56de\u3000\u65e0\u591a\u63a9\u5e54\u7559\u9999\u4f4f\u3000\u4f9d\u65e7\u7aa5\u4eba\u6709\u71d5\u6765 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u5341\u4e00\u56de\u3000\u65e0\u591a\u63a9\u5e54\u7559\u9999\u4f4f\u3000\u4f9d\u65e7\u7aa5\u4eba\u6709\u71d5\u6765 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u8fd9\u9ed1\u8863\u7537\u5b50\u4e00\u58f0\u602a\u7b11\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u77e5\u9053\u6211\u662f\u8c01\u4e48\uff1f\u6211\u5c31\u662f\u8fd9\u4e24\u53ea\u91d1 \u6bdb\u733f\u7684\u4e3b\u4eba\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u4e0d\u89c1\u5f97\u5427\uff0c\u636e\u6211\u770b\u6765\uff0c\u4f60\u8fd9\u53e5\u8bdd\u6709\u70b9\u6bdb\u75c5\uff0c\u81f3\u5c11\u4e5f\u6f0f\u4e86\u4e00\u4e2a\u5b57\u3002\u201d\u9ed1\u8863 \u7537\u5b50\u4fa7\u76ee\u659c\u8131\uff0c\u602a\u58f0\u95f4\u9053\uff1a\u201c\u6f0f\u4e86\u4ec0\u4e48\u5b57\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6f0f\u4e86\u4e00\u4e2a\u2018\u65b0\u2019\u5b57\uff0c\u5145\u5176\u91cf\u4f60\u53ea\u80fd\u8bf4\u662f\u8fd9\u4e24\u53ea\u91d1\u6bdb\u6d5a\u7684\u65b0\u4e3b\u4eba\u3002\u771f\u4eba\u9762\u524d \u4e0d\u6253\u5047\u8bdd\uff0c\u9601\u4e0b\u9ad8\u59d3\u5927\u540d\uff0c\u4ed9\u4e61\u4f55\u5904\uff0c\u51e0\u65f6\u5230\u4e86\u90a3\u706b\u5c71\u5c9b\u6536\u670d\u4e86\u8fd9\u4e24\u53ea\u91d1\u6bdb\u68ad\u7684\uff1f\u8fd8\u671b\u89c1 \u544a\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u7537\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u91d1\u4e16\u9057\uff0c\u4f60\u73b0\u5728\u5c31\u8981\u76d8\u67e5\u6211\u7684\u6765\u5386\uff0c\u672a\u514d\u65e9\u4e86\u4e00\u70b9\uff0c\u4f60\u61c2\u5f97\u6c5f\u6e56 \u4e0a\u7684\u89c4\u77e9\u4e48\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u5bf9\uff0c\u4f60\u662f\u5ba2\uff0c\u6211\u662f\u4e3b\uff0c\u6211\u5e94\u8be5\u5148\u95ee\u4f60\u7684\u6765\u610f\u3002\u8bf7\u95ee\u9601\u4e0b\uff0c\u4e3a\u4f55\u4e00\u89c1\u9762\u4f60\u5c31 \u5506\u4f7f\u91d1\u6bdb\u6d5a\u6293\u6211\uff1f\u5e78\u4e8f\u5b83\u4eec\u4e0d\u542c\u4f60\u7684\u8bdd\uff0c\u54c8\u54c8\uff0c\u4e5f\u5e78\u4e8f\u6211\u8fd8\u80fd\u964d\u4f0f\u5b83\u4eec\uff0c\u8981\u662f\u7ed9\u5b83\u4eec\u6293\u7834\u4e86 \u8111\u888b\uff0c\u6211\u4e5f\u5c31\u4e0d\u80fd\u548c\u4f60\u518d\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u89c4\u77e9\u5566\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u7537\u5b50\u602a\u7b11\u9053\uff1a\u201c\u8981\u4e0d\u662f\u6211\u53eb\u8fd9\u4e24\u53ea\u91d1\u6bdb\u68ad\u5148\u8bd5\u4e00\u8bd5\uff0c\u6211\u600e\u77e5\u9053\u4f60\u5c31\u662f\u91d1\u4e16\u9057\uff1f\u201d \u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5df1\u77e5\u9053\u6211\u5c31\u662f\u91d1\u4e16\u9057\uff0c\u53c8\u5f85\u5982\u4f55\uff1f\u201d \u3000\u3000\u90a3\u9ed1\u8863\u7537\u5b50\u7b11\u58f0\u4e00\u6536\uff0c\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u91d1\u4e16\u9057\uff0c\u54b1\u4eec\u5e9f\u8bdd\u5c11\u8bf4\uff0c\u82e5\u95ee\u6211\u7684\u6765\u610f\uff0c\u6211\u5c31\u5212\u4e0b \u4e24\u6761\u9053\u513f\uff0c\u968f\u4f60\u8d70\u5427\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u6211\u95ef\u8361\u6c5f\u6e56\u5341\u591a\u5e74\uff0c\u8fd8\u662f\u7b2c\u4e00\u6b21\u542c\u5f97\u522b\u4eba\u7528\u8fd9\u6837\u7684\u53e3\u6c14\u5411\u6211\u8bf4\u8bdd\u3002\u597d \u5427\uff0c\u4f60\u5c31\u8bf4\u5427\uff0c\u662f\u600e\u4e48\u6837\u7684\u4e24\u6761\u9053\u513f\uff1f\u8bf7\u9053\u5176\u8be6\uff01\u201d \u3000\u3000\u90a3\u9ed1\u8863\u7537\u5b50\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u7b2c\u4e00\u6761\uff0c\u4f60\u968f\u6211\u5230\u7ec4\u6765\u5c71\u53bb\uff0c\u6211\u6536\u4f60\u505a\u5929\u9b54\u6559\u7684\u5f1f\u5b50\uff0c\u4eb2\u81ea\u7ed9 \u4f60\u5728\u575b\u524d\u53d7\u6212\uff0c\u8ba9\u4f60\u4f5c\u672c\u6559\u7684\u62a4\u6cd5\u9999\u4e3b\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u5fcd\u4e0d\u4f4f\u54c8\u54c8\u5927\u7b11\uff0c\u90a3\u9ed1\u8863\u7537\u5b50\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u5148\u95ee\u4f60\uff0c\u4f60\u662f \u5929\u9b54\u6559\u7684\u4ec0\u4e48\u4eba\uff1f\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u4f60\u5012\u50cf\u662f\u5929\u9b54\u6559\u6559\u4e3b\u7684\u6a21\u6837\uff01\u201d \u3000\u3000\u90a3\u9ed1\u8863\u7537\u5b50\u677f\u7740\u8138\u5b54\u9053\uff1a\u201c\u4f60\u7ba1\u6211\u662f\u4ec0\u4e48\u4eba\u3002\u4f60\u8fdb\u4e86\u5929\u9b54\u6559\u6211\u81ea\u7136\u4f1a\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u867d\u7136\u4e0d\u77e5\u9053\u4f60\u662f\u4f55\u4eba\uff0c\u4f60\u5374\u662f\u77e5\u9053\u6211\u7684\u4e86\u3002\u4f60\u65e2\u7136\u662f\u5929\u9b54\u6559\u4e2d\u7684 \u9996\u8111\u4eba\u7269\uff0c\u90a3\u4e48\u4f60\u4e5f\u5f53\u7136\u5e94\u8be5\u77e5\u9053\u4f60\u4eec\u672c\u6559\u7684\u7956\u5e08\u662f\u8c01\u4e86\uff1f\u201d \u3000\u3000\u90a3\u9ed1\u8863\u7537\u5b50\u9053\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\uff0c\u4f60\u95ee\u8fd9\u4e2a\u5e72\u5417\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u5927\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5929\u9b54\u6559\u65e2\u628a\u5389\u59d1\u5a18\u786c\u62ac\u51fa\u6765\uff0c\u5949\u5979\u4f5c\u4f60\u4eec\u7956\u5e08\uff0c\u96be\u9053\u4f60\u7adf\u4f1a\u4e0d\u77e5 \u9053\u4f60\u4eec\u7684\u7956\u5e08\u662f\u6211\u91d1\u4e16\u9057\u7684\u4ec0\u4e48\u4eba\u5417\uff0c\u5982\u4f55\u4f60\u8fd8\u8981\u6536\u6211\u505a\u4e00\u4e2a\u5f1f\u5b50\uff0c\u54c8\u54c8\u3002\u8fd9\u5c82\u4e0d\u662f\u8352\u8c2c\u7edd \u4f26\uff1f\u201d \u3000\u3000\u90a3\u9ed1\u8863\u7537\u5b50\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5ffd\u5730\u6012\u9053\uff1a\u201c\u91d1\u4e16\u9057\uff0c\u4f60\u8fd9\u4e48\u8bf4\u6cd5\uff0c\u624d\u771f\u662f\u8352\u8c2c\u7edd\u4f26\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u5012\u5947\u4e86\uff0c\u600e\u4e48\u53cd\u800c\u662f\u6211\u8352\u8c2c\u7edd\u4f26\uff1f\u201d \u3000\u3000\u90a3\u9ed1\u8863\u7537\u5b50\u9053\uff1a\u201c\u6211\u4e5f\u8981\u5148\u95ee\u4f60\uff0c\u4f60\u628a\u5389\u59d1\u5a18\u5f53\u4f5c\u4f60\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4ec0\u4e48\u2018\u5f53\u4f5c\u2019\u4e0d\u2018\u5f53\u4f5c\u2019\u7684\uff1f\u5389\u59d1\u5a18\u662f\u6211\u7684\u59bb\u5b50\uff01\u201d \u3000\u3000\u90a3\u9ed1\u8863\u7537\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u7684\u4e8b\u60c5\u5929\u4e0b\u77e5\u95fb\uff0c\u8c01\u4e0d\u77e5\u9053\u5389\u59d1\u5a18\u5bf9\u4f60\u60c5\u6df1\u4e49\u91cd\uff1f\u800c\u4f60\u5bf9\u5979 \u5219\u662f\u5047\u610f\u865a\u60c5\uff01\u4f60\u662f\u4e3a\u4e86\u8981\u6551\u4f60\u7684\u5fc3\u4e0a\u4eba\u624d\u5411\u5979\u9a97\u5a5a\u7684\uff0c\u54fc\uff0c\u54fc\uff0c\u4f60\u5bb3\u6b7b\u4e86\u5979\uff0c\u4e8f\u4f60\u8fd8\u6709\u8138 \u76ae\u8ba4L\u4f5c\u59bb\u5b50\uff01\u201d \u3000\u3000\u8fd9\u4e9b\u8bf4\u8bdd\uff0c\u4e00\u53e5\u53e5\u4f3c\u5c16\u5200\u822c\u5730\u523a\u5728\u91d1\u4e16\u9057\u5fc3\u4e0a\u3002\u8981\u77e5\u81ea\u4ece\u90a3\u573a\u60c5\u573a\u60e8\u53d8\u53d1\u751f\u4e4b\u540e\uff0c\u867d\u7136 \u91d1\u4e16\u9057\u7684\u670b\u53cb\u5dee\u4e0d\u591a\u90fd\u662f\u8c05\u89e3\u4ed6\uff0c\u5e76\u4e0d\u540c\u60c5\u5389\u80dc\u7537\u7684\uff0c\u53ef\u662f\uff0c\u91d1\u4e16\u9057\u81ea\u5df1\u7684\u5fc3\u60c5\uff0c\u5374\u603b\u662f\u611f \u5230\u5185\u759a\uff0c\u611f\u89c9\u5bf9\u5389\u80dc\u7537\u4e0d\u4f4f\u3002\u5982\u4eca\uff0c\u8fd9\u4e2a\u9ed1\u8863\u5c11\u5e74\u5b8c\u5168\u7ad9\u5728\u5389\u80dc\u7537\u8fd9\u8fb9\uff0c\u6570\u8bf4\u4ed6\u7684\u4e0d\u662f\uff0c\u800c \u8fd9\u4e9b\u8bdd\u53c8\u6b63\u89e6\u53ca\u4e86\u4ed6\u7684\u75db\u5904\uff0c\u7109\u80fd\u4e0d\u4ee4\u4ed6\u6fc0\u52a8\u5982\u72c2\uff1f \u3000\u3000\u90a3\u9ed1\u8863\u5c11\u5e74\u7684\u773c\u5149\uff0c\u5982\u5229\u526a\u3001\u5982\u5bd2\u51b0\uff0c\u51b7\u51b7\u5730\u76ef\u7740\u4ed6\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u91d1\u4e16\u9057\u624d\u6e10\u6e10\u51b7 \u9759\u4e0b\u6765\uff0c\u5636\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e0e\u5389\u59d1\u5a18\u4e4b\u95f4\u7684\u4e8b\u60c5\u4e0d\u662f\u5916\u4eba\u6240\u80fd\u660e\u767d\u7684\uff0c\u6211\u5bf9\u5979\u7684\u4e00\u7247\u5fc3\u60c5\u66f4\u4e0d \u662f\u5916\u4eba\u6240\u80fd\u61c2\u5f97\u7684\uff01\u603b\u4e4b\uff0c\u5979\u751f\u524d\u662f\u6211\u7684\u59bb\u5b50\uff0c\u6b7b\u540e\u4e5f\u4ecd\u7136\u662f\u6211\u7684\u59bb\u5b50\uff01\u201d \u3000\u3000\u90a3\u9ed1\u8863\u5c11\u5e74\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u6211\u59d1\u4e14\u76f8\u4fe1\u4f60\u7684\u8bf4\u8bdd\uff0c\u597d\u5427\uff0c\u4f60\u65e2\u5c06\u5979\u8ba4\u4f5c\u59bb\u5b50\uff0c\u6211\u4eec\u79c9\u627f\u5979 \u7684\u9057\u5fd7\u6240\u521b\u7684\u5929\u9b54\u6559\uff0c\u4f60\u5374\u4e3a\u4f55\u9887\u6709\u4e0d\u5c51\u4e4b\u610f\uff1f\u4f5c\u5929\u9b54\u6559\u7684\u5f1f\u5b50\uff0c\u96be\u9053\u662f\u59d4\u5c48\u4f60\u4e86\u5417\uff1f\u54fc\uff0c \u54fc\uff0c\u6211\u4eec\u8ba9\u4f60\u8fdb\u6765\uff0c\u6b63\u662f\u7ed9\u4f60\u4e00\u4e2a\u8d4e\u7f6a\u7684\u3002\u5fcf\u6094\u7684\u673a\u4f1a\uff0c\u4f60\u4eca\u540e\u53ea\u6709\u4e3a\u672c\u6559\u7acb\u529f\uff0c\u624d\u80fd\u5bf9\u5f97 \u4f4f\u7ed9\u4f60\u5bb3\u6b7b\u7684\u5389\u7956\u5e08\uff01\u5982\u4eca\uff0c\u6211\u4eb2\u81ea\u6765\u70b9\u5316\u4f60\uff0c\u4f60\u53cd\u800c\u5f53\u4f5c\u7b11\u8bdd\uff0c\u8fd9\u5c82\u4e0d\u662f\u8352\u8c2c\u7edd\u4f26\uff01\u201d \u3000\u3000\u8981\u662f\u5728\u91d1\u4e16\u9057\u7684\u5c11\u5e74\u65f6\u4ee3\uff0c\u8fd9\u756a\u8bf4\u8bdd\u771f\u53ef\u80fd\u5c31\u6253\u52a8\u4e86\u4ed6\uff0c\u4f46\u73b0\u5728\u7684\u91d1\u4e16\u9057\u5374\u662f\u6bd4\u8f83\u80fd\u591f \u63a7\u5236\u81ea\u5df1\u7684\u611f\u60c5\u4e86\uff0c\u7ecf\u8fc7\u4e86\u8fd9\u9ed1\u8863\u5c11\u5e74\u7684\u4e00\u573a\u5927\u9a82\uff0c\u4ed6\u53cd\u800c\u6e05\u9192\u8fc7\u6765\uff0c\u84e6\u7136\u4ea2\u58f0\u8bf4\u9053\uff1a\u201c\u4f60 \u4eec\u8c01\u4e5f\u6ca1\u6709\u6211\u8fd9\u6837\u4e0e\u5389\u59d1\u5a18\u76f8\u77e5\u4e4b\u6df1\uff0c\u5979\u82e5\u8fd8\u5728\u751f\uff0c\u51b3\u4e0d\u4f1a\u521b\u7acb\u4ec0\u4e48\u5929\u9b54\u6559\uff01\u4f60\u4eec\u5949\u5979\u4f5c\u7956 \u5e08\uff0c\u90a3\u662f\u4f60\u4eec\u7684\u4e8b\uff0c\u8981\u6211\u4e3a\u4f60\u4eec\u4f5c\u9a6c\u524d\u8d70\u5352\uff0c\u90a3\u662f\u4e07\u4e07\u4e0d\u80fd\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u5c11\u5e74\u53d8\u4e86\u9762\u8272\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u5c31\u53ea\u5269\u4e0b\u4e00\u6761\u8def\u7ed9\u4f60\u8d70\u4e86\u3002\u201d\u91d1\u4e16\u9057 \u9053\uff1a\u201c\u5212\u51fa\u9053\u6765\u5427\uff01\u201d\u9ed1\u8863\u5c11\u5e74\u9634\u9633\u602a\u6c14\u5730\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u95ee\u5417\uff1f\u8fd9\u5c31\u662f\u6b7b\u8def\u4e00\u6761\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u6c14\u5f80\u4e0a\u6d8c\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6b7b\u8def\u6211\u4e5f\u8981\u95ef\u5b83\u4e00\u95ef\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u4e24\u9053\u78a7\u83b9\u83b9\u7684 \u5bd2\u5149\uff0c\u5df2\u662f\u95ea\u7535\u822c\u7684\u5411\u4ed6\u626b\u6765\uff0c\u90a3\u5c11\u5e74\u4eae\u51fa\u4e86\u4e00\u53cc\u7389\u5c3a\uff0c\u4e00\u51fa\u624b\u4fbf\u70b9\u5411\u91d1\u4e16\u9057\u7684\u8981\u5bb3\u7a74\u9053\u3002 \u3000\u3000\u91d1\u4e16\u9057\u4f7f\u4e86\u4e2a\u201c\u98ce\u522e\u843d\u82b1\u201d\u7684\u8eab\u6cd5\uff0c\u95ea\u8fc7\u4e00\u8fb9\uff0c\u54ea\u77e5\u9053\u9ed1\u8863\u5c11\u5e74\u51fa\u624b\u5feb\u6377\u4e4b\u6781\uff0c\u4e00\u51fb\u4e0d \u4e2d\uff0c\u7b2c\u4e8c\u62db\u3001\u7b2c\u4e09\u62db\u53c8\u63a5\u8fde\u800c\u6765\uff0c\u5f53\u771f\u6709\u5982\u957f\u6c5f\u5927\u6cb3\uff0c\u6eda\u6eda\u800c\u4e0a\u3002 \u3000\u3000\u91d1\u4e16\u9057\u63a5\u8fde\u4f7f\u4e86\u51e0\u79cd\u8eab\u6cd5\uff0c\u867d\u6ca1\u7ed9\u4ed6\u6253\u4e2d\uff0c\u5374\u4e5f\u6446\u8131\u4e0d\u5f00\u4ed6\u7684\u7ea0\u7f20\uff0c\u5947\u602a\u7684\u662f\uff0c\u8fde\u201c\u5929 \u7f57\u6b65\u201d\u90a3\u79cd\u5965\u5999\u7684\u8eab\u6cd5\u6b65\u6cd5\uff0c\u5bf9\u65b9\u7adf\u7136\u4e5f\u4f3c\u4f1a\u5bb6\uff0c\u4ed6\u7684\u811a\u4e0b\u4e5f\u6309\u7740\u516b\u5366\u65b9\u4f4d\uff0c\u4e0e\u91d1\u4e16\u9057\u62a2\u5360 \u8981\u4f4d\uff0c\u90a3\u4e24\u6839\u7389\u5c3a\uff0c\u81ea\u59cb\u81f3\u7ec8\uff0c\u90fd\u51e0\u4e4e\u7d27\u8d34\u7740\u91d1\u4e16\u9057\u7684\u80cc\u5fc3\u3002 \u3000\u3000\u91d1\u4e16\u9057\u672c\u6765\u65e0\u5fc3\u4e0e\u4ed6\u5bf9\u654c\uff0c\u4f46\u7ed9\u4ed6\u8feb\u5f97\u592a\u7d27\uff0c\u4e5f\u4e0d\u7981\u6709\u70b9\u513f\u52a8\u6012\uff0c\u5f53\u4e0b\u4e00\u58f0\u957f\u5578\uff0c\u6717\u58f0 \u8bf4\u9053\uff1a\u201c\u6211\u5df2\u8ba9\u591f\u4e86\u4f60\u5341\u62db\u4e86\uff0c\u4f60\u65e2\u4e0d\u77e5\u8fdb\u9000\uff0c\u6211\u5c31\u8bd5\u8bd5\u4f60\u7684\u529f\u592b\u5427\uff01\u201d\u66ae\u5730\u53cd\u624b\u4e00\u5f39\uff0c\u4f7f \u51fa\u4e86\u4f5b\u95e8\u7684\u4e0a\u4e58\u6b66\u529f\u2014\u2014\u2014\u6307\u7985\u529f\u3002 \u3000\u3000\u8fd9\u4e00\u6307\u7985\u529f\u7528\u7684\u662f\u521a\u67d4\u4e92\u6d4e\u7684\u5185\u5bb6\u771f\u529b\uff0c\u5373\u7b97\u662f\u94c1\u77f3\uff0c\u5728\u4ed6\u4e00\u5f39\u4e4b\u4e0b\uff0c\u4e5f\u8981\u7c89\u788e\uff0c\u53ea\u56e0 \u91d1\u4e16\u9057\u65e0\u610f\u4f24\u5bb3\u90a3\u5c11\u5e74\u7684\u6027\u547d\uff0c\u53ea\u7528\u4e86\u4e09\u6210\u529f\u529b\u3002 \u3000\u3000\u5728\u91d1\u4e16\u9057\u60f3\u6765\uff0c\u90a3\u4e24\u6839\u7389\u5c3a\u4efd\u91cf\u4e0d\u91cd\uff0c\u8fd9\u4e00\u5f39\u6700\u5c11\u4e5f\u53ef\u4ee5\u4ee4\u4ed6\u7684\u5175\u5668\u8131\u624b\u98de\u53bb\uff0c\u54ea\u77e5\u6307 \u5934\u4e0e\u90a3\u7389\u5c3a\u76f8\u63a5\uff0c\u84e6\u7136\u95f4\u53ea\u89c9\u4e00\u80a1\u6781\u6c89\u91cd\u7684\u529b\u9053\u538b\u4e0b\u6765\uff0c\u4f46\u542c\u5f97\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u5c11\u5e74\u5012\u9000 \u4e09\u6b65\uff0c\u91d1\u4e16\u9057\u7684\u864e\u53e3\u7adf\u4e5f\u611f\u5230\u4e00\u9635\u9635\u9178\u9ebb\uff01 \u3000\u3000\u91d1\u4e16\u9057\u4e00\u60ca\u4e4b\u4e0b\uff0c\u731b\u7136\u7701\u609f\uff0c\u201c\u539f\u6765\u8fd9\u662f\u6d77\u5e95\u5bd2\u7389\uff01\u201d\u4e54\u5317\u6e9f\u6240\u9057\u7559\u7684\u4e09\u5b9d\u4e4b\u4e2d\uff0c\u6709\u4e00 \u628a\u7389\u5f13\uff0c\u5c31\u662f\u6d77\u5e95\u5bd2\u7389\u9020\u7684\uff0c\u8fd9\u79cd\u5bd2\u7389\uff0c\u6c89\u91cd\u975e\u5e38\uff0c\u6bd4\u540c\u6837\u4f53\u79ef\u7684\u7269\u8d28\u8981\u91cd\u8fc7\u767e\u500d\u4e0d\u6b62\uff0c\u91d1 \u4e16\u9057\u5f53\u5e74\u672a\u7ec3\u79d8\u7c4d\u4e0a\u7684\u6b66\u529f\u4e4b\u524d\uff0c\u80cc\u90a3\u628a\u7389\u5f13\u8d70\u4e0a\u51e0\u91cc\u8def\u4e5f\u8981\u7d2f\u5f97\u6c14\u5598\u3002\u5982\u4eca\uff0c\u91d1\u4e16\u9057\u867d\u7136 \u5df2\u7ec3\u6210\u4e86\u7edd\u4e16\u6b66\u529f\uff0c\u4f46\u7531\u4e8e\u672a\u66fe\u53d1\u6325\u5168\u529b\uff0c\u4e5f\u7a0d\u7a0d\u5403\u4e86\u70b9\u4e8f\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u4f3c\u4e4e\u5df2\u8bc6\u5f97\u91d1\u4e16\u9057\u7684\u5389\u5bb3\uff0c\u4f46\u4ecd\u7136\u51f6\u608d\u81f3\u6781\uff0c\u65cb\u98ce\u822c\u7684\u53c8\u6251\u8fc7 \u6765\uff0c\u626c\u8d77\u7389\u5c3a\uff0c\u5411\u91d1\u4e16\u9057\u6402\u5934\u518d\u6253\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5fc3\u91cc\u597d\u751f\u7591\u60d1\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u662f\u4ece\u706b\u5c71\u5c9b\u4e0a\u6765\u7684\uff0c\u201c\u90a3\u662f\u65e0\u7591\u7684\u4e86\u3002\u53ea\u4e0d\u77e5\u4e0e \u5389\u5bb6\u6709\u4f55\u6e0a\u6e90\uff1f\u201d\u539f\u6765\u8fd9\u5c11\u5e74\u9664\u4e86\u90a3\u4e00\u53cc\u7389\u5c3a\u4e4b\u5916\uff0c\u4ed6\u7684\u6b66\u529f\uff0c\u4e5f\u9887\u6709\u51e0\u62db\u4f3c\u662f\u5389\u76fc\u5f52\u7684\u8def \u6570\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5df2\u4e0d\u6562\u8f7b\u654c\uff0c\u5374\u4e5f\u4e0d\u60f3\u4ee4\u5bf9\u65b9\u53d7\u4f24\uff0c\u4ed6\u5df2\u8bd5\u51fa\u4e86\u90a3\u5c11\u5e74\u7684\u5185\u529f\u867d\u662f\u9020\u8be3\u4e0d\u51e1\uff0c\u5374 \u5c1a\u4e0d\u5982\u81ea\u5df1\uff0c\u5f53\u4e0b\u518d\u7528\u4e00\u6307\u7985\u529f\uff0c\u4f7f\u51fa\u4e86\u4e03\u5206\u771f\u529b\u3002 \u3000\u3000\u8fd9\u4e00\u5f39\u53d1\u51fa\u4e86\u9707\u8033\u6b32\u804b\u7684\u91d1\u77f3\u58f0\uff0c\u90a3\u5c11\u5e74\u6643\u4e86\u4e24\u6643\uff0c\u53cc\u5c3a\u4e00\u5206\uff0c\u7a81\u7136\u4e5f\u4ee5\u5929\u7f57\u6b65\u6cd5\uff0c\u6b3a \u8fd1\u8eab\u524d\uff0c\u53cc\u5c3a\u5149\u534e\u95ea\u95ea\uff0c\u8fde\u70b9\u91d1\u4e16\u9057\u7684\u4efb\u8109\u7763\u8109\u5341\u4e09\u9053\u5927\u7a74\uff01 \u3000\u3000\u91d1\u4e16\u9057\u7cbe\u901a\u6b63\u90aa\u5404\u6d3e\u7684\u4e0a\u4e58\u6b66\u529f\uff0c\u5c24\u5176\u4ee5\u70b9\u7a74\u8fd9\u4e00\u95e8\u529f\u592b\uff0c\u66f4\u5df2\u662f\u767b\u5cf0\u9020\u6781\uff0c\u5929\u4e0b\u65e0\u4eba \u53ef\u4e0e\u6297\u4e89\uff0c\u4f46\u8fd9\u65f6\u89c1\u8fd9\u5c11\u5e74\u7528\u53cc\u5c3a\u4f7f\u51fa\u201c\u98de\u7ba1\u60ca\u795e\u201d\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u4e5f\u4e0d\u7981\u5fc3\u5934\u4e3a\u4e4b\u5fae\u51db\u3002 \u3000\u3000\u8fd9\u662f\u90aa\u6d3e\u70b9\u7a74\u624b\u6cd5\u4e2d\u6700\u602a\u5f02\u7684\u4e00\u79cd\uff0c\u5f53\u5e74\u91d1\u4e16\u9057\u5728\u5927\u5185\u603b\u7ba1\u5bc7\u65b9\u768b\u7684\u5bb4\u4f1a\u4e0a\uff0c\u66fe\u78b0\u8fc7\u5f53 \u65f6\u7684\u5929\u4e0b\u7b2c\u4e00\u70b9\u7a74\u540d\u5bb6\u8fde\u5bb6\u5144\u5f1f\uff0c\u8fd9\u53cc\u5144\u5f1f\u4ee5\u201c\u56db\u7b14\u70b9\u516b\u8109\u201d\u7684\u529f\u592b\uff0c\u66fe\u4e0e\u91d1\u4e16\u9057\u6597\u5230\u767e\u62db \u5f00\u5916\uff0c\u91d1\u4e16\u9057\u597d\u4e0d\u5bb9\u6613\u624d\u8d62\u4e86\u4ed6\u4eec\uff0c\u81ea\u5df1\u4e5f\u53d7\u4e86\u51e0\u5904\u4f24\u3002 \u3000\u3000\u800c\u4eca\u8fd9\u9ed1\u8863\u5c11\u5e74\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u6b63\u662f\u4e0e\u90a3\u8fde\u5bb6\u5144\u5f1f\u7684\u70b9\u7a74\u624b\u6cd5\u5f02\u66f2\u540c\u5de5\uff0c\u867d\u7136\u4ed6\u53ea\u6709\u4e00\u53cc \u624b\uff0c\u4e0d\u80fd\u50cf\u8fde\u5bb6\u5144\u5f1f\u90a3\u822c\u540c\u65f6\u4ee5\u56db\u7b14\u8fde\u70b9\u5947\u7ecf\u516b\u8109\uff0c\u4f46\u4ed6\u7684\u5185\u529f\u5374\u80dc\u8fc7\u8fde\u5bb6\u5144\u5f1f\u7684\u8054\u624b\uff0c\u4e00 \u53cc\u7389\u5c3a\u4e5f\u80fd\u540c\u65f6\u8fde\u70b9\u4efb\u7763\u4e8c\u8109\u7684\u5341\u4e09\u9053\u5927\u7a74\uff0c\u4e24\u76f8\u6bd4\u8f83\uff0c\u5b9e\u5728\u662f\u6bd4\u90a3\u8fde\u5bb6\u5144\u5f1f\u66f4\u80dc\u4e00\u7b79\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5fc3\u4e2d\u72d0\u7591\u66f4\u751a\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u600e\u7684\u6240\u4f1a\u7684\u6b66\u529f\u7adf\u4e5f\u5982\u6b64\u9a73\u6742\uff0c\u751a \u81f3\u8fde\u5bb6\u7edd\u4e0d\u5916\u4f20\u7684\u70b9\u7a74\u4e4b\u79d8\uff0c\u4ed6\u4e5f\u4f3c\u4e4e\u6df1\u6089\u5176\u4e2d\u5965\u5999\uff0c\u800c\u4e14\u8fd8\u80fd\u751f\u51fa\u65b0\u7684\u53d8\u5316\u6765\uff1f\u96be\u9053\u4ed6\u4e5f \u5f97\u4e86\u4e0a\u4e58\u7684\u6b66\u529f\u5fc3\u6cd5\uff0c\u53ef\u4ee5\u4e00\u7406\u901a\u800c\u767e\u7406\u878d\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u5192\u9669\u8fdb\u62db\uff0c\u6b3a\u8eab\u76f8\u8feb\uff0c\u773c\u770b\u91d1\u4e16\u9057\u7684\u5341\u4e8c\u9053\u5927\u7a74\u5df2\u5728\u4ed6\u7684\u53cc\u5c3a\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u6700\u5c11\u4e5f \u4f1a\u6709\u4e00\u4e24\u5904\u7a74\u9053\u7ed9\u4ed6\u70b9\u4e2d\uff0c\u54ea\u77e5\u5c31\u5728\u8fd9\u4fc4\u9877\u4e4b\u95f4\uff0c\u5ffd\u89c1\u91d1\u4e16\u9057\u8eab\u5f62\u4e00\u6643\uff0c\u767b\u65f6\u56db\u9762\u516b\u65b9\u90fd\u662f \u91d1\u4e16\u9057\u7684\u5f71\u5b50\uff0c\u4ece\u5404\u4e2a\u4e0d\u540c\u7684\u65b9\u4f4d\u5411\u4ed6\u53d1\u638c\u653b\u6765\uff01 \u3000\u3000\u9ed1\u8863\u5c11\u5e74\u65e9\u5df2\u77e5\u9053\u91d1\u4e16\u9057\u7684\u5389\u5bb3\uff0c\u4f46\u52a8\u624b\u4e4b\u521d\uff0c\u4e5f\u8fd8\u4e0d\u600e\u6837\u5bb3\u6015\uff0c\u8fd9\u65f6\u624d\u5f53\u771f\u662f\u5927\u5403\u4e00 \u60ca\uff0c\u773c\u524d\u638c\u5f71\u5343\u91cd\uff0c\u4e0d\u77e5\u8981\u5411\u4f55\u65b9\u62db\u67b6\uff01 \u3000\u3000\u4ee5\u91d1\u4e16\u9057\u73b0\u5728\u7684\u529f\u592b\uff0c\u80dc\u8fc7\u5f53\u5e74\u4f55\u6b62\u5341\u500d\uff1f\u8981\u7834\u90a3\u5c11\u5e74\u7684\u70b9\u7a74\u624b\u6cd5\u5df2\u975e\u96be\u4e8b\uff0c\u4f46\u4e0d\u77e5\u600e \u7684\uff0c\u89c1\u4e86\u8fd9\u5c11\u5e74\u4e4b\u540e\uff0c\u4ed6\u603b\u662f\u6709\u70b9\u5fc3\u795e\u4e0d\u5b9a\uff0c\u6000\u7591\u8fd9\u5c11\u5e74\u6216\u8005\u4e0e\u5389\u80dc\u7537\u6709\u751a\u6e0a\u6e90\uff0c\u56e0\u6b64\u4fbf\u4e0d \u81ea\u89c9\u7684\u603b\u662f\u624b\u4e0b\u7559\u60c5\uff0c\u5341\u6210\u529f\u592b\u6700\u591a\u7528\u5230\u4e03\u6210\u3002 \u3000\u3000\u4f46\u542c\u5f97\u4e00\u7247\u6323\u6323\u4e4b\u58f0\uff0c\u6709\u5982\u7e41\u5f26\u6025\u594f\uff0c\u90a3\u5c11\u5e74\u98de\u8eab\u8dc3\u8d77\uff0c\u5728\u534a\u7a7a\u4e2d\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u8eab\u5f62 \u672a\u66fe\u843d\u5730\uff0c\u7adf\u7136\u53c8\u5411\u91d1\u4e16\u9057\u6251\u4e0b\u6765\uff0c\u4ed6\u53cc\u5c3a\u4e00\u4e2a\u76d8\u65cb\uff0c\u5408\u6210\u4e86\u4e00\u4e2a\u5706\u5f27\uff0c\u7528\u7684\u5374\u662f\u622a\u7136\u4e0d\u540c \u7684\u62db\u5f0f\uff0c\u5de6\u624b\u7684\u7389\u5c3a\u4f7f\u7684\u7adf\u4e00\u53d8\u800c\u4e3a\u5251\u6cd5\uff0c\u5411\u91d1\u4e16\u9057\u7684\u7435\u7436\u9aa8\u6311\u6765\uff0c\u53f3\u624b\u7684\u7389\u5c3a\u5219\u4ecd\u7136\u662f\u5f53 \u4f5c\u5224\u5b98\u7b14\u7528\uff0c\u88ad\u51fb\u91d1\u4e16\u9057\u9634\u7ef4\u3001\u9633\u7ef4\u4e24\u8109\u7684\u4e2d\u9675\u3001\u5730\u9600\u3001\u5929\u65cb\u3001\u9f9f\u85cf\u3001\u4f0f\u5154\u3001\u73af\u8df3\u3001\u7389\u8861\u7b49 \u4e03\u5904\u5927\u7a74\uff0c\u7531\u4e8e\u4ed6\u662f\u51cc\u7a7a\u70b9\u4e0b\uff0c\u6765\u52bf\u66f4\u662f\u52b2\u75be\u975e\u5e38\uff01 \u3000\u3000\u91d1\u4e16\u9057\u5df2\u7528\u5230\u4e86\u4e03\u6210\u529f\u529b\uff0c\u4ee5\u4e00\u6307\u7985\u529f\uff0c\u63a5\u8fde\u5f39\u4e2d\u4e86\u90a3\u9ed1\u8863\u5c11\u5e74\u7684\u7389\u5c3a\uff0c\u89c1\u90a3\u5c11\u5e74\u5c45\u7136 \u7981\u53d7\u5f97\u8d77\uff0c\u53ea\u662f\u5728\u534a\u7a7a\u4e2d\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u4fbf\u53c8\u6251\u51fb\u4e0b\u6765\uff0c\u4e5f\u4e0d\u7981\u597d\u751f\u60ca\u8be7\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6 \u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5185\u529f\u600e\u7684\u4fbf\u6709\u8fd9\u822c\u9020\u8be3\uff1f\u770b\u6765\u4e0e\u90a3\u6587\u5c9b\u4e3b\u7adf\u4f3c\u5728\u4f2f\u4ef2\u4e4b\u95f4\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u6c5f\u5357\u7684\u58f0\u97f3\u5728\u8fdc\u5904\u5927\u53eb\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4f60\u5728\u54ea\u513f\uff1f\u53ef\u78b0\u89c1\u4e86\u90a3\u4e24\u53ea\u755c \u7272\u4e48\uff1f\u201d\u539f\u6765\u7384\u5973\u89c2\u8bf8\u4eba\uff0c\u90fd\u7ed9\u90a3\u91d1\u6bdb\u6d5a\u7684\u543c\u58f0\u60ca\u9192\uff0c\u4e0e\u6c5f\u5357\u4e00\u9053\u8ffd\u51fa\u6765\u4e86\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u5c11\u5e74\u53cc\u5c3a\u9f50\u4e0b\uff0c\u5de6\u624b\u7684\u7389\u5c3a\u5148\u5230\uff0c\u628a\u7389\u5c3a\u5f53\u4f5c\u957f\u5251\u6765\u7528\uff0c\u4f7f\u51fa\u4e86\u4e00\u62db\u6781\u51cc\u5389\u7684\u5251 \u6cd5\uff0c\u6311\u91d1\u4e16\u9057\u7684\u7435\u7436\u9aa8\uff0c\u8fd9\u7435\u7436\u9aa8\u4e00\u6311\uff0c\u591a\u597d\u7684\u6b66\u529f\u4e5f\u8981\u843d\u4e2a\u6b8b\u5e9f\uff0c\u4ed6\u89c1\u91d1\u4e16\u9057\u4e0d\u8eb2\u4e0d\u95ea\uff0c \u4e5f\u4e0d\u8fd8\u51fb\uff0c\u4e0d\u89c9\u4e00\u6014\uff0c\u4f46\u4e5f\u53ea\u7565\u4e00\u8fdf\u7591\uff0c\u7acb\u5373\u4fbf\u559d\u4e86\u4e00\u58f0\uff1a\u201c\u7740\uff01\u201d\u4ecd\u7136\u75be\u4e0b\u6740\u624b\uff01 \u3000\u3000\u54ea\u77e5\u91d1\u4e16\u9057\u662f\u6709\u610f\u8ba9\u4ed6\u6233\u4e2d\u8eab\u5b50\u7684\uff0c\u91d1\u4e16\u9057\u5df2\u7ec3\u6210\u4e86\u201c\u91d1\u521a\u4e0d\u574f\u795e\u529f\u201d\uff0c\u83ab\u8bf4\u662f\u8fd9\u9ed1\u8863 \u5c11\u5e74\uff0c\u5373\u7b97\u5389\u80dc\u7537\u590d\u751f\uff0c\u4e5f\u672a\u5fc5\u80fd\u4ee4\u4ed6\u4f24\u635f\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4fc4\u9877\u4e4b\u95f4\uff0c\u53ea\u542c\u5f97\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u9ed1\u8863\u5c11\u5e74\u7684\u7389\u5c3a\u5df2\u5728\u4ed6\u7684\u80a9\u5934\u91cd\u91cd\u7684\u6233\u4e86\u4e00 \u4e0b\uff0c\u53ef\u662f\u91d1\u4e16\u9057\u4e5f\u611f\u89c9\u5f97\u5230\uff0c\u90a3\u5c11\u5e74\u4e5f\u672a\u66fe\u7528\u5c3d\u5168\u529b\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4e00\u51fb\u5f97\u4e2d\uff0c\u6709\u624b\u5f53\u4f5c\u5224\u5b98\u7b14\u4f7f\u7684\u90a3\u6839\u7389\u5c3a\u6765\u52bf\u4fbf\u7f13\u4e86\u4e0b\u6765\uff0c\u91d1\u4e16\u9057\u60f3\u9053\uff1a\u201c\u539f\u6765 \u4ed6\u4e5f\u5e76\u4e0d\u60f3\u628a\u6211\u7f6e\u4e4b\u6b7b\u5730\uff0c\u53ea\u662f\u8bf4\u8bdd\u8bf4\u5f97\u51f6\u72e0\u800c\u5df2\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u7ed9\u91d1\u4e16\u9057\u7684\u53cd\u5f39\u4e4b\u529b\u4e00\u9707\uff0c\u8fd9\u624d\u611f\u5230\u4e0d\u5999\uff0c\u84e6\u5403\u4e00\u60ca\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u91d1\u4e16\u9057 \u5ffd\u5730\u559d\u4e86\u4e00\u58f0\uff1a\u201c\u6492\u624b\uff01\u201d\u95ea\u7535\u822c\u7684\u4fbf\u628a\u90a3\u5c11\u5e74\u7684\u4e24\u6839\u7389\u5c3a\u90fd\u593a\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5446\u4e86\u4e00\u5446\uff0c\u8f6c\u8eab\u4fbf\u8dd1\uff0c\u521a\u521a\u8dd1\u51fa\u51e0\u6b65\uff0c\u91d1\u4e16\u9057\u53c8\u5df2\u5982\u5f71\u968f\u5f62\uff0c\u8ffd\u4e86\u5230\u6765\uff0c\u5728\u4ed6\u7684 \u80a9\u5934\u8f7b\u8f7b\u4e00\u62cd\uff0c\u90a3\u5c11\u5e74\u6012\u9053\uff1a\u201c\u597d\uff0c\uff0c\u91d1\u4e16\u9057\uff0c\u6211\u628a\u8fd9\u6761\u6027\u547d\u4ea4\u7ed9\u4f60\u5566\uff01\u201d\u53cd\u624b\u4e00\u638c\uff0c\u52bf\u82e5 \u5954\u96f7\uff01\u4ed6\u4ee5\u4e3a\u91d1\u4e16\u9057\u4e0d\u80af\u653e\u8fc7\u4ed6\uff0c\u6545\u6b64\u8c41\u51fa\u4e86\u6027\u547d\uff0c\u8981\u4e0e\u91d1\u4e16\u9057\u4e00\u62fc\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u4e00\u638c\u6253\u51fa\uff0c\u5ffd\u5730\u611f\u5230\u624b\u5fc3\u4e00\u7247\u6c81\u51c9\uff0c\u5374\u539f\u6765\u662f\u91d1\u4e16\u9057\u5c06\u90a3\u4e24\u6839\u7389\u5c3a\u63b7\u5230\u4e86\u4ed6\u7684\u624b \u4e2d\uff0c\u8fd8\u7ed9\u4ed6\u3002\u90a3\u5c11\u5e74\u4e0d\u89c9\u53c8\u662f\u4e00\u5446\uff0c\u53ea\u542c\u5f97\u91d1\u4e16\u9057\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8d70\u5427\uff01\u4ee5\u540e\u4e0d\u8bb8\u518d\u5230\u6c13\u5c71 \u95f9\u4e8b\uff01\u518d\u6b21\u76f8\u9022\uff0c\u6211\u5c31\u5bf9\u4f60\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u5411\u91d1\u4e16\u9057\u77aa\u4e86\u4e00\u773c\uff0c\u6479\u5730\u4e00\u58f0\u957f\u5578\uff0c\u90a3\u4e24\u53ea\u91d1\u6bdb\u68ad\u8dd1\u4e86\u51fa\u6765\uff0c\u90a3\u5c11\u5e74\u6325\u4e00\u6325\u624b\uff0c \u8de8\u4e0a\u4e86\u4e00\u53ea\u91d1\u6bdb\u72e1\u7684\u80cc\u810a\uff0c\u5c06\u5b83\u5f53\u9a6c\u6765\u9a91\uff0c\u970e\u65f6\u95f4\uff0c\u4e00\u4eba\u4e24\u517d\uff0c\u5df2\u8dd1\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u91d1\u4e16\u9057\u6b63\u81ea\u5fc3\u5934\u6005\u60c6\uff0c\u5ffd\u542c\u5f97\u8033\u8fb9\u53a2\u6709\u4e2a\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u91d1\u4e16\u9057\uff0c\u4f60\u5be1\u60c5\u8584\u4e49\uff0c\u5c45\u7136\u8fd8\u6562 \u5145\u4f5c\u597d\u4eba\uff0c\u771f\u662f\u606c\u4e0d\u77e5\u803b\u3002\u4ece\u4eca\u4e4b\u540e\uff0c\u54b1\u4eec\u7684\u51a4\u4ec7\u662f\u7ed3\u5b9a\u7684\u4e86\u3002\u201d\u6b63\u662f\u90a3\u5c11\u5e74\u7684\u58f0\u97f3\uff0c\u539f\u6765 \u4ed6\u4e5f\u4f1a\u5929\u9041\u4f20\u97f3\u4e4b\u672f\u3002 \u3000\u3000\u542c\u8fd9\u8bdd\u8bed\uff0c\u8fd9\u5c11\u5e74\u4e4b\u6240\u4ee5\u75db\u6068\u91d1\u4e16\u9057\uff0c\u4e43\u662f\u4e3a\u5389\u80dc\u7537\u6253\u62b1\u4e0d\u5e73\uff0c\u56e0\u6b64\u91d1\u4e16\u9057\u867d\u662f\u5bf9\u4ed6\u624b \u4e0b\u7559\u60c5\uff0c\u4ed6\u4e5f\u8981\u628a\u91d1\u4e16\u9057\u5f53\u4f5c\u4ec7\u4eba\u3002\u91d1\u4e16\u9057\u542c\u4e86\uff0c\u66f4\u662f\u9eef\u7136\u795e\u4f24\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e3a\u4e86\u80dc\u7537\uff0c \u6211\u7ec8\u8eab\u4e0d\u5a36\uff0c\u5374\u4e0d\u6599\u5728\u522b\u4eba\u773c\u4e2d\uff0c\u6211\u4ecd\u7136\u8fd8\u662f\u4e2a\u8584\u5e78\u4e4b\u4eba\uff01\u201d\u53c8\u60f3\u9053\uff1a\u201c\u8fd9\u9ed1\u8863\u5c11\u5e74\u7adf\u7136\u4e3a \u4e86\u80dc\u7537\u800c\u6068\u6211\uff0c\u96be\u9053\u4ed6\u5f53\u771f\u662f\u548c\u80dc\u7537\u5927\u6709\u6e0a\u6e90\uff1f\u201d\u4f46\u5389\u80dc\u7537\u7684\u5168\u5bb6\uff0c\u8fde\u540c\u6d77\u5916\u90a3\u4e00\u652f\u4eb2\u4eba\u90fd \u5df2\u6b7b\u5c3d\u6b7b\u7edd\u4e86\uff0c\u8fd9\u5374\u53c8\u5982\u4f55\u89e3\u91ca\uff1f \u3000\u3000\u6c5f\u5357\u7684\u547c\u558a\u58f0\u53c8\u4f20\u4e86\u8fc7\u6765\uff0c\u91d1\u4e16\u9057\u8fd9\u624d\u6709\u5de5\u592b\u5e94\u4ed6\uff0c\u4e0d\u591a\u4e00\u4f1a\uff0c\u6c5f\u5357\u3001\u8c37\u4e4b\u534e\u3001\u8def\u82f1 \u8c6a\u3001\u767d\u82f1\u6770\u8fd9\u4e00\u73ed\u4eba\u90fd\u8d76\u4e86\u5230\u6765\u3002 \u3000\u3000\u91d1\u4e16\u9057\u89c1\u4e86\u8c37\u4e4b\u534e\uff0c\u8111\u6d77\u4e4b\u4e2d\u5ffd\u5730\u53c8\u6d6e\u73b0\u51fa\u5389\u80dc\u7537\u7684\u5f71\u5b50\uff0c\u5389\u80dc\u7537\u7684\u5e7b\u5f71\u5728\u5411\u4ed6\u51b7\u7b11\uff1b \u4e0d\u77e5\u600e\u7684\uff0c\u91d1\u4e16\u9057\u5ffd\u5730\u611f\u5230\u5fc3\u4e2d\u6709\u6127\uff0c\u4f3c\u4e4e\u81ea\u5df1\u5f53\u771f\u662f\u6709\u70b9\u513f\u5bf9\u4e0d\u8d77\u5389\u80dc\u7537\u3002 \u3000\u3000\u767d\u82f1\u6770\u95ee\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u53d1\u73b0\u4e86\u90a3\u4e24\u53ea\u602a\u517d\u4e48\uff1f\u6211\u4eec\u4f3c\u4e4e\u542c\u5f97\u8fd9\u8fb9\u6709\u6253\u6597\u7684\u58f0\u97f3\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u5b9a\u4e86\u5b9a\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u53ea\u91d1\u6bdb\u733f\u679c\u7136\u662f\u6211\u76f8\u8bc6\u7684\uff0c\u6211\u8fd8\u89c1\u7740\u4e86\u5b83\u4eec\u7684\u4e3b\u4eba\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d\u91d1\u4e16\u9057\u6447\u4e86\u6447\u5934\uff0c\u73b0\u51fa\u4e00\u6d3e\u832b\u7136\u7684\u795e\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9 \u4eba\u662f\u5929\u9b54\u6559\u7684\u4eba\uff0c\u4ed6\u8ba4\u5f97\u6211\uff0c\u6211\u5374\u4e0d\u8ba4\u5f97\u4ed6\u3002\u4ed6\u5df2\u7ecf\u7ed9\u6211\u6253\u8dd1\u4e86\uff0c\u4ee5\u540e\u4e5f\u4e0d\u4f1a\u518d\u5230\u6c13\u5c71\u4e86\u3002\u201d \u3000\u3000\u767d\u82f1\u6770\u7b49\u4eba\u90fd\u653e\u4e0b\u4e86\u5fc3\u4e0a\u7684\u77f3\u5934\uff0c\u53ea\u6709\u8c37\u4e4b\u534e\u5374\u611f\u5230\u4e86\u91d1\u4e16\u9057\u7684\u795e\u60c5\u6709\u5f02\uff0c\u6697\u6697\u5fe7\u8651\u3002 \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u660e\u5929\u6211\u60f3\u4e0e\u6c5f\u5357\u4fbf\u5230\u7ec4\u6765\u5c71\u53bb\u3002\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u8fd9\u4e48\u5feb\u4fbf\u8981\u8d70\u4e48\uff1f\u6211\u8fd8\u60f3 \u591a\u7559\u4f60\u4eec\u4e24\u5929\u5462\u3002\u201d\u63a5\u7740\u53c8\u7b11\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6c5f\u5357\u5931\u4e86\u5b69\u5b50\uff0c\u4e00\u5b9a\u5fc3\u6025\u5f97\u5f88\uff0c\u6211\u4e5f\u4e0d\u6562\u5f3a\u7559\u4f60 \u4eec\u4e86\uff0c\u7b49\u4f60\u4eec\u5c06\u5b69\u5b50\u6551\u4e86\u56de\u6765\uff0c\u54b1\u4eec\u518d\u53d9\u5427\u3002\u201d \u3000\u3000\u767d\u82f1\u6770\u9053\uff1a\u201c\u7a0b\u5e08\u5144\u7387\u9886\u540c\u95e8\u5728\u836f\u738b\u5e99\u5468\u56f4\u7684\u5c71\u5934\u641c\u7d22\uff0c\u521a\u624d\u5df2\u7ecf\u56de\u6765\u3002\u53d1\u73b0\u4e86\u8bb8\u591a\u8001 \u864e\u7684\u5c38\u4f53\uff0c\u53ea\u5269\u4e00\u4e2a\u7a7a\u58f3\uff0c\u5fc3\u809d\u8111\u9ad3\u90fd\u65e0\u534a\u70b9\u6b8b\u7559\uff0c\u6211\u4eec\u731c\u60f3\u90a3\u4e24\u53ea\u91d1\u6bdb\u68ad\u4e5f\u5403\u4e0d\u4e86\u8fd9\u8bb8 \u591a\uff0c\u8fd8\u6709\uff0c\u672c\u5c71\u730e\u6237\u6240\u57f9\u690d\u7684\u4e00\u79cd\u730e\u517d\u7684\u6bd2\u8349\uff0c\u90a3\u53ea\u662f\u672c\u5c71\u624d\u80fd\u751f\u957f\u4fa6\uff0c\u4e5f\u5df2\u7ed9\u4eba\u91c7\u5f97\u5e72\u5e72 \u51c0\u51c0\u3002\u6211\u4eec\u731c\u60f3\u8fd9\u4eba\u5b9a\u662f\u8981\u62ff\u8001\u864e\u7684\u5fc3\u809d\u8111\u9ad3\u548c\u8fd9\u79cd\u6bd2\u8349\u53bb\u5236\u70bc\u4ec0\u4e48\u6bd2\u836f\uff0c\u5982\u4eca\u91d1\u5927\u4fa0\u5df2\u7ecf \u77e5\u9053\u4e86\u8fd9\u4eba\u662f\u5929\u9b54\u6559\u4e2d\u7684\u4eba\u7269\uff0c\u8fd9\u5c31\u66f4\u53ef\u8651\u4e86\u3002\u201d \u3000\u3000\u8def\u82f1\u8c6a\u9053\uff1a\u201c\u5929\u9b54\u6559\u64c5\u7528\u6bd2\u836f\u5bb3\u4eba\uff0c\u91d1\u5927\u4fa0\uff0c\u4f60\u6b64\u53bb\u7ec4\u6765\u5c71\uff0c\u8d81\u5b83\u7fbd\u6bdb\u672a\u4e30\uff0c\u5c06\u5b83\u526a\u6389 \u4e86\u5427\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u5f85\u6211\u5230\u4e86\u7956\u889c\u5c71\uff0c\u770b\u770b\u4ed6\u4eec\u7684\u884c\u6b62\uff0c\u518d\u4f5c\u5b9a\u593a\u5427\u3002\u201d\u8def\u82f1\u8c6a \u5ac9\u6076\u5982\u4ec7\uff0c\u542c\u4e86\u91d1\u4e16\u9057\u6a21\u68f1\u4e24\u53ef\u7684\u8bf4\u8bdd\uff0c\u5927\u4e3a\u4e0d\u6ee1\u3002\u8c37\u4e4b\u534e\u5df2\u5148\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5929\u9b54\u6559\u867d\u7136 \u8ff9\u8fd1\u5996\u90aa\uff0c\u5012\u5e95\u8fd8\u672a\u66fe\u4f5c\u51fa\u4ec0\u4e48\u5927\u5978\u5927\u6076\u4e4b\u4e8b\uff0c\u672a\u53ef\u5373\u884c\u65a9\u5c3d\u6740\u7edd\uff0c\u8fd8\u662f\u518d\u770b\u770b\u4ed6\u4eec\u4ee5\u540e\u7684 \u884c\u4e8b\u518d\u8bf4\u3002\u201d\u8def\u82f1\u8c6a\u542c\u5f97\u638c\u95e8\u5df2\u7ecf\u540c\u610f\u91d1\u4e16\u9057\u7684\u610f\u89c1\uff0c\u4e5f\u5c31\u4e0d\u4fbf\u591a\u8bf4\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u6697\u6697\u60ed\u6127\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e4b\u534e\u5bf9\u4eba\u5904\u4e8b\uff0c\u78ca\u843d\u5149\u660e\uff0c\u5b9e\u662f\u975e\u6211\u53ef\u53ca\u3002\u201d\u539f\u6765\u4ed6\u5bf9 \u5929\u9b54\u6559\u867d\u65e0\u597d\u611f\uff0c\u5374\u4e5f\u5e76\u65e0\u7279\u522b\u6076\u611f\uff0c\u5185\u91cc\u539f\u56e0\uff0c\u81f3\u5c11\u6709\u4e00\u534a\u662f\u4e3a\u4e86\u5389\u80dc\u7537\u7684\u7f18\u6545\u3002\u2014\u2014\u867d \u7136\u4ed6\u4e0d\u76f8\u4fe1\u521b\u7acb\u5929\u9b54\u6559\u662f\u5389\u80dc\u7537\u7684\u9057\u5fd7\uff0c\u4f46\u4ed6\u5374\u4e0d\u80fd\u4e0d\u7591\u60d1\u5929\u9b54\u6559\u7684\u4eba\u7269\u4e0e\u5389\u80dc\u7537\u5927\u6709\u6e0a\u6e90\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\uff0c\u91d1\u4e16\u9057\u3001\u6c5f\u5357\u4fbf\u4e0e\u4f17\u4eba\u8f9e\u522b\uff0c\u8c37\u4e4b\u534e\u72ec\u81ea\u9001\u4e86\u4ed6\u4e00\u7a0b\uff0c\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u6b64\u884c\u82e5 \u7136\u987a\u5229\uff0c\u7d22\u56de\u6c5f\u5357\u7684\u5b69\u5b50\uff0c\u6211\u4f1a\u8bf7\u5f53\u5730\u7684\u4e10\u5e2e\u5f1f\u5b50\u7ed9\u4f60\u634e\u4e2a\u4fe1\u513f\uff0c\u6211\u4eec\u518d\u5f80\u82cf\u5dde\u8bbf\u9648\u5929\u5b87\uff0c \u7136\u540e\u548c\u4ed6\u4e00\u540c\u56de\u6765\uff0c\u7ed9\u4f60\u89e3\u90a3\u83b2\u513f\u7684\u8eab\u4e16\u4e4b\u8c1c\u3002\u53ea\u662f\u5982\u6b64\u4e00\u6765\uff0c\u6015\u8981\u5728\u534a\u5e74\u4e4b\u540e\uff0c\u624d\u80fd\u518d\u89c1 \u5230\u4f60\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u7b11\u9053\uff1a\u201c\u4eba\u4e4b\u76f8\u77e5\uff0c\u8d35\u76f8\u77e5\u5fc3\u3002\u738b\u52c3\u7684\u8bd7\u8bf4\u5f97\u597d\uff1a\u6d77\u5185\u5b58\u77e5\u5df1\uff0c\u5929\u6daf\u82e5\u6bd4\u90bb\u3002\u5f80 \u65e5\u4f60\u5728\u6d77\u5916\u6f02\u6d41\uff0c\u6211\u6bcf\u5f53\u60f3\u8d77\u4f60\u65f6\uff0c\u5c31\u5e38\u5e38\u5ff5\u8fd9\u4e24\u53e5\u8bd7\u7684\u3002\u5982\u4eca\u5c0f\u522b\u534a\u5e74\uff0c\u53c8\u4f55\u8db3\u70e6\u607c\uff1f\u4e16 \u9057\uff0c\u4f60\u8fd9\u6b21\u56de\u6765\uff0c\u6837\u6837\u90fd\u663e\u5f97\u8001\u6210\u591a\u4e86\u3002\u6211\u5f88\u653e\u5fc3\u3002\u53ea\u662f\u6211\u8fd8\u6709\u51e0\u53e5\u8bdd\u60f3\u548c\u4f60\u8bf4\u8bf4\u3002\u201d\u91d1\u4e16 \u9057\u7b11\u9053\uff1a\u201c\u6211\u6b63\u662f\u60f3\u8981\u4f60\u4e34\u522b\u8d60\u8a00\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u5ffd\u9053\uff1a\u201c\u4e16\u9057\uff0c\u4f60\u73b0\u5728\u60f3\u4e9b\u4ec0\u4e48\uff1f\u201d\u91d1\u4e16\u9057\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u60f3\u7684\u5f53\u7136\u662f\u600e\u6837 \u53bb\u6551\u6c5f\u5357\u7684\u5b69\u5b50\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u5f88\u597d\uff0c\u6211\u4f46\u613f\u4f60\u5c11\u60f3\u8fc7\u53bb\u7684\u4e8b\u60c5\uff0c\u591a\u60f3\u672a\u6765\u7684\u8ba1\u5212\uff01\u201d\u91d1\u4e16\u9057\u9762\u4e0a \u4e00\u7ea2\uff0c\u8fd9\u624d\u77e5\u9053\u4e86\u8c37\u4e4b\u534e\u8bdd\u4e2d\u7684\u771f\u610f\u3002\u4ed6\u6628\u665a\u4e00\u591c\u65e0\u7720\uff0c\u60f3\u7684\u5c3d\u662f\u4e0e\u5389\u80dc\u7537\u8fc7\u53bb\u7684\u79cd\u79cd\u4e8b \u60c5\uff0c\u8c37\u4e4b\u534e\u7684\u8bdd\u8bed\uff0c\u5c31\u4f3c\u5bdf\u7834\u4e86\u4ed6\u5fc3\u4e2d\u7684\u9690\u79d8\u4f3c\u7684\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u62ac\u8d77\u5934\u6765\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u770b\u5929\u4e0a\u7684\u90a3\u7247\u6d6e\u4e91\uff0c\u521a\u624d\u6d6e\u4e91\u63a9\u65e5\uff0c\u5929\u8272\u9634\u6c89\u6c89\u7684\uff1b\u73b0\u5728 \u6d6e\u4e91\u8fc7\u53bb\u4e86\uff0c\u53c8\u8c41\u7136\u5f00\u6717\u4e86\u3002\u2018\u7eb5\u6709\u6d6e\u4e91\u80fd\u63a9\u65e5\uff0c\u9634\u9706\u4ea6\u4ec5\u662f\u987b\u81fe\u2019\u3002\u6d6e\u4e91\u63a9\u65e5\u603b\u662f\u6682\u65f6 \u7684\uff0c\u4f46\u613f\u4f60\u7684\u5fc3\u5883\u4e5f\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8bf4\u8bdd\uff0c\u600e\u4e48\u603b\u662f\u7ed5\u7740\u5f2f\u5b50\uff0c\u50cf\u662f\u6253\u7740\u54d1\u8c1c\u4f3c\u7684\uff0c\u6211\u542c\u4e5f\u542c\u4e0d\u61c2\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u61ac\u7136\u5982\u6709\u6240\u609f\uff0c\u4e00\u63d6\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7684\u91d1\u7389\u826f\u8a00\u3002\u201d\u4f46\u4ed6\u5fc3\u5934\u4e0a\u7684\u9634\u5f71\u662f\u5426\u50cf\u853d \u65e5\u7684\u6d6e\u4e91\u4e00\u6837\uff0c\u8fc5\u901f\u79fb\u5f00\uff0c\u90a3\u5374\u662f\u8fde\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u7684\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u6005\u6005\u60c6\u60c6\uff0c\u4e0b\u4e86\u6c13\u5c71\uff0c\u4e09\u5929\u4e4b\u540e\uff0c\u4fbf\u4e0e\u6c5f\u5357\u8d76\u5230\u4e86\u7ec4\u6765\u5c71\u897f\u9762\u7684\u5e61\u9f99\u9547\u3002\u8fd9\u662f\u59ec \u6653\u98ce\u4e0e\u6c5f\u5357\u76f8\u7ea6\u4f1a\u9762\u7684\u5730\u65b9\u3002 \u3000\u3000\u6c5f\u5357\u5c48\u6307\u4e00\u7b97\uff0c\u7b11\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u54b1\u4eec\u6765\u65e9\u4e86\u4e00\u5929\u4e86\u3002\u59ec\u5927\u54e5\u4e0e\u6211\u5206\u624b\u4e4b\u65f6\uff0c\u662f\u7ea6\u6211\u5341 \u65e5\u4e4b\u540e\u5728\u6b64\u89c1\u9762\u7684\uff0c\u73b0\u5728\u624d\u662f\u7b2c\u4e5d\u5929\u3002\u65e9\u77e5\u5982\u6b64\uff0c\u54b1\u4eec\u8fd8\u53ef\u4ee5\u5728\u6c13\u5c71\u591a\u7559\u4e00\u5929\u7684\uff0c\u8fd9\u90fd\u662f\u4e3a \u4e86\u6211\u7684\u7f18\u6545\uff0c\u7d2f\u5f97\u4f60\u624d\u4e0e\u8c37\u5973\u4fa0\u89c1\u9762\uff0c\u4fbf\u53c8\u5206\u624b\u4e86\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6765\u65e9\u603b\u6bd4\u6765\u8fdf\u7684\u597d\u3002\u4e5f\u8bb8\u4ed6\u5df2\u7ecf\u4ece\u7ec4\u6765\u5c71\u56de\u6765\u4e86\u5462\uff1f\u201d \u3000\u3000\u76a4\u9f99\u9547\u5730\u65b9\u4e0d\u5927\uff0c\u4e24\u4eba\u5728\u9547\u4e0a\u8d70\u4e86\u4e00\u5708\uff0c\u5929\u8272\u5df2\u8fd1\u9ec4\u660f\uff0c\u91d1\u4e16\u9057\u9053\uff1a\u201c\u8981\u662f\u59ec\u6653\u98ce\u5728\u8fd9 \u9547\u4e0a\uff0c\u4ed6\u81ea\u4f1a\u6765\u627e\u54b1\u4eec\u3002\u770b\u6765\u4ed6\u662f\u8fd8\u672a\u56de\u6765\uff0c\u54b1\u4eec\u5c31\u6682\u4e14\u5728\u6b64\u4f4f\u5bbf\u4e00\u665a\u5427\uff0c\u8981\u662f\u660e\u5929\u8fd8\u4e0d\u89c1 \u4ed6\uff0c\u6211\u5c31\u548c\u4f60\u8fdb\u7ec4\u6765\u5c71\u53bb\u3002\u201d \u3000\u3000\u4e24\u4eba\u4fbf\u6295\u8fdb\u9547\u4e0a\u552f\u4e00\u7684\u5ba2\u5e97\u4f4f\u5bbf\uff0c\u6c5f\u5357\u8fde\u65e5\u5954\u6ce2\uff0c\u9971\u9910\u4e86\u4e00\u987f\uff0c\u5012\u5934\u4fbf\u7761\u3002\u91d1\u4e16\u9057\u5374\u662f \u5fc3\u4e8b\u5982\u9ebb\uff0c\u72ec\u501a\u7a97\u524d\u770b\u6708\u3002\u5230\u5f97\u4e09\u66f4\u65f6\u5206\uff0c\u5ffd\u542c\u5f97\u535c\u535c\u535c\u7684\u6572\u95e8\u58f0\uff0c\u6c5f\u5357\u8df3\u8d77\u6765\u9053\uff1a\u201c\u662f\u59ec \u5927\u54e5\u7684\u58f0\u97f3\u3002\u201d\u91d1\u4e16\u9057\u5fc3\u91cc\u6697\u6697\u5947\u602a\uff1a\u201c\u600e\u7684\u6765\u7684\u4f3c\u4e4e\u4e0d\u6b62\u4e00\u4eba\u3002\u201d\u6c5f\u5357\u4eae\u8d77\u6cb9\u706f\uff0c\u6253\u5f00\u623f \u95e8\uff0c\u53ea\u89c1\u4e24\u4e2a\u4eba\u6276\u7740\u59ec\u6653\u98ce\u8fdb\u6765\uff0c\u6c5f\u5357\u5413\u4e86\u4e00\u8df3\uff0c\u59ec\u6653\u98ce\u5df2\u5636\u58f0\u53eb\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u679c\u7136\u662f \u4f60\uff01\u4f60\u6765\u4e86\uff0c\u6211\u5c31\u5b89\u5fc3\u4e86\uff01\u6709\u91d1\u5927\u4fa0\u5728\u8fd9\u513f\uff01\u4f60\u4eec\u53ef\u4ee5\u8d70\u4e86\uff01\u201d\u540e\u9762\u8fd9\u4e24\u53e5\u8bdd\u662f\u5bf9\u90a3\u4e24\u4e2a\u6276 \u7740\u4ed6\u7684\u4eba\u8bf4\u7684\u3002 \u3000\u3000\u59ec\u6653\u98ce\u8eab\u4e0a\u6ca1\u6709\u8840\u6e0d\uff0c\u5934\u9762\u624b\u8db3\u4e5f\u6ca1\u4f24\u75d5\uff0c\u4f46\u542c\u90a3\u5636\u54d1\u4f4e\u6c89\u7684\u58f0\u97f3\uff0c\u5374\u663e\u7136\u662f\u4e2d\u6c14\u4e0d \u8db3\u3001\u53d7\u4e86\u91cd\u4f24\u3002\u6c5f\u5357\u518d\u4ed4\u7ec6\u770b\u65f6\uff0c\u521a\u624d\u90a3\u4e24\u4e2a\u4eba\u662f\u59ec\u6653\u98ce\u7684\u5f92\u5f1f\uff0c\u4e5f\u5c31\u662f\u5728\u65b0\u5b89\u9547\u4e0a\uff0c\u4e54\u626e \u6e05\u5175\uff0c\u968f\u540c\u59ec\u6653\u98ce\u6765\u52ab\u201c\u6587\u516c\u5b50\u201d\u73e0\u5b9d\u7684\u90a3\u4e24\u4e2a\u4eba\uff0c\u90a3\u4e24\u4e2a\u4eba\u5df2\u5306\u5306\u5fd9\u5fd9\u5730\u8d70\u4e86\u3002 \u3000\u3000\u5230\u4e86\u6b64\u65f6\uff0c\u91d1\u4e16\u9057\u4e5f\u81ea\u6709\u70b9\u60ca\u7591\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ed6\u7a76\u7adf\u662f\u78b0\u5230\u4e86\u4ec0\u4e48\u5389\u5bb3\u7684\u4eba\u7269\uff1f\u201d\u8981 \u77e5\u59ec\u6653\u98ce\u5c06\u4ed6\u7684\u4e24\u4e2a\u5f92\u5f1f\u5306\u5fd9\u9063\u8d70\uff0c\u90a3\u5206\u660e\u662f\u5bb3\u6015\u654c\u4eba\u5bfb\u6765\uff0c\u91d1\u4e16\u9057\u96be\u4ee5\u7167\u987e\u8fd9\u4e48\u591a\u4eba\u3002\u91d1 \u4e16\u9057\u662f\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u5f53\u7136\u8bc6\u5f97\u59ec\u6653\u98ce\u8fd9\u5c42\u7528\u610f\u3002 \u3000\u3000\u59ec\u6653\u98ce\u6643\u4e86\u4e24\u6643\uff0c\u201c\u5c0f\u5144\u5f1f\u201d\u65b9\u624d\u53eb\u5f97\u51fa\u53bb\uff0c\u4fbf\u5df2\u7ad9\u7acb\u4e0d\u7a33\uff0c\u8981\u5012\u4e0b\u53bb\uff0c\u91d1\u4e16\u9057\u8fde\u5fd9\u5c06 \u4ed6\u6276\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u59ec\u5927\u54e5\uff0c\u4f60\u522b\u5fd9\u7740\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u5c06\u4ed6\u653e\u5728\u5e8a\u4e0a\uff0c\u628a\u4e86\u4e00\u628a\u4ed6\u7684\u8109\u606f\uff0c\u9762\u8272\u8d8a\u53d1\u6c89\u91cd\uff0c\u6c5f\u5357\u5fe7\u5fc3\u4ef2\u5fe1\uff0c\u95ee\u9053\uff1a\u201c\u4ed6 \u5230\u5e95\u662f\u4f24\u5728\u54ea\u513f\uff1f\u201d\u91d1\u4e16\u9057\u5c06\u59ec\u6653\u98ce\u8f6c\u8fc7\u6765\uff0c\u84e6\u5730\u6495\u6bc1\u4e86\u4ed6\u7684\u8863\u88f3\uff0c\u53ea\u89c1\u5728\u4ed6\u7684\u80cc\u5fc3\u6b63\u4e2d \u592e\uff0c\u6709\u4e00\u4e2a\u9c9c\u660e\u7684\u638c\u5370\uff01 \u3000\u3000\u6c5f\u5357\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u542c\u5f97\u91d1\u4e16\u9057\u6012\u9053\uff1a\u201c\u539f\u6765\u53c8\u662f\u90a3\u53ae\u5e72\u7684\u597d\u4e8b\u60c5\uff01\u201d\u6c5f\u5357\u9053\uff1a\u201c\u662f \u8c01\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u8fd8\u6709\u8c01\u4eba\u80fd\u4ee4\u59ec\u5927\u54e5\u53d7\u5230\u8fd9\u6837\u4f24\u5bb3\uff0c\u5c31\u662f\u90a3\u4e2a\u6587\u5c9b\u4e3b\uff01\u59ec\u5927\u54e5\u4e2d\u4e86\u4ed6\u7684 \u72ec\u95e8\u8840\u624b\u5370\uff01\u201d \u3000\u3000\u5f53\u4e0b\u91d1\u4e16\u9057\u7528\u53cc\u638c\u7d27\u8d34\u59ec\u6653\u98ce\u7684\u80cc\u5fc3\uff0c\u7528\u672c\u8eab\u7684\u529f\u529b\u52a9\u4ed6\u7597\u4f24\uff0c\u91d1\u4e16\u9057\u6b64\u65f6\u7684\u5185\u529f\u9020\u8be3 \u5df2\u4e0e\u5510\u6653\u6f9c\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u59ec\u6653\u98ce\u4f3c\u6709\u4e00\u80a1\u70ed\u6d41\u6d41\u8d2f\u5168\u8eab\uff0c\u4e0d\u6d88\u591a\u65f6\u7600\u8840\u4fbf\u5df2\u5316\u5f00\uff0c\u867d\u7136\u6d51\u8eab\u75bc \u75db\u3001\u4f46\u5df2\u662f\u8212\u670d\u591a\u4e86\u3002 \u3000\u3000\u59ec\u6653\u98ce\u56ca\u4e2d\u6709\u5c11\u6797\u5bfa\u79d8\u5236\u7684\u5c0f\u8fd8\u4e39\uff0c\u90a3\u662f\u4ed6\u4ee5\u524d\u5728\u5c11\u6797\u5bfa\u76d7\u4e66\u4e4b\u65f6\uff0c\u987a\u624b\u7275\u7f8a\uff0c\u5077\u4e86\u4e00 \u74f6\u7684\uff0c\u4ed6\u7cbe\u795e\u7a0d\u7a0d\u6062\u590d\u4e4b\u540e\uff0c\u4fbf\u53eb\u6c5f\u5357\u5c06\u4ed6\u7684\u80cc\u56ca\u89e3\u4e0b\u6765\uff0c\u6361\u51fa\u4e86\u90a3\u5c0f\u8fd8\u4e39\uff0c\u541e\u4e0b\u4e86\u4e09\u7c92\u3002 \u8fd9\u5c0f\u8fd8\u4e39\u662f\u56fa\u672c\u57f9\u6e90\u7684\u5723\u836f\uff0c\u59ec\u6653\u98ce\u5f97\u91d1\u4e16\u9057\u4ee5\u7edd\u9876\u5185\u529f\u76f8\u52a9\uff0c\u836f\u529b\u89c1\u6548\u66f4\u5feb\uff0c\u4e0d\u5230\u4e00\u4e2a\u65f6 \u8fb0\uff0c\u4fbf\u5df2\u6062\u590d\u5982\u521d\uff0c\u8bf4\u51fa\u4e86\u4ed6\u5728\u7ec4\u6765\u5c71\u7684\u4e00\u6bb5\u906d\u9047\u3002 \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6211\u662f\u524d\u5929\u665a\u4e0a\u8fdb\u5165\u7ec4\u6765\u5c71\u7684\uff0c\u6211\u4ee5\u524d\u66fe\u5e94\u5929\u9b54\u6559\u4e3b\u4e4b\u9080\uff0c\u8fdb\u8fc7\u4e00\u6b21\u7ec4\u6765 \u5c71\uff0c\u4f46\u90a3\u6b21\u8fdb\u5c71\u51fa\u5c71\u90fd\u662f\u7f1a\u7740\u773c\u775b\uff0c\u597d\u5728\u4e8e\u6211\u8fd9\u884c\u7684\u90fd\u662f\u5728\u9ed1\u6697\u4e4b\u4e2d\u6765\u53bb\u81ea\u5982\u7684\uff0c\u8fdb\u51fa\u7684\u9053 \u8def\u6211\u5df2\u9ed8\u8bb0\u5fc3\u4e2d\uff0c\u4e0d\u8fc7\u5230\u5e95\u4e0d\u5982\u5f00\u773c\u8ba4\u8def\u7684\u6613\u8bb0\uff0c\u4e5f\u8fd8\u9887\u8d39\u4e86\u4e00\u4e9b\u6c14\u529b\uff0c\u624d\u6478\u5230\u4e86\u5979\u7684\u5de2 \u7a74\uff0c\u90a3\u65f6\u5df2\u662f\u5c06\u8fd1\u56db\u66f4\u7684\u65f6\u5206\u4e86\u3002 \u3000\u3000\u201c\u5929\u9b54\u6559\u7684\u89c4\u6a21\u4e0d\u7b97\u5f88\u5927\uff0c\u4f46\u4e5f\u6709\u767e\u6570\u5341\u95f4\u623f\u5b50\uff0c\u6211\u5230\u5404\u5904\u8d70\u4e86\u4e00\u904d\uff0c\u5df2\u662f\u5929\u4eae\u3002\u65e2\u672a \u53d1\u73b0\u6211\u7684\u4f84\u513f\uff0c\u4e5f\u672a\u53d1\u73b0\u5929\u9b54\u6559\u4e3b\u3002\u4e0d\u8fc7\uff0c\u505a\u5077\u513f\u7684\u60ef\u4f8b\u5148\u8981\u719f\u6089\u4e3b\u4eba\u5bb6\u7684\u95e8\u6237\uff0c\u6240\u4ee5\u6211\u8fd9 \u4e00\u665a\u7684\u529f\u592b\u4e5f\u6ca1\u767d\u8d39\uff0c\u867d\u7136\u4e00\u65e0\u6240\u5f97\uff0c\u5374\u4e5f\u5df2\u6478\u5230\u5e95\u4e86\u3002 \u3000\u3000\u201c\u6211\u5728\u5c71\u4e2d\u533f\u4e86\u4e00\u65e5\uff0c\u665a\u4e0a\u53c8\u518d\u51fa\u6765\uff0c\u8fd9\u4e00\u665a\u53ef\u89c1\u7740\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u8feb\u4e0d\u53ca\u5f85\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u89c1\u7740\u4e86\u8c01\uff1f\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u89c1\u7740\u4e86\u5929\u9b54\u6559\u4e3b\u3002\u5979\u4f3c\u4e4e\u521a\u4ece \u5916\u5730\u56de\u6765\uff0c\u6211\u85cf\u5728\u5979\u7a97\u524d\u7684\u4e00\u68f5\u6811\u4e0a\uff0c\u542c\u5f97\u5979\u95f4\u4e00\u4e2a\u4f8d\u5973\u9053\uff1a\u2018\u6211\u53bb\u4e86\u8fd9\u8bb8\u591a\u5929\uff0c\u90a3\u5b69\u5b50\u53ef \u5b89\u4efd\u5417\uff1f\u2019\u90a3\u4f8d\u5973\u9053\uff1a\u2018\u8fd9\u5b69\u5b50\u5012\u8fd8\u7b97\u4e56\u4e56\uff0c\u8fd9\u4e48\u591a\u5929\uff0c\u4ed6\u90fd\u4e0d\u5435\u4e0d\u95f9\uff0c\u5728\u7ec3\u4f60\u6559\u7ed9\u4ed6\u7684\u529f \u592b\u5462\u3002\u2019 \u3000\u3000\u201c\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u2018\u8fd9\u5b69\u5b50\u662f\u5f88\u53ef\u7231\uff0c\u548c\u4ed6\u7684\u7236\u4eb2\u5927\u4e0d\u76f8\u540c\uff0c\u201c\u4ed6\u7236\u4eb2\u89c1\u4e86\u4eba\u5c31\u54d7\u5566\u5566 \u7684\u8bf4\u4e2a\u4e0d\u4f11\uff0c\u8fd9\u5b69\u5b50\u89c1\u4e86\u4eba\u5374\u662f\u4e0d\u58f0\u4e0d\u54cd\u7684\u3002\u521d\u6765\u7684\u65f6\u5019\uff0c\u8fd8\u56b7\u7740\u8981\u7239\u8981\u5a18\uff0c\u6e10\u6e10\u5c31\u4f4f\u5f97\u60ef \u4e86\uff0c\u4e5f\u4e0d\u5435\u4e5f\u4e0d\u95f9\u4e86\u3002\u53ef\u60dc\u6211\u60f3\u7559\u4ed6\u4e5f\u7559\u4e0d\u4f4f\uff0c\u8fdf\u65e9\u90fd\u8981\u8fd8\u7ed9\u4ed6\u7239\u3002\u2019\u563f\u563f\uff0c\u5c0f\u5144\u5f1f\uff0c\u770b\u6765 \u8fd9\u5973\u9b54\u5934\u5012\u5f88\u6b22\u559c\u4f60\u7684\u5b69\u5b50\u5462\uff01\u201d \u3000\u3000\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u6211\u5938\u8d5e\u81ea\u5df1\u7684\u5b69\u5b50\uff0c\u8fd9\u5b69\u5b50\u672c\u6765\u5c31\u662f\u806a\u660e\u5f97\u5f88\uff0c\u4eba\u89c1\u4eba\u7231\u7684\uff0c\u4ed6\u7684\u5fc3 \u773c\u513f\u53ef\u4f36\u4fd0\u5462\uff0c\u77e5\u9053\u5435\u4e5f\u6ca1\u7528\u7684\u65f6\u5019\uff0c\u4ed6\u4e5f\u4e00\u58f0\u4e0d\u54cd\u7684\u4e86\u3002\u55ef\uff0c\u540e\u6765\u600e\u4e48\u6837\uff1f\u90a3\u5973\u9b54\u5934\u5f53\u771f \u5c31\u613f6\u4ea4\u8fd8\u7ed9\u6211\u5417\uff1f\u8fd9\u6211\u53ef\u6709\u70b9\u4e0d\u6562\u76f8\u4fe1\u4e86\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6211\u5f53\u65f6\u4e5f\u4e0d\u6562\u76f8\u4fe1\uff0c\u53ea\u542c\u5f97\u90a3\u4f8d\u5973\u95ee\u5979\u9053\uff1a\u2018\u6559\u4e3b\u65e2\u7136\u6b22\u559c\u8fd9\u4e2a\u5b69\u5b50\uff0c\u4e3a \u4f55\u4e0d\u5c06\u4ed6\u7559\u4e0b\u6765\uff0c\u96be\u9053\u8fd8\u5bb3\u6015\u6c5f\u5357\u90a3\u6d51\u5c0f\u5b50\u4e48\uff1f\u2019\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u2018\u4f60\u6709\u6240\u4e0d\u77e5\uff0c\u6c5f\u5357\u80cc\u540e\u6709 \u5ea7\u9760\u5c71\uff0c\u90a3\u2026\u2019\u90a3\u4f8d\u5973\u9053\uff1a\u2018\u54e6\uff0c\u6211\u77e5\u9053\uff0c\u4f60\u8bf4\u7684\u662f\u91d1\u4e16\u9057\u4e48\uff1f\u4ed6\u4e0d\u662f\u65e9\u4e0d\u77e5\u8e2a\u8ff9\u4e86\u5417\uff1f\u6709 \u4eba\u8bf4\u4ed6\u5df2\u7ecf\u6b7b\u5728\u6d77\u5916\u4e86\u3002\u2019\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u2018\u4e0d\uff0c\u4ed6\u6ca1\u6709\u6b7b\uff0c\u4ed6\u53c8\u56de\u6765\u4e86\u3002\u6211\u8fd9\u6b21\u524d\u5f80\u6c13\u5c71\uff0c \u4fbf\u662f\u8d25\u5728\u4ed6\u7684\u624b\u91cc\u7684\uff01\u2019\u90a3\u4f8d\u5973\u9053\uff1a\u2018\u5389\u526f\u6559\u4e3b\u524d\u65e5\u56de\u6765\uff0c\u542c\u8bf4\u4f60\u5df2\u5f80\u6c13\u5c71\uff0c\u4ed6\u7acb\u5373\u5c31\u53c8\u5306 \u5306\u8d70\u4e86\u3002\u60f3\u6765\u4e5f\u662f\u524d\u5f80\u6c13\u5c71\u3002\u2019 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u2018\u6211\u8fd8\u6ca1\u6709\u89c1\u7740\u4ed6\uff0c\u4e0d\u8fc7\u4f9d\u6211\u770b\u6765\uff0c\u5389\u526f\u6559\u4e3b\u52a0\u4e0a\u4ed6\u90a3\u4e24\u53ea\u91d1\u6bdb\u6d5a\uff0c\u4e5f\u8fd8 \u672a\u5fc5\u662f\u91d1\u4e16\u9057\u7684\u5bf9\u624b\u3002\u662f\u4ee5\u6211\u610f\u6b32\u606f\u4e8b\u5b81\u4eba\uff0c\u6211\u4f30\u8ba1\u91d1\u4e16\u9057\u5fc5\u4f1a\u4e0e\u90a3\u6c5f\u5357\u524d\u6765\uff0c\u5230\u65f6\u6211\u5c31\u5c06 \u5b69\u5b50\u4ea4\u56de\u7ed9\u4ed6\u4eec\uff0c\u4f46\u8981\u91d1\u4e16\u9057\u5411\u6211\u53d1\u8a93\uff0c\u4ed6\u7eb5\u4f7f\u4e0d\u52a9\u54b1\u4eec\uff0c\u4e5f\u51b3\u4e0d\u80fd\u4f38\u624b\u7ba1\u54b1\u4eec\u5929\u9b54\u6559\u7684\u4e8b \u60c5\u3002\u2019 \u3000\u3000\u90a3\u4f8d\u5973\u9053\uff1a\u2018\u4f60\u600e\u77e5\u9053\u91d1\u4e16\u9057\u51c6\u4f1a\u7b54\u5e94\uff1f\u2019\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u2018\u4ed6\u4e0e\u6c5f\u5357\u60c5\u903e\u624b\u8db3\uff0c\u90a3\u5b69\u5b50 \u53c8\u662f\u4ed6\u7684\u8bb0\u540d\u5f1f\u5b50\uff0c\u4ed6\u6295\u9f20\u5fcc\u5668\uff0c\u51b3\u4e0d\u6562\u518d\u5bf9\u54b1\u4eec\u96be\u4e3a\u3002\u4f55\u51b5\u8fd8\u6709\u5389\u7956\u5e08\u8fd9\u91cd\u5173\u7cfb\u3002\u2019 \u3000\u3000\u91d1\u4e16\u9057\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u89c9\u5fc3\u5934\u4e00\u9707\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u90a3\u9ed1\u8863\u5c11\u5e74\u679c\u7136\u59d3\u5389\uff0c\u539f\u6765\u8fd8\u662f\u5929\u9b54\u6559 \u7684\u526f\u6559\u4e3b\u3002\u54a6\uff0c\u5947\u602a\uff0c\u5389\u5bb6\u65e9\u5df2\u6b7b\u5c3d\u6b7b\u7edd\uff0c\u8fd9\u4e2a\u4eba\u53c8\u662f\u4ece\u54ea\u91cc\u94bb\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u6c5f\u5357\u5219\u559c\u5b5c\u5b5c\u4ed6\u8bf4\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u5979\u662f\u6015\u4e86\u91d1\u5927\u4fa0\uff0c\u6240\u4ee5\u5b81\u613f\u4ea4\u8fd8\u4e86\u3002\u8fd9\u5f88\u597d\u5440\uff0c\u6211 \u4e0e\u5979\u4eec\u5f80\u65e5\u65e0\u51a4\u3001\u8fd1\u65e5\u65e0\u4ec7\uff0c\u7ba1\u5979\u5929\u9b54\u5730\u9b54\uff0c\u53ea\u8981\u5f97\u56de\u5b69\u5b50\uff0c\u6211\u4e5f\u7518\u613f\u7f62\u4f11\u4e86\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u8d24\u5f1f\u4e14\u6162\u9ad8\u5174\uff0c\u4e8b\u60c5\u53ea\u6015\u53c8\u6709\u53d8\u5366\u4e86\u3002\u201d\u6c5f\u5357\u6e32\uff1a\u201c\u53c8\u6709\u4ec0\u4e48\u53d8\u5366\uff1f\u201d\u59ec \u6653\u98ce\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5929\u9b54\u6559\u4e3b\u8bb2\u4e86\u5979\u5728\u6c13\u5c71\u7684\u4e00\u6bb5\u906d\u9047\u4e4b\u540e\uff0c\u4fbf\u5429\u5490\u5979\u90a3\u4f8d\u5973\u9053\uff1a\u2018\u4f60\u53bb\u770b\u770b \u90a3\u5b69\u5b50\u7761\u4e86\u6ca1\u6709\uff1f\u82e5\u662f\u5df2\u7761\u719f\u4e86\uff0c\u4f60\u5c31\u4e0d\u8981\u60ca\u9192\u4ed6\uff0c\u5c06\u4ed6\u8f7b\u8f7b\u5730\u62b1\u51fa\u6765\u5427\u3002\u2019\u53c8\u8bf4\u9053\uff1a\u2018\u6211 \u5f53\u65e5\u8981\u4f60\u4eec\u53bb\u62a2\u8fd9\u4e2a\u5b69\u5b50\uff0c\u5c31\u662f\u4e3a\u4e86\u9884\u6599\u5230\u6709\u4eca\u65e5\u4e4b\u4e8b\uff0c\u5148\u5e03\u4e0b\u4e00\u7740\u68cb\u3002\u2019 \u3000\u3000\u201c\u6211\u6b63\u60f3\u8ddf\u8e2a\u90a3\u4e2a\u4f8d\u5973\uff0c\u62a2\u5728\u5979\u7684\u524d\u5934\uff0c\u5c06\u6211\u7684\u4f84\u513f\u5077\u8d70\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u5929\u9b54\u6559\u4e3b \u4e00\u58f0\u53eb\u9053\uff1a\u2018\u91d1\u4e16\u9057\uff0c\u4f60\u6765\u4e86\u4e48\uff1f\u597d\uff0c\u6211\u6b63\u5728\u7b49\u4f60\u5230\u6765\uff01\u201d \u3000\u3000\u6c5f\u5357\u5947\u9053\uff1a\u201c\u5979\u8bf4\u4ec0\u4e48\uff1f\u5979\u89c1\u9b3c\u4e86\u4e48\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u662f\u5440\uff0c\u90a3\u65f6\u6211\u4e5f\u60ca\u5947\u4e4b\u6781\uff01\u4ee5\u4e3a\u5f53\u771f\u662f\u91d1\u5927\u4fa0\u6765\u4e86\uff0c\u5e78\u800c\u6211\u8fd8\u6ca1\u6709\u6251\u51fa\uff0c \u53ea\u89c1\u5929\u9b54\u6559\u4e3b\u53eb\u4ed6\u4f5c\u2018\u91d1\u4e16\u9057\u2019\u7684\u90a3\u4e2a\u4eba\uff0c\u5df2\u7ecf\u4ece\u7a97\u53e3\u8df3\u8fdb\u6765\uff0c\u90a3\u4eba\u6234\u7740\u4eba\u76ae\u9762\u5177\uff0c\u4f46\u6211\u4e00 \u770b\u5c31\u77e5\u9053\u5e76\u4e0d\u662f\u91d1\u5927\u4fa0\uff01\u201d \u3000\u3000\u6c5f\u5357\u604d\u7136\u5927\u609f\uff0c\u53eb\u8d77\u6765\u9053\uff1a\u201c\u6211\u77e5\u9053\u8fd9\u4e2a\u4eba\u662f\u8c01\u4e86\u3002\u51c6\u662f\u90a3\u6587\u5c9b\u4e3b\uff01\u5f53\u65f6\u4ed6\u5728\u90ca\u5c71\u4e0a\u7a81 \u7136\u51fa\u73b0\uff0c\u4e0e\u90a3\u5929\u9b54\u6559\u4e3b\u4ea4\u624b\u4e4b\u65f6\uff0c\u8fde\u6211\u90fd\u4ee5\u4e3a\u4ed6\u662f\u91d1\u5927\u4fa0\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u63a5\u7eed\u8bf4\u9053\uff1a\u201c\u90a3\u4eba\u542c\u5f97\u5929\u9b54\u6559\u4e3b\u79f0\u4ed6\u4f5c\u91d1\u5927\u4fa0\uff0c\u65e2\u4e0d\u7b54\u5e94\uff0c\u5374\u4e5f\u4e0d\u5426\u8ba4\u3002\u53ea\u662f\u5fae \u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u2018\u6211\u77e5\u9053\u4f60\u5728\u7b49\u6211\uff0c\u6240\u4ee5\u6211\u5c31\u8d76\u5feb\u6765\u4e86\u3002\u2019\u5927\u9b54\u6559\u4e3b\u9053\uff1a\u2018\u4f60\u6765\u610f\u5982\u4f55\uff1f\uff0c \u90a3\u4eba\u53c8\u7b11\u4e86\u4e00\u7b11\uff0c\u53cd\u95ee\u9053\uff1a\u2018\u4f60\u4ee5\u4e3a\u6211\u6765\u610f\u5982\u4f55\uff1f\u2019 \u3000\u3000\u201c\u5929\u9b54\u6559\u4e3b\u610f\u6b8a\u4e0d\u60a6\uff0c\u7ad9\u8d77\u6765\u8bf4\u9053\uff1a\u2018\u54b1\u4eec\u63a8\u5f00\u7a97\u5b50\u8bf4\u4eae\u8bdd\uff0c\u522b\u62d6\u6ce5\u5e26\u6c34\u3002\u4f60\u82e5\u662f\u613f\u610f \u548c\u89e3\u7684\u8bdd\uff0c\u6211\u628a\u6c5f\u5357\u7684\u5b69\u5b50\u4ea4\u56de\u7ed9\u4f60\uff0c\u4ece\u6b64\u4f60\u8d70\u4f60\u7684\uff0c\u6211\u8d70\u6211\u7684\uff0c\u5404\u4e0d\u76f8\u72af\uff1b\u8981\u662f\u4f60\u51b3\u5fc3\u4e0e \u6211\u4e3a\u654c\uff0c\u6211\u820d\u547d\u5949\u966a\uff01\u4f46\u90a3\u5b69\u5b50\u4e5f\u4f11\u60f3\u6d3b\u4e86\uff01\u2019 \u3000\u3000\u201c\u90a3\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u2018\u6559\u4e3b\uff0c\u4f60\u8fd9\u662f\u65e0\u7684\u653e\u77e2\uff0c\u6211\u624d\u4e0d\u7406\u6c5f\u5357\u7684\u5b69\u5b50\u662f\u6b7b\u662f\u6d3b\u5462\uff01\u4ed6\u5173\u6211 \u4ec0\u4e48\u4e8b\uff1f\u2019 \u3000\u3000\u201c\u5929\u9b54\u6559\u4e3b\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u2018\u54a6\uff0c\u4f60\u4e0d\u662f\u4e3a\u4e86\u6c5f\u5357\u7684\u5b69\u5b50\uff0c\u90a3\u4f60\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u2019 \u3000\u3000\u201c\u90a3\u4eba\u772f\u7740\u773c\u775b\u7b11\u9053\uff1a\u2018\u4e3a\u7684\u53ea\u662f\u60f3\u7ed3\u8bc6\u4f60\uff01\u2019 \u3000\u3000\u201c\u5929\u9b54\u6559\u4e3b\u4e0d\u89c9\u6115\u7136\uff0c\u90a3\u4eba\u7b11\u9053\uff1a\u2018\u4f60\u662f\u6211\u5e73\u751f\u6240\u89c1\u7684\u6700\u7f8e\u8c8c\u7684\u5973\u5b50\uff0c\u53c8\u662f\u5dfe\u5e3c\u82f1\u96c4\uff0c \u6211\u5b9e\u5728\u540e\u6094\u90a3\u65e5\u5192\u72af\u4e86\u4f60\uff0c\u6240\u4ee5\u7279\u6765\u5411\u4f60\u8d54\u7f6a\u7684\u3002\u2019 \u3000\u3000\u201c\u5927\u62b5\u4e00\u4e2a\u4eba\u603b\u662f\u559c\u6b22\u522b\u4eba\u5bb6\u79f0\u8d5e\u4ed6\u7684\uff0c\u5929\u9b54\u6559\u4e3b\u542c\u4e86\uff0c\u867d\u7136\u5927\u51fa\u610f\u5916\uff0c\u795e\u60c5\u5374\u662f\u548c\u60a6 \u4e86\u8bb8\u591a\u3002\u90a3\u4eba\u53c8\u9053\uff1a\u2018\u6211\u4e0d\u53ea\u662f\u6765\u5411\u4f60\u8d54\u7f6a\uff0c\u8fd8\u8981\u6c42\u4f60\u6536\u5f55\u6211\u4f5c\u5f1f\u5b50\u3002 \u3000\u3000\u201c\u5929\u9b54\u6559\u4e3b\u60ca\u6115\u5f97\u96be\u4ee5\u5f62\u5bb9\uff0c\u5979\u5ffd\u5730\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u2018\u91d1\u4e16\u9057\uff0c\u4f60\u662f\u6709\u610f\u6765\u620f\u5f04\u6211 \u5417\uff1f\u600e\u7684\u548c\u6211\u8bf4\u8fd9\u4e9b\u8bdd\uff1f\u4f60\u77e5\u9053\u6211\u6559\u6240\u5949\u7684\u7956\u5e08\u662f\u8c01\uff1f\u2019 \u3000\u3000\u90a3\u4eba\u5230\u4e86\u8fd9\u4e2a\u65f6\u5019\uff0c\u624d\u8868\u9732\u51fa\u4ed6\u7684\u8eab\u4efd\u3002\u4ed6\u6b63\u6b63\u7ecf\u7ecf\u5730\u95ee\u9053\uff1a\u2018\u4f60\u4ee5\u4e3a\u6211\u662f\u8c01\uff1f\u2019\u5929\u9b54 \u6559\u4e3b\u4e00\u7247\u832b\u7136\uff0c\u8ddf\u7740\u95ee\u9053\uff1a\u2018\u4f60\u662f\u8c01\uff1f\u2019\u90a3\u4eba\u500f\u5730\u62c9\u4e0b\u4e86\u4ed6\u90a3\u4eba\u76ae\u9762\u5177\uff0c\u8bf4\u9053\uff1a\u2018\u4f60\u5927\u7ea6\u672a \u66fe\u89c1\u8fc7\u91d1\u4e16\u9057\uff0c\u4f46\u60f3\u5fc5\u4e5f\u66fe\u542c\u4eba\u8bf4\u8fc7\u4ed6\u7684\u76f8\u8c8c\u3002\u2019\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u2018\u54a6\uff0c\u4f60\u4e0d\u662f\u91d1\u4e16\u9057\uff0c\u4f60\u3001 \u4f60\u3001\u4f60\u7a76\u7adf\u662f\u8c01\uff1f\u2019 \u3000\u3000\u90a3\u4eba\u7b11\u9053\uff1a\u2018\u6211\u59d3\u6587\uff0c\u540d\u53eb\u6587\u5ef7\u58c1\uff0c\u662f\u5357\u6d77\u672a\u540d\u5c9b\u7684\u5c9b\u4e3b\u3002\u91d1\u4e16\u9057\u4e0e\u6211\u6709\u4ec7\uff0c\u4f60\u76f8\u4fe1\u4e86 \u5427\u3002\u2019 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u2018\u51ed\u4f60\u8fd9\u4e00\u8eab\u6b66\u529f\uff0c\u4f60\u4e3a\u4ec0\u4e48\u613f\u610f\u4f5c\u6211\u6559\u7684\u5f1f\u5b50\uff1f\u6211\u8fd8\u662f\u4e0d\u76f8\u4fe1\uff01\u2019 \u3000\u3000\u90a3\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u2018\u5509\uff0c\u4f60\u8fd8\u662f\u4e0d\u660e\u767d\u6211\u7684\u6765\u610f\uff0c\u6211\u662f\u4e3a\u4e86\u503e\u6155\u4e8e\u4f60\u624d\u6765\u7684\u5440\u3002\u5373 \u662f\u5c48\u8eab\u4e3a\u5974\uff0c\u6211\u4e5f\u613f\u610f\uff01\u597d\uff0c\u4f60\u82e5\u7136\u8fd8\u4e0d\u76f8\u4fe1\uff0c\u6211\u5c31\u5148\u9001\u7ed9\u4f60\u4e00\u4e2a\u89c1\u9762\u793c\u5427\u3002\u2019 \u3000\u3000\u201c\u6211\u4f0f\u5728\u6811\u4e0a\uff0c\u4e5f\u6b63\u81ea\u542c\u5f97\u60ca\u5f02\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u90a3\u4eba\u5ffd\u5730\u5c31\u5411\u7a97\u5916\u53d1\u4e86\u4e00\u638c\uff01\u201d \u3000\u3000\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u54ce\u5440\uff0c\u4f60\u600e\u7684\u4e0d\u52a0\u9632\u5907\uff1f\u201d\u542c\u5230\u8fd9\u91cc\uff0c\u4ed6\u5df2\u7ecf\u77e5\u9053\u6587\u5c9b\u4e3b\u8981\u5bf9\u4ed8\u59ec\u6653\u98ce\u4e86\u3002 \u3000\u3000\u59ec\u6653\u98ce\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4ed6\u4e00\u8fdb\u6765\u5c31\u53d1\u73b0\u4e86\u6211\u7684\u8e2a\u8ff9\uff0c\u4e5f\u60f3\u4e0d\u5230\u4ed6\u7684\u5288\u7a7a\u638c\u7ade\u6709\u8fd9\u4e48\u5927\u7684\u5a01 \u529b\u2026 \u3000\u3000\u59ec\u6653\u98ce\u559d\u4e86\u4e00\u53e3\u70ed\u8336\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u4ed6\u4e00\u638c\u53d1\u51fa\uff0c\u7adf\u9690\u9690\u5e26\u7740\u98ce\u96f7\u4e4b\u58f0\uff0c\u767b\u65f6\u6811\u53f6\u7eb7\u98de\uff0c \u8fde\u90a3\u68f5\u5927\u6811\u4e5f\u6447\u52a8\u8d77\u6765\u3002\u5929\u9b54\u6559\u4e3b\u8fd9\u65f6\u624d\u53d1\u73b0\u6211\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u2018\u59ec\u6653\u98ce\uff0c\u4f60\u5729\u5927\u80c6\uff01\u5c45\u7136\u5fd8 \u4e86\u6211\u7684\u544a\u8beb\uff0c\u53c8\u6765\u9001\u6b7b\uff01\uff0c \u3000\u3000\u201c\u90a3\u6587\u5c9b\u4e3b\u7b11\u9053\uff1a\u2018\u8fd9\u4e2a\u5c0f\u8d3c\uff0c\u4e0d\u503c\u5f97\u4f60\u4eb2\u52b3\u7389\u624b\uff0c\u77a7\u6211\u7684\u5427\uff01\u2019 \u3000\u3000\u201c\u54fc\uff0c\u54fc\uff0c\u90a3\u53ae\u4e5f\u5fd2\u770b\u8f7b\u6211\u4e86\uff0c\u6211\u867d\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u4e5f\u8fd8\u4e0d\u81f3\u4e8e\u4fbf\u843d\u5728\u4ed6\u7684\u624b\u4e2d\u3002\u90a3\u53ae \u8fde\u53d1\u4e86\u4e09\u8bb0\u5288\u7a7a\u638c\uff0c\u8fd9\u624d\u628a\u6211\u9707\u843d\u5730\u4e0b\uff0c\u4ed6\u7684\u638c\u529b\uff0c\u4e00\u638c\u6bd4\u4e00\u638c\u5a01\u731b\uff0c\u4f46\u6211\u4e5f\u8fd8\u6c89\u5f97\u4f4f\u6c14\uff0c \u5f85\u5230\u4ed6\u53d1\u7b2c\u4e09\u638c\u7684\u65f6\u5019\uff0c\u6211\u5c31\u501f\u7740\u4ed6\u7684\u638c\u529b\uff0c\u63d0\u4e00\u53e3\u6c14\uff0c\u65bd\u5c55\u5f2f\u7bad\u7a7f\u4e91\u7684\u8eab\u6cd5\uff0c\u4e00\u4e0b\u4e8e\u5c31\u98de \u51fa\u4e86\u56f4\u5899\u4e4b\u5916\uff01\u201d \u3000\u3000\u6c5f\u5357\u542c\u5f97\u7709\u98de\u8272\u821e\uff0c\u8d5e\u9053\uff1a\u201c\u59ec\u5927\u54e5\uff0c\u771f\u96be\u4e3a\u4f60\u3002\u63a5\u4e86\u4ed6\u4e09\u8bb0\u5288\u7a7a\u638c\uff0c\u5c45\u7136\u8fd8\u80fd\u591f\u65bd\u5c55 \u7edd\u9876\u8f7b\u529f\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6211\u8fd9\u662f\u6b7b\u91cc\u6c42\u751f\uff0c\u4e0d\u5f97\u4e0d\u5c14\u3002\u5176\u5b9e\u90a3\u65f6\u6211\u5df2\u662f\u5143\u6c14\u5927\u4f24\u4e86\u3002\u90a3\u53ae\u597d\u4e0d\u5389 \u5bb3\uff0c\u5927\u7ea6\u4e5f\u77a7\u51fa\u6211\u5df2\u53d7\u4f24\uff0c\u5982\u5f71\u968f\u5f62\u7684\u4fbf\u8ffd\u51fa\u6765\u3002\u8981\u662f\u6211\u53ea\u987e\u9003\u8dd1\u7684\u8bdd\uff0c\u5b9a\u7136\u88ab\u4ed6\u8ffd\u4e0a\uff0c\u6ca1 \u6cd5\u5b50\uff0c\u6211\u518d\u4e00\u6b21\u6b7b\u4e2d\u6c42\u6d3b\uff0c\u5f85\u4ed6\u8feb\u8fd1\uff0c\u7a81\u7136\u4e0e\u4ed6\u62fc\u4e86\u4e00\u638c\uff0c\u6211\u4e2d\u4e86\u4ed6\u7684\u8840\u624b\u5370\uff0c\u4f46\u4ed6\u4e5f\u88ab\u6211 \u4ee5\u4fee\u7f57\u9634\u715e\u529f\u51fb\u4e86\u4e00\u638c\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u4f60\u7684\u4fee\u7f57\u9634\u715e\u529f\u5df2\u7ec3\u5230\u4e86\u7b2c\u4e03\u91cd\uff0c\u8fd9\u4e00\u4e0b\u867d\u672a\u5fc5\u4f24\u5f97\u4e86\u4ed6\uff0c\u4e5f\u591f\u4ed6\u53d7 \u4e86\u3002\u60f3\u6765\u4ed6\u53d7\u4e86\u4f60\u7684\u4e86\u638c\uff0c\u5fc5\u7136\u8981\u8fd0\u529f\u8c03\u606f\uff0c\u987e\u4e0d\u5f97\u8ffd\u4f60\u4e86\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u6b63\u662f\u5982\u4f60\u6240\u6599\uff0c\u8981\u662f\u90a3\u53ae\u62fc\u7740\u53d7\u70b9\u5185\u4f24\uff0c\u7ee7\u7eed\u8ffd\u6211\uff0c\u6211\u5c31\u7cdf\u4e86\u3002\u60f3\u6765\u4ed6 \u662f\u4e0d\u613f\u51cf\u635f\u529f\u529b\uff0c\u6240\u4ee5\u53ea\u597d\u773c\u5149\u5149\u7684\u770b\u6211\u8d70\u4e86\u3002\u6211\u7684\u8fd0\u6c14\u4e5f\u771f\u597d\uff0c\u6211\u9884\u5148\u7559\u4e0b\u4e24\u4e2a\u5f92\u5f1f\u5728\u9547 \u4e0a\u63a5\u5e94\u6211\uff0c\u800c\u4e14\u4eca\u665a\u4fbf\u89c1\u5230\u4e86\u4f60\u4eec\uff0c\u8981\u662f\u4f60\u4eec\u8fdf\u6765\u4e00\u5929\uff0c\u6211\u867d\u6709\u5c0f\u8fd8\u4e39\uff0c\u53ea\u6015\u4e5f\u4e0d\u80fd\u6328\u5230\u73b0 \u5728\u4e86\u3002\u201d\u6c5f\u5357\u4f38\u4f38\u820c\u5934\uff0c\u7b11\u9053\uff1a\u201c\u4e5f\u5e78\u4e8f\u6211\u672a\u66fe\u591a\u4e8b\uff0c\u6211\u672c\u6765\u60f3\u53eb\u91d1\u5927\u4fa0\u5728\u6c13\u5c71\u591a\u7559\u4e00\u5929 \u7684\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u6c89\u541f\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u6587\u5ef7\u58c1\u4e0d\u60dc\u5351\u8eac\u5c48\u8282\uff0c\u5df4\u7ed3\u5929\u9b54\u6559\u4e3b\uff0c\u5176\u4e2d\u5b9a\u6709\u5978\u8c0b\u3002\u660e\u5929 \u665a\u4e0a\uff0c\u54b1\u4eec\u518d\u5230\u7956\u5f90\u5c71\u53bb\u4e00\u6b21\u5427\u3002\u59ec\u5927\u54e5\uff0c\u4f60\u4e5f\u8be5\u6b47\u6b47\u4e86\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u670d\u4e86\u5c0f\u8fd8\u4e39\uff0c\u81ea\u53bb\u8fd0\u529f\u8c03\u6cbb\uff0c\u4f11\u517b\u4e86\u4e00\u65e5\u4e00\u591c\uff0c\u5230\u4e86\u7b2c\u4e8c\u5929\u665a\u4e0a\uff0c\u529f\u529b\u5df2\u5b8c\u5168\u6062 \u590d\u3002\u4e8e\u662f\u7531\u4ed6\u5e26\u8def\uff0c\u4e00\u884c\u4e09\u4eba\u5728\u4e8c\u66f4\u51fa\u53d1\uff0c\u4e09\u66f4\u65f6\u5206\uff0c\u4fbf\u5230\u4e86\u7ec4\u6765\u5c71\u5929\u9b54\u6559\u7684\u4e2d\u67a2\u91cd\u5730\u3002\u91d1 \u4e16\u9057\u53eb\u59ec\u6653\u98ce\u7167\u987e\u6c5f\u5357\uff0c\u89c5\u4e00\u4e2a\u9690\u79d8\u5730\u65b9\u85cf\u8eab\uff0c\u82e5\u6709\u5371\u9669\uff0c\u7acb\u5373\u53d1\u58f0\u547c\u6551\u3002\u4ed6\u5355\u72ec\u4e00\u4eba\uff0c\u6478 \u5230\u4e86\u5929\u9b54\u6559\u4e3b\u7684\u540e\u7a97\u3002 \u3000\u3000\u8fd9\u665a\u53ea\u6709\u534a\u94a9\u65b0\u6708\uff0c\u51e0\u70b9\u7a00\u661f\uff0c\u4f46\u91d1\u4e16\u9057\u7684\u76ee\u529b\u6781\u4f73\uff0c\u4ed6\u4f0f\u5728\u7a97\u540e\u8fb9\u7684\u4e00\u5757\u5927\u6e56\u77f3\u4e0b\uff0c \u4ece\u7eb1\u7a97\u4e0a\u6de1\u6de1\u7684\u4e24\u4e2a\u5f71\u5b50\uff0c\u5df2\u8ba4\u51fa\u4e86\u662f\u6587\u5c9b\u4e3b\u548c\u5929\u9b54\u6559\u4e3b\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5929\u9b54\u6559\u4e3b\u67d4\u58f0\u95ee\u9053\uff1a\u201c\u5ef7\u58c1\uff0c\u4f60\u7684\u4f24\u5df2\u7ecf\u597d\u4e86\u5417\uff1f\u201d\u6587\u5c9b\u4e3b\u7b11\u9053\uff1a\u201c\u90a3\u5077\u513f\u7684\u4fee \u7f57\u9634\u715e\u529f\u5982\u4f55\u4f24\u5f97\u4e86\u6211\uff0c\u65e9\u5df2\u6ca1\u4e8b\u4e86\u3002\u83ab\u8bf4\u662f\u4ed6\uff0c\u5373\u7b97\u56db\u5927\u95e8\u6d3e\u7684\u638c\u95e8\u4eba\u90fd\u6765\uff0c\u4e5f\u4e0d\u653e\u5728\u6211 \u7684\u5fc3\u4e0a\u3002\u53ea\u662f\u8981\u60f3\u4e2a\u529e\u6cd5\u5bf9\u4ed8\u91d1\u4e16\u9057\uff0c\u53ea\u8981\u80fd\u628a\u4ed6\u5236\u4f0f\uff0c\u54b1\u4eec\u5c31\u53ef\u4ee5\u5929\u4e0b\u65e0\u654c\u4e86\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8bf4\u91d1\u4e16\u9057\u4e5f\u6015\u4f60\u4e09\u5206\u5417\uff1f\u201d\u6587\u5c9b\u4e3b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u548c\u4ed6\u662f\u534a\u65a4\u516b \u4e24\uff0c\u5f7c\u6b64\u90fd\u6709\u987e\u5fcc\u3002\u201d\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u4e5f\u662f\u6015\u4ed6\u4e09\u5206\u4e86\u3002\u201d \u3000\u3000\u6587\u5c9b\u4e3b\u5f53\u7136\u542c\u5f97\u51fa\u8fd9\u662f\u6316\u82e6\u7684\u8bf4\u8bdd\uff0c\u4f46\u4ed6\u5374\u4e00\u672c\u6b63\u7ecf\u4ed6\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u867d\u7136\u4e0d\u81f3\u4e8e\u8f93 \u7ed9\u4ed6\uff0c\u5374\u4e5f\u4e0d\u80fd\u7f6e\u4ed6\u6b7b\u547d\u3002\u6240\u4ee5\u54b1\u4eec\u624d\u8981\u540c\u5fc3\u5408\u529b\u5440\uff0c\u55ef\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u80af\u4e0e\u6211\u7422\u78e8\u6b66\u529f\uff1f\u201d \u3000\u3000\u540e\u9762\u8fd9\u4e2a\u201c\u4ed6\u201d\uff0c\u663e\u7136\u4e0d\u662f\u6307\u91d1\u4e16\u9057\uff0c\u91d1\u4e16\u9057\u542c\u5230\u8fd9\u91cc\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u6b63\u5728\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a \u2018\u4ed6\u2019\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u5929\u9b54\u6559\u4e3b\u8bf4\u9053\uff1a\u201c\u4ed6\u7684\u813e\u6c14\u602a\u5f97\u5f88\uff0c\u4ed6\u6837\u6837\u542c\u6211\u7684\u8bdd\uff0c\u5c31\u662f\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4ed6\u4e0d\u80af\u7b54 \u5e94\u3002\u201d \u3000\u3000\u6587\u5c9b\u4e3b\u610f\u6b8a\u4e0d\u60a6\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u540c\u4ec7\u654c\u5ffe\uff0c\u4e0d\u5e94\u5f7c\u6b64\u5f02\u5fc3\u3002\u518d\u8bf4\uff0c\u6211\u4e5f\u6709\u5bb6\u4f20\u7684\u7edd\u6280\u4e0e \u4ed6\u7422\u78e8\uff0c\u5e76\u4e0d\u662f\u5355\u5360\u4ed6\u7684\u4fbf\u5b9c\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u6211\u4e5f\u66fe\u8fd9\u6837\u529d\u8fc7\u4ed6\uff0c\u4ed6\u4e0d\u613f\u610f\uff0c\u6211\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u6587\u5c9b\u4e3b\u5ffd\u5730\u51b7\u7b11\u9053\uff1a\u201c\u54ce\uff0c\u6211\u77e5\u9053\u4e86\u3002\u4ed6\u662f\u5992\u5fcc\u6211\u4eb2\u8fd1\u4f60\uff0c\u4ed6\u5440\uff0c\u54c8\u54c8\uff0c\u4ed6\u5bf9\u4f60\u2026\u2026\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7acb\u5373\u622a\u65ad\u4ed6\u7684\u8bdd\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u6211\u5f53\u4ed6\u662f\u5c0f\u5f1f\u5f1f\u3002\u201d \u3000\u3000\u6587\u5c9b\u4e3b\u7b11\u9053\uff1a\u201c\u4f60\u5f53\u4ed6\u662f\u5c0f\u5f1f\u5f1f\uff0c\u6211\u770b\u4ed6\u53ef\u4e0d\u613f\u610f\u53ea\u628a\u4f60\u5f53\u4f5c\u59d0\u59d0\u5462\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u542c\u5230\u8fd9\u91cc\uff0c\u604d\u7136\u5927\u609f\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4e2a\u2018\u4ed6\u2019\u5c31\u662f\u90a3\u9ed1\u8863\u5c11\u5e74\u3002\u201d\u5fc3\u91cc\u53c8\u6697\u81ea \u7b11\u9053\uff1a\u201c\u5973\u5b69\u5b50\u603b\u662f\u559c\u6b22\u4f5c\u522b\u4eba\u7684\u59d0\u59d0\uff0c\u4e4b\u534e\u548c\u6211\u5c31\u662f\u8fd9\u6837\u3002\u4ee5\u6211\u770b\u6765\uff0c\u8fd9\u5929\u9b54\u6559\u4e3b\u7684\u5e74 \u7eaa\uff0c\u5176\u5b9e\u4e5f\u4e0d\u89c1\u5f97\u5927\u8fc7\u90a3\u9ed1\u8863\u5c11\u5e74\u3002\u201d \u3000\u3000\u5927\u7ea6\u662f\u90a3\u6587\u5c9b\u4e3b\u89c1\u5929\u9b54\u6559\u4e3b\u5df2\u7ecf\u542b\u55d4\u8574\u6012\uff0c\u7b11\u4e86\u4e00\u7b11\uff0c\u4fbf\u8f6c\u8fc7\u8bdd\u9898\u8bf4\u9053\uff1a\u201c\u4ed6\u4e0d\u613f\u610f\uff0c \u4f60\u603b\u8be5\u613f\u610f\u5427\uff1f\u6211\u53ea\u8981\u77e5\u9053\u79d8\u7c4d\u4e0a\u7684\u5fc3\u6cd5\uff0c\u518d\u548c\u4f60\u540c\u7ec3\u767e\u6bd2\u771f\u7ecf\uff0c\u7ec3\u6210\u4e86\u6bd2\u8840\u638c\uff0c\u6211\u5c31\u53ef\u4ee5 \u4e0e\u91d1\u4e16\u9057\u4e00\u62fc\u4e86\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u8bba\u8d77\u6b66\u529f\uff0c\u6211\u672c\u8be5\u62dc\u4f60\u4e3a\u5e08\uff0c\u53ef\u662f\u8fd9\u767e\u6bd2\u771f\u7ecf\u4e43\u662f\u6211\u597d\u4e0d\u5bb9\u6613\u624d\u5f97\u56de\u6765 \u7684\u4f20\u5bb6\u4e4b\u5b9d\uff0c\u7167\u89c4\u77e9\u662f\u4e0d\u80fd\u7ed9\u5916\u4eba\u7684\u3002\u201d \u3000\u3000\u6587\u5c9b\u4e3b\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u7ed9\u5916\u4eba\u4e0d\u53ef\u4ee5\uff0c\u7ed9\u81ea\u5df1\u4eba\u603b\u53ef\u4ee5\u5427\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u2026 \u3000\u3000\u6587\u5c9b\u4e3b\u9053\uff1a\u201c\u73e0\u739b\uff0c\u4f60\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u6211\u7684\u5fc3\u610f\uff0c\u4f60\u8fd8\u6709\u4e0d\u660e\u767d\u7684\u5417\uff1f\u201d\u4ed6\u7a81\u7136\u6539\u4e86\u79f0 \u547c\uff0c\u5524\u5929\u9b54\u6559\u4e3b\u7684\u5c0f\u540d\uff0c\u8bed\u6c14\u95f4\u4eb2\u70ed\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u683c\u683c\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u8bf4\u770b\uff0c\u6211\u7b28\u5f97\u5f88\u5462\uff01\u201d \u3000\u3000\u6587\u5c9b\u4e3b\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u73e0\u739b\uff0c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u662f\u6155\u4f60\u8272\u827a\u53cc\u5168\uff0c\u8fd9\u624d\u6765\u7684\u3002\u96be\u5f97\u54b1\u4eec\u53c8\u90fd\u4e0e \u91d1\u4e16\u9057\u6709\u4ec7\uff0c\u5229\u5bb3\u76f8\u540c\uff0c\u6b63\u8be5\u5408\u4e3a\u4e00\u4f53\u3002\u73e0\u739b\uff0c\u4f60\u82e5\u8bb8\u6211\u957f\u4f8d\u5986\u53f0\uff0c\u6211\u7518\u613f\u4f5c\u4f60\u88d9\u4e0b\u4e0d\u8d30\u4e4b \u81e3\uff0c\u4efb\u4f60\u5dee\u9063\uff01\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u53c8\u683c\u683c\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u662f\u5728\u5411\u6211\u6c42\u5a5a\u4e86\u3002\u53ef\u662f\uff0c\u6211\u8fd8\u672a\u80fd\u76f8\u4fe1\u4f60\u5462\uff01\u201d \u3000\u3000\u6587\u5c9b\u4e3b\u9053\uff1a\u201c\u4f60\u8981\u600e\u4e48\u6837\u624d\u76f8\u4fe1\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4fd7\u8bed\u8bf4\u5f97\u597d\uff1a\u8def\u9065\u77e5\u9a6c\u529b\uff0c\u65e5\u4e45\u89c1 \u4eba\u5fc3\u3002\u4f60\u624d\u6765\u4e86\u4e0d\u8fc7\u4e24\u5929\uff0c\u6211\u600e\u4e48\u80fd\u591f\u76f8\u4fe1\u4f60\u5462\uff1f\u4f55\u51b5\u8fd9\u662f\u7ec8\u8eab\u5927\u4e8b\uff0c\u603b\u5f97\u8ba9\u6211\u591a\u4e9b\u65f6\u95f4\u60f3 \u60f3\u554a\uff01\u2026 \u3000\u3000\u6587\u5c9b\u4e3b\u9053\uff1a\u201c\u90a3\u4f60\u8981\u60f3\u591a\u4e45\uff1a\u7ed9\u6211\u4e00\u4e2a\u671f\u9650\u5427\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u8bf4\u5f97\u5b9a\uff1f\u6211\u8981\u8003\u5bdf\u4f60\u8fd9\u4e2a\u4eba\u3002\u8981\u662f\u4f60\u6837\u6837\u542c\u6211\u7684\u8bdd\uff0c\u4e5f\u8bb8\u4e0d\u7528 \u591a\u4e45\uff0c\u6211\u5c31\u4f1a\u7b54\u5e94\u4f60\uff1b\u8981\u662f\u4f60\u53e3\u4e0d\u5bf9\u5fc3\u561b\uff0c\u90a3\u5c31\u518d\u8fc7\u4e00\u767e\u5e74\u4e5f\u4e0d\u6210\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u662f\u5728\u60c5\u573a\u6253\u8fc7\u6eda\u6765\u7684\uff0c\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u89c9\u6697\u81ea\u7b11\u9053\uff1a\u201c\u8fd9\u53ae\u53ef\u9047\u5230\u4e86\u5bf9\u624b\u4e86\u3002\u4ed6\u60f3 \u4eba\u8d22\u4e24\u5f97\uff0c\u9a97\u8272\u9a97\u827a\uff1b\u5929\u9b54\u6559\u4e3b\u5374\u7ed9\u4ed6\u6765\u4e2a\u7f13\u5175\u4e4b\u8ba1\uff0c\u771f\u4e2a\u662f\u5c14\u865e\u6211\u8bc8\uff0c\u5c06\u6765\u4e5f\u4e0d\u77e5\u662f\u8c01\u9a97 \u4e86\u8c01\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u811a\u6b65\u58f0\u54cd\uff0c\u53ea\u89c1\u90a3\u9ed1\u8863\u5c11\u5e74\u6012\u6c14\u51b2\u51b2\u5730\u8dd1\u4eca\uff0c\u5e76\u4e0d\u6572\u95e8\uff0c\u4fbf\u51b2\u8fdb\u53bb\uff1b\u91d1\u4e16\u9057\u53c8\u6697 \u81ea\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u6709\u4e00\u573a\u597d\u620f\u770b\u4e86\uff01\u201d \u3000\u3000\u5fc3\u5ff5\u65b9\u52a8\uff0c\u679c\u7136\u4fbf\u542c\u5f97\u91cc\u9762\u5927\u5435\u5927\u56b7\u8d77\u6765\u3002\u5148\u662f\u9ed1\u8863\u5c11\u5e74\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u4e09\u66f4\u534a\u591c\uff0c\u4f60 \u5230\u6211\u59d0\u59d0\u7684\u623f\u95f4\u4f5c\u4ec0\u4e48\uff1f\u201d\u6587\u5c9b\u4e3b\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6765\u5f97\u6211\u5c31\u6765\u4e0d\u5f97\u4e48\uff1f\u201d\u9ed1\u8863\u5c11\u5e74\u6012\u9053\uff1a\u201c\u4f60 \u8d77\u4ec0\u4e48\u4e1c\u897f\uff0c\u5c82\u53ef\u4e0e\u6211\u76f8\u6bd4\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5fd9\u9053\uff1a\u201c\u590d\u751f\uff0c\u4e0d\u53ef\u65e0\u793c\uff0c\u6587\u5148\u751f\u662f\u6765\u548c\u6211\u8c08\u6b63\u7ecf\u4e8b\u7684\u3002\u201d\u9ed1\u8863\u5c11\u5e74\u9053\uff1a\u201c\u4ed6 \u6709\u6b63\u7ecf\u4e8b\u53ef\u8c08\uff1f\u201d\u6587\u5c9b\u4e3b\u6de1\u6de1\u5730\u9053\uff1a\u201c\u73e0\u739b\uff0c\u544a\u8bc9\u4e86\u4ed6\u4e5f\u597d\u3002\u201d\u4ed6\u4ee5\u4e3a\u5929\u9b54\u6559\u4e3b\u662f\u60f3\u8bf4\u4ed6\u6765 \u6c42\u5a5a\u4e4b\u4e8b\uff0c\u54ea\u77e5\u5929\u9b54\u6559\u4e3b\u5374\u9053\uff1a\u201c\u6587\u5148\u751f\u662f\u5728\u548c\u6211\u5546\u8c08\u5bf9\u4ed8\u91d1\u4e16\u9057\u7684\u529e\u6cd5\u7684\u3002\u201d \u3000\u3000\u9ed1\u8863\u5c11\u5e74\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u60f3\u9a97\u6211\u7684\u6b66\u529f\u4e0d\u6210\uff0c\u53c8\u60f3\u9a97\u5979\u4e48\uff1f\u201d\u6587\u5c9b\u4e3b\u6bd4\u8f83\u9634\u6c89\uff0c\u8fd9\u65f6\u4e5f\u4e0d \u7981\u6012\u8d77\u6765\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u771f\u662f\u4e4b\u611a\u8822\u53c8\u72c2\u5984\uff0c\u6211\u4e0e\u4f60\u5207\u78cb\uff0c\u8fd8\u662f\u4fbf\u5b9c\u4e86\u4f60\u5462\uff0c\u96be\u9053\u4f60\u4ee5\u4e3a\u6211 \u7684\u6b66\u529f\u5f53\u771f\u4e0d\u5982\u4f60\u5417\uff1f\u201d\u9ed1\u8863\u5c11\u5e74\u84e6\u7136\u559d\u9053\uff1a\u201c\u5c31\u8bd5\u8bd5\u770b\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u201c\u7830\u201d\u7684\u4e00 \u58f0\uff0c\u60f3\u662f\u9ed1\u8863\u5c11\u5e74\u5df2\u4e00\u62f3\u6253\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u91d1\u4e16\u9057\u60f3\u4e00\u60f3\uff0c\u5bf9\u65b9\u4e09\u4e2a\u6700\u6709\u672c\u9886\u7684\u4eba\uff0c\u90fd\u5df2\u805a\u5728\u8fd9\u513f\uff0c\u5176\u4e2d\u4e24\u4eba\u53c8\u81ea\u884c\u706b\u62fc\uff0c\u8fd9\u6b63\u662f \u7edd\u597d\u7684\u65f6\u673a\uff0c\u4f7f\u7528\u201c\u5929\u9041\u4f20\u97f3\u201d\u4e4b\u672f\uff0c\u5411\u59ec\u6653\u98ce\u9001\u8bdd\u9053\uff1a\u201c\u59ec\u5927\u54e5\uff0c\u673a\u4e0d\u53ef\u5931\uff0c\u4f60\u5feb\u53bb\u6551\u4eba \u5427\uff01\u201d \u3000\u3000\u90a3\u9ed1\u8863\u5c11\u5e74\u4e5f\u61c2\u201c\u5929\u9041\u4f20\u97f3\u201d\u4e4b\u672f\uff0c\u4ed6\u542c\u5230\u4e86\u91d1\u4e16\u9057\u7684\u58f0\u97f3\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u84e6\u5730\u4e00\u58f0\u957f \u5578\uff0c\u53eb\u9053\uff1a\u201c\u91d1\u4e16\u9057\u6765\u4e86\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u4e0e\u6c5f\u5357\u521a\u4ece\u9690\u853d\u4e4b\u6240\u8dd1\u51fa\uff0c\u90a3\u4e24\u5934\u91d1\u6bdb\u5506\u542c\u5f97\u4e3b\u4eba\u7684\u5578\u58f0\uff0c\u5df2\u662f\u5982\u98de\u8d76\u6765\uff0c\u53ef\u662f \u5b83\u4eec\u95fb\u5230\u4e86\u91d1\u4e16\u9057\u7684\u6c14\u5473\uff0c\u654c\u610f\u5168\u6d88\uff0c\u6447\u6447\u5c3e\u5df4\uff0c\u5374\u5411\u91d1\u4e16\u9057\u8fd9\u8fb9\u8d70\u6765\u3002 \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684\u597d\u670b\u53cb\uff0c\u4f60\u4eec\u4e0d\u53ef\u54ac\u4ed6\uff01\u201d\u90a3\u4e24\u53ea\u91d1\u6bdb\u68ad\u679c\u7136\u542c\u8bdd\uff0c\u4efb\u4ece\u59ec\u6653\u98ce \u62c9\u7740\u6c5f\u5357\uff0c\u4ece\u5b83\u4eec\u7684\u8eab\u8fb9\u7ecf\u8fc7\u3002 \u3000\u3000\u91d1\u4e16\u9057\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6211\u6765\u4e86\u3002\u4f60\u4eec\u4e24\u4eba\u518d\u6bd4\u4e0b\u53bb\uff0c\u8ba9\u6211\u6765\u7ed9\u4f60\u4eec\u8bc4\u5224\uff0c\u6211\u6700 \u516c\u9053\uff0c\u51b3\u4e0d\u4f1a\u504f\u8892\u4e00\u65b9\u3002\u201d \u3000\u3000\u6587\u5c9b\u4e3b\u7ed9\u90a3\u9ed1\u8863\u5c11\u5e74\u6253\u4e86\u4e00\u62f3\uff0c\u8fc5\u5373\u8fd8\u4e86\u4e00\u638c\uff0c\u4ed6\u7528\u7684\u662f\u201c\u8840\u624b\u5370\u201d\u7684\u529f\u592b\uff0c\u9ed1\u8863\u5c11\u5e74 \u4f7f\u4e2a\u76d8\u9f99\u7ed5\u6b65\u7684\u8eab\u6cd5\u907f\u5f00\uff0c\u4f46\u80a9\u5934\u4ecd\u662f\u7ed9\u4ed6\u7684\u6307\u950b\u6cbe\u4e86\u4e00\u4e0b\uff0c\u706b\u8fa3\u8fa3\u4f5c\u75db\uff0c\u9ed1\u8863\u5c11\u5e74\u5927\u6012 \u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u60f3\u8981\u6211\u7684\u547d\uff01\u201d\u547c\u7684\u53c8\u8fd8\u4e86\u4e00\u62f3\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u6015\u5916\u4eba\u7b11\u6389\u4e86\u7259\u9f7f\u5417\uff1f\u8d76\u5feb\u7f62\u624b\uff0c\u540c\u5fc3\u5408\u529b\uff0c\u5e94\u4ed8\u5916\u654c\u3002\u201d\u9ed1 \u8863\u5c11\u5e74\u9053\uff1a\u201c\u597d\uff0c\u64d2\u4e86\u91d1\u4e16\u9057\uff0c\u6211\u518d\u8ddf\u4f60\u7b97\u5e10\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u4f60\u4eec\u8981\u6253\u8d77\u88c1\u5224\u6765\u4e86\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u6587\u5389\u4e8c\u4eba\u5df2\u662f\u53cc\u53cc\u6251\u5230\u3002\u91d1 \u4e16\u9057\u7684\u53e3\u4e2d\u4e0e\u4ed6\u4eec\u5f00\u73a9\u7b11\uff0c\u5fc3\u91cc\u5374\u8fd8\u771f\u4e0d\u6562\u8f7b\u654c\uff0c\u6587\u5c9b\u4e3b\u4e00\u4e2a\u201c\u8840\u624b\u5370\u201d\u62cd\u6765\uff0c\u91d1\u4e16\u9057\u7ad6\u8d77 \u4e2d\u6307\uff0c\u5411\u4ed6\u7684\u864e\u53e3\u4e00\u5f39\uff0c\u90a3\u9ed1\u8863\u5c11\u5e74\u5df2\u4eae\u51fa\u4e86\u4ed6\u90a3\u4e00\u5bf9\u7389\u5c3a\uff0c\u75be\u70b9\u91d1\u4e16\u9057\u5bf8\u5173\u5c3a\u4e09\u7126\u7ecf\u8109\u7684 \u4e03\u5904\u91cd\u7a74\uff0c\u5c3d\u7ba1\u4ed6\u4e0e\u6587\u5c9b\u4e3b\u6709\u4ec7\uff0c\u4f46\u5728\u8fd9\u5173\u53e3\uff0c\u4ed6\u5374\u662f\u8ba4\u771f\u52a9\u4ed6\u3001\u4e3a\u4ed6\u89e3\u5f00\u4e86\u94c1\u6307\u622a\u8109\u4e4b\u5371\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u4f30\u91cf\u4ed6\u4eec\u4e8c\u4eba\u53ef\u4ee5\u5bf9\u4ed8\u5f97\u4e86\u91d1\u4e16\u9057\uff0c\u62bd\u51fa\u8eab\u6765\uff0c\u4fbf\u5f85\u53bb\u8ffd\u59ec\u6653\u98ce\uff0c\u91d1\u4e16\u9057\u5ffd\u5730 \u4e00\u4e2a\u201c\u79fb\u5bab\u6362\u4f4d\u201d\uff0c\u4ee5\u8fc5\u6377\u65e0\u4f26\u7684\u8eab\u6cd5\u963b\u6b62\u4e86\u5979\uff0c\u4ed6\u957f\u8896\u4e00\u6325\uff0c\u767b\u65f6\u628a\u5929\u9b54\u6559\u4e3b\u8feb\u4e86\u8f6c\u6765\u3002 \u3000\u3000\u9ed1\u8863\u5c11\u5e74\u594b\u4e0d\u987e\u8eab\uff0c\u4e00\u58f0\u559d\u9053\uff1a\u201c\u4f11\u5f97\u4f24\u6211\u59d0\u59d0\uff01\u201d\u53cc\u5c3a\u76d8\u65cb\uff0c\u6b3a\u8eab\u76f4\u8fdb\u3002\u91d1\u4e16\u9057\u9053\uff1a \u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u53ef\u8981\u7528\u5251\u5566\uff01\u201d\u88c1\u4e91\u5b9d\u5251\uff0c\u500f\u5730\u51fa\u9798\uff0c\u5251\u5c3a\u76f8\u4ea4\uff0c\u53d1\u51fa\u4e86\u91d1\u77f3\u4e4b\u58f0\uff0c\u604d\u5982\u9f99\u541f \u864e\u5578\uff01\u8fd9\u4e24\u4ef6\u5175\u5668\u90fd\u662f\u4eba\u95f4\u5f02\u5b9d\uff0c\u4f46\u89c1\u706b\u82b1\u84ec\u98de\uff0c\u5374\u662f\u5404\u65e0\u4f24\u635f\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u65e7\u6068\u672a\u968f\u4e91\u6c34\u6c93\uff0c\u4f24\u5fc3\u4eca\u53c8\u52a8\u5e72\u6208\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000--------------- \u3000\u3000\u626c\u5251\u8f69\u5c45\u58eb \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u5341\u4e8c\u56de\u3000\u4eba\u6d77\u832b\u832b\u4f55\u5904\u89c5\u3000\u9e3f\u98de\u6e3a\u6e3a\u6709\u8c01\u77e5 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u5341\u4e8c\u56de\u3000\u4eba\u6d77\u832b\u832b\u4f55\u5904\u89c5\u3000\u9e3f\u98de\u6e3a\u6e3a\u6709\u8c01\u77e5 \u3000\u3000\u6587\u5c9b\u4e3b\u89c1\u9ed1\u8863\u5c11\u5e74\u7684\u7389\u5c3a\u53ef\u4ee5\u6321\u5f97\u4f4f\u91d1\u4e16\u9057\u7684\u5b9d\u5251\uff0c\u767b\u65f6\u7cbe\u795e\u5927\u632f\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec \u8fdc\u8fd1\u5939\u653b\uff0c\u4f60\u653e\u5927\u4e86\u80c6\u5b50\u7f20\u7740\u4ed6\uff0c\u5f85\u6211\u6765\u65bd\u5c55\u6740\u624b\uff0c\u53ea\u8981\u628a\u91d1\u4e16\u9057\u6253\u5012\uff0c\u54b1\u4eec\u5c31\u53ef\u4ee5\u5929\u4e0b\u65e0 \u654c\u4e86\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5012\u6253\u5f97\u4e2a\u597d\u5982\u610f\u7b97\u76d8\uff0c\u4f60\u5c31\u8bd5\u8bd5\u5427\uff01\u201d\u5ffd\u5730\u4f7f\u51fa\u4e86\u4e2a\u5929\u7f57\u6b65\u6cd5\uff0c\u95ea \u5f00\u4e86\u90a3\u9ed1\u8863\u5c11\u5e74\uff0c\u5530\u7684\u4e00\u5251\u5411\u6587\u5c9b\u4e3b\u523a\u53bb\uff01 \u3000\u3000\u90a3\u9ed1\u8863\u5c11\u5e74\u7684\u8eab\u624b\u4e5f\u654f\u6377\u4e4b\u6781\uff0c\u53ef\u662f\u4ed6\u7684\u6b65\u6cd5\u5374\u4e0d\u53ca\u91d1\u4e16\u9057\u7684\u201c\u5929\u7f57\u6b65\u201d\u7684\u8fc5\u901f\u591a\u53d8\uff0c \u4ecd\u7136\u6162\u4e86\u534a\u6b65\uff0c\u4f46\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u6587\u5c9b\u4e3b\u7684\u8863\u8896\u5df2\u7ed9\u524a\u53bb\u4e86\u4e00\u6bb5\u3002 \u3000\u3000\u6587\u5c9b\u4e3b\u7684\u6b66\u529f\u4e5f\u786e\u662f\u4e86\u5f97\uff0c\u5728\u5251\u5149\u7ed5\u4f53\u4e4b\u65f6\uff0c\u5c45\u7136\u8fd8\u80fd\u63d0\u6c14\u7eb5\u8eab\uff0c\u4e00\u4e2a\u201c\u4e91\u91cc\u5012\u7ffb\u201d\uff0c \u820d\u4e86\u534a\u6761\u8863\u8896\uff0c\u5c45\u7136\u5728\u91d1\u4e16\u9057\u7684\u5251\u5e95\u9003\u8131\uff0c\u63a0\u51fa\u4e86\u4e09\u4e08\u5f00\u5916\u3002\u4e0d\u8fc7\uff0c\u4e5f\u5e78\u4e8f\u90a3\u9ed1\u8863\u5c11\u5e74\u53ca\u65f6 \u8d76\u5230\uff0c\u4e00\u53cc\u7389\u5c3a\u67b6\u4f4f\u4e86\u91d1\u4e16\u9057\u7684\u5b9d\u5251\uff0c\u91d1\u4e16\u9057\u624d\u4e0d\u80fd\u591f\u8ddf\u8e2a\u518d\u523a\u3002 \u3000\u3000\u6587\u5c9b\u4e3b\u4e00\u7a33\u8eab\u5f62\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u5c31\u4eae\u51fa\u4e86\u4e00\u6761\u8f6f\u97ad\uff0c\u8fd9\u6761\u8f6f\u97ad\u53ea\u6709\u7b77\u5b50\u822c\u7c97\u7ec6\uff0c\u97ad\u7684\u4e00\u5934 \u88c5\u6ee1\u5012\u987b\uff0c\u5e73\u65f6\u662f\u56f4\u5728\u8170\u95f4\u5f53\u4f5c\u8170\u5e26\u7684\uff0c\u89e3\u4e86\u4e0b\u6765\uff0c\u5374\u6210\u4e3a\u4e00\u4ef6\u6781\u5389\u5bb3\u7684\u5175\u5668\u3002\u539f\u6765\u90a3\u4e9b\u5012 \u987b\u90fd\u662f\u6d78\u8fc7\u4e86\u6bd2\u6db2\u7684\uff0c\u53ea\u8981\u7ed9\u5b83\u6495\u7834\u4e00\u70b9\u76ae\u8089\uff0c\u7acb\u523b\u89c1\u8840\u5c01\u5589\u3002 \u3000\u3000\u91d1\u4e16\u9057\u8bc6\u5f97\u6587\u5c9b\u4e3b\u8fd9\u6761\u6bd2\u9f99\u97ad\u7684\u5389\u5bb3\uff0c\u4ed6\u867d\u7136\u5df2\u7ec3\u6210\u4e86\u201c\u91d1\u521a\u4e0d\u574f\u795e\u529f\u201d\uff0c\u4efb\u4f55\u5267\u6bd2\uff0c \u90fd\u4e0d\u80fd\u53d6\u4ed6\u6027\u547d\uff0c\u53ef\u662f\u82e5\u7136\u4e2d\u6bd2\uff0c\u5230\u5e95\u4e5f\u8981\u635f\u4f24\u5143\u6c14\uff0c\u6240\u4ee5\u8fd8\u662f\u5fc5\u987b\u52a0\u610f\u63d0\u9632\u3002 \u3000\u3000\u8fd9\u6761\u8f6f\u97ad\u957f\u8fbe\u4e00\u4e08\u6709\u591a\uff0c\u6587\u5c9b\u4e3b\u4fdd\u6301\u7740\u4e09\u4e08\u5de6\u53f3\u7684\u8ddd\u79bb\uff0c\u6325\u52a8\u957f\u97ad\uff0c\u53ea\u8981\u5f80\u524d\u4e00\u8df3\uff0c\u957f \u97ad\u5c31\u53ef\u6253\u5230\u91d1\u4e16\u9057\u7684\u8eab\u4e0a\uff0c\u91d1\u4e16\u9057\u5b9d\u5251\u867d\u5229\uff0c\u957f\u53ea\u4e09\u5c3a\uff0c\u5374\u523a\u4e0d\u53ca\u4ed6\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5fc3\u60f3\uff1a\u201c\u8fd9\u53ae\u5012\u771f\u72e1\u733e\uff0c\u539f\u6765\u662f\u8fd9\u4e48\u6837\u7684\u8fdc\u8fd1\u5939\u653b\uff01\u81ea\u5df1\u5148\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\uff0c\u5374\u6559 \u8fd9\u5c11\u5e74\u7ed9\u4ed6\u4f5c\u6321\u7bad\u724c\u3002\u6211\u975e\u8981\u4ee4\u4ed6\u5403\u70b9\u82e6\u5934\u4e0d\u53ef\uff01\u201d \u3000\u3000\u4f46\u5728\u5929\u9b54\u6559\u4e3b\u6307\u6325\u4e4b\u4e0b\uff0c\u8fd9\u9ed1\u8863\u5c11\u5e74\u7adf\u662f\u5168\u529b\u4ee5\u8d74\uff0c\u4e0d\u987e\u51f6\u9669\uff0c\u7518\u613f\u4f5c\u4e86\u6587\u5c9b\u4e3b\u7684\u6321\u7bad \u724c\u3002\u800c\u91d1\u4e16\u9057\u53c8\u5bf9\u4ed6\u5fc3\u5b58\u7231\u60dc\uff0c\u4e0d\u613f\u53d6\u4ed6\u6027\u547d\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u91d1\u4e16\u9057\u5c31\u65e0\u6cd5\u6446\u8131\u4ed6\u7684\u7f20\u6597\uff0c\u800c \u53bb\u8ffd\u90a3\u6587\u5c9b\u4e3b\u4e86\u3002 \u3000\u3000\u6587\u5c9b\u4e3b\u548c\u8fd9\u59d3\u5389\u7684\u5c11\u5e74\uff0c\u90fd\u5404\u6709\u72ec\u7279\u7684\u6b66\u529f\uff0c\u5728\u6b66\u6797\u4e2d\u90fd\u7b97\u5f97\u4e0a\u662f\u7b2c\u4e00\u6d41\u7684\u9ad8\u624b\uff0c\u8981\u662f \u4ed6\u4eec\u5355\u6253\u72ec\u6597\u7684\u8bdd\uff0c\u8c01\u90fd\u6321\u4e0d\u4e0d\u4e86\u91d1\u4e16\u9057\u7684\u4e94\u5341\u62db\uff0c\u4f46\u8054\u8d77\u624b\u6765\uff0c\u91d1\u4e16\u9057\u5373\u4f7f\u4e5f\u662f\u51fa\u5c3d\u5168 \u529b\uff0c\u4ea6\u5360\u4e0d\u4e86\u591a\u5927\u4fbf\u5b9c\uff0c\u4f55\u51b5\u4ed6\u73b0\u5728\u53c8\u6709\u6240\u987e\u5fcc\uff0c\u6b64\u6d88\u5f7c\u957f\uff0c\u7adf\u7136\u5904\u5904\u4e0b\u98ce\uff01\u90a3\u6587\u5c9b\u4e3b\u72e1\u733e \u4e4b\u6781\uff0c\u957f\u97ad\u6325\u821e\uff0c\u592d\u77eb\u5982\u9f99\uff0c\u4e00\u51fb\u4e0d\u4e2d\uff0c\u7acb\u5373\u6536\u56de\uff0c\u4f3a\u673a\u518d\u53d1\uff0c\u603b\u4e0d\u8ba9\u91d1\u4e16\u9057\u7684\u5b9d\u5251\u78b0\u7740\u3002 \u3000\u3000\u91d1\u4e16\u9057\u51b7\u7b11\u9053\uff1a\u201c\u6587\u5ef7\u58c1\uff0c\u4f60\u597d\u4e0d\u8981\u8138\uff01\u201d\u6587\u5c9b\u4e3b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u91cf\u5c0f\u975e\u541b\u5b50\uff0c\u65e0\u6bd2\u4e0d\u4e08 \u592b\uff01\u91d1\u4e16\u9057\uff0c\u5728\u4f60\u5728\u6c5f\u6e56\u4e0a\u6df7\u4e86\u8fd9\u8bb8\u591a\u5e74\uff0c\u96be\u9053\u8fde\u8fd9\u4e24\u53e5\u4fd7\u8bdd\u4e5f\u4e0d\u6653\u5f97\uff1f\u201d\u4ed6\u4e00\u9762\u51fa\u8a00\u8ba5 \u8bbd\uff0c\u624b\u5e95\u4ecd\u662f\u4e0d\u653e\u677e\uff0c\u6bd2\u9f99\u97ad\u5411\u91d1\u4e16\u9057\u4e0b\u4e09\u8def\u5377\u6765\uff0c\u53c8\u52a0\u4e0a\u4e86\u4e00\u8bb0\u5288\u7a7a\u638c\uff01 \u3000\u3000\u91d1\u4e16\u9057\u5927\u6012\uff0c\u8fd8\u4e86\u4e00\u638c\uff0c\u4ed6\u7684\u529f\u529b\u6bd4\u6587\u5c9b\u4e3b\u80dc\u8fc7\u4e0d\u6b62\u4e00\u7b79\uff0c\u4e00\u8bb0\u5288\u7a7a\u638c\uff0c\u628a\u6587\u5c9b\u4e3b\u9707\u5f97 \u6447\u6447\u6643\u6643\uff0c\u53ef\u60dc\u8ddd\u79bb\u8fd8\u662f\u8fdc\u4e86\u4e00\u70b9\uff0c\u672a\u80fd\u5c06\u4ed6\u51fb\u5012\uff0c\u4f46\u6587\u5c9b\u4e3b\u867d\u4e0d\u53ca\u4ed6\uff0c\u5374\u4e5f\u662f\u4e2a\u5f3a\u624b\uff0c\u91d1 \u4e16\u9057\u5206\u51fa\u5fc3\u795e\u53bb\u5e94\u4ed8\u4ed6\u8fd9\u8bb0\u5288\u7a7a\u638c\uff0c\u80a9\u5934\u5df2\u7ed9\u90a3\u9ed1\u8863\u5c11\u5e74\u7684\u7389\u5c3a\u626b\u4e86\u4e00\u4e0b\uff0c\u9976\u662f\u91d1\u4e16\u9057\u5df2\u7ec3 \u6210\u4e86\u91d1\u521a\u4e0d\u574f\u795e\u529f\uff0c\u4e5f\u611f\u5230\u6709\u51e0\u5206\u75bc\u75db\u3002 \u3000\u3000\u6587\u5c9b\u4e3b\u7b11\u9053\uff1a\u201c\u91d1\u4e16\u9057\uff0c\u4f60\u591a\u7559\u4e00\u70b9\u6c14\u529b\u5427\uff0c\u83ab\u8981\u5c31\u628a\u5b83\u7528\u4e86\u3002\u8fd8\u6709\u5389\u5bb3\u7684\u5728\u540e\u5934\u5462\uff0c \u73e0\u739b\uff0c\u9001\u51e0\u9897\u6709\u6bd2\u7684\u6697\u5668\u7ed9\u4ed6\u5c1d\u5c1d\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u5148\u522b\u5fd9\uff0c\u5f85\u6211\u5148\u53bb\u628a\u90a3\u59ec\u6653\u98ce\u6253\u53d1\u4e86\u518d\u6765\u3002\u201d\u54ea\u77e5\u521a\u521a\u8d77\u6b65\uff0c\u5ffd\u89c9\u6709\u4e00 \u80a1\u6781\u5927\u7684\u6f5c\u529b\u5c06\u5979\u6293\u4e86\u56de\u6765\uff0c\u539f\u6765\u662f\u91d1\u4e16\u9057\u4ee5\u7edd\u9876\u7384\u529f\uff0c\u65bd\u5c55\u51fa\u4e86\u201c\u64ce\u4e91\u624b\u201d\uff0c\u51cc\u7a7a\u4e00\u6293\uff0c \u4fbf\u5982\u8fd1\u8eab\u64d2\u62ff\u4e00\u822c\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u6012\u9053\uff1a\u201c\u91d1\u4e16\u9057\uff0c\u4f60\u83ab\u975e\u662f\u60f3\u8d76\u7740\u53bb\u89c1\u960e\u738b\u4e48\uff1f\u201d\u56de\u8eab\u52a0\u5165\u6218\u56e2\uff0c\u7389\u624b\u4e00\u626c\uff0c \u4e00\u80a1\u6bd2\u70df\uff0c\u5411\u91d1\u4e16\u9057\u8fce\u9762\u5c04\u53bb\u3002\u91d1\u4e16\u9057\u5f20\u53e3\u4e00\u5439\uff0c\u5c06\u6bd2\u70df\u5439\u5230\u4e86\u6587\u5c9b\u4e3b\u8eab\u65c1\u3002\u6587\u5c9b\u4e3b\u53e3\u4e2d\u65e9 \u5c31\u542b\u4e86\u89e3\u836f\uff0c\u81ea\u662f\u65e0\u59a8\u3002\u91d1\u4e16\u9057\u5374\u5438\u8fdb\u4e86\u5c11\u8bb8\uff0c\u6709\u70b9\u660f\u95f7\uff0c\u4e0d\u8fc7\u7ecf\u4ed6\u4e00\u8fd0\u7384\u529f\uff0c\u771f\u6c14\u6d41\u8f6c\u5168 \u8eab\uff0c\u8fd9\u4e00\u70b9\u70b9\u660f\u95f7\u4e4b\u611f\uff0c\u4e5f\u5c31\u5728\u7247\u523b\u4e4b\u95f4\u6d88\u6563\u4e86\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u6df1\u77e5\u91d1\u4e16\u9057\u5185\u529f\u6df1\u6e5b\uff0c\u6240\u4ee5\u5979\u672c\u6765\u662f\u60f3\u5f85\u91d1\u4e16\u9057\u8017\u5f97\u5dee\u4e0d\u591a\u7684\u65f6\u5019\uff0c\u518d\u6765\u52a9\u6218 \u7684\uff0c\u4f46\u4e00\u6765\u662f\u6587\u5c9b\u4e3b\u6025\u4e8e\u89c1\u529f\uff0c\u4e8c\u6765\u5979\u8981\u8d70\u4e5f\u8d70\u4e0d\u5f00\uff0c\u56e0\u6b64\u5979\u4e5f\u53ea\u597d\u6539\u4e86\u4e3b\u610f\uff0c\u65bd\u5c55\u4e86\u770b\u5bb6 \u672c\u9886\u7684\u4f7f\u6bd2\u529f\u592b\uff0c\u5411\u91d1\u4e16\u9057\u731b\u88ad\uff01 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7684\u6bd2\u70df\u3001\u6bd2\u96fe\u3001\u6bd2\u9488\u3001\u6bd2\u7bad\u4e4b\u7c7b\u7684\u6697\u5668\u3001\u6bd2\u836f\u5c42\u51fa\u4e0d\u7a77\uff1b\u91d1\u4e16\u9057\u4ed7\u7740\u62a4\u4f53\u795e \u529f\uff0c\u518d\u4ee5\u5288\u7a7a\u638c\u6765\u5bf9\u4ed8\uff0c\u867d\u7136\u4e5f\u8fd8\u5bf9\u4ed8\u5f97\u4e86\uff0c\u4e0d\u81f3\u53d7\u4f24\uff0c\u53ef\u662f\u5728\u4ed6\u4eec\u4e09\u4eba\u8054\u624b\u56f4\u653b\u4e4b\u4e0b\uff0c\u4e5f \u5df2\u7ecf\u611f\u5230\u6709\u70b9\u5e94\u4ed8\u4e3a\u96be\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5ffd\u5730\u559d\u9053\uff1a\u201c\u5c0f\u5fc3\uff0c\u63a5\u62db\uff01\u201d\u84e6\u7136\u95f4\u5251\u6cd5\u4e00\u53d8\uff0c\u5251\u5149\u66b4\u957f\uff0c\u7ed3\u6210\u4e86\u4e00\u4e2a\u4e2a\u7684\u5149 \u73af\uff0c\u5411\u5916\u6269\u5f20\uff0c\u90a3\u9ed1\u8863\u5c11\u5e74\u5403\u4e86\u4e00\u60ca\uff0c\u82e5\u975e\u91d1\u4e16\u9057\u5148\u51fa\u58f0\u8b66\u544a\uff0c\u9669\u4e9b\u5c31\u8981\u7ed9\u5149\u73af\u5957\u4e0a\u3002\u539f\u6765 \u8fd9\u662f\u91d1\u4e16\u9057\u81ea\u521b\u7684\u3001\u653b\u5b88\u517c\u5907\u7684\u201c\u5927\u5468\u5929\u201d\u5251\u6cd5\uff0c\u4e0e\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u201c\u5927\u987b\u5f25\u5251\u5f0f\u201d\u5f02\u66f2\u540c \u5de5\uff0c\u4e00\u65bd\u5c55\u5f00\u6765\uff0c\u5468\u8eab\u5728\u5251\u5149\u4fdd\u62a4\u4e4b\u4e0b\uff0c\u6cfc\u6c34\u96be\u8fdb\uff01\u654c\u4eba\u5728\u4e00\u4e08\u65b9\u5706\u4e4b\u5185\uff0c\u4e5f\u7acb\u4e0d\u4f4f\u8db3\uff01 \u3000\u3000\u6587\u5ef7\u58c1\u5012\u5438\u4e86\u4e00\u53e3\u51b7\u6c14\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u53ae\u521d\u5230\u6211\u7684\u672a\u540d\u5c9b\u4e0a\u4e4b\u65f6\uff0c\u6b66\u529f\u867d\u7136\u6781\u4e3a\u4e86 \u5f97\uff0c\u5374\u4e5f\u8fd8\u6ca1\u6709\u5982\u6b64\u795e\u5947\uff0c\u600e\u7684\u53ea\u4e0d\u8fc7\u77ed\u77ed\u4e09\u5e74\uff0c\u4ed6\u7adf\u4f3c\u6bd4\u5f53\u521d\u5f3a\u4e86\u4e00\u500d\uff1f\u201d\u4ed6\u54ea\u77e5\u9053\u91d1\u4e16 \u9057\u5c31\u662f\u5728\u88ab\u56da\u7684\u90a3\u6bb5\u65f6\u95f4\u5df2\u7ec3\u6210\u4e86\u7edd\u4e16\u6b66\u529f\uff0c\u800c\u540e\u6765\u4e0e\u4ed6\u51e0\u6b21\u4ea4\u624b\uff0c\u4e5f\u90fd\u672a\u66fe\u51fa\u5c3d\u5168\u529b\u3002 \u3000\u3000\u8fd9\u65f6\u91d1\u4e16\u9057\u5df2\u628a\u672c\u9886\u65bd\u5c55\u5230\u4e86\u4e5d\u5206\uff0c\u6587\u3001\u5389\u4e8c\u4eba\u4e0e\u90a3\u5929\u9b54\u6559\u4e3b\u90fd\u53ea\u80fd\u5728\u4e00\u4e08\u4e4b\u5916\u4e0e\u4ed6\u6e38 \u6597\uff0c\u518d\u4e5f\u4e0d\u6562\u8fd1\u8eab\uff0c\u9976\u662f\u5982\u6b64\uff0c\u4ecd\u662f\u611f\u5230\u68ee\u68ee\u5251\u6c14\uff0c\u904d\u4f53\u751f\u5bd2\uff0c\u5c3d\u7ba1\u5251\u950b\u672a\u66fe\u53ca\u8eab\uff0c\u5df2\u4f3c\u7ed9 \u4ed6\u7684\u65e0\u5f62\u5251\u6c14\u7b3c\u7f69\u4e86\u4e00\u822c\u3002 \u3000\u3000\u4f46\u5bf9\u65b9\u4e09\u4eba\u4e5f\u90fd\u662f\u9876\u513f\u5c16\u513f\u7684\u89d2\u8272\uff0c\u5176\u4e2d\u5929\u9b54\u6559\u4e3b\u867d\u7136\u8f83\u5f31\uff0c\u5374\u6709\u4f7f\u6bd2\u7684\u529f\u592b\u8865\u5176\u4e0d \u8db3\uff0c\u6240\u4ee5\u5f62\u52bf\u867d\u7136\u626d\u8f6c\uff0c\u91d1\u4e16\u9057\u4e5f\u8fd8\u672a\u80fd\u8f7b\u6613\u53d6\u80dc\uff0c\u5927\u4f53\u8bf4\u6765\uff0c\u8fd8\u662f\u4e2a\u76f8\u6301\u7684\u5c40\u9762\u3002 \u3000\u3000\u6b63\u6597\u5230\u5403\u7d27\u4e4b\u5904\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u59ec\u6653\u98ce\u80cc\u7740\u4e00\u4e2a\u5b69\u5b50\u8dd1\u4e86\u51fa\u6765\uff0c\u7d27\u8ddf\u7740\u6c5f\u5357\u4e5f\u73b0\u51fa \u4e86\u8eab\u5f62\uff0c\u559c\u6d0b\u6d0b\u5730\u53eb\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4f60\u7684\u5f92\u513f\u5df2\u6551\u51fa\u6765\u5566\uff0c\u54b1\u4eec\u73b0\u5728\u662f\u56de\u53bb\u5462\uff0c\u8fd8\u662f\u518d\u6253\u4e0b \u53bb\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u4ee4\u5b69\u5b50\u518d\u53d7\u60ca\u4e86\uff0c\u56de\u53bb\u5427\uff01\u201d\u4e00\u58f0\uff1a\u201c\u5931\u966a\uff01\u201d\u66ae\u7136\u95f4\u5251\u5149\u66b4\u957f\uff0c \u5c06\u90a3\u9ed1\u8863\u5c11\u5e74\u8feb\u9000\u4e86\u51e0\u6b65\uff0c\u6587\u5ef7\u58c1\u4e00\u97ad\u626b\u6765\uff0c\u91d1\u4e16\u9057\u51b7\u7b11\u9053\uff1a\u201c\u6211\u6b63\u8981\u4f60\u5403\u70b9\u82e6\u5934\uff01\u201d\u58f0\u5230 \u4eba\u5230\uff0c\u500f\u7136\u95f4\u5c31\u5230\u4e86\u4ed6\u7684\u9762\u524d\uff0c\u4e2d\u6307\u4e00\u5f39\uff0c\u4e00\u7f15\u51b7\u98ce\uff0c\u9510\u5982\u5229\u7bad\uff0c\u6587\u5c9b\u4e3b\u7684\u53cc\u773c\u51e0\u4e4e\u5f20\u4e0d\u5f00 \u6765\uff0c\u6bd2\u9f99\u97ad\u6253\u51fa\u5df2\u662f\u4e0d\u80fd\u5206\u8fa8\u65b9\u5411\uff0c\u91d1\u4e16\u9057\u4e00\u5251\u524a\u53bb\uff0c\u4f46\u542c\u5f97\u5580\u5693\u4e00\u58f0\uff0c\u90a3\u6761\u6bd2\u9f99\u97ad\u5df2\u88ab\u5f53 \u4e2d\u524a\u65ad\uff01 \u3000\u3000\u91d1\u4e16\u9057\u53cd\u624b\u4e00\u638c\uff0c\u4fbf\u63b4\u4ed6\u7684\u9762\u95e8\uff0c\u9ed1\u8863\u5c11\u5e74\u811a\u5c16\u4e00\u70b9\uff0c\u4e00\u63a0\u6570\u4e08\uff0c\u7389\u5c3a\u70b9\u6253\u91d1\u4e16\u9057\u80cc\u5fc3 \u7684\u201c\u98ce\u5e9c\u7a74\u201d\uff0c\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u4ed6\u6b3a\u4fae\u4f60\uff0c\u4f60\u5374\u8fd8\u820d\u547d\u62a4\u4ed6\uff1f\u201d\u5b9d\u5251\u659c\u98de\uff0c\u8361\u5f00\u4e86\u9ed1\u8863\u5c11\u5e74 \u7684\u4e00\u53cc\u7389\u5c3a\uff0c\u90a3\u8bb0\u8033\u5149\uff0c\u4ecd\u7136\u63b4\u4e0b\uff0c\u6587\u5ef7\u58c1\u7684\u6b66\u529f\u4e5f\u771f\u4e86\u5f97\uff0c\u8d81\u91d1\u4e16\u9057\u8981\u5206\u795e\u5e94\u4ed8\u9ed1\u8863\u5c11\u5e74 \u7684\u65f6\u5019\uff0c\u970d\u7684\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\u6070\u6070\u907f\u5f00\uff0c\u907f\u5f00\u6b64\u8fb1\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u5c11\u5e74\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4e3a\u4e86\u8fd9\u53ae\uff0c\u6211\u662f\u4e3a\u4e86\u6211\u7684\u73e0\u739b\u59d0\u59d0\uff0c\u4f60\u5f53\u6211\u4e5f\u50cf\u4f60\u4e00\u6837\u5be1 \u60c5\u8584\u4e49\u4e48\uff1f\u201d\u8fd9\u756a\u8bdd\u4e0e\u5176\u8bf4\u662f\u8bb2\u7ed9\u91d1\u4e16\u9057\u542c\u7684\uff0c\u6bcb\u5b81\u8bf4\u662f\u8bb2\u7ed9\u5929\u9b54\u6559\u4e3b\u542c\u7684\uff0c\u91d1\u4e16\u9057\u6709\u51e0\u5206 \u96be\u8fc7\uff0c\u53c8\u662f\u51e0\u5206\u597d\u7b11\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u4e0d\u61c2\u4eba\u4e8b\u7684\u6d51\u5c0f\u5b50\uff0c\u5bf9\u8fd9\u5929\u9b54\u6559\u4e3b\u5012\u662f\u4e00\u7247\u75f4\u60c5\uff01\u201d \u3000\u3000\u90a3\u5b69\u5b50\u4f0f\u5728\u59ec\u6653\u98ce\u7684\u80a9\u5934\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u597d\u672c\u4e8b\uff0c\u6211\u770b\u5f97\u9ad8\u5174\u6781\u4e86\uff0c\u4e00\u70b9\u4e5f\u4e0d\u5bb3\u6015\uff01\u201d \u6c5f\u5357\u7b11\u9053\uff1a\u201c\u4f60\u6bd4\u6211\u8fd8\u8981\u80c6\u5927\uff0c\u7adf\u7136\u628a\u6253\u67b6\u5f53\u4f5c\u620f\u8981\u4e48\uff1f\u201d\u4ed6\u987e\u7740\u8bf4\u8bdd\uff0c\u5374\u4e0d\u6599\u5929\u9b54\u6559\u4e3b\u7684 \u51e0\u4e2a\u4f8d\u5973\u5df2\u7a9c\u5230\u4e86\u4ed6\u7684\u8eab\u65c1\u3002 \u3000\u3000\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u6d77\u513f\uff0c\u4f60\u4e5f\u77a7\u77a7\u4f60\u7239\u7239\u7684\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5df2\u6709\u4e24\u4e2a\u4f8d\u5973\u51fa\u624b\u6293\u6765\uff0c \u6c5f\u5357\u7a81\u7136\u53cc\u624b\u62b1\u5934\uff0c\u56e2\u56e2\u4e71\u8f6c\uff0c\u5927\u53eb\u5927\u56b7\u9053\uff1a\u201c\u7537\u5973\u6388\u53d7\u4e0d\u4eb2\uff0c\u4f60\u522b\u78b0\u6211\uff0c\u4f60\u522b\u78b0\u6211\uff01\u201d\u4f46 \u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5de6\u8fb9\u8fd9\u4e2a\u4f8d\u5973\u7ed9\u53f3\u8fb9\u90a3\u4e2a\u4f8d\u5973\u6495\u53bb\u4e86\u534a\u622a\u8896\u5b50\uff0c\u7d27\u63a5\u7740\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c \u53f3\u8fb9\u8fd9\u4e2a\u4f8d\u5973\u53c8\u7ed9\u5de6\u8fb9\u90a3\u4e2a\u4f8d\u5973\u626f\u53bb\u4e86\u4e00\u7f15\u9752\u4e1d\u3002\u539f\u6765\u53ea\u8bba\u6b66\u529f\uff0c\u6c5f\u5357\u672a\u5fc5\u80dc\u5f97\u8fc7\u90a3\u4e24\u4e2a\u4f8d \u5973\uff0c\u4f46\u4ed6\u65b0\u8fd1\u5b66\u4f1a\u4e86\u91d1\u4e16\u9057\u6240\u6388\u7684\u201c\u5929\u7f57\u6b65\u6cd5\u201d\uff0c\u4ee5\u81f3\u5f04\u5f97\u90a3\u51e0\u4e2a\u4f8d\u5973\u8ddf\u7740\u4ed6\u56e2\u56e2\u4e71\u8f6c\uff0c\u81ea \u5df1\u4eba\u6253\u4e0a\u81ea\u5df1\u4eba\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e50\u5f97\u54c8\u54c8\u5927\u7b11\uff0c\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u522b\u620f\u800d\u4e86\uff01\u201d\u4e00\u628a\u63ea\u4f4f\u6c5f\u5357\uff0c\u7eb5\u8eab\u4fbf\u63a0\u8fc7 \u4e86\u5899\u5934\u3002\u5929\u9b54\u6559\u8bf8\u4eba\u89c1\u91d1\u4e16\u9057\u5df2\u4e0e\u4e8c\u4eba\u4f1a\u5408\uff0c\u54ea\u91cc\u8fd8\u6562\u518d\u8ffd\uff1f \u3000\u3000\u51fa\u5230\u7ec4\u6765\u5c71\u53e3\u3002\u5929\u5df2\u5927\u660e\uff0c\u4e00\u884c\u4eba\u7b49\uff0c\u4fbf\u5728\u6797\u4e2d\u7a0d\u6b47\uff0c\u6c5f\u5357\u9053\uff1a\u201c\u6d77\u513f\uff0c\u4f60\u5728\u5bb6\u91cc\u5929\u5929 \u7f20\u7740\u6211\u8981\u627e\u5e08\u7236\uff0c\u73b0\u5728\u5e08\u7236\u6765\u4e86\uff0c\u4f60\u8fd8\u4e0d\u78d5\u5934\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e5f\u771f\u4e56\u5de7\uff0c\u4fbf\u8dea\u4e0b\u53bb\u9053\uff1a\u201c\u591a\u8c22\u5e08\u7236\u6551\u6211\u51fa\u6765\uff0c\u6211\u7ed9\u4f60\u8001\u4eba\u5bb6\u591a\u52a0\u4e09\u4e2a\u54cd\u5934\u3002\u201d \u4ed6\u5df2\u77e5\u9053\u62dc\u5e08\u662f\u8981\u4e09\u8dea\u4e5d\u53e9\u7684\uff0c\u4ed6\u76f4\u78d5\u8db3\u4e86\u5341\u4e8c\u4e2a\u54cd\u5934\u3002\u78d5\u5f97\u989d\u89d2\u90fd\u575f\u8d77\u6765\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u4e00\u7247\u8bda\u5fc3\uff0c\u6211\u4e0d\u62e6\u963b\u4f60\uff0c\u53ea\u6015\u4f60\u7239\u7239\u5fc3\u75bc\u4e86\u3002\u201d\u6c5f\u5357\u4e50\u5f97\u5634\u4e5f\u5408 \u4e0d\u62e2\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u7684\u9020\u5316\u6bd4\u6211\u5f3a\u8fc7\u767e\u500d\uff0c\u6211\u82e5\u62dc\u5f97\u5982\u6b64\u540d\u5e08\uff0c\u7518\u613f\u53e9\u4e00\u767e\u4e2a\u54cd\u5934\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u597d\u751f\u7231\u60dc\uff0c\u5c06\u5b69\u5b50\u62c9\u4e86\u8d77\u6765\uff0c\u5ffd\u5730\u6014\u4e86\u4e00\u6014\uff0c\u4f3c\u662f\u5728\u5b69\u5b50\u8eab\u4e0a\u53d1\u73b0\u4e86\u4ec0\u4e48\uff0c\u95ee \u9053\uff1a\u201c\u90a3\u5929\u9b54\u6559\u4e3b\u53ef\u66fe\u6559\u4e86\u4f60\u4ec0\u4e48\u529f\u592b\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5979\u6bcf\u5929\u665a\u4e0a\u90fd\u8981\u6211\u76d8\u819d\u9759\u5750\uff0c\u6559\u6211 \u633a\u7740\u8170\u6162\u822c\u547c\u5438\u3002\u4e0d\u77e5\u8fd9\u662f\u4e0d\u662f\u529f\u592b\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60\u89c9\u5f97\u600e\u6837\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6bcf\u6b21\u9759 \u5750\u4e4b\u540e\uff0c\u6211\u90fd\u89c9\u5f97\u809a\u5b50\u91cc\u4f3c\u6709\u4e00\u56e2\u706b\u4f3c\u7684\uff0c\u6d51\u8eab\u5927\u6c57\u3002\u4e0d\u8fc7\u51fa\u8fc7\u4e86\u6c57\u540e\uff0c\u5c31\u5f88\u8212\u670d\u4e86\u3002\u8fd9\u4e2a \u6708\u6765\uff0c\u6211\u89c9\u5f97\u6211\u7684\u6c14\u529b\u4e5f\u5927\u4e86\u8bb8\u591a\uff0c\u4ee5\u524d\u642c\u4e0d\u52a8\u7684\u5927\u77f3\u5934\u73b0\u5728\u4e5f\u62ff\u5f97\u8d77\u6765\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u8fd9\u65f6\u4e5f\u5df2\u6ce8\u610f\u5230\u4e86\uff0c\u5728\u4ed6\u5b69\u5b50\u7684\u7709\u5fc3\u4e4b\u95f4\uff0c\u6709\u4e00\u4e1d\u6de1\u6de1\u7684\u9752\u6c14\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u95ee \u9053\uff1a\u201c\u90a3\u5973\u9b54\u5934\u6559\u4e86\u4ed6\u90aa\u6d3e\u7684\u5165\u95e8\u6b66\u529f\uff0c\u53ef\u6709\u59a8\u5bb3\u4e48\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u59a8\u5bb3\uff0c\u53ea \u662f\u6211\u7684\u6559\u6cd5\u5374\u8981\u53d8\u66f4\u4e86\u3002\u6211\u672c\u6765\u51c6\u5907\u8981\u4ed6\u7528\u5341\u5e74\u529f\u592b\u6253\u597d\u5185\u529f\u57fa\u7840\u7684\uff0c\u73b0\u5728\u5927\u7ea6\u53ea\u8981\u4e03\u5e74\u4fbf \u884c\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u5947\u9053\uff1a\u201c\u90a3\u5c82\u4e0d\u662f\u56e0\u7978\u5f97\u798f\u4e86\u4e48\uff1f\u201d\u91d1\u4e16\u9057\u542b\u7cca\u7b54\u9053\uff1a\u201c\u4e5f\u53ef\u4ee5\u8fd9\u4e48\u8bf4\u3002\u90a3\u5929\u9b54\u6559 \u4e3b\u5012\u662f\u5f88\u75bc\u4ed6\u7684\u3002\u201d\u6c5f\u5357\u4e0d\u61c2\u5176\u4e2d\u5965\u5999\uff0c\u542c\u5f97\u91d1\u4e16\u9057\u8fd9\u4e48\u7b54\u590d\uff0c\u4fbf\u653e\u4e0b\u4e86\u5fc3\u4e0a\u7684\u77f3\u5934\uff0c\u6b8a\u4e0d \u77e5\u91d1\u4e16\u9057\u5374\u6b63\u6709\u4e00\u70b9\u60cb\u60dc\u3002 \u3000\u3000\u539f\u6765\u91d1\u4e16\u9057\u672c\u662f\u60f3\u4ece\u6b63\u5b97\u7684\u5185\u529f\u5fc3\u6cd5\u5165\u624b\uff0c\u5f85\u5f92\u5f1f\u6839\u57fa\u6df1\u539a\u4e4b\u540e\uff0c\u518d\u4f20\u6388\u4ed6\u535a\u5927\u7cbe\u6df1\u7684 \u6b66\u529f\uff1b\u73b0\u5728\u6c5f\u6d77\u5929\u4e00\u5f00\u5934\u5c31\u5b66\u4e86\u90aa\u6d3e\u7684\u5185\u529f\uff0c\u82e5\u8981\u4ed6\u91cd\u65b0\u5f00\u59cb\uff0c\u5c31\u5f97\u7ed9\u4ed6\u6613\u7b4b\u6d17\u9ad3\uff0c\u5e9f\u6389\u4ed6 \u539f\u6709\u7684\u529f\u592b\uff0c\u4f46\u56e0\u4ed6\u5e74\u7eaa\u5927\u5c0f\uff0c\u8eab\u4f53\u7684\u62b5\u6297\u529b\u4e0d\u5982\u5927\u4eba\uff0c\u6613\u7b4b\u6d17\u9ad3\u65bd\u4e4b\u4e8e\u5927\u4eba\u5219\u53ef\uff0c\u65bd\u4e4b\u4e8e \u7ae5\u5b50\u5219\u51b3\u4e0d\u53ef\u884c\uff0c\u6240\u4ee5\u91d1\u4e16\u9057\u4e5f\u53ea\u6709\u542c\u5176\u81ea\u7136\u4e86\u3002 \u3000\u3000\u4ece\u90aa\u6d3e\u5185\u529f\u5165\u624b\uff0c\u5b66\u6b66\u53ef\u4ee5\u901f\u6210\uff0c\u4f46\u7ec3\u5230\u6700\u9ad8\u7684\u5883\u754c\u65f6\uff0c\u5374\u53ef\u80fd\u6709\u201c\u8d70\u706b\u5165\u9b54\u201d\u7684\u7978 \u60a3\uff0c\u50cf\u91d1\u4e16\u9057\u81ea\u5df1\u4ece\u524d\u6240\u53d7\u8fc7\u7684\u4e00\u822c\u3002\u8fd8\u5e78\u91d1\u4e16\u9057\u73b0\u5728\u5df2\u662f\u878d\u4f1a\u4e86\u6b63\u90aa\u5404\u6d3e\u4e4b\u957f\uff0c\u5bf9\u201c\u8d70\u706b \u5165\u9b54\u201d\u7684\u707e\u96be\uff0c\u4e5f\u53ef\u4ee5\u6709\u529e\u6cd5\u9632\u5fa1\u4e86\uff0c\u4e0d\u8fc7\uff0c\u5230\u4e86\u5176\u65f6\uff0c\u8fd8\u9700\u8981\u906d\u96be\u7684\u5f53\u4e8b\u4eba\u6709\u8654\u5fc3\u5b9a\u529b\uff0c \u624d\u53ef\u4ee5\u5bfc\u6c14\u5f52\u5143\uff0c\u6cd5\u9664\u201c\u5fc3\u9b54\u201d\u59cb\u6210\u201c\u6b63\u679c\u201d\u3002\u8fd9\u4e9b\u6b66\u5b66\u4e0a\u7684\u7384\u5999\u7406\u8bba\uff0c\u4e0d\u5fc5\u7ec6\u8c08\u3002\u6c5f\u6d77\u5929 \u56e0\u4e3a\u4eba\u95e8\u7684\u9014\u5f84\u8d70\u9519\uff0c\u540e\u6765\u9887\u7ecf\u8fc7\u4e00\u4e9b\u6ce2\u6298\uff0c\u7ecf\u8fc7\u4e00\u4e9b\u5947\u8ff9\uff0c\u624d\u56e0\u7978\u5f97\u798f\uff0c\u6210\u4e3a\u4e00\u4ee3\u5b97\u5e08\uff0c \u90a3\u4e5f\u662f\u540e\u8bdd\u3002 \u3000\u3000\u4e14\u8bf4\u91d1\u4e16\u9057\u7b49\u4e00\u884c\u56db\u4eba\uff0c\u51fa\u4e86\u7ec4\u6765\u5c71\u540e\uff0c\u5c31\u6309\u539f\u5b9a\u7684\u8ba1\u5212\uff0c\u5230\u6c5f\u82cf\u53bb\u8bbf\u9648\u5929\u5b87\u3002\u4e00\u8def\u4e0a \u6c5f\u5357\u662f\u7b11\u53e3\u5e38\u5f00\uff0c\u4e50\u4e0d\u53ef\u652f\uff1b\u91d1\u4e16\u9057\u5374\u662f\u795e\u60c5\u90c1\u95f7\uff0c\u9887\u4f3c\u6709\u4ec0\u4e48\u5fc3\u4e8b\u4f3c\u7684\u3002\u8d70\u4e86\u4e00\u7a0b\uff0c\u6c5f\u5357 \u6b63\u60f3\u95ee\u4ed6\u3002\u91d1\u4e16\u9057\u5ffd\u4ed6\u8bf4\u9053\uff1a\u201c\u6b64\u5730\u79bb\u767e\u82b1\u8c37\u5f88\u8fd1\uff0c\u6211\u60f3\u53bb\u796d\u626b\u80dc\u7537\u7684\u575f\u5893\u3002\u4f60\u4eec\u5148\u8d70\u4e00 \u7a0b\uff0c\u6211\u968f\u540e\u8d76\u4e0a\u3002\u201d \u3000\u3000\u6c5f\u5357\u8bf4\u9053\uff1a\u201c\u53cd\u6b63\u7528\u4e0d\u4e86\u591a\u5c11\u65f6\u95f4\uff0c\u8981\u53bb\u54b1\u4eec\u5c31\u4e00\u540c\u53bb\u5427\u3002\u201d\u5389\u80dc\u7537\u751f\u524d\uff0c\u6c5f\u5357\u5bf9\u5979\u4e0d \u6ee1\uff0c\u4f46\u4e3a\u4e86\u91d1\u4e16\u9057\u7684\u539f\u6545\uff0c\u4ed6\u4e5f\u613f\u5230\u5979\u575f\u524d\u4e00\u62dc\u3002 \u3000\u3000\u91d1\u4e16\u9057\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e5f\u597d\u3002\u4eba\u5df2\u6b7b\u4e86\uff0c\u8fc7\u53bb\u7684\u662f\u975e\u6069\u6028\u4e5f\u53ef\u4ee5\u629b\u5f00\u4e86\u3002\u591a\u51e0\u4e2a\u670b\u53cb\u53bb\u770b \u5979\uff0c\u5979\u4e5f\u4f1a\u9ad8\u5174\u7684\u3002\u201d\u8bf4\u8bdd\u7684\u795e\u6c14\uff0c\u5c31\u4f3c\u5389\u80dc\u7537\u867d\u6b7b\uff0c\u4e5f\u8fd8\u6709\u77e5\u89c9\u4f3c\u7684\u3002\u6c5f\u5357\u60f3\u8d77\u4e86\u5389\u80dc\u7537 \u751f\u524d\u7684\u5389\u5bb3\uff0c\u4e0d\u89c9\u6253\u4e86\u4e00\u4e2a\u5bd2\u6218\u3002 \u3000\u3000\u5c06\u8fd1\u9ec4\u660f\u65f6\u5206\uff0c\u4e00\u884c\u4eba\u8d70\u8fdb\u4e86\u767e\u82b1\u8c37\uff0c\u65f6\u5e8f\u5df2\u662f\u6625\u8001\u82b1\u6b8b\uff0c\u660f\u9e26\u566a\u8033\uff0c\u904d\u5730\u6b8b\u7ea2\uff0c\u8c37\u4e2d \u666f\u81f4\uff0c\u5728\u201c\u522b\u6709\u4f24\u5fc3\u6000\u62b1\u201d\u7684\u91d1\u4e16\u9057\u770b\u6765\uff0c\u66f4\u662f\u89e6\u76ee\u51c4\u51c9\u3002 \u3000\u3000\u59ec\u6653\u98ce\u4e00\u773c\u671b\u53bb\uff0c\u5ffd\u5730\u5413\u4e86\u4e00\u8df3\uff0c\u91d1\u4e16\u9057\u5927\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u8c01\u5e72\u7684\u597d\u4e8b\uff1f\u201d\u98de\u6b65\u8dd1\u5230\u5893 \u65c1\uff0c\u53ea\u89c1\u4ed6\u6240\u7acb\u7684\u90a3\u5757\u5893\u7891\u5df2\u5012\u4e86\u4e0b\u6765\uff0c\u5893\u7891\u4e0a\u5199\u7684\u672c\u662f\uff1a\u201c\u7231\u59bb\u5389\u80dc\u7537\u4e4b\u5893\u3002\u91d1\u4e16\u9057 \u7acb\u3002\u201d\u8fd9\u4e24\u884c\u5927\u5b57\u3002\u73b0\u5728\u201c\u91d1\u4e16\u9057\u7acb\u201d\u8fd9\u4e00\u884c\u56db\u5b57\u5df2\u5168\u88ab\u5265\u6389\uff0c\u6b63\u4e2d\u90a3\u884c\u7684\u201c\u7231\u59bb\u201d\u4e8c\u5b57\u4e5f \u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u90a3\u9992\u5934\u5f62\u7684\u575f\u5893\u88c2\u5f00\u4e86\u4e00\u9053\u5927\u7f1d\uff0c\u4f46\u89c1\u91cc\u9762\u7684\u68fa\u76d6\u5df2\u7ecf\u63ed\u5f00\uff0c\u53ea\u5269\u4e0b\u4e00\u526f\u7a7a\u68fa\u3002\u59ec\u3001\u6c5f \u4e8c\u4eba\u4e0d\u6562\u8bf4\u8bdd\uff0c\u91d1\u4e16\u9057\u7684\u9762\u8272\u6c89\u6697\u5f97\u9a87\u4eba\uff0c\u4ed6\u5446\u4e86\u597d\u4e00\u4f1a\uff0c\u5ffd\u5730\u653e\u58f0\u54ed\u9053\uff1a\u201c\u80dc\u7537\uff0c\u6211\u5bf9\u4f60 \u7684\u5fc3\u4e8b\uff0c\u53ea\u6709\u4f60\u5728\u6b7b\u524d\u4e00\u523b\u65b9\u59cb\u6df1\u77e5\uff0c\u53ef\u60dc\u4f60\u73b0\u5728\u53c8\u5df2\u4e0d\u80fd\u66ff\u6211\u8bf4\u8bdd\uff01\u53eb\u6211\u5982\u4f55\u5206\u8fa9\uff1f\u201d \u3000\u3000\u6c5f\u5357\u624b\u8db3\u65e0\u63aa\uff0c\u60f3\u62c9\u91d1\u4e16\u9057\u79bb\u5f00\u5893\u7a74\uff0c\u5374\u53c8\u6015\u4ed6\u66f4\u4f24\u5fc3\uff0c\u53ea\u597d\u8ba9\u4ed6\u54ed\u4e2a\u75db\u5feb\uff0c\u8fc7\u4e86\u597d\u4e00 \u4f1a\u5b50\uff0c\u5f85\u5230\u91d1\u4e16\u9057\u54ed\u58f0\u6e10\u6b62\uff0c\u6c5f\u5357\u65b9\u59cb\u60f3\u51fa\u51e0\u53e5\u8bdd\u6765\uff0c\u5b89\u6170\u4ed6\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4f60\u548c\u5389\u59d1\u5a18\u7684 \u4e8b\u60c5\uff0c\u670b\u53cb\u4eec\u90fd\u77e5\u9053\uff0c\u7edd\u6ca1\u6709\u4eba\u6562\u8bf4\u4f60\u8d1f\u5fc3\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u51c4\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u4e0d\u77e5\u9053\uff0c\u4f60\u4e0d\u77e5\u9053\u7684\u3002\u6211\u8981\u548c\u4ed6\u540c\u53bb\u3002\u201d\u6c5f\u5357\u5413\u4e86\u4e00\u8df3\uff0c\u5fc3 \u4e2d\u60f3\u9053\uff1a\u201c\u96be\u9053\u91d1\u5927\u4fa0\u7adf\u662f\u795e\u667a\u660f\u8ff7\u4e86\u4e48\uff1f\u201d\u539f\u6765\u6c5f\u5357\u4ee5\u4e3a\u91d1\u4e16\u9057\u8bf4\u7684\u201c\u4ed6\u201d\u4e43\u662f\u6307\u5389\u80dc \u7537\uff0c\u90a3\u5c31\u662f\u8981\u81ea\u5bfb\u77ed\u89c1\u4e86\u3002 \u3000\u3000\u6c5f\u5357\u8fde\u5fd9\u62c9\u7740\u91d1\u4e16\u9057\u7684\u8863\u8896\uff0c\u53eb\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4e0d\u53ef\uff0c\u4e0d\u53ef\uff01\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d \u53ef\uff1f\u6211\u4e00\u5b9a\u8981\u548c\u4ed6\u8bf4\u4e2a\u660e\u767d\uff0c\u624d\u5f97\u5fc3\u5b89\u3002\u4f60\u4eec\u5148\u8d70\u4e00\u7a0b\uff0c\u6211\u518d\u8fdb\u7ec4\u6765\u5c71\u4e00\u6b21\uff0c\u65e9\u5219\u660e\u5929\uff0c\u8fdf \u5219\u540e\u5929\uff0c\u4e00\u5b9a\u4f1a\u8d76\u4e0a\u4f60\u4eec\u3002\u201d\u8863\u8896\u8f7b\u8f7b\u4e00\u62c2\uff0c\u5c06\u6c5f\u5357\u6454\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u7edd\u5c18\u800c\u53bb\u3002 \u3000\u3000\u6c5f\u5357\u8fd9\u624d\u77e5\u9053\u8fd9\u4e2a\u201c\u4ed6\u201d\u4e0d\u662f\u5389\u80dc\u7537\uff0c\u800c\u662f\u6307\u7ec4\u6765\u5c71\u4e2d\u7684\u90a3\u4e2a\u9ed1\u8863\u5c11\u5e74\u3002\u722c\u4e86\u8d77\u6765\uff0c\u987f \u8db3\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4f60\u8fd9\u4e0d\u662f\u81ea\u5bfb\u70e6\u607c\u5417\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u53f9\u606f\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u4f60\u4ece\u672a\u6709\u8fc7\u4f24\u5fc3\u4e4b\u4e8b\uff0c\u4f60\u4e0d\u660e\u767d\u4e00\u4e2a\u4eba\u7684\u6094\u6068\u5fc3\u60c5\u7684\u3002\u90a3\u9ed1\u8863 \u5c11\u5e74\u59d3\u5389\uff0c\u9762\u8c8c\u53c8\u6709\u51e0\u5206\u4f3c\u5389\u59d1\u5a18\uff0c\u91d1\u5927\u4fa0\u5b9a\u7136\u7591\u5fc3\u4ed6\u662f\u5389\u59d1\u5a18\u7684\u5bb6\u4eba\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u5373\u7b97 \u662f\u5979\u7684\u5bb6\u4eba\uff0c\u53c8\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4f60\u8fd8\u672a\u770b\u51fa\u6765\u5417\uff1f\u636e\u6211\u770b\u6765\uff0c\u8fd9\u5893\u7891\u4e0a\u7684\u5b57\u5b9a\u662f\u90a3\u59d3\u5389\u7684\u5c11\u5e74\u5212\u53bb\u7684\uff0c\u5389 \u59d1\u5a18\u7684\u9ab8\u9aa8\u4e5f\u5b9a\u662f\u4ed6\u642c\u53bb\u8fc1\u846c\u4e86\uff0c\u867d\u7136\u54b1\u4eec\u90fd\u8ba4\u4e3a\u91d1\u5927\u4fa0\u5bf9\u5389\u59d1\u5a18\u5df2\u662f\u60c5\u81f3\u4e49\u5c3d\uff0c\u4f46\u91d1\u5927\u4fa0 \u672c\u4eba\u5374\u81ea\u89c9\u6709\u8d1f\u4e8e\u5979\uff0c\u66f4\u52a0\u4e0a\u8fd9\u4e2a\u59d3\u5389\u7684\u5c11\u5e74\u53c8\u4e0d\u539f\u8c05\u4ed6\uff0c\u4ed6\u600e\u80fd\u4e0d\u4f24\u5fc3\uff1f\u600e\u80fd\u4e0d\u6025\u4e8e\u60f3\u53bb \u5206\u8fa9\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u83ab\u540d\u5176\u5999\uff0c\u62ac\u8d77\u8ff7\u60c6\u7684\u773c\u775b\u95ee\u9053\uff1a\u201c\u5e08\u7236\u90a3\u4e48\u5927\u7684\u672c\u4e8b\uff0c\u4e3a\u4ec0\u4e48\u8981\u54ed\uff1f\u201d\u6c5f\u5357\u7ed9 \u4ed6\u9017\u5f97\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8bf4\u7ed9\u4f60\u542c\uff0c\u4f60\u4e5f\u4e0d\u660e\u767d\u7684\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7684\u813e\u6c14\u4e0e\u7236\u4eb2\u5927\u4e0d\u76f8\u540c\uff0c\u5e73\u65f6\u5f88\u5c11\u8bf4\u8bdd\uff0c\u4f46\u5374\u6267\u62d7\u5f97\u5f88\uff0c\u5fc3\u6709\u6240\u7591\uff0c\u5c31\u975e\u5f97\u95ee\u4e2a \u660e\u767d\u4e0d\u53ef\uff0c\u6c5f\u5357\u7ed9\u4ed6\u7f20\u5f97\u6ca1\u6cd5\uff0c\u53ea\u5f97\u8fd9\u6837\u8bf4\u9053\uff1a\u201c\u4f60\u5e08\u7236\u662f\u4e3a\u4e86\u4e00\u4e2a\u5973\u4eba\u7684\u7f18\u6545\uff0c\u90a3\u5973\u4eba\u5bb3 \u4e86\u4ed6\uff0c\u4f46\u4e5f\u4ee4\u4ed6\u4f24\u5fc3\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4f3c\u61c2\u975e\u61c2\u7684\u8bf4\uff1a\u201c\u539f\u6765\u5973\u4eba\u662f\u8fd9\u6837\u53ef\u6015\u7684\uff0c\u7239\uff0c\u4ee5\u540e\u6211\u957f\u5927\u4e86\u4e5f\u4e0d\u6562\u4eb2\u8fd1\u5973\u4eba \u4e86\u3002\u201d\u6c5f\u5357\u5927\u7b11\u9053\uff1a\u201c\u4e5f\u4e0d\u80fd\u4e00\u6982\u800c\u8bba\uff0c\u6211\u548c\u4f60\u5988\u4e0d\u5c31\u5f88\u597d\u5417\uff1f\u201d\u59ec\u6653\u98ce\u4e5f\u7b11\u9053\uff1a\u201c\u6c5f\u5357\uff0c \u8fd9\u662f\u4f60\u7684\u798f\u6c14\u3002\u5929\u6c14\u4e0d\u65e9\uff0c\u54b1\u4eec\u8fd8\u662f\u8d70\u5427\u3002\u5728\u8fd9\u4e2a\u7834\u5893\u65c1\u8fb9\uff0c\u6211\u603b\u89c9\u5f97\u6709\u70b9\u5bb3\u6015\uff1f\u201d \u3000\u3000\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u53ea\u662f\u6211\u5bb3\u6015\u5462\uff0c\u539f\u6765\u4f60\u4e5f\u5bb3\u6015\u5389\u59d1\u5a18\u7684\u9b3c\u9b42\u3002\u201d\u8bf4\u7f62\uff0c\u5c31\u62b1\u8d77\u5b69 \u5b50\uff0c\u6025\u6025\u5fd9\u5fd9\u7684\u79bb\u5f00\u767e\u82b1\u8c37\u3002 \u3000\u3000\u6c5f\u5357\u4e0e\u59ec\u6653\u98ce\u4e3a\u4e86\u91d1\u4e16\u9057\u4fbf\u4e8e\u8ffd\u8e2a\uff0c\u4e00\u8def\u4e0a\u505a\u4e0b\u6807\u8bb0\uff0c\u5e76\u653e\u6162\u811a\u7a0b\uff0c\u4e00\u5929\u4e0d\u8fc7\u8d70\u51e0\u5341\u91cc \u8def\uff0c\u8d70\u4e86\u4e09\u5929\u4ecd\u672a\u89c1\u91d1\u4e16\u9057\u8d76\u6765\u3002 \u3000\u3000\u5230\u4e86\u7b2c\u56db\u5929\uff0c\u6c5f\u5357\u5fe7\u5fc3\u5fe1\u5fe1\uff0c\u4e00\u8def\u8d70\u4e00\u8def\u56de\u5934\uff0c\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u4f60\u4e0d\u5fc5\u5fc3\u7126\uff0c\u91d1\u5927 \u4fa0\u6216\u8005\u662f\u88ab\u65c1\u7684\u4e8b\u60c5\u803d\u6401\u4e86\u3002\u4ed6\u7edd\u4e0d\u4f1a\u629b\u5f00\u54b1\u4eec\u7684\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u6211\u5c31\u662f\u6015\u4ed6\u51fa\u4e86\u4e8b\uff01\u4ed6\u8bf4 \u8fc7\u6700\u591a\u4e24\u5929\u5c31\u4f1a\u8d76\u6765\u7684\uff0c\u73b0\u5728\u5df2\u7ecf\u662f\u7b2c\u56db\u5929\u3002\u524d\u9762\u5df2\u662f\u90ca\u57ce\uff0c\u8fc7\u4e86\u90ca\u57ce\uff0c\u5c31\u8e0f\u8fdb\u6c5f\u82cf\u5883\u4e86\u3002 \u4ed6\u4e0d\u4f1a\u662f\u53d7\u4f24\u4e86\u5427\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u90a3\u7edd\u4e0d\u4f1a\u3002\u6587\u5389\u4e8c\u4eba\u52a0\u4e0a\u90a3\u5929\u9b54\u6559\u4e3b\uff0c\u6700\u591a\u4e5f\u4e0d\u8fc7\u548c\u4ed6\u6253\u4e2a\u5e73\u624b\u3002\u6211\u5012\u4e0d \u62c5\u5fc3\u4ed6\u8eab\u4f53\u53d7\u4f24\uff0c\u800c\u662f\u62c5\u5fc3\u4ed6\u5fc3\u91cc\u53d7\u4f24\u3002\u4f46\u613f\u4ed6\u80fd\u89c1\u5230\u90a3\u59d3\u5389\u7684\u5c11\u5e74\uff0c\u6d88\u9664\u4e86\u90a3\u4eba\u5bf9\u4ed6\u7684\u6076 \u611f\u3002\u201d \u3000\u3000\u6b63\u8bf4\u5230\u6b64\u5904\uff0c\u6c5f\u5357\u5ffd\u5730\u8df3\u8d77\u6765\u9053\uff1a\u201c\u597d\u4e86\uff0c\u91d1\u5927\u4fa0\u6765\u4e86\u3002\u5582\uff0c\u4f60\u53ef\u89c1\u7740\u4e86\u90a3\u4eba\u6ca1\u6709\uff1f\u201d \u8f6c\u773c\u4e4b\u95f4\uff0c\u91d1\u4e16\u9057\u5df2\u7136\u6765\u5230\uff0c\u4f46\u89c1\u4ed6\u9762\u8272\u6c89\u91cd\uff0c\u5982\u6709\u9690\u5fe7\u3002\u59ec\u6653\u98ce\u9053\uff1a\u201c\u53ef\u662f\u5929\u9b54\u6559\u7684\u4eba\u90fd \u5df2\u8d70\u4e86\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u662f\u8001\u6c5f\u6e56\uff0c\u679c\u7136\u4e00\u731c\u4fbf\u4e2d\u3002\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fde\u90a3\u5341\u51e0\u95f4\u623f\u5b50\u4e5f\u70e7\u6389\u4e86\u3002\u5440\uff0c \u4ed6\u4eec\u7adf\u4f3c\u6599\u5230\u4e86\u6211\u4f1a\u518d\u6765\uff0c\u4e0d\u80af\u89c1\u6211\u3002\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4e0d\u662f\u4ed6\u4eec\u4e0d\u80af\u89c1\u4f60\uff0c\u800c\u662f\u4ed6\u4eec\u6015\u4e86 \u4f60\uff0c\u8981\u907f\u5f00\u4f60\u3002\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u8fd9\u6b21\u56de\u53bb\uff0c\u53ef\u5e76\u6ca1\u6709\u6076\u610f\u7684\u554a\uff01\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4f46\u662f\u4f60\u7684 \u5fc3\u610f\uff0c\u4ed6\u4eec\u600e\u80fd\u77e5\u9053\uff1f\u4f60\u65e5\u524d\u5927\u95f9\u4e86\u7ec4\u6765\u5c71\uff0c\u5c06\u90a3\u6587\u5c9b\u4e3b\u4e5f\u6253\u4f24\u4e86\u3002\u4ed6\u4eec\u5df2\u77e5\u9053\u4e86\u5f97\u7f6a\u4e86 \u4f60\uff0c\u4e0d\u6015\u4f60\u518d\u53bb\u6363\u6bc1\u4ed6\u4eec\u7684\u5de2\u7a74\u5417\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u4e5f\u662f\u4e2a\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u5f53\u7136\u61c2\u5f97\u8fd9\u4e2a\u9053\u7406\uff0c\u53ef\u662f\u5fc3\u4e2d\u4ecd\u7136\u95f7\u95f7\u4e0d\u4e50\u3002\u6c5f\u5357\u5ffd \u9053\uff1a\u201c\u53ef\u60dc\u8c37\u5973\u4fa0\u4e0d\u5728\u8fd9\u513f\uff0c\u6211\u53c8\u4e0d\u5927\u61c2\u5f97\u5982\u4f55\u529d\u89e3\u3002\u54a6\uff0c\u771f\u5947\u602a\uff0c\u8c37\u5973\u4fa0\u9001\u54b1\u4eec\u4e0b\u6c13\u5c71\u7684 \u65f6\u5929\u4e0a\u6709\u7247\u6d6e\u4e91\u906e\u4f4f\u4e86\u65e5\u5934\uff0c\u73b0\u5728\u53c8\u6709\u8fd9\u4e48\u4e00\u7247\u6d6e\u4e91\u3002\u597d\u4e86\uff0c\u597d\u4e86\uff0c\u73b0\u5728\u6d6e\u4e91\u8fc7\u53bb\u4e86\u3002\u5440\uff0c \u6211\u8bb0\u5f97\u8c37\u5973\u4fa0\u66fe\u7528\u6d6e\u4e91\u63a9\u65e5\u6253\u8fc7\u6bd4\u65b9\uff0c\u8fd8\u5ff5\u4e86\u4e24\u53e5\u8bd7\uff0c\u8bd7\u53e5\u6211\u4e0d\u8bb0\u5f97\u4e86\uff0c\u610f\u601d\u6211\u53c8\u4e0d\u61c2\uff0c\u53ea \u9690\u9690\u77e5\u9053\u5979\u662f\u529d\u4f60\u8981\u628a\u5fc3\u60c5\u653e\u5bbd\u7684\u3002\u6211\u4e0d\u61c2\u8bf4\u8bdd\uff0c\u53ea\u597d\u501f\u8c37\u5973\u4fa0\u7684\u8bdd\u6765\u529d\u4f60\u4e86\uff01\u201d \u3000\u3000\u7ecf\u8fc7\u4e86\u6c5f\u5357\u8fd9\u4e48\u4e00\u8bf4\uff0c\u91d1\u4e16\u9057\u60f3\u8d77\u4e86\u8c37\u4e4b\u534e\u90a3\u65e5\u9001\u4ed6\u7684\u60c5\u610f\uff0c\u60f3\u8d77\u4e86\u8c37\u4e4b\u534e\u90a3\u756a\u8bed\u91cd\u5fc3 \u957f\u7684\u8bf4\u8bdd\u3002\u4ed6\u8033\u8fb9\u4f3c\u54cd\u8d77\u4e86\u8c37\u4e4b\u534e\u7684\u58f0\u97f3\uff1a\u201c\u7eb5\u6709\u6d6e\u4e91\u80fd\u63a9\u65e5\uff0c\u9634\u973e\u4ea6\u4ec5\u662f\u987b\u81fe\uff0c\u6d6e\u4e91\u853d\u65e5 \u603b\u662f\u6709\u65f6\u7684\uff0c\u4f46\u613f\u4f60\u7684\u5fc3\u5883\u4e5f\u662f\u5982\u6b64\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u60f3\u81f3\u6b64\u5904\uff0c\u5931\u795e\u7684\u773c\u775b\u91cd\u6cdb\u51fa\u4e86\u5149\u8f89\uff0c\u4ed6\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5e78\u4e8f\u4f60\u63d0\u9192\u4e86\u6211\u3002 \u4eba\u751f\u5f97\u4e00\u77e5\u5df1\uff0c\u5df2\u53ef\u65e0\u61be\uff0c\u6211\u4e0d\u5fc5\u518d\u7406\u4f1a\u65c1\u4eba\u8bf4\u751a\u77ed\u957f\u4e86\u3002\u201d \u3000\u3000\u4ece\u6b64\u4e4b\u540e\uff0c\u91d1\u4e16\u9057\u4fbf\u7edd\u53e3\u4e0d\u63d0\u5389\u80dc\u7537\u7684\u4e8b\u60c5\uff0c\u751a\u81f3\u8fde\u7ec4\u6765\u5c71\u4e0e\u5929\u9b54\u6559\u4e3b\u7b49\u7b49\u6709\u5173\u4eba\u7269\uff0c \u4e5f\u907f\u5f00\u4e0d\u8c08\u3002\u4f46\u6b63\u56e0\u5982\u6b64\uff0c\u8fde\u6c5f\u5357\u4e5f\u53ef\u4ee5\u89c9\u5bdf\u5f97\u5230\uff1a\u4ed6\u7684\u5fc3\u5883\u867d\u7136\u6bd4\u524d\u7565\u89c1\u5f00\u6717\uff0c\u4f46\u4ed6\u5fc3\u5934 \u4e0a\u7684\u7ed3\u5374\u8fd8\u672a\u89e3\u5f00\u3002 \u3000\u3000\u4ed6\u4eec\u4f1a\u5408\u4e4b\u540e\uff0c\u4fbf\u5373\u517c\u7a0b\u8d76\u8def\uff0c\u8fd9\u4e00\u65e5\u5230\u4e86\u9648\u5929\u5b87\u7684\u5bb6\u4e61\uff0c\u90a3\u662f\u5728\u82cf\u5dde\u4e1c\u9762\u7ea6\u56db\u4e94\u5341\u91cc \u7684\u4e00\u5904\u540d\u53eb\u201c\u6728\u6954\u201d\u7684\u4e61\u4e0b\uff0c\u9762\u4e34\u4e24\u6e56\uff0c\u98ce\u666f\u6781\u7f8e\u3002\u6c5f\u5357\u66fe\u7ecf\u5728\u8fd9\u91cc\u4f4f\u8fc7\u51e0\u5e74\uff0c\u65e7\u5730\u91cd\u6765\uff0c \u98ce\u5149\u5982\u6628\uff0c\u7981\u4e0d\u4f4f\u5fc3\u82b1\u6012\u653e\uff0c\u4e00\u8def\u4e0a\u8df3\u8df3\u8e66\u8e66\uff0c\u53e3\u8bb2\u6307\u5212\u4ed6\u8bf4\u7ed9\u4ed6\u513f\u5b50\u542c\uff1a\u5728\u8fd9\u7247\u8349\u5730\u4e0a\uff0c \u4ed6\u66fe\u6253\u8fc7\u6eda\uff0c\u5728\u90a3\u4e2a\u5c0f\u5c71\u8fb9\u4ed6\u66fe\u6349\u8fc7\u4e94\u8272\u7684\u8774\u8776\uff0c\u53c8\u5728\u90a3\u4e00\u5904\u6e56\u8fb9\u4ed6\u66fe\u9493\u8fc7\u9c7c\u2026\u2026 \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u4f60\u7b80\u76f4\u4e0d\u50cf\u4e00\u4e2a\u7236\u4eb2\uff0c\u5374\u50cf\u4e0e\u4f60\u513f\u5b50\u540c\u6837\u5e74\u5c81\u7684\u5c0f\u987d\u7ae5\uff01\u201d\u6c5f\u5357\u4e5f\u7b11 \u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u5c0f\u65f6\u5019\u7684\u786e\u662f\u6bd4\u4ed6\u6dd8\u6c14\u5f97\u591a\u3002\u6751\u5b50\u91cc\u7684\u4eba\u6ca1\u6709\u4e0d\u77e5\u9053\u6211\u7684\u3002\u201d \u3000\u3000\u53ef\u662f\u6c5f\u5357\u7684\u6b22\u60a6\u672a\u80fd\u4fdd\u7559\u591a\u4e45\uff0c\u4e00\u5230\u4e86\u9648\u5929\u5b87\u7684\u95e8\u524d\uff0c\u4fbf\u5403\u4e86\u4e00\u60ca\uff0c\u6ee1\u5929\u6b22\u559c\uff0c\u767b\u65f6\u6d88 \u5931\uff0c\u5fc3\u4e0a\u538b\u4e0a\u4e86\u7591\u4e91\u3002 \u3000\u3000\u4f46\u89c1\u5927\u95e8\u7d27\u9501\uff0c\u95e8\u4e0a\u8fd8\u6709\u51e0\u4e2a\u88c2\u7f1d\uff0c\u5e18\u5934\u7ed3\u6709\u86db\u7f51\uff0c\u6c5f\u5357\u6572\u4e86\u6572\u95e8\uff0c\u624b\u638c\u90fd\u6cbe\u6ee1\u4e86\u7070 \u5c18\uff0c\u91cc\u9762\u4e5f\u5f53\u7136\u662f\u6beb\u65e0\u58f0\u606f\uff01\u770b\u6837\u5b50\uff0c\u8fd9\u5bb6\u95e8\u5df2\u4e0d\u77e5\u6709\u591a\u5c11\u65f6\u5019\u672a\u66fe\u6709\u4eba\u8fdb\u51fa\u4e86\u3002 \u3000\u3000\u4e09\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u5fc3\u4e2d\u5747\u5728\u60ca\u8be7\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u8fd9\u4f4d\u53ef\u662f\u6c5f\u5357 \u5c0f\u54e5\u5417\uff1f\u201d\u6c5f\u5357\u4e00\u770b\uff0c\u8ba4\u5f97\u662f\u6751\u4e2d\u7684\u4fdd\u4e0a\u738b\u8001\u5934\uff0c\u8fde\u5fd9\u5e94\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u6c5f\u5357\uff0c\u6211\u56de\u6765 \u4e86\u3002\u201d\u738b\u8001\u5934\u9053\uff1a\u201c\u53ef\u662f\u9648\u516c\u5b50\u53eb\u4f60\u56de\u6765\u7684\u4e48\uff1f\u8fd9\u5c31\u597d\u4e86\uff01\u4f60\u518d\u4e0d\u56de\u6765\uff0c\u7816\u5934\u74e6\u7247\u4e5f\u8981\u7ed9\u4eba \u642c\u8d70\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5357\u60ca\u7591\u4e4b\u6781\uff0c\u95ee\u9053\uff1a\u201c\u6211\u7684\u4e49\u5144\u5462\uff1f\u4ed6\u4e0d\u5728\u5bb6\uff1f\u201d\u90a3\u738b\u8001\u5934\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u95f4\u9053\uff1a\u201c\u4f60 \u4e0d\u662f\u9648\u516c\u5b50\u53eb\u4f60\u56de\u6765\u7684\u4e48\uff1f\u8fd9\u4e24\u4f4d\u662f\u2014\u2014\u201d\u6c5f\u5357\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u662f\u6211\u7684\u670b\u53cb\uff0c\u8fd9\u662f\u6211\u7684\u5b69 \u5b50\u3002\u201d\u90a3\u738b\u8001\u5934\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u7684\u5b69\u5b50\uff0c\u5440\uff0c\u5149\u9634\u771f\u662f\u8fc7\u5f97\u5feb\uff0c\u4f60\u7684\u5b69\u5b50\u4e5f\u8fd9\u4e48\u5927\u4e86\uff0c\u4f60\u642c\u56de \u6765\u4f4f\u5427\uff0c\u8fd9\u4e2a\u5bb6\u73b0\u5728\u5df2\u662f\u6ca1\u4eba\u7ba1\u4e86\u5462\uff01\u201d \u3000\u3000\u90a3\u8001\u5934\u5b50\u5e74\u7eaa\u592a\u5927\uff0c\u8bf4\u8bdd\u7f57\u55e6\uff0c\u8bf4\u6765\u8bf4\u53bb\u6ca1\u6709\u8bf4\u5230\u6b63\u9898\uff0c\u82e5\u5728\u5e73\u65f6\uff0c\u6c5f\u5357\u6b63\u4e50\u5f97\u548c\u4ed6\u804a \u5929\uff0c\u4f46\u5728\u6b64\u9645\uff0c\u4ed6\u54ea\u91cc\u8fd8\u6709\u95f2\u60c5\u3002\u4ed6\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u8fdb\u53bb\u8bf4\u8bdd\uff0c\u6211\u4e5f\u8981\u770b\u770b\u91cc \u9762\u53d8\u6210\u4ec0\u4e48\u6837\u5b50\u4e86\uff1f\u201d\u7acb\u5373\u626d\u65ad\u4e86\u9501\uff0c\u6253\u5f00\u5927\u95e8\uff0c\u4f46\u89c9\u4e00\u80a1\u9709\u70c2\u7684\u6c14\u5473\u6251\u9f3b\u800c\u6765\uff0c\u5c4b\u5b50\u91cc\u7834 \u7834\u70c2\u70c2\u7684\u60c5\u5f62\uff0c\u6bd4\u4ed6\u6240\u80fd\u60f3\u8c61\u7684\u66f4\u751a\u5f97\u591a\u3002\u4f46\u89c1\u5ead\u9662\u4e4b\u4e2d\u957f\u6ee1\u91ce\u8349\uff0c\u5385\u5802\u7684\u53e4\u73a9\u6446\u8bbe\u5b57\u753b\u7b49 \u7b49\u5c3d\u90fd\u4e0d\u89c1\uff0c\u5185\u623f\u7684\u8863\u67dc\u4ea6\u5df2\u6253\u70c2\uff0c\u4e1c\u897f\u5dee\u4e0d\u591a\u90fd\u5df2\u88ab\u642c\u8fd0\u4e00\u7a7a\uff0c\u53ea\u5269\u4e0b\u51e0\u4ef6\u7834\u70c2\u7684\u5bb6\u79c1\u548c \u4e00\u5927\u5806\u5783\u573e\u3002 \u3000\u3000\u738b\u8001\u5934\u4e00\u8138\u5c34\u5c2c\u7684\u795e\u8272\uff0c\u54b3\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u5c0f\u54e5\uff0c\u4f60\u662f\u77e5\u9053\u7684\uff0c\u6751\u5b50\u91cc\u6709\u597d\u4eba\u4e5f\u6709 \u574f\u4eba\uff0c\u9648\u5bb6\u662f\u8457\u540d\u7684\u5927\u6237\u4eba\u5bb6\uff0c\u6ca1\u4eba\u770b\u5b88\uff0c\u96be\u514d\u6709\u4e9b\u8d2a\u5fc3\u7684\u4eba\u722c\u8fc7\u5899\u6765\u5077\u4e1c\u897f\uff0c\u4e5f\u8bb8\u8fd8\u6709\u95fb \u98ce\u800c\u6765\u7684\uff0c\u4e0d\u662f\u672c\u6751\u7684\u4eba\u5462\u3002\u6211\u867d\u662f\u4fdd\u6b63\uff0c\u4f46\u5e74\u7eaa\u8001\u8fc8\uff0c\u4e5f\u6ca1\u6709\u7cbe\u795e\u767d\u5929\u9ed1\u591c\u90fd\u5728\u8fd9\u91cc\u7ed9\u4f60 \u4eec\u770b\u5b88\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u602a\u4f60\uff0c\u4e1c\u897f\u4e8b\u5c0f\uff0c\u4e0d\u89c1\u4e86\u4eba\u4e8b\u5927\u3002\u6211\u7684\u4e49\u5144\u5230\u5e95\u662f\u4ec0\u4e48\u65f6\u5019\u79bb\u5bb6\u7684\uff1f \u4ed6\u5bf9\u4eb2\u670b\u621a\u53cb\u4e5f\u6ca1\u6709\u8bf4\u4e00\u58f0\u5417\uff1f\u8fd8\u6709\u4e24\u4f4d\u8001\u5bb6\u4eba\u5462\uff1f\u4ed6\u4eec\u53c8\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u738b\u8001\u5934\u9053\uff1a\u201c\u9648\u516c\u5b50\u4ec0\u4e48\u65f6\u5019\u79bb\u5bb6\uff0c\u786e\u5207\u7684\u65e5\u5b50\u8c01\u4e5f\u4e0d\u77e5\u9053\u3002\u90a3\u662f\u53bb\u5e74\u4e5d\u6708\u7684\u4e8b\u60c5\uff0c\u63a5 \u8fde\u6709\u597d\u51e0\u5929\uff0c\u9648\u5bb6\u7684\u5927\u95e8\u90fd\u4e0d\u6253\u5f00\uff0c\u5148\u6709\u4eba\u6ce8\u610f\u5230\u4e86\uff0c\u55a7\u95f9\u8d77\u6765\u3002\u4f46\u9648\u5bb6\u662f\u5b98\u5bb6\uff0c\u8c01\u4e5f\u4e0d\u6562 \u7834\u95e8\u800c\u5165\u3002\u540e\u6765\uff0c\u4ed6\u6709\u4e2a\u5728\u53bf\u57ce\u91cc\u5f53\u5178\u53f2\u7684\u4eb2\u621a\u4e5f\u77e5\u9053\u4e86\uff0c\u4fbf\u542f\u7980\u5b98\u5e9c\uff0c\u7531\u53bf\u5b98\u5927\u8001\u7237\u4eb2 \u6765\uff0c\u8fd9\u624d\u6562\u6253\u5f00\u89d2\u95e8\uff0c\u8fdb\u5185\u67e5\u52d8\u3002\u201d\u201d \u3000\u3000\u6c5f\u5357\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u5f53\u65f6\u89c1\u5230\u4ec0\u4e48\u60c5\u666f\uff1f\u201d\u738b\u8001\u5934\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u8001\u4ec6\u50f5\u5367\u5e8a\u4e0a\uff0c\u5c38\u4f53\u5df2\u5dee \u4e0d\u591a\u53d1\u81ed\uff0c\u7ecf\u8fc7\u5b98\u533b\u9a8c\u5c38\uff0c\u4e5f\u67e5\u4e0d\u51fa\u6b7b\u56e0\uff0c\u9664\u4e86\u8fd9\u4e2a\u5df2\u6b7b\u6389\u7684\u8001\u4ec6\u4e4b\u5916\uff0c\u522b\u65e0\u4e00\u4eba\u3002\u53bf\u5b98\u53ea \u597d\u547d\u6211\u5c06\u90a3\u4ec6\u4eba\u57cb\u846c\uff0c\u518d\u4eb2\u624b\u9501\u4e0a\u4e86\u5927\u95e8\uff0c\u5429\u5490\u4eca\u540e\u4efb\u4f55\u4eba\u7b49\u4e0d\u5f97\u79c1\u81ea\u5165\u5185\uff0c\u53ea\u6709\u9648\u5bb6\u7684\u4eba \u56de\u6765\u624d\u53ef\u4ee5\u6253\u5f00\u3002\u201d\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u987f\u4e86\u4e00\u987f\uff0c\u671b\u7740\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u9648\u8001\u7239\u5728\u751f\u524d\u5df2\u628a\u4f60 \u6536\u4e3a\u4e49\u5b50\uff0c\u4f60\u7b97\u5f97\u662f\u9648\u5bb6\u7684\u4eba\uff0c\u8981\u4e0d\u7136\u6211\u8fd8\u4e0d\u6562\u8ddf\u4f60\u8fdb\u6765\u5462\uff01\u201d \u3000\u3000\u738b\u8001\u5934\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5f53\u65f6\u672c\u6765\u5728\u5927\u95e8\u4e0a\u8fd8\u8d34\u6709\u77e5\u53bf\u7684\u5c01\u6761\uff0c\u4f46\u7ecf\u8fc7\u4e86\u8fd9\u8bb8\u591a\u65f6\u65e5\uff0c\u96e8\u6dcb \u65e5\u6652\uff0c\u65e9\u5df2\u635f\u6bc1\u65e0\u9057\uff0c\u8fde\u75d5\u8ff9\u4e5f\u4e0d\u89c1\u4e86\u3002\u201d\u8981\u77e5\u9648\u5929\u5b87\u7684\u7236\u4eb2\u9648\u5b9a\u57fa\u66fe\u505a\u8fc7\u5927\u5b98\uff0c\u6240\u4ee5\u77e5\u53bf \u624d\u8fd9\u6837\u614e\u91cd\uff1b\u82e5\u662f\u6362\u4e86\u666e\u901a\u4eba\u5bb6\uff0c\u5b98\u5e9c\u65e9\u5df2\u4e58\u673a\u6572\u8bc8\uff0c\u7ed9\u4f60\u5224\u4e00\u4e2a\u201c\u6bb4\u6bd9\u5bb6\u4eba\uff0c\u754f\u7f6a\u6f5c \u9003\u201d\uff0c\u5c06\u5bb6\u8d44\u7c4d\u6ca1\u5165\u5b98\u53f8\u4e86\u3002 \u3000\u3000\u6c5f\u5357\u95ee\u9053\uff1a\u201c\u4f60\u521a\u624d\u8bf4\u6b7b\u6389\u7684\u53ea\u662f\u4e00\u4f4d\u4ec6\u4eba\uff0c\u90a3\u4e48\u8fd8\u6709\u4e00\u4f4d\u4ec6\u4eba\u5462\uff1f\u201d\u738b\u8001\u5934\u9053\uff1a\u201c\u6768 \u8001\u4e09\u8fd8\u5728\u3002\u201d\u6c5f\u5357\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u5728\u54ea\u91cc\uff1f\u201d\u738b\u8001\u5934\u9053\uff1a\u201c\u4ed6\u5728\u9648\u5bb6\u770b\u5b88\u5893\u56ed\u3002\u5440\uff0c\u53ea\u662f\u4ed6\u7684 \u5883\u9047\u4e5f\u60e8\u5f97\u5f88\uff0c\u4f60\u4eec\u7eb5\u7136\u89c1\u7740\u4e86\u4ed6\uff0c\u53ea\u6015\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7528\u3002\u55ef\uff0c\u6c5f\u5c0f\u54e5\uff0c\u4f60\u77e5\u9053\u4ed6\u7684\u60c5\u5f62\u5417\uff1f\u201d \u3000\u3000\u6c5f\u5357\u7684\u5fc3\u60c5\u5df2\u662f\u7126\u6025\u4e4b\u6781\uff0c\u6015\u90a3\u738b\u8001\u5934\u7f57\u55e6\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u8001\u4f2f\uff0c\u591a\u8c22\u4f60\u4e86\u3002\u6768\u8001\u4e09\u7684 \u60c5\u5f62\uff0c\u6211\u89c1\u4e86\u4ed6\uff0c\u6211\u81ea\u4f1a\u95ee\u4ed6\uff0c\u6055\u6211\u4eec\u5931\u966a\u4e86\u3002\u201d\u8bf4\u7f62\uff0c\u4fbf\u8feb\u4e0d\u53ca\u5f85\u7684\u62b1\u8d77\u5b69\u5b50\uff0c\u8dd1\u51fa\u9648 \u5bb6\uff0c\u5934\u524d\u5e26\u8def\uff0c\u5e26\u9886\u91d1\u59ec\u4e8c\u4eba\u540c\u5f80\u5893\u56ed\u3002\u80cc\u540e\u8fd8\u9690\u9690\u542c\u5f97\u90a3\u738b\u8001\u5934\u5728\u5509\u58f0\u53f9\u6c14\u3002 \u3000\u3000\u6c5f\u5357\u5306\u5306\u5fd9\u5fd9\u8d76\u8def\u3002\u4e00\u8def\u4e0a\u78b0\u5230\u8bb8\u591a\u719f\u4eba\u4e0e\u4ed6\u62db\u547c\uff0c\u90a3\u4e9b\u4eba\u90fd\u7528\u60ca\u5947\u7684\u773c\u5149\u770b\u4ed6\uff0c\u6c5f\u5357 \u65e0\u6687\u4e0e\u4ed6\u4eec\u53d9\u8bdd\uff0c\u62db\u547c\u7684\u65f6\u5019\u4e5f\u6ca1\u6709\u505c\u4e0b\u811a\u6b65\u3002 \u3000\u3000\u534a\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u4ed6\u4eec\u5230\u8fbe\u4e86\u9648\u5bb6\u7684\u5893\u56ed\u3002\u9648\u5929\u5b87\u7684\u7236\u4eb2\u9648\u5b9a\u57fa\u524d\u5e74\u5df2\u7ecf\u53bb\u4e16\uff0c\u9648\u5929\u5b87\u5c06 \u7236\u6bcd\u5408\u846c\uff0c\u9664\u4e86\u8fd9\u5ea7\u65b0\u575f\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u5ea7\u65e7\u575f\uff0c\u90a3\u662f\u8428\u8fe6\u5b97\u571f\u53f8\u5973\u513f\u6851\u58c1\u4f0a\u7684\u575f\u5893\uff0c\u5f53\u5e74\u9648 \u5929\u5b87\u7684\u7236\u4eb2\u5f53\u897f\u85cf\u8428\u8fe6\u5b97\u5ba3\u6170\u4f7f\u7684\u65f6\u5019\uff0c\u571f\u53f8\u66fe\u8feb\u9648\u5929\u5b87\u5a36\u4ed6\u7684\u5973\u513f\uff0c\u9648\u5929\u5b87\u4e14\u66fe\u56e0\u6b64\u9003 \u5a5a\u3002\u540e\u6765\u6851\u58c1\u4f0a\u8ffd\u5230\u6728\u728a\u9648\u5bb6\uff0c\u4f24\u4e86\u9648\u5929\u5b87\u73b0\u5728\u7684\u59bb\u5b50\u5e7d\u840d\u4e4b\u540e\uff0c\u4fbf\u81ea\u5df1\u7528\u6bd2\u7bad\u81ea\u6740\u3002\uff08\u4e8b \u8be6\u300a\u4e91\u6d77\u7389\u5f13\u7f18\u300b\u6545\u6b64\u9648\u5929\u5b87\u4ee5\u59bb\u5b50\u4e4b\u793c\u846c\u5979\u3002 \u3000\u3000\u91d1\u4e16\u9057\u4e00\u8e0f\u8fdb\u5893\u56ed\uff0c\u4fbf\u9732\u51fa\u8be7\u5f02\u7684\u795e\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u91cc\u6709\u8fdc\u65b9\u7684\u5ba2\u4eba\u6765\u8fc7\uff01\u201d\u6c5f\u5357 \u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u91d1\u4e16\u9057\u7528\u624b\u4e00\u6307\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u8fd9\u4e0d\u662f\u897f\u85cf\u548c\u56de\u7586\u7684\u9ad8\u539f\u5730\u5e26\u624d \u6709\u7684\u91d1\u8fbe\u83b1\u82b1\u5417\uff1f\u201d\u91d1\u8fbe\u83b1\u82b1\u76db\u5f00\u7684\u65f6\u5019\u5176\u5927\u5982\u7897\uff0c\u989c\u8272\u91d1\u9ec4\uff0c\u5927\u7ea6\u662f\u56e0\u4e3a\u79fb\u690d\u5e73\u539f\uff0c\u4fbf\u53ea \u6709\u9152\u676f\u822c\u5927\u5c0f\uff0c\u989c\u8272\u4e5f\u6de1\u5f97\u591a\uff0c\u4e0d\u8fc7\u4ece\u8fd9\u79cd\u82b1\u7279\u6709\u7684\u9999\u6c14\u8fd8\u53ef\u4ee5\u8fa8\u8ba4\u5f97\u51fa\u3002 \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u6211\u8bb0\u5f97\u6851\u58c1\u4f0a\u662f\u6700\u559c\u6b22\u91d1\u8fbe\u83b1\u82b1\u7684\u3002\u96be\u9053\u9648\u5bb6\u6240\u53d1\u751f\u7684\u4e8b\uff0c\u662f\u8428\u8fe6\u5b97 \u7684\u571f\u53f8\u6d3e\u4eba\u6765\u7ed9\u5973\u513f\u62a5\u4ec7\u4e48\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u9648\u5929\u5b87\u592b\u59bb\u7684\u6b66\u529f\u975e\u540c\u5c0f\u53ef\uff0c\u8c05\u8428\u8fe6\u5b97\u4e00\u4e2a\u5c0f\u5c0f\u7684\u571f\u53f8\u4e5f\u8bf7\u4e0d\u5230\u4ec0\u4e48\u80fd \u4eba\u3002\u54a6\uff0c\u8fd9\u4e8b\u60c5\u6709\u70b9\u5947\u602a\uff01\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u597d\u5728\u6768\u8001\u4e09\u4fbf\u5728\u8fd9\u513f\uff0c\u4e00\u95ee\u4ed6\u4fbf\u77e5\u9053\u4e86\u3002\u201d\u6851\u58c1\u4f0a\u7684\u5893\u540e\u6709\u95f4\u8305\u5c4b\uff0c\u8bf4\u8bdd\u4e4b \u95f4\uff0c\u5df2\u6709\u4e00\u4e2a\u8001\u4eba\u4ece\u5c4b\u5185\u51fa\u6765\uff0c\u6b63\u662f\u90a3\u6768\u8001\u4e09\u3002 \u3000\u3000\u6c5f\u5357\u5927\u559c\u53eb\u9053\uff1a\u201c\u8001\u6768\uff0c\u6211\u6765\u4e86\uff01\u54a6\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u6211\u662f\u6c5f\u5357\u3002\u4f60\u4e0d\u8ba4\u5f97\u4e86\u5417\uff1f\u201d\u53ea\u89c1\u6768 \u8001\u4e09\u7ffb\u8d77\u4e00\u53cc\u767d\u6e17\u6e17\u7684\u773c\u73e0\uff0c\u5b9a\u775b\u671b\u4ed6\uff0c\u90a3\u795e\u60c5\u7b80\u76f4\u5c31\u50cf\u767d\u75f4\u4e00\u822c\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u4ed6\u4f3c\u4e4e\u8bb0 \u5f97\u6c5f\u5357\u4f3c\u66fe\u76f8\u8bc6\uff0c\u4f0a\u4f0a\u54d1\u54d1\u7684\u5636\u53eb\u8d77\u6765\uff0c\u53ef\u662f\u8c01\u4e5f\u542c\u4e0d\u51fa\u4ed6\u662f\u8bf4\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u53c8\u6709\u4e00\u4e2a\u5341\u591a\u5c81\u7684\u5b69\u5b50\u8ddf\u7740\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u6c5f\u62ff\u54e5\u54e5\uff0c\u4f60\u6765\u4e86\u5440\uff01\u4f60\u53ef\u77e5\u9053 \u4e86\u9648\u5bb6\u7684\u4e8b\u60c5\u4e48\uff1f\u201d\u6c5f\u5357\u8ba4\u5f97\u4ed6\u662f\u6768\u8001\u4e09\u7684\u758f\u623f\u4f84\u513f\uff0c\u5fd9\u9053\uff1a\u201c\u5c0f\u6768\u5b50\uff0c\u9648\u5bb6\u7684\u4e8b\u60c5\u6211\u5df2\u542c \u8bf4\u4e86\u3002\u6b63\u6765\u95ee\u4f60\u7684\u5927\u4f2f\uff0c\u4f60\u7684\u5927\u4f2f\u5374\u600e\u7684\u53d8\u6210\u4e86\u8fd9\u4e2a\u6837\u5b50\u5566\uff1f\u201d \u3000\u3000\u90a3\u5b69\u5b50\u9053\uff1a\u201c\u6211\u5927\u4f2f\u4ece\u53bb\u5e74\u8d77\u88ab\u6d3e\u5728\u8fd9\u91cc\u770b\u5b88\u5893\u56ed\uff0c\u5c31\u5728\u9648\u5bb6\u51fa\u4e8b\u4e4b\u540e\uff0c\u4ed6\u4e5f\u5c31\u53d8\u6210\u8fd9 \u4e2a\u6837\u5b50\u4e86\u3002\u6b63\u662f\u56e0\u6b64\uff0c\u6240\u4ee5\u6211\u624d\u6765\u966a\u4ed6\u4f4f\u3002\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u9e3f\u98de\u5bb5\u97f3\u77e5\u4f55\u5904\uff1f\u7591\u6848\u96be\u660e\u53c8\u4e00\u5b97\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000--------------- \u3000\u3000\u626c\u5251\u8f69\u5c45\u58eb \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u5341\u4e09\u56de\u3000\u65e7\u5730\u91cd\u6765\u589e\u6005\u60d8\u3000\u6545\u4eba\u4f55\u5f80\u60f9\u76f8\u601d \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u5341\u4e09\u56de\u3000\u65e7\u5730\u91cd\u6765\u589e\u6005\u60d8\u3000\u6545\u4eba\u4f55\u5f80\u60f9\u76f8\u601d \u3000\u3000\u5c0f\u6768\u5b50\u53c8\u9053\uff1a\u201c\u4ed6\u505a\u60ef\u7684\u65e5\u5e38\u5de5\u4f5c\u4e00\u6837\u4f1a\u505a\uff0c\u53ea\u662f\u795e\u667a\u4e0d\u6e05\uff0c\u53c8\u804b\u53c8\u54d1\uff0c\u6211\u4e5f\u66fe\u8bf7\u533b\u751f \u7ed9\u4ed6\u770b\u8fc7\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\u75c5\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u5ffd\u5730\u4f38\u51fa\u4e2d\u6307\uff0c\u5728\u4ed6\u8033\u540e\u7684\u201c\u7a8d\u9634\u7a74\u201d\u4e00\u5f39\uff0c\u90a3\u8001\u5934\u201c\u554a\u5440\u201d\u4e00\u58f0\u53eb\u5c06\u8d77\u6765\uff0c\u5ffd \u7136\u62b1\u7740\u6c5f\u5357\uff0c\u5e72\u53f7\u51e0\u58f0\uff0c\u90a3\u58f0\u97f3\u5c31\u4f3c\u53d7\u4f24\u7684\u91ce\u517d\u543c\u53eb\u4e00\u822c\uff0c\u53eb\u4eba\u542c\u4e86\uff0c\u5341\u5206\u96be\u53d7\uff0c\u611f\u5230\u6050 \u6016\uff0c\u53c8\u611f\u5230\u51c4\u60e8\u3002 \u3000\u3000\u6c5f\u5357\u6d41\u4e0b\u6cea\u6765\uff0c\u95ee\u9053\uff1a\u201c\u8001\u6768\uff0c\u4f60\u662f\u7ed9\u4f55\u4eba\u6240\u5bb3\uff0c\u8bf4\u7ed9\u6211\u542c\uff0c\u6211\u4e3a\u4f60\u62a5\u4ec7\uff0c\u6211\u662f\u6c5f\u5357\uff0c \u4f60\u60f3\u8d77\u6765\u4e86\uff1f\u4f60\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u5c31\u5199\u7ed9\u6211\u770b\u3002\u201d\u4ed6\u8bb0\u5f97\u6768\u8001\u4e09\u662f\u8ba4\u5f97\u51e0\u4e2a\u5b57\u7684\uff0c\u4fbf\u628a\u7740\u4ed6\u7684\u624b\uff0c \u60f3\u53eb\u4ed6\u5728\u6ce5\u571f\u4e0a\u4e66\u5199\u3002 \u3000\u3000\u6768\u8001\u4e09\u4f3c\u4e4e\u7a0d\u7a0d\u6062\u590d\u4e86\u77e5\u89c9\uff0c\u4f46\u53ea\u4e0d\u8fc7\u7247\u523b\uff0c\u4ed6\u7684\u773c\u775b\u53c8\u9eef\u6de1\u65e0\u795e\uff0c\u6f20\u7136\u7684\u63a8\u5f00\u6c5f\u5357\uff0c \u54d1\u54d1\u7684\u80e1\u53eb\u4e00\u901a\uff0c\u56de\u590d\u4e86\u5148\u524d\u7684\u72b6\u6001\u3002 \u3000\u3000\u91d1\u4e16\u9057\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4ed6\u662f\u88ab\u4eba\u7528\u9634\u6bd2\u7684\u624b\u6cd5\u70b9\u4e86\u8111\u6d77\u7a74\uff0c\u65f6\u65e5\u592a\u4e45\uff0c\u82e5\u8981\u7ed9\u4ed6\u89e3\u7a74\uff0c\u975e \u7528\u91cd\u624b\u6cd5\u4e0d\u884c\u3002\u53ef\u662f\u4ed6\u6beb\u65e0\u5185\u529f\u6839\u5e95\uff0c\u53c8\u53d7\u4e0d\u4e86\u91cd\u624b\u6cd5\u89e3\u7a74\u3002\u8fd9\u5df2\u7ecf\u662f\u65e0\u6cd5\u53ef\u60f3\u4e86\uff01\u201d \u3000\u3000\u6c5f\u5357\u53eb\u58f0\uff1a\u201c\u82e6\u4e5f\uff01\u201d\u8bf4\u9053\uff1a\u201c\u6768\u8001\u4e09\u662f\u552f\u4e00\u7684\u7ebf\u7d22\uff0c\u5982\u4eca\u5374\u6210\u4e86\u5e9f\u4eba\uff0c\u6211\u4e49\u5144\u7684\u906d \u9047\uff0c\u8fd8\u6709\u8c01\u80fd\u77e5\u9053\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4e8b\u5df2\u5982\u6b64\uff0c\u7559\u5728\u8fd9\u91cc\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7528\u5904\u4e86\u3002\u4e0d\u5982\u5148\u56de\u6c13\u5c71\u53bb\u5427\u3002\u201d \u3000\u3000\u6c5f\u5357\u81ea\u5c0f\u5f97\u6768\u8001\u4e09\u7167\u6599\uff0c\u96be\u514d\u4f24\u611f\uff0c\u5f53\u4e0b\u53ea\u597d\u7559\u4e0b\u51e0\u5341\u4e24\u94f6\u5b50\u7ed9\u4ed6\u7684\u4f84\u513f\uff0c\u7565\u8868\u5fc3\u610f\uff0c \u7136\u540e\u53c8\u643a\u540c\u4ed6\u7684\u513f\u5b50\u5230\u9648\u5b9a\u57fa\u7684\u7684\u575f\u5893\u524d\u62dc\u796d\u4e00\u756a\uff0c\u8fd9\u624d\u79bb\u53bb\u3002 \u3000\u3000\u8fd9\u56de\u8f6e\u5230\u4e86\u6c5f\u5357\u90c1\u90c1\u4e0d\u6b22\uff0c\u4e00\u8def\u4e0a\u90fd\u5df2\u65e0\u5fc3\u8bf4\u7b11\u4e86\u3002\u59ec\u6653\u98ce\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4f60\u770b \u8fd9\u662f\u4e0d\u662f\u5929\u9b54\u6559\u4e3b\u5e72\u7684\uff1f\u201d\u91d1\u4e16\u9057\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u662f\u6839\u636e\u4ec0\u4e48\u63a8\u6d4b\uff1f\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u70b9\u8111\u6d77\u7a74 \u4ee4\u4eba\u75f4\u5446\u7684\u624b\u6cd5\uff0c\u4f3c\u4e4e\u53ea\u662f\u5728\u4e54\u5317\u6e9f\u7684\u6b66\u529f\u79d8\u7c4d\u4e2d\u8f7d\u6709\uff0c\u5f53\u4e16\u61c2\u5f97\u8fd9\u79cd\u70b9\u7a74\u624b\u6cd5\u7684\u6ca1\u6709\u51e0 \u4eba\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u6700\u521d\u4e5f\u66fe\u8fd9\u6837\u63a8\u60f3\uff0c\u4f46\u518d\u60f3\u4e00\u60f3\uff0c\u5374\u672a\u5fc5\u5b9a\u662f\u5929\u9b54\u6559\u4e3b\u3002\u201d\u59ec\u6653\u98ce\u9053\uff1a \u201c\u6216\u8005\u662f\u90a3\u9ed1\u8863\u5c11\u5e74\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u90a3\u66f4\u4e0d\u4f1a\uff01\u201d\u6c5f\u5357\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u5728\u4f60\u5fc3\u76ee\u4e2d\u4ee5\u4e3a \u662f\u8c01\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u76ee\u524d\u6211\u53ea\u662f\u4e00\u79cd\u63a8\u6d4b\uff0c\u5bf9\u672a\u66fe\u8bc1\u5b9e\u7684\u4e8b\u60c5\u6211\u4e0d\u613f\u4e71\u8bf4\u3002\u603b\u4e4b\uff0c\u5bf9\u9648\u5929 \u5b87\u592b\u59bb\u7684\u906d\u9047\uff0c\u6211\u51b3\u4e0d\u4f1a\u7f6e\u4e4b\u4e0d\u7406\u5c31\u662f\u4e86\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d\u6c5f\u5357\u4e00\u5411\u5bf9\u91d1\u4e16\u9057\u6781\u4e3a \u4fe1\u670d\uff0c\u867d\u7136\u91d1\u4e16\u9057\u4e0d\u80af\u4ed4\u7ec6\u5256\u6790\uff0c\u4ed6\u5df2\u7a0d\u7a0d\u5bbd\u5fc3\uff0c\u4f46\u4e5f\u8fd8\u6709\u4e00\u70b9\u6000\u7591\u3002 \u3000\u3000\u6c5f\u5357\u5fc3\u91cc\u6000\u7591\u7684\u662f\uff0c\u9648\u5929\u5b87\u592b\u59bb\u5018\u771f\u662f\u78b0\u5230\u5f3a\u654c\uff0c\u4e3a\u4f55\u4e0d\u5c31\u8fd1\u5411\u5c11\u6797\u6d3e\u6216\u6c13\u5c71\u6d3e\u6c42\u63f4\uff0c \u5374\u8981\u8fdc\u8d70\u907f\u96be\uff1f\u800c\u4e14\u5373\u7b97\u4ed6\u8d70\u5f97\u5306\u5fd9\uff0c\u81f3\u4eca\u5df2\u6709\u516b\u4e5d\u4e2a\u6708\uff0c\u4e5f\u8be5\u6258\u4eba\u7ed9\u81ea\u5df1\u62a5\u4e2a\u4fe1\u606f\u5440\u3002 \u3000\u3000\u4f46\u8fd9\u4e24\u70b9\u6000\u7591\uff0c\u9664\u4e86\u9648\u5929\u5b87\u672c\u4eba\u4e4b\u5916\uff0c\u662f\u6ca1\u4eba\u80fd\u591f\u7ed9\u4eba\u89e3\u91ca\u7684\u3002\u6c5f\u5357\u53ea\u597d\u5b58\u7740\u7591\u56e2\uff0c\u548c \u91d1\u3001\u59ec\u4e8c\u4eba\u540c\u56de\u6c13\u5c71\u3002 \u3000\u3000\u56de\u5230\u6c13\u5c71\u4e4b\u540e\uff0c\u8c37\u4e4b\u534e\u542c\u4e86\u91d1\u4e16\u9057\u8bf4\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4e5f\u5f88\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u771f\u662f\u4e00\u6869\u65e0\u5934\u516c \u6848\uff0c\u54b1\u4eec\u7684\u670b\u53cb\u4e4b\u4e2d\uff0c\u53ea\u6709\u9648\u5929\u5b87\u6216\u8bc6\u5f97\u90a3\u7f8a\u76ae\u7eb8\u4e0a\u7684\u6587\u5b57\uff0c\u4ed6\u5931\u4e86\u8e2a\uff0c\u83b2\u513f\u7684\u8eab\u4e16\u4e4b\u8c1c\u4e5f \u6ca1\u4eba\u4e3a\u6211\u63ed\u7834\u4e86\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u8fdf\u65e9\u4f1a\u628a\u9648\u5929\u5b87\u518d\u627e\u56de\u6765\u3002\u597d\u5728\u5929\u9b54\u6559\u4ea6\u5df2\u6d88\u58f0\u533f\u8ff9\uff0c\u8c05\u4ed6\u4eec\u7ecf\u8fd9\u4e00\u5f79 \u4e4b\u540e\uff0c\u4e5f\u4e0d\u6562\u518d\u5230\u8fd9\u513f\u9a9a\u6270\u4f60\u4e86\uff0c\u4f60\u53ef\u4ee5\u5b89\u5fc3\u4f20\u6388\u83b2\u513f\u6b66\u529f\uff0c\u5979\u662f\u5929\u751f\u7684\u7ec3\u6b66\u8d44\u8d28\uff0c\u5c06\u6765\u5b9a \u53ef\u5149\u5927\u4f60\u7684\u95e8\u6237\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u4fbf\u5728\u6c13\u5c71\u4e0a\u6682\u4f4f\u4e0b\u6765\uff0c\u4ed6\u5df2\u4e0e\u59ec\u6653\u98ce\u8bf4\u597d\uff0c\u4ed6\u4eec\u4e24\u4eba\u90fd\u662f\u5b66\u517c\u6b63\u90aa\u5404\u6d3e\u4e4b\u957f\uff0c\u4e0d \u8fc7\u7a0b\u5ea6\u6df1\u6d45\u4e0d\u540c\u800c\u5df2\uff0c\u6b63\u597d\u5f7c\u6b64\u5207\u78cb\u3002\u59ec\u6653\u98ce\u672c\u6765\u8981\u62dc\u91d1\u4e16\u9057\u4e3a\u5e08\u7684\uff0c\u91d1\u4e16\u9057\u575a\u51b3\u4e0d\u5141\u3002 \u3000\u3000\u59ec\u6653\u98ce\u5077\u6765\u7684\u5404\u6d3e\u79d8\u5178\uff0c\u4e5f\u7684\u786e\u6709\u4e00\u4e9b\u662f\u91d1\u4e16\u9057\u672a\u66fe\u89c1\u8fc7\u7684\uff0c\u91d1\u4e16\u9057\u672c\u6765\u5df2\u7ecf\u878d\u4f1a\u5404 \u5bb6\uff0c\u521b\u5efa\u4e86\u4ed6\u81ea\u5df1\u7684\u6b66\u529f\uff0c\u5982\u4eca\u518d\u535a\u89c8\u5178\u7c4d\uff0c\u51b6\u4e8e\u4e00\u7089\uff0c\u4ed6\u6240\u521b\u7684\u8fd9\u95e8\u6b66\u529f\uff0c\u4fbf\u66f4\u5f62\u5b8c\u6574\uff0c \u66f4\u52a0\u6210\u719f\u3002\u5f53\u7136\u59ec\u6653\u98ce\u53d7\u60e0\u66f4\u591a\uff0c\u4e0d\u8fc7\u4ed6\u5e74\u7eaa\u5df2\u5927\uff0c\u6709\u597d\u4e9b\u4e0a\u4e58\u7684\u6b66\u529f\uff0c\u662f\u8981\u5728\u5c11\u5e74\u65f6\u5019\u6253 \u597d\u57fa\u7840\u7684\uff0c\u4ed6\u4fbf\u65e0\u6cd5\u7ec3\u4e86\u3002\u6240\u4ee5\u4ed6\u7ee7\u627f\u5b5f\u795e\u901a\u9057\u5fd7\u2014\u2014\u201c\u6b63\u90aa\u5408\u4e00\u201d\u2014\u2014\u7684\u5fc3\u613f\u867d\u7136\u5b8c\u6210\uff0c \u4f46\u7ec8\u5176\u4e00\u751f\uff0c\u5374\u4e5f\u672a\u80fd\u8fbe\u5230\u91d1\u4e16\u9057\u7684\u5883\u754c\u3002 \u3000\u3000\u8fc7\u4e86\u4e09\u4e2a\u6708\uff0c\u91d1\u3001\u59ec\u4e8c\u4eba\u5df2\u5f7c\u6b64\u4ea4\u6362\u4e86\u5e73\u751f\u6240\u5b66\uff0c\u6c5f\u5357\u4e5f\u6025\u4e8e\u56de\u5bb6\uff0c\u9080\u91d1\u4e16\u9057\u5230\u4ed6\u5bb6\u4e2d \u6559\u4ed6\u7684\u513f\u5b50\uff0c\u91d1\u4e16\u9057\u5e94\u4ed6\u4e4b\u8bf7\uff0c\u4e0e\u8c37\u4e4b\u534e\u3002\u59ec\u6653\u98ce\u518d\u5ea6\u5206\u624b\uff0c\u5206\u624b\u4e4b\u65f6\uff0c\u81ea\u6709\u4e00\u756a\u4f9d\u4f9d\u4e0d\u820d \u4e4b\u60c5\uff0c\u4e0d\u5fc5\u7ec6\u8868\u3002 \u3000\u3000\u4ece\u6b64\uff0c\u91d1\u4e16\u9057\u5c31\u5728\u6c5f\u5357\u5bb6\u4e2d\u4e13\u5fc3\u6388\u5f92\uff0c\u56e0\u4e3a\u6c5f\u6d77\u5929\u5df2\u5148\u5b66\u4e86\u90aa\u6d3e\u7684\u5185\u529f\uff0c\u4ed6\u4fbf\u56e0\u6750\u65bd \u6559\uff0c\u91c7\u64b7\u6b63\u90aa\u4e24\u6d3e\u7684\u5185\u529f\u7cbe\u534e\uff0c\u53e6\u8f9f\u8e22\u5f84\uff0c\u4f20\u6388\u4e86\u6c5f\u6d77\u5929\u4e00\u5957\u6613\u4e8e\u89c1\u6548\u3001\u975e\u6b63\u975e\u90aa\u7684\u4e0a\u4e58\u5185 \u529f\uff0c\u5f85\u4ed6\u7565\u6709\u57fa\u7840\uff0c\u518d\u4f20\u6388\u4ed6\u62f3\u7ecf\u5251\u8bc0\uff0c\u4e8e\u662f\u53ea\u4e0d\u8fc7\u4e24\u5e74\uff0c\u6c5f\u6d77\u5929\u5df2\u7ecf\u53ef\u4ee5\u548c\u4ed6\u7236\u4eb2\u6253\u4e2a\u5e73 \u624b\u3002 \u3000\u3000\u5230\u4e86\u7b2c\u4e09\u5e74\uff0c\u6709\u4e00\u5929\u665a\u4e0a\uff0c\u91d1\u4e16\u9057\u7a81\u7136\u548c\u6c5f\u5357\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u662f\u6211\u8be5\u8d70\u7684\u65f6\u5019\u4e86\uff0c\u6d77\u5929\u7684 \u57fa\u7840\u5df2\u7ecf\u6253\u597d\uff0c\u8fd9\u4e24\u5e74\u6765\uff0c\u6211\u4e5f\u5df2\u7ecf\u628a\u51c6\u5907\u6559\u4ed6\u7684\u4e1c\u897f\u90fd\u5199\u4e0b\u6765\u4e86\uff0c\u5171\u662f\u4e00\u5341\u4e09\u7bc7\uff0c\u4ed6\u53ef\u4ee5 \u6309\u90e8\u5c31\u73ed\uff0c\u81ea\u5df1\u7ec3\u4e86\u3002\u6211\u53c8\u5df1\u62dc\u6258\u4e86\u59ec\u5927\u54e5\uff0c\u8bf7\u4ed6\u6bcf\u5e74\u81f3\u5c11\u5230\u4f60\u5bb6\u4e00\u6b21\uff0c\u6d77\u5929\u82e5\u6709\u4e0d\u61c2\u7684\u5730 \u65b9\uff0c\u53ef\u4ee5\u5411\u4ed6\u8bf7\u6559\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u4f60\u53ef\u662f\u53bb\u5bfb\u8bbf\u5929\u5b87\u592b\u59bb\u7684\u4e0b\u843d\u4e48\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u8fd9\u662f\u6211\u79bb\u5f00\u7684\u539f\u56e0\u4e4b\u4e00\uff0c \u53e6\u5916\u4e5f\u8fd8\u6709\u51e0\u4ef6\u4e8b\u60c5\u8981\u529e\u3002\u6211\u5728\u8fd9\u91cc\u4e24\u5e74\uff0c\u5916\u95f4\u53c8\u4e0d\u77e5\u53d1\u751f\u4e86\u4e9b\u4ec0\u4e48\u53d8\u5316\u4e86\uff1f\u201d\u8bf4\u7f62\uff0c\u6df1\u6df1 \u7684\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u6c5f\u5357\u4e0d\u5927\u61c2\u5f97\u91d1\u4e16\u9057\u7684\u611f\u89e6\u4f55\u6765\uff0c\u4f46\u542c\u8bf4\u4ed6\u8981\u53bb\u5bfb\u8bbf\u9648\u5929\u5b87\u592b\u59bb\uff0c\u5fc3\u4e2d\u5374\u662f\u751a\u4e3a\u6b22\u559c\uff0c \u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u65e9\u5c31\u8be5\u53bb\u627e\u4ed6\u4eec\u4e86\u3002\u4e3a\u4e86\u6211\u7684\u5b69\u5b50\uff0c\u5df2\u7ecf\u803d\u6401\u4f60\u4e24\u5e74\u4e86\u3002\u4f46\u613f\u4f60\u80fd\u591f\u65e9\u65e5\u548c\u4ed6 \u4eec\u4e00\u540c\u56de\u6765\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60\u4e0d\u53ef\u628a\u4e8b\u60c5\u770b\u5f97\u592a\u6613\uff0c\u6211\u8fd9\u4e00\u53bb\uff0c\u8fd8\u8bf4\u4e0d\u5b9a\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u56de\u6765\u5462\uff01\u201d\u6c5f \u5357\u7b11\u9053\uff1a\u201c\u4eca\u5e74\u7b49\u4e0d\u5230\u660e\u5e74\u518d\u7b49\uff0c\u524d\u51e0\u5929\u7edb\u971e\u624d\u917f\u4e86\u4e00\u74ee\u6842\u82b1\u9152\uff0c\u6b63\u597d\u7b49\u5f85\u4f60\u56de\u6765\u540c\u559d\u3002\u201d \u4ed6\u5bf9\u91d1\u4e16\u9057\u4fe1\u5fc3\u5341\u8db3\uff0c\u6240\u4ee5\u4f30\u8ba1\u6700\u591a\u4e5f\u8981\u4e0d\u4e86\u4e24\u5e74\u3002\u91d1\u4e16\u9057\u89c1\u4ed6\u5982\u6b64\uff0c\u4e0d\u5fcd\u518d\u8bf4\u626b\u5174\u7684\u8bdd\uff0c \u5fc3\u91cc\u5374\u53c8\u6697\u6697\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u6c5f\u5357\u95ee\u9053\uff1a\u201c\u4f60\u662f\u73b0\u5728\u5c31\u8981\u8d70\u4e86\u4e48\uff1f\u4e3a\u4f55\u4e0d\u7b49\u5230\u5929\u4eae\uff0c\u4e5f\u597d\u53eb\u6d77\u5929\u7ed9\u4f60\u9001\u884c\u3002\u201d\u91d1\u4e16\u9057 \u7b11\u9053\uff1a\u201c\u6211\u5c31\u662f\u4e0d\u60f3\u7ed9\u4ed6\u77e5\u9053\uff0c\u6015\u4ed6\u4e0d\u80af\u653e\u6211\u8d70\u5462\u3002\u201d\u539f\u6765\u8fd9\u5b69\u5b50\u5bf9\u5e08\u7236\u751a\u4e3a\u4f9d\u604b\uff0c\u8fd9\u4e24\u5e74 \u6765\uff0c\u51e0\u4e4e\u662f\u4e00\u523b\u4e5f\u672a\u66fe\u79bb\u5f00\u8fc7\u91d1\u4e16\u9057\u3002 \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u9664\u4e86\u6211\u624b\u5199\u7684\u8fd9\u5341\u4e09\u7bc7\u7ec3\u529f\u53e3\u8bc0\u5916\uff0c\u8fd8\u6709\u4e24\u6837\u4e1c\u897f\u8981\u7559\u7ed9\u6d77\u5929\u3002\u201d\u8bf4\u7f62\uff0c \u4fbf\u89e3\u4e0b\u4ed6\u6240\u4f69\u7684\u90a3\u628a\u88c1\u4e91\u5b9d\u5251\uff0c\u8ddf\u7740\u518d\u53d6\u51fa\u4e86\u767d\u7389\u7532\u90fd\u4ea4\u7ed9\u6c5f\u5357\u3002 \u3000\u3000\u6c5f\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u4e00\u4e2a\u5b69\u5b50\uff0c\u600e\u6562\u53d7\u8fd9\u4e24\u4ef6\u7a00\u4e16\u4e4b\u5b9d\uff1f\u201d\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u8fd9\u662f \u4e54\u5317\u6e9f\u7559\u4e0b\u7684\u4e09\u5b9d\u4e4b\u4e8c\uff0c\u5f53\u521d\u6211\u672c\u6765\u5c31\u4e0d\u60f3\u8981\u4ed6\u7684\u4e1c\u897f\uff0c\u53ea\u56e0\u673a\u7f18\u51d1\u5408\u843d\u5728\u6211\u7684\u624b\u4e2d\u7f62\u4e86\u3002 \u800c\u4e14\u73b0\u5728\u6211\u4ea6\u5df2\u65e0\u987b\u518d\u7528\u5b9d\u5251\uff0c\u6211\u4e0d\u7ed9\u5f92\u5f1f\u8fd8\u7ed9\u8c01\uff1f\u4e0d\u8fc7\uff0c\u4e5f\u5e76\u4e0d\u662f\u5168\u7ed9\u4ed6\uff0c\u8fd9\u4ef6\u7389\u7532\uff0c\u5374 \u662f\u8981\u8bf7\u4ed6\u9001\u7ed9\u53e6\u4e00\u4e2a\u4eba\u7684\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u4ed6\u5f97\u4e00\u4ef6\u5df2\u662f\u975e\u4efd\u4e86\u3002\u201d\u8ddf\u7740\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u7389\u7532\u8fd8\u8981\u9001\u7ed9\u8c01\u4eba\uff1f\u201d\u91d1\u4e16\u9057 \u9053\uff1a\u201c\u5f85\u4ed6\u957f\u5927\u6210\u4eba\u4e4b\u540e\uff0c\u4f60\u53eb\u4ed6\u9001\u5230\u6c13\u5c71\u53bb\uff0c\u7ed9\u8c37\u4e4b\u534e\u7684\u5f92\u5f1f\u8c37\u4e2d\u83b2\uff0c\u8981\u4eb2\u624b\u4ea4\u7ed9\u5979\u3002\u201d \u6c5f\u5357\u8be7\u9053\uff1a\u201c\u4f60\u65e2\u6709\u5fc3\u9001\u7ed9\u5979\uff0c\u4e3a\u4f55\u5f53\u521d\u4f60\u5728\u6c13\u5c71\u7684\u65f6\u5019\uff0c\u4e0d\u62ff\u51fa\u6765\u4f5c\u89c1\u9762\u793c\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u8fd9\u793c\u7269\u7531\u4f60\u7684\u513f\u5b50\u4eb2\u624b\u9001\u51fa\uff0c\u8fd9\u624d\u66f4\u5b9d\u8d35\u5440\u3002\u800c\u4e14\u4e0d\u80fd\u592a\u65e9\u9001\u53bb\uff0c\u8981\u5f85 \u4ed6\u6210\u5e74\u4e4b\u540e\u518d\u9001\uff0c\u4f60\u61c2\u4e86\u4e48\uff1f\u201d\u6c5f\u5357\u4e00\u60f3\uff0c\u604d\u7136\u5927\u609f\uff0c\u5927\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd9\u4e2a\u5e08\u7236\u8fd8\u60f3\u517c\u505a \u5a92\u4eba\uff0c\u53ea\u4e0d\u77e5\u6211\u7684\u5b69\u5b50\u6709\u6ca1\u6709\u8fd9\u4e2a\u9020\u5316\u4e86\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u4e00\u4e00\u4ea4\u4ee3\u6e05\u695a\uff0c\u4fbf\u5373\u98d8\u7136\u79bb\u53bb\u3002\u7b2c\u4e8c\u5929\u6d77\u5929\u77e5\u9053\u4e86\uff0c\u679c\u7136\u5927\u54ed\u4e00\u573a\u3002 \u3000\u3000\u6c5f\u5357\u4ee5\u4e3a\u91d1\u4e16\u9057\u5f88\u5feb\u5c31\u4f1a\u56de\u6765\uff0c\u54ea\u77e5\u4e00\u76f4\u7b49\u4e86\u4e09\u5e74\uff0c\u8fd8\u672a\u89c1\u91d1\u4e16\u9057\u7684\u8e2a\u5f71\uff0c\u8fde\u4fe1\u606f\u4e5f\u4e1d \u6beb\u6ca1\u6709\u3002 \u3000\u3000\u8fd9\u4e09\u5e74\u4e2d\uff0c\u6c5f\u6d77\u5929\u867d\u7136\u79bb\u5f00\u4e86\u5e08\u7236\uff0c\u7ec3\u529f\u5374\u662f\u6beb\u4e0d\u61c8\u6020\uff0c\u59ec\u6653\u98ce\u4e5f\u5e38\u5e38\u5230\u4ed6\u5bb6\u6765\uff0c\u6c5f\u6d77 \u5929\u9047\u6709\u4e0d\u660e\u767d\u7684\u5730\u65b9\uff0c\u5c31\u5411\u4ed6\u8bf7\u6559\u3002\u91d1\u4e16\u9057\u7559\u4e0b\u7684\u90a3\u5341\u4e09\u7bc7\u7ec3\u529f\u79d8\u8bc0\uff0c\u662f\u4e13\u4e3a\u6c5f\u6d77\u5929\u5199\u7684\uff0c \u7531\u4e8e\u6c5f\u6d77\u5929\u6240\u7ec3\u7684\u5185\u529f\u7279\u5f02\uff0c\u4ee5\u540e\u6309\u90e8\u5c31\u73ed\u6240\u7ec3\u7684\u529f\u592b\uff0c\u4e5f\u90fd\u662f\u9002\u5e94\u4ed6\u539f\u6709\u7684\u57fa\u7840\u7684\u3002\u59ec\u6653 \u98ce\u61c2\u5f97\u5176\u4e2d\u9053\u7406\uff0c\u53ef\u4ee5\u7ed9\u6c5f\u6d77\u5929\u6307\u5bfc\uff0c\u90a3\u4e9b\u529f\u592b\uff0c\u5373\u7b97\u662f\u4ed6\uff0c\u4e5f\u7ec3\u4e0d\u6765\u3002\u91d1\u4e16\u9057\u8d70\u540e\u7684\u7b2c\u4e09 \u5e74\uff0c\u4ed6\u7684\u7236\u4eb2\u4e5f\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\u4e86\u3002 \u3000\u3000\u6c5f\u5357\u89c1\u513f\u5b50\u7684\u6b66\u529f\u65e5\u8ff8\uff0c\u5fc3\u91cc\u5f53\u7136\u6b22\u559c\uff0c\u53ef\u662f\uff0c\u4e00\u76f4\u4e0d\u89c1\u91d1\u4e16\u9057\u56de\u6765\uff0c\u8fd9\u6b22\u559c\u5374\u906e\u76d6\u4e0d \u4e86\u4ed6\u7684\u5fe7\u8651\u3002\u5230\u4e86\u4e00\u5929\uff0c\u4ed6\u770b\u513f\u5b50\u7ec3\u4e86\u4e00\u5957\u5251\u672f\u4e4b\u540e\uff0c\u4fbf\u5bf9\u59bb\u5b50\u90b9\u7edb\u971e\u8bf4\u9053\uff1a\u201c\u9648\u5bb6\u5bf9\u6211\u6069 \u6df1\u4e49\u539a\uff0c\u91d1\u5927\u4fa0\u81f3\u4eca\u5c1a\u672a\u627e\u56de\u6211\u7684\u4e49\u5144\uff0c\u6211\u60f3\u4eb2\u81ea\u53bb\u5bfb\u8bbf\u4ed6\u4e86\u3002\u597d\u5728\u6d77\u513f\u73b0\u5728\u5df2\u7ecf\u6bd4\u6211\u8fd8 \u5f3a\uff0c\u4e5f\u53ef\u4ee5\u652f\u6491\u95e8\u6237\u4e86\u3002\u8981\u662f\u518d\u6709\u4ec0\u4e48\u5929\u9b54\u6559\u4e4b\u7c7b\u7684\u4eba\u7269\u6765\u95f9\u4e8b\uff0c\u6709\u4ed6\u5e2e\u52a9\u4f60\u4eec\u6bcd\u5973\uff0c\u60f3\u6765 \u4ea6\u53ef\u4ee5\u5bf9\u4ed8\u5f97\u4e86\u3002\u201d\u90b9\u7edb\u971e\u89c1\u4e08\u592b\u5fc3\u610f\u5df2\u51b3\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u6700\u91cd\u9053\u4e49\uff0c\u81ea\u5df1\u4e0d\u4fbf\u963b\u62e6\u3002 \u3000\u3000\u54ea\u77e5\u6c5f\u5357\u4e00\u53bb\uff0c\u53c8\u662f\u97f3\u65e0\u97f3\u8baf\uff0c\u5306\u5306\u53c8\u8fc7\u4e86\u4e09\u5e74\uff0c\u6c5f\u6d77\u5929\u5df2\u7ecf\u5341\u516d\u5c81\uff0c\u4ed6\u56e0\u4e3a\u81ea\u5c0f\u4fbf\u65e5 \u5915\u7ec3\u6b66\uff0c\u4f53\u9b44\u58ee\u5065\uff0c\u8eab\u6750\u9ad8\u5927\uff0c\u770b\u4e0a\u53bb\u7adf\u4f3c\u5927\u4eba\u4e00\u822c\u3002\u4ed6\u81ea\u4ece\u62dc\u91d1\u4e16\u9057\u4e3a\u5e08\u4e4b\u540e\uff0c\u4ea6\u5df2\u7ec3\u6ee1 \u4e86\u516b\u5e74\uff0c\u90a3\u5341\u4e09\u7bc7\u5947\u95e8\u6b66\u529f\uff0c\u65e9\u5df2\u7ec3\u5f97\u6eda\u74dc\u70c2\u719f\uff0c\u5c24\u5176\u5728\u5185\u529f\u65b9\u9762\u3002\u7531\u4e8e\u91d1\u4e16\u9057\u662f\u7528\u901f\u6210\u7684 \u65b9\u6cd5\u6559\u4ed6\uff0c\u4ed6\u516b\u5e74\u7684\u65f6\u95f4\uff0c\u62b5\u5f97\u522b\u4eba\u4e09\u5341\u5e74\u7684\u529f\u529b\uff0c\u8fde\u59ec\u6653\u98ce\u4e0e\u4ed6\u6bd4\u8bd5\uff0c\u4e5f\u5f80\u5f80\u611f\u5230\u5e94\u4ed8\u8270 \u96be\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5b66\u6210\u4e4b\u540e\uff0c\u8d77\u4e86\u51fa\u95e8\u5bfb\u5e08\u3001\u89c5\u7236\u4e4b\u5ff5\uff0c\u548c\u5916\u5a46\u4e0e\u6bcd\u4eb2\u4ece\u65c1\u5546\u8bae\uff0c\u90b9\u7edb\u971e\u9053\uff1a\u201c\u4f60 \u7236\u4eb2\u4e45\u65e0\u97f3\u8baf\uff0c\u6211\u4e5f\u6302\u5ff5\u5f97\u5f88\u3002\u4ee5\u4f60\u7684\u6b66\u529f\u800c\u8bba\uff0c\u8d70\u5357\u95ef\u5317\uff0c\u6211\u90fd\u53ef\u4ee5\u653e\u5fc3\u4e86\u3002\u5c31\u53ea\u6015\u4f60\u6beb \u65e0\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u96be\u514d\u5403\u4e8f\uff0c\u4f60\u53ef\u5f97\u6bcf\u4e8b\u5c0f\u5fc3\u624d\u597d\u3002\u201d \u3000\u3000\u6768\u67f3\u9752\u5012\u6bd4\u5973\u513f\u8c6a\u8fc8\u5f97\u591a\uff0c\u7b11\u9053\uff1a\u201c\u6c5f\u6e56\u7ecf\u9a8c\u662f\u5386\u7ec3\u51fa\u6765\u7684\uff0c\u5c11\u5e74\u4eba\u5403\u70b9\u4e8f\u4e5f\u7b97\u4e0d\u4e86\u4ec0 \u4e48\u3002\u4f60\u5916\u516c\u5f53\u5e74\u9886\u8896\u6b66\u6797\uff0c\u5a01\u540d\u8fdc\u64ad\u3002\u4f60\u7684\u513f\u5b50\u4e5f\u7b97\u662f\u6768\u5bb6\u4e00\u8109\uff0c\u6b63\u5b9c\u53eb\u4ed6\u53bb\u626c\u540d\u7acb\u4e07\uff0c\u91cd \u632f\u5bb6\u98ce\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u6210\u540d\uff0c\u53ea\u60f3\u627e\u5f97\u7740\u7239\u7239\u548c\u5e08\u7236\uff0c\u518d\u7ec3\u4e00\u70b9\u529f\u592b\u3002\u53ea\u662f\u4eba\u6d77\u832b\u832b\uff0c\u5374 \u4e0d\u77e5\u8981\u5411\u4f55\u65b9\u5bfb\u89c5\u3002\u8bf7\u5916\u5a46\u6307\u70b9\u3002\u201d \u3000\u3000\u6768\u67f3\u9752\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u9648\u5929\u5b87\u4e0e\u5510\u7ecf\u5929\u76f8\u4ea4\u751a\u539a\uff0c\u4f60\u7236\u4eb2\u4e00\u5b9a\u5230\u8fc7\u4ed6\u90a3\u513f\u6253\u542c\u3002\u4f60 \u6b64\u884c\u53ef\u4ee5\u5148\u5f80\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\uff0c\u5411\u5510\u7ecf\u5929\u95ee\u95ee\u6d88\u606f\u3002\u8981\u662f\u4ecd\u65e0\u8baf\u606f\uff0c\u4f60\u53ef\u4ee5\u518d\u5230\u5929\u5c71\u53bb\u8c12\u89c1\u5510 \u7ecf\u5929\u7684\u7236\u4eb2\uff0c\u5f53\u4eca\u6b66\u6797\u4e2d\u5750\u7b2c\u4e00\u628a\u4ea4\u6905\u7684\u5510\u6653\u6f9c\uff0c\u4ed6\u77e5\u9053\u4f60\u662f\u6211\u7684\u5916\u5b59\uff0c\u4e00\u5b9a\u4f1a\u5bf9\u4f60\u53e6\u773c\u76f8 \u770b\uff0c\u5e2e\u5fd9\u4f60\u7684\u3002\u201d \u3000\u3000\u8ba1\u8bae\u5df2\u5b9a\uff0c\u6c5f\u6d77\u5929\u5e26\u4e86\u5b9d\u5251\u5b9d\u7532\uff0c\u4fbf\u5373\u52a8\u8eab\u3002 \u3000\u3000\u6b63\u662f\u66ae\u6625\u4e09\u6708\u7684\u65f6\u8282\uff0c\u6c13\u5c71\u4e0a\u6765\u4e86\u4e00\u4e2a\u5c11\u5e74\u3002\u6c13\u5c71\u6625\u65e5\uff0c\u98ce\u7269\u7edd\u4f73\u3001\u5c71\u82b1\u904d\u5730\uff0c\u7ea2\u91cc\u63ba \u767d\u7684\u8336\u82b1\u50cf\u662f\u5927\u7ea2\u739b\u7459\uff1b\u7f28\u7edc\u62ab\u5782\u7684\u675c\u9e43\u82b1\u50cf\u662f\u5410\u51fa\u91d1\u4e1d\u82b1\u854a\uff1b\u8fd8\u6709\u9752\u4e1d\u82b1\u854a\u9576\u7740\u4e73\u767d\u82b1\u74e3 \u7684\u62a5\u6625\u82b1\uff0c\u767e\u6001\u5343\u59ff\uff0c\u4e89\u598d\u6597\u4e3d\uff0c\u5bc6\u5bc6\u4e1b\u4e1b\uff0c\u6ee1\u773c\u90fd\u662f\u3002\u4f46\u8fd9\u5c11\u5e74\u5374\u4f3c\u65e0\u6687\u89c2\u8d4f\u5c71\u82b1\uff0c\u4ed6\u884c \u8272\u5306\u5306\uff0c\u4e0d\u65f6\u62e8\u5f00\u906e\u8def\u7684\u91ce\u82b1\uff0c\u9732\u51fa\u6625\u82b1\u822c\u7684\u5fae\u7b11\uff0c\u539f\u6765\u4ed6\u5e76\u975e\u4e0d\u7231\u6625\u82b1\uff0c\u800c\u662f\u4ed6\u6b63\u5728\u9050 \u601d\uff0c\u5728\u6ee1\u773c\u7684\u7e41\u82b1\u4e4b\u4e2d\uff0c\u5e7b\u51fa\u4e86\u4e00\u4f4d\u5982\u82b1\u5c11\u5973\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u6b63\u662f\u6c5f\u5357\u7684\u513f\u5b50\u6c5f\u6d77\u5929\uff0c\u4ed6\u6240\u60f3\u5ff5\u7684\u90a3\u4f4d\u5c11\u5973\u4fbf\u662f\u8c37\u4e4b\u534e\u7684\u517b\u5973\u8c37\u4e2d\u83b2\u3002\u4ed6\u662f\u7ed9 \u8c37\u4e2d\u83b2\u9001\u5b9d\u7532\u6765\u7684\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u66fe\u5728\u6c13\u5c71\u4e0a\u4f4f\u8fc7\u4e09\u4e2a\u6708\uff0c\u90a3\u65f6\u4ed6\u53ea\u6709\u516b\u5c81\uff0c\u73b0\u5728\u8fc7\u4e86\u516b\u5e74\uff0c\u4ed6\u5df2\u7ecf\u662f\u5341\u516d\u5c81\u7684\u5c11 \u5e74\u4e86\u3002\u5728\u90a3\u4e09\u4e2a\u6708\u4e2d\uff0c\u4ed6\u51e0\u4e4e\u6bcf\u5929\u90fd\u4e0e\u8c37\u4e2d\u83b2\u4e00\u8d77\u73a9\u800d\uff0c\u201c\u73b0\u5728\u5979\u4e5f\u957f\u5927\u4e86\u5427\uff1f\u53ef\u4e0d\u77e5\u8fd8\u50cf \u4e0d\u50cf\u4ee5\u524d\u90a3\u6837\u7231\u95f9\uff1f\u6211\u5012\u60f3\u7ea6\u5979\u518d\u8f83\u91cf\u4e00\u756a\uff0c\u770b\u5979\u8fd8\u80fd\u4e0d\u80fd\u628a\u6211\u518d\u6454\u4e00\u4e2a\u8001\u8fdc\u7684\u7b4b\u6597\uff1f\u201d \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u968f\u7236\u4eb2\u5728\u6c13\u5c71\u4f5c\u5ba2\u4e4b\u65f6\uff0c\u4e5f\u6b63\u662f\u4ed6\u521a\u88ab\u59ec\u6653\u98ce\u4ece\u7ec4\u6765\u5c71\u6551\u51fa\u6765\u4e4b\u540e\uff0c\u90a3\u65f6\u4ed6 \u6b63\u5f00\u59cb\u8ddf\u91d1\u4e16\u9057\u4fee\u4e60\u5185\u529f\uff0c\u6b66\u827a\u8fdc\u4e0d\u5982\u8c37\u4e2d\u83b2\uff0c\u5e38\u5e38\u7ed9\u5979\u6b3a\u8d1f\u7684\uff0c\u4ed6\u53c8\u60f3\u8d77\u7236\u4eb2\u5149\u7740\u5c41\u80a1\u7ed9 \u8c37\u4e2d\u83b2\u53d6\u7b11\u7684\u4e8b\uff0c\u90a3\u662f\u6c5f\u5357\u5f53\u4f5c\u7b11\u8bdd\u7ed9\u4ed6\u8bb2\u7684\u3002\u4ed6\u60f3\u8d77\u4e86\u8fd9\u4e9b\u6709\u8da3\u7684\u4e8b\u60c5\uff0c\u8d8a\u60f3\u8d8a\u89c9\u597d\u7b11\uff0c \u6068\u4e0d\u5f97\u65e9\u4e9b\u548c\u5979\u89c1\u9762\uff0c\u4e0e\u5979\u4ea4\u8c08\u5f80\u4e8b\u3002\u4ed6\u81ea\u7ec3\u6b66\uff0c\u8fd9\u516b\u5e74\u6765\u66f4\u662f\u8db3\u8ff9\u4e0d\u51fa\u5927\u95e8\uff0c\u7ae5\u5e74\u7684\u670b\u53cb \u53ea\u6709\u4e00\u4e2a\u8c37\u4e2d\u83b2\uff0c\u6240\u4ee5\u4e00\u5230\u6c13\u5c71\uff0c\u4fbf\u6ee1\u6000\u6b22\u60a6\u3002 \u3000\u3000\u4ed6\u4e09\u6b65\u5e76\u4f5c\u4e24\u6b65\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u5230\u4e86\u7384\u5973\u89c2\u524d\u3002\u5ffd\u542c\u5f97\u4e00\u9635\u9635\u566a\u8033\u7684\u5435\u58f0\uff0c\u62ac\u5934\u4e00\u770b\u3002\u53ea \u89c1\u7384\u5973\u89c2\u524d\u3002\u6709\u4e00\u4e2a\u88c5\u675f\u602a\u5f02\u7684\u6c49\u5b50\uff0c\u62ab\u7740\u53cd\u5e95\u7684\u8001\u7f8a\u76ae\u8884\uff0c\u6234\u7740\u906e\u8fc7\u8033\u6735\u7684\u98ce\u5e3d\uff0c\u5df2\u7ecf\u662f \u6696\u548c\u7684\u66ae\u6625\u5929\u6c14\u4e86\uff0c\u4ed6\u8fd8\u7a7f\u7740\u585e\u5916\u730e\u4eba\u7684\u51ac\u88c5\uff01\u4e24\u8fb9\u8033\u6735\u8fd8\u540a\u7740\u4e00\u4e32\u8033\u73af\uff0c\u4e00\u770b\u8fd9\u4e2a\u88c5\u675f\uff0c \u5c31\u77e5\u4ed6\u4e0d\u662f\u6c49\u4eba\u3002\u8fd9\u4e2a\u4eba\u6b63\u5728\u6307\u624b\u5212\u811a\u7684\u53eb\u56b7\u3002\u5728\u4ed6\u7684\u5bf9\u9762\u6709\u4e00\u5927\u5806\u4eba\u5835\u7740\u89c2\u95e8\uff0c\u770b\u6837\u5b50\u4f3c \u662f\u4e0d\u8bb8\u4ed6\u8fdb\u53bb\u3002\u5728\u8fd9\u5806\u4eba\u4e2d\uff0c\u4ed6\u8ba4\u5f97\u7a0b\u6d69\u3001\u767d\u82f1\u6770\u3001\u8def\u82f1\u8c6a\u3001\u7518\u4eba\u9f99\u8fd9\u51e0\u4f4d\u6c13\u5c71\u6d3e\u7684\u5927\u5f1f\u5b50\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8d70\u8fd1\u51e0\u6b65\uff0c\u542c\u5f97\u90a3\u4eba\u56b7\u9053\uff1a\u201c\u6211\u8fdc\u9053\u800c\u6765\uff0c\u4f60\u4eec\u7684\u8c37\u638c\u95e8\u4e3a\u4f55\u907f\u4e0d\u89c1\u6211\uff1f\u201d\u7a0b\u6d69 \u9053\uff1a\u201c\u6211\u4e0d\u662f\u65e9\u4e0e\u5c0a\u9a7e\u8bf4\u4e86\u5417\uff1f\u6211\u4eec\u7684\u8c37\u638c\u95e8\u51fa\u8fdc\u95e8\u53bb\u4e86\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\uff0c\u54ea\u6709\u8fd9 \u6837\u5de7\u7684\u4e8b\uff1f\u2026\u7a0b\u6d69\u9053\uff1a\u201c\u2018\u6211\u4eec\u4f55\u5fc5\u9a97\u4f60\uff0c\u4f60\u6709\u4ec0\u4e48\u4e8b\u60c5\u3002\u53ef\u4ee5\u4ea4\u4ee3\u4e0b\u6765\u5417\uff1f\u5f85\u638c\u95e8\u56de\u6765\uff0c \u6211\u7ed9\u4f60\u7980\u62a5\u3002\u201d\u90a3\u4eba\u969c\u7740\u773c\u775b\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u8fd9\u4ef6\u4e8b\u53ea\u6015\u4f60\u63a5\u4e0d\u4e0b\u6765\uff01\u201d \u3000\u3000\u7a0b\u6d69\u5fcd\u7740\u6c14\u7b54\u9053\uff1a\u201c\u638c\u95e8\u4e0d\u5728\uff0c\u7531\u6211\u6682\u4ee3\u3002\u201d\u6c13\u5c71\u6d3e\u7684\u4e8b\u60c5\uff0c\u5927\u5c0f\u90fd\u7531\u6211\u4f5c\u4e3b\u3002\u201d\u90a3\u4eba \u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u662f\u6c13\u5c71\u6d3e\u7684\u4ee3\u638c\u95e8\u3002\u4f46\u6211\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u867d\u4e0d\u80fd\u8bf4\u4e0e\u4f60\u6c13\u5c71\u6d3e\u5168\u65e0\u5173 \u7cfb\uff0c\u5374\u5e76\u4e0d\u662f\u51b2\u7740\u4f60\u6765\u7684\uff0c\u6211\u6240\u8981\u89c1\u7684\u53ea\u662f\u8c37\u4e4b\u534e\u4e00\u4eba\u3002\u201d\u7a0b\u6d69\u9053\uff1a\u201c\u65e2\u4e0e\u672c\u6d3e\u6709\u5173\uff0c\u6562\u8bf7 \u7ec6\u9053\u5176\u8be6\u3002\u8c37\u638c\u95e8\u5b9e\u662f\u4e0d\u5728\uff0c\u600e\u80fd\u89c1\u4f60\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u65e2\u7136\u8981\u63a5\uff0c\u90a3\u4fbf\u63a5\u5427\u3002\u6211\u5bb6\u4e3b\u4eba\u7ea6\u8c37\u4e4b\u534e\u5e08\u5f92\uff0c\u5728\u4eca\u5e74\u4f60\u4eec \u6c49\u4eba\u7684\u4e2d\u79cb\u8282\u65e5\uff0c\u5230\u963f\u5c14\u6cf0\u5c71\u811a\u4e0b\uff0c\u9a6c\u8428\u513f\u76df\u7684\u91d1\u9e70\u5bab\u8d74\u5bb4\uff0c\u5b9a\u8981\u51c6\u65f6\u8d74\u5bb4\uff0c\u5426\u5219\u5fc5\u6709\u707e \u6b83\uff01\u201d \u3000\u3000\u6b64\u8bed\u4e00\u51fa\uff0c\u6c13\u5c71\u6d3e\u5f1f\u5b50\u5747\u662f\u60ca\u5f02\u4e0d\u5df2\u3002\u8981\u77e5\u963f\u5c14\u6cf0\u5c71\u8fdc\u5728\u8fb9\u9672\u56fd\u5883\uff0c\u4e0e\u6c13\u5c71\u76f8\u53bb\u4f55\u6b62\u5343 \u91cc\uff0c\u867d\u8bf4\u8ddd\u79bb\u4e2d\u79cb\u8282\u8fd8\u6709\u4e94\u4e2a\u591a\u6708\u7684\u65f6\u95f4\uff0c\u4f46\u600e\u6562\u62c5\u4fdd\u8def\u4e0a\u6ca1\u6709\u803d\u6401\uff0c\u5b9a\u80fd\u8d76\u5230\uff1f\u518d\u8005\u201c\u9a6c \u8428\u513f\u76df\u201d\u8fd9\u4e2a\u5730\u540d\u4ed6\u4eec\u6839\u672c\u4e0d\u77e5\uff0c\u4ec0\u4e48\u201c\u91d1\u9e70\u5bab\u201d\u7684\u4e3b\u4eba\u4ed6\u4eec\u4e5f\u4ece\u6765\u6ca1\u542c\u8c37\u4e4b\u534e\u8bf4\u8fc7\uff0c\u600e\u6562 \u8d38\u7136\u66ff\u5979\u7b54\u5e94\uff1f\u8fd8\u6709\uff0c\u6700\u4ee4\u6c13\u5c71\u6d3e\u5f1f\u597d\u7740\u607c\u7684\uff0c\u662f\u90a3\u4eba\u7684\u53e3\u6c14\u72c2\u5984\u4e4b\u6781\uff0c\u7b80\u76f4\u4e0d\u628a\u6c13\u5c71\u6d3e\u653e \u5728\u773c\u4e0b\u3002 \u3000\u3000\u7acb\u5373\u4fbf\u6709\u51e0\u4e2a\u813e\u6c14\u7c97\u66b4\u7684\u5f1f\u5b50\u559d\u9a82\u8d77\u6765\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u8bf7\u5ba2\u662f\u8fd9\u6837\u7684\u5417\uff1f\u201d\u201c\u6c13\u5c71\u6d3e\u5c82 \u662f\u53d7\u4eba\u6050\u5413\u7684\uff1f\u54fc\uff0c\u54fc\uff0c\u8fd9\u4eba\u4e0d\u61c2\u793c\u8c8c\uff0c\u54b1\u4eec\u53c8\u4f55\u5fc5\u4e0e\u4ed6\u5ba2\u6c14\uff1f\u201d\u90a3\u4eba\u53cc\u76ee\u73af\u626b\uff0c\u51b7\u7b11\u8bf4 \u9053\uff1a\u201c\u600e\u4e48\uff0c\u6211\u53ea\u662f\u66ff\u4e3b\u4eba\u4f20\u8bdd\uff0c\u4f60\u4eec\u5374\u8981\u548c\u6211\u52a8\u624b\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u6d69\u5728\u6c13\u5c71\u6d3e\u4e2d\u4f4d\u5217\u7b2c\u4e09\uff0c\u66f9\u9526\u513f\u5df2\u6b7b\uff0c\u7ffc\u4ef2\u725f\u4e0d\u5728\uff0c\u4ed6\u4fbf\u662f\u4f17\u4eba\u7684\u5927\u5e08\u5144\uff0c\u6240\u4ee5\u8c37\u4e4b \u534e\u51fa\u95e8\uff0c\u4fbf\u7531\u4ed6\u4ee3\u7406\u638c\u95e8\u3002\u4ed6\u4e3a\u4eba\u8001\u6210\u6301\u91cd\uff0c\u8fde\u5fd9\u5c06\u4f17\u5e08\u5f1f\u6b62\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u4e14\u522b\u52a8\u6012\uff0c\u5f85\u6211 \u95ee\u4ed6\u3002\u201d \u3000\u3000\u5f53\u4e0b\uff0c\u4fbf\u5411\u90a3\u4eba\u95ee\u9053\uff1a\u201c\u8bf7\u5ba2\u4e5f\u5f97\u77e5\u9053\u4e3b\u4eba\u662f\u8c01\uff1f\u8bf7\u95ee\u4f60\u5bb6\u4e3b\u4eba\u9ad8\u59d3\u5927\u540d\uff0c\u4f55\u4e8b\u8bf7\u5ba2\uff0c \u82e5\u7136\u4e0d\u5230\uff0c\u53c8\u6709\u4ec0\u4e48\u707e\u6b83\uff1f\u201d \u3000\u3000\u90a3\u4eba\u7ffb\u8d77\u4e86\u4e00\u53cc\u767d\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u53ea\u8981\u8c37\u4e4b\u534e\u5230\u4e86\u9a6c\u8428\u513f\u76df\u6765\uff0c\u63d0\u8d77\u91d1\u9e70\u5bab\u7684\u4e3b\u4eba\uff0c\u4e09\u5c81 \u7684\u5b69\u7ae5\u4e5f\u4f1a\u77e5\u9053\u3002\u65e0\u987b\u73b0\u5728\u5c31\u95ee\u3002\u81f3\u4e8e\u6709\u4f55\u707e\u6b83\uff0c\u90a3\u4e5f\u53ea\u6709\u6211\u7684\u4e3b\u4eba\u624d\u80fd\u5b9a\u593a\u3002\u8bf4\u4e0d\u5b9a\u53ea\u662f \u8c37\u4e4b\u534e\u4e00\u4eba\u627f\u62c5\uff0c\u4e5f\u8bf4\u4e0d\u5b9a\u8981\u8fde\u7d2f\u4f60\u4eec\u6c13\u5c71\u6d3e\u3002\u4f60\u82e5\u7136\u4ee3\u63a5\u4f60\u5f53\u7136\u4e5f\u9003\u4e0d\u4e86\u5173\u7cfb\u3002\u8bdd\u5c3d\u4e8e \u6b64\uff0c\u8bf7\u5e16\u5c31\u5728\u8fd9\u513f\uff0c\u4f60\u63a5\u8fd8\u662f\u4e0d\u63a5\uff01\u201d \u3000\u3000\u7a0b\u6d69\u813e\u6c14\u518d\u597d\uff0c\u8fd9\u65f6\u4e5f\u7981\u4e0d\u4f4f\u52a8\u4e86\u6012\u706b\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u8c37\u638c\u95e8\u4e0d\u5728\u5bb6\uff0c\u5728\u5bb6\u4e5f\u4e0d\u4f1a\u7a00\u7f55 \u4f60\u8fd9\u5f20\u8bf7\u5e16\uff0c\u4f60\u5e26\u56de\u53bb\uff0c\u6709\u4f55\u707e\u6b83\uff0c\u4fc4\u4eec\u6c13\u5c71\u6d3e\u7b49\u5b83\u964d\u4e34\uff01\u201d \u3000\u3000\u90a3\u4eba\u9762\u8272\u9661\u53d8\uff0c\u563f\u3001\u563f\u3001\u563f\u7684\u51b7\u7b11\u4e86\u51e0\u58f0\uff0c\u7a0b\u6d69\u4ee5\u4e3a\u4ed6\u5c31\u8981\u53d1\u4f5c\uff0c\u54ea\u77e5\u4ed6\u7b11\u58f0\u4e00\u6536\uff0c\u5374 \u53c8\u6162\u6761\u65af\u7406\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u8c05\u4f60\u4e5f\u4e0d\u6562\u63a5\uff0c\u4e0d\u8fc7\uff0c\u4e5f\u8fd8\u6709\u5546\u91cf\u7684\u4f59\u5730\u3002\u8c37\u4e4b\u534e\u4e0d\u5728\uff0c\u4f60\u4eec\u8fd9 \u91cc\uff0c\u4e5f\u8fd8\u6709\u4e00\u4e2a\u4eba\u53ef\u4ee5\u63a5\u8fd9\u8bf7\u5e16\u7684\u3002\u201d \u3000\u3000\u7a0b\u6d69\u6014\u4e86\u4e00\u6014\uff0c\u6120\u9053\uff1a\u201c\u4f60\u8fd9\u8bf7\u5e16\u4e0d\u662f\u8981\u7ed9\u6211\u4eec\u8c37\u638c\u95e8\u7684\u5417\uff1f\u6211\u8bf4\u4e0d\u63a5\uff0c\u5c31\u662f\u4e0d\u63a5\uff01\u4f60 \u8fd8\u8981\u79c1\u81ea\u4ea4\u7ed9\u8c01\uff1f\u4f60\u61c2\u4e0d\u61c2\u6b66\u6797\u89c4\u77e9\uff1f\u201d\u8981\u77e5\u6b66\u6797\u4e2d\u4efb\u4f55\u5b97\u6d3e\uff0c\u90fd\u662f\u4ee5\u638c\u95e8\u4eba\u4f5c\u4e3a\u4ee3\u8868\uff0c\u7a0b \u6d69\u5df2\u7ecf\u8868\u660e\u4e86\u81ea\u5df1\u7684\u8eab\u4efd\u4e43\u662f\u6c13\u5c71\u6d3e\u7684\u4ee3\u7406\u638c\u95e8\uff0c\u800c\u8fd9\u4eba\u5374\u8981\u5c06\u4ed6\u6487\u5f00\uff0c\u53e6\u5916\u627e\u4eba\u6765\u63a5\u8bf7 \u5e16\uff0c\u7a0b\u6d69\u5f53\u7136\u8ba4\u4e3a\u8fd9\u662f\u4e00\u79cd\u85d0\u89c6\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u4eba\u5374\u7ffb\u8d77\u4e00\u53cc\u767d\u773c\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u624d\u662f\u4e0d\u61c2\u89c4\u77e9\uff0c\u6211\u5bb6\u4e3b\u4eba\u8bf7\u7684\u662f\u8c37\u4e4b \u534e\uff0c\u4e0d\u662f\u8bf7\u6c13\u5c71\u6d3e\u7684\u8c37\u638c\u95e8\uff01\u4f60\u4eec\u6c13\u5c71\u6d3e\u8981\u5c06\u638c\u95e8\u4eba\u7684\u79c1\u4e8b\u5305\u63fd\u8fc7\u6765\uff0c\u90a3\u662f\u4f60\u4eec\u81ea\u8ba8\u82e6\u5403\uff0c \u6211\u4e5f\u7531\u5f97\u4f60\u4eec\u3002\u4e0d\u8fc7\uff0c\u6211\u8fd9\u8bf7\u5e16\u8fd8\u662f\u5f97\u4ea4\u7ed9\u8be5\u63a5\u7684\u4eba\u3002\u8c37\u4e4b\u534e\u4e0d\u5728\uff0c\u4f60\u5524\u5979\u7684\u5f92\u5f1f\u51fa\u6765\u5427\u3002 \u6211\u8981\u89c1\u5979\uff01\u201d \u3000\u3000\u8fd9\u4eba\u7684\u53e3\u6c14\u867d\u7136\u6a2a\u86ee\uff0c\u5374\u4e5f\u6709\u70b9\u9053\u7406\uff0c\u7a0b\u6d69\u5403\u4ed6\u62a2\u767d\u4e86\u4e00\u987f\uff0c\u5ffd\u5730\u5fc3\u5934\u4e00\u52a8\uff0c\u60f3\u9053\uff1a \u201c\u8c37\u4e2d\u83b2\u7684\u8eab\u4e16\u751a\u4e3a\u53e4\u602a\uff0c\u81f3\u4eca\u672a\u660e\u3002\u83ab\u975e\u6b64\u4eba\u6765\u6b64\uff0c\u539f\u662f\u4e0e\u5979\u6709\u5173\uff1f\u201d\u5f53\u4e0b\u5fcd\u4f4f\u4e86\u6c14\uff0c\u518d \u95ee\u90a3\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u7684\u8c37\u638c\u95e8\u662f\u6709\u4e00\u4f4d\u5973\u5f1f\u5b50\uff0c\u4f60\u662f\u8ba4\u5f97\u5979\u7684\u5417\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u4e0d\u8ba4 \u5f97\u3002\u201d\u7a0b\u6d69\u518d\u95ee\u9053\uff1a\u201c\u7136\u5219\u4f60\u548c\u5979\u662f\u6cbe\u4eb2\u5e26\u6545\u4e48\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u4e5f\u4e0d\u662f\uff01\u201d \u3000\u3000\u7a0b\u6d69\u6012\u9053\uff1a\u201c\u65e2\u7136\u975e\u4eb2\u975e\u6545\uff0c\u4f60\u8981\u89c1\u5979\u505a\u4ec0\u4e48\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u8fd9\u8bf7\u5e16\u4e5f\u6709\u5979\u4e00\u4efd\uff01\u201d\u7a0b \u6d69\u9053\uff1a\u201c\u4f60\u8fd9\u6d3b\u5c31\u4e0d\u8fd1\u60c5\u7406\u4e86\u3002\u4f60\u77e5\u4e0d\u77e5\u9053\uff1a\u5979\u8fd8\u53ea\u662f\u4e00\u4e2a\u672a\u6210\u5e74\u7684\u5973\u5b69\u5b50\uff0c\u7eb5\u7b97\u4f60\u4e3b\u4eba\u548c \u5979\u6cbe\u4eb2\u5e26\u6545\uff0c\u53d1\u5e16\u8bf7\u5979\uff0c\u5979\u4e5f\u53ea\u80fd\u548c\u5e08\u7236\u540c\u53bb\uff0c\u96be\u9053\u4f60\u8981\u5979\u4e00\u4e2a\u672a\u6210\u5e74\u7684\u5973\u5b69\u5b50\uff0c\u8dcb\u6d89\u4e07\u6c34 \u5343\u5c71\uff0c\u5b64\u5355\u5355\u4e00\u4eba\uff0c\u53bb\u8d74\u4f60\u5bb6\u4e3b\u4eba\u7684\u5bb4\u4f1a\u4e48\uff1f\u201d \u3000\u3000\u90a3\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u771f\u662f\u7cca\u6d82\uff0c\u6211\u6765\u8bf7\u5979\uff0c\u96be\u9053\u8fd8\u4f1a\u4e22\u5f00\u5979\u4e0d\u7ba1\u5417\uff1f\u5f53\u7136\u662f\u7531\u6211\u966a\u7740\u5979 \u540c\u53bb\uff01\u6709\u6211\u966a\u5979\uff0c\u4f60\u8fd8\u6015\u8001\u864e\u5403\u6389\u5979\u4e0d\u6210\uff01\u201d\u7a0b\u6d69\u9053\uff1a\u201c\u5413\uff01\u8fd9\u4e48\u8bf4\uff0c\u4f60\u662f\u8981\u5c06\u5979\u5e26\u8d70\uff1f\u201d \u90a3\u4eba\u9053\uff1a\u201c\u6b63\u662f\u3002\u8c37\u4e4b\u534e\u82e5\u5728\u6b64\u5730\uff0c\u5c31\u7531\u8c37\u4e4b\u534e\u643a\u5979\u540c\u53bb\u3002\u8c37\u4e4b\u534e\u4e0d\u5728\uff0c\u5c31\u7531\u6211\u5e26\u5979\u8d70\uff0c\u5c31 \u662f\u8fd9\u6837\u3002\u201d \u3000\u3000\u7a0b\u6d69\u8c08\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u771f\u4f1a\u8bf7\u5ba2\uff0c\u602a\u4e0d\u5f97\u4f60\u4e3b\u4eba\u5dee\u9063\u4f60\u6765\u3002\u53ef\u60dc\u655d\u5e08\u4f84\u4e5f\u4e0d\u5728\u5bb6\u4e86\u3002\u201d\u90a3 \u4eba\u5927\u58f0\u95ee\u9053\uff1a\u201c\u5979\u5728\u54ea\u513f\uff1f\u201d\u7a0b\u6d69\u9053\uff1a\u201c\u548c\u5979\u7684\u5e08\u7236\u548c\u5979\u4e00\u540c\u51fa\u95e8\u53bb\u4e86\u3002\u4e0a\u54ea\u513f\u53bb\uff0c\u6211\u4eec\u4e0d \u77e5\u9053\u3002\u201d \u3000\u3000\u90a3\u4eba\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\u6709\u8fd9\u6837\u51d1\u5de7\u7684\u4e8b\uff0c\u6211\u4e00\u6765\u5979\u4eec\u5c31\u4eec\u51fa\u4e86\u95e8\uff1f\u4f60\u8bf4\u4e0d\u5728\uff0c\u6211\u5374 \u8981\u4eb2\u81ea\u8fdb\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u7a0b\u6d69\u5927\u6012\u9053\uff1a\u201c\u4f60\u597d\u751f\u65e0\u793c\uff0c\u6c13\u5c71\u6d3e\u7684\u7384\u5973\u89c2\u662f\u5bb9\u4f60\u4e71\u95ef\u7684\u5417\uff1f\u201d\u6d3b\u72b9\u672a\u4e86\uff0c\u90a3\u4eba\u5df2\u5411 \u524d\u8fc8\u8fdb\u51e0\u6b65\uff01\u6c13\u5c71\u6d3e\u4f17\u5f1f\u5b50\u6b63\u8981\u4e0a\u524d\u6321\u4ed6\uff0c\u4e00\u77a7\u5730\u4e0b\uff0c\u4e0d\u89c9\u90fd\u5403\u4e86\u4e00\u60ca\uff01 \u3000\u3000\u7384\u5973\u89c2\u5916\u8fd9\u6761\u8def\u662f\u7528\u9752\u77f3\u94fa\u7684\uff0c\u53ea\u89c1\u8fd9\u4eba\u6bcf\u79fb\u4e00\u6b65\uff0c\u77f3\u4e0a\u5c31\u51fa\u4e86\u4e00\u4e2a\u6df1\u6df1\u7684\u8db3\u5370\uff0c\u4f17\u5f1f \u5b50\u5747\u662f\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u8981\u662f\u6328\u4e0a\u4e86\u4ed6\u4e00\u811a\uff0c\u6015\u4e0d\u9aa8\u788e\u8eab\u4ea1\u3002\u201d \u3000\u3000\u7a0b\u6d69\u3001\u767d\u82f1\u6770\u3001\u8def\u82f1\u8c6a\u3001\u7518\u4eba\u9f99\u8fd9\u56db\u5927\u5f1f\u5b50\u662f\u89c1\u8fc7\u5927\u573a\u9762\u7684\uff0c\u6bd4\u8f83\u8fd8\u80fd\u4fdd\u6301\u9547\u5b9a\uff0c\u4f46\u4e5f \u4e0d\u514d\u6697\u6697\u5600\u5495\uff0c\u7a0b\u6d69\u5fc3\u60f3\uff1a\u201c\u8fd9\u53ae\u7684\u94c1\u811a\u795e\u529f\u8db3\u53ef\u4ee5\u4e0e\u5c11\u6797\u5bfa\u7684\u91d1\u521a\u795e\u638c\u76f8\u6bd4\uff0c\u53ea\u6015\u5927\u60b2\u7985 \u5e08\u4e5f\u6ca1\u6709\u8fd9\u822c\u529f\u529b\u3002\u504f\u504f\u8c37\u5e08\u59b9\u53c8\u4e0d\u5728\u8fd9\u513f\u3002\u5408\u6211\u4eec\u56db\u4eba\u4e4b\u529b\uff0c\u6216\u53ef\u4ee5\u6321\u5f97\u4f4f\u4ed6\u3002\u4f46\u4ed6\u53ea\u662f \u4e00\u4e2a\u4e0b\u4eba\u8eab\u4efd\uff0c\u6c13\u5c71\u56db\u5927\u5f1f\u5b50\u5408\u529b\u5bf9\u4ed8\u4e00\u4e2a\u4e0b\u4eba\uff0c\u5c82\u4e0d\u6559\u4eba\u7b11\u8bdd\uff1f\u201d\u767d\u82f1\u6770\u7b49\u4eba\u4e5f\u662f\u540c\u6837\u5fc3 \u601d\uff0c\u7247\u523b\u95f4\u90a3\u4eba\u5df2\u8fc8\u8fdb\u4e86\u516d\u4e03\u6b65\uff0c\u770b\u770b\u5c31\u8981\u8e0f\u8fdb\u89c2\u95e8\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u89c1\u90a3\u4eba\u786c\u95ef\u89c2\u95e8\uff0c\u8fd8\u626c\u8a00\u8981\u628a\u8c37\u4e2d\u83b2\u5e26\u8d70\uff0c\u6012\u6c14\u9661\u751f\uff0c\u7981\u4e0d\u4f4f\u4fbf\u8dd1\u4e0a\u524d\u53bb\uff0c\u5927\u58f0 \u559d\u9053\uff1a\u201c\u5454\uff01\u54ea\u91cc\u6765\u7684\u6076\u5ba2\uff0c\u80c6\u6562\u5982\u6b64\u6a2a\u86ee\uff0c\u8d76\u5feb\u7ed9\u6211\u6b62\u6b65\uff01\u201d \u3000\u3000\u90a3\u4eba\u7ed9\u6c5f\u6d77\u5929\u4e00\u559d\uff0c\u9707\u5f97\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u56de\u5934\u4e00\u671b\uff0c\u4e5f\u4e0d\u5f97\u5927\u5403\u4e00\u60ca\u3002\u539f\u6765\u6c5f\u6d77\u5929\u8d70\u8def \u7684\u59ff\u52bf\u5f88\u602a\uff0c\u811a\u677f\u63d0\u8d77\uff0c\u4e00\u6446\u6446\u7684\u5c31\u50cf\u626b\u628a\u5728\u5730\u4e0a\u626b\u8fc7\u4e00\u822c\uff0c\u90a3\u4eba\u7684\u8db3\u5370\u90fd\u7ed9\u4ed6\u626b\u5e73\u4e86\u3002 \u3000\u3000\u90a3\u4eba\u89c1\u6c5f\u6d77\u5929\u4e0d\u8fc7\u662f\u4e2a\u5341\u4e94\u516d\u5c81\u7684\u5c11\u5e74\uff0c\u867d\u89c9\u60ca\u5947\uff0c\u8fd8\u672a\u600e\u6837\u7740\u614c\uff0c\u5f53\u4e0b\u4fbf\u8f6c\u8fc7\u8eab\u6765\u95ee \u9053\uff1a\u201c\u4f60\u662f\u8c01\uff0c\u6211\u5bb6\u4e3b\u4eba\u7684\u4e8b\u60c5\uff0c\u5c82\u662f\u4f60\u8fd9\u4e73\u81ed\u672a\u5e72\u7684\u5c0f\u5b50\u7ba1\u5f97\u4e86\u7684\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u79bb\u5bb6\u4e4b\u524d\uff0c\u8ddf\u5916\u5a46\u6768\u67f3\u9752\u5b66\u8fc7\u4e00\u4e9b\u6c5f\u6e56\u53e3\u8bed\uff0c\u8fd9\u65f6\u4fbf\u642c\u51fa\u6765\u7528\u9053\uff1a\u201c\u5929\u4e0b\u4eba\u7ba1\u5f97 \u5929\u4e0b\u4e8b\uff0c\u6709\u7406\u4e0d\u5728\u5e74\u9ad8\uff0c\u4e09\u5c81\u5b69\u513f\u7ba1\u5927\u516c\u3002\u8bf7\u5ba2\u662f\u4e24\u76f8\u60c5\u613f\u7684\uff0c\u4f55\u51b5\u4eba\u5bb6\u53c8\u4e0d\u5728\u5bb6\uff0c\u4f60\u600e\u53ef \u4ee5\u4e0d\u95ee\u4e3b\u4eba\uff0c\u786c\u95ef\u8fdb\u53bb\uff0c\u4e71\u6d3e\u8bf7\u5e16\uff0c\u54fc\uff0c\u54fc\uff0c\u4f60\u5bb6\u4e3b\u4eba\u662f\u5929\u7687\u8001\u5b50\u4e5f\u7f62\uff0c\u6211\u662f\u7ba1\u5b9a\u7684\u4e86\uff01\u4f60 \u5feb\u5feb\u6eda\u5f00\uff0c\u5426\u5219\u4f11\u602a\u6211\u4e5f\u65e0\u793c\uff01\u201d \u3000\u3000\u90a3\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8981\u7ba1\u4e5f\u884c\uff0c\u4f60\u6765\u63a5\u8fd9\u8bf7\u5e16\u5427\u3002\u201d\u6c5f\u6d77\u5929\u5012\u662f\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5bb6 \u4e3b\u4eba\u7684\u9152\u5e2d\u6015\u6ca1\u4eba\u53bb\u5403\u5417\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u5927\u4e0d\u538b\u5c0f\uff0c\u6211\u4e0d\u60f3\u4e0e\u4f60\u52a8\u624b\uff0c\u4f60\u63a5\u4e86\u8bf7\u5e16\uff0c\u6211\u4e5f\u597d \u56de\u53bb\u4ea4\u5dee\u5440\u3002\u5230\u65f6\u8c37\u4e4b\u534e\u82e5\u4e0d\u80fd\u6765\uff0c\u4f60\u5c31\u66ff\u4f60\u7684\u638c\u95e8\u6765\u8d74\u5bb4\u5427\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u60f3\uff1a\u201c\u83ab\u975e\u4ed6\u7684\u4e3b\u4eba\u771f\u662f\u8fd9\u6837\u5429\u5490\u4ed6\u7684\uff0c\u4e00\u5b9a\u8981\u8bf7\u5f97\u4e00\u4e2a\u4eba\u6765\u3002\u82e5\u7136\u5982\u6b64\uff0c\u6211 \u5012\u4e0d\u53ef\u4ee4\u4ed6\u96be\u4e3a\u4e86\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6c13\u5c71\u6d3e\u7684\u4eba\uff0c\u4f46\u4f60\u8981\u8bf7\u7684\u90a3\u4f4d\u59d1\u5a18\u5374\u662f\u6211\u7684\u670b \u53cb\u3002\u4e3a\u670b\u53cb\u4e24\u808b\u63d2\u5200\uff0c\u4f55\u51b5\u4e00\u5f20\u8bf7\u5e16\uff0c\u4f60\u5c31\u4ea4\u7ed9\u6211\u5427\uff01\u201d \u3000\u3000\u90a3\u4eba\u6253\u91cf\u4e86\u6c5f\u6d77\u5929\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u90a3\u4f4d\u59d1\u5a18\u7684\u670b\u53cb\u4e48\u3001\u8fd9\u66f4\u597d\u4e86\uff0c\u63a5\u5427\uff01\u201d\u8bf4\u7f62\u4fbf \u5c06\u4e00\u4e2a\u7ea2\u6728\u5323\u5b50\u5411\u6c5f\u6d77\u5929\u80f8\u524d\u63a8\u53bb\uff0c\u6c5f\u6d77\u5929\u4f38\u624b\u4e00\u63a5\uff0c\u53ea\u89c9\u4e00\u80a1\u6781\u5927\u7684\u6f5c\u529b\u4f3c\u5de8\u6d6a\u822c\u500f\u5730\u51b2 \u6765\u3002 \u3000\u3000\u6c13\u5c71\u56db\u5927\u5f1f\u5b50\u53c8\u60ca\u53c8\u6012\uff0c\u767d\u82f1\u6770\u6700\u5feb\uff0c\u5df2\u5148\u6251\u6765\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u6076\u8d3c\u4f4f\u624b\uff0c\u4f11\u65bd\u6697\u7b97\uff0c \u4f60\u8981\u52a8\u624b\uff0c\u5c31\u51b2\u7740\u6c13\u5c71\u6d3e\u6765\u5427\uff01\u201d\u8981\u77e5\u6b64\u4e8b\u4e43\u662f\u56e0\u6c13\u5c71\u6d3e\u800c\u8d77\uff0c\u6c5f\u6d77\u5929\u4e0d\u8fc7\u662f\u6253\u62b1\u4e0d\u5e73\u8005\u7684 \u8eab\u4efd\uff0c\u767d\u82f1\u6770\u7b49\u4eba\u867d\u7136\u660e\u77e5\u4e0d\u654c\uff0c\u4f46\u8981\u662f\u5728\u6c13\u5c71\u4e4b\u4e0a\uff0c\u8ba9\u8fd9\u4eba\u4f24\u5bb3\u4e86\u6c5f\u6d77\u5929\uff0c\u6c13\u5c71\u6d3e\u53ef\u5c31\u8981 \u5931\u5c3d\u9762\u5b50\u4e86\u3002 \u3000\u3000\u53ef\u662f\u767d\u82f1\u6770\u5c1a\u672a\u66fe\u6251\u5230\uff0c\u6c5f\u6d77\u5929\u624b\u81c2\u4e00\u4f38\uff0c\u5df2\u628a\u90a3\u7ea2\u6728\u5323\u5b50\u63a5\u4e86\u8fc7\u6765\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684 \u4e00\u58f0\uff0c\u90a3\u4eba\u5df2\u662f\u6454\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u4ece\u5c71\u5761\u4e0a\u9aa8\u788c\u788c\u5730\u6eda\u4e0b\u53bb\u4e86\u3002\u539f\u6765\u6c5f\u6d77\u5929\u65e9\u5df2\u7ec3\u6210\u4e86\u62a4\u4f53\u795e \u529f\uff0c\u867d\u7136\u672a\u52a0\u9632\u5907\uff0c\u4f46\u4e00\u89e6\u53ca\u5916\u529b\uff0c\u4fbf\u81ea\u7136\u751f\u51fa\u53cd\u5e94\uff0c\u90a3\u4eba\u6240\u53d1\u7684\u529b\u9053\u8d8a\u731b\uff0c\u6240\u53d7\u7684\u53cd\u51fb\u4e4b \u529b\u4e5f\u8d8a\u5927\uff0c\u4ed6\u7684\u672c\u9886\u867d\u7136\u4e0d\u5f31\uff0c\u5374\u600e\u7981\u5f97\u4f4f\u91d1\u4e16\u9057\u6240\u4f20\u7684\u7edd\u4e16\u795e\u529f\uff1f \u3000\u3000\u8f6c\u773c\u95f4\u90a3\u4eba\u5df2\u6eda\u4e0b\u5c71\u5761\uff0c\u8fdc\u8fdc\u7684\u542c\u5f97\u4ed6\u5927\u53eb\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6709\u80c6\u7684\u5c31\u5230\u91d1\u9e70\u5bab\u6765\uff01\u54fc\uff0c \u54fc\uff0c\u8981\u662f\u4e0d\u6765\uff0c\u4f60\u4eec\u6c13\u5c71\u6d3e\uff0c\u81ea\u8c37\u4e4b\u534e\u4ee5\u4e0b\uff0c\u8fde\u4f60\u8fd9\u5c0f\u5b50\u5728\u5185\uff0c\u90fd\u6709\u5927\u7978\u96be\u9003\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5927\u6012\uff0c\u4e5f\u7528\u4f20\u97f3\u5165\u5bc6\u7684\u5185\u529f\u559d\u9053\uff1a\u201c\u4f60\u4e5f\u56de\u53bb\u53eb\u4f60\u5bb6\u4e3b\u4eba\u7b49\u7740\u5427\uff0c\u5c31\u662f\u6ca1\u6709\u8bf7 \u5e16\uff0c\u6211\u4e5f\u8981\u53bb\u77a7\u77a7\u4f60\u4eec\u662f\u4e9b\u4ec0\u4e48\u4e1c\u897f\uff0c\u5982\u6b64\u6a2a\u884c\u9738\u9053\uff1f\u201d\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5b8c\uff0c\u90a3\u4eba\u4e5f\u5df2\u8dd1\u5f97\u8fde\u5f71 \u5b50\u90fd\u4e0d\u89c1\u4e86\u3002\u4ed6\u53d7\u4e86\u6c5f\u6d77\u5929\u62a4\u4f53\u795e\u529f\u7684\u53cd\u51fb\uff0c\u5c45\u7136\u8fd8\u80fd\u65bd\u5c55\u8f7b\u529f\u9003\u8dd1\uff0c\u8db3\u89c1\u9020\u8be3\u4ea6\u81ea\u4e0d\u51e1\u3002 \u3000\u3000\u6c13\u5c71\u6d3e\u4f17\u5f1f\u5b50\u90fd\u56f4\u4e86\u4e0a\u6765\uff0c\u7a0b\u6d69\u4ee5\u4ee3\u7406\u638c\u95e8\u4eba\u7684\u8eab\u4efd\uff0c\u5411\u6c5f\u6d77\u5929\u8c22\u9053\uff1a\u201c\u591a\u8c22\u82f1\u96c4\u63f4 \u624b\uff0c\u6562\u95ee\u5c0a\u59d3\u5927\u540d\uff1f\u201d\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u7a0b\u4f2f\u4f2f\uff0c\u4f60\u4e0d\u8ba4\u5f97\u6211\u4e86\u5417\uff1f\u6211\u662f\u6c5f\u6d77\u5929\u5440\u3002\u4f2f\u4f2f\uff0c\u4f60 \u4f11\u591a\u793c\uff0c\u8fd9\u53ef\u6298\u715e\u6211\u4e86\u3002\u201d \u3000\u3000\u767d\u82f1\u6770\u773c\u5229\uff0c\u5148\u8ba4\u4e86\u51fa\u6765\uff0c\u201c\u554a\u5440\u201d\u7684\u4e00\u58f0\u53eb\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u6c5f\u8d24\u4f84\uff0c\u54c8\u54c8\uff0c\u6c5f\u5357\u5144\u5f1f \u6709\u4f60\u8fd9\u6837\u4e00\u4f4d\u82f1\u96c4\u513f\u5b50\uff0c\u60f3\u5fc5\u8981\u4e50\u715e\u4ed6\u4e86\uff0c\u6c5f\u8d24\u4f84\uff0c\u4f60\u7239\u7239\u5462\uff0c\u542c\u8bf4\u4ed6\u6709\u897f\u5317\u4e4b\u884c\uff0c\u53ef\u56de\u6765 \u6ca1\u6709\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u7239\u79bb\u5bb6\u5df2\u6709\u4e09\u5e74\uff0c\u5c1a\u672a\u56de\u6765\u3002\u6211\u6b63\u662f\u8981\u53bb\u5bfb\u8bbf\u4ed6\u7684\u3002\u8fdc\u884c\u4e4b\u524d\uff0c\u5148\u6765\u62dc \u8c12\u8c37\u5973\u4fa0\uff0c\u8fd8\u6709\u4e00\u4ef6\u4e1c\u897f\uff0c\u8981\u9001\u7ed9\u8c37\u4e2d\u83b2\u7684\u3002\u5979\u4eec\u53ef\u771f\u662f\u90fd\u4e0d\u5728\u5bb6\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u6d69\u9053\uff1a\u201c\u5979\u4eec\u771f\u7684\u662f\u51fa\u8fdc\u95e8\u53bb\u4e86\u3002\u8fd9\u5012\u5e76\u975e\u6545\u610f\u9a97\u90a3\u6076\u8d3c\u7684\u3002\u201d\u6c5f\u6d77\u5929\u5927\u5931\u6240\u671b\uff0c\u95ee \u9053\uff1a\u201c\u5979\u4eec\u662f\u4ec0\u4e48\u65f6\u5019\u51fa\u95e8\u4e86\u7684\uff1f\u201d\u7a0b\u6d69\u9053\uff1a\u201c\u5dee\u4e0d\u591a\u6709\u4e24\u4e2a\u4e14\u4e86\u3002\u662f\u4e3a\u4e86\u4e00\u4ef6\u610f\u5916\u4e4b\u4e8b\u8d70 \u7684\uff0c\u6c5f\u8d24\u4f84\uff0c\u96be\u5f97\u4f60\u6765\uff0c\u54b1\u4eec\u8fdb\u53bb\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u968f\u5728\u540e\u9762\uff0c\u8fdb\u5165\u9053\u89c2\uff0c\u4ed6\u66fe\u5728\u8fd9\u91cc\u5ea6\u8fc7\u7ae5\u5e74\u65f6\u5019\u6700\u5feb\u4e50\u7684\u4e00\u6bb5\u65f6\u5149\uff0c\u82b1\u9e1f\u8349\u6728\uff0c \u51e0\u4e4e\u5c3d\u662f\u65e7\u65f6\u76f8\u8bc6\uff0c\u4e00\u522b\u516b\u5e74\uff0c\u65e7\u5730\u91cd\u6765\uff0c\u89c2\u4e2d\u7684\u666f\u7269\u5012\u6ca1\u6709\u4ec0\u4e48\u6539\u53d8\uff0c\u53ea\u662f\u4e0d\u89c1\u4e86\u8c37\u4e2d \u83b2\uff0c\u4e0d\u7981\u5fc3\u5934\u60c6\u6005\uff0c\u4e00\u7247\u60c6\u7136\u3002 \u3000\u3000\u5750\u5b9a\u4e4b\u540e\uff0c\u7a0b\u6d69\u4fbf\u5c06\u8c37\u4e4b\u534e\u5e08\u5f92\u51fa\u95e8\u7684\u7ecf\u8fc7\u544a\u8bc9\u4ed6\u3002 \u3000\u3000\u90a3\u662f\u5143\u5bb5\u8fc7\u540e\u7684\u7b2c\u4e8c\u5929\uff0c\u90ae\u5c71\u4e0a\u6765\u4e86\u4e00\u4e2a\u5ba2\u4eba\uff0c\u662f\u9648\u7559\u53bf\u53f6\u541b\u5c71\u7684\u5f1f\u5b50\uff0c\u540d\u53eb\u6768\u78f7\u3002 \u3000\u3000\u7a0b\u6d69\u8bf4\u9053\uff1a\u201c\u53f6\u541b\u5c71\u662f\u9752\u57ce\u6d3e\u7684\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u751f\u524d\u5728\u6b66\u6797\u4e2d\u4e5f\u9887\u6709\u540d\u6c14\uff0c\u4f46\u4ed6\u4e2d\u5e74\u9690\u5c45\uff0c \u4e0e\u6c13\u5c71\u6d3e\u5374\u6ca1\u6709\u4ec0\u4e48\u5f80\u6765\u3002\u4ed6\u8fc7\u4e16\u5df2\u6709\u4e2a\u591a\u5e74\u4e86\uff0c\u95fb\u8bf4\u6b7b\u5f97\u5f88\u662f\u5947\u7279\uff0c\u6b7b\u7684\u90a3\u5929\uff0c\u767d\u5929\u91cc\u8fd8 \u597d\u597d\u7684\uff0c\u665a\u4e0a\u4fbf\u7a81\u7136\u66b4\u6bd9\u4e86\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u7684\u6b7b\u56e0\u3002\u8fd8\u6709\u4e00\u6869\u5947\u602a\u7684\u4e8b\u662f\uff1a\u4ed6\u53ea\u6709\u4e00\u4e2a\u5b69 \u5b50\uff0c\u5728\u4ed6\u6b7b\u7684\u65f6\u5019\uff0c\u90a3\u5b69\u5b50\u5927\u7ea6\u4e5f\u53ea\u6709\u56db\u4e94\u5c81\uff0c\u7236\u4eb2\u4e00\u6b7b\uff0c\u90a3\u5b69\u5b50\u4e5f\u5931\u8e2a\u4e86\u3002\u201d\u5176\u5b9e\u8fd9\u4e2a\u5b69 \u5b50\u5e76\u975e\u53f6\u541b\u5c71\u7684\u4eb2\u751f\uff0c\u6c13\u5c71\u6d3e\u4e2d\u53ea\u6709\u7ffc\u4ef2\u725f\u4e0e\u8c37\u4e4b\u534e\u7a0d\u7a0d\u77e5\u9053\u4ed6\u5bb6\u7684\u4e00\u4e9b\u79d8\u5bc6\uff0c\u7a0b\u6d69\u5bf9\u4ed6\u7684 \u5bb6\u4e8b\uff0c\u5374\u662f\u77e5\u800c\u4e0d\u8be6\u3002 \u3000\u3000\u7a0b\u6d69\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u53f6\u541b\u5c71\u6b7b\u7684\u65f6\u5019\uff0c\u6768\u78f7\u65e9\u5df2\u51fa\u5e08\uff0c\u4e0d\u5728\u8eab\u8fb9\u3002\u4ed6\u542c\u5f97\u5e08\u7236\u66b4\u6bd9\uff0c\u5e08\u5f1f \u5931\u8e2a\uff0c\u4e5f\u66fe\u8d76\u56de\u9648\u7559\uff0c\u67e5\u7a76\u539f\u56e0\uff0c\u7ecf\u8fc7\u591a\u5e74\uff0c\u5e76\u65e0\u7ed3\u684c\u3002\u4ed6\u77e5\u9053\u5e08\u7236\u5e76\u65e0\u4ec7\u5bb6\uff0c\u53c8\u95ee\u8fc7\u5f53\u65f6 \u7ed9\u4ed6\u5e08\u7236\u6536\u6b93\u7684\u90bb\u4eba\uff0c\u4e5f\u8bf4\u770b\u4e0d\u51fa\u6709\u4f55\u6b7b\u4e8e\u975e\u547d\u7684\u8ff9\u8c61\uff0c\u6210\u4e3a\u4e86\u4e00\u4ef6\u7591\u6848\u3002 \u3000\u3000\u201c\u6768\u78f7\u53d7\u5e08\u6069\u6df1\u91cd\uff0c\u5bf9\u5e08\u5f1f\u7684\u4e0b\u843d\uff0c\u5f53\u7136\u662f\u591a\u65b9\u8bbe\u6cd5\u6253\u542c\uff0c\u4e00\u6643\u8fc7\u4e86\u5341\u5e74\u6709\u591a\uff0c\u5230\u4e86\u53bb \u5e74\uff0c\u5f97\u5230\u4e00\u4e2a\u6d88\u606f\u3002 \u3000\u3000\u767d\u82f1\u6770\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u53f6\u541b\u5c71\u6709\u4e24\u4f4d\u4e61\u4eb2\uff0c\u6b66\u529f\u4e5f\u8fd8\u4e0d\u9519\uff0c\u662f\u5728\u5317\u4eac\u5f00\u9556\u5c40\u7684\u3002\u53bb\u5e74\u4ed6 \u4eec\u4fdd\u4e86\u4e00\u652f\u9556\u5230\u9752\u6d77\u53bb\uff0c\u9556\u4e3b\u53ef\u4e0d\u662f\u4e2a\u666e\u901a\u4eba\uff0c\u662f\u9752\u6d77\u9102\u5c14\u6c81\u65d7\u7684\u571f\u738b\uff0c\u4ed6\u4eec\u90a3\u91cc\u6700\u7f3a\u4e4f\u836f \u6750\uff0c\u6240\u4ee5\u5728\u5317\u4eac\u641c\u8d2d\u4e86\u4e00\u5927\u6279\uff0c\u6258\u9556\u5c40\u4ee3\u8fd0\u3002 \u3000\u3000\u201c\u7eff\u6797\u4e2d\u4eba\u6700\u559c\u6b22\u52ab\u7684\u662f\u91d1\u94f6\u73e0\u5b9d\uff0c\u4f53\u79ef\u5c0f\u800c\u53c8\u503c\u94b1\u7684\u4e1c\u897f\uff0c\u81f3\u4e8e\u836f\u6750\uff0c\u7eb5\u7136\u4e5f\u6709\u4e9b\u8d35 \u91cd\u5982\u7280\u725b\u89d2\u3001\u5eb8\u9999\u4e4b\u7c7b\u6742\u5728\u5176\u4e2d\uff0c\u4f46\u4e00\u6765\u5f3a\u76d7\u6253\u52ab\uff0c\u8bb2\u7a76\u7684\u662f\u5e72\u51c0\u4fd0\u843d\uff0c\u54ea\u6709\u95f2\u529f\u592b\u53bb\u8fa8\u8ba4 \u6311\u62e3\uff1b\u4e8c\u6765\uff0c\u9ed1\u9053\u4e0a\u4e5f\u6709\u5fcc\u8bb3\uff0c\u836f\u6750\u662f\u62ff\u53bb\u6551\u4eba\u7684\uff0c\u4ed6\u4eec\u8ba4\u4e3a\u52ab\u836f\u6750\u662f\u7f3a\u9634\u5fb7\u7684\u4e8b\u60c5\uff1b\u4e09\u6765 \u5373\u7b97\u5f97\u624b\uff0c\u4e5f\u96be\u4e8e\u8131\u624b\u6c42\u552e\u3002\u6545\u6b64\u4fdd\u8fd9\u79cd\u9556\uff0c\u6700\u662f\u7a33\u59a5\u4e0d\u8fc7\u3002\u4f55\u51b5\u90a3\u571f\u738b\u8fd8\u7b54\u5e94\u5230\u4e86\u9752\u6d77\u5883 \u5185\uff0c\u4fbf\u6d3e\u4eba\u63a5\u5e94\u3002 \u3000\u3000\u201c\u4f46\u7531\u4e8e\u8fd9\u652f\u9556\u8981\u8d70\u957f\u9014\uff0c\u9556\u4e3b\u4e0d\u662f\u666e\u901a\u4eba\uff0c\u6240\u4ee5\u8fd9\u95f4\u9556\u5c40\u8fd8\u662f\u6d3e\u51fa\u4e86\u4e24\u4e2a\u6700\u5f97\u529b\u7684\u9556 \u5e08\u3002\u201d \u3000\u3000\u767d\u82f1\u6770\u63a5\u7eed\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u5b97\u9556\u5c40\u8ba4\u4e3a\u7a33\u59a5\u4e0d\u8fc7\u7684\u751f\u610f\uff0c\u521a\u6765\u5230\u9102\u5c14\u6c81\u65d7\u7684\u5883\u5185\uff0c\u4fbf \u51fa\u4e86\u4e8b\u3002 \u3000\u3000\u201c\u90a3\u4e00\u5929\uff0c\u8ddd\u79bb\u9102\u5c14\u6c81\u65d7\u53ea\u6709\u534a\u65e5\u8def\u7a0b\uff0c\u571f\u738b\u4e14\u5df2\u6d3e\u6709\u4e00\u961f\u58eb\u5175\u63a5\u5e94\uff0c\u8fde\u540c\u9556\u5c40\u7684\u4eba\uff0c \u6709\u767e\u4f59\u4eba\u4e4b\u591a\uff0c\u5927\u961f\u4eba\u9a6c\uff0c\u5728\u8349\u539f\u4e0a\u6d69\u6d69\u8361\u8361\u7684\u884c\u8fdb\uff0c\u7a81\u7136\u5728\u8349\u539f\u4e0a\u78b0\u4e0a\u4e86\u4e00\u80a1\u5f3a\u76d7\uff0c\u5f3a\u76d7 \u7684\u4eba\u9a6c\u4e0d\u591a\uff0c\u5927\u7ea6\u53ea\u6709\u4e09\u56db\u5341\u4eba\uff0c\u53ef\u662f\u4eba\u4eba\u90fd\u662f\u9a81\u52c7\u975e\u5e38\uff0c\u4e00\u9635\u53ae\u6740\uff0c\u628a\u571f\u738b\u7684\u5175\u58eb\u548c\u9556\u5c40 \u7684\u4eba\u5458\u6740\u5f97\u7247\u7532\u4e0d\u7559\uff0c\u6b7b\u4f24\u904d\u5730\u3002\u90a3\u4e2a\u9556\u5e08\uff0c\u4e5f\u5373\u662f\u53f6\u541b\u5c71\u90a3\u4e24\u4e2a\u4e61\u4eb2\uff0c\u88ab\u4e00\u4e2a\u4e2d\u5e74\u7684\u5973\u532a \u9996\u7528\u5339\u7ea2\u7ef8\uff0c\u5c06\u4ed6\u4eec\u7684\u5175\u5668\u5377\u53bb\uff0c\u505a\u4e86\u4fd8\u864f\u3002\u5176\u4ed6\u53d7\u4e86\u8f7b\u4f24\u7684\u4e0e\u6ca1\u53d7\u4f24\u7684\u4e5f\u5c3d\u90fd\u88ab\u64d2\u3002 \u3000\u3000\u201c\u5f3a\u76d7\u4eec\u5c31\u8981\u4fd8\u864f\u7ed9\u4ed6\u4eec\u642c\u8fd0\u836f\u6750\uff0c\u4e00\u8def\u4e0a\u7528\u76ae\u97ad\u50ac\u4fc3\u4ed6\u4eec\uff0c\u7ecf\u8fc7\u7684\u90fd\u662f\u7a77\u5c71\u6076\u6c34\u3001\u8352 \u51c9\u4e0d\u6bdb\u4e4b\u5730\uff0c\u8d70\u4e86\u51e0\u5929\uff0c\u65b9\u59cb\u5230\u4e00\u4e2a\u57ce\u5821\uff0c\u836f\u6750\u5378\u4e86\u4e0b\u6765\uff0c\u4fd8\u864f\u4eec\u5219\u88ab\u5173\u5728\u4e00\u95f4\u5927\u5c4b\u5185\u3002\u5728 \u8349\u539f\u90a3\u4e00\u4ed7\u4e4b\u540e\uff0c\u6d3b\u7740\u7684\u5df1\u4e0d\u5230\u4e00\u534a\uff0c\u5728\u8def\u4e0a\u88ab\u6253\u6b7b\u7684\u4e5f\u4e0d\u5c11\uff0c\u5230\u4e86\u57ce\u5821\uff0c\u5269\u4e0b\u7684\u4fd8\u864f\u4ec5\u6709 \u4e09\u56db\u5341\u4eba\u4e86\u3002 \u3000\u3000\u4e24\u4e2a\u9556\u5e08\u88ab\u5173\u5728\u4e00\u8d77\uff0c\u6b63\u5728\u62c5\u5fc3\uff0c\u4e0d\u77e5\u5f3a\u76d7\u4eec\u8981\u5c06\u4ed6\u4eec\u5982\u4f55\u5904\u7f6e\uff0c\u5ffd\u89c1\u4e00\u4e2a\u62ab\u7740\u72d0\u88d8\u7684 \u5c11\u5e74\u8d70\u4e86\u8fdb\u6765\uff0c\u540e\u9762\u8ddf\u7740\u90a3\u4e2a\u532a\u9996\u548c\u4e03\u516b\u4e2a\u55bd\u7f57\u3002\u90a3\u5c11\u5e74\u4f3c\u662f\u4e2a\u8d35\u516c\u5b50\u6a21\u6837\uff0c\u5f3a\u76d7\u4eec\u5bf9\u4ed6\u7684 \u6001\u5ea6\u90fd\u5f88\u606d\u656c\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u53eb\u4eba\u5c06\u90a3\u4e24\u4e2a\u9556\u5e08\u63d0\u51fa\u6765\uff0c\u95ee\u9053\uff1a\u2018\u542c\u4f60\u4eec\u7684\u53e3\u97f3\uff0c\u4f3c\u662f\u6cb3\u5357\u9648\u7559\u53bf\u4eba\u6c0f\uff0c\u53ef\u662f \u4e0d\u662f\uff1f\u2019\u5947\u602a\u5f97\u5f88\uff0c\u90a3\u5c11\u5e74\u4e5f\u662f\u4e00\u53e3\u9648\u7559\u53bf\u7684\u4e61\u97f3\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u9556\u5e08\u8fde\u5fd9\u8ba4\u662f\uff0c\u90a3\u5c11\u5e74\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u8ba4\u8bc6\u53f6\u541b\u5c71\u53f6\u8001\u7237\u5b50\u4e48\uff1f\u201d\u90a3\u4e24\u4e2a\u9556\u5e08\u7591\u60d1 \u4e0d\u5b9a\uff0c\u53ef\u53c8\u4e0d\u6562\u95ee\u4ed6\u662f\u53f6\u541b\u5c71\u7684\u4ec0\u4e48\u4eba\uff0c\u5f53\u4e0b\u53ea\u597d\u81ea\u62a5\u59d3\u540d\uff0c\u4f9d\u5b9e\u56de\u7b54\uff1a\u201c\u4e0d\u4f46\u8ba4\u8bc6\uff0c\u6211\u4eec \u548c\u53f6\u8001\u7237\u5b50\u8fd8\u6cbe\u7740\u4e00\u70b9\u4eb2\u621a\u5173\u7cfb\uff0c\u4ed6\u662f\u6211\u4eec\u7684\u957f\u8f88\u3002\u53ef\u60dc\u4ed6\u5728\u5341\u591a\u5e74\u524d\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u7f25\u5e08\u7559\u5fc3\u7740\u8fd9\u5c11\u5e74\u7684\u795e\u8272\uff0c\u53ea\u89c1\u4ed6\u9762\u8272\u4e00\u5ea6\u6c89\u6697\uff0c\u540e\u5411\u90a3\u5973\u532a\u9996\u8bf4\u9053\uff1a\u2018\u8fd9\u4e24\u4e2a \u4eba\u6211\u8981\u5411\u4f60\u8ba8\u60c5\uff0c\u8bf7\u4f60\u5c06\u4ed6\u4eec\u91ca\u653e\u3002\u201d\u90a3\u5973\u532a\u9996\u9053\uff1a\u201c\u516c\u5b50\u6709\u547d\uff0c\u5c82\u6562\u4e0d\u4f9d\u3002\u201d\u5f53\u4e0b\uff0c\u5c31\u7ed9 \u90a3\u4e24\u4e2a\u9556\u5e08\u677e\u7f1a\uff0c\u90a3\u4e24\u4e2a\u9556\u5e08\u5411\u5c11\u5e74\u62dc\u8c22\uff0c\u6b63\u60f3\u8bf7\u95ee\u4ed6\u7684\u59d3\u540d\uff0c\u90a3\u5c11\u5e74\u5374\u4f3c\u4e0d\u60f3\u548c\u4ed6\u4eec\u591a \u8bf4\uff0c\u53ea\u5429\u5490\u4ed6\u4eec\u9053\uff1a\u2018\u4f60\u4eec\u5f97\u4e86\u6027\u547d\uff0c\u8fd8\u4e0d\u5feb\u8d70\uff1f\u4ee5\u540e\u53ef\u4e0d\u8981\u518d\u5230\u8fd9\u6761\u8def\u4e0a\u4fdd\u9556\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u9556\u5e08\u521a\u8d70\u51fa\u5c4b\u5916\uff0c\u4fbf\u542c\u5f97\u90a3\u5973\u532a\u9996\u5389\u58f0\u559d\u9053\uff1a\u201c\u4e00\u4e2a\u4e0d\u7559\uff01\u201d\u968f\u5373\u4fbf\u542c\u5f97\u5200\u65a7\u7684 \u5288\u65ab\u58f0\uff0c\u4fd8\u864f\u4eec\u7684\u5c16\u53eb\u58f0\uff0c\u5413\u5f97\u90a3\u4e24\u4e2a\u9556\u5e08\u9b42\u98de\u9b44\u6563\uff0c\u53ea\u6068\u7239\u5a18\u751f\u5c11\u4e86\u4e24\u6761\u817f\uff0c\u54ea\u91cc\u8fd8\u6562\u56de \u5934\u518d\u671b\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5b85\u5fc3\u4ec1\u539a\uff0c\u542c\u5f97\u6bdb\u9aa8\u609a\u7136\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u7684\u5982\u6b64\u6b8b\u66b4\uff0c\u628a\u4eba\u5f53\u6210\u866b\u8681\u4e00\u822c\uff01\u53ea\u4e0d\u77e5 \u90a3\u5c11\u5e74\u662f\u5426\u5f3a\u76d7\u4e00\u515a\uff1f\u201d \u3000\u3000\u767d\u82f1\u6770\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u4f60\u4ece\u672a\u6d89\u8db3\u6c5f\u6e56\uff0c\u4e0d\u77e5\u9ed1\u9053\u4e0a\u7684\u7981\u5fcc\u3002\u5f3a\u76d7\u4e2d\u4e5f\u6709\u597d\u6709\u574f\uff0c\u597d\u7684\u52ab \u5bcc\u6d4e\u8d2b\uff0c\u574f\u7684\u4e5f\u5c3d\u591a\u6740\u4eba\u4e0d\u7728\u773c\u7684\u5462\u3002\u8fd9\u80a1\u5f3a\u76d7\uff0c\u80c6\u6562\u52ab\u9752\u6d77\u4e00\u4e2a\u571f\u738b\u7684\u4e1c\u897f\uff0c\u60f3\u6765\u4e0d\u662f\u5bfb \u5e38\u5f3a\u76d7\uff0c\u4ed6\u4eec\u4e5f\u5fc5\u5b9a\u6015\u571f\u738b\u62a5\u590d\uff0c\u9ed1\u9053\u4e0a\u7684\u60ef\u4f8b\uff0c\u82e5\u7136\u505a\u4e86\u4e00\u4ef6\u6848\u5b50\uff0c\u9884\u6599\u4f1a\u6709\u7978\u60a3\u7684\u8bdd\uff0c \u8981\u561b\u5c31\u662f\u628a\u6349\u5230\u7684\u4eba\u5f85\u5982\u4e0a\u5bbe\uff0c\u7136\u540e\u8bf7\u6709\u5934\u9762\u7684\u4eba\u51fa\u6765\u8f6c\u571c\uff0c\u5f7c\u6b64\u5404\u8ba9\u4e00\u6b65\uff0c\u5f97\u4e9b\u597d\u5904\uff0c\u4fbf \u5373\u6536\u573a\uff1b\u8981\u561b\u5c31\u662f\u65a9\u5c3d\u6740\u7edd\uff0c\u4e0d\u7559\u4e00\u4e2a\u6d3b\u53e3\uff0c\u514d\u5f97\u5ba3\u626c\u51fa\u53bb\uff0c\u81f3\u4e8e\u90a3\u4e2a\u5c11\u5e74\uff0c\u8eab\u4efd\u786e\u662f\u4ee4\u4eba \u96be\u89e3\uff0c\u770b\u6765\u4e0d\u4f3c\u76d7\u515a\uff0c\u6240\u4ee5\u4ed6\u624d\u4ee5\u5ba2\u4eba\u7684\u53e3\u543b\u5411\u5973\u532a\u9996\u6c42\u60c5\uff1b\u4f46\u90a3\u80a1\u5f3a\u76d7\u7adf\u4f1a\u4e70\u4ed6\u9762\u5b50\uff0c\u8fdd \u53cd\u7981\u5fcc\uff0c\u7559\u4e0b\u4e24\u4e2a\u6d3b\u53e3\uff0c\u770b\u6765\u4ed6\u53c8\u4e00\u5b9a\u4e0e\u76d7\u515a\u6709\u6df1\u539a\u7684\u6e0a\u6e90\uff0c\u800c\u4e14\u6709\u5f88\u5927\u7684\u6765\u5934\uff0c\u7edd\u4e0d\u662f\u666e \u901a\u7684\u5ba2\u4eba\u3002\u201d \u3000\u3000\u767d\u82f1\u6770\u6b47\u4e0b\u6765\u559d\u8336\uff0c\u7a0b\u6d69\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u90a3\u5c11\u5e74\u662f\u4f55\u7b49\u8eab\u4efd\uff0c\u73b0\u5728\u672a\u660e\uff0c\u4f46\u53ef\u4ee5\u65ad\u5b9a\u7684 \u662f\uff0c\u4ed6\u5b9a\u7136\u662f\u53f6\u541b\u5c71\u90a3\u4e2a\u5931\u8e2a\u4e86\u5341\u591a\u5e74\u7684\u513f\u5b50\u3002 \u3000\u3000\u201c\u90a3\u4e2a\u9556\u5c40\u5931\u4e86\u8fd9\u679d\u9576\uff0c\u4fe1\u8a89\u5927\u51cf\uff0c\u800c\u4e14\u8fd9\u4e00\u5f79\u4ed6\u4eec\u53c8\u635f\u6298\u4e86\u5341\u51e0\u4e2a\u4f19\u8ba1\uff0c\u751f\u610f\u505a\u4e0d \u5f00\uff0c\u4eba\u529b\u53c8\u4e0d\u591f\uff0c\u8fd9\u95f4\u9556\u5c40\u4e5f\u5c31\u96be\u4e8e\u7ef4\u6301\u4e86\u3002\u9556\u5c40\u7684\u4e3b\u4eba\u94c1\u9e33\u9e2f\u97e9\u4e09\u7237\u75db\u5fc3\u75be\u9996\uff0c\u8a93\u62a5\u6b64 \u4ec7\uff0c\u4e5f\u66fe\u6258\u4eba\u67e5\u8bbf\u8fd9\u80a1\u5f3a\u76d7\u7684\u6765\u5386\uff0c\u5374\u662f\u6beb\u65e0\u7ed3\u679c\u3002\u53ea\u77e5\u90a3\u9102\u5c14\u6c81\u65d7\uff0c\u56e0\u4e3a\u53d1\u751f\u761f\u75ab\uff0c\u7f3a\u4e4f \u836f\u6750\uff0c\u58eb\u5175\u4eec\u4e5f\u90fd\u56e0\u60a3\u75c5\u4e0d\u80fd\u6253\u4ed7\uff0c\u7ed9\u90bb\u5883\u7684\u51e0\u4e2a\u914b\u957f\u5c06\u4ed6\u7684\u571f\u5730\u74dc\u5206\u4e86\u3002\u201d \u3000\u3000\u201c\u97e9\u4e09\u7237\u67e5\u4e0d\u51fa\u7ed3\u679c\uff0c\u4e00\u6c14\u4e4b\u4e0b\uff0c\u5173\u4e86\u9556\u5c40\uff0c\u592b\u5987\u4fe9\u5c31\u6b64\u79bb\u5f00\u5317\u4eac\uff0c\u6709\u4eba\u8bf4\u4ed6\u4eec\u662f\u8981\u4eb2 \u81ea\u5230\u9752\u6d77\u53bb\u62a5\u4ec7\u96ea\u6068\uff0c\u662f\u4e0e\u4e0d\u662f\uff0c\u65e0\u4eba\u786e\u77e5\u3002 \u3000\u3000\u201c\u53ea\u8bf4\u9556\u5c40\u5173\u95e8\u4e4b\u540e\uff0c\u90a3\u4e24\u4e2a\u4fa5\u5e78\u9003\u5f97\u6027\u547d\u7684\u9556\u5e08\uff0c\u56de\u5230\u6545\u4e61\u9648\u7559\uff0c\u4fbf\u53bb\u89c1\u53f6\u541b\u5c71\u7684\u5f92 \u5f1f\u6768\u78f7\uff0c\u5c06\u4eb2\u8eab\u7ecf\u5386\u7684\u4e8b\u60c5\uff0c\u5411\u4ed6\u4e00\u4e00\u8bc9\u8bf4\u3002 \u3000\u3000\u201c\u6768\u78f7\u5f97\u77e5\u7ecf\u8fc7\uff0c\u4e5f\u8ba4\u4e3a\u8fd9\u4e2a\u5c11\u5e74\u5fc5\u7136\u662f\u4ed6\u7684\u5e08\u5f1f\u3002\u65e2\u62c5\u5fc3\u5e08\u5f1f\u8bef\u5165\u6b67\u9014\uff0c\u53c8\u60f3\u53bb\u67e5\u7a76 \u5e08\u7236\u7684\u6b7b\u56e0\u3002\u4f46\u4ed6\u4e00\u4eba\u4e0d\u6562\u5192\u9669\uff0c\u90a3\u4e24\u4e2a\u9556\u5e08\u4e5f\u4e0d\u6562\u5e26\u8def\u3002\u4e8e\u662f\u53ea\u6709\u904d\u8bf7\u6b66\u6797\u540c\u9053\uff0c\u7ed9\u4ed6\u5e2e \u5fd9\u3002\u8fd9\u5c31\u662f\u6768\u78f7\u524d\u6765\u9099\u5c71\uff0c\u62dc\u8bbf\u6211\u4eec\u638c\u95e8\u5e08\u59b9\u7684\u539f\u56e0\u3002 \u3000\u3000\u201c\u9099\u5c71\u6d3e\u548c\u53f6\u541b\u5c71\u751f\u524d\u65e0\u751a\u4ea4\u60c5\uff0c\u50cf\u8fd9\u6837\u7684\u4e8b\u60c5\u672c\u6765\u53ef\u7406\u53ef\u4e0d\u7406\uff0c\u8981\u7406\u561b\uff0c\u6d3e\u51e0\u4e2a\u95e8\u4e0b \u5f1f\u5b50\u4e5f\u5c31\u591f\u4e86\u3002\u4f46\u51fa\u4e4e\u6211\u4eec\u610f\u5916\u7684\u662f\uff0c\u8c37\u638c\u95e8\u542c\u4e86\u6768\u78f7\u7684\u8bc9\u8bf4\uff0c\u7adf\u7136\u6beb\u4e0d\u8fdf\u7591\uff0c\u4e00\u53e3\u4fbf\u7b54\u5e94 \u4e0b\u6765\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u95ee\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4e2d\u83b2\u662f\u968f\u5979\u5e08\u7236\u5230\u9752\u6d77\u53bb\u4e86\uff1f\u201d\u767d\u82f1\u6770\u9053\uff1a\u201c\u6b63\u662f\u3002\u638c\u95e8\u5e08\u59b9\u8fd9 \u6b21\u5c31\u53ea\u643a\u5979\u540c\u884c\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u53ea\u5e26\u5979\u4e00\u4eba\uff1f\u201d\u767d\u82f1\u6770\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u4e0d\u61c2\u638c\u95e8\u7684\u7528 \u610f\uff0c\u6216\u8005\u662f\u501f\u6b64\u673a\u4f1a\uff0c\u8ba9\u5979\u5230\u6c5f\u6e56\u4e0a\u5386\u7ec3\u5386\u7ec3\u5427\u3002\u201d\u8fd9\u7406\u7531\u5176\u5b9e\u5e76\u4e0d\u5145\u5206\uff0c\u8981\u77e5\u638c\u95e8\u4eb2\u81ea\u51fa \u9a6c\uff0c\u800c\u4e14\u662f\u5230\u9065\u8fdc\u7684\u5730\u65b9\u53bb\u5192\u4e0d\u53ef\u77e5\u7684\u5371\u9669\uff0c\u8fd9\u4e43\u662f\u4e00\u4ef6\u5927\u4e8b\uff0c\u7406\u8be5\u5e26\u5f97\u529b\u7684\u540c\u95e8\u968f\u884c\u3002 \u3000\u3000\u4e00\u4e2a\u4ece\u672a\u51fa\u8fc7\u9053\u7684\u96cf\u513f\uff0c\u5373\u7b97\u8981\u5979\u5386\u7ec3\uff0c\u4e5f\u4e0d\u5b9c\u5c31\u8ba9\u5979\u53c2\u4e0e\u6b64\u7b49\u5927\u4e8b\u7684\u3002\u6c5f\u6d77\u5929\u867d\u7136\u5e74 \u8f7b\u8bc6\u6d45\uff0c\u4e5f\u60f3\u5230\u4e86\u8fd9\u4e00\u70b9\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u662f\u6c13\u5c71\u6d3e\u5185\u90e8\u7684\u4e8b\u60c5\uff0c\u4ed6\u7684\u813e\u6c14\u4e0e\u4ed6\u7236\u4eb2\u4e0d\u540c\u3002\u4e0d\u559c\u591a \u8a00\uff0c\u4e5f\u5c31\u4e0d\u518d\u95ee\u4e0b\u53bb\u4e86\uff0c\u53ea\u662f\u611f\u5230\u5fc3\u5934\u60c6\u6005\u3002 \u3000\u3000\u7a0b\u6d69\u8bf4\u9053\uff1a\u201c\u4e2d\u83b2\u7684\u8eab\u4e16\u662f\u6709\u4e9b\u53e4\u602a\uff0c\u4ee5\u524d\u66fe\u6709\u8fc7\u4ec0\u4e48\u7f2a\u592b\u4eba\u5192\u8ba4\u5979\u4f5c\u5973\u513f\uff0c\u5982\u4eca\u53c8\u6709 \u4ec0\u4e48\u91d1\u9e70\u5bab\u7684\u4e3b\u4eba\u7ed9\u5979\u9001\u6765\u8bf7\u5e16\uff0c\u63a5\u4e8c\u8fde\u4e09\uff0c\u8bb8\u591a\u7a00\u5947\u53e4\u602a\u7684\u4e8b\u60c5\uff0c\u90fd\u51fa\u5728\u5979\u7684\u8eab\u4e0a\u3002\u201d\u8bf4 \u5230\u6b64\u5904\uff0c\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u7740\u95ee\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8bf4\u5230\u8bf7\u5e16\uff0c\u8d24\u4f84\uff0c\u4f60\u63a5\u4e86\u90a3\u53ae\u7684\u8bf7\u5e16\uff0c\u53ef\u662f\u5f53\u771f\u60f3 \u5f80\u91d1\u9e70\u5bab\u53bb\u8d74\u5bb4\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e00\u8a00\u65e2\u51fa\uff0c\u9a77\u9a6c\u96be\u8ffd\u3002\u90a3\u53ae\u867d\u4e0d\u662f\u597d\u4eba\uff0c\u4f46\u6211\u65e2\u7b54\u5e94\u4e86\u4ed6\uff0c\u81ea\u8be5\u524d\u5f80\u3002\u201d \u3000\u3000\u5f53\u4e0b\u6c5f\u6d77\u5929\u5c31\u60f3\u628a\u90a3\u4e2a\u7ea2\u6f06\u6728\u5323\u6253\u5f00\uff0c\u53d6\u51fa\u8bf7\u5e16\u6765\u770b\uff0c\u767d\u82f1\u6770\u5ffd\u9053\uff1a\u201c\u8d24\u4f84\u4e14\u6162\uff01\u201d\u53eb \u6c5f\u6d77\u5929\u5c06\u5323\u5b50\u653e\u5728\u684c\u4e0a\uff0c\u4f17\u4eba\u90fd\u79bb\u5f00\u4e86\u5341\u591a\u6b65\uff0c\u767d\u82f1\u6770\u4e00\u626c\u624b\uff0c\u55d6\u7684\u4e00\u53e3\u98de\u5200\u5e73\u5c04\u51fa\u53bb\uff0c\u4ece \u90a3\u6728\u5323\u4e0a\u5212\u8fc7\uff0c\u521a\u597d\u628a\u90a3\u5323\u5b50\u5f53\u4e2d\u5256\u5f00\uff0c\u539f\u6765\u81ea\u82f1\u6770\u4e43\u662f\u4e2a\u8001\u6c5f\u6e56\uff0c\u4ed6\u6015\u5323\u5b50\u91cc\u85cf\u6709\u673a\u5173\u6697 \u5668\uff0c\u6545\u6b64\u4e0d\u6562\u8ba9\u6c5f\u6d77\u5929\u7528\u624b\u6765\u6253\u5f00\u5b83\u3002 \u3000\u3000\u5323\u5b50\u91cc\u5012\u6ca1\u6709\u4ec0\u4e48\u53e4\u602a\uff0c\u90a3\u8bf7\u5e16\u5374\u662f\u9887\u4e3a\u7279\u522b\uff0c\u6c5f\u6d77\u5929\u62ff\u8d77\u6765\u770b\uff0c\u53ea\u89c1\u8bf7\u5e16\u4e0a\u753b\u6709\u4e00\u53ea \u91d1\u8272\u5927\u9e70\uff0c\u795e\u6001\u51f6\u731b\uff0c\u6b63\u5f20\u5f00\u722a\u6293\u5730\u4e0b\u7684\u4e00\u6761\u58a8\u9f99\u3002\u5e16\u4e0a\u6709\u4e24\u884c\u6587\u5b57\uff0c\u4e00\u884c\u662f\u6c49\u6587\uff0c\u5199\u7684 \u662f\uff1a\u51ed\u6b64\u8bf7\u5e16\uff0c\u516b\u6708\u5341\u4e94\uff0c\u5165\u5bab\u8d74\u5bb4\u3002\u53e6\u4e00\u884c\u5374\u4e0d\u77e5\u662f\u4ec0\u4e48\u6587\u5b57\uff0c\u5f2f\u5f2f\u66f2\u66f2\u7684\u4f3c\u86af\u8693\u4e00\u822c\uff0c \u8c01\u90fd\u4e0d\u8ba4\u5f97\u3002\u4e0d\u8fc7\uff0c\u60f3\u6765\u5f53\u662f\u4e0e\u90a3\u884c\u6c49\u6587\u540c\u4e00\u610f\u601d\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u7684\u5e08\u5ac2\u8fa3\u624b\u4ed9\u5a18\u8c22\u4e91\u771f\u6070\u4e5f\u5728\u573a\uff0c\u5f53\u5e74\u90a3\u7f2a\u592b\u4eba\u4e0a\u5c71\u7d22\u5973\uff0c\u575a\u6301\u8981\u89c1\u8c37\u4e2d\u83b2\uff0c\u5c31 \u662f\u8c22\u4e91\u771f\u5c06\u8c37\u4e2d\u83b2\u62b1\u51fa\u6765\u89c1\u5979\u7684\u3002\u540e\u6765\u7f2a\u592b\u4eba\u6293\u88c2\u8c37\u4e2d\u83b2\u90a3\u4ef6\u68c9\u8884\uff0c\u9732\u51fa\u4e86\u5185\u85cf\u7684\u7f8a\u76ae\u4e66\uff0c \u8c22\u4e91\u771f\u867d\u672a\u8be6\u89c2\uff0c\u4e5f\u66fe\u8fc7\u76ee\uff0c\u8fd9\u65f6\u5ffd\u7136\u53eb\u4e86\u8d77\u6765\uff0c\u539f\u6765\u7f8a\u76ae\u4e66\u4e0a\u7684\u6587\u5b57\uff0c\u4e0e\u8bf7\u5e16\u4e0a\u8fd9\u884c\u602a \u5b57\uff0c\u5b57\u4f53\u5341\u5206\u76f8\u4f3c\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u89e6\u76ee\u60ca\u5fc3\u601d\u5f80\u4e8b\uff0c\u5b64\u96cf\u8eab\u4e16\u73b0\u7aef\u502a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000--------------- \u3000\u3000\u626c\u5251\u8f69\u5c45\u58eb \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u5341\u56db\u56de\u3000\u60ca\u5fc3\u602a\u5ba2\u4f20\u4e66\u67ec\u3000\u5b70\u6599\u5a07\u5a03\u662f\u8d3c\u5f92 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u5341\u56db\u56de\u3000\u60ca\u5fc3\u602a\u5ba2\u4f20\u4e66\u67ec\u3000\u5b70\u6599\u5a07\u5a03\u662f\u8d3c\u5f92 \u3000\u3000\u767d\u82f1\u6770\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u5e08\u5ac2\uff0c\u4f60\u53ef\u662f\u5728\u8fd9\u8bf7\u67ec\u4e0a\u770b\u51fa\u4e86\u4ec0\u4e48\uff1f\u201d\u8c22\u4e91\u771f\u9053\uff1a\u201c\u8fd9\u4e9b\u602a\u5b57\u6211 \u4e00\u4e2a\u4e5f\u4e0d\u8ba4\u5f97\uff0c\u4f46\u6211\u53ef\u65ad\u5b9a\uff0c\u8fd9\u548c\u83b2\u513f\u68c9\u8884\u4e2d\u6240\u85cf\u7684\u7f8a\u76ae\u4e66\uff0c\u5fc5\u662f\u540c\u5c5e\u4e00\u79cd\u6587\u5b57\u3002\u201d\u7a0b\u6d69\u8be7 \u9053\uff1a\u201c\u4ec0\u4e48\u7f8a\u76ae\u4e66\uff0c\u6211\u600e\u4e48\u4ece\u672a\u542c\u638c\u95e8\u5e08\u59b9\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u8c22\u4e91\u771f\u8fd9\u624d\u60f3\u8d77\uff0c\u8c37\u4e4b\u534e\u66fe\u5631\u5490\u8fc7\u5979\uff0c\u53eb\u5979\u4e0d\u8981\u5c06\u8c37\u4e2d\u83b2\u90a3\u4ef6\u53e4\u602a\u7684\u68c9\u8884\u7684\u79d8\u5bc6\u5411\u522b\u4eba \u6cc4\u9732\uff0c\u4f46\u73b0\u5728\u5df2\u4e0d\u614e\u8bf4\u4e86\u51fa\u6765\uff0c\u518d\u60f3\u53cd\u6b63\u90fd\u662f\u540c\u95e8\u7684\u5e08\u5144\u5f1f\uff0c\u8bf4\u4e00\u4e9b\u4e0d\u8bf4\u4e00\u4e9b\u90a3\u66f4\u4e0d\u597d\uff0c\u4fbf \u7d22\u6027\u628a\u81ea\u5df1\u6240\u77e5\uff0c\u5168\u90e8\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u767d\u82f1\u6770\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u638c\u95e8\u5e08\u59b9\u53ea\u5e26\u83b2\u513f\u968f\u884c\u3001\u60f3\u6765\u662f\u8d81\u6b64\u6b21\u585e\u5916\u4e4b\u884c\uff0c\u987a\u4fbf\u8bbf\u67e5\u83b2\u513f\u7684 \u8eab\u4e16\u3002\u53ef\u60dc\u8fd9\u5f20\u8bf7\u67ec\u6765\u5f97\u592a\u8fdf\uff0c\u8981\u4e0d\u7136\u5012\u53ef\u4f9b\u5979\u4e00\u4e2a\u7ebf\u7d22\u3002\u4f9d\u6211\u770b\u6765\uff0c\u83b2\u513f\u7684\u8eab\u4e16\u4e5f\u5f88\u53ef\u80fd \u5c31\u4e0e\u90a3\u4e2a\u4ec0\u4e48\u91d1\u9e70\u5bab\u7684\u4e3b\u4eba\u6709\u5173\u3002\u201d \u3000\u3000\u7a0b\u6d69\u4f5c\u4e8b\u7d20\u6765\u614e\u91cd\uff0c\u60f3\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u524d\u6765\u9001\u5e16\u7684\u8fd9\u4eba\u4e0d\u8fc7\u662f\u4e2a\u4ec6\u4eba\uff0c\u672c\u4e8b\u5df2\u7136\u8fd9\u4e48 \u4e86\u5f97\uff0c\u90a3\u91d1\u9e70\u5bab\u7684\u4e3b\u4eba\u81ea\u662f\u66f4\u4e0d\u53ef\u5c0f\u89d1\u3002\u6c5f\u8d24\u4f84\uff0c\u4f60\u7684\u6b66\u529f\u867d\u5f3a\uff0c\u4f46\u4e00\u5251\u5355\u8eab\uff0c\u6df1\u4eba\u864e\u7a74\uff0c \u7a76\u5c5e\u5371\u9669\uff0c\u6211\u60f3\u628a\u6211\u7684\u7ffc\u5e08\u5144\u8bf7\u6765\uff0c\u966a\u4f60\u540c\u5f80\uff0c\u4f60\u610f\u4e0b\u5982\u4f55\uff1f\u201d\u7a0b\u6d69\u7684\u201c\u7ffc\u5e08\u5144\u201d\u5373\u662f\u5357\u4e10 \u5e2e\u7684\u5e2e\u4e3b\u7ffc\u4ef2\u725f\uff0c\u66f9\u9526\u513f\u6b7b\u540e\u3002\u9099\u5c71\u6d3e\u4ee5\u4ed6\u7684\u8f88\u4efd\u6700\u5c0a\uff0c\u8c37\u4e4b\u534e\u4e5f\u8981\u65f6\u5e38\u5411\u4ed6\u8bf7\u6559\u7684\u3002\u800c\u4e14 \u4ed6\u4ea4\u6e38\u904d\u5929\u4e0b\uff0c\u5404\u5730\u53c8\u90fd\u6709\u4e10\u5e2e\u5f1f\u5b50\uff0c\u53ef\u901a\u6d88\u606f\uff0c\u82e5\u6709\u7ffc\u4ef2\u725f\u966a\u540c\u524d\u5f80\uff0c\u4e8b\u4e8b\u65b9\u4fbf\uff0c\u5904\u5904\u6709 \u4eba\uff0c\u90a3\u5f53\u7136\u662f\u7a33\u59a5\u591a\u4e86\u3002 \u3000\u3000\u53ef\u662f\u6c5f\u6d77\u5929\u4e00\u6765\u6025\u4e8e\u5bfb\u7236\uff0c\u4e8c\u6765\u4ed6\u4e5f\u60f3\u65e9\u65e5\u63ed\u7834\u8c37\u4e2d\u83b2\u8eab\u4e16\u4e4b\u8c1c\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u8bf7\u5e16\u4e0a \u7684\u65e5\u671f\u662f\u4eca\u5e74\u4e2d\u79cb\u8282\uff0c\u867d\u7136\u8ddd\u4eca\u8fd8\u6709\u4e94\u4e2a\u591a\u6708\uff0c\u4f46\u8def\u9014\u9065\u8fdc\uff0c\u96be\u4fdd\u8def\u4e0a\u6ca1\u6709\u4e00\u4e9b\u803d\u6401\uff0c\u4f84\u513f \u7b2c\u4e00\u6b21\u51fa\u9053\uff0c\u4e0b\u60f3\u5931\u4fe1\u4e8e\u4eba\uff0c\u7a0b\u4f2f\u4f2f\u7684\u597d\u610f\u6211\u5fc3\u9886\u4e86\u3002\u800c\u4e14\u6211\u8fd9\u6b21\u7684\u8def\u7a0b\u662f\u51c6\u5907\u5148\u5230\u5ff5\u9752\u5510 \u53e4\u62c9\u5c71\u62dc\u8c12\u5510\u7ecf\u5929\u4f2f\u6015\uff0c\u7136\u540e\u5230\u706d\u5c71\u62dc\u8c12\u4ed6\u7684\u7236\u4eb2\u5510\u6653\u6f9c\u5510\u8001\u524d\u8f88\uff0c\u542c\u8bf4\u963f\u5c14\u6cf0\u5c71\u662f\u5728\u65b0\u7586 \u5317\u8fb9\u4e0e\u8499\u53e4\u63a5\u5883\u4e4b\u5904\uff0c\u65e2\u7136\u91d1\u9e70\u5bab\u5c31\u5728\u963f\u5c14\u6cf0\u5c71\u811a\u535c\u7684\u9a6c\u8428\u513f\u76df\uff0c\u6211\u5230\u4e86\u5929\u5c71\u4e4b\u540e\uff0c\u6b63\u597d\u987a \u9053\u524d\u5f80\u3002\u201d \u3000\u3000\u767d\u82f1\u6770\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u5510\u8001\u524d\u8f88\u5bf9\u65b0\u7586\u3001\u897f\u85cf\u3001\u8499\u53e4\u5404\u5730\u7684\u5c71\u5ddd\u4eba\u7269\u90fd\u6781\u719f\u6089\uff0c\u4f60\u95ee\u95ee \u4ed6\uff0c\u6216\u8005\u4ed6\u4f1a\u77e5\u9053\u91d1\u9e70\u5bab\u4e3b\u4eba\u7684\u6765\u5386\u3002\u82e5\u5f97\u5510\u5927\u4fa0\u52a9\u4f60\uff0c\u90a3\u53c8\u80dc\u8fc7\u54b1\u4eec\u7684\u7ffc\u5e08\u5144\u4e86\u3002\u201d \u3000\u3000\u7a0b\u6d69\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u4f60\u8981\u5982\u671f\u8d76\u5230\uff0c\u6211\u4e5f\u4e0d\u4fbf\u7559\u4f60\u5728\u6b64\u7b49\u5019\u7ffc\u5e08\u5144\u4e86\u3002\u8bf4\u6765\u60ed\u6127\uff0c\u963f\u5c14\u6cf0 \u5c71\u7ef5\u4e98\u6570\u5343\u91cc\uff0c\u9a6c\u8428\u513f\u76df\u5728\u963f\u5c14\u6cf0\u5c71\u811a\u535c\u7684\u54ea\u4e00\u4e2a\u89d2\u843d\uff0c\u6211\u4eec\u4e5f\u6839\u672c\u4e0d\u77e5\u5462\u3002\u4f60\u786e\u662f\u975e\u5f97\u719f \u4eba\u6307\u5f15\u4e0d\u884c\u3002\u82e5\u662f\u5510\u5927\u4fa0\u4e0d\u4fbf\u52b3\u70e6\uff0c\u4f60\u8bf7\u5510\u7ecf\u5929\u592b\u5987\u540c\u5f80\uff0c\u60f3\u6765\u4e5f\u8db3\u4ee5\u5bf9\u4ed8\u90a3\u91d1\u9e70\u5bab\u4e3b\u4eba \u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5728\u7384\u5973\u89c2\u4f4f\u4e86\u4e00\u665a\uff0c\u7b2c\u4e8c\u5929\u4fbf\u5373\u52a8\u8eab\uff0c\u4e34\u884c\u7684\u65f6\u5019\uff0c\u7a0b\u6d69\u53c8\u5bf9\u4ed6\u8bf4\u9053\uff1a\u201c\u8d24\u4f84\uff0c \u591a\u8c22\u4f60\u8fd9\u6b21\u5e2e\u4e86\u6211\u4eec\u7684\u5927\u5fd9\uff0c\u4f46\u8fd9\u662f\u6211\u4eec\u9099\u5c71\u6d3e\u7684\u4e8b\u60c5\uff0c\u6211\u4eec\u4e5f\u51b3\u4e0d\u80fd\u7f6e\u4e4b\u4e0d\u7406\u3002\u4f60\u5148\u8d70\u4e00 \u6b65\uff0c\u6211\u4eec\u968f\u540e\u5c31\u4f1a\u6709\u4eba\u6765\u7684\u3002\u201d \u3000\u3000\u539f\u6765\u7a0b\u6d69\u6628\u665a\u5df2\u6d3e\u51fa\u5f97\u529b\u5f1f\u5b50\uff0c\u8fde\u591c\u4e0b\u5c71\uff0c\u9a70\u4e66\u7980\u62a5\u7ffc\u5fe1\u725f\uff0c\u8bf7\u4ed6\u4e3b\u6301\u5927\u8ba1\uff0c\u5e76\u8bf7\u4ed6\u7528 \u98de\u9e3d\u4f20\u4e66\uff0c\u901a\u77e5\u6cbf\u9014\u7684\u4e10\u5e2e\u5f1f\u5b50\uff0c\u6697\u4e2d\u7167\u6599\u6c5f\u6d77\u5929\u3002\u4e0d\u8fc7\u7a0b\u6d69\u77e5\u9053\u5c11\u5e74\u4eba\u7684\u813e\u6c14\uff0c\u5c11\u5e74\u4eba\u672a \u7ecf\u4e16\u6545\uff0c\u5374\u53c8\u5927\u90fd\u6015\u522b\u4eba\u76ee\u4e3a\u5e7c\u7a1a\uff0c\u4e0d\u6b22\u559c\u522b\u4eba\u8bf4\u8981\u7279\u53e6\u68da\u987e\u4ed6\u7684\uff0c\u6240\u4ee5\u7a0b\u6d69\u7684\u5b89\u6392\uff0c\u4e5f\u672a \u66fe\u5bf9\u6c5f\u6d77\u5929\u660e\u8bf4\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6000\u7740\u51e0\u5206\u60c6\u6005\u7684\u5fc3\u60c5\uff0c\u51e0\u5206\u5bf9\u672a\u6765\u7684\u5e7b\u60f3\uff0c\u79bb\u5f00\u4e86\u9099\u5c71\uff0c\u5954\u5411\u90a3\u795e\u79d8\u7684\u9065\u8fdc\u7684\u7ea6 \u4f1a\u5730\u65b9\u3002\u8fd9\u4e00\u53bb\u80fd\u591f\u518d\u89c1\u5230\u8c37\u4e2d\u83b2\u5417\uff1f\u80fd\u591f\u63ed\u5f00\u5979\u8eab\u4e16\u7684\u79d8\u5bc6\u5417\uff1f\u4ed6\u4e00\u8def\u5fc3\u4e8b\u5982\u9ebb\uff0c\u65e2\u62b1\u7740 \u671f\u671b\uff0c\u53c8\u5145\u6ee1\u5174\u594b\u3002 \u3000\u3000\u4ed6\u4e0b\u4e86\u90b1\u5c71\uff0c\u5728\u65b0\u5b89\u9547\u4e0a\u4e70\u4e86\u4e00\u5339\u9a6c\u3002\u4fbf\u9a6c\u4e0d\u505c\u8e44\u7684\u76f4\u5411\u4e24\u884c\uff0c\u4e0d\u5230\u4e00\u4e2a\u6708\uff0c\u4fbf\u5df2\u4ece\u5c71 \u4e1c\u7a7f\u8fc7\u6cb3\u5317\uff0c\u5230\u4e86\u5c71\u897f\u5883\u5185\u3002 \u3000\u3000\u8fd9\u4e00\u4eec\uff0c\u4ed6\u4e3a\u4e86\u8d76\u8def\uff0c\u9519\u8fc7\u5bbf\u5934\uff0c\u5df2\u662f\u66ae\u972d\u82cd\u832b\u7684\u65f6\u5019\uff0c\u8fd8\u627e\u4e0d\u5230\u4eba\u5bb6\uff0c\u6b63\u5728\u8352\u91ce\u4e0a\u9a70 \u9a71\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u5c16\u9510\u7684\u53eb\u58f0\uff0c\u5212\u7834\u4e86\u8352\u91ce\u7684\u5bc2\u9759\u3002 \u3000\u3000\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u62ab\u5934\u6563\u53d1\u7684\u5c11\u5973\uff0c\u4ece\u524d\u9762\u614c\u614c\u5f20\u5f20\u5730\u8dd1\u6765\uff0c\u8863\u670d\u534e\u4e3d\uff0c\u4f3c\u662f\u4e00\u4e2a\u5bcc \u5bb6\u5973\u5b50\uff0c\u4f46\u4e0a\u4e0b\u8863\u88f3\uff0c\u5df2\u88ab\u52fe\u78b4\u4e86\u8bb8\u591a\u5904\uff0c\u663e\u89c1\u90a3\u662f\u56e0\u4e3a\u4ed3\u7687\u9003\u547d\uff0c\u987e\u4e0d\u5f97\u7ed9\u8346\u68d8\u52fe\u7834\u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5973\u4e00\u89c1\u6709\u4eba\uff0c\u4fbf\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u6551\u547d\u5440\uff0c\u6551\u547d\uff01\u201d\u6c5f\u6d77\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u8df3\u4e0b\u9a6c\u6765\uff0c\u95ee \u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff0c\u59d1\u5a18\uff0c\u6709\u4ec0\u4e48\u4eba\u8981\u5bb3\u4f60\u5417\uff1f\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u6025\u4fc3\u7684\u9a6c\u8def\u58f0\uff0c\u6709\u5982\u96e8\u6253\u82ad\u8549\uff0c\u5df2\u662f\u81ea\u8fdc\u800c\u8fd1\u3002\u90a3\u5c11\u5973\u53eb\u9053\uff1a\u201c\u5f3a \u76d7\uff0c\u5f3a\u76d7\u62a2\u4eba\uff01\u6551\u547d\u5440\uff0c\u6551\u547d\uff01\u201d \u3000\u3000\u8f6c\u773c\u95f4\uff0c\u4f46\u89c1\u4e09\u9a91\u5065\u9a6c\uff0c\u5df2\u662f\u51b2\u8fc7\u571f\u5c97\uff0c\u4e00\u4e2a\u559d\u9053\uff1a\u201c\u770b\u4f60\u8dd1\u5f97\u4e0a\u5929\uff1f\u201d\u4e00\u4e2a\u563b\u76ae\u7b11\u8138 \u5730\u53eb\u9053\uff0c\u201c\u5c0f\u4e56\u4e56\uff0c\u8fd8\u662f\u8ddf\u6211\u56de\u53bb\u4eab\u798f\u5427\uff01\u201d\u53c8\u4e00\u4e2a\u9053\uff1a\u201c\u54fc\uff0c\u5c0f\u9a9a\u8d27\uff0c\u8dd1\u5230\u8fd9\u91cc\u4f1a\u60c5\u90ce \u5417\uff1f\u201d\u8fd9\u4e09\u4e58\u9a91\u5ba2\uff0c\u90fd\u662f\u7c97\u7709\u5927\u773c\uff0c\u8138\u8089\u6a2a\u751f\uff0c\u4e00\u770b\u4fbf\u77e5\u4e0d\u662f\u5584\u7c7b\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e0d\u7531\u5f97\u52c3\u7136\u5927\u6012\uff0c\u6717\u58f0\u559d\u9053\uff0c\u201c\u72d7\u5f3a\u76d7\uff0c\u767d\u65e5\u9752\u5929\u3002\u7adf\u6562\u62a2\u4eba\uff01\u201d\u968f\u624b\u62fe\u8d77\u4e09\u5757 \u77f3\u5934\uff0c\u7528\u8fde\u73e0\u624b\u6cd5\u6253\u51fa\uff0c\u76f8\u8ddd\u8fd8\u6709\u5341\u591a\u4e08\u8fdc\uff0c\u4f46\u4ed6\u8fd0\u8db3\u4e86\u5185\u52b2\uff0c\u4e09\u5757\u77f3\u5934\u90fd\u6253\u4e2d\u4e86\u654c\u4eba\uff0c\u53ea \u89c1\u524d\u9762\u90a3\u4e24\u4e2a\u6c49\u5b50\u8dcc\u4e0b\u9a6c\u6765\uff0c\u540e\u9762\u90a3\u4e2a\u6c49\u5b50\uff0c\u56e0\u4e3a\u8ddd\u79bb\u6821\u8fdc\uff0c\u4f3c\u4e4e\u8fd8\u6328\u5f97\u8d77\uff0c\u62e8\u8f6c\u9a6c\u5934\u4fbf\u8dd1\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u8981\u8dd1\u571f\u524d\u53bb\uff0c\u5c06\u90a3\u4e24\u4eba\u6d3b\u6349\uff0c\u5ffd\u542c\u5f97\u53c8\u662f\u4e00\u58f0\u5c16\u53eb\uff0c\u5165\u8033\u94bb\u5fc3\uff0c\u5c31\u4f3c\u7ed9\u4eba\u523a\u4e86 \u4e00\u5200\u90a3\u822c\u7684\u60e8\u53eb\uff0c\u6c5f\u6d77\u5927\u56de\u5934\u671b\u65f6\uff0c\u53ea\u89c1\u90a3\u5c11\u5973\u6454\u5012\u5730\u535c\uff0c\u8863\u8896\u4e00\u7247\u6bb7\u7ea2\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5413\u4e86\u4e00\u8df3\uff0c\u5fc3\u60f3\u6551\u4eba\u8981\u7d27\u3002\u53ea\u597d\u8ba9\u90a3\u4e24\u4e2a\u5f3a\u76d7\u9003\u8dd1\u3002\u8f6c\u8fc7\u8eab\u6765\u6276\u90a3\u5c11\u5973\uff0c\u95ee\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u90a3\u5c11\u5973\u6323\u624e\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u624d\u7ffb\u8f6c\u8eab\u6765\uff0c\u8ba9\u6c5f\u6d77\u5929\u8f7b\u8f7b\u5c06\u5979\u6276\u8d77\uff0c\u53c8\u8fc7 \u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u624d\u5a07\u58f0\u7ec6\u7ec6\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u7ed9\u77f3\u5b50\u7eca\u4f4f\u4e86\uff0c\u8dcc\u4e86\u4e00\u8de4\uff0c\u591a\u8c22\u4f60\u5566\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7b2c\u4e00\u6b21\u548c\u5973\u5b50\u63a5\u89e6\uff0c\u4e0d\u514d\u6709\u70b9\u5bb3\u7f9e\uff0c\u8fd9\u65f6\u65b9\u59cb\u6b63\u9762\u770b\u5979\uff0c\u53ea\u89c1\u5979\u67f3\u53f6\u53cc\u7709\uff0c\u6a31\u6843 \u5c0f\u5634\uff0c\u74dc\u5b50\u8138\u513f\uff0c\u957f\u5f97\u5012\u9887\u4e3a\u79c0\u6c14\uff0c\u8138\u4e0a\u8eab\u4e0a\u90fd\u6ca1\u6709\u4f24\u75d5\uff0c\u53ea\u662f\u624b\u8155\u4e0a\u6709\u4e00\u6761\u6de1\u8c08\u7684\u8840\u75d5\uff0c \u60f3\u662f\u521a\u624d\u7ed9\u950b\u5229\u7684\u77f3\u5b50\u5212\u7834\u7684\u3002\u6c5f\u6d77\u5929\u672c\u4ee5\u4e3a\u5979\u5df2\u662f\u53d7\u4e86\u91cd\u4f24\u7684\uff0c\u54ea\u77e5\u4ec5\u4ec5\u662f\u6454\u4e86\u4e00\u8de4\uff0c\u53d7 \u4e86\u4e00\u70b9\u70b9\u76ae\u8089\u635f\u7834\u7684\u8f7b\u4f24\uff0c\u4ed6\u653e\u4e0b\u4e86\u5fc3\u4e0a\u7684\u77f3\u5934\uff0c\u4f46\u540c\u65f6\u4ea6\u89c9\u5f97\u6709\u51e0\u5206\u9057\u61be\uff1a\u90a3\u51e0\u4e2a\u5f3a\u76d7\u65e9 \u5df2\u8dd1\u5f97\u65e0\u8e2a\u65e0\u5f71\u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5973\u8fd8\u5728\u5a07\u5598\u5401\u5401\uff0c\u96ea\u96ea\u547c\u75db\uff0c\u6c5f\u6d77\u5929\u6697\u6697\u76b1\u7709\uff0c\u5fc3\u9053\uff1a\u201c\u771f\u662f\u5a07\u751f\u60ef\u517b\u7684\u5c0f\u59d0\u3002\u201d \u65e0\u53ef\u5948\u4f8b\uff0c\u53ea\u597d\u638f\u51fa\u91d1\u5228\u836f\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u7528\u5bb3\u6015\uff0c\u8fd9\u70b9\u4f24\u4e0d\u8981\u6025\u7684\uff0c\u6211\u7ed9\u4f60\u6577\u4e0a\u4e86 \u836f\uff0c\u5c31\u4f1a\u597d\u4e86\u3002\u201d\u90a3\u5c11\u5973\u7d27\u9760\u7740\u4ed6\uff0c\u6c5f\u6d77\u5929\u542c\u5f97\u5979\u7684\u5fc3\u201c\u535c\u535c\u201d\u7684\u8df3\uff0c\u6c5f\u6d77\u5929\u8eab\u4e8e\u632a\u5f00\u4e86 \u4e9b\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e5f\u602a\u4e0d\u5f97\u5979\uff0c\u5979\u88ab\u5f3a\u76d7\u8ffd\u9010\uff0c\u867d\u672a\u53d7\u4f24\uff0c\u4e5f\u5413\u6b7b\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7ed9\u5979\u88f9\u597d\u4e86\u4f24\uff0c\u90a3\u5c11\u5973\u68c0\u887d\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u5566\uff0c\u8f9b\u4e8f\u78b0\u89c1\u4e86\u4f60\u3002\u60f3\u4e0d\u5230\u4f60 \u6709\u8fd9\u822c\u672c\u4e8b\uff0c\u5c06\u5f3a\u76d7\u90fd\u6253\u8dd1\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u95ee\u9053\uff0c\u201c\u59d1\u5a18\u59d3\u751a\u540d\u8c01\uff0c\u5bb6\u4f4f\u4f55\u65b9\uff0c\u600e\u7684\u72ec\u81ea\u4e00 \u4eba\uff0c\u5728\u8fd9\u8352\u91ce\u4e0a\u88ab\u5f3a\u76d7\u8ffd\u9010\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u5c0f\u5973\u5b50\u590d\u59d3\u6b27\u9633\uff0c\u5355\u540d\u4e00\u4e2a\u5a49\u5b57\u3002\u5bb6\u7236\u662f\u592a\u539f\u77e5\u5e9c\uff0c\u53bb\u5e74\u624d\u4e0a\u4efb\u7684\uff0c\u6211\u539f \u7c4d\u6cb3\u5317\u4fdd\u5b9a\uff0c\u4e0a\u6708\u5bb6\u7236\u6d3e\u4eba\u63a5\u6211\u6bcd\u5973\u5f80\u4ed6\u4efb\u6240\uff0c\u60f3\u4e0d\u5230\u4e2d\u9014\u9047\u76d7\uff0c\u5bb6\u4e01\u88ab\u6740\uff0c\u6bcd\u5973\u906d\u64d2\uff0c\u6628 \u5929\u88ab\u5173\u5728\u90a3\u8fb9\u5c71\u4e0a\u7684\u4e00\u4e2a\u6728\u68da\u5b50\u91cc\uff0c\u542c\u5f97\u90a3\u4e9b\u5f3a\u76d7\u5546\u8bae\uff0c\u8bf4\u8981\u628a\u6211\u732e\u7ed9\u4ed6\u7684\u5927\u738b\u505a\u4ec0\u4e48\uff0c\u505a \u4ec0\u4e48\u2026\u2026\u54ce\u5440\uff0c\u505a\u4ec0\u4e48\u538b\u5be8\u592b\u4eba\u3002\u660e\u5929\u4fbf\u8981\u62bc\u89e3\u6211\u4eec\u5230\u5927\u5be8\u53bb\u3002\u6211\u4e0d\u7518\u53d7\u8fb1\uff0c\u5f3a\u76d7\u4eec\u52a8\u5f97\u8d22 \u8d27\uff0c\u7f6e\u9152\u5e86\u8d3a\uff0c\u6211\u8d81\u7740\u4ed6\u4eec\u559d\u5f97\u916a\u914a\u5927\u9189\u4e4b\u65f6\uff0c\u6084\u6084\u9003\u8d70\uff0c\u6211\u6bcd\u4eb2\u6162\u4e86\u4e00\u6b65\uff0c\u7ed9\u4ed6\u4eec\u6349\u56de\uff0c \u6211\u5192\u9669\u4ece\u5c71\u5761\u4e0a\u6eda\u4e0b\uff0c\u533f\u4f0f\u8349\u95f4\uff0c\u4ee5\u4e3a\u53ef\u4ee5\u9003\u8fc7\uff0c\u53ef\u6068\u8fd9\u4e9b\u5929\u6740\u7684\u5f3a\u76d7\u4ecd\u7136\u4fa6\u9a91\u56db\u51fa\uff0c\u7a77\u8ffd \u4e0d\u820d\uff0c\u4fa5\u5e78\u5728\u8fd9\u91cc\u78b0\u4e0a\u4e86\u4f60\u6551\u547d\u6069\u4eba\uff01\u201d\u8fd9\u5c11\u5973\u7684\u8bf4\u8bdd\uff0c\u672c\u6765\u6709\u8bb8\u591a\u7834\u7efd\uff0c\u4f46\u6c5f\u6d77\u5929\u6beb\u65e0\u6c5f \u6e56\u7ecf\u9a8c\uff0c\u542c\u6765\u5374\u89c9\u5f97\u5408\u60c5\u5408\u7406\uff0c\u4e1d\u6beb\u6ca1\u6709\u8d77\u7591\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5979\u662f\u4e00\u4e2a\u5f31\u8d28\u5a07\u5a03\uff0c\u4e3a\u4e86\u4e0d\u7518\u53d7\u8fb1\uff0c\u7adf\u6709\u8fd9\u822c\u80c6\u91cf\u5192\u9669\u4ece\u864e\u7a74\u4e2d\u9003 \u51fa\u6765\uff0c\u5012\u662f\u53ef\u656c\u53ef\u4f69\u3002\u4f46\u5982\u4f55\u5b89\u7f6e\u5979\uff0c\u8fd9\u5374\u6559\u6211\u4e3a\u96be\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5df2\u662f\u5915\u9633\u843d\u5c71\uff0c\u5929\u5c06\u5165\u9ed1\u7684\u65f6\u5206\uff0c\u6c5f\u6d77\u5929\u56db\u987e\u82cd\u832b\uff0c\u5927\u662f\u8e0c\u8e87\uff0c\u90a3\u5c11\u5973\u5ffd\u7136\u8dea\u4e86\u4e0b \u6765\uff0c\u53eb\u4e86\u4e24\u58f0\u201c\u6069\u516c\u201d\uff0c\u6cea\u6c34\u6c6a\u6c6a\u5730\u671b\u7740\u6c5f\u6d77\u5929\u3002\u6c5f\u6bcf\u5929\u8fde\u5fd9\u5c06\u5979\u6276\u8d77\u9053\uff1a\u201c\u6709\u8bdd\u597d\u8bf4\uff0c\u4f55 \u5fc5\u5982\u6b64\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u9053\uff1a\u201c\u6211\u6015\uff0c\u6211\u6015\u2026\u2026\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8d3c\u4eba\u90fd\u5df2\u6253\u8dd1\u4e86\uff0c\u8fd8\u6015\u4ec0\u4e48\uff1f\u201d\u6b27\u9633\u5a49 \u9053\uff1a\u201c\u8d3c\u515a\u4f17\u591a\uff0c\u96be\u4fdd\u4e0d\u4f1a\u518d\u6765\u3002\u6211\u5f97\u6069\u516c\u6551\u4e86\u6027\u547d\uff0c\u672c\u4e0d\u6562\u518d\u7d2f\u6069\u516c\uff0c\u53ea\u662f\u6211\u5b64\u5355\u4e00\u4eba\uff0c \u600e\u80fd\u5230\u5f97\u592a\u539f\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u4e71\u5982\u9ebb\uff0c\u53ea\u5f97\u95ee\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u60f3\u6211\u9001\u4f60\u5230\u592a\u539f\u53bb\u4e48\uff1f\u201d\u6b27\u9633\u5a49\u9053\uff0c\u201c\u6211\u82e5 \u5f97\u7236\u5973\u56e2\u5706\uff0c\u51b3\u4e0d\u4f1a\u5fd8\u4e86\u6069\u516c\u7684\u597d\u5904\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6b64\u5904\u79bb\u4eba\u539f\u591a\u8fdc\uff1f\u201d\u6b27\u9633\u5a49\u9053\uff1a\u201c\u6211 \u4e5f\u4e0d\u77e5\uff0c\u4f46\u6211\u6628\u65e5\u9047\u76d7\u4e4b\u5904\uff0c\u9ad8\u5927\u539f\u662f\u4e09\u5929\u8def\u7a0b\u3002\u6211\u9003\u51fa\u6765\u4e0d\u8fa8\u65b9\u5411\uff0c\u8981\u662f\u65b9\u5411\u5bf9\u7684\uff0c\u540e\u5929 \u5c31\u80fd\u5230\u4e86\u3002\u592a\u539f\u662f\u5728\u897f\u8fb9\u3002\u201d \u3000\u3000\u65b0\u6708\u4ece\u5c71\u95f4\u5347\u8d77\uff0c\u6c5f\u6d77\u5929\u9762\u5411\u6708\u4eae\uff0c\u8bf4\u9053\uff0c\u201c\u65b9\u5411\u5012\u662f\u5bf9\u4e86\u3002\u4f46\u6211\u4e0d\u80fd\u9001\u4f60\u5230\u4f60\u7236\u4eb2\u7684 \u8859\u95e8\uff0c\u4eca\u543b\u54b1\u4eec\u6682\u4e14\u627e\u4e00\u5904\u4eba\u5bb6\u6743\u4f4f\u4e00\u665a\uff0c\u660e\u5929\u6211\u7ed9\u4f60\u96c7\u4e00\u8f86\u9aa1\u8f66\uff0c\u9001\u4f60\u5230\u592a\u539f\u57ce\u8fb9\uff0c\u6211\u4fbf \u8981\u8d70\u4e86\u3002\u201d \u3000\u3000\u6b27\u9633\u5a49\u559c\u9053\uff1a\u201c\u4f46\u5f97\u5982\u6b64\uff0c\u5982\u613f\u5df2\u8db3\u3002\u53ea\u662f\u672a\u80fd\u62a5\u7b54\u5927\u6069\uff0c\u5fc3\u5b9e\u4e0d\u5b89\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a \u201c\u8fd9\u662f\u6211\u7406\u6240\u5f53\u4e3a\u7684\u4e8b\u60c5\uff0c\u4f60\u4e0d\u7528\u9053\u8c22\uff0c\u6211\u4e5f\u51b3\u4e0d\u671b\u4f60\u62a5\u7b54\u3002\u8fd8\u6709\uff0c\u8bf7\u4f60\u4e0d\u8981\u65e5\u65e5\u58f0\u58f0\u53eb\u6211 \u6069\u516c\uff0c\u6211\u59d3\u6c5f\u3002\u8bf7\u4e0a\u9a6c\u5427\uff01\u201d \u3000\u3000\u6b27\u9633\u5a49\u9053\uff1a\u201c\u55ef\u2026\u2026\uff0c\u6c5f\uff0c\u6c5f\u76f8\u516c\uff0c\u6211\uff0c\u6211\u4e0d\u4f1a\u9a91\u9a6c\u3002\u201d\u6c5f\u6d77\u5929\u5927\u662f\u4e3a\u96be\uff0c\u5fc3\u91cc\u6b63\u9053\uff1a \u201c\u8fd9\u600e\u4e48\u529e\uff1f\u201d\u53ea\u542c\u5f97\u6b27\u9633\u5a49\u9053\uff1a\u201c\u6211\u3001\u6211\u4e5f\u8d70\u4e0d\u52a8\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u60f3\uff1a\u201c\u6551\u4eba\u8981\u7d27\u3002\u53ea\u597d\u4e0d\u907f\u5acc\u7591\u4e86\u3002\u201d\u6168\u7136\u4fbf\u9053\uff1a\u201c\u4f60\u5750\u5728\u540e\u9762\uff0c\u6276\u7740\u6211 \u5427\u3002\u201d\u5c06\u6b27\u9633\u5a49\u6276\u4e0a\u9a6c\u80cc\uff0c\u6b27\u9633\u5a49\u552f\u6050\u8dcc\u4e0b\u6765\u4f3c\u7684\uff0c\u53cc\u624b\u7d27\u7d27\u5730\u62b1\u7740\u4ed6\u7684\u8170\u3002\u6c14\u5598\u5401\u5401\uff0c\u5439 \u6c14\u5982\u5170\uff0c\u6c5f\u6d77\u5927\u7b2c\u4e00\u6b21\u8fd9\u6837\u4eb2\u8fd1\u7684\u55c5\u5230\u5c11\u5973\u7684\u6c14\u5473\uff0c\u4f46\u89c9\u53c8\u662f\u96be\u53d7\uff0c\u53c8\u662f\u8212\u670d\u3002\u8bf4\u4e0d\u51fa\u662f\u4e2a \u4ec0\u4e48\u5473\u513f\u3002 \u3000\u3000\u90a3\u5339\u9a6c\u8fde\u65e5\u5954\u9a70\uff0c\u591a\u4e86\u4e00\u4e2a\u4eba\uff0c\u4e0d\u514d\u5403\u529b\uff0c\u9ed1\u591c\u4e2d\u9053\u8def\u5d0e\u5c96\uff0c\u9ad8\u4e00\u6b65\u4f4e\u4e00\u6b65\u7684\u4ee4\u5f97\u90a3\u5c11 \u5973\u98a0\u7c38\u4e0d\u4f11\uff0c\u5ffd\u7136\u89c9\u5f97\u90a3\u5c11\u5973\u7ad9\u4e86\u8d77\u6765\uff0c\u4e5f\u4e0d\u77e5\u5979\u662f\u6709\u610f\u8fd8\u662f\u65e0\u610f\uff0c\u5341\u53ea\u6307\u5934\uff0c\u7528\u529b\u7684\u5728\u6c5f \u6d77\u5929\u8170\u773c\u4e00\u6293\u3002\u5de6\u624b\u6293\u7684\u6b63\u662f\u6108\u6c14\u7a74\u7684\u65b9\u4f4d\u3001\u53f3\u624b\u6293\u7684\u5219\u662f\u72c2\u7b11\u7a74\u7684\u65b9\u4f4d\uff0c\u6108\u6c14\u7a74\u662f\u4eba\u8eab\u6b7b \u7a74\u4e4b\u4e00\uff0c\u800c\u72c2\u7b11\u7a74\u5219\u662f\u9ebb\u7a74\u4e4b\u4e00\uff0c\u5e78\u800c\u6c5f\u6d77\u5929\u65e9\u5df2\u7ec3\u6210\u62a4\u4f53\u795e\u529f\uff0c\u5018\u82e5\u6362\u4e86\u4ed6\u4eba\uff0c\u5373\u7b97\u4e0d \u6b7b\uff0c\u6b66\u529f\u4e5f\u8981\u7acb\u5373\u6d88\u5931\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u81ea\u5c0f\u5f97\u4ed6\u7236\u4eb2\u6c5f\u5357\u4f20\u6388\uff0c\u672c\u6765\u65e9\u5c31\u5b66\u4f1a\u4e86\u98a0\u5012\u7a74\u9053\u7684\u529f\u592b\uff0c\u4f46\u4ed6\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u8fd9 \u5c11\u5973\u4f1a\u5bf9\u4ed6\u6697\u7b97\uff0c\u6240\u4ee5\u4e1d\u6beb\u672a\u52a0\u9632\u5907\uff0c\u53ea\u9760\u7740\u62a4\u4f53\u795e\u529f\u81ea\u53d1\u7684\u53cd\u5e94\uff0c\u867d\u7136\u672a\u53d7\u5230\u4f24\u5bb3\uff0c\u4f46\u56e0 \u201c\u72c2\u7b11\u7a74\u201d\u88ab\u6293\uff0c\u4e5f\u7981\u4e0d\u4f4f\u7b11\u51fa\u58f0\u6765\u3002 \u3000\u3000\u4e0e\u6c5f\u6d77\u5929\u53d1\u7b11\u7684\u540c\u65f6\uff0c\u90a3\u5c11\u5973\u4e5f\u662f\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u53eb\u5c06\u8d77\u6765\uff0c\u534a\u8fb9\u8eab\u4e8e\u503e\u659c\uff0c\u79bb\u5f00\u4e86\u9a6c \u80cc\uff0c\u5979\u662f\u56e0\u4e3a\u53d7\u4e86\u6c5f\u6d77\u5929\u62a4\u4f53\u795e\u529f\u7684\u9707\u8361\uff0c\u5e78\u800c\u6c5f\u62c7\u5929\u4e0d\u662f\u6709\u5fc3\u53cd\u5bf9\u5979\uff0c\u5426\u5219\u5979\u65e9\u5df2\u7ed9\u6454\u5f97 \u53d1\u660f\u4e86\u3002 \u3000\u3000\u8981\u662f\u6362\u4e86\u4e2a\u7a0d\u6709\u6c5f\u6e56\u7ecf\u9a8c\u7684\u4eba\uff0c\u90fd\u4f1a\u8bc6\u7834\u8fd9\u5c11\u5973\u7684\u6697\u7b97\u7684\u884c\u5f84\uff0c\u504f\u504f\u8fd9\u5c11\u5973\u78b0\u4e0a\u7684\u5374\u662f \u4e2a\u5fe0\u539a\u8001\u5b9e\uff0c\u5168\u4e0d\u61c2\u5f97\u4eba\u5fc3\u9669\u6076\u7684\u6c5f\u6d77\u5929\uff0c\u4ed6\u542c\u5f97\u5c11\u5973\u7684\u53eb\u558a\uff0c\u8fd8\u597d\u751f\u8fc7\u610f\u4e0d\u53bb\uff0c\u6025\u5fd9\u53cd\u624b \u5c06\u5979\u6293\u7262\uff0c\u8bf4\u9053\uff1a\u201c\u5750\u7a33\u4e86\uff0c\u4e0d\u8981\u5bb3\u6015\uff0c\u5df2\u7ecf\u5230\u4e86\u5e73\u5730\u4e86\u3002\u4f60\u7684\u624b\u81c2\u53ef\u611f\u5230\u9ebb\u75bc\u5417\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u4f0f\u4f5c\u4e00\u56e2\uff0c\u9760\u7740\u6c5f\u6d77\u5929\u7c97\u9614\u7684\u80a9\u818a\uff0c\u957f\u53d1\u6563\u5f00\uff0c\u523a\u5f97\u6c5f\u6d77\u5929\u7684\u8138\u4e0a\u75d2\u75d2\u7684\uff0c\u5979\u5a07 \u58f0\u8bf4\u9053\uff1a\u201c\u5413\u6b7b\u6211\u4e86\uff0c\u6211\u51e0\u4e4e\u5c31\u8981\u6454\u4e0b\u53bb\u4e86\uff0c\u600e\u4e48\uff0c\u4f60\u5374\u8fd8\u5728\u597d\u7b11\u5462\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u53ea\u89c9\u5f97\u6b27\u9633\u5a49\u7684\u8eab\u5b50\u8f6f\u7ef5\u7ef5\u7684\uff0c\u597d\u50cf\u6ca1\u6709\u534a\u70b9\u6c14\u529b\uff0c\u66f4\u4e0b\u4f1a\u6000\u7591\u5979\u6709\u70b9\u7a74\u7684\u529f \u592b\uff0c\u53ea\u9053\u662f\u5076\u7136\u7684\u5de7\u5408\uff0c\u540c\u65f6\u4ed6\u4e5f\u7ed9\u8fd9\u7d27\u9760\u7740\u4ed6\u7684\u3001\u8f6f\u7ef5\u7ef5\u7684\u5c11\u5973\u7684\u8eab\u8eaf\uff0c\u5f04\u5f97\u6709\u70b9\u795e\u8ff7\u610f \u4e71\uff0c\u6025\u5fd9\u5c06\u6b27\u9633\u5a49\u7684\u8eab\u5b50\u6276\u76f4\uff0c\u81ea\u5df1\u4e5f\u632a\u5f00\u4e86\u4e00\u4e9b\uff0c\u7136\u540e\u8bf4\u9053\uff0c\u201c\u6211\u4e0d\u662f\u7b11\u4f60\uff0c\u53ea\u662f\u56e0\u4e3a\u4f60 \u6070\u5de7\u6293\u7740\u6211\u7684\u75d2\u5904\u3002\u73b0\u5728\u5df2\u7ecf\u5230\u4e86\u5e73\u5730\uff0c\u4f60\u53ef\u4ee5\u4e0d\u5fc5\u518d\u6293\u5f97\u90a3\u4e48\u7d27\u4e86\u3002\u4f60\u624b\u81c2\u9ebb\u75bc\u5417\uff1f\u6211\u8fd9 \u91cc\u6709\u6563\u7600\u6e05\u8840\u7684\u836f\u818f\u3002\u201d \u3000\u3000\u6b27\u9633\u5a49\u6545\u4f5c\u6b49\u7136\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4ece\u672a\u9a91\u8fc7\u9a6c\uff0c\u7ed9\u8fd9\u755c\u751f\u4e00\u5413\uff0c\u6599\u4e0d\u5230\u7adf\u6293\u7740\u4e86\u4f60\u7684\u75d2\u5904\uff0c \u771f\u662f\u5bf9\u4e0d\u4f4f\u4f60\u3002\u8fd8\u597d\uff0c\u6211\u7684\u624b\u81c2\u521a\u624d\u6709\u70b9\u9ebb\u75bc\uff0c\u73b0\u5728\u5df2\u4e0d\u7d27\u8981\u4e86\u3002\u6211\u53ea\u6015\u6293\u574f\u4e86\u4f60\u3002\u201d\u8fd9\u4ee5 \u540e\uff0c\u5979\u679c\u7136\u4e0d\u6562\u518d\u7528\u529b\u7d27\u6293\u4e86\u3002\u8fd9\u4e0d\u662f\u56e0\u4e3a\u6c5f\u6d77\u5929\u7684\u5429\u5490\uff0c\u800c\u662f\u56e0\u4e3a\u5979\u5df2\u8bc6\u5f97\u4e86\u6c5f\u6d77\u5929\u7684\u5389 \u5bb3\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u4f1a\uff0c\u6b27\u9633\u5a49\u5ffd\u9053\uff1a\u201c\u4f60\u770b\u3002\u90a3\u8fb9\u662f\u4e0d\u662f\u6709\u95f4\u5c4b\u5b50\uff1f\u201d\u6c5f\u6d77\u5929\u5b9a\u775b\u4e00\u770b\uff0c\u8bf4\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u54c8\uff0c\u4f60\u7684\u76ee\u529b\u6bd4\u6211\u8fd8\u5f3a\uff0c\u770b\u6765\u662f\u4e2a\u519c\u5bb6\uff0c\u54b1\u4eec\u6b63\u597d\u524d\u5f80\u6295\u5bbf\u3002\u201d\u6b27\u9633\u5a49\u5ffd\u5730\u53c8\u5728\u4ed6 \u7684\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6c5f\u76f8\u516c\uff0c\u6211\u6c42\u4f60\u8ba4\u6211\u4f5c\u59b9\u59b9\u3002\uff1f\u6c5f\u6d77\u5929\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u8bf4\u9053\uff1a\u201c\u554a\uff0c\u6562 \u60c5\u4f60\u662f\u6015\u522b\u4eba\u731c\u7591\u4e48\uff1f\u4e5f\u597d\uff0c\u54b1\u4eec\u5c31\u6682\u4ee5\u5144\u59b9\u76f8\u79f0\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u7ecf\u5230\u4e86\u90a3\u5bb6\u4eba\u5bb6\u7684\u95e8 \u524d\uff0c\u6c5f\u6bcf\u5929\u5c06\u6b27\u9633\u5a49\u6276\u4e0b\u9a6c\u80cc\uff0c\u4fbf\u53bb\u521d\u95e8\u3002 \u3000\u3000\u8fd9\u5bb6\u519c\u5bb6\u5b64\u96f6\u96f6\u7684\u5750\u843d\u5c71\u8fb9\uff0c\u524d\u540e\u5de6\u53f3\u90fd\u6ca1\u4eba\u5bb6\uff0c\u6c5f\u6d77\u5929\u89c9\u5f97\u6709\u70b9\u7279\u522b\uff0c\u4f46\u8fd9\u65f6\u4e5f\u65e0\u6687 \u63a8\u7a76\uff0c\u53ea\u662f\u4f7f\u52b2\u5730\u6572\u95e8\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u90a3\u4e24\u6247\u677f\u95e8\u201c\u5440\u201d\u7684\u4e00\u58f0\u6253\u5f00\uff0c\u4e00\u4e2a\u8001\u6c49\u63a2\u5934\u51fa\u6765\uff0c\u5927\u58f0\u95ee\u9053\uff1a\u201c\u4ec0\u4e48 \u4eba\uff1f\u201d\u8fd9\u8001\u6c49\u9b13\u7709\u7686\u767d\uff0c\u4f46\u53cc\u76ee\u5374\u70af\u70af\u6709\u795e\uff0c\u6c5f\u6d77\u5929\u7ed9\u4ed6\u53cc\u76ee\u4e00\u77aa\uff0c\u5927\u58f0\u4e00\u559d\uff0c\u4e5f\u7981\u4e0d\u4f4f\u5413 \u4e86\u4e00\u8df3\u3002 \u3000\u3000\u6b27\u9633\u5a49\u7b54\u9053\uff1a\u201c\u6211\u4eec\u662f\u5144\u59b9\u4e8c\u4eba\uff0c\u4e0d\u5e78\u4e2d\u9014\u9047\u76d7\uff0c\u8d22\u7269\u5c3d\u5931\uff0c\u8fd8\u671b\u8001\u7237\u5b50\u89c1\u601c\uff0c\u6536\u5bb9\u6211 \u4eec\u4f4f\u5bbf\u4e00\u5bb5\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u89c9\u5f97\u4e0d\u597d\u968f\u4fbf\u6253\u6270\u4eba\u5bb6\uff0c\u5fd9\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u6211\u8fd8\u6709\u51e0\u4e24\u94f6\u5b50\u85cf\u5728\u8eab\u4e0a\uff0c\u672a\u66fe\u7ed9\u5f3a\u76d7 \u641c\u51fa\u6765\u3002\u8001\u7237\u4e86\uff0c\u4f60\u82e5\u80af\u6536\u7559\u6211\u4eec\uff0c\u8fd9\u51e0\u4e24\u94f6\u5b50\uff0c\u6211\u613f\u610f\u4e0e\u4f60\u6743\u4f5c\u996d\u94b1\u623f\u94b1\u3002\u201d \u3000\u3000\u90a3\u8001\u6c49\u7684\u76ee\u5149\u7a81\u7136\u53d8\u8910\u4e00\u7247\u6148\u548c\uff0c\u968f\u5373\u5c31\u6253\u4e2a\u54c8\u54c8\u8bf4\u9053\uff1a\u201c\u7b11\u6d3b\uff0c\u7b11\u8bdd\uff0c\u4f60\u4eec\u5df2\u4e0d\u5e78\u906d \u52ab\uff0c\u6211\u600e\u597d\u8fd8\u8981\u4f60\u4eec\u7684\u94b1\u3002\u4e00\u4e2a\u4eba\u884c\u5bb3\u6700\u4e50\uff0c\u8001\u6c49\u65e0\u529b\u884c\u5584\uff0c\u4f46\u4e00\u987f\u5bb6\u5e38\u4fbf\u996d\uff0c\u8fd8\u662f\u6709\u7684\uff0c \u8d76\u5feb\u8fdb\u6765\u5427\uff0c\u6211\u5c31\u53eb\u8001\u4f19\u4f34\u7ed9\u4f60\u751f\u4eba\u9020\u996d\u3002\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u53c8\u8bf4\u9053\u3002\u201c\u6211\u8d77\u521d\u542c\u4f60\u6572\u95e8\u6572\u5f97\u8fd9 \u6837\u6025\uff0c\u8fd8\u5f53\u662f\u5f3a\u76d7\u5462\uff0c\u540e\u6765\u4e00\u60f3\uff0c\u6211\u4e5f\u6ca1\u4ec0\u4e48\u7ed9\u5f3a\u76d7\u52ab\u7684\uff0c\u8fd9\u624d\u6562\u5f00\u95e8\u3002\u60f3\u4e0d\u5230\u4f60\u4eec\u624d\u662f\u7ed9 \u5f3a\u76d7\u52ab\u7684\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u6c5f\u6d77\u5929\u5df2\u968f\u90a3\u8001\u6c49\u8d70\u8fdb\u5c4b\u5185\uff0c\u53ea\u89c1\u56db\u9762\u5899\u58c1\u90fd\u6842\u7740\u517d\u76ae\uff0c\u8fd8\u6709\u8840\u6dcb\u6dcb\u7684\u534a\u8fb9 \u517d\u8089\uff0c\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4e0d\u662f\u519c\u5bb6\uff0c\u4e43\u662f\u730e\u6237\u3002\u602a\u4e0d\u5f97\u8fd9\u4e48\u58ee\u5065\uff0c\u4e0d\u4f3c\u666e\u901a\u7684\u8001\u4eba\u3002\u201d \u3000\u3000\u90a3\u8001\u6c49\u5524\u8d77\u59bb\u5b50\u4e0e\u4ed6\u4eec\u76f8\u89c1\u3002\u90a3\u8001\u5a46\u5a46\u66f4\u662f\u6148\u7965\uff0c\u542c\u8bf4\u4ed6\u4eec\u88ab\u52ab\uff0c\u8fde\u58f0\u8bf4\u9053\uff1a\u201c\u53ef\u601c\uff0c \u53ef\u601c\uff01\u8fd9\u5c0f\u5a18\u5b50\u7684\u8863\u670d\u90fd\u5df2\u7834\u788e\uff0c\u53c8\u6ee1\u662f\u6c61\u6ce5\u8840\u6e0d\u4e86\u3002\u201d\u90a3\u8001\u6c49\u9053\uff0c\u201c\u4ed6\u4eec\u4e43\u662f\u5144\u59b9\u3002\u201d\u8001 \u5a46\u5a46\u9053\u3002\u201c\u7f6a\u8fc7\uff0c\u7f6a\u8fc7\u3002\u6211\u89c1\u4f60\u4eec\u76f8\u8c8c\u4e0d\u540c\uff0c\u53ea\u5f53\u662f\u5bf9\u592b\u59bb\u5462\u3002\u60f3\u6765\u4f60\u4eec\u4e0d\u662f\u4e00\u6bcd\u6240\u751f \u7684\u3002\u201d\u6c5f\u6d77\u5929\u542b\u7cca\u5e94\u662f\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u53c8\u8bf4\u9053\uff1a\u201c\u6211\u6628\u5929\u521a\u597d\u505a\u4e86\u4e00\u4ef6\u65b0\u8863\uff0c\u662f\u51c6\u5907\u7ed9\u6211\u90a3\u51fa\u5ac1\u7684\u5973\u513f\u7684\u3002\u5c0f\u59d0\u3002\u4f60 \u4e0d\u5acc\u5f03\u7684\u8bdd\uff0c\u5c31\u62ff\u53bb\u6362\u4e00\u6362\u5427\u3002\u6362\u4e0b\u6765\u7684\uff0c\u6211\u7ed9\u4f60\u6d17\u51c0\u8865\u597d\uff0c\u8fd9\u91cc\u5c71\u98ce\u5f88\u5927\uff0c\u5230\u4e86\u660e\u5929\uff0c\u60f3 \u5fc5\u4e5f\u4f1a\u5439\u5e72\u4e86\u3002\u201d\u90a3\u8001\u6c49\u7b11\u9053\uff0c\u201c\u4f60\u8fd8\u662f\u65e9\u4e00\u4e9b\u7ed9\u4eba\u5bb6\u5f04\u996d\u5427\uff0c\u6362\u8863\u670d\u6162\u70b9\u4e5f\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u8fc7\u4e0d\u591a\u4e45\uff0c\u90a3\u8001\u5a46\u5a46\u628a\u996d\u7aef\u4e86\u51fa\u6765\uff0c\u8fd8\u6709\u4e00\u5927\u76d8\u70ed\u817e\u817e\u7684\u517d\u8089\uff0c\u8bf4\u9053\uff0c\u201c\u59d4\u5c48\u4f60\u4eec\u5403\u70b9 \u5269\u996d\uff0c\u5e78\u597d\u6211\u8fd9\u8001\u4f34\u6628\u5929\u6253\u4e86\u4e00\u53ea\u7350\u5b50\uff0c\u996d\u82e5\u4e0d\u591f\uff0c\u4f60\u4eec\u5c31\u591a\u5403\u4e00\u70b9\u6a1f\u8089\u5427\u3002\u201d\u90a3\u8001\u6c49\u9053\uff1a \u201c\u54b1\u4eec\u8fd8\u6709\u51e0\u65a4\u8001\u9152\uff0c\u4f60\u4e5f\u6696\u5b83\u4e00\u58f6\u62ff\u51fa\u6765\u5427\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u597d\u751f\u8fc7\u610f\u4e0b\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u9047\u96be\u4e4b\u4eba\uff0c\u4f46\u6c42\u679c\u8179\uff0c\u4e8e\u613f\u5df2\u8db3\uff0c\u600e\u6562\u539a\u6270\uff1f\u201d\u90a3\u8001\u6c49 \u9053\uff0c\u201c\u76f8\u516c\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u665a\u4e0a\u5c71\u98ce\u5f88\u5927\uff0c\u5403\u4e00\u70b9\u9152\u53ef\u4ee5\u5fa1\u5bd2\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u672c\u6765\u4e0d\u4f1a\u559d\u9152\uff0c\u4f46\u5728\u4e3b\u4eba\u76db\u60c5\u9080\u996e\u4e4b\u4e0b\uff0c\u4e5f\u53ea\u597d\u5e72\u4e86\u51e0\u676f\u3002\u90a3\u8001\u6c49\u966a\u4ed6\u559d\u9152\uff0c\u4e00 \u9762\u95ee\u4ed6\u9047\u76d7\u7684\u60c5\u5f62\uff0c\u6c5f\u6d77\u5929\u4e0d\u5584\u8bf4\u8c0e\uff0c\u5e78\u5f97\u6b27\u9633\u5a49\u66ff\u4ed6\u7f16\u4e86\u4e00\u5957\u8bf4\u8bcd\uff0c\u642a\u585e\u8fc7\u53bb\u3002\u6c5f\u6d77\u5929\u5fc3 \u91cc\u60f3\u9053\uff1a\u201c\u9971\u8bfb\u8bd7\u4e66\u7684\u5b98\u5bb6\u5973\u5b50\uff0c\u679c\u7136\u7f16\u8d77\u8c0e\u6d3b\u6765\u4e5f\u8981\u6bd4\u5e38\u4eba\u9ad8\u660e\u5f97\u591a\u3002\u201d\u4f46\u4ed6\u5374\u4e00\u70b9\u4e5f\u6ca1 \u60f3\u5230\uff0c\u6b27\u9633\u5a49\u65e5\u95f4\u5bf9\u4ed6\u8bf4\u7684\u9047\u76d7\u6545\u4e8b\uff0c\u4e5f\u662f\u4e00\u5957\u65e9\u5c31\u7f16\u597d\u4e86\u7684\u8c0e\u8bdd\u3002 \u3000\u3000\u5403\u9971\u4e4b\u540e\uff0c\u6b27\u9633\u5a49\u968f\u90a3\u8001\u5987\u4eba\u8fdb\u53bb\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6362\u4e86\u4e00\u5957\u65b0\u8863\u670d\u51fa\u6765\uff0c\u5012\u4e5f\u5f88\u5408\u8eab\u6750\uff0c \u8d8a\u53d1\u663e\u5f97\u5bb9\u5149\u8273\u4e3d\u3002\u90a3\u8001\u5987\u4eba\u4e00\u624b\u62ff\u7740\u4e00\u4e2a\u8336\u58f6\uff0c\u4e00\u624b\u62ff\u7740\u4e00\u76cf\u6cb9\u706f\uff0c\u8bf4\u9053\uff1a\u201c\u76f8\u516c\u4e0d\u8981\u89c1 \u602a\uff0c\u6211\u4eec\u7a77\u4eba\u5bb6\u6ca1\u6709\u591a\u4f59\u7684\u5730\u65b9\uff0c\u53ea\u597d\u59d4\u5c48\u4f60\u4eec\u5728\u67f4\u623f\u91cc\u6682\u4f4f\u4e00\u665a\uff0c\u597d\u5728\u4f60\u4eec\u662f\u5144\u59b9\uff0c\u4e0d\u5fc5 \u907f\u5acc\u3002\u201d\u6c5f\u6d77\u5929\u751a\u611f\u5c34\u5c2c\uff0c\u4f46\u4e5f\u53ea\u5f97\u8fde\u58f0\u9053\u8c22\u3002 \u3000\u3000\u90a3\u8001\u5987\u4eba\u5c06\u67f4\u623f\u6253\u626b\u5e72\u51c0\uff0c\u53c8\u642c\u6765\u4e86\u4e00\u5f20\u5e2d\u5b50\uff0c\u4e00\u5e8a\u68c9\u88ab\uff0c\u8bf4\u9053\uff1a\u201c\u60ed\u6127\u5f97\u5f88\uff0c\u6211\u4eec\u7a77 \u5bb6\u53ea\u632a\u5f97\u51fa\u4e00\u5e8a\u88ab\u76d6\uff0c\u59d1\u5a18\uff0c\u4f60\u5c06\u5c31\u7528\u5427\u3002\u76f8\u516c\uff0c\u4f60\u8981\u662f\u89c9\u5f97\u5bd2\u51b7\u7684\u8bdd\uff0c\u53ef\u4ee5\u751f\u706b\u53d6\u6696\u3002\u8fd9 \u4e00\u58f6\u8336\u7559\u5728\u8fd9\u91cc\u7ed9\u4f60\u4eec\u559d\u3002\u201d \u3000\u3000\u8001\u5987\u4eba\u8d70\u540e\uff0c\u6c5f\u6d77\u5929\u4e0e\u6b27\u9633\u5a49\u4e24\u4eba\u76f8\u5bf9\uff0c\u751a\u89c9\u4e0d\u597d\u610f\u601d\u3002\u597d\u5728\u6b27\u9633\u5a49\u5012\u662f\u795e\u8272\u5766\u7136\uff0c\u6e10 \u6e10\u6c5f\u6d77\u5929\u4e5f\u6ca1\u6709\u90a3\u4e48\u7a98\u4e86\u3002 \u3000\u3000\u6b27\u9633\u5a49\u5ffd\u5730\u5fae\u7b11\u95ee\u9053\uff1a\u201c\u6c5f\u3001\u6c5f\u5927\u54e5\uff0c\u591a\u627f\u76f8\u6551\uff0c\u6211\u8fd8\u672a\u77e5\u9053\u4f60\u7684\u5bb6\u4e16\u5462\uff0c\u4f60\uff0c\u4f60\u5bb6\u91cc \u6709\u4e9b\u4ec0\u4e48\u4eba\uff1f\u505a\u7684\u4ec0\u4e48\u8425\u751f\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u5bb6\u91cc\u53ea\u6709\u7238\u7238\u5988\u5988\uff0c\u8fd8\u6709\u5916\u5a46\u548c\u6211\u4eec\u540c\u4f4f\uff0c \u4e00\u5171\u662f\u56db\u4e2a\u4eba\u3002\u6211\u5916\u5a46\u6709\u70b9\u4ea7\u4e1a\uff0c\u6211\u4eec\u4f4f\u5979\u7684\u5c4b\u5b50\u3002\u201d \u3000\u3000\u6b27\u9633\u5a49\u7b11\u9053\uff1a\u201c\u6ca1\u6709\u65c1\u4eba\u4e86\u5417\uff1f\u55ef\uff0c\u8fd9\u6837\u8bf4\uff0c\u4f60\u662f\u5c1a\u672a\u5a36\u4eb2\u7684\u4e86\uff1f\u201d\u6c5f\u6d77\u5929\u9762\u7ea2\u8fc7\u8033\uff0c \u8bf4\u9053\uff0c\u201c\u6211\u4eca\u5e74\u624d\u6ee1\u5341\u516d\u5c81\uff0c\u65e9\u7740\u5462\u3002\u201d\u6b27\u9633\u5a49\u53c8\u7b11\u9053\uff0c\u201c\u7167\u6211\u4eec\u4e61\u4e0b\u7684\u4e60\u60ef\uff0c\u6ee1\u5341\u516d\u5c81\u5c31 \u7b97\u662f\u5927\u4eba\u4e86\u3002\u771f\u5de7\uff0c\u6211\u4e5f\u662f\u5341\u516d\u5c81\uff0c\u6bd4\u4f60\u5bb6\u4eba\u53e3\u66f4\u5c11\uff0c\u53ea\u6709\u7238\u7238\u5988\u5988\uff0c\u522b\u65e0\u4ed6\u4eba\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u66f4\u4e0d\u597d\u610f\u601d\uff0c\u5ffd\u89c9\u820c\u5c16\u82e6\u6da9\uff0c\u5fc3\u5934\u70e6\u8e81\uff0c\u76b1\u4e86\u76b1\u7709\uff0c\u6b27\u9633\u5a49\u8bf4\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c \u4f60\uff0c\u4f60\u4e0d\u8212\u670d\u5417\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u559d\u9152\uff0c\u60f3\u662f\u9152\u559d\u5f97\u591a\u4e86\u3002\u201d\u6b27\u9633\u5a49\u62ff\u8d77\u7897\u6765\uff0c\u5c31\u7ed9 \u4ed6\u5012\u4e86\u4e00\u7897\u8336\uff0c\u55c5\u4e86\u4e00\u55c5\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u8336\u597d\u9999\uff0c\u60f3\u662f\u96e8\u524d\u8336\uff0c\u4f60\u559d\u4e0b\u53bb\uff0c\u53ef\u4ee5\u89e3\u9152\u3002\u201d \u3000\u3000\u6b27\u9633\u5a49\u6367\u7740\u8336\u7b11\u76c8\u76c8\u5730\u7ad9\u5728\u4ed6\u7684\u9762\u524d\uff0c\u8336\u7897\u51e0\u4e4e\u8981\u78b0\u5230\u4ed6\u7684\u5507\u8fb9\uff0c\u8f6f\u8bed\u7ef5\u7ef5\uff0c\u771f\u662f\u8bf4\u4e0d \u5c3d\u7684\u6e29\u67d4\u4f53\u8d34\u3002\u6c5f\u6d77\u5929\u5fc3\u5934\u4e00\u8361\uff0c\u624b\u8db3\u65e0\u63aa\uff0c\u8fde\u5fd9\u4ea1\u9000\u540e\u4e24\u6b65\uff0c\u63a5\u8fc7\u4e00\u7897\uff0c\u5495\u565c\u565c\u7684\u5c31\u4ef0\u7740 \u8116\u5b50\u559d\u4e86\u4e2a\u5c3d\uff0c\u679c\u7136\u89c9\u5f97\u4e00\u80a1\u7518\u9999\uff0c\u6c81\u5165\u80ba\u8151\uff0c\u6709\u8bf4\u4e0d\u51fa\u7684\u8212\u670d\u3002 \u3000\u3000\u6b27\u9633\u5a49\u6253\u4e86\u4e2a\u5475\u6b20\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u6211\u53ef\u60f3\u7761\u4e86\uff0c\u4f60\u5462\uff1f\u4f60\u7761\u5728\u54ea\u513f\uff1f\u201d\u6c5f\u6d77\u5929 \u9053\uff1a\u201c\u6211\u4e0d\u7761\uff0c\u6211\u7ed9\u4f60\u5b88\u591c\u3002\u201d\u80cc\u8f6c\u4e86\u8eab\uff0c\u9762\u5bf9\u7740\u95e8\uff0c\u76d8\u819d\u800c\u5750\u3002\u53ea\u542c\u5f97\u6089\u6089\u7d22\u7d22\u7684\u58f0\u97f3\uff0c \u6b27\u9633\u5a49\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u7a77\u4eba\u5bb6\u96be\u5f97\u505a\u4e00\u4ef6\u8863\u88f3\uff0c\u8fd9\u65b0\u8863\u53ef\u4e0d\u8981\u628a\u5b83\u5f04\u810f\u76b1\u4e86\u3002\u201d\u4e0d\u540c\u53ef\u77e5\uff0c \u90a3\u662f\u6b27\u9633\u5a49\u6b63\u5728\u628a\u65b0\u8863\u8131\u4e0b\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5f04\u5f97\u547c\u5438\u7d27\u5f20\uff0c\u9762\u7ea2\u8033\u70ed\uff0c\u76ee\u89c2\u9f3b\uff0c\u9f3b\u89c2\u5fc3\uff0c\u8fde\u5fd9\u505a\u8d77\u5410\u7eb3\u529f\u5927\uff0c\u8bf4\u4e5f\u5947\u602a\uff0c\u4ed6 \u9759\u5750\u4e00\u4f1a\uff0c\u53cd\u800c\u89c9\u5f97\u5fc3\u5934\u6108\u6765\u6108\u70e6\u8e81\uff0c\u60f3\u8981\u5bfc\u6c14\u5f52\u5143\uff0c\u771f\u6c14\u7adf\u4e0d\u80fd\u5165\u4e39\u51fa\uff0c\u6e10\u6e10\uff0c\u8840\u6db2\u4e5f\u50cf \u5411\u5934\u90e8\u6d8c\u4e0a\u3002 \u3000\u3000\u518d\u8fc7\u4e00\u4f1a\uff0c\u60c5\u5f62\u8d8a\u53d1\u4e0d\u5999\uff0c\u5c0f\u8179\u9690\u9690\u4f5c\u75db\uff0c\u89c6\u529b\u6e10\u6e10\u6a21\u7cca\u4e0d\u6e05\uff0c\u6c5f\u6d77\u5929\u5927\u529b\u5403\u60ca\uff0c\u731b\u5730 \u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u4fbf\u8df3\u8d77\u6765\uff0c\u62d4\u51fa\u5b9d\u5251\u3002 \u3000\u3000\u4e00\u56de\u5934\uff0c\u53ea\u89c1\u6b27\u9633\u5a49\u4e5f\u8df3\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u8981\u662f\u7a0d\u5fae\u7559\u795e \u7684\u8bdd\uff0c\u5f53\u53ef\u77a7\u51fa\u6b27\u9633\u5a49\u8fd9\u4e00\u8dc3\u800c\u8d77\uff0c\u5b9e\u5728\u662f\u77eb\u6377\u4e4b\u6781\uff0c\u800c\u4e14\u76ee\u5149\u4e2d\u4e5f\u5145\u6ee1\u6740\u6c14\uff01\u4f46\u6c5f\u6d77\u5929\u8fd9 \u65f6\u6b63\u662f\u5fc3\u70e6\u610f\u4e71\uff0c\u4e3a\u4e86\u8fd9\u610f\u6599\u4e0d\u5230\u7684\u53d8\u6545\u800c\u6124\u6012\u4e0d\u582a\u3002 \u3000\u3000\u6b27\u9633\u5a49\u89c1\u4ed6\u5b9d\u5251\u51fa\u9798\uff0c\u5fc3\u4e2d\u4e5f\u7740\u4e86\u614c\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u53ef\u8981\u7cdf\u4e86\uff0c\u4ed6\u7684\u5185\u529f\u7adf\u6bd4\u6211\u9884\u6599\u7684 \u8fd8\u5f3a\u3002\u201d\u6b63\u5728\u4e0d\u77e5\u6240\u63aa\uff0c\u53ea\u542c\u5f97\u6c5f\u6d77\u5929\u6012\u5362\u53eb\u9053\uff1a\u201c\u8fd9\u5bf9\u8001\u592b\u5987\u4e0d\u662f\u597d\u4eba\uff0c\u6211\u7740\u4e86\u4ed6\u4eec\u7684\u9053 \u513f\u4e86\uff01\u90a3\u9152\u4e2d\u6709\u6bd2\uff0c\u6211\u8981\u6293\u7740\u4ed6\u4eec\uff0c\u8feb\u4ed6\u4eec\u4ea4\u51fa\u89e3\u836f\u6765\uff01\u201d\u6c5f\u6d77\u5929\u53ea\u6599\u662f\u9152\u4e2d\u6709\u6bd2\uff0c\u54ea\u77e5\u6b27 \u9633\u5a49\u7ed9\u4ed6\u659f\u7684\u90a3\u7897\u8336\uff0c\u6bd2\u6027\u66f4\u4e3a\u5389\u5bb3\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u76ee\u5149\u4e00\u77a5\uff0c\u89c1\u6b27\u9633\u5a49\u53ea\u7a7f\u7740\u4e00\u8eab\u8584\u8584\u7684\u7c89\u7ea2\u8272\u7684\u886c\u8863\uff0c\u6124\u6012\u4e4b\u4e2d\u4ed6\u4e5f\u8fd8\u77e5\u9053\u7f9e \u6127\uff0c\u8fde\u5fd9\u56de\u8fc7\u4e86\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u5bb3\u6015\uff0c\u6709\u6211\u5728\u6b64\uff0c\u4ed6\u4eec\u51b3\u4e0d\u80fd\u5bb3\u4f60\uff01\u201d\uff0f\u8bf4\u7f62\u5c31\u50cf\u4e00\u9635 \u98ce\u5730\u51b2\u51fa\u67f4\u623f\u3002 \u3000\u3000\u6b27\u9633\u5a49\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u60f3\u8981\u9003\u8dd1\uff0c\u53c8\u6015\u529f\u8d25\u5782\u6210\uff0c\u82e5\u4e0d\u9003\u8dd1\uff0c\u53c8\u6015\u6c5f\u6d77\u5929\u77a7\u51fa\u7834\u7efd\uff0c\u5979\u72b9\u7591 \u4e86\u4e00\u4f1a\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u50bb\u5b50\u8fd8\u672a\u6709\u4e1d\u6beb\u7591\u5fc3\u5230\u6211\uff0c\u6211\u4e0d\u5982\u518d\u5f85\u4e00\u4f1a\uff0c\u53cd\u6b63\u6bd2\u5df2\u53d1\u4f5c\uff0c\u6599\u4ed6 \u4e5f\u4e0d\u80fd\u652f\u6301\u5f97\u591a\u4e45\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u89c1\u6c5f\u6d77\u5929\u6c14\u51b2\u51b2\u7684\u53c8\u8dd1\u56de\u6765\uff0c\u5b9d\u5251\u4e00\u6325\uff0c\u628a\u4e00\u5757\u6728\u67f4\u65a9\u4e3a\u4e24\u6bb5\uff0c\u6068\u6068\u8bf4 \u9053\uff0c\u201c\u8fd9\u5bf9\u592b\u59bb\u679c\u7136\u4e0d\u662f\u597d\u4eba\uff0c\u4ed6\u4eec\u5df2\u7ecf\u8dd1\u4e86\uff01\u54fc\uff0c\u54fc\uff01\u8981\u4e0d\u662f\u505a\u8d3c\u5fc3\u865a\uff0c\u4ed6\u4eec\u600e\u4f1a\u9003 \u8dd1\uff1f\u201d\u6b27\u9633\u5a49\u6253\u4e86\u4e2a\u98a4\uff0c\u5fc3\u9053\uff1a\u201c\u5e78\u4e8f\u6211\u6ca1\u6709\u9003\u8dd1\u3002\u201d \u3000\u3000\u706f\u5149\u867d\u7136\u4e0d\u5f88\u660e\u4eae\uff0c\u4e5f\u7167\u89c1\u4e86\u6b27\u9633\u5a49\u90a3\u6ee1\u8138\u60ca\u60f6\u7684\u795e\u60c5\uff0c\u6c5f\u6d77\u5929\u8fde\u5fd9\u5c06\u5b9d\u5251\u8fd8\u9798\uff0c\u8d54\u7b11 \u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u7684\u6837\u5b50\u5f88\u51f6\u5427\uff1f\u5413\u4e86\u4f60\u4e86\u3002\u6211\u53ea\u662f\u607c\u6068\u8fd9\u5bb6\u4e3b\u4eba\uff0c\u4e0e\u54b1\u4eec\u7d20\u4e0d\u76f8\u8bc6\uff0c\u65e0\u51a4 \u65e0\u4ec7\uff0c\u4e0d\u77e5\u4e3a\u4f55\u8981\u4e0b\u6bd2\u624b\uff0c\u771f\u662f\u5c82\u6709\u6b64\u7406\uff01\u201d \u3000\u3000\u6b27\u9633\u5a49\u8f7b\u8f7b\u5401\u4e86\u53e3\u6c14\uff1a\u4e00\u5757\u5927\u77f3\u4ece\u5fc3\u4e0a\u653e\u4e0b\uff0c\u4f46\u5979\u8138\u4e0a\u4ecd\u662f\u4e00\u526f\u5fe7\u8651\u7684\u795e\u60c5\uff0c\u8bf4\u9053\uff1a \u201c\u4f60\u5bf9\u6211\u8fd9\u6837\u597d\uff0c\u6837\u5b50\u518d\u51f6\uff0c\u6211\u4e5f\u4e0d\u4f1a\u60ca\u6050\u7684\u3002\u6211\u53ea\u662f\u4e3a\u4f60\u62c5\u5fc3\uff0c\u54ce\u5440\uff0c\u8fd9\u6bd2\u836f\u5f88\u5389\u5bb3\u5427\uff1f \u4f60\u89c9\u788d\u600e\u4e48\u6837\u4e86\uff1a\u6ca1\u6709\u89e3\u836f\uff0c\u5982\u4f8b\u662f\u597d\uff1f\u4f60\u3001\u4f60\u7684\u8138\u4e0a\u90fd\u5df2\u73b0\u51fa\u9ed1\u6c14\u6765\u4e86\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u53cd\u800c\u5b89\u6170\u5979\u8bf4\uff0c\u201c\u4f60\u4e0d\u5fc5\u4e3a\u6211\u5bb3\u6015\uff0c\u6bd2\u836f\u867d\u7136\u5389\u5bb3\uff0c\u8fd8\u4e0d\u81f3\u4e8e\u5c31\u8981\u5f97\u4e86\u6211\u7684\u547d\uff01\u201d \u3000\u3000\u6b27\u9633\u5a49\u7559\u5fc3\u770b\u4ed6\u795e\u8272\uff0c\u53ea\u89c1\u4ed6\u76d8\u819d\u800c\u5750\uff0c\u5c06\u4e2d\u6307\u4e00\u633a\uff0c\u6307\u5c16\u5ffd\u5730\u88c2\u5f00\uff0c\u4e00\u80a1\u94f6\u9488\u4f3c\u7684\u6c34 \u7ebf\u7a81\u7136\u5c04\u4e86\u51fa\u6765\uff0c\u767b\u65f6\u9152\u6c14\u85b0\u4eba\uff0c\u6b27\u9633\u5a49\u597d\u751f\u60ca\u5f02\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u7684\u5e08\u7236\u4e5f\u6ca1\u6709\u8fd9\u6837\u6df1\u6e5b\u529f \u529b\uff0c\u5e78\u4e8f\u6211\u6ca1\u6709\u9c81\u83bd\u4ece\u4e8b\u3002\u201d\u539f\u6765\u6c5f\u6d77\u5929\u9ed8\u8fd0\u7384\u529f\uff0c\u5c06\u6bd2\u9152\u8feb\u5f97\u805a\u5728\u4e00\u5904\uff0c\u4ece\u6307\u5934\u4e0a\u5c04\u51fa\u6765\u3002 \u3000\u3000\u6b63\u5728\u6b27\u9633\u5a49\u5185\u5fc3\u6218\u4fd0\u7684\u65f6\u5019\uff0c\u6c5f\u6d77\u5929\u5374\u5ffd\u7136\u73b0\u51fa\u60ed\u6127\u7684\u795e\u60c5\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u5bf9\u6b27\u9633\u5a49\u9053\uff0c \u201c\u6211\u7684\u6027\u547d\u5927\u7ea6\u53ef\u4ee5\u4fdd\u6301\u5728\u4e86\uff0c\u53ea\u662f\u5374\u4e0d\u80fd\u4e0d\u5411\u4f60\u6df1\u6df1\u62b1\u6b49\uff01\u201d\u6b27\u9633\u5a49\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60 \u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u7684\u529f\u529b\u4e0d\u591f\uff0c\u53ea\u80fd\u628a\u6bd2\u9152\u8feb\u4e86\u51fa\u6765\uff0c\u4e94\u810f\u516d\u8151\u6240\u6cbe\u7684\u6bd2\uff0c\u5374\u6ca1\u6cd5\u5b50\u5c06\u5b83 \u6392\u51fa\uff0c\u8981\u6e05\u9664\u4f59\u6bd2\uff0c\u6211\u8fd8\u5f97\u518d\u9759\u5750\u4e24\u5929\u3002\u6211\u672c\u6765\u7b54\u5e94\u9001\u4f60\u5230\u592a\u539f\u7684\uff0c\u73b0\u5728\u6211\u5df2\u7ecf\u6ca1\u6709\u80fd\u529b\u4fdd \u62a4\u4f60\u4e86\u3002\u8fd9\u4f59\u6bd2\u82e5\u4e0d\u8d76\u5feb\u6e05\u9664\uff0c\u6211\u4f1a\u7ec8\u751f\u6b8b\u5e9f\u3002\u800c\u5de8\u6211\u73b0\u5728\u5185\u529b\u6d88\u8017\u5927\u591a\uff0c\u4e00\u4e24\u5929\u4e4b\u5185\u7edd\u96be \u6062\u590d\u3002\u5728\u672a\u66fe\u6062\u590d\u4e4b\u524d\uff0c\u6211\u4e5f\u4e0d\u8fc7\u50cf\u5e38\u4eba\u4e00\u822c\uff0c\u5bf9\u4f60\u6050\u6015\u6ca1\u6709\u4ec0\u4e48\u7528\u5904\u4e86\u3002\u6b27\u9633\u59d1\u5a18\uff0c\u6211\u5bf9 \u4f60\u5931\u4fe1\uff0c\u7eaf\u662f\u4e3a\u4e86\u610f\u5916\uff0c\u4f46\u6c42\u4f60\u4e0d\u8981\u602a\u6211\uff01\u201d \u3000\u3000\u6b27\u9633\u5a49\u60ca\u7591\u4e0d\u5b9a\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ed6\u662f\u8001\u5b9e\u4eba\uff0c\u5927\u7ea6\u4e0d\u4f1a\u88c5\u5047\u3002\u201d\u53ea\u89c1\u6c5f\u6d77\u5929\u53c8\u628a\u51e0\u952d\u94f6 \u5b50\u638f\u4e86\u51fa\u6765\uff0c\u6b27\u9633\u5a49\u95ee\u9053\uff0c\u201c\u4f60\u8fd9\u662f\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u906d\u5f3a\u76d7\u6240\u52ab\uff0c\u8eab\u4e0a\u60f3\u5fc5\u6ca1\u6709\u4f59\u94b1\u4e86\u3002\u8fd9\u70b9\u94f6\u5b50\u4f60\u62ff\u53bb\u4f5c\u8def\u4e0a\u4f7f\u7528\u5427\u3002\u597d \u5728\u4f60\u5df2\u6362\u4e0a\u8fd9\u8eab\u4e61\u95f4\u5987\u5973\u7684\u8863\u88f3\uff0c\u5c31\u96c7\u4e00\u8f86\u9aa1\u8f66\uff0c\u8bf4\u662f\u5230\u592a\u539f\u57ce\u53bb\u63a2\u4eb2\uff0c\u5927\u7ea6\u4e5f\u53ef\u4ee5\u906e\u4ffa\u5f97 \u8fc7\u53bb\u3002\u201d \u3000\u3000\u6b27\u9633\u5a49\u4e0d\u7531\u5f97\u611f\u60c5\u6fc0\u8361\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6211\u5728\u7b97\u8ba1\u4ed6\uff0c\u4ed6\u5374\u662f\u8fd9\u6837\u7684\u5173\u5fc3\u6211\uff0c\u201d\u6c5f\u6d77\u5929\u89c1 \u5979\u4e0d\u63a5\u94f6\u5b50\uff0c\u8fd8\u9053\u5979\u4e0d\u597d\u610f\u601d\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u4f60\u9003\u96be\u8981\u7d27\uff0c\u62d8\u8bba\u8fd9\u4e9b\u5c0f\u8282\u505a\u4ec0\u4e48\uff1f\u6211\u8fd8\u6709\u4e00\u6837 \u4e1c\u897f\u7ed9\u4f60\uff0c\u8fd9\u662f\u501f\u7ed9\u4f60\u7684\u3002\u201d\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u5c31\u89e3\u4e0b\u4e86\u6240\u4f69\u7684\u5b9d\u5251\uff0c\u6454\u5230\u4e86\u6b27\u9633\u5a49\u7684\u9762\u524d\u3002\u6b27 \u9633\u5a49\u53c8\u5413\u4e86\u4e00\u8df3\uff0c\u6c5f\u6d77\u592b\u9053\uff1a\u201c\u6211\u542c\u5e08\u7236\u8bf4\uff0c\u8fd9\u662f\u5929\u4e0b\u6700\u950b\u5229\u7684\u5b9d\u5251\uff0c\u4f60\u5e26\u5728\u8eab\u8fb9\u9632\u8eab\u5427\u3002 \u8fd9\u67c4\u5b9d\u5251\u5f88\u8f7b\uff0c\u4f60\u53ef\u4ee5\u4f7f\u5f97\u52a8\u7684\u3002\u201d \u3000\u3000\u6538\u9633\u5a49\u65e9\u5df2\u77e5\u9053\u8fd9\u628a\u88c1\u4e91\u5b9d\u5251\u4e43\u662f\u4e16\u4e0a\u65e0\u53cc\u3001\u4ef7\u503c\u8fde\u57ce\u7684\u5b9d\u5251\uff0c\u5979\u8fd9\u6b21\u5e03\u4e0b\u9677\u9631\u60f3\u6697\u5bb3 \u6c5f\u6d77\u5929\uff0c\u867d\u7136\u5c1a\u6709\u5176\u4ed6\u539f\u56e0\uff0c\u4f46\u8981\u60f3\u53d6\u5f97\u8fd9\u628a\u5b9d\u5251\uff0c\u4e5f\u662f\u539f\u56e0\u4e4b\u4e00\uff0c\u5979\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\uff0c\u6c5f\u6d77 \u5929\u7adf\u4f1a\u628a\u8fd9\u628a\u4e16\u4e0a\u65e0\u53cc\u7684\u5b9d\u5251\u53cc\u624b\u5949\u4e0a\uff0c\u7adf\u4f1a\u5bf9\u4e00\u4e2a\u964c\u8def\u76f8\u9022\u7684\u5973\u5b50\u5982\u6b64\u4fe1\u4efb\uff0c\u6beb\u65e0\u6212\u5fc3\uff01 \u3000\u3000\u8fd9\u65f6\u53ea\u8981\u4ed6\u63a5\u8fc7\u5b9d\u5251\uff0c\u4fe1\u624b\u4e00\u6325\uff0c\u4fbf\u53ef\u628a\u6c5f\u6d77\u5929\u65a9\u4e3a\u4e24\u6bb5\uff0c\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u5979\u7684\u624b\u8db3\u90fd\u4f3c \u6709\u5343\u65a4\u4e4b\u91cd\uff0c\u600e\u6d0b\u4e5f\u4e3e\u4e0d\u8d77\u6765\uff01\u6c5f\u6d77\u5929\u90a3\u8bda\u6073\u7684\u76ee\u5149\uff0c\u50cf\u662f\u6625\u98ce\uff0c\u53c8\u50cf\u5229\u7bad\uff0c\u65e2\u4ee4\u5979\u611f\u5230\u6e29 \u6696\uff0c\u53c8\u4ee4\u5979\u5fc3\u5934\u523a\u75db\uff0c\u7f9e\u6127\u96be\u5bb9\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u600e\u77e5\u9053\u5979\u7684\u5fc3\u60c5\uff0c\u89c1\u5979\u4f3c\u662f\u7a81\u7136\u5446\u4e86\uff0c\u81ea\u5df1\u4e5f\u4e0d\u7981\u4e00\u6014\uff0c\u4ed6\u60f3\u4e86\u4e00\u60f3\uff0c\u53c8\u518d\u8bf4 \u9053\uff1a\u201c\u6b27\u9633\u59d1\u5a18\uff0c\u6211\u77e5\u9053\u4f60\u662f\u95fa\u9601\u5343\u91d1\uff0c\u4e0d\u4f1a\u6b66\u827a\uff0c\u4e5f\u8bb8\u4ece\u6765\u6ca1\u6709\u6cbe\u8fc7\u5200\u5251\uff1a\u4f46\u4f60\u6562\u4ece\u8d3c\u7a9f \u4e2d\u9003\u51fa\u6765\uff0c\u4e5f\u662f\u4e2a\u6709\u80c6\u91cf\u7684\u5973\u5b50\uff0c\u8def\u9014\u4e0a\u82e5\u78b0\u5230\u5f3a\u4eba\uff0c\u4f60\u53ea\u8981\u8fd9\u6837\u60f3\u3002\u6211\u82e5\u4e0d\u4f24\u4ed6\u4eec\uff0c\u5c31\u8981 \u53d7\u4ed6\u4eec\u6240\u8fb1\uff0c\u8fd9\u6837\u4f60\u5c31\u5e94\u8be5\u6562\u52a8\u7528\u8fd9\u628a\u5b9d\u5251\u4e86\u3002\u4f60\u867d\u4e0d\u61c2\u6b66\u827a\uff0c\u597d\u5728\u8fd9\u5251\u950b\u5229\u5f02\u5e38\uff0c\u53ea\u987b\u4f60 \u7d27\u7d27\u63e1\u4f4f\u5251\u67c4\uff0c\u968f\u4fbf\u6325\u821e\u4e00\u901a\uff0c\u50cf\u53e3\u95ee\u6240\u9047\u7684\u90a3\u4e9b\u5f3a\u76d7\uff0c\u5341\u4e2a\u516b\u4e2a\uff0c\u8c05\u8fd8\u8fd1\u4e0d\u5382\u4f60\u7684\u8eab\u3002\u4f46 \u613f\u4f60\u4e00\u8def\u5e73\u5b89\uff0c\u65e0\u987b\u52a8\u7528\u3002\u5927\u7ea6\u8fdf\u5219\u4e94\u5929\uff0c\u5c11\u5219\u4e09\u5929\uff0c\u6211\u5c31\u4f1a\u5230\u592a\u539f\u5e9c\u8859\u5411\u4f60\u8981\u56de\u8fd9\u628a\u5251 \u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u628a\u5979\u5f53\u4f5c\u4e0d\u6562\u62ff\u5200\u5f04\u5251\u7684\u5343\u91d1\u5c0f\u59d0\uff0c\u6b63\u81ea\u5520\u5520\u53e8\u53e8\u7684\u548c\u5979\u8bf4\u8bdd\uff0c\u66ae\u7136\u95f4\uff0c\u5ffd\u89c1\u4e24 \u9897\u6676\u83b9\u7684\u6cea\u73e0\u4ece\u5979\u7684\u773c\u89d2\u6ef4\u4e86\u4e0b\u6765\uff0c\u6c5f\u6d77\u5929\u83ab\u540d\u6240\u4ee5\uff0c\u53c8\u662f\u4e00\u6014\uff0c\u95ee\u9053\uff1a\u201c\u6b27\u9633\u59d1\u5a18\uff0c\u4f60\uff0c \u4f60\u600e\u4e48\u54ed\u4e86\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u4f60\u82e5\u6e05\u9664\u4e86\u810f\u8151\u4e2d\u7684\u4f59\u6bd2\uff0c\u53ef\u4ee5\u9a6c\u4e0a\u6062\u590d\u529f\u529b\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u4e0d\u89e3\u5979 \u4f55\u4ee5\u8fd9\u6837\u53d1\u95ee\uff0c\u4f46\u8fd8\u662f\u636e\u5b9e\u56de\u7b54\u9053\uff1a\u201c\u6211\u8fd8\u672a\u7ec3\u5230\u91d1\u521a\u4e0d\u574f\u8eab\u7684\u9020\u8be3\uff0c\u5373\u7b97\u670d\u4e86\u89e3\u836f\uff0c\u5927\u7ea6 \u4e5f\u8fd8\u5f97\u4e00\u65f6\u4e09\u523b\u7684\u5de5\u592b\uff0c\u65b9\u80fd\u8fd0\u7528\u5185\u529b\u3002\u4f46\u8fd9\u5bf9\u8001\u592b\u5987\u90fd\u5df2\u9003\u4e86\uff0c\u54ea\u91cc\u53bb\u627e\u89e3\u836f\uff1f\u4f60\u4e0d\u5fc5\u7ba1 \u6211\u4e86\uff0c\u4f60\u8d76\u5feb\u6536\u4e86\u94f6\u5b50\uff0c\u62ff\u4e86\u8fd9\u628a\u5251\u53bb\u9003\u751f\u5427\uff01\u8ba9\u6211\u72ec\u81ea\u5728\u8fd9\u513f\u8fd0\u6c14\u7597\u4f24\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u91cc\u6b63\u60f3\uff1a\u201c\u771f\u662f\u4e2a\u4e0d\u61c2\u4e8b\u7684\u5973\u5b69\u5b50\uff0c\u660e\u77e5\u6ca1\u6709\u89e3\u836f\uff0c\u8fd9\u4e9b\u8bdd\u4e0d\u662f\u767d\u8bf4\u4e48\uff1f\u54ce \u5440\uff0c\u60f3\u662f\u5979\u820d\u4e0d\u5f97\u79bb\u5f00\u6211\uff0c\u6240\u4ee5\u80e1\u601d\u4e71\u60f3\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u6b27\u9633\u5a49\u629b\u4e0b\u4e00\u5c0f\u5305\u4e1c\u897f\uff0c\u6025\u58f0\u8bf4\u9053\uff0c\u201c\u8fd9\u662f\u89e3\u836f\uff0c\u4f60 \u8d76\u5feb\u670d\u535c\uff0c\u5982\u8fdf\u5c31\u6765\u4e0d\u53ca\u4e86\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6b27\u9633\u5a49\u5df2\u662f\u4e00\u9635\u98ce\u4f3c\u7684\uff0c\u63a8\u5f00\u67f4\u95e8\u98de\u8dd1\uff01\u770b\u90a3\u71d5\u5b50 \u63a0\u6ce2\u5f0f\u7684\u8f7b\u7075\u8eab\u6cd5\uff0c\u5206\u660e\u662f\u5177\u6709\u4e00\u8eab\u4e0a\u4e58\u7684\u8f7b\u529f\uff01\u6b63\u662f\uff1a \u3000\u3000\u5c11\u5e74\u4e0d\u8bc6\u6c5f\u6e56\u9669\uff0c\u60dc\u628a\u5f3a\u4eba\u5f53\u7f8e\u4eba\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u5341\u4e94\u56de\u3000\u5341\u5206\u9669\u6076\u7f57\u5947\u7978\u3000\u4e00\u7247\u771f\u8bda\u611f\u7389\u4eba \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u5341\u4e94\u56de\u3000\u5341\u5206\u9669\u6076\u7f57\u5947\u7978\u3000\u4e00\u7247\u771f\u8bda\u611f\u7389\u4eba \u3000\u3000\u6c5f\u6d77\u5929\u6014\u4e86\u4e00\u6014\uff0c\u8ffd\u51fa\u5c4b\u5916\uff0c\u53eb\u9053\uff1a\u201c\u6b27\u9633\u59d1\u5a18\uff0c\u8fd9\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u6b27\u9633\u5a49\u7684\u58f0\u97f3 \u8fdc\u8fdc\u4f20\u6765\uff1a\u201c\u6c5f\u5927\u54e5\uff0c\u4f60\u522b\u8ffd\u6765\uff0c\u6211\u65e0\u989c\u518d\u89c1\u4f60\u4e86\u3002\u4f60\u3001\u4f60\u5feb\u670d\u89e3\u836f\uff0c\u5feb\u670d\u89e3\u836f\uff01\u201d\u4ed6\u5fc3\u795e \u4e00\u4e71\uff0c\u6bd2\u8840\u4e0a\u51b2\u8111\u6d77\uff0c\u7a81\u7136\u773c\u775b\u53d1\u9ed1\uff0c\u660f\u7729\u8d77\u6765\uff0c\u9669\u9669\u683d\u5012\u3002\u5f85\u4ed6\u7ad9\u7a33\u811a\u6b65\uff0c\u6b27\u9633\u5a49\u7684\u5f71\u5b50 \u65e9\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e00\u9635\u8ff7\u832b\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u5979\uff0c\u5979\u4e3a\u4ec0\u4e48\u9a97\u6211\uff1f\u5979\u662f\u597d\u4eba\u8fd8\u662f\u574f\u4eba\uff1f\u5979\u662f\u60f3\u5bb3 \u6211\u5417\uff1f\u4e3a\u4ec0\u4e48\u5979\u53c8\u7ed9\u6211\u89e3\u836f\uff1f\u201d \u3000\u3000\u6bd2\u6027\u6e10\u6e10\u53d1\u4f5c\uff0c\u6c5f\u6d77\u5929\u8111\u75db\u6b32\u88c2\uff0c\u5df2\u6ca1\u6cd5\u518d\u7528\u601d\u60f3\uff0c\u53ea\u597d\u518d\u95ee\u67f4\u623f\uff0c\u62fe\u8d77\u90a3\u4e2a\u7eb8\u5305\u3002\u6253 \u5f00\u4e00\u770b\uff0c\u91cc\u9762\u6709\u4e09\u9897\u7c89\u7ea2\u8272\u7684\u4e38\u836f\uff0c\u6c5f\u6d77\u5929\u5fc3\u9053\uff0c\u201c\u8fd9\u5927\u7ea6\u4e0d\u4f1a\u662f\u518d\u9a97\u6211\u7684\u4e86\u5427\uff1f\u597d\uff0c\u5373\u7b97 \u5b83\u662f\u6bd2\u836f\uff0c\u6211\u4e5f\u4e0d\u5728\u4e4e\u591a\u98df\u51e0\u9897\uff0c\u6211\u5012\u8981\u8bd5\u8bd5\u5979\u662f\u5047\u662f\u771f\uff1f\u201d\u836f\u4e38\u53d1\u6563\u51fa\u4e00\u80a1\u81ed\u5473\uff0c\u6c5f\u6d77\u5929 \u634f\u7740\u9f3b\u5b50\uff0c\u4e00\u53e3\u6c14\u628a\u4e09\u9897\u836f\u4e38\u90fd\u541e\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u836f\u4e38\u670d\u4e0b\uff0c\u53ea\u89c9\u5f97\u547d\u8eab\u8840\u8109\u6f8e\u5f20\uff0c\u4e94\u810f\u516d\u8151\u90fd\u597d\u4f3c\u7ffb\u8f6c\u8fc7\u6765\uff0c\u6c5f\u6d77\u5929\u5927\u60ca\uff0c\u8fde\u5fd9\u9759\u5750\u8fd0 \u6c14\uff0c\u8bf4\u4e5f\u5947\u602a\uff0c\u521a\u624d\u8fd0\u6c14\u611f\u5230\u963b\u585e\u7684\u5730\u65b9\uff0c\u73b0\u5728\u90fd\u5df2\u7545\u901a\uff0c\u75db\u695a\u4e0d\u8fc7\u4e00\u4f1a\uff0c\u8840\u8109\u4e00\u8c03\u548c\u4e4b \u540e\uff0c\u7acb\u5373\u4fbf\u611f\u5230\u8212\u670d\u65e0\u6bd4\uff0c\u539f\u6765\u8fd9\u89e3\u836f\u4e43\u662f\u51e0\u79cd\u975e\u5e38\u5389\u5bb3\u7684\u70ed\u6027\u836f\u7269\u5408\u6210\uff0c\u5e38\u4eba\u670d\u4e0b\uff0c\u4f1a\u9ad8 \u70e7\u53d1\u72c2\uff0c\u4f46\u6c5f\u6d77\u5929\u4e2d\u7684\u6bd2\u4e43\u662f\u9634\u6027\u5bd2\u6bd2\uff0c\u6b63\u8981\u8fd9\u79cd\u89e3\u836f\u6765\u4ee5\u6bd2\u653b\u6bd2\uff0c\u6240\u4ee5\u670d\u836f\u4e4b\u521d\uff0c\u867d\u7136\u96be \u53d7\uff0c\u5374\u662f\u552f\u4e00\u5bf9\u75c7\u7684\u826f\u836f\u3002\u6c5f\u6d77\u5929\u8212\u4e86\u53e3\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u5979\u679c\u7136\u6ca1\u6709\u9a97\u6211\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7ee7\u7eed\u9759\u5750\u8fd0\u529f\uff0c\u6b63\u5230\u7d27\u8981\u5173\u5934\uff0c\u5ffd\u542c\u5f97\u5916\u95f4\u6709\u8f7b\u5fae\u7684\u201c\u55b3\u55b3\u201d\u4e4b\u58f0\uff0c\u6765\u5f97\u751a\u4e3a\u8fc5 \u901f\uff0c\u843d\u5728\u6c5f\u6d77\u5929\u8033\u4e2d\uff0c\u4e00\u542c\u4fbf\u77e5\u662f\u6709\u8f7b\u529f\u9ad8\u660e\u7684\u591c\u884c\u4eba\u6765\u4e86\u3002\u6c5f\u6d77\u5929\u5927\u4e3a\u5947\u602a\uff0c\u5fc3\u60f3\uff1a\u201c\u5979 \u600e\u7684\u53bb\u800c\u590d\u6765\uff1f\u54a6\uff0c\u542c\u8fd9\u811a\u6b65\u58f0\u8fd8\u4f3c\u4e4e\u4e0d\u53ea\u4e00\u4eba\u3002\u201d \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u5ffd\u89c1\u6709\u4e24\u4e2a\u4eba\u63a2\u5934\u8fdb\u6765\uff0c\u6b63\u662f\u90a3\u5bf9\u8001\u592b\u5987\uff0c\u6c5f\u6d77\u5929\u5927\u6012\uff0c\u4f46\u4ed6\u8fd0\u6c14\u6b63\u8fd0\u5230\u7d27 \u8981\u5173\u5934\uff0c\u60c5\u7eea\u4e00\u6012\uff0c\u51e0\u4e4e\u8d70\u5165\u5c94\u8def\uff0c\u6c5f\u6d77\u5929\u8fde\u5fd9\u6536\u675f\u771f\u6c14\uff0c\u7d22\u6027\u95ed\u4e0a\u773c\u775b\uff0c\u4e0d\u770b\u4ed6\u4eec\uff0c\u7ee7\u7eed \u8fd0\u529f\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u201c\u8001\u730e\u6237\u201d\u54a6\u7684\u4e00\u58f0\uff0c\u7d27\u63a5\u7740\u6709\u4e00\u4e2a\u5c11\u5973\u7684\u58f0\u97f3\u8bf4\u9053\uff0c\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u6211\u7684 \u5a49\u5e08\u59b9\u5462\uff1f\u201d\u6c5f\u4fae\u5929\u542c\u5f97\u5979\u662f\u6b27\u9633\u5a49\u7684\u5e08\u59d0\uff0c\u4e0d\u7981\u53c8\u7741\u773c\u6765\u77a7\uff0c\u53ea\u89c1\u90a3\u4e9b\u4eba\u90fd\u5df2\u8fdb\u4e86\u67f4\u623f\uff0c \u9664\u4e86\u5c4b\u4e3b\u592b\u5987\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u9ebb\u8863\u9053\u4eba\uff0c\u548c\u4e00\u4e2a\u5e74\u7ea6\u4e8c\u5341\u5de6\u53f3\u7684\u5c11\u5973\u3002\u8fd9\u56db\u4e2a\u4eba\u90fd\u5728\u9762\u9762\u76f8 \u89d1\uff0c\u73b0\u51fa\u975e\u5e38\u8be7\u5f02\u7684\u795e\u60c5\u3002 \u3000\u3000\u90a3\u9ebb\u8863\u9053\u58eb\u51b7\u7b11\u9053\uff1a\u201c\u9a6c\u8001\u5927\uff0c\u4f60\u4e0d\u662f\u81ea\u5938\u4f60\u7684\u79d8\u5236\u6bd2\u9152\u662f\u960e\u738b\u5e16\u4e48\uff1f\u8fd9\u5c0f\u5b50\u5374\u4e3a\u4ec0\u4e48 \u597d\u7aef\u7aef\u7684\uff1f\u201d\u90a3\u8001\u6c49\u5583\u5583\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u4e5f\u4e0d\u660e\u767d\u4e86\uff0c\u5f53\u771f\u662f\u6d3b\u89c1\u9b3c\uff0c\u6d3b\u89c1\u9b3c\uff01\u4ec0\u4e48\u9053\u7406\uff0c \u600e\u80fd\u633a\u5230\u73b0\u5728\uff0c\u8fd8\u4e0d\u660f\u8ff7\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u53cc\u7709\u4e00\u6311\uff0c\u8bf4\u9053\uff1a\u201c\u6562\u60c5\u662f\u5a49\u4e2b\u5934\u7a9d\u91cc\u53cd\u4e86\uff1f\u201d\u90a3\u8001\u5a46\u5a46\u63ed\u5f00\u4e86\u8336\u58f6\u76d6\u5b50\u4e00\u770b\uff0c \u8bf4\u9053\uff1a\u201c\u6e05\u59d1\u5a18\uff0c\u4f60\u4e0d\u53ef\u60dc\u602a\u4f60\u7684\u5e08\u59b9\uff0c\u8fd9\u58f6\u8336\u662f\u7528\u4fee\u7f57\u82b1\u6ce1\u7684\uff0c\u4e5f\u5df2\u7ed9\u8fd9\u5c0f\u5b50\u559d\u4e86\u534a\u58f6 \u4e86\u3002\u201d \u3000\u3000\u4fee\u7f57\u82b1\u662f\u85cf\u8fb9\u5927\u96ea\u5c71\u4e0a\u7279\u4ea7\u7684\u5947\u82b1\uff0c\u5e38\u4eba\u53ea\u8981\u55c5\u5230\u9999\u6c14\uff0c\u4fbf\u4f1a\u7b4b\u9165\u9aa8\u8f6f\uff0c\u4f55\u51b5\u7528\u672a\u6ce1 \u8336\uff0c\u5b9e\u662f\u6bd4\u90a3\u6bd2\u6d12\u66f4\u4e3a\u5389\u5bb3\u3002\u56e0\u6b64\uff0c\u4f17\u4eba\u542c\u4e86\u8fd9\u8bdd\uff0c\u66f4\u662f\u5927\u5927\u5403\u60ca\u3002 \u3000\u3000\u8fd9\u65f6\u6c5f\u6d77\u5929\u4ee5\u5168\u529b\u8fd0\u529f\uff0c\u6b63\u81ea\u5230\u4e86\u6700\u7d27\u8981\u7684\u5173\u5934\uff0c\u9876\u95e8\u4e0a\u70ed\u6c14\u817e\u817e\uff0c\u805a\u6210\u6d53\u96fe\uff0c\u5c31\u4f3c\u84b8 \u7b3c\u4e00\u822c\u3002\u8fd9\u56db\u4e2a\u4eba\u4e0d\u77e5\u4ed6\u5df2\u670d\u4e86\u89e3\u836f\uff0c\u5fc3\u91cc\u5747\u662f\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u559d\u4e86\u6bd2\u9152\uff0c\u53c8\u559d\u4e86\u6bd2\u8336\uff0c\u5c45 \u7136\u8fd8\u80fd\u8fd0\u7528\u8fd9\u6837\u6df1\u539a\u7684\u5185\u529f\uff0c\u54b1\u4eec\u5982\u4f55\u80fd\u662f\u4ed6\u7684\u5bf9\u624b\uff1f\u201d\u4ed6\u4eec\u54ea\u91cc\u77e5\u9053\u6c5f\u6d77\u5929\u6b63\u5728\u51dd\u805a\u771f \u6c14\uff0c\u529b\u6c42\u6253\u901a\u5341\u4e8c\u91cd\u5173\u3002\u5947\u7ecf\u516b\u8109\uff0c\u529f\u529b\u5b9e\u5728\u8fd8\u672a\u80fd\u7528\u6765\u5bf9\u654c\uff0c\u8fd9\u65f6\u5373\u7b97\u662f\u4e2a\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b \u7684\u4eba\uff1b\u4e5f\u53ef\u4ee5\u5c06\u4ed6\u6740\u6b7b\u3002 \u3000\u3000\u90a3\u9ebb\u8863\u9053\u571f\u5728\u56db\u4eba\u4e4b\u4e2d\uff0c\u6b66\u5b66\u9020\u8be3\u6700\u6df1\uff0c\u89c1\u8bc6\u4e5f\u6700\u9ad8\uff0c\u8fd9\u65f6\u4e5f\u5df2\u60f3\u5230\u4e86\u8fd9\u4e00\u70b9\uff0c\u4f46\u4ed6\u662f \u4e2a\u8001\u8c0b\u6df1\u7b97\u4e4b\u4eba\uff0c\u968f\u5373\u53c8\u60f3\u9053\uff1a\u201c\u5018\u82e5\u4ed6\u529f\u529b\u672a\u66fe\u6062\u590d\uff0c\u6211\u4eec\u81ea\u662f\u53ef\u4ee5\u4e00\u51fb\u6210\u529f\u3002\u4f46\u5018\u82e5\u4ed6 \u8fd8\u6709\u4f59\u529b\u5e94\u4ed8\uff0c\u6211\u53bb\u60f9\u4ed6\uff0c\u5c82\u975e\u5148\u81ea\u906d\u6b83\uff1f\u201d\u4ed6\u7709\u5934\u4e00\u76b1\uff0c\u8ba1\u4e0a\u5fc3\u6765\uff0c\u5ffd\u5730\u5bf9\u90a3\u8001\u6c49\u9053\uff1a \u201c\u9a6c\u8001\u5927\uff0c\u8fd9\u662f\u5728\u4f60\u5bb6\u4e2d\uff0c\u4f60\u7f16\u7684\u7ba9\u7b50\u4e0d\u5706\uff0c\u8be5\u5f53\u4f60\u81ea\u5df1\u53bb\u4fee\u3002\u4f60\u8fd8\u4e0d\u53bb\u5254\u5254\u6cb9\u706f\uff0c\u770b\u5b83\u662f \u4eae\u4e0d\u4eae\uff1f\u201d\u8fd9\u51e0\u53e5\u8bdd\u662f\u6c5f\u6e56\u9690\u8bed\uff0c\u610f\u601d\u662f\u8bf4\uff1a\u201c\u9a6c\u8001\u5927\uff0c\u4f60\u7684\u4e8b\u60c5\u529e\u5f97\u4e0d\u597d\uff0c\u53ea\u597d\u8bf7\u4f60\u53bb\u8bd5 \u8fd9\u5c0f\u5b50\u7684\u6b66\u529f\uff0c\u770b\u4ed6\u8fd8\u6709\u591a\u5f3a\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5374\u4e0d\u61c2\u5f97\u4ed6\u8bdd\u91cc\u7684\u610f\u601d\uff0c\u5fc3\u91cc\u5947\u602a\uff1a\u201c\u8fd9\u4e2a\u65f6\u5019\uff0c\u4ed6\u4eec\u600e\u7684\u6709\u95f2\u5fc3\u60c5\u53bb\u4fee\u7ba9\u7b50\u3001 \u5254\u6cb9\u706f\u554a\uff1f\u54ce\uff0c\u4ed6\u4eec\u80e1\u8a00\u4e71\u8bed\uff0c\u6211\u53ef\u4e0d\u80fd\u7ed9\u4ed6\u4eec\u6270\u4e71\u4e86\u5fc3\u795e\u3002\u201d\u7d22\u6027\u518d\u56e2\u4e0a\u773c\u775b\uff0c\u51dd\u795e\u8fd0 \u529f\uff0c\u5bf9\u5916\u540c\u4e00\u5207\uff0c\u4e0d\u95fb\u4e0d\u95ee\u3002 \u3000\u3000\u90a3\u8001\u6c49\u89c1\u6c5f\u6d77\u5929\u5982\u6b64\u9547\u5b9a\u4ece\u5bb9\uff0c\u5fc3\u91cc\u66f4\u7740\u4e86\u614c\uff0c\u4ed6\u72b9\u7591\u4e86\u597d\u4e00\u4f1a\uff0c\u5728\u90a3\u9053\u58eb\u51cc\u5389\u7684\u773c\u5149 \u5a01\u80c1\u4e4b\u4e0b\uff0c\u7ec8\u4e8e\u4e0d\u5f97\u4e0d\u6a2a\u8d77\u5fc3\u80a0\u3002\u786c\u7740\u5934\u76ae\uff0c\u52c9\u5f3a\u4e00\u8bd5\uff0c\u4ed6\u5728\u5c4b\u89d2\u6284\u8d77\u4e86\u4e00\u6761\u6241\u62c5\uff0c\u8eab\u5b50\u5fae \u5fae\u53d1\u6296\uff0c\u8d70\u4e00\u6b65\u3001\u505c\u4e00\u4e0b\uff0c\u8d70\u5230\u4e86\u6c5f\u6d77\u5929\u7684\u8ddf\u524d\uff0c\u89c1\u6c5f\u6d77\u5929\u4ecd\u662f\u95ed\u76ee\u7aef\u5750\uff0c\u8eab\u4e86\u52a8\u4e5f\u4e0d\u52a8\u3002 \u4ed6\u54ac\u4e86\u54ac\u5440\uff0c\u84e6\u5730\u4e00\u58f0\u5927\u559d\uff0c\u6a2a\u8d77\u6241\u62c5\uff0c\u671d\u7740\u6c5f\u6d77\u5929\u7684\u8111\u95e8\u4fbf\u7528\u529b\u4e00\u6251\u3002 \u3000\u3000\u731b\u542c\u5f97\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u5251\u5149\u8000\u773c\uff0c\u53ea\u89c1\u90a3\u8001\u6c49\u5df2\u5411\u540e\u8dcc\u4e86\u4e2a\u4ef0\u516b\u53c9\uff0c\u90a3\u6839\u6241\u62c5\u4e5f\u88ab\u524a\u6210 \u4e86\u4e24\u6bb5\u3002\u6c5f\u6d77\u5929\u4ecd\u7136\u76d8\u819d\u800c\u5750\uff0c\u53cc\u773c\u90fd\u672a\u66fe\u5f20\u5f00\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u5927\u60ca\uff0c\u8fde\u5fd9\u5c06\u5979\u4e08\u592b\u6276\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u7f8a\u726f\u4e0d\u9a6f\uff0c\u6843\u513f\u96be\u541e\uff0c\u4e0d\u5982\u626f\u547c\uff0c\u518d\u89c5\u5c60 \u592b\uff01\u201d\u90a3\u610f\u601d\u662f\u8bf4\uff1a\u201c\u654c\u4eba\u5389\u5bb3\uff0c\u4e09\u5341\u516d\u8ba1\uff0c\u8d70\u4e3a\u4e0a\u7b56\uff0c\u627e\u5230\u4e86\u5e2e\u624b\u518d\u6765\u3002\u201d \u3000\u3000\u9ebb\u8863\u9053\u4eba\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u9a6c\u5927\u5ac2\uff0c\u4f60\u8d70\u4e86\u773c\u5566\uff0c\u4f60\u95ee\u95ee\u9a6c\u5927\u54e5\uff0c\u662f\u4e0d\u662f\u4ed6\u81ea\u5df1\u8dcc\u5012\u7684\uff1f\u201d \u90a3\u8001\u6c49\u4e0d\u5f85\u4ed6\u59bb\u5b50\u6765\u6276\u5df2\u81ea\u8df3\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff0c\u201c\u4e0d\u9519\uff0c\u8fd9\u5c0f\u5b50\u529f\u529b\u672a\u590d\uff0c\u5e76\u80a9\u5b50\u4e0a\u5440\uff01\u201d \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u4e4b\u6240\u4ee5\u80fd\u591f\u524a\u65ad\u4ed6\u7684\u6241\u62c5\uff0c\u5b8c\u5168\u662f\u4ed7\u7740\u5b9d\u5251\u7684\u950b\u5229\uff0c\u548c\u5584\u4e8e\u201c\u501f\u529b\u4f7f\u529b\u201d\u7684 \u6cd5\u95e8\uff0c\u4ed6\u7684\u5b9d\u5251\u6709\u65ad\u91d1\u524a\u94c1\u4e4b\u80fd\uff0c\u53ea\u662f\u4e1d\u6beb\u76ae\u4e0d\u51fa\u6c14\u529b\uff0c\u90a3\u9a6c\u8001\u5927\u82e5\u662f\u7528\u529b\u4e0d\u5927\uff0c\u4ed6\u7684\u6241\u62c5 \u8fd8\u4e0d\u81f3\u4e8e\u524a\u65ad\uff0c\u6b63\u56e0\u4ed6\u7528\u529b\u5927\u5927\uff0c\u6240\u4ee5\u4e0d\u557b\u662f\u5e2e\u5fd9\u4e86\u6c5f\u6d77\u5929\uff0c\u81ea\u5df1\u7528\u8c46\u8150\u78b0\u5728\u5200\u53e3\u4e0a\u4e86\u3002\u90a3 \u9ebb\u8863\u9053\u4eba\u662f\u4e2a\u6b66\u5b66\u884c\u5bb6\uff0c\u4e00\u773c\u5c31\u770b\u51fa\u4e86\u90a3\u9a6c\u8001\u5927\u662f\u7ed9\u81ea\u5df1\u7684\u53cd\u529b\u6454\u5012\u7684\uff0c\u800c\u4e0d\u662f\u7ed9\u6c5f\u6d77\u5929\u7684 \u5185\u529b\u9707\u5012\u7684\u3002 \u3000\u3000\u90a3\u9ebb\u8863\u9053\u4eba\u770b\u51fa\u4e86\u6c5f\u6d77\u5929\u672a\u80fd\u8fd0\u7528\u5185\u529b\u4e4b\u540e\uff0c\u767b\u65f6\u5fc3\u96c4\u80c6\u58ee\uff0c\u4eba\u58f0\u53eb\u9053\uff1a\u201c\u53ea\u7559\u5fc3\u4e0d\u8981 \u78b0\u7740\u8fd9\u5c0f\u5b50\u7684\u5b9d\u5251\u5c31\u884c\u4e86\u3002\u54b1\u4eec\u6349\u4e2a\u6d3b\u7684\uff01\u201d\u4ed6\u4e00\u9a6c\u5f53\u5148\uff0c\u957f\u5251\u4e00\u633a\uff0c\u5c31\u523a\u6c5f\u6d77\u5929\u80c1\u4e0b\u7684\u8f6f \u9ebb\u7a74\u3002 \u3000\u3000\u4ed6\u4ee5\u4e3a\u6c5f\u6d77\u5929\u5df2\u4e1d\u6beb\u6ca1\u6709\u62b5\u6297\u7684\u80fd\u529b\uff0c\u90a3\u8fd8\u4e0d\u662f\u624b\u5230\u64d2\u6765\uff1f\u54ea\u77e5\u6b63\u5de7\u5c31\u5728\u8fd9\u4e2a\u65f6\u5019\uff0c\u6c5f \u6d77\u5929\u5df2\u7ecf\u6253\u901a\u4e86\u5341\u4e8c\u91cd\u5173\uff0c\u5947\u7b4b\u516b\u8109\uff0c\u771f\u6c14\u6d41\u8f6c\u5168\u8eab\uff0c\u529f\u529b\u5c3d\u90e8\u6062\u590d\uff01 \u3000\u3000\u773c\u770b\u90a3\u67c4\u957f\u5251\u582a\u582a\u523a\u5230\uff0c\u6c5f\u6d77\u5929\u5ffd\u5730\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u6307\u75be\u5f39\uff0c\u8fd9\u4e00\u5f39\u6b63\u4e2d\u5251\u810a\uff0c\u90a3\u9ebb\u8863\u9053 \u4eba\u867d\u7136\u529f\u529b\u4e0d\u51e1\uff0c\u5374\u600e\u53ca\u5f97\u4e0a\u6c5f\u6d77\u5929\u8fd9\u6b63\u90aa\u5408\u4e00\u7684\u72ec\u95e8\u7384\u529f\uff0c\u4f46\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u67c4\u957f \u5251\u5c31\u6709\u5982\u7ed9\u4eba\u7528\u94c1\u68d2\u6572\u51fb\u4e00\u822c\uff0c\u7acb\u5373\u8361\u5f00\uff0c\u51e0\u4e4e\u8131\u624b\u98de\u53bb\uff01 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u6b27\u9633\u5a49\u7684\u5e08\u59d0\u4ea6\u5df2\u63a0\u5230\uff0c\u5979\u4f7f\u7684\u662f\u6839\u8f6f\u97ad\uff0c\u8f6f\u97ad\u4e00\u6296\uff0c\u4f7f\u51fa\u4e86\u4e2a \u201c\u67af\u85e4\u7f20\u6811\u201d\u7684\u62db\u6570\uff0c\u5411\u6c5f\u6d77\u5929\u7684\u624b\u8155\u7f20\u6765\u3002\u539f\u6765\u5979\u662f\u754f\u60e7\u6c5f\u6d77\u5929\u7684\u5b9d\u5251\uff0c\u610f\u6b32\u5148\u628a\u4ed6\u7684\u5b9d \u5251\u593a\u51fa\u624b\u53bb\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u770b\u4f60\u662f\u5979\u59d0\u59d0\u7684\u4efd\u4e0a\uff0c\u6211\u4e0d\u6740\u4f60\uff01\u201d\u5ffd\u5730\u628a\u5b9d\u5251\u4e00\u63b7\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4f60 \u4eec\u8fd9\u4e00\u73ed\u4eba\u8fd8\u4e0d\u503c\u5f97\u6211\u52a8\u7528\u5b9d\u5251\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u7684\u97ad\u6cd5\u786e\u662f\u4e86\u5f97\uff0c\u6c5f\u6d77\u5929\u63b7\u5251\u56de\u8eab\uff0c\u7528\u7684\u4e43\u662f\u5929\u7f57\u6b65\u6cd5\uff0c\u65b9\u4f4d\u5728\u77ac\u606f\u4e4b\u95f4\u5df2\u7ecf\u4e09 \u53d8\uff0c\u4f46\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u4ecd\u7136\u7ed9\u90a3\u5c11\u5973\u7684\u8f6f\u97ad\u7f20\u4e0a\u4e86\u624b\u8155\uff0c\u90a3\u5c11\u5973\u8fc8\u524d\u4e24\u6b65\uff0c\u8f6f\u97ad\u6536\u7d27\uff0c \u5728\u6c5f\u6d77\u5929\u8109\u95e8\u4e0a\u56f4\u4e86\u4e09\u531d\uff0c\u6709\u5982\u7ed9\u4ed6\u6234\u4e0a\u4e86\u4e00\u526f\u624b\u62f7\uff01 \u3000\u3000\u9ebb\u8863\u9053\u4eba\u5927\u559c\uff0c\u4e00\u58f0\u559d\u9053\uff0c\u201c\u5c0f\u5b50\uff0c\u770b\u4f60\u8fd8\u6562\u901e\u80fd\uff1f\u201d\u5530\u7684\u4e00\u5251\u53c8\u523a\u8fc7\u6765\uff0c\u8fd9\u4e00\u5251\u6765\u5f97 \u66f4\u4e3a\u5389\u5bb3\uff0c\u76f4\u6307\u6c5f\u6d77\u5929\u5589\u4e0b\u4e09\u5bf8\u7684\u9b42\u95e8\u7a74\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u7247\u201c\u683c\u52d2\u201d\u201c\u683c\u52d2\u201d\u7684\u54cd\u58f0\uff0c\u5c31\u4f3c\u70ed\u9505\u5b50\u91cc\u7206\u88c2\u7684\u7092\u8c46\u58f0\u97f3\u4e00\u6837\uff0c\u53ea\u89c1\u90a3\u6761\u8f6f \u97ad\u5bf8\u5bf8\u788e\u88c2\uff0c\u7eb7\u7eb7\u843d\u4e0b\uff0c\u539f\u6765\u662f\u7ed9\u6c5f\u6d77\u5929\u7684\u62a4\u4f53\u795e\u529f\u9707\u5f97\u5bf8\u5bf8\u65ad\u4e86\uff01 \u3000\u3000\u9ebb\u8863\u9053\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6c5f\u6d77\u5929\u5df2\u662f\u4e00\u4e2a\u864e\u8df3\uff0c\u8fce\u7740\u4ed6\u7684\u5251\u950b\u559d\u9053\uff1a\u201c\u725b \u9f3b\u5b50\uff0c\u6211\u4e5f\u8981\u770b\u4f60\u8fd8\u6709\u4ec0\u4e48\u80fd\u8010\uff01\u201d\u518d\u5ea6\u5c55\u51fa\u4e00\u6307\u795e\u529f\uff0c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u53c8\u5728\u4ed6\u7684\u957f\u5251\u4e0a\u5f39 \u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u5f39\u6c5f\u6d77\u5929\u7528\u4e0a\u4e86\u516b\u6210\u529f\u529b\uff0c\u800c\u4e14\u4f7f\u4e0a\u4e86\u201c\u9694\u7269\u4f20\u529f\u201d\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u90a3\u9ebb\u8863\u9053\u4eba\u7684\u864e \u53e3\u4fbf\u5982\u7ed9\u4eba\u7528\u5229\u9525\u523a\u4e86\u4e00\u4e0b\u4f3c\u7684\uff0c\u767b\u65f6\u864e\u53e3\u88c2\u5f00\uff0c\u8840\u6d41\u5982\u6ce8\uff0c\u4ed6\u7684\u529f\u529b\u4e5f\u786e\u662f\u4e0d\u51e1\uff0c\u5c45\u7136\u672a \u7ed9\u9707\u5012\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u957f\u5251\u8131\u624b\u63b7\u51fa\uff0c\u76f4\u5411\u6c5f\u6d77\u5929\u7684\u54bd\u5589\u98de\u6765\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7109\u80fd\u7ed9\u4ed6\u523a\u4e2d\uff0c\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u4fbf\u5373\u95ea\u5f00\uff0c\u4f46\u90a3\u9ebb\u8863\u9053\u4eba\u4ea6\u5df2\u8d81\u6b64\u65f6\u673a\uff0c\u9003\u51fa\u67f4 \u623f\u53bb\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u53eb\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u4eec\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f60\u4eec\u4f55\u4ee5\u8981\u6697\u7b97\u6211\uff0c\u987b\u5f97\u8bb2\u51fa\u4e2a\u9053\u7406\u6765\uff01\u201d\u98de\u6b65\u4e0a \u524d\uff0c\u90a3\u5c11\u5973\u521a\u8dd1\u5230\u95e8\u53e3\u3002\u6c5f\u6d77\u5929\u7684\u4e94\u6307\u5df2\u642d\u4e0a\u5979\u7684\u80a9\u5934\u3002 \u3000\u3000\u90a3\u5c11\u5973\u65a5\u9053\uff1a\u201c\u4f60\u597d\u65e0\u793c\uff01\u201d\u80a9\u5934\u4e00\u6c89\uff0c\u500f\u5730\u56de\u8eab\uff0c\u671d\u7740\u6c5f\u6d77\u5929\u7684\u80f8\u53e3\u4fbf\u662f\u4e00\u638c\u3002\u6c5f\u6d77 \u5929\u8fd9\u4e00\u6293\u82e5\u7136\u6293\u4e0b\uff0c\u672c\u6765\u53ef\u4ee5\u5c06\u90a3\u5c11\u5973\u6293\u7262\uff0c\u4f46\u4ed6\u7ed9\u8fd9\u5c11\u5973\u4e00\u65a5\uff0c\u4e0d\u7531\u5f97\u5fc3\u5934\u7a81\u7136\u4e00\u8df3\uff0c\u60f3 \u9053\u3002\u201c\u4e0d\u9519\uff0c\u5979\u5230\u5e95\u662f\u4e2a\u5e74\u8f7b\u7684\u5973\u5b50\uff0c\u6211\u5c82\u53ef\u6293\u5979\u7684\u9165\u80f8\uff1f\u201d\u90a3\u5c11\u5973\u7684\u6b66\u529f\u4e0d\u5728\u9ebb\u8863\u9053\u4eba\u4e4b \u4e0b\uff0c\u6c5f\u6d77\u5929\u7a0d\u4e00\u72b9\u7591\uff0c\u5df2\u7ed9\u5979\u4e00\u638c\u6253\u4e2d\uff0c\u90a3\u5c11\u5973\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u88ab\u4ed6\u62a4\u4f53\u795e\u529f\u6240\u9707\uff0c\u6454\u51fa\u95e8 \u5916\uff0c\u8fde\u5fd9\u722c\u8d77\u8eab\u6765\u98de\u9003\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7ed9\u5979\u91cd\u91cd\u6253\u4e86\u4e00\u638c\uff0c\u867d\u672a\u53d7\u4f24\uff0c\u4e5f\u7ed9\u6253\u5f97\u773c\u5192\u91d1\u661f\uff0c\u8dc4\u8dc4\u8e09\u8e09\u7684\u9000\u4e86\u51e0\u6b65\u3002 \u3000\u3000\u90a3\u5bf9\u8001\u592b\u5987\u8f7b\u529f\u8f83\u5dee\uff0c\u8fd8\u672a\u66fe\u8dd1\u8fdc\uff0c\u6c5f\u6d77\u5929\u7ad9\u7a33\u811a\u6b65\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u626c\u58f0\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60 \u4e3a\u4ec0\u4e48\u7528\u6bd2\u9152\u5bb3\u6211\uff1f\u4e0d\u8bf4\u660e\u767d\uff0c\u53ef\u4f11\u60f3\u9003\uff1f\u201d\u4ed6\u811a\u5c16\u4e00\u70b9\uff0c\u767b\u65f6\u5982\u7bad\u9ad8\u5f26\uff0c\u53ea\u4e00\u6293\u5c31\u628a\u90a3 \u201c\u9a6c\u8001\u5927\u201d\u6293\u4f4f\uff01 \u3000\u3000\u90a3\u8001\u5934\u6740\u732a\u822c\u7684\u5927\u53eb\u4e00\u58f0\uff0c\u4e00\u5bf9\u767d\u6e17\u6e17\u7684\u773c\u73e0\u4f3c\u91d1\u9c7c\u822c\u7684\u51f8\u51fa\u6765\u3002\u6c5f\u6d77\u5929\u867d\u7136\u4e0d\u50cf\u4ed6\u7236 \u4eb2\u591a\u5634\uff0c\u4f46\u5fe0\u539a\u7684\u6027\u683c\uff0c\u5374\u662f\u548c\u7236\u4eb2\u3002\u4e00\u6837\uff0c\u89c1\u90a3\u8001\u5934\u75db\u695a\u7684\u795e\u60c5\uff0c\u60f3\u8d77\u4ed6\u662f\u4e2a\u4e0a\u4e86\u5e74\u7eaa\u7684 \u8001\u4eba\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u540c\u65f6\u4e5f\u6709\u70b9\u5bb3\u6015\uff0c\u4e0d\u77e5\u4e0d\u89c9\u7684\u4fbf\u653e\u677e\u4e86\u624b\u6307\u3002\u90a3\u8001\u5934\u66ae\u5730\u6323\u8131\uff0c\u4e94 \u6307\u7528\u529b\u7684\u5728\u6c5f\u6d77\u5929\u7684\u80f8\u53e3\u4e00\u63d2\u3002\u8fd9\u4e00\u63d2\u6b63\u662f\u6b7b\u7a74\u201c\u6f29\u7391\u7a74\u201d\u7684\u90e8\u4f4d\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u6709\u5dc5\u5012\u7a74\u9053\u7684\u529f\u592b\uff0c\u5f53\u7136\u4e0d\u4f1a\u6bd9\u547d\uff0c\u4f46\u542c\u5f97\u201c\u54c6\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u9a6c\u8001\u5927\u5374\u7ed9\u4ed6\u7684\u62a4 \u4f53\u795e\u529f\u9707\u7ffb\uff0c\u8dcc\u51fa\u4e86\u4e09\u4e08\u5f00\u5916\u3002 \u3000\u3000\u53ef\u662f\uff0c\u7531\u4e8e\u6c5f\u6d77\u5929\u6ca1\u6709\u9632\u5907\uff0c\u800c\u654c\u4eba\u7528\u7684\u53c8\u662f\u91cd\u624b\u6cd5\u70b9\u7a74\uff0c\u56e0\u6b64\u6c5f\u6d77\u5929\u867d\u6ca1\u53d7\u4f24\uff0c\u4f46\u4e5f \u611f\u5230\u6d51\u8eab\u9ebb\u8f6f\uff0c\u6323\u624e\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u624d\u722c\u5f97\u8d77\u6765\u3002\u90a3\u8001\u5a46\u5a46\u89c1\u4e08\u5927\u88ab\u6c5f\u6d77\u5929\u9707\u5f97\u53d1\u660f\uff0c\u6551\u5927\u7d27 \u8981\uff0c\u54ea\u91cc\u8fd8\u6562\u518d\u53bb\u60f9\u4ed6\uff1f\u5f85\u5230\u6c5f\u6d77\u5929\u80fd\u591f\u4e3e\u6b65\u4e4b\u65f6\uff0c\u90a3\u8001\u5a46\u5a46\u65e9\u5df2\u80cc\u4e86\u5927\u592b\uff0c\u8dd1\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8c03\u5300\u4e86\u6c14\u606f\uff0c\u56de\u8f6c\u67f4\u623f\uff0c\u53d6\u56de\u5b9d\u5251\uff0c\u80cc\u8d77\u884c\u56ca\uff0c\u8fd9\u65f6\u5df2\u662f\u5929\u8272\u5fae\u660e\uff0c\u4e1c\u65b9\u53d1\u767d\u7684 \u65f6\u5206\u3002\u4ed6\u90a3\u5339\u5750\u9a91\u65e9\u5df2\u4e0d\u89c1\uff0c\u5927\u7ea6\u662f\u90a3\u9a6c\u8001\u5927\u591c\u95f4\u9a91\u53bb\u62a5\u8baf\uff0c\u5c31\u6ca1\u6709\u518d\u9a91\u56de\u6765\uff0c\u6c5f\u6d77\u5929\u53ea\u597d \u5f92\u6b65\u767b\u7a0b\u3002 \u3000\u3000\u65f7\u91ce\u65e0\u4eba\uff0c\u53ea\u8349\u5730\u4e0a\u7559\u4e0b\u8bb8\u591a\u51cc\u4e71\u7684\u8db3\u5370\uff0c\u6c5f\u6d77\u5929\u8e0f\u7740\u90a3\u4e9b\u4eba\u7684\u8db3\u5370\uff0c\u60f3\u8d77\u6628\u665a\u7684\u79cd\u79cd \u602a\u4e8b\uff0c\u604d\u5982\u505a\u4e86\u4e00\u573a\u6076\u68a6\u3002\u81ea\u5df1\u548c\u90a3\u4e9b\u4eba\u83ab\u540d\u5176\u5999\u7684\u5927\u6253\u4e00\u573a\uff0c\u5230\u5e95\u90a3\u4e9b\u4eba\u4e3a\u751a\u4e48\u8981\u6697\u7b97 \u4ed6\uff0c\u5140\u81ea\u8fd8\u662f\u4e00\u4e2a\u8c1c\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6447\u4e86\u6447\u5934\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u5230\u5e95\u8fd8\u662f\u5916\u5a46\u7684\u8bf4\u8bdd\u5bf9\u4e86\uff0c\u5916\u5a46\u8bf4\u4eba\u5fc3\u9669 \u6076\uff0c\u679c\u7136\u4e0d\u9519\uff01\u201d \u3000\u3000\u4f46\u4ed6\u968f\u5373\u53c8\u6447\u4e86\u6447\u5934\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff0c\u201c\u7239\u7239\u7684\u8bdd\u4e5f\u4e0d\u9519\u3002\u4ed6\u8bf4\u4eba\u4e4b\u521d\uff0c\u6027\u672c\u5584\uff0c\u4eba\u4eba\u672c \u6765\u90fd\u662f\u597d\u7684\u3002\u53ea\u8981\u4f60\u62ff\u51fa\u826f\u5fc3\u5bf9\u4eba\uff0c\u522b\u4eba\u4e5f\u4f1a\u62ff\u51fa\u826f\u5fc3\u5bf9\u4f60\u3002\u90a3\u6b27\u9633\u59d1\u5a18\u8d77\u521d\u4e0d\u662f\u60f3\u5bb3\u6211\u7684 \u5417\uff1f\u5230\u5934\u6765\u5374\u8fd8\u662f\u5979\u62ff\u51fa\u89e3\u836f\uff0c\u6551\u4e86\u6211\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u6c5f\u6d77\u592d\u521d\u51fa\u6c5f\u6e56\uff0c\u7b2c\u4e00\u6b21\u5c31\u78b0\u4e0a\u4e86\u8fd9\u79cd\u602a\u4e8b\uff0c\u51e0\u4e4e\u7cca\u91cc\u7cca\u6d82\u7684\u9001\u4e86\u6027\u547d\uff0c\u7a76\u7adf\u7239\u7239\u7684\u8bdd \u5bf9\uff1f\u8fd8\u662f\u5916\u5a46\u7684\u8bdd\u5bf9\uff1f\u6216\u8005\u662f\u4ed6\u4eec\u4e8c\u4eba\u7684\u8bdd\u90fd\u6709\u70b9\u5bf9\u4e5f\u6709\u70b9\u4e0d\u5bf9\uff1f\u6c5f\u6d77\u5929\u8d8a\u60f3\u8d8a\u662f\u8ff7\u832b\uff0c\u53ea \u89c9\u5f97\u4e16\u95ee\u4e0a\u6700\u96be\u6d4b\u7684\u5c31\u662f\u4eba\u5fc3\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e0d\u4f1a\u996e\u9152\uff0c\u6628\u665a\u5f3a\u996e\u4e86\u534a\u58f6\u6bd2\u9152\uff0c\u4f59\u6bd2\u867d\u5df2\u6d88\u9664\uff0c\u9152\u60eb\u8fd8\u6709\u51e0\u5206\uff0c\u4ed6\u60f3\u8d77\u4e86\u6b27\u9633 \u5a49\u8fd9\u6837\u53ef\u7231\u7684\u59d1\u5a18\uff0c\u5374\u8bef\u5165\u6b67\u9014\uff0c\u4e0d\u7981\u4e3a\u5979\u53ef\u601c\uff0c\u4e5f\u4e3a\u5979\u53ef\u60dc\u3002\u5341\u516d\u4e03\u5c81\u7684\u5c11\u5e74\uff0c\u672c\u6765\u6613\u751f \u611f\u6168\uff0c\u6c5f\u6d77\u5929\u7684\u6027\u683c\uff0c\u4ece\u4ed6\u7236\u4eb2\u90a3\u513f\u63a5\u53d7\u4e86\u5584\u826f\u548c\u8bda\u6734\uff0c\u4e5f\u4ece\u5e08\u7236\u91d1\u4e16\u9057\u90a3\u513f\uff0c\u63a5\u53d7\u4e86\u51e0\u5206 \u8c6a\u653e\u758f\u72c2\uff0c\u8fd9\u65f6\u5fc3\u6709\u6240\u89e6\uff0c\u6d6e\u60f3\u8fde\u7fe9\uff0c\u7981\u4e0d\u4f4f\u4ef0\u5929\u957f\u5578\uff0c\u6717\u58f0\u541f\u9053\uff1a\u201c\u4efb\u4ed6\u6d4a\u6d6a\u9ad8\u5343\u4e08\uff0c\u6211 \u81ea\u9752\u83b2\u4e0d\u67d3\u6ce5\uff01\u201d \u3000\u3000\u6717\u541f\u672a\u5df2\uff0c\u5ffd\u6709\u4e00\u9a91\u5feb\u9a6c\u5954\u6765\uff0c\u9a91\u8005\u4f3c\u662f\u4e00\u4e2a\u4e66\u751f\uff0c\u542c\u5f97\u541f\u58f0\uff0c\u84e6\u7136\u5c06\u9a6c\u52d2\u4f4f\uff0c\u62f1\u624b\u95ee \u9053\uff1a\u201c\u4f60\u53ef\u662f\u6c5f\u6d77\u5929\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u592d\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u6562\u60c5\u53c8\u662f\u4e00\u4e2a\u8981\u6697\u7b97\u6211\u7684\u4eba\u6765\u4e86\uff1f\u201d\u7acb\u5373\u6212\u5907\u8d77\u6765\uff0c\u6717\u58f0\u95ee \u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u600e\u4e48\u77e5\u9053\u6211\u7684\u540d\u5b57\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u795e\u8272\u5028\u50b2\uff0c\u4e95\u672a\u79bb\u978d\uff0c\u5c31\u5728\u9a6c\u80cc\u4e0a\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u7ba1\u6211\u662f\u8c01\uff0c\u6211\u53ea\u95ee\u4f60\uff0c\u4f60 \u53ef\u662f\u4e2a\u6709\u80a9\u818a\uff0c\u80fd\u62c5\u5f53\u7684\u7537\u5b50\u6c49\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u83ab\u540d\u5176\u5999\uff0c\u76b1\u76fe\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u4f60\u8fd9\u8bdd\u662f\u751a\u4e48\u610f\u601d\uff0c\u6211\u505a\u4e86\u751a\u4e48\u89c1\u4e0d\u5f97\u4eba\u7684\u4e8b \u60c5\uff0c\u4e0d\u6562\u62c5\u5f53\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u51b7\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u8fd8\u4e0d\u77e5\u9053\u4e48\uff01\u6709\u4e00\u4e2a\u4eba\u5feb\u8981\u7ed9\u4f60\u5bb3\u6b7b\u4e86\uff0c\u4f60\u8fd8\u8fd9\u6837\u60a0\u6e38\u81ea\u5728\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8df3\u8d77\u672a\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u6211\u5bb3\u6b7b\u4e86\u4ec0\u4e48\u4eba\uff1f\u201d\u5fc3\u60f3\u3002\u201c\u6211\u624d\u662f\u51e0\u4e4e\u7ed9\u4eba\u5bb3\u6b7b\u5462\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u4f3c\u662f\u8fde\u4ed6\u8fd9\u53e5\u672a\u66fe\u8bf4\u51fa\u7684\u8bdd\u4e5f\u5df2\u77e5\u6653\uff0c\u7acb\u5373\u8bf4\u9053\u3002\u201c\u4f60\u5fd8\u8bb0\u4e86\u6628\u665a\u548c\u4f60\u5728\u4e00\u8d77\u7684 \u90a3\u4f4d\u59d1\u5a18\u4e48\uff1f\u4f60\u51e0\u4e4e\u7ed9\u4eba\u6bd2\u6b7b\u662f\u4e0d\u662f\uff1f\u540e\u6765\u662f\u5979\u7ed9\u89e3\u836f\u6551\u4e86\u4f60\u4e0d\u662f\uff1f\u4f60\u5f97\u4e86\u6551\uff0c\u5979\u53ef\u8981\u7ed9\u4f60 \u5bb3\u6b7b\u4e86\uff01\u5979\u7684\u5e08\u7236\u77e5\u9053\u4e86\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u73b0\u5728\u6b63\u8981\u628a\u5979\u5904\u6b7b\u5462\uff0c\u53ea\u5f85\u6349\u5230\u4e86\u4f60\u5c31\u4e00\u5e76\u884c\u5211\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5927\u6012\u9053\uff0c\u201c\u597d\uff0c\u4e0d\u5f85\u5979\u6765\u6349\u6211\uff0c\u6211\u5148\u53bb\u89c1\u5979\uff01\u5979\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u7528\u9a6c\u97ad\u4e00\u6307\u8bf4\u9053\uff1a\u201c\u5979\u4eec\u5c31\u5728\u524d\u9762\u5c71\u8c37\u4e4b\u4e2d\u4e00\u5ea7\u5706\u5c4b\u9876\u7684\u5821\u5792\u91cc\u3002\u4f60\u8981\u53cb\u5c31\u5f97\u5feb \u53bb\uff0c\u514d\u5f97\u6b27\u9633\u59d1\u5a18\u591a\u53d7\u76ae\u8089\u4e4b\u82e6\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6c14\u5f80\u4e0a\u51b2\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u6211\u73b0\u5728\u5c31\u53bb\uff01\u201d\u4f46\u4ed6\u521a\u8dd1\u5f97\u4e24\u6b65\uff0c\u90a3\u5c11\u5e74\u53c8\u53eb\u4f4f\u4ed6\u9053\uff1a \u201c\u5582\uff0c\u8fd8\u6709\u4e00\u6837\uff0c\u4f60\u82e5\u679c\u771f\u662f\u4e2a\u6709\u62c5\u5f53\u7684\u7537\u5b50\u6c49\uff0c\u53ef\u5343\u4e07\u522b\u6cc4\u6f0f\u4e86\u662f\u8c01\u6307\u70b9\u4f60\u6765\u7684\u3002\u201d\u6c5f\u6d77 \u5929\u9053\uff1a\u201c\u597d\u5566\uff0c\u4f60\u8fd9\u4eba\u597d\u7f57\u55e6\uff0c\u6211\u4e0e\u4f60\u65e0\u51a4\u65e0\u4ec7\uff0c\u4e3a\u4ec0\u4e48\u8981\u8fde\u7d2f\u4f60\uff1f\u54fc\uff0c\u54fc\uff0c\u4f60\u6015\u8fd9\u4e9b\u4eba\uff0c \u6211\u53ef\u4e0d\u6015\uff01\u201d\u8fd9\u51e0\u53e5\u8bdd\u672a\u66fe\u8bf4\u5b8c\uff0c\u90a3\u5c11\u5e74\u65e9\u5df2\u6325\u8d77\u9a6c\u97ad\uff0c\u50ac\u8d76\u9a6c\u513f\u75be\u9a70\u800c\u53bb\u3002 \u3000\u3000\u6653\u98ce\u6291\u9762\uff0c\u628a\u6c5f\u6d77\u5929\u6709\u70b9\u70ed\u660f\u7684\u8111\u888b\u5439\u5f97\u51b7\u4e86\u4e0b\u6765\uff0c\u4ed6\u84e6\u5730\u60f3\u9053\uff1a\u201c\u5947\u602a\uff0c\u8fd9\u4eba\u600e\u7684\u77e5 \u9053\u5f97\u5982\u6b64\u6e05\u695a\uff1f\u83ab\u975e\u53c8\u662f\u4e00\u4e2a\u9677\u9631\uff1f\u201d\u6c5f\u6d77\u5929\u7ecf\u4e00\u4e8b\u957f\u4e00\u667a\uff0c\u8fd9\u56de\u53ef\u8bf4\u662f\u731c\u5bf9\u4e86\u4e00\u534a\uff0c\u8fd9\u5c11 \u5e74\u4e0e\u6628\u665a\u90a3\u4e9b\u4eba\u786e\u662f\u4e00\u4f19\uff0c\u4f46\u4e5f\u6709\u4e00\u534a\u672a\u66fe\u731c\u5bf9\uff0c\u8fd9\u5c11\u5e74\u6fc0\u4ed6\u524d\u5f80\uff0c\u8fd8\u6709\u53e6\u5916\u539f\u56e0\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u867d\u7136\u5df2\u8d77\u4e86\u7591\u5fc3\uff0c\u4f46\u4f9d\u7136\u8fd9\u6837\u60f3\u9053\uff1a\u201c\u5b81\u53ef\u4fe1\u5176\u6709\uff0c\u4e0d\u53ef\u4fe1\u5176\u65e0\u3002\u6700\u591a\u6211\u518d\u53d7\u6b21 \u9a97\uff0c\u4f46\u5018\u82e5\u6b27\u9633\u59d1\u5a18\u5f53\u771f\u662f\u4e3a\u4e86\u6551\u6211\u800c\u7ed9\u5979\u5e08\u7236\u5904\u6b7b\uff0c\u6211\u7684\u826f\u5fc3\u600e\u5f97\u5b89\u5b81\uff1f\u201d \u3000\u3000\u8fd9\u4e48\u4e00\u60f3\uff0c\u6c5f\u6d77\u5929\u7acb\u5373\u53d1\u529b\u98de\u5954\uff0c\u8fdb\u4e86\u90a3\u4e2a\u5c71\u8c37\uff0c\u679c\u7136\u89c1\u6709\u4e00\u4e2a\u5f0f\u6837\u975e\u5e38\u53e4\u602a\u7684\u5927\u5c4b\uff0c \u692d\u5706\u5f62\u7684\u5c4b\u9876\u7f69\u4e0b\u6765\uff0c\u4f3c\u4e2a\u575f\u5893\u3002\u5c71\u8c37\u5df2\u7ecf\u9634\u51b7\uff0c\u518d\u52a0\u4e0a\u8fd9\u4e2a\u5360\u602a\u7684\u5efa\u7b51\u7269\uff0c\u66f4\u4ee4\u4eba\u611f\u5230\u8be1 \u79d8\u83ab\u6d4b\uff01 \u3000\u3000\u5728\u8fd9\u79cd\u602a\u5f02\u7684\u73af\u5883\u4e4b\u4e2d\uff0c\u6c5f\u6d77\u5929\u4e5f\u81ea\u6709\u70b9\u5fc3\u602f\uff0c\u8bc5\u4ed6\u662f\u521d\u751f\u4e4b\u728a\u4e0d\u754f\u864e\uff0c\u5fc3\u4e0b\u60f3\u9053\uff1a \u201c\u65e2\u6765\u4e4b\uff0c\u5219\u5b89\u4e4b\uff0c\u7ba1\u4ed6\u662f\u9f99\u6f6d\u864e\u7a74\uff0c\u6211\u4e5f\u5f97\u95ef\u4ed6\u4e00\u95ef\uff01\u201d\u9f13\u8d77\u52c7\u6c14\uff0c\u4ecd\u7136\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u8ddd\u79bb\u90a3\u602a\u5c4b\u5927\u7ea6\u8fd8\u6709\u767e\u6b65\u5de6\u53f3\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u8bf4\u9053\uff0c\u201c\u54a6\uff0c\u662f\u54ea\u4f4d\u5e08\u5144\u56de\u6765\u4e86\uff1f\u201d\u662f\u4e00\u4e2a \u5973\u5b50\u7684\u58f0\u97f3\u3002\u968f\u5373\u542c\u5f97\u4e00\u4e2a\u7537\u5b50\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u8fd9\u662f\u4e00\u4e2a\u964c\u751f\u4eba\uff01\u201d\u6c5f\u6d77\u5929\u5b9a\u775b\u4e00 \u77a7\uff0c\u53d1\u73b0\u90a3\u4e24\u4e2a\u4eba\u539f\u6765\u662f\u85cf\u5728\u4e00\u5757\u5927\u77f3\u80cc\u540e\uff0c\u8fd9\u65f6\u6b63\u81ea\u4f38\u51fa\u5934\u6765\u63a2\u671b\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u60f3\uff1a\u201c\u90a3\u5c11\u5e74\u7684\u8bdd\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff0c\u4e14\u95ee\u4ed6\u4eec\u4e00\u95ee\u3002\u201d\u4fbf\u7528\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u529f\u592b \u5c06\u8bdd\u58f0\u8fdc\u8fdc\u9001\u8fc7\u53bb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4eec\u8fd9\u91cc\u53ef\u6709\u4e00\u4f4d\u6b27\u9633\u5a49\u59d1\u5a18\u4e48\uff1a\u6211\u540d\u53eb\u6c5f\u6d77\u5929\uff0c\u6211\u662f\u6765\u8bbf\u6b27 \u9633\u59d1\u5a18\u7684\uff01\u201d\u4ed6\u7528\u4e86\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u529f\u592b\uff0c\u4e0d\u5355\u662f\u60f3\u8bf4\u7ed9\u90a3\u4e24\u4e2a\u4eba\u542c\uff0c\u4f30\u91cf\u6b27\u9633\u5a49\u5982\u679c\u5728\u5c4b \u5b50\u91cc\u9762\uff0c\u4e5f\u8be5\u53ef\u4ee5\u542c\u5f97\u89c1\u4e86\u3002 \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4fbf\u542c\u5f97\u90a3\u7537\u7684\u4e00\u58f0\u9a82\u9053\uff1a\u201c\u597d\u5927\u80c6\u7684\u6d51\u5c0f\u5b50\uff01\u201d\u970e\u7136\u95f4\u201c\u94ee\u94ee\u201d\u4e24\u58f0\uff0c\u4fbf\u662f \u4e24\u679a\u9e45\u5375\u822c\u5927\u7684\u94c1\u80c6\u98de\u6765\uff0c\u6c5f\u6d77\u5929\u5fc3\u4e2d\u6709\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u597d\u751f\u65e0\u793c\uff0c\u600e\u7684\u4e00\u89c1\u9762\u4fbf\u62ff\u6697\u5668\u6253 \u4eba\u3002\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5df2\u628a\u90a3\u4e24\u679a\u94c1\u80c6\u63a5\u5230\u624b\u4e2d\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6697\u8fd0\u795e\u529f\uff0c\u4e00\u624b\u6267\u4e00\u94c1\u80c6\uff0c\u731b\u5730\u5411\u5929\u4e00\u629b\uff0c\u90a3\u4e24\u679a\u94c1\u80c6\u5728\u534a\u7a7a\u4e00\u649e\uff0c\u767b\u65f6\u53d1\u51fa\u9707 \u8033\u6b32\u804b\u7684\u7206\u70b8\u58f0\uff0c\u88c2\u6210\u65e0\u6570\u788e\u7247\uff0c\u5c04\u51fa\u4e86\u65e0\u6570\u706b\u661f\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u90a3\u5c11\u5973\u6240\u53d1\u7684\u4e24\u53e3\u98de\u5200\u4ea6\u5df2 \u6765\u5230\uff0c\u542c\u90a3\u98de\u5200\u7834\u7a7a\u4e4b\u58f0\uff0c\u529f\u529b\u4f3c\u4e4e\u8fd8\u5728\u90a3\u7537\u5b50\u4e4b\u4e0a\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6709\u610f\u901e\u80fd\uff0c\u5413\u5413\u4ed6\u4eec\uff0c\u4ed6\u8eab\u4e0a\u7a7f\u6709\u4e54\u5317\u6e9f\u4e09\u5b9d\u4e4b\u4e00\u7684\u767d\u7389\u7532\uff0c\u5200\u5251\u96be\u5165\uff0c\u7d22\u6027\u5c31 \u8ba9\u90a3\u4e24\u53e3\u98de\u5200\u780d\u4e2d\u4ed6\u7684\u8eab\u4f53\uff0c\u4f46\u542c\u5f97\u4e00\u7247\u65ad\u91d1\u788e\u7389\u4e4b\u58f0\uff0c\u90a3\u4e24\u53e3\u98de\u5200\uff0e\u88ab\u4ed6\u62a4\u4f53\u795e\u529f\u542c\u9707\uff0c \u4e5f\u65ad\u4e3a\u56db\u6bb5\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff0c\u201c\u6709\u8bdd\u8fd8\u662f\u597d\u597d\u7684\u8bf4\u5427\uff0c\u4f3a\u5fc5\u89c1\u9762\u5c31\u8981\u6253\u67b6\uff1f\u201d\u6d3b\u8bed\u65e0\u4eba\u56de\u7b54\uff0c\u4ed4\u7ec6\u770b \u65f6\uff0c\u90a3\u4e24\u4e2a\u4eba\u5df2\u4e0d\u89c1\u4e86\u3002\u6c5f\u6d77\u5929\u6697\u6697\u7eb3\u7f55\uff1a\u201c\u8fd9\u4e24\u4eba\u6b66\u529f\u5e73\u5e38\uff0c\u8eab\u6cd5\u600e\u4f1a\u5982\u6b64\u5feb\u6377\uff0c\u4ec0\u4e48\u65f6 \u5019\u6e9c\u8d70\u7684\uff0c\u8fde\u6211\u4e5f\u6ca1\u77a7\u89c1\uff01\u201d\u4ed6\u54ea\u77e5\u9053\uff0c\u77f3\u5934\u662f\u4e2d\u7a7a\u7684\uff0c\u91cc\u9762\u85cf\u6709\u673a\u5173\uff0c\u90a3\u4e24\u4e2a\u4eba\u89c1\u4ed6\u5389 \u5bb3\uff0c\u65e9\u5c31\u4ece\u5730\u9053\u4e2d\u6e9c\u56de\u53bb\u62a5\u8baf\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8bb0\u8d77\u5916\u5a46\u7ed9\u4ed6\u6240\u8bb2\u7684\u6c5f\u6e56\u89c4\u77e9\u3002\u5fc3\u60f3\uff0c\u201c\u6211\u8fd8\u662f\u6b63\u6b63\u5f53\u5f53\u7684\u4f9d\u7740\u793c\u6570\u4ee5\u665a\u8f88\u4e4b\u793c \u6c42\u89c1\u5427\u3002\u201d\u8d70\u5230\u90a3\u602a\u5c4b\u524d\u9762\uff0c\u610f\u6b32\u53e9\u95e8\uff0c\u7adf\u5f79\u53d1\u73b0\u95e8\u6237\uff0e\u7528\u624b\u4e00\u6478\uff0c\u5899\u58c1\u662f\u575a\u539a\u7684\u82b1\u5c97\u77f3\uff0c \u53ea\u6015\u52a8\u7528\u5b9d\u5251\uff0c\u8981\u7834\u58c1\u800c\u5165\uff0c\u4e5f\u5f97\u534a\u4e2a\u65f6\u8fb0\u3002\u6c5f\u6d77\u5929\u8e0c\u8e87\u4e86\u4e00\u4f1a\uff0c\u4fbf\u6572\u4e86\u6572\u5899\u58c1\uff0c\u901a\u540d\u7980 \u9053\uff1a\u201c\u665a\u8f88\u6c5f\u6d77\u5929\u6c42\u89c1\u5c42\u4e2d\u4e3b\u4eba\uff0c\u8bf7\u5f00\u95e8\uff01\u201d \u3000\u3000\u5ffd\u542c\u5f97\u5c4b\u5b50\u91cc\u4e00\u4e2a\u51b7\u51b0\u51b0\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u4e0d\u4f1a\u8fdb\u6765\u5417\uff0c\u96be\u9053\u8fd8\u8981\u6211\u53bb\u63a5\u4f60\u4e0d \u6210\uff1f\u201d\u58f0\u97f3\u523a\u8033\u4e4b\u6781\uff0c\u5b9b\u5982\u91d1\u5c5e\u6572\u51fb\u3002\u9707\u5f97\u6c5f\u6d77\u5929\u7684\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u800c\u5df2\u7a81\u7136\u5fc3\u5934\u4e00\u9707\uff0c \u201c\u7075\u9b42\u201d\u597d\u50cf\u5c31\u8981\u51fa\u7a8d\u4e00\u822c\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u542c\u5e08\u7236\u8bf4\u8fc7\uff0c\u90aa\u6d3e\u4e2d\u6709\u4e00\u79cd\u201c\u547c\u9b42\u5524\u9b42\u5927\u6cd5\u201d\uff0c\u80fd\u4ee5\u602a\u58f0\u6270\u4eba\u5fc3\u795e\uff0c\u4ee4\u4eba\u660f\u8ff7\uff0c \u5fc3\u91cc\u60f3\u9053\uff0c\u201c\u539f\u6765\u8fd9\u91cc\u7684\u4e3b\u4eba\u679c\u7136\u662f\u4e00\u4e2a\u90aa\u6d3e\u9ad8\u624b\uff0c\u53ea\u4e0d\u77e5\u662f\u4e0d\u662f\u6b27\u9633\u5a49\u7684\u5e08\u7236\uff1f\u201d\u4ed6\u6240\u7ec3 \u7684\u5947\u95e8\u7384\u529f\u5df2\u5c06\u5230\u6b63\u90aa\u5408\u4e00\u5883\u754c\uff0c\u771f\u6c14\u4e00\u805a\uff0c\u62a4\u7740\u5fc3\u5934\uff0c\u7acb\u5373\u7cbe\u795e\u590d\u632f\u3002 \u3000\u3000\u4f46\u89c1\u4e00\u5e45\u5899\u58c1\u5ffd\u5730\u5de6\u53f3\u79fb\u5f00\uff0c\u73b0\u51fa\u4e86\u4e00\u9053\u95e8\u6237\uff0c\u539f\u6765\u662f\u4e00\u9053\u53ef\u4ee5\u6d3b\u52a8\u7684\u77f3\u95e8\u3002\u6c5f\u6d77\u5929\u5927 \u8e0f\u6b65\u4fbf\u8de8\u8fdb\u53bb\u3002\u6709\u4e2a\u58f0\u97f3\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5012\u597d\u80c6\u91cf\u3002\u201d\u53ea\u95fb\u5176\u58f0\uff0c\u4e0d\u89c1\u5176\u4eba\u3002 \u3000\u3000\u91cc\u9762\u662f\u6761\u96e8\u9053\uff0c\u5e7d\u51b7\u9634\u6697\uff0c\u56db\u9762\u65e0\u4eba\uff0c\u6c5f\u6d77\u5929\u884c\u5230\u5c3d\u5934\uff0c\u53c8\u662f\u4e00\u9053\u94c1\u95e8\uff0c\u91cc\u9762\u7684\u4eba\u4f3c\u6709 \u795e\u773c\uff0c\u5bf9\u4ed6\u7684\u4e3e\u52a8\u770b\u5f97\u4e00\u6e05\u4e8c\u695a\u3001\u4ed6\u521a\u8d70\u5230\u95e8\u524d\uff0c\u6b63\u8981\u6263\u95e8\uff0c\u90a3\u95e8\u53c8\u81ea\u5df1\u5f00\u4e86\u3002\u5982\u6b64\u8fd9\u822c\uff0c \u7ecf\u8fc7\u4e86\u4e09\u9053\u95e8\u6237\uff0c\u8d70\u8fdb\u4e86\u6700\u540e\u4e00\u9053\u94c1\u95e8\u7684\u65f6\u5019\uff0c\u6c5f\u6d77\u5929\u7a81\u7136\u773c\u775b\u4e00\u4eae\uff01 \u3000\u3000\u53ea\u89c1\u90a3\u662f\u4e00\u95f4\u50cf\u662f\u795e\u6bbf\u822c\u7684\u5c4b\u5b50\uff0c\u56db\u89d2\u56db\u5f20\u9999\u6848\uff0c\u6bcf\u5f20\u9999\u6848\u4e0a\u70b9\u7740\u56db\u6839\u7c97\u5982\u51e0\u81c2\u7684\u5de8 \u70db\uff0c\u8000\u773c\u751f\u8f89\u3002\u4f46\u90a3\u70db\u5149\u5374\u662f\u975e\u5e38\u5947\u602a\uff0c\u7eff\u5e7d\u5e7d\u7684\u5982\u540c\u9b3c\u706b\u4e00\u822c\uff0c\u4ee4\u4eba\u611f\u5230\u5bd2\u610f\u3002 \u3000\u3000\u5c4b\u5b50\u7684\u6b63\u4e2d\u5750\u7740\u4e00\u4e2a\u767d\u53d1\u5782\u80a9\u7684\u8001\u5987\uff0c\u9e70\u9f3b\u9614\u76ee\uff0c\u989d\u4e49\u51f8\u51fa\uff0c\u76f8\u8c8c\u751a\u662f\u4e11\u964b\u3002\u5de6\u624b\u8fb9\u7acb \u7740\u4e24\u4e2a\u5c11\u5e74\uff0c\u53f3\u624b\u8fb9\u7acb\u7740\u4e24\u4e2a\u5c11\u5973\u3002\u6c5f\u6d77\u5929\u8ba4\u5f97\u5176\u4e2d\u4e00\u4e2a\u5c11\u5973\u5c31\u662f\u6628\u665a\u66fe\u4e0e\u4ed6\u4ea4\u8fc7\u624b\u7684\u90a3\u4e2a \u6b27\u9633\u5a49\u7684\u5e08\u59d0\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u60f3\uff0c\u201c\u8fd9\u8001\u5987\u60f3\u5fc5\u662f\u6b27\u9633\u5a49\u7684\u5e08\u7236\u4e86\u3002\u90a3\u5c11\u5e74\u8bf4\u5979\u8981\u6740\u6211\uff0c\u4f46\u4ea6\u672a\u53ef\u5c31\u4fe1\u4ee5\u4e3a \u771f\uff0c\u6211\u8fd8\u662f\u4ee5\u793c\u76f8\u89c1\uff0c\u5148\u95ee\u5979\u4e00\u95ee\u3002\u201d \u3000\u3000\u5f53\u4e0b\uff0c\u6c5f\u6d77\u5929\u5c31\u8de8\u4e0a\u4e24\u6b65\uff0c\u5c48\u4e86\u534a\u819d\uff0c\u5411\u5979\u8bf7\u4e86\u4e2a\u5b89\uff0c\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u6c5f\u6d77\u5929\u53c2\u89c1\u524d\u8f88\u3002\u201d \u3000\u3000\u90a3\u8001\u5987\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u91d1\u4e16\u9057\u7684\u5f92\u5f1f\uff0c\u8fd9\u793c\u6211\u53d7\u4e0d\u8d77\uff01\u201d\u6c5f\u6d77\u5929\u5ffd\u89c9\u819d\u76d6\u4f3c\u88ab\u4eba\u4e00 \u62cd\uff0c\u4e0d\u8bb8\u4ed6\u5f2f\u4e0b\uff0c\u4f46\u6c5f\u6d77\u5929\u65e9\u5df2\u6709\u8fd0\u529f\u9632\u5907\u6697\u7b97\uff0c\u5f53\u4e0b\u7acb\u5373\u7528\u4e0a\u4e86\u5343\u65a4\u5760\u7684\u91cd\u8eab\u6cd5\uff0c\u4ecd\u7136\u884c \u4e86\u540e\u8f88\u53c2\u89c1\u524d\u8f88\u7684\u8bf7\u5b89\u793c\u3002 \u3000\u3000\u90a3\u8001\u5987\u4eba\u53cc\u76ee\u4e00\u5f20\uff0c\u8138\u4e0a\u73b0\u51fa\u51e0\u5206\u8be7\u5f02\u7684\u795e\u60c5\uff0c\u968f\u5373\u4fbf\u9634\u607b\u607b\u5730\u95ee\u9053\uff0c\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u89c1 \u6b27\u9633\u5a49\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6b27\u9633\u59d1\u5a18\u4e8e\u6211\u6709\u6069\uff0c\u6211\u662f\u6765\u627e\u5979\u9053\u8c22\u7684\u3002\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u9f87\u7259\u54a7\u9f7f\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u5012\u5f88\u6709\u826f\u5fc3\uff0c\u597d\uff0c\u5c31\u8ba9\u4f60\u89c1\u5979\u4e00\u89c1\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u5f53\u5577\u5577\u4e00\u7247\u58f0\u54cd\uff0c\u90a3\u662f\u94c1\u94fe\u62d6\u5730\u7684\u58f0\u97f3\uff0c\u6b27\u9633\u5a49\u6234\u7740\u624b\u9563\u8d70\u51fa\u6765\u4e86\u3002\u6c5f\u6d77\u5929\u4e2a\u7531 \u5f97\u5fc3\u5934\u4e00\u9707\uff0c\u4ec5\u4ec5\u4e00\u53e3\u4e4b\u9694\uff0c\u90a3\u660e\u8273\u52a8\u4eba\u7684\u6b27\u9633\u5a49\uff0c\u73b0\u5728\u5df2\u662f\u6194\u60b4\u5f97\u50cf\u67af\u840e\u7684\u82b1\u6735\u4e00\u822c\uff0c\u8138 \u4e0a\u82cd\u767d\u65e0\u795e\uff0c\u4e00\u5bf9\u60ca\u60f6\u5931\u8272\u7684\u773c\u775b\uff0c\u5077\u5077\u5730\u671b\u7740\u6c5f\u6d77\u5929\uff0c\u5374\u53c8\u5bb3\u6015\u548c\u4ed6\u7684\u773c\u5149\u63a5\u89e6\uff0c\u4f3c\u662f\u505a \u4e86\u89c1\u4e0d\u5f97\u4eba\u7684\u4e8f\u5fc3\u4e8b\u4f3c\u7684\uff0c\u773c\u5149\u91cc\u542b\u6709\u7f9e\u60ed\uff0c\u542b\u6709\u60ca\u6050\uff0c\u4f46\u4e5f\u542b\u6709\u4ee4\u4eba\u5fc3\u9189\u7684\u5173\u5207\u60c5\u6000\u3002 \u3000\u3000\u6c5f\u6d77\u592d\u4e0d\u7981\u8d77\u4e86\u601c\u60dc\u4e4b\u5ff5\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u53ea\u9053\u5929\u4e0b\u7684\u5e08\u7236\uff0c\u90fd\u662f\u50cf\u7236\u6bcd\u4e00\u6837\u7231\u60dc\u5f92\u5f1f\u7684\u3002 \u600e\u7684\u5979\u7684\u5e08\u7236\u5374\u8fd9\u822c\u6076\u6bd2\uff1f\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u51b7\u7b11\u4e00\u58f0\uff0c\u76ef\u7740\u6b27\u9633\u5a49\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u4f60\u8fd8\u6562\u9a97\u6211\u8bf4\u672a\u66fe\u628a\u89e3\u836f\u7ed9 \u8fd9\u5c0f\u5b50\u5417\uff1f\u201d\u8fd9\u58f0\u97f3\u51b7\u9177\u5f97\u96be\u4ee5\u5f62\u5bb9\uff0c\u53ea\u542c\u5f97\u201c\u535c\u901a\u201d\u4e00\u58f0\uff0c\u6b27\u9633\u5a49\u8dcc\u5012\u5730\u4e0a\uff0c\u6d51\u8eab\u98a4\u6218\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fcd\u4e0d\u4f4f\u5927\u58f0\u9053\uff1a\u201c\u6b27\u9633\u59d1\u5a18\u72af\u7684\u4ec0\u4e48\u7f6a\uff1f\u5c31\u662f\u4e3a\u4e86\u628a\u89e3\u836f\u7ed9\u6211\u5417\uff1f\u6551\u4eba\u6027\u547d\uff0c\u8fd9 \u662f\u5e94\u5f53\u5609\u5956\u7684\u4e8b\u60c5\uff0c\u600e\u53ef\u4ee5\u53cd\u800c\u5c06\u5979\u5904\u7f5a\uff1f\u8fd9\u5c82\u975e\u98a0\u5012\u9ed1\u767d\uff0c\u6ca1\u4e86\u662f\u975e\u4e86\uff01\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u54c8\u54c8\u5927\u7b11\u9053\uff0c\u201c\u4f60\u4eec\u542c\uff0c\u8fd9\u5c0f\u5b50\u5012\u6559\u8bad\u8d77\u6211\u6765\u4e86\u3002\u597d\u50cf\u6211\u6d3b\u4e86\u8fd9\u4e00\u5927\u628a\u5e74\u7eaa\uff0c \u8fd8\u4e0d\u61c2\u5f97\u4e3a\u5e08\u4e4b\u9053\u4f3c\u7684\u3002\u6b27\u9633\u5a49\uff0c\u6211\u95ee\u4f60\uff0c\u672c\u95e8\u7684\u7b2c\u4e00\u6761\u6212\u5f8b\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u6b3a\u5e08\u706d\u7956\u8005\u6b7b\uff01\u201d\u90a3\u8001\u5a46\u5a46\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u7136\u8bb0\u5f97\uff0c\u4e3a\u4f55\u660e\u77e5\u6545 \u72af\uff1f\u6211\u53eb\u4f60\u7528\u6bd2\u9152\u5c06\u8fd9\u5c0f\u5b50\u6349\u6765\uff0c\u4f60\u5374\u53cd\u800c\u7ed9\u4ed6\u89e3\u836f\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8fd9\u624d\u77e5\u9053\uff0c\u6697\u7b97\u4ed6\u7684\u90a3\u4e9b\u4eba\u8fde\u6b27\u9633\u5a49\u5728\u5185\uff1b\u90fd\u662f\u8fd9\u8001\u5a46\u5a46\u6307\u4f7f\u7684\uff0c\u4e0d\u7531\u5f97\u53c8\u60ca\u53c8 \u6012\uff0c\u6025\u58f0\u95f4\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u6211\u4ece\u4e0d\u8ba4\u8bc6\u4f60\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u5bb3\u6211\uff1f\u8bf7\u4f60\u7ed9\u665a\u8f88\u8bb2\u51fa\u4e00\u4e2a\u9053\u7406\u6765\uff01\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u563f\u563f\u7b11\u9053\uff1a\u201c\u4e2a\u628a\u4eba\u547d\u7b97\u5f97\u4e86\u4ec0\u4e48\uff0c\u8fd9\u4e5f\u8981\u8bb2\u9053\u7406\u4e48\uff1f\u54c8\u54c8\uff0c\u6211\u6d3b\u4e86\u8fd9\u4e48\u5927\u5c81 \u6570\uff0c\u8fd8\u4ece\u672a\u66fe\u78b0\u8fc7\u8981\u548c\u6211\u8bb2\u9053\u7406\u7684\u4eba\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6012\u6c14\u4e0a\u51b2\uff0c\u5927\u58f0\u8bf4\u9053\uff0c\u201c\u597d\uff0c\u73b0\u5728\u4e0d\u5fc5\u4f60\u518d\u8d39\u5fc3\u673a\uff0c\u6211\u81ea\u5df1\u4e0a\u95e8\u6765\u4e86\uff0c\u4f60\u5f85\u5c06\u6211 \u600e\u4e48\u6837\uff0c\u8981\u6740\u5462\uff0c\u8fd8\u662f\u8981\u5250\uff01\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u6025\u4ec0\u4e48\uff0c\u8fd8\u672a\u8f6e\u5230\u4f60\u5462\uff1f\u5a49\u513f\uff0c\u4f60\u8fc7\u6765\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u76f4\u6253\u54c6\u55e6\uff0c\u4f46\u5374\u4e0d\u6562\u4e0d\u722c\u8d77\u6765\uff0c\u8d70\u5230\u5979\u5e08\u7236\u9762\u524d\u3002\u90a3\u8001\u5a46\u5a46\u53c8\u51b7\u7b11\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a \u201c\u5a49\u513f\u3002\u4f60\u5f88\u559c\u6b22\u8fd9\u5c0f\u5b50\u4e48\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u82cd\u767d\u7684\u7c89\u8138\u73b0\u51fa\u4e00\u7247\u7ea2\u6655\uff0c\u5ffd\u5730\u62ac\u8d77\u5934\u6765\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u6709\u8fdd\u5e08\u547d\uff0c\u7518\u6b7b\u65e0\u8f9e\u3002\u4f46 \u4f9d\u7167\u672c\u95e8\u89c4\u77e9\uff0c\u5f1f\u5b50\u4e5f\u53ef\u8bf7\u6c42\u5e08\u7236\u4e00\u4ef6\u4e8b\u60c5\uff0c\u5bf9\u4e48\uff1f\u201d\u5979\u8bf4\u8fd9\u51e0\u53e5\u8bdd\u7684\u65f6\u5019\uff0c\u8eab\u8eaf\u4ecd\u5728\u98a4 \u6296\uff0c\u4f46\u58f0\u97f3\u5219\u575a\u5b9a\u975e\u5e38\u3002\u663e\u662f\u5df2\u4e0b\u4e86\u6781\u5927\u7684\u51b3\u5fc3\u3002 \u3000\u3000\u539f\u6765\u5979\u8fd9\u4e00\u95e8\u6709\u6761\u53e4\u602a\u7684\u89c4\u4f8b\uff0c\u5e08\u7236\u6709\u6743\u5904\u6b7b\u5f1f\u5b50\uff0c\u4f46\u88ab\u5904\u6b7b\u7684\u5f1f\u5b50\uff0c\u4e5f\u6709\u6743\u8981\u6c42\u5e08\u7236 \u7b54\u5e94\u4ed6\u4e00\u4ef6\u4e8b\u60c5\uff0c\u4e0d\u7ba1\u8fd9\u4ef6\u4e8b\u60c5\u591a\u96be\uff0c\u505a\u5e08\u7236\u7684\u90fd\u8981\u7ed9\u4ed6\u4ee3\u529e\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u4f3c\u4e4e\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u6211\u7ed9\u4f60\u505a\u4ec0\u4e48\u4e8b\u60c5\uff1f\u8bf4\u5427\uff01\u201d\u6b27\u9633\u5a49\u773c \u6ce2\u5411\u6c5f\u6d77\u5929\u4e00\u6e9c\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8bf7\u4f60\u5c06\u4ed6\u653e\u4e86\uff01\u201d\u58f0\u97f3\u4f4e\u788d\u5982\u540c\u868a\u53eb\uff0c\u53ef\u662f\u6c5f\u6d77\u5929\u5374\u5df2\u542c\u5f97 \u6e05\u6e05\u695a\u695a\u3002\u6b27\u9633\u5a49\u8fd9\u4e2a\u8bf7\u6c42\uff0c\u7b49\u4e8e\u662f\u95f4\u63a5\u7b54\u590d\u4e86\u5979\u5e08\u7236\u521a\u624d\u90a3\u53e5\u95ee\u8bdd\uff0c\u8868\u660e\u4e86\u5979\u662f\u201c\u559c\u6b22\u201d \u6c5f\u6d77\u5929\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u9762\u8272\u4e00\u6c89\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u5973\u751f\u5916\u5411\uff0c\u679c\u7136\u4e0d\u9519\u3002\u6709\u4e86\u60c5\u90ce\uff0c\u5c31\u8fde\u5e08\u7236\u4e5f\u53ef\u4ee5\u4e0d \u8981\u4e86\uff01\u201d\u6c5f\u6d77\u5929\u53c8\u7f9e\u53c8\u6012\uff0c\u53e3\u4e0d\u62e9\u8a00\u7684\u4fbf\u9a82\u9053\uff0c\u201c\u4f60\u80e1\u8bf4\u516b\u9053\u4ec0\u4e48\uff0c\u6211\u4e0e\u6b27\u9633\u59d1\u5a18\u840d\u6c34\u76f8 \u9022\uff0c\u4f60\u600e\u53ef\u8bec\u8511\u6211\u4eec\uff1f\u54fc\uff0c\u54fc\uff0c\u5929\u5e95\u4e0b\u7adf\u6709\u8fd9\u6837\u5b50\u505a\u5e08\u7236\u7684\u3002\u5f53\u771f\u662f\u4e3a\u8001\u4e0d\u5c0a\uff01\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u522b\u5fd9\uff0c\u4f60\u8981\u6559\u8bad\u6211\u4e48\uff1f\u73b0\u5728\u8fd8\u4e0d\u662f\u65f6\u5019\u3002\u201d \u3000\u3000\u53ea\u89c1\u5979\u7f13\u7f13\u8d70\u5230\u6b27\u9633\u5a49\u7684\u9762\u524d\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u8d4f\u7f5a\u7d22\u6765\u516c\u5e73\uff0c\u7f6a\u8be5\u6b7b\u7684\u6211\u7edd\u4e0d\u5bbd\u5bb9\uff0c \u4e0d\u8be5\u6b7b\u7684\u4f60\u8981\u6c42\u6b7b\u4e5f\u6b7b\u4e0d\u4e86\u3002\u5ff5\u5728\u4f60\u8fd9\u6b21\u53ea\u662f\u2018\u6b3a\u5e08\u2019\uff0c\u672a\u66fe\u2018\u706d\u7956\u2019\uff0c\u4f60\u7684\u6027\u547d\u53ef\u4ee5\u4fdd \u5168\uff0c\u5211\u7f5a\u5219\u4e0d\u53ef\u514d\uff0c\u6211\u7f5a\u4f60\u5728\u5e8a\u4e0a\u8eba\u4e0a\u4e09\u5e74\uff0c\u8ba9\u4f60\u5929\u5929\u53ef\u4ee5\u505a\u68a6\uff0c\u68a6\u89c1\u60c5\u90ce\uff01\u201d\u5979\u7f13\u7f13\u5730\u4e3e \u8d77\u638c\u6765\uff0c\u638c\u5fc3\u7684\u8089\u8272\u5ffd\u5730\u53d8\u5f97\u6709\u5982\u4e00\u56e2\u6d53\u58a8\uff01 \u3000\u3000\u6b27\u9633\u5a49\u8fd9\u4e00\u60ca\u771f\u662f\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u53ea\u542c\u5f97\u5979\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u5f00\u5f00\u6069\u6740\u4e86\u6211\u5427\uff0c\u6211 \u5b81\u613f\u6b7b\uff01\u4e0d\u613f\u53d7\u8fd9\u795e\u86c7\u638c\u7684\u6bd2\u5211\uff01\u201d\u539f\u6765\u8fd9\u201c\u795e\u86c7\u638c\u201d\u662f\u4e00\u79cd\u6781\u90aa\u95e8\u7684\u6bd2\u638c\uff0c\u5018\u53d7\u4e00\u638c\uff0c\u4e0d \u4f46\u6b66\u529f\u5168\u5e9f\uff0c\u800c\u4e14\u6700\u5c11\u6709\u4e09\u5e74\u4e0d\u80fd\u52a8\u5f39\uff0c\u8fd9\u8fd8\u4e0d\u7b97\uff0c\u800c\u5de8\u6bcf\u65e5\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff0c\u65e0\u65f6\u65e0\u523b\uff0c\u4f53\u5185 \u90fd\u4f3c\u6709\u5343\u767e\u6761\u6bd2\u86c7\u4e71\u556e\uff0c\u5f53\u771f\u662f\u4e16\u4e0a\u6700\u5389\u5bb3\u7684\u6bd2\u5211\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u867d\u7136\u672a\u8bc6\u795e\u86c7\u638c\u7684\u90aa\u6bd2\uff0c\u4f46\u89c1\u6b27\u9633\u5a49\u8fd9\u6837\u6050\u60e7\uff0c\u5f53\u7136\u4e5f\u60f3\u5f97\u5230\u8fd9\u662f\u4e00\u79cd\u6781\u5389\u5bb3\u7684 \u6bd2\u5211\uff0c\u4ed6\u672c\u5c31\u84c4\u52bf\u5f85\u53d1\uff0c\u8fd9\u65f6\u4fbf\u5982\u6d2a\u6ce2\u6e83\u5824\uff0c\u500f\u7136\u51b2\u51fa\uff0c\u62d4\u5251\u3001\u98de\u8eab\u3001\u6325\u638c\u3001\u62a2\u4eba\uff0c\u51e0\u4e2a\u52a8 \u4f5c\uff0c\u95ea\u7535\u822c\u7684\u4e00\u6c14\u5475\u6210\uff01 \u3000\u3000\u4ed6\u4eba\u8fd8\u672a\u5230\uff0c\u638c\u529b\u5148\u81f3\uff0c\u8fd9\u4e00\u8bb0\u5288\u7a7a\u638c\u4ed6\u8fd0\u8db3\u4e86\u5341\u6210\u529f\u529b\uff0c\u9690\u9690\u5e26\u7740\u98ce\u96f7\u4e4b\u58f0\uff0c\u9976\u662f\u90a3 \u8001\u5a46\u5a46\u6b66\u529f\u5389\u5bb3\uff0c\u4e5f\u7981\u4e0d\u7740\u5fc3\u5934\u4e00\u51db\uff0c\u8d76\u5fd9\u5c06\u53cc\u638c\u5e76\u4f38\uff0c\u4e5f\u8fd8\u4e86\u4e00\u8bb0\u5288\u638c\u3002 \u3000\u3000\u4f46\u542c\u5f97\u201c\u767b\u767b\u201d\u58f0\u54cd\uff0c\u90a3\u8001\u5a46\u5a46\u4e0a\u8eab\u4e00\u6643\uff0c\u5f80\u540e\u9000\u4e86\u4e09\u6b65\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6c5f\u6d77\u5929\u5df2 \u4e00\u624b\u6267\u7740\u94c1\u94fe\uff0c\u53ea\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u90a3\u6761\u62c7\u6307\u822c\u7c97\u7ec6\u7684\u94c1\u94fe\u767b\u65f6\u65ad\u4e86\u3002\u6c5f\u6d77\u5927\u53eb\u9053\uff1a\u201c\u6b27\u9633\u59d1 \u5a18\uff0c\u4f60\u5feb\u8d70\u5427\uff01\u8fd9\u6837\u7684\u5e08\u7236\u3002\u8981\u4e0d\u8981\u4e5f\u7f62\u5566\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u56fa\u7136\u52a8\u4f5c\u5feb\u6781\uff0c\u90a3\u8001\u5a46\u5a46\u4e5f\u65d7\u9f13\u76f8\u5f53\uff0c\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u8001\u5a46\u5a46 \u5df2\u8eab\u5f62\u6b65\u6362\uff0c\u500f\u5730\u4e00\u4e2a\u201c\u6e38\u7a7a\u63a2\u722a\u201d\uff0c\u5341\u6307\u957f\u7532\uff0c\u5411\u6c5f\u6d77\u592d\u6293\u5230\uff01 \u3000\u3000\u5979\u7684\u6307\u7532\u957f\u5f97\u6015\u4eba\uff0c\u8fde\u6700\u77ed\u7684\u5c0f\u6307\u6307\u7532\u4e5f\u6709\u4e94\u5bf8\u6765\u957f\uff0c\u6700\u957f\u7684\u4e2d\u6307\u6307\u7532\u51e0\u4e4e\u957f\u8fbe\u4e00\u5c3a\uff0c \u4e0d\u7528\u4e4b\u65f6\uff0c\u5377\u4f5c\u4e00\u56e2\uff0c\u4e00\u7528\u65f6\u9661\u7136\u4f38\u5f00\uff0c\u94ee\u94ee\u4f5c\u58f0\uff0c\u7adf\u4f3c\u5341\u652f\u5315\u9996\uff01\u539f\u6765\u5979\u7684\u201c\u6307\u7532\u201d\u4e43\u662f \u4e00\u79cd\u7279\u522b\u7684\u5408\u91d1\u505a\u7684\uff0c\u8fd9\u79cd\u5047\u6307\u7532\u5957\u5728\u624b\u6307\u4e0a\u9762\uff0c\u4e60\u60ef\u4e4b\u540e\uff0c\u53ef\u4ee5\u8fd0\u7528\u81ea\u5982\uff0c\u5f53\u4f5c\u5947\u95e8\u5175\u5668\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5251\u8bc0\u4e00\u9886\uff0c\u4e00\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u659c\u523a\u51fa\u53bb\uff0c\u4e24\u4eba\u52a8\u4f5c\u90fd\u662f\u5feb\u5982\u95ea\u7535\uff0c\u773c\u770b\u5c31\u8981\u78b0 \u4e0a\uff0c\u6c5f\u6d77\u5929\u5ffd\u5fc3\u5ff5\u4e0a\u52a8\uff1a\u201c\u5979\u867d\u7136\u53ef\u6076\uff0c\u6211\u8fd8\u662f\u4e0d\u8be5\u5c06\u5979\u523a\u6b7b\u5c38\u5f53\u5373\u5251\u968f\u5fc3\u8f6c\uff0c\u672c\u6765\u8fd9\u4e00\u5251 \u662f\u523a\u5411\u5bf9\u65b9\u80f8\u53e3\u7684\u201c\u7487\u7391\u7a74\u201d\u7684\uff0c\u73b0\u5728\u5374\u6539\u6362\u4f5c\u6a2a\u523a\u5979\u7684\u624b\u8155\u3002 \u3000\u3000\u9ad8\u624b\u6bd4\u62fc\uff0c\u54ea\u5bb9\u5f97\u7a0d\u6709\u8fdf\u7591\uff0c\u4ee5\u90a3\u8001\u5a46\u5a46\u7684\u6b66\u529f\uff0c\u6c5f\u6d77\u5929\u5373\u7b97\u65bd\u5c55\u51fa\u6700\u5389\u5bb3\u7684\u5251\u62db\uff0c\u4e5f \u672a\u5fc5\u4fbf\u80fd\u5728\u4e00\u62db\u4e4b\u5185\u3002\u5236\u5979\u6b7b\u547d\uff0c\u73b0\u5728\u7a0d\u4e00\u8fdf\u7591\uff0c\u53c8\u4e2d\u9014\u6362\u62db\uff0c\u8fd9\u4fbf\u7ed9\u4e86\u654c\u4eba\u4ee5\u53ef\u4e58\u4e4b\u673a\uff0c \u4f46\u542c\u5f97\u201c\u6323\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u8001\u5a46\u5a46\u4e2d\u6307\u4e00\u5f39\uff0c\u6307\u7532\u5df2\u5148\u6233\u4e2d\u4ed6\u7684\u864e\u53e3\uff0c\u767b\u65f6\u628a\u4ed6\u7684\u5b9d\u5251\u5f39\u8131\u4e86 \u624b\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u53cc\u638c\u659c\u5206\uff0c\u5de6\u6293\u6c5f\u6d77\u5929\uff1a\u53f3\u6293\u6b27\u9633\u5a49\u3002\u6c5f\u6d77\u5929\u5728\u5371\u96be\u4e4b\u4e2d\uff0c\u4ecd\u5fd8\u4e0d\u4e86\u7ed3\u6b27\u9633\u5a49 \u9632\u62a4\uff0c\u4ed6\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u6a2a\u638c\u5982\u5200\uff0c\u524a\u90a3\u8001\u5a46\u5a46\u7684\u819d\u76d6\uff1b\u53e6\u4e00\u638c\u8f7b\u8f7b\u4e00\u63a8\uff0c\u4f7f\u4e86\u4e00\u4e2a\u5de7\u52b2\uff1b \u5c06\u6b27\u9633\u5a49\u63a8\u5230\u4e86\u5c4b\u89d2\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u90a3\u4e00\u62db\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u8fd9\u8001\u5a46\u5b50\u518d\u51f6\uff0c\u4e5f\u4e0d\u5f97\u4e0d\u95ea\u5f00\u4e00\u6b65\uff0c\u53ef\u662f\u6c5f\u6d77\u5929\u4e00\u5fc3\u4e8c \u7528\uff0c\u5728\u7528\u5de7\u52b2\u63a8\u5f00\u6b27\u9633\u5a49\u4e4b\u9645\uff0c\u811a\u6b65\u4e5f\u7a0d\u7a0d\u6709\u70b9\u8f7b\u6d6e\uff0c\u90a3\u8001\u5a46\u5b50\u773c\u5149\u4f55\u7b49\u5389\u5bb3\uff0c\u4e00\u77a7\u51fa\u7834 \u7efd\uff0c\u8d81\u52bf\u4e00\u4e2a\u6ed1\u6b65\uff0c\u624b\u638c\u5288\u6765\uff0c\u5df2\u628a\u6c5f\u6d77\u5929\u7684\u8eab\u5f62\u5168\u90e8\u7b3c\u7f69\u5728\u5979\u7684\u638c\u52bf\u4e4b\u4e0b\u3002\u6c5f\u6d77\u5929\u731b\u5730\u95fb \u5f97\u4e00\u80a1\u8165\u5473\uff0c\u90a3\u662f\u8001\u5a46\u5a46\u6bd2\u638c\u53d1\u51fa\u7684\u8165\u98ce\uff0c\u4e2d\u4eba\u6b32\u5455\uff01\u6c5f\u6d77\u5929\u5927\u60ca\uff0c\u4e0d\u6562\u6b63\u9762\u63a5\u638c\uff0c\u4ed7\u7740\u62a4 \u4f53\u795e\u529f\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u5f97\u4e00\u58f0\uff0c\u6c5f\u6d77\u5929\u7528\u80cc\u5fc3\u63a5\u4e86\u5979\u8fd9\u4e00\u638c\uff01 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u7ed9\u4ed6\u7684\u62a4\u4f53\u795e\u529f\u4e00\u9707\uff0c\u659c\u8d70\u4e09\u6b65\uff0c\u65b9\u7a33\u5f97\u4f4f\u8eab\u5f62\u3002\u4f46\u6c5f\u6d77\u5929\u63a5\u4e86\u5979\u8fd9\u4e00\u638c\u3002\u4e5f \u611f\u5230\u80cc\u5fc3\u6709\u4e00\u9635\u9ebb\u75d2\u75d2\u7684\u611f\u89c9\uff0c\u751a\u4e0d\u8212\u670d\u3002\u539f\u6765\u90a3\u8001\u5a46\u5a46\u7684\u5047\u6307\u7532\u4e5f\u662f\u6dec\u8fc7\u6bd2\u836f\u7684\uff0c\u6c5f\u6d77\u5929 \u7684\u76ae\u8089\u7ed9\u5979\u7684\u201c\u6307\u7532\u201d\u5212\u7834\u4e86\u5c11\u8bb8\uff0c\u6bd2\u5df2\u4fb5\u5165\u808c\u80a4\uff0c\u5e78\u800c\u5979\u201c\u6307\u7532\u201d\u4e0a\u7684\u6bd2\u4e0d\u5982\u6bd2\u9152\uff0c\u6c5f\u6d77 \u5929\u7acb\u5373\u5c01\u95ed\u4e86\u80cc\u5fc3\u7684\u201c\u5fd7\u5802\u7a74\u201d\u963b\u6b62\u4e86\u6bd2\u7684\u8513\u5ef6\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5c1a\u65e0\u5927\u788d\u3002 \u3000\u3000\u6c5f\u6d77\u5927\u4e00\u5ff5\u4ec1\u6148\uff0c\u5403\u4e86\u5927\u4e8f\uff0c\u4e0d\u7531\u5f97\u6012\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u6211\u4e0e\u4f60\u65e0\u51a4\u65e0\u4ec7\uff0c\u4f60\u5b9a\u8981\u5c06\u6211 \u7f6e\u4e8e\u6b7b\u5730\uff0c\u90a3\u6211\u4e5f\u5c31\u53ea\u6709\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u51b7\u7b11\u900d\uff1a\u201c\u5c0f\u4f19\u5b50\uff0c\u8c01\u8981\u4f60\u5ba2\u6c14\u5440\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5979\u4e00\u4e2a\u65cb\u8eab\uff0c\u53c8\u5df2\u53cd \u624b\u4e00\u638c\u62cd\u6765\uff0c\u8165\u98ce\u6251\u9f3b\u3002\u6bd4\u524d\u66f4\u751a\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5df2\u8bc6\u5f97\u5979\u7684\u795e\u86c7\u638c\u7684\u5389\u5bb3\uff0c\u4e0d\u6562\u518d\u8ba9\u5979\u51fb\u4e2d\u3002\u5f53\u4e0b\u5c55\u5f00\u5929\u7f57\u6b65\u6cd5\uff0c\u5148\u53d1\u5236\u4eba\uff0c\u4ee5 \u4e00\u6307\u7985\u529f\uff0c\u6233\u90a3\u8001\u5a46\u5a46\u7684\u8155\u8109\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u4e94\u6307\u75be\u5f39\uff0c\u500f\u7136\u95f4\u4f3c\u4f38\u51fa\u4e86\u4e94\u652f\u5315\u9996\uff0c\u5979\u7684\u5047\u6307\u7532\u957f\uff0c\u6c5f\u6d77\u5929\u7684\u624b\u6307\u77ed\uff0c\u4e00\u6307 \u7985\u529f\u867d\u7136\u5965\u5999\uff0c\u5374\u8fd1\u4e0d\u4e86\u5979\u7684\u8eab\uff0c\u6c5f\u6885\u5929\u53ea\u597d\u518d\u7528\u5929\u7f57\u6b65\u6cd5\u95ea\u5f00\uff0c\u7f29\u638c\u56de\u8eab\uff0c\u4e0e\u5979\u7ed5\u8eab\u6e38\u6597\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u82e5\u6709\u5b9d\u5251\u5728\u624b\uff0c\u7eb5\u4e0d\u80fd\u80dc\uff0c\u6700\u5c11\u4e5f\u53ef\u4ee5\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\uff0c\u73b0\u5728\u53cc\u65b9\u90fd\u4ee5\u8089\u638c\u76f8\u640f\uff0c \u90a3\u8001\u5a46\u5a46\u7ec3\u6709\u6b79\u6bd2\u7684\u795e\u86c7\u638c\uff0c\u53c8\u6709\u53ef\u4ee5\u5f53\u4f5c\u5175\u5668\u7528\u7684\u5047\u6307\u7532\uff0c\u6c5f\u6d77\u5929\u4e0d\u6562\u6b3a\u8eab\u8fdb\u640f\uff0c\u5c31\u96be\u514d \u5927\u5927\u5403\u4e8f\uff01 \u3000\u3000\u4e24\u4eba\u8d8a\u6597\u8d8a\u70c8\uff0c\u90a3\u8001\u5a46\u5a46\u5728\u77ac\u606f\u4e4b\u95f4\uff0c\u8fde\u653b\u4e03\u638c\uff0c\u6c5f\u6d77\u5929\u9669\u9669\u7ed9\u5979\u6253\u4e2d\u3002\u5ffd\u542c\u5f97\u4e00\u58f0\u60ca \u547c\uff0c\u6c5f\u6d77\u5929\u773c\u5149\u77a5\u5904\uff0c\u53ea\u89c1\u6b27\u9633\u5a49\u6447\u6447\u6b32\u5760\uff0c\u539f\u6765\u662f\u5979\u770b\u5230\u7d27\u5f20\u4e4b\u5904\uff0c\u4ee5\u4e3a\u6c5f\u6d77\u5929\u5df2\u906d\u4e86\u5979 \u5e08\u7236\u7684\u6bd2\u624b\uff0c\u56e0\u6b64\u4e0d\u81ea\u89c9\u5730\u53eb\u51fa\u58f0\u6765\u3002 \u3000\u3000\u5979\u7684\u5e08\u59d0\u6b63\u5728\u65c1\u8fb9\u76d1\u89c6\u7740\u5979\uff0c\u89c1\u5979\u6447\u6447\u6b32\u5760\uff0c\u975e\u4f46\u4e0d\u6276\u52a9\u5979\uff0c\u53cd\u800c\u556a\u7684\u4e00\u5df4\u638c\u5c31\u63b4\u8fc7 \u53bb\uff0c\u9a82\u9053\uff1a\u201c\u4e0d\u8981\u8138\u7684\u8d31\u5a62\uff0c\u5c31\u53ea\u77e5\u9053\u5173\u5fc3\u5916\u4eba\u5417\uff1f\u201d\u5979\u7684\u5e08\u59d0\u4e00\u5411\u5992\u5fcc\u5979\u5f97\u5230\u5e08\u7236\u5ba0\u7231\uff0c \u8fd9\u65f6\u4e58\u673a\u6cc4\u6124\uff0c\u63b4\u4e86\u5979\u4e24\u5df4\u638c\uff0c\u7136\u540e\u53c8\u6362\u8fc7\u4e00\u526f\u624b\u9563\uff0c\u5c06\u5979\u9501\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7981\u4e0d\u4f4f\u5fc3\u795e\u7a0d\u4e71\uff0c\u5ffd\u89c9\u773c\u775b\u53d1\u9ed1\uff0c\u5934\u6655\u76ee\u7729\uff0c\u539f\u6765\u4ed6\u867d\u6709\u62a4\u4f53\u795e\u529f\uff0c\u4f46\u56e0\u4e3a\u8981\u4ee5 \u516b\u6210\u4ee5\u4e0a\u7684\u529f\u529b\u5bf9\u4ed8\u90a3\u4e2a\u8001\u5a46\u5a46\uff0c\u9632\u62a4\u672c\u8eab\u7684\u529b\u91cf\u81ea\u7136\u56e0\u4e4b\u51cf\u5f31\uff0c\u7a74\u9053\u5c01\u95ed\u4e0d\u4e25\uff0c\u6240\u4e2d\u7684\u6bd2 \u53c8\u6e10\u6e10\u8513\u5ef6\u5f00\u53bb\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6697\u6697\u53eb\u58f0\u201c\u4e0d\u5999\u201d\uff0c\u90a3\u8001\u5a46\u5a46\u4f55\u7b49\u5389\u5bb3\uff0c\u4e00\u77a7\u51fa\u7834\u7efd\uff0c\u7acb\u5373\u5de6\u638c\u4e00\u7275\uff0c\u5c06\u6c5f\u6d77\u5929 \u653b\u6765\u7684\u638c\u529b\u5378\u53bb\uff0c\u6c5f\u6d77\u5929\u88ab\u5979\u7684\u5185\u529b\u7275\u5f15\uff0c\u8eab\u5411\u524d\u503e\u3002\u90a3\u8001\u5a46\u5a46\u5927\u559d\u4e00\u58f0\uff1a\u201c\u7740\uff01\u201d\u53f3\u638c\u500f \u7136\u95f4\u4fbf\u6309\u5230\u4e86\u6c5f\u6d77\u5929\u7684\u80f8\u53e3\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u4f60\u5982\u6b64\u72e0\u6bd2\uff0c\u6211\u4e5f\u987e\u4e0d\u5f97\u4f60\u7684\u6b7b\u6d3b\uff0c\u53ea\u597d\u4e0e\u4f60\u62fc\u4e86\uff01\u201d\u8fd0\u8db3\u4e86\u5341\u6210\u529f \u529b\uff0c\u4e00\u638c\u62cd\u51fa\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u7684\u5185\u529b\u672c\u6765\u5df2\u7ec3\u5230\u53ef\u4ee5\u6536\u53d1\u81ea\u5982\u7684\u5883\u754c\uff0c\u54ea\u77e5\u8fd9\u4e00\u6b21\u7adf\u7136\u529b\u4e0d\u4ece\u5fc3\u3002\u4e00\u638c\u62cd\u51fa\uff0c \u5185\u529b\u521a\u5410\uff0c\u5374\u7a81\u7136\u95f4\u5728\u81c2\u5f2f\u7684\u4e09\u661f\u7a74\u65b9\u4f4d\uff0c\u611f\u5230\u4f3c\u6709\u4e00\u6839\u5229\u9488\u523a\u5165\uff0c\u767b\u8d22\u534a\u6761\u81c2\u818a\u9178\u9ebb\uff0c\u5173 \u8282\u4e5f\u7a81\u7136\u50f5\u786c\uff0c\u53d1\u51fa\u7684\u529b\u906d\u7adf\u7136\u53cd\u9707\u56de\u6765\uff0c\u5979\u7684\u62a4\u4f53\u795e\u529f\u81ea\u7136\u751f\u51fa\u53cd\u5e94\uff0c\u4e0e\u8fd9\u80a1\u53f5\u9707\u56de\u6765\u7684 \u672c\u8eab\u529b\u9053\u76f8\u62b5\u76f8\u6d88\uff0c\u767b\u65f6\u6c14\u529b\u5168\u6d88\uff0c\u52a8\u5f39\u4e0d\u5f97\uff0c\u53d1\u51fa\u7684\u638c\u4e5f\u6536\u4e0d\u56de\u6765\u3002\u4f46\u89c1\u4ed6\u6a2a\u7709\u6012\u76ee\uff0c\u62ac \u638c\u8e22\u817f\uff0c\u5374\u50f5\u7acb\u5728\u539f\u5730\u4e0a\u3002\u6709\u5982\u4e00\u5c0a\u5851\u50cf\u3002 \u3000\u3000\u539f\u6765\u4ed6\u662f\u88ab\u90a3\u8001\u5a46\u5a46\u5236\u4e86\u673a\u5148\uff0c\u7529\u91cd\u624b\u6cd5\u5148\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\u3002\u8fd9\u8001\u5a46\u5a46\u7684\u70b9\u7a74\u53e6\u6709\u4e00\u529f\uff0c \u5979\u662f\u4ee5\u957f\u201c\u6307\u7532\u201d\u6390\u7834\u5bf9\u65b9\u7684\u76ae\u80a4\uff0c\u5185\u52b2\u6df1\u5165\uff0c\u523a\u8fdb\u5bf9\u65b9\u7684\u7a74\u9053\u7684\u3002\u8fd9\u79cd\u201c\u6390\u7a74\u201d\u795e\u529f\uff0c\u6bd4 \u91cd\u624b\u6cd5\u70b9\u7a74\u66f4\u4e3a\u5389\u5bb3\u3002 \u3000\u3000\u4ed6\u4eec\u4e8c\u4eba\u5404\u4ee5\u672c\u8eab\u7edd\u5b66\u76f8\u640f\uff0c\u65f6\u95f4\u5148\u540e\uff0c\u76f8\u5dee\u4e0d\u8fc7\u6beb\u5398\uff0c\u90a3\u8001\u5a46\u5a46\u867d\u7136\u5148\u544a\u5f97\u624b\uff0c\u4f46\u6c5f \u6d77\u5929\u7684\u5185\u529b\u4e5f\u5410\u4e86\u4e00\u534a\uff0c\u90a3\u8001\u5a46\u5a46\u88ab\u4ed6\u8fd9\u80a1\u529b\u9053\u4e00\u9707\uff0c\u67af\u7626\u7684\u8eab\u8eaf\u4e5f\u98de\u4e86\u8d77\u6765\uff0c\u5728\u534a\u7a7a\u4e2d\u63a5 \u8fde\u7ffb\u4e86\u4e09\u4e2a\u7b4b\u6597\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u811a\u8e0f\u5b9e\u5730\uff0c\u7a33\u4f4f\u4e86\u8eab\u5f62\u4e4b\u540e\uff0c\u4e00\u77a7\u6c5f\u6d77\u5929\u5df2\u4f3c\u6ce5\u5851\u6728\u96d5\u822c\u7684\u4e0d\u80fd\u52a8\u5f39\uff0c\u4fbf\u54c8\u54c8 \u5927\u7b11\u9053\uff0c\u201c\u9976\u4f60\u518d\u51f6\uff0c\u4e5f\u7ec8\u4e8e\u9003\u4e0d\u8131\u6211\u7684\u638c\u5fc3\uff01\u201d\u6c5f\u6d77\u5929\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u6697\u53f9\u4e0d\u503c\u3002\u82e5\u8bba\u771f \u5b9e\u7684\u672c\u9886\uff0c\u4ed6\u672c\u6765\u4e0d\u81f3\u4e8e\u8f93\u8005\u8fd9\u8001\u5a46\u5a46\u7684\uff0c\u4f46\u73b0\u5728\u6bd5\u7adf\u662f\u8f93\u4e86\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u773c\u5149\u4e00\u8f6c\uff0c\u53c8\u8f6c\u5230\u4e86\u6b27\u9633\u5a49\u8fd9\u8fb9\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u5fc3\u5411\u5916\u4eba\uff0c\u6211\u672c\u6765\u5bb9\u4e0d\u5f97\u3002 \u4f46\u770b\u5728\u4f60\u6bcd\u4eb2\u7684\u4efd\u4e0a\uff0c\u518d\u7ed9\u4f60\u4e00\u6761\u751f\u8def\u3002\u201d\u5979\u987f\u4e86\u4e00\u987f\uff0c\u84e6\u5730\u4f3c\u7b11\u975e\u7b11\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u613f\u610f\u5ac1 \u7ed9\u8fd9\u5c0f\u5b50\u4e48\uff1f\u201d\u6b27\u9633\u5a49\u53c8\u7f9e\u53c8\u6025\uff0c\u6da8\u7ea2\u4e86\u8138\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u9053\uff1a\u201c\u4f60\u5ac1\u4e86\u4ed6\uff0c\u6211\u5c06\u4ed6\u4e5f\u6536\u4e3a\u5f1f\u5b50\u3002\u8fd9\u4e0d\u5f88\u597d\u4e48\uff1f\u4f46\u4f60\u662f\u77e5\u9053\u672c\u95e8\u89c4\u77e9\u7684\uff0c \u6211\u6536\u522b\u4eba\u7684\u5f92\u5f1f\uff0c\u53ef\u5148\u5f97\u8981\u4ed6\u541e\u4e0b\u8fd9\u4e24\u9897\u4e38\u836f\uff0c\u6211\u624d\u653e\u5fc3\u3002\u5a49\u513f\uff0c\u8fd9\u4e24\u9897\u4e5d\u836f\u73b0\u5728\u4ea4\u7ed9\u4f60 \u4e86\uff0c\u4ed6\u662f\u6b7b\u662f\u751f\uff0c\u662f\u7978\u662f\u798f\uff0c\u4e5f\u5168\u64cd\u5728\u4f60\u7684\u624b\u4e2d\u4e86\uff01\u201d \u3000\u3000\u6b27\u9633\u5a49\u6025\u5f97\u773c\u6cea\u76f4\u6d41\uff0c\u5c16\u58f0\u53eb\u9053\uff0c\u201c\u5e08\u7236\uff0c\u6211\u3001\u6211\u4e0d\u80fd\u5bb3\u4ed6\uff01\u201d\u5979\u4f3c\u662f\u5bb3\u6015\u86c7\u54ac\u4e00\u822c\uff0c \u672c\u80fd\u7684\u5c06\u624b\u4e00\u7f29\uff0c\u90a3\u8001\u5a46\u5a46\u4ea4\u7ed9\u5979\u7684\u90a3\u4e24\u9897\u4e38\u836f\uff0c\u4e5f\u5c31\u8dcc\u843d\u5730\u4e0a\u3002\u539f\u6765\u8fd9\u4e24\u9897\u4e38\u836f\u53ef\u4ee5\u4ee4\u4eba \u8ff7\u5931\u5fc3\u667a\uff0c\u773c\u836f\u4e4b\u540e\u4fbf\u6210\u75f4\u5446\uff0c\u53ea\u8bc6\u670d\u4ece\u4e3b\u4eba\u7684\u547d\u4ee4\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u9762\u8272\u516d\u6c89\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6307\u6761\u597d\u8def\u7ed9\u4f60\u4f60\u4e0d\u613f\u8d70\uff0c\u90a3\u6211\u5c31\u53ea\u597d\u6309\u7167\u5bb6\u6cd5\u5904\u7f6e \u4f60\u4e86\uff1a\u6211\u5148\u5e9f\u4e86\u8fd9\u5c0f\u5b50\u7684\u6b66\u529f\uff0c\u5bc4\u4e86\u4ed6\u7684\u7435\u7436\u9aa8\uff0c\u7136\u540e\u518d\u8ba9\u4f60\u53d7\u795e\u86c7\u638c\u7684\u6bd2\u5211\uff01\u201d\u5979\u4e00\u9762 \u8bf4\uff0c\u4e00\u9762\u7f13\u7f13\u7684\u5411\u6c5f\u6d77\u5929\u8d70\u53bb\uff01\u6b63\u662f\uff1a \u3000\u3000\u8eab\u9677\u7f51\u7f57\u906d\u6bd2\u624b\uff0c\u66f4\u4f24\u65e0\u8ba1\u6551\u4f73\u4eba\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u5341\u516d\u56de\u3000\u53e4\u5821\u52ab\u4eba\u6765\u5f02\u517d\u3000\u7a77\u9014\u5f15\u8def\u6709\u795e\u9e70 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u5341\u516d\u56de\u3000\u53e4\u5821\u52ab\u4eba\u6765\u5f02\u517d\u3000\u7a77\u9014\u5f15\u8def\u6709\u795e\u9e70 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u62a5\u9053\uff1a\u201c\u5929\u9b54\u6559\u5389\u526f\u6559\u4e3b\u6c42\u89c1\u3002\u201d\u90a3\u8001\u5a46\u5a46\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a \u201c\u6211\u4e0e\u4ed6\u4eec\u65e9\u5df2\u5206\u5bb6\uff0c\u8fd9\u5389\u590d\u751f\u8fd8\u5230\u8fd9\u91cc\u6765\u4f5c\u4ec0\u4e48\uff1f\u201d\u90a3\u8fdb\u6765\u7980\u62a5\u7684\u5973\u5f1f\u5b50\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u5e08 \u7236\u89c1\u4e0d\u89c1\u4ed6\uff1f\u201d\u90a3\u8001\u5a46\u5a46\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u82e5\u4e0d\u89c1\u4ed6\uff0c\u5acc\u9699\u66f4\u5927\uff0c\u8fd8\u662f\u8bf7\u4ed6\u8fdb\u6765\u5427\u3002\u201d \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u5f62\u8c8c\u53e4\u602a\u7684\u9ed1\u8863\u5c11\u5e74\u8d70\u4e86\u8fdb\u6765\uff0c\u8fd9\u5c11\u5e74\u957f\u53d1\u62ab\u80a9\uff0c\u8c8c\u5982\u5973\u5b50\uff0c\u80cc\u540e\u8ddf\u7740\u4e24\u53ea\u91d1 \u6bdb\u602a\u517d\uff0c\u6c5f\u6d77\u5929\u542c\u4ed6\u5e08\u7236\u8bf4\u8fc7\u8fd9\u9ed1\u8863\u5c11\u5e74\u7684\u4e8b\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u5c31\u662f\u90a3\u5e74\u4e0a\u9099\u5c71\u95f9\u8fc7\u4e00\u573a\u7684 \u5389\u590d\u751f\uff0c\u73b0\u5728\u5f53\u4e0a\u4e86\u5929\u9b54\u6559\u7684\u526f\u6559\u4e3b\u4e86\u3002\u201d \u3000\u3000\u7ed9\u4ed6\u5f00\u95e8\u7684\u90a3\u4e2a\u5973\u5f1f\u5b50\uff0c\u89c1\u4e3b\u6bdb\u9a8f\u795e\u6001\u72f0\u72de\uff0c\u9887\u6709\u602f\u610f\uff0c\u5389\u590d\u751f\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u5bb3\u6015\uff0c\u5b83 \u4eec\u6ca1\u6709\u6211\u7684\u547d\u4ee4\uff0c\u51b3\u4e0d\u4f1a\u80e1\u4e71\u4f24\u4eba\u7684\u3002\u201d\u4ed6\u8f7b\u8f7b\u4e00\u5578\uff0c\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u679c\u7136\u670d\u670d\u5e16\u5e16\u7684\u8e72\u5728\u95e8 \u8fb9\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u5389\u590d\u725b\u5bf9\u6c5f\u6d77\u5929\u671b\u4e86\u4e00\u773c\uff0c\u4fbf\u5411\u90a3\u8001\u5a46\u5a46\u65bd\u793c\u9053\uff1a\u201c\u9634\u59d1\u5a46\uff0c\u5c0f\u4f84\u4eca\u65e5\u7279\u6765\u5411\u4f60\u8d3a \u559c\u3002\u201d\u90a3\u8001\u5a46\u5a46\u6b20\u8eab\u9053\uff0c\u201c\u5389\u526f\u6559\u7acb\u4e0d\u5fc5\u591a\u793c\u3002\u8bf7\u95ee\u6211\u559c\u4ece\u4f55\u6765\uff1f\u201d \u3000\u3000\u5389\u590d\u751f\u5411\u6c5f\u6d77\u5929\u4e00\u6307\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u597d\u51e0\u5e2e\u4eba\u90fd\u5728\u6253\u8fd9\u5c0f\u5b50\u7684\u4e3b\u610f\uff0c\u73b0\u5728\u5374\u843d\u5728\u4f60\u8001\u4eba\u5bb6 \u624b\u4e2d\uff0c\u5c82\u975e\u4e00\u6869\u559c\u4e8b\u3002\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u7684\u6d88\u606f\u5012\u5f88\u7075\u901a\u554a\uff01\u201d \u3000\u3000\u5389\u590d\u751f\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u5c31\u662f\u5949\u4e86\u6559\u4e3b\u4e4b\u547d\uff0c\u4e00\u8def\u8ffd\u8e2a\u4ed6\u7684\u3002\u597d\u5728\u4ed6\u6ca1\u843d\u5728\u522b\u4eba\u624b \u91cc\uff0c\u800c\u662f\u843d\u5728\u59d1\u5a46\u624b\u4e2d\u3002\u8bf7\u4f60\u8001\u4eba\u5bb6\u7ed9\u6211\u4e00\u4e2a\u9762\u5b50\uff0c\u8ba9\u6211\u5c06\u4ed6\u5e26\u56de\u53bb\uff0c\u4e5f\u597d\u5411\u6559\u4e3b\u4ea4\u5dee\u3002\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u9053\uff1a\u201c\u4f60\u4ea4\u4e86\u5dee\uff0c\u6211\u5374\u62ff\u4ec0\u4e48\u5411\u91d1\u9e70\u5bab\u7684\u4e3b\u4eba\u4ea4\u5dee\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4e2a\u59d3\u9634\u7684\u8001\u5a46\u5a46\uff0c\u4e43\u662f\u4e0e\u91d1\u9e70\u5bab\u6709\u5173\u7684\u3002\u4f46\u5374\u4e3a\u4ec0\u4e48 \u6709\u51e0\u5e2e\u4eba\u7269\u90fd\u6b32\u5f97\u6211\u800c\u7518\u5fc3\u5462\uff1f\u201d \u3000\u3000\u5389\u590d\u751f\u9053\uff1a\u201c\u6559\u4e3b\u4e8b\u5148\u4ea6\u6709\u4ea4\u4ee3\uff0c\u5979\u4e5f\u77e5\u9053\u91d1\u9e70\u5b98\u7684\u4e3b\u4eba\u8981\u8fd9\u5c0f\u4e86\uff0c\u5979\u4f1a\u4eb2\u81ea\u5230\u91d1\u9e70\u5bab \u53bb\u89e3\u91ca\u7684\u3002\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u54b3\u4e86\u4e00\u58f0\uff0c\u5750\u56de\u539f\u4f4d\uff0c\u4e0d\u7f6e\u53ef\u5426\u3002\u5389\u590d\u751f\u53c8\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u8fd8\u6709\u4e24\u4ef6\u5b9d\u7269\uff0c\u4e43\u662f \u6211\u4eec\u4e54\u7956\u5e08\u7559\u4e0b\u6765\u7684\uff0c\u4e5f\u8bf7\u4f60\u8001\u4eba\u5bb6\u4e00\u5e76\u53d1\u8fd8\u3002\u201d\u4ed6\u8bf4\u7740\u6d3b\uff0c\u773c\u5149\u5374\u5411\u4f8d\u7acb\u5728\u90a3\u8001\u5a46\u5a46\u8eab\u540e \u7684\u90a3\u4e2a\u5c11\u5973\u5c04\u53bb\uff0c\u539f\u6765\u6c5f\u6d77\u5929\u90a3\u67c4\u88c1\u4e91\u5b9d\u5251\u65e9\u5df2\u88ab\u6b27\u9633\u5a49\u7684\u5e08\u59d0\u6361\u8d77\uff0c\u8fd9\u65f6\u5979\u6b63\u6367\u7740\u5b9d\u5251\uff0c \u6043\u7acb\u4e00\u65c1\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u76ae\u7b11\u8089\u4e0d\u7b11\u4ed6\u8bf4\u9053\uff0c\u201c\u53c8\u8981\u4eba\u53c8\u8981\u4e1c\u897f\uff0c\u4f60\u4eec\u5012\u60f3\u5f97\u5f88\u7f8e\u554a\uff01\u201d \u3000\u3000\u5389\u590d\u751f\u9053\uff0c\u201c\u82e5\u627f\u53d1\u8fd8\uff0c\u655d\u6559\u6559\u4e3b\u4e5f\u6709\u8584\u672d\u76f8\u8d60\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u53d6\u51fa\u4e00\u4e2a\u4e00\u5c3a\u89c1\u65b9\u7684\u7ea2\u7eeb\u5305\u88b1\uff0c\u5c06\u7ea2\u7eeb\u4e00\u5c42\u5c42\u89e3\u5f00\uff0c\u539f\u6765\u91cc\u9762\u5305\u7740\u7684\u4e43\u662f\u4e00\u672c \u4e66\uff0c\u5389\u590d\u751f\u9053\uff1a\u201c\u8fd9\u662f\u767e\u6bd2\u771f\u7ecf\u7684\u6284\u672c\uff0c\u4ee5\u7269\u6613\u7269\uff0c\u4f60\u8001\u4eba\u5bb6\u4e5f\u4e0d\u5403\u4e8f\u554a\uff01\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u53cc\u773c\u4e00\u5f20\uff0c\u53d1\u51fa\u78a7\u7eff\u7684\u5149\u8292\uff0c\u4e00\u624b\u5c31\u5c06\u90a3\u672c\u4e66\u6293\u4e86\u8fc7\u53bb\u3002\u5389\u590d\u751f\u559c\u9053\uff1a\u201c\u4f60\u8001 \u4eba\u5bb6\u5e94\u5141\u4e86\uff0c\u8bf7\u5c06\u8fd9\u67c4\u5b9d\u5251\u7ed9\u6211\uff0c\u8fd8\u6709\u90a3\u4ef6\u7389\u7532\uff0c\u4e5f\u8bf7\u4e00\u5e76\u53d1\u8fd8\uff0c\u65f6\u5019\u4e0d\u65e9\uff0c\u6211\u53ef\u8981\u628a\u4eba\u5e26 \u8d70\u4e86\u3002\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u5ffd\u5730\u6de1\u6de1\u8bf4\u9053\uff0c\u201c\u4f60\u56de\u53bb\u544a\u8bc9\u73e0\u739b\uff0c\u5c31\u8bf4\u8fd9\u672c\u767e\u6bd2\u771f\u7ecf\u6211\u7559\u4e0b\u4e86\u3002\u5979\u65e2\u7136\u53e6\u7acb \u95e8\u6237\uff0c\u4e03\u9634\u6559\u7684\u4f20\u5bb6\u5b9d\u5178\u4e5f\u7406\u5e94\u5f52\u8fd8\u7ed9\u6211\u4e86\u3002\u8981\u662f\u5979\u8fd8\u6709\u8bf4\u8bdd\uff0c\u8bf7\u5979\u5728\u4eca\u5e74\u7684\u4e2d\u79cb\uff0c\u5230\u91d1\u9e70 \u5bab\u6765\uff0c\u6211\u518d\u4e0e\u5979\u9762\u8c08\u3002\u201d\u8ddf\u7740\u5411\u4f8d\u7acb\u8eab\u65c1\u7684\u4f8d\u5973\u4e00\u6325\u624b\u9053\uff1a\u201c\u4f60\u628a\u8fd9\u5c0f\u5b50\u5e26\u4e0b\u53bb\uff0c\u641c\u4e00\u641c\u4ed6 \u8eab\u4e0a\u6709\u6ca1\u6709\u5b9d\u7532\uff1f\u201d \u3000\u3000\u5389\u590d\u751f\u6012\u9053\uff1a\u201c\u4f60\u8981\u4e86\u6211\u4eec\u7684\u4e1c\u897f\uff0c\u5374\u4e0d\u80af\u4ea4\u4eba\u8fd8\u5b9d\u4e48\uff1f\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u5916\u4eba\uff0c\u4f60\u4e0d\u77e5\u9053\u6211\u4e0e\u73e0\u739b\u4e4b\u95f4\u7684\u6e0a\u6e90\uff0c\u8fd9\u662f\u4e09\u767e\u591a\u5e74\u524d\u7684\u65e7 \u503a\uff0c\u8bf4\u7ed9\u4f60\u542c\u4f60\u4e5f\u4e0d\u6e05\u695a\u7684\u3002\u4f60\u8fd8\u662f\u56de\u53bb\u95ee\u4f60\u7684\u6559\u4e3b\u5427\u3002\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u53e3\u4e2d\u7684\u201c\u73e0\u739b\u201d\uff0c\u5373\u662f\u5929\u9b54\u6559\u4e3b\u7684\u5c0f\u540d\u3002\u5389\u590d\u751f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u6559\u4e3b\u79f0\u4ed6 \u4f5c\u59d1\u5a46\uff0c\u6211\u4e5f\u4e00\u76f4\u8ddf\u7740\u8fd9\u6837\u79f0\u547c\u5979\uff0c\u4f46\u5979\u4eec\u4e8c\u4eba\u53c8\u5e76\u4e0d\u540c\u59d3\uff0c\u8fd9\u662f\u4ec0\u4e48\u5173\u7cfb\uff0c\u6211\u5374\u83ab\u540d\u5176 \u5999\u3002\u83ab\u975e\u5979\u4eec\u4e8c\u5bb6\u5f53\u771f\u662f\u6709\u751a\u53e4\u602a\u7684\u6e0a\u6e90\uff1f\u201d \u3000\u3000\u53ef\u662f\u5389\u590d\u751f\u5bf9\u5929\u9b54\u6559\u4e3b\u6700\u662f\u5fe0\u5fc3\uff0c\u5929\u9b54\u6559\u4e3b\u4ea4\u7ed9\u4ed6\u529e\u7684\u4e8b\u60c5\uff0c\u4ed6\u662f\u975e\u505a\u5230\u4e0d\u53ef\u3002\u5f53\u4e0b\u628a \u5fc3\u4e00\u6a2a\uff0c\u4fbf\u4e5f\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u542c\u672c\u6559\u6559\u4e3b\u7684\u547d\u4ee4\uff0c\u8fd8\u671b\u4f60\u8001\u4eba\u5bb6\u89c1\u8c05\u3002\u6211\u518d\u8bf7\u95ee\u4f5c\u8005 \u4eba\u5bb6\u4e00\u904d\uff1a\u4f60\u5230\u5e95\u4ea4\u4e0d\u4ea4\u4eba\uff0c\u8fd8\u4e0d\u8fd8\u5b9d\uff1f\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u4ea4\u4eba\uff0c\u4e0d\u8fd8\u5b9d\u4f60\u53c8\u600e\u4e48\u6837\uff0c\u83ab\u975e\u4f60\u8981\u4e0e\u6211\u52a8\u6b66\u4e48\uff1f\u201d \u3000\u3000\u5389\u590d\u751f\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4f46\u4f60\u8001\u4eba\u5bb6\u4e0d\u7ed9\uff0c\u6211\u53ea\u597d\u81ea\u53d6\u4e86\u3002\u201d\u8bf4\u7f62\uff0c\u5ffd\u5730\u4e00\u58f0\u957f\u5578\u3002\u5578\u58f0\u4e00 \u4f5c\uff0c\u53ea\u89c1\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u7acb\u5373\u4fbf\u8df3\u8d77\u6765\uff0c\u4e00\u53ea\u6251\u5411\u6b27\u9633\u5a49\u7684\u5e08\u59d0\uff0c\u53e6\u4e00\u53ea\u5219\u6251\u5411\u6c5f\u6d77\u5929\u3002 \u3000\u3000\u6b27\u9633\u5a49\u7684\u5e08\u59d0\u4e5f\u5c31\u662f\u6367\u7740\u5b9d\u5251\u4f8d\u7acb\u5728\u65c1\u7684\u90a3\u4e2a\u5c11\u5973\uff0c\u9aa4\u7136\u89c1\u91d1\u6bdb\u72e1\u5411\u5979\u6251\u6765\uff0c\u5413\u5f97\u9b42\u98de \u9b44\u6563\uff0c\u6b63\u5f85\u62d4\u51fa\u5b9d\u5251\u8fce\u654c\uff0c\u5df2\u7ed9\u4f59\u6bdb\u72e1\u4e00\u722a\u6293\u4e0b\uff0c\u4fbf\u62a2\u53bb\u4e86\u5979\u624b\u4e2d\u7684\u5b9d\u5251\u3002\u5e78\u800c\u90a3\u53ea\u91d1\u6bdb\u72e1 \u53ea\u662f\u62a2\u5251\uff0c\u5e76\u672a\u4f24\u4eba\u3002 \u3000\u3000\u53e6\u4e00\u53ea\u6251\u5411\u6c5f\u6d77\u5929\u7684\u91d1\u6bdb\u72e1\u5374\u51e0\u4e4e\u906d\u4e86\u90a3\u8001\u5a46\u5a46\u7684\u6bd2\u624b\uff1b\u90a3\u8001\u5a46\u5a46\u542c\u5f97\u5389\u590d\u751f\u53d1\u5578\uff0c\u5df1 \u77e5\u4ed6\u662f\u8981\u6307\u6325\u91d1\u6bdb\u72e1\u62a2\u4eba\u593a\u7269\uff0c\u5979\u8eab\u5f62\u4e00\u6643\u3002\u5148\u5230\u4e86\u6c5f\u6d77\u5929\u7684\u8eab\u8fb9\uff0c\u91d1\u6bdb\u72e1\u4e00\u5954\u4e0a\u6765\uff0c\u4fbf\u5403 \u5979\u8fce\u5934\u4e00\u638c\u3002 \u3000\u3000\u90a3\u91d1\u6bdb\u72e1\u4e00\u58f0\u6012\u543c\uff0c\u540e\u817f\u4eba\u7acb\uff0c\u4f38\u51fa\u524d\u81c2\uff0c\u4e5f\u662f\u5411\u5979\u8fce\u9762\u4e00\u6293\u3002\u91d1\u6bdb\u72e1\u529b\u5927\u65e0\u7a77\uff0c\u52a8\u4f5c \u5982\u98ce\uff0c\u4f46\u5230\u5e95\u4e0d\u5982\u7ec3\u8fc7\u4e0a\u4e58\u6b66\u529f\u7684\u4eba\uff0c\u61c2\u5f97\u95ea\u3001\u51fb\u4e4b\u9053\uff1b\u90a3\u8001\u5a46\u5a46\u970d\u7684\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u90a3 \u91d1\u6bdb\u72e1\u52a8\u4f5c\u592a\u5feb\uff0c\u6536\u52bf\u4e0d\u4f4f\uff0c\u5df1\u4ece\u5979\u7684\u5934\u9876\u4e0a\u65b9\u6251\u4e86\u8fc7\u53bb\u3002\u90a3\u8001\u5a46\u5a46\u9a82\u9053\uff1a\u201c\u5b7d\u755c\u65e0\u793c\uff01\u201d \u5c0f\u81c2\u4e00\u5f2f\uff0c\u6325\u638c\u4e00\u62cd\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u6b63\u51fb\u4e2d\u5b83\u7684\u81c2\u90e8\uff0c\u9976\u662f\u5b83\u76ae\u575a\u8089\u539a\uff0c\u5403\u4e86\u8fd9\u638c\uff0c\u4e5f\u81ea \u96be\u5f53\uff0c\u767b\u65f6\u88ab\u6253\u5f97\u5728\u5730\u4e0a\u6253\u6eda\uff0c\u53d1\u51fa\u88c2\u4eba\u5fc3\u9b44\u7684\u72c2\u66dd\u3002\u5e78\u800c\u90a3\u8001\u5a46\u5a46\u7684\u91d1\u5c5e\u201c\u6307\u7532\u201d\u63d2\u4e0d\u8fdb \u5b83\u7684\u76ae\u8089\uff0c\u800c\u4e14\u56e0\u4e3a\u7528\u529b\u592a\u731b\uff0c\u6307\u7532\u6839\u4e5f\u6c81\u51fa\u8840\u6765\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u6b63\u8981\u4e0a\u53bb\u6253\u53e6\u4e00\u53ea\u91d1\u6bdb\u72e1\uff0c\u5ffd\u89c1\u773c\u524d\u5bd2\u5149\u4e00\u95ea\uff0c\u5389\u590d\u751f\u5df2\u53d6\u51fa\u7389\u5c3a\uff0c\u6012\u58f0\u8bf4\uff1a \u201c\u6253\u72d7\u4e5f\u5f97\u770b\u4e3b\u4eba\u9762\uff1b\u4f60\u6253\u4e86\u6211\u7684\u91d1\u72e1\uff0c\u6055\u6211\u4e5f\u8981\u65e0\u793c\u4e86\u3002\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u77e5\u9053\u4ed6\u662f\u5389\u5bb6\u7684\u540e\u4ee3\uff0c\u6b66\u529f\u5b9a\u7136\u4e0d\u5f31\uff0c\u4f46\u770b\u4ed6\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5374\u5e76\u4e0d\u600e\u6837\u653e\u5728\u5fc3 \u4e0a\uff0c\u5f53\u4e0b\u4e00\u58f0\u51b7\u7b11\u9053\uff1a\u201c\u6253\u4e86\u4f60\u7684\u91d1\u6bdb\u72e1\u4f60\u53c8\u5f85\u5982\u4f55\uff1f\u7ed9\u6211\u6eda\u51fa\u53bb\uff01\u201d\u53cc\u81c2\u4e00\u4f38\uff0c\u5341\u652f\u957f\u6307 \u7532\u90fd\u4f38\u4e86\u51fa\u6765\uff0c\u4fbf\u8981\u5c06\u4ed6\u6293\u4f4f\uff0c\u6454\u51fa\u95e8\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u5389\u590d\u751f\u5e74\u7eaa\u867d\u8f7b\uff0c\u6b66\u529f\u4e0a\u7684\u9020\u8be3\u5374\u5927\u662f\u4e0d\u5f31\uff1b\u5f53\u5e74\u4ed6\u5728\u9099\u5c71\u4e0a\u548c\u91d1\u4e16\u9057\u4ea4\u624b\uff0c\u867d\u8bf4 \u91d1\u4e16\u9057\u624b\u4e0b\u7559\u60c5\uff0c\u672a\u7528\u5168\u529b\u3002\u4f46\u4ed6\u4e5f\u80fd\u62b5\u6321\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u8db3\u89c1\u4ed6\u7684\u529f\u529b\u3002\u8fd9\u65f6\uff0c\u4ed6\u88ab\u8fd9\u8001\u5a46 \u5a46\u6fc0\u6012\uff0c\u767b\u65f6\u4e5f\u56de\u9a82\u8fc7\u53bb\u9053\uff0c\u201c\u597d\u5440\uff0c\u4f60\u501a\u8001\u5356\u8001\uff1b\u6211\u504f\u504f\u4e0d\u8d70\uff0c\u5012\u8981\u770b\u4f60\u5982\u4f55\u5c06\u6211\u6eda\u51fa \u53bb\uff01\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u8001\u5a46\u5a46\u7684\u5341\u6307\u957f\u7532\u5df2\u582a\u582a\u6293\u5230\uff0c\u5389\u590d\u751f\u8eab\u5f62\u4e00\u98d8\u4e00\u95ea\uff0c \u8fd0\u7528\u4e86\u5965\u5999\u7684\u201c\u5929\u7f57\u6b65\u6cd5\u201d\uff0c\u7adf\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u4e00\u95ea\u95ea\u5f00\uff0c\u53cd\u624b\u62a1\u5c3a\uff0c\u4fbf\u5411\u90a3\u8001\u5a46\u5a46\u7684\u864e \u53e3\u6572\u4e0b\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u559d\u58f0\uff1a\u201c\u64a4\u624b\uff01\u201d\u4fee\u5730\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u4e94\u6307\u75be\u5f39\uff0c\u8fd9\u4e00\u56de\u5979\u62ff \u634f\u65f6\u5019\uff0c\u51c6\u5907\u5f97\u4e0d\u7981\u6beb\u9ecd\uff0c\u6b63\u597d\u8fce\u4e0a\u90a3\u6839\u7389\u5c3a\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u81ea\u6043\u529f\u529b\u6df1\u539a\uff0c\u6ee1\u4ee5\u4e3a\u8fd9\u4e00\u5f39\u81f3\u4e0d\u6d4e\u4e5f\u53ef\u4ee5\u628a\u4ed6\u7684\u7389\u5c3a\u5f39\u51fa\u624b\u53bb\uff0c\u54ea\u77e5\u5389\u590d\u751f \u7684\u529f\u529b\u672c\u6765\u5c31\u548c\u5979\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u800c\u4e14\u4ed6\u90a3\u6839\u7389\u5c3a\uff0c\u4e43\u662f\u6d77\u5e95\u5bd2\u7389\u6253\u6210\u7684\uff0c\u957f\u53ea\u5c3a\u8bb8\uff0c\u4efd\u91cf\u5374 \u6c89\u91cd\u975e\u5e38\uff0c\u8001\u5a46\u5a46\u7684\u91d1\u5c5e\u6307\u7532\u548c\u5b83\u786c\u78b0\uff0c\u975e\u4f46\u5f39\u5b83\u4e0d\u5f00\uff0c\u800c\u4e14\u7ed9\u5b83\u7838\u5f97\u53cd\u5377\u8d77\u6765\u3002\u4fd7\u8bed\u8bf4\u5341 \u6307\u8fde\u5fc3\uff0c\u867d\u8bf4\u662f\u5047\u6307\u7532\uff0c\u4f46\u5957\u5728\u6307\u5934\u4e0a\u9762\uff0c\u53d7\u4e86\u5267\u9707\uff0c\u4e5f\u75db\u5f97\u90a3\u8001\u5a46\u5a46\u53eb\u51fa\u58f0\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u88ab\u8001\u5a46\u5a46\u51fb\u5012\u7684\u90a3\u53ea\u91d1\u6bdb\u72e1\u4ea6\u5df1\u722c\u4e86\u8d77\u6765\uff0c\u5411\u90a3\u8001\u5a46\u5a46\u6012\u76ee\u800c\u89c6\uff0c\u770b\u5b83\u90a3\u72f0\u72de\u7684\u795e \u6001\uff0c\u4fbf\u4f3c\u7acb\u523b\u8981\u6251\u8fc7\u6765\uff0c\u5c06\u90a3\u8001\u5a46\u5a46\u6495\u4e3a\u4e24\u7247\u3002 \u3000\u3000\u5389\u590d\u751f\u5374\u628a\u624b\u4e00\u6325\uff0c\u559d\u9053\uff0c\u201c\u72e1\u513f\u542c\u8bdd\uff0c\u6211\u4e0d\u8981\u4f60\u5e2e\uff0c\u4f60\u5feb\u5feb\u5c06\u4eba\u9001\u56de\u53bb\uff01\u201d \u3000\u3000\u90a3\u53ea\u91d1\u6bdb\u72e1\u88ab\u4e3b\u4eba\u4e00\u559d\uff0c\u65e0\u53ef\u5948\u67ef\u7684\u56de\u8f6c\u8eab\u5b50\uff0c\u5c06\u6c5f\u6d77\u5929\u62b1\u4e86\u8d77\u6765\uff0c\u631f\u5728\u80c1\u4e0b\uff0c\u6c5f\u6d77\u5929 \u7a74\u9053\u672a\u89e3\uff0c\u4e0d\u80fd\u52a8\u5f39\uff0c\u53ea\u6709\u4efb\u4ece\u5b83\u7684\u6446\u5e03\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u4e00\u626c\u624b\u53d1\u51fa\u4e00\u84ec\u6bd2\u9488\uff0c\u60f3\u628a\u91d1\u6bdb\u72e1\u7684\u773c\u775b\u5c04\u778e\u3002\u5389\u590d\u751f\u4e00\u8bb0\u5288\u7a7a\u638c\u6253\u53bb\uff0c\u5c06\u90a3 \u4e00\u84ec\u6bd2\u9488\uff0c\u5168\u90fd\u626b\u5f00\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u60f3\u7559\u4e0b\u8fd9\u4e24\u53ea\u91d1\u6bdb\u72e1\u5417\uff1f\u8fd9\u5bf9\u4f60\u6709\u4ec0\u4e48\u597d\u5904\uff1f\u54fc\uff0c \u54fc\uff0c\u90a3\u4f60\u53ef\u66f4\u8981\u5403\u4e8f\u4e86\uff01\u6211\u4e0d\u60f3\u4ee5\u4e8c\u654c\u4e00\uff0c\u6765\uff0c\u6765\uff0c\u6765\uff1a\u8fd8\u662f\u8ba9\u6211\u518d\u5355\u72ec\u9886\u6559\u4f60\u7684\u6bd2\u722a\u5427\uff01\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u6012\u6781\u6c14\u6781\uff0c\u4f46\u4e00\u60f3\u5389\u590d\u751f\u7684\u8bdd\u4e5f\u6709\u9053\u7406\uff0c\u6709\u5389\u590d\u751f\u5728\u65c1\uff0c\u81ea\u5df1\u51b3\u8ba1\u4f24\u4e0d\u4e86\u90a3\u4e24 \u53ea\u91d1\u6bdb\u72e1\uff0c\u82e5\u518d\u62db\u60f9\u5b83\u4eec\uff0c\u65e0\u5f02\u6dfb\u4e86\u4e24\u4e2a\u5f3a\u654c\uff0c\u56e0\u6b64\u867d\u7136\u4e07\u5206\u4e0d\u613f\uff0c\u4e5f\u53ea\u597d\u8ba9\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1 \u5c06\u5b9d\u5251\u548c\u4eba\u62a2\u8d70\u3002\u5f53\u4e0b\u4e24\u4eba\u518d\u5ea6\u4ea4\u624b\uff0c\u90a3\u8001\u5a46\u5a46\u6000\u4e86\u6212\u5fc3\uff0c\u4e0d\u6562\u518d\u786c\u78b0\u4ed6\u7684\u4e94\u5c3a\uff0c\u5389\u590d\u751f\u77e5 \u9053\u5979\u201c\u795e\u86c7\u638c\u201d\u548c\u6bd2\u6307\u7532\u7684\u5389\u5bb3\uff1a\u4e5f\u4e0d\u6562\u8ba9\u5979\u6293\u4e2d\uff0c\u4e24\u4eba\u5c55\u5f00\u4e86\u6e38\u8eab\u7f20\u6597\u7684\u529f\u592b\uff0c\u5f7c\u6b64\u90fd\u5728 \u4e58\u6687\u62b5\u9699\uff0c\u6253\u5f97\u6ee1\u5c4b\u5b50\u90fd\u662f\u638c\u98ce\u6fc0\u8361\u3002\u5728\u5c4b\u5185\u7684\u5f1f\u5b50\u7eb7\u7eb7\u8d70\u907f\u3002 \u3000\u3000\u90a3\u53ea\u91d1\u6bdb\u72e1\u62b1\u8d77\u4e86\u6c5f\u6d77\u5929\uff0c\u4f59\u6012\u672a\u606f\uff0c\u817e\u51fa\u4e00\u53ea\u957f\u81c2\uff0c\u89c1\u7269\u5c31\u6293\uff0c\u5c06\u6321\u5728\u9762\u524d\u7684\u8336\u51e0\u4e4b \u7c7b\u7684\u5bb6\u79c1\u6293\u5f97\u7247\u7247\u788e\u88c2\uff0c\u90a3\u8001\u5a46\u5a46\u7684\u95e8\u4e0b\u5f1f\u5b50\u90e8\u6015\u4e86\u5b83\u4eec\uff0c\u54ea\u6562\u4e0a\u524d\u62e6\u963b\uff0c\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u4e00 \u53ea\u8854\u770b\u5b9d\u5251\uff0c\u4e00\u53ea\u631f\u7740\u6c5f\u6d77\u5929\uff0c\u547c\u5578\u800c\u53bb\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u867d\u7136\u4e0d\u80fd\u52a8\u5f39\uff0c\u6930\u667a\u4ecd\u7136\u6e05\u9192\uff0c\u8033\u8fb9\u53a2\u53ea\u542c\u5f97\u6b27\u9633\u5a49\u60ca\u6050\u7684\u53eb\u58f0\uff0c\u8eab\u5b50\u5374\u4f3c\u817e\u4e91 \u9a7e\u96fe\u4e00\u822c\uff0c\u88ab\u90a3\u53ea\u91d1\u6bdb\u72e1\u631f\u7740\u98de\u5954\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u5df2\u51fa\u4e86\u90a3\u6761\u5c71\u8c37\u3002\u6c5f\u6d77\u5929\u628a\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c \u5fc3\u91cc\u60f3\u9053\uff0c\u201c\u60f3\u4e0d\u5230\u6211\u4f1a\u518d\u5ea6\u843d\u5728\u5929\u9b54\u6559\u4e3b\u4e4b\u624b\uff0c\u6211\u5c0f\u65f6\u5019\u5979\u5bf9\u6211\u5f88\u597d\uff0c\u4e0d\u77e5\u8fd9\u6b21\u5374\u4e3a\u4f55\u8981 \u6d3e\u90a3\u9ed1\u8863\u5c11\u5e74\u5e26\u4e86\u91d1\u6bdb\u72e1\u6765\u6349\u6211\uff1f\u6211\u88ab\u6349\u53bb\u4e0d\u6253\u7d27\uff0c\u53ea\u662f\u90a3\u6076\u6bd2\u7684\u8001\u5deb\u5a46\u5fc5\u7136\u66f4\u4e3a\u53d1\u6012\uff0c\u6b27 \u9633\u59d1\u5a18\u53c8\u4e0d\u77e5\u8981\u53d7\u600e\u6837\u7684\u6298\u78e8\u4e86\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u9a6c\u8e44\u5f97\u5f97\u4e4b\u58f0\uff0c\u8fce\u9762\u4e00\u9a91\u9a8f\u9a6c\u9a70\u6765\uff0c\u9a6c\u4e0a\u7684\u9a91\u8005\u6b63\u662f\u6307\u70b9\u6c5f\u6d77\u5929\u5230\u8fd9\u91cc\u6765\u7684\u90a3\u4e2a \u5c11\u5e74\uff0c\u4ed6\u7a81\u7136\u89c1\u6b64\u60c5\u72b6\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u6c5f\u76f8\u516c\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u6211\u7684\u5e08\u59b9\u5462\uff0c\u5979\u9003\u51fa\u6765\u4e86 \u6ca1\u6709\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u91d1\u6bdb\u72e1\u4e00\u58f0\u5927\u543c\uff0c\u90a3\u5339\u9a6c\u5ffd\u5730\u5411\u65c1\u8fb9\u4e00\u7a9c\uff0c\u4fbf\u5012\u4e86\u535c\u6765\uff0c\u5c06\u90a3\u5c11\u5e74\u6454\u5f97 \u53d1\u660f\u5760\u843d\u4e0b\u5730\uff0c\u539f\u6765\u8fd9\u5339\u9a6c\u88ab\u91d1\u6bdb\u72e1\u5413\u7834\u4e86\u80c6\uff0c\u5012\u5730\u6b7b\u4e86\u3002 \u3000\u3000\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u5949\u4e86\u4e3b\u4eba\u4e4b\u547d\uff0c\u4e0d\u51c6\u80e1\u4e71\u4f24\u4eba\uff0c\u56e0\u6b64\u4ece\u90a3\u6454\u6655\u4e86\u7684\u5c11\u5e74\u8eab\u8fb9\u8dd1\u8fc7\uff0c\u5374\u5e76\u4e0d \u53bb\u4f24\u5bb3\u4ed6\u3002\u90a3\u5c11\u5e74\u4e00\u65f6\u60c5\u6025\uff0c\u9732\u51fa\u4e86\u672c\u6765\u8eab\u4efd\uff0c\u6c5f\u6d77\u5929\u53e3\u4e0d\u80fd\u8a00\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u662f\u6b27 \u9633\u59d1\u5a18\u7684\u5e08\u5144\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u60f3\u8d77\u524d\u4e8b\uff0c\u604d\u7136\u5927\u609f\uff0c\u6697\u81ea\u7b11\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4ed6\u518d\u4e09\u53ee\u5631\uff0c\u4e0d\u8bb8\u6211\u6cc4\u6f0f\u662f\u8c01\u6307\u70b9\u6211 \u7684\uff0c\u770b\u6765\u4ed6\u662f\u60f3\u5047\u624b\u4e8e\u6211\uff0c\u6551\u51fa\u4ed6\u7684\u5e08\u59b9\u3002\u4ed6\u5e08\u7236\u8fd9\u6837\u51f6\uff0c\u4ed6\u5374\u7ade\u6562\u5192\u8fd9\u4e2a\u9669\uff0c\u55ef\uff0c\u4ed6\u5bf9\u5e08 \u59b9\u5012\u771f\u662f\u4e0d\u9519\u5462\uff01\u201d \u3000\u3000\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u7ee7\u7eed\u98de\u5954\uff0c\u4e0a\u4e86\u4e00\u5ea7\u9ad8\u5c71\uff0c\u631f\u7740\u6c5f\u6d77\u5929\u7684\u90a3\u53ea\u91d1\u6bdb\u72e1\u4f3c\u5acc\u4e0d\u4fbf\uff0c\u5c06\u6c5f\u6d77\u5929 \u653e\u5230\u80cc\u4e0a\uff0c\u5b83\u53c8\u4f3c\u4e4e\u77e5\u9053\u6c5f\u6d77\u5929\u53cc\u624b\u4e0d\u80fd\u7528\u529b\uff0c\u4fbf\u5c06\u5b83\u7684\u957f\u5c3e\u5df4\u5012\u5377\u8fc7\u6765\uff0c\u50cf\u6761\u7ef3\u7d22\u4e00\u6837\u3002 \u5c06\u6c5f\u6d77\u5929\u62e6\u8170\u6346\u4f4f\uff0c\u5b83\u56db\u811a\u7740\u5730\uff0c\u8dd1\u5f97\u66f4\u5feb\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4e2a\u5c71\u5773\uff0c\u5ffd\u89c1\u524d\u9762\u6709\u4e24\u4e2a\u9ed1\u70b9\uff0c\u968f\u5373\u542c\u5f97\u4e00\u4e2a\u5973\u5b50\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u5988\uff0c\u4f60 \u77a7\uff0c\u8fd9\u4e24\u4e2a\u602a\u517d\uff01\u201d\u7d27\u63a5\u7740\u4e00\u4e2a\u8f83\u4e3a\u82cd\u8001\u7684\u5987\u4eba\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5947\u602a\uff0c\u8fd9\u662f\u5929\u9b54\u6559\u526f\u6559\u4e3b\u7684\u91d1 \u6bdb\u72e1\u5440\uff01\u201d \u3000\u3000\u5148\u5934\u90a3\u4e2a\u5973\u5b50\u7684\u58f0\u97f3\u53c8\u9053\uff1a\u201c\u55ef\uff0c\u8fd9\u53ea\u91d1\u6bdb\u72e1\u8fd8\u80cc\u7740\u4e00\u4e2a\u4eba\u5462\uff01\u54c8\uff0c\u90a3\u53ea\u91d1\u6bdb\u72e1\u66f4\u6709 \u8da3\uff0c\u5b83\u53e3\u91cc\u8854\u7740\u4e00\u67c4\u957f\u5251\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53cc\u65b9\u7684\u8ddd\u79bb\u5df2\u8fd1\u3002\u6c5f\u6d77\u5929\u4ef0\u8d77\u8116\u5b50\uff0c\u53ea\u89c1\u662f\u4e00\u4e2a\u5999\u9f84\u5c11\u5973\u4f34\u7740\u4e00\u4e2a\u5e74\u7ea6\u4e94\u5341 \u5de6\u53f3\u3001\u5934\u53d1\u6709\u4e9b\u6591\u767d\u7684\u5987\u4eba\uff0c\u542c\u5979\u4eec\u7684\u79f0\u547c\uff0c\u5f53\u7136\u662f\u4e24\u6bcd\u5973\u4e86\u3002 \u3000\u3000\u90a3\u5987\u4eba\u6709\u70b9\u4f5d\u507b\uff0c\u6491\u7740\u4e00\u6839\u62d0\u6756\uff0c\u62d0\u6756\u5728\u5730\u4e0a\u4e00\u70b9\uff0c\u4fbf\u5411\u524d\u63a0\u51fa\u6570\u4e08\uff0c\u6765\u5f97\u5feb\u6781\u3002\u800c\u4e14 \u662f\u8fce\u7740\u8fd9\u4e24\u53ea\u91d1\u6bdb\u72e1\u800c\u6765\u3002\u6c5f\u6d77\u5929\u6697\u81ea\u5947\u602a\uff0c\u5fc3\u60f3\uff0c\u201c\u8fd9\u4e24\u6bcd\u5973\u7684\u80c6\u5b50\u5012\u4e5f\u771f\u5927\uff0c\u7adf\u7136\u4e0d\u6015 \u91d1\u6bdb\u72e1\uff01\u201d \u3000\u3000\u53ea\u542c\u5f97\u90a3\u5987\u4eba\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u91d1\u6bdb\u72e1\u8854\u7684\u662f\u67c4\u5b9d\u5251\uff0c\u8fd9\u5c0f\u5b50\u4e5d\u6210\u662f\u91d1\u4e16\u9057\u90a3 \u4e2a\u59d3\u6c5f\u7684\u5f92\u5f1f\u3002\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u6628\u665a\u9ebb\u8863\u9053\u4eba\u8dd1\u6765\u62a5\u4fe1\uff0c\u4e0d\u662f\u8bf4\u8fd9\u5c0f\u5b50\u5df1\u843d\u5728\u54b1\u4eec\u5a49\u59b9\u7684\u624b \u4e2d\u4e86\u4e48\uff1f\u201d\u90a3\u5987\u4eba\u9053\uff1a\u201c\u8fd9\u91cc\u9762\u5b9a\u6709\u53e4\u602a\uff0c\u7f62\uff0c\u7f62\uff0c\u6211\u4e5f\u987e\u4e0d\u5f97\u8981\u5f97\u7f6a\u5929\u9b54\u6559\u4e86\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53cc\u65b9\u7684\u8ddd\u79bb\u5df2\u5728\u6570\u4e08\u4e4b\u5185\uff0c\u524d\u9762\u90a3\u53ea\u91d1\u6bdb\u72e1\u89c1\u6709\u4eba\u62e6\u7740\u53bb\u8def\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u4fbf \u5411\u524d\u6251\u53bb\uff0c\u90a3\u5987\u4eba\u559d\u9053\uff1a\u201c\u5b7d\u755c\u4e0d\u957f\u773c\u775b\uff0c\u770b\u6211\u5c06\u4f60\u7684\u62db\u5b50\u5e9f\u4e86\uff01\u201d \u3000\u3000\u9661\u7136\u95ee\u53ea\u542c\u5f97\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\uff0c\u6c5f\u6d77\u5929\u4e0d\u77e5\u9053\u91d1\u6bdb\u72e1\u6709\u6ca1\u6709\u7ed3\u5979\u7684\u6697\u5668\u6253\u4e2d\uff0c\u53ea\u89c9\u5f97\u81ea\u5df1 \u7684\u8eab\u4e0a\u5df2\u4e2d\u4e86\u4e00\u4e0b\uff0c\u4f3c\u4e4e\u662f\u9897\u94c1\u83b2\u5b50\u3002\u201d \u3000\u3000\u90a3\u5987\u4eba\u6025\u58f0\u53eb\u9053\uff1a\u201c\u78a7\u513f\u9000\u5f00\uff01\u201d\u4e0d\u77e5\u600e\u7684\uff0c\u5728\u8fd9\u65f6\u5019\uff0c\u6c5f\u6d77\u5929\u5ffd\u7136\u89c9\u5f97\u8eab\u5b50\u4f3c\u4e4e\u80fd\u591f \u8f6c\u52a8\uff0c\u4ed6\u7528\u624b\u8098\u6309\u7740\u91d1\u6bdb\u72e1\u7684\u80cc\u810a\uff0c\u62ac\u8d77\u5934\u6765\u3002\u4fbf\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u5987\u4eba\u62a1 \u8d77\u62d0\u6756\uff0c\u6b63\u597d\u5411\u7740\u91d1\u6bdb\u72e1\u6253\u4e0b\uff01 \u3000\u3000\u90a3\u91d1\u6bdb\u72e1\u7740\u4e86\u4e00\u62d0\uff0c\u75db\u5f97\u72c2\u568e\uff0c\u500f\u5730\u8df3\u8d77\u6570\u4e08\u6765\u9ad8\uff0c\u8fd9\u5987\u4eba\u89c1\u5b83\u6765\u52bf\u51f6\u731b\u3002\u4e0d\u6562\u6b63\u9762\u8fce \u654c\uff0c\u811a\u8ddf\u4e00\u65cb\uff0c\u4fbf\u8f6c\u8fc7\u4e00\u8fb9\uff0c\u4e3e\u8d77\u62d0\u6756\uff0c\u6b63\u5f85\u62e6\u8170\u626b\u53bb\uff0c\u90a3\u53e6\u4e00\u53ea\u91d1\u6bdb\u72e1\u4e5f\u5df2\u6251\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u90a3\u5987\u4eba\u559d\u9053\uff0c\u201c\u5b7d\u755c\uff0c\u4f60\u4e5f\u6765\u9001\u6b7b\uff01\u201d\u8f6c\u8fc7\u62d0\u6756\u7529\u4e86\u4e00\u62db\u201c\u4e3e\u706b\u64a9\u5929\u201d\u3002\u5bf9\u51c6\u90a3\u53ea\u91d1\u6bdb \u72e1\u7684\u8111\u888b\uff0c\u8981\u662f\u5b83\u6251\u4e0a\u6765\uff0c\u8fd9\u4e00\u62d0\u51c6\u4f1a\u6572\u7834\u5b83\u7684\u5929\u7075\u76d6\u3002 \u3000\u3000\u9661\u7136\u95f4\uff0c\u5ffd\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u539f\u6765\u90a3\u53ea\u91d1\u6bdb\u72e1\u6781\u662f\u7075\u5f02\uff0c\u5b83\u770b\u89c1\u540c\u4f34\u5403\u4e86\u4e8f\uff0c\u5df2\u77e5\u81ea\u5df1\u4e0d\u662f \u8fd9\u5987\u4eba\u7684\u654c\u624b\uff0c\u5b83\u7adf\u4f3c\u6b66\u6797\u9ad8\u624b\u4e00\u822c\uff0c\u5728\u5239\u90a3\u95f4\uff0c\u5ffd\u7136\u540e\u817f\u4eba\u7acb\uff0c\u5c06\u90a3\u67c4\u5b9d\u5251\u62d4\u4e86\u51fa\u6765\uff0c\u524d \u81c2\u4e00\u4f38\uff0c\u5251\u5149\u66b4\u957f\uff0c\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u4fbf\u5c06\u90a3\u5987\u4eba\u7684\u62d0\u6756\u524a\u53bb\u4e00\u622a\u3002 \u3000\u3000\u90a3\u5987\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u8d5e\u9053\uff1a\u201c\u88c1\u4e91\u5b9d\u5251\uff0c\u679c\u7136\u5929\u4e0b\u65e0\u53cc\uff01\u201d\u614c\u5fd9\u6536\u62d0\uff1a\u6b63\u5f85\u53d8\u62db\u6253\u51fa\uff0c\u5c31 \u5728\u6b64\u65f6\uff0c\u5ffd\u5477\u5f97\u5979\u7684\u5973\u513f\u4e00\u58f0\u60ca\u547c\uff01 \u3000\u3000\u539f\u6765\u80cc\u7740\u6c5f\u6d77\u5929\u7684\u90a3\u53ea\u91d1\u6bdb\u72e1\uff0c\u4ece\u8fd9\u5987\u4eba\u7684\u5934\u9876\u4e0a\u65b9\u8df3\u8fc7\uff0c\u5374\u5e76\u4e0d\u56de\u8fc7\u8eab\u5e2e\u52a9\u540c\u4f34\uff0c\u800c \u662f\u6539\u4e86\u65b9\u5411\uff0c\u5411\u90a3\u5c11\u5973\u6251\u53bb\u3002\u90a3\u5c11\u5973\u5251\u65b9\u51fa\u9798\uff0c\u4fbf\u5403\u5b83\u4e00\u6293\u6293\u53bb\uff0c\u518d\u4e00\u6293\u4fbf\u6293\u88c2\u4e86\u5979\u7684\u8863\u88f3\uff01 \u3000\u3000\u90a3\u5c11\u5973\u811a\u5c16\u4e00\u70b9\uff0c\u98de\u8eab\u4e0a\u6811\uff0c\u91d1\u6bdb\u72e1\u4e00\u7eb5\uff0c\u8ddd\u79bb\u5979\u53ea\u6709\u5c11\u8bb8\uff0c\u9669\u9669\u5c31\u8981\u6293\u7740\u5979\u7684\u811a\u540e \u8ddf\uff0c\u770b\u6765\u5b83\u82e5\u4e0d\u662f\u80cc\u4e86\u4e2a\u4eba\uff0c\u8df3\u8dc3\u4e0d\u7075\uff0c\u8fd9\u4e00\u6293\u5c31\u8981\u628a\u90a3\u5c11\u5973\u6293\u4e86\u4e0b\u6765\u3002\u90a3\u5c11\u5973\u5413\u5f97\u9b42\u98de\u9b44 \u6563\uff0c\u614c\u5fd9\u53eb\u9053\uff1a\u201c\u5a18\uff0c\u5feb\u6765\u6551\u547d\uff01\u201d \u3000\u3000\u5230\u4e86\u8fd9\u65f6\uff0c\u8fd9\u5987\u4eba\u5f53\u7136\u987e\u4e0d\u5f97\u518d\u53bb\u6253\u91d1\u6bdb\u72e1\uff0c\u5979\u6012\u559d\u4e00\u58f0\uff1a\u201c\u5b7d\u755c\u5927\u80c6\uff01\u201d\u4e00\u9762\u53d1\u51fa\u6697 \u5668\uff0c\u8d76\u90a3\u91d1\u6bdb\u72e1\uff0c\u4e00\u9762\u98de\u8eab\u8fc7\u53bb\uff0c\u63f4\u6551\u5973\u513f\u3002 \u3000\u3000\u8fd9\u4e24\u53ea\u91d1\u6bdb\u72e1\u7bad\u4e00\u822c\u8c79\u5411\u524d\u5c04\u51fa\uff0c\u5f85\u5f97\u90a3\u5987\u4eba\u5c06\u5973\u513f\u4ece\u6811\u4e0a\u6551\u4e0b\uff0c\u5b83\u4eec\u65e9\u5df2\u8dd1\u8fc7\u4e86\u51e0\u9053 \u5c71\u5c97\uff0c\u4fa5\u662f\u90a3\u5987\u4eba\u8f7b\u529f\u518d\u9ad8\uff0c\u4e5f\u8ffd\u4e0d\u4e0a\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4f0f\u5728\u91d1\u6bdb\u72e1\u80cc\u4e0a\uff0c\u6697\u6697\u597d\u7b11\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e24\u53ea\u91d1\u6bdb\u72e1\u5f53\u771f\u673a\u7075\uff0c\u7adf\u7136\u4e5f\u61c2\u5f97\u58f0\u4e1c \u51fb\u897f\u4e4b\u8ba1\u3002\u8fd9\u5987\u4eba\u7684\u6b66\u529f\u4e5f\u771f\u5389\u5bb3\uff0c\u770b\u6765\u4e0d\u5728\u6b27\u9633\u5a49\u7684\u5e08\u7236\u4e4b\u4e0b\u3002\u542c\u90a3\u5c11\u5973\u7684\u79f0\u547c\uff0c\u5979\u53eb\u6b27 \u9633\u5a49\u4f5c\u2018\u59b9\u5b50\u2019\uff0c\u90a3\u4e48\u6b27\u9633\u5a49\u4e5f\u662f\u5979\u7684\u5973\u513f\u4e86\uff1f\u5979\u6709\u8fd9\u6837\u597d\u7684\u6b66\u529f\uff0c\u5374\u4e3a\u4f55\u8fd8\u8981\u5c06\u5973\u513f\u9001\u7ed9 \u522b\u4eba\u4f5c\u5f92\u5f1f\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u60f3\u8d77\u4e86\u6b27\u9633\u5a49\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u53c8\u662f\u4e00\u7247\u60d8\u7136\u3002\u4ed6\u5438\u4e86\u53e3\u6c14\uff0c\u5ffd\u5730\u89c9\u5f97\u6c14\u673a\u7545\u901a\uff0c\u4e00\u8bd5 \u4e4b\u4e0b\uff0c\u8eab\u624b\u7adf\u7136\u80fd\u591f\u6d3b\u52a8\u3002 \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u672c\u6765\u5c31\u4f1a\u201c\u98a0\u5012\u7a74\u9053\u201d\u7684\u529f\u592b\uff0c\u53ea\u56e0\u88ab\u90a3\u8001\u5a46\u5a46\u7528\u201c\u6390\u7a74\u201d\u7684\u602a\u624b\u6cd5\uff0c\u5185\u52b2 \u900f\u8fdb\u4ed6\u7684\u4f53\u5185\uff0c\u7a74\u9053\u9644\u8fd1\u7684\u8840\u8109\u53d7\u5230\u963b\u788d\uff0c\u4e0d\u80fd\u6d41\u901a\uff0c\u6545\u6b64\u7740\u4e86\u9053\u513f\u3002\u521a\u624d\u4ed6\u5403\u90a3\u5987\u4eba\u7684\u4e00 \u9897\u94c1\u83b2\u4e8e\u6253\u4e2d\uff0c\u6b63\u5de7\u6253\u5728\u76f8\u5e94\u7684\u7a74\u9053\u65b9\u4f4d\uff0c\u7a74\u9053\u53d7\u4e86\u523a\u6fc0\uff0c\u6c14\u8840\u7adf\u7136\u6e10\u6e10\u6d41\u901a\u8d77\u6765\uff0c\u52a0\u4e0a\u4ed6 \u672c\u8eab\u6709\u62a4\u4f53\u795e\u529f\uff0c\u771f\u6c14\u8fd0\u4e86\u51e0\u8f6c\uff0c\u4e0d\u4e45\uff0c\u7a74\u9053\u4fbf\u89e3\u5f00\u4e86\u3002 \u3000\u3000\u90a3\u53ea\u91d1\u6bdb\u72e1\u8dd1\u4e86\u4e00\u4f1a\uff0c\u5ffd\u7136\u6b47\u4e86\u4e0b\u6765\uff0c\u53d1\u51fa\u545c\u545c\u7684\u53eb\u58f0\uff0c\u5b83\u7684\u540c\u4f34\u8d70\u8fc7\u6765\uff0c\u8f7b\u8f7b\u548c\u5b83\u6328 \u64e6\u3002\u6c5f\u6d77\u5929\u62b1\u7740\u91d1\u6bdb\u72e1\u7684\u9888\u9879\uff0c\u4fef\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u91d1\u6bdb\u72e1\u7684\u773c\u89d2\uff0c\u6709\u70b9\u70b9\u9c9c\u8840\u6ef4\u4e0b\uff0c\u60f3\u5fc5\u662f \u88ab\u90a3\u5987\u4eba\u7684\u6697\u5668\u6253\u4f24\u7684\uff0c\u5e78\u800c\u6ca1\u6709\u6b63\u4e2d\u773c\u73e0\u3002\u6c5f\u6d77\u5929\u672c\u6765\u968f\u8eab\u5e26\u6709\u91d1\u521b\u836f\uff0c\u8fd9\u65f6\u4ed6\u5df2\u7ecf\u80fd\u591f \u6d3b\u52a8\uff0c\u4fbf\u4ece\u8eab\u4e0a\u638f\u51fa\u836f\u6765\uff0c\u5728\u91d1\u6bdb\u72e1\u773c\u89d2\u7684\u4f24\u53e3\u6577\u4e0a\uff0c\u91d1\u6bdb\u72e1\u611f\u5230\u4e00\u7247\u6e05\u51c9\uff0c\u75db\u695a\u5927\u51cf\uff0c\u559c \u6b22\u5f97\u8df3\u8d77\u6765\uff0c\u957f\u5c3e\u5df4\u8f7b\u8f7b\u7684\u5728\u6c5f\u6d77\u5929\u7684\u8eab\u4e0a\u626b\u6765\u626b\u53bb\uff0c\u8868\u793a\u4eb2\u70ed\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u73b0\u5728\u4ea4\u4e0a\u4e86\u670b\u53cb\u4e86\uff0c\u4f60\u7684\u5c3e\u5df4\u53ef\u4ee5\u653e\u677e\u4e86\u5427\uff1f\u201d\u5176\u5b9e\u6c5f\u6d77\u5929\u73b0\u5728\u7684 \u529f\u529b\u5df2\u7ecf\u6062\u590d\u4e86\u56db\u4e94\u5206\uff0c\u8981\u6323\u8131\u5df2\u975e\u96be\u4e8b\uff0c\u4f46\u4ed6\u4e0d\u5fcd\u4ee4\u8fd9\u91d1\u6bdb\u72e1\u53d7\u82e6\uff0c\u6545\u6b64\u8f6f\u8bed\u4e0e\u5b83\u5546\u91cf\u3002 \u3000\u3000\u90a3\u53ea\u91d1\u6bdb\u72e1\u4e5f\u679c\u771f\u901a\u7075\uff0c\u7adf\u4f3c\u542c\u61c2\u4e86\u6c5f\u6d77\u5929\u7684\u610f\u601d\uff0c\u5b83\u90a3\u6761\u5c3e\u5df4\u672c\u6765\u50cf\u7ef3\u7d22\u4e00\u822c\uff0c\u56f4\u8fc7 \u6c5f\u6d77\u5929\u7684\u8170\u80a2\uff0c\u5c06\u4ed6\u7ed1\u7740\u7684\uff0c\u8fd9\u65f6\u542c\u4e86\u6c5f\u6d77\u5929\u7684\u8981\u6c42\uff1b\u4fbf\u677e\u5f00\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5438\u4e86\u53e3\u6c14\uff0c\u6d3b\u52a8\u4e86\u4e00\u4e0b\u624b\u8db3\uff0c\u84e6\u5730\u4ece\u91d1\u6bdb\u72e1\u7684\u80cc\u4e0a\u4e00\u8dc3\u800c\u4e0b\uff0c\u4e00\u4f38\u624b\u53c8\u62a2\u56de\u4e86\u53e6 \u4e00\u53ea\u91d1\u6bdb\u72e1\u6240\u8854\u7684\u5b9d\u5251\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u81ea\u5df1\u56de\u5bb6\u53bb\u5427\uff0c\u6055\u6211\u4e0d\u548c\u4f60\u4eec\u540c\u884c\u4e86\u3002\u201d\u62d4\u817f\u4fbf\u8dd1\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u53ea\u91d1\u6bdb\u72e1\u4ece\u4ed6\u7684\u5934\u9876\u8dc3\u8fc7\uff0c\u53e6\u4e00\u53ea\u4e5f\u8ffd\u4e86\u4e0a\u6765\uff0c\u5c31\u540e\u5939\u653b\uff0c\u5404 \u4f38\u957f\u81c2\uff0c\u5411\u6c5f\u6d77\u5929\u4fbf\u6293\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5c31\u4e0d\u5ff5\u4e00\u70b9\u670b\u53cb\u7684\u60c5\u4efd\u5417\uff1f\u201d\u5728\u4ed6\u9762\u524d\u7684\u90a3\u53ea\u91d1\u6bdb\u72e1\u540e\u817f\u76f4\u7acb\uff0c\u524d \u81c2\u4e00\u62f1\uff0c\u7adf\u4f3c\u4e00\u4e2a\u4eba\u5411\u4ed6\u4f5c\u63d6\u4e00\u822c\uff0c\u63a5\u7740\u545c\u545c\u7684\u53eb\u4e86\u51e0\u58f0\uff0c\u6c5f\u6d77\u5929\u61c2\u5f97\u5b83\u7684\u610f\u601d\uff0c\u90a3\u662f\u56e0\u4e3a \u5b83\u4eec\u5949\u4e86\u4e3b\u4eba\u4e4b\u547d\uff0c\u975e\u6349\u4ed6\u56de\u53bb\u4e0d\u53ef\uff0c\u6545\u6b64\u8bf7\u6c42\u6c5f\u6d77\u5929\u539f\u8c05\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4e8b\u60c5\u8981\u529e\u54e9\uff0c\u4f60\u4eec\u8ba9\u6211\u8d70\u5427\u3002\u201d\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u6293\u4e86\u6293 \u816e\uff0c\u84e6\u5730\u53d1\u51fa\u4f4e\u6c89\u7684\u5578\u58f0\uff0c\u500f\u7136\u95f4\u4fbf\u540c\u65f6\u5411\u6c5f\u6d77\u5929\u6251\u5012\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4f7f\u51fa\u5929\u7f57\u6b65\u6cd5\uff0c\u4ece\u524d\u9762\u90a3\u53ea\u91d1\u6bdb\u72e1\u7684\u80c1\u4e0b\u94bb\u8fc7\uff0c\u54ea\u77e5\u540e\u9762\u90a3\u53ea\u91d1\u6bdb\u72e1\u52a8\u4f5c\u5feb\u6781\uff0c \u8ffd\u4e0a\u6765\u4e00\u6293\u4fbf\u6293\u7740\u4e86\u6c5f\u6d77\u5929\u7684\u80a9\u818a\uff0c\u6c5f\u6d77\u5929\u201c\u54ce\u54df\u201d\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff0c\u90a3\u91d1\u6bdb\u5506\u53ea\u8981\u5c06\u4ed6\u751f \u64d2\uff0c\u65e0\u610f\u5c06\u4ed6\u4f24\u5bb3\uff0c\u542c\u5f97\u53eb\u58f0\uff0c\u6307\u722a\u7a0d\u677e\uff0c\u5374\u7528\u957f\u5c3e\u5df4\u53cd\u5377\u8fc7\u6765\uff0c\u6c5f\u6d77\u5929\u65e9\u5df2\u7528\u4e86\u4e00\u4e2a \u201c\u5378\u201d\u5b57\u51b3\uff0c\u8131\u51fa\u4e86\u8eab\u3002 \u3000\u3000\u8fd9\u4e24\u53ea\u91d1\u6bdb\u72e1\u6beb\u4e0d\u653e\u677e\uff0c\u4ea6\u6b65\u4ea6\u8d8b\uff0c\u770b\u90a3\u6a21\u6837\uff0c\u975e\u628a\u6c5f\u6d77\u5929\u6d3b\u64d2\u4e0d\u53ef\uff0c\u6c5f\u6d77\u5929\u76b1\u4e86\u76b1 \u7709\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u518d\u4e0d\u9000\u4e0b\uff0c\u6211\u53ef\u8981\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d\u62d4\u51fa\u88c1\u4e91\u5b9d\u5251\uff0c\u4fe1\u624b\u4e00\u6325\uff0c\u201c\u5580\u5693\u201d\u4e00 \u58f0\uff0c\u5c06\u4e00\u5757\u77f3\u5934\u65a9\u4e3a\u4e24\u534a\uff0c\u968f\u5373\u6296\u8d77\u4e86\u6735\u6735\u5251\u82b1\uff0c\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u8bc6\u5f97\u5b9d\u5251\u7684\u5389\u5bb3\uff0c\u4f46\u4ea6\u4ec5\u662f \u5411\u4e24\u8fb9\u95ea\u8eb2\uff0c\u4ecd\u7136\u4e0d\u80af\u8dd1\u5f00\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u821e\u8d77\u5b9d\u5251\uff0c\u5316\u6210\u4e86\u4e00\u9053\u94f6\u8679\uff0c\u51b2\u51fa\u4e86\u5341\u51e0\u6b65\uff0c\u8033\u8fb9\u53a2\u53ea\u542c\u5f97\u98ce\u58f0\u98d2\u98d2\uff0c\u4e0d\u5fc5\u56de \u5934\uff0c\u5df2\u77e5\u662f\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u8ffd\u6765\u3002\u6c5f\u6d77\u5927\u6479\u5730\u56de\u8eab\uff0c\u865a\u65ab\u4e00\u5251\uff0c\u4f6f\u4f5c\u53d1\u6012\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u60f3 \u6d3b\u4e86\u4e48\uff1f\u6538\u5feb\u8d70\u5f00\uff0c\u522b\u518d\u6765\u7ea0\u7f20\u6211\u4e86\uff01\u201d \u3000\u3000\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u89c1\u5251\u5149\u5c04\u6765\uff0c\u7acb\u5373\u95ea\u5f00\uff0c\u53ef\u662f\u6c5f\u6d77\u5929\u4e00\u8d70\uff0c\u5b83\u4eec\u4ecd\u7136\u7d27\u7d27\u76f8\u968f\uff0c\u5636\u9e23\u4e0d \u5df2\uff01\u6c5f\u6d77\u5929\u5df2\u6709\u4e00\u5929\u4e00\u591c\uff0c\u672a\u66fe\u5403\u8fc7\u4e1c\u897f\uff0c\u8dd1\u4e86\u4e00\u4f1a\uff0c\u4fbf\u89c9\u5f97\u6709\u70b9\u5934\u6655\u773c\u82b1\uff0c\u80cc\u5fc3\u4e5f\u6709\u70b9\u9ebb \u75d2\u75d2\u7684\u611f\u89c9\uff0c\u539f\u6765\u4ed6\u65e5\u95f4\u88ab\u6b27\u9633\u5a49\u7684\u5e08\u7236\u7528\u6bd2\u6307\u7532\u6293\u7834\u4e86\u4e00\u70b9\u76ae\u8089\uff0c\u5168\u4ed7\u5185\u529f\u6df1\u6e5b\uff0c\u5c06\u6bd2\u8feb \u4f4f\uff0c\u4e0d\u8ba9\u5b83\u653b\u4e0a\u5fc3\u5934\uff0c\u73b0\u5728\u6c14\u529b\u6e10\u8870\uff0c\u6bd2\u6027\u4e5f\u4fbf\u6e10\u6e10\u53d1\u4f5c\uff0c\u5411\u4e0a\u8513\u5ef6\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5927\u4e3a\u70e6\u607c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u8dd1\u4e0d\u8fc7\u8fd9\u4e24\u53ea\u91d1\u6bdb\u72e1\uff0c\u6211\u4e0d\u6740\u5b83\uff0c\u5230\u4e86 \u529b\u7aed\u7b4b\u75b2\u4e4b\u65f6\uff0c\u7ec8\u987b\u88ab\u5b83\u64d2\u4e86\u3002\u800c\u4e14\u6211\u82e5\u4e0d\u8d76\u7d27\u627e\u4e2a\u5730\u65b9\u9759\u5750\u8fd0\u529f\uff0c\u6bd2\u6027\u53d1\u4f5c\uff0c\u4e0d\u5fc5\u91d1\u6bdb\u72e1 \u6765\u6293\u6211\uff0c\u6211\u5148\u8981\u6ca1\u547d\u4e86\u3002\u201d \u3000\u3000\u4ee5\u6c5f\u6d77\u5929\u7684\u6b66\u529f\u52a0\u4e0a\u4ed6\u8fd9\u67c4\u5929\u4e0b\u65e0\u53cc\u7684\u5b9d\u5251\uff0c\u8981\u6740\u8fd9\u4e24\u53ea\u91d1\u6bdb\u72e1\uff0c\u539f\u662f\u6613\u5982\u53cd\u638c\uff0c\u53ef\u662f \u4ed6\u60f3\u5230\u8fd9\u4e24\u53ea\u91d1\u6bdb\u72e1\u4e43\u662f\u4ed6\u5e08\u7236\u7684\u8001\u670b\u53cb\uff0c\u800c\u4e14\u5bf9\u4ed6\u4e5f\u5e76\u65e0\u6076\u610f\uff0c\u56e0\u6b64\u8e0c\u8e87\u518d\u4e09\uff1a\u8fd8\u662f\u4e0d\u613f \u7528\u5b9d\u5251\u771f\u7684\u53bb\u6740\u91d1\u6bdb\u72e1\u3002\u53ef\u662f\u6c5f\u6d77\u5929\u82e5\u53ea\u662f\u7528\u5b9d\u5251\u865a\u58f0\u6050\u5413\uff0c\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u5fe0\u4e8e\u4e3b\u4eba\uff0c\u5374\u53c8 \u662f\u65e0\u8bba\u5982\u4f55\u4e5f\u5413\u4e0d\u9000\u7684\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u5728\u88ab\u7ea0\u7f20\u788d\u65e0\u53ef\u5948\u4f55\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u5639\u4eae\u7684\u9e1f\u53eb\uff0c\u6c5f\u6d77\u5929\u5fc3\u5934\u4e00\u51db\uff0c\u6b63\u81ea\u60f3 \u9053\uff0c\u201c\u662f\u4ec0\u4e48\u5927\u4e4c\u3001\u53eb\u5f97\u8fd9\u6837\u54cd\u4eae\uff01\u201d\u5ffd\u89c1\u6674\u7a7a\u98de\u6765\u4e86\u4e00\u7247\u9ed1\u4e91\uff0c\u8d8a\u98de\u8d8a\u4f4e\uff0c\u5374\u539f\u6765\u662f\u4e00\u53ea \u7855\u5927\u65e0\u670b\u7684\u5140\u9e70\uff0c\u770b\u5b83\u53cc\u7fc5\u5c55\u5f00\uff1b\u4e00\u53ea\u7fc5\u8180\u8db3\u6709\u4e00\u4e08\u5f00\u5916\uff01 \u3000\u3000\u90a3\u5140\u9e70\u201c\u560e\u560e\u560e\u201d\u7684\u53eb\u4e86\u51e0\u58f0\uff0c\u5ffd\u5730\u4fbf\u6251\u4e86\u4e0b\u6765\uff0c\u6c5f\u6d77\u5929\u88ab\u5b83\u6247\u8d77\u7684\u98ce\u529b\u4e00\u522e\uff0c\u8eab\u5b50\u4e0d \u5c71\u5df2\u7684\u6643\u4e86\u51e1\u6643\uff0c\u6b63\u8981\u7528\u5251\u62a4\u8eab\uff0c\u53ea\u89c1\u90a3\u53ea\u5140\u9e70\u5df2\u5411\u91d1\u6bdb\u72e1\u6293\u4e0b\uff01 \u3000\u3000\u4e24\u53ea\u91d1\u6bdb\u72e1\u540c\u65f6\u7eb5\u8d77\u3002\u821e\u52a8\u957f\u81c2\u548c\u5b83\u5bf9\u6293\uff0c\u5140\u9e70\u88ab\u5b83\u4eec\u6293\u4e0b\u4e86\u4e00\u7247\u7fbd\u6bdb\uff0c\u53cc\u7fc5\u84e6\u5730\u4e00\u5f20 \u4e00\u6251\uff0c\u8fd9\u4e24\u53ea\u91d1\u6bdb\u72e1\u867d\u662f\u517d\u4e2d\u4e4b\u738b\uff0c\u5374\u5403\u4e0d\u4f4f\u90a3\u80a1\u98ce\u529b\uff0c\u8eab\u8eaf\u65b9\u624d\u7eb5\u8d77\uff0c\u4e00\u4e2a\u5012\u683d\u8471\u53c8\u8dcc\u4e86 \u4e0b\u6765\uff0c\u90a3\u5140\u9e70\u53cc\u722a\u9f50\u4e0b\uff0c\u5c06\u4e24\u53ea\u91d1\u6bdb\u72e1\u540c\u65f6\u6293\u8d77\uff0c\u98de\u4e0a\u7a7a\u4e2d\uff0c\u5ffd\u5730\u5c06\u722a\u677e\u5f00\uff0c\u628a\u4e24\u53ea\u91d1\u6bdb\u72e1 \u90fd\u629b\u4e0b\u4e86\u8c37\u5e95\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5927\u5403\u4e00\u60ca\uff0c\u4f46\u4e0d\u8fc7\u4e00\u4f1a\uff0c\u5df2\u542c\u5f97\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u5728\u8c37\u5e95\u543c\u53eb\uff0c\u6c5f\u6d77\u5929\u65b9\u59cb\u653e\u4e0b\u4e86\u5fc3 \u4e0a\u7684\u77f3\u5934\uff0c\u60f3\u9053\uff1a\u201c\u5e78\u4e8f\u8fd9\u4e24\u53ea\u91d1\u6bdb\u72e1\u76ae\u575a\u8089\u539a\uff0c\u82e5\u662f\u6362\u4e86\u4e2a\u4eba\uff0c\u6015\u4e0d\u8981\u6454\u6210\u8089\u997c\u3002\u201d\u5fc3\u91cc \u4e00\u559c\u4e00\u60ca\u3002\u559c\u8005\u662f\u6446\u8131\u4e86\u8fd9\u4e24\u53ea\u91d1\u6bdb\u72e1\u7684\u7ea0\u7f20\u3002\u60ca\u8005\u662f\u90a3\u53ea\u5140\u9e70\uff0c\u5b83\u6454\u4e86\u91d1\u6bdb\u72e1\u4e4b\u540e\uff0c\u4e0d\u77e5 \u4f1a\u4e0d\u4f1a\u518d\u6765\u6293\u4ed6\u3002 \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u8fd9\u53ea\u5140\u9e70\u7adf\u4f3c\u5bf9\u4ed6\u751a\u4e3a\u53cb\u5584\uff0c\u5728\u4ed6\u5934\u9876\u4e0a\u76d8\u65cb\uff0c\u53eb\u4e86\u51e0\u58f0\uff0c\u98de\u4e86\u5f00\u53bb\uff0c\u53c8\u98de \u56de\u6765\uff0c\u7fc5\u8180\u8f7b\u8f7b\u62cd\u4e86\u51e0\u4e0b\uff0c\u7136\u540e\u53c8\u518d\u7f13\u7f13\u524d\u98de\u3002\u5982\u662f\u8005\u98de\u53bb\u98de\u6765\uff0c\u7adf\u7136\u5728\u4ed6\u7684\u8eab\u8fb9\u76d8\u65cb\u4e86\u597d \u51e0\u6b21\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8be7\u5f02\u4e4b\u6781\uff0c\u5411\u90a3\u9e70\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u60f3\u8981\u6211\u8ddf\u4f60\u8d70\u4e48\uff1f\u201d\u5140\u9e70\u5f53\u7136\u4e0d\u4f1a\u7b54\u8bdd\uff0c\u4f46\u89c1\u5b83 \u5728\u5934\u9876\u4e0a\u7ed5\u4e86\u4e00\u56fd\uff0c\u5f88\u54cd\u4eae\u7684\u53eb\u4e86\u4e00\u58f0\uff0c\u7fc5\u8180\u51e0\u4e4e\u89e6\u53ca\u6c5f\u6d77\u5929\u7684\u8eab\u5b50\uff0c\u98de\u5f97\u53c8\u4f4e\u53c8\u6162\uff0c\u4fbf\u4f3c \u5728\u524d\u5f15\u8def\u4e00\u822c\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u597d\u5947\u5fc3\u8d77\uff0c\u7d22\u6027\u4fbf\u8ddf\u7740\u90a3\u53ea\u9e70\u8dd1\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u83ab\u975e\u5b83\u662f\u6709\u4eba\u517b\u7684\uff0c\u662f\u6709\u5fc3\u6765\u6551 \u6211\u4e48\uff1f\u201d\u8dd1\u4e86\u4e00\u4f1a\uff0c\u7ea2\u65e5\u897f\u6c89\uff0c\u5df2\u662f\u5c06\u8fd1\u9ec4\u660f\u7684\u65f6\u5206\uff0c\u6c5f\u6d77\u5929\u53d1\u73b0\u81ea\u5df1\u5df2\u5728\u4e00\u5ea7\u9669\u5ced\u7684\u5c71\u5cf0 \u4e0a\uff0c\u5c71\u98ce\u51bd\u51bd\uff0c\u6c5f\u6d77\u5929\u53ea\u89c9\u5f97\u5934\u6655\u773c\u82b1\uff0c\u53c8\u997f\u53c8\u51b7\uff0c\u5b9e\u5728\u8d70\u4e0d\u52a8\u4e86\u3002\u90a3\u53ea\u5140\u9e70\u5ffd\u5730\u957f\u9e23\u4e00 \u58f0\uff0c\u632f\u7ffc\u9ad8\u98de\uff0c\u7728\u773c\u95f4\u51b2\u5165\u4e91\u5c42\uff0c\u7adf\u7136\u4e0d\u77e5\u98de\u5230\u54ea\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u53eb\u4e86\u4e00\u58f0\u201c\u82e6\u4e5f\u201d\uff0c\u540e\u6094\u81ea\u5df1\u4e0d\u8be5\u597d\u5947\uff0c\u8ddf\u8fd9\u53ea\u53e4\u602a\u7684\u5140\u9e70\u8dd1\u5230\u4e86\u8fd9\u6837\u9669\u5cfb\u7684\u5c71 \u5cf0\u6765\uff0c\u73b0\u5728\u6c14\u529b\u90fd\u5df2\u8017\u5c3d\uff0c\u771f\u662f\u9677\u4e8e\u8fdb\u9000\u4e24\u96be\u7684\u5883\u5730\uff01\u800c\u4e14\u66f4\u8981\u547d\u7684\u662f\uff0c\u6240\u4e2d\u7684\u6bd2\u5df2\u56e0\u4ed6\u997f \u5f97\u6709\u6c14\u6ca1\u529b\uff0c\u96be\u4ee5\u8fd0\u7528\u7384\u529f\u800c\u52a0\u7d27\u53d1\u4f5c\u3002\u8d8a\u6765\u8d8a\u611f\u5230\u660f\u7729\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6323\u624e\u7740\u8d70\u4e86\u51e0\u6b65\uff0c\u5ffd\u89c1\u524d\u9762\u4f3c\u6709\u4e00\u661f\u7bdd\u706b\uff0c\u6c5f\u6d77\u5929\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u4ed6 \u5438\u4e86\u53e3\u6c14\uff0c\u63c9\u63c9\u773c\u775b\uff0c\u5b9a\u775b\u770b\u65f6\uff0c\u53ea\u89c1\u5728\u524d\u9762\u51e0\u68f5\u5927\u6811\u7684\u4e2d\u95f4\uff0c\u9690\u7ea6\u53ef\u89c1\u4e00\u95f4\u5c4b\u5b50\uff0c\u518d\u884c\u8fdb \u51e0\u6b65\uff0c\u539f\u6765\u90a3\u5c4b\u5b50\u5916\u9762\u6302\u7740\u4e00\u76cf\u706f\u7b3c\uff0c\u90a3\u661f\u201c\u7bdd\u706b\u201d\uff0c\u4e43\u662f\u706f\u7b3c\u900f\u51fa\u7684\u5149\u4eae\u3002 \u3000\u3000\u6c5f\u6d77\u592d\u8fd9\u65f6\u5df2\u6709\u70b9\u8ff7\u8ff7\u7cca\u7cca\uff0c\u4e5f\u65e0\u6687\u53bb\u601d\u7d22\u662f\u4ec0\u4e48\u4eba\u5bb6\u4f1a\u4f4f\u5728\u8fd9\u6837\u9669\u5cfb\u7684\u5c71\u5cf0\u4e0a\uff0c\u4e3a\u4ec0 \u4e48\u4ed6\u7684\u5c4b\u5b50\u5916\u9762\u4f1a\u6302\u6709\u706f\u7b3c\uff0c\u4ed6\u89c1\u4e86\u4eae\u5149\uff0c\u5c31\u50cf\u5927\u6d77\u4e2d\u5728\u6f02\u6d6e\u7684\u821f\u4e86\u770b\u89c1\u4e86\u706f\u5854\u4e00\u6837\uff0c\u5fc3\u91cc \u53ea\u662f\u60f3\u9053\uff1a\u201c\u597d\u4e86\uff0c\u597d\u4e86\u3002\u7ec8\u4e8e\u627e\u5230\u4e86\u4eba\u5bb6\u4e86\uff0c\u867e\u6b79\u4e5f\u5f97\u4e5e\u70b9\u4e1c\u897f\u6765\u5403\uff0c\u957f\u4e9b\u6c14\u529b\uff0c\u518d\u8fd0\u529f \u7597\u4f24\u3002\u201d \u3000\u3000\u4ed6\u51e0\u4e4e\u662f\u4f7f\u5c3d\u4e86\u5403\u5976\u7684\u6c14\u529b\uff0c\u8d70\u4e24\u6b65\u3001\u505c\u4e00\u505c\uff0c\u597d\u4e0d\u5bb9\u6613\u6323\u624e\u7740\u8d70\u5230\u4e86\u90a3\u5bb6\u4eba\u5bb6\u7684\u95e8 \u524d\uff0c\u5374\u4e3e\u4e0d\u8d77\u624b\u6765\u6572\u95e8\uff0c\u201c\u5495\u549a\u201d\u4e00\u58f0\u4fbf\u5012\u4e0b\u53bb\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e00\u4e2a\u6e05\u8106\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u679c\u7136\u6709\u4eba\u6765\u4e86\uff01\u201d\u4e24\u6247\u677f\u95e8\u6162\u6162\u6253\u5f00\uff0c\u8d70\u51fa\u6765\u4e00\u4e2a \u5c11\u5973\uff0c\u6c5f\u6d77\u5929\u5df2\u6ca1\u6709\u6c14\u529b\u8bf4\u8bdd\uff0c\u547b\u541f\u4e86\u4e24\u58f0\uff0c\u6309\u7740\u809a\u5b50\uff0c\u6323\u624e\u7740\u8ff8\u51fa\u4e24\u4e2a\u5b57\u6765\uff1a\u201c\u997f\uff0c \u997f\uff01\u201d\u58f0\u97f3\u4f4e\u6c89\u5f97\u8fde\u81ea\u5df1\u90fd\u51e0\u4e4e\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u90a3\u5c11\u5973\u53eb\u9053\uff1a\u201c\u54ce\u5401\uff0c\u597d\u53ef\u601c\uff01\u201d\u6c5f\u6d77\u5929\u5728\u8ff7\u8ff7\u7cca\u7cca\u4e2d\u53ea\u89c9\u5f97\u90a3\u5c11\u5973\u5df2\u628a\u4ed6\u6276\u4e86\u8fdb\u53bb\uff0c \u5c06\u4ed6\u5b89\u7f6e\u5728\u4e00\u4e2a\u7095\u4e0a\uff0c\u53c8\u4f3c\u4e4e\u542c\u5f97\u4e00\u4e2a\u8001\u5e74\u4eba\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6551\u4eba\u8981\u7d27\uff0c\u5bb3\u4ec0\u4e48\u81ca\uff0c\u5582\u4ed6\u5403 \u5427\uff01\u201d\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4fbf\u4f3c\u4e4e\u89c9\u5f97\u6709\u6d41\u8d28\u7684\u4e1c\u897f\u8fdb\u53e3\u3002\uff5e \u3000\u3000\u6c5f\u6d77\u5929\u6709\u4e86\u98df\u7269\u8fdb\u53e3\uff0c\u6e10\u6e10\u6062\u590d\u4e86\u4e00\u70b9\u7cbe\u795e\uff0c\u7741\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u4e00\u4e2a\u7ea2\u5149\u6ee1\u9762\u7684\u8001\u5934\u5750\u5728 \u4ed6\u7684\u5bf9\u9762\uff0c\u9762\u524d\u662f\u4e00\u5f20\u5706\u77f3\u684c\u5b50\uff0c\u684c\u5315\u6709\u4e00\u9505\u70ed\u817e\u817e\u7684\u767d\u7ca5\uff0c\u8fd8\u6709\u51e0\u5f0f\u5c0f\u83dc\uff0c\u90fd\u662f\u8089\u7cdc\u3001\u8c46 \u8150\u3001\u9e21\u86cb\u4e4b\u7c7b\u6613\u4e8e\u541e\u54bd\u7684\u4e1c\u897f\uff0c\u4ed6\u8eab\u65c1\u4e00\u4e2a\u5c11\u5973\uff0c\u6b63\u5728\u7528\u957f\u67c4\u5319\u7fb9\uff0c\u6258\u7740\u4ed6\u7684\u4e0b\u5df4\uff0c\u8200\u8d77\u7ca5 \u83dc\u5582\u4ed6\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u795e\u667a\u5df2\u6709\u70b9\u6e05\u9192\uff0c\u5fc3\u91cc\u4e0d\u89c9\u5947\u602a\u8d77\u6765\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5bb6\u662f\u4ec0\u4e48\u4eba\u5bb6\uff0c\u600e\u7684\u4ed6\u4eec\u7adf\u4f3c \u9884\u77e5\u6211\u4f1a\u6765\u5230\u4f3c\u7684\uff0c\u5c4b\u524d\u6709\u706f\u7b3c\u5f15\u8def\uff0c\u53c8\u9884\u5907\u4e86\u4e00\u9505\u70ed\u7ca5\u548c\u8fd9\u4e9b\u9002\u5408\u997f\u6655\u4e86\u7684\u4eba\u5403\u7684\u83b1\u80b4\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u597d\u4e86\uff0c\u9192\u8fc7\u6765\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u6323\u624e\u7740\u534a\u8eba\u534a\u5750\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\uff0c\u6211\u81ea \u5df1\u4f1a\u5403\u4e1c\u897f\u4e86\u3002\u201d\u63a5\u8fc7\u5319\u7fb9\uff0c\u5c06\u4e00\u9505\u70ed\u7ca5\u548c\u51e0\u5f0f\u83b1\u80b4\u5403\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u5c11\u5973\u5728\u65c1\u8fb9\u770b\u4ed6\u8fd9\u526f\u72fc \u541e\u864e\u54bd\u7684\u6a21\u6837\uff0c\u5fcd\u4e0d\u4f4f\u63a9\u53e3\u5077\u7b11\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9762\u4e0a\u4e00\u7ea2\uff0c\u5c34\u5c2c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u8001\u4e08\u548c\u59d1\u5a18\u6551\u547d\u4e4b\u6069\uff0c\u6211\u5f53\u771f\u662f\u997f\u5f97\u614c\u4e86\u3002\u8bf7\u95ee\u8001 \u4e08\u9ad8\u59d3\u5927\u540d\uff0c\u4f55\u4ee5\u7adf\u4f3c\u9884\u5148\u77e5\u9053\u5c0f\u53ef\u4f1a\u95ef\u5230\u8d35\u5e9c\uff1f\u201d \u3000\u3000\u90a3\u8001\u5934\u5b50\u9053\uff1a\u201c\u8001\u673d\u534e\u5929\u98ce\uff0c\u5979\u662f\u5c0f\u5973\u4e91\u78a7\u3002\u8fd9\u5c71\u4e0a\u5e38\u6709\u8ff7\u8def\u7684\u730e\u4eba\uff0c\u6240\u4ee5\u8001\u673d\u6bcf\u5929\u665a \u4e0a\u90fd\u5728\u5c4b\u5916\u6302\u4e2a\u706f\u7b3c\uff0c\u597d\u8ba9\u4ed6\u4eec\u524d\u6765\u6295\u5bbf\uff0c\u4e5f\u7b97\u662f\u884c\u70b9\u597d\u4e8b\u3002\u201d\u8fd9\u8bdd\u53ea\u80fd\u7b97\u662f\u7b54\u590d\u4e86\u4e00\u534a\uff0c \u5c1a\u672a\u89e3\u91ca\u4f55\u4ee5\u4f1a\u9884\u5907\u6709\u90a3\u4e9b\u98df\u7269\u7684\u539f\u56e0\uff0c\u6c5f\u6d77\u5929\u534a\u4fe1\u534a\u7591\uff0c\u6b63\u5f85\u518d\u95ee\uff0c\u90a3\u8001\u4eba\u5df2\u8d70\u8fc7\u6765\u8bf4 \u9053\uff1a\u201c\u76f8\u516c\uff0c\u4f60\u75b2\u52b3\u8fc7\u751a\uff0c\u65e9\u70b9\u5b89\u6b47\uff0c\u6709\u8bdd\u660e\u5929\u518d\u8bf4\u5427\uff01\u201d\u8fd9\u8001\u4eba\u597d\u4f3c\u5f53\u4ed6\u662f\u5b69\u5b50\u4f3c\u7684\uff0c\u8f7b \u8f7b\u7684\u629a\u62cd\u4ed6\uff0c\u6c5f\u6d77\u5929\u4f46\u89c9\u4ed6\u7684\u76ee\u5149\u4e00\u7247\u67d4\u548c\uff0c\u5728\u4ed6\u62cd\u629a\u4e4b\u4e0b\uff0c\u6d51\u8eab\u8212\u7545\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5c31\u5165\u4e86\u68a6 \u4e61\u3002\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u6c5f\u6d77\u5929\u5ffd\u88ab\u5639\u4eae\u7684\u9e1f\u53eb\u60ca\u9192\uff0c\u53ea\u89c1\u5df2\u662f\u7ea2\u65e5\u6ee1\u7a97\uff0c\u7a97\u524d\u4e00\u68f5\u6811\u4e0a\uff0c\u6b63 \u4f0f\u7740\u6628\u65e5\u6240\u89c1\u7684\u90a3\u5934\u5140\u9e70\uff0c\u4f38\u51fa\u957f\u9888\uff0c\u7adf\u50cf\u662f\u7aa5\u63a2\u4ed6\u4f3c\u7684\uff1a\u6c5f\u6d77\u5929\u6d3b\u52a8\u4e86\u4e00\u4e0b\u624b\u8db3\uff0c\u5b9b\u5982\u505a \u4e86\u4e00\u4e2a\u68a6\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6562\u60c5\u8fd9\u5934\u5140\u9e70\u5c31\u662f\u8fd9\u534e\u8001\u4e08\u517b\u7684\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53c8\u542c\u5f97\u6709\u4e2a\u7c97\u8c6a\u7684\u58f0\u97f3\u7b11\u9053\uff1a\u201c\u4f9d\u6b64\u770b\u6765\uff0c\u8fd9\u5c0f\u5b50\u6b66\u529f\u867d\u9ad8\uff0c\u5374\u662f\u4e2a\u6beb\u9605\u5386 \u7684\u96cf\u513f\uff01\u201d\u6c5f\u6d77\u5929\u5413\u4e86\u4e00\u8df3\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u53c8\u662f\u4e00\u4e2a\u9677\u9631\uff1f\u201d\u4f46\u968f\u5373\u60f3\u9053\uff1a\u201c\u8981\u662f\u4ed6\u4eec\u84c4\u610f \u5bb3\u6211\uff0c\u6628\u665a\u53c8\u4f55\u5fc5\u6551\u6211\uff1f\u201d\u4ed6\u8bd5\u4e00\u8bd5\u5410\u7eb3\u7684\u529f\u592b\uff0c\u53ea\u89c9\u6c14\u673a\u901a\u7545\uff0c\u6240\u4e2d\u7684\u6bd2\u7adf\u4f3c\u4e5f\u90fd\u53bb\u5e72\u51c0 \u4e86\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u4e2a\u58f0\u97f3\u53c8\u9053\uff1a\u201c\u534e\u8001\u524d\u8f88\uff0c\u5e78\u4e8f\u4f60\u517b\u6709\u8fd9\u5934\u795e\u9e70\uff0c\u6551\u4e86\u8fd9\u4e2a\u5c0f\u5b50\u3002\u4f46\u5374\u96be\u514d\u8981 \u7ed3\u4e86\u51e0\u4e2a\u4ec7\u5bb6\u4e86\u3002\u55ef\uff0c\u8fd9\u90fd\u662f\u6211\u7ed9\u4f60\u60f9\u6765\u7684\u9ebb\u70e6\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5750\u8d77\u6765\u4ece\u7a97\u53e3\u671b\u51fa\u53bb\uff0c\u53ea\u89c1\u4ed6\u4eec\u5c31\u5728\u9662\u4e8e\u91cc\uff0c\u8bf4\u8bdd\u7684\u662f\u4e2a\u4e2d\u5e74\u6c49\u5b50\uff0c\u8eab\u4e0a\u7684\u8863\u670d \u4e94\u989c\u516d\u8272\uff0c\u539f\u6765\u662f\u8bb8\u591a\u4e0d\u540c\u989c\u8272\u7684\u788e\u5e03\u62fc\u51d1\u6210\u7684\uff0c\u770b\u90a3\u6a21\u6837\uff0c\u4f3c\u662f\u4e00\u4e2a\u53eb\u5316\u3002\u6c5f\u6d77\u5929\u66f4\u4e3a\u7eb3 \u7f55\u3002\u5fc3\u60f3\uff1a\u201c\u600e\u7684\u53c8\u591a\u51fa\u4e00\u4e2a\u53eb\u5316\u5b50\u6765\u4e86\uff1f\u6211\u4e0e\u4ed6\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f46\u542c\u4ed6\u7684\u53e3\u6c14\uff0c\u5374\u662f\u4ed6\u8bf7\u6258\u8fd9 \u534e\u8001\u5934\u6765\u6551\u6211\u7684\uff0c\u8fd9\u91cc\u9762\u5230\u5e95\u6709\u751a\u56e0\u7531\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u81ea\u601d\u7591\uff0c\u5df2\u542c\u5f97\u90a3\u534e\u8001\u5934\u8bf4\u9053\uff1a\u201c\u4ef2\u8001\u5f1f\uff0c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u4e5f\u9887\u6709\u610f\u601d\u4e0e\u91d1\u9e70\u5bab \u7684\u4e3b\u4eba\u4e00\u4f1a\uff0c\u53cd\u6b63\u8981\u4e0e\u4ed6\u4eec\u7ed3\u4ec7\u7684\uff0c\u53ea\u662f\u8fdf\u65e9\u800c\u5df2\u3002\u4f60\u610f\u4e0b\u5982\u4f55\u3002\u53ef\u60f3\u53bb\u51d1\u4e2a\u70ed\u95f9\u4e48\uff1f\u201d\u90a3 \u4e2d\u5e74\u53eb\u5316\u9053\uff1a\u201c\u6211\u4e0e\u7ffc\u5e2e\u4e3b\u7ea6\u597d\u4e86\u4e03\u5915\u4e4b\u671f\uff0c\u5728\u767e\u7075\u5e99\u76f8\u4f1a\uff0c\u4e2d\u79cb\u8282\u91d1\u9e70\u5bab\u7684\u76db\u5bb4\uff0c\u80fd\u5426\u8d76 \u4e0a\uff0c\u5c1a\u672a\u53ef\u77e5\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u91d1\u9e70\u5bab\u7684\u4e3b\u4eba\u662f\u4f55\u7b49\u6837\u4eba\u7269\uff0c\u8fc4\u4eca\u6211\u5c1a\u6beb\u65e0\u6240\u77e5\uff1b\u542c\u8fd9\u4f4d\u534e\u8001\u524d\u8f88\u7684\u53e3 \u6c14\uff0c\u60f3\u5fc5\u4ed6\u662f\u77e5\u9053\u5e95\u7ec6\u7684\u4e86\u3002\u201d\u53c8\u60f3\u9053\uff0c\u201c\u8fd9\u5316\u5b50\u6240\u8bf4\u7684\u7ffc\u5e2e\u4e3b\uff0c\u5f53\u662f\u6307\u5357\u4e10\u5e2e\u7684\u5e2e\u4e3b\u7ffc\u4ef2 \u725f\uff0c\u8fd9\u4e48\u8bf4\u6765\uff0c\u60f3\u5fc5\u4ed6\u4e5f\u662f\u4e10\u5e2e\u4e2d\u4eba\u3002\u201d \u3000\u3000\u679c\u7136\u4fbf\u542c\u5f97\u534e\u8001\u5934\u8bf4\u9053\uff0c\u201c\u4ef2\u8001\u5f1f\uff0c\u795d\u4f60\u4eec\u4f1a\u8c08\u987a\u5229\u3002\u8981\u662f\u4f60\u4eec\u5357\u5317\u4e24\u4e10\u5e2e\u8054\u5408\u8d77\u6765\uff0c \u5929\u9b54\u6559\u51b3\u4e0d\u6562\u8086\u65e0\u5fcc\u60ee\u3002\u6c5f\u6e56\u4e0a\u7684\u5404\u5927\u5e2e\u6d3e\uff0c\u4e5f\u5c06\u552f\u4f60\u4eec\u7684\u9a6c\u9996\u662f\u77bb\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u542c\u5230\u8fd9 \u91cc\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u731b\u7136\u7701\u8d77\uff1a\u201c\u6562\u60c5\u8fd9\u4e2a\u4e2d\u5e74\u5316\u5b50\u5c31\u662f\u5317\u4e10\u5e2e\u7684\u5e2e\u4e3b\u4ef2\u957f\u7edf\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u66fe\u542c\u5e08\u7236\u8bf4\u8fc7\uff0c\u8fd9\u4ef2\u957f\u7edf\u4e43\u662f\u4e10\u5e2e\u4e2d\u4e00\u4e2a\u6770\u51fa\u7684\u4eba\u7269\uff0c\u4ed6\u4e0d\u5230\u4e09\u5341\u5c81\u4fbf\u63a5\u4efb\u4e86\u5317 \u4e10\u5e2e\u7684\u5e2e\u4e3b\u4e4b\u4f4d\uff0c\u4e0d\u8fc7\u51e0\u5e74\uff0c\u4fbf\u5c06\u5e2e\u4e2d\u4e8b\u52a1\uff0c\u6574\u987f\u5f97\u4e95\u4e95\u6709\u6761\u3002\u5317\u4e10\u5e2e\u672c\u6765\u4e45\u5df2\u8870\u5fae\uff0c\u5230\u4e86 \u4ed6\u624d\u59cb\u96c4\u98ce\u91cd\u632f\u3002\u8bba\u5e74\u9f84\uff0c\u7ffc\u4ef2\u725f\u6bd4\u4ed6\u5927\u5f97\u591a\uff0c\u4f46\u82e5\u8bba\u624d\u80fd\u4e0e\u6b66\u529f\uff0c\u5219\u53ea\u6015\u7ffc\u4ef2\u725f\u8fd8\u8981\u900a\u4ed6 \u4e09\u5206\u3002\u6c5f\u6d77\u5929\u5f88\u5c11\u542c\u8fc7\u5e08\u7236\u79f0\u8d5e\u522b\u4eba\uff0c\u56e0\u6b64\u65f6\u8fd9\u5317\u4e10\u5e2e\u5e2e\u4e3b\u4ef2\u957f\u7edf\u7684\u540d\u5b57\u7262\u7262\u8bb0\u5f97\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u60f3\uff1a\u201c\u534e\u8001\u524d\u8f88\u79f0\u4ed6\u4f5c\u4ef2\u8001\u5f1f\uff0c\u800c\u4ed6\u53c8\u8981\u4e0e\u7ffc\u4ef2\u725f\u4f1a\u8c08\u5357\u5317\u4e24\u4e10\u5e2e\u8054\u5408\u4e4b\u4e8b\uff0c \u8fd9\u5b9a\u7136\u662f\u5317\u4e10\u5e2e\u7684\u5e2e\u4e3b\u4ef2\u957f\u7edf\u4e86\u3002\u201d\u5fc3\u4e2d\u4e00\u559c\uff0c\u4fbf\u60f3\u51fa\u53bb\u4e0e\u4ed6\u76f8\u4f1a\u3002\u4f46\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9ad3\u53c8\u60f3 \u9053\uff1a\u201c\u4ed6\u4eec\u6b63\u5728\u5546\u8c08\u5927\u4e8b\uff0c\u4e5f\u8bb8\u4e0d\u9ad8\u5174\u522b\u4eba\u6253\u6270\uff1f\u518d\u8005\uff0c\u8981\u662f\u6211\u9519\u8ba4\u4e86\u4eba\uff0c\u5c82\u975e\u4e0d\u597d\u610f \u601d\uff1f\u201d\u4ed6\u8bb0\u8d77\u4e86\u6bcd\u4eb2\u548c\u4ed6\u6240\u8bf4\u7684\u90a3\u4e9b\u6c5f\u6e56\u7981\u5fcc\uff0c\u7ec8\u4e8e\u51b3\u5b9a\u4e86\u8fd8\u662f\u6682\u4e0d\u51fa\u53bb\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u534e\u8001\u5934\u53c8\u9053\uff1a\u201c\u8bf4\u8d77\u6765\u3001\u6211\u8fd8\u6b20\u7ffc\u4ef2\u725f\u4e00\u7b14\u4eba\u60c5\u5462\uff0c\u4f60\u89c1\u4e86\u4ed6\uff0c\u8bf7\u4ee3\u6211\u5411\u4ed6\u9053 \u8c22\u3002\u201d\u90a3\u513f\u5b50\u7b11\u9053\uff0c\u201c\u4f60\u6b20\u4ed6\u7684\u4eba\u60c5\u5df2\u8fd8\u6e05\u4e86\uff0c\u4f60\u5c1a\u672a\u77e5\u9053\u4e48\uff1f\u5012\u662f\u4ed6\u8981\u5411\u4f60\u9053\u8c22\u5462\uff01\u201d\u90a3 \u534e\u8001\u5934\u8be7\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u90a3\u5316\u5b50\u9053\uff0c\u201c\u8fd9\u4f4d\u6c5f\u5c0f\u4fa0\u662f\u91d1\u4e16\u9057\u7684\u5f92\u5f1f\uff0c\u4ed6\u8fd9\u6b21\u662f\u4e3a\u4e86\u9099\u5c71\u6d3e\u7684\u4e8b\u60c5\u5317\u4e0a\u7684\u3002\u7ffc\u4ef2\u725f \u662f\u9099\u5c71\u6d3e\u638c\u95e8\u8c37\u4e4b\u534e\u8c37\u5973\u4fa0\u7684\u5e08\u5144\uff0c\u6545\u6b64\uff0c\u5728\u8fd9\u4f4d\u6c5f\u5c0f\u4fa0\u52a8\u8eab\u5317\u4e0a\u7684\u65f6\u5019\uff0c\u7ffc\u5e2e\u4e3b\u65e9\u5df2\u7528\u98de \u9e3d\u4f20\u4e66\u3002\u8bf7\u6211\u4eec\u5317\u4e10\u5e2e\u5bf9\u4ed6\u6cbf\u9014\u7167\u987e\u4e86\u3002\u73b0\u5728\u4f60\u6551\u4e86\u4ed6\uff0c\u8fd9\u4e2a\u4eba\u60c5\u53ef\u4e0d\u5c0f\u5440\uff01\u201d\u534e\u8001\u5934\u54c8\u54c8 \u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u602a\u4e0d\u5f97\u4f60\u7684\u6d88\u606f\u8fd9\u6837\u7075\u901a\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8fd9\u65f6\u4e5f\u624d\u604d\u7136\u5927\u609f\uff0c\u5fc3\u91cc\u4e5f\u5728\u60f3\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u96be\u602a\u6211\u4e0e\u4ed6\u4eec\u7d20\u6627\u5e73\u751f\uff0c\u4ed6\u4eec \u5374\u4e00\u76f4\u5728\u6697\u4e2d\u4fdd\u62a4\u6211\u3002\u5374\u539f\u6765\u662f\u8c37\u5973\u4fa0\u4e0e\u7ffc\u5e2e\u4e3b\u8f97\u8f6c\u76f8\u6258\u7684\u3002\u201d \u3000\u3000\u52ff\u542c\u5f97\u4f69\u73af\u58f0\u54cd\uff0c\u534e\u4e91\u78a7\u8d70\u51fa\u6765\u9053\uff1a\u201c\u4ef2\u53d4\u53d4\uff0c\u4f60\u4e0d\u548c\u6211\u7684\u7239\u7239\u4e0b\u68cb\u4e48\uff1f\u6211\u66ff\u4f60\u6ce1\u4e86\u4e00 \u58f6\u4e0a\u597d\u7684\u4e91\u96fe\u8336\uff0c\u7b49\u4f1a\u513f\u518d\u7ed9\u4f60\u5f04\u51e0\u5f0f\u7cbe\u81f4\u7684\u5c0f\u83b1\u3002\u201d\u90a3\u53eb\u5316\u4e86\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4f60\u7239\u7239\u7684\u5bf9 \u624b\uff0c\u8fd9\u68cb\u561b\u4e0d\u4e0b\u4e5f\u7f62\u3002\u5012\u662f\u4f60\u7684\u5c0f\u83dc\uff0c\u5f15\u5f97\u6211\u6d41\u6d8e\u4e86\u3002\u597d\u4f84\u5973\uff0c\u6211\u6bcf\u6b21\u5230\u6765\u90fd\u53e8\u6270\u4f60\u7684\uff0c\u6211 \u5316\u5b50\u4e1c\u8ba8\u897f\u4e5e\uff0c\u53c8\u8ba8\u4e0d\u5230\u4ec0\u4e48\u597d\u4e1c\u897f\u9001\u7ed9\u4f60\uff0c\u771f\u662f\u8fc7\u610f\u4e0d\u53bb\uff0c\u201d\u534e\u4e91\u78a7\u7b11\u9053\uff1a\u201c\u4ef2\u53d4\u53d4\uff0c\u4f60 \u771f\u8981\u9001\u6211\u4e1c\u897f\uff1f\u597d\u5440\uff0c\u90a3\u4e48\u4f60\u5c06\u6df7\u5143\u4e00\u6c14\u529f\u6559\u7ed9\u6211\u5427\uff01\u201d\u90a3\u53eb\u5316\u5b50\u7b11\u9053\uff1a\u201c\u4f60\u7239\u7239\u7edd\u4e16\u795e \u529f\uff0c\u4f60\u54ea\u7528\u8981\u53eb\u5316\u5b50\u8fd9\u70b9\u73a9\u610f\u513f\uff1f\u55ef\uff0c\u6211\u5012\u662f\u60f3\u8d77\u6765\u4e86\uff0c\u9001\u4f60\uff0c\u4e00\u6837\u6bd4\u6df7\u5143\u4e00\u6c14\u529f\u5f3a\u8fc7\u4e07\u500d \u7684\u793c\u7269\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u542c\u5f97\u8bf4\u5f97\u90d1\u91cd\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u90a3\u662f\u4ec0\u4e48\u9738\u9053\u7684\u6b66\u529f\u5440\uff1f\u201d\u53eb\u5316\u5b50\u9053\uff1a\u201c\u6211\u7ed9\u4f60\u6311 \u4e00\u4f4d\u597d\u5973\u5a7f\uff0c\u53eb\u4f60\u4e00\u751f\u53d7\u7528\u4e0d\u5c3d\uff0c\u8fd9\u5c82\u4e0d\u6bd4\u4efb\u4f55\u6b66\u529f\u90fd\u5b9d\u8d35\u4e48\uff1f\u201d\u534e\u4e91\u78a7\u7f9e\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u5550 \u4e86\u4e00\u53e3\u9053\uff1a\u201c\u6211\u548c\u4f60\u8bf4\u6b63\u7ecf\u7684\uff0c\u4f60\u5012\u62ff\u6211\u5f00\u8d77\u73a9\u7b11\u6765\u4e86\u3002\u597d\uff0c\u6211\u4e0d\u505a\u83dc\u7ed9\u4f60\u5403\u4e86\u3002\u201d\u4f46\u5979\u8fd8 \u662f\u628a\u90a3\u58f6\u8336\u653e\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u5316\u5b50\u559d\u4e86\u4e00\u53e3\uff0c\u8d5e\u9053\uff0c\u201c\u597d\uff0c\u771f\u662f\u597d\u8336\u3002\u5c31\u8fd9\u4e00\u58f6\u4e48\uff1f\u201d\u534e\u4e91\u78a7\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u8fd8 \u4e0d\u591f\uff1f\u597d\u8336\u662f\u8be5\u6162\u6162\u54c1\u5c1d\u7684\uff0c\u4f60\u6574\u58f6\u559d\u4e0b\u53bb\uff0c\u90a3\u5c31\u53d8\u6210\u725b\u996e\u4e86\u3002\u201d\u90a3\u5316\u4e8e\u9053\uff1a\u201c\u597d\u4f84\u5973\uff0c\u4f60 \u522b\u7ed5\u7740\u5f2f\u513f\u9a82\u6211\u3002\u6211\u8fd9\u56de\u8bf4\u7684\u771f\u662f\u6b63\u7ecf\u7684\u3002\u4f60\u8be5\u9001\u4e00\u58f6\u8336\u7ed9\u4f60\u7684\u5ba2\u4eba\uff0c\u8fd9\u4e2a\u65f6\u5019\u4ed6\u5927\u7ea6\u4e5f\u8be5 \u9192\u4e86\u3002\u201d\u534e\u4e91\u78a7\u9762\u7ea2\u8fc7\u8033\uff0c\u4f46\u53c8\u4e0d\u597d\u9a82\u4ed6\u3002 \u3000\u3000\u534e\u5929\u98ce\u54b3\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u963f\u4e91\uff0c\u4f60\u5c31\u53bb\u770b\u770b\u6c5f\u76f8\u516c\u5427\uff0c\u201d\u534e\u4e91\u78a7\u66f4\u4e0d\u597d\u610f\u601d\uff0c\u8bf4\u9053\uff1a \u201c\u7b49\u4f1a\u513f\u518d\u53bb\u5427\u3002\u8ba9\u4ed6\u591a\u7761\u4e00\u4f1a\u513f\u3002\u201d\u90a3\u53eb\u5316\u5b50\u70b9\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0c\u597d\u4f53\u8d34\u3002\u54b1\u4eec\u7537\u5b50\u6c49\u5230\u5e95 \u6ca1\u6709\u5973\u5b69\u513f\u5bb6\u8fd9\u4e48\u7ec6\u5fc3\u3002\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u90a3\u4e5f\u597d\uff0c\u4f60\u518d\u53bb\u6ce1\u591a\u4e24\u58f6\u8336\u3002\u201d\u534e\u4e91\u78a7\u4ee5\u4e3a\u7236\u4eb2\u4e5f \u5728\u548c\u5979\u5f00\u73a9\u7b11\uff0c\u77aa\u8d77\u4e86\u773c\u775b\uff0c\u561f\u8d77\u4e86\u5c0f\u5634\u3002\u534e\u5929\u98ce\u9053\uff0c\u201c\u7b49\u4f1a\u513f\u53ea\u6015\u8fd8\u6709\u4e0d\u901f\u4e4b\u5ba2\u4f1a\u6765\u3002\u201d \u534e\u4e91\u78a7\u8fd9\u624d\u77e5\u9053\u7236\u4eb2\u4e0d\u662f\u5f00\u73a9\u7b11\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u7b49\u4f1a\u5c31\u6709\u70ed\u95f9\u770b\u4e86\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u8d70\u5f00\u4e4b\u540e\uff0c\u90a3\u53eb\u5316\u5b50\u6c89\u541f\u4e86\u534a\u664c\uff0c\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u77e5\u9053\u4f60\u4f4f\u5728\u8fd9\u91cc\u4e48\uff1f\u201d\u534e\u65e0\u98ce \u9053\uff1a\u201c\u5927\u7ea6\u8fd8\u672a\u77e5\u9053\u3002\u4e0d\u8fc7\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u4e43\u662f\u901a\u7075\u7684\u5f02\u517d\uff0c\u5b83\u4eec\u5403\u4e86\u4e8f\uff0c\u5f53\u7136\u4f1a\u5c06\u4e3b\u4eba\u5f15 \u6765\u3002\u55ef\uff0c\u4f60\u542c\uff0c\u8fd9\u4e0d\u662f\u6709\u4eba\u6765\u4e86\uff1f\u201d\u90a3\u53eb\u5316\u4e86\u51b7\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u6765\u5f97\u597d\u5feb\u5440\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u529f\u529b\u5df2\u7ecf\u6062\u590d\uff0c\u51dd\u795e\u7ec6\u542c\uff0c\u679c\u7136\u542c\u5f97\u6709\u811a\u6b65\u58f0\u8fdc\u8fdc\u4f20\u6765\uff0c\u4f30\u8ba1\u8fd8\u5728\u4e00\u91cc\u591a\u5916\uff0c\u4f46 \u8f6c\u77ac\u4e4b\u95f4\uff0c\u4fbf\u4f3c\u5230\u4e86\u95e8\u524d\uff0c\u811a\u6b65\u58f0\u5ffd\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u968f\u5373\u542c\u5f97\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u4f4e\u6c89\u7684\u543c\u58f0\uff1a\u7247\u523b \u4e4b\u540e\uff0c\u53ea\u542c\u5f97\u90a3\u9634\u8001\u592a\u5a46\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u60f3\u5fc5\u662f\u85cf\u5728\u8fd9\u5c4b\u5b50\u91cc\u4e86\u3002\u201d \u3000\u3000\u8ddf\u7740\u542c\u5f97\u5389\u590d\u751f\u7684\u58f0\u97f3\u7c97\u91cc\u7c97\u6c14\u4ed6\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u8fd8\u7b49\u4ec0\u4e48\uff1f\u201d\u90a3\u9634\u8001\u5927\u5a46\u9053\uff1a\u201c\u65e2\u7136\u627e \u5230\u4e86\u4ed6\u85cf\u8eab\u4e4b\u6240\u3002\u8fd8\u6015\u4ed6\u9003\u5f97\u4e0a\u5929\u4e48\uff0c\u54b1\u4eec\u5148\u8bf4\u597d\u4e86\uff0c\u4eba\u8ba9\u4f60\u5e26\u53bb\uff0c\u5b9d\u5251\u7559\u7ed9\u6211\uff0c\u5b9d\u7532\u9001\u7ed9 \u6b27\u9633\u4e8c\u5a18\u3002\u201d\u6c5f\u6d77\u5929\u6b63\u5728\u60f3\u9053\uff1a\u201c\u8fd9\u6b27\u9633\u4e8c\u5a18\u53c8\u662f\u8c01\u5462\uff1f\u201d\u4fbf\u5373\u542c\u5f97\u4e00\u4e2a\u5987\u4eba\u8bf4\u9053\uff1a\u201c\u6211\u5374 \u4e0d\u7a00\u7f55\u4ec0\u4e48\u5b9d\u7269\uff0c\u6211\u7684\u5a49\u513f\u72af\u4e86\u4f60\u7684\u95e8\u89c4\uff0c\u6211\u5e2e\u4f60\u4eec\u8fd9\u6b21\u5fd9\uff0c\u7b97\u662f\u7ed9\u5a49\u513f\u8d4e\u7f6a\uff0c\u597d\u4e48\uff1f\u201d\u8bed \u6c14\u4e4b\u4e2d\u9887\u6709\u51b7\u5632\u7684\u5473\u9053\u3002\u6c5f\u6d77\u5929\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u90fd\u5df2\u8bb2\u548c\u4e86\uff0c\u73b0\u5728\u6b63\u5728\u5408\u8c0b\u5bf9\u4ed8\u6211\u3002\u201d \u3000\u3000\u90a3\u9634\u8001\u592a\u5a46\u9053\uff1a\u201c\u6211\u662f\u6015\u5a49\u513f\u5e74\u8f7b\uff0c\u4e0a\u4e86\u4eba\u5bb6\u7684\u5f53\uff0c\u6240\u4ee5\u4e0d\u5f97\u4e0d\u5413\u5979\u4e00\u4e0b\uff0c\u4f60\u522b\u89c1\u602a\u3002 \u627f\u4f60\u5e2e\u5fd9\uff0c\u5b9d\u7269\u8fd8\u662f\u8981\u9001\u7ed9\u4f60\u7684\u3002\u201d\u5389\u590d\u751f\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u6177\u4ed6\u4eba\u4e4b\u6168\uff01\u201d\u9634 \u8001\u592a\u5a46\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u4e0d\u613f\u610f\u5417\uff1f\u90a3\u6211\u4eec\u5c31\u90fd\u4e0d\u7ba1\uff0c\u770b\u4f60\u80fd\u4e0d\u80fd\u591f\u5728\u4f60\u6559\u4e3b\u9762\u524d\u4ea4\u5f97 \u4e86\u5dee\uff1f\u201d\u5389\u590d\u751f\u4f3c\u662f\u6015\u5f53\u771f\u4ea4\u4e0d\u4e86\u5dee\uff0c\u95f7\u58f0\u4e0d\u554a\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u5934\u706b\u8d77\uff0c\u6697\u81ea\u9a82\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4eec\u5012\u6253\u5f97\u5982\u610f\u7b97\u76d8\uff0c\u5408\u8ba1\u6765\u5206\u6211\u7684\u4e1c\u897f\u4e86\u3002\u201d \u4ed6\u5fcd\u8010\u4e0d\u4f4f\uff0c\u63d0\u4e86\u5b9d\u5251\uff0c\u4fbf\u51fa\u623f\u6765\u3002 \u3000\u3000\u534e\u5929\u98ce\u8fce\u4e0a\u524d\u53bb\uff0c\u95ee\u9053\uff1a\u201c\u6c5f\u5c0f\u4fa0\uff0c\u4f60\u597d\u4e86\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u591a\u8c22\u8001\u4e08\uff0c\u6211\u5168\u597d\u4e86\u3002 \u8fd9\u4e9b\u4eba\u662f\u6765\u627e\u6211\u7684\uff0c\u6211\u4e0d\u60f3\u8fde\u7d2f\u4f60\u4eec\uff0c\u8bf7\u4f60\u4eec\u8ba9\u6211\u51fa\u53bb\u548c\u4ed6\u4eec\u4e00\u62fc\u3002\u201d\u534a\u5929\u98ce\u7b11\u9053\uff1a\u201c\u8fd9\u662f \u4ec0\u4e48\u8bdd\uff0c\u4f60\u5230\u4e86\u6211\u8fd9\u91cc\uff0c\u5c31\u662f\u6211\u7684\u5ba2\u4eba\uff0c\u505a\u4e3b\u4eba\u7684\u54ea\u6709\u8ba9\u5ba2\u4eba\u53bb\u62fc\u547d\u7684\u9053\u7406\uff1f\u201d\u90a3\u53eb\u513f\u5b50\u8d70 \u8fc7\u6765\u62c9\u7740\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6c5f\u8001\u5f1f\uff0c\u4f60\u548c\u6211\u4e00\u9053\u51e0\u77a7\u70ed\u95f9\u5427\u3002\u8981\u662f\u6211\u548c\u534e\u8005\u90fd\u6253\u4e0d\u8fc7\u4eba\u5bb6\uff0c\u90a3\u65f6 \u518d\u8bf7\u4f60\u51fa\u624b\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8fd9\u624d\u60f3\u5230\u81ea\u5df1\u8bf4\u9519\u4e86\u8bdd\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u4f60\u522b\u89c1\u602a\uff0c\u6709\u4e24\u4f4d\u8001\u524d\u8f88\u5728 \u6b64\uff0c\u8981\u5bf9\u4ed8\u8fd9\u51e0\u4e2a\u9b54\u5934\u81ea\u662f\u7ef0\u7ef0\u6709\u4f59\u3002\u6211\u53ea\u4e0d\u8fc7\u662f\u60f3\u4e00\u4eba\u505a\u4e8b\u4e00\u4eba\u5f53\u800c\u5df2\u3002\u201d\u534e\u5929\u51e4\u7b11\u9053\uff1a \u201c\u8fd9\u51e0\u4e2a\u9b54\u5934\u90fd\u662f\u975e\u540c\u5c0f\u53ef\u7684\u4eba\u7269\uff0c\u8981\u4e0d\u662f\u4ef2\u5e2e\u4e3b\u5728\u8fd9\u91cc\uff0c\u6211\u8fd8\u4e0d\u6562\u8bf4\u771f\u6709\u628a\u63e1\u5462\u3002\u201d\u6c5f\u6d77 \u5929\u542c\u4ed6\u8fd9\u6837\u79f0\u547c\uff0c\u77e5\u9053\u4e86\u8fd9\u4e2a\u53eb\u5316\u5b50\u786e\u662f\u5317\u4e10\u5e2e\u7684\u5e2e\u4e3b\u4ef2\u957f\u7edf\u3002 \u3000\u3000\u534e\u5929\u98ce\u8bdd\u72b9\u672a\u4e86\uff0c\u4fbf\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u5389\u590d\u751f\u7c97\u58f0\u53eb\u9053\uff1a\u201c\u91cc\u9762\u6709\u4eba\u5417\uff1f\u4e0d\u6765\u5f00\u95e8\uff0c \u6211\u4eec\u5c31\u8981\u81ea\u884c\u8fdb\u95e8\u4e86\u3002\u201d\u90a3\u4e24\u6247\u677f\u95e8\u7ed9\u4ed6\u62cd\u4e86\u4e00\u638c\uff0c\u767b\u65f6\u88c2\u5f00\u3002\u6c5f\u6d77\u5929\u5b9a\u775b\u671b\u53bb\uff0c\u53ea\u89c1\u95e8\u5916 \u5171\u662f\u4e94\u4e2a\u4eba\u3002\u9664\u4e86\u5389\u590d\u751f\u3001\u670b\u8001\u592a\u5a46\u548c\u6628\u65e5\u6240\u89c1\u7684\u90a3\u5987\u4eba\u4e4b\u5916\uff0c\u8fd8\u6709\u6b27\u9633\u5a49\u59d0\u59b9\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4f0f\u5728\u6811\u4e0a\u7684\u90a3\u5934\u5140\u9e70\uff0c\u5ffd\u5730\u632f\u7ffc\u957f\u9e23\uff0c\u98de\u4e86\u4e0b\u6765\uff0c\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u9886\u6559\u8fc7\u5b83\u7684 \u5389\u5bb3\uff0c\u5413\u5f97\u5939\u4e86\u5c3e\u5df4\u76f4\u8dd1\u3002\u6b27\u9633\u5a49\u7684\u6bcd\u4eb2\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u5927\u7684\u5140\u9e70\u5012\u662f\u5c11\u89c1\uff0c\u4e14\u770b\u6211\u80fd\u4e0d\u80fd\u6536 \u773c\u5b83\uff01\u201d\u90a3\u51e0\u9e70\u6251\u5c06\u4e0b\u6765\uff0c\u6b27\u9633\u4e8c\u5a18\u4e3e\u8d77\u62d0\u4e00\u63a0\uff0c\u6070\u597d\u88ab\u90a3\u5140\u9e70\u6293\u4f4f\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u81ea\u5fc3\u60f3\uff0c\u201c\u5140\u9e70\u8fd9\u4e00\u6293\u6015\u662f\u6709\u5343\u4e07\u65a4\u6c14\u529b\uff0c\u5c82\u662f\u4eba\u529b\u6240\u80fd\u76f8\u6297\uff1f\u201d\u4f46\u8bf4\u4e5f\u5947 \u602a\uff0c\u53ea\u89c1\u90a3\u5140\u9e70\u7684\u7fc5\u8180\u6251\u4e86\u51e0\u4e0b\uff0c\u5374\u5e76\u6ca1\u6709\u51b2\u4e0b\u6765\uff1b\u4f46\u82e5\u8bf4\u5b83\u662f\u632f\u7ffc\u6b32\u98de\u5427\uff0c\u5374\u53c8\u5e76\u6ca1\u6709\u98de \u4e0a\u53bb\u3002\u5c31\u8fd9\u4e48\u4e0d\u4e0a\u4e0d\u4e0b\u7684\uff0c\u5012\u50cf\u662f\u7ed9\u90a3\u6839\u62d0\u6756\u52fe\u4f4f\u4e86\u3002 \u3000\u3000\u5140\u9e70\u717d\u8d77\u7684\u72c2\u98ce\u522e\u5f97\u6c99\u98de\u77f3\u8d70\uff0c\u6b27\u9633\u4e8c\u5a18\u7684\u5934\u53d1\u4e5f\u90fd\u4e71\u4e86\uff0c\u4f46\u5979\u4ecd\u7136\u795e\u8272\u81ea\u5982\uff0c\u751a\u81f3\u811a \u6b65\u4e5f\u672a\u66fe\u79fb\u52a8\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5979\u7d22\u6027\u76d8\u8d77\u53cc\u819d\uff0c\u5750\u4e86\u4e0b\u6765\uff0c\u80cc\u9760\u7740\u4e00\u68f5\u5927\u6811\uff0c\u624b\u6267\u770b\u62d0\u6756\u7684\u4e2d \u95f4\uff0c\u62d0\u6756\u7684\u53e6\u4e00\u7aef\u5219\u7adf\u7136\u6401\u5728\u80a9\u4e0a\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u8d5e\u9053\uff1a\u201c\u8fd9\u5a46\u5a18\u5378\u529b\u8f6c\u52b2\u7684\u529f\u592b\u679c\u7136\u4e86\u5f97\uff01\u201d\u8bdd\u72b9\u6765\u4e86\uff0c\u53ea\u89c1\u90a3\u68f5\u5927\u6811\u4f3c\u53d7\u4e86\u4e00 \u80a1\u65e0\u5f62\u5de8\u529b\u7684\u6447\u64bc\u4e00\u822c\uff0c\u6c99\u6c99\u58f0\u54cd\uff0c\u679d\u53f6\u7eb7\u843d\uff0c\u518d\u8fc7\u4e00\u4f1a\u3002\u8fde\u6811\u5e72\u4e5f\u6447\u52a8\u8d77\u6765\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u5979\u90a3\u6839\u62d0\u6756\u6401\u5728\u80a9\u4e0a\uff0c\u4e00\u7aef\u88ab\u5140\u9e70\u6293\u4f4f\uff0c\u53e6\u4e00\u7aef \u5374\u4e0e\u5927\u6811\u76f8\u89e6\uff0c\u5140\u9e70\u7684\u795e\u529b\u90fd\u88ab\u5979\u8f6c\u79fb\u5230\u5927\u6811\u53bb\u4e86\u3002\u6c5f\u6d77\u5929\u4e5f\u66fe\u8ddf\u5e08\u7236\u5b66\u8fc7\u8fd9\u79cd\u529f\u5927\uff0c\u4f46\u56e0 \u4ed6\u6240\u5b66\u592a\u5e7f\uff0c\u672a\u66fe\u4e13\u7814\uff0c\u800c\u4e14\u8fd9\u79cd\u529f\u592b\u9700\u8981\u65f6\u65e5\uff0c\u624d\u80fd\u719f\u6781\u751f\u5de7\uff0c\u6545\u6b64\u6c5f\u6d77\u5929\u5c1a\u672a\u80fd\u8fd0\u7528\u81ea \u5982\u3002 \u3000\u3000\u534e\u5929\u98ce\u5fae\u7b11\u9053\uff1a\u201c\u5230\u8fbe\u8fd9\u79cd\u5883\u754c\u4e5f\u786e\u5b9e\u4e0d\u9519\u4e86\u3002\u5f53\u4eca\u4e4b\u4e16\uff0c\u80fd\u5728\u8fd9\u95e8\u529f\u592b\u4e0a\u80dc\u8fc7\u5979\u7684\uff0c \u5927\u6982\u4e5f\u53ea\u662f\u6709\u9650\u7684\u4e09\u4e24\u4e2a\u4eba\u800c\u5df2\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u4f3c\u4e4e\u5979\u8fd9\u95e8\u529f\u592b\u4e5f\u8fd8\u672a\u66fe\u7089\u706b\u7eaf\u9752\u3002\u6c5f\u6d77\u5929 \u4ed4\u7ec6\u770b\u65f6\uff0c\u53ea\u89c1\u90a3\u6b27\u9633\u4e8c\u5a18\u7684\u5934\u9876\u4e0a\u7f69\u7740\u4e00\u56e2\u6d53\u96fe\uff0c\u5c31\u50cf\u84b8\u7b3c\u4e00\u822c\uff0c\u70ed\u6c14\u817e\u817e\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u66fe\u542c\u5e08\u7236\u8bf4\u8fc7\uff0c\u82e5\u7136\u5b66\u7684\u6b63\u5b97\u5185\u529f\uff0c\u5230\u4e86\u6700\u9ad8\u6df1\u7684\u5883\u754c\uff0c\u4f7f\u51fa\u201c\u5378\u201d\u5b57\u8bc0\u548c \u201c\u8f6c\u201d\u5b57\u8bc0\uff0c\u53ef\u4ee5\u628a\u5bf9\u65b9\u653b\u6765\u7684\u529b\u9053\u6beb\u4e0d\u8d39\u529b\u7684\u8f6c\u79fb\u5230\u4efb\u4f55\u7269\u4f53\u4e0a\u53bb\uff0c\u4f46\u82e5\u7136\u5b66\u7684\u662f\u90aa\u6d3e\u5185 \u529f\uff0c\u5219\u7eb5\u7136\u5df2\u5230\u4e86\u6700\u9ad8\u7684\u5883\u754c\uff0c\u5728\u4f7f\u7528\u8fd9\u79cd\u5378\u529b\u8f6c\u52b2\u529f\u592b\u65f6\uff0c\u5374\u8fd8\u9700\u8981\u8017\u672c\u8eab\u7684\u771f\u529b\u6765\u7275 \u5f15\uff0c\u4e0d\u8fc7\u529f\u592b\u8d8a\u9ad8\uff0c\u672c\u8eab\u6240\u8017\u7684\u771f\u529b\u5c31\u6108\u5c0f\u800c\u5df2\u3002 \u3000\u3000\u636e\u5e08\u7236\u8bf4\uff0c\u636e\u4e54\u5317\u6e9f\u7684\u6b66\u529f\u79d8\u7c4d\u8bb0\u8f7d\uff0c\u4e54\u5317\u6e9f\u5230\u4e86\u665a\u5e74\uff0c\u4f7f\u7528\u8fd9\u95e8\u529f\u592b\uff0c\u5df2\u53ef\u4ee5\u4e0e\u6b63\u5b97 \u5185\u529f\u4e2d\u201c\u56db\u4e24\u62e8\u5343\u65a4\u201d\u7684\u529f\u592b\u5f02\u66f2\u540c\u5de5\u3002\u4f46\u4ea6\u5373\u662f\u8bf4\uff0c\u4e5f\u8fd8\u9700\u8981\u56db\u4e24\u4e4b\u529b\uff0c\u624d\u80fd\u62e8\u52a8\u5343\u65a4\uff0c \u73b0\u5728\u770b\u6b27\u9633\u4e8c\u5a18\u5403\u529b\u7684\u60c5\u5f62\uff0c\u5219\u663e\u7136\u5979\u5b66\u7684\u662f\u90aa\u6d3e\u5185\u529f\uff0c\u800c\u4e14\u8ddd\u79bb\u4e54\u5317\u6e9f\u6240\u66fe\u5230\u8fbe\u7684\u90a3\u79cd\u5883 \u754c\u4e5f\u8fd8\u8fdc\u751a\u3002\u4e0d\u8fc7\u82e5\u6bd4\u4e4b\u6c5f\u6d77\u5929\u5219\u81ea\u662f\u9ad8\u660e\u5f97\u591a\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u867d\u7136\u5982\u6b64\uff0c\u82e5\u4efb\u795e\u9e70\u4e0e\u5979\u76f8\u6301\u4e0b\u53bb\uff0c\u53ea\u6015\u795e\u9e70\u7ec8\u4f1a\u529b\u7aed\u7b4b\u75b2\uff0c\u6700\u5c11\u4e5f\u8981\u8017 \u4f60\u5fc3\u529b\uff0c\u7ed9\u5b83\u8c03\u6cbb\u4e2a\u5341\u5929\u534a\u6708\u4e86\u3002\u6211\u770b\u54b1\u4eec\u73b0\u5728\u8fd8\u662f\u51fa\u53bb\u5427\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u8f70\u9686\u4e00 \u58f0\uff0c\u90a3\u68f5\u5927\u6811\u7684\u4e0a\u534a\u622a\u5df2\u5012\u4e86\u4e0b\u6765\uff0c\u4e0e\u6b64\u540c\u65f6\uff0c\u6b27\u9633\u4e8c\u5a18\u7adf\u7ed9\u90a3\u5934\u5140\u9e70\u8fde\u4eba\u5e26\u6756\uff0c\u6293\u5f97\u5979\u53cc \u811a\u79bb\u5f00\u5730\u9762\u5c11\u8bb8\u3002 \u3000\u3000\u6b27\u9633\u5a49\u5931\u58f0\u60ca\u547c\uff0c\u9634\u8001\u592a\u5a46\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e8c\u5a18\u4e0d\u5fc5\u8d39\u52b2\u4e86\uff0c\u8fd8\u662f\u8ba9\u6211\u6765\u6253\u53d1\u5b83\u5427\uff01\u201d\u628a \u624b\u4e00\u626c\uff0c\u4e09\u900d\u7d2b\u8272\u5149\u534e\u7535\u5c04\u800c\u51fa\uff0c\u98de\u5411\u90a3\u5934\u5140\u9e70\u3002\u539f\u6765\u5979\u7684\u624b\u5fc3\u91cc\u65e9\u5c31\u6263\u4e86\u4e09\u53e3\u201c\u5316\u8840\u795e \u5200\u201d\uff0c\u4e43\u662f\u7528\u975e\u5e38\u5389\u5bb3\u7684\u6bd2\u836f\u6dec\u70bc\u8fc7\u7684\uff0c\u6bd2\u6027\u8db3\u4ee5\u89c1\u8840\u5c01\u5589\uff01 \u3000\u3000\u773c\u770b\u8fd9\u4e09\u53e3\u98de\u5200\u5c31\u8981\u523a\u4e2d\u5140\u9e70\u3002\u8bf4\u4e5f\u5947\u602a\uff0c\u53bb\u52bf\u7a81\u7136\u7f13\u6162\u4e0b\u6765\uff0c\u7ade\u4f3c\u53d7\u4e86\u4ec0\u4e48\u5916\u529b\u6240 \u963b\uff0c\u505c\u6b62\u4e0d\u52a8\u4e86\u3002\u4f46\u8fd9\u4e0d\u8fc7\u662f\u7247\u523b\u4e4b\u95f4\u7684\u5f62\u52bf\uff0c\u518d\u8fc7\u7247\u523b\uff0c\u90a3\u4e09\u53e3\u98de\u5200\u7adf\u7136\u5728\u7a7a\u4e2d\u6253\u8f6c\uff0c\u4fbf \u4f3c\u5728\u6ce2\u6d6a\u4e2d\u8f7d\u6d6e\u8f7d\u6c89\u4e00\u822c\uff0c\u7ec8\u4e8e\u7f13\u7f13\u964d\u4e0b\u3002\u8fd9\u65f6\u5927\u5bb6\u4ea6\u90fd\u5df2\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5728\u90a3\u4e09\u53e3\u98de\u5200\u7684 \u5200\u53e3\u4e0a\u90fd\u9644\u7740\u4e00\u6735\u7ea2\u82b1\u3002 \u3000\u3000\u9634\u8001\u5927\u5a46\u4e0e\u6b27\u9633\u4e8c\u5a18\u90fd\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u5979\u4eec\u90fd\u662f\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u5f53\u7136\u77e5\u9053\u8fd9\u662f\u7edd\u9876\u7684 \u201c\u98de\u82b1\u6458\u53f6\u201d\u795e\u529f\uff0c\u9634\u8001\u5927\u5a46\u53d1\u51fa\u98de\u5200\u65f6\u662f\u7528\u8db3\u4e86\u5185\u52b2\u7684\uff0c\u6240\u4ee5\u5728\u98de\u5200\u4e0e\u98de\u82b1\u63a5\u89e6\u7684\u90a3\u4e00\u5239 \u90a3\uff0c\u7531\u4e8e\u4e24\u80a1\u5185\u529b\u76f8\u6d88\uff0c\u56e0\u6b64\u98de\u5200\u505c\u987f\u4e00\u4e0b\uff0c\u800c\u6700\u540e\u98de\u5200\u7ec8\u4e8e\u964d\u4e0b\uff0c\u90a3\u5c31\u662f\u8bf4\u660e\u4e86\u963b\u8001\u592a\u5a46 \u7684\u5185\u529b\u6bd4\u4e0d\u4e0a\u98de\u82b1\u78b0\u5200\u7684\u8fd9\u4e2a\u4eba\u3002\u82b1\u6735\u7684\u4efd\u91cf\u8f7b\u5fae\uff0c\u7adf\u7136\u80fd\u628a\u98de\u5200\u6253\u843d\uff0c\u8fd9\u4eba\u7684\u5185\u529f\u4e4b\u5f3a\uff0c \u5b9e\u5df2\u5230\u4e86\u9707\u4e16\u9a87\u4fd7\u7684\u5730\u6b65\uff1b\u6b27\u9633\u4e8c\u5a18\u5fc3\u60f3\uff1a\u201c\u5f53\u4e16\u6700\u64c5\u4e8e\u7528\u98de\u82b1\u6ef4\u53f6\u4f24\u4eba\u7684\u4e43\u662f\u5929\u5c71\u6d3e\u7684\u51af \u7433\uff0c\u800c\u5979\u53c8\u662f\u6700\u6b22\u559c\u7ba1\u95f2\u4e8b\u7684\uff0c\u83ab\u975e\u7adf\u7136\u662f\u5979\u6765\u4e86\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u8bf4\u9053\u3002\u201c\u8bf7\u770b\u5728\u8001\u592b\u7684\u4efd\u4e0a\uff0c\u9976\u4e86\u8fd9\u5934\u755c\u7272\u5427\uff0c\u201d\u767b \u65f6\u5404\u4eba\u7684\u76ee\u5149\u90fd\u5411\u8fd9\u58f0\u97f3\u7684\u6765\u5904\u6295\u53bb\uff0c\u53ea\u89c1\u4e2a\u8eab\u6750\u9ad8\u5927\uff0c\u6ee1\u9762\u7ea2\u5149\u3001\u4e09\u7edc\u957f\u987b\u7684\u8001\u4eba\uff0c\u4e5f\u4e0d \u77e5\u662f\u4ec0\u4e48\u65f6\u5019\u8d70\u51fa\u6765\u7684\u3001\u8fd9\u65f6\u6b63\u501a\u5728\u4e00\u68f5\u6885\u6811\u4e0a\u3002 \u3000\u3000\u6885\u6811\u4e0a\u6709\u679d\u6811\u679d\u8fd8\u98a4\u52a8\u4e0d\u4f11\uff0c\u4e0d\u95ee\u53ef\u77e5\uff0c\u6253\u843d\u9634\u8001\u592a\u5a46\u7684\u98de\u5200\u7684\u5c31\u662f\u8fd9\u8001\u4eba\u4e86\uff0c\u6b27\u9633\u4e8c \u5a18\u5927\u611f\u610f\u5916\uff0c\u4f46\u540c\u65f6\u4e5f\u89c9\u5f97\u201c\u5c1a\u6709\u53ef\u4e3a\u201d\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u5e78\u4e8f\u4e0d\u662f\u51af\u7433\u3002\u201d\u5979\u8fd9\u6837\u60f3 \u6cd5\uff0c\u5e76\u975e\u56e0\u4e3a\u5979\u5df2\u786e\u77e5\u8fd9\u8001\u4eba\u7684\u6b66\u529f\u4e0d\u53ca\u51af\u7433\uff0c\u800c\u662f\u56e0\u4e3a\u5728\u51af\u7433\u7684\u80cc\u540e\u6709\u5929\u5c71\u6d3e\u6491\u8170\uff0c\u8fd9\u65e0 \u540d\u8001\u4eba\u6216\u8bb8\u6bd4\u51af\u7433\u66f4\u5f3a\uff0c\u4f46\u4ed6\u5b64\u8eab\u4e00\u4eba\uff0c\u6b27\u9633\u4e8c\u5a18\u4f30\u8ba1\u5408\u9634\u8001\u592a\u5a46\u4e0e\u5389\u590d\u751f\u4e4b\u529b\uff0c\u6700\u5c11\u53ef\u4ee5 \u4e0e\u4ed6\u6253\u4e2a\u5e73\u624b\u3002\u4f46\u867d\u7136\u5982\u6b64\uff0c\u5fc3\u4e2d\u4ecd\u4e0d\u7981\u60f4\u60f4\u4e0d\u5b89\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u4e00\u4e2a\u5206\u795e\uff0c\u53c8\u7ed9\u90a3\u5934\u5140\u9e70\u5e26\u7740\u62d0\u6756\u62d6\u5f97\u5411\u524d\u8d70\u4e86\u51e0\u6b65\u3002\u90a3\u8005\u5934\u513f\u8fd9\u65f6\u624d\u7f13\u7f13\u8d70 \u51fa\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u755c\u7272\u4e0d\u53ef\u65e0\u793c\uff01\u201d\u6325\u8896\u4e00\u62c2\uff0c\u8ddd\u79bb\u5c1a\u6709\u5341\u6765\u6b65\u8fdc\uff1a\u6b27\u9633\u4e8c\u5a18\u5df2\u611f\u5230\u4e00\u80a1\u65e0 \u5f62\u7684\u6f5c\u529b\u5728\u5979\u7684\u62d0\u6756\u4e0a\u4e00\u6258\uff0c\u90a3\u5934\u5140\u9e70\u5f97\u8001\u4eba\u89e3\u5f00\u4e86\u6b27\u9633\u4e8c\u5a18\u90a3\u80a1\u7275\u5f15\u4e4b\u529b\uff0c\u4e5f\u4fbf\u7acb\u5373\u632f\u7ffc \u9ad8\u98de\uff0c\u957f\u9e23\u51e0\u58f0\uff0c\u98de\u8fc7\u5c71\u5934\u53bb\u4e86\u3002\u6b27\u9633\u4e8c\u5a18\u6697\u6697\u5403\u60ca\uff0c\u4f46\u5979\u6697\u81ea\u53eb\u4e86\u4e00\u58f0\uff0c\u201c\u4fa5\u5e78\u3002\u201d\u56e0\u4e3a \u82e5\u4e0d\u662f\u5f97\u8fd9\u8001\u4eba\u89e3\u5f00\uff0c\u5979\u548c\u5140\u9e70\u53ea\u6015\u90fd\u8981\u7d2f\u5f97\u7b4b\u75b2\u529b\u7aed\uff0c\u4e24\u8d25\u4ff1\u4f24\u3002 \u3000\u3000\u9634\u8001\u592a\u5a46\u88ab\u534e\u5929\u98ce\u6253\u843d\u4e86\u5979\u7684\u98de\u5200\uff0c\u9887\u4e3a\u7740\u607c\uff0c\u8fc8\u6b65\u4e0a\u524d\uff0c\u5927\u523a\u523a\u5730\u95ee\u9053\uff0c\u201c\u4f60\u662f\u8c01\uff1f \u8fd9\u53ea\u6241\u6bdb\u755c\u7272\u662f\u4f60\u517b\u7684\u4e48\uff1f\u201d \u3000\u3000\u5ffd\u542c\u4e00\u9635\u54c8\u54c8\u5927\u7b11\uff0c\u4e00\u4e2a\u53eb\u5316\u5b50\u8d70\u4e86\u51fa\u6765\uff0c\u9634\u8001\u592a\u5a46\u7709\u5934\u4e00\u76b1\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u4ef2\u5e2e \u4e3b\uff0c\u4f60\u4e5f\u5728\u8fd9\u513f\uff01\u201d \u3000\u3000\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u8fd8\u672a\u76f8\u8bc6\uff0c\u4e14\u5f85\u6211\u5148\u4f5c\u2018\u66f9\u90b1\u2019\uff08\u4ecb\u7ecd\u4eba\u4e4b\u610f\uff09\uff0c\u8fd9\u4f4d\u662f\u534e\u5c71 \u533b\u9690\u534e\u5929\u98ce\u8001\u524d\u8f88\uff0c\u4e5f\u5c31\u662f\u8fd9\u91cc\u7684\u4e3b\u4eba\u3002\u201d\u8ddf\u7740\u4f9d\u6b21\u4ecb\u7ecd\u5ba2\u4eba\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u4e03\u9634\u6559\u7684\u9634\u5723\u59d1 \u9634\u8001\u524d\u8f88\uff0c\u8fd9\u4f4d\u662f\u7ec8\u5357\u5c71\u7684\u6b27\u9633\u4e8c\u5a18\uff0c\u8fd9\u4f4d\u662f\u5929\u9b54\u6559\u7684\u5389\u526f\u6559\u4e3b\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u672a\u66fe\u542c\u8fc7\u534e\u5929\u98ce\u7684\u540d\u5b57\u3002\u867d\u7136\u5df2\u77e5\u9053\u4ed6\u6b66\u529f\u9ad8\u5f3a\uff0c\u8fd8\u672a\u81f3\u4e8e\u6050\u60e7\u3002\u6b27\u9633\u4e8c\u5a18\u542c\u4e86 \u5374\u662f\u5185\u5fc3\u6697\u60ca\u3002\u539f\u6765\u5979\u7684\u4e08\u592b\u6b27\u9633\u4ef2\u548c\u6709\u4e00\u6b21\u5728\u534e\u5c71\u91c7\u836f\uff0c\u65e0\u610f\u4e2d\u95ef\u5165\u534e\u5929\u98ce\u7684\u836f\u5703\u3001\u53d1\u73b0 \u534e\u5929\u98ce\u81ea\u79cd\u7684\u8bb8\u591a\u5947\u836f\uff0c\u4fbf\u6b32\u76d7\u53d6\uff0c\u5374\u88ab\u534e\u5929\u98ce\u61c2\u89c1\uff0c\u4e00\u8a00\u4e0d\u5408\uff0c\u52a8\u8d77\u624b\u6765\uff0c\u672a\u5230\u767e\u62db\uff0c\u4fbf \u8d25\u5728\u534e\u5929\u51e4\u624b\u4e0b\u3002 \u3000\u3000\u6b27\u9633\u4e00\u5bb6\u4e43\u662f\u7ec8\u5357\u5c71\u7684\u6b66\u5b66\u4e16\u5bb6\u3002\u6b27\u9633\u4ef2\u548c\u5728\u4e09\u5144\u5f1f\u4e2d\u6b66\u529f\u6700\u5f3a\uff0c\u6b27\u9633\u4e8c\u5a18\u7684\u529f\u592b\u53c8\u591a \u534a\u662f\u4e08\u592b\u4f20\u6388\u7684\u3002\u6240\u4ee5\u5728\u542c\u5f97\u8fd9\u8001\u5934\u51e0\u4fbf\u662f\u534e\u5929\u51e4\u4e4b\u540e\uff0c\u4fbf\u4e0d\u7981\u60f3\u9053\uff1a\u201c\u6211\u7684\u529f\u592b\u6700\u591a\u53ca\u5f97 \u4e0a\u4ef2\u548c\u7684\u4e94\u6210\uff0c\u800c\u9634\u3001\u5389\u4e8c\u4eba\u7684\u529f\u592b\u5374\u6bd4\u6211\u8fd8\u7a0d\u6709\u4e0d\u5982\u3002\u4f9d\u6b64\u770b\u6765\uff0c\u53ea\u6015\u5408\u4e09\u4eba\u4e4b\u529b\uff0c\u4e5f\u672a \u5fc5\u80dc\u5f97\u4e86\u8fd9\u534e\u5929\u98ce\uff0c\u4f55\u51b5\u8fd8\u6709\u4e2a\u4ef2\u957f\u7edf\u4e5f\u662f\u4e2a\u51fa\u540d\u7684\u96be\u6597\u4eba\u7269\u3002\u201d\u5fc3\u4e2d\u6697\u840c\u9000\u610f\u3002 \u3000\u3000\u534e\u5929\u98ce\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u6c5f\u6e56\u4e0a\u4e09\u4f4d\u98ce\u4e91\u4eba\u7269\u6765\u4e86\uff0c\u4e45\u4ef0\uff01\u4e45\u4ef0\uff01\u5931\u656c\uff0c\u5931\u656c\uff01\u6211\u6240 \u517b\u7684\u8fd9\u53ea\u6241\u6bdb\u755c\u7261\uff0c\u4e0d\u8bc6\u5927\u540d\uff0c\u591a\u6709\u5192\u72af\uff0c\u3001\u8fd8\u671b\u6055\u7f6a\u3002\u518d\u8bf7\u95ee\u4e09\u4f4d\u8054\u8882\u800c\u6765\uff0c\u6709\u4f3a\u8d35\u5e72\uff1f\u201d \u3000\u3000\u5389\u590d\u751f\u6700\u4e3a\u7740\u6025\uff0c\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u6253\u6270\u534e\u8001\u5148\u751f\uff0c\u5b9e\u5728\u8fc7\u610f\u4e0d\u53bb\u3002\u4f46\u6211\u5949\u4e86\u655d\u6559\u6559\u4e3b\u4e4b \u547d\uff0c\u8981\u5c06\u4e00\u4f4d\u59d3\u6c5f\u7684\u5c11\u5e74\u5e26\u56de\u53bb\uff0c\u4e0d\u77e5\u534e\u8001\u5148\u751f\u5c4b\u4e2d\u53ef\u6709\u6b64\u4eba\u4e48\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u5462\uff1f\u4e5f\u90fd\u662f\u4e3a\u6b64\u4e8b\u800c\u6765\u4e48\uff1f\u201d\u9634\u5723\u59d1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u59d3\u6c5f\u7684\u5c0f\u5b50 \u5f97\u7f6a\u4e86\u91d1\u9e70\u5bab\u7684\u4eba\uff0c\u6211\u53d7\u91d1\u9e70\u5bab\u7684\u4f9b\u517b\uff0c\u5c11\u4e0d\u5f97\u4e5f\u8981\u7ba1\u4ed6\u4e00\u7ba1\u3002\u201d\u6b27\u9633\u4e8c\u5a18\u5219\u8bf4\uff1a\u201c\u8fd9\u4f4d\u6c5f \u5c0f\u4fa0\u548c\u6211\u4e5f\u6709\u70b9\u5c0f\u5c0f\u8fc7\u8282\uff0c\u82e5\u4ed6\u5728\u6b64\uff0c\u8bf7\u5bb9\u4e00\u89c1\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u8c29\u6761\u65af\u7406\u5730\u8bf4\u9053\uff1a\u201c\u6211\u5c4b\u5b50\u91cc\u662f\u6709\u4e00\u4f4d\u59d3\u6c5f\u7684\u5c11\u5e74\uff0c\u4f46\u4ed6\u4f4f\u5728\u6211\u7684\u5bb6\u91cc\uff0c\u5c31\u662f\u6211 \u7684\u5ba2\u4eba\uff0c\u8bf7\u6055\u8001\u592b\u4e5f\u8981\u7ba1\u7ba1\u95f2\u4e8b\u3002\u201d\u9634\u5723\u59d1\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u53ef\u662f\u8981\u5305\u5e87\u4ed6\u4e48\uff1f\u201d\u5341\u6307 \u500f\u4f38\uff0c\u5c31\u8981\u5411\u534e\u5929\u98ce\u6293\u53bb\u3002\u6b63\u662f\uff1a \u3000\u3000\u9c81\u73ed\u95e8\u524d\u5f04\u5927\u65a7\uff0c\u6562\u65bd\u6bd2\u624b\u5bb3\u795e\u533b\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u5341\u4e03\u56de\u3000\u5404\u901e\u5947\u80fd\u5bd2\u654c\u80c6\u3000\u6b32\u51ed\u7edd\u5b66\u6597\u5f3a\u4ec7 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u5341\u4e03\u56de\u3000\u5404\u901e\u5947\u80fd\u5bd2\u654c\u80c6\u3000\u6b32\u51ed\u7edd\u5b66\u6597\u5f3a\u4ec7 \u3000\u3000\u4ef2\u957f\u7edf\u5ffd\u5730\u5f2f\u8170\u8bf4\u9053\uff1a\u201c\u8def\u6ed1\u8bf7\u5c0f\u5fc3\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u9634\u5723\u59d1\u5c0f\u817f\u7684\u9ebb\u7a74\u7a81\u7136\u53d1\u75d2\uff0c\u4e00\u6b65 \u8e0f\u7a7a\uff0c\u51e0\u4e4e\u683d\u5012\uff0c\u4f46\u542c\u5f97\u201c\u5580\u5693\u201d\u58f0\u54cd\uff0c\u5979\u7684\u957f\u6307\u7532\u5df1\u63d2\u8fdb\u4e86\u4e00\u68f5\u6811\u5e72\uff0c\u8fd9\u624d\u7a33\u4f4f\u4e86\u8eab\u5f62\u3002 \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u5bf9\u554a\uff0c\u4f60\u770b\u6211\u53ef\u771f\u662f\u8001\u7cca\u6d82\u4e86\uff0c\u53ea\u987e\u5728\u95e8\u5916\u548c\u5ba2\u4eba\u4eec\u8bf4\u8bdd\u3002\u4ef2\u8001\u5f1f\uff0c\u5e78\u4e8f \u4f60\u63d0\u9192\u4e86\u6211\u3002\u201d\u987f\u5382\u4e00\u987f\uff0c\u8fb9\u7b11\u8fb9\u9053\uff1a\u201c\u96be\u5f97\u4f60\u4eec\u4e09\u4f4d\u8fdc\u5ba2\u5230\u6765\uff0c\u8bf7\u8fdb\u91cc\u9762\u5750\u5750\uff0c\u8ba9\u6211\u7a0d\u5c3d \u5730\u4e3b\u4e4b\u8c0a\u3002\u6709\u751a\u7406\u8bba\uff0c\u54b1\u4eec\u4e5f\u597d\u7ec6\u8bf4\u3002\u6c5f\u5c0f\u4fa0\u662f\u600e\u6837\u5f97\u7f6a\u4e86\u4f60\u4eec\uff0c\u6211\u8fd8\u4e0d\u77e5\u9053\u5462\uff01\u8981\u662f\u4f60\u4eec \u4fe1\u5f97\u8fc7\u6211\u548c\u4ef2\u5e2e\u4e3b\u7684\u8bdd\uff0c\u5c31\u8ba9\u6211\u4eec\u6765\u8bc4\u8bc4\u7406\u3002\u4f60\u4eec\u4e24\u65b9\u90fd\u662f\u6211\u7684\u5ba2\u4eba\uff0c\u6211\u51b3\u4e0d\u4f1a\u504f\u8892\u3002\u5f53\u771f \u662f\u6c5f\u5c0f\u4fa0\u7406\u4e8f\u7684\u8bdd\uff0c\u6211\u4e5f\u81ea\u96be\u2018\u5305\u5e87\u2019\u3002\u4e0b\u4e86\u4e24\u5927\u96e8\uff0c\u8def\u4e0a\u53c8\u6709\u9752\u82d4\uff0c\u8bf7\u4f60\u4eec\u8d70\u8def\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u4ed6\u4eec\u4e8c\u4eba\u4e00\u5531\u4e00\u548c\uff0c\u628a\u9634\u5723\u59d1\u521a\u624d\u5411\u534e\u5929\u98ce\u6251\u6765\u7684\u90a3\u4e2a\u4e3e\u529f\uff0c\u5f53\u4f5c\u662f\u5979\u60f3\u8fc8\u6b65\u8d70\u8fdb\u5c4b \u5b50\u3002\u4ef2\u957f\u7edf\u8fd8\u88c5\u6a21\u4f5c\u6837\u7684\u5f2f\u8170\u8bf4\u9053\uff1a\u201c\u9634\u8001\u524d\u8f88\uff0c\u4f60\u8001\u4eba\u5bb6\u8981\u4e0d\u8981\u6211\u6765\u6276\u4f60\u4e00\u6276\uff1f\u201d \u3000\u3000\u9634\u5723\u59d1\u53d7\u4e86\u4ef2\u957f\u7edf\u7684\u6697\u7b97\uff0c\u7ed9\u6349\u5f04\u5f97\u557c\u7b11\u7686\u975e\uff0c\u5374\u53c8\u53d1\u4f5c\u4e0d\u5f97\uff0c\u56e0\u4e3a\u8981\u662f\u56b7\u51fa\u6765\u7684\u8bdd\uff0c \u90a3\u5c31\u7b49\u4e8e\u5728\u5c0f\u8f88\u9762\u524d\uff0c\u627f\u8ba4\u81ea\u5df1\u672c\u9886\u4e0d\u5982\u4eba\u5bb6\uff0c\u683d\u4e86\u8ddf\u5934\u4e86\u3002\u4ef2\u957f\u7edf\u4e5f\u6b63\u662f\u56e0\u4e3a\u77e5\u9053\u9634\u5723\u59d1 \u8fd9\u4e2a\u201c\u6b7b\u7231\u9762\u5b50\uff0c\u4e0d\u80af\u8ba4\u8f93\u201d\u7684\u813e\u6c14\uff0c\u6240\u4ee5\u624d\u6545\u610f\u4f5c\u5f04\u5979\u4e00\u4e0b\u7684\u3002 \u3000\u3000\u9634\u5723\u59d1\u53c8\u60ca\u53c8\u6012\uff0c\u5fc3\u91cc\u60f3\u9053\uff1b\u201c\u6211\u542c\u4eba\u5bb6\u8bf4\u8fd9\u5316\u5b50\u5df2\u7ec3\u6210\u4e86\u6df7\u5143\u4e00\u6c14\u529f\uff0c\u6211\u8fd8\u4e0d\u76f8\u4fe1\u3002 \u54ea\u77e5\u4ed6\u679c\u7136\u80fd\u591f\u8fd0\u7528\u771f\u6c14\uff0c\u5df2\u7ecf\u53ef\u4ee5\u65bd\u5c55\u9694\u7a7a\u70b9\u7a74\u7684\u672c\u9886\u4e86\u3002\u201d\u89c1\u4ed6\u4f5c\u52bf\u8981\u6276\uff0c\u751f\u6015\u53c8\u7740\u4e86 \u4ed6\u7684\u9053\u513f\uff0c\u8fde\u5fd9\u5c01\u4e86\u5168\u8eab\u7a74\u9053\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u8001\u5a46\u5b50\u8fd8\u8d70\u5f97\u52a8\uff0c\u4e0d\u5fc5\u4f60\u732e\u6bb7\u52e4\u3002\u201d\u5c06\u957f \u201c\u6307\u7532\u201d\u62d4\u4e86\u51fa\u6765\uff0c\u53ea\u89c1\u90a3\u88f8\u6811\u4e0a\u7684\u82b1\u6735\uff0c\u5728\u8fd9\u7247\u523b\u4e4b\u95f4\uff0c\u7adf\u7136\u5168\u90fd\u67af\u840e\uff0c\u7eb7\u7eb7\u843d\u4e0b\u3002\u4ef2\u957f \u7edf\u4e5f\u4e0d\u7981\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u4e03\u9634\u6559\u4ee5\u64c5\u4e8e\u4f7f\u6bd2\u9a70\u540d\uff0c\u679c\u7136\u89c1\u9762\u80dc\u4f3c\u95fb\u540d\u3002\u8981\u662f\u6211\u7ed9\u5979\u8fd9\u4e48 \u6293\u4e86\u4e00\u4e0b\uff0c\u53ea\u6015\u4e5f\u5f97\u5927\u75c5\u4e00\u573a\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u6b64\u65f6\u5df2\u5728\u9662\u5b50\u91cc\u5e03\u7f6e\u597d\u4e86\uff0c\u4e24\u5f20\u77f3\u684c\u4e0a\u6446\u8bbe\u4e86\u8bb8\u591a\u8336\u70b9\uff0c\u6c5f\u6d77\u5929\u4e5f\u5728\u4e00\u65c1\u5e2e\u5979\u5e03 \u7f6e\u3002\u8fd9\u65f6\u534e\u5929\u51e4\u5df2\u548c\u5979\u4eec\u8d70\u4e86\u8fdb\u6765\uff0c\u534e\u5929\u51e4\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8d70\u4e86\u8bb8\u591a\u5c71\u8def\uff0c\u60f3\u5fc5\u4e5f\u6709\u70b9\u52b3\u7d2f \u4e86\u3002\u5148\u5750\u4e00\u5750\u559d\u559d\u8336\u5427\uff0c\u8fd9\u662f\u670b\u53cb\u4ece\u5e90\u5c71\u5e26\u6765\u7684\u4e91\u96fe\u8336\uff0c\u8fd9\u662f\u5c0f\u5973\u505a\u7684\u7c97\u70b9\u5fc3\u3002\u8001\u592d\u62ff\u4e0d\u51fa \u4ec0\u4e48\u597d\u4e1c\u897f\u6b3e\u5ba2\u3002\u89c1\u7b11\u4e86\uff0c\u201d \u3000\u3000\u5389\u590d\u751f\u8eab\u5f62\u4e00\u6643\uff0c\u5ffd\u5730\u4fbf\u6b3a\u5230\u4e86\u6c5f\u6d77\u5929\u7684\u773c\u524d\uff0c\u56b7\u9053\uff0c\u201c\u6211\u597d\u610f\u8ba9\u91d1\u6bdb\u72e1\u9001\u4f60\u56de\u53bb\uff0c\u4f60 \u600e\u7684\u4e2d\u9014\u9003\u8d70\uff0c\u8fd8\u628a\u5b83\u4eec\u5f04\u4f24\u4e86\uff1f\u6559\u5c6f\u5f88\u60f3\u89c1\u4f60\uff0c\u597d\uff0c\u4f60\u73b0\u5728\u5c31\u968f\u6211\u8d70\u54271\u201d\u624b\u8155\u4e00\u7ffb\uff0c\u4f7f \u51fa\u5927\u64d2\u62ff\u624b\u6cd5\uff0c\u4fbf\u5411\u6c5f\u6d77\u5929\u201c\u62ff\u201d\u4e0b\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u6c5f\u6d77\u5929\u4fd8\u638c\u76f8\u8fce\uff0c\u53cc\u638c\u767b\u65f6\u80f6\u7740\uff0c\u5389\u590d\u751f\u6ee1\u9762\u6da8\u7ea2\uff0c\u9752\u7b4b\u66b4\u73b0\uff0c \u5fc3\u91cc\u6697\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u5c0f\u5b50\u7684\u5185\u529f\u7adf\u662f\u8fd9\u4e48\u6df1\u539a\uff01\u6211\u672c\u6765\u4e0d\u60f3\u4f24\u4ed6\u7684\uff0c\u4f46\u82e5\u4e0d\u4f24\u4ed6\uff0c\u53c8\u4e0d\u80fd \u5c06\u4ed6\u4eec\u56de\u53bb\u4ea4\u5dee\u3002\u54ce\u3001\u8bf4\u4e0d\u5f97\u6211\u53ea\u597d\u8fd0\u7528\u4fee\u7f57\u9634\u715e\u529f\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5ffd\u89c9\u4e00\u80a1\u5947\u5bd2\u4e4b\u6c14\u88ad\u6765\uff0c\u867d\u6709\u62a4\u4f53\u795e\u529f\uff0c\u5728\u8fd9\u5239\u90a3\uff0c\u4e5f\u89c9\u5f97\u6709\u5982\u7a81\u7136\u5760\u5230\u51b0\u7a9f\u91cc \u4e00\u822c\uff0c\u51b7\u5f97\u96be\u53d7\u3002\u4ed6\u5fc3\u91cc\u4e5f\u5728\u6697\u81ea\u60f3\u9053\uff0c\u201c\u6211\u672c\u6765\u4e0d\u60f3\u4f24\u4ed6\u7684\uff0c\u4ed6\u5374\u4f7f\u51fa\u4e86\u8fd9\u822c\u72e0\u6bd2\u7684\u4fee\u7f57 \u9634\u715e\u529f\u6765\uff0c\u8bf4\u4e0d\u5f97\u6211\u53ea\u597d\u4ee5\u5c11\u9633\u7384\u529f\u6765\u53cd\u51fb\u4ed6\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u201c\u5c11\u9633\u7384\u529f\u201d\u6b63\u662f\u5415\u56db\u5a18\u6240\u4f20\u4e0b\u6765\u7684\uff0c\u5f53\u5e74\u5415\u56db\u5a18\u7ec3\u6b64\u5947\u529f\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u5bf9\u4ed8\u5b5f \u795e\u901a\u7684\u4fee\u7f57\u9634\u715e\u529f\uff0c\u6c5f\u6885\u5929\u7684\u5e08\u7236\u91d1\u4e16\u9057\u4ece\u8c37\u4e4b\u534e\u4e4b\u5904\uff0c\u5f97\u5230\u4e86\u201c\u5c11\u9633\u7384\u529f\u201d\u7684\u8bc0\u7a8d\uff0c\u7ecf\u8fc7 \u4ed6\u878d\u4f1a\u5404\u5bb6\uff0c\u5728\u8fd9\u95e8\u529f\u592b\u4e0a\u4e5f\u6709\u4e0d\u5c11\u589e\u76ca\uff0c\u7cbe\u76ca\u6c42\u7cbe\uff0c\u4e0d\u4f46\u53ef\u4ee5\u62b5\u5fa1\u4fee\u7f57\u9634\u715e\u529f\uff0c\u800c\u4e14\u53ef\u4ee5 \u5c06\u4e4b\u7834\u89e3\uff01 \u3000\u3000\u5389\u590d\u751f\u53ea\u89c9\u4e00\u80a1\u9633\u548c\u4e4b\u6c14\uff0c\u4ece\u6c5f\u6d77\u5929\u7684\u638c\u5fc3\u6e90\u6e90\u8f93\u51fa\uff1a\u4e0d\u8fc7\u7247\u523b\uff0c\u7adf\u4f3c\u5982\u6c90\u6625\u98ce\uff0c\u5728\u9189 \u4eba\u7684\u8273\u9633\u5929\u91cc\uff0c\u6e29\u6696\u5f97\u61d2\u6d0b\u6d0b\u7684\u63d0\u4e0d\u8d77\u52b2\u6765\u3002\u5389\u590d\u751f\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u52a0\u7d27\u65bd\u4e3a\uff0c\u4ed6\u7684\u4fee\u7f57\u9634 \u715e\u529f\u5df2\u7ec3\u5230\u7b2c\u516b\u91cd\u4ee5\u4e0a\uff0c\u5c06\u8981\u63a5\u8fd1\u6700\u9ad8\u5883\u754c\u7684\u7b2c\u4e5d\u91cd\u4e86\uff0c\u8fd9\u4e00\u52a0\u610f\u65bd\u4e3a\uff0c\u81ea\u662f\u975e\u540c\u5c0f\u53ef\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u867d\u7136\u5df1\u5b66\u4f1a\u4e86\u201c\u5c11\u9633\u7384\u529f\u201d\uff0c\u4f46\u529f\u529b\u8fd8\u5fae\u5acc\u4e0d\u8db3\uff0c\u7528\u6765\u4e0e\u5389\u590d\u751f\u5bf9\u6297\uff0c\u53ea\u662f\u7a0d\u5360 \u4e0a\u98ce\uff0c\u4e0d\u8fc7\u7247\u65f6\uff0c\u6c5f\u6d77\u5929\u51b7\u6c57\u6d94\u6d94\uff0c\u5389\u590d\u751f\u5219\u662f\u70ed\u6c57\u6eda\u6eda\uff0c\u4e24\u4eba\u90fd\u662f\u5fc3\u4e2d\u4e00\u51db\uff0c\u8c01\u80dc\u8c01\u8d25\uff0c \u4e14\u5148\u4e0d\u8bf4\uff0c\u53ea\u6015\u76f8\u6301\u4e0b\u53bb\uff0c\u52bf\u5c06\u4e24\u8d25\u4ff1\u4f24\uff01 \u3000\u3000\u4ef2\u957f\u7edf\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e8c\u4eba\u662f\u8001\u670b\u53cb\u4e86\uff0c\u602a\u4e0d\u5f97\u4e00\u89c1\u9762\u5c31\u8fd9\u4e48\u4eb2\u70ed\uff0c\u62c9\u7740\u624b\u513f \u4e0d\u80af\u653e\u3002\u55ef\uff0c\u8fd8\u662f\u5750\u4e0b\u6765\u6f2b\u6162\u8c08\u5427\uff01\u201d\u4ed6\u6325\u8896\u4e00\u62c2\uff0c\u8bf4\u4e5f\u5947\u602a\uff0c\u4ed6\u7684\u90a3\u6761\u957f\u8896\u7adf\u4f3c\u4e00\u53e3\u5200\u90a3 \u4e48\u4f3c\u7684\uff0c\u6070\u5de7\u4ece\u4e24\u4eba\u5f53\u4e2d\u201c\u5256\u201d\u4e0b\uff0c\u767b\u65f6\u53cc\u638c\u5206\u5f00\uff0c\u90fd\u5411\u540e\u9000\u4e86\u51e0\u6b65\u3002 \u3000\u3000\u539f\u6765\u4ef2\u957f\u7edf\u662f\u8fd0\u7528\u6df7\u5143\u4e00\u6c14\u529f\uff0c\u4ee5\u65e0\u5f62\u7684\u7f61\u6c14\uff0c\u5c06\u4e24\u4eba\u7684\u52b2\u529b\u9694\u65ad\uff0c\u4ed6\u4eec\u80f6\u7740\u7684\u624b\u638c\u624d \u80fd\u5206\u5f00\u7684\uff0c\u4f46\u662f\u5e76\u4e0d\u662f\u8bf4\u4ef2\u957f\u7edf\u7684\u201c\u6df7\u5143\u4e00\u6c14\u529f\u201d\u5c31\u80dc\u5f97\u8fc7\u5389\u590d\u751f\u7684\u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\uff0c\u6216\u8005 \u6c5f\u6d77\u5929\u7684\u201c\u5c11\u9633\u7384\u529f\u201d\uff0c\u800c\u662f\u56e0\u4e3a\u4ed6\u4eec\u4e8c\u4eba\u5dee\u4e0d\u591a\u65d7\u9f13\u76f8\u5f53\uff0c\u800c\u4ef2\u957f\u7edf\u53c8\u65bd\u7528\u5f97\u5de7\uff0c\u6545\u6b64\u65b9 \u80fd\u4e00\u4e3e\u594f\u6548\u3002 \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u6c5f\u5c0f\u4fa0\uff0c\u5389\u526f\u6559\u4e3b\u8981\u8bf7\u4f60\u53bb\u89c1\u4ed6\u4eec\u7684\u6559\u4e3b\uff0c\u4f60\u610f\u4e0b\u5982\u4f55\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a \u201c\u5389\u526f\u6559\u4e3b\u7684\u597d\u610f\u6211\u5fc3\u9886\u4e86\uff0c\u6211\u8fd8\u8981\u53bb\u5bfb\u8bbf\u6211\u7684\u7236\u4eb2\u548c\u5e08\u7236\uff0c\u82e5\u7136\u4ed6\u65e5\u6709\u7f18\uff0c\u6211\u518d\u53bb\u62dc\u89c1\u4f60 \u4eec\u7684\u6559\u4e3b\u5427\u3002\u201d\u5389\u590d\u751f\u6025\u9053\uff0c\u201c\u5475\u662f\u6559\u4e3b\u5429\u5490\u4e8e\u6211\uff0c\u5b9a\u8981\u6211\u5c06\u4f60\u5e26\u56de\u53bb\u5440\uff01\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u6c5f\u5c0f\u4fa0\u4e0e\u4f60\u4eec\u5929\u9b54\u6559\u6709\u4ec0\u4e48\u8fc7\u4e0d\u53bb\u7684\u6df1\u4ec7\u4e48\uff1f\u201d\u5389\u590d\u751f\u6115\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a \u201c\u6ca1\u6709\uff0c\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u4eec\u7684\u6559\u4e3b\u662f\u8bda\u5fc3\u8bf7\u5ba2\u7684\u4e86\u3002\u201d\u5389\u590d\u751f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u5c0f\u65f6 \u5019\uff0c\u6211\u4eec\u7684\u6559\u4e3b\u66fe\u629a\u517b\u8fc7\u4ed6\uff0c\u867d\u7136\u65e5\u5b50\u65e0\u591a\uff0c\u5bf9\u4ed6\u5012\u662f\u751a\u4e3a\u75bc\u7231\u7684\u3002\u73b0\u5728\u591a\u5e74\u4e0d\u89c1\uff0c\u96be\u514d\u601d \u5ff5\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u90a3\u662f\u6211\u516b\u5c81\u90a3\u5e74\uff0c\u5929\u9b54\u6559\u4e3b\u6d3e\u9063\u624b\u4e0b\u5411\u6211\u7236\u4eb2\u5077\u5e08\uff0c\u6015\u6211\u7236\u4eb2\u6cc4\u6f0f\u51fa\u53bb\uff0c \u662f\u4ee5\u5c06\u6211\u6263\u62bc\u8d77\u6765\uff0c\u4f5c\u4e3a\u4eba\u8d28\u7684\u4e8c\u4f46\u4e0d\u8bba\u5982\u4f55\uff0c\u5979\u5bf9\u6211\u662f\u7684\u786e\u4e0d\u9519\u2026\u2026\u201d\u5389\u590d\u751f\u80b2\u9053\uff1a\u201d\u65e2 \u7136\u5982\u6b64\uff0c\u4f60\u5c31\u8be5\u968f\u6211\u53bb\u89c1\u5979\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u56e0\u6b64\u6211\u4e5f\u5e76\u4e0d\u602a\u5979\uff0c\u800c\u4e14\u611f\u6fc0\u5979\u7684\u597d\u610f\u3002\u4f46\u6211\u73b0\u5728\u6709\u4e8b\u5728\u8eab\uff0c\u96be\u4ee5 \u5ef6\u963b\u3002\u4e14\u5f85\u6211\u529e\u4e86\u6b63\u4e8b\uff0c\u89c1\u4e86\u6211\u7684\u7236\u4eb2\u548c\u5e08\u7236\u4e4b\u540e\uff0c\u90a3\u65f6\u4e0d\u5f85\u4f60\u8bf7\uff0c\u6211\u4e5f\u4f1a\u4e0e\u5e08\u7236\u540c\u53bb\u89c1 \u5979\u3002\u636e\u6211\u6240\u77e5\uff0c\u6211\u5e08\u7236\u975e\u4f46\u60f3\u89c1\u5929\u9b54\u6559\u4e3b\u3002\u4ed6\u4e5f\u5f88\u60f3\u89c1\u4f60\u4e00\u5389\u526f\u6559\u4e3b\u5462\uff01\u201d\u5389\u590d\u751f\u542c\u5f97\u6c5f\u6d77 \u5929\u63d0\u8d77\u4ed6\u7684\u5e08\u7236\uff0c\u4e0d\u89c9\u53c8\u662f\u4e00\u6115\uff0c\u8138\u4e0a\u6709\u79cd\u96be\u4ee5\u5f62\u5bb9\u7684\u5947\u5f02\u8868\u60c5\u3002 \u3000\u3000\u534e\u5929\u98ce\u7b11\u9053\uff1a\u201c\u65e2\u7136\u4ed6\u4e0e\u8d35\u6559\u65e0\u4ec7\uff0c\u8d35\u6559\u4e3b\u8bf7\u4ed6\u53c8\u662f\u4e00\u756a\u597d\u610f\uff0c\u8fd9\u5c31\u5bb9\u6613\u8bf4\u4e86\u3002\u8bf7\u5ba2\u603b \u5f97\u53cc\u65b9\u60c5\u613f\uff0c\u4ed6\u4e0d\u613f\u610f\uff0c\u603b\u4e0d\u80fd\u5c06\u4ed6\u7ed1\u53bb\u7684\u554a\uff01\u5389\u526f\u6559\u4e3b\u3002\u51ed\u9053\u7406\u8bf4\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\u4e25\u5389\u590d\u751f \u96be\u4ee5\u518d\u8fa9\uff01\u671f\u671f\u827e\u827e\u4ed6\u8bf4\u9053\uff1a\u201c\u7167\u9053\u7406\u8bb2\uff0c\u662f\u8fd9\u6837\u3002\u4f46\u3001\u4f46\u2026\u2026\u201d \u3000\u3000\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u4f60\u6015\u5728\u6559\u4e3b\u9762\u524d\u4ea4\u4e0d\u4e86\u5dee\u4e48\uff1f\u597d\u5427\uff0c\u4f60\u5c31\u628a\u8fd9\u4e8b\u63a8\u5230\u6211\u7684\u8eab\u4e0a\u5427\u3002\u4f60\u95ee \u53bb\u8bf4\uff0c\u8fd9\u4f4d\u6c5f\u5c0f\u4fa0\u662f\u6211\u5317\u4e10\u5e2e\u5e2e\u4e3b\u4ef2\u957f\u7edf\u7684\u597d\u670b\u53cb\uff0c\u4ed6\u548c\u6211\u5728\u4e00\u8d77\uff0c\u4f60\u2018\u8bf7\u2019\u4e0d\u52a8\uff0c\u5979\u82e5\u89c1 \u602a\uff0c\u53eb\u5979\u602a\u6211\u597d\u4e86\uff01\u201d \u3000\u3000\u5389\u590d\u751f\u89c1\u8bc6\u8fc7\u4ef2\u957f\u7edf\u7684\u6df7\u5143\u4e00\u6c14\u529f\uff0c\u542c\u4ed6\u5982\u6b64\u8bf4\u8bdd\uff0c\u5fc3\u91cc\u60f3\u8fd9\u6211\u82e5\u662f\u7528\u5f3a\u7684\u8bdd\uff0c\u786e\u5b9e\u662f \u201c\u8bf7\u201d\u4ed6\u4e0d\u52a8\uff0c\u751a\u81f3\u4e0d\u5fc5\u8fd9\u5316\u5b50\u51fa\u624b\uff0c\u6211\u4e0e\u4ed6\u6700\u591a\u4e5f\u53ea\u662f\u4e24\u8d25\u4ff1\u4f24\uff0c\u8fd8\u662f\u201c\u8bf7\u201d\u4ed6\u4e0d\u52a8\u3002\u5f53 \u4e0b\u54d1\u53e3\u65e0\u8a00\u3002 \u3000\u3000\u9634\u5723\u59d1\u5ffd\u9053\uff1a\u201c\u5389\u526f\u6559\u4e3b\uff0c\u4f60\u4e0d\u8981\u4eba\uff0c\u5b9d\u7269\u4e5f\u4e0d\u8981\u4e86\u4e48\uff1f\u201d\u4f60\u4e0d\u662f\u8bf4\uff0c\u90a3\u4e24\u4ef6\u5b9d\u7269\uff0c\u672c \u6765\u8be5\u662f\u4f60\u5389\u5bb6\u7684\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u5934\u6e29\u6012\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u7684\u5b9d\u5251\u548c\u5b9d\u7532\u662f\u5e08\u7236\u7ed9\u6211\u7684\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u5b83\u4eec\u672c\u6765\u662f \u8c01\u5bb6\u7684\u4e1c\u897f\u3002\u8981\u7ed9\u4f60\u4e5f\u4e0d\u96be\uff0c\u4f46\u6211\u53ef\u5f97\u5148\u95ee\u8fc7\u5e08\u7236\u3002\u82e5\u7136\u5e08\u7236\u8bf4\u7ed9\uff0c\u6211\u65e5\u540e\u81ea\u5f53\u4eb2\u81ea\u767b\u95e8\uff0c \u53cc\u624b\u5949\u5448\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u518d\u6b21\u63d0\u8d77\u5e08\u7236\uff0c\u5389\u590d\u751f\u4f3c\u662f\u7ed9\u4eba\u7528\u5229\u9488\u523a\u4e86\u4e00\u4e0b\u4f3c\u7684\uff0c\u7a81\u7136\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4e0d\u5fc5\u8bf4 \u4e86\uff0c\u8001\u5b9e\u544a\u8bc9\u4f60\u5427\uff0c\u4e0d\u662f\u6211\u60f3\u8981\u8fd9\u4e24\u4ef6\u5b9d\u7269\uff0c\u662f\u5979\u4eec\u60f3\u8981\u3002\u597d\uff0c\u73b0\u5728\u6211\u4e0d\u7ba1\u4e86\uff0c\u968f\u4f60\u4eec\u53bb \u5427\uff01\u201d\u8bf4\u7f62\uff0c\u4e00\u69b4\u70df\u7684\u5c31\u8dd1\u51fa\u53bb\uff0c\u4e0d\u4e45\u5c31\u542c\u5f97\u91d1\u6bdb\u72e1\u7684\u543c\u58f0\u3002\u8f6c\u77ac\u4e4b\u95f4\uff0c\u90a3\u58f0\u97f3\u5df2\u5728\u6570\u91cc\u4e4b \u5916\uff0c\u60f3\u662f\u4ed6\u5df2\u8de8\u4e0a\u91d1\u6bdb\u72e1\u8dd1\u4e86\u3002 \u3000\u3000\u534e\u5929\u98ce\u7b11\u9053\uff1a\u201c\u5df2\u4e86\u7ed3\u4e00\u6869\u4e86\u3002\u4f60\u4eec\u4e0e\u6c5f\u5c0f\u4fa0\u53c8\u6709\u4ec0\u4e48\u8fc7\u8282\uff1f\u8bf4\u5427\uff01\u201d \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u9053\uff1a\u201c\u6211\u8fd8\u6709\u70b9\u4e8b\u60c5\uff1a\u8bf7\u8ba9\u6211\u5148\u8bf4\u3002\u201d\u4ed6\u7ad9\u4e86\u8d77\u6765\uff0c\u5ffd\u5730\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5a49\u513f\uff0c \u662f\u4f60\u8bf4\u5462\u8fd8\u662f\u6211\u8bf4\uff1f\u201d\u6b27\u9633\u5a49\u7ea2\u6655\u53cc\u988a\uff0c\u4f4e\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u7b11\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u5728\u964c\u751f\u4eba\u9762\u524d\u5bb3\u7f9e\uff0c\u8fd8\u662f\u6211\u4ee3\u5979\u8bf4\u5427\uff0c\u6c5f\u76f8\u516c\uff0c\u5a49\u513f\u4e0e\u6211\u5230 \u6b64\uff0c\u4e00\u6765\u662f\u5411\u4f60\u8d54\u7f6a\uff1b\u4e8c\u6765\u662f\u5411\u4f60\u9053\u8c22\u3002\u5979\u65e5\u524d\u5e03\u4e0b\u9677\u8bb2\uff0c\u5c06\u4f60\u6bd2\u5bb3\uff0c\u90a3\u662f\u5949\u4e86\u5e08\u5c0a\u4e4b\u547d\uff0c \u4e0d\u5f97\u4e0d\u7136\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u4e2a\u4e2a\u60ca\u5947\uff0c\u5927\u5bb6\u90fd\u4ee5\u4e3a\u5979\u662f\u6311\u8845\u6765\u7684\uff0c\u54ea\u77e5\u5374\u662f\u8d54\u7f6a\u6765\u4e86\u3002\u6c5f\u6d77\u5929 \u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u6ca1\u6709\u602a\u5979\u3002\u201d\u9634\u5723\u59d1\u6c14\u5f97\u9762\u8272\u53d1\u80b2\uff0c\u5728\u4e00\u65c1\u563f\u563f\u51b7\u7b11\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u5374\u4e0d\u7406\u5979\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6628\u65e5\u4f60\u5355\u8eab\u5339\u9a6c\uff0c\u524d\u5f80\u6551\u5979\u3002\u867d\u8bf4\u6309\u7167\u6b66\u6797\u89c4\u77e9\uff0c\u5979 \u662f\u72af\u4e86\u5e08\u95e8\u6212\u5f8b\uff0c\u7406\u5f53\u53d7\u7f5a\uff0c\u4e0e\u5916\u4eba\u65e0\u5173\uff0c\u5916\u4eba\u4e5f\u4e0d\u8be5\u5341\u9876\uff0c\u4f46\u8fd9\u662f\u4f60\u7684\u4e00\u7247\u597d\u5fc3\uff0c\u6240\u4ee5\u6211 \u4eec\u8fd8\u662f\u8981\u5411\u4f60\u9053\u8c22\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u9053\u8c22\u8fd9\u4e0d\u6562\u5f53\u3002\u8bf4\u6765\u8fd8\u662f\u6211\u8be5\u8bf7\u4f60\u4eec\u539f\u8c05\uff0c\u8bf7\u6055\u6211\u4e0d\u61c2\u8fd9\u95e8\u89c4\u77e9\uff0c\u51e0\u4e4e\u8fde \u7d2f\u4e86\u6b27\u9633\u59d1\u5a18\u3002\u6211\u53ea\u77e5\u9053\u6211\u7684\u5e08\u7236\u5bf9\u6211\u6781\u597d\uff0c\u6211\u4fbf\u4ee5\u4e3a\u5927\u4e0b\u7684\u5e08\u7236\u7686\u7136\u3001\u6240\u4ee5\u770b\u89c1\u9634\u8001\u524d\u8f88 \u8981\u7528\u6bd2\u5211\u5904\u7f6e\u5f92\u513f\uff0c\u6211\u4fbf\u7981\u4e0d\u4f4f\u8981\u5192\u6627\u4e0a\u524d\u4e3a\u5979\u8bf4\u8bdd\u4e86\u3002\u201d \u3000\u3000\u9634\u5723\u59d1\u6012\u6781\u6c14\u6781\u3001\u4ef0\u5929\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u662f\u6211\u7684\u597d\u59d0\u59b9\uff0c\u60f3\u4e0d\u5230\u4eca\u65e5\u4f60\u7adf\u80f3\u818a \u5916\u5f2f\uff0c\u5e2e\u8d77\u5916\u4eba\u6765\u4e86\u3002\u54fc\uff0c\u6216\u8005\u5728\u4f60\u773c\u4e2d\uff0c\u8fd9\u5c0f\u4e8e\u4e0d\u662f\u5916\u4eba\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u54c8\uff0c\u54c8\uff0c\u542c\u4f60\u4eec\u8bf4\u5f97 \u751c\u751c\u871c\u871c\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u628a\u59d1\u5a18\u7684\u516b\u5b57\u2026\u2026\u201d\u5979\u60f3\u8bf4\u7684\u662f\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u628a\u59d1\u5a18\u7684\u516b\u5b57\u5f00\u7ed9\u4eba \u5bb6\uff1f\u201d \u3000\u3000\u90a3\u4e00\u8bdd\u5c1a\u672a\u8bf4\u5b8c\uff0c\u6b27\u9633\u4e8c\u5a18\u4fbf\u51b7\u7b11\u9053\uff1a\u201c\u9634\u5927\u59d0\uff0c\u4e09\u5e74\u4e4b\u671f\uff0c\u53ea\u5dee\u51e0\u5929\u4fbf\u6ee1\uff0c\u6211\u60f3\u63d0\u524d \u628a\u5a49\u513f\u9886\u56de\u53bb\u3002\u6211\u79bb\u5bb6\u7684\u65f6\u5019\uff0c\u4ee4\u90ce\u53ea\u5dee\u4e00\u5957\u622a\u8109\u638c\u6cd5\u5c1a\u672a\u5b66\u751f\uff0c\u6211\u5df2\u8bf7\u5a49\u513f\u7684\u7239\u4ee3\u6388\uff0c\u53eb \u4ed6\u4e00\u5b66\u4f1a\u4e86\u4fbf\u53ef\u56de\u5bb6\u3002\u60f3\u6765\u8fd9\u51e0\u5929\u4e5f\u8be5\u53ef\u4ee5\u5230\u4e86\u3002\u4f60\u8981\u4ed6\u5b66\u7684\uff0c\u4ed6\u90fd\u5df2\u5b66\u6210\u4e86\uff0c\u4e0d\u4fe1\uff0c\u4f60\u53ef \u4ee5\u8bd5\u4ed6\u3002\u597d\uff0c\u9634\u5927\u59d0\uff0c\u54b1\u4eec\u4e24\u5bb6\u7684\u4e8b\u5c31\u8fd9\u6837\u4ea4\u4ee3\u4e86\u3002\u5a49\u513f\uff0c\u4f60\u8fc7\u53bb\u8c22\u4f60\u5e08\u7236\u4e09\u5e74\u4f20\u827a\u4e4b\u6069\u3002\u201d \u3000\u3000\u539f\u6765\u5979\u4eec\u4e8c\u4eba\u4e43\u662f\u6613\u5b50\u5973\u800c\u6559\uff0c\u6b27\u9633\u4e8c\u5a18\u8981\u9634\u5723\u59d1\u4f20\u6388\u5979\u5973\u513f\u4f7f\u6bd2\u7684\u529f\u592b\uff0c\u9634\u5723\u59d1\u5219\u547d \u513f\u5b50\u53bb\u5b66\u6b27\u9633\u5bb6\u7684\u5bb6\u4f20\u51e0\u95e8\u7edd\u6280\uff0c\u8bf4\u597d\u4e86\u4ee5\u4e09\u5e74\u4e3a\u671f\u7684\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u63d0\u8d77\u4e86\u5979\u7684\u513f\u5b50\uff0c\u9634\u5723\u59d1\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u51db\uff0c\u6015\u6b27\u9633\u4e8c\u5a18\u5bf9\u5979\u7684\u513f\u5b50\u6709\u6240\u4e0d\u5229\uff0c \u56e0\u6b64\u5979\u672c\u6765\u60f3\u8bf4\u4e00\u4e9b\u51b7\u5632\u70ed\u8bbd\u7684\u8bdd\u7684\uff0c\u8fd9\u65f6\u4e5f\u4e0d\u6562\u8bf4\u4e86\u3002\u4f46\u8fd9\u53e3\u95f7\u6c14\u65e0\u53ef\u53d1\u6cc4\uff0c\u6240\u4ee5\u5f53\u6b27\u9633 \u5a49\u5411\u5979\u78d5\u5934\u65f6\uff0c\u5979\u5374\u4e0d\u613f\u53d7\u793c\uff0c\u907f\u8fc7\u4e00\u8fb9\uff0c\u51b7\u51b7\u8bf4\u9053\uff0c\u201c\u65e2\u7136\u5e08\u5f92\u4e4b\u60c5\u5df2\u4e86\uff0c\u6b64\u540e\u4e5f\u4e0d\u5fc5\u4ee5 \u5e08\u5f92\u76f8\u79f0\u4e86\u3002\u8fd9\u5927\u793c\u561b\uff0c\u6211\u5f53\u4e0d\u8d77\uff01\u201d \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u9053\uff1a\u201c\u90a3\u4e5f\u597d\u3002\u90a3\u4e48\u5c31\u5f53\u54b1\u4eec\u662f\u4f5c\u4e86\u4e00\u573a\u516c\u5e73\u7684\u4ea4\u6613\u5427\uff0c\u5f7c\u6b64\u90fd\u6ca1\u6709\u6b20\u8c01\u7684 \u60c5\u3002\u4f60\u4e0d\u8ba4\u5979\u4f5c\u5f92\u513f\uff0c\u6211\u4e5f\u4e0d\u6562\u5984\u5c45\u4ee4\u90ce\u7684\u5e08\u957f\u3002\u597d\uff0c\u8a00\u5c3d\u4e8e\u6b64\uff0c\u5a49\u513f\uff0c\u54b1\u4eec\u8d70\u5427\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u5fae\u7b11\u9053\uff0c\u201c\u597d\uff0c\u90a3\u4e48\u53c8\u4e86\u5374\u4e00\u6869\u4e86\u3002\u201d\u6b27\u9633\u4e8c\u5a18\u7b11\u9053\uff1a\u201c\u6211\u4eec\u4e0e\u6c5f\u76f8\u516c\u4e4b\u95f4\uff0c\u672c \u6765\u5c31\u6ca1\u7ed3\u8fc7\u6881\u4e8e\uff0c\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u4e86\u5374\u4e0d\u4e86\u5374\uff0c\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u4e8c\u5a18\u6709\u4e8b\uff0c\u8001\u592b\u4e5f\u4e0d\u4fbf \u7559\u5ba2\u4e86\u3002\u70e6\u4e8c\u5a18\u4ee3\u4e3a\u62dc\u4e0a\u5c0a\u592b\uff0c\u8001\u673d\u5f53\u5e74\u4e0d\u77e5\u662f\u4ed6\uff0c\u591a\u6709\u5f97\u7f6a\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u6b27\u9633\u4e8c\u5a18\u4fbf \u6717\u58f0\u7b11\u9053\uff0c\u201c\u8fd9\u4e9b\u9648\u5e74\u65e7\u5e10\uff0c\u8fd8\u63d0\u5b83\u5e72\u561b\uff1f\u534e\u8001\u524d\u8f88\u82e5\u662f\u6709\u7a7a\uff0c\u51e0\u65f6\u8bf7\u5230\u7ec8\u5357\u5c71\u4e00\u53d9\u3002\u6211\u4eec \u5728\u5c71\u4e0a\u4e5f\u57f9\u690d\u4e86\u51e0\u682a\u9887\u4e3a\u96be\u5f97\u7684\u836f\u8349\uff0c\u8bf7\u8001\u524d\u8f88\u4ee3\u6211\u4eec\u9274\u5b9a\u3001\u9274\u5b9a\u3002\u201d\u534e\u5929\u98ce\u62f1\u624b\u9053\uff1a\u201c\u597d \u8bf4\uff0c\u597d\u8bf4\uff0c\u5f85\u8fc7\u4e86\u5e74\uff0c\u8001\u673d\u518d\u4f1a\u62dc\u8c12\u5c0a\u592b\uff1b\u8bf7\u6055\u4e0d\u8fdc\u9001\u4e86\u3002\u201d \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u643a\u4e86\u4e24\u4e2a\u5973\u513f\uff0c\u8d70\u5230\u95e8\u53e3\uff0c\u5ffd\u7136\u6b62\u6b65\uff0c\u53c8\u56de\u8fc7\u5934\u6765\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u6c5f\u76f8\u516c\uff0c\u82e5\u7136 \u51e0\u65f6\u6709\u7a7a\uff0c\u4e5f\u8bf7\u5230\u7ec8\u5357\u5c71\u6765\u901b\u901b\u3002\u201d\u6c5f\u6d77\u5929\u773c\u5149\u4e00\u77a5\uff0c\u53ea\u89c1\u6b27\u9633\u5a49\u7684\u773c\u5149\u4e5f\u6b63\u5411\u7740\u4ed6\u5c04\u6765\uff0c \u6c5f\u6d77\u5929\u9762\u4e0a\u4e00\u7ea2\uff1a\u4f4e\u4e0b\u4e86\u5934\uff0c\u542b\u7cca\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4e86\uff0c\u6211\u3001\u6211\u6709\u8bb8\u591a\u4e8b\u60c5\uff0c\u4e0d\u77e5\u4f55\u65f6\u59cb\u5f97\u7a7a \u6687\u3002\u201d\u9634\u5723\u59d1\u5728\u4e00\u65c1\u563f\u563f\u51b7\u7b11\u3002\u6c5f\u6d77\u5929\u7684\u9762\u4e0a\u66f4\u7ea2\u4e86\u3002\u6b27\u9633\u4e8c\u5a18\u4e5f\u4e0d\u7406\uff0c\u643a\u4e86\u4e24\u4e2a\u5973\u513f\uff0c\u626c \u957f\u800c\u53bb\u3002 \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u9634\u8001\u524d\u8f88\uff0c\u8f6e\u5230\u4f60\u4e86\u3002\u4f60\u4e0e\u6c5f\u5c0f\u4fa0\u53c8\u6709\u4ec0\u4e48\u8fc7\u4e0b\u53bb\u7684\u4e8b\u60c5\uff0c\u4e3a\u4f55\u4e24\u6b21\u4e09\u756a \u8981\u5c06\u4ed6\u7f6e\u4e8e\u6b7b\u5730\uff1f\u201d\u9634\u5723\u59d1\u51b7\u7b11\u9053\uff1a\u201c\u73b0\u5728\u6211\u662f\u5b64\u638c\u96be\u9e23\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u6e29\u9053\uff1a\u201c\u4ef2\u5e2e\u4e3b\u4e0e\u6211\u90fd\u7edd\u4e0d\u662f\u4ee5\u4f17\u6b3a\u5be1\u7684\u4eba\uff0c\u53ea\u8981\u4f60\u8bf4\u5f97\u6709\u7406\uff0c\u6211\u53eb\u6c5f\u5c0f\u4fa0\u5411\u4f60 \u8d54\u7f6a\u3002\u201d\u9634\u5723\u59d1\u9053\uff1a\u201c\u6211\u5e73\u751f\u4ece\u6765\u672a\u66fe\u548c\u4eba\u5bb6\u8bb2\u8fc7\u9053\u7406\uff0c\u5c24\u5176\u662f\u8fd9\u6869\u4e8b\u60c5\uff0c\u8981\u8bb2\u4e5f\u65e0\u4ece\u8bb2 \u8d77\u3002\u4f60\u4eec\u8981\u95f4\u9053\u7406\u4e48\uff0c\u5c31\u8bf7\u5411\u91d1\u9e70\u5bab\u7684\u4e3b\u4eba\u95ee\u53bb\u3002\u201d \u3000\u3000\u534e\u65e0\u98ce\u6014\u4e86\u4e00\u6014\uff0c\u95f4\u9053\uff1a\u201c\u6c5f\u5c0f\u4fa0\uff0c\u96be\u9053\u4f60\u4e5f\u548c\u91d1\u9e70\u5bab\u4e3b\u4eba\u6709\u4ec0\u4e48\u8fc7\u8282\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a \u201c\u6211\u6839\u672c\u5c31\u4e0d\u77e5\u91d1\u9e70\u5bab\u7684\u4e3b\u4eba\u662f\u8c01\uff0c\u4e0d\u8fc7\uff0c\u6211\u66fe\u4ee3\u9099\u5c71\u6d3e\u7684\u638c\u95e8\u8c37\u4e4b\u534e\u5973\u4fa0\u63a5\u4e86\u4ed6\u7684\u8bf7\u5e16\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u7b11\u9053\uff0c\u201c\u8fd9\u5c31\u597d\u529e\u4e86\uff0c\u6211\u4e5f\u63a5\u4e86\u91d1\u9e70\u5bab\u4e3b\u4eba\u7684\u8bf7\u5e16\u3002\u4eca\u5e74\u4e2d\u79cb\u8282\uff0c\u6211\u548c\u6c5f\u5c0f\u4fa0\u90fd \u8981\u8d74\u4f1a\u7684\u3002\u5230\u65f6\u54b1\u4eec\u5f53\u9762\u95ee\u4ed6\u597d\u4e86\u3002\u9634\u8001\u524d\u8f88\uff0c\u4f60\u8bf7\u4fbf\u5427\u3002\u201d \u3000\u3000\u9634\u5723\u59d1\u7ffb\u8d77\u4e00\u53cc\u767d\u60e8\u4fee\u7684\u773c\u73e0\u3001\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u8001\u5a46\u5b50\u60c5\u77e5\u4e0d\u662f\u4f60\u4eec\u7684\u5bf9\u624b\uff0c\u4f46\u65e2\u7136\u5230 \u6b64\uff0c\u4e5f\u603b\u4e0d\u80fd\u8fd9\u4e48\u5bb9\u6613\u5c31\u56de\u53bb\uff0c\u591a\u5c11\u4e5f\u5f97\u8bf7\u505a\u4e3b\u4eba\u7684\u6307\u6559\u6307\u6559\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u6012\u9053\uff1a\u201d\u597d\u5427\uff0c\u90a3\u5c31\u8ba9\u6211\u89c1\u8bc6\u89c1\u8bc6\u4f60\u4eec\u4e03\u9634\u6559\u7684\u4f7f\u6bd2\u529f\u592b\uff01\u201d \u3000\u3000\u9634\u5723\u59d1\u9053\uff1a\u201c\u4ef2\u5e2e\u4e3b\uff0c\u4f60\u82e5\u662f\u6709\u5fc3\u6307\u6559\u7684\u6d3b\uff0c\u54b1\u4eec\u6539\u53e3\u518d\u7ea6\uff0c\u4eca\u65e5\u6211\u4e43\u662f\u8981\u89c1\u8bc6\u6b64\u95f4\u4e3b \u4eba\u534e\u5c71\u533b\u9690\u7684\u6700\u62ff\u624b\u672c\u9886\uff01\u201d \u3000\u3000\u534e\u65e0\u98ce\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u8fdc\u6765\u662f\u5ba2\uff0c\u4e3b\u4e0d\u6b3a\u5ba2\uff0c\u4fbf\u8bf7\u4f60\u5212\u51fa\u9053\u513f\u3002\u201d\u9634\u5723\u7ad9\u9634\u607b\u607b\u5730 \u9053\uff0c\u201c\u4f60\u4e00\u51c6\u4f9d\u7167\u6211\u5212\u7684\u9053\u51e0\u8d70\u5417\uff1f\u201d\u534e\u5929\u98ce\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u534e\u67d0\u5c82\u662f\u8a00\u9762\u65e0\u4fe1\u4e4b\u4eba\uff1f\u4f60\u8981\u8f83 \u91cf\u54ea\u79cd\u529f\u592b\u3002\u53ea\u7ba1\u8bf4\u6765\uff0c\u6211\u90fd\u4e00\u51c6\u5949\u966a\u4fbf\u662f\u3002\u201d \u3000\u3000\u9634\u5723\u59d1\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u521a\u624d\u8bf4\u8fc7\uff0c\u8981\u89c1\u8bc6\u4f60\u6700\u62ff\u624b\u7684\u672c\u9886\uff0c\u534e\u5929\u98ce\uff0c\u4f60\u7684\u6b66\u529f\u7684\u786e\u9ad8 \u5f3a\uff0c\u6211\u8001\u5a46\u5b50\u81ea\u8ba4\u4e0d\u662f\u4f60\u7684\u5bf9\u624b\u3002\u4f46\u8bf7\u6055\u6211\u8bf4\u53e5\u4e0d\u77e5\u8f7b\u91cd\u7684\u8bdd\uff0c\u4f60\u7684\u6b66\u529f\u603b\u8fd8\u4e0d\u662f\u5929\u4e0b\u7b2c\u4e00 \u5427\u3002\u6240\u4ee5\uff0c\u6211\u8001\u5a46\u5b50\u8bf4\u8981\u89c1\u8bc6\u4f60\u6700\u62ff\u624b\u7684\u672c\u9886\uff0c\u5f53\u7136\u5e76\u975e\u662f\u8981\u5411\u4f60\u9886\u6559\u6b66\u529f\uff0c\u800c\u662f\u8981\u9886\u6559\u4f60 \u7684\u533b\u672f\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u5fae\u7b11\u9053\uff1a\u201c\u6211\u7684\u533b\u672f\u4e5f\u4e0d\u6562\u8bf4\u662f\u5929\u4e0b\u7b2c\u4e00\uff0c\u4f46\u6bd4\u8bd5\u533b\u672f\u603b\u8981\u6bd4\u6bd4\u8bd5\u6b66\u529f\u5c11\u4f24\u548c \u6c14\uff0c\u4f60\u8981\u600e\u6837\u6bd4\u8ba8\u5462\uff1f\u201d\u9634\u5723\u59d1\u9053\uff1a\u201c\u6211\u8001\u5a46\u5b50\u4e0d\u61c2\u533b\u672f\uff0c\u6211\u6240\u5b66\u7684\u4e0e\u4f60\u521a\u521a\u76f8\u53cd\uff0c\u4f60\u662f\u7528 \u533b\u672f\u6551\u4eba\uff0c\u6211\u662f\u7528\u6bd2\u836f\u5bb3\u4eba\u7684\u3002\u6240\u4ee5\u6211\u4e0d\u662f\u4e0e\u4f60\u6bd4\u8bd5\u533b\u672f\uff0c\u90a3\u2018\u6bd4\u2019\u5b57\u53ef\u4ee5\u53bb\u6389\uff0c\u6211\u53ea\u662f\u60f3 \u8bd5\u8bd5\u4f60\u7684\u533b\u672f\u3002\u76f4\u622a\u7684\u8bf4\uff0c\u5c31\u662f\u8981\u8bd5\u4f60\u7684\u89e3\u6bd2\u529f\u5927\uff0c\u770b\u770b\u662f\u6211\u8001\u5a46\u5b50\u4f7f\u6bd2\u7684\u529f\u592b\u5389\u5bb3\u5462\uff0c\u8fd8 \u662f\u4f60\u7684\u89e3\u6bd2\u529f\u592b\u9ad8\u660e\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u5c31\u8ba9\u6211\u8bd5\u8bd5\uff0c\u4f60\u7528\u54ea\u79cd\u6bd2\u836f\uff0c\u6211\u5f53\u7740\u4f60\u7684\u9762\u5c06\u5b83\u541e\u4e0b\u3002\u8981\u662f\u6211\u89e3 \u6bd2\u529f\u592b\u4e0d\u591f\uff0c\u6b7b\u800c\u65e0\u6028\u3002\u8fd9\u603b\u6210\u4e86\u5427\uff1f\u201d \u3000\u3000\u9634\u5723\u59d1\u6447\u5934\u9053\uff0c\u201c\u4f60\u8bf4\u8fc7\u8ba9\u6211\u5212\u51fa\u9053\u513f\u7684\u3002\u7167\u4f60\u8bf4\u7684\u53bb\u505a\uff0c\u8fd9\u4e0d\u53d8\u6210\u4e86\u4f60\u5212\u51fa\u9053\u513f\u5f3a\u6211 \u8d70\u5417\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u6291\u7740\u6012\u6c14\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u53ea\u4e0d\u8fc7\u60f3\u6311\u4e00\u6837\u6700\u96be\u7684\u6765\u8bd5\u7f62\u4e86\u3002\u4f60\u65e2\u7136\u6709\u522b\u7684\u529e\u6cd5\uff0c\u6211 \u4e5f\u5c31\u7167\u529e\u4fbf\u662f\u3002\u201d \u3000\u3000\u9634\u5723\u59d1\u53cc\u76fe\u4e00\u7ad6\uff0c\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u5c31\u542c\u6211\u8bf4\u5427\u3002\u8001\u5a46\u5b50\u8fd9\u53cc\u624b\u638c\u662f\u7528\u4e00\u5343\u6761\u4e0d\u540c\u79cd\u7c7b\u7684 \u6bd2\u86c7\u7684\u6bd2\u6db2\u70bc\u8fc7\u7684\uff0c\u53ef\u4ee5\u968f\u5fc3\u6240\u6b32\uff0c\u4ee4\u53d7\u638c\u4e2d\u6bd2\u4e4b\u4eba\uff0c\u8f7b\u8005\u5927\u75c5\u4e00\u573a\uff0c\u91cd\u8005\u5219\u5f62\u9500\u9aa8\u6bc1\u3002\u534a \u5929\u98ce\uff0c\u8001\u5a46\u5b50\u7528\u6bd2\u638c\u4f24\u4e86\u7684\uff0c\u4f60\u80fd\u591f\u5728\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u5c06\u4ed6\u6551\u56de\u6765\u4e48\uff1f\u201d\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u9634\u51b7\u7684 \u773c\u5149\u72e0\u72e0\u5730\u76ef\u7740\u6c5f\u6d77\u5929\uff0c\u4e0d\u8a00\u53ef\u55bb\uff0c\u5979\u662f\u60f3\u7528\u6c5f\u6d77\u5929\u6765\u8bd5\u5979\u7684\u6bd2\u638c\u4e86\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u52c3\u7136\u5927\u6012\uff0c\u6b63\u60f3\u65a5\u5979\u5c82\u6709\u6b64\u7406\uff0c\u5374\u89c1\u534e\u5929\u98ce\u5df2\u5728\u54c8\u54c8\u5927\u7b11\uff0c\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u9634\u5723 \u59d1\uff0c\u4f60\u4eec\u5230\u6b64\u5927\u7ea6\u672a\u591f\u4e00\u4e2a\u65f6\u8fb0\u5427\uff1f\u201d \u3000\u3000\u9634\u5723\u59d1\u83ab\u540d\u5176\u5999\uff0c\u770b\u770b\u65e5\u5f71\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u8fd8\u4e0d\u591f\u4e00\u4e2a\u65f6\u8fb0\u3002\u4f46\u8fd9\u4e0e\u6211\u8981\u8bd5\u4f60\u672c\u9886\u4e4b\u4e8b\u6709 \u4f55\u76f8\u5e72\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u7b11\u9053\uff1a\u201c\u4f60\u6240\u8981\u8bd5\u7684\uff0c\u6211\u65e9\u5df2\u5982\u547d\u505a\u5230\u4e86\uff01\u558f\uff0c\u4f60\u8ddf\u6211\u6765\u770b\u5427\uff01\u201d\u6b64\u541d\u4e00\u51fa\u3002\u4e0d \u4f46\u9634\u5723\u59d1\u83ab\u540d\u5176\u5999\uff0c\u8fde\u4ef2\u957f\u7edf\u4e0e\u6c5f\u6d77\u5929\u4e5f\u7cca\u6d82\u4e86\u3002\u9634\u5723\u59d1\u5230\u6b64\u4e4b\u540e\uff0c\u5e76\u672a\u66fe\u4f24\u8fc7\u4eba\uff0c\u4e0d\u77e5\u534e \u65e0\u98ce\u4f55\u4ee5\u7adf\u8bf4\u5df2\u5b8c\u6210\u6240\u547d\uff1f \u3000\u3000\u534e\u592d\u98ce\u8fb9\u8d70\u8fb9\u8bf4\uff0c\u963b\u5723\u59d1\u6ee1\u8179\u7591\u56e2\uff0c\u53ea\u597d\u8ddf\u4ed6\u8d70\u51fa\u5c4b\u5916\uff0c\u53ea\u89c1\u534e\u5929\u98ce\u6307\u7740\u4e00\u68f5\u6811\u9053\uff1a \u201c\u9634\u5723\u59d1\uff0c\u4f60\u77a7\uff0c\u8fd9\u4e0d\u662f\u88ab\u4f60\u6bd2\u638c\u6240\u4f24\u4e86\u7684\u6843\u6811\u4e48\uff1f\u533a\u533a\u4e0d\u624d\uff0c\u5df2\u5c06\u5b83\u533b\u597d\uff0c\u4ee4\u5b83\u590d\u6d3b\u4e86\u3002\u201d \u3000\u3000\u90a3\u68f5\u6843\u634c\u521a\u624d\u5df2\u7ecf\u6811\u53f6\u53d1\u9ec4\uff0c\u82b1\u6735\u67af\u840e\u4e86\u7684\uff0c\u4f46\u73b0\u5728\u7ea2\u82b1\u7eff\u53f6\uff0c\u5374\u662f\u4e00\u7247\u751f\u673a\u8302\u76db\u7684\u6c14 \u8c61\u3002\u8981\u4e0d\u662f\u6811\u5e72\u4e0a\u8fd8\u7559\u4e0b\u9634\u5723\u59d1\u6240\u6293\u7684\u6293\u75d5\uff0c\u771f\u4ee4\u4eba\u4e0d\u6562\u76f8\u4fe1\u8fd9\u5c31\u662f\u521a\u624d\u5df2\u7ecf\u6854\u840e\u4e86\u7684\u90a3\u68f5 \u6811\u3002 \u3000\u3000\u9634\u5723\u59d1\u76ee\u77aa\u53e3\u5446\uff0c\u4ef2\u957f\u7edf\u54c8\u54c8\u5927\u7b11\u9053\uff0c\u201c\u5999\u54c9\uff0c\u5999\u54c9\uff01\u771f\u662f\u533b\u672f\u901a\u795e\uff01\u6211\u521a\u624d\u53ea\u89c1\u4f60\u5c06 \u6811\u679d\u6276\u4e86\u4e00\uff0c\u5374\u539f\u6765\u4f60\u5df2\u7ecf\u5728\u6697\u4e2d\u65bd\u5c55\u672c\u9886\u4e86\u3002\u9634\u5723\u59d1\uff0c\u8fd9\u4f60\u603b\u6ca1\u8bdd\u8bf4\u4e86\u5427\uff1f\u533b\u6811\u8981\u6bd4\u533b\u4eba \u8fd8\u96be\u4e0a\u5341\u500d\uff0c\u4f60\u6211\u90fd\u662f\u884c\u5bb6\uff0c\u8fd9\u4e5f\u7528\u4e0d\u7740\u7ec6\u8bf4\u4e86\uff0c\u201d \u3000\u3000\u9634\u5723\u59d1\u5904\u6b64\u5883\u5730\uff0c\u4e5f\u7684\u786e\u5df2\u662f\u65e0\u8bdd\u53ef\u8bf4\u3002\u7b2c\u4e00\u3001\u5979\u521a\u624d\u51fa\u8c79\u9898\u76ee\uff0c\u53ea\u662f\u8981\u534e\u5929\u98ce\u5c06\u5979\u7528 \u6bd2\u638c\u201c\u4f24\u4e86\u7684\u201d\uff0c\u5728\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u5185\u6551\u56de\u6765\uff0c\u867d\u7136\u5979\u5fc3\u76ee\u4e2d\u6307\u7684\u662f\u201c\u4f24\u4e86\u7684\u4eba\u201d\uff0c\u4f46\u5979\u6240\u8bf4\u7684 \u8bdd\uff0c\u4e00\u65f6\u5306\u5fd9\uff0c\u5374\u4e95\u6ca1\u6709\u6307\u660e\u662f\u4eba\u662f\u7269\uff0c\u6240\u4ee5\u534e\u5929\u98ce\u533b\u6d3b\u4e86\u6811\uff0c\u4e5f\u7b97\u5f97\u662f\u4ea4\u4e86\u5377\u3002 \u3000\u3000\u7b2c\u4e8c\u3001\u533b\u6811\u7684\u786e\u662f\u8981\u6bd4\u533b\u4eba\u96be\u5f97\u591a\u3002\u4eba\u7684\u751f\u547d\u529b\u6bd4\u6811\u5f3a\uff0c\u5c24\u5176\u662f\u5185\u529f\u6709\u6839\u5e95\u7684\u4eba\uff0c\u8c46\u5177 \u6709\u6297\u6bd2\u7684\u672c\u9886\u3002\u5373\u4ee5\u6c5f\u6d77\u5929\u800c\u8bba\uff0c\u9634\u5723\u59d1\u5c31\u6ca1\u6709\u628a\u63e1\u80fd\u7528\u6bd2\u638c\u5c06\u4ed6\u5bb3\u6b7b\u3002\u5979\u7684\u5e0c\u671b\u4e5f\u4e0d\u8fc7\u662f \u4ee4\u6c5f\u6d77\u5929\u5403\u70b9\u82e6\u5934\uff0c\u81f3\u591a\u6210\u4e3a\u6b8b\u5e9f\u800c\u5df2\u3002 \u3000\u3000\u9634\u5723\u59d1\u672c\u6765\u662f\u6043\u7740\u81ea\u5df1\u4f7f\u6bd2\u7684\u672c\u9886\u5389\u5bb3\uff0c\u8fd9\u624d\u6562\u53d1\u6a2a\u7684\uff0c\u4f46\u73b0\u5728\u5979\u6700\u5389\u5bb3\u7684\u6bd2\u638c\u90fd\u5df2\u7ed9 \u4eba\u7834\u89e3\u4e86\uff0c\u4ea6\u5373\u662f\u8bf4\uff0c\u5728\u534e\u65e0\u98ce\u9762\u524d\uff0c\u5979\u5df2\u6ca1\u6709\u4e00\u6837\u672c\u9886\u53ef\u4ee5\u62ff\u5f97\u51fa\u6765\u5a01\u80c1\u4eba\u5bb6\uff0c\u82e5\u518d\u61c2\u86ee \u65e0\u7406\uff0c\u7ee7\u7eed\u7ea0\u7f20\uff0c\u90a3\u53ea\u6709\u81ea\u8ba8\u82e6\u5403\u800c\u5df2\uff0c\u5979\u601d\u5ff5\u53ca\u6b64\uff0c\u6c14\u7130\u5168\u6d88\uff0c\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u533b\u672f\u901a\u795e\uff0c \u4f69\u670d\uff0c\u4f69\u670d\uff1a\u91d1\u9e70\u5bab\u4f1a\u4e0a\u518d\u89c1\u5427\uff01\u201d\u534e\u5927\u98ce\u7b11\u9053\uff1a\u201c\u540e\u4f1a\u6709\u671f\uff0c\u6055\u4e0d\u8fdc\u9001\u3002\u201d \u3000\u3000\u56de\u5230\u9662\u5b50\uff0c\u4ef2\u957f\u7edf\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u540d\u8336\u7f8e\u70b9\uff0c\u4ed6\u4eec\u90fd\u6ca1\u6709\u52a8\u7528\uff0c\u6211\u53eb\u5316\u5b50\u767d\u5403\u60ef\u4e86\uff0c \u53ef\u4e0d\u5ba2\u6c14\uff0c\u8981\u5927\u56bc\u4e86\u3002\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u7239\uff0c\u6211\u521a\u624d\u771f\u4e3a\u4f60\u4eec\u62c5\u5fc3\uff0c\u62c5\u5fc3\u4f60\u7ed9\u90a3\u6076\u5a46\u5b50\u96be\u5012\u3002 \u8981\u662f\u5979\u8981\u62ff\u6c5f\u76f8\u516c\u6765\u8bd5\u6bd2\u638c\u7684\u8bdd\uff0c\u90a3\u5c31\u4e0d\u597d\u5e94\u4ed8\u4e86\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u56bc\u4e86\u4e00\u53e3\u7cd5\u997c\uff0c\u7b11\u9053\uff1a\u201c\u771f\u662f\u4eba\u7ed3\u4eba\u7f18\uff0c\u597d\u4f84\u5973\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u66ff\u6211\u62c5\u5fc3\u5462\uff1f\u6211\u521a \u624d\u4e5f\u66fe\u5192\u4e86\u8eab\u53d7\u4fee\u7f57\u9634\u715e\u529f\u4e4b\u9669\uff0c\u53bb\u62c9\u5f00\u4e86\u90a3\u5389\u526f\u6559\u4e3b\u5440\uff01\u201d\u534e\u4e91\u78a7\u55d4\u9053\uff1a\u201c\u8c01\u4e0d\u77e5\u9053\u4f60\u6b66 \u529f\u9ad8\u5f3a\uff0c\u6211\u4f55\u987b\u4e3a\u4f60\u62c5\u5fc3\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u4eba\u5bb6\u6c5f\u76f8\u516c\u662f\u91d1\u5927\u4fa0\u7684\u552f\u4e00\u4f20\u4eba\uff0c\u4f60\u6562\u8bf4\u4ed6\u7684\u529f\u592b\u4e0d\u597d\u5417\uff1f\u201d\u6c5f\u6d77\u5929\u8fd8 \u672a\u542c\u51fa\u4ef2\u957f\u7edf\u7684\u8bdd\u4e2d\u6709\u8bdd\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u6211\u600e\u80fd\u8ddf\u4ef2\u5e2e\u4e3b\u76f8\u6bd4\u3002\u4eca\u65e5\u5e78\u9003\u6b64\u96be\uff0c\u5168\u4ed7\u534e\u8001\u524d \u8f88\u548c\u4ef2\u5e2e\u4e3b\u4e24\u4f4d\u9f0e\u529b\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8fd9\u6837\u4e00\u672c\u6b63\u7ecf\u4ed6\u8bf4\u8bdd\uff0c\u4ef2\u957f\u7edf\u5012\u4e0d\u597d\u610f\u601d\u518d\u5347\u73a9\u7b11\u4e86\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u8bf4\u771f\u7684\uff0c\u6211 \u4e5f\u5728\u62c5\u5fc3\u5462\u3002\u4eca\u5929\u8fd9\u51e0\u4e2a\u9b54\u5934\u8054\u8882\u800c\u6765\uff0c\u6211\u4ee5\u4e3a\u603b\u96be\u514d\u8981\u6709\u4e00\u573a\u6fc0\u6597\u7684\uff0c\u54ea\u77e5\u7adf\u4e00\u4e2a\u4e2a\u6004\u65d7 \u606f\u9f13\u800c\u53bb\uff0c\u5c24\u5176\u4f60\u8fd9\u6837\u6253\u53d1\u4e86\u90a3\u9634\u8001\u5927\u5a46\uff0c\u66f4\u662f\u610f\u6599\u4e0d\u5230\uff0c\u5999\u4e0d\u53ef\u8a00\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u5ffd\u5730\u76b1\u8d77\u7709\u5934\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u90a3\u6076\u5a46\u4e8e\u662f\u8272\u5389\u5185\u834f\uff0c\u6211\u65e9\u7b97\u51c6\u5979\u4f1a\u77e5\u96be\u800c\u9000\u7684\u3002\u6700 \u4ee4\u6211\u5947\u602a\u7684\u5374\u662f\u6b27\u9633\u4e8c\u5a18\uff0c\u5979\u4eec\u592b\u5987\u90fd\u662f\u975e\u5e38\u9634\u72e0\u7684\u4eba\uff0c\u8bf4\u5230\u5389\u5bb3\uff0c\u5979\u5b9e\u5728\u5728\u90a3\u9634\u5723\u59d1\u4e4b \u4e0a\uff0c\u5979\u4eca\u5929\u7adf\u7136\u8fd9\u6837\u597d\u8bf4\u8bdd\uff0c\u5927\u51fa\u6211\u7684\u610f\u5916\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff0c\u201c\u5979\u4e08\u592b\u66fe\u8d25\u5728\u4f60\u7684\u624b\u4e0b\uff0c\u5979\u5f53\u7136\u8981\u89c1\u98ce\u8f6c\u8235\u4e86\u3002\u201d\u534e\u5929\u98ce\u53ea\u662f\u6447\u5934\uff0c\u5374\u4e0d \u8a00\u8bed\u3002\u534e\u4e91\u78a7\u7b11\u9053\uff1a\u201c\u6211\u770b\u5979\u4eec\u6bcd\u5973\u662f\u771f\u7684\u611f\u8c22\u6c5f\u76f8\u516c\u3002\u6c5f\u76f8\u516c\uff0c\u90a3\u4f4d\u6b27\u9633\u59d1\u5a18\u5bf9\u4f60\u4e5f\u771f\u662f \u597d\u5f97\u5f88\u554a\uff01\u4f60\u521a\u624d\u4e5f\u592a\u4e0d\u61c2\u8bf4\u8bdd\u4e86\uff0c\u4eba\u5bb6\u9080\u4f60\u5bb6\u91cc\u53bb\uff0c\u4f60\u6700\u5c11\u4e5f\u5f97\u548c\u4eba\u5bb6\u8bf4\u4e0a\u51e0\u53e5\u5ba2\u6c14\u7684\u8bdd \u5440\uff0c\u600e\u4e48\u4e00\u53e3\u5c31\u56de\u7edd\u4e86\u3002\u201d \u3000\u3000\u8fd9\u56de\u8f6e\u5230\u6c5f\u6d77\u5929\u7f9e\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u534e\u4e91\u78a7\u63a9\u53e3\u5077\u7b11\u3002\u4ef2\u957f\u7edf\u5ffd\u5730\u4f38\u4e86\u4e00\u4e2a\u61d2\u8170\uff0c\u81ea\u8a00\u81ea\u8bed \u9053\uff1a\u201c\u54c8\uff0c\u8fd9\u676f\u8336\u7684\u5473\u9053\u600e\u4e48\u6709\u70b9\u9178\u5462\uff01\u201d\u534e\u4e91\u78a7\u767b\u65f6\u7b11\u4e0d\u51fa\u6765\uff0c\u5927\u53d1\u5a07\u55d4\u9053\uff1a\u201c\u5473\u9053\u4e0d \u597d\uff0c\u4f60\u5c31\u522b\u559d\uff01\u201d\u4ef2\u957f\u7edf\u4e00\u672c\u6b63\u7ecf\u5730\u9053\uff0c\u201c\u4e0d\uff0c\u662f\u8981\u6709\u4e00\u70b9\u513f\u9178\uff0c\u624d\u591f\u5473\u513f\uff01\u201d \u3000\u3000\u534e\u5929\u98ce\u4ecd\u662f\u9ed8\u7136\u4e0d\u8bed\uff0c\u5982\u6709\u6240\u601d\u3002\u4ed6\u4e5f\u65e9\u5df2\u770b\u51fa\u90a3\u6b27\u9633\u5a49\u5bf9\u6c5f\u6d77\u5929\u662f\u6709\u70b9\u60c5\u610f\uff0c\u4f46\u4ed6\u4ece \u6c5f\u6e56\u540c\u9053\u7684\u53e3\u4e2d\uff0c\u65e9\u5df2\u6df1\u77e5\u6b27\u9633\u4e8c\u5a18\u7684\u4e3a\u4eba\uff0c\u5979\u8d8a\u662f\u966a\u7740\u7b11\u8138\u8bf4\u8bdd\u3002\u809a\u5b50\u91cc\u6240\u60f3\u7684\u8be1\u8ba1\u5c31\u8d8a \u6bd2\u8fa3\uff0c\u5373\u7b97\u4e3a\u4e86\u5973\u513f\u7684\u7f18\u6545\uff0c\u5979\u4e0d\u60f3\u4e0e\u6c5f\u6d77\u5929\u4e3a\u96be\uff0c\u4e5f\u51b3\u4e0d\u4f1a\u8fd9\u6837\u4f4e\u58f0\u4e0b\u6c14\u7684\u3002\u5c24\u5176\u81ea\u5df1\u4e0e \u5979\u7684\u4e08\u592b\u7ed3\u6709\u6881\u5b50\uff0c\u5979\u5bf9\u81ea\u5df1\uff0c\u4e5f\u51b3\u4e0d\u4f1a\u5982\u5979\u6240\u8bf4\u7684\u201c\u9648\u5e74\u65e7\u5e10\uff0c\u4e00\u7b14\u52fe\u6d88\u201d\u3002\u56e0\u4e3a\u4ed6\u4eec\u592b \u5987\uff0c\u7edd\u5bf9\u4e0d\u662f\u8fd9\u6837\u80f8\u895f\u5bbd\u5e7f\u7684\u4eba\uff0c\u534e\u5929\u98ce\u5fc3\u60f3\uff1a\u201c\u4eca\u540e\u6050\u6015\u66f4\u8981\u7740\u610f\u63d0\u9632\u7ec8\u5357\u5c71\u6b27\u9633\u5bb6\u7684\u4eba \u4e86\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u89c1\u534e\u5929\u98ce\u6c89\u541f\u4e0d\u8bed\uff0c\u5374\u60f3\u5230\u53e6\u4e00\u8fb9\u53bb\uff0c\u4ee5\u4e3a\u4ed6\u662f\u4e3a\u4e86\u5973\u513f\u800c\u62c5\u5fc3\u4e8b\uff0c\u5f53\u4e0b\uff0c\u5c06\u8bdd \u9898\u5f15\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u5c0f\u4fa0\uff0c\u4f60\u4e5f\u662f\u8981\u5230\u91d1\u9e70\u5bab\u53bb\u5417\uff1f\u6b63\u597d\u4e0e\u534e\u8001\u524d\u8f88\u540c\u884c\u3002\u4e91\u78a7\uff0c\u4f60\u4e5f\u6b63\u53ef \u4ee5\u8d81\u6b64\u673a\u4f1a\uff0c\u8ddf\u4f60\u7239\u7239\u53bb\u5f00\u5f00\u773c\u754c\u3002\u201d\u8fd9\u56de\uff0c\u4ef2\u957f\u7edf\u4e00\u70b9\u4e0d\u7528\u8bf4\u7b11\u7684\u53e3\u543b\uff0c\u4ed6\u662f\u6709\u5fc3\u64ae\u5408 \u6c5f\u3001\u534e\u4e8c\u4eba\uff0c\u8ba9\u4ed6\u4eec\u4e00\u8def\u540c\u884c\uff0c\u597d\u591a\u4e00\u4e9b\u4eb2\u8fd1\u7684\u673a\u4f1a\u7684\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u6b63\u8981\u8bf7\u95f4\u534e\u8001\u524d\u8f88\uff0c\u90a3\u91d1\u9e70\u5bab\u4e3b\u4eba\u7a76\u7adf\u662f\u600e\u6837\u4e00\u4e2a\u4eba\u7269\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u4f60\u5148\u542c\u6211\u8bb2\u51e0\u4e2a\u6545\u4e8b\u3002\u963f\u5c14\u6cf0\u5c71\u5c71\u4e0b\uff0c\u6709\u4e00\u4e2a\u53eb\u4f5c\u9a6c\u8428\u513f\u7684\u5c0f\u56fd\uff0c\u8fd9\u56fd\u5bb6 \u7684\u56fd\u738b\u5728\u5341\u4e8c\u5e74\u524d\u88ab\u4ed6\u624b\u4e0b\u7684\u5927\u5c06\u6240\u7be1\u4f4d\uff0c\u56fd\u738b\u738b\u540e\u5747\u5df2\u906d\u5bb3\uff0c\u4f46\u56fd\u738b\u7684\u4e00\u5b50\u4e00\u5973\u5374\u4e0d\u77e5\u4e0b \u843d\u3002\u2026\u2026\u201d\u521a\u8bf4\u5230\u8fd9\u91cc\uff0c\u6c5f\u6d77\u5929\u4fbf\u7981\u4e0d\u4f4f\u5fc3\u5934\u4e00\u52a8\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u66fe\u542c\u5e08\u7236\u8bf4\u8fc7\u9a6c\u8428\u513f\u56fd\u7684\u6545\u4e8b\uff0c\u5e76\u4e14\u6000\u7591\u8c37\u4e2d\u83b2\u4fbf\u662f\u9a6c\u8428\u513f\u56fd\u7684\u516c\u4e3b\uff0c\u56e0\u6b64\u5fc3\u4e2d \u4e00\u52a8\uff0c\u5206\u5916\u7559\u795e\u3002 \u3000\u3000\u53ea\u542c\u5f97\u534e\u5929\u98ce\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u9a6c\u8428\u513f\u56fd\u7684\u524d\u738b\u672c\u6765\u662f\u4f4d\u6770\u51fa\u7684\u4eba\u7269\uff0c\u636e\u8bf4\u5728\u6b66\u5b66\u4e0a\u4e5f\u6709\u5f88 \u7cbe\u6df1\u7684\u9020\u8be3\uff0c\u6781\u559c\u4e0e\u6b66\u6797\u4eba\u571f\u7ed3\u7eb3\uff0c\u751a\u81f3\u6709\u51e0\u4f4d\u4e2d\u539f\u7684\u6b66\u6797\u540d\u5bbf\uff0c\u4e5f\u66fe\u505a\u8fc7\u4ed6\u7684\u4e0a\u5bbe\u3002\u53ea\u56e0 \u540e\u6765\u5e74\u7eaa\u8001\u4e86\uff0c\u758f\u4e8e\u9632\u8303\uff0c\u7adf\u7ed9\u4ed6\u7684\u5fc3\u8179\u5927\u5c06\u8c0b\u6740\uff0c\u7be1\u593a\u4e86\u4ed6\u7684\u6c5f\u5c71\u3002\u4ed6\u7684\u90a3\u5bf9\u5b50\u5973\u9003\u5728\u4f55 \u65b9\uff0c\u65e0\u4eba\u77e5\u9053\u3002 \u3000\u3000\u201c\u65b0\u738b\u7be1\u4f4d\u4e4b\u540e\uff0c\u4e3a\u4e86\u65a9\u8349\u9664\u6839\uff0c\u6d3e\u51fa\u8bb8\u591a\u4eba\u53bb\u641c\u67e5\u8fd9\u5bf9\u5b64\u513f\u7684\u4e0b\u843d\uff0c\u540e\u6765\u788d\u5230\u4e00\u4e2a\u98ce \u58f0\uff0c\u636e\u8bf4\u662f\u7ed9\u524d\u738b\u7684\u5bbe\u5ba2\uff0c\u5e26\u4e86\u8fd9\u5bf9\u5b64\u513f\u9003\u8dd1\uff0c\u8dd1\u5230\u4e2d\u539f\u53bb\u4e86\u3002 \u3000\u3000\u201c\u65b0\u738b\u91ce\u5fc3\u6781\u5927\uff0c\u7be1\u4f4d\u4e4b\u540e\uff0c\u4e5f\u5927\u91cf\u62db\u7eb3\u4eba\u6750\uff0c\u5e76\u4ece\u5929\u7afa\u8bf7\u6765\u4e86\u4e00\u4f4d\u5b9d\u8c61\u6cd5\u5e08\uff0c\u8fd9\u4f4d\u6cd5 \u5e08\u542c\u8bf4\u662f\u5929\u7afa\u7b2c\u4e00\u9ad8\u624b\uff0c\u540d\u95fb\u5927\u4e0b\u7684\u6b66\u5b66\u5927\u5e08\u9f99\u6811\u63b8\u5e08\u81e3\u4e0b\u6700\u6709\u672c\u9886\u7684\u5f1f\u5b50\uff0c\u9a6c\u8428\u51e0\u7684\u56fd\u738b \u8058\u4ed6\u4e3a\u56fd\u5e08\uff0c\u7ed9\u4ed6\u2018\u664b\u53f7\u2019\u4e3a\u6cd5\u738b\uff0c\u5e76\u4e14\u7279\u522b\u4e3a\u4ed6\u5efa\u7b51\u4e86\u4e00\u5ea7\u5bab\u6bbf\uff0c\u540d\u4e3a\u91d1\u9e70\u5bab\u3002\u6240\u4ee5\u91d1\u9e70 \u5bab\u7684\u4e3b\u4eba\u4fbf\u662f\u8fd9\u4f4d\u5b9d\u8c61\u6cd5\u738b\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u95ee\u9053\uff1a\u201c\u4e2d\u79cb\u8282\u7684\u91d1\u9e70\u5bab\u4e4b\u4f1a\u53c8\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u8fd9\u4f4d\u5b9d\u8c61\u6cd5\u738b\u4e0e\u9099\u5c71\u6d3e\u7684\u638c\u95e8\u4eba \u8c37\u5973\u4fa0\u98ce\u9a6c\u725b\u4e0d\u76f8\u53ca\uff0c\u4f55\u4ee5\u53c8\u8bf7\u5979\u8d74\u4f1a\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u5185\u91cc\u56e0\u7531\uff0c\u6211\u4e5f\u8bf4\u4e0d\u4e0a\u6765\u3002\u53ea\u77e5\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u738b\u5f97\u4e86\u8fd9\u4f4d\u2018\u56fd\u5e08\u2019\u4e4b\u540e\uff0c \u5982\u864e\u6dfb\u7ffc\uff0c\u4e00\u9762\u5e76\u541e\u90bb\u8fd1\u7684\u90e8\u843d\uff0c\u5341\u591a\u5e74\u6765\uff0c\u56fd\u571f\u6269\u5c55\u4e86\u5c06\u8fd1\u5341\u500d\uff0c\u5b83\u672c\u662f\u56de\u7586\u5317\u90e8\u3001\u963f\u5c14 \u6cf0\u5c71\u5c71\u4e0b\u7684\u4e00\u4e2a\u5c0f\u56fd\uff0c\u73b0\u5728\u7586\u571f\u5df2\u7ecf\u6269\u5c55\u5230\u7518\u8427\u7684\u5317\u90e8\uff0c\u751a\u81f3\u6709\u4e00\u90e8\u4efd\u4fb5\u8fdb\u4e86\u9752\u6d77\u7684\u5883\u5185 \u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u542c\u5230\u8fd9\u91cc\uff0c\u53c8\u662f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u8d77\u4e86\u767d\u82f1\u6770\u3001\u7a0b\u6d69\u5bf9\u4ed6\u6240\u8bf4\u7684\u90a3\u4e2a\u6545\u4e8b\uff1a\u5317\u4eac\u9547\u8fdc \u9556\u5c40\u7684\u9556\u5e08\u66ff\u4e00\u4e2a\u9752\u6d77\u571f\u738b\u8fd0\u9001\u836f\u6750\u88ab\u52ab\uff0c\u540e\u6765\u571f\u738b\u7684\u90e8\u843d\u53d1\u751f\u761f\u75ab\uff0c\u65e0\u836f\u53ef\u533b\uff0c\u7ec8\u4e8e\u88ab\u4e24 \u4e2a\u90bb\u90a6\u5c06\u4ed6\u7684\u9886\u571f\u74dc\u5206\u6389\u3002\u6c5f\u6d77\u5929\u5411\u534e\u5929\u98ce\u4e00\u540c\uff0c\u679c\u7136\u9a6c\u8428\u513f\u56fd\u4fbf\u662f\u74dc\u5206\u4e86\u90a3\u4e2a\u4e0a\u738b\u9886\u571f\u7684 \u4e24\u4e2a\u90bb\u90a6\u4e4b\u4e00\u3002 \u3000\u3000\u534e\u5929\u98ce\u7ee7\u7eed\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u53e6\u4e00\u65b9\u9762\uff0c\u5b9d\u8c61\u6cd5\u738b\u4e5f\u6d3e\u9063\u4ed6\u91d1\u9e70\u5bab\u4e2d\u7684\u597d\u624b\u5230\u4e2d\u539f\u6765\u660e\u67e5\u6697 \u8bbf\uff0c\u8bbf\u67e5\u524d\u738b\u7684\u90a3\u5bf9\u5b50\u5973\uff0c\u542c\u8bf4\u6709\u51e0\u4f4d\u4ee5\u524d\u505a\u8fc7\u524d\u738b\u5bbe\u5ba2\u53d7\u5230\u5acc\u7591\u7684\u6b66\u6797\u4eba\u7269\u5df2\u7ed9\u90a3\u4e9b\u4eba\u6697 \u6740\uff0c\u81f3\u4e8e\u90a3\u5bf9\u5b64\u513f\u6709\u6ca1\u6709\u88ab\u4ed6\u4eec\u6349\u56de\u53bb\uff0c\u8fd9\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u636e\u6211\u731c\u60f3\uff0c\u8fd9\u5bf9\u5b64\u513f\u4e5f\u8bb8\u672a\u66fe\u843d\u5728\u4ed6\u4eec\u624b\u4e2d\u3002\u201d\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u518d\u5f80\u4e0b\u9053\uff1a \u201c\u636e\u6211\u6240\u77e5\uff0c\u8fd9\u5b9d\u8c61\u6cd5\u738b\u4e5f\u662f\u4f4d\u4e0d\u7518\u5bc2\u5bde\u7684\u4eba\u7269\uff0c\u9887\u6709\u626c\u540d\u4e2d\u571f\u7684\u96c4\u5fc3\uff0c\u4ed6\u4e3e\u884c\u8fd9\u4e2a\u76db\u4f1a\uff0c \u636e\u6211\u770b\u6765\uff0c\u6709\u4e24\u4e2a\u7528\u610f\uff0c\u4ed6\u5e7f\u9080\u6b66\u6797\u4eba\u7269\uff0c\u4e00\u8005\u662f\u60f3\u5f53\u573a\u70ab\u8000\u4ed6\u7684\u6b66\u529f\uff1a\u4e8c\u8005\u662f\u60f3\u6253\u542c\u90a3\u5bf9 \u5b64\u513f\u7684\u6d88\u606f\u3002\u8fde\u6211\u8fd9\u53eb\u5316\u5b50\u4e5f\u63a5\u5230\u4ed6\u4e00\u4efd\u8bf7\u5e16\uff0c\u8c37\u5973\u4fa0\u8eab\u4e3a\u4e00\u5927\u5b97\u6d3e\u7684\u638c\u95e8\uff0c\u90a3\u5f53\u7136\u66f4\u8981\u9080 \u8bf7\u4e86\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u8001\u5f1f\u7684\u770b\u6cd5\u5f88\u6709\u9053\u7406\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u4e4b\u6240\u4ee5\u613f\u8d74\u91d1\u9e70\u5bab\u4e4b\u4f1a\uff0c\u4e3a\u7684\u4e5f\u662f\u60f3 \u89c1\u8bc6\u89c1\u8bc6\u90a3\u5b9d\u8c61\u6cd5\u738b\u7684\u5929\u7afa\u4e00\u6d3e\u6b66\u529f\u3002\u201d\u6c5f\u6d77\u5929\u5fc3\u91cc\u5374\u5728\u60f3\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u83ab\u975e\u8c37\u4e2d\u83b2\u5f53 \u771f\u662f\u4ec0\u4e48\u9a6c\u8428\u513f\u56fd\u7684\u516c\u4e3b\uff0c\u90a3\u5b9d\u8c61\u6cd5\u738b\u9080\u8bf7\u8c37\u5973\u4fa0\u8d74\u4f1a\uff0c\u6050\u6015\u8fd8\u4e0d\u4ec5\u4ec5\u56e0\u4e3a\u5979\u662f\u9099\u5c71\u6d3e\u7684\u638c \u95e8\uff0c\u800c\u662f\u56e0\u4e3a\u5df2\u77e5\u9053\u4e86\u5979\u7684\u5f92\u5f1f\u7684\u672c\u6765\u8eab\u4efd\u3002\u201d \u3000\u3000\u4ef2\u6c0f\u7edf\u9053\uff0c\u201c\u53ef\u60dc\u6211\u8981\u5f80\u767e\u7075\u5e99\u8d74\u7ffc\u5e2e\u4e3b\u4e4b\u7ea6\uff0c\u4e0d\u80fd\u4e0e\u4f60\u4eec\u4e00\u9053\u540c\u884c\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff0c \u201c\u6211\u5728\u8d74\u91d1\u9e70\u5bab\u4e4b\u524d\uff0c\u60f3\u5148\u5f80\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\uff0c\u8c12\u89c1\u6211\u5e08\u7236\u7684\u597d\u53cb\u5510\u7ecf\u5929\u592b\u5987\u3002\u201d \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u867d\u7136\u53ea\u662f\u4e00\u4e2a\u521a\u8e0f\u8fdb\u5341\u4e03\u5c81\u7684\u5927\u5b69\u4e86\uff0c\u8fd8\u672a\u66fe\u61c2\u5f97\u4ec0\u4e48\u53eb\u505a\u7231\u60c5\uff0c\u4f46\u7531\u5348\u8c37 \u4e2d\u84ec\u662f\u4ed6\u9752\u6885\u7af9\u9a6c\u4e4b\u4ea4\uff0c\u7ed9\u4ed6\u7684\u5370\u8c61\u4e5f\u6700\u6df1\u523b\uff0c\u6240\u4ee5\u8c37\u4e2d\u83b2\u5728\u4ed6\u7684\u5fc3\u4e2d\u5b9e\u5728\u5df2\u662f\u5360\u4e86\u4e00\u4e2a\u91cd \u8981\u7684\u5730\u4f4d\uff0c\u4e5f\u8bb8\u8fde\u4ed6\u81ea\u5df1\u4e5f\u8fd8\u672a\u89c9\u5bdf\uff0c\u4ed6\u5bf9\u8c37\u4e2d\u83b2\u5b9e\u5728\u5df2\u662f\u53d1\u751f\u4e86\u4e00\u79cd\u201c\u6726\u80e7\u201d\u7684\u604b\u6155\u3002\u90a3 \u662f\u4e00\u4e2a\u521d\u6210\u957f\u7684\u5c11\u5e74\uff0c\u5bf9\u7b2c\u4e00\u4e2a\u201c\u95ef\u5165\u201d\u4ed6\u5fc3\u5934\u7684\u5c11\u5973\u6240\u7279\u6709\u7684\u4e00\u79cd\u60c5\u5e95 \u3000\u3000\u4e0d\u8fc7\uff0c\u4ed6\u867d\u7136\u81ea\u5df1\u6ca1\u6709\u89c9\u5bdf\u5230\u8fd9\u79cd\u60c5\u611f\uff0c\u800c\u5728\u201c\u6f5c\u610f\u8bc6\u201d\u4e0a\uff0c\u5374\u4f1a\u4e3a\u4e86\u81ea\u5df1\u5fc3\u4e2d\u6240\u201c\u604b \u6155\u201d\u7684\u5c11\u5973\u3002\u800c\u6709\u610f\u65e0\u610f\u7684\u907f\u514d\u548c\u7b2c\u4e8c\u4e2a\u5973\u5b69\u5b50\u4eb2\u8fd1\uff0c\u9664\u975e\u7b2c\u4e8c\u4e2a\u5973\u5b69\u5b50\u7ed9\u4e88\u4ed6\u66f4\u6df1\u523b\u7684\u5370 \u8c61\uff0c\u6216\u8005\u66f4\u5f3a\u70c8\u7684\u523a\u6fc0\uff0c\u624d\u4f1a\u51b2\u6de1\u4ed6\u5bf9\u7b2c\u4e00\u4e2a\u5973\u5b69\u5b50\u90a3\u79cd\u201c\u6726\u80e7\u201d\u7684\u3001\u672a\u6210\u719f\u7684\u201c\u7231\u60c5\u201d\u3002 \u6c5f\u6d77\u5929\u73b0\u5728\u8bf4\u8981\u5148\u53bb\u89c1\u5510\u7ecf\u5929\uff0c\u5b9e\u5728\u5373\u662f\u4ed6\u8fd9\u79cd\u201c\u6f5c\u610f\u8bc6\u201d\u7684\u8868\u73b0\uff1a\u4e0d\u60f3\u548c\u534e\u5929\u98ce\u7236\u5973\u540c \u884c\uff0c\u4ea6\u5373\u662f\u662f\u907f\u514d\u548c\u534e\u4e91\u78a7\u65e5\u76ca\u4eb2\u8fd1\u3002 \u3000\u3000\u54ea\u77e5\u534e\u5929\u98ce\u5374\u4e00\u7b11\u8bf4\u9053\uff0c\u201c\u539f\u6765\u4f60\u8981\u5148\u5f80\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\u53bb\uff0c\u8fd9\u66f4\u597d\u4e86\u3002\u6211\u4e5f\u6b63\u60f3\u53bb\u89c1\u89c1 \u5ff5\u9752\u5510\u53e4\u62c9\u5c71\u7684\u201c\u51b0\u5bab\u201d\u4e3b\u4eba\u3002\u6211\u548c\u5510\u6653\u6f9c\u5927\u4fa0\u66fe\u6709\u4e00\u9762\u4e4b\u7f18\uff0c\u548c\u4ed6\u7684\u513f\u5b50\u5ab3\u5987\u5374\u8fd8\u672a\u89c1 \u8fc7\u3002\u542c\u8bf4\u51b0\u5bab\u4e2d\u8bb8\u591a\u5947\u82b1\u5f02\u8349\uff0c\u6211\u6b63\u597d\u548c\u4f60\u540c\u53bb\u5f00\u5f00\u773c\u754c\uff1b\u53cd\u6b63\u8ddd\u79bb\u91d1\u9e70\u5bab\u4e4b\u4f1a\uff0c\u65f6\u95f4\u8fd8 \u65e9\u3002\u201d \u3000\u3000\u534e\u65e0\u98ce\u5bf9\u6c5f\u6d77\u5929\u6709\u6551\u547d\u4e4b\u6069\uff0c\u65e6\u53c8\u662f\u8005\u524d\u8f88\u7684\u8eab\u4efd\uff0c\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u6c5f\u6d77\u5929\u7eb5\u4f7f\u5fc3\u5e9c\u91cc\u6709 \u70b9\u4e0d\u613f\u610f\u3001\u4e5f\u4e0d\u4fbf\u62d2\u7edd\u4e86\uff0c\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u5f97\u4e0e\u8001\u524d\u8f88\u540c\u884c\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff0c\u201c\u4ef2\u8001\u5f1f\uff0c\u65e2\u7136\u54b1\u4eec\u90fd\u6709\u7684\u4f1a\uff0c\u6211\u4e5f\u4e0d\u60f3\u7559\u4f60\u591a\u4f4f\u4e86\uff0c\u54b1\u4eec\u4eca\u65e5\u5c31\u5404\u81ea\u52a8\u8eab \u5427\u3002\u201d\u534e\u4e91\u78a7\u7b11\u9053\uff0c\u201c\u597d\u5728\u4ef2\u53d4\u53d4\u4e0e\u54b1\u4eec\u4f3c\u5bb6\u4eba\u4e00\u822c\uff0c\u4e0d\u4f1a\u602a\u4f60\u3002\u4f60\u8fd9\u4e48\u8bf4\uff0c\u5012\u50cf\u662f\u505a\u4e3b\u4eba \u7684\u5148\u4e0b\u9010\u5ba2\u4ee4\u4e86\u3002\u201d\u5fe1\u957f\u7edf\u5927\u7b11\u9053\uff1a\u201c\u597d\u4f84\u5973\uff0c\u60f3\u4e0d\u5230\u4f60\u73b0\u5728\u4e5f\u4f1a\u8ba8\u597d\u6211\u4e86\uff0c\u6211\u77a7\uff0c\u4f60\u624d\u662f \u5fc3\u6025\u7740\u51fa\u95e8\u5462\uff0c\u5018\u82e5\u4f60\u7239\u7239\u4e0d\u4e0b\u9010\u5ba2\u4ee4\uff0c\u4f60\u4e5f\u8981\u6b7c\u53e3\u8d76\u6211\u7684\u4e86\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u4ece\u672a\u79bb\u8fc7\u5bb6\u95e8\uff0c\u7684\u786e\u662f\u60f3\u53bb\u770b\u770b\u5916\u9762\u7684\u4e16\u754c\uff0c\u7ed9\u4ef2\u957f\u7edf\u8bf4\u4e2d\u5fc3\u4e8b\uff0c\u4e00\u7b11\u4e4b\u4e0b\uff1b\u4e5f \u4e0d\u7f6e\u8fa9\uff0c\u5174\u5b5c\u5b5c\u7684\u4fbf\u53bb\u6536\u62fe\u884c\u674e\u3002 \u3000\u3000\u534e\u5929\u98ce\u63a5\u8fc7\u5973\u513f\u7ed9\u4ed6\u6536\u62fe\u597d\u7684\u884c\u56ca\u7b11\u9053\uff1a\u201c\u4f60\u628a\u6211\u7684\u533b\u4e66\u4e5f\u90fd\u653e\u8fdb\u53bb\u4e86\u3002\u201d\u534e\u4e91\u78a7\u9053\uff1a \u201c\u8fd9\u51e0\u90e8\u4e66\u662f\u4f60\u7684\u5b9d\u8d1d\uff0c\u6211\u6015\u4f60\u5728\u8def\u4e0a\u7a81\u7136\u4f1a\u60f3\u8d77\u4ec0\u4e48\u533b\u5b66\u4e0a\u7684\u96be\u9898\uff0c\u800d\u7ffb\u4e66\u67e5\u9605\uff0c\u6240\u4ee5\u90fd \u7ed9\u4f60\u5e26\u6765\u4e86\u3002\u201d\u534e\u5929\u98ce\u7b11\u9053\uff1a\u201c\u597d\uff0c\u5012\u5e95\u662f\u4f60\u77e5\u9053\u6211\u7684\u813e\u6c14\uff0c\u5e26\u5728\u8eab\u8fb9\u4e5f\u653e\u5fc3\u4e00\u4e9b\u3002\u201d \u3000\u3000\u56db\u4eba\u6b63\u8981\u51fa\u95e8\uff0c\u90a3\u5934\u5140\u9e70\u5728\u5c71\u5934\u89c5\u98df\u4e4b\u540e\uff0c\u4e5f\u6b63\u98de\u56de\u6765\uff0c\u534e\u4e91\u78a7\u95ee\u9053\uff1a\u201c\u7239\uff0c\u5e26\u4e0d\u5e26\u8fd9 \u5934\u795e\u9e70\u540c\u53bb\uff1f\u201d\u534e\u5929\u98ce\u9053\uff0c\u201c\u7559\u5b83\u4e0b\u6765\u770b\u5b88\u95e8\u6237\u5427\u3002\u53ea\u6709\u4e24\u4e2a\u836f\u7ae5\u7167\u6599\u836f\u56de\uff0c\u8981\u662f\u6709\u4ec0\u4e48\u5996 \u4eba\u524d\u6765\u76d7\u836f\uff0c\u4ed6\u4eec\u5e94\u4ed8\u4e0d\u4e86\u3002\u201d\u534e\u4e91\u78a7\u5f88\u820d\u4e0d\u5f97\u8fd9\u5934\u795e\u9e70\uff0c\u4f46\u60f3\u5230\u770b\u5b88\u7236\u4eb2\u7684\u836f\u5703\u66f4\u4e3a\u7d27 \u8981\uff0c\u4e5f\u5c31\u4e0d\u518d\u63d0\u4e86\u3002 \u3000\u3000\u5230\u4e86\u5c71\u4e0b\uff0c\u543e\u81ea\u5206\u9053\u626c\u9573\uff0c\u4ef2\u957f\u7edf\u81ea\u91d1\u767e\u7075\u5e99\u8d74\u7ffc\u4ef2\u725f\u7684\u7ea6\u4f1a\uff0c\u6c5f\u6d77\u5929\u5219\u4e0e\u534e\u5929\u98ce\u7236\u5973 \u540c\u884c\u3002\u534e\u65e0\u98ce\u89c1\u591a\u8bc6\u5e7f\uff0c\u4e00\u8def\u4e0a\u4e0e\u6c5f\u6d77\u5929\u8bf4\u4e9b\u6c5f\u6e56\u4e0a\u7684\u5947\u95fb\u5f02\u4e8b\uff0c\u6b66\u6797\u4e2d\u7684\u638c\u6545\u6e90\u6d41\u7b49\u7b49\uff0c \u4ee4\u6c5f\u6d77\u5929\u5f97\u76ca\u4e0d\u5c11\u3002\u4e0d\u6d88\u51e0\u5929\uff0c\u4ed6\u5bf9\u534e\u4e91\u78a7\u4e5f\u719f\u7edc\u8d77\u6765\uff0c\u6e10\u6e10\u548c\u534e\u4e91\u78a7\u8c08\u8bdd\u7684\u65f6\u5019\u66f4\u591a\u4e86\u3002 \u53ef\u662f\uff0c\u4ed6\u4ecd\u7136\u4e0d\u65f6\u4f1a\u60f3\u8d77\u8c37\u4e2d\u83b2\u6765\uff0c\u4ed6\u4e0e\u8c37\u4e2d\u83b2\u867d\u9694\u522b\u4e86\u516b\u5e74\u4e4b\u4e45\uff0c\u4f46\u8c37\u4e2d\u84ec\u90a3\u526f\u987d\u76ae\u7684\u795e \u6c14\uff0c\u4e00\u60f3\u8d77\u6765\uff0c\u5c31\u6d3b\u73b0\u773c\u524d\u3002\u201c\u5018\u82e5\u78b0\u89c1\u4e86\u5979\uff1b\u5979\u89c1\u6211\u4e0e\u534e\u4e91\u78a7\u540c\u5728\u4e00\u8d77\uff0c\u4e0d\u77e5\u4f1a\u4e0d\u4f1a\u5c06\u6211 \u53d6\u7b11\u3002\u201d\u6bcf\u5f53\u601d\u5ff5\u53ca\u6b64\uff0c\u4ed6\u5c31\u6709\u610f\u65e0\u610f\u7684\u5bf9\u534e\u4e91\u78a7\u51b7\u6de1\u4e00\u4e9b\u3002\u6709\u65f6\u4ed6\u4e5f\u4f1a\u60f3\u8d77\u6b27\u9633\u5a49\uff0c\u60f3\u8d77 \u5979\u7684\u7236\u4eb2\u548c\u5e08\u7236\u90fd\u662f\u90aa\u6d3e\u4e2d\u4eba\uff0c\u4fbf\u4e0d\u81ea\u7981\u7684\u6709\u4e00\u79cd\u60cb\u60dc\u4e4b\u611f\uff0c\u751a\u81f3\u65e0\u7aef\u7aef\u7684\u8d77\u4e86\u6005\u60d8\u4e4b\u60c5\u3002 \u3000\u3000\u8d70\u4e86\u5341\u591a\u5929\uff0c\u5df2\u8e0f\u8fdb\u7ef5\u4e98\u5728\u7518\u8427\u9752\u6d77\u4e24\u7701\u8fb9\u5883\u7684\u7941\u8fde\u5c71\uff0c\u8fd9\u4e00\u5929\uff0c\u5927\u5bb6\u5728\u8def\u4e0a\u8c08\u8c08\u7b11 \u7b11\uff0c\u534e\u5929\u98ce\u5ffd\u7136\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u505c\u4e0b\u811a\u6b65\u3002\u6c5f\u6d77\u5929\u968f\u7740\u4ed6\u6240\u6ce8\u89c6\u7684\u65b9\u5411\u770b\u53bb\uff0c\u53ea\u89c1\u524d\u9762\u4e00\u5757 \u5ca9\u77f3\u4e0a\u6709\u4e00\u53ea\u638c\u5370\uff0c\u8fd9\u638c\u5370\u6bd4\u666e\u901a\u4eba\u7684\u624b\u638c\u5927\u5f97\u591a\uff0c\u6c5f\u6d77\u706d\u5947\u9053\uff1a\u201c\u534e\u8001\u524d\u8f88\uff0c\u8fd9\u662f\u4ec0\u4e48 \u4eba\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u9762\u8272\u6c89\u4e98\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u627e\u4e2a\u5730\u65b9\u4f4f\u4e0b\u6765\uff0c\u6211\u518d\u6162\u6162\u548c\u4f60\u4eec\u8bf4\u5427\u3002\u201d\u534e\u4e91\u78a7\u9053\uff1a \u201c\u73b0\u5728\u5929\u8272\u672a\u665a\uff0c\u7239\uff0c\u4f60\u4e00\u8fb9\u8d70\u4e00\u8fb9\u8bf4\u4e0d\u884c\u4e48\uff1f\u54b1\u4eec\u6700\u5c11\u8fd8\u53ef\u4ee5\u8d74\u4ed6\u4e00\u767e\u51e0\u5341\u91cc\u8def\u3002\u201d\u534e\u5929 \u98ce\u9053\uff1a\u201c\u8fd9\u638c\u5370\u662f\u4e2a\u8bb0\u53f7\uff0c\u6211\u6709\u4e00\u4e2a\u8001\u670b\u53cb\u6765\u4e86\uff0c\u4ed6\u7ea6\u6211\u4eca\u665a\u89c1\u9762\uff0c\u6211\u82e5\u518d\u5f80\u524d\u8d70\uff0c\u4ed6\u4f1a\u4ee5 \u4e3a\u6211\u662f\u8eb2\u907f\u4ed6\u4e86\u3002\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u7239\uff0c\u6211\u4ece\u672a\u542c\u4f60\u8bf4\u8fc7\uff0c\u6709\u54ea\u4f4d\u670b\u53cb\u6709\u8fd9\u6837\u5927\u5f97\u51fa\u5947\u7684\u624b \u638c\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u82e6\u7b11\u4e0d\u8a00\uff0c\u53ea\u662f\u5728\u9644\u8fd1\u627e\u5bfb\u4f4f\u5904\uff0c\u7ec8\u4e8e\u627e\u5230\u4e86\u4e00\u4e2a\u9887\u4e3a\u5bbd\u5e7f\u7684\u5ca9\u6d1e\uff0c\u5ca9\u6d1e\u91cc\u6709\u6761 \u6a2a\u77f3\uff0c\u5c06\u5ca9\u6d1e\u5206\u6210\u5927\u5c0f\u4e24\u8fb9\uff0c\u5c31\u50cf\u95f4\u5f00\u4e86\u7684\u4e24\u4e2a\u623f\u95f4\u4f3c\u7684\u3002\u534e\u4e91\u78a7\u7b11\u9053\uff1a\u201c\u8fd9\u6b63\u5408\u9002\uff0c\u6d77\u5927 \u54e5\uff0c\u4f60\u4f4f\u8fd9\u4e00\u8fb9\u3002\u201d\u534e\u5929\u98ce\u5f85\u4ed6\u4eec\u653e\u597d\u884c\u56ca\uff0c\u53eb\u4ed6\u4eec\u5750\u4e86\u4e0b\u6765\uff0c\u8fd9\u624d\u90d1\u91cd\u4ed6\u8bf4\u9053\uff1a\u201c\u78a7\u513f\uff0c \u4f60\u53ef\u77e5\u9053\u7239\u7239\u4e3a\u4ec0\u4e48\u8981\u5b66\u533b\u5417\uff1f\u201d \u3000\u3000\u534e\u4e91\u78a7\u6447\u5934\u7b11\u9053\uff0c\u201c\u4f60\u4e0d\u8bf4\u6211\u600e\u4e48\u77e5\u9053\uff1f\u201d\u534e\u5929\u98ce\u9053\uff0c\u201c\u5341\u516b\u5e74\u524d\u3002\u4f60\u5a18\u6b63\u6000\u7740\u4f60\uff0c\u65e6 \u6709\u516b\u4e2a\u6708\u7684\u8eab\u5b55\u4e86\u3002\u6709\u4e00\u5929\uff0c\u6211\u4eec\u5728\u5e73\u51c9\u9053\u4e0a\uff0c\u78b0\u5230\u4e00\u4e2a\u6076\u540d\u662d\u5f70\u7684\u9b54\u5934\uff0c\u8fd9\u9b54\u5934\u4fbf\u662f\u90a3\u4e2a \u4eca\u65e5\u5728\u5ca9\u77f3\u4e0a\u7559\u4e0b\u638c\u5370\u7684\u4eba\uff0c\u4ed6\u6d51\u540d\u53eb\u505a\u6bd2\u624b\u5929\u5c0a\uff0c\u83ab\u540d\u53eb\u505a\u84b2\u5362\u864e\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u7b11\u9053\uff1a\u201c\u597d\u5927\u7684\u53e3\u6c14\uff0c\u6562\u79f0\u6bd2\u624b\u5929\u5c0a\u3002\u96be\u9053\u4ed6\u7684\u6bd2\u624b\u6bd4\u90a3\u9634\u8001\u5a46\u5b50\u7684\u795e\u86c7\u638c\u8fd8\u8981 \u5389\u5bb3\u4e48\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u6b63\u8d5b\u8bf4\u9053\uff1a\u201c\u662f\u8981\u6bd4\u9634\u8001\u5a46\u5b50\u7684\u795e\u86c7\u62ff\u8fd8\u5389\u5bb3\u5f97\u591a\u3002\u795e\u86c7\u638c\u662f\u7528\u4e00\u5343\u79cd\u6bd2\u86c7\u7684\u53e3 \u6d8e\u70bc\u7684\uff0c\u800c\u84b2\u5362\u864e\u8fd9\u53cc\u6bd2\u638c\u5374\u662f\u7528\u4e16\u4e0a\u4e03\u6837\u81f3\u6bd2\u7684\u4e1c\u897f\u70bc\u7684\uff0c\u8fd9\u4e03\u6837\u4e1c\u897f\u662f\u5b54\u96c0\u80c6\u3001\u9e64\u9876 \u7ea2\u3001\u91d1\u8695\u86ca\u3001\u8179\u86c7\u6d8e\u3001\u65ad\u80a0\u82b1\u3001\u8150\u9aa8\u8349\u548c\u9ed1\u5fc3\u83b2\u3002\u4ed6\u5c06\u4e03\u6837\u81f3\u6bd2\u7684\u836f\u4fa6\uff0c\u6eb6\u5316\u5728\u9e20\u9152\u4e4b\u4e2d\uff0c \u8fd0\u7528\u4ed6\u72ec\u95e8\u7684\u90aa\u6d3e\u5185\u529f\uff0c\u5c06\u53cc\u638c\u6d78\u5728\u6bd2\u9152\u91cc\u4e03\u4e03\u56db\u5341\u4e5d\u592d\uff0c\u8fd9\u624d\u7ec3\u6210\u4e86\u4ed6\u8fd9\u53cc\u6bd2\u638c\u3002\u201d\u534e\u4e91 \u78a7\u8fd9\u624d\u5403\u60ca\u8d77\u6765\uff0c\u95f4\u9053\uff1a\u201c\u54ce\u5440\uff0c\u90a3\u4f60\u4eec\u78b0\u5230\u4ed6\uff0c\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u7eed\u9053\uff1a\u201c\u6211\u4eec\u65e9\u5df2\u60f3\u628a\u4ed6\u9664\u6389\uff0c\u964c\u8def\u76f8\u9022\uff0c\u4e8c\u6d3b\u4e0d\u8bf4\uff0c\u4fbf\u5373\u52a8\u624b\u3002\u6211\u7528\u65b0\u7ec3\u6210\u7684 \u6d41\u4e91\u5251\u6cd5\u524a\u53bb\u4e86\u4ed6\u4e00\u6761\u624b\u81c2\uff0c\u53ef\u662f\u4f60\u5a18\u5374\u4e00\u4e2a\u758f\u795e\uff0c\u88ab\u4ed6\u7684\u638c\u7f18\u626b\u8fc7\uff0c\u6cbe\u4e0a\u4e86\u4e00\u70b9\u76ae\u8089\uff0c\u84b2 \u5362\u864e\u843d\u8352\u800c\u9003\uff0c\u6211\u89c1\u4f60\u5a18\u53d7\u4f24\uff0c\u4e5f\u4e0d\u6562\u8ffd\u8d76\u4ed6\u3002\u201d\u534e\u4e91\u78a7\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u540e\u6765\u600e\u4e48\u6837\uff1f\u201d\u534e\u5929 \u98ce\u53f9\u4e86\u53e3\u6c14\uff0c\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u4f60\u5a18\u5185\u529f\u7cbe\u7eaf\uff0c\u6bd4\u6211\u8fd8\u80dc\u4e09\u5206\uff0c\u53ea\u56e0\u5df2\u6709\u8eab\u5b55\uff0c\u96be\u4ee5\u5168\u529b\u8fd0\u529f\uff0c \u7ed3\u679c\u4f60\u672a\u6ee1\u4e5d\u4e2a\u6708\u4fbf\u65e9\u4ea7\u4e0b\u6765\uff0c\u4f60\u5a18\u6c14\u8840\u5927\u4e8f\uff0c\u4ea7\u540e\u4e09\u5929\u5c31\u6b7b\u4e86\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u6700\u521d\u672c\u662f\u9762\u5e26\u7b11\u5bb9\uff0c\u542c\u5f97\u6d25\u5f8b\u6709\u5473\u7684\uff0c\u8fd9\u65f6\u4e0d\u7531\u5f97\u8138\u8272\u5168\u90fd\u53d8\u4e86\uff0c\u773c\u6cea\u6ef4\u4e86\u4e0b \u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5988\u5988\u662f\u6b7b\u5728\u8fd9\u5996\u4eba\u7684\u624b\u4e0a\u7684\u4e86\uff1f\u7239\uff0c\u4f60\u600e\u4e48\u4e0d\u65e9\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u662f\u65e9\u4ea7\u7684\u539f\u6545\uff0c\u81ea\u5c0f\u8eab\u4f53\u7626\u5f31\u591a\u75c5\uff0c\u6211\u4e00\u6765\u4e3a\u4e86\u8981\u628a\u4f60\u629a\u517b\u6210\u4eba\uff0c \u4e8c\u6765\u4e3a\u4e86\u8981\u66ff\u4f60\u5a18\u62a5\u4ec7\uff0c\u5bf9\u4ed8\u90a3\u84b2\u5362\u864e\u7684\u6bd2\u638c\uff0c\u56e0\u6b64\u624d\u5728\u534e\u5c71\u9690\u5c4a\uff0c\u95ed\u95e8\u5b66\u533b\u3002\u8fd9\u4ec7\u4eba\u592a\u8fc7 \u5389\u5bb3\uff0c\u5728\u6211\u7684\u672c\u9886\u6765\u7ec3\u597d\u4e4b\u524d\uff0c\u4e5f\u6ca1\u7528\u5904\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u4f24\u5fc3\u4e86\u4e00\u4f1a\uff0c\u95ee\u9053\uff1a\u201c\u7239\uff0c\u90a3\u4e48\u4f60\u73b0\u5728\u53ef\u4ee5\u5bf9\u4ed8\u5f97\u4e86\u4ed6\u7684\u6bd2\u638c\u4e48\uff1f\u201d\u534e\u65e0\u98ce\u9053\uff1a \u201c\u8fd8\u4e0d\u6562\u8bf4\u6709\u5341\u6210\u628a\u63e1\uff0c\u4e5f\u8bb8\u6328\u4e86\u4ed6\u7684\u4e00\u638c\uff0c\u8fd8\u4f1a\u5c0f\u75c5\u4e00\u573a\u3002\u201d\u8fd9\u4e48\u8bf4\u6cd5\uff0c\u5373\u662f\u8868\u660e\u4ed6\u5df2\u7ad9 \u5728\u8d62\u9762\uff0c\u534e\u4e91\u78a7\u624d\u653e\u4e0b\u4e86\u5fc3\u3002 \u3000\u3000\u534e\u5929\u98ce\u7eed\u9053\uff1a\u201c\u6b63\u56e0\u4e3a\u6211\u5df2\u6709\u4e86\u4e03\u516b\u6210\u628a\u63e1\uff0c\u6211\u8fd9\u6b21\u624d\u6562\u5e26\u4f60\u53bb\u8d74\u91d1\u9e70\u5b98\u4e4b\u4f1a\uff0c\u6211\u6599\u60f3 \u91d1\u9e70\u5bab\u4e4b\u4f1a\u4e5f\u4f1a\u6709\u4ed6\uff0c\u672c\u4ee5\u4e3a\u5728\u91d1\u9e70\u5bab\u624d\u80fd\u78b0\u4e0a\u4ed6\u7684\uff0c\u54ea\u77e5\u9053\u73b0\u5728\u5728\u534a\u9014\u5c31\u78b0\u4e0a\u4e86\u3002\u770b\u6765\uff0c \u4ed6\u88ab\u6211\u524a\u4e0b\u4e00\u6761\u81c2\u8180\uff0c\u4e5f\u662f\u6000\u6068\u96be\u5fd8\uff0c\u4f46\u53ea\u4e0d\u77e5\u4ed6\u662f\u600e\u6837\u5f97\u5230\u7684\u6d88\u606f\uff0c\u9884\u5148\u77e5\u9053\u54b1\u4eec\u4f1a\u4ece\u8fd9 \u6761\u8def\u6765\uff0c\u7559\u4e0b\u4e86\u4ed6\u7684\u6bd2\u638c\u8bb0\u53f7\uff1f\u201d \u3000\u3000\u534e\u4e91\u78a7\u9053\uff1a\u201c\u7ba1\u4ed6\u662f\u600e\u4e48\u77e5\u9053\u7684\u3002\u7239\uff0c\u4f60\u65e2\u7136\u80dc\u7b97\u5728\u63e1\uff0c\u5f85\u90a3\u9b54\u5934\u6765\u4e86\uff0c\u4f60\u4e00\u5251\u5c06\u4ed6\u6740 \u6389\u4fbf\u662f\u3002\u201d\u5979\u54ea\u91cc\u77e5\u9053\u3002\u4ed6\u7236\u4eb2\u62c5\u5fc3\u7684\u4e0d\u53ea\u662f\u84b2\u5362\u864e\uff0c\u56e0\u4e3a\u84b2\u5362\u864e\u65e2\u7136\u9884\u5148\u77e5\u9053\u8baf\u606f\uff0c\u8fd8\u6562 \u7559\u4e0b\u8bb0\u53f7\uff0c\u7ea6\u534e\u5929\u98ce\u5728\u6b64\u51b3\u6218\uff0c\u90a3\u5f53\u7136\u662f\u6709\u51c6\u5907\u800c\u6765\u3002 \u3000\u3000\u534e\u5929\u98ce\u5355\u6253\u72ec\u6597\u53ef\u4ee5\u8d62\u5f97\u4e86\u84b2\u5362\u864e\uff0c\u4f46\u4e5f\u8fd8\u672a\u6709\u5341\u5206\u628a\u63e1\u3002\u5018\u82e5\u4ed6\u8fd8\u7ea6\u6709\u4e00\u4e24\u4e2a\u4e0e\u4ed6\u4e00 \u822c\u672c\u9886\u7684\u9ad8\u624b\u540c\u6765\uff0c\u534e\u65e0\u98ce\u53ef\u5c31\u96be\u4ee5\u5e94\u4ed8\u4e86\u3002\u4f46\u4ed6\u6015\u5973\u513f\u4f18\u5fc3\uff0c\u5f53\u4e0b\u53ea\u662f\u8bf4\u9053\uff1a\u201c\u8bdd\u662f\u5982 \u6b64\uff0c\u4f46\u90a3\u84b2\u5362\u864e\u7684\u6bd2\u638c\u786e\u5b9e\u662f\u5341\u5206\u6b79\u6bd2\uff0c\u5230\u65f6\u4e0d\u8bba\u5982\u4f55\uff0c\u4f60\u90fd\u4e0d\u8bb8\u51fa\u624b\uff01\u201d\u534e\u4e91\u78a7\u987a\u7740\u5c0f\u5634 \u513f\u9053\uff1a\u201c\u597d\u5427\uff0c\u5230\u65f6\u6211\u7ad9\u5728\u65c1\u8fb9\u77a7\u70ed\u95f9\u4fbf\u662f\u3002\u201d\u534e\u5929\u98ce\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u70ed\u95f9\u4e5f\u4e0d\u8bb8\u4f60\u77a7\uff01\u4f60\u4e00 \u5b9a\u8981\u542c\u4e3a\u7239\u7684\u5429\u5490\uff01\u201d\u534e\u4e91\u78a7\u8d4c\u6c14\u9053\uff1a\u201c\u597d\uff0c\u6211\u5403\u8fc7\u4e86\u996d\u5c31\u8499\u5934\u7761\u89c9\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u51fa\u6d1e\u730e\u4e86\u4e24\u53ea\u91ce\u5154\u56de\u6765\uff0c\u534e\u4e91\u78a7\u65e0\u5fc3\u505a\u83dc\uff0c\u628a\u5154\u5b50\u70e4\u5f97\u7126\u81ed\uff0c\u8349\u8349\u5403\u8fc7\u4e86\u665a\u996d\uff0c \u5979\u679c\u7136\u4fbf\u6253\u5f00\u94fa\u76d6\uff0c\u8499\u5934\u5927\u7761\u3002\u6c5f\u6d77\u5929\u5219\u4f34\u7740\u534e\u65e0\u98ce\uff0c\u62c5\u5fc3\u540a\u80c6\u7684\u7b49\u5019\u84b2\u5362\u864e\u5230\u6765\u3002 \u3000\u3000\u6708\u5149\u4ece\u5ca9\u9699\u4fb5\u8fdb\u6765\uff0c\u6c5f\u6d77\u5929\u4f38\u51fa\u5934\u53bb\u4e00\u671b\uff0c\u6708\u4eae\u5df2\u8fc7\u4e2d\u5929\u3002\u662f\u4e09\u66f4\u7684\u65f6\u5206\u4e86\uff0c\u4e0d\u7981\u5600\u5495 \u9053\uff1a\u201c\u5947\u602a\uff0c\u600e\u4e48\u5230\u4e86\u8fd9\u4e2a\u65f6\u5206\uff0c\u8fd8\u662f\u9b3c\u5f71\u90fd\u4e0d\u89c1\u4e00\u4e2a\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff0c\u201c\u6c5f\u8d24\u4f84\uff0c\u4f60\u5148\u7761\u5427\u3002\u82e5\u662f\u6211\u8981\u4f60\u5e2e\u5fd9\uff0c\u6211\u4f1a\u51fa\u58f0\u53eb\u4f60\u3002\u201d\u8fd9\u5341\u591a\u5929\u6765\uff0c\u4ed6 \u4e0e\u6c5f\u6d77\u5929\u76f8\u5904\u6709\u5982\u5bb6\u4eba\uff0c\u6700\u521d\u4ed6\u662f\u5c06\u6c5f\u6d77\u5929\u79f0\u4f5c\u201c\u6c5f\u5c0f\u4fa0\u201d\u7684\uff0c\u540e\u6765\u4fbf\u5e94\u6c5f\u6d77\u5929\u4e4b\u8bf7\uff0c\u6539\u53e3 \u4ee5\u201c\u8d24\u4f84\u201d\u76f8\u79f0\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e00\u9635\u8e0c\u8e87\uff0c\u534e\u5929\u98ce\u7b11\u9053\uff1a\u201c\u4f60\u5927\u7ea6\u672a\u600e\u4e48\u61c2\u5f97\u8fd9\u79cd\u9ed1\u9053\u4e0a\u7684\u89c4\u77e9\uff0c\u4ed6\u65e2\u7559\u4e0b\u4e86\u8bb0 \u53f7\u6311\u6218\uff0c\u5c31\u51b3\u4e0d\u4f1a\u5077\u88ad\u3002\u82e5\u4ed6\u6765\u4e86\uff0c\u4ed6\u5fc5\u5b9a\u8981\u5728\u7559\u4e0b\u8bb0\u53f7\u7684\u9644\u8fd1\uff0c\u53d1\u58f0\u957f\u5578\uff0c\u5524\u6211\u51fa\u6765\u3002\u6240 \u4ee5\u4f60\u53ef\u5b89\u5fc3\u7761\u89c9\uff0c\u6211\u4e5f\u60f3\u9759\u5750\u4e00\u4f1a\u5410\u7eb3\u529f\u592b\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u7761\u5728\u5979\u7236\u4eb2\u7684\u8eab\u8fb9\uff0c\u6c5f\u6d77\u5929\u5c11\u5e74\u817c\u8146\uff0c\u548c\u534e\u5929\u98ce\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u53cc\u76ee\u4e0d\u6562\u659c\u89c6\uff0c\u4e5f \u89c9\u5f97\u751a\u4e3a\u201c\u8f9b\u82e6\u201d\uff0c\u90a3\u5ca9\u6d1e\u6709\u4e00\u6761\u5929\u7136\u7684\u6a2a\u77f3\uff0c\u95f4\u4f5c\u4e24\u8fb9\u7684\uff0c\u5f53\u4e0b\u6c5f\u6d77\u5929\u542c\u5f97\u534e\u5929\u98ce\u5982\u6b64\u8bf4 \u6cd5\uff0c\u4fbf\u9053\uff1a\u201c\u8001\u4f2f\u8fd0\u529f\uff0c\u5c0f\u4f84\u4e0d\u6562\u6253\u6405\u4e86\u3002\u5018\u82e5\u90a3\u9b54\u5934\u5230\u6765\uff0c\u8bf7\u8001\u4f2f\u5c06\u6211\u5524\u9192\u3002\u7eb5\u7136\u5e2e\u4e0d\u4e0a \u5fd9\uff0c\u6211\u4e5f\u60f3\u89c1\u8bc6\u89c1\u8bc6\u3002\u201d\u8bf4\u7f62\u4fbf\u94bb\u8fc7\u4e86\u77f3\u6d1e\u7684\u90a3\u4e00\u8fb9\u3002 \u3000\u3000\u53ef\u662f\u8bdd\u867d\u5982\u6b64\uff0c\u6c5f\u6d77\u5929\u5374\u54ea\u91cc\u7761\u5f97\u7740\u89c9\uff0c\u8fc7\u4e86\u5927\u7ea6\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5df2\u662f\u5c06\u8fd1\u56db\u66f4\u7684\u65f6\u5206\uff0c\u6c5f \u6d77\u5929\u5fc3\u91cc\u6b63\u60f3\uff1a\u201c\u90a3\u84b2\u5362\u864e\u5927\u7ea6\u4eca\u665a\u4e0d\u4f1a\u6765\u4e86\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u51c4\u5389\u7684\u53eb\u558a\uff0c\u4ece\u8fdc \u5904\u4f20\u6765\u3002\u53ef\u662f\uff0c\u5947\u602a\uff0c\u8fd9\u5374\u4e0d\u662f\u7537\u4e88\u7684\u58f0\u97f3\uff0c\u518d\u542c\u4e00\u542c\uff0c\u58f0\u97f3\u7adf\u7136\u201c\u4f3c\u66fe\u76f8\u8bc6\u201d\uff0c\u6c5f\u6d77\u5929\u731b \u5730\u8df3\u4e86\u8d77\u6765\uff0c\u4ed6\u542c\u51fa\u8fd9\u662f\u6b27\u9633\u5a49\u7684\u58f0\u97f3\u4e86\uff01\u6b63\u662f\uff1a \u3000\u3000\u5f02\u58f0\u5348\u591c\u60ca\u5fc3\u9b44\uff0c\u4e0d\u610f\u8352\u5c71\u6765\u654c\u4eba\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u5341\u516b\u56de\u3000\u9677\u8eab\u4e0d\u7981\u7591\u4e91\u8d77\u3000\u8131\u9669\u8fd8\u60ca\u7978\u672a\u6d88 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u5341\u516b\u56de\u3000\u9677\u8eab\u4e0d\u7981\u7591\u4e91\u8d77\u3000\u8131\u9669\u8fd8\u60ca\u7978\u672a\u6d88 \u3000\u3000\u542c\u8fd9\u58f0\u97f3\uff0c\u4f3c\u662f\u4e00\u4e2a\u4eba\u7a81\u7136\u78b0\u5230\u4e86\u6781\u5176\u53ef\u6016\u7684\u7269\u4e8b\uff0c\u751f\u547d\u5373\u5c06\u6bc1\u706d\u4e00\u65e6\u3002\u6c5f\u6d77\u5929\u4e0d\u5047\u601d \u7d22\uff0c\u7acb\u5373\u98de\u5954\u51fa\u6d1e\uff0c\u5411\u90a3\u58f0\u97f3\u7684\u65b9\u5411\u8dd1\u53bb\u3002\u534e\u5929\u98ce\u6b63\u5728\u9759\u5750\u8fd0\u529f\uff0c\u731b\u7136\u60ca\u89c9\uff0c\u6025\u5fd9\u558a\u9053\uff1a \u201c\u6c5f\u8d24\u4f84\uff0c\u4f60\u3001\u4f60\u5e72\u4ec0\u4e48\uff1f\u63d0\u9632\u3001\u63d0\u9632\u2026\u2026\u201d\u53ef\u662f\u7531\u4e8e\u6c5f\u6d77\u5929\u52a8\u4f5c\u5feb\u6781\uff0c\u4ed6\u4eec\u4e4b\u95f4\u53c8\u6709\u4e00\u6761 \u6a2a\u77f3\u963b\u62e6\uff0c\u534e\u5929\u98ce\u8981\u60f3\u62d6\u4f4f\u4ed6\u5df2\u6765\u4e0d\u53ca\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5f53\u7136\u542c\u5230\u4e86\u534e\u65e0\u98ce\u7684\u558a\u58f0\uff0c\u53ef\u662f\u4ed6\u5fc3\u91cc\u5374\u5728\u60f3\u9053\uff1a\u201c\u534e\u8001\u524d\u8f88\u4e5f\u5fd2\u5c0f\u5fc3\u4e86\uff0c\u63d0\u9632 \u4ec0\u4e48\uff1f\u6b27\u9633\u59d1\u5a18\u9047\u5230\u4e86\u5371\u9669\u6211\u5c82\u80fd\u4e0d\u6551\uff1f\u96be\u9053\u8fd8\u4f1a\u662f\u4ec0\u4e48\u8be1\u8ba1\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u4e24\u6bcf\u9ed1\u5f71\u5df2\u4ece\u6811\u6797\u91cc\u51fa\u6765\uff0c\u6708\u8272\u867d\u7136\u4e0d\u5f88\u660e\u4eae\uff0c\u4f46\u4ece\u90a3\u82d7\u6761\u7684\u8eab\u5f71\uff0c\u5df2 \u53ef\u770b\u51fa\u8dd1\u5728\u524d\u5934\u7684\u662f\u4e2a\u59d1\u5a18\uff0c\u800c\u5728\u540e\u9762\u8ffd\u9010\u7740\u5979\u7684\u90a3\u4e2a\u4eba\u5219\u662f\u4e2a\u8eab\u5f62\u53e4\u602a\u7684\u7537\u5b50\uff0c\u8eab\u6750\u4e0d\u5230 \u4e94\u5c3a\uff0c\u5934\u9885\u5f88\u5927\uff0c\u53ea\u6709\u4e00\u6761\u81c2\u818a\uff0c\u800c\u90a3\u6761\u81c2\u818a\u53c8\u957f\u53c8\u5927\uff0c\u624b\u638c\u5f20\u5f00\uff0c\u5c31\u5982\u4e00\u7247\u4e4c\u4e91\uff0c\u5411\u7740\u524d \u9762\u90a3\u5c11\u5973\u7684\u5934\u9876\u7f69\u4e0b\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u5934\u4e00\u9707\uff0c\u201c\u83ab\u975e\u8fd9\u4eba\u5c31\u662f\u6bd2\u624b\u5929\u5c0a\uff1f\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u4ed6\u90a3\u84b2\u6247\u822c\u7684\u5927 \u624b\u582a\u582a\u5c31\u8981\u6293\u5230\u6b27\u9633\u5a49\u7684\u9876\u5fc3\uff0c\u6c5f\u6d77\u5927\u65e0\u6687\u67e5\u540d\u95ee\u59d3\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u653e\u624b\uff01\u6b3a\u4fae\u5973\u5b50\u7b97\u5f97\u4ec0 \u4e48\u82f1\u96c4\uff1f\u201d\u58f0\u5230\u4eba\u5230\uff0c\u8eab\u4f3c\u79bb\u5f26\u4e4b\u7bad\uff0c\u75be\u51b2\u8fc7\u53bb\u3002 \u3000\u3000\u773c\u770b\u53ea\u6709\u6570\u4e08\u8ddd\u79bb\uff0c\u51e0\u6b65\u53ef\u5230\uff0c\u5ffd\u5730\u4e00\u6b65\u8e0f\u4e0b\uff0c\u811a\u6b65\u4e00\u6d6e\uff0c\u597d\u50cf\u8e0f\u7740\u4e86\u4e00\u56e2\u68c9\u7d6e\uff0c\u6c5f\u6d77 \u5929\u65b9\u89c9\u6709\u5f02\uff0c\u7a81\u7136\u95ee\u5df2\u88ab\u4e00\u9762\u5927\u7f51\u7f51\u4f4f\uff0c\u539f\u6765\u90a3\u5f20\u7f51\u5c31\u94fa\u5728\u5730\u4e0a\uff0c\u7f51\u662f\u7eaf\u9ed1\u8272\u7684\uff0c\u5373\u7b97\u6c5f\u6d77 \u5929\u4e0d\u662f\u5fd9\u7740\u6551\u4eba\uff0c\u5728\u9ed1\u591c\u4e4b\u4e2d\uff0c\u4e5f\u96be\u4ee5\u53d1\u89c9\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\u6c5f\u6d77\u5929\u53c8\u60ca\u53c8\u6050\uff0c\u6b63\u56e0\u4e3a\u4ed6\u4ee5\u524d\u66fe\u4e2d\u8fc7\u6b27\u9633\u5a49\u7684\u4e00\u6b21\u8be1\u8ba1\uff0c\u800c\u4e8b\u540e\u6b27\u9633\u5a49\u5c31\u4ee5 \u884c\u52a8\u8868\u793a\u4e86\u5979\u7684\u5fcf\u6094\uff0c\u6240\u4ee5\u6c5f\u6d77\u5929\u624d\u4ee5\u4e3a\u8fd9\u6b21\u5979\u662f\u771f\u7684\u9047\u96be\uff0c\u7edd\u975e\u8be1\u8ba1\uff0c\u54ea\u77e5\u7adf\u7136\u53c8\u4e00\u6b21\u7684 \u7740\u4e86\u9053\u513f\uff01 \u3000\u3000\u90a3\u5f20\u7f51\u6162\u6162\u79bb\u5730\u9762\u8d77\uff0c\u800c\u4e14\u662f\u8d8a\u6765\u8d8a\u6536\u7f29\uff0c\u628a\u6c5f\u6d77\u5929\u7f51\u5f97\u5982\u540c\u7cbd\u5b50\u3002\u6c5f\u6d77\u5929\u6068\u58f0\u8bf4\u9053\uff1a \u201c\u597d\u5440\uff0c\u6b27\u9633\u5a49\uff0c\u4f60\u3001\u4f60\u3001\u4f60\u2026\u2026\u6b27\u9633\u5a49\u6211\u7b97\u662f\u8ba4\u5f97\u4f60\u4e86\uff01\u201d\u4ed6\u7528\u529b\u6495\u90a3\u5f20\u7f51\uff0c\u4ee5\u4ed6\u7684\u529f \u529b\uff0c\u672c\u6765\u4e00\u5f20\u7280\u725b\u76ae\u4e5f\u53ef\u6495\u88c2\uff0c\u54ea\u77e5\u8fd9\u5f20\u7f51\u4ed6\u7adf\u7136\u8d8a\u6495\u8d8a\u7d27\u3002\u539f\u6765\u8fd9\u5f20\u7f51\u662f\u7528\u6606\u4ed1\u5c71\u7684\u5929\u8695 \u7eba\u7ec7\u6210\u7684\uff0c\u575a\u97e7\u975e\u5e38\uff0c\u975e\u4e4e\u6307\u4e4b\u529b\u53ef\u4ee5\u6495\u65ad\u3002\u4f55\u51b5\u6c5f\u6d77\u5929\u53c8\u5df2\u88ab\u7f51\u5728\u7f51\u4e2d\uff0c\u6709\u6c14\u529b\u4e5f\u96be\u4ee5\u5b8c \u5168\u65bd\u5c55\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u89c1\u90a3\u602a\u4eba\u5df2\u505c\u535c\u4e86\u811a\u6b65\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6b27\u9633\u4e8c\u5a18\uff0c\u770b\u5728\u4f60\u7684\u4efd\u4e0a\uff0c\u8fd9\u5c0f\u5b50 \u6211\u5c31\u4e0d\u7ba1\u4ed6\u4e86\u3002\u201d\u4e0e\u6b64\u540c\u65f6\uff0c\u6b27\u9633\u5a49\u4e5f\u5728\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u5a18\uff0c\u4f60\u3001\u4f60\u3001\u4f60\u2026\u2026\u539f\u6765\u4f60\u4e5f\u5728\u8fd9 \u513f\uff0c\u8fd9\u3001\u8fd9\u3001\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4ef0\u9762\u4e00\u671b\uff0c\u53ea\u89c1\u5728\u90a3\u68f5\u5927\u6811\u6a2a\u4f38\u51fa\u6765\u7684\u6811\u679d\u4e0a\u5750\u7740\u4e00\u4e2a\u5973\u4eba\uff0c\u53ef\u4e0d\u6b63\u662f\u6b27\u9633\u4e8c \u5a18\uff0c\u5979\u6b63\u5728\u628a\u88ab\u7f51\u7740\u7684\u6c5f\u6d77\u5929\u626f\u4e0a\u53bb\u3002 \u3000\u3000\u6b27\u9633\u5a49\u98de\u6b65\u8dd1\u6765\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5a18\uff0c\u8fd9\u5996\u602a\u6b3a\u4fae\u6211\uff0c\u6c5f\u76f8\u516c\u662f\u6551\u6211\u6765\u7684\uff0c\u4f60\u53ef\u4e0d\u80fd\u5bb3 \u4ed6\uff01\u201d\u6b27\u9633\u4e8c\u5a18\u5c06\u7ef3\u5b50\u5728\u6811\u4e0a\u6253\u4e86\u4e2a\u7ed3\uff0c\u628a\u6c5f\u6d77\u5929\u6302\u5728\u534a\u7a7a\uff0c\u968f\u5373\u8df3\u4e0b\u6811\u6765\uff0c\u4e00\u624b\u628a\u5973\u513f\u6293 \u4f4f\uff0c\u559d\u9053\uff1a\u201c\u4f60\u61c2\u4ec0\u4e48\uff1f\u6211\u8fd9\u662f\u6551\u4ed6\uff0c\u8c01\u8bf4\u6211\u662f\u5bb3\u4ed6\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u6bcf\u5929\u4e2d\u8ba1\u906d\u64d2\uff0c\u5fc3\u5934\u6c14\u6068\u4e4b\u6781\uff0c\u5fcd\u4e0d\u4f4f\u6c14\uff0c\u4fbf\u7834\u53e3\u9a82\u9053\uff1a\u201c\u6b27\u9633\u59d1\u5a18\uff0c\u4f60\u4fe9\u6bcd\u5973\u6f14\u5f97 \u597d\u620f\uff0c\u53ea\u662f\u6211\u59d3\u6c5f\u7684\u4e5f\u4e95\u975e\u4e09\u5c81\u5c0f\u513f\uff0c\u518d\u4e5f\u4e0d\u4f1a\u53d7\u4f60\u9a97\u4e86\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00 \u58f0\uff0c\u539f\u6765\u662f\u6b27\u9633\u5a49\u7a81\u7136\u7528\u529b\u6323\u624e\uff0c\u8896\u5b50\u88ab\u5979\u7684\u6bcd\u4eb2\u626f\u4e0b\u4e86\u4e00\u5e45\uff0c\u53ef\u662f\uff0c\u6b27\u9633\u5a49\u521a\u51b2\u4e0a\u51e0\u6b65\uff0c \u542c\u5f97\u6c5f\u6d77\u5929\u8fd9\u6837\u9a82\u5979\uff0c\u53c8\u7a81\u7136\u4f3c\u4e2d\u4e86\u201c\u5b9a\u8eab\u6cd5\u201d\u4f3c\u7684\uff0c\u5446\u82e5\u6728\u9e21\uff0c\u7ad9\u7740\u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u89c1\u53c8\u662f\u4e00\u6761\u9ed1\u5f71\uff0c\u6765\u5f97\u5feb\u5f97\u96be\u4ee5\u5f62\u5bb9\uff0c\u90a3\u602a\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6b27\u9633\u4e8c\u54e5\uff0c\u4f60 \u6765\u5f97\u6b63\u597d\uff0c\u4ee4\u5343\u91d1\u8981\u653e\u4eba\u5462\uff01\u201d \u3000\u3000\u970e\u773c\u4e4b\u95f4\uff0c\u90a3\u6761\u9ed1\u5f71\u5df2\u6765\u5230\u4e86\u6b27\u9633\u5a49\u7684\u9762\u524d\uff0c\u5389\u58f0\u65a5\u9053\uff1a\u201c\u4e0d\u61c2\u4e8b\u7684\u7cca\u6d82\u4e2b\u5934\uff0c\u5feb\u7ed9\u6211 \u6eda\u56de\u53bb\uff0c\u518d\u8981\u80e1\u95f9\uff0c\u770b\u6211\u8001\u5927\u7684\u8033\u522e\u5b50\u6253\u4f60\uff01\u201d \u3000\u3000\u6708\u8272\u6726\u80e7\uff0c\u6c5f\u6d77\u5929\u4ece\u7f51\u5b54\u91cc\u770b\u51fa\u53bb\uff0c\u867d\u7136\u770b\u4e0d\u6e05\u695a\u6b27\u9633\u5a49\u8138\u90e8\u7684\u8868\u60c5\uff0c\u5374\u89c1\u5979\u5a07\u8eaf\u98a4 \u6296\uff0c\u5c31\u4f3c\u4e00\u682a\u5728\u72c2\u98ce\u66b4\u96e8\u4e0b\u7684\u82b1\u679d\uff0c\u663e\u89c1\u5979\u662f\u60ca\u9a87\u5df2\u6781\uff0c\u5979\u5446\u4e86\u7247\u523b\uff0c\u7a81\u7136\u4fbf\u8f6c\u8eab\u98de\u8dd1\uff0c\u8dd1 \u51fa\u4e86\u5341\u51e0\u6b65\uff0c\u624d\u84e6\u5730\u4e00\u58f0\u5c16\u53eb\uff0c\u8ddf\u7740\u75db\u54ed\u8d77\u6765\uff0c\u54ed\u58f0\u5636\u54d1\uff0c\u542c\u5f97\u6c5f\u6d77\u5929\u7684\u5fc3\u80ba\u90c1\u4f3c\u8981\u88ab\u90a3\u54ed \u58f0\u6495\u88c2\uff0c\u6bd4\u8d77\u521a\u624d\u5979\u88ab\u90a3\u602a\u4eba\u8ffd\u9010\u65f6\u7684\u53eb\u558a\uff0c\u66f4\u8981\u4ee4\u4eba\u96be\u53d7\uff01\u5e78\u800c\u5979\u8dd1\u5f97\u5f88\u5feb\uff0c\u4e0d\u8fc7\u4e00\u4f1a\uff0c \u5979\u7684\u5f71\u5b50\u548c\u54ed\u58f0\u90fd\u6d88\u5931\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5ffd\u5730\u611f\u5230\u5185\u759a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u83ab\u975e\u5979\u662f\u88ab\u8feb\u6765\u7684\uff0c\u6211\u9519\u602a\u5979\u4e86\uff01\u201d\u4f46\u8f6c\u5ff5\u53c8\u60f3\uff1a \u201c\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\uff0c\u5979\u53d1\u51fa\u53eb\u58f0\u5f15\u6211\u51fa\u6765\uff0c\u5206\u660e\u662f\u548c\u90a3\u602a\u4eba\u5408\u8c0b\u5bb3\u6211\uff01\u5979\u548c\u7236\u6bcd\u7684\u90a3\u756a\u505a\u4f5c\uff0c\u53ea \u4e0d\u8fc7\u662f\u6f14\u620f\u4e00\u822c\uff0c\u6545\u610f\u6f14\u6765\u7ed9\u6211\u770b\u7684\u3002\u201d\u53ef\u662f\uff0c\u5979\u54ed\u5f97\u90a3\u6837\u4f24\u5fc3\uff0c\u5374\u53c8\u4e0d\u4f3c\u505a\u4f5c\u5f97\u6765\uff1f\u201d\u6c5f \u6d77\u5929\u5de6\u601d\u53f3\u60f3\uff0c\u7ec8\u662f\u89c9\u5f97\u7591\u56e2\u91cd\u91cd\uff0c\u96be\u4ee5\u89e3\u91ca\u3002 \u3000\u3000\u88ab\u90a3\u602a\u4eba\u79f0\u4f5c\u201c\u6b27\u9633\u4e8c\u54e5\u201d\u7684\u90a3\u9ed1\u8863\u4eba\u5411\u6c5f\u62c7\u5929\u6295\u4e86\u4e00\u773c\uff0c\u5ffd\u5730\u5411\u59bb\u5b50\u9a82\u9053\uff1a\u201c\u4f60\u600e\u4e48 \u53ef\u4ee5\u8fd9\u6837\u5927\u610f\u3002\u4ed6\u662f\u91d1\u4e16\u9057\u7684\u5f92\u5f1f\uff0c\u8eab\u4e0a\u53c8\u6709\u88c1\u4e91\u5b9d\u5251\uff0c\u4f60\u672a\u5c01\u95ed\u4ed6\u7684\u7a74\u9053\uff0c\u4fbf\u5c06\u4ed6\u6401\u5728\u90a3 \u513f\uff01\u201d\u4ed6\u62ac\u8d77\u624b\u6765\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u55e4\u201d\u58f0\u664c\uff0c\u6c5f\u6d77\u5929\u80f8\u90e8\u7684\u201c\u81bb\u4e2d\u7a74\u201d\u4fbf\u7a81\u7136\u611f\u5230 \u4e00\u9635\u9178\u9ebb\uff0c\u539f\u6765\u4ed6\u662f\u4ee5\u201c\u9694\u7a7a\u70b9\u7a74\u201d\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u7528\u65e0\u5f62\u7684\u7f61\u6c14\u70b9\u4e86\u6c5f\u6d77\u5929\u7684\u7a74\u9053\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u4f3c\u4e4e\u9887\u60e7\u6015\u5979\u7684\u4e08\u592b\uff0c\u8d54\u7b11\u8bf4\u9053\uff0c\u201c\u662f\u6211\u4e00\u65f6\u758f\u5ffd\u4e86\uff0c\u4f46\u6709\u84b2\u5148\u751f\u5728\u8fd9\u513f\uff0c\u8c05 \u8fd9\u5c0f\u5b50\u4e5f\u8dd1\u4e0d\u6389\u3002\u201d\u90a3\u602a\u4eba\u9053\uff1a\u201c\u4e0d\u6562\u3002\u4eca\u665a\u6211\u8fd8\u5f97\u4ef0\u4ed7\u6b27\u9633\u5144\u7684\u5927\u529b\u5462\uff01\u201d\u6c5f\u6d77\u5929\u542c\u4e86\u4ed6 \u4eec\u5f7c\u6b64\u4e4b\u95f4\u7684\u79f0\u547c\uff0c\u77e5\u9053\u8fd9\u72ec\u81c2\u602a\u4eba\u786e\u662f\u90a3\u201c\u6bd2\u624b\u5929\u5c0a\u201d\u84b2\u5362\u864e\uff0c\u800c\u8fd9\u9ed1\u8863\u4eba\u5219\u662f\u6b27\u9633\u4e8c\u5a18 \u7684\u4e08\u592b\u6b27\u9633\u4ef2\u548c\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u9053\uff1a\u201c\u662f\u65f6\u5019\u4e86\u3002\u201d\u4e24\u4eba\u76f8\u89c6\u800c\u7b11\uff0c\u540c\u65f6\u53d1\u58f0\u957f\u5578\uff0c\u6b27\u9633\u4ef2\u548c\u7684\u5578\u58f0\u94ff\u94ff\u9535 \u9535\uff0c\u5e26\u7740\u91d1\u77f3\u4e4b\u58f0\uff1b\u90a3\u84b2\u5362\u864e\u7684\u5578\u58f0\u5219\u5982\u54ed\u4e27\u4e00\u822c\uff0c\u4ee4\u4eba\u6709\u8bf4\u4e0d\u51fa\u7684\u538c\u70e6\u4e4b\u611f\u3002\u5e78\u4e8f\u6c5f\u6d77\u5929 \u7684\u5185\u529f\u5e95\u5b50\u6781\u597d\uff0c\u8981\u662f\u7a0d\u5dee\u7684\u4eba\uff0c\u542c\u4e86\u4ed6\u4eec\u8fd9\u79cd\u602a\u5f02\u7684\u5578\u58f0\uff0c\u53ea\u6015\u7acb\u65f6\u4fbf\u8981\u53d1\u72c2\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u7684\u90aa\u6d3e\u5185\u529f\uff0c\u90fd\u5df2\u5230\u4e86\u6781\u9ad8\u7684\u5883\u754c\u3002\u53ea\u662f\u8fd8\u4e0d\u591f\u7cbe\u7eaf\uff0c\u53ef\u60dc \u6211\u73b0\u5728\u52a8\u5f39\u4e0d\u5f97\uff0c\u65e0\u6cd5\u52a9\u534e\u8001\u524d\u8f88\u4e00\u81c2\u4e4b\u529b\u3002\u201d\u539f\u6765\u6c5f\u6d77\u5929\u5e74\u7eaa\u867d\u8f7b\uff0c\u4f46\u4ed6\u6240\u5b66\u7684\u5185\u529f\uff0c\u5374 \u662f\u91d1\u4e16\u9057\u6240\u6295\u7684\u4e16\u4e0a\u65e0\u53cc\u7684\u201c\u6b63\u90aa\u5408\u4e00\u201d\u7684\u5185\u529f\uff0c\u5bf9\u4e8e\u5404\u79cd\u90aa\u6d3e\u5185\u529f\u90fd\u4e86\u5982\u6307\u638c\uff0c\u56e0\u6b64\u4ed6\u542c \u4e86\u8fd9\u4e24\u4eba\u7684\u5578\u58f0\uff0c\u7528\u4e0d\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u4fbf\u80fd\u8fa8\u522b\u51fa\u4ed6\u4eec\u6240\u7ec3\u7684\u662f\u54ea\u79cd\u90aa\u6d3e\u5185\u529f\uff0c\u540c\u65f6\u4fbf\u5728\u5fc3\u91cc \u51a5\u601d\u9ed8\u60f3\u7834\u89e3\u4ed6\u95e8\u8fd9\u79cd\u5185\u529f\u7684\u65b9\u6cd5\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u5578\u58f0\u8fd8\u5728\u6797\u5b50\u4e0a\u7a7a\u56de\u65cb\uff0c\u6c5f\u6d77\u5929\u4e5f\u8fd8\u5728\u7528\u5fc3\u601d\u60f3\uff0c\u5ffd\u89c1\u84b2\u5362\u864e\u8eab\u5f62\u4e00\u6643\uff0c\u5df2\u98de\u6b65 \u4e0a\u524d\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u534e\u8001\u5148\u751f\u679c\u662f\u4fe1\u4eba\uff0c\u4ffa\u84b2\u5362\u864e\u5728\u8fd9\u91cc\u606d\u5019\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u5728\u7f51\u5b54\u7535\u671b\u51fa \u53bb\uff0c\u539f\u6765\u662f\u534e\u5929\u98ce\u5df2\u7ecf\u5230\u4e86\u3002 \u3000\u3000\u534e\u65e0\u98ce\u51b7\u51b7\u8bf4\u9053\uff0c\u201c\u539f\u6765\u8fd8\u6709\u6b27\u9633\u5148\u751f\u3002\u201d\u6b27\u9633\u4ef2\u548c\u9053\uff1a\u201c\u5f53\u5e74\u6211\u591a\u627f\u6307\u6559\uff0c\u65e5\u524d\u62d9\u8346 \u53c8\u8499\u8bad\u6d77\uff0c\u6240\u4ee5\u6211\u4eec\u4eca\u665a\u7279\u501f\u6b64\u673a\u7f18\u3002\u6765\u6b64\u606d\u8fce\u5927\u9a7e\u3002\u4e00\u6765\u662f\u62a5\u7b54\u534e\u8001\u5148\u751f\u7684\u76db\u60c5\uff0c\u4e8c\u6765\u4e5f \u60f3\u518d\u5411\u534e\u8001\u5148\u751f\u8bf7\u6559\u8bf7\u6559\u3002\u201d\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u9732\u51fa\u597d\u72e1\u7684\u7b11\u5bb9\uff0c\u518d\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u6211\u521a\u624d\u65b9\u77e5 \u84b2\u5148\u751f\u4e0e\u534e\u8001\u5148\u751f\u6709\u7ea6\u5728\u5148\uff0c\u771f\u662f\u592a\u4e0d\u51d1\u5de7\u4e86\u3002\u8bf7\u534e\u8001\u5148\u751f\u653e\u5fc3\uff0c\u6211\u5730\u4e0d\u4e58\u4eba\u4e4b\u5371\uff0c\u5018\u82e5\u534e \u8001\u5148\u751f\u4eca\u665a\u7cbe\u795e\u4e0d\u6d4e\uff0c\u6211\u6539\u65e5\u9886\u6559\uff0c\u4e5f\u65e0\u4e0d\u53ef\u3002\u6015\u53ea\u6015\u4e24\u864e\u6f9c\u6597\uff0c\u5fc5\u6709\u4e00\u4f24\uff0c\u6211\u7eb5\u6709\u5fc3\u5411\u534e \u8001\u5148\u751f\u9886\u6559\uff0c\u4e5f\u96be\u5982\u613f\u3002\u201d \u3000\u3000\u534e\u592d\u98ce\u4e00\u89c1\u6b27\u9633\u592b\u5987\u4e0e\u84b2\u5362\u864e\u540c\u5728\u6b64\u5730\uff0c\u7acb\u5373\u604d\u7136\u5927\u609f\uff0c\u8fd9\u84b2\u5362\u864e\u5c31\u662f\u5f97\u5230\u6b27\u9633\u4e8c\u5a18\u7684 \u901a\u98ce\u62a5\u4fe1\uff0c\u624d\u4f1a\u9884\u5148\u5728\u81ea\u5df1\u5fc5\u7ecf\u4e4b\u5730\u76f8\u5019\u7684\u3002\u4f46\u4ed6\u4e5f\u5e76\u4e0d\u6233\u7834\uff0c\u5374\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u591a\u8499\u4e24\u4f4d\u6709 \u5fc3\u76f8\u5019\uff0c\u6211\u534e\u67d0\u5c82\u6562\u4ee4\u670b\u53cb\u5931\u671b\u800c\u5f52\uff0c\u5c31\u8bf7\u4e24\u4f4d\u90fd\u6765\u8d50\u6559\u597d\u4e86\u3002\u53ea\u662f\u6211\u8fd9\u4f4d\u5c0f\u53cb\u4e0e\u6b64\u4e8b\u65e0 \u6d89\uff0c\u4e5f\u5e76\u975e\u6211\u8bf7\u6765\u52a9\u62f3\u4e4b\u4eba\uff0c\u8fd8\u8bf7\u4e24\u4f4d\u6309\u7167\u6c5f\u6e56\u89c4\u77e9\uff0c\u5c06\u4ed6\u91ca\u653e\u624d\u662f\u3002\u201d \u3000\u3000\u539f\u6765\u534e\u5929\u98ce\u65e9\u5df2\u77e5\u9053\u4ed6\u4eec\u592b\u59bb\u7684\u5978\u72e1\u72e0\u6bd2\uff0c\u6240\u8bf4\u7684\u8bdd\u7edd\u96be\u76f8\u4fe1\u3002\u800c\u4e14\u4ee5\u534e\u5929\u98ce\u7684\u8eab\u4efd\uff0c \u4e5f\u4e0d\u80fd\u5411\u6b27\u9633\u4ef2\u548c\u793a\u5f31\uff0c\u6240\u4ee5\u4e0e\u5176\u8981\u63d0\u9632\u4ed6\u4eec\u7684\u6697\u7b97\uff0c\u4e0d\u5982\u7d22\u6027\u628a\u8bdd\u8bf4\u660e\uff0c\u8ba9\u4ed6\u4eec\u4e8c\u4eba\u540c\u4e0a\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u563f\u563f\u51b7\u7b11\uff0c\u534e\u5929\u51e4\u53cc\u7709\u4e00\u8f69\uff0c\u4ea2\u58f0\u8bf4\u9053\uff0c\u201c\u600e\u4e48\uff0c\u53ef\u662f\u8001\u673d\u7684\u8bdd\u8bf4\u9519\u4e86\u4e48\uff1f\u201d \u6b27\u9633\u4ef2\u548c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u534e\u8001\u5148\u751f\u7684\u8bdd\u6ca1\u9519\uff0c\u8fd9\u59d3\u6c5f\u7684\u5c0f\u5b50\u786e\u5b9e\u4e0e\u4f60\u4eec\u4eca\u665a\u7684\u7ea6\u4f1a\u65e0\u5173\uff0c\u4f46\u5374 \u4e0e\u6211\u6b27\u9633\u4ef2\u548c\u6709\u5173\uff0c\u4ed6\u8bf1\u60d1\u4e86\u6211\u7684\u5973\u513f\uff0c\u8d25\u574f\u4e86\u6211\u7684\u95e8\u98ce\uff0c\u6545\u6b64\u6211\u8981\u64d2\u4ed6\u56de\u53bb\u6cbb\u7f6a\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u65e0\u4eb2\u4ed6\u7684\u7a74\u9053\u672a\u89e3\uff0c\u6709\u53e3\u96be\u8a00\u3002\u534e\u5929\u98ce\u51b7\u7b11\u9053\uff0c\u201c\u6211\u542c\u5f97\u5c0a\u592b\u4eba \u53ef\u4e0d\u662f\u5982\u6b64\u8bf4\u3002\u5c0a\u592b\u4eba\u4e0e\u4ee4\u5a9b\u65e5\u524d\u66fe\u5230\u5bd2\u820d\uff0c\u6211\u4eb2\u8033\u542c\u5f97\u4ed6\u4eec\u5411\u6c5f\u76f8\u516c\u9053\u8c22\uff0c\u8bf4\u662f\u5018\u975e\u6c5f\u76f8 \u516c\u76f8\u6551\uff0c\u4ee4\u5a9b\u5df2\u96be\u514d\u53d7\u6076\u5e08\u7684\u6bd2\u5211\u4e86\u3002\u201d \u3000\u3000\u6b27\u9633\u4ef2\u548c\u9053\uff1a\u201c\u534e\u8001\u5148\u751f\uff0c\u4f60\u4e5f\u662f\u8001\u4e8e\u4e16\u6545\u7684\u4e86\uff0c\u96be\u9053\u8fde\u8fd9\u4e2a\u4e5f\u4e0d\u89e3\u4e48\uff1f\u6b64\u4e00\u65f6\uff0c\u5f7c\u4e00 \u65f6\u3002\u90a3\u65f6\u4f60\u4e0e\u4ef2\u5316\u5b50\u5728\u4e00\u8d77\uff0c\u5979\u4eec\u529b\u6709\u4e0d\u654c\uff0c\u5f53\u7136\u53ea\u6709\u5982\u6b64\u8bf4\u6cd5\u3002\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u597d\uff0c\u5c31\u7b97 \u90a3\u662f\u5c0a\u592b\u4eba\u7684\u780c\u8f9e\uff0c\u4f46\u73b0\u5728\u4e5f\u4e0d\u80fd\u53ea\u542c\u4f60\u4eec\u4e00\u9762\u4e4b\u8f9e\uff0c\u4f60\u628a\u6c5f\u76f8\u516c\u7684\u7a74\u9053\u89e3\u5f00\uff0c\u8ba9\u4ed6\u4e5f\u8bf4 \u8bf4\u3002\u201d \u3000\u3000\u6b27\u9633\u4ef2\u548c\u9762\u8272\u4e00\u6c89\uff0c\u5ced\u58f0\u8bf4\u9053\uff1a\u201c\u534e\u8001\u5148\u751f\uff0c\u4f60\u5de6\u4e00\u53e5\u6c5f\u6e56\u89c4\u77e9\uff0c\u53f3\u4e00\u53e5\u6c5f\u6e56\u89c4\u77e9\uff0c\u8fd9 \u4e2a\u89c4\u77e9\u4f60\u603b\u61c2\u5f97\u5427\uff1f\u6c5f\u6e56\u4e4b\u4e8b\uff0c\u80dc\u8005\u4e3a\u5f3a\uff0c\u4f60\u6709\u672c\u9886\uff0c\u5c3d\u53ef\u81ea\u5df1\u53bb\u89e3\u4ed6\u7684\u7a74\u9053\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u5927\u6012\uff0c\u5530\u7684\u4e00\u58f0\uff0c\u62d4\u5251\u51fa\u9798\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6b27\u9633\u4ef2\u548c\uff0c\u4f60\u53d1\u62db\u5427\uff01\u201d \u3000\u3000\u84b2\u5362\u864e\u5ffd\u5730\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u65e9\u4e9b\u52a8\u624b\uff0c\u514d\u5f97\u8bb8\u591a\u5435\u5506\uff01\u534e\u5929\u98ce\uff0c\u6709\u672c\u9886\u4f60\u518d \u524a\u6211\u4e00\u6761\u81c2\u818a\u3002\u6b27\u9633\u4e8c\u54e5\uff0c\u8bf7\u8ba9\u6211\u5149\u62a5\u6b64\u4ec7\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u62a2\u4e0a\u524d\u53bb\uff0c\u4fbf\u662f\u4e00\u638c\u3002 \u3000\u3000\u534e\u65e0\u98ce\u89c1\u4ed6\u624b\u638c\u4e00\u8d77\uff0c\u4fbf\u662f\u8165\u98ce\u6251\u9f3b\uff0c\u4e5f\u4e0d\u7531\u788d\u5fc3\u5934\u4e00\u51db\uff0c\u4f46\u4ed6\u60ef\u7ecf\u5927\u654c\uff0c\u867d\u77e5\u84b2\u5362\u864e \u7684\u6bd2\u638c\u5df2\u6bd4\u4ece\u524d\u7ec3\u5f97\u66f4\u4e3a\u6b79\u6bd2\uff0c\u5374\u4e5f\u4e0d\u60e7\u3002\u4ed6\u811a\u8e0f\u738b\u95e8\u516b\u5366\u65b9\u4f4d\uff0c\u500f\u5730\u4e00\u4e2a\u201c\u79fb\u5bab\u6362\u4f4d\u201d\uff0c \u957f\u5251\u4e00\u6307\uff0c\u4e00\u62db\u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\uff0c\u5df2\u62a2\u5230\u84b2\u5362\u864e\u7684\u607b\u8fb9\uff0c\u4e00\u5251\u5411\u4ed6\u7684\u624b\u8155\u523a\u53bb\u3002 \u3000\u3000\u53cc\u65b9\u8ddd\u79bb\u8fd8\u6709\u6570\u6b65\uff0c\u4f46\u534e\u5929\u98ce\u7684\u5251\u5c16\u4e00\u98a4\uff0c\u4fbf\u542c\u5f97\u201c\u55e4\u55e4\u201d\u58f0\u54cd\uff0c\u539f\u6765\u662f\u5251\u98ce\u6fc0\u8361\u6c14 \u6d41\uff0c\u5251\u672a\u523a\u5230\uff0c\u90a3\u80a1\u65e0\u5f62\u7684\u52b2\u529b\u5df2\u5148\u88ad\u6765\u3002\u84b2\u5362\u864e\u53ea\u89c9\u624b\u8155\u523a\u75db\uff0c\u4e00\u638c\u6253\u6b6a\uff0c\u534e\u65e0\u98ce\u7684\u957f\u5251 \u4e00\u5708\uff0c\u4fbf\u6765\u524a\u4ed6\u7684\u624b\u6307\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u84e6\u5730\u4e00\u58f0\u5927\u559d\uff0c\u4e00\u638c\u4fbf\u5288\u8fc7\u53bb\uff0c\u534e\u5929\u98ce\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u8fd8\u662f\u4e00\u9f50\u4e0a\u7684 \u597d\uff01\u201d\u6b27\u9633\u4ef2\u548c\u4e00\u638c\u5288\u51fa\uff0c\u63a5\u7740\u4e00\u6307\u6233\u6765\uff0c\u534e\u65e0\u98ce\u53f3\u624b\u7684\u957f\u5251\u4ecd\u7136\u6307\u5411\u84b2\u5362\u864e\uff0c\u5de6\u624b\u7684\u957f\u8896 \u4e00\u62c2\uff0c\u7adf\u7136\u4e5f\u7528\u7684\u662f\u6d41\u4e91\u5251\u6cd5\uff0c\u957f\u8896\u6325\u52a8\uff0c\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u5411\u6b27\u9633\u4ef2\u548c\u7684\u864e\u53e3\u201c\u65a9\u201d\u4e0b\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u8bc6\u5f97\u5389\u5bb3\uff0c\u6025\u5fd9\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u56de\u6307\u6233\u51fa\uff0c\u8fd9\u4e00\u6765\u4ed6\u90a3\u4e00\u638c\u5c31\u5288\u4e86\u4e2a\u7a7a\uff0c \u53ef\u662f\uff0c\u534e\u65e0\u98ce\u7684\u8863\u8896\u4e5f\u6ca1\u6709\u201c\u65a9\u201d\u4e2d\u4ed6\u7684\u864e\u53e3\uff0c\u4ed6\u98df\u6307\u4e00\u4f38\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5374\u628a\u534e\u5929\u98ce\u7684 \u8863\u8896\u6233\u7834\u4e86\u4e00\u6761\u88c2\u7f1d\u3002 \u3000\u3000\u534e\u5929\u98ce\u4e5f\u8bc6\u5f97\u6b27\u9633\u4ef2\u548c\u7684\u5389\u5bb3\uff0c\u4ed6\u8fd9\u4e00\u62db\u201c\u6d41\u4e91\u98de\u8896\u201d\u672c\u6765\u53ea\u662f\u60f3\u5316\u89e3\u5bf9\u65b9\u7684\u638c\u529b\u7684\uff0c \u4f46\u4ed6\u5374\u5ffd\u89c6\u4e86\u6b27\u9633\u4ef2\u548c\u7684\u6307\u529b\uff0c\u54ea\u77e5\u6b27\u9633\u4ef2\u548c\u7684\u6307\u529b\u7adf\u53e6\u6709\u4e00\u529f\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u7684\u624b\u6307\u5e76\u6ca1\u6709\u6cbe\u7740\u534e\u5929\u98ce\u7684\u76ae\u8089\uff0c\u4f46\u5728\u4ed6\u624b\u6307\u6233\u7a7f\u8863\u8896\u4e4b\u9645\uff0c\u534e\u5929\u98ce\u5374\u9661\u5730\u611f \u5230\u4e00\u80a1\u70ed\u6d6a\u8feb\u6765\uff0c\u76ae\u8089\u7adf\u4f3c\u9760\u8fd1\u4e00\u5757\u70e7\u7ea2\u7684\u94c1\u677f\u4f3c\u7684\uff0c\u8981\u4e0d\u662f\u534e\u5929\u98ce\u5185\u529f\u6df1\u539a\uff0c\u6362\u4e86\u4ed6\u4eba\uff0c \u81c2\u76ae\u53ef\u80fd\u5c31\u8981\u70e7\u7126\u3002 \u3000\u3000\u8fd9\u4e09\u4e2a\u4eba\u7684\u52a8\u4f5c\u90fd\u5feb\u5982\u95ea\u7535\uff0c\u84b2\u5362\u864e\u5f97\u6b27\u9633\u4ef2\u548c\u4e4b\u52a9\uff0c\u4e5f\u907f\u5f00\u4e86\u534e\u5929\u98ce\u90a3\u4e00\u5251\uff0c\u4f46\u542c\u5f97 \u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u534e\u5929\u98ce\u7684\u957f\u5251\u5df2\u88ab\u84b2\u5362\u864e\u4e00\u6307\u5f39\u5f00\uff0c\u4f46\u534e\u5929\u98ce\u7acb\u5373\u6a2a\u5251\u4e00\u5c01\uff0c\u5251\u5c16\u6296\u52a8\uff0c\u5c06 \u84b2\u5362\u864e\u7684\u4efb\u3001\u7763\u4e8c\u8109\u7684\u4e03\u5904\u5927\u7a74\uff0c\u90fd\u7b3c\u7f69\u5728\u5251\u52bf\u4e4b\u4e0b\uff0c\u84b2\u5362\u864e\u5f53\u5e74\u66fe\u5728\u4ed6\u8fd9\u53e3\u5251\u4e0b\u5403\u8fc7\u5927 \u4e8f\uff0c\u8fd9\u65f6\u867d\u7136\u5360\u4e86\u70b9\u5c0f\u5c0f\u7684\u4fbf\u5b9c\uff0c\u5fc3\u4e2d\u8fd8\u662f\u754f\u4ff1\uff0c\u4e0d\u6562\u8d38\u7136\u7684\u6251\u8fc7\u6765\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u5374\u662f\u4e00\u9000\u590d\u4e0a\uff0c\u54c8\u54c8\u7b11\u9053\uff0c\u201c\u534e\u8001\u524d\u8f88\u65e2\u8981\u4f38\u91cf\u4e8e\u6211\uff0c\u6211\u4e5f\u53ea\u6709\u606d\u656c\u4e0d\u5982\u4ece\u547d \u4e86\u3002\u201d\u7b11\u58f0\u672a\u4e86\uff0c\u9661\u5730\u53c8\u662f\u5927\u559d\uff0c\u638c\u6307\u517c\u65bd\u3002\u534e\u5929\u98ce\u8fd9\u65f6\u77a7\u5f97\u5206\u660e\uff0c\u4ed6\u6233\u6765\u7684\u8fd9\u4e00\u6307\uff0c\u6307\u5934 \u8d64\u7ea2\u5982\u706b\u3002 \u3000\u3000\u534e\u5929\u98ce\u4e5f\u7981\u4e0d\u4f4f\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u539f\u6765\u4ed6\u7adf\u7ec3\u6210\u4e86\u9739\u96f3\u638c\u4e0e\u96f7\u795e\u6307\u8fd9\u4e24\u95e8\u7edd\u6280\uff01\u201d\u8fd9\u4e24\u95e8\u7edd \u6280\u90fd\u662f\u6781\u5176\u5389\u5bb3\u7684\u90aa\u6d3e\u529f\u5927\uff0c\u9739\u96f3\u638c\u7528\u7684\u662f\u9633\u5219\u4e4b\u529b\uff0c\u96f7\u795e\u6307\u5219\u66f4\u662f\u4ee5\u672c\u8eab\u7684\u7eaf\u9633\u4e4b\u6c14\u53d1 \u51fa\uff0c\u4e24\u8005\u914d\u5408\uff0c\u76f8\u5f97\u76ca\u5f70\uff0c\u800c\u5df2\u4ed6\u6bcf\u6b21\u53d1\u638c\u4e4b\u65f6\u90a3\u4e00\u58f0\u5927\u559d\uff0c\u4e5f\u8db3\u4ee5\u4e0e\u4f5b\u95e8\u7684\u201c\u72ee\u5b50\u543c\u529f\u201d \u5339\u654c\uff0c\u540c\u6837\u53ef\u4ee5\u7528\u6765\u5236\u654c\u5fc3\u795e\uff0c\u914d\u5408\u4ed6\u90a3\u9739\u96f3\u638c\u4e0e\u96f7\u795e\u6307\u7684\u8fdb\u653b\u3002 \u3000\u3000\u534e\u5929\u98ce\u6c89\u7740\u5e94\u4ed8\uff0c\u5251\u5149\u970d\u970d\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u4ed6\u8fd9\u6d41\u4e91\u5251\u6cd5\u5c55\u5f00\uff0c\u5f53\u771f\u6709\u5982\u6d41\u6c34\u884c\u4e91\uff0c\u98d8\u9038 \u4e4b\u6781\u3002\u6b27\u9633\u4ef2\u548c\u4e0e\u84b2\u5362\u864e\u8fde\u756a\u653b\u51fb\uff0c\u518d\u4e5f\u4e0d\u80fd\u78b0\u7740\u4ed6\u7684\u8eab\u5b50\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5728\u7f51\u91cc\u51dd\u795e\u89c2\u6218\uff0c\u5ffd\u89c1\u534e\u5929\u98ce\u7684\u5251\u5149\u6e10\u6e10\u9eef\u6de1\uff0c\u4ed6\u90a3\u67c4\u957f\u5251\u672c\u662f\u5149\u534e\u8000\u76ee\u7684\uff0c\u8fc7 \u4e86\u4e00\u4f1a\uff0c\u7adf\u53d8\u5f97\u5982\u540c\u987d\u94c1\u4e00\u822c\uff0c\u9eef\u7136\u65e0\u5149\u3002\u539f\u6765\u4ed6\u7684\u957f\u5251\u88ab\u84b2\u5362\u864e\u7684\u6bd2\u6307\u5f39\u4e2d\u3001\u5251\u8eab\u90fd\u5df2\u53d8 \u5f97\u7d2b\u9ed1\u4e86\u3002\u800c\u4e14\u4ed6\u7684\u5251\u4e0a\u67d3\u4e86\u6bd2\uff0c\u968f\u65f6\u90fd\u6709\u53ef\u80fd\u4f20\u5230\u4ed6\u7684\u624b\u4e0a\uff0c\u56e0\u6b64\u534e\u5929\u98ce\u4e0d\u5f97\u4e0d\u52a0\u500d\u7559 \u5fc3\u3002\u6c5f\u6d77\u592d\u6b63\u5728\u6697\u6697\u60ca\u5fc3\uff0c\u5ffd\u542c\u5f97\u53c8\u662f\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u8fd9\u4e00\u56de\u84b2\u5362\u864e\u7adf\u7136\u5f39\u4e2d\u4e86\u4ed6\u7684\u5251\u67c4\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u8d70\u5230\u6811\u4e0b\uff0c\u7b11\u541f\u541f\u5730\u9053\uff1a\u201c\u6c5f\u76f8\u516c\uff0c\u4f60\u660e\u767d\u6211\u7684\u4e00\u7247\u82e6\u5fc3\u4e86\u5427\uff0c\u8981\u4e0d\u662f\u6211\u5c06\u4f60 \u64d2\u5728\u7f51\u4e2d\uff0c\u4f60\u5b9a\u7136\u53bb\u52a9\u90a3\u534e\u8005\u5934\u513f\uff0c\u5c82\u4e0d\u662f\u767d\u642d\u4e86\u4e00\u6761\u6027\u547d\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u95f7\u54fc\u4e00\u58f0\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f60\u4e5f\u5fd2\u628a\u534e\u8001\u524d\u8f88\u770b\u5c0f\u4e86\uff0c\u4f60\u4ee5\u4e3a\u4ed6\u5c31\u51c6\u8f93\u4e86\u4e48\uff1f\u201d\u8fd9 \u51e0\u53e5\u8bdd\u4ed6\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u5c31\u8981\u8bf4\u51fa\u6765\uff0c\u4f46\u8bdd\u5230\u53e3\u8fb9\uff0c\u4ecd\u7136\u541e\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u61c2\u5f97\u98a0\u5012\u7a74\u9053\u7684\u529f\u592b\uff0c\u8fd9\u65f6\u4ed6\u5df2\u6697\u81ea\u8fd0\u6c14\uff0c\u89e3\u5f00\u4e86\u7a74\u9053\uff0c\u4f46\u4e00\u6765\u4ed6\u7a74\u9053\u521d \u89e3\uff0c\u6c14\u8840\u8fd0\u884c\u5c1a\u672a\u6b63\u5e38\uff0c\u624b\u8db3\u4ecd\u7136\u611f\u5230\u9ebb\u6728\u4e0d\u7075\u3002\u4e8c\u6765\u4ed6\u5728\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e5f\u8fd8\u672a\u60f3\u51fa\u7834\u7f51\u800c\u51fa \u7684\u529e\u6cd5\uff0c\u6545\u6b64\u4ed6\u4ecd\u7136\u8981\u88c5\u4f5c\u7a74\u9053\u672a\u89e3\u3002\u4e0d\u80fd\u8bf4\u8bdd\u3001\u514d\u5f97\u6b27\u9633\u4e8c\u5a18\u518d\u52a0\u5bb3\u4ed6\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u534e\u5929\u98ce\u4e00\u58f0\u53f1\u54a4\uff0c\u201c\u55d6\u201d\u7684\u4e00\u5251\u524a\u8fc7\u53bb\uff0c\u968f\u7740\u98de\u8d77\u4e00\u84ec\u4e1c \u897f\uff0c\u4f3c\u4e71\u8349\u4e00\u822c\u5728\u7a7a\u4e2d\u98d8\u4e0b\uff0c\u539f\u6765\u662f\u84b2\u5362\u864e\u90a3\u4e00\u5934\u4e71\u53d1\uff0c\u5df2\u7ed9\u534e\u5929\u98ce\u4e00\u5251\u524a\u53bb\u4e86\u4e00\u5927\u7247\uff0c\u51e0 \u4e4e\u53d8\u6210\u5149\u5934\u4e86\u3002 \u3000\u3000\u8584\u5362\u864e\u4e00\u65f6\u5192\u8fdb\uff0c\u867d\u7136\u5f39\u4e2d\u4e86\u534e\u5929\u98ce\u7684\u5251\u67c4\uff0c\u81ea\u5df1\u5374\u51e0\u4e4e\u9001\u4e86\u547d\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u5bd2\u5fc3\uff0c\u8fde \u8fde\u540e\u9000\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u9053\uff1a\u201c\u84b2\u5144\u4e0d\u5fc5\u7740\u614c\uff0c\u770b\u6211\u7684\uff01\u201d\u4ed6\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u638c\u4e00\u6413\u3002\u5ffd\u5730\u6e85\u51fa\u70b9\u70b9\u706b \u661f\uff0c\u968f\u7740\u4ed6\u624b\u6307\u6240\u6233\u4e4b\u5904\uff0c\u534e\u5929\u98ce\u7684\u957f\u886b\u5df2\u7136\u7740\u706b\uff0c\u767b\u65f6\u70e7\u4e86\u8d77\u6765\u3002 \u3000\u3000\u534e\u65e0\u98ce\u559d\u9053\uff1a\u201c\u8fd9\u4ef6\u7834\u8863\u9001\u4e86\u7ed9\u4f60\u5427\uff01\u201d\u4e5f\u4e0d\u89c1\u4ed6\u7528\u624b\u8131\u8863\uff0c\u90a3\u4ef6\u957f\u886b\u5df2\u7136\u89e3\u5f00\uff0c\u4ed6\u7528 \u4e86\u4e00\u4e2a\u8eab\u6cd5\uff0c\u500f\u5730\u4e00\u4e2a\u76d8\u65cb\uff0c\u53cc\u80a9\u6446\u52a8\uff0c\u6574\u4ef6\u957f\u886b\u4fbf\u98de\u4e86\u51fa\u53bb\uff0c\u5e26\u7740\u878d\u878d\u7684\u706b\u5149\uff0c\u5411\u6b27\u9633\u4ef2 \u548c\u7f69\u4e0b\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u867d\u6ca1\u7ed9\u957f\u886b\u7f69\u7740\uff0c\u4f46\u4e5f\u5413\u4e86\u4e00\u5927\u8df3\uff0c\u534e\u65e0\u98ce\u63c9\u8eab\u6025\u8ffd\uff0c\u5693\u5693\u5693\u8fde\u73af\u6570\u5251\uff0c\u628a\u6b27 \u9633\u4ef2\u548c\u8feb\u5f97\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u84b2\u5362\u864e\u5ffd\u7136\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6b27\u9633\u4e8c\u54e5\uff0c\u4e0d\u5fc5\u6015\u4ed6\uff0c\u4ed6\u5df2\u662f\u5f3a\u5f29\u4e4b\u672a\u4e86\u3002\u201d\u6325\u821e\u5355\u81c2\uff0c\u518d \u6765\u653b\u51fb\u534e\u5929\u98ce\uff0c\u706b\u5149\u4e2d\uff0c\u6b27\u9633\u4ef2\u548c\u89c1\u534e\u5929\u98ce\u7684\u9762\u4e0a\u5df2\u900f\u9732\u4e00\u91cd\u9ed1\u6c14\uff0c\u6b27\u9633\u4ef2\u548c\u604d\u7136\u5927\u609f\uff0c\u7b11 \u9053\uff1a\u201c\u84b2\u5144\uff0c\u4f60\u7684\u6bd2\u624b\u5929\u5c0a\u7684\u79f0\u53f7\uff0c\u679c\u7136\u540d\u4e0b\u65e0\u865a\u3002\u201d \u3000\u3000\u84b2\u5362\u864e\u9053\uff1a\u201c\u6b27\u9633\u4e8c\u54e5\uff0c\u4f60\u4e0d\u8981\u53ea\u662f\u8d5e\u6211\uff0c\u534e\u8001\u5148\u751f\u53f7\u79f0\u534e\u5c71\u533b\u9690\uff0c\u672c\u9886\u4e5f\u786e\u662f\u4e0d\u51e1\u3002 \u53ef\u60dc\u4ed6\u73b0\u5728\u4e0d\u80fd\u4e13\u5fc3\u8fd0\u529f\u7597\u6cbb\uff1b\u518d\u8fc7\u4e00\u65f6\u4e09\u523b\uff0c\u53ea\u6015\u4ed6\u7684\u624b\u8db3\u4fbf\u8981\u4e0d\u542c\u4f7f\u5524\u4e86\u3002\u54c8\uff0c\u54c8\uff0c\u534e \u8001\u5148\u751f\uff0c\u4f60\u6df1\u901a\u533b\u672f\uff0c\u600e\u4e48\u8fd8\u8981\u548c\u6211\u4eec\u62fc\u547d\uff1f\u4f60\u53ef\u77e5\u9053\uff0c\u4f60\u6253\u5f97\u8d8a\u51f6\uff0c\u6bd2\u5c31\u8981\u53d1\u4f5c\u5f97\u8d8a\u5feb \u4e48\uff1f\u201d\u4ed6\u548c\u6b27\u9633\u4ef2\u548c\u4e00\u5531\u4e00\u548c\uff0c\u610f\u6b32\u74e6\u89e3\u534e\u65e0\u98ce\u7684\u6218\u610f\uff0c\u4ee4\u4ed6\u4e0d\u6218\u81ea\u5c48\u3002 \u3000\u3000\u54ea\u77e5\u534e\u5929\u98ce\u5374\u4e5f\u5ffd\u5730\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u539f\u6765\u6211\u8fd8\u6709\u4e00\u65f6\u4e09\u523b\u53ef\u4ee5\u6d3b\u547d\u4e48\uff0c\u597d\uff0c\u90a3\u4e48\u4f60\u4eec\u5c31 \u9003\u4e0d\u8fc7\u4e00\u65f6\u4e09\u523b\u4e86\u3002\u201d \u3000\u3000\u7b11\u58f0\u4e2d\u53ea\u89c1\u534e\u5929\u98ce\u8eab\u5f62\u4e00\u6643\uff0c\u4fe8\u5982\u9e70\u96bc\u7a7f\u6797\uff0c\u500f\u7136\u95f4\u5df2\u6b3a\u8eab\u5230\u4e86\u84b2\u5362\u864e\u7684\u8eab\u524d\uff0c\u4f46\u542c\u5f97 \u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u53cc\u638c\u78b0\u4e2a\u6b63\u7740\uff0c\u8fd9\u4e00\u638c\u76f4\u628a\u84b2\u5362\u864e\u9707\u51fa\u4e86\u6570\u4e08\u5f00\u5916\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4ed6\u500f \u5730\u4e00\u4e2a\u8f6c\u8eab\uff0c\u53c8\u8fce\u4e0a\u4e86\u6b27\u9633\u4ef2\u548c\uff0c\u4e00\u62db\u201c\u661f\u843d\u4e5d\u5929\u201d\uff0c\u5251\u5c16\u6296\u8d77\u4e86\u4e5d\u6735\u5251\u82b1\uff0c\u8fde\u88ad\u6b27\u9633\u4ef2\u548c \u7684\u4e5d\u5904\u5927\u7a74\uff0c\u6b27\u9633\u4ef2\u548c\u786e\u4e5f\u4e86\u5f97\uff0c\u5728\u767e\u5fd9\u4e2d\u4f7f\u51fa\u4e86\u201c\u94c1\u677f\u6865\u201d\u7684\u529f\u592b\uff0c\u53cc\u8db3\u9489\u5728\u5730\u4e0a\uff0c\u8eab\u8eaf \u540e\u5f2f\uff0c\u5e73\u76f4\u5f97\u7adf\u5982\u94c1\u677f\u4e00\u822c\uff0c\u534e\u5929\u98ce\u5530\u7684\u4e00\u5251\u4ece\u4ed6\u7684\u9762\u95e8\u524a\u8fc7\uff0c\u7adf\u672a\u66fe\u4f24\u7740\u4ed6\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u5927\u559d\u4e00\u58f0\uff0c\u8d81\u7740\u534e\u5929\u98ce\u5251\u62db\u4f7f\u8001\uff0c\u65b0\u62db\u6b32\u53d1\u672a\u53d1\u4e4b\u9645\uff0c\u65e9\u5df2\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253 \u633a\u201d\uff0c\u7ffb\u8d77\u8eab\u6765\uff0c\u53cc\u8db3\u9f50\u98de\uff0c\u638c\u6307\u4e95\u53d1\uff0c\u53cc\u8db3\u4ea4\u53c9\u8e22\u534e\u5929\u98ce\u819d\u76d6\u7684\u201c\u73af\u8df3\u7a74\u201d\uff0c\u638c\u5288\u989d\u95e8\uff0c \u6307\u6233\u524d\u80f8\u3002\u5239\u90a3\u4e4b\u95f4\uff0c\u56db\u62db\u5e76\u7528\uff0c\u5f53\u771f\u662f\u5389\u5bb3\u975e\u5e38\u3002\u54ea\u77e5\u534e\u5929\u98ce\u65e9\u6599\u5230\u4ed6\u5982\u6b64\u5e94\u7740\uff0c\u957f\u5251\u4e00 \u5708\uff0c\u5df2\u662f\u8eab\u968f\u5251\u8f6c\uff0c\u53cd\u524a\u4ed6\u7684\u53cc\u8db3\uff0c\u540c\u65f6\u9a88\u6307\u5982\u621f\uff0c\u4ece\u5251\u5e95\u4e0b\u500f\u7136\u6233\u51fa\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u8db3\u672a\u7740\u5730\uff0c\u987e\u4e0d\u5f97\u653b\u654c\uff0c\u4e24\u819d\u4e00\u78b0\uff0c\u7adf\u5c06\u8eab\u5f62\u5411\u65c1\u8fb9\u632a\u5f00\u4e86\u4e09\u5c3a\uff0c\u4f46\u534e\u65e0\u98ce\u8fd9 \u4e00\u5251\u4ed6\u867d\u7136\u907f\u8fc7\uff0c\u638c\u5fc3\u5374\u5df2\u7ed9\u534e\u5929\u98ce\u7684\u53cc\u6307\u6233\u4e0a\uff0c\u9976\u662f\u6b27\u9633\u4ef2\u548c\u529f\u529b\u6df1\u6e5b\uff0c\u7ed9\u4ed6\u8fd9\u4e48\u4e00\u6233\uff0c \u4e5f\u4f3c\u94f6\u9488\u523a\u4f53\u4e00\u822c\uff0c\u5185\u5bb6\u771f\u6c14\u4e5f\u7ed9\u4ed6\u8fd9\u4e00\u6233\u6cc4\u4e86\u51e0\u5206\uff0c\u9739\u96f3\u638c\u7684\u529f\u529b\u4e5f\u56e0\u800c\u51cf\u4e86\u3002 \u3000\u3000\u4f46\u534e\u5929\u98ce\u4ee5\u6307\u654c\u638c\uff0c\u6307\u529b\u603b\u662f\u4e0d\u5982\u638c\u529b\uff0c\u534e\u65e0\u98ce\u7684\u7528\u610f\u5728\u7834\u4ed6\u9739\u96f3\u638c\u7684\u9633\u521a\u5185\u52b2\uff0c\u867d\u7136 \u594f\u6548\uff0c\u53ef\u662f\u672c\u8eab\u4e5f\u7ed9\u4ed6\u7684\u638c\u529b\u9707\u5f97\u8dc4\u8dc4\u8e09\u8e09\uff0c\u8fde\u9000\u6570\u6b65\u3002 \u3000\u3000\u84b2\u5362\u864e\u201c\u7566\u201d\u7684\u4e00\u58f0\uff0c\u5410\u51fa\u4e86\u4e00\u53e3\u9c9c\u8840\uff0c\u7ffb\u8eab\u590d\u4e0a\uff0c\u5927\u6012\u559d\u9053\uff0c\u201c\u534e\u8001\u8d3c\uff0c\u4f60\u5f53\u771f\u4e0d\u8981 \u6027\u547d\u4e86\u4e48\uff1f\u8001\u5b50\u548c\u4f60\u62fc\u4e86\uff01\u201d\u534e\u5929\u98ce\u51b7\u7b11\u9053\uff1a\u201c\u8981\u62fc\u66f4\u62fc\uff0c\u4f55\u5fc5\u591a\u8a00\uff01\u201d\u6325\u638c\u8fce\u4e0a\uff0c\u4e1d\u6beb\u4e0d \u6015\u4ed6\u7684\u6bd2\u638c\uff01 \u3000\u3000\u539f\u6765\u534e\u65e0\u98ce\u56e0\u4e3a\u5df2\u7ecf\u4e2d\u6bd2\uff0c\u6240\u4ee5\u53cd\u800c\u4e0d\u4f3c\u5148\u524d\u987e\u5fcc\uff0c\u4ed6\u5fc3\u91cc\u8fd9\u6837\u6253\u7b97\uff0c\u4eca\u665a\u4e4b\u6218\uff0c\u53ea\u6709 \u4e24\u4e2a\u53ef\u80fd\uff0c\u4e00\u4e2a\u662f\u6b7b\u5728\u654c\u4eba\u4e4b\u624b\uff0c\u90a3\u4e48\u53d7\u6bd2\u6df1\u4e9b\u6216\u6d45\u4e9b\uff0c\u90fd\u662f\u4e00\u6837\uff1b\u53e6\u4e00\u4e2a\u53ef\u80fd\u662f\u81ea\u5df1\u6253\u8d25 \u4e86\u654c\u4eba\uff0c\u90a3\u4e48\u53cd\u6b63\u5df2\u4e2d\u4e86\u6bd2\uff0c\u8981\u5728\u4e8b\u540e\u6cbb\u7597\uff0c\u53d7\u6bd2\u6df1\u4e9b\uff0c\u4ea6\u4e0d\u8fc7\u591a\u8d39\u4e9b\u65f6\u65e5\u6cbb\u7597\u800c\u5df2\u3002\u6240\u4ee5 \u534e\u5929\u98ce\u987e\u5fcc\u4e4b\u5fc3\u4e00\u53bb\uff0c\u4fbf\u7d22\u6027\u786c\u63a5\u84b2\u5362\u864e\u7684\u6bd2\u638c\u4e86\u3002 \u3000\u3000\u534e\u5929\u98ce\u6218\u6cd5\u4e00\u53d8\uff0c\u53cc\u65b9\u6076\u6597\u5f97\u66f4\u5176\u6fc0\u70c8\uff0c\u5f53\u771f\u53ef\u4ee5\u8bf4\u5f97\u662f\u820d\u6b7b\u5fd8\u751f\u3002\u6c5f\u6d77\u5929\u770b\u5f97\u76ee\u7729\u795e \u6447\uff0c\u60ca\u5fc3\u4e0d\u5df2\u3002\u4e0d\u8fc7\u7247\u523b\uff0c\u53ea\u89c1\u6b27\u9633\u4ef2\u548c\u4e2d\u4e86\u534e\u5929\u98ce\u7684\u4e00\u5251\uff0c\u800c\u534e\u5929\u98ce\u4e5f\u7ed9\u4ed6\u6233\u4e86\u4e00\u6307\uff0c\u6b27 \u9633\u4ef2\u548c\u8840\u67d3\u8863\u88f3\uff0c\u534e\u5929\u98ce\u7684\u7d27\u8eab\u5185\u886b\u4e5f\u7ed9\u6233\u7a7f\uff0c\u9732\u51fa\u4e86\u4e00\u7247\u7126\u9ed1\u7684\u76ae\u8089\u3002\u6c5f\u6d77\u5929\u8fd9\u65f6\u53ea\u6709\u6597 \u5ff5\u5934\uff0c\u5fc5\u987b\u8d76\u5feb\u8131\u8eab\uff0c\u53bb\u52a9\u534e\u5929\u98ce\u4e00\u81c2\u4e4b\u529b\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u4e5f\u662f\u540c\u6837\u7684\u5fc3\u601d\uff0c\u60f3\u53bb\u63f4\u52a9\u5979\u7684\u4e08\u5927\uff0c\u4f46\u5979\u6df1\u77e5\u534e\u5927\u98ce\u7684\u5389\u5bb3\uff0c\u53ea\u6015\u81ea\u5df1\u63d2\u4e0d \u8fdb\u624b\uff0c\u8e0c\u8e87\u7247\u523b\uff0c\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u6709\u73b0\u6210\u7684\u5b9d\u5251\u5728\u6b64\uff0c\u6211\u95ee\u4e0d\u53d6\u7528\uff1f\u201d \u3000\u3000\u5c31\u5728\u6b64\u9645\uff0c\u5ffd\u89c1\u90a3\u5f20\u5927\u7f51\u4e00\u8361\uff0c\u6c5f\u6d77\u5929\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u6562\u6709\u52b3\u4e8c\u5a18\u6765\u53d6\uff0c\u6211\u81ea\u5df1\u53cc\u624b\u5949 \u4e0a\uff01\u201d\u6b27\u9633\u4e8c\u5a18\u5927\u5403\u4e00\u60ca\uff0c\u6b63\u8981\u4f38\u624b\u6293\u4efb\u90a3\u5f20\u7f51\uff0c\u53ea\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u6c5f\u6d77\u5929\u5df2\u7136\u7834\u7f51\u800c\u51fa\u3002 \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u7a74\u9053\u65e9\u89e3\u3002\u53ea\u662f\u8eab\u5728\u7f51\u4e2d\uff0c\u60ac\u5728\u534a\u7a7a\uff0c\u624b\u8db3\u96be\u5c55\uff0c\u4e0d\u4fbf\u7528\u529b\uff0c\u8fd9\u65f6\u5f97\u6b27\u9633\u4e8c \u5a18\u4e00\u8a00\u63d0\u9192\uff0c\u60f3\u8d77\u81ea\u5df1\u8eab\u6709\u5b9d\u5251\uff0c\u4f55\u4e0d\u5229\u7528\uff0c\u4eba\u6025\u8ba1\u751f\uff0c\u5c06\u90a3\u7f51\u8361\u5230\u6811\u8fb9\uff0c\u53cc\u811a\u4e00\u52fe\uff0c\u628a\u6811 \u5e72\u52fe\u4f4f\uff0c\u6709\u4e86\u51ed\u501f\uff0c\u6beb\u4e0d\u56f0\u96be\u7684\u5c31\u628a\u5b9d\u5251\u62d4\u4e86\u51fa\u6765\uff0c\u4ed6\u8fd9\u67c4\u88c1\u4e91\u5b9d\u5251\u53ef\u4ee5\u524a\u94c1\u5982\u6ce5\uff0c\u5439\u6bdb\u7acb \u65ad\uff0c\u5929\u8695\u4e1d\u7ec7\u6210\u7684\u7f51\u867d\u7136\u67d4\u97e7\u975e\u5e38\uff0c\u666e\u901a\u5200\u5251\u5272\u4e0d\u7834\uff0c\u4f46\u7528\u88c1\u4e91\u5b9d\u5251\uff0c\u5374\u662f\u5251\u5230\u529f\u6210\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5f04\u7834\u4e86\u7f51\uff0c\u7acb\u5373\u8fde\u4eba\u5e26\u5251\uff0c\u5316\u6210\u4e86\u4e00\u9053\u957f\u8679\uff0c\u5411\u6b27\u9633\u4e8c\u5a18\u51cc\u7a7a\u523a\u4e0b\uff0c\u6b27\u9633\u4e8c\u5a18\u6a2a \u62d0\u4e00\u5c01\uff0c\u4f46\u542c\u5f97\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u6756\u5934\u524a\u53bb\u4e86\u4e00\u622a\uff0c\u53ef\u662f\u6b27\u9633\u4e8c\u5a18\u8fd0\u7528\u501f\u529b\u5316\u52b2\u7684\u529f\u592b\uff0c\u8f7b\u8f7b \u4e00\u5e26\uff0c\u5374\u5c06\u6c5f\u6d77\u5929\u7684\u52b2\u9053\u89e3\u4e86\uff0c\u5de6\u81c2\u4e00\u52fe\uff0c\u4fbf\u6765\u62a2\u6c5f\u6d77\u5929\u7684\u5b9d\u5251\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u7684\u8eab\u624b\u5df2\u662f\u77eb\u6377\u975e\u5e38\uff0c\u53ef\u662f\u5979\u5206\u660e\u5df2\u6b3a\u5230\u4e86\u6c5f\u6d77\u5929\u7684\u773c\u524d\uff0c\u4e00\u6293\u4e4b\u4e0b\uff0c\u5374\u7adf\u7136 \u6293\u4e86\u4e2a\u7a7a\u3002\u5fc3\u4e2d\u4e00\u51db\uff0c\u65b9\u5f85\u79fb\u5f62\u6362\u4f4d\uff0c\u53d8\u62db\u653b\u51fb\uff0c\u53ea\u542c\u5f97\u6c5f\u6cfd\u5929\u5df2\u662f\u4e00\u58f0\u559d\u9053\uff1a\u201c\u7740\uff01\u201d\u5251 \u5149\u8fc7\u5904\uff0c\u5c06\u6b27\u9633\u4e8c\u5a18\u7684\u8896\u5b50\u524a\u53bb\u4e86\u534a\u6bb5\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u7528\u201c\u5929\u7f57\u6b65\u6cd5\u201d\uff0c\u4ece\u6b27\u9633\u4e8c\u5a18\u7edd\u5bf9\u610f\u60f3\u4e0d\u5230\u7684\u65b9\u4f4d\u51fa\u5251\uff0c\u8fd9\u4e00\u5251\u672c\u6765\u53ef\u4ee5\u5c06\u6b27\u9633 \u4e8c\u5a18\u7f6e\u4e8e\u6b7b\u5730\uff0c\u4f46\u4ed6\u7a76\u7adf\u5fc3\u5730\u5584\u826f\uff0c\u4e14\u53c8\u5ff5\u5728\u6b27\u9633\u4e8c\u5a18\u4e43\u662f\u6b27\u9633\u5a49\u7684\u6bcd\u4eb2\uff0c\u4e0d\u5fcd\u907d\u4e0b\u6740\u624b\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u82e5\u8bba\u672c\u8eab\u7684\u6b66\u529f\uff0c\u5979\u5b9e\u5728\u8fd8\u5728\u6c5f\u6d77\u5929\u4e4b\u4e0a\uff0c\u6c5f\u6d77\u5929\u6b63\u5728\u559d\u9053\uff0c \u201c\u5feb\u653e\u6211\u8fc7\u53bb\uff0c\u5426\u5219\u4f11\u602a\u6211\u2026\u2026\u201d\u201c\u5251\u4e0b\u65e0\u60c5\u201d\u56db\u5b57\u8fd8\u672a\u66fe\u9053\u5f97\u51fa\u6765\uff0c\u6b27\u9633\u4e8c\u5a18\u5df2\u662f\u500f\u5730\u8f6c \u8eab\uff0c\u62d0\u6756\u6389\u8fc7\u5934\u7c73\uff0c\u52fe\u4f4f\u4e86\u6c5f\u6d77\u5929\u7684\u5b9d\u5251\uff0c\u6c5f\u6d77\u5929\u7528\u529b\u524a\u53bb\uff0c\u54ea\u77e5\u7adf\u7136\u524a\u5b83\u4e0d\u52a8\uff0c\u539f\u6765\u4ed6\u90a3 \u5b9d\u5251\u5df2\u662f\u548c\u62d0\u6756\u7262\u7262\u7684\u9ecf\u5728\u4e00\u8d77\uff0c\u65e2\u96be\u4ee5\u5411\u524d\u524a\u53bb\uff0c\u540e\u64a4\u4e5f\u64a4\u4e0d\u56de\u6765\u3002 \u3000\u3000\u539f\u6765\u6b27\u9633\u4e8c\u5a18\u662f\u7528\u5979\u72ec\u95e8\u7684\u201c\u501f\u529b\u5316\u52b2\u201d\u529f\u592b\uff0c\u66f4\u7528\u4e0a\u4e86\u4e00\u4e2a\u201c\u9ecf\u201d\u5b57\u51b3\uff0c\u5b58\u5fc3\u8981\u548c\u6c5f \u6d77\u5929\u62fc\u8017\u5185\u529b\u3002\u5979\u5df2\u77e5\u9053\u6c5f\u6d77\u5929\u4e0d\u662f\u6613\u4e0e\u4e4b\u8f88\uff0c\u800c\u4e14\u53c8\u6709\u5929\u4e0b\u65e0\u53cc\u7684\u5b9d\u5251\uff0c\u66f4\u96be\u62b5\u654c\uff0c\u56e0\u6b64 \u53ea\u6709\u7528\u8fd9\u4e2a\u6cd5\u5b50\uff0c\u5f85\u8017\u5c3d\u6c5f\u6885\u5929\u7684\u5185\u529b\u4e4b\u540e\uff0c\u5b9d\u5251\u81ea\u7136\u53ef\u4ee5\u553e\u624b\u800c\u5f97\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u81ea\u4ee5\u4e3a\u6253\u5f97\u5982\u610f\u7b97\u76d8\uff0c\u5374\u4e0d\u77e5\u6b63\u662f\u653b\u654c\u4e4b\u6240\u957f\u3002\u6c5f\u6d77\u5927\u9650\u4e8e\u5e74\u7eaa\uff0c\u5176\u4ed6\u65b9\u9762\u7684 \u6b66\u529f\u706b\u5019\u6216\u8bb8\u4e0d\u8db3\uff0c\u4f46\u5728\u5185\u529f\u65b9\u9762\uff0c\u5374\u662f\u81ea\u5e7c\u4fbf\u6253\u597d\u4e86\u57fa\u7840\u7684\uff0c\u4ed6\u5728\u91d1\u4e16\u9057\u95e8\u4e0b\u516b\u5e74\uff0c\u5f97\u91d1 \u4e16\u9057\u4ee5\u79d8\u6cd5\u4f20\u6388\uff0c\u65e9\u5df2\u63a5\u8fd1\u4e86\u201c\u6b63\u90aa\u5408\u4e00\u201d\u7684\u5883\u754c\uff0c\u516b\u5e74\u7684\u529f\u592b\uff0c\u80dc\u8fc7\u522b\u4eba\u4e8c\u4e09\u5341\u5e74\u7684\u529f \u5927\u3002\u6b27\u9633\u4e8c\u5a18\u53ea\u89c9\u5bf9\u65b9\u7684\u5185\u529b\u6e90\u6e90\u800c\u6765\uff0c\u7adf\u4f3c\u65e0\u7a77\u65e0\u5c3d\uff0c\u4e0d\u7531\u5f97\u5927\u4e3a\u9707\u9a87\u3002\u53ea\u597d\u8fd0\u8db3\u4e86\u5341\u6210 \u529f\u529b\uff0c\u52a0\u7d27\u65bd\u4e3a\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u56fa\u7136\u9707\u9a87\uff0c\u4f46\u6c5f\u6d77\u5929\u66f4\u4e3a\u7126\u6025\uff01\u8fd9\u65f6\u534e\u5929\u98ce\u6b63\u662f\u4e0e\u90a3\u4e24\u4e2a\u9b54\u5934\u6597\u5230\u6700\u6fc0\u70c8\u7684\u65f6 \u5019\uff0c\u6c5f\u6d77\u5929\u8033\u8fb9\u53a2\u4f46\u542c\u5f97\u6b27\u9633\u4ef2\u548c\u58f0\u58f0\u5927\u559d\uff0c\u84b2\u5362\u864e\u602a\u5578\u60ca\u5fc3\uff0c\u76ee\u5149\u4e00\u77a5\uff0c\u53ea\u89c1\u4e09\u4e2a\u4eba\u90fd\u5df2 \u8840\u67d3\u8863\u88f3\uff0c\u534e\u5929\u98ce\u8eab\u6cd5\u8fdf\u6ede\uff0c\u5251\u62db\u4f7f\u51fa\uff0c\u5df2\u8fdc\u4e0d\u4f3c\u521a\u624d\u7684\u6ede\u6d12\u81ea\u5982\uff01\u6c5f\u6d77\u5929\u60f3\u6446\u8131\u6b27\u9633\u4e8c \u5a18\uff0c\u4f46\u5728\u8feb\u5207\u4e4b\u95f4\u5374\u53c8\u6446\u8131\u4e0d\u5f97\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u597d\u751f\u540e\u6094\uff0c\u540e\u6094\u521a\u624d\u90a3\u4e00\u5251\u624b\u4e0b\u7559\u60c5\uff0c\u672a\u66fe\u4f24\u4e86\u6b27\u9633\u4e8c\u5a18\u3002\u5ffd\u5730\u4ed6\u60f3\u8d77\u4e86\u5e08\u7236\u7ed9 \u4ed6\u7684\u6559\u8bad\uff0c\u53eb\u4ed6\u5728\u6c5f\u6e56\u884c\u8d70\u5fc5\u987b\u8bb0\u4f4f\u7684\u4e00\u53e5\u6d3b\uff1a\u201c\u4ee5\u5176\u4eba\u4e4b\u9053\uff0c\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\uff01\u201d\u5018\u82e5\u654c\u4eba \u662f\u4e2a\u5978\u72e1\u4e4b\u5f92\uff0c\u4f60\u5c31\u5207\u4e0d\u53ef\u81ea\u4f5c\u541b\u5b50\u3002\u4ed6\u51f6\uff0c\u4f60\u5c31\u8981\u66f4\u51f6\uff0c\u4ed6\u72e0\uff0c\u4f60\u5c31\u8981\u66f4\u72e0\uff01 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u52a0\u7d27\u65bd\u4e3a\uff0c\u5ffd\u89c9\u6c5f\u6d77\u5929\u7684\u5185\u529b\u6e10\u6e10\u51cf\u5f31\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u60f3\u9053\uff1a\u201c\u7a76\u7adf\u4ed6\u8fd8\u672a\u6709\u51e0 \u5e74\u529f\u529b\uff0c\u7ec8\u662f\u4e0d\u80fd\u6301\u4e45\uff01\u201d\u518d\u8fc7\u7247\u523b\uff0c\u6c5f\u6d77\u5927\u6c14\u5598\u5401\u5401\uff0c\u5df2\u663e\u51fa\u4e0d\u6587\u4e4b\u8c61\u3002\u6b27\u9633\u4e8c\u5a18\u867d\u7136\u89c9 \u5f97\u4ed6\u8d25\u5f97\u592a\u5feb\uff0c\u6709\u70b9\u5947\u602a\u3002\u4f46\u6bd4\u62fc\u5185\u529f\u4e0d\u540c\u522b\u6837\uff0c\u6bd4\u522b\u79cd\u529f\u592b\uff0c\u53ef\u4ee5\u8bc8\u8d25\uff0c\u6bd4\u5185\u529f\u5374\u662f\u4e0d\u80fd \u8bc8\u8d25\u7684\uff0c\u56e0\u4e3a\u53cc\u65b9\u4ee5\u5185\u529b\u76f8\u62fc\uff0c\u4f60\u4e00\u8bc8\u8d25\uff0c\u5c06\u5185\u529b\u51cf\u5f31\uff0c\u90a3\u5c31\u662f\u81ea\u53d6\u6740\u8eab\u4e4b\u7978\u4e86\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u7b11\u9053\uff1a\u201c\u6c5f\u76f8\u516c\uff0c\u4f60\u6b47\u6b47\u5427\uff0c\u5b9d\u5251\u62ff\u6765\uff01\u201d\u62d0\u6756\u4e00\u633a\uff0c\u8fd0\u8db3\u4e86\u5185\u529f\u538b\u53bb\uff0c\u53ea\u542c \u5f97\u201c\u5495\u549a\uff01\u201d\u4e00\u58f0\uff0c\u6c5f\u6d77\u5929\u5df2\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u8fde\u5fd9\u5954\u4e0a\u53bb\u6293\u4ed6\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e3a\u4e86\u5a49\u513f\u7684\u539f\u6545\uff0c\u6211\u65e6\u7559\u4ed6\u4e00\u6761\u6027\u547d\u3002\u201d\u6c5f\u6d77 \u5929\u5728\u5730\u4e0a\u6253\u4e86\u4e2a\u6eda\uff0c\u5230\u4e86\u6811\u4e0b\uff0c\u7a81\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u5c06\u90a3\u5f20\u7f51\u62ff\u4e86\u4e0b\u6765\uff0c\u4e00\u58f0\u7b11\u9053\uff1a\u201c\u591a\u8c22\u4f60\u4e00 \u7247\u597d\u5fc3\uff0c\u8bf7\u4f60\u4e5f\u5165\u7f51\u5427\uff01\u201d\u51fa\u624b\u5982\u7535\uff0c\u500f\u5730\u5c06\u90a3\u5f20\u7f51\u5411\u6b27\u9633\u4e8c\u5a18\u5f53\u5934\u7f69\u4e0b\uff0c\u4e24\u4eba\u8fd9\u65f6\u8ddd\u79bb\u6781 \u8fd1\uff0c\u6b27\u9633\u4e8c\u5a18\u731d\u4e0d\u53ca\u9632\uff0c\u7adf\u7ed9\u6c5f\u6d77\u5929\u64d2\u5165\u7f51\u4e2d\u3002 \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u7684\u5185\u529f\u81ea\u6210\u4e00\u5bb6\uff0c\u602a\u5f02\u4e4b\u6781\uff0c\u4ed6\u53ef\u4ee5\u5c06\u5185\u529f\u51dd\u805a\u62a4\u8eab\uff0c\u4e0d\u6015\u5bf9\u65b9\u4e58\u865a\u8fdb\u51fb\uff0c \u56e0\u6b64\uff0c\u522b\u4eba\u6bd4\u62fc\u5185\u529f\u4e0d\u80fd\u8bc8\u8d25\uff0c\u4ed6\u5374\u53ef\u4ee5\u8bc8\u8d25\u3002\u4ed6\u5c31\u662f\u5188\u4e3a\u6b27\u9633\u4e8c\u5a18\u5904\u5904\u4f7f\u8bc8\uff0c\u6240\u4ee5\u4ed6\u8fd9\u6b21 \u4e5f\u4ee5\u8bc8\u672f\u80dc\u5979\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u63d0\u8d77\u4e86\u5b9d\u5251\uff0c\u5ffd\u5730\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u521a\u624d\u6211\u8bc8\u8d25\u5012\u5730\u4e4b\u65f6\uff0c\u5979\u6ca1\u7528\u62d0\u6756\u51fb\u6211\uff0c\u53ef\u89c1\u5979 \u4e5f\u4e0d\u662f\u5b58\u5fc3\u5bb3\u6211\u3002\u201d\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u5b9d\u5251\u500f\u5730\u6536\u56de\uff0c\u5012\u6301\u5251\u628a\uff0c\u5c06\u5251\u67c4\u4e00\u649e\uff0c\u649e\u4e2d\u4e86\u6b27\u9633\u4e8c\u5a18\u7684 \u9ebb\u7a74\u3002\u5927\u7b11\u8bf4\u9053\uff0c\u201c\u4f60\u4e5f\u6b47\u4e00\u4f1a\u513f\u5427\uff0c\u8fd9\u7a74\u9053\u5728\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u540e\u4fbf\u4f1a\u81ea\u89e3\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e00\u4e2a\u8f6c\u8eab\uff0c\u98de\u5954\u8fc7\u53bb\uff0c\u534e\u5929\u98ce\u53eb\u9053\uff0c\u201c\u6c5f\u8d24\u4f84\uff0c\u5c0f\u5fc3\uff01\u201d\u53ea\u89c1\u84b2\u5362\u864e\u5df2\u8fce\u9762\u524d \u6765\uff0c\u5927\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u6709\u591a\u5c11\u9053\u884c\uff0c\u4e5f\u6562\u4e0a\u6765\u9001\u6b7b\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e0d\u7406\u4e0d\u776c\uff0c\u4e8c\u8bdd\u4e0d\u8bf4\uff0c\u5530\u7684\u4e00\u5251\u4fbf\u523a\u5c06\u8fc7\u53bb\uff0c\u8fd9\u4e00\u5251\u6c14\u52bf\u5982\u8679\uff0c\u540d\u4e3a\u201c\u53cc\u9f99\u51fa \u6d77\u201d\uff0c\u5148\u523a\u5de6\u65b9\uff0c\u518d\u523a\u53f3\u65b9\uff0c\u5251\u52bf\u672c\u6765\u51cc\u5389\u4e4b\u6781\uff0c\u53ef\u662f\u84b2\u5362\u864e\u662f\u7f3a\u4e86\u4e00\u6761\u53f3\u81c2\u7684\uff0c\u5148\u523a\u5de6\u65b9 \u7b49\u4e8e\u201c\u65e0\u7684\u653e\u77e2\u201d\uff0c\u84b2\u5362\u864e\u672c\u5c31\u8f7b\u654c\uff0c\u53c8\u89c1\u6c5f\u6d77\u5929\u51fa\u5251\u9519\u8bef\uff0c\u66f4\u662f\u4e0d\u628a\u6c5f\u6d77\u5929\u653e\u5728\u8ddf\u5185\uff0c\u4ed6 \u6beb\u65e0\u987e\u5fcc\u7684\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u6bd2\u624b\u4fbf\u5411\u6c5f\u6d77\u5929\u7684\u5934\u9876\u62cd\u4e0b\u6765\u3002 \u3000\u3000\u84b2\u5362\u864e\u6ee1\u5fc3\u4ee5\u4e3a\u8fd9\u4e00\u638c\u62cd\u4e0b\uff0c\u6c5f\u6d77\u5929\u51b3\u96be\u6d3b\u547d\uff0c\u54ea\u77e5\u5c31\u5728\u8fd9\u4fc4\u9877\u4e4b\u95f4\uff0c\u6c5f\u6d77\u5929\u7684\u5b9d\u5251\u5df2 \u7a81\u7136\u53d8\u4e86\u65b9\u5411\uff0c\u4f3c\u5de6\u5b9e\u53f3\uff0c\u5251\u5149\u95ea\u5904\uff0c\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u84b2\u5362\u864e\u4ec5\u5b58\u7684\u53f3\u624b\uff0c\u4e2d\u98df\u4e8c\u6307\uff0c\u53c8\u5df2 \u7ed9\u6c5f\u6d77\u5929\u524a\u65ad\u4e86\uff0c\u7740\u4e0d\u662f\u4ed6\u9003\u5f97\u5feb\uff0c\u6574\u4e2a\u624b\u638c\u90fd\u51e0\u4e4e\u7ed9\u5b9d\u5251\u5272\u53bb\uff01 \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u5fc3\u5730\u7eaf\u539a\uff0c\u4f46\u4eba\u5374\u6781\u4e3a\u806a\u660e\uff0c\u5bf9\u6b66\u5b66\u7684\u609f\u6027\u4e5f\u9ad8\uff0c\u4ed6\u4eba\u5728\u7f51\u4e2d\u7684\u65f6\u5019\uff0c\u65e9\u5df2 \u60f3\u597d\u4e86\u5bf9\u4ed8\u8fd9\u4e24\u4e2a\u9b54\u5934\u7684\u65b9\u6cd5\uff0c\u4ed6\u6b63\u662f\u8981\u5229\u7528\u84b2\u5362\u864e\u8f7b\u654c\u7684\u5fc3\u7406\uff0c\u7ed9\u4ed6\u4e00\u4e2a\u9519\u89c9\uff0c\u523a\u4ed6\u7684\u5de6 \u65b9\uff0c\u8ba9\u4ed6\u4ee5\u4e3a\u5bf9\u65b9\u771f\u662f\u6beb\u65e0\u7ecf\u9a8c\uff0c\u51fa\u5251\u9519\u8bef\uff0c\u8fd9\u624d\u80fd\u51fa\u5176\u4e0d\u610f\u7684\u514b\u654c\u5236\u80dc\u3002 \u3000\u3000\u84b2\u5362\u864e\u5389\u58f0\u6012\u568e\uff0c\u867d\u7136\u6124\u6068\u4e4b\u6781\uff0c\u5374\u5df2\u4e0d\u6562\u63a5\u6218\uff0c\u8f6c\u8eab\u98de\u9003\uff0c\u6b27\u9633\u4ef2\u548c\u5927\u6012\uff0c\u8eab\u5f62\u4e00 \u6643\uff0c\u500f\u5730\u4fbf\u7ed5\u5230\u4e86\u6c5f\u6d77\u5929\u80cc\u540e\u3002\u5927\u559d\u4e00\u58f0\uff0c\u638c\u82e5\u5954\u96f7\uff0c\u5411\u6c5f\u6d77\u5929\u7684\u80cc\u5fc3\u51fb\u53bb\uff0c\u534e\u5929\u98ce\u5927\u60ca\uff0c \u4f46\u4ed6\u8eabk\u4e2d\u6bd2\uff0c\u6b64\u65f6\u6bd2\u6027\u5df2\u5927\u5927\u53d1\u4f5c\uff0c\u53cc\u811a\u9ebb\u6728\u4e0d\u7075\uff0c\u60f3\u53bb\u63f4\u6551\uff0c\u5df2\u6765\u4e0d\u53ca\u3002 \u3000\u3000\u4f46\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u6b27\u9633\u4ef2\u548c\u8fd9\u4e00\u638c\u5df2\u51fb\u4e2d\u4e86\u6c5f\u6d77\u5929\u7684\u80cc\u5fc3\uff0c\u4ed6\u8fd9\u201c\u9739\u96f3\u638c\u201d\u4e0e\u5c11\u6797 \u6d3e\u7684\u201c\u91d1\u521a\u638c\u201d\u5f02\u66f2\u540c\u5de5\uff0c\u4e43\u662f\u81f3\u731b\u81f3\u521a\u7684\u638c\u529b\uff0c\u4ed6\u5728\u8fd9\u4e00\u638c\u53d1\u51fa\u4e4b\u9645\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6bd9\u4e86 \u8fd9\u5c0f\u5b50\uff0c\u4e5f\u597d\u8ba9\u5973\u513f\u65ad\u4e86\u5ff5\u5934\u3002\u201d \u3000\u3000\u54ea\u77e5\u5fc3\u5ff5\u672a\u5df1\uff0c\u5c31\u5728\u4ed6\u7684\u624b\u638c\u51fb\u4e2d\u6c5f\u6d77\u5929\u80cc\u5fc3\u7684\u65f6\u5019\uff0c\u5ffd\u89c9\u5bf9\u65b9\u7684\u8eab\u4f53\u6709\u80a1\u5f39\u529b\uff0c\u8bf4\u65f6 \u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6c5f\u6d77\u5929\u975e\u4f46\u5e76\u4e0d\u5012\u4e0b\uff0c\u4e14\u5df2\u500f\u7136\u7684\u8f6c\u8fc7\u8eab\u6765\uff01 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\u3001\u4ee5\u4e3a\u6c5f\u6d77\u5929\u5df2\u7ec3\u6210\u4e86\u81f3\u9ad8\u65e0\u4e0a\u7684\u201c\u91d1\u521a\u4e0d\u574f\u795e\u529f\u201d\uff0c\u987f\u65f6\u95f4 \u4e0d\u89c9\u5446\u4e86\u3002\u54ea\u77e5\u6c5f\u6d77\u5929\u7684\u5185\u529f\u867d\u597d\uff0c\u5374\u8fd8\u672a\u66fe\u5230\u90a3\u5883\u754c\uff0c\u539f\u6765\u4ed6\u8d34\u8eab\u7a7f\u7740\u4e54\u5317\u6e9f\u4e09\u5b9d\u4e4b\u4e00\u7684 \u767d\u7389\u7532\uff0c\u8fd9\u5b9d\u7532\u672c\u6765\u662f\u8981\u9001\u7ed9\u8c37\u4e2d\u83b2\u7684\uff0c\u4ed6\u4eba\u592a\u8001\u5b9e\uff0c\u4e00\u8def\u4e0a\u4e0d\u6562\u7a7f\uff0c\u6545\u6b64\u524d\u6b21\u53d7\u4e86\u9634\u5723\u59d1 \u201c\u795e\u86c7\u638c\u201d\u7684\u4f24\u5bb3\uff0c\u4f46\u6b63\u56e0\u4e3a\u53d7\u4e86\u90a3\u6b21\u6559\u8bad\uff0c\u6240\u4ee5\u4eca\u665a\u4ed6\u4fbf\u7a7f\u5728\u8eab\u4e0a\uff0c\u51c6\u5907\u5e2e\u534e\u5929\u98ce\u6765\u5bf9\u4ed8 \u84b2\u5362\u864e\uff0c\u5374\u60f3\u4e0d\u5230\u84b2\u5362\u864e\u4e00\u62db\u4fbf\u8d25\uff0c\u5728\u5bf9\u4ed8\u6b27\u9633\u4ef2\u548c\u65f6\u624d\u521a\u597d\u7528\u4e0a\u3002 \u3000\u3000\u4f46\u6b27\u9633\u4ef2\u548c\u6bd5\u7adf\u662f\u4e2a\u6b66\u5b66\u540d\u5bb6\uff0c\u6b66\u529f\u80c6\u8bc6\u90fd\u5728\u84b2\u5362\u864e\u4e4b\u4e0a\uff0c\u867d\u7136\u9aa4\u5403\u4e00\u60ca\uff0c\u5374\u8fd8\u80fd\u591f\u968f \u673a\u5e94\u53d8\uff0c\u5c31\u5728\u6c5f\u6d77\u5929\u8f6c\u8fc7\u8eab\u6765\uff0c\u5c1a\u672a\u51b3\u5b9a\u662f\u7528\u638c\u8fd8\u662f\u7528\u5251\u4e4b\u9645\uff0c\u6b27\u9633\u4ef2\u548c\u5df2\u662f\u4e00\u6307\u6233\u6765\uff0c\u6b63 \u6b63\u70b9\u4e2d\u6c5f\u6bcf\u5929\u7684\u8155\u8109\uff0c\u6c5f\u6d77\u5929\u53ea\u89c9\u5982\u540c\u706b\u70d9\u4e00\u822c\uff0c\u201c\u5f53\u5577\u201d\u4e00\u58f0\uff0c\u88c1\u4e91\u5b9d\u5251\u5df2\u8131\u624b\u843d\u5730\u3002\u4e0e \u6b64\u540c\u65f6\uff0c\u6b27\u9633\u4ef2\u548c\u4ea6\u5df2\u8f6c\u8eab\u98de\u8dd1\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u53ef\u7b97\u5f97\u5e78\u8fd0\u4e4b\u6781\uff0c\u5018\u82e5\u6b27\u9633\u4ef2\u548c\u5728\u70b9\u4e2d\u4ed6\u8155\u8109\u4e4b\u540e\uff0c\u518d\u53d1\u4e00\u6307\uff0c\u70b9\u4ed6\u7684\u592a\u9633\u7a74\u6216 \u7709\u5fc3\u7a74\uff0c\u90a3\u90fd\u662f\u5b9d\u7532\u4fdd\u62a4\u4e0d\u5230\u7684\u5730\u65b9\uff0c\u6c5f\u6d77\u5929\u7eb5\u6709\u62a4\u4f53\u795e\u529f\uff0c\u4ea6\u51b3\u96be\u62b5\u5fa1\uff0c\u4e0d\u6b7b\u4ea6\u5fc5\u91cd\u4f24\uff0c \u53ea\u56e0\u4ed6\u4e0d\u77e5\u9053\u6c5f\u6d77\u5929\u7684\u771f\u5b9e\u672c\u9886\uff0c\u521a\u624d\u5288\u4e86\u4ed6\u4e00\u638c\uff0c\u89c1\u4ed6\u6beb\u65e0\u4f24\u635f\uff0c\u53ea\u9053\u4ed6\u5df2\u7ec3\u6210\u201d\u91d1\u521a\u4e0d \u574f\u795e\u529f\u201d\uff0c\u81ea\u5df1\u65ad\u96be\u654c\u62b5\uff0c\u6240\u4ee5\u5728\u6233\u4e86\u4e00\u6307\u4e4b\u540e\uff0c\u4fbf\u7acb\u5373\u9003\u8d70\uff0c\u800c\u4ed6\u90a3\u4e00\u6307\uff0c\u539f\u610f\u4e5f\u4e0d\u8fc7\u662f \u56e0\u4e3a\u4e0d\u7518\u675f\u624b\u88ab\u64d2\uff0c\u6545\u6b64\u52c9\u529b\u800c\u4e3a\uff0c\u4e34\u5371\u81ea\u6551\u800c\u5df2\uff0c\u6839\u672c\u5c31\u60f3\u4e0d\u5230\u4f1a\u4f24\u5f97\u4e86\u6c5f\u6d77\u5929\u7684\u3002 \u3000\u3000\u5f85\u4ed6\u542c\u5230\u6c5f\u6d77\u5929\u5b9d\u5251\u5760\u5730\u7684\u58f0\u97f3\uff0c\u65b9\u59cb\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u5947\u602a\uff0c\u96be\u9053\u662f\u6211\u8d70\u4e86\u773c\u4e86\uff1f\u201d\u8fd9\u65f6 \u534e\u5929\u98ce\u6b63\u5728\u811a\u6b65\u8dc4\u8e09\u7684\u5411\u4ed6\u51b2\u6765\u3002\u6b27\u9633\u4ef2\u548c\u6076\u5ff5\u9661\u751f\u60f3\u9053\uff1a\u201c\u4e0d\u5982\u518d\u8bd5\u4e00\u8bd5\uff0c\u82e5\u7136\u9093\u5c0f\u5b50\u679c \u771f\u662f\u672c\u9886\u4e0d\u6d4e\uff0c\u5c06\u4ed6\u64d2\u4e0b\uff0c\u4eca\u665a\u5c31\u53ef\u4ee5\u53cd\u8d25\u4e3a\u80dc\u4e86\u3002\u201d\u8981\u77e5\u534e\u5929\u98ce\u5df2\u662f\u5f3a\u5f29\u4e4b\u672a\uff0c\u5018\u82e5\u4ed6\u80fd \u64d2\u83b7\u6c5f\u6d77\u5929\uff0c\u5c31\u53ef\u7528\u6c5f\u6d77\u5929\u6765\u5f53\u4f5c\u5175\u5668\u53bb\u5bf9\u4ed8\u534e\u65e0\u98ce\uff0c\u6599\u60f3\u534e\u5929\u98ce\u6b66\u529f\u518d\u5f3a\uff0c\u4e5f\u51b3\u4e0d\u6562\u65bd\u5c55 \u6740\u624b\uff0c\u53ea\u8981\u591a\u8017\u4e00\u4f1a\uff0c\u534e\u65e0\u98ce\u5fc5\u5c06\u652f\u6301\u4e0d\u4f4f\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5230\u5e95\u8fd8\u6709\u4e9b\u602f\u610f\uff0c\u6b63\u5728\u8e0c\u8e87\u672a\u51b3\u4e4b\u9645\uff0c\u6c5f\u6d77\u5929\u5df2\u5148\u8feb\u4e86\u4e0a\u6765\uff0c\u534e\u5929\u98ce\u4e5f\u7528\u5c3d\u4e86\u6b8b \u4f59\u7684\u6c14\u529b\uff0c\u5982\u98de\u5954\u81f3\uff0c\u6b27\u9633\u4ef2\u548c\u638c\u6307\u517c\u65bd\uff0c\u638c\u5288\u534e\u5929\u98ce\uff0c\u6307\u6233\u6c5f\u6d77\u5929\uff0c\u8fd9\u65f6\uff0c\u5018\u82e5\u4ed6\u662f\u5355\u6253 \u72ec\u6597\uff0c\u8981\u8d62\u534e\u5929\u98ce\u548c\u6c5f\u6d77\u5929\u90fd\u4e0d\u96be\uff0c\u5403\u4e8f\u5728\u4e00\u5fc3\u4e8c\u7528\uff0c\u5206\u654c\u4e24\u4eba\uff0c\u6c5f\u6d77\u5929\u867d\u7136\u8155\u8109\u53d7\u4f24\u3002\u8eab \u6cd5\u4f9d\u7136\u7075\u6d3b\uff0c\u4ed6\u89c1\u6b27\u9633\u4ef2\u548c\u5982\u6b64\u51f6\u72e0\uff0c\u7259\u6839\u4e00\u54ac\uff0c\u5fc3\u9053\uff1a\u201c\u8bf4\u4e0d\u5f97\u53ea\u597d\u5e9f\u6389\u4ed6\u7684\u6b66\u529f\u4e86\uff01\u201d \u4f7f\u51fa\u5929\u7f57\u6b65\u6cd5\uff0c\u500f\u7136\u95f4\u7ed5\u5230\u6b27\u9633\u4ef2\u548c\u80cc\u540e\uff0c\u4e00\u58f0\u559d\u9053\uff1a\u201c\u672d\u5c1a\u5f80\u6765\uff0c\u8fd8\u4f60\u4e00\u6307\uff01\u201d\u6b27\u9633\u4ef2\u548c \u7a81\u7136\u4e0a\u534a\u8eab\u5168\u90e8\u9ebb\u8f6f\uff0c\u88ab\u534e\u5929\u98ce\u4e00\u628a\u62ff\u7740\u3002 \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u5728\u7f51\u4e2d\u7684\u65f6\u5019\uff0c\u5df2\u770b\u51fa\u4e86\u6b27\u9633\u4ef2\u548c\u6240\u7ec3\u7684\u90aa\u6d3e\u5185\u529f\u6709\u4e8c\u4e2a\u7f3a\u70b9\uff0c\u4ed6\u6240\u7ec3\u7684 \u201c\u5c11\u9633\u7f61\u6c14\u201d\u867d\u7136\u5df2\u6709\u4e86\u516d\u4e03\u6210\u4eba\u5019\uff0c\u4f46\u810a\u690e\u9aa8\u672a\u7aef\u7684\u201c\u5c3e\u95f4\u7a74\u201d\u5374\u662f\u4ed6\u201c\u547d\u95e8\u201d\u6240\u5728\uff0c\u5c1a \u672a\u7ec3\u5230\u7684\u5730\u65b9\uff0c\u6c5f\u6d77\u5929\u66fe\u8ddf\u91d1\u4e16\u9057\u5b66\u8fc7\uff0c\u61c2\u5f97\u8981\u7834\u8fd9\u6d3e\u90aa\u6d3e\u5185\u529f\uff0c\u53ea\u987b\u5728\u4ed6\u7684\u201c\u5c3e\u95f4\u7a74\u201d\u7528 \u4e00\u6307\u7985\u529f\u6765\u6233\u7834\u4ed6\u7684\u201c\u5c11\u9633\u7f61\u6c14\u201d\u3002\u4e0d\u8fc7\u5018\u975e\u534e\u5929\u98ce\u6070\u5de7\u5728\u8fd9\u65f6\u4faf\u5230\u6765\u76f8\u52a9\u3002\u6c5f\u6d77\u5929\u51b3\u4e0d\u80fd \u5982\u6b64\u8f7b\u6613\u5f97 \u3000\u3000\u6c5f\u6d77\u5929\u4f4e\u5934\u4e00\u770b\u3002\u53ea\u89c1\u4e2d\u6307\u7ea2\u80bf\u5f97\u4f3c\u4e00\u652f\u8721\u70db\uff0c\u624b\u8155\u521a\u624d\u88ab\u70b9\u4e2d\u4e4b\u5904\uff0c\u66f4\u5982\u540c\u53d7\u8fc7\u70d9\u5370 \u4e00\u822c\u3002\u4e0d\u7981\u6697\u6697\u5fc3\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u597d\u5389\u5bb3\uff01\u8981\u4e0d\u662f\u8fd9\u9b54\u5934\u5148\u7ecf\u8fc7\u4e00\u573a\u6076\u6597\uff0c\u53ea\u6015\u6211\u8fd8\u4e0d\u80fd \u5982\u6b64\u4fa5\u5e78\uff0c\u4ec5\u4ec5\u53d7\u4e86\u8fd9\u70b9\u4f24\u5462\uff01\u5e78\u4e8f\u534e\u8001\u524d\u8f88\u73b0\u5728\u5df1\u628a\u4ed6\u64d2\u4f4f\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u62fe\u8d77\u5b9d\u5251\uff0c\u89c1\u534e\u5929\u98ce\u811a\u6b65\u8dc4\u8e09\uff0c\u751f\u6015\u4ed6\u6293\u4e0d\u7262\u6b27\u9633\u4ef2\u548c\uff0c\u6b63\u5f85\u8fc7\u53bb\u76f8\u52a9\uff0c\u5ffd\u89c1\u534e \u5929\u98ce\u6643\u4e86\u4e00\u6643\uff0c\u98a4\u58f0\u53eb\u9053\uff0c\u201c\u78a7\u513f\uff0c\u662f\u4f60\u4e48\uff1f\u201d\u8bdd\u72b9\u6765\u4e86\uff0c\u53ea\u542c\u5f97\u4e00\u58f0\u5145\u6ee1\u60ca\u60f6\u7684\u53eb\u558a\uff0c\u5212 \u7834\u957f\u7a7a\uff0c\u9690\u7ea6\u542c\u5f97\u51fa\u662f\u201c\u7239\u7239\u201d\u4e8c\u5b57\uff0c\u4fbf\u7a81\u7136\u4e2d\u65ad\u4e86\uff01 \u3000\u3000\u534e\u5929\u98ce\u6293\u7740\u6b27\u9633\u4ef2\u548c\u80cc\u5fc3\u7684\u201c\u5927\u690e\u7a74\u201d\uff0c\u51e0\u4e4e\u4f3c\u662f\u75af\u72c2\u822c\u5730\u51b2\u4e0a\u53bb\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97 \u6b27\u9633\u4e8c\u5a18\u54c8\u54c8\u5927\u7b11\uff0c\u84e6\u5730\u91cc\u5389\u58f0\u559d\u9053\uff0c\u201c\u534e\u5929\u98ce\uff0c\u4f60\u6562\u518d\u4e0a\u524d\u4e00\u6b65\uff0c\u4f60\u8fd8\u8981\u4e0d\u8981\u4f60\u7684\u5973\u513f\uff1f\u201d \u3000\u3000\u6708\u5149\u4e0b\uff0c\u53ea\u89c1\u6b27\u9633\u4e8c\u5a18\u7ad9\u5728\u6811\u4e0b\uff0c\u62c9\u7740\u90a3\u5f20\u5929\u8695\u4e1d\u7f51\uff0c\u534e\u4e91\u78a7\u5df2\u66ff\u4ee3\u4e86\u5979\u521a\u624d\u7684\u4f4d\u7f6e\uff0c \u8fdb\u4e86\u7f51\u4e2d\uff01\u539f\u6765\u6c5f\u6d77\u5929\u5306\u5fd9\u4e2d\u601d\u8651\u4e0d\u5468\uff0c\u53ea\u662f\u7528\u5251\u67c4\u649e\u4e2d\u5979\u7684\u201c\u81bb\u4e2d\u7a74\u201d\uff0c\u672a\u66fe\u7528\u6700\u5389\u5bb3\u7684 \u72ec\u95e8\u70b9\u7a74\u624b\u6cd5\uff0c\u4e5f\u8bb8\u662f\u4ed6\u6839\u672c\u5c31\u6ca1\u6709\u60f3\u8fc7\u8981\u7528\u72ec\u95e8\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u6765\u4f24\u5bb3\u6b27\u9633\u5a49\u7684\u6bcd\u4eb2\u3002\u600e\u77e5 \u6b27\u9633\u4e8c\u5a18\u7684\u201c\u5c11\u9633\u7f61\u6c14\u201d\u4e5f\u5df2\u6709\u4e24\u4e09\u6210\u706b\u5019\uff0c\u867d\u7136\u8fdc\u8fdc\u4e0d\u53ca\u5979\u7684\u5927\u592b\uff0c\u4f46\u7528\u6765\u51b2\u5173\u89e3\u7a74\uff0c\u5374 \u662f\u6e38\u5203\u6709\u4f59\uff0c\u56e0\u6b64\u6c5f\u6d77\u5929\u4e00\u8d70\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u5979\u7684\u7a74\u9053\u4fbf\u5df2\u89e3\u4e86\u3002\u6b63\u5de7\u534e\u4e91\u78a7\u75be\u5954\u800c\u6765\uff0c\u6025\u4e8e \u89c5\u7236\uff0c\u6070\u6070\u7ecf\u8fc7\u5979\u7684\u8eab\u8fb9\uff0c\u5979\u4e00\u8eab\u9ed1\u8863\uff1b\u53c8\u7528\u7eaf\u9ed1\u8272\u7684\u5929\u8695\u4e1d\u7f51\u906e\u5f80\u8eab\u4f53\uff0c\u534e\u4e91\u78a7\u4e00\u4e8c\u65f6\u95f4 \u672a\u80fd\u53d1\u73b0\uff0c\u9042\u51b7\u4e0d\u9632\u7684\u7ed9\u5979\u7f51\u4f4f\uff1b\u6b27\u9633\u4e8c\u5a18\u7684\u6b66\u529f\u9ad8\u51fa\u534e\u4e91\u78a7\u4e0d\u6b62\u4e00\u7b79\uff0c\u53c8\u662f\u5148\u53d1\u5236\u4eba\uff0c\u5f53 \u7136\u5f88\u5bb9\u6613\u7684\u4fbf\u628a\u534e\u4e91\u78a7\u5236\u4f0f\u4e86\u3002 \u3000\u3000\u534e\u5929\u98ce\u5636\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4e5f\u8fd8\u8981\u4e0d\u8981\u4f60\u7684\u4e08\u592b\uff1f\u201d\u6b27\u9633\u4e8c\u5a18\u7b11\u9053\uff1a\u201c\u4e08\u592b\u4e48\uff0c\u6b7b\u4e86\u8fd8\u53ef\u4ee5 \u518d\u6709\uff1a\u5973\u513f\u4e48\u3002\u6b7b\u4e86\u5c31\u4e0d\u80fd\u518d\u6709\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5927\u4e3a\u5947\u602a\uff0c\u5fc3\u60f3\u4ee5\u521a\u624d\u6240\u89c1\u7684\u60c5\u5f62\uff0c\u6b27\u9633\u4e8c\u5a18\u5206\u660e\u5bf9\u4e08\u5927\u751a\u4e3a\u656c\u754f\uff0c\u4f55\u4ee5\u7adf\u4f1a\u8bf4 \u51fa\u8fd9\u6837\u7684\u8bdd\uff1f\u4ed6\u5bdf\u770b\u4e86\u4e00\u4e0b\u6b27\u9633\u4ef2\u548c\u7684\u9762\u8272\uff0c\u53ea\u89c1\u6b27\u9633\u4ef2\u548c\u7684\u9762\u8272\u975e\u4f46\u4e0d\u6012\uff0c\u53cd\u800c\u9732\u51fa\u4e00\u4e2a \u6ee1\u610f\u7684\u7b11\u5bb9\uff0c\u597d\u50cf\u59bb\u5b50\u8fd9\u8bdd\uff0c\u6b63\u5408\u4ed6\u7684\u5fc3\u610f\u3002 \u3000\u3000\u534e\u5929\u98ce\u53c8\u6025\u53c8\u6012\uff0c\u4e00\u65f6\u95f4\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u90a3\u6b27\u9633\u4e8c\u5a18\u5374\u53c8\u6162\u6761\u65af\u7406\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u7740 \u6025\uff0c\u4f60\u653e\u4ed6\u56de\u6765\u4e5f\u597d\uff0c\u4e0d\u653e\u4ed6\u56de\u6765\u4e5f\u597d\uff0c\u6211\u90fd\u662f\u65e0\u53ef\u65e0\u4e0d\u53ef\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6b27\u9633\u5a76\u5a76\uff0c\u4f60\u6b64\u8a00\u5dee\u77e3\uff01\u4f60\u6362\u4e00\u4e2a\u4e08\u592b\u65e0\u6240\u8c13\uff0c\u4f60\u7684\u513f\u5973\u6362\u4e2a\u7236\u4eb2\u53ef\u5c31\u96be \u53d7\u4e86\u3002\u4f9d\u6211\u770b\u4f60\u8fd8\u662f\u8981\u539f\u6765\u8fd9\u4e2a\u4e08\u592b\u7684\u597d\u3002\u201d\u4ed6\u4e0d\u5584\u8bf4\u8f9e\uff0c\u53ea\u80fd\u60f3\u51fa\u8fd9\u4e2a\u7406\u7531\u6765\u8bf4\uff0c\u542c\u6765 \u201c\u53ef\u7b11\u201d\uff0c\u4f46\u5374\u8bf4\u5f97\u751a\u4e3a\u8bda\u6073\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u6c89\u541f\u534a\u664c\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u6c5f\u76f8\u516c\uff0c\u4f60\u8bb2\u5f97\u4e5f\u6709\u70b9\u9053\u7406\u3002\u90a3\u4e48\uff0c\u534e\u8001\u5934\uff0c\u4f60\u610f\u4e0b \u5982\u4f55\uff0c\u53ef\u613f\u505a\u6210\u8fd9\u5b97\u4ea4\u6613\u4e48\uff1f\u201d\u534e\u5929\u98ce\u9053\uff0c\u201c\u5f88\u597d\uff0c\u516c\u5e73\u4ea4\u6613\uff0c\u6211\u653e\u4f60\u7684\u4e08\u5927\uff0c\u4f60\u653e\u6211\u7684\u5973 \u513f\uff0e\u201d\u6b27\u9633\u4e8c\u5a18\u683c\u683c\u7b11\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u8fd9\u6837\u5e76\u4e0d\u516c\u5e73\u3002\u201d\u534e\u5929\u98ce\u6012\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u600e\u4e48\uff1f\u201d\u4ed6\u4e2d \u6bd2\u751a\u6df1\uff0c\u6012\u6c14\u4e00\u8d77\uff0c\u767b\u65f6\u5934\u6655\u76ee\u7729\u3002\u4ed6\u672c\u6765\u6df1\u901a\u533b\u7406\uff0c\u4f46\u5973\u513f\u5728\u5bf9\u65b9\u4e4b\u624b\uff0c\u5374\u662f\u65e0\u6cd5\u63a7\u5236\u81ea \u5df1\u7684\u611f\u60c5\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u53c8\u6545\u610f\u6c89\u541f\u4e86\u4e00\u4f1a\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u5973\u513f\u4e1d\u6beb\u6ca1\u4f24\uff0c\u6211\u7684\u4e08\u5927\u5374\u53d7\u4e86\u91cd\u4f24\uff0c \u4ee5\u4e00\u4e2a\u597d\u4eba\u6362\u4e00\u4e2a\u75c5\u4eba\uff0c\u600e\u80fd\u7b97\u5f97\u516c\u5e73\u3002\u201d\u534e\u5929\u51e4\u5438\u4e86\u53e3\u6c14\uff0c\u8fd9\u65f6\u5df2\u65e0\u6687\u4e0e\u5979\u4e89\u8bba\uff0c\u8fde\u5fd9\u95ee \u9053\uff0c\u201c\u4f9d\u4f60\u4e4b\u610f\uff0c\u600e\u6837\u624d\u7b97\u516c\u5e73\uff1f\u201d \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u9053\uff1a\u201c\u6709\u4e24\u6837\u529e\u6cd5\uff0c\u7b2c\u4e00\u6837\u561b\uff0c\u662f\u6211\u5c06\u4f60\u7684\u5973\u513f\u4e5f\u6253\u5f97\u91cd\u4f24\uff0c\u7136\u540e\u653e\u8fd8\u4e0e \u4f60\u3002\u201d\u6c5f\u6d77\u5929\u5fcd\u4e0d\u4f4f\u53eb\u9053\uff1a\u201c\u8fd9\u592a\u4e0d\u8fd1\u60c5\u7406\u4e86\uff01\u201d\u6b27\u9633\u4e8c\u5a18\u7b11\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u8fd9\u6837\u4e43\u662f\u5408\u60c5\u5408 \u7406\uff0c\u4f60\u8bf4\u4e0d\u5408\uff0c\u7a76\u7adf\u5982\u4f55\u4e0d\u5408\uff0c\u4f60\u5012\u8bf4\u8bf4\u770b\u3002\u201d\u534e\u5929\u98ce\u8001\u4e8e\u4e16\u6545\uff0c\u8fd9\u65f6\u5fc3\u4e2d\u4e00\u51db\uff0c\u8bc6\u7834\u4e86\u6b27 \u9633\u4e8c\u5a18\u7684\u8be1\u8ba1\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u662f\u662f\u975e\u975e\uff0c\u73b0\u5728\u4e0d\u5fc5\u4e89\u8bba\u4e86\u3002\u4f60\u5feb\u8bf4\u4f60\u7b2c\u4e8c\u4e2a\u529e\u6cd5\u3002\u201d \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u6b47\u4e86\u4e00\u6b47\uff0c\u7136\u540e\u4ecd\u7136\u662f\u6162\u6761\u65af\u7406\u4ed6\u8bf4\u9053\uff0c\u201c\u597d\uff0c\u7b2c\u4e00\u6761\u529e\u6cd5\u4f60\u4e0d\u540c\u610f\uff0c\u6211\u518d\u8bf4 \u7b2c\u4e8c\u6761\uff0c\u4f60\u5973\u513f\u6ca1\u4f24\uff0c\u6211\u4e08\u592b\u53d7\u4f24\uff0c\u6211\u77e5\u9053\u4f60\u533b\u672f\u9ad8\u660e\uff0c\u53cd\u6b63\u6211\u4e5f\u4e0d\u6025\uff0c\u4e0d\u5982\u4f60\u628a\u4ed6\u533b\u597d \u4e86\u3002\u6211\u518d\u4e0e\u4f60\u4ea4\u6362\u3002\u201d\u534e\u5929\u98ce\u9053\uff0c\u201c\u6211\u54ea\u6709\u8fd9\u4e48\u4e9b\u65f6\u5019\uff0c\u4f60\u4e08\u592b\u4e0d\u4f1a\u6b7b\u7684\uff0c\u56de\u53bb\u597d\u597d\u8c03\u6cbb\u4fbf \u884c\u3002\u6211\u5973\u513f\u53ef\u4e0d\u80fd\u7559\u5728\u4f60\u7684\u624b\u4e2d\uff01\u201d\u6b27\u9633\u4e8c\u5a18\u53c8\u7b11\u9053\uff1a\u201c\u8bdd\u53ef\u4e0d\u662f\u8fd9\u4e48\u8bf4\u4e86\uff0c\u5165\u5e99\u4e0d\u6c42\u7b7e\uff0c \u6709\u540d\u533b\u5728\u9762\u524d\u4e0d\u6c42\u836f\u3002\u54ea\u6709\u8fd9\u7b49\u7b28\u4eba\uff0c\u54c8\u54c8\u2026\u2026\u201d\u534e\u5929\u98ce\u6025\u5fd9\u6253\u65ad\u5979\u7684\u8bdd\u9053\uff1a\u201c\u4f60\u6c42\u836f\u4e48\uff0c \u8fd9\u5bb9\u6613\uff0c\u6211\u7ed9\u4ed6\u5c31\u662f\u3002\u201d\u6b27\u9633\u4e8c\u5a18\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u522b\u7684\u836f\u6211\u4e0d\u8981\uff0c\u4f60\u7ed9\u4ed6\u4e09\u7c92\u5c0f\u8fd8\u4e39\u3002\u201d \u3000\u3000\u534e\u65e0\u98ce\u7709\u5934\u4e00\u76b1\uff0c\u539f\u6765\u8fd9\u201c\u5c0f\u8fd8\u4e39\u201d\u4e43\u662f\u4ed6\u7528\u4e86\u5341\u4e8c\u79cd\u73cd\u5947\u7684\u836f\u7269\u914d\u5236\u800c\u6210\uff0c\u51c6\u5907\u7528\u6765 \u89e3\u6bd2\u7684\uff0c\u8eab\u4e0a\u53ea\u6709\u4e03\u7c92\uff0c\u4ed6\u5728\u8fd9\u6b21\u4e0e\u84b2\u5362\u864e\u52a8\u624b\u4e4b\u524d\uff0c\u9884\u5148\u670d\u4e86\u4e00\u7c92\uff0c\u4ed6\u9884\u8ba1\u5728\u4e2d\u6bd2\u4e4b\u540e\uff0c \u9664\u4e86\u670d\u5176\u4ed6\u7684\u836f\uff0c\u6bcf\u5929\u8fd8\u8981\u7528\u4e00\u7c92\u201c\u5c0f\u8fd8\u4e39\u201d\uff0c\u8fd9\u6837\u4f7f\u53ef\u5728\u4e03\u5929\u4e4b\u5185\uff0c\u5b8c\u5168\u5c06\u6bd2\u62d4\u6e05\uff0c\u5018\u82e5 \u88ab\u6b27\u9633\u4e8c\u5a18\u8981\u53bb\u4e86\u4e09\u7c92\uff0c\u81ea\u5df1\u4fbf\u4e0d\u591f\u7528\u4e86\u3002\u4ed6\u54ea\u77e5\u9053\uff0c\u597d\u5728\u6b27\u9633\u4e8c\u5a18\u672a\u77e5\u4ed6\u8fd8\u6709\u516d\u7c92\uff0c\u56e0\u4e3a \u8fd9\u836f\u975e\u5e38\u96be\u5f97\uff0c\u6b27\u9633\u4e8c\u5a18\u624d\u4ee5\u4e3a\u4ed6\u6700\u591a\u6709\u4e09\u56db\u7c92\uff0c\u8981\u4e0d\u7136\u5979\u53ef\u80fd\u5168\u90e8\u8981\u6e05\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u89c1\u4ed6\u76b1\u7709\uff0c\u4e5f\u7acb\u5373\u9762\u8272\u4e00\u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u613f\u4ea4\u6613\uff0c\u90a3\u5c31\u62c9\u5012\u3002\u201d\u534e\u5929\u98ce\u53ea\u5f97\u638f \u51fa\u4e86\u4e09\u9897\u5c0f\u8fd8\u4e39\uff0c\u53eb\u6c5f\u6d77\u5929\u62ff\u5230\u6b27\u9633\u4ef2\u548c\u9762\u524d\uff0c\u6b27\u9633\u4ef2\u548c\u4f38\u51fa\u820c\u5934\uff0c\u5728\u4e09\u9897\u4e38\u836f\u4e0a\u90fd\u8214\u4e86\u4e00 \u4e0b\uff0c\u7136\u540e\u8bf4\u9053\uff0c\u201c\u4e0d\u60dc\uff0c\u8fd9\u662f\u771f\u7684\u5c0f\u8fd8\u4e39\uff0c\u52b3\u9a7e\u4f60\u5305\u597d\uff0c\u653e\u5728\u6211\u7684\u888b\u5b50\u91cc\uff0c\u6211\u8fd8\u820d\u4e0d\u5f97\u4e00\u4e0b \u5b50\u90fd\u5c06\u5b83\u541e\u4e86\u5462\u3002\u5582\uff0c\u4f60\u5f53\u7740\u6211\u7684\u9762\u5305\u597d\u653e\u597d\uff0c\u522b\u5f04\u624b\u811a\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6012\u9053\uff0c\u201c\u4f60\u628a\u6211\u5f53\u4f5c\u4ec0\u4e48\u4eba\u4e86\uff1f\u201d\u6b27\u9633\u4e8c\u5a18\u8fdc\u8fdc\u7b11\u9053\uff1a\u201c\u5a49\u513f\u7684\u7239\uff0c\u4f60\u653e\u5fc3\uff0c\u6c5f \u76f8\u516c\u662f\u5149\u660e\u78ca\u843d\u7684\u541b\u4e86\uff0c\u8981\u4e0d\u7136\u54b1\u4eec\u7684\u5a49\u513f\u600e\u4f1a\u6ca1\u53e3\u7684\u79f0\u8d5e\u4ed6\u5462\uff1f\u201d\u6c5f\u6d77\u5929\u4e0d\u7981\u60f3\u9053\uff1a\u201c\u6b27 \u9633\u5a49\u5012\u8fd8\u77e5\u9053\u662f\u975e\u9ed1\u5c71\uff0c\u4f60\u4eec\u5374\u8981\u5979\u8ddf\u7740\u4f60\u4eec\u8d70\uff0c\u5c06\u5979\u4e5f\u53d8\u4f5c\u4e86\u5c0f\u4eba\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5c06\u90a3\u4e09\u7c92\u5c0f\u8fd8\u4e39\uff0c\u5f53\u7740\u6b27\u9633\u4ef2\u548c\u7684\u9762\u653e\u8fdb\u4ed6\u7684\u888b\u4e2d\uff0c\u6b27\u9633\u4e8c\u5a18\u8bf4\u9053\uff0c\u201c\u4f60\u4eec\u7ad9\u5728 \u539f\u5730\uff0c\u4e0d\u8bb8\u524d\u8fdb\u3002\u5f85\u6211\u6570\u5230\u5341\u4e0b\uff0c\u53cc\u65b9\u540c\u65f6\u653e\u4eba\u3002\u201d\u6c5f\u6d77\u5929\u8ba8\u538c\u4e4b\u6781\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8bf4\u597d\u4e86 \u653e\u4eba\uff0c\u5374\u504f\u8fd8\u6709\u8fd9\u4e48\u591a\u505a\u4f5c\u3002\u201d\u4ed6\u54ea\u91cc\u77e5\u9053\uff1b\u6b27\u9633\u4e8c\u5a18\u6b63\u662f\u6709\u610f\u8981\u62d6\u5ef6\u65f6\u95f4\uff0c\u4ee4\u534e\u5929\u98ce\u4e0d\u80fd \u5e73\u9759\u4e0b\u6765\u4e13\u5fc3\u7597\u4f24\uff0c\u62d6\u5f97\u4e00\u523b\uff0c\u534e\u5929\u98ce\u6240\u4e2d\u7684\u6bd2\uff0c\u4fbf\u8981\u591a\u6df1\u5490\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u7f13\u7f13\u7684\u4e00\u4e0b\u4e00\u4e0b\u7684\u6570\uff0c\u597d\u4e0d\u5bb9\u6613\u7b49\u5230\u5979\u6570\u5230\u201c\u5341\u201d\u5b57\uff0c\u534e\u65e0\u98ce\u5982\u91ca\u91cd\u8d1f\uff0c\u8f7b\u8f7b \u7684\u5728\u6b27\u9633\u4ef2\u548c\u7684\u80cc\u4e0a\u4e00\u62cd\uff0c\u559d\u9053\uff1a\u201c\u53bb\u5427\uff01\u201d\u6b27\u9633\u4ef2\u548c\u62d4\u6b65\u4fbf\u8dd1\uff0c\u867d\u7136\u6709\u70b9\u8e09\u8dc4\uff0c\u5374\u4ecd\u7136\u6bd4 \u5e38\u4eba\u5feb\u5f97\u591a\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8fd9\u624d\u77e5\u9053\uff0c\u81ea\u5df1\u867d\u7136\u7834\u4e86\u4ed6\u7684\u201c\u5c11\u9633\u7f61\u6c14\u201d\uff0c\u5374\u8fd8\u672a\u80fd\u5e9f\u6389\u4ed6\u7684\u6b66\u529f\u3002\u534e\u5929\u98ce\u4f3c \u662f\u77e5\u9053\u4ed6\u7684\u5fc3\u610f\uff0c\u5fae\u7b11\u8bf4\u9053\uff0c\u201c\u8d24\u4f84\uff0c\u4f60\u80fd\u7834\u4e86\u4ed6\u7684\u5c11\u9633\u7f61\u6c14\uff0c\u5df2\u7ecf\u975e\u5e38\u96be\u5f97\uff0c\u4ed6\u8981\u6062\u590d\u539f \u6765\u7684\u529f\u529b\uff0c\u90a3\u6700\u5c11\u662f\u4e09\u5e74\u4ee5\u540e\u7684\u4e8b\u4e86\uff01\u201d\u4ed6\u8fd9\u8bdd\u5176\u5b9e\u8fd8\u672a\u8bf4\u5f97\u5b8c\u5168\uff0c\u5018\u82e5\u4ed6\u6ca1\u6709\u5c06\u90a3\u4e09\u7c92\u5c0f \u8fd8\u4e39\u9001\u7ed9\u6b27\u9633\u4ef2\u548c\uff0c\u5219\u6b27\u9633\u4ef2\u548c\u6700\u5c11\u8981\u5341\u5e74\u4ee5\u4e0a\u624d\u80fd\u6062\u590d\u539f\u6765\u7684\u6b66\u529f\uff1b\u5982\u4eca\u4ed6\u5f97\u4e86\u7075\u4e39\uff0c\u5018 \u82e5\u61c2\u5f97\u7528\u7684\u8bdd\uff0c\u5219\u4e09\u5e74\u7684\u65f6\u95f4\u4e5f\u8fd8\u53ef\u4ee5\u5927\u5927\u7f29\u77ed\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8fd8\u62c5\u5fc3\u6b27\u9633\u4e8c\u5a18\u518d\u800d\u82b1\u62db\uff0c\u51dd\u795e\u770b\u65f6\uff0c\u53ea\u89c1\u6b27\u9633\u4e8c\u8270\u628a\u7f51\u4e00\u6492\uff0c\u534e\u4e91\u78a7\u4e5f\u5982\u98de\u5954 \u6765\uff0c\u679c\u7136\u4e0d\u50cf\u662f\u53d7\u4f24\u7684\u6837\u5b50\uff0c\u8fd9\u624d\u653e\u4e86\u5fc3\u3002 \u3000\u3000\u8f6c\u77ac\u4e4b\u95f4\uff0c\u534e\u4e91\u78a7\u5df2\u8dd1\u5230\u7236\u4eb2\u8ddf\u524d\uff0c\u773c\u6cea\u4e0d\u7981\u593a\u7736\u800c\u51fa\uff0c\u62b1\u7740\u534e\u5927\u98ce\u53eb\u9053\uff1a\u201c\u7239\u7239\uff01\u201d \u534e\u592d\u98ce\u5ffd\u5730\u9762\u8272\u4e00\u6c89\uff0c\u559d\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u534e\u4e91\u78a7\u6115\u7136\u671b\u7740\u7236\u4eb2\uff0c\u53ea\u542c\u5f97\u534e\u5929\u98ce\u51b7\u7b11\u9053\uff1a\u201c\u6b27 \u9633\u4e8c\u5a18\uff0c\u4f60\u522b\u5f97\u610f\uff0c\u5018\u82e5\u6211\u7684\u5973\u513f\u4e09\u4e2a\u6708\u540e\u6210\u4e3a\u6b8b\u5e9f\uff0c\u4f60\u7684\u5927\u592b\u5c31\u6d3b\u4e0d\u8fc7\u4e03\u5929\u3002\u5feb\u8bf4\uff0c\u4f60\u70b9 \u4e86\u5979\u90a3\u4e00\u5904\u9690\u7a74\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u542c\u5f97\u201c\u9690\u7a74\u201d\u4e8c\u5b57\uff0c\u4e0d\u7981\u5927\u4e3a\u60ca\u9a87\uff0c\u539f\u6765\u4eba\u8eab\u7684\u7a74\u9053\uff0c\u53ef\u5206\u4e3a\u4e24\u79cd\uff0c\u4e00\u79cd\u662f\u5728\u8eab \u4f53\u8868\u9762\u7684\uff0c\u7528\u624b\u6307\u5c31\u53ef\u4ee5\u89e6\u53ca\uff0c\u4e00\u79cd\u662f\u5728\u4f53\u5185\u810f\u8151\u4e4b\u4e2d\u7684\uff0c\u90a3\u5c31\u662f\u8089\u773c\u4e0d\u80fd\u5bdf\u89c1\u7684\u4e86\u3001\u6240\u4ee5 \u540d\u4e3a\u201c\u9690\u7a74\u201d\u3002 \u3000\u3000\u90aa\u6d3e\u5185\u529f\u4e2d\u6709\u597d\u51e0\u79cd\u70b9\u201c\u9690\u7a74\u201d\u4e4b\u6cd5\uff0c\u53d7\u5bb3\u4e4b\u540e\uff0c\u5168\u65e0\u5f02\u611f\uff0c\u8868\u9762\u4e5f\u770b\u4e0d\u51fa\u6765\uff0c\u53ef\u4ee5\u5728 \u51e0\u4e2a\u6708\u4e4b\u540e\u5bf8\u53d1\u4f5c\uff0c\u6216\u751f\u602a\u75c5\uff0c\u6216\u53d8\u75f4\u679c\uff0c\u7aef\u7684\u662f\u9634\u6bd2\u4e4b\u81f3\u3002\u800c\u4e14\u56e0\u4e3a\u201c\u9690\u7a74\u201d\u65e2\u4e0d\u80fd\u5bdf \u89c1\uff0c\u56e0\u6b64\u7eb5\u662f\u7cbe\u901a\u89e3\u7a74\u7684\u529f\u592b\uff0c\u4e5f\u4e0d\u80fd\u77e5\u9053\u53d7\u70b9\u7684\u662f\u54ea\u4e00\u9053\u9690\u7a74\uff0c\u975e\u5f97\u5bf9\u65b9\u544a\u77e5\uff0c\u4fbf\u65e0\u4ece\u7740 \u624b\uff01\u6b63\u662f\uff1a \u3000\u3000\u9632\u4e0d\u80dc\u9632\u906d\u6bd2\u624b\uff0c\u5e78\u5f97\u9b54\u9ad8\u9053\u66f4\u9ad8\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u5341\u4e5d\u56de\u3000\u5e7d\u8c37\u5bd2\u9e26\u6dfb\u5ba2\u6068\u3000\u96ea\u6ce5\u9e3f\u722a\u60f9\u4eba\u601d \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u5341\u4e5d\u56de\u3000\u5e7d\u8c37\u5bd2\u9e26\u6dfb\u5ba2\u6068\u3000\u96ea\u6ce5\u9e3f\u722a\u60f9\u4eba\u601d \u3000\u3000\u534e\u5929\u98ce\u6b64\u8a00\u4e00\u51fa\uff0c\u6b27\u9633\u4ef2\u548c\u4e5f\u4e0d\u7981\u5413\u4e86\u4e00\u8df3\uff0c\u8bd5\u4e00\u8fd0\u6c14\uff0c\u53ea\u89c9\u808b\u9aa8\u9690\u9690\u4f5c\u75db\uff0c\u4e0d\u7981\u5927\u6012 \u9053\uff1a\u201c\u534e\u65e0\u98ce\uff0c\u4f60\u597d\u4e0d\u8981\u8138\uff0c\u5728\u6211\u8eab\u4e0a\u505a\u4e86\u4ec0\u4e48\u624b\u811a\uff1f\u201d \u3000\u3000\u534a\u5929\u98ce\u51b7\u7b11\u9053\uff1a\u201c\u5bf9\u4e0d\u8d77\uff0c\u6211\u662f\u4ee5\u541b\u5b50\u4e4b\u9053\u5bf9\u5f85\u541b\u5b50\uff0c\u4ee5\u5c0f\u4eba\u4e4b\u9053\u5bf9\u5f85\u5c0f\u4eba\uff0c\u4f60\u522b\u4ee5\u4e3a \u6211\u53d7\u4e86\u91cd\u4f24\uff0c\u8bb2\u5230\u70b9\u9690\u7a74\u7684\u529f\u592b\uff0c\u4e5f\u8bb8\u6211\u8fd8\u6bd4\u4f60\u90a3\u5a46\u5a18\u7a0d\u80dc\u5c11\u8bb8\u3002\u4f60\u82e5\u8fd8\u60f3\u6d3b\u547d\u7684\u8bdd\uff0c\u53eb\u4f60 \u7684\u5a46\u5a18\u5148\u8bf4\u51fa\u6765\uff01\u201d\u539f\u6765\u534e\u5929\u98ce\u5728\u4e34\u653e\u4eba\u7684\u65f6\u5019\uff0c\u5728\u6b27\u9633\u4ef2\u548c\u80cc\u4e0a\u90a3\u4e00\u62cd\uff0c\u5df2\u662f\u5c01\u4e86\u4ed6\u809d\u810f \u7684\u4e09\u5904\u9690\u7a74\u3002\u201d \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u6211\u5148\u8bf4\u51fa\u6765\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u4f60\u5978\u8bc8\u767e\u51fa\uff0c\u6211\u4fe1\u4e0d\u8fc7\u4f60\u3002\u8fd9\u5b97 \u4ea4\u6613\uff0c\u4f60\u505a\u4e0d\u505a\uff0c\u968f\u4f60\u7684\u4fbf\u3002\u4f60\u4e5f\u77e5\u9053\u6211\u7a0d\u901a\u533b\u672f\uff0c\u6211\u7eb5\u4e0d\u80fd\u89e3\u7a74\uff0c\u6211\u5973\u513f\u6700\u5c11\u4e0d\u4f1a\u9001\u547d\uff0c \u563f\uff0c\u563f\uff01\u4f60\u7684\u4e08\u592b\u561b\uff0c\u90a3\u53ef\u96be\u8bf4\u4e86\uff01\u201d \u3000\u3000\u6b27\u9633\u4ef2\u548c\u88ab\u4ed6\u4e00\u5413\uff0c\u53ea\u89c9\u808b\u9aa8\u75db\u5f97\u8d8a\u53d1\u5389\u5bb3\uff0c\u8fde\u5fd9\u50ac\u5979\u7684\u59bb\u5b50\u9053\uff1a\u201c\u5feb\u8bf4\uff01\u201d\u6b27\u9633\u4e8c\u5a18 \u53ea\u5f97\u5148\u8bf4\u9053\uff1a\u201c\u6211\u662f\u70b9\u4e86\u5979\u80ba\u8151\u7684\u660e\u5937\u7a74\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u6c5f\u8d24\u4f84\uff0c\u4f60\u8fd8\u80fd\u8fd0\u7528\u4e00\u6307\u7985\u529f\u5417\uff1f\u201d\u6c5f\u6d77\u5929\u53f3\u624b\u7684\u4e2d\u6307\u80bf\u75c8\u4e0d\u582a\uff0c\u82e6\u7740\u8138 \u9053\uff1a\u201c\u6211\u5de6\u624b\u8fd8\u80fd\u8fd0\u7528\uff0c\u53ea\u662f\u6050\u6015\u6700\u591a\u53ea\u80fd\u4f7f\u5f97\u51fa\u539f\u6765\u7684\u4e94\u6210\u529f\u529b\u4e86\u3002\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u6709\u4e94 \u6210\u529f\u529b\uff0c\u5df2\u8db3\u591f\u4e86\uff0c\u4f60\u5e2e\u5fd9\u6211\u66ff\u5979\u89e3\u7a74\uff0c\u5728\u5979\u80c1\u4e0b\u808b\u9aa8\u7684\u7b2c\u4e09\u8282\u5c06\u5185\u529b\u8f93\u9001\u8fdb\u53bb\u3002\u201d\u539f\u6765\u534e \u5929\u98ce\u6b64\u65f6\u5df2\u662f\u7cbe\u75b2\u529b\u7aed\uff0c\u65e0\u6cd5\u518d\u8fd0\u7528\u5185\u529f\u89e3\u7a74\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5927\u662f\u8e0c\u8e87\uff0c\u539f\u6765\u7528\u8fd9\u4e2a\u529e\u6cd5\u89e3\u7a74\uff0c\u975e\u4f46\u8981\u89e6\u53ca\u5979\u7684\u8eab\u4f53\uff0c\u8fd8\u8981\u8d34\u7740\u5979\u7684\u808c\u80a4\uff0c\u4f46 \u6551\u4eba\u8981\u7d27\uff0c\u53ea\u5f97\u539a\u7740\u9762\u76ae\u4e0a\u53bb\uff0c\u8f7b\u8f7b\u62c9\u5f00\u534e\u4e91\u78a7\u7684\u5916\u8863\uff0c\u5c06\u5de6\u624b\u7684\u4e2d\u6307\u6309\u5728\u5979\u80c1\u4e0b\u7684\u7b2c\u4e09\u8282 \u808b\u9aa8\u4e0a\uff0c\u808c\u80a4\u76f8\u63a5\uff0c\u6c14\u606f\u60f3\u95fb\uff0c\u4e24\u4eba\u90fd\u7981\u4e0d\u4f4f\u9762\u7ea2\u8fc7\u8033\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u534e\u4e91\u78a7\u5589\u5934\u201c\u54af\u54af\u201d\u4f5c\u54cd\uff0c\u5410\u51fa\u4e86\u4e00\u53e3\u7600\u8840\uff0c\u534e\u4e91\u78a7\u82b1\u5bb9\u5931\u8272\uff0c\u6c5f\u6d77\u5927\u8bf4 \u9053\uff1a\u201c\u8fd9\u662f\u5e94\u6709\u4e4b\u8c61\uff0c\u4f60\u4e0d\u5fc5\u60ca\u614c\uff01\u201d\u5c06\u624b\u6307\u79fb\u5f00\uff0c\u534e\u5929\u98ce\u70b9\u70b9\u5934\u9053\uff0c\u201c\u5bf9\uff0c\u6c5f\u8d24\u4f84\uff0c\u4f60\u5f88 \u5728\u884c\uff01\u201d\u534e\u4e91\u78a7\u6574\u597d\u8863\u886b\uff0c\u4e00\u65f6\u7f9e\u6127\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u534e\u5929\u98ce\u8ddf\u7740\u4e5f\u628a\u4ed6\u6240\u70b9\u7684\u90a3\u4e09\u5904\u9690\u7a74\u544a\u8bc9\u4e86\u6b27\u9633\u4e8c\u5a18\uff0c\u6b27\u9633\u4e8c\u5a18\u4f9d\u6cd5\u89e3\u7a74\uff0c\u679c\u7136\u6b27\u9633\u4ef2 \u548c\u4e5f\u5410\u51fa\u4e00\u53e3\u7600\u8840\u3002\u968f\u540e\uff0c\u6b27\u9633\u4e8c\u5a18\u5c31\u6276\u7740\u4e08\u5927\u8d70\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5401\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u672a\u89c1\u8fc7\u5982\u6b64\u9634\u6bd2\u7684\u5987\u4eba\uff0c\u679c\u7136\u662f\u6bd4\u90a3\u9634\u8001\u592a\u5a46\u8fd8\u66f4\u72e0 \u8fa3\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u78a7\u513f\uff0c\u6211\u53eb\u4f60\u4e0d\u597d\u51fa\u6765\uff0c\u4f60\u600e\u4e48\u4e0d\u542c\u6211\u7684\u8bdd\uff1f\u201d\u534e\u4e91\u78a7\u9053\uff1a \u201c\u4e0d\u662f\u6211\u4e0d\u542c\u4f60\u7684\u8bdd\uff0c\u53ea\u56e0\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u53ea\u89c1\u534e\u5929\u98ce\u5df2\u4f3c\u98ce\u4e2d\u4e4b\u70db\uff0c\u6447\u6447\u6b32\u5760\uff01 \u3000\u3000\u534e\u4e91\u78a7\u60ca\u9053\uff1a\u201c\u7239\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u6ca1\u3001\u6ca1\u4ec0\u4e48\uff0c\u4f60\u3001\u4f60\u5feb\u6276\u6211\u56de\u53bb\uff01\u201d\u8bdd \u867d\u5982\u6b64\uff0c\u4f46\u89c1\u4ed6\u8138\u4e0a\u7684\u9ed1\u6c14\u5df2\u8d8a\u6765\u8d8a\u6d53\uff1b\u4e00\u9897\u9897\u9ec4\u8c46\u822c\u7c97\u5927\u7684\u6c57\u73e0\u4ece\u989d\u4e0a\u6ef4\u4e0b\u6765\uff1b\u534e\u4e91\u78a7\u66ff \u4ed6\u63e9\u6c57\uff0c\u6c57\u6c34\u7adf\u662f\u70ed\u5f97\u70eb\u624b\uff0c\u534e\u4e91\u78a7\u5fc3\u5934\u9e7f\u649e\uff0c\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u6709\u8bdd\u4e5f\u4e0b\u6562\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5b89\u6170\u5979\u9053\uff1a\u201c\u59d1\u5a18\u653e\u5fc3\uff0c\u4ee4\u5c0a\u533b\u672f\u901a\u795e\uff0c\u8c05\u65e0\u5927\u788d\uff01\u201d\u534e\u4e91\u78a7\u9762\u8272\u60e8\u767d\uff0c\u7d27\u7d27\u54ac \u7740\u5634\u5507\uff0c\u6447\u4e86\u6447\u5934\uff0c\u4e00\u53e5\u8bdd\u4e5f\u4e0d\u8bf4\u3002\u539f\u6765\u534e\u4e91\u78a7\u5bb6\u5b66\u6e0a\u6e90\uff0c\u9887\u901a\u533b\u7406\uff0c\u77e5\u9053\u5979\u7236\u4eb2\u6b63\u5728\u8fd0\u529f \u6297\u6bd2\uff0c\u800c\u770b\u8fd9\u60c5\u666f\uff0c\u6bd2\u5df2\u6df1\u5165\u810f\u8151\uff0c\u5185\u529f\u591a\u597d\uff0c\u4e5f\u51b3\u4e0d\u80fd\u5c06\u6bd2\u5b8c\u5168\u84b8\u53d1\u51fa\u6765\u3002\u5fc3\u91cc\u60f3\u9053\uff1a \u201c\u8981\u662f\u6ca1\u6709\u521a\u624d\u90a3\u4ef6\u610f\u5916\u7684\u4e8b\u60c5\u53d1\u751f\u8fd8\u597d\uff0c\u73b0\u5728\uff0c\u54ce\u2026\u2026\u201d\u5979\u4e0d\u6562\u518d\u60f3\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u89c1\u5979\u5982\u6b64\u795e\u60c5\uff0c\u4e5f\u7740\u4e86\u614c\uff0c\u6025\u6025\u5fd9\u5fd9\u548c\u5979\u6276\u534e\u5929\u98ce\u56de\u53bb\u3002\u4f46\u5947\u602a\u5f97\u5f88\uff0c\u5c06\u8fd1\u77f3 \u6d1e\uff0c\u534e\u4e91\u78a7\u7684\u811a\u6b65\u5374\u53cd\u800c\u6162\u4e86\u4e0b\u6765\uff0c\u795e\u8272\u4e5f\u8d8a\u53d1\u663e\u5f97\u4e0d\u5b89\uff0c\u7adf\u4f3c\u662f\u505a\u4e86\u4ec0\u4e48\u4e8f\u5fc3\u4e4b\u4e8b\u4f3c\u7684\u3002 \u6c5f\u6d77\u5929\u4e0d\u6562\u95ee\u5979\uff1a\u4f46\u5df2\u9690\u9690\u611f\u5230\u4e86\u4e0d\u7965\u4e4b\u5146\u3002 \u3000\u3000\u7ec8\u4e8e\u56de\u5230\u4e86\u4ed6\u4eec\u4f4f\u5bbf\u7684\u90a3\u4e2a\u77f3\u4f97\uff0c\u8fd9\u65f6\u5df2\u662f\u9ece\u660e\u65f6\u5206\u3002\u534e\u5929\u98ce\u597d\u5728\u9884\u5148\u773c\u4e86\u4e00\u9897\u5c0f\u8fd8 \u4e39\uff0c\u73b0\u5728\u8fd0\u4e86\u4e00\u4f1a\u6c14\u529f\uff0c\u836f\u529b\u5c55\u5f00\uff0c\u8138\u8272\u7565\u89c1\u597d\u8f6c\uff0c\u4ed6\u4e00\u8de8\u8fdb\u6d1e\u53e3\uff0c\u4fbf\u5f20\u5f00\u4e86\u773c\u775b\uff0c\u5401\u4e86\u53e3 \u6c14\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u6015\u4e86\uff0c\u54c8\u54c8\uff0c\u84b2\u5362\u864e\uff0c\u4f60\u5728\u79f0\u6bd2\u624b\u5929\u5c0a\uff0c\u4e5f\u672a\u5fc5\u5948\u4f55\u5f97\u4e86\u6211\u534e\u5c71\u533b\u9690\uff0c\u78a7 \u513f\uff0c\u5feb\u5c06\u6211\u7684\u836f\u56ca\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u7b11\u5bb9\u5ffd\u655b\uff0c\u8bdd\u58f0\u4e5f\u7a81\u7136\u4e2d\u65ad\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u4e00\u8fdb\u6d1e\u95e8\uff0c\u5df2\u89c9\u5f97\u60c5\u5f62\u4e0d\u5bf9\uff0c\u91cc\u9762\u7684\u4e1c\u897f\u4e03\u96f6\u516b\u4e71\uff0c\u800c\u534e\u5929\u98ce\u5219\u56e0\u53d7\u4f24\u4e4b\u540e\uff0c\u76ee \u529b\u4e0d\u4f73\uff0c\u4ece\u4eae\u5904\u8d70\u8fdb\u6697\u5904\uff0c\u73b0\u624d\u65b9\u59cb\u5bdf\u89c9\u3002 \u3000\u3000\u534e\u5929\u98ce\u5446\u4e86\u7247\u523b\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u662f\u8c01\u6765\u8fc7\u4e86\uff0c\u6211\u7684\u836f\u56ca\u5462\uff1f\u201d\u534e\u4e91\u78a7\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u7239\uff0c \u5973\u513f\u7f6a\u8be5\u4e07\u6b7b\uff0c\u836f\u56ca\u7ed9\u4eba\u62a2\u53bb\u4e86\uff01\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u662f\u8c01\u62a2\u53bb\u7684\uff1f\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u662f\u90a3\u5996\u5973\u62a2 \u53bb\u7684\uff0c\u5973\u513f\u523a\u4f24\u4e86\u5979\uff0c\u5374\u672a\u80fd\u5c06\u5979\u62e6\u4f4f\uff01\u201d\u4ed6\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u4e0d\u6562\u671b\u5979\u7684\u7236\u4eb2\uff0c\u5374\u671b\u7740\u6c5f\u6d77 \u5929\uff0c\u6c5f\u6d77\u592d\u5fc3\u5934\u4e00\u9707\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u8fd9\u5996\u5973\u5230\u5e95\u662f\u8c01\uff1f\u201d\u534e\u4e91\u78a7\u54ac\u7740\u7259\u6839\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u4f60\u7684 \u597d\u670b\u53cb\u6b27\u9633\u5a49\uff01\u201d \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u6c5f\u6d77\u5929\u50cf\u662f\u53d7\u96f7\u51fb\u4e00\u822c\uff1b\u6d51\u8eab\u98a4\u6296\uff0c\u5446\u4e86\u7247\u523b\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u5f53\u771f\u662f\u5979\uff1f\u201d \u534e\u4e91\u78a7\u9053\uff1a\u201c\u96be\u9053\u6211\u8fd8\u4f1a\u634f\u9020\u4e0d\u6210\uff0c\u6211\u773c\u775b\u672a\u778e\uff0c\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff01\u201d\u5979\u65e2\u662f\u7f9e\u60ed\uff0c\u53c8\u662f\u751f \u6c14\uff0c\u5bf9\u5979\u7684\u7236\u4eb2\u7f9e\u60ed\uff0c\u5bf9\u6c5f\u6d77\u5929\u751f\u6c14\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f60\u5403\u4e86\u5979\u7684\u5927\u4e8f\uff0c\u5982\u4eca\u5979\u53c8\u6765\u5bb3\u6211\u7684\u7236 \u4eb2\uff0c\u4f60\u7adf\u7136\u4ecd\u62a4\u7740\u5979\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u96be\u8fc7\u4e4b\u6781\uff0c\u5fc3\u91cc\u53ea\u662f\u60f3\u9053\uff1a\u201c\u5f53\u771f\u662f\u6b27\u9633\u5a49\u4e48\uff1f\u5f53\u771f\u662f\u6b27\u9633\u5a49\u4e48\uff1f\u201d\u4f46\u8fd9\u4e2a\u95ee \u9898\u3002\u534e\u4e91\u78a7\u65e9\u5df2\u7b54\u590d\u4ed6\u4e86\uff0c\u5979\u662f\u8bf4\u5f97\u90a3\u6837\u5206\u660e\uff0c\u4e0d\u5bb9\u4ed6\u4e0d\u76f8\u4fe1\u3002 \u3000\u3000\u6d89\u8db3\u6c5f\u6e56\u8fd9\u4e2a\u591a\u6708\u6765\uff0c\u6c5f\u6d77\u5929\u5df2\u78b0\u8fc7\u8bb8\u591a\u610f\u5916\uff0c\u800c\u4e14\u597d\u51e0\u6b21\u90fd\u662f\u4e0e\u6b27\u9633\u5a49\u6709\u5173\uff0c\u4f46\u5374\u4ee5 \u8fd9\u4e00\u6b21\u6700\u4ee4\u4ed6\u9707\u9a87\uff01\u8fd9\u5239\u90a3\u95f4\uff0c\u5f80\u4e8b\u4e00\u5e55\u5e55\u7684\u7ffb\u8fc7\u5fc3\u5934\uff0c\u4ed6\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6b27\u9633\u5a49\u5018\u82e5\u771f\u7684\u8fd9 \u4e48\u574f\uff0c\u5979\u90a3\u6b21\u672c\u6765\u53ef\u4ee5\u628a\u6211\u5bb3\u6b7b\u7684\uff0c\u5374\u4e3a\u4f55\u53cd\u800c\u7ed9\u6211\u89e3\u836f\uff1f\u4e3a\u4f55\u8981\u75db\u54ed\u6d41\u6d95\u7684\u4edf\u6094\uff1f\u96be\u9053\u8fd9 \u79cd\u79cd\u90fd\u662f\u505a\u4f5c\uff1f\u6211\u4eca\u665a\u8dcc\u8fdb\u7f51\u4e2d\uff0c\u83ab\u975e\u4e5f\u5f53\u771f\u662f\u5979\u5b89\u6392\u7684\u9677\u9631\uff1f\u5979\u540e\u6765\u5bf9\u5979\u7236\u6bcd\u7684\u54ed\u558a\uff1b\u96be \u9053\u4e5f\u53ea\u4ec5\u4ec5\u662f\u505a\u7ed9\u6211\u770b\u7684\uff1f\u5509\uff0c\u60f3\u4e0d\u5230\u5979\u7adf\u662f\u4e0e\u5979\u6bcd\u4eb2\u4e00\u6837\uff0c\u662f\u4e2a\u5fc3\u80a0\u6076\u6bd2\u5230\u96be\u4ee5\u60f3\u8c61\u7684\u5973 \u4eba\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7a81\u7136\u8f6c\u8fc7\u4e86\u8eab\uff0c\u534e\u5929\u98ce\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u4f60\u8981\u53bb\u54ea\u513f\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u8981\u5c06\u836f\u56ca\u8ffd \u56de\u6765\uff0c\u5c06\u90a3\u5996\u5973\u2026\u2026\u201d\u4ed6\u672c\u60f3\u8bf4\u53e5\u72e0\u8bdd\uff0c\u4f46\u5374\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u5979\u4eec\u5904\u5fc3\u79ef\u8651\u6765\u6697\u7b97\u6211\uff0c\u600e\u80fd\u8ba9\u4f60\u627e\u5f97\u5230\u5979\uff1f\u518d\u8bf4\uff0c\u5979\u4eec\u592b\u59bb\u6bcd\u5973\u4e09\u4eba\uff0c \u4f60\u8ffd\u4e0a\u4e86\u4e5f\u662f\u5b64\u638c\u96be\u545c\u3001\u5feb\u56de\u6765\u5427\u3002\u6211\u6709\u8bdd\u8bf4\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u534e\u8001\u524d\u8f88\uff0c\u6211\u5fc3\u91cc\u96be\u8fc7\u5f97\u5f88\uff0c\u60f3\u4e0d\u5230\u4f1a\u53d1\u751f\u8fd9\u6837\u7684\u4e8b\uff01\u201d\u534e\u5929\u98ce\u7b11\u9053\uff1a \u201c\u8fd9\u4e0e\u4f60\u4f55\u5e72\uff1f\u4f60\u4eca\u665a\u5df2\u7ecf\u6551\u4e86\u6211\u4e86\u3002\u8981\u602a\u53ea\u80fd\u602a\u6211\u81ea\u5df1\uff0c\u672a\u66fe\u5c06\u836f\u56ca\u5e26\u5728\u8eab\u8fb9\uff0c\u4e5f\u672a\u66fe\u5c06 \u5b83\u85cf\u597d\u3002\u201d\u4ed6\u54ea\u77e5\u9053\u6c5f\u6d77\u5929\u590d\u6742\u7684\u5fc3\u60c5\uff0c\u867d\u662f\u6b27\u9633\u5a49\u505a\u7684\u4e8b\u60c5\uff0c\u4ed6\u5374\u6df1\u6df1\u611f\u5230\u5185\u759a\u3002 \u3000\u3000\u534e\u4e91\u78a7\u8fd9\u65f6\u624d\u7f13\u8fc7\u6c14\u6765\uff0c\u95ee\u9053\uff1a\u201c\u533b\u4e66\u548c\u73cd\u8d35\u7684\u836f\u54c1\u4f60\u90fd\u6ca1\u5e26\u4e48\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u533b\u4e66 \u6211\u662f\u4e0d\u79bb\u8eab\u7684\uff0c\u5c0f\u8fd8\u4e39\u6211\u4e5f\u653e\u5728\u8eab\u4e0a\u4e86\u3002\u55ef\uff0c\u4f60\u4e0d\u5fc5\u8fd9\u4e48\u7740\u6025\uff0c\u8fd9\u51e0\u5929\u5185\uff0c\u6211\u4e0d\u4f1a\u6487\u5f00\u4f60 \u7684\uff01\u201d\u6c5f\u6bcf\u5929\u795e\u667a\u672a\u6e05\uff0c\u5bf9\u8fd9\u8bdd\u7684\u610f\u601d\u8fd8\u5f04\u4e0d\u6e05\u695a\uff0c\u8fd8\u5728\u5e86\u5e78\uff0c\u534e\u4e91\u78a7\u5374\u5df2\u542c\u51fa\u8bdd\u4e2d\u6709\u8bdd\uff0c \u4e0d\u7531\u5f97\u5931\u58f0\u53eb\u9053\uff1a\u201c\u7239\uff0c\u6709\u4e86\u5c0f\u8fd8\u4e39\uff0c\u4ecd\u7136\u96be\u4ee5\u6cbb\u597d\u4e48\uff1f\u201d\u56e0\u4e3a\u534e\u5929\u98ce\u8bdd\u4e2d\u4e4b\u610f\uff0c\u65e0\u5f02\u8bf4\u4ed6 \u53ea\u80fd\u518d\u6d3b\u51e0\u5929\u3002 \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u6b7b\u751f\u6709\u547d\uff0c\u6211\u662f\u60f3\u6d3b\u4e0b\u53bb\u7684\u3002\u4f46\u4e5f\u603b\u5f97\u9632\u5907\u610f\u5916\uff0c\u6240\u4ee5\u6211\u8981\u8d81\u8fd9\u65f6\u5019\uff0c\u548c \u4f60\u4eec\u8bf4\u51e0\u53e5\u8bdd\uff0c\u78a7\u513f\uff0c\u8fd9\u662f\u6211\u7684\u533b\u4e66\u548c\u6d41\u4e91\u5251\u8c31\uff0c\u4f60\u8981\u7528\u529f\u94bb\u7814\u3002\u84b2\u5362\u864e\u5df2\u53d7\u4e86\u6211\u7684\u638c\u529b\u6240 \u4f24\uff0c\u53ea\u6015\u6bd4\u6211\u4f24\u5f97\u66f4\u91cd\uff0c\u7eb5\u4f7f\u4e0d\u6b7b\u4e5f\u65e0\u80fd\u4f5c\u6076\u4e86\u3002\u6b27\u9633\u4ef2\u548c\u5f97\u4e86\u6211\u7684\u5c0f\u8fd8\u4e39\u53ef\u4ee5\u4e0d\u6b7b\uff0c\u4f46\u8fd9 \u756a\u6298\u78e8\u4e5f\u591f\u4ed6\u53d7\u4e86\u3002\u6240\u4ee5\u5018\u82e5\u6211\u6709\u4e09\u957f\u4e24\u77ed\u3001\u4f60\u4e0d\u5fc5\u4e3a\u6211\u62a5\u4ec7\uff01\u6211\u8981\u4f60\u7701\u533b\u5b66\u5251\u662f\u4e3a\u4e86\u6551\u4eba \u6d4e\u4e16\uff0c\u4e0d\u662f\u4e3a\u4e86\u62a5\u4ec7\u3002\u6211\u81ea\u6127\u7a7a\u6709\u4e00\u8eab\u6b66\u529f\u533b\u672f\uff0c\u5374\u4e3a\u4e86\u907f\u4ec7\u4e4b\u6545\u3001\u85cf\u5728\u6df1\u5c71\uff0c\u5f88\u5c11\u7528\u8fc7\u8fd9 \u4e24\u79cd\u672c\u9886\u52a9\u4eba\uff0c\u6240\u4ee5\u671b\u4f60\u6bd4\u6211\u505a\u5f97\u66f4\u597d\uff0c\u4f60\u660e\u767d\u4e48\uff1f\u55ef\uff0c\u4f60\u4e0d\u8981\u54ed\uff0c\u4f60\u660e\u767d\u4e86\u5c31\u597d\uff01\u201d\u4ed6\u8bf4 \u5f97\u975e\u5e38\u5e73\u9759\uff0c\u7b80\u76f4\u4e0d\u50cf\u4ea4\u4ee3\u540e\u4e8b\uff0c\u800c\u662f\u6559\u4ed6\u5973\u513f\u600e\u6837\u505a\u4eba\u3002 \u3000\u3000\u534e\u4e91\u78a7\u6cea\u5982\u96e8\u4e0b\uff0c\u62b1\u7740\u7236\u4eb2\u54d1\u58f0\u54ed\u9053\uff1a\u201c\u7239\uff0c\u4f60\uff0c\u4f60\uff0c\u4f60\u4e0d\u80fd\u629b\u5f00\u6211\u5440\uff01\u201d\u534e\u5929\u98ce\u8f7b\u629a \u5979\u7684\u5934\u53d1\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u60f3\u79bb\u5f00\u4f60\uff0c\u53ef\u662f\u73b0\u5728\u5df2\u4e0d\u80fd\u7531\u6211\u4f5c\u4e3b\u4e86\u3002\u5b69\u5b50\u3002\u4f60\u8d77\u6765\uff0c\u542c \u6211\u7684\u5b89\u6392\u3002\u6c5f\u8d24\u4f84\uff0c\u4f60\uff0c\u4f60\u4e5f\u8bf7\u8fc7\u6765\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8d70\u5230\u4ed6\u7684\u8eab\u8fb9\uff0c\u53ea\u89c1\u4ed6\u8138\u4e0a\u9732\u51fa\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6709\u4ef6\u4e8b\u60c5\u8981\u62dc\u6258\u4f60\uff0c\u4f60\u7b54\u5e94 \u5417\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8d74\u6c64\u8e48\u4eba\uff0c\u5728\u6240\u4e0d\u8f9e\uff0c\u8001\u4f2f\u53ea\u987e\u5429\u5490\uff01\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u6211\u6050\u6015\u4e0d\u80fd\u966a\u4f60\u5230 \u91d1\u9e70\u5bab\u8d74\u4f1a\u4e86\uff0c\u4f60\u613f\u610f\u66ff\u6211\u7167\u987e\u4e91\u78a7\u4e48\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u8fd9\u8bdd\uff0c\u5b9e\u5728\u5373\u662f\u4ee5\u5973\u513f\u7684\u7ec8\u8eab\u76f8\u6258\uff0c\u53ef\u662f\u6c5f\u6d77\u5929\u5374\u542c\u4e0d\u61c2\u8fd9\u4e2a\u610f\u601d\uff0c\u4ed6\u6ee1\u6000\u6fc0 \u52a8\uff0c\u4e0d\u5047\u601d\u7d22\u7684\u4fbf\u8bf4\u9053\uff1a\u201c\u8001\u4f2f\uff0c\u8fd9\u662f\u54ea\u91cc\u540e\u6765\uff0c\u8001\u6015\u5bf9\u6211\u8fd9\u6837\u597d\uff0c\u6211\u600e\u80fd\u4e0d\u5c3d\u5fc3\u7167\u987e\u4e91 \u78a7\u3002\u8001\u4f2f\uff0c\u8981\u662f\u4f60\u4e0d\u5acc\u5f03\u7684\u8bdd\uff0c\u6211\uff0c\u6211\u60f3\u2026\u2026\u201d\u534e\u5929\u98ce\u53cc\u773c\u4e00\u5f20\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u4f60\u60f3\u600e \u4e48\uff0c\u8bf4\u5427\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u60f3\u8ba4\u4f60\u4f5c\u4e49\u7236\u3002\u4ece\u4e89\u4e4b\u540e\uff0c\u6211\u548c\u4e91\u78a7\uff0c\u5c31\u4f3c\u59d0\u5f1f\u4e00\u822c\uff01\u201d\u534e\u5929\u98ce\u5598\u6c14\u8bf4 \u9053\uff1a\u201c\u54e6\uff0c\u662f\u8fd9\u6837\u5417\uff1f\u201d\u5ffd\u5730\u95ed\u4e0a\u773c\u775b\uff0c\u5411\u540e\u4fbf\u5012\uff0c\u539f\u6765\u4ed6\u65e9\u5df2\u5fc3\u529b\u4ea4\u75b2\uff0c\u53ea\u60f3\u7b49\u5f85\u6c5f\u6d77\u5929 \u4e00\u53e5\u8bf4\u8bdd\uff0c\u53ef\u662f\u6c5f\u6d77\u5929\u6240\u8bf4\u7684\uff0c\u5374\u5e76\u4e0d\u662f\u4ed6\u6240\u5e0c\u671b\u7684\u8bf4\u8bdd\uff0c\u4ed6\u4e00\u53e3\u771f\u6c14\u8d70\u6b6a\uff0c\u4fbf\u652f\u6301\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u534e\u4e91\u78a7\u60ca\u5f97\u5446\u4e86\u3002\u8fd8\u672a\u54ed\u5f97\u51fa\u6765\uff0c\u5ffd\u89c1\u6c5f\u6d77\u5929\u6251\u4e0a\u524d\u53bb\uff0c\u4e00\u628a\u62b1\u7740\u534e\u5929\u98ce\uff0c \u5de6\u624b\u62c7\u6307\u9876\u7740\u4ed6\u810a\u690e\u7684\u201c\u5929\u67f1\u7a74\u201d\uff0c\u84e6\u7136\u5f20\u53e3\u5bf9\u7740\u80a9\u5934\u4fbf\u54ac\uff01 \u3000\u3000\u534e\u4e91\u78a7\u53eb\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u4f46\u4ed6\u5230\u5e95\u662f\u4e2a\u9887\u901a\u533b\u7406\u7684\u4eba\uff0c\u7acb\u5373\u7701\u609f\u3002\u7981\u4e0d\u4f4f\u773c\u6cea \u593a\u7736\u800c\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u6d77\u54e5\uff0c\u4f60\u600e\u597d\u8fd9\u6837\uff1f\u8fd9\u4e0d\u8fde\u7d2f\u4e86\u4f60\u4e48\uff1f\u201d \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u6b63\u4ee5\u5185\u529f\u5c06\u534e\u5929\u98ce\u4f53\u5185\u7684\u6bd2\u8840\u6324\u5230\u80a9\u5934\uff0c\u66ff\u4ed6\u542e\u6bd2\uff0c\u534e\u4e91\u78a7\u4e0a\u53bb\u963b\u4f4f\u4ed6\uff0c\u5374 \u7ed9\u4ed6\u7528\u62a4\u4f53\u795e\u529f\u5f39\u4e86\u5f00\u53bb\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u53ea\u89c1\u6c5f\u6d77\u5929\u5f20\u53e3\u5410\u51fa\u4e00\u5927\u6ee9\u9ed1\u8272\u7684\u8840\u6db2\uff0c\u7b11\u9053\uff1a\u201c\u4e0d \u8981\u7d27\uff0c\u6211\u4e0d\u4f1a\u4e2d\u6bd2\u7684\uff0c\u6211\u8fd8\u6709\u78a7\u7075\u4e39\u3002\u201d\u4ed6\u5e26\u7b11\u8bf4\u8bdd\uff0c\u53ef\u662f\u4ed6\u7684\u820c\u5934\u4ea6\u5df2\u7ecf\u9ebb\u672c\uff0c\u8bf4\u8bdd\u4e5f\u4e0d \u6e05\u695a\u4e86\u3002 \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u867d\u7136\u4e0d\u61c2\u533b\u672f\uff0c\u4f46\u5374\u4ece\u5e08\u7236\u90a3\u513f\u542c\u8fc7\u8fd9\u79cd\u6025\u6551\u7684\u6cd5\u5b50\uff0c\u4ed6\u8ddf\u5e08\u7236\u6240\u7ec3\u7684\u5185\u529f \u4e0e\u4f17\u4e0d\u540c\uff0c\u53ea\u8981\u8eab\u4e0a\u6ca1\u6709\u4f24\u53e3\uff0c\u4e00\u542e\u5373\u5c06\u6bd2\u8840\u5410\u51fa\uff0c\u4fbf\u4e0d\u81f3\u4e8e\u6709\u6027\u547d\u4e4b\u5fe7\u3002\u5f53\u7136\uff0c\u82e5\u662f\u4e8b\u540e \u4e0d\u80fd\u9002\u5f53\u8c03\u6cbb\uff0c\u4ecd\u7136\u8fd8\u4f1a\u8499\u53d7\u4f24\u5bb3\uff0c\u6240\u4ee5\u4ed6\u5728\u542e\u4e86\u6bd2\u8840\u4e4b\u540e\uff0c\u4fbf\u8981\u53e3\u542b\u7528\u5929\u5c71\u96ea\u83b2\u6240\u6ce1\u5236\u7684 \u78a7\u7075\u4e39\u6765\u6d88\u9664\u53e3\u8154\u4e2d\u7684\u79fd\u6bd2\u3002 \u3000\u3000\u91d1\u4e16\u9057\u66fe\u9001\u7ed9\u4ed6\u7236\u4eb2\u6c5f\u5357\u4e09\u9897\u78a7\u7075\u4e39\uff0c\u6c5f\u5357\u79bb\u5bb6\u4e4b\u65f6\uff0c\u5e26\u8d70\u4e86\u4e00\u9897\uff0c\u7559\u4e00\u9897\u5728\u5bb6\u4e2d\u7ed9\u4ed6 \u5cb3\u6bcd\u4ee5\u5907\u4e0d\u65f6\u4e4b\u9700\uff0c\u6700\u540e\u4e00\u9897\u5219\u4ea4\u7ed9\u4e86\u513f\u5b50\uff0c\u53ee\u5631\u4ed6\u975e\u5230\u6551\u547d\u4e4b\u65f6\uff0c\u4e0d\u53ef\u8f7b\u7528\u3002\u4f46\u73b0\u5728\uff0c\u4ed6 \u4e0d\u4e3a\u6551\u81ea\u5df1\u7684\u547d\uff0c\u800c\u662f\u4e3a\u4e86\u6551\u534e\u5929\u98ce\u7684\u6027\u547d\u7528\u4e0a\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u534e\u5929\u98ce\u60a0\u60a0\u9192\u8f6c\uff0c\u89c1\u6c5f\u6d77\u5929\u5634\u8fb9\u7684\u8840\u6e0d\uff0c\u6115\u4e86\u4e00\u6115\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8d24\u4f84\uff1b\u4f60 \u8fd9\u662f\u4f55\u82e6\u5462\uff1f\u8001\u592b\u5df2\u6d3b\u4e86\u8fd9\u4e00\u5927\u628a\u5e74\u7eaa\uff0c\u65e2\u6b7b\u4ea6\u65e0\u8db3\u60dc\uff0c\u4f60\u4f55\u5fc5\u8017\u635f\u529f\u529b\uff0c\u4ee4\u6211\u82df\u5ef6\u6b8b\u5598\u3002\u201d \u3000\u3000\u539f\u6765\u534e\u5929\u98ce\u7ecf\u4ed6\u542e\u6bd2\u4e4b\u540e\uff0c\u6027\u547d\u867d\u7136\u6682\u53ef\u65e0\u5fe7\uff0c\u4f46\u56fd\u5931\u4e86\u836f\u56ca\u6240\u8d2e\u5907\u7684\u836f\u54c1\uff0c\u53ea\u4ed7\u5c0f\u8fd8 \u4e39\u4e4b\u529b\uff0c\u4ecd\u7136\u65e0\u6cd5\u6e05\u9664\u810f\u8151\u4e2d\u7684\u4f59\u6bd2\uff0c\u800c\u4e14\u5728\u8fd9\u8352\u5c71\u77f3\u7a9f\uff0c\u8bf8\u7269\u6b20\u7f3a\uff0c\u53c8\u975e\u9002\u5b9c\u4e8e\u517b\u75c5\u4e4b \u5730\uff0c\u4ed6\u81ea\u5fd6\u7eb5\u80fd\u591a\u6d3b\u4e9b\u65f6\u3002\u4e5f\u4e0d\u8fc7\u62d6\u5ef6\u65f6\u65e5\u800c\u5df2\uff0c\u56e0\u6b64\u4ecd\u7136\u662f\u4e00\u7247\u60b2\u51c9\u5931\u671b\u7684\u60c5\u7eea\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5ffd\u5730\u90d1\u91cd\u8bf4\u9053\uff1a\u201c\u8001\u4f2f\uff0c\u4f60\u8fd9\u8bdd\u4e0d\u5bf9\uff01\u201d\u534e\u65e0\u98ce\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u521a\u624d\u4e0d\u662f\u53f9\u606f\u7a7a\u6709\u4e00\u8eab\u6b66\u529f\u533b\u672f\uff0c\u5374\u672a\u66fe\u600e\u6837\u7528\u6765\u6d4e\u4e16\u6551\u4eba\u5417\uff1f\u78a7\u59d0\u867d \u7136\u5f97\u4f60\u6240\u4f20\uff0c\u4f46\u8981\u5b66\u5230\u4f60\u5982\u4eca\u8fd9\u822c\u672c\u9886\uff0c\u6700\u5c11\u8fd8\u5f97\u591a\u5e74\uff0c\u4f60\u53ef\u4ee5\u6d3b\u4e3a\u4ec0\u4e48\u4e0d\u6d3b\u4e0b\u53bb\uff1f\u4f60\u80fd\u591f \u505a\u800c\u53c8\u5e94\u8be5\u505a\u7684\u4e8b\uff0c\u4e3a\u4ec0\u4e48\u8981\u6446\u5728\u5973\u513f\u80a9\u4e0a\uff1f\u8fd8\u4e0d\u662f\u63a8\u5378\u505a\u4eba\u7684\u8d23\u4efb\u5417\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u7ed9\u4ed6\u8bf4\u5f97\u5446\u4e86\uff0c\u534e\u4e91\u78a7\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f60\u6559\u5973\u513f\u533b\u672f\u7684\u65f6\u5019\u8bf4\u8fc7\uff1b\u53ea\u8981\u75c5\u4eba\u8fd8 \u6709\u4e00\u7ebf\u5e0c\u671b\uff0c\u5c31\u8981\u60f3\u6cd5\u533b\u597d\u4ed6\uff0c\u505a\u533b\u751f\u7684\u5207\u4e0d\u53ef\u754f\u96be\u7f29\u624b\uff0c\u90a3\u4e48\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u60f3\u6cd5\u5b50\u533b\u597d\u81ea \u5df1\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u5446\u4e86\u7247\u523b\uff0c\u4e24\u9897\u6cea\u73e0\u4ece\u773c\u89d2\u6d41\u4e86\u4e0b\u6765\uff0c\u4f46\u4f18\u90c1\u7684\u795e\u8272\u5df2\u662f\u4e00\u626b\u800c\u7a7a\uff0c\u7b11\u7740\u8bf4\u9053\uff1a \u201c\u4f60\u4eec\u90fd\u8fd9\u4e48\u8bf4\uff0c\u90a3\u53ef\u8feb\u5f97\u6211\u975e\u52a8\u52a8\u8111\u7b4b\uff0c\u60f3\u60f3\u529e\u6cd5\u4e0d\u53ef\u4e86\uff0c\u8981\u4e0d\u7136\u4e5f\u8f9c\u8d1f\u4e86\u6c5f\u8d24\u4f84\u7684\u4e00\u756a \u597d\u610f\u3002\u201d\u4ed6\u773c\u5149\u4e00\u77a5\uff0c\u89c1\u6c5f\u6d77\u5929\u7684\u624b\u6307\u4ecd\u7136\u7ea2\u80bf\uff0c\u53c8\u7b11\u7740\u9053\uff1a\u201c\u78a7\u513f\uff0c\u9488\u7a74\u653e\u8840\u4e4b\u6cd5\u4f60\u662f\u5b66 \u8fc7\u7684\u4e86\uff0c\u4f60\u5c31\u66ff\u6d77\u5929\u6cbb\u4e00\u6cbb\u5427\u3002\u201d\u8bf4\u7f62\u95ed\u4e86\u53cc\u76ee\uff0c\u5982\u6709\u6240\u601d\u3002 \u3000\u3000\u534e\u4e91\u78a7\u9053\uff1a\u201c\u5230\u8fd9\u8fb9\u6765\u3001\u8ba9\u7239\u7239\u9759\u9759\u7528\u795e\u3002\u201d\u5979\u63e1\u7740\u6c5f\u6d77\u5929\u7ea2\u80bf\u7684\u4e2d\u6307\uff0c\u6ee1\u8138\u53c8\u662f\u611f\u6fc0 \u53c8\u662f\u601c\u501f\u7684\u795e\u60c5\uff0c\u6c5f\u6d77\u5929\u7ea2\u4e86\u8138\u53c8\u4e0d\u6562\u50ac\u5979\u5feb\u6cbb\u3002\u534a\u664c\u4e4b\u540e\uff0c\u534e\u4e91\u78a7\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u6d77\u54e5\uff0c\u4f60 \u5bf9\u6211\u4eec\u8fd9\u6837\u597d\uff0c\u6211\u771f\u4e0d\u77e5\u8be5\u600e\u6837\u6279\u7b54\u4f60\uff1f\u6211\u4e0d\u61c2\u8bf4\u8bdd\uff0c\u521a\u624d\u4e00\u65f6\u7740\u6025\uff0c\u8fc1\u6012\u4e8e\u4f60\uff1b\u671b\u4f60\u4e0d\u8981 \u89c1\u602a\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u672c\u6765\u662f\u6211\u4e0d\u597d\uff0c\u602a\u4e0d\u5f97\u4f60\u3002\u6211\u8bef\u4ea4\u532a\u4eba\u6094\u5df1\u65e0\u53ca\u3002\u65e5\u540e\u8981\u662f\u78b0\u89c1\u90a3\u4e2a\u5996 \u5973\uff0c\u6211\u4e00\u5b9a\u8981\u66ff\u8001\u4f2f\u62a5\u4ec7\u3002\u201d\u534e\u4e91\u78a7\u672c\u6765\u662f\u6101\u5bb9\u6ee1\u9762\u7684\uff0c\u8fd9\u65f6\u5374\u4e0d\u7981\u5c55\u7709\u4e00\u7b11\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u5f53\u771f\uff1f\u53ea\u6015\u4f60\u89c1\u5230\u5979\u65f6\u53c8\u820d\u4e0d\u5f97\u4e86\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6da8\u7ea2\u4e86\u8138\uff0c\u6b63\u5f85\u5206\u8fa9\uff0c\u201c\u534e\u4e91\u78a7\u5df2\u5835\u7740\u4ed6\u7684\u5634\u9053\uff1a\u201c\u6211\u662f\u7ed9\u4f60\u95f9\u7740\u73a9\u7684\uff0c\u6c5f\u6e56\u9669 \u6076\uff0c\u4eba\u5fc3\u96be\u6d4b\uff0c\u4e0d\u7ecf\u4e00\u4e8b\uff0c\u4e0d\u957f\u4e00\u667a\uff0c\u4f60\u65e2\u8bc6\u7834\u4e86\u90a3\u5996\u5973\u7684\u672c\u6765\u9762\uff0c\u4ee5\u540e\u5c0f\u5fc3\uff0c\u8fd9\u5c31\u884c \u4e86\u3002\u201d\u6c5f\u6d77\u5929\u8033\u6735\u542c\u5979\u8bf4\u8bdd\uff0c\u8111\u6d77\u91cc\u5374\u6cdb\u8d77\u4e86\u6b27\u9633\u5a49\u7684\u5f71\u5b50\uff0c\u53ea\u89c9\u4e00\u7247\u832b\u7136\uff0c\u4e0d\u7981\u53c8\u5728\u60f3 \u9053\uff1a\u201c\u6b27\u9633\u5a49\u5f53\u771f\u662f\u8fd9\u4e48\u574f\u4e48\uff1f\u201d \u3000\u3000\u534e\u4e91\u78a7\u53d6\u51fa\u4e00\u652f\u94f6\u9488\uff0c\u6311\u7834\u6c5f\u6d77\u5929\u7684\u4e2d\u6307\uff0c\u5c06\u6bd2\u8840\u6324\u4e86\u51fa\u6765\uff0c\u518d\u9488\u523a\u4ed6\u624b\u5c11\u9633\u7ecf\u8109\u7684\u4e09 \u5904\u7a74\u9053\u3001\u65bd\u672f\u4e4b\u540e\uff0c\u6c5f\u6d77\u5929\u53ea\u89c9\u4e00\u7247\u6e05\u51c9\uff0c\u75db\u695a\u5c3d\u5931\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8c22\u8c22\u3002\u201d\u534e\u4e91\u78a7\u7b11\u9053\uff1a \u201c\u4f60\u600e\u4e48\u8001\u662f\u548c\u6211\u5ba2\u6c14\uff0c\u8fd9\u70b9\u5c0f\u4e8b\uff0c\u4e5f\u8981\u591a\u8c22\uff0c\u90a3\u4e48\u6211\u53c8\u8be5\u5982\u4f55\u8c22\u4f60\u5462\uff1f\u5582\uff0c\u4f60\u662f\u51e0\u65f6\u751f \u65e5\uff1f\u201d\u8fd9\u8bdd\u95ee\u5f97\u751a\u662f\u7a81\u5140\uff0c\u6c5f\u6d77\u5929\u6014\u4e86\u4e00\u6014\uff0c\u7b54\u9053\uff1a\u201c\u4e09\u6708\u4e8c\u5341\u4e00\u5348\u65f6\u3002\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u6211 \u662f\u56db\u6708\u521d\u516b\u751f\u65e5\uff0c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u5e94\u8be5\u662f\u6211\u7684\u54e5\u54e5\u3002\u201d\u6c5f\u6d77\u5929\u548c\u5979\u540c\u662f\u5341\u516d\u5c81\uff0c\u8fd9\u662f\u5979\u65e9\u5df2\u77e5\u9053 \u7684\u4e86\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u534e\u5929\u51e4\u8f7b\u58f0\u54b3\u55fd\uff0c\u6c5f\u6d77\u5929\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u4ed6\u5df2\u5f20\u5f00\u53cc\u773c\uff0c\u76ee\u5149\u6b63\u5411\u7740\u8fd9\u8fb9\u6295\u6765\uff0c \u76ee\u5149\u4e2d\u4f3c\u5408\u559c\u6c14\uff0c\u597d\u4f3c\u89e3\u51b3\u4e86\u4ec0\u4e48\u96be\u9898\u4f3c\u7684\u3002 \u3000\u3000\u534e\u4e91\u78a7\u8d70\u8fc7\u53bb\u9053\uff1a\u201c\u7239\uff0c\u6211\u5df2\u66ff\u6d77\u54e5\u6cbb\u597d\u4f24\u4e86\uff0c\u4f60\u5462\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u7b11\u9053\uff1a\u201c\u6211\u7684\u4f24\u53ef\u4e0d\u80fd\u5728\u8fd9\u91cc\u6cbb\uff0c\u521a\u624d\u6211\u5076\u7136\u60f3\u8d77\uff0c\u8fd9\u5c71\u897f\u9762\u5927\u7ea6\u767e\u91cc\u5de6\u53f3\uff0c\u6709 \u4e2a\u6c34\u4e91\u4e61\uff0c\u4e61\u4e2d\u6709\u4e2a\u59d3\u4e91\u7684\u4eba\u5bb6\uff0c\u4f60\u4eec\u5c06\u6211\u9001\u53bb\u3002\u8bf7\u4ed6\u6536\u7559\uff0c\u6211\u53ef\u4ee5\u6258\u4ed6\u4e70\u836f\uff0c\u5728\u90a3\u91cc\u517b \u4f24\u3002\u5f85\u6062\u590d\u4e86\u513f\u5206\uff0c\u6211\u518d\u8bf7\u4ed6\u4eec\u9001\u6211\u56de\u5bb6\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u9053\uff1a\u201c\u90a3\u59d3\u4e91\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u662f\u4e2a\u6b66\u6797\u4e16\u5bb6\u3002\u636e\u8bf4\u4ed6\u4eec\u7684\u8fdc\u7956\u4e43\u662f \u4e0e\u5f20\u4e39\u67ab\u540c\u65f6\u7684\u524d\u671d\u5927\u4fa0\u4e91\u91cd\uff0c\u660e\u4ea1\u4e4b\u540e\uff0c\u4e3e\u5bb6\u907f\u96be\uff0c\u5728\u7941\u8fde\u5c71\u4e0b\uff0c\u5f00\u8f9f\u4e86\u8fd9\u6c34\u4e91\u4e61\u3002\u73b0\u5728 \u7684\u5e84\u4e3b\u540d\u53eb\u4e91\u53ec\uff0c\u5bb6\u4f20\u7684\u5927\u529b\u91d1\u94a2\u638c\u529f\u592b\uff0c\u5929\u4e0b\u65e0\u53cc\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u9053\uff1a\u201c\u7239\uff0c\u4ed6\u662f\u4f60\u7684\u597d\u670b\u53cb\u4e48\uff1f\u600e\u4e48\u6211\u4ece\u672a\u542c\u4f60\u8bf4\u8fc7\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u8ba4 \u8bc6\u4ed6\u3002\u201d\u534e\u4e91\u78a7\u8fdf\u7591\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u8bf7\u4ed6\u6536\u7559\uff0c\u4e0d\u5acc\u5192\u6627\u4e48\uff1f\u201d\u534e\u5929\u98ce\u7b11\u9053\uff1a\u201c\u6c5f\u6e56\u4e49\u58eb\uff0c \u809d\u80c6\u76f8\u7167\uff0c\u5f7c\u6b64\u95fb\u540d\uff0c\u4f55\u987b\u76f8\u8bc6\uff1f\u90a3\u4e91\u53ec\u662f\u4e2a\u53ef\u4ee5\u6027\u547d\u4ea4\u6258\u7684\u4eba\uff1a\u65e0\u987b\u62d8\u6ce5\u4fd7\u793c\uff0c\u4f60\u4eec\u628a\u6211 \u9001\u53bb\u4fbf\u662f\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u4e5f\u66fe\u542c\u5e08\u7236\u63d0\u8d77\u8fc7\u4e91\u53ec\u7684\u5927\u540d\uff0c\u8bf4\u4ed6\u7684\u786e\u662f\u4e2a\u53e4\u9053\u70ed\u80a0\u7684\u541b\u5b50\u3002\u8001\u4f2f\uff0c \u4f60\u5728\u90a3\u91cc\u533b\u4f24\uff0c\u54ea\u662f\u6700\u597d\u4e0d\u8fc7\uff0c\u6211\u80cc\u4f60\u53bb\u3002\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u521a\u624d\u4e0d\u662f\u8bf4\u8981\u62dc\u6211\u7239\u7239\u4f5c \u4e49\u7236\u5417\uff1f\u600e\u4e48\u8fd8\u662f\u8fd9\u4e2a\u79f0\u547c\uff1f\u201d\u6c5f\u6bcf\u5929\u9053\uff1a\u201c\u5c31\u4e0d\u77e5\u534e\u8001\u524d\u8f88\u80af\u4e0d\u80af\u8981\u6211\uff1f\u201d\u534e\u5929\u98ce\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u53ea\u6015\u6211\u6ca1\u6709\u8fd9\u4e2a\u798f\u6c14\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8dea\u4e0b\u78d5\u5934\uff0c\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u5e72\u7239\u3002\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u6211\u5df1\u95ee\u8fc7\u4ed6\u7684\u751f\u65e5\u4e86\uff0c\u4ed6\u6bd4\u6211\u5927 \u534a\u4e2a\u6708\u3002\u201d\u6c5f\u6d77\u5929\u4e0e\u5979\u76f8\u4e92\u4e00\u62dc\uff0c\u4ece\u6b64\u4e5f\u6539\u53e3\u4ee5\u5144\u59b9\u76f8\u79f0\u3002\u534e\u5929\u98ce\u5e76\u975e\u5341\u5206\u6ee1\u610f\uff0c\u5374\u4e5f\u6b22 \u559c\u3002\u534e\u4e91\u78a7\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u5fc3\u65e0\u6742\u5ff5\uff0c\u4ed6\u53ea\u77e5\u9053\u5f88\u559c\u6b22\u6c5f\u6d77\u5929\uff0c\u6839\u672c\u672a\u60f3\u5230\u7231\u60c5\uff0c\u6240\u4ee5\u8ba4\u4e86\u4ed6\u4f5c \u54e5\u54e5\uff0c\u4fbf\u5df2\u5fc3\u6ee1\u610f\u8db3\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u78a7\u59b9\uff0c\u4f60\u8d76\u5feb\u6536\u62fe\u884c\u674e\uff0c\u54b1\u4eec\u73b0\u5728\u5c31\u8d70\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u80cc\u7740\u75c5\u4eba\uff0c\u4e0d\u6562\u5feb\u8dd1\uff0c\u7941\u8fde\u5c71\u5c71\u52bf\u9669\u5cfb\uff0c\u4e0a\u5c71\u4e0d\u6613\uff0c\u4e0b\u5c71\u66f4\u96be\uff0c\u8d70\u4e86\u5927\u534a\u5929\uff0c\u624d \u5c06\u8fd1\u51fa\u5c71\u7684\u8c37\u53e3\u3002\u534e\u5929\u98ce\u5ffd\u9053\uff1a\u201c\u6709\u4eba\u6765\u4e86\uff0c\u8d76\u5feb\u8eb2\u8d77\u6765\u3002\u201d \u3000\u3000\u9644\u8fd1\u6709\u4e00\u4e1b\u8305\u8349\uff0c\u6bd4\u4eba\u8fd8\u9ad8\uff0c\u6c5f\u6d77\u5929\u5c06\u534e\u65e0\u98ce\u80cc\u8fdb\u8305\u8349\u4e1b\u4e2d\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u679c\u7136\u542c\u5f97\u6709\u811a \u6b65\u58f0\u8fdc\u8fdc\u4f20\u6765\uff0c\u6765\u5f97\u8fc5\u901f\u4e4b\u6781\uff0c\u6c5f\u6d77\u5929\u5fc3\u91cc\u6697\u9053\uff1a\u201c\u5e72\u7239\u867d\u5728\u75c5\u4e2d\uff0c\u8033\u76ee\u8fd8\u662f\u6bd4\u6211\u7075\u654f\u5f97 \u591a\u3002\u542c\u8fd9\u6b65\u58f0\uff0c\u8fd9\u4e24\u4eba\u7684\u6b66\u529f\u7adf\u4f3c\u4e0d\u5728\u6b27\u9633\u4ef2\u548c\u4e0e\u84b2\u5362\u864e\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u8f6c\u77ac\u4e4b\u95f4\uff0c\u90a3\u4e24\u6761\u4eba\u5f71\u5df1\u4ece\u8305\u8349\u65c1\u8fb9\u63a0\u8fc7\uff0c\u534e\u65e0\u98ce\u7b49\u4e09\u4eba\u90fd\u5c4f\u606f\u4e86\u547c\u5438\uff0c\u5e78\u559c\u6ca1\u6709\u7ed9\u4ed6 \u4eec\u53d1\u73b0\u3002\u4ece\u80cc\u5f71\u770b\u6765\uff0c\u53ef\u4ee5\u5206\u522b\u51fa\u662f\u4e00\u7537\u4e00\u5973\uff0c\u9762\u76ee\u5c31\u770b\u4e0d\u6e05\u695a\u4e86\uff0c\u53ea\u542c\u5f97\u90a3\u5973\u7684\u8bf4\u9053\uff1a \u201c\u84b2\u5362\u864e\u8bf4\u90a3\u8001\u7684\u5df2\u53d7\u4e86\u4f24\uff0c\u8c05\u4ed6\u4eec\u8d70\u5f97\u4e0d\u8fdc\uff1a\u5374\u600e\u7684\u4e0d\u89c1\u8e2a\u8ff9\uff1f\u201d\u90a3\u7537\u7684\u9053\uff1a\u201c\u8001\u7684\u4e0d\u89c1 \u8fd8\u65e0\u6240\u8c13\uff0c\u59d3\u6c5f\u90a3\u5c0f\u4e8e\u5374\u662f\u975e\u6293\u7740\u4ed6\u4e0d\u53ef\uff01\u201d\u6c5f\u6d77\u5929\u6012\u6c14\u6697\u751f\uff0c\u4f46\u542c\u4ed6\u4eec\u7684\u5395\u6c14\uff0c\u5012\u4f3c\u4e4e\u4e0d \u662f\u84b2\u5362\u864e\u7684\u4e00\u515a\uff0c\u800c\u662f\u51b2\u7740\u4ed6\u6765\u7684\u3002 \u3000\u3000\u8fdc\u8fdc\u542c\u5f97\u90a3\u7537\u5b50\u7b11\u9053\uff1a\u201c\u7a46\u5927\u59d0\uff0c\u4f60\u4e5f\u5fd2\u8fa3\u624b\u4e86\uff0c\u84b2\u5362\u864e\u7ed9\u54b1\u4eec\u634e\u6765\u8fd9\u6837\u5b9d\u8d35\u7684\u6d88\u606f\uff0c \u4f60\u5374\u4e00\u5200\u5c06\u4ed6\u5241\u4e86\uff01\u54ea\u5973\u7684\u4e5f\u7b11\u9053\uff1a\u201c\u4ed6\u53cd\u6b63\u5df2\u53d7\u4e86\u91cd\u4f24\uff0c\u4e0d\u6740\u4ed6\u4e5f\u6d3b\u4e0d\u6210\u4e86\uff0c\u4f55\u5fc5\u591a\u7559\u6d3b \u53e3\u3002\u201d\u8bf4\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u90a3\u4e24\u4eba\u7684\u80cc\u5f71\u5df2\u7ecf\u4e0d\u89c1\uff0c\u8bdd\u58f0\u4e5f\u542c\u4e0d\u5230\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4f4e\u58f0\u9a82\u9053\uff1a\u201c\u53c8\u662f\u4e00\u4e2a\u72e0\u6bd2\u7684\u5973\u4eba\uff01\u6211\u4e0e\u4ed6\u4eec\u65e0\u51a4\u65e0\u4ec7\uff0c\u4e0d\u77e5\u4ed6\u4eec\u4e3a\u4f55\u8981\u52a0\u5bb3\u4e8e \u6211\uff1f\u201d\u534e\u4e91\u78a7\u9053\uff0c\u201c\u4f60\u5c31\u5fcd\u7740\u70b9\u5427\uff0c\u63d0\u9632\u4ed6\u4eec\u56de\u6765\u5bfb\u627e\u3002\u201d\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u5018\u82e5\u4e0d\u662f\u4e3a\u4e86\u5e72 \u7239\u3002\u6211\u5012\u8981\u548c\u4f60\u4eec\u89c1\u89c1\u9ad8\u4e0b\uff0c\u770b\u4f60\u4eec\u80fd\u5426\u5c06\u6211\u6293\u7740\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u4f0f\u5730\u542c\u58f0\uff0c\u5ffd\u4ed6\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u4e24\u4eba\u5df2\u8d70\u5230\u5c71\u8c37\u4e86\uff0c\u54a6\uff0c\u4ece\u90a3\u8fb9\u53c8\u6765\u4e86\u4e24\u4e2a\u4eba\uff0c\u4ed6 \u4eec\u8981\u78b0\u5934\u4e86\u3002\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u90a3\u5973\u7684\u5389\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u7ed9\u6211\u7ad9\u4f4f\uff0c\u4e0d\u8bb8\u52a8\uff01\u201d\u6c5f\u6d77\u5929\u5403\u4e86\u4e00\u60ca\uff0c \u4f46\u968f\u5373\u660e\u767d\uff0c\u8fd9\u5973\u4eba\u4e43\u662f\u5411\u53e6\u5916\u90a3\u4e24\u4e2a\u4eba\u559d\u95ee\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e00\u4e2a\u5c11\u5973\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u6211\u4eec\u8d70\u6211\u4eec\u7684\uff0c\u4e0e\u4f60\u4f55\u5e72\uff1f\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c \u80c6\u6562\u5728\u8fd9\u91cc\u6a2a\u884c\u9738\u9053\u3002\u201d\u5979\u4eec\u60f3\u5fc5\u90fd\u662f\u52a8\u4e86\u809d\u706b\uff0c\u5973\u5b50\u7684\u58f0\u97f3\u53c8\u7279\u522b\u5c16\u9510\uff0c\u6240\u4ee5\u6c5f\u6d77\u5929\u90fd\u542c \u5f97\u89c1\u3002 \u3000\u3000\u968f\u5373\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u534e\u5929\u98ce\u4fcf\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u5c11\u5973\u8eab\u6cd5\u597d\u5feb\uff0c\u907f\u8fc7\u4e86\u4e00\u638c\u4e86\u3002\u8fd9\u4e00\u638c \u6253\u4e2d\u4e86\u5ca9\u77f3\u3002\u201d\u8ddf\u7740\u4e00\u4e2a\u5c11\u5e74\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u8d3c\u5a46\u5a18\uff0c\u4f60\u6562\u6253\u4eba\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u78b0\u5230\u8fd9\u6837 \u7684\u6076\u4eba\uff0c\u8fd8\u548c\u5979\u591a\u8bf4\u5219\u751a\uff1f\u8d3c\u5a46\u5a18\u770b\u638c\uff01\u201d \u3000\u3000\u53ea\u542c\u5f97\u8c37\u5e95\u4f20\u6765\u95f7\u96f7\u4f3c\u7684\u58f0\u54cd\uff0c\u6c5f\u6d77\u5929\u597d\u751f\u8be7\u5f02\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u600e\u7684\u8fd9\u4e24\u4e2a\u5987\u9053\u4eba\u5bb6\uff0c \u7adf\u7136\u90fd\u662f\u7528\u9633\u521a\u638c\u529b\uff01\u201d\u8981\u77e5\u5973\u4e86\u4f53\u8d28\u8f83\u5f31\uff0c\u5f88\u5c11\u4ee5\u638c\u529b\u89c1\u957f\uff0c\u7eb5\u6709\u4e60\u638c\u6cd5\u7684\uff0c\u4e5f\u591a\u662f\u504f\u4e8e \u9634\u67d4\u4e00\u8def\uff0c\u6240\u4ee5\u6c5f\u6bcf\u5929\u89c9\u5f97\u5947\u602a\u3002 \u3000\u3000\u534e\u5929\u98ce\u7b11\u9053\uff1a\u201c\u90a3\u8d3c\u5a46\u5a18\u5403\u4e86\u70b9\u4e8f\u4e86\uff0c\u79f0\u542c\u5f97\u51fa\u6765\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u5c11\u5973\u53ea \u9000\u4e86\u4e09\u6b65\uff0c\u800c\u5979\u7684\u5bf9\u624b\u5374\u9000\u4e86\u4e94\u6b65\uff0c\u8fd8\u4f3c\u4e4e\u649e\u7740\u4e86\u4ec0\u4e48\u7269\u4f53\u3002\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u8c37\u5e95\u4e0d\u662f\u77f3\u5934 \u5c31\u662f\u6811\u6728\uff0c\u8fd9\u58f0\u97f3\u4e0d\u662f\u649e\u7740\u6728\u77f3\u7684\u58f0\u97f3\uff0c\u60f3\u5fc5\u662f\u90a3\u7537\u7684\u6276\u7740\u5979\u3002\u201d \u3000\u3000\u679c\u7136\u542c\u5f97\u90a3\u7537\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u662f\u8c01\uff0c\u5feb\u8bf4\u51fa\u6765\uff0c\u4ee5\u514d\u81ea\u8bef\u3002\u201d\u521a\u624d\u9a82\u201c\u8d3c\u5a46\u5a18\u201d\u7684\u90a3\u4e2a \u5c11\u5973\u7684\u58f0\u97f3\u51b7\u7b11\u9053\uff1a\u201c\u6211\u504f\u4e0d\u8bf4\uff0c\u770b\u4f60\u4eec\u80fd\u628a\u6211\u600e\u6837\uff1f\u201d \u3000\u3000\u90a3\u7537\u7684\u9053\uff1a\u201c\u4f60\u522b\u4ee5\u4e3a\u4f60\u7684\u6b66\u529f\u4e86\u5f97\uff0c\u6211\u8fd8\u4e0d\u5c51\u548c\u4f60\u6253\u67b6\u5462\uff01\u597d\u5427\uff0c\u4f60\u4e0d\u62a5\u59d3\u540d\u4e5f\u7f62\uff0c \u6211\u95ee\u4f60\u4eec\uff1b\u4f60\u4eec\u66fe\u5426\u89c1\u5230\u8fd9\u6837\u7684\u4e09\u4e2a\u4eba\uff1a\u4e00\u4e2a\u957f\u80e1\u5b50\u7684\u8001\u5934\uff0c\u53d7\u4e86\u4f24\u7684\uff0c\u8fd8\u6709\u4e00\u65f6\u5341\u516d\u4e03\u5c81 \u5de6\u53f3\u7684\u5c11\u7537\u5c11\u5973\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u6253\u542c\u8fd9\u4e2a\u5e72\u4ec0\u4e48\uff1f\u201d\u90a3\u7537\u7684\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u522b\u591a\u7ba1\u95f2\u4e8b\uff0c\u4f60\u53ea\u8bf4\u6709\u6ca1\u6709 \u89c1\u7740\u4ed6\u4eec\uff0c\u8bf4\u4e86\uff0c\u6211\u5c31\u653e\u4f60\u8fc7\u53bb\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u51b7\u7b11\u9053\uff1a\u201c\u6211\u77a7\u4f60\u4eec\u5c31\u4e0d\u662f\u597d\u4eba\u3001\u662f\u4e0d\u662f\u4f60\u6253\u4f24\u4e86\u90a3\u4e2a\u8001\u5934\uff0c\u8fd8\u60f3\u62a2\u4eba\u5bb6\u7684\u95fa \u5973\u3002\u54fc\uff0c\u6211\u77e5\u9053\u4e5f\u4e0d\u4f1a\u8bf4\u7ed9\u4f60\u542c\uff0c\u8ba9\u4f60\u53bb\u5bb3\u4eba\uff01\u201d \u3000\u3000\u90a3\u7537\u7684\u6012\u559d\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u4e0b\u77a7\u4f60\u662f\u4e2a\u9ec4\u6bdb\u4e2b\u5934\uff0c\u6211\u5c31\u6253\u4f60\u5634\u5df4\uff01\u201d\u88ab\u9a82\u4f5c\u201c\u8d3c\u5a46 \u5a18\u201d\u7684\u90a3\u4e2a\u5973\u4eba\u5c16\u58f0\u7b11\u9053\uff1a\u201c\u53f6\u516c\u5b50\uff0c\u4f60\u8fd8\u771f\u4f1a\u601c\u9999\u60dc\u7389\u5440\uff01\u201d \u3000\u3000\u731b\u542c\u5f97\u4e00\u4e2a\u5c11\u5e74\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u72d7\u5f3a\u76d7\uff0c\u4f60\u6562\u4fae\u8fb1\u6211\u7684\u59b9\u59b9\uff0c\u770b\u5200\uff01\u201d\u8fd9\u5c11\u5e74\u7684\u58f0\u97f3\u8fd8 \u6709\u51e0\u5206\u7ae5\u97f3\uff0c\u542c\u6765\u5e74\u7eaa\u6700\u591a\u4e5f\u4e0d\u8fc7\u662f\u5341\u516d\u4e03\u5c81\u3002\u4f46\u8fc5\u5373\u4f20\u6765\u7684\u4e3b\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u5374\u663e\u5f97\u529b\u9053\u96c4 \u6d51\u975e\u5e38\uff0c\u8fd8\u8fdc\u5728\u4ed6\u7684\u59b9\u59b9\u4e4b\u4e0a\u3002 \u3000\u3000\u534e\u65e0\u98ce\u4f4e\u58f0\u8d5e\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff0c\u597d\u5200\u6cd5\uff01\u201d\u539f\u6765\u5c31\u5728\u90a3\u77ac\u606f\u4e4b\u95f4\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e00\u4e32\u8fde \u73e0\u5bc6\u54cd\uff0c\u6c5f\u6d77\u5929\u5fc3\u4e2d\u5bc6\u6570\uff0c\u53cc\u65b9\u7684\u5175\u5668\u5df2\u5728\u90a3\u77ac\u606f\u4e4b\u95f4\uff0c\u63a5\u89e6\u4e86\u4e03\u4e0b\u3002 \u3000\u3000\u968f\u5373\u542c\u5f97\u5494\u5693\u7684\u5200\u5251\u523a\u51fb\u58f0\uff0c\u547c\u547c\u7684\u638c\u98ce\u58f0\uff0c\u6c5f\u6bcf\u5929\u4f0f\u5730\u542c\u58f0\u7684\u672c\u9886\u672a\u591f\u706b\u5019\uff0c\u5df2\u5206\u522b \u4e0d\u51fa\u662f\u54ea\u4e00\u65b9\u3002 \u3000\u3000\u534e\u5929\u98ce\u51dd\u795e\u7ec6\u542c\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u662f\u90a3\u5bf9\u5e74\u8f7b\u7684\u5144\u59b9\u843d\u4e86\u4e0b\u98ce\u4e86\u3002\u201d\u8bdd \u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u5c11\u5e74\u66b4\u96f7\u4f3c\u7684\u5927\u559d\u4e00\u58f0\uff0c\u4f46\u63a5\u7740\u5374\u662f\u90a3\u201c\u8d3c\u5a46\u5a18\u201d\u7684\u4e00\u58f0\u5c16\u53eb\u3002\u8ddf\u7740\u662f\u90a3\u5c11 \u5e74\u5e26\u7740\u60ca\u7a7a\u7684\u58f0\u97f3\u95ee\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u534e\u4e91\u78a7\u751a\u4e3a\u5947\u602a\uff0c\u5c0f\u58f0\u95ee\u5979\u7236\u4eb2\u9053\uff1a\u201c\u54a6\uff0c\u7a76\u7adf\u662f\u8c01\u53d7\u4e86\u4f24\u4e86\uff1f\u201d\u534e\u5929\u51e4\u9053\uff1a\u201c\u4e24\u4e2a\u5973 \u7684\u90fd\u53d7\u4e86\u4f24\u4e86\uff0c\u8fd9\u5c11\u5e74\u7684\u59b9\u59b9\u4f24\u5f97\u66f4\u91cd\uff0c\u6240\u4ee5\u8fde\u53eb\u4e5f\u53eb\u4e0d\u51fa\u6765\u3002\u90a3\u5c11\u5e74\u60f3\u662f\u56e0\u89c1\u59b9\u59b9\u53d7\u4f24\u624d \u53d1\u72e0\u5c06\u90a3\u8d3c\u5a46\u5a18\u6253\u4f24\u7684\u3002\u201d \u3000\u3000\u679c\u7136\u542c\u5f97\u90a3\u7537\u7684\u5927\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u628a\u6211\u7a46\u5927\u59d0\u4f24\u4e86\u8fd8\u60f3\u8d70\u4e48\uff1f\u201d\u968f\u5373\u542c\u5f97\u201c\u5f53\u201d\u7684 \u4e00\u58f0\uff0c\u63a5\u7740\u53c8\u662f\u95f7\u96f7\u4f3c\u7684\u4e00\u58f0\u58f0\u54cd\u3002\u542c\u5f97\u51fa\u8fd9\u4e24\u4eba\u90fd\u662f\u5251\u638c\u517c\u65bd\uff0c\u8981\u53d6\u5bf9\u65b9\u7684\u6027\u547d\u3002 \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u8fd9\u7537\u7684\u5389\u5bb3\u975e\u5e38\uff0c\u90a3\u5c11\u5e74\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff01\u201d\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u90a3\u201c\u8d3c\u5a46 \u5a18\u201d\u5927\u58f0\u547b\u541f\uff0c\u4f3c\u4e4e\u8bf4\u4e86\u4e00\u53e5\u4ec0\u4e48\u8bdd\uff0c\u5374\u542c\u4e0d\u6e05\u695a\u3002 \u3000\u3000\u90a3\u7537\u7684\u9053\uff1a\u201c\u6765\u5566\uff0c\u6765\u5566\uff01\u201d\u968f\u5373\u542c\u5f97\u201c\u84ec\u3001\u84ec\u201d\u4e24\u58f0\uff0c\u90a3\u7537\u7684\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u8ba9\u4f60 \u591a\u6d3b\u4e24\u5929\uff0c\u5feb\u4e0e\u4f60\u59b9\u59b9\u56de\u5bb6\u7b49\u6b7b\u5427\uff01\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\u4eba\u53ea\u542c\u5f97\u5306\u5306\u5fd9\u5fd9\u5954\u8dd1\u7684\u811a\u6b65\u58f0\uff0c\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u90a3\u5bf9\u5144\u59b9\u5df2\u7ecf\u8dd1\u4e86\u3002\u201d\u518d\u8fc7\u4e00 \u4f1a\uff0c\u90a3\u201c\u8d3c\u5a46\u5a18\u201d\u7684\u547b\u541f\u58f0\u4e5f\u6e10\u53bb\u6e10\u8fdc\uff0c\u534e\u65e0\u98ce\u9053\uff1a\u201c\u8fd9\u59d3\u53f6\u7684\u8d3c\u4eba\u4e5f\u80cc\u4e86\u4ed6\u7684\u90a3\u4e2a\u7a46\u5927\u59d0 \u8d70\u5566\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u90a3\u8d3c\u5a46\u5a18\u6b7b\u4e86\u4e5f\u4e0d\u8db3\u60dc\uff0c\u8fd9\u5bf9\u5144\u59b9\u5374\u662f\u597d\u4eba\u3002\u534e\u8001\u524d\u8f88\uff0c\u542c\u90a3\u59d3\u53f6\u7684\u6076\u8d3c \u53e3\u6c14\uff0c\u8fd9\u5bf9\u5144\u59b9\u4f3c\u4e4e\u53ea\u6709\u51e0\u5929\u53ef\u6d3b\u3002\u53ef\u662f\u771f\u7684\u4e48\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u5ffd\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u4f60\u5e08\u7236\u9664\u4e86\u4f60\u4e4b\u5916\uff0c\u8fd8\u6709\u6ca1\u6709\u53e6\u5916\u6536\u8fc7\u5f92\u5f1f\uff1f\u201d\u6c5f\u6d77\u592d\u751a\u4e3a\u5947\u602a \u9053\uff1a\u201c\u6ca1\u6709\u5440\uff01\u5e72\u7239\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u95ee\u8fd9\u4e2a\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u90a3\u59d3\u53f6\u7684\u6076\u8d3c\u770b\u4ed6\u5e74\u7eaa\u4e0d\u5927\uff0c\u4f46\u638c\u529b\u5374\u662f\u521a\u67d4\u517c\u5907\uff0c\u53d1\u51fa\u7684\u58f0\u97f3\u4e5f\u751a\u4e3a\u602a \u5f02\uff0c\u6211\u6000\u7591\u8fd9\u662f\u4e54\u5317\u6e9f\u6b66\u529f\u79d8\u7c4d\u4e2d\u7684\u5927\u4e58\u822c\u82e5\u638c\uff0c\u6211\u867d\u7136\u6ca1\u6709\u89c1\u8fc7\u4e54\u5317\u6e9f\u7684\u6b66\u529f\u79d8\u7c4d\uff0c\u4f46\u6211 \u5374\u542c\u8bf4\u8fc7\uff0c\u636e\u8bf4\uff0c\u5b5f\u795e\u901a\u5f53\u5e74\u5728\u90ce\u5c71\u4ee5\u53cc\u638c\u5206\u654c\u5c11\u6797\u6d3e\u7684\u75db\u7985\u4e0a\u4eba\u4e0e\u5ce8\u5d4b\u6d3e\u7684\u91d1\u5149\u5927\u5e08\uff0c\u7528 \u7684\u5c31\u662f\u8fd9\u5927\u4e58\u822c\u82e5\u638c\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u5e72\u7239\uff0c\u4f60\u4e5f\u8fd9\u4e48\u8bf4\u3002\u90a3\u4e00\u5b9a\u662f\u4e86\u3002\u6211\u521a\u624d\u542c\u8fd9\u638c\u58f0\uff0c\u4e5f\u89c9\u5f97 \u8be7\u5f02\uff0c\u4f46\u6211\u4f18\u5730\u542c\u58f0\u7684\u672c\u9886\u4e0d\u591f\uff0c\u6240\u4ee5\u8fd8\u4e0d\u6562\u7acb\u5373\u65ad\u5b9a\u5c31\u662f\u5927\u4e58\u822c\u82e5\u638c\u3002\u5509\uff0c\u7cdf\u7cd5\uff0c\u7cdf\u7cd5\uff01 \u5947\u602a\uff0c\u5947\u602a1\u201d \u3000\u3000\u534e\u4e91\u78a7\u7b11\u9053\uff1a\u201c\u7cdf\u7cd5\u4ec0\u4e48\uff1f\u5947\u602a\u4ec0\u4e48\uff1f\u4f60\u5feb\u70b9\u8bf4\u51fa\u6765\u5440\uff01\u6025\u715e\u6211\u4e86\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5927\u4e58\u822c\u82e5\u638c\u662f\u4e00\u79cd\u6b63\u90aa\u5408\u4e00\u7684\u638c\u529b\uff0c\u80fd\u4f24\u5bf9\u65b9\u7684\u5947\u7ecf\u516b\u8109\uff0c\u4e54\u5317\u6e9f\u6b66\u529f\u79d8 \u7c4d\u4e2d\u6709\u4e03\u79cd\u5389\u5bb3\u7684\u795e\u638c\u529f\u592b\uff0c\u8fd9\u5927\u4e58\u822c\u82e5\u638c\u4ec5\u6b21\u4e8e\u4fee\u7f57\u9634\u715e\u529f\uff01\u4e2d\u638c\u4e4b\u540e\uff0c\u6c57\u6d41\u4e0d\u6b62\uff0c\u8fdf\u5219 \u4e03\u65e5\uff0c\u5c11\u5219\u4e09\u65e5\uff0c\u5b9a\u5fc5\u5f62\u9500\u9aa8\u7acb\uff0c\u8eab\u4f53\u5e72\u67af\u800c\u4ea1\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u60ca\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u8fd9\u5bf9\u5144\u59b9\u5c82\u975e\u4e0d\u80fd\u6d3b\u4e86\uff0c\u5f53\u771f\u662f\u7cdf\u7cd5\u5f97\u5f88\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5947\u602a\u7684\u662f\uff0c\u8fd9\u6076\u8d3c\u600e\u4f1a\u61c2\u5f97\u8fd9\u79cd\u529f\u592b\uff1f\u636e\u6211\u6240\u77e5\u3002\u4e54\u5317\u6e9f\u7684\u6b66\u529f\u79d8\u7c4d\uff0c\u9664 \u4e86\u5389\u80dc\u7537\u548c\u6211\u5e08\u7236\u5f97\u7aa5\u5168\u8c79\u4e4b\u5916\uff0c\u8fd8\u6709\u5929\u9b54\u6559\u7684\u526f\u6559\u4e3b\u4e5f\u83b7\u5f97\u4e00\u9cde\u534a\u722a\uff0c\u4f46\u4ed6\u4eec\u90fd\u672a\u66fe\u7ec3\u6210 \u5927\u4e58\u822c\u82e5\u638c\u3002\u751a\u81f3\u6211\u5e08\u7236\u4ea6\u53ea\u61c2\u5f97\u8fd9\u95e8\u529f\u592b\uff0c\u4ed6\u81ea\u5df1\u4e5f\u6ca1\u6709\u7ec3\u3002\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u5e08\u7236\u5e38\u8bf4\uff0c\u6b66\u5b66\u4e4b\u9053\uff0c\u867d\u4e0d\u59a8\u91c7\u7eb3\u6b63\u90aa\u5404\u6d3e\u4e4b\u957f\uff0c\u4f46\u603b\u5e94\u4ee5\u5149\u660e\u6b63\u5927\u4e3a \u4e3b\uff0c\u592a\u8fc7\u9634\u6bd2\u7684\u90aa\u6d3e\u529f\u592b\uff0c\u7ec3\u4e86\u4e0d\u4f46\u4f1a\u5bf9\u672c\u8eab\u6709\u5bb3\uff0c\u800c\u4e14\u4f1a\u4f7f\u7ec3\u7684\u4eba\u5fc3\u672f\u4e0d\u6b63\uff0c\u6545\u6b64\u4e0d\u5b9c\u591a \u7ec3\u3002\u53ea\u8981\u61c2\u5f97\u5e94\u4ed8\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u9053\uff1a\u201c\u7136\u5219\u4f60\u53ef\u4ee5\u5e94\u4ed8\u5f97\u4e86\u5927\u4e58\u822c\u82e5\u638c\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u8c0a\uff1a\u201c\u4f3c\u90a3\u59d3\u53f6\u7684\u6076\u8d3c\uff0c\u4ed6 \u7684\u5927\u4e58\u822c\u82e5\u638c\u5927\u7ea6\u53ea\u6709\u4e09\u6210\u706b\u5019\uff0c\u4f30\u91cf\u6211\u8fd8\u53ef\u4ee5\u5e94\u4ed8\u3002\u82e5\u4ed6\u7ec3\u5230\u4e94\u6210\uff0c\u6211\u5c31\u4e0d\u6562\u8bf4\u4e86\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u9053\uff1a\u201c\u54b1\u4eec\u53ef\u4ee5\u8d70\u4e86\u5427\uff1f\u54a6\uff0c\u7239\u7239\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d\u539f\u6765\u534e\u5929\u98ce\u6b63\u5728\u95ed\u76ee\u51dd\u601d\uff0c\u542c \u4e86\u5973\u513f\u7684\u8bdd\uff0c\u624d\u5f20\u5f00\u773c\u775b\u8bf4\u9053\uff1a\u201c\u6211\u6b63\u5728\u60f3\uff0c\u5018\u82e5\u662f\u90a3\u5bf9\u5144\u59b9\u5411\u6211\u6c42\u533b\uff0c\u6211\u8be5\u5982\u4f55\u533b\u6cbb\uff1f\u201d \u534e\u4e91\u78a7\u9053\uff1a\u201c\u60f3\u51fa\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u6447\u5934\u9053\uff1a\u201c\u5947\u7ecf\u516b\u8109\u53d7\u4f24\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u8fd8\u672a\u60f3\u51fa\u5207\u5b9e\u53ef\u884c\u7684\u7597\u6cd5\u3002\u201d\u4ed6\u8bf4\u8bdd\u4e4b \u65f6\uff0c\u6c14\u5598\u5401\u5401\uff0c\u8138\u8272\u53c8\u89c1\u7070\u767d\uff0c\u75b2\u6001\u6bd5\u9732\u3002\u534e\u4e91\u78a7\u9053\uff1a\u201c\u7239\uff0c\u4f60\u5c31\u522b\u518d\u7528\u5fc3\u601d\u4e86\u5427\uff01\u4f55\u51b5\u54b1 \u4eec\u53c8\u4e0d\u77e5\u9053\u4ed6\u4eec\u662f\u4f55\u65b9\u4eba\u6c0f\uff0c\u4f60\u5c31\u662f\u60f3\u51fa\u4e86\u6cd5\u5b50\uff0c\u4e5f\u65e0\u4ece\u53bb\u627e\u4ed6\u4eec\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u4f60\u6240\u8bf4\u7684\u6211\u4f55\u5c1d\u4e0d\u77e5\uff0c\u4f46\u6211\u7684\u813e\u6c14\u5374\u662f\u4e00\u78b0\u5230\u533b\u5b66\u7684\u96be\u9898\uff0c\u5c31\u975e\u5f97\u7528\u5fc3\u601d \u7d22\u4e0d\u53ef\u3002\u6b63\u5982\u5b66\u6b66\u7684\u4eba\uff0c\u78b0\u5230\u4e86\u4e00\u62db\u602a\u62db\uff0c\u5c31\u5fc5\u5b9a\u8981\u51fa\u4e00\u4e2a\u62db\u6765\u7834\u5b83\u3002\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u90a3 \u4e48\uff0c\u4f60\u5c31\u7b49\u7cbe\u795e\u597d\u4e86\u4e00\u4e9b\u518d\u60f3\u5427\u3002\u6015\u53ea\u6015\u4f60\u7528\u5fc3\u8fc7\u5ea6\uff0c\u5bf9\u4eba\u65e0\u52a9\u3002\u5bf9\u81ea\u5df1\u7684\u75c5\u4f53\u53cd\u800c\u6709\u635f \u4e86\u3002\u201d\u534e\u5929\u98ce\u9053\uff0c\u201c\u4f60\u4e5f\u8bf4\u5f97\u662f\uff0c\u90a3\u4e48\u54b1\u4eec\u5c31\u8d70\u5427\u3002\u90a3\u4e24\u4e2a\u5f3a\u76d7\uff0c\u8fd9\u65f6\u5019\u5927\u7ea6\u4e5f\u65e9\u5df2\u4e0b\u4e86\u5c71 \u4e86\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u867d\u7136\u529d\u7236\u4eb2\u522b\u7528\u5fc3\u601d\uff0c\u5176\u5b9e\u5979\u5bf9\u90a3\u5bf9\u5144\u59b9\uff0c\u5374\u662f\u5341\u5206\u60cb\u60dc\u4e0e\u540c\u60c5\uff1a\u6c5f\u6d77\u5929\u7684\u5fc3\u60c5 \u4e5f\u6b63\u548c\u5979\u4e00\u6837\u3002\u4ed6\u4eec\u867d\u7136\u4e0d\u8ba4\u8bc6\u90a3\u5bf9\u5144\u59b9\uff0c\u4f46\u60f3\u5230\u4ed6\u4eec\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u51e0\u5929\u540e\u5c31\u8981\u79bb\u5f00\u4eba\u4e16\uff1a\u90fd \u89c9\u5f97\u5341\u5206\u96be\u8fc7\u3002 \u3000\u3000\u8fd9\u65f6\u65e5\u5f71\u5df2\u6e10\u5411\u897f\u79fb\uff0c\u8c37\u5e95\u4e00\u7247\u9634\u6c89\uff0c\u6c5f\u6d77\u5929\u80cc\u7740\u534e\u5929\u98ce\u8d70\u8fc7\u521a\u624d\u6076\u6218\u7684\u5904\u6240\uff0c\u53ea\u89c1\u904d \u5730\u90fd\u662f\u788e\u77f3\uff0c\u5728\u51e0\u5757\u51f8\u51fa\u6765\u5ca9\u77f3\u4e0a\uff0c\u8fd8\u53ef\u4ee5\u770b\u5f97\u51fa\u5251\u524a\u638c\u5288\u7684\u75d5\u8ff9\uff0c\u5730\u4e0a\u6709\u70b9\u70b9\u8840\u6e0d\u3002\u5929\u4e0a \u6709\u51e0\u53ea\u4e0d\u77e5\u540d\u7684\u731b\u79bd\u98de\u6765\u98de\u53bb\uff0c\u60f3\u662f\u5b83\u4eec\u95fb\u5230\u4e86\u5730\u4e0a\u7684\u8840\u8165\uff0c\u4ee5\u4e3a\u6709\u5c38\u4f53\u53ef\u4ee5\u4f9b\u5b83\u4eec\u5544\u4ea1\uff0c \u8fd9\u666f\u8c61\u89e6\u76ee\u60ca\u5fc3\u3002\u53ef\u4ee5\u60f3\u89c1\u521a\u624d\u8fd9\u4e00\u573a\u6076\u6218\u662f\u4f55\u7b49\u6fc0\u70c8\uff01 \u3000\u3000\u90a3\u51e0\u53ea\u98df\u8089\u9e1f\u76d8\u65cb\u4f4e\u98de\uff0c\u51e0\u4e4e\u5c31\u8981\u6293\u5230\u4ed6\u4eec\u5934\u4e0a\uff0c\u534e\u4e91\u78a7\u611f\u5230\u6076\u5fc3\uff0c\u968f\u5b50\u62fe\u8d77\u51e0\u9897\u5c0f\u77f3 \u5b50\u60f3\u628a\u5b83\u4eec\u8d76\u8d70\uff0c\u54ea\u77e5\u77f3\u5b50\u4e00\u634f\u5230\u624b\u5fc3\uff0c\u4fbf\u5373\u788e\u6210\u7c89\u672a\uff0c\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u662f\u88ab\u5927\u4e58\u822c\u82e5\u638c\u9707\u88c2 \u7684\u788e\u77f3\uff0c\u4e0d\u80fd\u7528\u7684\u4e86\u3002\u201d\u534e\u4e91\u78a7\u4e0d\u7981\u9a87\u7136\u8bf4\u9053\uff1a\u201c\u8fd9\u6076\u8d3c\u53ea\u6709\u4e09\u6210\u706b\u5019\uff0c\u5df2\u8fd9\u822c\u5389\u5bb3\uff0c\u5018\u82e5 \u88ab\u4ed6\u7ec3\u5230\u529f\u884c\u5706\u6ee1\uff0c\u90a3\u8fd8\u4e86\u5f97\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u811a\u5c16\u4e00\u6311\uff0c\u5c06\u4e24\u9897\u77f3\u5b50\u8e22\u8d77\uff1a\u6070\u6070\u843d\u5728\u4ed6\u7684\u624b\u5fc3\uff0c\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u5927\u7ea6\u662f\u7ed9\u5927\u529b \u91d1\u521a\u638c\u5288\u88c2\u7684\uff0c\u8fd8\u53ef\u4ee5\u7528\uff0c\u201d\u679c\u7136\u77f3\u4e86\u53d1\u51fa\uff0c\u5e26\u7740\u5f3a\u52b2\u7684\u7834\u7a7a\u4e4b\u58f0\uff0c\u5c06\u98de\u5f97\u6700\u4f4e\u7684\u90a3\u53ea\u731b\u79bd \u6253\u5f97\u7fbd\u6bdb\u7eb7\u98de\uff0c\u53e6\u5916\u90a3\u51e0\u53ea\u4f3c\u662f\u8bc6\u5f97\u5389\u5bb3\uff0c\u4e5f\u90fd\u98de\u8d70\u4e86\u3002 \u3000\u3000\u5f53\u6c5f\u6d77\u5929\u8bf4\u5230\u201c\u5927\u529b\u91d1\u521a\u638c\u201d\u8fd9\u4e94\u4e2a\u5b57\u7684\u65f6\u5019\uff0c\u534e\u5929\u98ce\u500f\u5730\u5f20\u5f00\u773c\u775b\uff0c\u795e\u8272\u4e5f\u4f3c\u4e4e\u52a8\u4e86 \u4e00\u4e0b\u3002\u4f46\u6c5f\u6bcf\u5929\u662f\u80cc\u8d1f\u7740\u4ed6\uff0c\u5374\u6ca1\u6709\u77a7\u89c1\u4ed6\u7684\u8138\u8272\uff1a\u534e\u4e91\u78a7\u6015\u5979\u7236\u4eb2\u53c8\u7528\u5fc3\u601d\uff0c\u4e0d\u60f3\u4e0e\u4ed6\u518d \u8c08\u6b66\u5b66\u4e0a\u6216\u533b\u5b66\u4e0a\u7684\u8bdd\u9898\uff0c\u56e0\u6b64\u4e5f\u6ca1\u6709\u52a8\u95ee\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u4f1a\uff0c\u534e\u5929\u98ce\u5ffd\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u8ba9\u78a7\u513f\u80cc\u6211\u5427\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5e72\u7239\uff0c\u6211\u4e0d\u7d2f\u3002\u201d\u534e \u4e91\u78a7\u7b11\u9053\uff1a\u201c\u6d77\u54e5\u5df2\u62dc\u4f60\u4f5c\u4e49\u7236\uff0c\u4f60\u8fd8\u53eb\u4ed6\u8d24\u4f84\uff1f\u201d\u534e\u5929\u98ce\u7b11\u9053\uff1a\u201c\u53eb\u60ef\u4e86\u4e00\u65f6\u6539\u4e0d\u4e86 \u53e3\u3002\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u6d77\u54e5\uff0c\u4f60\u8ba4\u4e86\u4e49\u7236\uff0c\u7239\u8fd8\u6ca1\u6709\u89c1\u9762\u793c\u7ed9\u4f60\uff0c\u5c31\u8981\u4f60\u8fd9\u822c\u51fa\u529b\uff0c\u8bf4\u771f\u7684\uff0c \u4f60\u7eb5\u4e0d\u7d2f\uff0c\u6211\u4e5f\u4e0d\u8be5\u504f\u52b3\u4f60\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u65e2\u4e0d\u60ef\u5ba2\u5957\u3002\u53c8\u4e0d\u597d\u548c\u5979\u4e89\uff0c\u53ea\u597d\u5c06\u534e\u5929\u98ce\u4ea4\u7ed9\u5979 \u80cc\u3002 \u3000\u3000\u534e\u4e91\u78a7\u9053\uff1a\u201c\u7239\uff0c\u4f60\u53c8\u5728\u60f3\u4ec0\u4e48\u4e86\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u8fd9\u91cc\u662f\u5e73\u5730\uff0c\u4f60\u53ef\u4ee5\u8d70\u5feb \u4e00\u4e9b\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u4eba\u5927\u7b11\u9053\uff1a\u201c\u5feb\u8d70\u6162\u8d70\u90fd\u6ca1\u6709\u7528\uff0c\u53cd\u6b63\u662f\u8dd1\u4e0d\u4e86\uff01\u5582\uff0c\u4f60\u662f \u6c5f\u6d77\u5929\u5417\uff1f\u201d\u4e71\u77f3\u4e1b\u4e2d\u8df3\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u6b63\u662f\u88ab\u90a3\u5973\u8d3c\u79f0\u4f5c\u201c\u53f6\u516c\u5b50\u201d\u7684\u90a3\u4e2a\u4eba\u3002\u539f\u6765\u4ed6\u5c06\u53d7 \u4f24\u7684\u540c\u4f34\u5b89\u7f6e\u597d\u540e\uff0c\u53c8\u56de\u6765\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u521a\u624d\u53ea\u89c1\u4ed6\u7684\u80cc\u5f71\uff0c\u53ea\u9053\u4ed6\u662f\u4e2a\u51f6\u795e\u6076\u715e\u7684\u5f3a\u76d7\uff0c\u73b0\u5728\u4e00\u6253\u7167\u9762\uff0c\u5374\u4e0d\u7531\u5f97\u5403\u4e86 \u4e00\u60ca\uff0c\u4e0d\u662f\u56e0\u4e3a\u4ed6\u76f8\u8c8c\u51f6\u6076\uff0c\u6070\u6070\u76f8\u53cd\uff0c\u8fd9\u4eba\u4e00\u8868\u65af\u6587\uff0c\u7709\u6e05\u76ee\u79c0\u54ea\u91cc\u50cf\u4e2a\u5f3a\u76d7\uff0c\u7adf\u662f\u4e2a\u6d4a \u4e16\u4f73\u516c\u5b50\uff01\u8fd9\u8fd8\u4e0d\u7b97\u5947\u602a\uff0c\u66f4\u5947\u602a\u7684\u662f\u6c5f\u6d77\u5929\u548c\u4ed6\u4e00\u6253\u7167\u9762\uff0c\u4fbf\u89c9\u788d\u8fd9\u4eba\u4f3c\u66fe\u76f8\u8bc6\uff0c\u5728\u8fd9\u5239 \u90a3\u4e4b\u95f4\uff0c\u6c5f\u6d77\u5929\u7adf\u662f\u83ab\u540d\u5176\u5999\u7684\u5bf9\u4ed6\u53d1\u751f\u4e86\u597d\u611f\u3002 \u3000\u3000\u53ef\u662f\u5f53\u6c5f\u6d77\u5929\u4e00\u60f3\u8d77\u8fd9\u4eba\u5c31\u662f\u7528\u9634\u72e0\u638c\u529b\u4f24\u5bb3\u90a3\u65f6\u5144\u59b9\u7684\u51f6\u624b\uff0c\u73b0\u5728\u53c8\u8981\u4f24\u5bb3\u4ed6\u548c\u534e\u5929 \u98ce\u7684\u65f6\u5019\uff0c\u597d\u611f\u8fc5\u5373\u6d88\u5931\uff0c\u6012\u58f0\u7b54\u9053\uff1a\u201c\u5927\u4e08\u592b\u884c\u4e0d\u66f4\u540d\uff0c\u5750\u4e0d\u6539\u59d3\u3002\u4e0d\u9519\uff0c\u6c5f\u6d77\u5929\u5c31\u662f \u6211\u3002\u6211\u5c31\u662f\u6c5f\u6d77\u5929\uff0c\u4f60\u5f85\u600e\u4e48\uff1f\u201d\u6b63\u662f\u3002 \u3000\u3000\u964c\u8def\u76f8\u9022\u7591\u662f\u68a6\uff0c\u4f3c\u66fe\u76f8\u8bc6\u662f\u4f55\u4eba\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u56de\u3000\u671b\u95e8\u6295\u6b62\u60ca\u5947\u53d8\u3000\u6708\u73b0\u4e91\u5f00\u8bc6\u8be1\u8c0b \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e8c\u5341\u56de\u3000\u671b\u95e8\u6295\u6b62\u60ca\u5947\u53d8\u3000\u6708\u73b0\u4e91\u5f00\u8bc6\u8be1\u8c0b \u3000\u3000\u90a3\u201c\u53f6\u516c\u5b50\u201d\u9053\uff0c\u201c\u6211\u73b0\u5728\u6ca1\u6709\u529f\u592b\u548c\u4f60\u4eec\u591a\u8bf4\uff0c\u4e24\u4ef6\u4e8b\u60c5\uff0c\u4f60\u4eec\u4ed4\u7ec6\u542c\u7740\uff0c\u7b2c\u4e00\u4ef6\uff1b \u6c5f\u6d77\u5929\u4f60\u73b0\u5728\u9a6c\u4e0a\u5f97\u8ddf\u6211\u8d70\uff0c\u4f60\u4e0d\u5fc5\u5bb3\u6015\uff0c\u6211\u613f\u5c06\u4f60\u5f53\u4f5c\u5ba2\u4eba\uff1b\u7b2c\u4e8c\u4ef6\uff0c\u534e\u5929\u98ce\uff0c\u6211\u77e5\u9053\u4f60 \u8eab\u4e0a\u6709\u5c0f\u8fd8\u4e39\uff0c\u6211\u53ea\u8981\u4f60\u4e00\u9897\uff0c\u4f60\u5feb\u7ed9\u6211\u3002 \u3000\u3000\u534e\u4e91\u78a7\u751f\u6015\u8fd9\u4eba\u4f24\u5bb3\u5979\u7684\u7236\u4eb2\uff0c\u6839\u672c\u5c31\u672a\u66fe\u5c06\u4ed6\u7684\u8bdd\u8bed\u542c\u8fdb\u8033\u6735\uff0c\u8f6c\u8eab\u4fbf\u8dd1\uff0c\u4e00\u9762\u8dd1\u4e00 \u9762\u53eb\u9053\uff1a\u201c\u6d77\u54e5\uff0c\u4f60\u8fd8\u4e0d\u4eae\u5251\uff01\u201d \u3000\u3000\u90a3\u201c\u53f6\u516c\u5b50\u201d\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u656c\u9152\u4e0d\u5403\u8981\u5403\u7f5a\u9152\u4e48\uff1f\u201d\u811a\u5c16\u4e00\u70b9\uff0c\u8eab\u4f3c\u79bb\u5f26\u4e4b\u7bad\uff0c\u500f\u5730 \u8ffd\u5230\u4e86\u534e\u4e91\u78a7\u80cc\u540e\uff0c\u6c5f\u6d77\u5929\u5927\u6012\u559d\u9053\uff1a\u201c\u6076\u8d3c\u4f11\u5f97\u884c\u51f6\uff01\u201d\u5c55\u5f00\u516b\u6b65\u8d76\u8749\u529f\u592b\uff0c\u51b2\u4e0a\u53bb\u5c31\u662f \u4e00\u638c\uff01 \u3000\u3000\u90a3\u201c\u53f6\u516c\u5b50\u201d\u53cd\u624b\u4e00\u62cd\uff0c\u8bf4\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u884c\u51f6\uff1f\u6211\u662f\u8bda\u5fc3\u8bf7\u5ba2\uff0c\u8bda\u5fc3\u6c42\u836f\uff01\u201d\u6c5f\u6d77\u5929\u6015 \u4ed6\u7528\u5927\u4e58\u822c\u82e5\u638c\uff0c\u4e00\u51fa\u624b\u4fbf\u662f\u4e13\u7834\u5185\u5bb6\u771f\u6c14\u7684\u4e00\u6307\u7985\u529f\u3002 \u3000\u3000\u90a3\u201c\u53f6\u516c\u5b50\u201d\u9762\u8272\u9661\u53d8\uff0c\u559d\u9053\uff1a\u201c\u6c5f\u62c7\u5929\uff0c\u4f60\u597d\u72e0\uff0c\u4f60\u8fd9\u624d\u662f\u884c\u51f6\uff01\u201d\u6c5f\u6d77\u5929\u521a\u53d1\u89c9\u4ed6 \u7684\u638c\u529b\u4e0d\u662f\u5927\u4e58\u822c\u82e5\u638c\uff0c\u5bf9\u65b9\u5df2\u500f\u5730\u53d8\u62db\uff0c\u53cc\u638c\u9f50\u53d1\uff0c\u7b2c\u4e00\u638c\u6765\u5f97\u65e0\u58f0\u65e0\u606f\uff0c\u7d27\u63a5\u7740\u7684\u7b2c\u4e8c \u638c\u5374\u53d1\u51fa\u95f7\u96f7\u4f3c\u7684\u58f0\u54cd\u3002\u7b2c\u4e00\u638c\u662f\u7ef5\u638c\uff0c\u7b2c\u4e8c\u638c\u624d\u786e\u5b9e\u662f\u5927\u4e58\u822c\u82e5\u638c\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5168\u795e\u8d2f\u6ce8\u7684\u662f\u4ed6\u7684\u5927\u4e58\u822c\u82e5\u638c\uff0c\u51b7\u4e0d\u9632\u5374\u5148\u7ed9\u4ed6\u7684\u7ef5\u638c\u51fb\u4e2d\uff0c\u8fd9\u7ef5\u638c\u7528\u7684\u662f\u9634\u67d4 \u638c\u529b\uff0c\u529f\u592b\u6df1\u65f6\uff0c\u53ef\u4ee5\u788e\u77f3\u5982\u7c89\uff0c\u5e78\u4e8f\u6c5f\u6d77\u5929\u8eab\u7a7f\u5b9d\u7532\uff0c\u53c8\u6709\u62a4\u4f53\u795e\u529f\uff0c\u4e0d\u81f4\u53d7\u4f24\uff0c\u4f46\u7ed9\u4ed6 \u6253\u4e86\u4e00\u638c\uff0c\u4e5f\u5f97\u8f6c\u4e86\u4e00\u5704\uff0c\u624d\u6d88\u89e3\u4e86\u8eab\u53d7\u7684\u90a3\u80a1\u529b\u9053\u3002\u53ef\u662f\u8fd9\u5c11\u5e74\u4e5f\u6ca1\u4fbf\u5b9c\uff0c\u9976\u662f\u4ed6\u53d8\u62db\u5f97 \u5feb\u3002\u6c5f\u6d77\u5929\u90a3\u4e00\u6307\u4e5f\u6cbe\u7740\u4e86\u4ed6\u7684\u76ae\u8089\uff0c\u5c06\u4ed6\u8feb\u9000\u4e86\u51e0\u6b65\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8eab\u5f62\u672a\u7a33\uff0c\u90a3\u201c\u53f6\u516c\u5b50\u201d\u5df2\u7136\u53c8\u5230\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6c5f\u6d77\u5929\uff0c\u4f60\u771f\u662f\u4e0d\u77e5\u597d\u6b79\uff0c\u6211 \u8bf7\u4f60\u4e0d\u52a8\uff0c\u53ef\u5c31\u8981\u6293\u4f60\u53bb\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u5927\u6012\u9053\uff1a\u201c\u4f60\u6709\u672c\u9886\u5c31\u8bd5\u8bd5\u770b\uff0c\u8c01\u5e0c\u7f55\u505a\u4f60\u7684\u5ba2\u4eba\u3002\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u5de6\u8db3\u98de\u8d77\u8e22\u6c5f\u6d77\u5929\u819d\u76d6\u201c\u73af\u8df3\u7a74\u201d\uff0c\u53f3\u638c\u5288\u4ed6\u808b\u9aa8\uff0c\u5de6\u81c2\u4e00 \u5f2f\uff0c\u4e00\u4e2a\u201c\u8098\u9524\u201d\u53c8\u649e\u4ed6\u524d\u5fc3\uff0c\u8fd9\u4e00\u62db\u4e4f\u5f0f\uff0c\u6709\u5982\u5954\u96f7\u9ab8\u7535\uff0c\u8fc5\u731b\u7edd\u4f26\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6025\u7528\u201c\u5929\u7f57\u6b65\u6cd5\u201d\u95ea\u907f\uff0c\u4f46\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u524d\u80f8\u5df2\u7ed9\u5bf9\u65b9\u201c\u8098\u9524\u201d\u649e\u4e2d\uff0c\u867d \u6709\u62a4\u4f53\u795e\u529f\uff0c\u4e5f\u89c9\u808b\u9aa8\u9690\u9690\u4f5c\u75db\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u6211\u4e0d\u51fa\u6740\u624b\uff0c\u53ea\u6015\u5bf9\u4ed8\u4e0d\u4e86\u8fd9\u4e2a\u6076\u8d3c\u3002\u201d\u5530\u7684\u4e00\u58f0\uff0c\u5b9d\u5251\u51fa\u9798\uff0c\u90a3\u5c11\u5e74 \u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u6709\u5b9d\u5251\uff0c\u5b9d\u5251\u53c8\u4f55\u8db3\u60e7\uff1f\u201d\u6c5f\u6bcf\u5929\u4f7f\u4e86\u4e00\u62db\u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\uff0c\u6a2a\u524a\u8fc7 \u53bb\uff0c\u6c5f\u6d77\u5929\u7a76\u7adf\u662f\u5fc3\u5730\u7eaf\u539a\uff0c\u867d\u8bf4\u51b3\u610f\u65bd\u5c55\u6740\u624b\uff0c\u4f46\u8fd9\u4e00\u5251\u5374\u8fd8\u4e0d\u60f3\u53d6\u4ed6\u6027\u547d\uff1b\u53ea\u662f\u60f3\u524a\u65ad \u4ed6\u7684\u624b\u8155\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u559d\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u770b\u5251\uff01\u201d\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u957f\u5251\u4ea6\u5df1\u500f\u5730\u51fa\u9798\uff0c\u4f46\u89c1\u4ed6 \u4e00\u4e2a\u62d7\u6b65\u56de\u8eab\uff1b\u4e0d\u4f46\u907f\u5f00\u4e86\u6c5f\u6d77\u5929\u90a3\u4e00\u62db\u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\u3001\u800c\u4e14\u53cd\u5708\u5230\u6c5f\u6d77\u5929\u80cc\u540e\uff0c\u5bd2\u5149\u95ea \u5904\uff0c\u4e00\u62db\u201c\u674e\u5e7f\u5c04\u77f3\u201d\uff0c\u4e3b\u5ba2\u6613\u4f4d\uff0c\u5f84\u523a\u6c5f\u6d77\u5929\u80a9\u540e\u7684\u201c\u98ce\u5e9c\u7a74\u201d\uff01 \u3000\u3000\u534e\u4e91\u78a7\u7981\u4e0d\u4f4f\u5931\u58f0\u60ca\u53eb\uff0c\u6c5f\u6d77\u5929\u53f5\u624b\u4e00\u5251\uff0c\u90a3\u5c11\u5e74\u8bc6\u5f97\u4ed6\u8fd9\u5b9d\u5251\u7684\u5389\u5bb3\uff0c\u4e00\u4e2a\u201c\u5927\u5f2f \u8170\uff0c\u659c\u63d2\u67f3\u201d\u3001\u53c8\u5df2\u79fb\u5f62\u6362\u4f4d\u3001\u5251\u950b\u524a\u5230\u6c5f\u6d77\u5929\u7684\u819d\u76d6\uff0c\u6c5f\u6d77\u5929\u5251\u950b\u53cd\u5c55\uff0c\u7528\u4e86\u4e00\u62db\u201c\u62e8\u4e91 \u89c1\u65e5\u201d\uff0c\u5c06\u4ed6\u7684\u62db\u6570\u7834\u4e86\u3002\u4e24\u4eba\u52a8\u4f5c\u90fd\u662f\u5feb\u5982\u95ea\u7535\uff0c\u4e00\u6cbe\u5373\u5206\uff0c\u4e00\u89e6\u5373\u9000\uff0c\u53cc\u65b9\u7684\u5251\u62db\u90fd\u672a \u66fe\u7528\u5b9e\uff0c\u4f46\u5374\u53c8\u90fd\u662f\u53ef\u865a\u53ef\u5b9e\uff0c\u53ea\u8981\u54ea\u4e00\u65b9\u7a0d\u6709\u758f\u795e\uff0c\u4fbf\u7acb\u5373\u6709\u8840\u6e85\u9ec4\u6c99\u4e4b\u9669\u3002\u5c31\u5728\u8fd9\u7247\u523b \u4e4b\u95f4\uff0c\u534e\u4e91\u78a7\u7684\u53eb\u58f0\u72b9\u81ea\u4f59\u97f3\u672a\u4e86\uff0c\u4ed6\u4eec\u5df1\u4ea4\u6362\u4e86\u4e03\u516b\u62db\u4e0a\u4e58\u5251\u6cd5\uff0c\u6bcf\u4e00\u62db\u90fd\u53ef\u53d6\u5bf9\u65b9\u6027\u547d\u3002 \u3000\u3000\u5230\u4e86\u8fd9\u65f6\uff0c\u6c5f\u6d77\u5929\u5df2\u4f7f\u51fa\u6d51\u8eab\u672c\u9886\uff0c\u4f46\u53ef\u60dc\u4ed6\u5728\u8d77\u624b\u7b2c\u4e00\u5251\u4e4b\u65f6\uff0c\u8feb\u5f97\u4e0d\u7d27\uff0c\u7ed9\u654c\u4eba\u53cd \u5236\u673a\u5148\uff0c\u518d\u8981\u6273\u6210\u5e73\u624b\uff0c\u5c31\u5f97\u4eba\u8d39\u6c14\u529b\u3002\u518d\u52a0\u4ee5\u8fd9\u5c11\u5e74\u7684\u4e34\u654c\u7ecf\u9a8c\u5341\u5206\u4e30\u5bcc\uff0c\u800c\u6c5f\u6d77\u5929\u5374\u8fd8 \u662f\u7b2c\u4e00\u6b21\u4e0e\u4eba\u6b63\u5f0f\u6bd4\u5251\uff0c\u56e0\u6b64\u6c5f\u6d77\u5929\u867d\u6709\u4e00\u67c4\u5b9d\u5251\uff0c\u76f8\u5f62\u4e4b\u4e0b\uff0c\u4ecd\u662f\u5403\u4e8f\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u5ffd\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u5c11\u5e74\u7684\u5251\u5203\u51e0\u4e4e\u662f\u8d34\u7740\u6c5f\u6d77\u5929\u7684\u5251\u810a\u201c\u6ed1\u201d\u5c06\u8fc7\u6765\uff0c\u6c5f \u6d77\u5929\u4ece\u672a\u89c1\u8fc7\u8fd9\u6837\u7684\u6253\u6cd5\uff0c\u51b7\u4e0d\u9632\u7ed9\u4ed6\u524a\u53bb\u4e86\u4e00\u6bb5\u8896\u53e3\uff0c\u9669\u9669\u4f24\u53ca\u76ae\u8089\u3002\u6c5f\u6d77\u5929\u4e00\u638c\u51fb\u4e0b\uff0c \u90a3\u5c11\u5e74\u53c8\u65e9\u5df2\u6599\u5b9a\u4ed6\u6709\u6b64\u4e00\u7740\uff0c\u53cd\u624b\u6233\u4ed6\u8155\u8109\uff0c\u201c\u5693\u201d\u7684\u4e00\u4e0b\uff0c\u6c5f\u6d77\u5929\u864e\u53e3\u7ed9\u4ed6\u638c\u7f18\u524a\u4e2d\uff0c \u6bd4\u8d77\u521a\u624d\u63a5\u90a3\u4e00\u62db\u5251\u62db\uff0c\u5403\u4e8f\u66f4\u5927\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5ffd\u5fd9\u53eb\u9053\uff1a\u201c\u78a7\u59b9\uff0c\u4f60\u548c\u7239\u7239\u5feb\u8d70\uff01\u201d\u5374\u4e0d\u6599\u534e\u4e91\u78a7\u53cd\u800c\u505c\u4e0b\u4e86\u811a\u6b65\uff0c\u5c31\u5728\u8fd9 \u65f6\uff0c\u53ea\u542c\u5f97\u534e\u5929\u98ce\u56b7\u9053\uff1a\u201c\u8d70\u65e0\u5984\uff0c\u8fdb\u5f52\u5bb9\uff01\u201d\u4e0a\u4e58\u6b66\u5b66\u4e2d\uff0c\u8bb2\u5230\u6700\u590d\u6742\u7684\u8eab\u5f62\uff0c\u4e43\u662f\u7528\u6613 \u7ecf\u516b\u516b\u516d\u5341\u56db\u5366\u7684\u65b9\u4f4d\u6765\u6392\u5b9a\u7684\uff0c\u65e0\u5984\u3001\u5f52\u5bb9\u90fd\u662f\u516d\u5341\u56db\u5366\u65b9\u4f4d\u4e4b\u4e00\uff0c\u6c5f\u6d77\u5929\u58f0\u5165\u5fc3\u901a\uff0c\u4e0d \u5047\u601d\u7d22\uff0c\u6309\u7167\u534e\u5929\u98ce\u6240\u6307\u70b9\u7684\u65b9\u4f4d\u8d70\u53bb\u3002\u679c\u7136\u4e00\u5251\u523a\u51fa\uff0c\u5251\u950b\u4fbf\u6307\u5230\u4e86\u90a3\u5c11\u5e74\u7684\u80a9\u5934\u3002\u5e78\u800c \u90a3\u5c11\u5e74\u5c61\u7ecf\u9635\u4ed7\uff0c\u4e34\u5371\u4e0d\u4e71\uff0c\u6025\u5fd9\u6c89\u80a9\u7aef\u80cc\uff0c\u7435\u7436\u9aa8\u624d\u4e0d\u81f3\u4e8e\u88ab\u5b9d\u5251\u6d1e\u7a7f\uff0c\u53ef\u662f\u5982\u6b64\u4e00\u6765\uff0c \u6c5f\u6d77\u5929\u53c8\u5df2\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u593a\u4e86\u5148\u624b\u3002 \u3000\u3000\u534e\u5929\u98ce\u53c8\u53eb\u9053\uff1a\u201c\u653b\u4ed6\u4e0b\u76d8\uff0c\u518d\u523a\u4ed6\u9633\u767d\u7a74\uff01\u201d\u8981\u77e5\u6c5f\u6d77\u5929\u5b66\u517c\u5404\u6d3e\u4e4b\u957f\uff0c\u534e\u5929\u98ce\u65e9\u5df2 \u770b\u51fa\uff0c\u4ed6\u7684\u5251\u6cd5\u638c\u6cd5\u90fd\u8981\u80dc\u8fc7\u5bf9\u65b9\u4e0d\u6b62\u4e00\u7b79\u800c\u529f\u529b\u4e5f\u4e0d\u5728\u5bf9\u65b9\u4e4b\u4e0b\uff0c\u8bba\u7406\u51b3\u4e0d\u81f3\u4e8e\u5403\u4e8f\uff0c\u5403 \u4e8f\u7684\u53ea\u5728\u7ecf\u9a8c\u4e0d\u8db3\u800c\u5df2\u3002\u6240\u4ee5\u534e\u5929\u98ce\u4e0d\u987b\u8bf4\u51fa\u62db\u6570\u7684\u540d\u79f0\uff0c\u56e0\u4e3a\u82e5\u8bba\u62db\u6570\u7684\u53d8\u5316\uff0c\u6c5f\u6d77\u5929\u6240 \u61c2\u5f97\u7684\uff0c\u534e\u5929\u98ce\u4e5f\u8fd8\u672a\u5fc5\u61c2\u3002\u53ea\u662f\u6559\u4ed6\u5982\u4f55\u6253\u6cd5\uff0c\u53ca\u5982\u4f55\u8d70\u4f4d\u4fbf\u884c\uff0c \u3000\u3000\u6c5f\u6d77\u5929\u811a\u8e0f\u9f99\u95e8\uff0c\u7ed5\u51fa\u9707\u4f4d\uff0c\u5b9d\u5251\u6296\u8d77\u4e86\u5339\u7ec3\u4f3c\u7684\u4e00\u9053\u5bd2\u5149\u3002\u4e00\u62db\u201c\u4e58\u5c24\u5f15\u51e4\u201d\uff0c\u5f84\u523a \u90a3\u65f6\u516c\u5b50\u7684\u7709\u5fc3\uff0c\u8fd9\u4e00\u62db\u662f\u4ece\u5929\u5c71\u5251\u6cd5\u4e2d\u53d8\u5316\u51fa\u6765\u7684\uff0c\u5c24\u5176\u662f\u4ece\u8fd9\u4e2a\u65b9\u4f4d\u523a\u6765\uff0c\u5bf9\u65b9\u7684\u4e0a\u4e09 \u8def\u90fd\u5df2\u5728\u5251\u5149\u7b3c\u7f69\u4e4b\u4e0b\u3002 \u3000\u3000\u90a3\u201c\u53f6\u516c\u4e8e\u201d\u970d\u5730\u4e00\u4e2a\u51e4\u70b9\u5934\uff0c\u5411\u4e0b\u4e00\u8e72\uff0c\u8eab\u8eaf\u9661\u7136\u77ee\u4e86\u5c3a\u8bb8\uff0c\u633d\u5251\u4fbf\u8981\u523a\u6c5f\u6d77\u5929\u7684\u811a \u80cc\uff0c\u8fd9\u4e00\u62db\u6709\u4e2a\u540d\u5802\uff0c\u5524\u4f5c\u201c\u94c1\u725b\u8015\u5730\u201d\uff0c\u5df2\u662f\u5728\u8fd9\u6837\u5f62\u52bf\u4e0b\uff0c\u5e94\u4ed8\u5f97\u6700\u6070\u5f53\u7684\u4e00\u62db\uff1a\u54ea\u77e5 \u6c5f\u6d77\u5929\u5f97\u4e86\u534e\u5929\u98ce\u7684\u6307\u6559\uff0c\u5fc3\u9886\u795e\u4f1a\uff0c\u5411\u5bf9\u65b9\u7684\u4e0a\u4e09\u8def\u4f6f\u653b\u4e00\u5251\uff0c\u8fc5\u5373\u4fbf\u8f6c\u8fc7\u6765\u653b\u51fb\u5bf9\u65b9\u7684 \u4e0b\u76d8\uff0c\u4e00\u638c\u5288\u51fa\uff0c\u521a\u597d\u5288\u4e2d\u5bf9\u65b9\u7684\u81c0\u90e8\u7684\u76d8\u9aa8\uff0c\u4f46\u542c\u5f97\u5495\u549a\u4e00\u58f0\uff0c\u90a3\u201c\u53f6\u516c\u5b50\u201d\u8dcc\u51fa\u4e86\u4e09\u4e08 \u5f00\u5916\uff01 \u3000\u3000\u90a3\u201c\u53f6\u516c\u5b50\u201d\u4e5f\u597d\u751f\u4e86\u5f97\uff1b\u542c\u4ed6\u8dcc\u5f97\u751a\u91cd\uff0c\u5c45\u7136\u4e00\u8dc3\u4fbf\u8d77\uff0c\u5927\u6012\u9a82\u9053\uff1a\u201c\u5148\u6740\u4e86\u4f60\u8fd9\u9976 \u820c\u7684\u8001\u6740\u6750\uff01\u201d\u65cb\u98ce\u4f3c\u7684\u5411\u534e\u65e0\u98ce\u7236\u5973\u5954\u53bb\uff0c\u5413\u5f97\u534e\u4e91\u78a7\u9762\u65e0\u4eba\u8272\uff0c\u98a4\u58f0\u5c16\u53eb\u3002 \u3000\u3000\u534e\u5929\u98ce\u5374\u662f\u6beb\u4e0d\u614c\u5f20\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u53f6\u516c\u5b50\uff0c\u987b\u77e5\u87b3\u8782\u6355\u8749\uff0c\u9ec4\u96c0\u5728\u540e\u3002\u201d\u90a3\u201c\u53f6\u516c \u5b50\u201d\u5fc3\u4e2d\u4e00\u51db\uff0c\u5df2\u542c\u5f97\u80cc\u540e\u7684\u5288\u7a7a\u638c\u58f0\u3002\u6c5f\u6d77\u5929\u65e9\u5c31\u63d0\u9632\u5230\u4ed6\u6709\u6b64\u4e00\u7740\uff0c\u8fd9\u65f6\u5df2\u662f\u5982\u5f71\u968f\u5f62 \u7684\u8ffd\u4e86\u5230\u6765\u3002 \u3000\u3000\u90a3\u201c\u53f6\u516c\u5b50\u201d\u53cd\u624b\u4e00\u638c\uff0c\u6c5f\u6d77\u5929\u6068\u4ed6\u800d\u4f24\u5bb3\u75c5\u4eba\uff0c\u8fd9\u4e00\u638c\u4e5f\u7528\u5c3d\u4e86\u5168\u529b\uff0c\u4f46\u542c\u5f97\u201c\u84ec\u201d \u7684\u4e00\u58f0\uff0c\u6c5f\u6d77\u5929\u4e0a\u8eab\u6643\u4e86\u4e00\u6643\uff0c\u90a3\u201c\u53f6\u516c\u5b50\u201d\u5374\u518d\u5ea6\u7ed9\u4ed6\u638c\u529b\u9707\u7ffb\uff0c\u5728\u5730\u4e0a\u63a5\u8fde\u6253\u4e86\u51e0\u4e2a\u7b4b \u6597\uff0c\u4e00\u6e9c\u70df\u7684\u8dd1\u4e86\u3002\u6c5f\u6d77\u5929\u4e5f\u4e0d\u7981\u9a87\u7136\uff0c\u91cc\u5fc3\u60f3\u9053\uff1a\u201c\u6211\u7528\u4e86\u5927\u987b\u5f25\u638c\u529b\uff0c\u4ed6\u63a5\u8fde\u6328\u4e86\u6211\u7684 \u4e24\u638c\u3001\u5c45\u7136\u8fd8\u672a\u53d7\u4f24\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5e72\u7239\u3002\u7d2f\u4f60\u53d7\u60ca\u4e86\uff0c\u591a\u8c22\u4f60\u7684\u6307\u70b9\u3002\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u5ba2\u6c14\u7684\u8bdd\u3001\u5927\u5bb6\u90fd\u522b \u8bf4\u4e86\uff0c\u8d76\u5feb\u8d70\u5427\uff1a\u5230\u4e86\u4e91\u5bb6\u5e84\uff0c\u201d\u54b1\u4eec\u624d\u6709\u4e2a\u5b89\u8eab\u4e4b\u6240\u3002\u201d \u3000\u3000\u5e78\u559c\u5df2\u5230\u4e86\u5e73\u5730\uff0c\u53ef\u4ee5\u653e\u9f50\u811a\u6b65\uff0c\u4f46\u90a3\u4e91\u5bb6\u5e84\u5728\u7941\u8fde\u5c71\u897f\u9762\u8fd8\u6709\u767e\u91cc\uff0c\u534e\u4e91\u78a7\u80cc\u7740\u4eba\uff0c \u7a76\u7adf\u4e0d\u6562\u8dd1\u5f97\u592a\u5feb\uff1b\u8d70\u4e86\u6ca1\u6709\u591a\u4e45\uff0c\u5929\u8272\u5df2\u9ed1\uff0c\u597d\u5728\u6709\u6708\u5149\uff0c\u4ed6\u4eec\u4fbf\u5728\u6708\u5149\u4e0b\u8fde\u591c\u8d76\u8def\u3002\u8def \u9014\u4e2d\u534e\u5929\u98ce\u53d1\u4e86\u4e24\u6b21\u9ad8\u70e7\uff0c\u6709\u4e00\u6b21\u4e14\u660f\u8ff7\u8fc7\u53bb\uff0c\u534e\u4e91\u78a7\u8ba9\u4ed6\u518d\u670d\u4e86\u4e00\u7c92\u5c0f\u8fd8\u4e39\uff0c\u624d\u9192\u8fc7\u6765\u3002 \u3000\u3000\u8d70\u5230\u5927\u7ea6\u5348\u591c\u65f6\u5206\uff0c\u5ffd\u89c9\u773c\u524d\u4e00\u4eae\uff0c\u4f46\u89c1\uff0d\u7247\u6e56\u6c34\uff0c\u5728\u6708\u5149\u4e0b\u5206\u5916\u6676\u83b9\uff0c\u9001\u4e2a\u79c0\u4e3d\u7684\u4eba \u5de5\u6e56\u662f\u5728\u5c71\u5c97\u5408\u62b1\u4e4b\u4e2d\u5f00\u8f9f\u51fa\u6765\u7684\uff0c\u8fdc\u5904\u5c71\u5ce6\u8d77\u4f0f\uff0c\u6e56\u6ee8\u67f3\u6811\u6210\u884c\uff0c\u6708\u6620\u6eaf\u5fc3\uff0c\u4e91\u6d6e\u6c34\u9762\uff0c \u5c71\u5c97\u540e\u9762\uff0c\u9690\u9690\u770b\u89c1\u4eba\u5bb6\u3002\u6c5f\u6d77\u5929\u8d5e\u9053\uff1a\u201c\u679c\u7136\u4e0d\u6127\u8fd9\u2018\u6c34\u4e91\u4e61\u2019\u4e09\u5b57\u3002\u201d \u3000\u3000\u4ed6\u4eec\u8d70\u8fc7\u5c71\u5c97\uff0c\u627e\u5230\u4e00\u95f4\u5927\u5c4b\uff0c\u95e8\u524d\u6709\u5bf9\u77f3\u72ee\u5b50\uff0c\u6731\u6f06\u5927\u95e8\uff0c\u6c14\u6d3e\u4e0d\u51e1\uff0c\u6c5f\u6d77\u5929\u559c\u9053\uff1a \u201c\u8fd9\u5b9a\u662f\u4e91\u5927\u4fa0\u7684\u4f4f\u5b85\u4e86\u3002\u201d\u5f53\u4e0b\u4fbf\u4e0a\u53bb\u6572\u95e8\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u91cc\u9762\u4ecd\u65e0\u534a\u70b9\u58f0\u606f\uff0c\u6c5f\u6d77\u5929\u63d0\u9ad8\u4e86\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u534e\u5c71\u533b\u9690\u534e\u5929\u98ce\u6c42\u89c1\u4e91\u5e84 \u4e3b\uff01\u201d\u4ed6\u4e00\u65f6\u60c5\u6025\uff0c\u5c06\u90a3\u6731\u6f06\u5927\u95e8\uff0c\u62cd\u5f97\u9707\u5929\u4ef7\u54cd\u3002\u534e\u5929\u98ce\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u4e91\u5e84\u4e3b\u4e49\u8584\u4e91\u5929\uff0c \u5176\u5b9e\u4f60\u4e0d\u5fc5\u4eae\u51fa\u6211\u7684\u540d\u5934\uff0c\u4ed6\u4e5f\u4f1a\u6536\u7559\u7684\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u53cd\u800c\u663e\u5f97\u62db\u6447\u4e86\u3002\u201d \u3000\u3000\u53c8\u8fc7\u4e86\u4e00\u4f1a\uff0c\u90a3\u4e24\u6247\u5927\u95e8\u624d\u5f90\u5f90\u6253\u5f00\uff0c\u53ea\u89c1\u706b\u628a\u901a\u660e\uff0c\u4e03\u516b\u4e2a\u4f69\u6234\u7740\u5175\u5668\u7684\u6c49\u5b50\uff0c\u7c07\u62e5 \u7740\u4e00\u4e2a\u4e94\u5341\u5c81\u4e0a\u4e0b\u7684\uff0c\u9762\u76ae\u7126\u9ec4\uff0c\u8eab\u8eaf\u5fae\u507b\uff0c\u9887\u663e\u5f97\u6709\u70b9\u8001\u6001\u602a\u6a21\u6837\u7684\u4eba\u51fa\u6765\uff0c\u6c5f\u6d77\u5929\u9887\u89c9 \u610f\u5916\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u82f1\u540d\u8fdc\u64ad\u7684\u4e91\u53ec\uff0c\u5374\u662f\u8fd9\u4e2a\u6a21\u6837\u3002\u8fd9\u4e9b\u624b\u6267\u5175\u5668\u7684\u4eba\uff0c\u60f3\u5fc5\u662f\u4ed6 \u7684\u5f1f\u5b50\u4e86\uff1f\u54a6\uff0c\u4ed6\u4eec\u4e3a\u4f55\u6446\u51fa\u8fd9\u4e2a\u9635\u4ed7\uff0c\u7adf\u4f3c\u5982\u4e34\u5927\u654c\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u867d\u662f\u5fc3\u6709\u6240\u7591\uff0c\u4ecd\u7136\u4e0d\u5931\u672d\u6570\uff0c\u5f53\u4e0b\u4fbf\u8eac\u8eab\u8bf4\u9053\uff1f\u201c\u665a\u8f88\u6c5f\u6d77\u5929\u7aed\u89c1\u4e91\u5e84\u4e3b\u3002\u8fd9 \u4f4d\u662f\u6211\u7684\u4e49\u7236\u534e\u5929\u98ce\u3002\u201d\u90a3\u8001\u6c49\u54b3\u4e86\u4e00\u58f0\uff0c\u6253\u91cf\u4e86\u4ed6\u4eec\u4e09\u4eba\u4e00\u773c\uff0c\u5f90\u5f90\u8bf4\u9053\uff1a\u201c\u6c5f\u58ee\u571f\u8bef\u4f1a \u4e86\uff0c\u6211\u4e0d\u662f\u4e91\u5e84\u4e3b\uff0c\u6211\u662f\u8fd9\u91cc\u7684\u7ba1\u5bb6\u3002\u201d\u6b47\u4e86\u4e00\u4e0b\uff0c\u53c8\u9053\uff1a\u201c\u8bf7\u95ee\u4e09\u4f4d\u6df1\u591c\u5230\u6765\uff0c\u6240\u4e3a\u4f55 \u4e8b\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u4e49\u7236\u53d7\u4e86\u70b9\u4f24\uff0c\u60f3\u501f\u5b9d\u5e84\u517b\u75c5\u3002\u201d \u3000\u3000\u90a3\u7ba1\u5bb6\u56de\u5934\u4e00\u76b1\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u662f\u4e0d\u5de7\u5f97\u5f88\uff0c\u655d\u5e84\u4e3b\u6070\u6070\u51fa\u95e8\u53bb\u4e86\uff0c\u6211\u4eec\u4e0d\u6562\u64c5\u81ea\u7559\u5ba2\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u542c\u4e86\u8fd9\u8bdd\uff0c\u6709\u5982\u5934\u9876\u6253\u4e86\u4e00\u4e2a\u7126\u96f7\uff0c\u5446\u4e86\u7247\u523b\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u7d20\u95fb\u4e91\u5bb6\u5e84\u597d\u5ba2\uff0c \u6211\u4eec\u624d\u6562\u524d\u6765\uff0c\u6211\u4e49\u7236\u75c5\u91cd\uff0c\u5e84\u4e3b\u867d\u7136\u4e0d\u5728\uff1a\u60f3\u6765\u4e5f\u53ef\u6682\u501f\u4e00\u5e2d\u4e4b\u5730\u5b89\u8eab\uff1f\u201d \u3000\u3000\u90a3\u7ba1\u5bb6\u9053\uff0c\u201c\u8fd9\u4f4d\u8001\u5148\u751f\u5c31\u662f\u534e\u5c71\u533b\u9690\u534e\u65e0\u98ce\u4e48\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u6b63\u662f\u8001\u673d\u3002\u201d \u4ed6\u5fc3\u529b\u4ea4\u75b2\uff0c\u5728\u8def\u9014\u4e0a\u53c8\u672a\u66fe\u597d\u597d\u6b47\u606f\uff0c\u8bf4\u8bdd\u4f4e\u6c89\uff0c\u6709\u6c14\u6ca1\u529b\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u4eec\u770b\u4ed6\u75c5\u5f97\u8fd9\u4e2a\u6a21\u6837\uff0c\u600e\u80fd\u8fd8\u5230\u522b\u5904\uff1f\u5b9e\u662f\u6025\u9700\u6b47\u4e0b\u6765\u8c03\u6cbb\u7684\u4e86\u3002\u5c31\u8bf7 \u8d35\u7ba1\u5bb6\u884c\u4e2a\u65b9\u4fbf\u5427\uff1f\u201d \u3000\u3000\u90a3\u7ba1\u5bb6\u813e\u6c14\u6781\u4e3a\u51b7\u6f20\uff1b\u53c8\u518d\u6253\u91cf\u4e86\u534e\u5929\u98ce\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u7d20\u95fb\u534e\u5c71\u533b\u9690\u533b\u672f\u901a\u795e\uff0c\u82e5\u662f \u6709\u75c5\uff0c\u60f3\u5fc5\u4e5f\u80fd\u81ea\u884c\u8c03\u6cbb\uff0c\u51b3\u65e0\u59a8\u788d\u3002\u5e84\u4e3b\u4e0d\u5728\uff0c\u6211\u4eec\u4e0d\u6562\u6536\u7559\u4eb2\u4eba\uff0c\u8fd9\u91cc\u6709\u4e94\u5341\u4e24\u7684\u767d\u94f6 \u4e00\u952d\uff0c\u8bf8\u4f4d\u5018\u82e5\u7f3a\u5c11\u76d8\u7f20\uff0c\u9700\u8981\u4e70\u836f\uff0c\u4fbf\u8bf7\u6536\u4e0b\u3002\u524d\u9762\u4e09\u5341\u591a\u91cc\uff0c\u4fbf\u6709\u4e00\u4e2a\u5c0f\u9547\uff0c\u5ba2\u5e97\u836f\u5e97 \u90fd\u6709\uff0c\u8bf8\u4f4d\u8bf7\u4fbf\u3002\u6211\u529b\u4e4b\u6240\u53ca\uff0c\u53ea\u80fd\u5982\u6b64\u4e86\uff0c\u8bf7\u8bf8\u4f4d\u89c1\u8c05\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u7adf\u628a\u4ed6\u4eec\u5f53\u6210\u662f\u6253 \u79cb\u98ce\u7684\u3002\u4f46\u89c1\u4ed6\u628a\u624b\u4e00\u6325\uff0c\u7acb\u5373\u6709\u4e2a\u58ee\u6c49\u6367\u4e0a\u4e00\u4e2a\u76d8\u5b50\uff0c\u76d8\u4e2d\u7aef\u7aef\u6b63\u6b63\u7684\u653e\u7740\u4e00\u952d\u5927\u94f6\u3002 \u3000\u3000\u534e\u5929\u98ce\u53cc\u773c\u500f\u5f20\uff0c\u6124\u7136\u8bf4\u9053\uff1a\u201c\u6d77\u5929\uff0c\u4e0d\u5fc5\u5f3a\u4eba\u6240\u96be\u4e86\uff0c\u8d70\u5427\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u84e6\u5730\u4ef0\u5929\u5927\u7b11\u9053\uff0c\u201c\u95fb\u540d\u4e0d\u5982\u89c1\u9762\uff0c\u89c1\u9762\u4e0d\u4f3c\u95fb\u540d\uff01\u54c8\u54c8\uff0c\u4e91\u5bb6\u5e84\u597d\u5ba2\u4e4b\u540d\uff0c\u539f \u6765\u5982\u6b64\uff01\u591a\u627f\u539a\u8d50\uff0c\u5fc3\u9886\u656c\u58c1\uff01\u201d\u5c06\u90a3\u952d\u5927\u94f6\u4e00\u634f\uff0c\u90a3\u534a\u6708\u5f62\u7684\u5143\u5b9d\u767b\u65f6\u88ab\u634f\u6210\u4e00\u56e2\uff0c \u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u6c5f\u6d77\u5929\u5c06\u5927\u94f6\u63b7\u56de\u76d8\u5b50\uff0c\u5934\u4e5f\u4e0d\u56de\u7684\u4fbf\u548c\u534e\u5929\u98ce\u7236\u5973\u8d70\u4e86\u3002 \u3000\u3000\u6731\u6f06\u5927\u95e8\u5173\u4e0a\uff0c\u9690\u9690\u8fd8\u542c\u5f97\u90a3\u4e9b\u4eba\u5728\u95e8\u5185\u8c29\u9a82\u4e4b\u58f0\uff0c\u8bf8\u58f0\u7eb7\u6742\u4e4b\u4e2d\uff0c\u4f3c\u542c\u5f97\u4e00\u4e2a\u7c97\u8c6a\u7684 \u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8981\u4e0d\u662f\u5e08\u7236\u65e9\u6709\u5429\u5490\uff1b\u6211\u771f\u60f3\u628a\u90a3\u5c0f\u5b50\u75db\u6253\u4e00\u987f\uff1b\u54fc\uff1a\u54b1\u4eec\u4e91\u5bb6\u5e84\u4eab\u8a89\u6c5f\u6e56\uff1b \u8c01\u4e0d\u94a6\u4ef0\uff0c\u4ed6\u7adf\u6562\u5982\u6b64\u4e0d\u656c\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u4e2d\u4e00\u52a8\uff1b\u6068\u6068\u8bf4\u9053\uff1a\u201c\u8bf4\u8bdd\u8fd9\u4eba\u60f3\u5fc5\u662f\u4e91\u53ec\u7684\u5f92\u5f1f\uff0c\u54fc\uff0c\u90a3\u4e91\u5e84\u4e3b\u539f\u6765\u5c31\u5728\u5bb6 \u4e2d\uff0c\u5374\u662f\u6545\u610f\u780c\u8f9e\u9010\u5ba2\u7684\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u8574\u6cea\u9053\uff1a\u201c\u6d77\u54e5\uff0c\u5929\u4e0b\u4e4b\u5927\uff0c\u96be\u9053\u9664\u4e86\u4e91\u5bb6\u5e84\u5c31\u6ca1\u6709\u5bb9\u8eab\u4e4b\u5904\uff1f\u522b\u4eba\u65e2\u4e0d\u80af\u6536 \u7559\uff0c\u4f60\u8fd8\u95f9\u4ec0\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u53ea\u662f\u6c14\u4ed6\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u9ed1\u591c\u832b\u832b\uff0c\u534e\u5929\u98ce\u7684\u75c5\u53c8\u6025\u9700\u89c5\u5730\u9759\u517b\uff0c\u66f4\u53ef\u6015\u7684\u662f\uff1a\u968f\u65f6\u8fd8\u53ef\u80fd\u6709\u654c\u4eba \u7aa5\u4f3a\u5728\u65c1\uff0c\u9664\u4e86\u4e91\u5bb6\u5e84\uff0c\u7684\u786e\u96be\u4ee5\u627e\u5230\u5b89\u5168\u7684\u517b\u75c5\u4e4b\u6240\u3002\u6c5f\u6d77\u5929\u548c\u534e\u4e91\u78a7\u7684\u5fc3\u5934\u90fd\u611f\u5230\u5341\u5206 \u6c89\u91cd\u3002 \u3000\u3000\u534e\u5929\u98ce\u4e00\u76f4\u9ed8\u9ed8\u4e0d\u8a00\uff0c\u8fc7\u4e86\u7ea6\u6709\u534a\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u4eec\u5df2\u8d70\u8fc7\u4e86\u6e56\u6ee8\uff0c\u60f3\u627e\u4e2a\u5c71\u95ee\u7684\u730e\u6237\u501f \u5bbf\uff0c\u4e00\u65f6\u4e4b\u95f4\u3002\u5c1a\u672a\u53d1\u73b0\u4eba\u5bb6\uff0c\u534e\u5929\u51e4\u5ffd\u9053\uff1a\u201c\u8fd9\u4e8b\u770b\u6765\u6709\u70b9\u8e4a\u8df7\uff01\u201d\u6c5f\u6d77\u5929\u8be7\u9053\uff1a\u201c\u90a3\u4e91 \u53ec\u660e\u660e\u907f\u800c\u4e0d\u89c1\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef\u8bf4\u7684\u5462\uff1f\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u89c1\u4e00\u6761\u4eba\u5f71\uff0c\u5982\u98de\u5954\u6765\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u60f3\u9003\u5417\uff1f\u54fc\uff0c\u4e91\u5bb6\u5e84\u662f\u8fd9\u4e48 \u5bb9\u6613\u8ba9\u4f60\u4eec\u8981\u6765\u4fbf\u6765\uff0c\u8981\u53bb\u4fbf\u53bb\u7684\u5417\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u52c3\u7136\u5927\u6012\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4e91\u5bb6\u5e84\u4e0d\u8ba9\u6211\u4eec\u501f\u5bbf\uff0c\u90a3\u4e5f\u7f62\u4e86\uff0c\u96be\u9053\u4f60\u4eec\u8fd8\u60f3\u4e58\u4eba\u4e4b \u96be\uff0c\u8d81\u706b\u6253\u52ab\u4e0d\u6210\uff1f\u201d \u3000\u3000\u90a3\u82cd\u8001\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u653e\u5c41\uff01\u4f60\u77e5\u9053\u4f60\u662f\u5728\u548c\u8c01\u8bf4\u8bdd\uff1f\u201d\u534e\u5929\u98ce\u5fd9\u9053\uff1a\u201c\u6709\u8bdd\u6162\u8bb2\uff01\u201d \u3000\u3000\u53ef\u662f\uff0c\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u534e\u5929\u98ce\u7684\u8bdd\u58f0\u672a\u4e86\uff0c\u90a3\u8eab\u6750\u9ad8\u5927\u7684\u8001\u4eba\u5df2\u5230\u4e86\u6c5f\u6d77\u5929\u7684\u9762\u524d\uff0c \u4ed6\u4e0e\u6c5f\u6d77\u5929\u6253\u4e86\u4e00\u4e2a\u7167\u9762\uff0c\u84e6\u5730\u53cc\u773c\u706b\u7ea2\uff0c\u9a82\u9053\uff1a\u201c\u6b63\u662f\u4f60\u8fd9\u8d3c\u5c0f\u5b50\uff01\u201d\u547c\u7684\u4e00\u638c\uff0c\u5c31\u6253\u4e0b \u6765\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u4e00\u638c\u62cd\u51fa\uff0c\u53ea\u89c9\u5bf9\u65b9\u7684\u52b2\u529b\u5927\u5f97\u5f02\u5e38\uff0c\u9976\u662f\u4ed6\u6709\u62a4\u4f53\u795e\u529f\u3002\u63a5\u4e86\u4ed6\u8fd9\u4e00\u638c\uff0c\u4e5f\u89c9 \u767e\u9ab8\u6b32\u88c2\uff0c\u4e94\u810f\u7ffb\u817e\uff0c\u800c\u4e14\u88ab\u4ed6\u7684\u638c\u98ce\u4e00\u8feb\uff0c\u547c\u5438\u4e5f\u51e0\u4e4e\u7a92\u606f\uff01 \u3000\u3000\u534e\u5929\u98ce\u6323\u624e\u7740\u7528\u5c3d\u6c14\u529b\u558a\u9053\uff1a\u201c\u6765\u7684\u53ef\u662f\u4e91\u5927\u4fa0\u5417\uff1f\u7d20\u95fb\u4e91\u5927\u4fa0\u4ee5\u5fb7\u773c\u4eba\uff0c\u4e3a\u4f55\u89c1\u9762\u4fbf \u6253\uff1f\u201d \u3000\u3000\u90a3\u8eab\u6750\u9ad8\u5927\u7684\u8001\u4eba\u9053\uff1a\u201c\u4e0e\u4f60\u4eec\u8fd8\u6709\u4f55\u7406\u53ef\u8bf4\uff1f\u201d\u547c\u547c\u4e24\u8bb0\u8fde\u73af\u638c\u53c8\u5288\u8fc7\u6765\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u4e00\u98d8\u4e00\u95ea\uff0c\u4f7f\u51fa\u5929\u7f57\u6b65\u6cd5\uff0c\u54ea\u77e5\u4e91\u53ec\u7684\u638c\u529b\u7aef\u7684\u5389\u5bb3\u975e\u5e38\uff0c\u4ed6\u4e00\u638c\u6253\u7a7a\uff0c\u84e6\u5730\u51cc \u7a7a\u8dc3\u8d77\uff0c\u53cc\u638c\u4ecd\u662f\u8fde\u73af\u51fb\u4e0b\uff0c\u5468\u56f4\u6570\u4e08\u65b9\u5706\u4e4b\u5730\uff0c\u5168\u5728\u4ed6\u7684\u638c\u529b\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u9976\u662f\u6c5f\u6d77\u5929\u8eab\u5f62 \u5de7\u5feb\uff0c\u4ea6\u5df2\u65e0\u6cd5\u9003\u907f\uff01 \u3000\u3000\u4f46\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u6c5f\u6d77\u5929\u53f3\u638c\u62cd\u51fa\uff0c\u4e0e\u4ed6\u78b0\u4e2a\u6b63\u7740\uff0c\u5de6\u624b\u9a84\u6307\u5982\u4e7e\uff0c\u4fbf\u70b9\u4ed6\u7684\u8109 \u95e8\uff0c\u500f\u7136\u95f4\uff0c\u4f46\u89c1\u4e24\u6761\u4eba\u5f71\u4e00\u5206\uff0c\u6c5f\u6d77\u5929\u63a5\u8fde\u7684\u9000\u51fa\u4e86\u516d\u4e03\u6b65\uff0c\u53ef\u662f\u5374\u6ca1\u6709\u8dcc\u5012\u3002 \u3000\u3000\u539f\u6765\u4ed6\u8fd9\u6b21\u662f\u7528\u4e0a\u4e86\u5927\u987b\u5f25\u638c\u529b\uff0c\u4e14\u914d\u5408\u4e86\u4e00\u6307\u7985\u529f\uff0c\u6c5f\u6d77\u5929\u7684\u8089\u529f\u5df2\u8db3\u591f\u4e0e\u7b2c\u4e00\u6d41\u9ad8 \u624b\u6297\u8861\uff0c\u8fd9\u5927\u987b\u5f25\u638c\u529b\u66f4\u662f\u6700\u4e0a\u4e58\u7684\u62a4\u8eab\u529f\u592b\uff0c\u53ea\u5b88\u4e0d\u653b\uff0c\u654c\u4eba\u7eb5\u7136\u8fdc\u80dc\u4e8e\u4ed6\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c \u4e5f\u65e0\u6cd5\u5c06\u4ed6\u51fb\u7834\u3002 \u3000\u3000\u53ef\u662f\u4e91\u53ec\u7684\u5927\u529b\u91d1\u521a\u638c\u5929\u4e0b\u65e0\u53cc\uff0c\u800c\u6c5f\u6d77\u5929\u7684\u4e34\u654c\u7ecf\u9a8c\u53c8\u592a\u6b20\u7f3a\uff0c\u4e91\u53ec\u4e00\u638c\u51fb\u51fa\uff0c\u5ffd\u89c9 \u5bf9\u65b9\u7684\u6297\u529b\u5927\u5f97\u51fa\u5947\uff0c\u4fbf\u6479\u5730\u5c06\u638c\u529b\u4e00\u6536\uff0c\u6c5f\u6d77\u5929\u5374\u4e0d\u6653\u4e34\u673a\u5e94\u53d8\uff0c\u524d\u9762\u7684\u963b\u529b\u5ffd\u7136\u6d88\u5931\uff0c \u4ed6\u4e0d\u7531\u5f97\u8eab\u5411\u524d\u503e\uff0c\u5c31\u5728\u8fd9\u7b2c\u4e00\u638c\u7684\u638c\u529b\u7528\u201c\u8001\u201d\uff0c\u65b0\u529b\u672a\u53d1\u4e4b\u9645\uff0c\u4e91\u53ec\u95ea\u7535\u822c\u7684\u7b2c\u4e8c\u638c\u53c8 \u5df2\u5288\u6765\uff0c\u6545\u6b64\u6c5f\u6d77\u5929\u624d\u7ed9\u4ed6\u9707\u5f97\u8fde\u9000\u4e86\u516d\u4e03\u6b65\u3002\u8fd9\u8fd8\u662f\u7531\u4e8e\u4e91\u53ec\u5fcc\u60ee\u4ed6\u540c\u65f6\u5e76\u53d1\u7684\u4e00\u6307\u795e \u529f\uff0c\u672a\u6562\u8fde\u7eed\u5288\u51fa\u7b2c\u4e09\u638c\uff0c\u8981\u4e0d\u7136\u6c5f\u6d77\u5929\u65e9\u5df2\u53d7\u4e86\u91cd\u4f24\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4e91\u53ec\u53c8\u5df2\u5982\u5f71\u968f\u5f62\uff0c\u63a5\u56e0\u800c\u81f3\uff01\u6c5f\u6d77\u5929\u6012\u9053\uff1a\u201c\u4e91\u53ec\uff0c\u6211\u656c\u4f60\u662f\u4f4d\u4fa0 \u4e49\u524d\u8f88\uff0c\u4e0d\u613f\u4e0e\u4f60\u62fc\u547d\uff0c\u4f60\u5374\u8fd9\u7b49\u86ee\u4e0d\u8bb2\u7406\uff0c\u5f53\u771f\u8981\u8feb\u6211\u4e0e\u4f60\u4e00\u51b3\u6b7b\u751f\u4e48\uff1f\u201d\u4e91\u53ec\u559d\u9053\uff1a \u201c\u5c0f\u8d3c\u65e0\u8c13\u591a\u8a00\uff0c\u4f60\u6709\u4ec0\u4e48\u672c\u9886\uff0c\u5c3d\u7ba1\u65bd\u5c55\uff01\u8001\u5927\u662f\u51b3\u610f\u5c06\u4f60\u6bd9\u4e8e\u638c\u4e0b\u7684\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9002\u624d\u4e0e\u4ed6\u786c\u5bf9\u4e00\u638c\uff0c\u672a\u81f3\u8dcc\u5012\uff0c\u5fc3\u60c5\u5df2\u9547\u5b9a\u4e86\u8bb8\u591a\uff0c\u53ef\u662f\u4e91\u53ec\u4e00\u638c\u7d27\u4f3c\u4e00\u638c\uff0c\u6c5f \u6d77\u5929\u63a5\u8fde\u7528\u4e86\u516d\u4e03\u79cd\u5e08\u7236\u79d8\u4f20\u7684\u4e0a\u4e58\u529f\u592b\uff0c\u4ecd\u7136\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\u3002 \u3000\u3000\u4e91\u53ec\u4e5f\u4e0d\u7531\u5f97\u6697\u6697\u5947\u602a\uff1a\u201c\u8fd9\u5c0f\u5b50\u662f\u54ea\u91cc\u94bb\u51fa\u6765\u7684\uff1f\u6240\u5b66\u7684\u6b66\u529f\u7adf\u662f\u8fd9\u6837\u9a73\u6742\uff0c\u4f46\u6bcf\u4e00 \u6837\u529f\u592b\u5374\u53c8\u8fd9\u6837\u7cbe\u7eaf\uff01\u8981\u662f\u8ba9\u4ed6\u591a\u6d3b\u51e0\u5e74\uff0c\u90a3\u8fd8\u4e86\u5f97\uff1f\u201d\u9664\u201c\u6076\u201d\u4e4b\u5ff5\u4e00\u8d77\uff0c\u638c\u529b\u8d8a\u53d1\u521a\u731b \u7edd\u4f26\uff0c\u6253\u5f97\u6c5f\u6d77\u5929\u5934\u7b4b\u66b4\u6da8\uff0c\u6c57\u5982\u96e8\u4e0b\u3002 \u3000\u3000\u534e\u4e91\u78a7\u53eb\u9053\uff1a\u201c\u6d77\u54e5\uff0c\u62d4\u5251\u5440\uff01\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u62d4\u5251\uff1f\u201d\u6c5f\u6d77\u5929\u6447\u5934\u9053\uff1a\u201c\u4ed6\u4e0e\u84b2\u5362\u864e\u7b49 \u8f88\u4e0d\u540c\uff0c\u4ed6\u8d64\u624b\u7a7a\u62f3\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u7528\u5b9d\u5251\uff1f\u201d\u539f\u6765\u6c5f\u6d77\u5929\u81ea\u5c0f\u4fbf\u53d7\u7236\u3001\u5e08\u7684\u718f\u9676\uff0c\u5bf9\u4e8e\u4e00\u4e2a \u201c\u4fa0\u201d\u5b57\u6700\u4e3a\u7740\u91cd\uff0c\u5c3d\u7ba1\u4ed6\u5bf9\u4e91\u53ec\u4eca\u665a\u7684\u65e0\u7406\u653b\u51fb\u6124\u6012\uff0c\u4f46\u7531\u4e8e\u4e91\u53ec\u7d20\u8457\u4fa0\u540d\uff0c\u6c5f\u6d77\u5929\u4fbf\u4e5f \u8981\u4ee5\u201c\u4fa0\u4e49\u201d\u81ea\u6301\uff0c\u4e0d\u613f\u7528\u5b9d\u5251\u6765\u5bf9\u4ed8\u8089\u638c\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8bf4\u8bdd\u5206\u5fc3\uff0c\u9669\u9669\u53c8\u4e2d\u4e86\u4e91\u53ec\u7684\u4e00\u638c\uff0c\u534e\u4e91\u78a7\u53c8\u662f\u601c\u501f\uff0c\u53c8\u662f\u751f\u6c14\uff0c\u6b63\u60f3\u518d\u884c\u529d \u8bf4\u2026\u2026 \u3000\u3000\u534e\u5929\u98ce\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u6d77\u513f\uff0c\u628a\u5b9d\u5251\u63b7\u8fc7\u6765\uff01\u201d\u6c5f\u6d77\u5929\u4ee5\u4e3a\u4ed6\u8981\u5b9d\u5251\u9632\u8eab\uff0c\u5f53\u4e0b\u4f7f\u4e86\u4e00\u62db \u201c\u8131\u888d\u89e3\u7532\u201d\uff0c\u659c\u8eab\u53d1\u638c\uff0c\u6321\u4e86\u4e91\u53ec\u7684\u4e00\u62db\uff0c\u4e00\u4e2a\u9000\u6b65\u8eab\uff0c\u5df1\u89e3\u4e0b\u8170\u95f4\u7684\u4f69\u5251\uff0c\u63b7\u5c06\u8fc7\u53bb\u3002 \u3000\u3000\u534e\u4e91\u78a7\u65e9\u5df2\u628a\u7236\u4eb2\u653e\u4e0b\uff0c\u5c06\u5b9d\u5251\u63a5\u5230\u4e86\u624b\uff0c\u7acb\u5373\u62d4\u5251\u51fa\u9798\uff0c\u4fbf\u8981\u5954\u4e0a\u524d\u53bb\uff0c\u534e\u5929\u98ce\u5ffd\u53c8 \u53eb\u9053\uff1a\u201c\u78a7\u513f\uff0c\u4f4f\u6b65\uff01\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u534e\u4e91\u78a7\u8be7\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8981\u4ed6\u628a\u5251\u7ed9\u6211\uff0c\u662f\u60f3\u6211\u53bb\u52a9\u9635 \u5417\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u4e0d\uff01\u6211\u53ea\u8981\u4f60\u663e\u4e00\u663e\u8fd9\u5b9d\u5251\u7684\u5a01\u529b\uff0c\u8ba9\u4e91\u5e84\u4e3b\u5f00\u5f00\u773c\u754c\uff01\u201d \u3000\u3000\u534e\u4e91\u78a7\u6014\u4e86\u4e00\u6014\uff0c\u4f46\u5979\u7a76\u7adf\u662f\u4e2a\u5fc3\u7a8d\u73b2\u73d1\u7684\u5c11\u5973\uff0c\u4e00\u70b9\u4fbf\u900f\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5bf9\u4e86\u3002\u8fd9\u59d3 \u4e91\u7684\u65f6\u6d77\u54e5\u5982\u6b64\u86ee\u4e0d\u8bb2\u7406\uff0c\u5176\u4e2d\u5fc5\u662f\u6709\u6240\u8bef\u4f1a\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u633d\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u968f\u624b\u524a\u53bb\uff0c\u5251\u5149\u89e6\u5904\uff0c\u5ca9\u77f3\u5e94\u624b\u800c\u88c2\uff0c\u77f3\u788e\u7c89\u98de\u5982\u96e8\uff0c\u5f53\u771f\u662f\u65e0 \u575a\u4e0d\u6467\uff0c\u6321\u8005\u7acb\u6bc1\uff01 \u3000\u3000\u4e91\u53ec\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u89c1\u4e86\u8fd9\u5b9d\u5251\u7684\u5a01\u529b\uff0c\u4e5f\u4e0d\u7981\u6697\u6697\u5fc3\u60ca\uff01\u8981\u77e5\u53cc\u65b9\u7684\u6b66\u529f\uff0c\u82e5 \u662f\u592a\u8fc7\u60ac\u6b8a\uff0c\u5f31\u8005\u7eb5\u6709\u5b9d\u5251\uff0c\u4e5f\u51b3\u975e\u5f3a\u8005\u4e4b\u654c\uff0c\u4f46\u82e5\u76f8\u5dee\u4e0d\u8fdc\uff0c\u6709\u4e86\u5b9d\u5251\uff0c\u4fbf\u53ef\u5927\u5360\u4fbf\u5b9c\uff0c \u4e91\u53ec\u4e0d\u7531\u788d\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5018\u82e5\u4f7f\u7528\u6b64\u5251\uff0c\u867d\u7136\u672a\u5fc5\u4fbf\u4f24\u5f97\u4e86\u6211\uff0c\u4f46\u6700\u5c11\u4ea6\u5df2\u7acb\u4e8e\u4e0d\u8d25 \u4e4b\u5730\uff1a\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u7528\u5b9d\u5251\u5462\uff1f\u201d\u4ed6\u4e00\u76f4\u628a\u6c5f\u6d77\u5929\u5f53\u4f5c\u7a77\u51f6\u6781\u6076\u4e4b\u5f92\uff0c\u8fd9\u65f6\u4e0d\u7981\u5bf9\u81ea\u5df1\u7684\u60f3\u6cd5 \u8d77\u4e86\u6000\u7591\uff0c\u638c\u529b\u4e5f\u5c31\u6e10\u6e10\u653e\u677e\u4e00\u4e9b\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7f13\u4e86\u53e3\u6c14\uff0c\u518d\u6b21\u95ee\u9053\uff1a\u201c\u4e91\u5e84\u4e3b\uff0c\u6211\u4e0e\u4f60\u5f80\u65e5\u65e0\u51a4\u3002\u8fd1\u65e5\u65e0\u4ec7\uff0c\u4e3a\u4ec0\u4e48\u4f60\u5b9a\u8981\u5c06 \u6211\u6bd9\u4e8e\u638c\u4e0b\u3002\u201d\u5c82\u77e5\u8fd9\u4e00\u95ee\u53c8\u64a9\u8d77\u4e86\u4e91\u53ec\u7684\u6012\u706b\uff0c\u4ed6\u53cc\u773c\u5706\u7741\uff0c\u5927\u58f0\u559d\u9053\uff0c\u201c\u5c0f\u8d3c\uff0c\u4f60\u81ea\u5df1 \u505a\u7684\u4e8b\u60c5\u4f60\u81ea\u5df1\u660e\u767d\uff0c\u8fd8\u88c5\u4ec0\u4e48\u6837\uff1f\u201d\u58f0\u53d1\u638c\u5230\uff0c\u731b\u82e5\u5954\u96f7\uff0c\u767b\u65f6\u53c8\u628a\u6c5f\u6d77\u5929\u8feb\u5f97\u900f\u4e0d\u8fc7\u6c14 \u6765\uff0c\u96be\u4ee5\u8fa9\u89e3\u3002 \u3000\u3000\u534e\u5929\u98ce\u5ffd\u4e49\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u6d77\u513f\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u7528\u5927\u4e58\u822c\u82e5\u638c\uff1f\u6c5f\u6d77\u5929\u5df2\u4e0d\u80fd\u5206\u795e\u8bf4\u8bdd\uff0c\u534e \u4e91\u78a7\u4ee3\u4ed6\u7b54\u9053\uff1a\u201c\u7239\uff0c\u6d77\u54e5\u4e0d\u662f\u8bf4\u8fc7\u5417\uff01\u4ed6\u53ea\u61c2\u5f97\u8fd9\u95e8\u6b66\u529f\uff0c\u5374\u8fd8\u672a\u66fe\u7ec3\u8fc7\uff0c\u4f60\u600e\u4e48\u5fd8\u8bb0 \u4e86\uff1f\u201d\u5979\u5fc3\u91cc\u6697\u6697\u62c5\u5fe7\uff0c\u4ee5\u4e3a\u7236\u4eb2\u5df1\u662f\u75c5\u5f97\u7cca\u91cc\u7cca\u6d82\u4e86\u3002 \u3000\u3000\u679c\u7136\u534e\u5929\u98ce\u4fbf\u81ea\u8a00\u81ea\u8bed\uff1a\u201c\u5514\uff0c\u662f\u6211\u7cca\u6d82\u4e86\u3002\u4e0d\u8fc7\uff0c\u5374\u6709\u4eba\u6bd4\u6211\u66f4\u52a0\u7cca\u6d82\uff01\u201d\u534e\u4e91\u78a7\u6014 \u4e86\u4e00\u6014\uff0c\u95ee\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u6df1\u901a\u6b66\u5b66\uff0c\u60f3\u6765\u5f53\u4f1a\u77e5\u9053\u5927\u4e58\u822c\u82e5 \u638c\u80fd\u4f24\u5947\u7ecf\u516b\u8109\u3001\u4ed6\u4e0d\u53bb\u7f09\u6355\u771f\u51f6\uff0c\u5374\u628a\u4e00\u4e2a\u4e0d\u4f1a\u4f7f\u5927\u4e58\u822c\u82e5\u638c\u7684\u4eba\u5f53\u4f5c\u771f\u51f6\uff0c\u8fd9\u5c82\u4e0d\u662f\u6bd4 \u6211\u66f4\u7cca\u6d82\u4e48\uff1f\u201d \u3000\u3000\u4e91\u53ec\u731b\u5730\u4e00\u6014\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u7ed9\u6211\u8feb\u5f97\u8fd9\u822c\u72fc\u72c8\uff0c\u8981\u662f\u4f1a\u4f7f\u5927\u4e58\u822c\u82e5\u638c\uff0c\u65e9\u5c31 \u8be5\u4f7f\u51fa\u6765\u4e86\uff01\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u4e0d\u7531\u5f97\u4fbf\u6536\u56de\u53cc\u638c\uff0c\u8df3\u51fa\u5708\u5b50\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u65e2\u8bf4\u6211\u7cca \u6d82\uff1a\u90a3\u6211\u5c31\u5148\u95ee\u4e2a\u660e\u767d\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5598\u8fc7\u53e3\u6c14\uff0c\u8feb\u4e0d\u53ca\u5f85\u7684\u4fbf\u628a\u521a\u624d\u60f3\u8bf4\u7684\u8bdd\u5148\u8bf4\u4e86\u51fa\u6765\uff1a\u201c\u4e91\u8001\u82f1\u96c4\uff0c\u4f60\u8bf4\u6211\u81ea\u5df1 \u505a\u8fc7\u7684\u4e8b\u60c5\u6211\u8be5\u660e\u767d\uff0c\u53ef\u662f\u6211\u5374\u5b9e\u5728\u4e0d\u660e\u767d\uff01\u6211\u4eec\u501f\u4f4f\u5b9d\u5e84\uff0c\u672a\u8499\u5bb9\u7eb3\uff0c\u6211\u786e\u662f\u53d1\u4e86\u51e0\u53e5\u6028 \u8a00\uff0c\u96be\u9053\u8fd9\u5c31\u8be5\u6b7b\u7f6a\uff1f\u201d \u3000\u3000\u4e91\u53ec\u7741\u5927\u4e86\u773c\u775b\uff0c\u4ed4\u7ec6\u7684\u6253\u91cf\u4e86\u6c5f\u6d77\u5929\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7684\u7ba1\u5bb6\u62d2\u4e0d\u6536\u5bb9\uff0c\u4f60\u4eec\u4e5f\u4e0d\u8be5 \u5077\u95ef\u6211\u4e91\u5bb6\u5e84\uff0c\u518d\u65bd\u6697\u7b97\u5440\uff1f\u8bf7\u95ee\u4f60\u4eec\u4e0e\u6211\u7684\u513f\u5973\u4f55\u51a4\u4f55\u4ec7\uff0c\u4e3a\u4f55\u8981\u51e0\u6b21\u4e09\u756a\uff0c\u8d76\u5c3d\u6740\u7edd\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5927\u4e3a\u8be7\u5f02\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u672a\u8499\u6536\u5bb9\uff0c\u7acb\u5373\u5934\u4e5f\u4e0d\u56de\u5730\u8d70\u4e86\uff0c\u51e0\u65f6\u518d\u5230\u8fc7\u5b9d \u5e84\uff1f\u518d\u8bf4\uff0c\u4f60\u7684\u516c\u5b50\u548c\u5c0f\u59d0\uff0c\u6211\u4e00\u4e2a\u4e5f\u4e0d\u8ba4\u5f97\uff0c\u8fd9\u8bdd\u4ece\u54ea\u513f\u8bf4\u8d77\uff1f\u201d \u3000\u3000\u4e91\u53ec\u76b1\u4e86\u7709\u5934\uff0c\u73b0\u51fa\u6781\u5176\u7591\u60d1\u7684\u795e\u60c5\uff0c\u5ffd\u5730\u671d\u7740\u534e\u5929\u98ce\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u4f60\u53c8\u600e\u77e5\u9053\u6211 \u7684\u513f\u5973\u53d7\u4f24\uff1f\u597d\uff0c\u6211\u59d1\u4e14\u76f8\u4fe1\u4ed6\u4eec\u4e0d\u662f\u8fd9\u5c0f\u2026\u2026\u8fd9\u5c0f\u5b50\u4f24\u7684\uff1b\u7136\u5219\u771f\u51f6\u53c8\u662f\u54ea\u4e2a\uff1f\u201d\u663e\u7136\u542c \u5f97\u51fa\u6765\u3002\u4ed6\u672c\u662f\u60f3\u8bf4\u201c\u5c0f\u8d3c\u201d\u7684\uff0c\u8bdd\u5230\u53e3\u8fd1\uff0c\u5374\u6539\u6210\u4e86\u201c\u5c0f\u5b50\u201d\uff0c\u867d\u7136\u654c\u610f\u672a\u6d88\uff0c\u4f46\u5df2\u662f\u548c \u7f13\u591a\u4e86\u3002 \u3000\u3000\u534e\u5929\u98ce\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5c0f\u8001\u5934\u59d3\u534e\uff0c\u540d\u5524\u5929\u98ce\uff0c\u4ee4\u90ce\u4ee4\u5a9b\uff0c\u4eca\u65e5\u5728\u7941\u8fde\u5c71\u9047\u96be\uff1a\u6211\u4eec\u6070\u5de7 \u85cf\u5728\u9644\u8fd1\uff0c\u672a\u66fe\u76ee\u7779\uff0c\u5374\u4e5f\u8033\u95fb\u3002\u53ea\u56e0\u8001\u592b\u8eab\u53d7\u91cd\u4f24\uff0c\u60ed\u6127\u672a\u66fe\u63f4\u624b\uff0c\u90a3\u771f\u51f6\u7684\u6765\u5386\uff0c\u6211\u4e5f \u6beb\u65e0\u6240\u77e5\u3002\u201d \u3000\u3000\u4e91\u53ec\u53c8\u5403\u4e00\u60ca\uff0c\u5fd9\u9053\uff1a\u201c\u4f60\u5f53\u771f\u662f\u534e\u5c71\u533b\u9690\u534e\u5929\u98ce\uff1f\u201d\u534e\u5929\u98ce\u7b11\u9053\uff1a\u201c\u90a3\u534e\u5929\u98ce\u53c8\u4e0d\u662f \u4ec0\u4e48\u4e86\u4e0d\u8d77\u7684\u4eba\u7269\uff1b\u6211\u4e3a\u4ec0\u4e48\u8981\u5047\u5192\u4ed6\u7684\u540d\u5b57\uff1f\u201d \u3000\u3000\u4e91\u53ec\u8eab\u5f62\u4e00\u8d77\uff0c\u5ffd\u5730\u4f3c\u5140\u9e70\u4e00\u822c\uff0c\u5411\u534e\u5929\u98ce\u6251\u53bb\uff0c\u534e\u4e91\u78a7\u5927\u5403\u4e00\u60ca\uff0c\u614c\u5fd9\u4e00\u5251\u523a\u51fa\uff0c\u534e \u65e0\u98ce\u559d\u9053\uff1a\u201c\u78a7\u513f\uff0c\u8d70\u5f00\uff01\u6d77\u513f\uff0c\u4f60\u4e5f\u4f11\u6765\uff01\u201d\u559d\u58f0\u751a\u662f\u4e25\u5389\u3002\u6c5f\u6d77\u5929\u7ed9\u4ed6\u559d\u4f4f\u4e86\uff0c\u4f46\u534e\u4e91 \u78a7\u90a3\u4e00\u5251\u5374\u5df2\u6536\u624b\u4e0d\u53ca\u3002 \u3000\u3000\u4e91\u53ec\u8863\u8896\u4e00\u62c2\uff0c\u4f46\u89c1\u5251\u5149\u8fc7\u5904\uff0c\u4e91\u53ec\u7684\u8863\u8896\u5df2\u7ed9\u5979\u524a\u53bb\u4e8c\u5e45\uff0c\u968f\u5373\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c \u534e\u4e91\u78a7\u7684\u5b9d\u5251\u8131\u624b\u5760\u5730\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4e91\u53ec\u5df2\u4e00\u638c\u5411\u7740\u534e\u5929\u98ce\u524d\u80f8\u201c\u5370\u201d\u4e0b\uff0c\u6c5f\u6d77\u5929\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\u3002\u4f46\u4ed6 \u521a\u559d\u5f97\u4e00\u58f0\u201c\u8001\u5339\u592b\u2026\u2026\u201d\u5e95\u4e0b\u7684\u9a82\u8bed\u8fd8\u672a\u66fe\u8bf4\u51fa\uff0c\u5374\u5df2\u89c1\u4e91\u53ec\u5411\u534e\u5929\u98ce\u4fdd\u6df1\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a \u201c\u6055\u8001\u592b\u65e0\u793c\uff0c\u4f60\u679c\u7136\u662f\u53d7\u4e86\u91cd\u4f24\uff0c\u4e5f\u679c\u7136\u662f\u534e\u5c71\u533b\u9690\u534e\u5929\u98ce\uff01\u201d \u3000\u3000\u539f\u6765\u4e91\u53ec\u662f\u4e2a\u6df1\u901a\u6b66\u5b66\uff0c\u4e14\u53c8\u8001\u4e8e\u4e16\u6545\u7684\u6c5f\u6e56\u5927\u884c\u5bb6\uff0c\u5c31\u5728\u4ed6\u8fd9\u638c\u529b\u6b32\u5410\u8fd8\u6536\u4e4b\u9645\uff0c\u5df2 \u8bd5\u51fa\u4e86\u534e\u5929\u98ce\u7684\u771f\u4f2a\u3002\u7b2c\u4e8c\uff0c\u534e\u5929\u98ce\u82e5\u662f\u574f\u4eba\uff0c\u51b3\u4e0d\u4f1a\u5c06\u6c5f\u6d77\u5929\u559d\u4f4f\uff0c\u4efb\u7531\u4ed6\u638c\u5370\u5fc3\u80f8\uff0c\u5192 \u8fd9\u751f\u547d\u4e4b\u9669\u3002\u7531\u6b64\u4e5f\u53ef\u89c1\u534e\u65e0\u98ce\u662f\u82f1\u96c4\u8bc6\u82f1\u96c4\uff0c\u5bf9\u4ed6\u5b8c\u5168\u4fe1\u4efb\uff1b\u7b2c\u4e8c\uff0c\u4ed6\u8fd9\u4e00\u638c\u5370\u4e0b\uff1a\u5df2\u8bd5 \u51fa\u534e\u5929\u98ce\u786e\u662f\u529f\u529b\u5df2\u7ecf\u6d88\u5931\uff0c\u5e76\u975e\u6545\u610f\u88c5\u75c5\u3002\u4e91\u53ec\u7684\u638c\u529b\u5df2\u5230\u4e86\u6536\u53d1\u81ea\u5982\u7684\u5883\u754c\uff0c\u4e00\u6709\u6240 \u89c9\uff0c\u638c\u529b\u7acb\u5373\u5168\u90e8\u64a4\u56de\uff0c\u6545\u6b64\u5bf9\u534e\u5929\u98ce\u65e0\u4f24\u5bb3\uff1b\u7b2c\u4e09\u4eba\uff0c\u4ed6\u6307\u5934\u6cbe\u7740\u534e\u5929\u98ce\u7684\u808c\u80a4\uff0c\u4fbf\u611f\u5230 \u70eb\u624b\uff0c\u4ed6\u89c1\u591a\u8bc6\u5e7f\uff1b\u7acb\u5373\u4e86\u7136\u4e8e\u80f8\uff0c\u77e5\u9053\u8fd9\u662f\u53d7\u4e86\u84b2\u5362\u864e\u7684\u6bd2\u638c\u6240\u4f24\u3002\u7531\u6b64\uff0c\u4e5f\u5c31\u53ef\u4ee5\u786e\u5b9a \u4e86\u8fd9\u4eba\u4fbf\u662f\u534e\u5c71\u533b\u9690\u534e\u5929\u98ce\uff0c\u56e0\u4e3a\u5018\u82e5\u6362\u4e86\u522b\u4eba\uff0c\u8eab\u4e2d\u5267\u6bd2\uff0c\u51b3\u96be\u6d3b\u5230\u73b0\u5728\uff0c\u800c\u65e5\u8fd8\u53ef\u4ee5\u8c08 \u7b11\u81ea\u5982\uff01 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u867d\u7136\u8bd5\u51fa\u4e86\u534e\u5929\u98ce\u7684\u771f\u4f2a\uff0c\u5bf9\u6c5f\u6d77\u5929\u5374\u8fd8\u6709\u70b9\u6000\u7591\uff0c\u5f53\u4e0b\u4e0d\u7531\u5f97\u95ee\u9053\uff1a\u201c\u534e\u8001 \u5148\u751f\uff0c\u8fd9\u4e24\u4f4d\u7aef\u7684\u662f\u8c01\uff1f\u201d\u534e\u5929\u98ce\u9053\uff0c\u201c\u8fd9\u662f\u6211\u7684\u5c0f\u5973\u4e91\u78a7\uff1b\u4ed6\u662f\u91d1\u5927\u4fa0\u91d1\u4e16\u9057\u7684\u5f92\u5f1f\u6c5f\u6d77 \u5929\uff0c\u4e5f\u662f\u6211\u7684\u5e72\u513f\u3002\u4ed6\u4eec\u4e24\u4eba\u81ea\u548b\u665a\u81f3\u4eca\uff0c\u4ece\u672a\u66fe\u79bb\u5f00\u8fc7\u6211\u534a\u6b65\uff01\u201d \u3000\u3000\u4e91\u53ec\u201c\u554a\u5440\u201d\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff1a\u201c\u539f\u6765\u662f\u91d1\u5927\u4fa0\u7684\u5f1f\u5b50\uff01\u201d\u65cb\u53c8\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u8fd9\u5c31\u771f\u7684 \u5947\u602a\u4e86\uff0c\u6211\u81ea\u4fe1\u8001\u773c\u65e0\u82b1\uff0c\u90a3\u4e48\u6211\u521a\u624d\u542c\u89c1\u7684\u8fd9\u4eba\u5374\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5927\u4e3a\u8be7\u5f02\uff0c\u6b63\u5f85\u95ee\u4ed6\uff0c\u5ffd\u542c\u5f97\u4e91\u53ec\u559d\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u53ea\u89c1\u4e00\u6761\u4eba \u5f71\uff0c\u4ece\u6797\u5b50\u91cc\u51b2\u51fa\u6765\uff0c\u731b\u5730\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6211\u59d3\u97e9\u7684\u4e0e\u4f60\u62fc\u5566\uff01\u201d\u547c\u547c\u58f0\u54cd\uff0c\u4e24\u4ef6\u9ed1\u9edd\u9edd \u7684\u4e1c\u897f\u5411\u6c5f\u6d77\u5929\u500f\u5730\u98de\u6765\u3002\u542c\u8fd9\u52b2\u98ce\uff0c\u8fd9\u4eba\u53d1\u6697\u5668\u7684\u529f\u592b\u662f\u7b2c\u4e00\u6d41\u9ad8\u624b\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u8981\u7528\u5929\u7f57\u6b65\u6cd5\u95ea\u5f00\uff0c\u4e91\u53ec\u5df2\u628a\u8fd9\u4e24\u4ef6\u6697\u5668\u63a5\u4f4f\uff0c\u9976\u662f\u4ed6\u7684\u5927\u529b\u91d1\u521a\u638c\u5929\u4e0b\u65e0 \u5bf9\uff0c\u63a5\u4e86\u8fd9\u4e24\u4ef6\u6697\u5668\uff0c\u4e5f\u4e0d\u7531\u5f97\u8eab\u8eaf\u8fde\u6643\u51e0\u6643\u3001\u4f4e\u5934\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u4e00\u5bf9\u94c1\u9e2f\u9e33\u3002 \u3000\u3000\u4e91\u53ec\u5927\u53eb\u9053\uff1a\u201c\u6765\u7684\u53ef\u662f\u97e9\u4e8c\u7239\u4e48\uff1f\u201d\u90a3\u4eba\u89c1\u662f\u4e91\u53ec\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\u3002 \u6b63\u662f\u5c0f\u5f1f\u97e9\u7487\uff0c\u4e91\u5e84\u751f\uff0c\u4f60\u600e\u7684\u4e0e\u8fd9\u5c0f\u5b50\u4e00\u9053\uff1f\u201d\u4e91\u53ec\u9053\uff1a\u201c\u4e14\u6162\u52a8\u624b\uff1b\u4f60\u4e0e\u8fd9\u4f4d\u6c5f\u5c0f\u54e5\u6709 \u4f55\u8fc7\u8282\uff0c\u8bf4\u7ed9\u6211\u542c\u542c\uff01\u201d\u4ed6\u5bf9\u6c5f\u6d77\u5929\u7684\u79f0\u547c\u4ece\u201c\u5c0f\u8d3c\u201d\u3001\u201c\u5c0f\u5b50\u201d\u800c\u81f3\u201c\u5c0f\u54e5\u201d\uff0c\u654c\u610f\u662f\u8d8a \u6765\u8d8a\u51cf\u4e86\u3002 \u3000\u3000\u97e9\u7487\u9053\uff1a\u201c\u4e00\u8a00\u96be\u5c3d\uff0c\u6211\u53ea\u8bf4\u521a\u624d\u4e4b\u4e8b\uff0c\u5c31\u5728\u7247\u523b\u4e4b\u524d\uff1a\u4ed6\u521a\u521a\u6253\u4f24\u6211\u7684\u8001\u4f34\uff0c\u54e6\uff0c\u8fd8 \u6709\u8fd9\u4e2a\u5973\u8d3c\uff0c\u4e5f\u662f\u548c\u4ed6\u4e00\u9053\u3002\u54a6\uff0c\u5947\u602a\u2014\u2014\u201d\u4e91\u53ec\u8fde\u5fd9\u95ee\u9053\uff1a\u2018\u4ec0\u4e48\u4e8b\u5947\u602a\uff1f\u201d \u3000\u3000\u97e9\u7487\u5450\u5450\u8bf4\u9053\uff1a\u201c\u8fd9\uff0c\u8fd9\u4f4d\u59d1\u5a18\u2014\u2014\u201d\u4e91\u53ec\u5ffd\u5730\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u548c\u90a3\u4e2a\u5973\u8d3c\u4e0d\u5927 \u76f8\u4f3c\uff0c\u662f\u4e48\uff1f\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u97e9\u7487\u56fa\u7136\u662f\u5927\u611f\u60ca\u5947\uff0c\u6c5f\u6d77\u5929\u548c\u534e\u4e91\u78a7\u4e5f\u90fd\u89c9\u5f97\u5947\u602a\uff1b \u3000\u3000\u97e9\u7487\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e91\u5e84\u4e3b\uff1a\u4f60\u5982\u4f55\u77e5\u9053\uff1f\u201d\u4e91\u53ec\u9053\uff1a\u201c\u4f60\u8bf4\u7247\u523b\u4e4b\u524d\uff0c\u8fd9\u4f4d\u6c5f\u5c0f\u54e5\u66fe\u548c \u4f60\u4eec\u592b\u5987\u4ea4\u624b\u3002\u8bf7\u4f60\u8bf4\u5f97\u66f4\u786e\u5207\u4e9b\uff0c\u8fd9\u4e2a\u2018\u7247\u523b\u2019\uff0c\u53ef\u6709\u534a\u4e2a\u65f6\u8fb0\uff1f\u201d\u97e9\u7487\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4 \u9053\uff1a\u201c\u6211\u4eec\u548c\u4ed6\u52a8\u624b\u4e0d\u5230\u4e09\u5341\u62db\uff0c\u4ed6\u4f24\u4e86\u6211\u7684\u8001\u4f34\u4fbf\u9003\u4e86\u3002\u7b97\u6765\u4e0d\u591f\u534a\u4e2a\u65f6\u8fb0\u3002\u201d\u4e91\u53ec\u9053\uff1a \u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u662f\u770b\u9519\u4eba\u4e86\uff01\u5728\u8fd9\u534a\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u6c5f\u5c0f\u54e5\u6b63\u5728\u548c\u6211\u4ea4\u624b\uff0c\u4ed6\u51b3\u4e0d\u80fd\u5206\u8eab\u518d\u4e0e\u4f60 \u4eec\u5bf9\u654c\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53ea\u89c1\u6797\u5b50\u91cc\u53c8\u51fa\u6765\u4e86\u4e00\u4e2a\u4eba\uff0c\u662f\u4e2a\u4e94\u5341\u5c81\u5de6\u53f3\u7684\u5987\u4eba\uff0c\u624b\u62b1\u94c1\u7435\u7436\u3002\u811a\u6b65\u8e09 \u8dc4\uff0c\u4e00\u8df7\u4e00\u62d0\u7684\u6c14\u547c\u547c\u5730\u9053\uff1a\u201c\u53c8\u78b0\u4e0a\u8fd9\u4e2a\u5c0f\u8d3c\u5566\uff0c\u4f60\u600e\u4e48\u8fd8\u4e0d\u52a8\u624b\uff1f\u201d\u97e9\u7487\u9053\uff1a\u201c\u4f60\u5148\u522b \u6025\uff0c\u5148\u6765\u89c1\u8fc7\u4e91\u5e84\u4e3b\u3002\u201d \u3000\u3000\u90a3\u5987\u4eba\u9053\uff0c\u201c\u662f\u4e91\u53ec\uff0c\u4e91\u8001\u82f1\u96c4\u5417\uff1f\u5de7\u6781\u4e86\uff0c\u6211\u4eec\u6b63\u60f3\u5230\u8d35\u5e84\u6c42\u63f4\u3002\u6211\u7ed9\u8fd9\u5c0f\u8d3c\u6253\u4f24 \u4e86\u3002\u201d\u8fd9\u5987\u4eba\u6b63\u662f\u97e9\u7487\u7684\u59bb\u5b50\uff0c\u97e9\u7487\u4e0e\u4e91\u53ec\u662f\u8001\u670b\u53cb\uff0c\u5979\u5374\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\u4e91\u53ec\u3002 \u3000\u3000\u4e91\u53ec\u9053\uff1a\u201c\u97e9\u4e8c\u5ac2\uff0c\u8fd9\u4e8b\u60c5\u6709\u70b9\u53e4\u602a\uff01\u201d\u97e9\u4e8c\u5a18\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u53e4\u602a\uff0c\u4ed6\u4f24\u4e86\u6211\uff0c\u70e7\u53d8\u4e86 \u7070\u6211\u4e5f\u8ba4\u5f97\u4ed6\u3002\u201d\u4e91\u53ec\u9053\uff1a\u201c\u6211\u521a\u624d\u4e5f\u81ea\u4fe1\u8001\u773c\u65e0\u82b1\uff0c\u4f46\u73b0\u5728\u5374\u4e0d\u6562\u8bf4\u4e86\uff0c\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u524d\uff0c \u6709\u4e2a\u76f8\u8c8c\u548c\u8fd9\u4f4d\u6c5f\u5c0f\u54e5\u4e00\u6a21\u4e00\u6837\u7684\u4eba\uff0c\u5077\u95ef\u5bd2\u820d\uff0c\u60f3\u6697\u7b97\u6211\u7684\u743c\u513f\u3001\u58c1\u513f\uff0c\u6211\u5927\u7ea6\u662f\u8ffd\u9519\u4e86 \u65b9\u5411\uff0c\u8ffd\u4e0a\u4e86\u8fd9\u4f4d\u6c5f\u5c0f\u54e5\u3002\u5728\u8fd9\u534a\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u6211\u4e0e\u4ed6\u7cca\u91cc\u7cca\u6d82\u7684\u6076\u6597\u4e86\u4e00\u573a\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u4e00\u76f4\u5728\u65c1\u6c89\u601d\uff0c\u8fd9\u65f6\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u8fd9\u6ca1\u6709\u4ec0\u4e48\u5947\u602a\uff0c\u6539\u5bb9\u6613\u8c8c\u4e4b\u672f\uff0c\u8001\u592b\u4e5f\u4f1a\u3002 \u90a3\u59d3\u53f6\u7684\u5c0f\u8d3c\u4e0e\u6211\u7684\u5e72\u513f\u6d77\u5929\u6628\u65e5\u66fe\u7ecf\u4ea4\u624b\uff0c\u60f3\u6765\u4ed6\u4e5f\u806a\u660e\u5f97\u5f88\uff0c\u9884\u5148\u6599\u6211\u4eec\u4f1a\u5230\u4e91\u5bb6\u5e84\u6c42 \u533b\uff0c\u6545\u6b64\u53d8\u5316\u9762\u8c8c\uff0c\u5047\u5192\u6d77\u5929\u5230\u4e91\u5bb6\u5e84\u95f9\u4e8b\u3002\u53ef\u60dc\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u7a81\u7136\u505c\u6b62\u3002 \u3000\u3000\u4e91\u53ec\u6ee1\u9762\u5c34\u5c2c\uff0c\u8fde\u9053\u4e86\u4e24\u58f0\u201c\u60ed\u6127\uff01\u201d\u7136\u540e\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53ef\u60dc\u6211\u90a3\u7ba1\u5bb6\u6709\u773c\u65e0\u73e0\uff0c\u672a \u66fe\u5c06\u4f60\u4eec\u7559\u4e0b\u6765\u3002\u8981\u4e0d\u7136\u5c31\u53ef\u6f14\u4e00\u51fa\u771f\u674e\u9035\u89c1\u5047\u674e\u9035\u7684\u597d\u620f\u4e86\u3002\u4e0d\u8fc7\uff0c\u4e5f\u4e0d\u80fd\u5168\u602a\u6211\u90a3\u7ba1 \u5bb6\uff0c\u8fd9\u91cc\u9762\u8fd8\u6709\u4e2a\u7f18\u6545\u3002\u55ef\uff0c\u8fd8\u662f\u8bf7\u5230\u5bd2\u820d\u518d\u8bf4\u5427\uff01\u534e\u8001\u5148\u751f\uff0c\u6211\u4e91\u53ec\u8fd9\u53a2\u7ed9\u4f60\u8d54\u5229\u4e86\u3002\u52a1 \u6c42\u4f60\u4e0d\u8981\u89c1\u602a\uff0c\u6551\u6551\u6211\u7684\u5b69\u5b50\uff01\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u4e91\u5e84\u4e3b\u8a00\u91cd\u4e86\u3002\u8001\u673d\u6b63\u8981\u6258\u5e87\u8d35\u5e84\uff0c\u82e5\u6709\u7528 \u5230\u8001\u673d\u4e4b\u5904\uff0c\u6562\u4e0d\u5c3d\u529b\u3002\u201d \u3000\u3000\u97e9\u7487\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u4e91\u5e84\u4e3b\uff0c\u4ee4\u90ce\u4ee4\u5a9b\u53d7\u4e86\u4f55\u4eba\u6240\u4f24\uff1f\u201d\u4e91\u53ec\u9053\uff1a\u201c\u73b0\u5728\u8fd8\u672a\u786e\u5207 \u77e5\u9053\uff0c\u4f46\u770b\u6765\u4e5d\u6210\u5c31\u662f\u4f24\u4e86\u4f60\u4e8c\u5ac2\u7684\u90a3\u4e2a\u8d3c\u4eba\u3002\u201d\u5f53\u4e0b\uff0c\u534e\u5929\u98ce\u548c\u97e9\u7487\u8fd9\u4e24\u4f19\u4eba\u90fd\u968f\u7740\u4e91\u53ec \u56de\u5bb6\u3002 \u3000\u3000\u5728\u8def\u9014\u4e2d\uff0c\u4e91\u53ec\u624d\u6709\u529f\u5927\u5c06\u4ed6\u5bb6\u7684\u906d\u9047\u8bf4\u51fa\u6765\uff0c\u539f\u6765\u6628\u65e5\u5728\u7941\u8fde\u5c71\u4e2d\u4e0e\u90a3\u201c\u4e91\u516c\u5b50\u201d\u906d \u9047\u7684\u90a3\u5bf9\u5c11\u5e74\u7537\u5973\uff0c\u5c31\u662f\u4ed6\u7684\u513f\u5b50\u4e91\u743c\u548c\u5973\u513f\u4e91\u58c1\u3002\u4ed6\u4eec\u5144\u59b9\u90fd\u53d7\u4e86\u5927\u4e58\u822c\u82e5\u638c\u6240\u4f24\uff0c\u4e91\u743c \u529f\u529b\u8f83\u9ad8\uff0c\u5c06\u59b9\u59b9\u80cc\u4e86\u56de\u5bb6\uff0c\u4f46\u4e00\u5230\u5bb6\u4e2d\u4ea6\u5df2\u662f\u652f\u6301\u4e0d\u4f4f\u3002\u53ea\u8bf4\u5f97\u4e24\u53e5\u534a\u8bdd\u4fbf\u5373\u660f\u8ff7\u4e86\u3002\u90a3 \u4e24\u53e5\u534a\u8bdd\u662f\uff1a\u201c\u7239\u7239\u7ed9\u6211\u62a5\u4ec7\uff0c\u4ec7\u4eba\u662f\u4e2a\u5341\u516d\u4e03\u5c81\u7684\u5c11\u5e74\uff0c\u8fd8\u6709\u4e2a\u5973\u5b50\u2026\u2026\u201d\u90a3\u5973\u5b50\u5e74\u7eaa\u82e5 \u4f55\uff0c\u76f8\u8c8c\u600e\u6837\uff0c\u90fd\u672a\u66fe\u8bf4\u51fa\u3002 \u3000\u3000\u4e91\u53ec\u8bbe\u5c3d\u6cd5\u5b50\u6551\u6cbb\uff0c\u90fd\u65e0\u6548\u679c\u3002\u6b63\u5728\u4e91\u5bb6\u9616\u5bb6\u4e0d\u5b89\u3001\u4eba\u5fc3\u60f6\u60f6\u7684\u65f6\u5019\uff0c\u6c5f\u6d77\u5929\u548c\u534e\u4e91\u78a7 \u80cc\u4e86\u534e\u5929\u98ce\u5230\u6765\u6295\u9760\uff0c\u4e91\u5bb6\u7684\u4eba\u4e00\u6765\u56e0\u4e3a\u5bb6\u4e2d\u53d1\u751f\u4e86\u8fd9\u7b49\u5927\u4e8b\uff0c\u4e0d\u60f3\u518d\u53bb\u70e6\u5fe7\u4e91\u53ec\uff1b\u4e8c\u6765\u6c5f \u6d77\u5929\u53c8\u6b63\u662f\u4e2a\u5341\u516d\u4e03\u5c81\u7684\u5c11\u5e74\uff0c\u90a3\u7ba1\u5bb6\u7684\u5fc3\u6709\u6240\u7591\uff0c\u4fbf\u64c5\u4f5c\u4e3b\u5f20\uff0c\u5148\u62d2\u7edd\u4e86\u518d\u884c\u7980\u62a5\uff1a\u4e09 \u6765\uff0c\u90a3\u7ba1\u5bb6\u4e5f\u4e0d\u76f8\u4fe1\u534e\u5929\u98ce\u771f\u662f\u201c\u534e\u5c71\u533b\u9690\u201d\uff0c\u53ea\u9053\u4ed6\u4eec\u662f\u6545\u610f\u501f\u7740\u6709\u75c5\u4eba\u800c\u6765\u201c\u8d5a\u95e8\u201d\u7684\u3002 \u3000\u3000\u4e91\u53ec\u542c\u4e86\u90a3\u7ba1\u5bb6\u7684\u7980\u62a5\u4e4b\u540e\uff0c\u4e5f\u6709\u70b9\u6000\u7591\u6c5f\u6d77\u5929\u4fbf\u662f\u4ed6\u513f\u5b50\u6240\u8bf4\u7684\u90a3\u4e2a\u5c11\u5e74\uff0c\u7279\u610f\u6765\u63a2 \u542c\u60c5\u5f62\u7684\uff0c\u672c\u6765\u4e91\u53ec\u5c31\u8981\u8ffd\u51fa\u53bb\u7684\uff0c\u4f46\u4e3a\u4e86\u5b88\u62a4\u513f\u5973\uff0c\u5374\u8fd8\u672a\u4fbf\u79bb\u5f00\u3002\u60f3\u4e0d\u5230\u5c31\u5728\u8bae\u8bba\u4e4b \u95f4\uff0c\u4e00\u5bf9\u5c11\u5e74\u7537\u5973\u5ffd\u5730\u524d\u6765\u5077\u88ad\uff0c\u7537\u7684\u5728\u524d\uff0c\u5973\u7684\u5728\u540e\uff0c\u90a3\u7ba1\u5bb6\u4e00\u89c1\uff0c\u4fbf\u60ca\u53eb\u8d77\u6765\uff0c\u539f\u6765\u90a3 \u5973\u7684\u9762\u8c8c\u672a\u770b\u5f97\u5206\u660e\uff0c\u90a3\u7537\u7684\u9762\u8c8c\uff0c\u5374\u662f\u548c\u6c5f\u6d77\u5929\u5341\u5206\u76f8\u4f3c\u3002\u90a3\u7ba1\u5bb6\u7684\u4e00\u65f6\u95f4\u60f3\u4e0d\u5230\u6709\u6539\u5bb9 \u6613\u8c8c\u4e4b\u672f\uff0c\u53ea\u9053\u662f\u521a\u624d\u6c42\u5bbf\u7684\u90a3\u5bf9\u7537\u5973\u53bb\u800c\u590d\u6765\uff0c \u3000\u3000\u4e91\u53ec\u4e00\u8bb0\u5288\u7a7a\u638c\u6253\u51fa\uff0c\u5c06\u90a3\u5c11\u5e74\u7684\u4e00\u624b\u4e09\u6697\u5668\u5168\u90fd\u8361\u5f00\uff0c\u90a3\u7537\u7684\u4e00\u51fb\u4e0d\u4e2d\u3002\u8fd8\u4e86\u4e00\u8bb0\u5288 \u7a7a\u638c\uff0c\u7acb\u5373\u4fbf\u548c\u90a3\u5c11\u5973\u9003\u8d70\u4e86\u3002\u4e91\u53ec\u4e5f\u662f\u81ea\u8d1f\u8fc7\u751a\uff0c\u53ea\u9053\u51ed\u7740\u4ed6\u7684\u91d1\u521a\u638c\u529b\uff0c\u4e8c\u8bb0\u5288\u7a7a\u638c\u5373 \u53ef\u628a\u90a3\u5bf9\u7537\u5973\u6253\u4e0b\u6765\uff0c\u54ea\u77e5\u8fd9\u5c11\u5e74\u7684\u529f\u529b\u7adf\u51fa\u4e4e\u4ed6\u7684\u610f\u6599\u4e4b\u5916\uff0c \u3000\u3000\u6b63\u56e0\u4e91\u53ec\u4e00\u5ff5\u8f7b\u654c\uff0c\u8ffd\u51fa\u53bb\u65f6\u5df2\u8fdf\u4e86\u4e00\u6b65\uff0c\u90a3\u65f6\u5c11\u5e74\u7537\u5973\u65e9\u5df2\u8d70\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002\u4e91\u53ec\u8ddf\u7740 \u5730\u4e0a\u7684\u8db3\u5370\u8ffd\u53bb\uff0c\u4fbf\u8ffd\u4e0a\u4e86\u6c5f\u6d77\u5929\u8fd9\u4e00\u4f19\u3002 \u3000\u3000\u534e\u4e91\u78a7\u7b11\u9053\uff1a\u201c\u4e91\u8001\u82f1\u96c4\uff0c\u5f53\u4f60\u4e0e\u6211\u6d77\u54e5\u4ea4\u624b\u4e4b\u65f6\uff0c\u662f\u5426\u5df2\u770b\u51fa\u4e86\u6211\u7684\u9762\u8c8c\u4e0e\u90a3\u5973\u8d3c\u6709 \u522b\uff0c\u201d \u3000\u3000\u4e91\u53ec\u9053\uff1a\u201c\u6b63\u662f\u56e0\u6b64\uff0c\u6240\u4ee5\u6211\u521a\u624d\u5bf9\u6c5f\u5c0f\u54e5\u5df2\u5c3d\u5168\u529b\uff0c\u5bf9\u59d1\u5a18\u5374\u672a\u6562\u65bd\u5c55\u6740\u624b\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u53c8\u7b11\u9053\uff1a\u201c\u90a3\u5973\u7684\u662f\u5426\u74dc\u5b50\u8138\u513f\uff0c\u53cc\u7709\u5165\u9b13\uff0c\u9887\u6709\u70b9\u5996\u51b6\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u4e91\u53ec\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u867d\u7136\u770b\u4e0d\u771f\u5979\u662f\u5426\u5996\u51b6\uff0c\u4f46\u8138\u578b\u5374\u662f\u74dc\u4e8e\u8138\u513f\u3002\u59d1\u5a18\uff0c\u542c\u4f60\u8fd9\u4e48 \u8bf4\uff0c\u4f60\u4f3c\u4e4e\u5df2\u77e5\u9053\u8fd9\u5973\u8d3c\u662f\u8c01\u4e86\uff1f\u201d \u3000\u3000\u534e\u4e91\u78a7\u9053\uff1a\u201c\u4f9d\u6211\u770b\u672a\uff0c\u8fd9\u5996\u5973\u5fc5\u662f\u6b27\u9633\u5a49\u65e0\u7591\u3002\u201d\u6c5f\u6d77\u5929\u4e5f\u6b63\u81ea\u6709\u6b64\u6000\u7591\uff0c\u542c\u4e86\u4e4b \u540e\uff0c\u66f4\u662f\u5fc3\u4e71\u5982\u9ebb\uff1a\u5341\u5206\u96be\u8fc7\u3002 \u3000\u3000\u4e91\u53ec\u9053\uff1a\u201c\u6b27\u9633\u5a49\uff1f\u53ef\u662f\u7ec8\u5357\u5c71\u6b27\u9633\u5bb6\u7684\u4eba\u4e48\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u6b27\u9633\u5a49\u6b63\u662f\u6b27 \u9633\u4ef2\u548c\u7684\u5973\u513f\u3002\u78a7\u513f\uff0c\u4f60\u4e5f\u5c06\u54b1\u4eec\u7684\u906d\u9047\u544a\u8bc9\u4e91\u5e84\u4e3b\u5427\u3002\u201d \u3000\u3000\u5f53\u4e0b\uff0c\u534e\u4e91\u78a7\u4ece\u5934\u8bf4\u8d77\uff0c\u5c06\u84b2\u5362\u864e\u4e0e\u6b27\u9633\u4ef2\u548c\u8054\u624b\u5411\u5979\u7236\u4eb2\u5bfb\u4ec7\uff0c\u6b27\u9633\u5a49\u6765\u76d7\u836f\u56ca\uff0c\u4ee5 \u53ca\u540e\u6765\u600e\u6837\u8eb2\u5728\u8305\u8349\u4e1b\u4e2d\uff0c\u542c\u5f97\u90a3\u5bf9\u7537\u5973\u8d3c\u4eba\u4e0e\u4e91\u743c\u4e91\u58c1\u6076\u6597\u7b49\u7b49\u60c5\u8282\u5168\u90fd\u8bf4\u4e86\u3002\u7136\u540e\u8fd8\u52a0 \u4e0a\u81ea\u5df1\u7684\u63a8\u6d4b\u9053\uff1a\u201c\u60f3\u5fc5\u662f\u540e\u6765\u90a3\u5bf9\u8d3c\u7537\u5973\u78b0\u89c1\u4e86\u6b27\u9633\u4e8c\u5a18\u6bcd\u5973\u3002\u90a3\u59d3\u53f6\u7684\u6076\u8d3c\u4fbf\u5c06\u53d7\u4e86\u4f24 \u7684\u5973\u8d3c\u4ea4\u7ed9\u6b27\u9633\u4e8c\u5a18\u7167\u6599\u3002\u800c\u4ed6\u5374\u501f\u4e86\u6b27\u9633\u5a49\uff0c\u53eb\u6b27\u9633\u5a49\u626e\u6210\u6211\u7684\u6837\u5b50\uff0c\u4ed6\u5219\u626e\u6210\u6211\u6d77\u54e5\u7684 \u6837\u5b50\uff0c\u524d\u6765\u5b9d\u5e84\uff0c\u65bd\u8fd9\u4e00\u77f3\u4e8c\u9e1f\u3001\u5192\u540d\u5bb3\u4eba\u7684\u6076\u6bd2\u8ba1\u8c0b\uff01\u5e78\u4e8f\u90a3\u5996\u5973\u7684\u9762\u8c8c\u4e0e\u6211\u5927\u4e0d\u76f8\u540c\uff0c \u626e\u5f97\u4e0d\u50cf\uff0c\u8981\u4e0d\u7136\u6211\u5c31\u6c89\u51a4\u83ab\u767d\u4e86\uff01\u201d \u3000\u3000\u4e91\u53ec\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u6b27\u9633\u4e8c\u5a18\u7d20\u6765\u9634\u72e0\u6bd2\u8fa3\uff0c\u6709\u5176\u6bcd\u5fc5\u6709\u5176\u5973\uff0c\u8fd9\u5b9a\u7136\u662f\u4ed6\u4eec\u5e72\u7684 \u4e86\u3002\u5e78\u4e8f\u9047\u5230\u4e86\u4f60\u4eec\uff0c\u5f97\u5230\u4e86\u8fd9\u6761\u7ebf\u7d22\u3002\u5f85\u6211\u513f\u5973\u4f24\u597d\u4e4b\u540e\uff0c\u8001\u592b\u4eb2\u81ea\u5230\u7ec8\u5357\u5c71\u53bb\uff0c\u5c31\u7740\u843d \u5728\u6b27\u9633\u4ef2\u548c\u7684\u8eab\u4e0a\uff0c\u603b\u8981\u8ffd\u67e5\u51fa\u90a3\u59d3\u53f6\u7684\u51f6\u624b\u6765\u3002\u201d \u3000\u3000\u97e9\u7487\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u4e0e\u90a3\u59d3\u53f6\u7684\u4e00\u9053\u7684\u90a3\u4e2a\u5973\u8d3c\u76f8\u8c8c\u5982\u4f55\uff1f\u662f\u5426\u4e00\u4e2a\u4e09\u5341\u5c81\u5de6\u53f3\u7684\u4e2d\u5e74\u5987 \u4eba\uff0c\u90a3\u59d3\u53f6\u7684\u5bf9\u5979\u5982\u4f55\u79f0\u547c\uff1f \u3000\u3000\u534e\u4e91\u78a7\u9053\uff1a\u201c\u76f8\u8c8c\u6211\u4eec\u770b\u4e0d\u6e05\u695a\uff0c\u542c\u5979\u7684\u58f0\u97f3\uff0c\u4ece\u80cc\u540e\u770b\u5979\u7684\u4f53\u6001\uff0c\u7684\u786e\u50cf\u662f\u4e2a\u4e2d\u5e74\u5987 \u4eba\u3002\u5979\u53eb\u90a3\u7537\u7684\u505a\u2018\u53f6\u516c\u5b50\u2019\u3002\u90a3\u7537\u7684\u53eb\u5979\u505a\u2018\u7a46\u5927\u59d0\u2019\u3002\u201d \u3000\u3000\u97e9\u7487\u53cc\u638c\u4e00\u62cd\uff0c\u8bf4\u9053\uff1a\u201c\u5c31\u5bf9\u4e86\u3002\u521a\u624d\u6211\u4eec\u4e0e\u8fd9\u5bf9\u8d3c\u7537\u5973\u4ea4\u624b\u4e4b\u65f6\uff0c\u6211\u5df2\u662f\u6709\u70b9\u6000\u7591\uff0c \u8fd9\u5973\u8d3c\u4e0d\u8be5\u5982\u6b64\u5e74\u8f7b\uff0c\u6b66\u529f\u4e5f\u4f3c\u4e4e\u7a0d\u5f31\u4e00\u4e9b\u3002\u201d \u3000\u3000\u4e91\u53ec\u95ee\u9053\uff1a\u201c\u97e9\u4e8c\u54e5\uff0c\u4f60\u4e0e\u4ed6\u4eec\u53c8\u4e3a\u4e86\u4f55\u4e8b\u7ed3\u4ec7\uff0c\u542c\u4f60\u6240\u8bf4\uff0c\u4f60\u4e0e\u4ed6\u4eec\u4f3c\u4e4e\u8fd8\u662f\u7b2c\u4e00\u6b21 \u4ea4\u624b\uff0c\u5374\u53c8\u600e\u77e5\u9053\u90a3\u5973\u8d3c\u6b66\u529f\u7684\u6df1\u6d45\uff1f\u201d \u3000\u3000\u97e9\u7487\u9053\uff1a\u201c\u6211\u672c\u6765\u5728\u5317\u4eac\u5f00\u7740\u9547\u8fdc\u9556\u5c40\uff0c\u53bb\u5e74\u6211\u4eec\u9556\u5c40\u66ff\u9102\u5c14\u6c81\u65d7\u7684\u571f\u738b\u62a4\u9001\u4e00\u6279\u836f\u6750 \u5230\u9752\u6d77\u53bb\uff0c\u9014\u4e2d\u9047\u52ab\uff0c\u6211\u4eec\u7684\u4eba\u9664\u4e86\u9648\u7559\u7c4d\u7684\u4e24\u4e2a\u9556\u5e08\u4e4b\u5916\uff0c\u5176\u4ed6\u7684\u5168\u90e8\u906d\u4e86\u6bd2\u624b\uff0c\u9547\u8fdc\u9556 \u5c40\u56e0\u6b64\u53ea\u5f97\u592b\u4e86\u5927\u95e8\u3002\u52ab\u9556\u7684\u4eba\u4fbf\u662f\u90a3\u59d3\u7a46\u7684\u5973\u8d3c\uff0c\u6211\u4eec\u592b\u59bb\u4e3a\u4e86\u66ff\u9556\u5c40\u7684\u5144\u5f1f\u62a5\u4ec7\uff0c\u8ffd\u5bfb \u90a3\u5973\u8d3c\u5df2\u6709\u4e00\u5e74\u591a\u4e86\uff01\u201d \u3000\u3000\u4e91\u53ec\u60ca\u9053\uff1a\u201c\u9547\u8fdc\u9556\u5c40\u5a01\u540d\u8fdc\u632f\uff0c\u60f3\u4e0d\u5230\u7adf\u9047\u5230\u8fd9\u5b97\u7978\u4e8b\uff01\u4f46\u6211\u6709\u4e00\u4e8b\u4e0d\u660e\uff0c\u4f55\u4ee5\u90a3\u4e24 \u4e2a\u9648\u7559\u7c4d\u7684\u9556\u5e08\u5374\u80fd\u5e78\u514d\uff1f\u201d \u3000\u3000\u97e9\u6f29\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u4e5f\u5f04\u4e0d\u660e\u767d\uff0c\u4ed6\u4eec\u88ab\u64d2\u4e4b\u540e\uff0c\u90a3\u59d3\u7a46\u7684\u5973\u8d3c\u8981\u4ed6\u4eec\u642c\u8fd0\u836f\u6750\uff0c\u9001\u5230\u4e00 \u5904\u5c71\u5be8\uff0c\u8fd9\u65f6\u5019\u5c31\u6765\u4e86\u90a3\u59d3\u53f6\u7684\u5c0f\u8d3c\uff0c\u4ed6\u4e00\u542c\u8fd9\u4e24\u4e2a\u9556\u5e08\u8bf4\u7684\u662f\u9648\u7559\u53e3\u97f3\uff0c\u5c31\u628a\u4ed6\u4eec\u653e\u4e86\u3002 \u636e\u8fd9\u4e24\u4e2a\u9556\u5e08\u8bf4\uff0c\u90a3\u5c0f\u8d3c\u4e5f\u5e26\u70b9\u9648\u76ee\u7684\u4e61\u97f3\uff0c\u5927\u7ea6\u662f\u770b\u5728\u540c\u4e61\u7684\u9762\u4e0a\uff0c\u6545\u6b64\u5c06\u4ed6\u4eec\u653e\u4e86\u3002\u201d \u3000\u3000\u97e9\u7487\u5f04\u4e0d\u660e\u767d\uff0c\u6c5f\u6d77\u5929\u542c\u4e86\uff0c\u5374\u662f\u5fc3\u5934\u4e00\u9707\u3002\u8fd9\u4e2a\u6545\u4e8b\uff0c\u4ed6\u662f\u65e9\u5c31\u542c\u5f97\u767d\u82f1\u6770\u8bf4\u8fc7\u4e86 \u7684\uff0c\u5fc3\u91cc\u4e0d\u7981\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5341\u4e8c\u5e74\u524d\uff0c\u9648\u7559\u53bf\u7684\u53f6\u541b\u5c71\u7a81\u7136\u66b4\u6bd9\uff0c\u4ed6\u6536\u517b\u7684\u4e00\u4e2a\u5b64\u513f\u4e5f\u79bb\u5947 \u5931\u8e2a\uff0c\u636e\u767d\u82f1\u6770\u7684\u5224\u65ad\uff0c\u8fd9\u59d3\u53f6\u7684\u5c11\u5e74\u53ef\u80fd\u4fbf\u662f\u90a3\u4e2a\u5b64\u513f\uff0c\u90a3\u4e24\u4e2a\u9556\u5e08\u662f\u53f6\u541b\u5c71\u7684\u4e61\u4eb2\uff0c\u4ed6 \u662f\u770b\u5728\u65f6\u541b\u5c71\u7684\u9762\u4e0a\u5c06\u4ed6\u4eec\u653e\u7684\u3002\u5509\uff0c\u7cdf\u7cd5\uff0c\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u6240\u78b0\u89c1\u7684\u8fd9\u4f4d\u2018\u53f6\u516c\u5b50\u2019\u5c82\u4e0d\u6b63 \u662f\u8c37\u4e2d\u83b2\u7684\u5b6a\u751f\u5144\u5f1f\uff0c\u602a\u4e0d\u5f97\u770b\u6765\u4f3c\u66fe\u76f8\u8bc6\uff01\u201d \u3000\u3000\u8981\u77e5\u8c37\u4e2d\u83b2\u7684\u8eab\u4e16\u867d\u672a\u5927\u767d\uff0c\u4f46\u5f53\u5e74\u7ffc\u4ef2\u725f\u5728\u4e18\u5ca9\u624b\u4e2d\u5c06\u5979\u63a5\u8fc7\u6765\u7684\u65f6\u5019\uff0c\u4e18\u5ca9\u66fe\u7ecf\u8bf4 \u8fc7\u5979\u6709\u4e2a\u5b6a\u751f\u5144\u5f1f\u53d7\u53f6\u541b\u5c71\u6536\u517b\uff0c\u8fd9\u662f\u4e18\u5ca9\u4e34\u6b7b\u4e4b\u65f6\u6240\u8bf4\u7684\u8bdd\uff0c\u60f3\u6765\u51b3\u4e0d\u662f\u80e1\u4e71\u634f\u9020\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u53c8\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4ed6\u4e00\u89c1\u6211\uff0c\u5c31\u65e5\u65e5\u58f0\u58f0\u8bf4\u662f\u5bf9\u6211\u5e76\u65e0\u6076\u610f\uff0c\u53ea\u662f\u8981\u76d8\u95ee\u6211\u4e00\u4ef6 \u4e8b\u60c5\u3002\u60f3\u6765\u5c31\u662f\u8981\u63a2\u542c\u4ed6\u59b9\u59b9\u7684\u6d88\u606f\u4e86\u3002\u53ef\u60dc\u4ed6\u592a\u5f3a\u6a2a\uff0c\u800c\u6211\u53c8\u4e00\u76f4\u628a\u4ed6\u5f53\u4f5c\u7a77\u51f6\u6781\u6076\u7684\u532a \u5f92\uff0c\u4ee5\u81f4\u4e00\u8a00\u4e0d\u5408\uff0c\u4fbf\u5373\u4ea4\u624b\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6000\u7591\u4e0d\u5b9a\uff0c\u5fc3\u4e8b\u5982\u6f6e\u3002\u4f46\u56e0\u8fd9\u6709\u5173\u8c37\u4e2d\u83b2\u8eab\u4e16\u4e4b\u8c1c\uff0c\u8c37\u4e4b\u534e\u66fe\u53ee\u5631\u8fc7\u4ed6\u7684\u5e08\u7236\uff0c \u4ed6\u7684\u5e08\u7236\u5219\u53ee\u5631\u8fc7\u4ed6\uff0c\u51b3\u4e0d\u53ef\u5411\u5916\u4eba\u6cc4\u6f0f\u7684\uff0c\u800c\u4e14\u8fd9\u59d3\u65f6\u7684\u65e2\u4f24\u4e86\u4e91\u53ec\u5b50\u5973\uff0c\u53c8\u4f24\u4e86\u97e9\u7487\u59bb \u5b50\uff0c\u6c5f\u6d77\u5929\u4e5f\u4e0b\u6562\u5c06\u4ed6\u7684\u6765\u5386\u5728\u4ed6\u4eec\u9762\u524d\u8bf4\u51fa\u6765\uff0c\u53ea\u662f\u4e3a\u8c37\u4e2d\u83b2\u6709\u8fd9\u6837\u4e00\u4e2a\u54e5\u54e5\u800c\u611f\u5230\u96be \u8fc7\u3002\u5fc3\u4e2d\u6697\u81ea\u9053\uff0c\u201c\u8fd9\u4e8b\u6211\u7ec8\u987b\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\uff0c\u76fc\u53ea\u76fc\u8fd9\u59d3\u53f6\u7684\u4e0d\u8981\u771f\u662f\u83b2\u59b9\u7684\u54e5\u54e5\u3002\u8981\u4e0d \u7136\u3002\u5018\u82e5\u7ed9\u83b2\u59b9\u77e5\u9053\uff0c\u5979\u4e00\u5b9a\u6bd4\u6211\u66f4\u96be\u8fc7\u4e86\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7684\u5fc3\u4e8b\u6309\u4e0b\u4e0d\u8868\u3002\u4e14\u8bf4\u4e91\u53ec\u542c\u4e86\uff0c\u5374\u5fae\u9732\u8be7\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u8fd9\u59d3\u53f6\u7684\u5c0f \u8d3c\u867d\u7136\u51f6\u6076\u3002\u5374\u4e0d\u662f\u4f60\u4eec\u9556\u5c40\u7684\u4ec7\u4eba\u554a\uff01\u201d \u3000\u3000\u97e9\u7487\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u592b\u59bb\u592b\u4e86\u9556\u5c40\u4e4b\u540e\uff0c\u5c31\u6765\u5230\u897f\u5317\u5230\u5904\u8bbf\u67e5\uff0c\u672c\u6765\u4e5f\u53ea\u662f\u60f3\u627e\u90a3\u5973 \u8d3c\u62a5\u4ec7\u7684\uff0c\u6628\u5929\u6211\u4eec\u5f97\u5230\u8fd9\u5973\u8d3c\u5728\u8fd9\u6761\u8def\u4e0a\u51fa\u73b0\u7684\u6d88\u606f\uff0c\u5c31\u8d76\u5fd9\u8ffd\u6765\uff0c\u60f3\u4e0d\u5230\u6ca1\u6709\u78b0\u5230\u6b63\u70b9 \u513f\uff0c\u5374\u78b0\u5230\u4e86\u8fd9\u59d3\u53f6\u7684\u5c0f\u8d3c\u3002\u201d\u6b63\u662f\uff1a \u3000\u3000\u964c\u8def\u76f8\u9022\u9f99\u864e\u6597\uff0c\u662f\u4ec7\u662f\u53cb\u5c1a\u96be\u660e\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u5ffd\u95fb\u60c5\u6d77\u751f\u6ce2\u6d6a\u3000\u53c8\u89c1\u4f0a\u4eba\u9001\u836f\u6765 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u5ffd\u95fb\u60c5\u6d77\u751f\u6ce2\u6d6a\u3000\u53c8\u89c1\u4f0a\u4eba\u9001\u836f\u6765 \u3000\u3000\u97e9\u7487\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u90a3\u65f6\u6708\u8272\u660f\u6697\uff0c\u6211\u4eec\u62a5\u4ec7\u5fc3\u5207\uff0c\u4e00\u78b0\u4e0a\u4fbf\u5373\u52a8\u624b\u3001\u540e\u6765\u6211\u624d\u53d1\u89c9\u90a3\u5973 \u7684\u5e74\u7eaa\u4f3c\u4e4e\u6709\u70b9\u4e0d\u5bf9\u3001\u4f46\u90a3\u65f6\u53cc\u65b9\u90fd\u5df2\u4e0d\u80fd\u7f62\u624b\u4e86\u3002\u201d \u3000\u3000\u4e91\u53ec\u9053\uff1b\u201c\u4f60\u4eec\u4e00\u76f4\u90fd\u6765\u66fe\u4e0e\u5bf9\u65b9\u7b54\u8bdd\u4e48\u201d\u97e9\u7487\u9053\uff1b\u201c\u90a3\u59d3\u53f6\u7684\u5c0f\u8d3c\u65e9\u5df2\u77e5\u9053\u6211\u4eec\u7684\u6765 \u5386\uff0c\u6211\u4eec\u521a\u4e00\u73b0\u8eab\uff0c\u4ed6\u4fbf\u559d\u9053\uff1a\u2018\u662f\u94c1\u9e33\u9e2f\u97e9\u5bb6\u4e8c\u8001\u4e48\uff1f\u4f60\u4eec\u9547\u8fdc\u9556\u5c40\u7684\u4e09\u5341\u56db\u6761\u547d\u503a\u5411\u6211 \u8ba8\u5427\uff0c\u4e0e\u5979\u65e0\u5173\uff0e\u2019\u8fd9\u5c0f\u8d3c\u7684\u72e0\u4e4b\u6781\uff0c\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u638c\u529b\u5df2\u662f\u6392\u5c71\u5012\u6d77\u800c\u6765\u3002\u6211\u53ea\u9053\u4ed6\u662f\u8981\u5e87 \u62a4\u90a3\u4e2a\u5973\u7684\uff0c\u4ed6\u65e2\u7136\u628a\u547d\u503a\u63fd\u5728\u4ed6\u81ea\u5df1\u7684\u8eab\u4e0a\uff0c\u628a\u8bdd\u8bf4\u5c3d\uff0c\u628a\u4e8b\u505a\u7edd\uff0c\u6211\u4e5f\u53ea\u597d\u4e0e\u4ed6\u62fc\u547d \u5566\u3002\u201d \u3000\u3000\u97e9\u7487\u7684\u59bb\u5b50\u9053\uff1a\u201c\u6211\u6ca1\u6709\u4f60\u8fd9\u6837\u7ec6\u5fc3\uff0c\u6211\u6839\u672c\u5c31\u6ca1\u6709\u60f3\u5230\u8fbe\u5973\u7684\u4e0d\u662f\u6b63\u70b9\u513f\u3002\u5f53\u65f6\u6211\u53ea \u6015\u90a3\u5973\u7684\u9003\u8d70\uff0c\u5c31\u5728\u4f60\u53d1\u51fa\u94c1\u9e33\u9e2f\u7684\u65f6\u5019\u3002\u6211\u4e5f\u53d1\u51fa\u4e86\u94c1\u9e33\u9e2f\uff01\u201d\u8fd9\u94c1\u9e33\u9e2f\u662f\u4e00\u79cd\u6781\u9738\u9053\u7684 \u6697\u5668\uff0c\u5f62\u5982\u98de\u9e1f\uff0c\u5934\u5c16\u89d2\u957f\uff0c\u8179\u5185\u4e2d\u7a7a\uff0c\u5185\u85cf\u6885\u82b1\u9488\u3002\u4e00\u7269\u4e09\u7528\uff0c\u5c16\u524a\u5982\u540c\u5229\u5544\u7684\u5934\u90e8\u53ef\u7a7f \u654c\u4eba\u7684\u7435\u7436\u9aa8\uff1b\u5f62\u5982\u9e1f\u7ffc\u7684\u5c3e\u90e8\u53ef\u81ea\u52a8\u5f20\u5f00\u526a\u65ad\u654c\u4eba\u7684\u7b4b\u8109\uff1b\u53e6\u5916\u8fd8\u6709\u4ece\u8179\u4e2d\u55b7\u51fa\u4e13\u94bb\u7a74\u9053 \u7684\u6885\u82b1\u8ba1\u3002\u97e9\u7487\u592b\u59bb\u90fd\u5584\u4e8e\u4f7f\u7528\u8fd9\u79cd\u6697\u5668\uff0c\u6240\u4ee5\u6c5f\u6e56\u4e0a\u79f0\u4ed6\u4eec\u592b\u5987\u4f5c\u201c\u94c1\u9e33\u9e2f\u201d\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u66fe\u542c\u5e08\u7236\u8bf4\u8fc7\u5404\u5bb6\u5404\u6d3e\u7684\u5389\u5bb3\u6697\u5668\uff0c\u6df1\u77e5\u8fd9\u94c1\u9e33\u9e2f\u7684\u5389\u5bb3\u3002\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u7531\u5f97\u5403 \u4e86\u4e00\u60ca\u3001\u95ee\u9053\uff1b\u201c\u90a3\u5973\u8d3c\u53ef\u53d7\u4f24\u4e86\uff1f\u201d\u534e\u4e91\u78a7\u6a2a\u4e86\u4ed6\u4e00\u773c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f60\u5012\u5173\u5fc3\u5979\u554a\uff01\u201d \u3000\u3000\u97e9\u7487\u7684\u59bb\u5b50\u5374\u6ca1\u7559\u610f\uff0c\u63a5\u7eed\u8bf4\u9053\uff1a\u201c\u6ca1\u60f3\u5230\u90a3\u59d3\u53f6\u7684\u5c0f\u8d3c\u6b66\u529f\u597d\u5f97\u51fa\u5947\uff0c\u4ed6\u4e00\u8bb0\u5288\u7a7a\u638c \u5c06\u6885\u82b1\u9488\u626b\u843d\uff0c\u7adf\u7136\u628a\u6211\u5f53\u5bb6\u7684\u6697\u5668\u6309\u4f4f\uff0c\u53cd\u624b\u5c31\u5411\u6211\u6253\u6765\uff0c\u5c06\u6211\u7684\u6697\u5668\u4e5f\u78b0\u843d\u4e86\u3001\u6211\u8fd9\u6761 \u53f3\u817f\uff0c\u5c31\u662f\u7ed9\u6211\u81ea\u5df1\u7684\u94c1\u9e33\u9e2f\u6253\u4f24\u7684\uff0c\u5e78\u800c\u4ed6\u4e0d\u61c2\u5f97\u8fd0\u7528\u94c1\u9e33\u9e2f\uff0c\u53ea\u662f\u7ed9\u4ed6\u7684\u529b\u9053\u53cd\u9707\u56de \u6765\uff0c\u64e6\u4f24\u4e86\u4e00\u70b9\u76ae\u8089\u3002\u54fc\uff0c\u54fc\uff0c\u8981\u4e0d\u662f\u90a3\u7537\u7684\u51fa\u624b\uff0c\u90a3\u6b21\u8d3c\u5c82\u6b62\u53d7\u4f24\uff1f\u6211\u65e9\u8981\u4e86\u5979\u7684\u547d\u4e86\uff01\u201d \u3000\u3000\u534e\u4e91\u78a7\u95ee\u9053\uff1a\u201c\u90a3\u5973\u8d3c\u4f24\u5f97\u91cd\u4e48\uff1f\u201d\u97e9\u4e8c\u5a18\u9893\u7136\u8bf4\u9053\uff1b\u201c\u53ea\u662f\u7ed9\u94c1\u9e33\u9e2f\u7684\u5c3e\u90e8\u524a\u53bb\u4e86\u4e00 \u5c0f\u7247\u8033\u6735\uff0c\u94c1\u9e33\u9e2f\u672c\u6765\u53ef\u4ee5\u7ffb\u817e\u8fc7\u6765\u518d\u7a7f\u5979\u7684\u7435\u7436\u9aa8\u7684\u3001\u4f46\u5df2\u7ed9\u90a3\u7537\u7684\u53cd\u9707\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u97e9\u7487\u7b11\u9053\uff1a\u201c\u5e78\u800c\u4f60\u524a\u53bb\u4e86\u5979\u4e00\u5c0f\u7247\u8033\u6735\uff0c\u628a\u5979\u5413\u8dd1\u3001\u90a3\u7537\u7684\u624d\u8ddf\u7740\u8dd1\u4e86\u3001\u8981\u4e0d\u7136\u54b1\u4eec \u53ea\u6015\u8fd8\u672a\u5fc5\u662f\u90a3\u5c0f\u8d3c\u7684\u5bf9\u624b\u5462\u3002\u201d\u97e9\u4e8c\u5a18\u9053\uff1a\u201c\u8fd9\u5973\u7684\u867d\u7136\u4e0d\u662f\u6740\u54b1\u4eec\u9556\u5c40\u5144\u5f1f\u7684\u4ec7\u4eba\uff0c\u4f46 \u73b0\u5728\u6211\u4eec\u5df2\u7ecf\u77e5\u9053\u5979\u4e5f\u4e0d\u662f\u4e2a\u597d\u4eba\uff0c\u6211\u6253\u4f24\u4e86\u5979\u3002\u4e5f\u7528\u4e0d\u7740\u5185\u759a\u4e86\u3002\u201d\u4ed6\u4eec\u4e00\u8def\u8bf4\u8bdd\u3001\u4e0d\u77e5 \u4e0d\u89c9\u5df2\u5230\u4e86\u4e91\u5bb6\u95e8\u53e3\u3002 \u3000\u3000\u90a3\u8001\u7ba1\u5bb6\u4e0e\u4e91\u53ec\u7684\u51e0\u4e2a\u5f1f\u5b50\uff0c\u65e9\u5df2\u624b\u6267\u706b\u628a\uff0c\u5728\u95e8\u524d\u76f8\u5019\uff0c\u89c1\u8d23\u53ec\u4e0e\u4ed6\u4eec\u4e00\u540c\u56de\u6765\uff0c\u90fd \u662f\u6781\u4e3a\u8be7\u5f02\u3002\u97e9\u7487\u9053\uff1a\u201c\u8001\u4faf\uff0c\u4f60\u77aa\u7740\u773c\u775b\u770b\u6211\u5e72\u4ec0\u4e48\uff0c\u4f60\u4e0d\u8ba4\u5f97\u6211\u5417\uff1f\u201d\u4e91\u53ec\u54c8\u545b\u7b11\u9053\uff1b \u201c\u4ed6\u662f\u6709\u773c\u65e0\u73e0\u3002\u8ba4\u4e0d\u5f97\u534e\u8001\u5148\u751f\u4e0e\u6c5f\u5c0f\u4fa0\u3002\u201d\u7b11\u58f0\u4e00\u6536\uff0c\u63a5\u7740\u5bf9\u90a3\u7ba1\u5bb6\u9053\uff1b\u201c\u4ee5\u540e\u4e0d\u8bba\u662f \u4ec0\u4e48\u4eba\u3001\u53ea\u8981\u662f\u5230\u4e91\u5e84\u6765\u6c42\u52a9\u7684\uff0c\u90a3\u5c31\u662f\u4eba\u5bb6\u4fe1\u8d56\u54b1\u4eec\uff0c\u770b\u5f97\u8d77\u54b1\u4eec\uff0c\u4f60\u4f7f\u7980\u62a5\u4e8e\u6211\uff0c\u5207\u4e0d \u53ef\u64c5\u4f5c\u4e3b\u5f20\uff0c\u62d2\u4eba\u4e8e\u5343\u91cc\u4e4b\u5916\uff01\u201d \u3000\u3000\u90a3\u8001\u7ba1\u5bb6\u6ee1\u9762\u7f9e\u60ed\uff0c\u8fde\u5fd9\u5411\u534e\u5929\u6c11\u8d54\u7f6a\uff0c\u534e\u5929\u98ce\u9053\uff1a\u201c\u4f60\u5bb6\u5c11\u4e3b\u4eba\u51fa\u4e86\u4e8b\uff0c\u4f60\u81ea\u8be5\u591a\u4e9b \u5c0f\u5fc3\uff0c\u52a0\u610f\u9632\u8303\uff0c\u8fd9\u602a\u4e0d\u5f97\u4f60\u3002\u55ef\u3002\u4f60\u5bb6\u7684\u516c\u5b50\u5c0f\u59d0\uff0c\u73b0\u5728\u5982\u4f55\u4e86\uff1f\u201d\u90a3\u8001\u8425\u5bb6\u9053\uff1a\u201c\u8fd8\u662f \u660f\u8ff7\u672a\u9192\uff0c\u51b7\u6c57\u51fa\u5f97\u5f88\u591a\u3002\u201d \u3000\u3000\u4e91\u53ec\u9053\uff1a\u201c\u97e9\u4e8c\u7239\uff0c\u54b1\u4eec\u662f\u8001\u670b\u53cb\u4e86\uff0c\u4f60\u5230\u6211\u8fd9\u91cc\u6765\uff0c\u4e0d\u5fc5\u5ba2\u6c14\u3002\u6211\u77e5\u9053\u4f60\u4eec\u5df2\u662f\u591f\u7d2f \u7684\u4e86\uff0c\u4e8c\u5ac2\u53c8\u53d7\u4e86\u4f24\uff0c\u4e0a\u843d\u4e0d\u4fbf\u3002\u4f60\u4eec\u5148\u5b89\u6b47\u53bb\u5427\u3001\u4faf\u4e49\uff0c\u4f60\u597d\u597d\u62db\u547c\u97e9\u4e8c\u7239\u3002\u201d\u97e9\u7487\u672c\u60f3 \u53bb\u63a2\u671b\u4e3b\u4eba\u5b50\u5973\uff0c\u4f46\u60f3\u5230\u81ea\u5df1\u65e2\u4e0d\u61c2\u533b\u672f\uff0c\u4eba\u591a\u4e86\u5bf9\u75c5\u4eba\u53cd\u800c\u4e0d\u4fbf\uff0c\u4e5f\u5c31\u7b97\u4e86\u3002 \u3000\u3000\u4e91\u53ec\u6c89\u541f\u534a\u664c\u3001\u518d\u9053\uff1a\u201c\u534e\u8001\u5148\u751f\u2014\u2014\u201d\u534e\u5929\u98ce\u65e9\u5df2\u77e5\u9053\u4ed6\u7684\u5fc3\u610f\uff0c\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u6211\u81ea \u7136\u8981\u5931\u53bb\u770b\u770b\u4ee4\u90ce\u4ee4\u5a9b\u3002\u201d\u4e91\u53ec\u6b49\u7136\u8bf4\u9053\uff1b\u201c\u4f60\u8eab\u4e0a\u4e5f\u53d7\u4e86\u4f24\uff0c\u6211\u672a\u80fd\u597d\u597d\u62db\u547c\uff0c\u53cd\u800c\u4ee4\u4f60 \u52b3\u5fc3\uff0c\u5b9e\u5728\u8fc7\u610f\u4e0d\u53bb\u3002\u201d\u534e\u5929\u98ce\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u90a3\u5c31\u662f\u89c1\u5916\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u80cc\u7740\u534e\u5929\u98ce\uff0c\u968f\u4e91\u53ec\u4e0a\u697c\u3002\u8fdb\u4e86\u4e00\u95f4\u623f\u95f4\uff0c\u53ea\u89c1\u5e8a\u571f\u8eba\u7740\u4e00\u4e2a\u5c11\u5e74\uff0c\u6c57\u6c34\u4e0d\u65ad\u5730 \u6dcc\u4e0b\u6765\u3001\u8138\u8272\u60e8\u767d\u5982\u7eb8\u3002 \u3000\u3000\u4e91\u53ec\u9053\uff1a\u201c\u8fd9\u662f\u5c0f\u513f\u4e91\u743c\uff0c\u5c0f\u5973\u4e91\u58c1\u5728\u91cc\u95f4\uff0c\u60c5\u5f62\u4e5f\u662f\u4e00\u6837\uff0c\u534e\u8001\u5148\u751f\uff0c\u4f60\u770b\u4f60\u80fd\u653e \u5417\uff1f\u201d\u4e00\u4e2a\u4e2b\u5934\u642c\u4e86\u6709\u9760\u80cc\u7684\u957f\u6905\u4e0e\u9526\u57ab\u8fc7\u6765\uff0c\u8bf7\u534e\u5929\u98ce\u8eba\u4e0b\u3002 \u3000\u3000\u534e\u5929\u98ce\u9053\uff1b\u201c\u4e91\u5728\u4e3b\u4e0d\u7528\u60ca\u614c\uff0c\u4ee4\u5373\u867d\u662f\u4f24\u5f97\u4e0d\u8f7b\u3002\u4f46\u603b\u53ef\u4ee5\u6551\u6cbb\u3002\u201d\u4e91\u53ec\u867d\u7136\u5e76\u975e\u6df1 \u901a\u533b\u7406\uff0c\u5374\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u4ed6\u4e09\u6307\u63a7\u5728\u513f\u5b50\u7684\u8109\u95e8\u4e0a\uff0c\u7981\u4e0d\u4f4f\u4ecd\u662f\u5fe7\u5fc3\u5fe1\u5fe1\u5730\u95ee\u9053\uff1a \u201c\u4ed6\u7684\u5947\u7ecf\u516b\u8109\u90fd\u51e0\u4e4e\u505c\u6b62\u8fd0\u884c\u4e86\uff0c\u534e\u8001\u5148\u751f\u3002\u4f60\u53ef\u8981\u66ff\u4ed6\u8bca\u65ad\u4e00\u4e0b\u4e48\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u5fae\u7b11\u9053\uff1b\u201c\u5947\u7ecf\u516b\u9645\u53d7\u4f24\u3002\u5728\u4ee5\u524d\u662f\u4e2a\u7edd\u75c7\uff0c\u4f46\u4ece\u4eca\u4ee5\u540e\u5c31\u4e0d\u662f\u4e86\u3002\u8001\u673d\u65e9\u5df2\u60f3 \u597d\u533b\u6848\uff0e\u4e0d\u7528\u518d\u8bca\u65ad\u4e86\u3002\u8fd9\u91cc\u6709\u4e24\u7c92\u836f\u4e38\uff0c\u6bcf\u4eba\u4e00\u7c92\u3002\u8bf7\u4f60\u5148\u7ed9\u4ed6\u4eec\u773c\u4e0b\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u4e00\u770b\uff0c\u7236\u4eb2\u62ff\u6765\u7ed9\u4e91\u53ec\u65f6\u6b63\u662f\u5c0f\u8fd8\u4e39\uff0c\u4e0d\u7531\u5f97\u9762\u8272\u5fae\u53d8\uff0c\u8f7b\u8f7b\u7684\u201c\u566b\u201d\u4e86\u4e00\u58f0\u3002 \u8981\u77e5\u534e\u5929\u98ce\u603b\u5171\u4e0d\u8fc7\u5236\u70bc\u4e86\u4e03\u7c92\u5c0f\u8fd8\u4e39\uff0c\u88ab\u6b27\u9633\u4ef2\u548c\u8981\u53bb\u4e86\u4e09\u7c92\uff0c\u4ed6\u81ea\u5df1\u53d7\u4f24\u524d\u540e\u670d\u4e86\u4e24 \u4f4d\uff0c\u73b0\u5728\u5c31\u53ea\u5269\u4e0b\u4e86\u8fd9\u6700\u540e\u7684\u4e24\u4f4d\u4e86\uff01 \u3000\u3000\u4e91\u53ec\u542c\u5f97\u90a3\u4e00\u58f0\u8f7b\u566b\uff0c\u4e0d\u7531\u84e6\u5730\u4e00\u6014\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u7d20\u95fb\u534e\u5c71\u533b\u9690\u72ec\u95e8\u79d8\u5236\u7684\u5c0f\u8fd8\u4e39\u529f \u80fd\u7eed\u547d\u3002\u73cd\u8d35\u65e0\u6bd4\uff0c\u83ab\u975e\u4ed6\u7ed9\u6211\u7684\u5c31\u662f\u5c0f\u8fd8\u6708\uff0c\u4ed6\u7684\u5973\u513f\u820d\u4e0d\u5f97\u4e48\uff1f\u201d\u534e\u5929\u98ce\u4e0d\u5f85\u4ed6\u53d1\u95ee\uff0c \u5df2\u5148\u8bf4\u9053\uff1a\u201c\u78a7\u89c1\uff0c\u4f60\u4e0d\u7528\u5bb3\u6015\uff0c\u8fd9\u8f93\u8840\u7597\u4f24\u4e4b\u672f\u5e76\u4e0d\u96be\u505a\uff0c\u4f60\u63a5\u534e\u9640\u795e\u5256\u7b2c\u5341\u516d\u89e3\u7684\u56fe \u89e3\uff0c\u5148\u5c01\u4e86\u660e\u8f6e\u3001\u79c0\u5b9e\u4e24\u5904\u7a74\u9053\uff0c\u4e00\u7136\u540e\u7ed9\u4ed6\u4eec\u63a5\u9a73\uff0c\u518d\u7528\u63a8\u8840\u8fc7\u5b98\u4e4b\u6cd5\u4fbf\u53ef\u4ee5\u5927\u529f\u544a\u6210 \u4e86\u3002\u4f60\u6d77\u54e5\u8eab\u4f53\u58ee\u5065\uff0c\u6d41\u4e00\u70b9\u8840\u5e76\u65e0\u59a8\u788d\u3002\u201d \u3000\u3000\u4e91\u53ec\u60ca\u9053\uff1b\u201c\u8981\u52a8\u7528\u5200\u5251\u4e48\uff1f\u600e\u4e48\u53eb\u505a\u8f93\u8840\u7597\u4f24\uff1f\u201d\u534e\u5929\u98ce\u7b11\u9053\uff1a\u201c\u6240\u4ee5\u5c0f\u5973\u6709\u70b9\u5bb3 \u6015\u3002\u5176\u5b9e\u6614\u65f6\u534e\u9640\u7ed9\u5173\u7fbd\u522e\u9aa8\u7597\u6bd2\uff0c\u8fd8\u66fe\u732e\u8bae\u8981\u7ed9\u66f9\u64cd\u5256\u8111\u4ee5\u6cbb\u5934\u98ce\uff0c\u8fd9\u624d\u771f\u662f\u795e\u672f\u9a87\u4eba \u5462\uff01\u8f93\u8840\u7597\u4f24\u53ea\u662f\u628a\u53e6\u4e00\u4e2a\u7684\u8840\u6db2\u8f93\u5230\u75c5\u4eba\u4f53\u4e2d\uff0c\u8fd9\u5728\u534e\u9640\u7684\u2018\u795e\u5256\u672f\u2019\u4e4b\u4e2d\u53ea\u662f\u5c0f\u7109\u8005 \u4e5f\u3002\u201d\u534e\u5929\u98ce\u8f7b\u8f7b\u7684\u66ff\u5973\u513f\u63a9\u9970\u8fc7\u53bb\uff0c\u540c\u65f6\u6307\u70b9\u4e86\u5973\u513f\u5982\u4f55\u8fdb\u884c\u6cbb\u7597\uff0c\u534e\u4e91\u78a7\u662f\u4e2a\u806a\u660e\u4eba\u3002 \u7acb\u5373\u5fc3\u9886\u795e\u4f1a\u3002\u5fc3\u60f3\u7236\u4eb2\u5c06\u6700\u540e\u7684\u4e24\u7c92\u5c0f\u8fd8\u4e39\u8fdb\u51fa\u53bb\uff0c\u60f3\u5fc5\u662f\u6709\u4e86\u4e24\u5168\u4e4b\u7b56\uff0c\u4e00\u4e5f\u5c31\u4e0d\u90a3\u4e48 \u62c5\u5fe7\u4e86\u3002 \u3000\u3000\u4e91\u53ec\u9053\uff1b\u201c\u82e5\u8981\u8f93\u8840\u7597\u4f24\uff0c\u4f55\u4e0d\u5c31\u7528\u8001\u592b\u7684\u5462\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u4f60\u5148\u628a\u4e5d\u836f\u7ed9\u4ed6\u773c\u4e0b\uff0c \u7136\u540e\u54b1\u4eec\u518d\u8c08\u3002\u201d \u3000\u3000\u4e91\u53ec\u5e76\u4e0d\u77e5\u9053\u8fd9\u662f\u534e\u5929\u98ce\u4ec5\u6709\u7684\u4e24\u7c92\u5c0f\u8fd8\u4e39\uff0c\u8fd9\u65f6\u4ed6\u65e2\u4e0d\u6000\u7591\u534e\u5bb6\u7236\u5973\u6709\u541d\u60dc\u4e4b\u5fc3\uff0c\u4ed6 \u662f\u4e2a\u8c6a\u723d\u7684\u4eba\u3001\u4fbf\u5766\u7136\u53d7\u4e0b\u4e86\u3002 \u3000\u3000\u4e91\u53ec\u5c06\u4e24\u7c92\u5c0f\u8fd8\u4e39\u5206\u522b\u7ed9\u5b50\u5973\u670d\u4e0b\u4e4b\u540e\uff0c\u518d\u4e0e\u534e\u5929\u98ce\u5546\u8bae\uff0c\u4ed6\u4ecd\u7136\u575a\u6267\u4e0d\u80af\u8ba9\u6c5f\u6d77\u5929\u8f93 \u8840\uff0c\u534e\u5929\u98ce\u7b11\u9053\uff1a\u201c\u4e91\u5e84\u4e3b\uff0c\u4eca\u540e\u6211\u4eec\u6258\u5e87\u8d35\u5728\uff0c\u8981\u501a\u4ed7\u4f60\u7684\u65e5\u5b50\u591a\u7740\u5462\uff01\u8f93\u8840\u867d\u65e0\u5927\u788d\uff0c \u4f46\u4e5f\u5f97\u6b47\u606f\u51e0\u5929\uff0c\u5018\u82e5\u53c8\u6709\u5bf9\u5934\u524d\u6765\uff0c\u7531\u4e91\u5e84\u4e3b\u51fa\u53bb\u5e94\u4ed8\u603b\u6bd4\u4ed6\u8981\u80dc\u4e00\u7b79\uff0e\u54b1\u4eec\u809d\u80c6\u76f8\u4ea4\uff0c \u4e0d\u5fc5\u62d8\u8bba\u8fd9\u4e9b\u5c0f\u8282\u4e86\u3002\u201d \u3000\u3000\u4e91\u53ec\u5f97\u4ed6\u63d0\u9192\uff0c\u60f3\u8d77\u81ea\u5df1\u8981\u8d1f\u8d23\u62a4\u536b\u5168\u5e84\uff0c\u53ea\u597d\u4e0d\u518d\u63a8\u8f9e\uff0c\u5f53\u4e0b\u4ed6\u5411\u534e\u5929\u98ce\u548c\u6c5f\u6d77\u5929\u4f5c \u4e86\u4e00\u4e2a\u957f\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u6069\u4e0d\u8a00\u62a5\uff0c\u65e5\u540e\u534e\u8001\u5148\u751f\u4e0e\u6c5f\u5c0f\u4fa0\u82e5\u6709\u8981\u7528\u5230\u4e91\u67d0\u4e4b\u5904\uff0c\u8d74\u6c64\u8e48 \u706b\uff0c\u51b3\u4e0d\u6562\u8f9e\u3002\u201d\u6c5f\u6d77\u5929\u8fde\u5fd9\u4ee5\u5c0f\u8f88\u4e4b\u793c\u7b54\u8c22\u3002 \u3000\u3000\u5f53\u4e0b\u534e\u4e91\u78a7\u6309\u7167\u201c\u534e\u9640\u795e\u5256\u672f\u201d\u7684\u56fe\u89e3\u4f9d\u6cd5\u65bd\u4e3a\uff0c\u5c06\u4e00\u6761\u76ae\u5e26\u7f1d\u6210\u7684\u7ba1\u5b50\u63a5\u8fde\u4e8c\u4eba\u7684\u624b \u81c2\uff0c\u52a9\u6c5f\u6d77\u5929\u7ed9\u4e91\u743c\u8f93\u8840\u3002\u8f93\u8840\u7684\u624b\u672f\u5728\u73b0\u4ee3\u7684\u533b\u5b66\u751a\u662f\u5e73\u5e38\uff0c\u4f46\u5728\u4e2d\u56fd\u53e4\u4ee3\u5374\u51e0\u4e4e\u662f\u4e00\u95e8 \u201c\u7edd\u5b66\u201d\uff0c\u8fd9\u662f\u534e\u5929\u98ce\u4ece\u4e00\u4e2a\u5076\u7136\u7684\u673a\u7f18\u3002\u5f97\u5230\u4e86\u534e\u9640\u4f20\u4e0b\u7684\u79d8\u672c\u624d\u5b66\u5230\u7684\u3002\u5f53\u65f6\u9664\u4e86\u4ed6\u4eec \u7236\u5973\u4e8c\u4eba\u4e4b\u5916\u3002\u66f4\u65e0\u7b2c\u4e09\u4e2a\u4eba\u61c2\u5f97\u8fd9\u79cd\u624b\u672f\uff0c\u76f4\u628a\u4e91\u53ec\u770b\u5f97\u76ee\u77aa\u53e3\u5446\uff01 \u3000\u3000\u5927\u7ea6\u8fc7\u4e86\u4e00\u70b7\u9999\u7684\u65f6\u523b\uff0c\u534e\u5929\u98ce\u9053\uff1a\u201c\u884c\u4e86\u3001\u4f60\u4eec\u518d\u53bb\u7ed9\u4e91\u59d1\u5a18\u65bd\u672f\u5427\u3002\u201d\u4e91\u53ec\u9053\uff1b \u201c\u6015\u4e0d\u6015\u6d41\u8840\u8fc7\u591a\uff0c\u6362\u4e00\u4e2a\u4eba\u53ef\u4ee5\u4e48\uff1f\u201d\u534e\u5929\u98ce\u5524\u6c5f\u6d77\u5929\u8fc7\u6765\uff0c\u66ff\u4ed6\u628a\u4e86\u4e00\u4e0b\u8109\u3002\u8bf4\u9053\uff1a \u201c\u4ed6\u8eab\u4f53\u8fd8\u53ef\u4ee5\u53d7\u5f97\u8d77\uff0c\u4ed6\u61c2\u5f97\u5c01\u7a74\u6b62\u8840\uff0c\u6362\u4e86\u522b\u4eba\uff0c\u53ea\u6015\u6d41\u8840\u66f4\u591a\u3002\u201d \u3000\u3000\u8fd9\u201c\u5c01\u7a74\u6b62\u8840\u201d\u7684\u672c\u9886\uff0c\u5fc5\u987b\u8eab\u6709\u4e0a\u4e58\u5185\u529f\u7684\u4eba\u624d\u53ef\u4ee5\u505a\u5f97\u5230\uff0c\u4e91\u53ec\u4e00\u60f3\uff0c\u9664\u4e86\u4ed6\u81ea\u5df1 \u4e4b\u5916\uff0c\u4e91\u5bb6\u5e84\u91cc\u3001\u65e0\u4eba\u6709\u6b64\u80fd\u4e3a\uff0c\u5973\u513f\u7684\u6027\u547d\u4e0d\u80fd\u4e0d\u6551\uff0c\u56e0\u6b64\u5fc3\u4e2d\u867d\u7136\u6781\u4e3a\u8fc7\u610f\u4e0d\u53bb\uff0c\u4e5f\u53ea \u597d\u518d\u6b21\u76f8\u70e6\u3001\u5f53\u4e0b\uff0c\u4ed6\u53eb\u4e86\u4e00\u4e2a\u4e2b\u5bf0\u5c06\u6c5f\u3001\u534e\u4e8c\u4eba\u9886\u5165\u91cc\u95f4\uff0c\u4ed6\u81ea\u5df1\u5219\u7559\u4e0b\u6765\u966a\u4f34\u534e\u5929\u98ce\u3002 \u3000\u3000\u6c5f\u3001\u534e\u4e8c\u4eba\u968f\u90a3\u4e2b\u9b1f\u8fdb\u5165\u4e91\u58c1\u7684\u95fa\u623f\u3002\u4e91\u53ec\u7684\u59bb\u5b50\u65e9\u5df2\u5f97\u77e5\u6b64\u4e8b\uff0c\u6ee1\u6000\u611f\u6fc0\u7684\u8fce\u63a5\u4ed6 \u4eec\uff0e\u5979\u89c1\u6c5f\u6d77\u5929\u5c11\u5e74\u82f1\u4fca\uff0c\u534e\u4e91\u78a7\u4e5f\u662f\u82b1\u6735\u513f\u4f3c\u7684\uff0c\u66f4\u662f\u559c\u6b22\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u4eec\u662f\u4e49 \u5144\u59b9\uff0c\u53ea\u4e0d\u77e5\u8ba2\u4e86\u4eb2\u6ca1\u6709\uff1f\u5018\u5f97\u4ed6\u4eec\u4e00\u4e2a\u505a\u6211\u7684\u5973\u5a7f\uff0c\u4e00\u4e2a\u505a\u6211\u7684\u5ab3\u5987\uff0c\u90a3\u5c31\u6700\u597d\u4e0d\u8fc7\u4e86\uff01\u201d \u3000\u3000\u7f57\u5e10\u63ed\u5f00\uff0c\u53ea\u89c1\u4e00\u4e2a\u5c11\u5973\u8eba\u5728\u5e8a\u4e0a\uff0c\u4e0e\u5979\u54e5\u54e5\u7684\u60c5\u5f62\u4e00\u6837\uff0c\u4e5f\u662f\u6c57\u5982\u96e8\u4e0b\uff0c\u6d51\u8eab\u6e7f\u900f\uff0c \u51b0\u808c\u7389\u9aa8\uff0c\u9690\u7ea6\u53ef\u89c1\u3002\u6c5f\u6d77\u5929\u9762\u5ae9\uff0c\u8fde\u5fd9\u4f4e\u4e0b\u5934\u6765\uff0c\u4e0d\u6562\u4e89\u89c6\u3001\u4f46\u4ed6\u8fd9\u4e00\u4f4e\u5934\uff0c\u5374\u683c\u597d\u770b\u89c1 \u4e91\u74a7\u8170\u95f4\u6240\u7cfb\u7684\u4e00\u6761\u624b\u5e15\uff0c\u624b\u5e15\u4e0a\u7ee3\u6709\u4e00\u6735\u83b2\u82b1\uff0c\u9c9c\u8273\u593a\u76ee\u3002\u6c5f\u6d77\u5929\u597d\u751f\u8be7\u5f02\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a \u201c\u539f\u6765\u5979\u4e5f\u662f\u8fd9\u6837\u559c\u6b22\u83b2\u82b1\u7684\u3002\u8fd9\u624b\u5e15\u5229\u83b2\u59b9\u6240\u7528\u7684\u90a3\u4e9b\u624b\u5e15\u4e00\u6a21\u4e00\u6837\u3002\u201d\u539f\u6765\u8c37\u4e2d\u83b2\u56e0\u4e3a \u540d\u5b57\u4e2d\u6709\u4e2a\u201c\u83b2\u201d\u5b57\uff0c\u5979\u7684\u8863\u7269\u81ea\u5c0f\u5c31\u559c\u6b22\u7ee3\u4e0a\u83b2\u82b1\uff0c\u6c5f\u6d77\u5929\u5c0f\u65f6\u5019\u66fe\u548c\u5979\u76f8\u5904\u8fc7\u51e0\u4e2a\u6708\uff0c \u65e9\u5df2\u770b\u60ef\u4e86\u3002 \u3000\u3000\u534e\u4e91\u78a7\u8f7b\u8f7b\u5730\u634f\u4e86\u4ed6\u4e00\u4e0b\uff0c\u6c5f\u6d77\u5929\u9762\u4e0a\u4e00\u7ea2\uff0c\u8fde\u5fd9\u4f38\u51fa\u81c2\u6765\u3001\u534e\u4e91\u7b49\u5df2\u6709\u4e86\u4e00\u6b21\u7ecf\u9a8c\uff0c \u8fd9\u6b21\u505a\u7684\u624b\u672f\u7eaf\u719f\u5f97\u591a\u3002 \u3000\u3000\u4e91\u74a7\u7684\u529f\u529b\u867d\u7136\u4e0d\u53ca\u54e5\u54e5\uff0c\u4f46\u5979\u53d7\u7684\u4f24\u5374\u8f83\u8f7b\uff0c\u800c\u4e14\u5979\u662f\u5728\u53d7\u4f24\u4e4b\u540e\uff0c\u4fbf\u5f97\u54e5\u54e5\u8d1f\u7740\u5979 \u8dd1\u7684\uff0c\u4f53\u529b\u7684\u6d88\u8017\u4e5f\u8f83\u5c11\uff0c\u56e0\u6b64\u5728\u8f93\u8840\u4e4b\u540e\uff0c\u53cd\u800c\u6bd4\u54e5\u54e5\u66f4\u5feb\u89c1\u6548\u3002 \u3000\u3000\u534e\u4e91\u78a7\u521a\u521a\u66ff\u6c5f\u6d77\u5929\u5305\u624e\u597d\u624b\u81c2\uff0c\u53ea\u542c\u5f97\u201c\u5624\u201d\u7684\u4e00\u58f0\uff0c\u4e91\u74a7\u5df2\u80fd\u591f\u4f4e\u58f0\u547b\u541f\uff0c\u4e91\u592b\u4eba \u559c\u9053\uff1a\u201c\u74a7\u513f\uff0c\u4f60\u9192\u4e86\u4e48\uff1f\u5e78\u4e8f\u8fd9\u4f4d\u6c5f\u5c0f\u4fa0\u548c\u534e\u59d1\u5a18\uff0c\u5c06\u4f60\u7684\u6027\u547d\u6551\u56de\u6765\u4e86\u3002\u201d\u4e91\u74a7\u661f\u7738\u5fae \u542f\uff1b\u4e5f\u4e0d\u77e5\u5979\u662f\u5426\u542c\u5f97\u6e05\u695a\u4e86\u6bcd\u4eb2\u7684\u8bf4\u8bdd\uff0c\u773c\u5149\u7f13\u7f13\u7684\u5411\u6c5f\u6d77\u5929\u8fd9\u8fb9\u79fb\u6765\u3002 \u3000\u3000\u4e91\u592b\u4eba\u672c\u6765\u8fd8\u60f3\u7559\u4ed6\u4eec\u591a\u5750\u4e00\u4f1a\uff0c\u7b49\u5973\u513f\u795e\u667a\u6062\u590d\u4e4b\u540e\uff0c\u548c\u6c5f\u6d77\u5929\u8bf4\u4e0a\u51e0\u53e5\uff0c\u4f46\u534e\u4e91\u78a7 \u60e6\u8bb0\u7740\u7236\u4eb2\uff0c\u66ff\u6c5f\u6d77\u5929\u5305\u624e\u597d\u540e\uff0c\u4fbf\u5373\u544a\u9000\u3002\u4e91\u592b\u4eba\u8fd9\u624d\u60f3\u8d77\u534e\u5929\u98ce\u4e5f\u662f\u53d7\u4e86\u4f24\u7684\uff0c\u4e0d\u4fbf\u518d \u7559\u4ed6\u4eec\uff0c\u4e8e\u662f\u53ea\u597d\u53c8\u4e00\u6b21\u6df1\u6df1\u7684\u5411\u4ed6\u4eec\u9053\u8c22\uff0c\u76ee\u9001\u534e\u4e91\u78a7\u6276\u7740\u6c5f\u6d77\u5929\u8d70\u4e86\u3002 \u3000\u3000\u51fa\u4e86\u4e91\u58c1\u7684\u95fa\u623f\uff1b\u6c5f\u6d77\u5929\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u81ea\u5df1\u8fd8\u80fd\u8d70\u8def\uff0c\u4f60\u4e0d\u7528\u6276\u6211\u4e86\u3002\u201d\u534e\u4e91\u78a7\u4e00\u7b11 \u8bf4\u9053\uff1a\u201c\u90a3\u4f4d\u8001\u592a\u592a\u5f88\u75bc\u4f60\u5462\uff01\u521a\u624d\u6211\u82e5\u662f\u4e0d\u6765\u6276\u4f60\uff0c\u5979\u4e5f\u4e00\u5b9a\u4f1a\u53eb\u4e2b\u9b1f\u6276\u4f60\u7684\u3002\u597d\uff0c\u90a3\u4f60 \u5c31\u81ea\u5df1\u8d70\u5427\u3002\u201d\u5176\u5b9e\u534e\u4e91\u78a7\u6df1\u901a\u533b\u7406\uff0c\u5979\u5f53\u7136\u77e5\u9053\u6c5f\u6d77\u5929\u80fd\u591f\u8d70\u8def\uff0c\u5979\u662f\u6545\u610f\u505a\u7ed9\u4e91\u74a7\u7684\u6bcd \u4eb2\u770b\u7684\u3002 \u3000\u3000\u56de\u5230\u4e86\u4e91\u743c\u7684\u623f\u95f4\uff0c\u53ea\u89c1\u4e91\u53ec\u5b88\u5728\u75c5\u69bb\u65c1\u8fb9\uff0c\u4e91\u743c\u4f9d\u7136\u672a\u9192\uff0c\u4f46\u9762\u8272\u5df2\u6682\u8f6c\u7ea2\u6da6\uff0c\u4e91\u53ec \u8bf4\u9053\uff0c\u201c\u591a\u8c22\u6c5f\u5c0f\u4fa0\u548c\u534e\u59d1\u5a18\uff0c\u5c0f\u5973\u600e\u4e48\u6837\uff1f\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u5979\u53d7\u7684\u4f24\u8f83\u8f7b\uff0c\u73b0\u5728\u5df2\u7ecf\u9192\u8fc7 \u6765\u4e86\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u4e00\u76f4\u8eba\u5728\u6709\u9760\u80cc\u7684\u957f\u6905\u4e0a\uff0c\u95ed\u7740\u53cc\u76ee\uff0c\u5f62\u5982\u8001\u50e7\u5165\u5b9a\uff0c\u8fd9\u65f6\u5ffd\u5730\u53cc\u76ee\u500f\u5f20\u3002\u54c8\u54c8 \u7b11\u9053\uff0c\u201c\u4ece\u4eca\u5929\u4e4b\u540e\uff0c\u5728\u6211\u7684\u533b\u4e66\u4e0a\u53c8\u53ef\u6dfb\u4e0a\u4e86\u4e00\u6761\u65b0\u533b\u6848\u4e86\u3002\u5947\u7ecf\u516b\u8109\u53d7\u4f24\uff0c\u5e76\u975e\u7edd \u75c7\uff01\u201d\u90a3\u7b11\u58f0\u8d77\u5934\u54cd\u4eae\uff0c\u8d8a\u5230\u540e\u5934\uff0c\u8d8a\u662f\u5fae\u5f31\u3002 \u3000\u3000\u534e\u4e91\u78a7\u542c\u51fa\u4e0d\u5999\uff0c\u5fd9\u9053\uff1b\u201c\u7239\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u53ea\u89c1\u534e\u5929\u98ce\u5782\u4e0b\u5934\u6765\uff1b\u53cc\u76ee\u53c8\u518d\u7d27\u95ed\uff0c\u534e \u4e91\u78a7\u4e0a\u524d\u4e00\u628a\u4ed6\u7684\u8109\u640f\uff0c\u53ea\u89c9\u4ed6\u7684\u8109\u606f\u5df2\u662f\u5f31\u4f3c\u6e38\u4e1d\u3002\u539f\u6765\u534e\u5929\u98ce\u5728\u8fd9\u4e24\u65e5\u4e4b\u5185\uff0c\u91cd\u4f24\u8fc7 \u540e\uff0c\u53c8\u63a5\u8fde\u906d\u9047\u610f\u5916\uff0c\u5df2\u662f\u5fc3\u529b\u4ea4\u75b2\uff0c\u518d\u52a0\u4e0a\u7981\u4e0d\u4f4f\u7684\u4e00\u65f6\u72c2\u559c\uff0c\u5c31\u6655\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u534e\u4e91\u78a7\u624b\u8db3\u65e0\u63aa\uff0c\u53cc\u76ee\u76f4\u89c6\uff0c\u5446\u82e5\u6728\u9e21\u3002\u4e91\u53ec\u9053\uff0c\u201c\u534e\u59d1\u5a18\uff0c\u4f60\u628a\u5c0f\u8fd8\u4e39\u53d6\u51fa\u6765\u7ed9\u4ed6\u670d \u5427\u3002\u201d\u4ed6\u53ea\u9053\u534e\u4e91\u78a7\u662f\u4e00\u65f6\u614c\u5f20\uff0c\u5fd8\u8bb0\u4e86\u5979\u7236\u4eb2\u8eab\u4e0a\u6709\u5c0f\u8fd8\u4e39\uff0c\u56e0\u6b64\u51fa\u8a00\u63d0\u9192\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u4e49\u7236\u54ea\u8fd8\u6709\u5c0f\u8fd8\u4e39\uff0c\u521a\u624d\u90a3\u4e24\u9897\u5df2\u662f\u6700\u540e\u7684\u4e24\u9897\u4e86\uff01\u201d\u4ed6\u5fd8\u4e86\u987e\u5fcc\uff1b\u4e00 \u65f6\u8bf4\u4e86\u51fa\u6765\u3002\u4e91\u53ec\u5403\u4e86\u4e00\u60ca\uff0c\u767b\u65f6\u6115\u4f4f\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u4ed6\u5bf9\u534e\u5929\u98ce\u662f\u611f\u6fc0\u5230\u4e86\u6781\u70b9\uff0c\u96be\u8fc7\u4e5f\u5230 \u4e86\u6781\u70b9\uff0c\u864e\u76ee\u8574\u6cea\uff0c\u4e0d\u77e5\u8bf4\u4e9b\u4ec0\u4e48\u624d\u597d\uff1f \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e49\u7236\u6240\u4e2d\u7684\u6bd2\u4e0d\u662f\u5df2\u51cf\u8f7b\u4e86\u4e48\uff1f\u4e00\u65f6\u6655\u5012\uff0c\u4e0d\u59a8\u4e8b\u5427\uff1f\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u6bd2 \u867d\u51cf\u8f7b\uff0c\u4f46\u4ed6\u4f53\u529b\u5f88\u5f31\uff0c\u96be\u4ee5\u62b5\u6297\uff0c\u4f60\u2014\u2014\u201d\u731b\u7136\u60f3\u8d77\u6c5f\u6d77\u5929\u521a\u5728\u8f93\u8840\u4e4b\u540e\uff0c\u96be\u4ee5\u8fd0\u7528\u5185 \u529f\uff0c\u8bdd\u5230\u53e3\u8fb9\uff0c\u53c8\u6536\u4e86\u56de\u53bb\u3002 \u3000\u3000\u4e91\u53ec\u7565\u61c2\u533b\u7406\uff0c\u4e00\u542c\u4e4b\u540e\uff0c\u767b\u65f6\u7701\u609f\uff0c\u8fde\u5fd9\u5c06\u624b\u638c\u8d34\u7740\u534e\u5929\u98ce\u7684\u80cc\u5fc3\u3002\u4e00\u80a1\u5185\u5bb6\u771f\u529b\u8f93 \u9001\u8fdb\u53bb\uff0c\u52a9\u4ed6\u8840\u8109\u6d41\u901a\uff0c\u589e\u5f3a\u6297\u529b\u3002\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u4f55\u4e0d\u65e9\u8bf4\uff0c\u8001\u592b\u867d\u662f\u529f\u529b\u6d45\u8584\uff0c\u4f46\u603b \u8fd8\u53ef\u4e3a\u4ed6\u63a8\u8840\u8fc7\u5bab\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u7ed9\u7236\u4eb2\u8bca\u8109\u4e4b\u540e\uff0c\u5df2\u77e5\u63a8\u8840\u8fc7\u5bab\u4e0d\u8fc7\u80fd\u6682\u65f6\u5c06\u4ed6\u6551\u9192\uff0c\u5018\u7136\u4f59\u6bd2\u65e0\u6cd5\u6e05\u9664\uff0c\u6027\u547d \u7ec8\u662f\u96be\u4fdd\u3002\u4f46\u5979\u5df2\u4e0d\u6562\u5c06\u771f\u76f8\u8bf4\u51fa\u6765\uff0c\u53ea\u76fc\u7236\u4eb2\u9192\u540e\uff0c\u518d\u60f3\u529e\u6cd5\u4e86\u3002 \u3000\u3000\u5c31\u5728\u5979\u5fe7\u5fc3\u4ef2\u5fe1\u4e4b\u9645\uff0c\u5ffd\u7136\u53c8\u542c\u5f97\u5916\u9762\u6709\u55a7\u95f9\u7684\u58f0\u97f3\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u90a3\u8001\u7ba1\u5bb6\u548c\u4e00\u4e2a\u5c11\u5e74\u8d70\u8fdb\u623f\u6765\uff0c\u89c1\u4e91\u53ec\u6b63\u5728\u66ff\u534e\u5929\u98ce\u63a8\u8840\u8fc7\u5bab\uff0c\u4fbf\u5782\u624b\u6043\u7acb \u4e24\u65c1\uff0c\u8138\u4e0a\u90fd\u9732\u51fa\u7126\u6025\u4e0d\u5b89\u7684\u795e\u60c5\u3002 \u3000\u3000\u4e91\u53ec\u52a0\u7d27\u65bd\u4e3a\uff0c\u5927\u7ea6\u8fc7\u4e86\u4e00\u58f6\u8336\u7684\u65f6\u523b\uff0c\u534e\u5929\u98ce\u7684\u989d\u4e0a\u5192\u51fa\u6c57\u73e0\uff0c\u8109\u606f\u4e5f\u7a0d\u7a0d\u7c97\u58ee\uff0c\u4e91 \u53ec\u7f13\u4e86\u53e3\u6c14\uff0c\u8fd9\u624d\u95ee\u9053\uff1a\u201c\u51fa\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u8bf4\u5427\uff01\u201d\u4ed6\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u53cc\u638c\u4ecd\u7136\u8d34\u4f4f\u534e\u5929\u98ce\u7684\u80cc \u5fc3\uff0c\u5934\u4e5f\u4e0d\u62ac\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u7980\u5e08\u7236\uff0c\u521a\u624d\u6709\u4f4d\u8499\u9762\u7684\u5973\u5b50\u5230\u6765\uff0c\u6211\u6b63\u5728\u5c4b\u4e0a\u5b88\u591c\uff0c\u95ee\u5979\u6765\u610f\uff0c\u5979\u5374\u629b \u4e86\u4e00\u6837\u4e1c\u897f\u7ed9\u6211\u3002\u201d\u539f\u6765\u8fd9\u4e2a\u5c11\u5e74\u4e43\u662f\u4e91\u53ec\u7684\u4e09\u5f1f\u5b50\u5b87\u6587\u6717\u3002\u4e91\u53ec\u7684\u5927\u5f1f\u5b50\u3001\u4e8c\u5f1f\u5b50\u5df2\u4e1a\u6ee1 \u51fa\u5e08\uff0c\u53ea\u5269\u4e0b\u4ed6\u7559\u4f8d\u5e08\u7236\u3002 \u3000\u3000\u4e91\u53ec\u9053\uff1a\u201c\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u90a3\u5c11\u5e74\u53d6\u51fa\u4e00\u4e2a\u9ad8\u7ea6\u4e94\u5bf8\u7684\u94f6\u74f6\uff0c\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4e2a\uff0c\u5979\u53eb\u6211 \u62ff\u7ed9\u534e\u8001\u524d\u8f88\u3002\u201d\u534e\u4e91\u78a7\u542c\u4e86\u5927\u4e3a\u8be7\u5f02\u3002 \u3000\u3000\u4e91\u53ec\u9053\uff1a\u201c\u5979\u8fd8\u8bf4\u4e86\u4ec0\u4e48\uff1f\u201d\u90a3\u5c11\u5e74\u9053\uff0c\u201c\u6211\u95ee\u5979\uff0c\u8fd9\u91cc\u9762\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u5979\u53ea\u8bf4\u4f60\u62ff\u7ed9 \u534e\u8001\u5148\u751f\u770b\u5c31\u77e5\u9053\u4e86\uff0c\u5979\u8499\u7740\u8138\uff0c\u4f46\u884c\u52a8\u5374\u663e\u5f97\u751a\u662f\u614c\u5f20\uff0c\u629b\u4e0b\u4e86\u8fd9\u4e2a\u94f6\u74f6\uff0c\u6254\u4e0b\u4e86\u90a3\u4e24\u53e5 \u8bdd\uff0c\u5c31\u7acb\u523b\u8dd1\u4e86\u3002\u201d \u3000\u3000\u90a3\u8005\u7ba1\u5bb6\u9053\uff1a\u201c\u7980\u5e84\u4e3b\uff0c\u90a3\u5c11\u5973\u9003\u8dd1\u4e4b\u65f6\uff0c\u6211\u66fe\u548c\u5979\u6253\u4e86\u4e00\u4e2a\u7167\u9762\uff0c\u9762\u8c8c\u867d\u7136\u770b\u4e0d\u6e05 \u695a\uff0c\u4f46\u770b\u90a3\u8eab\u6750\uff0c\u5374\u4f3c\u662f\u521a\u624d\u90a3\u4e2a\u5c11\u5973\u3002\u201d \u3000\u3000\u4e91\u53ec\u9053\uff1a\u201c\u5c31\u662f\u5192\u5145\u534e\u59d1\u5a18\u90a3\u4e2a\u5c11\u5973\u4e48\uff1f\u201d\u90a3\u7ba1\u5bb6\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u770b\u4e5d\u6210\u662f\u5979\uff01\u201d\u4e91\u53ec \u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u5c06\u5979\u622a\u4e0b\uff1f\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u5979\u8eab\u6cd5\u592a\u5feb\uff0c\u6211\u8ffd\u4e0d\u4e0a\u5979\u3002\u201d\u90a3 \u7ba1\u5bb6\u9053\uff0c\u201c\u6211\u8bb0\u4f4f\u5e84\u4e3b\u521a\u624d\u7684\u5429\u5490\uff0c\u5728\u672a\u77e5\u5e95\u8574\u4e4b\u524d\uff0c\u4e0d\u6562\u5f97\u7f6a\u6765\u4eba\uff0c\u5f85\u6211\u5fc3\u91cc\u8d77\u7591\uff0c\u5df2\u662f \u6765\u4e0d\u53ca\u4e86\u3002\u201d\u4e91\u53ec\u9053\uff1a\u201c\u4f60\u628a\u8fd9\u74f6\u5b50\u7ed9\u534e\u59d1\u5a18\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u63a5\u8fc7\u94f6\u74f6\uff0c\u53ea\u89c1\u74f6\u5185\u6709\u4e24\u7247\u6de1\u9ec4\u8272\u7684\u4e1c\u897f\uff0c\u6c5f\u6d77\u5929\u5728\u5979\u65c1\u8fb9\uff0c\u5ffd\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u770b \u8fd9\u74f6\u5b50\u4e0a\u4f3c\u523b\u6709\u6807\u8bb0\uff0c\u5440\uff0c\u662f\u4e00\u4e2a\u638c\u5370\uff0c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u534e\u4e91\u78a7\u7ec6\u5fc3\u4e00\u770b\uff0c\u679c\u7136\u89c1\u5230\u74f6\u5b50 \u7684\u4e00\u9762\u523b\u6709\u51f9\u75d5\uff0c\u75d5\u8ff9\u5f88\u6de1\uff1b\u4f46\u5374\u53ef\u770b\u51fa\u8fd9\u662f\u638c\u5370\u3002 \u3000\u3000\u534e\u4e91\u78a7\u51b7\u7b11\u9053\uff0c\u201c\u8fd9\u662f\u6bd2\u624b\u5929\u5c0a\u84b2\u5362\u864e\u7684\u4e1c\u897f\u3002\u201d\u6c5f\u6d77\u5929\u5fc3\u4e2d\u4e00\u52a8\uff0c\u51b2\u53e3\u8bf4\u9053\uff1a\u201c\u6562\u60c5 \u8fd9\u91cc\u9762\u4e43\u662f\u89e3\u836f\uff1f\u90a3\u5973\u5b50\u662f\u9001\u89e3\u836f\u6765\u4e86\uff1f\u201d \u3000\u3000\u534e\u4e91\u78a7\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u600e\u4e48\u89c1\u5f97\uff1f\u201d\u6c5f\u6d77\u5929\u559c\u5b5c\u5b5c\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u8bb0\u5f97\u4e48\uff1f\u6628\u65e5\u54b1 \u4eec\u78b0\u5230\u90a3\u5bf9\u7537\u5973\u8d3c\u4eba\uff0c\u4ed6\u4eec\u4e0d\u662f\u8bf4\u84b2\u5362\u864e\u5df2\u6b7b\u5728\u90a3\u5973\u8d3c\u4e4b\u624b\u4e86\u4e48\uff1f\u5018\u82e5\u521a\u624d\u6765\u7684\u90a3\u4e2a\u5973\u5b50\u679c \u7136\u662f\u6b27\u9633\u5a49\uff0c\u5979\u548c\u4ed6\u4eec\u662f\u4e00\u4f19\u4eba\uff0c\u4e0d\u662f\u5f88\u5bb9\u6613\u53ef\u4ee5\u53d6\u5f97\u84b2\u5362\u864e\u7559\u4e0b\u6765\u7684\u89e3\u836f\u4e48\uff1f\u201d \u3000\u3000\u534e\u4e91\u78a7\u51b7\u7b11\u9053\uff1a\u201c\u5979\u5bf9\u4f60\u6216\u4f1a\u5982\u6b64\uff0c\u5bf9\u6211\u4eec\u54ea\u6709\u8fd9\u7b49\u597d\u5fc3\uff01\u8981\u662f\u5979\u4eca\u65e5\u679c\u662f\u6765\u9001\u89e3\u836f\uff0c \u5f53\u521d\u4e5f\u4e0d\u4f1a\u62a2\u6211\u7239\u7239\u90a3\u4e2a\u836f\u56ca\u4e86\u3002\u5206\u660e\u662f\u9001\u5047\u836f\u5ba2\u4eba\uff0c\u54fc\uff0c\u6211\u4eec\u53d7\u5979\u7684\u5bb3\u5df2\u53d7\u591f\u4e86\uff0c\u53ea\u6709\u4f60 \u8fd8\u76f8\u4fe1\u5979\uff01\u201d\u534e\u4e91\u78a7\u8d8a\u8bf4\u8d8a\u6c14\uff0c\u201c\u4e53\u201d\u7684\u4e00\u58f0\uff0c\u5c31\u628a\u90a3\u94f6\u74f6\u6454\u4e86\u4e0b\u5730\uff01 \u3000\u3000\u534e\u5929\u98ce\u5ffd\u5730\u5f20\u5f00\u53cc\u773c\u3001\u8bf4\u9053\uff1a\u201c\u5947\u602a\u3002\u54ea\u6765\u7684\u8fd9\u80a1\u836f\u5473\uff1f\u201d\u534e\u4e91\u78a7\u89c1\u7236\u4eb2\u5df2\u7ecf\u82cf\u9192\uff0c\u53c8 \u60ca\u53c8\u559c\uff0c\u5fd9\u9053\uff1a\u201c\u7239\uff0c\u4f60\u4e0d\u5fc5\u7ba1\u4ed6\uff0c\u4f60\u5148\u6b47\u4e00\u6b47\uff0c\u5f85\u4f60\u517b\u597d\u7cbe\u795e\uff0c\u5973\u513f\u518d\u8bf4\u7ed9\u4f60\u542c\u3002\u201d\u534e\u5929 \u98ce\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u8981\u4f60\u73b0\u5728\u5c31\u8bf4\uff0c\u4f60\u54ea\u513f\u627e\u6765\u7684\u8fd9\u4e2a\u836f\uff1f\u201d\u534e\u4e91\u78a7\u53ea\u5f97\u8bf4\u9053\uff1a\u201c\u662f\u6b27\u9633\u5a49\u9001\u6765 \u7684\u5047\u836f\u60f3\u5bb3\u4f60\u7684\uff0c\u53ef\u60dc\u5df2\u7ed9\u5979\u8dd1\u4e86\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u5403\u4e86\u60ca\uff0c\u9053\uff0c\u201c\u4f60\u4eec\u770b\u6e05\u695a\u4e86\uff0c\u5f53\u771f\u662f\u5979\u4e48\uff1f\u4e0d\u5bf9\uff01\u8fd9\u91cc\u9762\u5b9a\u6709\u8e4a\u8df7\uff0c\u4f60\u5feb\u628a\u90a3 \u836f\u6361\u8d77\u6765\uff0c\u5f85\u6211\u518d\u4ed4\u7ec6\u4e00\u8fa8\u3002\u201d\u539f\u6765\u534e\u5929\u98ce\u6df1\u901a\u836f\u6027\uff0c\u5bf9\u4efb\u4f55\u836f\u54c1\u7684\u6c14\u5473\uff1a\u4e00\u95fb\u4fbf\u77e5\uff0c\u4ed6\u95fb \u5f97\u7684\u8fd9\u80a1\u6c14\u5473\uff0c\u6b63\u662f\u4e00\u79cd\u975e\u5e38\u96be\u5f97\u7684\u89e3\u6bd2\u836f\u8349\u7684\u6c14\u5473\uff0c\u800c\u4e14\u6c14\u5473\u6d53\u90c1\uff0c\u663e\u7136\u662f\u7ecf\u8fc7\u63d0\u70bc\u7684\u3002 \u3000\u3000\u534e\u4e91\u78a7\u6b63\u8981\u53bb\u62fe\u90a3\u836f\u74f6\uff0c\u4e91\u53ec\u5ffd\u5730\u559d\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u7a81\u7136\u95f4\u53ea\u542c\u5f97\u201c\u4e53\u3001\u4e53\u201d\u4e24\u58f0\u7a97\u95e8 \u788e\u88c2\uff0c\u3001\u4ece\u5916\u9762\u98de\u8fdb\u4e2a\u4eba\uff0c\u5b57\u6587\u6717\u7ad9\u5728\u7a97\u8fb9\uff0c\u89c1\u662f\u4e2a\u964c\u751f\u5973\u4eba\u7a9c\u4e86\u8fdb\u6765\uff0c\u614c\u5fd9\u4f7f\u51fa\u4e86\u4e00\u62db \u201c\u53cc\u9f99\u62a2\u73e0\u201d\u7684\u64d2\u62ff\u624b\u6cd5\uff0c\u8981\u628a\u90a3\u5973\u4eba\u7684\u53cc\u8db3\u62ff\u4f4f\u3002 \u3000\u3000\u90a3\u5973\u4eba\u597d\u751f\u4e86\u5f97\u3002\u8eab\u5b50\u60ac\u7a7a\uff0c\u53cc\u8db3\u5df2\u662f\u8fde\u73af\u8e22\u51fa\uff0c\u5b57\u6587\u6717\u62ff\u4e0d\u7740\u5979\uff0c\u53cd\u800c\u7ed9\u5979\u8e22\u4e86\u4e00\u4e2a \u7b4b\u6597\u3002\u4e91\u53ec\u5927\u6012\uff1a\u4e00\u8bb0\u5288\u7a7a\u638c\u53d1\u51fa\uff0c\u4f46\u4ed6\u4e3a\u4e86\u8981\u62a4\u536b\u534e\u5929\u98ce\uff0c\u4e0d\u6562\u79bb\u5f00\u534e\u5929\u98ce\u7684\u8eab\u8fb9\uff0c\u8ddd\u79bb \u8fdc\u4e86\u4e00\u70b9\uff0c\u8fd9\u4e00\u638c\u672a\u80fd\u5c06\u90a3\u5973\u5b50\u6253\u7ffb\u3002 \u3000\u3000\u90a3\u5973\u5b50\u6643\u4e86\u4e00\u6643\uff0c\u534e\u4e91\u78a7\u5251\u5df2\u51fa\u9798\uff0c\u4e00\u62db\u201c\u7389\u5973\u7a7f\u9488\u201d\u5411\u5979\u80f8\u53e3\u523a\u53bb\uff0c\u8fd9\u65f6\u5979\u624d\u770b\u6e05\u695a \u4e86\uff0c\u6765\u7684\u5e76\u4e0d\u662f\u6b27\u9633\u5a49\uff0c\u5374\u662f\u4e2a\u4eba\u6765\u672a\u8d1d\u8fc7\u9762\u7684\u5996\u91cc\u5996\u6c14\u7684\u4e2d\u5e74\u5987\u4eba\u3002 \u3000\u3000\u90a3\u5987\u4eba\u4e00\u4e2a\u79fb\u5f62\u6362\u4f4d\uff0c\u7528\u7684\u7adf\u7136\u4e5f\u662f\uff1a\u201c\u5929\u7f57\u6b65\u6cd5\u201d\u3002\u534e\u4e91\u78a7\u4e00\u5251\u523a\u7a7a\uff0c\u90a3\u5987\u4eba\u8863\u8896\u4e00 \u6325\uff0c\u4fbf\u5411\u5979\u6253\u4e0b\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5b87\u6587\u6717\u4e5f\u5df2\u8df3\u4e86\u8d77\u6765\uff0c\u62d4\u5200\u5411\u5979\u65ab\u53bb\uff0c\u5b57\u6587\u6717\u5df2\u5f97\u4e86\u5e08 \u7236\u7684\u4e09\u56db\u6210\u529f\u592b\uff0c\u53ea\u56e0\u4ece\u672a\u6709\u8fc7\u4e0e\u5f3a\u624b\u640f\u6597\u7684\u7ecf\u9a8c\uff0c\u6240\u4ee5\u624d\u4f1a\u7ed9\u90a3\u5987\u4eba\u4e00\u7167\u9762\u4fbf\u5c06\u4ed6\u8e22\u5012\u3002 \u73b0\u5728\u4ed6\u5728\u7f9e\u6012\u4ea4\u52a0\u4e4b\u4e0b\uff0c\u6325\u5200\u72c2\u5288\uff0c\u90a3\u5987\u4eba\u5012\u4e0d\u6562\u548c\u4ed6\u62fc\u547d\u4e86\u3002 \u3000\u3000\u4f46\u90a3\u5987\u4eba\u7684\u529f\u592b\u5230\u5e95\u662f\u6bd4\u4ed6\u9ad8\u5f97\u591a\uff0c\u54ea\u80fd\u7ed9\u4ed6\u65ab\u4e2d\uff0c\u53ea\u89c1\u5979\u8863\u8896\u4e00\u6325\uff0c\u5df2\u628a\u5b57\u6587\u6717\u7684\u5200 \u5f15\u51fa\u5916\u95e8\uff0c\u8eab\u5f62\u4e00\u98d8\uff0c\u53c8\u95ea\u5f00\u4e86\u534e\u4e91\u78a7\u523a\u6765\u7684\u4e00\u5251\u3002 \u3000\u3000\u4e91\u53ec\u53eb\u9053\uff1a\u201c\u6717\u513f\uff0c\u9000\u4e0b\uff01\u201d\u5c31\u5728\u8fd9\u65f6\uff1b\u90a3\u5987\u4eba\u5012\u9000\u4e09\u6b65\uff0c\u5c31\u4f3c\u80cc\u540e\u957f\u7740\u773c\u775b\u4e00\u822c\uff0c\u957f \u8896\u4e00\u5377\uff0c\u6b63\u597d\u628a\u90a3\u836f\u74f6\u5377\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4e91\u53ec\u5927\u559d\u9053\uff1a\u201c\u653e\u4e0b\uff01\u201d\u4ed6\u5de6\u638c\u4ecd\u7136\u8d34\u7740\u534e\u5929\u98ce\u7684\u80cc\u5fc3\uff0c\u8eab\u5f62\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u53ea\u662f\u5de6\u624b\u7684\u4e2d \u6307\u4e00\u5f39\uff0c\u4fbf\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u5987\u4eba\u7684\u864e\u53e3\u7a81\u7136\u4f3c\u7ed9\u94f6\u8ba1\u523a\u4e86\u4e00\u4e0b\u4f3c\u7684\uff0c\u201c\u5f53\u5577\u201d\u58f0\u54cd\uff0c \u94f6\u74f6\u53c8\u590d\u5760\u5730\u3002\u539f\u6765\u4e91\u53ec\u4ee5\u6700\u4e0a\u4e58\u7684\u5185\u529f\uff0c\u65bd\u5c55\u51fa\u9694\u7a7a\u70b9\u8109\u7684\u91d1\u521a\u6307\u529b\uff0c\u9976\u662f\u90a3\u5987\u4eba\u4e5f\u6709\u95ed \u7a74\u7684\u529f\u592b\uff0c\u4ea6\u81ea\u7981\u53d7\u4e0d\u8d77\u3002\u53ef\u662f\u90a3\u5987\u4eba\u5728\u94f6\u74f6\u88ab\u9707\u788d\u8131\u624b\u4e4b\u524d\uff0c\u5df2\u8fd0\u7528\u4e86\u7ef5\u638c\u788e\u77f3\u7684\u529f\u592b\uff0c \u94f6\u74f6\u5760\u5730\uff0c\u88c2\u6210\u7247\u7247\u3002 \u3000\u3000\u534e\u4e91\u78a7\u5fc3\u5934\u4e00\u9707\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u771f\u662f\u89e3\u836f\uff1f\u201d\u6025\u5fd9\u4e00\u8dc3\u9762\u524d\uff1a\u8981\u62fe\u90a3\u4e24\u7247\u836f\u7247\u3002 \u6c5f\u6d77\u5929\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3\uff01\u201d\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u5987\u4eba\u628a\u624b\u4e00\u626c\u3002\u98de\u51fa\u4e86\u4e00\u4e2a\u5706\u7403\uff0c\u7a81 \u7136\u5728\u7a7a\u4e2d\u7206\u88c2\uff0c\u4e00\u56e2\u6d53\u70df\u70c8\u7130\uff0c\u5411\u534e\u4e91\u78a7\u5f53\u5934\u7f69\u4e0b\u3002\u6d53\u70df\u70c8\u7130\u4e4b\u4e2d\uff0c\u8fd8\u6709\u65e0\u6570\u95ea\u773c\u7684\u91d1\u8292\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u540e\u53d1\u5148\u81f3\uff0c\u5de6\u8098\u4e00\u649e\uff0c\u7528\u4e86\u4e2a\u5de7\u52b2\uff0c\u5c06\u5b87\u6587\u6717\u649e\u8fc7\u4e00\u8fb9\uff0c\u53f3\u624b\u4e00\u62c9\uff0c\u5c06\u534e\u4e91\u78a7\u62d6 \u5230\u4e86\u5899\u89d2\uff0c\u4ed6\u521a\u5728\u8f93\u8840\u4e4b\u540e\uff1a\u4f53\u529b\u75b2\u5f31\uff0c\u8fd9\u65f6\u4e3a\u4e86\u6551\u534e\u4e91\u78a7\u548c\u5b57\u6587\u6717\u8131\u9669\uff0c\u7528\u4e86\u6d51\u8eab\u672c\u9886\uff0c \u7d2f\u5f97\u5598\u4e0d\u8fc7\u6c14\u6765\uff0c\u8eab\u8eaf\u6447\u6643\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u628a\u534e\u4e91\u78a7\u63fd\u5230\u6000\u4e2d\u53bb\u4e86\u3002 \u3000\u3000\u4e91\u53ec\u53c8\u4e00\u8bb0\u5288\u7a7a\u638c\u53d1\u51fa\uff0c\u90a3\u5987\u4eba\u65e9\u5df2\u501f\u7737\u70df\u5e55\u7684\u9690\u853d\u9041\u8d70\uff0c\u90a3\u56e2\u706b\u5149\u88ab\u638c\u98ce\u4e00\u522e\uff0c\u70e7\u5f97 \u66f4\u65fa\u3002 \u3000\u3000\u534e\u4e91\u78a7\u9762\u7ea2\u8033\u70ed\uff0c\u6323\u8131\u8eab\u5b50\uff0c\u60f3\u53bb\u6551\u706b\uff0c\u4e91\u53ec\u5df2\u62a2\u5728\u5979\u7684\u524d\u9762\uff0c\u62ff\u8d77\u4e86\u4e00\u5e8a\u68c9\u88ab\uff0c\u628a\u90a3 \u706b\u7130\u6251\u706d\u4e86\u3002 \u3000\u3000\u4e91\u53ec\u76b1\u4e86\u76b1\u7709\uff0c\u201c\u566b\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u5987\u4eba\u662f\u8c01\uff1f\u201d\u5979\u600e\u7684\u7adf\u4f1a\u4f7f\u7528\u5389\u80dc\u7537\u5f53\u5e74\u6240\u7528\u7684 \u6697\u5668\uff1f\u6c5f\u5c0f\u4fa0\uff0c\u4f60\u597d\u4f3c\u4e5f\u8ba4\u5f97\u8fd9\u79cd\u6697\u5668\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u5996\u5987\u4e43\u662f\u5929\u9b54\u6559\u4e3b\u7684\u59d0\u59d0\uff0c\u5979\u7684 \u624b\u4e0b\u53eb\u5979\u505a\u7f2a\u592b\u4eba\uff0c\u5341\u591a\u5e74\u524d\uff0c\u66fe\u5728\u9099\u5c71\u95f9\u4e8b\uff0c\u4e5f\u7528\u8fc7\u8fd9\u4e2a\u91d1\u9488\u70c8\u7130\u5f39\u3002\u5f53\u65f6\uff0c\u6211\u4e0e\u7239\u7239\u6b63 \u597d\u5728\u9099\u5c71\u7384\u5973\u89c2\u4f5c\u5ba2\u3002\u201d \u3000\u3000\u4e91\u53ec\u628a\u68c9\u88ab\u79fb\u5f00\uff0c\u53ea\u89c1\u90a3\u4e24\u7247\u836f\u7247\u5df2\u5316\u4e3a\u7070\u70ec\u3002\u534e\u65e0\u98ce\u9053\uff1a\u201c\u5e78\u4e8f\u5979\u8fd9\u70c8\u7130\u5f39\u8fd8\u6ca1\u6709\u5e26 \u7740\u6bd2\u96fe\uff0c\u4e0e\u5389\u80dc\u7537\u5f53\u5e74\u6240\u7528\u7684\u7a0d\u6709\u4e0d\u540c\u3002\u78a7\u51e1\uff0c\u4f60\u628a\u90a3\u7070\u70ec\u522e\u6765\u8ba9\u6211\u770b\u770b\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u522e\u8d77\u4e86\u8584\u8584\u4e00\u5c42\u7070\u70ec\u94fa\u5728\u4e00\u5f20\u7eb8\u4e0a\uff0c\u9012\u7ed9\u4e86\u7236\u4eb2\uff0c\u534e\u5929\u98ce\u55c5\u4e86\u4e00\u55c5\uff1b \u8bf4\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u8fd9\u6b63\u662f\u6bd2\u624b\u5929\u5c0a\u84b2\u5362\u864e\u81ea\u5236\u7684\u89e3\u836f\uff01\u201d\u534e\u4e91\u78a7\u53c8\u60ca\u53c8\u559c\u53c8\u662f\u540e\u6094\uff0c\u5931\u58f0 \u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u90a3\u8499\u9762\u5973\u5b50\u5f53\u771f\u662f\u7ed9\u7239\u7239\u9001\u89e3\u836f\u6765\u7684\u4e86\uff1f\u8fd9\uff0c\u8fd9\u7070\u70ec\u8fd8\u80fd\u7528\u4e48\uff1f\u201d\u534e\u5929\u98ce \u8f7b\u8f7b\u5730\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u534e\u4e91\u78a7\u5fc3\u5934\u4e00\u6c89\uff0c\u53ea\u9053\u836f\u5df2\u4e0d\u80fd\u518d\u7528\uff0c\u5374\u542c\u5f97\u534e\u5929\u98ce\u8bf4\u9053\uff1a\u201c\u7528\u662f\u4ecd\u7136\u6709\u7528\uff0c\u53ef\u60dc\u70e7\u6210 \u4e86\u7070\u70ec\uff0c\u836f\u529b\u5df2\u7ecf\u5927\u51cf\u4e86\u3002\u4e91\u5e84\u4e3b\uff0c\u6211\u6050\u6015\u8981\u5728\u5b9d\u5e84\u501f\u4f4f\u4e09\u4e24\u4e2a\u6708\u624d\u884c\u3002\u201d\u4e91\u53ec\u542c\u4ed6\u8bed\u6c14\uff0c \u6027\u547d\u5df2\u662f\u65e0\u59a8\uff0c\u53ea\u9700\u591a\u4e9b\u65f6\u65e5\u8c03\u6cbb\u800c\u5df2\uff0c\u5f53\u4e0b\u5927\u559c\u8bf4\u9053\uff1a\u201c\u534e\u8001\u5148\u751f\u5f53\u4ee3\u9ad8\u8d24\u3001\u5018\u82e5\u4e0d\u662f\u9047 \u4e0a\u6b64\u4e8b\uff0c\u6211\u662f\u8bf7\u4e5f\u8bf7\u4e0d\u6765\u7684\uff0c\u83ab\u8bf4\u4e09\u4e24\u4e2a\u6708\uff0c\u6211\u5df4\u4e0d\u5f97\u4f60\u5728\u6b64\u4f4f\u4e0a\u5341\u5e74\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\u4e0a\u7684\u77f3\u5934\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u836f\u5982\u4f55\u7528\u6cd5\uff1f\u201d\u534e\u65e0\u98ce\u8981\u8fc7\u7eb8\u7b14\uff0c\u53e6\u5916\u5f00\u4e86 \u51e0\u5473\u836f\uff0c\u8bf4\u9053\uff1a\u201c\u5c06\u8fd9\u513f\u5473\u836f\u7814\u788e\u4e0e\u8fd9\u4e9b\u7070\u70ec\u63fd\u5300\uff0c\u7529\u4e09\u7897\u6c34\u714e\u6210\u4e00\u7897\uff0c\u4fbf\u53ef\u670d\u4e86\u3002\u201d\u4e91\u53ec \u4e00\u770b\uff0c\u8fd9\u51e0\u5473\u836f\u90fd\u751a\u666e\u901a\uff0c\u4ed6\u7684\u5bb6\u91cc\u5907\u6709\u4e00\u4e9b\u5e38\u7528\u7684\u836f\u54c1\uff0c\u8fd9\u51e0\u5473\u836f\u6070\u5de7\u90fd\u6709\uff0c\u5f53\u4e0b\u7acb\u5373\u5429 \u5490\u7ba1\u5bb6\u5230\u836f\u5e93\u91cc\u53bb\u6361\u51fa\u6765\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4e91\u743c\u4e5f\u53d1\u51fa\u4e86\u547b\u541f\u4e4b\u58f0\uff0c\u5f00\u59cb\u9192\u6765\uff0c\u4ed6\u7b2c\u4e00\u773c\u770b\u89c1\u6c5f\u6d77\u5929\uff0c\u5ffd\u5730\u201c\u566b\u201d\u4e86\u4e00 \u58f0\uff0c\u624b\u8098\u652f\u5e8a\uff0c\u4f3c\u4e4e\u4f5c\u52bf\u5c31\u8981\u8df3\u8d77\u6765\uff0c\u6c5f\u6d77\u5929\u6014\u4e86\u4e00\u6014\uff0c\u6b63\u81ea\u83ab\u540d\u5176\u5999\uff0c\u4f46\u4e91\u743c\u968f\u5373\u53c8\u8eba\u4e0b \u53bb\uff0c\u9732\u51fa\u4e00\u70b9\u5c34\u5c2c\u7684\u795e\u60c5\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u8c01\uff1f\u201d\u4e91\u53ec\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u91d1\u4e16\u9057\u5927\u4fa0\u7684\u8863\u94b5\u4f20 \u4eba\u2014\u2014\u6c5f\u6d77\u5929\u6c5f\u5c0f\u4fa0\uff0c\u8fd9\u4f4d\u662f\u534e\u5c71\u533b\u9690\u534e\u8001\u5148\u751f\uff1b\u4ed6\u4eec\u4e24\u4f4d\u4e43\u662f\u4f60\u7684\u6551\u547d\u6069\u4eba\u3002\u5f85\u4f60\u597d\u4e86\u4e00 \u4e9b\uff0c\u6211\u518d\u548c\u4f60\u7ec6\u9053\u5176\u8be6\u3002\u201d \u3000\u3000\u4e91\u743c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u60ed\u6127\uff0c\u6211\u521a\u624d\u51e0\u4e4e\u8ba4\u9519\u4e86\u4eba\u3002\u6c5f\u5c0f\u4fa0\uff0c\u8bf7\u6055\u6211\u4e0d\u80fd\u8d77\u5e8a\u9053\u8c22\u3002\u201d\u8ddf\u7740 \u5bf9\u4ed6\u7236\u4eb2\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f24\u5bb3\u6211\u548c\u59b9\u59b9\u7684\u90a3\u4e2a\u5c0f\u8d3c\uff0c\u8eab\u6750\u4e0e\u6c5f\u5c0f\u4fa0\u4e00\u822c\u9ad8\u77ee\uff0c\u597d\u5728\u6211\u8fd8\u8bb0\u5f97\u4ed6 \u7684\u76f8\u8c8c\uff0c\u8981\u4e0d\u7136\u771f\u4ee5\u4e3a\u662f\u4ed6\u6765\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u8fd9\u624d\u7701\u609f\uff0c\u539f\u6765\u662f\u4ed6\u521a\u624d\u4e4d\u9192\uff0c\u628a\u81ea\u5df1\u9519\u770b\u4f5c\u662f \u90a3\u201c\u53f6\u516c\u5b50\u201d\u4e86\u3002 \u3000\u3000\u4e91\u53ec\u7b11\u9053\uff1a\u201c\u90a3\u5c0f\u8d3c\u5df2\u7ecf\u6765\u8fc7\u4e86\uff0c\u6211\u548c\u6c5f\u5c0f\u4fa0\u8fd8\u5f53\u771f\u53d1\u751f\u4e86\u4e00\u573a\u8bef\u4f1a\u5462\u3002\u201d\u4e91\u743c\u8be7\u9053\uff1a \u201c\u4ed6\u4eec\u8eab\u6750\u76f8\u4f3c\uff0c\u76f8\u8c8c\u5374\u5e76\u4e0d\u76f8\u540c\u5440\u3002\u201d\u4e91\u53ec\u9053\uff1a\u201c\u90a3\u5c0f\u8d3c\u662f\u7528\u4e0a\u4e86\u6613\u5bb9\u4e39\uff0c\u6709\u5fc3\u626e\u6210\u6c5f\u5c0f \u4fa0\u7684\u6a21\u6837\u7684\u3002\u4eca\u665a\u53d1\u751f\u7684\u4e8b\u60c5\u591a\u7740\u5462\uff0c\u5f85\u4f60\u517b\u597d\u4e86\u7cbe\u795e\uff0c\u6211\u518d\u4e00\u4ef6\u4e00\u4ef6\u548c\u4f60\u8bf4\u5427\u3002\u2019 \u3000\u3000\u4e0d\u4e45\uff0c\u7ba1\u5bb6\u628a\u836f\u714e\u597d\u7aef\u6765\uff0c\u5df2\u662f\u4e1c\u65b9\u53d1\u81ea\u7684\u65f6\u5206\u3002\u4e91\u53ec\u5f85\u534e\u5929\u98ce\u559d\u4e86\u836f\u8336\uff0c\u7b11\u9053\uff1a\u201c\u534e \u59d1\u5a18\u3001\u6c5f\u5c0f\u4fa0\uff0c\u7d2f\u4f60\u4eec\u6298\u817e\u4e86\u4e00\u591c\uff0c\u6211\u8fd9\u4e2a\u505a\u4e3b\u4eba\u7684\u771f\u662f\u8fc7\u610f\u4e0d\u53bb\uff0c\u73b0\u5728\u53ef\u4ee5\u8bf7\u5927\u5bb6\u5b89\u6b47 \u4e86\u3002\u201d \u3000\u3000\u534a\u5929\u98ce\u9053\uff1a\u201c\u4e14\u6162\uff0c\u5f85\u6211\u518d\u5f00\u4e24\u5f20\u836f\u65b9\u3002\u8fd9\u4e00\u5f20\u662f\u7ed9\u4ee4\u90ce\u4ee4\u5a9b\u670d\u7684\uff0c\u8fd9\u4e00\u5f20\u662f\u6211\u81ea\u5df1\u7528 \u7684\u3002\u6bcf\u5929\u4e00\u5242\uff0c\u8fde\u670d\u4e09\u5929\uff0c\u7136\u540e\u518d\u6362\u3002\u201d\u4e91\u53ec\u5c06\u836f\u65b9\u4ea4\u7ed9\u4e86\u7ba1\u5bb6\uff0c\u53eb\u4ed6\u770b\u4e00\u770b\uff0c\u54ea\u4e00\u4e9b\u662f\u5bb6 \u91cc\u6ca1\u6709\u7684\u8d76\u5feb\u53bb\u4e70\u3002 \u3000\u3000\u4e91\u53ec\u65e9\u5df2\u7ed9\u4ed6\u4eec\u51c6\u5907\u597d\u4e86\u623f\u95f4\uff0c\u5f53\u4e0b\u4fbf\u8981\u4e0e\u5b57\u6587\u6717\u5c06\u534e\u5929\u98ce\u62ac\u5230\u5ba2\u623f\u53bb\uff0c\u534e\u5929\u98ce\u7b11\u9053\uff1a \u201c\u5f85\u6211\u8d70\u51e0\u6b65\u8bd5\u8bd5\uff0c\u53eb\u78a7\u513f\u6276\u7740\u6211\u4fbf\u884c\u3002\u4e0d\u6562\u52b3\u70e6\u4e91\u5e84\u4e3b\u4e86\u3002\u201d\u4ed6\u8d70\u4e86\u51e0\u6b65\uff0c\u811a\u6b65\u867d\u7136\u6709\u70b9 \u8e09\u8dc4\uff0c\u4f46\u5374\u662f\u6bd4\u521a\u624d\u597d\u5f97\u591a\u4e86\u3002 \u3000\u3000\u534e\u4e91\u78a7\u5341\u5206\u6b22\u559c\uff0c\u6276\u7740\u7236\u4eb2\uff0c\u4fbf\u968f\u90a3\u7ba1\u5bb6\u4e0b\u697c\u3002\u4e91\u53ec\u7ed9\u4ed6\u4eec\u5b89\u5f98\u7684\u5ba2\u623f\u4fbf\u5728\u8fd9\u5c42\u697c\u4e0b\uff0c \u5e76\u6392\u7684\u4e24\u95f4\uff1a\u534e\u5929\u98ce\u7236\u5973\u5408\u4f4f\u4e00\u95f4\u6709\u5957\u623f\u7684\uff0c\u6c5f\u6d77\u5929\u5219\u4f4f\u5728\u90bb\u623f\uff0c\u8fd9\u6837\u7684\u5b89\u6392\uff0c\u697c\u4e0a\u697c\u4e0b\uff0c \u90fd\u597d\u4e92\u76f8\u7167\u5e94\u3002 \u3000\u3000\u90a3\u7ba1\u5bb6\u8d70\u540e\uff0c\u534e\u5929\u98ce\u7b11\u9053\uff1a\u201c\u771f\u662f\u4fa5\u5e78\uff0c\u9001\u51fa\u4e86\u5c0f\u8fd8\u4e39\uff1b\u5374\u5f97\u6765\u4e86\u89e3\u836f\uff1b\u53ef\u89c1\u5929\u65e0\u7edd\u4eba \u4e4b\u8def\uff0c\u597d\u5fc3\u7ec8\u6709\u597d\u62a5\u3002\u53ea\u662f\u5927\u4e08\u592b\u8bb2\u7a76\u6069\u6028\u5206\u660e\uff0c\u6211\u8fd9\u56de\u5374\u662f\u7cca\u91cc\u7cca\u6d82\u7684\u53d7\u4e86\u4eba\u5bb6\u7684\u6069\u60e0 \u4e86\u3002\u201d\u539f\u6765\u8fd9\u89e3\u836f\u70e7\u6210\u4e86\u7070\u70ec\u4e4b\u540e\uff0c\u529f\u6548\u6070\u597d\u76f8\u5f53\u4e8e\u4e24\u7247\u5c0f\u8fd8\u4e39\uff0c\u534e\u5929\u98ce\u4ecd\u7136\u53ef\u4ee5\u6309\u7167\u539f\u6765 \u7684\u8ba1\u5212\u533b\u6cbb\uff0c\u4f46\u53ea\u56e0\u4e0d\u77e5\u6069\u4eba\u662f\u8c01\uff0c\u6545\u6b64\u803f\u803f\u4e8e\u5fc3\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u78a7\u59b9\uff1a\u6211\u8bf4\u5bf9\u4e86\u5427\uff1f\u6211\u8bf4\u90a3\u6b27\u9633\u5a49\u662f\u9001\u89e3\u836f\u6765\u7684\uff0c\u4f60\u6700\u521d\u8fd8\u4e0d\u76f8\u4fe1\u5462\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u53ea\u8bf4\u5bf9\u4e86\u4e00\u534a\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u600e\u4e48\u53ea\u662f\u5bf9\u4e86\u4e00\u534a\uff1f\u201d\u534e\u4e91\u78a7 \u9053\uff1a\u201c\u89e3\u836f\u7684\u786e\u662f\u89e3\u836f\u4e86\uff0c\u4f46\u90a3\u9001\u836f\u7684\u8499\u9762\u5973\u5b50\u5374\u672a\u5fc5\u5c31\u662f\u6b27\u9633\u5a49\uff01\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u90a3\u7ba1\u5bb6 \u4e0d\u4e5f\u662f\u8bf4\uff0c\u8fd9\u8499\u9762\u5973\u5b50\u5c31\u662f\u66fe\u7ecf\u6765\u8fc7\u8fd9\u91cc\u7684\u5973\u8d3c\u5417\uff1f\u8fd8\u4e0d\u662f\u6b27\u9633\u5a49\uff1f\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u4ed6\u53ea\u662f \u8bf4\u8eab\u6750\u76f8\u4f3c\u800c\u5df2\uff0c\u800c\u4e14\u5192\u5145\u6211\u7684\u90a3\u4e2a\u5973\u8d3c\u5230\u5e95\u662f\u4e0d\u662f\u6b27\u9633\u5a49\uff0c\u4e5f\u8fd8\u672a\u80fd\u786e\u5b9a\u5462\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u4f60\u5fd8\u8bb0\u4e86\u4f60\u8bf4\u8fc7\u4ec0\u4e48\u4e86\uff0c\u4f60\u4e0d\u662f\u81ea\u5df1\u4e5f\u66fe\u8bf4\u8fc7\uff0c\u90a3\u5192\u5145\u4f60\u7684\u5973\u8d3c\u5b9a\u662f\u6b27 \u9633\u5a49\u65e0\u7591\u5417\uff1f\u201d\u534e\u4e91\u78a7\u55d4\u9053\uff1a\u201c\u603b\u4e4b\uff0c\u6211\u7edd\u4e0d\u76f8\u4fe1\u6b27\u9633\u5a49\u6709\u8fd9\u6837\u597d\u5fc3\uff01\u4f60\u4e5f\u5fd8\u8bb0\u4e86\u5979\u66fe\u8bf1\u4f60 \u843d\u7f51\uff1a\u5fd8\u8bb0\u4e86\u5979\u66fe\u62a2\u4e86\u6211\u7239\u7239\u7684\u836f\u56ca\uff1b\u5fd8\u8bb0\u4e86\u4f60\u66fe\u53d1\u8fc7\u7684\u8a93\u4e48\uff1f\u201d\u4e00\u8fde\u4e09\u4e2a\u201c\u5fd8\u8bb0\u201d\uff0c\u628a\u6c5f \u6d77\u5929\u8bf4\u5f97\u54d1\u53e3\u65e0\u8a00\uff0c\u96be\u4ee5\u5206\u8fa9\u3002 \u3000\u3000\u534e\u5929\u98ce\u9053\uff0c\u201c\u4e0d\u5fc5\u4e89\u8fa9\uff0c\u4e8b\u60c5\u603b\u4f1a\u6c34\u843d\u77f3\u51fa\uff1a\u6211\u53ea\u6709\u4e24\u53e5\u8bdd\u60f3\u529d\u544a\u4f60\u4eec\uff0c\u78a7\u513f\uff0c\u4f60\u4e0d\u8981 \u628a\u4eba\u770b\u5f97\u6c38\u8fdc\u4e0d\u53d8\uff0c\u597d\u7684\u5c31\u6c38\u8fdc\u662f\u597d\uff0c\u574f\u7684\u5c31\u6c38\u8fdc\u662f\u574f\uff0c\u6d77\u513f\uff0c\u4f60\u4e5f\u4e0d\u8981\u592a\u8fc7\u5fe0\u539a\uff0c\u6beb\u65e0\u9632 \u4eba\u4e4b\u5fc3\u3002\u597d\u4e86\uff0c\u6d77\u513f\uff0c\u4f60\u8f93\u8840\u8fc7\u540e\uff0c\u4e5f\u5e94\u8be5\u6b47\u606f\u4e86\u3002\u201d\u4ed6\u867d\u7136\u4e5f\u8d23\u5907\u4e86\u5973\u513f\uff0c\u4f46\u8bed\u6c14\u4e4b\u4e2d\uff0c \u663e\u7136\u4e5f\u672a\u76f8\u4fe1\u90a3\u9001\u836f\u7684\u5973\u5b50\u4fbf\u662f\u6b27\u9633\u5a49\u3002 \u3000\u3000\u4ece\u6b64\u4e4b\u540e\uff0c\u534e\u4e91\u78a7\u4e00\u76f4\u907f\u514d\u4e0e\u6c5f\u6d77\u5929\u518d\u63d0\u6b27\u9633\u5a49\uff1a\u8fc7\u4e86\u51e0\u5929\uff0c\u6c5f\u6bcf\u5929\u7684\u8eab\u4f53\u4e5f\u6e10\u6e10\u590d \u539f\uff0c\u534e\u5929\u98ce\u548c\u4e91\u5bb6\u5144\u59b9\u7684\u75c5\u4e5f\u65e5\u6709\u8d77\u8272\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\uff0c\u6c5f\u6d77\u5929\u72ec\u81ea\u5230\u82b1\u56ed\u6563\u6b65\uff0c\u4e91\u5bb6\u867d\u7136\u4e0d\u662f\u8c6a\u5bcc\u4e4b\u5bb6\uff0c\u4f46\u90a3\u82b1\u56ed\u4e5f\u5e03\u7f6e\u5f97\u9887\u4e3a\u5e7d \u96c5\u3002\u82b1\u6728\u7af9\u77f3\uff0c\u5047\u5c71\u8377\u6c60\uff0c\u7ecf\u8fc7\u4e86\u5de7\u5320\u7684\u5b89\u6392\uff0c\u6784\u6210\u4e86\u4e00\u5e45\u7f8e\u5999\u7684\u56fe\u753b\u3002\u8fd9\u65f6\u6b63\u662f\u83b2\u82b1\u76db\u5f00 \u7684\u65f6\u8282\uff0c\u6c5f\u6d77\u5929\u4fe1\u6b65\u8d70\u5230\u8377\u5858\u65c1\u8fb9\uff0c\u5ffd\u89c1\u8377\u5858\u8fb9\u7684\u5047\u5c71\u524d\u9762\uff0c\u6709\u4e2a\u5c11\u5973\u4e5f\u6b63\u81ea\u72ec\u8d4f\u83b2\u82b1\u3002 \u3000\u3000\u90a3\u5c11\u5973\u542c\u5f97\u811a\u6b65\u58f0\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u89c1\u662f\u4e2a\u964c\u751f\u4eba\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e91\u59d1\u5a18\uff0c\u4f60\u75c5 \u597d\u4e86\uff1f\u201d\u90a3\u5c11\u5973\u6b63\u662f\u4e91\u74a7\uff0c\u5979\u201c\u554a\u201d\u4e86\u4e00\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u662f\u6c5f\u5c0f\u4fa0\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4fa0\u5b57\u7edd \u4e0d\u6562\u5f53\uff0c\u6211\u540d\u53eb\u6d77\u5929\uff0c\u59d1\u5a18\u4f60\u522b\u5ba2\u6c14\u3002\u201d \u3000\u3000\u4e91\u74a7\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u6211\u5ba2\u6c14\uff0c\u662f\u4f60\u592a\u5ba2\u6c14\u4e86\u3002\u8981\u662f\u4f60\u8fd8\u914d\u4e0d\u4e0a\u4e00\u4e2a\u4fa0\u5b57\uff0c\u90a3\u4e9b\u6c5f\u6e56\u4e0a\u7684\u4ec0 \u4e48\u5927\u4fa0\u5c0f\u4fa0\uff0c\u53ef\u90fd\u8981\u6c57\u989c\u65e0\u5730\u4e86\uff0c\u6c5f\u76f8\u516c\uff0c\u4f60\u4e3a\u6211\u4e00\u4e2a\u4e0d\u76f8\u5e72\u7684\u4eba\u6d41\u4e86\u8bb8\u591a\u8840\uff0c\u6211\u5b9e\u5728\u8fc7\u610f \u4e0d\u53bb\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u54b1\u4eec\u90fd\u662f\u6b66\u6797\u7684\u540c\u9053\u4e2d\u4eba\uff0c\u60a3\u96be\u76f8\u52a9\uff0c\u90a3\u662f\u5e94\u8be5\u7684\u3002\u6211\u4e49\u7236\u6258\u5e87\u5b87\u4e0b\uff0c\u82e5 \u7136\u8bf4\u5230\u4e00\u4e2a\u8c22\u5b57\uff0c\u6211\u5c31\u5148\u8be5\u591a\u8c22\u4f60\u4eec\u3002\u201d \u3000\u3000\u4e91\u74a7\u9053\uff1a\u201c\u5bf9\u4e86\u3002\u6211\u542c\u7236\u4eb2\u8bf4\u4f60\u7684\u4e49\u7236\u534e\u8001\u5148\u751f\u4e5f\u53d7\u4e86\u4f24\uff0c\u6211\u4eca\u65e5\u521a\u8d77\u5e8a\uff0c\u8fd8\u672a\u66fe\u53bb\u62dc \u89c1\u8fc7\u4ed6\u5462\u3002\u4ed6\u7684\u75c5\u4f53\u5982\u4f55\uff0c\u8fd8\u6709\u4e00\u4f4d\u534e\u59d1\u5a18\u5462\uff1f\u4f60\u4eec\u662f\u4e09\u4e2a\u4eba\u4e00\u540c\u6765\u7684\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u4e49\u7236\u4e5f\u597d\u591a\u4e86\u3002\u4ed6\u521a\u719f\u7761\uff0c\u534e\u59d1\u5a18\u8981\u770b\u62a4\u4ed6\uff0c\u6240\u4ee5\u6211\u4e00\u4e2a\u4eba\u5230\u56ed\u5b50\u6765\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e0d\u5584\u8f9e\u4ee4\uff0c\u5bf9\u7740\u4e00\u4e2a\u521d\u6b21\u76f8\u8bc6\u7684\u5c11\u5973\uff0c\u4e0d\u514d\u6709\u70b9\u513f\u817c\u8146\uff0c\u51e0\u53e5\u5ba2\u6c14\u8bdd\u8bf4\u8fc7\uff0c\u5c31\u611f \u5230\u65e0\u8bdd\u53ef\u8bf4\u3002\u4ed6\u76ee\u5149\u4e00\u77a5\uff0c\u5ffd\u89c1\u4e91\u74a7\u8170\u95f4\u6240\u7cfb\u7684\u4e00\u65b9\u9999\u7f57\u6c57\u5e15\uff0c\u6b63\u662f\u90a3\u65e5\u6240\u89c1\u7684\u7ee3\u6709\u83b2\u82b1\u7684 \u90a3\u6761\u624b\u5e15\u3002\u4e0d\u89c9\u591a\u770b\u4e86\u4e24\u773c\u3002 \u3000\u3000\u4e91\u74a7\u4e5f\u611f\u89c9\u5230\u4e86\uff0c\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff0c\u201c\u6c5f\u76f8\u516c\uff0c\u4f60\u6b22\u559c\u8fd9\u6761\u624b\u7ee2\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u624b \u7ee2\u4e0a\u7ee3\u7684\u8fd9\u6735\u83b2\u82b1\u5f88\u597d\u770b\u3002\u201d\u4e91\u58c1\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u90a3\u5929\u6211\u7ed9\u59d1\u5a18\u6cbb\u75c5\uff0c \u5df2\u89c1\u5230\u4e86\u3002\u201d \u3000\u3000\u4e91\u58c1\u89e3\u4e0b\u624b\u5e15\uff0c\u4f4e\u5934\u5fae\u7b11\u9053\uff1a\u201c\u8981\u662f\u4f60\u6b22\u559c\u7684\u8bdd\uff0c\u6211\u7167\u6837\u7ee3\u4e00\u6761\u7ed9\u4f60\uff0c\u8fd9\u6761\u624b\u7ee2\u4e0d\u662f\u6211 \u7684\u4e1c\u897f\uff0c\u6211\u4e0d\u597d\u9001\u7ed9\u4f60\u3002\u201d \u3000\u3000\u539f\u6765\u4e91\u74a7\u8bef\u4f1a\u4e86\u6c5f\u6d77\u5929\u7684\u610f\u601d\uff0c\u5979\u60c5\u7aa6\u521d\u5f00\uff0c\u5e73\u65e5\u8bfb\u8fc7\u4e00\u4e9b\u5f39\u8f9e\u5c0f\u8bf4\u4e4b\u7c7b\u7684\u95f2\u4e66\uff0c\u4e66\u4e2d \u7684\u624d\u5b50\u4f73\u4eba\u5728\u540e\u82b1\u56ed\u76f8\u4f1a\uff0c\u603b\u5c11\u4e0d\u4e86\u8981\u4e92\u8d60\u201c\u8868\u8bb0\u201d\uff0c\u6c57\u5dfe\u8377\u5305\u4e4b\u7c7b\uff0c\u5979\u89c1\u6c5f\u6d77\u5929\u8fd9\u6837\u7559\u610f \u5979\u7684\u624b\u5e15\uff0c\u53ea\u4ee5\u4e3a\u6c5f\u6d77\u5929\u662f\u8981\u5411\u5979\u7d22\u8d60\u201c\u8868\u8bb0\u201d\uff0c\u4e0d\u89c9\u5fc3\u5934\u9e7f\u649e\uff0c\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u53c8\u60ca\u53c8\u559c\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u54ea\u91cc\u77e5\u9053\u5979\u6709\u8fd9\u4e2a\u5fc3\u601d\uff0c\u542c\u5f97\u5979\u8fd9\u4e48\u8bf4\uff0c\u51b2\u53e3\u5c31\u53eb\uff0c\u51fa\u6765\u9053\uff1a\u201c\u539f\u6765\u8fd9\u6761\u624b\u5e15\u4e0d \u662f\u4f60\u7684\u4e48\uff1f\u201d\u4e91\u58c1\u8be7\u9053\uff0c\u201c\u4e0d\u9519\uff0c\u662f\u53e6\u4e00\u4f4d\u59d1\u5a18\u9001\u7ed9\u6211\u7684\u3002\u6709\u4ec0\u4e48\u4e0d\u5bf9\u5417\uff1f\u201d\u6c5f\u6d77\u5929\u8fde\u5fd9\u95ee \u9053\uff1a\u201c\u90a3\u4f4d\u59d1\u5a18\u662f\u4e0d\u662f\u59d3\u8c37\u7684\uff0c\u540d\u53eb\u8c37\u4e2d\u83b2\uff1f\u201d\u4e91\u58c1\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u4f60\u8ba4\u5f97\u5979\uff1f\u201d\u6c5f\u6d77\u5929 \u9053\uff1a\u201c\u6211\u4e03\u5c81\u7684\u5bf9\u5019\u5728\u9099\u5c71\u4f4f\u8fc7\uff0c\u73b0\u5728\u5df2\u6709\u76f8\u8fd1\u5341\u5e74\u672a\u89c1\u8fc7\u9762\u4e86\u3002\u201d\u4e91\u58c1\u62bf\u5634\u7b11\u9053\uff1a\u201c\u539f\u6765 \u4f60\u4eec\u662f\u9752\u6885\u7af9\u9a6c\u7684\u597d\u670b\u53cb\u3002\u201d\u4f46\u5fc3\u91cc\u5374\u6697\u6697\u559c\u6b22\u3002 \u3000\u3000\u4e91\u74a7\u5fc3\u91cc\u6697\u81ea\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u76f8\u8bc6\u7684\u65f6\u5019\uff0c\u53cc\u65b9\u90fd\u8fd8\u662f\u672a\u61c2\u4eba\u4e8b\u7684\u5b69\u5b50\u3001\u4f55\u51b5\u73b0\u5728\u53c8 \u76f8\u9694\u591a\u5e74\uff1b\u66f4\u4e0d\u53ef\u80fd\u6709\u4ec0\u4e48\u7537\u5973\u79c1\u60c5\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6839\u672c\u5c31\u6ca1\u6709\u7559\u610f\u5979\u7684\u795e\u60c5\uff0c\u542c\u8bf4\u679c\u7136\u662f\u8c37\u4e2d\u83b2\uff0c\u5c31\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u662f\u51e0\u65f6\u89c1\u5230\u5979 \u7684\uff0c\u5979\u5c06\u81ea\u5df1\u7684\u6c57\u5dfe\u9001\u7ed9\u4f60\uff0c\u4f60\u4eec\u7684\u4ea4\u60c5\u4e00\u5b9a\u662f\u5f88\u597d\u4e86\uff1f\u201d \u3000\u3000\u4e91\u58c1\u7b11\u9053\uff0c\u201c\u5979\u548c\u6211\u7684\u4ea4\u60c5\u786e\u662f\u4e0d\u9519\uff0c\u4f46\u5979\u548c\u6211\u54e5\u54e5\u7684\u4ea4\u60c5\u66f4\u597d\uff0c\u6211\u5f97\u5979\u9001\u6211\u8fd9\u6761\u624b \u5e15\uff0c\u5176\u5b9e\u662f\u6cbe\u4e86\u6211\u54e5\u54e5\u7684\u5149\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5446\u4e86\u4e00\u5446\uff0c\u5f3a\u81ea\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u8fd9\u6761\u624b\u5e15\u8fd8\u6709\u8bb8\u591a\u66f2\u6298\uff1f\u201d\u4e91\u58c1\u7b11\u9053\uff0c\u201c\u8fd9\u6545 \u4e8b\u5f88\u6709\u8da3\u3002\u4f60\u6b22\u559c\u542c\uff0c\u6211\u5c31\u8bf4\u7ed9\u4f60\u542c\u3002\u201d \u3000\u3000\u4e91\u58c1\u5c06\u624b\u5e15\u7ed5\u7740\u6307\u5934\uff0c\u7f13\u7f13\u8bf4\u9053\uff0c\u201c\u4e24\u4e2a\u6708\u524d\uff0c\u9099\u5c71\u6d3e\u7684\u638c\u95e8\u8c37\u5973\u4fa0\u548c\u5979\u7684\u5973\u513f\u8def\u8fc7\u6b64 \u5730\uff0c\u5728\u820d\u4e0b\u4f4f\u4e86\u51e0\u5929\uff1a\u90a3\u51e0\u5929\u6211\u7684\u54e5\u54e5\u5931\u9b42\u843d\u9b44\u4f3c\u7684\u3002\u8001\u662f\u8ddf\u7740\u8c37\u59d1\u5a18\uff0c\u51e0\u5929\u7684\u529f\u592b\u3001\u4ed6\u4eec \u7684\u4ea4\u60c5\u5c31\u6253\u5f97\u70ed\u547c\u547c\u7684\uff0c\u7b80\u76f4\u50cf\u662f\u8001\u670b\u53cb\u4e86\u3002\u201d \u3000\u3000\u4e91\u58c1\u662f\u6709\u610f\u5938\u5f20\uff0c\u6c5f\u6d77\u5929\u542c\u4e86\uff0c\u5374\u6ee1\u4e0d\u662f\u5473\u513f\uff0c\u4ed6\u5b9a\u4e86\u5b9a\u795e\uff0c\u95ee\u9053\uff1a\u201c\u8c37\u5973\u4fa0\u8981\u5230\u4ec0\u4e48 \u5730\u65b9\u53bb\uff0c\u4f60\u53ef\u77e5\u9053\u5417\uff1f\u8c37\u5973\u4fa0\u662f\u5bb6\u5e08\u7684\u597d\u670b\u53cb\uff0c\u6211\u6b63\u60f3\u63a2\u542c\u5979\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u4e91\u74a7\u5bdf\u89c9\u4ed6\u7684\u795e\u8272\u6709\u5f02\uff0c\u5fc3\u91cc\u6697\u7b11\uff1a\u201c\u53ea\u6015\u4f60\u8981\u63a2\u542c\u8c37\u4e2d\u83b2\u7684\u6d88\u606f\u5427\uff1f\u201d\u4e0d\u77e5\u600e\u7684\uff0c\u4e5f \u7a81\u7136\u6709\u4e86\u9178\u69b4\u69b4\u7684\u611f\u89c9\u3002\u4f46\u5979\u4e5f\u77e5\u9053\u6c5f\u6d77\u5929\u7684\u786e\u662f\u8981\u53bb\u5bfb\u5e08\u89c5\u7236\uff0c\u56e0\u6b64\u968f\u5373\u53c8\u81ea\u60f3\u9053\uff1a\u201c\u6216 \u8005\u4ed6\u771f\u7684\u662f\u4e3a\u4e86\u5e08\u7236\uff0c\u624d\u6e34\u6b32\u77e5\u9053\u8c37\u5973\u4fa0\u7684\u884c\u8e2a\u3002\u54ce\uff0c\u4e0d\u7ba1\u4ed6\u662f\u5173\u5fc3\u6bcd\u4eb2\u4e5f\u597d\uff0c\u5973\u513f\u4e5f\u597d\uff0c \u4e0e\u6211\u53c8\u6709\u4ec0\u4e48\u76f8\u5e72\uff1f\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u8138\u4e0a\u4e0d\u89c9\u98de\u8d77\u4e86\u4e00\u7247\u7ea2\u6655\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u54ea\u91cc\u61c2\u5f97\u5973\u5b69\u513f\u8fd9\u6837\u66f2\u6298\u590d\u6742\u7684\u5fc3\u4e8b\uff0c\u89c1\u5979\u65e0\u7aef\u7aef\u7684\u8138\u7ea2\u8d77\u6765\u3002\u8fd8\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4 \u9053\uff1a\u201c\u4e91\u59d1\u5a18\uff0c\u4f60\u521a\u521a\u75c5\u597d\uff0c\u4e0d\u5b9c\u592a\u8fc7\u52b3\u795e\uff0c\u662f\u4e0d\u662f\u4f60\u53c8\u53d1\u70e7\u4e86\uff1f\u4f60\u5018\u82e5\u8981\u6b47\u606f\u7684\u8bdd\uff0c\u8fd9\u6545 \u4e8b\u56fd\u5f85\u660e\u5929\u518d\u8bb2\u4e5f\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u4e91\u58c1\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e49\u7236\u533b\u672f\u901a\u795e\uff0c\u4f60\u5374\u4e00\u70b9\u672c\u9886\u4e5f\u6ca1\u5b66\u5230\u4e48\uff1f\u6211\u597d\u7aef\u7aef\u7684 \u4f60\u600e\u4e48\u8bf4\u6211\u53d1\u70e7\uff1f\u6211\u77e5\u9053\u4f60\u6025\u4e8e\u60f3\u77e5\u9053\u5979\u4eec\u6bcd\u5973\u7684\u6d88\u606f\uff0c\u7559\u5f85\u660e\u5929\u518d\u8bf4\uff0c\u4f60\u4e0d\u6015\u4eca\u665a\u7761\u4e0d\u7740 \u89c9\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u4e0d\u597d\u610f\u601d\u95ee\u5979\u4e3a\u4ec0\u4e48\u8138\u7ea2\uff0c\u4ed6\u53c8\u7559\u795e\u770b\u4e86\u4e00\u770b\uff0c\u89c1\u4e91\u58c1\u5e76\u6ca1\u9732\u51fa\u75b2\u5026\u7684\u795e \u6001\uff0c\u653e\u4e0b\u4e86\u5fc3\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5979\u8bf4\u5f97\u4e0d\u9519\uff0c\u8981\u662f\u5979\u73b0\u5728\u4e0d\u8bf4\uff0c\u6211\u4eca\u665a\u53ea\u6015\u771f\u7684\u96be\u4ee5\u5b89\u7720\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u4e91\u58c1\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u8c37\u5973\u4fa0\u8bf4\u5979\u8981\u5230\u9a6c\u8428\u513f\u76df\u53bb\uff0c\u636e\u8bf4\u90a3\u662f\u5728\u963f\u5c14\u6cf0\u5c71\u5c71\u811a\u4e00\u4e2a\u5f88 \u8fdc\u5f88\u8fdc\u7684\u5730\u65b9\uff0c\u8c37\u5973\u4fa0\u77e5\u9053\u6211\u7239\u7239\u66fe\u5230\u8fc7\u963f\u5c14\u6cf0\u5c71\u4e00\u5e26\uff0c\u6240\u4ee5\u6765\u5411\u4ed6\u63a2\u542c\u90a3\u4e2a\u5730\u65b9\u7684\u60c5\u5f62\uff0c \u53e6\u5916\u5979\u53c8\u8981\u6253\u542c\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5979\u6253\u542c\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u6c5f\u6d77\u5929\u672c\u4ee5\u4e3a\u8c37\u4e4b\u534e\u5b9a\u7136\u662f\u67e5\u8bbf\u4ed6\u5e08\u7236\u7684\u6d88\u606f\uff0c\u54ea \u77e5\u4e91\u58c1\u7b54\u9053\uff1a\u201c\u5979\u6253\u542c\u7684\u4eba\u6ca1\u6709\u540d\u5b57\u3002\u201d\u6c5f\u6d77\u5929\u8be7\u9053\uff1a\u201c\u600e\u4e48\u6ca1\u6709\u540d\u5b57\uff1f\u201d \u3000\u3000\u4e91\u58c1\u9053\uff1a\u201c\u5979\u8981\u6253\u542c\u7684\u662f\u5317\u65b9\u6b66\u6797\u4e2d\u65b0\u51fa\u9053\u7684\u672c\u9886\u6700\u5f3a\u7684\u5c11\u5e74\u8c6a\u6770\u3002\u5979\u56e0\u4e3a\u6211\u7239\u7239\u719f\u6089 \u5317\u9053\u4e0a\u7684\u5404\u8def\u82f1\u96c4\uff0c\u662f\u4ee5\u7279\u5730\u6765\u5411\u6211\u7239\u7239\u67e5\u8bbf\u7684\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u90a3\u662f\u8c01\u5462\uff1f\u201d\u4e91\u58c1\u9053\uff1a \u201c\u6211\u7239\u7239\u8bf4\u4e86\u597d\u513f\u4e2a\u9ed1\u767d\u4e24\u9053\u7684\u540e\u8d77\u4e4b\u79c0\uff0c\u5979\u4e00\u542c\u90fd\u4e0d\u662f\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5979\u600e\u4e48\u77e5\u9053\u4e0d \u662f\uff1f\u201d\u4e91\u58c1\u9053\uff1a\u201c\u5979\u8bf4\u5979\u6240\u8981\u67e5\u8bbf\u7684\u4eba\u4e43\u662f\u4e2a\u5341\u4e03\u516b\u5c81\u6b66\u529f\u6781\u597d\u7684\u5c11\u5e74\uff0c\u6211\u7239\u7239\u6240\u8bf4\u7684\u90a3\u51e0 \u4e2a\u4eba\uff0c\u6700\u5e74\u8f7b\u7684\u4e5f\u8fc7\u4e8c\u5341\u5c81\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u5fc3\u4e2d\u987f\u7136\u660e\u767d\u3002\u60f3\u9053\uff1a\u201c\u539f\u6765\u5979\u662f\u8981\u67e5\u8bbf\u83b2\u59b9\u7684\u54e5\u54e5\u3002\u53ea\u56e0\u5979\u4e0d \u613f\u6cc4\u6f0f\u83b2\u59b9\u7684\u8eab\u4e16\u4e4b\u8c1c\uff0c\u6240\u4ee5\u5bf9\u4e91\u8001\u82f1\u96c4\u4e5f\u672a\u66fe\u6d0b\u8a00\u3002\u201d \u3000\u3000\u4e91\u58c1\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u8c37\u5973\u4fa0\u548c\u6211\u7239\u7239\u6bcf\u5929\u90fd\u5728\u8bae\u8bba\u585e\u5916\u5404\u5730\u7684\u98ce\u4fd7\u6c11\u60c5\uff0c\u5c71\u5ddd\u5730\u7406\uff0c\u4ee5\u53ca \u6b66\u6797\u4e2d\u7684\u4eba\u7269\u60c5\u5f62\u3002\u505a\u5c0f\u8f88\u7684\u4e0d\u4fbf\u53bb\u6253\u6270\u4ed6\u4eec\uff0c\u6211\u7684\u54e5\u54e5\u4fbf\u4e50\u5f97\u5bf8\u6b65\u4e0d\u79bb\u7684\u966a\u7740\u90a3\u4f4d\u8c37\u59d1 \u5a18\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u4e91\u58c1\u53c8\u7b11\u4e86\u4e00\u7b11\uff0c\u518d\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u90a3\u4e00\u5929\uff0c\u4ed6\u4eec\u4e5f\u6b63\u662f\u5728\u8fd9\u8377\u5858\u65c1\u8fb9\uff0c\u8c37\u59d1 \u5a18\u8170\u4e0a\u4e5f\u662f\u7cfb\u7740\u8fd9\u6761\u624b\u7ee2\uff0c\u6211\u7684\u54e5\u54e5\u4e5f\u662f\u50cf\u4f60\u8fd9\u6837\uff0c\u8d5e\u624b\u7ee2\u4e0a\u7684\u83b2\u82b1\u7ee3\u5f97\u597d\u770b\u2026\u2026\u201d\u5979\u4e00\u8fde \u8bf4\u4e86\u4e09\u4e2a\u201c\u4e5f\u662f\u201d\uff0c\u8fd9\u624d\u84e6\u7136\u60f3\u8d77\u8fd9\u5c82\u4e0d\u662f\u628a\u6c5f\u6d77\u5929\u6bd4\u4f5c\u5979\u7684\u54e5\u54e5\uff0c\u800c\u81ea\u5df1\u5219\u662f\u90a3\u4f4d\u201c\u8c37\u59d1 \u5a18\u201d\u4e86\uff1f\u5979\u8138\u4e0a\u7684\u7ea2\u6655\u672c\u6765\u5df2\u7ecf\u892a\u4e86\u3002\u8fd9\u65f6\u4e0d\u89c9\u53c8\u7ea2\u5230\u4e86\u8033\u6839\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u5973\u5b69\u513f\u5bb6\u771f\u662f\u52a8\u4e0d\u52a8\u5c31\u5bb3\u7f9e\uff0c\u5979\u8bf4\u7684\u662f\u5979\u54e5\u54e5\u7684\u4e8b\u60c5\uff0c\u4e5f\u4f1a\u9762\u7ea2\uff0c\u54ce \u5440\uff0c\u96be\u9053\u83b2\u59b9\u548c\u5979\u7684\u54e5\u54e5\u8fd8\u6709\u4e0d\u582a\u8a00\u8bf4\u4e4b\u4e8b\uff1f\u201d\u6c5f\u6d77\u5929\u54ea\u91cc\u77e5\u9053\u4e91\u74a7\u4e0d\u662f\u4e3a\u4e86\u5979\u54e5\u54e5\u7684\u79c1 \u7cbe\uff0c\u800c\u662f\u4e3a\u4e86\u81ea\u5df1\u5fc3\u4e2d\u7684\u79d8\u5bc6\u800c\u9762\u7ea2\u3002 \u3000\u3000\u4e91\u74a7\u8f7b\u54b3\u4e00\u58f0\uff0c\u63a9\u9970\u4e86\u5979\u7684\u7a98\u6001\uff0c\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u8c37\u59d1\u5a18\u5012\u5f88\u5927\u65b9\uff0c\u5979\u628a\u624b\u7ee2\u62ff\u4e86\u51fa\u6765\uff0c \u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u81ea\u5df1\u7ee3\u7684\uff0c\u8fd8\u597d\u770b\u5417\uff1f\u201d\u6211\u7684\u54e5\u54e5\u53ef\u597d\u7b11\u4e86\uff0c\u4ed6\u7684\u8138\u7ea2\u5f97\u5c31\u50cf\u719f\u900f\u4e86\u7684\u67ff \u5b50\uff0c\u671f\u671f\u827e\u827e\u4ed6\u8bf4\u9053\uff1a\u201c\u597d\u770b\uff0c\u597d\u770b\u2026\u2026\u597d\u9999\uff0c\u597d\u9999\u2026\u2026\u6bd4\u6c60\u5b50\u7684\u83b2\u82b1\u8fd8\u597d\u770b\uff0c\u8fd8\u8981 \u9999\u2026\u2026\u201d\u5979\u5b66\u7740\u5979\u54e5\u54e5\u90a3\u65e5\u8bf4\u8bdd\u7684\u795e\u60c5\u548c\u8bed\u8c03\uff0c\u6c5f\u6d77\u5929\u4e5f\u4e0d\u89c9\u7ed9\u5979\u9017\u7b11\u4e86\u3002\u5fc3\u91cc\u5374\u53c8\u60f3\u9053\uff1a \u201c\u4f60\u53ea\u77e5\u9053\u8bf4\u4f60\u7684\u54e5\u54e5\uff0c\u4f60\u7684\u8138\u867d\u7136\u8fd8\u4e0d\u4f3c\u719f\u900f\u7684\u67ff\u5b50\uff0c\u5927\u7ea6\u4e5f\u5dee\u4e0d\u591a\u4e86\u3002\u201d \u3000\u3000\u4e91\u74a7\u7b11\u4e86\u4e00\u4f1a\uff0c\u7ee7\u7eed\u8bf4\u9053\uff0c\u201c\u90a3\u65f6\u6070\u597d\u6211\u4e5f\u5728\u573a\uff0c\u6211\u4fbf\u8bf4\u9053\uff1a\u2018\u8c37\u59d1\u5a18\uff0c\u6211\u54e5\u54e5\u5f88\u6b22\u559c \u4f60\u8fd9\u6761\u624b\u7ee2\uff0c\u4f60\u5c31\u9001\u7ed9\u4ed6\u5427\u3002\u54e5\u54e5\uff0c\u6211\u66ff\u4f60\u5f00\u53e3\u8ba8\u4e1c\u897f\uff0c\u4f60\u4e0d\u4f1a\u602a\u6211\u591a\u4e8b\u5427\uff1f\u2019\u6211\u54e5\u54e5\u65e2\u4e0d \u6562\u627f\u8ba4\uff0c\u53c8\u4e0d\u6562\u5426\u8ba4\uff0c\u66f4\u7a98\u4e86\u3002\u8c37\u59d1\u5a18\u7b11\u4e86\u4e00\u7b11\uff1b\u5374\u62ff\u51fa\u4e24\u65b9\u624b\u5e15\u6765\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u54e5\u54e5\u53ea\u8981\u4e00\u6761\uff0c\u5979\u9001\u4e86\u4e24\u6761\u4e48\uff1f\u201d\u4e91\u58c1\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u9001\u4e1c\u9762 \u53ea\u662f\u4e00\u79cd\u610f\u601d\uff0c\u4f60\u5f53\u662f\u5f53\u771f\u62ff\u6765\u7528\u7684\uff0c\u591a\u591a\u76ca\u5584\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u54e6\uff0c\u6211\u660e\u767d\u4e86\uff0c\u6709\u4e00\u6761 \u662f\u9001\u7ed9\u4f60\u7684\u3002\u201d\u4e91\u58c1\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u7ec8\u4e8e\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u60f3\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u96be\u731c\uff0c\u65e2\u7136\u4e0d\u662f\u4e24\u6761\u90fd\u9001\u4e0e\u4f60\u7684\u54e5\u54e5\uff0c\u90a3\u5f53\u7136\u662f\u6bcf\u4eba\u4e00\u6761\u4e86\uff0c \u5973\u5b69\u513f\u603b\u662f\u6b22\u559c\u628a\u5c0f\u4e8b\u90fd\u8bf4\u5f97\u5341\u5206\u7d27\u5f20\uff0c\u5341\u5206\u90d1\u91cd\u3002\u201d\u5176\u5b9e\u6c5f\u6d77\u5929\u662f\u5230\u4e86\u6b64\u9645\uff0c\u8fd8\u672a\u660e\u767d\u4e91 \u58c1\u6240\u8bf4\u7684\u90a3\u4e2a\u201c\u610f\u601d\u201d\uff0c\u56e0\u4e3a\u4ed6\u542c\u5f97\u51fa\u795e\uff0c\u4e00\u76f4\u628a\u5fc3\u601d\u653e\u5728\u8c37\u4e2d\u83b2\u4e0a\uff0c\u662f\u4ee5\u6839\u672c\u5c31\u5fd8\u8bb0\u4e86\u521a \u521a\u4e91\u58c1\u8bf4\u8981\u9001\u624b\u5e15\u7ed9\u4ed6\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e91\u74a7\u5e26\u7b11\u8bf4\u9053\uff1a\u201c\u5979\u62ff\u51fa\u4e24\u65b9\u624b\u5e15\uff0c\u4fbf\u5411\u6211\u7b11\u9053\uff1a\u2018\u4e00\u65b9\u624b\u5e15\uff0c\u503c\u5f97\u4ec0\u4e48\u3002\u65e2\u7136 \u4f60\u4eec\u6b22\u559c\uff0c\u4fbf\u8bf7\u6536\u4e0b\u5427\u3002\u2019\u4f60\u8bf4\uff0c\u6211\u662f\u4e0d\u662f\u6cbe\u4e86\u54e5\u54e5\u7684\u5149\uff1f\u201d\u6c5f\u6d77\u5929\u50bb\u91cc\u50bb\u6c14\u5730\u95ee\u9053\uff1a\u201c\u5979 \u662f\u671b\u7740\u4f60\u7b11\u5417\uff1f\u201d\u8bdd\u51fa\u4e86\u53e3\uff0c\u624d\u5ffd\u5730\u611f\u5230\u95ee\u5f97\u201c\u611a\u8822\u201d\uff0c\u95ee\u5f97\u201c\u65e0\u804a\u201d\u3002\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u4ed6\u542c \u8bf4\u8c37\u4e2d\u83b2\u662f\u5411\u7740\u4e91\u58c1\u7b11\uff0c\u5fc3\u4e2d\u4fbf\u4f3c\u5b89\u6170\u4e86\u4e00\u4e9b\u3002 \u3000\u3000\u4ed6\u7684\u95ee\u8bdd\uff0c\u9017\u5f97\u4e91\u58c1\u53c8\u662f\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5979\u5411\u6211\u7b11\uff0c\u5373\u662f\u5411\u6211\u7684\u54e5\u54e5\u7b11\u3002\u6211\u53ea \u662f\u966a\u886c\u7684\uff0c\u56e0\u4e3a\u6709\u6211\u5728\u573a\uff0c\u5979\u4e0d\u597d\u592a\u7740\u75d5\u8ff9\uff0c\u6240\u4ee5\u4e5f\u9001\u4e86\u4e00\u65b9\u7ed9\u6211\u3002\u4f60\u60f3\uff0c\u5979\u80af\u628a\u6c57\u5dfe\u9001\u7ed9 \u4e00\u4e2a\u7537\u5b50\uff0c\u800c\u8fd9\u4e2a\u7537\u5b50\uff0c\u53c8\u5e76\u975e\u662f\u50cf\u4f60\u5bf9\u6211\u4e00\u6837\uff0c\u6709\u6551\u547d\u4e4b\u6069\u7684\uff0c\u8fd9\u6837\u7684\u4ea4\u60c5\u5c82\u4e0d\u662f\u5f88\u4e0d\u5bfb \u5e38\u4e86\u4e48\uff1f\u201d \u3000\u3000\u5176\u5b9e\u6b63\u662f\u4e91\u74a7\u81ea\u5df1\u4e0d\u613f\u201c\u592a\u7740\u75d5\u8ff9\u201d\uff0c\u8bdd\u4e2d\u6709\u8bdd\u3001\u610f\u601d\u662f\u8bf4\uff1a\u201c\u5982\u679c\u662f\u6211\u9001\u7ed9\u4f60\uff1b\u4f60\u5c31 \u522b\u8981\u8bef\u4f1a\u3002\u201d\u5f53\u7136\uff0c\u5c31\u662f\u8fd9\u6697\u793a\u7684\u8bf4\u8bdd\uff0c\u4e5f\u53ea\u662f\u4e00\u79cd\u63a9\u9970\uff0c\u4e5f\u4e0d\u80fd\u5b8c\u5168\u4ece\u6b63\u9762\u89e3\u91ca\uff0c\u4fe1\u4ee5\u4e3a \u771f\u3002\u4f46\u6c5f\u6d77\u5929\u8fde\u7b2c\u4e00\u91cd\u610f\u601d\u4e5f\u672a\u61c2\u5f97\uff0c\u66f4\u4e0d\u8981\u8bf4\u7b2c\u4e8c\u91cd\u4e86\u3002\u4e00\u4e2a\u60c5\u7aa6\u521d\u5f00\u7684\u5c11\u5973\uff0c\u5fc3\u4e8b\u603b\u662f \u6781\u4e3a\u66f2\u6298\uff0c\u65e2\u6015\u5979\u6b22\u559c\u7684\u4eba\u77e5\u9053\uff0c\u4f46\u540c\u65f6\u5374\u53c8\u6015\u4ed6\u4e0d\u61c2\u3002\u8fd9\u79cd\u77db\u76fe\u7684\u5fc3\u60c5\uff0c\u53ea\u6709\u8fc7\u6765\u4eba\u624d\u4f1a \u660e\u767d\u3002 \u3000\u3000\u53e6\u4e00\u65b9\u9762\uff0c\u540c\u6837\u7684\u4e8b\u5b9e\u3001\u4e5f\u53ef\u4ee5\u6709\u5404\u79cd\u4e0d\u540c\u7684\u89e3\u91ca\uff0c\u5c31\u62ff\u8c37\u4e2d\u83b2\u9001\u624b\u5e15\u4e0e\u4e91\u58c1\u7684\u54e5\u54e5\u6765 \u8bf4\uff0c\u4e91\u58c1\u5c31\u662f\u4ee5\u81ea\u5df1\u5fc3\u610f\u6765\u4ee3\u66ff\u8c37\u4e2d\u83b2\u89e3\u91ca\uff0c\u8bf4\u6210\u662f\u8c37\u4e2d\u83b2\u6b22\u559c\u5979\u7684\u54e5\u54e5\uff0c\u800c\u5979\u4e0d\u8fc7\u662f\u6cbe\u4e86 \u54e5\u54e5\u7684\u5149\u800c\u5df2\u3002\u4f46\u5018\u82e5\u961f\u53e6\u4e00\u65b9\u9762\u89e3\u91ca\uff0c\u4e5f\u53ef\u8bf4\u662f\u4e91\u743c\u6cbe\u4e86\u4ed6\u59b9\u59b9\u7684\u5149\u3001\u8c37\u4e2d\u83b2\u4e3a\u4e86\u6015\u4ed6\u96be \u4e3a\u60c5\uff0c\u6240\u4ee5\u5144\u59b9\u90fd\u9001\uff0c\u8fd9\u6837\u5904\u7406\u6b63\u663e\u5f97\u843d\u843d\u5927\u65b9\u3002\u5f53\u7136\uff0c\u5230\u5e95\u662f\u54ea\u4e00\u79cd\u610f\u601d\uff1b\u53ea\u6709\u8c37\u4e2d\u83b2\u81ea \u5df1\u65b9\u80fd\u591f\u56de\u7b54\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u542c\u4e86\u8fd9\u6bb5\u201c\u6545\u4e8b\u201d\uff0c\u60d8\u60d8\u7136\u5982\u6709\u6240\u5931\uff0c\u54ea\u8fd8\u80fd\u591f\u5e73\u9759\u4e0b\u6765\u4ed4\u7ec6\u63a8\u6572\u8c37\u4e2d\u83b2\u7684\u5fc3 \u610f\u3002\u4e91\u58c1\u7b11\u9053\uff0c\u201c\u4f60\u5728\u60f3\u4e9b\u4ec0\u4e48\uff0c\u6211\u8bf4\u5f97\u8fd9\u6837\u6e05\u695a\u4e86\uff0c\u4f60\u8fd8\u4e0d\u660e\u767d\u4e48\uff1f\u2014\u2014\u6211\u662f\u8bf4\u4ed6\u4eec\u4e24\u4eba \u4e4b\u95f4\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u50bb\u91cc\u50bb\u6c14\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u660e\u767d\u4e86\uff0c\u660e\u767d\u4e86\uff0c\u4f60\u7684\u54e5\u54e5\u5f88\u597d\u3002\u201d\u8fd9\u4e24\u53e5\u8bdd\u8bf4 \u5f97\u751a\u4e3a\u7a81\u5140\uff0c\u4e4d\u542c\u4f3c\u662f\u8fde\u4e0d\u8d77\u7c73\u3002\u539f\u6765\u6c5f\u6d77\u5929\u5fc3\u91cc\u5728\u60f3\uff1a\u201c\u4e91\u743c\u51fa\u8eab\u540d\u95e8\uff0c\u6b66\u529f\u53c8\u5f3a\uff0c\u4eba\u53c8 \u82f1\u4fca\u3002\u5018\u82e5\u4ed6\u4e0e\u83b2\u59b9\u6210\u4e3a\u7231\u4fa3\uff0c\u90a3\u4e5f\u5f88\u597d\u5475\uff01\u201d \u3000\u3000\u4e91\u58c1\u7b11\u5f97\u6709\u5982\u82b1\u679d\u4e71\u98a4\uff1b\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u5979\u6bcd\u4eb2\u53eb\u9053\uff1a\u201c\u58c1\u513f\uff0c\u4f60\u8be5\u56de\u6765\u5403\u836f\u4e86\u3002\u201d \u3000\u3000\u4e91\u592b\u4eba\u542c\u5f97\u5979\u5973\u513f\u7684\u7b11\u58f0\uff0c\u8d70\u4e86\u8fc7\u6765\uff0c\u6b63\u81ea\u5fc3\u60f3\uff0c\u201c\u5979\u548c\u8c01\u8bf4\u5f97\u90a3\u4e48\u9ad8\u5174\uff1f\u201d\u4e00\u62ac\u5934\uff0c \u5c31\u770b\u89c1\u4e86\u6c5f\u6d77\u5929\uff0c\u5fc3\u4e2d\u5f88\u662f\u6b22\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u6c5f\u5c0f\u4fa0\u4f34\u7740\u4f60\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u89c1\u8fc7\u4e86\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u4e91\u59d1\u5a18\u597d\u5f97\u5f88\u5feb\uff0c\u4f2f\u6bcd\uff0c\u4f60\u53ef\u4ee5\u5b89\u5fc3\u4e86\u3002\u201d\u4e91\u592b\u4eba\u7709\u5f00\u773c \u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u90fd\u662f\u4f60\u7684\u529f\u52b3\u3002\u6c5f\u5c0f\u4fa0\uff0c\u8bf7\u5230\u5c4b\u5b50\u91cc\u8bf4\u8bdd\u5427\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\u4e86\uff0c\u6211\u5df2\u7ecf \u8017\u4e86\u4e91\u59d1\u5a18\u8bb8\u591a\u65f6\u95f4\uff0c\u73b0\u5728\u6211\u4e5f\u8be5\u56de\u53bb\u770b\u770b\u4e49\u7236\u4e86\u3002\u201d\u4e91\u592b\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u5230\u6211\u4eec\u8fd9\u91cc\u6765\uff0c\u6211 \u95e8\u90fd\u672a\u80fd\u966a\u4f60\u73a9\uff0c\u8fc7\u4e24\u5929\u58c1\u513f\u597d\u4e86\uff0c\u4f60\u53eb\u5979\u966a\u4f60\u5230\u5404\u5904\u8d70\u8d70\uff0c\u4e0d\u5fc5\u5ba2\u6c14\u3002\u201d \u3000\u3000\u4e91\u58c1\u6bcd\u5973\u8d70\u540e\uff0c\u6c5f\u6d77\u5929\u6005\u6005\u60d8\u60d8\uff0c\u4e00\u4f1a\u513f\u9ad8\u5174\uff0c\u4e00\u4f1a\u513f\u53c8\u4f3c\u6709\u70b9\u4f24\u5fc3\uff1a\u54ea\u8fd8\u6709\u5fc3\u60c5\u8d4f\u73a9 \u56ed\u5b50\u91cc\u7684\u98ce\u5149\uff1a\u4ed6\u60d8\u60d8\u7136\u5730\u8d70\u4e86\u4e00\u4f1a\uff0c\u7ecf\u8fd9\u4e00\u7247\u7af9\u6797\uff0c\u5ffd\u7136\u53c8\u542c\u5f97\u4e00\u9635\u9635\u7684\u5973\u5b69\u5b50\u7684\u7b11\u58f0\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u672c\u662f\u65e0\u5fc3\u5077\u542c\uff0c\u4f46\u90a3\u5973\u5b69\u5b50\u7684\u8bdd\u58f0\u5df1\u94bb\u8fdb\u4ed6\u7684\u8033\u6735\uff0c\u53ea\u542c\u5f97\u5979\u683c\u683c\u7b11\u9053\uff1a\u201c\u8001\u592b \u4eba\u8fd9\u4e3b\u610f\u5999\u5f97\u7d27\u554a\uff01\u5018\u82e5\u771f\u80fd\u6210\u4e8b\uff0c\u5c82\u4e0d\u662f\u53cc\u559c\u4e34\u95e8\u4e86\u4e48\uff1f\u201d\u53e6\u4e00\u4e2a\u5973\u5b69\u5b50\u63a5\u7740\u7b11\u9053\uff1a\u201c\u8001 \u592b\u4eba\u7684\u7b97\u76d8\u662f\u6253\u5f97\u4e0d\u9519\uff0c\u4f46\u4f9d\u6211\u770b\u6765\uff0c\u8fd9\u4e24\u6869\u559c\u4e8b\uff0c\u53ea\u6015\u53ea\u80fd\u6210\u5c31\u4e00\u6869\u3002\u201d\u5148\u5934\u90a3\u5973\u5b50\u8bf4 \u9053\uff1a\u201c\u54e6\uff1b\u4f60\u662f\u5c0f\u59d0\u7684\u5fc3\u8179\u5a62\u5973\uff0c\u83ab\u975e\u4f60\u5df2\u7ecf\u77e5\u9053\u4e86\u5c0f\u59d0\u7684\u5fc3\u610f\uff0c\u5c0f\u59d0\u4e0d\u613f\u5ac1\u90a3\u59d3\u6c5f\u7684 \u4e48\uff1f\u201d\u539f\u6765\u662f\u4e24\u4e2a\u4e2b\u9b1f\u5728\u80cc\u540e\u5077\u5077\u8c08\u8bba\u5c0f\u59d0\u548c\u516c\u5b50\u7684\u5a5a\u4e8b\u3002\u6c5f\u6d77\u5929\u4e00\u542c\uff0c\u6b63\u662f\u8bf4\u5230\u4ed6\u7684\u8eab \u4e0a\uff0c\u4e0d\u89c9\u505c\u4e0b\u4e86\u811a\u6b65\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u8bdd\u4ece\u54ea\u513f\u8bf4\u8d77\uff1f\u8fd9\u59d3\u6c5f\u7684\u6216\u8005\u662f\u53e6\u6709\u5176\u4eba\u5427\uff1f\u201d \u3000\u3000\u4e91\u58c1\u90a3\u4e2a\u8d34\u8eab\u5a62\u5973\u9053\uff1a\u201c\u5c0f\u59d0\u5012\u6ca1\u6709\u900f\u9732\u8fc7\u5979\u7684\u5fc3\u610f\uff0c\u4e0d\u8fc7\uff0c\u4f9d\u6211\u770b\u6765\uff0c\u5979\u662f\u5343\u80af\u4e07\u80af \u7684\u4e86\u3002\u6210\u95ee\u9898\u662f\u54b1\u4eec\u7684\u5c11\u7237\uff0c\u4ed6\u4e00\u5b9a\u4e0d\u4f1a\u7b54\u5e94\uff0c\u201d\u5148\u5934\u90a3\u4e2b\u9b1f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff0c\u90a3\u4f4d\u534e\u59d1\u5a18\u4e0d \u4e5f\u662f\u624d\u8c8c\u53cc\u5168\u4e48\uff1f\u201d \u3000\u3000\u4e91\u58c1\u90a3\u8d34\u8eab\u5a62\u5973\u7b11\u9053\uff1a\u201c\u4f60\u7684\u8033\u6735\u592a\u4e0d\u7075\u4e86\uff0c\u4f60\u4e0d\u77e5\u9053\u5c11\u7237\u65e9\u5df2\u6709\u4e86\u5fc3\u4e0a\u4eba\u4e48\uff1f\u201d\u5c31\u662f \u4e0a\u4e2a\u6708\u6765\u7684\u90a3\u4f4d\u8c37\u59d1\u3002\u6211\u542c\u5f97\u670d\u4f8d\u5c11\u7237\u7684\u674f\u4e2b\u5934\u8bf4\uff0c\u90a3\u4f4d\u8c37\u59d1\u5a18\u8d70\u540e\uff0c\u4ed6\u5931\u9b42\u843d\u9b44\u7684\u597d\u51e0\u5929 \u5462\uff0c\u5e38\u5e38\u4e00\u4e2a\u4eba\u5728\u8377\u6c60\u8fb9\u53d1\u5446\u3002\u4e0d\u8fc7\u5c11\u7237\u8138\u76ae\u5ae9\uff0c\u4e0d\u6562\u5bf9\u4ed6\u7236\u6bcd\u8bb2\u3002\u201d\u5148\u5934\u90a3\u4e2b\u9b1f\u9053\uff1a\u201c\u539f \u6765\u5982\u6b64\u3002\u4f46\u4f60\u53c8\u600e\u9053\u5c0f\u59d0\u8fd9\u95e8\u4eb2\u4e8b\u51c6\u6210\uff1f\u201d\u4e91\u58c1\u7684\u8d34\u8eab\u4e2b\u56ca\u9053\uff0c\u201c\u8fd9\u4e2a\u5440\uff0c\u6709\u4e24\u4e2a\u7406\u7531\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u6211\u5012\u8981\u542c\u542c\u662f\u4ec0\u4e48\u7406\u7531\uff1f\u201d\u53ea\u542c\u5f97\u90a3\u4e2b\u9b1f\u8bf4\u9053\uff1a\u201c\u7b2c\u4e00\u4e2a\u7406\u7531\u3002\u54b1\u4eec\u7684 \u5c0f\u59d0\u548c\u90a3\u4f4d\u6c5f\u5c0f\u4fa0\u5df2\u662f\u8840\u8089\u76f8\u8fde\uff0c\u4e0d\u5ac1\u4ed6\u8fd8\u80fd\u5ac1\u8c01\uff1f\u201d\u53e6\u4e00\u4e2a\u4e2b\u9b1f\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u8fd9\u6837\u3002\u6211 \u4e5f\u66fe\u542c\u8bf4\u90a3\u665a\u6c5f\u5c0f\u4fa0\u6551\u5c0f\u59d0\u7684\u547d\uff0c\u4e43\u662f\u5c06\u4ed6\u7684\u8840\u8f93\u5230\u5c0f\u59d0\u8eab\u4f53\u5185\u7684\uff0c\u6211\u4ece\u6ca1\u6709\u542c\u8fc7\u8fd9\u6837\u7684\u4e8b \u60c5\uff0c\u5f53\u521d\u8fd8\u4e0d\u76f8\u4fe1\u5462\u3002\u73b0\u5728\u542c\u4f60\u8bf4\u6765\uff0c\u7adf\u662f\u771f\u7684\u4e86\u3002\u201d \u3000\u3000\u4e91\u58c1\u90a3\u8d34\u8eab\u5a62\u5973\u9053\uff1a\u201c\u5c31\u662f\u56e0\u4e3a\u5c0f\u59d0\u7684\u8eab\u4f53\u91cc\u3002\u6709\u4e86\u4e00\u4e2a\u7537\u4e8e\u7684\u8840\u6db2\u5440\uff0c\u542c\u90a3\u4e9b\u8001\u5988\u5988 \u8bf4\u3002\u53e4\u65f6\u5019\u7684\u5927\u5bb6\u95fa\u79c0\uff0c\u53ea\u8981\u7ed9\u964c\u751f\u7537\u4eba\u770b\u4e86\u4e00\u773c\uff0c\u5c31\u975e\u5f97\u5ac1\u90a3\u7537\u4eba\u4e0d\u6210\u3002\u4e91\u5bb6\u867d\u8bf4\u662f\u6b66\u6797 \u4eba\u7269\uff0c\u5bf9\u4ec0\u4e48\u2018\u7537\u5973\u6388\u53d7\u4e0d\u4eb2\u2019\u4e4b\u7c7b\u7684\u81ed\u89c4\u77e9\u770b\u5f97\u4e0d\u91cd\u3002\u4f46\u6c5f\u5c0f\u4fa0\u7684\u9c9c\u8840\u548c\u5c0f\u59d0\u7684\u6df7\u6210\u4e00 \u7247\uff0c\u8fd9\u5230\u5e95\u4e0d\u6bd4\u5bfb\u5e38\uff0c\u518d\u5ac1\u522b\u7684\u7537\u4eba\u603b\u4f3c\u4e4e\u6709\u70b9\u4e0d\u59a5\uff0c\u4f60\u8bf4\u662f\u5417\uff1f\u201d\u6c5f\u6d77\u5929\u542c\u4e86\uff0c\u5fc3\u91cc\u6697\u6697 \u53eb\u82e6\uff0c\u4ed6\u5f53\u65e5\u4e00\u610f\u6551\u4eba\u3001\u54ea\u60f3\u60f3\u5230\u522b\u4eba\u4f1a\u6709\u8fd9\u6837\u7684\u770b\u6cd5\uff0c\u5fc3\u9053\uff1a\u201c\u4f46\u613f\u4e91\u5bb6\u7236\u5973\u4e0d\u662f\u8fd9\u6837\u60f3 \u624d\u597d\u3002\u201d \u3000\u3000\u5148\u5934\u90a3\u4e2b\u9b1f\u9053\uff1a\u201c\u7b2c\u4e8c\u4e2a\u7406\u7531\u5462\uff1f\u201d\u4e91\u58c1\u90a3\u8d34\u8eab\u5a62\u5973\u9053\uff1a\u201c\u7b2c\u4e8c\u4e2a\u7406\u7531\uff0c\u662f\u8005\u7237\u548c\u592b\u4eba \u4e5f\u975e\u5e38\u6b22\u559c\u90a3\u59d3\u6c5f\u7684\uff1b\u8fd9\u4e24\u665a\uff0c\u6211\u8001\u662f\u542c\u5f97\u4ed6\u4eec\u5728\u5411\u5c0f\u59d0\u5938\u8bf4\u90a3\u4f4d\u6c5f\u5c0f\u4fa0\uff0c\u8bf4\u4ed6\u662f\u540e\u8f88\u4e2d\u7684 \u7b2c\u4e00\u4eba\u7269\uff0c\u6b66\u529f\u597d\u5230\u4e0d\u5f97\u4e86\uff0c\u4eba\u7269\u53c8\u597d\u5230\u4e0d\u5f97\u4e86\u3002\u542c\u8fd9\u53e3\u6c14\uff0c\u5f53\u7136\u662f\u60f3\u628a\u4ed6\u62db\u4f5c\u5973\u5a7f\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e2b\u9b1f\u7b11\u9053\uff1a\u201c\u5c0f\u59d0\u4e0d\u6bd4\u5c11\u7237\u5df2\u6709\u4e86\u5fc3\u4e0a\u4eba\uff0c\u8fd9\u4e48\u8bf4\uff0c\u8fd9\u6869\u5a5a\u4e8b\u662f\u5fc5\u7136\u6210\u529f\u7684\u4e86\u3002\u201d\u5728 \u5979\u4eec\u7684\u5fc3\u76ee\u4e2d\uff0c\u4e91\u5bb6\u662f\u6b66\u6797\u6570\u4e00\u6570\u4e8c\u7684\u4eba\u5bb6\uff0c\u53ea\u8981\u5973\u65b9\u80af\u4e86\uff0c\u7537\u65b9\u5c31\u51b3\u65e0\u62d2\u7edd\u4e4b\u7406\uff0c\u56e0\u6b64\u5979 \u4eec\u6839\u672c\u5c31\u6ca1\u6709\u8003\u8651\u8fc7\u6c5f\u6d77\u5929\u80af\u662f\u4e0d\u80af\uff1f \u3000\u3000\u90a3\u4e2b\u9b1f\u53c8\u95ee\u9053\uff1a\u201c\u65e2\u7136\u7136\u5982\u6b64\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u63d0\u4eb2\uff1f\u201d\u4e91\u58c1\u90a3\u8d34\u8eab\u5a62\u5973\u9053\uff1a\u201c\u8fd9\u4e2a\u561b\uff0c\u4e5f\u6709 \u4e24\u4e2a\u7406\u7531\u3002\u201d\u90a3\u4e2b\u9b13\u7b11\u9053\uff1a\u201c\u4f60\u7684\u4e24\u4e2a\u7406\u7531\u53c8\u6765\u4e86\u3002\u201d\u539f\u6765\u4e91\u8087\u90a3\u8d34\u8eab\u5a62\u5973\uff0c\u95ee\u5979\u4ec0\u4e48\u4e8b \u60c5\uff0c\u5979\u90fd\u603b\u8981\u51d1\u591f\u201c\u4e24\u4e2a\u7406\u7531\u201d\u7684\uff0c\u8fd9\u5df2\u7ecf\u6210\u4e3a\u53e3\u5934\u7985\u4e86\u3002 \u3000\u3000\u4e91\u58c1\u90a3\u8d34\u8eab\u5a62\u5973\u7b11\u9053\uff1a\u201c\u4f60\u542c\u7740\uff1a\u8fd9\u4e24\u4e2a\u7406\u7531\u53ef\u4e0d\u662f\u51d1\u7684\u3002\u7b2c\u4e00\u3001\u56e0\u4e3a\u90a3\u4f4d\u534e\u8001\u5148\u751f\u5c1a \u672a\u75c5\u597d\uff0c\u8001\u7237\u548c\u592b\u4eba\u5546\u8bae\uff0c\u4e00\u5f85\u75c5\u597d\u5c31\u63d0\u3002\u201d\u90a3\u4e2b\u9b1f\u9053\uff1a\u201c\u4f60\u542c\u5f97\u4ed6\u4eec\u8fd9\u6837\u5546\u8bae\u7684\uff1f\u201d\u4e91\u58c1 \u90a3\u8d34\u8eab\u5a62\u5973\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u6211\u65e0\u610f\u95f4\u542c\u5f97\u4ed6\u4eec\u5546\u8bae\uff0c\u6240\u4ee5\u540e\u6765\u592b\u4eba\u5c31\u53eb\u6211\u5355\u72ec\u8fdb\u53bb\uff0c\u5429\u5490\u6211\u4e0d \u8bb8\u5c06\u6d88\u606f\u8fc7\u65e9\u6cc4\u6f0f\uff0c\u56e0\u4e3a\u6015\u5c0f\u59d0\u77e5\u9053\u4e86\uff0c\u5c0f\u59d0\u4e5f\u8bb8\u5c31\u4f1a\u5bb3\u7f9e\uff0c\u4e0d\u6562\u966a\u6c5f\u76f8\u516c\u73a9\u800d\u4e86\u3002\u6211\u77e5\u9053 \u592b\u4eba\u7684\u7528\u5fc3\uff0c\u592b\u4eba\u662f\u60f3\u5728\u63d0\u4eb2\u4e4b\u524d\uff0c\u4ed6\u4eec\u4fbf\u6210\u4e3a\u4e86\u4e00\u5bf9\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u542c\u8fd9\u91cc\uff0c\u4e00\u5207\u90fd\u5df2\u660e\u767d\uff0c\u6084\u6084\u4fbf\u8d70\uff0c\u4ed6\u5fc3\u4e2d\u4e03\u4e0a\u516b\u843d\uff0c\u6709\u70b9\u6b22\u559c\uff0c\u4e5f\u6709\u70b9\u5fc3\u70e6\uff1a \u6b63\u662f\uff1a \u3000\u3000\u53ea\u56e0\u91cd\u4e49\u7518\u8f93\u8840\uff0c\u4e0d\u6599\u60c5\u4e1d\u5df2\u6697\u7275\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u70db\u5f71\u6447\u7ea2\u817e\u6740\u6c14\u3000\u5200\u5149\u5982\u96ea\u95f9\u534e\u5802 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u70db\u5f71\u6447\u7ea2\u817e\u6740\u6c14\u3000\u5200\u5149\u5982\u96ea\u95f9\u534e\u5802 \u3000\u3000\u4e00\u822c\u6765\u8bf4\uff0c\u5973\u5b69\u5b50\u603b\u662f\u6bd4\u7537\u5b69\u5b50\u8f83\u4e3a\u65e9\u719f\uff0c\u6240\u4ee5\u540c\u6837\u5e74\u9f84\uff0c\u534e\u4e91\u78a7\u4e0e\u4e91\u58c1\u5df2\u662f\u60c5\u7aa6\u521d \u5f00\uff0c\u800c\u6c5f\u6d77\u5929\u5374\u8fd8\u672a\u66fe\u8003\u8651\u8fc7\u5a5a\u59fb\u7684\u95ee\u9898\uff0c\u5bf9\u7537\u5973\u4e4b\u60c5\uff0c\u4e5f\u8fd8\u662f\u534a\u61c2\u4e0d\u61c2\uff0c\u5c3d\u7ba1\u4ed6\u4e5f\u4f1a\u65f6\u5e38 \u601d\u5ff5\u8c37\u4e2d\u83b2\uff0c\u4f46\u90a3\u53ea\u662f\u7531\u4e8e\u9752\u6885\u7af9\u9a6c\u4e4b\u4ea4\uff0c\u5bf9\u4ed6\u7684\u5370\u8c61\u7279\u522b\u6df1\u523b\uff1a\u6700\u591a\u53ea\u80fd\u8bf4\u662f\u4e00\u79cd\u201c\u6726\u80e7 \u7684\u604b\u6155\u201d\u800c\u5df2\u3002\u56e0\u6b64\u73b0\u5728\u4ed6\u542c\u5f97\u90a3\u4e24\u4e2a\u4e2b\u9b13\u79c1\u8bed\uff0c\u8bf4\u662f\u4e91\u53ec\u51c6\u5907\u5c06\u4ed6\u62db\u4e3a\u5973\u5a7f\uff0c\u4fbf\u4e0d\u89c9\u610f\u4e71 \u5fc3\u70e6\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5018\u82e5\u5f85\u4ed6\u771f\u7684\u5411\u4e49\u7236\u63d0\u8d77\u5a5a\u4e8b\uff0c\u53ef\u53eb\u6211\u600e\u751f\u56de\u7b54\uff1f\u54ce\u5440\uff0c\u90a3\u4e0d\u662f\u96be\u4e3a\u60c5 \u6b7b\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e00\u8def\u80e1\u601d\u4e71\u60f3\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u56de\u5230\u81ea\u5df1\u7684\u623f\u95f4\uff0c\u542c\u5f97\u90bb\u623f\u534e\u5929\u98ce\u7236\u5973\u7684\u8c08\u8bdd\u58f0\uff0c\u4fbf \u63a8\u95e8\u8fdb\u53bb\u3002 \u3000\u3000\u534e\u4e91\u78a7\u7b11\u9053\uff1a\u201c\u6d77\u54e5\uff0c\u4f60\u5230\u54ea\u513f\u53bb\u4e86\uff0c\u7239\u521a\u624d\u6b63\u63d0\u8d77\u4f60\u5462\uff01\u201d\u6c5f\u6d77\u5929\u542b\u7cca\u5e94\u9053\uff1a\u201c\u6211\u5230 \u56ed\u4e8e\u91cc\u8d70\u4e86\u4e00\u8d9f\uff0c\u83b2\u82b1\u5df2\u5728\u76db\u5f00\u4e86\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u6d77\u513f\uff0c\u4f60\u5168\u597d\u4e86\uff1f\u4f60\u8bd5\u7ec3\u8fc7\u529f\u592b\u6ca1\u6709\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4eca\u65e9\u5df2\u7ec3\u8fc7\u4e00\u8d9f\uff0c \u5927\u81f4\u6062\u590d\u4e86\uff0c\u5e72\u7239\uff0c\u4f60\u5462\uff1f\u201d\u534e\u5929\u98ce\u7b11\u9053\uff1a\u201c\u6211\u6700\u5c11\u8fd8\u5f97\u4e2a\u591a\u6708\uff0c\u6240\u4ee5\u6211\u624d\u60f3\u548c\u4f60\u5546\u91cf\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u60f3\u95ee\u4ed6\u5546\u91cf\u4f55\u4e8b\uff0c\u534e\u4e91\u78a7\u5374\u7b11\u9053\uff1a\u201c\u6d77\u54e5\uff0c\u96be\u4e3a\u4f60\u521a\u521a\u75ca\u6108\uff0c\u5c31\u6709\u8fd9\u4e48\u597d\u5174\u81f4 \u53bb\u8d4f\u83b2\u82b1\u3002\u201d\u6c5f\u6d77\u5929\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6709\u5fc3\u8d4f\u83b2\uff0c\u6211\u662f\u5728\u60f3\uff0c\u2026\u2026\u201d\u534e\u4e91\u78a7\u6709\u70b9\u8be7 \u5f02\uff0c\u51dd\u671b\u7740\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u53ef\u662f\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u6211\u89c1\u83b2\u82b1\u76db\u5f00\uff0c\u60f3\u8d77\u65f6\u5149\u8fc7\u5f97\u771f\u5feb\uff0c\u6211\u6ca1\u6709\u8ba1\u7b97\u65e5\u5b50\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48 \u65f6\u8282\u4e86\uff1f\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u8fd8\u6709\u4e09\u5929\u5c31\u662f\u4e03\u5de7\u8282\u4e00\u600e\u4e48\u5566\uff01\u4f60\u53ef\u6709\u70b9\u50cf\u662f\u5931\u9b42\u843d\u9b44\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u7b11\u9053\uff1a\u201c\u6d77\u513f\uff0c\u6211\u5df2\u7ecf\u77e5\u9053\u4f60\u5728\u60f3\u4e9b\u4ec0\u4e48\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u5fc3\u5934\u4e00\u8df3\uff0c\u53ea\u542c\u5f97\u534e\u5929\u98ce \u63a5\u7740\u8bf4\u9053\uff1a\u201c\u65f6\u95f4\u662f\u8fc7\u5f97\u5feb\uff0c\u73b0\u5728\u79bb\u5f00\u4e2d\u79cb\u8282\u53ea\u6709\u4e00\u4e2a\u6708\u96f6\u5341\u4e00\u5929\u4e86\uff0c\u4f60\u53ef\u662f\u8bb0\u6302\u7740\u91d1\u9e70\u5bab \u4e4b\u4f1a\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u677e\u4e86\u53e3\u6c14\u8bf4\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u6211\u5df2\u4ee3\u8c37\u5973\u4fa0\u63a5\u4e0b\u4e86\u8bf7\u5e16\uff0c\u4e0d\u80fd\u5931\u4fe1\u4e8e\u4eba\uff0c\u603b\u788d\u5982\u671f \u8d76\u81f3\u624d\u597d\uff1b\u53ef\u662f\u5e72\u7239\u4f60\u2026\u2026\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u6709\u4e91\u5e84\u4e3b\u7167\u6599\uff0c\u4f60\u5c3d\u53ef\u653e\u5fc3\uff0c\u6211\u521a\u624d\u5c31 \u662f\u60f3\u8d77\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6240\u4ee5\u8981\u4e0e\u4f60\u5546\u91cf\uff0c\u65e2\u7136\u4f60\u5df2\u7ecf\u75ca\u6108\uff0c\u4f60\u5c31\u65e9\u65e5\u53bb\u8d76\u7ea6\u5427\uff0c\u53ef\u60dc\u6211\u4e0d\u80fd\u966a\u4f60 \u4e86\u3002\u6211\u672c\u6765\u60f3\u53eb\u78a7\u513f\u548c\u4f60\u4e00\u9053\u8d70\u7684\u2014\u2014\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fd9\u9053\uff1a\u201c\u5e72\u7239\uff1a\u4f60\u5728\u75c5\u4e2d\u603b\u5f97\u6709\u4e2a\u4eb2\u8fd1\u7684\u4eba\u968f\u8eab\u7167\u4f8b\uff0c\u6211\u4e0d\u80fd\u966a\u4f34\u4e8e\u4f60\uff0c\u5df2\u662f\u5fc3 \u6709\u4e0d\u5b89\uff0c\u53c8\u8981\u78a7\u59b9\u79bb\u5f00\uff0c\u90a3\u662f\u4e07\u4e07\u4e0d\u53ef\u3002\u201d\u534e\u5929\u98ce\u672c\u662f\u8bd5\u63a2\u6c5f\u6d77\u5929\u7684\u610f\u601d\uff0c\u8981\u77e5\u5b64\u7537\u5be1\u5927\u540c \u884c\uff0c\u603b\u5f97\u6709\u4e2a\u540d\u4efd\uff0c\u624d\u4e0d\u81f3\u843d\u4eba\u95f2\u8bdd\uff0c\u56e0\u6b64\u5018\u82e5\u6c5f\u6d77\u5929\u613f\u610f\u4ed6\u5973\u513f\u540c\u5728\uff0c\u5c31\u53ef\u4ee5\u987a\u7406\u6210\u7ae0\u63d0 \u8d77\u5a5a\u4e8b\u3002\u2026\u2026 \u3000\u3000\u4ed6\u542c\u5f97\u6c5f\u6d77\u5929\u5982\u6b64\u56de\u7b54\uff0c\u6709\u70b9\u5931\u671b\uff0c\u4f46\u968f\u5373\u60f3\u9053\uff1a\u201c\u5b69\u4e8e\u8fd8\u5c0f\u7740\u5462\uff0c\u8fc7\u51e0\u5e74\u518d\u8bf4\u4e5f\u4e0d \u8fdf\u3002\u201d\u201c\u4ed6\u867d\u7136\u4e0d\u660e\u6211\u7684\u5fc3\u610f\uff0c\u4f46\u603b\u662f\u4e3a\u6211\u7740\u60f3\u3002\u201d\u5982\u6b64\u4e00\u60f3\uff0c\u5fc3\u4e2d\u4e5f\u81ea\u6b23\u6170\uff0c\u4fbf\u9053\uff1a\u201c\u78a7 \u513f\u4e5f\u60f3\u5230\u8fd9\u5c42\uff0c\u5979\u4e5f\u629b\u4e0d\u4e0b\u6211\uff0c\u53ea\u597d\u8ba9\u4f60\u4e00\u4eba\u4e0a\u8def\u4e86\u3002\u4f46\u4f60\u6beb\u65e0\u6c5f\u6e56\u7ecf\u9a8c\u3002\u4e00\u8def\u4e4b\u4e0a\uff0c\u987b\u5f97 \u4e8b\u4e8b\u5f53\u5fc3\u65b9\u597d\u3002\u78a7\u513f\uff0c\u4f60\u53bb\u8bf7\u4e91\u5e84\u4e3b\u8fc7\u6765\u3002\u4ed6\u5728\u585e\u5916\u4ea4\u6e38\u6781\u5e7f\uff0c\u6211\u518d\u9762\u6258\u4ed6\u7167\u6599\u4f60\u3002\u201d \u3000\u3000\u4e91\u53ec\u542c\u5f97\u6c5f\u6d77\u5929\u8981\u8d74\u91d1\u9e70\u5bab\u7684\u4e2d\u79cb\u4e4b\u4f1a\uff0c\u9887\u4e3a\u8be7\u5f02\uff0c\u95ee\u9053\uff1a\u201c\u91d1\u9e70\u5bab\u4e3b\u4eba\u600e\u4f1a\u77e5\u9053\u4f60 \u7684\uff1f\u201d\u7ecf\u6c5f\u6d77\u5929\u8bf4\u660e\u4e4b\u540e\uff0c\u4e91\u53ec\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u4ee3\u8c37\u5973\u4fa0\u63a5\u7684\u8bf7\u5e16\uff0c\u53c8\u66fe\u5411\u4e3b\u9e70\u5bab\u7684\u4ec6\u4eba\u663e \u8fc7\u6b66\u529f\uff0c\u8fd9\u5c31\u5bf9\u4e86\u3002\u201d\u539f\u6765\u4e91\u53ec\u4e5f\u66fe\u6536\u5230\u4e00\u4efd\u8bf7\u5e16\uff0c\u4ed6\u662f\u77e5\u9053\u91d1\u9e70\u5bab\u7684\u8bf7\u5e16\u53ea\u53d1\u7ed9\u6210\u540d\u82f1\u96c4 \u7684\uff0c\u662f\u4ee5\u6709\u6b64\u4e00\u95ee\u3002 \u3000\u3000\u4e91\u53ec\u9053\uff0c\u201c\u8c37\u5973\u4fa0\u6708\u524d\u66fe\u7ecf\u8fc7\u6b64\u5730\uff0c\u5728\u820d\u4e0b\u4f4f\u4e86\u51e0\u5929\u3002\u90a3\u65f6\uff0c\u5979\u8fd8\u672a\u77e5\u6709\u91d1\u9e70\u5bab\u4e4b\u4f1a\uff0c \u66f4\u4e0d\u77e5\u9053\u4f1a\u6709\u8bf7\u5e16\u7ed9\u5979\u3002\u597d\u5728\u5979\u8981\u53bb\u7684\u5730\u65b9\uff0c\u5c31\u662f\u91d1\u9e70\u5bab\u4e3b\u4eba\u6240\u5728\u7684\u9a6c\u8428\u513f\u76df\u3002\u4f60\u5230\u90a3\u513f\uff0c \u8bf4\u4e0d\u5b9a\u5c31\u4f1a\u9047\u89c1\u5979\u3002\u201d \u3000\u3000\u4e91\u53ec\u53c8\u9053\uff1a\u201c\u4f60\u6709\u4e8b\u5728\u8eab\uff0c\u6211\u4e0d\u4fbf\u62e6\u963b\uff0c\u4f46\u660e\u5929\u5c31\u8d70\uff0c\u672a\u514d\u592a\u5306\u4fc3\u4e86\u5427\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a \u201c\u6211\u8fd8\u60f3\u5728\u7ecf\u8fc7\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\u7684\u65f6\u5019\uff0c\u524d\u5f80\u51b0\u5bab\uff0c\u62dc\u89c1\u5510\u7ecf\u5929\u592b\u5987\uff0c\u63a2\u95ee\u6211\u7236\u3001\u5e08\u7684\u6d88 \u606f\u3002\u201d\u4e91\u53ec\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u4e48\u660e\u5929\u6211\u7ed9\u4f60\u996f\u884c\u3002\u201d \u3000\u3000\u4e00\u5bbf\u65e0\u8bdd\uff0c\u7b2c\u4e8c\u5929\u559d\u8fc7\u4e86\u4e91\u53ec\u7684\u996f\u884c\u9152\uff0c\u6c5f\u6d77\u5929\u5148\u53bb\u5411\u534e\u5929\u98ce\u7236\u5973\u544a\u8f9e\uff0c\u534e\u4e91\u78a7\u9001\u4ed6\u5230 \u623f\u95e8\u53e3\uff0c\u4fbf\u5373\u6b62\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u6d77\u54e5\uff0c\u6055\u6211\u4e0d\u8fdc\u9001\u4f60\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u8981\u7167\u6599\u7239\u7239\uff0c\u4e0d\u7528 \u5ba2\u6c14\u3002\u201d\u534e\u4e91\u78a7\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u662f\u6015\u5728\u4eba\u524d\u54ed\u4e86\u51fa\u6765\uff0c\u53eb\u4eba\u7b11\u8bdd\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8fd9\u624d\u6ce8\u610f\u5230\u5979\u53cc\u773c\u7ea2\u6da6\u3002\u773c\u6cea\u5df2\u662f\u6ceb\u7136\u6b32\u6ef4\u3002\u4e0d\u7981\u5927\u4e3a\u611f\u52a8\uff0c\u63e1\u4f4f\u5979\u7684\u624b\u9053\uff1a \u201c\u6211\u8fc7\u4e86\u4e2d\u79cb\u4e4b\u4f1a\uff0c\u5c31\u4f1a\u56de\u6765\u770b\u4f60\u7684\u3002\u4f46\u76fc\u6211\u80fd\u627e\u7740\u7239\u7239\uff0c\u6211\u7239\u7239\u77e5\u9053\u4e86\u54b1\u4eec\u7684\u4e8b\uff0c\u4ed6\u4e5f\u4e00 \u5b9a\u5f88\u6b22\u559c\u7684\u3002\u201d\u6c5f\u6d77\u5929\u4e4b\u610f\uff0c\u662f\u6307\u4ed6\u4eec\u7ed3\u4e3a\u5144\u59b9\u4e4b\u4e8b\uff0c\u534e\u4e91\u78a7\u542c\u4e86\u3002\u5374\u522b\u6709\u4f1a\u5fc3\uff0c\u8138\u513f\u4e00 \u7ea2\uff0c\u79cb\u6ce2\u4e00\u8f6c\uff0c\u8f7b\u8f7b\u9053\u4e86\u4e00\u58f0\uff1a\u201c\u73cd\u91cd\u201d\uff0c\u5c31\u56de\u5934\u8d70\u8fdb\u623f\u95f4\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u518d\u53bb\u5411\u4e91\u53ec\u544a\u8f9e\uff0c\u4e91\u53ec\u9053\uff1a\u201c\u6211\u548c\u58c1\u513f\u9001\u4f60\u4e00\u7a0b\u3002\u201d\u6c5f\u6d77\u5929\u63a8\u8f9e\u4e0d\u5f97\uff0c\u53ea\u597d\u7531\u4ed6\u3002 \u3000\u3000\u4e91\u53ec\u9001\u4ed6\u5230\u4e86\u6e56\u6ee8\uff0c\u6c5f\u6d77\u5929\u518d\u4e00\u6b21\u8bf7\u4ed6\u7559\u6b65\u3002\u4e91\u53ec\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u6c5f\u5c0f\u4fa0\uff0c\u4f60\u5bf9\u6211\u5bb6\u5927\u6069 \u5927\u5fb7\uff0c\u8001\u592b\u65e0\u4ee5\u4e3a\u8c22\uff0c\u53ea\u6709\u79c0\u624d\u4eba\u60c5\u7eb8\u534a\u5f20\uff0c\u4f60\u6536\u4e0b\u5427\u3002\u201d\u6c5f\u6d77\u5929\u4e00\u770b\uff0c\u539f\u6765\u662f\u5e45\u5730\u56fe\uff0c\u5730 \u56fe\u4e0a\u7ed8\u6709\u524d\u5f80\u9a6c\u8428\u513f\u76df\u7684\u8be6\u7ec6\u9053\u8def\uff0c\u5728\u56fe\u4e2d\u8fd8\u7528\u8747\u5934\u5c0f\u6977\uff0c\u5199\u6709\u4e00\u4e9b\u540d\u5b57\u3002 \u3000\u3000\u4e91\u53ec\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u90fd\u662f\u6211\u5728\u8fd9\u6761\u8def\u4e0a\u7684\u597d\u670b\u53cb\uff0c\u4f60\u82e5\u6709\u4e8b\u3002\u53ef\u4ee5\u5c31\u8fd1\u53bb\u627e\u4ed6\u4eec\u3002\u201d \u3000\u3000\u4e91\u58c1\u7b11\u9053\uff1a\u201c\u7239\u7239\u6628\u665a\u4e00\u665a\u672a\u7761\uff0c\u7ed9\u4f60\u7ed8\u8fd9\u5730\u56fe\u3002\u201d\u6c5f\u6d77\u5929\u597d\u751f\u8fc7\u610f\u4e0d\u53bb\uff0c\u8fde\u5fd9\u9053\u8c22\u3002 \u3000\u3000\u4e91\u53ec\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u6837\u4e1c\u897f\u8981\u7ed9\u4f60\uff0c\u4f5c\u4e3a\u51ed\u4fe1\u3002\u201d\u8bf4\u7f62\u62ff\u51fa\u4e00\u5757\u4e94\u5bf8\u89c1\u65b9\u7684\u5c0f\u91d1\u724c\uff0c\u91d1\u724c \u4e0a\u6709\u5de7\u624b\u5320\u4eba\u96d5\u523b\u7684\u4e00\u53ea\u5f20\u7259\u821e\u722a\u3001\u795e\u6001\u751f\u52a8\u7684\u72ee\u5b50\u3002\u4e91\u53ec\u7f28\u7f13\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u4e91\u5bb6\u7684\u91d1\u72ee \u4ee4\uff0c\u6211\u7684\u8001\u670b\u53cb\u90fd\u8ba4\u5f97\u7684\uff0c\u4ee5\u4f60\u7684\u6b66\u529f\u800c\u8bba\uff0c\u672c\u6765\u8db3\u591f\u95ef\u8361\u6c5f\u6e56\uff0c\u4f46\u603b\u662f\u6709\u5907\u65e0\u60a3\u7684\u597d\u3002\u4f60 \u6536\u4e0b\u5427\u3002\u201d \u3000\u3000\u539f\u6765\u4e91\u5bb6\u4e43\u662f\u6b66\u6797\u4e16\u5bb6\uff0c\u5a01\u9547\u5317\u65b9\uff0c\u4e91\u5bb6\u5e84\u4e3b\uff0c\u5b9e\u9645\u5c31\u7b49\u4e8e\u5317\u65b9\u7684\u6b66\u6797\u76df\u4e3b\uff0c\u8fd9\u9762\u91d1\u72ee \u4ee4\u662f\u4ed6\u7956\u4f20\u4e4b\u7269\uff0c\u4e0d\u4f46\u4ed6\u7684\u8001\u670b\u53cb\u8ba4\u5f97\uff0c\u5728\u6b66\u6797\u4e2d\u6709\u70b9\u540d\u671b\u7684\u4eba\uff0c\u5dee\u4e0d\u591a\u90fd\u8ba4\u5f97\u7684\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u63a5\u8fc7\u4e86\u91d1\u72ee\u4ee4\uff0c\u518d\u62dc\u8c22\u9053\uff1a\u201c\u627f\u8499\u5e84\u4e3b\u5982\u6b64\u539a\u7231\uff0c\u665a\u8f88\u611f\u6fc0\u4e0d\u5c3d\uff0c\u8d74\u4f1a\u5f52\u6765\uff0c\u4fbf \u5f53\u7f34\u4ee4\u3002\u201d\u4e91\u53ec\u6380\u80e1\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff0c\u4f46\u613f\u4f60\u5728\u4f1a\u4e0a\u5927\u663e\u58f0\u540d\u3002\u6211\u5728\u820d\u4e0b\u4f2b\u5019\u4f73\u97f3\u3002\u672c\u6765\u6211\u4e5f \u63a5\u4e86\u8bf7\u5e16\u7684\uff0c\u4f1a\u4e0a\u82e5\u662f\u6709\u4eba\u95ee\u8d77\u6211\uff0c\u4f60\u5c31\u7ed9\u6211\u4ee3\u8bf4\u4e00\u58f0\u3002\u201d \u3000\u3000\u4e91\u53ec\u5c06\u8bf8\u4e8b\u4ea4\u4ee3\u5b8c\u6bd5\uff0c\u773c\u5149\u4e00\u671b\uff0c\u89c1\u4ed6\u5973\u513f\u8fd8\u7ad9\u7740\u4e0d\u52a8\uff0c\u4fbf\u7b11\u9053\uff1a\u201c\u58c1\u513f\uff0c\u4f60\u6709\u4ec0\u4e48\u8bdd \u8981\u548c\u6c5f\u5c0f\u4fa0\u8bf4\u4e48\uff1f\u201d \u3000\u3000\u4e91\u58c1\u674f\u8138\u98de\u971e\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u54e5\u54e5\u6709\u51e0\u53e5\u8bdd\u8981\u6211\u548c\u6c5f\u5c0f\u4fa0\u8bf4\u3002\u201d\u4e91\u53ec\u7b11\u9053\uff0c\u201c\u597d\u5427\uff0c \u90a3\u4f60\u5c31\u4ee3\u8868\u4f60\u54e5\u54e5\u8bf4\u5427\u3002\u201d\u8d1f\u624b\u5f98\u5f8a\uff0c\u6545\u610f\u8d70\u8fc7\u4e00\u8fb9\u3002\u4e91\u53ec\u4ee5\u4e3a\u4ed6\u7684\u5973\u513f\u662f\u627e\u4e2a\u501f\u53e3\uff0c\u5176\u5b9e \u4e5f\u53ea\u731c\u4e2d\u4e86\u4e00\u534a\u3002 \u3000\u3000\u4e91\u58c1\u4e0a\u524d\uff0c\u5c0f\u58f0\u8bf4\u9053\uff0c\u201c\u6c5f\u76f8\u516c\uff0c\u6211\u54e5\u54e5\u77e5\u9053\u4f60\u662f\u8c37\u59d1\u5a18\u7684\u9752\u6885\u7af9\u9a6c\u4e4b\u4ea4\uff0c\u975e\u5e38\u9ad8\u5174\u3002 \u4ed6\u62dc\u6258\u4f60\u4e00\u4ef6\u4e8b\u60c5\uff0c\u8981\u662f\u4f60\u89c1\u7740\u4e86\u8c37\u59d1\u5a18\uff0c\u522b\u5fd8\u4e86\u66ff\u4ed6\u95ee\u5019\u3002\u4ed6\u672c\u5e94\u4eb2\u81ea\u5411\u4f60\u8bf4\u7684\uff0c\u4f46\u4ed6\u5bb3 \u7f9e\uff0c\u7ec8\u4e8e\u8fd8\u662f\u8981\u6211\u4ee3\u8bf4\u3002\u4f60\u8bf4\u53ef\u7b11\u5417\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u542c\u4e86\uff0c\u8bf4\u4e0d\u51fa\u662f\u4e2a\u4ec0\u4e48\u5473\u513f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e91\u743c\u5bf9\u83b2\u59b9\u7684\u6069\u6155\uff0c\u539f\u6765\u5df2\u662f\u5982\u6b64\u4e4b \u6df1\uff01\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\u3002\u6211\u4e00\u5b9a\u66ff\u4f60\u54e5\u54e5\u628a\u8bdd\u5e26\u5230\u3002\u53ea\u6050\u6211\u53e3\u7b28\u8f9e\u62d9\uff0c\u8868\u8fbe\u4e0d\u51fa\u4f60\u54e5 \u54e5\u7684\u5fc3\u610f\u3002\u201d \u3000\u3000\u4e91\u58c1\u79cb\u6ce2\u4e00\u8f6c\uff0c\u7565\u663e\u5ff8\u6029\u4e4b\u6001\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u6709\u4e00\u6837\u4e1c\u897f\u7ed9\u4f60\uff0c\u8fd9\u662f\u4f60 \u6628\u65e5\u5411\u6211\u8981\u7684\uff0c\u6211\u8d76\u7740\u7ed9\u4f60\u7ee3\u597d\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u4e00\u6014\uff0c\u53ea\u89c1\u5979\u628a\u4e00\u6761\u624b\u5e15\u9012\u4e86\u8fc7\u6765\uff0c\u624b\u5e15\u4e0a\u7ee3 \u7684\u90a3\u6735\u83b2\u82b1\uff0c\u6298\u5728\u5916\u9762\uff0c\u679c\u7136\u548c\u8c37\u4e2d\u83b2\u7684\u90a3\u4e00\u65b9\u4e00\u6a21\u4e00\u6837\u3002 \u3000\u3000\u5176\u5b9e\u6c5f\u6d77\u5929\u6628\u5929\u5e76\u6ca1\u6709\u5411\u5979\u8981\u8fc7\u624b\u5e15\uff0c\u53ef\u662f\u4e91\u58c1\u5df2\u7ecf\u8fd9\u4e48\u8bf4\u4e86\uff0c\u6c5f\u6d77\u5929\u603b\u4e0d\u80fd\u8bf4\uff1a\u201c\u4f60 \u9519\u4e86\uff0c\u6211\u5e76\u6ca1\u6709\u5411\u4f60\u8981\u8fc7\u3002\u201d\u53ea\u597d\u5c06\u624b\u5e15\u6536\u4e0b\uff0c\u9053\u4e86\u4e00\u58f0\uff1a\u201c\u8c22\u8c22\u3002\u201d\u6c5f\u6d77\u5929\u6536\u4e86\u624b\u5e15\uff1b\u4e0d \u7531\u5f97\u60f3\u8d77\u4e91\u58c1\u6628\u65e5\u548c\u4ed6\u8bf4\u7684\u90a3\u4e9b\u8bdd\u6765\uff1b\u8fd9\u65f6\uff0c\u7eb5\u4f7f\u4ed6\u662f\u6728\u5934\u4eba\u513f\uff0c\u4e5f\u5df2\u61c2\u5f97\u4e86\u4e91\u58c1\u5bf9\u4ed6\u5b9e\u662f \u522b\u6709\u4e00\u756a\u60c5\u610f\u3002 \u3000\u3000\u4e91\u58c1\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65f6\u5019\u4e0d\u65e9\uff0c\u4f60\u4e0a\u8def\u5427\uff0c\u6055\u6211\u4e0d\u8fdc\u9001\u4e86\u3002\u7239\uff0c\u6c5f\u76f8\u516c\u8d70\u5566\uff0c\u4f60\u8fd8\u6709 \u8bdd\u8981\u8bf4\u4e48\uff1f\u201d\u4e91\u54e5\u8fd9\u624d\u56de\u8fc7\u5934\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6211\u6b63\u62c5\u5fc3\u4f60\u4e0d\u77e5\u8981\u8bf4\u5230\u4ec0\u4e48\u65f6\u5019\uff0c\u8bef\u4e86\u6c5f\u5c0f\u4fa0\u7684 \u884c\u7a0b\u5462\uff01\u597d\u5566\uff0c\u8bdd\u603b\u662f\u8bf4\u4e0d\u5b8c\u7684\uff0c\u7559\u5f85\u6c5f\u5c0f\u4fa0\u56de\u6765\u518d\u8bf4\u5427\uff01\u201d\u4e91\u58c1\u7ea2\u7740\u8138\u513f\u56de\u5230\u7239\u7239\u8eab\u8fb9\uff0c \u7236\u5973\u4fe9\u9042\u4e0e\u6c5f\u6d77\u5929\u6325\u624b\u9053\u522b\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u6708\u6765\uff0c\u6c5f\u6d77\u5929\u5148\u540e\u7ed3\u4ea4\u4e86\u534e\u5bb6\u4e91\u5bb6\u4e24\u4f4d\u59d1\u5a18\uff0c\u6bcf\u65e5\u91cc\u90fd\u6709\u4eba\u4f5c\u4f34\uff0c\u800c\u4eca\u53c8\u53d8\u590d\u4e86 \u539f\u6765\u7684\u60c5\u51b5\uff0c\u4e00\u5251\u5355\u8eab\uff0c\u957f\u9014\u8dcb\u6d89\uff0c\u4e0d\u514d\u9887\u6709\u5bc2\u5bde\u4e4b\u611f\u3002\u4ed6\u867d\u7136\u672a\u89e3\u7537\u5973\u4e4b\u60c5\uff0c\u4f46\u4e00\u8def\u4e0a\u4e5f \u5e38\u5e38\u4f1a\u60f3\u8d77\u8fd9\u4e24\u4f4d\u59d1\u5a18\uff0c\u534e\u4e91\u78a7\u7684\u806a\u660e\u4f36\u4fd0\u3001\u5b9c\u559c\u5b9c\u55d4\uff0c\u4e91\u58c1\u7684\u82f1\u6c14\u5a07\u59ff\uff0c\u5a40\u5a1c\u521a\u952e\uff0c\u4e24\u7686 \u517c\u6709\uff0c\u8fd9\u79cd\u79cd\u98ce\u60c5\uff0c\u90fd\u7ed9\u4ed6\u7559\u4e0b\u6839\u6df1\u7684\u5370\u8c61\u3002 \u3000\u3000\u4ed6\u60f3\u8d77\u8fd9\u4e24\u4f4d\u59d1\u5a18\u5bf9\u4ed6\u7684\u597d\u5904\uff0c\u4e0d\u89c9\u60c6\u7136\u6005\u7136\uff0c\u5ffd\u751f\u602a\u60f3\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e00\u7537\u4e00\u5973\uff0c\u603b\u4e0d\u80fd \u50cf\u4e24\u4e2a\u7537\u5b50\u4e00\u6837\u7684\u597d\u4e0b\u53bb\uff0c\u5230\u4e86\u540e\u6765\uff0c\u603b\u662f\u4f3c\u4e4e\u975e\u505a\u592b\u59bb\u4e0d\u884c\uff1f\u5018\u82e5\u4e16\u95f4\u6839\u672c\u6ca1\u6709\u8fd9\u79cd\u4fd7 \u793c\uff0c\u751a\u4e48\u2018\u7537\u5927\u5f53\u5a5a\uff0c\u5973\u5927\u5f53\u5ac1\u2019\u4e4b\u7c7b\u7684\u81ed\u89c4\u77e9\u90fd\u6254\u5230\u5927\u6d77\u91cc\u53bb\uff0c\u4e0d\u62d8\u662f\u7537\u662f\u5973\uff0c\u5927\u5bb6\u53ea\u8981 \u5fc3\u6027\u76f8\u6295\uff0c\u5c31\u805a\u5728\u4e00\u8d77\uff0c\u4e5f\u6ca1\u6709\u8c01\u4f1a\u60f3\u5230\u592b\u59bb\u4e0a\u5934\uff0c\u90a3\u4e48\u6211\u548c\u83b2\u59b9\uff0c\u534e\u59d1\u5a18\u3001\u4e91\u59d1\u5a18\u90fd\u4e00\u6837 \u7684\u597d\uff0c\u8fd9\u5c82\u4e0d\u662f\u5927\u5bb6\u90fd\u5feb\u5feb\u4e50\u4e50\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e00\u4eba\u72ec\u884c\uff0c\u8def\u4e0a\u6ca1\u6709\u803d\u6401\uff0c\u811a\u7a0b\u5012\u662f\u5feb\u4e86\u8bb8\u591a\uff1b\u4e0d\u8fc7\u51e0\u5929\uff0c\u5c31\u5230\u4e86\u7518\u8083\u7684\u5929\u6c34 \u53bf\u3002\u7ec8\u5357\u5c71\u4ece\u9655\u897f\u897f\u90e8\u873f\u8712\u800c\u6765\uff0c\u5230\u4e86\u5929\u6c34\u53bf\u4e43\u662f\u7ec8\u70b9\uff0c\u7ed3\u8109\u800c\u4e3a\u79e6\u5cad\u3002\u6c5f\u6d77\u5929\u8fd9\u51e0\u5929\u6240\u8d70 \u7684\u8def\u7a0b\uff0c\u90fd\u662f\u7ec8\u5357\u5c71\u8109\u873f\u8712\u7ecf\u8fc7\u7684\u5730\u65b9\u3002\u4f46\u56e0\u6c5f\u6d77\u5929\u8bb0\u5f97\u6b27\u9633\u4e8c\u5a18\u8bf4\u8fc7\u5979\u5bb6\u4f4f\u5728\u7ec8\u5357\u5c71\uff0c\u6545 \u6b64\u6c5f\u6d77\u5929\u603b\u662f\u907f\u514d\u4ece\u7ec8\u5357\u5c71\u811a\u7ecf\u8fc7\u7684\uff0c\u6709\u65f6\u5019\uff1b\u5b81\u53ef\u7ed5\u4e00\u4e2a\u5f2f\u513f\u3002\u5230\u4e86\u5929\u6c34\u53bf\uff1b\u8fd9\u624d\u7a0d\u7a0d\u6d88 \u51cf\u4e86\u7d27\u5f20\u7684\u60c5\u7eea\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u662f\u4ed6\u6709\u751f\u4ee5\u6765\uff0c\u6240\u89c1\u8fc7\u7684\u6700\u72e0\u6bd2\u7684\u5973\u4eba\uff0c\u4f46\u4ed6\u907f\u9053\u800c\u884c\uff0c\u4e0e\u5176\u8bf4\u662f\u4e3a\u4e86\u6015\u6b27\u9633 \u4e8c\u5a18\uff0c\u6bcb\u5b81\u8bf4\u662f\u4ed6\u4e3a\u4e86\u907f\u514d\u518d\u89c1\u6b27\u9633\u5a49\u3002\u4ed6\u66fe\u5411\u534e\u4e91\u78a7\u53d1\u8fc7\u8a93\uff0c\u5018\u82e5\u518d\u78b0\u89c1\u6b27\u9633\u5a49\uff0c\u5c31\u8981\u7ed9 \u534e\u5929\u98ce\u62a5\u4ec7\u7684\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u53c8\u6000\u7591\u540e\u6765\u9001\u89e3\u836f\u7ed9\u534e\u65e0\u98ce\u7684\u90a3\u4e2a\u8499\u9762\u5c11\u5973\u662f\u6b27\u9633\u5a49\uff0c\u201c\u5979\u7a76\u7adf\u662f\u597d\u4eba\u8fd8\u662f\u574f\u4eba \u5462\uff1f\u201d\u201c\u54ce\uff0c\u662f\u597d\u4eba\u4e5f\u7f62\uff0c\u574f\u4eba\u4e5f\u7f62\uff1b\u603b\u662f\u4ee5\u4e0d\u89c1\u4e3a\u4f73\uff01\u6211\u4e3a\u5730\u800c\u60f9\u6765\u7684\u9ebb\u70e6\uff0c\u4e5f\u5df2\u7ecf\u591f \u4e86\u3002\u201d\u201c\u5373\u4f7f\u5979\u662f\u597d\u4eba\uff0c\u4f46\u534e\u5bb6\u90fd\u5bf9\u5979\u7684\u4e00\u5bb6\u4eba\u6068\u4e4b\u5165\u9aa8\uff0c\u96be\u9053\u6211\u8fd8\u53ef\u4ee5\u548c\u5979\u4ea4\u670b\u53cb\u5417\uff1f\u201d \u5018\u82e5\u5979\u662f\u574f\u4eba\uff0c\u4f46\u5979\u7ed9\u6211\u7684\u89e3\u836f\u603b\u662f\u771f\u7684\uff0c\u96be\u9053\u6211\u5f53\u771f\u8981\u5e94\u5141\u534e\u59d1\u5a18\u5c06\u5979\u6740\u6389\uff1f\u201d\u4ed6\u4e3a\u4e86\u6b27 \u9633\u5a49\u4e4b\u4e8b\uff0c\u5de6\u601d\u53f3\u60f3\uff0c\u5fc3\u4e71\u5982\u9ebb\uff0c\u56e0\u6b64\u7ed3\u8bba\u53ea\u80fd\u662f\u201c\u603b\u4ee5\u4e0d\u89c1\u4e3a\u4f73\u201d\u4e86\uff01\u4f46\u867d\u7136\u5982\u6b64\uff0c\u6c5f\u6d77 \u5929\u5374\u4e5f\u65e0\u6cd5\u5c06\u5979\u5fd8\u6000\uff0c\u6709\u65f6\u8fd8\u4f1a\u7a81\u7136\u8d77\u4e86\u597d\u5947\u4e4b\u5ff5\uff1a\u201c\u4e0d\u77e5\u6b27\u9633\u5a49\u73b0\u5728\u5982\u4f55\u4e86\uff1f\u5018\u82e5\u90a3\u89e3\u836f \u771f\u662f\u5979\u5077\u6765\u7684\uff0c\u5979\u7236\u6bcd\u80fd\u5bb9\u5f97\u5979\u5417\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u65e5\u6c5f\u6d77\u5929\u6b63\u5728\u8def\u4e0a\u80e1\u601d\u4e71\u60f3\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\u6709\u4eba\u53eb\u9053\uff1a\u201c\u6c5f\u5c0f\u4fa0\uff0c\u5e78\u4f1a\uff0c\u5e78\u4f1a\uff01\u8bf7\u7a0d \u7559\u7247\u523b\u5982\u4f55\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6115\u7136\u56db\u987e\uff0c\u89c1\u662f\u4e2a\u6b66\u58eb\u88c5\u675f\u7684\u5c11\u5e74\u3001\u6b63\u81ea\u89c9\u5f97\u597d\u751f\u9762\u719f\uff1b\u90a3\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u6c5f\u5c0f\u4fa0 \u8bb0\u4e0d\u5f97\u4e86\u4e48\uff1f\u5c0f\u5f1f\u4e8e\u5c11\u9cb2\uff0c\u662f\u6b27\u9633\u5a49\u59d1\u5a18\u7684\u5e08\u5144\u3002\u5f53\u65e5\u627f\u8499\u8d50\u52a9\uff0c\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d\u6c5f\u6d77\u5929 \u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u8fd8\u4ed6\u4e00\u672d\uff0e\u95ee\u9053\uff1a\u201c\u539f\u6765\u4e43\u662f\u4e8e\u5144\uff0c\u4e0d\u77e5\u4f55\u6545\u5b64\u8eab\u5230\u6b64\uff1f\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u4e8e\u5c11\u9cb2\u5c31\u662f\u4ece\u524d\u5411\u6c5f\u6d77\u5929\u901a\u98ce\u62a5\u8baf\u7684\u90a3\u4e2a\u4eba\uff0c\u5f53\u65f6\u6b27\u9633\u5a49\u56e0\u4e3a\u5077\u9001\u89e3\u836f\u7ed9\u6c5f\u6d77 \u5929\u7684\u7f18\u6545\uff0c\u88ab\u5979\u7684\u5e08\u7236\u9634\u5723\u59d1\u6346\u7f1a\u8d77\u6765\uff0c\u8981\u65bd\u6bd2\u5211\uff0c\u6c5f\u6d77\u5929\u5168\u9760\u4ed6\u7684\u62a5\u8baf\uff0c\u8d76\u5230\u9634\u5723\u59d1\u7684\u79d8 \u7a9f\uff0c\u6b27\u9633\u5a49\u624d\u5f97\u5e78\u514d\u4e8e\u96be\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u6c5f\u6d77\u5929\u7a81\u7136\u4e0e\u4ed6\u9053\u4e0a\u76f8\u9022\u3001\u5fc3\u91cc\u4e0d\u7981\u6697\u6697\u5600\u5495\uff0c\u53ea\u542c\u5f97\u4e8e\u5c11\u9cb2\u8bf4\u9053\uff1a\u201c\u6c5f\u5144\u6709\u6240 \u4e0d\u77e5\uff0c\u5c0f\u5f1f\u5c31\u662f\u4e3a\u4e86\u90a3\u4ef6\u4e8b\u60c5\u3001\u4e0d\u6562\u518d\u7559\u5728\u6076\u5e08\u95e8\u4e0b\uff0c\u662f\u4ee5\u79c1\u9003\u51fa\u6765\u3002\u8a00\u4e4b\u7f9e\u6127\uff0c\u60f3\u6c5f\u5144\u4e0d \u4f1a\u770b\u8f7b\u6211\u5427\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e8e\u5144\u7684\u4f5c\u4e3a\u5e76\u65e0\u53ef\u8bae\u4e4b\u5904\uff0c\u5f1f\u7109\u6562\u770b\u8f7b\u4e86\u5e08\u5c0a\u3001\u5e08\u5c0a\uff0c\u5e08\u7236\u56fa\u7136\u5e94 \u5f53\u5c0a\u656c\uff0c\u4f46\u4e5f\u5f53\u5206\u8fa8\u90aa\u6b63\uff0c\u5f03\u90aa\u5f52\u6b63\uff0c\u7eb5\u7136\u80cc\u53db\u5e08\u95e8\uff1b\u4e5f\u662f\u5149\u660e\u78ca\u843d\u3002\u201d \u3000\u3000\u4e8e\u5c11\u9cb2\u53cc\u7709\u8212\u5c55\uff0c\u5411\u6c5f\u6d77\u5929\u4e00\u63d6\u8bf4\u9053\uff1a\u201c\u5230\u5e95\u6c5f\u5144\u662f\u4e2a\u6709\u89c1\u8bc6\u7684\u4eba\u3001\u8bf4\u8bba\u540d\u8a00\uff1a\u4ee4\u6211\u987f \u5f00\u8305\u585e\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u867d\u7136\u4e0d\u662f\u51fa\u8eab\u540d\u95e8\uff0c\u4f46\u5bb6\u7236\u4e5f\u662f\u6b66\u6035\u4e2d\u7684\u6b63\u6d3e\u4eba\u7269\uff0c\u53ea\u56e0\u6211\u6c42\u827a\u5fc3 \u5207\uff0c\u8bef\u4eba\u6b67\u9014\uff0c\u9519\u62dc\u6076\u5e08\uff0c\u6094\u4e4b\u65e0\u53ca\uff01\u5982\u4eca\u8df3\u4e86\u51fa\u6765\uff0c\u201d\u4e5f\u8fd8\u662f\u65e0\u989c\u89c1\u6c5f\u6e56\u8c6a\u6770\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u53c8\u4f55\u5fc5\uff0c\u77e5\u8fc7\u80fd\u6539\uff0c\u5584\u83ab\u5927\u7109\u3002\u4f55\u51b5\u4e8e\u5144\u867d\u6295\u6076\u5e08\uff0c\u672c\u8eab\u5374\u672a\u66fe\u4f5c\u6076\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6577\u884d\u4e86\u4ed6\u51e0\u53e5\uff0c\u6b63\u60f3\u544a\u8f9e\u4e0a\u8def\uff0c\u90a3\u4e8e\u5c11\u9cb2\u5ffd\u9053\uff1a\u201c\u4ee4\u5c0a\u5927\u540d\uff0c\u662f\u5426\u4e00\u4e2a\u2018\u5357\u2019 \u5b57\uff1f\u201d\u6c5f\u6d77\u5929\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u5bb6\u7236\u3002\u201d\u968f\u5373\u60f3\u9053\uff1a\u201c\u6211\u7239\u53c2\u4ea4\u6e38\u5e7f\u9614\uff0c\u4ed6\u77e5\u6211 \u7239\u7239\u7684\u540d\u5b57\uff0c\u4e5f\u4e0d\u8db3\u4e3a\u5947\u3002\u201d \u3000\u3000\u54ea\u77e5\u4e8e\u5c11\u9cb2\u63a5\u7740\u4fbf\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u4e0e\u6c5f\u5144\u76ca\u53d1\u4e0d\u662f\u5916\u4eba\u4e86\u3002\u5bb6\u7236\u540d\u53eb\u4e8e\u5927\u9e4f\uff0c\u672c\u662f \u5c11\u6797\u6d3e\u7684\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u548c\u4ee4\u5c0a\u4e5f\u662f\u76f8\u719f\u7684\u670b\u53cb\uff0c\u4e0d\u77e5\u4ee4\u5c0a\u53ef\u66fe\u63d0\u8fc7\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u77e5\u9053\u7236\u4eb2\u6709\u8bb8\u591a\u5c11\u6797\u6d3e\u7684\u670b\u53cb\uff0c\u4f46\u8fd9\u4e2a\u4e8e\u5927\u9e4f\u7684\u540d\u5b57\uff0c\u4ed6\u5df2\u8bb0\u4e0d\u6e05\u695a\u7236\u4eb2\u66fe\u5426\u8bf4 \u8fc7\u4e86\uff0c\u4f46\u5bf9\u65b9\u65e2\u7136\u5982\u6b64\u8bf4\uff0c\u4ed6\u53ea\u597d\u201c\u5b81\u53ef\u4fe1\u5176\u6709\u201d\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4ee4\u5c0a\u4e43\u662f\u6211\u7684\u7236 \u6267\uff0c\u8bf7\u5144\u53f0\u4ee3\u6211\u95f4\u5019\u3002\u65e5\u540e\u6709\u7f18\uff0c\u5f53\u518d\u62dc\u89c1\u3002\u201d \u3000\u3000\u4e8e\u5c11\u9cb2\u7acb\u5373\u8bf4\u9053\uff1a\u201c\u6c5f\u5144\u4e0d\u662f\u5fd9\u7740\u8d76\u8def\u5427\uff1f\u6211\u7684\u5bb6\u5c31\u5728\u8fd9\u9644\u8fd1\uff0c\u53ef\u5bb9\u6211\u7565\u5c3d\u5730\u4e3b\u4e4b\u8c0a \u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8e0c\u8e87\u672a\u7b54\uff0c\u4e8e\u5c11\u9cb2\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5f53\u65e5\u6211\u9003\u56de\u5bb6\u4e2d\uff0c\u66fe\u5c06\u5144\u53f0\u76f8\u52a9\u4e4b\u4e8b\uff0c\u7980\u544a\u5bb6 \u7236\u3002\u5bb6\u7236\u542c\u4e86\u5144\u53f0\u7684\u540d\u5b57\uff0c\u4fbf\u54c8\u54c8\u7b11\u9053\uff1a\u2018\u539f\u6765\u662f\u6211\u6545\u4eba\u7684\u513f\u5b50\uff01\u2019\u4ed6\u8bf4\u4ed6\u548c\u4ee4\u5c0a\u5c06\u8fd1\u4e8c\u5341 \u5e74\u7684\u4ea4\u60c5\uff0c\u5f53\u771f\u8bf4\u5f97\u4e0a\u662f\u4e2a\u809d\u80c6\u76f8\u7167\u7684\u670b\u53cb\uff0c\u4e09\u5e74\u524d\u4ee4\u5c0a\u8def\u8fc7\u6b64\u5730\u3002\u4e5f\u66fe\u5230\u8fc7\u5bd2\u820d\u7684\uff0c\u53ea\u53ef \u60dc\u6211\u90a3\u65f6\u4e0d\u5728\u5bb6\u4e2d\u3002\u5bb6\u7236\u542c\u6211\u8bf4\u8d77\u4f60\u7684\u7edd\u4e16\u6b66\u529f\uff0c\u9ad8\u5174\u5f97\u4e0d\u5f97\u4e86\uff0c\u5f88\u60f3\u89c1\u4f60\u4e00\u9762\u3002\u60f3\u4e0d\u5230\u4eca \u65e5\u5982\u6b64\u51d1\u5de7\uff1b\u7adf\u5728\u8def\u4e0a\u76f8\u9022\uff0c\u8981\u662f\u6c5f\u5144\u8fc7\u95e8\u4e0d\u5165\uff0c\u5bb6\u7236\u4e00\u5b9a\u8981\u602a\u6211\u4e0d\u4f1a\u62db\u547c\u5ba2\u4eba\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4ed6\u6025\u4e8e\u5bfb\u7236\uff0c\u6b63\u82e6\u65e0\u5904\u6253\u542c\u7236\u4eb2\u7684\u6d88\u606f\uff0c\u5982\u4eca\u542c\u8bf4\u7236\u4eb2\u66fe\u5230\u8fc7\u4e8e\u5bb6\uff0c \u5fc3\u773c\u513f\u4fbf\u6709\u70b9\u6d3b\u52a8\uff0c\u60f3\u53bb\u95ee\u4e2a\u7a76\u7adf\u3002 \u3000\u3000\u53ef\u662f\u6c5f\u6d77\u5929\u5230\u5e95\u4e0b\u6562\u5b8c\u5168\u76f8\u4fe1\u8fd9\u4e8e\u5c11\u9cb2\uff0c\u4ed6\u60f3\u8d77\u4ee5\u5f80\u597d\u51e0\u6b21\u8f7b\u4fe1\u522b\u4eba\uff0c\u4ee5\u81f4\u4e0a\u5f53\u7684\u4e8b\uff1b \u4e0d\u7981\u53c8\u8fdf\u7591\u8d77\u6765\uff1b\u4ed6\u8f6c\u4e86\u597d\u51e0\u6b21\u5ff5\u5934\uff0c\u7ec8\u4e8e\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4e8e\u5144\u597d\u610f\uff0c\u4f46\u5b9e\u4e0d\u76f8\u7792\uff1b\u6211\u786e\u662f\u6709 \u4e8b\u5728\u8eab\uff0c\u6025\u4e8e\u8d76\u8def\uff0c\u8bf7\u5728\u4ee4\u5c0a\u8ddf\u524d\uff0c\u4ee3\u4e3a\u544a\u7f6a\uff0c\u5bb9\u6211\u56de\u7a0b\u4e4b\u65e5\uff0c\u518d\u6765\u62dc\u8c12\u5427\uff01\u201d \u3000\u3000\u4e8e\u5c11\u9cb2\u9762\u8272\u500f\u53d8\uff0c\u795e\u8272\u6115\u7136\uff0c\u5ffd\u5730\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u6c5f\u5c0f\u4fa0\uff0c\u4f60\u5fc3\u91cc\u5934\u7684\u8bdd\u6211\u4ee3\u4f60\u8bf4\u51fa\u6765 \u5427\uff1a\u2018\u54fc\uff0c\u54fc\uff0c\u4f60\u59d3\u4e8e\u7684\u662f\u4e2a\u6765\u5386\u4e0d\u660e\u7684\u90aa\u6d3e\u4e2d\u4eba\uff0c\u5984\u60f3\u9ad8\u6500\uff0c\u4e5f\u4e0d\u7167\u7167\u955c\u5b50\uff1f\u6211\u5c82\u80fd\u4e0e\u4f60 \u8fd9\u7b49\u4eba\u7ed3\u4ea4\uff1f\u2019\u54c8\uff0c\u54c8\uff0c\u6c5f\u5c0f\u4fa0\uff0c\u4f60\u8bf4\u5f97\u597d\u542c\uff1a\u5f52\u6839\u7ed3\u4f4e\uff0c\u539f\u6765\u8fd8\u662f\u770b\u4e0d\u8d77\u6211\uff01\u201d\u7b11\u58f0\u51c4 \u60e8\uff0c\u4ee4\u4eba\u542c\u4e86\u65e0\u9650\u96be\u8fc7\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fd9\u9053\uff1a\u201c\u4e8e\u5144\u4f11\u8981\u8bef\u4f1a\u2026\u2026\u201d\u53ea\u542c\u5f97\u90a3\u4e8e\u5c11\u9cb2\u5df2\u662f\u6124\u7136\u8bf4\u9053\uff0c\u201c\u6c5f\u5c0f\u4fa0\uff0c\u6211\u5984\u60f3 \u9ad8\u6500\uff0c\u786e\u662f\u81ea\u53d6\u5176\u8fb1\uff01\u4f46\u6211\u6562\u5411\u5929\u53d1\u8a93\uff0c\u5018\u82e5\u6211\u5bf9\u6c5f\u5144\u6709\u534a\u70b9\u574f\u5fc3\uff0c\u6709\u5982\u6b64\u6307\uff01\u201d\u5ffd\u5730\u62d4\u51fa \u4f69\u5200\uff0c\u201c\u55d6\u201d\u7684\u4e00\u5200\uff0c\u5c06\u5c0f\u6307\u5934\u524a\u4e0b\uff0c\u82e5\u7b11\u9053\uff1a\u201c\u6c5f\u5144\u53ef\u4ee5\u4fe1\u5f97\u8fc7\u6211\u4e86\u5427\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5927\u5403\u4e00\u60ca\uff0c\u4ed6\u662f\u4e2a\u5fc3\u5730\u5584\u826f\uff1a\u5bb9\u6613\u53d7\u4eba\u611f\u52a8\u7684\u4eba\uff0c\u89c1\u6b64\u60c5\u5f62\uff0c\u5fc3\u4e2d\u5185\u759a\uff0c\u5927\u611f\u4e0d \u5b89\uff0c\u8fde\u5fd9\u8bf4\u9053\uff0c\u201c\u4e8e\u5144\u8a00\u91cd\u4e86\uff0c\u4f55\u82e6\u5982\u6b64\uff1f\u8fd9\u53c8\u4e0d\u662f\u4ec0\u4e48\u5927\u4e8b\u60c5\uff01\u597d\u5427\uff0c\u4e3a\u4e86\u514d\u5f97\u4e8e\u5144\u8bef \u4f1a\uff0c\u5c0f\u5f1f\u73b0\u5728\u5c31\u53bb\u62dc\u89c1\u4ee4\u5c0a\uff0c\u53ea\u662f\u6050\u6015\u4e0d\u80fd\u4e45\u7559\uff0c\u5148\u6b64\u8bf4\u660e\uff0c\u8fd8\u8bf7\u89c1\u8c05\u3002\u201d \u3000\u3000\u4e8e\u5c11\u9cb2\u8fd9\u624d\u6362\u4e0a\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u6c5f\u5144\u6709\u4e8b\u5728\u8eab\uff0c\u6211\u5f53\u7136\u4e5f\u4e0d\u6562\u4e45\u7559\u5927\u9a7e\u3002\u597d\u5728\u5bd2\u820d \u5c31\u5728\u8fd9\u6751\u5b50\u91cc\uff0c\u6700\u591a\u4e0d\u8fc7\u803d\u6401\u5927\u9a7e\u4e2a\u628a\u65f6\u8fb0\u3002\u201d \u3000\u3000\u4e8e\u662f\u4e8e\u5c11\u9cb2\u5728\u524d\u5f15\u8def\uff0c\u8d70\u4e86\u4e00\u4e2a\u6751\u5e84\uff0c\u8fdc\u8fdc\u770b\u89c1\u524d\u9762\u5c71\u811a\u4e0b\u6709\u4e2a\u4eba\u5bb6\uff0c\u95e8\u524d\u60ac\u7740\u5927\u7ea2\u706f \u7b3c\uff0c\u9f13\u4e50\u4e4b\u58f0\uff0c\u968f\u98ce\u98d8\u6765\uff0c\u4f3c\u662f\u5728\u529e\u559c\u4e8b\u3002\u6709\u4e9b\u4f3c\u662f\u8d3a\u5ba2\u6a21\u6837\u7684\u4eba\uff0c\u6b63\u4e09\u4e09\u4e94\u4e94\uff0c\u7edc\u7ece\u4e0d\u7edd \u7684\u5411\u90a3\u5bb6\u4eba\u5bb6\u8d70\u53bb\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u4e8e\u5c11\u9cb2\u5ffd\u7136\u505c\u4e0b\u4e86\u811a\u6b65\uff01 \u3000\u3000\u4e8e\u5c11\u9cb2\u4e00\u62cd\u8111\u888b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u770b\u6211\u597d\u7cca\u6d82\uff0c\u7adf\u5fd8\u4e86\u4eca\u65e5\u662f\u5f20\u5927\u53d4\u5ac1\u5973\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u6115\u7136 \u95ee\u9053\uff1a\u201c\u8fd9\u548c\u54b1\u4eec\u6709\u4f55\u76f8\u5e72\uff1f\u201d\u4e8e\u5c11\u9cb2\u9053\uff1a\u201c\u4ec1\u5144\u6709\u6240\u4e0d\u77e5\uff0c\u8fd9\u5f20\u5927\u53d4\u662f\u6211\u7239\u7239\u7684\u597d\u670b\u53cb\uff0c \u4ed6\u5bb6\u4eca\u65e5\u62db\u5a7f\u4e0a\u95e8\uff0c\u6211\u7239\u7239\u600e\u80fd\u4e0d\u53bb\u559d\u4ed6\u4e00\u676f\u559c\u9152\uff1f\u2019\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u90a3\u4e48\u6211\u5148\u5230\u5e9c\u4e0a\u7b49\u5019\u4ee4 \u5c0a\u5427\u3002\u201d\u4e8e\u5c11\u9cb2\u9053\uff1a\u201c\u8fd9\u5f20\u5927\u53d4\u548c\u6211\u7239\u7239\u6700\u662f\u8981\u597d\uff0c\u53ea\u6015\u8981\u7b49\u5230\u6240\u6709\u7684\u5ba2\u4eba\u90fd\u6563\u4e86\uff0c\u4ed6\u624d\u653e \u6211\u7239\u7239\u56de\u5bb6\u3002\u201d\u6c5f\u6d77\u5929\u8e0c\u8e87\u9053\uff1a\u201c\u90a3\u4e48\u2014\u2014\u201d\u4ed6\u672c\u60f3\u8d81\u6b64\u544a\u8f9e\uff0c\u4f46\u53c8\u6015\u4e8e\u5c11\u9cb2\u8bef\u4f1a\u662f\u77a7\u4ed6\u4e0d \u8d77\uff0c\u4e00\u65f6\u6ca1\u6709\u4e86\u4e3b\u610f\u3002 \u3000\u3000\u4e8e\u5c11\u9cb2\u5ffd\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u4f60\u5403\u8fc7\u5348\u996d\u6ca1\u6709\uff1f\u201d\u6c5f\u6d77\u5929\u8001\u5b9e\u56de\u7b54\u9053\uff1a\u201c\u6211\u4e00\u65e9\u8d74\u8def\uff0c\u8fd8\u672a\u7528 \u8fc7\u5348\u996d\u3002\u201d\u4e8e\u5c11\u9cb2\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4e0d\u5982\u8fd9\u6837\u5427\u3002\u8fd9\u4f4d\u5f20\u5927\u53d4\u662f\u4e2a\u8c6a\u723d\u597d\u5ba2\u7684\u4eba\uff0c\u4e0d\u5982\u4f60\u6211\u5c31\u53bb \u53e8\u6270\u4ed6\u4e00\u676f\u559c\u9152\uff0c\u4e5f\u597d\u8ba9\u6211\u7239\u7239\u89c1\u4f60\u4e00\u9762\uff0c\u4f60\u559d\u5b8c\u4e86\u5c31\u8d70\uff0c\u4e5f\u82b1\u4e0d\u4e86\u591a\u5c11\u65f6\u5019\u3002\u201d\u6c5f\u6d77\u5929 \u9053\uff1a\u201c\u8fd9\u600e\u4e48\u4f7f\u5f97\uff1f\u4e00\u6765\u975e\u4eb2\u975e\u6545\uff0c\u4e8c\u6765\u6211\u4e5f\u6ca1\u5907\u8d3a\u793c\u3002\u201d\u4e8e\u5c11\u9cb2\u7b11\u9053\uff1a\u201c\u6c5f\u6e56\u4eba\u7269\uff0c\u54ea\u91cc \u62d8\u8bba\u8fd9\u4e9b\u5c0f\u8282\uff1f\u6211\u4eec\u8fd9\u91cc\u7684\u98ce\u4fd7\uff0c\u559c\u5e86\u7684\u65e5\u5b50\uff0c\u5018\u6709\u5916\u8def\u7684\u5ba2\u4eba\u6765\u5230\uff0c\u90a3\u5c31\u53eb\u505a\u2018\u559c\u795e\u4e34 \u95e8\u2019\uff0c\u4e3b\u4eba\u5bb6\u9ad8\u5174\u8fd8\u6765\u4e0d\u53ca\u5462\u3002\u4f55\u51b5\u8fd9\u4e3b\u4eba\u5bb6\u7d20\u6765\u53c8\u5f88\u597d\u5ba2\u3002\u81f3\u4e8e\u8bf4\u6ca1\u5907\u8d3a\u793c\uff0c\u6211\u7ed9\u4f60\u8865\u529e \u4e00\u4efd\u597d\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e16\u6545\u4e0d\u6df1\uff0c\u542c\u4e8e\u5c11\u9cb2\u8bf4\u5f97\u9887\u6709\u9053\u7406\uff0c\u5fc3\u91cc\u76d8\u7b97\u9053\uff1a\u201c\u6211\u65e2\u4e0d\u60f3\u591a\u803d\u6401\u4e00\u5929\uff0c\u4f46\u53c8 \u60f3\u77e5\u9053\u6211\u7239\u7239\u7684\u6d88\u606f\uff0c\u90a3\u4e48\u53ea\u597d\u505a\u4e2a\u4e0d\u901f\u4e4b\u5ba2\u4e86\u3002\u559d\u4e0d\u559d\u559c\u9152\u5012\u65e0\u6240\u8c13\uff0c\u53ea\u8981\u78b0\u89c1\u90a3\u4e8e\u5927 \u9e4f\uff0c\u548c\u4ed6\u8c08\u4e0a\u51e0\u53e5\u4fbf\u884c\u3002\u770b\u6765\u8fd9\u59d3\u4e8e\u7684\u751a\u4e3a\u8bda\u6073\uff0c\u4e0d\u4f3c\u6709\u4ec0\u4e48\u6076\u610f\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5982 \u6b64\uff0c\u6211\u542c\u4f60\u7684\u4fbf\u662f\u3002\u53ea\u662f\u6700\u597d\u4e0d\u8981\u60ca\u52a8\u4e3b\u4eba\uff0c\u514d\u5f97\u8bb8\u591a\u5ba2\u5957\u3002\u201d \u3000\u3000\u4e8e\u5c11\u9cb2\u9053\uff1a\u201c\u884c\uff01\u6211\u4e5f\u4e0d\u60f3\u591a\u803d\u6401\u4f60\u7684\u65f6\u95f4\uff0c\u8fd9\u6837\u5427\uff0c\u8fdb\u53bb\u540e\uff0c\u6211\u5c31\u548c\u77e5\u5ba2\u8bf4\u4e00\u58f0\uff0c\u53eb \u6211\u7239\u7239\u51fa\u6765\u62db\u547c\u4f60\u597d\u4e86\u3002\u7136\u540e\u6211\u53bb\u548c\u90a3\u5f20\u5927\u53d4\u8bf4\u4f60\u6709\u4e8b\u5728\u8eab\uff0c\u4e0d\u80fd\u4e45\u7559\u3002\u4f60\u613f\u610f\u559d\u559c\u9152\u561b\u5c31 \u559d\uff0c\u4e0d\u613f\u610f\u5c31\u5411\u4e3b\u4eba\u9053\u4e2a\u559c\u4fbf\u8d70\u4e5f\u884c\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u6837\u5b89\u6392\u6700\u597d\uff01\u201d\u4fbf\u8ddf\u4e8e\u5c11\u9cb2\u5411\u90a3\u5bb6\u4eba\u5bb6\u8d70\u53bb\uff0c\u8d77\u521d\u4ed6\u8fd8\u53ea\u9053\u662f\u4e2a\u666e \u901a\u4eba\u5bb6\uff0c\u54ea\u77e5\u8d8a\u884c\u8d8a\u8fd1\uff0c\u53ea\u89c1\u90a3\u5bb6\u4eba\u5bb6\u501a\u5c71\u4fee\u5efa\uff0c\u5d07\u697c\u9ad8\u9601\uff0c\u5c4b\u5b87\u8fde\u4e91\uff0c\u6731\u6f06\u5927\u95e8\uff0c\u95e8\u524d\u8fd8 \u6709\u4e24\u5bf9\u9ad8\u8fc7\u4eba\u7684\u77f3\u72ee\u5b50\uff0c\u770b\u8fd9\u6c14\u6d3e\uff0c\u54ea\u91cc\u662f\u4e2a\u666e\u901a\u4eba\u5bb6\uff1f\u6c5f\u6d77\u5929\u4e0d\u89c9\u6709\u4e9b\u8d77\u7591\uff0c\u95ee\u9053\uff1a\u201c\u8fd9 \u5bb6\u4eba\u5bb6\u662f\u505a\u5b98\u7684\u5417\uff1f\u201d\u4e8e\u5c11\u9cb2\u9053\uff1a\u201c\u5b98\u5012\u4e0d\u662f\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u662f\u6211\u4eec\u53bf\u91cc\u7684\u9996\u5bcc\uff1b\u6240\u4ee5\u4f4f\u5b85\u6bd4\u505a \u5b98\u7684\u8fd8\u8bb2\u7a76\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u5230\u4e86\u95e8\u524d\uff0c\u6709\u77e5\u5ba2\u51fa\u6765\u8fce\u63a5\uff0c\u90a3\u4eba\u4f3c\u662f\u4e0e\u4e8e\u5c11\u9cb2\u751a\u719f\uff0c\u4e00\u89c1\u4e86\u4ed6\uff0c\u4fbf\u7b11 \u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u6765\u8fdf\u4e86\uff0c\u73b0\u5728\u53ea\u80fd\u770b\u65b0\u4eba\u62dc\u5802\u884c\u793c\u5566\uff01\u201d\u7b11\u5f97\u9887\u4e3a\u53e4\u602a\uff0c\u795e\u8272\u4e5f\u4f3c\u4e0d\u5927\u81ea\u7136\uff01 \u3000\u3000\u4e8e\u5c11\u9cb2\u6da9\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\u5f97\u5f88\uff0c\u6211\u6b63\u662f\u8981\u6765\u770b\u65b0\u4eba\u62dc\u5802\u3002\u201d\u6c5f\u6d77\u5929\u6697\u6697\u7eb3\u7f55\uff0c\u5fc3\u91cc\u60f3 \u9053\uff1a\u201c\u8fd9\u4e8e\u5c11\u9cb2\u4e5f\u602a\uff0c\u4ed6\u6765\u559d\u4eba\u5bb6\u7684\u559c\u9152\uff0c\u600e\u7684\u8138\u4e0a\u5374\u4e0d\u5e26\u534a\u70b9\u7b11\u5bb9\uff1f\u8bf4\u8bdd\u7684\u795e\u6c14\uff0c\u5c31\u50cf\u4eba \u5bb6\u6b20\u4e86\u4ed6\u7684\u503a\u4f3c\u7684\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u867d\u7136\u611f\u5230\u6709\u70b9\u4e0d\u5bf9\uff0c\u4f46\u5728\u8fd9\u8d3a\u5ba2\u76c8\u95e8\u4e4b\u9645\uff0c\u5374\u4e0d\u65b9\u4fbf\u95ee\u4ed6\u3002\u4e0d\u77e5\u4e0d\u89c9\u95f4\u5df2\u968f\u7740\u4eba \u7fa4\u62e5\u4e86\u8fdb\u53bb\uff0c\u53ea\u542c\u5f97\u516b\u97f3\u9f50\u594f\uff0c\u97ad\u70ae\u4e5f\u567c\u567c\u556a\u556a\u7684\u54cd\u4e86\u8d77\u6765\uff0c\u6c5f\u6d77\u5929\u5939\u5728\u4eba\u4e1b\u4e4b\u4e2d\uff0c\u8eab\u4e0d\u7531 \u5df1\u7684\u5df2\u8fdb\u4e86\u793c\u5802\uff0c\u56de\u5934\u4e00\u671b\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u4e8e\u5c11\u9cb2\u5df2\u4e0d\u5728\u4ed6\u7684\u8eab\u65c1\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u662f\u53bb\u77e5\u4f1a\u4ed6\u7684\u7236\u4eb2\uff0c\u5374\u600e\u7684\u4e0d\u5411\u6211\u5148\u8bf4\u4e00\u58f0\uff1f\u201d\u76ee\u5149 \u56db\u4e0b\u641c\u7d22\uff0c\u8fd9\u624d\u53d1\u89c9\u4e8e\u5c11\u9cb2\u5df1\u6324\u5230\u524d\u9762\u53bb\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5e74\u7eaa\u867d\u8f7b\uff0c\u4f46\u5728\u6b66\u5b66\u4e0a\u5374\u662f\u4e2a\u5927\u884c\u5bb6\uff0c\u5c31\u5728\u4ed6\u76ee\u5149\u641c\u7d22\u4e8e\u5c11\u9cb2\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u4e86\u53e6 \u4e00\u4ef6\u53ef\u7591\u4e4b\u5904\uff0c\u90a3\u6ee1\u5802\u5bbe\u5ba2\uff0c\u7adf\u4f3c\u4eba\u4eba\u90fd\u6709\u6b66\u529f\u5e95\u5b50\uff0c\u800c\u4e14\u5176\u4e2d\u8fd8\u9887\u6709\u51e0\u4e2a\u5177\u6709\u4e0a\u4e58\u5185\u529f\u7684 \u4eba\u7269\uff1a\u6c5f\u6d77\u5929\u4e0d\u7531\u5f97\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u59d3\u5f20\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\u6765\u5386\uff1f\u4e8e\u5c11\u9cb2\u8bf4\u4ed6\u662f\u4e2a\u5927\u5bcc\u8c6a\uff0c\u5374\u600e \u4e48\u4ed6\u7684\u4eb2\u53cb\u5168\u90fd\u662f\u6b66\u6797\u4eba\u7269\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u8981\u6324\u4e0a\u524d\u53bb\u95ee\u4e8e\u5c11\u9cb2\uff0c\u5ffd\u542c\u5f97\u4eba\u4e1b\u4e2d\u6709\u4eba\u56b7\u9053\uff1a\u201c\u65b0\u90ce\u6765\u4e86\uff0c\u5feb\u6765\u770b\u5440\uff01\u54c8\uff0c \u4e00\u8868\u4eba\u6750\uff0c\u957f\u5f97\u5012\u771f\u4e0d\u9519\u5462\uff01\u201d\u201c\u5c82\u53ea\u4eba\u6750\u4e0d\u9519\uff0c\u4f60\u4eec\u77e5\u4e0d\u77e5\u9053\uff0c\u4ed6\u7684\u53d4\u53d4\u662f\u5f53\u4eca\u6b66\u6797\u7b2c\u4e00 \u9ad8\u624b\uff1f\u542c\u8bf4\u91d1\u4e16\u9057\u4e5f\u6253\u4e0d\u8fc7\u4ed6\u5462\uff1f\u8fd9\u65b0\u90ce\u5bb6\u5b66\u6e0a\u6e90\uff0c\u542c\u8bf4\u5df2\u5c3d\u5f97\u4e86\u4ed6\u53d4\u53d4\u7684\u771f\u4f20\u4e86\uff01\u201d\u6c5f\u6d77 \u5929\u5927\u5403\u4e00\u60ca\uff0c\u5b9a\u775b\u770b\u65f6\uff0c\u53ea\u89c9\u90a3\u65b0\u90ce\u4f3c\u66fe\u76f8\u8bc6\uff0c\u65c1\u8fb9\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u77a7\uff0c\u90a3\u4eba\u5c31\u662f\u65b0\u90ce\u7684\u53d4\u53d4 \u4e86\uff0c\u4eca\u5929\u662f\u4ed6\u4ee3\u7537\u65b9\u4e3b\u5a5a\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8fd9\u624d\u8bb0\u8d77\uff0c\u539f\u6765\u8fd9\u4e2a\u7537\u65b9\u7684\u4e3b\u5a5a\u4eba\u524d\u662f\u66fe\u7ecf\u5bb3\u8fc7\u4ed6\u5e08\u7236\u7684\u90a3\u4e2a\u6587\u5c9b\u4e3b\u3001\u6587\u5ef7\u58c1\uff0c \u524d\u90a3\u4e2a\u65b0\u90ce\u5219\u662f\u4ed6\u7684\u4f84\u513f\u6587\u9053\u5e84\uff0c\u4e5f\u5c31\u662f\u516b\u5e74\u4e4b\u524d\u66ff\u548c\u7ec5\u62bc\u8fd0\u73e0\u5b9d\u7684\u90a3\u4e2a\u5c11\u5e74\uff0c\u4ed6\u4eec\u53d4\u4f84\u4e8c \u4eba\u5230\u4e86\u4e2d\u4e0a\u4e4b\u540e\uff0c\u90fd\u6295\u5165\u5929\u9b54\u6559\u4e0b\uff0c\u6c5f\u6d77\u5929\u5728\u7956\u5f95\u5c71\u5929\u9b54\u6559\u603b\u5802\u7684\u65f6\u5019\uff0c\u66fe\u89c1\u8fc7\u4ed6\u4eec\u53d4\u4f84\u4e8c \u4eba\u7684\u3002\u53ea\u56e0\u5f53\u65f6\u6c5f\u6d77\u5929\u8fd8\u53ea\u662f\u4e2a\u5c0f\u5b69\u5b50\uff0c\u4e14\u53c8\u4e8b\u9694\u591a\u5e74\uff0c\u6240\u4ee5\u4e00\u65f6\u60f3\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff1f\u539f\u6765\u662f\u8fd9\u4e24\u4e2a\u9b54\u5934\uff01\u8fd9\u59d3\u6587\u7684\u771f\u4f1a\u5439\u725b\uff0c\u7adf\u6562\u8bf4\u6211\u7684\u5e08\u7236\u6253 \u4e0d\u8fc7\u4ed6\uff01\u201d \u3000\u3000\u65c1\u8fb9\u53c8\u6709\u4eba\u8bae\u8bba\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5973\u5bb6\u8981\u7ed3\u8fd9\u95e8\u4eb2\u4e8b\uff0c\u5927\u7ea6\u8fd8\u662f\u4e3a\u4e86\u4ed6\u53d4\u53d4\u7684\u7f18\u6545\uff0c\u6709 \u4e86\u8fd9\u4e2a\u5f3a\u624b\uff0c\u4ed6\u4eec\u5c31\u53ef\u4ee5\u5bf9\u4ed8\u4efb\u4f55\u4ec7\u5bb6\u4e86\uff01\u201d \u3000\u3000\u542c\u5ba2\u4eba\u4eec\u7684\u7a83\u7a83\u79c1\u8bae\uff0c\u8fd9\u5973\u5bb6\u5206\u660e\u662f\u5728\u6b66\u6797\u4e2d\u5927\u6709\u6765\u5934\uff0c\u51b3\u975e\u666e\u901a\u4eba\u7269\u3002\u6c5f\u6bcf\u5929\u6b63\u81ea\u601d \u7591\uff0c\u53ea\u542c\u5f97\u8eab\u65c1\u53c8\u6709\u4e00\u4e2a\u5ba2\u4eba\u7b11\u9053\uff1a\u201c\u65b0\u90ce\u7eb5\u7136\u6587\u6b66\u5168\u624d\uff0c\u4f46\u542c\u8bf4\u4eba\u54c1\u5374\u4e0d\u600e\u4e48\u597d\uff0c\u8fd8\u662f\u4e2a \u91c7\u82b1\u6deb\u8d3c\u5462\uff01\u201d\u4ed6\u7684\u540c\u4f34\u8fde\u5fd9\u5c0f\u58f0\u8bf4\u9053\uff1a\u201c\u5664\u58f0\uff0c\u53eb\u90a3\u59d3\u6587\u7684\u542c\u89c1\uff0c\u4f60\u53ef\u8981\u5403\u4e0d\u4e86\u515c\u7740\u8d70\uff01\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u5ba2\u4eba\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u6015\u4ec0\u4e48\uff1f\u4f60\u4e0d\u6562\u8bf4\uff0c\u6211\u504f\u8981\u8bf4\u3002\u6211\u8bf4\u5440\uff0c\u8fd9 \u624d\u53eb\u505a\u95e8\u5f53\u6237\u5bf9\uff01\u59d3\u6587\u7684\u56fa\u7136\u81ed\u540d\u662d\u5f70\uff0c\u7ec8\u5357\u5c71\u7684\u6b27\u9633\u5bb6\u5728\u6c5f\u6e56\u4e0a\u4e5f\u4e0d\u89c1\u5f97\u4fbf\u662f\u53e3\u7891\u8f7d \u9053\uff01\u201d\u8fd9\u4eba\u548c\u5927\u5bb6\u4f3c\u4e4e\u9887\u6709\u5acc\u9699\uff0c\u770b\u6765\u4e5f\u4f3c\u4e2a\u6b63\u6d3e\u4e2d\u4eba\uff0c\u4f46\u867d\u7136\u5982\u6b64\uff0c\u4ed6\u90a3\u51e0\u53e5\u51b7\u8a00\u51b7\u8bed\uff0c \u4e5f\u8fd8\u4e0d\u6562\u5927\u58f0\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u542c\u4e86\u201c\u7ec8\u5357\u5c71\u6b27\u9633\u5bb6\u201d\u8fd9\u516d\u4e2a\u5b57\uff0c\u5374\u7981\u4e0d\u4f4f\u5fc3\u5934\u4e00\u9707\uff0c\u201c\u7cdf\u7cd5\uff0c\u539f\u6765\u90a3\u4e8e\u5c11\u9cb2\u662f \u9a97\u6211\u6765\u7684\uff01\u201d\u4f46\u5904\u6b64\u5883\u5730\uff0c\u4ed6\u867d\u7136\u5fc3\u4e2d\u6709\u6c14\uff0c\u5374\u5df2\u4e0d\u80fd\u53d1\u4f5c\u3002\u597d\u5728\u90a3\u6587\u5ef7\u74a7\u53d4\u4f84\uff0c\u4f3c\u4e4e\u8fd8\u672a \u66fe\u53d1\u73b0\u4ed6\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u5728\u7740\u6025\uff0c\u5ffd\u542c\u5f97\u8033\u6735\u8fb9\u6709\u4e00\u4e2a\u6781\u4e3a\u719f\u6089\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6c5f\u8d24\u4f84\uff0c\u4e0d\u8981\u6015\uff0c\u6211\u5728 \u8fd9\u513f\uff0c\u5f85\u6211\u5077\u4e86\u51e0\u4ef6\u4e1c\u897f\uff0c\u54b1\u4eec\u4fe9\u4e00\u9053\u8d70\uff01\u201d\u6c5f\u6d77\u5929\u542c\u5f97\u8fd9\u4e2a\u58f0\u97f3\uff0c\u5f53\u771f\u662f\u60ca\u559c\u4ea4\u96c6\uff0c\u9669\u4e9b \u513f\u53eb\u51fa\u58f0\u6765\uff01 \u3000\u3000\u539f\u6765\u8fd9\u6b63\u662f\u4ed6\u7236\u4eb2\u7684\u628a\u5144\u2014\u2014\u5999\u624b\u795e\u5077\u59ec\u6653\u98ce\u7684\u58f0\u97f3\uff01\u53ea\u56e0\u4ed6\u7528\u7684\u662f\u201c\u5929\u9041\u4f20\u97f3\u201d\u4e4b \u672f\uff0c\u9664\u4e86\u6c5f\u6d77\u5929\u5916\u4e00\u5176\u4ed6\u7684\u4eba\u90fd\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6e38\u76ee\u56db\u987e\uff0c\u627e\u4e0d\u7740\u59ec\u6653\u98ce\uff0c\u5374\u89c1\u90a3\u4e8e\u5c11\u9cb2\u5dee\u4e0d\u591a\u5df1\u6324\u5230\u6700\u524d\u9762\u7684\u4e00\u6392\u4e86\u3002\u6c5f\u6d77\u5929 \u5fc3\u60f3\uff1a\u201c\u59ec\u4f2f\u4f2f\u5b9a\u7136\u662f\u7528\u4e0a\u4e86\u6613\u5bb9\u672f\u3002\u54fc\uff0c\u8fd9\u59d3\u4e8e\u7684\u574f\u86cb\uff0c\u4ed6\u60f3\u5bb3\u6211\uff0c\u6211\u5c82\u80fd\u8f7b\u6613\u9976\u4ed6\uff1f\u201d \u4ed6\u4f7f\u51fa\u5929\u7f57\u6b65\u6cd5\uff0c\u5728\u4eba\u4e1b\u4e2d\u5de6\u7a7f\u53f3\u63d2\u3002\u6ed1\u4f3c\u6e38\u9c7c\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5c31\u5230\u4e86\u4e8e\u5c11\u9cb2\u80cc\u540e\uff0c\u5fc3\u4e2d\u62ff\u5b9a \u4e86\u4e3b\u610f\u3001\u5018\u82e5\u90a3\u4e8e\u5c11\u9cb2\u51fa\u58f0\u63ed\u53d1\uff0c\u4ed6\u4fbf\u8981\u7acb\u5373\u5c06\u4ed6\u6bd9\u4e8e\u638c\u4e0b\uff01 \u3000\u3000\u4e8e\u5c11\u9cb2\u56de\u5934\u4e00\u671b\uff0c\u60e8\u7136\u4e00\u7b11\uff0c\u5c0f\u58f0\u8bf4\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u5f85\u4f1a\u513f\uff0c\u4f60\u5c31\u660e\u767d\uff0c\u6211\u8fd9\u6b21\u9a97\u4f60\u662f\u4e8b \u975e\u5f97\u5df2\uff0c\u8bf7\u4f60\u76f8\u4fe1\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u5e2e\u5fd9\u6211\u8fd9\u4e00\u6b21\u3002\u201d\u6c5f\u6d77\u5929\u5fc3\u60f3\uff1a\u201c\u4f60\u8fd9\u574f\u86cb\uff0c\u8fd8\u60f3\u6211\u5e2e\u5fd9 \u4f60\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u516b\u97f3\u9f50\u594f\uff0c\u95f9\u5f97\u6b63\u6b22\uff0c\u4e8e\u5c11\u9cb2\u51d1\u5728\u6c5f\u6d77\u5929\u7684\u8033\u65c1\u8fb9\u8bf4\u8bdd\uff0c\u522b\u4eba\u4e5f\u6ca1\u6ce8\u610f\u4ed6\u4eec\u3002\u6c5f\u6d77 \u5929\u5374\u6ce8\u610f\u4e86\u4e8e\u5c11\u9cb2\u7684\u9762\u8272\uff1b\u53ea\u89c1\u4ed6\u6101\u5bb9\u6ee1\u9762\uff0c\u5982\u6709\u91cd\u5fe7\uff0c\u4f46\u5374\u5e76\u4e0d\u662f\u60ca\u614c\uff0c\u90a3\u51e0\u53e5\u8bdd\u4e5f\u8bf4\u5f97 \u8bda\u631a\u4e4b\u6781\u3002\u6c5f\u6d77\u5929\u5fc3\u5934\u4e00\u52a8\uff0c\u84e6\u7136\u60f3\u8d77\uff0c\u5f53\u65e5\u4e8e\u5c11\u9cb2\u8981\u4ed6\u53bb\u6551\u6b27\u9633\u5a49\uff0c\u5411\u4ed6\u62a5\u544a\u6b27\u9633\u5a49\u5c06\u53d7 \u6bd2\u5211\u7684\u65f6\u5019\uff0c\u8138\u4e0a\u4e5f\u51fa\u73b0\u8fc7\u8fd9\u6837\u7684\u795e\u60c5\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e50\u58f0\u4e00\u6b62\uff0c\u8d3a\u5ba2\u7eb7\u7eb7\u56b7\u9053\uff1a\u201c\u770b\u5440\uff0c\u65b0\u5a18\u5b50\u6765\u4e86\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u5f26\u98a4\u6218\uff0c\u773c\u9762\u524d\u4f46\u89c1\u82b1\u56e2\u9526\u7c07\uff0c\u8033\u8fb9\u53a2\u53ea\u542c\u5f97\u73af\u4f69\u53ee\u549a\uff0c\u4e00\u7fa4\u4e2b\u9b1f\uff0c\u4fe8\u5982\u201c\u4f17 \u661f\u6367\u6708\u201d\u4e00\u822c\uff0c\u5df2\u628a\u90a3\u65b0\u5a18\u5b50\u201c\u6367\u201d\u4e86\u51fa\u6765\uff0c\u6276\u7740\u90a3\u65b0\u5a18\u5b50\u7684\u6b63\u662f\u6b27\u9633\u4e8c\u5a18\uff01\u8ddf\u5728\u540e\u9762\u7684\u5219 \u662f\u6b27\u9633\u4e8c\u5a18\u7684\u5927\u592b\u2014\u2014\u6b27\u9633\u4ef2\u548c\uff0c\u4ed6\u6276\u7740\u62d0\u6756\uff0c\u4e00\u8ddb\u4e00\u62d0\u7684\uff0c\u8138\u8272\u67af\u9ec4\uff0c\u770b\u6765\u4f3c\u662f\u5927\u75c5\u8fc7 \u540e\uff0c\u5c1a\u672a\u590d\u539f\u3002 \u3000\u3000\u65b0\u5a18\u5b50\u4f9d\u7167\u4e60\u4fd7\uff0c\u7528\u7ea2\u7f57\u5e15\u8499\u8fc7\u4e86\u5934\u9762\uff0c\u8981\u5f85\u5403\u8fc7\u4e86\u201c\u4ea4\u676f\u9152\u201d\uff0c\u624d\u80fd\u8ba9\u65b0\u90ce\u6311\u5f00\u3002\u6c5f \u6d77\u5929\u867d\u7136\u770b\u4e0d\u89c1\u65b0\u5a18\u7684\u9762\u5bb9\uff0c\u4f46\u5728\u8fd9\u6837\u7684\u60c5\u5f62\u4e4b\u4e0b\uff0c\u8fd9\u65b0\u5a18\u5b50\u662f\u6b27\u9633\u5a49\uff0c\u90a3\u662f\u7edd\u65e0\u53ef\u7591\u7684\u4e86\uff01 \u3000\u3000\u5c3d\u7ba1\u4ed6\u4e0e\u6b27\u9633\u5a49\u4e4b\u95f4\u6709\u8bb8\u591a\u6069\u6069\u6028\u6028\uff0c\u76f4\u5230\u5982\u4eca\uff0c\u662f\u654c\u662f\u53cb\uff0c\u4e5f\u5c1a\u672a\u5206\u660e\uff1b\u4f46\u4ed6\u773c\u770b\u770b \u6b27\u9633\u5a49\u5c31\u8981\u548c\u6587\u9053\u5e84\u62dc\u5802\u6210\u4eb2\uff0c\u5374\u7981\u4e0d\u4f4f\u4e00\u7247\u60d8\u7136\uff0c\u5341\u5206\u60cb\u60dc\uff0c\u5fc3\u4e2d\u6697\u60f3\uff1a\u201c\u8fd9\u6587\u9053\u5e84\u662f\u4e2a \u5978\u90aa\u6deb\u6076\u7684\u5c0f\u9b54\u5934\uff0c\u6b27\u9633\u5a49\u5ac1\u7ed9\u4ed6\uff0c\u5f53\u771f\u662f\u4e00\u6735\u9c9c\u82b1\u63d2\u5728\u725b\u7caa\u4e0a\u4e86\uff01\u201d \u3000\u3000\u6b27\u9633\u4ef2\u548c\u867d\u7136\u72b9\u5e26\u75c5\u5bb9\uff0c\u4f46\u5374\u662f\u559c\u6c14\u6d0b\u6d0b\uff0c\u4ed6\u4e0e\u6587\u5ef7\u58c1\u4e92\u76f8\u8d3a\u559c\u4e4b\u540e\uff0c\u968f\u7740\u5411\u4e00\u4e2a\u8001\u5934 \u5b50\u95ee\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4e09\u5f1f\u8fd8\u672a\u56de\u6765\u5417\uff1f\u201d\u539f\u6765\u6b27\u9633\u4ef2\u548c\u5144\u5f1f\u4e09\u4eba\uff0c\u4ed6\u5c45\u5f53\u4e2d\uff0c\u8fd9\u8001\u5934\u4e8e\u662f\u8001\u5927 \u6b27\u9633\u4f2f\u548c\uff0c\u8fd8\u6709\u4e00\u4e2a\u5f1f\u5f1f\u662f\u6b27\u9633\u5b63\u548c\u3002\u521a\u624d\u5728\u6b27\u9633\u4ef2\u548c\u672a\u51fa\u672a\u4e4b\u524d\uff0c\u5c31\u662f\u4ed6\u7684\u54e5\u54e5\u6b27\u9633\u4f2f\u548c \u4ee3\u8868\u5973\u5bb6\u62db\u547c\u5bbe\u5ba2\u7684\u3002 \u3000\u3000\u6b27\u9633\u4f2f\u548c\u9053\uff1a\u201c\u4e0d\u5fc5\u7b49\u4ed6\u4e86\uff0c\u5409\u65f6\u5df2\u5230\uff0c\u5148\u884c\u793c\u5427\uff01\u201d\u4e8e\u662f\u65b0\u90ce\u65b0\u5a18\u5e76\u80a9\u800c\u7acb\u3002\u9762\u671d\u7740 \u5973\u5bb6\u7684\u7956\u5148\u724c\u4f4d\uff0c\u8d5e\u793c\u7684\u5f00\u59cb\u5531\u9053\uff1a\u201c\u65b0\u4eba\u4e0a\u5802\uff0c\u559c\u6c14\u6d0b\u6d0b\uff0c\u767e\u5e74\u597d\u5408\uff0c\u4e94\u4e16\u5176\u660c\uff0c\u594f\u4e50\u3002 \u53e9\u9996\u2014\u2014\u201d \u3000\u3000\u5ffd\u542c\u5f97\u201c\u4e53\u201d\u7684\u4e00\u58f0\uff0c\u5bbe\u5ba2\u4eec\u8fd8\u4ee5\u4e3a\u662f\u97ad\u70ae\u58f0\u54cd\uff0c\u54ea\u77e5\u5374\u662f\u4e00\u56e2\u706b\u5149\uff0c\u59a5\u7136\u95f4\u5728\u65b0\u90ce\u7684 \u80cc\u4e0a\u7206\u70b8\u5f00\u6765\uff01\u4e08\u9053\u5e84\u5927\u53eb\u4e00\u58f0\uff0c\u53cc\u81c2\u4e00\u7529\uff0c\u90a3\u4ef6\u5d2d\u65b0\u7684\u957f\u888d\u7247\u7247\u788e\u88c2\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u6587\u5ef7\u58c1 \u4e0e\u6b27\u9633\u4f2f\u548c\u4e0d\u7ea6\u800c\u540c\u4e00\u9f50\u51fa\u624b\uff0c\u6587\u5ef7\u58c1\u957f\u8896\u4e00\u62e9\uff0c\u5c06\u90a3\u56e2\u706b\u5149\u5377\u4e86\u8fc7\u6765\uff0c\u767b\u65f6\u7184\u706d\uff0c\u4ed6\u8863\u8896 \u4e00\u6296\uff0c\u53ea\u89c1\u65e0\u6570\u6885\u82b1\u9488\u6563\u4e86\u6ee1\u5730\uff01\u6b27\u9633\u6015\u548c\u53cc\u6307\u4e00\u5f39\uff0c\u8d3a\u5ba2\u4e2d\u767b\u65f6\u6709\u4e2a\u4eba\u5927\u53eb\u4e00\u58f0\uff0c\u4ec6\u5012\u5730 \u4e0a\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u559d\u9053\uff1a\u201c\u4e8e\u5c11\u9cb2\uff0c\u4f60\u597d\u5927\u7684\u80c6\u5b50\uff01\u201d\u539f\u6765\u8fd9\u4e2a\u5411\u65b0\u90ce\u5077\u88ad\u7684\u4eba\uff0c\u6b63\u662f\u4e0e\u6c5f\u6d77\u5929 \u540c\u6765\u7684\u90a3\u4e2a\u4e8e\u5c11\u9cb2\uff0c\u53ea\u89c1\u4ed6\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\uff0c\u7ffb\u4e86\u8d77\u6765\uff0c\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u6c5f\u5c11\u4fa0\uff0c\u6211\u6c42\u4f60\u7684\u5c31\u662f \u8fd9\u4ef6\u4e8b\uff0c\u6211\u5b81\u613f\u5979\u5ac1\u7ed9\u4f60\uff0c\u4f60\u8d76\u5feb\u5e26\u5979\u8d70\u5427\uff01\u201d\u6b27\u9633\u4e8c\u5a18\u98de\u6251\u8fc7\u6765\uff0c\u53ef\u662f\u5979\u8fd8\u672a\u66fe\u6293\u7740\u4e8e\u5c11 \u9cb2\uff0c\u4e8e\u5c11\u9cb2\u5df2\u653e\u51fa\u4e00\u67c4\u5315\u9996\uff0c\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u63d2\u8fdb\u81ea\u5df1\u7684\u80f8\u819b\u4e86\uff01\u6b63\u662f\uff1a \u3000\u3000\u559c\u5e2d\u672a\u5f00\u7ea2\u70db\u706d\uff0c\u4e0d\u8f9e\u4e00\u6b7b\u4e3a\u6b89\u60c5\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u75f4\u60c5\u672a\u5410\u8eab\u5148\u6b7b\u3000\u5b7d\u503a\u96be\u507f\u70db\u5df2\u7070 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u75f4\u60c5\u672a\u5410\u8eab\u5148\u6b7b\u3000\u5b7d\u503a\u96be\u507f\u70db\u5df2\u7070 \u3000\u3000\u539f\u6765\u8fd9\u4e8e\u5c11\u9cb2\u4e0e\u6b27\u9633\u5a49\u540c\u5b66\u4e09\u5e74\uff0c\u5bf9\u5979\u65e9\u5df2\u662f\u79c1\u5fc3\u604b\u6155\u3002\u53ea\u56e0\u6b27\u9633\u5a49\u7684\u6b66\u529f\u6bd4\u4ed6\u5f3a\uff0c\u95e8 \u7b2c\u53c8\u6bd4\u4ed6\u9ad8\uff0c\u5979\u81ea\u60ed\u5f62\u79fd\uff0c\u8fd9\u4efd\u7231\u610f\uff0c\u5c31\u59cb\u7ec8\u4e0d\u6562\u8868\u9732\u51fa\u6765\u3002\u4f46\u4e5f\u6b63\u662f\u56e0\u6b64\uff0c\u7231\u6155\u65e5\u6df1\uff0c\u611f \u60c5\u65e0\u4ece\u5ba3\u6cc4\uff0c\u4fbf\u5230\u4e86\u5982\u75f4\u5982\u72c2\uff0c\u96be\u4ee5\u81ea\u5236\u7684\u7a0b\u5ea6\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u6b27\u9633\u5a49\u7684\u7236\u6bcd\u5c06\u5979\u8bb8\u914d\u7ed9\u6587\u9053\u5e84\u4e4b\u540e\uff0c\u6ee1\u6000\u60b2\u6124\uff0c\u51b3\u610f\u4ee5\u6b7b\u6765\u963b\u6320\u5979\u7684\u5a5a\u4e8b\u3002\u56e0 \u6b64\u9884\u5148\u51c6\u5907\u4e86\u6b79\u6bd2\u7684\u6697\u5668\uff0c\u624d\u6765\u8d74\u5bb4\uff0c\u51c6\u5907\u5728\u6697\u6740\u4e86\u65b0\u90ce\u4e4b\u540e\uff0c\u4fbf\u5373\u81ea\u5c3d\u3002\u4eca\u65e5\u7684\u4e00\u5207\uff0c\u53ef \u8bf4\u5168\u90fd\u662f\u6309\u7167\u4ed6\u7684\u8ba1\u5212\u800c\u884c\u3002 \u3000\u3000\u65e0\u5de7\u4e0d\u5de7\uff0c\u4ed6\u5728\u8d74\u5bb4\u7684\u9014\u4e2d\uff0c\u9047\u5230\u4e86\u6c5f\u6d77\u5929\uff0c\u4ed6\u662f\u6df1\u77e5\u6587\u5bb6\u53d4\u4f84\u7684\u5389\u5bb3\u7684\uff0c\u6b63\u6050\u6697\u6740\u4e0d \u6210\uff0c\u81ea\u8eab\u6b7b\u4e86\u4e5f\u963b\u6320\u4e0d\u4e86\u5a5a\u4e8b\uff0c\u96be\u5f97\u9047\u89c1\u6c5f\u6d77\u5929\uff0c\u4e8e\u662f\u9042\u7f16\u4e86\u4e00\u5957\u8c0e\u8a00\uff0c\u9a97\u5f97\u6c5f\u6d77\u5929\u4e0e\u4ed6\u540c \u884c\uff0c\u628a\u6c5f\u6d77\u5929\u5377\u5165\u4e86\u8fd9\u573a\u7ea0\u7eb7\u4e4b\u4e2d\u3002\u4e0d\u8fc7\uff0c\u4ed6\u867d\u7136\u662f\u7528\u4e86\u8be1\u8ba1\uff0c\u4f46\u5374\u7684\u786e\u662f\u4e3a\u4e86\u7231\u4ed6\u5e08\u59b9\u7684 \u7f18\u6545\uff0c\u4ed6\u4e5f\u7684\u786e\u662f\u5b81\u613f\u6b27\u9633\u5a49\u5ac1\u7ed9\u6c5f\u6d77\u5929\uff0c\u800c\u4e0d\u613f\u5979\u5ac1\u7ed9\u6587\u9053\u5e84\u7684\u3002 \u3000\u3000\u4e66\u63a5\u524d\u6587\uff0c\u4e14\u8bf4\u5728\u8fd9\u7ea2\u70db\u9ad8\u70e7\uff0c\u65b0\u4eba\u62dc\u5802\u4e4b\u9645\uff0c\u7a81\u7136\u53d1\u751f\u4e86\u8fd9\u4ef6\u610f\u5916\uff0c\u6ee1\u5802\u5bbe\u5ba2\u7981\u4e0d\u4f4f \u54d7\u7136\u60ca\u547c\uff0c\u79e9\u5e8f\u767b\u65f6\u5927\u4e71\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u5728\u53d1\u5446\uff0c\u6b27\u9633\u4e8c\u5a18\u5df2\u7ecf\u53d1\u89c9\uff0c\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\u3002\u4e00\u58f0\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u539f\u6765\u662f\u4f60\u5c0f \u8d3c\u524d\u6765\u6363\u9b3c\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fd9\u9053\uff0c\u201c\u8fd9\u4e0d\u5173\u6211\u7684\u4e8b\uff0c\u6211\u662f\u76f4\u5230\u73b0\u5728\u624d\u77e5\u9053\u662f\u4f60\u51fa\u5ac1\u5973\u513f\u7684\u3002\u4e0d\u8fc7\uff0c\u4e8e\u5c11\u9cb2\u8bf4 \u5f97\u4e5f\u6709\u9053\u7406\uff0c\u8fd9\u59d3\u6587\u7684\u786e\u5b9e\u4e0d\u662f\u597d\u4eba\uff0c\u4f60\u5973\u513f\u4e0d\u5ac1\u4ed6\u4e5f\u7f62\uff01\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6b27\u9633\u4e8c\u5a18\u5df2\u632f\u81c2\u6251\u6765\uff0c\u6012\u58f0\u9a82\u9053\uff1a\u201c\u4e0d\u5ac1\u7ed9\u4ed6\u5ac1\u7ed9\u4f60\u4e48\uff1f\u80e1\u8bf4\u516b\u9053\uff0c \u770b\u6211\u5148\u628a\u4f60\u7684\u5634\u6495\u4e86\uff01\u201d \u3000\u3000\u65b0\u5a18\u5b50\u5c16\u53eb\u4e00\u58f0\uff0c\u63a8\u5f00\u4e2b\u9b1f\uff0c\u5f80\u540e\u5802\u4fbf\u8dd1\u3002\u6587\u9053\u5e84\u8dcc\u5012\u5730\u4e0a\uff0c\u8fd9\u65f6\u624d\u722c\u8d77\u6765\u3002\u773c\u770b\u597d\u597d \u4e00\u573a\u5a5a\u4e8b\uff0c\u7ed9\u5f04\u5f97\u4e00\u584c\u7cca\u6d82\uff0c\u4e5f\u6c14\u5f97\u5927\u9a82\uff0c\u53ef\u662f\u4ed6\u624d\u9a82\u5f97\u4e24\u58f0\uff0c\u4e00\u53e3\u9c9c\u8840\u53c8\u55b7\u4e86\u51fa\u6765\uff0c\u767b\u65f6 \u6655\u4e86\u8fc7\u53bb\u3002\u539f\u6765\u4ed6\u5728\u62dc\u5802\u884c\u672d\uff0c\u6beb\u65e0\u9632\u5907\u4e4b\u9645\uff0c\u4e2d\u4e86\u4e8e\u5c11\u9cb2\u7684\u6b79\u6bd2\u6697\u5668\uff0c\u867d\u5f97\u53d4\u7236\u53ca\u65f6\u76f8 \u6551\uff0c\u672a\u81f3\u8eab\u6b7b\u5f53\u573a\uff0c\u4f46\u4e5f\u53d7\u4f24\u4e0d\u5c11\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u53eb\u9053\uff1a\u201c\u4f60\u522b\u8bef\u4f1a\uff0c\u6211\u5bf9\u4f60\u5973\u513f\u5e76\u65e0\u522b\u7684\u5ff5\u5934\uff0c\u6211\u2026\u2026\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u6b27\u9633\u4e8c\u5a18\u5df2 \u662f\u5341\u6307\u5982\u94a7\uff0c\u5411\u4ed6\u731b\u6251\uff0c\u6c5f\u6d77\u5929\u8ba4\u5f97\u8fd9\u662f\u201c\u8708\u86a3\u526a\u201d\u7684\u6076\u6bd2\u624b\u6cd5\uff0c\u5018\u82e5\u5185\u529f\u7a0d\u5f31\uff0c\u7ed9\u5979\u6293 \u4e2d\uff0c\u4fbf\u96be\u514d\u5f00\u819b\u7834\u8179\u4e4b\u707e\u3002\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u6697\u6697\u751f\u6c14\u3002 \u3000\u3000\u793c\u5802\u4e0a\u5230\u5904\u662f\u4eba\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u56de\u65cb\u4e4b\u5730\uff0c\u6c5f\u6d77\u5929\u4e00\u54ac\u7259\u6839\uff0c\u5fc3\u4e2d\u60f3\u9053\uff0c\u201c\u4f60\u65e2\u7136\u5b9a\u8981\u628a \u6211\u7f6e\u4e8e\u6b7b\u5730\uff0c\u8bf4\u4e0d\u5f97\u6211\u4e5f\u53ea\u597d\u5f97\u7f6a\u4f60\u4e86\uff01\u201d\u5f53\u4e0b\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u4e5f\u7acb\u5373\u53cd\u624b\u5411\u6b27\u9633\u4e8c\u5a18\u6293\u53bb\uff01 \u3000\u3000\u8fd1\u8eab\u640f\u6597\uff0c\u5018\u82e5\u529f\u529b\u76f8\u5dee\u4e0d\u8fdc\uff0c\u8bb2\u7a76\u7684\u4fbf\u662f\u8eab\u624b\u77eb\u6377\uff0c\u62db\u6570\u5947\u5e7b\u3002\u6b27\u9633\u4e8c\u5a18\u4fa0\u6570\u5341\u5e74\u529f \u529b\uff0c\u672c\u6765\u4e0d\u81f3\u4e8e\u8f93\u7ed9\u6c5f\u6d77\u5929\uff0c\u4f46\u662f\u6c5f\u6d77\u5929\u901a\u6653\u6b63\u90aa\u5404\u6d3e\u7684\u5bb6\u6570\uff0c\u6b27\u9633\u4e8c\u5a18\u4e00\u51fa\u624b\uff0c\u4ed6\u4fbf\u77e5\u9053 \u5bf9\u65b9\u5c06\u8981\u5982\u4f55\u653b\u51fb\uff0c\u751a\u81f3\u540e\u7740\u6f14\u53d8\u5982\u95f4\uff0c\u4e5f\u65e9\u5df2\u4e86\u7136\u4e8e\u80f8\u3002\u8fd9\u4e00\u70b9\uff0c\u6b27\u9633\u4e8c\u5a18\u5c31\u5dee\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u811a\u8e0f\u4e5d\u5bab\u516b\u5366\u65b9\u4f4d\u3002\u7528\u7684\u662f\u5929\u7f57\u6b65\u6cd5\uff0c\u53cc\u638c\u4f7f\u7684\uff0c\u5219\u662f\u201c\u9634\u9633\u6293\u201d\u7684\u529f\u592b\u3002\u5929\u7f57 \u6b65\u6cd5\uff0c\u5730\u65b9\u8d8a\u7a84\uff0c\u8d8a\u89c1\u795e\u5999\uff1b\u201c\u9634\u9633\u6293\u201d\u5219\u662f\u4e54\u5317\u6e9f\u79d8\u7c4d\u4e2d\u7684\u4e00\u95e8\u7edd\u5b66\uff0c\u53cc\u638c\u4e00\u9634\u4e00\u9633\uff0c\u865a \u5b9e\u76f8\u751f\uff0c\u53d8\u5316\u83ab\u6d4b\uff0c\u800c\u4e14\u53cc\u624b\u4e00\u6293\uff0c\u7acb\u5373\u4fbf\u6709\u521a\u67d4\u4e24\u670d\u529b\u9053\uff0c\u4e92\u76f8\u6fc0\u8361\uff0c\u4e92\u76f8\u7275\u5f15\uff0c\u8fd9\u79cd\u795e \u5999\u7684\u529f\u592b\uff0c\u6b27\u9633\u4e8c\u5a18\u66f4\u662f\u8fde\u89c1\u4e5f\u6ca1\u6709\u89c1\u8fc7\uff01 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u51fa\u624b\u5728\u524d\uff0c\u773c\u770b\u8fd9\u4e8c\u6293\u5bf9\u65b9\u51b3\u96be\u8eb2\u907f\uff0c\u54ea\u77e5\u4e00\u6293\u4e4b\u4e0b\uff0c\u7adf\u7136\u8fde\u6c5f\u6d77\u5929\u7684\u8863\u89d2\u4e5f \u6ca1\u6cbe\u7740\uff1a\u6b63\u81ea\u5fc3\u4e2d\u4e00\u51db\uff0c\u6c5f\u6d77\u5929\u5df2\u53cd\u624b\u6293\u6765\uff01 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u88ab\u6c5f\u6d77\u5929\u7684\u638c\u529b\u7275\u5f15\uff0c\u91cd\u5fc3\u4e0d\u7a33\uff0c\u8eab\u5411\u524d\u503e\uff0c\u6b27\u9633\u4e8c\u5a18\u60c5\u77e5\u4e0d\u5999\uff0c\u6025\u5fd9\u6ef4\u6e9c\u6e9c \u4e00\u4e2a\u8f6c\u8eab\uff0c\u4f46\u5df2\u7ecf\u8fdf\u4e86\uff01\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u6c5f\u6d77\u5929\u53cc\u624b\u6293\u4e0b\uff0c\u628a\u5979\u65b0\u505a\u7684\u4e00\u4ef6\u5927\u7ea2\u88d9 \u5b50\uff0c\u6495\u6210\u4e24\u7247\uff01 \u3000\u3000\u8fd9\u4ef6\u88d9\u5b50\uff0c\u662f\u5979\u4e3a\u4e86\u65b0\u4efb\u4e08\u6bcd\u5a18\u800c\u505a\u7684\uff0c\u4e0d\u6599\u4ea4\u624b\u4e00\u62db\uff0c\u5c31\u7ed9\u6c5f\u6d77\u5929\u6495\u7834\uff0c\u5e78\u4e8f\u91cc\u9762\u8fd8 \u6709\u886c\u886b\u886c\u88d9\uff0c\u4f46\u4ea6\u5df2\u7ecf\u72fc\u72c8\u4e0d\u582a\u4e86\uff01 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u867d\u662f\u8001\u9762\u76ae\uff0c\u4e5f\u7981\u4e0d\u4f4f\u9762\u7ea2\u8033\u8d64\uff0c\u5979\u662f\u4e2a\u65b0\u4e08\u6bcd\u5a18\uff0c\u5728\u6ee1\u5802\u5bbe\u5ba2\uff0c\u4f17\u76ee\u777d\u777d\u4e4b \u4e0b\uff0c\u6068\u4e0d\u5f97\u6709\u4e2a\u5730\u6d1e\u94bb\u4e0b\u53bb\u65b9\u597d\uff01\u54ea\u91cc\u8fd8\u6562\u518d\u6253\uff0c\u6025\u5fd9\u51b2\u8fdb\u540e\u5802\uff0c\u5230\u4e86\u540e\u5802\uff0c\u624d\u6562\u9a82\u51fa\u58f0\u6765\u3002 \u3000\u3000\u5176\u5b9e\u6c5f\u6d77\u5929\u5e76\u4e0d\u662f\u6709\u5fc3\u6495\u7834\u5979\u7684\u88d9\u5b50\u7684\uff0c\u53ea\u56e0\u6b27\u9633\u4e8c\u5a18\u90a3\u4e00\u8f6c\u8eab\uff0c\u6070\u597d\u78b0\u4e0a\uff0c\u6536\u624b\u5df2\u6765 \u4e0d\u53ca\u3002\u6c5f\u6d77\u5929\u597d\u751f\u540e\u6094\uff0c\u6b63\u81ea\u60f3\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u6211\u600e\u4e48\u4f1a\u6495\u7834\u5979\u7684\u88d9\u5b50\u7684\uff1f\u5f53\u7740\u8fd9\u4e48\u591a\u513f\uff0c\u522b \u4eba\u4e00\u5b9a\u4ee5\u4e3a\u6211\u662f\u4e2a\u8f7b\u8584\u5c11\u5e74\u4e86\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u6587\u5ef7\u58c1\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4eb2\u5bb6\u6bcd\u4e0d\u5fc5\u751f\u6c14\uff0c\u4f8d\u6211\u6765\u64d2\u8fd9\u5c0f\u8d3c\uff01\u201d\u58f0\u5230\u4eba\u5230\uff0c\u8f7b\u98d8\u98d8 \u7684\u5411\u6c5f\u6d77\u5929\u865a\u62cd\u4e00\u638c\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u8868\u9762\u770b\u672a\uff1a\u8f7b\u8f7b\u5143\u529b\uff0c\u5176\u5b9e\u5374\u662f\u7528\u4e0a\u4e86\u201c\u4e09\u8c61\u5f52\u5143\u201d\u2014\u2014\u795e\u3001\u6c14\u3001\u5f62\u4e09\u8005\u5408\u4e00\u7684 \u6700\u4e0a\u4e58\u5185\u529f\uff0c\u6f5c\u529b\u4ece\u56db\u9762\u516b\u65b9\u538b\u6765\uff0c\u6709\u5982\u6697\u6d41\u6c79\u6d8c\uff01 \u3000\u3000\u5728\u6c5f\u6d77\u5929\u4e0e\u6b27\u9633\u4e8c\u5a18\u640f\u6597\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u65c1\u8fb9\u7684\u5bbe\u5ba2\u6bd5\u7adf\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u6bd4\u8d77\u6587\u5ef7\u58c1\u6765\uff0c\u603b\u662f \u8981\u7a0d\u900a\u4e00\u7b79\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u6839\u672c\u4e0d\u7406\u90a3\u4e9b\u8dcc\u7ffb\u4e86\u7684\u8d3a\u5ba2\uff0c\u5de6\u638c\u672a\u6536\uff0c\u53f3\u638c\u53c8\u63a5\u7740\u62cd\u51fa\uff0c\u8fd9\u4e00\u638c\u6015\u51fa\uff0c\u4ed6\u5df2\u662f \u4e0e\u6c5f\u6d77\u5929\u9762\u5bf9\u7740\u9762\uff0c\u8ddd\u79bb\u4e0d\u5230\u54ab\u5c3a\u4e4b\u5730\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5728\u4ed6\u638c\u529b\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u5929\u7f57\u6b65\u6cd5\u4ea6\u96be\u95ea\u907f\uff0c\u9000\u4e5f\u65e0\u9000\uff0c\u53ea\u6709\u594b\u8d77\u5168\u529b\u4e0e\u4ed6\u786c\u62fc\u4e00\u638c\uff01 \u3000\u3000\u53ea\u542c\u5f97\u4e86\u201c\u7bf7\u201d\u7684\u4e00\u58f0\uff0c\u6c5f\u6d77\u5929\u8fde\u6643\u51e0\u6643\uff0c\u767b\u65f6\u989d\u89d2\u4e0a\u5192\u51fa\u4e00\u9897\u9897\u9ec4\u8c46\u822c\u7c97\u5927\u7684\u6c57\u73e0\uff0c \u800c\u6587\u5ef7\u58c1\u5374\u8fd8\u662f\u795e\u8272\u81ea\u5982\uff01 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6587\u5ef7\u58c1\u8de8\u524d\u4e00\u6b65\uff0c\u5df2\u662f\u6b3a\u5230\u4e86\u6c5f\u6d77\u5929\u8eab\u524d\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u91d1\u4e16\u9057\u6559\u51fa \u6765\u7684\u5f92\u5f1f\uff0c\u539f\u6765\u4e5f\u4e0d\u8fc7\u5982\u6b64\u8fd9\u822c\uff01\u6211\u770b\u4f60\u80fd\u63a5\u6211\u51e0\u638c\uff1f\u201d\u7b2c\u4e09\u6b21\u53d1\u638c\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u529b\u9053\u6bd4\u524d \u5f3a\u4e86\u4e00\u500d\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u6211\u8d25\u4e86\u4e0d\u6253\u7d27\uff0c\u53ef\u4e0d\u80fd\u8ba9\u5e08\u7236\u7ed9\u4eba\u770b\u8f7b\uff01\u201d\u6b64\u5ff5\u4e00\u751f\uff0c\u767b\u65f6\u6a2a\u8d75\u5fc3\u80a0\uff0c \u84e6\u5730\u4e00\u54ac\u820c\u5c16\uff0c\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u5927\u559d\u9053\uff1a\u201c\u4f60\u662f\u6211\u5e08\u7236\u624b\u4e0b\u8d25\u5c06\uff0c\u6211\u8fd8\u6015\u4f60\u4e0d\u6210\uff01\u201d\u4e5f \u662f\u53cc\u638c\u5e76\u51fa\uff0c\u4e0e\u6587\u5ef7\u58c1\u8fce\u4e2a\u6b63\u7740\uff0c\u8fd9\u56de\u56db\u638c\u76f8\u4ea4\uff0c\u5374\u662f\u6beb\u65e0\u58f0\u54cd\u3002\u6587\u5ef7\u58c1\u53ea\u89c9\u5bf9\u65b9\u7684\u5185\u529b\uff0c \u7a81\u7136\u5927\u5f97\u51fa\u5947\uff0c\u624b\u638c\u7adf\u7136\u7ed9\u5bf9\u65b9\u80f6\u7740\uff0c\u4e0d\u80fd\u6446\u8131\uff0c\u4e0d\u7981\u4e0d\u4f4f\u4e0a\u8eab\u6643\u4e86\u4e00\u6643\uff0c\u5934\u4e0a\u5192\u51fa\u4e86\u70ed\u817e \u817e\u7684\u767d\u6c14\u6765\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u54ac\u7834\u820c\u5c16\u4e4b\u65f6\uff0c\u6587\u5ef7\u58c1\u8fd8\u4ee5\u4e3a\u4ed6\u662f\u652f\u6301\u4e0d\u4f4f\uff0c\u4ee5\u81f4\u53e3\u5410\u9c9c\u8840\uff0c\u54ea\u6599\u8f6c\u77ac\u4e4b\u95f4\uff0c\u6c5f \u6d77\u5929\u7684\u5185\u529b\u53cd\u800c\u5e73\u6dfb\u4e00\u500d\uff0c\u4e0d\u4f46\u5b88\u7a33\u4e86\u95e8\u6237\uff0c\u5de8\u6709\u4f59\u529b\u8fd8\u51fb\u8fc7\u6765\uff0c\u9976\u662f\u6587\u5ef7\u58c1\u5728\u6b66\u5b66\u4e0a\u6709\u6df1 \u6e5b\u7684\u7814\u7a76\uff0c\u8fd9\u65f6\u4e5f\u7ed9\u4ed6\u5f04\u5f97\u60ca\u7591\u4e0d\u5b9a\uff0c\u9707\u9a87\u83ab\u540d\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u54ea\u4f1a\u77e5\u9053\uff0c\u6c5f\u6d77\u5929\u7528\u7684\u662f\u4e54\u5317\u6e9f\u79d8\u7c4d\u4e2d\u4e00\u79cd\u6700\u53e4\u602a\u3001\u7684\u529f\u592b\uff0c\u540d\u4e3a\u201c\u5929\u9b54\u89e3\u4f53\u5927 \u6cd5\u201d\uff0c\u5728\u81ea\u6b8b\u8eab\u4f53\u7684\u4efb\u4f55\u4e00\u90e8\u4ed5\u4e4b\u540e\uff0c\u5185\u529b\u53ef\u4ee5\u9661\u589e\u4e00\u500d\u4ee5\u4e0a\u3002\u5f53\u5e74\uff0c\u5389\u80dc\u7537\u5728\u5929\u5c71\u5357\u9ad8\u5cf0 \u4e0e\u5510\u6653\u6f9c\u6bd4\u62fc\u5185\u529f\uff0c\u5c31\u66fe\u7ecf\u7528\u8fc7\u8fd9\u79cd\u90aa\u6cd5\uff0c\u53cd\u8d25\u4e3a\u80dc\uff0c\u51e0\u4e4e\u8981\u4e86\u5510\u6653\u6f9c\u7684\u6027\u547d\u3002\u4f46\u201c\u5929\u9b54\u89e3 \u4f53\u5927\u6cd5\u201d\u6700\u4e3a\u8017\u635f\u5143\u6c14\uff0c\u6240\u4ee5\u91d1\u4e16\u9057\u5728\u4f20\u6388\u8fd9\u79cd\u529f\u592b\u7684\u65f6\u5019\uff0c\u4e5f\u66fe\u7ecf\u5411\u6c5f\u6d77\u5929\u518d\u4e09\u544a\u8beb\uff0c\u8981 \u4ed6\u975e\u5728\u4e07\u4e0d\u5f97\u5df2\u4e4b\u65f6\uff0c\u51b3\u4e0d\u53ef\u4ee5\u8f7b\u7528\uff0c\u5982\u4eca\u6c5f\u6d77\u5929\u662f\u56e0\u4e3a\u6587\u5ef7\u8fb1\u53ca\u4ed6\u7684\u5e08\u7236\uff0c\u4e00\u6012\u4e4b\u4e0b\uff0c\u624d \u4e0d\u987e\u540e\u679c\uff0c\u51b3\u610f\u4e0e\u654c\u4eba\u4e24\u8d25\u4ff1\u4f24\u7684\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u867d\u7136\u53e3\u51fa\u5927\u8a00\uff0c\u4f3c\u4e4e\u628a\u91d1\u4e16\u9057\u90fd\u4e0d\u653e\u5728\u773c\u5185\uff0c\u5176\u5b9e\uff0c\u5728\u6c5f\u6d77\u5929\u63a5\u4e86\u4ed6\u5f00\u5934\u7684\u8fde\u73af \u4e8c\u638c\u4e4b\u540e\uff0c\u4ed6\u5df2\u662f\u5fcc\u60ee\u5230\u6781\uff0c\u4ed6\u5fc3\u91cc\u771f\u6b63\u60f3\u7684\u662f\uff1a\u201c\u91d1\u4e16\u9057\u7684\u6b66\u5b66\u9020\u8be3\u771f\u662f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u6559\u51fa \u6765\u7684\u5f92\u5f1f\uff0c\u4e0d\u8fc7\u5341\u4e03\u516b\u5c81\uff0c\u4e5f\u5c45\u7136\u63a5\u5f97\u4f4f\u6211\u4e09\u8c61\u5f52\u5143\u7684\u7edd\u9876\u5185\u529f\uff0c\u5018\u82e5\u518d\u8fc7\u51e0\u5e74\uff0c\u90a3\u8fd8\u4e86 \u5f97\uff1f\u4eca\u65e5\u5982\u4e0d\u628a\u4ed6\u9664\u53bb\uff0c\u4ed6\u4eec\u4e24\u5e08\u5f92\u8054\u624b\uff0c\u91d1\u4e16\u9057\u66f4\u662f\u5982\u864e\u6dfb\u7ffc\u4e86\uff01\u201d\u6b63\u662f\u7531\u4e8e\u8fd9\u4e00\u5ff5\u5934\uff0c \u4ed6\u51b3\u5fc3\u8981\u628a\u6c5f\u6d77\u5929\u6bd9\u4e8e\u638c\u4e0b\uff01 \u3000\u3000\u54ea\u77e5\u6c5f\u6d77\u5929\u4e0d\u501f\u8017\u635f\u5143\u6c14\u3002\u7adf\u628a\u201c\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\u201d\u65bd\u5c55\u51fa\u6765\uff0c\u6587\u5ef7\u58c1\u7684\u529f\u529b\u867d\u7136\u6bd4\u6c5f\u6d77 \u5929\u6df1\u539a\u5f97\u591a\uff0c\u4f46\u4e5f\u672a\u80fd\u6bd4\u4ed6\u9ad8\u51fa\u4e00\u500d\uff0c\u56e0\u6b64\u56db\u638c\u76f8\u4ea4\u4e4b\u540e\uff0c\u6c5f\u6d77\u5929\u53cd\u800c\u5360\u4e86\u4e0a\u98ce\uff0c\u8feb\u5f97\u6587\u5ef7 \u58c1\u51b7\u6c57\u76f4\u6d41\uff0c\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u800c\u65e0\u53cd\u653b\u4e4b\u529b\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u5fc3\u5934\u5927\u9707\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u5c0f\u5b50\u521a\u624d\u662f\u6545\u610f\u793a\u5f31\uff0c\u8bf1\u6211\u4e0a\u5f53\u7684\u4e48\uff1f\u201d\u8981\u77e5\u6bd4 \u62fc\u5185\u529f\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u529b\u5f3a\u5219\u80dc\uff0c\u529b\u5f31\u5fc5\u8d25\uff0c\u51b3\u96be\u4fa5\u5e78\u3002\u5982\u4eca\u6587\u5ef7\u58c1\u7684\u53cc\u638c\u5df2\u88ab\u80f6\u7740\uff0c\u5185\u529b\u6b63 \u81ea\u6e90\u6e90\u6cc4\u51fa\uff0c\u5018\u82e5\u6446\u8131\u4e0d\u5f00\uff0c\u518d\u8fc7\u4e00\u70b7\u9999\u7684\u65f6\u523b\uff0c\u52bf\u5fc5\u4e24\u4eba\u7686\u53d7\u91cd\u4f24\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u7a76\u7adf\u662f\u4e2a\u6df1\u901a\u6b66\u5b66\u3001\u8001\u7ec3\u975e\u51e1\u7684\u4eba\u7269\uff0c\u89c9\u51fa\u4e0d\u5999\uff0c\u7acb\u5373\u5f3a\u6151\u5fc3\u795e\u3002\u9547\u5b9a\u4e0b\u6765\uff0c\u7ec6 \u5bdf\u5bf9\u65b9\u7684\u865a\u5b9e\u3002\u8fc7\u4e86\u7247\u523b\uff0c\u53ea\u89c9\u5bf9\u65b9\u7684\u5185\u529b\u6e90\u6e90\u653b\u6765\uff0c\u867d\u7136\u96c4\u539a\u975e\u5e38\uff0c\u4f46\u5374\u5e76\u975e\u59cb\u7ec8\u5982\u4e00\uff0c \u4ea6\u5373\u201c\u8282\u594f\u201d\u5e76\u4e0d\u5e73\u5300\uff0c\u5ffd\u5f3a\u5ffd\u5f31\uff0c\u5c31\u5982\u6f6e\u6c34\u4e00\u822c\uff0c\u4e00\u9635\u6d6a\u5934\u8fc7\u540e\uff0c\u6f6e\u6c34\u5c31\u8981\u9000\u4e0b\u53bb\uff0c\u7136\u540e \u518d\u6d8c\u4e0a\u6765\u3002\u539f\u6765\u8fd9\u662f\u56e0\u4e3a\u6c5f\u6d77\u5929\u706b\u5019\u672a\u5230\uff0c\u4ed6\u7684\u5185\u529b\u7a81\u7136\u95f4\u589e\u5f3a\u4e86\u4e00\u500d\uff0c\u6025\u5207\u95f4\u5c31\u4e0d\u80fd\u968f\u5fc3 \u8fd0\u7528\uff0c\u6307\u6325\u5982\u610f\uff0c\u53ea\u597d\u8ba9\u90a3\u80a1\u9aa4\u7136\u589e\u5f3a\u7684\u5185\u529b\uff0c\u80e1\u4e71\u5411\u654c\u4eba\u51b2\u51fb\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u5bdf\u89c9\u5230\u4e86\u8fd9\u4e00\u70b9\uff0c\u7acb\u5373\u5c06\u5185\u529b\u51dd\u805a\u4e2d\u6307\u6307\u5c16\uff0c\u8d81\u7740\u5bf9\u65b9\u201c\u9000\u6f6e\u201d\u7684\u65f6\u5019\uff0c\u84e6\u5730\u4e00 \u5f39\uff0c\u8fd9\u4e00\u7740\u6697\u5408\u5175\u6cd5\u4e0a\u201c\u907f\u5176\u671d\u9510\uff0c\u51fb\u5176\u66ae\u5f52\u201d\u7684\u9053\u7406\uff0c\u767b\u65f6\u628a\u6c5f\u6d77\u5929\u7684\u5185\u529b\u5316\u89e3\u3002\u53cc\u638c\u4ece \u5bb9\u7684\u64a4\u4e86\u56de\u6765\u3002 \u3000\u3000\u4e5f\u5e78\u4e8f\u6587\u5ef7\u58c1\u89c1\u673a\u5f97\u65e9\uff0c\u5426\u5219\u4ed6\u56fa\u7136\u96be\u514d\u91cd\u4f24\uff0c\u6c5f\u6d77\u5929\u5143\u6c14\u6d88\u8017\u8fc7\u751a\uff0c\u53ea\u6015\u6700\u5c11\u4e5f\u5f97\u5927 \u75c5\u4e00\u573a\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7684\u624b\u5c11\u9633\u7ecf\u8109\u88ab\u5bf9\u65b9\u7684\u9634\u52b2\u4e00\u51b2\uff0c\u8eab\u8eaf\u4e00\u9707\uff0c\u4e0d\u7531\u81ea\u5df1\u9000\u4e86\u4e09\u6b65\uff0c\u8840\u8109\u672a\u8212\u4e00\u5185 \u529b\u7684\u901a\u8def\u53d7\u963b\uff0c\u4e0d\u80fd\u5ba3\u6cc4\uff0c\u767b\u65f6\u8840\u8109\u503a\u5f20\uff0c\u53cc\u773c\u706b\u7ea2\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u60ca\u7591\u4e0d\u5b9a\uff0c\u54ea\u6562\u518d\u9c81\u83bd\u8fdb\u51fb\uff1f\u53ea\u597d\u6697\u52a0\u6212\u5907\uff0c\u51dd\u795e\u6ce8\u610f\u5bf9\u65b9\u7684\u53d8\u5316\uff0c\u53cc\u65b9\u5c31\u4f3c\u6597 \u9e21\u4e00\u822c\uff0c\u4f60\u76ef\u7740\u6211\uff0c\u6211\u76ef\u7740\u4f60\uff0c\u8c01\u90fd\u4e0d\u6562\u8f7b\u52a8\u3002 \u3000\u3000\u5ffd\u5730\u4ece\u8d3a\u5ba2\u4e2d\u95f4\u51b2\u51fa\u4e86\u4e00\u4e2a\u80a5\u5934\u5927\u8033\u7684\u548c\u5c1a\uff0c\u63d0\u7740\u4e00\u6839\u6c34\u78e8\u7985\u6756\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u59d3\u6587 \u7684\uff0c\u6211\u662f\u7ed9\u6b27\u9633\u4e8c\u54e5\u7684\u9762\u5b50\uff0c\u624d\u6765\u559d\u8fd9\u4e00\u676f\u559c\u9152\uff0c\u4f60\u80c6\u6562\u6b3a\u8d1f\u6211\u7684\u5f92\u513f\uff1f\u201d\u624b\u8d77\u6756\u843d\uff0c \u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u5c31\u5411\u6587\u5ef7\u58c1\u7684\u80cc\u5fc3\u51fb\u4e0b\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u80d6\u548c\u5c1a\uff0c\u5c31\u662f\u521a\u5bf8\u88ab\u6587\u5ef7\u58c1\u638c\u529b\u9707\u7ffb\uff0c\u8dcc\u5f97\u5934\u7834\u8840\u6d41\u7684\u90a3\u4e2a\u5c0f\u548c\u5c1a\u7684\u5e08\u7236\uff0c\u4ed6 \u662f\u96c1\u8361\u5c71\u9f99\u6e6b\u5bfa\u7684\u65b9\u4e08\u9f99\u9690\u5927\u5e08\uff0c\u4e00\u8eab\u6a2a\u7ec3\u7684\u5916\u5bb6\u529f\u592b\u5df2\u5230\u4e86\u9a88\u6307\u53ef\u6d1e\u725b\u8179\uff0c\u6a2a\u638c\u53ef\u6bd9\u864e\u8c79 \u7684\u5730\u6b65\uff0c\u4ed6\u867d\u662f\u4e2a\u51fa\u5bb6\u4eba\uff0c\u813e\u6c14\u5374\u751a\u4e3a\u66b4\u8e81\uff0c\u8fd9\u6b21\u4ed6\u5e26\u4e86\u5f92\u5f1f\uff0c\u8001\u8fdc\u7684\u5230\u7ec8\u5357\u5c71\u6b27\u9633\u5bb6\u6765\u4f5c \u8d3a\u5ba2\uff0c\u4e0d\u6599\u5728\u6ee1\u5802\u5bbe\u5ba2\u4e4b\u524d\uff0c\u4ed6\u7684\u5f92\u5f1f\u7adf\u7136\u7a81\u906d\u6a2a\u7978\uff0c\u88ab\u6587\u5ef7\u5288\u7684\u638c\u529b\u9707\u7ffb\uff0c\u4ed6\u81ea\u89c9\u989c\u9762\u65e0 \u5149\uff0c\u4e00\u65f6\u95f4\u6d4a\u6c14\u4e0a\u6d8c\uff0c\u4e0d\u987e\u4e00\u5207\uff0c\u5c31\u8981\u51fa\u6765\u4e0e\u6587\u5ef7\u58c1\u62fc\u547d\u3002 \u3000\u3000\u4ee5\u4ed6\u7684\u5916\u5bb6\u529f\u592b\uff0c\u8fd9\u4e00\u6756\u6253\u4e0b\uff0c\u5c31\u662f\u4e00\u5757\u5927\u77f3\u5934\uff0c\u4e5f\u8981\u88ab\u4ed6\u6253\u6210\u7c89\u788e\u3002\u53ef\u662f\u6587\u5ef7\u58c1\u5374\u604d \u5982\u4e0d\u95fb\u4e0d\u89c1\uff0c\u6839\u672c\u5c31\u4e0d\u7406\u776c\u4ed6\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u8fd9\u4e00\u6756\u5df2\u7ecf\u6253\u4e2a\u6b63\u7740\uff0c\u53ef\u662f\u88ab\u6253\u7ffb\u7684\u4e0d\u662f\u6587\u5ef7\u58c1\uff0c\u53cd\u800c\u662f\u9f99\u9690\u5927 \u5e08\u7ed9\u9707\u5f97\u629b\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6b27\u9633\u4f2f\u548c\u8fde\u5fd9\u8dd1\u6765\uff0c\u53eb\u9053\uff1a\u201c\u9f99\u9690\u5927\u5e08\uff0c\u8bf7\u770b\u5728\u6211\u7684\u9762\u4e0a\uff0c\u5c06\u8fd9\u573a\u8fc7\u8282\u63ed\u5f00\u4e86\u5427\uff01\u201d\u4ed6 \u53cc\u81c2\u4e00\u4f38\uff1a\u5c06\u9f99\u9690\u5927\u5e08\u63a5\u7740\u3002\u5e78\u4e8f\u6709\u4ed6\u8fd9\u4e48\u4e00\u63a5\uff0c\u628a\u9f99\u9690\u5927\u5e08\u8eab\u4e0a\u7684\u53cd\u5f39\u4e4b\u529b\u79fb\u8f6c\u5230\u81ea\u5df1\u8eab \u4e0a\uff0c\u9f99\u9690\u5927\u5e08\u624d\u5154\u4e8e\u683d\u66f4\u5927\u7684\u7b4b\u6597\u3002 \u3000\u3000\u201c\u539f\u6765\u6587\u5ef7\u58c1\u6b63\u5728\u51dd\u795e\u8fd0\u6c14\uff0c\u51c6\u5907\u4ee5\u6700\u4e0a\u4e58\u7684\u201c\u4e09\u8c61\u5f52\u5143\u201d\u7684\u5185\u529f\uff0c\u4e0e\u6c5f\u6d77\u5929\u76f8\u6297\uff0c\u9f99 \u9690\u5927\u5e08\u7684\u5916\u5bb6\u529f\u529b\u867d\u662f\u4e0d\u51e1\uff0c\u5374\u600e\u53ca\u5f97\u4e0a\u6587\u5ef7\u58c1\u8fd9\u5965\u5999\u795e\u5947\u7684\u5185\u529f\uff0c\u4ed6\u62ac\u8d77\u7985\u6756\u4e00\u770b\uff0c\u53ea\u89c1 \u90a3\u7897\u822c\u7c97\u5927\u7684\u7985\u6756\uff0c\u4e5f\u5df2\u4e24\u7aef\u7fd8\u8d77\uff0c\u4e2d\u95f4\u5f2f\u66f2\u4e86\uff01 \u3000\u3000\u9f99\u9690\u5927\u5e08\u5012\u62bd\u4e86\u4e00\u53e3\u51b7\u6c14\uff0c\u8fd9\u624d\u77e5\u9053\u4ee5\u81ea\u5df1\u8fd9\u70b9\u80fd\u4e3a\uff0c\u7edd\u5bf9\u4e0d\u662f\u4eba\u5bb6\u7684\u5bf9\u624b\uff01\u6012\u706b\u867d\u8fd8 \u672a\u7184\uff0c\u5374\u5df2\u505a\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u6b27\u9633\u4f2f\u548c\u9053\uff1a\u201c\u6587\u5148\u751f\u8bef\u4f24\u4ee4\u5f92\uff0c\u5b9e\u975e\u6709\u610f\uff0c\u8bf7\u5927\u5e08\u5927\u5ea6\u5305\u6db5\uff0c\u4e8b\u60c5\u8fc7\u540e\uff0c\u8001\u592b\u4e0e\u6587\u5148 \u751f\u5f53\u518d\u6765\u5411\u5927\u5e08\u8c22\u7f6a\u3002\u201d\u53c8\u5524\u4e86\u4e24\u4e2a\u95e8\u4eba\u8fc7\u6765\uff0c\u5429\u5490\u4ed6\u4eec\u9053\uff1a\u201c\u4f60\u4eec\u966a\u5927\u5e08\u8fdb\u540e\u5385\u6b47\u606f\uff1b\u7528 \u4e0a\u597d\u7684\u91d1\u521b\u836f\u66ff\u5c0f\u5e08\u7236\u6cbb\u4f24\u3002\u201d\u9f99\u9690\u5927\u5e08\u5927\u8896\u4e00\u6d51\uff0c\u53eb\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff0c\u8fd9\u90fd\u602a\u6211\u4eec\u5e08\u5f92 \u4fe9\u672c\u4e8b\u4e0d\u6d4e\uff1b\u4ee5\u81f4\u51fa\u4e56\u9732\u4e11\uff01\u91d1\u521b\u836f\u6211\u8fd8\u5907\u6709\uff0c\u4e0d\u52b3\u8d50\u60e0\u4e86\u3002\u6e5b\u7a7a\uff0c\u54b1\u4eec\u56de\u5c71\u53bb\u5427\uff01\u201d\u6012\u6c14 \u51b2\u51b2\uff0c\u643a\u4e86\u5f92\u5f1f\uff0c\u4fbf\u8d70\u51fa\u95e8\u3002 \u3000\u3000\u6b27\u9633\u4f2f\u548c\u6b63\u8981\u529d\u963b\uff0c\u5ffd\u542c\u5f97\u6587\u5ef7\u58c1\u4e00\u58f0\u5927\u53eb\uff0c\u51e0\u4e4e\u4e0e\u6b64\u540c\u65f6\uff0c\u4e00\u80a1\u7099\u70ed\u65f6\u52b2\u98ce\u3001\u4e5f\u7a81\u7136 \u5411\u4ed6\u88ad\u6765\uff01 \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u56e0\u4e3a\u5185\u529b\u5e73\u6dfb\u4e00\u500d\u4e4b\u540e\uff0c\u901a\u8def\u7a81\u7136\u88ab\u963b\uff0c\u8fd9\u60c5\u5f62\u5c31\u50cf\u6d2a\u6c34\u6da8\u4e86\uff0c\u52bf\u5fc5\u8981\u7a81\u56f4 \u800c\u51fa\u4e00\u822c\u3002\u6c5f\u6d77\u5929\u597d\u4e0d\u5bb9\u6613\u4ee5\u6b63\u5b97\u5185\u529f\u4e2d\u201c\u5bfc\u6c14\u8fd0\u884c\u201d\u4e4b\u6cd5\uff0c\u5c06\u5185\u5bb6\u771f\u6c14\u4e5f\u5bfc\u5230\u4e86\u6307\u5c16\uff0c\u4e00 \u65f6\u6536\u675f\u4e0d\u4f4f\uff0c\u53ea\u597d\u4efb\u51ed\u5b83\u4ece\u4e2d\u6307\u7aef\u7684\u201c\u4e2d\u9675\u7a74\u201d\u5c04\u51fa\u3002 \u3000\u3000\u8bf4\u5230\u6587\u5ef7\u58c1\u3002\u6587\u5ef7\u58c1\u56e0\u4e3a\u672a\u660e\u5bf9\u65b9\u865a\u5b9e\uff0c\u4e00\u76f4\u5728\u84c4\u6c14\u8fd0\u52b2\uff0c\u4e0d\u6562\u9996\u5148\u53d1\u96be\u3002\u4ed6\u6328\u4e86\u9f99\u9690 \u5927\u5e08\u4e00\u4ed7\uff0c\u867d\u672a\u53d7\u4f24\uff0c\u4f46\u5728\u8fd0\u6c14\u53cd\u9707\u4e4b\u65f6\uff0c\u5143\u6c14\u4e5f\u81ea\u4e0d\u65e0\u635f\u8017\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u6c5f\u6d77\u5929\u90a3\u4e00\u80a1\u65e0 \u5f62\u7f61\u6c14\uff0c\u7a81\u7136\u5411\u4ed6\u5c04\u6765\uff0c\u5176\u5229\u5982\u5200\uff0c\u5176\u70ed\u5982\u706b\uff0c\u6587\u5ef7\u58c1\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u95ea\u907f\uff0c\u53ea\u542c\u5f97 \u201c\u54e7\u201d\u4e4b\u58f0\u54cd\uff0c\u4ed6\u7684\u957f\u888d\u5df2\u7a7f\u4e86\u51e0\u4e2a\u5c0f\u6d1e\uff0c\u4ed6\u7684\u5185\u529b\u5df2\u5230\u4e86\u201c\u4e09\u8c61\u5f52\u5143\u201d\u7684\u5883\u754c\uff0c\u6c5f\u6d77\u5929\u53c8 \u672a\u80fd\u968f\u5fc3\u6240\u6b32\uff0c\u9a7e\u9a6d\u8fd9\u80a1\u7a81\u7136\u5c04\u51fa\u6765\u7684\u7f61\u6c14\uff0c\u7528\u4ee5\u4f24\u4eba\uff0c\u6545\u6b64\u6587\u5ef7\u74a7\u5e78\u672a\u53d7\u4f24\uff0c\u4e0d\u8fc7\u808c\u80a4\u88ab \u8fd9\u80a1\u7f61\u6c14\u70eb\u8fc7\uff0c\u4e5f\u4f3c\u53d7\u8fc7\u4e86\u706b\u70d9\u4e00\u822c\uff0c\u4e0d\u7531\u5f97\u5931\u58f0\u547c\u53eb\uff01 \u3000\u3000\u6b27\u9633\u4f2f\u548c\u4fa7\u8eab\u4e00\u95ea\uff0c\u968f\u7740\u4e00\u638c\u6263\u51fa\uff0c\u4ed6\u4e0e\u6c5f\u6d77\u5929\u7684\u8ddd\u79bb\u6bd4\u6587\u5ef7\u58c1\u8fdc\u5f97\u591a\uff0c\u8fd9\u80a1\u65e0\u4eba\u9a7e\u9a6d \u7684\u7f61\u6c14\u5c04\u4e86\u5230\u6765\uff0c\u5a01\u529b\u5df2\u51cf\uff0c\u4f46\u6b27\u9633\u4f2f\u548c\u5e94\u4ed8\u5f97\u5b9c\uff0c\u4ed6\u7684\u95e8\u4e0b\u5f1f\u5b50\u5374\u62b5\u6321\u4e0d\u4f4f\uff0c\u6709\u4e2a\u95e8\u4eba\uff0c \u6b63\u5de7\u7ed9\u90a3\u80a1\u7f61\u6c14\u78b0\u4e0a\uff0c\u767b\u65f6\u5927\u53eb\u4e00\u58f0\uff0c\u5c31\u50cf\u88ab\u5229\u5203\u7a81\u7136\u5272\u4e86\u51e0\u5200\uff0c\u597d\u51e0\u5904\u76ae\u7834\u8840\u6d41\uff0c\u8863\u88f3\u90fd \u67d3\u7ea2\u4e86\u3002 \u3000\u3000\u6b27\u9633\u4f2f\u548c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u8d77\u521d\u4ee5\u4e3a\u7531\u6587\u5ef7\u74a7\u51fa\u4e8e\u6765\u5bf9\u4ed8\u8fd9\u4e2a\u5c0f\u5b50\uff0c\u8fd8\u4e0d\u662f\u624b\u5230\u64d2 \u6765\uff0c\u6e10\u6e10\u53d1\u89c9\u4e0d\u5bf9\uff0c\u5982\u4eca\u4ed6\u5f1f\u5b50\u53d7\u4f24\uff0c\u53c8\u542c\u5f97\u6587\u5ef7\u58c1\u7684\u53eb\u558a\uff0c\u53ea\u4ee5\u4e3a\u8fde\u6587\u5ef7\u58c1\u4e5f\u53d7\u4e86\u4f24\uff0c\u5f53 \u7136\u66f4\u4e3a\u9707\u9a87\u3002\u672c\u6765\u6309\u7167\u4ed6\u548c\u6587\u5ef7\u74a7\u7684\u8eab\u4efd\uff0c\u7edd\u65e0\u4e24\u4eba\u8054\u624b\uff0c\u53bb\u6b3a\u8d1f\u4e00\u4e2a\u5c0f\u8f88\u7684\u9053\u7406\uff0c\u4f46\u5230\u4e86 \u6b64\u65f6\uff0c\u4ed6\u5df2\u662f\u65e0\u6687\u518d\u987e\u8eab\u4efd\uff0c\u5f53\u4e0b\u5927\u559d\u4e00\u58f0\uff0c\u7acb\u5373\u65bd\u5c55\u9739\u96f3\u638c\u4e0e\u96f7\u795e\u6307\u7684\u529f\u592b\uff0c\u62a2\u8fdb\u4e86\u6587\u3001 \u6c5f\u4e8c\u4eba\u7684\u4e2d\u95f4\uff0c\u5411\u6c5f\u6d77\u5929\u4fbf\u5c55\u5f00\u4e86\u653b\u51fb\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u4ee5\u524d\u66fe\u5728\u6b27\u9633\u4ef2\u548c\u624b\u5e95\u4e0b\u5403\u8fc7\u4e8f\uff0c\u8bc6\u5f97\u8fd9\u4e24\u79cd\u529f\u592b\u7684\u9738\u9053\uff1a\u5fc3\u60f3\u8fd9\u8001\u5934\u5b50\u662f\u6b27\u9633 \u4ef2\u548c\u7684\u54e5\u54e5\uff0c\u60f3\u5fc5\u66f4\u4e3a\u5389\u5bb3\uff0c\u5f53\u4e0b\u4e0d\u6562\u6020\u6162\uff0c\u53cd\u624b\u4e00\u70b9\uff0c\u4e5f\u65bd\u5c55\u51fa\u4e86\u6df1\u5965\u7684\u4f5b\u95e8\u6b66\u5b66\u2014\u2014\u4e00 \u6307\u7985\u529f\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u561b\u556a\u201d\u4e00\u58f0\uff0c\u4e24\u80a1\u6c14\u6d41\u5728\u7a7a\u4e2d\u51b2\u51fb\uff0c\u5c31\u4f3c\u4e00\u4ef6\u7269\u4f53\u7206\u70b8\u5f00\u6765\u4f3c\u7684\uff0c\u4f46\u5374\u53ea\u662f\u542c \u4e4b\u6709\u58f0\uff0c\u89c6\u4e4b\u65e0\u5f62\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u6b27\u9633\u6015\u548c\u53ea\u89c9\u80f8\u53e3\u4e00\u7d27\uff0c\u4f3c\u88ab\u7a81\u7136\u52a0\u4e86\u4e00\u9053\u94c1\u7b8d\uff0c\u4e0d\u7531\u5f97\u5927 \u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u9000\u540e\u4e00\u6b65\uff0c\u6697\u8fd0\u7384\u529f\uff0c\u6d88\u9664\u8eab\u4e0a\u6240\u53d7\u7684\u538b\u529b\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u6c5f\u6d77\u5929\u4e5f\u662f\u5fc3\u5934\u4e00\u51db\uff0c\u6697\u53eb\u201c\u4e0d\u5999\u201d\uff0c\u539f\u6765\u4ed6\u7684\u5185\u529b\u5df1\u6cc4\u53bb\u4e86\u4e00\u5c0f\u534a\uff0c\u7a81\u7136 \u8d77\u4e86\u4e00\u9635\u8f7b\u5fae\u7684\u6655\u7729\u4e4b\u611f\uff0c\u8fd9\u5728\u5e38\u4eba\uff0c\u4e5f\u8bb8\u4e0d\u4f1a\u4e88\u4ee5\u91cd\u89c6\uff0c\u4f46\u6c5f\u6d77\u5929\u81ea\u5df1\u660e\u767d\uff0c\u8fd9\u4e43\u662f\u5143\u6c14 \u5927\u4f24\u7684\u5f81\u8c61\uff01 \u3000\u3000\u4f46\u4e5f\u5e78\u800c\u4ed6\u7684\u5185\u529b\u6cc4\u53bb\u4e86\u4e00\u5c0f\u534a\uff0c\u8fd9\u65f6\uff0c\u7531\u4e8e\u4ed6\u7528\u201c\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\u201d\u6240\u589e\u5f3a\u7684\u5185\u529b\uff0c\u867d \u7136\u4ecd\u6bd4\u4ed6\u539f\u6709\u7684\u5185\u4e3a\u4e3a\u5f3a\uff0c\u5374\u5df2\u53ef\u4ee5\u52c9\u5f3a\u9a7e\u9a6d\u5f97\u4f4f\uff0c\u5f53\u4e0b\u7acb\u5373\u4ee5\u201c\u5bfc\u6c14\u5f52\u5143\u201d\u4e0a\u4e58\u5fc3\u6cd5\uff0c\u628a \u771f\u6c14\u6536\u655b\uff0c\u540c\u65f6\uff0c\u518d\u5411\u6b27\u9633\u4f2f\u548c\u8fd8\u4e86\u4e00\u638c\uff0c\u8fd9\u4e00\u638c\u662f\u9632\u5907\u4ed6\u4e58\u865a\u653b\u51fb\u7684\u3002\u4f46\u7531\u4e8e\u5185\u529b\u5df2\u7ecf\u524a \u5f31\uff0c\u4e24\u4eba\u5bf9\u4e86\u8fd9\u4e00\u8bb0\u5288\u7a7a\u638c\uff0c\u6b27\u9633\u4f2f\u548c\u867d\u7136\u4ecd\u8981\u7565\u5904\u4e0b\u98ce\uff0c\u8e6c\u8e6c\u8e6c\u7684\u5411\u540e\u518d\u9000\u4e86\u4e09\u6b65\uff0c\u800c\u6c5f \u6d77\u5929\u4e5f\u7981\u4e0d\u4f4f\u6643\u4e86\u4e24\u6643\uff01 \u3000\u3000\u6587\u5ef7\u58c1\u6df1\u901a\u6b66\u5b66\uff0c\u773c\u529b\u4f55\u7b49\u9ad8\u660e\uff0c\u5230\u4e86\u8fd9\u65f6\uff0c\u6c5f\u6d77\u5929\u4e0d\u80fd\u968f\u5fc3\u6240\u6b32\u8fd0\u7528\u672c\u8eab\u7684\u771f\u6c14\u4f24 \u4eba\uff0c\u4ee5\u53ca\u5185\u529b\u6e10\u6e10\u51cf\u5f31\uff0c\u8fd9\u4e24\u79cd\u8ff9\u8c61\u4ed6\u65e9\u5df2\u770b\u4e86\u51fa\u6765\uff01 \u3000\u3000\u6587\u5ef7\u58c1\u7cbe\u795e\u4e00\u632f\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6b27\u9633\u4eb2\u7fc1\uff0c\u8fd9\u5c0f\u5b50\u5df2\u662f\u5916\u5f3a\u4e2d\u5e72\uff0c\u4e0d\u8db3\u4e3a\u8651\u4e86\u3002\u4f60\u770b\u51fa \u4e86\u4e48\uff0c\u4ed6\u7528\u7684\u4e43\u662f\u65c1\u95e8\u5de6\u9053\u4e4b\u672f\uff0c\u5176\u5b9e\uff0c\u672c\u8eab\u7684\u529f\u529b\uff0c\u8fdc\u8fdc\u4e0d\u5982\u54b1\u4eec\uff01\u201d \u3000\u3000\u6b27\u9633\u4f2f\u548c\u534a\u4fe1\u534a\u7591\uff0c\u4f46\u4ed6\u4e0d\u60f3\u5728\u6587\u5ef7\u58c1\u8ddf\u524d\u8f93\u4e86\u9762\u5b50\uff0c\u4e5f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u600e\u4f1a\u653e\u5728 \u6211\u7684\u773c\u5185\u3002\u6211\u521a\u624d\u4e0d\u8fc7\u7a0d\u8bd5\u4ed6\u7684\u672c\u9886\u7f62\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6012\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5c31\u8bd5\u5427\uff01\u201d\u84e6\u7136\u4f7f\u51fa\u5929\u7f57\u6b65\u6cd5\uff0c\u6b3a\u5230\u4e86\u6b27\u9633\u4f2f\u548c\u8eab\u524d\uff0c\u5288\u80f8\u4fbf\u662f \u4e00\u638c\u3002\u6c5f\u6d77\u5929\u77e5\u9053\u5728\u4e24\u4eba\u4e4b\u4e2d\uff0c\u6b27\u9633\u4f2f\u548c\u8f83\u5f31\uff0c\u4ed6\u662f\u60f3\u5728\u201c\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\u201d\u7684\u529f\u6548\u672a\u66fe\u6d88\u5931 \u4e4b\u524d\u3002\u5148\u628a\u8fd9\u8f83\u5f31\u7684\u4e00\u73af\u7a81\u7834\uff01 \u3000\u3000\u54ea\u77e5\u6587\u5ef7\u58c1\u65e9\u5df2\u7aa5\u4f3a\u5728\u65c1\uff0c\u4ed6\u6709\u610f\u901e\u80fd\uff0c\u6c5f\u6d77\u5929\u8eab\u5f62\u4e00\u8d77\uff0c\u4ed6\u4e5f\u7acb\u5373\u6251\u4e86\u8fc7\u6765\uff0c\u66ff\u6b27\u9633 \u4f2f\u548c\u6321\u4e86\u4e00\u638c\u3002\u8fd9\u4e00\u638c\uff0c\u6587\u5ef7\u58c1\u867d\u7136\u4ecd\u672a\u80fd\u628a\u6c5f\u6d77\u5929\u51fb\u5012\uff0c\u4f46\u5374\u5df2\u662f\u534a\u65a4\u516b\u4e24\uff0c\u4e24\u4e0d\u8f93\u4e8f\u3002 \u3000\u3000\u6b27\u9633\u4f2f\u548c\u770b\u51fa\u4e86\u4fbf\u5b9c\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u8ddf\u7740\u62a2\u8fc7\u53bb\u53d1\u4e86\u4e00\u8bb0\u9739\u96f3\u638c\u3002\u6c5f\u6d77\u5929\u5728\u4e0e\u6587\u5ef7\u58c1\u786c\u62fc \u4e86\u4e00\u638c\u4e4b\u540e\uff0c\u5185\u529b\u53c8\u6d88\u8017\u4e86\u4e00\u4e9b\uff0c\u7ed3\u679c\u5bf9\u4ed8\u8fd9\u4e2a\u8f6f\u5f31\u7684\u6b27\u9633\u4f2f\u548c\uff0c\u53cd\u800c\u7ed9\u4ed6\u8feb\u9000\u4e86\u4e00\u6b65\uff0c\u6b27 \u9633\u4f2f\u548c\u5f97\u610f\u6d0b\u6d0b\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u77e5\u9053\u5389\u5bb3\u4e86\u4e48\uff1f\u201d\u7d27\u63a5\u7740\u53d1\u51fa\u4e86\u96f7\u795e\u6307\uff0c\u70b9\u6c5f\u6d77\u5929 \u7684\u8109\u95e8\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4eba\u58f0\u5982\u6cb8\uff1a\u201c\u6349\u8d3c\uff0c\u6349\u8d3c\uff01\u201d\u201c\u6551\u706b\uff0c\u6551\u706b\uff01\u201d\u6b27\u9633\u4f2f\u548c\u53c8\u60ca\u53c8\u6012\uff0c\u5fc3\u60f3\uff1a\u201c\u4ec0 \u4e48\u4eba\u8fd9\u6837\u5927\u80c6\uff0c\u6562\u5230\u6211\u5bb6\u6765\u5077\u76d7\uff0c\u653e\u706b\uff1f\u201d\u4ed6\u7ed9\u8fd9\u7a81\u5982\u5176\u6765\u7684\u4e8b\u53d8\u5206\u4e86\u5fc3\u795e\uff0c\u96f7\u795e\u6307\u5931\u4e86\u51c6 \u5934\uff0c\u7ed9\u6c5f\u6d77\u5929\u907f\u5f00\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u6b27\u9633\u4e8c\u5a18\u4e00\u53e0\u58f0\u7684\u5927\u547c\u5c0f\u53eb\u9053\uff1a\u201c\u5feb\u622a\u7740\u4ed6\uff0c\u622a\u7740\u4ed6\uff01\u201d\u201c\u59ec\u8001\u8d3c\uff0c \u4f60\u597d\u5927\u7684\u80c6\u5b50\uff01\u201d \u3000\u3000\u4ece\u540e\u5802\u91cc\u65cb\u98ce\u4f3c\u7684\u51b2\u51fa\u4e86\u4e24\u4e2a\u4eba\uff0c\u524d\u9762\u8fd9\u4eba\u8eab\u6cd5\u592a\u5feb\uff0c\u8fde\u9762\u8c8c\u4e5f\u770b\u4e0d\u6e05\u695a\uff0c\u53ea\u9690\u7ea6\u53ef\u4ee5 \u5206\u8fa8\u5f97\u51fa\u662f\u4e2a\u7537\u5b50\uff1b\u540e\u9762\u8fd9\u4eba\u5219\u662f\u6b27\u9633\u4e8c\u5a18\uff0c\u5979\u5df2\u7ecf\u6362\u8fc7\u4e86\u4e00\u5957\u8863\u88f3\uff0c\u60f3\u662f\u592a\u8fc7\u5306\u5fd9\uff0c\u6709\u51e0 \u9897\u94ae\u6263\u8fd8\u672a\u66fe\u6263\u4e0a\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u53eb\u9053\uff1a\u201c\u4ef2\u548c\uff0c\u4ed6\u5077\u3001\u5077\u2026\u2026\u201d\u8bdd\u72b9\u6765\u4e86\uff0c\u53ea\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u4e2a\u6c49\u5b50\u5df2 \u4ece\u6b27\u9633\u4ef2\u548c\u7684\u8eab\u8fb9\u63a0\u8fc7\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u6012\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u59ec\u6653\u98ce\uff0c\u4f60\u6562\u8d81\u6211\u53d7\u4f24\uff0c\u524d\u6765\u6b3a\u6211\uff1f\u201d\u4e3e\u8d77\u62d0\u6756\u4fbf\u6253\uff0c\u4ed6 \u867d\u5c1a\u672a\u590d\u539f\uff0c\u8fd9\u4e00\u6756\u6253\u4e0b\uff0c\u5374\u4e5f\u52b2\u98ce\u547c\u547c\uff0c\u521a\u731b\u4e4b\u6781\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u6839\u672c\u4e0d\u77e5\u9053\u4ed6\u662f\u53d7\u4f24\u672a\u6108\uff0c\u4e5f\u5e76\u975e\u6709\u610f\u6765\u88ad\u51fb\u4ed6\uff0c\u4ed6\u500f\u5730\u5c31\u4ece\u6756\u5e95\u4e0b\u94bb\u8fc7\uff0c\u5ffd\u7136 \u53c8\u8eab\u5b50\u5411\u540e\u5012\u9000\uff0c\u628a\u624b\u4e00\u626c\uff1b\u559d\u9053\uff1a\u201c\u8d3c\u5a46\u5a18\uff0c\u63a5\u6697\u5668\uff01\u201d\u6b27\u9633\u4e8c\u5a18\u7684\u6b66\u529f\u6bd4\u59ec\u6653\u98ce\u8981\u7a0d\u80dc \u4e00\u7b79\u3002\u4f46\u8eab\u6cd5\u5374\u8fdc\u8fdc\u4e0d\u53ca\u4ed6\u7684\u7075\u654f\u5feb\u6377\uff0c\u59ec\u6653\u51e4\u7a81\u7136\u5012\u9000\uff0c\u5c31\u4f3c\u628a\u4e00\u4ef6\u4e1c\u897f\u9001\u5230\u5979\u7684\u9762\u524d\u4f3c \u7684\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u52b2\u8d2f\u53cc\u638c\uff0c\u4f7f\u52b2\u4e00\u62cd\uff0c\u53ea\u89c9\u638c\u4e0a\u6cb9\u817b\u817b\u7684\uff0c\u539f\u6765\u4e0d\u662f\u4ec0\u4e48\u6697\u5668\uff0c\u800c\u662f\u534a\u8fb9\u6cb9 \u9e21\u3002\u9e21\u6c41\u6e85\u5f97\u5979\u6ee1\u9762\u90fd\u662f\uff0c\u6709\u597d\u4e9b\u8089\u5c51\u9aa8\u788e\uff0c\u5f84\u6cbe\u5230\u4e86\u5979\u7684\u5507\u8fb9\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u60f3\u8d77\u59ec\u6653\u98ce\u90a3\u526f\u80ae\u810f\u6a21\u6837\uff0c\u51e0\u4e4e\u8981\u5455\u51fa\u6765\uff0c\u59ec\u6653\u98ce\u54c8\u54c8\u5927\u7b11\uff0c\u65e9\u5df2\u8dd1\u5f00\u3002\u53ea\u89c1 \u4ed6\u6495\u4e0b\u4e00\u6761\u9e21\u817f\uff0c\u9001\u5230\u53e3\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u6211\u59d1\u4e14\u5145\u5f53\u4f60\u4eec\u6b27\u9633\u5bb6\u7684\u7956\u5b97\uff0c\u54c8\u54c8\uff0c\u8fd9\u53ea\u9e21\u7684\u5473\u9053 \u8fd8\u5f53\u771f\u4e0d\u9519\u5462\u3002\u201d\u539f\u6765\u4ed6\u521a\u624d\u63a0\u8fc7\u6b27\u9633\u4ef2\u548c\u7684\u8eab\u540e\uff0c\u4e43\u662f\u53bb\u652b\u53d6\u4f9b\u684c\u4e0a\u7684\u80a5\u9e21\u7684\uff0c\u56e0\u4e3a\u4ed6\u8fd8 \u672a\u66fe\u5403\u8fc7\u4e2d\u996d\uff0c\u6b63\u997f\u5f97\u53d1\u614c\uff0c \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u6b27\u9633\u4f2f\u548c\uff0c\u4f60\u6562\u4e0d\u6562\u5206\u4eab\u4f60\u7956\u5b97\u7684\u4e1c\u897f\uff1f\u5206\u4e00\u6761\u9e21\u817f\u7ed9\u4f60\uff01\u201d \u3000\u3000\u6b27\u9633\u4f2f\u548c\u7109\u80fd\u7ed9\u4ed6\u6253\u4e2d\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u628a\u90a3\u6761\u9e21\u817f\u5f39\u5f00\uff0c\u53ef\u662f\u5982\u6b64\u4e00\u6765\uff0c\u4ed6\u7b2c\u4e09\u6b21\u5411\u6c5f\u6d77 \u5929\u653b\u51fa\u7684\u4e00\u6307\u53c8\u843d\u7a7a\u4e86\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u559d\u9053\uff1a\u201c\u597d\u4e2a\u59ec\u6653\u98ce\uff0c\u5f53\u521d\u4fa5\u5e78\u7ed9\u4f60\u4ece\u7956\u5f95\u5c71\u9003\u8131\u4e86\u6027\u547d\uff0c\u4eca\u65e5\u5374\u53c8\u6765\u9001\u6b7b \u4e48\uff1f\u201d\u56de\u8eab\u4e00\u6307\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u9635\u52b2\u98ce\u5c04\u51fa\uff0c\u4f7f\u51fa\u4e86\u9694\u7a7a\u70b9\u7a74\u7684\u7edd\u4e16\u795e\u529f\u3002 \u3000\u3000\u59ec\u6653\u98ce\u8bc6\u5f97\u5389\u5bb3\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u95ea\u8fc7\u4e00\u8fb9\uff0c\u9976\u4ed6\u8eab\u6cd5\u5feb\u6377\uff0c\u5c0f\u817f\u4e5f\u7a81\u7136\u611f\u5230\u4e00\u9635\u9178\u9ebb\uff0c\u5e78 \u800c\u672a\u70b9\u6b63\u7a74\u9053\u3002 \u3000\u3000\u59ec\u6653\u98ce\u867d\u7136\u6b66\u529f\u8f83\u5f31\uff0c\u4f46\u4ed6\u4ee5\u95ea\u7535\u822c\u7684\u8eab\u6cd5\uff0c\u5728\u77ac\u606f\u4e4b\u95f4\uff0c\u904d\u88ad\u4e24\u5927\u9ad8\u624b\uff0c\u5374\u7ed9\u4e86\u6c5f\u6d77 \u5929\u4e00\u4e2a\u5598\u606f\u7684\u673a\u4f1a\uff0c\u8fd9\u65f6\u4ed6\u65b9\u59cb\u817e\u5f97\u51fa\u624b\u6765\uff0c\u4e00\u58f0\u559d\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u6562\u6b3a\u8d1f\u6211\u7684\u59ec\u4f2f \u4f2f\uff0c\u770b\u5251\uff01\u5b9d\u5251\u500f\u5730\u51fa\u9798\uff0c\u5316\u6210\u4e86\u4e00\u9053\u957f\u8679\uff0c\u4fbf\u5411\u6587\u5ef7\u74a7\u62e6\u8170\u65a9\u53bb\uff01 \u3000\u3000\u88c1\u4e91\u5b9d\u5251\uff0c\u5a01\u529b\u4e4b\u5f3a\uff0c\u5929\u4e0b\u65e0\u654c\uff0c\u9976\u662f\u6587\u5ef7\u58c1\u6280\u9ad8\u80c6\u5927\uff0c\u4e5f\u4e0d\u7981\u5fc3\u4e2d\u4e00\u51db\uff0c\u6025\u5fd9\u4e00\u4e2a\u76d8 \u9f99\u7ed5\u6b65\uff0c\u7528\u8db3\u4e86\u5341\u6210\u529f\u529b\uff0c\u53cd\u638c\u4e00\u62ac\uff0c\u9769\u529b\u9707\u8361\uff0c\u5251\u5149\u6d41\u6563\uff0c\u4fe8\u5982\u9ed1\u591c\u7e41\u661f\uff0c\u5343\u70b9\u4e07\u70b9\uff0c\u9152 \u843d\u4e0b\u6765\uff0c\u6b27\u9633\u4f2f\u548c\u529f\u529b\u7a0d\u900a\uff0c\u4ed6\u867d\u7136\u4e5f\u540c\u65f6\u4f7f\u51fa\u4e86\u9739\u96f3\u638c\u7684\u529f\u592b\uff0c\u4f46\u53ea\u80fd\u628a\u5b9d\u5251\u7684\u843d\u70b9\u9707 \u6b6a\uff0c\u5251\u5149\u8fc7\u5904\uff0c\u5374\u628a\u4ed6\u7684\u5934\u53d1\u524a\u53bb\u4e86\u4e00\u7f15\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u5c55\u5f00\u4e86\u5927\u987b\u5f25\u5251\u6cd5\uff0c\u65b9\u5706\u4e00\u4e08\u4e4b\u5185\uff0c\u5251\u5149\u71ce\u7ed5\uff0c\u6cfc\u6c34\u4e0d\u8fdb\uff0c\u5c06\u59ec\u6653\u98ce\u62a4\u5728\u5149\u5e62\u4e4b \u5185\u3002\u59ec\u665a\u98ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u591a\u627f\u76f8\u62a4\uff0c\u6211\u9001\u4f60\u597d\u4e1c\u897f\u5403\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u60f3\uff1a\u201c\u59ec\u4f2f\u4f2f\u6b22\u559c\u5f00\u73a9\u7b11\u7684\u813e\u6c14\uff0c\u81f3\u8001\u4e0d\u6539\uff0c\u8fd9\u4e2a\u65f6\u5019\uff0c\u8c01\u8fd8\u6709\u5fc3\u60c5\u5403\u4e1c \u897f\uff1f\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u997f\uff0c\u4f60\u81ea\u5df1\u5403\u5427\uff01\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u4f60\u5c82\u4e0d\u540c\uff1a\u2018\u957f\u8005\u8d50\uff0c\u5c11 \u8005\u4e0d\u6562\u8f9e\u2019\u4e48\uff1f\u6211\u53eb\u4f60\u5403\uff0c\u4f60\u5c31\u975e\u5403\u4e0d\u53ef\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u4ed6\u5df2\u628a\u4e00\u6837\u4e1c\u897f\uff0c\u9001\u5230\u4e86\u6c5f\u6d77\u5929\u7684 \u53e3\u8fb9\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u53ea\u9053\u662f\u662f\u9e21\u817f\uff0c\u5374\u4e0d\u6599\u5ffd\u95fb\u5f97\u4e00\u80a1\u6e05\u9999\uff0c\u4ed6\u5f3a\u7528\u201c\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\u201d\uff0c\u63a5\u4e86\u6587\u5ef7\u58c1 \u6570\u638c\uff0c\u6b63\u81ea\u611f\u5230\u80f8\u53e3\u80c0\u95f7\uff0c\u55c5\u5230\u4e86\u8fd9\u80a1\u6e05\u9999\uff0c\u767b\u65f6\u7cbe\u795e\u6296\u64de\uff0c\u80c0\u95f7\u4e4b\u611f\u987f\u6d88\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u4ed6\u5077\u5403\u4e86\u54b1\u4eec\u7684\u5343\u5e74\u7075\u829d\u4e86\uff01\u201d\u6b27\u9633\u4ef2\u548c\u5927\u543c\u9053\uff1a\u201c\u5c82\u6709\u6b64 \u7406\uff0c\u4f60\u522b\u7684\u4e0d\u5077\uff0c\u5355\u5355\u607c\u6211\u7684\u5343\u5e74\u7075\u829d\uff01\u201d\u4ed6\u884c\u52a8\u4e0d\u4fbf\uff0c\u4e00\u6012\u4e4b\u4e0b\uff0c\u5c06\u62d0\u6756\u63b7\u51fa\uff0c\u8fd9\u679d\u62d0 \u6756\uff0c\u662f\u7528\u6781\u575a\u786c\u7684\u6a61\u672c\u505a\u7684\uff0c\u4f46\u5374\u600e\u7981\u5f97\u8d77\u5b9d\u5251\u7684\u950b\u8292\uff0c\u53ea\u4e00\u524a\u66f4\u524a\u65ad\u4e86\u3002\u6b27\u9633\u4ef2\u548c\u8eab\u4f53\u5c1a \u672a\u590d\u539f\uff0c\u7528\u529b\u8fc7\u5ea6\uff0c\u5931\u4e86\u5e73\u8861\uff0c\u53cd\u800c\u201c\u5495\u549a\u201d\u7684\u4e00\u58f0\u8dcc\u5012\u4e86\u3002 \u3000\u3000\u59ec\u6653\u98ce\u5927\u7b11\u9053\uff1a\u201c\u6b27\u9633\u8001\u4e8c\uff0c\u539f\u6765\u4f60\u662f\u6709\u75c5\u5728\u8eab\uff0c\u8981\u8fd9\u652f\u7075\u829d\u6cbb\u75c5\u4e48\uff1f\u6055\u6211\u4e0d\u77e5\uff0c\u5c06\u5b83 \u5077\u4e86\u3002\u4e0d\u8fc7\uff0c\u6211\u770b\u4f60\u7684\u8138\u8272\u4e5f\u8fd8\u4e0d\u574f\uff0c\u4f60\u597d\u597d\u4fdd\u91cd\u5427\uff0c\u5343\u4e07\u522b\u8981\u518d\u751f\u6c14\uff0c\u7eb5\u65e0\u7075\u829d\uff0c\u6211\u4e5f\u6562 \u62c5\u4fdd\u4f60\u5728\u4eca\u5e74\u5185\u5730\u6b7b\u4e0d\u53bb\u7684\uff01\u54c8\uff0c\u4f60\u5e74\u7eaa\u4e5f\u4e0d\u5c0f\uff0c\u600e\u7684\u8fd9\u6837\u7cca\u6d82\uff0c\u98a0\u4e09\u5012\u56db\u7684\u95ee\u6211\u4e3a\u4ec0\u4e48\u5077 \u4f60\u7684\u7075\u829d\uff1f\u4f60\u4e0d\u60f3\u60f3\uff0c\u4f60\u5bb6\u91cc\u8fd8\u6709\u4ec0\u4e48\u4e1c\u897f\u503c\u5f97\u6211\u5999\u624b\u795e\u5077\u59ec\u6653\u98ce\u4e0b\u624b\uff1f\u201d \u3000\u3000\u8fd9\u652f\u7075\u829d\u662f\u6b27\u9633\u4ef2\u548c\u8d39\u5c3d\u5fc3\u529b\u91c7\u56de\u6765\u7684\uff0c\u51c6\u5907\u5728\u75c5\u540e\u670d\u7528\uff0c\u65e9\u4e9b\u6062\u590d\u5143\u6c14\uff0c\u5374\u4e0d\u6599\u88ab\u59ec \u6653\u98ce\u5077\u4e86\uff0c\u8fd8\u563b\u76ae\u7b11\u8138\u7684\u8c03\u4f83\u4ed6\uff0c\u5f53\u771f\u628a\u4ed6\u6c14\u5f97\u4e2a\u4e03\u7a8d\u751f\u70df\uff1a \u3000\u3000\u6b27\u9633\u5bb6\u7684\u95e8\u4eba\u5b50\u5f1f\u8fde\u5fd9\u5c06\u4ed6\u6276\u8d77\uff0c\u6b27\u9633\u4e8c\u5a18\u529d\u9053\uff1a\u201c\u4ef2\u548c\uff0c\u4f60\u4e0d\u7528\u5fc3\u7126\uff0c\u4f60\u6587\u4eb2\u5bb6\u5728 \u6b64\uff0c\u8c05\u8fd9\u4e24\u4e2a\u5c0f\u8d3c\u63d2\u7ffc\u96be\u9003\uff0c\u4f60\u8fdb\u53bb\uff01\u6b47\u6b47\u5427\u3002\u201d\u6b27\u9633\u4ef2\u548c\u773c\u5149\u4e00\u77a5\uff0c\u89c1\u6587\u5ef7\u58c1\u6b63\u5728\u4e1c\u8eb2\u897f \u95ea\uff0c\u9065\u9065\u53d1\u638c\uff0c\u770b\u6765\u4ed6\u5bf9\u6c5f\u6d77\u5929\u8fd9\u53e3\u5b9d\u5251\u76f8\u5f53\u5fcc\u60ee\uff0c\u56e0\u6b64\u53ea\u662f\u91c7\u7528\u201c\u4e0d\u6c42\u6709\u529f\uff0c\u4f46\u6c42\u65e0\u8fc7\u201d \u7684\u6253\u6cd5\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8fc1\u6012\u5230\u95e8\u4eba\u5b50\u5f1f\u8eab\u4e0a\uff0c\u6c14\u72fc\u72e0\u5730\u9a82\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u662f\u6b7b\u4eba\uff0c\u90fd\u662f \u778e\u4e86\u773c\u775b\u7684\uff0c\u8fd9\u4e48\u591a\u4eba\uff0c\u5374\u7ed9\u8d3c\u4eba\u6df7\u4e86\u8fdb\u6765\uff0c\u5077\u4e86\u4e1c\u897f\u8fd8\u4e0d\u77e5\u9053\uff01\u201d\u6b27\u9633\u4e8c\u5a18\u76b1\u8d77\u7709\u5934\uff0c\u529d \u9053\uff1a\u201c\u4ef2\u548c\uff0c\u4eca\u5929\u662f\u559c\u5e86\u65e5\u5b50\uff0c\u522b\u8bf4\u89e6\u9709\u5934\u7684\u8bf4\u8bdd\u3002\u201d\u6b27\u9633\u4ef2\u548c\u9a82\u9053\uff1a\u201c\u95f9\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u8fd8 \u6709\u4ec0\u4e48\u53ef\u559c\u53ef\u5e86\uff1f\u5973\u5a7f\u4f24\u4e86\uff0c\u5973\u513f\u8dd1\u4e86\uff0c\u8fde\u6211\u4fdd\u547d\u7684\u7075\u829d\u4e5f\u7ed9\u4eba\u5077\u4e86\uff0c\u54b1\u4eec\u6b27\u9633\u5bb6\u7684\u9762\u5b50\u90fd \u53bb\u5c3d\u4e86\uff0c\u54fc\uff0c\u54fc\uff0c\u6211\u770b\u4f60\u8fd9\u4e08\u6bcd\u5a18\u600e\u6837\u6536\u573a\uff1f\u201d \u3000\u3000\u672c\u6765\u662f\u559c\u6c14\u6d0b\u6d0b\u7684\u793c\u5802\uff0c\u7684\u786e\u5df2\u662f\u95f9\u5f97\u4e0d\u6210\u4e2a\u6a21\u4f6f\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c \u5802\u4e0a\u90a3\u4e24\u652f\u9ad8\u70e7\u7684\u7ea2\u70db\u5df2\u7ed9\u638c\u98ce\u706d\u6389\uff0c\u63a5\u7740\u201c\u4e53\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u9648\u5217\u7740\u793c\u7269\u7684\u201c\u559c\u684c\u201d\u4e5f\u7ed9\u6253 \u7ffb\uff0c\u6c5f\u6d77\u5929\u7684\u5b9d\u5251\u76d8\u65cb\u98de\u821e\uff0c\u5251\u5149\u56db\u5c04\uff0c\u56db\u58c1\u6302\u7740\u7684\u559c\u5e5b\u4e5f\u90fd\u7ed9\u5272\u5f97\u7834\u7834\u70c2\u70c2\u3002\u90a3\u6ee1\u5802\u5bbe \u5ba2\uff0c\u80c6\u5c0f\u7684\u65e9\u5df2\u9003\u56de\u5bb6\u53bb\uff0c\u80c6\u5927\u7684\u4e5f\u4e0d\u6562\u7559\u5728\u793c\u5802\u4e4b\u4e2d\uff0c\u5f53\u771f\u662f\u6709\u5982\u9c81\u667a\u6df1\u5927\u95f9\u4e94\u53f0\u5c71\u2014\u2014 \u95f9\u5f97\u4e2a\u201c\u5377\u5802\u5927\u6563\u201d\uff01\u6b27\u9633\u4e8c\u5a18\u6015\u4e08\u592b\u53d7\u5230\u8bef\u4f24\uff0c\u8fde\u5fd9\u53eb\u95e8\u4eba\u5f1f\u5b50\u6276\u4ed6\u8fdb\u53bb\uff0c\u6b27\u9633\u4ef2\u548c\u5140\u662f \u4e00\u8def\u9a82\u58f0\u4e0d\u7edd\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u5c06\u7075\u829d\u5206\u4e3a\u4e24\u6bb5\uff0c\u4ed6\u5403\u4e86\u534a\u652f\uff0c\u5c06\u53e6\u4e00\u534a\u4ea4\u8fd8\u7ed9\u59ec\u6653\u98ce\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u534a\u652f\u4f60\u7559\u4e0b \u5427\uff0c\u6211\u65e0\u9700\u8fd9\u8bb8\u591a\u3002\u201d\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u6ecb\u5473\u8fd8\u4e0d\u9519\u5427\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u7684\u786e\u4e0d\u9519\uff0c\u591a\u8c22\u59ec\u4f2f \u4f2f\u3002\u201d\u59ec\u6653\u98ce\u7b11\u9053\uff0c\u201c\u6211\u8fd9\u53ea\u80a5\u9e21\u7684\u5473\u9053\u4e5f\u5f88\u4e0d\u9519\uff0c\u54b1\u4eec\u90fd\u8be5\u591a\u8c22\u4e3b\u4eba\u3002\u201d\u90a3\u53ea\u80a5\u9e21\u5df2\u7ed9\u4ed6 \u5403\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u4ed6\u53cc\u638c\u4e00\u62b9\uff0c\u970d\u5730\u7528\u4e86\u5341\u62db\u201c\u7ae5\u5b50\u62dc\u89c2\u97f3\u201d\u7684\u62db\u5f0f\uff0c\u5c31\u5411\u6b27\u9633\u4f2f\u548c\u6253\u53bb\uff0c\u6b27 \u9633\u4f2f\u548c\u6599\u4e0d\u5230\u4ed6\u7a81\u7136\u8d70\u51fa\u5251\u5149\u5708\u5b50\uff0c\u5411\u81ea\u5df1\u653b\u6765\uff0c\u767e\u5fd9\u4e2d\u7528\u4e86\u4e00\u4e2a\u201c\u5927\u5f2f\u8170\uff0c\u659c\u63d2\u67f3\u201d\u7684\u89e3 \u6570\uff0c\u4e00\u9762\u95ea\u907f\uff0c\u4e00\u9762\u8fd8\u51fb\uff0c\u59ec\u6653\u98ce\u4e00\u51fb\u4e0d\u4e2d\uff0c\u500f\u5730\u53c8\u9000\u53e3\u6765\u3002\u7b11\u9053\uff1a\u201c\u591a\u8c22\u4e3b\u4eba\u5bb6\u7684\u597d\u4e1c \u897f\uff0c\u4f60\u4e0d\u5fc5\u8fd8\u793c\u4e86\u3002\u201d\u628a\u6b27\u9633\u4f2f\u548c\u6c14\u5f97\u534a\u6b7b\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u53c8\u662f\u5fc3\u75db\uff0c\u53c8\u662f\u6c14\u6012\uff0c\u5ffd\u89c9\u811a\u4e0b\u6709\u7269\u4ef6\u7eca\u4f4f\uff0c\u539f\u6765\u662f\u65b0\u90ce\u8eab\u4e0a\u6240\u62ab\u7684\u90a3\u4e24\u6761\u5f69 \u7ef8\uff0c\u65b0\u90ce\u53d7\u4e86\u4f24\uff0c\u5f69\u7ef8\u4e5f\u5f03\u4e8e\u5730\u4e0a\u4e86\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u62ac\u8d77\u4e86\u8fd9\u4e24\u6761\u5f69\u7ef8\uff0c\u53cc\u624b\u4e00\u6296\uff0c\u5de6\u624b\u7684\u5f69\u7ef8\u5377\u5411\u59ec\u6653\u98ce\uff0c\u53f3\u624b\u7684\u5f69\u7ef8\u5377\u5411\u6c5f\u6bcf \u5929\uff0c\u59ec\u6653\u98ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5582\uff0c\u4f60\u770b\u9519\u4eba\u5566\uff0c\u6211\u4e0d\u662f\u65b0\u90ce\uff0c\u4f60\u522b\u7ed9\u6211\u62ab\u7ea2\u6302\u5f69\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u624b\u821e\u8db3\u8e48\uff0c\u968f\u7740\u90a3\u6761\u7ea2\u7ef8\uff0c\u5ffd\u800c\u51cc\u7a7a\u659c\u63a0\uff0c\u5ffd\u800c\u8d34\u5730\u76d8\u65cb\uff0c\u65c1\u4eba\u770b\u6765\uff0c\u5c31\u4f3c\u4ed6\u662f \u6709\u610f\u4e0e\u6b27\u9633\u4e8c\u5a18\u914d\u5408\uff0c\u540c\u4f5c\u7ea2\u7ef8\u821e\u4e00\u822c\u3002\u6b27\u9633\u4e8c\u5a18\u63a5\u8fde\u7528\u4e86\u5341\u51e0\u79cd\u4e0d\u540c\u7684\u624b\u6cd5\uff0c\u90fd\u672a\u80fd\u5c06\u4ed6 \u5377\u4f4f\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6043\u7740\u6709\u5b9d\u5251\u5728\u624b\uff0c\u7ea2\u7ef8\u5377\u6765\uff0c\u4ed6\u6325\u5251\u4fbf\u524a\uff0c\u5374\u4e0d\u6599\u90a3\u7ea2\u7ef8\u8f7b\u98d8\u98d8\u7684\uff0c\u6beb\u4e0d\u53d7\u529b\uff0c \u84e6\u7136\u95f4\u7ed9\u5979\u5377\u4f4f\uff0c\u6b27\u9633\u4e8c\u5a18\u5389\u58f0\u559d\u9053\uff1a\u201c\u64a4\u624b\uff01\u201d\u4fbf\u8981\u628a\u6c5f\u6d77\u5929\u7684\u5b9d\u5251\u5377\u53bb\u3002 \u3000\u3000\u5e78\u800c\u6c5f\u6d77\u5929\u7684\u201c\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\u201d\uff0c\u529f\u6548\u5c1a\u672a\u5b8c\u5168\u6d88\u5931\uff0c\u5185\u529b\u4e0a\u8981\u80dc\u8fc7\u6b27\u9633\u4e8c\u5a18\uff0c\u6b27\u9633\u4e8c \u5a18\u4e00\u8fd0\u52b2\uff0c\u7ea2\u7ef8\u53d7\u529b\uff0c\u767b\u65f6\u7ed9\u5b9d\u5251\u524a\u53bb\u4e86\u4e00\u7247\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c31\u5728\u6c5f\u6d77\u5929\u5b9d\u5251\u88ab\u5377\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u6587\u5ef7\u58c1\u5df2\u662f\u4e00\u638c\u5288\u6765\uff0c\u6c5f\u6d77\u5929\u8feb\u5f97 \u518d\u4e0e\u4ed6\u786c\u62fc\u4e00\u8f88\u3002\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u6c5f\u6d77\u5929\u9000\u540e\u4e09\u6b65\uff0c\u6587\u5ef7\u58c1\u6b63\u5f85\u8ffd\u4e0a\u53bb\u518d\u8865\u4e00\u638c\uff0c\u6c5f\u6d77\u5929\u7684 \u5b9d\u5251\u5df2\u7ecf\u6446\u8131\u4e86\u7ea2\u7ef8\u7684\u7f20\u7ed5\uff0c\u4e00\u62db\u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\uff0c\u6321\u4f4f\u4e86\u6587\u5ef7\u58c1\u7684\u53bb\u8def\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u53ef\u60dc\u201d\uff0c\u4f46\u5979\u8bd5\u51fa\u4e86\u6c5f\u6d77\u5929\u7684\u529f\u529b\u4e4b\u540e\uff0c\u4e5f\u7acb\u5373\u66f4\u6362\u4e86\u5bf9\u7b56\uff0c \u4e0d\u6025\u4e8e\u5377\u8d70\u5b9d\u5251\uff0c\u5374\u4f7f\u51fa\u4e86\u4ee5\u67d4\u514b\u521a\u7684\u529f\u592b\u3002 \u3000\u3000\u539f\u6765\u6b27\u9633\u4e8c\u5a18\u7684\u529f\u529b\u867d\u7136\u8fdc\u8fdc\u4e0d\u53ca\u6587\u5ef7\u58c1\uff0c\u751a\u81f3\u6bd4\u4e0d\u4e0a\u6b27\u9633\u4f2f\u548c\uff0c\u4f46\u5979\u5374\u6700\u64c5\u957f\u4e8e\u201c\u5378 \u529b\u5316\u52b2\u201d\u7684\u529f\u592b\uff0c\u6c5f\u6d77\u5929\u7684\u5b9d\u5251\u4e00\u7ed9\u7ea2\u7ef8\u6cbe\u4e0a\uff0c\u5c31\u6bcf\u6bcf\u7ed9\u7ed9\u5979\u5e26\u8fc7\u4e00\u8fb9\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u5b9d\u5251\u7684 \u5a01\u529b\u5c31\u6253\u4e86\u6298\u6263\uff0c\u5927\u5927\u6709\u5229\u4e8e\u6587\u5ef7\u58c1\u4e0e\u6b27\u9633\u4f2f\u548c\u7684\u8fdb\u653b\u3002 \u3000\u3000\u4e0d\u6d88\u591a\u4e45\uff0c\u6c5f\u6d77\u5929\u7ed9\u8feb\u5230\u4e86\u4e00\u9685\uff0c\u60c5\u5f62\u53d1\u53d1\u53ef\u5371\uff0c\u6587\u5ef7\u58c1\u51b7\u7b11\u9053\uff0c\u201c\u597d\u5c0f\u5b50\uff0c\u8fd8\u4e0d\u773c\u8f93 \u5417\uff1f\u9664\u975e\u5730\u5e95\u6709\u7f1d\uff0c\u4f60\u53d8\u4e86\u8682\u8681\u94bb\u51fa\u53bb\uff0c\u5426\u5219\u662f\u51b3\u96be\u9003\u4e86\u3002\u5ff5\u5728\u4f60\u4e0e\u6211\u4eec\u6559\u4e3b\u6709\u6bb5\u6e0a\u6e90\uff0c\u6211 \u7ed9\u4f60\u6307\u4e00\u6761\u751f\u8def\u5427\u3002\u4f60\u628a\u5b9d\u5251\u629b\u4e0b\uff0c\u4f5c\u4e3a\u8d54\u507f\u6211\u6b27\u9633\u4eb2\u5bb6\u7684\u7075\u829d\uff0c\u7136\u540e\u8d1f\u8346\u8bf7\u7f6a\uff0c\u6216\u8005\u6211\u53ef \u4ee5\u7ed9\u4f60\u8bf4\u60c5\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u6b63\u81ea\u82e5\u601d\u65e0\u8ba1\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u5ffd\u5730\u7075\u673a\u4e00\u89e6\uff0c\u53eb\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u6709\u4e86\uff01\u54b1\u4eec\u5165\u5730\u65e0 \u95e8\uff0c\u4f46\u5374\u8fd8\u4e0a\u5929\u6709\u672f\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e5f\u771f\u673a\u7075\uff0c\u7acb\u5373\u542c\u61c2\u4e86\u59ec\u6653\u98ce\u7684\u610f\u89c1\uff0c\u84e6\u5730\u5927\u559d\u4e00\u58f0\uff0c\u5530\u5530\u4e24\u5251\uff0c\u628a\u6587\u5ef7\u58c1\u8feb\u9000 \u4e86\u4e00\u6b65\uff0c\u6587\u5ef7\u58c1\u53ea\u9053\u4ed6\u662f\u201c\u56f0\u517d\u72b9\u6597\u201d\uff0c\u6015\u4ed6\u62fc\u547d\uff0c\u5f53\u771f\u8fd8\u6709\u51e0\u5206\u987e\u5fcc\u3002\u54ea\u77e5\u6c5f\u6bcf\u5929\u5374\u662f\u4ee5 \u8fdb\u4e3a\u9000\u7684\u6218\u672f\uff0c\u5c31\u5728\u6587\u5ef7\u58c1\u9000\u6b65\u7684\u8fd9\u4e00\u5239\u90a3\uff0c\u4ed6\u84e6\u5730\u7eb5\u8eab\u8dc3\u8d77\uff0c\u4e00\u5251\u523a\u7a7f\u4e86\u5c4b\u9876\uff0c\u968f\u7740\u4e00\u638c \u5c06\u88c2\u6d1e\u9707\u5f00\uff0c\u5c4b\u74e6\u7684\u788e\u88c2\u58f0\u5760\u5730\u58f0\u54cd\u6210\u4e00\u7247\u3002 \u3000\u3000\u6b27\u9633\u4f2f\u548c\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u8fd9\u5c0f\u8d3c\u8981\u9003\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u6c5f\u6d77\u5929\u5df2\u94bb\u4e86\u51fa\u53bb\uff01\u6b27\u9633\u4e8c\u5a18\u5fd9\u628a \u7ea2\u7ef8\u629b\u6765\uff0c\u610f\u6b32\u628a\u59ec\u6653\u98ce\u62d6\u4e0b\u3002 \u3000\u3000\u59ec\u6653\u98ce\u8eab\u6cd5\u5feb\u6781\uff0c\u4f46\u90a3\u6d1e\u53e3\u53ea\u80fd\u901a\u8fc7\u4e00\u4eba\uff0c\u4ed6\u8ddf\u5728\u6c5f\u6d77\u5929\u7684\u540e\u9762\uff0c\u8eab\u6cd5\u591a\u5feb\uff0c\u4e5f\u603b\u5f97\u8ba9 \u6c5f\u6d77\u5929\u5148\u94bb\u51fa\u53bb\u624d\u884c\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u7684\u7ea2\u7ef8\u6325\u821e\uff0c\u592d\u77eb\u5982\u9f99\uff0c\u53ea\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u59ec\u6653\u98ce\u4e0a\u534a\u8eab\u94bb\u51fa\u5916\u9762\uff0c\u4e00\u53ea \u811a\u5374\u8fd8\u672a\u6765\u5f97\u53ca\u62d4\u51fa\uff0c\u811a\u8e1d\u521a\u597d\u88ab\u7ea2\u7ef8\u5377\u4f4f\u4e86\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u5927\u559c\uff0c\u6b63\u8981\u4f7f\u52b2\u62d6\u4ed6\uff0c\u5ffd\u89c9\u90a3\u6761\u7ef8\u5e26\u7a81\u7136\u53d8\u5f97\u5982\u540c\u51b0\u68d2\uff0c\u4e00\u80a1\u5947\u5bd2\u4e4b\u6c14\u76f4\u523a\u5979 \u7684\u638c\u5fc3\u3002\u6b27\u9633\u4e8c\u5a18\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u58f0\uff1a\u201c\u4e0d\u5999\uff01\u201d\u6025\u6025\u5fd9\u5fd9\u6492\u624b\u3002 \u3000\u3000\u59ec\u6653\u98ce\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u4f60\u628a\u65b0\u90ce\u7684\u5f69\u7ef8\uff0c\u9001\u7ed9\u6211\u505a\u7f20\u811a\u5e03\uff0c\u6211\u53ef\u4e0d\u9886\u4f60\u8fd9\u4e2a\u60c5\u3002\u201d\u8f6c\u77ac \u4e4b\u95f4\uff0c\u90a3\u7b11\u58f0\u5df2\u4f3c\u5728\u56f4\u5899\u4e4b\u5916\u3002 \u3000\u3000\u539f\u6765\u59ec\u6653\u98ce\u7684\u4fee\u7f57\u9634\u715e\u529f\u5df2\u7ec3\u5230\u4e86\u7b2c\u4e03\u91cd\u5883\u754c\uff0c\u53ef\u4ee5\u9694\u7269\u4f20\u529f\uff0c\u53ea\u56e0\u4f7f\u7528\u8fd9\u79cd\u6781\u5389\u5bb3\u7684 \u90aa\u6d3e\u529f\u592b\uff0c\u6700\u4e3a\u8017\u62a5\u5185\u529b\uff0c\u6545\u6b64\u59ec\u6653\u98ce\u4e0d\u80af\u8f7b\u6613\u4f7f\u7529\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u673a\u4f36\u4f36\u5730\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff0c\u6587\u5ef7\u58c1\u5ffd\u7136\u5c06\u5979\u7684\u624b\u638c\u5341\u63e1\u4e00\u8f7b\u8f7b\u7684\u6413\u4e86\u51e0\u6413\u3001\u6b27\u9633 \u4f2f\u548c\u53eb\u9053\uff1a\u201c\u6587\u4eb2\u5bb6\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u6b27\u9633\u4e8c\u5a18\u9762\u7ea2\u8033\u8d64\uff0c\u65b9\u826f\u4e00\u5f81\uff1b\u5ffd\u89c9\u4e00\u80a1\u70ed\u6c14\u4ece\u6587\u5ef7\u58c1 \u7684\u638c\u5fc3\u53d1\u51fa\uff0c\u8fd9\u624d\u77e5\u9053\u662f\u6587\u5ef7\u58c1\u7528\u672c\u8eab\u7684\u529f\u529b\u66ff\u5979\u9a71\u9664\u5bd2\u6c14\uff0c\u6b27\u9633\u4e8c\u5a18\u672c\u8eab\u7684\u529f\u529b\u4e0d\u5f31\uff0c\u59ec \u6653\u98ce\u53c8\u53ea\u662f\u201c\u9694\u7269\u4f20\u529f\u201d\uff0c\u56e0\u6b64\u5979\u6240\u53d7\u7684\u670b\u5bd2\u4e4b\u6c14\u8fd8\u4e0d\u7b97\u91cd\uff0c\u5f97\u4e86\u6587\u5ef7\u58c1\u4e4b\u52a9\uff0c\u5f88\u5feb\u5c31\u6062\u590d \u4e86\u539f\u72b6\u3002 \u3000\u3000\u6b27\u9633\u4f2f\u548c\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u53ea\u56e0\u6587\u5ef7\u58c1\u5728\u6c5f\u6e56\u4e0a\u58f0\u540d\u72fc\u85c9\uff1b\u6545\u6b64\u9002\u624d\u6709\u70b9\u8bef\u4f1a\uff0c\u5230\u4e86 \u8fd9\u65f6\uff0c\u5f53\u7136\u4e5f\u5df2\u660e\u767d\u4e86\u6587\u5ef7\u58c1\u5e76\u975e\u8c03\u620f\u4ed6\u7684\u5f1f\u5987\uff0c\u8fde\u5fd9\u4e0e\u6b27\u9633\u4e8c\u5a18\u5411\u4ed6\u9053\u8c22\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u9053\uff1a\u201c\u4e0d\u80fd\u518d\u803d\u6401\u4e86\uff01\u8d76\u5feb\u8ffd\u5427\uff01\u201d\u6b27\u9633\u6015\u548c\u9053\uff1a\u201c\u59ec\u6653\u98ce\u7684\u8f7b\u529f\u5999\u7edd\u5929\u4e0b\uff0c\u53ea \u6015\u8ffd\u4e0d\u4e0a\u4e86\u3002\u201d\u6587\u5ef7\u58c1\u7b11\u9053\uff1a\u201c\u4eb2\u5bb6\u4e0d\u5fc5\u62c5\u5fc3\u3001\u6211\u62c5\u4fdd\u4f60\u8ffd\u5f97\u4e0a\uff01\u201d\u6b27\u9633\u4f2f\u548c\u89c1\u4ed6\u8bf4\u5f97\u6781\u6709 \u628a\u63e1\uff0c\u534a\u4fe1\u534a\u7591\uff0c\u53ea\u597d\u4e0e\u6587\u5ef7\u58c1\u4e00\u540c\u8ffd\u53bb\u3002 \u3000\u3000\u4e14\u8bf4\u59ec\u6653\u98ce\u51fa\u4e86\u6b27\u9633\u5bb6\uff0c\u7acb\u5373\u62d4\u811a\u98de\u5954\uff0c\u6c5f\u6d77\u5929\u7528\u5c3d\u5168\u529b\uff0c\u7d27\u7d27\u8ffd\u968f\uff0c\u6700\u521d\u603b\u662f\u843d\u540e\u6570 \u4e08\uff0c\u6e10\u6e10\u5c31\u53ef\u4ee5\u4e0e\u4ed6\u5e76\u80a9\u800c\u884c\u3002\u6c5f\u6d77\u5929\u8fd8\u4ee5\u4e3a\u4ed6\u662f\u6545\u610f\u653e\u6162\u811a\u6b65\uff0c\u540e\u6765\u542c\u5f97\u4ed6\u7684\u547c\u5438\u4e4b\u58f0\u4f3c \u751a\u6c89\u91cd\uff0c\u624d\u53d1\u89c9\u6709\u70b9\u4e0d\u5bf9\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u4eec\u5df2\u9003\u8fdb\u79e6\u5cad\u7684\u4e1b\u6797\u4e2d\uff0c\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u59ec\u6015\u4f2f\uff0c\u8c05\u4ed6\u4eec\u8ffd\u4e0d\u4e0a\u4e86\uff0c\u54b1\u4eec\u6b47\u6b47 \u5427\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u6587\u5ef7\u58c1\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u59ec\u6653\u98ce\uff0c\u4f60\u8fd8\u8dd1\u5f97\u5230\u54ea\u91cc\u53bb\uff1f\u7559\u7740\u70b9\u6c14\u529b \u5427\uff0c\u514d\u5f97\u81ea\u8ba8\u82e6\u5403\u4e86\uff01\u54fc\uff0c\u4f60\u8fd8\u8981\u8dd1\uff0c\u7ad9\u4f4f\uff0c\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7ed9\u5413\u4e86\u4e00\u8df3\uff0c\u6e38\u76ee\u56db\u987e\uff0c\u5374\u4e0d\u89c1\u6587\u5ef7\u58c1\u7684\u5f71\u5b50\uff0c\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u4ed6\u662f\u7528\u4f20\u97f3\u5165\u5bc6 \u7684\u529f\u592b\uff0c\u610f\u56fe\u5c06\u54b1\u4eec\u9a97\u51fa\u6765\u7684\u3002\u522b\u4e0a\u4ed6\u7684\u5f53\u3002\u201d\u5176\u5b9e\u6c5f\u6d77\u5929\u4e5f\u5b66\u8fc7\u8fd9\u95e8\u529f\u592b\uff0c\u53ea\u56e0\u4e34\u654c\u7684\u7ecf \u9a8c\u4e0d\u8db3\uff0c\u4e00\u65f6\u614c\u5f20\uff0c\u5c31\u6ca1\u6709\u5bdf\u89c9\u51fa\u6765\u3002 \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u542c\u8fd9\u58f0\u97f3\uff0c\u4ed6\u6700\u5c11\u79bb\u5f00\u54b1\u4eec\u8fd8\u6709\u4e09\u91cc\uff0c\u8d24\u4f84\uff0c\u4f60\u610f\u6b32\u5982\u4f55\uff1b\u662f\u8ddf\u4ed6\u4eec\u62da \u5462\uff1b\u8fd8\u662f\u518d\u9003\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f2f\u4f2f\uff0c\u4f60\uff0c\u4f60\u662f\u4e0d\u662f\u53d7\u4e86\u4f24\u4e86\uff1f\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4f24\u5012\u6ca1\u6709\uff0c \u53ea\u662f\u88ab\u4ed6\u90a3\u53ae\u7528\u9694\u7a7a\u70b9\u7a74\u7684\u529f\u592b\u6574\u4e86\u4e00\u4e0b\uff0c\u8fd8\u672a\u80fd\u817e\u51fa\u65f6\u95f4\u6765\u8fd0\u6c14\u884c\u8840\u2026\u2026\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u90a3\u4f60\u600e\u4e48\u521a\u624d\u8fd8\u7528\u4fee\u7f57\u9634\u715e\u529f\uff1f\u5185\u529b\u5c82\u975e\u66f4\u635f\u8017\u4e86\uff1f\u201d\u59ec\u6653\u98ce \u9053\uff1a\u201c\u8981\u662f\u518d\u9003\u7684\u8bdd\uff0c\u6211\u5927\u7ea6\u8fd8\u53ef\u652f\u6301\u534a\u4e2a\u65f6\u8fb0\uff0c\u4f46\u7ec8\u987b\u7ed9\u4ed6\u4eec\u8ffd\u4e0a\uff0c\u6211\u770b\u4e0d\u5982\u62fc\u4e86\u5427\u201d\u6c5f \u6d77\u5929\u9053\uff1a\u201c\u600e\u4e48\u62fc\u6cd5\uff1f\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6211\u518d\u7528\u4fee\u7f57\u9634\u715e\u529f\uff0c\u7eb5\u7136\u4f24\u4e0d\u4e86\u6587\u5ef7\u58c1\uff0c\u6700\u5c11\u4e5f\u53ef\u4ee5 \u51fb\u6bd9\u6b27\u9633\u4e8c\u5a18\uff0c\u5c31\u662f\u90a3\u6b27\u9633\u4f2f\u548c\uff0c\u8c05\u4ed6\u4e5f\u8981\u53d7\u4f24\u3002\u8fd9\u4e48\u6837\uff0c\u4f60\u6709\u5b9d\u5251\uff0c\u5c31\u4e0d\u96be\u6253\u8d62\u6587\u5ef7\u58c1 \u4e86\u3002\u201d \u3000\u3000\u201c\u6c5f\u6d77\u5929\u70ed\u6cea\u76c8\u7736\uff0c\u9053\uff1a\u201c\u4f2f\u4f2f\uff0c\u591a\u8c22\u4f60\u7684\u597d\u610f\u3002\u4f46\u6211\u4e0d\u8d5e\u540c\u3002\u201d\u4ed6\u77e5\u9053\u4ee5\u59ec\u6653\u98ce\u73b0\u5728 \u7684\u60c5\u51b5\uff0c\u5018\u82e5\u518d\u7528\u4fee\u7f57\u9634\u715e\u529f\uff0c\u7eb5\u7136\u4f24\u5f97\u654c\u4eba\uff0c\u81ea\u5df1\u4e5f\u51b3\u96be\u6d3b\u547d\u3002\u59ec\u6653\u98ce\u4e0d\u8fc7\u662f\u60f3\u727a\u7272\u81ea\u5df1 \u6765\u4fdd\u5168\u4ed6\u800c\u5df2\u3002 \u3000\u3000\u59ec\u6653\u98ce\u82e6\u7b11\u9053\uff1a\u201c\u96be\u9053\u54b1\u4eec\u5c31\u675f\u624b\u5f85\u6bd9\u5417\uff1f\u201d\u6c5f\u6d77\u5929\u6c89\u541f\u9053\uff1a\u201c\u6700\u597d\u54b1\u4eec\u80fd\u627e\u4e2a\u5730\u65b9\u6682 \u65f6\u8eb2\u907f\u4e00\u4e0b\uff0c\u53ea\u9700\u534a\u4e2a\u65f6\u8fb0\uff0c\u54b1\u4eec\u5c31\u53ef\u4ee5\u5404\u81ea\u8fd0\u529f\uff0c\u6062\u590d\u7cbe\u795e\u4e86\u3002\u201d\u539f\u6765\u6c5f\u6d77\u5929\u867d\u7136\u5f97\u90a3\u534a \u6839\u7075\u829d\uff0c\u5143\u6c14\u4e0d\u81f3\u4e8e\u600e\u6837\u53d7\u4f24\uff0c\u4f46\u4e5f\u8fd8\u6025\u9700\u8fd0\u529f\u8c03\u6cbb\u3002 \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4e34\u6025\u4e34\u5fd9\uff0c\u54ea\u91cc\u627e\u5f97\u8fd9\u6837\u7684\u597d\u5730\uff1f\u8981\u662f\u968f\u4fbf\u627e\u4e00\u5757\u5ca9\u77f3\u6216\u6811\u6728\u63a9\u853d\uff0c\u90a3\u5c31 \u8981\u5b8c\u5168\u8bb2\u8fd0\u6c14\u4e86\uff0c\u5018\u82e5\u7ed9\u4ed6\u4eec\u53d1\u73b0\uff0c\u5371\u9669\u66f4\u5927\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e5f\u81ea\u5f77\u5fa8\u65e0\u8ba1\uff0c\u5ffd\u5730\u4ed6\u60f3\u8d77\u4e86\u4e91\u53ec\u7ed9\u4ed6\u7684\u90a3\u5f20\u5730\u56fe\uff0c\u5728\u5730\u56fe\u4e0a\u7684\u8bb8\u591a\u5730\u65b9\uff0c\u4e91\u53ec \u90fd\u52a0\u6709\u9644\u6ce8\uff0c\u5199\u4e0a\u4ed6\u597d\u670b\u53cb\u7684\u540d\u5b57\uff0c\u53eb\u6c5f\u6d77\u5929\u5728\u6025\u96be\u4e4b\u65f6\uff0c\u53ef\u4ee5\u627e\u4ed6\u4eec\u76f8\u52a9\u7684\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u62ff\u51fa\u5730\u56fe\u4e00\u770b\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u679c\u7136\u6709\u4e00\u4e2a\u4e8e\u5927\u9e4f\uff0c\u90a3\u4e8e\u5c11\u9cb2\u6ca1\u6709\u9a97\u6211\uff01 \u8fd9\u4e8e\u5927\u9e4f\u5927\u7ea6\u5f53\u771f\u662f\u4ed6\u7684\u7236\u4eb2\uff01\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4e8e\u5927\u9e4f\u662f\u5c11\u6797\u6d3e\u7684\u4fd7\u5bb6\u9ad8\u624b\uff0c\u4f60\u4e0d\u77e5\u9053\u5417\uff1f \u4f60\u4e3a\u4ec0\u4e48\u63d0\u8d77\u4ed6\u6765\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u4e8b\u6211\u6162\u6162\u548c\u4f60\u518d\u8bf4\uff0c\u54b1\u4eec\u73b0\u5728\u8d76\u5feb\u627e \u8fd9\u4e8e\u5927\u9e4f\u53bb\uff0c\u4ed6\u5c31\u4f4f\u5728\u9644\u8fd1\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u54e6\uff0c\u4e8e\u5927\u9e4f\u5c31\u4f4f\u5728\u8fd9\u5c71\u4e2d\u5417\uff1f\u5750\u843d\u4f55\u65b9\uff1f\u201d\u6c5f\u6d77\u5929\u770b\u4e86\u770b\u5730\u56fe\uff0c\u8bf4\u9053\uff1a \u201c\u5728\u795e\u5973\u5cf0\u9e23\u7434\u6d1e\u8fb9\u3002\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4f60\u600e\u77e5\u4ed6\u80af\u6536\u5bb9\u54b1\u4eec\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u6709\u4e91\u53ec\u7684\u91d1 \u72ee\u4ee4\u3002\u201d\u59ec\u6653\u98ce\u6c89\u541f\u7247\u523b\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4e8b\u5230\u4e34\u5934\uff0c\u6211\u4e5f\u53ea\u597d\u4e0d\u987e\u8fd9\u8001\u9762\u76ae\u4e86\u3002\u59d1\u4e14\u53bb\u4e00 \u8bd5\u5427\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u542c\u4ed6\u53e3\u6c14\uff0c\u4f3c\u4e4e\u4e0d\u5927\u60f3\u89c1\u8fd9\u4e8e\u5927\u9e4f\uff0c\u4f46\u5df2\u65e0\u6687\u95ee\u4ed6\u7f18\u6545\u3002\u53ea\u89c1\u59ec\u6653\u98ce\u8eab\u5f62\u4e00\u8d77\uff0c \u4ece\u4e1b\u6797\u4e2d\u7a7f\u8fc7\uff0c\u624b\u62e8\u7e41\u679d\u5bc6\u53f6\uff0c\u8eab\u5f62\u8fc7\u5904\uff0c\u6811\u53f6\u94fa\u4e86\u4e00\u5730\u3002\u6c5f\u6d77\u706d\u4ea1\u7528\u5929\u9041\u4f20\u97f3\u4e4b\u672f\u53eb\u9053\uff1a \u201c\u59ec\u4f2f\u6015\uff0c\u4f60\u9519\u4e86\uff0c\u4e0d\u662f\u8fd9\u4e2a\u65b9\u5411\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u500f\u5730\u6298\u56de\uff0c\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u8fd9\u79e6\u5cad\u6211\u66fe\u8fdb\u51fa\u6570\u6b21\uff0c\u4e86\u5982\u6307\u638c\uff0c\u4e0d\u5fc5\u770b\u56fe\uff0c\u6211\u5c31\u53ef\u4ee5 \u5e26\u4f60\u5230\u9e23\u7434\u6da7\u3002\u6211\u8fd9\u662f\u6545\u5e03\u7591\u9635\uff0c\u6587\u5ef7\u58c1\u89c1\u4e86\u8fd9\u6ee1\u5730\u843d\u53f6\uff0c\u4e00\u5b9a\u4ee5\u4e3a\u54b1\u706f\u662f\u5306\u5306\u5fd9\u5fd9\uff0c\u4ece\u8fd9 \u8fb9\u9003\u8d70\u3002\u201d\u6c5f\u6d77\u5929\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u5fc3\u60f3\uff1a\u201c\u59ec\u4f2f\u4f2f\u771f\u4e0d\u6127\u662f\u4e2a\u8001\u6c5f\u6e56\uff0c\u673a\u667a\u7cbe\u7ec6\uff0c\u786e\u975e\u5e38\u4eba \u53ef\u53ca\u3002\u6211\u53ef\u5f97\u8ddf\u4ed6\u597d\u597d\u7684\u5b66\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4f60\u63d0\u4e00\u53e3\u6c14\uff0c\u7528\u8e0f\u96ea\u65e0\u75d5\u7684\u8f7b\u529f\uff0c\u5c3d\u91cf\u907f\u514d\u5728\u5730\u4e0a\u7559\u4e0b\u8db3\u8ff9\u3002\u201d\u6c5f\u6d77\u5929\u7d27 \u7d27\u76f8\u968f\uff0c\u4e0d\u5230\u4e00\u70b7\u9999\u7684\u65f6\u523b\uff0c\u4fbf\u5230\u4e86\u9e23\u7434\u6da7\u8fb9\u3002 \u3000\u3000\u4e00\u6761\u7011\u5e03\u4ece\u60ac\u5d16\u4e0a\u6cfb\u4e0b\uff0c\u6c47\u5230\u6da7\u4e2d\uff0c\u5176\u58f0\u53ee\u549a\u679c\u7136\u751a\u4f3c\u7434\u58f0\uff0c\u59ec\u6653\u98ce\u6e38\u76ee\u56db\u987e\uff0c\u5374\u4e0d\u89c1 \u4eba\u5bb6\u3002\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u8fd9\u60ac\u5d16\u4e0a\u679c\u7136\u6709\u4e94\u68f5\u677e\u6811\u3002\u201d\u4ed6\u4eec\u4e8c\u4eba\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u907f\u8fc7\u7011\u5e03 \u7684\u6b63\u9762\u3002\u6500\u4e0a\u60ac\u5d16\uff0c\u4f46\u90a3\u7011\u5e03\u98de\u73e0\u6e85\u7389\uff0c\u4ecd\u7136\u4e0d\u514d\u6e7f\u4e86\u8863\u88f3\u3002 \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u627e\u7740\u4e86\uff0c\u5403\u70b9\u82e6\u5934\uff0c\u4e5f\u8fd8\u503c\u5f97\u3002\u201d\u53ea\u89c1\u90a3\u4e94\u68f5\u677e\u6811\uff0c\u4ea4\u62b1\u5982\u76d6\uff0c\u5360\u5730\u751a \u5e7f\uff0c\u677e\u836b\u4e4b\u4e2d\uff0c\u9732\u51fa\u4e00\u89d2\u77ed\u5899\uff0c\u679c\u7136\u6709\u4e2a\u5c0f\u6237\u4eba\u5bb6\u3002\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5e78\u4e8f\u4e91\u5e84\u4e3b\u5728\u5730\u56fe\u4e0a\u7684\u9644 \u6ce8\u8bf4\u5f97\u660e\u767d\uff0c\u5018\u82e5\u4e0d\u7136\uff0c\u8fd8\u5f53\u771f\u96be\u4ee5\u53d1\u73b0\u5462\u3002\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u54b1\u4eec\u6b63\u8981\u65c1\u4eba\u96be\u4ee5\u53d1\u73b0\u3002\u201d\u5f53 \u4e0b\uff0c\u5c31\u5411\u90a3\u5bb6\u4eba\u5bb6\u8d70\u53bb\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u91cc\u6b63\u81ea\u601d\u91cf\uff0c\u201c\u8981\u4e0d\u8981\u5c06\u4ed6\u513f\u5b50\u7684\u6b7b\u8baf\u544a\u8bc9\u4ed6\u5462\uff1f\u201d\u59ec\u6653\u98ce\u5df2\u8d70\u5230\u95e8\u524d\uff0c\u5ffd\u5730 \u76b1\u4e86\u76b1\u7709\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u91cc\u9762\u6709\u4eba\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u662f\u4e2a\u5973\u5b50\u3002\u4e8e \u5927\u9e4f\u7684\u8001\u4f34\u65e9\u5df2\u6b7b\u4e86\uff0c\u6211\u53ea\u77e5\u9053\u4ed6\u6709\u4e24\u4e2a\u513f\u5b50\uff0c\u6ca1\u6709\u5973\u513f\uff0c\u8fd9\u4e2a\u5973\u5b50\u4e0d\u77e5\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u59ec\u6653 \u98ce\u662f\u5929\u4e0b\u7b2c\u4e00\u795e\u5077\uff0c\u8033\u529b\u76ee\u529b\uff0c\u90fd\u8d85\u8fc7\u5e38\u4eba\u5341\u500d\uff0c\u6c5f\u6d77\u5929\u5728\u8fd9\u65b9\u9762\u8fdc\u4e0d\u5982\u4ed6\uff0c\u542c\u4e0d\u5230\u91cc\u9762\u7684 \u8bdd\u8bed\u3002\u5fc3\u60f3\uff1a\u201c\u7ba1\u5979\u662f\u4ec0\u4e48\u4eba\uff0c\u6572\u95e8\u518d\u8bf4\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6572\u4e86\u51e0\u4e0b\u95e8\uff0c\u59ec\u6653\u98ce\u53c8\u9690\u7ea6\u542c\u8910\u91cc\u9762\u6709\u4e2a\u82cd\u8001\u800c\u53c8\u60ca\u60f6\u6025\u901f\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5feb\uff0c \u5feb\u8eb2\u8d77\u6765\uff01\u201d\u59ec\u6653\u98ce\u5927\u4e3a\u5947\u602a\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u9093\u4e24\u6247\u95e8\u201c\u5440\u201d\u7684\u4e00\u58f0\u5f00\u4e86\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u51fa\u6765\u4e00\u4e2a\u8001\u4eba\uff0c\u8138\u5e1d\u60ca\u60f6\u4e4b\u8272\uff0c\u76ef\u7740\u6c5f\u6d77\u5929\u5389\u58f0\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u5230\u8fd9\u91cc\u6765\u5e72\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u4ece\u8001\u677e\u6811\u540e\u84e6\u5730\u73b0\u51fa\u8eab\u5f62\uff0c\u7b11\u9053\uff1a\u201c\u8001\u4e8e\uff0c\u8fd8\u8ba4\u5f97\u6211\u4e48\uff1f\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u53c8\u662f\u4e00\u60ca\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u59ec\u6653\u98ce\uff0c\u539f\u6765\u662f\u4f60\uff01\u4f60\u5bb3\u5f97\u6211\u597d\u82e6\uff0c\u53c8\u60f3\u6765\u5077\u4e1c \u897f\u4e86\u5417\uff1f\u201d\u58f0\u51fa\u638c\u968f\uff0c\u4e00\u51fa\u624b\u4fbf\u662f\u5c11\u6797\u5bfa\u7684\u94c1\u7802\u638c\u529f\u592b\uff0c\u6a2a\u638c\u5982\u5200\uff0c\u5482\u5411\u59ec\u6653\u98ce\u524a\u4e0b\u3002 \u3000\u3000\u59ec\u6653\u98ce\u7109\u80fd\u7ed9\u4ed6\u524a\u7740\uff0c\u6ef4\u6e9c\u6e9c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u65e9\u907f\u4e86\u5f00\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u8001\u4e8e\uff0c\u6211\u8fd9\u56de\u4e0d\u662f\u6765\u4f5c \u8d3c\u7684\uff0c\u4f60\u8bd5\u60f3\uff0c\u6211\u82e5\u8981\u6765\u5077\u4f60\u7684\u4e1c\u897f\uff0c\u8fd8\u4f1a\u6572\u95e8\u5417\uff1f\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u4e00\u60f3\u679c\u6709\u9053\u7406\uff0c\u4f46\u4ed6\u4ecd\u662f\u4e0d\u6562\u653e\u677e\u6212\u5907\uff0c\u6c14\u547c\u547c\u7684\u76ef\u7740\u59ec\u6653\u98ce\uff0c\u59ec\u6653\u98ce\u53c8\u7b11\u9053\uff1a \u201c\u8001\u4e8e\uff0c\u4f60\u5927\u7ea6\u8fd9\u51e0\u5e74\u6ca1\u6709\u56de\u8fc7\u5c11\u6797\u5bfa\uff0c\u6211\u501f\u8d35\u5bfa\u7684\u90a3\u4e24\u672c\u6b66\u529f\u79d8\u7c4d\uff0c\u65e9\u5df2\u5f52\u8fd8\u7ed9\u5927\u60b2\u7985\u5e08 \u5566\u3002\u91d1\u4e16\u9057\u5927\u4fa0\u4e3a\u4e86\u8fd9\u4e8b\uff0c\u8fd8\u7279\u5730\u4e0a\u8d35\u5bfa\u4e3a\u6211\u8bf4\u60c5\uff0c\u627f\u8499\u8d35\u5bfa\u7684\u8001\u65b9\u4e08\u75db\u7985\u4e0a\u4eba\u4eb2\u53e3\u7b54\u5141\uff0c \u8bf4\u662f\u539f\u7269\u65e2\u7136\u5f97\u56de\uff0c\u8fc7\u53bb\u7684\u4e8b\u60c5\u5c31\u4e0d\u5fc5\u8ffd\u7a76\u4e86\u3002\u8001\u4e8e\uff0c\u4f60\u7684\u6d88\u606f\u4e5f\u592a\u4e0d\u7075\u901a\u4e86\uff0c\u600e\u4e48\u8fd8\u628a\u6211 \u5f53\u4f5c\u4ec7\u4eba\u3002\u201d \u3000\u3000\u539f\u6765\u5f53\u5e74\u59ec\u6653\u98ce\u5728\u5c11\u6797\u5bfa\u5077\u4e66\u7684\u65f6\u5019\uff0c\u4e8e\u5927\u9e4f\u4e5f\u6b63\u5728\u5c11\u6797\u5bfa\u6267\u5f79\uff0c\u5c11\u6797\u89c4\u77e9\uff0c\u4fd7\u5bb6\u5f1f\u5b50 \u827a\u6210\u4e4b\u540e\uff0c\u8fd8\u8981\u8f6e\u6d41\u7ed9\u672c\u5bfa\u6267\u5f79\u4e09\u5e74\u3002\u5f53\u65f6\u4ed6\u6b63\u662f\u770b\u5b88\u7ecf\u5802\u7684\u5341\u4e8c\u540d\u5f1f\u5b50\u4e4b\u4e00\uff0c\u4e3a\u4e86\u5931\u4e66\u4e4b \u4e8b\uff0c\u4ed6\u8fd8\u53d7\u8fc7\u76d1\u5bfa\u7684\u8d23\u7f5a\uff0c\u6240\u4ee5\u4ed6\u4e00\u89c1\u59ec\u6653\u98ce\uff0c\u4fbf\u9a82\u59ec\u6653\u98ce\u5bb3\u5f97\u4ed6\u597d\u82e6\u3002 \u3000\u3000\u4e8e\u5927\u9e4f\u5413\u4e86\u59ec\u6653\u98ce\u7684\u8bdd\uff0c\u534a\u4fe1\u534a\u7591\uff0c\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u76f8\u4fe1\u6211\uff0c\u6709\u4e00\u4e2a\u4eba\u4f60\u603b\u8be5\u76f8\u4fe1 \u5427\uff1f\u6c34\u4e91\u4e61\u7684\u4e91\u5e84\u4e3b\u4e91\u53ec\u662f\u4e0d\u662f\u4f60\u7684\u8001\u670b\u53cb\uff1f\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u6c5f\u6d77\u5929\u4e0a\u524d\u65bd\u4e86\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4e91 \u8001\u82f1\u96c4\u7684\u91d1\u72ee\u4ee4\uff0c\u8bf7\u4e8e\u8001\u4f2f\u9a8c\u770b\uff0c\u4e91\u8001\u82f1\u96c4\u8bf7\u6211\u6301\u6b64\u4fe1\u7269\u6765\u62dc\u89c1\u8005\u4f2f\u7684\u3002\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u5927\u60ca\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u4e91\u53ec\u4ed6\u600e\u80af\u628a\u91d1\u72ee\u4ee4\u4ea4\u7ed9\u4f60\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u665a\u8f88\u7684\u59d3 \u540d\u6765\u5386\uff0c\u542c\u8bf4\u4ee4\u90ce\u66fe\u5411\u8001\u4f2f\u63d0\u8fc7\uff0c\u6211\u5c31\u662f\u2014\u2014\u201d\u4e8e\u5927\u9e4f\u66f4\u662f\u5403\u60ca\uff0c\u8fde\u5fd9\u8bf4\u9053\uff0c\u201c\u54e6\uff0c\u539f\u6765\u4f60 \u5c31\u662f\u6c5f\u5357\u4e4b\u5b50\uff0c\u91d1\u4e16\u9057\u4e4b\u5f92\uff0c\u540d\u53eb\u6c5f\u6d77\u5929\u7684\u90a3\u4f4d\u5c11\u5e74\u82f1\u96c4\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\u6562\u3002\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u6c89\u541f\u9053\uff1a\u201c\u53ef\u60dc\u5c0f\u513f\u4e0d\u5728\u2026\u2026\u55ef\uff0c\u4f60\u8bf4\u4f60\u5c31\u662f\u6c5f\u6d77\u5929\uff0c\u6709\u4f55\u8bc1\u636e\uff1f\u201d \u3000\u3000\u539f\u6765\u4e8e\u5927\u9e4f\u8fd8\u662f\u4e0d\u6562\u76f8\u4fe1\uff0c\u4ed6\u5fc3\u91cc\u5728\u60f3\uff1a\u201c\u91d1\u72ee\u4ee4\u786e\u662f\u4e0d\u5047\uff0c\u4f46\u7109\u77e5\u8fd9\u4e0d\u662f\u59ec\u6653\u98ce\u5077\u6765 \u7684\u5462\uff1f\u201d \u3000\u3000\u53e4\u65f6\u5019\u53c8\u6ca1\u6709\u201c\u8eab\u4efd\u8bc1\u201d\u4e4b\u7c7b\u7684\u4e1c\u897f\uff0c\u6c5f\u6d77\u5929\u7ed9\u4ed6\u4e00\u95ee\uff0c\u4e0d\u89c9\u5446\u82e5\u6728\u9e21\uff0c\u5fc3\u91cc\u5728\u60f3\uff0c \u201c\u8fd9\u53ef\u96be\u4e86\uff0c\u6211\u600e\u4e48\u80fd\u8bc1\u660e\u6211\u5c31\u662f\u6211\u81ea\u5df1\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u5ffd\u5730\u628a\u6c5f\u6d77\u5929\u90a3\u628a\u5b9d\u5251\u62d4\u4e86\u51fa\u6765\uff0c\u4e8e\u5927\u9e4f\u5927\u60ca\uff0c\u8fde\u5fd9\u540e\u9000\uff0c\u53ea\u542c\u5f97\u59ec\u6653\u98ce\u7b11\u9053\uff1a \u201c\u8001\u4e8e\uff0c\u5f53\u5e74\u5d69\u5c71\u5343\u969c\u576a\u4e4b\u4f1a\uff0c\u4f60\u4e5f\u66fe\u7ecf\u5728\u573a\uff0c\u603b\u8be5\u8ba4\u5f97\u91d1\u5927\u4fa0\u8fd9\u628a\u5b9d\u5251\u5427\uff1f\u201d\u4fe1\u624b\u4e00\u6325\uff0c \u5982\u540c\u5207\u8c46\u8150\u4e00\u822c\u628a\u4e00\u5757\u5927\u77f3\u4ece\u4e2d\u5256\u5f00\uff0c\u7136\u540e\u8bf4\u9053\uff1a\u201c\u4f60\u60f3\uff0c\u5018\u82e5\u4ed6\u4e0d\u662f\u91d1\u5927\u4fa0\u7684\u8863\u94b5\u4f20\u4eba\uff0c \u7109\u80fd\u5f97\u6709\u8fd9\u628a\u88c1\u4e91\u5b9d\u5251\uff1f\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u5fc3\u60f3\uff1a\u201c\u4e91\u53ec\u7684\u91d1\u72ee\u4ee4\uff0c\u6216\u8005\u59ec\u6653\u98ce\u8fd8\u6562\u53bb\u5077\uff1b\u91d1\u4e16\u9057\u7684\u5b9d\u5251\uff0c\u4ed6\u51b3\u6ca1\u6709\u80c6\u91cf\u7a83 \u53d6\uff0c\u800c\u4e14\uff0c\u5c31\u7b97\u4ed6\u5403\u4e86\u72ee\u5b50\u7684\u5fc3\uff0c\u8c79\u5b50\u7684\u80c6\uff0c\u91d1\u4e16\u9057\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u4e5f\u51b3\u4e0d\u80fd\u5bb9\u4ed6\u5f97\u624b\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8fd9\u65f6\u4e5f\u60f3\u8d77\u4e86\u4e00\u4ef6\u201c\u4fe1\u7269\u201d\uff0c\u628a\u4e91\u53ec\u6240\u7ed8\u7684\u90a3\u5f20\u5730\u56fe\u53d6\u4e86\u51fa\u6765\uff0c\u53cc\u624b\u6367\u4e0a\uff0c\u8bf4 \u9053\uff1a\u201c\u8001\u4f2f\u8bf7\u770b\u8fd9\u5f20\u5730\u56fe\uff0c\u5730\u56fe\u4e0a\u4e91\u8001\u82f1\u96c4\u8fd8\u4eb2\u7b14\u5199\u6709\u8001\u4f2f\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u8ba4\u5f97\u4e91\u53ec\u7684\u7b14\u8ff9\uff0c\u91d1\u72ee\u4ee4\u52a0\u4e0a\u5730\u56fe\u518d\u52a0\u4e0a\u90a3\u67c4\u5b9d\u5251\uff0c\u5df2\u8db3\u591f\u8bc1\u660e\u4e86\u6c5f\u6d77\u5929\u7684\u8eab\u4efd\u3002 \u3000\u3000\u4e8e\u5927\u9e4f\u518d\u65e0\u534a\u70b9\u7591\u5fc3\uff0c\u6b49\u7136\u8bf4\u9053\uff1a\u201c\u6c5f\u5c0f\u4fa0\uff0c\u8bf7\u6055\u8001\u5927\u9002\u624d\u65e0\u793c\uff0c\u53ea\u56e0\uff0c\u53ea\u56e0\u2026\u2026\u201d\u59ec \u6653\u98ce\u7b11\u9053\uff1a\u201c\u53ea\u56e0\u6709\u6211\u5999\u624b\u795e\u5077\u5728\u65c1\uff0c\u6240\u4ee5\u4f60\u89c1\u4e86\u91d1\u72ee\u4ee4\u4e5f\u8fd8\u4e0d\u6562\u76f8\u4fe1\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u7ed9\u4ed6\u8bf4\u4e2d\uff0c\u5c34\u5c2c\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u59ec\u6653\u98ce\uff0c\u4f60\u4eca\u5929\u662f\u6cbe\u4e86\u6c5f\u5c0f\u4fa0\u7684\u5149\uff0c\u6211\u73b0\u5728\u4e5f\u53ea \u80fd\u76f8\u4fe1\u4f60\u4e86\u3002\u8bf7\u4e24\u4f4d\u8fdb\u53bb\u5427\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u597d\uff0c\u73b0\u5728\u54b1\u4eec\u4ea4\u4e0a\u4e86\u670b\u53cb\uff0c\u4f60\u4e0d\u5fc3\u518d\u63d0\u9632\u6211\u5077\u4f60\u7684\u4e1c\u897f\u4e86\u3002\u201d \u3000\u3000\u8fdb\u4e86\u5ba2\u5385\uff0c\u59ec\u6653\u98ce\u4fbf\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u548c\u6211\u5ba2\u5957\uff0c\u6211\u53ea\u60f3\u501f\u4f60\u4e00\u95f4\u9759\u5ba4\u4e00\u7528\u3002\u201d\u4e8e\u5927\u9e4f\u8fd9\u65f6 \u521a\u521a\u770b\u51fa\u59ec\u6653\u98ce\u7684\u9762\u8272\u6709\u70b9\u4e0d\u5bf9\uff0c\u662f\u6076\u6218\u8fc7\u540e\u5185\u529b\u4e8f\u635f\u7684\u8ff9\u8c61\uff0c\u4e0d\u7981\u53c8\u662f\u7591\u4e91\u5927\u8d77\uff0c\u59ec\u6653\u98ce \u9053\uff1a\u201c\u7b49\u4f1a\u513f\u6c5f\u5c0f\u4fa0\u4f1a\u5bf9\u4f60\u8bf4\u4e2a\u660e\u542f\u7684\uff0c\u6211\u53ef\u8feb\u4e0d\u53ca\u5f85\u4e86\u3002\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u5c31\u8bf7\u59ec\u5148\u751f\u5230\u6211\u4e66\u623f\u6682\u6b47\uff0c\u5582\uff0c\u4e66\u623f\u5728\u8fd9\u4e00\u8fb9\uff01\u201d\u539f\u6765\u59ec\u6653\u98ce\u5728 \u8ddf\u4ed6\u8d70\u5411\u4e66\u623f\u7684\u65f6\u5019\uff0c\u5374\u5ffd\u7136\u5411\u53e6\u4e00\u540c\u53a2\u623f\u63a2\u5934\u63a2\u8111\u7684\u5f20\u671b\uff0c\u7ed9\u4e8e\u5927\u9e4f\u53d1\u89c9\u4e86\u3002\u6c5f\u6d77\u5929\u4e0d\u660e \u5176\u4e2d\u7f18\u6545\uff0c\u5fc3\u91cc\u6697\u6697\u57cb\u6028\uff1a\u201c\u59ec\u4f2f\u4f2f\u597d\u4e0d\u6b63\u7ecf\uff0c\u600e\u4e48\u5230\u4e86\u4eba\u5bb6\u5bb6\u91cc\uff0c\u8fd8\u662f\u8fd9\u4e48\u8d3c\u5934\u8d3c\u8111\u7684\uff1f \u8981\u662f\u8fd9\u623f\u5185\u6709\u4eba\u5bb6\u7684\u5185\u7737\uff0c\u90a3\u624d\u96be\u4e3a\u60c5\u5462\uff0c\u201d\u4ed6\u54ea\u77e5\u9053\u59ec\u6653\u98ce\u5df2\u5bdf\u89c9\u8fd9\u53a2\u623f\u91cc\u6709\u4eba\u57cb\u4f0f\uff0c\u4ece \u90a3\u4e2a\u4eba\u7684\u547c\u5438\u6c14\u606f\u542c\u6765\uff0c\u8fd8\u662f\u4e00\u4e2a\u5185\u529f\u9887\u6709\u70b9\u6839\u5e95\u7684\u4eba\uff0c\u800c\u4e14\u8fd9\u4e2a\u4eba\u4e5f\u4f3c\u4e4e\u6015\u7ed9\u5916\u4eba\u53d1\u73b0\uff0c \u6240\u4ee5\u6781\u529b\u628a\u547c\u5438\u7684\u6c14\u606f\u51cf\u8f7b\uff0c\u5018\u82e5\u4e0d\u662f\u59ec\u6653\u98ce\u7684\u542c\u89c9\u5f02\u4e8e\u5e38\u4eba\uff0c\u4e5f\u51b3\u96be\u5bdf\u89c9\uff0c\u6b63\u662f\uff1a \u3000\u3000\u6025\u96be\u76f8\u6295\u987b\u8c28\u614e\uff0c\u8c01\u77e5\u65e7\u4fa3\u53c8\u76f8\u9022\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u56db\u56de\u3000\u75db\u5931\u7231\u513f\u62fc\u8001\u547d\u3000\u60ca\u770b\u60c5\u4fa3\u6597\u4eb2\u5a18 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e8c\u5341\u56db\u56de\u3000\u75db\u5931\u7231\u513f\u62fc\u8001\u547d\u3000\u60ca\u770b\u60c5\u4fa3\u6597\u4eb2\u5a18 \u3000\u3000\u4e8e\u5927\u9e4f\u5fc3\u91cc\u4e03\u4e0a\u516b\u843d\uff0c\u5bf9\u59ec\u6653\u98ce\u7684\u884c\u52a8\u751a\u662f\u60ca\u7591\uff0c\u6697\u81ea\u60f3\u5230\uff1a\u201c\u8fd9\u53ae\u662f\u4e2a\u9b3c\u7cbe\u7075\uff0c\u83ab\u975e \u4ed6\u5df2\u77a7\u51fa\u7834\u7efd\u6765\u4e86\uff1f\u201d\u968f\u5373\u53c8\u60f3\uff1a\u201c\u6c5f\u6d77\u5929\u662f\u62ff\u4e86\u4e91\u53ec\u7684\u91d1\u72ee\u4ee4\u6765\u89c1\u6211\u7684\uff0c\u60f3\u4e0d\u81f3\u5e72\u5bf9\u6211\u4e0d \u5229\u3002\u53ea\u662f\u8fd9\u4ef6\u4e8b\u8981\u4e0d\u8981\u544a\u8bc9\u4ed6\u5462\uff1f\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u5b89\u987f\u4e86\u59ec\u6653\u98ce\u4e4b\u540e\uff0c\u6000\u7740\u6ee1\u8179\u7591\u56e2\uff0c\u4fbf\u5411\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8bf7\u95ee\u6c5f\u5c0f\u4fa0\u6b64\u6765\uff0c\u7aef\u7684\u662f \u4e3a\u4e86\u4f55\u4e8b\uff1f\u59ec\u5148\u751f\u53c8\u662f\u4f55\u4ee5\u8981\u8fd0\u529f\u7597\u4f24\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e91\u5e84\u4e3b\u86e4\u6211\u91d1\u72ee\u4ee4\u7684\u65f6\u5019\uff0c\u66fe\u5bf9\u6211\u8a00\u9053\uff0c\u82e5\u6709\u6025\u96be\u4e4b\u65f6\uff0c\u53ef\u6c42\u8001\u4f2f\u76f8\u52a9\uff0c \u662f\u4ee5\u6211\u4e0d\u8f9e\u5192\u6627\uff0c\u767b\u95e8\u62dc\u8bbf\u3002\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u78b0\u5230\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u66fe\u78b0\u5230\u4e86\u4ee4\u90ce\u3002\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u9053\uff1a\u201c\u5594\uff0c\u4e0d\u9519\uff0c\u5c0f\u513f\u4e5f\u66fe\u8bf4\u8fc7\u6b64\u4e8b\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\u662f\u5728\u7384\u9634\u8c37\u7684\u90a3\u4e00\u6b21\uff0c\u6211\u662f\u8bf4\u4eca\u65e5\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u5fc3\u5934\u4e00\u9707\uff0c\u5fd9\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u662f\u4eca\u5929\u78b0\u89c1\u4ed6\u7684\u5417\uff1f\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u81ea\u5fc3\u60f3\uff0c\u8981\u4e0d\u8981\u5c06\u4ed6\u513f\u5b50\u7684\u5669\u8017\u5409\u8bc9\u4ed6\uff0c\u5de6\u8fb9\u53a2\u623f\u7684\u623f\u95e8\u5ffd\u5730\u6253\u5f00\uff0c\u4e00\u4e2a\u5c11\u5973 \u98de\u5954\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u6c5f\u76f8\u516c\uff0c\u5f53\u771f\u662f\u4f60\uff01\u4f60\u600e\u4e48\u5230\u8fd9\u513f\u6765\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8fd9\u4e00\u60ca\u6bd4\u4e8e\u5927\u9e4f\u66f4\u751a\uff0c\u5b9a\u4e86\u773c\u775b\uff0c\u5413\u5f97\u5446\u4e86\uff0c\u9762\u524d\u8fd9\u4e2a\u5c11\u5973\uff0c\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u6b27 \u9633\u5a49\uff01\u8fd9\u5239\u90a3\u95f4\uff0c\u6c5f\u6d77\u5929\u5fc3\u4e71\u5982\u9ebb\uff0c\u4e0d\u77e5\u6240\u63aa\u3002 \u3000\u3000\u6b27\u9633\u5a49\u201c\u6251\u54e7\u201d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u795e\u6c14\u4e3a\u4ec0\u4e48\u8fd9\u6837\u96be\u770b\uff1f\u54e6\uff0c\u662f\u4e86\uff0c\u4f60\u4e00\u5b9a\u662f\u5f53\u6211\u5077 \u4f60\u4e49\u7236\u7684\u836f\u56ca\uff0c\u6240\u4ee5\u6068\u6b7b\u6211\u4e86\u3002\u6211\u73b0\u5728\u4e0d\u59a8\u544a\u8bc9\u4f60\uff0c\u5077\u4f60\u4e49\u7236\u836f\u56ca\u7684\uff0c\u4ee5\u53ca\u548c\u53f6\u516c\u5b50\u5230\u4e91\u5bb6 \u5e84\u7684\u90a3\u4e2a\u4eba\u90fd\u4e0d\u662f\u6211\uff0c\u90a3\u662f\u6211\u7684\u59d0\u59d0\uff0c\u540e\u6765\u9001\u89e3\u836f\u7684\u90a3\u4e2a\u4eba\u624d\u662f\u6211\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u91cc\u85cf\u4e86\u8bb8\u4e45\u7684\u95f7\u846b\u82a6\u8fd9\u624d\u6253\u7834\uff0c\u4f46\u4ed6\u60ca\u7591\u7684\u795e\u60c5\u5374\u8fd8\u672a\u80fd\u6d88\u9664\uff0c\u4e0d\u5047\u601d\u7d22\u4fbf\u5373 \u51b2\u53e3\u800c\u51fa\uff0c\u95ee\u9053\uff1a\u201c\u597d\uff01\u8fd9\u4e24\u4ef6\u4e8b\u6211\u660e\u767d\u4e86\u3002\u4f46\u6211\u521a\u624d\u6240\u89c1\u7684\u90a3\u65b0\u5a18\u5b50\u4e5f\u4e0d\u662f\u4f60\u5417\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u4e5f\u6014\u4e86\u4e00\u6014\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u5df3\u7ecf\u5230\u8fc7\u6211\u7684\u5bb6\u91cc\u4e86\uff1f\u201d\u5fc3\u91cc\u751c\u4e1d\u4e1d\u7684\uff0c\u60f3\u9053\uff1a \u201c\u539f\u6765\u4ed6\u7684\u5fc3\u4e0a\u4e5f\u6709\u6211\u5728\uff0c\u7adf\u7136\u4e0d\u6015\u6211\u7684\u7239\u5a18\uff0c\u5192\u4e86\u5371\u9669\u5230\u6211\u5bb6\u91cc\u53bb\u63a2\u8bbf\u6211\u3002\u201d \u3000\u3000\u6b27\u9633\u5a49\u683c\u683c\u7b11\u9053\uff1a\u201c\u90a3\u65b0\u5a18\u5b50\u4e5f\u662f\u6211\u7684\u59d0\u59d0\uff0c\u6211\u4eec\u59d0\u59b9\u4fe9\u672c\u6765\u957f\u5f97\u5f88\u76f8\u4f3c\uff0c\u65b0\u5a18\u5b50\u53c8\u4e00 \u5b9a\u8981\u7528\u7ea2\u7f57\u5e15\u8499\u8fc7\u5934\u9762\uff0c\u602a\u4e0d\u5f97\u4f60\u8ba4\u9519\u4e86\u4eba\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\uff0c\u8fd9\u53ef\u5947\u6027\u4e86\uff01\u4f60\uff0c\u4f60\u5e08\u5144\u2026\u2026\u3002\u4ed6\u6240\u5947\u602a\u7684\u662f\uff1a\u65e2\u7136\u6b27\u9633\u5a49\u9003\u5230\u4e86\u4e8e \u5bb6\uff0c\u5374\u4e3a\u4f55\u4e8e\u5c11\u9cb2\u8fd8\u8981\u53bb\u627e\u90a3\u65b0\u90ce\u7684\u6666\u6c14\uff0c\u800c\u4e14\u4e5f\u628a\u90a3\u65b0\u5a18\u5b50\u5f53\u4f5c\u4e86\u6b27\u9633\u5a49\u5462\uff1f \u3000\u3000\u6b27\u9634\u5a49\u5374\u8bef\u4f1a\u4e86\u6c5f\u6d77\u5929\u7684\u610f\u601d\uff0c\u622a\u65ad\u4ed6\u7684\u8bdd\u5934\uff0c\u4fbf\u5373\u8bf4\u9053\uff0c\u201c\u8fd9\u6709\u4ec0\u4e48\u5947\u602a\uff0c\u6211\u4e0d\u613f\u5ac1 \u90a3\u59d3\u6587\u7684\uff0c\u79c1\u9003\u51fa\u6765\uff0c\u5728\u9644\u8fd1\u53c8\u6ca1\u6709\u76f8\u719f\u7684\u4eba\u5bb6\uff0c\u7b97\u6765\u7b97\u53bb\uff0c\u53ea\u6709\u4e8e\u5e08\u5144\u8fd9\u513f\u53ef\u4ee5\u6682\u65f6\u907f\u96be \u7684\u3002\u6211\u4eec\u90fd\u662f\u6c5f\u6e56\u513f\u5973\uff0c\u4e8b\u6025\u76f8\u6295\uff0c\u96be\u901a\u8fd8\u8981\u8bb2\u7a76\u4ec0\u4e48\u5fcc\u907f\u4e0d\u6210\uff1f\u6bd4\u5982\u4f60\u4eec\uff0c\u4f60\u4eec\u548c\u4e8e\u8001\u4f2f \u7d20\u4e0d\u76f8\u8bc6\uff0c\u4e0d\u662f\u4e5f\u59d1\u8eb2\u5230\u8fd9\u91cc\u6765\u4e48\uff1f\u6211\u5230\u5e08\u5144\u8863\u4e2d\u6682\u65f6\u501f\u4f4f\uff0c\u53c8\u6709\u4ec0\u4e48\u4e0d\u53ef\u4ee5\uff1f\u201d\u539f\u6765\u5979\u662f \u8bef\u4f1a\u6c5f\u6d77\u5929\u5403\u918b\uff0c\u8bdd\u4f3c\u8fde\u73e0\uff0c\u7b80\u76f4\u4e0d\u5bb9\u6c5f\u6d77\u5929\u6709\u8fa9\u767d\u7684\u4f59\u5730\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6da8\u7ea2\u4e86\u8138\uff0c\u4ed6\u7d20\u6765\u62d9\u4e0e\u8a00\u8f9e\uff0c\u660e\u77e5\u6b27\u9633\u5a49\u8bef\u4f1a\uff0c\u5374\u53c8\u4e0d\u597d\u76f4\u8bf4\uff0c\u6211\u4e0d\u662f\u8fd9\u4e2a\u610f \u601d\uff0c\u6211\u51b3\u4e0d\u4f1a\u5992\u5fcc\u4f60\u7684\u5e08\u5144\u3002\u201d\u53ea\u597d\u4f4e\u4e0b\u4e86\u5934\uff0c\u542c\u5979\u8bc9\u8bf4\u3002 \u3000\u3000\u6b27\u9633\u5a49\u7b11\u4e86\u4e00\u7b11\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5728\u6211\u7684\u540c\u95e8\u4e4b\u4e2d\uff0c\u53ea\u6709\u4e8e\u5e08\u5144\u662f\u4e2a\u6b63\u76f4\u7684\u4eba\uff0c\u4ed6\u4e5f\u66fe\u529d \u8fc7\u6211\uff0c\u8bf4\u90a3\u59d3\u6587\u7684\u4e0d\u662f\u597d\u4eba\uff0c\u53eb\u6211\u4e0d\u8981\u5ac1\u4ed6\u7684\uff0c\u5176\u5b9e\uff0c\u5c31\u7b97\u90a3\u59d3\u6587\u7684\u5341\u5168\u5341\u7f8e\uff0c\u6211\u4e5f\u4e0d\u4f1a\u5ac1 \u4ed6\u3002\u4ec0\u4e48\u7f18\u6545\uff0c\u6211\u4e0d\u8bf4\uff0c\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u5934\u4e00\u8df3\uff0c\u6015\u5979\u518d\u8bf4\u4e0b\u53bb\uff0c\u66f4\u4e3a\u9732\u9aa8\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u4e8b\u5148\u6ca1\u6709\u548c\u5e08\u5144\u5546\u91cf\u8fc7 \u5417\uff1f\u4f60\u4eca\u5929\u89c1\u8fc7\u4e86\u4ed6\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u9053\uff1a\u201c\u6211\u662f\u6628\u665a\u624d\u548c\u59d0\u59d0\u60f3\u51fa\u8fd9\u4e2a\u529e\u6cd5\u7684\uff0c\u7531\u59d0\u59d0\u4ee3\u5ac1\uff0c\u6211\u624d\u6562\u79c1\u9003\u51fa\u6765\u3002\u54ea\u6709 \u65f6\u95f4\u53bb\u89c1\u4e8e\u5e08\u5144\u5546\u91cf\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7981\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u65e2\u7136\u4f60\u4eec\u77e5\u9053\u90a3\u59d3\u6587\u7684\u4e0d\u662f\u597d\u4eba\uff0c\u4e3a\u4f55\u4f60\u59d0\u59d0\u53c8\u80af\u5ac1\u4ed6\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u59d0\u59d0\u6700\u8fd1\u6709\u4ef6\u5931\u610f\u7684\u4e8b\u60c5\uff0c\u4e0d\u4fbf\u5bf9\u4f60\u8a00\u8bf4\uff0c\u603b\u4e4b\u5979\u662f\u4f24\u5fc3 \u900f\u4e86\uff0c\u5979\u7684\u7891\u6c14\u53c8\u4e0e\u6211\u5927\u5927\u4e0d\u540c\uff0c\u5979\u4e00\u6765\u4e3a\u4e86\u6211\u7684\u7f18\u6545\uff0c\u4e8c\u6765\u5728\u5931\u610f\u4e4b\u4f59\uff0c\u4e5f\u60f3\u968f\u4fbf\u5ac1\u4e2a\u4eba \u7b97\u4e86\u3002\u6211\u59d0\u59d0\u8bf4\uff0c\u90a3\u59d3\u6587\u7684\u867d\u7136\u4e0d\u662f\u597d\u4eba\uff0c\u6b66\u529f\u5374\u8fd8\u5f53\u771f\u4e0d\u9519\uff0c\u6211\u4e5f\u4e0d\u662f\u4ec0\u4e48\u6b63\u6d3e\u51fa\u8eab\uff0c\u6211 \u5ac1\u4e86\u4ed6\uff0c\u4efb\u4ed6\u80e1\u4e3a\uff0c\u6211\u53ea\u6253\u7b97\u5077\u5b66\u4ed6\u7684\u5bb6\u4f20\u6b66\u529f\uff0c\u5c06\u6765\uff0c\u5c06\u6765\u4e5f\u597d\u51fa\u4e00\u53e3\u6c14\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c \u4f3c\u4e4e\u6709\u4ec0\u4e48\u96be\u8a00\u4e4b\u9690\uff0c\u53c8\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u60f3\u8bf4\u4e8e\u5c11\u9cb2\u7684\u4e8b\u60c5\uff0c\u4f46\u53c8\u6015\u4e8e\u5927\u9e4f\u96be\u8fc7\uff0c\u4e00\u65f6\u8e0c\u8e87\u672a\u51b3\uff0c\u6b27\u9633\u5a49\u53c8\u7ee7\u7eed\u60a6\u9053\uff1a \u201c\u6211\u53ea\u76fc\u671b\u8eb2\u8fc7\u4e86\uff0c\u5c31\u6ca1\u4e8b\u4e86\u3002\u504f\u504f\u4e8e\u5e08\u5144\u53c8\u4e0d\u5728\u5bb6\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u53bb\u4e86\u54ea\u91cc\uff0c\u627e\u4e0d\u5230\u4eba\u6253\u542c\u6211 \u5bb6\u4e2d\u7684\u6d88\u606f\uff0c\u6c5f\u76f8\u516c\uff0c\u4f60\u6765\u5f97\u6b63\u597d\u3002\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u4f60\u521a\u624d\u8bf4\u78b0\u89c1\u5c0f\u513f\uff0c\u662f\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u4ec0\u4e48\u65f6\u5019\uff0c\u662f\u5728\u4f60\u5230\u6b27\u9633\u5bb6\u4e4b \u524d\u8fd8\u662f\u4e4b\u540e\uff1f\u201d\u8fd9\u51e0\u4e2a\u95ee\u9898\uff0c\u4ed6\u65e9\u5df2\u60f3\u95ee\u7684\u4e86\uff0c\u53ea\u662f\u88ab\u6b27\u9633\u5a49\u51fa\u6765\u4e00\u6253\u5c94\uff0c\u73b0\u5728\u65b9\u6709\u673a\u4f1a\u63d0 \u51fa\u6765\u3002 \u3000\u3000\u5230\u4e86\u6b64\u65f6\uff0c\u6c5f\u6d77\u5929\u81ea\u662f\u4e0d\u5f97\u4e0d\u8bf4\uff1a\u201c\u6211\u548c\u4ee4\u90ce\u4e43\u662f\u5728\u9053\u4e0a\u76f8\u9022\uff0c\u6b63\u662f\u4ed6\u9080\u6211\u53bb\u559d\u6b27\u9633\u59d1 \u5a18\u7684\u559c\u9152\u7684\u3002\u201d \u3000\u3000\u6b27\u9633\u5a49\u53cc\u7709\u4e00\u76b1\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6b32\u5e08\u5144\u66fe\u5411\u6211\u90d1\u91cd\u8bf4\u8fc7\uff0c\u8bf4\u662f\u5047\u82e5\u6211\u4e0d\u4f9d\u6240\u529d\uff0c \u5ac1\u90a3\u59d3\u6587\u7684\u8bdd\uff0c\u4ed6\u662f\u51b3\u4e0d\u4f1a\u6765\u559d\u6211\u7684\u559c\u9152\u7684\uff0c\u600e\u4e48\u4ed6\u53c8\u6765\u4e86\uff1f\u201d\u540c\u65f6\u4e5f\u6709\u70b9\u5931\u671b\uff0c\u539f\u6765\u6c5f\u6d77 \u5929\u5e76\u4e0d\u662f\u4e13\u6210\u6765\u63a2\u8bbf\u6211\uff0c\u800c\u662f\u4e8e\u5c11\u9cb2\u9080\u4ed6\u7684\u3002\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u4e5f\u76b1\u8d77\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u4eca\u65e9\u51fa\u95e8\u7684\u65f6\u5019\uff0c\u4e5f\u6ca1\u6709\u8bf4\u662f\u53bb\u559d\u559c\u9152\uff0c\u54fc\uff0c\u4ed6\u771f\u662f\u80e1 \u95f9\uff0c\u4ed6\u51fa\u95e8\u6ca1\u591a\u4e45\uff0c\u6b27\u9633\u59d1\u5a29\u5c31\u6765\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\uff0c\u6c5f\u6d77\u5929\u5c06\u78b0\u89c1\u4e8e\u5c11\u9cb2\u7684\u60c5\u5f62\uff0c\u8bf4\u4e86\u4e00\u904d\uff0c\u95ee\u9053\uff1a\u201c\u4e8e\u8001\u4f2f\u53ef\u66fe\u771f\u662f\u89c1\u8fc7\u5bb6\u7236\u5417\uff1f\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u771f\u4e0d\u660e\u767d\uff0c\u8fd9\u755c\u751f\u4e3a\u4f55\u8981\u5bf9\u4f60\u7f16\u9020\u8c0e\u8a00\u3002\u4e0d\u9519\uff0c\u6211\u548c\u4ee4\u5c0a \u662f\u66fe\u6709\u8fc7\u6570\u9762\u4e4b\u7f18\uff0c\u4e0d\u8fc7\u81ea\u4ece\u90a3\u6b21\u5728\u5343\u969c\u576a\u4e4b\u4f1a\u5206\u624b\u4ee5\u540e\uff0c\u5c31\u518d\u4e5f\u8bbe\u6709\u89c1\u8fc7\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u90a3\u4e48\u8001\u6015\u4e1d\u6beb\u4e0d\u77e5\u5bb6\u7236\u7684\u6d88\u606f\u5417\uff1f\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8bb0\u8d77\u6765\u4e86\uff0c\u53bb\u5e74\u6709\u4e00\u4e2a\u670b\u53cb\u4ece\u9752\u6d77\u56de\u6765\uff0c\u8bf4\u662f\u66fe\u5728\u767d\u6559\u6559 \u738b\u7684\u9102\u514b\u6c81\u5bab\u89c1\u8fc7\u4ee4\u5c0a\uff0c\u90a3\u65f6\u4ee4\u5c0a\u6b63\u5728\u6559\u738b\u7684\u5bab\u4e2d\u4f5c\u5ba2\u3002\u6211\u7684\u670b\u53cb\u662f\u7ed9\u6559\u738b\u8fd0\u836f\u6750\u7684\uff0c\u591f\u4e0d \u4e0a\u548c\u4ee4\u5c0a\u540c\u5e2d\uff0c\u5f53\u65f6\u4e5f\u6ca1\u6709\u4ed4\u7ec6\u6253\u542c\uff0c\u6211\u6240\u77e5\u9053\u7684\u6d88\u606f\uff0c\u5c31\u662f\u8fd9\u4e48\u591a\u4e86\uff0c\u5c0f\u513f\u66fe\u542c\u6211\u8bf4\u8fc7\u8fd9 \u4ef6\u4e8b\uff0c\u56e0\u6b64\u4ed6\u624d\u77e5\u9053\u6211\u4e0e\u4ee4\u5c0a\u662f\u719f\u8bc6\u7684\uff0c\u6c5f\u5c0f\u4fa0\uff0c\u540e\u6765\u600e\u4e48\u6837\uff1f\u4f60\u4eec\u53bb\u548c\u559c\u9152\uff0c\u53ef\u6709\u95f9\u51fa\u4e8b \u6765\uff1f\u201d' \u3000\u3000\u6b27\u9633\u5a49\u4e5f\u7126\u6025\u5730\u671b\u7740\u6c5f\u6d77\u5929\uff0c\u4e8e\u5927\u9e4f\u4e0d\u660e\u767d\uff0c\u5979\u5374\u662f\u731c\u5230\u4e86\u51e0\u5206\uff0c\u5fc3\u60f3\uff1a\u201c\u83ab\u975e\u4e8e\u5e08\u5144 \u5df2\u8bc6\u5f97\u4e86\u6211\u7684\u5fc3\u4e8b\uff0c\u77e5\u9053\u6211\u662f\u559c\u6b22\u6c5f\u76f8\u516c\uff0c\u6240\u4ee5\u4ed6\u624d\u5f15\u4ed6\u524d\u5f80\u3002\u201d\u53ef\u601c\u6b27\u9633\u5a49\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d \u77e5\u5176\u4e8c\uff0c\u76f4\u5230\u5982\u4eca\uff0c\u5979\u8fd8\u4e0d\u77e5\u9053\u4e8e\u5c11\u9cb2\u5bf9\u5979\u7684\u539a\u7231\u6df1\u60c5\uff0c\u4f46\u66f4\u53ef\u601c\u7684\u8fd8\u662f\u4e8e\u5c11\u9cb2\uff0c\u4ed6\u4e3a\u5fc3\u4e0a \u4eba\u62fc\u4e86\u6027\u547d\uff0c\u5fc3\u4e0a\u4eba\u5374\u4e0d\u77e5\u9053\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u91cc\u6697\u6697\u53f9\u6c14\uff0c\u4ed6\u4e00\u76f4\u5728\u8e0c\u8e87\uff0c\u4e00\u76f4\u5728\u62d6\u5ef6\u7740\u4e0d\u5fcd\u8bf4\u51fa\u4e8e\u5c11\u9cb2\u7684\u6b7b\u8baf\u7684\uff0c\u8fd9\u65f6\u5df2 \u65e0\u6cd5\u518d\u9690\u7792\u4e86\uff0c\u4ed6\u5583\u5583\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5230\u4e86\u6b27\u9633\u59d1\u5a18\u5bb6\u4e2d\uff0c\u6b27\u9633\u59d1\u5a18\uff0c\u4e0d\uff0c\u6b27\u9633\u59d1\u5a18\u7684\u59d0\u59d0\u6b63 \u5728\u548c\u65b0\u90ce\u62dc\u5802\uff0c\u4e8e\u5927\u54e5\u4f7f\u51fa\u70c8\u7130\u5f39\uff0c\u5c06\u90a3\u65b0\u90ce\u6253\u5f97\u91cd\u4f24\u4e86\uff01\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u672c\u6765\u5df2\u662f\u4e00\u76f4\u63d0\u5fc3\u540a\u80c6\uff0c\u8fd9\u65f6\u66f4\u662f\u5927\u5403\u4e00\u60ca\uff0c\u731b\u5730\u8df3\u8d77\u6765\u9053\uff1a\u201c\u8fd9\u755c\u751f\uff0c\u8fd9\u755c\u751f \u679c\u7136\u95ef\u51fa\u5927\u7978\u6765\u4e86\uff0c\u602a\u4e0d\u5f97\uff0c\u602a\u4e0d\u5f97\u4ed6\u7ed9\u6211\u7559\u4e0b\u4e86\u8fd9\u6837\u7684\u4fe1\uff01'\u201d \u3000\u3000\u6b27\u9633\u5a49\u9053\uff1a\u201c\u54e6\uff0c\u4ed6\u7559\u4e0b\u4e86\u4e66\u4fe1\uff1f\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u554a\uff0c\u602a\u4e0d\u5f97\u6211\u5230\u6765\u7684\u65f6\u5019\uff0c\u770b\u89c1\u4f60\u6101\u7709\u4e0d \u5c55\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u8ba8\u538c\u6211\u6765\u5462\uff1f\u6562\u60c5\u6562\u60c5\u2026\u2026\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u9053\uff1a\u201c\u4ed6\u7559\u4e0b\u4e00\u5c01\u4fe1\uff0c\u53eb\u627e\u8d76\u5feb\u79bb\u5bb6\uff0c\u5230\u4eac\u57ce\u53bb\u6295\u9760\u9547\u8fdc\u9556\u5c40\u3002\u6211\u83ab\u540d\u5176\u5999\uff0c\u5374 \u539f\u6765\u4ed6\u662f\u65e9\u5df2\u51c6\u5907\u597d\u8981\u53bb\u95ef\u7978\u7684\u4e86\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u9547\u8fdc\u9556\u5c40\uff1f\u90a3\u4e0d\u662f\u94c1\u9e33\u9e2f\u592b\u5987\u592b\u5987\u5f00\u7684\u5417\uff1f\u201d\u4e8e\u5927\u9e4f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c0f\u513f\u5728 \u9547\u8fdc\u9556\u5c40\u91cc\u5f53\u9556\u5e08\uff0c\u6211\u548c\u97e9\u6c0f\u5927\u5987\u4e5f\u662f\u6709\u51e0\u5341\u5e74\u4ea4\u60c5\u7684\u3002\u201d\u539f\u6765\u4ed6\u8fd8\u672a\u77e5\u9053\u9547\u8fdc\u9556\u5c40\u5df2\u7ecf\u5012 \u95ed\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5230\u5e95\u5e74\u7eaa\u5927\u8f7b\uff0c\u9605\u5386\u65e0\u591a\uff0c\u601d\u8651\u4e0d\u5468\uff0c\u8fd9\u65f6\u65b9\u59cb\u731b\u5730\u60f3\u8d77\uff1a\u201c\u4e8e\u5c11\u9cb2\u4f24\u4e86\u6b27\u9633\u5bb6 \u7684\u65b0\u5973\u5a7f\uff0c\u6b27\u9633\u4e8c\u5a18\u600e\u4f1a\u653e\u8fc7\u4ed6\u7684\u7236\u4eb2\uff0c\u8fdf\u65e9\u5fc5\u4f1a\u6765\u627e\u4e8e\u5bb6\u7684\u9ebb\u70e6\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u73b0\u5728\u6025\u4e8e\u8ffd\u6355 \u81ea\u5df1\u4e00\u65f6\u65e0\u6687\u987e\u53ca\u800c\u5df2\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u60f3\u5230\u6b64\u5904\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u54b1\u4eec\u8d76\u5feb\u9003\u5427\uff01\u54ce\u5440\uff0c\u4e0d\u77e5\u9053\u6211\u7684\u59ec \u4f2f\u4f2f\u597d\u4e86\u6ca1\u6709\uff0c\u5f85\u6211\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u8d8a\u53d1\u60ca\u614c\uff0c\u8fde\u5fd9\u4e00\u628a\u626f\u4f4f\u4ed6\u9053\uff1a\u201c\u6c5f\u5c0f\u4fa0\uff0c\u4e14\u6162\uff0c\u4e14\u6162\uff0c\u5c0f\u513f\u95ef\u4e86\u5927\u7978\uff0c\u73b0\u5728\u5230 \u5e95\u600e\u4e48\u6837\u4e86\uff0c\u4f60\uff0c\u4f60\u8d76\u5feb\u8bf4\u5440\uff01'\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6ee1\u5934\u5927\u6c57\uff0c\u5450\u5450\u8bf4\u9053\uff1a\u201c\u540e\u6765\uff0c\u540e\u6765\u2026\u2026\u54ce\u5440\uff0c\u8bf4\u6765\u8bdd\u957f\uff0c\u8fd8\u662f\u5148\u9003\u5427\uff01\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u53ea\u8bf4\u4e00\u53e5\uff0c\u5c0f\u513f\u5230\u5e95\u662f\u6b7b\u662f\u6d3b\uff1f\u201d\u6c5f\u6d77\u5929\u54ac\u7d27\u7259\u6839\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9 \u4e2a\uff0c\u8fd9\u4e2a-----\u540e\u6765\uff0c\u540e\u7c73------\u4ed6\u662f\uff0c\u4ed6\u662f----\u201d\u201c\u6b7b\u4e86\u201d\u8fd9\u4e24\u5341\u5b57\u6b63\u5728\u820c\u5c16\u6253\u6eda\uff0c\u8fd8\u672a \u81ea\u66fe\u8bf4\u5f97\u51fa\u6765\uff0c\u5ffd\u542c\u5f97\u730e\u72ac\u201d\u6c6a\u6c6a\u201d\u7684\u72c2\u5420\u58f0\uff0c\u63a5\u7740\u4fbf\u542c\u5f97\u6587\u5ef7\u74a7\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u59ec\u6653\u98ce\u548c \u90a3\u59d3\u6c5f\u7684\u5c0f\u8d3c\u96be\u9053\u5c31\u8eb2\u5728\u8fd9\u9644\u8fd1\uff1f\u54a6\uff0c\u8fd9\u9644\u8fd1\u6ca1\u6709\u4ec0\u4e48\u53ef\u4ee5\u85cf\u8eab\u4e4b\u5904\u5440\uff01\u201d \u3000\u3000\u539f\u6765\u6b27\u9633\u4f2f\u548c\u517b\u6709\u4e24\u5934\u5410\u9c81\u756a\u51fa\u4ea7\u7684\u5f02\u79cd\u730e\u72ac\uff0c\u55c5\u89c9\u6781\u7075\uff0c\u4ed6\u4eec\u662f\u5e26\u4e86\u730e\u72ac\u6765\u8ffd\u8e2a\u7684\uff0c \u730e\u72ac\u4ece\u59ec\u3001\u6c5f\u4e8c\u4eba\u4e00\u8def\u4e0a\u6240\u7559\u4e0b\u7684\u6c14\u5473\uff0c\u8ffd\u5230\u4e86\u8fd9\u91cc\uff0c\u56e0\u4e3a\u53d7\u963b\u4e8e\u7011\u5e03\uff0c\u8df3\u4e0d\u4e0a\u60ac\u5ca9\uff0c\u6240\u4ee5 \u72c2\u5420\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u53eb\u9053\uff1a\u201c\u5440\uff0c\u5bf9\u4e86\uff0c\u8fd9\u4e0a\u9762\u6709\u4e2a\u4eba\u5bb6\uff0c\u6b63\u662f\u4e8e\u5927\u9e4f\u7236\u5b50\u7684\u6240\u5c45\uff01\u201d\u6587\u5ef7\u74a7\u9053\uff1a \u201c\u54ea\u4e2a\u4e8e\u5927\u9e4f\uff1f\u201d\u6b27\u9633\u4e8c\u5a18\u9053\uff1a\u201c\u5c31\u662f\u521a\u624d\u4f24\u4e86\u4ee4\u4f84\u7684\u90a3\u4e2a\u5c0f\u8d3c\u7684\u7236\u4eb2\uff01\u54fc\uff0c\u54fc\uff01\u4e0d\u7528\u95ee \u4e86\uff0c\u4ed6\u4eec\u4e00\u5b9a\u662f\u548c\u4e8e\u5bb6\u65e9\u6709\u52fe\u7ed3\u4e86\u7684\uff0c\u73b0\u5728\u4e5f\u4e00\u5b9a\u662f\u8eb2\u5728\u4e8e\u5bb6\uff01\u4f60\u4eec\u8ddf\u6211\u6765\u5427\uff0c\u6211\u8ba4\u5f97 \u8def\uff01\u201d\u4e8e\u5927\u9e4f\uff0c\u6c5f\u6d77\u5929\u7684\u542c\u89c9\u867d\u6bd4\u4e0d\u4e0a\u59ec\u6653\u98ce\uff0c\u4f46\u4ed6\u4eec\u6b66\u529f\u6df1\u6e5b\uff0c\u5230\u5e95\u51fa\u5e38\u4eba\u7075\u5f97\u591a\uff0c\u6b27\u9633 \u4e8c\u5a18\u4e0e\u6587\u5ef7\u74a7\u5728\u5ca9\u4e0b\u7684\u8bdd\u8bed\uff0c\u4e00\u53e5\u4e00\u53e5\uff0c\u4ed6\u4eec\u90fd\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u4e8e\u5927\u9e4f\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u6682\u4e14\u8eb2\u907f\uff0c\u5f85\u6211\u5e94\u4ed8\uff0c\u7792\u5f97\u8fc7\u53bb\u6700\u597d\uff0c\u5018\u82e5\u5e94\u4ed8\u4e0d\u4e86\uff0c\u6c5f\u5c0f\u4fa0 \u4f60\u518d\u51fa\u6765\u3002\u201d \u3000\u3000\u6587\u5ef7\u74a7\u4ed6\u4eec\u6765\u5f97\u5feb\u6781\u4e86\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u53ea\u542c\u5f97\u201d\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u90a3\u4e24\u6247\u5927\u95e8\u5df2\u7ecf\u9707\u584c\u3002\u6b27\u9633 \u4e8c\u5a18\u4e00\u9a6c\u5f53\u5148\uff0c\u6012\u6c14\u51b2\u51b2\u5730\u8d70\u4e86\u8fdb\u6765\uff0c\u51b2\u7740\u4e8e\u5927\u9e4f\u4fbf\u559d\u9053\uff1a\u201c\u4f60\u7684\u513f\u5b50\u5728\u6211\u5bb6\u80e1\u95f9\uff0c\u4f60\u77e5\u5230 \u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6b27\u9633\u4f2f\u548c\u9053\uff1a\u201c\u4e8c\u5ac2\u4e14\u6162\u52a8\u624b\uff0c\u8001\u4e8e\uff0c\u6211\u548c\u4f60\u76f8\u8bc6\u591a\u5e74\uff0c\u597d\u6b79\u4e5f\u7b97\u4e2a\u670b\u53cb\uff0c\u4f60\u513f\u5b50\u7684\u4e8b \u60c5\uff0c\u6211\u4e14\u6682\u4e0d\u8ffd\u7a76\uff0c\u6211\u5148\u95ee\u4f60\uff0c\u6709\u4e00\u4e2a\u59ec\u6653\u98ce\u4f60\u662f\u8ba4\u8bc6\u7684\uff0c\u8fd8\u6709\u4e00\u4e2a\u59d3\u6c5f\u7684\u5c0f\u5b50\uff0c\u4ed6\u4eec\u4e8c\u4eba \u662f\u4e0d\u662f\u85cf\u5728\u4f60\u5bb6\uff0c\u5feb\u5feb\u4ea4\u51fa\u6765\uff0c\u6216\u8005\u6211\u8fd8\u53ef\u4ee5\u9976\u6055\u4f60\u7eb5\u5b50\u884c\u51f6\u4e4b\u7f6a\uff01\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u53cc\u773c\u706b\u7ea2\uff0c\u6da9\u58f0\u8bf4\u9053\uff1a\u201c\u6b27\u9633\u8001\u5927\uff0c\u6211\u90a3\u4e0d\u8096\u7684\u513f\u5b50\u5462\uff1f\u8bf7\u4f60\u653e\u4ed6\u56de\u6765\uff0c\u6211\u7ed9\u4f60 \u8d1f\u8346\u8d54\u7f6a\u3002\u201d \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7684\u513f\u5b50\u95ef\u4e0b\u8fd9\u7b49\u5927\u7978\uff0c\u8fd8\u60f3\u6d3b\u7740\u56de\u6765\u5417\uff1f\u201d \u3000\u3000\u4e8e\u5927\u9e4f\u53cc\u773c\u7ffb\u767d\uff0c\u8e66\u5730\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u53eb\u9053\uff1a\u201c\u662f\u4f60\u6740\u4e86\u4ed6\u5417\uff1f\u201d\u6b27\u9633\u4e8c\u5a18\u51b7\u7b11\u9053\uff1a\u201c\u662f \u6211\u6740\u7684\uff0c\u53c8\u600e\u4e48\u6837\uff1f\u201d\u5176\u5b9e\u4e8e\u5c11\u9cb2\u4e43\u662f\u53d7\u4f24\u4e4b\u540e\uff0c\u7528\u5315\u9996\u81ea\u6740\u7684\u3002\u4e0d\u8fc7\uff0c\u6b27\u9633\u4e8c\u5a18\u6068\u6781\u4e86\u4e8e \u5c11\u9cb2\u6405\u574f\u4e86\u5979\u5973\u513f\u7684\u5a5a\u4e8b\uff0c\u6709\u610f\u6c14\u6c14\u4ed6\u7684\u7236\u4eb2\u3002 \u3000\u3000\u4e8e\u5927\u9e4f\u5927\u543c\u4e00\u58f0\uff1a\u201c\u597d\u4e2a\u6cfc\u5987\uff0c\u6211\u4e0e\u4f60\u62fc\u4e86\uff01\u201d\u547c\u7684\u4e00\u62f3\u6363\u51fa\uff0c\u4e8e\u5927\u9e4f\u662f\u5c11\u6797\u6d3e\u7684\u4fd7\u5bb6 \u9ad8\u624b\uff0c\u4f7f\u51fa\u4e86\u5c11\u6797\u795e\u62f3\uff0c\u864e\u864e\u751f\u98ce\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u5355\u638c\u4e00\u5e26\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u6b7b\u6d3b\u7684\u8001\u5339\u592b\uff0c\u5c45\u7136\u8fd8\u60f3\u62fc\u547d\u5440\uff1f\u201d\u6b27\u9633\u4e8c\u5a18\u81ea\u4ee5 \u4e3a\u672c\u9886\u8981\u80dc\u8fc7\u4e8e\u5927\u9e4f\uff0c\u6240\u4ee5\u5e76\u672a\u5c06\u4ed6\u653e\u5728\u5fc3\u4e0a\u3002\u54ea\u77e5\u4e8e\u5927\u9e4f\u8c41\u51fa\u4e86\u6027\u547d\uff0c\u8fd9\u4e00\u62f3\u4e43\u662f\u4ed6\u6bd5\u751f \u529f\u529b\u4e4b\u6240\u805a\uff0c\u6b27\u9633\u4e8c\u5a18\u7684\u201d\u5378\u529b\u5316\u52b2\u201d\u529f\u592b\uff0c\u5374\u672a\u80fd\u5c06\u4ed6\u7684\u62f3\u52bf\u5b8c\u5168\u5316\u89e3\uff0c\u88ab\u4ed6\u731b\u529b\u4e00\u51b2\uff0c \u8dcc\u4e86\u4e2a\u56db\u811a\u671d\u5929\u3002 \u3000\u3000\u6587\u5ef7\u74a7\u76b1\u7709\u9053\uff1a\u201c\u4eb2\u5bb6\uff0c\u4ed6\u53d1\u75af\u4e86\uff0c\u4f55\u5fc5\u8fd8\u548c\u4ed6\u8bb2\u4ec0\u4e48\u4ea4\u60c5\uff0c\u5f0a\u4e86\u4ed6\uff0c\u54b1\u4eec\u81ea\u5df1\u641c\u4eba\uff01\u201d \u3000\u3000\u6b27\u9633\u4f2f\u548c\u6bd4\u8f83\u539a\u9053\u4e00\u4e9b\uff0c\u4e0d\u5fcd\u4eb2\u4e0b\u6740\u624b\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u5c31\u8bf7\u4eb2\u5bb6\u6210\u5168\u4e86\u4ed6\u5427\uff01\u201d \u3000\u3000\u6587\u5ef7\u74a7\u65bd\u5c55\u4e86\u201c\u4e09\u8c61\u89c4\u5143\u201d\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u4e00\u638c\u62cd\u51fa\uff0c\u62f3\u638c\u76f8\u4ea4\uff0c\u53ea\u542c\u5f97\u201c\u7830\u201d\u7684\u4e00\u58f0\u5de8 \u54cd\uff0c\u4e8e\u5927\u9e4f\u7acb\u8db3\u4e0d\u7a33\uff0c\u5728\u5730\u4e0a\u6253\u4e86\u5341\u51e0\u4e2a\u76d8\u65cb\uff0c\u6709\u5982\u98ce\u4e2d\u4e4b\u70db\uff0c\u6447\u6447\u6b32\u5760\u3002\u4e0d\u8fc7\uff0c\u6587\u5ef7\u74a7\u8fd9 \u4e00\u4e5f\u8fd8\u672a\u80fd\u7acb\u5373\u5f0a\u4e86\u4ed6\u7684\u6027\u547d\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u4e00\u8dc3\u800c\u8d77\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u8001\u8d3c\uff0c\u770b\u4f60\u8fd8\u6562\u901e\u5f3a\uff1f\u201d\u5979\u7684\u672c\u9886\u7a76\u7adf\u8981\u6bd4\u4e8e\u5927\u9e4f\u80dc\u8fc7 \u4e00\u7b79\uff0c\u4f55\u51b5\u73b0\u5728\u662f\u4e58\u5371\u8fdb\u88ad\uff0c\u53ea\u542c\u5f97\u201c\u5361\u5693\u201d\u4e00\u58f0\u8f7b\u54cd\uff0c\u6b27\u9633\u4e8c\u5a18\u4ee5\u95ea\u7535\u822c\u7684\u624b\u6cd5\uff0c\u767b\u65f6\u628a \u4e8e\u5927\u9e4f\u7684\u4e00\u6761\u624b\u81c2\uff0c\u62d7\u62d6\u4e86\u81fc\u3002 \u3000\u3000\u773c\u770b\u4e8e\u5927\u9e4f\u5c31\u8981\u6027\u547d\u4e0d\u4fdd\uff0c\u6b27\u9633\u5a49\u5ffd\u5730\u4e00\u58f0\u5c16\u53eb\uff0c\u51b2\u4e86\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u5a18\uff0c\u4f4f\u624b\uff0c\u5426\u5219 \u6211\u6b7b\u5728\u4f60\u7684\u9762\u524d\uff01\u201d\u53ea\u89c1\u5979\u9b13\u4e91\u9022\u4e71\uff0c\u6cea\u73e0\u6676\u83b9\uff0c\u624b\u4e2d\u62ff\u7740\u4e00\u628a\u5315\u9996\uff0c\u5315\u9996\u6b63\u6307\u7740\u81ea\u5df1\u7684\u80f8 \u819b\u3002 \u3000\u3000\u4e03\u9634\u6559\u548c\u5929\u9b54\u6559\u7d20\u6709\u5f80\u6765\uff0c\u53d9\u8d77\u8f88\u5206\uff0c\u4e03\u9634\u6559\u4e3b\u9634\u5723\u59d1\u8fd8\u662f\u5929\u9b54\u6559\u4e3b\u7684\u957f\u8f88\uff0c\u5f53\u6b27\u9633\u5a49 \u8fd8\u5728\u9634\u5723\u59d1\u95e8\u4e0b\u4e60\u6280\u4e4b\u65f6\uff0c\u6709\u4e00\u6b21\u6587\u5ef7\u74a7\u53d4\u4f84\u5949\u4e86\u5929\u9b54\u6559\u6559\u4e3b\u4e4b\u547d\uff0c\u9001\u793c\u7ed9\u4e03\u9634\u6559\u4e3b\u9634\u5723 \u59d1\uff0c\u5c31\u5728\u90a3\u6b21\uff0c\u4ed6\u4eec\u8ba4\u5f97\u4e86\u6b27\u9633\u5a49\uff0c\u6240\u4ee5\u540e\u6765\u6587\u5ef7\u74a7\u4ee3\u4f84\u513f\u5411\u6b27\u9633\u4f2f\u548c\u6c42\u4eb2\uff0c\u867d\u7136\u77e5\u9053\u4ed6\u8fd8 \u6709\u4e2a\u5927\u5973\u513f\uff0c\u4f46\u6c42\u7684\u4ecd\u7136\u662f\u6b21\u5973\u6b27\u9633\u5a49\u3002\u5728\u4eca\u65e5\u62dc\u5802\u6210\u4eb2\u7684\u65f6\u5019\uff0c\u6b27\u9633\u4e8c\u5a18\u4e00\u76f4\u5c31\u5728\u63d0\u5fc3\u540a \u80c6\uff0c\u6015\u4ed6\u4eec\u53d4\u4f84\u770b\u7834\u7684\u4e86\u3002 \u3000\u3000\u6587\u5ef7\u74a7\u679c\u7136\u5927\u4e3a\u5947\u602a\uff0c\u54a6\u4e86\u4e00\u58f0\uff0c\u7741\u5927\u4e86\u773c\u775b\uff0c\u671b\u7740\u6b27\u9633\u5a49\uff0c\u53c8\u671b\u671b\u6b27\u9633\u4e8c\u5a18\u3002\u8fd9\u5239\u90a3 \u95f4\uff0c\u6b27\u9633\u4e8c\u5a18\u5c34\u5c2c\u4e4b\u6781\uff0c\u53c8\u662f\u6c14\u8111\uff0c\u53c8\u662f\u60ca\u60f6\uff0c\u53c8\u662f\u601c\u60dc\uff0c\u4f46\u5979\u6700\u75bc\u7231\u8fd9\u4e2a\u4e0b\u5973\u513f\uff0c\u867d\u662f\u6c14 \u8111\uff0c\u4e5f\u4e0d\u7531\u5f97\u4e0d\u653e\u5f00\u4e86\u4e8e\u5927\u9e4f\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u91ce\u4e2b\u5934\u6c14\u6b7b\u6211\u4e86\uff0c\u5feb\u5feb\u653e\u4e0b\u5200\u5b50\uff0c\u8fc7\u6765\u89c1\u8fc7\u6587\u4f2f\u4f2f\u3002\u201d\u6b27\u9633\u5a49 \u9053\uff1a\u201c\u4f60\u4eec\u9000\u51fa\u53bb\uff0c\u6211\u8ddf\u4f60\u56de\u5bb6\uff0c\u5230\u4e86\u5bb6\u4e2d\uff0c\u4efb\u51ed\u4f60\u4eec\u5904\u7f6e\uff0c\u5728\u672a\u56de\u5bb6\u4e4b\u524d\uff0c\u6211\u8fd9\u5200\u5b50\u662f\u51b3 \u4e0d\u653e\u4e0b\u7684\u3002\u201d \u3000\u3000\u6587\u5ef7\u74a7\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4eb2\u5bb6\u6bcd\uff0c\u8fd9\u4e8b\u60c5\u600e\u4e48\u529e\uff1f\u201d\u6b27\u9633\u4e8c\u5a18\u5428\u8db3\u9053\uff1a\u201c\u6211\u53ea\u5f53\u5f53\u521d\u6ca1\u6709\u751f \u8fd9\u4e2a\u5973\u513f\uff0c\u8fd9\u5973\u513f\u6211\u4e0d\u60f3\u8981\u4e86\uff0c\u968f\u4fbf\u4f60\u600e\u4e48\u529e\u5427\uff1f\u201d\u8fd9\u5f53\u7136\u662f\u6c14\u6124\u4e4b\u8a00\u3002\u6587\u5ef7\u74a7\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u4eb2\u5bb6\u6bcd\uff0c\u4f60\u65e0\u987b\u7740\u91d1\uff0c\u5973\u513f\u8fd8\u662f\u7531\u4f60\u9886\u56de\u53bb\uff0c\u7136\u540e\u54b1\u4eec\u518d\u597d\u597d\u5546\u91cf\u3002\u4e0d\u8fc7\uff0c\u8bf7\u4f60\u6055\u6211\u8981\u7a0d \u5fae\u65e0\u793c\u4e86\uff01\u201d\u3002 \u3000\u3000\u6b27\u9633\u60cb\u542c\u4ed6\u8bf4\u5230\u201d\u65e0\u793c\u201d\u4e8c\u5b57\uff0c\u5fc3\u5934\u4e00\u9547\uff0c\u5315\u9996\u4fbf\u5411\u80f8\u819b\u63d2\u4e0b\uff0c\u4f46\u662f\u6587\u5ef7\u74a7\u7684\u52a8\u4f5c\u6bd4\u5979 \u66f4\u5feb\uff0c\u53ea\u542c\u5f97\u201d\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u6b27\u9633\u5a49\u7684\u624b\u8155\u7a81\u7136\u4f3c\u7ed9\u9488\u523a\u4e86\u4e00\u4e0b\uff0c\u5315\u9996\u201d\u5f53\u5577\u201d\u843d\u5730\uff0c\u8eab\u5b50 \u4e5f\u7acb\u5373\u4e0d\u80fd\u52a8\u5f39\u3002\u539f\u6765\u662f\u7ed9\u6587\u5ef7\u74a7\u4ee5\u201d\u9694\u7a7a\u70b9\u7a74\u201d\u7684\u8d85\u5353\u795e\u529f\u5c01\u95ed\u4e86\u7a74\u9053\uff0c\u90a3\u5200\u5c16\u5212\u8fc7\uff0c\u867d \u7136\u672a\u6709\u63d2\u8fdb\u80f8\u819b\uff0c\u4e5f\u5212\u7834\u4e86\u5c11\u8bb8\u76ae\u8089\uff0c\u5df2\u6709\u59ca\u9c9c\u8840\u6c81\u51fa\u6765\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u4e0d\u7531\u5f97\u5931\u58f0\u9a87\u53eb\uff0c\u8fde\u5fd9\u62a2\u4e0a\u524d\u53bb\uff0c\u5c06\u5973\u513f\u62b1\u4f4f\uff0c\u5f85\u89c1\u5973\u513f\u53ea\u662f\u7565\u53d7\u4f24\u635f\uff0c\u8fd9\u624d \u5401\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5ffd\u5730\u4e00\u58f0\u5927\u559d\uff0c\u4e5f\u51b2\u4e86\u51fa\u6765\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u8fd8\u914d\u505a\u5979\u7684\u6bcd\u4eb2\u5417\uff1f\u201d\u5b9d\u5251\u4e00\u6325\uff0c\u5de6\u81c2 \u4ece\u638c\u5e95\u7a7f\u51fa\uff0c\u5c31\u8981\u6765\u62c9\u6b27\u9633\u5a49\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u5fcc\u60ee\u4ed6\u7684\u5b9d\u5251\uff0c\u614c\u4e0d\u8fed\u7684\u8fde\u5fd9\u6492\u624b\u540e\u9000\uff0c\u6b27\u9633\u4f2f\u548c\u5927\u6012\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u6562\u78b0 \u6211\u7684\u4f84\u5973\u513f\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7684\u672c\u610f\u662f\u66ff\u6b27\u9633\u5a49\u89e3\u5f00\u7a74\u9053\u7684\uff0c\u7ed9\u6b27\u9633\u4f2f\u548c\u8fd9\u4e48\u9661\u7136\u4e00\u9a82\uff0c\u53c8\u7f9e\u53c8\u6012\uff0c\u4e0d\u89c9\u8fdf\u7591 \u4e00\u4e0b\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6b27\u9633\u4f2f\u548c\u5df2\u662f\u4e00\u58f0\u5927\u559d\uff0c\u9739\u96f3\u795e\u638c\u4e0e\u96f7\u795e\u6307\u517c\u65bd\uff0c\u731b\u5730\u5411\u6c5f\u6d77\u5929\u653b \u5230\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6a2a\u5251\u4e00\u5c01\uff0c\u5927\u53eb\u9053\uff1a\u201c\u4e8e\u8001\u4f2f\u5feb\u8dd1\uff01\u201d\u6b27\u9633\u4f2f\u548c\u4e00\u638c\u62cd\u51fa\uff0c\u5c06\u6c5f\u6d77\u5929\u7684\u5251\u70b9\u9707 \u6b6a\uff0c\u6b27\u9633\u4e8c\u5a18\u4e5f\u5df2\u89e3\u4e0b\u8170\u5e26\uff0c\u5f53\u4f5c\u8f6f\u97ad\u6765\u4f7f\uff0c\u6765\u5377\u6c5f\u6d77\u5929\u7684\u5b9d\u5251\uff0c\u6c5f\u6d77\u5929\u4e00\u58f0\u5927\u559d\uff0c\u6296\u8d77\u4e00 \u6735\u5251\u82b1\uff0c\u4f7f\u51fa\u4e86\u201d\u8ffd\u98ce\u5251\u6cd5\u201d\u7684\u7edd\u62db\uff0c\u5f53\u771f\u662f\u6709\u5982\u8ffd\u98ce\u9010\u7535\uff0c\u5728\u5251\u5149\u95ea\u70c1\u4e4b\u4e0b\uff0c\u6b27\u9633\u4f2f\u548c\u4e0e \u6b27\u9633\u4e8c\u5a18\u90fd\u4f3c\u4e4e\u89c9\u5f97\u5251\u5c16\u523a\u5230\u4e86\u9762\u95e8\uff0c\u4f46\u542c\u5f97\u201c\u5636\u201d\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u6b27\u9633\u4e8c\u5a18\u7684\u90a3\u6761\u7ef8\u5e26\u5df2\u77ed \u4e86\u534a\u622a\uff0c\u4e24\u4eba\u90fd\u5fd9\u4e0d\u8fed\u7684\u540e\u9000\u3002 \u3000\u3000\u6587\u5ef7\u74a7\u51b7\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u8fd8\u6562\u901e\u5f3a\uff01\u201d\u4e00\u4e2a\u79fb\u5f62\u6362\u4f4d\uff0c\u731b\u7136\u95f4\u4fbf\u63a0\u5230\u4e86\u6c5f\u6d77\u5929\u7684\u80cc \u540e\uff0c\u5411\u6c5f\u6d77\u5929\u7684\u540e\u5fc3\u53d1\u638c\u5077\u88ad\u3002 \u3000\u3000\u8981\u77e5\u6c5f\u6d77\u5929\u7684\u6b66\u529f\u867d\u5f3a\uff0c\u4f46\u4e0e\u6b27\u9633\u4f2f\u548c\u6700\u591a\u4e5f\u4e0d\u5c31\u534a\u65a4\u516b\u4e24\uff0c\u52a0\u4e0a\u4e86\u4e00\u4e2a\u6b27\u9633\u4e8c\u5a18\uff0c\u4ed6 \u5df2\u662f\u5e94\u4ed8\u4e0d\u6613\uff0c\u4ed6\u4e4b\u6240\u4ee5\u80fd\u591f\u9707\u6151\u654c\u4eba\uff0c\u6709\u4e00\u5927\u534a\u8fd8\u662f\u9760\u5b9d\u5251\u7684\u5a01\u529b\uff0c\u5bf9\u65b9\u867d\u7136\u7ed9\u4ed6\u8feb\u9000\uff0c \u4f46\u8eab\u6cd5\u6b65\u6cd5\uff0c\u4e1d\u6beb\u4e0d\u4e71\uff0c\u4ecd\u662f\u84c4\u52bf\u5f85\u653b\u3002\u6545\u6b64\u6c5f\u6d77\u5929\u4ecd\u8981\u52a0\u610f\u63d0\u9632\uff0c\u5982\u4eca\u6587\u5ef7\u74a7\u7a81\u4ece\u8eab\u540e\u653b \u6765\uff0c\u6c5f\u6d77\u5929\u5982\u82e5\u8f6c\u8eab\u5e94\u4ed8\uff0c\u5b9a\u7136\u8981\u7ed9\u6b27\u9633\u4f2f\u548c\u4e58\u673a\u653b\u51fb\u3002\u8fd9\u5f62\u52bf\u5f53\u771f\u662f\u80cc\u8179\u53d7\u654c\uff0c\u5371\u9669\u975e\u5e38\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u6c5f\u6d77\u5929\u5df3\u662f\u65e0\u6687\u601d\u7d22\uff0c\u6b63\u8981\u62fc\u7740\u53d7\u6b27\u9633\u4f2f\u548c\u7684\u201c\u96f7\u795e\u6307\u201d\u6240\u4f24\uff0c\u56de\u8eab\u6765\u6321\u6587 \u5ef7\u74a7\u8fd9\u4e00\u638c\uff0c\u5ffd\u542c\u5f97\u4e8e\u5927\u9e4f\u4e00\u58f0\u5927\u543c\uff1a\u201c\u4f60\u4eec\u6740\u4e86\u6211\u7684\u513f\u5b50\uff0c\u6211\u4e5f\u4e0d\u60f3\u6d3b\u4e86\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c \u5c31\u548c\u8eab\u5411\u6587\u5ef7\u74a7\u649e\u53bb\u3002 \u3000\u3000\u53c8\u5ef7\u74a7\u6599\u4e0d\u5230\u4ed6\u7adf\u662f\u8fd9\u6837\u86ee\u6253\uff0c\u53ea\u597d\u5c06\u653b\u5411\u6c5f\u6d77\u5929\u90a3\u4e00\u638c\u64a4\u56de\u6765\u62a4\u8eab\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684 \u4e00\u58f0\uff0c\u4e24\u4eba\u5df2\u7ecf\u649e\u4e0a\uff0c\u6587\u5ef7\u74a7\u8fd9\u4e00\u638c\u8fd0\u8db3\u4e86\u5341\u6210\u529f\u529b\uff0c\u638c\u5fc3\u4e00\u6309\uff0c\u201c\u5580\u5580\u5580\u201d\u4e00\u7247\u58f0\u54cd\uff0c\u4e8e \u5927\u9e4f\u7684\u80f8\u9aa8\u808b\u9aa8\u5168\u90fd\u6298\u65ad\uff0c\u53ef\u662f\u6587\u5ef7\u74a7\u7ed9\u4ed6\u8fd9\u4e48\u62fc\u6b7b\u4e00\u649e\uff0c\u4e5f\u767b\u65f6\u8dcc\u4e86\u4e2a\u56db\u811a\u671d\u5929\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u56de\u8eab\u4e00\u5251\u523a\u4e0b\uff0c\u6587\u5ef7\u74a7\u5728\u5730\u4e0a\u6253\u4e86\u51e0\u4e2a\u5927\u7ffb\uff0c\u201c\u8f70\u9686\u201d\u4e00\u4e00\u58f0\uff0c\u7adf\u628a\u59ec\u6273\u6653\u98ce\u6240 \u5728\u7684\u90a3\u95f4\u9694\u623f\u7684\u864f\u95e8\u649e\u7834\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u6c5f\u6d77\u5929\u7684\u540e\u5fc3\u4e5f\u5df2\u7ed9\u6b27\u9633\u4f2f\u548c\u4e00\u6307\u6233\u4e2d\uff0c\u53ea\u89c9\u5f97\u4e00\u9635 \u70ed\u8fa3\u8fa3\u7684\uff0c\u80cc\u5fc3\u4f3c\u7ed9\u70e7\u7ea2\u7684\u94c1\u68d2\u70d9\u8fc7\u4e00\u822c\uff0c\u5e78\u800c\u4ed6\u7a7f\u6709\u62a4\u8eab\u5b9d\u7532\uff0c\u4e0d\u81f4\u91cd\u4f24\uff0c\u4f46\u6b27\u9633\u9634\u4f2f\u548c \u7684\u201d\u96f7\u795e\u6307\u201d\uff0c\u9694\u4e86\u4e00\u5c42\u5b9d\u7532\uff0c\u4ecd\u6709\u8fd9\u4e48\u5a01\u529b\uff0c\u4e5f\u786e\u662f\u5389\u5bb3\u975e\u5e38\u4e86\uff01 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6b27\u9633\u4f2f\u548c\u4e0e\u4ed6\u7684\u5f1f\u5987\u5de6\u53f3\u5939\u653b\uff0c\u5df2\u8feb\u5f97\u6c5f\u6d77\u5929\u4e0d\u80fd\u4e24\u8ffd\u51fb\u6587\u5ef7\u74a7\uff0c\u800c \u8981\u8f6c\u8fc7\u8eab\u6765\uff0c\u518d\u5e94\u4ed8\u4ed6\u4eec\u4e86\u3002 \u3000\u3000\u4e8e\u5927\u9e4f\u5ffd\u5730\u5636\u58f0\u53eb\u9053\uff1a\u201c\u6c5f\u5c0f\u4fa0\uff0c\u8bf7\u6055\u8001\u673d\u65e0\u80fd\u76f8\u52a9\u4e86\uff0c\u8bf7\u4f60\uff0c\u8bf7\u4f60\u5230\u5c11\u6797\u5bfa\u62a5 \u8baf\u2026\u2026\u201d\u58f0\u97f3\u4f4e\u6c89\u5636\u54d1\uff0c\u8bf4\u5230\u6700\u540e\u201d\u4e3a\u6211\u62a5\u4ec7\u201d\u90a3\u56db\u4e2a\u5b57\uff0c\u5df2\u662f\u542b\u80e1\u4e0d\u6e05\uff01\u6c5f\u6d77\u5929\u628a\u773c\u4e00 \u770b\uff0c\u53ea\u89c1\u5730\u4e0a\u4e00\u6ee9\u9c9c\u8840\uff0c\u4e8e\u5927\u9e4f\u8eba\u5728\u8840\u6cca\u4e4b\u4e2d\uff0c\u5df2\u662f\u5bc2\u7136\u4e0d\u52a8\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u54c0\u53eb\u9053\uff1a\u201c\u4e8e\u8001\u4f2f\uff0c\u4e8e\u8001\u4f2f\uff0c\u6211\u8fde\u7d2f\u4f60\u4e86\uff01\u201d\u4ed6\u65e2\u4e3a\u4e8e\u5927\u9e4f\u4e4b\u6b7b\u800c\u60b2\u6124\uff0c\u53c8\u62c5\u5fc3 \u59ec\u6653\u98ce\u906d\u53d7\u6587\u5ef7\u74a7\u7684\u6bd2\u624b\uff0c\u60b2\u6124\u52a0\u4e0a\u7126\u6025\uff0c\u5316\u6210\u4e86\u4e00\u80a1\u529b\u91cf\uff0c\u84e6\u5730\u5927\u543c\u4e00\u58f0\uff0c\u5530\u5530\u5530\u4e00\u8fde\u51e0 \u5251\uff0c\u5168\u90fd\u662f\u8c41\u51fa\u4e86\u6027\u547d\u7684\u8fdb\u624b\u62db\u6570\uff0c\u5f53\u771f\u662f\u6709\u5982\u75af\u864e\u4e00\u822c\uff0c \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u548c\u5927\u4f2f\u8054\u624b\uff0c\u672c\u662f\u7a33\u5360\u4e0a\u98ce\uff0c\u4f46\u7a81\u7136\u95f4\u7ed9\u6c5f\u6d77\u5929\u75af\u72c2\u653b\u51fb\uff0c\u4e24\u4eba\u8054\u624b\uff0c\u4ea6\u662f\u62b5 \u6321\u4e0d\u4f4f\uff0c\u6fc0\u6218\u4e2d\u6b27\u9633\u4e8c\u5a18\u5ffd\u89c9\u5934\u76ae\u4e00\u7247\u51b5\u6c81\u51c9\uff0c\u4e00\u7f15\u9752\u4e1d\uff0c\u5df2\u662f\u968f\u7740\u5251\u5149\u98de\u6563\uff01 \u3000\u3000\u6b27\u9633\u5a49\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u4e0d\u80fd\u52a8\u5f39\uff0c\u770b\u89c1\u6c5f\u6d77\u5929\u548c\u5979\u7684\u6bcd\u4eb2\u5982\u6b64\u820d\u6b7b\u5fd8\u751f\u7684\u6012\u6597\uff0c\u5fc3\u91cc\u53c8\u6025 \u53c8\u6015\uff0c\u5589\u5934\u201d\u54af\u54af\u201d\u4f5c\u54cd\uff0c\u53ea\u662f\u53eb\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5ffd\u542c\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u59ec\u6653\u98ce\u6240\u5728\u7684\u90a3\u95f4\u53a2\u623f\uff0c\u5899\u58c1\u5ffd\u7136\u88c2\u5f00\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u59ec\u6653\u98ce\u4ece\u6d1e\u91cc \u94bb\u51fa\u6765\uff0c\u8ddf\u7740\u6587\u5ef7\u74a7\u5927\u547c\u5c0f\u53eb\uff0c\u4e5f\u8ffd\u4e86\u51fa\u6765\uff0c\u53ea\u89c1\u59ec\u6653\u98ce\u8863\u886b\u7834\u70c2\uff0c\u6ee1\u8eab\u90fd\u662f\u6ce5\u571f\uff0c\u6587\u5ef7\u74a7 \u5219\u6ee1\u5934\u6ee1\u9762\u90fd\u662f\u75f0\uff0c\u4e24\u4eba\u5747\u662f\u72fc\u72c8\u4e0d\u582a\u3002 \u3000\u3000\u539f\u6765\u6587\u5ef7\u74a7\u6eda\u8fdb\u53a2\u623f\u7684\u65f6\u5589\uff0c\u6b63\u5de7\u59ec\u6653\u98ce\u521a\u505a\u5b8c\u5410\u7eb3\u529f\u592b\uff0c\u7cbe\u795e\u5df2\u6062\u590d\u4e86\u4e03\u516b\u5206\uff0c\u59ec\u6653 \u98ce\u4f55\u7b49\u673a\u7075\uff0c\u8d81\u7740\u4ed6\u672a\u80fd\u5373\u65f6\u722c\u8d77\u4e4b\u9645\uff0c\u7acb\u5373\u5c55\u5f00\u6e38\u8eab\u516b\u5366\u638c\u7684\u529f\u592b\uff0c\u5411\u4ed6\u653b\u51fb\uff0c\u540c\u65f6\u4e00\u5927 \u53e3\u4e00\u5927\u53e3\u7684\u6d53\u75f0\u5411\u4ed6\u5410\u53bb\uff0c\u8fd9\u662f\u4ed6\u8ddf\u91d1\u4e16\u9057\u5b66\u7684\uff0c\xb7\u6d53\u75f0\u867d\u7136\u4e0d\u80fd\u4f24\u5bb3\u8eab\u6709\u5185\u529f\u7684\u4eba\uff0c\u4f46\u5374 \u53ef\u4ee5\u6fc0\u6012\u654c\u4eba\uff0c\u4f7f\u4ed6\u5fc3\u6d6e\u6c14\u8e81\uff0c\u800c\u4e14\uff0c\u5018\u82e5\u7ed9\u6d53\u75f0\u5410\u4e2d\u53cc\u76ee\uff0c\u4e5f\u6709\u53ef\u80fd\u4ee4\u5bf9\u65b9\u53d8\u6210\u778e\u5b50\u3002 \u3000\u3000\u6587\u5ef7\u74a7\u88ab\u4e8e\u5927\u9e4f\u4e34\u6b7b\u524d\u7684\u4e00\u649e\uff0c\u8ddf\u7740\u53c8\u7ed9\u6c5f\u6d77\u5929\u8ffd\u51fb\uff0c\u4e00\u65f6\u95f4\u5c1a\u672a\u6709\u529f\u592b\u5316\u89e3\u8eab\u4e0a\u6240\u53d7 \u7684\u52b2\u9053\uff01\u8fd9\u65f6\u4ed6\u5728\u5730\u4e0a\u8fde\u7ffb\u5e26\u6eda\uff0c\u65e2\u8981\u5e94\u4ed8\u59ec\u6653\u98ce\u7684\u653b\u51fb\uff0c\u6d53\u75f0\u5c31\u8eb2\u907f\u4e0d\u5f00\uff0c\u504f\u504f\u59ec\u6653\u98ce\u521a \u5403\u8fc7 \u5927\u534a\u53ea\u80a5\u9e21\uff0c\u6ee1\u809a\u6cb9\u817b\uff0c\u75f0\u7279\u522b\u4e4b\u591a\uff0c\u5410\u5f97\u4ed6\u6ee1\u5934\u6ee1\u9762\u3002 \u3000\u3000\u6587\u5ef7\u74a7\u4e5f\u662f\u4e2a\u8001\u5978\u5de8\u733e\u4e4b\u4eba\uff0c\u5fc3\u77e5\u59ec\u6653\u98ce\u662f\u60f3\u6fc0\u6012\u4ed6\uff0c\u8d81\u673a\u9003\u8d70\uff0c\u4ed6\u5fcd\u7740\u6c14\uff0c\u4e00\u9762\u5835\u7740 \u95e8\u53e3\uff0c\u7528\u5288\u7a7a\u638c\u6765\u5bf9\u4ed8\u59ec\u6653\u98ce\uff0c\u4e00\u9762\u9ed8\u8fd0\u7384\u529f\uff0c\u6d88\u89e3\u8eab\u4e0a\u6240\u53d7\u7684\u52b2\u9053\uff0c\u6ca1\u591a\u4e45\u5c31\u7ed9\u4ed6\u6062\u590d\u4e86 \u539f\u6765\u7684\u529f\u529b\uff0c\u722c\u4e86\u8d77\u6765\u3002\u4f46\u6b63\u5de7\u5728\u4ed6\u722c\u8d77\u6765\u5927\u9a82\u59ec\u6653\u98ce\u7684\u65f6\u5019\uff0c\u53c8\u7ed9\u59ec\u6653\u98ce\u4e00\u53e3\u6d53\u75f0\u7c98\u4e86\u4ed6 \u7684\u80e1\u987b\uff0c\u8981\u4e0d\u662f\u95ed\u5634\u5f97\u6289\uff0c\u51e0\u4e4e\u5c31\u8981\u541e\u4e86\u4ed6\u7684\u6d53\u75f0\uff0c\u6587\u5ef7\u74a7\u518d\u4e5f\u6309\u637a\u4e0d\u4f4f\uff0c\u4f7f\u51fa\u201d\u4e09\u8c61\u5f52 \u5143\u201d\u7684\u7edd\u9876\u795e\u529f\uff0c\u6251\u5c06\u8fc7\u53bb\uff0c\u5411\u59ec\u6653\u98ce\u731b\u7684\u4fbf\u662f\u4e00\u638c\uff0c\u4f46\u59ec\u6653\u98ce\u7684\u8f7b\u529f\u51fa\u795e\u5165\u5316\uff0c\u660e\u660e\u5df2\u7ed9 \u6587\u5ef7\u74a7\u8feb\u5230\u5899\u6839\uff0c\u9000\u65e0\u53ef\u9000\uff0c\u4f46\u4ecd\u7136\u7ed9\u4ed6\u4fa7\u8eab\u6ed1\u5f00\uff0c\u6587\u5ef7\u74a7\u8fd9\u4e00\u638c\u672a\u6253\u4e2d\u59ec\u6653\u98ce\uff0c\u5374\u628a\u5899\u7ed9 \u9707\u88c2\u4e86\u3002 \u3000\u3000\u4e14\u8bf4\u6c5f\u6d77\u5929\u89c1\u59ec\u6653\u98ce\u94bb\u4e86\u51fa\u6765\uff0c\u867d\u7136\u867d\u7136\u72fc\u72c8\u4e0d\u582a\uff0c\u5374\u559c\u5b89\u7136\u65e0\u6059\uff0c\u5fc3\u4e0a\u7684\u4e00\u5757\u77f3\u5934\u624d \u653e\u4e0b\u6765\u3002 \u3000\u3000\u6587\u5ef7\u74a7\u5374\u4e0d\u7531\u5f97\u6697\u6697\u5403\u60ca\uff0c\u4ed6\u672c\u4ee5\u4e3a\u6b27\u9633\u4f2f\u548c\u4e0e\u6b27\u9633\u4e8c\u5a18\u8054\u624b\uff0c\u7eb5\u4f7f\u4e00\u65f6\u4e4b\u95f4\u80dc\u4e0d\u4e86\u6c5f \u6d77\u5929\uff0c\u4e5f\u51b3\u4e0d\u81f3\u4e8e\u843d\u8d25\uff0c\u54ea\u77e5\u51fa\u73b0\u5728\u4ed6\u773c\u524d\u7684\u666f\u8c61\u5374\u662f\uff1a\u6b27\u9633\u4f2f\u548c\u4e0e\u4ed6\u7684\u5f1f\u5987\u7adf\u7ed9\u6c5f\u6d77\u5929\u8feb \u5f97\u6b65\u6b65\u540e\u9000\u3002\u6587\u5ef7\u74a7\u60ca\u7591\u4e0d\u5b9a\uff0c\u5fc3\u60f3\uff1a\u201c\u83ab\u975e\u8fd9\u5c0f\u5b50\u5f53\u771f\u6709\u4ec0\u4e48\u90aa\u6cd5\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u53cc\u65b9\u53c8\u518d\u7531\u5206\u800c\u5408\uff0c\u59ec\u6653\u98ce\u4e0a\u524d\u52a9\u6c5f\u6d77\u5929\uff0c\u6587\u5ef7\u74a7\u4e5f\u6765\u52a9\u6b27\u9633\u4f2f\u548c\uff0c\u59ec\u6653\u98ce\u5148\u5230\u4e00 \u6b65\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u4e00\u7f15\u51b7\u98ce\u5411\u6b27\u9633\u4e8c\u5a18\u540e\u8111\u5c04\u53bb\uff0c\u6b27\u9633\u4e8c\u5a18\u53ea\u9053\u4ed6\u53c8\u4f7f\u51fa\u4fee\u7f57\u9634\u71ac\u715e\u529f\uff0c\u614c\u5fd9 \u95ea\u907f\uff0c\u6c5f\u6d77\u5929\u8eab\u5f62\u4e00\u6643\uff0c\u500f\u5730\u4fbf\u4ece\u7f3a\u53e3\u51b2\u51fa\uff0c\u518d\u6b21\u5230\u4e86\u6b27\u9633\u5a49\u8eab\u65c1\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u518d\u4e0d\u8fdf\u7591\uff0c\u7acb\u5373\u4fbf\u7ed9\u6b27\u9633\u5a49\u89e3\u4e86\u7a74\u9053\uff0c\u4e5f\u5e78\u4e8f\u662f\u6c5f\u6d77\u5929\uff0c\u624d\u89e3\u5f97\u6587\u5ef7\u74a7\u7684\u72ec\u95e8\u70b9 \u7a74\u624b\u6cd5\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u53eb\u9053\uff1a\u201c\u6b27\u9633\u59d1\u5a18\uff0c\u4f60\u5feb\u8d70\u5427\uff01\u8fd9\u6837\u7684\u7236\u6bcd\uff0c\u4f60\u5c31\u662f\u4ee5\u540e\u6c38\u8fdc\u4e0d\u89c1\u4ed6\u4eec\uff0c\u4e5f\u6ca1\u6709 \u4ec0\u4e48\u53ef\u60dc\u4e86\uff01\u201d \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u5927\u6012\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u7adf\u6562\u79bb\u95f4\u6211\u4eec\u6bcd\u5973\uff01\u201d\u6325\u638c\u5954\u4e0a\uff0c\u6c5f\u6d77\u5929\u5b9d\u5251\u4e00\u5c01\uff0c \u4e00\u62db\u201d\u5927\u6f20\u9ec4\u7802\u201d\uff0c\u4f46\u89c1\u5251\u6c14\u7eb5\u6a2a\uff0c\u767b\u65f6\u6709\u5982\u5e03\u4e0b\u4e00\u9053\u5251\u5e55\uff0c\u963b\u6b62\u4e86\u6b27\u9633\u4e8c\u5a18\u7684\u53bb\u8def\u3002\u6b27\u9633 \u5a49\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u6c5f\u76f8\u516c\uff0c\u5a18\uff0c\u4f60\u4eec\u5c31\u770b\u5728\u6211\u7684\u4efd\u4e0a\uff0c\u522b\u518d\u52a8\u624b\u4e86\uff01\u201d \u3000\u3000\u6587\u5ef7\u74a7\u4e00\u638c\u8feb\u5f00\u59ec\u6653\u98ce\uff0c\u63c9\u8eab\u6025\u4e0a\uff0c\u5de6\u624b\u4e00\u8bb0\u5288\u7a7a\u638c\uff0c\u52a9\u6b27\u9633\u4e8c\u5a18\u8361\u5f00\u6c5f\u6d77\u5929\u7684\u5b9d\u5251\uff0c \u53f3\u624b\u4e2d\u6307\u4e00\u6233\uff0c\u91cd\u65bd\u6545\u6280\uff0c\u4f7f\u51fa\u201c\u9694\u7a7a\u70b9\u7a74\u201d\u7684\u529f\u592b\uff0c\u8981\u70b9\u6b27\u9633\u5a49\u7684\u7a74\u9053\u3002\u6c5f\u6d77\u5929\u65e9\u5df2\u63d0 \u9632\uff0c\u6587\u5ef7\u74a7\u529f\u529b\u672a\u53d1\uff0c\u4ed6\u5df2\u5148\u7528\u5929\u7f57\u6b65\u6cd5\u95ea\u5f00\uff0c\u906e\u5728\u6b27\u9633\u5a49\u7684\u8eab\u524d\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u4e5f\u4f7f\u51fa\u4e86\u4e00 \u6307\u7985\u529f\uff0c\u4f46\u542c\u7684\u55e4\u55e4\u58f0\u54cd\uff0c\u4e24\u80a1\u6c14\u6d41\u78b0\u4e2a\u6b63\u7740\uff0c\u6c5f\u6d77\u5929\u7684\u529f\u529b\u867d\u7136\u8f83\u5f31\uff0c\u5374\u4e5f\u582a\u582a\u5316\u89e3\u5f97\u4e86 \u6587\u5ef7\u74a7\u90a3\u80a1\u201c\u9694\u7a7a\u70b9\u7a74\u201d\u7684\u65e0\u5f62\u6f5c\u529b\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6587\u5ef7\u74a7\u6251\u4e0a\u6765\uff0c\u6c5f\u6d77\u5929\u5530\u5530\u5530\uff0c\u8fde\u73af\u4e09\u5251\uff0c\u5c06\u4ed6\u6321\u4f4f\uff0c\u987f\u8db3\u53eb\u9053\uff1a \u201c\u6b27\u9633\u7530\u59d1\u5a18\uff0c\u4f60\u8fd8\u4e0d\u8d70\uff0c\u8981\u5f85\u4ed6\u4eec\u5c06\u4f60\u6349\u56de\u53bb\uff0c\u8feb\u4f60\u5ac1\u4eba\u4e48\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u7a81\u7136\u201d\u54c7\u201d\u7684\u4e00\u58f0\u54ed\u4e86\u51fa\u6765\uff0c\u63a9\u9762\u75be\u5954\uff0c\u4ece\u7a97\u53e3\u7a9c\u51fa\u3002\u6b27\u9633\u4e8c\u5a18\u51b2\u4e0d\u7834\u5251\u5e55\uff0c\u6b27 \u9633\u4f2f\u548c\u4e5f\u7ed9\u59ec\u6653\u98ce\u6321\u4f4f\uff0c\u53ea\u6709\u773c\u7741\u7741\u7684\u770b\u5979\u9003\u53bb\uff01 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u6b27\u9633\u4f2f\u548c\u8fd9\u8fb9\u7684\u4e09\u4e2a\u4eba\u66f4\u4e3a\u607c\u6012\uff0c\u4e09\u4eba\u5e03\u6210\u4e86\u6b39\u89d2\u4e4b\u52bf\uff0c\u6b65\u6b65\u8fdb\u8feb\uff0c\u4e0d\u4e45\uff0c\u53c8 \u628a\u6c5f\u6d77\u5929\u56f0\u5728\u6838\u5fc3\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u53eb\u9053\uff1a\u201c\u59ec\u4f2f\u4f2f\uff0c\u4e8e\u8001\u524d\u8f88\u4e34\u6b7b\u4e4b\u524d\uff0c\u8981\u6211\u4eec\u5230\u5c11\u6797\u5bfa\u62a5\u8baf\uff0c\u8fd9\u4e2a\u62c5\u5b50\u592a\u91cd\uff0c\u5c0f \u4f84\u529b\u6709\u672a\u902e\uff0c\u8fd8\u662f\u8bf7\u4f60\u8001\u4eba\u5bb6\u62c5\u8d77\u6765\u5427\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8fd9\u65f6\u6b63\u5728\u5168\u529b\u62a2\u653b\uff0c\u4ee5\u8fc5\u6377\u65e0\u4f26\u7684\u8ffd\u51e4\u5251\u6cd5\u7d27\u7d27\u7684\u5c06\u6587\u5ef7\u74a7\u8feb\u4f4f\uff0c\u6559\u4ed6\u65e0\u6cd5\u65c1 \u89c2\u3002\u5018\u82e5\u59ec\u6653\u98ce\u8981\u9003\uff0c\u786e\u662f\u6709\u673a\u4f1a\u53ef\u4ee5\u9003\u51fa\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u4ed6\u5fc3\u91cc\u8f6c\u4e86\u597d\u51e0\u4e2a\u5ff5\u5934\uff0c\u8981\u662f\u4e0d \u9003\uff0c\u4e45\u6218\u4e0b\u53bb\uff0c\u53ea\u4f2f\u4ed6\u4eec\u4e8c\u4eba\u90fd\u8981\u540c\u5f52\u4e8e\u5c3d\uff0c\u4f46\u5018\u82e5\u4ed6\u5148\u9003\u4e86\u51fa\u53bb\uff0c\u6c5f\u6d77\u5929\u5374\u662f\u5fc5\u6b7b\u65e0\u7591\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u89c1\u4ed6\u8e0c\u8e87\u4e0d\u51b3\uff0c\u7126\u6025\u53eb\u9053\uff1a\u201c\u59ec\u4f2f\u4f2f\uff0c\u4f60\u96be\u9053\u5fcd\u5fc3\u773c\u7741\u7741\u770b\u4e8e\u524d\u8f88\u6789\u6b7b\uff0c\u4e0d\u53bb\u7ed9 \u4ed6\u7ed9\u4ed6\u62a5\u8baf\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u53cc\u7709\u4e00\u7ad6\uff0c\u5fc3\u610f\u5df2\u51b3\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5148\u987e\u751f\u7684\uff0c\u518d\u987e\u6b7b\u7684\uff01\u6c5f\u8d24\u4f84\uff0c\u4f60\u5fd8\u4e86\u6211\u4e0e \u4f60\u7684\u7236\u4eb2\u662f\u516b\u62dc\u4e4b\u4ea4\u4e48\uff1f\u4eca\u65e5\u6211\u82e5\u7136\u820d\u4f60\u800c\u53bb\uff0c\u53eb\u6211\u4ee5\u540e\u6709\u4f55\u9762\u76ee\u89c1\u4f60\u7236\u4eb2\uff1f\u201d\u4ed6\u975e\u4f46\u4e0d \u9003\uff0c\u53cd\u800c\u6251\u4e0a\u524d\u53bb\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u518d\u5ea6\u65bd\u5c55\u201d\u7384\u9634\u6307\u201d\u7684\u529f\u592b\uff0c\u5411\u6b27\u9633\u4e8c\u5a18\u88ad\u51fb\u3002 \u3000\u3000\u6b27\u9633\u4f2f\u548c\u6012\u9053\uff1a\u201c\u597d\uff0c\u5f85\u6211\u6765\u9886\u6559\u4f60\u7684\u4fee\u7f57\u9634\u715e\u529f\uff01\u201d\u4ed6\u6240\u7ec3\u7684\u96f7\u795e\u6307\uff0c\u53d1\u51fa\u7684\u4e43\u662f\u4e00 \u80a1\u7eaf\u9633\u4e4b\u6c14\uff0c\u6b63\u597d\u662f\u201d\u7384\u9634\u6307\u201d\u7684\u514b\u661f\uff0c\u4e24\u80a1\u6c14\u6d41\u4e00\u78b0\uff0c\u4f46\u542c\u5f97\u55e4\u55e4\u58f0\u54cd\uff0c\u5192\u51fa\u4e86\u767d\u8499\u8499\u7684 \u6c34\u6c14\uff0c\u59ec\u6653\u98ce\u53ea\u89c9\u4e00\u80a1\u70ed\u98ce\u6251\u6765\uff0c\u4e0d\u7531\u5f97\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u6b27\u9633\u4f2f\u548c\u54c8\u54c8\u4eba\u7b11\u9053\uff1a\u201c\u6211\u53ea\u5f53\u4fee\u7f57\u9634\u715e\u529f\u662f\u600e\u4e48\u6837\u7684\u4e86\u4e0d\u5f97\uff0c\u5374\u539f\u6765\u4e5f\u662f\u8a00\u8fc7\u5176 \u5b9e\uff01\u201d\u8bdd\u4ec7\u672a\u4e86\uff0c\u5ffd\u5730\u4e00\u80a1\u72c2\u98da\u5377\u5730\u800c\u6765\uff0c\u59ec\u6653\u98ce\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8001\u5339\u592b\uff0c\u4f60\u6709\u773c\u65e0\u73e0\uff0c\u6559\u4f60 \u89c1\u8bc6\u771f\u6b63\u7684\u4fee\u7f57\u9634\u715e\u529f\uff01\u201d \u3000\u3000\u539f\u6765\u201d\u7384\u9634\u6307\u201d\u7684\u529f\u592b\u4e43\u662f\u4ece\u4fee\u7f57\u9634\u715e\u529f\u53d8\u5316\u51fa\u6765\u7684\uff0c\u540c\u6837\u80fd\u4ee5\u9634\u715e\u4e4b\u6c14\u4f24\u4eba\uff0c\u4e0d\u8fc7\u4e00 \u6765\u56e0\u4e3a\u7ec3\u6cd5\u7565\u6709\u4e0d\u540c\uff0c\u4e8c\u6765\u56e0\u4e3a\u5b83\u662f\u7528\u6307\u529b\u53d1\u51fa\uff0c\u5a01\u529b\u5374\u662f\u8fdc\u8fdc\u4e0d\u5982\u7528\u638c\u529b\u53d1\u51fa\u7684\u4fee\u7f57\u9634\u715e \u529f\uff0c\u5927\u7ea6\u7384\u9634\u6307\u7ec3\u5230\u6700\u9ad8\u7684\u5883\u754c\uff0c\u4e5f\u4e0d\u8fc7\u76f8\u5f53\u4e8e\u7b2c\u4e94\u91cd\u7684\u4fee\u7f57\u9634\u715e\u529f\u3002 \u3000\u3000\u59ec\u6653\u98ce\u7684\u8eab\u6cd5\u8fc5\u5982\u95ea\u7535\uff0c\u6b27\u9633\u4f2f\u548c\u65e0\u53ef\u95ea\u903c\uff0c\u4f46\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u53cc\u638c\u5df2\u7136\u78b0\u4e0a\uff0c\u59ec \u6653\u98ce\u7684\u4fee\u7f57\u9634\u715e\u529f\u5df2\u7ec3\u5230\u4e86\u7b2c\u4e03\u91cd\uff0c\u6b27\u9633\u4f2f\u548c\u53ef\u4ee5\u5316\u89e3\u5f97\u4e86\u4ed6\u7684\u7384\u9634\u6307\uff0c\u4f46\u5374\u5316\u89e3\u4e0d\u4e86\u4ed6\u4e03 \u91cd\u7684\u4fee\u7f57\u9634\u715e\u529f\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u628a\u6b27\u9633\u4f2f\u548c\u9707\u5f97\u51e0\u4e4e\u7ad9\u7acb\u4e0d\u7a33\uff0c\u76f4\u9000\u5230\u4e86\u5899\u8fb9\uff0c\u800c\u4e14\u7259\u5173\u683c\u683c\u4f5c\u54cd\uff0c\u5c31\u50cf\u5bb3\u4e86\u4e25 \u91cd\u7684\u53d1\u51b7\u75c5\u4e00\u822c\u3002 \u3000\u3000\u53ef\u662f\u59ec\u6653\u98ce\u5143\u6c14\u521a\u590d\uff0c\u53c8\u518d\u65bd\u5c55\u8fd9\u79cd\u6700\u4e3a\u8017\u635f\u771f\u529b\u7684\u4fee\u7f57\u9634\u715e\u529f\uff0c\u8eab\u5b50\u4e5f\u81ea\u6709\u70b9\u62b5\u53d7\u4e0d \u8d77\uff0c\u767b\u65f6\u4e5f\u6c14\u5598\u5401\u5401\uff0c\u51b7\u6c57\u76f4\u6d41\u3002 \u3000\u3000\u6587\u5ef7\u74a7\u7684\u529f\u529b\u5230\u5e95\u6bd4\u6c5f\u6d77\u5929\u80dc\u8fc7\u4e00\u7b79\uff0c\u6c5f\u6d77\u5929\u4e00\u8f6e\u731b\u653b\u4e4b\u540e\uff0c\u540e\u52b2\u4e0d\u7ee7\uff0c\u7ed9\u4ed6\u4ee5\u201d\u4e09\u8c61 \u5f52\u5143\u201d\u7684\u7edd\u9876\u5185\u529f\u628a\u5b9d\u5251\u8361\u5f00\uff0c\u8131\u51fa\u8eab\u6765\uff0c\u7acb\u5373\u4e00\u58f0\u51b7\u7b11\u9053\uff1a\u201c\u59ec\u6653\u98ce\uff0c\u4f60\u7684\u4fee\u7f57\u9634\u715e\u529f\u53ef \u60dc\u8fd8\u53ea\u662f\u7ec3\u5230\u7b2c\u4e03\u91cd\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u54ac\u5b9e\u7259\u6839\uff0c\u6b63\u8981\u628a\u5168\u8eab\u529f\u529b\u51dd\u805a\u638c\u5fc3\uff0c\u518d\u53d1\u4e00\u638c\uff0c\u6c5f\u6d77\u5929\u5df2\u7ecf\u62e6\u5728\u4ed6\u7684\u9762\u524d\uff0c\u6025 \u5fd9\u53eb\u9053\uff1a\u201c\u59ec\u4f2f\u4f2f\uff0c\u4f60\u53ef\u4e0d\u80fd\u518d\u4f7f\u7528\u4fee\u7f57\u9634\u715e\u529f\u4e86\uff01\u201d \u3000\u3000\u6587\u5ef7\u74a7\u53cc\u6307\u4e00\u5f39\uff0c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u628a\u5b9d\u5251\u5f39\u5f00\uff0c\u6b3a\u8eab\u76f4\u8fdb\uff0c\u970d\u5730\u4e00\u638c\u4fbf\u6a2a\u626b\u8fc7\u53bb\uff0c\u638c\u529b \u6709\u5982\u6392\u5c71\u5012\u800c\u6765\uff0c\u5df2\u4e0d\u662f\u7528\u5b9d\u5251\u6240\u80fd\u5316\u89e3\uff0c\u6c5f\u6d77\u5929\u4e5f\u53ea\u5f97\u4e00\u638c\u5288\u53bb\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u9707\u5f97\u5899\u58c1\u6447 \u52a8\uff0c\u5c4b\u74e6\u788e\u88c2\uff0c\u6c5f\u6d77\u5929\u4e00\u4e2a\u7b4b\u6597\u5012\u7ffb\u51fa\u53bb\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u7a33\u5f97\u4f4f\u8eab\u5f62\u3002\u6587\u5ef7\u74a7\u4e5f\u5728\u5730\u4e0a\u6253\u4e86\u4e24 \u4e2a\u76d8\u65cb\uff0c\u624d\u6536\u5f97\u4f4f\u811a\u6b65\u3002 \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u201c\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\u201d\u7684\u529f\u6548\u5df2\u5931\uff0c\u4f46\u4ed6\u5e78\u4e8f\u5403\u4e86\u90a3\u534a\u679d\u524d\u5e74\u7075\u829d\uff0c\u4fdd\u4f4f\u4e86\u5143 \u6c14\uff0c\u6240\u4ee5\u529f\u529b\u867d\u7136\u6ca1\u6709\u589e\u52a0\uff0c\u4e5f\u6ca1\u6709\u6709\u51cf\u9000\uff0c\u800c\u6587\u5ef7\u74a7\u5219\u53d7\u4e8e\u5927\u9e4f\u90a3\u4e00\u649e\uff0c\u529f\u529b\u51cf\u4e86\u4e24\u5206\uff0c \u6b64\u6d88\u5f7c\u957f\uff0c\u6bd4\u5bf9\u8d77\u6765\uff0c\u6587\u5ef7\u74a7\u867d\u7136\u8fd8\u662f\u4e0a\u98ce\uff0c\u5374\u4e5f\u5360\u4e0d\u5230\u5f88\u5927\u7684\u4fbf\u5b9c\u4e86\u3002 \u3000\u3000\u6587\u5ef7\u74a7\u5360\u4e86\u4e0a\u98ce\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u6325\u638c\u590d\u4e0a\uff0c\u53cc\u65b9\u53c8\u6253\u4f5c\u4e00\u56e2\u3002\u6b27\u9633\u4f2f\u548c\u9ed8\u8fd0\u7384\u529f\uff0c\u6d88\u4e86\u51e0 \u5206\u5bd2\u6c14\uff0c\u4ecd\u7136\u4e0a\u524d\u76f8\u52a9\uff0c\u4ed6\u53d7\u4e86\u4fee\u7f57\u9634\u715e\u529f\u7684\u4f24\u5bb3\uff0c\u529f\u529b\u56fa\u7136\u662f\u5927\u4e0d\u5982\u524d\uff0c\u4f46\u59ec\u6653\u98ce\u4e5f\u5df2\u5230 \u4e86\u5f3a\u5f29\u4e4b\u672b\uff0c\u548c\u6b27\u9633\u4f2f\u548c\u6b63\u662f\u4e00\u4e2a\u534a\u65a4\uff0c\u4e00\u4e2a\u516b\u4e24\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u6068\u6781\u4e86\u6c5f\u6d77\u5929\uff0c\u4e0a\u524d\u52a9\u6587\u5ef7\u74a7\u5939\u51fb\uff0c\u6c5f\u6d77\u5929\u53ea\u5bf9\u4ed8\u4e00\u4e2a\u6587\u5ef7\u74a7\uff0c\u5df2\u662f\u96be\u514d\u5904\u4e8e \u4e0b\u98ce\uff0c\u52a0\u4e0a\u4e86\u4e00\u4e2a\u6b27\u9633\u4e8c\u5a18\uff0c\u81ea\u662f\u66f4\u96be\u5e94\u4ed8\uff0c\u4e0d\u591a\u4e00\u4f1a\uff0c\u6587\u5ef7\u74a7\u8d8a\u653b\u8d8a\u731b\uff0c\u628a\u59ec\u3001\u6c5f\u4e8c\u4eba\uff0c \u90fd\u7b3c\u7f69\u5728\u638c\u529b\u4e4b\u4e0b\uff0c\u5230\u4e86\u8fd9\u65f6\uff0c\u59ec\u6653\u51e4\u5c31\u662f\u60f3\u9003\uff0c\u4e5f\u4e0d\u80fd\u591f\u4e86\uff01 \u3000\u3000\u6fc0\u6218\u4e2d\u6c5f\u6d77\u5929\u53c8\u4e0e\u6587\u5ef7\u74a7\u786c\u62fc\u4e86\u4e00\u638c\uff0c\u8fd9\u4e00\u6b21\u6c5f\u6d77\u5929\u7528\u7684\u662f\u5185\u5bb6\u638c\u6cd5\u4e2d\u6700\u5965\u5999\u7684\u201c\u987b\u5f25 \u638c\u201d\u7528\u4ee5\u62a4\u8eab\uff0c\u53ef\u4ee5\u62b5\u6321\u5f97\u4f4f\u529f\u529b\u80dc\u8fc7\u81ea\u5df1\u7684\u5f3a\u654c\uff0c\u6587\u5ef7\u74a7\u4e00\u638c\u5288\u53bb\uff0c\u89c1\u6c5f\u6d77\u5929\u52a8\u4e5f\u4e0d\u52a8\uff0c \u5403\u4e86\u4e00\u60ca\uff0c\u6b63\u8981\u6536\u638c\u518d\u53d1\uff0c\u5374\u5ffd\u542c\u5f97\u59ec\u6653\u98ce\u5c16\u53eb\u4e00\u58f0\uff0c\u201d\u54c7\u201d\u7684\u4e00\u53e3\u9c9c\u8840\u55b7\u51fa\u6765\uff0c\u539f\u6765\u6c5f\u6d77 \u5929\u8fd9\u201d\u987b\u5f25\u638c\u201d\u529b\u7528\u4e8e\u62a4\u8eab\uff0c\u6700\u5999\u4e0d\u8fc7\uff0c\u4f46\u5374\u4e0d\u80fd\u517c\u987e\u65c1\u4eba\uff0c\u59ec\u6653\u98ce\u88ab\u6587\u5ef7\u74a7\u7684\u638c\u529b\u6ce2\u53ca\uff0c \u5148\u53d7\u4e86\u4f24\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u53c8\u6127\u53c8\u6094\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u600e\u7684\u8fd9\u6837\u7cca\u6d82\uff0c\u53ea\u77e5\u4fdd\u5992\u81ea\u5df1\uff0c\u5374\u5fd8\u4e86\u4fdd\u62a4\u59ec\u4f2f\u4f2f\u4e86\u3002\u201d \u6587\u5ef7\u74a7\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u8fd9\u65f6\u4e5f\u770b\u51fa\u4e86\u6c5f\u6d77\u5929\u7684\u987b\u5f25\u638c\u529b\uff0c\u53ea\u662f\u80fd\u5b88\u800c\u4e0d\u80fd\u653b\uff0c\u4fbf\u53c8\u54c8\u54c8 \u5927\u7b11\uff0c\u4e00\u6b65\u4e00\u6b65\u7684\u8feb\u4e0a\u6765\u3002 \u3000\u3000\u5728\u4ed6\u7684\u5927\u7b11\u58f0\u4e2d\uff0c\u5ffd\u5730\u6709\u4e00\u4e2a\u964c\u751f\u7684\u7b11\u58f0\u63a9\u4e86\u8fdb\u6765\uff0c\u867d\u7136\u6ca1\u6709\u628a\u4ed6\u7684\u7b11\u58f0\u7f69\u8fc7\uff0c\u5374\u662f\u523a \u8033\u975e\u5e38\uff0c\u6587\u5ef7\u74a7\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u559d\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u767d\u8863\u5c11\u5e74\u8d70\u4e86\u8fdb\u6765\uff0c\u7b11\u9053\uff1a\u201c\u4e09\u8c61\u5f52\u5143\u7684\u5185\u529f\u786e\u662f\u4eba\u95f4\u7f55\u89c1\uff0c\u987b\u5f25\u638c\u529b\u4e5f\u5927\u662f \u4e0d\u51e1\uff0c\u6211\u4eca\u65e5\u5f97\u89c1\u4e24\u79cd\u7edd\u4e16\u795e\u529f\uff0c\u771f\u662f\u773c\u798f\u4e0d\u6d45\uff01\u201d \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u5927\u559c\u9053\uff1a\u201c\u53f6\u516c\u5b50\uff0c\u662f\u4ec0\u4e48\u98ce\u628a\u4f60\u5439\u6765\u7684\uff1f\u771f\u662f\u5de7\u6781\u4e86\uff0c\u4f60\u6765\u5f97\u6b63\u662f\u65f6\u5019\u3002\u201d \u6b27\u9633\u4e8c\u5a18\u5927\u559c\uff0c\u6c5f\u6d77\u5929\u5374\u4e0d\u7981\u5927\u60ca\uff0c\u8fd9\u5c11\u5e74\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u5728\u7941\u8fde\u5c71\u4e2d\u4e0e\u4ed6\u6076\u6218\u8fc7\u4e00\u573a\u7684\u90a3 \u4e2a\u201c\u53f6\u516c\u5b50\u3002\u6b63\u662f\uff1a \u3000\u3000\u6765\u610f\u5982\u4f55\u96be\u731c\u5ea6\uff0c\u662f\u4ec7\u662f\u53cb\u672a\u5206\u660e\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002"}),showOnHomepage:!0},{name:"\u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u7389\u5973\u6709\u5fc3\u968f\u4fa0\u58eb\u3000\u5c11\u5e74\u4ed7\u4e49\u65a5\u5978\u90aa ",content:(0,t.jsx)(t.Fragment,{children:"\u3000\u90a3\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u662f\u554a\uff0c\u771f\u662f\u5de7\u9047\uff0c\u6211\u6709\u4e00\u4e8b\u672a\u660e\uff0c\u6b63\u8981\u627e\u4f60\u3002\u201d\u6b27\u9633\u4e8c\u5a18\u9053\uff1a\u201c\u6709\u4ec0 \u4e48\u4e8b\uff0c\u8fc7\u540e\u518d\u8bf4\u597d\u5417\uff1f\u54b1\u4eec\u5148\u628a\u8fd9\u5c0f\u5b50\u6599\u7406\u4e86\uff0c\u8fd9\u5c0f\u5b50\u4e5f\u662f\u4f60\u4ec7\u4eba\u554a\uff01\u201d \u3000\u3000\u90a3\u767d\u8863\u5c11\u5e74\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u6000\u7740\u8fd9\u4e2a\u7f51\u846b\u82a6\uff0c\u53ef\u5f97\u5148\u6253\u7834\u4e86\u624d\u6210\u3002\u5426\u5219\uff0c\u6211 \u53ef\u6ca1\u6709\u7cbe\u795e\u6253\u67b6\u3002\u201d \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u65e0\u5948\uff0c\u53ea\u5f97\u95ee\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u5230\u5e95\u6709\u4f55\u4e8b\u95f7\u5728\u5fc3\u4e2d\uff1f\u201d \u3000\u3000\u90a3\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u6211\u521a\u624d\u5230\u8fc7\u4f60\u5bb6\uff0c\u60f3\u89c1\u4f60\u5bb6\u7684\u6e05\u59d1\u5a18\uff0c\u4e0d\u77e5\u5979\u4f55\u4ee5\u4e0d\u80af\u89c1\u6211\uff1f\u201d\u4f60\u662f \u5979\u7684\u6bcd\u4eb2\uff0c\u60f3\u5fc5\u77e5\u9053\u5176\u4e2d\u7f18\u6545\u3002\u201d \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u5fc3\u8df3\u8033\u70ed\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u54ce\u5440\uff0c\u83ab\u975e\u4ed6\u5f53\u771f\u662f\u559c\u6b22\u4e0a\u6211\u4eec\u7684\u6e05\u4e2b\u5934\uff0c\u5982\u4eca\u662f\u6765 \u8d23\u5907\u6211\u4e86\uff0c\u602a\u4e0d\u5f97\u6e05\u4e2b\u5934\u5728\u7761\u68a6\u91cc\u4e5f\u66fe\u5ff5\u8fc7\u4ed6\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u539f\u6765\u6b27\u9633\u4e8c\u5a18\u7684\u5927\u5973\u513f\u6b27\u9633\u6e05\uff0c\u81ea\u4ece\u8ba4\u8bc6\u4e86\u8fd9\u4f4d\u201c\u53f6\u516c\u5b50\u201d\u4e4b\u540e\uff0c\u5c31\u628a\u4e00\u7f15\u60c5\u4e1d\u7cfb\u5728\u4ed6 \u7684\u8eab\u4e0a\uff0c\u4f46\u4ed6\u5374\u4f6f\u4f5c\u4e0d\u77e5\uff0c\u8fd8\u6545\u610f\u5bf9\u6b27\u9633\u6e05\u51b7\u51b7\u6de1\u6de1\uff0c\u6c14\u5f97\u6b27\u9633\u6e05\u51e0\u4e4e\u751f\u51fa\u75c5\u6765\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u4e5f\u77e5\u9053\u5927\u5973\u513f\u7684\u5fc3\u610f\uff0c\u672c\u6765\u60f3\u5728\u529e\u4e86\u6b27\u9633\u5a49\u7684\u5a5a\u4e8b\u4e4b\u540e\uff0c\u5c31\u627e\u5a92\u4eba\u66ff\u4ed6\u4eec\u64ae\u5408 \u7684\uff0c\u4e0d\u6599\u5728\u5409\u65e5\u524d\u5915\uff0c\u6b27\u9633\u5a49\u79c1\u9003\uff0c\u6b27\u9633\u6e05\u5374\u81ea\u613f\u9876\u66ff\uff0c\u6b27\u9633\u4e8c\u5a18\u6015\u5f97\u7f6a\u6587\u5bb6\u53d4\u4f84\uff0c\u4e8b\u8d77\u4ed3 \u5352\uff0c\u522b\u65e0\u4ed6\u6cd5\u53ef\u60f3\uff0c\u4fbf\u53ea\u597d\u7528\u4e86\u8fd9\u201c\u674e\u4ee3\u6843\u50f5\u201d\u4e4b\u8ba1\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u987e\u7740\u548c\u90a3\u201c\u53f6\u516c\u5b50\u201d\u8bf4\u8bdd\uff0c\u81ea\u662f\u4e0d\u80fd\u5168\u529b\u51fa\u51fb\u3002\u6c5f\u6d77\u5929\u8fd9\u8fb9\u7684\u9635\u811a\uff0c\u53c8\u6e10\u6e10\u7a33 \u5b9a\u4e0b\u6765\u3002 \u3000\u3000\u59ec\u6653\u98ce\u5ffd\u5730\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u7b11\u5440\u597d\u7b11\uff0c\u59d3\u53f6\u7684\uff0c\u4f60\u662f\u88c5\u7cca\u6d82\u5462\uff1f\u8fd8\u662f\u7279\u5730\u6765\u4e0e\u4eba\u5bb6\u4e89\u98ce \u5403\u918b\uff1f\u201d\u6b27\u9633\u4e8c\u5a18\u53d8\u4e86\u9762\u8272\uff0c\u559d\u9053\uff1a\u201c\u4f60\u80e1\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u4f60\u4eca\u5929\u662f\u5ac1\u5973\u513f\u4e0d\u662f\uff0c\u4f60\u7684\u5973\u513f\u51fa\u5ac1\uff0c\u8fd8\u6015\u6211\u8bf4\u4e48\uff0c\u54c8\u54c8\uff0c\u59d3\u53f6\u7684\uff0c\u6211 \u544a\u8bc9\u4f60\u5427\uff0c\u4f60\u90a3\u4f4d\u6e05\u59d1\u5a18\u65e9\u5df2\u4e0e\u4eba\u5bb6\u62dc\u4e86\u5802\u6210\u4e86\u4eb2\u5566\uff01\u8fd9\u4e2a\u59d3\u6587\u7684\u5c31\u662f\u90a3\u65b0\u90ce\u7684\u53d4\u7236\u3002\u54c8 \u54c8\uff0c\u4f60\u5f53\u7740\u7537\u5973\u4e24\u5bb6\u7684\u5c0a\u957f\uff0c\u6765\u95ee\u4eba\u5bb6\u7684\u65b0\u5a18\uff0c\u8fd9\u672a\u514d\u5927\u6ca1\u6709\u793c\u8c8c\u4e86\u5427\uff1f\u201d \u3000\u3000\u6b27\u9633\u4f2f\u548c\u5927\u6012\u9053\uff1a\u201c\u8001\u8d3c\uff0c\u4f60\u7adf\u6562\u80e1\u8a00\u4e71\u8bed\uff0c\u6311\u62e8\u662f\u975e\uff0c\u5403\u6211\u4e00\u638c\uff01\u201d\u4f46\u6240\u53d7\u7684\u4f24\u6bd4\u59ec \u6653\u98ce\u66f4\u91cd\uff0c\u53cd\u88ab\u59ec\u6653\u98ce\u4e00\u638c\u9707\u9000\u3002 \u3000\u3000\u90a3\u201c\u53f6\u516c\u5b50\u201d\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5982\u6b64\uff0c\u90a3\u5c31\u8bf7\u6055\u6211\u4e0d\u77e5\uff0c\u83ab\u8981\u89c1\u602a\u3002\u201d\u6b27\u9633\u4e8c\u5a18\u5fc3\u60f3\uff1a \u201c\u4f60\u5df2\u7136\u5230\u8fc7\u6211\u5bb6\uff0c\u8d3a\u5ba2\u6599\u8fd8\u6765\u6563\uff0c\u793c\u5802\u867d\u7136\u6253\u5f97\u843d\u82b1\u6d41\u6c34\uff0c\u529e\u559c\u4e8b\u7684\u5e03\u7f6e\u4e5f\u8fd8\u770b\u5f97\u51fa\u6765\uff0c \u4f60\u5c82\u6709\u4e0d\u77e5\u4e4b\u7406\uff1f\u201d \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u6349\u6478\u4e0d\u900f\u8fd9\u201c\u53f6\u4e48\u5b50\u201d\u7684\u6765\u610f\uff0c\u5fc3\u4e2d\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u6b63\u81ea\u6109\u89d1\u4ed6\u7684\u9762\u8272\u3002\u90a3\u201c\u53f6\u516c \u5b50\u201d\u5df2\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u59d3\u59ec\u7684\u8bdd\u54b1\u4eec\u5f53\u4ed6\u653e\u5c41\uff0c\u53ef\u662f\u4e8c\u5a18\uff0c\u6211\u8fd8\u662f\u8981\u602a\u4f60\u2026\u2026\u201d \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u5fc3\u9053\uff1a\u201c\u6765\u4e86\uff0c\u6765\u4e86\uff0c\u4e14\u542c\u4ed6\u8bf4\u4ec0\u4e48\u3002\u201d\u90a3\u201c\u53f6\u516c\u5b50\u201d\u987f\u4e86\u4e00\u987f\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a \u201c\u665a\u8f88\u4e0d\u624d\uff0c\u4e0a\u6b21\u5728\u7941\u8fde\u5c71\u4e0e\u6c34\u4e91\u5e84\u4e24\u5904\uff0c\u4e5f\u66fe\u4e3a\u4f60\u5bb6\u7684\u4e8b\u60c5\uff0c\u6548\u8fc7\u5fae\u52b3\uff0c\u4eca\u65e5\u4ee4\u5a9b\u51fa\u9601\u4e4b \u559c\uff0c\u5374\u4e3a\u4f55\u541d\u60dc\u4e00\u676f\u559c\u9152\uff0c\u4e0d\u8bf7\u6211\u559d\u5462\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u53c8\u5927\u7b11\u9053\uff1a\u201c\u59d3\u53f6\u7684\uff0c\u4f60\u7684\u9762\u76ae\u4e5f\u5b9e\u5728\u592a\u539a\u4e86\uff0c\u4f60\u4e0d\u5728\u4e4e\u5403\u60c5\u4eba\u7684\u559c\u9152\u3002\u4eba\u5bb6\u505a \u4e08\u6bcd\u5a18\u7684\uff0c\u54c8\u54c8\uff0c\u5374\u600e\u597d\u610f\u601d\u628a\u5973\u513f\u7684\u91ce\u7537\u4eba\u8bf7\u6765\uff1f\u201d \u3000\u3000\u6587\u5ef7\u58c1\u559d\u9053\uff1a\u201c\u4f60\u518d\u80e1\u8bf4\uff1b\u6211\u5c31\u6495\u4f60\u7684\u5634\uff01\u201d\u4e00\u638c\u626b\u53bb\uff0c\u59ec\u6653\u98ce\u6ed1\u6b65\u95ea\u5f00\uff0c\u6c5f\u6d77\u5929\u53c8\u786c \u63a5\u4e86\u4ed6\u4e00\u638c\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u9053\uff1a\u201c\u5c0f\u5973\u8fd9\u6b21\u7684\u5a5a\u4e8b\u529e\u5f97\u4ed3\u5352\uff0c\u8def\u9014\u9065\u8fdc\uff0c\u6765\u4e0d\u53ca\u6d3e\u4eba\u9001\u559c\u5e16\u7ed9\u516c\u5b50\uff0c\u8fd8\u671b \u516c\u5b50\u89c1\u8c05\u3002\u516c\u5b50\u82e5\u80af\u8d4f\u9762\uff0c\u6211\u81ea\u5f53\u8bf7\u516c\u5b50\u9a7e\u4e34\u5bd2\u820d\uff0c\u7ed9\u4f60\u8d54\u7f6a\u3001\u9053\u8c22\u3002\u8fd8\u671b\u516c\u5b50\u518d\u5e2e\u8fd9\u4e00\u6b21 \u5fd9\u3002\u201d \u3000\u3000\u90a3\u767d\u8863\u5c11\u5e74\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u5a18\u3001\u8fd9\u4e00\u6b21\u662f\u6211\u8981\u5148\u8bf7\u4f60\u5e2e\u5fd9\uff01\u201d\u6b27\u9633\u4e8c\u5a18\u9053\uff1a\u201c\u516c \u5b50\u6709\u4e8b\uff0c\u6211\u4eec\u81ea\u5f53\u6548\u52b3\uff0c\u4f46\u773c\u524d\u8fd9\u4e24\u4e2a\u8d3c\u4e88\uff0c\u603b\u5f97\u5148\u6253\u53d1\u624d\u884c\u3002\u201d \u3000\u3000\u90a3\u767d\u8863\u5c11\u5e74\u53c8\u7b11\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u8981\u4f60\u5e2e\u5fd9\u7684\u4e8b\u60c5\u5c31\u6b63\u662f\u2014\u2014\u201d\u6b27\u9633\u4e8c\u5a18\u5fc3\u4e2d\u4e00\u51db\uff0c\u9053\uff1a \u201c\u6b63\u662f\u4ec0\u4e48\uff1f\u201d\u90a3\u767d\u8863\u5c11\u5e74\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5c31\u6b63\u662f\u8981\u8bf7\u4f60\u5c06\u4ed6\u4eec\u653e\u4e86\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u6c5f\u6d77\u5929\u56fa\u7136\u5927\u611f\u610f\u5916\uff0c\u6b27\u9633\u4e8c\u5a18\u4e5f\u662f\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u53f6\u516c\u5b50\uff0c\u4f60\u4e0d \u662f\u5f00\u73a9\u7b11\u5427\uff1f\u8fd9\u59d3\u6c5f\u7684\u5c0f\u5b50\u4e0d\u6b63\u662f\u4f60\u7684\u654c\u4eba\u5417\uff1f\u4ed6\u53c8\u662f\u91d1\u9e70\u5bab\u6240\u8981\u6349\u62ff\u7684\u4eba\u3002\u201d \u3000\u3000\u90a3\u767d\u8863\u5c11\u5e74\u677f\u8d77\u9762\u5b54\u9053\uff1a\u201c\u6211\u4ece\u6765\u4e0d\u5f00\u73a9\u7b11\uff0c\u91d1\u9e70\u5bab\u7684\u547d\u4ee4\u4e5f\u7ba1\u4e0d\u7740\u6211\uff0c\u6211\u9ad8\u5174\u5e2e\u8c01\u5c31 \u5e2e\u8c01\uff01\u201d \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u9053\uff1a\u201c\u522b\u7684\u4e8b\u60c5\u53ef\u4ee5\u7b54\u5e94\u516c\u5b50\uff0c\u8fd9\u4ef6\u4e8b\u4e48\uff0c\u8bf7\u6055\u3001\u8bf7\u6055\u4e0d\u80fd\u4ece\u547d\uff01\u201d \u3000\u3000\u90a3\u767d\u8863\u5c11\u5e74\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5934\u4e00\u6b21\u5411\u4f60\u8ba8\u60c5\uff0c\u4f60\u5c31\u4e0d\u80af\u7b54\u5e94\u4e48\uff1f\u597d\u5427\uff0c\u90a3\u4e48\u54b1\u4eec\u8fc7\u5f80\u7684\u4ea4 \u60c5\u5c31\u4e00\u7b14\u52fe\u9500\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u6df1\u77e5\u8fd9\u201c\u53f6\u516c\u5b50\u201d\u7684\u672c\u9886\uff0c\u5fc3\u60f3\uff1a\u201c\u5927\u4f2f\u5df2\u7136\u53d7\u4f24\uff0c\u5018\u7136\u4ed6\u52a9\u5bf9\u65b9\uff0c\u548c\u6c5f\u6d77\u5929 \u8054\u8d77\u624b\u6765\uff0c\u53ea\u6015\u6587\u4eb2\u5bb6\u4e5f\u7edd\u96be\u53d6\u80dc\u3002\u201d\u5979\u4e0d\u6562\u7b49\u5230\u90a3\u767d\u8863\u5c11\u5e74\u628a\u8bdd\u8bf4\u7edd\uff0c\u8fde\u5fd9\u62e6\u4f4f\u9053\uff1a\u201c\u53f6 \u516c\u5b50\uff0c\u975e\u662f\u6211\u6562\u8fdd\u547d\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u662f\u6587\u5148\u751f\u4f5c\u4e3b\u7684\u3002\u8bf7\u4f60\u5411\u6587\u5148\u751f\u8ba8\u4e00\u53e5\u8bdd\u3002\u54e6\uff0c\u4f60\u4eec\u8fd8\u6ca1 \u6709\u89c1\u8fc7\u5427\uff1f\u8fd9\u4f4d\u662f\u5929\u9b54\u6559\u7684\u6587\u526f\u6559\u4e3b\uff0c\u8fd9\u4f4d\u662f\u9a6c\u8428\u51e0\u56fd\u7684\u738b\u5b50\u53f6\u51b2\u9704\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u542c\u4e86\u6b27\u9633\u4e8c\u5a18\u8fd9\u6837\u79f0\u547c\u90a3\u201c\u53f6\u516c\u5b50\u201d\uff0c\u5927\u60d1\u4e0d\u89e3\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5018\u82e5\u4ed6\u662f\u83b2\u59b9\u7684 \u5144\u5f1f\uff0c\u90a3\u5c31\u5e94\u8be5\u662f\u9a6c\u8428\u513f\u56fd\u524d\u738b\u7684\u592a\u5b50\uff0c\u73b0\u5728\u7684\u56fd\u738b\u662f\u7be1\u4f4d\u7684\uff0c\u542c\u8bf4\u4ed6\u81ea\u7be1\u4f4d\u4e4b\u540e\uff0c\u5c31\u5e7f\u8058 \u80fd\u4eba\uff0c\u6d3e\u5230\u56db\u65b9\uff0c\u53bb\u8ffd\u67e5\u524d\u738b\u90a3\u5bf9\u513f\u5973\u7684\u4e0b\u843d\u3002\u8fd9\u59d3\u53f6\u7684\u82e5\u7136\u5c31\u662f\u524d\u738b\u7684\u592a\u5b50\uff0c\u4ed6\u600e\u6562\u8868\u9732 \u8eab\u4efd\uff1f\u7136\u5219\uff0c\u8fd9\u2018\u738b\u5b50\u2019\u7684\u79f0\u547c\u53c8\u4ece\u4f55\u6765\uff1f\u800c\u4e14\u3002\u8fd8\u4e0d\u6b62\u8fd9\u4e00\u70b9\u53ef\u7591\u2026\u2026\u201d \u3000\u3000\u6c5f\u6d77\u5929\u53c8\u518d\u60f3\u9053\uff1a\u201c\u82e5\u7136\u4ed6\u65e6\u6062\u590d\u4e86\u672c\u6765\u8eab\u4efd\uff0c\u53d8\u6210\u4e86\u9a6c\u8428\u513f\u56fd\u7684\u738b\u5b50\uff0c\u5374\u4e3a\u4f55\u8fd8\u7528\u6c49 \u4eba\u59d3\u540d\uff0c\u800c\u65c1\u4eba\u4e5f\u53ea\u662f\u79f0\u547c\u4ed6\u4f5c\u53f6\u516c\u5b50\uff1f\u201d \u3000\u3000\u4e0d\u8bf4\u6c5f\u6d77\u5929\u5fc3\u4e2d\u7591\u60d1\uff0c\u4e14\u8bf4\u6587\u5ef7\u58c1\u542c\u4e86\u6b27\u9633\u4e8c\u5a18\u7684\u4ecb\u7ecd\u540e\u3002\u5fc3\u4e2d\u4e5f\u662f\u60ca\u602a\u4e0d\u5b9a\uff0c\u5ffd\u5730\u54c8 \u54c8\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u662f\u5927\u6c34\u51b2\u5012\u9f99\u738b\u5e99\u4e86\u3002\u6211\u4e0e\u8d35\u56fd\u7684\u56fd\u5e08\u5b9d\u8c61\u6cd5\u738b\u4ea4\u60c5\u975e\u6d45\uff0c\u6b63\u62df\u5f85 \u820d\u4f84\u5b8c\u5a5a\u4e4b\u540e\uff0c\u4fbf\u4e0e\u4ed6\u540c\u8d74\u6cd5\u738b\u7684\u91d1\u9e70\u5bab\u4e4b\u4f1a\u7684\uff01\u201d \u3000\u3000\u53f6\u51b2\u9704\u8e0f\u4e0a\u4e00\u6b65\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4e45\u4ef0\u6587\u5148\u751f\u662f\u6b66\u6797\u7b2c\u4e00\u9ad8\u624b\uff0c\u91d1\u9e70\u5bab\u4e4b\u4f1a\uff0c\u5f97\u6587\u5148\u751f\u5230 \u573a\uff0c\u751f\u8272\u4e0d\u5c11\uff0c\u4f46\u4e0d\u77e5\u6587\u5148\u751f\u52a9\u54ea\u4e00\u8fb9\uff1f\u201d\u6587\u5ef7\u58c1\u9053\uff1a\u201c\u6211\u5f53\u7136\u662f\u52a9\u8d35\u56fd\u7684\u5b9d\u8c61\u6cd5\u738b\uff0c\u4f55\u987b \u518d\u95ee\uff01\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u53ea\u6015\u5728\u4f1a\u4e2d\u4e0d\u80fd\u5411\u6587\u5148\u751f\u9886\u6559\u4e86\u3002\u96be\u5f97\u5728\u6b64\u76f8\u9022\u2014\u2014\u201d \u3000\u3000\u6587\u5ef7\u58c1\u53cc\u773c\u4e00\u7ffb\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6bbf\u4e0b\u662f\u6709\u610f\u6765\u8bd5\u6211\u7684\u6b66\u529f\u4e48\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4e0d\u6562\u3002\u53ea\u662f\u6211\u4e0d\u77e5\u6240\u8c13\u2018\u6b66\u6797\u7b2c\u4e00\u9ad8\u624b\u2019\u7684\u6b66\u529f\u7a76\u7adf\u662f\u600e\u4e48\u7684\u6df1\u4e0d\u53ef\u6d4b\uff0c \u5018\u82e5\u6587\u5148\u751f\u80af\u8d50\u6559\u7684\u8bdd\uff0c\u4ee4\u6211\u5f97\u5f00\u773c\u754c\uff0c\u4e5f\u662f\u4e00\u5927\u5e78\u4e8b\uff01\u201d \u3000\u3000\u6587\u5ef7\u58c1\u5e73\u751f\u81ea\u8d1f\u60ef\u4e86\uff0c\u542c\u5f97\u53f6\u51b2\u9704\u516c\u7136\u5411\u4ed6\u6311\u6218\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u6709\u6c14\u3002\u6697\u81ea\u60f3\u9053\uff1a\u201c\u4ee5\u6211 \u548c\u5b9d\u8c61\u6cd5\u738b\u7684\u4ea4\u60c5\uff0c\u4ee5\u6211\u5728\u6b66\u6797\u4e2d\u7684\u8eab\u4efd\uff0c\u6211\u5230\u4e86\u9a6c\u8428\u513f\u56fd\uff0c\u56fd\u738b\u4e5f\u5f53\u656c\u6211\u4e09\u5206\uff0c\u83ab\u8bf4\u4f60\u4e0d \u662f\u56fd\u738b\u7684\u4eb2\u751f\u513f\u5b50\uff0c\u5c31\u7b97\u771f\u662f\u592a\u5b50\uff0c\u89c1\u4e86\u6211\u4e5f\u5f53\u5148\u884c\u665a\u8f88\u4e4b\u793c\uff01\u201d \u3000\u3000\u539f\u6765\u6587\u5ef7\u58c1\u4e4b\u6240\u4ee5\u4e0e\u5b9d\u8c61\u6cd5\u738b\u6df1\u76f8\u7ed3\u7eb3\uff0c\u4e00\u6765\u662f\u5949\u4e86\u6559\u4e3b\u4e4b\u547d\uff0c\u4e8c\u6765\u4e5f\u56e0\u4e3a\u5b9d\u8c61\u6cd5\u738b\u5929 \u7afa\u4e00\u6d3e\u7684\u6b66\u529f\uff0c\u4e5f\u786e\u6709\u7279\u5f02\u7684\u5730\u65b9\uff0c\u4e8c\u4eba\u7ed3\u7eb3\uff0c\u5bf9\u5f7c\u6b64\u7684\u6b66\u529f\u90fd\u6709\u589e\u76ca\u3002 \u3000\u3000\u90a3\u5b9d\u8c61\u6cd5\u738b\u5728\u9a6c\u8428\u513f\u56fd\u88ab\u5c01\u4e3a\u56fd\u5e08\uff0c\u56fd\u738b\u7ed9\u4ed6\u76d6\u4e86\u4e00\u5ea7\u91d1\u9e70\u5b98\uff0c\u8fd1\u5e74\u6765\u4ed6\u5e7f\u6536\u5f92\u4f17\uff0c\u52bf \u529b\u65e5\u5927\uff0c\u56fd\u738b\u5904\u5904\u8981\u501a\u4ed7\u4ed6\uff0c\u5bf9\u4ed6\u662f\u53c8\u656c\u53c8\u754f\uff0c\u8a00\u542c\u8ba1\u4ece\u3002\u6240\u4ee5\u5b9d\u8c61\u6cd5\u6b63\u5728\u9a6c\u8428\u513f\u56fd\u7684\u5730 \u4f4d\uff0c\u53ef\u4ee5\u8bf4\u51e0\u4e4e\u662f\u8d85\u8fc7\u4e86\u56fd\u738b\u3002\u6587\u5ef7\u58c1\u65e2\u4e0d\u6253\u7b97\u5728\u4e00\u4e2a\u5c0f\u56fd\u6c42\u53d6\u5bcc\u8d35\uff0c\u540c\u65f6\u53c8\u6043\u7740\u81ea\u5df1\u548c\u5ba4 \u8c61\u6cd5\u738b\u7684\u4ea4\u60c5\uff0c\u56e0\u6b64\u4e5f\u5c31\u4e0d\u6015\u5f97\u7f6a\u88ab\u9a6c\u8428\u513f\u56fd\u56fd\u738b\u8ba4\u4f5c\u738b\u5b50\u7684\u53f6\u51b2\u9704\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u5927\u60ca\u5931\u8272\uff1b\u6b63\u60f3\u529d\u963b\uff0c\u6587\u5ef7\u58c1\u5df2\u81ea\u8bf4\u9053\uff1a\u201c\u4e8c\u5a18\uff0c\u8bf7\u4f60\u548c\u5927\u4f2f\u770b\u7740\u59ec\u8001\u8d3c\uff0c\u4e0d \u8981\u8ba9\u4ed6\u9003\u53bb\u3002\u597d\u5427\uff0c\u53f6\u516c\u5b50\uff0c\u4f60\u8981\u8bd5\u62db\uff0c\u5c31\u8bf7\u6765\u5427\uff01\u201d\u8fd9\u53e3\u6c14\u5373\u662f\u5df2\u628a\u65f6\u51b2\u9704\u4e0e\u6c5f\u6d77\u5929\u5f53\u4f5c \u4e00\u4f19\uff0c\u4ed6\u8981\u4ee5\u53cc\u638c\u4e4b\u529b\u5206\u654c\u4e8c\u4eba\u3002 \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u8bf7\u4f60\u6682\u4e14\u9000\u4e0b\uff0c\u4f8d\u6211\u5411\u6587\u5148\u751f\u9886\u6559\u4e4b\u540e\u3002\u5018\u82e5\u6587\u5148\u751f\u8fd8\u662f\u4e0d\u51c6\u6211\u7684 \u4eba\u60c5\u3002\u90a3\u65f6\u518d\u8bf7\u6c5f\u5144\u76f8\u52a9\u3002\u201d\u6c5f\u6d77\u5929\u4e5f\u4e0d\u613f\u610f\u4ee5\u4e8c\u654c\u4e00\uff0c\u7a0d\u4e3a\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u4fbf\u9053\uff1a\u201c\u597d\uff0c\u90a3 \u5c31\u591a\u8c22\u53f6\u5144\u4e86\u3002\u201d \u3000\u3000\u6587\u5ef7\u58c1\u89c1\u6c5f\u6d77\u5929\u9000\u4e0b\uff0c\u66f4\u65e0\u987e\u5fcc\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u53f6\u516c\u5b50\u679c\u7136\u662f\u82f1\u96c4\u6c14\u6982\uff0c\u4e0d\u80af\u7a0d\u5360\u4fbf \u5b9c\uff0c\u5c31\u8bf7\u4eae\u5251\u5427\uff01\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u6587\u5148\u751f\uff0c\u4f60\u7528\u4ec0\u4e48\u5175\u5668\uff1f\u201d\u6587\u5ef7\u58c1\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u6211\u81ea\u5230\u4e2d\u571f\u4ee5\u6765\uff0c\u548c\u4eba\u6587 \u624b\u4e00\u4ece\u6765\u4e0d\u7528\u5175\u5668\uff01\u201d\u53f6\u51b2\u9704\u5fae\u7b11\u9053\uff1a\u201c\u542c\u8bf4\u6587\u5148\u751f\u5df2\u7ecf\u7ec3\u6210\u2018\u4e09\u8c61\u5f52\u5143\u2019\u7684\u7edd\u4e16\u795e\u529f\uff0c\u65e2 \u7136\u6587\u5148\u751f\u4e0b\u62df\u52a8\u7528\u5175\u5668\uff0c\u5c31\u8bf7\u53d1\u638c\u5427\uff01\u201d \u3000\u3000\u6587\u5ef7\u74a7\u4e0d\u7531\u5f97\u6c14\u5728\u4e0a\u51b2\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u771f\u662f\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\uff0c\u7adf\u6562\u6307\u5b9a\u8981\u6211\u4f7f\u7528\u2018\u4e09 \u8c61\u5f52\u5143\u2019\u7684\u7edd\u4e16\u795e\u529f\uff01\u201d\u5f53\u4e0b\uff0c\u4e00\u638c\u63a8\u51fa\uff0c\u53bb\u52bf\u6781\u6162\uff0c\u5230\u4e86\u53f6\u51b2\u9704\u80f8\u524d\u534a\u5c3a\u5de6\u53f3\uff0c\u4fbf\u51e0\u4e4e\u505c \u4f4f\u4e0d\u52a8\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u53f6\u516c\u5b50\uff0c\u5018\u82e5\u4f60\u611f\u5230\u6c14\u8840\u7ffb\u6d8c\uff0c\u4fbf\u8bf7\u7acb\u5373\u8dc3\u5f00\uff0c\u5207\u4e0d\u53ef\u5f3a\u884c\u8fd0\u529b\uff0c\u6211 \u81ea\u4f1a\u7ed9\u4f60\u8c03\u6cbb\uff01\u201d \u3000\u3000\u201c\u539f\u6765\u6587\u5ef7\u58c1\u867d\u7136\u607c\u6068\u53f6\u51b2\u9704\u72c2\u5984\uff0c\u4f46\u788d\u7740\u5b9d\u8c61\u6cd5\u738b\u7684\u60c5\u4efd\uff0c\u8fd8\u4e0d\u6562\u5f53\u771f\u5c31\u4f24\u4e86\u4ed6\u7684\u6027 \u547d\u3002\u6240\u4ee5\u4ed6\u8fd9\u4e00\u638c\u53ea\u7528\u4e86\u4e03\u6210\u529f\u529b\uff0c\u5e76\u4e14\u4e8b\u5148\u7ed9\u4ed6\u6307\u70b9\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u7b11\u9053\uff1a\u201c\u591a\u8c22\u5173\u7167\uff0c\u53ea\u662f\u8fd9\u8bdd\u8bf4\u5f97\u65e9\u4e86\u4e00\u70b9\u5427\uff01\u201d\u7b11\u58f0\u4e00\u6536\uff0c\u4fbf\u4e5f\u4e00\u638c\u62cd\u51fa\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u638c\uff0c\u770b\u8d77\u6765\u4f3c\u4e4e\u8f7b\u98d8\u98d8\u7684\u6beb\u4e0d\u7740\u529b\uff0c\u6587\u5ef7\u58c1\u8fd8\u4ee5\u4e3a\u4ed6\u6709\u610f\u8511\u89c6\u3002\u54ea\u77e5\u53cc\u638c\u4e00\u4ea4\uff0c \u53ea\u89c9\u5bf9\u65b9\u653b\u6765\u7684\u529b\u9053\u7adf\u5982\u5de8\u65a7\u5f00\u5c71\u3001\u94c1\u9524\u51ff\u77f3\uff0c\u521a\u52b2\u65e0\u6bd4\uff0c\u95f7\u96f7\u4f3c\u7684\u4e00\u58f0\u8fc7\u540e\u3002\u6587\u5ef7\u58c1\u7adf\u7981 \u4e0d\u4f4f\u4e0a\u8eab\u6447\u6643\uff0c\u9000\u4e86\u4e00\u6b65\uff0c\u9762\u8272\u5927\u53d8\u3002\u6c5f\u6d77\u5929\u5fc3\u4e2d\u6697\u7b11\uff1a\u201c\u8fd9\u53ae\u81ea\u5c01\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\uff0c\u5374\u4e0d\u8bc6 \u5927\u4e58\u822c\u7740\u638c\u7684\u529f\u592b\uff01\u201d \u3000\u3000\u539f\u6765\u53f6\u51b2\u9704\u6240\u7528\u7684\u201c\u5927\u4e58\u822c\u82e5\u638c\u201d\u4e43\u662f\u5b5f\u795e\u901a\u4e03\u79cd\u5389\u5bb3\u7684\u795e\u638c\u529f\u592b\u4e4b\u4e00\uff0c\u9738\u9053\u65e0\u6bd4\uff0c\u80fd \u4f24\u5bf9\u65b9\u7684\u5947\u7ecf\u516b\u8109\u3001\u4e0a\u6b21\u4e91\u743c\u5144\u59b9\uff0c\u5c31\u662f\u9669\u4e9b\u88ab\u4ed6\u7684\u201c\u5927\u4e58\u822c\u82e5\u638c\u201d\u9001\u4e86\u6027\u547d\u7684\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u7684\u529f\u529b\u672c\u6765\u8981\u80dc\u8fc7\u53f6\u51b2\u9704\u4e00\u7b79\uff0c\u4f46\u4e00\u6765\u4ed6\u53d7\u4e86\u4e8e\u5927\u9e4f\u4e34\u6b7b\u7684\u62fc\u547d\u4e00\u51fb\uff0c\u4e8c\u6765\u4ed6\u4e0e \u6c5f\u6d77\u5929\u4fbf\u5bf9\u4e86\u51e0\u638c\uff0c\u771f\u529b\u8017\u635f\u4e0d\u5c11\uff0c\u4e09\u6765\u4ed6\u53c8\u53ea\u7528\u4e03\u6210\u529f\u529b\uff0c\u6b64\u6d88\u5f7c\u957f\uff0c\u7ed3\u679c\u7adf\u7136\u843d\u5728\u4e0b\u98ce\uff01 \u3000\u3000\u6587\u5ef7\u58c1\u9762\u8272\u5927\u53d8\uff0c\u5f3a\u81ea\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\u529f\u592b\uff01\u201d\u53cc\u773c\u4e00\u77aa\uff0c\u95ea\u7535\u822c\u7684\u53c8\u518d\u4e00\u638c\u5288\u4e0b\u3002\u8fd9 \u4e00\u638c\u4ed6\u54ea\u91cc\u8fd8\u6562\u8f7b\u654c\uff0c\u7adf\u7136\u4f7f\u51fa\u4e86\u5341\u6210\u672c\u9886\uff01 \u3000\u3000\u4f46\u542c\u5f97\u53c8\u662f\u95f7\u96f7\u4f3c\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u53f6\u51b2\u9704\u7ed9\u9707\u5f97\u629b\u4e86\u8d77\u6765\uff0c\u6587\u5ef7\u58c1\u5219\u4e0d\u4f4f\u7684\u540e\u9000\uff0c\u5634\u89d2\u6c81 \u51fa\u8840\u4e1d\uff0c\u989d\u89d2\u6c57\u6d41\u5982\u96e8\uff01\u539f\u6765\u4ed6\u56e0\u4e00\u5ff5\u8f7b\u654c\uff0c\u7b2c\u4e00\u6b21\u63a5\u638c\u65f6\u672a\u7528\u5168\u529b\uff0c\u5947\u7ecf\u516b\u8109\u5df2\u53d7\u9707\u8361\uff0c \u7b2c\u4e8c\u6b21\u867d\u7136\u4f7f\u51fa\u6d51\u8eab\u672c\u9886\uff0c\u529f\u529b\u5374\u5df2\u6253\u4e86\u6298\u6263\u4e86\u3002\u6240\u4ee5\u8fd9\u6b21\u6bd4\u4e86\u4e0b\u6765\uff0c\u4ecd\u7136\u53ea\u662f\u534a\u65a4\u516b\u4e24\uff0c \u5f7c\u6b64\u90fd\u6ca1\u5360\u5230\u4fbf\u5b9c\u3002 \u3000\u3000\u53f6\u51b2\u9704\u5728\u534a\u7a7a\u4e2d\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u843d\u4e86\u4e0b\u6765\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6587\u5148\u751f\u662f\u5426\u611f\u5230\u5185\u606f\u4e0d\u8c03\uff0c\u8bf7 \u56de\u53bb\u9759\u517b\u5427\uff01\u201d \u3000\u3000\u6587\u5ef7\u58c1\u8fd9\u65f6\u6b63\u5728\u9ed8\u8fd0\u7384\u529f\uff0c\u679c\u7136\u611f\u89c9\u5230\u6709\u5185\u606f\u4e0d\u8c03\uff0c\u7ecf\u8109\u963b\u6ede\u7684\u73b0\u8c61\uff0c\u5fc3\u5185\u6697\u6697\u5403\u60ca\uff0c \u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\u9634\u72e0\u7684\u638c\u529b\uff0c\u4f46\u53ea\u6015\u4f60\u8fd9\u6b21\u786c\u5145\u597d\u6c49\uff0c\u5bf9\u4f60\u4e5f\u662f\u6709\u635f\u65e0\u76ca\uff0c\u4f60\u4e5f\u8981\u5c0f\u5fc3 \u8c03\u6cbb\u4e86\u3002\u4e0b\u6708\u5341\u4e94\u91d1\u9e70\u5bab\u4e4b\u4f1a\uff0c\u5230\u65f6\u5018\u82e5\u516c\u5b50\u65e0\u6059\uff0c\u6211\u518d\u6765\u5411\u516c\u5b50\u8ba8\u6559\u5427\uff01\u201d \u3000\u3000\u6587\u5ef7\u58c1\u4e00\u8d70\uff0c\u6b27\u9633\u4f2f\u548c\u4e0e\u6b27\u9633\u4e8c\u5a18\u5f53\u7136\u4e5f\u5c31\u76f8\u7ee7\u79bb\u5f00\uff0c\u6b27\u9633\u4e8c\u5a18\u4e34\u8d70\u65f6\u8fd8\u671b\u4e86\u65f6\u51b2\u9704\u4e00 \u773c\uff0c\u4f3c\u4e4e\u6709\u8bdd\u8981\u8bf4\uff0c\u4f46\u788d\u7740\u6587\u5ef7\u58c1\u5728\u65c1\uff0c\u5374\u672a\u66fe\u5f00\u53e3\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u89c1\u53f6\u51b2\u9704\u9762\u8272\u7070\u767d\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4e0d\u788d\u4e8b\u5427\uff1f\u201d\u53f6\u51b2\u9704\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa \u6765\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u9b54\u5934\u679c\u7136\u597d\u5389\u5bb3\uff0c\u4f46\u4e5f\u8fd8\u672a\u80fd\u8981\u4e86\u6211\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6b32\u4ee5\u672c\u8eab\u529f\u529b\u52a9\u4ed6\u7597\u4f24\uff0c\u6b63\u5f85\u542f\u9f7f\uff0c\u53f6\u51b2\u9704\u5ffd\u5730\u54c8\u54c8\u4e00\u7b11\uff0c\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u6c5f\u5c0f \u4fa0\uff0c\u4f60\u73b0\u5728\u8fd8\u6068\u6211\u4e48\uff1f\u6211\u4f24\u4e86\u4e91\u743c\u5144\u59b9\uff0c\u7d2f\u4f60\u8f93\u8840\u6551\u4eba\u3002\u75c5\u4e86\u597d\u51e0\u5929\uff0c\u5b9e\u5728\u8fc7\u610f\u4e0b\u53bb\uff0c\u6211\u8fd9 \u53a2\u7ed9\u4f60\u8d54\u793c\u4e86\uff1b\u54ce\uff1a\u4f60\u76b1\u7740\u7709\u5934\u3001\u8e6c\u7740\u773c\u775b\uff0c\u6562\u60c5\u8fd8\u662f\u5bbf\u6068\u672a\u6d88\uff1f\u597d\u5427\uff0c\u5018\u82e5\u4f60\u613f\u610f\u548c\u6211\u4ea4 \u4e2a\u670b\u53cb\uff0c\u6211\u5c31\u7559\u4e0b\u6765\uff0c\u54b1\u4eec\u518d\u8c08\uff0c\u5426\u5219\u5c31\u6b64\u522b\u8fc7\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u5730\u5bbd\u539a\uff0c\u672c\u6765\u5bf9\u4ed6\u5df1\u662f\u53ea\u6709\u611f\u6fc0\u3002\u5fd8\u4e86\u65e7\u6068\u7684\u4e86\uff0c\u4f46\u542c\u4ed6\u63d0\u8d77\u5f80\u4e8b\uff0c\u5374\u4e0d\u7531\u5f97 \u60f3\u8d77\u4ed6\u5bf9\u4e91\u743c\u5144\u59b9\u7684\u9634\u72e0\u624b\u6bb5\uff0c\u548c\u5192\u5145\u81ea\u5df1\u5077\u88ad\u4e91\u5bb6\u5e84\u7684\u79cd\u79cd\u52a3\u8ff9\u6076\u884c\uff0c\u4e0d\u7531\u5f97\u53c8\u751f\u4e86\u51e0\u5206 \u6076\u611f\u3002\u4ed6\u662f\u4e2a\u76f4\u5fc3\u773c\u7684\u4eba\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u4fbf\u5373\u8bf4\u9053\uff1a\u201c\u53f6\u516c\u5b50\uff0c\u4eca\u65e5\u8499\u4f60\u76f8\u52a9\uff0c\u4f60\u6211\u4e4b\u95f4\u7684\u65e7 \u5e10\u4e00\u7b14\u52fe\u9500\uff0c\u4e0d\u5fc5\u518d\u63d0\u3002\u4f46\u534e\u5bb6\u4e91\u5bb6\u8981\u627e\u4f60\u7b97\u5e10\uff0c\u6211\u5c31\u7ba1\u4e0d\u7740\u4e86\uff01\u8bf4\u8bdd\u5728\u524d\uff0c\u4e8b\u5230\u5176\u65f6\uff0c\u6211 \u51b3\u4e0d\u4f1a\u5e2e\u4f60\uff01\u201d \u3000\u3000\u53f6\u51b2\u9704\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6c5f\u5c0f\u4fa0\u679c\u7136\u662f\u4e2a\u723d\u5feb\u7684\u4eba\uff0c\u4f60\u8bf4\u5f97\u6709\u7406\uff0c\u5404\u987e\u5404\u7684\u4ea4\u60c5\uff0c\u5404\u4eba\u7ba1\u5404 \u4eba\u7684\u4e8b\uff0c\u6211\u4e5f\u51b3\u4e0d\u4f1a\u5f3a\u4eba\u6240\u96be\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u4f60\u4eca\u65e5\u52a9\u6211\u8131\u96be\uff0c\u6211\u5148\u5411\u4f60\u9053\u8c22\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u4e5f\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u53f6\u516c\u5b50\uff0c\u4f60\u521a\u624d\u9a82\u6211\u8bf4\u8bdd\u653e\u5c41\uff0c\u6211\u4e5f\u4e0d\u602a\u4f60\u4e86\u3002\u6211\u4e0d\u60ef\u9886 \u4eba\u7684\u60c5\u3001\u8fd9\u91cc\u6709\u534a\u652f\u7075\u829d\uff0c\u54b1\u4eec\u5206\u98df\u4e86\u5427\u3002\u600e\u4e48\uff0c\u4f60\u7741\u5927\u4e86\u773c\u775b\u770b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u6b27\u9633\u4f2f\u548c\u7684\u4e1c\u897f\uff1f\u4f60\u628a\u5b83\u5077\u6765\u4e86\u3002\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u501f\u82b1 \u656c\u4f5b\uff0c\u987a\u4fbf\u8fd8\u4f60\u7684\u4eba\u60c5\u3002\u4f60\u522b\u62c5\u5fc3\uff0c\u4f60\u73b0\u5728\u662f\u6211\u4f84\u513f\u7684\u670b\u53cb\u4e86\uff0c\u6211\u4e0d\u4f1a\u6109\u4f60\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u5c06\u7075\u829d\u6298\u4e3a\u4e24\u6bb5\uff0c\u53f6\u51b2\u9704\u63a5\u4e86\u8fc7\u6765\uff0c\u7b11\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u4e4b\u95f4\u7684\u5e10\u4e00\u7b14\u52fe\u9500\uff0c \u4f60\u4e0d\u602a\u6211\uff0c\u6211\u4e5f\u4e0d\u602a\u4f60\u4e86\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u541e\u98df\u4e86\u90a3\u534a\u6bb5\u7075\u829d\uff0c\u7cbe\u795e\u4e00\u773c\uff0c\u8d5e\u9053\uff1a\u201c\u679c\u7136\u662f\u597d\u4e1c\u897f\uff01\u201d\u4ed6\u5185\u529f\u6df1\u539a\uff0c\u4ee5\u6c14\u5bfc \u5f15\uff0c\u7075\u829d\u7684\u836f\u529b\u8fd0\u884c\u5168\u8eab\uff0c\u81ea\u662f\u6613\u4e8e\u89c1\u6548\u3002 \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u4f46\u64c5\u957f\u5077\u4e1c\u897f\uff0c\u8fd8\u62ff\u624b\u7ed9\u4eba\u4f20\u4e66\u9012\u7b80\uff0c\u7a7f\u9488\u5f15\u7ebf\uff0c\u4f60\u53ef\u8981\u6211\u6548\u52b3 \u4e48\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u591a\u8c22\u4e86\uff0c\u6211\u73b0\u5728\u7528\u4e0d\u7740\u3002\u5230\u7528\u5f97\u7740\u7684\u65f6\u5019\uff0c\u6211\u81ea\u4f1a\u60c5\u4f60\u5e2e\u5fd9\u3002\u201d\u59ec\u6653\u98ce \u9053\uff1a\u201c\u4f60\u4e0d\u662f\u6b22\u559c\u6b27\u9633\u5bb6\u7684\u5927\u5c0f\u59d0\u5417\uff1f\u96be\u9053\u4f60\u5f53\u771f\u613f\u610f\u5979\u505a\u90a3\u59d3\u6587\u7684\u65b0\u5a18\uff1f\u201d \u3000\u3000\u8981\u77e5\u59ec\u6653\u98ce\u662f\u4e2a\u5f80\u60c5\u4e2d\u4eba\uff0c\u4ed6\u867d\u7136\u4e00\u76f4\u6ca1\u6709\u7ed3\u5a5a\uff0c\u4e5f\u4ece\u672a\u5f97\u8fc7\u5973\u4eba\u7684\u9752\u7750\uff0c\u4f46\u4ed6\u5374\u662f\u751f \u6210\u4e86\u7231\u7ba1\u95f2\u4eab\u7684\u6027\u683c\uff0c\u613f\u89c1\u201c\u5929\u4e0b\u6709\u60c5\u4eba\u7ec8\u6210\u7737\u5c5e\u201d\uff0c\u6240\u4ee5\u4ed6\u66fe\u7ecf\u4e3a\u4e86\u60f3\u4fc3\u4f7f\u91d1\u4e16\u9057\u4e0e\u8c37\u4e4b \u534e\u7684\u590d\u5408\u800c\u5954\u6ce2\uff0c\u800c\u53c8\u4e3a\u4e86\u4ed6\u4eec\u7684\u7ec8\u4e8e\u4e0d\u80fd\u590d\u5408\u55df\u53f9\u4e0d\u5df3\u3002 \u3000\u3000\u53f6\u51b2\u9704\u5927\u7b11\u9053\uff1a\u201c\u6211\u82e5\u60f3\u8981\u6b27\u9633\u5bb6\u7684\u5927\u5c0f\u59d0\uff0c\u6211\u4e0d\u4f1a\u4e58\u7740\u90a3\u65b0\u90ce\u73b0\u5728\u53d7\u4f24\u7684\u65f6\u5019\uff0c\u4e00\u638c \u5c06\u4ed6\u51fb\u6bd9\u5417\uff1f\u54c8\uff0c\u6b27\u9633\u6e05\u5ac1\u7ed9\u90a3\u59d3\u6587\u7684\uff0c\u6b63\u5408\u6211\u7684\u5fc3\u610f\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u5947\u9053\uff1a\u201c\u90a3\u59d3\u6587\u7684\u662f\u4e2a\u51fa\u4e86\u540d\u7684\u574f\u86cb\uff0c\u4f60\u5373\u4f7f\u4e0d\u559c\u6b22\u6b27\u9633\u59d1\u5a18\uff0c\u5374\u600e\u5012\u613f\u610f\u770b\u89c1 \u5979\u5ac1\u7ed9\u574f\u86cb\u5462\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4f60\u4e0a\u4e86\u5e74\u7eaa\uff0c\u8fd9\u4e9b\u7537\u5973\u7684\u4e8b\u4f60\u662f\u4e0d\u61c2\u7684\u4e86\u3002\u5979\u5ac1\u4e86\u4e00\u4e2a\u4e0d\u559c\u6b22\u7684\u4eba\uff0c\u624d\u4f1a \u6000\u5ff5\u6211\u5440\u3002\u800c\u6211\u5462\uff0c\u53c8\u4e50\u5f97\u6beb\u65e0\u62d8\u675f\uff0c\u6446\u8131\u4e86\u5979\u7684\u7ea0\u7f20\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u77aa\u8d77\u4e86\u773c\u775b\uff0c\u9065\u9065\u5934\u9053\uff1a\u201c\u597d\u53e4\u602a\u7684\u5ff5\u5934\uff0c\u4f60\u4eec\u5c11\u5e74\u4eba\u7684\u5fc3\u610f\uff0c\u6211\u771f\u662f\u4e0d\u61c2\uff0c\u4e0d \u61c2\uff01\u201d\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4e0d\u4f46\u5fc3\u72e0\u624b\u8fa3\uff0c\u800c\u4e14\u8fd8\u662f\u4e2a\u8f7b\u8584\u7684\u5c11\u5e74\uff01\u201d\u5bf9\u4ed6\u7684\u6076\u610f\uff0c\u53c8\u6dfb \u4e86\u4e24\u5206\u3002 \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u59ec\u5148\u751f\uff0c\u4f60\u662f\u4e2a\u70ed\u5fc3\u4eba\uff0c\u6211\u5fc3\u9886\u4f60\u7684\u597d\u610f\uff0c\u5c06\u6765\u6211\u4e5f\u8bb8\u8981\u6c42\u4f60\u5e2e\u5fd9\u3002\u73b0\u5728 \u5462\uff0c\u6211\u5374\u8981\u5148\u8bf7\u6c5f\u5144\u5e2e\u5fd9\u6211\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff0c\u201c\u8bf7\u8bf4\uff0c\u53ea\u8981\u662f\u4e0d\u8fdd\u80cc\u6211\u7684\u826f\u5fc3\uff0c\u6211\u90fd \u53ef\u4ee5\u5c3d\u529b\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u975e\u4f46\u4e0d\u8fdd\u80cc\u4f60\u7684\u826f\u5fc3\uff0c\u800c\u4e14\u4f60\u4e0d\u505a\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4f60\u5c31\u4f1a\u5fc3\u6709\u4e0d \u5b89\u7684\u3002\u8bf7\u8ddf\u6211\u6765\u5427\uff01\u201d\u6c5f\u6d77\u5929\u89c1\u4ed6\u7b11\u5f97\u8be1\u79d8\uff0c\u9887\u6709\u6000\u7591\u3002 \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u653e\u5fc3\uff0c\u6211\u51b3\u4e0d\u4f1a\u5bb3\u4f60\u3002\u6211\u8fd8\u6709\u8981\u7d27\u4e4b\u4e8b\u548c\u4f60\u5546\u91cf\u5462\u3002\u59ec\u5148\u751f\uff0c\u52b3\u9a7e\u4f60\u628a\u8fd9 \u4e8e\u5927\u9e4f\u7684\u5c38\u4f53\u57cb\u4e86\uff0c\u7136\u540e\u5230\u524d\u5c71\u6765\u4f1a\u6211\u4eec\u3002\u201d \u3000\u3000\u59ec\u6653\u51e4\u9053\uff1a\u201c\u5bf9\uff0c\u6211\u8fd8\u6ca1\u6709\u7eb7\u8fd9\u4f4d\u8001\u670b\u53cb\u9053\u8c22\uff01\u201d\u4ed6\u5411\u4e8e\u5927\u9e4f\u7684\u5c38\u4f53\u62dc\u4e86\u4e09\u62dc\uff0c\u8bf4\u9053\uff1a \u201c\u8001\u4e8e\uff0c\u4f60\u5bf9\u6211\u7684\u597d\u5904\uff0c\u6211\u4f1a\u6c38\u8fdc\u8bb0\u7740\u3002\u4f60\u4ea4\u6258\u7684\u4e8b\uff0c\u6211\u4e00\u5b9a\u4f1a\u7ed9\u4f60\u529e\u5230\u3002\u4f60\u597d\u597d\u8d70\u5427\u3002\u201d \u4ed6\u751f\u6027\u6ed1\u7a3d\uff0c\u4f46\u8bf4\u8fd9\u51e0\u53e5\u8bdd\u65f6\u9762\u5bb9\u5374\u751a\u4e3a\u4e25\u8083\uff0c\u773c\u7736\u4e2d\u5145\u6ee1\u4e86\u6cea\u6c34\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u60f3\u8d77\u4ed6\u7236\u5b50\u53cc\u4ea1\uff0c\u66f4\u4e3a\u4f24\u611f\u3002\u4e5f\u4e0a\u53bb\u78d5\u4e86\u4e09\u4e2a\u5934\uff0c\u5fc3\u60f3\uff1a\u201c\u4ed6\u56fa\u7136\u6b7b\u5f97\u4e0d\u503c\u3002\u4ed6 \u513f\u5b50\u66f4\u662f\u53ef\u60b2\uff0c\u4e8e\u5c11\u9cb2\u867d\u7136\u662f\u90aa\u6d3e\u4e2d\u4eba\uff0c\u5374\u5177\u6709\u81f3\u60c5\u81f3\u6027\uff0c\u770b\u6765\u8981\u6bd4\u8fd9\u53f6\u516c\u5b50\u80dc\u8fc7\u591a\u591a\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u4e00\u8138\u5b54\u4e0d\u8010\u70e6\u7684\u795e\u6c14\uff0c\u5c06\u6c5f\u6d77\u5929\u62c9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4eba\u6b7b\u4e0d\u80fd\u590d\u751f\uff0c\u4f60\u4eec\u4e5f\u4e0d\u5fc5 \u8fd9\u6837\u5a46\u5a46\u5988\u5988\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u9887\u4e3a\u4e0d\u6ee1\uff0c\u4f46\u4e5f\u4e0d\u4fbf\u53cd\u5507\u76f8\u7a3d\uff0c\u53ea\u597d\u8ddf\u7740\u4ed6\u8d70\u3002\u59ec\u6653\u98ce\u627e\u4e86\u4e00\u67c4 \u9504\u5934\uff0c\u81ea\u5230\u540e\u56ed\u6398\u571f\uff0c\u51c6\u5907\u63a9\u57cb\u4e8e\u5927\u9e4f\uff0c\u4e0d\u63d0\u3002 \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u4eba\u5728\u7b49\u7740\u54b1\u4eec\uff0c\u4e0d\u53ef\u4ee4\u5979\u7b49\u5f97\u592a\u8fc7\u5fc3\u7126\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u662f\u4ec0\u4e48 \u4eba\uff1f\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4f60\u89c1\u4e86\u81ea\u7136\u77e5\u9053\u3002\u201d\u8fc8\u5f00\u5927\u6b65\u4fbf\u8d70\uff0c\u6c5f\u6d77\u5929\u89c1\u4ed6\u5728\u53d7\u4f24\u4e4b\u540e\uff0c\u4ecd\u7136\u5065\u6b65 \u5982\u98de\uff0c\u4e5f\u4e0d\u7981\u6697\u6697\u4f69\u670d\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6ee1\u8179\u7591\u56e2\uff0c\u6025\u4e8e\u60f3\u63ed\u5f00\u4ed6\u8eab\u4e16\u4e4b\u8c1c\uff0c\u4e5f\u8fc8\u5f00\u5927\u6b65\uff0c\u4e0e\u4ed6\u5e76\u80a9\u800c\u884c\uff0c\u95ee\u9053\uff1a\u201c\u542c\u53f6 \u5144\u7684\u53e3\u97f3\uff0c\u4f3c\u4e4e\u662f\u6cb3\u5357\u9648\u7559\u4eba\u6c0f\uff0c\u4e0d\u77e5\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4e0d\u60c5\uff0c\u6211\u5c11\u5c0f\u79bb\u5bb6\uff0c\u4e61\u97f3 \u672a\u6539\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u9648\u56fd\u6709\u4e00\u4f4d\u53f6\u541b\u5c71\u3001\u53f6\u8001\u524d\u8f88\uff0c\u4e0d\u77e5\u4e0e\u53f6\u5144\u600e\u6837\u79f0\u547c\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u6b63\u662f\u5bb6\u7236\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u53ea\u6015\u771f\u7684\u662f\u83b2\u59b9\u7684\u54e5\u54e5\u3002\u201d\u53c8\u95ee\u9053\uff1a\u201c\u521a\u624d\u6211\u542c \u5f97\u4ed6\u4eec\u79f0\u4f60\u505a\u6bbf\u4e0b\u2026\u2026\u201d \u3000\u3000\u53f6\u51b2\u9704\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8bf4\u6765\u771f\u662f\u5947\u9047\uff0c\u6211\u81ea\u5e7c\u4e27\u7236\uff0c\u6d41\u6d6a\u585e\u5916\uff0c\u5f97\u9a6c\u8428\u513f\u56fd\u738b\u6536\u517b\uff0c\u8ba4\u4e3a \u4e49\u5b50\uff0c\u76ee\u4e0b\u6211\u5728\u9a6c\u8428\u513f\u56fd\u5b98\u5c4a\u2018\u6267\u91d1\u543e\u2019\u5c06\u519b\u4e4b\u804c\u3002\u56fd\u738b\u672c\u6765\u8d50\u6211\u5fa1\u59d3\uff0c\u503c\u6211\u8fd8\u662f\u6b22\u559c\u4eba\u5bb6 \u53eb\u6211\u505a\u53f6\u51b2\u9704\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u66f4\u662f\u8d77\u7591\uff0c\u6b63\u5f85\u518d\u95ee\uff0c\u53f6\u51b2\u9704\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u5230\u4e86\uff0c\u5230\u4e86\u3002\u4f60\u770b\u5979\u662f\u8c01\uff1f\u201d \u3000\u3000\u53ea\u89c1\u5c71\u5773\u91cc\u8f6c\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u5728\u91ce\u82b1\u4e1b\u4e2d\uff0c\u6b63\u81ea\u5411\u8fd9\u8fb9\u671b\u6765\u3002\u6c5f\u6d77\u5929\u53c8\u60ca\u53c8\u559c\u3002\u53eb\u9053\uff1a \u201c\u6b27\u9633\u5a49\uff0c\u662f\u4f60\u5440\uff1f\u4f60\u8fd8\u6ca1\u6709\u8d70\u5417\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u7b11\u9053\uff1a\u201c\u6211\u5c31\u662f\u53eb\u4ece\u6765\u7684\u3001\u5979\u8fd8\u6ca1\u77e5\u9053\u4f60\u7684\u6d88\u606f\uff0c\u600e\u4f1a\u8d70\u5462\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u8fce\u4e0a\u524d\u6765\uff0c\u4ed4\u7ec6\u6253\u91cf\u4e86\u6c5f\u6d77\u5929\u4e00\u4f1a\uff0c\u5401\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u4f60\u6ca1\u6709\u53d7 \u4f24\u3002\u201d\u5173\u5207\u4e4b\u60c5\uff0c\u6ea2\u4e8e\u8a00\u8868\u3002\u53f6\u51b2\u9704\u7b11\u9053\uff1a\u201c\u5e78\u4e0d\u8fb1\u547d\uff0c\u5c06\u4f60\u7684\u6c5f\u76f8\u516c\u5e26\u6765\u4e86\u3002\u4f60\u53ef\u4ee5\u653e\u5fc3 \u4e86\u5427\uff1f\u201d\u6b27\u9633\u5a49\u95ee\u9053\uff1a\u201c\u6211\u5a18\u5462\uff1f\u201d\u53f6\u51b0\u9704\u9053\uff1a\u201c\u4f60\u5a18\u4e5f\u65e0\u635f\u4f24\u3002\u6211\u5c06\u90a3\u6587\u5ef7\u58c1\u6253\u9000\uff0c\u4f60\u5a18 \u548c\u4f60\u5927\u4f2f\u90fd\u56de\u5bb6\u4e86\u3002\u201d \u3000\u3000\u6b27\u9633\u5a49\u9053\uff1a\u201c\u591a\u8c22\u53f6\u516c\u5b50\uff0c\u591a\u8c22\u6c5f\u76f8\u516c\u3002\u521a\u624d\u771f\u662f\u5413\u715e\u6211\u4e86\u3002\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4f60 \u5374\u662f\u4e0d\u80fd\u56de\u5bb6\u4e86\u3002\u201d\u6b27\u9633\u5a49\u4f4e\u4e0b\u4e86\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u73b0\u5728\u6b63\u6ca1\u4e3b\u610f\u3002\u201d\u53f6\u51b2\u9704\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u6211\u5c31\u662f\u7ed9\u4f60\u627e\u4e00\u4e2a\u53ef\u4ee5\u4f5c\u4e3b\u7684\u4eba\u6765\u4e86\u3002\u6c5f\u5144\uff0c\u4f60\u5f85\u5c06\u5979\u5982\u4f55\u5904\u7f6e\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6014\u4e86\u4e00\u6014\uff0c\u970e\u65f6\u95f4\u6ee1\u9762\u901a\u7ea2\u3002\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u6b27\u9633\u59d1\u5a18\u6bd4\u6211\u806a\u660e\u767e \u500d\uff0c\u5c82\u65e0\u81ea\u5904\u4e4b\u9053\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u53cc\u7709\u4e00\u76b1\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6c5f\u5144\u6b64\u8a00\u5dee\u77e3\uff01\u4f60\u4eec\u662f\u81ea\u547d\u4fa0\u4e49\u9053\u7684\u4eba\u7269\uff0c\u5c82\u4e0d\u95fb\uff1a\u6551 \u4eba\u5c31\u8981\u6551\u5f97\u5f7b\u5e95\uff0c\u9001\u4f5b\u5c31\u8981\u9001\u4e0a\u897f\u5929\uff1f\u4f55\u51b5\u6b27\u9633\u59d1\u5a18\u662f\u4e3a\u4e86\u4f60\u7684\u7f18\u6545\uff0c\u624d\u548c\u5bb6\u91cc\u95f9\u7ffb\u7684\uff01\u201d \u3000\u3000\u6b27\u9633\u5a49\u4f4e\u5782\u7c89\u9888\uff0c\u8f7b\u63a0\u4e91\u9b13\uff0c\u5e7d\u5e7d\u6028\u6028\u4ed6\u8bf4\u9053\uff1a\u201c\u6c5f\u76f8\u516c\uff0c\u6211\u7684\u7239\u5a18\u5c61\u6b21\u8981\u60f3\u5bb3\u4f60\uff0c\u6211 \u5b9e\u5728\u53c8\u662f\u7f9e\u6127\uff0c\u53c8\u662f\u96be\u8fc7\u3002\u6211\u5df2\u7ecf\u542c\u4ece\u4f60\u7684\u826f\u8a00\u529d\u544a\uff0c\u4ece\u6b64\u79bb\u5f00\u4ed6\u4eec\uff0c\u4eca\u540e\u662f\u51b3\u4e0d\u56de\u5bb6\u7684 \u4e86\uff0c\u53ea\u662f\u51f9\u6d77\u832b\u832b\uff0c\u4f60\u53eb\u6211\u6295\u5954\u4f55\u5904\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u731b\u5730\u60f3\u8d77\uff0c\u521a\u624d\u4ed6\u5728\u4e8e\u5bb6\u89e3\u5f00\u6b27\u9633\u5a49\u7684\u7a74\u9053\uff0c\u50ac\u4fc3\u8baa\u9003\u8d70\u7684\u65f6\u5019\uff0c\u7531\u4e8e\u4e00\u65f6\u6fc0 \u52a8\uff0c\u7684\u786e\u662f\u66fe\u8bf4\u8fc7\u8fd9\u6837\u7684\u8bdd\uff1a\u201c\u8fd9\u6837\u7684\u7236\u6bcd\uff0c\u4f60\u4e0d\u8ba4\u4e5f\u7f62\uff0c\u5c31\u662f\u4ee5\u540e\u6c38\u4e0d\u518d\u89c1\u4ed6\u4eec\uff0c\u4e5f\u6ca1\u6709 \u4ec0\u4e48\u53ef\u60dc\u4e86\u3002\u201d\u5f53\u65f6\u6ca1\u6709\u6df1\u601d\u719f\u8651\uff0c\u73b0\u5728\u60f3\u6765\uff0c\u8fd9\u8bdd\u5b9e\u5728\u662f\u8bf4\u5f97\u5f88\u4e0d\u59a5\u5f53\uff0c\u8f7b\u7387\u3001\u5192\u6627\uff0c\u975e \u4f46\u662f\u201c\u4ee5\u758f\u95f4\u4eb2\u201d\u800c\u4e14\u5bb9\u6613\u6559\u4eba\u8bef\u4f1a\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u672c\u6765\u5c31\u4e0d\u64c5\u8a00\u8f9e\uff0c\u8fd9\u65f6\u5f53\u771f\u662f\u5c34\u5c2c\u4e4b\u6781\uff0c\u5fc3\u91cc\u6697\u9053\uff1a\u201c\u7cdf\u4e86\uff0c\u7cdf\u4e86\uff01\u8fd9\u771f\u662f\u81ea\u627e \u9ebb\u70e6\u4e86\u3002\u60f3\u4e0d\u5230\u5979\u7adf\u7136\u5c31\u6293\u7740\u6211\u8fd9\u51e0\u53e5\u8bdd\uff0c\u597d\u4f3c\u4ece\u4eca\u4ee5\u540e\uff0c\u5979\u8fd9\u4e2a\u4eba\uff0c\u5c31\u5f97\u7531\u6211\u8d1f\u8d23\u4e86\uff0c\u8fd9 \u5374\u6559\u6211\u5982\u4f55\u5bf9\u4ed8\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u53c8\u903c\u7d27\u4e86\u4e00\u53e5\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u5979\u542c\u4ece\u4e86\u4f60\u7684\u8bdd\uff0c\u79bb\u5f00\u4e86\u7236\u6bcd\uff0c\u5979\u4e00\u4e2a\u5b64\u8eab\u5973 \u5b50\uff0c\u96be\u9053\u4f60\u53eb\u5979\u6d41\u843d\u6c5f\u6e56\u5417\uff1f\u4f60\u600e\u80fd\u64a4\u624b\u4e0d\u7ba1\uff01\u201d\u6c5f\u6d77\u5929\u6ee1\u9762\u901a\u7ea2\uff0c\u65e0\u53ef\u5948\u4f55\u644a\u5f00\u53cc\u624b\u8bf4 \u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u53eb\u6211\u5982\u4f55\u7ba1\u6cd5\uff1f\u6211\u81ea\u5df1\u4e5f\u6b63\u5728\u56db\u65b9\u6d41\u6d6a\uff0c\u5bfb\u627e\u7236\u5e08\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u5fae\u7b11\u9053\uff1a\u201c\u6211\u7ed9\u4f60\u51fa\u4e2a\u4e3b\u610f\u5427\uff0c\u542c\u8bf4\u4f60\u8981\u524d\u5f80\u655d\u56fd\uff0c\u53ef\u662f\u771f\u7684\uff1f\u201d\u6c5f\u6d77\u5929\u6700\u6015\u4ed6 \u9523\u5bf9\u9523\u3001\u9f13\u5bf9\u9f13\u7684\u5f53\u9762\u5824\u4eb2\uff0c\u73b0\u5728\u542c\u5f97\u4ed6\u95ee\u7684\u5374\u662f\u53e6\u4e00\u4ef6\u4e8b\u60c5\uff0c\u7a0d\u5fae\u677e\u4e86\u53e3\u6c14\uff0c\u5f53\u4e0b\u7b54\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u6211\u66fe\u4ee3\u5cb7\u5c71\u6d3e\u7684\u638c\u95e8\u8c37\u5973\u4fa0\uff0c\u63a5\u4e0b\u4e86\u8d35\u56fd\u91d1\u9e70\u5bab\u4e3b\u4eba\u7684\u8bf7\u5e16\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u8fd9\u5c31\u6700\u597d\u4e0d\u8fc7\u4e86\uff0c\u6211\u6709\u70b9\u4e8b\u60c5\uff0c\u8fd8\u8981\u5230\u522b\u7684\u5730\u65b9\u8d70\u4e00\u8d9f\uff0c\u8bf4\u4e0d\u5b9a\u4e0b\u6708\u7684\u91d1 \u9e70\u5bab\u4e4b\u4f1a\uff0c\u6211\u8fd8\u4e0d\u80fd\u8d76\u56de\u6765\u3002\u6c5f\u5144\uff0c\u4e0d\u5982\u4f60\u5c31\u5e26\u5979\u540c\u8d70\uff0c\u5230\u4e86\u853d\u56fd\uff0c\u53ef\u4ee5\u53bb\u89c1\u6211\u7684\u7236\u738b\uff0c\u4f4f \u5230\u7687\u5bab\u91cc\u53bb\uff0c\u505a\u4ed6\u7684\u5ba2\u4eba\u3002\u5c06\u6765\u5018\u82e5\u4f60\u8981\u79bb\u5f00\u655d\u56fd\uff0c\u613f\u610f\u5e26\u6b27\u9633\u59d1\u5a18\u540c\u8d70\u56fa\u597d\uff0c\u82e5\u662f\u6709\u6240\u4e0d \u4fbf\uff0c\u4e5f\u53ef\u4ee5\u5c06\u5979\u7559\u4e0b\uff0c\u968f\u4fbf\u5979\u4f4f\u5230\u4ec0\u4e48\u65f6\u5019\u90fd\u6210\uff0c\u8fd9\u6837\u5979\u6709\u4e86\u6816\u8eab\u4e4b\u6240\uff0c\u4f60\u4e5f\u6709\u4e86\u4ea4\u4ee3\u3002\u5979 \u7684\u7236\u6bcd\u5c31\u662f\u77e5\u9053\u3002\u4e5f\u51b3\u8ba1\u4e0d\u6562\u95ef\u5230\u7687\u5bab\u91cc\u5c06\u5979\u6293\u56de\u53bb\uff0c\u8fd9\u4e0d\u662f\u4e00\u4e3e\u4e09\u5f97\u4e48\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u9996\u5148\u8868\u793a\u540c\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u679c\u7136\u662f\u4e2a\u597d\u4e3b\u610f\uff0c\u6211\u5c31\u662f\u6015\u4e00\u4eba\u4e0a\u8def\uff0c\u7ed9\u6211\u7684\u7236\u6bcd\u6293 \u56de\uff0c\u800c\u4e14\u6211\u5bb6\u7684\u4ec7\u4eba\u53c8\u591a\uff0c\u82e5\u662f\u5728\u8def\u4e0a\u78b0\u7740\uff0c\u6211\u672c\u9886\u4f4e\u5fae\uff0c\u4e5f\u96be\u4ee5\u5e94\u4ed8\u3002\u6709\u6c5f\u76f8\u516c\u62a4\u9001\uff0c\u6211 \u5c31\u653e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6027\u60c5\u6734\u539a\uff0c\u4f46\u5374\u5e76\u975e\u7cca\u6d82\uff0c\u5c24\u5176\u662f\u5728\u6c5f\u6e56\u4e0a\u7ecf\u8fc7\u8bb8\u591a\u98ce\u6d6a\u4e4b\u540e\uff0c\u9605\u5386\u5927\u589e\uff0c\u5df2\u6e10 \u6e10\u61c2\u5f97\u89c2\u4eba\u4e8e\u5fae\uff0c\u9047\u4e8b\u4e5f\u80af\u7528\u4e0a\u5fc3\u601d\u4e86\u3002\u5f53\u4e0b\u60f3\u9053\uff1a\u201c\u770b\u6765\u4ed6\u4eec\u662f\u65e9\u5df2\u5546\u91cf\u5b9a\u5f53\u7684\u4e86\uff0c\u53ea\u7b49 \u6211\u6765\u3002\u8fd9\u4e3b\u610f\u662f\u53f6\u51b2\u9704\u51fa\u7684\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u6211\u81ea\u884c\u6295\u5230\u9a6c\u8428\u513f\u56fd\u7684\u7687\u5bab\u91cc\u53bb\uff1f\u201d\u53c8\u60f3\u9053\uff1a\u201c\u8fc7 \u5f80\u7684\u4e8b\uff0c\u6682\u4e14\u4e0d\u8bf4\uff0c\u53ea\u4ece\u4eca\u65e5\u7684\u51e0\u4ef6\u5c0f\u4e8b\u770b\u6765\uff0c\u8fd9\u53f6\u51b2\u9704\u5c31\u662f\u4e2a\u5fc3\u672f\u4e0d\u6b63\u4e4b\u4eba\u3002\u83ab\u975e\u53c8\u662f\u5b89 \u6392\u4e86\u4ec0\u4e48\u9677\u9631\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u7b11\u9053\uff1a\u201c\u6211\u8f88\u6c5f\u6e56\u7537\u5973\uff0c\u4e0d\u62d8\u5c0f\u8282\uff0c\u6b27\u9633\u59d1\u5a18\u5df2\u81ea\u7b54\u5e94\u4e86\uff0c\u4f60\u8fd8\u987e\u8651\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u6b63\u662f\u6709\u6240\u987e\u8651\uff01\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u613f\u95fb\u5176\u8be6\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8d35\u56fd\u7684\u56fd\u5e08 \u4e0d\u77e5\u4e0e\u6211\u6709\u4f55\u5acc\u9699\uff0c\u6211\u4ee3\u8c37\u5973\u4fa0\u63a5\u4e0b\u8bf7\u5e16\uff0c\u51c6\u5907\u53bb\u8d74\u4ed6\u7684\u76db\u4f1a\uff0c\u8fd9\u4e8b\u60c5\u6709\u4ed6\u7684\u4f7f\u8005\u56de\u62a5\uff0c\u60f3 \u6765\u4ed6\u662f\u65e9\u5df2\u77e5\u9053\u7684\u4e86\u3002\u6309\u9053\u7406\u8bf4\uff0c\u6211\u4ee3\u8868\u8c37\u5973\u4fa0\u8d74\u4f1a\uff0c\u5c31\u662f\u4ed6\u7684\u5ba2\u4eba\uff0c\u4ed6\u5e94\u8be5\u4ee5\u793c\u76f8\u5f85\u3002\u5374 \u4e0d\u77e5\u4f55\u6545\uff0c\u6211\u8fd8\u6765\u5230\u8d35\u56fd\uff1b\u4ed6\u5c31\u5df2\u7ecf\u8981\u6d3e\u4eba\u6765\u6349\u6211\u4e86\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u6545\u4f5c\u60ca\u8be7\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\uff1a\u201c\u4f60\u4e0d\u76f8\u4fe1\uff0c\u53ef\u4ee5\u95ee\u6b27\u9633\u59d1\u5a18\uff0c\u5979\u4ee5 \u524d\u7684\u5e08\u7236\u9634\u5723\u59d1\u5c31\u66fe\u4eb2\u53e3\u8bf4\u51fa\uff0c\u5979\u662f\u5949\u4e86\u91d1\u9e70\u5bab\u4e3b\u4eba\u4e4b\u547d\uff0c\u8981\u5c06\u6211\u62ff\u53bb\u4f5c\u793c\u7269\u7684\u3002\u201d\u60f3\u4e86\u4e00 \u60f3\uff0c\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u4e5f\u592a\u5584\u5fd8\u4e86\u3002\u5c31\u662f\u521a\u624d\u5728\u4e8e\u5bb6\u7684\u65f6\u5019\uff0c\u6b27\u9633\u4e8c\u5a18\u4e0d\u662f\u4e5f\u66fe\u5bf9\u4f60\u6307 \u51fa\uff0c\u8bf4\u6211\u662f\u91d1\u9e70\u5bab\u6240\u8981\u6349\u62ff\u7684\u4eba\u5417\uff1f\u5979\u8fd8\u8981\u4f60\u5e2e\u5fd9\u5462\uff0c\u4f60\u600e\u4e48\u5fd8\u4e86\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u7565\u663e\u5c34\u5c2c\u4e4b\u8272\uff0c\u4f46\u968f\u5373\u4f7f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u8bb0\u8d77\u6765\u4e86\u3002\u201d\u8fd9\u91cc\u9762\u6709\u4e2a\u8bef\u4f1a\u3002 \u636e\u6211\u6240\u77e5\uff0c\u5b9d\u8c61\u6cd5\u738b\u66fe\u4e0e\u4ee4\u5e08\u7ed3\u4e0b\u4e00\u70b9\u5c0f\u5c0f\u7684\u6881\u5b50\uff0c\u4ed6\u5411\u6765\u662f\u4f69\u670d\u4ee4\u5e08\u7684\uff0c\u4f46\u4ee4\u5e08\u5374\u4e0d\u80af\u89c1 \u8c05\u3002\u4ed6\u66fe\u5bf9\u6211\u8a00\u9053\uff0c\u4ed6\u5bf9\u4ee4\u5e08\u51b3\u65e0\u654c\u610f\uff0c\u53ea\u82e6\u4e8e\u65e0\u6cd5\u8ba9\u4ee4\u5e08\u77e5\u9053\uff0c\u82e5\u5f97\u4e00\u4e2a\u4ee4\u5e08\u4eb2\u8fd1\u7684\u6216\u76f8 \u4fe1\u7684\u4eba\u6765\u66ff\u4ed6\u8c03\u89e3\uff0c\u90a3\u5c31\u597d\u4e86\u3002\u56e0\u6b64\uff0c\u636e\u6211\u63a8\u6d4b\uff0c\u4ed6\u5b9a\u7136\u662f\u8981\u9634\u5723\u59d1\u5728\u4f1a\u524d\u5c06\u4f60\u2018\u63a5\u2019\u53bb\u3002 \u4ee5\u4fbf\u8fdb\u884c\u8c03\u89e3\u7684\uff0c\u53ef\u80fd\u662f\u4f20\u8bdd\u6709\u8bef\uff0c\u9634\u5723\u59d1\u62ff\u4e86\u9e21\u6bdb\u5f53\u4ee4\u7bad\uff0c\u5c31\u4ee5\u4e3a\u662f\u8981\u5c06\u4f60\u2018\u6349\u2019\u53bb\u4e86\u3002 \u81f3\u4e8e\u6b27\u9633\u4e8c\u5a18\uff0c\u5979\u66f4\u662f\u95f4\u63a5\u4ece\u9634\u5723\u59d1\u90a3\u513f\u542c\u6765\u7684\u547d\u4ee4\uff0c\u9634\u5723\u59d1\u4e00\u641e\u9519\uff0c\u5979\u5f53\u7136\u4e5f\u8ddf\u7740\u9519\u4e86\u3002\u201d \u3000\u3000\u6b27\u9633\u5a49\u5fd9\u9053\uff1a\u201c\u53f6\u516c\u5b50\uff0c\u4f60\u7684\u63a8\u6d4b\uff0c\u5f88\u6709\u9053\u7406\u3002\u6211\u90a3\u5e08\u7236\u4e00\u5411\u5c31\u662f\u5f88\u7c97\u5fc3\u5927\u610f\u7684\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6697\u7b11\uff1a\u201c\u4f60\u6562\u60c5\u662f\u628a\u6211\u5f53\u4f5c\u5c0f\u5b69\u5b50\u4e86\uff0c\u8fd9\u6837\u7684\u9b3c\u8bdd\u62ff\u6765\u9a97\u6211\uff01\u201d\u4f46\u8fd9\u6b21\u4ed6\u5374\u5fcd\u7740 \u4e0d\u5148\u53d1\u4f5c\uff0c\u95ee\u9053\uff1a\u201c\u6211\u7684\u5e08\u7236\u548c\u5b9d\u8c61\u6cd5\u738b\u7ed3\u4e86\u4ec0\u4e48\u6881\u5b50\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u542b\u7cca\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u662f\u542c\u5b9d\u8c61\u6cd5\u738b\u8fd9\u4e48\u8bf4\u8fc7\uff0c\u5230\u5e95\u662f\u4f55\u4e8b\u60c5\uff0c\u7ecf\u8fc7\u600e\u6837\uff0c\u6211\u4e5f\u4e0d\u77e5 \u5176\u8be6\u3002\u597d\u5728\u4ee4\u5e08\u4e5f\u662f\u8981\u8d74\u4f1a\u7684\u3002\u6c5f\u5144\uff0c\u4f60\u5230\u4e86\u655d\u56fd\uff0c\u5b9d\u8c61\u6cd5\u738b\u8981\u4f60\u8c03\u89e3\uff0c\u5f53\u7136\u4f1a\u544a\u8bc9\u4f60\uff1b\u5373 \u7b97\u4e0d\u544a\u8bc9\u4f60\uff0c\u5230\u4e86\u5176\u65f6\uff0c\u4f60\u4eec\u5e08\u5f92\u4f1a\u9762\uff0c\u4e5f\u5c31\u4f1a\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u81ea\u4ece\u79bb\u5bb6\u4e4b\u540e\uff0c\u8fd9\u8fd8\u662f\u7b2c\u4e00\u6b21\u542c\u5230\u5173\u4e8e\u5e08\u7236\u7684\u6d88\u606f\uff0c\u5fc3\u4e2d\u751a\u662f\u6b22\u559c\uff0c\u6697\u81ea\u60f3\u9053\uff1a \u201c\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u529f\u592b\u3002\u5374\u539f\u6765\u5e08\u7236\u4e5f\u8981\u53c2\u52a0\u91d1\u9e70\u5bab\u4e4b\u4f1a\uff0c\u6211\u53ef\u4ee5\u65e0\u9700\u518d\u5230\u522b \u5904\u53bb\u4e86\u3002\u201d\u4ed6\u770b\u4e86\u6b27\u9633\u5a49\u4e00\u773c\uff0c\u5fc3\u5185\u53c8\u9053\uff1a\u201c\u6211\u5f53\u7136\u662f\u8981\u5230\u9a6c\u8428\u513f\u56fd\u53bb\u7684\u3002\u4f46\u5374\u4e0d\u80fd\u4e0e\u4f60\u540c \u884c\uff0c\u6559\u4eba\u95f2\u8bdd\u3002\u53ef\u662f\uff0c\u6211\u5374\u600e\u6837\u6446\u8131\u5979\u5462\uff1f\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u53c8\u4e0d\u7981\u6697\u6697\u70e6\u607c\uff0c \u3000\u3000\u53f6\u51b2\u9704\u662f\u4e2a\u4e03\u5de7\u73b2\u73d1\u3001\u6ee1\u809a\u5fc3\u8ba1\u7684\u4eba\uff0c\u4ed6\u5728\u4e00\u65c1\u76d1\u8c8c\u5bdf\u8272\uff0c\u89c1\u6c5f\u6d77\u5929\u5ffd\u559c\u5ffd\u5fe7\uff0c\u65e9\u5df2\u77e5 \u9053\u4ed6\u5fc3\u601d\u4e0d\u5b9a\uff0c\u5f53\u4e0b\u53c8\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u4f60\u8fd8\u662f\u4e0d\u653e\u5fc3\u4e48\uff1f\u544a\u8bc9\u4f60\u5427\uff0c\u6211\u65e9\u5df2\u7ed9\u4f60\u5199\u597d\u4e86 \u4e24\u5c01\u4fe1\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u601d\u6ca1\u6709\u4ed6\u8f6c\u5f97\u8fd9\u4e48\u5feb\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u8981\u6211\u5e26\u4fe1\u4e0e\u8c01\uff1f\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4e8c\u5c01\u662f\u7ed9\u56fd \u5e08\u5b9d\u8c61\u6cd5\u738b\u7684\uff1b\u4e00\u5c01\u662f\u7ed9\u6211\u7236\u738b\u7684\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u867d\u7136\u4e0d\u662f\u56fd\u738b\u7684\u4eb2\u751f\u9aa8\u8089\uff0c\u4f46\u5374\u4e00\u5411\u5f88\u5f97 \u7236\u738b\u7684\u5ba0\u7231\uff0c\u8bf4\u5f97\u4e0a\u662f\u8a00\u542c\u8ba1\u4ece\u7684\u3002\u5b9d\u8c61\u6cd5\u738b\u5bf9\u6211\uff0c\u4e5f\u6709\u5e08\u5f92\u7684\u540d\u4efd\u3002\u4f60\u5e26\u4e86\u6211\u7684\u4fe1\u53bb\uff0c\u4ed6 \u4eec\u5b9a\u7136\u5bf9\u4f60\u4f18\u672d\u6709\u52a0\u3002\u6211\u7236\u738b\u53c8\u662f\u7d20\u6765\u6b22\u559c\u6709\u672c\u9886\u7684\u5c11\u5e74\u7684\u3001\u4ed6\u8fd1\u5e74\u6b63\u5728\u5343\u65b9\u767e\u8ba1\u62db\u63fd\u4eba\u624d \u5462\u3002\u54c8\u54c8\uff0c\u6c5f\u5144\uff0c\u5018\u82e5\u4f60\u613f\u7559\u5728\u655d\u56fd\uff0c\u6211\u6562\u4fdd\u4f60\u6709\u9526\u7ee3\u524d\u7a0b\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u53ef\u4ee5\u505a\u51fa\u4e00\u756a\u4e8b\u4e1a \u5462\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fcd\u4f4f\u4e86\u6c14\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u4e1a\uff1f\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u6211\u7236\u738b\u867d\u662f\u5c0f\u56fd\u4e4b\u738b\uff0c\u5374\u6709\u4e89\u96c4\u5929 \u4e0b\u4e4b\u5fc3\uff0c\u7eb5\u4e0d\u80fd\u95ee\u9f0e\u4e2d\u539f\uff0c\u4e5f\u53ef\u7edf\u4e00\u897f\u9672\uff0c\u626c\u5a01\u57df\u5916\u3002\u8fd9\u4e0d\u662f\u4e00\u756a\u5927\u4e8b\u4e1a\u4e48\uff1f\u7537\u513f\u5f53\u5fd7\u5728\u56db \u65b9\uff0c\u6c5f\u5144\u4ea6\u6709\u610f\u5728\u57df\u5916\u79f0\u738b\u4e4e\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u6ca1\u6709\u8fd9\u4e2a\u672c\u4e8b\uff0c\u4e5f\u6ca1\u6709\u8fd9\u4e2a\u798f\u4efd\u3002\u201d\u53f6\u51b2\u9704\u89c1\u4ed6\u65e0\u52a8\u4e8e\u8877\uff0c\u7b11\u9053\uff1a\u201c\u90a3 \u4e48\uff0c\u8fd9\u4e2a\u4ee5\u540e\u518d\u8bf4\u3002\u6c5f\u5144\uff0c\u8fd9\u4e24\u5c01\u4fe1\u4f60\u62ff\u53bb\u5427\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7b3c\u7740\u53cc\u624b\uff0c\u5e76\u4e0d\u63a5\u4fe1\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6055\u6211\u5192\u6627\uff0c\u8fd8\u60f3\u95ee\u4f60\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d\u53f6 \u51b2\u9704\u5fc3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u597d\u9ebb\u70e6\u3002\u201d\u5374\u548c\u989c\u60a6\u8272\u5730\u95ee\u9053\uff1a\u201c\u4f55\u4e8b\uff0c\u8bf7\u8bf4\uff01\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u53f6\u5144\uff0c \u4f60\u5bf9\u4f60\u7684\u8eab\u4e16\uff0c\u662f\u5426\u5df2\u7ecf\u660e\u767d\u4e86\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u9762\u8272\u4e00\u53d8\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u81ea\u5df1\u7684\u8eab\u4e16\uff0c\u6211\u5f53\u7136\u660e\u767d\u3002\u6c5f\u5144\uff0c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\u610f\u601d\uff0c\u4f60\u65e2\u7136\u660e\u767d\uff0c\u90a3\u6211\u5c31\u4e0d\u7528\u8bf4\u4e86\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u60f3\u6765\u6c5f\u5144\u5bf9\u5c0f\u5f1f\u7684\u8eab\u4e16\u6216\u5df2\u7565\u6709\u6240\u95fb\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u771f\u4eba\u9762\u524d\u4e0d\u8bf4\u5047\u6d3b\uff0c\u6211 \u6b63\u662f\u4e3a\u4e86\u4e00\u4ef6\u4e8b\u60c5\u70e6\u607c\u3002\u8981\u8bf7\u6c5f\u5144\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u4f60\u8fd8\u77e5\u9053\u70e6\u607c\uff0c\u90a3\u5c31\u597d\u4e86\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u4e00\u5b9a\u5c3d\u529b\u800c\u4e3a\u3002\u201d\u53f6\u51b2 \u9704\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ee4\u5e08\u548c\u9099\u5c71\u6d3e\u7684\u638c\u95e8\u8c37\u5973\u4fa0\u672c\u662f\u4e00\u5bf9\u60c5\u4fa3\uff0c\u4ee4\u5c0a\u548c\u8c37\u5973\u4fa0\u4e5f\u751a\u6709\u4ea4\u60c5\uff0c\u8c37\u4e4b \u534e\u6709\u4e2a\u5f1f\u5b50\u540d\u53eb\u8c37\u4e2d\u83b2\uff0c\u60f3\u5fc5\u6c5f\u5144\u81ea\u5c0f\u548c\u5979\u8ba4\u5f97\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5c0f\u65f6\u5019\u5728\u9099\u5c71\u7384\u5973\u89c2\u4f4f\u8fc7\uff0c\u548c\u5979\u7b97\u5f97\u662f\u9752\u6885\u7af9\u9a6c\u4e4b\u4ea4\u3002\u201d\u53f6\u51b2\u9704 \u9053\uff1a\u201c\u542c\u8bf4\u5979\u5c0f\u65f6\u5019\u662f\u4e2d\u725f\u53bf\u7684\u4e18\u5ca9\u629a\u517b\u5979\u7684\uff1f\u6c5f\u5144\u53ef\u77e5\u9053\u5f97\u786e\u5b9e\u4e48\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u4e18\u5ca9\u4e34\u6b7b\uff0c\u5c06\u5979\u4ed8\u6258\u7ed9\u5357\u4e10\u5e2e\u7684\u5e2e\u4e3b\u7ffc\u4f2f\u725f\uff0c\u7ffc\u4f2f\u725f\u662f\u8c37\u5973\u4fa0 \u7684\u5e08\u5144\uff0c\u56e0\u800c\u9001\u7ed9\u8c37\u5973\u4fa0\u505a\u517b\u5973\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u8fd9\u5c31\u5bf9\u4e86\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u548c\u5979\u4e43\u662f\u4e00\u6bcd\u6240\u751f\u7684\u540c\u80de\uff0c\u800c\u4e14\u672c\u6765\u90fd\u662f\u9a6c\u8428\u513f \u56fd\u7684\u4eba\u6c0f\uff0c\u53ea\u56e0\u5f53\u5e74\u9a6c\u8428\u513f\u56fd\u5927\u4e71\uff0c\u6211\u5f97\u53f6\u541b\u5c71\u629a\u517b\uff0c\u843d\u7c4d\u9648\u7559\uff0c\u5979\u5f97\u4e18\u5ca9\u629a\u517b\uff0c\u843d\u7c4d\u4e2d \u725f\uff0c\u53ea\u4e0d\u77e5\u5979\u5bf9\u81ea\u5df1\u7684\u8eab\u4e16\uff0c\u53ef\u66fe\u660e\u767d\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u8c37\u5973\u4fa0\u662f\u77e5\u9053\u4e86\u7684\u3002\u5c31\u4e0d\u77e5\u662f\u5426\u66fe\u544a\u8bc9\u4e86\u5979\uff1f\u201d\u53f6\u51b2\u9704\u53f9\u53e3\u6c14 \u9053\uff1a\u201c\u53ef\u662f\u6211\u627e\u5230\u4e86\u5979\uff0c\u5979\u5374\u4e0d\u80af\u8ba4\u6211\uff01\u201d\u6c5f\u6d77\u5929\u53c8\u60ca\u53c8\u559c\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u5144\u59b9\u4e1a\u5df2\u76f8\u9022\u4e86\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u6211\u8981\u8ba4\u5979\u4f5c\u59b9\u59b9\u3001\u5979\u5374\u4e0d\u66fe\u8ba4\u6211\u505a\u54e5\u54e5\uff01\u201d\u6c5f\u6d77\u5929\u89c1\u4ed6\u5fe7\u8651\u4e0e\u7126\u6025\u4e4b\u7cbe\uff0c \u73b0\u4e8e\u8f9e\u8272\uff0c\u987e\u4e0d\u53ca\u95ee\u4ed6\u662f\u5728\u4f55\u65f6\u4f55\u5730\uff0c\u600e\u6837\u89c1\u7740\u8c37\u4e2d\u83b2\u7684\uff0c\u4fbf\u5148\u8bf4\u9053\uff1a\u201c\u8fd9\u4e5f\u96be\u602a\uff0c\u4f60\u5927\u7ea6 \u6ca1\u6709\u5411\u5979\u89e3\u91ca\u6e05\u695a\uff0c\u4f60\u4e3a\u4ec0\u4e48\u505a\u4e86\u73b0\u5728\u9a6c\u8428\u513f\u56fd\u56fd\u738b\u7684\u5e72\u6bbf\u4e0b\u5427\uff1f\u201d \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u5fc3\u4e2d\u662f\u8fd9\u6837\u60f3\u7684\uff1a\u201c\u53f6\u51b2\u9704\u65e2\u7136\u5bf9\u81ea\u5df1\u7684\u8eab\u4e16\u5b8c\u5168\u660e\u767d\uff0c\u5f53\u7136\u4e5f\u5e94\u8be5\u77e5\u9053\u4e86 \u73b0\u5728\u7684\u56fd\u738b\u662f\u4ed6\u7684\u6740\u7236\u4ec7\u4eba\u3002\u4ed6\u5c48\u8eab\u4e8b\u4ec7\uff0c\u60f3\u5fc5\u662f\u5b58\u7740\u5b64\u81e3\u5b7d\u5b50\u4e4b\u5fc3\uff0c\u56e0\u800c\u5fcd\u8fb1\u4e00\u65f6\uff0c\u4f3a\u673a \u96ea\u6068\u7684\u3002\u201d\u8981\u77e5\u6c5f\u6d77\u5929\u662f\u4e2a\u803f\u76f4\u7684\u4eba\uff0c\u4ed6\u7edd\u4e0d\u80fd\u60f3\u8c61\u4e00\u4e2a\u4eba\u80af\u7518\u5fc3\u670d\u4f8d\u6740\u7236\u7684\u4ec7\u4eba\u3002 \u3000\u3000\u4e0d\u6599\u53f6\u51b2\u9704\u5374\u8fd9\u6837\u56de\u7b54\u9053\uff1a\u201c\u6211\u5f97\u56fd\u738b\u8ba4\u4e3a\u513f\u5b50\uff0c\u8eab\u53d7\u5927\u6069\uff0c\u6211\u600e\u4f1a\u4e0d\u544a\u8bc9\u81ea\u5df1\u7684\u59b9\u59b9 \u5462\uff1f\u6211\u4e00\u89c1\u5979\uff0c\u5c31\u539f\u539f\u672c\u672c\u7684\u90fd\u544a\u8bc9\u5979\u4e86\uff0c\u800c\u4e14\u6211\u8fd8\u544a\u8bc9\u5979\uff0c\u56fd\u5de5\u8981\u8058\u5979\u4f5c\u592a\u5b50\u5983\uff0c\u5c06\u5979\u63a5 \u5165\u5bab\u4e2d\uff0c\u62e9\u65e5\u5b8c\u5a5a\u5462\u3002\u54ea\u77e5\u5979\u542c\u4e86\u4e4b\u540e\uff0c\u4e8c\u8bdd\u4e0b\u8bf4\uff0c\u5c31\u5c06\u6211\u8d76\u4e86\u51fa\u6765\u3002\u6c5f\u5144\uff0c\u6211\u8981\u4f60\u5e2e\u5fd9\u7684 \u5c31\u662f\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u8bf7\u4f60\u4e3a\u6211\u529d\u529d\u5979\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u4ed6\u662f\u53c8\u60ca\u53c8\u6012\uff0c\u53c8\u66ff\u8c37\u4e2d\u83b2\u4f24\u5fc3\uff0c\u6574\u4e2a\u4eba \u5446\u4e86\uff0c\u597d\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u53f6\u51b2\u9704\u5927\u4e3a\u5947\u602a\uff0c\u53eb\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u6b27\u9633\u5a49\u4e5f\u83ab\u540d\u5176\u5999\uff0c\u5ffd\u5730\u60f3\u9053\uff1a\u201c\u8fd9\u4e8b \u4e0d\u5999\uff0c\u4ed6\u4e0e\u8c37\u4e2d\u83b2\u662f\u9752\u6885\u7af9\u9a6c\u4e4b\u4ea4\uff0c\u83ab\u975e\u4e24\u5c0f\u65e0\u731c\uff0c\u65e9\u5df2\u6709\u60c5\uff1f\u56e0\u6b64\uff0c\u4ed6\u542c\u8bf4\u9a6c\u8428\u513f\u56fd\u7684\u592a \u5b50\u8981\u7eb3\u8c37\u4e2d\u83b2\u4e3a\u5983\uff0c\u4ed6\u4e0d\u4e50\u610f\uff1f\u201d\u5f53\u4e0b\u4fbf\u7528\u8bf4\u8bdd\u8bd5\u63a2\u95ee\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u662f\u6210\u4eba\u4e4b\u7f8e\uff0c\u4e49\u6240\u5f53 \u4e3a\u3002\u6c5f\u76f8\u516c\uff0c\u4f60\u72b9\u8c6b\u4ec0\u4e48\uff1f\u54e6\uff0c\u83ab\u975e\u4f60\u6015\u81ea\u5df1\u4e0d\u4f1a\u8bf4\u8bdd\u3002\u96be\u4f5c\u5a92\u4eba\uff1f\u82e5\u662f\u5982\u6b64\uff0c\u4f60\u53ef\u4ee5\u8ba9\u6211 \u8ba4\u8bc6\u8fd9\u4f4d\u8c37\u59d1\u5a18\uff0c\u6211\u4eec\u90fd\u662f\u5973\u5b69\u513f\u5bb6\uff0c\u65b9\u4fbf\u8bf4\u8bdd\u3002\u4f60\u770b\u53ef\u597d\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5ffd\u5730\u5927\u58f0\u53eb\u9053\uff1a\u201c\u95ed\u5634\uff01\u4f60\u4eec\u628a\u6211\u5f53\u4f5c\u4ec0\u4e48\u4eba\u4e86\uff1f\u6211\u5c82\u80fd\u540c\u6d41\u5408\u6c61\uff0c\u5e2e\u4f60\u505a\u8fd9\u7b49 \u4e0d\u5fe0\u4e0d\u5b5d\u4e0d\u4ec1\u4e0d\u4e49\u4e4b\u4e8b\uff01\u201d \u3000\u3000\u53f6\u51b2\u9704\u6c14\u5f97\u6d51\u8eab\u53d1\u6296\uff0c\u9762\u8272\u94c1\u9752\uff0c\u8de8\u4e0a\u4e00\u6b65\uff0c\u77aa\u8d77\u773c\u775b\u559d\u9053\uff1a\u201c\u59d3\u6c5f\u7684\uff0c\u4f60\u8bf4\u6e05\u695a\u4e86\u518d \u8d70\uff0c\u6211\u600e\u7684\u662f\u4e0d\u5fe0\u4e0d\u5b5d\u4e0d\u4ec1\u4e0d\u4e49\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u4efb\u51ed\u6743\u5978\u7a83\u56fd\u3002\u7f6e\u4e4b\u4e0d\u540c\uff0c\u8fd9\u5c31\u662f\u4e0d\u5fe0\uff01\u4f60\u8ba4\u8d3c\u4f5c\u7236\uff0c\u8fd9\u5c31\u662f\u4e0d\u5b5d\uff01\u4f60 \u732e\u59b9\u6c42\u8363\uff0c\u8fd9\u5c31\u662f\u4e0d\u4ec1\uff01\u4f60\u8981\u9677\u5bb3\u670b\u53cb\u5e2e\u4f60\u505a\u89c1\u4e0d\u5f97\u4eba\u7684\u4e11\u4e8b\uff0c\u8fd9\u5c31\u662f\u4e0d\u4e49\uff01\u54fc\uff0c\u54fc\uff0c\u4e0d \u5fe0\u3001\u4e0d\u5b5d\uff0c\u4e0d\u4ec1\u3001\u4e0d\u4e49\uff0c\u8fd9\u8fd8\u7b97\u9a82\u5f97\u8f7b\u4e86\uff0c\u4f60\u81ea\u5df1\u95fb\u95fb\uff0c\u4f60\u8eab\u4e0a\u8fd8\u6709\u4e00\u4e1d\u4eba\u5473\u4e48\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u5927\u6012\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u51fa\u53e3\u4f24\u4eba\uff01\u4f60\u9a82\u6211\u4e5f\u8fd8\u7f62\u4e86\uff0c\u8fd8\u7adf\u6562\u8bec\u6bc1\u6211\u7684\u7236\u738b\u3002\u80e1 \u8bf4\u516b\u9053\uff0c\u79bb\u95f4\u6211\u4eec\u7236\u5b50\u3002\u6211\u8bf4\u4f60\u624d\u662f\u6ca1\u6709\u4e00\u4e1d\u4eba\u5473\uff0c\u4f60\u6069\u5c06\u4ec7\u62a5\uff0c\u771f\u6094\u4e0d\u8be5\u6551\u4e86\u4f60\u7684\u6027\u547d\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u66fe\u7ecf\u6551\u4e86\u6211\u4e00\u6b21\u6027\u547d\u3002\u4ee5\u540e\u6211\u52a0\u500d\u5949\u8fd8\uff0c\u5018\u82e5\u4f60\u6709\u6027\u547d\u4e4b\u5fe7\uff0c\u4e0d \u8bba\u5982\u4f55\uff0c\u6211\u4e5f\u7b54\u5e94\u6551\u4f60\u4e24\u6b21\u3002\u597d\uff0c\u8a00\u5c3d\u4e8e\u6b64\uff0c\u4f60\u8981\u8ba4\u8d3c\u4f5c\u7236\uff0c\u6211\u4e5f\u53ea\u597d\u7531\u4f60\u3002\u544a\u8f9e\uff01\u201d \u3000\u3000\u53f6\u51b2\u9704\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4f55\u987b\u8981\u4f60\u6551\u547d\uff0c\u4f60\u8fd8\u662f\u5f53\u5fc3\u4f60\u81ea\u5df1\u8fd9\u6761\u5c0f\u547d\u5427\uff01\u201d\u84e6\u5730\u4e00\u58f0\u5927\u559d\uff1a \u201c\u6211\u80fd\u6551\u4f60\u7684\u547d\u4e5f\u80fd\u53d6\u4f60\u7684\u547d\uff01\u201d\u731b\u7684\u4e00\u638c\u5288\u4e0b\uff0c\u6c5f\u6d77\u5929\u521a\u597d\u8f6c\u8fc7\u4e86\u8eab\uff0c\u80cc\u5411\u7740\u53f6\u51b2\u9704\uff0c\u53f6 \u51b2\u9704\u8fd9\u4e00\u638c\u6b63\u662f\u4e58\u5176\u4e0d\u5907\u3002 \u3000\u3000\u6b27\u9633\u5a49\u5c16\u58f0\u60ca\u547c\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u6c5f\u6d77\u5929\u5954\u51fa\u51e0\u6b65\uff0c\u56de\u5934\u51b7\u7b11\u9053\uff1a\u201c\u597d\u672c\u9886\uff0c\u597d \u624b\u6bb5\uff01\u6211\u770b\u5728\u521a\u624d\u4f60\u66fe\u6551\u6211\u7684\u4efd\u4e0a\uff0c\u6211\u4e0d\u8fd8\u624b\uff0c\u4f46\u6211\u53ea\u80fd\u5fcd\u4f60\u4e00\u6b21\uff0c\u4f60\u82e5\u518d\u6765\u3001\u6211\u5c31\u4e0d\u5ba2\u6c14 \u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u4e0e\u4ed6\u7ffb\u9762\u4e4b\u540e\uff0c\u6697\u4e2d\u65e9\u5df2\u63d0\u9632\uff0c\u5c06\u771f\u6c14\u51dd\u805a\u80cc\u5fc3\uff0c\u4ed6\u53c8\u6709\u5b9d\u7532\u62a4\u8eab\uff0c\u6240\u4ee5\u867d \u7136\u53d7\u4e86\u4e00\u638c\uff0c\u5374\u65e0\u5927\u788d\u3002 \u3000\u3000\u53f6\u51b2\u9704\u4e0d\u77e5\u4ed6\u6709\u5b9d\u7532\u62a4\u8eab\uff0c\u53ea\u9053\u4ed6\u662f\u5168\u51ed\u7740\u5185\u529f\u63a5\u4e86\u4ed6\u8fd9\u4e00\u638c\u3002\u5fc3\u4e2d\u5927\u9a87\uff0c\u60f3\u9053\uff1a\u201c\u60f3 \u4e0d\u5230\u4ed6\u529f\u529b\u8fd9\u4e48\u6df1\u539a\uff0c\u602a\u4e0d\u5f97\u6587\u5ef7\u58c1\u52a0\u4e0a\u4e86\u6b27\u9633\u4f2f\u548c\u4e0e\u6b27\u9633\u4e8c\u5a18\uff0c\u4e5f\u8fd8\u662f\u65e0\u5948\u4ed6\u4f55\uff01\u201d\u56e0 \u6b64\uff0c\u867d\u7136\u6012\u6c14\u51b2\u5929\uff0c\u5374\u662f\u4e0d\u6562\u518d\u53bb\u8ffd\u4e86\u3002 \u3000\u3000\u6b27\u9633\u5a49\u5927\u53eb\u9053\uff1a\u201c\u6c5f\u76f8\u516c\uff0c\u4f60\u4e0d\u7406\u6211\u4e86\u4e48\u4e86\u5509\uff0c\u4f60\u4eec\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u597d\u7aef\u7aef\u5730\u6253\u8d77\u67b6 \u6765\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u6015\u6ca1\u4eba\u7406\u4e48\uff1f\u4f60\u8d70\u4f60\u7684\u9633\u5173\u9053\uff0c\u6211\u8d70\u6211\u7684\u72ec\u6728\u6865\uff0c\u4f60\u522b\u518d\u6765\u7f20 \u6211\uff01\u201d\u4ed6\u5c55\u5f00\u4e86\u7edd\u9876\u8f7b\u529f\uff0c\u6b27\u9633\u5a49\u5982\u4f55\u8ffd\u5f97\u4e0a\uff1f\u4f55\u51b5\u5979\u542c\u4e86\u6c5f\u6d77\u5929\u8fd9\u4e00\u756a\u8bdd\uff0c\u4e5f\u662f\u4e0d\u7531\u5f97\u53c8 \u7f9e\u53c8\u6c14\u53c8\u662f\u4f24\u5fc3\uff0c\u767b\u65f6\u53cc\u8db3\u597d\u50cf\u7262\u7262\u9489\u5728\u5730\u4e0a\u4e00\u822c\uff0c\u518d\u4e5f\u4e0d\u80fd\u5411\u524d\u79fb\u52a8\u3002 \u3000\u3000\u53f6\u51b2\u9704\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4ed6\u4e0d\u8981\u4f60\u4e86\uff0c\u4f60\u8fd8\u662f\u8ddf\u6211\u8d70\u5427\u3002\u4f60\u522b\u751f\u6c14\uff0c\u53ea\u8981\u4f60\u542c\u6211\u7684\u8bdd\uff0c\u6211\u7ed9 \u4f60\u60f3\u6cd5\u62a5\u4ec7\u3002\u201d\u8f6c\u773c\u95f4\u6c5f\u6d77\u5929\u5df2\u53bb\u5f97\u8fdc\u4e86\uff0c\u6b27\u9633\u5a49\u5446\u4e86\u4e00\u4f1a\uff0c\u8fd9\u624d\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u54ed\u4e86\u51fa \u6765\u3002\u6b63\u662f\uff1a \u3000\u3000\u843d\u82b1\u6709\u610f\u968f\u6d41\u6c34\uff0c\u6d41\u6c34\u65e0\u60c5\u53ea\u81ea\u6d41\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u516d\u56de\u3000\u5723\u5bfa\u7adf\u5bb9\u5bb5\u5c0f\u8f88\u3000\u9ad8\u50e7\u65e0\u8bed\u5bf9\u826f\u670b \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e8c\u5341\u516d\u56de\u3000\u5723\u5bfa\u7adf\u5bb9\u5bb5\u5c0f\u8f88\u3000\u9ad8\u50e7\u65e0\u8bed\u5bf9\u826f\u670b \u3000\u3000\u4e14\u8bf4\u6c5f\u6d77\u5929\u6446\u8131\u4e86\u6b27\u9633\u5a49\uff0c\u6b63\u5728\u6c14\u547c\u547c\u7684\u75be\u8dd1\uff0c\u5ffd\u89c9\u80cc\u540e\u5fae\u98ce\u98d2\u7136\uff0c\u6c5f\u6d77\u5929\u5927\u6012\uff0c\u53cd\u624b \u4e00\u6293\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5f53\u771f\u662f\u51a4\u9b42\u4e0d\u6563\uff0c\u8fd8\u8981\u518d\u7f20\uff1f\u6211\u53ef\u8981\u5bf9\u4f60\u4e0d\u5ba2\u6c14\u5566\uff0c\u54ce\u5440\uff0c\u59ec\u4f2f\u4f2f\uff0c\u662f \u4f60\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u8be7\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u4f60\u5728\u751f\u8c01\u7684\u6c14\uff1f\u6211\u51e0\u4e4e\u7ed9\u4f60\u634f\u574f\u4e86\u8001\u9aa8\u5934\u3002\u201d\u6c5f\u6d77\u5929\u8fde\u5fd9\u8d54\u7f6a\uff0c \u8bf4\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u4ee5\u4e3a\u662f\u90a3\u59d3\u53f6\u7684\u8d3c\u5b50\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u66f4\u662f\u5947\u602a\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u548c\u53f6\u51b2\u9704\u95f9\u7ffb\u4e86\u4e48\uff1f\u600e\u7684\u8fd9\u6837\u9a82\u4ed6\uff1f\u8fd9\u59d3\u65f6\u7684\u6211\u4e5f\u4e0d\u559c\u6b22 \u4ed6\uff0c\u770b\u6765\u662f\u50cf\u4e2a\u7f33\u8584\u5c11\u5e74\uff0c\u4e0d\u8fc7\uff0c\u4eca\u5929\u4ed6\u603b\u7b97\u662f\u5e2e\u4e86\u54b1\u4eec\u5440\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6c14\u72b9\u672a\u606f\uff0c\u8bf4\u9053\uff1a\u201c\u5c82\u53ea\u5047\u8584\uff0c\u59ec\u6015\u4f2f\uff0c\u6211\u8bf4\u7ed9\u4f60\u542c\uff0c\u4f60\u53ef\u66fe\u542c\u8fc7\uff0c\u4e16\u4e0a\u7adf\u6709\u8fd9 \u7b49\u4e0d\u5fe0\u4e0d\u5b5d\u4e0d\u4ec1\u4e0d\u4e49\u4e4b\u4eba\uff1f\u201d\u5f53\u4e0b\u4e00\u4e94\u4e00\u5341\u7684\uff0c\u5c06\u53f6\u51b2\u9704\u548c\u4ed6\u7684\u8c08\u8bdd\u90fd\u544a\u8bc9\u4e86\u59ec\u6653\u98ce\u3002 \u3000\u3000\u59ec\u6653\u98ce\u76b1\u4e86\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4f9d\u4f60\u8bf4\u6765\uff0c\u8fd9\u59d3\u65f6\u7684\u679c\u7136\u662f\u6ca1\u6709\u4e00\u4e1d\u4eba\u5473\u4e86\u3002\u4e0d\u8fc7\uff0c\u6211\u5374\u6709 \u70b9\u6000\u7591\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e8b\u60c5\u6446\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4ed6\u5df2\u7136\u77e5\u9053\u4e86\u81ea\u5df1\u7684\u8eab\u4e16\uff0c\u5374\u8fd8\u8d2a\u56fe\u5bcc\u8d35\uff0c\u5c48 \u8eab\u4e8b\u4ec7\uff0c\u505a\u9a6c\u8428\u513f\u56fd\u7684\u4e8e\u6bb4\u4e0b\uff0c\u800c\u4e14\u8fd8\u60f3\u732e\u59b9\u6c42\u8363\uff0c\u8fd9\u8fd8\u6709\u4ec0\u4e48\u6000\u7591\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6211\u5c31\u662f\u6000\u7591\u4ed6\u7ed9\u90a3\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u738b\u9a97\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u9a97\u4e86\uff1f\u8981\u662f\u4ed6\u4e0d \u77e5\u9053\u81ea\u5df1\u7684\u8eab\u4e16\uff0c\u8fd9\u8fd8\u53ef\u4ee5\u8bf4\u5f97\u8fc7\u53bb\uff0c\u4f46\u4ed6\u5374\u662f\u5206\u660e\u77e5\u9053\u4e86\u7684\u5440\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4ed6\u6709\u6ca1\u6709\u8bf4\uff0c\u4ed6\u5df2\u7ecf\u77e5\u9053\u4e86\u81ea\u5df1\u662f\u9a6c\u8428\u513f\u56fd\u524d\u738b\u7684\u513f\u5b50\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a \u201c\u8fd9\u5012\u6ca1\u6709\u3002\u4e0d\u8fc7\u5176\u4ed6\u7684\u60c5\u8282\u4ed6\u90fd\u8bf4\u5bf9\u4e86\u3002\u4e5f\u8bb8\u4ed6\u662f\u5fc3\u4e2d\u6709\u6127\uff0c\u4e0d\u6562\u63d0\u8d77\u524d\u738b\u3002\u800c\u4e14\uff0c\u4ed6\u4e5f \u77e5\u9053\u6211\u5bf9\u4ed6\u7684\u8eab\u4e16\u5df2\u6709\u6240\u95fb\uff0c\u4e5f\u5c31\u7528\u4e0d\u7740\u8be6\u8bf4\u4e86\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5bf9\u3002\u8fd9\u662f\u6700\u91cd\u8981\u7684\u4e00\u70b9\uff0c\u4ed6\u4e3a\u4f55\u4e0d\u63d0\uff1f\u8fd8\u6709\uff0c\u5018\u82e5\u4ed6\u662f\u5fc3 \u4e2d\u6709\u6127\uff0c\u4f60\u9a82\u4ed6\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u4e0d\u4f1a\u90a3\u6837\u7684\u6c14\u6124\uff0c\u611f\u5230\u662f\u53d7\u4e86\u4f60\u7684\u4fae\u8fb1\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e00\u60f3\uff0c\u53f6\u51b2\u9704\u521a\u624d\u53e3\u53e3\u58f0\u58f0\u9a82\u4ed6\u201c\u5fd8\u6069\u8d1f\u4e49\u201d\uff0c\u5012\u597d\u50cf\u662f\u4ed6\u7406\u76f4\u6c14\u58ee\uff0c\u9762\u5bf9\u65b9\u53cd \u800c\u7406\u4e8f\u4e86\u7684\uff0c\u4e0d\u89c9\u5446\u4e86\u4e00\u5446\uff0c\u95ee\u9053\uff1a\u201c\u59ec\u6015\u6015\uff0c\u4f9d\u4f60\u4e4b\u89c1\uff0c\u53c8\u662f\u5982\u4f55\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u8fc7\u53ea\u662f\u731c\u60f3\u3002\u6211\u731c\u662f\u90a3\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u738b\uff0c\u5c06\u4ed6\u7684\u8eab\u4e16\u9690\u7792\u4e86\u4e00\u4e9b\uff0c \u6216\u8005\u662f\u8bf4\u4e86\u51e0\u5206\u771f\u7684\uff0c\u53c8\u634f\u9020\u51e0\u5206\u5047\u7684\uff0c\u771f\u771f\u5047\u5047\uff0c\u6df7\u4f5c\u4e00\u56e2\u3002\u6240\u4ee5\u53f6\u51b2\u9704\u826f\u5df1\u4ee5\u4e3a\u662f\u660e\u767d \u4e86\uff0c\u5176\u5b9e\u5374\u662f\u7cca\u91cc\u7cca\u6d82\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5927\u60ca\u9053\uff1a\u201c\u82e5\u5f53\u771f\u5982\u6b64\uff0c\u6211\u5c31\u9519\u602a\u4e86\u4eba\u4e86\uff01\u4f46\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u738b\u4e3a\u4f55\u8981\u8fd9\u6837\u505a\uff0c\u4ed6 \u77e5\u9053\u4e86\u4ec7\u4eba\u7684\u513f\u5b50\uff0c\u4f55\u4ee5\u4e0d\u5c06\u4ed6\u4e00\u5200\u6740\u4e86\uff0c\u5012\u8981\u6536\u4e3a\u4e49\u5b50\u5462\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u8fd9\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u597d\u5728\u4f60\u4e0d\u4e45\u5c31\u53ef\u4ee5\u89c1\u5230\u5e08\u7236\uff0c\u603b\u53ef\u4ee5\u63a2\u5f97\u4e00\u70b9\u7aef\u502a\u3002\u201d \u6c5f\u6d77\u5929\u9053\uff0c\u201c\u53ef\u60dc\u6211\u521a\u624d\u6ca1\u6709\u95ee\u4ed6\uff0c\u8c37\u5973\u5feb\u5e08\u5f92\u5728\u4f55\u65b9\uff1f\u55ef\uff0c\u83b2\u59b9\u4e0d\u80af\u8ba4\u4ed6\uff0c\u60f3\u6765\u4e5f\u662f\u4e0e\u6211 \u4e00\u6837\uff0c\u5211\u4ed6\u6709\u6240\u8bef\u4f1a\u4e86\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u4f60\u6027\u60c5\u803f\u76f4\uff0c\u5fc3\u5730\u5355\u7eaf\uff0c\u6240\u4ee5\u96be\u514d\u5ac9\u6076\u5982\u4ec7\uff0c\u4f46\u53c8\u5bb9\u6613\u76f8\u4fe1\u522b \u4eba\uff0c\u82db\u8d23\u81ea\u5df1\uff1b\u5176\u5b9e\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u4e3a\u4e86\u8fd9\u4e8b\u96be\u8fc7\uff0c\u90a3\u59d3\u53f6\u7684\u7eb5\u975e\u8ba4\u8d3c\u4f5c\u7236\uff0c\u4ed6\u7684\u4eba\u54c1\u8fd8\u662f\u751a \u5dee\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff0c\u201c\u4f46\u6211\u8fd8\u672a\u66fe\u95ee\u5f97\u6e05\u695a\uff0c\u5c31\u4e0d\u7559\u4f59\u5730\u7684\u75db\u9a82\u4e86\u4ed6\u4e00\u987f\uff0c\u8fd9\u603b\u662f\u6211\u7684\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u73b0\u5728\u6682\u4e14\u4e0d\u8c08\u8fd9\u59d3\u53f6\u7684\uff0c\u4f60\u662f\u5426\u8981\u8d76\u7740\u8d74\u91d1\u9e70\u5bab\u4e3b\u4eba\u4e4b\u4f1a\uff1f\u201d\u6c5f\u6bcf\u5929\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u4f1a\u671f\u662f\u4e0b\u6708\u5341\u4e94\uff0c\u53ea\u6709\u4e00\u4e2a\u6708\u96f6\u4e24\u5929\u4e86\u3002\u59ec\u4f2f\u4f2f\uff0c\u4f60\u662f\u5426\u4e5f\u63a5\u4e86\u91d1\u9e70\u5b98\u7684\u8bf7\u5e16\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u6211\u662f\u4e00\u4e2a\u5c0f\u5077\uff0c\u4e0a\u4e0d\u5f97\u53f0\u76d8\u7684\uff0c\u54ea\u91cc\u4f1a\u6709\u8bf7\u5e16\u3002\u4e0d\u8fc7\uff0c\u6211\u4eec\u505a\u5c0f\u5077\u7684\u662f \u4e0d\u5fc5\u4e3b\u4eba\u8bf7\u7684\uff0c\u5230\u65f6\u8bf4\u4e0d\u5b9a\u6211\u4e00\u65f6\u6280\u75d2\uff0c\u5c31\u53bb\u6df7\u6c34\u6478\u9c7c\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u559c\u9053\uff1a\u201c\u59ec\u6015\u6015\uff0c\u4f60\u5c31\u548c\u6211\u4e00\u9053\u53bb\u5427\uff01\u91d1\u9e70\u5bab\u6b64\u6b21\u76db\u4f1a\uff0c\u904d\u8bf7\u5404\u65b9\u4eba\u7269\uff0c\u503c\u5f97\u4f60 \u5077\u7684\u597d\u4e1c\u897f\u4e00\u5b9a\u4e0d\u5c11\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u53bb\u6211\u662f\u8981\u53bb\u7684\uff0c\u4f46\u6211\u8fd8\u8981\u5230\u522b\u7684\u5730\u65b9\u5148\u8d70\u4e00\u8d9f\uff0c\u8d24\u4f84\uff0c\u54b1\u4eec\u8fd9\u6b21\u5de7\u9047\uff0c \u771f\u4f3c\u6709\u5929\u610f\u5b89\u6392\uff0c\u4f60\u53ef\u60f3\u77e5\u9053\u6211\u662f\u4e3a\u4e86\u4f55\u4e8b\u5417\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5c0f\u4f84\u6b63\u662f\u60f3\u95ee\u8001\u4f2f\u3002\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4f60\u53ef\u66fe\u6253\u542c\u5230\u4f60\u7236\u4eb2\u7684\u6d88\u606f\uff1f\u201d\u6c5f\u6d77 \u5929\u9053\uff1a\u201c\u6211\u662f\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002\u59ec\u4f2f\u4f2f\u4f60\u8fd9\u4e48\u8bf4\uff0c\u83ab\u975e\u2014\u2014\u201d\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u4e0d\u4e45\u4e4b\u524d\uff0c\u6211\u8fd8 \u548c\u4ee4\u5c0a\u5728\u4e00\u8d77\uff0c\u8fd9\u6b21\u4e5f\u662f\u8981\u53bb\u4e0e\u4ed6\u76f8\u4f1a\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5927\u559c\u5982\u72c2\uff0c\u8bf4\u9053\uff1a\u201c\u59ec\u4f2f\u4f2f\uff0c\u4f60\u600e\u4e0d\u65e9\u8bf4\uff1f\u6211\u7239\u7239\u73b0\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u59ec\u6653\u98ce\u7b11 \u9053\uff1a\u201c\u521a\u624d\u6211\u8fd9\u6761\u8001\u547d\u8fd8\u4e0d\u77e5\u4fdd\u4e0d\u4fdd\u5f97\u4f4f\u5462\uff0c\u54ea\u6709\u5de5\u592b\u7ec6\u8bf4\uff1f\u73b0\u5728\u597d\u4e86\uff0c\u4f60\u522b\u7740\u6025\uff0c\u4e14\u542c\u6211 \u6162\u6162\u9053\u6765\u3002\u8fd9\u4ef6\u4e8b\u8981\u4ece\u5934\u8bf4\u8d77\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u59ec\u4f2f\u4f2f\u4e0a\u4e86\u4e00\u70b9\u5e74\u7eaa\uff0c\u8bf4\u8bdd\u4e5f\u50cf\u6211\u7239\u7239\u4e00\u6837\uff0c\u62d6\u6ce5\u5e26\u6c34\uff0c\u7f20\u5939\u4e0d\u6e05 \u4e86\u3002\u201d\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u5c31\u4ece\u5934\u8bf4\u8d77\u5427\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4f60\u7239\u7239\u662f\u4e3a\u4e86\u4ed6\u4e49\u5144\u9648\u5929\u5b87\u5931\u8e2a\uff0c\u8fd9\u624d\u51fa\u95e8\u53bb\u5bfb\u4ed6\u7684\u3002\u8fd9\u4e8b\u60c5\u4f60\u662f\u77e5\u9053\u7684 \u4e86\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u77e5\u9053\u4e86\u3002\u6211\u7239\u7239\u627e\u5230\u4e86\u9648\u4f2f\u4f2f\u6ca1\u6709\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4f60\u53c8\u7740\u6025\u4e86\uff0c\u8bf4\u5230\u8fd9\u91cc\uff0c\u6211\u5e94\u8be5\u5148\u8bb2\u4e00\u8bb2\u9648\u5929\u5b87\u4e3a\u4f55\u5931\u8e2a\u7684\u4e8b\uff0c\u7136\u540e\u624d\u53ef \u4ee5\u8bf4\u5230\u4f60\u7236\u4eb2\u8eab\u4e0a\u3002\u201d\u6c5f\u6d77\u5929\u65e0\u53ef\u5948\u4f55\uff0c\u518d\u4e5f\u4e0d\u6562\u50ac\u4ed6\uff0c\u53ea\u597d\u542c\u4ed6\u5148\u8bb2\u9648\u5929\u5b87\u7684\u4e8b\u3002 \u3000\u3000\u59ec\u6653\u98ce\u6162\u6761\u65af\u7406\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u731c\u9648\u5929\u5b87\u4e3a\u4f55\u5931\u8e2a\uff1f\u539f\u6765\u662f\u4ed6\u7684\u59bb\u5b50\u7ed9\u4eba\u52ab\u53bb\u4e86\u3002\u90a3\u4e00\u665a \u6709\u4e24\u4e2a\u8d3c\u4eba\u6109\u4eba\u4ed6\u5bb6\uff0c\u7528\u4e00\u79cd\u795e\u5947\u7684\u8ff7\u9999\u8ff7\u660f\u4e86\u4ed6\u7684\u5168\u5bb6\uff0c\u9648\u5929\u5b87\u5185\u529f\u5df2\u6709\u6839\u5e95\uff0c\u5f53\u65f6\u8ff7\u8ff7 \u7cca\u7cca\uff0c\u4f46\u8fd8\u6ca1\u6709\u5b8c\u5168\u5931\u53bb\u4e86\u77e5\u89c9\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u7eed\u9053\uff1a\u201c\u9648\u5929\u5b87\u773c\u7741\u7741\u7684\u770b\u7740\u8d3c\u4eba\u52ab\u8d70\u4ed6\u7684\u59bb\u5b50\uff0c\u6beb\u65e0\u529e\u6cd5\uff0c\u4f46\u4ed6\u6bd5\u7adf\u662f\u4e2a\u8bfb\u4e66 \u4eba\uff0c\u6709\u70b9\u806a\u660e\uff0c\u5728\u65e0\u53ef\u4eb2\u4f55\u4e4b\u4e2d\uff0c\u5ffd\u5730\u7075\u673a\u4e00\u89e6\uff0c\u4e00\u4e0b\u72e0\u52b2\uff0c\u4fbf\u54ac\u7834\u4e86\u820c\u5c16\u3002\u201d\u6c5f\u6d77\u5929\u542c\u5f97 \u7d27\u5f20\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u6709\u4f55\u7528\uff1f\u96be\u9053\u4ed6\u4e5f\u61c2\u5f97\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\u4e48\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4ed6\u5e76\u975e\u61c2\u5f97\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\uff0c\u4ed6\u54ac\u7834\u820c\u5c16\uff0c\u662f\u4e3a\u4e86\u8981\u4f7f\u81ea\u5df1\u611f\u5230\u75bc\u75db\uff0c\u8fd9\u624d \u80fd\u533e\u8d77\u7cbe\u795e\uff0c\u5c3d\u7ba1\u8eab\u4f53\u4e0d\u80fd\u52a8\u5f39\uff0c\u4f46\u4ecd\u4fdd\u6301\u7740\u77e5\u89c9\uff0c\u4e0d\u81f3\u660f\u8ff7\uff0c\u540e\u6765\u4ed6\u5bf9\u6211\u8bf4\uff0c\u4ed6\u5f53\u65f6\u53ea\u6709 \u4e00\u4e2a\u5fc3\u601d\uff0c\u5c31\u662f\u8981\u7262\u7262\u7684\u8bb0\u7740\u90a3\u4e24\u4e2a\u8d3c\u4eba\u7684\u5f62\u8c8c\uff0c\u5018\u82e5\u795e\u667a\u660f\u8ff7\uff0c\u90a3\u5c31\u505a\u4e0d\u5230\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u8ff7\u9999\u7684\u6548\u529b\u672c\u6765\u53ef\u4ee5\u4fdd\u6301\u4e00\u65e5\u4e00\u591c\uff0c\u800c\u4e14\u82e5\u662f\u660f\u8ff0\u4e86\u4e24\u4e2a\u65f6\u8fb0\u4ee5\u4e0a\uff0c\u5c1a\u672a\u5f97\u5230\u89e3\u6551 \u7684\u8bdd\uff0c\u5c06\u6765\u9192\u4e86\uff0c\u4e5f\u4f1a\u53d8\u6210\u767d\u75f4\u3002\u5e78\u4e8f\u9648\u5929\u5b87\u5185\u529f\u5df2\u6709\u6839\u5e95\uff0c\u5f53\u65f6\u53c8\u54ac\u7834\u4e86\u820c\u5c16\uff0c\u4fdd\u6301\u7740\u77e5 \u89c9\uff0c\u8d3c\u4eba\u8d70\u540e\uff0c\u4ed6\u6323\u624e\u7740\u8d77\u6765\uff0c\u6253\u5f00\u4e86\u7a97\u6237\uff0c\u9ed8\u8fd0\u7384\u529f\u3002\u7ec8\u4e8e\u5728\u534a\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u6e10\u6e10\u6062\u590d\u4e86 \u4f53\u529b\u3002 \u3000\u3000\u201c\u4ed6\u5c11\u5e74\u65f6\u5019\uff0c\u66fe\u670d\u8fc7\u51b0\u5bab\u4e2d\u7684\u5f02\u679c\uff0c\u8eab\u8f7b\u5982\u71d5\uff0c\u8bba\u5230\u8f7b\u529f\u3002\u5f53\u4eca\u4e4b\u4e16\uff0c\u5927\u7ea6\u9664\u4e86\u4f60\u7684 \u5e08\u7236\u3001\u51b0\u5ddd\u5929\u5973\u4e0e\u53ca\u6211\u4e4b\u5916\uff0c\u4ed6\u53ef\u4ee5\u6570\u5230\u5f1f\u56db\u4f4d\uff0c\u5f53\u65f6\u4ed6\u8fd8\u5e0c\u671b\u53ef\u4ee5\u8ffd\u4e0a\u654c\u4eba\uff0c\u593a\u56de\u59bb\u5b50\uff0c \u53ef\u60dc\u7ec8\u662f\u8fdf\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u4e0d\u7720\u4e0d\u98df\uff0c\u8ffd\u4e86\u4e00\u5929\u4e00\u591c\uff0c\u5140\u662f\u672a\u80fd\u53d1\u73b0\u8d3c\u4eba\u7684\u8e2a\u5f71\u3002\u53ef\u89c1\u90a3\u4e24 \u4e2a\u8d3c\u4eba\u4e5f\u786e\u5b9e\u6709\u70b9\u672c\u9886\uff0c\u5e76\u975e\u5168\u9760\u8ff7\u9999\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u53f9\u4e86\u53e3\u6c14\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u540e\u6765\u7684\u4e8b\uff0c\u4f60\u662f\u77e5\u9053\u7684\u4e86\uff0c\u9648\u5929\u5b87\u4ece\u6b64\u5c31\u6ca1\u6709\u518d\u56de\u8fc7\u8001 \u5bb6\uff0c\u5728\u522b\u4eba\u773c\u4e2d\uff0c\u662f\u79bb\u5947\u7684\u5931\u8e2a\u4e86\u3002\u4ed6\u7684\u4e24\u4e2a\u8001\u5bb6\u4eba\uff0c\u5438\u4e86\u8ff7\u9999\uff0c\u4e00\u4e2a\u4e2d\u6bd2\u800c\u6b7b\uff0c\u53e6\u4e00\u4e2a\u5219 \u6210\u4e86\u767d\u75f4\uff0c\u90a3\u5c31\u662f\u54b1\u4eec\u5f53\u5e74\u5728\u9648\u5bb6\u5893\u56fd\u6240\u89c1\u7684\u90a3\u4e2a\u6768\u8001\u5927\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u65f6\u5019\u9047\u89c1\u9648\u4f2f\u4f2f\u7684\uff1f\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u540e\u6765\u4f60\u7684\u7239\u7239\u548c\u6211\u76f8\u7ee7\u51fa\u53bb \u5bfb\u627e\u4ed6\uff0c\u4f60\u7684\u7239\u7239\u76f4\u5230\u73b0\u5728\u8fd8\u6ca1\u78b0\u89c1\u4ed6\u7684\u4e49\u5144\uff0c\u6211\u5374\u5e78\u8fd0\u5f97\u5f88\uff0c\u4e09\u5e74\u524d\u6211\u5230\u897f\u85cf\u4e00\u95f4\u5587\u561b\u5bfa \u53bb\uff0c\u60f3\u5077\u4ed6\u4eec\u5e99\u4e2d\u7684\u4e00\u5c0a\u6a80\u9999\u53e4\u4f5b\uff0c\u4e0d\u6599\u5374\u6b63\u78b0\u89c1\u9648\u5929\u5b87\u5728\u90a3\u91cc\u62dc\u4f5b\uff0c\u54c8\u54c8\uff0c\u4e5f\u8bb8\u662f\u90a3\u5bfa\u5e99 \u7684\u53e4\u4f5b\u6709\u70b9\u795e\u7075\uff0c\u77e5\u9053\u6211\u8981\u5077\u5b83\uff0c\u5c31\u6559\u6211\u4e0e\u9648\u5929\u5b87\u5de7\u9047\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u4ed6\u5728\u62dc\u4f5b\uff0c\u6211\u5f53\u7136\u5c31\u4e0d \u597d\u610f\u601d\u518d\u628a\u4f5b\u50cf\u5077\u8d70\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8be7\u9053\uff1a\u201c\u6211\u7239\u7239\u548c\u9648\u4f2f\u4f2f\u7d20\u6765\u662f\u4e0d\u4fe1\u4f5b\u7684\uff0c\u96be\u9053\u662f\u9648\u4f2f\u4f2f\u81ea\u5df1\u6ca1\u4e86\u4e3b\u610f\uff0c\u5c31\u53ea\u597d \u6307\u671b\u83e9\u8428\u4fdd\u4f51\u4e48\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u662f\uff0c\u8fd9\u91cc\u9762\u6709\u4e2a\u539f\u56f5\u3002\u6211\u521a\u624d\u4e0d\u662f\u8bf4\u9648\u5929\u5b57\u5df2\u628a\u90a3\u4e24\u4e2a\u8d3c\u4eba\u7684\u9762 \u8c8c\u7262\u7262\u5730\u8bb0\u4f4f\u4e86\u4e48\uff1f\u4ed6\u4e5f\u771f\u597d\u672c\u4e8b\uff0c\u51ed\u7740\u8bb0\u5fc6\uff0c\u5c31\u628a\u90a3\u4e24\u4e2a\u8d3c\u4eba\u7684\u76f8\u8c8c\u753b\u4e86\u51fa\u6765\uff0c\u753b\u5f97\u50cf\u6781 \u4e86\uff0c\u7b80\u76f4\u548c\u771f\u4eba\u4e00\u6a21\u4e00\u6837\uff01\u201d\u6c5f\u6bcf\u5929\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\uff0c\u56e0\u4e3a \u540e\u6765\u6211\u5c31\u51ed\u8fd9\u5f20\u753b\u50cf\uff0c\u627e\u5230\u4e86\u90a3\u4e24\u4e2a\u8d3c\u4eba\uff01\u54c8\uff0c\u8fd9\u624d\u771f\u53eb\u5947\u602a\u5462\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u662f\u4e24\u4e2a\u5587\u561b\u50e7\u88c5\u675f\u7684\u897f\u57df\u4eba\u3002\u201d\u6c5f\u6d77\u5929\u5947\u602a \u9053\uff1a\u201c\u4ed6\u4eec\u65e2\u662f\u897f\u57df\u50e7\u4eba\uff0c\u4e3a\u4f55\u8fdc\u6765\u4e2d\u571f\uff0c\u63b3\u63a0\u4e00\u4e2a\u5987\u4eba\u3002\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u548c\u9648\u5929 \u5b87\u4e5f\u662f\u767e\u601d\u83ab\u5f97\u5176\u89e3\u3002\u82e5\u8bf4\u662f\u9648\u5929\u5b87\u65e0\u610f\u4e2d\u7ed3\u4e0b\u7684\u4ec7\u5bb6\uff0c\u5f53\u665a\u9648\u5929\u5b87\u4e2d\u4e86\u8ff7\u9999\u4e4b\u540e\uff0c\u4ed6\u4eec\u8981 \u6740\u9648\u5929\u5b87\u6613\u5982\u53cd\u638c\uff0c\u4f46\u4ed6\u4eec\u5374\u53c8\u6ca1\u6709\u4f24\u5bb3\u9648\u5929\u5b87\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u6b47\u4e86\u7247\u523b\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u82e5\u8bf4\u4ed6\u4eec\u662f\u4e3a\u4e86\u52ab\u8272\uff0c\u8fd9\u4e5f\u4e0d\u50cf\u3002\u9648\u5929\u5b87\u7684\u59bb\u5b50\u5c11\u5e74\u4e4b \u65f6\u786e\u662f\u76f8\u5f53\u7f8e\u4e3d\uff0c\u4f46\u6bd4\u5979\u597d\u770b\u7684\u5973\u4eba\u4e5f\u8fd8\u591a\u7740\uff0c\u4f55\u51b5\u5979\u5728\u88ab\u52a8\u4e4b\u65f6\uff0c\u4e5f\u5df2\u662f\u5f90\u5a18\u534a\u8001\u4e86\uff0c\u4ed6 \u4eec\u4f55\u5fc5\u7528\u5c3d\u5fc3\u673a\uff0c\u4e0d\u8f9e\u52b3\u7d2f\u3001\uff0c\u8fdc\u6765\u4e2d\u571f\uff0c\u52ab\u4e00\u4e2a\u534a\u8001\u5f90\u5a18\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u7eed\u9053\uff1a\u201c\u867d\u7136\u60c5\u7406\u96be\u901a\uff0c\u4f46\u4e8b\u5b9e\u5df2\u7ecf\u5982\u6b64\u3002\u9648\u5929\u5b87\u65e2\u8ba4\u5b9a\u8d3c\u4eba\u662f\u897f\u57df\u5587\u561b\uff0c\u4fbf\u5230 \u5404\u5904\u5587\u561b\u5e99\u660e\u67e5\u6697\u8bbf\uff0c\u4ed6\u53c8\u8ba4\u5f97\u5f53\u665a\u6240\u95fb\u5230\u7684\u8ff7\u9999\uff0c\u5e26\u6709\u963f\u4fee\u7f57\u82b1\u7684\u9999\u6c14\uff0c\u8fd9\u963f\u4fee\u7f57\u82b1\u662f\u53ea \u751f\u957f\u5728\u559c\u9a6c\u62c9\u96c5\u5c71\u9876\u7684\u4e00\u79cd\u5947\u82b1\uff0c\u6c49\u540d\u53eb\u9b54\u9b3c\u82b1\uff0c\u5438\u4e86\u6b64\u82b1\u7684\u9999\u6c14\uff0c\u5c31\u5982\u559d\u9189\u4e86\u9152\u4e00\u822c\uff0c\u6240 \u4ee5\u897f\u57df\u7684\u90aa\u6d3e\u4e2d\u4eba\uff0c\u591a\u91c7\u7528\u6b64\u82b1\u6765\u914d\u5236\u8ff7\u9999\u3002\u9648\u5929\u5b87\u7531\u4e8e\u8ba4\u5f97\u8fd9\u79cd\u8ff7\u9999\uff0c\u662f\u4ee5\u5bf9\u4e8e\u897f\u85cf\u7684\u5587 \u561b\u50e7\u4eba\u53c8\u7279\u522b\u6ce8\u610f\u3002\u201d \u3000\u3000\u201c\u5587\u561b\u6559\u76db\u884c\u4e8e\u897f\u57df\u897f\u85cf\u3001\u8499\u53e4\uff0c\u9752\u6d77\u5404\u5730\uff0c\u53ca\u4e0e\u8499\u85cf\u6bd7\u8fde\u7684\u4e2d\u4e9a\u7ec6\u4e9a\u82e5\u5e72\u5730\u65b9\uff0c\u5355\u53ea \u897f\u85cf\u4e00\u5730\uff0c\u5c31\u6709\u5587\u561b\u5e99\u6570\u5343\u95f4\uff0c\u5587\u561b\u50e7\u4e0d\u4e0b\u5341\u4e07\u4e4b\u4f17\uff0c\u8981\u4ece\u6570\u5343\u95f4\u5bfa\u5e99\u3001\u5341\u4e07\u50e7\u4eba\u4e4b\u4e2d\uff0c\u627e \u51fa\u8fd9\u4e24\u4e2a\u4eba\u6765\uff1f\u8c08\u4f55\u5bb9\u6613\uff1f \u3000\u3000\u201c\u9648\u5929\u5b87\u62ff\u90a3\u753b\u56fe\u7ed9\u6211\u770b\u4e86\uff0c\u4fbf\u8bf7\u6211\u5e2e\u5fd9\uff0c\u6211\u4e49\u4e0d\u5bb9\u8f9e\uff0c\u5f53\u7136\u7b54\u5e94\u3002\u4e8e\u662f\u5206\u5934\u884c\u4e8b\u3002\u6211 \u662f\u505a\u60ef\u5077\u513f\u7684\uff0c\u65e5\u8d70\u5343\u5bb6\uff0c\u591c\u8d70\u767e\u6237\uff0c\u4e60\u5df2\u4e3a\u5e38\uff0c\u4f46\u8d70\u904d\u4e86\u897f\u85cf\u5404\u5904\u5bfa\u5e99\uff0c\u4e5f\u6574\u6574\u82b1\u4e86\u6211\u4e00 \u5e74\u65f6\u95f4\uff0c\u7ed3\u679c\u6beb\u65e0\u53d1\u73b0\u3002\u201d \u3000\u3000\u201c\u6211\u79bb\u5f00\u897f\u85cf\uff0c\u518d\u5f80\u5176\u4ed6\u5730\u65b9\u6697\u8bbf\u660e\u67e5\u3002\u524d\u5e74\u6625\u5929\u5230\u4e86\u9752\u6d77\uff0c\u6211\u5b58\u7740\u59d1\u4e14\u4e00\u8bd5\u7684\u5ff5\u5934\uff0c \u5728\u4e00\u5929\u665a\u4e0a\uff0c\u5077\u8fdb\u4e86\u767d\u6559\u6cd5\u738b\u7684\u9102\u514b\u6c81\u5bab\uff0c\u5077\u770b\u4ed6\u5bab\u4e2d\u6bcf\u4e2a\u5587\u561b\u7684\u76f8\u8c8c\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u4f2f\u4f2f\uff0c\u4f60\u597d\u5927\u80c6\uff01\u7adf\u6562\u618e\u8fdb\u767d\u6559\u6cd5\u738b\u7684\u5bab\u6bbf\u3002\u6211\u542c\u5e08\u7236\u8bf4\uff0c\u8fd9\u767d \u6559\u6cd5\u738b\u8eab\u6000\u7edd\u4e16\u6b66\u529f\uff0c\u5f53\u5e74\u6211\u5e08\u7236\u66fe\u4e0e\u4ed6\u8f83\u91cf\u8fc7\u4e00\u6b21\uff0c\u5f7c\u6b64\u4e0d\u5206\u80dc\u8d1f\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u5f97\u610f\u626c\u626c\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u6211\u7684\u786e\u662f\u80c6\u5927\u4e86\u4e00\u4e9b\uff0c\u90a3\u4e00\u665a\u4e5f\u7684\u786e\u597d\u9669\u3002\u53ef \u662f\u51fa\u4e4e\u610f\u5916\uff0c\u6211\u7adf\u7136\u5728\u767d\u6559\u6cd5\u738b\u7684\u5bab\u4e2d\uff0c\u89c1\u7740\u4e86\u9648\u5929\u5b57\u753b\u56fe\u4e2d\u90a3\u4e24\u4e2a\u5587\u561b\uff01\u4f46\u5728\u6211\u53d1\u73b0\u4ed6\u4eec \u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u4e5f\u53d1\u73b0\u4e86\u6211\uff01\u767b\u65f6\u6572\u54cd\u4e86\u8b66\u949f\uff0c\u9616\u5bfa\u7684\u5587\u561b\u90fd\u8dd1\u6765\u8ffd\u6355\u6211\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6cd5\u738b\u6709\u6ca1\u6709\u51fa\u6765\uff1f\u201d\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u82e5\u662f\u6cd5\u738b\u4e5f\u51fa\u6765\u4e86\uff0c\u6211\u8fd8\u80fd\u6d3b\u5230\u4eca\u5929 \u548c\u4f60\u8bf4\u6d3b\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u54e6\uff0c\u90a3\u6211\u5c31\u4e0d\u7528\u66ff\u4f60\u62c5\u4f18\u4e86\u3002\u6cd5\u5de5\u6ca1\u6709\u51fa\u6765\uff0c\u90a3\u4e9b\u5587\u561b\u51b3\u8ba1\u8dd1 \u4e0d\u8fc7\u4f60\uff0c\u5f53\u7136\u662f\u7ed9\u4f60\u4e00\u8d70\u4e86\u4e4b\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u6700\u540e\u5f53\u7136\u662f\u7ed9\u6211\u8dd1\u4e86\uff0c\u53ef\u662f\u5374\u6ca1\u6709\u4f60\u8bf4\u5f97\u8fd9\u4e48\u8f7b\u677e\u5bb9\u6613\u3002\u6cd5\u738b\u6ca1\u6709\u51fa \u6765\uff0c\u4ed6\u5ea7\u4e0b\u7684\u56db\u5927\u62a4\u6cd5\u5f1f\u5b50\u53ef\u90fd\u6765\u4e86\uff0c\u4f60\u77a7\u2014\u2014\u201d\u4ed6\u63ed\u8d77\u4e0a\u8863\u6307\u7ed9\u6c5f\u6d77\u5929\u770b\uff0c\u53ea\u89c1\u8170\u80c1\u4e00\u9053 \u957f\u957f\u7684\u75a4\u75d5\uff0c\u51f9\u9677\u6570\u5206\u3002\u59ec\u6653\u98ce\u53eb\u4ed6\u7528\u624b\u6307\u8f7b\u6309\uff0c\u53ea\u89c9\u624b\u6307\u6240\u89e6\u90fd\u662f\u4e00\u7247\u67d4\u5ae9\u7684\u808c\u8089\uff0c\u539f\u6765 \u5e94\u6709\u7684\u808b\u9aa8\u5374\u4e0d\u89c1\u4e86\u3002\u59ec\u6653\u98ce\u9053\uff0c\u201c\u6211\u7b97\u662f\u8dd1\u5f97\u5feb\u4e86\uff0c\u4f46\u8fd8\u662f\u7ed9\u6cd5\u738b\u7684\u9996\u5ea7\u62a4\u6cd5\u5f1f\u5b50\uff0c\u6253\u4e86 \u4e00\u8bb0\u4e5d\u73af\u9521\u6756\uff0c\u8fd9\u6761\u808b\u9aa8\uff0c\u5c31\u662f\u7ed9\u4ed6\u6253\u65ad\u4e86\u7684\u3002\u5e78\u4e8f\u6211\u4ee5\u524d\u5077\u7684\u5d06\u5cd2\u6d3e\u9f50\u8001\u5934\u5b50\u7684\u8865\u5929\u818f\u8fd8 \u5269\u4e0b\u4e86\u4e00\u4e9b\uff0c\u8fd9\u624d\u4e0d\u81f3\u4e8e\u843d\u4e2a\u6b8b\u5e9f\u3002\u201d\u6c5f\u6d77\u5929\u542c\u4e86\u4e0d\u89c9\u9a87\u7136\u3002 \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6211\u9003\u51fa\u6765\u4e4b\u540e\uff0c\u4e5f\u662f\u60ca\u9a87\u4e4b\u6781\u3002\u4e0d\u8fc7\uff0c\u6211\u5374\u5e76\u975e\u60ca\u9a87\u4e8e\u767d\u6559\u5587\u561b\u7684\u6b66\u529f\u7280 \u5229\uff0c\u800c\u662f\u60ca\u9a87\u4e8e\u6211\u6240\u53d1\u73b0\u7684\u8fd9\u4ef6\u4e8b\u60c5\uff1a\u90a3\u4e24\u4e2a\u8d3c\u4eba\u7adf\u662f\u9102\u514b\u6c81\u5bab\u7684\u5587\u561b\uff0c\u82e5\u975e\u773c\u89c1\uff0c\u771f\u662f\u4e0d \u6562\u60f3\u8c61\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u5374\u4e3a\u4f55\uff1f\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u4f60\u6709\u6240\u4e0d\u77e5\u3002\u5587\u561b\u6559\u73b0\u6709\u4e09\u652f\uff0c\u7ea2\u6559\u3001 \u9ec4\u6559\u548c\u767d\u6559\uff0c\u4ee5\u81ea\u6559\u7684\u52bf\u529b\u6700\u5c0f\uff0c\u4f46\u6212\u5f8b\u5374\u6700\u4e3a\u7cbe\u4e25\u3002\u90a3\u767d\u6559\u6cd5\u6b63\u786e\u662f\u4e2a\u4e16\u51fa\u7684\u5947\u624d\uff0c\u9053\u5fb7 \u6b66\u529f\u90fd\u662f\u5587\u561b\u4e09\u6559\u4e2d\u7684\u7b2c\u4e00\u4eba\u7269\u3002\u5728\u4ed6\u4eb2\u81ea\u4e3b\u6301\u4e0b\u7684\u9102\u514b\u6c81\u5bab\uff0c\u90a3\u4e9b\u5587\u561b\u66f4\u90fd\u662f\u7ecf\u5e38\u8981\u53d7\u4ed6 \u8003\u6838\u7684\uff0c\u6ca1\u6709\u4ed6\u7684\u6cd5\u65e8\uff0c\u51b3\u4e0d\u80fd\u79c1\u81ea\u51fa\u5b98\u3002\u90a3\u4e24\u4e2a\u8d3c\u5587\u561b\u8fdc\u5230\u6c5f\u5357\u52ab\u8d70\u9648\u5929\u5b87\u7684\u59bb\u5b50\uff0c\u6700\u5c11 \u8981\u79bb\u5f00\u672c\u5bfa\u534a\u5e74\uff0c\u4ed6\u4eec\u662f\u600e\u6837\u7792\u8fc7\u4e86\u6cd5\u738b\u7684\uff1f\u4f60\u8bf4\u5947\u4e0d\u5947\u602a\u5462\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u65e2\u7136\u5fc3\u6709\u6240\u7591\uff0c\u4f55\u4e0d\u5149\u660e\u6b63\u5927\u7684\u53bb\u6c42\u89c1\u6cd5\u738b\uff0c\u63ed\u53d1\u6b64\u4e8b\uff0c\u4e5f\u597d\u52a9\u4ed6\u8083\u6e05 \u8d25\u5173\uff0c\u6574\u987f\u95e8\u98ce\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u8bba\u7406\u5e94\u8be5\u5982\u6b64\uff0c\u4f46\u6211\u5374\u4e0d\u80fd\u8fd9\u4e48\u505a\uff0c\u4f60\u8981\u77e5\u9053\uff0c\u4f60\u59ec\u4f2f\u6015\u662f\u4e2a\u5077\u513f\u51fa\u8eab \u7684\uff0c\u76f4\u5230\u73b0\u5728\uff0c\u5018\u82e5\u89c1\u5230\u5fc3\u7231\u7684\u4e1c\u897f\uff0c\u4e5f\u8fd8\u662f\u5fcd\u4e0d\u7740\u8981\u5077\u7684\u3002\u603b\u800c\u8a00\u4e4b\uff0c\u6211\u7684\u574f\u540d\u58f0\u662f\u65e9\u5df2 \u4f20\u904d\u5929\u4e0b\u4e86\uff0c\u767d\u6559\u6cd5\u738b\u4f55\u7b49\u5c0a\u8363\uff0c\u5c82\u80fd\u7ea4\u5c0a\u964d\u8d35\u63a5\u89c1\u4e00\u4e2a\u5077\u513f\uff1f\u56fa\u7136\u6211\u4e5f\u53ef\u4ee5\u65e0\u987b\u8bf7\u4eba\u901a \u62a5\uff0c\u5077\u5077\u7684\u4fbf\u53bb\u89c1\u4ed6\uff0c\u4f46\u4ed6\u53c8\u600e\u80af\u76f8\u4fe1\u6211\u7684\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u7eed\u9053\uff1a\u201c\u4e3a\u4e86\u8ba1\u51fa\u4e07\u5168\uff0c\u6211\u53ea\u6709\u8bf7\u6bd4\u6211\u6709\u8eab\u4efd\u3002\u4e0e\u4ed6\u6709\u4ea4\u60c5\u7684\u4eba\u53bb\u89c1\u4ed6\uff0c\u8c03\u67e5\u6b64 \u4e8b\u3002\u6211\u5fc3\u76ee\u4e2d\u6709\u4e24\u4e2a\u4eba\uff0c\u4e00\u4e2a\u662f\u51b0\u5ddd\u5929\u5973\uff0c\u4e00\u4e2a\u662f\u4f60\u7684\u5e08\u7236\u3002\u51b0\u5ddd\u5929\u5973\u662f\u5c3c\u6cca\u5c14\u56fd\u7684\u516c\u4e3b\uff0c \u5979\u6709\u4f5b\u6559\u7684\u5b9d\u7269\u2014\u2014\u8d1d\u53f6\u7075\u7b26\uff0c\u53c8\u66fe\u53d7\u5929\u7afa\u96f7\u97f3\u5bfa\u5c01\u4e3a\u4f5b\u6559\u7684\u5973\u201c\u62a4\u6cd5\u201d\uff08\u8be6\u89c1\u300a\u51b0\u5ddd\u5929\u5973 \u4f20\u300b\uff09\uff0c\u4e14\u4e0e\u767d\u6559\u6cd5\u738b\u751a\u6709\u4ea4\u60c5\uff0c\u81ea\u662f\u6700\u9002\u5f53\u7684\u4eba\u9009\uff0c\u5176\u6b21\u662f\u4f60\u7684\u5e08\u7236\uff0c\u4f60\u7684\u5e08\u7236\u548c\u767d\u6559\u6cd5 \u738b\u6b63\u662f\u6240\u8c13\u2018\u4e0d\u6253\u4e0d\u6210\u76f8\u8bc6\u2019\uff0c\u7ecf\u8fc7\u4e86\u90a3\u6b21\u8f83\u91cf\u4e4b\u540e\uff0c\u6253\u51fa\u4e86\u6df1\u539a\u7684\u4ea4\u60c5\uff0c\u5f7c\u6b64\u4e92\u76f8\u656c\u91cd\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u5e08\u7236\u884c\u8e2a\u65e0\u5b9a\uff0c\u4ed6\u7684\u813e\u6c14\u53c8\u662f\uff1a\u53ea\u6709\u4ed6\u60f3\u89c1\u4f60\u7684\u65f6\u5019\uff0c\u4ed6\u81ea\u7136\u4f1a\u6765\u627e \u4f60\uff1b\u4f60\u53bb\u627e\u4ed6\uff0c\u5374\u662f\u5f88\u4e0d\u5bb9\u6613\u3002\u51b0\u5ddd\u5929\u5973\u5230\u5e95\u662f\u5c45\u6709\u5b9a\u5740\uff0c\u867d\u8bf4\u5979\u7684\u51b0\u5bab\u9ad8\u5904\u5ff5\u9752\u5510\u53e4\u62c9\u5c71 \u4e4b\u5dc5\uff0c\u90a3\u4e5f\u96be\u4e0d\u5012\u59ec\u6015\u4f2f\uff0c\u59ec\u4f2f\u4f2f\uff0c\u60f3\u6765\u4f60\u662f\u5148\u53bb\u8bf7\u51b0\u5ddd\u5929\u5973\u5427\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u6211\u5f53\u5e74\u672a\u66fe\u6539\u90aa\u5f52\u6b63\u7684\u65f6\u5019\uff0c\u66fe\u4e0e\u51b0\u5ddd\u5929\u5973\u7684\u4e08\u592b\u5510\u7ecf\u5929\u6253\u8fc7\u4e00\u67b6\uff0c\u5f53 \u65f6\u662f\u5728\u5929\u4e0b\u82f1\u96c4\u4e4b\u524d\uff0c\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\u4ea4\u624b\uff0c\u7ed3\u679c\u867d\u7136\u662f\u6211\u8f93\u4e86\uff0c\u4f46\u5510\u7ecf\u5929\u4e5f\u5403\u4e86\u4e00\u70b9\u4e8f\uff0c\u8fd9 \u8fc7\u8282\u4e00\u76f4\u672a\u6709\u673a\u4f1a\u63ed\u5f00\uff0c\u6240\u4ee5\u6211\u4e0d\u597d\u610f\u601d\u53bb\u627e\u4ed6\u4eec\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u5510\u7ecf\u5929\u6709\u5927\u4fa0\u4e4b\u79f0\uff0c\u60f3\u6765\u4e5f\u4e0d\u81f3\u4e8e\u6c14\u91cf\u72ed\u7a84\uff0c\u800c\u4e14\u4ed6\u548c\u6211 \u7684\u9648\u4f2f\u4f2f\u53c8\u662f\u5f88\u8981\u597d\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6b63\u662f\u56e0\u6b64\uff0c\u6240\u4ee5\u6211\u89c9\u5f97\u4e0d\u5982\u7531\u9648\u5929\u5b87\u53bb\u8bf7\u4ed6\u4eec\u592b\u5987\u8f83\u597d\uff0c\u6211\u5b81\u613f\u5230\u5904\u53bb\u78b0 \u8fd0\u6c14\uff0c\u5e0c\u671b\u78b0\u89c1\u4f60\u7684\u5e08\u7236\u3002\u201d \u3000\u3000\u539f\u6765\u59ec\u6653\u98ce\u56e0\u4e3a\u662f\u5c0f\u5077\u51fa\u8eab\uff0c\u53c8\u66fe\u7ecf\u662f\u8fc7\u5927\u9b54\u5934\u5b5f\u795e\u901a\u7684\u5f1f\u5b50\uff0c\u6240\u4ee5\u867d\u7136\u6539\u90aa\u5f52\u6b63\u4e4b \u540e\uff0c\u4e5f\u4ecd\u7136\u96be\u514d\u6709\u70b9\u81ea\u5351\u5fc3\u7406\uff0c\u7531\u4e8e\u81ea\u5351\uff0c\u4e5f\u5c31\u81ea\u5c0a\uff0c\u6240\u4ee5\u4ed6\u53ea\u613f\u610f\u548c\u51fa\u8eab\u5dee\u4e0d\u591a\u7684\u4eba\u5982\u91d1 \u4e16\u9057\u3001\u6c5f\u5357\u7b49\u4eba\u7ed3\u4e3a\u83ab\u9006\u4e4b\u4ea4\uff0c\u5374\u4e0d\u5927\u613f\u610f\u548c\u540d\u95e8\u6b63\u6d3e\u3002\u5730\u4f4d\u5d07\u9ad8\u7684\u4eba\u5f80\u6765\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u4e5f\u597d\u3002\u4f60\u548c\u9648\u4f2f\u6015\u5206\u9053\u626c\u9573\uff0c\u53cc\u7ba1\u9f50\u4e0b\uff0c\u603b\u6709\u4e00\u65b9\u6709\u4e2a\u7740\u843d\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u53ef\u662f\u73b0\u5728\u5374\u8fd8\u6ca1\u6709\u4e00\u4e2a\u7740\u843d\u5462\u3002\u9648\u5929\u5b87\u524d\u5f80\u51b0\u5bab\uff0c\u4e0d\u6599\u53bb\u5e74\u6070\u5de7\u662f\u5929\u5c71 \u6d3e\u95e8\u4e0b\u5f1f\u5b50\u5341\u5e74\u4e00\u5927\u4f1a\u4e4b\u671f\uff0c\u5510\u7ecf\u5929\u592b\u5987\u90fd\u5230\u5929\u5c71\u5357\u9ad8\u5cf0\u4ed6\u7236\u4eb2\u90a3\u91cc\u53bb\u4e86\u3002\u9648\u5929\u5b87\u53ea\u597d\u518d\u5230 \u5929\u5c71\u53bb\uff0c\u7ea6\u5b9a\u4ee5\u4e00\u5e74\u4e3a\u671f\uff0c\u5728\u81ea\u6559\u6cd5\u738b\u9102\u514b\u6c81\u5b98\u6240\u5728\u5730\u7684\u5723\u5580\u4f26\u5c71\u4e0e\u6211\u76f8\u4f1a\u3002\u5728\u8fd9\u4e00\u5e74\u4e2d\uff0c \u6211\u5230\u5404\u65b9\u5bfb\u89c5\u4f60\u7684\u5e08\u7236\uff0c\u6ca1\u6709\u89c1\u7740\uff0c\u5374\u5148\u78b0\u89c1\u4e86\u4f60\u7684\u7236\u4eb2\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5927\u559c\u9053\uff1a\u201c\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u5728\u897f\u85cf\u7684\u8428\u8fe6\u3002\u6211\u61c2\u5f97\u4f60\u7236\u4eb2\u7684\u813e\u6c14\uff0c \u4ed6\u662f\u6700\u5ff5\u65e7\u60c5\u7684\uff0c\u4ed6\u66fe\u5728\u8428\u8fe6\u4f4f\u8fc7\u5341\u5e74\uff0c\u6709\u4e0d\u5c11\u719f\u4eba\uff0c\u6211\u731c\u60f3\u4ed6\u4e00\u5b9a\u4f1a\u5230\u8428\u8fe6\u62dc\u8bbf\u6545\u65e7\uff0c\u5c31 \u5148\u5230\u8428\u8fe6\u7b49\u4ed6\uff0c\u679c\u7136\u7ed9\u6211\u7b49\u7740\u4e86\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u7eed\u9053\uff1a\u201c\u4f60\u7236\u4eb2\u5728\u8428\u8fea\u4e5f\u6709\u70b9\u4e0d\u5e73\u5e38\u7684\u906d\u9047\uff0c\u4e0d\u8fc7\u4e0e\u672c\u9898\u65e0\u5173\uff0c\u4e8b\u60c5\u4e5f\u4e0d\u5f88\u91cd \u8981\u3002\u6211\u77e5\u9053\u4f60\u6025\u4e8e\u77e5\u9053\u4f60\u7236\u4eb2\u7684\u8fd1\u51b5\u3002\u6211\u6682\u4e14\u628a\u4ed6\u5728\u8428\u8fe6\u7684\u4e8b\u60c5\u6401\u540e\u518d\u8c08\u5427\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u542c\u8bf4\u8428\u8ff9\u4e4b\u4e8b\uff0c\u65e0\u5173\u91cd\u8981\uff0c\u4fbf\u9053\uff1a\u201c\u5bf9\uff0c\u8fd8\u662f\u5148\u8bf4\u6211\u7239\u7239\u548c\u9648\u4f2f\u4f2f\u7684\u4e8b\u5427\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4f60\u7239\u7239\u77e5\u9053\u4e86\u4ed6\u4e49\u5144\u7684\u6d88\u606f\u4e4b\u540e\uff0c\u6781\u662f\u5173\u6000\uff0c\u81ea\u544a\u594b\u52c7\uff0c\u5230\u9102\u514b\u6c81\u5bab\u6c42\u89c1 \u767d\u6559\u6cd5\u738b\uff0c\u63a2\u8bbf\u4e49\u5ac2\u7684\u4e0b\u843d\u3002\u4ed6\u5f53\u5e74\u66fe\u4e0e\u4f60\u5e08\u7236\u4e00\u9053\u89c1\u8fc7\u767d\u6559\u6cd5\u4e3b\u7684\uff0c\u6240\u4ee5\u6709\u70b9\u4ea4\u60c5\u3002\u201d\u6c5f \u6d77\u5929\u9053\u201c\u54e6\uff0c\u539f\u6765\u5982\u6b64\u3002\u602a\u4e0d\u5f97\u9002\u624d\u5e72\u8001\u524d\u8f88\u8bf4\u53bb\u5e74\u66fe\u6709\u4eba\u5728\u9102\u514b\u6c81\u5bab\u89c1\u8fc7\u5bb6\u7236\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u7239\u7239\u89c1\u4e86\u767d\u6559\u6cd5\u738b\uff0c\u7ed3\u679c\u5982\u4f55\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4ed6\u4eec\u4e8c\u4eba\u591a\u5e74\u672a\u89c1\uff0c\u6240\u4ee5\u521d\u89c1\u9762\u7684\u65f6\u5019\uff0c\u5927\u5bb6\u90fd\u5f88\u9ad8\u5174\u3002\u767d\u6559\u6cd5\u738b\u5e76\u4e0d\u56e0 \u4e3a\u4f60\u7236\u4eb2\u51fa\u8eab\u4f4e\u5fae\u2014\u2014\u8d24\u4f84\uff0c\u4f60\u7239\u7239\u4ece\u4e0d\u8bb3\u8a00\u826f\u5df1\u7684\u51fa\u8eab\u4f4e\u5fae\uff0c\u6211\u4e5f\u5c31\u76f4\u8a00\u4e86\uff0c\u8bf7\u52ff\u89c1 \u602a\u3002\u2014\u2014\u7a0d\u6709\u6020\u6162\uff0c\u800c\u662f\u5bf9\u4ed6\u6bb7\u52e4\u62db\u5f85\u3001\u656c\u5982\u4e0a\u5bbe\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\uff0c\u4e00\u5230\u4f60\u7239\u7239\u63d0\u8d77\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u8bf4\u4ed6\u7684\u9102\u514b\u6c81\u5bab\u91cc\u85cf\u6709\u4e24\u4e2a\u4e3a\u975e\u4f5c\u6b79\u7684\u574f\u4eba\u7684\u65f6 \u5019\u3002\u6cd5\u738b\u7684\u6001\u5ea6\u4fbf\u9a6c\u4e0a\u53d8\u4e86\uff0c\u795e\u8272\u96be\u770b\u4e4b\u6781\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u4e5f\u96be\u602a\uff0c\u8fd9\u662f\u5927\u5927\u6709\u635f\u4ed6\u989c\u9762\u7684\u4e8b\u60c5\u3002\u4ed6\u7109\u5e3d\u4e0d\u4e3a\u4e4b\u9707\u6012\uff1f\u60f3\u6765\u90a3\u4e24\u4e2a \u5587\u561b\uff0c\u5fc5\u5b9a\u8981\u53d7\u4ed6\u91cd\u91cd\u7684\u60e9\u7f5a\u4e86\uff0c\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4e0d\uff0c\u4ed6\u5e76\u6ca1\u6709\u52a8\u6012\u3002\u636e\u4f60\u7239\u7239\u8bf4\uff0c\u6cd5\u738b\u5f53\u65f6\u7684\u795e\u60c5\u4f3c\u4e4e\u6709\u70b9\u5c34\u5c2c\uff0c\u4ed6\u5446\u4e86 \u4e00\u4f1a\uff0c\u663e\u5f97\u9887\u4e3a\u9893\u4e27\u7684\u6837\u5b50\uff0c\u8bb8\u4e45\u90fd\u6ca1\u6709\u8bf4\u8bdd\u3002\u4f60\u7239\u7239\u4ee5\u4e3a\u4ed6\u662f\u5728\u4f24\u5fc3\uff0c\u6b63\u60f3\u7528\u2018\u5bfa\u5927\u618e \u4f17\uff0c\u96be\u514d\u6709\u826f\u840e\u4e0d\u9f50\u2019\u4e4b\u7c7b\u7684\u8bf4\u8bdd\u53bb\u529d\u6170\u4ed6\uff0c\u5374\u4e0d\u6599\u4ed6\u5ffd\u7136\u8868\u793a\u51fa\u4e0d\u76f8\u4fe1\u7684\u6001\u5ea6\uff0c\u95ee\u4f60\u7239\u7239 \u6709\u4f55\u51ed\u636e\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u65e9\u5df2\u9632\u5907\u6cd5\u5de5\u4f1a\u6709\u6b64\u4e00\u95ee\u7684\uff0c\u6240\u4ee5\u6211\u4e8b\u524d\u5df2\u7ed9\u4f60\u7239\u7239\u51c6\u5907\u597d\u4e86\uff0c\u9648\u5929\u5b87\u90a3\u5e45\u753b\u5728 \u6211\u624b\u4e2d\uff0c\u4f60\u7239\u7239\u53bb\u8c12\u89c1\u6cd5\u738b\u7684\u65f6\u5019\uff0c\u6211\u5c31\u628a\u90a3\u5e45\u753b\u7ed9\u4f60\u7239\u7239\u968f\u8eab\u5e26\u53bb\u3002 \u3000\u3000\u201c\u6cd5\u738b\u770b\u4e86\u90a3\u5e45\u753b\uff0c\u4f60\u731c\u4ed6\u600e\u4e48\u8bf4\uff1f\u2014\u2014\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4ee5\u6cd5\u738b\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u8ba4\u51fa\u4e86\u662f\u4ed6\u5bfa\u4e2d\u7684\u5587\u561b\uff0c\u4ed6\u603b\u4e0d\u597d\u610f\u601d\u62b5\u8d56\u5427\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u6124\u7136\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u62b5\u8d56\uff01\u4ed6\u770b\u4e86\u4e4b\u540e\uff0c\u7adf\u6562\u8bf4\u9053\uff1a\u2018\u8fd9\u4e24\u4e2a\u4eba\u662f\u8c01\uff1f\u6211\u7684\u9102\u514b\u6c81 \u5bab\u5e76\u6ca1\u6709\u8fd9\u4e24\u4e2a\u4eba\uff01\u2019\u201d \u3000\u3000\u201c\u8fd9\u4e24\u4e2a\u4eba\u662f\u6211\u4eb2\u773c\u89c1\u5230\u7684\uff0c\u662f\u4ed6\u5bab\u4e2d\u7684\u5587\u561b\uff0c\u51b3\u4e0d\u4f1a\u9519\u3002\u53ef\u662f\u6cd5\u738b\u8fd9\u4e48\u8bf4\uff0c\u4f60\u7239\u7239\u8fd8 \u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u603b\u4e0b\u80fd\u53eb\u6cd5\u738b\u5c06\u5bab\u4e2d\u7684\u5587\u561b\u90fd\u53ec\u96c6\u8d77\u6765\uff0c\u8ba9\u4ed6\u6309\u56fe\u6307\u8ba4\u3002\u201d \u3000\u3000\u201c\u6cd5\u738b\u53c8\u95ee\u4f60\u7239\u7239\u8fd9\u6d88\u606f\u7684\u6765\u6e90\uff0c\u7239\u7239\u662f\u4e2a\u76f4\u5fc3\u773c\u513f\u7684\u4eba\uff0c\u4ece\u4e0d\u8bf4\u8c0e\u7684\uff0c\u4ed6\u4e00\u7740\u6025\uff0c\u4fbf \u628a\u6211\u4f9b\u4e86\u51fa\u6765\uff0c\u8bf4\u8fd9\u662f\u2018\u795e\u5077\u59ec\u6653\u98ce\u2019\u4eb2\u773c\u5728\u8d35\u5bfa\u770b\u89c1\u7684\u3002 \u3000\u3000\u201c\u6cd5\u738b\u6c89\u4e0b\u4e86\u9762\uff0c\u8bf4\uff1a\u2018\u8fd9\u4e8b\u60c5\u6211\u7684\u62a4\u6cd5\u5f1f\u5b50\u65e9\u6709\u7980\u62a5\u3002\u795e\u5077\u59ec\u6653\u98ce\u90a3\u6b21\u5077\u8fdb\u672c\u5bfa\uff0c\u610f \u56fe\u76d7\u5b9d\uff0c\u6328\u4e86\u4ed6\u4e00\u8bb0\u4e5d\u73af\u9521\u6756\u3002\u60f3\u5fc5\u662f\u59ec\u6653\u98ce\u56e0\u6b64\u542b\u6068\uff0c\u634f\u9020\u51fa\u4e00\u6d3e\u80e1\u8a00\uff01\u2019\u201d\u4ed6\u53c8\u8bf4\uff1a \u201c\u4f60\u56de\u53bb\u544a\u8bc9\u59ec\u6653\u98ce\uff0c\u53eb\u4ed6\u4ece\u4eca\u4e4b\u540e\uff0c\u5728\u9102\u514b\u6c81\u5bab\u5468\u56f4\u767e\u91cc\u4e4b\u5185\uff0c\u5343\u4e07\u4e0d\u53ef\u8e0f\u8fdb\u6765\u3002\u5426\u5219\u82e5 \u7ed9\u672c\u5bfa\u5f1f\u5b50\u53d1\u73b0\uff0c\u6027\u547d\u96be\u4fdd\uff01\u2019\u4f60\u77a7\uff0c\u4ed6\u4e0d\u4f46\u53cd\u54ac\u6211\u4e00\u53e3\uff0c\u800c\u4e14\u8fd8\u5bf9\u6211\u65bd\u52a0\u606b\u5413\u3002\u54c8\u54c8\uff0c\u6211 \u59ec\u6653\u98ce\u5c82\u662f\u6015\u4eba\u5413\u7684\uff1f\u767d\u6559\u6cd5\u738b\u867d\u6709\u7edd\u4e16\u6b66\u529f\uff0c\u4f46\u6211\u59ec\u6653\u98ce\u5374\u6709\u4e00\u6761\u70c2\u547d\uff0c\u6211\u5c31\u504f\u504f\u4e0d\u6015 \u4ed6\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u662f\u53d7\u4e86\u5f1f\u5b50\u7684\u8499\u9a97\u5427\uff1f\u4ed6\u662f\u6211\u5e08\u7236\u656c\u91cd\u7684\u4eba\uff0c\u603b\u4e0d\u81f3\u4e8e\u8fd9\u6837\u5351\u9119 \u5427\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u53c8\u9053\uff1a\u201c\u59ec\u6015\u4f2f\uff0c\u8bf7\u4f60\u4e5f\u6055\u6211\u76f4\u8a00\uff0c\u4f60\u662f\u5929\u4e0b\u77e5\u540d\u7684\u5999\u624b\u795e\u5077\uff0c\u8bbe\u82e5\u6cd5\u738b\u6709\u51e0\u4e2a \u5f1f\u5b50\u548c\u90a3\u4e24\u4e2a\u8d3c\u4eba\u662f\u540c\u515a\u7684\uff0c\u5728\u4ed6\u9762\u524d\u626f\u8c0e\uff0c\u5f02\u53e3\u540c\u58f0\u8bf4\u662f\u53d1\u73b0\u4f60\u5077\u5165\u5bfa\u4e2d\uff0c\u610f\u56fe\u76d7\u5b9d\uff0c\u6cd5 \u738b\u5e76\u4e0d\u6df1\u77e5\u4f60\u7684\u4e3a\u4eba\uff0c\u90a3\u4e5f\u96be\u602a\u4ed6\u4f1a\u76f8\u4fe1\u7684\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6cd5\u738b\u5bf9\u6211\u7684\u6076\u611f\uff0c\u4e5f\u53ef\u80fd\u53d7\u4e86\u4e9b\u5f1f\u5b50\u7684\u8499\u9a97\uff0c\u4f60\u8fd9\u6837\u7684\u8bf4\u6cd5\u4e5f\u672a\u5c1d \u4e0d\u8bf4\u5f97\u901a\u3002\u4f46\u90a3\u4e24\u4e2a\u8d3c\u4eba\uff0c\u5206\u660e\u662f\u4ed6\u5bfa\u4e2d\u7684\u5587\u561b\uff0c\u6309\u4ed6\u81ea\u5df1\u6240\u5b9a\u7684\u89c4\u77e9\uff0c\u4ed6\u5bfa\u4e2d\u7684\u5587\u561b\uff0c\u6bcf \u4e09\u5341\u6708\u8981\u53d7\u4e00\u6b21\u5c0f\u8003\u6838\uff0c\u5e74\u7ec8\u6709\u4e00\u6b21\u5927\u8003\u6838\uff0c\u4ed6\u600e\u4f1a\u4e0d\u80fd\u8ba4\u5f97\u81ea\u5df1\u624b\u4e0b\u7684\u5587\u561b\uff1f\u4f46\u4ed6\u5728\u4f60\u7239 \u7239\u9762\u524d\u5374\u63a8\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u7adf\u6562\u8bf4\u4ed6\u7684\u9102\u514b\u6c81\u5bab\u91cc\u6ca1\u6709\u8fd9\u4e24\u4e2a\u4eba\uff1b\u8fd9\u603b\u4e0d\u80fd\u8bf4\u662f\u53d7\u4e86\u5f1f\u5b50\u7684\u8499 \u9a97\u5427\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u60c5\u7684\u786e\u662f\u6709\u70b9\u4ee4\u4eba\u83ab\u540d\u5176\u5999\u3002\u597d\u5728\u9648\u4f2f\u4f2f\u5df2\u8d76\u5f80\u5929\u5c71\u9080\u8bf7\u5510\u7ecf\u5929\u592b \u5987\uff0c\u603b\u4f1a\u6709\u6c34\u843d\u77f3\u51fa\u7684\u65e5\u5b50\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u518d\u8bf4\u4f60\u7684\u7239\u7239\u3002\u767d\u6559\u6cd5\u738b\u8d77\u521d\u5bf9\u4ed6\u6bb7\u52e4\u62db\u5f85\uff0c\u656c\u5982\u4e0a\u5bbe\uff0c\u540e\u6765 \u4f60\u7239\u7239\u63d0\u8d77\u4e86\u90a3\u4ef6\u4e8b\u60c5\uff0c\u53cc\u65b9\u8bdd\u4e0d\u6295\u673a\uff0c\u6cd5\u738b\u7684\u6001\u5ea6\u5c31\u53d8\u5f97\u51b7\u51b7\u6de1\u8c08\u4e86\u3002\u4f60\u7239\u7239\u5bf9\u4eba\u968f\u548c\uff0c \u813e\u6c14\u6781\u597d\uff0c\u4f46\u4ed6\u4e5f\u662f\u4e2a\u6709\u9aa8\u6c14\u7684\u786c\u6c49\u5b50\uff0c\u5f53\u4e0b\u5c31\u7acb\u5373\u544a\u8f9e\u3002\u201d \u3000\u3000\u201c\u6cd5\u738b\u4e5f\u5e76\u4e0d\u633d\u7559\uff0c\u4f46\u5728\u9001\u4f60\u7239\u7239\u51fa\u5bab\u7684\u65f6\u5019\uff0c\u5374\u8bf4\u4e86\u51e0\u53e5\u8010\u4eba\u5bfb\u5473\u7684\u8bf4\u8bdd\u3002\u4ed6\u8bf4\uff1a \u2018\u4f60\u4e0b\u6b21\u6765\u6700\u597d\u4e0e\u91d1\u5927\u4fa0\u540c\u6765\uff0c\u4e8b\u5148\u4e0d\u5fc5\u7ecf\u6211\u95e8\u4e0b\u5f1f\u5b50\u901a\u62a5\uff0c\u91d1\u5927\u4fa0\u4ed6\u4f1a\u77e5\u9053\u600e\u6837\u4e0e\u6211\u89c1\u9762 \u7684\u3002\u9664\u4e86\u662f\u4e3b\u5927\u4fa0\uff0c\u5176\u4ed6\u7684\u4eba\uff0c\u4f60\u5207\u4e0d\u53ef\u5192\u6627\u7684\u5e26\u9886\u4ed6\u4eec\u524d\u6765\u3002\u2019\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u51e0\u53e5\u8bdd\u786e\u662f\u6709\u70b9\u53e4\u602a\u3002\u4f3c\u4e4e\u6cd5\u738b\u5f88\u60f3\u89c1\u6211\u7684\u5e08\u7236\uff0c\u800c\u4e14\u662f\u60f3\u7792\u4f4f\u5f1f \u5b50\u6765\u548c\u6211\u5e08\u7236\u89c1\u9762\u7684\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u5c31\u53ef\u60dc\u627e\u4e0d\u7740\u4f60\u7684\u5e08\u7236\uff0c\u5426\u5219\u8fd9\u4e2a\u54d1\u8ff7\u53ef\u4ee5\u65e9\u4e9b\u6253\u7834\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u521a\u624d\u90a3\u53f6\u51b2\u9704\u8bf4\uff0c\u6211\u7684\u5e08\u7236\u4ed6\u4e5f\u8981\u53c2\u52a0\u91d1\u9e70\u5bab\u4e4b\u4f1a\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u91d1\u9e70\u5bab\u7684\u4f1a\u671f\u662f\u4e0b\u6708\u5341\u4e94\uff0c\u4f46\u6211\u5df2\u7ea6\u597d\u4e86\u4f60\u7684\u7239\u7239\u5728\u672c\u6708\u6708\u5e95\u76f8\u4f1a\u3002\u56e0\u4e3a \u9648\u5929\u5b87\u5728\u524d\u5f80\u5929\u5c71\u4e4b\u65f6\uff0c\u4e0e\u6211\u5b9a\u4e0b\u4e86\u4e00\u5e74\u4e4b\u540e\uff1b\u5728\u9102\u514b\u6c81\u5b98\u7684\u6240\u5728\u5730\u5723\u5580\u4f26\u5c71\u76f8\u89c1\uff0c\u65f6\u95f4\u6b63 \u662f\u672c\u6708\u6708\u5e95\u3002\u4f60\u7239\u7239\u8fd8\u672a\u66fe\u4e0e\u9648\u5929\u5b87\u89c1\u8fc7\u9762\uff0c\u56e0\u6b64\u6211\u5c31\u5c06\u4e0e\u9648\u5929\u5b87\u7ea6\u597d\u7684\u65f6\u95f4\u5730\u70b9\u544a\u8bc9\u4ed6\uff0c \u4ed6\u8bf4\u4ed6\u51c6\u5907\u65e9\u4e24\u4e09\u5929\u5148\u5230\u5723\u5580\u4f26\u5c71\u7b49\u5019\u6211\u4eec\u5462\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u53c8\u9053\uff1a\u201c\u672c\u6765\u7b49\u5230\u89c1\u7740\u4e86\u4f60\u7684\u5e08\u7236\u4e4b\u540e\uff0c\u4e00\u540c\u524d\u5f80\u6700\u597d\u3002\u53ef\u662f\u6211\u53c8\u6015\u4f60\u7239\u7239\u72ec\u81ea \u4e00\u4eba\u7b49\u5f97\u5fc3\u7126\u3002\u800c\u4e14\u8001\u5b9e\u8bf4\uff0c\u6211\u5bf9\u767d\u6559\u6cd5\u4e3b\u4e5f\u4fe1\u4e0d\u8fc7\u4e86\uff0c\u6211\u62c5\u5fc3\u4f60\u7239\u7239\u4e00\u4eba\u4f1a\u95f9\u51fa\u4e8b\u6765\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u90a3\u4e48\u53ea\u597d\u5148\u89c1\u6211\u7684\u7239\u7239\u4e86\uff0c\u597d\u5728\u4ee5\u54b1\u4eec\u7684\u811a\u7a0b\uff0c\u65f6\u95f4\u5927\u7ea6\u8fd8\u6765\u5f97\u53ca\u3002\u201d\u4e24 \u4eba\u8ba1\u8bae\u5df2\u5b9a\uff0c\u4fbf\u5373\u52a8\u8eab\uff0c\u5411\u9752\u6d77\u8fdb\u53d1\u3002\u6b63\u662f\uff1a \u3000\u3000\u98ce\u4e91\u53d8\u5e7b\u6b8a\u96be\u6d4b\uff0c\u8c01\u633d\u5929\u6cb3\u6d17\u7532\u5175\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u8ff7\u9014\u5927\u6f20\u906d\u5947\u9669\u3000\u8bc6\u9014\u795e\u5077\u9047\u6545\u4eba \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u8ff7\u9014\u5927\u6f20\u906d\u5947\u9669\u3000\u8bc6\u9014\u795e\u5077\u9047\u6545\u4eba \u3000\u3000\u767d\u6559\u6cd5\u738b\u7684\u9102\u514b\u6c81\u5bab\u5728\u9752\u6d77\u7684\u4f0a\u514b\u662d\u76df\uff0c\u5317\u9762\u6709\u5723\u5580\u4f26\u5c71\u6321\u4f4f\u4ece\u897f\u5317\u9ad8\u539f\u5439\u6765\u7684\u5bd2\u98ce\uff0c \u5357\u9762\u6709\u5723\u6606\u5e03\u6cb3\u4ece\u76d8\u5730\u6d41\u8fc7\uff0c\u56e0\u6b64\u5728\u90a3\u65b9\u5706\u767e\u91cc\u7684\u76d8\u5730\u4e0a\uff0c\u6c34\u8349\u80a5\u7f8e\uff0c\u725b\u7f8a\u7e41\u6b96\uff0c\u50cf\u897f\u85cf\u7684 \u62c9\u8428\u4e00\u6837\uff0c\u6709\u201c\u585e\u4e0a\u6c5f\u5357\u201d\u4e4b\u79f0\u3002\u4f46\u5728\u5723\u5580\u4f26\u5c71\u5916\u8fb9\uff0c\u79bb\u5f00\u9102\u514b\u6c81\u5bab\u7ea6\u767e\u91cc\u4e4b\u5730\u7684\u4e00\u5927\u7247\u5730 \u65b9\uff0c\u5374\u662f\u5bf8\u8349\u4e0d\u751f\u7684\u6c99\u6f20\uff0c\u540d\u4e3a\u547c\u6c57\u90aa\u6208\u58c1\uff0c\u9762\u79ef\u7ea6\u6709\u4e09\u767e\u5e73\u65b9\u516c\u91cc\uff0c\u662f\u9752\u6d77\u7684\u7b2c\u4e09\u5927\u6c99\u6f20\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\uff0c\u59ec\u6653\u98ce\u4e0e\u6c5f\u6d77\u5929\u8fdb\u5165\u4e86\u547c\u6c57\u90aa\u6208\u58c1\uff0c\u6c5f\u6d77\u5929\u8fd8\u662f\u7b2c\u4e00\u6b21\u8e0f\u8fdb\u6c99\u6f20\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c \u53ea\u89c1\u6f20\u6f20\u9ec4\u6c99\uff0c\u9065\u63a5\u5929\u9645\uff0c\u6709\u65f6\u4e00\u811a\u8e0f\u7740\u6d6e\u6c99\uff0c\u8981\u8d39\u5f88\u5927\u7684\u6c14\u529b\u624d\u62d4\u5f97\u51fa\u6765\uff0c\u4e0d\u7981\u6709\u70b9\u5fc3 \u60b8\uff0c\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u4f60\u672a\u8d70\u8fc7\u6c99\u6f20\uff0c\u5f88\u4e0d\u4e60\u60ef\u5427\uff1f\u5176\u5b9e\u8fd9\u4e2a\u547c\u6c57\u90aa\u6208\u58c1\uff0c\u5c1a\u662f\u6c99\u6f20\u4e2d\u7684\u5c0f\u7109 \u8005\u4e5f\uff0c\u6211\u5f53\u5e74\u8d70\u8fc7\u56de\u7586\u7684\u5927\u6208\u58c1\uff0c\u90a3\u624d\u771f\u662f\u540d\u526f\u5176\u5b9e\u7684\u5927\u5462\u3002\u6211\u4e5f\u8981\u8d70\u4e09\u5929\u4e09\u591c\u624d\u8d70\u5f97\u51fa \u6765\u3002\u6bcf\u5e74\u56e0\u4e3a\u8ff7\u5931\u65b9\u5411\u800c\u88ab\u57cb\u5728\u6c99\u6f20\u4e2d\u7684\u4e0d\u77e5\u591a\u5c11\uff01\u90a3\u6b21\u6211\u5e78\u4e8f\u6ca1\u6709\u9047\u7740\u522e\u98ce\uff0c\u53c8\u96c7\u6709\u9a86 \u9a7c\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u59ec\u4f2f\u4f2f\uff0c\u4f60\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u672c\u9886\u53c8\u9ad8\uff0c\u6c99\u6f20\u5f53\u7136\u96be\u4e0d\u5012\u4f60\u3002\u5c0f\u4f84\u4ece\u672a\u8d70\u8fc7\uff0c\u6bcf \u4e00\u6b65\u90fd\u5f97\u5c0f\u5fc3\uff0c\u4e0d\u7792\u4f60\u8bf4\uff0c\u5374\u662f\u6709\u70b9\u5fc3\u602f\u5462\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u4f60\u4e5f\u5b66\u4f1a\u7ed9\u4eba\u6234\u9ad8\u5e3d\u5b50\u4e86\u3002\u8bba\u672c\u9886\uff0c\u4f60\u73b0\u5728\u5df2\u6bd4\u6211\u9ad8\u660e\u5f97\u591a\uff1b\u8bf4 \u5230\u7ecf\u9a8c\uff0c\u90a3\u56fa\u7136\u662f\u9700\u8981\u65e5\u5b50\u5386\u7ec3\u51fa\u6765\u7684\uff0c\u4f46\u4e5f\u4e0d\u662f\u4ec0\u4e48\u4e86\u4e0d\u8d77\u7684\u79d8\u5bc6\uff0c\u6211\u53ef\u4ee5\u5c06\u51e0\u70b9\u91cd\u8981\u7684 \u7ecf\u9a8c\u4f20\u6388\u7ed9\u4f60\u3002\u201d\u5f53\u4e0b\u8bf4\u4e86\u51e0\u70b9\uff0c\u4f8b\u5982\u9047\u89c1\u9686\u8d77\u7684\u5730\u65b9\u90a3\u5c31\u662f\u5e95\u4e0b\u4e2d\u7a7a\u7684\uff0c\u4e0d\u53ef\u8e0f\u4e0a\u53bb\uff1a\u5929 \u672a\u522e\u98ce\u3002\u800c\u6c99\u571f\u6709\u79fb\u52a8\u8ff9\u8c61\u7684\u90a3\u5c31\u662f\u6d6e\u6c99\uff0c\u4e0d\u53ef\u6328\u8fd1\uff0c\u7b49\u7b49\u3002\u6c5f\u6d77\u5929\u4f9d\u8a00\u800c\u884c\uff1b\u679c\u7136\u8d70\u5f97\u987a \u5229\u8bb8\u591a\u3002 \u3000\u3000\u59ec\u6653\u98ce\u53c8\u9053\uff1a\u201c\u5176\u5b9e\u4ee5\u4f60\u7684\u672c\u9886\uff0c\u8fd8\u53ef\u4ee5\u7528\u4e00\u4e2a\u5999\u6cd5\uff0c\u4f60\u53ef\u4ee5\u65bd\u5c55\u767b\u840d\u5ea6\u6c34\u7684\u8f7b\u529f\uff0c\u4ece \u8fd9\u91cc\u8d70\uff0c\u5927\u7ea6\u518d\u8d70\u56db\u4e94\u5341\u91cc\u5c31\u662f\u5723\u5580\u4f26\u5c71\u5c71\u811a\uff0c\u90a3\u513f\u4fbf\u662f\u8349\u5730\u4e86\u3002\u56db\u4e94\u5341\u91cc\u8def\u5728\u4f60\u7528\u4e0d\u4e86\u4e00 \u4e2a\u65f6\u8fb0\u3002\u767b\u840d\u5ea6\u6c34\u7684\u529f\u592b\u8981\u9a6d\u6c14\u800c\u884c\uff0c\u6211\u6ca1\u6709\u8fd9\u4e2a\u672c\u9886\u53ef\u4ee5\u652f\u6301\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4f60\u5927\u7ea6\u53ef\u4ee5\uff0c\u4e0d \u59a8\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u7136\u5929\u8272\u5927\u53d8\uff0c\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u8fd9\u662f\u522e\u98ce\u7684\u9884\u5146\u3002\u201d\u679c\u7136\u6ca1\u6709\u591a\u4e45\uff0c\u72c2 \u98ce\u5927\u4f5c\uff0c\u9ec4\u6c99\u6f2b\u5929\uff0c\u5982\u70df\u5982\u96fe\uff0c\u773c\u524d\u7684\u666f\u7269\u4e00\u7247\u6a21\u7cca\u3002\u59ec\u6653\u98ce\u53eb\u9053\uff1a\u201c\u8d24\u4f84\u5c0f\u5fc3\uff0c\u8ddf\u7740\u6211 \u8d70\u3002\u201d \u3000\u3000\u98ce\u529b\u8d8a\u6765\u8d8a\u5927\uff0c\u547c\u547c\u8f70\u8f70\uff0c\u5927\u5730\u90fd\u4f3c\u4e4e\u8981\u9707\u52a8\u8d77\u6765\uff0c\u9976\u662f\u59ec\u3001\u6c5f\u4e8c\u4eba\u4f7f\u51fa\u7edd\u9876\u8f7b\u529f\uff0c\u6d51 \u8eab\u672c\u9886\uff0c\u4e5f\u88ab\u5439\u5f97\u4e1c\u6b6a\u897f\u5012\uff0c\u4e0a\u524d\u4e09\u6b65\uff0c\u540e\u9000\u4e24\u6b65\u3002\u6c5f\u6d77\u5929\u60f3\u8dd1\u4e0a\u53bb\u62c9\u7740\u59ec\u6653\u98ce\uff0c\u5f7c\u6b64\u6276 \u6301\uff0c\u540c\u6297\u98ce\u66b4\uff0c\u773c\u770b\u8fd8\u6709\u51e0\u6b65\u5c31\u53ef\u8d70\u5230\uff0c\u5ffd\u5730\u9762\u524d\u5192\u8d77\u4e00\u822c\u6c99\u67f1\uff0c\u4e0a\u51b2\u9704\u6c49\uff0c\u59ec\u6653\u98ce\u5927\u53eb \u9053\uff1a\u201c\u5feb\u80cc\u7740\u98ce\u5411\u8dd1\uff0c\u8fd9\u662f\u9f99\u5377\u98ce\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u80a1\u6c99\u67f1\u7a81\u7136\u6a2a\u5377\u8fc7\u6765\uff0c\u6c5f\u6d77\u5929\u7acb\u8db3\u4e0d \u7a33\uff0c\u6025\u5fd9\u548c\u8eab\u6251\u5730\uff0c\u80cc\u7740\u98ce\u5411\uff0c\u4f7f\u7528\u201c\u71d5\u9752\u5341\u516b\u7ffb\u201d\u7684\u529f\u592b\u76f4\u6eda\u51fa\u53bb\u3002\u9ec4\u6c99\u5982\u5929\u6cb3\u503e\u6cfb\uff0c\u5012 \u5728\u4ed6\u7684\u8eab\u4e0a\uff1a \u3000\u3000\u8fd9\u4e00\u77ac\u95f4\u6c5f\u6d77\u5929\u51e0\u4e4e\u611f\u5230\u7a92\u606f\uff0c\u5e78\u4e8f\u8fd9\u80a1\u9f99\u5377\u98ce\u6765\u5f97\u5feb\u53bb\u5f97\u4e5f\u5feb\uff0c\u6c5f\u6d77\u5929\u53c8\u662f\u80cc\u7740\u98ce \u5411\uff0c\u503e\u6cfb\u5728\u4ed6\u8eab\u4e0a\u7684\u9ec4\u6c99\uff0c\u521a\u521a\u628a\u4ed6\u7684\u8eab\u4f53\u76d6\u8fc7\uff0c\u8fd9\u80a1\u98ce\u4e5f\u5c31\u8fc7\u53bb\u4e86\u3002\u6c5f\u6d77\u5929\u5185\u529f\u6df1\u539a\uff0c\u95ed \u4f4f\u4e86\u6c14\u3002\u4e5f\u8fd8\u52c9\u5f3a\u53ef\u4ee5\u652f\u6301\uff0c\u5f85\u515a\u98ce\u529b\u7a0d\u5f31\uff0c\u4fbf\u94bb\u51fa\u6765\uff0c\u8fd9\u65f6\u4e00\u671b\u65e0\u9645\u7684\u6c99\u6f20\u4e0a\uff0c\u5c3d\u662f\u9ec4\u7070 \u8272\u7684\u6c99\u96fe\uff0c\u89c6\u529b\u672c\u6765\u5c31\u53d7\u5230\u4e86\u969c\u788d\uff0c\u6c5f\u6d77\u5929\u6e38\u76ee\u56db\u987e\uff0c\u7adf\u6ca1\u53d1\u73b0\u59ec\u6653\u98ce\u7684\u5f71\u5b50\uff0c\u4e0d\u7531\u5f97\u5927\u4e3a \u6050\u60e7\uff0c\u53e0\u58f0\u53eb\u9053\uff1a\u201c\u59ec\u4f2f\u4f2f\uff0c\u59ec\u4f2f\u4f2f\uff01\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5ffd\u542c\u5f97\u59ec\u6653\u98ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u9669\uff0c\u597d\u9669\uff0c\u9669\u4e9b\u5c31\u8981\u88ab\u6d3b\u57cb\u5566\uff01\u201d\u6c5f\u6d77\u5929\u5faa\u58f0 \u6ce8\u76ee\uff0c\u53ea\u89c1\u59ec\u6653\u98ce\u4ece\u4e00\u4e2a\u571f\u5806\u91cc\u722c\u51fa\u6765\uff0c\u539f\u6765\u4ed6\u4e0e\u6c5f\u6d77\u5929\u540c\u4e00\u906d\u9047\uff0c\u5374\u6bd4\u6c5f\u6d77\u5929\u66f4\u4e3a\u72fc\u72c8\u3002 \u3000\u3000\u90a3\u80a1\u9f99\u5377\u98ce\u867d\u7136\u6563\u4e86\uff0c\u4f46\u98d3\u98ce\u8fd8\u672a\u505c\u6b62\uff0c\u6f2b\u5929\u6c99\u96fe\uff0c\u50cf\u6570\u5341\u767e\u91cd\u539a\u539a\u7684\u9ec4\u5e55\uff0c\u906e\u5929\u853d \u5730\uff0c\u5341\u6b65\u4e4b\u5185\u7684\u666f\u7269\uff0c\u53ea\u89c1\u4e00\u56e2\u5f71\u5b50\uff0c\u8d70\u8def\u547c\u5438\u90fd\u5f88\u56f0\u96be\u3002 \u3000\u3000\u59ec\u6653\u98ce\u8d70\u5357\u95ef\u5317\uff0c\u4e00\u751f\u4e4b\u4e2d\uff0c\u4e0d\u77e5\u9047\u8fc7\u591a\u5c11\u5371\u9669\uff0c\u4f46\u5374\u4ece\u672a\u9047\u8fc7\u8fd9\u6837\u7684\u98d3\u98ce\uff0c\u800c\u4e14\u662f\u6c99 \u6f20\u4e0a\u7684\u98d3\u98ce\uff0c\u4ed6\u75db\u5b9a\u601d\u75db\uff0c\u4e0d\u7531\u5f97\u81ea\u5df1\u8d23\u5907\u81ea\u5df1\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u6211\u7684\u8fc7\u9519\uff0c\u6211\u672c\u6765\u5e94\u8be5\u96c7\u4e24\u5339 \u9a86\u9a7c\u7684\uff0c\u6211\u5374\u5c0f\u89d1\u4e86\u8fd9\u4e2a\u6c99\u6f20\uff0c\u4ee5\u4e3a\u6700\u591a\u4e24\u4e2a\u65f6\u8fb0\u5c31\u53ef\u7a7f\u8fc7\uff0c\u54ea\u77e5\u5929\u6709\u4e0d\u6d4b\u4e4b\u98ce\u4e91\uff0c\u5728\u56de\u7586 \u7684\u5927\u6208\u58c1\u6211\u5b89\u7136\u8fc7\u4e86\uff0c\u5728\u8fd9\u4e2a\u5c0f\u5c0f\u7684\u6c99\u6f20\u91cc\uff0c\u5374\u9047\u4e0a\u4e86\u98ce\u707e\u3002\u201d\u201c\u6211\u82e5\u4e27\u8eab\u6c99\u6f20\uff0c\u8fd9\u662f\u7531\u4e8e \u6211\u81ea\u5df1\u7684\u8fc7\u9519\uff0c\u6028\u4e0d\u5f97\u8c01\uff0c\u53ef\u662f\u62d6\u7d2f\u4e86\u6d77\u5929\u8d24\u4f84\uff0c\u6211\u5374\u600e\u5bf9\u5f97\u4f4f\u6c5f\u5357\u8d24\u5f1f\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u7684\u9a7c\u94c3\uff0c\u5939\u5728\u98ce\u58f0\u4e4b\u4e2d\uff0c\u81ea\u8fdc\u800c\u8fd1\uff0c\u59ec\u6653\u98ce\u5927\u559c\u9053\uff1a\u201c\u5929\u65e0 \u7edd\u4eba\u4e4b\u8def\uff0c\u8d24\u4f84\u5feb\u6765\uff01\u201d \u3000\u3000\u6c99\u96fe\u8ff7\u6f2b\u4e4b\u4e2d\uff0c\u5df2\u7ecf\u9690\u7ea6\u53ef\u4ee5\u770b\u89c1\u4e24\u5339\u9a86\u9a7c\u7684\u5f71\u5b50\uff0c\u59ec\u6653\u98ce\u63d0\u4e86\u4e00\u53e3\u6c14\uff0c\u4ee5\u201c\u9646\u5730\u98de \u817e\u201d\u7684\u8f7b\u529f\u75be\u8dd1\u8fc7\u53bb\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6551\u547d\uff0c\u6551\u547d\uff01\u201d \u3000\u3000\u90a3\u4e24\u5339\u9a86\u9a7c\u6765\u5230\u4e86\u4ed6\u7684\u9762\u524d\uff0c\u9a91\u5728\u9a7c\u80cc\u4e0a\u7684\u90a3\u4e24\u4e2a\u4eba\u8eab\u6750\u9ad8\u5927\uff0c\u9762\u8c8c\u770b\u4e0d\u771f\u5207\uff0c\u4f3c\u4e4e\u4e0d \u50cf\u6c49\u4eba\uff0c\u59ec\u6653\u98ce\u6015\u4ed6\u4eec\u542c\u4e0d\u61c2\u81ea\u5df1\u7684\u8bdd\uff0c\u6b63\u8981\u518d\u6253\u624b\u52bf\uff0c\u90a3\u4e24\u4e2a\u6c49\u5b50\u5ffd\u7136\u5404\u81ea\u4e3e\u8d77\u4e86\u4e00\u67c4\u5927 \u94c1\u9524\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u5c31\u5411\u59ec\u6653\u98ce\u6253\u4e0b\u6765\uff01 \u3000\u3000\u8fd9\u771f\u662f\u7edd\u5bf9\u610f\u60f3\u4e0d\u5230\u7684\u4e8b\u60c5\uff0c\u59ec\u6653\u98ce\u5927\u60ca\u4e4b\u4e0b\uff0c\u4fa7\u8eab\u4e00\u95ea\uff0c\u95ea\u8fc7\u4e86\u5de6\u8fb9\u7684\u4e00\u9524\uff0c\u4f46\u4ed6\u5fc3 \u614c\u610f\u4e71\u3002\u672c\u6765\u5e94\u8be5\u5411\u540e\u5012\u7eb5\uff0c\u624d\u53ef\u907f\u5f00\u53cc\u9524\u7684\uff0c\u4ed6\u5374\u95ea\u5411\u53f3\u65b9\uff0c\u5e9c\u8fb9\u7684\u4e00\u9524\u6b63\u597d\u671d\u7740\u4ed6\u7684\u540e \u8111\u51fb\u4e0b\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u56db\u6e85\uff0c\u94c1\u9524\u5e76\u6ca1\u6709\u6253\u4e2d\u59ec\u6653\u98ce\uff0c\u5374\u539f\u6765\u662f\u6c5f\u6d77\u5929\u53ca\u65f6\u8d76\u81f3\uff0c \u4e00\u5251\u524a\u53bb\uff0c\u5c06\u90a3\u67c4\u94c1\u9524\u524a\u53bb\u4e86\u4e00\u5757\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u6c49\u5b50\u6599\u4e0d\u5230\u6c5f\u6d77\u5929\u7684\u5b9d\u5251\u5982\u6b64\u5389\u5bb3\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u4e0d\u6562\u518d\u6253\uff0c\u6025\u5fd9\u4e00\u62cd\u9a7c\u5cf0\uff0c\u50ac\u9a86 \u9a7c\u75be\u8dd1\uff0c\u9a86\u9a7c\u867d\u662f\u5e9e\u7136\u5927\u7269\uff0c\u4f46\u5728\u6c99\u6f20\u4e0a\u8dd1\u8d77\u6765\uff0c\u5374\u6bd4\u9a8f\u9a6c\u8fd8\u5feb\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u5c31\u6ca1\u5165\u9ec4\u6c99\u6f20 \u6f20\u4e4b\u4e2d\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u53ea\u89c1\u4e24\u4e2a\u9ed1\u70b9\u3002 \u3000\u3000\u59ec\u6653\u98ce\u5927\u6012\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u4e0d\u5982\u63f4\u624b\u4e5f\u8fd8\u7f62\u4e86\uff0c\u5374\u53cd\u800c\u5bb3\u6211\uff01\u597d\u5440\u3001\u4f60\u65e2\u4e0d\u4ec1\uff0c\u6211 \u4e5f\u4e0d\u4e49\uff01\u201d\u8eab\u5f62\u4e00\u8d77\uff0c\u5c55\u5f00\u4e86\u7edd\u9876\u8f7b\u529f\uff0c\u5c31\u5728\u6c99\u6f20\u4e0a\u8ffd\u90a3\u4e24\u5339\u9a86\u9a7c\u3002 \u3000\u3000\u8981\u77e5\u59ec\u6653\u98ce\u81ea\u5c0a\u5fc3\u91cd\uff0c\u5e73\u751f\u4ece\u6ca1\u6709\u4f4e\u58f0\u4e0b\u6c14\u7684\u6c42\u8fc7\u4eba\uff0c\u8fd9\u6b21\u662f\u4e3a\u4e86\u8981\u6551\u6c5f\u6d77\u5929\u8131\u9669\uff0c\u8fd9 \u624d\u5411\u90a3\u4e24\u4e2a\u4eba\u53eb\u6551\u547d\u7684\uff0c\u5c82\u77e5\u6240\u6307\u671b\u7684\u6551\u547d\u6069\u4eba\uff0c\u53cd\u800c\u6210\u4e86\u50ac\u547d\u5224\u5b98\uff0c\u9669\u4e9b\u8981\u4e86\u4ed6\u7684\u6027\u547d\uff0c \u4ed6\u7109\u80fd\u4e0d\u8d8a\u60f3\u8d8a\u6c14\u3002\u56e0\u6b64\u4ed6\u6253\u7b97\u52ab\u90a3\u4e24\u5339\u9a86\u9a7c\uff0c\u5c06\u90a3\u4e24\u4e2a\u6c49\u5b50\u6454\u4e0b\u6c99\u6f20\u3002\u8ba9\u4ed6\u4eec\u81ea\u751f\u81ea\u706d\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u5730\u7eaf\u539a\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u4eba\u5bb6\u4e0d\u80af\u6551\u52a9\uff0c\u90a3\u4e5f\u4e0d\u5fc5\u52c9\u5f3a\u4eba\u5bb6\u3002\u59ec\u6015\u4f2f\uff0c\u56de\u6765\u5427\uff0c \u54b1\u4eec\u53e6\u60f3\u529e\u6cd5\u4eba\u3002\u201d\u59ec\u6653\u98ce\u54ea\u91cc\u80af\u4f9d\uff0c\u8dd1\u5f97\u66f4\u5feb\u4e86\u3002\u6c5f\u6d77\u5929\u4e00\u6765\u6015\u4ed6\u6709\u5931\uff1b\u4e8c\u6765\u4e5f\u6015\u4ed6\u4e00\u65f6 \u52a8\u6012\uff0c\u5f53\u771f\u5c31\u5c06\u90a3\u4e24\u4e2a\u4eba\u6740\u6389\uff0c\u56e0\u6b64\u4e5f\u53ea\u597d\u65bd\u5c55\u8f7b\u529f\uff0c\u8dd1\u53bb\u8ffd\u4ed6\u3002 \u3000\u3000\u5ffd\u5730\u4e00\u80a1\u65cb\u98ce\u522e\u6765\uff0c\u6c5f\u6d77\u5929\u6beb\u65e0\u7ecf\u9a8c\uff0c\u4e0d\u8bc6\u8fa8\u522b\u98ce\u5411\uff0c\u6b63\u5de7\u649e\u8fdb\u98ce\u529b\u7684\u4e2d\u5fc3\uff0c\u9976\u662f\u4ed6\u7528 \u5c3d\u4e86\u6d51\u8eab\u672c\u9886\u8c03\u4e5f\u4e0d\u80fd\u518d\u5411\u524d\u884c\u8fdb\u4e00\u6b65\uff0c\u88ab\u90a3\u80a1\u65cb\u98ce\u5439\u5f97\u5728\u539f\u5730\u4e0a\u53e3\u56e2\u6253\u8f6c\uff0c\u5934\u6655\u773c\u82b1\uff0c\u6c5f \u6d77\u5929\u6697\u53eb\u201c\u4e0d\u5999\u201d\uff0c\u4e00\u65f6\u65e0\u6687\u601d\u7d22\uff0c\u7adf\u4f7f\u51fa\u4e86\u201c\u5343\u65a4\u5760\u201d\u7684\u91cd\u8eab\u6cd5\u6765\uff0c\u60f3\u7a33\u4f4f\u8eab\u5f62\u3002\u201c\u5343\u65a4 \u5760\u201d\u7684\u91cd\u8eab\u6cd5\u53ea\u5b9c\u5728\u5e73\u5730\u4e0a\u4f7f\u7528\uff0c\u5728\u6c99\u6f20\u91cc\u54ea\u53ef\u65bd\u4e3a\uff1f\u4e0d\u7528\u5012\u8fd8\u7f62\u4e86\uff0c\u4e00\u7528\u51fa\u6765\uff0c\u767b\u65f6\u9677\u8fdb \u4e86\u6d6e\u6c99\u4e4b\u4e2d\u3002 \u3000\u3000\u6b66\u529f\u518d\u5f3a\uff0c\u9677\u8fdb\u4e86\u6d6e\u6c99\u4e5f\u662f\u6beb\u65e0\u529e\u6cd5\u3002\u56e0\u4e3a\u6d6e\u6c99\u4e58\u4e0d\u7740\u4e00\u4e2a\u4eba\u7684\u4f53\u91cd\uff0c\u5018\u8981\u7528\u529b\u8df3\u8d77\uff0c \u90a3\u53ea\u6709\u8d8a\u9677\u8d8a\u6df1\uff0c\u5fc5\u987b\u8981\u6709\u5916\u529b\u5c06\u4ed6\u62c9\u8d77\u6765\u624d\u884c\u3002\u8fd9\u9053\u7406\u5c31\u7b49\u4e8e\u5929\u4e0b\u6700\u5927\u529b\u6c14\u7684\u4eba\u4e5f\u4e3e\u4e0d\u8d77 \u81ea\u5df1\u7684\u8eab\u4f53\u4e00\u6837\uff0c\u56e0\u4e3a\u65e0\u6240\u51ed\u501f\uff0c\u6c14\u529b\u5c31\u4f7f\u4e0d\u51fa\u6765\u3002\u5e78\u800c\u6c5f\u6d77\u5929\u8fd8\u7b97\u673a\u4f36\uff0c\u4e00\u53d1\u89c9\u8df3\u4e0d\u8d77 \u6765\uff0c\u5c31\u5f20\u5f00\u4e24\u81c2\uff0c\u6491\u7740\u6c99\u58c1\uff0c\u867d\u7136\u4ecd\u662f\u4e0b\u9677\uff0c\u4f46\u4e0b\u9677\u7684\u901f\u5ea6\u5df2\u662f\u5927\u51cf\u3002 \u3000\u3000\u518d\u8bf4\u59ec\u6653\u98ce\u53bb\u8ffd\u90a3\u4e24\u5339\u9a86\u9a7c\uff0c\u4ed6\u8bc6\u5f97\u8fa8\u522b\u98ce\u5411\uff0c\u53ea\u8981\u4e0d\u662f\u78b0\u7740\u9f99\u5377\u98ce\uff0c\u4fbf\u4e0d\u81f3\u4e8e\u6709\u5927\u5371 \u9669\uff0c\u4ed6\u4f7f\u51fa\u4e86\u7edd\u9876\u8f7b\u529f\uff0c\u5f53\u771f\u662f\u6709\u5982\u98ce\u9a70\u7535\u9010\uff0c\u8ffd\u4e86\u4e00\u4f1a\uff0c\u4e0e\u90a3\u4e24\u5339\u9a86\u9a7c\u7684\u8ddd\u79bb\u8d8a\u6765\u8d8a\u8fd1\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u6c49\u5b50\u5ffd\u5730\u4e00\u58f0\u53f1\u54a4\uff0c\u628a\u624b\u4e00\u626c\uff0c\u98de\u51fa\u4e00\u84ec\u6697\u5668\u3002\u59ec\u6653\u98ce\u5927\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u7684\u6697\u5668\u529f \u592b\u8fd8\u5f97\u518d\u7ec3\u5341\u5e74\uff01\u201d\u539f\u6765\u4ed6\u4eec\u6240\u53d1\u7684\u6697\u5668\u51c6\u5934\u751a\u5dee\uff0c\u59ec\u6653\u98ce\u4e0d\u7528\u8d39\u4ec0\u4e48\u6c14\u529b\u5c31\u907f\u5f00\u4e86\u3002\u4f46\u5728 \u6c99\u970e\u8ff7\u6f2b\u4e4b\u4e2d\u5374\u770b\u4e0d\u771f\u5207\u90a3\u4e9b\u6697\u5668\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u59ec\u6653\u98ce\u6280\u9ad8\u80c6\u5927\uff0c\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u4ecd\u7136\u594b\u529b \u8feb\u8d76\u3002 \u3000\u3000\u4e0d\u6599\u4e00\u811a\u8e0f\u4e0b\uff0c\u75db\u5982\u5200\u63d2\uff0c\u539f\u6765\u90a3\u4e24\u4e2a\u6c49\u5b50\u6492\u51fa\u7684\u6697\u5668\u4e43\u662f\u4e24\u5934\u950b\u5229\u7684\u5c16\u9489\uff0c\u6709\u767e\u6570\u5341 \u679a\u4e4b\u591a\uff0c\u6240\u4ee5\u6839\u672c\u65e0\u987b\u8bb2\u7a76\u4ec0\u4e48\u51c6\u5934\u3002\u82e5\u5728\u5e73\u65f6\uff0c\u4ee5\u59ec\u6653\u98ce\u7684\u65e5\u529b\u548c\u8eab\u6cd5\uff0c\u81ea\u662f\u77e5\u6240\u8d8b\u907f\uff0c \u51b3\u65e0\u4e0a\u5f53\u4e4b\u7406\uff0c\u4f46\u5728\u8fd9\u6c99\u96fe\u8ff7\u6f2b\uff0c\u65e5\u6708\u65e0\u5149\u7684\u60c5\u51b5\u4e0b\uff0c\u52a0\u4ee5\u4ed6\u4e00\u5ff5\u8f7b\u654c\uff0c\u7ed3\u679c\u4fbf\u96be\u514d\u5927\u5927\u5403 \u4e8f\u3002 \u3000\u3000\u59ec\u6653\u98ce\u88ab\u5c16\u9489\u63d2\u5165\u8db3\u8ddf\u3001\u75db\u5f7b\u5fc3\u80ba\uff0c\u9669\u4e9b\u8dcc\u5012\uff0c\u6c14\u5f97\u7834\u53e3\u5927\u9a82\uff0c\u90a3\u4e24\u4e2a\u6c49\u5b50\u54c8\u54c8\u5927\u7b11\uff0c \u7b11\u5f97\u6709\u5982\u67ad\u9e1f\u591c\u9e23\uff0c\u96be\u542c\u4e4b\u6781\u3002\u5c31\u5728\u7b11\u58f0\u4e2d\u98de\u51fa\u4e86\u4e24\u6761\u94c1\u6293\uff0c\u5411\u59ec\u6653\u98ce\u6293\u6765\u3002 \u3000\u3000\u59ec\u6653\u98ce\u54ac\u7d27\u7259\u9f88\uff0c\u53cc\u6307\u634f\u7740\u94c1\u9489\uff0c\u7528\u529b\u4e00\u6280\uff0c\u9c9c\u8840\u968f\u7740\u6c69\u6c69\u6d41\u51fa\uff0c\u4ed6\u811a\u8ddf\u53d7\u4f24\uff0c\u8f7b\u529f\u6253 \u4e86\u6298\u6263\uff0c\u90a3\u4e24\u6761\u94c1\u6293\uff0c\u4e00\u5de6\u4e00\u53f3\u3002\u604d\u5982\u53cc\u9f99\u63a2\u722a\uff0c\u4ed6\u8fd8\u672a\u8dd1\u51fa\u51e0\u6b65\uff0c\u53ea\u542c\u5f97\u55e4\u55e4\u58f0\u54cd\uff0c\u4e24\u6761 \u519c\u8896\uff0c\u90fd\u5df2\u7ed9\u94c1\u6293\u6495\u7834\u3002 \u3000\u3000\u5c31\u5728\u94c1\u6293\u89e6\u7740\u4ed6\u7435\u7436\u9aa8\u7684\u65f6\u5019\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u6c99\u96fe\u4e2d\u5ffd\u89c1\u767d\u5149\u4e00\u9053\uff0c\u7834\u7a7a\u98de\u6765\uff0c\u539f \u6765\u662f\u4e00\u67c4\u98de\u5200\uff0c\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u5c31\u628a\u9093\u6761\u94c1\u6293\u5f53\u4e2d\u65a9\u65ad\u3002\u59ec\u6653\u98ce\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u4e2a\uff0c\u53e6\u4e00\u6761\u94c1\u6293 \u6293\u4e86\u4e2a\u7a7a\uff0c\u4e5f\u7f29\u56de\u53bb\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u53c8\u4e00\u5339\u9a86\u9a7c\u5927\u6b65\u8dd1\u6765\uff0c\u9a7c\u80cc\u4e0a\u662f\u4e2a\u9ed1\u8863\u6c49\u5b50\uff0c\u7528\u85cf\u8bed\u5927\u559d\u906d\uff1a\u201c\u8c01\u6562\u5728\u6b64\u4e3a\u975e\u4f5c \u6b79\uff0c\u4f11\u602a\u6211\u624b\u4e0b\u65e0\u60c5\uff01\u683c\u9c81\u5df4\u6d2a\u514b\u4ea5\u90a3\u9c81\u5947\uff01\u201d\u59ec\u9662\u6653\u8db3\u8ff9\u904d\u5929\u4e0b\uff0c\u61c2\u5f97\u5404\u5730\u65b9\u8a00\uff0c\u90a3\u4e24\u53e5 \u85cf\u8bed\u4ed6\u4e5f\u542c\u61c2\u4e86\uff0c\u4f46\u540e\u9762\u90a3\u4e00\u53e5\u4ed6\u5374\u4e0d\u61c2\u662f\u4ec0\u4e48\u610f\u601d\u3002 \u3000\u3000\u524d\u5934\u90a3\u4e24\u4e2a\u6c49\u5b50\u4f3c\u4e4e\u9887\u4e3a\u5fcc\u60ee\uff0c\u4e00\u58f0\u4e0d\u53d1\uff0c\u50ac\u8d76\u9a86\u9a7c\u4fbf\u8d70\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u4fbf\u5728\u6c99\u96fe\u4e4b\u4e2d\u6ca1 \u4e86\u8e2a\u8ff9\uff0c\u540e\u9762\u90a3\u5339\u9a86\u9a7c\uff0c\u4e0d\u4e45\u4e5f\u5c31\u8d76\u5230\u4e86\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u6c49\u5b50\u9053\uff1a\u201c\u4f60\u722c\u5f97\u4e0a\u6765\u5417\uff1f\u201d\u59ec\u6653\u98ce\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e2a\u4eba\u5927\u7ea6\u4e0d\u4f1a\u518d\u5bb3\u6211\u4e86\u5427\uff1f\u201d\u5f53 \u4e0b\uff0c\u5fcd\u7740\u75bc\u75db\uff0c\u98de\u8eab\u4e00\u8dc3\uff0c\u8df3\u4e0a\u9a7c\u80cc\u3002\u4ed6\u53d7\u8fc7\u4e00\u6b21\u6559\u8bad\uff0c\u8fd9\u56de\u7279\u522b\u5c0f\u5fc3\uff0c\u5750\u5728\u90a3\u4e2a\u4eba\u7684\u80cc \u540e\uff0c\u8fd9\u6837\uff0c\u5c31\u53ea\u6709\u4ed6\u53ef\u4ee5\u6697\u7b97\u524d\u9762\u7684\u4eba\uff0c\u522b\u4eba\u662f\u51b3\u8ba1\u6697\u7b97\u4e0d\u5230\u4ed6\u4e86\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u6c49\u5b50\u7adf\u4f3c\u5168\u65e0\u673a\u5fc3\uff0c\u65e2\u4e0d\u95ee\u4ed6\uff0c\u4e5f\u4e0d\u56de\u5934\u671b\u4ed6\uff0c\u4fbf\u5f84\u81ea\u8f6c\u4e86\u4e00\u4e2a\u65b9\u5411\u8d70\u53bb\uff0c\u59ec\u6653 \u98ce\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4e00\u4e2a\u4f84\u513f\u2026\u2026\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u6c5f\u6d77\u5929\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u59ec\u6015\u4f2f\uff0c\u59ec\u4f2f \u4f2f\u2026\u2026\u201d \u3000\u3000\u90a3\u9ed1\u8863\u6c49\u5b50\u53eb\u9053\uff1a\u201c\u6293\u4f4f\uff01\u201d\u629b\u51fa\u4e00\u6761\u8f6f\u7d22\uff0c\u6c5f\u6d77\u5929\u8fd9\u65f6\u5168\u8eab\u90fd\u5df2\u9677\u8fdb\u6c99\u4e2d\uff0c\u53ea\u9732\u51fa\u5934 \u90e8\uff0c\u8f6f\u7d22\u629b\u5230\u4ed6\u7684\u9762\u524d\uff0c\u4ed6\u53ea\u80fd\u7528\u7259\u9f7f\u54ac\u4f4f\u3002\u90a3\u6c49\u5b50\u559d\u4e00\u58f0\u201c\u8d77\uff01\u201d\u5c06\u6c5f\u6d77\u5929\u626f\u4e86\u8d77\u6765\uff0c\u8f6f \u7d22\u4e00\u6325\uff0c\u6c5f\u6d77\u5929\u8eab\u5b50\u51cc\u7a7a\u98de\u8d77\uff0c\u5728\u534a\u7a7a\u4e2d\u7ffb\u8fc7\u8eab\u6765\uff0c\u8f7b\u8f7b\u5de7\u5de7\u7684\u4fbf\u843d\u5728\u9a7c\u80cc\u4e0a\u3002\u8fd9\u4e00\u624b\u53cc\u65b9 \u90fd\u9732\u51fa\u4e86\u9ad8\u660e\u7684\u6b66\u529f\uff0c\u59ec\u6653\u98ce\u53c8\u60ca\u53c8\u559c\u53c8\u662f\u4f69\u670d\u3002 \u3000\u3000\u559c\u8005\u662f\u6c5f\u6d77\u5929\u5e76\u672a\u53d7\u4f24\uff1b\u60ca\u8005\u662f\u6b64\u4eba\u6b66\u529f\u8d85\u5353\uff0c\u4e0d\u77e5\u662f\u4f55\u8def\u9053\uff1f\u4f46\u4ed6\u65e2\u7136\u51fa\u624b\u6551\u4eba\uff0c\u60f3 \u6765\u5f53\u65e0\u6076\u610f\uff0c\u56e0\u6b64\u7d27\u5f20\u7684\u5fc3\u60c5\u4e5f\u5c31\u653e\u677e\u4e86\u3002 \u3000\u3000\u6c99\u571f\u98de\u626c\uff0c\u98ce\u58f0\u547c\u5578\u4e4b\u4e2d\u4e0d\u4fbf\u4ea4\u8c08\uff0c\u59ec\u3001\u6c5f\u4e8c\u4eba\u53ea\u5f97\u4e00\u58f0\u201c\u591a\u8c22\u201d\uff0c\u90a3\u9ed1\u8863\u6c49\u5b50\u4e5f\u5e94\u4e86 \u4e00\u58f0\uff0c\u4fbf\u65e0\u8a00\u8bed\u3002\u59ec\u6653\u98ce\u5fc3\u60f3\uff1a\u201c\u4e14\u5f85\u8131\u9669\u4e4b\u540e\uff0c\u518d\u95ee\u4ed6\u7684\u59d3\u540d\u6765\u5386\uff0c\u5f90\u56fe\u62a5\u7b54\u5427\u3002\u201d\u4ed6\u8eab \u4e0a\u8fd8\u85cf\u6709\u5c0f\u534a\u74f6\u201c\u8865\u5929\u818f\u201d\uff0c\u90a3\u662f\u4ed6\u4ece\u5d06\u5cd2\u6d3e\u540d\u5bbf\u9f50\u5929\u4e50\u90a3\u513f\u5077\u6765\u7684\uff0c\u529f\u80fd\u63a5\u9aa8\u6b62\u8840\uff0c\u7075\u6548 \u65e0\u6bd4\uff0c\u4ed6\u6311\u4e86\u4e00\u70b9\u8865\u5929\u818f\uff0c\u6577\u4e0a\u4f24\u53e3\uff0c\u75db\u695a\u7acb\u51cf\u3002 \u3000\u3000\u9a86\u9a7c\u4e4b\u529b\uff0c\u53ef\u8d1f\u5343\u65a4\uff0c\u867d\u7136\u662f\u9a7c\u4e86\u4e09\u4e2a\u4eba\uff0c\u5728\u6c99\u6f20\u4e0a\u4ecd\u7136\u662f\u5065\u6b65\u5982\u98de\uff0c\u8d70\u4e86\u5927\u7ea6\u4e00\u4e2a\u65f6 \u8fb0\uff0c\u98ce\u5df2\u9759\u6b62\uff0c\u6f2b\u5929\u7684\u9ec4\u6c99\u4e5f\u6e10\u6e10\u6f84\u6e05\uff0c\u518d\u8fc7\u4e00\u4f1a\uff0c\u53ea\u89c1\u524d\u9762\u5df2\u662f\u4e00\u7247\u8349\u5730\uff0c\u8349\u5730\u7684\u90a3\u4e00 \u8fb9\uff0c\u5df2\u662f\u5723\u5580\u4f26\u5c71\u811a\u4e0b\u4e86\u3002 \u3000\u3000\u9a86\u9a7c\u505c\u4e86\u4e0b\u6765\uff0c\u90a3\u9ed1\u8863\u6c49\u5b50\u56de\u8fc7\u5934\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u53ef\u518d\u5411\u524d\u884c\u4e86\uff0c\u72c2\u98ce\u5df2\u8fc7\uff0c\u770b\u8fd9 \u5929\u8272\uff0c\u660e\u5929\u5929\u6c14\u5f88\u597d\u3002\u4f60\u4eec\u662f\u6765\u6b64\u5bfb\u8bbf\u670b\u53cb\u7684\u5427\uff1f\u5018\u82e5\u89c1\u7740\u53cb\u4eba\uff0c\u6700\u597d\u6717\u5929\u4fbf\u7acb\u5373\u56de\u53bb\uff0c\u4e0d \u8981\u8d8a\u8fc7\u8fd9\u5723\u5580\u4f26\u5c71\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u65f6\u98ce\u6c99\u9759\u6b62\uff0c\u5929\u8272\u5df2\u6062\u590d\u6674\u660e\uff0c\u53ea\u89c1\u90a3\u9ed1\u8863\u6c49\u5b50\u7684\u9ed1\u8272\u7f69\u888d\u4e4b\u5185\uff0c \u9732\u51fa\u5587\u561b\u88c5\u67ec\u7684\u767d\u8272\u98d8\u5e26\uff0c\u59ec\u6653\u98ce\u4e00\u60ca\u4e4b\u4e0b\uff0c\u8fde\u5fd9\u62c9\u4e86\u6c5f\u6d77\u5929\u8df3\u4e0b\u9a86\u9a7c\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u9102\u514b \u6c81\u5bab\u7684\u5587\u561b\u5417\uff1f\u4f60\u600e\u4e48\u77e5\u9053\u6211\u6765\u6b64\u8bbf\u5bfb\u670b\u53cb\uff1f\u201d \u3000\u3000\u90a3\u5587\u561b\u7b11\u9053\uff1a\u201c\u6211\u8ba4\u5f97\u4f60\u662f\u795e\u5077\u59ec\u6653\u98ce\uff0c\u53ef\u60dc\u4f60\u6ca1\u6709\u548c\u91d1\u5927\u4fa0\u540c\u6765\uff0c\u6240\u4ee5\u53ea\u597d\u8bf7\u4f60\u56de\u53bb \u4e86\u3002\u201d \u3000\u3000\u503e\u6653\u98ce\u9053\uff1a\u201c\u4f60\u4f20\u7684\u53ef\u662f\u6cd5\u738b\u7684\u610f\u65e8\uff1f\u201d \u3000\u3000\u90a3\u5587\u561b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u548c\u51e0\u4f4d\u540c\u95e8\uff0c\u5949\u4e86\u5e08\u5c0a\u4e4b\u547d\uff0c\u8fd9\u4e2a\u6708\u6765\uff0c\u6bcf\u5929\u90fd\u8f6e\u6d41\u5728\u6b64\u5de1\u67e5\uff0c \u4e3a\u7684\u5c31\u662f\u6015\u4f60\u4e0d\u77e5\u597d\u6b79\uff0c\u5fd8\u8bb0\u4e86\u6211\u4eec\u5e08\u5c0a\u5f53\u5e74\u7684\u5429\u5490\u3002\u90a3\u662f\u6211\u4eec\u5e08\u5c0a\u8bf7\u6c5f\u5357\u5148\u751f\u8f6c\u544a\u4f60\u7684\uff0c \u96be\u9053\u4ed6\u6ca1\u6709\u544a\u8bc9\u4f60\u5417\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u7591\u4e91\u5927\u8d77\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7684\u6551\u547d\u4e4b\u6069\u2026\u2026\u201d\u90a3\u5587\u561b\u63d2\u53e3\u9053\uff1a\u201c\u4e0d\u8981 \u591a\u8c22\u6211\uff0c\u4f60\u5e94\u8be5\u591a\u8c22\u6211\u7684\u5e08\u5c0a\uff0c\u4e0d\u662f\u4ed6\u8001\u4eba\u5bb6\u5631\u5490\u6211\u4eec\u5728\u6b64\u5de1\u67e5\uff0c\u6211\u5c31\u4e0d\u4f1a\u9047\u4e0a\u4f60\uff0c\u4e5f\u4e0d\u4f1a \u6551\u4f60\u7684\u547d\u4e86\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u7eed\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u8bf7\u4f60\u4ee3\u6211\u5411\u4f60\u7684\u5e08\u5c0a\u9053\u8c22\uff0c\u540c\u65f6\u8bf7\u4f60\u4ee3\u4e3a\u7980\u544a\uff0c\u8bf4\u6211\u59ec\u6653\u98ce\u5e76 \u6ca1\u6709\u5fd8\u8bb0\u4ed6\u7684\u5429\u5490\uff0c\u4ed6\u4e0d\u8bb8\u6211\u8e0f\u8fdb\u9102\u514b\u6c81\u5bab\u5468\u56f4\u767e\u91cc\u4e4b\u5185\uff0c\u6211\u4e5f\u77e5\u9053\uff0c\u8fc7\u4e86\u5723\u5580\u4f26\u5c71\uff0c\u5c31\u662f \u8fdd\u80cc\u4ed6\u7684\u7981\u4ee4\u4e86\u3002\u201d\u90a3\u5587\u561b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u65e2\u7136\u77e5\u9053\uff0c\u5c31\u4e0d\u5b9c\u518d\u5411\u524d\u884c\u4e86\u3002\u201d\u59ec\u6653\u98ce\u9053\uff1a \u201c\u53ef\u662f\u6211\u5b9e\u5728\u4e0d\u660e\u767d\u6cd5\u738b\u7684\u610f\u601d\uff0c\u5018\u82e5\u4ed6\u8ba4\u5b9a\u6211\u662f\u4e2a\u574f\u4eba\uff0c\u4f60\u4eca\u5929\u5c31\u4e0d\u5e94\u6551\u6211\uff1b\u5018\u82e5\u662f\u53e6\u6709 \u539f\u56e0\uff0c\u6211\u5c31\u975e\u5f97\u95ee\u4e2a\u660e\u767d\u4e0d\u53ef\uff01\u201d \u3000\u3000\u90a3\u5587\u561b\u9053\uff1a\u201c\u672c\u95e8\u7684\u4e8b\u60c5\uff0c\u59ec\u5148\u751f\u4f60\u662f\u5916\u4eba\uff0c\u4f3c\u4e4e\u4e0d\u5b9c\u591a\u95ee\uff0c\u6211\u4eec\u7684\u5e08\u5c0a\u4e5f\u4e0d\u4f1a\u63a5\u89c1 \u4f60\u3002\u800c\u4e14\uff0c\u4f60\u518d\u5411\u524d\u884c\uff0c\u4fbf\u662f\u8fdd\u80cc\u4e86\u6211\u4eec\u5e08\u5c0a\u7684\u7981\u4ee4\uff0c\u672c\u5bfa\u50e7\u4f17\uff0c\u53ea\u6015\u4e5f\u4e0d\u4f1a\u5bf9\u4f60\u5ba2\u6c14\u4e86\u3002 \u6211\u8a00\u5c3d\u4e8e\u6b64\uff0c\u4f60\u5018\u82e5\u8981\u4efb\u6027\u800c\u4e3a\uff0c\u90a3\u4e5f\u53ea\u597d\u7531\u4f60\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u5f88\u4e0d\u670d\u6c14\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u5e08\u5c0a\u7684\u7981\u4ee4\uff0c\u4e0e\u6211\u6709\u5173\uff0c\u600e\u80fd\u63a8\u8bf4\u662f\u672c\u95e8\u7684\u4e8b\u60c5\u4e0d\u8bb8\u6211 \u95ee\uff0c\u8fd9\u4e0d\u662f\u6709\u70b9\u5f3a\u8f9e\u593a\u7406\u5417\uff1f\u201d\u4f46\u662f\u8fd9\u5587\u561b\u7684\u795e\u60c5\u5df2\u4f3c\u751a\u4e3a\u7740\u607c\uff0c\u5fc3\u60f3\uff0c\u4ed6\u5230\u5e95\u662f\u81ea\u5df1\u7684\u6069 \u4eba\uff0c\u4e5f\u5c31\u53ea\u597d\u5fcd\u4f4f\u4e86\u3002 \u3000\u3000\u90a3\u5587\u561b\u6b63\u4e0e\u59ec\u6653\u98ce\u5206\u624b\uff0c\u5ffd\u89c1\u6709\u51e0\u5339\u9a6c\u75be\u9a70\u800c\u6765\uff0c\u90a3\u5587\u561b\u795e\u8272\u5927\u53d8\u3002\u59ec\u6653\u98ce\u4e00\u770b\uff0c\u90a3\u51e0 \u4e2a\u9a91\u58eb\u90fd\u662f\u7a7f\u7740\u767d\u888d\u7684\u5587\u561b\uff0c\u59ec\u6653\u98ce\u8ba4\u5f97\u5176\u4e2d\u4e00\u4eba\u4e43\u662f\u767d\u6559\u6cd5\u738b\u5ea7\u4e0b\u56db\u5927\u62a4\u6cd5\u5f1f\u5b50\u4e4b\u4e00\u3002 \u3000\u3000\u59ec\u6653\u98ce\u6b63\u81ea\u5947\u602a\uff1a\u201c\u8fd9\u5587\u561b\u89c1\u4e86\u672c\u5bfa\u7684\u618e\u4fa3\uff0c\u5374\u4e3a\u4f55\u7adf\u6709\u60ca\u60e7\u7684\u795e\u60c5\uff1f\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u90a3 \u51e0\u9a91\u5065\u9a6c\u4e98\u5982\u98de\u800c\u81f3\uff0c\u5c06\u90a3\u4e2a\u52a9\u59ec\u6653\u98ce\u8131\u9669\u7684\u5587\u561b\u56e2\u56e2\u56f4\u4f4f\u4e86\u3002 \u3000\u3000\u90a3\u62a4\u6cd5\u5f1f\u5b50\u9762\u631f\u5bd2\u971c\uff0c\u51b7\u51b7\u7684\u5411\u90a3\u5587\u561b\u95ee\u9053\uff1a\u201c\u54c8\u51e1\u63d0\uff0c\u4f60\u5728\u505a\u4ec0\u4e48\uff1f\u201d\u90a3\u5587\u561b\u9053\uff1a \u201c\u6211\u5728\u5c71\u4e0a\u6765\u836f\uff0c\u9002\u9047\u5927\u98ce\uff0c\u542c\u5f97\u6c99\u6f20\u4e2d\u6709\u4eba\u547c\u558a\u6551\u547d\uff0c\u662f\u4ee5\u6765\u6b64\u3002\u201d\u59ec\u6653\u98ce\u4e0d\u7981\u53c8\u662f\u5927\u529b \u5947\u602a\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u662f\u5949\u4e86\u6559\u4e3b\u4e4b\u547d\u6765\u7684\uff0c\u5374\u4e3a\u4f55\u8981\u5411\u81ea\u5df1\u4eba\u6492\u8d77\u8c0e\u6765\uff1f\u201d \u3000\u3000\u90a3\u62a4\u6cd5\u5f1f\u5b50\u5411\u59ec\u6653\u98ce\u626b\u4e86\u4e00\u670d\uff0c\u53c8\u5411\u90a3\u5587\u561b\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4eba\u662f\u4f60\u6551\u7684\u5417\uff1f\u201d\u90a3\u5587\u561b\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u662f\u6211\u6551\u7684\u3002\u201d \u3000\u3000\u90a3\u62a4\u6cd5\u5f1f\u5b50\u5927\u6012\u559d\u9053\uff1a\u201c\u54c8\u51e1\u63d0\uff0c\u4f60\u5e72\u7684\u597d\u4e8b\uff0c\u6211\u7f5a\u4f60\u81ea\u6253\u56db\u5341\u4e0b\u8033\u5149\uff0c\u7136\u540e\u628a\u8888\u88df\u8131 \u4e0b\uff0c\u968f\u6211\u56de\u53bb\u9886\u7f6a\u3002\u201d\u8888\u88df\u662f\u5587\u561b\u7684\u6cd5\u8863\uff0c\u90a3\u62a4\u6cd5\u5f1f\u5b50\u8981\u4ed6\u628a\u8888\u88df\u8131\u4e0b\uff0c\u5c31\u7b49\u4e8e\u662f\u9769\u9664\u4ed6\u7684 \u5587\u561b\u8eab\u4efd\uff0c\u5ba3\u5224\u4ed6\u662f\u672c\u6559\u7684\u53db\u5f92\u3002 \u3000\u3000\u90a3\u5587\u561b\u9762\u9c7c\u94c1\u9752\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u89c1\uff0c\u6211\u72af\u4e86\u4ec0\u4e48\u7f6a\uff1f\u8981\u53d7\u5982\u6b64\u91cd\u7f5a\uff1f\u201d \u3000\u3000\u90a3\u62a4\u6cd5\u5f1f\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u660e\u77e5\u6545\u95ee\uff0c\u8fd9\u4e2a\u4eba\u662f\u8c01\uff0c\u96be\u9053\u4f60\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u4f60\u52fe\u7ed3\u654c \u4eba\uff0c\u8be5\u5f53\u4f55\u7f6a\uff01\u201d \u3000\u3000\u90a3\u5587\u561b\u4ea2\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4f5b\u6148\u60b2\uff0c\u666e\u6e21\u4f17\u751f\uff0c\u6211\u4eca\u65e5\u6551\u4eba\uff0c\u6b63\u662f\u5949\u884c\u4f5b\u7956\u7684\u9053\u7406\uff01\u800c\u4e14\u6559 \u4e3b\u7684\u7981\u4ee4\uff0c\u4e5f\u53ea\u662f\u4e0d\u8bb8\u6b64\u4eba\u5728\u672c\u5bfa\u767e\u91cc\u4e4b\u5185\u505c\u7559\uff0c\u5e76\u65e0\u547d\u4ee4\u54b1\u4eec\u5728\u767e\u91cc\u4e4b\u5916\u6740\u4ed6\uff01\u6b64\u5730\u6070\u597d \u662f\u5728\u672c\u5bfa\u767e\u91cc\u4e4b\u5916\uff0c\u4ed6\u5728\u6b64\u5730\u9047\u9669\uff0c\u4e0e\u4e00\u822c\u4eba\u9047\u9669\u5e76\u65e0\u5206\u522b\uff0c\u6211\u4f55\u4ee5\u4e0d\u53ef\u6551\u4ed6\uff1f\u201d \u3000\u3000\u90a3\u62a4\u6cd5\u5f1f\u5b50\u9053\uff1a\u201c\u5728\u767e\u91cc\u4e4b\u5916\uff0c\u4e5f\u8fd8\u662f\u672c\u5bfa\u7684\u654c\u4eba\uff0c\u600e\u80fd\u4e0e\u4e00\u822c\u4eba\u76f8\u63d0\u5e76\u8bba\uff1f\u72e1\u8fa9\u65e0 \u76ca\uff0c\u4ece\u901f\u8ba4\u7f6a\uff0c\u5211\u7f5a\u6216\u8005\u8fd8\u53ef\u51cf\u8f7b\uff01\u201d \u3000\u3000\u90a3\u5587\u561b\u9053\uff1a\u201c\u4f5b\u7956\u5272\u8089\u5582\u9e70\uff0c\u820d\u8eab\u6551\u864e\uff0c\u9e6b\u9e70\u731b\u864e\uff0c\u96be\u9053\u4e0d\u662f\u4eba\u7c7b\u4e4b\u654c\uff1f\u4f5b\u7956\u8fd8\u8981\u666e\u6e21 \u5b83\u4eec\uff0c\u4f55\u51b5\u6211\u662f\u6551\u4eba\uff1f\u6211\u4e0d\u8ba4\u7f6a\uff01\u201d \u3000\u3000\u90a3\u62a4\u6cd5\u5f1f\u5b50\u6012\u9053\uff1a\u201c\u5f3a\u8f9e\u593a\u7406\uff0c\u6211\u4e0d\u5c51\u4e0e\u4f60\u8fa9\u9a73\u3002\u4e0d\u7ba1\u4f60\u8ba4\u7f6a\u4e5f\u597d\uff0c\u4e0d\u8ba4\u7f6a\u4e5f\u597d\uff0c\u603b\u4e4b \u4f60\u662f\u96be\u9003\u5211\u7f5a\u7684\u4e86\uff01\u201d \u3000\u3000\u90a3\u62a4\u6cd5\u5f1f\u4e88\u84e6\u5730\u63d0\u9ad8\u58f0\u97f3\uff0c\u559d\u9053\uff1a\u201c\u5265\u6389\u4ed6\u7684\u8888\u88df\uff0c\u62bc\u56de\u672c\u5bfa\uff01\u201d\u547d\u4ee4\u4e00\u4e0b\uff0c\u7acb\u5373\u6709\u4e24 \u4e2a\u5587\u561b\u8dd1\u6765\uff0c\u5c06\u54c8\u51e1\u63d0\u62c9\u4e0b\u9a86\u9a7c\uff0c\u53cd\u7ed1\u4e86\u4ed6\u7684\u53cc\u624b\uff0c\u8ddf\u7740\u5c31\u8981\u5265\u4ed6\u7684\u8888\u88df\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6fc0\u4e8e\u4e49\u6124\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u4f60\u4eec\u600e\u53ef\u5982\u6b64\u86ee\u4e0d\u8bb2\u7406\uff1f\u201d\u90a3\u62a4\u6cd5\u5f1f\u5b50\u628a\u624b\u4e00\u6325\uff0c\u559d \u9053\uff1a\u201c\u5c06\u8fd9\u4e24\u4eba\u4e5f\u4e00\u5e76\u62ff\u4e86\uff01\u201d\u6c5f\u6d77\u5929\u4e0d\u5f85\u4ed6\u4eec\u8fc7\u6765\uff0c\u98de\u8eab\u4e00\u60ca\uff0c\u5df2\u5148\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u54c8\u51e1\u63d0\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u6211\u672c\u95e8\u7684\u4e8b\u60c5\uff0c\u79f0\u5343\u4e07\u522b\u7ba1\uff0c\u6211\u4e5f\u4e0d\u8981\u4f60\u4e3a\u6211\u5206\u8fa8\uff01\u5c0f\u6a80\u8d8a\uff0c \u8bf7\u4f60\u8fd8\u662f\u987e\u4f60\u81ea\u5df1\u5427\uff01\u201d\u6c5f\u6d77\u5929\u4e00\u6014\uff0c\u539f\u6765\u4ed6\u6b63\u662f\u60f3\u548c\u90a3\u62a4\u6cd5\u5f1f\u5b50\u7406\u8bba\uff0c\u4f46\u542c\u8fd9\u54c8\u51e1\u63d0\u7684\u8bdd \u65f6\uff0c\u5374\u5206\u660e\u662f\u5411\u4ed6\u6697\u793a\uff0c\u8bf7\u4ed6\u4e0d\u8981\u628a\u771f\u76f8\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u62bc\u89e3\u54c8\u51e1\u63d0\u7684\u5587\u561b\uff0c\u4e00\u4eba\u62c9\u7740\u54c8\u51e1\u63d0\u7684\u4e00\u6761\u81c2\u818a\uff0c\u5c31\u5728\u6c5f\u6d77\u5929\u5373\u5c06\u8ffd\u5230\u4e4b\u65f6\uff0c\u84e6 \u5730\u628a\u54c8\u51e1\u63d0\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u6253\u4e86\u4e00\u4e2a\u5708\u5708\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u75be\u629b\u51fa\u53bb\u3002\u968f\u7740\u4fbf\u8f6c\u8fc7\u8eab\u6765\uff0c\u4e0e\u6c5f\u6d77\u5929\u76f8 \u6597\u3002 \u3000\u3000\u90a3\u62a4\u6cd5\u5f1f\u5b50\u4e00\u624b\u5c06\u54c8\u51e1\u63d0\u63a5\u4e0b\uff0c\u53e6\u4e00\u53ea\u624b\u4fbf\u50fb\u50fb\u556a\u556a\u7684\u4e71\u6253\u4ed6\u7684\u8033\u5149\uff0c\u9a82\u9053\uff1a\u201c\u6211\u53eb\u4f60 \u81ea\u6253\u8033\u5149\uff0c\u4f60\u7adf\u6562\u6297\u547d\uff0c\u5982\u4eca\u52a0\u500d\u5904\u7f5a\uff0c\u6253\u4f60\u516b\u5341\u8033\u5149\uff01\u201d \u3000\u3000\u53ef\u662f\u4ed6\u8fd8\u672a\u6253\u5230\u5341\u4e0b\uff0c\u5ffd\u89c9\u52b2\u98ce\u6251\u9762\uff0c\u539f\u6765\u662f\u59ec\u6653\u98ce\u6765\u4e86\u3002 \u3000\u3000\u90a3\u62a4\u6cd5\u5f1f\u5b50\u5728\u9102\u514b\u6c81\u5bab\u662f\u540d\u5217\u7b2c\u4e94\u7684\u9ad8\u624b\uff0c\u4e00\u89c9\u52b2\u98ce\u6251\u9762\uff0c\u7acb\u5373\u4e00\u638c\u62cd\u51fa\uff1a\u59ec\u6653\u98ce\u4f7f\u51fa \u9634\u9633\u638c\u529b\uff0c\u60f3\u628a\u4ed6\u6454\u5012\uff0c\u54ea\u77e5\u4ed6\u7684\u638c\u529b\u6709\u5982\u91d1\u521a\u731b\u6251\uff0c\u7adf\u628a\u59ec\u6653\u98ce\u4e24\u80a1\u4e92\u76f8\u7275\u5f15\u7684\u638c\u529b\u4e00\u4e3e \u7834\u5f00\uff0c\u59ec\u6653\u98ce\u53cd\u800c\u9669\u4e9b\u7ed9\u4ed6\u7684\u63a8\u529b\u9707\u5012\u3002 \u3000\u3000\u59ec\u6653\u98ce\u8f7b\u529f\u8d85\u5353\uff0c\u4e00\u89c9\u4e0d\u5999\uff0c\u8eab\u5f62\u5e73\u5730\u62d4\u8d77\uff0c\u907f\u5f00\u6b63\u9762\uff0c\u8fc5\u5373\u4f7f\u51fa\u201c\u7384\u9634\u6307\u201d\u7684\u529f\u592b\uff0c \u51cc\u7a7a\u4e00\u6307\u6233\u4e0b\u3002 \u3000\u3000\u90a3\u62a4\u6cd5\u5f1f\u5b50\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u54c8\u51e1\u63d0\uff0c\u4f60\u679c\u7136\u662f\u548c\u654c\u4eba\u52fe\u7ed3\uff01\u201d\u6293\u7740\u54c8\u51e1\u63d0\u7684\u80cc\u5fc3\u3001\u98ce\u8f66 \u822c\u7684\u4e00\u8f6c\uff0c\u59ec\u6653\u98ce\u4e00\u6307\u6233\u4e0b\uff0c\u6ca1\u6709\u6233\u4e2d\u90a3\u62a4\u6cd5\u5f1f\u5b50\uff0c\u5374\u6233\u4e2d\u4e86\u54c8\u51e1\u63d0\u3002\u54c8\u51e1\u63d0\u88ab\u6253\u4e86\u4e03\u516b\u4e0b \u8033\u5149\uff0c\u65e9\u5df2\u5934\u5982\u9488\u523a\uff0c\u8033\u4f3c\u96f7\u9e23\uff0c\u518d\u88ab\u201c\u7384\u9634\u6307\u201d\u4e00\u6233\uff0c\u90a3\u80a1\u5947\u5bd2\u4e4b\u6c14\uff0c\u4ece\u4ed6\u7684\u80cc\u5fc3\u5927\u7a74\u4fb5 \u8fdb\uff0c\u4ed6\u7109\u80fd\u62b5\u53d7\u5f97\u8d77\uff0c\u767b\u65f6\u5168\u8eab\u98a4\u6296\uff0c\u6c57\u5982\u96e8\u4e0b\u3002\u90a3\u62a4\u6cd5\u5f1f\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u548c\u654c\u4eba\u52fe\u7ed3\uff0c\u5c31 \u8ba9\u4f60\u5148\u5c1d\u654c\u4eba\u7684\u82e6\u5934\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u5927\u4e3a\u607c\u6012\uff0c\u559d\u9053\uff1a\u201c\u5929\u4e0b\u7adf\u6709\u4f60\u8fd9\u7b49\u72e0\u5fc3\u7684\u5e08\u5144\u3002\u201d\u4f7f\u51fa\u5929\u7f57\u6b65\u6cd5\uff0c\u8eab\u5f62\u4e00\u95ea\uff0c \u7acb\u5373\u6b3a\u5230\u4ed6\u8eab\u524d\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u62cd\u6253\u4ed6\u7684\u7a74\u9053\u3002\u90a3\u62a4\u6cd5\u5f1f\u5b50\u4e00\u4e2a\u659c\u8eab\u6ed1\u6b65\uff0c\u4ecd\u7136\u628a\u54c8\u51e1\u63d0\u5f53\u4f5c \u76fe\u724c\uff0c\u53ea\u542c\u5f97\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u59ec\u6653\u98ce\u5de6\u638c\u6253\u5728\u54c8\u51e1\u63d0\u8eab\u4e0a\uff0c\u53f3\u638c\u5374\u548c\u90a3\u62a4\u6cd5\u5f1f\u5b50\u78b0\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u59ec\u6653\u98ce\u65e9\u5df2\u6599\u5230\u90a3\u62a4\u6cd5\u5f1f\u5b50\u4f1a\u628a\u54c8\u51e1\u63d0\u5f53\u4f5c\u76fe\u724c\uff0c\u4e5f\u65e9\u5df2\u60f3\u5230\u4e86\u5bf9\u4ed8\u4e4b\u7b56\u2026\u2026 \u3000\u3000\u6765\u59ec\u6653\u98ce\u7684\u6b66\u529f\u867d\u7136\u56e0\u4e3a\u5e7c\u65f6\u7684\u6839\u57fa\u6ca1\u6709\u624e\u597d\uff0c\u59cb\u7ec8\u7ec3\u4e0d\u5230\u6700\u4e0a\u4e58\u7684\u5883\u754c\uff0c\u4f46\u82e5\u8bba\u5230\u6b66 \u5b66\u4e4b\u535a\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u9664\u4e86\u91d1\u4e16\u9057\u4e4b\u5916\uff0c\u5374\u5c31\u8981\u6570\u5230\u4ed6\u4e86\u3002 \u3000\u3000\u4ed6\u4e00\u751f\u4e4b\u4e2d\uff0c\u4e0d\u77e5\u66fe\u5077\u9605\u4e86\u591a\u5c11\u6b66\u5b66\u5178\u7c4d\uff0c\u5bf9\u5404\u5bb6\u5404\u6d3e\u7684\u6b66\u529f\u5dee\u4e0d\u591a\u90fd\u66fe\u6d89\u730e\uff0c\u800c\u4e14\u53ef \u4ee5\u8fd0\u7528\u81ea\u5982\u3002\u4ed6\u6253\u5728\u54c8\u51e1\u63d0\u8eab\u4e0a\u90a3\u4e00\u638c\uff0c\u7528\u7684\u662f\u7ec8\u5357\u6d3e\u201c\u56de\u6625\u638c\u201d\u7684\u529f\u592b\uff0c\u8fd9\u662f\u4e00\u79cd\u975e\u5e38\u5947 \u5999\u7684\u529f\u592b\uff0c\u4e43\u662f\u7ec8\u5357\u6d3e\u7684\u7b2c\u516d\u4ee3\u7956\u5e08\u2014\u2014\u6e05\u521d\u5927\u5112\u517c\u540d\u533b\u5085\u9752\u4e3b\u6240\u521b\uff0c\u8fd9\u79cd\u529f\u592b\u4e0d\u662f\u7528\u6765\u4f24 \u4eba\u7684\uff0c\u6070\u6070\u76f8\u53cd\uff0c\u4e43\u662f\u7528\u672c\u8eab\u7684\u529f\u529b\u6765\u52a9\u4eba\u7597\u4f24\u7684\u3002\u522b\u6d3e\u867d\u7136\u4e5f\u6709\u7c7b\u4f3c\u7684\u529f\u592b\uff0c\u4f46\u603b\u5f97\u82b1\u4e00 \u6bb5\u65f6\u95f4\uff0c\u7edd\u4e0d\u80fd\u5982\u201c\u56de\u6625\u638c\u201d\u7684\u4e00\u638c\u89c1\u6548\u3002\u54c8\u51e1\u63d0\u53d7\u7684\u5947\u5bd2\u4e4b\u6c14\uff0c\u88ab\u4ed6\u7684\u4e00\u638c\u4fbf\u5c3d\u90fd\u5316\u89e3 \u4e86\uff0c\u514d\u53bb\u5927\u75c5\u4e00\u573a\u3002 \u3000\u3000\u4ed6\u548c\u90a3\u62a4\u6cd5\u5f1f\u5b50\u76f8\u78b0\u7684\u4e00\u638c\uff0c\u8fd9\u624d\u662f\u6700\u80fd\u4f24\u4eba\uff0c\u9634\u6bd2\u9738\u9053\u7684\u4fee\u7f57\u9634\u715e\u529f\uff0c\u4e0d\u8fc7\uff0c\u56e0\u4e3a\u4ed6 \u540c\u65f6\u8981\u8fd0\u529f\u6551\u4eba\uff0c\u4ed6\u672c\u6765\u5177\u6709\u7684\u7b2c\u4e03\u91cd\u529f\u529b\uff0c\u5374\u53ea\u80fd\u53d1\u6325\u4e94\u516d\u6210\uff0c\u4f46\u9976\u662f\u5982\u6b64\uff0c\u90a3\u62a4\u6cd5\u5f1f\u5b50 \u4e5f\u7981\u4e0d\u4f4f\u6d51\u8eab\u98a4\u6296\uff0c\u63a5\u8fde\u6253\u4e86\u51e0\u4e2a\u5bd2\u5664\u3002 \u3000\u3000\u90a3\u62a4\u6cd5\u5f1f\u5b50\u5927\u6012\uff1a\u628a\u54c8\u51e1\u63d0\u518d\u629b\u7ed9\u4e00\u4e2a\u540c\u95e8\u5e08\u5f1f\uff0c\u8bf4\u9053\uff1a\u201c\u901f\u628a\u4ed6\u62bc\u56de\u53bb\u4ea4\u7ed9\u6267\u6cd5\u5e08 \u5144\u3002\u6211\u662f\u975e\u628a\u8fd9\u90aa\u9b54\u5916\u9053\u6536\u62fe\u4e86\u4e0d\u53ef\uff01\u201d\u4e00\u8f6c\u8fc7\u8eab\uff0c\u7acb\u5373\u5411\u59ec\u6653\u98ce\u5c55\u5f00\u4e86\u75be\u98ce\u66b4\u96e8\u7684\u653b\u51fb\u3002 \u3000\u3000\u59ec\u6653\u98ce\u521a\u624d\u53ea\u4ee5\u4e00\u534a\u7684\u529f\u592b\uff0c\u548c\u90a3\u62a4\u6cd5\u5f1f\u5b50\u786c\u5bf9\u4e86\u4e00\u638c\uff0c\u771f\u529b\u4e5f\u6d88\u8017\u4e86\u4e0d\u5c11\u3002\u4ed6\u7684\u201c\u4fee \u7f57\u9634\u715e\u529f\u201d\u53c8\u672a\u7ec3\u5230\u6700\u9ad8\u7684\u5883\u754c\uff0c\u4e0d\u80fd\u8fde\u7eed\u4e0d\u65ad\u5730\u4f7f\u7528\uff0c\u90a3\u62a4\u6cd5\u5f1f\u5b50\u7684\u529f\u529b\u5728\u4ed6\u4e4b\u4e0a\uff0c\u653b\u51fb \u5c55\u5f00\u4e0d\u8fc7\u7247\u523b\uff0c\u987a\u628a\u59ec\u6653\u98ce\u7b3c\u7f69\u5728\u638c\u529b\u4e4b\u4e0b\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u6700\u521d\u62bc\u89e3\u54c8\u51e1\u63d0\u7684\u5587\u561b\uff0c\u5374\u4e0d\u662f\u6c5f\u6d77\u5929\u7684\u5bf9\u624b\uff0c\u6c5f\u6d77\u5929\u672c\u6765\u53ef\u4ee5\u5728\u5341\u62db\u4e4b\u5185\u5c06\u4ed6 \u4eec\u6253\u8d25\u7684\uff0c\u4f46\u56e0\u4e3a\u4ed6\u4e0d\u80af\u7528\u6740\u624b\u4f24\u4eba\uff0c\u8fd9\u624d\u5bb9\u5f97\u90a3\u4e24\u4e2a\u5587\u561b\u62fc\u5230\u4e8c\u5341\u4f59\u62db\uff0c\u5f85\u5230\u4ed6\u4eec\u7b4b\u75b2\u529b \u7aed\u4e4b\u540e\uff0c\u8fd9\u624d\u77e5\u96be\u800c\u9000\u3002 \u3000\u3000\u53ef\u662f\u6c5f\u6d77\u5929\u4e00\u5ff5\u4e4b\u6148\uff0c\u5374\u9519\u8fc7\u4e86\u6551\u54c8\u51e1\u63d0\u7684\u673a\u4f1a\u3002\u4ed6\u51fb\u9000\u4e86\u90a3\u4e24\u4e2a\u5587\u561b\u4e4b\u540e\uff0c\u53ea\u89c1\u54c8\u51e1 \u63d0\u5df2\u7ed9\u4eba\u98de\u9a6c\u5e26\u8d70\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u53eb\u9053\uff1a\u201c\u59ec\u4f2f\u4f2f\uff0c\u4e0d\u8981\u6253\u4e86\u3002\u54b1\u4eec\u4ee5\u540e\u518d\u4e0e\u6cd5\u738b\u7406\u8bba\u5427\uff0c\u8fd9\u4e00\u573a\u67b6\u518d\u6253\u4e0b\u53bb\u4e5f\u6ca1 \u6709\u4ec0\u4e48\u610f\u601d\u4e86\uff01\u201d\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u6253\u4eba\u5bb6\uff0c\u4eba\u5bb6\u5374\u8981\u6253\u6211\u5462\uff01\u201d\u6c5f\u6d77\u5929\u773c\u5149\u4e00\u77a5\uff0c\u8fd9\u624d \u77a7\u51fa\u59ec\u6653\u98ce\u5df2\u5728\u5bf9\u65b9\u638c\u529b\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u867d\u7136\u8f7b\u529f\u8d85\u5353\uff0c\u4e5f\u8131\u4e0d\u4e86\u8eab\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7709\u5934\u4e00\u76b1\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u5f85\u6211\u6765\u7ed9\u4f60\u5316\u89e3\u5427\uff01\u201d\u98de\u8eab\u63a0\u8d77\uff0c\u58f0\u5230\u4eba\u5230\uff0c\u6717\u58f0\u8bf4 \u9053\uff1a\u201c\u5feb\u5feb\u64a4\u638c\uff0c\u5426\u5219\u6211\u53ef\u8981\u5bf9\u4f60\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d\u90a3\u62a4\u6cd5\u5f1f\u5b50\u54ea\u91cc\u5c06\u4ed6\u653e\u5728\u773c\u5185\uff0c\u95fb\u8a00\u5927\u6012\uff0c \u5206\u5c71\u4e00\u638c\uff0c\u4fbf\u5411\u4ed6\u6253\u6765\u3002 \u3000\u3000\u90a3\u62a4\u6cd5\u5f1f\u5b50\u54ea\u91cc\u77e5\u9053\uff0c\u6c5f\u6d77\u5929\u5e74\u7eaa\u867d\u8f7b\uff0c\u529f\u529b\u5374\u8fdc\u5728\u59ec\u6653\u98ce\u4e4b\u4e0a\uff0c\u4ed6\u5373\u7b97\u4ee5\u5168\u529b\u5e94\u4ed8\uff0c \u8fd8\u672a\u5fc5\u80c1\u5f97\u8fc7\u6c5f\u6d77\u5929\uff0c\u4f55\u51b5\u53ea\u662f\u5206\u51fa\u4e00\u638c\uff1f \u3000\u3000\u4f46\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u62a4\u6cd5\u5f1f\u5b50\u7ed9\u6c5f\u6d77\u5929\u7684\u638c\u529b\u9707\u5f97\u6447\u6447\u6643\u6643\u3002\u539f\u6765\u6c5f\u6d77\u5929\u542c\u8bf4\u4ed6\u662f \u767d\u6559\u62a4\u6cd5\u5f1f\u5b50\uff0c\u53c8\u89c1\u59ec\u6653\u98ce\u7ed9\u4ed6\u7684\u638c\u529b\u7f69\u7740\uff0c\u53ea\u9053\u4ed6\u679c\u7136\u4e86\u5f97\uff0c\u56e0\u800c\u4f7f\u51fa\u4e86\u8f7b\u800c\u4e0d\u80af\u4e00\u7528\u7684 \u201c\u987b\u5f25\u795e\u638c\u201d\u6765\uff0c\u201c\u987b\u5f25\u795e\u638c\u201d\u662f\u5929\u5c71\u6d3e\u7684\u9547\u5c71\u638c\u6cd5\uff0c\u90a3\u62a4\u6cd5\u5f1f\u5b50\u7109\u80fd\u7981\u53d7\uff1f \u3000\u3000\u90a3\u62a4\u6cd5\u5f1f\u5b50\u63a5\u8fde\u6253\u4e86\u5341\u51e0\u4e2a\u5708\u56f5\uff0c\u624d\u7a33\u5f97\u4f4f\u8eab\u5f62\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u518d\u4e0d\u6562\u591a\u8bf4\u4e00\u53e5\uff0c \u98de\u8eab\u4e0a\u9a6c\uff0c\u7acb\u5373\u56de\u5bfa\uff0c\u90a3\u51e0\u4e2a\u5587\u561b\u4e5f\u8ddf\u7740\u4ed6\u8d70\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u540c\u662f\u767d\u6559\u7684\u67b6\u5b50\uff0c\u90a3\u54c8\u51e1\u63d0\u5bf9\u54b1\u4eec\u4f55\u7b49\u53cb\u5584\uff0c\u8fd9\u51e0\u4e2a\u5587\u561b\u5374\u662f\u5982\u6b64\u53ef \u6076\uff01\u201d\u59ec\u6653\u98ce\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u4e8b\u60c5\u6709\u70b9\u8e4a\u8df7\uff0c\u6211\u770b\u4ed6\u4eec\u4e0d\u662f\u4e00\u8def\u7684\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211 \u4e5f\u6b63\u5728\u5947\u602a\uff0c\u90a3\u54c8\u51e1\u63d0\u4e3a\u4f55\u4e0d\u80af\u628a\u6cd5\u738b\u4ea4\u7ed9\u6c60\u7684\u547d\u4ee4\u8bf4\u51fa\u6765\uff0c\u5374\u53e6\u5916\u7f16\u4e86\u4e00\u5957\u8c0e\u8bdd\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u636e\u6211\u63a8\u6d4b\uff0c\u9102\u514b\u6c81\u5bab\u7684\u50e7\u4f17\u53ef\u80fd\u5df2\u5206\u6210\u4e86\u4e24\u6d3e\u3002\u56e0\u6b64\uff0c\u4ed6\u4eec\u5bf9\u4ed8\u54b1\u4eec\u7684\u6001 \u5ea6\u4e5f\u5c31\u5927\u6709\u4e0d\u540c\uff1b\u6cd5\u738b\u7684\u672c\u610f\u5176\u5b9e\u5e76\u4e0d\u60f3\u628a\u6211\u7f6e\u4e8e\u6b7b\u5730\uff0c\u4ed6\u8981\u54c8\u51e1\u63d0\u6765\u963b\u62e6\u54b1\u4eec\u524d\u8fdb\uff0c\u5f88\u53ef \u80fd\u8fd8\u662f\u4e00\u756a\u597d\u610f\uff01\u81f3\u4e8e\u540e\u6765\u7684\u90a3\u51e0\u4e2a\u5587\u561b\uff0c\u5374\u5b8c\u5168\u662f\u628a\u54b1\u4eec\u5f53\u4f5c\u654c\u4eba\u770b\u5f85\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5927\u9a87\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6cd5\u738b\u5c82\u4e0d\u662f\u5df2\u4e0d\u80fd\u9a7e\u9a6d\u672c\u6d3e\u5f1f\u5b50\uff1f\u751a\u81f3\u5bf9\u90a3\u4e00\u90e8\u4ef7\u4e0d\u80af\u670d \u4ece\u4ed6\u7684\u5f1f\u5b50\u8fd8\u9887\u6709\u987e\u5fcc\uff1f\u8fd9\u771f\u662f\u4e0d\u53ef\u60f3\u8c61\u4e4b\u4e8b\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4e8b\u60c5\u8d8a\u6765\u8d8a\u662f\u53e4\u602a\uff0c\u4ee5\u6cd5\u738b\u7684\u5a01\u671b\uff0c\u4ed6\u624b\u4e0b\u7684\u50e7\u4f17\u600e\u6562\u4e0d\u670d\u4ece\u4ed6\uff0c\u8fd9\u6211\u4e5f \u60f3\u4e0d\u660e\u767d\u3002\u4f46\u4ece\u4eca\u65e5\u4e4b\u4e8b\u770b\u6765\uff0c\u53c8\u786e\u662f\u5982\u6b64\u3002\u6211\u770b\uff0c\u53ea\u597d\u5f85\u89c1\u4e86\u4f60\u7236\u4eb2\u4e4b\u540e\uff0c\u518d\u5546\u91cf\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6cd5\u738b\u7684\u7981\u4ee4\u5462\uff1f\u54b1\u4eec\u8fd8\u7406\u4e0d\u7406\u4f1a\uff1f\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6211\u73b0\u5728\u6000\u7591\uff0c\u751a\u81f3\u6cd5\u738b \u7684\u7981\u4ee4\uff0c\u4e5f\u6709\u53e6\u5916\u7684\u610f\u601d\u2026\u2026\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u54e6\uff0c\u4ed6\u662f\u6015\u53e6\u4e00\u6d3e\u7684\u5f1f\u5b50\u5bf9\u4f60\u65bd\u52a0\u6bd2\u624b\uff1f\u201d\u59ec \u6653\u98ce\u7b11\u9053\uff1a\u201c\u5373\u7b97\u662f\u6cd5\u738b\u7684\u771f\u610f\uff0c\u6211\u4e5f\u4e0d\u6015\uff0c\u4f55\u51b5\u73b0\u5728\u6211\u5df2\u6709\u6000\u7591\uff0c\u66f4\u8981\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa \u4e86\u3002\u201d\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f2f\u4f2f\u65e2\u7136\u4e0d\u6015\uff0c\u54b1\u4eec\u5c31\u4e0d\u5fc5\u7406\u4f1a\u4ed6\u7684\u7981\u4ee4\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u7eed\u5411\u524d\u884c\uff0c\u4e0d\u4e45\u5c31\u6df1\u5165\u4e86\u5723\u5580\u4f26\u5c71\uff0c\u5ffd\u542c\u5f97\u4e0a\u9762\u4f3c\u6709\u5435\u95f9\u4e4b\u58f0\uff0c\u59ec\u6653\u98ce\u4fa7\u8033\u4e00\u542c\uff0c \u53eb\u9053\uff1a\u201c\u54a6\uff0c\u4f3c\u4e4e\u662f\u4f60\u7684\u7236\u4eb2\u5728\u548c\u522b\u4eba\u4e89\u5435\uff01\u5bf9\u65b9\u5927\u7ea6\u6709\u56db\u4e94\u4e2a\u4eba\u3002\u201d\u6c5f\u6d77\u5929\u53c8\u60ca\u53c8\u559c\uff0c\u6025 \u5fd9\u65bd\u5c55\u8f7b\u529f\uff0c\u98de\u6b65\u4e0a\u5c71\uff0c\u5230\u4e86\u4e0a\u9762\u7684\u4e00\u4e2a\u5c71\u5cf0\uff0c\u679c\u7136\u53d1\u73b0\u6709\u4e94\u4e2a\u5587\u561b\u56f4\u7740\u4ed6\u7684\u7236\u4eb2\u3002\u6709\u7684\u5587 \u561b\u5728\u5411\u4ed6\u7236\u4eb2\u5408\u4ec0\u65bd\u793c\uff0c\u6709\u7684\u5587\u561b\u5374\u53c8\u4e0e\u4ed6\u7684\u7236\u4eb2\u62c9\u62c9\u626f\u626f\u3002\u6c5f\u6d77\u5929\u89c1\u4e86\uff0c\u5341\u5206\u5947\u602a\u3002 \u3000\u3000\u59ec\u6653\u98ce\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u542c\uff0c\u4ed6\u4eec\u4f3c\u4e4e\u662f\u5728\u9756\u4f60\u7684\u7236\u4eb2\u53bb\u505a\u5ba2\u4eba\u5462\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u679c\u7136\u542c \u5f97\u4e00\u4e2a\u5587\u561b\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6c5f\u65bd\u4e3b\uff0c\u4f60\u662f\u54b1\u4eec\u6559\u4e3b\u7684\u597d\u670b\u53cb\uff0c\u65e2\u7136\u5230\u6b64\uff0c\u5c82\u53ef\u8fc7\u95e8\u4e0d\u5165\uff1f\u4f60\u4e0d \u7ed9\u6211\u4eec\u9762\u5b50\u4e8b\u5c0f\uff0c\u6211\u4eec\u8bf7\u4e0d\u5230\u4f60\uff0c\u56de\u53bb\u8981\u53d7\u6559\u4e3b\u8d23\u7f5a\u3002\u4e8b\u60c5\u53ef\u5c31\u5927\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u54ce\u5440\uff0c\u4f60\u4eec\u522b\u62c9\u62c9\u626f\u626f\u4e86\uff0c\u6211\u6015\u75d2\u5440\uff0c\u4f60\u4eec\u89e6\u7740\u6211\u7684\u75d2\u5904\u4e86\uff0c\u8fd8\u4e0d\u7f29 \u624b\uff01\u201d\u59ec\u6653\u98ce\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u51fa\u6765\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u542c\uff0c\u4f60\u7239\u7239\u8fd8\u662f\u5f53\u5e74\u7684\u7231\u73a9\u7231\u95f9\u7684\u813e \u6c14\uff0c\u4f60\u662f\u4ed6\u7684\u513f\u5b50\uff0c\u5374\u53cd\u800c\u6bd4\u4ed6\u8001\u6210\u5f97\u591a\u3002\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u5587\u561b\u9053\uff1a\u201c\u597d\uff0c\u53ea\u8981\u65bd\u4e3b\u80af\u53bb\u5c31\u6210\u3002\u201d\u6c5f\u5357\u95ee\u9053\uff1a\u201c\u662f\u4f60\u4eec\u6559\u4e3b\u8981\u4f60\u4eec\u6765\u8bf7\u6211\u7684 \u4e48\uff1f\u201d\u5934\u4e00\u4e2a\u5587\u561b\u9053\uff1a\u201c\u6211\u4eec\u600e\u6562\u6492\u8c0e\uff0c\u6559\u4e3b\u542c\u8bf4\u4f60\u8fd9\u51e0\u5929\u8981\u5230\uff0c\u65e9\u5c31\u5429\u5490\u6211\u4eec\u606d\u5019\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u5475\u54df\uff0c\u6211\u4e0d\u53bb\u4e86\uff01\u201d\u90a3\u4e2a\u5587\u561b\u9053\uff1a\u201c\u8fd9\u5374\u4e3a\u4f55\uff1f\u201d\u6c5f\u5357\u7b11\u563b\u563b\u5730\u9053\uff1a\u201c\u4f60 \u4eec\u4e0d\u77e5\u9053\uff0c\u6211\u662f\u6700\u6015\u53d7\u62d8\u675f\u7684\uff0c\u4f60\u4eec\u6559\u4e3b\u8fd9\u6837\u9686\u91cd\u7684\u6765\u63a5\u5f85\u6211\uff0c\u90a3\u4e00\u5b9a\u662f\u5982\u5f85\u4e0a\u5bbe\uff0c\u793c\u4eea\u5468 \u5168\u7684\u4e86\uff0c\u4ed6\u662f\u4e00\u6559\u4e4b\u4e3b\uff0c\u5bf9\u6211\u793c\u9047\u6709\u52a0\uff0c\u6211\u53c8\u600e\u53ef\u653e\u8086\uff0c\u5f53\u7136\u4e5f\u8981\u89c4\u884c\u77e9\u6b65\u4e86\u3002\u54ce\u5440\uff0c\u6211\u4e0d \u613f\u53d7\u8fd9\u4e2a\u6d3b\u7f6a\uff01\u201d \u3000\u3000\u90a3\u51e0\u4e2a\u5587\u561b\u9762\u9762\u76f8\u89d1\uff0c\u534a\u54cd\u8bf4\u9053\uff1a\u201c\u65bd\u4e3b\u8bf4\u7b11\u8bdd\u4e86\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u6709\u65f6\u4f1a\u8bf4\u5927 \u6d3b\uff0c\u7b11\u8bdd\u5374\u662f\u4ece\u6765\u4e0d\u4f1a\u8bf4\u7684\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u5e94\u8be5\u7ed9\u4ed6\u89e3\u56f4\u4e86\u3002\u201d\u4e24\u4eba\u73b0\u51fa\u8eab\u5f62\uff0c\u75be\u5954\u8fc7\u53bb\uff0c\u6c5f\u5357\u559c\u5f97\u5927\u53eb\u9053\uff1a \u201c\u8001\u54e5\u54e5\u8c03\u4f60\u5148\u6765\u4e86\uff01\u5929\u513f\uff0c\u4f60\u4e5f\u6765\u4e86\uff01\u201d \u3000\u3000\u90a3\u51e0\u4e2a\u5587\u561b\u8ba4\u5f97\u59ec\u6653\u98ce\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u6709\u4e24\u4e2a\u5587\u561b\u8fce\u4e86\u4e0a\u6765\uff0c\u63d0\u7740\u4e5d\u73af\u9521\u6756\uff0c\u4f3c\u4e4e\u5c31\u60f3\u52a8 \u624b\uff0c\u53ef\u662f\u4ed6\u4eec\u770b\u770b\u6c5f\u5357\uff0c\u53c8\u770b\u770b\u59ec\u6653\u98ce\uff0c\u5374\u53c8\u4f3c\u4e4e\u6253\u4e0d\u5b9a\u4e3b\u610f\uff0c\u795e\u60c5\u751a\u662f\u5c34\u5c2c\u3002 \u3000\u3000\u6c5f\u5357\u5ffd\u53c8\u7b11\u563b\u563b\u4ed6\u8bf4\u9053\uff1a\u201c\u6709\u4e86\uff0c\u6709\u4e86\uff0c\u6211\u627e\u5230\u66ff\u8eab\u4e86\u3002\u6211\u8fd9\u4f4d\u54e5\u54e5\u6700\u662f\u998b\u5634\uff0c\u4f60\u4eec\u5bfa \u91cc\u6709\u8bb8\u591a\u597d\u4e1c\u897f\u5403\uff0c\u4e0d\u5982\u8bf7\u4ed6\u53bb\u5427\uff01\u201d\u90a3\u4e3a\u9996\u7684\u5587\u561b\u751a\u662f\u607c\u6012\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u6a80\u8d8a\u522b\u5f00\u73a9\u7b11 \u4e86\u3002\u8fd9\u4f4d\u59ec\u5148\u751f\u6211\u4eec\u662f\u8981\u8bf7\u4ed6\u7684\uff0c\u4e0d\u8fc7\u4e0d\u80fd\u548c\u4f60\u4e00\u822c\u770b\u5f85\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u4ed6\u5c31\u662f\u672c\u5bfa\u6240\u8981\u6349\u62ff \u7684\u8d3c\u4eba\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u90a3\u4e24\u4e2a\u5587\u561b\u7acb\u5373\u4e3e\u8d77\u4e5d\u73af\u9521\u6756\u6765\u6253\u59ec\u6653\u98ce\u3002 \u3000\u3000\u6c5f\u5357\u5927\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8981\u62ff\u6211\u7684\u54e5\u54e5\uff0c\u6211\u66f4\u4e0d\u80fd\u4f5c\u4f60\u4eec\u7684\u5ba2\u4eba\u4e86\u3002\u201d\u90a3\u4e3a\u9996\u7684\u5587\u561b\u9053\uff1a \u201c\u4f60\u4e0d\u53bb\u4e5f\u4e0d\u6210\uff01\u201d\u52a8\u624b\u4fbf\u62c9\u6c5f\u5357\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u521a\u624d\u770b\u4e86\u90a3\u4e24\u4e2a\u63d0\u7740\u4e5d\u73af\u9521\u6756\u7684\u5587\u561b\u7684\u6b65\u6cd5\u548c\u773c\u795e\uff0c\u5df2\u77e5\u4ed6\u4eec\u7684\u6b66\u529f\u6df1\u6d45\uff0c\u59ec\u6653 \u98ce\u5e94\u4ed8\u4ed6\u4eec\u90a3\u662f\u7ef0\u6709\u4f59\u683c\u7684\uff0c\u4e0d\u5fc5\u81ea\u5df1\u5e2e\u5fd9\u3002\u5f53\u4e0b\u4ed6\u4e00\u4e2a\u7bad\u6b65\uff0c\u51b2\u4e0a\u524d\u53bb\uff0c\u4fbf\u628a\u90a3\u4e3a\u9996\u7684\u5587 \u561b\u63a8\u5f00\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7528\u4e86\u4e09\u5206\u52b2\u529b\uff0c\u968f\u624b\u4e00\u63a8\uff0c\u5c06\u90a3\u5587\u561b\u63a8\u5f00\u4e86\u6570\u6b65\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u54ea\u6709\u8fd9\u6837\u8bf7\u5ba2\u7684 \u9053\u7406\uff1f\u6211\u7239\u7239\u597d\u8bf4\u8bdd\uff0c\u4f60\u5c31\u6b3a\u8d1f\u4ed6\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u4eec\u600e\u6837\u8bf7\u5ba2\uff0c\u603b\u662f\u4e00\u756a\u597d\u610f\uff0c\u5929\u513f\uff0c\u4f60\u4e0d\u53ef\u65e0\u793c\uff0c\u7ed9\u8fd9\u51e0\u4f4d\u5927\u5e08\u8d54 \u4e2a\u4e0d\u662f\u5427\u3002\u201d \u3000\u3000\u90a3\u4e3a\u9996\u7684\u5587\u561b\u7ed9\u4ed6\u63a8\u5f97\u51e0\u4e4e\u8dcc\u5012\uff0c\u5fc3\u4e2d\u607c\u6012\uff0c\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u6c5f\u6a80\u8d8a\u7684\u516c\u5b50\uff0c\u4e0d\u6562 \u5f53\uff0c\u4e0d\u6562\u5f53\uff01\u54b1\u4eec\u4eb2\u8fd1\u4eb2\u8fd1\uff01\u201d\u4ed6\u7b11\u91cc\u85cf\u5200\uff0c\u81ea\u6043\u6709\u91d1\u94a2\u6307\u7684\u529f\u592b\uff0c\u4fbf\u4f38\u51fa\u624b\u6765\u62c9\u6c5f\u6d77\u5929\uff0c \u4f6f\u4f5c\u662f\u963b\u6b62\u6c5f\u6d77\u5929\u884c\u793c\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u597d\uff0c\u81ea\u4eec\u4eb2\u8fd1\u4eb2\u8fd1\uff01\u201d\u51fa\u624b\u4e0e\u90a3\u5587\u561b\u4e00\u63e1\uff0c\u90a3\u5587\u561b\u6709\u5982\u89e6\u7535\uff0c\u9661\u7136\u4e00\u9707\uff0c \u614c\u4e0d\u8fed\u7684\u7f29\u624b\uff0c\u4e94\u53ea\u6307\u5934\u90fd\u5411\u540e\u62d7\u66f2\uff0c\u5f2f\u4e0d\u8fc7\u6765\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u5148\u4f7f\u574f\uff0c\u602a\u4e0d\u5f97\u6211\uff0c\u55ef\uff0c\u8fd8\u6709\u4e24\u4f4d\u5927\u5e08\uff0c\u54b1\u4eec\u4e5f\u4eb2\u8fd1\u4eb2\u8fd1\uff0c\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u5587\u561b\u8fde\u5fd9\u9000\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u53bb\u4e5f\u7f62\uff0c\u4f55\u5fc5\u751f\u6c14\uff0c\u4f55\u5fc5\u751f\u6c14\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u59ec\u6653\u98ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u652f\u9521\u6756\u7ed9\u4e86\u6211\u505a\u6252\u706b\u68cd\u5427\u3002\u201d\u201c\u54c8\uff0c\u8fd9\u4e00\u652f\u9521\u6756\u505a\u6253\u72d7 \u68d2\u4e5f\u6b63\u597d\uff01\u201d\u539f\u6765\u4ed6\u5df2\u628a\u90a3\u4e24\u4e2a\u4e3e\u6756\u6253\u4ed6\u7684\u5587\u561b\u5236\u4f0f\uff0c\u5c06\u4ed6\u4eec\u7684\u4e5d\u73af\u9521\u6756\u90fd\u593a\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4e5d\u73af\u9521\u6756\u662f\u4ed6\u4eec\u7684\u5175\u5668\uff0c\u4e5f\u662f\u4ed6\u4eec\u7684\u6cd5\u5668\uff0c\u600e\u7518\u88ab\u593a\uff0c\u4e24\u4eba\u6c14\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u4e00\u9762\u547c\u63f4\uff0c \u4e00\u9762\u53d1\u638c\uff0c\u8981\u628a\u4ed6\u4eec\u7684\u4e5d\u73af\u9521\u6756\u593a\u56de\u3002 \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u4e24\u4e2a\u4eba\u6253\u4e00\u4e2a\u5df2\u7ecf\u4e0d\u5408\u89c4\u77e9\uff0c\u8fd8\u8981\u518d\u6765\u51e0\u4e2a\uff0c\u90a3\u5c31\u7b80\u76f4\u662f\u4e0d\u8981\u8138\u4e86\u3002\u6211\u662f\u6cd5 \u738b\u7684\u670b\u53cb\uff0c\u53ef\u4e0d\u613f\u610f\u6211\u7684\u670b\u53cb\u6709\u4e0d\u8981\u8138\u7684\u5f1f\u5b50\u3002\u8c01\u8fd8\u8981\u6765\uff0c\u6211\u5c31\u8981\u66ff\u6cd5\u738b\u7ba1\u6559\u4ed6\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u7239\u7239\uff0c\u6709\u4e8b\u5e94\u8be5\u5c0f\u8f88\u6548\u52b3\u3002\u201d\u4ed6\u633a\u8eab\u4e00\u7ad9\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7239\u7239\u8bf4\u7684\uff0c\u6700\u591a\u51c6 \u4f60\u4eec\u4e24\u4e2a\u6253\u4e00\u4e2a\uff0c\u8fd8\u6709\u54ea\u4e2a\u8981\u6765\uff0c\u5c31\u8bf7\u548c\u6211\u4ea4\u624b\u5427\uff01\u7239\uff0c\u662f\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff1f\u201d\u6c5f\u5357\u7b11\u9053\uff1a \u201c\u6b63\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u597d\u513f\u5b50\uff0c\u4f60\u73b0\u5728\u61c2\u4e8b\u591a\u4e86\uff0c\u5f88\u8bc6\u5f97\u4e3a\u7236\u7684\u5fc3\u610f\u3002\u201d\u4ed6\u4eec\u4e24\u7236\u5b50\u4e00\u5439\u4e00\u5531\uff0c \u53e6\u5916\u7684\u90a3\u4e09\u4e2a\u5587\u561b\u54ea\u6562\u4e0a\u6765\u3002 \u3000\u3000\u59ec\u6653\u98ce\u624b\u821e\u8db3\u8e48\uff0c\u201c\u5c06\u4e24\u652f\u4e5d\u73af\u9521\u6756\u4e71\u821e\uff0c\u5341\u516b\u4e2a\u94dc\u73af\u6717\u6717\u4f5c\u54cd\uff0c\u7b11\u9053\uff1a\u201c\u771f\u662f\u6700\u597d\u4e0d \u8fc7\u7684\u6253\u72d7\u68d2\uff0c\u78b0\u5230\u6076\u72d7\uff0c\u4e0d\u5fc5\u6253\u4ed6\uff0c\u8fd9\u561f\u561f\u7684\u94c3\u58f0\uff0c\u5c31\u53ef\u5c06\u72d7\u5413\u8dd1\uff0c\u5999\u6781\uff0c\u5999\u6781\uff01\u597d\u542c\uff0c\u597d \u542c\uff01\u4f60\u4eec\u4e5f\u542c\u542c\u5440\uff01\u201d\u4ed6\u8eab\u5f62\u4e00\u98d8\u4e00\u6643\uff0c\u5728\u90a3\u4e24\u4e2a\u5587\u561b\u8033\u8fb9\u4e71\u6447\uff0c\u968f\u624b\u4e00\u6311\uff0c\u628a\u4e00\u4e2a\u5587\u561b\u7684 \u50e7\u5e3d\u6311\u4e0b\uff0c\u53cd\u624b\u4e00\u626f\uff0c\u53c8\u628a\u4e00\u4e2a\u5587\u561b\u7684\u8888\u88df\u626f\u8131\u3002 \u3000\u3000\u90a3\u4e3a\u9996\u7684\u5587\u561b\u6c14\u5f97\u9762\u8272\u94c1\u9752\uff0c\u5927\u559d\u9053\uff1a\u201c\u522b\u5728\u8fd9\u91cc\u4e22\u4eba\u73b0\u4e16\u5566\uff0c\u90fd\u7ed9\u6211\u6eda\u56de\u53bb\uff01\u201d\u8f6c\u77ac \u4e4b\u95f4\uff0c\u8fd9\u51e0\u4e2a\u5587\u561b\u90fd\u8d70\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6c5f\u5144\u5f1f\uff0c\u4f60\u8fd8\u662f\u65e7\u65e5\u7684\u813e\u6c14\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u59ec\u5927\u54e5\uff0c\u4f60\u7684\u6027\u60c5\u4e5f\u72b9\u662f\u5f53 \u5e74\uff01\u201d\u4e24\u4e2a\u7ed3\u62dc\u5144\u5f1f\u76f8\u4e92\u62e5\u62b1\uff0c\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u59ec\u6653\u98ce\u5ffd\u7136\u4f38\u957f\u8116\u5b50\uff0c\u4ef0\u8d77\u5934\u6765\uff0c\u6c5f\u5357\u5947\u9053\uff1a\u201c\u59ec\u5927\u54e5\uff0c\u8fd9\u6811\u4e0a\u6709\u4ec0\u4e48\u597d\u4e1c\u897f\uff0c\u4f60\u770b\u5f97 \u8fd9\u6837\u51fa\u795e\uff1f\u201d\u59ec\u6653\u98ce\u9053\uff1a\u201c\u8fd9\u6811\u4e0a\u4f3c\u4e4e\u6709\u4eba\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u6811\u4e0a\u679c\u7136\u6709\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a \u201c\u59ec\u5927\u54e5\uff0c\u4f60\u771f\u597d\u773c\u529b\uff0c\u6211\u670d\u4e86\u4f60\u4e86\u3002\u201d\u968f\u5373\u8df3\u4e86\u4e0b\u6765\uff0c\u201c\u4f17\u4eba\u4e8c\u770b\uff0c\u6b63\u662f\u9648\u5929\u5b87\u3002 \u3000\u3000\u6c5f\u5357\u559c\u4e0a\u52a0\u559c\uff0c\u53eb\u9053\uff1a\u201c\u5b57\u54e5\uff0c\u4f60\u600e\u4e48\u8eb2\u5728\u8fd9\u91cc\uff1f\u8fd9\u51e0\u5e74\u6211\u60f3\u5f97\u4f60\u597d\u82e6\u3002\u201d\u9648\u5929\u5b87\u7b11 \u9053\uff1a\u201c\u6211\u85cf\u5728\u6811\u4e0a\u770b\u4f60\u4eec\u620f\u800d\u8fd9\u73ed\u5587\u561b\uff0c\u5374\u771f\u662f\u4e50\u5f97\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u7b11\u51fa\u6765\u3002\u55ef\uff0c\u4f84\u513f\u4e5f\u8fd9\u822c\u5927 \u4e86\uff0c\u521a\u624d\u4ed6\u9732\u7684\u90a3\u4e24\u624b\u6b66\u529f\uff0c\u66f4\u662f\u4ee4\u6211\u770b\u5f97\u9ad8\u5174\u3002\u201d \u3000\u3000\u6c5f\u5357\u643a\u5b50\u4e0a\u524d\uff0c\u4e0e\u9648\u5929\u5b87\u89c1\u8fc7\u4e86\u793c\uff0c\u95ee\u9053\uff1a\u201c\u5510\u5927\u4fa0\u592b\u5987\u672a\u66fe\u6765\u4e48\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u53bb\u5e74\u6211\u5230\u5929\u5c71\uff0c\u4ed6\u4eec\u592d\u5c71\u6d3e\u7684\u805a\u4f1a\u5df2\u7ecf\u6563\u4e86\u3002\u6211\u8c12\u89c1\u5510\u8001\u638c\u95e8\uff0c\u59cb\u77e5\u5510\u7ecf \u5929\u592b\u5987\u53c8\u5df2\u53bb\u4e86\u522b\u5904\uff0c\u4ed6\u4eec\u884c\u8e2a\u65e0\u5b9a\uff0c\u4e5f\u672a\u8bf4\u597d\u4ec0\u4e48\u65f6\u5019\u56de\u6765\u3002 \u3000\u3000\u201c\u6211\u53ea\u597d\u5c06\u4f60\u5ac2\u5b50\u79bb\u5947\u5931\u8e2a\u4e4b\u4e8b\u544a\u77e5\u5510\u8001\u638c\u95e8\uff0c\u627f\u8499\u5510\u8001\u638c\u95e8\u7b54\u5e94\uff0c\u6d3e\u51fa\u95e8\u4e0b\u5f1f\u5b50\uff0c\u53bb \u627e\u4ed6\u7684\u513f\u5b50\u56de\u6765\u3002\u6211\u56e0\u4e3a\u4e0e\u4f60\u4eec\u6709\u7ea6\uff0c\u4e0d\u80fd\u5728\u5929\u5c71\u4e45\u5019\uff0c\u4fbf\u5306\u52ff\u544a\u522b\u4e86\u5510\u8001\u638c\u95e8\u3002\u201d \u3000\u3000\u201c\u6211\u662f\u4eca\u65e5\u4e2d\u5348\u65f6\u5206\uff0c\u8e0f\u8fdb\u8fd9\u5723\u5580\u4f26\u5c71\u7684\uff0c\u5728\u6811\u6797\u91cc\u5ffd\u7136\u9047\u7740\u4e00\u4f19\u5587\u561b\uff0c\u5728\u90a3\u91cc\u7a83\u7a83\u79c1 \u8bae\uff0c\u6211\u9690\u7ea6\u542c\u788d\u4ed6\u4eec\u63d0\u8d77\u6211\u4eec\u4e09\u4eba\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u201c\u5176\u65f6\u6b63\u522e\u5927\u98ce\uff0c\u5728\u6811\u6797\u91cc\u6709\u6811\u6728\u6321\u98ce\uff0c\u5371\u9669\u4e0d\u5927\uff0c\u4f46\u65ad\u679d\u6b8b\u53f6\uff0c\u968f\u98ce\u98de\u821e\uff0c\u52a0\u4ee5\u5929\u8272 \u6c89\u6697\uff0c\u5341\u6b65\u4e4b\u5916\uff0c\u4e5f\u662f\u4e00\u7247\u6a21\u7cca\u3002\u201d \u3000\u3000\u201c\u597d\u5728\u6709\u8fd9\u573a\u98ce\uff0c\u6211\u8ddf\u5728\u4ed6\u4eec\u7684\u540e\u9762\uff0c\u4fa5\u5e78\u672a\u7ed9\u53d1\u73b0\u3002\u542c\u5f97\u5176\u4e2d\u6709\u4e2a\u5587\u561b\u53ee\u5631\u4ed6\u7684\u5e08\u5f1f \u9053\uff1a\u2018\u4f60\u8bb0\u7740\u4e86\uff0c\u5018\u82e5\u9047\u5230\u6c5f\u5357\uff0c\u54b1\u4eec\u8981\u4ee5\u5ba2\u793c\u76f8\u5f85\uff0c\u5047\u6258\u5e08\u5c0a\u4e4b\u547d\uff0c\u8bf7\u4ed6\u5230\u672c\u5bfa\u53bb\uff0c\u5018\u82e5 \u9047\u5230\u59ec\u6653\u98ce\uff0c\u90a3\u5c31\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u56f4\u800c\u653b\u4e4b\uff0c\u6349\u4f4f\u5148\u63cd\u4ed6\u4e00\u987f\uff1a\u5018\u82e5\u662f\u9047\u5230\u9648\u5929\u5b87\uff0c\u5219\u53ea\u8bb8\u6d3b \u64d2\uff0c\u4e0d\u8bb8\u4f24\u5bb3\u3002\u2019\u4ed6\u5e08\u5f1f\u95ee\u9053\uff1a\u2018\u5018\u82e5\u662f\u9047\u5230\u4ed6\u4eec\u90fd\u5728\u4e00\u8d77\uff0c\u6216\u8005\u662f\u6c5f\u5357\u548c\u5176\u4e2d\u4e00\u4eba\u5728\u4e00\u8d77 \u5462\uff1f\u2019\u90a3\u5587\u561b\u7b54\u9053\uff1a\u2018\u90a3\u5c31\u770b\u6211\u7684\u773c\u8272\u884c\u4e8b\u3002\u2019\u8fd9\u4e2a\u5587\u561b\u5c31\u662f\u521a\u624d\u7ed9\u6c5f\u8d24\u4f84\u5413\u9000\u7684\u90a3\u4e2a\u4e3a\u9996 \u5587\u561b\u4e86\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u6700\u6068\u7684\u662f\u6211\u3002\u53ef\u501f\u6211\u521a\u624d\u672a\u66fe\u5c06\u4ed6\u4eec\u63cd\u4e00\u987f\u3002\u201d\u9648\u592d\u5b57\u9053\uff1a \u201c\u4ed6\u4eec\u6068\u4f60\uff0c\u90a3\u662f\u56e0\u4e3a\u4f60\u95ef\u8fc7\u4ed6\u4eec\u7684\u9102\u514b\u6c81\u5bab\uff0c\u4ed6\u4eec\u51fa\u52a8\u4e86\u9616\u5bfa\u4e4b\u4f17\uff0c\u4ecd\u7136\u7ed9\u4f60\u9003\u8131\uff0c\u662f\u89c9 \u5f97\u5927\u5931\u4f53\u9762\u3002\u4f46\u4ed6\u4eec\u8981\u9a97\u6c5f\u5144\u5f1f\uff0c\u53c8\u8981\u5c06\u6211\u6d3b\u64d2\uff0c\u5176\u4e2d\u5374\u4e0d\u77e5\u6709\u4f55\u8be1\u8ba1\uff0c\u66f4\u4ee4\u6211\u8981\u52a0\u500d\u63d0 \u9632\u3002\u6211\u5c31\u4e00\u76f4\u8ddf\u7740\u4ed6\u4eec\uff0c\u4e0d\u4e45\u4ed6\u4eec\u5c31\u4e0e\u6c5f\u5144\u5f1f\u76f8\u9047\uff0c\u6f14\u51fa\u4e86\u4e00\u51fa\u9738\u738b\u8bf7\u5ba2\u7684\u597d\u620f\uff0c\u6211\u77e5\u9053\u6c5f \u5144\u5f1f\u6ca1\u6709\u5371\u9669\uff0c\u6682\u65f6\u4e0d\u60f3\u73b0\u8eab\uff0c\u4fbf\u85cf\u5728\u6811\u4e0a\u9759\u89c2\u5176\u53d8\u3002\u201d \u3000\u3000\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u6211\u65e9\u5c31\u77e5\u9053\u90a3\u51e0\u4e2a\u5587\u561b\u662f\u8bf4\u8c0e\u7684\u4e86\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u6c5f\u5357 \u9053\uff1a\u201c\u4e0a\u4e00\u6b21\u6211\u4e0e\u6cd5\u738b\u5206\u624b\u4e4b\u65f6\uff0c\u6cd5\u738b\u66fe\u5bf9\u6211\u8a00\u9053\uff0c\u4e0b\u6b21\u4f60\u5018\u82e5\u8981\u6765\u672c\u5bfa\uff0c\u6700\u597d\u4e0e\u91d1\u5927\u4fa0\u540c \u6765\uff0c\u5426\u5219\u5c31\u4e0d\u5fc5\u6765\u4e86\uff0c\u5f53\u65f6\u6ca1\u6709\u7b2c\u4e09\u4e2a\u4eba\uff0c\u6cd5\u738b\u8bf4\u8bdd\u7684\u795e\u60c5\u5e84\u91cd\u800c\u53c8\u8bda\u6073\uff0c\u8fd8\u4f3c\u4e4e\u6015\u6211\u5bf9\u4ed6 \u6709\u6240\u4e0d\u8c05\u4f3c\u7684\u3002\u4ed6\u4e3a\u4f55\u8981\u8fd9\u6837\u5429\u5490\u6211\uff0c\u6211\u81f3\u4eca\u672a\u5f97\u5176\u89e3\u3002\u4e0d\u8fc7\uff0c\u6211\u5df2\u628a\u4ed6\u7684\u8bdd\u7262\u7262\u8bb0\u4e86\u4e0b \u6765\u3002\u521a\u624d\u90a3\u51e0\u4e2a\u5587\u561b\u5374\u8bf4\u662f\u5949\u4e86\u6cd5\u738b\u4e4b\u547d\u8bf7\u6211\u53bb\u7684\uff0c\u8fd9\u5c82\u4e0d\u662f\u524d\u8a00\u4e0d\u5bf9\u540e\u8bed\uff0c\u5206\u660e\u662f\u5047\u4f20\u5e08 \u547d\uff0c\u5411\u6211\u8bf4\u8c0e\u5417\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u767d\u6559\u6cd5\u738b\u7684\u6212\u5f8b\u6700\u4e25\uff0c\u4ed6\u7684\u95e8\u4e0b\u5f1f\u5b50\u7adf\u6562\u5047\u4f20\u5e08\u547d\uff0c\u8fd9\u53c8 \u662f\u4e00\u4ef6\u4e0d\u53ef\u89e3\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4ef6\u4e8b\u4e5f\u4e0d\u96be\u7d22\u89e3\uff0c\u4e0d\u8fc7\uff0c\u6211\u8fd8\u672a\u60f3\u900f\u5185\u91cc\u7684\u539f\u56e0\u3002\u201d\u9648 \u5929\u5b87\u9053\uff1a\u201c\u4f9d\u4f60\u4e4b\u89c1\u5982\u4f55\uff1f\u4f60\u5c31\u628a\u4f60\u60f3\u5230\u7684\u5148\u8bf4\u8bf4\u5427\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u5148\u628a\u4ed6\u4e0e\u6c5f\u6d77\u5929\u521a\u624d\u7684\u906d\u9047\u8bf4\u4e86\u4e00\u904d\uff0c\u9648\u5929\u5b87\u9ed8\u9ed8\u6c89\u601d\uff0c\u5ffd\u5730\u62cd\u638c\u8bf4\u9053\uff1a\u201c\u5bf9 \u4e86\uff01\u201d\u6c5f\u5357\u8be7\u9041\uff1a\u201c\u4ec0\u4e48\u5bf9\u4e86\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u6211\u731c\u59ec\u5927\u54e5\u662f\u6000\u7591\u767d\u6559\u5587\u561b\u5df2\u5206\u6210\u4e86\u4e24\u6d3e\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\uff0c\u800c\u4e14\u6697\u4e2d\u53cd\u5bf9\u6cd5\u738b\u7684\u8fd9\u4e00\u6d3e\uff0c\u5927\u7ea6\u540e\u9762\u8fd8\u6709\u80fd\u4eba\u6491\u8170\u3002\u5426\u5219\u4ed6 \u4eec\u4e0d\u6562\u5982\u6b64\uff0c\u6cd5\u738b\u4e4b\u6240\u4ee5\u8981\u6c5f\u8d24\u5f1f\u548c\u91d1\u5927\u4fa0\u540c\u5728\uff0c\u90a3\u662f\u56e0\u4e3a\u4ed6\u7ba1\u675f\u4e0d\u4e86\u53e6\u4e00\u6d3e\u7684\u4eba\uff0c\u6015\u4ed6\u4eec \u5bf9\u6c5f\u8d24\u5f1f\u6709\u6240\u4e0d\u5229\uff0c\u6709\u91d1\u5927\u4fa0\u540c\u884c\uff0c\u90a3\u5c31\u65e0\u987b\u987e\u8651\u4e86\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u4e5f\u8bb8\u4ed6\u4e5f\u6b63\u60f3\u4ef0\u4ed7\u91d1\u5927\u4fa0 \u7684\u52a9\u529b\u3002\u201d \u3000\u3000\u6c5f\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6cd5\u738b\u7684\u5904\u5883\u5c82\u975e\u4e5f\u751a\u5371\u9669\uff1f\u6211\u770b\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u54b1\u4eec\u4eca \u665a\u5c31\u6109\u6109\u5230\u9102\u514b\u6c81\u5bab\u53bb\u770b\u4e2a\u7a76\u7adf\u5427\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u6211\u4e5f\u6025\u4e8e\u8981\u628a\u59bb\u5b50\u88ab\u52ab\u4e4b\u4e8b\u67e5\u4e2a\u6c34\u843d\u77f3 \u51fa\u3002\u53ef\u662f\u54b1\u4eec\u5f97\u60f3\u4e2a\u6cd5\u5b50\uff0c\u53ea\u8981\u5355\u72ec\u89c1\u5230\u6cd5\u738b\uff0c\u5426\u5219\u548c\u90a3\u4e9b\u5587\u561b\u53c8\u5927\u6253\u4e00\u573a\u4e5f\u6ca1\u6709\u4ec0\u4e48\u610f \u601d\uff1a\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4ee5\u59ec\u5927\u54e5\u7684\u8f7b\u529f\uff0c\u4e0a\u6b21\u4e5f\u7ed9\u4ed6\u4eec\u53d1\u89c9\u4e86\u3002\u73b0\u5728\u54b1\u4eec\u6709\u56db\u4eba\u4e4b\u591a\uff0c\u8981\u795e\u4e0d\u77e5 \u9b3c\u4e0d\u89c9\u7684\u8fdb\u53bb\uff1b\u53ea\u6015\u751a\u96be\u3002\u8bf4\u4e0d\u5f97\u53ea\u597d\u786c\u95ef\u4e86\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u6211\u65e9\u5df2\u60f3\u597d\u4e86\u5999\u6cd5\u3002\u201d\u6c5f\u5357\u559c\u9053\uff1a\u201c\u59ec\u5927\u54e5\uff0c\u4f60\u4f55\u4e0d\u65e9\u8bf4\uff1f\u201d\u59ec\u6653 \u98ce\u7b11\u9053\uff1a\u201c\u73b0\u5728\u576f\u6b20\u51e0\u4ef6\u9053\u5177\uff0c\u6211\u846b\u82a6\u91cc\u7684\u54d1\u8c1c\u8fd8\u5f97\u7b49\u4e00\u4e0b\u624d\u63ed\u6653\u5462\u3002\u6c5f\u5144\u5f1f\uff0c\u4f60\u613f\u610f\u8ba9\u6d77 \u5929\u548c\u6211\u53bb\u4e00\u8d9f\u5417\uff1f\u201d\u6c5f\u5357\u9053\uff1a\u201c\u4f60\u8981\u53bb\u4ec0\u4e48\u5730\u65b9\uff0c\u529e\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u4e0d\u80fd\u8bf4\u4e0e\u4f60\u77e5\u9053\u3002\u4e0d\u8fc7\u4f60\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u8fd9\u4e8b\u60c5\u662f\u5f88\u5bb9\u6613\u529e\u7684\u3002\u53ea\u8981\u6d77\u5929\u4e00 \u4eba\u5c31\u591f\u4e86\u3002\u201d\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u53ea\u8981\u4f60\u4e0d\u662f\u6559\u4ed6\u505a\u8d3c\u5c31\u884c\u3002\u201d\u59ec\u6653\u98ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u6b63\u662f\u8981\u6559\u4ed6 \u505a\u8d3c\uff01\u201d\u5927\u7b11\u58f0\u4e2d\uff0c\u62c9\u4e86\u6c5f\u6d77\u5929\u4fbf\u8dd1\u3002 \u3000\u3000\u9648\u5929\u5b87\u53f9\u9053\uff1a\u201c\u8fd9\u4f4d\u59ec\u5927\u54e5\u8ba1\u667a\u8fc7\u4eba\uff0c\u96be\u5f97\u4ed6\u53c8\u662f\u4e00\u526f\u70ed\u5fc3\u80a0\uff0c\u53ea\u8981\u4e0e\u4f60\u6295\u7f18\uff0c\u5c31\u4e0d\u60dc \u8d74\u6c64\u8e48\u706b\u3002\u8fd9\u6b21\u6211\u5f97\u4ed6\u7684\u5e2e\u5fd9\u53ef\u8fd8\u771f\u4e0d\u5c0f\u5462\u3002\u90a3\u4e24\u4e2a\u8d3c\u4eba\u85cf\u5728\u9102\u514b\u6c81\u5b98\u5c31\u662f\u4ed6\u67e5\u51fa\u7684\u3002\u60f3\u5f53 \u5e74\uff0c\u6211\u4eec\u90fd\u628a\u4ed6\u5f53\u4f5c\u90aa\u6d3e\u5996\u4eba\u770b\u5f85\uff0c\u5374\u4e0d\u77e5\u4ed6\u5176\u5b9e\u662f\u4e2a\u597d\u4eba\uff0c\u8bf4\u6765\u771f\u662f\u60ed\u6127\u3002\u201d \u3000\u3000\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u4e14\u770b\u8fd9\u6b21\u4ed6\u846b\u82a6\u91cc\u5356\u4ec0\u4e48\u836f\u3002\u201d\u4e24\u4eba\u5750\u4e86\u4e0b\u6765\uff0c\u5404\u8bc9\u522b\u540e\u60c5\u51b5\uff0c\u53ea\u8fc7\u4e86\u5927 \u7ea6\u4e00\u987f\u996d\u5149\u666f\uff0c\u59ec\u6653\u98ce\u4e0e\u6c5f\u6d77\u5929\u4fbf\u5df2\u56de\u6765\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u4eec\u5404\u62b1\u7740\u4e24\u5957\u8888\u88df\uff0c\u5934\u4e0a\u8fd8\u6234\u7740\u50e7\u5e3d\uff0c\u563b\u563b\u54c8\u54c8\uff0c\u53e3\u8bb2\u6307\u5212\u5730\u8d70\u8fc7\u6765\u3002\u6c5f\u5357\u7b11 \u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u4f60\u5f53\u771f\u662f\u6559\u6d77\u5929\u53bb\u505a\u8d3c\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u4f20\u6388\u4ed6\u770b\u5bb6\u672c\u9886\u5462\u3002\u8fd9\u6b21\u53ea\u662f\u62e6\u8def\u6253\u52ab\uff0c\u6839\u672c\u5c31\u7528\u4e0d\u5230\u4e0a\u4e58\u7684 \u5077\u76d7\u653b\u592b\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u672a\u8d70\u5230\u5c71\u4e0b\uff0c\u5c31\u8ffd\u4e0a\u4e86\u521a\u624d\u90a3\u4f19\u5587\u561b\uff0c\u4ed6\u4eec\u4ee5\u4e3a\u6211\u4eec\u662f\u8981\u8d76\u5c3d\u6740 \u7edd\uff0c\u5bb3\u6015\u5f97\u4e86\u4e0d\u788d\u3002\u59ec\u4f2f\u4f2f\u672c\u6765\u8981\u628a\u4ed6\u4eec\u63cd\u4e00\u987f\u7684\uff0c\u662f\u6211\u62e6\u963b\u4f4f\u4e86\u3002\u201d\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u8c01\u53eb \u4ed6\u4eec\u5728\u80cc\u540e\u5439\u725b\uff0c\u8bf4\u8981\u628a\u6211\u6349\u4f4f\u5c31\u5148\u63cd\u5341\u987f\u3002\u4e0d\u8fc7\uff0c\u8fd9\u6b21\u867d\u6ca1\u6709\u63cd\u4ed6\u4eec\uff0c\u8fd9\u53e3\u6c14\u5374\u4e5f\u51fa\u4e86\u3002 \u6211\u7528\u4e54\u7956\u79d8\u7c4d\u7684\u2018\u70b9\u9690\u7a74\u2019\u7684\u72ec\u95e8\u624b\u6cd5\uff0c\u70b9\u4e86\u4ed6\u4eec\u7684\u7a74\u9053\uff0c\u4e00\u4e2a\u4e2a\u653e\u5728\u6811\u7abf\u91cc\u9762\uff0c\u8981\u8fc7\u5341\u4e8c \u4e2a\u65f6\u8fb0\uff0c\u65b9\u80fd\u81ea\u89e3\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u59ec\u5927\u54e5\u4e4b\u610f\u83ab\u975e\u662f\u8981\u626e\u4f5c\u5587\u561b\uff0c\u6df7\u8fdb\u9102\u514b\u6c81\u5bab\u4e48\uff1f\u8ba1\u662f\u597d\u8ba1\uff0c\u4f46\u8fd8\u6709\u4e00\u6837 \u7834\u7efd\u3002\u201d\u59ec\u6653\u98ce\u76f4\uff1a\u201c\u6709\u4f55\u7834\u7efd\uff1f\u201d\u9648\u5929\u5b87\u9053\uff1a\u201c\u767d\u6559\u5587\u561b\u5341\u4e5d\u662f\u897f\u57df\u4eba\uff0c\u54b1\u4eec\u7684\u8138\u578b\u4f53\u6001 \u90fd\u4e0d\u50cf\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u65e9\u5df2\u51c6\u5907\u597d\u4e86\u3002\u201d\u62ff\u51fa\u4e86\u56db\u5f20\u6781\u8584\u7684\u4eba\u76ae\u9762\u5177\uff0c\u8bf4\u9053\uff1a\u201c\u897f\u85cf\u5927\u5b89 \u8fbe\u68ee\u6797\u91cc\u6709\u4e2a\u730e\u5934\u65cf\uff0c\u6740\u4e86\u4eba\u5c31\u628a\u5934\u76ae\u5265\u4e0b\u6765\uff0c\u5236\u6210\u5934\u50cf\u548c\u9762\u5177\u3002\u897f\u85cf\u4eba\u5f88\u5c0a\u656c\u5587\u561b\uff0c\u53ea\u6709 \u8fd9\u4e2a\u730e\u5934\u65cf\u6562\u6740\u4ed6\u4eec\u3002\u8fd9\u51e0\u5f20\u4eba\u76ae\u9762\u5177\u5c31\u662f\u6211\u4ece\u5927\u5b89\u8fbe\u68ee\u6797\u7684\u730e\u5934\u65cf\u5077\u6765\u7684\uff0c\u4f60\u4eec\u6234\u4e0a\u8bd5 \u8bd5\u3002\u81f3\u4e8e\u4f53\u6001\uff0c\u6211\u6559\u4f60\u4eec\u5047\u626e\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u7b49\u4eba\u7a7f\u4e0a\u8888\u88df\uff0c\u5e26\u4e0a\u9762\u5177\u3001\u5728\u5c71\u6da7\u8fb9\u4e34\u6d41\u81ea\u7167\uff0c\u679c\u7136\u6d3b\u8131\u8131\u662f\u4e2a\u5587\u561b\u3002\u59ec\u6653\u98ce\u53c8 \u5c06\u5587\u561b\u7684\u4e3e\u6b62\u3001\u4e60\u60ef\uff0c\u548c\u4e00\u4e9b\u7b80\u5355\u7684\u9752\u6d77\u571f\u8bed\u6559\u7ed9\u4ed6\u4eec\uff0c\u64cd\u7ec3\u4e86\u4e00\u756a\uff0c\u867d\u4e0d\u6562\u8bf4\u6beb\u65e0\u7834\u7efd\uff0c \u4f46\u5047\u5982\u4e0d\u662f\u88ab\u4eba\u4ed4\u7ec6\u76d8\u95ee\uff0c\u4f30\u91cf\u4e5f\u53ef\u4ee5\u5e94\u4ed8\u5f97\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u9648\u5929\u5b87\u5e26\u6709\u5e72\u7cae\uff0c\u59ec\u6653\u98ce\u53c8\u53bb\u6349\u4e86\u4e24\u53ea\u91ce\u5154\uff0c\u70e4\u719f\u4e86\u9971\u9910\u4e00\u987f\uff0c\u5f85\u5230\u5929\u8272\u5165\u9ed1\uff0c\u4fbf\u4e00\u9f50 \u524d\u5f80\u9102\u514b\u6c81\u5bab\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u4eba\u90fd\u6709\u4e00\u8eab\u4e0a\u6765\u7684\u8f7b\u529f\uff0c\u8fc7\u4e86\u4e8c\u66f4\u65f6\u5206\uff0c\u4fbf\u5230\u4e86\u9102\u514b\u6c81\u5bab\u5916\u56f4\u7684\u7981\u5730\uff0c\u9648\u5929\u5b87\u548c \u6c5f\u5357\u90fd\u5f88\u5c0f\u5fc3\u6212\u60e7\uff0c\u4f46\u5947\u602a\u5f97\u5f88\uff0c\u5374\u5e76\u6ca1\u6709\u78b0\u5230\u4ec0\u4e48\u9ad8\u624b\uff0c\u6709\u51e0\u4e2a\u5de1\u591c\u7684\u5587\u561b\u672c\u9886\u5f88\u662f\u5e73 \u5e38\uff0c\u59ec\u6653\u98ce\u7b49\u4eba\u4ece\u4ed6\u4eec\u8eab\u8fb9\u63a0\u8fc7\uff0c\u4ed6\u4eec\u4e5f\u6ca1\u6709\u53d1\u73b0\u3002 \u3000\u3000\u5c06\u8fd1\u5bfa\u95e8\uff0c\u5ffd\u542c\u5f97\u949f\u58f0\u5f53\u5f53\u3001\u59ec\u6653\u98ce\u5fc3\u4e2d\u9ed8\u6570\uff0c\u5171\u6572\u4e86\u4e8c\u5341\u4e00\u4e0b\uff0c\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u4ed6\u8bc6 \u5f97\u767d\u6559\u7684\u89c4\u77e9\uff0c\u545c\u949f\u805a\u4f1a\uff0c\u666e\u901a\u662f\u53ea\u6572\u4e03\u4e0b\uff0c\u9047\u5230\u91cd\u8981\u7684\u4e8b\u60c5\uff0c\u90a3\u5c31\u52a0\u500d\u6572\u5341\u56db\u4e0b\u3002\u82e5\u7136\u8fde \u6572\u4e8c\u5341\u4e00\u4e0b\uff0c\u90a3\u5c31\u662f\u6709\u5173\u672c\u6559\u8363\u8fb1\u5b58\u4ea1\u7684\u5927\u4e8b\uff0c\u8981\u9616\u5bfa\u50e7\u4f17\u6765\u516c\u8bae\u7684\u4e86\u3002\u4e00\u4e2a\u767d\u6559\u5587\u561b\uff0c\u4e00 \u751f\u4e4b\u4e2d\u4e5f\u672a\u5fc5\u6709\u673a\u4f1a\u80fd\u542c\u5230\u5854\u9876\u7684\u5927\u949f\u8fde\u6572\u4e8c\u5341\u4e00\u4e0b\u7684\u3002 \u3000\u3000\u9102\u514b\u6c81\u5bab\u867d\u6bd4\u4e0d\u4e0a\u897f\u85cf\u5e03\u8fbe\u62c9\u5bab\u7684\u96c4\u4f1f\u58ee\u4e3d\uff0c\u4f46\u5927\u5927\u5c0f\u5c0f\u7684\u5efa\u7b51\u7269\uff0c\u661f\u7f57\u68cb\u5e03\uff0c\u4e5f\u6709\u6570 \u767e\u5e62\u4e4b\u591a\uff0c\u949f\u58f0\u4e00\u54cd\uff0c\u5587\u561b\u4eec\u4fbf\u4ece\u56db\u9762\u516b\u65b9\u8d76\u6765\uff0c\u6c47\u6210\u4e86\u4e00\u80a1\u4eba\u6d41\uff0c\u5411\u4e2d\u95f4\u4e00\u5ea7\u9ad8\u5927\u7684\u6bbf\u5b57 \u6d8c\u53bb\u3002\u59ec\u6653\u98ce\u604d\u7136\u5927\u609f\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u4eca\u665a\u6709\u6781\u5176\u91cd\u8981\u7684\u805a\u4f1a\uff0c\u804c\u4f4d\u9ad8\u6b66\u529f\u597d\u7684\u5587\u561b\u5f53\u7136\u90fd\u7559 \u5728\u5bfa\u4e2d\uff0c\u6d3e\u51fa\u53bb\u5de1\u591c\u7684\u90a3\u53ea\u80fd\u662f\u672c\u9886\u4f4e\u5fae\u3001\u65e0\u5173\u91cd\u8981\u4e4b\u8f88\u4e86\u3002\u602a\u4e0d\u5f97\u6211\u4eec\u80fd\u591f\u5982\u6b64\u987a\u5229\u901a\u8fc7 \u7981\u533a\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b49\u4e00\u884c\u56db\u4eba\u6df7\u5728\u5587\u561b\u7fa4\u4e2d\uff0c\u66f4\u6ca1\u4eba\u6ce8\u610f\u4ed6\u4eec\uff0c\u5927\u949f\u6572\u5b8c\u4e86\u4e8c\u5341\u4e00\u4e0b\uff0c\u4ed6\u4eec\u4e5f\u5df2\u968f \u7740\u4eba\u6d41\uff0c\u6d8c\u8fdb\u4e86\u90a3\u5ea7\u6bbf\u5b57\u3002 \u3000\u3000\u6bbf\u4e2d\u6709\u5ea7\u795e\u9f9b\uff0c\u4f9b\u5949\u7740\u4ed6\u4eec\u6559\u7956\u7684\u91d1\u8eab\uff0c\u5ea7\u4e0b\u6709\u5f20\u6f06\u91d1\u4ea4\u6905\uff0c\u60f3\u5fc5\u662f\u767d\u6559\u6cd5\u738b\u7684\u5ea7\u4f4d\u3002 \u4e24\u65c1\u53e6\u6709\u56db\u5f20\u6905\u5b50\uff0c\u662f\u56db\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u7684\u5ea7\u4f4d\uff0c\u4e5f\u90fd\u5728\u7a7a\u7740\u3002\u59ec\u6653\u98ce\u5fc3\u9053\uff1a\u201c\u539f\u6765\u9996\u8111\u7684\u4eba\u7269 \u90fd\u8fd8\u672a\u51fa\u6765\u3002\u201d\u8fd9\u65f6\u53c8\u6709\u4eba\u7aef\u4e00\u5f20\u6905\u5b50\u51fa\u6765\uff0c\u653e\u5728\u6cd5\u738b\u5ea7\u4f4d\u7684\u5bf9\u9762\u3002\u770b\u6765\u8fd9\u628a\u4ea4\u6905\u7684\u4e3b\u4eba\uff0c \u5730\u4f4d\u663e\u7136\u5728\u90a3\u56db\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u4e4b\u4e0a\uff0c\u5374\u4e0d\u77e5\u662f\u8c01\u5750\u7684\u3002\u5176\u4ed6\u7684\u5587\u561b\uff0c\u90fd\u6ca1\u6709\u5ea7\u4f4d\uff0c\u53ea\u6309\u7740\u804c\u4f4d \u9ad8\u4f4e\uff0c\u4e00\u6392\u4e00\u6392\u7684\u7ad9\u7acb\uff0c\u804c\u4f4d\u9ad8\u7684\u5728\u524d\uff0c\u4f4e\u7684\u5728\u540e\u3002\u59ec\u6653\u98ce\u7b49\u4eba\u6df7\u5728\u4e2d\u95f4\u7684\u884c\u5217\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4e00\u7fa4\u4eba\u9ad8\u58f0\u6b22\u547c\uff0c\u53ea\u89c1\u4e00\u4e2a\u8eab\u6750\u9ad8\u5927\u7684\u5587\u561b\uff0c\u5728\u8bb8\u591a\u4eba\u7c07\u62e5\u4e4b\u4e0b\u8d70\u8fdb\u6765\uff0c\u80cc\u540e \u7d27\u7d27\u8ddf\u968f\u7740\u7684\u662f\u4e09\u4e2a\u62a4\u6cd5\u5f1f\u5b50\uff0c\u8eac\u8170\u54c8\u80cc\uff0c\u5bf9\u90a3\u5587\u561b\u7684\u6001\u5ea6\u751a\u4e3a\u606d\u8c28\uff0c\u59ec\u6653\u98ce\u4e0d\u7981\u53c8\u662f\u4e00 \u60ca\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5728\u62a4\u6cd5\u5f1f\u5b50\u4e0e\u6cd5\u738b\u540c\u65f6\u51fa\u73b0\u7684\u573a\u5408\uff0c\u62a4\u6cd5\u5f1f\u5b50\u603b\u662f\u968f\u4f8d\u770b\u6cd5\u738b\u7684\uff0c\u8fd9\u4eba\u662f \u8c01\uff0c\u5982\u6b64\u50ed\u8d8a\uff1f\u8fd9\u4e09\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u53c8\u4e3a\u4f55\u968f\u4f8d\u7740\u4ed6\uff1f\u8fd8\u6709\u4e00\u4e2a\u5462\uff0c\u5374\u53c8\u4e3a\u4f55\u6765\u89c1\u51fa\u73b0\uff1f\u201d\u4ed6\u5411 \u90a3\u5587\u561b\u6253\u91cf\u4e86\u4e00\u4e0b\uff0c\u53ea\u89c1\u4ed6\u4e24\u8fb9\u592a\u9633\u7a74\u5fae\u5fae\u9f13\u8d77\uff0c\u53cc\u76ee\u795e\u5149\u6e5b\u7136\uff0c\u59ec\u6653\u98ce\u662f\u6b66\u5b66\u884c\u5bb6\uff0c\u4e00\u770b \u5c31\u77e5\u9053\u8fd9\u4e2a\u5587\u561b\u7684\u5185\u529f\u6781\u4e3a\u9ad8\u6df1\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u53ea\u4f2f\u53ea\u6709\u5510\u6653\u6f9c\u3001\u91d1\u4e16\u9057\u3001\u75db\u7985\u4e0a\u4eba\u3001\u767d\u6559\u6cd5 \u738b\u3001\u91d1\u5149\u5927\u5e08\u7b49\u6709\u9650\u51e0\u4eba\uff0c\u53ef\u4ee5\u4e0e\u4e4b\u5339\u654c\u3002 \u3000\u3000\u8fd9\u5587\u561b\u51fa\u6765\uff0c\u6bbf\u4e2d\u6709\u4e00\u7fa4\u4eba\u5728\u6b22\u547c\uff0c\u53e6\u4e00\u7fa4\u4eba\u5219\u6728\u7136\u6beb\u65e0\u8868\u60c5\u3002\u59ec\u6653\u98ce\u4ece\u6b22\u547c\u58f0\u97f3\u4e2d\uff0c \u77e5\u9053\u8fd9\u4e2a\u5587\u561b\u7684\u6cd5\u53f7\u662f\u201c\u5b54\u96c0\u660e\u4f26\u738b\u201d\u3002\u5b54\u96c0\u662f\u4f5b\u6559\u5723\u9e1f\uff0c\u6cd5\u53f7\u4e2d\u53c8\u6709\u4e00\u4e2a\u201c\u738b\u201d\u5b57\uff0c\u53ef\u60f3 \u800c\u77e5\uff0c\u5730\u4f4d\u5f53\u7136\u662f\u975e\u5e38\u91cd\u8981\u7684\u4e86\uff01\u6b63\u662f\uff1a \u3000\u3000\u53ea\u56e0\u5bcc\u8d35\u8363\u534e\u5ff5\uff0c\u7adf\u81f4\u9ad8\u50e7\u8d77\u5185\u4e89\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u516b\u56de\u3000\u53c8\u89c1\u7a77\u8fb9\u817e\u5251\u6c14\u3000\u5ffd\u95fb\u57df\u5916\u8d77\u98ce\u96f7 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e8c\u5341\u516b\u56de\u3000\u53c8\u89c1\u7a77\u8fb9\u817e\u5251\u6c14\u3000\u5ffd\u95fb\u57df\u5916\u8d77\u98ce\u96f7 \u3000\u3000\u59ec\u6653\u98ce\u540e\u9762\u4e00\u4e2a\u5587\u561b\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u8fd8\u6ca1\u6709\u89c1\u8fc7\u8fd9\u4f4d\u5e08\u53d4\u5427\uff1f\u4ed6\u51fa\u5916\u4e91\u6e38\u4e86\u5341\u591a\u5e74\uff0c \u56de\u6765\u4e5f\u8fd8\u6ca1\u51e0\u5929\u5462\u3002\u73b0\u5728\u662f\u7b2c\u4e00\u6b21\u516c\u5f00\u9732\u9762\u3002\u201d\u4ed6\u53f3\u4fa7\u4e00\u4e2a\u5c0f\u5587\u561b\u9053\uff1a\u201c\u542c\u8bf4\u5e08\u53d4\u4e0e\u5e08\u7236\u4e3a \u4e86\u4e00\u4ef6\u5927\u4e8b\u4e89\u6267\u3002\u6240\u4ee5\u624d\u53ec\u96c6\u8fd9\u6b21\u5927\u4f1a\u7684\u3002\u4f46\u90a3\u51e0\u4f4d\u5e08\u5144\u5374\u4e0d\u80af\u544a\u8bc9\u6211\u662f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6211\u53ea\u542c \u5f97\u4ed6\u4eec\u5728\u4e89\u5435\uff0c\u6709\u4eba\u62e5\u62a4\u5e08\u53d4\uff0c\u6709\u4eba\u62e5\u62a4\u5e08\u7236\uff0c\u5e08\u5144\u4f60\u53ef\u77e5\u9053\u8be6\u60c5\uff0c\u5230\u5e95\u662f\u8c01\u5bf9\u5462\uff1f\u201d \u3000\u3000\u90a3\u5e74\u7eaa\u8f83\u8001\u7684\u5587\u561b\u4f4e\u4f4e\u5618\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u54b1\u4eec\u53ef\u4ee5\u79c1\u4e0b\u8c08\u8bba\u7684\uff0c\u4f60\u4e5f\u4e0d\u7528\u5fc3 \u6025\uff0c\u7b49\u4f1a\u51e0\u5e08\u5c0a\u81ea\u4f1a\u5f53\u4f17\u5ba3\u5e03\u3002\u201d\u59ec\u6653\u98ce\u8fd9\u624d\u77e5\u9053\uff0c\u8fd9\u4f4d\u201c\u5b54\u96c0\u660e\u4f26\u738b\u201d\u539f\u6765\u5c31\u662f\u6cd5\u738b\u7684\u5e08 \u5f1f\uff0c\u5fc3\u91cc\u660e\u767d\u4e86\u51e0\u5206\u3002\u4ed6\u53c8\u6ce8\u610f\u5230\u201c\u5b54\u96c0\u660e\u4f26\u738b\u201d\u7684\u968f\u4ece\u4e4b\u4e2d\uff0c\u6709\u4e24\u4e2a\u4eba\u6b63\u662f\u65e5\u95f4\u5728\u6c99\u6f20\u4e0a \u7528\u94c1\u9524\u6253\u4ed6\u7684\u90a3\u4e24\u4e2a\u6c49\u5b50\u3002\u4e0d\u8fc7\u73b0\u5728\u5df2\u6362\u4e0a\u4e86\u5587\u561b\u7684\u88c5\u675f\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u5b54\u96c0\u660e\u4f26\u4e3b\u8d70\u5230\u4e86\u6cd5\u738b\u5ea7\u4f4d\u5bf9\u9762\u7684\u90a3\u5f20\u6905\u5b50\u65c1\u8fb9\uff0c\u90a3\u4e09\u5341\u62a4\u6cd5\u5f1f\u4e8e\u5df2\u7ed9\u4ed6\u6276\u597d\u6905 \u5b50\uff0c\u4f46\u4ed6\u5374\u5e76\u4e0d\u5750\u4e0b\u3002\u90a3\u4e09\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u5927\u7ea6\u56e0\u4ed6\u6ca1\u6709\u5c31\u5ea7\uff0c\u56e0\u6b64\u4ed6\u4eec\u867d\u6709\u5ea7\u4f4d\uff0c\u4e5f\u4e0d\u6562\u53bb \u5750\uff0c\u4ecd\u7136\u968f\u4f8d\u5728\u5b54\u96c0\u660e\u4f26\u738b\u7684\u8eab\u540e\u3002 \u3000\u3000\u4f17\u5587\u561b\u6b63\u5728\u7a83\u7a83\u79c1\u8bae\uff0c\u5ffd\u542c\u5f97\u4e5d\u73af\u9521\u6756\u6447\u52a8\u7684\u58f0\u97f3\uff0c\u767b\u65f6\u8bf8\u58f0\u9759\u6b62\uff0c\u53ea\u89c1\u767d\u6559\u6cd5\u738b\u5df2\u5728 \u5341\u516d\u4e2a\u4eea\u4ed7\u50e7\u968f\u4f8d\u4e4b\u4e0b\u51fa\u6765\uff0c\u4ed6\u540e\u9762\u53ea\u8ddf\u7740\u4e00\u4e2a\u62a4\u6cd5\u5f1f\u5b50\uff0c\u4e0d\u8fc7\u90fd\u662f\u9996\u5ea7\u62a4\u6cd5\u5f1f\u5b50\u3002 \u3000\u3000\u9996\u5ea7\u62a4\u6cd5\u5f1f\u5b50\u5728\u6559\u7956\u7684\u795e\u9f9b\u524d\u70b9\u4e0a\u4e86\u4e09\u70db\u9999\uff0c\u767d\u6559\u6cd5\u738b\u5408\u4ec0\u7977\u544a\uff0c\u59ec\u6653\u98ce\u542c\u89c9\u6700\u4e3a\u7075 \u654f\uff0c\u53ea\u542c\u4ed6\u8bf4\u7684\u662f\uff1a\u201c\u672c\u6559\u9762\u4e34\u91cd\u5927\u6289\u62e9\uff0c\u8bf7\u6559\u7956\u8d50\u4e0e\u5f1f\u5b50\u5b9a\u529b\uff0c\u514d\u53d7\u90aa\u9b54\u5916\u9053\u6240\u8bf1\u3002\u201d\u5f53 \u6cd5\u738b\u8bda\u5fc3\u7977\u544a\u4e4b\u65f6\uff0c\u4ed6\u5e08\u5f1f\u7684\u5634\u89d2\u5374\u9732\u51fa\u4e00\u4e1d\u51b7\u7b11\u3002 \u3000\u3000\u6cd5\u738b\u7977\u544a\u4e4b\u540e\uff0c\u5411\u5b54\u96c0\u660e\u4f26\u738b\u548c\u90a3\u51e0\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u6325\u624b\u9053\uff1a\u201c\u4f60\u4eec\u5750\u4e0b\u6765\u8bf4\u5427\u3002\u201d\u5b54\u96c0\u660e \u4f26\u738b\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5f85\u5927\u4e8b\u51b3\u5b9a\u4e86\u518d\u5750\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u4f3c\u4e4e\u6cd5\u738b\u7684\u5ea7\u4f4d\u4e5f\u5f97\u53d6\u51b3\u4e8e\u8fd9 \u4ef6\u5927\u4e8b\uff0c\u82e5\u4e0d\u5706\u6ee1\u89e3\u51b3\uff0c\u6cd5\u738b\u7684\u5ea7\u4f4d\u4e5f\u4e0d\u5b89\u7a33\u3002\u7231\u62a4\u6cd5\u738b\u7684\u8fd9\u6d3e\u5587\u561b\uff0c\u5fc3\u4e2d\u6124\u6012\u4e4b\u6781\uff0c\u4f46\u56e0 \u4e3a\u6cd5\u738b\u6ca1\u6709\u8bf4\u8bdd\uff0c\u8c01\u4e5f\u4e0d\u6562\u5f00\u58f0\u3002 \u3000\u3000\u6cd5\u738b\u4e1d\u6beb\u6ca1\u6709\u6012\u58f0\uff0c\u9762\u5411\u50e7\u4f17\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u672c\u6559\u73b0\u5728\u6709\u4ef6\u5927\u4e8b\uff0c\u6709\u5173\u672c\u6559\u7684\u6c14\u8fd0\u5174 \u8870\u3002\u5b54\u96c0\u660e\u4f26\u738b\u5c31\u662f\u4e3a\u8fd9\u4ef6\u4e8b\u5954\u8dd1\u7684\u4eba\uff0c\u73b0\u5728\u5148\u8bf7\u4ed6\u548c\u4f60\u4eec\u8bf4\u8bf4\u4e8b\u60c5\u7684\u7ecf\u8fc7\u5427\uff0c\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u60c5\u5df2\u8fdb\u884c\u4e86\u597d\u51e0\u5e74\u4e86\uff0c\u539f\u6765\u4f60\u4e00\u76f4\u5728\u7792\u7740\u4ed6\u4eec\uff0c\u73b0\u5728\u624d \u8981\u6211\u5ba3\u5e03\u5417\uff1f\u201d \u3000\u3000\u6cd5\u738b\u795e\u60c5\u8083\u7a46\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5fdd\u4e3a\u672c\u6559\u4e4b\u4e3b\uff0c\u6709\u6743\u51b3\u5b9a\u5982\u4f55\u5904\u7406\uff0c\u5018\u82e5\u6211\u63aa\u65bd\u4e0d\u5f53\uff0c \u7b49\u4e0b\u53ef\u4ed8\u8bf8\u516c\u8bba\u3002\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u88ab\u4ed6\u5e08\u5144\u8fd9\u4e48\u4e00\u8bf4\uff0c\u9762\u8272\u751a\u4e3a\u96be\u770b\uff0c\u4f46\u4e5f\u53ea\u5f97\u6536\u8d77\u98de\u626c\u8dcb\u6248\u4e4b\u6001\uff0c\u8fc7\u4e86\u534a \u664c\uff0c\u8baa\u8baa\u8bf4\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u90a3\u5c31\u7531\u6211\u6765\u5ba3\u5e03\uff0c\u4ed8\u8bf8\u516c\u51b3\u5427\u3002\u201d \u3000\u3000\u4f17\u5587\u561b\u51dd\u795e\u9759\u6c14\uff0c\u53ea\u542c\u5f97\u90a3\u5b54\u96c0\u660e\u4f26\u738b\u8a00\u9053\uff1a\u201c\u8fd9\u4e8b\u8bf4\u6765\u8bdd\u957f\uff0c\u4f46\u4e5f\u7b80\u5355\u5f97\u5f88\uff0c\u90a3\u5c31\u662f \u5c3c\u6cca\u5c14\u738b\u51b3\u610f\u5949\u54b1\u4eec\u7684\u767d\u6559\u4e3a\u56fd\u6559\uff0c\u9080\u8bf7\u54b1\u4eec\u9102\u514b\u6c81\u5bab\u6240\u6709\u7684\u50e7\u4f17\uff0c\u90fd\u8fc1\u5230\u5b83\u7684\u4eac\u57ce\u52a0\u5fb7\u6ee1 \u90fd\u53bb\uff01\u201d \u3000\u3000\u8fd9\u4ef6\u4e8b\u60c5\u804c\u4f4d\u8f83\u9ad8\u7684\u50e7\u4fa3\u5dee\u4e0d\u591a\u90fd\u5df2\u77e5\u9053\uff0c\u4f46\u4e5f\u6709\u8bb8\u591a\u5c0f\u5587\u561b\u8fd8\u672a\u77e5\u9053\u7684\uff0c\u56e0\u6b64\u5b54\u96c0\u660e \u4f26\u738b\u6b64\u8a00\u4e00\u51fa\uff0c\u5168\u573a\u767b\u65f6\u54c4\u52a8\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u505c\u4e86\u4e00\u4f1a\uff0c\u5f85\u4f17\u4eba\u7684\u60c5\u7eea\u7a0d\u7a0d\u5e73\u9759\uff0c\u518d\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u662f\u7531\u6211\u4ee3\u8868\u672c \u6559\u548c\u5c3c\u6cca\u5c14\u738b\u5546\u8c08\u7684\uff0c\u73b0\u5728\u6211\u518d\u4ece\u5934\u62a5\u544a\u4e8b\u60c5\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u201c\u5927\u7ea6\u4e03\u5e74\u4e4b\u524d\uff0c\u6211\u8def\u8fc7\u5c3c\u6cca\u5c14\uff0c\u56fd\u738b\u5bf9\u6211\u975e\u5e38\u5c0a\u656c\uff0c\u9080\u6211\u5230\u4ed6\u7684\u7687\u5bab\u91cc\u4f4f\u4e86\u51e0\u5929\uff0c\u4ed6 \u6df1\u6089\u672c\u6559\u7684\u60c5\u5f62\uff0c\u5bf9\u672c\u6559\u7684\u88ab\u8feb\u56f0\u5904\u9752\u6d77\u4e00\u9685\uff0c\u5341\u5206\u540c\u60c5\uff0c\u5bf9\u6559\u4e3b\u5e08\u5144\u7684\u5fb7\u671b\u6b66\u529f\uff0c\u4e5f\u6781\u4e3a \u94a6\u4ef0\uff1a\u6211\u4eec\u5546\u8c08\u4e4b\u540e\uff0c\u4ed6\u5c31\u6709\u610f\u8bf7\u6559\u4e3b\u5e08\u5144\u524d\u5f80\u52a0\u5fb7\u6ee1\u90fd\u4e0e\u4ed6\u4f1a\u9762\uff0c\u7136\u540e\u518d\u8c08\u5408\u4f5c\u7684\u7ec6\u8282\u3002 \u3000\u3000\u201c\u90a3\u4e00\u5e74\uff0c\u5c3c\u6cca\u5c14\u738b\u5c31\u6d3e\u9063\u4e86\u4f7f\u8005\uff0c\u5e76\u5e26\u4e86\u6211\u7684\u4e66\u4fe1\uff0c\u5230\u8fc7\u672c\u5bfa\u89c1\u8fc7\u6559\u4e3b\u5e08\u5144\uff0c\u4f46\u5e08\u5144 \u5374\u4e00\u5473\u63a8\u5ef6\uff0c\u8fdf\u8fdf\u4e0d\u80af\u7b54\u590d\uff0c\u4e5f\u6ca1\u6709\u5230\u52a0\u5fb7\u6ee1\u90fd\u56de\u62dc\u56fd\u738b\u3002\u201d \u3000\u3000\u201c\u8fd9\u51e0\u5e74\u6765\u636e\u6211\u6240\u77e5\uff0c\u5c3c\u6cca\u5c14\u738b\u5df2\u6d3e\u8fc7\u4e09\u6b21\u4f7f\u8005\u6765\u4e86\uff0c\u6761\u4ef6\u4e00\u6b21\u6bd4\u4e00\u6b21\u4f18\u539a\u3002\u4f46\u662f\u5e08\u5144 \u8fd8\u6ca1\u6709\u786e\u5b9e\u7684\u7b54\u590d\uff1a\u56e0\u6b64\u6211\u53ea\u597d\u4eb2\u81ea\u56de\u6765\uff0c\u4f20\u8fbe\u5c3c\u6cca\u5c14\u738b\u7684\u610f\u65e8\uff0c\u5e76\u8bf7\u9616\u5bfa\u50e7\u4f17\u4f5c\u51fa\u516c\u51b3\u3002\u201d \u3000\u3000\u201c\u56fd\u738b\u53eb\u6211\u5e26\u56de\u6765\u7684\u610f\u65e8\uff0c\u9664\u4e86\u4e0a\u8ff0\u7684\u5efa\u8bae\u4e4b\u5916\uff0c\u8fd8\u6dfb\u4e86\u4e00\u6837\uff0c\u90a3\u662f\u4e13\u4e3a\u6559\u4e3b\u5e08\u5144\u800c\u8bbe \u7684\u3002\u54b1\u4eec\u767d\u6559\u5018\u82e5\u8fc1\u79fb\u8be5\u56fd\uff0c\u4ed6\u613f\u610f\u62e5\u6234\u6559\u4e3b\u664b\u4f4d\u2018\u6d3b\u4f5b\u2019\uff0c\u4e0e\u8fbe\u8d56\u73ed\u7985\u5728\u897f\u85cf\u7684\u5730\u4f4d\u76f8\u540c\u3002 \u3000\u3000\u201c\u5c3c\u6cca\u5c14\u56fd\u662f\u4f5b\u6559\u53e4\u56fd\uff0c\u662f\u91ca\u8fe6\u4f5b\u79df\u8bde\u751f\u7684\u5723\u5730\uff08\u6309\u5c3c\u6cca\u5c14\u65e7\u5c5e\u5370\u5ea6\uff0c\u91ca\u8fe6\u725f\u5c3c\u8bde\u751f\u4e8e \u8be5\u56fd\u7684\u5170\u6bd7\u5c3c\u56ed\uff09\uff0c\u54b1\u4eec\u767d\u6559\u82e5\u5f97\u56fd\u738b\u5c0a\u4e3a\u56fd\u6559\uff0c\u90a3\u771f\u662f\u6781\u96be\u5f97\u7684\u673a\u9047\u4e86\u3002 \u3000\u3000\u201c\u597d\u4e86\uff0c\u6211\u6240\u8981\u62a5\u544a\u7684\u5c31\u662f\u8fd9\u4e48\u591a\u4e86\u3002\u603b\u800c\u8a00\u4e4b\uff0c\u5c3c\u6cca\u5c14\u738b\u5bf9\u54b1\u4eec\u767d\u6559\u4e0e\u6559\u4e3b\u7684\u5c0a\u5d07\uff0c \u90a3\u662f\u81f3\u77e3\u5c3d\u77e3\uff0c\u8511\u4ee5\u52a0\u77e3\uff01\u81f3\u4e8e\u4ed6\u7684\u597d\u610f\uff0c\u4f60\u4eec\u613f\u4e0d\u613f\u610f\u63a5\u53d7\uff0c\u90a3\u5c31\u8981\u8bf7\u4f60\u4eec\u4f5c\u51fa\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u62a5\u544a\u4e4b\u540e\uff0c\u7fa4\u60c5\u8038\u52a8\uff0c\u8bae\u8bba\u7eb7\u7eb7\uff0c\u4f46\u8fc7\u4e86\u51e0\u4e4e\u534a\u4e2a\u65f6\u8fb0\uff1b\u8fd8\u6ca1\u6709\u4e00\u4e2a\u4eba\u6562\u7ad9\u51fa\u6765\u8bf4\u8bdd\u3002 \u9996\u5ea7\u62a4\u6cd5\u5f1f\u5b50\u574f\u987e\u5168\u573a\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5f90\u5f90\u8bf4\u9053\uff0c\u201c\u5179\u4e8b\u4f53\u5927\uff0c\u8fd8\u662f\u8bf7\u6559\u4e3b\u7ed9\u6211\u4eec\u62ff\u5b9a\u4e3b\u610f \u5427\u3002\u201d\u767b\u65f6\u6709\u5341\u51e0\u4e2a\u9ad8\u7ea7\u50e7\u4fa3\u540c\u58f0\u9644\u548c\uff1a\u201c\u5bf9\uff0c\u5bf9\uff01\u6559\u4e3b\u9ad8\u77bb\u8fdc\u77a9\uff0c\u89c1\u8bc6\u5f53\u7136\u6bd4\u54b1\u4eec\u9ad8\u660e\u5f97 \u591a\uff0c\u6211\u4eec\u90fd\u613f\u670d\u4ece\u6559\u4e3b\u7684\u51b3\u5b9a\u3002\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u672c\u6765\u5df1\u8054\u7edc\u4e86\u8bb8\u591a\u4eba\uff0c\u4f46\u89c1\u62e5\u62a4\u4ed6\u5e08\u5144\u7684\u4f9d\u7136\u4e0d\u5c11\uff0c\u5fc3\u91cc\u751a\u4e0d\u9ad8\u5174\u3001\u65e0\u53ef\u5948 \u4f55\uff0c\u53ea\u5f97\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u7684\u4e3b\u610f\u62ff\u5b9a\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u81ea\u6559\u6cd5\u738b\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u6211\u60f3\u5148\u542c\u542c\u4f60\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4e3b\u5f20\u63a5\u53d7\u5c3c\u6cca\u5c14\u738b\u7684\u597d\u610f\u7684\u3002\u201d\u6b64\u4e8b\u5bf9\u54b1\u4eec\u6709\u767e\u5229\u800c\u65e0\u4e00 \u5bb3\uff0c\u4f55\u7528\u72b9\u7591\uff1f\u201d \u3000\u3000\u62a4\u6cd5\u5f1f\u5b50\u4e4b\u4e00\u7684\u8fe6\u6bd7\u7f57\u8d77\u7acb\u8bf4\u9053\uff1a\u201c\u672c\u6559\u672c\u6765\u662f\u5728\u897f\u85cf\u521b\u6559\uff0c\u4e0e\u7ea2\u6559\u9ec4\u6559\u9f0e\u8db3\u800c\u4e09\uff0c\u540e \u6765\u9ec4\u6559\u5174\u8d77\uff0c\u5c06\u54b1\u4eec\u9010\u51fa\u897f\u85cf\uff0c\u767e\u4f59\u5e74\u6765\uff1a\u54b1\u4eec\u5c40\u5904\u9752\u6d77\u4e00\u9685\uff0c\u90c1\u90c1\u96be\u4f38\uff0c\u73b0\u5728\u96be\u5f97\u6709\u6b64\u673a \u9047\uff0c\u53ef\u4ee5\u53d1\u626c\u672c\u6559\uff0c\u5b8f\u6cd5\u5229\u751f\uff0c\u7109\u53ef\u60dc\u8fc7\uff1f\u201d \u3000\u3000\u53c8\u4e00\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u65f6\u6e21\u8d77\u7acb\u8bf4\u9053\uff1a\u201c\u5b54\u96c0\u738b\u4e0e\u8fe6\u6bd7\u7f57\u5e08\u5144\u4e4b\u8a00\u826f\u662f\uff0c\u8bd5\u60f3\u4ee5\u54b1\u4eec\u73b0\u5728\u7684\u5883 \u9047\uff0c\u51b3\u96be\u6062\u590d\u7956\u4e1a\uff0c\u9ec4\u6559\u5728\u897f\u85cf\u5df2\u662f\u6839\u6df1\u8482\u56fa\uff0c\u53c8\u6709\u6e05\u5ef7\u9881\u7ed9\u91d1\u672c\u5df2\u74f6\uff0c\u786e\u7acb\u4e86\u2018\u6d3b\u4f5b\u8f6c \u751f\u2019\u7684\u5236\u5ea6\uff0c\u90a3\u8fd8\u6709\u54b1\u4eec\u63d2\u8db3\u7684\u4f59\u5730\u5417\uff1f\u65e2\u4e0d\u80fd\u91cd\u56de\u6545\u4e0a\uff0c\u4f55\u5982\u5f02\u5730\u6c42\u5b58\uff1f\u56fe\u8c0b\u53d1\u5c55\uff01\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u6177\u6168\u9648\u8f9e\uff0c\u8bf4\u5230\u672c\u8eab\u5229\u5bb3\uff0c\u58f0\u6cea\u4ff1\u4e0b\uff0c\u786e\u5b9e\u6253\u52a8\u4e86\u8bb8\u591a\u4eba\u5fc3\uff0c\u573a\u4e2d\u6c14 \u6c1b\uff0c\u663e\u7136\u5bf9\u5b54\u96c0\u660e\u4f26\u738b\u5927\u5927\u6709\u5229\u3002 \u3000\u3000\u9996\u5ea7\u62a4\u6cd5\u5f1f\u5b50\u5ffd\u5730\u4e5f\u8d77\u7acb\u8bf4\u9053\uff1a\u201c\u5b54\u96c0\u738b\u8bf4\u6b64\u4e8b\u5bf9\u54b1\u4eec\u6709\u767e\u5229\u800c\u65e0\u4e00\u5bb3\uff0c\u5f1f\u5b50\u4e0d\u654f\uff0c\u5bf9 \u6b64\u5b9e\u611f\u6000\u7591\uff01\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u5706\u7741\u53cc\u773c\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u6000\u7591\u4ec0\u4e48\uff1f\u5c3c\u6cca\u5c14\u738b\u5df2\u4e3a\u54b1\u4eec\u5728\u4ed6\u7684\u4eac\u57ce\u4fee\u5efa\u4e86 \u4e00\u5ea7\u5927\u5bab\u6bbf\u4e86\uff0c\u8fd9\u662f\u6211\u4eb2\u81ea\u7763\u5de5\u4fee\u5efa\u7684\uff0c\u4f60\u8fd8\u80fd\u6000\u7591\u4ed6\u7684\u8bda\u610f\u4e48\uff1f\u201d \u3000\u3000\u9996\u5ea7\u62a4\u6cd5\u5f1f\u5b50\u51b7\u9759\u8bf4\u9053\uff1a\u201c\u6211\u5e76\u975e\u6000\u7591\u5c3c\u6cca\u5c14\u738b\u8a00\u800c\u65e0\u4fe1\uff0c\u4f46\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\u5462\uff1f\u5f53 \u771f\u662f\u4e3a\u4e86\u5c0a\u5d07\u672c\u6559\u5417\uff1f\u6015\u4e0d\u89c1\u5f97\uff0c\u6211\u6000\u7591\u4ed6\u522b\u6709\u4f01\u56fe\uff01\u201d\u5b54\u96c0\u660e\u4f26\u738b\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4ee5\u5c0f \u4eba\u4e4b\u5fc3\u5ea6\u541b\u4e4b\u5b50\u8179\uff01\u201d\u767d\u6559\u6cd5\u5de5\u6325\u4e86\u6325\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u4e0d\u8981\u5148\u6307\u8d23\u4ed6\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u65e2 \u662f\u4ed8\u4e4b\u516c\u8bae\uff0c\u4e5f\u8be5\u8ba9\u4ed6\u628a\u5fc3\u91cc\u7684\u6000\u7591\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u9996\u5ea7\u62a4\u6cd5\u5f1f\u5b50\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u6000\u7591\u5c3c\u6cca\u5c14\u738b\u662f\u60f3\u6311\u62e8\u54b1\u4eec\u4e0e\u9ec4\u6559\u4f5c\u9e6c\u868c\u4e4b\u4e89\uff0c\u4ed6\u597d\u6e14\u4eba \u5f97\u5229\u3002\u5404\u4f4d\u957f\u8001\u5927\u7ea6\u8fd8\u672a\u66fe\u5fd8\u8bb0\uff0c\u8ddd\u4eca\u7ea6\u4e8c\u5341\u5e74\u524d\uff0c\u5c3c\u6cca\u5c14\u738b\u66fe\u6709\u4e00\u6b21\u51fa\u5175\u897f\u85cf\uff0c\u9669\u4e9b\u5175\u8fde \u7978\u7ed3\uff0c\u540e\u6765\u5e78\u5f97\u671d\u5ef7\u7684\u5927\u519b\u8d76\u81f3\uff0c\u53c8\u6709\u5510\u7ecf\u5929\u592b\u5987\u51fa\u6765\u8c03\u89e3\u3002\u65b9\u59cb\u5316\u4e8e\u6208\u800c\u4e3a\u7389\u5e1b\u3002\u5f53\u65f6\u5c3c \u6cca\u5c14\u738b\u4e5f\u66fe\u4ee5\u5229\u76f8\u8bf1\uff0c\u7b54\u5141\u6276\u52a9\u54b1\u4eec\u91cd\u8fd4\u897f\u85cf\uff0c\u5e78\u4e8f\u5e08\u5c0a\u4e0d\u4e3a\u6240\u52a8\uff0c\u65b9\u59cb\u514d\u4e86\u4e00\u573a\u5927\u7978\u3002 \uff08\u4e8b\u8be6\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u524d\u8f66\u4e4b\u9274\uff0c\u7701\u53ef\u4e0d\u614e\uff01\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u9053\uff1a\u201c\u8fd9\u662f\u8fc7\u53bb\u7684\u4e8b\u4e86\uff0c\u73b0\u5728\u4ed6\u662f\u8bf7\u54b1\u4eec\u8fc1\u79fb\u5230\u4ed6\u7684\u56fd\u571f\uff0c\u5949\u672c\u6559\u4e3a\u56fd\u6559\uff0c \u53c8\u4e0d\u662f\u8981\u81ea\u4eec\u52a9\u4ed6\u8fdb\u4fb5\u897f\u85cf\uff0c\u4f60\u591a\u7591\u4f5c\u751a\uff1f\u201d \u3000\u3000\u6709\u5ea7\u62a4\u6cd5\u5f1f\u5b50\u9053\uff1a\u201c\u975e\u662f\u5f1f\u5b50\u591a\u7591\uff0c\u5c3c\u6cca\u5c14\u73b0\u5728\u8fd9\u4f4d\u56fd\u738b\u5c31\u662f\u4ece\u524d\u6325\u5175\u5165\u85cf\u7684\u90a3\u4f4d\u56fd \u738b\uff0c\u5f53\u5e74\u4ed6\u683c\u4e8e\u5f62\u52bf\uff0c\u52c9\u5f3a\u9000\u5175\uff0c\u4f60\u53c8\u600e\u4fdd\u5f97\u4f4f\u4ed6\u6ca1\u6709\u5377\u571f\u91cd\u6765\u7684\u6253\u7b97\uff1f\u8fd8\u6709\u4e00\u5c42\uff0c\u54b1\u4eec\u4e16 \u4e16\u4ee3\u4ee3\u90fd\u662f\u751f\u4e8e\u65af\uff0c\u957f\u4e8e\u65af\uff0c\u597d\u574f\u90fd\u662f\u5728\u672c\u4e61\u672c\u571f\uff0c\u4e00\u65e6\u8fdc\u9002\u5f02\u56fd\uff0c\u5bc4\u4eba\u7bf1\u4e0b\uff0c\u7eb5\u7136\u5c0a\u8d35\uff0c \u4e5f\u603b\u662f\u65e0\u6839\u7684\u6d6e\u840d\uff01\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u51b7\u7b11\u9053\uff1a\u201c\u4f9d\u4f60\u6240\u8bf4\uff0c\u4f5b\u95e8\u4e2d\u90a3\u4e9b\u79bb\u4e61\u522b\u4e95\u3001\u8fdc\u9002\u5f02\u56fd\u7684\u53e4\u5723\u524d\u8d24\uff0c\u90fd\u662f\u4e0d \u8db3\u6548\u6cd5\u7684\u4e86\uff1f\u6cd5\u663e\u8fdc\u822a\u6c42\u6cd5\u3001\u7384\u5958\u767d\u9a6c\u4f20\u7ecf\uff0c\u8fd9\u4e24\u4f4d\u9ad8\u50e7\u662f\u4ece\u4e2d\u56fd\u5f80\u5929\u7afa\u53bb\u7684\uff0c\u9e20\u6469\u7f57\u4ec0\u6765 \u534e\uff0c\u540e\u79e6\u738b\u59da\u5174\u5f85\u4ee5\u56fd\u5e08\u4e4b\u793c\uff0c\u4f5b\u6559\u4e43\u5927\u884c\u4e8e\u4e2d\u571f\uff1b\u8fbe\u6469\u7956\u5e08\u4e00\u82c7\u6e21\u6c5f\uff0c\u81f3\u5d69\u5c71\u9762\u58c1\u4e09\u5e74\uff0c \u4e2d\u56fd\u59cb\u6709\u5c11\u6797\u6d3e\u7684\u6b66\u529f\u3002\u8fd9\u4e24\u4f4d\u9ad8\u50e7\u662f\u4ece\u5929\u7afa\u6765\u534e\u7684\uff0c\u8bf7\u95ee\u5982\u6cd5\u663e\u3001\u7384\u5958\u3001\u9e20\u6469\u7f57\u4ec0\u3001\u8fbe\u6469 \u7956\u5e08\u7b49\u4eba\uff0c\u82e5\u90fd\u4f3c\u4f60\u8fd9\u822c\u9f20\u76ee\u5bf8\u5149\uff0c\u4ed6\u4eec\u7109\u80fd\u540d\u5782\u5343\u53e4\uff1f\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u535a\u5b66\u591a\u624d\uff0c\u80fd\u8a00\u5584\u8fa9\uff0c\u4ed6\u4e3e\u7684\u8fd9\u51e0\u4e2a\u4f8b\u5b50\uff0c\u53c8\u90fd\u662f\u4f5b\u95e8\u5f1f\u5b50\u4eba\u4eba\u719f\u77e5\u7684\u6545 \u4e8b\uff0c\u9996\u5ea7\u62a4\u6cd5\u5f1f\u5b50\u88ab\u4ed6\u9510\u5229\u7684\u8f9e\u950b\u9a73\u5012\uff0c\u5fc3\u4e2d\u867d\u7136\u4e0d\u670d\uff0c\u4e00\u65f6\u95f4\u5374\u8fd8\u60f3\u4e0d\u51fa\u5982\u4f55\u53f5\u9a73\u8fc7\u53bb\u3002 \u3000\u3000\u6cd5\u738b\u5ffd\u5730\u4e5f\u7ad9\u7acb\u8d77\u6765\uff0c\u7f13\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u6240\u4e3e\u7684\u8fd9\u51e0\u4f4d\u9ad8\u50e7\uff0c\u90fd\u662f\u4f5b\u95e8\u7684\u5927\u5fb7\u9ad8 \u8d24\uff0c\u8c01\u4e0d\u94a6\u656c\uff1f\u53ef\u662f\u8fd9\u4e9b\u4f8b\u5b50\u5374\u4e0d\u80fd\u4e0e\u4eca\u65e5\u4e4b\u4e8b\u76f8\u63d0\u5e76\u8bba\uff01 \u3000\u3000\u201c\u6cd5\u663e\u3001\u7384\u5958\u3001\u9e29\u6469\u7f57\u4ec0\u548c\u8fbe\u6469\u7956\u5e08\uff0c\u4ed6\u4eec\u90fd\u662f\u4ee5\u4e2a\u4eba\u7684\u8eab\u4efd\uff0c\u5230\u522b\u7684\u56fd\u5bb6\u6216\u662f\u53d6\u7ecf\u6216 \u662f\u4f20\u6cd5\u7684\uff0c\u4ed6\u4eec\u9664\u4e86\u53ea\u77e5\u5b8f\u626c\u4f5b\u6cd5\u5916\uff0c\u4e0d\u77e5\u5176\u4ed6\u3002\u5373\u5982\u9e20\u6469\u7f57\u4ec0\uff0c\u4ed6\u867d\u7136\u505a\u4e86\u540e\u79e6\u7684\u56fd\u5e08\uff0c \u4f46\u4ed6\u6bd5\u751f\u81f4\u529b\u7684\u4e43\u662f\u54c6\u91ca\u7ecf\u5178\uff0c\u8bd1\u51fa\u4e86\u91d1\u521a\u7ecf\u3001\u6cd5\u534e\u7ecf\u3001\u7ef4\u6469\u7ecf\u3001\u4e2d\u89c2\u4f26\u3001\u5341\u4e8c\u95e8\u8bba\u7b49\u4e09\u767e \u4f59\u5377\uff0c\u56e0\u800c\u540d\u5782\u4e0d\u673d\u3002\u5018\u82e5\u4ed6\u53ea\u662f\u8d2a\u6155\u2018\u56fd\u5e08\u2019\u7684\u865a\u8363\uff0c\u51b3\u4e0d\u80fd\u6709\u6b64\u6210\u5c31\u3002 \u3000\u3000\u201c\u5982\u4eca\u5c3c\u6cca\u5c14\u738b\u5374\u662f\u8981\u54b1\u4eec\u5168\u90e8\u50e7\u4f17\u8fc1\u79fb\u8be5\u56fd\uff0c\u5728\u4ed6\u90a3\u513f\u5f00\u5b97\u7acb\u6559\uff0c\u8fd9\u4e0d\u662f\u5206\u660e\u6446\u51fa\u4e0e \u897f\u85cf\u9ec4\u6559\u5bf9\u652f\u7acb\u5f62\u52bf\u5417\uff1f\u4e0d\u662f\u6211\u504f\u8892\u987b\u83e9\u63d0\uff08\u9996\u5ea7\u5f1f\u5b50\u4e4b\u540d\uff09\uff0c\u4ed6\u7684\u8bdd\u7684\u786e\u662f\u503c\u5f97\u4e09\u601d\u3002\u54b1 \u4eec\u4e0d\u8981\u7ed9\u4eba\u5229\u7528\u4e86\u3002\u54b1\u4eec\u73b0\u5728\u4e0e\u9ec4\u6559\u540c\u5904\u4e00\u56fd\uff0c\u7eb5\u6709\u4e0d\u548c\uff0c\u65e0\u4f24\u5927\u96c5\uff0c\u5018\u82e5\u5404\u4f9d\u4e00\u56fd\uff0c\u5206\u9053 \u626c\u9573\uff0c\u5f7c\u6b64\u90fd\u662f\u81f3\u9ad8\u65e0\u4e0a\u7684\uff0c\u2018\u6d3b\u4f5b\u2019\u90a3\u5c31\u5bb9\u6613\u7ed9\u91ce\u5fc3\u8005\u6240\u4e58\uff0c\u5236\u9020\u6df7\u4e71\u4e86\u3002 \u3000\u3000\u201c\u800c\u4e14\u636e\u6211\u6240\u77e5\uff0c\u5c3c\u6015\u5c14\u738b\u6050\u6015\u8fd8\u4e0d\u662f\u7740\u91cd\u5728\u8981\u54b1\u4eec\u4f20\u6559\uff0c\u800c\u662f\u770b\u4e2d\u4e86\u672c\u6d3e\u7684\u6b66\u529f\uff0c\u5e0c \u671b\u54b1\u4eec\u4e00\u53bb\uff0c\u589e\u5f3a\u4ed6\u7684\u5b9e\u529b\u3002\u5e08\u5f1f\uff0c\u542c\u8bf4\u4f60\u5728\u5c3c\u6cca\u5c14\u8fd9\u4e48\u591a\u5e74\uff0c\u5c31\u6ca1\u6709\u8bb2\u8fc7\u4e00\u6b21\u7ecf\uff0c\u5374\u7ed9\u5c3c \u6cca\u5c14\u738b\u8bad\u7ec3\u4e86\u4e09\u767e\u540d\u6b66\u58eb\uff0c\u8fd9\u662f\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u5c3c\u6cca\u5c14\u738b\u4ee5\u56fd\u5e08\u4e4b\u793c\u5f85\u6211\uff0c\u6211\u4e3a\u4ed6\u505a\u4e9b\u4e8b\u60c5\uff0c\u4e5f\u4e0d\u8fc7\u662f \u6295\u6843\u62a5\u674e\u800c\u5df2\uff0c\u5e08\u5144\uff0c\u4f60\u8981\u8d23\u5907\u6211\u4e48\uff1f\u201d \u3000\u3000\u6cd5\u738b\u8bf4\u9053\uff1a\u201c\u6211\u5e76\u975e\u8d23\u5907\u4f60\uff0c\u4e0d\u8fc7\u662f\u4f5c\u4e3a\u4e00\u4e2a\u4f8b\u5b50\uff0c\u9632\u8303\u672a\u6765\u53ef\u80fd\u53d1\u751f\u4e4b\u4e8b\u800c\u5df2\u3002\u8bd5 \u60f3\uff0c\u5047\u5982\u54b1\u4eec\u90fd\u8fc1\u79fb\u5230\u52a0\u5fb7\u6ee1\u90fd\uff0c\u5728\u90a3\u91cc\u5f00\u5b97\u7acb\u6559\uff0c\u63a5\u53d7\u5c3c\u6cca\u5c14\u738b\u7684\u4f9b\u517b\uff0c\u4e07\u4e00\u4ed6\u8981\u8fdb\u72af\u897f \u85cf\uff0c\u54b1\u4eec\u5982\u4f55\u81ea\u5904\uff1f\u82e5\u7136\u8896\u624b\u65c1\u89c2\uff0c\u90a3\u662f\u6709\u8d1f\u4e8e\u4ed6\uff0c\u82e5\u7136\u52a9\u4ed6\u8fdb\u72af\uff0c\u90a3\u662f\u4e0e\u9ec4\u6559\u81ea\u76f8\u6b8b\u6740\uff0c \u66f4\u5c5e\u4e07\u4e07\u4e0d\u53ef\u3002\u56e0\u6b64\u4e0e\u5176\u5f02\u65e5\u4e3a\u96be\uff0c\u4f55\u5982\u4eca\u65e5\u5b89\u4efd\u5b88\u5df1\uff1f\u201d \u3000\u3000\u6cd5\u738b\u6b47\u4e86\u4e00\u6b47\uff0c\u518d\u63a5\u7eed\u8bf4\u9053\uff1a\u201c\u800c\u4e14\u636e\u6211\u6240\u77e5\uff0c\u5c3c\u6cca\u5c14\u738b\u4e0e\u9a6c\u8428\u513f\u56fd\u738b\u4ea6\u5df2\u8ba2\u4e86\u76df\u7ea6\uff0c \u672c\u6708\u5341\u4e94\u65e5\u7684\u91d1\u9e70\u5bab\u4e4b\u4f1a\uff0c\u5c3c\u6cca\u5c14\u7684\u9ad8\u624b\u4ea6\u5c06\u6709\u5927\u6279\u524d\u6765\u3002\u9a6c\u8428\u513f\u56fd\u738b\u91ce\u5fc3\u52c3\u52c3\uff0c\u5468\u8c0b\u79f0\u9738 \u897f\u57df\u3002\u8fd9\u662f\u4eba\u5c3d\u7686\u77e5\u7684\u4e86\u3002\u5c3c\u6cca\u5c14\u738b\u4e0e\u4ed6\u6df1\u76f8\u7ed3\u7eb3\uff0c\u7528\u610f\u4f55\u5728\uff0c\u4e0d\u80fd\u65e0\u7591\uff01 \u3000\u3000\u201c\u4f5b\u95e8\u5f1f\u5b50\uff0c\u6700\u5fcc\u4e3a\u540d\u5229\u6240\u52a8\uff0c\u4f55\u51b5\u662f\u672a\u89c1\u5176\u5229\u5148\u89c1\u5176\u5bb3\u5462\uff1f\u56e0\u6b64\u6211\u7684\u610f\u601d\u662f\u4e00\u52a8\u4e0d\u5982 \u4e00\u9759\uff0c\u4ed6\u8981\u62e5\u6234\u6211\u664b\u4f4d\u2018\u6d3b\u4f5b\u2019\u7684\u2018\u597d\u610f\u2019\uff0c\u6211\u4e5f\u53ea\u597d\u656c\u8c22\u4e0d\u654f\u4e86\uff01\u201d \u3000\u3000\u6cd5\u738b\u8fd9\u756a\u8bdd\u5256\u6790\u5229\u5bb3\uff0c\u8bf4\u5f97\u6709\u7406\u6709\u60c5\uff0c\u4f1a\u573a\u7684\u7a7a\u6c14\u53c8\u4e3a\u4e4b\u4e00\u53d8\u3002\u53ef\u662f\u597d\u9ad8\u9a9b\u8fdc\u4e4b\u5fc3\uff0c\u51fa \u5bb6\u4eba\u4e5f\u5728\u6240\u96be\u514d\uff0c\u5bf9\u4e8e\u8fd9\u4e00\u4e9b\u672a\u80fd\u5fd8\u6000\u540d\u5229\u4e4b\u4eba\uff0c\u5c3c\u6cca\u5c14\u738b\u6240\u5e94\u8bb8\u7684\u6761\u4ef6\u4e0a\u2014\u2014\u5949\u767d\u6559\u4e3a\u56fd \u6559\uff0c\u5949\u6cd5\u4e3b\u4e3a\u6d3b\u4f5b\u2014\u2014\u7684\u786e\u662f\u4e00\u4e2a\u6781\u5927\u7684\u8bf1\u60d1\uff0c\u56e0\u800c\u62e5\u62a4\u5b54\u96c0\u660e\u4f26\u738b\u7684\u4eba\u4e5f\u4f9d\u7136\u4e0d\u5c11\u3002\u50e7\u4f17 \u4eec\u5206\u6210\u4e86\u4e24\u6d3e\uff0c\u8bae\u8bba\u7eb7\u7eb7\uff0c\u6574\u4e2a\u4f1a\u573a\uff0c\u5c31\u50cf\u4e00\u9505\u6cb8\u817e\u4e86\u7684\u5f00\u6c34\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u9762\u8272\u94c1\u9752\uff0c\u5ffd\u5730\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u662f\u4e00\u6559\u4e4b\u4e3b\uff0c\u6211\u4eec\u5e94\u5f53\u5c0a\u91cd\u4f60\u7684\u610f \u601d\uff0c\u4f46\u662f\u6709\u4e00\u4ef6\u7978\u4e8b\uff0c\u76ee\u524d\u5c31\u8981\u53d1\u4f5c\uff0c\u4f60\u53c8\u5982\u4f55\u5e94\u4ed8\u5462\uff1f\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u5587\u561b\u767b\u65f6\u505c\u6b62\u4e86\u8bae\u8bba\uff0c\u4eba\u4eba\u7684\u76ee\u5149\u90fd\u6ce8\u89c6\u7740\u5b54\u96c0\u660e\u4f26\u738b\uff0c\u76ee\u5149\u4e2d\u90fd\u542b\u6709\u8fd9 \u6837\u7684\u7591\u95ee\uff1a\u201c\u6709\u4ec0\u4e48\u7978\u4e8b\uff0c\u4e3a\u4ec0\u4e48\u6211\u4eec\u4e00\u70b9\u98ce\u58f0\u4e5f\u6ca1\u6709\u542c\u5230\uff1f\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5e08\u5144\uff0c\u6211\u770b\u6b64\u4e8b\u4e0d\u8be5\u518d\u9690\u7792\u4e86\uff0c\u8bf7\u8ba9\u6211\u90fd\u8bf4\u51fa\u6765\u5427\u3002\u201d\u6cd5\u738b\u6728 \u7136\u6beb\u65e0\u8868\u60c5\uff0c\u6de1\u8c08\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u51fa\u6765\u4e5f\u597d\u3002\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u9762\u5411\u50e7\u4f17\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5c3c\u6cca\u5c14\u738b\u5728\u4eca\u65e5\u4e4b\u524d\uff0c\u66fe\u6d3e\u8fc7\u4e09\u6b21\u4f7f\u8005\u5230\u6b64\uff0c\u9664\u4e86 \u7ed9\u4ed6\u4eec\u7684\u56fd\u738b\u9001\u4fe1\u4e0e\u5e08\u5144\u4e4b\u5916\uff0c\u8fd8\u505a\u4e86\u4e00\u4e9b\u79d8\u5bc6\u7684\u4e8b\u60c5\u3002\u672c\u6765\u6211\u662f\u4e0d\u8be5\u8bf4\u7684\uff0c\u4f46\u521a\u597d\u8fd9\u4ef6\u79d8 \u5bc6\u5c31\u5728\u4eca\u65e5\u95f9\u7a7f\uff0c\u662f\u4ee5\u6211\u4e5f\u4e0d\u6015\u8bf4\u4e86\u3002 \u3000\u3000\u201c\u60f3\u5fc5\u4f60\u4eec\u5df2\u6709\u8bb8\u591a\u4eba\u77e5\u9053\uff0c\u5c3c\u6cca\u5c14\u738b\u4e43\u662f\u51b0\u5ddd\u5929\u5973\u7684\u8868\u5144\uff0c\u4ed6\u5373\u4f4d\u4e4b\u540e\uff0c\u66fe\u6709\u597d\u51e0\u6b21 \u60f3\u8bf7\u51b0\u5ddd\u5929\u5973\u56de\u56fd\uff0c\u51b0\u5ddd\u5929\u5973\u6ca1\u6709\u7b54\u5e94\u3002\u56e0\u6b64\u4ed6\u53ea\u5f97\u53e6\u5916\u8bbe\u6cd5\uff0c\u5148\u628a\u51b0\u5ddd\u5929\u5973\u4e4b\u5b50\u4e0e\u5979\u4e00\u4e2a \u5fc3\u8179\u4f8d\u5973\uff0c\u4ea6\u5373\u9648\u5929\u5b87\u4e4b\u59bb\uff0c\u8bf7\u5230\u5c3c\u6cca\u5c14\u53bb\u3002\u8fd9\u4e24\u4ef6\u4e8b\u60c5\uff0c\u5c3c\u6cca\u5c14\u738b\u6d3e\u6765\u7684\u4f7f\u8005\u90fd\u5df2\u505a\u5230 \u4e86\u3002\u4ed6\u4eec\u5728\u8fdb\u884c\u8fd9\u79d8\u5bc6\u52fe\u5f53\u7684\u65f6\u5019\uff1a\u4e5f\u5373\u662f\u4ed6\u4eec\u5728\u9102\u514b\u6c81\u5bab\u4f5c\u5ba2\u7684\u65f6\u5019\u3002\u6362\u800c\u8a00\u4e4b\uff0c\u4ed6\u4eec\u662f \u501f\u7528\u672c\u5bfa\u4f5c\u4e3a\u63a9\u62a4\u7684\u4e86\u3002\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u5c06\u8fd9\u79d8\u5bc6\u63ed\u5f00\uff0c\u9648\u5929\u5b87\u8fd9\u624d\u604d\u7136\u5927\u609f\uff1a\u201c\u539f\u6765\u6211\u7684\u59bb\u5b50\u662f\u7ed9\u5c3c\u6cca\u5c14\u738b\u7684\u4f7f\u8005 \u63b3\u53bb\u7684\uff0c\u602a\u4e0d\u5f97\u6c5f\u5357\u90a3\u6b21\u6765\u95ee\u6cd5\u738b\uff0c\u6cd5\u738b\u4e5f\u4e0d\u6562\u76f4\u8bf4\u51fa\u6765\u3002\u201d\u53c8\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u8fd8\u63b3\u53bb\u4e86 \u5510\u7ecf\u5929\u7684\u513f\u5b50\uff0c\u8fd9\u6211\u53ef\u8fd8\u672a\u66fe\u77e5\u9053\u3002\u5947\u602a\uff0c\u6211\u5230\u5929\u5c71\uff0c\u89c1\u4e86\u5929\u5c71\u6d3e\u7684\u638c\u95e8\u5510\u6653\u6f9c\uff0c\u5510\u6653\u6593\u4e5f \u6ca1\u6709\u8bf4\u3002\u201d \u3000\u3000\u8fd9\u4ef6\u79d8\u5bc6\uff0c\u5728\u9102\u514b\u6c81\u5bab\uff0c\u4e5f\u53ea\u662f\u90a3\u56db\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u548c\u51e0\u4e2a\u804c\u4f4d\u6700\u9ad8\u7684\u5587\u561b\u65b9\u59cb\u77e5\u9053\uff0c\u5176\u4ed6 \u8c79\u4eba\uff0c\u90fd\u662f\u4e0d\u660e\u5e95\u8574\uff0c\u542c\u4e86\u4e4b\u540e\u3002\u4e0d\u89c9\u53c8\u662f\u8be7\u5f02\uff0c\u53c8\u662f\u60ca\u6050\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u9648\u5929\u5b57\u5931\u4e86\u59bb\u5b50\uff0c\u5f53\u7136\u5230\u5904\u5bfb\u8bbf\u3002\u4ed6\u6709\u4e2a\u597d\u670b\u53cb\uff0c\u5c31\u662f\u90a3\u5929\u4e0b \u77e5\u540d\u7684\u795e\u6109\u59ec\u6653\u98ce\u3002\u8fd9\u53ae\u662f\u4e2a\u9b3c\u7075\u7cbe\uff0c\u4e0d\u77e5\u600e\u7684\u5f97\u4e86\u98ce\u58f0\uff0c\u7adf\u5230\u672c\u5bfa\u5077\u67e5\uff0c\u548c\u90a3\u4e24\u4e2a\u4f7f\u8005\u671d \u4e86\u76f8\u3002\u4e0d\u4e45\u4e4b\u540e\uff0c\u9648\u5929\u5b87\u7684\u53e6\u4e00\u4e2a\u597d\u53cb\u6c5f\u5357\u4fbf\u6765\u95ee\u6559\u4e3b\u5e08\u5144\u8981\u4eba\uff0c\u5f53\u65f6\u5e08\u5144\u662f\u63a9\u9970\u8fc7\u53bb\u4e86\uff0c \u53ef\u662f\u5bf9\u65b9\u5374\u5e76\u4e0d\u76f8\u4fe1\u3002 \u3000\u3000\u201c\u5c31\u662f\u4ed6\u4eec\uff0c\u4eca\u5929\u53c8\u6765\u4e86\uff01\u540c\u6765\u7684\u8fd8\u6709\u4e00\u4e2a\u5c11\u5e74\uff0c\u73b0\u5728\u5df2\u7ecf\u67e5\u77e5\u662f\u91d1\u4e16\u9057\u7684\u5f92\u5f1f\u3002\u672c\u5bfa \u5587\u561b\uff0c\u5df2\u6709\u4e94\u4e2a\u4eba\u4f24\u5728\u4ed6\u7684\u624b\u4e0b\uff0c\u8fe6\u6bd7\u7f57\uff0c\u4f60\u5f53\u65f6\u5728\u573a\uff0c\u4f60\u7c91\u8fd9\u4e8b\u7684\u7ecf\u8fc7\u8bf4\u4e00\u8bf4\u5427\u3002\u201d \u3000\u3000\u8fe6\u6bd7\u7f57\u5c31\u662f\u9644\u4ece\u5b54\u96c0\u660e\u4f26\u738b\u7684\u90a3\u4e09\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u4e4b\u4e00\uff0c\u4e5f\u5373\u662f\u5728\u6c99\u6f20\u4e0a\u8981\u5c06\u59ec\u6653\u98ce\u6d3b\u64d2\u7684 \u90a3\u4e2a\u5587\u561b\u3002\u4ed6\u7ad9\u4e86\u51fa\u6765\uff0c\u5c06\u7ecf\u8fc7\u8bf4\u4e86\u4e00\u904d\uff0c\u59ec\u6653\u98ce\u8fd9\u624d\u77e5\u9053\uff0c\u539f\u6765\u5b54\u96c0\u660e\u4f26\u738b\u65e9\u5df2\u5f97\u77e5\u4ed6\u4eec \u8981\u6765\u7684\u6d88\u606f\uff0c\u6d3e\u51fa\u4e86\u5fc3\u8179\u5f1f\u5b50\uff0c\u5728\u9102\u514b\u6c81\u5bab\u767e\u91cc\u4e4b\u5185\u5de1\u67e5\u4e86\u3002\u90a3\u8fe6\u6bd7\u7f57\u5c31\u662f\u5949\u4e86\u5b54\u96c0\u660e\u4f26\u738b \u4e4b\u547d\u6765\u62ff\u4ed6\u7684\u3002 \u3000\u3000\u59ec\u6653\u98ce\u53c8\u662f\u5f97\u610f\uff0c\u53c8\u89c9\u60ca\u5947\u3002\u5f97\u610f\u7684\u662f\u5b54\u7701\u660e\u4f26\u738b\u4e5f\u5f97\u77e5\u4ed6\u7684\u5927\u540d\uff1b\u60ca\u5947\u7684\u662f\u8fd9\u5b54\u96c0\u660e \u4f26\u738b\u521a\u81ea\u5c3c\u6cca\u5c14\u56de\u6765\uff0c\u6d88\u606f\u7adf\u7136\u5982\u6b64\u7075\u901a\u3002\u59ec\u6653\u98ce\u54ea\u91cc\u77e5\u9053\uff0c\u4ed6\u548c\u6c5f\u6d77\u5929\u7684\u884c\u8e2a\uff0c\u4e00\u76f4\u5728\u91d1 \u9e70\u5bab\u4e3b\u4eba\u7684\u6ce8\u610f\u4e4b\u4e2d\uff0c\u800c\u5b54\u96c0\u660e\u4f26\u738b\u5728\u56de\u5230\u672c\u5bfa\u4e4b\u524d\uff0c\u5df2\u5148\u5230\u4e3b\u9e70\u5bab\u548c\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u5e08\u89c1\u8fc7 \u9762\u4e86\u3002 \u3000\u3000\u59ec\u6653\u98ce\u6b63\u81ea\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u662f\u4ed6\u4eec\u4fe9\u5e08\u5144\u5f1f\u5728\u6597\u6cd5\uff0c\u5374\u9669\u4ee4\u6211\u906d\u4e86\u6b83\u3002\u201d\u53ea\u542c\u5f97\u8fe6\u6bd7\u7f57\u53c8 \u9053\uff1a\u201c\u672c\u5bfa\u5f1f\u5b50\u54c8\u51e1\u63d0\u79c1\u901a\u654c\u4eba\uff0c\u8bf7\u6055\u6211\u672a\u66fe\u7980\u660e\uff0c\u5df2\u628a\u4ed6\u62ff\u4e0b\u6765\u4e86\u3002\u8bf7\u6559\u4e3b\u53d1\u843d\u3002\u201d \u3000\u3000\u6cd5\u738b\u7709\u5934\u4e00\u76b1\uff0c\u8bf4\u9053\uff1a\u201c\u59ec\u6653\u98ce\u4e5f\u4e0d\u80fd\u7b97\u662f\u672c\u5bfa\u7684\u654c\u4eba\uff0c\u54c8\u51e1\u63d0\u662f\u5949\u4e86\u6211\u7684\u547d\u4ee4\uff0c\u8981\u4ed6 \u53bb\u529d\u59ec\u6653\u98ce\u79bb\u5f00\u7684\uff0c\u4f60\u4f11\u5f97\u6a2a\u751f\u679d\u8282\uff0c\u5feb\u53bb\u901a\u77e5\u638c\u5211\u5f1f\u5b50\u5c06\u4ed6\u653e\u4e86\u5427\uff01\u201d\u8fe6\u6bd7\u7f57\u6c55\u6c55\u9000\u4e0b\uff0c \u6ee1\u9762\u901a\u7ea2\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u5927\u58f0\u9053\uff1a\u201c\u72ee\u5144\uff0c\u4f60\u6253\u7684\u662f\u4ec0\u4e48\u4e3b\u610f\uff1f\u54b1\u4eec\u73b0\u5728\u5df2\u662f\u5377\u5165\u6f29\u6da1\uff0c\u51b3\u4e0d\u80fd\u7f6e\u8eab \u4e8b\u5916\u4e86\uff01\u4f60\u5018\u82e5\u4e0d\u628a\u59ec\u6653\u98ce\u5f53\u4f5c\u654c\u4eba\uff0c\u90a3\u5c31\u662f\u8981\u628a\u5c3c\u6cca\u5c14\u738b\u5f53\u4f5c\u654c\u4eba\u4e86\uff01\u201d \u3000\u3000\u6cd5\u738b\u4f4e\u9996\u6c89\u601d\uff0c\u4f3c\u4e4e\u4ed6\u4e5f\u6b63\u5728\u4e3a\u6b64\u4e8b\u4f24\u795e\uff0c\u59d4\u51b3\u4e0d\u4e0b\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u5f97\u610f\u6d0b\u6d0b\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u59ec\u6653\u98ce\u8fd9\u53ae\u65e0\u8db3\u8f7b\u91cd\uff0c\u4e5f\u8fd8\u7f62\u4e86\u3002\u4f46\u6d88\u606f\u4f20\u64ad\u51fa \u53bb\uff0c\u5c82\u4e0d\u6709\u635f\u672c\u6559\u58f0\u540d\uff0c\u751a\u81f3\u7ed9\u672c\u6559\u62db\u6765\u5927\u7978\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u6211\u5371\u8a00\u8038\u542c\uff0c\u53ea\u6015\u5927\u7978\u5df2\u7ecf\u8feb\u8fd1\u7709\u776b\u4e86\uff01\u91d1\u4e16\u9057\u7684\u5f1f\u5b50\u5df2\u7ecf\u51fa\u73b0\uff0c\u91d1\u4e16\u9057\u8fdf\u65e9\u4e5f \u4e00\u5b9a\u4f1a\u6765\u3002\u91d1\u4e16\u9057\u662f\u51fa\u4e86\u540d\u7684\u5fc3\u72e0\u624b\u8fa3\u7684\u9b54\u5934\uff0c\u4ed6\u53c8\u4e0d\u660e\u5e95\u8574\uff0c\u53ea\u9053\u4ed6\u597d\u53cb\u7684\u59bb\u5b50\u662f\u7ed9\u672c\u6559 \u5f1f\u5b50\u62a2\u53bb\u7684\uff0c\u4ed6\u5c82\u80fd\u4e0e\u672c\u6559\u5e72\u4f11\uff1f \u3000\u3000\u201c\u4f55\u51b5\u8fd9\u4ef6\u4e8b\u60c5\u8fd8\u7275\u6d89\u4e86\u5929\u5c71\u6d3e\uff0c\u5929\u5c71\u6d3e\u638c\u95e8\u5510\u6653\u6f9c\u662f\u6b66\u6797\u9886\u8896\uff0c\u6bd4\u91d1\u4e16\u9057\u66f4\u96be\u5e94\u4ed8\uff01 \u4ed6\u82e5\u542c\u5230\u6d88\u606f\uff0c\u5f53\u7136\u4e5f\u4f1a\u6000\u7591\u4ed6\u7684\u5b59\u5b50\u662f\u7ed9\u672c\u6559\u6240\u63b3\u3002\u90a3\u65f6\uff0c\u4ed6\u82e5\u7387\u9886\u4e86\u6b66\u6797\u4eba\u7269\uff0c\u5927\u4e3e\u800c \u6765\uff0c\u5411\u672c\u6559\u5bfb\u4ec7\uff0c\u5e08\u5144\uff0c\u8bf7\u95ee\u4f60\u53c8\u5982\u4f55\u5e94\u4ed8\uff1f\u201d \u3000\u3000\u6cd5\u738b\u53cc\u7709\u4e00\u8f69\uff0c\uff0c\u84e6\u5730\u8d77\u7acb\u8bf4\u9053\uff1a\u201c\u6211\u7684\u4e3b\u610f\u5df2\u7ecf\u6253\u5b9a\uff0c\u5018\u82e5\u5510\u6653\u6f9c\u4e0e\u91d1\u4e16\u9057\u524d\u6765\uff0c\u6211 \u5c31\u5411\u4ed6\u4eec\u8ba4\u9519\uff0c\u5373\u4f7f\u4ed6\u4eec\u4e0d\u6765\uff0c\u6211\u4e5f\u8981\u627e\u5230\u9648\u5929\u5b87\uff0c\u5411\u4ed6\u8bf4\u660e\u771f\u76f8\u3002\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u5927\u53eb\u9053\uff1a\u201c\u8ba4\u9519\uff1f\u4f60\u662f\u672c\u6559\u6559\u4e3b\u7684\u8eab\u4efd\uff0c\u4f60\u662f\u4e0e\u8fbe\u8d56\u73ed\u7985\u540c\u7b49\u5730\u4f4d\u7684\u4eba\uff0c\u5c82 \u80fd\u8ba4\u9519\uff1f\u4e00\u8ba4\u4e86\u9519\uff0c\u672c\u6559\u66f4\u8981\u7ed9\u9ec4\u6559\u538b\u4e0b\u53bb\u4e86\u3002\u4f60\u7eb5\u4e0d\u7231\u60dc\u81ea\u5df1\uff0c\u4e5f\u8be5\u7231\u62a4\u672c\u6559\u554a\uff01\u201d \u3000\u3000\u6cd5\u738b\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u672c\u6765\u662f\u6211\u7684\u9519\uff0c\u5f53\u65f6\u6211\u4e00\u5ff5\u4e4b\u5dee\uff0c\u5ff5\u5728\u90a3\u4e24\u4e2a\u4f7f\u8005\u4e43\u662f\u5c3c\u6cca\u5c14\u738b\u6d3e \u6765\u7684\u8d35\u5ba2\uff0c\u4ed6\u4eec\u53c8\u662f\u5949\u4e86\u672c\u56fd\u56fd\u738b\u4e4b\u547d\u800c\u4e3a\uff0c\u5916\u4eba\u4e0d\u4fbf\u5e72\u6d89\uff0c\u6240\u4ee5\u6211\u4e5f\u5c31\u6ca1\u6709\u53ca\u65f6\u5236\u6b62\u3002\u4ee5 \u81f4\u5927\u9519\u94f8\u6210\uff01\u6211\u5018\u82e5\u518d\u52a0\u9690\u7792\uff0c\u90a3\u5c31\u662f\u9519\u4e0a\u52a0\u9519\uff01\u201d \u3000\u3000\u9996\u5ea7\u62a4\u6cd5\u5f1f\u5b50\u9053\uff1a\u201c\u5e08\u5c0a\uff0c\u8fd9\u5176\u5b9e\u4e5f\u4e0d\u662f\u4f60\u9519\uff1a\u51b0\u5dde\u5929\u5973\u662f\u5c3c\u6cca\u5c14\u738b\u7684\u8868\u59b9\uff0c\u5c3c\u6cca\u5c14\u738b \u5c06\u5979\u7684\u513f\u5b50\u548c\u4f8d\u5973\u2018\u8bf7\u2019\u53bb\uff0c\u4e3a\u7684\u662f\u8981\u51b0\u5ddd\u5929\u5973\u56de\u56fd\u3002\u4e0d\u8bba\u4ed6\u505a\u5f97\u5bf9\u4e0e\u4e0d\u5bf9\uff0c\u90a3\u603b\u662f\u4ed6\u4eec\u56fd \u5bb6\u7684\u4e8b\uff0c\u6b63\u5982\u5e08\u5c0a\u521a\u624d\u6240\u8bf4\uff0c\u54b1\u4eec\u662f\u5916\u4eba\uff0c\u4e0d\u4fbf\u5e72\u9884\u3002\u4e0d\u8fc7\u4e0d\u5e78\u7684\u662f\uff0c\u54b1\u4eec\u9002\u9022\u5176\u4f1a\uff0c\u88ab\u5377 \u5165\u4e86\u6f29\u6da1\u3002\u6240\u4ee5\u6211\u4e5f\u8d5e\u540c\u5e08\u5c0a\u7684\u4e3b\u610f\uff0c\u5c06\u771f\u76f8\u8bf4\u51fa\u6765\uff0c\u4f46\u5374\u4e0d\u662f\u8ba4\u9519\u3002\u201d \u3000\u3000\u6cd5\u738b\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u7ef4\u62a4\u6211\u4e86\uff0c\u8981\u77e5\u90a3\u4e24\u4e2a\u4f7f\u8005\u5bc4\u5bd3\u672c\u5bfa\u4e4b\u4e2d\uff0c\u4ed6\u4eec\u6240\u505a\u7684\u4e8b\u60c5\uff0c \u5c31\u8be5\u7531\u6211\u8d1f\u8d23\u3002\u6211\u5fb7\u8584\u80fd\u9c9c\uff0c\u51b3\u4e0d\u6562\u4ee5\u2018\u6d3b\u4f5b\u2019\u81ea\u5c45\uff0c\u9519\u4e86\u5c31\u662f\u9519\u4e86\uff0c\u4f55\u4ee5\u4e0d\u8ba4\uff1f\u201d \u3000\u3000\u6cd5\u738b\u8bf4\u5230\u8fd9\u91cc\uff0c\u8f6c\u8fc7\u4e00\u4e2a\u65b9\u5411\uff0c\u671b\u7740\u4ed6\u7684\u5e08\u5f1f\u8bf4\u9053\uff1a\u201c\u81f3\u4e8e\u4f60\u8bf4\u5230\u8981\u7231\u62a4\u672c\u6559\u7684\u58f0\u540d\uff0c \u8fd9\u786e\u662f\u5e94\u5f53\u3002\u6211\u505a\u4e86\u8fd9\u4ef6\u9519\u4e8b\uff0c\u7d2f\u53ca\u672c\u6559\uff0c\u5fc3\u5b9e\u4e0d\u5b89\u3002\u6240\u4ee5\u6211\u5df2\u51b3\u610f\u8f9e\u4e86\u6559\u4e3b\u4e4b\u4f4d\uff0c\u4e8b\u60c5\u89e3 \u51b3\u4e4b\u540e\uff0c\u6211\u613f\u4ee5\u5f85\u7f6a\u4e4b\u8eab\uff0c\u7559\u5728\u672c\u6559\u6267\u5f79\u3002\u603b\u4e4b\uff0c\u9519\u5728\u6211\u4e00\u4eba\u8eab\u4e0a\uff0c\u4e0e\u672c\u6559\u65e0\u5173\u3002\u8fd9\u6837\u5927\u7ea6 \u53ef\u4ee5\u4fdd\u5168\u672c\u6559\u7684\u58f0\u540d\u4e86\u5427\u3002\u201d \u3000\u3000\u6cd5\u738b\u7d20\u6765\u5f97\u4eba\u7231\u6234\uff0c\u867d\u8bf4\u8fd9\u6b21\u4ed6\u4e3a\u4e86\u8981\u4e0d\u8981\u8fc1\u79fb\u5c3c\u6cca\u5c14\u4e4b\u4e8b\uff0c\u4e0e\u5e08\u5f1f\u6709\u6240\u5206\u6b67\uff0c\u5728\u4ed6\u7684 \u95e8\u4e0b\u5f1f\u5b50\u4e2d\uff0c\u4e5f\u6709\u8bb8\u591a\u4eba\u662f\u8d5e\u6210\u5b54\u96c0\u738b\u610f\u89c1\u7684\uff0c\u4f46\u8bf4\u5230\u4e0d\u8981\u4ed6\u505a\u6559\u4e3b\uff0c\u9616\u5bfa\u5587\u561b\uff0c\u7edd\u5927\u591a\u6570 \u90fd\u662f\u8fde\u60f3\u4e5f\u4e0d\u6562\u8fd9\u6837\u60f3\u7684\u3002\u6240\u4ee5\u6cd5\u738b\u6b64\u8a00\u4e00\u51fa\uff0c\u767b\u65f6\u5168\u573a\u9a9a\u52a8\u8d77\u6765\uff01 \u3000\u3000\u6709\u4e0d\u5c11\u5587\u561b\u53eb\u9053\uff1a\u201c\u5f1f\u5b50\u90fd\u613f\u4e0e\u5e08\u5c0a\u5171\u540c\u60a3\u96be\uff0c\u8bf7\u5e08\u5c0a\u5207\u4e0d\u53ef\u5b58\u4e86\u9000\u4f4d\u4e4b\u5fc3\u3002\u201d\u6cd5\u738b\u8fde \u8fde\u6325\u624b\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u628a\u9a9a\u52a8\u5e73\u9759\u4e0b\u6765\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u8bf4\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u662f\u4f17\u671b\u6240\u5f52\uff0c\u9000\u4f4d\u4e4b\u8bf4\uff0c\u90a3\u662f\u4e0d\u5fc5\u63d0\u4e86\u3002\u8fd8\u662f\u8ba9\u54b1\u4eec\u518d\u4ece \u957f\u8ba1\u8bae\u5427\u3002\u201d\u4ed6\u4ee5\u9000\u4e3a\u8fdb\u3001\u6bb7\u6bb7\u633d\u7559\u5e08\u5144\u7559\u4efb\uff0c\u6cd5\u738b\u8fd9\u4e00\u6d3e\u4eba\uff0c\u672c\u6765\u5bf9\u4ed6\u6709\u53cd\u611f\u7684\uff0c\u4e5f\u5927\u5927 \u51cf\u8f7b\u4e86\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f9d\u6211\u4e4b\u89c1\uff0c\u5e08\u5144\uff0c\u4f60\u5373\u7b97\u52c7\u4e8e\u8ba4\u9519\u3002\u6b64\u4e8b\u53ea\u6015\u4e5f\u4e0d\u80fd\u4f5c\u4e86\u3002\u4e00 \u6765\u5bf9\u65b9\u5728\u6012\u706b\u4e0a\u5934\uff0c\u672a\u5fc5\u80af\u5c31\u6b64\u7f62\u624b\uff0c\u5c24\u5176\u91d1\u4e16\u9057\u662f\u4e2a\u51fa\u4e86\u540d\u7684\u4e0d\u8bb2\u7406\u7684\u9b54\u5934\u3002\u4e8c\u6765\u4f60\u867d\u7136 \u8bf4\u662f\u4e00\u4eba\u4f5c\u4e8b\u4e00\u4eba\u5f53\uff0c\u522b\u4eba\u5374\u672a\u5fc5\u8fd9\u6837\u60f3\u6cd5\uff0c\u603b\u4e4b\uff0c\u4f60\u4e00\u8ba4\u4e86\u9519\uff0c\u672c\u6559\u5a01\u671b\u4fbf\u5b9a\u7136\u5927\u51cf\u3002\u5018 \u975e\u53e6\u56fe\u826f\u7b56\uff0c\u53ea\u6015\u5728\u9752\u6d77\u4e5f\u65e0\u7acb\u8db3\u4e4b\u5730\u3002\u4e09\u6765\u6700\u91cd\u8981\u7684\uff0c\u4f60\u4e00\u63ed\u9732\u4e86\u5c3c\u6cca\u5c14\u738b\u7684\u79d8\u5bc6\uff0c\u90a3\u5c31 \u662f\u4e0e\u4ed6\u4f5c\u5bf9\u4e86\u3002\u4f60\u53ef\u4ee5\u8584\u2018\u6d3b\u4f5b\u2019\u800c\u4e0d\u4e3a\uff0c\u4f46\u672c\u6559\u4e2d\u5174\u7684\u5e0c\u671b\uff0c\u4e5f\u4ece\u6b64\u7edd\u4e86\uff0c\u5e08\u5144\uff0c\u8fd9\u662f\u767e \u4e16\u96be\u9022\u7684\u673a\u9047\uff0c\u8bf7\u4f60\u4ee5\u672c\u6559\u4e3a\u91cd\uff0c\u518d\u601d\u4e09\u601d\uff01\u201d \u3000\u3000\u6cd5\u738b\u6c89\u9ed8\u7247\u523b\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u5fc3\u610f\u5df2\u51b3\uff0c\u4e00\u4e0d\u53bb\u5c3c\u6cca\u5c14\uff0c\u4e8c\u5c06\u771f\u76f8\u63ed\u660e\u3002\u4f46\u8fd9\u662f\u6709\u5173 \u672c\u6559\u5174\u8870\u7684\u5927\u4e8b\uff0c\u6211\u4e5f\u4e0d\u80fd\u5f3a\u5236\u4f60\u4eec\u670d\u4ece\u6211\u7684\u4e3b\u5f20\uff0c\u73b0\u5728\u53cc\u65b9\u7684\u7406\u7531\u90fd\u5df2\u8bf4\u51fa\u6765\u4e86\uff0c\u8bf7\u4f60\u4eec \u614e\u91cd\u601d\u91cf\u3001\u5f85\u8fd9\u4f4f\u9999\u7184\u706d\uff0c\u4fbf\u5373\u4ed8\u4e4b\u516c\u51b3\u3002\u201d \u3000\u3000\u95f9\u54c4\u54c4\u7684\u6c14\u6c1b\u7acb\u5373\u5f52\u4e8e\u6c89\u5bc2\uff0c\u5343\u591a\u4eba\u6324\u6ee1\u7684\u5927\u6bbf\uff0c\u542c\u4e0d\u5230\u4e00\u70b9\u58f0\u97f3\u3002\u6bcf\u4e2a\u4eba\u7684\u5fc3\u91cc\u90fd\u5728 \u5229\u5bb3\u4ea4\u6218\uff01\u6cd5\u4e3b\u7684\u8bdd\u56fa\u7136\u662f\u4e49\u6b63\u8f9e\u4e25\uff0c\u4f46\u5c3c\u6cca\u5c14\u738b\u7684\u6761\u4ef6\uff0c\u5bf9\u4e8e\u8870\u843d\u5df2\u4e45\u7684\u767d\u6559\uff0c\u5374\u662f\u4e00\u4e2a \u6781\u5927\u7684\u8bf1\u60d1\u3002\u6709\u4e0d\u5c11\u5587\u561b\u5747\u662f\u5982\u6b64\u60f3\u9053\uff1a\u201c\u6559\u4e3b\u6015\u5c3c\u6cca\u5c14\u738b\u5229\u7528\u672c\u6559\uff0c\u90a3\u53ea\u662f\u4e00\u79cd\u987e\u8651\uff1b\u63a5 \u53d7\u4e86\u5c3c\u6cca\u5c14\u7389\u7684\u9080\u8bf7\uff0c\u90a3\u5374\u6709\u65e0\u5c3d\u7684\u5c0a\u8363\uff01\u201d \u3000\u3000\u795e\u5ea7\u4e0a\u9999\u70df\u7f2d\u7ed5\uff0c\u4eba\u4eba\u7684\u773c\u775b\u90fd\u671b\u7740\u90a3\u4e00\u70db\u9999\uff0c\u5728\u8fd9\u4e00\u6ce8\u9999\u7684\u65f6\u523b\u4e2d\uff0c\u6bcf\u4e00\u4e2a\u4eba\u7684\u5fc3\u91cc \u90fd\u7ecf\u8fc7\u4e86\u65e0\u6570\u6b21\u7684\u53d8\u5316\u3002 \u3000\u3000\u8fd9\u4f4f\u9999\u4e00\u5bf8\u4e00\u5bf8\u7684\u7f29\u77ed\uff0c\u7ec8\u4e8e\u70e7\u6210\u4e86\u7070\u70ec\u3002\u6cd5\u738b\u4e3e\u624b\u8bf4\u9053\uff1a\u201c\u8d5e\u6210\u53bb\u5c3c\u6cca\u5c14\u7684\uff0c\u8bf7\u7ad9\u5728 \u4e00\u8fb9\u3002\u8d5e\u6210\u7559\u5728\u672c\u5bfa\u7684\uff0c\u7559\u5728\u539f\u4f4d\u4e0d\u52a8\u3002\u201d\u5b54\u96c0\u660e\u4f26\u738b\u9996\u5148\u7ad9\u4e86\u51fa\u6765\uff0c\u4e00\u4e2a\u4e00\u4e2a\u5587\u561b\u9ed8\u9ed8\u65e0 \u8a00\u7684\u63a5\u7740\u8ddf\u4e0a\u3002\u4e5f\u6709\u597d\u51e0\u4e2a\u5587\u561b\u8d70\u5230\u534a\u9014\uff0c\u53c8\u6298\u56de\u539f\u4f4d\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4e24\u8fb9\u7684\u4eba\u90fd\u5df2\u5206\u5f00\uff0c\u6ca1\u6709\u4eba\u518d\u79fb\u52a8\u4e86\u3002\u6cd5\u738b\u53eb\u9996\u5ea7\u62a4\u6cd5\u5f1f\u4e8e\u70b9\u6570\uff0c\u8d5e\u6210\u53bb\u5c3c \u6cca\u5c14\u7684\u6709\u4e94\u767e\u96f6\u516b\u4eba\uff0c\u8d5e\u6210\u7559\u5728\u672c\u5bfa\u7684\u6709\u4e94\u767e\u96f6\u4e09\u4eba\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u80dc\u5229\u4e86\uff0c\u4ed6\u4eec\u8fd9\u8fb9\u591a\u4e86\u4e94 \u4eba\uff0c\u7981\u4e0d\u8d77\u540d\u5229\u8bf1\u60d1\u7684\u4eba\u7a76\u7adf\u662f\u591a\u6570\uff01 \u3000\u3000\u6cd5\u738b\u795e\u8272\u9eef\u7136\uff0c\u79bb\u5ea7\u800c\u8d77\u3002\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u8bf7\u4f60\u5c31\u5ea7\u5427\u3002\u6211\u51b3\u610f\u4f20\u4f4d\u4e8e\u4f60\uff0c\u4ece\u6b64\u523b\u8d77\uff0c \u4f60\u5c31\u662f\u672c\u6559\u7684\u6559\u4e3b\u4e86\uff01\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u5185\u5fc3\u6b22\u559c\uff0c\u8868\u9762\u5374\u4e0d\u5f97\u4e0d\u8c26\u8f9e\u9053\uff1a\u201c\u5c0f\u5f1f\u4f55\u5fb7\u4f55\u80fd\uff0c\u7109\u6562\u63a5\u6b64\u5927\u4f4d\uff0c\u8bf7\u5e08\u5144 \u6536\u56de\u6210\u547d\u3002\u201d \u3000\u3000\u6cd5\u738b\u90d1\u91cd\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u4e3b\u5f20\u5f97\u5230\u591a\u6570\u62e5\u62a4\uff0c\u4f60\u548c\u5c3c\u6cca\u5c14\u738b\u53c8\u65e9\u5df2\u6709\u4e86\u5341\u5e74\u5bbe\u4e3b\u4e4b\u8c0a\uff0c\u4eca \u540e\u672c\u6559\u8fc1\u79fb\u8be5\u56fd\uff0c\u6559\u4e3b\u7684\u804c\u8d23\u3002\u81ea\u662f\u4ee5\u4f60\u6267\u638c\u4e3a\u5b9c\u3002\u5e08\u5f1f\uff0c\u4e8b\u5df2\u5982\u65af\uff0c\u6e05\u4f60\u4f9d\u4ece\u4f17\u8bae\uff0c\u4e0d\u5fc5 \u518d\u63a8\u8f9e\u4e86\u3002\u201d \u3000\u3000\u5587\u561b\u4eec\u5728\u8868\u793a\u53bb\u7559\u7684\u6001\u5ea6\u65f6\uff0c\u65e9\u5df2\u60f3\u5230\u4e86\u8fd9\u4e8b\u60c5\u8981\u7275\u6d89\u5230\u6559\u4e3b\u7684\u6210\u7acb\u3002\u4ed6\u4eec\u8861\u91cf\u5229\u5bb3\uff0c \u867d\u7136\u5bf9\u65e7\u6559\u4e3b\u4e4b\u53bb\uff0c\u5fc3\u5b58\u60cb\u501f\u3002\u4f46\u4e5f\u65e0\u53ef\u4eb2\u4f55\u4e86\u3002\u6240\u4ee5\uff0c\u6cd5\u738b\u8ba9\u4f4d\u5e08\u5f1f\uff0c\u53ef\u8bf4\u662f\u201c\u610f\u6599\u4e2d\u4e4b \u4e8b\u201d\uff0c\u56e0\u6b64\u4f17\u4eba\u7684\u60c5\u7eea\uff0c\u4fbf\u53cd\u800c\u4e0d\u5982\u521a\u624d\u7684\u6fc0\u52a8\uff0c\u867d\u7136\u4e5f\u4ecd\u662f\u6709\u4e00\u4e9b\u4eba\u4f4e\u58f0\u996e\u4f4d\uff0c\u9eef\u9eef\u795e\u4f24\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u4f5c\u51fa\u4e00\u526f\u8bda\u60f6\u8bda\u6050\u7684\u795e\u60c5\uff0c\u6700\u540e\u4f3c\u4e4e\u8feb\u4e0d\u5f97\u5df2\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5e08\u5144\u6267\u610f \u5982\u6b64\uff0c\u5f1f\u4e5f\u53ea\u597d\u52c9\u4e3a\u5176\u96be\u4e86\u3002\u201d\u5f53\u4e0b\uff0c\u4ed6\u5728\u8fe6\u6bd7\u7f57\u7b49\u62a4\u6cd5\u5f1f\u4e8e\u6276\u6301\u4e4b\u4e0b\uff0c\u5750\u4e0a\u4e86\u6559\u4e3b\u7684\u5b9d\u5ea7\u3002 \u3000\u3000\u6cd5\u738b\u9886\u5934\uff0c\u5411\u65b0\u6559\u4e3b\u884c\u4e86\u5927\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u63aa\u65bd\u4e0d\u5f53\uff0c\u8bf7\u6559\u4e3b\u964d\u7f6a\u3002\u201d\u5b54\u96c0\u660e\u4f26\u738b\u9053\uff1a \u201c\u672c\u6559\u5927\u8ba1\u5df2\u5b9a\uff0c\u4eca\u540e\u5c31\u662f\u5c3c\u6cca\u5c14\u7684\u56fd\u6559\u4e86\u3002\u5e08\u5144\uff0c\u4f60\u66fe\u534f\u52a9\u8be5\u56fd\u7684\u4f7f\u8005\u6267\u884c\u4e86\u56fd\u738b\u7684\u547d \u4ee4\uff0c\u6b63\u662f\u5927\u5927\u7684\u529f\u52b3\uff0c\u4f55\u7f6a\u4e4b\u6709\uff1f\u6b64\u4e8b\u63ed\u8fc7\u3002\u4ece\u6b64\u4f11\u63d0\u3002\u201d \u3000\u3000\u6cd5\u738b\u96be\u8fc7\u4e4b\u6781\uff0c\u671b\u4e86\u5e08\u5f1f\u4e00\u773c\uff0c\u518d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8c22\u6559\u4e3b\u514d\u7f6a\u3002\u4f46\u8bf7\u6559\u4e3b\u51c6\u6211\u4ee5\u8d1f\u7f6a\u4e4b\u8eab \u79bb\u5f00\u672c\u6559\u3002\u201d\u9996\u5ea7\u62a4\u6cd5\u5f1f\u4e8e\u4e5f\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u60f3\u5230\u5c3c\u6cca\u5c14\u53bb\uff0c\u8bf7\u6559\u4e3b\u51c6\u6211\u5949\u4f8d\u5e08\u5c0a\u3002\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u76b1\u4e86\u76b1\u7709\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u5e08\u5144\uff0c\u4f60\u65e2\u51b3\u610f\u79bb\u5f00\u3002\u6211\u4e5f\u4e0d\u4fbf\u5f3a\u4f60\u6240\u96be\uff0c\u51c6\u4f60 \u5e26\u987b\u83e9\u63d0\u53bb\u5427\u3002\u5176\u4ed6\u7684\u4eba\uff0c\u4e0d\u53ef\u4e3a\u4f8b\u3002\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u7eed\u9053\uff1a\u201c\u8fe6\u6bd7\u7f57\uff0c\u4f60\u53bb\u8bf7\u5c3c\u6cca\u5c14\u738b\u90a3\u51e0\u4f4d\u4f7f\u8005\u51fa\u6765\uff0c\u4e0e\u5927\u5bb6\u89c1\u89c1\u9762\u5427\u3002\u55ef\uff0c \u5e08\u5144\uff0c\u4f60\u8fd8\u60f3\u89c1\u89c1\u4ed6\u4eec\u4e48\uff1f\u201d\u8bb8\u591a\u5587\u561b\u8fd9\u624d\u77e5\u9053\uff0c\u539f\u6765\u5c3c\u6cca\u5c14\u7684\u4f7f\u8005\u8fd8\u85cf\u5728\u5bfa\u4e2d\uff0c\u800c\u4e14\u4e0d\u6b62 \u4e00\u4eba\u3002 \u3000\u3000\u6cd5\u738b\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\u3002\u201d\u4e0e\u987b\u83e9\u63d0\u4e8c\u4eba\u6b63\u8981\u79bb\u5f00\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u5ffd\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4e14\u6162\uff01\u201d \u3000\u3000\u6cd5\u738b\u9053\uff1a\u201c\u6559\u4e3b\u6709\u4f55\u5429\u5490\u3002\u201d\u5b54\u96c0\u660e\u4f26\u738b\u9053\uff1b\u201c\u672c\u5bfa\u50e7\u4f17\uff0c\u603b\u6570\u82e5\u5e72\uff1f\u201d\u6cd5\u738b\u9053\uff1a\u201c\u6709 \u540d\u518c\u53ef\u67e5\u3002\u201d\u5b54\u96c0\u660e\u4f26\u738b\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u73b0\u5728\u5c31\u60f3\u77e5\u9053\uff0c\u5e08\u5144\uff0c\u4f60\u8bb0\u5f97\u4e48\uff1f\u201d\u6cd5\u738b\u60f3\u4e86\u4e00\u4e0b\uff0c \u8bf4\u9053\uff1a\u201c\u8fde\u4f60\u6211\u5728\u5185\uff0c\u5171\u662f\u4e00\u5343\u96f6\u4e09\u5341\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u9053\uff1a\u201c\u4eca\u65e5\u4e0d\u5230\u4f1a\u7684\u6709\u51e0\u4eba\uff1f\u201d\u79bb\u804c\u7684\u9996\u5ea7\u62a4\u6cd5\u5f1f\u5b50\u987b\u83e9\u63d0\u9053\uff1a\u201c\u6d3e\u51fa\u7684\u5b88 \u536b\u548c\u56e0\u75c5\u672a\u80fd\u5230\u6765\u7684\u5171\u662f\u4e8c\u5341\u56db\u4eba\u3002\u8fd9\u4e8c\u5341\u56db\u4eba\u6211\u90fd\u5df1\u95ee\u8fc7\u4ed6\u4eec\u7684\u4e3b\u610f\u4e86\uff0c\u613f\u53bb\u5c3c\u6cca\u5c14\u7684\u4e0e \u4e0d\u613f\u53bb\u5c3c\u6cca\u5c14\u7684\u6070\u597d\u662f\u4e00\u534a\u5bf9\u4e00\u534a\u3002\u6240\u4ee5\u5e76\u4e0d\u5f71\u54cd\u521a\u624d\u7684\u51b3\u5b9a\u3002\u201d\u987b\u83e9\u63d0\u4e3a\u4eba\u516c\u6b63\uff0c\u867d\u7136\u62e5 \u62a4\u5e08\u5c0a\uff0c\u4ecd\u7136\u5982\u5b9e\u8bf4\u51fa\u3002\u5b54\u96c0\u660e\u4f26\u738b\u5ffd\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u600e\u4e48\u591a\u51fa\u56db\u4e2a\u4eba\u6765\u4e86\uff1f\u201d \u3000\u3000\u9996\u5ea7\u62a4\u6cd5\u5f1f\u5b50\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u600e\u4e48\u591a\u4e86\u56db\u4eba\uff1f\u201d\u5b54\u96c0\u660e\u4f26\u738b\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u7b97\u4e00\u7b97\u6570\uff0c \u672c\u5bfa\u50e7\u4f17\u5171\u662f\u4e00\u5343\u96f6\u4e09\u5341\u4e00\u4eba\uff0c\u4e0d\u5230\u4f1a\u7684\u4e8c\u5341\u56db\u4eba\uff0c\u90a3\u4e48\u5728\u6b64\u5730\u7684\u662f\u4e0d\u662f\u5e94\u8be5\u5b58\u4e00\u5343\u96f6\u4e03\u4e2a \u4eba\uff1f\u201d\u9996\u5ea7\u62a4\u6cd5\u5f1f\u5b50\u5fc3\u4e2d\u9ed8\u6570\u4e0b\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u5b54\u96c0\u660e\u4f26\u738b\u9053\uff1a\u201c\u4f46\u521a\u624d\u4f60\u70b9\u8fc7\u4e86 \u6570\uff0c\u5728\u8fd9\u91cc\u7684\u50e7\u4f17\uff0c\u613f\u53bb\u5c3c\u6cca\u5c14\u7684\u662f\u4e94\u767e\u96f6\u516b\u4eba\uff0c\u4e0d\u613f\u53bb\u7684\u662f\u4e94\u767e\u96f6\u4e09\u4eba\uff0c\u5408\u8d77\u6765\u5c31\u662f\u4e00\u5343 \u96f6\u5341\u4e00\u4e2a\u4e86\uff0c\u8fd9\u4e0d\u662f\u591a\u51fa\u4e86\u56db\u4e2a\u4eba\u5417\uff1f\u201d \u3000\u3000\u9996\u5ea7\u62a4\u6cd5\u5f1f\u5b50\u5927\u53eb\u9053\uff1a\u201c\u5feb\u67e5\u5978\u7ec6\uff01\u201d\u59ec\u6653\u98ce\u5fc3\u5934\u4e00\u9707\uff0c\u65b9\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u5b54\u96c0\u738b\u679c\u7136\u662f \u7cbe\u660e\u5389\u5bb3\uff01\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u89c1\u90a3\u8fe6\u6bd7\u7f57\u8eab\u5f62\u4e00\u6643\uff0c\u500f\u7136\u95f4\u5c31\u5230\u4e86\u4ed6\u7684\u9762\u524d\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u8fd9 \u91cc\u6709\u4e00\u4e2a\u5978\u7ec6\uff01\u662f\u9ec4\u6559\u4e2d\u4eba\u5192\u5145\u672c\u6559\u5f1f\u5b50\uff01\u201d \u3000\u3000\u4f60\u9053\u8fe6\u6bd7\u7f57\u4f55\u4ee5\u80fd\u591f\u5982\u6b64\u8fc5\u901f\u53d1\u73b0\uff1f\u539f\u6765\u59ec\u6653\u98ce\u6240\u6234\u7684\u90a3\u5f20\u4eba\u76ae\u9762\u5177\uff0c\u6b63\u662f\u8fbe\u8d56\u5ea7\u4e0b\u7684 \u4e00\u4e2a\u201c\u884c\u9999\u201d\u5f1f\u5b50\uff0c\u9ec4\u6559\u6709\u4e2a\u89c4\u77e9\uff0c\u6bcf\u9022\u6559\u4e2d\u6709\u5927\u5178\u4e3e\u884c\u4e4b\u65f6\uff0c\u4f8b\u5982\u4f5b\u8bc5\u8bde\u8fb0\u6216\u6bcf\u5e74\u4e00\u5ea6\u7684 \u5f00\u5149\u5178\u793c\u4e4b\u7c7b\uff0c\u5c31\u8981\u6d3e\u51fa\u8bb8\u591a\u201c\u884c\u9999\u5f1f\u5b50\u201d\u5230\u5404\u5730\u5587\u561b\u5bfa\u53bb\u76d1\u793c\uff0c\u5e76\u4ee3\u8868\u6d3b\u4f5b\u4e0a\u7b2c\u4e00\u4f4f\u9999\u3002 \u6709\u4e00\u5e74\uff0c\u8fd9\u4e00\u4e2a\u884c\u9999\u5f1f\u5b50\u5230\u5927\u5b89\u8fbe\u68ee\u6797\u9644\u8fd1\u7684\u4e00\u4e2a\u5587\u561b\u5bfa\u53bb\uff0c\u88ab\u68ee\u6797\u4e2d\u7684\u730e\u5934\u65cf\u6349\u83b7\uff0c\u5265\u4e0b \u5934\u76ae\uff0c\u5236\u6210\u9762\u5177\u3001\u730e\u5934\u65cf\u5236\u9762\u5177\u7684\u79d8\u6cd5\u6781\u4e3a\u5de7\u5999\uff0c\u7ecf\u8fc7\u8bb8\u591a\u5e74\u6708\uff0c\u4ecd\u662f\u6829\u6829\u5982\u751f\u3002\u8fbe\u8d56\u5ea7\u4e0b \u8fd9\u4e2a\u201c\u884c\u9999\u5f1f\u5b50\u201d\uff0c\u8fe6\u6bd7\u7f57\u662f\u89c1\u8fc7\u7684\uff0c\u4f46\u5374\u4e0d\u77e5\u4ed6\u5df2\u7ecf\u88ab\u5927\u5b89\u8fbe\u68ee\u6797\u7684\u730e\u5934\u65cf\u5bb3\u4e86\uff0c\u56e0\u6b64\u5c31 \u628a\u6234\u4e86\u8fd9\u5f20\u4eba\u76ae\u9762\u5177\u7684\u59ec\u6653\u98ce\u8ba4\u4e86\u51fa\u6765\uff0c\u8ba4\u4e3a\u662f\u90a3\u4e2a\u5f1f\u5b50\u3002 \u3000\u3000\u8fe6\u6bd7\u7f57\u8eab\u4e3a\u6cd5\u738b\u5ea7\u4e0b\u7684\u56db\u5927\u62a4\u6cd5\u5f1f\u5b50\u4e4b\u4e00\uff0c\u6b66\u529f\u4e0a\u7684\u9020\u8be3\u81ea\u662f\u4e0d\u51e1\uff0c\u59ec\u6653\u98ce\u4e00\u60ca\u4e4b\u4e0b\uff0c \u9669\u9669\u7ed9\u4ed6\u6293\u7740\uff0c\u6c5f\u5357\u89c1\u72b6\u4e0d\u5999\uff0c\u65e0\u6687\u601d\u7d22\uff0c\u4ed6\u6b63\u7ad9\u5728\u59ec\u6653\u98ce\u7684\u8eab\u8fb9\uff0c\u4e00\u6307\u4fbf\u6233\u8fc7\u53bb\u3002 \u3000\u3000\u6c5f\u5357\u7528\u7684\u662f\u91d1\u4e16\u9057\u6240\u6388\u7684\u72ec\u95e8\u70b9\u7a74\u624b\u6cd5\uff0c\u8fe6\u6bd7\u7f57\u88ab\u4ed6\u4e00\u6307\u6233\u4e2d\uff0c\u767b\u65f6\u534a\u8fb9\u8eab\u4e88\u9ebb\u6728\uff0c\u4f38 \u51fa\u53bb\u7684\u90a3\u6761\u624b\u81c2\uff0c\u5782\u5728\u534a\u7a7a\uff0c\u4e0d\u80fd\u52a8\u5f39\uff0c\u5f62\u72b6\u751a\u662f\u53e4\u602a\u3002 \u3000\u3000\u8fd9\u4e48\u4e00\u6765\uff0c\u6c5f\u5357\u4e5f\u66b4\u9732\u4e86\u76ee\u6807\uff0c\u5587\u561b\u7eb7\u7eb7\u53eb\u56b7\uff1a\u201c\u6349\u5978\u7ec6\u554a\uff0c\u6349\u5978\u7ec6\u554a\uff01\u201d\u56db\u9762\u516b\u65b9\u6d8c \u4e0a\uff0c\u5c06\u4ed6\u4eec\u56f4\u5728\u6838\u5fc3\u3002 \u3000\u3000\u59ec\u6653\u98ce\u4e0e\u6c5f\u5357\u80cc\u5411\u7740\u80cc\uff0c\u5404\u81ea\u8e0f\u51fa\u5929\u7f57\u6b65\u6cd5\uff0c\u5de6\u884c\u4e09\u6b65\uff0c\u53f3\u884c\u4e24\u6b65\uff0c\u524d\u884c\u4e09\u6b65\u3002\u540e\u9000\u4e24 \u6b65\uff0c\u5ffd\u800c\u53c8\u811a\u8ddf\u7740\u5730\uff0c\u6253\u4e86\u4e00\u4e2a\u76d8\u65cb\uff0c\u8fd9\u79cd\u53e4\u602a\u7684\u6b65\u6cd5\u4f7f\u4e86\u51fa\u672a\uff0c\u5f53\u771f\u662f\u77bb\u4e4b\u5728\u524d\uff0c\u5ffd\u7109\u5728 \u540e\uff0c\u77bb\u4e4b\u5728\u5de6\uff0c\u5ffd\u7109\u5728\u53f3\uff0c\u5f15\u5f97\u4e00\u7fa4\u5587\u561b\u8ddf\u7740\u4ed6\u4eec\u56e2\u56e2\u4e71\u8f6c\u3002\u5587\u561b\u4eec\u4e92\u76f8\u78b0\u649e\uff0c\u201c\u54ce\u54df\u54df\u201d \u7684\u547c\u75db\u4e4b\u58f0\u6b64\u8d77\u5f7c\u843d\uff0c\u4e71\u5f97\u4e00\u584c\u7cca\u6d82\u3002 \u3000\u3000\u59ec\u6653\u98ce\u5de6\u7a7f\u53f3\u63d2\uff0c\u6b63\u8981\u4ece\u4eba\u4e1b\u4e4b\u4e2d\u6e9c\u8d70\u51fa\u53bb\uff0c\u5ffd\u89c9\u4e00\u80a1\u5927\u529b\u8fce\u9762\u6293\u6765\uff0c\u59ec\u6653\u98ce\u53cc\u638c\u63a8 \u51fa\uff0c\u7adf\u7136\u62b5\u6321\u4e0d\u4f4f\uff0c\u88ab\u90a3\u4eba\u4e00\u628a\u6293\u4e86\u8d77\u6765\uff0c\u968f\u624b\u5c31\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\u3002 \u3000\u3000\u6293\u7740\u59ec\u6653\u98ce\u7684\u6b63\u662f\u90a3\u5b54\u96c0\u660e\u4f26\u738b\uff0c\u4ed6\u628a\u59ec\u6653\u98ce\u4e00\u6454\uff0c\u8fc5\u5373\u53c8\u662f\u4e00\u628a\u5411\u6c5f\u5357\u6293\u6765\uff01 \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u4e00\u5927\u7fa4\u5587\u561b\u5df2\u4f3c\u6f6e\u6c34\u822c\u7684\u5411\u4e24\u8fb9\u9000\u4e0b\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u8ddd\u79bb\u6c5f\u5357\u4e5f\u8fd8\u6709\u4e08\u8bb8\u4e4b\u9065\uff1a \u4f46\u4ed6\u90a3\u865a\u7a7a\u4e00\u6293\uff0c\u7adf\u6bd4\u8fd1\u8eab\u8089\u640f\u529b\u9053\u8fd8\u5f3a\uff0c\u6c5f\u5357\u88ab\u90a3\u80a1\u6697\u529b\u6240\u7275\uff0c\u610f\u4f3c\u9677\u8eab\u5728\u6fc0\u6d41\u6025\u6e4d\u4e4b \u4e2d\uff0c\u8eab\u4e0d\u7531\u5df2\u7684\u76f4\u6253\u4e86\u51e0\u4e2a\u76d8\u65cb\uff0c\u4f3c\u662f\u88ab\u4eba\u7275\u7740\u4f3c\u7684\uff0c\u5411\u7740\u5b54\u96c0\u660e\u4f26\u738b\u7684\u65b9\u5411\u8f6c\u8fc7\u53bb\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u53ea\u9053\u4f60\u6709\u4ec0\u4e48\u672c\u9886\uff0c\u7adf\u6562\u6df7\u8fdb\u6765\u505a\u5978\u7ec6\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c \u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u6c5f\u6d77\u5929\u5927\u559d\u9053\uff1a\u201c\u4f11\u5f97\u4f24\u5bb3\u6211\u7239\uff01\u201d\u4ed6\u4eba\u672a\u5230\uff0c\u638c\u5148\u53d1\uff0c\u7528\u7684\u662f\u987b\u5f25\u638c \u529b\uff0c\u638c\u98ce\u9707\u8361\uff0c\u201c\u84ec\u201d\u7136\u6709\u58f0\u3002\u767b\u65f6\u628a\u5b54\u96c0\u660e\u4f26\u738b\u7684\u90a3\u80a1\u6697\u529b\u89e3\u4e86\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u7b2c\u4e09\u4e2a\u5978\u7ec6\u4e5f\u53d1\u73b0\u4e86\uff01\u201d\u6539\u6293\u4e3a\u5288\uff0c\u4e00\u638c\u6a2a\u626b\u8fc7 \u53bb\uff0c\u6c5f\u6d77\u592d\u7ffb\u638c\u4e00\u8fce\uff0c\u78b0\u4e2a\u6b63\u7740\uff0c\u53ea\u89c9\u5982\u540c\u89e6\u7740\u4e00\u5757\u70e7\u7ea2\u7684\u94c1\u5757\u4e00\u822c\uff0c\u8eab\u4e0d\u7531\u5df2\u7684\u5012\u9000\u4e09 \u6b65\uff0c\u5168\u8eab\u6c14\u8840\u7ffb\u817e\uff0c\u6781\u4e4b\u96be\u53d7\uff0c\u4e0d\u7981\u5927\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4ed6\u7684\u5e08\u5144\u5f53\u5e74\u80fd\u591f\u4e0e\u6211\u7684\u5e08\u7236\u6253 \u6210\u5e73\u624b\uff0c\u539f\u6765\u4ed6\u5df2\u7ecf\u8fd9\u4e48\u5389\u5bb3\uff0c\u4eca\u65e5\u53ea\u6015\u96be\u4ee5\u8131\u8eab\u4e86\u3002\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u4e0a\u8eab\u4e5f\u6643\u4e86\u4e00\u6641\uff0c\u5fc3\u4e2d\u66f4\u662f\u60ca\u5947\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u9ec4\u6559\u4ece\u54ea\u7687\u8bf7\u6765\u7684\u8fd9\u4e2a\u9ad8 \u624b\uff1f\u6240\u4ed6\u7684\u8bf4\u8bdd\u8fd8\u5e26\u7ae5\u97f3\uff0c\u7adf\u7136\u6562\u786c\u63a5\u4e86\u6211\u7684\u4e00\u638c\uff01\u5947\u602a\uff0c\u4ed6\u7684\u76f8\u8c8c\u8981\u6bd4\u58f0\u97f3\u82cd\u8001\u5f97\u591a\uff0c\u53c8 \u53eb\u8fd9\u4e2a\u4eba\u505a\u7239\u7239\uff0c\u771f\u662f\u90aa\u95e8\uff01\u201d \u3000\u3000\u6cd5\u738b\u4e5f\u628a\u59ec\u6653\u98ce\u8bef\u8ba4\u4e3a\u8fbe\u8d56\u5ea7\u4e0b\u7684\u90a3\u4e2a\u201c\u884c\u9999\u5f1f\u5b50\u201d\uff0c\u53ea\u9053\u6df7\u8fdb\u6765\u7684\u8fd9\u51e0\u4e2a\u4eba\uff0c\u679c\u7136\u662f \u9ec4\u6559\u6d3e\u6765\u7684\u5978\u7ec6\uff0c\u4e0d\u7981\u4e5f\u662f\u5927\u60ca\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6b64\u4e8b\u7a0d\u4e00\u5904\u7f6e\u4e0d\u5f53\uff0c\u5c31\u8981\u5f04\u6210\u4e24\u6559\u4e4b\u4e89\u3002\u201d \u4e00\u65f6\u7740\u6025\uff0c\u5fd8\u8bb0\u4e86\u81ea\u5df1\u5df2\u7ecf\u4e0d\u5728\u6559\u4e3b\u4e4b\u4f4d\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f4f\u624b\uff01\u4e0d\u53ef\u4f24\u4eba\uff01\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u5df2\u79bb\u5f00\u672c\u6559\uff0c\u672c\u6559\u4e4b\u4e8b\uff0c\u5c31\u8bf7\u4f60\u4e0d\u5fc5\u518d\u64cd\u5fc3\u4e86\uff01\u201d\u4ed6 \u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u5e95\u6beb\u4e0d\u653e\u677e\uff0c\u80ef\u4e0a\u4e00\u6b65\uff0c\u201c\u5de6\u5f13\u53f3\u7bad\u201d\uff0c\u53cc\u638c\u4e00\u9f50\u62cd\u51fa\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9ed8\u8fd0\u7384\u529f\uff0c\u5de6\u638c\u53f3\u6307\uff0c\u4ee5\u987b\u5f25\u638c\u529b\u548c\u4e00\u6307\u7985\u529f\u518d\u63a5\u4e86\u5b54\u96c0\u660e\u4f26\u738b\u7684\u4e00\u62db\uff0c\u8fd9\u4e00\u6b21 \u4ed6\u65e9\u6709\u51c6\u5907\uff0c\u53ea\u5b88\u4e0d\u653b\uff0c\u867d\u7136\u4ecd\u662f\u7ed9\u5b54\u96c0\u660e\u4f26\u738b\u7684\u638c\u529b\u9707\u9000\uff0c\u4f46\u5df2\u4e0d\u4f3c\u521a\u624d\u7684\u96be\u53d7\u3002 \u3000\u3000\u6cd5\u738b\u6697\u6697\u53f9\u6c14\uff0c\u9996\u5ea7\u62a4\u6cd5\u5f1f\u5b50\u9053\uff1a\u201c\u5e08\u5c0a\uff0c\u8d70\u4e86\u5427\uff1f\u201d\u6cd5\u738b\u6447\u4e86\u6447\u5934\uff0c\u9ed8\u9ed8\u65e0\u8a00\u3002\u8981\u77e5 \u4ed6\u5f53\u4e86\u51e0\u5341\u5e74\u7684\u6559\u4e3b\uff0c\u4e0e\u81ea\u6559\u5b9e\u5df2\u662f\u8840\u8089\u76f8\u8fde\uff0c\u5728\u8fd9\u7d27\u8981\u5173\u5934\uff0c\u54ea\u5fcd\u5f97\u4e0b\u5fc3\u98d8\u7136\u79bb\u53bb\u3002\u53ef\u662f \u4ed6\u4e5f\u60f3\u8d77\u4e86\u73b0\u5728\u5df2\u4e0d\u662f\u6559\u4e3b\u4e86\uff0c\u767d\u6559\u4e4b\u89c4\uff0c\u4e0a\u4e0b\u5c0a\u5351\u4e4b\u4efd\u6781\u4e25\uff0c\u4ed6\u53c8\u4e0d\u80fd\u5e72\u6d89\u4ed6\u7684\u5e08\u5f1f\u3002\u4e0d \u7531\u5f97\u5fc3\u4e71\u5982\u9ebb\uff0c\u8fdb\u9000\u7ef4\u8c37\u3002 \u3000\u3000\u8fd9\u65f6\u53e6\u5916\u7684\u4e24\u4e2a\u62a4\u6cd5\u5f1f\u5b50\uff0c\u4e00\u4e2a\u6276\u7740\u8fe6\u6bd7\u7f57\uff0c\u4e00\u4e2a\u62ff\u7740\u59ec\u6653\u98ce\uff0c\u53cc\u53cc\u8d70\u5230\u6cd5\u738b\u9762\u524d\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e24\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u89e3\u4e0d\u5f00\u8fe6\u5472\u7f57\u7684\u7a74\u9053\uff0c\u6545\u6b64\u6765\u5411\u6cd5\u738b\u6c42\u6551\u3002 \u3000\u3000\u6cd5\u738b\u671b\u4e86\u4e00\u773c\uff0c\u9732\u51fa\u8be7\u5f02\u795e\u8272\uff0c\u968f\u5373\u4f38\u51fa\u4e09\u6307\uff0c\u66ff\u8fe6\u6bd7\u7f57\u628a\u4e86\u628a\u8109\uff0c\u4ed6\u6b66\u5b66\u6df1\u6e5b\uff0c\u53ef\u4ee5 \u4ece\u4f24\u8005\u7684\u8109\u606f\u63a2\u6d4b\u51fa\u662f\u54ea\u4e00\u5904\u7a74\u9053\u88ab\u5c01\u3002 \u3000\u3000\u53ea\u89c9\u8fe6\u6bd7\u7f57\u7684\u8109\u606f\u5ffd\u7c97\u5ffd\u7ec6\uff0c\u51cc\u4e71\u65e0\u7ae0\uff0c\u6cd5\u738b\u8fd9\u4e00\u60ca\u66f4\u751a\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5947\u602a\uff0c\u8fd9\u662f\u54ea \u4e00\u5bb6\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u6211\u7adf\u7136\u5bdf\u89c9\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u4ed6\u6c89\u541f\u7247\u523b\uff0c\u7d22\u6027\u65bd\u5c55\u4e0a\u4e58\u5185\u529f\uff0c\u4ee5\u638c\u5fc3\u8d34\u770b\u8fe6\u6bd7\u7f57\u7684\u201c\u5929\u67a2\u7a74\u201d\uff0c\u5c06\u4e00\u80a1\u771f\u529b\u8f93\u9001\u8fdb \u53bb\u3002\u8fd9\u201c\u5929\u67a2\u7a74\u201d\u662f\u5947\u7ecf\u516b\u8109\u6587\u6c47\u4e4b\u70b9\uff0c\u8fe6\u6bd7\u7f57\u672c\u8eab\u7684\u529f\u529b\u4e0d\u5f31\uff0c\u518d\u52a0\u4e0a\u6cd5\u7389\u4ee5\u4e0a\u4e58\u5185\u529f\u76f8 \u52a9\uff0c\u767b\u65f6\u6d51\u8eab\u901a\u6cf0\uff0c\u4e0d\u5fc5\u7528\u89e3\u7a74\u6cd5\uff0c\u800c\u7a74\u9053\u5df2\u81ea\u89e3\u4e86\u3002 \u3000\u3000\u6cd5\u738b\u5ffd\u5730\u5931\u58f0\u5fae\u54a6\uff0c\u8fe6\u6bd7\u7f57\u4ee5\u4e3a\u662f\u81ea\u5df1\u53d7\u4e86\u5185\u4f24\uff0c\u6014\u6014\u5730\u671b\u770b\u6cd5\u738b\uff0c\u6cd5\u738b\u9053\uff1a\u201c\u4f60\u6ca1\u4e8b \u4e86\uff0c\u9000\u4e0b\u53bb\u6b47\u606f\u5427\u3002\u201d \u3000\u3000\u539f\u6765\u6cd5\u738b\u6b64\u9645\u6b63\u60f3\u8d77\u4e00\u4e2a\u4eba\u6765\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u96be\u9053\u662f\u91d1\u4e16\u9057\u6765\u4e86\uff1f\u201d\u4ed6\u66fe\u548c\u91d1\u4e16\u9057\u4ea4\u8fc7 \u624b\uff0c\u6df1\u77e5\u91d1\u4e16\u9057\u7684\u70b9\u7a74\u624b\u6cd5\u795e\u5999\u83ab\u6d4b\uff0c\u4ed6\u5f53\u5e74\u4e5f\u66fe\u5403\u8fc7\u82e6\u5934\u3002 \u3000\u3000\u6cd5\u738b\u53c8\u60ca\u53c8\u559c\uff0c\u628a\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u4e0e\u4ed6\u5e08\u5f1f\u4ea4\u624b\u7684\u90a3\u4e24\u4e2a\u4eba\uff0c\u6b63\u5728\u88ab\u8feb\u5f97\u6b65\u6b65\u540e\u9000\uff0c\u6cd5\u738b \u53c8\u4e0d\u7981\u5446\u4e86\u4e00\u5446\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u5bf9\u3002\u8fd9\u4e24\u4e2a\u4eba\u51b3\u4e0d\u662f\u91d1\u5927\u4fa0\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5c3c\u6cca\u5c14\u7684\u56db\u4e2a\u4f7f\u8005\u5728\u5b54\u96c0\u738b\u7684\u5fc3\u8179\u5f1f\u5b50\u5e26\u5f15\u4e4b\u4e0b\uff0c\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u9648\u592d\u5b57\u4e00\u770b\uff0c\u5176\u4e2d\u4e8c\u4eba\u6b63\u5c31\u662f\u90a3\u4e00\u665a\u5077\u5165\u4ed6\u5bb6\uff0c\u63b3\u8d70\u5e7d\u840d\u7684\u90a3\u4e24\u4e2a\u4eba\u3002\u8fd9\u6b63\u662f\u4ec7\u4eba\u89c1 \u9762\uff0c\u5206\u5916\u773c\u7ea2\uff0c\u9648\u5929\u5b57\u4e0d\u7531\u5f97\u6012\u4ece\u5fc3\u8d77\uff0c\u500f\u5730\u6495\u4e0b\u4e86\u9762\u5177\uff0c\u5927\u53eb\u9053\uff1a\u201c\u9648\u5929\u5b87\u5728\u6b64\uff0c\u4f60\u4eec\u8fd8 \u6211\u7684\u59bb\u5b50\u6765\uff01\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u4f7f\u8005\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u9648\u5929\u5b87\u5df2\u662f\u8fd0\u5251\u5982\u98ce\uff0c\u4e00\u62db\u201c\u53cc\u9f99\u51fa\u6d77\u201d\uff0c\u4e00 \u62db\u4e4b\u95f4\uff0c\u8fde\u88ad\u4e8c\u4eba\u3002 \u3000\u3000\u9648\u5929\u5b87\u4e4b\u6240\u4ee5\u6495\u4e0b\u9762\u5177\uff0c\u4e00\u6765\u662f\u56e0\u4e3a\u81ea\u5df1\u5360\u7740\u7406\uff0c\u7d22\u6027\u6446\u660e\u4e86\u548c\u4ed6\u4eec\u7406\u6ca6\uff1b\u4e8c\u6765\u4ed6\u5df2\u77e5 \u9053\u6cd5\u738b\u662f\u613f\u610f\u5e2e\u4ed6\u7684\uff0c\u90a3\u66f4\u72af\u4e0d\u7740\u7ed9\u4eba\u5f53\u4f5c\u5978\u7ec6\u4e86\u3002\u4ed6\u6253\u7b97\u4e00\u4e3e\u5236\u4f0f\u90a3\u4e24\u4e2a\u4f7f\u8005\uff0c\u53ea\u8981\u80fd\u591f \u62ff\u7740\u4e00\u4eba\uff0c\u5c31\u53ef\u4ee5\u4ee4\u5f97\u5b54\u96c0\u7389\u6295\u9f20\u5fcc\u5668\uff0c\u4e0d\u6562\u80e1\u6765\u3002\u201d\u767d\u6559\u4e0d\u4e4f\u660e\u7406\u4e4b\u4eba\uff0c\u53ea\u8981\u5b54\u96c0\u738b\u4e0d\u6562 \u80e1\u6765\uff0c\u4ed6\u5c31\u6709\u5e0c\u671b\u5f15\u8d77\u767d\u6559\u7684\u5185\u4e89\uff0c\u8bf4\u4e0d\u5b9a\u53ef\u4ee5\u4ee4\u5f97\u5b54\u96c0\u660e\u4f26\u738b\u521a\u5750\u4e0a\u6559\u4e3b\u4e4b\u4f4d\uff0c\u7acb\u5373\u53c8\u88ab \u63a8\u5012\u3002 \u3000\u3000\u4e0d\u6599\u90a3\u4e24\u4e2a\u4f7f\u8005\u6b66\u529f\u5927\u662f\u4e0d\u5f31\uff0c\u9648\u5929\u5b87\u75be\u653b\u4e24\u5251\uff0c\u90fd\u7ed9\u4ed6\u4eec\u907f\u5f00\uff0c\u7b2c\u4e09\u4e2a\u4f7f\u8005\u4e3e\u8d77\u4e86\u4e00 \u4e2a\u91d1\u94b5\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\u6321\u4e86\u9648\u5929\u5b87\u7684\u4e00\u5251\uff0c\u7b2c\u56db\u4e2a\u4f7f\u8005\u8131\u4e0b\u8888\u88df\uff0c\u4e25\u5982\u4e00\u6735\u7ea2\u4e91\uff0c\u5411\u9648\u5929\u5b87 \u5f53\u5934\u7f69\u4e0b\uff0c\u8fd9\u4eba\u6b66\u529f\u6700\u5f3a\uff0c\u9648\u5929\u5b87\u7684\u957f\u5251\u88ab\u4ed6\u7684\u8888\u88df\u64d2\u4f4f\uff0c\u65bd\u5c55\u4e0d\u5f00\uff0c\u5148\u5934\u90a3\u4e24\u4e2a\u4f7f\u8005\u8fd9\u65f6 \u4e5f\u7f13\u8ba9\u6c14\u672a\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u5411\u9648\u5929\u5b87\u7684\u4e24\u80c1\u6293\u53bb\u3002 \u3000\u3000\u773c\u770b\u9648\u5929\u5b87\u5c31\u8981\u88ab\u64d2\uff0c\u6cd5\u738b\u5ffd\u5730\u559d\u9053\uff1a\u201c\u8bf7\u5404\u4f4d\u4f4f\u624b\uff0c\u542c\u6211\u4e00\u8a00\uff01\u201d\u4ed6\u7684\u4f5b\u95e8\u72ee\u5b50\u543c \u529f\uff0c\u5df2\u7ec3\u5230\u4e86\u6700\u9ad8\u7684\u5883\u754c\uff0c\u53ef\u4ee5\u4f60\u4eba\u6216\u4e0d\u4f24\u4eba\uff0c\u8fd9\u65f6\u4ed6\u4e00\u5ff5\u548c\u5e73\uff0c\u7528\u7684\u72ee\u5b50\u543c\u529f\u4e2d\u7684\u201c\u5f53\u5934 \u68d2\u559d\u201d\uff0c\u4eba\u4eba\u542c\u4e86\uff0c\u90fd\u4e0d\u89c9\u5fc3\u5934\u4e00\u9707\uff0c\u5c24\u5176\u662f\u5fc3\u4e2d\u5b58\u6709\u6076\u5ff5\u7684\u4eba\uff0c\u7531\u4e8e\u6d4a\u6c14\u4e0a\u6d8c\uff0c\u5fc3\u7eea\u672c\u5c31 \u4e0d\u5b81\uff0c\u88ab\u4ed6\u8fd9\u7a81\u5982\u5176\u6765\u7684\u201c\u5f53\u5934\u68d2\u559d\u201d\uff0c\u9707\u6050\u5f97\u66f4\u4e3a\u5389\u5bb3\uff0c\u4f46\u5374\u4e0d\u4f1a\u53d7\u5230\u635f\u4f24\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u5c3c\u6cca\u5c14\u4f7f\u8005\u5fc3\u5934\u4e00\u9707\uff0c\u4fa7\u6b65\u8dc4\u8e09\uff0c\u53cc\u624b\u6293\u7a7a\u3001\u4e0d\u7531\u5f97\u5927\u529b\u60ca\u6115\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u6cd5 \u738b\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u6cd5\u738b\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u9648\u6a80\u8d8a\uff0c\u8001\u8872\u521a\u624d\u5728\u8fd9\u91cc\u6240\u8bf4\u7684\uff0c\u60f3\u6765\u4f60\u90fd\u5df2\u542c\u5230\u4e86\uff0c\u6211\u5148\u5411\u4f60\u8ba4 \u9519\uff01\u201d\u9648\u5929\u5b87\u6025\u5fd9\u56de\u793c\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u8fd8\u671b\u6cd5\u738b\u4e3b\u6301\u516c\u9053\u3002\u201d \u3000\u3000\u6cd5\u738b\u9053\uff1a\u201c\u8bf7\u6055\u8001\u8872\u65e0\u80fd\u4e3a\u529b\u3002\u4f60\u7684\u59bb\u5b50\u65e9\u5df2\u4e0d\u5728\u672c\u5bfa\uff0c\u4f60\u8981\u89c1\u5979\uff0c\u9664\u975e\u662f\u5230\u5c3c\u6cca\u5c14\u53bb \u4e86\u3002\u201d \u3000\u3000\u6cd5\u738b\u53c8\u5411\u90a3\u56db\u4e2a\u4f7f\u8005\u8bf4\u9053\uff1a\u201c\u8d35\u56fd\u56fd\u738b\u8981\u628a\u51b0\u5ddd\u5929\u5973\u53ca\u5176\u4ece\u4eba\u63a5\u56de\u56fd\u53bb\uff0c\u8fd9\u662f\u8d35\u56fd\u7684\u4e8b \u60c5\uff0c\u8001\u8896\u4e0d\u6562\u5e72\u9884\u3002\u4f46\u8fd9\u4f4d\u9648\u6a80\u8d8a\u5931\u4e86\u59bb\u5b50\u3002\u6025\u4e8e\u627e\u56de\uff0c\u8fd9\u4e5f\u662f\u4eba\u4e4b\u5e38\u60c5\u3002\u4ed6\u4e0d\u662f\u8d35\u56fd\u5b50 \u6c11\uff0c\u8d35\u56fd\u56fd\u738b\u4e5f\u6ca1\u6709\u547d\u4ee4\u8981\u4f60\u4eec\u6349\u4ed6\uff0c\u5c31\u8bf7\u4f60\u4eec\u770b\u5728\u8001\u8872\u4efd\u4e0a\uff0c\u8ba9\u4ed6\u8d70\u5427\u3002\u6211\u5e76\u4e0d\u60f3\u5e2e\u54ea\u4e00 \u65b9\uff0c\u53ea\u60f3\u8bf7\u4f60\u4eec\u53cc\u65b9\u606f\u4e89\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u4e00\u60f3\uff0c\u8fd9\u4ef6\u4e8b\u7684\u786e\u662f\u53ea\u80fd\u5411\u5c3c\u6cca\u5c14\u738b\u7406\u8bba\uff0c\u5411\u5c3c\u6cca\u5c14\u738b\u8981\u4eba\uff0c\u8fd9\u4e24\u4e2a\u4f7f\u8005\u4e0d\u8fc7\u662f \u5949\u547d\u800c\u4e3a\uff0c\u4f5c\u4e0d\u4e86\u4e3b\uff0c\u56e0\u6b64\u5c31\u662f\u6740\u4e86\u4ed6\u4eec\uff0c\u4e5f\u6ca1\u6709\u7528\u3001\u4ed6\u5982\u6b64\u4e00\u60f3\uff0c\u5bf9\u90a3\u4e24\u4e2a\u4f7f\u8005\u7684\u6068\u610f\u4fbf \u51cf\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u5c3c\u6cca\u5c14\u4f7f\u8005\uff0c\u542c\u4e86\u6cd5\u738b\u7684\u8fd9\u756a\u8bdd\uff0c\u5374\u90fd\u662f\u6728\u7136\u6beb\u65e0\u8868\u60c5\uff0c\u5e76\u4e0d\u8868\u793a\u53ef\u5426\uff0c\u53ea\u662f\u628a \u773c\u775b\u671d\u7740\u5b54\u96c0\u660e\u4f26\u738b\u770b\u53bb\uff0c\u4f3c\u4e4e\u8981\u542c\u5b54\u96c0\u738b\u7684\u4e3b\u610f\u3002 \u3000\u3000\u6cd5\u738b\u9053\uff1a\u201c\u9648\u6a80\u8d8a\uff0c\u8fd9\u51e0\u4f4d\u662f\u548c\u4f60\u4e00\u9053\u6765\u7684\u5427\uff1f\u201d\u9648\u5929\u5b57\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u4eec\u90fd\u662f\u7ed9\u6211\u5e2e \u5fd9\u7684\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u6cd5\u738b\u9762\u5411\u5e08\u5f1f\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u8fd9\u51e0\u4e2a\u4eba\u5e76\u975e\u5978\u7ec6\uff0c\u8bf7\u4f60\u8ba9\u4ed6\u4eec\u90fd\u8d70\u4e86\u5427\u3002\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u6211\u73b0\u5728\u5df2\u53d7\u8058\u4e3a\u5c3c\u6cca\u5c14\u56fd\u5e08\uff0c\u4e0d\u7ba1\u4ed6\u4eec\u662f\u4e0d\u662f\u5978\u7ec6\uff0c\u6211 \u90fd\u8981\u62ff\u53bb\u7ed9\u5384\u6cca\u5c14\u56fd\u738b\u53d1\u843d\uff01\u201d \u3000\u3000\u6cd5\u738b\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u8fd9\u662f\u4e0d\u662f\u505a\u5f97\u6709\u70b9\u8fc7\u4efd\u4e86\uff1f\u8bf7\u4f60\u518d\u542c\u6211\u4e00\u8a00\u2026\u2026\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u51b7\u7136\u4e00\u7b11\uff0c\u6ee1\u8138\u4e0d\u8010\u70e6\u7684\u795e\u6c14\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u662f\u4f60\u4f5c\u4e3b\u8fd8\u662f\u6211\u4f5c \u4e3b\uff1f\u201d\u6cd5\u738b\u751a\u662f\u5c34\u5c2c\uff0c\u53ea\u5f97\u7b54\u9053\uff1a\u201c\u5f53\u7136\u662f\u7531\u6559\u4e3b\u4f5c\u4e3b\u3002\u201d\u5b54\u96c0\u660e\u4f26\u738b\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3 \u4f60\u5c31\u4e0d\u5fc5\u591a\u8a00\u3002\u6211\u81ea\u6709\u4e3b\u610f\uff01\u4f60\u5df2\u79bb\u5f00\u672c\u6559\uff0c\u5018\u4e0d\u613f\u610f\u542c\u6211\u53f7\u4ee4\uff0c\u5c3d\u7ba1\u8896\u624b\u65c1\u89c2\uff0c\u4f46\u5374\u4e0d\u80fd \u5e2e\u52a9\u654c\u4eba\uff0c\u5426\u5219\u5c31\u662f\u72af\u4e86\u53db\u6559\u4e4b\u7f6a\uff01\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u628a\u624b\u4e00\u6325\uff0c\u53eb\u9053\uff1a\u201c\u5c06\u4eca\u65e5\u6df7\u8fdb\u6765\u7684\u5978\u7ec6 \u90fd\u62ff\u4e0b\u4e86\uff0c\u5929\u5927\u7684\u4e8b\uff0c\u6709\u6211\u62c5\u5f53\uff01\u201d \u3000\u3000\u90a3\u56db\u4e2a\u5c3c\u6cca\u5c14\u4f7f\u8005\u5411\u6cd5\u738b\u65bd\u4e86\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u6055\u6211\u4eec\u53ea\u80fd\u542c\u4ece\u8d35\u6559\u6559\u4e3b\u7684\u547d\u4ee4\u3002\u201d\u968f \u5373\u53c8\u5411\u9648\u5929\u5b87\u5c55\u5f00\u653b\u51fb\u3002 \u3000\u3000\u6cd5\u738b\u6c14\u5f97\u9762\u8272\u94c1\u9752\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u505a\u9519\u4e86\u4e8b\u60c5\uff0c\u518d\u8ba9\u4ed6\u4eec\u628a\u9648\u5929\u5b87\u64d2\u53bb\uff0c\u5c82\u4e0d\u662f \u9519\u4e0a\u52a0\u9519\uff1f\u201d\u4f46\u968f\u5373\u60f3\u5230\uff1a\u201c\u6211\u8981\u51fa\u624b\u5236\u6b62\u4e0d\u96be\uff0c\u4f46\u90a3\u6837\u4e00\u6765\uff0c\u52bf\u5c06\u95f9\u5f97\u96be\u6536\u62fe\uff0c\u53ea\u6015\u672c\u6559 \u9a6c\u4e0a\u5c31\u8981\u5206\u88c2\u3002\u4f46\u6211\u65e2\u7136\u8ba9\u4e86\u6559\u4e3b\u4e4b\u4f4d\u7ed9\u5e08\u5f1f\uff0c\u53c8\u600e\u597d\u9a6c\u4e0a\u62c6\u4ed6\u7684\u53f0\uff1f\u201d\u4e00\u65f6\u5fc3\u610f\u8e0c\u8e87\uff0c\u8fdb \u9000\u7ef4\u8c37\u3002 \u3000\u3000\u6c5f\u5357\u7236\u5b50\uff0c\u8fd9\u65f6\u4e5f\u628a\u9762\u5177\u626f\u4e86\u4e0b\u6765\u3002\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u6cd5\u738b\uff0c\u4f60\u662f\u4e2a\u597d\u4eba\uff0c\u4f60\u522b\u96be\u8fc7\u3001\u6211\u4e0d \u602a\u4f60\u3002\u53ea\u8981\u4f60\u8896\u624b\u65c1\u89c2\uff0c\u6211\u4eec\u5df2\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d\u6cd5\u738b\u542c\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5fc3\u91cc\u66f4\u662f\u96be\u8fc7\u3002 \u3000\u3000\u6c5f\u5357\u53c8\u53eb\u9053\uff1a\u201c\u5582\uff0c\u6c42\u4f60\u4eec\u522b\u8981\u4e3a\u96be\u6211\u7684\u59ec\u5927\u54e5\uff0c\u884c\u4e0d\u884c\uff1f\u201d\u539f\u6765\u8fd9\u65f6\u6b63\u6709\u4e00\u4e2a\u62a4\u6cd5\u5f1f \u5b50\u8981\u5c06\u59ec\u6653\u98ce\u63d0\u53bb\u5211\u5802\u3002\u4f46\u4ed6\u5374\u8fd8\u672a\u77e5\u9053\u8fd9\u4eba\u5c31\u662f\u59ec\u6653\u98ce\u3002\u201d \u3000\u3000\u6cd5\u738b\u5fc3\u4e2d\u4e00\u52a8\uff0c\u731c\u5230\u4e86\u51e0\u5206\uff0c\u4f7f\u51fa\u4e86\u9694\u7a7a\u89e3\u7a74\u7684\u7edd\u9876\u795e\u529f\uff0c\u4e2d\u6307\u865a\u7a7a\u4e00\u70b9\uff0c\u4e00\u80a1\u6c14\u6d41\uff0c \u5c04\u5230\u4e86\u59ec\u6653\u98ce\u80cc\u4e0a\u706d\u67a2\u7a74\u7684\u90e8\u4f4d\uff0c\u59ec\u6653\u98ce\u624b\u8db3\u767b\u65f6\u80fd\u591f\u6d3b\u52a8\uff0c\u731b\u5730\u8df3\u4e86\u8d77\u6765\uff0c\u624b\u8098\u4e00\u649e\uff0c\u53cd \u800c\u628a\u90a3\u62a4\u6cd5\u5f1f\u5b50\u649e\u7ffb\u4e86\u3002 \u3000\u3000\u59ec\u6653\u98ce\u51b2\u4e0a\u524d\u53bb\uff0c\u201c\u5478\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u53e3\u6d53\u75f0\uff0c\u5c31\u5411\u5b54\u96c0\u660e\u4f26\u738b\u5410\u53bb\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u6325\u8896\u4e00 \u62c2\uff0c\u5c06\u59ec\u6653\u98ce\u62c2\u5f97\u5012\u9000\u4e09\u6b65\uff0c\u4f46\u5b54\u96c0\u660e\u4f26\u738b\u8fd9\u65f6\u9762\u4e34\u5927\u654c\uff0c\u5df2\u4e0d\u80fd\u5168\u529b\u6765\u5bf9\u4ed8\u59ec\u6653\u98ce\uff0c\u59ec\u6653 \u98ce\u6478\u5230\u4e86\u4ed6\u7684\u5f31\u70b9\uff0c\u6709\u610f\u62a5\u88ab\u64d2\u4e4b\u4ec7\uff0c\u8d81\u7740\u6c5f\u6d77\u5929\u53d1\u638c\u7684\u65f6\u5019\uff0c\u731b\u5730\u53c8\u6b3a\u8eab\u76f4\u8fdb\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u5df1\u7136\u5c06\u4ed6\u62c2\u9000\uff0c\u600e\u4e5f\u6599\u4e0d\u5230\u4ed6\u8fd8\u6562\u5982\u6b64\u5927\u80c6\uff0c\u51b7\u4e0d\u53ca\u9632\uff0c\u59ec\u6653\u98ce\u5df2\u6251\u5230\u9762 \u524d\uff0c\u4e00\u638c\u62cd\u4e0b\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u5de5\u5ffd\u89c9\u4e00\u80a1\u5947\u5bd2\u88ad\u5230\uff0c\u4ed6\u7684\u638c\u529b\u5df2\u53d1\u4e86\u51fa\u53bb\u4e0e\u6c5f\u6d77\u5929\u7684\u638c\u529b\u6b63\u9762\u76f8\u6297\uff0c\u4e00\u65f6\u4e4b \u95f4\uff0c\u96be\u4ee5\u64a4\u56de\uff0c\u8fde\u5fd9\u6c89\u80a9\u4e00\u649e\uff0c\u59ec\u6653\u98ce\u4f55\u7b49\u6e9c\u6ed1\uff0c\u95ea\u7535\u822c\u7684\u4e00\u626f\uff0c\u626f\u8131\u4e86\u4ed6\u7684\u4e00\u64ae\u80e1\u5b50\uff0c\u54c8 \u54c8\u5927\u7b11\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u5df2\u8131\u51fa\u4e86\u4ed6\u638c\u529b\u7684\u8303\u56f4\u3002\u5012\u662f\u5b54\u96c0\u660e\u4f26\u738b\u53d7\u4e86\u4ed6\u7684\u4fee\u7f57\u9634\u715e\u529f\u7684\u9634\u715e\u4e4b \u6c14\uff0c\u867d\u7136\u4ed7\u7740\u5185\u529f\u6df1\u539a\uff0c\u4e0d\u81f3\u91cd\u4f24\uff0c\u4f46\u4e5f\u8981\u9ed8\u8fd0\u7384\u529f\uff0c\u5c06\u653b\u8fdb\u4f53\u5185\u7684\u9634\u715e\u4e4b\u6c14\u6392\u9664\u51fa\u53bb\u3002\u4e5f \u5e78\u4e8f\u59ec\u6653\u98ce\u6562\u4e8e\u8fd9\u6837\u7684\u5192\u9669\u4e00\u51fb\uff0c\u65e0\u5f62\u4e2d\u7b49\u4e8e\u5e2e\u4e86\u6c5f\u6d77\u5929\u4e00\u4e2a\u5927\u5fd9\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u4e3a\u4e86\u8981\u5206\u7528 \u771f\u6c14\uff0c\u5bf9\u4ed8\u6c5f\u6d77\u5929\u7684\u529f\u529b\u5c31\u524a\u5f31\u4e86\u4e24\u4e09\u5206\uff0c\u7adf\u7ed9\u6c5f\u6d77\u5929\u6273\u6210\u4e86\u5e73\u624b\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u53eb\u9053\uff1a\u201c\u59ec\u4f2f\u4f2f\uff0c\u4f60\u53bb\u5e2e\u5fd9\u9648\u4f2f\u4f2f\u5427\uff01\u201d\u9648\u5929\u5b87\u529b\u654c\u90a3\u56db\u4e2a\u5c3c\u6cca\u5c14\u4f7f\u8005\uff0c\u6b63\u81ea\u652f \u6301\u4e0d\u4f4f\uff0c\u5f97\u59ec\u6653\u98ce\u5e2e\u5fd9\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u6682\u65f6\u7a33\u4f4f\u4e86\u9635\u811a\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u82e6\u82e6\u76f8\u8feb\uff0c\u8bf7\u6055\u6211\u4e5f\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d\u5b54\u96c0\u660e\u4f26\u738b\u8fd9\u65f6\u8fd8\u5728\u8fd0\u529f\u6392 \u9664\u9634\u715e\u4e4b\u6c14\uff0c\u638c\u529b\u7f69\u4ed6\u4e0d\u4f4f\uff0c\u6c5f\u6d77\u5929\u8d8b\u6b64\u673a\u4f1a\uff0c\u7acb\u5373\u628a\u5b9d\u5251\u62d4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5251\u5149\u4e00\u8d77\uff0c\u592d\u77eb\u5982\u9f99\uff0c\u5728\u65b9\u5706\u6570\u4e08\u4e4b\u5185\u7ad9\u7acb\u7684\u5587\u561b\uff0c\u90fd\u89c9\u51b7\u6c14\u68ee\u68ee\uff0c\u5bd2\u5149\u8000\u76ee\uff0c\u614c\u4e0d\u8fed \u7684\u540e\u9000\u3002\u5b54\u96c0\u660e\u4f26\u738b\u5c55\u8896\u4e00\u62c2\uff0c\u53ea\u542c\u5f97\u201c\u54e7\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u6761\u8863\u8896\u5df2\u968f\u7740\u5251\u5149\u98de\u53bb\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u970d\u5730\u4e00\u4e2a\u6643\u8eab\uff0c\u521b\u8bc0\u4e00\u9886\uff0c\u6377\u5982\u98de\u9e1f\u822c\u7684\u76f4\u5411\u90a3\u56db\u4e2a\u4f7f\u8005\u51b2\u53bb\uff0c\u6b66\u529f\u6700\u9ad8\u7684\u90a3\u4e2a \u4f7f\u8005\u4e3e\u8d77\u91d1\u94b5\u4e00\u6321\uff0c\u54ea\u77e5\u6c5f\u6d77\u5929\u8fd9\u67c4\u5b9d\u5251\u7adf\u6709\u65ad\u91d1\u5207\u7389\u4e4b\u80fd\uff0c\u4f46\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u4ed6\u90a3\u4e2a \u7eaf\u91d1\u7684\u76c2\u94b5\u4e5f\u7ed9\u5b9d\u5251\u7a7f\u8fc7\u4e86\uff01 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u5927\u6012\uff0c\u593a\u8fc7\u4e86\u4e00\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u7684\u4e5d\u73af\u9521\u6756\uff0c\u4e00\u62db\u201c\u5927\u6f20\u5b64\u70df\u201d\uff0c\u5411\u6c5f\u6d77\u5929\u7684\u624b \u8155\u75be\u70b9\u3002\u6c5f\u6d77\u5929\u6a2a\u5251\u4e00\u524a\uff0c\u628a\u6756\u5934\u524a\u53bb\u4e86\u4e00\u6bb5\uff0c\u53ef\u662f\u4ed6\u7684\u5b9d\u5251\u4e5f\u7ed9\u5b54\u96c0\u738b\u7528\u4e86\u4e00\u4e2a\u201c\u7c98\u201d\u5b57 \u51b3\u7c98\u5f00\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5b54\u96c0\u738b\u7684\u7b2c\u4e8c\u62db\u53c8\u5df2\u53d1\u51fa\u3002\u8fd9\u4e8c\u62db\u540d\u4e3a\u201c\u957f\u6cb3\u843d\u65e5\u201d\uff0c\u4e5d\u73af\u9521\u6756 \u821e\u6210\u4e86\u4e00\u9053\u5706\u5708\u3001\u628a\u6c5f\u6d77\u5929\u8fde\u4eba\u5e26\u5251\u5708\u4f4f\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8fd8\u4e86\u4e00\u62db\u201c\u516b\u65b9\u98ce\u96e8\u201d\uff0c\u5251\u5149\u5411\u56db\u9762\u516b\u65b9\u6563\u5f00\uff0c\u4f46\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c \u539f\u6765\u5728\u90a3\u77ac\u606f\u4e4b\u95f4\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u7684\u9521\u6756\u5df2\u4e0e\u6c5f\u6d77\u5929\u7684\u5b9d\u5251\u78b0\u51fb\u4e86\u6570\u5341\u4e0b\uff0c\u53ea\u56e0\u53cc\u65b9\u52a8\u4f5c\u90fd\u5feb \u5230\u4e86\u6781\u70b9\uff0c\u5b9d\u5251\u867d\u7136\u78b0\u5230\u4e86\u9521\u6756\uff0c\u5374\u7b49\u4e8e\u6ed1\u8fc7\u4e00\u822c\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u4ee5\u6700\u4e0a\u4e58\u7684\u5378\u529b\uff0c\u5378\u53bb\u4e86\u6c5f \u6d77\u5929\u7684\u4e03\u516b\u5206\u529b\u9053\uff0c\u6545\u6b64\u4ed6\u7684\u4e5d\u73af\u9521\u6756\u867d\u662f\u4f24\u75d5\u6591\u9a73\uff0c\u4f46\u5374\u5e76\u6ca1\u6709\u7ed9\u6c5f\u6d77\u5929\u7684\u5b9d\u5251\u524a\u65ad\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u8fd9\u65f6\u5df2\u628a\u4f53\u5185\u7684\u9634\u715e\u4e4b\u6c14\u6392\u9664\u51c0\u5c3d\uff0c\u53ef\u7528\u5168\u526f\u7cbe\u795e\u6765\u5bf9\u4ed8\u6c5f\u6d77\u5929\u3002\u5728\u5175\u5668\u4e0a \u4ed6\u867d\u7136\u5927\u5927\u5403\u4e8f\uff0c\u4f46\u4ed6\u7684\u5185\u529f\u6df1\u539a\u4e4b\u6781\uff0c\u6bd4\u6c5f\u6d77\u5929\u5b9e\u4e0d\u6b62\u80dc\u8fc7\u4e00\u7b79\uff0c\u6bcf\u6b21\u5251\u6756\u76f8\u4ea4\uff0c\u6c5f\u6d77\u5929 \u90fd\u88ab\u4ed6\u7684\u5185\u529b\u9707\u64bc\uff0c\u604d\u5982\u5728\u60ca\u7977\u9a87\u6d6a\u4e4b\u4e2d\u98a0\u7c38\u7684\u5c0f\u8239\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u817e\u51fa\u5de6\u624b\uff0c\u547c\u547c\u4e24\u638c\uff0c\u628a\u9648\u5929\u5b87\u9707\u9000\uff0c\u59ec\u6653\u98ce\u4e5f\u4e0d\u5f97\u4e0d\u95ea\u8fc7\u4e00\u8fb9\uff0c\u8fd9\u4e48\u4e00 \u6765\uff0c\u6c5f\u6d77\u5929\u4e0e\u4ed6\u4eec\u7684\u8054\u7edc\u767b\u65f6\u53c8\u88ab\u5207\u65ad\u3002\u5b54\u96c0\u660e\u4f26\u738b\u8ba4\u5b9a\u4e86\u6c5f\u6d77\u5929\u4e43\u662f\u6700\u5f3a\u7684\u654c\u4eba\uff0c\u7acb\u5fc3\u8981 \u4ee5\u5168\u526f\u7cbe\u795e\uff0c\u5148\u6253\u5012\u4e86\u8fd9\u4e2a\u6700\u5927\u7684\u5f3a\u654c\u3002 \u3000\u3000\u6c5f\u5357\u5374\u62bd\u51fa\u8eab\u6765\uff0c\u52a9\u9648\uff0c\u59ec\u4e8c\u4eba\u5408\u6218\u90a3\u56db\u4e2a\u5c3c\u6cca\u5c14\u4f7f\u8005\uff0c\u6b66\u529f\u6700\u9ad8\u7684\u90a3\u4e2a\u4f7f\u8005\u91d1\u94b5\u5df2\u88ab \u6233\u7a7f\uff0c\u867d\u7136\u8fd8\u53ef\u4ee5\u4f7f\u7528\uff0c\u4f46\u5a01\u529b\u5df2\u662f\u5927\u51cf\uff0c\u6b64\u6d88\u5f7c\u957f\uff0c\u53cc\u65b9\u6070\u6070\u6253\u6210\u5e73\u624b\u3002 \u3000\u3000\u65f6\u95f4\u7a0d\u957f\uff0c\u6c5f\u6d77\u5929\u5df2\u6e10\u5904\u4e0b\u98ce\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u4f7f\u51fa\u6700\u4e0a\u4e58\u7684\u201c\u9694\u7269\u4f20\u529f\u201d\u672c\u9886\uff0c\u5b9d\u5251\u53ea\u8981 \u78b0\u5230\u4ed6\u7684\u9521\u6756\uff0c\u6c5f\u6d77\u5929\u7684\u864e\u53e3\u4fbf\u662f\u4e00\u9635\u9178\u9ebb\uff0c\u6e10\u6e10\u529b\u4e0d\u4ece\u5fc3\uff0c\u5251\u62db\u4e5f\u5c31\u8fdf\u7f13\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u6cd5\u738b\u6b63\u5728\u5fe7\u5fc3\u5982\u711a\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\uff1f\u5ffd\u89c1\u4e00\u4e2a\u5587\u561b\u98de\u8dd1\u8fdb\u6765\uff0c\u614c\u614c\u5f20\u5f20\u5730\u7980\u544a\u9053\uff1a\u201c\u51b0 \u5ddd\u5929\u5973\u6c42\u89c1\u5e08\u5c0a\uff01\u201d\u6cd5\u738b\u5927\u559c\u9053\uff1a\u201c\u5feb\u8bf7\u5979\u8fdb\u6765\uff01\u201d\u90a3\u5f1f\u5b50\u628a\u773c\u659c\u6e9c\u5b54\u96c0\u660e\u4f26\u738b\uff0c\u5b54\u96c0\u660e\u4f26 \u738b\u5927\u6012\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u600e\u4e48\u53ef\u4ee5\u64c5\u81ea\u4f5c\u4e3b\uff1f\u201d\u6cd5\u738b\u5e73\u5fc3\u9759\u6c14\u8bf4\u9053\uff1a\u201c\u6211\u867d\u7136\u4e0d\u5728\u6559\u4e3b\u4e4b\u4f4d\uff0c \u4f46\u6709\u670b\u53cb\u6765\u8bbf\uff0c\u6211\u603b\u53ef\u4ee5\u63a5\u89c1\u5427\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u5510\u7ecf\u5929\u592b\u5987\u5df2\u8e0f\u8fdb\u5927\u95e8\uff0c\u5728\u4ed6\u4eec\u80cc\u540e\uff0c \u8fd8\u8ddf\u7740\u56db\u4e2a\u5f85\u5973\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u4e2a\u4f8d\u5973\u6025\u6b65\u4e0a\u524d\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u5929\u5b87\uff0c\u5929\u5b87\uff01\u201d\u58f0\u97f3\u4e2d\u5145\u6ee1\u60ca\u559c\u4e4b\u60c5\u3002 \u3000\u3000\u9648\u5929\u5b87\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u56de\u5934\u4e00\u770b\uff0c\u53ef\u4e0d\u6b63\u662f\u81ea\u5df1\u7684\u59bb\u5b50\u662f\u8c01\uff1f\u4ed6\u5927\u53eb\u9053\uff1a\u201c\u840d \u59b9\uff0c\u8fd9\u4e0d\u662f\u505a\u68a6\u4e86\u5427\uff01\u201d\u5404\u81ea\u5411\u5bf9\u65b9\u5954\u53bb\uff0c\u7d27\u7d27\u62b1\u5728\u4e00\u8d77\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u5c3c\u6cca\u5c14\u4f7f\u8005\u89c1\u4e86\u51b0\u5ddd\u5929\u5973\uff0c\u5df2\u662f\u53d1\u614c\uff0c\u518d\u89c1\u5e7d\u840d\u4e5f\u5728\u6b64\u5904\u73b0\u8eab\uff0c\u6599\u60f3\u56fd\u5185\u6709\u53d8\uff0c \u60ca\u60f6\u66f4\u751a\uff0c\u54ea\u91cc\u8fd8\u6562\u963b\u62e6\u3002 \u3000\u3000\u4e3a\u9996\u7684\u90a3\u4e2a\u4f7f\u8005\u7387\u9886\u540c\u4f34\uff0c\u4e0a\u524d\u884c\u793c\u9053\uff1a\u201c\u4e0d\u77e5\u516c\u4e3b\u83b2\u9a7e\u5230\u6765\uff0c\u6709\u5931\u8fdc\u8fce\uff0c\u8fd8\u671b\u6055 \u7f6a\u3002\u201d\u51b0\u5ddd\u5929\u5973\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e3a\u9996\u7684\u4f7f\u8005\u9053\uff1a\u201c\u6211\u4eec\u662f\u5949\u4e86\u56fd\u4e3b\u4e4b\u547d\uff0c\u5230\u6b64\u8fce\u63a5\u56fd\u5e08\u7684\u3002\u201d\u7b2c\u4e8c\u4e2a\u4f7f\u8005\u9053\uff1a\u201c\u56fd\u738b\u8bf7 \u516c\u4e3b\u56de\u56fd\uff0c\u6211\u4eec\u51c6\u5907\u5728\u63a5\u4e86\u56fd\u5e08\u4e4b\u540e\uff0c\u5c31\u4e0a\u51b0\u5bab\u4fc3\u9a7e\u7684\uff0c\u4e0d\u60f3\u516c\u4e3b\u4eca\u65e5\u5230\u6765\uff0c\u90a3\u662f\u6700\u597d\u4e5f\u6ca1 \u6709\u4e86\u3002\u8bf7\u516c\u4e3b\u793a\u4e0b\uff0c\u662f\u5426\u4e0e\u56fd\u5e08\u540c\u884c\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6211\u81ea\u5df1\u7684\u56fd\u5bb6\uff0c\u6211\u51e0\u65f6\u6b22\u559c\u56de\u53bb\u5c31\u51e0\u65f6\u56de\u53bb\uff1b\u7528\u4e0d\u7740\u4f60\u4eec\u6765\u8bf7\u3002\u4f60\u4eec\u5feb \u7ed9\u6211\u6eda\u51fa\u53bb\uff01\u201d \u3000\u3000\u90a3\u56db\u4e2a\u4f7f\u8005\u9762\u9762\u60f3\u89d1\uff0c\u671b\u4e00\u671b\u5b54\u96c0\u660e\u4f26\u738b\u53c8\u671b\u4e00\u671b\u51b0\u5ddd\u5929\u5973\uff0c\u4ed6\u4eec\u4f3c\u4e4e\u610f\u88ab\u52a8\u624b\uff0c\u4f46\u4e3a \u51b0\u5ddd\u5929\u5973\u7684\u5a01\u4e25\u9707\u6151\uff0c\u5374\u53c8\u4e0d\u6562\u4e0a\u524d\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u67f3\u7709\u4e00\u7ad6\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u4eec\u6562\u4e0d\u542c\u6211\u7684\u5429\u5490\uff1f\u597d\uff0c\u5e7d\u840d\uff0c\u4f60\u628a\u4ed6\u4eec\u90fd\u7f1a\u8d77\u6765\uff0c\u62bc \u56de\u672c\u56fd\uff0c\u542c\u5019\u65b0\u738b\u53d1\u843d\uff01\u201d \u3000\u3000\u90a3\u56db\u4e2a\u4f7f\u8005\u542c\u5f97\u201c\u65b0\u738b\u201d\u4e8c\u5b57\uff0c\u5413\u5f97\u90fd\u8df3\u4e86\u8d77\u6765\u3002\u8fd9\u56db\u4e2a\u4f7f\u8005\u90fd\u6709\u9ad8\u6df1\u7684\u6b66\u529f\uff0c\u5728\u672c\u56fd \u5730\u4f4d\u4e5f\u751a\u9ad8\u3002\u4ed6\u4eec\u867d\u7136\u542c\u8bf4\u8fc7\u51b0\u5ddd\u5929\u5973\u7684\u5389\u5bb3\uff0c\u4f46\u56e0\u51b0\u5ddd\u5929\u5973\u81ea\u5e7c\u79bb\u5f00\u672c\u56fd\uff0c\u7a76\u7adf\u672c\u9886\u5982 \u4f55\uff0c\u4ed6\u4eec\u5374\u662f\u672a\u66fe\u89c1\u8fc7\u3002\u4ed6\u4eec\u4e00\u6765\u6043\u6709\u56fd\u738b\u7684\u547d\u4ee4\uff0c\u4e8c\u6765\u6043\u7740\u4e00\u8eab\u6b66\u529f\uff0c\u54ea\u80af\u675f\u624b\u53d7\u64d2\uff0c\u4e0d \u7ea6\u800c\u540c\u7684\u4e2a\u4e2a\u4e3e\u8d77\u4e86\u5175\u5668\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u65a5\u9053\uff1a\u201c\u5927\u80c6\uff01\u201d\u628a\u624b\u4e00\u626c\uff0c\u51b0\u9b44\u795e\u5f39\u8fde\u73e0\u53d1\u51fa\uff0c\u5979\u7684\u51b0\u5f39\u4e43\u5929\u4e0b\u6700\u795e\u5947\u7684\u6697 \u5668\uff1a\u518d\u7ecf\u8fc7\u5979\u5728\u51b0\u5bab\u591a\u5e74\u7684\u82e6\u7ec3\uff0c\u624b\u6cd5\u66f4\u4e3a\u5965\u5999\uff0c\u90a3\u51e0\u679a\u51b0\u9b44\u795e\u5f39\u4e0d\u662f\u94bb\u8fdb\u8033\u5b54\uff0c\u5c31\u662f\u6253\u5165 \u9f3b\u5b54\uff0c\u4e00\u4e2a\u4f7f\u8005\u6b63\u60f3\u547c\u5438\uff0c\u90a3\u679a\u51b0\u9b44\u795e\u5f39\u5c31\u98de\u5165\u4ed6\u7684\u53e3\u4e2d\uff0c\u603b\u4e4b\u65e0\u4e00\u843d\u7a7a\uff0c\u56db\u4e2a\u4f7f\u8005\uff0c\u90fd \u201c\u5c1d\u201d\u4e86\u51b0\u9b44\u795e\u5f39\u7684\u6ecb\u5473\u3002 \u3000\u3000\u4ee5\u4ed6\u4eec\u56db\u4eba\u7684\u529f\u529b\uff0c\u5018\u82e5\u51b0\u5f39\u53ea\u662f\u5728\u4ed6\u4eec\u9762\u524d\u70b8\u88c2\u3001\u4ed6\u4eec\u4e5f\u8bb8\u8fd8\u53ef\u4ee5\u5fcd\u53d7\u90a3\u80a1\u5947\u5bd2\u4e4b \u6c14\uff0c\u4f46\u73b0\u5728\u51b0\u5f39\u8fdb\u5165\u4ed6\u4eec\u7684\u4f53\u5185\uff0c\u767b\u65f6\u4ee4\u5f97\u4ed6\u4eec\u7684\u8840\u6db2\u90fd\u51e0\u4e4e\u51dd\u7ed3\u8d77\u6765\uff0c\u4eba\u4eba\u8db3\u624b\u50f5\u786c\uff0c\u9ebb \u6728\u4e0d\u7075\uff0c\u4e1d\u6beb\u6ca1\u6709\u62b5\u6297\u4e4b\u529b\u3002\u5e7d\u840d\u6307\u6325\u90a3\u51e0\u4e2a\u4f8d\u5973\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u5c31\u628a\u8fd9\u56db\u4e2a\u5c3c\u6cca\u5c14\u4f7f\u8005\u5c3d\u90fd \u7f1a\u4e86\u3002\u5e7d\u840d\u5f53\u5e74\u66fe\u88ab\u4ed6\u4eec\u6240\u64d2\uff0c\u8fd9\u65f6\u65b9\u59cb\u51fa\u4e86\u53e3\u6c14\u3002\u6b63\u662f\uff1a \u3000\u3000\u4e0d\u9053\u98ce\u4e91\u591a\u53d8\u6362\uff0c\u591a\u884c\u4e0d\u4e49\u5fc5\u906d\u6b83\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u6cd5\u7f51\u590d\u4f4d\u5978\u8c0b\u7834\u3000\u5c0f\u4fa0\u906d\u6b83\u9b54\u5973\u6765 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u6cd5\u7f51\u590d\u4f4d\u5978\u8c0b\u7834\u3000\u5c0f\u4fa0\u906d\u6b83\u9b54\u5973\u6765 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u5ffd\u5730\u5c06\u8888\u88df\u4e0a\u5c55\uff0c\u62e6\u4f4f\u4e86\u90a3\u56db\u4e2a\u4f8d\u5973\uff0c\u559d\u9053\uff1a\u201c\u8c01\u6562\u5728\u6b64\u6355\u4eba\uff1f\u5feb\u5feb\u653e \u4e0b\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6211\u81ea\u62d8\u6355\u6211\u672c\u56fd\u4e4b\u4eba\uff0c\u4e0e\u4f60\u4f55\u5e72\uff1f\u4ed6\u4eec\u56db\u4eba\u4f5c\u5978\u72af\u79d1\uff0c\u56fd\u6cd5\u4f5b\u6cd5\uff0c \u4ff1\u4e0d\u80fd\u5bb9\uff0c\u533f\u8eab\u4f5b\u5bfa\uff0c\u6c61\u53ca\u4f5b\u95e8\uff0c\u7f6a\u66f4\u4e0d\u5c0f\u3002\u6211\u66fe\u53d7\u5c01\u4f5b\u6559\u62a4\u6cd5\uff0c\u56e0\u6b64\u5373\u7b97\u4e0d\u662f\u4ed6\u4eec\u7684 \u516c\u4e3b\uff0c\u4e5f\u53ef\u4ee5\u62ff\u5f97\u4ed6\u4eec\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u6211\u8fd8\u672a\u66fe\u5475\u4f60\u5305\u5e87\u4e4b\u7f6a\u5462\uff0c\u4f60\u6562\u51fa\u5934\u963b\u62e6\uff1f\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u5c3c\u6cca\u5c14\u56fd\u7684\u516c\u4e3b\uff0c\u4f46\u4f60\u662f\u5426\u5df2\u7ecf\u77e5\u9053\u6211\u4e43\u662f\u4f60\u56fd\u7684\u56fd \u5e08\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4ed6\u4eec\u8bf4\u8981\u8fce\u63a5\u56fd\u5e08\uff0c\u8fd9\u56fd\u5e08\u5c31\u662f\u4f60\u4e48\uff1f\u5931\u656c\uff0c\u5931\u656c\uff01\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u6d0b\u6d0b\u5f97\u610f\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u73b0\u5728\u77e5\u9053\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u4ed6\u4eec\u662f\u56fd\u738b\u6d3e\u6765\u7684\u4f7f\u8005\uff0c\u6240 \u4f5c\u6240\u4e3a\uff0c\u90fd\u662f\u5949\u4e86\u56fd\u738b\u4e4b\u547d\u7684\u3002\u4f60\u7eb5\u7136\u662f\u516c\u4e3b\uff1a\u516c\u4e3b\u603b\u4e0d\u80fd\u5927\u8fc7\u56fd\u738b\u5427\uff1f\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u51b7\u51b7\u4e00\u7b11\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u8fd8\u6709\u5462\uff0c\u56fd\u738b\u7684\u547d\u4ee4\u8981\u4f60\u4e5f\u4ece\u901f\u56de\u56fd\uff0c\u6211\u6070\u597d \u4e5f\u6b63\u8981\u52a8\u8eab\uff0c\u6211\u770b\u4f60\u8fd8\u662f\u91ca\u653e\u4e86\u4ed6\u4eec\uff0c\u548c\u6211\u4e00\u9053\u8d70\u5427\u3002\u4f60\u82e5\u8fd8\u6709\u4ec0\u4e48\u7406\u8bba\uff0c\u6211\u548c\u4f60\u89c1\u4e86 \u56fd\u738b\u518d\u8bf4\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u8fd9\u516c\u8bf4\uff0c\u4f60\u53cd\u800c\u8981\u62ff\u6211\u4e86\uff1f\u201d\u624e\u96c0\u660e\u4f26\u738b\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u6211\u662f\u8bf7\u4f60\u3002\u201d \u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u8bf7\u4e0d\u52a8\u5462\uff1f\u201d\u5b54\u96c0\u660e\u4f26\u738b\u51b7\u7b11\u9053\uff1a\u201c\u516c\u4e3b\u82e5\u662f\u4e0d\u9075\u56fd\u4e3b\u4e4b\u547d\uff0c\u53c8\u4e0d\u80af\u8d4f \u9762\u7ed9\u6211\u7684\u8bdd\uff0c\u90a3\u5c31\u6050\u6015\u53ea\u597d\u5f97\u7f6a\u516c\u4e3b\u4e86\u3002\u201d \u3000\u3000\u4f60\u9053\u5b54\u96c0\u660e\u4f26\u738b\u4f55\u4ee5\u5982\u6b64\u5927\u80c6\uff1f\u539f\u6765\u4ed6\u5728\u5c3c\u6cca\u5c14\u591a\u5e74\uff0c\u65e9\u5df2\u77e5\u9053\u5c3c\u6cca\u5c14\u738b\u7684\u610f\u56fe\u3002 \u5c3c\u6cca\u5c14\u738b\u5e76\u975e\u6000\u5ff5\u4eb2\u4eba\u624d\u8981\u63a5\u51b0\u5ddd\u5929\u5973\u56de\u56fd\u7684\uff0c\u4ed6\u5b9e\u662f\u60f3\u5e76\u541e\u897f\u85cf\uff0c\u5374\u6015\u51b0\u5ddd\u5929\u5973\u4ece\u4e2d \u963b\u6897\uff0c\u751a\u81f3\u52a8\u6447\u4ed6\u7684\u7389\u4f4d\uff0c\u6545\u6b64\u8981\u8bbe\u6cd5\u8bf1\u5979\u56de\u56fd\uff0c\u53bb\u6389\u7978\u6839\u3002\u53e6\u5916\u4ed6\u4e5f\u60f3\u53d6\u5f97\u51b0\u5ddd\u5929\u5973 \u90a3\u628a\u51b0\u9b44\u5bd2\u5149\u5251\u4f5c\u4e3a\u9547\u56fd\u4e4b\u5b9d\u3002 \u3000\u3000\u9664\u4e86\u8fd9\u4e24\u4e2a\u539f\u56e0\uff0c\u4ed6\u8fd8\u5782\u6d8e\u51b0\u5ddd\u5929\u5973\u7684\u7f8e\u8272\uff0c\u4ed6\u4ece\u963f\u62c9\u4f2f\u8bf7\u6765\u4e86\u9ad8\u660e\u7684\u836f\u5e08\uff0c\u5c06\u91c7 \u81ea\u559c\u9a6c\u62c9\u96c5\u5c71\u7684\u9b54\u9b3c\u82b1\uff0c\u79d8\u5236\u6210\u4e00\u79cd\u8ff7\u9999\uff0c\u5c31\u662f\u51c6\u5907\u7528\u6765\u5bf9\u4ed8\u51b0\u5ddd\u5929\u5973\u7684\u3002\u4e0d\u8fc7\u4ed6\u4e5f\u6df1 \u77e5\u51b0\u51b0\u5ddd\u5929\u5973\u7684\u5389\u5bb3\uff0c\u8fd9\u90aa\u5ff5\u672a\u5fc5\u53ef\u4ee5\u4ece\u5fc3\u6240\u6b32\uff0c\u56e0\u6b64\u4ed6\u4e5f\u62b1\u5b9a\u4e86\u4e3b\u610f\uff0c\u5018\u82e5\u51b0\u5ddd\u5929\u5973 \u6709\u5a01\u80c1\u5230\u4ed6\u738b\u4f4d\u7684\u5371\u9669\u65f6\uff0c\u4ed6\u662f\u5b81\u613f\u653e\u5f03\u7f8e\u8272\uff0c\u800c\u628a\u51b0\u5ddd\u5929\u5973\u6740\u6389\u7684\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u6b63\u662f\u56e0\u4e3a\u5df2\u6df1\u6089\u5c3c\u6cca\u5c14\u738b\u7684\u4f01\u56fe\uff0c\u6240\u4ee5\u4e0d\u6015\u4e0e\u51b0\u5ddd\u5929\u5973\u4e3a\u654c\u3002 \u3000\u3000\u8fd9\u65f6\u6bcf\u4e2a\u4eba\u7684\u76ee\u5149\u90fd\u96c6\u4e2d\u5728\u5b54\u96c0\u660e\u4f26\u738b\u4e0e\u51b0\u5ddd\u5929\u5973\u7684\u8eab\u4e0a\uff0c\u6cd5\u738b\u66f4\u662f\u5fe7\u5fc3\u5982\u711a\uff0c\u6b63 \u5f85\u4e0d\u987e\u4e00\u5207\uff0c\u51fa\u53bb\u963b\u6b62\u5e08\u5f1f\uff0c\u5374\u89c1\u51b0\u5ddd\u5929\u5973\u6446\u4e86\u6446\u624b\uff0c\u8138\u4e0a\u6beb\u65e0\u6120\u6012\u4e4b\u8272\uff0c\u53ea\u662f\u6de1\u6de1\u8bf4 \u9053\uff1a\u201c\u4f60\u79bb\u5f00\u52a0\u5fb7\u6ee1\u90fd\uff0c\u5c06\u8fd1\u534a\u5e74\u4e86\u5427\uff1f\u6709\u4e00\u4ef6\u4e8b\u60c5\uff0c\u4f60\u5927\u7ea6\u672a\u66fe\u77e5\u9053\uff0c\u6211\u56fd\u65e7\u738b\u5df2\u88ab \u767e\u59d3\u63a8\u7ffb\uff0c\u65b0\u738b\u4e5f\u5df2\u7ecf\u5373\u4f4d\u4e86\u3002\u4f60\u7684\u56fd\u5e08\u4e5f\u6ca1\u6709\u5566\uff01\u6211\u6b63\u662f\u53d7\u65b0\u738b\u4e4b\u6258\uff0c\u8981\u628a\u8fd9\u56db\u4e2a\u4eba \u62bc\u89e3\u56de\u53bb\u7684\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u8fd9\u51e0\u53e5\u6d3b\u8f7b\u63cf\u6de1\u5199\u7684\u9053\u6765\uff0c\u5374\u4f3c\u9752\u5929\u6253\u4e86\u4e2a\u9739\u96f3\uff0c\u5e73\u5730\u54cd\u8d77\u4e86\u7126\u96f7\uff0c\u4e0d\u4f46 \u9707\u5f97\u5b54\u96c0\u660e\u4f26\u738b\u5931\u9b42\u843d\u9b44\uff0c\u800c\u4e14\u4e5f\u9707\u7834\u4e86\u62e5\u62a4\u4ed6\u7684\u90a3\u4e00\u5927\u7fa4\u5587\u561b\u7684\u7f8e\u68a6\uff01 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u5446\u4e86\u4e00\u5446\uff0c\u5ffd\u5730\u559d\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\uff01\u201d\u8eab\u5f62\u75be\u8d77\uff0c\u5f20\u5f00\u84b2\u6247\u822c\u7684\u5927\u624b\uff0c \u731b\u5730\u5c31\u5411\u51b0\u5ddd\u5929\u5973\u6293\u53bb\uff01\u5176\u5b9e\u4e0e\u5176\u8bf4\u662f\u4ed6\u4e0d\u76f8\u4fe1\uff0c\u65e0\u5b81\u8bf4\u4ed6\u662f\u53d1\u4e86\u72c2\u3002\u4ed6\u662f\u6000\u7740\u6781\u5927\u7684 \u5e0c\u671b\uff0c\u7a81\u7136\u5ba3\u544a\u5e7b\u706d\uff0c\u5374\u786c\u4e0d\u80af\u627f\u8ba4\u4e8b\u5b9e\u3002\u8fd8\u60f3\u7d27\u7d27\u6293\u7740\u90a3\u7834\u788e\u7684\u5e0c\u671b\uff01 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u8f7b\u529f\u5353\u7edd\uff0c\u54ea\u80fd\u8ba9\u4ed6\u6293\u7740\u3002\u5979\u4e00\u98d8\u4e00\u95ea\uff0c\u65e9\u5df2\u9000\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u7389\u624b\u4e00\u626c\uff0c \u63a5\u8fde\u98de\u51fa\u4e86\u4e03\u679a\u51b0\u9b44\u795e\u5f39\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u7684\u6b66\u529f\u7aef\u7684\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u53ea\u89c1\u4ed6\u53cc\u638c\u62cd\u51fa\uff0c\u70ed\u98ce\u547c\u547c\uff1a\u51b0\u9b44\u795e\u5f39\u672a\u66fe\u89e6 \u7740\u4ed6\u7684\u8eab\u4f53\uff0c\u4fbf\u90fd\u6eb6\u5316\uff0c\u5316\u4f5c\u4e86\u4e00\u56e2\u5bd2\u96fe\uff0c\u5927\u6bbf\u4e0a\u4eba\u4eba\u90fd\u89c9\u5f97\u51b7\u6c14\u6c81\u808c\uff01 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u5de5\u673a\u4f36\u4f36\u5730\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff0c\u5ffd\u5730\u5927\u543c\u4e00\u58f0\uff0c\u4ece\u5bd2\u96fe\u4e4b\u4e2d\u51b2\u51fa\uff0c\u5510\u7ecf\u5929\u51b7\u7b11 \u9053\uff1a\u201c\u4f60\u4e0d\u76f8\u4fe1\uff1f\u5c31\u8ba9\u4f60\u5230\u5c3c\u6cca\u5c14\u81ea\u5df1\u53bb\u770b\u5427\uff01\u201d\u8eab\u5f62\u4e00\u6643\uff0c\u62e6\u4f4f\u4e86\u5b54\u96c0\u660e\u4f26\u738b\u7684\u53bb\u8def\uff0c \u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\uff0c\u53cc\u638c\u9f50\u53d1\uff0c\u5de6\u638c\u662f\u5927\u64d2\u62ff\u624b\u6cd5\uff0c\u53f3\u638c\u5219\u53d1\u51fa\u4e86\u987b\u5f25\u638c\u529b\u3002 \u3000\u3000\u5510\u7ecf\u5929\u7684\u5185\u529f\u65e9\u5df2\u5230\u4e86\u4e0a\u4e58\u5883\u754c\uff0c\u987b\u5f25\u638c\u529b\u51fa\u795e\u5165\u5316\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u4e00\u638c\u62cd\u51fa\uff0c\u53ea\u89c9 \u5bf9\u65b9\u7684\u638c\u529b\u4f3c\u6709\u8fd8\u65e0\uff0c\u81ea\u5df1\u8fd9\u4e00\u638c\uff0c\u7adf\u4f3c\u6253\u5728\u68c9\u82b1\u5806\u4e2d\uff0c\u7eb5\u6709\u5343\u4e07\u65a4\u6c14\u529b\uff0c\u5486\u4f7f\u4e0d\u51fa\u52b2 \u6765\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u5230\u5e95\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u867d\u5728\u75af\u72c2\u4f0f\u6001\u4e4b\u4e2d\uff0c\u5374\u4e5f\u77e5\u9053\u4e0d\u5999\uff0c\u970d\u7136\u4e00\u60ca\uff0c \u7acb\u5373\u9ed8\u8fd0\u795e\u529f\uff0c\u628a\u638c\u529b\u786c\u64a4\u56de\u6765\uff0c\u540c\u65f6\u811a\u4e0b\u201c\u5012\u8e29\u4e03\u661f\u6b65\u201d\uff0c\u5fd9\u4e0d\u8fed\u7684\u540e\u9000\u3002 \u3000\u3000\u5510\u7ecf\u5929\u600e\u80af\u8ba9\u4ed6\u5168\u8eab\u800c\u9000\uff0c\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u4ed6\u7684\u987b\u5f25\u638c\u529b\u5df2\u4e00\u53d1\u65e0\u9057\uff0c\u5de6\u638c\u4ee5\u5927\u64d2 \u62ff\u624b\u6cd5\uff0c\u4e5f\u6293\u7740\u4e86\u5b54\u96c0\u660e\u4f26\u738b\u7684\u7435\u7436\u9aa8\uff01 \u3000\u3000\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u7684\u8eab\u8eaf\u98de\u4e86\u8d77\u6765\uff0c\u7bad\u4e00\u822c\u5730\u5411\u524d\u649e\u53bb\uff0c\u9762\u524d\u662f\u4e00 \u6839\u77f3\u67f1\uff0c\u773c\u770b\u4ed6\u5c31\u8981\u649e\u5f97\u5934\u5f7c\u8840\u6d41\uff0c\u53ea\u542c\u5f97\u53c8\u662f\u201c\u84ec\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u4ed6\u4e00\u638c\u6309\u5728\u67f1\u4e0a\uff0c \u5b9a\u4e0b\u4e86\u8eab\u5f62\uff0c\u77f3\u5c51\u7eb7\u98de\uff0c\u77f3\u67f1\u4e0a\u767b\u65f6\u73b0\u51fa\u4e00\u56e2\u638c\u5370\u3002 \u3000\u3000\u5510\u7ecf\u5929\u89c1\u5b54\u96c0\u660e\u4f26\u738b\u5982\u6b64\u51f6\u731b\uff0c\u81ea\u5df1\u5df2\u7ecf\u6293\u7740\u4e86\u4ed6\u7684\u7435\u7436\u9aa8\uff0c\u4ecd\u7136\u7ed9\u4ed6\u8131\u8eab\u800c\u53bb\uff0c \u800c\u4e14\u4ed6\u5728\u63a5\u4e86\u987b\u5f25\u638c\u529b\u4e4b\u540e\uff0c\u4ecd\u7136\u80fd\u591f\u4f7f\u51fa\u8fd9\u7b49\u4e0a\u4e58\u529f\u592b\uff0c\u4e5f\u597d\u751f\u9a87\u5f02\uff01\u6b63\u60f3\u518d\u53bb\u6293\u4ed6\uff0c \u6cd5\u738b\u5df2\u5728\u5408\u4ec0\u8bf4\u9053\uff1a\u201c\u5510\u5927\u4fa0\uff0c\u8bf7\u770b\u5728\u8001\u8872\u4efd\u4e0a\uff0c\u7531\u4ed6\u53bb\u5427\uff01\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u521d\u65f6\u8fd8\u81ea\u6043\u6b66\u529f\uff0c\u8981\u60f3\u6d3b\u64d2\u51b0\u5ddd\u5929\u5973\uff0c\u5982\u4eca\u63a5\u8fde\u9886\u6559\u4e86\u51b0\u9b42\u795e\u5f39\u548c\u987b\u5f25 \u638c\u529b\u7684\u5389\u5bb3\uff0c\u60c5\u77e5\u53ea\u662f\u5510\u7ecf\u5929\u4e00\u4eba\uff0c\u81ea\u5df1\u5c31\u672a\u5fc5\u6253\u5f97\u8fc7\u4ed6\uff0c\u4f55\u51b5\u8fd8\u6709\u6c34\u5ddd\u5929\u5973\u4e0e\u6c5f\u6d77\u5929 \u5728\u65c1\uff0c\u4efb\u4ed6\u5982\u4f55\u75af\u72c2\uff0c\u8fd9\u65f6\u4e5f\u4e0d\u6562\u901e\u5f3a\u4f5c\u6076\u3002\u5c31\u5728\u6cd5\u738b\u7684\u201c\u53bb\u5427\u201d\u58f0\u4e2d\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u6012 \u6c14\u51b2\u51b2\u5730\u8d70\u4e86\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u4e00\u8d70\uff0c\u539f\u5148\u62e5\u62a4\u4ed6\u7684\u90a3\u4e8c\u73ed\u4eba\uff0c\u4e2a\u4e2a\u5fc3\u4e2d\u6127\u6094\uff0c\u4e00\u9f50\u4fef\u4f0f\u5730\u4e0a\uff0c\u5411\u6cd5\u738b \u8bf7\u7f6a\uff0c\u5e76\u6c42\u6cd5\u738b\u590d\u4f4d\uff0c\u90a3\u4e09\u4e2a\u6700\u521d\u8ddf\u4ece\u5b54\u96c0\u660e\u4f26\u738b\u80f8\u62a4\u6cd5\u5f1f\u5b50\uff0c\u66f4\u662f\u8bda\u60f6\u8bda\u6050\uff0c\u58f0\u6cea\u4ff1 \u4e0b\u5730\u5411\u6cd5\u738b\u7980\u544a\u9053\uff1a\u201c\u5f1f\u5b50\u8f88\u9053\u529b\u672a\u575a\uff0c\u4e3a\u9b54\u6240\u8bf1\uff0c\u4e00\u5ff5\u4e4b\u5dee\uff0c\u51e0\u4e4e\u94f8\u6210\u5927\u9519\uff0c\u6bc1\u574f\u672c \u6559\u57fa\u4e1a\u3002\u7f6a\u5b7d\u6df1\u91cd\uff0c\u613f\u53d7\u5e08\u5c0a\u4efb\u4f55\u5904\u5206\u3002\u53ea\u6c42\u5e08\u5c0a\u91cd\u6267\u6559\u4e3b\u6cd5\u6756\uff0c\u514d\u5f97\u543e\u7b49\u518d\u5165\u6b67\u9014\u3002\u201d \u3000\u3000\u6cd5\u738b\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f5b\u7956\u6614\u5e74\u629b\u5f03\u738b\u5b50\u4e4b\u5c0a\uff0c\u8dcc\u5750\u83e9\u63d0\u6811\u4e0b\uff0c\u4e03\u4e03\u56db\u5341\u513f\u5929\uff0c \u65b9\u59cb\u5999\u609f\u4f5b\u6cd5\uff0c\u5f97\u8bc1\u5927\u9053\u3002\u8fd9\u5bcc\u8d35\u5c0a\u8363\u7684\u6b32\u5ff5\uff0c\u672c\u6765\u5c31\u4e0d\u662f\u6bcf\u4e00\u4e2a\u4eba\u90fd\u53ef\u4ee5\u5272\u820d\u5f97\u4e86\u7684\u3002 \u4eba\u8c01\u65e0\u8fc7\uff0c\u8fc7\u800c\u80fd\u6539\uff0c\u5584\u83ab\u5927\u7109\uff0c\u4f60\u4eec\u90fd\u8d77\u6765\u5427\uff01\u201d\u5f53\u4e0b\u96e8\u8fc7\u5929\u6674\uff0c\u7686\u5927\u6b22\u559c\uff0c\u6cd5\u738b\u63a5 \u4e86\u6cd5\u6756\uff0c\u91cd\u767b\u6559\u4e3b\u4e4b\u4f4d\u3002 \u3000\u3000\u662f\u665a\u6cd5\u738b\u8bbe\u4e0b\u7d20\u7b75\uff0c\u6b3e\u5f85\u5bbe\u5ba2\u3002\u5510\u7ecf\u5929\u592b\u5987\u3001\u9648\u5929\u5b87\u592b\u5987\u3001\u6c5f\u5357\u7236\u5b50\u4e0e\u59ec\u6653\u98ce\u7b49\u4eba\uff0c \u90fd\u6210\u4e86\u6cd5\u738b\u7684\u8d35\u5bbe\u3002\u9648\u5929\u5b87\u7684\u59bb\u5b50\u5e7d\u840d\u8fd9\u65f6\u4e5f\u624d\u6709\u4f59\u6687\uff0c\u8bf4\u51fa\u5728\u5c3c\u6cca\u5c14\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u539f\u6765\u5e7d\u840d\u88ab\u64d2\u4e4b\u540e\uff0c\u5c3c\u6cca\u5c14\u738b\u5012\u4e5f\u4e0d\u6562\u600e\u6837\u5c06\u5979\u96be\u4e3a\uff0c\u53ea\u662f\u5c06\u5979\u56da\u5165\u51b7\u5bab\uff0c\u540c\u65f6\u5c06 \u5979\u8eab\u4e0a\u6240\u85cf\u7684\u4e00\u74f6\u51b0\u9b44\u795e\u5f39\u641c\u53bb\uff0c\u4ea4\u7ed9\u4e00\u4f4d\u4ece\u5370\u5ea6\u8058\u6765\u7684\u9ad8\u624b\uff0c\u8ba9\u4ed6\u7814\u7a76\u8fd9\u79cd\u51b0\u5f39\u7684\u6027 \u80fd\uff0c\u597d\u51c6\u5907\u5c06\u6765\u7528\u4ee5\u5bf9\u4ed8\u51b0\u5ddd\u5929\u5973\u3002 \u3000\u3000\u5e7d\u840d\u4e2d\u4e86\u9b54\u9b3c\u82b1\u7684\u6bd2\uff0c\u6b66\u529f\u867d\u7136\u8fd8\u5728\uff0c\u4f46\u5df2\u5931\u6389\u52b2\u529b\uff0c\u4f7f\u4e0d\u51fa\u6765\uff0c\u8fde\u5403\u996d\u7a7f\u8863\uff0c\u90fd \u8981\u522b\u4eba\u5e2e\u5979\u52a8\u624b\u3002\u5c3c\u6cca\u5c14\u738b\u6d3e\u4e86\u56db\u4e2a\u5bab\u5973\u670d\u4f8d\u5979\uff0c\u540c\u65f6\u5411\u5979\u63a2\u95f4\u51b0\u5ddd\u5929\u5973\u5728\u4e2d\u56fd\u7684\u79cd\u79cd \u60c5\u5f62\uff0c\u5e7d\u840d\u53ea\u662f\u95ed\u53e3\u4e0d\u8bf4\u3002\u65e5\u5b50\u4e45\u4e86\uff0c\u90a3\u56db\u4e2a\u5bab\u5973\u4e5f\u5c31\u4e0d\u8ffd\u95ee\u5979\u4e86\u3002\u5176\u4e2d\u6709\u4e00\u4e2a\u5bab\u5973\u8fd8 \u548c\u5979\u4ea4\u4e0a\u4e86\u670b\u53cb\u3002 \u3000\u3000\u8fd9\u4e2a\u5bab\u5973\u539f\u6765\u662f\u6709\u4e00\u4e2a\u60c5\u4eba\u7684\uff0c\u88ab\u9009\u5165\u5bab\u4e4b\u540e\u3002\u5bf9\u60c5\u4eba\u4ecd\u662f\u975e\u5e38\u6000\u5ff5\uff0c\u5e7d\u840d\u5f97\u77e5\u5979 \u7684\u5fc3\u4e8b\uff0c\u4fbf\u6559\u5979\u4e00\u4e2a\u6cd5\u5b50\uff0c\u5176\u65f6\u5c3c\u6cca\u5c14\u738b\u6b63\u5728\u8003\u9009\u6b66\u58eb\uff0c\u5e7d\u840d\u7684\u6cd5\u5b50\u4fbf\u662f\u8981\u90a3\u9636\u5bab\u5973\u7684 \u60c5\u4eba\u5e94\u8003\uff0c\u5018\u82e5\u5f97\u5230\u56fd\u738b\u7684\u4fe1\u4efb\uff0c\u6d3e\u5230\u5bab\u4e2d\u6765\u5f53\u6043\u536b\uff0c\u5c31\u53ef\u4ee5\u6709\u673a\u4f1a\u548c\u5979\u89c1\u9762\u4e86\u3002\u5374\u4e0d \u6599\u90a3\u5bab\u5973\u7684\u60c5\u4eba\u53ea\u662f\u4e00\u4e2a\u519c\u5bb6\u5b50\u5f1f\uff0c\u5b8c\u5168\u4e0d\u61c2\u6b66\u827a\u7684\u3002 \u3000\u3000\u9648\u5929\u5b87\u542c\u5230\u8fd9\u91cc\uff0c\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7684\u7528\u5fc3\uff0c\u4f60\u662f\u5e0c\u671b\u90a3\u4e2a\u5bab\u5973\u7684\u60c5\u4eba\u8fdb\u5bab\uff0c\u4f60\u4e5f \u53ef\u4ee5\u6709\u673a\u4f1a\u548c\u5916\u9762\u6697\u901a\u6d88\u606f\u4e86\u3002\u4f46\u4ed6\u4e0d\u4f1a\u6b66\u827a\uff0c\u4f60\u8fd9\u8ba1\u5212\u5c82\u4e0d\u662f\u843d\u7a7a\u4e86\uff1f\u201d \u3000\u3000\u5e7d\u840d\u9053\uff1a\u201c\u6211\u60f3\u4e86\u4e00\u4e2a\u665a\u4e0a\uff0c\u7ec8\u4e8e\u8fd8\u662f\u7ed9\u6211\u60f3\u51fa\u4e86\u4e00\u4e2a\u6cd5\u5b50\uff0c\u6211\u5c06\u5b66\u6b66\u7684\u5165\u95e8\u529f\u592b \u5199\u5728\u7eb8\u4e0a\uff0c\u8ba9\u90a3\u5bab\u5973\u8bbe\u6cd5\u9001\u7ed9\u5979\u7684\u60c5\u4eba\u3002\u867d\u8bf4\u662f\u5165\u95e8\u7684\u7c97\u6d45\u529f\u592b\uff0c\u4f46\u5b66\u4f1a\u4e4b\u540e\uff0c\u4e5f\u5df2\u80dc \u8fc7\u5c3c\u6cca\u5c14\u738b\u7684\u4e00\u822c\u6b66\u58eb\u4e86\u3002\u540e\u6765\u90a3\u5bab\u5973\u7684\u60c5\u4eba\u679c\u7136\u8003\u53d6\uff0c\u5f53\u4e0a\u4e86\u5c3c\u6cca\u5c14\u738b\u7684\u536b\u58eb\u3002\u5176\u65f6 \u6211\u88ab\u56da\u5728\u51b7\u5bab\uff0c\u4ea6\u5df2\u6709\u4e86\u4e09\u5e74\u4e4b\u4e45\uff0c\u5c3c\u6cca\u5c14\u738b\u4e5f\u6e10\u6e10\u4e0d\u6ce8\u610f\u6211\u4e86\uff0c\u90a3\u5bab\u5973\u8fd8\u66fe\u7ecf\u5e26\u8fc7\u5979 \u7684\u60c5\u4eba\u6765\u5077\u5077\u548c\u6211\u89c1\u9762\uff0c\u5411\u6211\u9053\u8c22\u3002\u201d \u3000\u3000\u5e7d\u840d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u5bab\u5973\u7684\u60c5\u4eba\u540d\u53eb\u5df4\u52c3\uff0c\u867d\u662f\u519c\u5bb6\u5b50\u5f1f\uff0c\u5374\u9887\u660e\u4e8b\u7406\uff0c\u800c\u4e14\u662f \u4e00\u4e2a\u7231\u56fd\u5fc3\u91cd\u7684\u70ed\u8840\u5c11\u5e74\u3002\u201d \u3000\u3000\u201c\u6211\u548c\u5df4\u52c3\u4f1a\u9762\u4e4b\u540e\uff0c\u624d\u77e5\u9053\u5c3c\u6cca\u5c14\u738b\u5f88\u4e0d\u5f97\u6c11\u5fc3\uff0c\u4ed6\u4e3a\u4e86\u56fe\u8c0b\u541e\u5e76\u897f\u85cf\uff0c\u517b\u4e86\u8bb8 \u591a\u519b\u961f\uff0c\u8fde\u5e74\u6765\u6a2a\u5f81\u66b4\u655b\uff0c\u65e9\u5df2\u5f04\u5f97\u6c11\u6028\u6cb8\u817e\u3002\u8001\u767e\u59d3\u4eec\u90fd\u5f88\u601d\u5ff5\u516c\u4e3b\uff0c\u76fc\u671b\u516c\u4e3b\u6709\u65e5 \u56de\u56fd\uff0c\u505a\u4ed6\u4eec\u7684\u5973\u738b\u3002\u6211\u542c\u4e86\u5fc3\u91cc\u6697\u7b11\uff0c\u867d\u7136\u60f3\u6cd5\u4e0d\u540c\uff0c\u4f46\u56fd\u738b\u548c\u767e\u59d3\u5374\u90fd\u662f\u540c\u6837\u7684\u76fc \u671b\u516c\u4e3b\u4f60\u56de\u56fd\u5462\u3002 \u3000\u3000\u201c\u5927\u7ea6\u8fc7\u4e86\u534a\u5e74\uff0c\u5df2\u52c3\u7b2c\u4e8c\u6b21\u548c\u6211\u4f1a\u9762\uff0c\u5e26\u6765\u4e86\u4e00\u4e2a\u6d88\u606f\u3002\u8bf4\u662f\u52a0\u6e90\u8499\u73e0\u4e5f\u5df1\u88ab\u56fd \u738b\u64d2\u6765\uff0c\u5173\u5728\u5bab\u4e2d\uff0c\u8fd9\u6d88\u606f\u5916\u9762\u4ea6\u5df2\u6709\u4eba\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u95ee\u9053\uff1a\u201c\u8c01\u662f\u52a0\u6e90\u8499\u73e0\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a\u201c\u5e7d\u840d\uff0c\u6211\u548c\u4f60\u65e9\u5df2\u662f\u59d0\u59b9\u76f8\u79f0\uff0c\u4f60\u600e\u4e48\u8fd8\u662f\u7ed9\u6211\u6bcd\u5b50\u52a0\u4e0a\u5934\u8854\uff1f \u516c\u4e3b\u5566\uff0c\u8499\u73e0\u5566\uff0c\u53eb\u4e2a\u4e0d\u505c\uff0c\u542c\u8d77\u6765\u602a\u4ee4\u4eba\u4e0d\u597d\u610f\u601d\u3002\u201d\u968f\u7740\u89e3\u91ca\u9053\uff1a\u201c\u52a0\u6e90\u662f\u6211\u513f\u5b50 \u7684\u540d\u5b57\uff0c\u53d6\u5176\u6e90\u6d41\u51fa\u4e8e\u52a0\u5fb7\u6ee1\u90fd\u4e4b\u610f\u3002\u2018\u8499\u73e0\u2019\u662f\u5c3c\u6cca\u5c14\u5bf9\u4e8e\u516c\u4e3b\u4e4b\u5b50\u7684\u5c0a\u79f0\u3002\u201d \u3000\u3000\u5e7d\u840d\u7b11\u9053\uff1a\u201c\u51b0\u5a25\u59d0\u59d0\u3002\u4f60\u522b\u602a\u6211\u3002\u5c3c\u6cca\u5c14\u7684\u767e\u59d3\u8c08\u8d77\u4f60\u4eec\u6bcd\u5b50\u7684\u65f6\u5019\uff0c\u90fd\u662f\u8fd9\u6837 \u79f0\u547c\u7684\u3002\u6211\u8f6c\u8fbe\u4ed6\u4eec\u7684\u8bdd\uff0c\u603b\u4e0d\u80fd\u968f\u4fbf\u7ed9\u4ed6\u4eec\u66f4\u6539\u5440\u3002\u201d \u3000\u3000\u6c5f\u5357\u6b63\u81ea\u542c\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u7b11\u9053\uff1a\u201c\u522b\u6253\u65ad\u8bdd\u67c4\uff0c\u8fd9\u4e9b\u5c0f\u8282\uff0c\u4e14\u4e0d\u5fc5\u7ba1\u5b83\uff0c\u4f60\u8bf4\u4e0b\u53bb \u5427\u3002\u201d \u3000\u3000\u5e7d\u840d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8001\u767e\u59d3\u77e5\u9053\u8fd9\u4e2a\u6d88\u606f\u7684\uff0c\u90fd\u5f88\u4e3a\u52a0\u6e90\u3001\u52a0\u6e90\u8d24\u4f84\u62c5\u5fc3\uff0c\u4ed6\u4eec\u60f3\u6551 \u52a0\u6e90\u8d24\u4f84\u51fa\u6765\uff0c\u800c\u4e14\u60f3\u62e5\u7acb\u4ed6\u4e3a\u738b\u3002 \u3000\u3000\u201c\u5df4\u52c3\u53c8\u544a\u8bc9\u6211\u8bf4\u3002\u52a0\u6e90\u8d24\u4f84\u7684\u906d\u9047\u4e0e\u6211\u76f8\u540c\uff0c\u4e5f\u662f\u4e2d\u4e86\u9b54\u9b3c\u82b1\u4e4b\u6bd2\uff0c\u5168\u8eab\u52b2\u529b\u6d88 \u5931\uff0c\u4f7f\u4e0d\u51fa\u6b66\u529f\u3002 \u3000\u3000\u201c\u8001\u767e\u59d3\u867d\u6709\u63a8\u7ffb\u66b4\u541b\u4e4b\u5fc3\uff0c\u4f46\u4e00\u6765\u65e0\u4eba\u5e26\u5934\uff0c\u4e8c\u6765\u56fd\u738b\u7684\u6b66\u58eb\u5982\u4e91\uff0c\u53c8\u4ece\u5916\u56fd\u8058 \u8bf7\u4e86\u8bb8\u591a\u80fd\u4eba\u76f8\u52a9\uff0c\u8001\u767e\u59d3\u8981\u60f3\u4e3e\u4e8b\uff0c\u8c08\u4f55\u5bb9\u6613\uff1f \u3000\u3000\u201c\u5df4\u52c3\u8fd9\u6b21\u5077\u5077\u548c\u6211\u4f1a\u9762\uff0c\u539f\u6765\u5c31\u662f\u8981\u548c\u6211\u5546\u91cf\u600e\u6837\u5e2e\u52a9\u767e\u59d3\u8d77\u4e8b\u7684\u3002\u6211\u60f3\u4e0d\u51fa\u4ec0 \u4e48\u597d\u6cd5\u5b50\uff0c\u4f46\u4ed6\u5374\u6709\u4e86\u4e00\u5957\u5468\u5bc6\u4ed6\u8ba1\u5212\uff0c\u540e\u6765\u6211\u4eec\u5c31\u6309\u7167\u4ed6\u8fd9\u5957\u8ba1\u5212\u884c\u4e8b\uff0c\u5509\uff0c\u8fd9\u8ba1\u5212 \u597d\u662f\u597d\uff0c\u4f46\u5df4\u52c3\u7684\u727a\u7272\u5374\u592a\u5927\u4e86\u3002\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u4f60\u53c8\u5356\u5173\u5b50\u4e86\uff0c\u7a76\u7adf\u662f\u4ec0\u4e48\u8ba1\u5212\uff1f\u201d \u3000\u3000\u5e7d\u840d\u9053\uff1a\u201c\u5df4\u52c3\u7684\u60c5\u4eba\uff0c\u4e5f\u5c31\u662f\u548c\u6211\u5f88\u8981\u597d\u7684\u90a3\u4e2a\u5bab\u5973\uff0c\u4eba\u957f\u5f97\u5f97\u5f88\u6f02\u4eae\u3002\u5c3c\u6cca\u5c14 \u738b\u65e9\u60f3\u6536\u5979\u4f5c\u5983\u5b50\u4e86\uff0c\u53ea\u56e0\u5979\u662f\u4e00\u5411\u670d\u4f8d\u738b\u540e\u7684\uff0c\u738b\u540e\u5f88\u559c\u6b22\u5979\uff0c\u95ee\u8fc7\u5979\u7684\u610f\u601d\uff0c\u5979\u575a \u4e0d\u5e94\u5141\uff0c\u738b\u540e\u4e5f\u4e0d\u613f\u6709\u4eba\u5206\u5ba0\uff0c\u5c31\u66ff\u5979\u62d2\u7edd\u4e86\u56fd\u738b\u7684\u8981\u6c42\uff0c\u738b\u540e\u51fa\u8eab\u540d\u95e8\u5927\u65cf\uff0c\u56fd\u738b\u5bf9 \u5979\u4e5f\u6709\u51e0\u5206\u5bb3\u6015\uff0c\u8fd9\u4e8b\u60c5\u5c31\u6401\u4e0b\u6765\u4e86\u3002\u2026\u2026\u201d \u3000\u3000\u6c5f\u5357\u9053\uff1a\u201c\u54e6\uff0c\u6211\u660e\u767d\u4e86\u3002\u5df4\u52c3\u8981\u4ed6\u7684\u60c5\u4eba\u7b54\u5e94\u56fd\u4e3b\uff0c\u505a\u4ed6\u7684\u5983\u5b50\uff0c\u597d\u76f8\u673a\u884c\u523a\uff1f\u201d \u5e7d\u840d\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u82e5\u8981\u884c\u523a\uff0c\u5df4\u52c3\u4e0d\u4f1a\u81ea\u5df1\u4e0b\u624b\u5417\uff1f\u800c\u4e14\u56fd\u738b\u7684\u52bf\u529b\u65e9\u5df2\u6839\u6df1\u8482\u56fa\uff0c\u82e5 \u7136\u4e0d\u662f\u5c06\u4ed6\u7684\u52bf\u529b\u8fde\u6839\u62d4\u8d77\uff0c\u53ea\u6740\u6389\u4ed6\u4e00\u4e2a\u4eba\uff0c\u90a3\u4e5f\u8fd8\u662f\u4e0d\u80fd\u89e3\u9664\u82db\u653f\u7684\u3002\u4f55\u51b5\u56fd\u738b\u5b54 \u6b66\u6709\u529b\uff0c\u53c8\u591a\u9ad8\u624b\u62a4\u536b\uff0c\u8981\u884c\u523a\u4ed6\u4e5f\u5e76\u4e0d\u662f\u5bb9\u6613\u7684\u5462\u3002\u201d\u6c5f\u5357\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u5df4\u52c3\u662f\u4e3a\u4e86 \u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e7d\u840d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5df4\u52c3\u662f\u8981\u4ed6\u7684\u60c5\u4eba\u7b54\u5e94\u56fd\u738b\uff0c\u727a\u7272\u81ea\u5df1\uff0c\u505a\u56fd\u738b\u7684\u5983\u5b50\u3002\u4f46\u5374\u4e0d \u662f\u4e3a\u4e86\u884c\u523a\uff0c\u800c\u662f\u4e3a\u4e86\u7ed9\u6211\u4eec\u76d7\u53d6\u89e3\u836f\u3002\u201d \u3000\u3000\u5e7d\u840d\u53f9\u4e86\u53e3\u6c14\uff0c\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u5df4\u52c3\u7684\u8ba1\u5212\u5206\u4e09\u65b9\u9762\u8fdb\u884c\uff0c\u4e00\u662f\u7531\u4ed6\u7684\u60c5\u4eba\u76d7\u53d6\u89e3 \u836f\uff0c\u597d\u8ba9\u6211\u548c\u52a0\u6e90\u8d24\u4f84\u6062\u590d\u6b66\u529f\uff1b\u4e8c\u662f\u7531\u4ed6\u7684\u4e86\u4e00\u73ed\u5fd7\u540c\u9053\u5408\u7684\u4eba\uff0c\u4ed6\u6559\u4ed6\u4eec\u6b66\u827a\uff0c\u4e00 \u6709\u673a\u4f1a\uff0c\u5c31\u6388\u5f15\u4ed6\u4eec\u8fdb\u5bab\u6765\u5f53\u536b\u58eb\uff0c\u540c\u65f6\u4e5f\u8054\u7edc\u539f\u6709\u7684\u536b\u58eb\uff0c\u5728\u738b\u5bab\u91cc\u7ed3\u5408\u6210\u4e00\u80a1\u529b\u91cf\uff1b \u7b2c\u4e09\u518d\u8bbe\u6cd5\u4e0e\u5916\u9762\u7684\u4e49\u519b\u9886\u8896\u8054\u7edc\uff0c\u65f6\u673a\u4e00\u5230\uff0c\u4fbf\u91cc\u5e94\u5916\u5408\uff0c\u63a8\u7ffb\u56fd\u738b\uff0c\u94f2\u9664\u5978\u515a\u3002\u201d \u3000\u3000\u9648\u5929\u5b57\u9053\uff1a\u201c\u4e3a\u4e86\u63a8\u7ffb\u66b4\u541b\uff0c\u5df4\u52c3\u5b81\u613f\u727a\u7272\u81ea\u5df1\u7684\u60c5\u4eba\uff0c\u5f53\u771f\u662f\u4ee4\u4eba\u53c8\u540c\u60c5\u3001\u53c8\u8d77 \u656c\u3002\u4f46\u90a3\u5bab\u5973\u80af\u7b54\u5e94\u4e48\uff1f\u201d\u5e7d\u840d\u9053\uff1a\u201c\u4ed6\u4eec\u4e24\u4eba\u66fe\u7ecf\u5c71\u76df\u6d77\u8a93\uff0c\u77e2\u5fd7\u4e0d\u79fb\u7684\u3002\u90a3\u5bab\u5973\u60f3 \u4e0d\u5230\u60c5\u4eba\u7adf\u4f1a\u63d0\u51fa\u8fd9\u6837\u7684\u8981\u6c42\uff0c\u8d77\u5148\u4e0d\u80af\u7b54\u5e94\uff0c\u540e\u6765\u7ecf\u8fc7\u5df4\u52c3\u7684\u529d\u8bf4\uff0c\u4e24\u4eba\u62b1\u5934\u75c8\u54ed\u4e86 \u4e00\u573a\uff0c\u90a3\u5bab\u5973\u7ec8\u4e8e\u7b54\u5e94\u4e86\u3002\u201d \u3000\u3000\u4f17\u4eba\u90fd\u4e0d\u7981\u6697\u6697\u53f9\u606f\u3002\u5e7d\u840d\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5df4\u52c3\u8fd9\u51e0\u4e2a\u8ba1\u5212\u540c\u65f6\u8fdb\u884c\u3002\u5230\u4e86\u4eca\u5e74\u4e94\u6708\uff0c \u56fd\u738b\u4e0d\u77e5\u600e\u7684\uff0c\u5c06\u4ed6\u7684\u7b2c\u4e00\u6d41\u9ad8\u624b\uff0c\u6d3e\u51fa\u4e86\u4e00\u5927\u534a\uff0c\u636e\u5df4\u52c3\u63a2\u542c\u6240\u5f97\uff0c\u542c\u8bf4\u662f\u6d3e\u53bb\u53c2\u52a0 \u4e00\u4e2a\u4ec0\u4e48\u5c0f\u56fd\u56fd\u738b\u6240\u53ec\u5f00\u7684\u6b66\u58eb\u5927\u4f1a\u7684\u3002\u662f\u54ea\u4e00\u4e2a\u5c0f\u56fd\uff0c\u5728\u4f11\u4e48\u5730\u65b9\uff0c\u5df4\u52c3\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u60f3\u6765\u5b9a\u662f\u672c\u4e14\u5341\u4e94\u7684\u90a3\u4e2a\u91d1\u9e70\u5bab\u4e4b\u4f1a\u4e86\u3002\u90a3\u662f\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u5e08\u53ec\u5f00\u7684\uff0c \u4e0d\u662f\u56fd\u738b\u3002\u201d \u3000\u3000\u6cd5\u738b\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e8b\u60c5\u6211\u4e5f\u542c\u5f97\u5e08\u5f1f\u8bf4\u8fc7\uff0c\u4ed6\u81ea\u5df1\u672c\u6765\u51c6\u5907\u4e5f\u548c\u90a3\u51e0\u4e2a\u4f7f \u8005\u5728\u56de\u56fd\u4e4b\u524d\uff0c\u5148\u53bb\u53c2\u52a0\u90a3\u4e2a\u5927\u4f1a\u7684\u3002\u201d \u3000\u3000\u5e7d\u840d\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u5230\u4ec0\u4e48\u56fd\u5bb6\uff0c\u53c2\u52a0\u4ec0\u4e48\u5927\u4f1a\uff0c\u5bf9\u6211\u4eec\u6765\u8bf4\uff0c\u5e76\u4e0d\u91cd\u8981\u3002\u91cd\u8981 \u7684\u662f\uff0c\u8fd9\u662f\u4e00\u4e2a\u6700\u9002\u5f53\u7684\u65f6\u673a\uff0c\u8d81\u56fd\u738b\u529b\u91cf\u51cf\u5f31\u7684\u65f6\u5019\uff0c\u6211\u4eec\u53ef\u4ee5\u8d77\u4e8b\u3002 \u3000\u3000\u201c\u4e8e\u662f\u5df4\u52c3\u4fbf\u4e0e\u4e49\u519b\u6697\u901a\u6d88\u606f\uff0c\u7ea6\u597d\u4e86\u4e00\u4e2a\u65e5\u671f\uff0c\u5230\u65f6\u91cc\u5e94\u5916\u5408\uff0c\u4e00\u4e3e\u5c06\u66b4\u541b\u63a8\u7ffb\u3002 \u540c\u65f6\u4ed6\u53c8\u50ac\u4fc3\u4ed6\u7684\u60c5\u4eba\uff0c\u52a1\u5fc5\u8981\u5728\u7ea6\u597d\u7684\u65e5\u671f\u4e4b\u524d\uff0c\u5c06\u89e3\u836f\u76d7\u5230\u624b\u4e2d\u3002\u597d\u8ba9\u6211\u548c\u52a0\u6e90\u8d24 \u4f84\u6062\u590d\u6b66\u529f\u3002\u8fd9\u6837\u5c31\u4e0d\u4f46\u53ef\u4ee5\u5e2e\u5fd9\u4ed6\u4eec\uff0c\u800c\u4e14\u53ef\u4ee5\u7ed9\u8001\u767e\u59d3\u4e00\u4e2a\u4ed6\u4eec\u6240\u613f\u610f\u7231\u6234\u7684\u9886\u8896\uff0c \u8fd9\u610f\u4e49\u5c31\u66f4\u5927\u4e86\u3002\u201d \u3000\u3000\u5e7d\u840d\u8bf4\u5230\u8fd9\u91cc\uff0c\u53c8\u957f\u957f\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u7ed3\u679c\u89e3\u836f\u662f\u76d7\u5230\u4e86\uff0c\u4f46\u90a3\u4e2a\u5bab\u5973\uff0c\u5509\uff0c \u4e5f\u56e0\u6b64\u727a\u7272\u4e86\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u6062\u590d\u4e86\u6b66\u529f\uff0c\u4f55\u4ee5\u8fd8\u4e0d\u80fd\u6551\u5979\uff1f\u201d\u5e7d\u840d\u9053\uff1a\u201c\u5979\u662f \u7518\u613f\u820d\u5f03\u6027\u547d\uff0c\u5411\u56fd\u738b\u81ea\u9996\u7684\uff0c\u7b49\u6211\u4eec\u77e5\u9053\uff0c\u5df2\u7ecf\u8fdf\u4e86\u3002\u201d\u6c5f\u5357\u7741\u5927\u4e86\u670d\u775b\uff0c\u95ee\u9053\uff1a \u201c\u4e3a\u4ec0\u4e48\uff1f\u4e3a\u4ec0\u4e48\u8fd9\u6837\u7b28\uff1f\u201d \u3000\u3000\u5e7d\u840d\u542b\u6cea\u8bf4\u9053\uff1a\u201c\u5979\u4e0d\u662f\u611a\u7b28\u3002\u5979\u662f\u4e3a\u4e86\u6551\u4eba\u3002\u56fd\u738b\u53d1\u89c9\u89e3\u836f\u88ab\u76d7\uff0c\u9707\u6012\u975e\u5e38\uff0c\u5374 \u8fd8\u6ca1\u6709\u7591\u5fc3\u5230\u5979\u3002\u5979\u672c\u6765\u53ef\u4ee5\u9003\u51fa\u6765\u7684\uff0c\u4f46\u56fd\u738b\u58f0\u8a00\uff0c\u82e5\u7136\u67e5\u4e0d\u51fa\u76d7\u836f\u4e4b\u4eba\uff0c\u5c31\u8981\u62c5\u5f53 \u665a\u5728\u5bdd\u5bab\u503c\u591c\u7684\u5341\u4e8c\u4e2a\u5bab\u5973\u6d3b\u6d3b\u6253\u6b7b\uff0c\u4ed6\u773c\u89c1\u540c\u4f34\u53d7\u5230\u975e\u5211\u62f7\u6253\uff0c\u547b\u541f\u547c\u53f7\uff0c\u4e8e\u5fc3\u4e0d\u5fcd\uff0c \u5c31\u7ad9\u51fa\u53bb\u81ea\u9996\u5566\uff01\u56fd\u738b\u4e00\u6012\u4e4b\u4e0b\uff0c\u6beb\u4e0d\u987e\u5ff5\u6069\u60c5\uff0c\u5c31\u4e00\u5251\u5c06\u5979\u6740\u4e86\uff01\u201d \u3000\u3000\u4f17\u4eba\u65e0\u4e0d\u550f\u5618\u53f9\u606f\uff0c\u6cd5\u738b\u5408\u4ec0\u8bf4\u9053\uff1a\u201c\u8fd9\u771f\u662f\u5927\u4ec1\u5927\u52c7\u7684\u83e9\u8428\u5fb7\u884c\uff0c\u6bd4\u5272\u8089\u5582\u9e70\u3001 \u820d\u8eab\u6551\u864e\u5c24\u4e3a\u53ef\u4f69\u3002\u4f5b\u7956\u5e38\u4e91\uff1a\u6211\u4e0d\u5165\u5730\u72f1\uff0c\u8c01\u5165\u5730\u72f1\uff1f\u6b64\u5973\u5fb7\u884c\uff0c\u5eb6\u51e0\u8fd1\u77e3\u3002\u201d \u3000\u3000\u5e7d\u840d\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u56fd\u738b\u6740\u4e86\u5979\u4e4b\u540e\uff0c\u7acb\u5373\u6d3e\u4eba\u6765\u6349\u6211\u4eec\uff0c\u5e78\u800c\u6211\u4eec\u5f97\u4e86\u5df4\u52c3\u4e4b\u52a9\uff0c \u65e9\u5df2\u53e6\u5916\u627e\u4e86\u4e2a\u79d8\u5bc6\u5730\u65b9\u8eb2\u85cf\u8d77\u6765\u3002\u540c\u65f6\u8fc5\u901f\u5411\u5916\u9762\u7684\u4e49\u519b\u9012\u9001\u6d88\u606f\uff0c\u5f53\u665a\u4e09\u66f4\uff0c\u4e49\u519b \u5c31\u6253\u4e86\u8fdb\u6765\uff0c\u90a3\u65f6\u6211\u4eec\u670d\u4e0b\u89e3\u836f\u4e5f\u5df2\u8fc7\u4e86\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff0c\u6b66\u529f\u4e1a\u5df2\u6062\u590d\u4e86\u3002 \u3000\u3000\u201c\u4e8e\u662f\u91cc\u5e94\u5916\u5408\uff0c\u5c06\u56fd\u738b\u7684\u6b66\u58eb\u6253\u5f97\u843d\u82b1\u6d41\u6c34\uff0c\u53ef\u60dc\u529f\u4e8f\u4e00\u7bd1\uff0c\u6700\u540e\u7ed9\u4ed6\u4ece\u79d8\u9053\u4e2d \u9003\u51fa\u53bb\u3002 \u3000\u3000\u201c\u4ed6\u6839\u6df1\u62b5\u56fa\uff0c\u7fbd\u7ffc\u4f17\u591a\uff0c\u6709\u4e00\u90e8\u4efd\u519b\u961f\uff0c\u4f9d\u7136\u542c\u547d\u4e8e\u4ed6\u3002\u53e6\u5916\u8fd8\u6709\u4e00\u4e9b\u53d7\u8fc7\u4ed6\u597d \u5904\u7684\u8d35\u65cf\uff0c\u4e5f\u5e26\u9886\u5bb6\u5175\uff0c\u53f7\u79f0\uff0c\u2018\u52e4\u738b\u2019\uff0c\u524d\u6765\u52a9\u4ed6\u2018\u5e73\u4e71\u2019\u3002 \u3000\u3000\u201c\u4e49\u519b\u672c\u6765\u8981\u62e5\u62a4\u52a0\u6e90\u8d24\u4f84\u4e3a\u738b\uff0c\u52a0\u6e90\u8d24\u4f84\u575a\u8f9e\u4e0d\u5141\uff0c\u53ea\u80af\u4e0e\u5df4\u52c3\u5206\u4efb\u5de6\u53f3\u5927\u5c06\u519b\uff0c \u8f85\u4f50\u65b0\u738b\u3002\u540e\u6765\u4e49\u519b\u5728\u5306\u5306\u5fd9\u5fd9\u4e4b\u4e2d\uff0c\u53ea\u597d\u627e\u4e86\u4e00\u4f4d\u524d\u738b\u7684\u8fd1\u652f\u5b50\u5b59\uff0c\u63a8\u4ed6\u5750\u4e0a\u5b9d\u5ea7\u3002 \u3000\u3000\u201c\u65e7\u738b\u56db\u5904\u6563\u64ad\u8c23\u8a00\uff0c\u6307\u52a0\u6e90\u8d24\u4f84\u4e43\u662f\u6c49\u4eba\uff0c\u8fd9\u6b21\u53db\u4e71\u662f\u4ed6\u717d\u52a8\u7684\uff0c\u610f\u56fe\u4fb5\u5e76\u5c3c\u6cca \u5c14\u3002\u5e78\u4e8f\u52a0\u6e90\u8d24\u4f84\u6709\u8bc6\u89c1\uff0c\u65e9\u5c31\u63a8\u8f9e\u4e86\u738b\u4f4d\uff0c\u8981\u4e0d\u7136\u8fd9\u8c23\u8a00\u5c31\u66f4\u6613\u4ee4\u4eba\u5165\u4fe1\u4e86\u3002 \u3000\u3000\u201c\u5f53\u7136\u4e5f\u6709\u8bb8\u591a\u8001\u767e\u59d3\u77e5\u9053\u52a0\u6e90\u8d24\u4f84\u7684\u8840\u7edf\u6765\u5386\uff0c\u4ed6\u4eec\u6000\u5ff5\u516c\u4e3b\uff0c\u7231\u5c4b\u53ca\u4e4c\uff0c\u90fd\u8d77 \u6765\u62e5\u62a4\u4ed6\u3002\u800c\u4e14\u65e7\u738b\u7684\u66b4\u8650\uff0c\u65e9\u5df2\u5927\u5931\u4eba\u5fc3\uff0c\u56e0\u6b64\u4e49\u519b\u7684\u52bf\u529b\u8fd8\u662f\u6bd4\u4ed6\u4eec\u7684\u2018\u8ba8\u4e71\u519b\u2019 \u5927\u5f97\u591a\u3002 \u3000\u3000\u201c\u7ecf\u8fc7\u4e86\u51e0\u573a\u6076\u6218\uff0c\u65e7\u738b\u7684\u519b\u961f\u8282\u8282\u8d25\u9000\uff0c\u9000\u5230\u4e86\u897f\u5317\u7684\u5c71\u533a\uff0c\u4f46\u8fd8\u672a\u66fe\u6d88\u706d\uff0c\u5c1a \u662f\u9690\u5fe7\u3002\u800c\u4e14\u98ce\u95fb\u4ed6\u5df2\u4e0e\u5370\u5ea6\u7684\u5580\u6797\u90a6\u52fe\u7ed3\uff0c\u53ea\u6015\u4ed6\u8fd8\u4f1a\u5f15\u8fdb\u5916\u5175\uff0c\u917f\u6210\u5185\u4e71\u3002 \u3000\u3000\u201c\u5df4\u52c3\u5fe7\u5fc3\u5982\u711a\uff0c\u7b79\u601d\u518d\u4e09\uff0c\u51b3\u5b9a\u53eb\u6211\u56de\u6765\uff0c\u8bf7\u516c\u4e3b\u56de\u56fd\uff0c\u6536\u62fe\u4eba\u5fc3\uff0c\u4ee5\u516c\u4e3b\u548c\u62a4 \u6cd5\u7684\u4e24\u91cd\u8eab\u4efd\uff0c\u6b63\u5f0f\u7b56\u7acb\u65b0\u738b\uff0c\u5e73\u5b9a\u5185\u4e71\u3002\u201d \u3000\u3000\u5e7d\u840d\u7684\u6545\u4e8b\uff0c\u81f3\u6b64\u624d\u544a\u4e00\u4e2a\u6bb5\u843d\u3002\u9648\u5929\u5b87\u542c\u7f62\uff0c\u4e0d\u89c9\u6cea\u5149\u3001\u83b9\u7136\uff0c\u7d27\u63e1\u7740\u5979\u7684\u624b\u9053\uff1a \u201c\u840d\u59b9\uff0c\u8fd9\u51e0\u5e74\u6765\u4f60\u5403\u5c3d\u4e86\u82e6\u4e86\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u5f1f\u5ac2\u5403\u4e86\u82e6\u4f46\u4e5f\u7acb\u4e86\u529f\uff0c\u8981\u4e0d\u662f\u5979\u548c\u90a3\u4e2a\u5bab\u5973\u8bbe\u8c0b\u5b9a\u8ba1\uff0c\u5c06\u5df4\u52c3\u5f15 \u8fdb\u738b\u5bab\uff0c\u5c3c\u6cca\u5c14\u7684\u66b4\u541b\u8fd8\u4e0d\u4f1a\u8fd9\u6837\u88ab\u63a8\u7ffb\u5462\uff0c\u8fd9\u5c82\u4e0d\u662f\u5927\u529f\u4e00\u4ef6\uff1f\u597d\uff0c\u54b1\u4eec\u90fd\u4e3a\u4ed6\u4eec\u592b \u59bb\u4fe9\u56e2\u5706\u5e72\u4e00\u676f\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u559d\u8fc7\u4e86\u9152\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u5b87\uff0c\u62b1\u6b49\u5f97\u5f88\uff0c\u4f60\u4eec\u592b\u59bb\u53ea\u6015\u4e0d\u80fd\u56e2\u805a\u51e0\u5929\uff0c\u53c8\u8981\u5206 \u5f00\u4e00\u4e9b\u65f6\u5019\u4e86\u3002\u6211\u4eec\u5c31\u8981\u52a8\u8eab\u5230\u5c3c\u6cca\u5c14\u53bb\u4e86\u2026\u2026\u201d\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u51b0\u5ddd\u5929\u5973\u5ffd\u5730\u7b11\u9053\uff1a \u201c\u4f55\u5fc5\u8981\u4ed6\u592b\u59bb\u5206\u624b\uff0c\u5c31\u8bf7\u5929\u5b57\u540c\u53bb\uff0c\u4e0d\u5f88\u597d\u4e48\uff1f\u201d \u3000\u3000\u5510\u7ecf\u5929\u6572\u4e86\u6572\u8111\u888b\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u6211\u8111\u888b\u7cca\u6d82\uff0c\u6211\u6b63\u662f\u8981\u4f60\u51fa\u9762\u9080\u8bf7\u4ed6\u554a\uff0c\u4f60\u662f \u4e3b\u4eba\uff0c\u6211\u53ef\u4e0d\u80fd\u4ee3\u4f60\u8bf7\u5ba2\u5462\u3002\u201d\u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a\u201c\u4f60\u5fd8\u4e86\u840d\u59b9\u4e5f\u662f\u4e3b\u4eba\u4e48\uff1f\u4f55\u987b\u9080\u8bf7\uff0c \u8fd9\u53eb\u505a\u2014\u2014\u201d\u6c5f\u5357\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u8fd9\u53eb\u505a\u5a36\u9e21\u968f\u9e21\uff0c\u5a36\u72d7\u968f\u72d7\uff0c\u5f52\u5531\u592b\u968f\uff01\u201d\u5e7d\u840d\u4f6f\u6012\u9053\uff1a \u201c\u5357\u5f1f\uff0c\u4f60\u5012\u4f1a\u8bf4\u8bdd\uff0c\u7ed5\u7740\u5f2f\u513f\u9a82\u4eba\u4e86\u3002\u201d \u3000\u3000\u5f7c\u6b64\u5f00\u4e86\u4e00\u4f1a\u73a9\u7b11\uff0c\u5c06\u9648\u5929\u5b87\u592b\u59bb\u7684\u4f24\u611f\u51b2\u6de1\u4e86\u4e0d\u5c11\u3002\u51b0\u5ddd\u5929\u5973\u5ffd\u9053\uff1a\u201c\u522b\u5f00\u73a9\u7b11 \u4e86\uff0c\u8fd8\u6709\u6b63\u7ecf\u7684\u4e8b\u5462\u3002\u59ec\u5148\u751f\uff0c\u4f60\u521a\u624d\u6240\u8bf4\u7684\u90a3\u4e2a\u9a6c\u8428\u513f\u56fd\u662f\u5426\u5728\u963f\u5c14\u6cf0\u5c71\u5c71\u811a\u7684\u4e00\u4e2a \u5c0f\u56fd\u5bb6\uff1f\u4ed6\u4eec\u7684\u56fd\u5e08\u662f\u4e0d\u662f\u4e00\u4e2a\u4ece\u5370\u5ea6\u6765\u7684\u50e7\u4eba\u540d\u53eb\u5b9d\u8c61\u6cd5\u5e08\u7684\uff1f\u4ed6\u53ec\u5f00\u7684\u91d1\u9e70\u5bab\u4e4b\u4f1a \u4f1a\u671f\u662f\u54ea\u4e00\u5929\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u539f\u6765\u4f60\u65e9\u5df2\u77e5\u9053\u90a3\u91d1\u9e70\u5bab\u4e3b\u4eba\u7684\u6765\u5386\u4e86\u3002\u4f1a\u671f\u662f\u672c\u6708\u5341\u4e94\uff0c\u54ce\uff0c \u8ddd\u4eca\u53ea\u6709\u4e94\u5929\u4e86\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5411\u9648\u5929\u5b87\u8bf4\u9053\uff1a\u201c\u4f60\u53bb\u5e74\u4e0a\u5929\u5c71\u7684\u65f6\u5019\uff0c\u6211\u4eec\u6b63\u5728\u5bfb\u627e\u5c0f\u513f\uff0c\u66fe\u8fdc\u5230\u4e2d\u5370 \u4ea4\u754c\u7684\u5927\u5409\u5cad\uff0c\u78b0\u89c1\u4e86\u9f99\u7075\u77eb\u592b\u59bb\uff0c\u4ed6\u4eec\u6b63\u6e38\u7f62\u5929\u7afa\u56de\u6765\uff0c\u5411\u6211\u8bf4\u8d77\u90a3\u5b9d\u8c61\u6cd5\u5e08\u7684\u6765\u5386\u3002 \u4ed6\u4eec\u66fe\u5230\u8fc7\u90a3\u70c2\u9640\u5bfa\u8c12\u89c1\u9f99\u53f6\u4e0a\u4eba\uff0c\u9f99\u53f6\u4e0a\u4eba\u5df2\u8fc7\u767e\u5c81\uff0c\u4f46\u56e0\u4ed6\u4ed6\u4eec\u8fdc\u9053\u800c\u6765\uff0c\u6240\u4ee5\u8fd8 \u662f\u4eb2\u8eab\u63a5\u89c1\u3002 \u3000\u3000\u201c\u9f99\u53f6\u4e0a\u4eba\u5bf9\u4ed6\u63d0\u53ca\u6709\u8fd9\u4e48\u4e00\u4e2a\u5f1f\u5b50\uff0c\u53d7\u8058\u4e3a\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u5e08\uff0c\u8bf7\u4ed6\u4eec\u4ee3\u529e\u67e5\u8003\u4ed6 \u8fd9\u4e2a\u5f1f\u5b50\u5728\u9a6c\u8428\u513f\u56fd\u7684\u884c\u4e3a\u5982\u4f55\u3002\u56e0\u4e3a\u6709\u4eba\u544a\u8bc9\u9f99\u53f6\u4e0a\u4eba\uff0c\u8bf4\u4ed6\u8fd9\u4e2a\u5f1f\u5b50\u8fde\u5e74\u6765\u9080\u8bf7\u4e86 \u8bb8\u591a\u5370\u5ea6\u7684\u6b66\u5b66\u9ad8\u624b\u524d\u5f80\u9a6c\u8428\u513f\u56fd\uff0c\u751a\u81f3\u4e00\u5411\u4e0e\u4f5b\u6559\u4e3a\u654c\u7684\u5a46\u7f57\u95e8\u6559\u4e2d\u7684\u4e09\u5927\u9ad8\u624b\u4e5f\u63a5 \u53d7\u4e86\u4ed6\u7684\u9080\u8bf7\u3002\u9f99\u53f6\u4e0a\u4eba\u4e0d\u77e5\u4ed6\u7684\u5f1f\u5b50\u5e72\u4e9b\u4ec0\u4e48\uff0c\u662f\u4ee5\u52a8\u95ee\u3002 \u3000\u3000\u201c\u9f99\u7075\u77eb\u592b\u5987\u5e76\u4e0d\u77e5\u9053\u5b9d\u8c61\u5176\u4eba\uff0c\u53ea\u597d\u7b54\u5e94\u56de\u56fd\u540e\u518d\u884c\u67e5\u8bbf\u3002\u4ed6\u4eec\u5f53\u65f6\u8fd8\u5949\u627f\u4e86\u9f99 \u53f6\u4e0a\u4eba\u4e00\u756a\uff0c\u8bf4\u65e2\u7136\u662f\u4f60\u5927\u5e08\u7684\u9ad8\u8db3\uff0c\u6599\u60f3\u4e0d\u4f1a\u4f5c\u51fa\u4ec0\u4e48\u574f\u4e8b\u3002\u9f99\u53f6\u4e0a\u4eba\u9ed8\u7136\u4e0d\u8bed\uff0c\u4f3c \u4e4e\u5bf9\u8fd9\u4e2a\u5f1f\u5b50\u5f88\u4e0d\u653e\u5fc3\u3002 \u3000\u3000\u201c\u9f99\u7075\u77eb\u5c06\u8fd9\u4ef6\u4e8b\u60c5\u544a\u8bc9\u6211\u4eec\uff0c\u6211\u4eec\u6b63\u662f\u81ea\u8eab\u6709\u4e8b\uff0c\u5f53\u65f6\u53ea\u662f\u653e\u5728\u5fc3\u4e0a\uff0c\u65e0\u6687\u5230\u9a6c \u8428\u513f\u56fd\u53bb\u63a2\u4e2a\u7a76\u7adf\u3002\u53ef\u662f\u73b0\u5728\u542c\u4e86\u6cd5\u738b\u548c\u59ec\u5148\u751f\u6240\u8bf4\uff0c\u6562\u60c5\u6211\u56fd\u597d\u738b\u6240\u6d3e\u51fa\u7684\u4e00\u6279\u9ad8\u624b\uff0c \u5c31\u662f\u6765\u53c2\u52a0\u4ed6\u7684\u4ec0\u4e48\u91d1\u9e70\u5bab\u4e4b\u4f1a\u7684\uff0c\u8fd9\u4e8b\u60c5\u5c31\u4e0d\u540c\u4e86\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u559d\u4e86\u53e3\u8336\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u56de\u6765\u4e4b\u540e\uff0c\u516c\u516c\u8bf4\u4e0e\u6211\u77e5\uff0c\u6211\u624d\u77e5\u9053\u5929\u5b57\u6765\u8fc7\uff0c \u5929\u5b57\u5e26\u6765\u7684\u6d88\u606f\u4ee4\u6211\u4e00\u559c\u4e00\u5fe7\uff0c\u5fe7\u8005\u662f\u5e7d\u840d\u59b9\u5b50\u4e5f\u83ab\u540d\u5176\u5999\u7684\u5931\u4e86\u8e2a\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u4eba\u63b3 \u53bb\uff1f\u559c\u8005\u662f\u5230\u5e95\u5f97\u5230\u4e86\u4e00\u70b9\u7ebf\u7d22\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u8bf4\u5230\u6b64\u5904\uff0c\u5411\u6cd5\u738b\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e00\u559c\u4e00\u5fe7\u4e4b\u5916\u8fd8\u8981\u52a0\u4e0a\u4e00\u5947\uff0c\u5f53\u6211 \u542c\u8bf4\u63b3\u8d70\u5e7d\u840d\u7684\u90a3\u4e24\u4e2a\u8d3c\u4eba\u7adf\u662f\u85cf\u5728\u4f60\u7684\u9102\u514b\u6c81\u5bab\uff0c\u6211\u7b80\u76f4\u4e0d\u6562\u76f8\u4fe1\uff0c\u8c01\u77e5\u662f\u8fd9\u4e48\u4e00\u56de \u4e8b\uff0c\u4e0e\u4f60\u5b8c\u5168\u65e0\u5173\u3002\u201d \u3000\u3000\u6cd5\u738b\u5408\u4ec0\u8bf4\u9053\uff1a\u201c\u60ed\u6127\uff0c\u60ed\u6127\uff01\u6b64\u4e8b\u7684\u7f6a\u9b41\u7978\u9996\u867d\u662f\u8001\u8872\u90a3\u4e0d\u8096\u5e08\u5f1f\uff0c\u4f46\u8001\u8896\u672a\u80fd \u5236\u6b62\uff0c\u4e5f\u662f\u917f\u6210\u5927\u9519\u4e4b\u56e0\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a\u201c\u8fd9\u4e8b\u6487\u8fc7\u4e0d\u6de1\u3002\u82e5\u8bf4\u5230\u771f\u6b63\u7684\u7f6a\u9b41\u7978\u9996\uff0c\u8fd8\u662f\u6211\u56fd\u56fd\u738b\uff0c\u6211\u4e5f\u5e94 \u8be5\u60ed\u6127\u5462\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4e0d\u4e45\u5e7d\u840d\u59b9\u5b50\u4e5f\u8131\u9669\u5f52\u6765\u4e86\uff0c\u4e91\u96fe\u62e8\u5f00\uff0c\u6c34\u843d\u77f3\u51fa\uff0c\u53ea\u6709\u4e00 \u70b9\u8fd8\u6765\u660e\u767d\u7684\u662f\uff1a\u5978\u738b\u6d3e\u51fa\u7684\u90a3\u4e00\u6279\u9ad8\u624b\uff0c\u5230\u5e95\u662f\u53bb\u4ec0\u4e48\u5730\u65b9\uff1f \u3000\u3000\u201c\u5f53\u65f6\u7167\u5e7d\u840d\u59b9\u5b50\u7684\u610f\u601d\uff0c\u672c\u6765\u662f\u8981\u6211\u7acb\u523b\u542f\u7a0b\uff0c\u524d\u5f80\u52a0\u5fb7\u6ee1\u90fd\u7684\u3002\u4f46\u6211\u4e00\u60f3\uff0c\u90a3 \u4ef6\u4e8b\u60c5\u672a\u5f04\u6e05\u695a\uff0c\u7a76\u7adf\u4e0d\u80fd\u653e\u5fc3\u3002\u8bf4\u5b9e\u5728\u7684\uff0c\u6211\u662f\u5341\u5206\u62c5\u5fe7\u597d\u738b\u6d3e\u51fa\u7684\u90a3\u6279\u9ad8\u624b\uff0c\u8981\u5728 \u897f\u85cf\u5174\u98ce\u4f5c\u6d6a\u5462\u3002 \u3000\u3000\u201c\u6211\u628a\u5929\u5b87\u5230\u8fc7\u5929\u5c71\uff0c\u4ee5\u53ca\u4ed6\u5e26\u6765\u7684\u6d88\u606f\u4e5f\u544a\u8bc9\u4e86\u5e7d\u840d\u59b9\u5b50\uff0c\u5e7d\u840d\u59b9\u5b50\u542c\u8bf4\u4e08\u592b\u8fd9 \u4e9b\u5e74\u6765\uff0c\u56db\u65b9\u5954\u8dd1\u3001\u8d39\u5c3d\u5fc3\u529b\u53bb\u627e\u5bfb\u5979\uff0c\u5341\u5206\u611f\u52a8\uff0c\u5927\u54ed\u4e86\u4e00\u573a\uff0c\u5f53\u4e0b\u4e5f\u5c31\u4e0d\u575a\u6301\u7acb\u5373 \u56de\u56fd\u4e86\u3002\u201d \u3000\u3000\u5e7d\u840d\u6ee1\u9762\u901a\u7ea2\uff0c\u5c34\u5c2c\u8bf4\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u5927\u54ed\u4e00\u573a\uff0c\u6389\u4e86\u51e0\u9897\u773c\u6cea\uff0c\u90a3\u662f\u6709\u7684\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7b11\u7740\u7eed\u9053\uff1a\u201c\u5927\u54ed\u4e5f\u7f62\uff0c\u5c0f\u54ed\u4e5f\u7f62\uff0c\u603b\u4e4b\u4f60\u662f\u54ed\u4e86\u3002\u4e0d\u8fc7\uff0c\u73b0\u5728\u597d\u4e86\uff0c\u4f60 \u4eec\u592b\u59bb\u56e2\u5706\uff0c\u4e8b\u60c5\u4e5f\u90fd\u5b8c\u5168\u6e05\u695a\u4e86\u3002 \u3000\u3000\u201c\u5978\u738b\u6d3e\u51fa\u7684\u8fd9\u6279\u9ad8\u624b\uff0c\u65e2\u7136\u662f\u8981\u53bb\u53c2\u52a0\u4ec0\u4e48\u91d1\u9e70\u5bab\u4e4b\u4f1a\uff0c\u90a3\u4e48\u6211\u4eec\u4e5f\u4f3c\u4e4e\u8981\u5148\u5230 \u9a6c\u8428\u513f\u56fd\u8d70\u4e00\u8d9f\u4e86\u3002\u4e00\u6765\u5c06\u5978\u738b\u7684\u8fd9\u6279\u515a\u7fbd\u6253\u53d1\u6389\uff0c\u514d\u5f97\u4ed6\u4eec\u518d\u56de\u53bb\u76f8\u52a9\u5978\u738b\u3002\u4e8c\u6765\u4e5f \u4e0d\u8d1f\u9f99\u7075\u77eb\u7684\u8f6c\u6258\u3002\u9f99\u53f6\u4e0a\u4eba\u5f53\u5e74\u6295\u6211\u8d1d\u53f6\u7075\u7b26\uff0c\u5c01\u6211\u4e3a\u5973\u62a4\u6cd5\u3002\u73b0\u5728\u4ed6\u4eec\u4e0b\u51fa\u4e86\u4e0d\u8096 \u5f1f\u5b50\uff0c\u6709\u73b7\u4ed6\u7684\u58f0\u7737\uff0c\u4ed6\u8001\u4eba\u5bb6\u65e2\u4e3a\u6b64\u4e8b\u6302\u5fc3\uff0c\u6211\u4e5f\u8be5\u4e3a\u4ed6\u8001\u4eba\u5bb6\u5c3d\u70b9\u529b\u624d\u5bf9\u3002\u201d \u3000\u3000\u6c5f\u5357\u5927\u559c\u9053\uff1a\u201c\u597d\u5440\uff0c\u54b1\u4eec\u90fd\u5230\u91d1\u9e70\u5bab\u505a\u4e2a\u95ef\u5ba2\uff0c\u8fd9\u53ef\u771f\u70ed\u95f9\u4e86\u3002\u6d77\u513f\uff0c\u4f60\u73b0\u5728\u53ef \u4ee5\u653e\u5fc3\u5566\uff0c\u6709\u8fd9\u4e48\u591a\u53d4\u53d4\u4f2f\u4f2f\u5a76\u5a76\u548c\u4f60\u4e00\u540c\u53bb\uff0c\u4f60\u518d\u4e5f\u4e0d\u7528\u6015\u52bf\u5b64\u529b\u5f31\u5566\u3002\u201d \u3000\u3000\u5e7d\u840d\u8be7\u9053\uff1a\u201c\u4f84\u513f\u672c\u6765\u662f\u5230\u91d1\u9e70\u5bab\u8d74\u5bb4\u7684\u5417\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u4ee3\u90ce\u5c71\u7684 \u8c37\u638c\u95e8\u6536\u4e0b\u8bf7\u5e16\u7684\u3002\u4f60\u4eec\u53ef\u6709\u6dee\u77e5\u9053\u6211\u5e08\u7236\u7684\u6d88\u606f\u5417\uff1f\u201d\u5f53\u4e0b\u6c5f\u6d77\u5929\u4e5f\u628a\u4ed6\u7684\u906d\u9047\u8bf4\u4e86 \u51fa\u6765\u3002\u542c\u5f97\u4f17\u4eba\u65e0\u4e0d\u8be7\u5f02\u3002\u4f46\u91d1\u4e16\u9057\u7684\u6d88\u606f\u5374\u4e43\u662f\u65e0\u4eba\u77e5\u9053\uff0c\u51b0\u5ddd\u5929\u5973\u542c\u8bf4\u91d1\u4e16\u9057\u59cb\u7ec8 \u4e0d\u80fd\u4e0e\u8c37\u4e4b\u534e\u6210\u4e3a\u4f73\u5076\uff0c\u4e5f\u4e0d\u89c9\u60d8\u7136\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u4f17\u4eba\u76f8\u805a\uff0c\u4e43\u662f\u7236\u5b50\u3001\u592b\u59bb\u3001\u5144\u5f1f\u3001\u826f\u670b\u7684\u4e45\u522b\u91cd\u9022\uff0c\u7aef\u7684\u662f\u6709\u8bf4\u4e0d\u5b8c\u7684\u3001 \u53d9\u4e0d\u5c3d\u7684\u60c5\uff0c\u76f4\u8c08\u5230\u6708\u8fc7\u4e2d\u5929\uff0c\u65b9\u59cb\u6563\u5e2d\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\u8d77\u6765\uff0c\u51b0\u5ddd\u5929\u5973\u7387\u9886\u4f17\u4eba\u5411\u6cd5\u738b\u544a\u522b\uff0c\u6cd5\u738b\u8bf4\u9053\uff1a\u201c\u6211\u6709\u4e24\u5339\u5b9d\u9a6c\uff0c\u8d60 \u5404\u4f4d\uff0c\u8fd9\u662f\u5eb7\u5c45\u56fd\u738b\u5230\u672c\u5bfa\u8fdb\u9999\u65f6\u6240\u5949\u732e\u7684\u5fa1\u9a6c\uff0c\u653e\u5f00\u811a\u7a0b\uff0c\u53ef\u4ee5\u65e5\u884c\u5343\u91cc\u3002\u53ef\u60dc\u53ea\u6709 \u4e24\u5339\uff0c\u5176\u4ed6\u51e1\u9a6c\uff0c\u5374\u662f\u914d\u4e0d\u4e0a\u5404\u4f4d\u3002\u8584\u793c\u4e0d\u606d\uff0c\u8fd8\u671b\u6055\u8fc7\u3002\u201d\u59ec\u6653\u98ce\u7b11\uff1a\u201c\u6709\u4e24\u5339\u5df1\u8db3 \u591f\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u6b64\u53bb\u9a6c\u8428\u513f\u56fd\uff0c\u6709\u5343\u591a\u91cc\u8def\u7a0b\uff0c\u800c\u8ddd\u79bb\u91d1\u9e70\u5bab\u4e4b\u4f1a\u7684\u4f1a\u671f\uff0c\u5219\u53ea\u6709\u4e94\u5929\uff0c\u6cd5\u738b \u77e5\u59ec\u6653\u98ce\u4e0e\u51b0\u5ddd\u5929\u5973\u7684\u8f7b\u529f\u5353\u7edd\uff0c\u5c3d\u53ef\u5728\u4f1a\u671f\u524d\u8d76\u5230\uff0c\u4f46\u5176\u4ed6\u5404\u4eba\u7684\u529f\u592b\u6df1\u6d45\uff0c\u4ed6\u5374\u4e0d \u5c3d\u77e5\u6653\uff0c\u662f\u4ee5\u8d60\u9001\u5b9d\u9a6c\uff0c\u51c6\u5907\u7ed9\u4ed6\u4eec\u4e4b\u4e2d\u8f7b\u529f\u8f83\u5f31\u7684\u4eba\u7528\u4f5c\u4ee3\u6b65\u7684\u3002 \u3000\u3000\u4f17\u4eba\u51fa\u4e86\u9102\u514b\u6c81\u5bab\u4e4b\u540e\uff0c\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6c5f\u5357\uff0c\u4f60\u4e24\u7236\u5b50\u5404\u4e58\u4e00\u5339\u5427\u3002\u201d\u6c5f\u5357\u7b11\u9053\uff1a \u201c\u6211\u7684\u811a\u7a0b\u51b3\u8ba1\u8d76\u4e0d\u4e0a\u4f60\u4eec\uff0c\u4e3a\u4e86\u907f\u514d\u803d\u8bef\u5927\u4e8b\uff0c\u6211\u5c31\u4e0d\u5ba2\u6c14\u4e86\u3002\u6d77\u513f\u5e74\u8f7b\u529b\u58ee\uff0c\u8be5\u8ba9 \u4ed6\u591a\u8d70\u8d70\u8def\uff0c\u53e6\u4e00\u5339\u8bf7\u4e8c\u5ac2\u9a91\u5427\u3002\u201d\u5e7d\u840d\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u53ef\u4ee5\u8d70\u5f97\u3002\u201d\u9648\u5929\u5b87\u4f53\u8d34\u59bb\u5b50\uff0c \u8bf4\u9053\uff1a\u201c\u4f60\u8fde\u65e5\u5954\u5f7c\uff0c\u6682\u4e14\u7528\u4e0a\u4e00\u5929\u4ee3\u6b65\u5427\u3002\u201d\u6c5f\u5357\u9053\uff1a\u201c\u5bf9\uff0c\u8981\u662f\u6d77\u5929\u660e\u65e5\u8d70\u4e0d\u52a8\u4e86\uff0c \u518d\u6362\u7ed9\u4ed6\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a\u201c\u59ec\u5148\u751f\uff0c\u4e45\u4ef0\u4f60\u8f7b\u529f\u5353\u7edd\uff0c\u6211\u4e0e\u4f60\u6bd4\u8bd5\u4e00\u4e0b\u3002\u201d\u59ec\u6653\u98ce\u8c6a\u5174\u5927\u8d77\uff0c \u8bf4\uff1a\u201c\u6b63\u60f3\u5411\u5929\u5973\u9886\u6559\u3002\u201d\u5510\u7ecf\u5929\u9053\uff1a\u201c\u53cd\u6b63\u54b1\u4eec\u90fd\u8981\u8d76\u8def\uff0c\u5927\u5bb6\u90fd\u6765\u7adf\u8d70\uff0c\u6b63\u662f\u4e00\u4e3e \u4e24\u5f97\u3002\u201d \u3000\u3000\u59ec\u6653\u98ce\u5fc3\u9053\uff1a\u201c\u542c\u8bf4\u51b0\u5ddd\u5929\u5973\u5f53\u5e74\u66fe\u5728\u559c\u9a6c\u62c9\u96c5\u5c71\u65bd\u5c55\u8f7b\u529f\uff0c\u538b\u5012\u4e86\u963f\u62c9\u4f2f\u7b2c\u4e00\u9ad8 \u624b\u63d0\u6469\u8fbe\u591a\uff0c\u9707\u60ca\u4e86\u5f53\u65f6\u5c6f\u5175\u5728\u5c71\u8c37\u7684\u4e2d\u5c3c\u4e24\u56fd\u5341\u4e07\u5927\u519b\u3002\u4e0d\u77e5\u6bd4\u6211\u5982\u4f55\uff1f\u201d\u4ed6\u4e89\u80dc\u4e4b \u5ff5\u4e00\u8d77\uff0c\u65bd\u5c55\u51fa\u5e73\u751f\u672c\u9886\uff0c\u5f53\u771f\u662f\u8ffd\u98ce\u9010\u7535\uff0c\u8d5b\u8fc7\u5954\u9a6c\u3002\u51b0\u5ddd\u5929\u5973\u4e0d\u5373\u4e0d\u79bb\u7684\u4e0e\u4ed6\u5e76\u80a9 \u800c\u884c\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u8d76\u8fc7\u4e86\u4ed6\uff0c\u59ec\u6653\u98ce\u4e00\u52a0\u52b2\uff0c\u518d\u8d76\u8fc7\u5979\uff0c\u4e24\u4eba\u5ffd\u800c\u4f60\u5728\u6211\u524d\uff0c\u5ffd\u800c\u6211\u5728 \u4f60\u524d\uff0c\u7aef\u7684\u662f\u96be\u5206\u9ad8\u4e0b\u3002 \u3000\u3000\u9648\u5929\u5b87\u66fe\u670d\u98df\u8fc7\u51b0\u5bab\u5f02\u679c\uff0c\u8eab\u8f7b\u5982\u71d5\uff0c\u8f7b\u8f7b\u4e00\u8dc3\uff0c\u5c31\u662f\u6570\u4e08\u4e4b\u9065\uff1b\u5510\u7ecf\u5929\u4ed7\u7740\u5185\u529f \u6df1\u6e5b\uff0c\u8d77\u521d\u7a0d\u7a0d\u843d\u540e\uff0c\u968f\u5373\u5c31\u8d76\u4e0a\u4e86\u4ed6\u3002\u4e24\u4eba\u4e5f\u662f\u96be\u5206\u9ad8\u4e0b\uff0c\u867d\u7136\u8ffd\u4e0d\u4e0a\u59ec\u6653\u98ce\u4e0e\u51b0\u5ddd \u5929\u5973\uff0c\u4f46\u4e5f\u843d\u540e\u4e0d\u8fdc\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8d77\u521d\u9887\u4e3a\u81ea\u4fe1\uff0c\u4ee5\u4e3a\u81ea\u5df1\u7eb5\u7136\u4e89\u4e0d\u5230\u7b2c\u4e00\uff0c\u4e5f\u7edd\u4e0d\u81f3\u4e8e\u843d\u540e\uff0c\u54ea\u77e5\u4e00\u80de\u8d77\u6765\uff0c \u6700\u521d\u8fd8\u52c9\u5f3a\u53ef\u4ee5\u8ddf\u5f97\u4e0a\u5510\u7ecf\u5929\uff0c\u6e10\u6e10\u5c31\u7ed9\u4ed6\u629b\u5728\u540e\u9762\uff0c\u7adf\u7136\u843d\u4e86\u4e2a\u5012\u6570\u7b2c\u4e00\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a \u201c\u602a\u4e0d\u5f97\u5e08\u7236\u5e38\u8bf4\u5b66\u65e0\u6b62\u5883\uff0c\u5929\u5916\u6709\u5929\u3002\u6211\u53ea\u9053\u6211\u5df2\u5c3d\u5f97\u5e08\u7236\u771f\u4f20\uff0c\u529f\u592b\u5df2\u8fc7\u5f97\u53bb\u3002\u54ea \u77e5\u53ea\u662f\u8fd9\u8f7b\u529f\u4e00\u95e8\uff0c\u8fd9\u51e0\u4f4d\u4f2f\u4f2f\uff0c\u5c31\u4e2a\u4e2a\u8fdc\u80dc\u4e8e\u6211\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u81ea\u611f\u60ed\u6127\uff0c\u5510\u3001\u9648\u4e8c\u4eba\u5374\u662f\u5927\u5927\u7684\u60ca\u5947\uff0c\u4ed6\u4eec\u6700\u521d\u8fd8\u62c5\u5fc3\u6c5f\u6d77\u5929\u4f1a\u843d\u540e\u5927\u8fdc\uff0c \u803d\u8bef\u884c\u7a0b\uff0c\u54ea\u77e5\u4e00\u53e3\u6c14\u8dd1\u4e86\u4e24\u4e2a\u65f6\u8fb0\uff0c\u6c5f\u6d77\u5929\u867d\u7136\u843d\u540e\uff0c\u4f46\u4ea6\u4e0d\u8fc7\u843d\u540e\u5341\u6570\u4e08\u4e4b\u9065\u3002\u5510 \u7ecf\u5929\u5fc3\u60f3\uff1a\u201c\u771f\u96be\u4e3a\u4e86\u91d1\u4e16\u9057\uff0c\u4e0d\u77e5\u4ed6\u662f\u600e\u6837\u8c03\u6559\u51fa\u6765\u7684\uff1f\u4e0d\u4f46\u8f7b\u529f\u4e86\u5f97\uff0c\u5185\u529b\u66f4\u662f\u60a0 \u957f\u3002\u4e0d\u6d88\u51e0\u5e74\uff0c\u7b2c\u4e00\u6d41\u9ad8\u624b\u4e4b\u4e2d\uff0c\u5c31\u5e94\u8865\u4e0a\u4ed6\u7684\u540d\u5b57\u4e86\u3002\u201d \u3000\u3000\u5e78\u4e8f\u6c5f\u5357\u9a91\u7684\u4e43\u662f\u5b9d\u9a6c\uff0c\u4f46\u5728\u6700\u521d\u7684\u5341\u91cc\u8def\u7a0b\u4e4b\u5185\uff0c\u51b0\u5929\u5973\u548c\u59ec\u6653\u98ce\u8fd8\u662f\u8d76\u8fc7\u4e86\u4ed6\u3002 \u8d70\u51fa\u4e86\u4e09\u5341\u91cc\u5916\uff0c\u4ed6\u548c\u5e7d\u840d\u8fd9\u4e24\u5339\u5750\u9a91\u624d\u4e00\u76f4\u9886\u524d\u3002\u6c5f\u5357\u5fc3\u60f3\uff1a\u201c\u5018\u82e5\u662f\u6362\u4e86\u4e00\u5339\u5bfb\u5e38 \u7684\u5750\u9a91\uff0c\u5f53\u771f\u662f\u8981\u8d70\u8def\u7684\u7b49\u9a91\u9a6c\u7684\u4e86\u3002\u201d \u3000\u3000\u8d70\u5230\u5929\u9ed1\uff0c\u4e00\u7b97\u8def\u7a0b\uff0c\u6574\u6574\u8dd1\u4e86\u516b\u767e\u91cc\u6709\u591a\u3002\u6700\u540e\u7684\u7ed3\u679c\u662f\u51b0\u5ddd\u5929\u5973\u8dd1\u4e86\u4e2a\u7b2c\u4e00\uff0c \u59ec\u6653\u98ce\u5403\u4e8f\u5728\u5185\u529b\u4e0d\u5982\uff0c\u53cd\u800c\u843d\u5728\u5510\u7ecf\u5929\u4e4b\u540e\uff0c\u5f97\u4e86\u4e2a\u7b2c\u4e09\u3002\u6c5f\u6d77\u5929\u5219\u8d76\u4e0a\u4e86\u9648\u5929\u5b87\uff0c \u76f8\u5dee\u4e0d\u8fc7\u51e0\u6b65\u3002\u867d\u7136\u4ecd\u662f\u6700\u540e\uff0c\u4f46\u4eba\u4eba\u90fd\u4e0d\u4f4f\u53e3\u7684\u5938\u8d5e\u4ed6\u3002 \u3000\u3000\u4e0d\u8fc7\u6c5f\u6d77\u5929\u4e3a\u4e86\u5c11\u5e74\u597d\u80dc\uff0c\u8fd9\u4e00\u65e5\u957f\u8dd1\uff0c\u4e5f\u5dee\u4e0d\u591a\u7528\u5c3d\u4e86\u6c14\u529b\uff0c\u75b2\u5026\u4e0d\u582a\u4e86\u3002\u7761\u89c9 \u7684\u65f6\u5019\uff0c\u9aa8\u5934\u90fd\u9690\u9690\u4f5c\u75db\uff0c\u4e00\u89c9\u5c31\u7761\u5230\u4e86\u5927\u5929\u5149\uff0c\u76f4\u5230\u4ed6\u7236\u4eb2\u5524\u4ed6\u624d\u9192\u3002 \u3000\u3000\u9648\u5929\u5b87\u7b11\u9053\uff1a\u201c\u6628\u5929\u54b1\u4eec\u5df2\u8dd1\u4e86\u516b\u767e\u91cc\u6709\u591a\uff0c\u4eca\u5929\u5c31\u662f\u6162\u4e00\u4e9b\u4e5f\u4e0d\u7d27\u8981\u4e86\u3002\u6c5f\u8d24\u4f84\uff0c \u7b49\u4e0b\u4f60\u548c\u5a76\u5a76\u6362\u4e00\u6362\u5427\uff0c\u4f60\u548c\u4f60\u7239\u7239\u4e00\u540c\u9a91\u9a6c\u8def\u4e0a\u4e5f\u597d\u8bf4\u8bdd\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u517b \u597d\u7cbe\u795e\u4e86\uff0c\u518d\u8dd1\u4e0d\u6015\u3002\u201d\u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u8bf4\u4f60\u6015\u8dd1\u8def\uff0c\u4f60\u8be5\u4e3a\u4f60\u7684\u9648\u4f2f\u4f2f\u8bbe\u60f3\uff0c \u4ed6\u548c\u4f60\u7684\u5a76\u5a76\u5206\u522b\u591a\u5e74\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u8bdd\u513f\u8981\u8bf4\uff0c\u4e5f\u8be5\u8ba9\u4ed6\u4eec\u5728\u8def\u4e0a\u53d9\u53d9\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973 \u8fd9\u4e48\u4e00\u8bf4\uff0c\u6c5f\u6d77\u5929\u8fd9\u624d\u4f9d\u4ece\u4e86\u3002 \u3000\u3000\u5e7d\u840d\u7684\u8f7b\u529f\u8981\u6bd4\u6c5f\u6d77\u5929\u7565\u900a\u4e00\u7b79\uff0c\u597d\u5728\u4ed6\u4eec\u5934\u4e00\u5929\u591a\u8dd1\u4e86\u8def\uff0c\u4e00\u7b97\u8def\u7a0b\uff0c\u5c31\u662f\u6162\u4e9b\uff0c \u6700\u591a\u7b2c\u56db\u5929\u4e5f\u53ef\u5230\u8fbe\u9a6c\u8428\u513f\u56fd\uff0c\u7528\u4e0d\u4e86\u4e94\u5929\u3002\u56e0\u6b64\u8fd9\u4e00\u5929\u5927\u5bb6\u90fd\u4e0d\u7adf\u8d70\uff0c\u51b0\u5ddd\u5929\u5973\u548c\u59ec \u6653\u98ce\u4e5f\u7a0d\u5fae\u653e\u6162\u4e86\u811a\u8d70\uff0c\u4f7f\u5e7d\u840d\u53ef\u4ee5\u8ddf\u5f97\u4e0a\u4ed6\u4eec\uff0c\u4e00\u8def\u4e0a\u8c08\u8c08\u7b11\u7b11\u3002 \u3000\u3000\u6c5f\u5357\u4e0e\u513f\u5b50\u4e5f\u9694\u522b\u4e86\u51e0\u5e74\uff0c\u89c1\u513f\u5b50\u957f\u5f97\u8fd9\u4e48\u9ad8\u5927\uff0c\u529f\u592b\u53c8\u662f\u8fdc\u80dc\u81ea\u5df1\uff0c\u5fc3\u4e2d\u4ea6\u662f\u6709 \u8bf4\u4e0d\u51fa\u7684\u9ad8\u5174\uff0c\u4e24\u7236\u5b50\u5e76\u9a6c\u800c\u884c\uff0c\u6c5f\u5357\u4e0d\u4f4f\u7684\u5411\u513f\u5b50\u53d1\u95ee\uff0c\u95ee\u4ed6\u8fd9\u51e0\u5e74\u6765\u7ec3\u4e86\u4e9b\u4ec0\u4e48\u6b66 \u529f\uff0c\u4e00\u8def\u4e0a\u78b0\u5230\u4e86\u54ea\u4e9b\u4eba\u3002\u9047\u5230\u4e86\u54ea\u4e9b\u4e8b\uff0c\u5176\u4e2d\u6709\u4e00\u4e9b\u4e8b\u60c5\uff0c\u6c5f\u6d77\u5929\u5728\u6cd5\u738b\u5e2d\u4e0a\u5df2\u7ecf\u8bf4 \u8fc7\u4e86\u7684\uff0c\u4ed6\u4ecd\u662f\u4e0d\u538c\u6c42\u8be6\uff0c\u95ee\u4e86\u518d\u95ee\u3002\u5c24\u5176\u5bf9\u534e\u5929\u98ce\u7236\u5973\u7684\u4e8b\u60c5\uff0c\u540c\u5f97\u66f4\u662f\u4ed4\u7ec6\u3002\u4ed6\u4eec \u4e24\u7236\u5b50\u53ea\u987e\u8c08\u8bdd\uff0c\u4efb\u51ed\u80ef\u4e0b\u7684\u9a8f\u9a6c\u81ea\u884c\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5c31\u4e0e\u540e\u5934\u6b65\u884c\u7684\u4eba\u79bb\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u4e0d\u4e45\uff0c\u8d70\u8fdb\u4e86\u4e00\u6761\u5728\u4e24\u5cf0\u5939\u5cd9\u4e4b\u4e2d\u5f00\u8f9f\u51fa\u6765\u7684\u5c71\u8def\uff0c\u6781\u662f\u5d0e\u5c96\uff0c\u597d\u5728\u8fd9\u4e24\u5339\u5b9d\u9a6c\u767b \u5c71\u6d89\u9669\uff0c\u5982\u5c65\u5e73\u5730\uff0c\u4e5f\u4e0d\u89c9\u5f97\u600e\u6837\u96be\u884c\uff0c\u6b63\u884c\u8d70\u95f4\uff0c\u5ffd\u542c\u5f97\u4e24\u58f0\u91ce\u517d\u66f2\u543c\u58f0\uff0c\u53ea\u89c1\u6709\u4e24 \u53ea\u91d1\u5149\u95ea\u95ea\u7684\u602a\u517d\u5982\u98de\u5954\u6765\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u7239\u7239\u7559\u795e\uff0c\u662f\u91d1\u6bdb\u72e1\u6765\u4e86\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u91d1\u6bdb\u72e1 \u53c8\u662f\u4e00\u58f0\u5927\u543c\uff0c\u90a3\u4e24\u5339\u9a8c\u9a6c\u5413\u5f97\u8df3\u4e86\u8d77\u6765\uff0c\u6c5f\u5357\u7236\u5b50\u8fde\u5fd9\u4e0b\u9a6c\uff0c\u90a3\u4e24\u5339\u9a6c\u81ea\u987e\u9003\u547d\uff0c\u56de \u5934\u4fbf\u8dd1\u3002 \u3000\u3000\u91d1\u6bdb\u72e1\u5176\u884c\u5982\u98ce\uff0c\u6bd4\u5343\u91cc\u9a6c\u66f4\u5feb\u5f97\u591a\uff0c\u8f6c\u773c\u4fbf\u5df2\u6765\u5230\uff0c\u53ea\u89c1\u4e24\u53ea\u91d1\u6bdb\u72e1\u7684\u80cc\u4e0a\u90fd\u6709 \u4eba\u9a91\u7740\uff0c\u4e00\u7537\u4e00\u5973\uff0c\u5927\u7684\u6b63\u662f\u5929\u9b54\u6559\u4e3b\uff0c\u7537\u7684\u5219\u662f\u90a3\u957f\u53d1\u5782\u80a9\u7684\u5c11\u5e74\u5389\u590d\u751f\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u8dc3\u4e0b\u4e86\u91d1\u6bdb\u72e1\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u54e5\u513f\uff0c\u8fd8\u8ba4\u5f97\u6211\u5417\uff1f\u51e0\u5e74\u4e0d\u89c1\uff0c\u4f60\u957f\u5f97\u8fd9\u4e48\u9ad8 \u4e86\u3002\u201d\u6c5f\u6d77\u5929\u5c0f\u65f6\u5019\u88ab\u5979\u6d3e\u4f8d\u5973\u5f3a\u884c\u63b3\u53bb\uff0c\u592b\u5728\u9b54\u7a9f\u91cc\u4e09\u4e2a\u591a\u6708\uff0c\u672c\u662f\u6709\u4ec7\uff0c\u4f46\u5ff5\u5728\u5979 \u5bf9\u81ea\u5df1\u8fd8\u7b97\u4e0d\u9519\uff0c\u4e0d\u613f\u4e0e\u5979\u751f\u4e8b\uff0c\u4fbf\u56de\u4e86\u4e00\u793c\uff1a\u8bf4\u9053\uff1a\u201c\u6559\u4e3b\uff0c\u4f60\u597d\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u4e8f\u4f60\u8fd8\u8bb0\u5f97\u6211\uff0c\u4e0a\u6b21\u6211\u53eb\u4ed6\u8bf7\u4f60\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u6765\uff1f\u201d\u6c5f \u6d77\u5929\u9053\uff1a\u201c\u6211\u6709\u6211\u7684\u4e8b\u60c5\uff0c\u65e0\u6687\u53bb\u62dc\u89c1\u6559\u4e3b\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u8fc7\u53bb\u7684\u5c31\u7b97\u4e86\uff0c\u6211\u4e5f\u4e0d\u602a\u4f60\uff0c\u73b0\u5728\u4f60\u968f\u6211\u8d70\u5427\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u73b0 \u5728\u6211\u4e5f\u6709\u8981\u4e8b\u5728\u8eab\u3002\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u7d27\u8981\u7684\u4e8b\uff1f\u201d\u6c5f\u6d77\u5929\u52a8\u4e86\u6c14\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u4f60\u5c11\u7ba1\u95f2\u4e8b\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u4fa7\u76ee\u659c\u7768\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5728\u5411\u6211\u53d1\u813e\u6c14\u4e48\uff1f\u4f60\u7109\u77e5\u6211\u662f\u5728\u7ba1\u95f2\u4e8b\uff0c\u4e5f\u8bb8\u6b63 \u662f\u4e3a\u4e86\u4f60\u597d\u5462\u3002\u6211\u95ee\u4f60\uff0c\u4f60\u662f\u4e0d\u662f\u5728\u8d74\u91d1\u9e70\u5bab\u4e4b\u4f1a\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u662f\u53c8\u600e\u6837\uff1f\u201d\u5929\u9b54 \u6559\u4e3b\u9053\uff1a\u201c\u90a3\u5c31\u6b63\u597d\u4e00\u8def\u4e86\u3002\u6211\u5e26\u4f60\u8d70\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u81ea\u5df1\u4f1a\u8d70\uff01\u5404\u8d70\u5404\u7684\uff0c\u95ee\u5fc5 \u4f60\u5e26\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u81ea\u5df1\u4f1a\u8d70\uff0c\u4f46\u4f60\u81ea\u5df1\u524d\u5f80\uff0c\u5374\u672a\u5fc5\u627e\u5f97\u5230\u4f60\u6240\u8981\u6211\u7684\u4eba\u3002\u201d \u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u4f60\u77e5\u9053\u6211\u8981\u627e\u8c01\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff0c\u201c\u4f60\u7b2c\u4e00\u4e2a\u8981\u627e\u7684\u662f\u4f60\u7684\u5e08 \u7236\u91d1\u4e16\u9057\uff1b\u7b2c\u4e8c\u4e2a\u8981\u627e\u7684\u662f\u8c37\u4e4b\u534e\u7684\u517b\u5973\u8c37\u4e2d\u83b2\u3002\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ed6 \u4eec\u6240\u5728\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u77e5\u9053\uff0c\u4f46\u4e0d\u80fd\u544a\u8bc9\u4f60\uff0c\u4f60\u8981\u89c1\u8fd9\u4e24\u4e2a\u4eba\uff0c\u5c31\u8ddf\u6211 \u6765\u3002\u201d \u3000\u3000\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u6d77\u513f\uff0c\u522b\u4e0a\u5979\u7684\u5f53\u3002\u91d1\u5927\u4fa0\u82e5\u5728\u524d\u9762\uff0c\u81ea\u4f1a\u6765\u89c1\u54b1\u4eec\u3002\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a \u201c\u8fd9\u5374\u4e0d\u89c1\u5f97\u3002\u201d\u6c5f\u5357\u5fc3\u91cc\u4e5f\u6709\u4e86\u6c14\uff0c\u5411\u5929\u9b54\u6559\u4e3b\u6253\u606d\u4f5c\u63d6\u9053\uff1a\u201c\u6211\u7684\u597d\u59d1\u5976\u5976\uff0c\u54b1\u4fe9 \u7236\u5b50\u4e0e\u4f60\u5f80\u65e5\u65e0\u51a4\uff0c\u8fd1\u65e5\u65e0\u4ec7\uff0c\u4f60\u600e\u7684\u4e24\u6b21\u4e09\u756a\u8001\u662f\u5411\u6211\u4eec\u627e\u9ebb\u70e6\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4f60\u662f\u72d7\u54ac\u5415\u6d1e\u5bbe\uff0c\u4e0d\u8bc6\u597d\u4eba\u5fc3\uff0c\u59d1\u4e14\u5ff5\u5728\u4f60\u7684\u5634\u4e56\uff0c\u4e5f\u770b\u5728\u4f60\u7684\u513f \u5b50\u7684\u4efd\u4e0a\uff0c\u514d\u4e86\u4f60\u7684\u6253\u5427\u3002\u201d\u63a5\u7740\u53c8\u56de\u8fc7\u5934\u5411\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u5c42\uff0c\u4f60\u81ea\u5df1\u4e00\u4e2a\u4eba\u8d70 \u5371\u9669\u5f97\u5f88\u554a\uff0c\u8fd8\u662f\u8ba9\u6211\u628a\u4f60\u5e26\u8d70\u8981\u597d\u5f97\u591a\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u607c\u5979\u51fa\u8a00\u4e0d\u900a\uff0c\u4fae\u8fb1\u81ea\u5df1\u7684\u7236\u4eb2\uff0c\u7981\u4e0d\u4f4f\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u8d70\u5f00\uff0c\u4f60\u518d\u4e0d \u8d70\u5f00\uff0c\u6211\u53ef\u8981\u4e0d\u5ba2\u6c14\u5566\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u5386\u5c3d\u8270\u96be\u6765\u8d74\u4f1a\uff0c\u9014\u4e2d\u5ffd\u53c8\u8d77\u98ce\u6ce2\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e09\u5341\u56de\u3000\u4e0d\u610f\u6843\u6e90\u9022\u7389\u5973\u3000\u8c01\u77e5\u738b\u5b50\u662f\u5978\u56fe \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e09\u5341\u56de\u3000\u4e0d\u610f\u6843\u6e90\u9022\u7389\u5973\u3000\u8c01\u77e5\u738b\u5b50\u662f\u5978\u56fe \u3000\u3000\u5929\u9b54\u6559\u4e3b\u6014\u4e86\u4e00\u6014\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u73b0\u5728\u662f\u7fbd\u6bdb\u4e30\u6ee1\uff0c\u81ea\u5df1\u4f1a\u98de\u5566\uff01\u597d\uff0c\u6211\u5012\u8981\u770b \u4f60\u600e\u4e48\u6837\u5bf9\u6211\u4e0d\u5ba2\u6c14\u3002\u201d\u8eab\u5f62\u4e00\u6643\uff0c\u500f\u7136\u95f4\u8863\u8896\u62c2\u5230\u4e86\u6c5f\u6d77\u5929\u7684\u9762\u95e8\u3002 \u3000\u3000\u5979\u8fd9\u4e00\u62db\u53e4\u602a\u4e4b\u6781\uff0c\u53cc\u624b\u7b3c\u5728\u8863\u8896\u4e4b\u4e2d\uff0c\u8863\u8896\u672a\u892a\uff0c\u62db\u6570\u5df2\u53d1\u3002\u5bf9\u65b9\u6839\u672c\u4e0d\u77e5\u9053\u5979 \u8981\u653b\u51fb\u54ea\u4e2a\u65b9\u4f4d\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u867d\u7136\u65e9\u6709\u9632\u5907\uff0c\u7a76\u7adf\u7ecf\u9a8c\u65e0\u591a\uff0c\u800c\u4e14\u4ed6\u53c8\u662f\u672c\u5c31\u65e0\u610f\u4f24\u5bb3\u5929\u9b54\u6559\u4e3b\uff0c\u53ea\u51c6\u5907 \u5979\u4e00\u51fa\u624b\uff0c\u5c31\u7834\u4e86\u5979\u7684\u62db\u6570\uff0c\u4ee4\u5979\u77e5\u96be\u800c\u9000\u7684\u3002\u54ea\u77e5\u5929\u9b54\u6559\u4e3b\u7a81\u5982\u5176\u6765\uff0c\u7528\u4e0a\u4e86\u8fd9\u6837\u53e4 \u602a\u7684\u6253\u6cd5\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u65b9\u81ea\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8fd8\u672a\u60f3\u597d\u5982\u4f55\u5e94\u4ed8\uff0c\u5929\u9b54\u6559\u4e3b\u5df2\u662f\u51fa\u624b\u5982\u7535\uff0c\u500f\u5730\u5c31\u6293\u4f4f\u4e86 \u4ed6\u80a9\u4e0a\u7684\u7435\u7436\u9aa8\uff0c\u7b11\u9053\uff1a\u201c\u4e56\u4e56\u7684\u8ddf\u6211\u8d70\u5427\uff01\u201d \u3000\u3000\u7435\u7436\u9aa8\u4e43\u662f\u529f\u592b\u6700\u96be\u7ec3\u5230\u7684\u5730\u65b9\uff0c\u6b66\u529f\u591a\u597d\u7684\u4eba\uff0c\u4e00\u65e6\u7ed9\u4eba\u6293\u7740\u4e86\u7435\u7436\u9aa8\uff0c\u4e5f\u662f\u4e0d \u80fd\u52a8\u5f39\uff0c\u53ea\u80fd\u4efb\u4eba\u5bb0\u5272\uff0c\u6240\u4ee5\u5929\u9b54\u6559\u4e3b\u624d\u8fd9\u6837\u6d0b\u5f97\u610f\u3002 \u3000\u3000\u54ea\u77e5\u6c5f\u6d77\u5929\u6240\u7ec3\u7684\u5185\u529f\u4e0e\u4f17\u4e0d\u540c\uff0c\u5929\u9b54\u6559\u4e3b\u634f\u7740\u4ed6\u7684\u7435\u7436\u9aa8\uff0c\u53ea\u89c9\u5982\u540c\u634f\u7740\u4e00\u56e2\u68c9 \u82b1\u4e00\u822c\uff0c\u6c5f\u6d77\u5929\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u5f53\u6211\u4f5c\u5c0f\u5b69\u5b50\u6b3a\u4fae\u4e48\uff1f\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u62a4\u4f53\u795e\u529f\u5df1\u662f\u751f\u51fa \u53cd\u5e94\uff0c\u4e00\u56e2\u68c9\u82b1\u500f\u7136\u95f4\u53d8\u6210\u4e86\u4e00\u5757\u94a2\u94c1\uff0c\u5929\u9b54\u6559\u4e3b\u7684\u6307\u5934\u90fd\u51e0\u4e4e\u7ed9\u78b0\u5f97\u6298\u65ad\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6c5f\u6d77\u5929\u5df2\u662f\u53cd\u624b\u62cd\u51fa\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5929\u9b54\u6559\u4e3b\u7684\u8863\u8896\u53cd \u800c\u7ed9\u4ed6\u6495\u7834\u4e86\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u7684\u6b66\u529f\u5df2\u662f\u7ec3\u5f97\u51fa\u7c7b\u62d4\u8403\u4e86\uff0c\u53ef\u559c\uff0c\u53ef\u8d3a\uff01\u53ea\u662f\u8981\u5728\u6211\u7684 \u9762\u524d\u901e\u5f3a\uff0c\u90a3\u8fd8\u4e0d\u591f\uff01\u201d \u3000\u3000\u5c31\u5728\u8fd9\u51e0\u53e5\u8bdd\u5f53\u4e2d\uff0c\u5929\u9b54\u6559\u4e3b\u5df2\u63a5\u8fde\u5411\u6c5f\u6d77\u5929\u653b\u51fa\u4e86\u516b\u62db\uff0c\u6bcf\u4e00\u62db\u6c5f\u6d77\u5929\u90fd\u662f\u8bc6\u5f97 \u7684\uff0c\u4f46\u7531\u4e8e\u5979\u51fa\u624b\u592a\u5feb\uff0c\u53d8\u62db\u6781\u901f\uff0c\u6c5f\u6d77\u5929\u7adf\u7136\u7ed9\u5979\u8feb\u5f97\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5927\u6012\u9053\uff1a\u201c\u6211\u5f53\u771f\u8981\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d\u53cc\u624b\u5408\u62b1\uff0c\u5212\u4e86\u4e00\u9053\u5706\u5708\uff0c\u8fd9\u4e00\u62db\u540d\u4e3a \u201c\u987b\u5f25\u516d\u5408\u201d\uff0c\u4e43\u662f\u987b\u5f25\u638c\u6cd5\u4e2d\u7684\u7cbe\u534e\u6240\u5728\uff0c\u5929\u9b54\u6559\u4e3b\u7684\u95ea\u7535\u624b\u6cd5\u7adf\u662f\u653b\u4e0d\u8fdb\u8fd9\u4e2a\u5706\u5708\u3002 \u800c\u4e14\u8fd8\u611f\u5230\u4e00\u80a1\u975e\u5e38\u5f3a\u70c8\u7684\u529b\u9053\uff0c\u4ece\u5706\u5708\u4e2d\u53d1\u4e86\u51fa\u6765\uff0c\u5411\u81ea\u5df1\u51b2\u51fb\uff0c\u6559\u81ea\u5df1\u4e0d\u80fd\u5411\u524d\u79fb \u52a8\u3002\u8fd9\u6709\u5f62\u7684\u5706\u5708\uff0c\u5374\u4f3c\u7b51\u8d77\u4e86\u65e0\u5f62\u7684\u94c1\u58c1\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u7684\u5185\u529f\u4e5f\u4e0d\u9519\u4e86\uff0c\u90a3\u54b1\u4eec\u5c31\u518d\u6bd4\u4e00\u6bd4\u5185\u529f\u5427\u3002\u201d\u4f9d\u4f6f\u5212\u846b\u82a6 \u7684\u4e5f\u5212\u4e86\u4e00\u9053\u5706\u5708\uff0c\u6c5f\u6d77\u592d\u53ea\u89c9\u81ea\u5df1\u653b\u51fa\u53bb\u7684\u529b\u9053\u53d7\u963b\uff0c\u4f46\u5374\u5e76\u4e0d\u611f\u5230\u5bf9\u65b9\u5185\u529b\u7684\u5a01\u80c1\uff0c \u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f60\u81f3\u591a\u53ea\u662f\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u5374\u65e0\u8fd8\u624b\u4e4b\u529b\uff0c\u6211\u4f55\u987b\u60e7\u4f60\u3002\u201d\u54ea\u77e5\u4e0d\u8fc7\u7247\u523b\uff0c \u4ed6\u5ffd\u5730\u6709\u61d2\u6d0b\u6d0b\u7684\u611f\u89c9\uff0c\u597d\u50cf\u60f3\u53bb\u7761\u89c9\u4e00\u822c\uff0c\u529b\u4e0d\u4ece\u5fc3\uff0c\u60f3\u53d1\u51fa\u5341\u6210\u529b\u9053\uff0c\u81f3\u591a\u53ea\u80fd\u53d1 \u51fa\u4e03\u6210\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u4e2d\u4e00\u51db\uff0c\u6697\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u8fd9\u5929\u9b54\u6559\u4e3b\u5584\u4e8e\u4f7f\u6bd2\uff0c\u83ab\u8981\u7740\u4e86\u5979\u7684\u9053\u513f\u3002\u201d\u5f53 \u4e0b\u7528\u51fa\u95ed\u606f\u6362\u6c14\u7684\u529f\u592b\uff0c\u8fd9\u662f\u4e00\u79cd\u7279\u6b8a\u7684\u5410\u7eb3\u65b9\u6cd5\uff0c\u53ef\u4ee5\u5728\u4e00\u6bb5\u65f6\u95f4\u4e4b\u5185\uff0c\u4e0d\u5fc5\u5438\u8fdb\u5916 \u95f4\u7684\u7a7a\u6c14\uff0c\u53ea\u51ed\u4f53\u5185\u7684\u771f\u6c14\u81ea\u7136\u6d41\u8f6c\u3002\u5370\u5ea6\u6709\u4e00\u79cd\u9b54\u672f\u5e08\uff0c\u53ef\u4ee5\u8eba\u5728\u94dc\u68fa\u4e4b\u5185\uff0c\u4e25\u5bc6\u5c01 \u95ed\uff0c\u8ba9\u4eba\u6c89\u5165\u6d77\u4e2d\uff0c\u8fc7\u4e86\u51e0\u4e2a\u65f6\u8fb0\uff0c\u518d\u635e\u8d77\u6765\uff0c\u4ecd\u7136\u53ef\u4ee5\u751f\u5b58\uff0c\u5c31\u662f\u61c2\u5f97\u4f7f\u7528\u8fd9\u79cd\u95ed\u606f \u6362\u6c14\u7684\u529f\u592b\u3002\u4f46\u8fd9\u5fc5\u987b\u957f\u671f\u7ec3\u4e60\uff0c\u4e60\u60ef\u4e86\u624d\u80fd\u6301\u4e45\u3002\u6c5f\u6d77\u5929\u672a\u66fe\u4e60\u60ef\uff0c\u53ea\u80fd\u652f\u6301\u534a\u70b7\u9999 \u7684\u65f6\u523b\uff0c\u8fc7\u4e86\u8fd9\u4e2a\u65f6\u523b\uff0c\u4ecd\u7136\u8981\u6df1\u6df1\u547c\u5438\u3002 \u3000\u3000\u4f46\u4ed6\u53ea\u8981\u95ed\u4e86\u547c\u5438\uff0c\u867d\u7136\u4e0d\u80fd\u6301\u4e45\uff0c\u60c5\u5f62\u5df2\u662f\u597d\u8f6c\u5f97\u591a\u3002\u539f\u6765\u5929\u9b54\u6559\u4e3b\u7684\u6307\u7532\u5185\u85cf \u6709\u4e00\u79cd\u79d8\u5236\u7684\u8ff7\u9b42\u7c89\uff0c\u5f39\u6307\u53d1\u51fa\uff0c\u968f\u7740\u638c\u98ce\u5439\u53bb\uff0c\u5316\u4e3a\u6c24\u6c32\u4e4b\u6c14\uff0c\u4fbf\u80fd\u4f24\u4eba\u4e8e\u4e0d\u77e5\u4e0d\u89c9 \u4e4b\u4e2d\u3002\u73b0\u5728\u6c5f\u6d77\u5929\u7528\u4e86\u95ed\u606f\u6362\u6c14\u7684\u529f\u592b\uff0c\u51cf\u5c11\u4e86\u547c\u5438\uff0c\u6240\u53d7\u7684\u4f24\u5bb3\u4e5f\u5c31\u968f\u4e4b\u51cf\u8f7b\u4e86\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u8fde\u53d1\u4e09\u638c\uff0c\u90fd\u7ed9\u6c5f\u6d77\u5929\u6321\u5f00\uff0c\u5927\u4e3a\u60ca\u8be7\uff0c\u4e0d\u77e5\u4ed6\u7684\u5185\u529b\u4f55\u4ee5\u53c8\u7a81\u7136\u589e\u5f3a\uff0c \u5f53\u4e0b\u4e5f\u5c31\u4e0d\u6562\u5f3a\u53d6\u653b\u52bf\uff0c\u4e24\u4eba\u4e00\u6765\u4e00\u5f80\uff0c\u53c8\u6210\u4e86\u76f8\u6301\u4e4b\u5c40\u3002 \u3000\u3000\u6c5f\u5357\u63d2\u4e0d\u8fdb\u624b\uff0c\u5374\u5728\u4e00\u65c1\u7ed9\u513f\u5b50\u5450\u558a\u52a9\u5a01\uff0c\u5929\u9b54\u6559\u4e3b\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u590d\u751f\uff0c\u4f60\u628a\u4ed6\u7684 \u8001\u5b50\u4e5f\u6349\u4e86\uff0c\u6349\u4e86\u8001\u5b50\uff0c\u518d\u6349\u513f\u5b50\uff01\u201d \u3000\u3000\u6c5f\u5357\u5927\u53eb\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u8001\u5b50\u4e5f\u6349\u5f97\u7684\u5417\uff1f\u201d\u5389\u590d\u751f\u6014\u4e86\u4e00\u6014\uff0c\u65b9\u59cb\u7701\u609f\u4ed6\u662f\u9a82 \u4eba\uff0c\u6012\u9053\uff1a\u201c\u4f60\u624d\u662f\u5c82\u6709\u6b64\u7406\uff0c\u80c6\u6562\u8ba8\u6211\u7684\u4fbf\u5b9c\u3002\u201d \u3000\u3000\u6c5f\u5357\u89c1\u4ed6\u621f\u6307\u800c\u9a82\uff0c\u5fc3\u4e2d\u5927\u4e50\uff0c\u6b63\u60f3\u518d\u548c\u4ed6\u5f00\u51e0\u53e5\u73a9\u7b11\uff0c\u5ffd\u89c9\u4e00\u80a1\u65e0\u5f62\u7684\u7f61\u6c14\u5f84\u88ad \u8fc7\u6765\uff0c\u76f4\u53d6\u4ed6\u80c1\u4e0b\u7684\u201c\u6108\u6c14\u7a74\u201d\uff0c\u9ebb\u75d2\u75d2\u7684\u597d\u4e0d\u96be\u53d7\u3002\u539f\u6765\u5389\u590d\u751f\u4e5f\u6709\u9694\u7a7a\u70b9\u7a74\u7684\u672c\u9886\uff0c \u4e0d\u8fc7\u5c1a\u672a\u7ec3\u5230\u4e0a\u4e58\u5883\u754c\uff0c\u53ea\u80fd\u5728\u4e09\u4e08\u4e4b\u5185\u4f24\u4eba\u3002 \u3000\u3000\u6c5f\u5357\u5c16\u53eb\u4e00\u58f0\uff1a\u201c\u535c\u901a\u201d\u4fbf\u5012\uff0c\u5389\u590d\u751f\u51b7\u7b11\u9053\uff1a\u201c\u770b\u4f60\u8fd8\u9a82\u4e0d\u9a82\u4eba\uff1f\u201d\u4e00\u8dc3\u8fc7\u6765\uff0c \u4f38\u624b\u4fbf\u6293\uff0c\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u4f60\u5f53\u771f\u8981\u6349\u8001\u5b50\u5417\uff1f\u201d\u8d34\u4ed6\u4e00\u4e2a\u76d8\u65cb\uff0c\u7a81\u7136\u7eb5\u8eab\u98de\u817f\uff0c\u8e22\u5389\u590d \u751f\u819d\u76d6\u4e0b\u4e09\u5bf8\u7684\u201c\u767d\u5e02\u7a74\u201d\u3002\u5389\u590d\u751f\u4e0d\u77e5\u9053\u6c5f\u5357\u6709\u98a0\u5012\u7a74\u9053\u7684\u529f\u592b\uff0c\u9669\u9669\u7ed9\u4ed6\u8e22\u4e2d\u3002 \u3000\u3000\u4f46\u5389\u590d\u751f\u7684\u6b66\u529f\u7a76\u7adf\u6bd4\u6c5f\u5357\u9ad8\u51fa\u8bb8\u591a\uff0c\u53cd\u624b\u4e00\u524a\uff0c\u7acb\u5373\u628a\u6c5f\u5357\u7684\u8fde\u73af\u9e33\u9e2f\u817f\u7834\u4e86\uff0c \u8ddf\u7740\u53c8\u4ee5\u5927\u64d2\u62ff\u624b\u6cd5\u5939\u7740\u5c0f\u5929\u661f\u638c\u529b\uff0c\u5411\u6c5f\u5357\u75be\u653b\u3002 \u3000\u3000\u5389\u590d\u751f\u773c\u770b\u5c31\u8981\u6293\u7740\u6c5f\u5357\uff0c\u6c5f\u5357\u5ffd\u5730\u4e00\u4e2a\u7b4b\u6597\u7ffb\u4e86\u51fa\u53bb\uff0c\u5389\u590d\u751f\u6012\u9053\uff1a\u201c\u8fd8\u8981\u8dd1\u5417\uff1f\u201d \u98de\u8eab\u8ffd\u4e0a\uff0c\u8fde\u5288\u4e09\u638c\uff0c\u6c5f\u5357\u4e5f\u63a5\u8fde\u7ffb\u4e86\u4e09\u4e2a\u7b4b\u6597\uff0c\u6bcf\u4e00\u6b21\u90fd\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\u907f\u5f00\uff0c\u8fd9\u79cd \u53e4\u602a\u7684\u8eab\u6cd5\u662f\u91d1\u4e16\u9057\u6559\u4ed6\u7684\uff0c\u7528\u6765\u9003\u547d\uff0c\u90a3\u662f\u6700\u5999\u4e0d\u8fc7\u3002 \u3000\u3000\u53ea\u662f\u8fd9\u6837\u7684\u592a\u7ffb\u7b4b\u6597\uff0c\u6bd5\u7adf\u662f\u5927\u8017\u6c14\u529b\uff0c\u5389\u590d\u751f\u5982\u5f71\u968f\u5f62\uff0c\u4e00\u6b65\u4e5f\u4e0d\u653e\u677e\uff0c\u4e0d\u6d88\u7247 \u523b\uff0c\u6c5f\u5357\u5df2\u662f\u9669\u8c61\u73af\u751f\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u672c\u6765\u5c31\u5728\u62c5\u5fc3\u5bf9\u65b9\u4e0d\u80af\u653e\u8fc7\u4ed6\u7684\u7236\u4eb2\uff0c\u679c\u7136\u6240\u5fe7\u8651\u7684\u7adf\u6210\u4e86\u4e8b\u5b9e\uff0c\u8fd9\u65f6\uff0c\u4ed6 \u89c1\u7236\u4eb2\u9047\u9669\uff0c\u54ea\u91cc\u8fd8\u80fd\u591f\u4e13\u5fc3\u5bf9\u654c\uff1f\u53ef\u662f\u5929\u9b54\u6559\u4e3b\u7f20\u5f97\u6781\u7d27\uff0c\u6c5f\u6d77\u5929\u51b2\u51fb\u4e86\u4e94\u516d\u6b21\uff0c\u90fd \u7ed9\u5929\u9b54\u6559\u4e3b\u6321\u4f4f\u4e00\u7adf\u7136\u51b2\u4e0d\u8fc7\u53bb\u63f4\u6551\u4ed6\u7684\u7236\u4eb2\u3002 \u3000\u3000\u9ad8\u624b\u6bd4\u62fc\uff0c\u54ea\u5bb9\u5f97\u7a0d\u7a0d\u5206\u5fc3\uff1f\u5c24\u5176\u6c5f\u6d77\u5929\u8fd8\u4e0d\u4e60\u60ef\u4e8e\u95ed\u606f\u6362\u6c14\uff0c\u8fd9\u65f6\u5fc3\u795e\u4e00\u4e71\uff0c\u518d \u52a0\u4ee5\u731b\u529b\u51b2\u51fb\uff0c\u547c\u5438\u96be\u514d\u7d27\u5f20\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u7adf\u662f\u4e0d\u7531\u5f97\u4ed6\u4e0d\u5f20\u53e3\u547c\u5438\uff0c\u767b\u65f6\u53c8\u5438\u8fdb\u4e86\u4e24 \u53e3\u6bd2\u6c14\u3002 \u3000\u3000\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u6d77\u513f\uff0c\u4f60\u5feb\u8dd1\u56de\u53bb\u50ac\u5510\u5927\u4fa0\u5feb\u6765\uff01\u201d\u4ed6\u5374\u4e0d\u60f3\u60f3\uff0c\u6c5f\u6d77\u5929\u8981\u662f\u80fd\u591f\u8dd1 \u5f00\uff0c\u8fd8\u4e0d\u5148\u6765\u6551\u4ed6\uff1f\u4ed6\u8fd9\u4e48\u4e00\u53eb\uff0c\u6c5f\u6d77\u5929\u66f4\u662f\u60ca\u614c\uff0c\u4ed6\u8dd1\u4e0d\u5f00\uff0c\u53ea\u597d\u53d1\u58f0\u957f\u5578\uff0c\u5438\u8fdb\u7684 \u6bd2\u6c14\u5c31\u66f4\u591a\u4e86\u3002 \u3000\u3000\u6c5f\u5357\u63a5\u8fde\u7ffb\u4e86\u51e0\u5341\u4e2a\u7b4b\u6597\u3001\u6e10\u89c9\u6c14\u529b\u4e0d\u652f\uff0c\u773c\u770b\u5c31\u8981\u7ed9\u5389\u590d\u751f\u6349\u7740\uff0c\u5ffd\u542c\u5f97\u59ec\u6653\u98ce \u7684\u58f0\u97f3\u53eb\u76f4\uff1a\u201c\u8d24\u5f1f\u522b\u614c\uff0c\u8001\u54e5\u54e5\u6765\u4e86\uff01\u201d\u53ea\u89c1\u59ec\u6653\u98ce\u65cb\u98ce\u7684\u75be\u8dd1\u8fc7\u6765\uff0c\u7d27\u7d27\u8ddf\u5728\u540e\u9762 \u662f\u5510\u7ecf\u5929\u592b\u5987\u3002 \u3000\u3000\u59ec\u6653\u98ce\u6df1\u77e5\u6c5f\u6d77\u5929\u672c\u9886\u9ad8\u5f3a\u8c03\uff0c\u5fc3\u795e\u53ea\u662f\u653e\u5728\u6c5f\u5357\u8eab\u4e0a\uff0c\u4ed6\u6700\u5148\u8d76\u5230\uff0c\u7acb\u5373\u4fbf\u4e0a\u53bb \u76f8\u52a9\u6c5f\u5357\u3002\u5389\u590d\u751f\u4e00\u638c\u62cd\u51fa\uff0c\u5c06\u59ec\u6653\u98ce\u9707\u5f00\u4e09\u6b65\uff0c\u4e00\u8f6c\u8eab\uff0c\u53c8\u8981\u53bb\u6293\u6c5f\u5357\u3002 \u3000\u3000\u5510\u7ecf\u5929\u53eb\u9053\uff1a\u201c\u59ec\u5927\u54e5\u9000\u4e0b\uff01\u201d\u55d6\u7684\u4e00\u58f0\uff0c\u5929\u5c71\u795e\u8292\u7535\u5c04\u800c\u51fa\uff01 \u3000\u3000\u5389\u590d\u751f\u53eb\u9053\uff1a\u201c\u597d\u5bb6\u4f19\uff0c\u8fd9\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u62d4\u51fa\u7389\u5c3a\u4e00\u6321\uff0c\u53ea\u542c\u5f97\u58f0\u5982\u65ad\u91d1\u788e\u7389\uff0c \u5929\u5c71\u795e\u8292\u867d\u7136\u7ed9\u4ed6\u6253\u843d\uff0c\u4ed6\u7684\u7389\u5c3a\u4e5f\u635f\u4e86\u4e00\u4e2a\u7f3a\u53e3\u3002\u8fd9\u662f\u4ed6\u7684\u62a4\u8eab\u5b9d\u7269\uff0c\u4e0d\u7531\u5f97\u5927\u4e3a\u5fc3 \u75db\u3002 \u3000\u3000\u5929\u5c71\u795e\u8292\u4e43\u662f\u5a01\u529b\u6781\u5f3a\u3001\u65e0\u575a\u4e0d\u6467\u7684\u6697\u5668\uff0c\u5510\u7ecf\u5929\u89c1\u8fd9\u4e2a\u5c11\u5e74\u5c45\u7136\u80fd\u628a\u4ed6\u7684\u795e\u8292\u6253 \u843d\uff0c\u4e5f\u4e0d\u7531\u5f97\u5927\u4e3a\u60ca\u5f02\uff0c\u4ed6\u5374\u4e0d\u77e5\u5389\u590d\u751f\u8fd9\u628a\u7389\u5c3a\uff0c\u4e43\u662f\u4e54\u5317\u6e9f\u5f53\u5e74\u91c7\u7528\u6d77\u5e95\u5bd2\u7389\u6240\u70bc \u7684\u3002 \u3000\u3000\u5510\u7ecf\u5929\u9053\uff1a\u201c\u597d\uff0c\u4f60\u518d\u63a5\u6211\u4e00\u5251\uff01\u201d\u58f0\u5230\u4eba\u5230\uff0c\u6e38\u9f99\u5251\u5316\u6210\u4e86\u4e00\u9053\u5bd2\u5149\uff0c\u5411\u5389\u590d\u751f \u62e6\u8170\u524a\u53bb\uff0c\u5389\u590d\u751f\u4e3e\u5c3a\u4e00\u8fce\uff0c\u53ea\u542c\u5f97\u53c8\u662f\u4e00\u9635\u65ad\u91d1\u788e\u7389\u4e4b\u58f0\uff0c\u8fd9\u56de\u6e38\u9f99\u5251\u548c\u5bd2\u7389\u5c3a\u90fd\u635f \u4e86\u4e00\u4e2a\u5c0f\u5c0f\u7684\u7f3a\u53e3\u3002\u5510\u7ecf\u5929\u60f3\u4e0d\u5230\u5bf9\u65b9\u7684\u7389\u5c3a\u6c89\u91cd\u5f97\u51fa\u5947\uff0c\u864e\u53e3\u7adf\u7ed9\u9707\u5f97\u9690\u9690\u4f5c\u75db\uff0c\u5f53 \u4e0b\u7cbe\u795e\u9661\u957f\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u6211\u5012\u8981\u770b\u770b\u4f60\u80fd\u63a5\u5f97\u6211\u51e0\u62db\uff1f\u201d\u5c55\u5f00\u8ffd\u98ce\u5251\u6cd5\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c \u53d1\u51fa\u4e86\u516d\u516d\u4e09\u5341\u516d\u62db\uff0c\u628a\u5389\u590d\u751f\u6740\u5f97\u624b\u5fd9\u811a\u4e71\uff01 \u3000\u3000\u5510\u7ecf\u5929\u548c\u59ec\u6653\u98ce\u90fd\u53ea\u60f3\u5230\u8981\u63f4\u6551\u6c5f\u5357\uff0c\u51b0\u5ddd\u5929\u5973\u5374\u770b\u51fa\u4e86\u6c5f\u6d77\u5929\u4ea6\u662f\u5904\u5883\u4e0d\u5999\uff0c\u5f53 \u4e0b\u4e00\u626c\u624b\u4fbf\u53d1\u51fa\u4e86\u4e09\u9897\u51b0\u5f39\u3002\u51b0\u5ddd\u5929\u5973\u77e5\u9053\u6c5f\u6d77\u5929\u7684\u5185\u529f\u5df2\u5f97\u91d1\u4e16\u9057\u771f\u4f20\uff0c\u6599\u60f3\u4ed6\u4e0d\u81f3 \u4e8e\u88ab\u51b0\u5f39\u7684\u5bd2\u6c14\u6ce2\u53ca\uff0c\u8fd9\u624d\u653e\u5fc3\u4f7f\u7528\u7684\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u8fd0\u6c14\u4e00\u5439\uff0c\u51b0\u5f39\u672a\u6253\u5230\u5979\u7684\u8eab\u4e0a\u4fbf\u5148\u7206\u88c2\uff0c\u5f62\u6210\u4e86\u4e00\u56fd\u5bd2\u5149\u51b7\u96fe\u3001\u628a\u5979\u548c \u6c5f\u6d77\u5929\u90fd\u7f69\u4f4f\u4e86\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5728\u5bd2\u5149\u51b7\u96fe\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u673a\u4f36\u4f36\u5730\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff0c\u72b9\u81ea\u53ef\u4ee5\u652f\u6301\uff1b\u6c5f\u6d77\u5929\u5374 \u5982\u559d\u9189\u4e86\u9152\u4e00\u822c\uff0c\u7acb\u811a\u4e0d\u5b9a\uff0c\u6447\u6447\u6643\u6643\uff0c\u5934\u4e0a\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u540c\u65f6\u5374\u53c8\u662f\u7259\u5173\u6253\u6218\uff0c \u683c\u683c\u6709\u58f0\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5ffd\u5730\u4e00\u58f0\u957f\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u52a9\u6211\u4e00\u81c2\u4e4b\u529b\uff01\u201d\u4e00\u4f38\u624b\u6293\u7740\u4e86\u6c5f\u6d77\u5929\u7684 \u540e\u9886\uff0c\u5c06\u4ed6\u63d0\u4e86\u8d77\u6765\uff0c\u500f\u5730\u5c31\u51b2\u51fa\u5bd2\u5149\u51b7\u96fe\u3002 \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u5438\u591a\u4e86\u5929\u9b54\u6559\u4e3b\u7684\u8680\u9aa8\u8ff7\u9999\uff0c\u65e9\u5df2\u662f\u5934\u6655\u76ee\u7729\uff0c\u9aa8\u8f6f\u7b4b\u9165\uff0c\u6b63\u81ea\u5168\u529b\u8fd0 \u529f\u652f\u6491\uff0c\u5ffd\u88ab\u5947\u5bd2\u4e4b\u6c14\u4e00\u88ad\uff0c\u4e24\u4e0b\u5939\u653b\uff0c\u767b\u65f6\u6655\u4e86\u8fc7\u53bb\uff0c\u56e0\u800c\u5929\u9b54\u6559\u4e3b\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\uff0c \u5c31\u628a\u4ed6\u64d2\u4f4f\u4e86\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u5feb\u6551\u4eba\u5440\uff01\u201d\u5979\u4e0d\u518d\u53d1\u51b0\u5f39\uff0c\u62d4\u51fa\u51b0\u9b44\u5bd2 \u5149\u5251\u5c31\u8ffd\u4e0a\u4f1a\uff0c\u5929\u9b54\u6559\u4e3b\u628a\u624b\u4e00\u626c\uff0c\u98de\u51fa\u4e00\u6761\u4e94\u8272\u6591\u6593\u7684\u5f69\u5e26\uff0c\u5c06\u51b0\u5ddd\u5929\u5973\u963b\u4e86\u4e00\u963b\uff0c \u968f\u5373\u53d1\u51fa\u5578\u58f0\uff0c\u91d1\u6bdb\u72e1\u5e94\u58f0\u800c\u81f3\u3002 \u3000\u3000\u90a3\u6761\u5f69\u5e26\u540d\u4e3a\u795e\u86c7\u7d22\uff0c\u662f\u4ee5\u6bd2\u86c7\u7684\u53e3\u6d8e\u548c\u836f\u70bc\u6210\u7684\u6bd2\u5e26\uff0c\u8165\u98ce\u6251\u9f3b\uff0c\u96be\u95fb\u4e4b\u6781\uff0c\u6a2a \u5377\u8fc7\u5b8b\uff0c\u51b0\u5ddd\u5929\u5973\u8f7b\u529f\u7edd\u9876\uff0c\u5185\u529f\u53c8\u9ad8\uff0c\u7acb\u5373\u95ea\u5f00\uff0c\u5e76\u5145\u53d7\u4f24\u635f\u3002\u59ec\u6653\u98ce\u5374\u6070\u5de7\u78b0\u4e0a\uff0c \u867d\u7136\u4e5f\u7acb\u5373\u95ea\u5f00\uff0c\u4f46\u88ab\u90a3\u80a1\u8165\u6c14\u4e00\u51b2\uff0c\u5374\u4e0d\u7981\u54c7\u7684\u4e00\u58f0\uff0c\u5c06\u9694\u5bbf\u9152\u996d\u90fd\u5455\u4e86\u51fa\u6765\uff0c\u4e00\u9635 \u660f\u7729\uff0c\u7adf\u7136\u649e\u7740\u4e86\u4e00\u68f5\u5927\u6811\u3002 \u3000\u3000\u5510\u7ecf\u5929\u6b63\u81ea\u6740\u5f97\u5389\u590d\u751f\u624b\u5fd9\u811a\u4e71\uff0c\u5ffd\u542c\u5f97\u59bb\u5b50\u7684\u547c\u558a\uff0c\u4e5f\u662f\u5927\u5403\u4e00\u60ca\u3002\u5389\u590d\u751f\u4e58\u673a \u8131\u8eab\uff0c\u811a\u8e0f\u5929\u7f57\u6b65\u6cd5\uff0c\u4e00\u4e2a\u201c\u4e4b\u201d\u5b57\u76d8\u65cb\uff0c\u8d70\u51fa\u4e86\u5510\u7ecf\u5929\u5251\u5149\u7b3c\u7f69\u7684\u8303\u56f4\u4e4b\u5185\u3002\u8de8\u4e0a\u4e86 \u91d1\u6bdb\u72e1\uff0c\u4e5f\u968f\u7740\u5929\u9b54\u6559\u4e3b\u8dd1\u4e86\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u867d\u662f\u8f7b\u529f\u5353\u7edd\uff0c\u4f46\u91d1\u6bdb\u72e1\u5176\u884c\u5982\u98ce\uff0c\u5c82\u662f\u4eba\u529b\u6240\u80fd\u8ffd\u4e0a\uff1f\u5510\u7ecf\u5929\u63a5\u8fde\u53d1\u51fa \u4e24\u652f\u795e\u8292\uff0c\u7b2c\u4e00\u652f\u88ab\u5389\u590d\u751f\u7528\u7389\u5c3a\u53cd\u624b\u6253\u843d\uff0c\u7b2c\u4e8c\u652f\u5219\u6839\u672c\u8ffd\u4e0d\u4e0a\u91d1\u6bdb\u72e1\uff0c\u5728\u540e\u9762\u6389\u4e86 \u4e0b\u6765\u3002\u8f6c\u773c\u4e4b\u95f4\uff0c\u4e24\u53ea\u91d1\u6bdb\u72e1\u5df1\u662f\u8dd1\u5f97\u65e0\u8e2a\u65e0\u5f71\u3002 \u3000\u3000\u5510\u7ecf\u5929\u6276\u8d77\u59ec\u6653\u98ce\uff0c\u7ed9\u4ed6\u541e\u4e86\u4e00\u9897\u78a7\u7075\u4e39\uff0c\u89e3\u5f00\u6bd2\u6c14\u3002\u4e0d\u4e45\u9648\u5929\u5b87\u592b\u59bb\u4e5f\u9a91\u4e86\u5b9d\u9a6c \u5230\u6765\uff0c\u539f\u6765\u4ed6\u4eec\u592b\u59bb\u5728\u8def\u4e0a\u622a\u4f4f\u4e86\u8fd9\u4e24\u5339\u9a6c\uff0c\u4f46\u8fd9\u4e24\u5339\u9a6c\u6015\u4e86\u91d1\u6bdb\u72e1\uff0c\u76f4\u81f3\u95fb\u4e0d\u5230\u91d1\u6bdb \u72e1\u7684\u6c14\u5473\uff0c\u624d\u6562\u8dd1\u6765\u7684\uff0c\u6240\u4ee5\u53cd\u800c\u6765\u8fdf\u4e86\u3002 \u3000\u3000\u59ec\u6653\u98ce\u7834\u53e3\u5927\u9a82\uff0c\u51b0\u5ddd\u5929\u5973\u66f4\u662f\u5f15\u548e\u81ea\u8d23\u3002\u53cd\u800c\u662f\u6c5f\u5357\u5b89\u6170\u4ed6\u4eec\u9053\uff1a\u201c\u7978\u798f\u6709\u5b9a\uff0c \u542c\u90a3\u5929\u9b54\u6559\u4e3b\u7684\u53e3\u98ce\uff0c\u4e5f\u4f3c\u4e4e\u5e76\u4e0d\u60f3\u4f24\u5bb3\u543e\u513f\uff0c\u53ea\u4e0d\u77e5\u5979\u662f\u4f55\u7528\u610f\u7f62\u4e86\u3002\u54b1\u4eec\u8fd8\u662f\u6309\u539f \u5b9a\u8ba1\u5212\uff0c\u8d76\u5230\u91d1\u9e70\u5bab\u53bb\uff0c\u53ea\u8981\u4f1a\u89c1\u4e86\u91d1\u5927\u4fa0\uff0c\u5b9a\u7136\u53ef\u4ee5\u628a\u6d77\u513f\u6551\u4e86\u56de\u6765\u3002\u201d\u4f17\u4eba\u65e0\u8ba1\u53ef \u65bd\uff0c\u5f53\u7136\u4e5f\u53ea\u597d\u5982\u6b64\u3002 \u3000\u3000\u4e14\u8bf4\u6c5f\u6d77\u5929\u660f\u8ff7\u8fc7\u53bb\uff0c\u8ff7\u8ff7\u7cca\u7cca\u7684\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5ffd\u89c9\u6e05\u98ce\u62c2\u9762\uff0c\u82b1\u9999\u88ad\u4eba\uff0c\u6c5f \u6d77\u5929\u5438\u4e86\u51e0\u53e3\u65b0\u9c9c\u7a7a\u6c14\uff0c\u5341\u5206\u8212\u670d\uff0c\u5f90\u5f90\u7684\u5f20\u5f00\u773c\u775b\uff0c\u53ea\u89c1\u7eff\u8349\u5982\u8335\uff0c\u7e41\u82b1\u4f3c\u9526\uff0c\u767d\u4e91 \u7247\u7247\u9e1f\u9e23\u5624\u5624\uff0c\u773c\u524d\u6240\u89c1\uff0c\u7adf\u662f\u4e00\u7247\u8d4f\u5fc3\u60a6\u76ee\u7684\u5927\u597d\u98ce\u5149\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e0d\u7981\u5927\u4e3a\u5947\u602a\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u6e38\u76ee\u56db\u987e\uff0c\u8fdc\u5904\u662f\u4e00 \u7247\u81ea\u832b\u832b\u7684\u6e56\u6c34\uff0c\u5468\u56f4\u662f\u8471\u830f\u82cd\u90c1\u7684\u6811\u6728\uff0c\u8fd9\u624d\u77e5\u9053\u5904\u8eab\u4e4b\u5730\u4e43\u662f\u6e56\u4e2d\u7684\u4e00\u4e2a\u5c0f\u5c9b\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5c1a\u6709\u70b9\u6655\u7729\uff0c\u597d\u5728\u6797\u4e2d\u5230\u5904\u90fd\u662f\u6d41\u6cc9\uff0c\u4ed6\u63ac\u53d6\u6e05\u6cc9\uff0c\u6d17\u4e86\u628a\u9762\u8c03\u7cbe\u795e\u4e3a\u4e4b\u4e00 \u723d\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff0c\u7adf\u4f3c\u4e16\u5916\u6843\u6e90\u4f3c\u7684\uff1f\u6211\u600e\u4e48\u5230\u4e86\u8fd9\u4e2a\u5730\u65b9\u6765\u4e86\u3002\u7239\u7239 \u5462\uff1f\u59ec\u4f2f\u4f2f\u5462\uff1f\u600e\u7684\u5168\u90fd\u4e0d\u89c1\u4e86\uff0c\u53ea\u6709\u6211\u4e00\u4e2a\u4eba\u5728\u8fd9\u513f\uff0c\u8be5\u4e0d\u662f\u68a6\u5427\uff1f\u201d \u3000\u3000\u4ed6\u5b9a\u4e0b\u5fc3\u795e\uff0c\u6162\u6162\u7684\u60f3\u4e86\u8d77\u6765\uff0c\u60f3\u8d77\u4e86\u6628\u5929\u5728\u8def\u4e0a\u78b0\u89c1\u5929\u9b54\u6559\u4e3b\uff0c\u60f3\u8d77\u4e86\u90a3\u4e00\u573a\u6076 \u6597\uff0c\u4e0d\u7981\u970d\u7136\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u83ab\u4e0d\u662f\u6211\u7740\u4e86\u5929\u9b54\u6559\u4e3b\u7684\u9053\u513f\uff0c\u88ab\u5979\u64d2\u4e86\uff1f\u4f46\u600e\u7684\u8fde\u5929\u9b54 \u6559\u4e3b\u4e5f\u4e0d\u89c1\u5462\uff1f\u201d \u3000\u3000\u4ed6\u7684\u8bb0\u5fc6\u6e10\u6e10\u6062\u590d\uff0c\u4fbf\u8d8a\u6765\u8d8a\u662f\u60ca\u559c\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u51b3\u4e0d\u4f1a\u9519\u4e86\uff0c\u6211\u5728\u5931\u53bb\u77e5\u89c9\u4e4b \u524d\uff0c\u786e\u662f\u88ab\u90a3\u5929\u9b54\u6559\u4e3b\u6293\u4f4f\uff0c\u662f\u843d\u5728\u654c\u4eba\u7684\u624b\u4e2d\u4e86\uff01\u4ed6\u4eec\u8d39\u5c3d\u5fc3\u673a\u5c06\u6211\u62ff\u83b7\uff0c\u5374\u4f55\u4ee5\u53c8 \u5c06\u6211\u4e00\u4e2a\u4eba\u629b\u5728\u8fd9\u513f\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u767e\u601d\u4e0d\u5f97\u5176\u89e3\uff0c\u53ea\u597d\u6682\u65f6\u629b\u5f00\u4e0d\u60f3\uff0c\u4fbf\u5728\u5c0f\u5c9b\u4e0a\u6f2b\u65e0\u76ee\u7684\u7684\u4e71\u8d70\uff0c\u60f3\u770b\u770b\u5c9b \u4e0a\u662f\u5426\u53e6\u5916\u6709\u4eba\u3002 \u3000\u3000\u5c9b\u4e0a\u7684\u82b1\u8349\u6811\u6728\u90fd\u4f3c\u7ecf\u8fc7\u4fee\u7406\u7684\uff0c\u4f46\u6c5f\u6bcf\u5929\u5230\u5904\u4e71\u8d70\uff0c\u5374\u4e00\u76f4\u4e0d\u89c1\u6709\u4eba\u3002\u6811\u6797\u91cc\u53d1 \u73b0\u6709\u51e0\u9897\u679c\u6811\uff0c\u6811\u4e0a\u7ed3\u6709\u9c9c\u8273\u593a\u76ee\u7684\u5927\u7ea2\u679c\u5b50\uff0c\u5374\u53eb\u4e0d\u51fa\u540d\u5b57\u3002\u6c5f\u6d77\u5929\u6b63\u611f\u5230\u6709\u70b9\u8179\u997f\uff0c \u4fbf\u5411\u90a3\u4e00\u4e1b\u679c\u6811\u8d70\u53bb\u3002 \u3000\u3000\u6b63\u81ea\u5206\u5f00\u6821\u65f6\uff0c\u5ffd\u89c1\u90a3\u4e00\u8fb9\u7684\u7e41\u82b1\u5bc6\u53f6\u4e4b\u4e2d\uff0c\u6709\u4e00\u89d2\u7ea2\u5899\uff0c\u534a\u9690\u534a\u73b0\uff0c\u6c5f\u6d77\u5929\u5927\u559c \u9053\uff1a\u201c\u539f\u6765\u662f\u6709\u4eba\u5bb6\u7684\uff01\u201d\u6b63\u60f3\u51fa\u58f0\u53eb\u558a\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u5a07\u53f1\uff0c\u7a81\u7136\u6709\u4e2a\u5973\u5b50\u4ece\u82b1\u6811\u4e1b\u4e2d \u94bb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u89c1\u90a3\u5973\u5b50\u5df2\u62d4\u51fa\u4e86\u4e00\u7c91\u5b9d\u5251\uff0c\u5251\u5149\u95ea\u95ea\uff0c\u76f4\u6307\u5230\u6c5f\u6d77\u5929\u7684\u9762\u95e8\uff0c \u6012\u58f0\u65a5\u9053\uff0c\u201c\u6211\u4e0d\u8981\u518d\u89c1\u4f60\u4eec\u3002\u7ed9\u6211\u6eda\u56de\u53bb\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fd9\u7528\u5929\u7f57\u6b65\u6cd5\u95ea\u5f00\uff0c\u5ffd\u89c9\u8fd9\u5c11\u5973\u9762\u8c8c\u597d\u719f\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u90a3\u5c11\u5973\u4e5f\u4f3c\u4e4e\u6781\u4e3a\u60ca \u8be7\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u7a81\u7136\u628a\u5251\u52bf\u715e\u4f4f\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u5934\u6251\u901a\u4e00\u8df3\uff0c\u84e6\u5730\u53eb\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u83b2\u59b9\u5417\uff1f\u201d\u90a3\u5c11\u5973\u4e5f\u51e0\u4e4e\u540c\u65f6\u53eb\u9053\uff1a \u201c\u4f60\u4e0d\u662f\u6d77\u5929\u54e5\u54e5\u5417\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5973\u6b63\u662f\u6c5f\u6d77\u5929\u65e5\u601d\u591c\u60f3\u7684\u8c37\u4e2d\u83b2\uff01\u4ed6\u505a\u68a6\u4e5f\u68a6\u60f3\u4e0d\u5230\u7adf\u4f1a\u5728\u8fd9\u6837\u7684\u73af\u5883\u4e0b\u89c1\u9762\uff01 \u3000\u3000\u4ed6\u4eec\u4e8c\u4eba\u4e43\u662f\u9752\u6885\u7af9\u9a6c\u4e4b\u4ea4\uff0c\u9694\u522b\u4e86\u5c06\u8fd1\u5341\u5e74\uff0c\u5ffd\u7136\u5728\u8fd9\u5b64\u5c9b\u76f8\u9022\uff0c\u90fd\u89c9\u5f97\u5927\u51fa\u610f \u5916\uff0c\u5f7c\u6b64\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u90fd\u4e50\u5f97\u8df3\u4e86\u8d77\u6765\u3002\u5c24\u5176\u662f\u8c37\u4e2d\u83b2\u5728\u5b64\u5c9b\u4e0a\u5bc2\u5bde\u591a\u65f6\uff0c\u4e00\u65e6\u6545\u53cb\u91cd \u9022\uff0c\u7b80\u76f4\u4e50\u5f97\u5fd8\u5f62\uff0c\u5fd8\u8bb0\u4e86\u81ea\u5df1\u5df2\u957f\u5927\u6210\u4eba\uff0c\u662f\u4e2a\u5927\u59d1\u5a18\u4e86\uff0c\u5979\u6beb\u4e0d\u907f\u5acc\uff0c\u5c31\u4f3c\u5c0f\u65f6\u5019 \u4e0e\u6c5f\u6d77\u5929\u73a9\u800d\u7684\u60c5\u666f\uff0c\u62c9\u7740\u4ed6\u7684\u53cc\u624b\uff0c\u5c31\u5927\u53eb\u5927\u56b7\u8d77\u6765\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u6709\u65e0\u6570\u7591\u56e2\uff0c\u8981\u5411\u4ed6\u53d1\u95ee\uff0c\u8c37\u4e2d\u83b2\u7a81\u7136\u9762\u8272\u4e00\u6c89\uff0c\u6454\u5f00\u4e86\u6c5f\u6d77\u5929\u7684\u53cc\u624b\uff0c \u559d\u9053\uff1a\u201c\u6eda\u5f00\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5446\u4e86\u4e00\u5446\uff0c\u8bf4\u9053\uff1a\u201c\u83b2\u59b9\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u4f60\u4e0d\u9ad8\u5174\u89c1\u6211\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8c01\u662f \u4f60\u7684\u83b2\u59b9\uff1f\u4f60\uff0c\u4f60\u2014\u2014\u201d\u63a5\u8fde\u51e0\u4e2a\u201c\u4f60\u201d\u5b57\uff0c\u4f3c\u4e4e\u6012\u5f97\u5598\u4e0d\u8fc7\u6c14\u6765\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u53d8\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c\u6c5f\u6d77\u5929\u7b80\u76f4\u83ab\u540d\u5176\u5999\uff0c\u7741\u5927\u4e86\u773c\u775b\u95ee\u9053\uff0c\u201c\u4f60\u4e0d\u8ba4\u6211\u4e86\uff1f\u201d \u8c37\u4e2d\u83b2\u6012\u58f0\u8bf4\u9053\uff1a\u201c\u8c01\u8ba4\u4f60\u8fd9\u6ca1\u51fa\u606f\u7684\u5974\u624d\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u53eb\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u6211\u7eb5\u7136\u6ca1\u6709\u51fa\u606f\uff0c\u4f46\u5374\u51b3\u4e0d\u81f3\u4e8e\u662f\u4e2a\u5974\u624d\uff0c\u4f60\u600e\u4e48\u53ef\u4ee5 \u5f00\u53e3\u9a82\u4eba\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u7741\u5927\u4e86\u773c\u775b\uff0c\u4ed4\u7ec6\u5730\u625b\u91cf\u6c5f\u6bcf\u5929\u4e00\u773c\uff0c\u89c1\u4ed6\u7a7f\u6625\u5e38\u4eba\u670d\u9970\uff0c\u4e0d\u7981\u7eb3\u7f55\u95ee\u9053\uff1a \u201c\u4f60\u4e0d\u662f\u9a6c\u8428\u513f\u56fd\u56fd\u738b\u6d3e\u4f60\u6765\u7684\u5417\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u8fde\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u738b\u90fd\u6ca1\u6709\u89c1\u8fc7\uff0c \u8fd9\u8bdd\u4ece\u54ea\u513f\u8bf4\u8d77\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u662f\u56fd\u738b\u7684\u9e70\u5927\uff0c\u600e\u77e5\u6211\u5728\u8fd9\u4e2a\u5730\u65b9\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31 \u4e0d\u77e5\u9053\u4f60\u5728\u8fd9\u91cc\u7684\u5440\uff01\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u90a3\u4f60\u662f\u600e\u4e48\u6765\u7684\uff1f\u6ca1\u4eba\u9001\u4f60\u6765\uff0c\u4f60\u81ea\u5df1\u98de\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u600e\u6837\u6765\u7684\uff1f\u6211\u88ab\u5929\u9b54\u6559\u4e3b\u64d2\u83b7\uff0c\u4e00\u89c9\u9192\u6765\uff0c\u5c31\u5728\u8fd9\u5c0f\u5c9b\u4e0a\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u534a\u4fe1\u534a\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u592a\u8fc7\u79bb\u5947\uff0c\u6211\u53ef\u4e0d\u6562\u76f8\u4fe1\uff01\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u51e0 \u65f6\u9a97\u8fc7\u4f60\u6765\uff1f\u8981\u662f\u6211\u8bf4\u8c0e\u8bdd\uff0c\u8001\u5929\u7237\u7f5a\u6211\u5634\u4e0a\u957f\u4e2a\u5927\u75ae\uff0c\u6c38\u8fdc\u5f00\u4e0d\u4e86\u53e3\u3002\u201d\u4ed6\u5c0f\u65f6\u5019\u4e0e \u8c37\u4e2d\u83b2\u73a9\u8981\uff0c\u6bcf\u9022\u89e6\u72af\u4e86\u8c37\u4e2d\u83b2\uff0c\u8c37\u4e2d\u83b2\u5c31\u8981\u4ed6\u8fd9\u6837\u8d4c\u5492\uff0c\u4ed6\u5982\u4eca\u4e00\u6025\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u53c8 \u628a\u5c0f\u65f6\u5019\u8bf4\u60ef\u7684\u5492\u8bed\u62ff\u51fa\u6765\u4e86\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u201c\u6251\u54e7\u201d\u4e00\u7b11\uff0c\u6ee1\u8138\u6012\u6c14\uff0c\u767b\u65f6\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u770b\u4f60\u81ea\u5c0f\u8001 \u5b9e\uff0c\u6211\u8fd8\u771f\u4e0d\u6562\u76f8\u4fe1\u4f60\u5462\uff01\u201d\u6c5f\u6d77\u5929\u559c\u9053\uff1a\u201c\u4f60\u73b0\u5728\u76f8\u4fe1\u6211\u4e86\uff1f\u201d\u8c37\u4e2d\u83b2\u70b9\u70b9\u5934\u9053\uff1a \u201c\u4f60\u662f\u600e\u6837\u88ab\u5929\u9b54\u6559\u4e3b\u64d2\u6765\u7684\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5c06\u548b\u65e5\u7684\u906d\u9047\u8bf4\u4e86\u4e00\u904d\uff0c\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4ed6\u4eec\u4e3a\u4f55\u5982\u6b64\u597d\u5fc3\uff0c\u5c06\u4f60\u6765\u6b64\u5730\uff0c\u4e0e \u6211\u4f1a\u9762\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u5440\uff01\u4f60\u5462\uff0c\u4f60\u53c8\u600e\u4e48\u4f1a\u5728\u6b64\u7684\uff1f\u8fd9\u5c9b\u4e0a\u8fd8\u6709\u522b\u4eba\u5417\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u4e5f\u662f\u7ed9\u4eba\u64d2\u6765\u7684\u3002\u201d\u539f\u6765\u4ed6\u548c\u5e08\u7236\u8c37\u4e4b\u534e\u521a\u8e0f\u8fdb\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u571f\uff0c \u4fbf\u7ed9\u516b\u4e2a\u6b66\u58eb\u62e6\u9014\u622a\u51fb\uff0c\u5979\u4eec\u5be1\u4e0d\u654c\u4f17\uff0c\u7ed3\u679c\u8c37\u4e2d\u83b2\u906d\u64d2\uff0c\u8c37\u4e4b\u534e\u65e0\u529b\u6551\u5979\uff0c\u53ea\u80fd\u81ea\u5df1 \u4ed7\u5251\u51b2\u51fa\u91cd\u56f4\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u6700\u521d\u88ab\u4ed6\u4eec\u9001\u5165\u738b\u5bab\uff0c\u4ed6\u4eec\u3001\u4ed6\u4eec\u8981\u6211\u3001\u8981\u6211\u2026\u2026\u201d\u6c5f\u6d77\u5929\u9053\uff1a \u201c\u4ed6\u4eec\u8981\u4f60\u5ac1\u7ed9\u738b\u5b50\uff0c\u662f\u4e48\uff1f\u201d\u8c37\u4e2d\u83b2\u8be7\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u5148\u8bf4\u4e86\u4f60\u7684\u6545\u4e8b\uff0c\u6211\u518d\u544a\u8bc9\u4f60\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u4e0d\u80af\u4f9d\u4ece\uff0c\u628a\u90a3 \u56fd\u738b\u9a82\u4e86\u4e00\u987f\u3002\u56fd\u738b\u52a8\u4e86\u6012\uff0c\u5c31\u8981\u6740\u6211\u3002\u6709\u4e2a\u756a\u50e7\u548c\u4ed6\u5728\u4e00\u8d77\u88ab\u4ed6\u79f0\u4e3a\u56fd\u5e08\u7684\u5374\u529d\u4ed6\u9053\uff1a \u2018\u8fd9\u6837\u7f8e\u8c8c\u7684\u59d1\u5a18\uff0c\u6740\u4e86\u672a\u514d\u53ef\u60dc\uff0c\u4f46\u5173\u5728\u5bab\u91cc\uff0c\u4e5f\u6015\u6709\u6627\u9877\uff0c\u4e0d\u5982\u628a\u5979\u9001\u5230\u767e\u82b1\u6d32\u5b64 \u5c9b\u4e0a\u53bb\uff1b\u5916\u4eba\u51b3\u4e0d\u77e5\u9053\u90a3\u4e2a\u5730\u65b9\uff0c\u8981\u60f3\u6551\u5979\u4e5f\u4e0d\u80fd\u591f\u3002\u7b49\u5979\u51e0\u65f6\u56de\u5fc3\u8f6c\u610f\u518d\u653e\u5979\u56de\u6765\u3002 \u770b\u5979\u4e00\u4e2a\u5c0f\u59d1\u5a18\u80fd\u652f\u6301\u591a\u4e45\uff1f\u2019\u56fd\u738b\u542c\u4ece\u4e86\u4ed6\u7684\u4e3b\u610f\uff0c\u5c31\u628a\u6211\u9001\u5230\u8fd9\u91cc\u6765\u4e86\u3002\u201d\u8c37\u4e2d\u83b2 \u8bf4\u5230\u8fd9\u91cc\uff0c\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u522b\u53d1\u6101\uff0c\u54b1\u4eec\u60f3\u6cd5\u5b50\u51fa\u53bb\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u5012\u4e0d\u5355\u662f\u4e3a\u4e86\u88ab\u56da\u7981 \u800c\u53d1\u6101\uff0c\u2026\u2026\u55ef\uff0c\u4f60\u4e0d\u662f\u5916\u4eba\uff0c\u6211\u544a\u8bc9\u4f60\u4e00\u4ef6\u79d8\u5bc6\uff0c\u4f60\u53ef\u77e5\u9053\u5e08\u7236\u4e3a\u4ec0\u4e48\u5e26\u6211\u5230\u9a6c\u8428\u513f \u56fd\u6765\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u91cc\u6697\u77e3\uff1a\u201c\u6211\u65e9\u5df2\u77e5\u9053\u4e86\u3002\u201d\u4f46\u5374\u4e0d\u4f5c\u58f0\uff0c\u9759\u542c\u5979\u8bf4\u3002\u53ea\u542c\u5f97\u8c37\u4e2d\u83b2\u8bf4\u9053\uff1a \u201c\u6211\u5e08\u7236\u6000\u7591\u6211\u662f\u9a6c\u8428\u513f\u56fd\u524d\u738b\u7684\u5973\u513f\uff0c\u56e0\u6b64\u5e26\u6211\u5230\u8fd9\u5c0f\u56fd\u6765\uff0c\u7528\u610f\u5c31\u662f\u60f3\u8bbf\u67e5\u771f\u76f8\u7684\uff0c \u6211\u4e00\u5230\u6765\uff0c\u9a6c\u8428\u513f\u7684\u56fd\u738b\u5c31\u6d3e\u4eba\u6349\u6211\uff0c\u770b\u6765\u5e08\u7236\u7684\u6000\u7591\u5e76\u975e\u6355\u98ce\u6349\u5f71\u7684\u4e86\u3002\u5509\uff0c\u5018\u82e5\u6211 \u771f\u524d\u738b\u7684\u5973\u513f\uff0c\u73b0\u5728\u7684\u56fd\u738b\u5c31\u662f\u6211\u7684\u6740\u7236\u4ec7\u4eba\u4e86\u3002\u6211\u65e0\u529b\u62a5\u4ec7\uff0c\u53cd\u88ab\u4ec7\u4eba\u6b3a\u4fae\uff0c\u7109\u788d\u4e0d \u607c\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u56fd\u738b\u4e5f\u7b97\u80c6\u5927\uff0c\u4ed6\u7adf\u6562\u8981\u4ec7\u4eba\u7684\u5973\u513f\u4f5c\u81ea\u5df1\u7684\u5ab3\u5987\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a \u201c\u8fd8\u4e0d\u6b62\u8fd9\u4e00\u70b9\u53ef\u7591\uff0c\u6211\u5173\u5728\u8fd9\u91cc\u5c06\u8fd1\u4e00\u6708\uff0c\u4ed6\u66fe\u6d3e\u8fc7\u4e0d\u5c11\u4eba\u6765\u5957\u95f4\u6211\u7684\u53e3\u98ce\uff0c\u770b\u6211\u5bf9 \u81ea\u5df1\u7684\u8eab\u4e16\u79d8\u5bc6\u5207\u9053\u4e86\u591a\u5c11\u3002\u4f3c\u4e4e\u4ed6\u4eec\u662f\u5728\u5bb3\u6015\u524d\u738b\u6709\u4ec0\u4e48\u91cd\u8981\u7684\u79d8\u5bc6\u843d\u5728\u5916\u9762\u4f3c\u7684\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u78b0\u89c1\u4e86\u4f60\u7684\u54e5\u54e5\u4e48\uff1f\u201d\u8c37\u4e2d\u83b2\u53cc\u7709\u4e00\u7ad6\uff0c\u9053\uff1a\u201c\u6211\u54ea\u6765\u7684\u4ec0\u4e48\u54e5\u54e5\uff1f\u201d \u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u5728\u8def\u4e0a\u78b0\u89c1\u4f60\u7684\u54e5\u54e5\uff0c\u56fd\u4e3b\u8981\u4f60\u505a\u5ab3\u5987\u7684\u4e8b\u60c5\uff0c\u5c31\u662f\u4ed6\u544a\u8bc9\u6211\u7684\u3002\u4ed6\u8bf4 \u4ed6\u5df2\u7ecf\u89c1\u8fc7\u4f60\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u90a3\u65f6\u51b2\u9704\u5417\uff1f\u4ed6\u4e0d\u662f\u6211\u7684\u54e5\u54e5\uff01\u201d\u6c5f\u6d77\u5929\u5927\u4e3a\u60ca\u6115\uff0c\u8bf4\u9053\uff1a \u201c\u4f60\u662f\u5426\u56e0\u4e3a\u4ed6\u5c06\u4ec7\u4eba\u5f53\u4f5c\u4e49\u7236\uff0c\u6545\u6b64\u4e0d\u8096\u8ba4\u4ed6\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff0c\u201c\u4e0d\u662f\uff0c\u4ed6\u6839\u672c\u5c31\u4e0d\u662f \u6211\u7684\u54e5\u54e5\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u662f\uff1f\u5f53\u521d\u9a6c\u8428\u513f\u56fd\u5927\u4e71\uff0c\u4f60\u88ab\u4e18\u5ca9\u6551\u8d70\uff0c\u4ed6\u88ab\u53f6\u541b\u5c71\u6551\u8d70\uff0c\u867d \u7136\u957f\u5927\u4e86\u5404\u81ea\u4e00\u4e2a\u59d3\u6c0f\uff0c\u5374\u786e\u5b9e\u662f\u5b6a\u751f\u5144\u59b9\uff0c\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u4f60\u5e08\u7236\u6ca1\u6709\u544a\u8bc9\u4f60\u5417\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8fd9\u4e8b\u60c5\u6211\u90fd\u77e5\u9053\u4e86\u3002\u6211\u6b63\u8981\u8bbf\u67e5\u6211\u90a3\u54e5\u54e5\u7684\u4e0b\u843d\u3002\u4f46\u6211\u5374\u4e0d\u80fd\u80e1\u4e71\u8ba4 \u4e00\u4e2a\u4eba\u4f5c\u6211\u7684\u54e5\u54e5\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u53f6\u51b2\u9704\u7684\u8eab\u4e16\u6765\u5386\u90fd\u7b26\u5408\u4e86\uff0c\u4f55\u4ee5\u4f60\u4e00\u53e3\u54ac\u5b9a\u8bf4\u4ed6 \u4e0d\u662f\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4ed6\u4e0d\u4f1a\u634f\u9020\u5417\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u70b9\u663e\u800c\u6613\u89c1\u7684\u662f\uff1a\u4ed6\u7684\u76f8\u8c8c\u4e5f \u548c\u4f60\u5f88\u4e3a\u76f8\u4f3c\u5440\u3002\u201d\u8c37\u4e2d\u83b2\u51b7\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u76f8\u8c8c\u76f8\u4f3c\u7684\u4eba\u591a\u5f97\u5f88\uff0c\u4f55\u8db3\u4e3a\u5947\uff1f\u201d\u6c5f\u6d77\u5929 \u89c1\u5979\u56fa\u6267\u5df1\u89c1\uff0c\u751a\u4e3a\u4e0d\u89e3\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8fd9\u5c0f\u5c9b\u4e0a\u6709\u4e00\u5e62\u5efa\u7b51\uff0c\u672c\u662f\u56fd\u738b\u7684\u590f\u5b98\uff0c\u73b0\u5728\u5c31\u53ea\u6211\u4e00\u4e2a\u4eba\u5c45\u4f4f\uff0c\u5bc2 \u5bde\u6781\u4e86\uff0c\u4f60\u6765\u4e86\u6b63\u597d\uff0c\u53ef\u4ee5\u966a\u6211\u3002\u201d\u5f53\u4e0b\u4fbf\u5e26\u5f15\u6c5f\u6d77\u5929\u5728\u5979\u5c45\u4f4f\u7684\u5730\u65b9\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4ed6\u4eec\u6ca1\u6709\u6d3e\u4eba\u770b\u5b88\u4f60\u5417\uff1f\u201d\u5fc3\u4e2d\u83b2\u9053\uff1a\u201c\u60f3\u6765\u5f53\u7136\u6709\u7684\uff0c\u4e0d\u8fc7\u6ca1\u6709\u9732 \u9762\u3002\u6709\u4e00\u6b21\u6211\u60f3\u9020\u4e00\u4e2a\u6728\u7b4f\uff0c\u6ca1\u6709\u9020\u6210\uff0c\u7b2c\u4e8c\u5929\u65e9\u4e0a\uff0c\u4fbf\u7ed9\u4eba\u6bc1\u4e86\u3002\u53ef\u89c1\u8fd9\u5c9b\u4e0a\u8fd8\u85cf\u6709 \u522b\u4eba\uff0c\u6240\u4ee5\u4f60\u8981\u7279\u522b\u5c0f\u5fc3\u624d\u597d\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u53c8\u9053\uff1a\u201c\u8fd9\u590f\u5bab\u91cc\u85cf\u6709\u8bb8\u591a\u7cae\u98df\uff0c\u6bcf\u9694\u4e09\u5929\u8fd8\u6709\u4eba\u7ed9\u6211\u9001\u65b0\u9c9c\u7684\u852c\u83dc\u6765\u3002\u4ed6 \u4eec\u672c\u6765\u8fd8\u6d3e\u6709\u4e24\u4e2a\u5bab\u5973\u670d\u4f8d\u6211\u7684\uff0c\u6211\u5acc\u5979\u4eec\u7f57\u55e6\uff0c\u4e5f\u6015\u53d7\u5979\u4eec\u7684\u6697\u7b97\uff0c\u7b2c\u4e8c\u5929\u5c31\u628a\u5979\u4eec \u8d76\u8d70\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u4f1a\u5f04\u996d\u5417\uff1f\u201d\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u4f1a\u70e7\u83b1\u5462\uff0c\u4eca\u665a\u6211\u5c31\u5f04\u4e24\u6837 \u53ef\u53e3\u7684\u5c0f\u83dc\u7ed9\u4f60\u63a5\u98ce\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u70e7\u83dc\uff0c\u4f46\u6211\u4f1a\u716e\u996d\uff0c\u53ef\u4ee5\u5e2e\u4f60\u7684\u5fd9\u3002\u201d \u3000\u3000\u4e24\u4eba\u8bf4\u8bf4\u7b11\u7b11\uff0c\u4f9d\u7a00\u6062\u590d\u5c0f\u65f6\u5019\u7684\u5149\u666f\uff0c\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u8981\u662f\u54b1\u4eec\u65e0\u6cd5\u8131\u56f0\uff0c\u4f60\u5c31 \u8981\u966a\u6211\u5728\u8fd9\u91cc\u8fc7\u4e00\u4e16\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e5f\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u65e0\u6b8a\u4e16\u5916\u6843\u6e90\uff0c\u5c31\u662f\u5728\u8fd9\u91cc\u8fc7\u4e00\u4e16\u4e5f\u5f88\u4e0d\u9519\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a \u201c\u4f46\u4f60\u7684\u7239\u7239\u548c\u6211\u7684\u5e08\u7236\u5374\u8981\u5728\u5916\u9762\u6025\u6b7b\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u662f\u5440\uff0c\u6240\u4ee5\u54b1\u4eec\u8fd8\u662f\u5f97\u60f3 \u4e2a\u6cd5\u5b50\u8131\u8eab\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5ffd\u9053\uff1a\u201c\u6d77\u5929\uff0c\u4f60\u8ddf\u91d1\u5927\u4fa0\u5b66\u827a\u591a\u5e74\uff0c\u672c\u9886\u5f88\u4e0d\u9519\u4e86\u5427\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd8 \u4e0d\u6210\u5462\u3002\u600e\u4e48\uff0c\u54b1\u4eec\u521a\u89c1\u9762\u5c31\u8981\u4f38\u91cf\u6211\u5417\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4e0d\u662f\u6211\u8981\u4f38\u91cf\u4f60\u3002\u6211\u53ea\u60f3\u77e5 \u9053\u4f60\u80fd\u4e0d\u80fd\u6253\u8d62\u90a3\u53f6\u51b2\u9704\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u548c\u4ed6\u4ea4\u8fc7\u4e24\u6b21\u624b\u4e86\uff0c\u4ed6\u7684\u6b66\u529f\u786e\u662f\u4e0d\u5f31\u3002\u201d \u8c37\u4e2d\u83b2\u597d\u751f\u5931\u671b\uff0c\u9053\uff1a\u201c\u4f60\u8f93\u4e86\uff1f\u201d\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u4e24\u6b21\u6211\u90fd\u6070\u597d\u80dc\u4ed6\u3002\u201d\u8c37\u4e2d \u83b2\u5927\u559c\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u5c31\u6709\u6cd5\u5b50\u8131\u8eab\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u600e\u4e48\uff1f\u6211\u8fd8\u662f\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u90a3\u65f6\u51b2\u9704\u5192\u8ba4\u6211\u7684\u54e5\u54e5\uff0c\u6bcf\u9694\u4e09\u4e24\u5929\u5c31\u8981\u6765\u529d\u6211\u4e00\u6b21\uff0c\u6709\u65f6\u662f\u4ed6\u4e00\u4e2a \u4eba\u6765\u3002\u6709\u65f6\u662f\u51e0\u4e2a\u4eba\u6765\u3002\u6211\u6c14\u607c\u6781\u4e86\uff0c\u53ef\u662f\u6211\u6253\u4ed6\u4e0d\u8fc7\uff0c\u6ca1\u6cd5\u963b\u6b62\u4ed6\u4e0a\u95e8\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u4e0d\u9ad8\u5174\u89c1\u4ed6\uff0c\u8981\u6211\u7ed9\u4f60\u6321\u9a7e\uff1f\u201d\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u4e0d\uff0c\u6709\u4f60\u5728\u8fd9\u91cc\uff0c \u8fd9\u6b21\u6211\u6b63\u5df4\u4e0d\u5f97\u4ed6\u5feb\u70b9\u6765\u3002\u4ed6\u5f53\u7136\u662f\u4e58\u8239\u6765\u7684\uff0c\u6211\u5bf9\u4ed8\u4ed6\u7684\u4ece\u4eba\uff0c\u4f60\u5c06\u4ed6\u62ff\u7740\uff0c\u8feb\u4ed6\u9001 \u6211\u4eec\u51fa\u53bb\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u62cd\u4e00\u62cd\u8170\uff0c\u53eb\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a \u201c\u6211\u7684\u5b9d\u5251\u5931\u4e86\uff0c\u60f3\u5fc5\u662f\u7ed9\u90a3\u5929\u9b54\u6559\u4e3b\u62ff\u8d70\u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8131\u56f0\u4e4b\u540e\uff0c\u518d\u53bb\u627e\u90a3\u5929 \u9b54\u6559\u4e3b\u8981\u56de\u5b9d\u5251\u5427\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\uff0c\u6ca1\u6709\u5b9d\u5251\uff0c\u6211\u5c31\u6ca1\u6709\u628a\u63e1\u8d62\u5f97\u90a3\u53f6\u51b0\u9704\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u53ef\u4ee5 \u57cb\u4f0f\u6697\u5904\u5077\u88ad\u4ed6\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u5927\u4e08\u592b\u884c\u5f84\u3002\u201d\u8c37\u4e2d\u83b2\u751f\u6c14\u9053\uff1a\u201c\u4ed6\u4eec\u7528\u5c3d\u9634 \u8c0b\u8be1\u8ba1\u9677\u5bb3\u54b1\u4eec\uff0c\u4f60\u8fd8\u8981\u8bb2\u5927\u4e08\u592b\u884c\u5f84\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u4f9d\u4f60\uff0c\u597d\u574f\u8bd5\u5b83\u4e00\u8bd5 \u5427\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5ffd\u53c8\u53eb\u9053\uff1a\u201c\u597d\u5728\u767d\u7389\u7532\u8fd8\u5728\u8eab\u4e0a\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u53ea\u89c1 \u6c5f\u6d77\u5929\u5c06\u8863\u88f3\u4e00\u4ef6\u4e00\u4ef6\u8131\u6389\uff0c\u539f\u6765\u4ed6\u53d1\u73b0\u767d\u7389\u7532\u8fd8\u5728\uff0c\u4e00\u9635\u72c2\u559c\uff0c\u8feb\u4e0d\u53ca\u5f85\u7684\u5c31\u8981\u8131\u4e0b \u6765\u9001\u7ed9\u8c37\u4e2d\u83b2\u3002 \u3000\u3000\u5f85\u542c\u5f97\u8c37\u4e2d\u83b2\u90a3\u4e48\u4e00\u56b7\uff0c\u6c5f\u6d77\u5929\u624d\u731b\u5730\u60f3\u8d77\u6765\uff0c\u4ed6\u548c\u8c37\u4e2d\u83b2\u90fd\u5df2\u662f\u957f\u5927\u6210\u4eba\uff0c\u4e0d\u80fd \u50cf\u5c0f\u65f6\u5019\u7684\u6beb\u65e0\u907f\u5fcc\u4e86\u3002\u4ed6\u9762\u4e0a\u4e00\u7ea2\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u4f60\u80cc\u8fc7\u8eab\uff0c\u6211\u6709\u4e00\u6837\u597d\u4e1c\u897f\u9001\u4f60\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u201c\u54ad\u54ad\u201d\u7684\u7b11\u5f97\u6709\u5982\u82b1\u679d\u4e71\u98a4\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u53eb\u6211\u60f3\u8d77\u4f60\u7684\u7239\u7239\u6765\u4e86\u3002\u201d\u6c5f\u6d77 \u5929\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u7239\u7239\u7b2c\u4e00\u6b21\u89c1\u6211\u7684\u65f6\u5019\uff0c\u662f\u5149\u7740\u5c41\u80a1\u7684\u3002\u4f60\u5982\u4eca\u5012\u662f \u5149\u7740\u810a\u80cc\uff0c\u6b63\u662f\u6709\u5176\u4e49\u5fc5\u6709\u5176\u5b50\u3002\u201d\u6c5f\u6d77\u5929\u4e5f\u542c\u7236\u4eb2\u8bf4\u8fc7\u8fd9\u4ef6\u8da3\u4e8b\uff0c\u4e0d\u7981\u5484\u54c8\u5927\u7b11\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9664\u4e0b\u7389\u7532\uff0c\u5c06\u8863\u88f3\u7a7f\u597d\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4e54\u5317\u6e9f\u6240\u9057\u7559\u7684\u4e09\u5b9d\u4e4b\u4e00\uff0c\u7a7f\u5728\u8eab\u4e0a\uff0c \u591a\u950b\u5229\u7684\u5200\u5251\u4e5f\u523a\u4e0d\u8fdb\u53bb\u3002\u6211\u5e08\u7236\u53eb\u6211\u9001\u7ed9\u4f60\u7684\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u7559\u7740\u5427\u3002\u201d\u6c5f \u6d77\u5929\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u7684\u4e1c\u897f\u3002\u6211\u5728\u8def\u4e0a\u5df2\u7ecf\u6cbe\u4e86\u4f60\u7684\u5149\uff0c\u501f\u7528\u8fd9\u4ef6\u5b9d\u7532\uff0c\u907f\u8fc7\u597d\u51e0\u6b21\u707e\u96be \u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u63a8\u8f9e\u4e0d\u6389\uff0c\u53ea\u597d\u6536\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\u6211\u6ca1\u6709\u4ec0\u4e48\u597d\u4e1c\u897f\u9001\u7ed9\u4f60\u3002\u201d\u5ffd\u5730\u4f4e \u5782\u7c89\u9888\uff0c\u5982\u6709\u6240\u601d\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u662f\u81ea\u5c0f\u4e00\u8d77\u73a9\u7684\uff0c\u4f60\u8fd8\u548c\u6211\u8bb2\u4ec0\u4e48\u5ba2\u6c14\uff1f\u4f60\u8fd8\u8bb0\u5f97\u5417\uff0c\u5c0f\u65f6\u5019 \u4f60\u4f1a\u722c\u6811\uff0c\u6211\u8fd8\u4e0b\u4f1a\uff0c\u638f\u9e1f\u86cb\u5566\uff0c\u6458\u679c\u5b50\u5566\uff0c\u90fd\u662f\u4f60\u5f04\u4e86\u6765\u5206\u7ed9\u6211\u7684\uff0c\u6211\u53e8\u5149\u4f60\u7684\u4e1c\u897f \u5df2\u4e0d\u5c11\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u53ea\u9053\u5979\u8fc7\u610f\u4e0d\u53bb\u3002\u5374\u4e0d\u77e5\u9053\u5979\u6b63\u5728\u60f3\u53e6\u5916\u4e00\u4ef6\u4e8b\u60c5\u3002\u5979\u60f3\u4e86\u53c8\u60f3\uff0c \u7ec8\u4e8e\u51b3\u5b9a\u4e86\u8fd8\u662f\u6682\u65f6\u4e0d\u8bf4\u3002 \u3000\u3000\u4e24\u4eba\u8bf4\u8bf4\u7b11\u7b11\uff0c\u4e0d\u4e45\u5c31\u5230\u4e86\u8c37\u4e2d\u83b2\u7684\u4f4f\u6240\u3002\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u54ce\uff0c\u8fd9\u4e48\u5927\u7684\u623f\u5b50\uff0c\u4f60 \u4e00\u4e2a\u4eba\u4f4f\uff0c\u665a\u4e0a\u4e0d\u5bb3\u6015\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8d77\u521d\u51e0\u665a\uff0c\u6211\u665a\u4e0a\u90fd\u4e0d\u6562\u7761\u89c9\u3002\u597d\u5728\u4ed6\u4eec\u5012\u6ca1 \u6709\u6765\u9a9a\u6405\u6211\uff0c\u6e10\u6e10\u4e5f\u5c31\u4e60\u60ef\u4e86\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u6c5f\u6d77\u5929\u53c8\u53d1\u73b0\u5c4b\u4e2d\u7684\u4e00\u6839\u5f80\u5b50\uff0c\u523b\u6709\u8bb8\u591a \u5200\u75d5\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u6015\u5fd8\u8bb0\u65e5\u5b50\uff0c\u6bcf\u8fc7\u4e00\u5929\uff0c\u4fbf\u5728\u67f1\u4e0a\u523b\u4e0a \u4e00\u9053\u5200\u75d5\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u660f\u8ff7\u4e86\u4e0d\u77e5\u591a\u4e45\uff0c\u6b63\u60f3\u77e5\u9053\u4eca\u5929\u662f\u4ec0\u4e48\u65e5\u5b50\u3002\u201d\u8c37\u4e2d\u83b2\u6570 \u6570\u5200\u75d5\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6765\u5230\u5c9b\u4e0a\u8fd9\u5929\u662f\u4e03\u6708\u5341\u767e\uff0c\u8fd9\u67f1\u4e0a\u6709\u4e8c\u5341\u516b\u9053\u9053\uff0c\u55ef\uff0c\u4eca\u5929\u5e94\u8be5\u662f \u516b\u6708\u5341\u4e8c\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u51e0\u79bb\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u90fd\u5927\u7ea6\u591a\u8fdc\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u88ab\u4ed6\u4eec \u62bc\u6765\uff0c\u5148\u662f\u4e58\u8f66\uff0c\u5927\u7ea6\u8d70\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u540e\u6765\u5750\u8239\uff0c\u5927\u7ea6\u534a\u4e2a\u65f6\u8fb0\u3002\u770b\u6765\u4e0d\u4f1a\u8d85\u8fc7\u4e94\u5341\u91cc\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5927\u4e3a\u9a87\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u6211\u660f\u8ff7\u5b88\u4e0d\u8fc7\u51e0\u4e2a\u65f6\u8fb0\uff0c\u6211\u662f\u5728\u4e94\u767e\u91cc\u5916\u88ab\u62a1\u64d2\uff0c \u53ea\u4e0d\u8fc7\u51e0\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u4eec\u5c31\u5c06\u6211\u9001\u5230\u6b64\u5730\uff0c\u91d1\u6bdb\u72e1\u7684\u811a\u529b\u771f\u662f\u5feb\u5f97\u60ca\u4eba\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u53c8\u9053\uff1a\u201c\u4eca\u5929\u662f\u516b\u6708\u5341\u4e8c\uff0c\u91d1\u9e70\u5bab\u7684\u4f1a\u671f\u90a3\u5c31\u662f\u5927\u540e\u5929\u4e86\u3002\u6211\u7239\u7239\u548c\u59ec\u4f2f\u4f2f \u4ed6\u4eec\u662f\u4e00\u5b9a\u80fd\u8d76\u4e0a\u7684\uff0c\u53ef\u60dc\u54b1\u4eec\u662f\u51d1\u4e0d\u4e0a\u70ed\u95f9\u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u90a3\u4e5f\u8bf4\u4e0d\u5b9a\uff0c\u5018\u82e5\u53f6 \u51b2\u9704\u8fd9\u53ae\u5728\u8fd9\u4e24\u5929\u5185\u5230\u6765\uff0c\u54b1\u4eec\u5c06\u4ed6\u62ff\u7740\uff0c\u5c31\u53ef\u4ee5\u8131\u56f0\u4e86\u3002\u201d \u3000\u3000\u65e5\u5f71\u6e10\u6e10\u897f\u79fb\uff0c\u4f59\u971e\u6563\u7eee\uff0c\u6e56\u9762\u6cdb\u8d77\u4e00\u7247\u91d1\u5149\uff0c\u8c37\u4e2d\u83b2\u6709\u70b9\u5931\u671b\u3001\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u53ea \u6015\u6ca1\u4eba\u6765\u4e86\u3002\u54b1\u4eec\u5148\u5f04\u665a\u996d\u5403\u4e86\u518d\u8bf4\u3002\u201d\u6c5f\u6d77\u5929\u5e2e\u5979\u5f04\u996d\uff0c\u8c37\u4e2d\u83b2\u679c\u7136\u70e7\u4e86\u56db\u6837\u7cbe\u7f8e\u53ef \u53e3\u7684\u5c0f\u83dc\uff0c\u5403\u5f97\u6c5f\u6d77\u5929\u55b7\u55b7\u8d5e\u8d4f\u3002\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u4f60\u7684\u996d\u53ef\u70e7\u5f97\u5f88\u4e0d\u597d\u5462\uff0c\u6709\u4e00\u5927\u534a\u90fd \u70e7\u7cca\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u6b63\u5728\u8bf4\u7b11\uff0c\u8c37\u4e2d\u83b2\u5ffd\u9053\uff1a\u201c\u4f60\u542c\uff0c\u6a79\u58f0\u3001\u54bf\u5440\uff0c\u6709\u8239\u6765\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u8d70\u5230\u7a97\u53e3 \u4e00\u671b\uff0c\u679c\u7136\u6b63\u6709\u4e00\u53ea\u5c0f\u8239\u9a76\u6765\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5c0f\u8239\u9760\u5cb8\uff0c\u53ea\u89c1\u53ea\u6709\u4e00\u4e2a\u4eba\u8d70\u4e0a\u5cb8\u6765\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5927\u559c\u9053\uff1a\u201c\u6b63\u662f\u90a3\u53f6\u51b2\u9704\uff0c\u4ed6\u4e0d\u5e26\u968f\u4ece\uff0c\u66f4\u6613\u5bf9\u4ed8\u4e86\u3002\u4f60\u8d76\u5feb\u8eb2\u8d77\u6765\uff0c\u542c\u6211 \u4ee5\u54b3\u58f0\u4e3a\u53f7\uff0c\u7acb\u5373\u51fa\u6765\u88ad\u51fb\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u60f3\u518d\u95ee\u4ed6\u4e00\u5411\uff0c\u6211\u6000\u7591\u4ed6\u771f\u662f\u4f60\u7684\u54e5\u54e5\uff0c\u4ed6\u5c48\u8eab\u4e8b\u4ec7\uff0c\u53ea\u6015\u53e6\u6709\u9690 \u60c5\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u6012\u9053\uff1a\u201c\u4f60\u600e\u4e48\u535e\u76f8\u4fe1\u6211\uff0c\u6211\u8bf4\u4ed6\u4e0d\u662f\u4ed6\u5c31\u4e0d\u662f\uff0c\u4f60\u8fd8\u95ee\u4ec0\u4e48\uff1f\u65f6\u673a\u4e0d\u53ef\u9519 \u8fc7\uff0c\u4f60\u5018\u82e5\u4e00\u95ee\uff0c\u5077\u88ad\u5c31\u4e0d\u6210\u4e86\u3002\u4f60\u77a7\uff0c\u4ed6\u5c31\u5feb\u6765\u5230\u4e86\uff0c\u5feb\u8eb2\uff0c\u5feb\u8eb2\u3002\u201d\u6c5f\u6d77\u5929\u5c1a\u5728\u8fdf \u7591\uff0c\u8c37\u4e2d\u83b2\u5df2\u4e0d\u7531\u5206\u8bf4\u5730\u628a\u4ed6\u63a8\u5230\u5e10\u540e\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u628a\u7897\u8c0d\u5306\u5306\u5fd9\u5fd9\u5730\u6536\u62fe\u8d77\u6765\uff0c\u514d\u5f97\u7ed9\u4eba\u77a7\u7834\u662f\u6709\u4e24\u4eba\u5403\u996d\u3002\u521a\u521a\u6536\u62fe\u59a5\u5f53\uff0c \u53ea\u542c\u5f97\u53f6\u51b2\u9704\u5df2\u5728\u53e9\u95e8\u9053\uff1a\u201c\u83b2\u59b9\uff0c\u6211\u53c8\u6765\u770b\u4f60\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8fd9\u95e8\u662f\u865a\u63a9\u7684\uff0c\u4f60\u5c31\u8bf7\u8fdb\u6765\u5427\u3002\u201d\u53f6\u51b2\u9704\u63a8\u95e8\u8fdb\u6765\uff0c\u7b11\u9053\uff1a\u201c\u600e\u7684\u4eca \u79be\u8fd9\u6837\u5ba2\u6c14\uff0c\u4f60\u80af\u8ba4\u6211\u505a\u54e5\u54e5\u4e86\u5427\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u8bf4\u4f60\u662f\u6211\u7684\u54e5\u54e5\uff0c\u5374\u4e3a\u4f55\u8001\u662f\u5e2e\u7740\u5916\u4eba\u6765\u6b3a\u8d1f\u6211\uff1f\u201d\u53f6\u51b2\u9704\u9053\uff1a \u201c\u600e\u4e48\u662f\u6b3a\u8d1f\u4f60\uff0c\u6211\u529d\u4f60\u5ac1\u7ed9\u592a\u5b50\uff0c\u8fd9\u4e5f\u7b97\u662f\u6b3a\u8d1f\u4f60\u5417\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u60f3\u501f\u6211\u6c42\u53d6\u8363\u534e\u5bcc\u8d35\uff0c\u597d\u4e0d\u8981\u8138\uff01\u201d\u53f6\u51b2\u9704\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8363\u534e\u5bcc\u8d35\uff0c \u6211\u65e9\u5c31\u5df2\u7ecf\u6709\u4e86\uff0c\u4f55\u9700\u8d39\u529b\u5bfb\u6c42\uff0c\u6211\u662f\u9a6c\u8428\u513f\u56fd\u7684\u4e8e\u6bbf\u4e0b\uff0c\u5b98\u5c45\u2018\u6267\u91d1\u543e\u2019\u5927\u5c06\u519b\uff0c\u7236 \u738b\u5f85\u6211\u6709\u5982\u4eb2\u751f\u9aa8\u8089\uff0c\u6211\u7684\u6743\u529b\u6bd4\u592a\u5b50\u8fd8\u80dc\u4e09\u5206\u3002\u96be\u9053\u4f60\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4eba\u5404\u6709\u5fd7\uff0c\u4f60\u8d2a\u6155\u8363\u534e\u5bcc\u8d35\uff0c\u5c3d\u7ba1\u505a\u4f60\u7684\u5e72\u6bbf\u4e0b\u597d\u4e86\u3002\u4f60\u662f\u6211\u7684\u54e5\u54e5 \u4e5f\u7f62\uff0c\u4e0d\u662f\u6211\u7684\u54e5\u54e5\u4e5f\u7f62\uff0c\u6211\u51b3\u4e0d\u6cbe\u4f60\u534a\u70b9\u5149\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u6b6a\u7740\u773c\u775b\u7b11\u9053\uff1a\u201c\u4f55\u4ee5\u4f60\u4e0d\u80af\u5ac1\u7ed9\u592a\u5b50\uff1f\u55ef\uff0c\u83ab\u975e\u4f60\u65e9\u5df2\u6709\u4e86\u610f\u4e2d\u4eba\u4e86\uff1f\u201d \u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u518d\u80e1\u8bf4\u516b\u9053\uff0c\u6211\u53ef\u8981\u8bf7\u4f60\u8d70\u5566\uff01\u201d\u53f6\u51b2\u9704\u5ffd\u9053\uff1a\u201c\u91d1\u4e16\u9057\u6709\u4e2a\u5f92\u5f1f\u540d\u53eb \u6c5f\u6d77\u5929\uff0c\u662f\u548c\u4f60\u81ea\u5e7c\u76f8\u719f\u7684\u5427\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u6697\u81ea\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u4ed6\u5df2\u77e5\u9053\u4e86\u6c5f\u6d77\u5929\u5728\u6211\u8fd9\u513f\uff1f\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u662f\u53c8 \u600e\u6837\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4e0d\u600e\u4e48\u6837\u3002\u8fd9\u4eba\u6211\u4e5f\u662f\u8ba4\u8bc6\u7684\u3002\u4ed6\u548c\u7ec8\u5357\u5c71\u6b27\u9633\u4ef2\u548c\u7684\u5973\u513f\u65e9 \u5df2\u79c1\u8ba2\u7ec8\u8eab\uff01\u4ed6\u7684\u672a\u5a5a\u59bb\u5b50\u5df2\u7ecf\u5230\u4e86\u8fd9\u513f\uff0c\u6b63\u5728\u76fc\u671b\u4ed6\u6765\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u53d8\u4e86\u9762\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u610f\u601d\u3002\u4e0d \u8fc7\u6211\u77e5\u9053\u4f60\u548c\u6c5f\u6d77\u5929\u662f\u9752\u6885\u7af9\u9a6c\u4e4b\u4ea4\uff0c\u60f3\u5fc5\u5bf9\u4ed6\u6709\u70b9\u5173\u5fc3\uff0c\u6240\u4ee5\u544a\u8bc9\u4f60\u8fd9\u4ef6\u4e8b\u60c5\u7f62\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u591a\u8c22\u4e86\u3002\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8981\u8bf4\u5417\uff1f\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u7684\u5fc3\u610f\u8f6c\u4e86\u6ca1 \u6709\uff1f\u54b1\u4eec\u5230\u5e95\u662f\u5144\u59b9\uff0c\u4f60\u82e5\u5ac1\u7ed9\u592a\u5b50\uff0c\u54b1\u4eec\u5144\u59b9\u5c31\u53ef\u4ee5\u805a\u5728\u4e00\u8d77\u4e86\u3002\u4f60\u4e00\u4e2a\u5b64\u8eab\u5973\u5b50\uff0c \u6d6a\u8361\u6c5f\u6e56\u6709\u4ec0\u4e48\u597d\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5c31\u662f\u5b81\u613f\u6d6a\u8361\u6c5f\u6e56\uff0c\u4e5f\u51b3\u4e0d\u613f\u5411\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u738b\u4f4e\u5934\uff01\u201d\u53f6\u51b2 \u9704\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u53ef\u5947\u602a\u4e86\uff0c\u4f60\u600e\u4e48\u4f3c\u662f\u4e0e\u56fd\u738b\u6709\u4ec7\u6068\u4f3c\u7684\uff1f\u201d\u8c37\u4e2d\u83b2\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u662f\u56fd \u738b\u6d3e\u6765\u5411\u6211\u63a2\u542c\u79d8\u5bc6\u7684\u5427\uff1f\u201d\u53f6\u51b2\u9704\u9732\u51fa\u8ff7\u60d8\u7684\u795e\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u79d8\u5bc6\uff0c\u6211\u662f\u4e00\u70b9\u4e5f \u4e0d\u77e5\u9053\u5440\u3002\u54a6\uff0c\u4f60\u6709\u4ec0\u4e48\u79d8\u5bc6\uff1f\u201d\u4ed6\u8fb9\u8bf4\u8fb9\u628a\u8eab\u5b50\u632a\u8fc7\u6765\u3002\u80cc\u810a\u6b63\u5bf9\u7740\u6c5f\u6d77\u5929\u85cf\u533f\u7684\u65b9 \u5411\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5ffd\u5730\u4e00\u58f0\u54b3\u55fd\uff0c\u6c5f\u6d77\u5929\u65e9\u5df2\u5fcd\u8010\u4e0d\u4f4f\uff0c\u500f\u5730\u5c31\u8df3\u4e86\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u77e5 \u9053\u5417\uff1f\u4f60\u505a\u4e86\u9a6c\u8428\u513f\u56fd\u7684\u5e72\u6bbf\u4e0b\u5c31\u5fc3\u6ee1\u610f\u8db3\u4e86\u5417\uff1f\u6211\u544a\u8bc9\u4f60\uff0c\u4f60\u672c\u6765\u5e94\u8be5\u662f\u592a\u5b50\u7684\uff0c\u4f60 \u662f\u9a6c\u8428\u513f\u56fd\u524d\u738b\u7684\u513f\u5b50\uff0c\u73b0\u5728\u7684\u56fd\u738b\u662f\u4f60\u7684\u6740\u7236\u4ec7\u4eba\uff0c\u4f60\u660e\u767d\u4e86\u5417\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u6ee1\u4ee5\u4e3a\u6c5f\u6d77\u5929\u4e00\u8dd1\u51fa\u6765\u5c31\u4f1a\u5411\u53f6\u51b2\u9704\u88ad\u51fb\u7684\uff0c\u6240\u4ee5\u5979\u4e00\u53d1\u51fa\u6697\u53f7\uff0c\u7acb\u5373\u4fbf\u62d4 \u51fa\u6697\u85cf\u7684\u5315\u9996\u5411\u53f6\u51b2\u9704\u523a\u53bb\uff0c\u54ea\u77e5\u6c5f\u6d77\u5929\u5b85\u5fc3\u5fe0\u539a\uff0c\u59cb\u7ec8\u8ba4\u5b9a\u53f6\u51b2\u9704\u662f\u56e0\u4e3a\u4e0d\u77e5\u9053\u81ea\u5df1 \u751f\u8eab\u4e4b\u79d8\uff0c\u624d\u80af\u7518\u5fc3\u4e8b\u4ec7\uff0c\u4ed6\u8df3\u4e86\u51fa\u6765\uff0c\u5fcd\u4e0d\u7740\u4fbf\u8981\u5411\u53f6\u51b2\u9704\u8bf4\u4e2a\u6e05\u695a\uff0c\u4e24\u4eba\u672a\u66fe\u914d\u5408\u3002 \u53f6\u51b2\u9704\u7684\u6b66\u529f\u80dc\u8fc7\u8c37\u4e2d\u83b2\uff0c\u4e00\u4f38\u624b\u4fbf\u628a\u5979\u7684\u5315\u9996\u62cd\u843d\u4e86\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u6c14\u6012\u4ea4\u52a0\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6c5f\u6d77\u5929\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u4f60\u5e2e\u6211\u8fd8\u662f\u5e2e\u4ed6\uff1f\u201d\u5979\u5931\u4e86\u5315 \u9996\uff0c\u4ecd\u7136\u52c7\u731b\u5411\u524d\uff0c\u638c\u62cd\u6307\u6233\uff0c\u5979\u6700\u5403\u4e8f\u7684\u662f\u529b\u4e0d\u5982\u4eba\uff0c\u4f46\u8fd9\u7591\u638c\u4e00\u6307\uff0c\u4e43\u662f\u5415\u56db\u5a18\u542c \u4f20\u7684\u9099\u5c71\u7edd\u6280\uff0c\u4e13\u7834\u654c\u4eba\u7684\u6c14\u529f\u3002\u53f6\u51b2\u9704\u4e5f\u4e0d\u6562\u5c0f\u89d1\uff0c\u5f53\u4e0b\u53cc\u638c\u4e00\u5708\uff0c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c \u4f7f\u51fa\u4e86\u5927\u4e58\u822c\u82e5\u638c\u529b\uff0c\u4e25\u5982\u5728\u8eab\u5b50\u7684\u5468\u56f4\u7b51\u8d77\u4e86\u4e00\u9053\u6697\u5899\uff0c\u8c37\u4e2d\u83b2\u5728\u4e94\u6b65\u4e4b\u5916\uff0c\u7ed9\u4ed6\u7684 \u638c\u529b\u6321\u4f4f\uff0c\u518d\u4e5f\u4e0d\u80fd\u5411\u524d\u8fc8\u8fdb\u534a\u6b65\u3002\u5e78\u800c\u4ed6\u7684\u5927\u4e58\u822c\u82e5\u638c\u53ea\u662f\u7528\u6765\u9632\u5b88\uff0c\u5426\u5219\u8c37\u4e2d\u83b2\u7684 \u5947\u7ecf\u516b\u8109\uff0c\u53ea\u6015\u4e5f\u96be\u514d\u53d7\u4f24\u3002 \u3000\u3000\u53f6\u51b2\u9704\u56de\u8fc7\u5934\u6765\uff0c\u6ee1\u8138\u60f6\u60d1\u7684\u795e\u60c5\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u771f\u662f\u5047\uff1f\u6211\u3001\u6211\u3001\u6211\u5f53\u771f\u662f \u524d\u738b\u7684\u513f\u5b50\u5417\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u5e72\u5417\u8981\u9a97\u4f60\u3002\u4f60\u4e0d\u4fe1\u95ee\u4f60\u7684\u59b9\u59b9\uff01\u201d\u53f6\u51b2\u9704\u53eb\u9053\uff1a \u201c\u83b2\u59b9\uff0c\u4f60\u5feb\u8bf4\uff0c\u4f60\u5feb\u8bf4\u5440\uff01\u8fd9\u7a76\u7adf\u662f\u771f\u662f\u5047\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u6025\u58f0\u53eb\u9053\uff1a\u201c\u6d77\u5929\uff0c\u6d77\u5929\uff0c\u522b\u4e0a\u4ed6\u7684\u5f53\uff0c\u4ed6\u6839\u672c\u4e0d\u662f\u6211\u7684\u54e5\u54e5\uff01\u4f60\u8fd8\u4e0d\u52a8\u624b\uff0c \u66f4\u5f85\u4f55\u65f6\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u5ffd\u5730\u53cc\u6cea\u76f4\u6d41\uff0c\u54fd\u54bd\u8bf4\u9053\uff1a\u201d\u54e6\uff0c\u6211\u660e\u767d\u4e86\uff0c\u602a\u4e0d\u5f97\u4f60\u4e0d\u80af\u8ba4\u6211\u4f5c\u54e5\u54e5\u3002\u539f \u6765\u56fd\u738b\u4e43\u662f\u54b1\u4eec\u7684\u6740\u7236\u4ec7\u4eba\uff0c\u6211\u662f\u9519\u628a\u4ec7\u4eba\u5f53\u4f5c\u6069\u4eba\u4e86\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u89c1\u4ed6\u5982\u6b64\u60c5\u72b6\uff0c\u5fcd\u4e0d\u4f4f\u8bf4\u9053\uff1a\u201c\u83b2\u59b9\uff0c\u4fd7\u8bed\u8bf4\u5f97\u597d\uff1a\u4e0d\u77e5\u4e0d\u7f6a\u3002\u4ed6\u6bd5\u7adf\u662f\u4f60 \u7684\u54e5\u54e5\uff0c\u65e2\u7136\u660e\u767d\u8fc7\u6765\uff0c\u4f60\u5c31\u539f\u8c05\u4e86\u4ed6\u5427\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u77aa\u4e86\u6c5f\u6d77\u5929\u4e00\u773c\uff0c\u4ecd\u662f\u72e0\u72e0\u653b\u51fb\uff0c\u53f6\u51b2\u9704\u5374\u4f3c\u662f\u4e0d\u613f\u548c\u5979\u518d\u6253\u4e86\uff0c\u7ed9\u5979\u8feb\u5f97 \u6b65\u6b65\u540e\u9000\uff0c\u4e00\u9897\u9897\u7684\u6cea\u73e0\u4e0d\u65ad\u5730\u6389\u4e0b\u6765\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u90a3\u6c14\u607c\u7684\u795e\u8272\u5341\u5206\u660e\u663e\uff0c\u90a3\u662f\u6c14\u607c\u6c5f\u6d77\u5929\u4e0d\u6765\u5e2e\u5979\uff0c\u53ef\u662f\u6c5f\u6d77\u5929\u65e9\u5df2\u88ab\u53f6\u51b2 \u9704\u7684\u773c\u6cea\u8f6f\u5316\uff0c\u8fd9\u65f6\u4ed6\u5fc3\u4e2d\u6240\u60f3\u7684\u53ea\u662f\u5982\u4f55\u4f7f\u4ed6\u4eec\u5144\u59b9\u91cd\u5f52\u4e8e\u597d\uff0c\u54ea\u91cc\u8fd8\u80af\u53bb\u706b\u4e0a\u6dfb\u6cb9\uff1f \u3000\u3000\u53f6\u51b2\u9704\u54fd\u54bd\u8bf4\u9053\uff1a\u201c\u83b2\u59b9\uff0c\u4f60\u65e2\u7136\u607c\u6211\uff0c\u6211\u5c31\u7ed9\u4f60\u6253\u4e00\u987f\u51fa\u51fa\u6c14\u5427\u3002\u201d\u679c\u7136\u53cc\u624b\u4e0b \u5782\uff0c\u6beb\u4e0d\u8fd8\u51fb\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u5df2\u7ed9\u8c37\u4e2d\u83b2\u91cd\u91cd\u5730\u6253\u4e86\u4e00\u638c\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e0d\u5fcd\uff0c\u98de\u6b65\u4e0a\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5750\u4e0b\u6765\u597d\u597d\u8bf4\u5427\u3002\u201d\u6b63\u8981\u5c06\u4ed6\u4eec\u4e8c\u4eba\u62c9\u5f00\uff0c \u53f6\u51b2\u9704\u5ffd\u5730\u51fa\u638c\u5982\u7535\uff0c\u500f\u5730\u5411\u6c5f\u6d77\u5929\u6253\u53bb\u3002 \u3000\u3000\u82e5\u5728\u5e73\u65f6\uff0c\u6c5f\u6d77\u5929\u7109\u80fd\u8ba9\u4ed6\u6253\u4e2d\uff1f\u4f46\u5728\u6b64\u523b\uff0c\u4ed6\u6839\u672c\u662f\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u53f6\u51b2\u96f7\u4f1a\u52a8\u624b \u6253\u4ed6\uff0c\u4ed6\u4eec\u4e8c\u4eba\u7684\u529f\u529b\u672c\u5c31\u76f8\u5dee\u4e0d\u8fdc\uff1a\u6c5f\u6d77\u5929\u867d\u6709\u62a4\u4f53\u795e\u529f\uff0c\u4e5f\u7ed9\u4ed6\u4e00\u638c\u6253\u7ffb\uff0c\u773c\u524d\u91d1 \u661f\u4e71\u5192\uff0c\u51e0\u4e4e\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u80af\u539f\u8c05\u6211\uff0c\u6211\u53c8\u4e0d\u613f\u610f\u7ed9\u4f60\u4eec\u6d3b\u6d3b\u6253\u6b7b\u3002\u5bf9\u4e0d\u8d77\uff0c\u6211\u53ea\u597d\u5f97\u7f6a \u4f60\u4e86\u3002\u6c5f\u5144\uff0c\u8c13\u4f60\u6055\u7f6a\uff01\u6055\u7f6a\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5438\u4e86\u53e3\u6c14\uff0c\u9ed8\u8fd0\u7384\u529f\u3001\u7cbe\u795e\u7a0d\u7a0d\u6062\u590d\uff0c\u5fc3\u91cc\u72d0\u7591\u4e0d\u5b9a\u3001\u6697\u81ea\u60f3\u9053\uff1a\u201c\u4e0d\u77e5\u4ed6 \u8bf4\u7684\u662f\u771f\u662f\u5047\uff1b\u96be\u9053\u4ed6\u5f53\u771f\u628a\u6211\u7684\u597d\u5fc3\u8bef\u4f5c\u6076\u610f\uff0c\u6211\u662f\u4e0a\u524d\u529d\u67b6\uff0c\u4ed6\u4e5f\u770b\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u5ffd\u5730\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6c5f\u5144\uff0c\u4f60\u5feb\u8eb2\u4e00\u8eb2\uff0c\u6211\u7684\u4e24\u4e2a\u540c\u4f34\u6765\u4e86\uff0c\u4ed6\u4eec\u662f\u592a\u5b50\u7684\u4eba\u3002\u201d \u3000\u3000\u8bdd\u72b9\u6765\u4e86\uff0c\u53ea\u542c\u5f97\u5916\u9762\u5df2\u6709\u4eba\u56b7\u9053\uff1a\u201c\u5e72\u6bbf\u4e0b\uff0c\u4f60\u4eec\u5144\u59b9\u4e8c\u4eba\u53c8\u5728\u6253\u67b6\u5417\uff1f\u201d\u6c5f\u6d77 \u5929\u672a\u53ca\u8eb2\u85cf\uff0c\u90a3\u4e24\u4e2a\u4eba\u5df2\u63a8\u95e8\u800c\u8fdb\uff0c\u662f\u4e24\u4e2a\u7c97\u7709\u5927\u773c\u7684\u548c\u5c1a\u3002 \u3000\u3000\u4ed6\u4eec\u84e6\u7136\u53d1\u73b0\u5c4b\u5b50\u91cc\u591a\u4e86\u4e00\u4e2a\u964c\u751f\u4eba\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u5f53\u524d\u7684\u4e00\u4e2a\u548c\u5c1a\u5927\u559d\u9053\uff1a \u201c\u4f60\u8fd9\u5c0f\u5b50\u54ea\u6765\u7684\uff1f\u201d\u53e6\u4e00\u4e2a\u9053\uff1a\u201c\u4e0d\u5fc5\u95ee\u4e86\uff0c\u5b9a\u662f\u5978\u5f92\uff0c\u5feb\u5c06\u4ed6\u7ed1\u8d77\u6765\uff0c\u62ff\u53bb\u7ed9\u592a\u5b50 \u5ba1\u95ee\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u53eb\u9053\uff1a\u201c\u6d77\u5929\uff0c\u54b1\u4eec\u4eca\u65e5\u843d\u5728\u574f\u4eba\u624b\u4e0a\uff0c\u5b81\u6b7b\u4e0d\u53ef\u53d7\u8fb1\uff01\u201d\u53cc\u638c\u4e00\u5206\uff0c\u4e00\u62db \u201c\u5f2f\u5f13\u5c04\u96d5\u201d\uff0c\u5206\u51fb\u4e24\u4e2a\u548c\u5c1a\u3002 \u3000\u3000\u80d6\u7684\u90a3\u4e2a\u548c\u5c1a\u6b6a\u7740\u773c\u775b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u548c\u8fd9\u6d51\u5c0f\u5b50\u60f3\u662f\u6709\u4e86\u79c1\u60c5\u5427\uff0c\u6015\u6211\u4eec\u5ba1\u95ee\u51fa \u6765\uff1f\u8fd9\u66f4\u4e0d\u80fd\u5bb9\u4e86\uff01\u201d \u3000\u3000\u53f6\u51b2\u9704\u6025\u5f97\u56e2\u56e2\u4e71\u8f6c\uff0c\u4f3c\u4e4e\u62ff\u4e0d\u5b9a\u4e3b\u610f\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u4e2a\u7626\u548c\u5c1a\u5df2\u4e00\u638c\u628a\u8c37 \u4e2d\u83b2\u63a8\u5f00\uff0c\u559d\u9053\uff1a\u201c\u8d3c\u5c0f\u5b50\uff0c\u8fd8\u4e0d\u675f\u624b\u5c31\u64d2\uff0c\u60f3\u62d2\u6355\u5417\uff1f\u201d\u5530\u7684\u4e00\u97ad\uff0c\u5c31\u5411\u6c5f\u6d77\u5929\u8eab\u4e0a \u6253\u53bb\u3002 \u3000\u3000\u53f6\u51b2\u9704\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u4e0d\u662f\u7684\uff0c\u4e0d\u662f\u7684\uff01\u201d\u90a3\u548c\u5c1a\u8be7\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u662f\u7684\uff1f\u201d\u4ed6\u53e3\u4e2d\u8bf4 \u8bdd\uff0c\u624b\u5e95\u4e1d\u6beb\u4e0d\u7f13\uff0c\u5df2\u63a5\u8fde\u5728\u6c5f\u6d77\u5929\u8eab\u4e0a\u62bd\u4e86\u4e09\u97ad\u3002 \u3000\u3000\u8fd9\u4e09\u97ad\u6b63\u6253\u5728\u6c5f\u6d77\u5929\u7684\u4f24\u5904\uff0c\u6c5f\u6d77\u5929\u5927\u6012\uff0c\u4e00\u56bc\u820c\u5c16\uff0c\u4f7f\u51fa\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\uff0c\u5c06\u529f\u529b \u51dd\u805a\u8d77\u6765\uff0c\u66ae\u7136\u8df3\u8d77\uff0c\u547c\u7684\u4e00\u638c\uff0c\u5c31\u5411\u90a3\u7626\u548c\u5c1a\u51fb\u53bb\u3002 \u3000\u3000\u90a3\u548c\u5c1a\u7684\u957f\u97ad\u6253\u5728\u6c5f\u6d77\u5929\u8eab\u4e0a\uff0c\u5ffd\u5730\u201c\u903c\u535c\u201d\u4e00\u58f0\uff0c\u65ad\u4e3a\u4e24\u6bb5\uff0c\u90a3\u548c\u5c1a\u5403\u4e86\u4e00\u60ca\uff0c \u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u7684\u6b66\u529f\u8fd8\u771f\u4e0d\u5f31\u5462\uff01\u201d\u8fb9\u8bf4\u8fb9\u8fd8\u4e86\u4e00\u638c\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e5f\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u51db\uff0c\u8981\u77e5\u4ed6\u4e0d\u60dc\u6d88\u8017\u771f\u6c14\uff0c\u4f7f\u51fa\u8fd9\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\uff0c\u529f\u529b\u5df2\u548c \u672a\u53d7\u4f24\u4e4b\u524d\u5dee\u4e0d\u4e86\u591a\u5c11\uff0c\u8fd9\u7626\u548c\u5c1a\u5c45\u7136\u80fd\u63a5\u5f97\u7740\u4ed6\u7684\u638c\u529b\uff0c\u663e\u89c1\u4e5f\u662f\u4e00\u6d41\u9ad8\u624b\uff0c\u81f3\u5c11\u4e0d \u5728\u53f6\u51b2\u9704\u4e4b\u4e0b\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u91cc\u60f3\u9053\uff1a\u201d\u602a\u4e0d\u5f97\u53f6\u51b2\u9704\u5bf9\u4ed6\u4eec\u6709\u6240\u987e\u5fcc\uff0c\u8fdf\u8fdf\u7591\u7591\u7684\u4e0d\u6562\u559d\u6b62\u4ed6\u4eec\u3002\u201d \u539f\u6765\u6c5f\u6d77\u5929\u770b\u89c1\u65f6\u51b2\u9704\u7740\u6025\u7684\u795e\u60c5\uff0c\u5fc3\u91cc\u5bf9\u4ed6\u53c8\u76f8\u4fe1\u51e0\u5206\uff0c\u4ee5\u4e3a\u521a\u624d\u5f53\u771f\u662f\u4e00\u573a\u8bef\u4f1a\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df1\uff0c\u53ea\u6240\u5f97\u53f6\u51b2\u9704\u679c\u7136\u53eb\u9053\uff1a\u201c\u8bf7\u4e24\u4f4d\u7f62\u624b\uff0c\u4ed6\u4e0d\u662f\u3001\u4e0d\u662f\u3001\u4e0d\u662f\u7684\u2026\u2026\u201d \u80d6\u7684\u90a3\u4e2a\u548c\u5c1a\u8fd9\u65f6\u5df2\u5c06\u8c37\u4e2d\u83b2\u70b9\u4e86\u7a74\u9053\uff0c\u8d70\u8fc7\u6765\u6b63\u60f3\u52a9\u6218\uff0c\u542c\u4e86\u53f6\u51b2\u9704\u7684\u6d3b\uff0c\u6014\u4e86\u4e00\u6014\uff0c \u4ed6\u7684\u5fc3\u601d\u6bd4\u90a3\u7626\u7684\u806a\u654f\uff0c\u968f\u5373\u4f5c\u604d\u7136\u5927\u609f\u72b6\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u662f\u7684\uff1f\u554a\uff0c\u4f60\u662f\u8bf4\u4ed6\u548c\u4f60\u7684\u59b9 \u59b9\u6ca1\u6709\u79c1\u60c5\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u51b2\u7834\u6a0a\u7b3c\u539f\u4e0d\u6613\uff0c\u6843\u6e90\u5883\u91cc\u52a8\u5200\u5175\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e00\u56de\u3000\u795e\u529f\u51ed\u501f\u5929\u5fc3\u77f3\u3000\u79d8\u5bc6\u6df1\u85cf\u4e00\u7eb8\u4e66 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e09\u5341\u4e00\u56de\u3000\u795e\u529f\u51ed\u501f\u5929\u5fc3\u77f3\u3000\u79d8\u5bc6\u6df1\u85cf\u4e00\u7eb8\u4e66 \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u54b1\u4eec\u7684\u654c\u4eba\u3002\u201d\u7626\u7684\u90a3\u4e2a\u548c\u5c1a\u8be7\u9053\uff1a\u201c\u54a6\uff0c\u521a\u624d\u548c\u4f60\u6253\u67b6\u7684\u4e0d \u662f\u4ed6\u5417\uff1f\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u4ed6\u3002\u201d\u7626\u548c\u5c1a\u9053\uff1a\u201c\u90a3\u4f60\u600e\u7684\u8bf4\u4ed6\u4e0d\u662f\u654c\u4eba\uff1f\u6211\u5012\u4e0d \u89e3\u4e86\u3002\u201d\u80d6\u548c\u5c1a\u4e5f\u9053\uff0c\u201c\u90a3\u4e48\u4ed6\u7684\u6765\u56fd\u6bbf\u4e0b\u662f\u77e5\u9053\u7684\u4e86\uff1f\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4ed6\u662f\u91d1\u4e16\u9057\u7684\u5f1f\u5b50\u3002\u6211\u59b9\u59b9\u7684\u5e08\u7236\u662f\u9099\u5c71\u6d3e\u638c\u95e8\u4eba\u8c37\u4e4b\u534e\u3002\u4ed6\u4eec\u4e24\u4eba \u7684\u4ea4\u60c5\u5f88\u597d\u3002\u201d\u7626\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u4eec\u65e9\u5df2\u77e5\u9053\uff0c\u76f4\u767d\u7684\u8bf4\uff0c\u8c37\u4e4b\u534e\u662f\u91d1\u4e16\u9057\u7684\u60c5\u4eba\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u8c37\u4e4b\u534e\u5f53\u7136\u4e0d\u4f1a\u77e5\u9053\u7236\u738b\u5bf9\u6211\u7684\u59b9\u5b50\u4e43\u662f\u4e00\u756a\u597d\u610f\uff0c\u60f3\u5fc5\u662f\u4ed6\u53bb\u6c42\u91d1 \u4e16\u9057\u8425\u6551\u6211\u7684\u59b9\u5b50\uff0c\u56e0\u800c\u91d1\u4e16\u9057\u5c31\u6d3e\u4e86\u4ed6\u7684\u5f92\u5f1f\u6765\u3002\u4ed6\u7684\u76ee\u7684\u53ea\u5728\u6551\u4eba\uff0c\u5e76\u975e\u53cd\u5bf9\u7687\u4e0a\u3002\u201d \u3000\u3000\u90a3\u7626\u548c\u5c1a\u9053\uff1a\u201c\u6bb4\u4e0b\u6b64\u8a00\u5dee\u77e3\uff0c\u4ee4\u59b9\u5df2\u7136\u662f\u592a\u5b50\u5983\u4e86\uff0c\u8fd9\u5c0f\u5b50\u8981\u6765\u6551\u4eba\uff0c\u8fd8\u4e0d\u662f\u654c \u4eba\u5417\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4e24\u4f4d\u6709\u6240\u4e0d\u77e5\uff0c\u56fd\u5e08\u6b63\u8981\u4e0e\u91d1\u4e16\u9057\u7ed3\u7eb3\uff0c\u7236\u738b\u4e5f\u60f3\u5f97\u91d1\u4e16\u9057\u52a9\u4ed6\u4e00\u81c2 \u4e4b\u529b\u3002\u54b1\u4eec\u82e5\u662f\u5f97\u7f6a\u4e86\u91d1\u4e16\u9057\u7684\u5f92\u5f1f\uff0c\u90a3\u65f6\u5012\u771f\u7684\u662f\u8981\u8feb\u91d1\u4e16\u9057\u53d8\u4f5c\u54b1\u4eec\u7684\u654c\u4eba\u4e86\u3002\u5c82 \u975e\u8fdd\u80cc\u4e86\u7236\u738b\u548c\u56fd\u5e08\u7684\u4e3b\u610f\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u548c\u5c1a\u6b63\u662f\u5b9d\u8c61\u6cd5\u5e08\u7684\u5f1f\u5b50\uff0c\u4ed6\u4eec\u5bf9\u56fd\u738b\u8fd8\u4e0d\u600e\u4e48\u60e7\u6015\uff0c\u4f46\u53f6\u51b2\u9704\u62ac\u51fa\u4e86\u4ed6\u4eec \u7684\u5e08\u7236\u6765\u538b\u4ed6\u4eec\uff0c\u4ed6\u4eec\u600e\u6562\u8fdd\u80cc\u5e08\u7236\u7684\u610f\u5fd7\uff1f\u53ea\u662f\u4ed6\u4eec\u9762\u9762\u76f8\u89d1\uff0c\u4f3c\u4e4e\u662f\u6b63\u5728\u8e0c\u8e87\uff0c\u4e00 \u65f6\u4e4b\u95f4\uff0c\u62ff\u4e0d\u5b9a\u4e3b\u610f\u3002 \u3000\u3000\u80d6\u548c\u5c1a\u9053\uff1a\u201c\u7136\u5219\u4efb\u7531\u4ed6\u5c06\u4ee4\u59b9\u5e26\u8d70\u5417\uff1f\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u8fd9\u5f53\u7136\u4e0d\u80fd\uff0c\u5426\u5219\u6211\u521a\u624d \u4e5f\u4e0d\u4f1a\u4e0e\u4ed6\u6253\u67b6\u4e86\u3002\u201d\u7626\u548c\u5c1a\u9053\uff1a\u201c\u65e2\u4e0d\u80fd\u5f53\u4ed6\u662f\u654c\u4eba\uff0c\u53c8\u4e0d\u80fd\u8ba9\u4ed6\u5c06\u4eba\u5e26\u8d70\uff0c\u8fd9\u600e\u4e48 \u529e\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4f9d\u6211\u4e4b\u89c1\uff0c\u4e0d\u5982\u7531\u6211\u53bb\u7980\u544a\u56fd\u5e08\uff0c\u600e\u6837\u5904\u7f6e\u6b64\u4eba\uff0c\u7531\u4ed6\u4f5c\u4e3b\u3002\u4f46\u4f60\u4eec \u8981\u5c06\u4ed6\u7f1a\u53bb\uff0c\u4e8b\u60c5\u5c31\u4f1a\u5f04\u69fd\u7cd5\u4e86\u3002\u201d\u7626\u548c\u5c1a\u8fdf\u8fdf\u7591\u7591\u8bf4\u9053\uff1a\u201c\u56de\u53bb\u7980\u544a\u56fd\u5e08\uff0c\u8fd9\u5f53\u7136\u5f88 \u597d\u3002\u53ef\u662f\u8fd9\u5c31\u5f97\u7b49\u5230\u660e\u5929\u624d\u80fd\u5904\u7406\u4e86\uff0c\u4eca\u665a\u5c31\u8ba9\u4ed6\u5728\u8fd9\u91cc\u5417\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4f60\u4e0d\u89c1\u4ed6\u5df2\u53d7\u4e86\u91cd\u4f24\u5417\uff1f\u4f60\u4eec\u4eca\u665a\u5c31\u591a\u6d3e\u4e9b\u4eba\u5728\u5c9b\u4e0a\u770b\u5b88\uff0c\u8c05\u4ed6\u63d2\u7ffc \u4e5f\u96be\u9003\u8d70\u3002\u201d\u90a3\u4e24\u4e2a\u548c\u5c1a\u70b9\u4e86\u70b9\u5934\uff0c\u4f46\u663e\u7136\u8fd8\u6709\u60f6\u60d1\u7684\u795e\u6c14\u3002 \u3000\u3000\u53f6\u51b2\u9704\u53c8\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u4f60\u4eec\u5c06\u4ed6\u7f1a\u8d70\uff0c\u4e5f\u6b63\u662f\u56e0\u4e3a\u4ed6\u5df2\u53d7\u4e86\u91cd\u4f24\u3002\u6b64\u53bb\u4eac\u57ce\u8fd8\u6709\u516d \u5341\u591a\u91cc\uff0c\u54b1\u4eec\u6ca1\u53d7\u4f24\u7684\u4e0d\u5f53\u4f5c\u4e00\u56de\u4e8b\uff0c\u4ed6\u53d7\u4e86\u4f24\uff0c\u5018\u82e5\u5c06\u4ed6\u79fb\u52a8\uff0c\u4e00\u8def\u6362\u8f66\u6362\u8239\uff0c\u9053\u8def \u53c8\u5f88\u5d0e\u5c96\uff0c\u5018\u82e5\u4ed6\u4e2d\u9014\u6b7b\u4e86\uff0c\u54b1\u4eec\u548c\u91d1\u4e16\u9057\u8fd9\u4e2a\u6028\u53ef\u5c31\u7ed3\u6709\u5927\u4e86\u3002\u90a3\u65f6\u975e\u4f46\u65e0\u529f\u53ef\u9886\uff0c \u53ea\u6015\u56fd\u5e08\u8fd8\u8981\u8d23\u602a\u54b1\u4eec\uff0c\u6240\u4ee5\u4f9d\u6211\u4e4b\u89c1\uff0c\u4eca\u665a\u53ea\u597d\u8ba9\u4ed6\u5728\u8fd9\u91cc\u517b\u4f24\u3002\u201d \u3000\u3000\u80d6\u548c\u5c1a\u9053\uff1a\u201c\u5018\u82e5\u51fa\u4e86\u5c94\u5b50\uff0c\u6bb4\u4e0b\u662f\u5426\u72ec\u81ea\u62c5\u5f53\uff1f\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u7528\u62c5\u5fe7\uff0c \u7eb5\u7136\u5929\u584c\u4e0b\u6765\uff0c\u4e5f\u4e0d\u7528\u4f60\u4eec\u62c5\u5f53\u5c31\u662f\uff01\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u548c\u5c1a\u9f50\u9053\uff1a\u201c\u6bbf\u4e0b\u65e2\u7136\u5982\u6b64\u5429\u5490\uff0c\u6211\u7b49\u9075\u547d\u4fbf\u662f\u3002\u201d\u4ed6\u4eec\u4e34\u8d70\u65f6\u8fd8\u5411\u6c5f\u6d77\u5929 \u5408\u4ec0\u65bd\u793c\u9053\uff1a\u201c\u6211\u7b49\u4e0d\u77e5\u4f60\u662f\u91d1\u5927\u4fa0\u7684\u5f1f\u5b50\uff0c\u591a\u6709\u5192\u72af\uff0c\u8fd8\u671b\u6055\u7f6a\u3002\u201d\u6c5f\u6d77\u5929\u4e00\u76f4\u6ca1\u6709 \u5f00\u53e3\u8bf4\u8bdd\uff0c\u4f46\u4ed6\u5b85\u5fc3\u5fe0\u539a\uff0c\u89c1\u4ed6\u4eec\u8d54\u7f6a\uff0c\u4e5f\u5c31\u9ed8\u9ed8\u5730\u8fd8\u4e86\u4e00\u793c\u3002 \u3000\u3000\u65f6\u51b2\u9704\u53d6\u51fa\u4e00\u74f6\u836f\u818f\uff0c\u653e\u5728\u51e0\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4e0a\u597d\u7684\u91d1\u521b\u836f\uff0c\u4f60\u81ea\u5df1\u6577\u4f24\u5427\u3002\u201d \u968f\u5373\u89e3\u5f00\u4e86\u8c37\u4e2d\u83b2\u7684\u7a74\u9053\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u80af\u8ba4\u6211\u4f5c\u54e5\u54e5\uff0c\u6211\u4ecd\u7136\u5f53\u4f60\u662f\u59b9\u59b9\u3002\u4f60\u4eca\u665a\u597d \u597d\u60f3\u4e00\u60f3\uff0c\u660e\u65e5\u6211\u518d\u6765\u770b\u4f60\u3002\u201d\u8bf4\u7f62\u4fbf\u4e0e\u90a3\u4e24\u4e2a\u548c\u5c1a\u4e00\u540c\u8d70\u4e86\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u7a74\u9053\u65b9\u89e3\uff0c\u6c14\u8840\u672a\u8212\uff0c\u5fc3\u4e2d\u607c\u6068\u3001\u5374\u9a82\u4e0d\u51fa\u6765\u3002\u6c5f\u6d77\u5929\u8fc7\u6765\uff0c\u66ff\u5979\u690e\u62ff\uff0c\u6d3b \u52a8\u7b4b\u8109\uff0c\u8c37\u4e2d\u83b2\u66ae\u5730\u987f\u8db3\u9a82\u9053\uff1a\u201c\u4f60\u771f\u662f\u5fe0\u539a\u5f97\u8fd1\u4e4e\u7cca\u6d82\uff0c\u597d\u597d\u7684\u8ba1\u5212\uff0c\u90fd\u7ed9\u4f60\u5f04\u574f\u4e86\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8d54\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u867d\u7136\u4e0d\u80fd\u8131\u56f0\uff0c\u4f46\u6700\u5c11\u5df2\u5f04\u660e\u767d\u4e86\u4e00\u4ef6\u4e8b\u60c5\uff0c\u4f60\u7684\u54e5\u54e5\u867d\u7136 \u540d\u5229\u5fc3\u91cd\uff0c\u5374\u8fd8\u4e0d\u662f\u4e00\u4e2a\u5f88\u574f\u7684\u4eba\u3002\u539f\u6765\u4ed6\u786e\u5b9e\u662f\u4e0d\u77e5\u9053\u81ea\u5df1\u751f\u8eab\u7684\u79d8\u5bc6\u3002\u6211\u5947\u602a\u4f60\u4e3a \u4ec0\u4e48\u603b\u662f\u4e0d\u80af\u539f\u8c05\u4ed6\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u6012\u9053\uff1a\u201c\u6211\u66f4\u5947\u602a\u4e3a\u4ec0\u4e48\u4f60\u603b\u662f\u4e0d\u542c\u6211\u7684\u540e\uff0c\u90a3\u53f6\u51b2\u9704\u4e0d\u77e5\u662f\u56fd\u738b\u4ece\u54ea\u91cc\u5f04 \u6765\u7684\u91ce\u5c0f\u5b50\uff0c\u600e\u4f1a\u662f\u6211\u7684\u54e5\u54e5\uff1f\u4f60\u7ed9\u4ed6\u6253\u5f97\u8fd8\u4e0d\u591f\u5417\uff1f\u504f\u8981\u542c\u4fe1\u4ed6\u7684\u8bdd\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7ed9\u5979\u9a82\u5f97\u624b\u8db3\u65e0\u63aa\uff0c\u4e00\u7247\u832b\u7136\u3002\u4ed6\u672c\u6765\u5df2\u6709\u4e03\u516b\u5206\u76f8\u4fe1\u90a3\u53f6\u51b2\u9704\u4e86\uff0c\u4f46\u542c\u5f97 \u8c37\u4e2d\u83b2\u8fd9\u4e48\u4e00\u9a82\uff0c\u5374\u53c8\u4e0d\u7531\u5f97\u60f3\u9053\uff1a\u201c\u5018\u82e5\u5979\u53ea\u662f\u607c\u6068\u54e5\u54e5\u8ba4\u8d3c\u4f5c\u7236\uff0c\u8bf4\u4e0d\u4f1a\u9a82\u4ed6\u4f5c\u91ce \u5c0f\u5b50\uff0c\u54a6\uff0c\u96be\u9053\u53f6\u51b2\u9704\u5f53\u771f\u4e0d\u662f\u5979\u7684\u54e5\u54e5\uff1f\u201d\u5fc3\u91cc\u72d0\u7591\u4e0d\u5b9a\uff0c\u4e0d\u77e5\u96be\u602a\u8c01\u975e\u3002 \u3000\u3000\u4ed6\u5728\u53d7\u4f24\u4e4b\u540e\u66f4\u65bd\u7528\u201c\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\u201d\uff0c\u771f\u6c14\u8017\u635f\u4e0d\u5c11\u3002\u8c37\u4e2d\u83b2\u89c1\u4ed6\u7cbe\u795e\u59d4\u987f\uff0c\u4f24 \u53e3\u8fd8\u5728\u6c69\u6c69\u6d41\u8840\uff0c\u800c\u4ed6\u4e0d\u987e\u672c\u8eab\u7684\u53d7\u4f24\uff0c\u5374\u5148\u6765\u7167\u6599\u81ea\u5df1\uff0c\u4e0d\u7981\u53c8\u662f\u601c\u60dc\uff0c\u53c8\u662f\u611f\u6fc0\uff0c \u867d\u7136\u8fd8\u662f\u6709\u6c14\uff0c\u4f46\u5df2\u7ed9\u601c\u60dc\u4e0e\u611f\u6fc0\u4e4b\u60c5\u62b5\u6d88\u4e86\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5509\uff0c\u4f60\u8fd9\u4f24\u771f\u662f\u53d7\u5f97\u4e0d\u503c\uff0c\u5f85\u6211\u7ed9\u4f60\u5305\u624e\u8d77\u6765\uff0c\u4f60\u597d\u597d\u6b47\u4e00\u6b47\uff0c\u7136\u540e \u6211\u518d\u544a\u8bc9\u4f60\u4e00\u4e9b\u4e8b\u60c5\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5ffd\u9053\uff1a\u201c\u4f46\u6211\u6240\u53d7\u7684\u4f24\uff0c\u5374\u4e5f\u4f3c\u4e4e\u8bc1\u660e\u4e86\u53f6\u51b2\u9704\u5bf9\u6211\u65e0\u751a\u6076\u610f\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a \u201c\u4ed6\u5047\u6d41\u773c\u6cea\uff0c\u9a97\u5f97\u4f60\u76f8\u4fe1\u4ed6\uff0c\u7136\u540e\u4e58\u4f60\u4e0d\u5907\uff0c\u7a81\u65bd\u731b\u88ad\u3002\u8fd9\u8fd8\u4e0d\u7b97\u6076\u610f\uff0c\u8981\u600e\u6837\u624d\u7b97 \u6076\u610f\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4ed6\u773c\u6cea\u662f\u771f\u662f\u5047\uff1f\u7528\u5fc3\u662f\u597d\u662f\u574f\uff0c\u6211\u4e0d\u5f97\u800c\u77e5\uff0c\u4f46\u4ed6\u8fd9\u4e00\u638c\u53ea\u53ef\u8bf4\u662f \u6697\u88ad\uff0c\u5374\u8fd8\u4e0d\u80fd\u8bf4\u662f\u731b\u88ad\u3002\u4ee5\u4ed6\u7684\u5927\u4e58\u822c\u82e5\u638c\u529b\uff0c\u5728\u6211\u6beb\u65e0\u9632\u5907\u4e4b\u4e0b\uff0c\u672c\u6765\u8fd8\u53ef\u4ee5\u628a\u6211 \u4f24\u5f97\u66f4\u91cd\u7684\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54e6\uff0c\u90a3\u4f60\u5c45\u7136\u8fd8\u5728\u611f\u6fc0\u4ed6\u624b\u4e0b\u7559\u60c5\u4e86\u3002\u201d\u8fb9\u8131\u8fb9\u6495\u4e0b\u4e86\u4e00\u5e45\u8863\u886b\uff0c\u53c8 \u627e\u6765\u4e86\u4e00\u4e9b\u9999\u7070\uff0c\u8981\u6765\u66ff\u6c5f\u6d77\u5929\u88f9\u4f24\u3002\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e14\u6162\uff0c\u8fd9\u91cc\u65e2\u7136\u6709\u4e0a\u597d\u7684\u91d1\u521b\u836f\uff0c \u4e3a\u4f55\u4e0d\u62ff\u6765\u4e00\u7528\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u600e\u53ef\u5982\u6b64\u8f7b\u6613\u4fe1\u4eba\uff0c\u7109\u77e5\u8fd9\u4e0d\u662f\u6bd2\u836f\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5018\u82e5\u4ed6\u8981\u6740 \u6211\uff0c\u521a\u624d\u5df2\u7ecf\u6740\u4e86\uff0c\u4f55\u5fc5\u4f7f\u7528\u6bd2\u836f\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4ed6\u4fdd\u7559\u4f60\u7684\u6027\u547d\uff0c\u5fc5\u5b9a\u662f\u53e6\u6709\u6076\u6bd2 \u5fc3\u80a0\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u4ed6\u7684\u7528\u610f\u5982\u4f55\uff0c\u90a3\u5c31\u8981\u770b\u4ee5\u540e\u7684\u4e8b\u60c5\u4e86\uff0c\u4e0d\u7ba1\u600e\u6837\uff0c\u4ed6\u6b64\u523b\u65e2\u8981\u4fdd \u7559\u6211\u7684\u6027\u547d\uff0c\u5c31\u51b3\u4e0d\u81f3\u4e8e\u7528\u6bd2\u836f\u5bb3\u6211\u3002\u201d\u8c37\u4e2d\u83b2\u60f3\u60f3\u4e5f\u6709\u9053\u7406\uff0c\u59d1\u4e14\u8ba9\u4ed6\u6311\u4e00\u70b9\u836f\u818f\u6577 \u4e0a\uff0c\u679c\u7136\u4e00\u7247\u6e05\u51c9\uff0c\u75db\u695a\u5927\u51cf\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4ed6\u4eec\u660e\u5929\u5c31\u8981\u6765\u62ff\u4f60\u53bb\u89c1\u90a3\u4e2a\u56fd\u5e08\u4e86\uff0c\u4f60\u73b0\u5728\u6d41\u8840\u5df2\u6b62\uff0c\u4f46\u5185\u4f24\u672a\u6108\uff0c \u6211\u53c8\u65e0\u529b\u5e87\u62a4\u4f60\uff0c\u670b\u5929\u4e4b\u4e8b\uff0c\u5982\u4f55\u662f\u597d\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u53d7\u7684\u8fd9\u70b9\u5185\u4f24\u5012\u4e0d\u59a8\u4e8b\uff0c\u53ea\u662f\u7eb5\u7136\u6211\u7684\u6b66\u529f\u6062\u590d\uff0c\u597d\u6c49\u4e5f\u6253\u4e0d\u8fc7\u4eba \u591a\u3002\u54ea\u5b9d\u8c61\u6cd5\u5e08\u987e\u5fcc\u6211\u7684\u5e08\u7236\uff0c\u672a\u5fc5\u5c31\u6562\u8981\u4e86\u6211\u7684\u6027\u547d\u3002\u6211\u5012\u662f\u62c5\u5fe7\u4f60\uff0c\u62c5\u5fe7\u6211\u88ab\u4ed6\u4eec \u62bc\u8d70\u4e4b\u540e\uff0c\u4f60\u4e00\u4e2a\u4eba\u5c31\u66f4\u96be\u8131\u9669\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u89c1\u4ed6\u5904\u5904\u987e\u7740\u5979\uff0c\u201c\u5fc3\u4e2d\u5f88\u662f\u611f\u52a8\u3002\u8fc7\u4e86\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6709\u628a\u63e1\u6062\u590d\u6b66\u529f \u5417\uff1f\u90a3\u5c31\u5148\u6cbb\u597d\u4f60\u7684\u5185\u4f24\u518d\u8bf4\u5427\u3002\u54ce\uff0c\u4f60\u8fd9\u4f24\u53ef\u4e0d\u8f7b\u554a\uff01\u201d\u5979\u6495\u5f00\u6c5f\u6d77\u5929\u7684\u5185\u8863\uff0c\u53ea\u89c1 \u80cc\u5fc3\u4e0a\u6709\u4e00\u4e2a\u9ed1\u8272\u7684\u638c\u5370\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u5e08\u7236\u6559\u8fc7\u6211\u8fd0\u6c14\u7597\u4f24\u7684\u6cd5\u5b50\u3002\u201d\u5f53\u4e0b\u76d8\u819d\u9759\u5750\uff0c\u9ed8\u8fd0\u62a4\u4f53\u795e\u529f\uff0c\u5185 \u606f\u6d41\u8f6c\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u679c\u7136\u6c14\u8109\u7545\u901a\uff0c\u7cbe\u795e\u590d\u632f\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u966a\u5750\u4e00\u65c1\uff0c\u89c1\u4ed6\u5934\u9876\u4e0a\u70ed\u6c14\u817e\u817e\uff0c\u90a3\u4e2a\u638c\u5370\u7531\u6d53\u800c\u6de1\uff0c\u7531\u6de1\u800c\u5b8c\u5168\u6d88\u5931\uff0c\u6c5f \u6d77\u5929\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u5927\u4e08\u592b\u5b81\u6b7b\u4e0d\u8fb1\uff0c\u54b1\u4eec\u8981\u6b7b\u4e5f\u6b7b\u5728\u4e00\u8d77\u3002\u660e\u5929\u4ed6\u4eec\u82e5\u662f \u8981\u6765\u6349\u6211\uff0c\u6211\u5c31\u8c41\u4e86\u6027\u547d\uff0c\u548c\u4ed6\u4eec\u518d\u6253\u4e00\u573a\u3002\u201d\u8bd5\u8bd5\u6d3b\u52a8\u624b\u811a\uff0c\u547c\u7684\u4e00\u638c\u6253\u51fa\uff0c\u628a\u9662\u5b50 \u91cc\u7684\u8001\u69d0\u6811\u6253\u5f97\u679d\u53f6\u7eb7\u98de\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u53c8\u60ca\u53c9\u559c\u3001\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u5185\u529f\u6df1\u6e5b\uff0c\u7adf\u6709\u5982\u6b64\u795e\u5947\u7684\u6548\u529b\uff0c\u53ef\u662f\u654c\u4eba\u4e5f \u5f88\u5389\u5bb3\uff0c\u53ea\u51ed\u8840\u6c14\u4e4b\u52c7\uff0c\u4ea6\u975e\u4e0a\u7b56\u554a\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8bf4\u4e0d\u5b9a\u660e\u65e5\u5cf0\u56de\u8def\u8f6c\uff0c\u4fbf\u6709\u8f6c\u673a\u3002\u4f60\u3001\u4f60\u54e5\u54e5\u521a\u624d\u4e0d\u662f\u5df2\u7ecf\u62e6\u963b\u4e86\u90a3 \u4e24\u4e2a\u79c3\u9a74\u5417\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u5df2\u5728\u6697\u4e2d\u4e3a\u54b1\u4eec\u8bbe\u6cd5\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u6120\u9053\uff1a\u201c\u6211\u5bf9\u4f60\u8bf4\u8fc7\u591a\u5c11\u6b21\u4e86\uff0c\u90a3\u65f6\u51b2\u9704\u4e0d\u662f\u6211\u7684\u54e5\u54e5\uff01\u201d\u6c5f\u6d77\u5929\u8d54\u7b11\u9053\uff1a \u201c\u6211\u4e00\u5411\u4ee5\u4e3a\u4ed6\u662f\u4f60\u7684\u54e5\u54e5\uff0c\u8bf4\u60ef\u4e86\u5634\uff0c\u4e00\u65f6\u5fd8\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u8001\u5b9e\u544a\u8bc9\u4f60\u5427\uff0c\u4ed6\u662f\u5192\u5145\u6211\u54e5\u54e5\u7684\u5978\u5f92\uff0c\u5fc3\u672f\u574f\u900f\u4e86\u3002\u4f60\u5207\u4e0d\u53ef\u6307 \u671b\u4ed6\u4f1a\u5bf9\u54b1\u4eec\u6709\u5229\uff0c\u54b1\u4eec\u5fc5\u987b\u9760\u81ea\u5df1\u7684\u529b\u91cf\uff0c\u60f3\u6cd5\u5b50\u8131\u9669\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u662f\u5978\u4eba\uff0c\u540e\u5145\u4f60\u7684\u54e5\u54e5\u5462\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u6c89\u541f\u7247\u523b\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u5916\u4eba\uff0c\u6211\u544a\u8bc9\u4f60\u4e00\u4ef6\u79d8\u5bc6\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4ec0\u4e48\u79d8\u5bc6\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u7684\u7236\u4eb2\u7684\u786e\u662f\u9a6c\u8428\u513f\u56fd\u7684\u524d\u738b\uff0c\u65e5\u95f4\u6211 \u89c1\u4f60\u7684\u65f6\u5019\uff0c\u6211\u8fd8\u6ca1\u6709\u5c3d\u5410\u771f\u60c5\uff0c\u53ea\u662f\u8bf4\u6709\u6b64\u6000\u7591\uff0c\u5176\u5b9e\u8fd9\u6000\u7591\u5df2\u662f\u65e9\u7ecf\u8bc1\u5b9e\u7684\u4e86\u3002\u5f53 \u65f6\u6211\u8fd8\u672a\u6562\u5b8c\u5168\u76f8\u4fe1\u4f60\u3002\u8bf7\u4f60\u539f\u8c05\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u8fd9\u79d8\u5bc6\u6211\u662f\u65e9\u5df2\u77e5\u9053\u7684\u4e86\u3002\u9a6c\u8428\u513f\u56fd\u7684\u524d\u738b\u88ab\u6743\u81e3\u7be1\u4f4d\uff0c\u8d70\u8131\u4e86\u4e00 \u65f6\u5b6a\u751f\u5b50\u5973\uff0c\u8fd9\u4ef6\u4e8b\u662f\u6211\u5e08\u7236\u63a2\u542c\u51fa\u6765\uff0c\u544a\u8bc9\u4f60\u7684\u5e08\u7236\u7684\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4e0d\uff0c\u8fd8\u6709\u53e6\u5916\u7684\u79d8\u5bc6\uff0c\u4f60\u5e08\u7236\u672a\u77e5\u9053\u7684\u3002\u4f60\u5e08\u7236\u5f53\u5e74\u53ea\u662f\u731c\u6d4b\u6211\u53ef\u80fd \u662f\u90a3\u4e2a\u516c\u4e3b\u800c\u5df2\uff0c\u8bc1\u5b9e\u6b64\u4e8b\uff0c\u8fd8\u662f\u4e0d\u4e45\u4ee5\u524d\u7684\u4e8b\u60c5\uff0c\u800c\u4e14\u8fd8\u63ed\u9732\u4e86\u4e00\u4e2a\u79d8\u5bc6\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u505c\u4e86\u4e00\u4e0b\uff0c\u6df1\u60c5\u5730\u671b\u4e86\u6c5f\u6d77\u5929\u4e00\u773c\uff0c\u51b3\u610f\u5bf9\u4ed6\u6beb\u4e0d\u9690\u7792\uff0c\u4e8e\u662f\u7ee7\u7eed\u8bf4\u9053\uff1a \u201c\u6211\u7236\u738b\u65e9\u5df2\u77e5\u9053\u624b\u4e0b\u7684\u5927\u5c06\u6709\u7be1\u4f4d\u4e4b\u5fc3\uff0c\u53ea\u56e0\u4ed6\u7684\u52bf\u529b\u592a\u5927\uff0c\u65e0\u6cd5\u9632\u6b62\u3002\u4ed6\u4e3a\u4e86\u4fdd\u5168 \u6211\u4eec\u4e24\u5144\u59b9\uff0c\u4e71\u4e8b\u4e00\u8d77\uff0c\u5c31\u53eb\u4ed6\u7684\u4e24\u4e2a\u5fc3\u8179\u5ba2\u537f\uff0c\u643a\u5e26\u6211\u4eec\u5206\u5934\u9003\u8d70\uff0c\u8fd9\u4e24\u4e2a\u5ba2\u537f\u5c31\u662f \u4e2d\u725f\u53bf\u7684\u4e18\u5ca9\uff0c\u548c\u9648\u7559\u53bf\u7684\u53f6\u541b\u5c71\u4e86\u3002\u201d \u3000\u3000\u201c\u7236\u738b\u4e5f\u65e9\u5df2\u987e\u8651\u5230\u6211\u4eec\u89c1\u59b9\u4f1a\u5728\u6b64\u6218\u4e71\u4e4b\u4e2d\u5931\u6563\uff0c\u9884\u5148\u7559\u4e0b\u4fe1\u7269\uff0c\u6bcf\u4eba\u4e00\u4ef6\uff0c\u4ee5 \u4fbf\u4ed6\u5e74\u76f8\u8ba4\u3002\u53e6\u5916\u8fd8\u7ed9\u6211\u4eec\u6bcf\u4eba\u7559\u4e0b\u4e86\u4e00\u5f20\u7f8a\u76ae\u4e66\uff0c\u7f8a\u76ae\u4e66\u4e0a\u7684\u6587\u5b57\u6709\u4e09\u5206\u4e4b\u4e8c\u662f\u76f8\u540c \u7684\u3002 \u3000\u3000\u201c\u5929\u9b54\u6559\u4e3b\u4e0a\u9099\u5c71\u95f9\u4e8b\u90a3\u5e74\uff0c\u6211\u5e08\u7236\u53d1\u73b0\u4e86\u90a3\u5f20\u7f8a\u76ae\u4e66\uff0c\u5979\u548c\u91d1\u5927\u4fa0\u90fd\u4e0d\u8ba4\u5f97\u90a3\u4e0a \u9762\u7684\u6587\u5b57\uff0c\u60f3\u53bb\u8bf7\u6559\u9648\u5929\u5b87\uff0c\u9648\u5929\u5b87\u5374\u53c8\u6070\u5de7\u5931\u8e2a\u3002\u201d \u3000\u3000\u201c\u5e08\u7236\u4e3a\u4e86\u63a2\u7d22\u6211\u7684\u8eab\u4e16\u4e4b\u9690\uff0c\u5c06\u6211\u5e26\u5230\u9a6c\u8428\u513f\u56fd\u3002\u5728\u8e0f\u8fdb\u897f\u57df\uff0c\u5c1a\u672a\u5165\u9a6c\u8428\u513f\u56fd \u4e4b\u5883\u4e4b\u524d\uff0c\u5979\u5df2\u77e5\u9053\u90a3\u7f8a\u76ae\u4e66\u4e0a\u7684\u6587\u5b57\uff0c\u662f\u897f\u57df\u51e0\u4e2a\u5c0f\u56fd\u901a\u884c\u7684\u56de\u9e58\u6587\u3002\u5979\u5f53\u7136\u4e0d\u6562\u62ff \u6765\u5411\u4eba\u8bf7\u6559\uff0c\u5979\u60f3\u51fa\u4e86\u4e00\u4e2a\u6cd5\u5b50\uff0c\u5c06\u7f8a\u76ae\u4e66\u4e0a\u7684\u6587\u5b57\uff0c\u4e00\u4e2a\u4e00\u4e2a\u4f9d\u6837\u5199\u4e0b\u6765\uff0c\u5411\u8fd9\u4e00\u4e2a \u4eba\u95ee\u4e00\u4e2a\u5b57\uff0c\u5411\u53e6\u4e00\u4e2a\u4eba\u95ee\u7b2c\u4e8c\u4e2a\u5b57\uff0c\u7ecf\u8fc7\u4e86\u51e0\u4e2a\u6708\u7684\u529f\u592b\uff0c\u7ec8\u4e8e\u628a\u90a3\u4e0a\u9762\u7684\u6587\u5b57\u90fd\u8ba4 \u5f97\u9f50\u5168\u4e86\uff0c\u61c2\u5f97\u4e86\u5176\u4e2d\u7684\u610f\u601d\u3002 \u3000\u3000\u201c\u7f8a\u76ae\u4e66\u5206\u4e3a\u4e09\u4e2a\u90e8\u4efd\uff0c\u7b2c\u4e00\u90e8\u4efd\u662f\u8bf4\u660e\u4e8b\u60c5\u7684\u7ecf\u8fc7\uff0c\u4e5f\u5373\u662f\u6211\u4eec\u5144\u59b9\u7684\u8eab\u4e16\u6765\u5386 \u4e86\uff0c\u7b2c\u4e8c\u90e8\u4efd\u662f\u7559\u4e0b\u590d\u56fd\u7684\u8ba1\u5212\uff0c\u5217\u660e\u56fd\u4e2d\u6709\u54ea\u4e9b\u4eba\u662f\u5fe0\u81e3\uff0c\u5176\u4e2d\u53c8\u6709\u54ea\u51e0\u4e2a\u662f\u51c6\u5907\u63a9 \u85cf\u81ea\u5df1\u7684\u8eab\u518c\uff0c\u4f2a\u4f5c\u6295\u987a\u65b0\u738b\u7684\u3002\u8fd8\u6709\u4ed6\u5386\u5e74\u57cb\u85cf\u7684\u91d1\u94f6\u73e0\u5b9d\uff0c\u8fd9\u4e2a\u79d8\u5bc6\u7684\u6240\u5728\u4e5f\u5728\u7f8a \u76ae\u4e66\u4e0a\u8be6\u7ec6\u5199\u660e\uff0c\u53eb\u6211\u4eec\u5c06\u6765\u53d1\u6398\u51fa\u6765\uff0c\u4f5c\u4e3a\u62db\u5175\u4e70\u9a6c\u4e4b\u7528\u3002 \u3000\u3000\u201c\u7b2c\u4e09\u90e8\u4efd\u6700\u4e3a\u5947\u602a\uff0c\u5374\u662f\u534a\u7bc7\u6b66\u529f\u79d8\u5178\uff0c\u4e66\u4e0a\u8bf4\u660e\uff0c\u6211\u54e5\u54e5\u4e5f\u6709\u8fd9\u6837\u4e00\u5f20\u7f8a\u76ae\u4e66\uff0c \u524d\u9762\u4e24\u90e8\u4efd\u76f8\u540c\uff0c\u540e\u9762\u8fd9\u90e8\u4efd\u4e0d\u540c\uff0c\u90a3\u53e6\u534a\u7bc7\u6b66\u529f\u79d8\u5178\u5728\u6211\u54e5\u54e5\u7684\u90a3\u5f20\u7f8a\u76ae\u4e66\u4e0a\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u770b\u6765\uff0c\u4f60\u7684\u7236\u4eb2\u8fd8\u662f\u4e2a\u5f88\u4e0d\u5bfb\u5e38\u7684\u56fd\u7389\u5462\uff01\u62e5\u6709\u91d1\u94f6\u73e0\u5b9d\u4e4b\u5916\uff0c \u8fd8\u73cd\u85cf\u7740\u6b66\u529f\u79d8\u5178\uff0c\u4f46\u5374\u4e3a\u4f55\u53ea\u7ed9\u4f60\u534a\u7bc7\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8fd9\u7bc7\u79d8\u5178\uff0c\u636e\u8bf4\u662f\u51e0\u767e\u5e74\u524d\uff0c\u6709\u4e2a\u6b66\u6797\u4eba\u7269\u9003\u5230\u6211\u56fd\u907f\u7978\uff0c\u56e0\u611f\u5148\u8bc5 \u4f8d\u4ed6\u6069\u539a\u3002\u7559\u4e0b\u6765\u7684\u3002\u5f53\u5bf9\u5f97\u5230\u8fd9\u7bc7\u79d8\u5178\u7684\u6211\u90a3\u4f4d\u7956\u5148\uff0c\u8fd8\u53ea\u662f\u4e00\u4e2a\u90e8\u843d\u4e4b\u4e3b\uff0c\u540e\u6765\u7ec3 \u6210\u6b66\u827a\uff0c\u90e8\u843d\u4e5f\u5f3a\u76db\u8d77\u6765\uff0c\u7ec8\u4e8e\u5efa\u7acb\u4e86\u9a6c\u8428\u513f\u56fd\uff0c\u505a\u4e86\u56fd\u738b\u3002\u53ef\u60dc\u540e\u6765\u7684\u56fd\u738b\uff0c\u5927\u534a\u6ca1 \u6709\u6052\u5fc3\u7ec3\u6b66\u3001\u4e00\u8fd9\u7bc7\u79d8\u5178\uff0c\u4e5f\u5c31\u5c18\u5c01\u5728\u5185\u5e93\u4e4b\u4e2d\u4e86\u3002 \u3000\u3000\u201c\u6211\u7684\u7236\u4eb2\u6284\u4e0b\u526f\u672c\uff0c\u7ed9\u6211\u4eec\u5144\u59b9\u6bcf\u4eba\u534a\u7bc7\uff0c\u90a3\u662f\u5e0c\u671b\u6211\u4eec\u5144\u59b9\u4f1a\u5408\u4e4b\u540e\uff0c\u540c\u7ec3\u8fd9 \u79d8\u5178\u4e0a\u7684\u6b66\u529f\uff0c\u597d\u7ed9\u4ed6\u62a5\u4ec7\u7684\u3002\u540c\u65f6\u8fd9\u4e5f\u53ef\u4f5c\u4e3a\u6211\u4eec\u5144\u59b9\u76f8\u8ba4\u7684\u53c8\u4e00\u4ef6\u4fe1\u7269\u3002 \u3000\u3000\u201c\u6211\u5e08\u7236\u770b\u8fc7\u8fd9\u534a\u7bc7\u6b66\u529f\u79d8\u5178\uff0c\u636e\u5979\u8bf4\u4e0e\u4e2d\u571f\u7684\u6b66\u5b66\u5927\u4e0d\u76f8\u540c\uff0c\u5176\u4e2d\u9887\u6709\u4e00\u4e9b\u5965\u5999 \u7684\u5730\u65b9\u3002\u4f46\u6211\u4eec\u6ca1\u6709\u65f6\u95f4\u7ec3\u8fd9\u4e0a\u9762\u7684\u6b66\u529f\uff0c\u53ea\u597d\u7559\u5f85\u5c06\u6765\u518d\u8bf4\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u8bf4\u81f3\u6b64\uff0c\u505c\u4e86\u4e00\u4e0b\uff0c\u5fae\u7b11\u95ee\u6c5f\u6d77\u5929\uff1a\u201c\u73b0\u5728\u4f60\u60f3\u5fc5\u77e5\u9053\u6211\u4f55\u4ee5\u4f1a\u8bc6\u7834\u90a3\u53f6\u51b2 \u9704\u662f\u5047\u5192\u7684\u4e86\u5427\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4ed6\u6ca1\u6709\u4f60\u7236\u4eb2\u6240\u7559\u7684\u4fe1\u7269\uff0c\u4e5f\u6ca1\u6709\u8fd9\u5f20\u7f8a\u76ae\u4e66\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5982\u679c\u4ed6\u771f\u662f\u6211\u7684\u54e5\u54e5\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u63d0\u8d77\u8fd9\u4e24\u4ef6\u4fe1\u7269\u7684\u3002\u4f46\u4ed6\u5374\u8f6c \u5f2f\u62b9\u89d2\u7684\u6765\u5957\u53d6\u6211\u7684\u53e3\u98ce\uff0c\u6211\u5f53\u7136\u77e5\u9053\u4ed6\u662f\u5047\u5192\u7684\u4e86\u3002\u6211\u4eec\u5144\u59b9\u4e8c\u4eba\uff0c\u4e00\u4e2a\u662f\u4e2d\u725f\u53bf\u4e18 \u5ca9\u629a\u517b\uff0c\u4e00\u4e2a\u662f\u9648\u7559\u53bf\u7684\u53f6\u541b\u5c71\u629a\u517b\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u4ed6\u5927\u7ea6\u662f\u65e9\u5df2\u8c03\u67e5\u6e05\u695a\u7684\u4e86\uff0c\u4ed6\u5c31\u81ea\u8ba4 \u662f\u53f6\u541b\u5c71\u6240\u629a\u517b\u7684\u90a3\u4f4d\u5b64\u513f\uff0c\u53c8\u51ed\u7740\u76f8\u5269\u548c\u6211\u6709\u51e0\u5206\u76f8\u4f3c\uff0c\u5c31\u60f3\u6211\u76f8\u4fe1\u4ed6\u662f\u6211\u7684\u54e5\u54e5\uff0c \u6211\u5c82\u4f1a\u4e0a\u4ed6\u7684\u5f53\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u90a3\u53f6\u51b2\u9704\u786e\u5b9e\u662f\u4e2a\u5978\u8bc8\u5c0f\u4eba\u4e86\u3002\u597d\uff0c\u660e\u5929\u4ed6\u518d\u6765\u9a97\u6211\uff0c\u6211 \u5c31\u548c\u4ed6\u62fc\u4e2a\u6b7b\u8bdd\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4f60\u7eb5\u7136\u80dc\u5f97\u4e86\u53f6\u51b2\u9704\uff0c\u4e5f\u7edd\u96be\u80dc\u8fc7\u4ed6\u548c\u90a3\u4e24\u4e2a\u548c\u5c1a\u8054\u624b\u3002\u4f60\u8981 \u62fc\u6211\u4e0d\u53cd\u5bf9\uff0c\u4f46\u603b\u5f97\u6709\u4e03\u516b\u5206\u628a\u63e1\u624d\u6210\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6447\u5934\u9053\uff1a\u201c\u8fd9\u53ef\u96be\u4e86\uff0c\u6211\u7684\u6b66\u529f\u600e\u80fd\u5728\u4e00\u591c\u4e4b\u95f4\u589e\u957f\u4e00\u500d\u4e86\u9664\u975e\u6211\u518d\u7528\u5929\u9b54 \u89e3\u4f53\u5927\u6cd5\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u90a3\u4e0d\u597d\u3002\u6211\u542c\u5e08\u7236\u8bf4\u8fc7\uff0c\u8fd9\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\u6700\u4e3a\u6d88\u8017\u771f\u5143\uff0c\u5389\u80dc\u7537\u5f53\u5e74 \u5c31\u662f\u56e0\u4e3a\u7528\u4e86\u6b64\u6cd5\u6597\u8d62\u4e86\u5929\u5c71\u6d3e\u638c\u95e8\u5510\u5927\u4fa0\uff0c\u5f53\u5929\u665a\u4e0a\uff0c\u5979\u81ea\u5df1\u5c31\u6b7b\u4e86\u3002\u6211\u4e0d\u51c6\u4f60\u518d\u7528 \u8fd9\u79cd\u90aa\u6cd5\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u90a3\u6211\u5c31\u5b8c\u5168\u6ca1\u6709\u628a\u63e1\u8d62\u5f97\u654c\u4eba\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u51dd\u601d\u7247\u523b\uff0c\u5ffd\u9053\uff1a\u201c\u6d77\u5929\uff0c\u6709\u4e00\u4e2a\u6cd5\u5b50\u3002\u867d\u7136\u4e5f\u662f\u6709\u70b9\u5192\u9669\uff0c\u4f46\u7a76\u7adf\u8981\u6bd4\u7528 \u5929\u9b54\u89e3\u4f53\u5927\u6cd5\u597d\u5f97\u591a\uff0c\u4f60\u53ef\u613f\u610f\u8bd5\u4e00\u8bd5\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u54b1\u4eec\u88ab\u56f0\u5b64\u5c9b\uff0c\u53cd\u6b63\u662f\u65e0\u6cd5\u53ef\u60f3\u7684\u4e86\u3002\u201d\u6211\u6b7b\u4e14\u4e0d\u60e7\uff0c\u4f55\u6015\u5192\u9669\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u597d\uff0c\u4f60\u80cc\u8f6c\u8eab\u5b50\uff0c\u95ed\u4e0a\u773c\u775b\u3002\u201d\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u4f60\u5728\u53d8\u620f\u6cd5\u5417\uff1f\u201d\u8c37 \u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u7ba1\uff0c\u6211\u53eb\u4f60\u7741\u5f00\u773c\u775b\u65f6\uff0c\u4f60\u518d\u8f6c\u8fc7\u8eab\u6765\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6ee1\u6000\u7eb3\u7f55\uff0c\u53ea\u597d\u542c\u5979\u7684\u5429\u5490\uff0c\u8c37\u4e2d\u83b2\u89e3\u5f00\u8863\u88f3\uff0c\u6362\u4e86\u4e00\u4ef6\u8d34\u8eab\u7684\u6c57\u886b\uff0c\u6536\u62fe \u505c\u5f53\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u7741\u5f00\u773c\u775b\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u8f6c\u8fc7\u8eab\u6765\uff0c\u53ea\u89c1\u8c37\u4e2d\u83b2\u628a\u53f3\u638c\u644a\u5f00\uff0c\u8bf4\u9053\uff1a \u201c\u4f60\u5927\u7ea6\u4e0d\u8ba4\u5f97\u8fd9\u662f\u4ec0\u4e48\u4e1c\u897f\u5427\uff1f\u201d \u3000\u3000\u53ea\u89c1\u5979\u7684\u624b\u638c\u6709\u4e03\u9897\u7070\u767d\u8272\u7684\u4f3c\u662f\u9aa8\u8d28\u7684\u94ae\u6263\uff0c\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u628a\u8863\u670d\u4e0a\u7684 \u94ae\u6263\u6458\u4e0b\u6765\u4e86\uff1f\u6211\u8fd8\u5f53\u662f\u4ec0\u4e48\u5b9d\u8d1d\u5462\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u7b11\u5f97\u6253\u8dcc\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u771f\u662f\u6709\u773c\u4e0d\u8bc6\u5b9d\u8d1d\uff01\u201d\u6c5f\u6d77\u5929\u5947\u9053\uff1a\u201c\u5f53\u771f\u662f\u5b9d\u8d1d\uff1f\u201d \u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4ec0\u4e48\u5b9d\u8d1d\u90fd\u6bd4\u4e0d\u4e0a\u5b83\uff0c\u8fd9\u662f\u6b66\u6797\u4eba\u58eb\u68a6\u5bd0\u4ee5\u6c42\uff0c\u96be\u5f97\u4e00\u89c1\u7684\u5929\u5fc3\u77f3\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u542c\u5979\u8bf4\u5f97\u5982\u6b64\u90d1\u91cd\uff0c\u534a\u4fe1\u534a\u7591\uff0c\u95ee\u9053\uff1a\u201c\u7136\u5219\u5b83\u5b58\u4ec0\u4e48\u7528\u5904\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a \u201c\u5c06\u8fd9\u5929\u5fc3\u77f3\u7c89\u788e\uff0c\u548c\u9152\u670d\u4e0b\uff0c\u6bcf\u4e00\u9897\u53ef\u4ee5\u589e\u957f\u5341\u5e74\u529f\u529b\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8be7\u9053\uff1a\u201c\u6709\u8fd9\u6837\u795e\u5947\u7684\u6548\u529b\uff01\u4f60\u600e\u6837\u5f97\u6765\u7684\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5728\u6211\u9003\u96be\u7684\u65f6 \u5019\u3001\u7236\u4eb2\u7ed9\u6211\u7a7f\u4e0a\u4e00\u4ef6\u68c9\u7946\u3002\u68c9\u8884\u4e0a\u90a3\u4e03\u7c92\u94ae\u6263\uff0c\u539f\u6765\u90fd\u662f\u5929\u5fc3\u77f3\uff01\u81f3\u4e8e\u4ed6\u662f\u600e\u6837\u5f97\u6765 \u7684\uff0c\u6211\u53ef\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u53c8\u9053\uff1a\u201c\u5929\u5fc3\u77f3\u5728\u9633\u5149\u4e4b\u4e0b\uff0c\u77f3\u4e2d\u4f1a\u6cdb\u51fa\u7ea2\u6655\uff0c\u6211\u5e08\u7236\u5f53\u5e74\u53d1\u73b0\u8fd9\u4e2a\u5947\u8ff9\uff0c \u5c1a\u672a\u77e5\u9053\u8fd9\u662f\u6b66\u6797\u5f02\u5b9d\u3002\u540e\u6765\u5411\u6c5f\u5357\u533b\u9690\u53f6\u9038\u82cd\u8bf7\u6559\uff0c\u8fd9\u624d\u77e5\u9053\u662f\u5929\u5fc3\u77f3\u3002\u636e\u8bf4\u5929\u5fc3\u77f3 \u53ea\u5728\u6606\u4ed1\u5c71\u7684\u661f\u5bbf\u6d77\u624d\u6709\u3002\u4e00\u6765\u7531\u4e8e\u6606\u4ed1\u7edd\u9876\uff0c\u4eba\u6240\u96be\u4e0a\uff1b\u4e8c\u6765\u7531\u4e8e\u6606\u4ed1\u5c71\u57a6\u5bbf\u7c3f\u4e0a\uff0c \u4f3c\u8fd9\u822c\u5f62\u72b6\u7684\u77f3\u5b50\uff0c\u6052\u6cb3\u6c99\u6570\uff0c\u5fc5\u987b\u6709\u8bc6\u5f97\u6b64\u5b9d\u4e4b\u4eba\uff0c\u4e00\u9897\u4e00\u9897\u7684\u5728\u9633\u5149\u4e4b\u4e0b\u68c0\u9a8c\uff0c\u65b9 \u80fd\u5728\u5343\u4e07\u9897\u77f3\u5b50\u4e4b\u4e2d\uff0c\u627e\u51fa\u4e00\u9897\u5929\u5fc3\u77f3\u6765\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u6bd4\u62ab\u6c99\u62e3\u91d1\u8fd8\u8981\u96be\u4e86\uff01\u6709\u8bc6\u5f97\u6b64\u5b9d\u7684\u4eba\uff0c\u4e5f\u672a\u5fc5\u6709\u6b64\u6052\u5fc3\u3002\u201d \u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u82e5\u975e\u5982\u6b64\u96be\u5f97\uff0c\u5b83\u8fd8\u7b97\u5f97\u5b9d\u8d1d\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u559c\u9053\uff1a\u201c\u65e2\u6709\u5982\u6b64\u5b9d\u8d1d\uff0c\u4f60\u4e3a\u4f55\u4e0d\u4f9d\u65b9\u670d\u4e0b\uff0c\u4e00\u9897\u53ef\u4ee5\u62b5\u5f97\u5341\u5e74\u529f\u529b\uff0c\u54c8\uff0c \u90a3\u4f60\u670d\u4e0b\u4e86\u8fd9\u4e03\u9897\u5929\u5fc3\u77f3\uff0c\u5c82\u975e\u5f53\u4e16\u65e0\u654c\uff0c\u8fd8\u6015\u4ec0\u4e48\u53f6\u51b2\u9704\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u82e5\u7136\u6211\u53ef\u4ee5\u670d\u5f97\uff0c\u6211\u8fd8\u7528\u5f97\u7740\u4f60\u6307\u6559\u5417\uff1f\u4f60\u6709\u6240\u4e0d\u77e5\uff0c\u8fd9\u5929\u5fc3\u77f3\u529f \u6548\u795e\u5947\uff0c\u4f46\u4e5f\u542b\u6709\u5f3a\u70c8\u7684\u6bd2\u6027\u3002\u5fc5\u987b\u5185\u529f\u6781\u4e3a\u6df1\u539a\u7684\u4eba\uff0c\u670d\u98df\u4e4b\u540e\uff0c\u624d\u53ef\u4ee5\u62b5\u6321\u5f97\u4f4f\u6bd2 \u6027\uff0c\u82e5\u662f\u529f\u529b\u7a0d\u5dee\u7684\u4eba\uff0c\u670d\u4e86\u975e\u4f46\u4e0d\u89c1\u5176\u5229\uff0c\u53cd\u89c1\u5176\u5bb3\uff0c\u751a\u81f3\u4f1a\u4e03\u7a8d\u6d41\u8840\u800c\u4ea1\uff0c\u56e0\u6b64\uff0c \u5185\u529f\u5018\u82e5\u65e9\u5df2\u5230\u4e86\u4e0a\u4e58\u5883\u754c\u7684\u4eba\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u8d2a\u56fe\u6b64\u5b9d\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u8ba9\u6211\u8bd5\u4e00\u8bd5\u5427\u3002\u6211\u7684\u9648\u53d4\u53d4\u4ee5\u524d\u5728\u51b0\u5bab\u4e2d\u4e5f\u66fe\u5728\u65e0\u610f\u4e2d\u670d\u8fc7 \u4e00\u679a\u5f02\u679c\uff0c\u5f53\u65f6\u96be\u53d7\u5f97\u5f88\uff0c\u4f46\u8fc7\u4e86\u7247\u523b\uff0c\u4e5f\u5c31\u6ca1\u4e8b\u4e86\u3002\u6211\u7684\u5185\u529f\u867d\u7136\u4e0d\u6562\u8bf4\u5df2\u5230\u4e86\u4e0a\u4e58 \u5883\u754c\uff0c\u4f46\u4e5f\u7ec3\u6709\u62a4\u4f53\u795e\u529f\uff0c\u6bd4\u5f53\u5e74\u7684\u9648\u53d4\u53d4\u603b\u80dc\u8fc7\u4e00\u7b79\uff0c\u5929\u5fc3\u77f3\u7684\u6bd2\u6027\u7eb5\u7136\u6bd4\u51b0\u5bab\u5f02\u679c \u5389\u5bb3\uff0c\u6599\u60f3\u4e5f\u53ef\u65e0\u59a8\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u5c31\u662f\u89c1\u4f60\u5185\u529f\u6df1\u539a\uff0c\u6240\u4ee5\u624d\u60f3\u5230\u8981\u8ba9\u4f60\u8bd5\u4e00\u8bd5\u7684\u3002\u201d\u8c37\u4e2d\u83b2\u6240\u4f4f\u7684 \u5730\u65b9\u672c\u662f\u56fd\u738b\u7684\u590f\u5bab\uff0c\u5f53\u7136\u85cf\u6709\u8bb8\u591a\u7f8e\u9152\uff0c\u8c37\u4e2d\u83b2\u6253\u5f00\u4e86\u4e00\u6a3d\u9648\u5e74\u8001\u9152\uff0c\u62d4\u4e0b\u5934\u4e0a\u7684\u94f6 \u7c2a\u8bd5\u4e86\u4e00\u8bd5\uff0c\u94f6\u7c2a\u6ca1\u6709\u53d8\u8272\uff0c\u77e5\u9053\u6ca1\u6709\u6bd2\uff0c\u5c31\u653e\u5fc3\u4ea4\u7ed9\u4e86\u6c5f\u6d77\u5929\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u5148\u670d\u4e00\u9897\u8bd5\u8bd5\u3002\u201d\u6c5f\u6d77\u5929\u7528\u91d1\u94a2\u6307\u65b9\uff0c\u634f\u788e\u4e86\u4e00\u9897\u5929\u5fc3\u77f3\uff0c\u51b2\u9152\u670d \u4e0b\uff0c\u53ea\u89c9\u4e00\u80a1\u70ed\u6c14\uff0c\u51b2\u4e0a\u5fc3\u5934\uff0c\u7a0d\u5fae\u6709\u70b9\u96be\u53d7\uff0c\u4f46\u4e5f\u5e76\u4e0d\u600e\u4e48\u3002\u4ed6\u5e76\u975e\u8d2a\u56fe\u7075\u4e39\u5999\u836f\uff0c \u4f46\u4ed6\u4e00\u5fc3\u60f3\u52a9\u8c37\u4e2d\u83b2\u8131\u9669\uff0c\u751f\u6015\u836f\u529b\u4e0d\u591f\uff0c\u529f\u529b\u589e\u957f\u6709\u9650\uff0c\u4fbf\u4e0d\u80fd\u6253\u8d25\u654c\u4eba\u3002\u56e0\u6b64\u5f53\u8c37 \u4e2d\u83b2\u95ee\u4ed6\u611f\u89c9\u5982\u4f55\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u6545\u610f\u8bf4\u9053\uff1a\u201c\u6709\u70b9\u751c\u5473\uff0c\u5f88\u597d\u5403\u5462\u3002\u4f60\u518d\u7ed9\u6211\u51e0\u9897\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u53ea\u9053\u4ed6\u529f\u529b\u6df1\u6e5b\uff0c\u8db3\u4ee5\u514b\u5236\u836f\u6027\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u53c8\u7ed9\u4ed6\u4e24\u9897\uff0c\u7b11\u9053\uff1a\u201c\u5018\u82e5\u5173 \u4e8e\u5929\u5fc3\u77f3\u7684\u4f20\u8bf4\u4e43\u662f\u771f\u7684\uff0c\u4f60\u5c31\u53ef\u4ee5\u589e\u957f\u4e09\u5341\u5e74\u7684\u529f\u529b\u4e86\u3002\u5f53\u4eca\u4e4b\u4e16\uff0c\u53ea\u6015\u9664\u4e86\u4f60\u7684\u5e08 \u7236\u4e4b\u5916\uff0c\u5c31\u6ca1\u6709\u8c01\u53ef\u4ee5\u4e0e\u4f60\u6297\u624b\u4e86\u3002\u6211\u53ea\u662f\u6015\u591a\u670d\u4e8e\u4f60\u6709\u5bb3\uff0c\u4f60\u522b\u8bef\u4f1a\u6211\u820d\u4e0d\u5f97\u5c3d\u6570\u7ed9 \u4f60\u3002\u201d\u6c5f\u6d77\u5929\u6e10\u6e10\u89c9\u5f97\u6709\u70b9\u70ed\uff0c\u7b11\u9053\uff1a\u201c\u591f\u4e86\uff0c\u591f\u4e86\u3002\u8fd9\u6837\u7684\u5b9d\u8d1d\uff0c\u6211\u4e00\u53e3\u6c14\u541e\u4e86\u4f60\u7684 \u4e09\u9897\u4e4b\u591a\uff0c\u5df2\u7ecf\u5f88\u8fc7\u610f\u4e0d\u53bb\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8bd5\u8bd5\u6d3b\u52a8\u624b\u8db3\uff0c\u4e00\u62f3\u5411\u77f3\u67f1\u6253\u53bb\u3002\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u77f3\u67f1\u7ed9\u4ed6\u6253\u5f97\u51f9\u4e0b \u4e00\u5757\uff0c\u77f3\u5c51\u7eb7\u98de\uff0c\u8c37\u4e2d\u83b2\u559c\u9053\uff1a\u201c\u8fd9\u5929\u5fc3\u77f3\u4f2a\u6548\u529b\u679c\u7136\u795e\u5947\uff0c\u5e78\u4e8f\u6211\u6ca1\u6709\u7ed9\u4ed6\u4eec\u641c\u53bb\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8fd9\u65f6\u5df1\u611f\u5230\u8eab\u4f53\u53d1\u6eda\uff0c\u6c57\u6c34\u5f00\u59cb\u6dcc\u4e0b\uff0c\u8c37\u4e2d\u83b2\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u6c5f\u6d77\u5929\u6015\u5979\u62c5\u5fc3\uff0c\u6545\u4f5c\u795e\u8272\u81ea\u5982\uff0c\u7b11\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u7a0d\u5fae\u6709\u70b9\u53d1\u70ed\u3002\u201d\u4ed6\u6709\u610f\u9017\u8c37\u4e2d\u83b2 \u8bf4\u8bdd\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u90a3\u4ef6\u5c0f\u68c9\u8884\u662f\u5b69\u5b50\u7a7f\u7684\uff0c\u4ed6\u4eec\u600e\u4e48\u6ca1\u6709\u6ce8\u610f\uff0c\u8ba9\u4f60\u4fdd\u5b58\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u90a3\u4ef6\u68c9\u8884\u6ca1\u6709\u4ec0\u4e48\uff0c\u91cd\u8981\u7684\u662f\u5185\u85cf\u7684\u7f8a\u76ae\u4e66\u548c\u90a3\u4e03\u9897\u94ae\u6263\uff0c\u6211\u5f53\u7136\u4e0d \u4f1a\u643a\u5e26\u5c0f\u5b69\u5b50\u7a7f\u7684\u68c9\u8884\u5f15\u4eba\u6ce8\u610f\uff0c\u6211\u65e9\u5df2\u628a\u90a3\u5f20\u7f8a\u76ae\u4e66\u548c\u4e03\u9897\u5929\u5fc3\u77f3\u94ae\u6263\u90fd\u9664\u4e86\u4e0b\u6765\uff0c \u94ae\u6263\u9489\u5728\u6211\u7684\u6c57\u8863\u4e0a\uff0c\u7f8a\u76ae\u4e66\u85cf\u5728\u6211\u7684\u5f13\u978b\u5185\uff0c\u8fd9\u978b\u5b50\u662f\u5939\u5c42\u7684\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u4f60\u771f\u806a\u660e\uff0c\u82e5\u679c\u662f\u6211\uff0c\u5c31\u60f3\u4e0d\u51fa\u8fd9\u6837\u5999\u6cd5\u3002\u201d\u4ed6\u7b11\u58f0\u5636\u54d1\u3002\u542c\u5728\u81ea \u5df1\u7684\u8033\u6735\u91cc\uff0c\u4e5f\u89c9\u523a\u8033\u975e\u5e38\uff0c\u5b8c\u5168\u4e0d\u4f3c\u81ea\u5df1\u5e73\u65e5\u7684\u58f0\u97f3\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u53eb\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u4f60\u4e00\u5b9a\u662f\u751f\u75c5\u4e86\u3002\u201d\u7528\u624b\u4e00\u6478\u4ed6\u7684\u989d\u89d2\uff0c\u53ea\u89c9\u706b\u70e7\u4e00\u822c\u7684\u70eb \u624b\uff0c\u70ed\u5ea6\u9ad8\u5f97\u60ca\u4eba\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u72b9\u81ea\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u6ca1\u4e8b\uff0c\u6ca1\u4e8b\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5df2\u662f\u652f\u6301\u4e0d\u4f4f\uff0c\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u4eac\u9053\uff1a\u201c\u4f60\u5feb\u8fd0\u62a4\u4f53\u795e\u529f\uff01\u201d\u54ea\u77e5\u4e0d\u8fd0\u795e\u529f\u8fd8\u597d\u4e00\u4e9b\uff0c\u4e00\u8fd0\u795e\u529f\uff0c\u66f4\u662f\u5168\u8eab \u53d1\u6eda\uff0c\u70ed\u5f97\u96be\u7981\uff0c\u539f\u6765\u8fd9\u5929\u5fc3\u77f3\u4e43\u662f\u836f\u6027\u6781\u70ed\u4e4b\u7269\uff0c\u4e14\u6c5f\u6d77\u5929\u6240\u8fd0\u7684\u795e\u529f\u53c8\u6b63\u662f\u7eaf\u9633\u4e4b \u6c14\uff0c\u7b49\u4e8e\u706b\u4e0a\u6dfb\u6cb9\uff01 \u3000\u3000\u836f\u529b\u53d1\u4f5c\uff0c\u4e24\u4e0b\u5939\u653b\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u6c5f\u6d77\u5929\u5df2\u53d1\u70e7\u5f97\u8ff7\u8ff7\u7cca\u7cca\uff0c\u53ea\u6709\u5598\u6c14\u7684\u4efd\u513f\uff01\u4ed6 \u6240\u547c\u51fa\u7684\u6c14\u606f\uff0c\u4e5f\u662f\u707c\u70ed\u9a87\u4eba\uff0c\u4e00\u547c\u51fa\u6765\uff0c\u4e0e\u5916\u9762\u7684\u51b7\u7a7a\u6c14\u63a5\u89e6\uff0c\u7acb\u5373\u51dd\u6210\u4e00\u9897\u9897\u7684\u6c34 \u73e0\uff0c\u6ef4\u5728\u8c37\u4e2d\u83b2\u7684\u624b\u4e0a\uff0c\u8fde\u6c34\u73e0\u4e5f\u662f\u70ed\u7684\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u675f\u624b\u65e0\u7b56\uff0c\u96be\u8fc7\u4e4b\u6781\uff0c\u62b1\u7740\u6c5f\u6d77\u5929\u60b2\u58f0\u8bf4\u9053\uff1a\u201c\u65e9\u77e5\u5982\u6b64\uff0c\u4e0d\u8bd5\u8fd8\u597d\uff0c\u90fd\u662f \u6211\u5bb3\u4e86\u4f60\uff01\u201d \u3000\u3000\u5ffd\u542c\u5f97\u201c\u8f67\u8f67\u201d\u58f0\u54cd\uff0c\u5bf9\u9762\u7684\u5899\u58c1\u7a81\u7136\u88c2\u5f00\uff0c\u73b0\u51fa\u4e00\u9053\u9053\u95e8\uff0c\u4e00\u4e2a\u5996\u8273\u7684\u5973\u4eba\u8d70\u4e86 \u51fa\u6765\uff0c\u6b63\u662f\u5929\u9b54\u6559\u4e3b\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5a07\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u522b\u60ca\u614c\uff0c\u6211\u662f\u6765\u5e2e\u5fd9\u4f60\u7684\uff0c\u5e2e\u5fd9\u4f60\u8bbe\u6cd5\u6551\u4ed6\u3002\u201d\u5929\u9b54\u6559\u4e3b \u4e0a\u9099\u5c71\u95f9\u4e8b\u90a3\u5e74\uff0c\u8c37\u4e2d\u83b2\u66fe\u89c1\u8fc7\u5979\uff0c\u4f9d\u7a00\u8fd8\u8ba4\u5f97\u5979\u7684\u76f8\u8c8c\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u53eb\u9053\uff1a \u201c\u4f60\uff0c\u4f60\u4e0d\u662f\u5929\u9b54\u6559\u4e3b\u5417\uff1f\u4f60\u6709\u8fd9\u6837\u597d\u5fc3\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u96be\u4e3a\u4f60\u8fd8\u8bb0\u5f97\u6211\u3002\u6211\u9001\u4ed6\u4e0e\u4f60\u4f1a\u9762\uff0c\u6b63\u662f\u4e00\u7247\u597d\u5fc3\uff0c\u8c01\u77e5 \u4f60\u628a\u6211\u4e5f\u5f53\u4f5c\u654c\u4eba\uff0c\u7ed9\u4ed6\u8bef\u670d\u4e86\u5929\u5fc3\u77f3\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u80fd\u591f\u6551\u4ed6\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u628a\u4f60\u7684\u7f8a\u76ae\u4e66\u548c\u5929\u5fc3\u77f3\u7ed9\u6211\uff0c\u6211\u518d\u8bbe \u6cd5\u6551\u4ed6\uff01\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u8981\u8fd9\u4e24\u4ef6\u4e1c\u897f\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u7684 \u79d8\u5bc6\u6211\u5168\u90fd\u77e5\u9053\u4e86\uff0c\u4f60\u4e5f\u4e0d\u7528\u5bf9\u6211\u9690\u7792\u4e86\uff0c\u8fd9\u4e24\u4ef6\u4e1c\u897f\u5bf9\u4f60\u6709\u635f\u65e0\u76ca\uff0c\u5feb\u5feb\u62ff\u6765\u7ed9\u6211\uff01\u201d \u3000\u3000\u539f\u6765\u5c06\u6c5f\u6bcf\u5929\u9001\u5230\u6b64\u95f4\uff0c\u6b63\u662f\u5929\u9b54\u6559\u4e3b\u5b89\u6392\u7684\u8be1\u8ba1\uff0c\u4ed6\u5df2\u77e5\u9053\u53f6\u51b2\u9704\u65e0\u6cd5\u5957\u53d6\u8c37\u4e2d \u83b2\u7684\u79d8\u5bc6\uff0c\u8981\u5979\u5410\u9732\u79d8\u5bc6\uff1b\u201c\u9664\u975e\u662f\u8ba9\u5979\u5355\u72ec\u5bf9\u7740\u5979\u6240\u6700\u76f8\u4fe1\u7684\u4eba\u3002\u8fd9\u590f\u5bab\u4e2d\u5230\u5904\u662f\u673a \u5173\u548c\u6697\u5ba4\uff0c\u5979\u548c\u5979\u7684\u4e00\u7fa4\u4f8d\u5a62\u65e9\u5df2\u85cf\u5728\u91cc\u9762\uff1b\u5bf9\u6c5f\u6d77\u706d\u4e0e\u8c37\u4e2d\u83b2\u7684\u4e00\u8a00\u4e00\u8bed\uff0c\u90fd\u542c\u5f97\u6e05 \u6e05\u695a\u695a\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u867d\u7136\u8ff7\u8ff7\u7cca\u7cca\uff0c\u795e\u667a\u8fd8\u672a\u5b8c\u5168\u6d88\u5931\uff0c\u542c\u5f97\u5929\u9b54\u6559\u4e3b\u7684\u58f0\u97f3\uff0c\u7fdf\u7136\u4e00\u60ca\uff0c\u84e6\u5730 \u8df3\u8d77\u6765\u5927\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u5996\u5987\u5bb3\u5f97\u6211\u597d\u82e6\uff01\u201d\u547c\u7684\u4e00\u62f3\uff0c\u5c31\u5411\u5979\u6253\u53bb\uff01 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u88ab\u62f3\u98ce\u4e00\u51b2\uff0c\u51e0\u4e4e\u7ad9\u4e0d\u7a33\u811a\u6b65\uff0c\u9669\u4e9b\u5c31\u8981\u683d\u5012\u3002\u5979\u662f\u6545\u610f\u7b49\u5230\u6c5f\u6d77\u5929\u836f\u529b \u53d1\u4f5c\u624d\u51fa\u6765\u7684\uff0c\u672c\u4ee5\u4e3a\u4ed6\u662f\u6beb\u65e0\u62b5\u6297\u80fd\u529b\u7684\u4e86\uff0c\u51c6\u77e5\u6c5f\u6d77\u5929\u7adf\u4f1a\u7a81\u7136\u8dc3\u8d77\uff0c\u800c\u4e14\u8fd8\u80fd\u4f7f \u51fa\u5288\u7a7a\u795e\u62f3\uff0c\u529f\u529b\u8fdc\u80dc\u4ece\u524d\uff01\u5929\u9b54\u6559\u4e3b\u6697\u6697\u53eb\u82e6\uff0c\u540e\u6094\u672a\u66fe\u628a\u91d1\u6bdb\u72e1\u5e26\u6765\u3002\u8c37\u4e2d\u83b2\u5219\u559c \u51fa\u671b\u5916\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u6d77\u54e5\uff0c\u518d\u7ed9\u5979\u4e00\u62f3\u3002\u54a6\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u5979\u5728\u54ea\u91cc\uff0c\u4f60\u6ca1\u6709\u770b\u89c1\u5417\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u72b9\u5982\u9152\u9189\u4e00\u822c\uff0c\u53ea\u89c9\u773c\u524d\u4e00\u7247\u9ed1\u5f71\uff0c\u6839\u672c\u5c31\u5206\u8fa8\u4e0d\u51fa\u54ea\u4e2a\u662f\u5929\u9b54\u6559\u4e3b\uff0c\u54ea\u4e2a \u662f\u8c37\u4e2d\u83b2\uff0c\u547c\u547c\u547c\u547c\uff0c\u4e1c\u5357\u897f\u5317\uff0c\u4e71\u6253\u6570\u62f3\uff0c\u5929\u9b54\u6559\u4e3b\u4e0e\u8c37\u4e2d\u83b2\u90fd\u614c\u5fd9\u8eb2\u907f\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u6c5f\u6d77\u5929\u4e00\u62f3\u6253\u4e2d\u5899\u58c1\uff0c\u5899\u58c1\u5bc4\u5f00\u4e86\u4e86\u4e2a\u5927\u6d1e\uff0c\u7816\u77f3\u6a2a\u98de\u3002\u5c4b \u5b50\u90fd\u4f3c\u4e4e\u6447\u52a8\u8d77\u6765\uff0c\u8c37\u4e2d\u83b2\u8eb2\u5230\u53e6\u4e00\u8fb9\u5c4b\u89d2\uff0c\u53eb\u9053\uff1b\u201c\u6d77\u54e5\uff0c\u6d77\u54e5\uff0c\u4f60\u542c\u4e0d\u51fa\u6211\u7684\u58f0\u97f3 \u5417\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u201c\u549a\u201d\u7684\u4e00\u58f0\uff0c\u6c5f\u6d77\u5929\u5df2\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u539f\u6765\u4ed6\u65e9\u5df2\u88ab\u836f\u529b\u70e7\u5f97\u5934\u6655\u76ee\u7729\uff0c\u4f46\u7531\u4e8e\u4ed6\u662f\u5177\u6709\u6df1\u539a\u6b66\u529f\u7684\u4eba\uff0c\u81ea\u6709\u4e00\u79cd\u6297\u51fb\u654c \u4eba\u7684\u672c\u80fd\uff0c\u56e0\u6b64\u867d\u7136\u5728\u660f\u8ff7\u72b6\u6001\u4e4b\u4e2d\uff0c\u4e00\u5bdf\u89c9\u6709\u654c\u4eba\u6765\u5230\uff0c\u4e5f\u4f1a\u7a81\u7136\u5174\u594b\u3002\u4f46\u8fd9\u79cd\u5174\u594b \u7247\u523b\u5373\u8fc7\uff0c\u4ed6\u53c8\u4e71\u7528\u771f\u529b\uff0c\u66f4\u5f15\u5f97\u70ed\u6c14\u653b\u5fc3\uff0c\u56e0\u6b64\u8fd9\u4e00\u6b21\u660f\u8ff7\uff0c\u7adf\u662f\u5168\u7136\u5931\u53bb\u4e86\u77e5\u89c9\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5c4f\u4e86\u547c\u5438\uff0c\u8f7b\u8f7b\u7684\u4ece\u6c5f\u6d77\u5929\u8eab\u8fb9\u7ecf\u8fc7\uff0c\u811a\u5c16\u4e00\u62e8\uff0c\u6c5f\u6d77\u5929\u7ffb\u4e86\u4e2a\u8eab\uff0c\u53cc\u76ee \u7d27\u95ed\uff0c\u5df2\u662f\u4e1d\u6beb\u4e0d\u80fd\u52a8\u5f39\uff0c\u5929\u9b54\u6559\u4e3b\u8fd9\u624d\u653e\u5fc3\uff0c\u540c\u65f6\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5929 \u5fc3\u77f3\u7684\u6548\u529b\u5982\u6b64\u795e\u5947\uff0c\u836f\u6027\u5374\u53c8\u5982\u6b64\u6bd2\u70c8\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u53eb\u9053\uff1a\u201c\u6d77\u54e5\uff0c\u6d77\u54e5\uff01\u201d\u5979\u614c\u5f97\u5f79\u4e86\u4e3b\u610f\uff0c\u987e\u4e0d\u5f97\u5929\u6248\u6559\u4e3b\u5728\u65c1\uff0c\u4fbf\u8981\u6765\u5bdf \u770b\u6c5f\u6d77\u5929\u3002\u5929\u9b54\u6559\u4e3b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4ed6\u4e00\u65f6\u8fd8\u6b7b\u4e0d\u4e86\uff0c\u4f60\u628a\u90a3\u4e24\u6837\u4e1c\u897f\u7ed9\u6211\uff0c\u6211\u518d\u8bbe\u6cd5\u6551 \u4ed6\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u5148\u628a\u4ed6\u6551\u6d3b\uff0c\u6211\u518d\u7ed9\u4f60\u3002\u201d\u5176\u5b9e\u5929\u9b54\u6559\u4e3b\u54ea\u6709\u672c\u4e8b\u6551\u6d3b\u6c5f\u6d77\u5929\uff0c\u5f53 \u4e0b\u4e00\u58f0\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u7ed9\u6211\uff0c\u96be\u9053\u6211\u5c31\u4e0d\u4f1a\u81ea\u5df1\u6765\u62ff\uff1f\u201d\u51fa\u624b\u5982\u7535\uff0c\u500f\u5730\u5c31\u70b9\u5411\u8c37 \u4e2d\u83b2\u7684\u201c\u6108\u6c14\u7a74\u201d\uff0c\u8fd9\u4e2a\u7a74\u9053\u5018\u88ab\u70b9\u4e2d\uff0c\u7acb\u5373\u5168\u8eab\u9ebb\u8f6f\uff0c\u4e0d\u80fd\u52a8\u5f39\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u662f\u5415\u56db\u5a18\u7684\u5ae1\u7cfb\u4f20\u4eba\uff0c\u8eab\u624b\u4ea6\u81ea\u4e0d\u5f31\uff0c\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u5728\u8fd9\u7535\u5149\u77f3\u706b\u4e4b \u95f4\uff0c\u4e0d\u4f46\u907f\u5f00\u4e86\u5bf9\u65b9\u7684\u70b9\u7a74\uff0c\u800c\u4e14\u5c45\u7136\u8fd8\u51fb\u4e86\u4e00\u638c\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u53cc\u638c\u98de\u821e\uff0c\u9877\u523b\u4e4b\u95f4\uff0c\u5df2\u662f\u53d8\u6362\u4e86\u5341\u4e03\u4e2a\u62db\u5f0f\uff0c\u653b\u5f97\u8c37\u4e2d\u83b2\u624b\u5fd9\u811a\u4e71\uff0c\u4f46 \u5979\u4ee5\u7384\u5973\u5251\u6cd5\u5316\u5230\u638c\u6cd5\u4e0a\u6765\uff0c\u7d27\u5b88\u95e8\u6237\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5929\u9b54\u6559\u4e3b\u5374\u4e5f\u672a\u80fd\u5c06\u5979\u51fb\u8d25\u3002\u5929\u9b54 \u6559\u4e3b\u4e0d\u5927\u8010\u70e6\uff0c\u66ae\u5730\u4e00\u53e3\u6c14\u5439\u53bb\uff0c\u5979\u662f\u542b\u4e86\u9b54\u9b3c\u82b1\u79d8\u5236\u7684\u8ff7\u9999\u5728\u53e3\u5439\u51fa\u53bb\u7684\uff01\u8c37\u4e2d\u83b2\u7684 \u529f\u529b\u8fdc\u4e0d\u53ca\u6c5f\u6d77\u5929\uff0c\u95fb\u4e86\u8ff7\u9999\uff0c\u767b\u65f6\u7b4b\u9165\u9aa8\u8f6f\uff0c\u7ec8\u4e8e\u7ed9\u5929\u9b54\u6559\u4e3b\u70b9\u4e86\u5979\u7684\u9ebb\u7a74\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u641c\u5979\u7684\u8eab\u5b50\uff0c\u5148\u53d6\u53bb\u4e86\u5269\u4e0b\u7684\u90a3\u56db\u9897\u5929\u5fc3\u77f3\uff0c\u5929\u9b54\u6559\u4e3b\u662f\u5f53\u4eca\u4e4b\u4e16\u7b2c\u4e00\u4e2a \u5584\u4e8e\u4f7f\u6bd2\u7684\u4eba\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5f85\u6211\u56de\u53bb\u518d\u4ed4\u7ec6\u53c2\u8be6\u767e\u6bd2\u771f\u7ecf\uff0c\u7814\u7a76\u51fa\u5929\u5fc3\u77f3\u7684\u6bd2\u751f\u6240\u5728\uff0c \u603b\u53ef\u4ee5\u627e\u5f97\u89e3\u6bd2\u4e4b\u65b9\u3002\u54c8\uff0c\u54c8\uff0c\u90a3\u65f6\u6211\u628a\u8fd9\u56db\u9897\u5929\u5fc3\u77f3\u670d\u4e0b\uff0c\u5929\u4e0b\u8fd8\u6709\u8c01\u662f\u6211\u7684\u654c\u624b\uff1f\u201d \u63a5\u7740\u5929\u9b54\u6559\u4e3b\u53c8\u628a\u8c37\u4e2d\u83b2\u6309\u5012\uff0c\u8131\u4e0b\u5979\u7684\u978b\u5b50\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u62d4\u5251\u51fa\u9798\uff0c\u8fd9\u628a\u5251\u6b63\u662f\u5979\u593a\u81ea\u6c5f\u6d77\u5929\u624b\u4e2d\u7684\u90a3\u628a\u88c1\u4e91\u5b9d\u5251\uff0c\u8f7b\u8f7b\u4e00\u5212\uff0c\u5c06 \u8c37\u4e2d\u83b2\u8fd9\u5bf9\u5f13\u978b\u5212\u5f00\uff0c\u679c\u7136\u5728\u53f3\u811a\u90a3\u53ea\u978b\u5b50\u7684\u5939\u5c42\u4e2d\u627e\u5230\u4e86\u7f8a\u76ae\u4e66\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5c06\u7f8a\u76ae\u4e66\u6253\u5f00\uff0c\u8fc5\u901f\u770b\u4e86\u4e00\u904d\uff0c\u968f\u5373\u6495\u4e0b\u4e86\u6700\u540e\u4e24\u9875\uff0c\u5f97\u610f\u5fd8\u5f62\uff0c\u5927\u7b11\u4e86 \u4e00\u901a\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u6211\u628a\u8fd9\u534a\u7bc7\u2018\u9f99\u529b\u79d8\u85cf\u2019\u7559\u4e0b\uff0c\u5c06\u5176\u4f59\u4e24\u90e8\u4efd\u9001\u7ed9\u5b9d\u8c61\u6cd5\u5e08\u548c\u53f6 \u51b2\u9704\uff0c\u8ba9\u5b9d\u8c61\u6cd5\u5e08\u5f97\u5230\u524d\u738b\u7684\u5b9d\u85cf\uff0c\u8ba9\u53f6\u51b2\u9704\u5f97\u5230\u90a3\u7eb8\u540d\u5355\uff0c\u4e5f\u603b\u53ef\u4ee5\u5bf9\u5f97\u4f4f\u4ed6\u4eec\u4e86\uff01\u201d \u3000\u3000\u5979\u85cf\u597d\u4e86\u7f8a\u76ae\u7237\uff0c\u5411\u8c37\u4e2d\u83b2\u671b\u4e86\u4e00\u773c\uff0c\u5ffd\u53c8\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u6837\u5b9d\u8d1d\uff0c\u51e0\u4e4e\u5fd8\u4e86\u3002\u201d \u8d70\u8fc7\u53bb\u53c8\u5265\u4e0b\u4e86\u8c37\u4e2d\u83b2\u7684\u8863\u88f3\uff0c\u5c06\u6c5f\u6d77\u5929\u9001\u7ed9\u5979\u7684\u90a3\u4ef6\u767d\u7389\u7532\u8131\u4e86\u4e0b\u6765\u3002\u8c37\u4e2d\u83b2\u7ec3\u6709\u5c11 \u9633\u7384\u529f\uff0c\u88ab\u5929\u9b54\u6559\u4e3b\u7528\u91cd\u624b\u6cd5\u70b9\u4e86\u7a74\u9053\uff0c\u867d\u7136\u4e0d\u80fd\u52a8\u5f39\uff0c\u4eba\u5374\u5c1a\u8fd8\u6e05\u9192\uff0c\u4e0d\u7981\u53c8\u7f9e\u53c8\u6c14\u3002 \u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u7389\u4f53\u6676\u83b9\uff0c\u771f\u662f\u6211\u89c1\u72b9\u601c\uff0c\u602a\u4e0d\u5f97\u6c5f\u6d77\u5929\u7518\u5fc3\u966a\u4f60\u540c\u751f\u5171\u6b7b\u4e86\u3002\u597d\uff0c \u6211\u4e5f\u4e0d\u4f24\u5bb3\u4f60\u4eec\uff0c\u662f\u6b7b\u662f\u6d3b\uff0c\u770b\u4f60\u4eec\u7684\u9020\u5316\u5427\u3002\u201d \u3000\u3000\u5979\u7528\u683d\u4e91\u5b9d\u5251\u5728\u767d\u7389\u7532\u4e0a\u4e00\u5212\uff0c\u53ea\u89c1\u7389\u7532\u4e0a\u53ea\u662f\u73b0\u51fa\u4e86\u4e00\u9053\u6de1\u6de1\u7684\u5251\u75d5\uff0c\u88c1\u4e91\u5b9d\u5251 \u7adf\u4e5f\u4e0d\u80fd\u5c06\u5b83\u5212\u5f00\u3002\u5929\u9b54\u6559\u4e3b\u53c8\u7981\u4e0d\u4f4f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e54\u5317\u6e9f\u4e09\u5b9d\u6211\u5df2\u6709\u5176\u4e8c\uff0c\u53c8\u5f97\u4e86\u5929 \u5fc3\u77f3\u548c\u2018\u9f99\u529b\u79d8\u85cf\u2019\uff0c\u54c8\u54c8\uff0c\u53ea\u6015\u4e54\u5317\u6e9f\u590d\u751f\uff0c\u5f20\u4e39\u67ab\u518d\u4e16\uff0c\u4e5f\u672a\u5fc5\u662f\u6211\u7684\u5bf9\u624b\u4e86\uff01\u201d \u3000\u3000\u5979\u6b63\u5728\u5fc3\u6ee1\u610f\u8db3\uff0c\u4e50\u6781\u5fd8\u5f62\u4e4b\u9645\uff0c\u5ffd\u89c9\u80cc\u540e\u5fae\u98ce\u98d2\u7136\uff0c\u5728\u8fd9\u6697\u5ba4\u4e2d\u85cf\u6709\u5979\u7684\u51e0\u4e2a\u5fc3 \u8179\u4f8d\u5973\uff0c\u5979\u53ea\u9053\u6765\u8005\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u4fbf\u5373\u8bf4\u9053\uff1a\u201c\u5927\u529f\u544a\u6210\uff0c\u54b1\u4eec\u53ef\u4ee5\u8d70\u4e86\u3002\u201d \u8bdd\u72b9\u672a\u4e86\uff0c\u7a81\u7136\u88ab\u4eba\u6237\u628a\u62ff\u7740\u3002\u4e00\u652f\u51b0\u51b7\u7684\u94f6\u9488\u5bf9\u7740\u5979\u7684\u80f8\u53e3\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u6b27\u9633\u59d1\u5a18\uff0c\u522b\u5f00\u73a9\u7b11\uff01\u201d\u539f\u6765\u8fd9\u4e2a\u4eba\u6b63\u662f\u6b27\u9633\u5a49\u3002\u6b27 \u9633\u5a49\u7684\u6b66\u529f\u672c\u6765\u53ea\u662f\u4e0e\u8c37\u4e2d\u83b2\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u8fdc\u4e0d\u53ca\u5929\u9b54\u6559\u4e3b\uff0c\u5374\u4e0d\u6599\u5929\u9b54\u6559\u4e3b\u4e00\u65f6\u5927\u610f\uff0c \u7adf\u88ab\u6b27\u9633\u5a49\u62ff\u7740\u3002\u6b27\u9633\u5a49\u66fe\u8ddf\u9634\u5723\u59d1\u5b66\u8fc7\u4f7f\u6bd2\u7684\u529f\u592b\uff0c\u5979\u7528\u6765\u5bf9\u7740\u5929\u9b54\u6559\u4e3b\u80f8\u53e3\u7684\u90a3\u652f \u94f6\u9488\uff0c\u6b63\u662f\u4e00\u652f\u6bd2\u9488\uff0c\u5929\u9b54\u6559\u4e3b\u662f\u4e2a\u4f7f\u6bd2\u7684\u5927\u884c\u5bb6\uff0c\u5f53\u7136\u8ba4\u5f97\u3002 \u3000\u3000\u6b27\u9633\u5a49\u9053\uff1a\u201c\u5f97\u7f6a\u6559\u4e3b\u4e86\uff0c\u6211\u53ea\u60f3\u8bf7\u95ee\u4f60\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4f55\u4e8b\uff1f\u201d\u6b27 \u9633\u5a49\u9053\uff1a\u201c\u5929\u5fc3\u77f3\u4e4b\u6bd2\uff0c\u4f55\u7269\u53ef\u89e3\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u4f60\u4e5f\u662f\u60f3\u6551\u8fd9\u5c0f\u5b50\uff0c\u6211 \u6709\u529e\u6cd5\uff0c\u4f60\u653e\u5f00\u6211\u518d\u8bf4\u3002\u201d \u3000\u3000\u6b27\u9633\u5a49\u9053\uff1a\u201c\u6210\uff0c\u6211\u5148\u7ed9\u4f60\u6253\u4e0a\u4e00\u652f\u6bd2\u9488\uff0c\u8981\u662f\u4f60\u7684\u6cd5\u5b50\u7075\u9a8c\uff0c\u6211\u81ea\u7136\u4f1a\u7ed9\u4f60\u89e3\u836f\u3002 \u54c8\u54c8\uff0c\u5426\u5219\u4f60\u4e5f\u522b\u60f3\u6d3b\u547d\u4e86\u3002\u201d\u5929\u9b54\u6559\u4e3b\u51b7\u6c57\u76f4\u6d41\uff0c\u53eb\u9053\uff1a\u201c\u6b27\u9633\u59d1\u5a18\uff0c\u4f60\u624b\u6bb5\u597d\u72e0\uff01\u201d \u6b27\u9633\u5a49\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u73ed\u95e8\u5f04\u65a7\uff0c\u89c2\u7b11\u89c1\u7b11\uff01\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5fc3\u4e2d\u5b9e\u662f\u6050\u60e7\u4e4b\u6781\uff0c\u5374\u5ffd\u5730\u683c\u683c\u5a07\u7b11\u3002\u6b27\u9633\u5a49\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d\u5929\u9b54\u6559 \u4e3b\u9053\uff1a\u201c\u6211\u7b11\u4f60\u5927\u50bb\uff0c\u4f55\u5fc5\u5bf9\u8fd9\u5c0f\u5b50\u5982\u6b64\u75f4\u60c5\uff1f\u4ed6\u65e9\u5df2\u6709\u4e86\u610f\u4e2d\u4eba\u5566\uff0c\u5c31\u662f\u8ddf\u524d\u8fd9\u4f4d\u8c37 \u59d1\u5a18\u3002\u4f60\u6551\u6d3b\u4e86\u4ed6\uff0c\u4ed6\u4e5f\u51b3\u4e0d\u4f1a\u5a36\u4f60\u3002\u201d\u6b27\u9633\u5a49\u9762\u8272\u82cd\u767d\uff0c\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u53ea\u9053\u5979\u5fc3\u610f\u5df2\u52a8\uff0c\u5fd9\u7740\u53c8\u9053\uff1a\u201c\u6b27\u9633\u59d1\u5a18\uff0c\u6211\u628a\u4ed6\u8fd9\u67c4\u5b9d\u5251\u7ed9\u4f60\uff0c\u53e6\u5916\u518d \u9001\u4f60\u4e24\u9897\u5929\u5fc3\u77f3\uff0c\u6bcf\u4e00\u9897\u53ef\u4ee5\u4ee4\u4f60\u589e\u957f\u5341\u5e74\u529f\u529b\u3002\u5c06\u6765\u4f60\u6b66\u529f\u65e0\u654c\uff0c\u53c8\u6709\u5b9d\u5251\uff0c\u8fd8\u6015\u627e \u4e0d\u5230\u6bd4\u6c5f\u6d77\u5929\u66f4\u597d\u5341\u500d\u7684\u4eba\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u4e03\u7a8d\u73b2\u73d1\uff0c\u4e00\u542c\u8fd9\u8bdd\uff0c\u5c31\u77e5\u5929\u9b54\u6559\u4e3b\u6839\u672c\u6ca1\u6709\u672c\u9886\u89e3\u5929\u5fc3\u77f3\u4e4b\u6bd2\uff0c\u5979\u94f6\u7259\u4e00 \u54ac\uff0c\u84e6\u5730\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u90fd\u662f\u6211\u7684\uff0c\u6211\u4f55\u5fc5\u8981\u4f60\u7ed9\u6211\uff1f\u201d\u6307\u5934\u4e00\u6309\uff0c\u5c06\u6bd2\u9488\u523a\u8fdb\u4e86\u5929\u9b54 \u6559\u4e3b\u7684\u80f8\u4e2d\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5929\u9b54\u6559\u4e3b\u5524\u5979\u6b27\u9633\u59d1\u5a18\uff0c\u60f3\u5fc5\u5c31\u662f\u53f6\u51b2\u9704\u6240\u8bf4\u7684\u90a3\u4e2a\u6b27\u9633\u4ef2\u548c \u7684\u5973\u513f\u4e86\u3002\u53f6\u51b2\u9704\u8bf4\u5979\u4e0e\u6d77\u54e5\u60c5\u6295\u610f\u5408\uff0c\u73b0\u5728\u770b\u6765\u3001\u5979\u5bf9\u6d77\u54e5\u5374\u662f\u75f4\u60c5\u4e00\u7247\uff0c\u53ea\u4e0d\u77e5\u4fae \u54e5\u662f\u5426\u771f\u7684\u4e5f\u559c\u6b22\u5979\uff1f\u54ce\u5440\uff0c\u5979\u7684\u624b\u6bb5\u5982\u6b64\u72e0\u6bd2\uff0c\u53ef\u60dc\u6d77\u54e5\u6ca1\u6709\u4eb2\u773c\u770b\u89c1\u3002\u201d \u3000\u3000\u53ea\u89c1\u6b27\u9633\u5a49\u5c06\u5929\u9b54\u6559\u4e3b\u8eab\u4e0a\u7684\u5929\u5fc3\u77f3\u548c\u7f8a\u76ae\u4e66\u5168\u90e8\u641c\u53bb\uff0c\u5c06\u88c1\u4e91\u5b9d\u5251\u4f69\u4e0a\uff0c\u63a5\u7740\u7c91 \u767d\u7389\u7532\u4e5f\u53d6\u4e86\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u4e24\u9053\u51b0\u51b7\u7684\u773c\u5149\u76f4\u5411\u8c37\u4e2d\u83b2\u5c04\u6765\uff0c\u8c37\u4e2d\u83b2\u4e0b\u7531\u5f97\u5fc3\u5934\u4e00\u51db\uff1a \u201c\u83ab\u975e\u5979\u6709\u5bb3\u6211\u4e4b\u610f\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u6b27\u9633\u5a49\u5df2\u8d70\u4e86\u8fd1\u6765\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u5929\u4ed9\u7f8e\u4eba\u3002\u602a\u4e0d\u5f97\u6c5f\u6d77\u592d \u7ed9\u4f60\u8ff7\u4e86\uff01\u201d\u6076\u6bd2\u7684\u773c\u5149\u5728\u8c37\u4e2d\u83b2\u7684\u8eab\u4e0a\u8f6c\u6765\u8f6c\u53bb\uff0c\u76ef\u5f97\u8c37\u4e2d\u83b2\u5fc3\u91cc\u53d1\u6bdb\uff0c\u4e0d\u77e5\u6b27\u9633\u5a49 \u8981\u600e\u6837\u6298\u78e8\u5979\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u6b27\u9633\u5a49\u786e\u6709\u9664\u6389\u8c37\u4e2d\u83b2\u4e4b\u610f\uff0c\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u51e0\u6b21\u610f\u6b32\u4e0b\u624b\uff0c\u5374\u53c8\u5fc3\u91cc\u53d1\u6bdb\uff0c \u539f\u6765\u5979\u66fe\u4e0e\u6c5f\u6d77\u5929\u76f8\u5904\u4e00\u6bb5\u65f6\u95f4\uff0c\u591a\u591a\u5c11\u5c11\u5df2\u53d7\u4e86\u6c5f\u6d77\u5929\u7684\u718f\u9676\uff0c\u8fd9\u65f6\u5584\u6076\u4e24\u4e2a\u5ff5\u5934\uff0c \u6b63\u5728\u5fc3\u4e2d\u4ea4\u6218\uff01 \u3000\u3000\u5979\u4e00\u5411\u81ea\u8d1f\u7f8e\u8c8c\uff0c\u73b0\u5728\u8d8a\u770b\u8d8a\u89c9\u5f97\u8c37\u4e2d\u83b2\u7684\u7f8e\u8c8c\u66f4\u80dc\u8fc7\u81ea\u5df1\uff0c\u5fc3\u4e2d\u5992\u610f\u4e5f\u5c31\u66f4\u6d53\uff0c \u5fcd\u4e0d\u4f4f\u53d6\u51fa\u4e00\u652f\u6bd2\u9488\uff0c\u5bf9\u51c6\u4e86\u8c37\u4e2d\u83b2\u7684\u8111\u95e8\uff0c\u53ea\u8981\u4e00\u63d2\u8fdb\u53bb\uff0c\u8c37\u4e2d\u83b2\u9a6c\u4e0a\u5c31\u8981\u7389\u6b92\u9999\u6d88\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u5979\u5ffd\u5730\u5fc3\u5934\u4e00\u9707\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u4ed6\u66fe\u5c61\u6b21\u529d\u6211\u6539\u90aa\u5f52\u6b63\uff0c \u8981\u662f\u4ed6\u77e5\u9053\u6211\u5bb3\u4e86\u8c37\u4e2d\u83b2\uff0c\u7eb5\u7136\u6211\u80fd\u591f\u628a\u4ed6\u6551\u6d3b\uff0c\u4ed6\u4e5f\u51b3\u4e0d\u80fd\u7231\u6211\uff01\u201d\u6bd2\u9488\u505c\u4e86\u4e0b\u6765\uff0c \u8f6c\u5ff5\u53c8\u60f3\uff1a\u201c\u6211\u4e0d\u544a\u8bc9\u4ed6\u4ed6\u600e\u80fd\u77e5\u9053\uff1f\u7559\u4e0b\u6b64\u4eba\uff0c\u603b\u662f\u7978\u5bb3\uff0c\u4e0d\u5982\u8fd8\u662f\u628a\u5979\u9664\u4e86\u5427\uff1f\u201d \u6bd2\u9488\u53c8\u6e10\u6e10\u79fb\u5230\u4e86\u8c37\u4e2d\u83b2\u7684\u9762\u524d\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u65e9\u5df2\u81ea\u5fd6\u5fc5\u6b7b\uff0c\u4f46\u8fd9\u65f6\u89e6\u5230\u4e86\u51b0\u51b7\u7684\u6bd2\u9488\uff0c\u5374\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u5fc3\u60b8\uff0c\u773c\u5149\u4e2d\u9732\u51fa \u4e86\u6b7b\u4ea1\u7684\u6050\u60e7\uff01 \u3000\u3000\u6b27\u9633\u5a49\u4e0d\u89c9\u53c8\u662f\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u9053\uff1a\u201c\u5979\u548c\u5929\u9b54\u6559\u4e3b\u5927\u4e0d\u76f8\u540c\uff0c\u5979\u662f\u4e2a\u5584\u826f\u7684\u5973\u5b50\uff0c \u6211\u5bb3\u4e86\u5979\uff0c\u4e8e\u5fc3\u4f55\u5fcd\uff1f\u5509\uff0c\u5018\u82e5\u6211\u505a\u4e86\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u7eb5\u7136\u6c5f\u6d77\u5929\u6c38\u8fdc\u90fd\u4e0d\u77e5\u9053\uff0c\u6211\u4e5f\u8981 \u5185\u759a\u7ec8\u751f\uff01\u201d\u5411\u5584\u4e4b\u5ff5\uff0c\u7ec8\u4e8e\u5360\u4e86\u4e0a\u98ce\u52fa\u6b27\u9633\u5a49\u7684\u76ee\u5149\u6e10\u8f6c\u67d4\u548c\u3002 \u3000\u3000\u5ffd\u89c1\u6c5f\u6d77\u5929\u5728\u5730\u4e0a\u7ffb\u4e86\u4e2a\u8eab\uff0c\u68a6\u5453\u4f3c\u7684\u542b\u7cca\u8bf4\u9053\uff1a\u201c\u4f60\u3001\u4f60\u6765\u4e86\u4e48\uff1f\u201d\u6b27\u9633\u5a49\u53c8\u60ca \u53c8\u559c\uff0c\u8fde\u5fd9\u8fc7\u53bb\uff0c\u5728\u6c5f\u6d77\u5929\u8033\u8fb9\u4f4e\u58f0\u5524\u9053\uff1a\u201c\u6d77\u5929\uff0c\u662f\u6211\u6765\u5566\uff0c\u4f60\u7741\u5f00\u773c\u775b\u770b\u770b\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5e76\u6ca1\u6709\u7741\u5f00\u773c\u775b\uff0c\u7761\u5f97\u4f3c\u4e4e\u66f4\u6c89\u4e86\u3002\u6b27\u9633\u5a49\u4e00\u6478\u4ed6\u7684\u989d\u89d2\uff0c\u70ed\u5f97\u60ca\u9a87\uff0c\u6b27\u9633 \u5a49\u4e0d\u7531\u5f97\u6cea\u5982\u96e8\u4e0b\uff0c\u62b1\u7740\u4ed6\u7684\u8eab\u5b50\u4e71\u6447\u3002\u6cea\u73e0\u4e00\u9897\u4e00\u9897\u5730\u6ef4\u5728\u4ed6\u7684\u9762\u4e0a\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5e76\u975e\u719f\u7761\uff0c\u800c\u662f\u88ab\u836f\u529b\u70ed\u5f97\u660f\u8ff7\uff0c\u4ed6\u5728\u8ff7\u8ff7\u7cca\u7cca\u4e2d\u9690\u9690\u611f\u5230\u6709\u4e2a\u4eba\u8d70\u5230\u4ed6\u7684 \u8eab\u8fb9\uff0c\u5ffd\u5730\u53c8\u6709\u4e00\u7247\u6e05\u51c9\u7684\u611f\u89c9\uff0c\u4ed6\u6323\u624e\u7740\u5f20\u5f00\u4e86\u773c\u775b\uff0c\u6b27\u9633\u5a49\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u6211 \u4e48\uff1f\u6211\u662f\u6b27\u9633\u5a49\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u773c\u524d\u53ea\u6709\u4e00\u4e2a\u6726\u80e7\u7684\u4eba\u5f71\uff0c\u4ed6\u7684\u89c6\u529b\u8fd8\u6ca1\u6709\u6062\u590d\uff0c\u4f46\u4ed6\u5df2\u542c\u51fa\u4e86\u662f\u6b27\u9633\u5a49\u7684 \u58f0\u97f3\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7aed\u529b\u5f20\u5f00\u5634\u5507\uff0c\u6b27\u9633\u5a49\u5c06\u8033\u6735\u4e0a\u53bb\u542c\uff0c\u53ea\u542c\u5f97\u6c5f\u6d77\u5929\u65ad\u65ad\u7eed\u7eed\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\uff0c \u6211\u4e0d\u6210\u5566\uff01\u6211\uff0c\u6211\u53ea\u6c42\u4f60\u4e00\u4ef6\u4e8b\u60c5\uff0c\u6c42\u4f60\u628a\u3001\u628a\u8fd9\u4f4d\u8c37\u59d1\u5a18\u6551\u4e86\u51fa\u53bb\u3002\u201d\u4ed6\u8bf4\u4e86\u8fd9\u51e0\u53e5 \u8bdd\uff0c\u75b2\u5026\u4e0d\u582a\uff0c\u773c\u76ae\u53c8\u9616\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u6b27\u9633\u5a49\u5446\u82e5\u6728\u9e21\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5f97\u53c8\u9178\u53c8\u75db\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u4e34\u6b7b\u4e5f\u8fd8\u662f\u5ff5\u5ff5\u4e0d\u5fd8\u8981\u6551\u8c37\u4e2d \u83b2\uff01\u201d\u60f3\u5230\u4e86\u8fd9\u4e2a\u201c\u6b7b\u201d\u5b57\uff0c\u5fc3\u75db\u5982\u7ede\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u6b7b\uff0c\u4f60\u4e0d\u80fd\u6b7b\uff01\u201d \u3000\u3000\u5979\u7528\u624b\u6307\u4e00\u63a2\u6c5f\u6d77\u5929\u9f3b\u7aef\uff0c\u53d1\u89c9\u4ed6\u8fd8\u6709\u6c14\u606f\u3002\u6b27\u9633\u5a49\u5b9a\u4e86\u5b9a\u795e\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u8fd8 \u6709\u4e00\u7ebf\u5e0c\u671b\uff0c\u6211\u4e0d\u80fd\u653e\u8fc7\u3002\u8981\u6b7b\uff0c\u4f60\u4e5f\u8981\u6b7b\u5728\u6211\u7684\u6000\u4e2d\u3002\u201d \u3000\u3000\u6b27\u9633\u5a49\u628a\u6c5f\u6d77\u5929\u62b1\u4e86\u8d77\u6765\uff0c\u7f13\u7f13\u7684\u4ece\u8c37\u4e2d\u83b2\u8eab\u8fb9\u8d70\u8fc7\u3002\u5979\u770b\u4e86\u8c37\u4e2d\u83b2\u4e00\u773c\uff0c\u53c8\u4f4e\u5934 \u770b\u770b\u5979\u6000\u4e2d\u7684\u6c5f\u6d77\u5929\uff0c\u5fc3\u4e71\u5982\u9ebb\uff1a\u201c\u4ed6\u8fd9\u6837\u90d1\u91cd\u7684\u5631\u6258\u6211\uff0c\u6211\u542c\u4e0d\u542c\u4ed6\u7684\u5429\u5490\uff0c\u6551\u4e0d\u6551 \u8fd9\u4f4d\u8c37\u59d1\u5a18\u5462\uff1f\u201d\u5979\u60f3\u4e86\u4e00\u4f1a\uff0c\u6447\u4e86\u6447\u5934\uff0c\u62ff\u8d77\u4e86\u8c37\u4e2d\u83b2\u7684\u8863\u88f3\uff0c\u66ff\u5979\u62ab\u4e0a\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u8c37\u59d1\u5a18\uff0c\u8bf7\u539f\u8c05\u6211\u4e0d\u80fd\u6551\u4f60\u3002\u5c31\u4f1a\u6709\u4eba\u6765\u7684\uff0c\u4e00\u5207\u90fd\u770b\u4f60\u81ea\u5df1\u7684\u9020\u5316\u5427\u3002\u201d \u3000\u3000\u8981\u77e5\u6b27\u9633\u5a49\u672c\u662f\u90aa\u6d3e\u51fa\u8eab\uff0c\u4ed6\u4e0d\u6740\u8c37\u4e2d\u83b2\uff0c\u5df2\u662f\u6781\u5c3d\u514b\u5236\u7684\u80fd\u529b\u4e86\uff0c\u8981\u5979\u518d\u628a\u8c37\u4e2d \u83b2\u5e26\u7740\u540c\u8d70\uff0c\u8ba9\u8c37\u4e2d\u83b2\u4e5f\u5728\u6c5f\u6d77\u5929\u7684\u8eab\u8fb9\uff0c\u5979\u662f\u65e0\u8bba\u5982\u4f55\u4e5f\u505a\u4e0d\u5230\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\u6b27\u9633\u5a49\u4e5f\u611f\u5230\u6709\u70b9\u6b49\u610f\uff0c\u5979\u4e0d\u6562\u518d\u5bf9\u7740\u8c37\u4e2d\u83b2\u7684\u76ee\u5149\uff0c\u6025\u6025\u5fd9\u5fd9\u4fbf\u8d70\uff0c\u5fc3\u4e2d\u4e00 \u9762\u76c6\u7b97\uff1a\u4e07\u4e00\u6c5f\u6d77\u5929\u80fd\u591f\u6551\u6d3b\uff0c\u81ea\u5df1\u5c06\u600e\u6837\u7f16\u4e00\u5957\u5047\u8bdd\u9a97\u4ed6\uff1f \u3000\u3000\u6b27\u9633\u5a49\u6b63\u5728\u60f3\u7740\u5fc3\u4e8b\uff0c\u8fd8\u672a\u66fe\u8d70\u5230\u95e8\u53e3\uff0c\u5ffd\u542c\u5f97\u6709\u4e2a\u4eba\u563f\u563f\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5440\uff0c \u6b27\u9633\u5a49\uff0c\u4f60\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff1f\u201d\u8fd9\u4eba\u6b63\u662f\u53f6\u51b2\u9704\u3002\u6b27\u9633\u5a49\u65e9\u5df2\u77e5\u9053\u4ed6\u4f1a\u8d76\u6765\uff0c\u4f46\u5374\u6599\u4e0d\u5230 \u4ed6\u6765\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u53f6\u51b2\u9704\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u600e\u4e48\u5566\uff1f\u4f60\u8981\u5e26\u4ed6\u79c1\u9003\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u5ffd\u5730\u51fa\u58f0\u8bf4\u9053\uff1a \u201c\u5979\u5c82\u6b62\u53ea\u662f\u8981\u8fd9\u5c0f\u5b50\uff0c\u8c37\u4e2d\u83b2\u5bc6\u85cf\u7684\u524d\u738b\u9057\u4e66\uff0c\u4ee5\u53ca\u6b66\u6797\u5f02\u5b9d\u5929\u5fc3\u77f3\uff0c\u90fd\u7ed9\u5979\u4e00\u53e4\u8111 \u513f\u5077\u53bb\u4e86\u5566\uff01\u201d \u3000\u3000\u539f\u6765\u5929\u9b54\u6559\u4e3b\u4f7f\u6bd2\u7684\u672c\u9886\u5929\u4e0b\u65e0\u53cc\uff0c\u5e73\u5e38\u4e5f\u7ecf\u5e38\u8bd5\u670d\u5404\u79cd\u6bd2\u836f\uff0c\u8eab\u4f53\u81ea\u7136\u751f\u51fa\u4e00\u79cd \u6297\u6bd2\u7684\u672c\u80fd\uff0c\u6b27\u9633\u5a49\u90a3\u652f\u6bd2\u9488\u867d\u7136\u5389\u5bb3\u65e0\u6bd4\uff0c\u5374\u4e5f\u4e0d\u80fd\u5c31\u8981\u4e86\u5979\u7684\u6027\u547d\uff0c\u5979\u521a\u624d\u662f\u5047\u88c5 \u4e0d\u7701\u4eba\u4e8b\u7684\u3002 \u3000\u3000\u65f6\u51b2\u9704\u5927\u6012\u9053\uff1a\u201c\u6b27\u9633\u5a49\uff0c\u6211\u5f85\u4f60\u4e0d\u8584\uff0c\u4f60\u4e3a\u4f55\u53db\u6211\uff1f\u201d\u6b27\u9633\u5a49\u9053\uff1a\u201c\u5509\uff0c\u6211\u5bf9\u4f60 \u4e00\u756a\u597d\u610f\uff0c\u4f60\u5374\u4e0d\u77e5\u3002\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4f60\u5377\u7269\u79c1\u9003\uff0c\u6211\u5df2\u4eb2\u773c\u770b\u89c1\uff0c\u4f60\u8fd8\u80fd\u72e1\u8fa9\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff0c\u8981\u4e0d\u662f\u6211\u65e9\u6765\u4e00\u6b65\uff0c\u5b9d\u7269\u65e9\u5df2\u7ed9\u5929\u9b54\u6559\u4e3b\u53d6\u4e86\u53bb\u5566\u3002\u6211\u4ece \u5979\u624b\u4e0a\u5bfa\u6765\uff0c\u672c\u662f\u8981\u7ed9\u4f60\u7684\uff0c\u53ea\u6c42\u4f60\u8ba9\u6211\u5c06\u4ed6\u5e26\u8d70\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u53f6\u516c\u5b50\uff0c\u522b\u76f8\u4fe1\u5979\u7684\u9b3c\u8bdd\uff0c\u4f60\u82e5\u4e0d\u65e9\u6765\u4e00\u6b65\uff0c\u5979\u624d\u771f\u7684\u662f\u9003\u4e4b\u592d\u592d \u4e86\u5462\uff01\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e0d\u4f1a\u76f8\u4fe1\u5979\u7684\u9b3c\u6d3b\u3002\u54fc\uff0c\u54fc\uff0c\u6b27\u9633\u59d1\u5a18\uff0c\u6211\u53ea\u95ee\u4f60\uff0c\u4f60\u5018\u82e5\u771f \u662f\u6709\u8fd9\u756a\u5fc3\u610f\uff0c\u4e3a\u4f55\u4e0d\u5148\u5bf9\u6211\u8a00\u660e\uff0c\u5374\u8981\u79c1\u81ea\u5077\u4e86\u6211\u7236\u738b\u8fd9\u5ea7\u590f\u5bab\u7684\u5730\u81ea\uff0c\u7792\u7740\u6211\u72ec\u81ea \u524d\u6765\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u65e0\u53ef\u7b54\u8fa9\uff0c\u5ffd\u5730\u5c06\u6c5f\u6d77\u5929\u653e\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u53f6\u516c\u5b50\uff0c\u4f60\u522b\u751f\u6c14\uff0c\u6211\u90fd\u7ed9\u4f60\u5c31\u662f\u3002\u201d \u5929\u9b54\u6559\u4e3b\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u6b27\u9633\u5a49\u624b\u81c2\u4e00\u62ac\uff0c\u8896\u4e2d\u98de\u51fa\u4e86\u4e00\u84ec\u6bd2\u9488\u3002 \u3000\u3000\u65f6\u51b2\u9704\u65e9\u6709\u63d0\u9632\uff0c\u4e00\u8bb0\u5288\u7a7a\u638c\u6253\u51fa\uff0c\u5c06\u90a3\u84ec\u6bd2\u9488\u5168\u90e8\u9707\u843d\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6b27\u9633 \u5a49\u5df2\u62d4\u51fa\u4e86\u88c1\u4e91\u5b9d\u5251\uff0c\u4e00\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u5411\u53f6\u51b2\u9704\u75be\u523a\u3002 \u3000\u3000\u53f6\u51b2\u9704\u51b7\u7b11\u9053\uff1a\u201c\u51ed\u4f60\u8fd9\u70b9\u672c\u4e8b\uff0c\u5c31\u60f3\u53db\u6211\uff1f\u201d\u4e00\u8bb0\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\uff0c\u53f3\u81c2\u5f2f\u66f2\u5982\u5f13\uff0c \u4f7f\u5f00\u4e86\u64d2\u62ff\u624b\u6cd5\uff0c\u5de6\u624b\u4f38\u6307\u5982\u7bad\uff0c\u8f7b\u70b9\u6b27\u9633\u5a49\u7684\u7a74\u9053\u3002 \u3000\u3000\u6b27\u9633\u5a49\u9053\uff1a\u201c\u4f60\u65e2\u4e0d\u89c1\u8c05\uff0c\u6211\u53ea\u597d\u4e0e\u4f60\u62fc\u5566\uff01\u201d\u8fde\u5165\u5e26\u5251\uff0c\u4e00\u4e2a\u98ce\u8f66\u75be\u8f6c\uff0c\u5251\u5149\u56db \u9762\u8361\u5f00\uff0c\u81ea\u8eab\u5219\u85cf\u5728\u5149\u5e62\u4e4b\u5185\u3002 \u3000\u3000\u6b27\u9633\u5a49\u7684\u6b66\u529f\u672c\u6765\u4e0e\u53f6\u51b2\u9704\u76f8\u5dee\u5f88\u8fdc\uff0c\u4f46\u5979\u7528\u7684\u8fd9\u628a\u88c1\u4e91\u5b9d\u5251\uff0c\u5374\u662f\u950b\u5229\u65e0\u6bd4\uff0c\u53f6 \u51b2\u9704\u8fd8\u5f53\u771f\u4e0d\u6562\u592a\u8fc7\u8feb\u8fd1\u3002\u53ea\u597d\u8fd0\u7528\u5927\u4e58\u822c\u82e5\u638c\u529b\uff0c\u5c06\u5979\u7d27\u7d27\u8feb\u7740\uff0c\u6559\u5979\u817e\u4e0d\u51fa\u624b\u6765\u65bd \u653b\u6bd2\u836f\u6bd2\u9488\u3002 \u3000\u3000\u53f6\u51b2\u9704\u559d\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u8d76\u5feb\u629b\u4e0b\u5b9d\u5251\uff0c\u6211\u638c\u529b\u5c3d\u53d1\uff0c\u7ba1\u6559\u4f60\u4e03\u7a8d\u6d41\u8840\u800c\u4ea1\uff01\u201d\u6b27\u9633 \u5a49\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u6b3a\u8d1f\u6211\uff0c\u6211\u6b7b\u4e5f\u4e0d\u773c\u4f60\uff01\u6211\u6000\u4e2d\u85cf\u6709\u70c8\u7130\u5f39\uff0c\u5230\u4e86\u6700\u540e\u5173\u5934\uff0c\u7eb5\u7136\u6211\u4e0d \u80fd\u53d6\u6765\u4f24\u4f60\uff0c\u96be\u9053\u6211\u4e0d\u80fd\u53eb\u5b83\u81ea\u884c\u7206\u70b8\u4e48\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u5fc3\u4e2d\u4e00\u51db\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u77e5\u9053\u6211\u548c\u4f60\u59d0\u59d0\u662f\u4f55\u7b49\u60c5\u4efd\uff0c\u6211\u600e\u80fd\u591f\u6740\u4f60\uff1f\u4f60\u628a \u5251\u653e\u4e0b\u6765\uff0c\u6709\u8bdd\u597d\u597d\u7684\u8bf4\uff0c\u522b\u4f24\u4e86\u54b1\u4eec\u4e24\u5bb6\u7684\u548c\u6c14\u3002\u201d \u3000\u3000\u8981\u77e5\u53f6\u51b2\u9704\u6700\u60f3\u5f97\u5230\u7684\u5c31\u662f\u90a3\u5f20\u7f8a\u76ae\u4e66\u548c\u5929\u5fc3\u77f3\uff0c\u5018\u82e5\u6b27\u9633\u5a49\u5c06\u6000\u4e2d\u7684\u70c8\u7130\u5f39\u81ea\u884c \u7206\u70b8\uff0c\u5979\u6b7b\u4e0d\u8db3\u60dc\uff0c\u4f46\u90a3\u4e24\u4ef6\u5b9d\u8d1d\u5c82\u975e\u540c\u5f52\u4e8e\u5c3d\uff1f \u3000\u3000\u6b27\u9633\u5a49\u4f55\u7b49\u673a\u7075\uff0c\u65e9\u5df2\u8bc6\u7834\u53f6\u51b2\u9704\u5fc3\u610f\uff0c\u5b9d\u5251\u821e\u5f97\u66f4\u6025\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u624d\u4e0d\u4e0a\u4f60 \u7684\u5f53\u5462\u3002\u4f60\u60f3\u8981\u90a3\u4e24\u6837\u4e1c\u897f\u662f\u4e0d\u662f\uff1f\u597d\uff0c\u9664\u975e\u4f60\u4f9d\u6211\u4e00\u4e8b\u3002\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u6b27\u9633\u5a49\u9053\uff1a\u201c\u4f60\u4eb2\u81ea\u9001\u6211\u548c\u6c5f\u76f8\u516c\u51fa\u53bb\uff0c\u7ed9\u6211\u4e00\u6761\u7a7a\u8239\u4e0e\u6211\uff0c\u6211\u4e0a\u4e86\u8239\u4e4b\u540e\uff0c\u81ea\u4f1a \u5c06\u4f60\u6240\u8981\u7684\u4e1c\u897f\u629b\u4e0a\u5cb8\u6765\u3002\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u8981\u662f\u4f60\u4e0d\u629b\u4e0a\u6765\u5462\uff1f\u201d\u6b27\u9633\u5a49\u9053\uff1a\u201c\u4f60\u82e5\u4fe1 \u4e0d\u8fc7\u6211\uff0c\u90a3\u5c31\u4f5c\u7f62\u3002\u6211\u820d\u4e86\u4e00\u6761\u6027\u547d\uff0c\u4f60\u4e5f\u4f11\u60f3\u5f97\u5230\u90a3\u4e24\u6837\u4e1c\u897f\u3002\u201d\u53f6\u51b2\u9704\u5fc3\u610f\u8e0c\u8e87\uff0c \u4e00\u65f6\u96be\u51b3\u3002 \u3000\u3000\u6b27\u9633\u5a49\u773c\u5149\u4e00\u77a5\uff0c\u5ffd\u89c1\u5929\u9b54\u6559\u4e3b\u8212\u5c55\u624b\u8db3\uff0c\u624b\u4e2d\u62ff\u7684\u6b63\u662f\u5979\u7684\u90a3\u652f\u6bd2\u9488\uff0c\u7f13\u7f13\u5411\u6c5f \u6d77\u5929\u8d70\u53bb\u3002\u6b27\u9633\u5a49\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u539f\u6765\u5929\u9b54\u6559\u4e3b\u4f7f\u6bd2\u7684\u529f\u592b\u8fd8\u5728\u6b27\u9633\u5a49\u7684\u5e08\u7236\u9634\u5723\u59d1\u4e4b\u4e0a\uff0c\u8fd9\u79cd\u6bd2\u9488\u7684\u89e3\u836f\uff0c\u5979\u672c\u6765 \u5c31\u6709\u3002\u4f46\u5728\u6b27\u9633\u5a49\u672a\u88ab\u65f6\u51b2\u9704\u7684\u638c\u529b\u56f0\u4f4f\u4e4b\u524d\uff0c\u5979\u5374\u4e0d\u6562\u53d6\u51fa\u6765\u3002\u56e0\u4e3a\u5979\u4e2d\u4e86\u6bd2\u9488\uff0c\u867d \u7136\u672a\u81f3\u660f\u8ff7\uff0c\u5374\u5df2\u4e0d\u80fd\u8fd0\u7528\u771f\u529b\uff0c\u51b3\u8ba1\u4e0d\u662f\u6b27\u9633\u5a49\u7684\u5bf9\u624b\uff0c\u6240\u4ee5\u5728\u53f6\u51b2\u9704\u672a\u6765\u4e4b\u524d\u5979\u52a8 \u4e5f\u4e0d\u6562\u4e00\u52a8\uff0c\u751f\u6015\u7ed9\u6b27\u9633\u5a49\u53d1\u89c9\u5979\u5c1a\u672a\u660f\u8ff7\u3002\u5979\u8fde\u52a8\u90fd\u4e0d\u6562\u52a8\uff0c\u5f53\u7136\u66f4\u4e0d\u6562\u9c81\u83bd\u4ece\u4e8b\uff0c \u62ff\u51fa\u89e3\u836f\u4e86\u3002 \u3000\u3000\u5f85\u81f3\u6b27\u9633\u5a49\u5df2\u662f\u9677\u4e8e\u82e6\u6597\u4e4b\u4e2d\uff0c\u65e0\u6cd5\u5206\u8eab\u4e4b\u9645\uff0c\u5979\u8fd9\u624d\u62d4\u51fa\u6bd2\u9488\uff0c\u541e\u4e0b\u89e3\u836f\uff0c\u4f46\u56e0 \u4e3a\u65f6\u95f4\u672a\u4e45\uff0c\u5979\u7684\u771f\u529b\u53ea\u6062\u590d\u4e86\u4e00\u4e24\u5206\uff0c\u8fd8\u4e0d\u80fd\u4eb2\u81ea\u53bb\u5bf9\u4ed8\u6b27\u9633\u5a49\uff0c\u6545\u6b64\u53e6\u51fa\u5947\u8c0b\uff0c\u522b \u65bd\u8be1\u8ba1\u3002 \u3000\u3000\u6b27\u9633\u5a49\u8fd9\u4e00\u5403\u60ca\uff0c\u5929\u9b54\u6559\u4e3b\u66f4\u4e3a\u5f97\u610f\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\uff0c\u4f60\u9001\u6211\u8fd9\u53e3\u6bd2\u9488\uff0c \u6211\u4e0d\u8981\u4e86\uff0c\u6211\u4ee3\u4f60\u8f6c\u9001\u7ed9\u6c5f\u6d77\u5929\uff01\u201d \u3000\u3000\u6b27\u9633\u5a49\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u5927\u53eb\u9053\uff1a\u201c\u4f60\u522b\u8fd9\u6837\uff01\u4f60\u8981\u4ec0\u4e48\uff1f\u6211\uff0c\u6211\u90fd\u4f9d\u4f60\uff01\u201d\u5979\u5168\u4ed7 \u7740\u5b9d\u5251\u62a4\u8eab\uff0c\u54ea\u5bb9\u5f97\u7a0d\u6709\u5206\u5fc3\uff1f\u5c31\u5728\u5979\u5931\u58f0\u60ca\u547c\uff0c\u5251\u638c\u7a0d\u7f13\u4e4b\u9645\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53f6 \u51b2\u9704\u5df2\u662f\u4e58\u865a\u800c\u5165\uff0c\u95ea\u7535\u822c\u5730\u70b9\u4e86\u5979\u80c1\u4e0b\u7684\u9ebb\u7a74\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5e76\u975e\u771f\u7684\u60f3\u5bb3\u6c5f\u6d77\u5929\uff0c\u5f53\u4e0b\u54c8\u54c8\u5927\u7b11\uff0c\u8d70\u8fc7\u6765\u9053\uff1a\u201c\u90a3\u4e24\u6837\u4e1c\u897f\u90e8\u5728\u5979\u7684 \u8eab\u4e0a\uff0c\u53f6\u516c\u5b50\uff0c\u4f60\u641c\u51fa\u6765\u5427\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u5c06\u6b27\u9633\u5a49\u8eab\u4e0a\u7684\u4e1c\u897f\u90e8\u641c\u4e86\u51fa\u6765\uff0c\u4f46\u4ed6\u5374\u4e0d\u8ba4\u5f97\u5929\u5fc3\u77f3\uff0c\u53ea\u597d\u5411\u5929\u9b54\u6559\u4e3b\u8bf7 \u6559\u3002\u5929\u9b54\u6559\u4e3b\u6328\u8fd1\u4ed6\u7684\u8eab\u8fb9\uff0c\u6307\u6307\u70b9\u70b9\uff0c\u53f6\u51b2\u9704\u6ee1\u6000\u611f\u6fc0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6b21\u5168\u9760\u6559\u4e3b\u5e2e\u5fd9\uff0c \u4e0d\u4f46\u63a2\u53d6\u4e86\u524d\u738b\u7684\u79d8\u5bc6\uff0c\u8fd8\u5f97\u5230\u4e86\u8fd9\u6837\u7684\u6b66\u6797\u5f02\u5b9d\uff0c\u6559\u4e3b\u4e4b\u6069\uff0c\u6ca1\u9f7f\u4e0d\u5fd8\uff01\u201d\u5929\u9b54\u6559\u4e3b \u5ffd\u5730\u54c8\u54c8\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u8981\u4f60\u7a7a\u53e3\u9053\u8c22\uff0c\u62ff\u8fc7\u6765\u5427\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u5f3a\u4e2d\u8fd8\u6709\u5f3a\u4e2d\u624b\uff0c\u6211\u8bc8\u4f60\u865e\u5404\u901e\u80fd\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e8c\u56de\u3000\u5584\u6076\u6613\u5206\u987b\u6289\u62e9\u3000\u53cb\u4ec7\u96be\u8fa8\u6700\u5f77\u5fa8 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e09\u5341\u4e8c\u56de\u3000\u5584\u6076\u6613\u5206\u987b\u6289\u62e9\u3000\u53cb\u4ec7\u96be\u8fa8\u6700\u5f77\u5fa8 \u3000\u3000\u53f6\u51b2\u9704\u6014\u4e86\u4e00\u6014\uff0c\u77aa\u773c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u62ff\u6765\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4f60\u522b\u88c5\u7cca\u6d82\u5566\uff0c\u96be\u9053 \u4f60\u8fd8\u4e0d\u77e5\u9053\u6211\u8981\u4ec0\u4e48\uff1f\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u662f\u60f3\u8981\u90a3\u5929\u5fc3\u77f3\u548c\u7f8a\u76ae\u4e66\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u8fd8\u6709\u90a3\u67c4\u5b9d\u5251\u548c\u90a3\u4ef6\u7389\u7532\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u80c3\u53e3\u597d\u5927\uff0c\u6837\u6837\u90fd\u60f3\u8981\u4e48\uff1f\u4e0d\u9519\uff0c\u8fd9\u79d8\u5bc6\u867d\u7136\u662f\u4f60\u8bbe\u8ba1\u63a2\u51fa\u6765 \u7684\uff0c\u4f46\u662f\u6211\u4e0d\u6765\u5236\u4f0f\u6b27\u9633\u5a49\uff0c\u4f60\u65e9\u5df2\u4e27\u547d\u5728\u5979\u7684\u6bd2\u9488\u4e4b\u4e0b\u5566\uff01\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u662f\u8981\u8fc7\u6cb3\u62c6\u6865\u3002\u6253\u5b8c\u658b\u5c31\u4e0d\u8981\u548c\u5c1a\u4e86\uff1f\u201d\u53f6\u51b2\u9704\u6de1 \u6de1\u8bf4\u9053\uff1a\u201c\u8bdd\u53ef\u4e0d\u80fd\u8fd9\u4e48\u8bf4\uff0c\u6c5f\u6e56\u4e0a\u8bb2\u7a76\u6069\u6028\u5206\u660e\uff0c\u4f60\u52a9\u6211\u53d6\u5f97\u8fd9\u4e24\u4ef6\u5b9d\u7269\uff0c\u6211\u5f88\u611f\u6fc0 \u4f60\uff1a\u4f46\u6211\u4e5f\u66fe\u6551\u4e86\u4f60\u7684\u6027\u547d\uff0c\u4f60\u4e5f\u8be5\u611f\u8c22\u6211\u3002\u4f60\u4e00\u6761\u6027\u547d\uff0c\u603b\u6bd4\u5f97\u4e0a\u4e24\u4ef6\u5b9d\u7269\u5427\uff1f\u4e00\u6761 \u6027\u547d\u6362\u4e24\u4ef6\u5b9d\u7269\uff0c\u516c\u5e73\u7684\u8bf4\uff0c\u8c01\u90fd\u4e0d\u6b20\u8c01\u7684\u4eba\u60c5\uff01\u201d\u5929\u9b54\u6559\u4e3b\u51b7\u7b11\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u6069\u6028\u5206 \u660e\uff01\u201d \u3000\u3000\u53f6\u51b2\u9704\u53cc\u7709\u4e00\u7ad6\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u670d\u6c14\u4e48\uff1f\u597d\u5427\uff0c\u53ea\u8981\u4f60\u6709\u8fd9\u672c\u9886\uff0c\u4f60\u5c3d\u7ba1\u6765\u53d6\uff01\u201d \u8bf4\u81f3\u6b64\u5904\uff0c\u6740\u673a\u9661\u8d77\uff0c\u773c\u4e2d\u51f6\u5149\u66b4\u9732\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u53f6\u516c\u5b50\uff0c\u4f60\u4e0d\u7ed9\u4e5f\u5c31\u662f\u4e86\uff0c\u4f55\u5fc5\u52a8\u6012\uff1f\u201d\u53f6\u51b2\u9704\u770b\u51fa\u5979\u4e2d\u6bd2\u4e4b\u540e\uff0c \u6b66\u529f\u5c1a\u672a\u590d\u539f\uff0c\u6740\u673a\u4e00\u8d77\uff0c\u4e0d\u53ef\u6b47\u6b62\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u5982\u8d81\u8fd9\u673a\u4f1a\u65a9\u8349\u9664\u6839\uff0c\u514d\u5f97\u5979\u4ee5 \u540e\u518d\u6765\u7f57\u55e6\u3002\u201d\u4f46\u4ed6\u4e5f\u6709\u51e0\u5206\u987e\u5fcc\u5929\u9b54\u6559\u4e3b\u7684\u4f7f\u6bd2\u672c\u9886\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5c1a\u672a\u6562\u9c81\u83bd\u4ece\u4e8b\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u4e1d\u6beb\u6ca1\u6709\u52a8\u6012\uff0c\u53cd\u800c\u6ee1\u9762\u5806\u7740\u7b11\u5bb9\uff0c\u53c8\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u53f6\u516c\u5b50\uff0c\u6211\u786e\u5b9e\u6ca1\u6709 \u672c\u9886\u5411\u4f60\u786c\u8ba8\u5b9d\u7269\uff0c\u8fd9\u4e00\u70b9\u4f60\u770b\u5bf9\u4e86\u3002\u55ef\uff0c\u6211\u5982\u4eca\u5fc3\u7518\u60c5\u613f\u5411\u4f60\u670d\u8f93\uff0c\u606d\u8d3a\u4f60\u5f97\u5230\u7a00\u4e16 \u4e4b\u73cd\uff01\u201d \u3000\u3000\u53f6\u51b2\u9704\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u4f60\u6765\u8ba8\u597d\uff01\u201d\u5929\u9b54\u6559\u4e3b\u81ea\u987e\u81ea\u4ed6\u8bf4\u4e0b\u53bb\u9053\uff1a\u201c\u4f60\u5f97\u5230\u5b9d\u7269\uff0c \u53ef\u559c\u53ef\u8d3a\uff0c\u4e0d\u8fc7\uff0c\u6211\u4e5f\u6709\u70b9\u4e3a\u4f60\u53ef\u60dc\u554a\uff01\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u867d\u5f97\u5b9d\u7269\uff0c\u5374\u65e0\u798f\u6d88\u53d7\uff01\u201d\u53f6\u51b2\u9704\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u5996\u5987\u80c6\u6562\u5492 \u6211\uff0c\u6211\u4e00\u4e3e\u624b\u5c31\u6bd9\u4e86\u4f60\uff01\u201d\u5929\u9b54\u6559\u4e3b\u5a07\u7b11\u9053\uff1a\u201c\u4f60\u6bd9\u4e86\u6211\u5bb9\u6613\uff0c\u4f46\u6211\u6b7b\u4e86\u4e4b\u540e\uff0c\u53ef\u4e5f\u6ca1 \u4eba\u80fd\u591f\u6551\u4f60\u6027\u547d\u5566\uff01\u53f6\u516c\u5b50\uff0c\u4f60\u522b\u751f\u6c14\uff0c\u4f60\u8bd5\u8fd0\u6c14\u770b\u770b\uff0c\u5c31\u77e5\u9053\u6211\u4e0d\u662f\u865a\u58f0\u6050\u5413\u4e86\uff01\u201d \u3000\u3000\u53f6\u51b2\u9704\u6697\u6697\u5403\u60ca\uff0c\u8bd5\u4e00\u8fd0\u6c14\uff0c\u53ea\u89c9\u771f\u6c14\u884c\u5230\u9888\u80cc\u7684\u201c\u5927\u690e\u7a74\u201d\u4e4b\u65f6\uff0c\u9690\u9690\u4f5c\u75db\uff0c\u5929 \u9b54\u6559\u4e3b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53f6\u516c\u5b50\uff0c\u600e\u4e48\u5566\uff1f\u53ef\u662f\u611f\u5230\u4e0d\u8212\u670d\u4e86\u4e48\uff1f\u201d\u53f6\u51b2\u9704\u5927\u6012\u9053\uff1a\u201c\u4f60\u8fd9 \u5996\u5987\u6363\u4ec0\u4e48\u9b3c\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u6211\u529d\u4f60\u5bf9\u6211\u5ba2\u6c14\u4e00\u4e9b\uff0c\u987b\u77e5\u9053\u73b0\u5728\u662f\u4f60\u8981\u6765\u6c42\u6211\uff0c\u4e0d\u662f\u6211\u6765\u6c42\u4f60\uff01 \u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u5df2\u5728\u4f60\u8eab\u4e0a\u6492\u4e86\u4e00\u70b9\u6bd2\u7c89\uff0c\u4f60\u7684\u751f\u547d\u5927\u7ea6\u53ea\u6709\u4e00\u4e2a\u65f6\u8fb0\u4e86\u3002\u666e\u5929\u4e4b\u4e0b\u3002\u53ea \u6709\u4e24\u4e2a\u4eba\u53ef\u4ee5\u7ed9\u4f60\u89e3\u6551\uff0c\u4e00\u4e2a\u662f\u6211\uff0c\u4e00\u4e2a\u662f\u9634\u5723\u59d1\uff0c\u4e0d\u8fc7\uff0c\u53ea\u6709\u4e00\u4e2a\u65f6\u8fb0\u3002\u5927\u7ea6\u4f60\u603b\u4e0d \u80fd\u5c06\u9634\u5723\u59d1\u8bf7\u6765\u7684\u4e86\uff01\u201d \u3000\u3000\u53f6\u51b2\u9704\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u6709\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u7684\u4e1c\u897f\u6211\u7ed9\u4f60\u5c31\u662f\uff0c\u8bf7\u628a\u89e3\u836f\u62ff\u6765\u3002\u201d\u5929 \u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4f60\u6025\u4ec0\u4e48\uff1f\u542c\u6211\u7684\u5429\u5490\u3002\u201d\u53f6\u51b2\u9704\u5782\u4e0b\u53cc\u624b\uff0c\u4f4e\u58f0\u4e0b\u6c14\u8bf4\u9053\uff1a\u201c\u662f\uff0c\u6211\u542c \u6559\u4e3b\u5429\u5490\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u9000\u540e\u4e94\u6b65\uff0c\u201d\u53f6\u51b2\u9704\u4e0d\u6562\u4e0d\u4f9d\uff0c\u5fd9\u4e0d\u8fed\u7684\u540e\u9000\uff0c\u5929\u9b54\u6559\u4e3b\u9053\uff1a \u201c\u628a\u6211\u6495\u4e0b\u7684\u90a3\u4e24\u9875\u7f8a\u76ae\u4e66\u653e\u5728\u5730\u4e0a\uff0c\u56db\u9897\u5929\u5fc3\u77f3\u4e5f\u653e\u4e0b\u6765\uff0c\u597d\uff0c\u89e3\u4e0b\u8fd9\u67c4\u5b9d\u5251\uff0c\u5bf9\uff0c \u8fd8\u6709\u90a3\u4ef6\u7389\u7532\uff0c\u90fd\u653e\u4e0b\u6765\uff01\u201d\u53f6\u51b2\u9704\u4e00\u4e00\u4f9d\u4ece\uff0c\u8bf4\u9053\uff1a\u201c\u89e3\u836f\u53ef\u4ee5\u7ed9\u6211\u4e86\u5427\uff1f\u201d\u5929\u77a9\u6559 \u4e3b\u9053\uff1a\u201c\u4f60\u6025\u4ec0\u4e48\uff0c\u518d\u9000\u540e\u4e03\u6b65\uff01\u201d \u3000\u3000\u539f\u6765\u5929\u9b54\u6559\u4e3b\u662f\u6015\u53f6\u51b2\u9704\u6697\u7b97\u5979\u3002\u6240\u4ee5\u8981\u4ed6\u79bb\u5f00\u5f97\u8d8a\u8fdc\u8d8a\u597d\uff0c\u5979\u6709\u610f\u62d6\u5ef6\u65f6\u523b\uff0c\u60f3 \u7b49\u81ea\u5df1\u6062\u590d\u4e86\u4e94\u6210\u529f\u529b\uff0c\u518d\u628a\u89e3\u836f\u7ed9\u4ed6\u3002\u90a3\u65f6\uff0c\u5979\u6709\u5b9d\u5251\u5728\u624b\uff0c\u5c31\u4e0d\u6015\u53f6\u51b2\u9704\u62a5\u590d\u4e86\u3002 \u3000\u3000\u53f6\u51b2\u9704\u60f4\u60f4\u4e0d\u5b89\uff0c\u8bf4\u9053\uff1a\u201c\u6559\u4e3b\uff0c\u6211\u4e00\u5207\u90fd\u4f9d\u4ece\u4f60\u4e86\uff0c\u8bf7\u4f60\u522b\u4f5c\u5f04\u6211\u4e86\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4f60\u5bb3\u6015\u4ec0\u4e48\uff1f\u6211\u8bf4\u4e86\u8bdd\u5c31\u7b97\u6570\uff0c\u6211\u62ff\u5230\u4e86\u4e1c\u897f\uff0c\u5f53\u7136\u4f1a\u7ed9\u4f60\u89e3\u836f\u3002 \u4f60\u8981\u77e5\u9053\uff0c\u6211\u53ea\u662f\u8c0b\u8d22\uff0c\u4e0d\u60f3\u5bb3\u547d\uff0c\u7edd\u4e0d\u50cf\u4f60\u4e00\u6837\u53cd\u8138\u65e0\u60c5\u3002\u4f60\u77a7\uff0c\u90a3\u4efd\u7f8a\u76ae\u4e66\u6211\u53ea\u662f \u6495\u4e0b\u4e86\u4e24\u9875\uff0c\u53e6\u5916\u7684\u4e0d\u662f\u90fd\u7ed9\u4e86\u4f60\u4e48\uff1f\u4f60\u53ef\u4ee5\u6309\u56fe\u7d22\u9aa5\uff0c\u53bb\u627e\u524d\u738b\u7684\u5b9d\u85cf\uff0c\u4e5f\u53ef\u4ee5\u5c06\u5176 \u4e2d\u90a3\u5f20\u524d\u738b\u515a\u7fbd\u7684\u540d\u5355\u732e\u7ed9\u56fd\u738b\uff0c\u4fdd\u4f60\u4e00\u751f\u5bcc\u8d35\u3002\u6211\u9001\u4f60\u8fd9\u4efd\u4eba\u60c5\uff0c\u4e5f\u4e0d\u5c0f\u4e86\u5427\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u7ed9\u5979\u5f04\u5f97\u557c\u7b11\u7686\u975e\uff0c\u4f46\u89c9\u9888\u540e\u7684\u5927\u690e\u7a74\u4f3c\u662f\u9488\u523a\u4e00\u822c\uff0c\u75db\u5f97\u66f4\u5389\u5bb3\u4e86\uff0c\u53ea\u597d \u518d\u54c0\u6c42\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u591a\u8c22\u4f60\u5929\u5927\u7684\u4eba\u60c5\uff0c\u6211\u4e00\u751f\u90fd\u4e0d\u4f1a\u5fd8\u8bb0\u3002\u4f60\u8981\u7684\u4e1c\u897f\u73b0\u5728\u90fd\u653e\u5728 \u5730\u4e0a\u4e86\uff0c\u4f60\u5feb\u53bb\u62ff\u5440\uff01\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5438\u4e86\u53e3\u6c14\uff0c\u81ea\u89c9\u529f\u529b\u5df2\u6062\u590d\u4e86\u4e94\u6210\uff0c\u8fd9\u624d\u7ad9\u8d77\u8eab\u6765\uff0c\u7f13\u7f13\u5730\u5411\u90a3\u51e0\u4ef6\u5b9d\u7269 \u8d70\u53bb\u3002 \u3000\u3000\u5c31\u5728\u5979\u4f38\u624b\u53ef\u53ca\u7684\u65f6\u5019\uff0c\u5ffd\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u9053\u91d1\u5149\u76f4\u65f6\u8fc7\u6765\uff0c\u5929\u9b54\u6559\u4e3b\u53ea\u987e \u7740\u9632\u5907\u5bf9\u9762\u7684\u53f6\u51b2\u9704\uff0c\u8fd9\u9053\u91d1\u5149\u5374\u662f\u4ece\u4fa7\u9762\u88ad\u6765\uff0c\u51b7\u4e0d\u53ca\u9632\uff0c\u51e0\u4e4e\u7ed9\u6697\u5668\u9489\u7740\u5979\u7684\u624b\u638c\u3002 \u5e78\u800c\u5929\u9b54\u6559\u4e3b\u5df2\u6062\u590d\u4e86\u4e94\u6210\u529f\u529b\uff0c\u767e\u5fd9\u4e2d\u4e00\u4e2a\u9e5e\u5b50\u7ffb\u8eab\uff0c\u6eda\u8fc7\u4e00\u8fb9\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00 \u58f0\uff0c\u5374\u539f\u6765\u662f\u4e00\u67c4\u91d1\u68ad\uff0c\u90a3\u91d1\u68ad\u4e24\u5934\u950b\u5229\uff0c\u5929\u9b54\u6559\u4e3b\u867d\u7136\u907f\u5f00\uff0c\u6307\u5934\u5374\u5df2\u7ed9\u91d1\u68ad\u5272\u7834\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4f46\u89c1\u4e00\u56e2\u767d\u5f71\uff0c\u95ea\u7535\u822c\u5730\u6251\u4e86\u8fdb\u6765\uff0c\u5929\u9b54\u6559\u4e3b\u5c1a\u672a\u770b\u5f97\u5206\u660e\uff0c\u90a3 \u4eba\u5df2\u628a\u5929\u5fc3\u77f3\u548c\u7f8a\u76ae\u4e66\u90fd\u6293\u5230\u4e86\u624b\u4e2d\u3002\u5929\u9b54\u6559\u4e3b\u6025\u5fd9\u53d1\u51fa\u4e00\u84ec\u6bd2\u9488\uff0c\u90a3\u4eba\u957f\u8896\u4e00\u6325\uff0c\u6570 \u5341\u53e3\u6bd2\u9488\uff0c\u5168\u90fd\u7ed9\u4ed6\u5377\u53bb\u3002 \u3000\u3000\u90a3\u4eba\u6b63\u8981\u53bb\u6293\u5b9d\u5251\uff0c\u4f46\u56e0\u4e3a\u4ed6\u8896\u5377\u6bd2\u9488\uff0c\u5df2\u88ab\u5929\u9b54\u6559\u4e3b\u963b\u8fdf\u4e86\u7247\u523b\uff0c\u53f6\u51b2\u9704\u770b\u51fa\u6709 \u673a\u53ef\u4e58\u3002\u731b\u51b2\u8fc7\u6765\uff0c\u62a2\u5feb\u534a\u6b65\uff0c\u4e00\u811a\u628a\u5b9d\u5251\u8e0f\u4f4f\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u4e5f\u770b\u6e05\u695a\u4e86\u90a3\u4eba\u7684\u9762\u8c8c\uff0c \u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff01 \u3000\u3000\u8fd9\u4eba\u662f\u4e2a\u4e0d\u5230\u4e8c\u5341\u5c81\u7684\u5c11\u5e74\uff0c\u53f6\u51b2\u9704\u4e4b\u6240\u4ee5\u5927\u5403\u4e00\u60ca\uff0c\u8fd8\u4e0d\u5355\u662f\u56e0\u4e3a\u6b64\u4eba\u5e74\u7eaa\u8f7b\u8f7b\uff0c \u6b66\u827a\u9ad8\u5f3a\uff0c\u800c\u662f\u56e0\u4e3a\u8fd9\u4eba\u7684\u76f8\u8c8c\uff0c\u7adf\u7136\u4e0e\u4ed6\u751a\u4e3a\u76f8\u4f3c\u3002 \u3000\u3000\u53f6\u51b2\u9704\u5fc3\u5934\u4e00\u51db\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u90a3\u5c11\u5e74\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5192\u5145\u6211\u8bb8\u591a\u5e74\u4e86\uff0c\u8fd8\u4e0d \u77e5\u9053\u6211\u662f\u8c01\u4e48\uff1f\u201d\u547c\u7684\u4e00\u638c\u5288\u53bb\uff0c\u7adf\u7136\u4e5f\u662f\u5927\u4e58\u822c\u82e5\u638c\uff0c\u800c\u4e14\u6bd4\u53f6\u51b2\u9704\u8fd8\u6df1\u539a\u5f97\u591a\uff0c\u53f6 \u51b2\u9704\u62b5\u6321\u4e0d\u4f4f\uff0c\u7ed9\u4ed6\u4e00\u638c\u9707\u9000\uff01 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u8863\u8896\u4e00\u6325\uff0c\u98de\u51fa\u4e00\u6761\u4e94\u8272\u6591\u6593\u7684\u5f69\u5e26\uff0c\u8165\u98ce\u6251\u9f3b\uff0c\u663e\u7136\u662f\u542b\u6709\u5267\u6bd2\u3002\u90a3\u5c11 \u5e74\u65e0\u6687\u62fe\u53d6\u5b9d\u5251\uff0c\u7eb5\u8eab\u95ea\u8fc7\uff0c\u5411\u5929\u9b54\u6559\u4e3b\u8fd8\u4e86\u4e00\u8bb0\u5288\u7a7a\u638c\u3002 \u3000\u3000\u8fd9\u65f6\u53d8\u6210\u4e86\u53cc\u65b9\u4e89\u593a\u5b9d\u5251\u7684\u5c40\u9762\uff0c\u8c01\u4eba\u5f97\u4e86\u5b9d\u5251\uff0c\u5c31\u53ef\u4ee5\u7a33\u64cd\u80dc\u5238\u3002\u90a3\u5c11\u5e74\u987e\u5fcc\u5929 \u9b54\u6559\u4e3b\u7684\u6bd2\u7d22\uff0c\u4e0d\u6562\u5f2f\u8170\u62fe\u5251\uff0c\u4f46\u4ed6\u4e00\u638c\u7d27\u4f3c\u4e00\u638c\uff0c\u5929\u9b54\u6559\u4e3b\u4e0e\u53f6\u51b2\u9704\u4e5f\u4e0d\u6562\u5411\u524d\u3002 \u3000\u3000\u53f6\u51b2\u9704\u4e00\u58f0\u957f\u5578\uff0c\u53eb\u9053\uff1a\u201c\u6765\u4eba\u5566\uff01\u201d\u5c31\u5728\u6b64\u65f6\uff0c\u90a3\u5c11\u5e74\u7709\u95f4\u4e00\u76b1\uff0c\u8ba1\u4e0a\u5fc3\u95f4\uff0c\u84e6 \u5730\u811a\u5c16\u4e00\u6311\uff0c\u5c06\u90a3\u67c4\u88c1\u4e91\u5b9d\u5251\u6311\u4e86\u8d77\u6765\uff0c\u5929\u9b54\u6559\u4e3b\u6325\u7d22\u6025\u5377\uff0c\u90a3\u5c11\u5e74\u4e00\u8bb0\u5288\u7a7a\u638c\u5c06\u6bd2\u7d20 \u8361\u5f00\uff0c\u90a3\u67c4\u5b9d\u5251\u7ed9\u4ed6\u7684\u638c\u529b\u4e00\u9001\uff0c\u95ea\u7535\u822c\u7684\u5411\u8c37\u4e2d\u83b2\u98de\u53bb\u3002 \u3000\u3000\u53f6\u51b2\u9704\u4e00\u65f6\u95f4\u5c1a\u672a\u60f3\u5230\u8fd9\u5c11\u5e74\u7684\u7528\u610f\uff0c\u5929\u9b54\u6559\u4e3b\u53eb\u58f0\uff1a\u201c\u4e0d\u597d\uff01\u201d\u5fd9\u5411\u8c37\u4e2d\u83b2\u6251\u53bb\uff0c \u53ef\u662f\u8fd9\u5c11\u5e74\u6bd4\u5979\u66f4\u5feb\uff0c\u4e00\u4e2a\u201c\u79fb\u5f62\u6362\u4f4d\u201d\uff0c\u5df2\u62e6\u5728\u5929\u9b54\u6559\u4e3b\u4e0e\u8c37\u4e2d\u83b2\u7684\u4e2d\u95f4\u3002 \u3000\u3000\u90a3\u67c4\u5b9d\u5251\u5e73\u5e73\u7a33\u7a33\u5730\u843d\u5728\u8c37\u4e2d\u83b2\u811a\u8fb9\uff0c\u5c11\u5e74\u53cd\u624b\u4e00\u6307\uff0c\u55e4\u55e4\u58f0\u54cd\uff0c\u7adf\u7136\u5728\u4e00\u4e08\u8ddd\u79bb \u5f00\u5916\uff0c\u4ee5\u4e0a\u4e58\u7684\u91d1\u521a\u6307\u529b\uff0c\u66ff\u8c37\u4e2d\u83b2\u89e3\u5f00\u4e86\u7a74\u9053\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u62ac\u8d77\u4e86\u5b9d\u5251\uff0c\u65e0\u6687\u4e0e\u8fd9\u5c11\u5e74\u53d9\u8bdd\uff0c\u4e00\u5251\u5c31\u5411\u5929\u9b54\u6559\u4e3b\u524a\u53bb\uff0c\u5929\u9b54\u6559\u4e3b\u7684\u6bd2\u7d22 \u592d\u77eb\u56de\u65cb\uff0c\u7adf\u6b32\u7f20\u4e0a\u5979\u7684\u624b\u8155\uff0c\u8c37\u4e2d\u83b2\u5b9d\u5251\u4f7f\u5f00\uff0c\u5149\u8292\u66b4\u957f\u3001\u5251\u5149\u7ed5\u5904\uff0c\u5929\u9b54\u6559\u4e3b\u7684\u6bd2 \u7d22\u53ea\u5269\u4e0b\u4e86\u534a\u6bb5\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u5c11\u5e74\u4e0e\u8c37\u4e2d\u83b2\u8fd9\u4e00\u65b9\u767b\u65f6\u5360\u4e86\u4e0a\u98ce\uff0c\u53f6\u51b2\u9704\u53ea\u89c9\u773c\u775b\u53d1\u9ed1\uff0c\u6c14\u529b\u4e0d\u52a0\uff0c\u614c \u5fd9\u53eb\u9053\uff1a\u201c\u6559\u4e3b\u3001\u6559\u4e3b\uff0c\u7ed9\u6211\u89e3\u836f\uff01\u201d\u8c37\u4e2d\u83b2\u7a7a\u5251\u6325\u821e\uff0c\u5979\u6068\u6781\u5929\u9b54\u6559\u4e3b\uff0c\u7740\u7740\u5411\u5979\u8fdb \u8feb\uff0c\u5929\u9b54\u6559\u4e3b\u7eb5\u6709\u89e3\u6551\u53f6\u51b2\u9704\u4e4b\u5fc3\uff0c\u5374\u54ea\u80fd\u817e\u51fa\u624b\u6765\uff1f \u3000\u3000\u90a3\u5c11\u5e74\u559d\u9053\uff1a\u201c\u65e0\u803b\u5978\u5f92\uff0c\u7ed9\u6211\u8eba\u4e0b\uff01\u201d\u4e00\u6293\u6293\u7740\u4e86\u53f6\u51b2\u9704\u7684\u80cc\u5fc3\uff0c\u53f6\u51b2\u9704\u4e5f\u7ec3\u6709 \u62a4\u4f53\u795e\u529f\uff0c\u5371\u6025\u4e4b\u65f6\uff0c\u8fd0\u65b9\u4e00\u72f0\uff0c\u8863\u88f3\u788e\u88c2\uff0c\u5c11\u5e74\u672a\u80fd\u5c06\u4ed6\u6293\u7262\uff0c\u6b63\u8981\u518d\u6b21\u6293\u4e0b\uff0c\u53ea\u542c \u5f97\u201c\u4e52\u201d\u7684\u4e00\u58f0\uff0c\u65e5\u95f4\u66fe\u4e0e\u6c5f\u3001\u8c37\u4e8c\u4eba\u6076\u6597\u7684\u90a3\u4e24\u4e2a\u548c\u5c1a\u5df2\u7ecf\u7834\u95e8\u9762\u5165\u3002\u80d6\u548c\u5c1a\u5148\u5230\uff0c \u4e00\u6756\u5411\u90a3\u5c11\u5e74\u51fb\u4e0b\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5927\u6012\uff0c\u53cd\u624b\u4e00\u6293\uff0c\u6293\u770b\u6756\u5934\uff0c\u559d\u9053\uff1a\u201c\u53bb\uff01\u201d\u8fd9\u80d6\u548c\u5c1a\u7981\u4e0d\u4f4f\u5c11\u5e74\u7684\u8865\u529b\uff0c \u5e94\u58f0\u800c\u5012\uff0c\u8dcc\u4e86\u4e2a\u56db\u811a\u671d\u5929\u3002\u90a3\u5c11\u5e74\u968f\u7740\u4e00\u8bb0\u5288\u7a7a\u638c\uff0c\u53c8\u628a\u7626\u548c\u5c1a\u7684\u7985\u6756\u8361\u5f00\uff0c\u8c37\u4e2d\u83b2 \u5b9d\u5251\u4e00\u6325\uff0c\u5c06\u4ed6\u7684\u7985\u6756\u4e5f\u524a\u65ad\u4e86\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u548c\u5c1a\u662f\u5b9d\u8c61\u6cd5\u5e08\u7684\u5f1f\u5b50\uff0c\u6b66\u529f\u59d4\u5b9e\u4e0d\u5f31\uff0c\u4ed6\u4eec\u89c1\u8bc6\u4e86\u5b9d\u5251\u7684\u5389\u5bb3\uff0c\u6b65\u6b65\u5c0f\u5fc3\uff0c \u8054\u624b\u518d\u4e0e\u8c37\u4e2d\u83b2\u76f8\u6597\uff0c\u5404\u81ea\u51ed\u770b\u534a\u6bb5\u7985\u6756\uff0c\u907f\u514d\u4e0e\u8c37\u4e2d\u83b2\u7684\u5b9d\u5251\u76f8\u78b0\uff0c\u5c45\u7136\u4f7f\u51fa\u4e86\u5224\u5b98 \u7b14\u7684\u62db\u6570\uff0c\u5bfb\u6687\u62b5\u9699\uff0c\u627e\u8c37\u4e2d\u83b2\u7684\u7a74\u9053\uff0c\u8c37\u4e2d\u83b2\u6709\u5b9d\u5251\u62a4\u8eab\uff0c\u7109\u80fd\u7ed9\u4ed6\u4eec\u70b9\u4e2d\uff1f\u4f46\u7531\u4e8e \u4ed6\u4eec\u8d8b\u907f\u5f97\u5b9c\uff0c\u8c37\u4e2d\u83b2\u518d\u8981\u524a\u65ad\u4ed6\u4eec\u7684\u7985\u6756\uff0c\u5374\u4e5f\u5927\u4e3a\u4e0d\u6613\u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u89c1\u8c37\u4e2d\u83b2\u5b9d\u5251\u5728\u624b\uff0c\u7a33\u5360\u4e0a\u98ce\uff0c\u9042\u653e\u4e0b\u4e86\u5fc3\uff0c\u4e13\u5fc3\u5bf9\u4ed8\u5929\u9b54\u6559\u4e3b\u4e0e\u53f6\u51b2\u9704\u3002 \u5929\u9b54\u6559\u4e3b\u53ea\u6062\u590d\u4e86\u4e94\u6210\u529f\u529b\uff0c\u867d\u7136\u5979\u6709\u8bb8\u591a\u5389\u5bb3\u7684\u6697\u5668\uff0c\u4f46\u90a3\u5c11\u5e74\u638c\u529b\u96c4\u6d51\uff0c\u6bcf\u4e00\u638c\u53d1 \u51fa\uff0c\u90fd\u662f\u52b2\u98ce\u547c\u547c\uff0c\u591a\u5389\u5bb3\u6697\u5668\u4e5f\u6253\u4e0d\u8fdb\u53bb\uff0c\u66f4\u4f55\u51b5\uff0c\u90a3\u5c11\u5e74\u8feb\u5f97\u6781\u7d27\uff0c\u5929\u9b54\u6559\u4e3b\u5e94\u4ed8 \u4e0d\u6687\uff0c\u54ea\u91cc\u8fd8\u80fd\u591f\u62bd\u7a7a\u5077\u53d1\u6697\u5668\uff1f \u3000\u3000\u53f6\u51b2\u9704\u6bd4\u5929\u9b54\u6559\u4e3b\u66f4\u7cdf\uff0c\u5929\u9b54\u6559\u4e3b\u8fd8\u6709\u4e94\u6210\u529f\u529b\u3001\u800c\u4ed6\u7684\u529f\u529b\u5374\u6b63\u5728\u524a\u51cf\u4e4b\u4e2d\uff0c\u6b64 \u53f6\u8fde\u4e94\u6210\u4e5f\u4e0b\u5230\u4e86\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u6697\u89c9\u4e0d\u5999\uff0c\u8ba1\u4e0a\u5fc3\u5934\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u6682\u4e14\u7559\u8fd9\u5c0f\u5b50\u4e00\u547d\uff0c\u660e\u65e5\u518d\u6765\uff01\u201d\u90a3 \u4e24\u4e2a\u548c\u5c1a\u5168\u795e\u4e0e\u8c37\u4e2d\u83b2\u76f8\u6597\uff0c\u8fd9\u65f6\u542c\u5f97\u5929\u9b54\u6559\u4e3b\u7684\u53eb\u58f0\uff0c\u628a\u773c\u4e00\u770b\uff0c\u624d\u77e5\u9053\u5929\u9b54\u6559\u4e3b\u4e0e \u53f6\u51b2\u9704\u5df2\u843d\u5728\u4e0b\u98ce\uff0c\u5f62\u52bf\u6bd4\u81ea\u5df1\u8fd9\u8fb9\u8fd8\u8981\u5371\u9669\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u8981\u77e5\u8fd9\u4e24\u4e2a\u548c\u5c1a\u4e4b\u6240\u4ee5\u6562\u4e8e\u604b\u6218\uff1b\u4e43\u662f\u56e0\u4e3a\u4ed6\u4eec\u6df1\u77e5\u53f6\u51b2\u9704\u7684\u6b66\u529f\u8fdc\u5728\u4ed6\u4eec\u4e4b\u4e0a\uff0c \u800c\u5929\u9b54\u6559\u4e3b\u7684\u4f7f\u6bd2\u672c\u9886\u66f4\u662f\u4e16\u4e0a\u65e0\u53cc\uff0c\u53ea\u8981\u4ed6\u4eec\u6536\u62fe\u4e86\u90a3\u4e2a\u5c11\u5e74\uff0c\u81ea\u5df1\u4fbf\u53ef\u4ee5\u53cd\u8d25\u4e3a\u80dc\u3002\u201d \u54ea\u77e5\u73b0\u5728\u4e00\u770b\uff0c\u5929\u9b54\u6559\u4e3b\u4e0e\u53f6\u51b2\u9704\u7adf\u662f\u81ea\u8eab\u96be\u4fdd\uff0c\u54ea\u8ff9\u8c08\u5f97\u5230\u5e2e\u52a9\u4ed6\u4eec\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u626c\u8a00\u8981\u8d70\uff0c\u6b63\u5408\u4ed6\u4eec\u7684\u5fc3\u610f\u3002\u8fd9\u4e24\u4e2a\u548c\u5c1a\u7acb\u5373\u5e94\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\u3002\u7559\u5f97\u9752 \u5c71\u5728\uff0c\u54ea\u6015\u6ca1\u67f4\u70e7\u3002\u626f\u547c\uff01\u201d\u8c37\u4e2d\u83b2\u867d\u5360\u4e0a\u98ce\uff0c\u82e5\u8bba\u771f\u5b9e\u6b66\u529f\uff0c\u5374\u8fd8\u53ca\u4e0d\u4e0a\u8fd9\u4e24\u4e2a\u548c\u5c1a\uff0c \u5f53\u7136\u662f\u62e6\u963b\u4e0d\u4f4f\uff1a \u3000\u3000\u5929\u9b54\u6559\u4e3b\u4e0e\u53f6\u51b2\u9704\u88ab\u90a3\u5c11\u5e74\u7684\u638c\u529b\u6240\u56f0\uff0c\u672c\u6765\u65e0\u6cd5\u8131\u8eab\uff0c\u8fd9\u65f6\u5f97\u8fd9\u4e24\u4e2a\u548c\u5c1a\u524d\u6765\u4f1a \u5408\uff0c\u5f62\u52bf\u4fbf\u597d\u8f6c\u4e86\u8bb8\u591a\uff0c\u6709\u4e86\u8131\u56f0\u7684\u5e0c\u671b\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u51b7\u7b11\u9053\uff1a\u201c\u522b\u4eba\u53ef\u4ee5\u8d70\uff0c\u4f60\u8fd9\u5978\u5f92\u5374\u4e0d\u8bb8\u8d70\uff0c\u8fd8\u6709\u6559\u4e3b\uff0c\u4f60\u7684\u83b2\u9a7e\u4e5f\u8bf7\u6682\u7559\uff01\u201d \u4ed6\u7d22\u6027\u653e\u8fc7\u90a3\u4e24\u4e2a\u548c\u5c1a\uff0c\u53cc\u638c\u98de\u821e\uff0c\u4ecd\u7136\u6ce8\u5b9a\u4e86\u5929\u9b54\u6559\u4e3b\u4e0e\u53eb\u53f6\u51b2\u9704\u4e8c\u4eba\u3002 \u3000\u3000\u54ea\u77e5\u5929\u9b54\u6559\u4e3b\u8d81\u6b64\u8d22\u673a\uff0c\u5374\u5df2\u62bd\u7a7a\u53d6\u51fa\u4e00\u4ef6\u6697\u5668\uff0c\u53ea\u542c\u5f97\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u7a81\u7136\u98de\u8d77 \u4e86\u4e00\u56e2\u6d53\u96fe\uff0c\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\uff0c\u8fd9\u5e76\u4e0d\u662f\u6bd2\u836f\u6697\u5668\uff0c\u800c\u662f\u63a9\u62a4\u9003\u8d70\u7528\u7684\u3002\u4f46\u5979\u4ee5\u5584\u4e8e\u4f7f\u6bd2 \u9a70\u540d\uff0c\u90a3\u5c11\u5e74\u4e0e\u8c37\u4e2d\u83b2\u4e0d\u80fd\u4e0d\u52a0\u4ee5\u63d0\u9632\uff0c\u5c0f\u5fc3\u6212\u5907\uff0c\u5728\u4e00\u56e2\u6f06\u9ed1\u4e4b\u4e2d\u3002\u5c4f\u606f\u547c\u5438\uff0c\u4e0d\u653e \u9c81\u83bd\u8ffd\u8e2a\u3002\u5929\u9b54\u6559\u4e3b\u4fbf\u5728\u9ed1\u6697\u4e2d\u6084\u6084\u6e9c\u8d70\u3002 \u3000\u3000\u53f6\u51b2\u9704\u66f4\u4e3a\u72e1\u733e\uff0c\u4ed6\u672c\u662f\u4e0e\u90a3\u80d6\u548c\u5c1a\u5e76\u80a9\u5fa1\u654c\uff0c\u6d53\u96fe\u4e00\u8d77\uff0c\u4ed6\u7acb\u5373\u4e00\u628a\u6293\u7740\u80d6\u548c\u5c1a \u7684\u540e\u5fc3\uff0c\u80d6\u548c\u5c1a\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u201c\u81ea\u5df1\u4eba\u201d\u4f1a\u65bd\u6697\u7b97\uff0c\u7ed9\u53f6\u51b2\u9704\u4e00\u63a8\uff0c\u8eab\u4e0d\u7531\u5df2\u7684\u4fbf\u5411\u90a3 \u5c11\u5e74\u649e\u53bb\uff0c\u5c11\u5e74\u5728\u9ed1\u6697\u4e4b\u4e2d\u3002\u53ea\u77e5\u662f\u6709\u654c\u4eba\u88ad\u51fb\uff0c\u54ea\u8fd8\u987e\u5f97\u662f\u8c01\uff0c\u638c\u529b\u4e00\u53d1\uff0c\u767b\u65f6\u9707\u88c2 \u4e86\u90a3\u80d6\u548c\u5c1a\u7684\u79c3\u9885\u3002\u53f6\u51b2\u9704\u65e9\u5df2\u540e\u811a\u63a5\u7740\u524d\u811a\uff0c\u8ddf\u7740\u5929\u9b54\u6559\u4e3b\u6e9c\u8d70\uff0c\u5411\u5979\u8ba8\u53d6\u89e3\u836f\u53bb\u4e86\u3002 \u3000\u3000\u8fd8\u6709\u4e00\u4e2a\u7626\u548c\u5c1a\u4e5f\u662f\u4e2a\u9c81\u83bd\u7684\u811a\u8272\uff0c\u6d53\u96fe\u4e00\u8d77\uff0c\u4fbf\u5f20\u7687\u5931\u63aa\u5730\u4f7f\u5f00\u7985\u6756\u4e71\u6253\u4e00\u901a\uff0c \u6253\u5230\u8c37\u4e2d\u83b2\u8eab\u8fb9\uff01\u8c37\u4e2d\u83b2\u672c\u6765\u4e0d\u4e00\u5b9a\u8981\u6740\u4ed6\uff0c\u4f46\u5374\u4e0d\u80fd\u4e0d\u9632\u5907\u7ed9\u4ed6\u4e71\u6756\u6253\u4e2d\uff0c\u53ea\u597d\u6325\u5251 \u906e\u62e6\uff0c\u7ed3\u679c\u90a3\u7626\u548c\u5c1a\u7ec8\u4e8e\u7ed9\u8c37\u4e2d\u83b2\u4e00\u5251\u523a\u6b7b\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6d53\u96fe\u6d88\u6563\uff0c\u8c37\u4e2d\u83b2\u7591\u56e2\u585e\u80f8\uff0c\u6b63\u8981\u95ee\u90a3\u5c11\u5e74\uff0c\u90a3\u5c11\u5e74\u5ffd\u5730\u53d6\u51fa\u4e00\u4ef6\u9752\u8272 \u7684\u5c0f\u68c9\u8884\uff0c\u5c06\u68c9\u8884\u6495\u7834\uff0c\u4e00\u5f20\u7f8a\u76ae\u4e66\u9732\u4e86\u51fa\u6765\u3002\u90a3\u5c11\u5e74\u5c06\u7f8a\u76ae\u4e66\u6253\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8ba4\u5f97 \u8fd9\u5b57\u8ff9\u5417\uff1f\u201d\u7f8a\u76ae\u4e66\u4e0a\u7684\u5b57\u8ff9\u548c\u8c37\u4e2d\u83b2\u7684\u90a3\u4efd\u4e00\u6a21\u4e00\u6837\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5bf9\u8fd9\u5c11\u5e74\u7684\u8eab\u4efd\u672c\u6765\u5c31\u5df2\u731c\u5230\u4e86\u51e0\u5206\uff0c\u89c1\u4e86\u8fd9\u4e24\u4ef6\u4fe1\u7269\uff0c\u66f4\u8bc1\u5b9e\u4e86\u5979\u6599\u60f3\u65e0 \u5dee\uff0c\u4e0b\u7981\u5931\u58f0\u53eb\u9053\uff1a\u201c\u54e5\u54e5\uff01\u201d\u4e24\u5144\u59b9\u70ed\u6cea\u76c8\u7736\uff0c\u4e07\u8bed\u5343\u8a00\uff0c\u4e0d\u77e5\u4ece\u54ea\u91cc\u8bf4\u8d77\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u4eca\u65e5\u54b1\u4eec\u5144\u59b9\u56e2\u5706\uff0c\u662f\u4e00\u4ef6\u5927\u5927\u7684\u559c\u4e8b\uff0c\u59b9\u59b9\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u518d\u4f24\u5fc3\u4e86\u3002 \u90a3\u5978\u5f92\u5df2\u7ecf\u9003\u8131\uff0c\u54b1\u4eec\u53ef\u5f97\u9632\u5907\u4ed6\u518d\u5f15\u4eba\u6765\uff0c\u987b\u5f97\u65e9\u65e9\u79bb\u5f00\u6b64\u5730\u624d\u662f\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u8d70\u8fc7\u53bb\u5c06\u6c5f\u6d77\u5929\u6276\u8d77\uff0c\u6c5f\u6d77\u5929\u5c1a\u5728\u6655\u8ff7\u72b6\u6001\u4e4b\u4e2d\uff0c\u89e6\u624b\u5982\u711a\uff0c \u70ed\u5ea6\u4f3c\u4e4e\u6bd4\u521a\u624d\u53c8\u9ad8\u4e86\u51e0\u5206\uff0c\u6d51\u8eab\u8863\u670d\u90fd\u5df2\u7ed9\u6c57\u6c34\u6e7f\u900f\uff0c\u8c37\u4e2d\u83b2\u5fe7\u5fc3\u5fe1\u5fe1\uff0c\u5144\u59b9\u91cd\u9022\u7684 \u559c\u60a6\u4e5f\u62b5\u4e0d\u8fc7\u8fd9\u4e2a\u4f24\u5fc3\uff0c\u4e0d\u7981\u53c8\u662f\u6ceb\u7136\u6b32\u6ce3\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u8d70\u8fc7\u53bb\u4e00\u770b\uff0c\u8be7\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u4f60\u600e\u4e48\u628a\u5929\u5fc3\u77f3\u4e0e\u4ed6\u670d\u4e86\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff0c \u201c\u4ed6\u662f\u6211\u7684\u4e00\u4e2a\u597d\u670b\u53cb\uff0c\u8981\u6765\u6551\u6211\u51fa\u53bb\u7684\u3002\u6211\u53ea\u9053\u8ba9\u4ed6\u670d\u4e86\u5929\u5fc3\u77f3\uff0c\u53ef\u4ee4\u4ed6\u529f\u529b\u589e\u8fdb\uff0c \u54ea\u77e5\uff0c\u54ea\u77e5\u2026\u2026\u201d\u8bdd\u672a\u5fc5\u5b8c\uff0c\u90a3\u5c11\u5e74\u5ffd\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u540d\u5b57\uff0c\u53ef\u662f\u53eb\u505a\u6c5f\u6d77\u5929\u4e48\uff1f\u201d\u8c37\u4e2d \u83b2\u5927\u4e3a\u5947\u602a\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\uff0c\u5f85\u6551\u9192\u4e86\u4ed6\u518d\u8bf4\u3002\u201d\u8c37\u4e2d\u83b2\u5927\u559c\u9053\uff1a\u201c\u4f60\u80fd\u6551\u9192\u4ed6\uff1f\u201d \u90a3\u5c11\u5e74\u9053\uff1a\u201c\u6709\u4e00\u4e8b\u4f60\u5c1a\u6765\u77e5\uff0c\u6211\u8fd9\u4ef6\u68c9\u8884\u4e0a\u90a3\u4e03\u9897\u94ae\u6263\u4e5f\u662f\u5b9d\u7269\uff0c\u540d\u4e3a\u5bd2\u661f\u77f3\u3002\u5929\u5fc3 \u77f3\u51fa\u4e8e\u6606\u4ed1\u5c71\u661f\u5bbf\u6d77\u4e4b\u5357\uff0c\u5bd2\u661f\u77f3\u51fa\u4e8e\u661f\u5bbf\u6d77\u4e4b\u5317\u3002\u5929\u5fc3\u77f3\u80fd\u589e\u8fdb\u529f\u529b\uff0c\u5b83\u5374\u4e0d\u80fd\uff0c\u4e0d \u8fc7\u5b83\u5374\u6070\u6070\u80fd\u89e3\u5929\u5fc3\u77f3\u7684\u70ed\u6bd2\uff01\u7236\u738b\u672c\u6765\u662f\u8981\u54b1\u4eec\u5144\u59b9\u4f1a\u9762\u4e4b\u540e\u540c\u670d\u7684\uff0c\u5927\u7ea6\u5f53\u65e5\u56e0\u4e3a \u5927\u8fc7\u5306\u5fd9\uff0c\u4ed6\u5148\u5199\u6211\u8fd9\u4e00\u4efd\u7f8a\u76ae\u4e66\uff0c\u540e\u5199\u4f60\u90a3\u4e00\u4efd\uff0c\u654c\u4eba\u8feb\u8fd1\u5bab\u95e8\uff0c\u5c31\u6765\u4e0d\u53ca\u5728\u4f60\u7684\u90a3 \u4efd\u7f8a\u76ae\u4e66\u4e0a\u5199\u4e0b\u8fd9\u4e2a\u79d8\u5bc6\u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u5fc3\u5934\u559c\u60a6\uff0c\u5bb9\u5149\u7115\u53d1\uff0c\u8bf4\u9053\u3002\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4e8b\u4e0d \u5b9c\u8fdf\uff0c\u54e5\u54e5\uff0c\u5c31\u8bf7\u4f60\u7ed9\u4ed6\u89e3\u4e86\u5929\u5fc3\u77f3\u7684\u70ed\u6bd2\u5427\u3002\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u7ed9\u4ed6\u670d\u4e86\u51e0\u9897\u5929\u5fc3\u77f3\uff1f\u201d \u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4e09\u9897\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u597d\uff0c\u4f60\u62ff\u4e00\u7897\u6c34\u6765\u3002\u201d\u4ed6\u5728\u68c9\u8884\u4e0a\u6458\u4e0b\u4e86\u4e09\u9897\u94ae\u6263\uff0c\u7528\u91d1\u521a\u6307\u529b\u634f\u788e\uff0c \u5c06\u6c34\u548c\u5300\uff0c\u53eb\u8c37\u4e2d\u83b2\u6316\u5f00\u6c5f\u6d77\u5929\u7684\u5634\u5df4\uff0c\u7ed9\u4ed6\u704c\u4e0b\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u597d\u5728\u6c5f\u5c0f\u4fa0\u4e0d\u662f\u5916\u4eba\uff0c\u8fd9\u4e09\u9897\u5929\u5fc3\u77f3\u8ba9\u4ed6\u773c\u4e86\uff0c\u4e5f\u8fd8\u503c\u5f97\u3002\u201d\u8c37\u4e2d \u83b2\u542c\u51fa\u4e86\u54e5\u54e5\u8bdd\u4e2d\u7684\u610f\u601d\uff0c\u674f\u8138\u98de\u971e\uff0c\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5ffd\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u662f\u8c01\uff1f\u201d\u4ed6\u6307\u7684\u662f\u6b27\u9633\u5a49\u3002\u6b27\u9633\u5a49\u7ed9\u70b9\u4e86\u7a74\u9053\uff0c\u52a8\u5f39\u4e0d\u5f97\uff0c \u4f46\u5bf9\u4ed6\u4eec\u5144\u59b9\u7684\u8a00\u8bed\u5374\u542c\u5206\u660e\uff0c\u5fc3\u4e2d\u5927\u6050\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u6211\u521a\u624d\u6ca1\u6709\u9664\u6389\u8c37\u4e2d\u83b2\uff0c \u53ea\u4f2f\u5979\u5982\u4eca\u5374\u4e0d\u80af\u653e\u8fc7\u6211\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u671b\u4e86\u6b27\u9633\u5a49\u4e00\u773c\uff0c\u5374\u4e0d\u4f5c\u58f0\uff0c\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u5979\u7ed9\u4eba\u7528\u91cd\u624b\u6cd5\u70b9\u4e86\u7a74\u9053\uff0c\u65f6\u95f4 \u4e45\u4e86\uff0c\u53ea\u6015\u8981\u53d7\u5185\u4f24\u3002\u5979\u7a76\u7adf\u662f\u53cb\u662f\u654c\uff1f\u201d\u8c37\u4e2d\u83b2\u671b\u7740\u6b27\u9633\u5a49\u832b\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u5979 \u662f\u53cb\u662f\u654c\uff1f\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u662f\u8c01\u70b9\u4e86\u5979\u7684\u7a74\u9041\u7684\uff1f\u770b\u6765\u4e0d\u4f3c\u662f\u4f60\u4eec\u9099\u5c71\u6d3e\u7684\u5185\u5bb6\u624b\u6cd5\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5979\u5c31\u662f\u7ed9\u5192\u5145\u4f60\u7684\u90a3\u4e2a\u597d\u4eba\u70b9\u4e86\u7a74\u9053\u7684\u3002\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u55ef\uff0c\u90a3\u4e48\u5979 \u5e94\u8be5\u662f\u4f60\u7684\u670b\u53cb\u4e86\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u5e76\u4e0d\u8ba4\u8bc6\u5979\uff0c\u6211\u53ea\u77e5\u9053\u5979\u662f\u6b27\u9633\u4ef2\u548c\u7684\u5973\u513f\u3002\u201d \u90a3\u5c11\u5e74\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5979\u662f\u7ec8\u5357\u5c71\u6b27\u9633\u5bb6\u7684\u4eba\uff1f\u90a3\u5c31\u662f\u654c\u4eba\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5ffd\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u7ed9\u5979\u89e3\u4e86\u7a74\u9053\u5427\u3002\u4e0d\u5fc5\u518d\u95ee\u5979\u662f\u53cb\u662f\u654c\u3002\u201d\u90a3\u5c11\u5e74\u751a\u662f\u4e0d \u89e3\uff0c\u4f46\u56e0\u4e3a\u8fd9\u662f\u59b9\u59b9\u7b2c\u4e00\u6b21\u6c42\u4ed6\u7684\u4e8b\u60c5\uff0c\u5f53\u4e0b\u4e5f\u5c31\u4e0d\u518d\u591a\u95ee\uff0c\u4f38\u624b\u4fbf\u7ed9\u6b27\u9633\u5a49\u89e3\u4e86\u7a74\u9053\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u521a\u624d\u4e0d\u6740\u6211\u3001\u6211\u73b0\u5728\u4e5f\u4e0d\u6740\u4f60\uff0c\u4f60\u8d70\u5427\uff01\u201d\u6b27\u9633\u5a49\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u597d\uff0c \u90a3\u4e48\u54b1\u4eec\u5f7c\u6b64\u90fd\u4e0d\u5fc5\u987b\u60c5\uff01\u4f60\u5c0f\u5fc3\u770b\u62a4\u4ed6\u5427\uff0c\u4ed6\u662f\u4f60\u7684\u4e86\uff01\u201d\u8bf4\u5230\u6700\u540e\u4e24\u53e5\uff0c\u58f0\u97f3\u6709\u70b9 \u54fd\u54bd\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u56de\u5230\u6c5f\u6d77\u5929\u7684\u8eab\u8fb9\uff0c\u53cc\u7709\u5fae\u8e59\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u4ed6\u8fd8\u6ca1\u6709\u9192\u5462\uff1f\u201d\u5728\u6c5f\u6d77\u5929\u989d \u5934\u4e0a\u4e00\u6309\u8bd5\u63a2\u4ed6\u7684\u70ed\u5ea6\u3002\u6c5f\u6094\u5929\u5ffd\u5730\u5927\u53eb\u4e00\u58f0\uff0c\u8e66\u5730\u8df3\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u8c37\u4e2d\u83b2\u5927\u5403\u4e00\u60ca\uff0c \u8fde\u5fd9\u5c06\u4ed6\u62b1\u7740\uff0c\u8fde\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff1f\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u53ea\u89c9\u4e00\u80a1\u6d4a\u6c14\u5728\u8eab\u4f53\u5185\u5de6\u51b2\u53f3\u7a81\uff0c\u65e0\u5904\u5ba3\u6cc4\uff0c\u7adf\u4f3c\u8981\u88c2\u8179\u800c\u7206\uff0c\u96be\u8fc7\u975e\u5e38\u3002 \u5fcd\u4e0d\u4f4f\u5728\u8c37\u4e2d\u83b2\u6000\u4e2d\u6323\u624e\uff0c\u8c37\u4e2d\u83b2\u62b1\u6301\u4e0d\u4f4f\uff0c\u6c5f\u6d77\u5929\u8e66\u7684\u53c8\u8df3\u4e86\u5f00\u53bb\uff0c\u5728\u5730\u4e0a\u8f6c\u4e86\u5341\u51e0 \u4e2a\u5708\u5708\uff0c\u53eb\u9053\uff0c\u201c\u95f7\u6b7b\u6211\u5566\uff0c\u95f7\u6b7b\u6211\u5566\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u4e5f\u662f\u60ca\u7591\u4e0d\u5df2\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u5e94\u6709\u6b64\u73b0\u8c61\u3002\u201d\u8fc7\u53bb\u5c06\u6c5f\u6d77\u5929\u4e00\u62c9\uff0c\u5ffd\u89c9\u89e6\u624b\u5982 \u7535\uff0c\u7a81\u7136\u95f4\u7ed9\u6c5f\u6d77\u5929\u7684\u5185\u52b2\u4e00\u9707\uff0c\u51e0\u4e4e\u8dcc\u5012\uff01\u90a3\u5c11\u5e74\u604d\u7136\u5927\u609f\uff0c\u53eb\u9053\uff0c\u201c\u6211\u660e\u767d\u4e86\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u660e\u767d\u4ec0\u4e48\uff1f\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u6c5f\u5144\u521d\u7ec3\u7684\u662f\u4e0d\u662f\u90aa\u6d3e\u5185\u529f\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u4ed6\u5c0f\u65f6\u5019\u66fe\u88ab\u5929\u9b54\u6559\u4e3b\u63b3\u53bb\uff0c\u5f53\u65f6\u5e74\u5e7c\u65e0\u77e5\uff0c\u66fe\u5b66\u4e86\u90a3\u9b54\u5973\u7684\u5185\u529f\u5fc3\u6cd5\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6709\u6b64\u73b0\u8c61\u3002\u6c5f\u5144\uff0c\u4f60\u5feb\u5bfc\u5f15\u771f\u6c14\uff1b\u4ece\u4efb\u8109\u7684\u5929\u9600\u7a74\u5f00\u59cb\uff0c\u5faa\u957f \u5f3a\u7a74\u3001\u94c1\u76c6\u7a74\u3001\u98ce\u5e9c\u7a74\u3001\u5927\u690e\u7a74\u3001\u65e0\u5984\u7a74\u3001\u5f52\u85cf\u7a74\u8fd0\u884c\u4e00\u5468\uff0c\u7136\u540e\u518d\u5bfc\u5165\u7763\u8109\u7684\u7389\u6237\u3001 \u91d1\u6c60\u3001\u7075\u67a2\u3001\u4e2d\u5f80\u8bf8\u7a74\uff0c\u4efb\u7763\u4e8c\u8109\u4e00\u901a\uff0c\u4f60\u7684\u771f\u6c14\u5c31\u53ef\u4ee5\u7eb3\u5165\u4e39\u7530\uff0c\u8fd0\u7528\u5982\u610f\u4e86\u3002\u201d\u8fd9 \u79cd\u5bfc\u6c14\u5f52\u5143\u4e4b\u672f\uff1a\u662f\u4e00\u79cd\u6781\u4e3a\u590d\u6742\u6df1\u5965\u7684\u5185\u529f\uff1b\u6c5f\u6d77\u5929\u867d\u7136\u80fd\u591f\u505a\u5230\uff0c\u4f46\u5018\u82e5\u6ca1\u6709\u8fd9\u5c11 \u5e74\u7684\u6307\u70b9\uff0c\u9519\u5f15\u771f\u6c14\u8fdb\u5165\u53e6\u5916\u7684\u7ecf\u7a74\uff0c\u7acb\u5373\u4fbf\u4f1a\u6709\u8d70\u706b\u5165\u9b54\u7684\u5371\u9669\uff1a \u3000\u3000\u6c5f\u6d77\u5929\u7684\u6b66\u5b66\u9020\u8be3\u751a\u9ad8\uff0c\u4e00\u542c\u4e4b\u4e0b\u4e5f\u4fbf\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u4ed6\u670d\u4e86\u5929\u5fc3\u77f3\u4e4b\u540e\uff0c\u529f\u529b\u5927 \u8fdb\uff0c\u771f\u6c14\u4e0d\u80fd\u6536\u675f\uff1b\u53c8\u56e0\u4e3a\u4ed6\u6700\u521d\u7ec3\u7684\u90aa\u6d3e\u5185\u529f\uff0c\u4ee5\u9738\u9053\u4e3a\u4e3b\uff0c\u6545\u800c\u66f4\u52a0\u5982\u706b\u4e0a\u52a0\u6cb9\uff0c \u4ee4\u5f97\u771f\u6c14\u5145\u585e\u4f53\u5185\uff0c\u96be\u4ee5\u5ba3\u6cc4\u3002 \u3000\u3000\u5f53\u4e0b\u6c5f\u6d77\u5929\u7acb\u5373\u4f9d\u4ece\u90a3\u5c11\u5e74\u7684\u6307\u70b9\uff0c\u76d8\u819d\u800c\u5750\uff0c\u5f00\u59cb\u8fd0\u6c14\uff0c\u679c\u7136\u771f\u6c14\u4e00\u8fdb\u5165\u957f\u5f3a\u7a74\uff0c \u75db\u82e6\u4fbf\u51cf\u8f7b\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u4f46\u5982\u6b64\u4e00\u6765\uff0c\u4ed6\u4eec\u4e5f\u5fc5\u987b\u7b49\u5f85\u6c5f\u6d77\u5929\u771f\u6c14\u8d2f\u901a\u4e4b\u540e\uff0c\u624d\u80fd\u591f\u79bb\u5f00\u6b64\u5730\u4e86\u3002\u90a3\u5c11\u5e74\u4f30 \u8ba1\u6c5f\u6d77\u5929\u987b\u5f97\u534a\u4e2a\u65f6\u8fb0\uff0c\u624d\u80fd\u529f\u884c\u5706\u6ee1\uff0c\u4e0d\u7981\u5fe7\u5fc3\u5fe1\u5fe1\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u4f46\u6c42\u5728\u8fd9\u534a\u4e2a\u65f6 \u8fb0\u4e4b\u5185\uff0c\u5e73\u5b89\u65e0\u4e8b\u3002\u8fc7\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u591a\u5389\u5bb3\u7684\u654c\u4eba\u90a3\u4e5f\u4e0d\u6015\u4e86\u3002\u201d \u3000\u3000\u54ea\u77e5\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u201c\u4e52\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u5927\u95e8\u5df2\u662f\u7ed9\u4eba\u649e\u5f00\uff0c\u53ea\u89c1\u4e00\u5bf9\u4e94\u5341\u5c81\u5de6 \u53f3\u7684\u7537\u5973\u8d70\u4e86\u8fdb\u6765\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5a49\u513f\uff01\u5a49\u513f\uff01\u201d\u539f\u6765\u8fd9\u4e24\u4e2a\u4eba\u6b63\u662f\u6b27\u9633\u4ef2\u548c\u592b\u5987\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u76ee\u5149\u4e00\u77a5\uff0c\u770b\u89c1\u4e86\u6c5f\u6d77\u5929\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u5927\u6012\u9a82\u9053\uff1a\u201c\u597d\u5440\uff0c\u53c8\u662f\u4f60\u8fd9\u5c0f\u5b50\uff01 \u6211\u7684\u5973\u513f\u5462\uff1f\u201d\u539f\u6765\u4ed6\u4eec\u662f\u6765\u8d74\u91d1\u9e70\u5bab\u4e4b\u4f1a\u7684\uff0c\u4e00\u5230\u91d1\u9e70\u5bab\uff0c\u4fbf\u542c\u5230\u5973\u513f\u5077\u4e86\u5730\u56fe\uff0c\u79c1 \u6765\u6b64\u5c9b\u7684\u6d88\u606f\uff0c\u56e0\u800c\u6025\u6025\u5fd9\u5fd9\u8d76\u6765\uff0c\u5c9b\u4e0a\u6b67\u8def\u751a\u591a\uff0c\u4ed6\u4eec\u548c\u6b27\u9633\u5a49\u5404\u8d70\u4e00\u8def\uff0c\u6ca1\u6709\u78b0\u89c1\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u95ee\u7684\u662f\u6b27\u9633\u5a49\u59d1\u5a18\u5427\uff1f\u5979\u521a\u521a\u8d70\u4e86\u3002\u201d\u6b27\u9633\u4e8c\u5a18\u9053\uff1a\u201c\u4f60 \u662f\u8c01\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u662f\u9099\u5c71\u5f1f\u5b50\u8c37\u4e2d\u83b2\u3002\u201d \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u5c31\u662f\u8c37\u4e4b\u534e\u629a\u517b\u7684\u90a3\u4e2a\u5973\u5b69\u5b50\u5417\uff1f\u542c\u8bf4\u4f60\u548c\u8fd9\u5c0f\u5b50\u7684\u4ea4 \u60c5\u5f88\u4e0d\u9519\u554a\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u4e0d\u660e\u767d\u5979\u7684\u7528\u610f\uff0c\u6709\u70b9\u5c34\u5c2c\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u4ee4\u5a9b\u548c\u4ed6\u7684\u4ea4\u60c5\u4e5f\u5f88\u4e0d\u9519\uff0c\u521a\u624d \u5979\u8fd8\u60f3\u6765\u6551\u4ed6\u5462\u3002\u201d\u8c37\u4e2d\u83b2\u8fd9\u4e48\u8bf4\uff0c\u4ee5\u4e3a\u6b27\u9633\u4ef2\u548c\u592b\u5987\u770b\u5728\u5973\u513f\u4efd\u4e0a\uff0c\u5f53\u4e0d\u81f4\u5bf9\u6c5f\u6d77\u5929 \u4e3a\u96be\u3002 \u3000\u3000\u54ea\u77e5\u4e0d\u8bf4\u8fd8\u597d\uff0c\u4e00\u8bf4\u4e4b\u540e\uff0c\u6b27\u9633\u4e8c\u5a18\u9762\u8272\u7acb\u53d8\uff0c\u6307\u7740\u8c37\u4e2d\u83b2\u559d\u9053\uff1a\u201c\u5feb\u8bf4\uff0c\u4f60\u628a\u6211 \u7684\u5973\u513f\u600e\u4e48\u4e86\uff1f\u201d\u8c37\u4e2d\u83b2\u5413\u5f97\u9000\u4e86\u4e24\u6b65\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u65e9\u8bf4\u4e86\u5417\uff1f\u4f60\u7684\u5973\u513f\u5df2\u7ecf\u8d70\u4e86\u3002 \u4f60\u8d76\u5feb\u5411\u6e56\u8fb9\u8d70\uff0c\u4e5f\u8bb8\u8fd8\u8ffd\u5f97\u4e0a\u3002\u201d \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u9b3c\u8bdd\u9a97\u5f97\u4e86\u8c01\uff1f\u6211\u5973\u513f\u7684\u813e\u6c14\u6211\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u5979\u89c1\u4e86\u4f60\u548c \u8fd9\u5c0f\u5b50\u540c\u5728\u4e00\u8d77\uff0c\u4e0d\u6740\u4e86\u4f60\u4eec\uff0c\u5c31\u80af\u8dd1\u5f00\uff1f\u5982\u4eca\u4f60\u4eec\u90fd\u8fd8\u6d3b\u7740\uff0c\u90a3\u5c31\u5b9a\u662f\u5979\u906d\u4e86\u4f60\u4eec\u7684 \u6bd2\u624b\u4e86\u3002\u5feb\u8bf4\uff0c\u4f60\u662f\u628a\u5979\u6740\u4e86\uff0c\u8fd8\u662f\u628a\u5979\u4f24\u4e86\u56da\u7981\u8d77\u6765\uff1f\u201d\u8c37\u4e2d\u83b2\u6025\u9053\uff1a\u201c\u5979\u786e\u5b9e\u6ca1\u6b7b\uff0c \u4e5f\u6ca1\u53d7\u4f24\uff0c\u4f60\u4e0d\u76f8\u4fe1\uff0c\u6211\u4e5f\u65e0\u6cd5\u3002\u201d \u3000\u3000\u6b27\u9633\u4ef2\u548c\u9053\uff1a\u201c\u5979\u4e0d\u80af\u8bf4\uff0c\u4f60\u8fd8\u548c\u5979\u7f57\u55e6\u4ec0\u4e48\uff1f\u8d76\u5feb\u628a\u5979\u6293\u8d77\u6765\u5427\uff0c\u5973\u513f\u6b7b\u4e86\uff0c\u8981 \u5979\u507f\u547d\uff0c\u6ca1\u6b7b\u3002\u5c31\u8feb\u5979\u4ea4\u4eba\uff01\u201d\u6b27\u9633\u4e8c\u5a18\u8fdf\u7591\u7247\u523b\u3002\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u8c41\u4e86\u51fa\u53bb\uff0c\u62fc\u7740 \u5f97\u7f6a\u56fd\u738b\uff0c\u5c06\u8fd9\u4e2b\u5934\u6293\u8d77\u6765\u518d\u8bf4\u3002\u8fd9\u5c0f\u5b50\u4e5f\u4e00\u5e76\u6293\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u6b27\u9633\u4e8c\u5a18\u4e4b\u6240\u4ee5\u8fdf\u8fdf\u672a\u6562\u52a8\u624b\uff0c\u4e43\u662f\u56e0\u4e3a\u8c37\u4e2d\u83b2\u7684\u7279\u6b8a\u8eab\u4efd\u3002\u8c37\u4e2d\u83b2\u662f\u9a6c\u8428\u513f \u56fd\u56fd\u738b\u7684\u4ec7\u4eba\uff0c\u4f46\u53c8\u662f\u56fd\u738b\u8981\u5343\u65b9\u767e\u8ba1\uff0c\u4f7f\u5c3d\u5a01\u80c1\u5229\u8bf1\u624b\u6bb5\uff0c\u8981\u4ece\u5979\u7684\u8eab\u4e0a\u5957\u53d6\u79d8\u5bc6\u7684 \u4eba\u3002\u5728\u79d8\u5bc6\u6ca1\u6709\u5410\u9732\u4e4b\u524d\uff0c\u56fd\u738b\u4e00\u5b9a\u8981\u4fdd\u5168\u5979\u7684\u6027\u547d\uff0c\u6b27\u9633\u4ef2\u548c\u592b\u5987\u6765\u8d74\u91d1\u9e70\u5bab\u4e4b\u5bb4\uff0c \u4e5f\u5c31\u662f\u56fd\u738b\u7684\u5ba2\u4eba\uff0c\u6240\u4ee5\u5728\u5bf9\u8c37\u4e2d\u83b2\u52a8\u624b\u4e4b\u524d\uff0c\u4e0d\u80fd\u4e0d\u614e\u91cd\u8003\u8651\u3002 \u3000\u3000\u8003\u8651\u4e4b\u540e\uff0c\u5230\u5e95\u662f\u5973\u513f\u8981\u7d27\uff0c\u4e24\u592b\u59bb\u540c\u4e00\u5fc3\u601d\uff1a\u201c\u5148\u628a\u8c37\u4e2d\u83b2\u548c\u8fd9\u5c0f\u5b50\u6293\u8d77\u6765\u518d\u8bf4\u3002\u201d \u4e8e\u662f\u4e00\u4e2a\u5954\u5411\u8c37\u4e2d\u83b2\uff0c\u4e00\u4e2a\u5954\u5411\u6c5f\u6d77\u5929\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4f46\u6c42\u80fd\u62d6\u5f97\u4e00\u523b\u4fbf\u662f\u4e00\u523b\uff0c\u4f46\u8fd9\u65f6\u5bf9\u65b9\u5df2\u7ecf\u52a8\u624b\uff0c\u4ed6\u53ea\u5f97\u5148\u53d1\u5236\u4eba\uff0c\u6b27\u9633\u4ef2 \u548c\u8eab\u5f62\u4e00\u8d77\uff0c\u53ea\u89c9\u4e00\u80a1\u52b2\u98ce\u8fce\u9762\u6251\u6765\uff0c\u90a3\u5c11\u5e74\u5df2\u62e6\u5728\u6c5f\u6d77\u5929\u524d\u9762\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8981\u6293\u8c01\uff1f\u201d \u5927\u4e58\u822c\u82e5\u638c\u529b\u731b\u82e5\u96f7\u9706\uff0c\u8fc5\u5373\u53d1\u51fa\u3002 \u3000\u3000\u201c\u6b27\u9633\u4ef2\u548c\u65e9\u5df2\u770b\u51fa\u8fd9\u5c11\u5e74\u529f\u529b\u6df1\u6e5b\uff0c\u4f46\u5374\u8fd8\u60f3\u4e0d\u5230\u4ed6\u7684\u638c\u529b\u7adf\u7136\u8fd8\u8d85\u51fa\u4e86\u4ed6\u7684\u4f30 \u8ba1\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u6b27\u9633\u4ef2\u548c\u7adf\u7ed9\u4ed6\u7684\u638c\u529b\u9707\u5f97\u5230\u9000\u6570\u6b65\uff0c\u4e94\u810f\u516d\u8151 \u90fd\u597d\u4f3c\u7ffb\u8f6c\u8fc7\u6765\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u53c8\u60ca\u53c8\u6012\uff0c\u4f46\u4ed6\u7a76\u7adf\u6709\u6570\u5341\u5e74\u52a8\u529b\uff0c\u662f\u6c5f\u6e56\u4e0a\u9f0e\u9f0e\u6709\u540d\u7684\u5927\u9b54\u5934\uff0c\u771f\u6c14\u4e00 \u51dd\uff0c\u7acb\u5373\u53c8\u518d\u6251\u4e0a\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u638c\u6307\u517c\u65bd\uff0c\u4f7f\u51fa\u4e86\u9739\u96f3\u638c\u548c\u96f7\u795e\u6307\u7684\u7edd\u6280\uff01 \u3000\u3000\u90a3\u5c11\u5e74\u4e00\u638c\u62cd\u51fa\uff0c\u53ea\u89c9\u4e00\u80a1\u70ed\u6c14\uff0c\u76f4\u523a\u638c\u5fc3\uff0c\u5c11\u5e74\u638c\u52bf\u4e00\u533b\uff0c\u6b27\u9633\u4ef2\u548c\u7684\u6307\u950b\u4e00\u6233 \u4e2d\u4ed6\u7684\u638c\u5fc3\uff0c\u5de6\u638c\u8fc5\u5373\u63a5\u4e0a\uff0c\u548c\u4ed6\u53c8\u786c\u5bf9\u4e86\u4e00\u638c\uff0c\u8fd9\u4e00\u56de\u53cc\u65b9\u90fd\u6ca1\u6709\u5360\u5230\u4fbf\u5b9c\uff0c\u6b27\u9633\u4ef2 \u548c\u8e09\u8e09\u8dc4\u8dc4\u7684\u53c8\u9000\u4e86\u4e09\u6b65\uff0c\u90a3\u5c11\u5e74\u638c\u5fc3\u88ab\u96f7\u795e\u6307\u6233\u4e2d\u4e4b\u5904\uff0c\u5219\u6709\u5982\u88ab\u70e7\u7ea2\u7684\u706b\u94c1\u70d9\u8fc7\u4e00 \u822c\u3002 \u3000\u3000\u53cc\u65b9\u5404\u5177\u6212\u5fc3\uff0c\u77ac\u606f\u95f4\u53c8\u8fc7\u4e86\u4e03\u516b\u62db\uff0c\u90a3\u5c11\u5e74\u4e0d\u6562\u518d\u8ba9\u6b27\u9633\u4ef2\u548c\u7684\u6307\u5934\u6233\u4e0a\u3002\u6b27\u9633 \u4ef2\u548c\u4e5f\u4e0d\u6562\u786c\u63a5\u4ed6\u7684\u638c\u529b\u3002\u4e0d\u8fc7\uff0c\u5f7c\u6b64\u7ed5\u8eab\u6e38\u6597\uff0c\u5c11\u5e74\u7684\u638c\u529b\u8f83\u4e3a\u96c4\u6d51\uff0c\u5374\u662f\u7a0d\u5360\u4e0a\u98ce\u3002 \u3000\u3000\u53e6\u4e00\u8fb9\uff0c\u6b27\u9633\u4e8c\u5a18\u4e5f\u5df2\u548c\u8c37\u4e2d\u83b2\u4ea4\u4e0a\u4e86\u624b\uff0c\u8c37\u4e2d\u83b2\u4ed7\u7740\u5b9d\u5251\uff0c\u4f7f\u5f00\u4e86\u8f7b\u7075\u7fd4\u52a8\u7684\u7384 \u5973\u5251\u6cd5\uff0c\u4e5f\u6321\u4f4f\u4e86\u6b27\u9633\u4e8c\u5a18\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u8d5e\u9053\uff1a\u201c\u597d\u4e00\u628a\u5ba4\u5251\uff01\u201d\u5fc3\u4e2d\u5b58\u4e86\u593a\u5251\u4e4b\u610f\uff0c\u84e6\u5730\u6b3a\u8eab\u76f4\u8fdb\uff0c\u957f\u8896\u4e00\u5c55\uff0c \u5f84\u5411\u5251\u950b\u62c2\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u62c2\u624b\u6cd5\u5feb\u5230\u4e86\u6781\u70b9\uff0c\u5185\u4e2d\u85cf\u6709\u9634\u67d4\u7684\u5378\u529b\u529f\u592b\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u6b27 \u9633\u4e8c\u5a18\u7684\u8863\u8896\u88ab\u524a\u53bb\u4e86\u4e00\u5c0f\u7247\uff0c\u4f46\u8c37\u4e2d\u83b2\u7684\u4e3b\u5251\u4e5f\u88ab\u5979\u5f15\u8fc7\u4e00\u8fb9\u3002\u6b27\u9633\u4e8c\u5a18\u7acb\u5373\u4ece\u8896\u4e2d \u51fa\u638c\uff0c\u4e09\u6307\u4e00\u4f38\uff0c\u6765\u6263\u8c37\u4e2d\u83b2\u7684\u8155\u8109\u3002 \u3000\u3000\u54ea\u77e5\u8c37\u4e2d\u83b2\u5251\u4e0a\u7684\u529b\u9053\u867d\u88ab\u5378\u53bb\u4e00\u534a\uff0c\u4f59\u52bf\u672a\u8870\uff0c\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u5979\u5b9d\u5251\u4e00\u7ad6\uff0c \u5251\u950b\u6b63\u5bf9\u7740\u6b27\u9633\u4e8c\u5a18\u7684\u624b\u638c\uff0c\u867d\u7136\u672a\u80fd\u591f\u7acb\u5373\u53d1\u62db\u8fd8\u51fb\uff0c\u4f46\u5018\u82e5\u6b27\u9633\u4e8c\u5a18\u4ecd\u7136\u6043\u5f3a\u593a\u5251\uff0c \u90a3\u5c31\u7b49\u4e8e\u5c06\u624b\u6307\u9001\u4e0a\u53bb\u7ed9\u5979\u524a\u4e86\u3002\u6b27\u9633\u4e8c\u5a18\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u5f97\u64a4\u638c\u53d8\u62db\u3002\u5fc3\u91cc\u6697\u9053\uff1a\u201c\u60f3 \u4e0d\u5230\u8fd9\u4e2b\u5934\u7684\u529f\u529b\u548c\u5b9d\u5251\u7684\u950b\u5229\u5c1a\u5728\u6211\u4f30\u8ba1\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u539f\u6765\u6b27\u9633\u5bb6\u201c\u6d41\u4e91\u8896\u201d\u5378\u529b\u7684\u529f\u592b\u4e43\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u624b\u6cd5\u5947\u5999\u5feb\u6377\uff0c\u800c\u4e14\u8863\u8896\u53c8\u662f\u67d4 \u8f6f\u4e0d\u53d7\u529b\u4e4b\u7269\uff0c\u5018\u975e\u8fd9\u628a\u88c1\u4e91\u5b9d\u5251\u6709\u5439\u6bdb\u7acb\u65ad\u4e4b\u80fd\uff0c\u5251\u950b\u4e00\u88ab\u5b83\u88f9\u4f4f\uff0c\u5b9d\u5251\u5b9a\u7136\u8131\u624b\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u4e00\u6b21\u4e0d\u6210\uff0c\u53c8\u6765\u4e8c\u6b21\u4e09\u6b21\uff0c\u53ea\u89c1\u5979\u53cc\u8896\u8d8a\u821e\u8d8a\u6025\uff0c\u201c\u55e4\u55e4\u201d\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e \u8033\uff0c\u4e00\u5c0f\u7247\u4e00\u5c0f\u7247\u7684\u8863\u8896\u4e0d\u65ad\u7684\u88ab\u5b9d\u5251\u524a\u4e86\u4e0b\u6765\uff0c\u6709\u5982\u5f69\u8776\u7eb7\u98de\uff0c\u8c37\u4e2d\u83b2\u6e10\u6e10\u89c9\u5f97\u5e94\u4ed8 \u8270\u96be\uff0c\u6bcf\u4e00\u5251\u523a\u51fa\uff0c\u4e0d\u662f\u5411\u4e1c\u6b6a\u659c\uff0c\u5c31\u662f\u5411\u897f\u6b6a\u659c\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u5403\u60ca\uff0c\u53ea\u6050\u672a\u80fd\u5c06\u6b27\u9633 \u4e8c\u5a18\u7684\u53cc\u8896\u524a\u5149\uff0c\u5b9d\u5251\u5c31\u4f1a\u7ed9\u5979\u593a\u53bb\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u84e6\u5730\u4e00\u58f0\u5927\u559d\uff0c\u7a81\u7136\u8f6c\u8eab\u53d1\u638c\uff0c\u5f84\u5411\u6b27\u9633\u4e8c\u5a18\u6253\u53bb\u3002\u6b27\u9633\u4e8c\u5a18\u7684\u52a8\u529b\u4e0d\u53ca\u4e08 \u592b\uff0c\u7ed9\u4ed6\u7684\u638c\u529b\u4e00\u9707\uff0c\u7acb\u8db3\u4e0d\u7a33\uff0c\u8eab\u5411\u524d\u503e\u3002\u8c37\u4e2d\u83b2\u4e58\u673a\u4e00\u5251\u523a\u51fa\uff0c\u5728\u5979\u7684\u80a9\u4e0a\u5212\u5f00\u4e86 \u4e00\u9053\u4f24\u53e3\uff0c\u8fd8\u5e78\u5979\u95ea\u907f\u5f97\u5b9c\uff0c\u8981\u4e0d\u7136\u7435\u7436\u9aa8\u4e5f\u9669\u4e9b\u88ab\u8fd9\u4e00\u5251\u523a\u7a7f\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u5927\u60ca\uff0c\u614c\u5fd9\u62a2\u8fc7\u6765\u76f8\u52a9\u59bb\u5b50\u3002\u4f46\u90a3\u5c11\u5e74\u4e5f\u5df2\u4e0e\u8c37\u4e2d\u83b2\u4f1a\u5408\uff0c\u53d8\u6210\u4e86\u53cc\u65b9\u8054 \u624b\u6df7\u6218\u7684\u5c40\u9762\u3002\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u4f60\u770b\u51c6\u4e86\uff0c\u8c01\u6709\u7834\u7efd\uff0c\u5c31\u7ed9\u4ed6\u4e00\u5251\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u4f7f\u51fa\u5927\u4e58\u822c\u82e5\u638c\u529b\uff0c\u6b63\u9762\u4e0e\u6b27\u9633\u4ef2\u548c\u592b\u5987\u76f8\u6297\uff0c\u5c06\u654c\u4eba\u5bf9\u8c37\u4e2d\u83b2\u7684\u653b\u51fb\uff0c\u90fd \u63a5\u4e86\u4e0b\u6765\u3002\u8c37\u4e2d\u83b2\u5219\u5728\u4e00\u65c1\u4e58\u6687\u62b5\u9699\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u4e13\u627e\u654c\u4eba\u7684\u7834\u7efd\u731b\u653b\u3002\u5982\u6b64\u4e00\u6765\uff0c\u8c37 \u4e2d\u83b2\u5b9d\u5251\u7684\u5a01\u529b\u5927\u5927\u589e\u5f3a\uff0c\u6b27\u9633\u4ef2\u548c\u592b\u5987\u65e2\u8981\u8eb2\u907f\u5ba4\u5251\u7684\u653b\u51fb\uff0c\u81ea\u7136\u4e0d\u80fd\u5168\u529b\u5bf9\u4ed8\u8fd9\u4e2a \u5c11\u5e74\u3002\u4e0d\u8fc7\u4e00\u4f1a\uff0c\u8c37\u4e2d\u83b2\u5144\u59b9\u5927\u5360\u4e0a\u98ce\uff0c\u5c06\u6b27\u9633\u4ef2\u548c\u592b\u5987\u8feb\u5f97\u6b65\u6b65\u540e\u9000\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u662f\u4e2a\u6b66\u5b66\u5927\u884c\u5bb6\uff0c\u4ed6\u4e1a\u5df2\u770b\u51fa\u6c5f\u6d77\u5929\u6b63\u5728\u5bfc\u6c14\u5f52\u5143\u3002\u800c\u4e14\u6b63\u5230\u4e86\u7d27\u8981\u7684\u592b \u5934\uff0c\u4ed6\u5374\u4e0d\u77e5\u6c5f\u6d77\u5929\u662f\u670d\u4e86\u5929\u5fc3\u77f3\u800c\u836f\u6027\u53d1\u4f5c\uff0c\u53ea\u9053\u662f\u81ea\u5df1\u7684\u5973\u513f\u7531\u5992\u751f\u6068\uff0c\u4ee4\u6c5f\u6d77\u5929 \u4e2d\u4e86\u6bd2\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u773c\u89c1\u8fd9\u59d3\u6c5f\u7684\u5c0f\u5b50\u65e0\u80fd\u4e3a\u529b\uff0c\u5374\u8bbe\u6cd5\u4e0a\u53bb\u52a8\u4ed6\u3002\u201d\u540c\u65f6\u5fc3\u5185\u53c8\u4e0d \u7981\u6697\u6697\u5403\u60ca\u3002 \u3000\u3000\u4f60\u9053\u6b27\u9633\u4ef2\u548c\u4f55\u4ee5\u6697\u6697\u5fc3\u60ca\uff1f\u539f\u6765\u4ed6\u5728\u6076\u6218\u4e4b\u4e2d\u4ecd\u662f\u4e00\u76f4\u6ce8\u610f\u7740\u6c5f\u6d77\u5929\u3002\u521d\u65f6\u89c1\u6c5f \u6bcf\u5929\u9762\u5982\u91d1\u7eb8\uff0c\u201c\u5929\u5ead\u201d\u4e14\u662f\u4e00\u7247\u7070\u6697\u7684\u989c\u8272\uff0c\u4ed6\u8e0f\u8fdb\u8fd9\u95f4\u5c4b\u5b50\u5230\u73b0\u5728\u4e0d\u8fc7\u662f\u4e00\u70b7\u9999\u7684 \u65f6\u523b\uff0c\u800c\u6b64\u523b\u6c5f\u6d77\u5929\u7684\u9762\u8272\u5df2\u662f\u7ea2\u6da6\u5f02\u5e38\uff0c\u5728\u6b66\u5b66\u884c\u5bb6\u7684\u773c\u4e2d\u770b\u5f97\u51fa\u4ed6\u662f\u795e\u5149\u5185\u8574\uff0c\u771f \u6c14\u5145\u76c8\uff01 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u4e0d\u7981\u5fc3\u5934\u4e00\u51db\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5947\u602a\uff0c\u8fd9\u5c0f\u5b50\u7684\u5185\u529f\u600e\u7684\u5982\u6b64\u6df1\u539a\uff1f\u770b\u6765\u4ed6 \u4e0d\u9700\u591a\u4e45\u5c31\u53ef\u4ee5\u5bfc\u6c14\u5f52\u5143\uff0c\u884c\u52a8\u5982\u5e38\u4e86\u3002\u201d\u6b27\u9633\u4ef2\u548c\u6b64\u9645\u5df2\u843d\u5728\u4e0b\u98ce\uff0c\u5018\u82e5\u6c5f\u6d77\u5929\u6b66\u529f \u4e00\u65e6\u6062\u590d\uff0c\u4ed6\u4eec\u4e24\u592b\u59bb\u5b9a\u662f\u5fc5\u8d25\u65e0\u7591\u3002\u6b27\u9633\u4ef2\u548c\u60f3\u81f3\u6b64\u5904\uff0c\u731b\u5730\u5927\u559d\u4e00\u58f0\uff0c\u638c\u6307\u517c\u65bd\uff0c \u75be\u653b\u51e0\u62db\uff0c\u8eab\u5f62\u4e4b\u5373\u540e\u9000\u3002\u539f\u6765\u4ed6\u65e9\u5df2\u62b1\u5b9a\u201c\u4e09\u5341\u516d\u7740\u8d70\u4e3a\u4e0a\u7740\u201d\u7684\u5fc3\u610f\uff0c\u8868\u9762\u4f6f\u653b\uff0c \u5b9e\u5219\u662f\u63a9\u62a4\u64a4\u9000\u3002\u6b27\u9633\u4e8c\u5a18\u4e0e\u4ed6\u5fc3\u610f\u76f8\u901a\uff0c\u4e24\u592b\u59bb\u4e14\u6218\u4e14\u9000\uff0c\u4e00\u6b65\u6b65\u9000\u5230\u4e86\u95e8\u8fb9\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5144\u59b9\u6068\u4e0d\u5f97\u4ed6\u4eec\u8d70\u5f97\u8d8a\u5feb\u8d8a\u597d\uff0c\u54ea\u77e5\u6b27\u9633\u4ef2\u548c\u8d70\u5230\u95e8\u8fb9\uff0c\u5374\u5ffd\u7136\u505c\u4e0b\u4e86\u811a\u6b65\u3002 \u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u6709\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u771f\u662f\u51d1\u5de7\uff0c\u4eb2\u5bb6\u7fc1\uff0c\u4eb2\u5bb6\u6bcd\u3001\u539f\u6765\u4f60\u4eec\u90fd\u5df2\u6765\u4e86\uff01\u201d \u6b27\u9633\u4ef2\u548c\u9053\uff1b\u201c\u6587\u4eb2\u5bb6\uff0c\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u6c5f\u6d77\u5929\u8fd9\u5c0f\u5b50\u6b63\u5728\u8fd9\u513f\uff01\u201d\u6765\u8005\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f \u6587\u5ef7\u58c1\uff01\u6587\u5ef7\u58c1\u4e00\u6765\uff0c\u6b27\u9633\u4ef2\u548c\u592b\u59bb\u9a6c\u4e0a\u6539\u4e86\u4e3b\u610f\uff0c\u53c8\u518d\u653b\u5165\u95e8\u6765\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u5411\u90a3\u5c11\u5e74\u626b\u4e86\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u6b66\u529f\u5f88\u4e0d\u9519\u5440\uff0c\u602a\u4e0d\u5f97\u53f6\u51b2\u9704\u8d25\u5728\u4f60\u7684\u624b\u4e0b\uff0c \u4f60\u7684\u5e08\u7236\u662f\u8c01\uff1f\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u6211\u5e08\u7236\u7684\u540d\u5b57\u8bf4\u51fa\u6765\u5413\u7834\u4f60\u7684\u80c6\uff0c\u4f60\u4e0d\u95ee\u4e5f\u7f62\u3002\u4f60\u8981\u7ed9 \u53f6\u51b2\u9704\u62a5\u4ec7\uff0c\u5c3d\u7ba1\u4e0a\u6765\uff01\u201d \u3000\u3000\u6587\u5ef7\u58c1\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u771f\u662f\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\uff0c\u4f60\u7684\u6b66\u529f\u867d\u7136\u672c\u9519\uff0c\u4f46\u8981\u60f3\u5728\u6211\u9762\u524d\u901e \u80fd\uff0c\u6700\u5c11\u8fd8\u5f97\u518d\u7ec3\u5341\u5e74\uff01\u5ff5\u5728\u4f60\u8fd9\u8eab\u529f\u592b\u5f97\u6765\u4e0d\u6613\uff0c\u5929\u5fc3\u77f3\u62ff\u51fa\u6765\u5427\uff01\u201d\u8bdd\u81f3\u6b64\u5904\uff0c\u5ffd \u5730\u4e00\u638c\u62cd\u51fa\uff0c\u4f46\u5374\u4e0d\u662f\u5411\u8fd9\u5c11\u5e74\u653b\u51fb\uff0c\u800c\u662f\u6253\u5728\u77f3\u67f1\u4e4b\u4e0a\uff0c\u53ea\u89c1\u77f3\u67f1\u4e0a\u4e00\u4e2a\u638c\u5370\uff0c\u51f9\u5165 \u51e0\u5206\u3002 \u3000\u3000\u539f\u6765\u6587\u5ef7\u58c1\u5728\u9014\u4e2d\u9002\u9047\u5929\u9b54\u6559\u4e3b\uff0c\u4ed6\u662f\u5949\u4e86\u5929\u9b54\u6559\u4e3b\u4e4b\u547d\u6765\u53d6\u5929\u5fc3\u77f3\u7684\u3002\u4ed6\u4e0d\u8ba4\u5f97 \u5929\u5fc3\u77f3\uff0c\u53c8\u6015\u81ea\u5df1\u786c\u6765\uff0c\u8fd9\u5c11\u5e74\u53ef\u80fd\u4f1a\u5c06\u5929\u5fc3\u77f3\u6bc1\u4e86\uff0c\u6545\u6b64\u6709\u610f\u70ab\u8000\u5927\u624b\u5370\u88c2\u77f3\u795e\u529f\uff0c \u60f3\u8feb\u8fd9\u5c11\u5e74\u81ea\u52a8\u652f\u51fa\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u5403\u4e86\u6597\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u8fd9\u5c0f\u5b50\u7adf\u85cf\u6709\u6b66\u6797\u5f02\u5b9d\u5929\u5fc3\u77f3\u5417\uff1f\u201d\u6587\u5ef7\u58c1 \u9053\uff1a\u201c\u4eb2\u5bb6\uff0c\u5929\u5fc3\u77f3\u662f\u672c\u6559\u6559\u4e3b\u8981\u7684\uff0c\u5b9d\u5251\u548c\u7389\u7532\u53ef\u4ee5\u5206\u7ed9\u4f60\u4eec\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u8981\u5929\u5fc3\u53f3\u4e48\uff1f\u55ef\uff0c\u83b2\u59b9\uff0c\u521a\u624d\u4f60\u628a\u5b83\u57cb\u85cf\u5728\u54ea\u513f\uff1f\u201d\u8c37\u4e2d\u83b2\u4e03\u7a8d\u73b2 \u73d1\uff0c\u5f53\u7136\u77e5\u9053\u8fd9\u662f\u5979\u54e5\u54e5\u7ee5\u5175\u4e4b\u8ba1\uff0c\u4fbf\u5373\u8bf4\u9053\uff1a\u201c\u5f85\u6211\u60f3\u60f3\uff0c\u55ef\uff0c\u6211\u8bb0\u8d77\u6765\u4e86\uff0c\u662f\u57cb\u5728 \u4e1c\u9762\u7b2c\u4e09\u5ea7\u9662\u5b50\uff0c\u5de6\u9996\u7b2c\u4e8c\u5ea7\u5047\u5c71\u4ece\u897f\u8fb9\u6570\u8fc7\u6765\u7684\u7b2c\u4e03\u5757\u592a\u6e56\u77f3\u4e0b\u9762\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u731b \u542c\u5f97\u6b27\u9633\u4ef2\u548c\u53eb\u9053\uff1a\u201c\u4eb2\u5bb6\u4f60\u4e0a\u5f53\u5566\uff01\u201d\u6587\u5ef7\u58c1\u6ee1\u8138\u901a\u7ea2\uff0c\u7acb\u5373\u559d\u9053\uff1a\u201c\u4f4f\u53e3\uff01\u201d \u3000\u3000\u8981\u77e5\u6587\u5ef7\u58c1\u7684\u6b66\u5b66\u9020\u8be3\u66f4\u5728\u6b27\u9633\u4ef2\u548c\u4e4b\u4e0a\uff0c\u53ea\u56e0\u4ed6\u521a\u624d\u7ad9\u5728\u95e8\u8fb9\uff0c\u4e00\u5fc3\u60f3\u5de7\u53d6\u8c6a\u593a \u90a3\u6b66\u6797\u5f02\u5b9d\u5929\u5fc3\u77f3\uff0c\u5bf9\u91cc\u9762\u6ca1\u6709\u600e\u4e48\u7559\u610f\u3002\u5982\u4eca\u4ed6\u5f97\u5230\u6b27\u9633\u4ef2\u548c\u63d0\u9192\uff0c\u8e0f\u8fdb\u5c4b\u5185\uff0c\u4e00\u773c \u5c31\u770b\u51fa\u4e86\u6c5f\u6d77\u5927\u7684\u6c14\u8272\u5927\u4e0d\u5bfb\u5e38\uff0c\u7adf\u4f3c\u7ec3\u6210\u4e86\u4e00\u79cd\u6781\u5389\u5bb3\u5185\u529f\u7684\u5f81\u5146\uff0c\u540c\u65f6\u4ed6\u4e5f\u770b\u51fa\u4e86 \u6c5f\u6d77\u5929\u73b0\u5728\u8fd8\u672a\u80fd\u591f\u52a8\u5f39\uff0c\u4f46\u4e8f\u5f88\u5feb\u5c31\u53ef\u4ee5\u529f\u884c\u5706\u6ee1\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u8001\u597d\u5de8\u733e\uff0c\u5f53\u4ed6\u4e00\u53d1\u73b0\u4e86\u6c5f\u6d77\u5929\u7684\u8fd9\u79cd\u60c5\u51b5\uff0c\u8c37\u4e2d\u83b2\u5144\u59b9\u7684\u7f13\u5175\u4e4b\u8ba1\u5982\u4f55\u8fd8 \u7792\u5f97\u8fc7\u4ed6\uff1f\u4ed6\u5fc3\u5ff5\u7535\u8f6c\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u987b\u5f97\u8d76\u5feb\u6536\u62fe\u4e86\u6c5f\u6d77\u5929\uff0c\u5929\u5fc3\u77f3\u5c1a\u4e0d\u59a8\u8fdf\u4e00\u6b65\u518d \u53d6\u3002\u201d\u5f53\u4e0b\u4e00\u58f0\u5927\u559d\uff0c\u53cc\u638c\u9f50\u6325\uff0c\u4fbf\u5411\u6c5f\u6d77\u5929\u51b2\u53bb\u3002 \u3000\u3000\u5728\u8fd9\u7d27\u8981\u5173\u5934\uff0c\u90a3\u5c11\u5e74\u7109\u80fd\u5bb9\u4ed6\u8fc7\u53bb\uff1f\u51e0\u4e4e\u5c31\u5728\u540c\u4e00\u65f6\u95f4\uff0c\u4ed6\u7684\u5927\u4e58\u822c\u82e5\u638c\u529b\u4ea6\u5df2 \u53d1\u51fa\uff0c\u4e24\u80a1\u731b\u70c8\u4e4b\u6781\u7684\u638c\u529b\u4e00\u649e\uff0c\u53d1\u51fa\u4e86\u90c1\u96f7\u822c\u7684\u58f0\u54cd\uff01\u6587\u5ef7\u58c1\u6643\u4e86\u4e00\u6643\uff0c\u90a3\u5c11\u5e74\u7684\u80f8 \u53e3\u5374\u5982\u4e2d\u5de8\u9524\uff0c\u767b\u65f6\u6c14\u8840\u7ffb\u6d8c\uff0c\u9669\u4e9b\u8dcc\u5012\uff01 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6587\u5ef7\u58c1\u53c8\u62a2\u4e0a\u6570\u6b65\uff0c\u7b2c\u4e8c\u638c\u7b2c\u4e09\u638c\u63a5\u7eed\u800c\u6765\uff0c\u4e00\u638c\u731b\u8fc7\u4e00\u638c\uff0c\u90a3 \u5c11\u5e74\u7aed\u5c3d\u5168\u529b\uff0c\u8fde\u63a5\u4e09\u638c\uff0c\u9976\u4ed6\u7ec3\u6709\u62a4\u4f53\u795e\u529f\uff0c\u4ea6\u662f\u652f\u6301\u4e0d\u4f4f\uff0c\u63a5\u5230\u4e86\u7b2c\u56db\u638c\uff0c\u53ea\u89c9\u773c \u524d\u91d1\u661f\u98de\u821e\u3002\u5168\u8eab\u9aa8\u8282\u90fd\u4f3c\u8981\u677e\u6563\u5f00\u6765\uff0c\u53ea\u597d\u95ea\u8fc7\u4e00\u65c1\uff0c\u773c\u7741\u7741\u5730\u770b\u6587\u5ef7\u58c1\u51b2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5b64\u638c\u96be\u9e23\uff0c\u66f4\u654c\u4e0d\u8fc7\u6b27\u9633\u4ef2\u548c\u592b\u5987\u7684\u8054\u624b\u5939\u653b\uff0c\u6218\u5230\u5206\u9645\uff0c\u6b27\u9633\u4e8c\u5a18\u53cc\u8896\u9f50 \u98de\uff0c\u5b9b\u5982\u53cc\u9f99\u51fa\u6d77\uff0c\u500f\u5730\u5c31\u5377\u4f4f\u4e86\u5979\u7684\u5251\u950b\uff0c\u6b27\u9633\u4ef2\u548c\u5288\u624b\u593a\u4e0b\u5979\u7684\u5b9d\u5251\uff0c\u4ea4\u7ed9\u59bb\u5b50\uff0c \u968f\u5373\u7528\u91cd\u624b\u6cd5\u70b9\u4e86\u8c37\u4e2d\u83b2\u7684\u7a74\u9053\u3002\u592b\u5987\u4e8c\u4eba\u4e5f\u4e00\u540c\u5411\u6c5f\u6d77\u5929\u5954\u53bb\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u5148\u5230\uff0c\u4e00\u638c\u5411\u6c5f\u6d77\u5929\u80cc\u5fc3\u51fb\u4e0b\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u6c5f\u6d77\u5929\u52a8\u4e5f\u4e0d\u52a8\uff0c\u6587 \u5ef7\u58c1\u7684\u624b\u638c\u5374\u604d\u5982\u89e6\u7535\uff0c\u7a81\u7136\u611f\u5230\u4e00\u80a1\u5de8\u529b\u53cd\u649e\u56de\u6765\uff0c\u4e0d\u7531\u5f97\u5fc3\u5934\u4e00\u9707\uff0c\u7adf\u4f3c\u7740\u4e86\u5b9a\u8eab \u6cd5\u4f3c\u7684\u5446\u4f4f\u4e86\uff01 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u63a5\u7740\u6765\u5230\uff0c\u4ed6\u5c1a\u672a\u77e5\u6587\u5ef7\u58c1\u5403\u4e86\u5927\u4e8f\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u4e5f\u8ddf\u7740\u4e00\u638c\u5288\u4e0b\uff01\u4ed6\u624b \u6bb5\u66f4\u72e0\uff0c\u8fd9\u4e00\u638c\u5f84\u5288\u6c5f\u6d77\u5929\u7684\u9876\u5fc3\u3002\u6c5f\u6d77\u5929\u4ecd\u7136\u52a8\u4e5f\u4e0d\u52a8\uff0c\u76f4\u5230\u5bf9\u65b9\u7684\u638c\u950b\u5df2\u7ecf\u89e6\u53ca\u5934 \u9885\uff0c\u4ed6\u7684\u5934\u9885\u624d\u7a81\u7136\u5411\u540e\u4e00\u633a\uff0c\u6b27\u9633\u4ef2\u548c\u7684\u9739\u96f3\u638c\u672c\u662f\u6781\u521a\u731b\u7684\u638c\u529b\uff0c\u5c31\u662f\u4e00\u5757\u77f3\u5934\u7740 \u4e86\u4ed6\u7684\u4e00\u638c\u4e5f\u4f1a\u788e\u88c2\uff0c\u54ea\u77e5\u6c5f\u6d77\u5929\u7684\u5934\u9885\u7adf\u4f3c\u6bd4\u94a2\u94c1\u8fd8\u786c\uff0c\u53ea\u542c\u5f97\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u6b27\u9633 \u4ef2\u548c\u7684\u624b\u638c\u9f50\u8155\u7fd8\u8d77\uff0c\u638c\u5fc3\u671d\u5929\uff0c\u7adf\u7136\u653e\u4e0d\u4e0b\u6765\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u6700\u540e\u6765\u5230\uff0c\u8fd9\u65f6\u5979\u4e1a\u5df2\u770b\u51fa\u4e08\u592b\u662f\u5403\u4e86\u5927\u4e8f\uff0c\u4f46\u5979\u81ea\u6043\u6709\u5b9d\u5251\u5728\u624b\uff0c\u5fc3\u60f3\uff1a \u201c\u7eb5\u7136\u4f60\u7ec3\u6210\u4e86\u7edd\u9876\u795e\u529f\uff0c\u5230\u5e95\u662f\u8840\u8089\u4e4b\u8eaf\uff0c\u96be\u9053\u8fd8\u80fd\u6321\u5f97\u4f4f\u6211\u7684\u5b9d\u5251\uff1f\u201d\u6beb\u4e0d\u8e0c\u8e87\uff0c \u4e00\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u4fbf\u5411\u6c5f\u6d77\u5929\u540e\u5fc3\u7684\u201c\u5929\u67a2\u7a74\u201d\u523a\u53bb\uff01\u6b63\u662f\uff1a \u3000\u3000\u795e\u529f\u7ec3\u5c31\u771f\u65e0\u654c\uff0c\u54ea\u6015\u90aa\u9b54\u6c14\u7130\u5149\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e09\u56de\u3000\u7ec3\u5f97\u795e\u529f\u9664\u5927\u654c\u3000\u559c\u95fb\u4e49\u58eb\u62a4\u5b64\u513f \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e09\u5341\u4e09\u56de\u3000\u7ec3\u5f97\u795e\u529f\u9664\u5927\u654c\u3000\u559c\u95fb\u4e49\u58eb\u62a4\u5b64\u513f \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u6c5f\u6d77\u5929\u53ea\u89c9\u4e00\u80a1\u6e05\u51c9\u7684\u5185\u606f\u4ece\u957f\u5f3a\u3001\u5929\u9619\u3001\u534e\u76d6\u3001\u7389\u5802\u3001\u4e2d\u5ead\u3001\u9e20 \u5c3e\u3001\u5173\u5143\u3001\u5730\u85cf\u5404\u5904\u7a74\u9053\u4e00\u8def\u987a\u884c\u4e0b\u6765\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u4efb\u7763\u4e8c\u8109\u5df1\u662f\u5168\u90e8\u6253\u901a\uff0c\u771f\u6c14\u7eb3\u5165 \u4e86\u4e39\u7530\uff0c\u5168\u8eab\u8212\u7545\u3002 \u3000\u3000\u539f\u6765\u4ed6\u56e0\u4e3a\u670d\u98df\u4e86\u5929\u5fc3\u77f3\u4e4b\u540e\uff0c\u771f\u6c14\u7a81\u7136\u5145\u585e\u4f53\u5185\uff0c\u82e5\u4ee5\u672c\u8eab\u539f\u6709\u7684\u529f\u529b\u6536\u67ec\uff0c\u4f9d \u7167\u90a3\u5c11\u5e74\u7684\u7684\u6307\u70b9\uff0c\u6700\u5c11\u4e5f\u8fd8\u5f97\u534a\u4e2a\u65f6\u8fb0\uff0c\u5374\u4e0d\u6599\u6b63\u5f53\u7d27\u8981\u7684\u5173\u5934\uff0c\u6587\u5ef7\u58c1\u4e0e\u6b27\u9633\u4ef2\u548c \u5404\u81ea\u6253\u4e86\u4ed6\u4e00\u638c\uff0c\u4ed6\u771f\u6c14\u5145\u585e\u4f53\u5185\uff0c\u8fd9\u4e24\u638c\u975e\u4f46\u5bf9\u4ed6\u6beb\u65e0\u4f24\u5bb3\uff0c\u53cd\u800c\u4ee4\u4ed6\u771f\u6c14\u52a0\u5feb\u6536\u675f\uff0c \u7b49\u4e8e\u52a9\u4e86\u4ed6\u4e00\u81c2\u4e4b\u529b\uff0c\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u4fbf\u5373\u529f\u884c\u5706\u6ee1\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u90a3\u4e00\u5251\u523a\u51fa\u7684\u65f6\u5019\uff1a\u4e5f\u6b63\u662f\u6c5f\u6d77\u5929\u529f\u884c\u5706\u6ee1\u7684\u65f6\u5019\uff0c\u53ea\u89c1\u4ed6\u8863\u8882\u98d8\u98d8\uff0c\u597d \u50cf\u91cc\u9762\u5145\u6ee1\u4e86\u6c14\u4f53\uff0c\u8863\u88f3\u9f13\u8d77\uff0c\u6b27\u9633\u4e8c\u5a18\u4e00\u5251\u523a\u4e0b\uff0c\u5ffd\u5730\u611f\u5230\u4e00\u80a1\u65e0\u5f62\u7684\u52b2\u529b\u963b\u4f4f\u5251\u5c16\uff0c \u5fc3\u5934\u6b63\u81ea\u4e00\u98a4\uff0c\u5ffd\u542c\u5f97\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u4fe8\u5982\u4e00\u4e2a\u5927\u6c14\u7ffc\u7206\u88c2\uff0c\u6b27\u9633\u4e8c\u5a18\u53d7\u4e0d\u4e86\u90a3\u80a1\u65e0\u5f62 \u7f61\u6c14\u7684\u51b2\u51fb\uff0c\u8dcc\u4e86\u4e2a\u56db\u811a\u671d\u5929\uff0c\u5b9d\u5251\u4e5f\u8131\u624b\u843d\u5730\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e00\u8dc3\u800c\u8d77\uff0c\u559d\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u8fd8\u4f60\u4e00\u638c\uff01\u201d\u8fd9\u4e00\u638c\u671d\u7740\u6587\u5ef7\u58c1\u6253\u53bb\uff0c \u6587\u5ef7\u58c1\u65e9\u5df2\u7ec3\u6210\u201c\u4e09\u8c61\u5f52\u5143\u201d\u7684\u90aa\u6d3e\u795e\u529f\uff0c\u8fd9\u65f6\u521a\u521a\u6d88\u9664\u4e86\u521a\u624d\u6240\u53d7\u7684\u53cd\u9707\u4e4b\u529b\uff0c\u60f3\u8bd5 \u8bd5\u6c5f\u6d77\u5929\u7684\u529f\u529b\u7a76\u7adf\u589e\u957f\u5230\u4f55\u79cd\u7a0b\u5ea6\uff0c\u9042\u786c\u7740\u5934\u76ae\uff0c\u518d\u4e0e\u4ed6\u5bf9\u4e86\u4e00\u638c\u3002 \u3000\u3000\u53cc\u638c\u78b0\u51fb\uff0c\u58f0\u5982\u96f7\u9e23\uff0c\u9707\u8033\u6b32\u804b\uff0c\u6587\u5ef7\u58c1\u201c\u4e09\u8c61\u5f52\u5143\u201d\u7684\u795e\u529f\uff0c\u7adf\u654c\u4e0d\u8fc7\u6c5f\u6d77\u5929\u8fd9 \u4e00\u638c\u4e4b\u529b\u3002\u6587\u5ef7\u58c1\u5927\u53eb\u4e00\u58f0\uff0c\u6574\u4e2a\u8eab\u4e8e\u7ed9\u629b\u4e86\u8d77\u6765\uff0c\u53bb\u52bf\u5982\u7bad\uff0c\u76f4\u98de\u51fa\u95e8\u5916\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6b27\u9633\u8001\u4f2f\uff0c\u5c0f\u4f84\u4e5f\u8fd8\u4f60\u4e00\u638c\u3002\u201d\u6b27\u9633\u4ef2\u548c\u5927\u60ca\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u6211\u5973\u513f \u4e4b\u4e8b\uff0c\u6211\u4ece\u6b64\u4e0d\u518d\u8ffd\u7a76\u4e86\uff0c\u8fd9\u4e00\u638c\u4f60\u4e5f\u4e0d\u5fc5\u8fd8\u5566\uff01\u201d\u6276\u8d77\u4e86\u6b27\u9633\u4e8c\u5a18\uff0c\u4e24\u592b\u59bb\u6025\u6025\u5fd9\u5fd9 \u9003\u8d70\u3002\u90a3\u67c4\u88c1\u4e91\u5b9d\u5251\u5f53\u7136\u662f\u4e0d\u6562\u518d\u8981\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u54c8\u54c8\u5927\u7b11\uff0c\u89e3\u4e86\u8c37\u4e2d\u83b2\u7684\u7a74\u9053\uff0c\u968f\u5373\u4ee5\u4e00\u638c\u8d34\u7740\u90a3\u5c11\u5e74\u7684\u540e\u5fc3\uff0c\u90a3\u5c11\u5e74\u521a\u624d \u4e0e\u6587\u5ef7\u58c1\u5bf9\u4e86\u56db\u638c\u3002\u5143\u6c14\u9887\u53d7\u635f\u4f24\uff0c\u6b64\u65f6\u4ecd\u662f\u59d4\u987f\u4e0d\u582a\uff0c\u6c5f\u6d77\u5929\u8d34\u7740\u4ed6\u7684\u540e\u5fc3\uff0c\u5185\u529b\u6e90 \u6e90\u8f93\u9001\u8fdb\u53bb\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u90a3\u5c11\u5e74\u5df1\u6062\u590d\u539f\u72b6\uff0c\u8bf4\u4e86\u4e00\u58f0\u201c\u8c22\u8c22\u201d\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4eca\u65e5\u5168\u9760\u5144\u53f0\u820d\u547d\u76f8\u52a9\uff0c\u4f60\u624d\u662f\u6211\u8981\u591a\u8c22\u7684\u6551\u547d\u6069\u4eba\u3002\u201d\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a \u201c\u4f60\u4eec\u4e24\u4eba\u90fd\u4e0d\u5fc5\u5ba2\u6c14\u4e86\uff0c\u54b1\u4eec\u8d76\u5feb\u79bb\u5f00\u6b64\u5730\uff0c\u6709\u8bdd\u6162\u8bf4\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u5916\u9762\u4eba\u58f0\u811a\u6b65\u58f0\u7eb7\u81f3\u6c93\u6765\uff0c\u539f\u6765\u56fd\u738b\u5728\u8fd9\u5c0f\u5c9b\u4e0a\u672c\u6765\u57cb\u4f0f\u6709\u8bb8\u591a \u6b66\u58eb\uff0c\u662f\u9632\u5907\u8c37\u4e2d\u83b2\u9003\u8d70\u7684\uff0c\u5e73\u65f6\u672a\u5949\u547d\u4ee4\uff0c\u5374\u4e0b\u8bb8\u8e0f\u8fdb\u5c4b\u5185\u3002\u53f6\u51b2\u9704\u9003\u51fa\u53bb\u65f6\uff0c\u8fd9\u624d \u9881\u4e0b\u547d\u4ee4\u3002\u8981\u8fd9\u4e9b\u6b66\u58eb\u5165\u5c4b\u62ff\u4eba\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u6765\u5f97\u597d\uff0c\u6211\u6b63\u60f3\u6d3b\u52a8\u6d3b\u52a8\u624b\u811a\uff01\u201d\u4e0d\u5f85\u4ed6\u4eec\u8fdb\u6765\uff0c\u5148\u8fce\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u5df2\u662f\u5348\u591c\u65f6\u5206\uff0c\u597d\u5728\u8fd8\u6709\u4e24\u5929\u5c31\u662f\u4e2d\u79cb\uff0c\u6708\u5149\u660e\u4eae\uff1a\u6c5f\u6d77\u5929\u6253\u5f00\u5927\u95e8\uff0c\u5927\u6447\u5927 \u6446\u7684\u51fa\u6765\uff0c\u53ea\u89c1\u5f71\u7ef0\u7ef0\u7684\u7ea6\u6709\u5341\u591a\u4e2a\u4eba\uff0c\u6563\u5728\u5c4b\u5916\u3002 \u3000\u3000\u90a3\u4e9b\u4eba\u53d1\u4e00\u58f0\u558a\uff0c\u56f4\u62e2\u4e0a\u6765\uff0c\u6709\u4eba\u53eb\u9053\uff1a\u201c\u662f\u4e2a\u5c0f\u8d3c\uff0c\u201d\u6709\u4e2a\u624b\u6301\u91d1\u65a7\u7684\u6b66\u58eb\u4f3c\u662f \u5934\u9886\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6bbf\u4e0b\u6709\u547d\uff0c\u9664\u4e86\u90a3\u4e2a\u5973\u7684\u4e0d\u8bb8\u4f24\u5979\u6027\u547d\u4e4b\u5916\uff0c\u8fd8\u6709\u4e24\u4e2a\u7537\u7684\uff0c\u683c\u6740 \u65e0\u59a8\u3002\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5f53\u524d\u4e00\u4e2a\u6b66\u58eb\uff0c\u6325\u52a8\u4e00\u6839\u94c1\u68cd\uff0c\u7167\u5934\u5c31\u6253\u4e0b\u6765\uff0c\u6c5f\u6d77\u5929\u632f\u81c2\u4e00\u683c\uff0c \u53ea\u542c\u5f97\u4e00\u58f0\u88c2\u4eba\u5fc3\u9b44\u7684\u60e8\u53eb\uff0c\u90a3\u6b66\u58eb\u5df2\u662f\u56db\u811a\u671d\u5929\uff0c\u53e3\u4e2d\u72c2\u55b7\u9c9c\u8840\uff0c\u90a3\u6839\u94c1\u68cd\u5c1a\u81ea\u7262\u7262 \u5730\u6293\u5728\u4ed6\u7684\u624b\u4e2d\uff0c\u53ef\u662f\u5374\u5df2\u5f2f\u6210\u4e86\u534a\u6708\u5f62\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e5f\u60f3\u4e0d\u5230\u81ea\u5df1\u7684\u5185\u529b\u7adf\u662f\u5982\u6b64\u4e4b\u5f3a\uff0c\u4e0d\u7981\u5446\u4e86\u4e00\u5446\uff0c\u9887\u4e3a\u540e\u6094\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a \u201c\u65e9\u77e5\u5982\u6b64\uff0c\u5e94\u8be5\u53ea\u7528\u4e09\u6210\u529f\u529b\u3002\u201d \u3000\u3000\u90a3\u4e9b\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u5200\u67aa\u5251\u621f\u7eb7\u7eb7\u6233\u4e0b\uff0c\u6c5f\u6d77\u5929\u53cc\u638c\u4e00\u62ac\uff0c\u53ea\u4ee5\u4e09\u6210\u5a01\u529b\u4f7f\u51fa\u4e86\u5288\u7a7a \u638c\u7684\u529f\u592b\uff0c\u201c\u4e52\u4e53\u201d\u4e24\u58f0\uff0c\u6700\u524d\u9762\u7684\u90a3\u4e24\u4e2a\u6b66\u571f\u5df2\u662f\u7ed9\u4ed6\u7684\u5288\u7a7a\u638c\u529b\u629b\u4e86\u8d77\u6765\uff0c\u521a\u597d\u5c06 \u540e\u9762\u7684\u4e24\u4e2a\u6b66\u58eb\u649e\u5012\uff0c\u56db\u4e2a\u4eba\u8005\u53d8\u6210\u4e86\u6eda\u5730\u846b\u82a6\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e0d\u613f\u591a\u6240\u6740\u622e\uff0c\u4fe1\u624b\u593a\u4e86\u4e00\u6839\u957f\u97ad\uff0c\u4e00\u62db\u201c\u516b\u65b9\u98ce\u96e8\u201d\uff0c\u957f\u97ad\u6325\u4e86\u4e00\u4e2a\u5706\u5708\uff0c \u53ea\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u5200\u67aa\u5251\u621f\uff0c\u6ee1\u7a7a\u98de\u821e\uff0c\u5c11\u8bf4\u4e5f\u6709\u4e03\u516b\u4ef6\u5175\u5668\uff0c\u88ab\u4ed6\u4e00\u97ad \u5c31\u5377\u8131\u4e86\u624b\u3002\u90a3\u73ed\u6b66\u571f\u51e0\u66fe\u89c1\u8fc7\u8fd9\u7b49\u5389\u5bb3\u7684\u529f\u592b\uff0c\u4e0d\u7531\u5f97\u6597\u5fd7\u5168\u6d88\uff0c\u767b\u65f6\u56db\u6563\u3002 \u3000\u3000\u90a3\u4f7f\u91d1\u65a7\u7684\u6b66\u58eb\u5927\u6012\uff0c\u5524\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6211\u4e0e\u4f60\u62fc\u5566\uff01\u201d\u6c5f\u6d77\u5929\u957f\u97ad\u4e00\u6325\uff0c\u5377\u7740\u4e86 \u65a7\u67c4\uff0c\u559d\u58f0\u201c\u5012\u201d\uff01\u90a3\u6b66\u58eb\u5140\u7acb\u5982\u5c71\uff0c\u7adf\u672a\u5e94\u58f0\u800c\u5012\uff0c\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u672c\u9886\u4e0d\u5f31\u3002\u201d \u6b63\u5f85\u518d\u52a0\u4e09\u6210\u529f\u529b\uff0c\u5ffd\u89c1\u90a3\u6b66\u58eb\u4e00\u5bf9\u773c\u73e0\u7a81\u4e86\u51fa\u6765\uff0c\u5934\u4e0a\u9752\u7b4b\u66b4\u9732\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5634\u89d2\u3001 \u9f3b\u5b54\u3001\u8033\u6735\u90fd\u6d41\u51fa\u8840\u6765\uff0c\u6c5f\u6d77\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u677e\u624b\uff0c\u53ea\u89c1\u90a3\u6b66\u58eb\u5934\u9885\u6162\u6162\u4e0b\u5782\uff0c\u91d1\u65a7 \u201c\u5f53\u5577\u201d\u5760\u5730\uff0c\u7adf\u662f\u6b7b\u4e86\u3002 \u3000\u3000\u539f\u6765\u8fd9\u6b66\u58eb\u4e43\u5fa1\u524d\u4f8d\u536b\u7edf\u9886\u7684\u8eab\u4efd\uff0c\u8fd9\u67c4\u91d1\u65a7\u4e43\u662f\u56fd\u738b\u6240\u8d50\uff0c\u4ed6\u751f\u6027\u5014\u5f3a\uff0c\u4e0d\u80af\u8ba9 \u6c5f\u6d77\u5929\u593a\u8d70\u4ed6\u7684\u91d1\u65a7\uff0c\u6545\u6b64\u62fc\u547d\u652f\u6491\uff0c\u4ed6\u7684\u6b66\u529f\u867d\u7136\u9ad8\u51fa\u6d4e\u8f88\uff0c\u5374\u600e\u6321\u5f97\u4f4f\u6c5f\u6d77\u5929\u5185\u529b \u7684\u51b2\u51fb\uff0c\u7ec8\u4e8e\u5fc3\u810f\u7206\u88c2\uff0c\u4e03\u7a8d\u6d41\u8840\u800c\u4ea1\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u656c\u4ed6\u662f\u4e2a\u597d\u6c49\uff0c\u5c06\u91d1\u65a7\u62fe\u8d77\uff0c\u653e\u5728\u4ed6\u7684\u811a\u65c1\uff0c\u5fc3\u4e2d\u9887\u6709\u6b49\u610f\u3002\u90a3\u5c11\u5e74\u4e0e\u8c37\u4e2d \u83b2\u8d70\u4e86\u51fa\u6765\uff0c\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u5927\u83b7\u5168\u80dc\uff0c\u600e\u7684\u53cd\u800c\u76b1\u4e86\u7709\u5934\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u5185 \u529b\u7a81\u589e\uff0c\u5018\u672a\u80fd\u8fd0\u7528\u81ea\u5982\uff0c\u8bef\u6740\u4e8c\u4eba\uff0c\u5b9e\u662f\u4e0d\u5b89\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u53cc\u65b9\u4ea4\u624b\uff0c\u4f24\u4ea1\u96be\u514d\uff0c \u4f60\u4e0d\u6740\u4ed6\u4eec\uff0c\u4ed6\u4eec\u5c31\u6740\u4f60\u4e86\u3002\u522b\u518d\u53d1\u5446\u5566\uff0c\u8d70\u5427\u3002\u201d\u6c5f\u6d77\u5929\u9ed8\u9ed8\u65e0\u8a00\u8ddf\u7740\u5979\u8d70\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5f53\u524d\u5f15\u8def\uff0c\u8d70\u5230\u6e56\u8fb9\uff0c\u90a3\u5c11\u5e74\u62e8\u5f00\u82a6\u82c7\uff0c\u82a6\u82c7\u4e2d\u85cf\u7740\u4e00\u53ea\u5c0f\u8239\u3002 \u3000\u3000\u8fd9\u53ea\u5c0f\u8239\u6b63\u597d\u5bb9\u7eb3\u5f97\u4e0b\u4ed6\u4eec\u4e09\u4e2a\u4eba\uff0c\u8c37\u4e2d\u83b2\u5750\u5728\u4e2d\u95f4\uff0c\u90a3\u5c11\u5e74\u4e0e\u6c5f\u6d77\u5929\u5404\u5750\u4e00\u8fb9\u5212 \u6d46\uff0c\u4ed6\u4eec\u8155\u529b\u5f3a\u52b2\uff0c\u53c8\u90fd\u719f\u6089\u6c34\u6027\uff0c\u6bd4\u6700\u8001\u7ec3\u3001\u6700\u5f3a\u58ee\u7684\u6c34\u624b\u8fd8\u9ad8\u660e\u5f97\u591a\uff0c\u8f7b\u821f\u5982\u7bad\uff0c \u4e0d\u6d88\u4e00\u4f1a\uff0c\u5c31\u628a\u90a3\u5c0f\u5c9b\u8fdc\u8fdc\u629b\u5728\u540e\u9762\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u677e\u4e86\u53e3\u6c14\uff0c\u5411\u90a3\u5c11\u5e74\u91cd\u65b0\u65bd\u5b54\uff0c\u95ee\u9053\uff1a\u201c\u8fd8\u672a\u8bf7\u6559\u5144\u53f0\u9ad8\u59d3\u5927\u540d\u3002\u201d\u90a3\u5c11\u5e74 \u9053\uff1a\u201c\u6211\u6709\u4e24\u4e2a\u59d3\u540d\uff0c\u4e00\u4e2a\u662f\u6c49\u540d\uff0c\u6211\u4e49\u7236\u53f6\u541b\u5c71\u7ed9\u6211\u8d77\u7684\u3002\u2026\u2026\u201d\u6c5f\u6d77\u5929\u53c8\u60ca\u53c8\u559c\uff0c \u53eb\u9053\uff1a\u2018\u4f60\u4e49\u7236\u662f\u53f6\u541b\u5c71\uff0c\u554a\uff0c\u90a3\u4e48\uff0c\u4f60\uff0c\u4f60\u662f\u2014\u2014\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4ed6\u6b63\u662f\u6211\u7684\u54e5\u54e5\u3002\u201d\u90a3\u5c11\u5e74\u8bf4\u9053\uff1a\u201c\u6211\u4e49\u7236\u7ed9\u6211\u8d77\u7684\u6c49\u540d\u53eb\u53f6 \u51b2\u9704\uff0c\u8fd9\u540d\u5b57\u5df1\u6709\u5978\u5f92\u5192\u7528\uff0c\u6211\u4eca\u540e\u662f\u4e0d\u60f3\u518d\u7528\u5b83\u4e86\u3002\u8fd8\u6709\u4e00\u4e2a\u662f\u6211\u7684\u672c\u540d\u3002\u59b9\u59b9\uff0c\u54b1 \u4eec\u7684\u540d\u5b57\u662f\u7236\u738b\u540c\u65f6\u8d77\u7684\uff0c\u4f60\u53ef\u77e5\u9053\u4e86\u4e48\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u7f8a\u76ae\u4e66\u4e0a\u5199\u6709\u6211\u7684\u540d\u5b57\uff0c\u4f46\u6211\u4e0d\u61c2\u5f97\u5ff5\uff0c\u4e5f\u4e0d\u61c2\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u6211\u6015\u6cc4 \u6f0f\u8eab\u4efd\uff0c\u4e00\u5411\u4e0d\u6562\u95ee\u4eba\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u54b1\u4eec\u7684\u59d3\u6c0f\u53eb\u505a\u2018\u5510\u52aa\u2019\u7236\u738b\u5c06\u2018\u73e0\u7a46\u6717\u739b\u2019\u56db\u5b57\u62c6\u5f00\u4f5c\u54b1\u4eec\u7684\u540d\u5b57\uff0c \u6211\u7684\u59d3\u540d\u662f\u5510\u52aa\u73e0\u7a46\uff0c\u4f60\u662f\u5510\u52aa\u6717\u739b\u3002\u73e0\u7a46\u6717\u739b\u7684\u610f\u601d\u662f\u2018\u81f3\u9ad8\u65e0\u4e0a\u2019\uff0c\u4e16\u754c\u6700\u9ad8\u5cf0\u4fbf \u53eb\u505a\u2018\u73e0\u7a46\u6717\u739b\u2019\u5cf0\uff0c\u8fd9\u672c\u662f\u85cf\u8bed\uff0c\u7236\u738b\u501f\u7528\u6765\u4f5c\u54b1\u4eec\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u5c06\u6765\u662f\u8981\u505a\u56fd\u738b\u7684\uff0c\u6211\u5374\u4e0d\u60f3\u505a\u516c\u4e3b\u4e86\u3002\u6211\u5e08\u7236\u5bf9\u6211\u6069\u6df1\u4e49\u91cd\uff0c\u4ee5 \u540e\u6211\u5bf9\u4eba\u8fd8\u662f\u7528\u6211\u539f\u6765\u7684\u540d\u5b57\u2014\u2014\u8c37\u4e2d\u83b2\uff0c\u54b1\u4eec\u5144\u59b9\u4e4b\u95f4\u624d\u7528\u5bb6\u65cf\u7684\u672c\u540d\uff0c\u4f60\u770b\u53ef\u597d\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u770b\u4e86\u5979\u548c\u6c5f\u6d77\u592d\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u4e0d\u60f3\u505a\u56fd\u738b\uff0c\u4e0d\u8fc7\u5bb6\u56fd\u4e4b\u4ec7\u5374\u662f \u5fc5\u987b\u62a5\u7684\u3002\u201d\u8c37\u4e2d\u83b2\u63d2\u53e3\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\uff0c\u6211\u4e0d\u7528\u672c\u540d\uff0c\u5e76\u975e\u662f\u5fd8\u4e86\u7236\u6bcd\u5927\u4ec7\u3002\u201d\u5510\u52aa \u73e0\u7a46\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u6211\u5373\u7b97\u5c06\u6765\u505a\u4e86\u56fd\u738b\uff0c\u4e5f\u4e0d\u4f1a\u5f3a\u8feb\u4f60\u7559\u5728\u8fd9\u5c71\u56fd\u91cc\u505a\u516c\u4e3b\u7684\u3002\u4f60\u8fd9 \u4e00\u751f\u603b\u662f\u8981\u548c\u6c49\u4eba\u5728\u4e00\u8d77\uff0c\u7528\u6c49\u4eba\u7684\u540d\u5b57\uff0c\u81ea\u662f\u65b9\u4fbf\u5f97\u591a\u3002\u201d\u4ed6\u8bdd\u4e2d\u6709\u8bdd\uff0c\u8c37\u4e2d\u83b2\u542c\u51fa \u4e86\u4ed6\u7684\u610f\u601d\uff0c\u4e0d\u7981\u9762\u7ea2\u8fc7\u8033\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u606d\u559c\u4f60\u4eec\u5144\u59b9\u56e2\u5706\u3002\u201d\u90a3\u5c11\u5e74\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd8\u6709\u4e00\u6869\u53ef\u559c\u7684\u4e8b\u60c5\uff0c\u54b1\u4eec\u5e08\u5144\u5f1f\u4eca\u6708\u521d\u6b21\u76f8\u9022\u3002\u201d\u6c5f\u6d77\u5929\u6014\u4e86\u4e00\u6014\uff0c\u53eb\u9053\uff1a \u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u90a3\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u867d\u662f\u540c\u5e74\u751f\u7684\uff0c\u4f46\u4f60\u4eba\u95e8\u5728\u524d\u3002\u6211\u7406\u8be5\u5c0a\u4f60\u4e00\u58f0\u5e08 \u5144\uff0c\u8fd8\u671b\u4f60\u4ee5\u540e\u591a\u591a\u6559\u8bf2\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5927\u559c\u5982\u72c2\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u54b1\u4eec\u662f\u540c\u4e00\u4e2a\u5e08\u7236\uff1f\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6\u73b0\u5728\u54ea\u513f\uff1f\u201d\u8c37 \u4e2d\u83b2\u6b64\u523b\u624d\u77e5\u9053\u54e5\u54e5\u7684\u5e08\u95e8\u6765\u5386\uff0c\u5fd9\u4e0d\u8fed\u7684\u4e5f\u95ee\uff1a\u201c\u6211\u7684\u5e08\u7236\u89c1\u5230\u4e86\u91d1\u5927\u4fa0\u4e48\uff1f\u6211\u88ab\u56da \u7684\u6d88\u606f\u662f\u4e0d\u662f\u5979\u544a\u8bc9\u4f60\u4eec\u7684\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6b63\u662f\u8c37\u5973\u4fa0\u4e0e\u6211\u4eec\u7684\u5e08\u7236\u4f1a\u9762\u4e4b\u540e\uff0c\u6211\u624d\u5949\u4e86\u5e08\u7236\u4e4b\u547d\uff0c\u5230\u8fd9\u91cc\u6765 \u7684\u3002\u4ed6\u4eec\u90fd\u5df2\u7ecf\u5230\u4e86\u4eac\u57ce\uff0c\u51c6\u5907\u53c2\u52a0\u540e\u65e5\u7684\u91d1\u9e70\u5bab\u4e4b\u4f1a\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u89c1\u8fc7\u6211\u7684\u5e08\u7236\uff0c\u6211\u7684\u906d\u9047\uff0c\u4f60\u662f\u77e5\u9053\u7684\u4e86\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u70b9\u4e86\u70b9 \u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u628a\u6211\u7684\u906d\u9047\uff0c\u8bb2\u7ed9\u4f60\u542c\u3002\u201d \u3000\u3000\u539f\u6765\u5c31\u5728\u4e18\u5ca9\u5411\u7ffc\u4ef2\u725f\u6258\u5b64\uff0c\u7ffc\u4ef2\u725f\u5c06\u8c37\u4e2d\u83b2\u643a\u4e0a\u9099\u5c71\u7684\u90a3\u7f55\uff0c\u5510\u52aa\u73e0\u7a46\u7684\u4e49\u7236\u53f6 \u541b\u5c71\u4e5f\u906d\u9022\u4e86\u4e0d\u5e78\u3002\u5728\u4e00\u4e2a\u6708\u9ed1\u98ce\u9ad8\u7684\u665a\u4e0a\uff0c\u7a81\u7136\u6709\u4e00\u7fa4\u8499\u9762\u6c49\u5b50\u7834\u95e8\u800c\u5165\uff0c\u6740\u6b7b\u4e86\u53f6 \u541b\u5c71\uff0c\u52ab\u53bb\u4e86\u5510\u52aa\u73e0\u7a46\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u8bf4\u9053\uff1a\u201c\u540e\u6765\u6211\u624d\u77e5\u9053\uff0c\u8fd9\u73ed\u51f6\u5f92\u4e43\u662f\u5978\u738b\u6d3e\u51fa\u6765\u7684\u3002\u4ed6\u6d3e\u51fa\u4e24\u6279\u4eba\uff0c\u4e00 \u6279\u6765\u6697\u6740\u53f6\u541b\u5c71\uff0c\u4e00\u6279\u6765\u6697\u6740\u4e18\u5ca9\uff0c\u76ee\u7684\u5c31\u662f\u8981\u5c06\u54b1\u4eec\u5144\u59b9\u6349\u62ff\u56de\u53bb\u3002\u4e5f\u8bb8\u56e0\u4e3a\u6211\u662f\u53ef \u4ee5\u7ee7\u627f\u738b\u4f4d\u7684\u7537\u5b69\uff0c\u597d\u738b\u5bf9\u6211\u66f4\u4e3a\u91cd\u89c6\uff0c\u6d3e\u53bb\u6697\u6740\u6211\u4e49\u7236\u7684\u4eba\uff0c\u6b66\u529f\u4e2a\u4e2a\u7cbe\u5f3a\uff0c\u6211\u4e49\u7236 \u5f53\u573a\u5c31\u7ed9\u4ed6\u4eec\u6253\u6b7b\u4e86\u3002\u4e18\u5ca9\u5219\u6ca1\u6709\u5f53\u573a\u8eab\u6b7b\uff0c\u800c\u4e14\u8fd8\u4fdd\u62a4\u4e86\u4f60\uff0c\u4e0d\u4ee4\u51f6\u5f92\u5f97\u901e\uff0c\u5c06\u4f60\u643a \u8d70\uff0c\u4f46\u4ed6\u4e5f\u53d7\u4e86\u5f88\u91cd\u7684\u5185\u4f24\uff0c\u6240\u4ee5\u624d\u6709\u540e\u6765\u5411\u7ffc\u5e2e\u4e3b\u6258\u5b64\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5782\u6cea\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u8001\u4eba\u5bb6\u5bf9\u54b1\u4eec\u5144\u59b9\u7684\u5927\u6069\u5927\u5fb7\uff0c\u662f\u6ca1\u6cd5\u5b50\u62a5\u7b54\u7684\u4e86\u3002\u5c31\u662f \u4e3a\u4e86\u4ed6\u4eec\u4e24\u4f4d\u8001\u4eba\u5bb6\u7684\u65e0\u8f9c\u9001\u547d\uff0c\u54b1\u4eec\u4e5f\u8be5\u66ff\u4ed6\u4eec\u62a5\u4ec7\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4e3a\u54b1\u4eec\u65e0\u8f9c\u9001\u547d\u7684\u5171\u6709\u4e09\u4f4d\u8001\u4eba\u5bb6\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4f4d\u662f\u8c01\uff1f\u201d \u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u662f\u9690\u5c45\u5728\u559c\u9a6c\u62c9\u96c5\u5c71\u7684\u65b9\u8001\u524d\u8f88\uff0c\u65b9\u4eca\u660e\u3002\u201d\u6c5f\u6d77\u592d\u95ee\u9053\uff1a\u201c\u662f\u4e0d\u662f\u56db \u5341\u5e74\u524d\u53f7\u79f0\u795e\u62f3\u65e0\u654c\u7684\u90a3\u4f4d\u65b9\u5927\u4fa0\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u662f\u4ed6\u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u8be7\u9053\uff1a \u201c\u4f60\u4e5f\u662f\u548c\u6211\u4eec\u4e00\u822c\u5e74\u7eaa\uff0c\u600e\u4e48\u77e5\u9053\u6b64\u4eba\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u542c\u5e08\u7236\u8bf4\u8fc7\uff0c\u8fd9\u4f4d\u65b9\u8001\u524d\u8f88\u662f\u548c\u5929\u5c71\u638c\u95e8\u5510\u6653\u6f9c\u540c\u4e00\u8f88\u4efd\u7684\u4eba\uff0c\u548c \u6211\u7684\u5e08\u4e49\u4e5f\u662f\u5fd8\u5e74\u4e4b\u652f\u3002\u6211\u8fd9\u6b21\u51fa\u6765\u5bfb\u8bbf\u5e08\u7236\uff0c\u672c\u6765\u5c31\u62df\u597d\u4e86\u8ba1\u5212\uff0c\u5148\u4e0a\u5929\u5c71\u53bb\u62dc\u89c1\u5510 \u6653\u6f9c\uff0c\u5018\u82e5\u6ca1\u6709\u5e08\u7236\u7684\u6d88\u606f\uff0c\u5c31\u518d\u4e0a\u559c\u9a6c\u62c9\u96c5\u5c71\u5411\u65b9\u8001\u524d\u8f88\u6253\u542c\u3002\u73b0\u5728\u5e08\u7236\u5df2\u7ecf\u6765\u5230\u6b64 \u5730\uff0c\u6211\u4e5f\u53ef\u4ee5\u5c11\u8d70\u8bb8\u591a\u8def\u4e86\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u53f9\u606f\u9053\uff1a\u201c\u4f60\u6599\u5f97\u4e0d\u9519\uff0c\u54b1\u4eec\u7684\u5e08\u7236\u679c\u7136\u662f\u5230\u4e86\u559c\u9a6c\u62c9\u96c5\u5c71\u63a2\u8bbf\u65b9\u8001\u524d\u8f88\uff0c \u53ef\u60dc\u4ed6\u6765\u7684\u90a3\u5929\uff0c\u4e5f\u6b63\u662f\u65b9\u8001\u524d\u8f88\u5f25\u7559\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u65b9\u8001\u524d\u8f88\u65e2\u662f\u4e00\u4f4d\u9690\u4fa0\uff0c\u4f55\u4ee5\u53c8\u4f1a\u4e0e\u54b1\u4eec\u5144\u59b9\u4e4b\u4e8b\u53d1\u751f\u5e72\u8fde\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u8bf4\u8d77\u6765\u8fd9\u4f4d\u65b9\u8001\u524d\u8f88\u7684\u9ad8\u98ce\u539a\u4e49\uff0c\u5c31\u66f4\u503c\u5f97\u4eba\u656c\u4f69\u4e86\u3002\u7236\u7389\u751f\u524d\u6700 \u559c\u7ed3\u4ea4\u6b66\u5b66\u9ad8\u660e\u4e4b\u58eb\uff0c\u4e18\u5ca9\u548c\u53f6\u541b\u5c71\u5c31\u662f\u4ed6\u4ece\u4e2d\u539f\u8058\u6765\u7684\u5ba2\u537f\uff0c\u4e34\u5230\u5371\u96be\u4e4b\u65f6\uff0c\u6ee1\u671d\u6587 \u6b66\u9003\u907f\u4e00\u7a7a\uff0c\u9760\u4e86\u8fd9\u4e24\u4f4d\u5ba2\u537f\u4e4b\u529b\uff0c\u624d\u4fdd\u5168\u4e86\u54b1\u4eec\u5144\u59b9\u3002 \u3000\u3000\u201c\u65b9\u8001\u524d\u8f88\u4e0d\u662f\u7236\u738b\u7684\u5ba2\u537f\uff0c\u4f46\u4ed6\u7684\u5927\u540d\uff0c\u7236\u738b\u65e9\u5c31\u4ef0\u6155\u4e86\u7684\uff0c\u56e0\u6b64\u4e5f\u66fe\u6d3e\u9063\u4f7f\u8005\uff0c \u643a\u5e26\u793c\u7269\uff0c\u5343\u8f9b\u4e07\u82e6\u5730\u627e\u5230\u4e86\u4ed6\u9690\u5c45\u4e4b\u5904\uff0c\u8bf7\u4ed6\u51fa\u5c71\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u65b9\u8001\u524d\u8f88\u5bf9\u90a3\u4f7f\u8005\u8bf4\u4ed6\u907f\u4e16\u5df2\u4e45\uff0c\u4e0d\u613f\u518d\u5c65\u5c18\u4fd7\uff0c\u793c\u7269\u4e5f\u6ca1 \u6709\u6536\u4e0b\uff0c\u90a3\u4f7f\u8005\u53ea\u597d\u5931\u671b\u800c\u5f52\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4ed6\u65e2\u6ca1\u6709\u5e94\u7236\u738b\u4e4b\u8058\uff0c\u7136\u5219\u540e\u6765\u53c8\u600e\u4f1a\u5377\u5165\u6f29\u6da1\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u73b0\u5728\u518d\u56de\u8fc7\u5934\u6765\u8bf4\u6211\u7684\u906d\u9047\uff0c\u8bf4\u4e0b\u53bb\u4f60\u5c31\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u88ab\u90a3\u4e9b\u51f6\u5f92\u52ab\u8d70\uff0c\u4e00\u8def\u897f\u884c\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65e5\u5b50\uff0c\u6709\u4e00\u5929\u5230\u4e86\u4e00\u5ea7\u9ad8\u5c71\u811a\u4e0b\uff0c \u8fd9\u5ea7\u5c71\u5f88\u7279\u522b\uff0c\u53cc\u5cf0\u631f\u5cd9\uff0c\u5c71\u8170\u6709\u4e2a\u90e8\u4efd\u597d\u50cf\u662f\u88ab\u5de8\u65a7\u5288\u5f00\u4f3c\u7684\uff0c\u53ef\u4ee5\u901a\u8fc7\u884c\u4eba\uff0c\u6709\u6761 \u6cb3\u6d41\u4ece\u8fd9\u5c71\u811a\u6d41\u8fc7\u3002\u540e\u6765\u6211\u624d\u77e5\u9053\u8fd9\u4e2a\u5730\u65b9\u540d\u53eb\u201c\u94c1\u95e8\u5173\u201d\uff0c\u7a7f\u8fc7\u2018\u94c1\u95e8\u5173\u2019\uff0c\u5c31\u8e0f\u5165 \u672c\u56fd\u7684\u56fd\u5883\u4e86\u3002 \u3000\u3000\u201c\u90a3\u4e9b\u51f6\u5f92\u8d70\u4e86\u597d\u51e0\u5929\u6c99\u6f20\uff0c\u90fd\u662f\u75b2\u7d2f\u4e0d\u582a\uff1a\u5982\u4eca\u5df2\u5230\u56fd\u95e8\uff0c\u9644\u8fd1\u53c8\u6709\u6c34\u8349\uff0c\u4fbf\u5728 \u6cb3\u8fb9\u6b47\u4e0b\u6765\uff0c\u653e\u9a6c\u559d\u6c34\u3002\u4ed6\u4eec\u5927\u7ea6\u662f\u56e0\u5fc3\u60c5\u6109\u5feb\uff0c\u4fbf\u5174\u9ad8\u91c7\u70c8\u7684\u8c08\u8bba\u8d77\u6765\uff0c\u6240\u8bf4\u7684\u5f53\u7136 \u662f\u600e\u6837\u5411\u597d\u738b\u62a5\u544a\u9886\u8d4f\u7684\u4e8b\u60c5\u5566\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u6b63\u5728\u8bf4\u5f97\u9ad8\u5174\uff0c\u5ffd\u5730\u5b58\u4e2a\u8001\u4eba\u4ece\u5c71\u8170\u88c2\u5f00\u7684\u90a3\u9053\u7a84\u95e8\u8d70\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u2018\u628a\u8fd9 \u5b69\u5b50\u7ed9\u6211\u7559\u4e0b\uff01\u2019\u90a3\u4e9b\u51f6\u5f92\u5927\u6012\uff0c\u95ee\u4ed6\u662f\u4ec0\u4e48\u4eba\uff0c\u4ed6\u8bf4\uff1a\u2018\u4f60\u522b\u7ba1\u6211\u662f\u4ec0\u4e48\u4eba\uff0c\u4f60\u4eec\u7684 \u8c08\u8bdd\u6211\u90fd\u5df2\u542c\u89c1\u4e86\uff0c\u4f60\u4eec\u7684\u4e3b\u4eba\u5df2\u7ecf\u5bb3\u6b7b\u4e86\u8fd9\u5b69\u5b50\u7684\u7236\u6bcd\uff0c\u8fd9\u5b69\u5b50\u6211\u662f\u4e0d\u80fd\u8ba9\u4f60\u4eec\u518d\u5bb3 \u4ed6\u4e86\uff01\u2019\u201d \u3000\u3000\u4e25\u51f6\u5f92\u5f53\u4ee3\u4e0d\u4f1a\u4f9d\u4ece\uff0c\u5f53\u4e0b\u4e00\u62e5\u800c\u4e0a\uff0c\u4e0e\u90a3\u8001\u4eba\u5c55\u5f00\u4e86\u4e00\u573a\u6076\u6218\u3002\u90a3\u8001\u4eba\u8d64\u624b\u7a7a\u62f3\uff0c \u628a\u5341\u4e8c\u4e2a\u5e26\u7740\u5175\u5668\u7684\u51f6\u5f92\u4e00\u4e2a\u4e0d\u7559\uff0c\u5168\u90e8\u6253\u6b7b\u3002\u6211\u770b\u5f97\u5fc3\u60ca\u80c6\u6218\uff0c\u54c7\u7684\u54ed\u4e86\u51fa\u6765\u3002 \u3000\u3000\u201c\u90a3\u8001\u4eba\u5c06\u6211\u62b1\u8d77\uff0c\u8bf4\u9053\uff1a\u2018\u597d\u5b69\u5b50\uff0c\u522b\u5bb3\u6015\uff0c\u8fd9\u4e9b\u90fd\u662f\u574f\u4eba\u3002\u6253\u6b7b\u4e86\u4ed6\u4eec\uff0c\u5c31\u6ca1 \u6709\u4eba\u5bb3\u4f60\u4e86\u3002\u2019\u6211\u5f53\u65f6\u5e74\u7eaa\u5f88\u5c0f\uff0c\u4f46\u4e5f\u77e5\u9053\u90a3\u4e9b\u4eba\u4e43\u662f\u574f\u4eba\uff0c\u5c31\u6536\u4e86\u773c\u6cea\uff0c\u5e76\u5411\u90a3\u8001\u4eba \u9053\u8c22\uff0c\u90a3\u8005\u4eba\u8bf4\u6211\u4e56\u5de7\uff0c\u66f4\u662f\u559c\u6b22\u3002 \u3000\u3000\u201c\u4ece\u6b64\u90a3\u8001\u4eba\u5c31\u5e26\u7740\u6211\u8d70\uff0c\u4e00\u8def\u4e4b\u4e0a\uff0c\u5bf9\u6211\u7167\u6599\u5f97\u65e0\u5fae\u4e0d\u81f3\uff0c\u6211\u95ee\uff1a\u2018\u4f60\u4e3a\u4ec0\u4e48\u5bf9 \u6211\u8fd9\u6837\u597d\uff1f\u2019\u90a3\u8001\u4eba\u9053\uff1a\u2018\u56e0\u4e3a\u6211\u8981\u62a5\u7b54\u4f60\u7684\u7239\u7239\u3002\u2019\u6211\u95ee\u4ed6\u9053\uff1a\u2018\u539f\u6765\u4f60\u8ba4\u5f97\u6211\u7684\u7239 \u7239\uff0c\u4e3a\u4ec0\u4e48\u6211\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u4f60\uff1f\u2019\u90a3\u8001\u4eba\u9053\uff1a\u2018\u4e0d\u662f\u4f60\u59d3\u53f6\u7684\u90a3\u4f4d\u7239\u7239\uff0c\u4f60\u53e6\u6709\u4e00\u4f4d\u7239 \u7239\u3002\u2019\u8fd9\u53ef\u628a\u6211\u5f04\u7cca\u6d82\u4e86\uff0c\u6211\u7741\u5927\u4e86\u773c\u775b\u671b\u4ed6\u3002 \u3000\u3000\u201c\u90a3\u8001\u4eba\u9053\uff1a\u2018\u59d3\u65f6\u7684\u662f\u4f60\u7684\u4e49\u7236\uff0c\u4f60\u751f\u8eab\u7684\u7239\u7239\u662f\u4e2a\u56fd\u738b\uff0c\u4ed6\u65e9\u5df2\u88ab\u574f\u4eba\u5bb3\u6b7b\u4e86\u3002 \u6211\u4e5f\u672a\u66fe\u89c1\u8fc7\u4f60\u7684\u7239\u7239\uff0c\u4f46\u4ed6\u5bf9\u6211\u5f88\u597d\uff0c\u6240\u4ee5\u6211\u8981\u62a5\u7b54\u4ed6\uff0c\u6211\u4eca\u665a\u5c31\u5e26\u4f60\u53bb\u796d\u4f60\u7239\u7239\u7684 \u575f\u3002\u2019\u8fd9\u4e00\u665a\u4ed6\u679c\u7136\u5e26\u6211\u5230\u4e00\u5ea7\u575f\u524d\uff0c\u53eb\u6211\u78d5\u5934\u3002\u6211\u867d\u7136\u4e0d\u61c2\u4e8b\uff0c\u4f46\u6211\u76f8\u4fe1\u8fd9\u8001\u4eba\u7684\u8bf4 \u8bdd\uff0c\u6211\u77e5\u9053\u8eba\u5728\u575f\u5893\u4e2d\u7684\u662f\u6211\u7684\u7239\u7239\uff0c\u6211\u5c31\u5927\u54ed\u4e86\u4e00\u573a\u3002 \u3000\u3000\u201c\u90a3\u8001\u4eba\u5374\u6ca1\u6709\u54ed\uff0c\u4ed6\u5e26\u4e86\u4e00\u4e2a\u846b\u5e90\u7684\u9152\uff0c\u628a\u9152\u90fd\u6d12\u5728\u575f\u524d\uff0c\u6d12\u5b8c\u4e86\u9152\uff0c\u5ffd\u5730\u4ef0\u5929 \u5927\u7b11\u4e09\u58f0\u3002 \u3000\u3000\u201c\u53ea\u542c\u5f97\u4ed6\u6717\u58f0\u8bf4\u9053\uff1a\u2018\u58eb\u4e3a\u77e5\u5df1\u8005\u6b7b\uff0c\u6211\u867d\u7136\u6ca1\u6709\u63a5\u53d7\u4f60\u7684\u8058\u793c\uff0c\u4f46\u4f60\u5f53\u5e74\u6d3e\u9063 \u4f7f\u8005\uff0c\u8dcb\u6d89\u4e07\u6c34\u5343\u5c71\u6765\u987e\u6211\u7684\u8349\u5e90\uff0c\u8fd9\u4efd\u60c5\u610f\uff0c\u6211\u662f\u6c38\u4e16\u4e0b\u5fd8\uff0c\u5982\u4eca\u6211\u53ef\u4ee5\u62a5\u7b54\u4f60\u4e86\uff0c \u4f60\u653e\u5fc3\u5427\uff0c\u6211\u4e00\u5b9a\u628a\u4f60\u7684\u513f\u5b50\u629a\u517b\u6210\u4eba\uff0c\u6276\u52a9\u4ed6\u6062\u590d\u738b\u4f4d\u3002\u2019\u201d \u3000\u3000\u201c\u8fd9\u8001\u4eba\u4f60\u4eec\u60f3\u5fc5\u53ef\u4ee5\u731c\u5230\uff0c\u4ed6\u5c31\u662f\u65b9\u8001\u524d\u8f88\u65b9\u4eca\u660e\u4e86\u3002\u8bf4\u6765\u4e5f\u771f\u662f\u5947\u9047\uff0c\u4ed6\u5df2\u6709 \u4e8c\u5341\u5e74\u672a\u4e0b\u8fc7\u5c71\uff0c\u8fd9\u6b21\u662f\u53bb\u63a2\u8bbf\u4ed6\u7684\u5973\u5a7f\u9f99\u7075\u77eb\uff0c\u5728\u56de\u7a0b\u4e0a\u987a\u8def\u5230\u9a6c\u8428\u513f\u56fd\uff0c\u60f3\u6253\u542c\u6253 \u542c\u524d\u738b\u662f\u600e\u6837\u88ab\u5bb3\u7684\uff0c\u60f3\u4e0d\u5230\u6070\u5de7\u5c31\u649e\u4e0a\u8fd9\u6279\u51f6\u5f92\uff0c\u800c\u90a3\u6279\u51f6\u5f92\u53c8\u4ee5\u4e3a\u9644\u8fd1\u65e0\u4eba\uff0c\u8086\u65e0 \u5fcc\u60ee\u7684\u8c08\u8bba\uff0c\u8ba9\u4ed6\u542c\u5230\u4e86\u5168\u90e8\u7684\u79d8\u5bc6\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u7684\u8fd0\u6c14\u771f\u597d\u3002\u521a\u624d\u6211\u542c\u5230\u4f60\u88ab\u90a3\u4e9b\u51f6\u5f92\u52ab\u6301\u897f\u884c\u7684\u65f6\u5019\uff0c\u6211 \u771f\u4e3a\u4f60\u62c5\u5fc3\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u7684\u8fd0\u6c14\u597d\uff0c\u65b9\u8001\u524d\u8f88\u7684\u8fd0\u6c14\u53ef\u4e0d\u597d\u4e86\u3002\u6211\u628a\u707e\u661f\u5e26\u4e86\u7ed9\u4ed6\u3002 \u60f3\u5fc5\u662f\u597d\u738b\u53d1\u73b0\u4e86\u4ed6\u6240\u6d3e\u51fa\u7684\u90a3\u4e00\u6279\u4eba\u6b7b\u5728\u94c1\u95e8\u5173\uff0c\u4ed6\u63a5\u8fde\u6d3e\u51fa\u4e86\u51e0\u6279\u4eba\u6765\u641c\u7d22\u6211\u4eec\uff0c \u65b9\u8001\u524d\u8f88\u5728\u8def\u4e0a\u906d\u9047\u4e86\u4e09\u6b21\u88ad\u51fb\uff0c\u524d\u4e24\u6b21\u4ed6\u90fd\u5927\u83b7\u5168\u80dc\uff0c\u628a\u90a3\u4e9b\u8ffd\u5175\u6253\u5f97\u8dd1\u7684\u8dd1\u3001\u6b7b\u7684 \u6b7b\u3001\u4f24\u7684\u4f24\u3002\u7b2c\u4e09\u6b21\u53ef\u4e0d\u5e78\u4e86\uff0c\u4ed6\u8ba2\u6b7b\u4e86\u654c\u4eba\uff0c\u81ea\u5df1\u5374\u4e5f\u53d7\u4e86\u4f24\u3002 \u3000\u3000\u201c\u8fd9\u6b21\u6765\u7684\u53ea\u6709\u4e24\u4e2a\u5929\u7afa\u548c\u5c1a\uff0c\u540e\u6765\u77e5\u9053\u5c31\u662f\u90a3\u5978\u738b\u7684\u2018\u56fd\u5e08\u2019\u2014\u2014\u5b9d\u8c61\u6cd5\u5e08\u6700\u5f97 \u529b\u7684\u4e24\u4e2a\u5f1f\u5b50\uff0c\u8fd9\u4e24\u4e2a\u548c\u5c1a\u7528\u9752\u7af9\u679d\uff0c\u5584\u70b9\u5bf9\u65b9\u5947\u7ecf\u516b\u8109\u7684\u9690\u7a74\uff0c\u65b9\u8001\u524d\u8f88\u7a0d\u4e0d\u5c0f\u5fc3\uff0c \u7ed9\u4ed6\u4eec\u70b9\u4e2d\u4e86\u2018\u5e26\u8109\u2019\u7684\u4e00\u5904\u9690\u7a74\uff0c\u65b9\u8005\u524d\u8f88\u4e00\u6012\u4e4b\u4e0b\uff0c\u5927\u65bd\u521a\u52c7\uff0c\u7528\u795e\u62f3\u51fb\u6bd9\u4e86\u4ed6\u4eec\u3002 \u3000\u3000\u201c\u65b9\u8001\u524d\u8f88\u529f\u529b\u6df1\u6e5b\uff0c\u53ef\u662f\u2018\u9690\u7a74\u2019\u88ab\u70b9\uff0c\u4e5f\u7ed9\u4ed6\u5e26\u6765\u4e86\u75be\u75c5\uff0c\u4ed6\u4e00\u8def\u5f3a\u81ea\u652f\u6301\uff0c \u53c8\u53d7\u5230\u4e0d\u5c11\u98ce\u971c\u4e4b\u82e6\uff0c\u5f85\u56de\u5230\u4ed6\u9690\u5c45\u4e4b\u5904\uff0c\u75c5\u51b5\u66f4\u4e3a\u6076\u5316\uff0c\u7ec8\u4e8e\u5f97\u4e86\u4e2a\u534a\u8eab\u4e0d\u9042\u4e4b\u75c7\u3002 \u4f46\u4ed6\u4e3a\u4e86\u7167\u6599\u6211\uff0c\u81ea\u5df1\u505a\u4e86\u4e00\u8f86\u72ec\u8f6e\u8f66\uff0c\u6bcf\u9694\u51e0\u5929\u8fd8\u8981\u51fa\u53bb\u730e\u517d\u3001\u62fe\u67f4\u8349\uff0c\u505a\u996d\u7ed9\u6211\u5403\u3002 \u4ed6\u4e0d\u80fd\u8d70\u52a8\uff0c\u53ea\u7b49\u5f85\u91ce\u517d\u7ecf\u8fc7\uff0c\u5c31\u7528\u5288\u7a7a\u638c\u529b\u5c06\u91ce\u517d\u51fb\u6bd9\uff0c\u4ed6\u9664\u4e86\u730e\u517d\u4e4b\u5916\u3001\u6bcf\u5929\u8fd8\u4f20 \u6388\u6211\u7684\u6b66\u529f\u3002\u8fd9\u6837\u8fc7\u4e86\u4e09\u56db\u5e74\uff0c\u6211\u5df2\u6709\u516b\u4e5d\u5c81\u4e86\uff0c\u624d\u6e10\u6e10\u80fd\u591f\u7ed9\u4ed6\u5206\u52b3\u3002 \u3000\u3000\u201c\u5230\u6211\u5341\u4e00\u5c81\u90a3\u5e74\uff0c\u65b9\u8001\u524d\u8f88\u75c5\u5165\u818f\u76f2\uff0c\u5b8c\u5168\u4e0d\u80fd\u52a8\u5f39\uff0c\u6709\u4e00\u65e5\u6211\u6b63\u5728\u5e8a\u524d\u670d\u4f8d\u4ed6\uff0c \u5ffd\u7136\u6765\u4e86\u4e2a\u4e0d\u901f\u4e4b\u5ba2\uff0c\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u6211\u540e\u6765\u7684\u5e08\u7236\uff0c\u65b9\u8001\u524d\u8f88\u89c1\u4e86\u4ed6\uff0c\u6b22\u559c\u5f97\u5f88\uff0c\u628a \u6211\u7684\u6765\u5386\u544a\u8bc9\u4e86\u4ed6\uff0c\u6258\u4ed6\u7167\u6599\u6211\uff0c\u91d1\u5927\u4fa0\u8bf4\uff1a\u2018\u6211\u6b63\u662f\u4e3a\u4e86\u6253\u542c\u4ed6\u7684\u6d88\u606f\u6765\u8bbf\u95ee\u4f60\u7684\u3002 \u6ca1\u8bf4\u7684\uff0c\u8981\u662f\u4f60\u4e0d\u5acc\u5f03\u6211\u6559\u5f97\u4e0d\u597d\uff0c\u4f60\u628a\u8fd9\u5f92\u5f1f\u8ba9\u7ed9\u6211\u5427\u3002\u2019\u65b9\u8001\u524d\u8f88\u9a6c\u4e0a\u53eb\u6211\u5411\u91d1\u5927 \u4fa0\u78d5\u5934\uff0c\u4ed6\u4eb2\u773c\u770b\u6211\u884c\u4e86\u62dc\u5e08\u4e4b\u672d\uff0c\u54c8\u54c8\u54c8\u53c8\u5927\u7b11\u4e86\u4e09\u58f0\uff0c\u8bf4\uff1a\u2018\u5982\u4eca\u6211\u662f\u771f\u7684\u53ef\u4ee5\u653e \u5fc3\u4e86\u3002\u2019\u4ed6\u5c31\u5728\u7b11\u58f0\u4e2d\u54bd\u4e86\u6c14\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u4e0d\u7981\u795e\u8272\u9eef\u7136\uff0c\u550f\u5618\u55df\u53f9\u9053\uff1a\u201c\u8fd9\u4f4d\u65b9\u8001\u524d\u8f88\u771f\u662f\u6709\u53e4\u4e49\u58eb\u4e4b\u98ce\uff0c\u4ee4\u4eba\u611f\u52a8\u3002 \u54e5\u54e5\uff0c\u8fd9\u91cc\u7684\u4e8b\u60c5\u5b8c\u4e86\u4e4b\u540e\uff0c\u4f60\u5e26\u6211\u53bb\u796d\u626b\u4ed6\u8001\u4eba\u5bb6\u7684\u575f\u5893\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u60f3\u8d77\u4e00\u4e8b\uff0c\u95ee\u9053\uff1a\u201c\u65b9\u8001\u524d\u8f88\u7684\u5973\u5a7f\u662f\u9f99\u7075\u77eb\u5427\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4e0d\u9519\u3002 \u4ed6\u90a3\u6b21\u5c31\u662f\u53bb\u63a2\u671b\u5973\u513f\u5973\u5a7f\u7684\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a \u3000\u3000\u201c\u90a3\u51e0\u5e74\u9f99\u7075\u77eb\u592b\u5987\u6709\u6ca1\u6709\u56de\u53bb\u63a2\u671b\u8fc7\u4ed6\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u8001\u4eba\u5bb6\u6b7b\u4e86\u8fd9\u8bb8\u591a\u5e74\uff0c\u5973\u513f\u5973\u5a7f\u4e5f\u6ca1\u6709\u6765\u796d\u8fc7\u575f\uff0c\u53ef\u80fd\u8fd8\u4e0d\u77e5\u9053\u4ed6\u8001\u4eba\u5bb6\u5df2\u7ecf\u8fc7 \u4e16\u4e86\u3002\u4f60\u95ee\u8d77\u4ed6\u4eec\u53ef\u6709\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u542c\u5f97\u5510\u7ecf\u5929\u4f2f\u4f2f\u8bf4\uff0c\u4ed6\u5728\u4e2d\u5370\u652f\u754c\u7684\u5927\u5409\u5cad\uff0c\u66fe\u78b0\u89c1\u8fc7\u9f99\u7075\u77eb\u592b\u5987\uff0c \u90a3\u65f6\u9f99\u6c0f\u5927\u5987\u521a\u4ece\u5370\u5ea6\u7684\u90a3\u70c2\u9640\u5bfa\u5f52\u6765\uff0c\u5728\u90a3\u70c2\u9640\u5bfa\u66fe\u62dc\u8c12\u8fc7\u5b9d\u8c61\u7684\u5e08\u7236\u9f99\u53f6\u4e0a\u4eba\uff0c\u9f99 \u53f6\u571f\u4eba\u6258\u4ed6\u4eec\u67e5\u8bbf\u5b9d\u8c61\u7684\u52a3\u8ff9\u3002\u53ef\u60dc\u4ed6\u4eec\u5728\u884c\u524d\u6ca1\u6709\u53bb\u63a2\u671b\u65b9\u8001\u524d\u8f88\uff0c\u4ee5\u81f4\u5bf9\u5b9d\u8c61\u4e4b\u4e8b \u6beb\u65e0\u6240\u77e5\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6211\u542c\u5e08\u7236\u8bf4\u8fc7\u9f99\u7075\u77eb\u662f\u4e2a\u6b66\u5b66\u5947\u624d\uff0c\u529f\u592b\u53ea\u6015\u8fd8\u5728\u4ed6\u5cb3\u7236\u4e4b\u4e0a\u3002\u4ed6 \u82e5\u77e5\u9053\u5cb3\u7236\u4e4b\u6b7b\uff0c\u662f\u7531\u4e8e\u53d7\u4e86\u5b9d\u8c61\u6cd5\u5e08\u5f1f\u5b50\u6240\u4f24\uff0c\u4e00\u5b9a\u4e0d\u80af\u653e\u8fc7\u90a3\u53ae\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a \u201c\u91d1\u9e70\u5bab\u4e4b\u4f1a\u5728\u6c5f\u6e56\u4e0a\u5df2\u95f9\u5f97\u6cb8\u6cb8\u626c\u626c\uff0c\u9f99\u7075\u77eb\u592b\u5987\u6839\u53ef\u80fd\u95fb\u98ce\u800c\u6765\uff0c\u4ed6\u4eec\u53c8\u662f\u53d7\u4e86\u9f99 \u53f6\u4e0a\u4eba\u4e4b\u6258\uff0c\u8981\u4e3a\u9f99\u53f6\u4e0a\u4eba\u6e05\u7406\u95e8\u6237\u7684\u3002\u54c8\uff0c\u4ed6\u4eec\u5018\u82e5\u8d74\u4f1a\uff0c\u90a3\u53ef\u66f4\u6709\u597d\u620f\u770b\u4e86\u3002 \u3000\u3000\u4e00\u8bf4\u8d77\u91d1\u9e70\u5bab\u4e4b\u4f1a\uff0c\u5927\u5bb6\u90fd\u5174\u594b\u8d77\u6765\uff0c\u8c37\u4e2d\u83b2\u62ac\u5934\u4e00\u671b\uff0c\u4e1c\u65b9\u5929\u9645\u5df2\u5fae\u9732\u66d9\u5149\uff0c\u7b11 \u5230\uff1a\u201c\u73b0\u5728\u5df2\u662f\u516b\u6708\u5341\u56db\u6e05\u6668\uff0c\u660e\u5929\u7684\u91d1\u9e70\u5bab\u4e4b\u4f1a\uff0c\u6b63\u597d\u8d76\u5f97\u4e0a\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u628a\u4ed6\u62a2\u56de\u7684\u90a3\u56db\u9897\u5929\u5fc3\u77f3\u638f\u51fa\uff0c\u7b11\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u6211\u6b63\u597d\u548c\u4f60\u6bcf\u4eba\u5206\u98df\u4e24\u9897\uff0c \u660e\u65e5\u4e4b\u4f1a\uff0c\u9ad8\u624b\u5982\u4e91\uff0c\u54b1\u4eec\u670d\u4e86\u5929\u5fc3\u77f3\uff0c\u624d\u53ef\u4ee5\u6709\u6043\u65e0\u6050\u5462\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u7684\u5185\u529f\u6839\u57fa\u592a\u8584\uff0c\u600e\u80fd\u670d\u5929\u5fc3\u77f3\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u65e0\u59a8\uff0c\u6211\u6709 \u89e3\u5929\u5fc3\u77f3\u4e4b\u6bd2\u7684\u5bd2\u661f\u77f3\uff0c\u800c\u4e14\u6211\u8fd8\u53ef\u4ee5\u62c5\u4fdd\uff0c\u5728\u6211\u4eec\u4e09\u4eba\u4e4b\u4e2d\uff0c\u53ea\u6709\u4f60\u670d\u98df\u4e86\u5929\u5fc3\u77f3\uff0c \u53ef\u4ee5\u6beb\u65e0\u75db\u82e6\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u8be7\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u4e0d\u662f\u8bf4\u7b11\u5427\uff1f\u6d77\u54e5\u90a3\u6837\u6df1\u539a\u7684\u5185\u529f\uff0c\u670d\u4e86\u4e4b\u540e\uff0c\u8fd8\u8981\u53d1\u70e7 \u5f97\u6b7b\u53bb\u6d3b\u6765\u5462\uff0c\u540e\u6765\u670d\u4e86\u89e3\u836f\uff0c\u4e5f\u8fd8\u8981\u7ecf\u8fc7\u534a\u4e2a\u65f6\u8fb0\u7684\u714e\u71ac\u3002\u6211\u600e\u4e48\u53ef\u4ee5\u6beb\u65e0\u75db\u82e6\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u8bf4\u7b11\u3002\u4f60\u6709\u6240\u4e0d\u77e5\uff0c\u6c5f\u5144\u662f\u56e0\u4e3a\u4ed6\u6700\u65e9\u7ec3\u7684\u662f\u90aa\u6d3e\u5185\u529f\uff0c \u6545\u800c\u53d7\u82e6\u6700\u5927\u3002\u6211\u7684\u5185\u529f\u5165\u95e8\u662f\u8ddf\u65b9\u8001\u524d\u8f88\u7ec3\u7684\uff0c\u65b9\u8001\u524d\u8f88\u529f\u529b\u6df1\u539a\uff0c\u4f46\u5374\u8fd8\u4e0d\u662f\u6700\u7eaf \u6b63\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u540e\u6765\u6211\u624d\u8ddf\u5e08\u7236\u7ec3\u6b63\u90aa\u5408\u4e00\u7684\u5185\u529f\u3002\u4e09\u4eba\u4e4b\u4e2d\uff0c\u53ea\u6709\u4f60\u662f\u81ea\u59cb\u81f3\u7ec8\u90fd\u7ec3 \u4e60\u6b63\u5b97\u5185\u529f\u3002\u6545\u800c\u6211\u53ef\u4ee5\u62c5\u4fdd\uff0c\u670d\u98df\u4e86\u5929\u5fc3\u77f3\uff0c\u53ea\u6709\u4f60\u53ef\u4ee5\u6beb\u65e0\u75db\u82e6\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u559c\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u4e00\u4e2a\u6708\u6765\u6211\u53d7\u5c3d\u4e86\u95f7\u6c14\uff0c\u5f85\u6211\u529f\u529b\u5927\u589e\u4e4b\u540e\uff0c\u6211\u5b9a\u8981\u75db\u75db\u5feb \u5feb\u5e72\u51e0\u6869\u5410\u6c14\u626c\u7709\u7684\u4e8b\u60c5\u3002\u4f60\u4eec\u731c\uff0c\u6211\u7b2c\u4e00\u4ef6\u60f3\u505a\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5979\u671b\u4e86\u6c5f\u6d77\u5929\u4e00\u773c\uff0c\u6c5f\u6d77\u5929\u8fd8\u6ca1\u6709\u731c\uff0c\u5979\u5df2\u7981\u4e0d\u4f4f\u81ea\u5df1\u5148\u8bf4\u51fa\u6765\u9053\uff1a\u201c\u6211\u8981\u5148\u63cd\u90a3 \u53f6\u51b2\u9704\u4e00\u987f\u3002\u8fd9\u53ae\u592a\u53ef\u6076\u4e86\uff0c\u7adf\u6562\u5192\u5145\u6211\u7684\u54e5\u54e5\uff0c\u8fd8\u4e00\u672c\u6b63\u7ecf\u7684\u7aef\u8d77\u54e5\u54e5\u7684\u8eab\u4efd\uff0c\u529d\u6211 \u8fd9\u6837\uff0c\u529d\u6211\u90a3\u6837\uff0c\u8bf4\u7684\u90fd\u662f\u6211\u6700\u8ba8\u538c\u7684\u8bf4\u8bdd\u3002\u504f\u504f\u8fd8\u6709\u4eba\u771f\u7684\u76f8\u4fe1\u4ed6\u662f\u6211\u7684\u54e5\u54e5\uff1f\u4f60\u8bf4 \u6c14\u4e0d\u6c14\u4eba\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u82e6\u7b11\u9053\uff1a\u201c\u4ed6\u548c\u4f60\u54e5\u54e5\u7684\u76f8\u8c8c\u4e5f\u771f\u957f\u5f97\u76f8\u50cf\uff0c\u8bf4\u4e0d\u5b9a\u6211\u4eca\u540e\u78b0\u89c1\u4e86\u4ed6\uff0c\u4e5f\u8fd8 \u4f1a\u8bef\u8ba4\u5462\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u6b21\u968f\u5e08\u7236\u56de\u56fd\uff0c\u5176\u4e2d\u7684\u4e00\u4e2a\u539f\u56e0\uff0c\u4e5f\u662f\u60f3\u770b\u770b\u8fd9\u4e2a\u5192\u5145\u6211\u7684 \u4eba\u5462\u3002\u614e\u4e0d\u77e5\u5978\u738b\u662f\u4ece\u54ea\u91cc\u627e\u6765\u8fd9\u4e2a\u4eba\u7684\uff0c\u548c\u6211\u8fd9\u4e48\u76f8\u4f3c\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u542c\u4f60 \u7684\u53e3\u6c14\uff0c\u4f60\u65e9\u5df2\u77e5\u9053\u6709\u8fd9\u4e48\u4e00\u4e2a\u4eba\u5192\u5145\u4f60\u4e86\uff0c\u4f60\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff0c\u201c\u8fd9\u53ef\u5f97\u53c8\u4ece\u5e08\u7236\u8eab\u4e0a\u8bf4\u8d77\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u6b63\u60f3\u591a\u77e5\u9053\u4e00\u4e9b\u5173\u4e8e\u5e08\u7236\u7684 \u6d88\u606f\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\uff0c\u4f60\u521a\u624d\u6b63\u8bf4\u5230\u5e08\u7236\u5c06\u4f60\u6536\u5165\u95e8\u4e0b\uff0c\u8bdd\u9898\u5c31\u7ed9\u6211\u6253\u65ad\u4e86\u3002\u4f60\u63a5\u7eed\u8bf4\u4e0b \u53bb\u5427\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u65b9\u8001\u524d\u8f88\u53bb\u4e16\u4e4b\u540e\uff0c\u5e08\u7236\u548c\u6211\u5c31\u5728\u4ed6\u90a3\u95f4\u5c4b\u5b50\u4f4f\u4e0b\u6765\uff0c\u767d \u5929\u6559\u6211\u6b66\u529f\uff0c\u665a\u4e0a\u6559\u6211\u5ff5\u4e66\uff0c\u5982\u662f\u8005\u8fc7\u4e86\u4e94\u5e74\uff0c\u6211\u5341\u516d\u5c81\u4e86\uff0c\u6b66\u827a\u867d\u6ca1\u7ec3\u6210\uff0c\u4f46\u4ea6\u5df2\u53ef \u4ee5\u624b\u640f\u731b\u517d\uff0c\u7bad\u5c04\u5140\u9e70\u3002\u5e08\u7236\u5c31\u548c\u6211\u8bf4\uff1a\u2018\u4f60\u7684\u5bb6\u56fd\u5927\u4ec7\uff0c\u5e94\u8be5\u7531\u4f60\u81ea\u5df1\u53bb\u62a5\u3002 \u3000\u3000\u4f46\u8981\u62a5\u4ec7\uff0c\u5c31\u5fc5\u987b\u77e5\u5df1\u77e5\u5f7c\uff0c\u6211\u7ed9\u4f60\u5230\u9a6c\u8428\u513f\u56fd\u53bb\u63a2\u542c\u4e00\u8d9f\uff0c\u770b\u770b\u4f60\u7684\u654c\u4eba\u7684\u865a\u5b9e \u52a8\u9759\u3002\u2019\u6211\u672c\u6765\u60f3\u8ddf\u5e08\u7236\u540c\u53bb\u7684\uff0c\u4f46\u5e08\u7236\u4e0d\u8bb8\u3002\u4ed6\u8bf4\uff1a\u2018\u4ee5\u4f60\u73b0\u5728\u7684\u529f\u592b\uff0c\u6211\u53ef\u4ee5\u653e\u5fc3 \u7559\u4f60\u4e00\u4e2a\u4eba\u5728\u8fd9\u513f\uff0c\u4f46\u6211\u8fd8\u4e0d\u653e\u5fc3\u8ba9\u4f60\u56de\u56fd\u3002\u53cd\u6b63\u6211\u8fd9\u6b21\u53ea\u662f\u53bb\u63a2\u542c\u654c\u4eba\u7684\u52a8\u9759\uff0c\u6700\u591a \u4e24\u4e2a\u6708\u5c31\u56de\u6765\u3002\u2019 \u3000\u3000\u201c\u5e08\u7236\u679c\u7136\u4e0d\u5230\u4e24\u4e2a\u6708\u5c31\u56de\u6765\u4e86\uff0c\u4ed6\u7ed9\u6211\u5e26\u6765\u4e86\u8bb8\u591a\u6d88\u606f\uff0c\u6211\u77e5\u9053\u597d\u738b\u91ce\u5fc3\u52c3\u52c3\uff0c \u8058\u8bf7\u4e86\u8bb8\u591a\u6b66\u58eb\uff0c\u60f3\u7edf\u4e00\u897f\u57df\uff0c\u8fdb\u7aa5\u534e\u590f\u3002\u4f46\u8fd9\u8bb8\u591a\u6d88\u606f\u4e4b\u4e2d\uff0c\u6211\u6700\u611f\u5174\u8da3\u7684\u5374\u662f\uff1a\u5978 \u738b\u6536\u4e86\u4e00\u4e2a\u2018\u5e72\u6bbf\u4e0b\u2019\uff0c\u9762\u8c8c\u7adf\u7136\u4e0e\u6211\u751a\u4e3a\u76f8\u4f3c\u3002 \u3000\u3000\u201c\u6211\u5e08\u7236\u66fe\u4e09\u6b21\u8fdb\u8fc7\u738b\u5bab\uff0c\u6697\u4e2d\u7aa5\u63a2\u90a3\u5192\u79f0\u2018\u53f6\u51b2\u9704\u2019\u7684\u5c0f\u4e8e\uff0c\u90a3\u5c0f\u5b50\u867d\u6709\u51e0\u5206\u672c \u9886\uff0c\u548c\u54b1\u4eec\u7684\u5e08\u7236\u53ef\u5dee\u5f97\u592a\u8fdc\uff0c\u5f53\u7136\u4e0d\u4f1a\u77e5\u9053\u3002\u6709\u4e00\u6b21\u4ed6\u5728\u5fa1\u82b1\u56ed\u7ec3\u6b66\uff0c\u5e08\u7236\u6697\u4e2d\u5077\u770b\uff0c \u4e5f\u9887\u611f\u8be7\u5f02\uff0c\u2026\u2026 \u3000\u3000\u8c37\u4e2d\u83b2\u63d2\u53e3\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff0c\u4ed6\u8fd9\u70b9\u529f\u592b\uff0c\u96be\u9053\u8fd8\u4f1a\u653e\u5728\u91d1\u5927\u4fa0\u7684\u5fc3\u4e0a\uff1f\u201d\u5510\u52aa\u73e0\u7a46 \u9053\uff1a\u201c\u5e08\u7236\u4e0d\u662f\u8be7\u5f02\u4ed6\u7684\u529f\u5927\u4e86\u5f97\uff0c\u800c\u662f\u8be7\u5f02\u4ed6\u7684\u529f\u592b\u5bb6\u6570\u3002\u4f60\u53ef\u77e5\u9053\u90a3\u5c0f\u5b50\u6700\u5389\u5bb3\u662f \u54ea\u6837\u529f\u592b\u5417\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u5e08\u7236\u53ea\u6559\u6211\u672c\u95e8\u7684\u7eaf\u6b63\u529f\u592b\uff0c\u5176\u4ed6\u65c1\u95e8\u6742\u6d3e\uff0c\u6211\u5c31\u4e00\u6982\u4e0d\u61c2\u3002\u201d\u6c5f \u6d77\u5929\u9053\uff1a\u201c\u662f\u4e0d\u662f\u5927\u4e58\u82e5\u82e5\u638c\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a \u3000\u3000\u201c\u4e0d\u9519\uff0c\u8fd9\u5c0f\u5b50\u6700\u5389\u5bb3\u7684\u529f\u592b\uff0c\u6b63\u662f\u5927\u4e58\u822c\u82e5\u638c\uff01\u201d \u3000\u3000\u4ed6\u6b47\u4e86\u4e00\u6b47\uff0c\u63a5\u7eed\u8bf4\u9053\uff1a\u201c\u6c5f\u5e08\u5144\uff0c\u4f60\u5f53\u7136\u77e5\u9053\u7684\u4e86\uff0c\u8fd9\u5927\u4e58\u822c\u82e5\u638c\u4e43\u662f\u4e54\u5317\u6e9f\u79d8 \u7c4d\u4e2d\u7684\u529f\u592b\uff0c\u5a01\u529b\u4ec5\u900a\u4e8e\u4fee\u7f57\u9634\u715e\u529f\u7684\u4e00\u79cd\u90aa\u6d3e\u638c\u529b\u3002\u4e54\u5317\u6e9f\u79d8\u7c4d\u81ea\u5389\u80dc\u7537\u6b7b\u540e\u5c31\u843d\u5230 \u54b1\u4eec\u5e08\u7236\u624b\u4e2d\uff0c\u54b1\u4eec\u5e08\u7236\u65e9\u5df2\u5c06\u5b83\u6bc1\u4e86\u3002\u636e\u5e08\u7236\u6240\u77e5\uff0c\u5929\u9b54\u6559\u4e3b\u59d0\u59b9\uff0c\u66fe\u83b7\u5f97\u79d8\u7c4d\u4e2d\u7684 \u4e00\u9cde\u534a\u722a\uff0c\u4f46\u5927\u4e58\u822c\u8457\u638c\u662f\u79d8\u7c4d\u4e2d\u6700\u6df1\u5965\u7684\u529f\u592b\u4e4b\u4e00\uff0c\u5929\u9b54\u6559\u4e3b\u51b3\u8ba1\u4e0d\u4f1a\u3002\u6240\u4ee5\u5e08\u7236\u4ed6 \u8001\u4eba\u5bb6\u90a3\u665a\u6697\u4e2d\u5077\u770b\uff0c\u89c1\u8fd9\u5c0f\u5b50\u7ec3\u7684\u7adf\u662f\u8fd9\u95e8\u529f\u592b\uff0c\u5fc3\u4e2d\u81ea\u662f\u4e0d\u514d\u6709\u70b9\u8be7\u5f02\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u63d2\u53e3\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u4e3a\u4f55\u4e0d\u5c06\u4ed6\u5f53\u573a\u62ff\u4e0b\uff0c\u95ee\u4e2a\u6c34\u843d\u77f3\u51fa\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a \u201c\u8fd9\u5c31\u6709\u5931\u5e08\u7236\u7684\u8eab\u4efd\u4e86\uff0c\u4ed6\u7684\u6b66\u529f\u5df2\u88ab\u6b66\u6797\u516c\u8ba4\u662f\u4e3e\u4e16\u65e0\u53cc\uff0c\u8fd9\u662f\u4f55\u7b49\u8eab\u4efd\uff0c\u5c82\u80fd\u843d \u4e2a\u4ee5\u5927\u538b\u5c0f\u4e4b\u540d\uff1f \u3000\u3000\u8fd8\u6709\u4e00\u5c42\uff0c\u5e08\u7236\u4e5f\u4e0d\u613f\u6cc4\u6f0f\u884c\u85cf\uff0c\u60ca\u52a8\u654c\u4eba\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7eed\u9053\uff1a\u201c\u5e08\u7236\u56e0\u4e3a\u5927\u4e58\u822c\u82e5\u638c\u4e13\u4f24\u5947\u7ecf\u516b\u8109\u3002\u592a\u8fc7\u6b79\u6bd2\uff0c\u672c\u6765\u662f\u4e0d\u60f3\u6559\u7ed9 \u6211\u7684\uff0c\u4f46\u56de\u6765\u4e4b\u540e\uff0c\u5374\u6539\u4e86\u4e3b\u610f\uff0c\u7528\u4e00\u5e74\u529f\u592b\uff0c\u8981\u6211\u4e13\u7ec3\u5927\u4e58\u822c\u82e5\u638c\uff0c\u52a1\u5fc5\u8981\u80dc\u8fc7\u5192\u5145 \u6211\u7684\u90a3\u5c0f\u5b50\u3002\u201d\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u5999\u5f97\u5f88\uff0c\u8fd9\u6b63\u662f\u4ee5\u6bd2\u653b\u6bd2\uff0c\u4ee5\u90aa\u5236\u90aa\uff01\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6211\u7ec3\u6210\u4e4b\u540e\uff0c\u5df2\u662f\u4eca\u5e74\u6625\u521d\u3002\u4e0d\u4e45\u3002\u5b9d\u8c61\u6cd5\u5e08\u8981\u5728\u4e2d\u79cb\u5f00\u91d1\u9e70\u5bab\u4e4b \u4f1a\uff0c\u5e7f\u9080\u5929\u4e0b\u82f1\u96c4\u7684\u6d88\u606f\u4ea6\u5df2\u4f20\u6765\uff0c\u5e08\u7236\u8ba4\u4e3a\u65f6\u673a\u5df2\u81f3\uff0c\u5c31\u5e26\u6211\u56de\u56fd\u4e86\u3002\u6211\u4eec\u662f\u5341\u5929\u4e4b \u524d\u5df2\u5230\u4e86\uff0c\u4e00\u76f4\u9690\u85cf\u7740\u8e2a\u8ff9\uff0c\u4e0d\u8ba9\u654c\u4eba\u77e5\u9053\u3002\u6211\u6309\u7167\u7f8a\u76ae\u4e66\u4e0a\u7684\u540d\u5355\uff0c\u66fe\u62dc\u8bbf\u8fc7\u51e0\u4f4d\u7236 \u738b\u8ba4\u4e3a\u6700\u53ef\u9760\u7684\u5fe0\u81e3\u3002\u4f60\u88ab\u56da\u7981\u5728\u8fd9\u5c9b\u4e0a\uff0c\u5c31\u662f\u4e00\u4e2a\u5047\u610f\u6548\u5fe0\u597d\u738b\u7684\u5185\u4f8d\u536b\u5927\u81e3\u7ed9\u6211\u63a2 \u542c\u7684\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\u3002\u5929\u5df2\u4eae\u4e86\uff0c\u4e00\u8f6e\u7ea2\u65e5\u4ece\u5730\u5e73\u7ebf\u4e0a\u5189\u5189\u5347\u8d77\uff0c\u70df\u6ce2\u6d69\u6e3a\uff0c\u90b1\u671b\u53bb\uff0c\u5c31\u4f3c\u4ece \u6e56\u4e2d\u8dc3\u51fa\u4e00\u822c\uff0c\u67d3\u5f97\u6e56\u6c34\u5982\u91d1\uff0c\u8000\u773c\u751f\u8f89\u3002\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u65e5\uff0c\u660e\u65e5\u5c31\u662f\u4f1a\u671f\u4e86\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5c06\u5c0f\u821f\u5212\u5230\u4e00\u5ea7\u4e34\u7322\u7684\u5c71\u5cf0\u811a\u4e0b\uff0c\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u600e\u4e48\uff1f\u5728\u8fd9\u91cc\u4e0a\u5cb8\u5417\uff1f\u8fd9 \u53ef\u662f\u4e2a\u5ced\u58c1\u5440\u3002\u201d\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u5ced\u58c1\u4e0a\u522b\u6709\u6d1e\u5929\uff0c\u4f60\u968f\u6211\u6765\u3002\u201d\u5c06\u5c0f\u821f\u85cf\u597d\uff0c\u4e09\u4eba \u65bd\u5c55\u4e0a\u4e58\u8f7b\u529f\uff0c\u6500\u7f18\u800c\u4e0a\u3002 \u3000\u3000\u53ea\u89c1\u5c71\u4e0a\u91ce\u82b1\u6742\u5347\uff0c\u6d41\u6cc9\u5904\u5904\uff0c\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u679c\u7136\u597d\u98ce\u666f\u3002\u201d\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u4e0d \u53ea\u98ce\u666f\u597d\u5462\uff0c\u4f60\u77a7\u8fd9\u91cc\u3002\u201d\u4ed6\u63a8\u5f00\u4e86\u4e24\u5757\u5927\u77f3\uff0c\u9732\u51fa\u4e00\u4e2a\u6d1e\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u7236\u738b\u5b9d\u7a9f \u4e4b\u4e00\uff0c\u57cb\u5728\u6d1e\u5185\uff0c\u54b1\u4eec\u73b0\u5728\u8fd8\u4e0d\u9700\u7528\uff0c\u4e14\u4e0d\u7406\u5b83\u3002\u4f46\u8fd9\u4e2a\u5730\u65b9\uff0c\u5374\u6b63\u597d\u4f9b\u54b1\u4eec\u4f5c\u7ec3\u529f\u7684 \u9759\u5ba4\u4e4b\u7528\uff0c\u51b3\u4e0d\u4f1a\u7ed9\u4eba\u9a9a\u6270\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7b49\u4f1a\u54b1\u4eec\u670d\u98df\u4e86\u5929\u5fc3\u77f3\uff0c\u662f\u9700\u8981\u4e00\u95f4\u9759\u5ba4\u7ec3\u529f\u3002\u201d\u8d70\u4eba\u6d1e\u7a9f\uff0c \u53ea\u89c1\u91cc\u9762\u65e9\u5df2\u6253\u626b\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u89d2\u843d\u5806\u6709\u51bb\u9e21\u3001\u814a\u8089\u3001\u9762\u6761\u7b49\u98df\u54c1\uff0c\u8fd8\u6709\u51e0\u74f6\u9152\u548c\u4e00\u53e3 \u94c1\u9505\u3002\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6211\u5728\u7f8a\u76ae\u4e66\u4e0a\u77e5\u9053\u8fd9\u4e2a\u6240\u5728\uff0c\u524d\u51e0\u5929\u6211\u548c\u5e08\u7236\u5c31\u4f4f\u5728\u8fd9\u51e0\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5e08\u7236\u8fd8\u4f1a\u4e0d\u4f1a\u56de\u5230\u8fd9\u513f\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u8fd9\u4e24\u5929\u5404\u5730\u90fd\u9646\u7eed\u6709\u4eba\u524d \u6765\u8d74\u4f1a\uff0c\u5176\u4e2d\u4e0d\u5c11\u662f\u5e08\u7236\u76f8\u8bc6\u7684\uff0c\u6240\u4ee5\u5e08\u7236\u524d\u5929\u5c31\u642c\u51fa\u53bb\u4e86\uff0c\u4ed6\u548c\u6b66\u5f53\u638c\u95e8\u96f7\u9707\u5b50\u3001\u9752 \u57ce\u540d\u5bbf\u8427\u9752\u5cf0\uff0c\u8fd8\u6709\u83b2\u59b9\u7684\u5e08\u7236\u9099\u5c71\u638c\u95e8\u8c37\u5973\u4fa0\u7b49\u4e00\u73ed\u4eba\uff0c\u4f4f\u5728\u5d06\u5cd2\u6d3e\u4e00\u4e2a\u957f\u8001\u7684\u5bb6\u91cc\uff0c \u5e03\u7f6e\u660e\u65e5\u8d74\u4f1a\u4e4b\u4e8b\uff0c\u5927\u7ea6\u662f\u4e0d\u4f1a\u56de\u5230\u8fd9\u513f\u4e86\uff0c\u4ed6\u66fe\u5631\u5490\u6211\uff0c\u53eb\u6211\u6551\u51fa\u83b2\u59b9\u4e4b\u540e\uff0c\u660e\u65e5\u5f84 \u81ea\u8d74\u4f1a\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6253\u5f00\u4e86\u4e00\u74f6\u9152\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5e08\u7236\u8fd9\u4e24\u5e74\u6765\u5f88\u559c\u6b22\u559d\u9152\uff0c\u8fd9\u662f\u4ed6\u81ea\u917f\u7684\u9752 \u7a1e\u9152\uff0c\u9152\u6027\u5f88\u70c8\u3002\u6211\u662f\u4e0d\u559d\u9152\u7684\uff0c\u4f46\u670d\u98df\u5929\u5fc3\u77f3\uff0c\u5374\u9700\u7528\u9152\u51b2\u670d\uff0c\u836f\u529b\u624d\u80fd\u8fc5\u901f\u8fd0\u884c\uff0c \u4e5f\u53ea\u597d\u7834\u4f8b\u559d\u4e00\u70b9\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u4ed6\u53d6\u51fa\u90a3\u56db\u9897\u5929\u5fc3\u77f3\uff0c\u4e0e\u8c37\u4e2d\u83b2\u5206\u800c\u670d\u4e4b\uff0c\u4e24\u4eba\u76d8\u819d\u5750\u5730\u4e0a\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u53ea\u89c1 \u5510\u52aa\u73e0\u7a46\u5934\u4e0a\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff01\u8c37\u4e2d\u83b2\u5219\u5982\u559d\u4e86\u8fc7\u91cf\u7684\u9152\u4e00\u822c\uff0c\u9189\u989c\u9161\u7ea2\uff0c\u4f46\u5934\u9876\u5374 \u6ca1\u6709\u767d\u6c14\u3002 \u3000\u3000\u4e24\u4eba\u518d\u541e\u670d\u4e86\u89e3\u5929\u5fc3\u77f3\u70ed\u6bd2\u7684\u5bd2\u661f\u77f3\uff0c\u53c8\u8fc7\u4e86\u5927\u7ea6\u4e00\u70b7\u9999\u7684\u65f6\u523b\uff0c\u5510\u52aa\u73e0\u7a46\u5934\u9876\u7684 \u767d\u6c14\u8fd8\u672a\u5b8c\u5168\u6d88\u6563\uff0c\u8c37\u4e2d\u83b2\u5df2\u662f\u4e00\u8dc3\u800c\u8d77\uff0c\u72c2\u559c\u53eb\u9053\uff1a\u201c\u6211\u5df2\u6c14\u7eb3\u4e39\u7530\uff0c\u516b\u8109\u7545\u901a\uff0c\u53ea \u89c9\u6d51\u8eab\u662f\u52b2\u3002\u65e0\u5904\u53d1\u6cc4\uff01\u201d\u6293\u7740\u6c5f\u6d77\u5929\u7684\u624b\u4e71\u6447\uff0c\u6c5f\u6d77\u5929\u672a\u66fe\u9632\u5907\uff0c\u7ed9\u5979\u4e00\u6293\uff0c\u811a\u6b65\u8e09 \u8dc4\uff0c\u9669\u9669\u8dcc\u5012\uff0c\u4f46\u968f\u5373\u6c5f\u6d77\u5929\u7684\u62a4\u4f53\u795e\u529f\u751f\u51fa\u53cd\u5e94\uff0c\u4e5f\u5c31\u7ad9\u7a33\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u7684\u529f\u529b\u5df2\u662f\u6bd4\u4ece\u524d\u6700\u5c11\u589e\u4e86\u4e24\u500d\u6709\u591a\u3002\u201d\u539f\u6765\u8c37\u4e2d\u83b2\u56e0\u4e3a \u7ec3\u7684\u662f\u6700\u7eaf\u6b63\u7684\u5185\u529f\uff0c\u6240\u4ee5\u5f97\u76ca\u4e5f\u6700\u5927\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4f38\u51fa\u4e00\u638c\u8d34\u7740\u5510\u52aa\u73e0\u7a46\u7684\u80cc\u5fc3\uff0c\u52a9\u4ed6\u5bfc\u6c14\u5f52\u5143\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5510\u52aa\u73e0\u7a46\u5934\u9876\u4e0a \u7684\u767d\u6c14\u6e10\u6e10\u6d88\u6563\uff0c\u904d\u4f53\u6e05\u51c9\uff0c\u5927\u529f\u4ea6\u5df2\u544a\u6210\u3002\u4ed6\u7ad9\u4e86\u8d77\u6765\uff0c\u63e9\u5e72\u4e86\u6c57\u6c34\uff0c\u7b11\u9053\uff1a\u201c\u6c5f\u5e08 \u5144\uff0c\u54b1\u4eec\u73a9\u4e2a\u6e38\u620f\u3002\u201d\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u539f\u6765\u4f60\u4e5f\u72b9\u6709\u91cd\u5fc3\uff0c\u54b1\u4eec\u73a9\u4e2a\u4ec0\u4e48\u6e38\u620f\uff1f\u201d \u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4f60\u4eec\u968f\u6211\u6765\u3002\u201d \u3000\u3000\u4e09\u4eba\u8d70\u51fa\u6d1e\u7a9f\uff0c\u5510\u52aa\u73e0\u7a46\u62e3\u4e86\u4e00\u68f5\u7c97\u53ef\u5408\u62b1\u7684\u677e\u6811\uff0c\u4ee5\u638c\u4f5c\u5200\uff0c\u5728\u6811\u8eab\u4e0a\u5212\u4e86\u4e00\u5708\uff0c \u559d\u58f0\u201c\u5012\uff01\u201d\u8f7b\u8f7b\u4e00\u63a8\uff0c\u8fd9\u68f5\u677e\u6811\u679c\u7136\u5e94\u58f0\u5012\u4e0b\u3002\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5f85\u6211\u8bd5\u8bd5\u80fd\u4e0d\u80fd\u529e\u5230\u3002\u201d \u4f9d\u6837\u753b\u846b\u82a6\uff0c\u4e5f\u63a8\u5012\u4e86\u4e00\u68f5\u677e\u6811\uff0c\u7b11\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u662f\u8981\u73a9\u62d4\u6811\u7684\u6bd4\u8d5b\u5417\uff1f\u201d\u5510\u52aa\u73e0\u7a46 \u9053\uff1a\u201c\u4e0d\u662f\u62d4\u6811\uff0c\u54b1\u4eec\u6765\u73a9\u62d4\u6cb3\u7684\u6e38\u620f\u3002\u8bd5\u6d4b\u4e00\u4e0b\u672c\u8eab\u6b66\u529b\uff0c\u987a\u4fbf\u6563\u529f\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u7528\u8fd9\u68f5\u6811\u6765\u73a9\u62d4\u6cb3\u6e38\u620f\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u62d4\u6cb3\u672c\u6765\u662f\u7528\u7ef3\u5b50 \u7684\uff0c\u73b0\u5728\u627e\u4e0d\u5230\u7ef3\u5b50\uff0c\u5c31\u7528\u8fd9\u68f5\u6811\u6765\u4ee3\u66ff\u7ef3\u5b50\u3002\u201d\u6c5f\u6d77\u5929\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u662f\u6e38\u620f\uff0c \u83b2\u59b9\uff0c\u4f60\u73b0\u5728\u4e0d\u662f\u611f\u5230\u6d51\u8eab\u662f\u52b2\u4e48\uff1f\u6b63\u5b9c\u501f\u6b64\u6563\u529f\u3002\u201d \u3000\u3000\u539f\u6765\u4ed6\u4eec\u670d\u98df\u4e86\u5929\u5fc3\u77f3\u4e4b\u540e\uff0c\u771f\u529b\u7a81\u98de\u731b\u8fdb\uff0c\u867d\u8bf4\u4e1a\u5df2\u5bfc\u6c14\u5f52\u5143\uff0c\u4f46\u8eab\u4f53\u5728\u4e00\u65f6\u4e4b \u95f4\uff0c\u4e5f\u8fd8\u672a\u80fd\u5b8c\u5168\u9002\u5e94\uff0c\u5510\u52aa\u73e0\u7a46\u77e5\u9053\u81ea\u5df1\u4e0e\u6c5f\u6d77\u65e0\u53ef\u4ee5\u65e0\u59a8\uff0c\u5374\u6015\u5bf9\u59b9\u59b9\u6709\u5bb3\uff0c\u6545\u800c \u63d0\u8bae\u7528\u6e38\u620f\u6765\u201c\u6563\u529f\u201d\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u5e08\u5144\uff0c\u6211\u4e0e\u4f60\u5148\u73a9\u3002\u59b9\u59b9\uff0c\u7b49\u4e0b\u4f60\u518d\u6765\u6291\u5f3a\u6276\u5f31\u3002\u201d\u4ed6\u4e0e\u6c5f\u6d77\u5929\u5404 \u5728\u4e00\u5934\uff0c\u5c06\u90a3\u68f5\u6811\u62ac\u4e86\u8d77\u6765\uff0c\u5404\u81ea\u8fd0\u529b\u5c06\u5bf9\u65b9\u62c9\u8fc7\u81ea\u5df1\u8fd9\u8fb9\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6c5f\u6d77\u592d\u4f7f\u51fa\u4e86 \u4e03\u6210\u529b\u6c14\uff0c\u731b\u7684\u4e00\u62c9\uff0c\u5510\u52aa\u73e0\u7a46\u201c\u767b\u3001\u767b\u3001\u767b\u201d\u7684\u5f80\u524d\u79fb\u52a8\u4e86\u4e09\u6b65\uff0c\u7b11\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u4f60 \u6765\u5e2e\u6211\u3002\u201d \u3000\u3000\u4e24\u5144\u59b9\u5408\u529b\u4e00\u62c9\uff0c\u6c5f\u6d77\u5929\u6e10\u6e10\u611f\u5230\u652f\u6301\u4e0d\u4f4f\uff0c\u7528\u8db3\u4e86\u5341\u6210\u529b\u9053\uff0c\u76f8\u6301\u4e86\u4e00\u4f4f\u9999\u7684\u65f6 \u523b\uff0c\u7ed3\u679c\u8fd8\u662f\u8eab\u4e0d\u7531\u5df1\u7684\u5411\u524d\u79fb\u52a8\u4e86\u51e0\u6b65\u3002\u539f\u6765\u4ed6\u539f\u6709\u7684\u529f\u529b\u5927\u7ea6\u662f\u4e0e\u5510\u52aa\u73e0\u7a46\u534a\u65a4\u516b \u4e24\uff0c\u4f46\u4ed6\u670d\u4e86\u4e09\u9897\u592d\u5fc3\u77f3\uff0c\u5510\u52aa\u73e0\u7a46\u5144\u59b9\u5219\u662f\u670d\u4e86\u4e24\u9897\uff0c\u6545\u800c\u8f83\u91cf\u8d77\u6765\uff0c\u6c5f\u6d77\u5929\u81ea\u662f\u8981 \u80dc\u8fc7\u5510\u52aa\u73e0\u7a46\uff0c\u4f46\u5374\u53c8\u4e0d\u53ca\u4ed6\u4eec\u5144\u59b9\u8054\u624b\u4e4b\u529b\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u6211\u8f93\u5566\u3002\u597d\u4e86\uff0c\u54b1\u4eec\u4e5f\u53ef\u4ee5\u6b47\u606f\u4e86\u3002\u201d\u53cc\u65b9\u6539\u5411\u524d\u63a8\uff0c\u5c06\u638c\u529b\u9001\u51fa\uff0c \u53ea\u542c\u5f97\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u5b9b\u5982\u96f7\u9e23\uff0c\u90a3\u68f5\u6811\u5e72\u88c2\u6210\u4e86\u5341\u51e0\u6bb5\u3002\u6728\u5c51\u6563\u4e86\u4e00\u5730\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u6413\u6413\u624b\uff0c\u559c\u6781\u800c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6211\u7684\u529b\u6c14\u7a81\u7136\u5927\u4e86\u8fd9\u4e48\u591a\u4e86\uff0c\u54c8\uff0c\u6211\u5728\u60f3\u2014\u2014\u201d \u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u60f3\u6253\u90a3\u53f6\u51b2\u9704\u4e00\u987f\uff1f\u201d\u8fd9\u8bf4\u8bdd\u672c\u6765\u662f\u8c37\u4e2d\u83b2\u8bf4\u8fc7\u7684\uff0c\u6240\u4ee5\u6c5f\u6d77\u5929\u624d\u8fd9\u4e48 \u8bf4\u7684\uff0c\u54ea\u77e5\u8c37\u4e2d\u83b2\u5374\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d\u6c5f\u6d77\u5929\u8be7\u9053\uff1a\u201c\u4f60\u4e0d\u6068\u4ed6\u4e86\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u4e0d\u6068\u4ed6\u3002\u6211\u662f\u5728\u60f3\u8fd9\u53ae\u867d\u7136\u53ef\u6068\uff0c\u4f46\u54b1\u4eec\u6700\u5927\u7684\u4ec7\u4eba\u7a76\u7adf \u662f\u90a3\u4e2a\u5978\u738b\u3002\u201d\u8f6c\u8fc7\u5934\u5bf9\u5510\u52aa\u73e0\u7a46\u8bf4\u9053\uff1a \u3000\u3000\u201c\u54e5\u54e5\uff0c\u91d1\u9e70\u5bab\u4e4b\u4f1a\u662f\u660e\u65e5\u4e3e\u884c\uff0c\u4eca\u665a\u6ca1\u6709\u4e8b\uff0c\u54b1\u4eec\u5148\u53bb\u53d6\u90a3\u597d\u738b\u7684\u72d7\u547d\uff01\u7236\u6bcd\u4e4b \u4ec7\u4e0d\u5171\u6234\u5929\uff0c\u54b1\u4eec\u7684\u672c\u9886\u5df2\u7ecf\u5927\u80dc\u4ece\u524d\uff0c\u6211\u662f\u4e00\u523b\u4e5f\u4e0d\u80fd\u518d\u7b49\u5f85\u4e86\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u62a5\u4ec7\u5fc3\u5207\uff0c\u7edd\u4e0d\u5728\u4ed6\u59b9\u59b9\u4e4b\u4e0b\uff0c\u4f46\u4ed6\u6bd4\u8f83\u8c28\u614e\uff0c\u60f3\u4e86\u4e00\u4f1a\u65b9\u59cb\u8bf4\u9053\uff1a\u201c\u4f60 \u8bf4\u7684\u4e5f\u6709\u9053\u7406\uff0c\u660e\u65e5\u4fbf\u662f\u4f1a\u671f\uff0c\u5b9d\u8c61\u6cd5\u5e08\u548c\u4ed6\u7684\u4e00\u5e72\u515a\u7fbd\u5fc5\u5b9a\u662f\u5728\u91d1\u9e70\u5b98\u4e2d\u5e03\u7f6e\u4e00\u5207\u3002 \u7687\u5bab\u5185\u7684\u9632\u8303\u5c31\u8981\u8f83\u758f\u4e86\u3002\u8fd9\u6b63\u662f\u4e00\u4e2a\u673a\u4f1a\uff0c\u54b1\u4eec\u4e5f\u4e0d\u5fc5\u9a6c\u4e0a\u5c31\u8981\u90a3\u5978\u738b\u6027\u547d\uff0c\u5c06\u4ed6\u6d3b \u6349\u66f4\u4f73\uff0c\u660e\u65e5\u4fbf\u5e26\u4ed6\u5230\u91d1\u9e70\u5bab\u8d74\u4f1a\uff0c\u4ed6\u6240\u8058\u6765\u7684\u90a3\u4e9b\u9ad8\u624b\uff0c\u52bf\u5fc5\u8981\u6811\u5012\u7322\u72f2\u6563\u4e86\uff0c\u5bf9\uff0c \u8fd9\u6837\u505a\u867d\u7136\u5192\u9669\uff0c\u5374\u53ef\u4ee5\u514d\u53bb\u4e00\u573a\u5e72\u6208\uff0c\u5374\u4e5f\u503c\u5f97\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u559c\u9053\uff1a\u201c\u90a3\u4e48\u54e5\u54e5\u662f\u8d5e\u540c\u4eca\u665a\u53bb\u4e86\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a \u3000\u3000\u201c\u53bb\u662f\u53ef\u4ee5\u53bb\u7684\uff0c\u4f46\u4f60\u53ef\u5f97\u82b1\u70b9\u65f6\u95f4\u505a\u505a\u51c6\u5907\u529f\u592b\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a \u3000\u3000\u201c\u4ec0\u4e48\u51c6\u5907\u529f\u592b\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u8fd9\u5ea7\u7687\u5b98\u662f\u501a\u5c71\u4fee\u5efa\u7684\uff0c\u5c42\u697c\u8038\u7fe0\uff0c\u5927\u5927\u5c0f\u5c0f\u7684\u5efa\u7b51\u7269\u4f55\u6b62\u6570\u5341\u767e \u95f4\uff0c\u54b1\u4eec\u8981\u8fdb\u5bab\u53bb\u641c\u7d22\u5978\u738b\uff0c\u6700\u5c11\u5f97\u5bf9\u5468\u56f4\u7684\u5730\u5f62\u548c\u7687\u5bab\u7684\u6784\u9020\u77e5\u9053\u6e05\u695a\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a \u201c\u8fd9\u53ef\u96be\u4e86\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u600e\u80fd\u77e5\u9053\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u5e78\u800c\u6211\u65e9\u5df2\u6709\u4e86\u51c6\u5907\uff0c\u7687\u5bab\u6240\u5728\u7684\u5730\u5f62\u548c\u6784\u9020\u65e9\u5df2\u5728\u6211\u8111\u5b50\u4e4b\u4e2d\u3002\u201d \u8c37\u4e2d\u83b2\u8be7\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fd9\u6837\u6e05\u695a\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6211\u867d\u7136\u6ca1\u6709\u53bb\u8fc7\uff0c\u4f46\u5e08\u7236\u5374\u662f\u53bb\u8fc7\u51e0\u6b21\u7684\u4e86\uff0c\u4ed6\u5bf9\u5bab\u4e2d\u4e00\u5207\uff0c\u4e86\u5982 \u6307\u638c\uff0c\u66fe\u753b\u51fa\u56fe\u5f62\uff0c\u8be6\u8be6\u7ec6\u7ec6\u5730\u5bf9\u6211\u89e3\u8bf4\uff0c\u53ef\u60dc\u6211\u56e0\u4e3a\u56fe\u5f62\u5df2\u6df1\u5370\u6211\u8111\u6d77\u4e4b\u4e2d\uff0c\u6240\u4ee5\u6ca1 \u6709\u5e26\u51fa\u6765\uff0c\u4e0d\u8fc7\uff0c\u6211\u53ef\u4ee5\u7167\u6837\u753b\u51fa\uff0c\u4e1d\u6beb\u65e0\u8bef\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5927\u559c\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u5c31\u753b\u51fa\u6765\u7ed9\u6211\u770b\u5427\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u4ee5\u6307\u4ee3\u7b14\uff0c\u4f7f\u51fa\u91d1\u521a\u6307\u529b\uff0c\u4fbf\u5728\u5730\u4e0a\u5c06\u7687\u5bab\u7684\u56fe\u5f62\u753b\u51fa\u6765\uff0c\u8c37\u4e2d\u83b2\u806a\u660e\u7edd \u9876\uff0c\u51dd\u795e\u9ed8\u8bb0\uff0c\u4e0d\u6d88\u591a\u4e45\uff0c\u56fe\u5f62\u4ea6\u5df2\u662f\u6df1\u5370\u8111\u5c6f\uff0c\u518d\u8fc7\u4e00\u4f1a\uff0c\u6c5f\u6d77\u5929\u4e5f\u53ef\u4ee5\u4e1d\u6beb\u65e0\u8bef\u7684 \u7167\u753b\u51fa\u6765\u4e86\u3002 \u3000\u3000\u7b11\u9053\uff1a\u201c\u6211\u4eca\u665a\u968f\u4f60\u4eec\u53bb\uff0c\u4f60\u4eec\u62a5\u4ec7\uff0c\u6211\u7ed9\u4f60\u4eec\u63a9\u62a4\u3002\u201d\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u597d\u5f97\u5f88\uff0c \u6709\u4f60\u8fd9\u4f4d\u4fdd\u9556\uff0c\u83ab\u8bf4\u5978\u738b\u7684\u738b\u5bab\uff0c\u5c31\u662f\u864e\u7a74\u9f99\u6f6d\u54b1\u4eec\u4e5f\u5c3d\u53ef\u4ee5\u95ef\u5f97\u8fc7\u53bb\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u5bf9\u654c\u5e94\u8be5\u80c6\u5927\uff0c\u4f46\u4e5f\u8981\u5fc3\u7ec6\uff0c\u867d\u7136\u5b9d\u8c61\u6cd5\u5e08\u4eca\u665a\u591a\u534a \u4f1a\u7559\u5728\u4ed6\u81ea\u5df1\u7684\u91d1\u9e70\u5bab\uff0c\u4f46\u738b\u5bab\u4e2d\u7684\u597d\u624b\u4e5f\u8fd8\u4e0d\u5c11\uff0c\u4e0d\u53ef\u592a\u8fc7\u8f7b\u654c\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff0c\u201c\u6211 \u77e5\u9053\u3002\u5230\u4e86\u5176\u65f6\uff0c\u6211\u542c\u4f60\u6307\u6325\u4fbf\u662f\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u62ac\u5934\u4e00\u770b\uff0c\u65e5\u5934\u5df2\u7ecf\u8fc7\u5348\uff0c\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u56de\u53bb\u5f04\u70b9\u4e1c\u897f\u5403\u5427\u3002\u6628\u665a\u95f9\u4e86\u4e00 \u665a\uff0c\u5927\u5bb6\u90fd\u672a\u66fe\u7761\u8fc7\uff0c\u4e5f\u5e94\u8be5\u6b47\u6b47\u4e86\u3002\u201d \u3000\u3000\u9971\u9910\u4e4b\u540e\uff0c\u5404\u4eba\u5404\u81ea\u6253\u5750\u4f11\u606f\u3001\u90fd\u517b\u8db3\u4e86\u7cbe\u795e\uff0c\u5df2\u662f\u9ec4\u660f\u65f6\u5206\uff0c\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u53ef \u4ee5\u53bb\u4e86\uff0c\u7687\u5bab\u5efa\u5728\u5b5f\u79ef\u52a0\u6bd4\u5c71\u4e0a\uff0c\u5c31\u5728\u8fd9\u5ea7\u5c71\u7684\u5317\u8fb9\uff0c\u7ffb\u8fc7\u5c71\u53bb\uff0c\u4ece\u4e0a\u9762\u8d70\u4e0b\u6765\uff0c\u4e0d\u8fc7 \u4e09\u5341\u591a\u91cc\u5c71\u8def\u3002\u201d \u3000\u3000\u4e09\u4eba\u90fd\u662f\u4e00\u8eab\u4e0a\u4e58\u7684\u8f7b\u529f\uff0c\u4e0d\u6d88\u534a\u4e2a\u65f6\u8fb0\uff0c\u5c31\u5230\u4e86\u5b5f\u79ef\u52a0\u6bd4\u5c71\u7684\u4e3b\u5cf0\uff0c\u4ece\u4e0a\u9762\u671b\u4e0b \u6765\uff0c\u53ea\u89c1\u91d1\u78a7\u8f89\u714c\u7684\u5c4b\u5b57\u91cd\u91cd\u53e0\u53e0\uff0c\u5510\u52aa\u73e0\u7a46\u884c\u524d\u5f15\u8def\uff0c\u8d8a\u8fc7\u51e0\u91cd\u5c97\u5ce6\uff0c\u201c\u4ece\u4e00\u5ea7\u5cfb\u5ca9 \u722c\u4e0b\u6765\uff0c\u7687\u5bab\u5468\u56f4\u4e94\u91cc\u4e4b\u5185\uff0c\u867d\u6709\u8bb8\u591a\u7ad9\u5c97\u7684\u6b66\u571f\uff0c\u5374\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u4f1a\u6709\u4eba\u4ece\u5cfb\u5ced\u7684\u5c71 \u4e0a\u4e0b\u6765\uff0c\u95ee\u51b5\u5510\u52aa\u73e0\u7a46\u7b49\u4eba\u53c8\u90fd\u662f\u8eab\u8f7b\u4f3c\u71d5\uff0c\u65e0\u58f0\u65e0\u606f\uff0c\u4ed6\u4eec\u54ea\u80fd\u53d1\u73b0\uff1f \u3000\u3000\u6248\u52aa\u73e0\u7a46\u5e26\u5934\uff0c\u98de\u8fc7\u4e00\u91cd\u56f4\u5899\uff0c\u56f4\u5899\u5185\u6b63\u662f\u5fa1\u82b1\u56ed\u3002\u5510\u52aa\u73e0\u7a46\u5ffd\u7136\u505c\u4e0b\u811a\u6b65\uff0c\u6478\u51fa \u4e86\u4e09\u679d\u78a7\u7eff\u8272\u7684\u77ed\u7bad\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u505a\u4ec0\u4e48\u7528\u7684\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u5c0f\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u86c7\u7130\u7bad\uff0c\u5c04\u4e0a\u7a7a\u4e2d\uff0c \u4f1a\u53d1\u51fa\u4e00\u6e9c\u84dd\u8272\u7684\u706b\u7130\u548c\u523a\u8033\u7684\u5578\u58f0\u3002\u59b9\u59b9\uff0c\u6211\u548c\u4f60\u5206\u5934\u53bb\u641c\u7d22\u5978\u738b\uff0c\u6c5f\u5e08\u5144\uff0c\u4f60\u85cf\u5728 \u56ed\u4e2d\u8fd9\u5ea7\u5c0f\u84ec\u83b1\u5c71\u4e0a\u63a5\u5e94\u6211\u4eec\u3002\u5728\u8fd9\u5ea7\u5c71\u4e0a\uff0c\u53ef\u4ee5\u671b\u89c1\u738b\u5b98\u5404\u5904\u3002\u54b1\u4eec\u4e4b\u4e2d\uff0c\u5018\u82e5\u6709\u8c01 \u9047\u5230\u5f3a\u654c\u56f4\u653b\uff0c\u96be\u4ee5\u5e94\u4ed8\u7684\u8bdd\uff0c\u5c31\u7acb\u5373\u53d1\u51fa\u86c7\u7130\u7bad\u62a5\u8b66\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5c06\u86c7\u7130\u7bad\u5206\u7ed9\u6bcf\u4eba\u4e00\u679d\uff0c\u968f\u540e\u95ee\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u8fd9\u5bab\u4e2d\u7684\u5730\u5f62\u548c\u7ed3\u6784\uff0c\u4f60\u90fd \u8bb0\u719f\u4e86\u5417\uff1f\u201d\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u6211\u4e00\u95ed\u4e0a\u773c\u775b\uff0c\u6574\u5e45\u738b\u5bab\u56fe\u5f62\u5c31\u4f1a\u5728\u6211\u8111\u6d77\u4e2d\u6d6e\u73b0\u51fa\u6765\uff0c \u4e0d\u7528\u62c5\u5fc3\uff0c\u7edd\u9519\u4e0d\u4e86\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u597d\uff0c\u6211\u4ece\u4e1c\u9762\u641c\u7d22\uff0c\u4f60\u4ece\u897f\u9762\u641c\u7d22\uff0c\u4f60\u90a3\u6761\u8def\u7ebf\uff0c\u7279\u522b\u8981\u6ce8\u610f\u5ef6\u5e86 \u5bab\uff0c\u897f\u4e50\u82d1\u3001\u6e05\u534e\u9601\u3001\u900d\u9065\u6bbf\u8fd9\u51e0\u5904\u5730\u65b9\u3002\u4e0d\u8bba\u662f\u5426\u64d2\u83b7\u5978\u738b\uff0c\u4e00\u6253\u4e94\u66f4\uff0c\u5c31\u56de\u5230\u5c0f\u84ec \u83b1\u5c71\u96c6\u5408\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\u3002\u201d\u5f53\u4e0b\u4e09\u4eba\u5206\u5934\u884c\u4e8b\u3002 \u3000\u3000\u5148\u8bf4\u8c37\u4e2d\u83b2\u8fd9\u4e00\u8def\uff0c\u897f\u4e50\u82d1\u6700\u8fd1\uff0c\u5979\u4e8e\u662f\u5148\u5230\u897f\u4e50\u82d1\u4fa6\u67e5\u3002 \u3000\u3000\u5fa1\u82b1\u56ed\u4e2d\u6709\u4e2a\u4e0d\u5927\u4e0d\u5c0f\u7684\u4eba\u5de5\u6e56\uff0c\u897f\u4e50\u82d1\u662f\u6e56\u8fb9\u7684\u4e00\u5e62\u5efa\u7b51\uff0c\u8c37\u4e2d\u83b2\u4e00\u5230\u6e56\u8fb9\uff0c\u4fbf \u542c\u5f97\u82d1\u4e2d\u4f20\u51fa\u7b19\u9f13\u4e4b\u58f0\uff0c\u539f\u6765\u8fd9\u662f\u5bab\u4e2d\u6559\u7ec3\u6b4c\u821e\u7684\u5730\u65b9\uff0c\u56fd\u738b\u7ecf\u5e38\u4f1a\u5230\u8fd9\u513f\u5bfb\u4e50\u7684\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u6700\u64c5\u957f\u8f7b\u529f\uff0c\u670d\u98df\u4e86\u5929\u5fc3\u77f3\u4e4b\u540e\uff0c\u771f\u6c14\u8fd0\u7528\u81ea\u5982\uff0c\u8f7b\u529f\u66f4\u52a0\u8d85\u5999\uff0c\u5f53\u4e0b\u8f7b\u8f7b \u4e00\u8dc3\uff0c\u8dc3\u4e0a\u7409\u7483\u74e6\u9762\u3001\u5f53\u771f\u6709\u5982\u4e00\u53f6\u98d8\u5760\uff0c\u6beb\u65e0\u58f0\u606f\u3002\u5979\u4ece\u5c4b\u9876\u4e0a\u671b\u4e0b\u53bb\uff0c\u53ea\u89c1\u4e00\u961f\u5b98 \u5973\uff0c\u6b63\u5728\u7fe9\u7fe9\u8d77\u821e\uff0c\u5ffd\u5982\u8774\u8776\u7a7f\u82b1\uff0c\u5ffd\u5982\u98de\u71d5\u63a0\u6c34\uff1b\u961f\u5f62\u77ac\u606f\u767e\u53d8\uff0c\u7aef\u7684\u662f\u8d4f\u5fc3\u60a6\u76ee\uff0c \u597d\u770b\u715e\u4eba\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5fc3\u9053\uff1a\u201c\u8fd9\u5978\u738b\u5012\u4f1a\u4eab\u4e50\u3002\u201d\u53ea\u542c\u5f97\u4e00\u4e2a\u4f3c\u662f\u6559\u5934\u6a21\u6837\u7684\u4eba\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u8fd8 \u672a\u5341\u5206\u7eaf\u719f\uff0c\u4f60\u4eec\u518d\u7ec3\u4e86\u904d\uff0c\u7687\u4e0a\u8bf4\u597d\u4eca\u665a\u6765\u7684\u3002\u201d\u8c37\u4e2d\u83b2\u5fc3\u5934\u5927\u559c\uff0c\u201c\u771f\u60f3\u4e0d\u5230\u8fd9\u4e48 \u5bb9\u6613\uff0c\u6d3b\u8be5\u90a3\u5978\u738b\u4e27\u5728\u6211\u7684\u624b\u4e0a\u3002\u201d \u3000\u3000\u90a3\u7fa4\u5bab\u5973\u9e3e\u58f0\u5456\u5456\u7684\u7eb7\u7eb7\u7b54\u5e94\uff0c\u6b63\u8981\u518d\u7ec3\uff0c\u5ffd\u89c1\u5bab\u95e8\u5f00\u5904\uff0c\u6709\u4e2a\u5c11\u5e74\u8d70\u4e86\u8fdb\u6765\uff0c\u6b63 \u662f\u90a3\u53f6\u51b2\u9704\u3002 \u3000\u3000\u90a3\u6559\u5934\u6a21\u6837\u7684\u4eba\u8fde\u5fd9\u8fc7\u6765\u656c\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u6bbf\u4e0b\u5148\u6765\u4e86\uff0c\u7687\u4e0a\u8d77\u9a7e\u4e86\u6ca1\u6709\uff1f\u201d\u53f6\u51b2\u9704 \u9053\uff1a\u201c\u7687\u4e0a\u4eca\u665a\u6709\u4e8b\uff0c\u4e0d\u4f1a\u6765\u4e86\u3002\u4f60\u4eec\u8f9b\u82e6\u4e86\uff0c\u53ef\u4ee5\u6b47\u606f\u53bb\u5427\u3002\u201d\u6709\u4e2a\u5bb9\u8c8c\u5f88\u7f8e\u7684\u5bab\u5973\uff0c \u4f3c\u662f\u548c\u8d22\u51b2\u9704\u9887\u4e3a\u719f\u79f8\uff0c\u62c9\u7740\u4ed6\u7684\u8896\u5b50\u9053\uff1a\u201c\u7687\u4e0a\u4e0d\u6765\uff0c\u6bbf\u4e0b\u6765\u4e86\u4e5f\u662f\u4e00\u6837\u3002\u6211\u4eec\u7ec3\u7ed9 \u4f60\u770b\u5427\u3002\u201d\u65f6\u51b2\u9704\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u7f20\u6211\uff0c\u6211\u4e5f\u6709\u4e8b\u3002\u201d \u3000\u3000\u90a3\u5bab\u5973\u5658\u8d77\u5c0f\u5634\u513f\u55d4\u9053\uff1a\u201c\u8c01\u7f20\u4f60\u4e86\uff1f\u8fd9\u5c0f\u871c\u821e\u8fd8\u662f\u4f60\u6307\u5b9a\u8981\u6211\u4eec\u7ec3\u7684\uff0c\u597d\u5440\uff0c\u6211 \u4eec\u7ec3\u719f\u4e86\uff0c\u4f60\u5012\u6446\u8d77\u67b6\u5b50\u6765\u4e86\u3002\u4e0d\u770b\u4fbf\u7f62\uff0c\u8c01\u5e0c\u7f55\u4f60\u770b\uff1f\u201d \u3000\u3000\u8fd9\u5bab\u5973\u548c\u53f6\u51b2\u9704\u662f\u6253\u60c5\u9a82\u4fcf\u60ef\u4e86\u7684\uff0c\u6ee1\u4ee5\u4e3a\u65f6\u51b2\u9704\u4f1a\u5411\u5979\u8d54\u7f6a\uff0c\u6700\u5c11\u4e5f\u770b\u5979\u4e00\u573a\u6b4c \u821e\uff0c\u8c01\u77e5\u53f6\u51b2\u9704\u5374\u4f3c\u6709\u6ee1\u6000\u5fc3\u4e8b\uff0c\u795e\u8272\u4e0d\u6b22\uff0c\u7529\u5f00\u4e86\u8896\u5b50\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u597d\u59d0\u59d0\uff0c\u6211\u771f \u7684\u6709\u4e8b\u3002\u660e\u5929\u665a\u4e0a\uff0c\u6211\u65e9\u70b9\u6765\u770b\u4f60\u3002\u201d\u90a3\u5bab\u5973\u8d4c\u6c14\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u662f\u8d35\u4eba\u4e8b\u5fd9\uff0c\u6211\u4e0d\u6562 \u7559\u4f60\u3002\u53bb\u5427\uff0c\u53bb\u5427\u3002\u201d\u53f6\u51b2\u9704\u8336\u4e5f\u4e0d\u559d\u4e00\u676f\u5c31\u8d70\u4e86\u3002\u7aef\u7684\u662f\u6765\u4e5f\u5306\u5306\uff0c\u53bb\u4e5f\u5306\u5306\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u597d\u751f\u5931\u671b\uff0c\u731b\u5730\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u662f\u5978\u738b\u6d3e\u4ed6\u6765\u7684\uff0c\u4ed6\u4e00\u5b9a\u77e5\u9053\u597d\u738b\u5728\u4ec0\u4e48\u5730 \u65b9\u3002\u201d\u672c\u60f3\u7acb\u523b\u8dc3\u4e0b\u53bb\u5c06\u53f6\u51b2\u9704\u6293\u4f4f\uff0c\u4f46\u968f\u5373\u60f3\u9053\uff1a\u201c\u6211\u7684\u6b66\u529f\u867d\u7136\u589e\u5f3a\u4e86\u4e24\u500d\uff0c\u76f8\u4fe1 \u53ef\u4ee5\u80dc\u5f97\u8fc7\u8fd9\u5978\u8d3c\u4e86\u3002\u4f46\u8981\u5728\u5341\u62db\u516b\u62db\u4e4b\u5185\uff0c\u5c06\u4ed6\u751f\u64d2\uff0c\u5374\u4e5f\u672a\u5fc5\u80fd\u591f\u3002\u4e00\u95f9\u8d77\u6765\uff0c\u8fd9 \u53ef\u5c31\u8981\u6253\u8349\u60ca\u86c7\u4e86\u3002\u201d\u4e8e\u662f\u6539\u53d8\u4e86\u4e3b\u610f\uff0c\u5fc3\u60f3\u81ea\u5df1\u7684\u8f7b\u529f\u73b0\u5728\u5df2\u662f\u8fdc\u5728\u53f6\u51b2\u9704\u4e4b\u4e0a\uff0c\u4e0d \u5982\u6697\u6697\u8ddf\u8e2a\uff0c\u4ece\u4ed6\u7684\u8eab\u4e0a\uff0c\u8ffd\u51fa\u597d\u4e3b\u7684\u4e0b\u843d\u3002 \u3000\u3000\u54ea\u77e5\u5979\u53ea\u662f\u8e0c\u8e87\u7247\u523b\uff0c\u5f85\u5230\u8ffd\u51fa\u897f\u4e50\u82d1\u5916\uff0c\u5df2\u662f\u4e0d\u89c1\u4e86\u53f6\u51b2\u9704\u7684\u8e2a\u8ff9\u3002\u9644\u8fd1\u6ca1\u6709\u623f \u5c4b\uff0c\u4ee5\u53f6\u51b2\u9704\u7684\u8f7b\u529f\uff0c\u51b3\u4e0d\u80fd\u5728\u7247\u523b\u4e4b\u95f4\uff0c\u5c31\u5728\u5979\u9650\u76ae\u5e95\u4e0b\u6d88\u5931\uff0c\u60f3\u6765\u662f\u53e6\u6709\u79d8\u9053\u3002\u8c37 \u4e2d\u83b2\u9887\u4e3a\u61ca\u607c\uff0c\u6094\u6068\u81ea\u5df1\u72b9\u7591\u4e0d\u51b3\uff0c\u9519\u5931\u826f\u673a\u3002\u53ea\u597d\u4f9d\u7167\u54e5\u54e5\u6240\u89c4\u5b9a\u7684\u8def\u7ebf\uff0c\u7ee7\u7eed\u5411\u524d \u641c\u7d22\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u4f1a\uff0c\u5ffd\u89c1\u4e24\u4e2a\u5bab\u5973\uff0c\u6253\u7740\u706f\u7b3c\u8d70\u6765\uff0c\u8c37\u4e2d\u83b2\u95ea\u5230\u4e00\u5757\u5c4f\u98ce\u822c\u7684\u5047\u5c71\u77f3\u540e\uff0c\u53ea \u542c\u5f97\u4e00\u4e2a\u5bab\u5973\u8bf4\u9053\uff1a\u201c\u4f60\u6740\u8fc7\u4eba\u6ca1\u6709\uff1f\u201d\u8c37\u4e2d\u83b2\u9887\u89c9\u5947\u602a\uff0c\u7ad6\u8d77\u4e86\u8033\u6735\uff0c\u7b2c\u4e8c\u4e2a\u5bab\u5973\u8bf4 \u9053\uff1a\u201c\u6211\u8fde\u4e00\u53ea\u9e21\u4e5f\u6ca1\u6740\u8fc7\uff0c\u6211\u7684\u5fc3\u53ef\u8df3\u5f97\u614c\u4e86\u3002\u201d\u5148\u5934\u90a3\u5bab\u5973\u9053\uff1a\u201c\u6211\u4e5f\u662f\u5462\u3002\u65e9\u77e5 \u6709\u4eca\u665a\u4e4b\u4e8b\uff0c\u6211\u4e5f\u4e0d\u7ec3\u4ec0\u4e48\u6b66\u529f\u4e86\u3002\u6211\u5b9e\u5728\u4e0d\u60f3\u6740\u4eba\uff0c\u4f46\u7687\u4e0a\u7684\u547d\u4ee4\uff0c\u5374\u53c8\u600e\u80fd\u8fdd\u80cc\uff1f\u201d \u5979\u7684\u540c\u4f34\u9053\uff1a\u201c\u597d\u5728\u7687\u4e0a\u53ea\u662f\u8981\u5979\u81ea\u6740\u3002\u201d \u3000\u3000\u5148\u5934\u90a3\u5b98\u5973\u9053\uff1a\u201c\u53ef\u662f\u5979\u5018\u82e5\u4e0d\u80af\u81ea\u6740\uff0c\u5c31\u5f97\u54b1\u4eec\u52a8\u624b\u4e86\u3002\u201d\u7b2c\u4e8c\u4e2a\u5bab\u5973\u9053\uff1a\u201c\u5509\uff0c \u6211\u4f46\u613f\u5979\u4e00\u53e3\u6c14\u5c31\u773c\u4e86\u6bd2\u836f\uff0c\u8fd9\u6837\u6700\u5feb\uff1b\u8981\u4e0d\u7136\u62ff\u8d77\u5200\u6765\u4e00\u62b9\u8116\u5b50\u4e5f\u5e72\u51c0\u5229\u843d\uff0c\u5343\u4e07\u4e0d \u8981\u4e0a\u540a\uff0c\u6211\u6700\u6015\u770b\u540a\u6b7b\u9b3c\u7684\u6a21\u6837\u3002\u201d\u5148\u5934\u90a3\u5bab\u5973\u9053\uff0c\u201c\u4f46\u5018\u82e5\u8981\u6211\u52a8\u624b\uff0c\u6211\u5c31\u5b81\u613f\u770b\u540a \u6b7b\u9b3c\u4e86\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u4e2a\u5bab\u5973\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u662f\u5012\u6963\uff0c\u88ab\u6d3e\u4e0a\u8fd9\u6837\u7684\u5dee\u4e8b\u3002\u5582\uff0c\u4f60\u53ef\u77e5\u9053\u90a3\u5973 \u4eba\u662f\u8c01\uff1f\u201d\u8fd9\u6b63\u662f\u8c37\u4e2d\u83b2\u5fc3\u4e2d\u7684\u7591\u95ee\uff0c\u53ea\u542c\u5f97\u5148\u5934\u90a3\u5bab\u5973\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u662f\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002 \u542c\u8bf4\u5173\u5728\u51b7\u5bab\u4e4b\u4e2d\u5df1\u6709\u5341\u591a\u5e74\u4e86\uff0c\u5173\u5728\u51b7\u5bab\u4e4b\u4e2d\uff0c\u5927\u7ea6\u603b\u662f\u5931\u5ba0\u7684\u5983\u5b50\u4e4b\u7c7b\u5427\uff1f\u201d \u3000\u3000\u5979\u7684\u540c\u4f34\u9053\uff1a\u201c\u65e2\u7136\u5173\u4e86\u5341\u591a\u5e74\uff0c\u90a3\u662f\u51b3\u4e0d\u80fd\u89e6\u6012\u7687\u4e0a\u7684\u4e86\u3002 \u3000\u3000\u5373\u7b97\u7687\u4e0a\u4e0d\u559c\u6b22\u5979\uff0c\u5173\u4e86\u5979\u5341\u591a\u5e74\uff0c\u4e5f\u5e94\u8be5\u4ec0\u4e48\u6c14\u90fd\u6d88\u4e86\u3002\u4e3a\u4ec0\u4e48\u7687\u4e0a\u5374\u53c8\u7a81\u7136\u4e0b \u4ee4\u5c06\u4ed6\u5904\u6b7b\uff1f\u201d \u3000\u3000\u5148\u5934\u90a3\u5bab\u5973\u9053\uff1a\u201c\u6211\u600e\u4e48\u77e5\u9053\uff1f\u662f\u771f\u5fb7\u7687\u989d\u5a18\u5c06\u7687\u4e0a\u7684\u547d\u4ee4\u8f6c\u4ea4\u7ed9\u6211\u7684\u3002\u5979\u53ea\u662f\u8bf4\uff0c \u8981\u6211\u548c\u4f60\u53bb\u8feb\u8fd9\u4e2a\u5973\u4eba\u81ea\u5c3d\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u5343\u4e07\u4e0d\u53ef\u6cc4\u6f0f\uff0c\u5426\u5219\u4f60\u6211\u6027\u547d\u4e0d\u4fdd\u3002\u4f60\u60f3\u60f3\u5979\u8fd9 \u6837\u51f6\uff0c\u6211\u8fd8\u6562\u591a\u95f4\u534a\u53e5\u5417\uff1f\u201d\u5979\u7684\u540c\u4f34\u9053\uff0c\u201c\u8fd9\u4f4d\u7687\u989d\u5a18\u4e5f\u662f\u5947\u602a\uff0c\u5979\u2026\u2026\u201d\u8fd9\u4e24\u4e2a\u5bab \u5973\u8d8a\u884c\u8d8a\u8fdc\uff0c\u8bf4\u5230\u8fd9\u91cc\uff0c\u58f0\u97f3\u5df2\u662f\u7ec6\u4e0d\u53ef\u8fa8\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5fc3\u9053\uff1a\u201c\u542c\u4ed6\u4eec\u6240\u8bf4\uff0c\u770b\u6765\u5979\u4eec\u4e5f\u662f\u4e0d\u77e5\u9053\u5978\u738b\u6240\u5728\u7684\u4e86\u3002\u8981\u4e0d\u8981\u6551\u90a3\u53ef\u51b7 \u7684\u5973\u4eba\u5462\uff1f\u201d\u672c\u6765\u5979\u4eca\u665a\u7684\u76ee\u7684\u4e2d\u662f\u8981\u523a\u6740\u56fd\u738b\uff0c\u5018\u82e5\u4e0d\u80fd\u5728\u5bab\u5973\u8eab\u4e0a\uff0c\u8ffd\u67e5\u51fa\u56fd\u738b\u6240 \u5728\uff0c\u5c31\u4e0d\u8be5\u7ba1\u8fd9\u95f2\u4e8b\uff0c\u4ee5\u514d\u6253\u8349\u60ca\u86c7\uff0c\u4f46\u8fd9\u4ef6\u4e8b\u5173\u7cfb\u4e00\u4e2a\u4eba\u7684\u5f80\u547d\uff0c\u4e0d\u77e5\u9053\u4e5f\u8fd8\u7f62\u4e86\uff0c \u5982\u4eca\u5df2\u7136\u77e5\u9053\uff0c\u6492\u624b\u4e0d\u7ba1\u3001\u53c8\u4f3c\u4e4e\u4e8e\u5fc3\u4e0d\u5fcd\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u6b63\u60f3\u4ece\u5047\u5c71\u77f3\u540e\u8dc3\u51fa\uff0c\u5ffd\u89c1\u6709\u4e2a\u9ed1\u8863\u4eba\u7684\u80cc\u5f71\u6b63\u5728\u5c71\u77f3\u4e4b\u524d\uff0c\u4f3c\u4e4e\u542c\u5230\u4e00\u70b9 \u58f0\u606f\uff0c\u7a81\u7136\u56de\u8fc7\u5934\u6765\uff0c\u95ee\u9053\uff1a\u201c\u8c01\u5728\u8fd9\u513f\uff1f\u201d\u8c37\u4e2d\u83b2\u9a88\u6307\u4e00\u70b9\uff0c\u90a3\u4eba\u541e\u80f8\u5438\u8179\uff0c\u5e73\u7a7a\u632a \u540e\u534a\u5c3a\uff0c\u8c37\u4e2d\u83b2\u5fc3\u4e2d\u4e00\u51db\uff0c\u77e5\u9053\u662f\u4e2a\u52b2\u654c\uff0c\u6b63\u8981\u53d8\u62db\u64d2\u62ff\uff0c\u5ffd\u542c\u5f97\u90a3\u4eba\u5589\u5934\u5495\u5495\u4f5c\u54cd\uff0c \u201c\u535c\u901a\u201d\u4e00\u58f0\uff0c\u5df2\u662f\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u6014\u4e86\u4e00\u6014\uff0c\u53ea\u662f\u8fd8\u4e0d\u660e\u767d\u8fd9\u4eba\u4f55\u4ee5\u4f1a\u5ffd\u7136\u5012\u4e0b\uff0c\u540e\u6765\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8bd5\u628a\u624b\u6307\u5411 \u77f3\u4e0a\u4e00\u6233\uff0c\u53ea\u89c1\u77f3\u5c51\u7eb7\u98de\uff0c\u706b\u661f\u8ff8\u73b0\uff0c\u8fd9\u624d\u604d\u7136\u5927\u609f\u3002\u539f\u6765\u5979\u529f\u529b\u5927\u8fdb\u4e4b\u540e\uff0c\u5df2\u662f\u7ec3\u6210 \u4e86\u65e0\u5f62\u7f61\u6c14\uff0c\u8fde\u5979\u81ea\u5df1\u4e5f\u672a\u77e5\u9053\u3002\u65e0\u5f62\u7f61\u6c14\u7ec3\u6210\uff0c\u5018\u80fd\u8fd0\u7528\u81ea\u5982\uff0c\u53ef\u4ee5\u5728\u6570\u4e08\u4e4b\u5916\uff0c\u70b9 \u4eba\u7a74\u9053\uff0c\u8c37\u4e2d\u83b2\u73b0\u5728\u8fd8\u672a\u5230\u8fd9\u5883\u754c\uff0c\u4f46\u521a\u624d\u5979\u7684\u624b\u6307\u4ea6\u5df2\u89e6\u53ca\u90a3\u4eba\u7684\u8863\u88f3\uff0c\u867d\u6ca1\u6cbe\u7740\u76ae \u8089\uff0c\u4f46\u7f61\u6c14\u76f4\u900f\u6307\u5c16\uff0c\u4e5f\u5c31\u7b49\u4e8e\u91cd\u624b\u6cd5\u70b9\u7a74\u4e86\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5927\u559c\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6b64\u4eba\u6b66\u529f\u751a\u9ad8\uff0c\u60f3\u6765\u4e0d\u662f\u4e00\u822c\u536b\u58eb\uff0c\u96be\u5f97\u4ed6\u9001\u4e0a\u95e8\u6765\uff0c \u4e14\u5f85\u6211\u5ba1\u4ed6\u4e00\u5ba1\u3002\u201d\u4e00\u628a\u6293\u4f4f\u90a3\u4eba\u7684\u8863\u9886\uff0c\u5c06\u4ed6\u62d6\u5230\u5047\u5c71\u77f3\u540e\uff0c\u4e00\u638c\u8d34\u7740\u4ed6\u7684\u540e\u5fc3\uff0c\u7136 \u540e\u89e3\u5f00\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u5728\u4ed6\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u95ee\u4e00\u53e5\u4f60\u7b54\u4e00\u53e5\uff0c\u5018\u6562\u53eb\u56b7\uff0c\u4e00\u638c\u53d6\u4f60\u7684 \u72d7\u547d\uff0c\u4f60\u660e\u767d\u4e86\u4e48\uff1f\u201d\u90a3\u4eba\u60c5\u77e5\u8c37\u4e2d\u83b2\u6b66\u529f\u80dc\u4ed6\u5341\u500d\uff0c\u60ca\u60f6\u4e4b\u6781\uff0c\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u56fd\u738b\u5728\u54ea\u513f\uff1f\u201d\u90a3\u4eba\u6447\u4e86\u6447\u5934\uff0c\u8868\u793a\u4e0d\u77e5\u3002\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u6562 \u5bf9\u6211\u9690\u7792\uff0c\u6211\u90fd\u77e5\u9053\u4e86\u3002\u201d\u638c\u5fc3\u5fae\u5fae\u5410\u52b2\uff0c\u90a3\u4eba\u8179\u5982\u5200\u7ede\uff0c\u614c\u5fd9\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u76f8\u7792\uff0c\u6211 \u867d\u7136\u63a5\u4e86\u56fd\u738b\u7684\u547d\u4ee4\uff0c\u4f46\u5374\u5b9e\u5728\u4e0d\u77e5\u7687\u4e0a\u5728\u54ea\u513f\uff0c\u547d\u4ee4\u662f\u5185\u4f8d\u536b\u957f\u8f6c\u4ea4\u7ed3\u6211\u7684\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5176\u5b9e\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u8fd9\u4eba\u7684\u4e8b\u60c5\uff0c\u59d1\u4e14\u5413\u4ed6\u4e00\u5413\uff0c\u4e00\u5413\u5c31\u5413\u51fa\u4ed6\u7684\u5b9e\u6d3b\uff0c\u5012\u662f \u4e2a\u610f\u5916\u6536\u83b7\u3002\u8c37\u4e2d\u83b2\u7acb\u5373\u95ee\u9053\uff1a\u201c\u7687\u4e0a\u7ed9\u4e86\u4f60\u4ec0\u4e48\u547d\u4ee4\uff0c\u5feb\u8bf4\u51fa\u6765\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u7687\u4e0a \u8981\u6211\u53bb\u53d6\u4e00\u4e2a\u4eba\u7684\u9996\u7ea7\u3002\u201d\u6b63\u662f\u3002 \u3000\u3000\u7981\u82d1\u4e2d\u5bb5\u7aa5\u9690\u79d8\uff0c\u9a87\u4eba\u95fb\u542c\u53d6\u4eba\u5934\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e09\u5341\u56db\u56de\u3000\u60c6\u6005\u51b7\u5bab\u7aa5\u9690\u79d8\u3000\u51c4\u51c9\u7981\u82d1\u8bdd\u524d\u56e0 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e09\u5341\u56db\u56de\u3000\u60c6\u6005\u51b7\u5bab\u7aa5\u9690\u79d8\u3000\u51c4\u51c9\u7981\u82d1\u8bdd\u524d\u56e0 \u3000\u3000\u8c37\u4e2d\u83b2\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5982\u6b64\u6b8b\u66b4\u65e0\u9053\uff0c\u53c8\u8981\u6740\u4eba\uff01\u4ed6\u8981\u4f60\u6740\u4ec0\u4e48\u4eba\uff1f\u5feb\u8bf4\uff01\u201d \u90a3\u4eba\u55eb\u55eb\u5685\u5685\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u8981\u6211\u6740\u4eba\uff0c\u53ea\u662f\u8981\u6211\u53d6\u56de\u4e00\u4e2a\u4eba\u7684\u9996\u7ea7\u3002\u201d\u8c37\u4e2d\u83b2\u5fc3\u4e2d\u4e00\u52a8\uff0c \u95ee\u9053\uff1a\u201c\u54e6\uff0c\u6211\u77e5\u9053\u4e86\uff0c\u662f\u4e0d\u662f\u592b\u5728\u51b7\u5bab\u4e2d\u7684\u90a3\u4e2a\u5973\u4eba\uff1f\u4ed6\u5df2\u7ecf\u6d3e\u51fa\u4e24\u4e2a\u5bab\u5973\u53bb\u8feb\u5979\u81ea \u5c3d\u4e86\uff0c\u60f3\u662f\u8fd8\u4e0b\u653e\u5fc3\uff0c\u6240\u4ee5\u518d\u52a0\u6d3e\u4f60\u5427\uff1f\u8981\u662f\u90a3\u4e2a\u5973\u5b50\u4e0d\u80af\u81ea\u6740\uff0c\u90a3\u5c31\u662f\u52b3\u70e6\u4f60\u7684\u8d35\u624b \u4e86\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u90a3\u4eba\u7741\u5927\u4e86\u773c\u775b\uff0c\u8be7\u5f02\u4e4b\u6781\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4ec0\u4e48\u90fd\u77e5\u9053\u4e86\uff0c\u90a3\u6211\u4e5f\u4e0d\u5fc5\u7792\u4f60\uff0c\u6b63\u662f\u8fd9 \u6837\u3002\u7687\u4e0a\u6015\u90a3\u4e24\u4e2a\u5bab\u5973\u5fc3\u8f6f\uff0c\u4e0d\u6562\u6740\u4eba\uff0c\u6240\u4ee5\u8981\u6211\u4e5f\u53bb\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u90a3\u5973\u4eba\u7a76\u7adf\u662f \u4ec0\u4e48\u4eba\uff1f\u201d\u90a3\u4eba\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e2a\u6211\u53ef\u5c31\u59d4\u5b9e\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u7591\u4e91\u5927\u8d77\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5978\u738b\u63a5\u8fde\u6d3e\u51fa\u4e86\u4e24\u62e8\u4eba\u8981\u53d6\u90a3\u5973\u4eba\u7684\u6027\u547d\uff0c\u60f3\u6765\u90a3 \u5973\u4eba\u7684\u6765\u5386\u5b9a\u4e0d\u5bfb\u5e38\uff0c\u6216\u8005\u53ef\u4ee5\u4ece\u5979\u7684\u8eab\u4e0a\u63a2\u542c\u51fa\u4e00\u4e9b\u6d88\u606f\u3002\u201d\u5f53\u4e0b\u95ee\u9053\uff0c\u201d\u51b7\u5bab\u5728\u54ea \u513f\uff1f\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u5728\u7ea2\u83b2\u5c0f\u7b51\u4e4b\u897f\uff0c\u5c31\u662f\u539f\u6765\u6c34\u6708\u5eb5\u7684\u5730\u65b9\uff0c\u4ece\u8fd9\u91cc\u518d\u5411\u897f\u8d70\u2026\u2026\u201d\u6b63\u60f3 \u8be6\u7ec6\u8bf4\u660e\u8def\u7ebf\uff0c\u8c37\u4e2d\u83b2\u5df2\u5207\u65ad\u4ed6\u7684\u8bdd\u9053\uff1a \u3000\u3000\u201c\u6211\u77e5\u9053\u5566\uff0c\u597d\uff0c\u4f60\u5728\u8fd9\u91cc\u8eba\u4e00\u4f1a\u513f\uff0e\u8fc7\u4e86\u4e24\u4e2a\u65f6\u8fb0\uff0c\u7a74\u9053\u81ea\u89e3\u3002\u201d\u7687\u5bab\u7684\u56fe\u5f62\u5df2 \u6df1\u5370\u5979\u7684\u8111\u4e2d\uff0c\u53ea\u662f\u5979\u4e0d\u77e5\u9053\u51b7\u5bab\u5c31\u662f\u6c34\u6708\u5eb5\u800c\u5df2\uff0c\u90a3\u4eba\u5df2\u7136\u8bf4\u51fa\u4e86\u6c34\u6708\u5eb5\u8fd9\u4e2a\u5730\u65b9\uff0c \u8c37\u4e2d\u83b2\u5f53\u7136\u662f\u4e0d\u5fc5\u4ed6\u518d\u8be6\u52a0\u89e3\u8bf4\u4e86\u3002\u5f53\u4e0b\u4e00\u4f38\u624b\u5c31\u70b9\u4e86\u90a3\u4eba\u7684\u660f\u7761\u7a74\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u65bd\u5c55\u51fa\u201c\u516b\u6b65\u8d76\u8749\u201d\u7684\u529f\u592b\uff0c\u4e0d\u6d88\u4e00\u4f1a\uff0c\u6b63\u597d\u5728\u6c34\u6708\u5eb5\u524d\uff0c\u8ffd\u4e0a\u4e86\u90a3\u4e24\u4e2a\u5bab \u5973\uff0c\u6c34\u6708\u5eb5\u524d\u662f\u4e00\u4e1b\u7af9\u6797\uff01\u8c37\u4e2d\u83b2\u4ee5\u4e0a\u4e58\u8f7b\u529f\uff0c\u98de\u8eab\u800c\u8d77\uff0c\u4ece\u7af9\u68a2\u4e0a\u9762\u8e0f\u8fc7\uff0c\u90a3\u4e24\u4e2a\u5bab \u5973\u4e00\u70b9\u4e5f\u6ca1\u53d1\u89c9\uff0c\u8c37\u4e2d\u83b2\u6bd4\u5979\u4eec\u5148\u8fdb\u4e86\u51b7\u5bab\u3002 \u3000\u3000\u4ece\u5c4b\u9876\u4e0a\u671b\u4e0b\uff0c\u53ea\u89c1\u5728\u4e00\u4e2a\u5c0f\u9662\u843d\u91cc\uff0c\u6709\u4e24\u4e2a\u6b66\u58eb\u76f8\u5bf9\u800c\u7acb\uff0c\u4e00\u770b\u5c31\u77e5\u9053\u662f\u5949\u547d\u5728 \u8fd9\u91cc\u628a\u5b88\u7684\u3002\u8fd9\u51b7\u5bab\u6709\u597d\u51e0\u95f4\u623f\u5b50\uff0c\u4f46\u5176\u4ed6\u7684\u623f\u5b50\u90fd\u6ca1\u4eba\u628a\u5b88\uff0c\u8c37\u4e2d\u83b2\u867d\u7136\u7f3a\u4e4f\u7ecf\u9a8c\uff0c \u4ea6\u53ef\u4ee5\u60f3\u5f97\u8fd9\u95f4\u623f\u5b50\u5b9a\u662f\u5173\u7740\u91cd\u8981\u4eba\u72af\uff0c\u5341\u4e5d\u5c31\u662f\u56fd\u738b\u6240\u8981\u6740\u7684\u90a3\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u6084\u65e0\u58f0\u606f\u7684\u7ed5\u5230\u4e86\u90a3\u95f4\u623f\u5b50\u7684\u540e\u9762\uff0c\u7528\u4e86\u4e00\u4e2a\u5012\u5377\u73e0\u5e18\u7684\u59ff\u52bf\uff0c\u53cc\u8db3\u52fe\u7740\u5e18 \u89d2\uff0c\u8eab\u8eaf\u5012\u6302\uff0c\u7528\u53e3\u6c34\u8f7b\u8f7b\u5f04\u635f\u4e86\u4e00\u70b9\u7a97\u7eb8\uff0c\u63a2\u5934\u5185\u671b\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u5c4b\u5185\u7684\u5973\u4eba\u5e7d\u5e7d\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u73e0\u7a46\u3001\u6717\u739b\uff0c\u73e0\u7a46\u3001\u6717\u739b\uff0c\u5509\uff0c \u6211\u8fd9\u4e24\u4e2a\u53ef\u601c\u7684\u5b69\u5b50\uff0c\u73b0\u5728\u4e0d\u77e5\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u6211\u65e5\u76fc\u591c\u76fc\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u4eec\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u56de \u6765\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5fc3\u5934\u5927\u9707\uff0c\u201c\u96be\u9053\u8fd9\u4e2a\u5973\u4eba\u7adf\u662f\u6211\u7684\u4eb2\u5a18\uff1f\u201d\u5b9a\u4e86\u5b9a\u795e\uff0c\u7741\u5927\u4e86\u773c\u775b\u770b\u4ed4\u7ec6\uff0c \u53ea\u89c1\u90a3\u5973\u4eba\u7ea6\u83ab\u56db\u5341\u591a\u5c81\u5e74\u7eaa\uff0c\u60f3\u662f\u56e0\u4e3a\u957f\u5904\u51b7\u5b98\uff0c\u4e0d\u89c1\u9633\u5149\uff0c\u8138\u8272\u975e\u5e38\u82cd\u767d\uff0c\u4f46\u4ece\u5979 \u90a3\u6194\u60b4\u67af\u69c1\u7684\u989c\u5bb9\uff0c\u8fd8\u9690\u7ea6\u53ef\u4ee5\u770b\u5f97\u51fa\u81ea\u5df1\u7684\u5f71\u5b50\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u4ece\u90a3\u5973\u4eba\u7684\u8eab\u4e0a\u9690\u7ea6\u770b\u5230\u81ea\u5df1\u7684\u5f71\u5b50\uff0c\u90a3\u5973\u4eba\u5219\u4ece\u68b3\u5986\u5408\u4e0a\u7684\u660e\u955c\u4e2d\u6e05\u695a\u7684 \u770b\u5230\u81ea\u5df1\u7684\u5f71\u5b50\u3002\u5979\u7167\u4e86\u4e00\u4e0b\u955c\u5b50\uff0c\u62d4\u4e0b\u4e86\u4e24\u6761\u767d\u53d1\uff0c\u53c8\u957f\u957f\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a \u201c\u6211\u90fd\u4e0d\u8ba4\u5f97\u6211\u81ea\u5df1\u4e86\uff0c\u90a3\u4e24\u4e2a\u5b69\u5b50\u66f4\u4e0d\u4f1a\u8ba4\u5f97\u6211\u4e86\u3002\u54ce\uff0c\u4f46\u613f\u771f\u795e\u4fdd\u4f51\uff0c\u8fd9\u4e24\u4e2a\u5b69\u5b50\uff0c \u65e0\u707e\u65e0\u96be\uff0c\u957f\u5927\u6210\u4eba\uff01\u201d \u3000\u3000\u5979\u82cd\u767d\u7684\u9762\u4e0a\u51fa\u73b0\u4e00\u4e1d\u7b11\u5bb9\uff0c\u6570\u6570\u624b\u6307\uff0c\u53c8\u81ea\u8a00\u542f\u8bed\u5730\u9053\uff1a \u3000\u3000\u201c\u4ed6\u4eec\u4eca\u5e74\u5e94\u8be5\u662f\u5341\u516b\u5c81\u4e86\uff0c\u5df2\u7ecf\u662f\u6210\u5e74\u4eba\u5566\u3002\u201d\u8c37\u4e2d\u83b2\u542c\u5230\u8fd9\u91cc\uff0c\u5fc3\u5934\u66f4\u4e3a\u9707\u52a8\uff0c \u8fd9\u5973\u4eba\u6240\u8bf4\u7684\u4e24\u4e2a\u5b69\u5b50\uff0c\u540c\u662f\u5341\u516b\u5c81\u5e74\u9f84\uff0c\u540d\u5b57\u53c8\u6b63\u597d\u4e00\u4e2a\u53eb\u505a\u201c\u73e0\u7a46\u201d\uff0c\u4e00\u4e2a\u53eb\u505a \u201c\u6717\u739b\u201d\uff0c\u90a3\u8fd8\u4e0d\u662f\u6211\u4eec\u5144\u59b9\u662f\u8c01\uff1f \u3000\u3000\u5c4b\u5916\u9762\u8c37\u4e2d\u83b2\u70ed\u6cea\u76c8\u7736\uff0c\u5c4b\u5b50\u91cc\u90a3\u5973\u4eba\u4e5f\u662f\u6cea\u5982\u96e8\u4e0b\uff0c\u53ea\u542c\u5f97\u5979\u54bd\u6cea\u542b\u60b2\uff0c\u53c8\u5728\u81ea \u8a00\u81ea\u8bed\u9053\uff1a\u201c\u73e0\u7a46\u3001\u6717\u739b\u8fd9\u4e24\u4e2a\u5b69\u5b50\u5f53\u5e74\u6709\u4eba\u5e26\u8d70\uff0c\u6211\u8fd8\u6709\u4e00\u7ebf\u6307\u671b\uff0c\u7ae0\u5cf0\u8fd9\u5b69\u5b50\u66f4\u53ef \u601c\uff0c\u4e0d\u77e5\u4ed6\u662f\u6b7b\u662f\u6d3b\u3002\u5509\uff0c\u53ea\u6015\u591a\u534a\u662f\u6b7b\u4e86\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u6b63\u81ea\u5fc3\u60f3\uff1a\u201c\u7ae0\u5cf0\u53c8\u662f\u8c01\u4eba\uff1f\u96be\u9053\u6211\u8fd8\u6709\u4e00\u4e2a\u5144\u5f1f\uff1f\u201d\u5c31\u5728\u8fd9\u4e2a\u65f6\u5019\uff0c\u90a3\u4e24 \u4e2a\u5bab\u5973\u5df2\u7ecf\u8d70\u4e86\u8fdb\u6765\u3002\u5979\u4eec\u628a\u56fd\u738b\u7684\u547d\u4ee4\u7ed9\u628a\u5b88\u7684\u90a3\u4e24\u4e2a\u6b66\u58eb\u770b\u4e86\uff0c\u90a3\u4e24\u4e2a\u6b66\u58eb\u70b9\u70b9\u5934 \u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u5c4b\u5b50\u91cc\u662f\u5173\u6709\u8fd9\u6837\u7684\u4e00\u4e2a\u5973\u4eba\u3002\u201d\u968f\u5373\u53d6\u51fa\u9501\u5319\uff0c\u6253\u5f00\u4e86\u5916\u9762\u7684\u94c1\u9501\uff0c \u653e\u8fd9\u4e24\u4e2a\u5bab\u5973\u8fdb\u53bb\u3002 \u3000\u3000\u90a3\u5973\u4eba\u62ed\u4e86\u62ed\u773c\u6cea\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u662f\u8c01\uff1f\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u6211\u592b\u5728\u8fd9\u91cc\u5341\u4e94\u5e74\u4e86\uff0c\u4ece \u6ca1\u6709\u4eba\u6765\u770b\u8fc7\u6211\uff01\u201d\u90a3\u4e24\u4e2a\u5bab\u5973\u53cc\u53cc\u8dea\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\u53eb\u6211\u4eec\u9001\u4e09\u4ef6\u4e1c\u897f\u6765\u7ed9\u5a18\u5a18\u3002\u201d \u5979\u4eec\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u8fd9\u4e2a\u5973\u4eba\u7684\u6765\u5386\uff0c\u4f46\u89c1\u8fd9\u5973\u4eba\u867d\u5728\u87ba\u6cc4\u4e4b\u4e2d\uff0c\u5374\u81ea\u6709\u4e00\u79cd\u9ad8\u8d35\u7684\u6c14\u5ea6\uff0c \u4e0d\u6562\u8f7b\u6162\uff0c\u56e0\u800c\u5c06\u5979\u5524\u4f5c\u201c\u5a18\u5a18\u201d\u3002 \u3000\u3000\u90a3\u5973\u4eba\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4f60\u4eec\u7684\u5a18\u5a18\u3002\u90a3\u8d3c\u5b50\u7ed9\u6211\u4ec0\u4e48\u4e1c\u897f\uff1f\u4f60\u7ed9\u6211\u539f\u7269\u5949\u8fd8\uff0c \u8bf4\u6211\u4ec0\u4e48\u4e5f\u4e0d\u8981\u4ed6\u7684\uff0c\u522b\u5047\u4ec1\u5047\u4e49\u5566\uff01\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u5bab\u5973\u5927\u4e3a\u60ca\u6050\uff0c\u5979\u4eec\u521d\u65f6\u4ee5\u4e3a\u8fd9\u5973\u4eba\u5927\u7ea6\u662f\u4e2a\u5931\u5ba0\u7684\u5983\u5b50\uff0c\u4f46\u8fd9\u58f0\u201c\u8d3c\u5b50\u201d \u4e00\u53eb\u5979\u4eec\u7acb\u5373\u77e5\u9053\u731c\u60f3\u9519\u4e86\uff0c\u4e00\u4e2a\u5931\u5ba0\u7684\u5983\u5b50\uff0c\u65e0\u8bba\u600e\u6837\u5fc3\u6000\u6028\u6124\uff0c\u4e5f\u662f\u51b3\u6c41\u4e0d\u6562\u5c06\u56fd \u738b\u65a5\u4f5c\u201c\u8d3c\u5b50\u201d\u7684\uff0c\u770b\u6765\u8fd9\u4e2a\u5973\u4eba\u7684\u6765\u5386\u53ea\u6015\u6bd4\u5979\u4eec\u6240\u6599\u60f3\u7684\u66f4\u4e0d\u5bfb\u5e38\u3002 \u3000\u3000\u8dea\u5728\u524d\u9762\u7684\u90a3\u4e2a\u5b98\u5973\u9053\uff1a\u201c\u542f\u7980\u5a18\u5a18\uff0c\u8fd9\u4e09\u6837\u4e1c\u897f\u662f\u4e0d\u80fd\u9000\u56de\u53bb\u7684\u3002\u7687\u4e0a\u6709\u4ee4\uff0c\u5a18 \u5a18\u4e00\u5b9a\u8981\u9009\u4e00\u6837\u3002\u201d \u3000\u3000\u90a3\u5973\u4eba\u9053\uff1a\u201c\u4e0d\u80fd\u9000\u56de\u7684\uff0c\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u90a3\u5bab\u5973\u6296\u6296\u7d22\u7d22\u5730\u62ff\u51fa\u4e09\u6837\u4e1c\u897f\uff0c\u53ea\u89c1\u662f \u4e00\u6761\u7ef3\u5b50\uff0c\u4e00\u628a\u5200\u5b50\uff0c\u8fd8\u6709\u4e00\u4e2a\u7eb8\u5305\u3002\u90a3\u5bab\u5973\u9053\uff1a\u201c\u90a3\u7eb8\u5305\u91cc\u662f\u6bd2\u836f\uff0c\u7ef3\u5b50\u3001\u5200\u5b50\u3001\u6bd2 \u836f\uff0c\u8fd9\u4e09\u6837\u4e1c\u897f\uff0c\u8bf7\u5a18\u5a18\u968f\u4fbf\u9009\u62e9\u4e00\u6837\uff01\u201d \u3000\u3000\u8fd9\u5373\u662f\u8bf4\u8981\u90a3\u5973\u4eba\u5728\u773c\u6bd2\u3001\u4e0a\u540a\u3001\u81ea\u520e\u8fd9\u4e09\u6837\u6b7b\u6cd5\u4e2d\u9009\u62e9\u4e00\u79cd\uff0c\u90a3\u5973\u4eba\u5446\u4e86\u4e00\u5446\uff0c \u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u65e9\u5df2\u6599\u5230\u4f1a\u6709\u4eca\u5929\uff0c\u4ed6\u5ba2\u6211\u591a\u6d3b\u4e86\u5341\u4e94\u5e74\uff0c\u6211\u5df2\u7ecf\u89c9\u5f97\u5947\u602a\u4e86\uff01\u53ea\u662f\u4ed6 \u4e3a\u4ec0\u4e48\u65e9\u4e0d\u8981\u6211\u6b7b\uff0c\u8fdf\u4e0d\u8981\u6211\u6b7b\uff0c\u5374\u504f\u504f\u8981\u62e3\u9009\u4eca\u5929\u6765\u8981\u6211\u6b7b\uff0c\u4f60\u4eec\u53ef\u77e5\u9053\u5176\u4e2d\u7f18\u6545\u4e48\uff1f\u201d \u90a3\u4e24\u4e2a\u5bab\u5973\u9053\uff1a\u201c\u6211\u4eec\u53ea\u662f\u5949\u65e8\u800c\u884c\uff0c\u522b\u7684\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u90a3\u5973\u4eba\u6765\u56de\u7684\u8e31\u6b65\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u60f3\u5fc5\u662f\u53d1\u751f\u4e86\u4ec0\u4e48\u610f\u5916\u7684\u53d8\u5316\uff0c\u5bf9\u4ed6\u4e0d\u5229\u7684\u4e8b \u60c5\uff0c\u4ed6\u624d\u60f3\u8d77\u8981\u6740\u6211\u706d\u53e3\uff0c\u6211\u6b7b\u4e0d\u6253\u7d27\u3002 \u3000\u3000\u53ea\u662f\u6211\u513f\u5973\u8fd8\u672a\u56de\u6765\uff0c\u6211\u6b7b\u4e0d\u7791\uff01\u201d\u90a3\u4e24\u4e2a\u5bab\u5973\u7980\u9053\uff1a\u201c\u7687\u4e0a\u7b49\u7740\u590d\u547d\uff0c\u8bf7\u5a18\u5a18\u539f \u8c05\u3002\u201d \u3000\u3000\u90a3\u5973\u4eba\u9053\uff1a\u201c\u597d\uff0c\u4f60\u7ed9\u6211\u5012\u4e00\u676f\u8336\u3002\u628a\u90a3\u5305\u6bd2\u836f\u653e\u8fdb\u53bb\u3002\u201d\u4e24\u4e2a\u5bab\u5973\u4e00\u4e2a\u5012\u8336\uff0c\u4e00 \u4e2a\u653e\u6bd2\uff0c\u5979\u4eec\u89c1\u90a3\u5973\u4eba\u613f\u610f\u81ea\u5c3d\uff0c\u5982\u91ca\u91cd\u8d1f\uff0c\u4e24\u4eba\u90fd\u5401\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u90a3\u5973\u4eba\u64ce\u7740\u6bd2\u676f\uff0c\u5207\u9f7f\u9a82\u9053\uff1a\u201c\u597d\u4e2a\u72e0\u5fc3\u7684\u8d3c\u5b50\uff0c\u4f60\u6740\u4e86\u6211\u7684\u4e08\u592b\uff0c\u593a\u4e86\u738b\u4f4d\uff0c\u5bb3 \u5f97\u6211\u6bcd\u5b50\u5206\u79bb\uff0c\u8fd8\u4e0d\u5fc3\u6ee1\u610f\u8db3\uff0c\u8fd8\u8981\u5bb3\u6211\uff0c\u6211\u6b7b\u4e3a\u5389\u9b3c\uff0c\u8a93\u62a5\u6b64\u4ec7\uff01\u201d \u3000\u3000\u6bd2\u676f\u5df2\u6cbe\u5230\u5979\u7684\u5507\u8fb9\uff0c\u5ffd\u542c\u5f97\u201c\u545b\u5577\u201d\u4e00\u58f0\uff0c\u8c37\u4e2d\u83b2\u7a7f\u7a97\u800c\u5165\uff0c\u62d4\u4e0b\u5934\u4e0a\u7684\u7389\u7c2a\uff0c \u98de\u63b7\u8fc7\u53bb\uff0c\u5c06\u6bd2\u676f\u6253\u5f97\u7c89\u788e\uff0c\u5927\u58f0\u53eb\u9053\uff0c\u201c\u5a18\uff0c\u4f60\u4e0d\u80fd\u6b7b\uff0c\u4f60\u5973\u513f\u56de\u6765\u4e86\uff01\u201d\u5979\u542c\u4e86\u90a3 \u5973\u4eba\u4e34\u6b7b\u4e4b\u8a00\uff0c\u66f4\u786e\u5207\u77e5\u9053\u662f\u5979\u7684\u6bcd\u4eb2\u65e0\u7591\u4e86\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u5bab\u5973\u5927\u60ca\uff0c\u614c\u614c\u5f20\u5f20\u7684\u5fd9\u8df3\u8fdb\u6765\uff0c\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u770b\u5728\u4f60\u4eec\u7684\u5fc3\u80a0\u8fd8\u4e0d\u5927\u574f\uff0c \u9976\u4f60\u4eec\u4e0d\u6b7b\uff0c\u8eba\u4e00\u4f1a\u513f\u5427\u3002\u201d\u968f\u624b\u6307\u4e86\u4e24\u6307\uff0c\u90a3\u4e24\u4e2a\u5bab\u5973\u521a\u521a\u8df3\u8d77\uff0c\u817f\u5f2f\u4e00\u9ebb\uff0c\u767b\u65f6\u53c8 \u53cc\u53cc\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u90a3\u4e24\u4e2a\u5728\u5916\u9762\u628a\u5b88\u7684\u6b66\u58eb\u4e5f\u51b2\u4e86\u8fdb\u6765\uff0c\u60ca\u6012\u4ea4\u52a0\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u54ea\u91cc\u6765 \u7684\u5927\u80c6\u5973\u8d3c\uff0c\u4f60\u4e0d\u60f3\u6d3b\u5566\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a \u3000\u3000\u201c\u5a18\uff0c\u4f60\u8981\u4ed6\u4eec\u6d3b\u8fd8\u662f\u8981\u4ed6\u4eec\u6b7b\uff1f\u201d \u3000\u3000\u90a3\u5973\u4eba\u72b9\u5982\u8eab\u5728\u68a6\u4e2d\uff0c\u4e0d\u6562\u76f8\u4fe1\u8fd9\u662f\u771f\u4e8b\uff0c\u5446\u5446\u7684\u770b\u7740\u8c37\u4e2d\u83b2\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u8bf4\u4e0d\u51fa \u540e\u3002\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u597d\uff0c\u6211\u5148\u70b9\u4e86\u4ed6\u4eec\u7684\u7a74\u9053\uff0c\u518d\u8bf7\u6bcd\u4eb2\u5904\u7f6e\u3002\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u6b66\u58eb\u7684\u672c\u9886\u6bd4\u5bab\u5973\u5f53\u7136\u8981\u5f3a\u5f97\u591a\uff0c\u8c37\u4e2d\u83b2\u5728\u4e00\u4e08\u5f00\u5916\u7684\u8ddd\u79bb\uff0c\u7528\u9694\u7a7a\u70b9\u7a74\u7684 \u529f\u592b\u70b9\u4ed6\u7684\u7a74\u9053\uff0c\u4ed6\u4eec\u53ea\u662f\u611f\u5230\u4e00\u9635\u9178\u9ebb\uff0c\u5374\u672a\u8dcc\u5012\uff0c\u4e00\u4e2a\u6301\u5200\uff0c\u4e00\u4e2a\u62ff\u5251\uff0c\u8dc4\u8dc4\u8e09\u8e09 \u7684\u5954\u8dd1\u8fc7\u6765\uff0c\u5927\u9a82\u9053\uff1a\u201c\u5996\u5973\uff0c\u4f60\u4f7f\u4ec0\u4e48\u5996\u6cd5\u3002\u5403\u6211\u4e00\u5200\uff0c\u5403\u6211\u4e00\u5251\uff01\u201d \u3000\u3000\u90a3\u5973\u4eba\u84e6\u5730\u5927\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8981\u6740\u6740\u6211\uff0c\u522b\u5bb3\u6211\u7684\u5973\u513f\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u8fdf\uff0c\u90a3\u4e24\u4e2a \u6b66\u58eb\u5df2\u51b2\u5230\u8ddf\u524d\uff0c\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u5a18\uff0c\u4e0d\u7528\u5bb3\u6015\uff01\u201d\u8fd9\u65f6\u8ddd\u79bb\u5df2\u8fd1\uff0c\u5979\u53c8\u52a0\u4e86\u51e0\u6210\u529f\u529b\uff0c \u6307\u4e86\u4e24\u6307\uff1a\u90a3\u4e24\u4e2a\u6b66\u58eb\u54ea\u8fd8\u7981\u5f97\u8d77\uff1f\u767b\u65f6\u4e5f\u90fd\u5012\u4e86\uff01 \u3000\u3000\u90a3\u5973\u4eba\u89c1\u8c37\u4e2d\u83b2\u672c\u9886\u5982\u6b64\u9ad8\u5f3a\uff0c\u4e0d\u7981\u53c8\u60ca\u53c8\u559c\uff0c\u53c8\u4e0d\u6562\u76f8\u4fe1\u3002\u8c37\u4e2d\u83b2\u70b9\u4e86\u90a3\u4e24\u4e2a\u6b66 \u58eb\u7684\u6655\u7761\u7a74\uff0c\u5fcd\u4e0d\u7740\u5c31\u5f20\u5f00\u53cc\u81c2\uff0c\u5954\u5411\u5979\u7684\u6bcd\u4eb2\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5a18\uff0c\u5973\u513f\u56de\u6765\u5566\uff01\u201d \u3000\u3000\u90a3\u5973\u4eba\u5b9a\u4e86\u5b9a\u795e\uff0c\u601d\u601d\u7591\u7591\u5730\u95ee\u9053\uff1a\u201c\u4f60\u5f53\u771f\u662f\u6211\u7684\u6717\u739b\uff1f\u201d\u8c37\u4e2d\u83b2\u638f\u51fa\u4e86\u7f8a\u76ae\u4e66\uff0c \u8bf4\u9053\uff1a\u201c\u5a18\uff0c\u4f60\u770b\u8fd9\u4e2a\u3002\u201d\u90a3\u5973\u4eba\u8fd9\u624d\u76f8\u4fe1\u8c37\u4e2d\u83b2\u5c31\u662f\u5979\u7684\u5973\u513f\uff0c\u4e24\u6bcd\u5973\u7d27\u7d27\u76f8\u62b1\uff0c\u6cea \u4e0b\u5982\u96e8\u3002 \u3000\u3000\u8fc7\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u90a3\u5973\u4eba\u624d\u6536\u4e86\u773c\u6cea\uff0c\u8f7b\u8f7b\u629a\u6478\u8c37\u4e2d\u83b2\u7684\u5934\u53d1\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u65e5\u76fc\u591c\u76fc\uff0c \u603b\u7b97\u628a\u4f60\u76fc\u6765\u4e86\u3002\u5b69\u5b50\uff0c\u4eba\u4eca\u4e4b\u540e\uff0c\u6211\u662f\u4e0d\u80af\u518d\u8ba9\u4f60\u79bb\u5f00\u6211\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5a18\uff0c\u4f60\u653e\u5fc3\uff0c\u6211\u7247\u523b\u4e5f\u4e0d\u4f1a\u79bb\u5f00\u4f60\uff0c\u7edd\u4e0d\u5141\u8bb8\u597d\u4eba\u5bb3\u4f60\u3002\u201d\u5979\u672c\u6765\u662f \u8981\u53bb\u523a\u6740\u56fd\u738b\u7684\uff0c\u4f46\u5982\u4eca\u6bcd\u5973\u76f8\u9022\uff0c\u4fdd\u62a4\u6bcd\u4eb2\u6bd4\u4ec0\u4e48\u90fd\u91cd\u8981\uff0c\u8c37\u4e2d\u83b2\u53ea\u597d\u628a\u62a5\u4ec7\u4e4b\u4e8b\u6682 \u6401\u4e0b\u6765\uff0c\u966a\u4f34\u6bcd\u4eb2\uff0c\u5979\u5fc3\u4e2d\u6fc0\u52a8\u4e4b\u6781\uff0c\u4e07\u8bed\u5343\u8a00\uff0c\u4e0d\u77e5\u4ece\u54ea\u513f\u8bf4\u8d77\u3002 \u3000\u3000\u90a3\u5973\u4eba\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u4eba\u662f\u6b7b\u4e86\u5417\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4e0d\u9000\uff0c\u4ed6\u4eec\u90fd\u662f\u7ed9\u5973\u513f\u70b9\u4e86\u7a74\u9053\uff0c \u6682\u65f6\u5931\u6389\u77e5\u89c9\u7684\u3002\u201d\u90a3\u5973\u4eba\u9053\uff1a\u201c\u6211\u770b\u770b\u5bb3\u6015\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5bf9\uff0c\u54b1\u4eec\u6bcd\u5973\u76f8\u805a\uff0c\u4e0d\u80fd\u5bb9\u8bb8\u8fd9\u4e9b\u574f\u4eba\u4e5f\u5728\u8fd9\u513f\uff0c\u867d\u7136\u4ed6\u4eec\u5df2\u662f\u5931\u4e86 \u77e5\u89c9\uff0c\u4ec0\u4e48\u90fd\u542c\u4e0d\u89c1\u3002\u201d\u4e8e\u662f\u5c06\u90a3\u4e24\u4e2a\u6b66\u58eb\u548c\u90a3\u4e24\u4e2a\u5bab\u5973\u90fd\u62d6\u51fa\u53bb\uff0c\u56de\u6765\u95ee\u9053\uff1a\u201c\u8fd9\u51b7 \u5bab\u91cc\u8fd8\u6709\u4ec0\u4e48\u574f\u4eba\u5417\uff1f\u201d\u90a3\u5973\u4eba\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4f46\u6211\u77e5\u9053\u8fd9\u91cc\u53ea\u662f\u5173\u7981\u6211\u4e00\u4e2a\u4eba\uff0c\u9664 \u4e86\u770b\u5b88\u6211\u7684\u6b66\u58eb\u4e4b\u5916\uff0c\u5927\u7ea6\u4e0d\u4f1a\u518d\u6709\u5176\u4ed6\u4eba\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5c06\u90a3\u4e09\u6837\u4e1c\u897f\uff0c\u5200\u5b50\u3001\u7ef3\u5b50\u3001\u7eb8\u5305\u7684\u6bd2\u836f\u5168\u629b\u51fa\u53bb\u6253\u5f00\u7a97\u6237\uff0c\u8ba9\u4e00\u80a1\u65b0\u9c9c\u7684 \u7a7a\u6c14\u900f\u8fdb\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5a18\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u4f60\u518d\u4e5f\u4e0d\u7528\u5bb3\u6015\u5566\uff01\u201d\u90a3\u5973\u4eba\u6ee1\u662f\u6cea\u75d5\u7684\u8138\u4e0a\u7efd \u51fa\u4e86\u7b11\u5bb9\uff0c\u8fd9\u662f\u5341\u4e94\u5e74\u6765\u5979\u7b2c\u4e00\u6b21\u5c55\u5f00\u7b11\u8138\u3002 \u3000\u3000\u90a3\u5973\u4eba\u9053\uff0c\u201c\u4f60\u8fd8\u6709\u4e00\u4e2a\u631b\u751f\u5144\u5f1f\uff0c\u4ed6\u2014\u2014\u201d\u8c37\u4e2d\u83b2\u9053\uff1a \u3000\u3000\u201c\u597d\u6559\u6bcd\u4eb2\u6b22\u559c\uff0c\u54e5\u54e5\u4e5f\u56de\u6765\u5566\uff01\u201d\u90a3\u5973\u4eba\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4ed6\u5728\u54ea\u513f\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u548c\u4f60 \u540c\u6765\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54e5\u54e5\u662f\u548c\u6211\u4e00\u540c\u6765\u7684\uff0c\u6211\u4eec\u8981\u523a\u6740\u5978\u738b\u4e3a\u4f60\u62a5\u4ec7\uff0c\u54e5\u54e5\u548c\u6211\u5206\u5934 \u641c\u67e5\u90a3\u5978\u738b\u7684\u6240\u5728\u3002\u201d \u3000\u3000\u90a3\u5973\u4eba\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u4f60\u4eec\u8981\u523a\u6740\u5978\u738b\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5a18\uff0c\u4f60\u4e0d\u7528\u60ca\u614c\uff0c\u54e5\u54e5\u7684 \u672c\u4e8b\u6bd4\u6211\u66f4\u5927\u3002\u6211\u4eec\u8fd8\u6709\u4e00\u4f4d\u670b\u53cb\u5e2e\u5fd9\uff0c\u8fd9\u4f4d\u670b\u53cb\u7684\u672c\u4e8b\u66f4\u4e86\u4e0d\u8d77\uff0c\u5bab\u4e2d\u8fd9\u4e9b\u6b66\u58eb\uff0c\u4e00 \u5343\u4e2a\u4e00\u4e07\u4e2a\u4e5f\u6253\u4e0d\u8fc7\u4ed6\uff01\u201d \u3000\u3000\u90a3\u5973\u4eba\u89c1\u8fc7\u5973\u513f\u7684\u672c\u4e8b\uff0c\u6ee1\u6000\u6b22\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u7ec3\u6210\u4e86\u672c\u9886\uff0c\u8fd9\u5c31\u597d\u5566\u3002\u54b1\u4eec \u5df2\u7ecf\u53d7\u5341\u4e94\u5e74\u7684\u82e6\u96be\uff0c\u4e5f\u5e94\u8be5\u662f\u62a5\u4ec7\u7684\u65f6\u5019\u4e86\u3002\u5509\uff0c\u5c31\u4e0d\u77e5\u7ae0\u5cf0\u8fd9\u5b69\u5b50\u662f\u4e0d\u662f\u8fd8\u5728\u4eba\u95f4\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8c01\u662f\u7ae0\u5cf0\uff1f\u662f\u4e0d\u662f\u6211\u4eec\u8fd8\u6709\u4e00\u4f4d\u5144\u5f1f\uff1f\u201d\u90a3\u5973\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7ae0\u5cf0\u5c31 \u662f\u4f60\u4eec\u7684\u54e5\u54e5\u3002\u4ed6\u7684\u547d\u53ea\u6015\u6bd4\u4f60\u4eec\u66f4\u82e6\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5988\uff0c\u8fd9\u4f4d\u5927\u54e5\u662f\u600e\u6837\u5931\u843d\uff1f\u662f\u4e0d\u662f\u5927\u4e71\u90a3\u5e74\uff0c\u4e5f\u6709\u4eba\u4fdd\u62a4\u4ed6\u9003\u8d70\u4e86\u5462\uff1f \u4e3a\u4ec0\u4e48\u7236\u738b\u5728\u7f8a\u76ae\u4e66\u4e2d\u6ca1\u6709\u63d0\u53ca\uff1f\u201d\u90a3\u5973\u4eba\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u5927\u54e5\u5728\u4e00\u51fa\u751f\u7684\u90a3\u4e00\u5929\uff0c\u5c31\u7ed9 \u574f\u4eba\u62a2\u53bb\u4e86\u3002\u7687\u4e0a\u4e5f\u4e0d\u77e5\u9053\u4ed6\u662f\u6b7b\u662f\u6d3b\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5927\u4e3a\u5947\u602a\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u7239\u662f\u4e00\u56fd\u4e4b\u738b\uff0c\u4e3a\u4f55\u4e0d\u80fd\u5e87\u62a4\u4ed6\u7684\u513f\u5b50\uff1f\u201d \u3000\u3000\u90a3\u5973\u4eba\u9053\uff1a\u201c\u4f60\u7239\u7239\u662f\u56fd\u738b\uff0c\u4f46\u6211\u5374\u4e0d\u662f\u7687\u540e\u3002\u739b\u513f\uff0c\u4f60\u8fd8\u8bb0\u5f97\u4f60\u5c0f\u65f6\u5019\u662f\u4f4f\u5728\u4ec0 \u4e48\u5730\u65b9\u5417\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u8bb0\u5f97\u6211\u5c0f\u65f6\u5019\u662f\u4f4f\u5728\u5e10\u5e55\u91cc\uff0c\u5f88\u5927\u5f88\u5927\u7684\u5e10\u5e55\uff0c\u91cc\u9762\u6709\u8bb8\u591a\u623f\u5b50\uff0c \u5e10\u5e55\u5916\u6709\u5f88\u5927\u7684\u8349\u5730\u3002\u6211\u5f88\u5947\u602a\u2014\u2014\u201d\u90a3\u5973\u4eba\u9053\uff1a\u201c\u4f60\u4ec0\u4e48\u65f6\u5019\u624d\u77e5\u9053\u81ea\u5df1\u662f\u56fd\u738b\u7684\u5973 \u513f\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8fd8\u672a\u5230\u534a\u5e74\uff0c\u6211\u662f\u5230\u4e86\u9a6c\u8428\u513f\u56fd\u3002\u624d\u770b\u5f97\u61c2\u90a3\u4e9b\u6587\u5b57\u7684\u3002\u201d\u90a3\u5973\u4eba\u9053\uff1a \u201c\u4f60\u660e\u767d\u4e86\u8eab\u4e16\u4e4b\u540e\uff0c\u662f\u4e0d\u662f\u5f88\u89c9\u5947\u602a\u4e3a\u4ec0\u4e48\u4f60\u5c0f\u65f6\u5019\u4e0d\u4f4f\u5728\u738b\u5bab\u5374\u4f4f\u5728\u5e10\u5e55\uff1f\u201d\u8c37\u4e2d \u83b2\u9053\uff1a\u201c\u662f\u5440\uff0c\u8fd8\u6709\u8bb8\u591a\u5947\u602a\u7684\u5730\u65b9\uff0c\u7236\u738b\u4ece\u6765\u6ca1\u6709\u6765\u770b\u8fc7\u6211\uff0c\u5988\uff0c\u4f60\u4e5f\u53ea\u662f\u6765\u770b\u8fc7\u6211 \u4e00\u6b21\u3002\u201d \u3000\u3000\u90a3\u5973\u4eba\u4e0d\u7981\u53c8\u6389\u4e0b\u6cea\u73e0\uff0c\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u96be\u4e3a\u4f60\u8fd8\u8bb0\u5f97\uff0c\u90a3\u65f6\u4f60\u53ea\u6709\u4e09\u5c81\uff0c\u6211\u662f\u5192 \u9669\u6765\u770b\u4f60\u7684\u3002\u540e\u6765\u6709\u4eba\u544a\u8bc9\u4f60\uff0c\u8bf4\u4f60\u7684\u4eb2\u5a18\u5df2\u7ecf\u6b7b\u4e86\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u662f\u540e\u6765\u5e26\u6211\u9003\u96be\u7684\u90a3\u4f4d\u4e18\u7237\u7237\u544a\u8bc9\u6211\u7684\u3002\u8fd9\u4f4d\u4e18\u7237\u7237\u5bf9\u6211\u975e \u5e38\u597d\u3002\u6211\u76f8\u4fe1\u4ed6\u7684\u8bdd\u3002\u6211\u6700\u521d\u5728\u8fd9\u5c4b\u5b50\u5916\u9762\uff0c\u542c\u5230\u4f60\u53eb\u6211\u7684\u540d\u5b57\uff0c\u6211\u8fd8\u4e0d\u6562\u76f8\u4fe1\u4f60\u5c31\u662f \u6211\u7684\u6bcd\u4eb2\u3002\u540e\u6765\u8d8a\u542c\u8d8a\u6e05\u695a\u4e86\uff0c\u6211\u624d\u6562\u8fdb\u6765\u8ba4\u4f60\u3002\u5988\uff0c\u8fd9\u4f4d\u4e18\u7237\u7237\u5bf9\u6211\u975e\u5e38\u597d\uff0c\u5374\u53c8\u529b \u4f55\u8981\u54c4\u9a97\u6211\u5462\uff1f\u201d \u3000\u3000\u90a3\u5973\u4eba\u9053\uff1a\u201c\u739b\u513f\uff0c\u4f60\u7684\u8eab\u4e16\u4f60\u53ea\u662f\u660e\u767d\u4e86\u4e00\u534a\uff0c\u602a\u4e0d\u5f97\u4f60\u5fc3\u4e2d\u6ee1\u662f\u7591\u56e2\u3002\u8fd9\u4e9b\u4f24 \u5fc3\u7684\u4e8b\u513f\u6211\u672c\u4e0d\u613f\u518d\u63d0\uff0c\u4f46\u4eca\u665a\u54b1\u4eec\u6bcd\u5973\u91cd\u9022\uff0c\u6211\u662f\u4e0d\u80fd\u4e0d\u5bf9\u4f60\u8bf4\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u638f\u51fa\u624b\u5e15\uff0c\u66ff\u5979\u6bcd\u4eb2\u63e9\u4e86\u773c\u6cea\uff0c\u53ea\u542c\u788d\u5979\u6bcd\u4eb2\u7528\u6c89\u91cd\u7684\u8bed\u8c03\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u6211\u4e0d\u662f\u7687\u540e\uff0c\u6211\u662f\u4f60\u7236\u4eb2\u4e00\u4e2a\u6ca1\u6709\u540d\u4efd\u7684\u5983\u5b50\u3002\u7687\u540e\u662f\u4e2a\u5f88\u6709\u6743\u52bf\u7684\u5927\u81e3\u5973\u513f\uff0c\u6027\u60c5 \u975e\u5e38\u5992\u5fcc\uff0c\u4e0d\u8bb8\u7687\u4e0a\u548c\u4efb\u4f3a\u5983\u5ad4\u5bab\u5973\u4eb2\u8fd1\uff0c\u53ef\u60dc\u5979\u809a\u76ae\u4e0d\u4e89\u6c14\uff0c\u6ca1\u751f\u8fc7\u4e00\u7537\u534a\u5973\uff0c\u7687\u4e0a \u5e74\u8fc7\u534a\u767e\uff0c\u5c1a\u65e0\u63a5\u7eed\u5927\u7edf\u7684\u55e3\u541b\uff0c\u7687\u4e0a\u4e3a\u6b64\u70e6\u607c\uff0c\u6709\u4e00\u73ed\u5fe0\u5fc3\u7684\u81e3\u5b50\u4e5f\u5f88\u62c5\u5fe7\u3002 \u3000\u3000\u201c\u5176\u4e2d\u6709\u4e2a\u8001\u81e3\u66ff\u56fd\u738b\u60f3\u4e86\u4e00\u4e2a\u529e\u6cd5\uff0c\u4ed6\u628a\u4ed6\u7684\u5973\u513f\u5077\u5077\u9001\u8fdb\u5bab\u6765\uff0c\u53eb\u56fd\u738b\u7528\u91cd\u91d1 \u8d3f\u8d42\u5de6\u53f3\uff0c\u4e0d\u8ba9\u7687\u540e\u77e5\u9053\u3002\u4ed6\u662f\u8981\u4ed6\u7684\u5973\u513f\u66ff\u56fd\u738b\u751f\u4e0b\u55e3\u541b\u3002\u8fd9\u662f\u975e\u5e38\u5371\u9669\u7684\u529e\u6cd5\uff0c\u5018 \u7136\u6cc4\u6f0f\u98ce\u58f0\uff0c\u7687\u540e\u8bf4\u4e0d\u5b9a\u5c31\u8981\u628a\u4ed6\u7684\u5973\u513f\u6740\u4e86\uff0c\u751a\u81f3\u8fd8\u8981\u7f6a\u53ca\u5979\u7684\u5bb6\u4eba\u3002\u90a3\u8001\u81e3\u4e3a\u4e86\u5c3d \u5fe0\uff0c\u4ed6\u5973\u513f\u4e3a\u4e86\u5c3d\u5b5d\uff0c\u4e5f\u81ea\u5fc3\u7518\u60c5\u613f\uff0c\u4e0d\u987e\u5371\u9669\uff0c\u4ece\u7236\u4e4b\u547d\uff0c\u5165\u5bab\u4f8d\u5949\u56fd\u738b\uff0c\u90a3\u4e2a\u5973\u513f \u5c31\u662f\u6211\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5988\uff0c\u771f\u662f\u59d4\u5c48\u4f60\u4e86\uff01\u201d\u90a3\u5973\u4eba\u9053\uff1a\u201c\u6211\u5012\u4e0d\u89c9\u5f97\u600e\u4e48\u59d4\u5c48\uff0c\u4f60\u7239\u7239\u9887 \u80fd\u5173\u5fc3\u767e\u59d3\u75be\u82e6\uff0c\u7b97\u5f97\u662f\u4e2a\u597d\u7687\u5e1d\uff0c\u4ed6\u4e5f\u9887\u60f3\u6709\u4e9b\u4f5c\u4e3a\uff0c\u628a\u8fd9\u5c0f\u5c0f\u5c71\u56fd\u6cbb\u7406\u5f97\u66f4\u597d\uff0c\u4ed6 \u4e0e\u90bb\u90a6\u4fee\u7766\uff0c\u5728\u56fd\u5185\u5174\u529e\u5b66\u5802\u3001\u8fd8\u8bf7\u4e86\u597d\u4e9b\u6c49\u4eba\u6765\u5f53\u6559\u4e60\u3002\u53ef\u60dc\u4ed6\u53d7\u5236\u4e8e\u6743\u81e3\u608d\u5c06\uff0c\u7687 \u65cf\u540e\u515a\u4e5f\u591a\u662f\u4e0d\u8d5e\u52a9\u4ed6\u7684\uff0c\u4ed6\u540d\u4e49\u662f\u4e2a\u7687\u4e0a\uff0c\u5176\u5b9e\u5374\u662f\u5bc2\u5bde\u53ef\u601c\u3001\u5b64\u7acb\u65e0\u63f4\u7684\u4eba\uff0c\u867d\u6709 \u51e0\u4e2a\u5fc3\u8179\u8001\u81e3\uff0c\u5728\u671d\u5ef7\u5374\u6ca1\u4ec0\u4e48\u52bf\u529b\u3002\u6211\u8d77\u521d\u672c\u662f\u987a\u4ece\u7236\u610f\uff0c\u62b1\u7740\u727a\u7272\u81ea\u5df1\u7684\u51b3\u5fc3\u7684\u3002 \u65e5\u5b50\u4e00\u957f\uff0c\u6211\u53d1\u73b0\u4f60\u7239\u7239\u662f\u771f\u5fc3\u5b9e\u610f\u7684\u7231\u4e0a\u4e86\u6211\uff0c\u6211\u4e5f\u6e10\u6e10\u6b22\u559c\u4ed6\u5566\u3002\u201d \u3000\u3000\u90a3\u5973\u4eba\u5e7d\u5e7d\u53f9\u4e86\u53e3\u6c14\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\u597d\u666f\u4e0d\u957f\uff0c\u4e00\u5e74\u4e4b\u540e\uff0c\u6211\u6000\u4e86\u5b55\uff0c\u751f\u4e0b\u4e86 \u4f60\u7684\u5927\u54e5\uff0c\u4f60\u7239\u7239\u9884\u5148\u7ed9\u4ed6\u53d6\u540d\u7ae0\u5cf0\uff0c\u8fd9\u662f\u54b1\u4eec\u56fd\u4e2d\u7b2c\u4e00\u5ea7\u9ad8\u5cf0\u2014\u2014\u7ae0\u7acb\u8d21\u5cf0\u7684\u7b80\u79f0\uff0c \u4f60\u7239\u7239\u5e0c\u671b\u8fd9\u5b69\u5e72\u5c06\u6765\u4f3c\u7ae0\u7acb\u8d21\u5cf0\u7684\u9876\u5929\u7acb\u5730\u3002\u4f60\u7239\u7239\u6e34\u671b\u5b69\u5b50\uff0c\u5982\u4eca\u6211\u7ed9\u4ed6\u751f\u4e86\u4e00\u4e2a \u7537\u5b69\uff0c\u8fd9\u672c\u6765\u662f\u4e2a\u5927\u559c\u4e4b\u4e8b\uff0c\u53ef\u662f\u60f3\u4e0d\u5230\u5c31\u5728\u6211\u5f97\u4e86\u4f60\u5927\u54e5\u7684\u90a3\u4e00\u5929\uff0c\u4e5f\u4e0d\u77e5\u662f\u8c01\u6cc4\u6f0f \u4e86\u6d88\u606f\uff0c\u7687\u540e\u77e5\u9053\u4e86\uff0c\u9a6c\u4e0a\u8d76\u6765\uff0c\u5979\u5e26\u4e86\u4e00\u73ed\u608d\u4ec6\uff0c\u628a\u6211\u7684\u5b69\u5b50\u62a2\u8d70\u3002\u4e0d\u7406\u6211\u8fd8\u5728\u8925\u4e2d\uff0c \u5c31\u5c06\u6211\u9010\u51fa\u5bab\u5916\uff01\u8fd9\u8fd8\u662f\u56e0\u4e3a\u4f60\u5916\u7956\u662f\u4e09\u671d\u8001\u81e3\uff0c\u5979\u6709\u6240\u987e\u5fcc\uff0c\u8981\u4e0d\u7136\u53ea\u6015\u5f53\u573a\u5c31\u628a\u6211 \u6740\u6b7b\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u6124\u7136\u9053\uff1a\u201c\u597d\u6076\u6bd2\u7684\u7687\u540e\uff01\u5979\u628a\u5927\u54e5\u62a2\u53bb\uff0c\u4ee5\u540e\u5c31\u6ca1\u6d88\u606f\u4e86\u4e48\u5435\uff1f\u201d\u90a3\u5973\u4eba \u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u5979\u662f\u8981\u81ea\u5df1\u629a\u517b\u5b69\u5b50\uff0c\u540e\u6765\u624d\u77e5\u9053\u4e0d\u662f\u3002\u5979\u771f\u662f\u5929\u4e0b\u7f55\u89c1\u7684\u5992\u5987\uff0c\u5979\u53ea\u6015 \u5b69\u4e8e\u4e0d\u662f\u5979\u4eb2\u751f\u7684\u3002\u5c06\u6765\u96be\u4fdd\u4e0d\u77e5\u9053\u81ea\u5df1\u7684\u8eab\u4e16\uff0c\u90a3\u65f6\u5c31\u4f1a\u5bf9\u5979\u4e0d\u5229\uff0c\u5979\u7adf\u4e0d\u60dc\u65a9\u65ad\u56fd \u738b\u7684\u8840\u55e3\uff0c\u610f\u56fe\u52a0\u5bb3\u6211\u7684\u513f\u5b50\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u98a4\u58f0\u9053\uff1a\u201c\u5979\u628a\u5927\u54e5\u6740\u4e86\uff1f\u201d\u90a3\u5973\u4eba\u9053\uff1a\u201c\u8c01\u77e5\u9053\u5462\uff1f \u3000\u3000\u6211\u542c\u5230\u51e0\u4e2a\u4e0d\u540c\u7684\u8bf4\u6cd5\uff0c\u6709\u7684\u8bf4\u6211\u7684\u5b69\u5b50\u5df2\u88ab\u629b\u4e0b\u5fa1\u6cb3\uff0c\u6709\u7684\u8bf4\u662f\u88ab\u629b\u5230\u5c71\u4e0a\u53bb\u5582 \u72fc\u3002\u4f46\u4e5f\u6709\u4e2a\u4e0d\u540c\u7684\u8bf4\u6cd5\uff0c\u8bf4\u662f\u5949\u547d\u5bb3\u6211\u5b69\u5b50\u90a3\u4eba\uff0c\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u5077\u5077\u5c06\u90a3\u5b69\u5b50\u9001\u4e86\u4e2a\u597d \u5fc3\u4eba\u5bb6\u3002\u4e5f\u4e0d\u77e5\u9053\u8fd9\u4e9b\u8bf4\u6cd5\u54ea\u4e2a\u662f\u771f\uff0c\u54ea\u4e2a\u662f\u5047\u3002\u201d \u3000\u3000\u8bf4\u81f3\u6b64\u5904\uff0c\u8c37\u4e2d\u83b2\u7684\u6bcd\u4eb2\u53c8\u4e0d\u7981\u54ed\u4e86\u4e00\u573a\uff0c\u54ed\u8fc7\u4e4b\u540e\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u7687\u540e\u5927\u53d1\u96cc\u5a01 \u7684\u65f6\u5019\uff0c\u4f60\u7239\u7239\u8fd8\u5728\u5916\u9762\u4e0e\u671d\u81e3\u5546\u8bae\u56fd\u4e8b\uff0c\u53ef\u601c\u4f60\u5927\u54e5\u51fa\u4e16\uff0c\u8fd8\u672a\u66fe\u89c1\u8fc7\u7236\u4eb2\u4e00\u9762\uff01\u5f85 \u4ed6\u95fb\u8baf\u8d76\u56de\u540e\u5bab\uff0c\u4e00\u5207\u90fd\u5df2\u8fdf\u4e86\uff0c\u4ed6\u7684\u5b69\u5b50\u548c\u4ed6\u5fc3\u7231\u7684\u4eba\u90fd\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u201c\u4ece\u6b64\u4ed6\u5c31\u548c\u7687\u540e\u7ffb\u4e86\u8138\uff0c\u4ed6\u987e\u5fcc\u56fd\u4e08\u7684\u52bf\u529b\uff0c\u4e0d\u6562\u5e9f\u7acb\u7687\u540e\uff0c\u4f46\u4ece\u6b64\u7ec8\u4ed6\u4e00\u751f\uff0c \u4ed6\u6ca1\u6709\u548c\u7687\u540e\u8bf4\u8fc7\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u201c\u4ed6\u60e6\u8bb0\u6211\uff0c\u4e5f\u75db\u5fc3\u5931\u4e86\u5b69\u5b50\uff0c\u4ed6\u4e0d\u987e\u7687\u540e\u7684\u5ac9\u5992\uff0c\u79c1\u81ea\u51fa\u5bab\u4e0e\u6211\u5e7d\u4f1a\uff0c\u8fd9\u6837\u5230\u4e86 \u7b2c\u4e8c\u5e74\uff0c\u624d\u53c8\u751f\u4e0b\u4e86\u4f60\u4eec\u5144\u59b9\u3002\u53ef\u662f\u4ed6\u867d\u7136\u8bf4\u4e0d\u5bb3\u6015\u7687\u540e\uff0c\u4f46\u5374\u4e0d\u80fd\u4e0d\u987e\u5fcc\u5979\u518d\u52a0\u5bb3\u6211 \u4eec\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54e6\uff0c\u6211\u660e\u767d\u4e0b\uff0c\u7236\u738b\u6015\u90a3\u6076\u6bd2\u7684\u7687\u540e\u52a0\u5bb3\u6211\u4eec\uff0c\u6240\u4ee5\u4e0d\u6562\u63a5\u6211\u4eec\u5144\u59b9 \u5230\u5b98\u91cc\u5934\u4f4f\u3002\u201d \u3000\u3000\u90a3\u5973\u4eba\u9053\uff1a\u201c\u4e0d\u4f46\u5982\u6b64\uff0c\u8fde\u6211\u4e5f\u4e0d\u80fd\u548c\u4f60\u4eec\u540c\u4f4f\u4e86\u3002\u4ed6\u7ed9\u4f60\u4eec\u6606\u59b9\u5728\u7ae0\u7acb\u8d21\u5c71\u7684\u5c71 \u8c37\u642d\u4e86\u4e00\u5ea7\u5927\u5e10\u5e55\uff0c\u7167\u987e\u4f60\u95e8\u7684\u90a3\u4e2a\u8001\u4eba\u540d\u53eb\u5e9e\u90fd\uff0c\u662f\u7687\u4e0a\u7684\u5fe0\u4ec6\uff0c\u4ed6\u624b\u4e0b\u53c8\u6709\u51e0\u4e2a\u6267 \u5f79\u7684\u4ec6\u4eba\uff0c\u6bcf\u4e00\u4e2a\u6708\u5077\u5077\u7ed9\u7687\u4e0a\u9001\u4e00\u6b21\u4fe1\uff0c\u62a5\u544a\u4f60\u4eec\u5144\u59b9\u7684\u751f\u6d3b\u60c5\u5f62\u3002\u5e78\u4e8f\u5e9e\u90fd\u662f\u4e00\u4e2a \u975e\u5e38\u8c28\u614e\u7684\u4eba\uff0c\u8fd9\u79d8\u5bc6\u4fdd\u6301\u4e86\u4e09\u5e74\uff0c\u6ca1\u6709\u7ed9\u7687\u540e\u53d1\u73b0\u3002 \u3000\u3000\u201c\u8fd9\u65f6\u56fd\u738b\u624b\u4e0b\u7684\u5927\u5c06\u76d6\u6e29\u7fbd\u7ffc\u5df2\u6210\uff0c\u56fe\u8c0b\u7be1\u4f4d\u4e4b\u5fc3\u65e5\u5207\uff0c\u4ed6\u77e5\u9053\u56fd\u738b\u592b\u59bb\u53cd\u76ee\uff0c \u53c8\u4e0e\u540e\u515a\u52fe\u7ed3\u8d77\u6765\uff0c\u5bab\u91cc\u5bab\u5916\uff0c\u90fd\u6709\u4ed6\u4eec\u7684\u8033\u76ee\uff0c\u56fd\u738b\u4e00\u4e3e\u4e00\u52a8\uff0c\u90fd\u5f97\u5c0f\u5fc3\u3002\u4ed6\u5f53\u7136\u4e0d \u6562\u79bb\u5bab\u6765\u63a2\u671b\u4f60\u4eec\u3002\u5978\u515a\u9664\u4e86\u6ce8\u610f\u56fd\u738b\u4e4b\u5916\uff0c\u53e6\u4e00\u4e2a\u76ee\u6807\u5c31\u662f\u6211\uff0c\u56e0\u6b64\u6211\u4e5f\u4e0d\u6562\u8f7b\u6613\u5230 \u4f60\u4eec\u90a3\u91cc\u53bb\u3002\u4e09\u5e74\u4e2d\u6211\u53ea\u53bb\u8fc7\u4e00\u6b21\uff0c\u90a3\u6b21\u8fd8\u662f\u4e58\u7740\u76d6\u6e29\u4e0d\u5728\u4eac\u90fd\uff0c\u534a\u591c\u91cc\u6211\u6234\u7740\u9762\u7eb1\uff0c \u5192\u9669\u53bb\u770b\u4f60\u4eec\u4e00\u6b21\u7684\u3002 \u3000\u3000\u201c\u90a3\u6b21\u8fc7\u540e\uff0c\u4e0d\u5230\u4e09\u4e2a\u6708\uff0c\u53db\u515a\u5c31\u4e3e\u4e8b\u4e86\uff0c\u76d6\u6e29\u7684\u5175\u628a\u738b\u5bab\u5305\u56f4\u8d77\u6765\uff0c\u4f60\u5916\u7956\u7236\u5e26 \u9886\u5bb6\u4e01\u51b2\u8fdb\u738b\u5bab\u60f3\u4fdd\u62a4\u56fd\u738b\u7a81\u56f4\uff0c\u6211\u4e5f\u8c41\u4e86\u6027\u547d\uff0c\u8ddf\u4f60\u5916\u7956\u7236\u51b2\u8fdb\u5bab\u91cc\u3002\u6211\u4e0e\u4f60\u7684\u7239\u7239 \u5c31\u5728\u70fd\u706b\u4e4b\u4e2d\u76f8\u4f1a\uff0c\u53ef\u601c\u90a3\u4e5f\u662f\u6700\u540e\u4e00\u6b21\u7684\u76f8\u4f1a\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u7684\u6bcd\u4eb2\u8bf4\u81f3\u6b64\u5904\uff0c\u4e0d\u7981\u53c8\u662f\u73e0\u6cea\u6eda\u6eda\u800c\u4e0b\uff0c\u54fd\u54bd\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u7687\u540e\u65e9\u5df2\u4e0e \u76d6\u6e29\u6709\u4e86\u5978\u60c5\uff0c\u5728\u8fd9\u7d27\u6025\u7684\u5173\u5934\uff0c\u5979\u7adf\u7136\u6253\u5f00\u5b98\u95e8\uff0c\u8fce\u63a5\u76d6\u6e29\u8fdb\u6765\u3002\u4f60\u7239\u7239\u7684\u5bdd\u5b98\u4e5f\u88ab \u5305\u56f4\u4e86\uff0c\u5e78\u4e8f\u4ed6\u8fd8\u6709\u4e00\u73ed\u5fc3\u8179\u6b66\u58eb\u7ed9\u4ed6\u62b5\u6321\uff0c\u4ed6\u662f\u5728\u5200\u5149\u5251\u5f71\u4e4b\u4e2d\u5199\u597d\u90a3\u4e24\u4efd\u7f8a\u76ae\u4e66\u7684\uff0c \u4ed6\u53eb\u4e24\u4e2a\u672c\u4e8b\u6700\u597d\u7684\u6b66\u58eb\u7ed9\u4f60\u4eec\u9001\u53bb\uff0c\u90a3\u4e24\u4e2a\u6b66\u58eb\u5c31\u662f\u4e18\u5ca9\u548c\u53f6\u541b\u5c71\u4e86\u3002 \u3000\u3000\u201c\u4f60\u7239\u7239\u7684\u90a3\u73ed\u6b66\u58eb\u867d\u7136\u5fe0\u52c7\uff0c\u53ef\u60dc\u4eba\u6570\u5927\u5c11\uff0c\u4ed6\u4eec\u6fc0\u6218\u4e86\u4e00\u65e5\u4e00\u591c\uff0c\u5168\u90e8\u6218\u6b7b\uff1b \u4f60\u7684\u5916\u7956\u7236\u548c\u5bb6\u4e0b\u4e5f\u5168\u90e8\u6218\u6b7b\uff1b\u4f60\u7239\u7239\u4e0d\u7518\u53d7\u8fb1\uff0c\u81ea\u520e\u800c\u4ea1\u3002\u6211\u6765\u4e0d\u53ca\u8ffd\u968f\u4ed6\uff0c\u5c31\u7ed9\u7687 \u540e\u7684\u4eba\u6349\u4f4f\u4e86\u3002 \u3000\u3000\u201c\u7687\u540e\u672c\u6765\u8981\u6740\u6211\u7684\uff0c\u4f46\u76d6\u6e29\u4e0d\u8bb8\uff0c\u4ed6\u8981\u5728\u6211\u8eab\u4e0a\u8ffd\u67e5\u51fa\u4f60\u4eec\u7684\u4e0b\u843d\uff0c\u6211\u5b81\u6b7b\u4e0d\u8bf4\uff0c \u4ed6\u53ea\u597d\u5c06\u6211\u5173\u5165\u51b7\u5bab\uff0c\u53eb\u4eba\u4e25\u5bc6\u76d1\u89c6\uff0c\u53eb\u6211\u6c42\u751f\u4e0d\u5f97\uff0c\u6c42\u6b7b\u4e0d\u80fd\u3002 \u3000\u3000\u201c\u5230\u4e86\u8fd9\u65f6\uff0c\u6211\u53cd\u800c\u5b58\u4e86\u4e00\u7ebf\u5e0c\u671b\uff0c\u4e0d\u60f3\u81ea\u5c3d\u4e86\uff0c\u6211\u77e5\u9053\u4ed6\u4e00\u5929\u4e0d\u6740\u6211\uff0c\u5c31\u662f\u4ed6\u6ca1 \u6709\u6349\u5230\u4f60\u4eec\uff0c\u6211\u6bcf\u65e5\u7977\u544a\u771f\u795e\uff0c\u8bf7\u771f\u795e\u4fdd\u4f51\u4f60\u4eec\uff0c\u6211\u65e5\u76fc\u591c\u76fc\uff0c\u76fc\u671b\u4f60\u4eec\u56de\u6765\u62a5\u4ec7\uff0c\u8fd9 \u4e00\u76fc\u5c31\u76fc\u4e86\u5341\u4e94\u5e74\uff0c\u603b\u7b97\u628a\u4f60\u4eec\u76fc\u6765\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u66ff\u6bcd\u4eb2\u62ed\u5e72\u773c\u6cea\uff1b\u8bf4\u9053\uff1a\u201c\u5a18\uff0c\u8fd9\u5341\u4e94\u5e74\u6765\u4f60\u53d7\u5c3d\u4e86\u82e6\u96be\uff0c\u597d\u5728\u73b0\u5728\u5df2\u82e6\u5c3d \u7518\u6765\uff0c\u4f60\u4e0d\u7528\u518d\u4f24\u5fc3\u4e86\uff0c\u4f60\u4e00\u5b9a\u53ef\u4ee5\u4eb2\u773c\u89c1\u5230\u4f60\u7684\u513f\u5973\u4f60\u7ed9\u62a5\u4ec7\uff01\u201d\u90a3\u5973\u4eba\u7834\u6d95\u4e3a\u7b11\uff0c \u4f46\u968f\u5373\u53c8\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5a18\uff0c\u6211\u4e0d\u8bb8\u4f60\u518d\u4f24\u5fc3\u4e86\uff0c\u4f60\u4e3a\u4ec0\u4e48\u53c8\u53f9\u6c14\u5566\uff01\u201d\u90a3\u5973\u4eba\u9053\uff1a\u201c\u6211\u89c1\u4e86 \u4f60\uff0c\u9ad8\u5174\u5f97\u5f88\uff0c\u5509\uff0c\u53ea\u662f\u4f60\u7684\u54e5\u54e5\u2014\u2014\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54e5\u54e5\u4e0d\u4e45\u4e5f\u4f1a\u89c1\u5230\u4f60\u7684\uff0c\u5a18\uff0c\u90a3 \u65f6\u5019\u4f60\u66f4\u9ad8\u5174\u5566\uff01\u201d\u90a3\u5973\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4e09\u4e2a\u513f\u5973\uff0c\u5df2\u7ecf\u5f97\u56de\u4e24\u4e2a\uff0c\u4e5f\u5e94\u8be5\u5fc3\u8db3\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u8fd9\u624d\u77e5\u9053\u6bcd\u4eb2\u521a\u624d\u6240\u60f3\u5ff5\u7684\u662f\u53e6\u4e00\u4e2a\u54e5\u54e5\u3002\u5979\u542c\u8bf4\u90a3\u4e2a\u54e5\u54e5\u7684\u547d\u8fd0\u6bd4\u5979\u66f4\u60e8\uff0c \u5fc3\u91cc\u4e5f\u6839\u96be\u8fc7\u3001\u5f53\u4e0b\u52c9\u5f3a\u88c5\u51fa\u7b11\u5bb9\uff0c\u5b89\u6170\u5979\u7684\u6bcd\u4eb2\u9053\uff1a\u201c\u8fd9\u4e16\u4e0a\u672c\u6765\u5c31\u6709\u8bb8\u591a\u610f\u60f3\u4e0d\u5230 \u7684\u4e8b\u60c5\uff0c\u5c31\u62ff\u76ee\u524d\u7684\u4e8b\u6765\u8bf4\u5427\uff0c\u54b1\u4eec\u6bcd\u5973\u76f8\u9022\uff0c\u53c8\u6709\u8c01\u6599\u5f97\u5230\u5462\uff1f\u8bf4\u4e0d\u5b9a\u5927\u54e5\u54e5\u4e5f\u548c\u6211 \u4eec\u7684\u906d\u9047\u4e00\u6837\uff0c\u9022\u51f6\u5316\u5409\uff0c\u9047\u96be\u6210\u7965\uff0c\u4e0d\u4e45\u54b1\u4eec\u5c31\u4f1a\u4e00\u5bb6\u56e2\u805a\u3002\u201d\u90a3\u5973\u4eba\u51c4\u7136\u8bf4\u9053\uff1a \u201c\u4f46\u613f\u5982\u6b64\u3002\u4f46\u5929\u4e0b\u95f4\u7684\u5947\u4e8b\u54ea\u6709\u8fd9\u4e48\u51d1\u5de7\uff0c\u90fd\u51fa\u5728\u6211\u7684\u8eab\u4e0a\uff1f\u201d \u3000\u3000\u90a3\u5973\u4eba\u5c06\u68b3\u5986\u53f0\u4e0a\u7684\u660e\u955c\u632a\u5230\u9762\u524d\uff0c\u63fd\u7740\u8c37\u4e2d\u83b2\u4e00\u540c\u7167\u955c\uff0c\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u5a18\uff0c\u4f60 \u770b\u6211\u4f3c\u4e0d\u4f3c\u4f60\uff1f\u201d\u90a3\u5973\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u662f\u6211\u8eab\u4e0a\u5206\u51fa\u6765\u7684\u9aa8\u8089\uff0c\u54ea\u6709\u4e0d\u76f8\u4f3c\u7684\u5462\uff1f\u5176\u5b9e\u6211 \u521a\u624d\u4e0d\u7528\u770b\u90a3\u4efd\u7f8a\u76ae\u4e66\uff0c\u4e5f\u5e94\u8be5\u77e5\u9053\u4f60\u662f\u6211\u7684\u5973\u513f\u4e86\u3002\u201d\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u4f60\u7684\u73e0\u7a46\u54e5\u54e5\u4f3c \u4e0d\u4f3c\u4f60\uff1f\u201d\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u6211\u548c\u4ed6\u662f\u4e00\u6bcd\u5b6a\u751f\uff0c\u5f53\u7136\u662f\u66f4\u76f8\u4f3c\u4e86\u3002\u201d \u3000\u3000\u90a3\u5973\u4eba\u9053\uff1a\u201c\u7ae0\u5cf0\u6bd4\u4f60\u4eec\u5927\u4e24\u5c81\uff0c\u8981\u662f\u4ed6\u8fd8\u6d3b\u7740\uff0c\u4eca\u5e74\u8be5\u662f\u4e8c\u5341\u5c81\u4e86\uff0c\u4ed6\u751f\u4e0b\u6765\u9aa8 \u9abc\u5c31\u6bd4\u4f60\u4eec\u7c97\u5927\uff0c\u8eab\u6750\u5e94\u8be5\u6bd4\u4f60\u4eec\u9ad8\u4e00\u4e9b\uff0c\u5927\u7ea6\u76f8\u8c8c\u4e5f\u4e0d\u4f1a\u5dee\u5f97\u592a\u8fdc\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u84e6\u5730\u5fc3\u5934\u4e00\u51db\uff0c\u4e0d\u81ea\u89c9\u7684\u5c31\u91cd\u590d\u5979\u6bcd\u4eb2\u6700\u540e\u7684\u90a3\u53e5\u8bdd\uff1a\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u76f8\u8c8c \u4e0d\u4f1a\u5dee\u5f97\u592a\u8fdc\u3002\u201d\u90a3\u5973\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u95ee\u9053\uff1a\u201c\u739b\u513f\uff0c\u662f\u5426\u4f60\u66fe\u7ecf\u89c1\u8fc7\u53e6\u4e00\u4e2a\u76f8\u8c8c\u4e0e\u4f60\u76f8 \u4f3c\u4e4b\u4eba\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u6ca1\u6709\u3002\u201d\u5fc3\u91cc\u6697\u60f3\uff1a\u201c\u5a18\u8bf4\u5f97\u5bf9\uff0c\u5929\u4e0b\u54ea\u6709\u8fd9\u8bb8\u591a\u5de7\u4e8b \u90fd\u51fa\u5728\u6211\u7684\u5bb6\u4e2d\u3002\u4ed6\u600e\u4f1a\u662f\u6211\u7684\u54e5\u54e5\uff1f\u6211\u4e5f\u4e0d\u8981\u8fd9\u6837\u7684\u54e5\u54e5\u3002\u5509\uff0c\u4f46\u5018\u82e5\u4ed6\u771f\u7684\u662f\u6211\u7684 \u54e5\u54e5\uff0c\u90a3\u53c8\u600e\u529e\uff1f \u3000\u3000\u6bcd\u4eb2\u77e5\u9053\u4e86\u4ed6\u7684\u884c\u5f84\uff0c\u90a3\u5c82\u4e0d\u662f\u8981\u6c14\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u539f\u6765\u8c37\u4e2d\u83b2\u6b64\u9645\uff0c\u6b63\u5728\u60f3\u7740\u4e00\u4e2a\u76f8\u8c8c\u4e0e\u5979\u201c\u5dee\u5f97\u4e0d\u4f1a\u592a\u8fdc\u201d\u7684\u4eba\uff0c\u8fd9\u4e2a\u4eba\u5c31\u662f\u53f6\u51b2 \u9704\uff0c\u5979\u867d\u7136\u66ff\u81ea\u5df1\u627e\u51fa\u65e0\u6570\u7406\u7531\uff0c\u6765\u201c\u65ad\u5b9a\u201d\u53f6\u51b2\u9704\u4e0d\u4f1a\u662f\u5979\u7684\u54e5\u54e5\uff0c\u4f46\u5fc3\u4e2d\u5374\u662f\u4e0d\u7531 \u81ea\u5df2\u7684\u611f\u5230\u4e00\u80a1\u5bd2\u610f\u3002 \u3000\u3000\u6309\u4e0b\u8c37\u4e2d\u83b2\u6bcd\u5973\u4e4b\u4e8b\u6682\u4e14\u4e0d\u63d0\uff0c\u518d\u8bf4\u5510\u52aa\u73e0\u7a46\u8fd9\u8def\u3002\u5510\u52aa\u73e0\u7a46\u5411\u4e1c\u641c\u7d22\uff0c\u4ed6\u662f\u4e2a\u7ec6 \u5fc3\u7684\u4eba\uff0c\u7687\u5b98\u7684\u6bcf\u4e00\u4e2a\u89d2\u843d\u90fd\u4e0d\u653e\u8fc7\uff0c\u4ed6\u6697\u4e2d\u4fa6\u5bdf\u4e86\u5341\u5ea7\u5bab\u6bbf\uff0c\u4ecd\u672a\u53d1\u73b0\u4f18\u4eba\uff0c\u5df2\u8fc7\u4e86 \u4e00\u4e2a\u66f4\u6b21\uff0c\u5fc3\u4e2d\u6b63\u81ea\u7126\u8e81\uff0c\u5ffd\u5730\u5728\u4e00\u5ea7\u5f69\u51e4\u697c\u4e0b\uff0c\u542c\u5f97\u697c\u4e0a\u4e24\u4e2a\u5973\u4eba\u8bf4\u8bdd\uff0c\u4e00\u4e2a\u8bf4\u9053\uff1a \u201c\u8fd9\u4e48\u8bf4\uff0c\u4ed6\u4eec\u4e24\u5144\u59b9\u90fd\u6765\u4e86\u5566\uff1f\u201d\u53e6\u4e00\u4e2a\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u90a3\u7537\u7684\u662f\u5426\u5979\u7684\u54e5\u54e5\uff0c\u4f46\u76f8\u8c8c \u662f\u5341\u5206\u76f8\u4f3c\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u7684\u58f0\u97f3\u672c\u6765\u5f88\u7ec6\uff0c\u4f46\u5510\u52aa\u73e0\u7a46\u5e7c\u4e60\u6b66\u529f\uff0c\u8033\u76ee\u806a\u654f\uff0c\u670d\u4e86\u5929\u5fc3\u77f3\u4e4b\u540e\u3002\u66f4\u662f\u5177 \u6709\u8d85\u4eba\u7684\u672c\u9886\u3002\u90a3\u4e24\u4e2a\u5973\u4eba\u867d\u662f\u5728\u697c\u4e0a\u4f4e\u58f0\u8bf4\u8bdd\uff0c\u4ed6\u5728\u697c\u4e0b\u5374\u4e5f\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u800c\u4e14\u542c \u51fa\u5176\u4e2d\u4e00\u4e2a\u58f0\u97f3\uff0c\u7adf\u662f\u4f3c\u66fe\u76f8\u8bc6\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u5728\u8bf4\u6211\u4e48\uff1f\u201d\u7acb\u5373\u65bd\u5c55\u8f7b\u529f\uff0c\u8dc3\u4e0a \u7409\u7483\u74e6\u9762\uff0c\u5bfb\u5230\u6709\u706f\u706b\u7684\u6240\u5728\uff0c\u7ed5\u5230\u540e\u7a97\uff0c\u5077\u5077\u5f20\u671b\u3002\u53ea\u89c1\u91cc\u9762\u4e24\u4e2a\u5973\u4eba\uff0c\u4e00\u8001\u4e00\u5c11\uff0c \u8001\u7684\u90a3\u4e2a\u7ea6\u6709\u56db\u5341\u591a\u5c81\uff0c\u8eab\u62ab\u72d0\u88d8\uff0c\u73e0\u5149\u5b9d\u6c14\u4f3c\u4e2a\u8d35\u5987\u3002\u5e74\u7eaa\u8f7b\u8f7b\u7684\u90a3\u4e2a\uff0c\u5374\u662f\u6628\u665a\u548c \u5510\u52aa\u73e0\u7a46\u4ea4\u8fc7\u624b\u7684\u90a3\u4e2a\u5929\u9b54\u6559\u4e3b\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u8d35\u5987\u6a21\u6837\u7684\u5973\u4eba\u795e\u8272\u751a\u662f\u4e0d\u5b89\uff0c\u84e6\u5730\u7528\u529b\u4e00\u62cd\u684c\u5b50\uff0c\u72e0\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u65e9\u529d\u7687 \u4e0a\u628a\u90a3\u4e2b\u5934\u6740\u4e86\uff0c\u4ed6\u4e0d\u80af\u542c\uff0c\u597d\u5566\uff0c\u73b0\u5728\u5374\u7ed9\u5979\u9003\u51fa\u6765\u5566\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u7687\u989d\u5a18\u4e0d\u5fc5\u62c5\u5fe7\uff0c\u8fd9\u4e24\u5144\u59b9\u7684\u6b66\u529f\u867d\u7136\u4e0d\u5f31\uff0c\u54b1\u4eec\u8fd8\u6709\u597d\u51e0\u4e2a\u4eba\u53ef \u4ee5\u80dc\u5f97\u8fc7\u4ed6\u4eec\uff0c\u8c05\u4ed6\u4eec\u4e5f\u4e0d\u6562\u5230\u5bab\u4e2d\u5371\u5bb3\u5a18\u5a18\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u4e0d\u7981\u5927\u4e3a\u8be7\u5f02\uff0c\u539f\u6765\u6309\u7167\u9a6c\u8428\u513f\u56fd\u7684\u5c01\u53f7\uff0c\u201c\u7687\u989d\u5a18\u201d\u6bd4\u7687\u540e\u66f4\u4e3a\u5c0a\u8d35\uff0c \u5f97\u8fd9\u5c01\u53f7\u7684\u591a\u662f\u5e74\u9ad8\u5fb7\u5c0a\uff0c\u6216\u8005\u5bf9\u56fd\u5bb6\u6709\u529f\u52b3\u7684\uff0c\u7687\u5e1d\u957f\u4e00\u8f88\u7684\u4eb2\u5c5e\u3002\u4f46\u8fd9\u5973\u4eba\u4e0d\u8fc7\u56db \u5341\u591a\u5c81\uff0c\u800c\u7be1\u4f4d\u7684\u90a3\u4e2a\u597d\u738b\uff0c\u5374\u662f\u4e94\u5341\u5f00\u5916\u7684\u4eba\u4e86\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5fc3\u60f3\uff1a\u201c\u54ea\u6765\u7684\u8fd9\u4e2a\u5996\u91cc\u5996\u6c14\u7684\u7687\u989d\u5a18\uff1f\u542c\u5979\u7684\u53e3\u6c14\uff0c\u8fd9\u5996\u5987\u4f3c\u4e4e\u6015\u6211\u4eec \u5411\u5979\u5bfb\u4ec7\uff0c\u6211\u5374\u6839\u672c\u4e0d\u77e5\u9053\u6709\u5979\u8fd9\u6837\u7684\u4e00\u4e2a\u5973\u4eba\u3002\u201d \u3000\u3000\u90a3\u201c\u7687\u989d\u5a18\u201d\u53c8\u95ee\u9053\uff1a\u201c\u5f53\u65f6\u5e72\u6bbf\u4e0b\u5728\u573a\u5417\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a \u3000\u3000\u201c\u6211\u548c\u4ed6\u90fd\u5728\u573a\u7684\u3002\u53e6\u5916\u8fd8\u6709\u5b9d\u8c61\u6cd5\u5e08\u7684\u4e24\u4e2a\u5f1f\u5b50\u3002\u201d\u90a3\u201c\u7687\u989d\u5a18\u201d\u54fc\u4e86\u4e00\u58f0\uff0c\u51b7 \u7b11\u8bf4\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u81ea\u5938\u9664\u4e86\u5e08\u7236\u4e4b\u5916\uff0c\u4ed6\u7684\u6b66\u529f\u5929\u4e0b\u65e0\u654c\u5417\uff0c\u4e3a\u4ec0\u4e48\u6253\u4e0d\u8fc7\u90a3\u4e2a\u91ce\u79cd\uff1f \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5f53\u7136\u542c\u5f97\u660e\u767d\uff0c\u8fd9\u201c\u7687\u989d\u5a18\u201d\u8bf4\u7684\u201c\u5e72\u6bbf\u4e0b\u201d\u6307\u7684\u662f\u53f6\u51b2\u9704\uff1b\u201c\u91ce\u79cd\u201d\u5c31 \u662f\u6307\u4ed6\u2014\u2014\u5510\u52aa\u73e0\u7a46\u4e86\u3002\u5510\u52aa\u73e0\u7a46\u4e0d\u7981\u5fc3\u4e2d\u5927\u6012\uff0c\u60f3\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff1f\u6211\u4e0e\u4f60\u6709\u4f55\u51a4\u4ec7\uff0c \u7adf\u6562\u8fb1\u53ca\u6211\u7684\u7236\u738b\u6bcd\u540e\u3002\u201d\u5fcd\u4e0d\u4f4f\u6c14\uff0c\u51e0\u4e4e\u5c31\u60f3\u9a6c\u4e0a\u95ef\u8fdb\u53bb\u5c06\u5979\u6740\u6389\uff0c\u4f46\u968f\u5373\u60f3\u9053\uff1a \u201c\u6211\u4e14\u6682\u5fcd\u4e00\u65f6\uff0c\u542c\u542c\u5979\u518d\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u8bf4\u9053\uff1a\u201c\u5e72\u6bbf\u4e0b\u662f\u592a\u8fc7\u5938\u53e3\u4e86\u4e00\u70b9\uff0c\u4e0d\u8fc7\u4ed6\u7684\u6b66\u529f\u4e5f\u786e\u5b9e\u4e0d\u9519\uff0c\u548c\u8c37\u4e2d\u83b2 \u7684\u90a3\u4e2a\u54e5\u54e5\u81f3\u5c11\u662f\u529f\u529b\u6089\u654c\uff0c\u53ea\u56e0\u8c37\u4e2d\u83b2\u624b\u4e2d\u6301\u6709\u5b9d\u5251\uff0c\u800c\u6211\u53c8\u4e0d\u5e78\u5148\u53d7\u4e86\u4f24\uff0c\u5e2e\u4e0d\u4e0a \u4ed6\u751a\u4e48\u5fd9\uff0c\u8bf4\u6765\u771f\u662f\u60ed\u6127\u3002\u201d \u3000\u3000\u90a3\u201c\u7687\u989d\u5a18\u201d\u5ffd\u5730\u53cc\u7709\u5012\u7ad6\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u5bb4\u8bdd\uff0c\u662f\u4e0d\u662f\u5e72\u6bbf\u4e0b\u6709\u610f\u653e\u8d70\u90a3\u4e24\u4e2a\u91ce \u79cd\u7684\uff1f\u4f60\u522b\u591a\u5fc3\uff0c\u6211\u51b3\u4e0d\u4f1a\u6000\u7591\u4f60\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u5a18\u5a18\uff0c\u4f60\u4e5f\u5fd2\u591a\u7591\u5fc3\uff0c\u7687\u4e0a\u5bf9\u5e72\u6bbf\u4e0b\u6709\u5982\u9aa8\u8089\uff0c\u4ed6\u600e\u4f1a\u80cc\u53db\u7687\u4e0a \u548c\u5a18\u5a18\u3002\u201d \u3000\u3000\u90a3\u201c\u7687\u989d\u5a18\u201d\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5e76\u4e0d\u662f\u6211\u778e\u7591\u4e86\uff0c\u5509\uff0c\u8fd9\uff0c\u8fd9\u2026\u2026\u201d\u5979\u4f3c\u662f\u60f3\u5410 \u8bf4\u4ec0\u4e48\u673a\u5bc6\uff0c\u8bdd\u5230\u53e3\u8fb9\uff0c\u5374\u53c8\u5fcd\u4f4f\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u8fd9\u51e0\u5929\u6211\u8001\u662f\u89c9\u5f97\u5e72\u6bbf\u4e0b\u795e\u8272\u4e0d\u5bf9\u3002 \u5509\uff0c\u6628\u665a\u6211\u8fd8\u505a\u4e86\u4e00\u4e2a\u68a6\uff0c\u68a6\u89c1\u5e72\u6bbf\u4e0b\u62ff\u4e86\u4e00\u628a\u8840\u5200\uff0c\u51f6\u9738\u9738\u7684\u8981\u6765\u6740\u6211\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u5996\u68a6\u65e0\u51ed\uff0c\u5c82\u53ef\u76f8\u4fe1\u3002\u201d\u90a3\u201c\u7687\u989d\u5ab3\u201d\u9053\uff1a \u3000\u3000\u201c\u8fd9\u51e0\u65e5\u6211\u8001\u662f\u5fc3\u60ca\u80c6\u6218\uff0c\u679c\u7136\u4eca\u5929\u4f7f\u542c\u5230\u4e86\u574f\u6d88\u606f\uff0c\u90a3\u4e24\u4e2a\u91ce\u79cd\u679c\u7136\u662f\u5b66\u6210\u4e86\u6b66 \u529f\uff0c\u8981\u56de\u6765\u62a5\u4ec7\u4e86\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u5a18\u5a18\u8981\u662f\u5bb3\u6015\uff0c\u6211\u6765\u966a\u4f34\u5a18\u5a18\u3002\u5c31\u53ea\u6015\u7687\u4e0a\u4e0d\u4f9d\u3002\u201d\u90a3\u201c\u7687\u989d\u5a18\u201d \u6068\u6068\u8bf4\u9053\uff1a\u201c\u4f60\u522b\u63d0\u8fd9\u4e2a\u8d1f\u5fc3\u4eba\u5566\uff01\u6211\u771f\u540e\u6094\uff0c\u6211\u653e\u7740\u597d\u597d\u7684\u7687\u540e\u4e0d\u505a\uff0c\u5374\u53bb\u5e2e\u4ed6\u7be1\u4f4d\u3002 \u5148\u5e1d\u867d\u7136\u5bf9\u6211\u4e0d\u597d\uff0c\u5bf9\u6211\u603b\u8fd8\u662f\u5ba2\u5ba2\u6c14\u6c14\u7684\uff0c\u4ed6\u5440\uff0c\u54fc\uff0c\u7ed9\u4e86\u6211\u4e00\u4e2a\u5c0a\u53f7\uff0c\u5c31\u518d\u4e5f\u4e0d\u7406 \u6211\u4e86\u3002\u6211\u540d\u4e49\u4e0a\u662f\u7687\u989d\u5a18\uff0c\u5b9e\u9645\u6bd4\u5173\u5728\u51b7\u5bab\u91cc\u7684\u90a3\u4e2a\u72d0\u72f8\u7cbe\u4e5f\u597d\u4e0d\u4e86\u591a\u5c11\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u89c9\u5927\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5973\u4eba\u81ea\u79f0\u662f\u5148\u5e1d\u7684\u7687\u540e\uff0c\u96be\u9053\u662f\u6211 \u7684\u6bcd\u4eb2\uff1f\u5979\u600e\u80fd\u8fd9\u6837\u72e0\u6bd2\uff0c\u7adf\u7136\u5e2e\u52a9\u5916\u4eba\uff0c\u8c0b\u6740\u4eb2\u592b\uff0c\u7be1\u593a\u7687\u4f4d\uff1f\u201d\u539f\u6765\u4ed6\u4e00\u5411\u4ee5\u4e3a\u81ea \u5df1\u7684\u6bcd\u4eb2\u4fbf\u662f\u7687\u540e\uff0c\u5374\u4e0d\u77e5\u751f\u6bcd\u53e6\u6709\u5176\u4eba\u3002\u968f\u5373\u60f3\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u5929\u4e0b\u51b3\u6ca1\u6709\u628a\u4eb2\u751f\u513f\u5973 \u9a82\u4f5c\u2018\u91ce\u79cd\u2019\u7684\u6bcd\u4eb2\uff0c\u8fd9\u91cc\u9762\u5b9a\u7136\u8fd8\u6709\u5185\u60c5\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u90a3\u201c\u7687\u989d\u5a18\u201d\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5361\u5170\u59ae\uff0c\u4f60\u7684\u6bcd\u4eb2\u5728\u751f\u4e4b\u65f6\u4e0e\u6211\u60c5\u540c\u59d0 \u59b9\uff0c\u6211\u4e5f\u4e00\u5411\u628a\u4f60\u5f53\u4f84\u5973\u770b\u5f85\uff0c\u4eca\u5929\u53ea\u6709\u4f9d\u9760\u4f60\u4e86\u3002\u201d\u5927\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u5a18\u5a18\uff0c\u4f60\u6709\u4ec0\u4e48 \u4e8b\u60c5\u8981\u6211\u6548\u52b3\uff0c\u5c3d\u8bf4\u65e0\u59a8\u3002\u201d \u3000\u3000\u90a3\u201c\u7687\u989d\u5a18\u201d\u9053\uff1a\u201c\u8bf4\u5b9e\u5728\u7684\uff0c\u6211\u867d\u7136\u5bb3\u6015\u90a3\u4e24\u4e2a\u91ce\u79cd\u62a5\u4ec7\uff0c\u4f46\u4ed6\u4eec\u8981\u60f3\u8fdb\u5bab\u884c\u523a\uff0c \u6bd5\u7adf\u4e5f\u4e0d\u5bb9\u6613\u3002\u6211\u6700\u62c5\u5fc3\u7684\u5012\u662f\u5fc3\u8179\u4e4b\u60a3\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u6014\u4e86\u4e00\u6014\uff0c\u4f46\u53c8\u4f3c\u731c\u5230\u4e86\u51e0\u5206\u7684\u795e\u8272\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u5fc3\u8179\u4e4b\u60a3\uff0c\u5a18\u5a18\u53ef\u4ee5 \u8bf4\u7ed9\u6211\u542c\u5417\uff1f\u201d \u3000\u3000\u90a3\u201c\u7687\u989d\u5a18\u201d\u53cc\u773c\u76ef\u7740\u5929\u9b54\u6559\u4e3b\uff0c\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u5361\u5170\u59ae\uff0c\u4f60\u7684\u6b66\u529f\u6bd4\u5e72\u6bbf\u4e0b\u5982\u4f55\uff1f\u201d \u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u5f53\u7136\u662f\u5e72\u6bbf\u4e0b\u6bd4\u6211\u9ad8\u660e\u3002\u201d\u90a3\u201c\u7687\u989d\u5a18\u201d\u9053\uff1a\u201c\u4f60\u522b\u5ba2\u6c14\uff0c\u8981\u8bf4\u5b9e\u5728\u7684 \u8bdd\u3002\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a \u3000\u3000\u201c\u8bf4\u5b9e\u5728\u7684\uff0c\u5355\u51ed\u6b66\u529f\uff0c\u201d\u6211\u6253\u8d25\u4ed6\u5b9e\u5728\u4e0d\u6613\uff0c\u4ed6\u8981\u80dc\u6211\uff0c\u5927\u7ea6\u4e5f\u96be\u3002\u4e0d\u8fc7\u6211\u8fd8\u6709 \u522b\u7684\u672c\u9886\u53ef\u4ee5\u514b\u5236\u4ed6\u3002\u201d \u3000\u3000\u90a3\u201c\u7687\u989d\u5a18\u201d\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u542c\u8bf4\u5389\u80dc\u7537\u65e9\u5df2\u628a\u767e\u6bd2\u771f\u7ecf\u5f52\u8fd8\u4f60\u5bb6\u4e86\uff0c\u4f60\u7684\u4f7f\u6bd2\u672c\u9886 \u4e00\u5b9a\u662f\u5f88\u4e86\u4e0d\u8d77\u4e86\u3002\u201d\u5929\u9b54\u6559\u4e3b\u5fae\u7b11\u9053\uff1a \u3000\u3000\u201c\u8981\u770b\u662f\u5bf9\u4ed8\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u5018\u82e5\u5185\u529f\u5df2\u7ec3\u5230\u8d85\u51e1\u5165\u5723\uff0c\u767e\u6bd2\u4e0d\u4fb5\uff0c\u90a3\u6211\u4e5f\u65e0\u6cd5\u5bf9\u4ed8\u3002 \u5982\u679c\u662f\u6b66\u529f\u4e0e\u6211\u5dee\u4e0d\u591a\u7684\uff0c\u50cf\u5e72\u6bbf\u4e0b\u8fd9\u6837\u7684\u4eba\uff0c\u90a3\u5012\u5bb9\u6613\u3002\u201d \u3000\u3000\u90a3\u201c\u7687\u989d\u5a18\u201d\u5927\u559c\u9053\uff1a\u201c\u5361\u5170\u59ae\uff0c\u4f60\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u4f60\u4e00\u5b9a\u77e5\u9053\u6211\u7684\u610f\u601d\u4e86\u3002\u6211\u7684\u5fc3 \u8179\u5927\u60a3\u5c31\u662f\u5e72\u6bbf\u4e0b\uff01\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5fc3\u5934\u4e00\u52a8\uff0c\u6545\u610f\u88c5\u51fa\u5403\u60ca\u7684\u6837\u5b50\uff0c\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u60f3\u4e0d\u5230\u5e72\u6bbf \u4e0b\u4e0e\u5a18\u5a18\u7adf\u662f\u52bf\u4e0d\u4e24\u7acb\u3002\u5a18\u5a18\u7684\u610f\u601d\u662f\u2014\u2014\u201d\u90a3\u201c\u7687\u989d\u5a18\u201d\u9053\uff1a\u201c\u5361\u5170\u59ae\uff0c\u4f60\u52a1\u5fc5\u8981\u5e2e \u6211\u8fd9\u4e2a\u5fd9\uff0c\u628a\u8fd9\u4e2a\u5fc3\u8179\u4e4b\u60a3\u9664\u6389\uff01\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u5a18\u5a18\u95ee\u4ee5\u5982\u6b64\u6068\u4ed6\uff1f\u201d\u90a3\u201c\u7687\u989d\u5a18\u201d \u9053\uff1a\u201c\u6709\u4e9b\u4e8b\u60c5\u4f60\u8fd8\u672a\u660e\u767d\uff0c\u5f85\u4f60\u5c06\u4ed6\u9664\u6389\uff0c\u6211\u81ea\u4f1a\u544a\u8bc9\u4f60\u7684\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u534a\u664c\u4e0d\u8bed\uff0c\u770b\u6837\u5b50\u4f3c\u662f\u6b63\u5728\u5fc3\u4e2d\u76d8\u7b97\u3002\u90a3\u201c\u7687\u989d\u5a18\u201d\u7126\u6025\u975e\u5e38\uff0c\u5fd9\u7740\u53c8\u9053\uff1a \u201c\u5361\u5170\u59ae\uff0c\u4f60\u81ea\u5c0f\u7684\u6027\u5b50\u5c31\u662f\u5929\u4e0d\u6015\u5730\u4e0d\u6015\u7684\u2026\u2026\u201d\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u51e0\u5e74\u5728\u4e2d\u539f \u5f00\u5b97\u7acb\u6559\uff0c\u8fd8\u5f97\u4e86\u4e00\u4e2a\u5929\u9b54\u6559\u4e3b\u7684\u2018\u7f8e\u540d\u2019\u5462\uff01\u201d\u90a3\u201c\u7687\u989d\u5a18\u201d\u9053\uff1a\u201c\u662f\u554a\uff0c\u4f60\u662f\u7ecf\u8fc7 \u5927\u98ce\u5927\u6d6a\u7684\u4eba\uff0c\u96be\u9053\u5e72\u8fd9\u70b9\u5c0f\u4e8b\u4e5f\u4f1a\u5bb3\u6015\u4e48\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u4e0d\u662f\u5c0f\u4e8b\u554a\uff0c\u5e72\u6bbf\u4e0b\u6781\u5f97\u7687\u4e0a\u5ba0\u7231\uff0c\u73b0\u5728\u53c8\u6b63\u662f\u5b98\u5c45\uff0c \u2018\u6267\u91d1\u543e\u2019\u5927\u5c06\u519b\u4e4b\u804c\u3002\u201d\u90a3\u201c\u7687\u989d\u5a18\u201d\u9053\uff1a \u3000\u3000\u201c\u5361\u5170\u59ae\uff0c\u4f60\u4e0d\u7528\u62c5\u5fc3\uff0c\u6211\u518d\u544a\u8bc9\u4f60\u4e00\u4e2a\u79d8\u5bc6\uff0c\u7687\u4e0a\u5ba0\u7231\u5e72\u6bbf\u4e0b\u90a3\u662f\u5047\u7684\u3002\u201d\u5929\u9b54 \u6559\u4e3b\u9053\uff1a\u201c\u7687\u4e0a\u4eb2\u53e3\u544a\u8bc9\u4f60\u5417\uff1f\u201d \u3000\u3000\u90a3\u201c\u7687\u989d\u5a18\u201d\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u6211\uff0c\u6211\u770b\u5f97\u51fa\u4ed6\u7684\u5fc3\u610f\u3002\u5361\u5170\u59ae\uff0c\u4f60\u2014\u2014\u201d\u5929\u9b54\u6559\u4e3b\u7f13 \u7f13\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u5f97\u60f3\u4e00\u60f3\u3002\u201d\u90a3\u201c\u7687\u989d\u5a18\u201d\u9053\uff1a \u3000\u3000\u201c\u5361\u5170\u59ae\uff0c\u4f60\u5e2e\u6211\u8fd9\u4e2a\u5fd9\uff0c\u6211\u6c38\u8fdc\u4e0d\u4f1a\u5fd8\u8bb0\u4f60\u7684\u597d\u5904\uff0c\u4f60\u8981\u4ec0\u4e48\uff0c\u6211\u7ed9\u4f60\u4ec0\u4e48\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u5a18\u5a18\uff0c\u4f60\u6709\u6240\u4e0d\u77e5\uff0c\u5e72\u6bbf\u4e0b\u662f\u5b9d\u8c61\u6cd5\u5e08\u7684\u5f1f\u5b50\uff0c\u5b9d\u8c61\u6cd5\u5e08\u7684 \u6b66\u529f\u5f53\u4e16\u65e0\u4eba\u53ef\u53ca\uff0c\u6211\u82e5\u6740\u4e86\u4ed6\u7684\u5f1f\u5b50\uff0c\u7687\u4e0a\u7eb5\u7136\u4e0d\u52a0\u8ffd\u7a76\uff0c\u90a3\u5b9d\u8c61\u6cd5\u5e08\u53ea\u6015\u8981\u4e3a\u4ed6\u62a5 \u4ec7\uff0c\u4ed6\u95e8\u4e0b\u5f1f\u5b50\u4e4b\u591a\uff0c\u6211\u5373\u4f7f\u8fdc\u8d70\u9ad8\u98de\uff0c\u4e5f\u96be\u514d\u4e00\u751f\u53d7\u4ed6\u4eec\u7ea0\u7f20\u3002\u201d \u3000\u3000\u90a3\u201c\u7687\u989d\u5a18\u201d\u9893\u7136\u5750\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u7adf\u662f\u4e0d\u80fd\u52a8\u4ed6\u4e86\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u8bf4\u9053\uff1a \u201c\u9664\u975e\u6211\u5f97\u5230\u4e00\u6837\u4e1c\u897f\u3002\u201d\u90a3\u201c\u7687\u989d\u5a18\u201d\u5fd9\u9053\uff1a\u201c\u4ec0\u4e48\u4e1c\u897f\uff0c\u5feb\u8bf4\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u770b\u5728\u5a18\u5a18\u5f85\u6211\u7684\u60c5\u4efd\uff0c\u5a18\u5a18\uff0c\u4f60\u5018\u82e5\u7ed9\u6211\u627e\u5230\u90a3\u5377\u2018\u9f99\u529b\u79d8\u85cf\u2019\uff0c \u6211\u7ec3\u4e86\u79d8\u85cf\u4e0a\u7684\u529f\u592b\uff0c\u4e5f\u8bb8\u8fd8\u672a\u80fd\u5bf9\u4ed8\u5b9d\u8c61\u6cd5\u5e08\uff0c\u4f46\u6700\u5c11\u53ef\u4ee5\u5e94\u4ed8\u4ed6\u95e8\u4e0b\u5f1f\u5b50\u7684\u7ea0\u7f20\uff0c \u90a3\u6211\u5c31\u53ef\u4ee5\u5b89\u5fc3\u7ed9\u5a18\u5a18\u529e\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u542c\u5230\u8fd9\u91cc\uff0c\u7981\u4e0d\u4f4f\u53c8\u662f\u4e00\u60ca\uff0c\u539f\u6765\u4ed6\u90a3\u7f8a\u76ae\u4e66\u4e0a\u6240\u8f7d\u7684\u6b66\u529f\u5c31\u6b63\u662f\u201c\u9f99\u529b \u79d8\u85cf\u201d\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u201c\u7687\u989d\u5a18\u201d\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5361\u5170\u59ae\uff0c\u4f60\u4e0d\u76f8\u4fe1\u6211\u4e48\uff1f\u5c31\u5728\u76d6\u6e29\u7be1 \u4f4d\u4e4b\u540e\uff0c\u6211\u66fe\u7ecf\u5230\u5b9d\u5e93\u627e\u8fc7\uff0c\u90a3\u5377\u2018\u9f99\u529b\u79d8\u85cf\u2019\u65e9\u5df2\u4e0d\u7ffc\u800c\u98de\uff0c\u60f3\u662f\u7ed9\u5148\u5e1d\u70e7\u6389\u4e86\u3002\u4ed6 \u624b\u6284\u7684\u4e24\u4efd\u526f\u672c\uff0c\u5728\u90a3\u4e24\u4e2a\u91ce\u79cd\u8eab\u4e0a\uff0c\u6211\u4e0d\u662f\u5bf9\u4f60\u8bf4\u8fc7\u4e86\u5417\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u59b9\u59b9\u5341\u51e0\u5e74\u6765\u60f3\u4e0d\u901a\u7684\u4e8b\u60c5\uff0c\u73b0\u5728\u53ef \u662f\u771f\u76f8\u5927\u767d\u4e86\u3002\u201d \u3000\u3000\u5341\u4e00\u5e74\u524d\uff0c\u8c37\u4e2d\u83b2\u4e03\u5c81\u7684\u65f6\u5019\uff0c\u5929\u9b54\u6559\u4e3b\u59d0\u59b9\u76f8\u7ee7\u4e0a\u9099\u5c71\u95f9\u4e8b\uff0c\u8d77\u521d\u662f\u5979\u7684\u59d0\u59d0\u7f2a \u592b\u4eba\u5192\u8ba4\u8c37\u4e2d\u83b2\u662f\u5979\u7684\u79c1\u751f\u5973\u513f\uff0c\u968f\u540e\u5c31\u662f\u5929\u9b54\u6559\u4e3b\u8981\u6765\u5f3a\u62a2\uff0c\u5f53\u65f6\u5927\u5bb6\u90fd\u60f3\u4e0d\u901a\u5176\u4e2d \u7f18\u6545\u3002\u4e00\u76f4\u5230\u4e86\u6628\u65e5\uff0c\u5510\u52aa\u73e0\u7a46\u5144\u59b9\u4e5f\u8fd8\u662f\u5f04\u4e0d\u660e\u767d\uff1a\u4f55\u4ee5\u5929\u9b54\u6559\u4e3b\u4e00\u6765\uff0c\u5c31\u77e5\u9053\u4e86\u7f8a \u76ae\u4e66\u7684\u79d8\u5bc6\uff1f\u73b0\u5728\u5510\u52aa\u73e0\u7a46\u65b9\u59cb\u660e\u767d\uff0c\u539f\u6765\u662f\u8fd9\u4e2a\u201c\u7687\u989d\u5a18\u201d\u6cc4\u6f0f\u7684\uff0c\u800c\u8fd9\u4e2a\u201c\u7687\u989d\u5a18\u201d \u7adf\u7136\u662f\u4ed6\u7236\u4eb2\u4ee5\u524d\u7684\u6b63\u5bab\u7687\u540e\uff01\u542c\u5979\u4eec\u7684\u8c08\u8bdd\uff0c\u5979\u4eec\u4e43\u662f\u4e16\u4ee3\u4ea4\u60c5\uff0c\u65e0\u602a\u8fd9\u201c\u7687\u989d\u5a18\u201d \u628a\u5929\u9b54\u6559\u4e3b\u5f15\u4e3a\u5fc3\u8179\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u542c\u4e86\u5979\u4eec\u8fd9\u4e00\u756a\u5bc6\u5ba4\u79c1\u6d3b\uff0c\u4e0d\u7531\u5f97\u5fc3\u5934\u706b\u8d77\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u5996\u5987\u771f\u662f\u65e0 \u803b\u4e4b\u9f99\uff0c\u72e0\u6bd2\u5df1\u6781\uff01\u6211\u771f\u60f3\u4e0d\u5230\u6709\u8fd9\u6837\u7684\u5ae1\u6bcd\u3002\u201d\u4f46\u4ed6\u6bd5\u7adf\u662f\u4e2a\u6bd4\u8f83\u51b7\u9759\u7684\u4eba\uff0c\u968f\u5373\u60f3 \u9053\uff1a\u201c\u8fd9\u5996\u5987\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\uff0c\u6211\u5148\u6740\u4e86\u5978\u738b\uff0c\u518d\u6765\u5904\u7f6e\u5979\u4e5f\u8fd8\u4e0d\u8fdf\uff0c\u514d\u5f97\u6253\u8349\u60ca\u86c7\u3002\u4e14 \u542c\u542c\u4ed6\u4eec\u518d\u8bf4\u4ec0\u4e48\u3002\u201d \u3000\u3000\u53ea\u89c1\u90a3\u201c\u7687\u989d\u5a18\u201d\u7684\u773c\u73e0\u5b50\u9aa8\u788c\u788c\u5730\u8f6c\u4e86\u4e24\u8f6c\uff0c\u63a5\u7eed\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u60f3\u8981\u90a3\u2018\u9f99\u529b\u79d8\u85cf\u2019\u6211\u662f\u65e0\u6cd5\u5e94\u547d\u4e86\uff0c\u4e0d\u8fc7\uff0c\u6211\u8fd8\u53e6\u85cf\u6709\u4e00\u6761\u5b9d\u5e93\u7684\u9501\u5319\uff0c\u7687 \u4e0a\u5374\u4e0d\u77e5\u9053\u3002\u6211\u73b0\u5728\u662f\u4e0d\u6562\u79c1\u5f00\u5b9d\u5e93\u4e86\uff0c\u4f46\u4f60\u5374\u53ef\u4ee5\u8fdb\u53bb\u3002\u5b9d\u5e93\u91cc\u8fd8\u6709\u51e0\u4ef6\u7a00\u4e16\u4e4b\u73cd\uff0c \u672a\u5fc5\u53ca\u4e0d\u4e0a\u90a3\u2018\u9f99\u529b\u79d8\u85cf\u2019\u3002\u6211\u66fe\u542c\u5148\u5e1d\u8bf4\u8fc7\uff0c\u636e\u8bf4\u5176\u4e2d\u6709\u4e00\u4e24\u6837\u4e1c\u897f\uff0c\u5bf9\u5b66\u6b66\u7684\u4eba\u5f88 \u6709\u7528\u5904\u3002\u53ef\u60dc\u5bf9\u4e8e\u6b66\u5b66\uff0c\u6211\u662f\u4e00\u7a8d\u4e0d\u901a\uff0c\u5f53\u65f6\u6ca1\u6709\u4ed4\u7ec6\u95ee\u4ed6\uff0c\u4f46\u4ed6\u90fd\u90a3\u4e48\u5f53\u4f5c\u5b9d\u8d1d\u7684\u5938 \u8bf4\uff0c\u60f3\u5fc5\u662f\u597d\u4e1c\u897f\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u6026\u7136\u5fc3\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u5929\u5fc3\u6709\u7684\u795e\u5947\u81f4\u529b\u6211\u662f\u66fe\u7ecf\u76ee\u51fb\u7684\u4e86\uff0c\u83ab\u975e\u5b9d\u5e93\u91cc\u8fd8 \u6709\uff1f\u6216\u8005\u6709\u5176\u4ed6\u5b9d\u7269\u6bd4\u5f97\u4e0a\u5929\u5fc3\u6709\u7684\uff1f\u201d \u3000\u3000\u90a3\u7687\u989d\u5a18\u9053\uff1a\u201c\u5361\u5170\u59ae\uff0c\u6211\u628a\u5b9d\u5e93\u7684\u9492\u5319\u7ed9\u4f60\uff0c\u6362\u5e72\u6bbf\u4e0b\u7684\u4e00\u6761\u6027\u547d\u5982\u4f55\uff1f\u201d\u5929\u9b54 \u6559\u4e3b\u9053\uff1a\u201c\u597d\uff0c\u5a18\u5a18\u65e2\u7136\u5b9a\u8981\u5c06\u4ed6\u9664\u53bb\uff0c\u6211\u5c31\u5192\u9669\u7ed9\u5a18\u5a18\u4e86\u7ed3\u8fd9\u4e2a\u5fc3\u4e8b\u5427\u3002\u201d\u90a3\u201c\u7687\u989d \u5a18\u201d\u9053\uff1a\u201c\u4f60\u628a\u4ed6\u5de6\u8fb9\u90a3\u53ea\u8033\u6735\u5272\u4e0b\u6765\u3002\u6211\u8ba4\u5f97\u8fd9\u53ea\u8033\u6735\uff0c\u6211\u89c1\u4e86\u8033\u6735\uff0c\u9a6c\u4e0a\u5c31\u628a\u5b9d\u5958 \u7684\u9501\u5319\u7ed9\u4f60\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5fc3\u91cc\u6697\u9a82\uff1a\u201c\u8fd9\u8001\u8654\u5a46\u8fde\u6211\u4e5f\u76f8\u4fe1\u4e0d\u8fc7\u3002\u6211\u4e5f\u4e0d\u6015\u4f60\u8d56\u5e10\uff0c\u53cd\u6b63\u6211\u6740\u4e86\u53f6 \u51b2\u9704\u4e5f\u8981\u8fdc\u8d70\u9ad8\u98de\u7684\uff0c\u7d22\u6027\u628a\u5b9d\u5e93\u91cc\u7684\u73cd\u5ba4\u4e00\u5377\u800c\u7a7a\u3002\u54c8\uff0c\u8fd9\u4ea4\u6613\u5012\u771f\u662f\u4e0d\u9519\uff01\u201d\u4e8e\u662f \u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u8bf7\u5a18\u5a18\u5728\u8fd9\u91cc\u7b49\u6211\uff0c\u6211\u53bb\u53bb\u5c31\u6765\u3002\u201d\u90a3\u201c\u7687\u989d\u5a18\u201d\u7709\u5f00\u773c\u7b11\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u597d\u4f84\u5973\uff0c\u90fd\u62dc\u6258\u4f60\u5566\uff0c\u795d\u4f60\u9a6c\u5230\u6210\u529f\uff01\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6025\u5fd9\u85cf\u5230\u6697\u5904\uff0c\u53ea\u89c1\u5929\u9b54\u6559\u4e3b\u4ece\u7a97\u53e3\u8dc3\u51fa\uff0c\u4e00\u6e9c\u70df\u5730\u8d70\u4e86\u3002\u5510\u52aa\u73e0\u7a46\u63ed\u5f00 \u4e00\u7247\u74e6\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u6682\u4e14\u4e0d\u6740\u8fd9\u5996\u5987\uff0c\u4f46\u4e5f\u8981\u53eb\u5979\u5403\u5403\u82e6\u5934\u3002\u201d\u4f7f\u51fa\u9694\u7a7a\u70b9\u7a74\u7684\u672c\u9886\uff0c \u90a3\u7687\u989d\u5a18\u542c\u5f97\u58f0\u54cd\uff0c\u65b9\u624d\u62ac\u5934\u4e00\u671b\uff0c\u5df2\u7ed9\u5510\u52aa\u73e0\u7a46\u70b9\u4e86\u7a74\u9053\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7528\u7684\u662f\u4ed6\u5e08\u7956\u6bd2\u9f99\u5c0a\u8005\u7684\u72ec\u95e8\u70b9\u7a74\u624b\u6cd5\uff0c\u70b9\u4e86\u90a3\u201c\u7687\u989d\u5a18\u201d\u810a\u690e\u9aa8\u7b2c\u4e03\u5757 \u8282\u9aa8\u4e0b\u9762\u7684\u201c\u7ae0\u95e8\u7a74\u201d\uff0c\u6b64\u7a74\u4e00\u70b9\uff0c\u53d7\u8005\u5468\u8eab\u9aa8\u8282\uff0c\u90fd\u4f3c\u7ed9\u5229\u9488\u7a7f\u523a\uff0c\u75db\u82e6\u4e0d\u582a\uff0c\u4f46\u5374 \u4e0d\u80fd\u52a8\u5f39\uff0c\u60f3\u53eb\u4e5f\u53eb\u4e0d\u51fa\u6765\uff0c\u53ea\u80fd\u54d1\u5fcd\u3002\u5510\u52aa\u73e0\u7a46\u51fa\u4e86\u53e3\u6c14\uff0c\u7acb\u5373\u79bb\u5f00\uff0c\u6708\u8272\u6726\u80e7\u4e4b\u4e0b\uff0c \u53ea\u89c1\u4e00\u6761\u9ed1\u5f71\u5df2\u5728\u897f\u5317\u7684\u82b1\u6811\u4e1b\u4e2d\u51fa\u6ca1\uff0c\u79bb\u5f00\u8fd9\u5ea7\u5f69\u697c\u5f88\u8fdc\u4e86\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5fc3\u9053\uff1a\u201c\u8fd9\u9b54\u5973\u7684\u8eab\u6cd5\u5012\u4e5f\u5f88\u5feb\uff0c\u5c31\u5355\u51ed\u6b66\u529f\uff0c\u53f6\u51b2\u9704\u4e5f\u672a\u5fc5\u662f\u5979\u7684\u5bf9\u624b\u3002\u201d \u5ffd\u5730\u597d\u5947\u4e4b\u5fc3\u5927\u8d77\uff0c\u201c\u90a3\u5996\u5987\u4e3a\u4ec0\u4e48\u975e\u8981\u628a\u53f6\u51b2\u9704\u6740\u6389\u4e0d\u53ef\uff1f\u8fd9\u91cc\u9762\u83ab\u975e\u6709\u4ec0\u4e48\u96be\u4ee5\u544a \u4eba\u7684\u79d8\u5bc6\uff1f\u201d\u65f6\u51b2\u9704\u5192\u5145\u4ed6\u7684\u8eab\u4efd\uff0c\u4ed6\u65f6\u65f6\u51b2\u9704\u7684\u6076\u611f\u672c\u6765\u5f88\u6df1\uff0c\u4f46\u4e5f\u6df1\u5207\u611f\u5230\u5b64\u201c\u7687 \u989d\u5a18\u201d\u60f3\u8981\u8c0b\u5bb3\u53f6\u51b2\u9704\u4e4b\u540e\uff0c\u4e0d\u5982\u600e\u7684\uff0c\u5bf9\u4ed6\u7684\u6076\u611f\u7adf\u662f\u51cf\u8f7b\u4e86\u4e00\u4e9b\uff0c\u6ca1\u6709\u4ee5\u524d\u7684\u5f3a\u70c8 \u4e86\u3002\u5f53\u4e0b\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u6b63\u82e6\u4e8e\u65e0\u5904\u89c5\u90a3\u5978\u738b\uff0c\u4e0d\u5982\u5c31\u5148\u627e\u5230\u4e86\u90a3\u53f6\u51b2\u9704\u518d\u8bf4\uff0c\u4ed6\u662f \u5978\u738b\u7684\u5e72\u513f\u5b50\uff0c\u6216\u8bb8\u4f1a\u77e5\u9053\u5978\u738b\u7684\u6240\u5728\u3002\u6211\u5148\u4e0d\u58f0\u5f20\uff0c\u770b\u90a3\u9b54\u5973\u600e\u6837\u5bb3\u4ed6\uff0c\u53ef\u80fd\u8fd8\u4f1a\u63a2 \u542c\u5230\u4e00\u4e9b\u79d8\u5bc6\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u4e00\u9762\u601d\u91cf\uff0c\u4e00\u9762\u52a0\u5feb\u811a\u6b65\uff0c\u8ffd\u8e2a\u5929\u9b54\u6559\u4e3b\u3002\u4ed6\u670d\u4e86\u5929\u5fc3\u77f3\u4e4b\u540e\uff0c\u8f7b\u529f\u5df2\u6bd4 \u5929\u9b54\u6559\u4e3b\u80dc\u8fc7\u8bb8\u591a\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u4e24\u4eba\u4e4b\u95f4\u7684\u8ddd\u79bb\u8d8a\u62c9\u8d8a\u8fd1\u4e86\u3002\u5510\u52aa\u73e0\u7a46\u6015\u5979\u53d1\u73b0\uff0c\u53cd\u800c \u4e0d\u6562\u592a\u8fc7\u63a5\u8fd1\uff0c\u4e00\u76f4\u4fdd\u6301\u7740\u5341\u4e08\u5de6\u53f3\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u53ea\u89c1\u5929\u9b54\u6559\u4e3b\u7684\u8eab\u5f62\u8fdb\u4e86\u4e00\u5ea7\u5bab\u6bbf\uff0c\u5510\u52aa\u73e0\u7a46\u8ddf\u7740\u4e5f\u8d8a\u8fc7\u56f4\u5899\uff0c\u5ffd\u89c9\u6709\u4e00\u80a1\u6de1\u6de1\u7684 \u9999\u6c14\uff0c\u6c14\u5473\u5341\u5206\u53e4\u602a\uff0c\u5018\u975e\u55c5\u89c9\u7279\u522b\u7075\u654f\uff0c\u51b3\u8ba1\u55c5\u4e0d\u51fa\u6765\u3002\u5510\u52aa\u73e0\u7a46\u6df1\u77e5\u5929\u9b54\u6559\u4e3b\u5584\u4e8e \u4f7f\u6bd2\uff0c\u6015\u7740\u4e86\u5979\u7684\u9053\u513f\uff0c\u8fde\u5fd9\u53d6\u51fa\u4e00\u7247\u96ea\u83b2\uff0c\u542b\u5728\u53e3\u5185\uff0c\u6709\u5907\u65e0\u60a3\u3002 \u3000\u3000\u5bab\u6bbf\u91cc\u6b7b\u6c14\u6c89\u6c89\uff0c\u7b80\u76f4\u89c9\u5bdf\u4e0d\u51fa\u4ec0\u4e48\u58f0\u606f\uff0c\u5510\u52aa\u73e0\u7a46\u6709\u70b9\u5947\u602a\uff0c\u968f\u5373\u604d\u7136\u5927\u609f\uff0c \u201c\u662f\u4e86\uff0c\u4e00\u5b9a\u662f\u8fd9\u9b54\u5973\u5728\u7528\u8ff7\u9999\uff0c\u4f7f\u5f97\u5b88\u591c\u7684\u4eba\u660f\u8ff7\u8fc7\u53bb\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5ea7\u5bab\u6bbf\u7684\u5899\u89d2\u6709\u9897\u5927\u6811\uff0c\u5510\u52aa\u73e0\u7a46\u8eb2\u5728\u6811\u4e0a\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u53ea\u89c1\u5929\u9b54\u6559\u4e3b\u7684\u8eab\u5f62\u94bb \u8fdb\u94bb\u51fa\uff0c\u4e1c\u5f20\u897f\u671b\uff0c\u4f46\u5374\u5e76\u6ca1\u6709\u8fdb\u5165\u4efb\u4f55\u4e00\u540c\u623f\u95f4\uff0c\u4f3c\u4e4e\u8fd8\u672a\u53d1\u73b0\u53f6\u51b2\u9704\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c \u5929\u9b54\u6559\u4e3b\u5728\u4e00\u4e2a\u7a97\u53e3\u4e0b\u9762\u505c\u4e0b\u4e86\u811a\u6b65\u3002\u90a3\u68f5\u5927\u6811\u6b63\u5bf9\u7740\u8fd9\u4e2a\u7a97\u53e3\uff0c\u5929\u9b54\u6559\u4e3b\u4ece\u901a\u82b1\u7a97\u683c \u4e2d\u671b\u8fdb\u53bb\u3002\u5510\u52aa\u73e0\u7a46\u4e5f\u4ece\u7a97\u53e3\u4e0a\u65b9\u7684\u7a7a\u9699\u671b\u8fdb\u53bb\uff0c\u4e24\u4eba\u4e0d\u7ea6\u800c\u540c\u7684\u90fd\u662f\u597d\u751f\u5931\u671b\uff0c\u623f\u95f4 \u91cc\u9b3c\u5f71\u4e5f\u6ca1\u4e00\u4e2a\u3002\u5929\u9b54\u6559\u4e3b\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u5947\u602a\uff0c\u8fd9\u662f\u4ed6\u7684\u5367\u623f\uff0c\u8fd9\u4e48\u665a\u4e86\uff0c\u4ed6\u600e\u7684\u8fd8 \u4e0d\u56de\u6765\u7761\u89c9\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u4f55\u4e8b\u7687\u5a18\u6740\u6bbf\u4e0b\uff0c\u6b64\u4e2d\u60c5\u8282\u8d39\u7591\u731c\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e94\u56de\u3000\u5f1f\u5144\u76f8\u89c1\u4e0d\u76f8\u8bc6\u3000\u6069\u6028\u7ea0\u7f20\u5c1a\u672a\u660e \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e09\u5341\u4e94\u56de\u3000\u5f1f\u5144\u76f8\u89c1\u4e0d\u76f8\u8bc6\u3000\u6069\u6028\u7ea0\u7f20\u5c1a\u672a\u660e \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5728\u7a97\u5916\u5f98\u5f8a\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u5140\u662f\u672a\u89c1\u52a8\u9759\uff0c\u6b63\u60f3\u79bb\u5f00\uff0c\u5ffd\u542c\u5f97\u623f\u95f4\u5185\u201c\u8f67\u8f67\u201d \u58f0\u54cd\uff0c\u5929\u9b54\u6559\u4e3b\u5927\u4e3a\u5947\u602a\uff0c\u6025\u5fd9\u8d34\u8fd1\u7a97\u5b50\uff0c\u51dd\u795e\u5f20\u671b\uff0c\u53ea\u89c1\u623f\u95f4\u5185\u9760\u7740\u5899\u58c1\u7684\u90a3\u5f20\u5927\u5e8a\uff0c \u81ea\u884c\u79fb\u52a8\uff0c\u5411\u5916\u632a\u5f00\u4e86\u51e0\u5bf8\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u5e8a\u5e95\u4e0b\u94bb\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u6b63\u662f\u90a3\u53f6\u51b2\u9704\u3002\u539f\u6765\u8fd9 \u5e8a\u5e95\u4e0b\u6709\u4e00\u6761\u79d8\u5bc6\u5730\u9053\uff0c\u8fd9\u5f20\u5e8a\u662f\u6709\u677f\u58c1\u7684\uff0c\u677f\u58c1\u8d34\u5899\uff0c\u6070\u6070\u5835\u7740\u4ed6\u9053\u7684\u51fa\u53e3\uff0c\u6240\u4ee5\u6709 \u4eba\u4ece\u5730\u9053\u51fa\u6765\u7684\u65f6\u4faf\uff0c\u5fc5\u987b\u628a\u5927\u5e8a\u5411\u5916\u63a8\u5f00\u5c11\u8bb8\u3002 \u3000\u3000\u53f6\u51b2\u9704\u7ad9\u4e86\u8d77\u6765\uff0c\u4f38\u4e86\u4e00\u4e2a\u61d2\u8170\uff0c\u4f3c\u662f\u751a\u4e3a\u75b2\u5026\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u54a6\uff0c\u6211\u53ea\u559d\u4e86\u4e24 \u676f\u9152\uff0c\u600e\u7684\u5c31\u4f3c\u4e4e\u6709\u70b9\u9189\u610f\u4e86\u3002\u201d\u4ed6\u8d70\u4e86\u51e0\u6b65\uff0c\u5728\u4e66\u684c\u4e0a\u62ff\u8d77\u4e00\u9762\u955c\u5b50\uff0c\u5929\u9b54\u6559\u4e3b\u6697\u6697 \u7559\u795e\uff0c\u53ea\u89c1\u4ed6\u7684\u811a\u6b65\u679c\u7136\u662f\u6709\u70b9\u8dc4\u8e09\uff0c\u8138\u4e0a\u4e5f\u4f3c\u6d82\u4e86\u4e00\u5c42\u80ed\u8102\uff0c\u4e00\u7247\u6655\u7ea2\u3002\u5929\u9b54\u6559\u4e3b\u5fc3 \u4e2d\u6697\u559c\uff1a\u201c\u4ed6\u559d\u9189\u4e86\u9152\uff0c\u6211\u66f4\u5bb9\u6613\u4e0b\u624b\u4e86\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u62ff\u8d77\u955c\u5b50\u7167\u4e86\u4e00\u7167\uff0c\u5ffd\u5730\u53c8\u81ea\u8a00\u81ea\u8bed\uff1a\u201c\u5947\u602a\uff01\u6211\u771f\u7684\u5f88\u50cf\u8c37\u4e2d\u83b2\u7684\u54e5\u54e5\u3002 \u602a\u4e0d\u5f97\u7236\u738b\u8981\u6211\u5192\u5145\u4ed6\u3002\u53ef\u662f\u7236\u738b\u4e5f\u4ece\u672a\u89c1\u8fc7\u90a3\u4e2a\u5c0f\u5b50\uff0c\u4ed6\u600e\u4e48\u4f1a\u77e5\u9053\u6211\u957f\u5927\u4e4b\u540e\uff0c\u76f8 \u8c8c\u4e00\u5b9a\u4f3c\u4ed6\uff0c\u81ea\u5c0f\u5c31\u8981\u6211\u9876\u66ff\u4ed6\u7684\u540d\u5b57\uff0c\u53eb\u6211\u5728\u6c5f\u6e56\u8d70\u52a8\uff0c\u5c31\u5192\u8ba4\u662f\u53f6\u51b2\u9704\u7684\u8eab\u4efd\u5462\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5728\u5916\u9762\u5077\u542c\uff0c\u4e5f\u7981\u4e0d\u4f4f\u5fc3\u5934\u4e00\u9707\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u53ae\u95ee\u5f97\u6709\u7406\uff0c\u4e0d\u9519\uff0c\u6211 \u5c0f\u65f6\u5019\u66fe\u88ab\u5978\u738b\u7684\u722a\u7259\u64d2\u83b7\uff0c\u4f46\u662f\u90a3\u73ed\u5bb6\u4f19\uff0c\u90fd\u88ab\u65b9\u8001\u524d\u8f88\u51fb\u6bd9\u4e86\u3002\u518d\u9000\u4e00\u6b65\u8bf4\uff0c\u7eb5\u4f7f \u5176\u4e2d\u6709\u4e00\u4e24\u4e2a\u4fa5\u5e78\u672a\u6b7b\uff0c\u5c06\u6211\u7684\u76f8\u8c8c\u544a\u8bc9\u5978\u738b\uff0c\u4ed6\u627e\u4e00\u4e2a\u4e0e\u6211\u76f8\u4f3c\u7684\u5b69\u5b50\u629a\u517b\uff0c\u53ef\u662f\u957f \u5927\u4e4b\u540e\uff0c\u4e5f\u4e0d\u80fd\u5982\u6b64\u76f8\u4f3c\u5440\uff1f\u542c\u4ed6\u7684\u53e3\u6c14\uff0c\u90a3\u5978\u738b\u8981\u4ed6\u81ea\u5c0f\u5c31\u9876\u66ff\u6211\u7684\u540d\u5b57\uff0c\u7adf\u662f\u65e9\u6709 \u9884\u8c0b\u7684\u4e86\uff01\u201d \u3000\u3000\u53f6\u51b2\u9704\u7684\u9189\u610f\u4f3c\u4e4e\u8d8a\u6765\u8d8a\u6d53\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u6211\u660e\u767d\u4e86\uff01\u201d\u201c\u5f53\u5577\u201d\u58f0\u54cd\uff0c \u5c06\u90a3\u9762\u955c\u5b50\u63b7\u5f97\u7247\u7247\u788e\u88c2\uff0c\u63a9\u9762\u53eb\u9053\uff1a \u3000\u3000\u201c\u6211\u660e\u767d\u4e86\uff0c\u7236\u738b\u6536\u517b\u6211\uff0c\u5c01\u6211\u505a\u5e72\u6bbf\u4e0b\uff0c\u7ed9\u6211\u9ad8\u5b98\u539a\u7984\uff0c\u539f\u6765\u5c31\u662f\u56e0\u4e3a\u6211\u7684\u76f8\u8c8c \u4f3c\u90a3\u5c0f\u5b50\uff0c\u4ed6\u65e9\u60f3\u5230\u6709\u4eca\u65e5\u4e4b\u4e8b\uff0c\u8981\u7528\u6211\u53bb\u9a97\u53d6\u90a3\u4e2b\u5934\u7684\u79d8\u5bc6\uff0c\u597d\u9664\u53bb\u4ed6\u7684\u5fc3\u8179\u4e4b\u60a3\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u4e00\u5411\u81ea\u8d1f\uff0c\u4ee5\u4e3a\u662f\u51ed\u7740\u81ea\u5df1\u7684\u806a\u660e\u672c\u9886\u5f97\u5230\u56fd\u738b\u7684\u5ba0\u7231\u7684\uff0c\u5982\u4eca\u6709\u4e86\u51e0\u5206\u9152 \u610f\uff0c\u7a81\u7136\u60f3\u5230\u8bb8\u591a\u7591\u70b9\uff0c\u5185\u91cc\u53ef\u80fd\u53e6\u6709\u539f\u56e0\uff0c\u4e0d\u7981\u5927\u4e3a\u61ca\u4e27\uff0c\u9893\u7136\u5367\u5012\uff0c\u53c8\u5583\u5583\u81ea\u8bed\u9053\uff1a \u201c\u53ef\u662f\u6211\u4eca\u65e5\u5df2\u662f\u4e00\u8d25\u6d82\u5730\uff0c\u90a3\u4e2b\u5934\u5df2\u88ab\u5979\u7684\u54e5\u54e5\u6551\u51fa\u53bb\u4e86\u3002\u7236\u738b\u629a\u517b\u6211\u5341\u591a\u5e74\uff0c\u5018\u82e5 \u4ed6\u7684\u76ee\u7684\u53ea\u662f\u8981\u7528\u6211\u6765\u54c4\u9a97\u8c37\u4e2d\u83b2\u7684\u8bdd\uff0c\u90a3\u4e48\uff0c\u6211\u73b0\u5728\u5bf9\u4ed6\u5df2\u662f\u6beb\u65e0\u7528\u5904\u4e86\uff01\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5fc3\u9053\uff1a\u201c\u7687\u989d\u5a18\u65e9\u5c31\u5bf9\u6211\u8bf4\u8fc7\uff0c\u56fd\u738b\u5e76\u4e0d\u662f\u771f\u7684\u559c\u6b22\u4ed6\uff0c\u6211\u8fd8\u4e0d\u6562\u76f8\u4fe1\u3002 \u54c8\uff0c\u73b0\u5728\u4ed6\u4e5f\u8fd9\u4e48\u8bf4\uff0c\u770b\u6765\u7687\u989d\u5a18\u7684\u8bdd\uff0c\u5012\u5e76\u4e0d\u662f\u9a97\u6211\u7684\u4e86\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u77e5\u9053\u4e86\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u66f4\u65e0\u987e\u5fcc\uff0c\u6b63\u60f3\u4e0b\u624b\uff0c\u5ffd\u542c\u5f97\u6709\u811a\u6b65\u58f0\u5411\u8fd9\u8fb9\u8d70\u6765\uff0c\u5929 \u9b54\u6559\u4e3b\u95ea\u5230\u5047\u5c71\u77f3\u540e\uff0c\u53ea\u89c1\u6765\u7684\u662f\u4e2a\u5973\u5b50\uff0c\u5c31\u662f\u6628\u665a\u7528\u6bd2\u9488\u5c06\u5979\u51e0\u4e4e\u523a\u6b7b\u7684\u90a3\u4e2a\u6b27\u9633\u5a49\u3002 \u3000\u3000\u6b27\u9633\u5a49\u63a8\u5f00\u623f\u95e8\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5e72\u6bbf\u4e0b\uff0c\u4f60\u73b0\u5728\u8fd8\u751f\u6211\u7684\u6c14\u4e48\uff1f\u201d\u53f6\u51b2\u9704\u8df3\u4e0b\u5e8a\u6765\uff0c \u8bf4\u9053\uff1a\u201c\u59d3\u6c5f\u90a3\u5c0f\u5b50\u65e9\u5df2\u9c8d\u4e86\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\u5566\uff0c\u4f60\u8fd8\u6765\u89c1\u6211\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u9053\uff1a\u201c\u6211\u5c31\u662f\u56e0\u4e3a\u4ed6\u4eec\u5df2\u7ecf\u8131\u9669\uff0c\u624d\u613f\u610f\u5c06\u4f60\u5f53\u4f5c\u670b\u53cb\u770b\u5f85\uff0c\u524d\u6765\u770b\u4f60\u3002\u54fc\uff0c \u8981\u662f\u4f60\u5c06\u4ed6\u4eec\u5bb3\u4e86\uff0c\u6211\u8fd8\u4f1a\u653e\u8fc7\u4f60\u5417\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u591a\u8c22\uff0c\u96be\u5f97\u4f60\u8fd8\u5c06\u6211\u5f53\u4f5c\u670b\u53cb\uff0c\u90a3\u4e48\uff0c\u6211\u529d\u4f60\u8fd8\u662f\u65e9\u65e5\u56de\u5bb6\u5427\uff0c\u4f60\u5728 \u5bab\u4e2d\u662f\u4e0d\u5b9c\u518d\u4f4f\u4e0b\u53bb\u4e86\u3002\u201d\u6b27\u9633\u5a49\u9053\uff1a \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u662f\u4f60\u8bf7\u6211\u6765\u7684\uff0c\u73b0\u5728\u53c8\u8981\u64b5\u6211\u8d70\u5566\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u6211\u73b0\u5728\u662f\u5e72\u6bbf\u4e0b\uff0c\u4ee5\u540e\u8fd8\u662f\u4e0d\u662f\u5e72\u6bbf\u4e0b\uff0c\u90a3\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u4f60\u5f97\u7f6a\u4e86\u5929 \u9b54\u6559\u4e3b\uff0c\u5979\u662f\u7687\u989d\u5a18\u7684\u5fc3\u8179\uff0c\u53c8\u5584\u4e8e\u4f7f\u6bd2\uff0c\u7236\u738b\u4e5f\u5f97\u5fcc\u60ee\u5979\u51e0\u5206\uff0c\u53ea\u6015\u6211\u6ca1\u6709\u80fd\u529b\u5e87\u62a4 \u4f60\u4e86\u3002\u201d \u3000\u3000\u6b27\u9633\u5a49\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u5c45\u7136\u8fd8\u4f1a\u66ff\u6211\u7740\u60f3\uff0c\u826f\u5fc3\u8fd8\u5e76\u672a\u592a\u574f\u554a\u3002 \u3000\u3000\u90a3\u4e48\uff0c\u6211\u4e5f\u4e3a\u4f60\u8bbe\u60f3\uff0c\u4f60\u65e2\u7136\u77e5\u9053\u4e86\u81ea\u5df1\u53ef\u80fd\u5931\u52bf\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u79bb\u5f00\u8fd9\u513f\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u53eb\u6211\u53bb\u54ea\u513f\uff1f\u4f55\u51b5\u7236\u738b\u629a\u517b\u4e86\u6211\u5341\u51e0\u5e74\uff0c\u6211\u672c\u6765\u662f\u4e2a\u7a77\u82e6\u4eba\u5bb6 \u7684\u5b64\u513f\uff0c\u5e73\u767d\u5f97\u4e86\u4e00\u573a\u504c\u5927\u7684\u5bcc\u8d35\uff0c\u5373\u4f7f\u7236\u738b\u4e0d\u518d\u5ba0\u7231\u6211\uff0c\u6211\u8fd8\u662f\u8981\u62a5\u7b54\u4ed6\u7684\u6df1\u6069\u7684\u3002 \u600e\u80fd\u5728\u8fd9\u4e2a\u65f6\u5019\u629b\u5f00\u4ed6\u5462\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u51b7\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u8fd8\u8981\u62a5\u7b54\u4f60\u7236\u738b\u7684\u6069\u4e49\uff1f\u201d\u53f6\u51b2\u9704\u6012\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60 \u5f53\u6211\u662f\u4e2a\u574f\u5230\u4e0d\u53ef\u6536\u62fe\u7684\u51c9\u8584\u5c0f\u4eba\u4e48\uff1f\u4e0d\u9519\uff0c\u6211\u662f\u574f\u4eba\uff0c\u4f46\u8fd8\u4e0d\u81f3\u4e8e\u50cf\u4f60\u60f3\u8c61\u7684\u8fd9\u4e48\u574f\u3002\u201d \u3000\u3000\u6b27\u9633\u5a49\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u8bdd\u7f13\u63d0\u3002\u4f60\u8bf4\u4f60\u4e0d\u662f\u51c9\u8584\u5c0f\u4eba\uff0c\u90a3\u4e48\uff0c\u6211\u4e14\u95ee\u4f60\uff0c\u4f60\u8fd8\u8bb0\u5f97\u6211 \u7684\u59d0\u59d0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u6211\u6839\u96be\u8fc7\uff0c\u6211\u8f9c\u8d1f\u4e86\u5979\u7684\u597d\u610f\u3002\u201d\u6b27\u9633\u5a49\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u53ea \u4f1a\u5047\u6148\u60b2\uff0c\u6211\u59d0\u59d0\u7ed9\u4f60\u5bb3\u6b7b\u5566\uff01\u201d\u5ffd\u5730\u53d6\u51fa\u4e00\u7f15\u9752\u4e1d\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u59d0\u59d0\u4e34\u7ec8\u65f6\u5272\u4e0b \u6765\u7684\uff0c\u53ef\u601c\u5979\u8fd8\u6ca1\u6709\u5fd8\u8bb0\u4f60\u8fd9\u4e2a\u8d1f\u5fc3\u6c49\u5b50\uff0c\u4e34\u7ec8\u4ea4\u6258\u6211\u5988\uff0c\u8981\u6211\u5c06\u5979\u8fd9\u7f15\u9752\u4e1d\u9001\u7ed9\u4f60\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u5403\u4e86\u4e00\u60ca\uff1a\u53eb\u9053\uff1a\u201c\u4ee4\u59d0\u6b7b\u5566\uff01\u51e0\u65f6\u6b7b\u7684\uff1f\u4f60\u5728\u5bab\u4e2d\u600e\u4e48\u77e5\u9053\uff1f\u201d\u6b27\u9633\u5a49\u9053\uff1a \u201c\u6211\u7239\u5a18\u90fd\u6765\u4e86\uff0c\u4f60\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u4ed6\u6068\u900f\u4e86\u4f60\uff0c\u672c\u6765\u4e0d\u51c6\u6211\u5c06\u8fd9\u4ef6\u4e8b\u60c5\u544a\u8bc9\u4f60\u7684\u3002\u4f46\u6211 \u5374\u8981\u6765\u770b\u4e00\u770b\u4f60\u5230\u5e95\u8fd8\u6709\u6ca1\u6709\u70b9\u4eba\u5fc3\u3002\u201d \u3000\u3000\u539f\u6765\u6b27\u9633\u5a49\u7684\u59d0\u59d0\u6b27\u9633\u6e05\u88ab\u8feb\u5ac1\u7ed9\u6587\u9053\u5e86\u4e4b\u540e\uff0c\u90c1\u90c1\u5be1\u6b22\uff0c\u7ec8\u4e8e\u75c5\u6b7b\u3002\u6587\u9053\u5e84\u5219\u4ecd \u5728\u7ec8\u5357\u5c71\u6b27\u9633\u5bb6\u517b\u4f24\uff0c\u4ed6\u7684\u59bb\u5b50\u75c5\u6b7b\uff0c\u4ed6\u7684\u53d4\u7236\u6587\u5ef7\u58c1\u4e5f\u8fd8\u672a\u77e5\u9053\u3002 \u3000\u3000\u53f6\u51b2\u9704\u5f53\u5e74\u4ee5\u738b\u5b50\u7684\u8eab\u4efd\uff0c\u6d6a\u8ff9\u6c5f\u6e56\uff0c\u6625\u98ce\u5f97\u610f\uff0c\u5e74\u5c11\u98ce\u6d41\u3001\u4e0e\u6b27\u9633\u6e05\u4e4b\u604b\uff0c\u5b9e\u662f \u4ee5\u6e38\u620f\u6001\u5ea6\u5f85\u4e4b\uff0c\u8c08\u4e0d\u4e0a\u6709\u751a\u6df1\u60c5\u3002\u4f46\u6b64\u523b\u4ed6\u6b63\u662f\u5931\u610f\u7684\u65f6\u5019\uff0c\u6613\u751f\u4f24\u611f\uff0c\u9aa4\u7136\u542c\u5230\u6b27 \u9633\u6e05\u4e3a\u4ed6\u800c\u6b7b\u7684\u6d88\u606f\uff0c\u4e5f\u4e0d\u7981\u60b2\u4ece\u4e2d\u6765\uff0c\u624b\u63e1\u9752\u4e1d\uff0c\u70ed\u6cea\u6d94\u6d94\uff0c\u8ffd\u601d\u5f80\u65e5\uff0c\u6094\u6068\u91cd\u91cd\uff0c \u6084\u7136\u53f9\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6211\u53f6\u51b2\u9704\u8fd8\u6709\u8fd9\u6837\u4e00\u4f4d\u7ea2\u989c\u77e5\u5df1\uff01 \u3000\u3000\u6e05\u59b9\uff01\u6e05\u59b9\uff0c\u90fd\u662f\u6211\u5bb3\u4e86\u4f60\u4e86\uff01\u201d \u3000\u3000\u6b27\u9633\u5a49\u5728\u4e00\u65c1\u51b7\u51b7\u770b\u4ed6\uff0c\u770b\u4ed6\u54ed\u4e86\u4e00\u573a\u4e4b\u540e\uff0c\u8fd9\u624d\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u770b\u5728\u4f60\u8fd9\u526f\u773c\u6cea\u7684 \u4efd\u4e0a\u6211\u6551\u4f60\u4e00\u547d\uff01\u201d \u3000\u3000\u53f6\u51b2\u9704\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u6b27\u9633\u5a49\u51b7\u51b7\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u7684\u7236\u738b\u8981\u53d6\u4f60\u7684\u6027\u547d\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u201d\u53f6\u51b2\u9704\u5413\u5f97\u8df3\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u80e1\u8bf4\uff01\u201d \u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c9\u6d51\u8eab\u4e4f\u529b\uff0c\u53cc\u817f\u9178\u9ebb\uff0c\u4ed6\u8fd9\u4e00\u8df3\u7adf\u7136\u6536\u675f\u4e0d\u4f4f\uff0c\u8e09\u8e09\u8dc4\u8dc4\u7684\u5411\u524d\u51b2\u51fa\u51e0 \u6b65\u3002 \u3000\u3000\u6b27\u9633\u5a49\u5c06\u4ed6\u4e00\u628a\u62c9\u7740\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u67d1\u4fe1\u4e48\uff1f\u4f60\u8bd5\u60f3\u60f3\uff0c\u4f60\u53ea\u559d\u4e86\u4e24\u676f\u9152\uff0c\u600e\u7684 \u8fde\u6c14\u529b\u90fd\u6d88\u5931\u4e86\uff1f\u201d\u53f6\u51b2\u9704\u8d8a\u53d1\u5403\u60ca\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u559d\u4e86\u4e24\u676f\u9152\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u9053\uff1a\u201c\u6211\u7239\u7239\u5df2\u7ecf\u6765\u5230\u5bab\u4e2d\uff0c\u6587\u5ef7\u58c1\u5e26\u4ed6\u53bb\u8c12\u89c1\u56fd\u738b\uff0c\u56fd\u738b\u95ee\u4e86\u4ed6\u4eec\u6628\u65e5\u548c \u8c37\u4e2d\u83b2\u5144\u59b9\u52a8\u624b\u7684\u60c5\u5f62\uff0c\u6587\u5ef7\u58c1\u8bf4\u662f\u4f60\u6709\u610f\u5c06\u90a3\u4e24\u5144\u59b9\u653e\u8d70\u7684\uff0c\u56fd\u738b\u5927\u6012\uff0c\u5c31\u4ea4\u7ed9\u6587\u5ef7 \u58c1\u4e00\u4e2a\u547d\u4ee4\uff0c\u8981\u4ed6\u5c06\u4f60\u9664\u6389\u3002\u201d\u53f6\u51b2\u9704\u5927\u6012\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u6587\u5ef7\u58c1\u8fd9\u53ae\u516c\u62a5\u79c1\u4ec7\uff0c\u4ed6 \u4ee5\u524d\u5403\u6211\u6253\u4e86\u4e00\u638c\uff0c\u73b0\u5728\u631f\u5acc\u62a5\u590d\uff0c\u6211\u975e\u5411\u7236\u738b\u5206\u8fa9\u4e0d\u53ef\uff01\u201d \u3000\u3000\u6b27\u9633\u5a49\u53f9\u9053\uff1a\u201c\u4f60\u806a\u660e\u4e00\u4e16\uff0c\u600e\u7684\u7cca\u6d82\u4e00\u65f6\uff1f\u82e5\u975e\u6587\u5ef7\u58c1\u77e5\u9053\u56fd\u738b\u6709\u5fc3\u9664\u4f60\uff0c\u4ed6\u600e \u6562\u8fdb\u8c17\uff1f\u8001\u5b9e\u544a\u8bc9\u4f60\u5427\uff0c\u4f60\u559d\u7684\u90a3\u4e24\u676f\u6bd2\u9152\uff0c\u8fd8\u662f\u5728\u6587\u5ef7\u74a7\u5230\u6765\u4e4b\u524d\uff01\u201d \u3000\u3000\u53f6\u51b2\u9704\u5706\u7741\u53cc\u773c\uff0c\u5413\u5f97\u5446\u4e86\uff0c\u53ea\u542c\u5f97\u6b27\u9633\u5a49\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a \u3000\u3000\u201c\u56fd\u738b\u5fcc\u60ee\u4f60\u7684\u6b66\u529f\u4e86\u5f97\uff0c\u4e0d\u6562\u5f53\u573a\u5c06\u4f60\u6740\u5bb3\uff0c\u8fd9\u836f\u9152\u662f\u6162\u6162\u53d1\u4f5c\u7684\uff0c\u559d\u7684\u65f6\u5019\uff0c \u51b3\u4e0d\u4f1a\u53d1\u89c9\u3002\u56fd\u738b\u672c\u60f3\u5f85\u4f60\u9152\u529b\u53d1\u4f5c\u4e4b\u540e\uff0c\u53eb\u6b66\u58eb\u6765\u53d6\u4f60\u7684\u9996\u6781\u3002\u6070\u597d\u6587\u5ef7\u58c1\u548c\u6211\u7239\u7239 \u5230\u6765\uff0c\u56fd\u738b\u4e3a\u4e86\u4e07\u5143\u4e00\u5931\uff0c\u5c31\u53eb\u6587\u5ef7\u58c1\u6765\u4ee3\u66ff\u6b66\u58eb\u4e8e\u8fd9\u4e2a\u4e8b\u3002\u6211\u7239\u7239\u5bf9\u4f60\u542b\u6068\uff0c\u4e5f\u81ea\u613f \u52a9\u6587\u5ef7\u58c1\u4e00\u81c2\u4e4b\u529b\u3002\u4ed6\u4eec\u7b97\u597d\u6bd2\u9152\u6548\u529b\u53d1\u4f5c\u7684\u65f6\u8fb0\uff0c\u7ea6\u597d\u4e86\u4eca\u665a\u4e09\u66f4\u8fc7\u540e\uff0c\u6765\u5230\u6b64\u5730\uff0c \u53ea\u6015\u5c31\u8981\u5230\u4e86\uff01\u6211\u662f\u5077\u542c\u7239\u7239\u548c\u5a18\u7684\u8c08\u8bdd\u77e5\u9053\u8fd9\u4ef6\u4e8b\u7684\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u8bd5\u8fd0\u5185\u529b\uff0c\u4e00\u53e3\u6c14\u7adf\u662f\u63d0\u4e0d\u4e0a\u6765\uff0c\u4e0d\u7531\u5f97\u9762\u5982\u571f\u8272\uff0c\u53e0\u58f0\u53eb\u9053\uff1a\u201c\u6b27\u9633\u59d1\u5a18 \u6551\u6211\uff0c\u6b27\u9633\u59d1\u5a18\u6551\u6211\uff01\u201d \u3000\u3000\u6b27\u9633\u5a49\u9053\uff1a\u201c\u4e0d\u662f\u4e3a\u4e86\u6551\u4f60\uff0c\u6211\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d\u53f6\u51b2\u9704\u9053\uff1a \u3000\u3000\u201c\u8fd9\u91cc\u6709\u6761\u5730\u9053\uff0c\u4f60\u6276\u6211\u4ece\u5730\u9053\u8d70\u5427\u3002\u201d\u6b27\u9633\u5a49\u76b1\u7709\u9053\uff1a\u201c\u5730\u9053\u600e\u4e48\u80fd\u8d70\uff1f\u201d\u53f6\u51b2 \u9704\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u7701\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u771f\u662f\u7cca\u6d82\u4e86\uff0c\u7136\u5219\u600e\u4e48\u8d70\u5462\uff1f\u6211\u5df2\u7ecf\u4e0d\u80fd\u5ba1\u9ad8\u7eb5\u4f4e \u4e86\u3002\u201d\u8981\u77e5\u5730\u9053\u5bf9\u5916\u9762\u4eba\u6765\u8bf4\u662f\u4e2a\u79d8\u5bc6\uff0c\u4f46\u5bf9\u4e8e\u56fd\u738b\u6765\u8bf4\uff0c\u5374\u662f\u6beb\u65e0\u79d8\u5bc6\u53ef\u8a00\uff0c\u8bf4\u4e0d\u5b9a \u5730\u9053\u7684\u53e6\u4e00\u8fb9\u51fa\u53e3\uff0c\u65e9\u5df2\u6709\u4f0f\u5175\u57cb\u85cf\u7684\u4e86\u3002 \u3000\u3000\u6b27\u9633\u5a49\u9053\uff1a\u201c\u6765\u5427\uff0c\u6211\u80cc\u4f60\u51fa\u53bb\u3002\u201d\u53f6\u51b2\u9704\u6709\u70b9\u5c34\u5c2c\uff0c\u4e5f\u6709\u70b9\u60ed\u6127\uff0c\u60f3\u4e0d\u5230\u81ea\u5df1\u7adf \u843d\u5230\u8fd9\u822c\u5730\u6b65\uff0c\u8981\u4ef0\u4ed7\u4e00\u4e2a\u6b66\u529f\u8fdc\u4e0d\u5982\u4ed6\u7684\u5973\u5b50\u6551\u547d\u3002\u518d\u60f3\u60f3\u5341\u8f7d\u7e41\u534e\uff0c\u6d51\u5982\u4e00\u68a6\uff0c\u4ece \u4eca\u4e4b\u540e\uff0c\u4e0d\u77e5\u4f55\u5904\u5b89\u8eab\uff0c\u4e0d\u7981\u767e\u611f\u4e1b\u751f\uff0c\u6cea\u73e0\u513f\u5728\u773c\u7736\u6253\u8f6c\u3002 \u3000\u3000\u6b27\u9633\u5a49\u9053\uff1a\u201c\u522b\u5a46\u5a46\u5988\u5988\u4e86\uff0c\u6765\u5427\u3002\u201d\u53f6\u51b2\u9704\u6b63\u8981\u8fc7\u53bb\uff0c\u5ffd\u542c\u5f97\u6b27\u9633\u5a49\u5931\u58f0\u53eb\u9053\uff1a \u201c\u4e0d\u597d\uff0c\u6709\u4eba\u6765\u4e86\uff01\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5929\u9b54\u6559\u4e3b\u5df2\u662f\u4e00\u638c\u51fb\u788e\u7a97\u6237\uff0c\u7a9c\u5165\u623f\u4e2d\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u5e72\u6bbf\u4e0b\uff0c\u73b0\u5728\u8981 \u8d70\u5df2\u7ecf\u8fdf\u4e86\uff01\u201d\u539f\u6765\u5929\u9b54\u6559\u4e3b\u521a\u624d\u4e4b\u6240\u4ee5\u4e0d\u5373\u52a8\u624b\uff0c\u4e43\u662f\u5979\u6839\u672c\u6ca1\u628a\u6b27\u9633\u5a49\u653e\u5728\u773c\u5185\uff0c \u5b58\u5fc3\u8981\u5077\u542c\u5979\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u5f85\u542c\u5230\u4e86\u56fd\u5de5\u4e5f\u8981\u6740\u53f6\u51b2\u9704\u7684\u79d8\u5bc6\u4e4b\u540e\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u518d\u65e0\u987e\u5fcc\uff0c \u8fd9\u624d\u8fdb\u6765\u3002 \u3000\u3000\u53f6\u51b2\u9704\u5403\u60ca\u9053\uff1a\u201c\u600e\u4e48\u662f\u4f60\uff1f\u4f60\u8981\u4ec0\u4e48\uff0c\u5c3d\u53ef\u5546\u91cf\uff01\u201d\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u4ec0 \u4e48\uff0c\u603b\u6bd4\u4e0d\u4e0a\u7687\u989d\u5a18\u7ed9\u6211\u7684\u5185\u5e93\u9501\u5319\u5427\uff1f\u201d\u53f6\u51b2\u9704\u53eb\u9053\uff1a\u201c\u600e\u4e48\uff0c\u7687\u989d\u5a18\u4e5f\u8981\u6740\u6211\uff1f\u6211 \u53ef\u6ca1\u6709\u5f97\u7f6a\u8fc7\u5979\u5440\uff01\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u5f97\u7f6a\u5979\uff0c\u6211\u53ef\u4e0d\u7ba1\uff01\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u5582\uff0c\u6211\u628a\u5927\u4e58\u822c\u82e5\u638c\u6cd5\u7684\u79d8\u672c\u7ed9\u4f60\u5982\u4f55\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e5f \u4e0d\u4e00\u5b9a\u8981\u53d6\u4f60\u7684\u6027\u547d\uff0c\u4f46\u4f60\u5f97\u628a\u5de6\u8033\u5272\u4e0b\u6765\u7ed9\u6211\uff0c\u600e\u4e48\uff0c\u4f60\u6ca1\u6c14\u529b\u81ea\u5df1\u52a8\u624b\u4e86\uff0c\u662f\u5417\uff1f \u6211\u6765\u5e2e\u4f60\u52a8\u624b\uff1a\u6b27\u9633\u59d1\u5a18\uff0c\u501f\u4f60\u7684\u5229\u5251\u4e00\u7528\u3002\u201d \u3000\u3000\u6b27\u9633\u5a49\u521a\u62d4\u5251\u51fa\u9798\u5929\u9b54\u6559\u4e3b\u5df2\u5230\u4e86\u5979\u7684\u8eab\u524d\uff0c\u6b27\u9633\u5a49\u66f4\u4e0d\u7b54\u8bdd\uff0c\u201c\u5587\u201d\u7684\u4e00\u5251\u5c31\u523a \u51fa\u53bb\uff0c\u5929\u9b54\u6559\u4e3b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u524d\u65e5\u4f60\u7528\u6bd2\u9488\u523a\u6211\uff0c\u6211\u8fd8\u672a\u66fe\u4e0e\u4f60\u7b97\u5e10\u5462\uff01\u201d\u8863\u8896\u4e00\u6325\uff0c \u5df2\u5377\u7740\u4e86\u5251\u67c4\u3002\u5ffd\u542c\u5f97\u201c\u55e4\u55e4\u201d\u58f0\u54cd\uff0c\u4e00\u4e1b\u6bd2\u9488\u98de\u5c04\u51fa\u6765\uff0c\u539f\u6765\u5251\u67c4\u4e2d\u7a7a\uff0c\u88c5\u6709\u673a\u5173\u7684\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u51b7\u7b11\u9053\uff1a\u201c\u7c73\u7c92\u4e4b\u73e0\u3002\u4e5f\u653e\u5149\u534e\u3002\u201d\u53ea\u89c1\u90a3\u4e00\u4e1b\u6bd2\u9488\u5728\u5929\u9b54\u6559\u4e3b\u80f8\u524d\u7ed3\u6210 \u4e00\u56e2\uff0c\u5374\u6ca1\u6709\u4e00\u53e3\u6bd2\u9488\u523a\u7834\u5979\u7684\u8863\u88f3\u3002\u539f\u6765\u5929\u9b54\u6559\u4e3b\u524d\u65e5\u5403\u4e86\u4e00\u6b21\u4e8f\u4e4b\u540e\uff0c\u65e9\u5df2\u6709\u4e86\u9632 \u5907\uff0c\u5979\u80f8\u53e3\u6697\u85cf\u4e00\u5757\u6444\u94c1\u77f3\uff0c\u78c1\u529b\u6781\u5f3a\uff0c\u6bd2\u9488\u9694\u7740\u8863\u88f3\uff0c\u5df2\u88ab\u5438\u4f4f\u4e86\u3002\u5929\u9b54\u6559\u4e3b\u54c8\u54c8\u4e00 \u7b11\uff0c\u65e9\u5df2\u593a\u4e86\u6b27\u9633\u5a49\u7684\u4f69\u5251\uff0c\u4fe1\u624b\u53c8\u70b9\u4e86\u5979\u7684\u7a74\u9053\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u624b\u6301\u5229\u5251\uff0c\u4e00\u6b65\u4e00\u6b65\u5411\u53f6\u51b2\u9704\u8d70\u8fd1\uff0c\u5a07\u58f0\u7b11\u9053\uff1a \u3000\u3000\u201c\u5e72\u6bbf\u4e0b\uff0c\u4f60\u613f\u610f\u820d\u5f03\u4e00\u53ea\u8033\u6735\u8fd8\u662f\u4e00\u9897\u5934\u9885\uff1f\u82e5\u60f3\u4fdd\u5168\u9996\u7ea7\uff0c\u90a3\u5c31\u5feb\u628a\u5927\u4e58\u822c\u82e5 \u638c\u6cd5\u7684\u79d8\u672c\u62ff\u6765\u5427\uff0c\u6211\u8f7b\u8f7b\u524a\u6389\u4f60\u7684\u8033\u6735\uff0c\u5305\u4f60\u4e0d\u75db\u3002\u201d\u90a3\u526f\u795e\u6c14\uff0c\u6d3b\u50cf\u732b\u6349\u7740\u4e86\u8001\u9f20\uff0c \u5728\u672a\u5403\u8001\u9f20\u4e4b\u524d\uff0c\u8981\u5c3d\u60c5\u620f\u5f04\u4e2a\u591f\u3002 \u3000\u3000\u53f6\u51b2\u9704\u672c\u60f3\u4ee5\u79d8\u672c\u6362\u53d6\u6027\u547d\uff0c\u4f46\u542c\u5f97\u5929\u9b54\u6559\u4e3b\u8fd8\u662f\u8981\u5272\u4ed6\u7684\u8033\u6735\uff0c\u4e0d\u7531\u5f97\u6012\u6c14\u586b\u80f8\u3002 \u8981\u77e5\u4ed6\u9a84\u50b2\u60ef\u4e86\uff0c\u5c82\u7518\u53d7\u8fb1\uff1f\u5f53\u4e0b\u6068\u6068\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u6740\u4fbf\u6740\uff0c\u4f55\u5fc5\u591a\u8a00\uff01\u201d\u5929\u9b54\u6559\u4e3b\u7b11 \u9053\uff1a\u201c\u597d\uff0c\u77a7\u4e0d\u51fa\u4f60\u5012\u8fd8\u6709\u70b9\u9aa8\u5934\u3002\u597d\u5427\uff0c\u6211\u5c31\u53ea\u524a\u4e0b\u4f60\u4e00\u53ea\u8033\u6735\uff0c\u5927\u4e58\u822c\u82e5\u638c\u7684\u79d8\u672c \u6211\u81ea\u5df1\u4f1a\u641c\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u72e0\u72e0\u7684\u4e00\u54ac\u7259\uff0c\u4e00\u5934\u5c31\u5411\u5979\u7684\u5251\u5c16\u649e\u53bb\uff0c\u5929\u9b54\u6559\u4e3b\u8f7b\u8f7b\u4e00\u95ea\uff0c\u53f6\u51b2\u9704\u649e\u4e86\u4e2a \u7a7a\uff0c\u5df2\u7ed9\u5979\u626d\u7740\u4e86\u624b\u81c2\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u4f60\u662f\u6253\u7b97\u5b81\u6b7b\u4e0d\u8fb1\u662f\u5417\uff1f\u90a3\u53c8\u4f55\u5fc5\u5982\u6b64\uff01 \u3000\u3000\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u6b7b\u4e86\u4e0d\u592a\u53ef\u60dc\u5417\uff1f\u597d\uff0c\u6211\u7d22\u6027\u518d\u5356\u4f60\u4e00\u4e2a\u4ea4\u60c5\uff0c\u53ea\u5272\u6389\u4f60\u4e00\u7247\u8033\u5c16\u3002\u201d \u5251\u5c16\u4e00\u6643\uff0c\u500f\u5730\u5c31\u5411\u53f6\u51b2\u9704\u7684\u8033\u6735\u5207\u4e0b\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u539f\u6765\u662f\u5510\u52aa\u73e0\u7a46\u6298\u4e0b\u4e00\u6bb5\u6811\u679d\uff0c\u7ea6\u624b\u6307\u822c\u957f\u77ed\uff0c\u5f53\u4f5c\u77ed\u7bad\u5c04 \u4eba\uff0c\u5c06\u5929\u9b54\u6559\u4e3b\u7684\u957f\u5251\u5f39\u5f00\u4e86\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5927\u5403\u4e00\u60ca\uff0c\u8f6c\u8eab\u4e00\u671b\uff0c\u5510\u52aa\u73e0\u7a46\u5df2\u8fdb\u5165\u623f\u4e2d\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u628a\u5251\u6254\u4e0b\uff0c\u7ed9 \u6211\u6eda\u5f00\uff0c\u6211\u4e0d\u8bb8\u4f60\u5bb3\u4eba\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5ffd\u5730\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff1f\u539f\u6765\u662f\u4f60\uff01\u8fd9\u5012\u5947\u4e86\uff01 \u3000\u3000\u4ed6\u5192\u5145\u4f60\u7684\u8eab\u4efd\uff0c\u51e0\u6b21\u4e09\u756a\uff0c\u8981\u5bb3\u4f60\u4eec\u5144\u59b9\uff0c\u600e\u4e48\u4f60\u53cd\u800c\u7ed9\u4ed6\u5f53\u8d77\u4fdd\u9556\u6765\u4e86\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6211\u7684\u4e8b\u60c5\uff0c\u4e0d\u7528\u4f60\u7ba1\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5929\u9b54\u6559\u4e3b\u628a\u624b\u4e00\u626c\uff0c\u4e00\u80a1\u7d2b\u6c14\uff0c \u4ece\u5979\u8896\u4e2d\u5c04\u51fa\uff0c\u5979\u6b63\u662f\u60f3\u9017\u5510\u52aa\u73e0\u7a46\u8bf4\u8bdd\uff0c\u51b7\u4e0d\u9632\u7684\u5c31\u7528\u6bd2\u70df\u6697\u88ad\u3002 \u3000\u3000\u5c82\u77e5\u5510\u52aa\u73e0\u7a46\u7684\u529f\u529b\u5df2\u662f\u4eca\u975e\u6614\u6bd4\uff0c\u800c\u4e14\u53e3\u4e2d\u53c8\u542b\u7740\u5929\u5c71\u96ea\u83b2\uff0c\u5929\u9b54\u6559\u4e3b\u8fd9\u80a1\u6bd2\u70df \u867d\u7136\u5389\u5bb3\uff0c\u5374\u662f\u65e0\u5948\u4ed6\u4f55\u3002\u5510\u52aa\u73e0\u7a46\u5927\u6012\uff0c\u7acb\u5373\u4f7f\u51fa\u9694\u7a7a\u70b9\u7a74\u7684\u529f\u592b\uff0c\u4e00\u6307\u5411\u5979\u70b9\u53bb\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7684\u6b66\u529f\u672c\u6765\u4e0e\u5510\u52aa\u73e0\u7a46\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u89c1\u4ed6\u4f38\u6307\u6447\u70b9\uff0c\u72b9\u672a\u5728\u610f\uff0c\u4ecd\u7136\u6325\u8896 \u62cd\u51fa\u3002\u54ea\u77e5\u5510\u52aa\u73e0\u79fb\u670d\u98df\u4e86\u5929\u5fc3\u77f3\u4e4b\u540e\uff0c\u5e73\u6dfb\u4e86\u4e8c\u5341\u5e74\u529f\u529b\uff0c\u5df2\u6bd4\u539f\u6765\u7684\u529f\u529b\u5f3a\u4e86\u4e00\u500d \u6709\u591a\uff0c\u6307\u529b\u6fc0\u8361\uff0c\u55e4\u55e4\u6709\u58f0\uff0c\u5929\u9b54\u6559\u4e3b\u80c1\u4e0b\u4e00\u9ebb\uff0c\u9669\u9669\u8dcc\u5012\u3002\u624b\u81c2\u4e5f\u767b\u65f6\u9178\u8f6f\u4e4f\u529b\uff0c\u62cd \u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5fc3\u5934\u5927\u9707\uff0c\u5fc3\u9053\uff1a\u201c\u54ce\u5440\uff0c\u600e\u7684\u5728\u4e00\u591c\u4e4b\u95f4\uff0c\u4ed6\u7684\u6b66\u529f\u7adf\u7cbe\u8fdb\u5982\u65af\uff1f\u83ab\u975e \u4e5f\u662f\u670d\u98df\u4e86\u5929\u5fc3\u77f3\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u8fd9\u4e00\u6307\u70b9\u5979\u4e0d\u5012\uff0c\u4e5f\u6709\u70b9\u8be7\u5f02\uff0c\u539f\u6765\u8fd9\u662f\u4ed6\u529f\u529b\u9aa4\u957f\uff0c\u7f61\u6c14\u867d\u7136\u7ec3\u6210\uff0c\u4e00 \u65f6\u95f4\u5c1a\u672a\u80fd\u8fd0\u7528\u81ea\u5982\u7684\u7f18\u6545\u3002\u5929\u9b54\u6559\u4e3b\u4f55\u7b49\u6e9c\u6ed1\uff0c\u8d81\u4ed6\u4e00\u6014\u4e4b\u9645\uff0c\u7acb\u5373\u53c8\u98de\u51fa\u4e86\u6bd2\u96fe\u91d1 \u9488\u70c8\u7130\u5f39\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u8fd0\u638c\u5982\u98ce\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u4f7f\u7684\u5374\u662f\u622a\u7136\u4e0d\u540c\u7684\u62db\u6570\uff0c\u5de6\u638c\u8f7b\u8f7b\u4e00\u62cd\uff0c\u89e3\u5f00\u4e86 \u6b27\u9633\u5a49\u7684\u7a74\u9053\uff0c\u53f3\u638c\u5374\u4ee5\u6700\u521a\u731b\u7684\u5927\u4e58\u822c\u82e5\u638c\u529b\uff0c\u5bf9\u51c6\u6bd2\u96fe\u91d1\u9488\u70c8\u7130\u5f39\u98de\u6765\u7684\u65b9\u5411\u62cd\u53bb\u3002 \u53ea\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u74e6\u7247\u7eb7\u843d\u5982\u96e8\u3002 \u3000\u3000\u539f\u6765\u662f\u90a3\u9897\u70c8\u7130\u5f39\u88ab\u4ed6\u7684\u521a\u731b\u638c\u529b\u5411\u4e0a\u63a8\u9001\uff0c\u7adf\u628a\u5c4b\u9876\u70b8\u7a7f\u4e86\u4e00\u4e2a\u5927\u6d1e\u3002\u4e5f\u5e78\u4e8f\u4ed6 \u5e94\u4ed8\u5f97\u5b9c\uff0c\u4ee4\u8fd9\u9897\u70c8\u7130\u5f39\u5728\u5c4b\u9876\u4e0a\u7a7a\u7206\u70b8\uff0c\u5018\u82e5\u5728\u5c4b\u5185\u7206\u70b8\u7684\u8bdd\uff0c\u4ed6\u672c\u4eba\u867d\u53ef\u65e0\u59a8\uff0c\u53f6 \u51b2\u9704\u529f\u529b\u5df2\u5931\uff0c\u5374\u4e0d\u514d\u8981\u53d7\u5230\u6bd2\u96fe\u7684\u4fb5\u5bb3\u4e86\u3002 \u3000\u3000\u4f46\u662f\u6bd2\u96fe\u867d\u7136\u5728\u5c4b\u9876\u4e0a\u7a7a\u88ab\u98ce\u5439\u6563\uff0c\u90a3\u71c3\u70e7\u7740\u7684\u5f39\u7247\u5374\u6709\u51e0\u7247\u843d\u4e86\u4e0b\u6765\uff0c\u6070\u597d\u843d\u5728 \u90a3\u5f20\u5e8a\u4e0a\uff0c\u5e8a\u5e10\u88ab\u8925\u7740\u706b\u5373\u71c3\uff0c\u767b\u65f6\u53d1\u51fa\u4e86\u878d\u878d\u7684\u706b\u5149\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u89c1\u5510\u52aa\u73e0\u7a46\u5982\u6b64\u5389\u5bb3\uff0c\u6b63\u5728\u60ca\u60f6\uff0c\u5ffd\u542c\u5f97\u4e24\u58f0\u957f\u5578\uff0c\u706b\u5149\u4e2d\u7a9c\u8fdb\u4e86\u4e24\u4e2a\u4eba \u6765\uff0c\u6b63\u662f\u6587\u5ef7\u58c1\u4e0e\u6b27\u9633\u4ef2\u548c\u3002\u5929\u9b54\u6559\u4e3b\u559c\u51fa\u671b\u5916\uff0c\u8fde\u5fd9\u558a\u9053\uff1a\u201c\u6587\u526f\u6559\u4e3b\uff0c\u4f60\u6765\u5f97\u6b63\u597d\uff0c \u5feb\u628a\u8fd9\u5c0f\u5b50\u62ff\u4e0b\u3002\u201d \u3000\u3000\u6587\u5ef7\u58c1\u4e00\u89c1\u4e0d\u662f\u6c5f\u6d77\u5929\uff0c\u65e9\u5df2\u653e\u4e0b\u4e86\u5fc3\u3002\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6559\u4e3b\uff0c\u4f60\u4e5f\u6765\u4e86\uff1f\u4f60\u653e\u5fc3\uff0c \u8fd9\u5c0f\u5b50\u662f\u6211\u624b\u4e0b\u8d25\u5c06\uff0c\u5931\u8dd1\u4e0d\u4e86\u3002\u201d\u5929\u9b54\u6559\u4e3b\u53eb\u9053\uff1a\u201c\u6587\u526f\u6559\u4e3b\uff0c\u4e0d\u53ef\u8f7b\u654c\uff0c\u5168\u529b\u65bd\u4e3a\uff01\u201d \u8bdd\u72b9\u672a\u4e86\uff0c\u72c2\u98d9\u9aa4\u8d77\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u4ed6\u4eec\u4e24\u4eba\u5df2\u5bf9\u4e86\u4e00\u638c\uff01 \u3000\u3000\u524d\u65e5\u5728\u90a3\u5c9b\u4e0a\uff0c\u6587\u5ef7\u58c1\u66fe\u4e0e\u5510\u52aa\u73e0\u7a46\u4ea4\u8fc7\u4e00\u6b21\u624b\uff0c\u90a3\u65f6\u5510\u52aa\u73e0\u7a46\u5c1a\u672a\u670d\u98df\u5929\u5fc3\u77f3\uff0c \u81ea\u7136\u4e0d\u662f\u6587\u5ef7\u58c1\u7684\u5bf9\u624b\uff0c\u4ec5\u4ec5\u80fd\u5e94\u4ed8\u4e09\u638c\uff0c\u7b2c\u56db\u638c\u4fbf\u5e94\u4ed8\u4e0d\u6765\u3002\u6587\u5ef7\u58c1\u54ea\u91cc\u5c06\u4ed6\u653e\u5728\u5fc3 \u4e0a\uff0c\u4f46\u56e0\u5929\u9b54\u6559\u4e3b\u90d1\u91cd\u5429\u5490\uff0c\u4ed6\u4e5f\u7528\u4e86\u516b\u4e5d\u5206\u529f\u529b\u3002 \u3000\u3000\u53cc\u638c\u4e00\u78b0\uff0c\u5510\u52aa\u73e0\u7a46\u7684\u638c\u529b\u6709\u5982\u6392\u5c71\u5012\u6d77\u800c\u6765\uff0c\u6587\u5ef7\u58c1\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u624d\u77e5\u9053\u5389\u5bb3\uff0c \u8fde\u5fd9\u5168\u529b\u65bd\u4e3a\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u62db\u67b6\u5f97\u4f4f\u3002 \u3000\u3000\u539f\u6765\u6587\u5ef7\u58c1\u5df2\u7ec3\u6210\u4e86\u201c\u4e09\u8c61\u5f52\u5143\u201d\u7684\u8d85\u5353\u795e\u529f\uff0c\u5f53\u5e74\u4ed6\u4e0e\u91d1\u4e16\u9057\u8f83\u91cf\uff0c\u4e5f\u8fd8\u53ef\u4ee5\u786c \u63a5\u91d1\u4e16\u9057\u6570\u638c\uff0c\u5510\u52aa\u73e0\u7a46\u867d\u7136\u5e73\u6dfb\u4e86\u4e8c\u5341\u5e74\u529f\u529b\uff0c\u4f46\u6bd4\u8d77\u5e08\u7236\uff0c\u7a76\u7adf\u8fd8\u662f\u4e0d\u5982\uff0c\u6240\u4ee5\u6587 \u5ef7\u58c1\u5168\u529b\u65bd\u4e3a\uff0c\u8fd8\u53ef\u52c9\u5f3a\u62db\u67b6\u3002\u4f46\u8fd9\u65f6\u53cc\u65b9\u771f\u529b\u5df2\u7ecf\u63a5\u89e6\uff0c\u8c01\u90fd\u4e0d\u80fd\u64a4\u638c\u3002\u6587\u5ef7\u58c1\u53ea\u89c9 \u5bf9\u65b9\u7684\u5185\u529b\u6e90\u6e90\u800c\u6765\uff0c\u4f3c\u4e4e\u65e0\u7a77\u65e0\u5c3d\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u53eb\u82e6\uff01 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u89c1\u6587\u5ef7\u58c1\u5c1a\u53ef\u52c9\u5f3a\u652f\u6491\uff0c\u5374\u662f\u5927\u4e3a\u6b23\u6170\uff0c\u8d81\u6b64\u65f6\u673a\uff0c\u4fbf\u5411\u53f6\u51b2\u9704\u6251\u53bb\uff0c\u6b27 \u9633\u5a49\u62a2\u5feb\u4e00\u6b65\uff0c\u62fe\u8d77\u5929\u9b54\u6559\u4e3b\u521a\u624d\u88ab\u5510\u52aa\u73e0\u7a46\u5f39\u843d\u7684\u90a3\u53e3\u5229\u5251\uff0c\u6321\u5728\u53f6\u51b2\u9704\u8eab\u524d\u3002\u5530\u5530 \u5530\uff0c\u8fde\u73af\u4e09\u5251\uff0c\u5251\u5251\u90fd\u662f\u523a\u5411\u5929\u9b54\u6559\u4e3b\u7684\u8981\u5bb3\u7a74\u9053\uff01 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7684\u672c\u9886\u5f53\u7136\u6bd4\u6b27\u9633\u5a49\u9ad8\u51fa\u8bb8\u591a\uff0c\u521a\u624d\u4ea4\u624b\u4e0d\u8fc7\u4e09\u62db\uff0c\u5979\u5c31\u628a\u6b27\u9633\u5a49\u7684\u4f69\u5251 \u62a2\u53bb\uff0c\u4f46\u6b64\u65f6\u60c5\u52bf\u5df2\u662f\u5927\u5927\u4e0d\u540c\uff0c\u6b27\u9633\u5a49\u8fd9\u8fde\u73af\u4e09\u5251\u7adf\u628a\u5929\u9b54\u6559\u4e3b\u903c\u5f97\u6709\u70b9\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u8fd9\u91cc\u9762\u6709\u4e09\u4e2a\u539f\u56e0\uff0c\u4e00\u6765\u662f\u5929\u9b54\u6559\u4e3b\u521a\u521a\u88ab\u5510\u52aa\u73e0\u7a46\u7528\u9694\u7a7a\u70b9\u7a74\u7684\u795e\u529f\u70b9\u4e86\u4e00\u6307\uff0c \u867d\u7136\u672a\u80fd\u5c01\u95ea\u5979\u7684\u7a74\u9053\uff0c\u4f46\u4ea6\u5df2\u4ee4\u5979\u6c14\u8109\u4e0d\u8212\uff0c\u624b\u811a\u5f53\u7136\u4e0d\u53ca\u539f\u6765\u7684\u7075\u6d3b\uff1a\u4e8c\u6765\u662f\u6b27\u9633 \u4ef2\u548c\u5df2\u7ecf\u6765\u5230\uff0c\u5929\u9b54\u6559\u4e3b\u591a\u5c11\u8981\u7ed9\u4ed6\u4e00\u70b9\u60c5\u9762\uff0c\u56e0\u800c\u4e5f\u5c31\u591a\u4e86\u4e00\u5c42\u987e\u5fcc\uff0c\u4e0d\u6562\u65bd\u5c55\u8fa3\u624b\uff1a \u4e09\u6765\u6b27\u9633\u5a49\u90a3\u8fde\u73af\u4e09\u5251\uff0c\u4e43\u662f\u8c41\u4e86\u6027\u547d\uff0c\u62fc\u7740\u4e24\u8d25\u4f46\u4f24\u7684\u5251\u6cd5\uff0c\u786e\u5b9e\u4e5f\u51cc\u5389\u975e\u5e38\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u559d\u9053\uff1a\u201c\u5a49\u513f\uff0c\u5feb\u4f4f\u624b\uff0c\u522b\u80e1\u95f9\uff01\u4ed6\u4e0d\u662f\u4ec0\u4e48\u5e72\u6bbf\u4e0b\u5566\uff0c\u662f\u56fd\u738b\u7684\u547d\u4ee4\u8981 \u6211\u4eec\u6765\u6740\u4ed6\u7684\u3002\u4f60\u8fd8\u62a4\u7740\u4ed6\u5e72\u561b\uff1f\u201d\u4ed6\u4e00\u9762\u65a5\u8d23\u5973\u513f\uff0c\u4e00\u9762\u4fbf\u8d70\u4e0a\u524d\u53bb\u3002\u53f6\u51b2\u9704\u77e5\u9053\u4ed6 \u7684\u5389\u5bb3\uff0c\u5fc3\u4e2d\u5927\u6050\uff0c\u6a2a\u638c\u62a4\u80f8\uff0c\u62fc\u547d\u60f3\u628a\u5185\u529b\u63d0\u4e0a\u6765\uff0c\u53ef\u662f\u8179\u4e2d\u5374\u4f3c\u7a7a\u8361\u8361\u7684\uff0c\u54ea\u91cc\u8fd8 \u80fd\u5c06\u771f\u6c14\u51dd\u805a\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u5374\u4e5f\u6709\u51e0\u5206\u987e\u5fcc\u4ed6\u7684\u5927\u4e58\u822c\u82e5\u638c\uff0c\u4e0d\u77e5\u90a3\u6bd2\u9152\u6548\u529b\u5982\u4f55\uff0c\u4e00\u65f6\u95f4\u5012\u4e5f\u4e0d\u6562 \u592a\u8fc7\u9c81\u83bd\uff0c\u5c31\u5728\u4ed6\u8fd0\u8db3\u5185\u52b2\uff0c\u6b63\u8981\u51c6\u5907\u53d1\u638c\u4e4b\u9645\uff0c\u6b27\u9633\u5a49\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u8bf7\u4f60\u770b\u5728 \u6b7b\u4e86\u7684\u59d0\u59d0\u4efd\u4e0a\uff0c\u4e0d\u8981\u5bb3\u53f6\u516c\u5b50\u3002\u201d\u5979\u5728\u8bf4\u8fd9\u51e0\u53e5\u8bdd\u7684\u65f6\u5019\uff0c\u773c\u4e2d\u6cea\u5149\u83b9\u7136\uff0c\u58f0\u97f3\u51c4\u82e6 \u4e4b\u6781\uff0c\u6b27\u9633\u4ef2\u548c\u5fc3\u91cc\u4e00\u9178\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u63d0\u4f60\u59d0\u59d0\u4e5f\u8fd8\u7f62\u4e86\u3002\u4f60\u59d0\u59d0\u5c31\u662f\u4ed6\u5bb3\u6b7b\u7684\uff0c \u4f60\u600e\u80fd\u8fd8\u5e2e\u8fd9\u65e0\u4e49\u4e4b\u4eba\uff01\u201d \u3000\u3000\u6b27\u9633\u5a49\u9053\uff1a\u201c\u53ef\u662f\u59d0\u59d0\u4e34\u7ec8\u7684\u65f6\u5019\u8bf4\u8fc7\u4ec0\u4e48\u8bdd\u6765\uff0c\u7239\u7239\uff0c\u4f60\u90a3\u65f6\u662f\u5728\u59d0\u59d0\u8eab\u8fb9\u7684\uff0c \u6211\u53ea\u662f\u542c\u5f97\u5988\u5988\u7684\u8f6c\u8ff0\uff0c\u5df2\u89c9\u5fc3\u9178\uff0c\u7239\u7239\uff0c\u96be\u9053\u4f60\u5c31\u4e0d\u80fd\u987e\u5168\u7236\u5973\u4e4b\u60c5\uff0c\u6210\u5168\u59d0\u59d0\u7684\u5fc3 \u613f\uff1f\u201d\u6b27\u9633\u4ef2\u548c\u6c89\u541f\u4e0d\u8bed\uff0c\u6b27\u9633\u5a49\u53c8\u9053\uff1a\u201c\u7239\u7239\uff0c\u5018\u82e5\u4f60\u4eb2\u624b\u6740\u4e86\u59d0\u59d0\u5fc3\u7231\u7684\u4eba\uff0c\u59d0\u59d0 \u5728\u6cc9\u4e0b\u5c82\u80fd\u7791\u76ee\uff1f\u201d\u8bdd\u8bf4\u81f3\u6b64\uff0c\u7eb5\u7136\u6b27\u9633\u4ef2\u548c\u4f55\u7b49\u5fcd\u5fc3\uff0c\u4e5f\u4e0d\u7531\u5f97\u8001\u6cea\u76c8\u7736\u3002\u5f53\u4e0b\u8f6c\u8fc7 \u4e86\u8eab\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u4f9d\u4f60\u4e00\u6b21\uff0c\u6211\u4e0d\u4eb2\u624b\u6740\u4ed6\uff0c\u522b\u4eba\u6740\u4ed6\uff0c\u6211\u53ef\u4e0d\u7ba1\uff01\u201d\u6b27\u9633\u5a49\u77e5\u9053 \u7236\u4eb2\u5fc3\u610f\u5df2\u51b3\uff0c\u96be\u518d\u8bf7\u6c42\uff0c\u552f\u6709\u62fc\u547d\u62b5\u6321\u5929\u9b54\u6559\u4e3b\u7684\u653b\u51fb\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u6b27\u9633\u524d\u8f88\u653e\u5fc3\uff0c\u6211\u4e0b\u4f1a\u4f24\u4e86\u4ee4\u5a9b\uff0c\u8bf7\u4f60\u53bb\u76f8\u52a9\u6587\u5148\u751f\u5427\uff01\u201d\u6b27\u9633 \u5a49\u7684\u6b66\u529f\u8fdc\u8fdc\u4e0d\u5982\u5929\u9b54\u6559\u4e3b\uff0c\u5982\u4eca\u6b27\u9633\u4ef2\u548c\u5df2\u7ecf\u8a00\u660e\u4e0d\u518d\u63d2\u624b\uff0c\u4efb\u7531\u65c1\u4eba\u6740\u90a3\u53f6\u51b2\u9704\uff0c \u5929\u9b54\u6559\u4e3b\u53bb\u4e86\u4e00\u5c42\u987e\u8651\uff0c\u540c\u65f6\uff0c\u5728\u8fd9\u65f6\u95f4\u4e4b\u5185\uff0c\u5979\u4ea6\u5df2\u8c03\u5300\u6c14\u606f\uff0c\u529f\u529b\u53c8\u6062\u590d\u4e86\u51e0\u5206\uff0c \u6b27\u9633\u5a49\u4f7f\u5c3d\u4e86\u5403\u5976\u7684\u6c14\u529b\uff0c\u62b5\u6321\u4e86\u5341\u4f59\u62db\uff0c\u5251\u6cd5\u5df2\u662f\u51cc\u4e71\u65e0\u7ae0\uff0c\u88ab\u5929\u9b54\u6559\u4e3b\u7684\u638c\u529b\u7f69\u4f4f\uff01 \u3000\u3000\u53f6\u51b2\u9704\u60f3\u4e0d\u5230\u6b27\u9633\u5a49\u7adf\u4f1a\u5982\u6b64\u820d\u547d\u62a4\u4ed6\uff0c\u5fc3\u4e2d\u53c8\u662f\u611f\u6fc0\uff0c\u53c8\u662f\u60ed\u6127\uff0c\u53c8\u662f\u6094\u6068\uff0c\u4e0d \u7531\u5f97\u4e5f\u6389\u4e0b\u6cea\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6b27\u9633\u59d1\u5a18\uff0c\u6211\u6b7b\u6709\u4f59\u8f9c\uff0c\u4f60\u4e0d\u5fc5\u518d\u987e\u5ff5\u6211\u4e86\uff0c\u4f60\u81ea\u5df1\u8d70\u5427\u3002\u201d \u6b27\u9633\u5a49\u5df2\u4e0d\u80fd\u5206\u795e\u8bf4\u8bdd\uff0c\u53ea\u662f\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u90a3\u8fb9\u53a2\u6587\u5ef7\u58c1\u4e0e\u5510\u52aa\u73e0\u7a46\u5bf9\u638c\uff0c\u53cc\u65b9\u62fc\u6597\u5185\u529b\uff0c\u6b63\u81ea\u5230\u4e86\u5403\u7d27\u7684\u592b\u5934\u3002\u6b27\u9633\u4ef2\u548c\u5927 \u559d\u4e00\u58f0\uff0c\u9739\u96f3\u638c\u4e0e\u96f7\u795e\u6307\u540c\u65f6\u653b\u51fa\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u6b63\u81ea\u4e00\u638c\u62cd\u51fa\uff0c\u5ffd\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u6587\u5ef7\u58c1\u5df2\u662f\u8dc4\u8dc4\u8e09\u8e09\u7684\u5012\u9000\u6570\u6b65\uff0c \u539f\u6765\u4ed6\u4e0e\u5510\u52aa\u73e0\u7a46\u6bd4\u62fc\u5185\u529b\uff0c\u6b63\u662f\u5230\u4e86\u6700\u5403\u7d27\u7684\u65f6\u5019\uff0c\u5510\u52aa\u73e0\u7a46\u7684\u5185\u529b\u6e90\u6e90\u800c\u6765\u3002\u4ed6\u773c \u770b\u652f\u6301\u4e0d\u4f4f\uff0c\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\u3002\u6b27\u9633\u4ef2\u548c\u6765\u5f97\u6070\u662f\u65f6\u5019\uff0c\u5510\u52aa\u73e0\u7a46\u8981\u5206\u51fa\u4e00\u638c\u53bb\u5e94\u4ed8\u6b27\u9633 \u4ef2\u548c\uff0c\u6587\u5ef7\u58c1\u8fd9\u624d\u5f97\u4ee5\u8131\u8eab\u3002\u53ef\u662f\uff0c\u4ed6\u7531\u4e8e\u53d7\u4e86\u5bf9\u65b9\u5f3a\u52b2\u7684\u5185\u529b\u6240\u9707\uff0c\u867d\u5f97\u8131\u8eab\uff0c\u4e00\u65f6 \u4e4b\u95f4\uff0c\u5374\u8fd8\u672a\u80fd\u6536\u5f97\u4f4f\u52bf\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u8eab\u4f3c\u9640\u87ba\u62e7\u8f6c\uff0c\u5728\u5730\u4e0a\u63a5\u8fde\u6253\u4e86\u51e0\u5341\u4e2a\u5708\u5708\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5de6\u638c\u8f7b\u8f7b\u4e00\u6325\uff0c\u53ea\u7528\u4e86\u4e09\u6210\u529f\u529b\uff0c\u6b27\u9633\u4ef2\u548c\u7684\u9739\u96f3\u638c\u529b\uff0c\u5df2\u7ed9\u4ed6\u5168\u90e8\u5c01\u4f4f\uff0c \u53cd\u9707\u56de\u6765\u3002\u6b27\u9633\u4ef2\u548c\u7684\u9739\u96f3\u638c\u4e43\u662f\u7eaf\u9633\u638c\u529b\uff0c\u4e00\u53cd\u9707\u56de\u6765\uff0c\u767b\u65f6\u5168\u8eab\u53d1\u6eda\u3002\u6b27\u9633\u4ef2\u548c\u5927 \u5403\u4e00\u60ca\uff0c\u8fd9\u65f6\u4ed6\u7684\u96f7\u795e\u6307\u521a\u521a\u6233\u51fa\uff0c\u8981\u60f3\u6536\u56de\uff0c\u5df2\u6765\u4e0d\u53ca\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5510\u52aa\u73e0\u7a46 \u51fa\u624b\u5982\u7535\uff0c\u4e5f\u662f\u4ee5\u6307\u5bf9\u6307\uff0c\u6b27\u9633\u4ef2\u548c\u4e00\u6307\u6233\u4e2d\u4ed6\u7684\u5c0f\u81c2\uff0c\u53ea\u89c9\u8f6f\u7ef5\u7ef5\u7684\u67d4\u82e5\u65e0\u9aa8\u3001\u7adf\u662f \u65e0\u4ece\u7740\u529b\uff0c\u5510\u52aa\u73e0\u7a46\u6307\u529b\u540e\u53d1\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u5374\u5f39\u4e2d\u4e86\u4ed6\u7684\u638c\u5fc3\uff0c\u6b27\u9633\u4ef2\u548c\u53ea\u89c9\u4e00\u80a1\u7099\u70ed \u4e4b\u6c14\uff0c\u4ece\u638c\u5fc3\u76f4\u201c\u94bb\u201d\u8fdb\u6765\uff0c\u767b\u65f6\u638c\u5fc3\u7ea2\u80bf\uff0c\u72b9\u5982\u53d7\u8fc7\u70ae\u70d9\u4e4b\u5211\uff01 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u770b\u5728\u4f60\u8fd8\u6709\u4e00\u5ff5\u4e4b\u6148\uff0c\u4e5f\u770b\u5728\u4f60\u5973\u513f\u7684\u4efd\u4e0a\uff0c\u638c\u529b\u6307\u529b\u5168\u90e8 \u5949\u56de\uff0c\u6211\u4e0d\u53e6\u52a0\u8fd8\u656c\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\u623f\u4e2d\u706b\u52bf\u5df2\u65fa\uff0c\u4e0d\u4f46\u5e8a\u5e10\u88ab\u8925\u65e9\u5df2\u70e7\u7740\uff0c\u5c4b\u6881\u677f\u58c1\u4e5f\u90fd\u7740\u4e86\u706b\uff0c\u70df\u96fe\u8ff7\u6f2b\uff0c\u6728 \u5934\u70e7\u88c2\u5f97\u201c\u8feb\u8feb\u535c\u535c\u201d\u7684\u58f0\u97f3\u4e5f\u90fd\u542c\u5f97\u89c1\u4e86\u3002\u5728\u8fd9\u4e9b\u58f0\u54cd\u4e4b\u4e2d\uff0c\u5ffd\u53c8\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\u3002 \u5374\u539f\u6765\u662f\u5929\u9b54\u6559\u4e3b\u51fb\u843d\u4e86\u6b27\u9633\u5a49\u7684\u957f\u5251\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5411\u7740\u53f6\u51b2\u9704\u6251\u53bb\uff0c\u5ffd\u89c9\u52b2\u98ce\u98d2\u7136\uff0c\u4e00\u822c\u5de8\u529b\u5df2\u81ea\u8eab\u540e\u63a8\u6765\uff0c\u5929\u9b54\u6559\u4e3b\u54ea\u6562 \u63a5\u62db\uff0c\u6025\u6025\u5fd9\u5fd9\u4e00\u4e2a\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\u3002 \u3000\u3000\u5012\u7eb5\u95ea\u5f00\uff0c\u53ea\u89c1\u4e00\u6761\u9ed1\u5f71\uff0c\u65e9\u5df2\u8d8a\u8fc7\u5979\u7684\u524d\u5934\uff0c\u62b1\u8d77\u53f6\u4e2d\u9704\uff0c\u5c31\u4ece\u5c4b\u9876\u4e0a\u7a7f\u5f00\u7684\u90a3 \u4e2a\u5927\u6d1e\u7a9c\u4e86\u51fa\u53bb\uff0c\u5c06\u53f6\u51b2\u9704\u6551\u51fa\u9669\u5883\u7684\u8fd9\u4eba\uff0c\u5f53\u7136\u662f\u5510\u52aa\u73e0\u7a46\u4e86\u3002 \u3000\u3000\u53f6\u51b2\u9704\u60f4\u60f4\u4e0d\u5b89\uff0c\u53ea\u6015\u843d\u5728\u4ec7\u4eba\u624b\u4e2d\uff0c\u6240\u53d7\u7684\u6298\u78e8\u66f4\u5927\u3002\u5fc3\u4e2d\u6b63\u81ea\u80e1\u601d\u4e71\u60f3\uff0c\u5510\u52aa \u73e0\u7a46\u5df2\u631f\u7740\u4ed6\u8d8a\u8fc7\u4e86\u5341\u51e0\u91cd\u74e6\u9762\uff0c\u5230\u4e86\u4e00\u5ea7\u5047\u5c71\u80cc\u540e\uff0c\u5c06\u4ed6\u8f7b\u8f7b\u5730\u653e\u4e0b\u6765\uff1a \u3000\u3000\u53f6\u51b2\u9704\u5636\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5192\u4e86\u4f60\u7684\u8eab\u4efd\uff0c\u7528\u4e86\u4f60\u7684\u540d\u5b57\uff0c\u6211\u4e00\u77e5\u4eba\u4e8b\uff0c\u5c31\u6ce8\u5b9a\u662f\u8981\u548c \u4f60\u4f5c\u5bf9\u7684\u4e86\uff0c\u5982\u4eca\u843d\u5728\u4f60\u7684\u624b\u4e2d\uff0c\u6211\u4e5f\u4e0d\u60f3\u6d3b\u4e86\uff0c\u53ea\u6c42\u4f60\u7ed9\u6211\u4e00\u4e2a\u723d\u5feb\uff0c\u522b\u518d\u6298\u78e8\uff01\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u8bf4\u9053\uff1a\u201c\u51a4\u6709\u5934\uff0c\u503a\u6709\u4e3b\uff0c\u6211\u6298\u78e8\u4f60\u4f5c\u4ec0\u4e48\uff1f\u867d\u7136\u4f60\u4e3a\u864e\u4f5c\u6005\uff0c\u8bba\u7406\u6211\u4e0d \u8be5\u6551\u4f60\uff0c\u4f46\u5ff5\u5728\u4f60\u4f3c\u5df2\u6709\u4e86\u6094\u610f\uff0c\u6211\u5982\u4eca\u7ed9\u4f60\u4e00\u4e2a\u5c06\u529f\u8d4e\u7f6a\u7684\u673a\u4f1a\uff0c\u5978\u738b\u85cf\u5728\u4ec0\u4e48\u5904\u6240\uff0c \u4f60\u5feb\u8bf4\u51fa\u6765\uff01\u201d\u53f6\u51b2\u9704\u8e0c\u8e87\u4e0d\u8bed\uff0c\u5510\u52aa\u73e0\u7a46\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7684\u2018\u7236\u738b\u2019\u5904\u5fc3\u79ef\u8651\u7684\u8981\u5c06\u4f60 \u9664\u60bc\uff0c\u4f60\u5982\u4eca\u8fd8\u8981\u8ba4\u8d3c\u4f5c\u7236\u5417\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4e0d\u662f\u6211\u4e0b\u80af\u8bf4\uff1a\u53ea\u6015\u4ed6\u73b0\u5728\u5df2\u4e0d\u5728\u5bab\u4e2d\u4e86\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u53bb\u4e86\u4ec0 \u4e48\u5730\u65b9\uff1f\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4eca\u665a\u4ed6\u672c\u6765\u662f\u51c6\u5907\u5230\u897f\u4e50\u82d1\u53bb\u770b\u6b4c\u821e\u7684\u3002\u540e\u6765\u4ed6\u53eb\u6211\u53bb\u901a\u77e5\u897f \u4e50\u82d1\u7684\u627f\u5949\u5b98\uff0c\u4e34\u65f6\u64a4\u6d88\u4e86\u8fd9\u4e2a\u8282\u76ee\u3002\u636e\u4ed6\u8bf4\uff0c\u56e0\u4e3a\u660e\u5929\u5c31\u662f\u91d1\u9e70\u5bab\u76db\u4f1a\u4e4b\u671f\uff0c\u4ed6\u60f3\u5728 \u4f1a\u524d\u4e0e\u5b9d\u8c61\u6cd5\u5e08\u4e00\u6664\uff0c\u6050\u6015\u4f1a\u5728\u91d1\u9e70\u5bab\u8fc7\u591c\u3002\u91d1\u9e70\u5bab\u4e2d\u9ad8\u624b\u5982\u4e91\uff0c\u6211\u7684\u5e08\u7236\u5b9d\u8c61\u6cd5\u5e08\u66f4 \u662f\u795e\u529f\u65e0\u6545\uff0c\u6211\u662f\u4e0d\u60f3\u4f60\u53bb\u5192\u8fd9\u4e2a\u9669\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u4e00\u60f3\u73b0\u5728\u5df2\u662f\u56db\u66f4\uff0c\u5373\u4f7f\u81ea\u5df1\u654c\u5f97\u8fc7\u90a3\u5b9d\u8c61\u6cd5\u5e08\u3002 \u3000\u3000\u8d76\u5230\u91d1\u9e70\u5bab\u6700\u5c11\u4ea6\u5df2\u662f\u5929\u660e\u65f6\u5206\uff0c\u4f55\u51b5\u81ea\u5df1\u5bf9\u91d1\u9e70\u5bab\u7684\u7ed3\u6784\u3001\u5730\u5f62\u53c8\u6beb\u4e0d\u719f\u6089\uff0c\u53ea \u597d\u8ba9\u90a3\u5978\u738b\u591a\u6d3b\u4e00\u5929\u4e86\u3002 \u3000\u3000\u5047\u5c71\u65c1\u8fb9\u6b63\u6709\u4e00\u4e2a\u8377\u5858\uff0c\u8fd9\u665a\u53c8\u6b63\u662f\u4e2d\u79cb\u524d\u5915\uff0c\u6708\u4eae\u53c8\u5927\u53c8\u5706\uff0c\u4e24\u4eba\u5728\u8377\u5858\u65c1\u8fb9\u5750 \u4e0b\uff0c\u5510\u52aa\u73e0\u7a46\u4f4e\u9996\u6c89\u601d\uff0c\u8377\u5858\u5982\u955c\uff0c\u4e24\u4eba\u7684\u5f71\u5b50\u6e05\u6f88\u53ef\u89c1\uff0c\u5ffd\u6709\u4e00\u9635\u98ce\u5439\u8fc7\uff0c\u6c34\u6708\u4ea4\u6eb6\uff0c \u4eba\u5f71\u6563\u4e71\u3002\u5510\u52aa\u73e0\u7a46\u5982\u6709\u6240\u89e6\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u518d\u4ed4\u7ec6\u6253\u91cf\u4e86\u53f6\u51b2\u9704\u4e00\u773c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5947 \u602a\uff0c\u8fd9\u4eba\u7684\u76f8\u8c8c\u679c\u7136\u662f\u4e0e\u6211\u76f8\u4f3c\u5f97\u5f88\u3002\u65e0\u602a\u90a3\u5978\u738b\u6307\u4f7f\u4ed6\u5192\u5145\u6211\uff0c\u53ef\u662f\u5978\u738b\u5374\u53c8\u600e\u4f1a\u77e5 \u6211\u7684\u76f8\u8c8c\u4e0e\u4ed6\u76f8\u4f3c\u7684\u5462\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u597d\u5947\u5fc3\u8d77\uff0c\u53d6\u51fa\u4e00\u7247\u5929\u5c71\u96ea\u83b2\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5929\u5c71\u96ea\u83b2\u80fd\u89e3\u767e\u6bd2\uff0c\u6216\u8005\u53ef\u4ee5 \u6709\u52a9\u4e8e\u4f60\uff0c\u4f60\u542b\u5728\u53e3\u4e2d\u5427\uff0c\u5f85\u4f60\u7cbe\u795e\u6062\u590d\uff0c\u6211\u8fd8\u60f3\u95f4\u4f60\u51e0\u53e5\u8bdd\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u542b\u4e86\u5929\u5c71\u96ea\u83b2\uff0c\u53ea\u89c9\u4e00\u7f15\u6e05\u9999\uff0c\u76f4\u900f\u80ba\u8151\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u8840\u8109\u5df2\u662f\u6e10\u6e10\u901a\u7545\uff0c \u5185\u529b\u867d\u8fd8\u672a\u80fd\u6062\u590d\uff0c\u7cbe\u795e\u5df1\u662f\u597d\u4e86\u8bb8\u591a\u3002\u53f6\u51b2\u9704\u6168\u53f9\u9053\uff1a\u201c\u771f\u60f3\u4e0d\u5230\u56fd\u738b\u4e00\u5411\u5ba0\u7231\u6211\uff0c \u4eca\u665a\u5374\u8981\u6740\u6211\u3002\u4f60\u662f\u6211\u7684\u654c\u4eba\uff0c\u53cd\u800c\u6551\u4e86\u6211\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4f60\u662f\u600e\u4e48\u8fdb\u5bab\u6765\u7684\uff1f\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u6211\u662f\u4e2a\u65e0\u7236\u65e0\u6bcd\u7684\u5b64\u513f\uff0c\u56fd\u738b \u6709\u4e00\u6b21\u548c\u7687\u989d\u5a18\u53bb\u6253\u730e\uff0c\u53d1\u73b0\u6211\u5728\u8349\u5730\u4e0a\u5b09\u620f\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u56fd\u738b\u4e00\u89c1\u6211\u5c31\u5f88\u559c\u6b22\uff0c\u5c31\u8981 \u90a3\u7687\u989d\u5a18\u5c06\u6211\u62b1\u4e86\u56de\u6765\uff0c\u8ba4\u4e3a\u4e49\u5b50\u3002\u201d\u5176\u5b9e\u53f6\u51b2\u9704\u81ea\u5df1\u4e5f\u4e0d\u77e5\uff0c\u8fd9\u662f\u56fd\u738b\u5b89\u6392\u597d\u4e86\u7684\uff0c \u90a3\u6b21\u6253\u730e\uff0c\u6709\u610f\u7ecf\u8fc7\u4ed6\u7684\u95e8\u524d\uff0c\u5e76\u975e\u4e34\u65f6\u53d1\u73b0\u7684\uff0c\u5185\u4e2d\u60c5\u7531\uff0c\u4ee5\u540e\u518d\u8868\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u66f4\u662f\u7591\u60d1\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u6211\u662f\u4ec0\u4e48\u4eba\uff1f\u5978\u738b\u4e3a\u4ec0\u4e48\u8981\u4f60\u81ea\u5c0f\u5c31\u5192\u5145\u6211\uff1f\u201d \u53f6\u51b2\u9704\u9053\uff1a\u201c\u4ed6\u6700\u521d\u7ed9\u6211\u53d6\u8fd9\u4e2a\u6c49\u540d\uff0c\u6211\u4e5f\u89c9\u5f97\u6709\u70b9\u5947\u602a\u3002\u540e\u6765\u6211\u957f\u5927\u4e86\uff0c\u4ed6\u624d\u544a\u8bc9\u6211\uff0c \u8bf4\u6709\u8fd9\u4e48\u4e00\u5bf9\u5b6a\u751f\u5144\u59b9\uff0c\u662f\u4ed6\u4e00\u4e2a\u4ec7\u4eba\u7684\u513f\u5973\uff0c\u54e5\u54e5\u5df2\u4e0d\u77e5\u4e0b\u843d\uff0c\u59b9\u59b9\u8fd8\u5728\u4eba\u95f4\uff0c\u4ed6\u8bf4 \u90a3\u4e2a\u4ec7\u4eba\u672c\u6765\u662f\u4ed6\u7684\u670b\u53cb\uff0c\u540e\u6765\u4e3a\u4e86\u90a3\u4eba\u4e0e\u4ed6\u4e89\u6743\uff0c\u624d\u4e0d\u5f97\u4e0d\u5c06\u90a3\u4eba\u6740\u4e86\u7684\uff0c\u4ed6\u53c8\u8bf4\u4ed6 \u4e3a\u4e86\u6b64\u4e8b\uff0c\u5f88\u662f\u540e\u6094\uff0c\u610f\u6b32\u5c06\u90a3\u4ec7\u4eba\u7684\u5973\u513f\u627e\u56de\u6765\uff0c\u6545\u6b64\u8981\u6211\u5192\u5145\u5979\u7684\u54e5\u54e5\uff0c\u4ed6\u65e5\u627e\u5230 \u4e86\u90a3\u4e2a\u59b9\u59b9\u4e4b\u65f6\uff0c\u53ef\u4ee5\u7531\u6211\u53bb\u89c1\uff0c\u52a8\u4ee5\u5144\u59b9\u4e4b\u60c5\u3002\u4ed6\u5bf9\u4f60\u5c11\u65f6\u7684\u7ecf\u5386\uff0c\u8c03\u67e5\u5f97\u6e05\u6e05\u695a\u695a\uff0c \u90fd\u544a\u8bc9\u6211\u4e86\u3002\u5c31\u662f\u4e0d\u544a\u8bc9\u6211\u4f60\u7a76\u7adf\u662f\u8c01\u4eba\u7684\u513f\u5b50\uff0c\u4ee5\u53ca\u4f55\u4ee5\u4f1a\u6709\u90a3\u4e00\u756a\u7ecf\u5386\u3002\u201d\u6b63\u662f\uff1a \u3000\u3000\u5047\u4f5c\u771f\u6765\u771f\u4f5c\u5047\uff0c\u5b64\u513f\u8eab\u4e16\u672a\u80fd\u660e\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e09\u5341\u516d\u56de\u3000\u9aa8\u8089\u56e2\u5706\u60b2\u5316\u559c\u3000\u7231\u60c5\u4ea4\u96c6\u68a6\u5982\u70df \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e09\u5341\u516d\u56de\u3000\u9aa8\u8089\u56e2\u5706\u60b2\u5316\u559c\u3000\u7231\u60c5\u4ea4\u96c6\u68a6\u5982\u70df \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u4f60\u5192\u5145\u6211\uff0c\u5374\u4e0d\u77e5\u9053\u6211\u662f\u4ec0\u4e48\u4eba\uff0c\u8fd9\u53ef\u771f\u662f\u4e00\u4ef6\u7a00\u5947\u53e4\u602a\u7684\u4e8b\u60c5\u3002\u201d \u53f6\u51b2\u9704\u9053\uff1a\u201c\u56fd\u738b\u8bf4\u4f60\u7236\u4eb2\u5f53\u5e74\u4e0e\u4ed6\u4e89\u6743\u593a\u5229\uff0c\u56e0\u800c\u88ab\u4ed6\u6740\u6389\uff0c\u6211\u4ee5\u4e3a\u4f60\u4eec\u662f\u5fe0\u4e8e\u524d\u738b \u7684\u5927\u81e3\u540e\u88d4\uff0c\u76f4\u5230\u524d\u5929\uff0c\u6211\u770b\u4e86\u90a3\u4efd\u7f8a\u76ae\u4e66\uff0c\u867d\u7136\u53ea\u770b\u4e86\u4e00\u9875\uff0c\u5c31\u7ed9\u4f60\u593a\u56de\uff0c\u4f46\u6211\u5df2\u7ecf \u660e\u767d\u4e86\uff0c\u539f\u6765\u4f60\u624d\u662f\u771f\u6b63\u7684\u6bbf\u4e0b\uff01\u201d \u3000\u3000\u6b64\u9645\uff0c\u4ed6\u5df2\u7136\u660e\u767d\u4e86\u5510\u52aa\u73e0\u7a46\u7684\u8eab\u4efd\uff0c\u53c8\u611f\u6fc0\u5510\u52aa\u73e0\u7a46\u7684\u6551\u547d\u4e4b\u6069\uff0c\u5c31\u8981\u5411\u4ed6\u884c\u541b \u81e3\u4e4b\u793c\u3002\u5510\u52aa\u73e0\u7a46\u6b62\u4f4f\u4ed6\u9053\uff1a\u201c\u4f11\u8981\u5982\u6b64\u3002\u6211\u56de\u6765\u5e76\u4e0d\u662f\u4e3a\u4e86\u8d2a\u56fe\u738b\u4f4d\uff0c\u53ea\u662f\u4e3a\u4e86\u62a5\u4ec7\uff0c \u4f60\u7740\u80fd\u52a9\u6211\u62a5\u4ec7\uff0c\u6211\u4fbf\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7591\u56e2\u672a\u91ca\uff0c\u53c8\u518d\u95ee\u9053\uff1a\u201c\u90a3\u7687\u989d\u5a18\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u77e5\u9053 \u5417\uff1f\u5979\u5c31\u662f\u524d\u738b\u7684\u738b\u540e\uff0c\u4f60\u7684\u6bcd\u4eb2\u3002\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u773c\u4e2d\u5ffd\u7136\u9732\u51fa\u60e7\u610f\uff0c\u5510\u52aa\u73e0\u7a46\u9053\uff1a \u201c\u4e0d\uff0c\u5979\u7edd\u4e0d\u662f\u6211\u7684\u6bcd\u4eb2\uff0c\u4f60\u4e0d\u7528\u5bb3\u6015\uff0c\u5979\u8981\u6740\u4f60\uff0c\u6211\u662f\u4e0d\u4f1a\u8ba9\u5979\u5f97\u901e\u7684\u3002\u8001\u5b9e\u544a\u8bc9\u4f60 \u5427\uff0c\u6211\u6b63\u662f\u56e0\u4e3a\u542c\u5f97\u5979\u4e0e\u90a3\u5929\u9b54\u6559\u4e3b\u5728\u5bc6\u5ba4\u79c1\u8bae\uff0c\u8bf4\u8981\u8c0b\u5bb3\u4f60\uff0c\u6211\u624d\u8ddf\u8e2a\u5929\u9b54\u6559\u4e3b\uff0c\u6765 \u6b64\u6551\u4f60\u7684\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5929\u9b54\u6559\u4e3b\u7684\u8bdd\u90fd\u662f\u771f\u7684\u4e86\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u5047\u3002 \u6211\u6b63\u60f3\u95ee\u4f60\uff0c\u90a3\u7687\u989d\u5a18\u4f55\u4ee5\u5982\u6b64\u6068\u4f60\uff0c\u5b9a\u8981\u5c06\u4f60\u9664\u6389\uff1f\u201d\u53f6\u51b2\u9704\u4e00\u6d3e\u60f6\u60d1\u7684\u795e\u60c5\uff0c\u6c89\u601d \u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u83ab\u540d\u5176\u5999\u3002\u81ea\u5c0f\u90a3\u7687\u989d\u5a18\u5bf9\u6211\u5c31\u4f3c\u4e4e\u5f88\u8ba8\u538c\uff0c\u4f46\u6211\u5374\u53c8\u662f\u5979\u62b1\u56de\u6765 \u7684\u3002\u56fd\u738b\u8fd8\u8981\u6211\u4ee5\u4e8b\u6bcd\u4eb2\u4e4b\u793c\u4f8d\u5949\u5979\u5462\uff0c\u6211\u6015\u4e86\u5979\u7684\u51f6\u6076\uff0c\u4e00\u76f4\u4e0d\u6562\u4eb2\u8fd1\u5979\u3002\u201d\u5510\u52aa\u73e0 \u7a46\u9053\uff0c\u201c\u597d\uff0c\u6211\u73b0\u5728\u4e0e\u4f60\u53bb\u89c1\u5979\uff0c\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u4f3c\u4e4e\u6709\u70b9\u4e3a\u96be\u7684\u795e\u6c14\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5530\u4e00\u58f0\u523a\u8033\u7684\u7834\u7a7a\u4e4b\u58f0\uff0c\u534a\u7a7a\u4e2d\u7a81\u7136 \u73b0\u51fa\u4e00\u56e2\u84dd\u8272\u7684\u706b\u7130\u3002\u5510\u52aa\u73e0\u7a46\u9053\uff1a \u3000\u3000\u201c\u4e0d\u597d\uff0c\u6211\u7684\u59b9\u59b9\u9047\u9669\u4e86\uff0c\u6211\u5f97\u5148\u7ed9\u5979\u89e3\u56f4\u53bb\u3002\u4f60\uff0c\u4f60\u4e5f\u968f\u6211\u6765\u5427\u3002\u201d\u53f6\u51b2\u9704\u4e0d\u6562 \u4e0d\u4f9d\uff0c\u8fd9\u65f6\u4ed6\u5df2\u6062\u590d\u4e86\u4e94\u516d\u6210\u529f\u529b\uff0c\u81ea\u5df1\u53ef\u4ee5\u8dd1\u5f97\u52a8\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u65bd\u5c55\u8f7b\u529f\uff0c\u5411\u86c7\u7130\u7bad\u5347\u8d77\u7684\u65b9\u5411\u5954\u53bb\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u90a3\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u5df2\u662f\u6108\u6765 \u6108\u8fd1\u3002\u53f6\u51b2\u9704\u9053\uff1a\u201c\u566b\uff0c\u8fd9\u662f\u51b7\u5bab\uff01\u201d\u5510\u52aa\u73e0\u7a46\u4e5f\u6709\u70b9\u5947\u602a\uff0c\u59b9\u59b9\u600e\u4e48\u8dd1\u5230\u51b7\u5bab\u6765\u4e86\uff1f \u6309\u8bf4\u51b7\u5bab\u662f\u738b\u5bab\u4e2d\u6700\u65e0\u5173\u91cd\u8981\u4e4b\u5730\uff0c\u5374\u53c8\u600e\u7684\u504f\u504f\u5728\u8fd9\u91cc\u906d\u9022\u5f3a\u654c\u5305\u56f4\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u52a0\u5feb\u811a\u6b65\uff0c\u5148\u95ef\u8fdb\u4e86\u51b7\u5bab\uff0c\u53ea\u89c1\u5bab\u6bbf\u91cc\u4eba\u5f71\u7ef0\u7ef0\uff0c\u56f4\u653b\u8c37\u4e2d\u83b2\u7684\u6b66\u58eb\u4e0d\u4e0b \u4e8c\u4e09\u5341\u4eba\uff0c\u5f53\u524d\u7684\u662f\u4e2a\u62ab\u7740\u5927\u7ea2\u8888\u6c99\u7684\u80e1\u50e7\uff0c\u4f7f\u7740\u4e00\u6839\u7897\u53e3\u5927\u7684\u7985\u6756\uff0c\u6700\u4e3a\u51f6\u731b\uff0c\u5404\u4e2d \u83b2\u906e\u5728\u4e00\u4e2a\u5987\u4eba\u7684\u8eab\u524d\uff0c\u4f7f\u5f00\u5b9d\u5251\uff0c\u4f3c\u4e4e\u662f\u5168\u529b\u4fdd\u62a4\u90a3\u4e2a\u5987\u4eba\u3002\u539f\u6765\u56fd\u738b\u5e76\u672a\u79bb\u5f00\u738b\u5bab\uff0c \u4ed6\u8bf4\u8981\u5230\u91d1\u9e70\u5bab\u53bb\uff0c\u90a3\u662f\u6545\u610f\u9a97\u53f6\u51b2\u9704\u7684\u3002\u2026\u2026 \u3000\u3000\u56fd\u738b\u7b49\u4e86\u4e00\u4e2a\u66f4\u6b21\uff0c\u4e0d\u89c1\u90a3\u4e24\u4e2a\u5949\u547d\u5230\u51b7\u5bab\u6740\u4eba\u7684\u5bab\u5973\u56de\u6765\u590d\u547d\uff0c\u60c5\u77e5\u6709\u53d8\uff0c\u6025\u5fd9 \u7eed\u6d3e\u6b66\u58eb\u524d\u6765\u5bdf\u770b\uff0c\u8c37\u4e2d\u83b2\u8981\u4fdd\u62a4\u6bcd\u4eb2\uff0c\u51b2\u4e86\u4e24\u6b21\uff0c\u51b2\u4e0d\u51fa\u53bb\uff0c\u53ea\u597d\u53d1\u51fa\u86c7\u7130\u7bad\u6c42\u63f4\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u53cc\u81c2\u4e00\u4f38\uff0c\u6293\u7740\u4e24\u4e2a\u6b66\u58eb\u7684\u540e\u5fc3\uff0c\u76f4\u60ef\u51fa\u53bb\uff0c\u90a3\u7ea2\u8863\u756a\u50e7\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u62db \u201c\u7ffb\u6c5f\u5012\u6d77\u201d\uff0c\u7897\u53e3\u822c\u7c97\u5927\u7684\u7985\u4ed7\u5df2\u662f\u62e6\u8170\u626b\u6765\uff0c\u5510\u52aa\u73e0\u7a46\u542c\u90a3\u52b2\u98ce\u547c\u547c\uff0c\u77e5\u9053\u662f\u4e2a\u5f3a \u654c\uff0c\u5927\u4e58\u822c\u82e5\u638c\u529b\u4e00\u638c\u62cd\u51fa\uff0c\u5c06\u90a3\u7985\u6756\u6309\u4f4f\uff0c\u53cc\u6307\u4e00\u6233\uff0c\u4fbf\u4f7f\u51fa\u4e86\u9694\u7a7a\u70b9\u7a74\u7684\u529f\u592b\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u55e4\u55e4\u201d\u58f0\u54cd\uff0c\u90a3\u756a\u50e7\u201c\u767b\u767b\u767b\u201d\u7684\u8fde\u9000\u4e09\u6b65\uff0c\u6ee1\u9762\u901a\u7ea2\uff0c\u773c\u5982\u94dc\u94c3\uff0c\u773c\u4e2d \u4f3c\u4e4e\u5c31\u8981\u55b7\u51fa\u706b\u6765\uff0c\u50e7\u888d\u4e5f\u88ab\u5510\u52aa\u73e0\u7a46\u7684\u6307\u529b\u6233\u7a7f\u4e86\u51e0\u4e2a\u5c0f\u5b54\uff0c\u53ef\u662f\u5374\u5e76\u6ca1\u6709\u5012\u4e0b\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6b63\u8981\u518d\u53d1\u4e00\u638c\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u5577\u5577\u201d\u7684\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u4e24\u6761\u94c1\u7d22\u592d\u77eb\u5982\u9f99\uff0c \u500f\u5730\u5408\u6210\u4e86\u4e00\u9053\u5706\u5708\uff0c\u5c06\u5510\u52aa\u73e0\u7a46\u7684\u8eab\u5f62\u7f69\u4f4f\uff0c\u5510\u52aa\u73e0\u7a46\u970d\u7684\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u5de6\u624b\u4e00 \u62db\u201c\u955c\u82b1\u6c34\u6708\u201d\uff0c\u4f7f\u51fa\u5378\u52b2\u8fd8\u51fb\u7684\u9634\u67d4\u638c\u529b\uff0c\u5c06\u90a3\u94c1\u7d22\u5f15\u5f00\uff0c\u53f3\u638c\u5219\u4ecd\u4ee5\u521a\u731b\u7684\u5927\u4e58\u822c \u82e5\u638c\u529b\uff0c\u786c\u5288\u90a3\u53e6\u5916\u4e00\u6761\u94c1\u7d22\uff0c\u4e24\u6761\u94c1\u7d22\u540c\u65f6\u8361\u5f00\uff0c\u53ef\u662f\u53ea\u662f\u4e00\u77ac\u4e4b\u95f4\uff0c\u53c8\u7acb\u5373\u5408\u6210\u4e86 \u5706\u5708\uff0c\u5a01\u529b\u7adf\u4f3c\u672a\u66fe\u5c11\u51cf\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u4e0d\u5230\u5bab\u4e2d\u8fd8\u6709\u8fd9\u6837\u7684\u597d\u624b\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u7ea2\u8863\u756a\u50e7\u7985 \u6756\u4e00\u633a\uff0c\u7adf\u7528\u53c8\u957f\u53c8\u7c97\u7684\u7985\u6756\uff0c\u4f7f\u51fa\u4e86\u5251\u672f\u4e2d\u201c\u91d1\u9488\u5ea6\u52a8\u201d\u7684\u7cbe\u5de7\u5251\u62db\uff0c\u5f84\u523a\u5510\u52aa\u73e0\u7a46 \u7684\u5c0f\u8179\u3002\u8fd9\u4e09\u4e2a\u4eba\u82e5\u7136\u5355\u6253\u72ec\u6597\uff0c\u51b3\u8ba1\u4e0d\u662f\u5510\u52aa\u73e0\u7a46\u7684\u5bf9\u624b\uff0c\u4f46\u4e09\u4eba\u8054\u624b\u800c\u653b\uff0c\u5510\u52aa\u73e0 \u7a46\u5374\u4e5f\u611f\u5230\u6709\u70b9\u5e94\u4ed8\u4e0d\u6687\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u538b\u529b\u4e00\u677e\uff0c\u5b9d\u5251\u7acb\u5373\u5316\u6210\u4e86\u4e00\u9053\u94f6\u8679\uff0c\u7a81\u56f4\u800c\u51fa\uff0c\u53ea\u542c\u5f97\u4e00\u7247\u65ad\u91d1\u560e\u7389\u4e4b\u58f0\uff0c \u597d\u51e0\u67c4\u5200\u5251\u5df2\u7ecf\u7ed9\u5979\u524a\u65ad\uff0c\u90a3\u756a\u50e7\u903c\u5f97\u8f6c\u8fc7\u7985\u6756\uff0c\u62b5\u6321\u5979\u7684\u5251\u62db\u3002\u756a\u50e7\u8fd9\u6839\u9554\u94c1\u7985\u6756\u6c89 \u91cd\u5f02\u5e38\uff0c\u867d\u7136\u4e5f\u7ed9\u5b9d\u5251\u524a\u4e86\u597d\u51e0\u5904\u7f3a\u53e3\uff0c\u4f46\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u662f\u524a\u5b83\u4e0d\u65ad\uff0c\u90a3\u5973\u4eba\u5ffd\u5730\u201c\u566b\u201d \u4e86\u4e00\u58f0\uff01 \u3000\u3000\u8c37\u4e2d\u83b2\u53eb\u9053\uff1a\u201c\u5988\uff0c\u4f60\u653e\u5fc3\uff0c\u54e5\u54e5\u6765\u4e86\uff0c\u5b9a\u80fd\u4fdd\u4f60\u5e73\u5b89\uff01\u201d\u5510\u52aa\u73e0\u7a46\u5403\u4e86\u4e00\u60ca\uff0c\u53eb \u9053\uff1a\u201c\u59b9\u59b9\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u5979\u662f\u8c01\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54b1\u4eec\u7684\u6bcd\u4eb2\u8fd8\u6d3b\u7740\uff0c\u54e5\u54e5\uff0c\u6253\u9000\u4e86\u654c \u4eba\u518d\u8bf4\uff01\u201c\u5510\u52aa\u73e0\u7a46\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u795e\u4e00\u5206\uff0c\u9669\u9669\u7ed9\u94c1\u7d22\u626b\u4e2d\u3002 \u3000\u3000\u90a3\u5973\u4eba\u53c8\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u5fc3\u91cc\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u68a6\u5417\uff1f\u8fd9\u8bb8\u591a\u610f\u60f3\u4e0d\u5230\u7684\u5947\u4e8b\uff0c\u90fd\u5728\u4eca \u665a\u53d1\u751f\uff01\u5374\u4e3a\u4f55\u6717\u739b\u53ea\u53eb\u4e8c\u4e2a\u54e5\u54e5\uff1f\u540e\u9762\u8fd9\u4e2a\u4eba\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u8fd9\u65f6\u4ea6\u5df2\u8d76\u5230\uff0c\u90a3\u756a\u618e\u5e76\u672a\u77e5\u8fd9\u4e2a\u56fd\u738b\u8981\u9664\u4ed6\u4e4b\u4e8b\uff0c\u5927\u559c\u53eb\u9053\uff1a\u201c\u5e72\u6bbf\u4e0b\uff0c \u4f60\u6765\u5f97\u6b63\u597d\uff0c\u5feb\u6765\u52a9\u6211\u4e00\u81c2\u4e4b\u529b\uff01\u201d\u90a3\u5973\u4eba\u542c\u4e86\u8fd9\u4e00\u58f0\u201c\u5e72\u6bbf\u4e0b\u201d\uff0c\u5fc3\u5934\u767b\u65f6\u4f3c\u533a\u4e86\u4e00 \u5757\u5927\u77f3\uff0c\u201c\u539f\u6765\u4e0d\u662f\u6211\u7684\u513f\u5b50\uff01\u55ef\uff0c\u6211\u5df2\u7ecf\u5f97\u56de\u4e24\u4e2a\u513f\u5973\uff0c\u4e5f\u4e0d\u8be5\u592a\u8fc7\u5962\u671b\u4e86\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6b63\u5728\u5fc3\u60f3\uff1a\u201c\u4e14\u770b\u4ed6\u5e2e\u8c01\uff1f\u201d\u53ea\u89c1\u53f6\u51b2\u9704\u4e00\u8138\u60f6\u6025\u7684\u795e\u60c5\uff0c\u5927\u58f0\u53eb\u9053\uff1a \u201c\u5927\u5e08\u5144\uff0c\u5927\u4e8b\u4e0d\u597d\u5566\uff01\u91d1\u4e16\u9057\u4e0e\u56db\u5927\u95e8\u6d3e\u7684\u5f1f\u5b50\u5728\u91d1\u9e70\u5bab\u95f9\u7ffb\u4e86\u5929\u5566\uff01\u5e08\u7236\u6709\u547d\uff0c\u53eb \u4f60\u901f\u901f\u56de\u53bb\uff01\u201d\u539f\u6765\u8fd9\u4e2a\u7ea2\u8863\u756a\u50e7\u4e43\u662f\u5b9d\u8c61\u6cd5\u5e08\u7684\u5927\u5f1f\u5b50\uff0c\u5728\u738b\u5bab\u62c5\u5f53\u796d\u5e08\u4e4b\u804c\u7684\u3002 \u3000\u3000\u8fd9\u756a\u50e7\u4fe1\u4ee5\u4e3a\u771f\uff0c\u547c\u7684\u4e00\u6756\u5411\u5510\u52aa\u73e0\u7a46\u51fb\u4e0b\uff0c\u4ee5\u653b\u51fb\u63a9\u62a4\u64a4\u9000\uff0c\u5510\u52aa\u73e0\u7a46\u6709\u610f\u663e\u9732 \u795e\u529f\uff0c\u4e00\u638c\u5411\u7985\u6756\u4e2d\u95f4\u6240\u53bb\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u90a3\u7985\u6756\u7684\u4e24\u5934\u7adf\u7136\u5f2f\u66f2\u4e0b\u6765\uff0c \u90a3\u756a\u50e7\u4e0d\u7531\u81ea\u5df1\u7684\u6253\u4e86\u4e00\u4e2a\u76d8\u65cb\uff0c\u7985\u6756\u4e24\u7aef\u5404\u89e6\u53ca\u4e00\u4e2a\u6b66\u58eb\uff0c\u767b\u65f6\u628a\u90a3\u4e24\u4e2a\u6b66\u58eb\u6253\u6b7b\uff0c \u800c\u90a3\u756a\u50e7\u501f\u7985\u6756\u89e6\u53ca\u522b\u4eba\u8eab\u4f53\u7684\u529b\u5ea6\uff0c\u6574\u4e2a\u8eab\u5b50\u4e5f\u98de\u817e\u8d77\u6765\uff0c\u4ed6\u7ed9\u5510\u52aa\u73e0\u7a46\u8fd9\u4e00\u51fb\u5413\u7834 \u4e86\u80c6\uff0c\u7eb5\u4f7f\u4e0d\u662f\u672c\u5bfa\u544a\u6025\u3002 \u3000\u3000\u4ed6\u4e5f\u4e0d\u6562\u604b\u6218\u4e86\u3002\u5f53\u4e0b\u4eba\u5728\u534a\u7a7a\uff0c\u4e00\u4e2a\u7b4b\u6597\uff0c\u5df2\u4ece\u4f17\u6b66\u58eb\u7684\u5934\u4e0a\u8d8a\u8fc7\uff0c\u6025\u6025\u5fd9\u5fd9\uff0c \u843d\u8352\u800c\u9003\u3002\u4ed6\u624b\u4e0b\u7684\u51e0\u4e2a\u5c0f\u5f1f\u5b50\uff0c\u4e5f\u8ddf\u7740\u8dd1\u4e86\u3002\u5510\u52aa\u73e0\u7a46\u89c1\u4ed6\u63a5\u8fde\u53d7\u4e86\u81ea\u5df1\u4e24\u6b21\u638c\u529b\uff0c \u5c45\u7136\u8fd8\u80fd\u591f\u7eb5\u8dc3\u5982\u98de\uff0c\u4e5f\u597d\u751f\u9a87\u5f02\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5f1f\u5b50\u5c1a\u4e14\u5982\u6b64\uff0c\u5e08\u7236\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u53ea\u6015\u6211\u867d\u7136\u773c\u98df\u4e86\u5929\u5fc3\u77f3\uff0c\u4e5f\u672a\u5fc5\u662f\u90a3\u5b9d\u8c61\u6cd5\u5e08\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u53c8\u53eb\u9053\uff1a\u201c\u9c81\u5140\u3001\u9c81\u8d64\uff0c\u738b\u4e0a\u6709\u547d\uff0c\u53eb\u4f60\u4eec\u56de\u53bb\u62a4\u9a7e\uff0c\u6050\u9632\u8d3c\u515a\u6df1\u5165\u5185\u5ef7\u3002 \u8fd9\u4e24\u4e2a\u5c0f\u8d3c\u7531\u4ed6\u53bb\u5427\u3002\u201d\u9c81\u5140\u3001\u9c81\u8d64\u5c31\u662f\u90a3\u4e24\u4e2a\u4f7f\u94c1\u7d22\u7684\u4eba\uff0c\u662f\u5fa1\u6797\u519b\u7684\u6b63\u526f\u6559\u5934\uff0c\u9a6c \u8428\u513f\u56fd\u6570\u4e00\u6570\u4e8c\u7684\u52c7\u58eb\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u5bf9\u65f6\u51b2\u9704\u7684\u8bdd\uff0c\u5374\u662f\u534a\u4fe1\u534a\u7591\uff0c\u4e0d\u80af\u7acb\u5373\u64a4\u9000\u3002\u9c81\u5140\u8bf4\u9053\uff1a\u201c\u6211\u5949\u4e86\u7687\u4e0a\u4e4b \u547d\uff0c\u52a1\u5fc5\u8981\u628a\u5728\u51b7\u5bab\u95f9\u4e8b\u7684\u8d3c\u5b50\u6d3b\u64d2\uff0c\u7687\u4e0a\u5c82\u4f1a\u7acb\u5373\u6539\u53d8\u4e3b\u610f\u3002\u53c8\u8c03\u6211\u56de\u53bb\uff1f\u201d\u9c81\u8d64\u8bf4 \u9053\uff1a\u201c\u5e72\u6bbf\u4e0b\uff0c\u4e0d\u5982\u4f60\u56de\u53bb\u62a4\u9a7e\u5427\uff01\u201d\u8fd9\u4e24\u4eba\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u94c1\u7d22\u4ecd\u662f\u76d8\u65cb\u98de\u821e\uff0c\u6beb\u4e0d\u653e\u677e\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4ecd\u662f\u4e00\u6bcd\u6240\u751f\u7684\u540c\u80de\uff0c\u5fc3\u610f\u76f8\u901a\uff0c\u81ea\u5c0f\u7ec3\u8fd9\u94c1\u7d22\u5408\u51fb\u4e4b\u6280\uff0c\u914d\u5408\u5f97\u5999\u5230\u6beb\u5dc5\uff0c \u6240\u4ee5\u4ee5\u5510\u52aa\u73e0\u7a46\u7684\u795e\u529f\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u4e5f\u8fd8\u672a\u80fd\u5c06\u4ed6\u4eec\u4e24\u4eba\u903c\u9000\u3002 \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u5c31\u5feb\u5feb\u5c06\u8fd9\u4e24\u4e2a\u5c0f\u8d3c\u64d2\u4e86\uff0c\u597d\u8d76\u56de\u53bb\u3002 \u3000\u3000\u6211\u6765\u5e2e\u4f60\u3002\u201d\u4ece\u4eba\u4e1b\u4e2d\u7a7f\u8fdb\uff0c\u4ed6\u662f\u201c\u5e72\u6bbf\u4e0b\u201d\u7684\u8eab\u4efd\uff0c\u4f17\u6b66\u58eb\u81ea\u662f\u4e0d\u7591\u6709\u4ed6\u3002\u54ea\u77e5 \u53f6\u51b2\u9704\u4e00\u5230\u9c81\u5140\u8eab\u65c1\uff0c\u6084\u65e0\u58f0\u7684\u5ffd\u5730\u4e00\u638c\u62cd\u51fa\uff0c\u8fd9\u4e00\u62ff\u770b\u6765\u867d\u662f\u8f7b\u8f7b\u62cd\u51fa\uff0c\u5b9e\u5df2\u7528\u4e0a\u4e86 \u521a\u731b\u7684\u5927\u4e58\u822c\u82e5\u638c\u529b\u3002\u53ea\u53ef\u60dc\u4ed6\u529f\u529b\u672a\u66fe\u5b8c\u5168\u6062\u590d\uff1b\u53ea\u53ca\u539f\u6765\u7684\u4e00\u534a\u3002 \u3000\u3000\u9c81\u5140\u7684\u94c1\u7d22\u653b\u8fdc\u4e0d\u653b\u8fd1\uff0c\u5ffd\u7136\u95f4\u53d7\u4e86\u4e00\u638c\uff0c\u75db\u5f97\u4ed6\u5927\u543c\u4e00\u58f0\uff0c\u7acb\u5373\u4e00\u4e2a\u65f6\u6376\u5411\u540e\u649e \u53bb\u3002\u53f6\u51b2\u9704\u8bc6\u5f97\u4ed6\u7684\u5389\u5bb3\uff0c\u65e9\u6709\u9632\u5907\uff0c\u5728\u4ed6\u4e00\u638c\u62cd\u51fa\u4e4b\u65f6\uff0c\u53e6\u4e00\u624b\u6293\u8d77\u4e86\u4e00\u4e2a\u6b66\u58eb\u4f5c\u4e3a \u76fe\u724c\u3002\u9c81\u5140\u4e00\u4e2a\u65f6\u6376\u5c06\u90a3\u6b66\u58eb\u7684\u5fc3\u53e3\u649e\u7834\uff0c\u65f6\u51b2\u9704\u5374\u65e9\u5df2\u907f\u5f00\u4e86\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7684\u6b66\u529f\u672c\u6765\u5728\u9c81\u5140\u5144\u5f1f\u4e4b\u4e0a\uff0c\u53ea\u56e0\u4ed6\u4eec\u94c1\u7d22\u5408\u51fb\u4e4b\u6280\u592a\u8fc7\u795e\u5999\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c \u65e0\u6cd5\u7834\u5b83\uff0c\u8fd9\u65f6\u9c81\u5140\u53d7\u4e86\u4e00\u638c\uff0c\u8fd9\u5927\u4e58\u822c\u82e5\u638c\u529b\u53c8\u662f\u4e13\u4f24\u5947\u7ecf\u516b\u8109\u7684\uff0c\u9976\u4ed6\u94dc\u76ae\u94c1\u9aa8\uff0c \u4e5f\u4e0d\u7981\u4e00\u4e2a\u8e09\u8dc4\u3002 \u3000\u3000\u9c81\u8d64\u94c1\u7d22\u6a2a\u626b\u8fc7\u6765\uff0c\u4ed6\u54e5\u54e5\u7531\u4e8e\u811a\u6b65\u8e09\u8dc4\uff0c\u5374\u914d\u5408\u4e0d\u4e0a\uff0c\u4e24\u6761\u94c1\u7d22\uff0c\u76f8\u5dee\u4e09\u5bf8\uff0c\u672a \u80fd\u5408\u6210\u5706\u5708\uff0c\u5510\u52aa\u73e0\u7a46\u8fc5\u5373\u4e00\u638c\u4ece\u7f1d\u9699\u4e2d\u7a7f\u51fa\uff0c\u6293\u7740\u4e86\u9c81\u5140\u7684\u7d22\u5934\uff0c\u53cd\u624b\u4e00\u64a9\uff0c\u5c06\u4e24\u6761 \u94c1\u7d22\u7ed3\u5728\u4e00\u8d77\u3002 \u3000\u3000\u8fd9\u4e24\u6761\u94c1\u7d22\u7684\u529b\u9053\u76f8\u53cd\uff0c\u5927\u5c0f\u76f8\u7b49\uff0c\u53ea\u542c\u5f97\u7830\u7830\u4e24\u58f0\uff0c\u4e24\u5144\u5f1f\u5404\u81ea\u7ed9\u5bf9\u65b9\u7684\u529b\u9053\u6454 \u7ffb\uff0c\u8c37\u4e2d\u83b2\u6b63\u8981\u4e00\u5251\u523a\u53bb\uff0c\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u90fd\u7b97\u5f97\u662f\u597d\u6c49\u5b50\uff0c\u4e0d\u53ef\u4f24\u4e86\u4ed6\u4eec\u6027\u547d\uff01\u201d \u6293\u7740\u94c1\u7d22\u7684\u4e2d\u95f4\uff0c\u4e00\u4e2a\u65cb\u98ce\u6025\u821e\uff0c\u9c81\u6c0f\u5144\u5f1f\u4e00\u4eba\u540a\u5728\u4e00\u5934\uff0c\u817e\u4e91\u9a7e\u96fe\u4e00\u822c\uff0c\u7ed9\u5510\u52aa\u73e0\u7a46 \u8fde\u4eba\u5e26\u7d22\uff0c\u629b\u8fc7\u4e86\u51b7\u5bab\u7684\u9ad8\u5899\u3002 \u3000\u3000\u7ea2\u8863\u756a\u50e7\u548c\u9c81\u6c0f\u5144\u5f1f\u4e43\u662f\u5bab\u4e2d\u672c\u9886\u6700\u5f3a\u7684\u4e09\u5927\u9ad8\u624b\uff0c\u4f17\u6b66\u58eb\u89c1\u8fd9\u4e09\u5927\u9ad8\u624b\u90fd\u5df2\u7ed9\u5bf9 \u65b9\u6253\u8d25\uff0c\u5982\u4f55\u8fd8\u6562\u604b\u6218\uff0c\u53d1\u4e00\u58f0\u558a\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u8d70\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u89c1\u53f6\u51b2\u9704\u51fa\u624b\u76f8\u52a9\uff0c\u5341\u5206\u8be7\u5f02\uff0c\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u5df2\u7ecf\u4e0d\u662f\u5e72\u6bbf\u4e0b\u4e86\uff0c \u54b1\u4eec\u4e5f\u4e0d\u5fc5\u518d\u8bb0\u524d\u5acc\u4e86\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u6ee1\u9762\u7f9e\u60ed\uff0c\u8fc7\u6765\u9053\u6b49\uff0c\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u4f60\u5192\u5145\u6211\u7684\u54e5\u54e5\uff0c\u628a\u6211\u7684\u771f\u54e5\u54e5\u5f15\u6765 \u4e86\uff0c\u4e8e\u6211\u4e5f\u672a\u5c1d\u6ca1\u6709\u597d\u5904\uff0c\u6211\u4e0d\u602a\u4f60\u3002\u201d\u5979\u8bf4\u5230\u201c\u5192\u5145\u201d\u4e8c\u5b57\uff0c\u5ffd\u5730\u60f3\u8d77\u6bcd\u4eb2\u521a\u624d\u6240\u8bf4 \u7684\u6545\u4e8b\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u628a\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u6bcd\u4eb2\u4e00\u6d3e\u8ff7\u60d8\u7684\u795e\u8272\uff0c\u5206\u4e0d\u51fa\u662f\u559c\u662f\u5fe7\u3002\u539f\u6765\u5979\u7684 \u6bcd\u4eb2\u6b63\u81ea\u5fc3\u60f3\uff1a\u201c\u65e2\u662f\u5192\u5145\uff0c\u90a3\u5c31\u4e0d\u662f\u771f\u7684\u4e86\u3002\u4f46\u662f\u8c01\u4eba\u53eb\u4ed6\u5192\u5145\u7684\u5462\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u65e0\u6687\u53d9\u8ff0\u4e0e\u53f6\u51b2\u9704\u5316\u654c\u4e3a\u53cb\u7684\u7ecf\u8fc7\uff0c\u5148\u4e0a\u6765\u89c1\u8fc7\u6bcd\u4eb2\u3002\u8c37\u4e2d\u83b2\u53fd\u53fd\u5471\u5471\u7684 \u66ff\u6bcd\u4eb2\u8bf4\u51fa\u524d\u56e0\u540e\u679c\uff0c\u4f46\u56e0\u4e8b\u60c5\u592a\u8fc7\u66f2\u6298\u590d\u6742\uff0c\u5979\u4e5f\u53ea\u80fd\u5148\u8bf4\u51fa\u4ed6\u4eec\u5144\u59b9\u7684\u8eab\u4e16\uff0c\u4ee5\u524d \u672a\u66fe\u77e5\u9053\u7684\u8fd9\u4e00\u90e8\u4efd\uff0c\u81f3\u4e8e\u4ed6\u4eec\u8fd8\u6709\u4e00\u4e2a\u751f\u6b7b\u672a\u535c\u7684\u5927\u54e5\uff0c\u5374\u8fd8\u6765\u4e0d\u53ca\u8a00\u8bf4\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u5988\uff0c\u6211\u521a\u624d\u5df2\u89c1\u7740\u90a3\u4e2a\u51f6\u6076\u7684\u7687\u989d\u5a18\u4e86\uff0c\u539f\u6765\u5979\u5c31\u662f\u5bb3\u82e6\u4e86\u54b1\u4eec\u4e00 \u5bb6\u7684\u90a3\u4e2a\u7687\u540e\uff0c\u602a\u4e0d\u5f97\u5979\u5bf9\u6211\u4eec\u5144\u59b9\u6068\u4e4b\u5207\u9aa8\uff0c\u4e00\u63d0\u8d77\u6211\u4eec\u5c31\u6c61\u8a00\u79fd\u8bed\u7684\u9a82\u4e2a\u4e0d\u4f11\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u53c8\u9053\uff1a\u201c\u8fd9\u6bd2\u5987\u5df2\u7ed9\u6211\u70b9\u4e86\u7a74\uff0c\u5988\uff0c\u7b49\u4f1a\u513f\u6211\u548c\u4f60\u53bb\u770b\u5979\uff0c\u4f60\u9ad8\u5174\u600e\u6837\u5904 \u7f6e\u5979\u5c31\u600e\u6837\u5904\u7f6e\u5979\u3002\u201d\u4ed6\u7684\u6bcd\u4eb2\u6cea\u75d5\u6ee1\u9762\uff0c\u4f46\u5374\u7b11\u5f97\u751a\u4e3a\u6b22\u7545\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5982\u4eca\u5df2\u5f97\u56de \u5b50\u5973\uff0c\u8fd9\u6bd2\u5987\u5374\u662f\u5b64\u5355\u4e00\u4eba\uff0c\u4ec0\u4e48\u8363\u534e\u5bcc\u8d35\uff0c\u5230\u5934\u6765\u90fd\u662f\u4e00\u573a\u7a7a\uff0c\u5982\u4eca\u6765\u8bf4\uff0c\u6211\u5df2\u7ecf\u6bd4 \u5979\u5f3a\u5f97\u591a\u4e86\u3002\u5c31\u7531\u5f97\u5979\u5077\u6d3b\u4e16\u95f4\uff0c\u5fcd\u53d7\u90a3\u51c4\u51c9\u7684\u5c81\u6708\u5427\uff0c\u6211\u4e5f\u4e0d\u60f3\u62a5\u4ec7\u4e86\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u51b7\u843d\u4e00\u65c1\uff0c\u89c1\u4ed6\u4eec\u6bcd\u5b50\u6b22\u805a\uff0c\u60f3\u8d77\u81ea\u5df1\u4e00\u51fa\u751f\u5c31\u662f\u5b64\u51e0\uff0c\u8fde\u7236\u6bcd\u4e5f\u6ca1\u89c1\u8fc7\uff0c \u4e0d\u7531\u5f97\u9eef\u7136\u795e\u4f24\u3002\u5ffd\u89c1\u5510\u52aa\u73e0\u7a46\u7684\u6bcd\u4eb2\u5411\u4ed6\u62db\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u53f6\u516c\u5b50\uff0c\u8bf7\u4f60\u8fc7\u6765\u3002\u201d \u3000\u3000\u539f\u6765\u5510\u52aa\u73e0\u7a46\u6b63\u5728\u548c\u4ed6\u6bcd\u4eb2\u8bf4\u5230\u4ed6\u5728\u90a3\u201c\u7687\u989d\u5a18\u201d\u7a97\u4e0b\u5077\u542c\u5230\u7684\u79d8\u5bc6\uff0c\u4ed6\u6bcd\u4eb2\u8d8a\u542c \u8d8a\u662f\u7591\u5fc3\uff0c\u56e0\u6b64\u4fbf\u8bf7\u53f6\u51b2\u9704\u8fc7\u6765\u95ee\u4e2a\u7a76\u7adf\u3002 \u3000\u3000\u53f6\u51b2\u9704\u5c0a\u4e86\u4e00\u58f0\u201c\u4f2f\u6bcd\u201d\uff0c\u89c1\u8fc7\u793c\u540e\uff0c\u53ea\u89c1\u5510\u52aa\u73e0\u7a46\u7684\u6bcd\u4eb2\u5b9a\u4e86\u773c\u775b\u770b\u4ed6\uff0c\u795e\u60c5\u751a \u662f\u5947\u5f02\uff0c\u534a\u664c\u95ee\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u662f\u76d6\u6e29\u7684\u4e49\u5b50\u3002\u5728\u5bab\u4e2d\u662f\u5e72\u6bbf\u4e0b\u7684\u8eab\u4efd\uff1f\u201d\u53f6\u51b2\u9704\u542b\u7f9e\u5e26 \u6127\uff0c\u8bf4\u9053\uff1a\u201c\u4ece\u524d\u662f\u7684\uff0c\u73b0\u5728\u4e0d\u662f\u4e86\u3002\u201d\u90a3\u5973\u4eba\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u73b0\u5728\u53c8\u4e0d\u662f\u4e86\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u4ee3\u4ed6\u56de\u7b54\u901a\uff1a\u201c\u5988\uff0c\u4ed6\u7684\u2018\u7236\u738b\u2019\u8981\u5c06\u4ed6\u6740\u6389\uff0c\u4ed6\u600e\u80fd\u8fd8\u8ba4\u6740\u4ed6\u4e4b\u4eba\u4e3a\u7236\uff1f\u201d \u90a3\u5973\u4eba\u9053\uff1a\u201c\u54e6\uff0c\u76d6\u6e29\u4e5f\u8981\u6740\u4ed6\uff0c\u4ec0\u4e48\u7f18\u6545\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u5927\u7ea6\u662f\u76d6\u6e29\u8ba4\u4e3a\u4ed6\u672a\u5c3d \u5168\u529b\uff0c\u6349\u62ff\u6211\u4eec\u5144\u59b9\u5427\uff1f\u201d\u90a3\u5973\u4eba\u9053\uff1a\u201c\u76d6\u6e29\u8981\u4f60\u81ea\u5c0f\u5c31\u5192\u5145\u6211\u7684\u513f\u5b50\uff0c\u4f60\u4e0d\u89c9\u5f97\u5947\u602a \u5417\uff1f\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u6211\u6b63\u662f\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u3002\u4e0d\u77e5\u4ed6\u4f55\u4ee5\u6709\u5148\u77e5\u4e4b\u672f\uff0c\u77e5\u9053\u6211\u957f\u6210\u4e4b\u540e\uff0c \u76f8\u8c8c\u4f1a\u4e0e\u6bbf\u4e0b\u76f8\u540c\u3002\u201d\u90a3\u5973\u4eba\u53c8\u95ee\u9053\uff1a\u201c\u7687\u989d\u5a18\u4e3a\u4f55\u53c8\u8981\u6740\u4f60\uff1f\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u6211\u4e5f\u662f \u83ab\u540d\u5176\u5999\uff0c\u6211\u53ea\u77e5\u9053\u5979\u662f\u81ea\u5c0f\u5c31\u8ba8\u538c\u6211\u7684\u3002\u201d \u3000\u3000\u90a3\u5973\u4eba\u5ffd\u5730\u6cea\u4e0b\u4e24\u884c\uff0c\u62c9\u7740\u53f6\u51b2\u9704\u7684\u624b\u53eb\u9053\uff1a\u201c\u7ae0\u5cf0\uff0c\u4f60\u811a\u677f\u5e95\u662f\u4e0d\u662f\u6709\u4e00\u9897\u7ea2\u75e3\uff1f\u201d \u8fd9\u4e00\u53e5\u8bdd\u604d\u5982\u6674\u5929\u9739\u96f3\uff0c\u628a\u53f6\u51b2\u9704\u5413\u5f97\u5446\u4e86\uff0c\u4ed6\u5f20\u5927\u4e86\u773c\u775b\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u4f60\u3001\u4f60\u600e\u4e48 \u77e5\u9053\uff1f\u201d \u3000\u3000\u539f\u6765\u201c\u7ae0\u5cf0\u201d\u6b63\u662f\u4ed6\u7684\u5c0f\u540d\uff0c\u8fd9\u4e2a\u5c0f\u540d\u53ea\u6709\u81ea\u5e7c\u629a\u517b\u4ed6\u7684\u90a3\u4e2a\u8001\u4eba\u53eb\u4ed6\uff0c\u5165\u5bab\u4e4b\u540e\uff0c \u65e9\u5df2\u5e9f\u5f03\uff0c\u5bab\u4e2d\u4e5f\u65e0\u4eba\u77e5\u9053\u4ed6\u6709\u8fd9\u4e2a\u540d\u5b57\u3002\u81f3\u4e8e\u4ed6\u811a\u677f\u5e95\u6709\u9897\u7ea2\u75e3\uff0c\u90a3\u66f4\u662f\u65e0\u4eba\u77e5\u9053\u7684 \u4e86\u3002 \u3000\u3000\u90a3\u5973\u4eba\u4e00\u628a\u5c06\u53f6\u51b2\u9704\u63fd\u4f4f\uff0c\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u90a3\u4e48\u8fd9\u662f\u771f\u7684\u4e86\uff0c\u5929\u554a\uff01\u201d\u5510\u52aa\u73e0\u7a46\u60ca\u9053\uff1a \u201c\u5988\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u90a3\u5973\u4eba\u9053\uff1a\u201c\u591a\u8c22\u4e0a\u5929\uff01\u4f60\u4eec\u5144\u5f1f\u3001\u5144\u59b9\u5feb\u6765\u91cd\u65b0\u89c1\u8fc7\uff0c\u4ed6\u662f\u4f60\u7684 \u5927\u54e5\uff01\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u600e\u4e48\uff0c\u6211\u8fd8\u6709\u4e00\u4f4d\u5927\u54e5\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u7a46\u54e5\uff0c\u8fd9\u4f4d\u5927\u54e5\u7684\u6545 \u4e8b\u4f60\u8fd8\u6ca1\u6709\u542c\u8fc7\uff0c\u4ed6\u7684\u906d\u9047\u4e4b\u60e8\uff0c\u5e76\u4e0d\u5728\u54b1\u4eec\u4e4b\u4e0b\u3002\u5988\uff0c\u4f60\u518d\u8bf4\u4e00\u904d\u5427\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u5fc3\u60c5\u6fc0\u52a8\uff0c\u6240\u5f97\u90a3\u5973\u4eba\u5c06\u4ed6\u8eab\u4e16\u4e4b\u79d8\u4e00\u4e00\u63ed\u9732\uff0c\u4e0d\u7531\u5f97\u70ed\u6cea\u76c8\u7736\uff0c\u91cd\u65b0\u62dc\u5012\uff0c \u53eb\u4e86\u4e00\u58f0\u201c\u5988\u5988\u201d\uff01 \u3000\u3000\u539f\u6765\u90a3\u6076\u6bd2\u7684\u738b\u540e\uff0c\u5f53\u5e74\u6d3e\u4eba\u5c06\u8fd9\u4e2a\u521d\u751f\u7684\u5a74\u513f\u62a2\u53bb\uff0c\u5374\u4e5f\u8fd8\u6709\u70b9\u987e\u5fcc\u56fd\u738b\u8ffd\u7a76\uff0c \u4e0d\u6562\u7acb\u5373\u6740\u4ed6\uff0c\u5c06\u4ed6\u4ea4\u7ed9\u4e00\u4e2a\u4eb2\u4fe1\u7684\u4eba\u517b\u5728\u5bab\u5916\u3002\u56fd\u738b\u5374\u4ee5\u4e3a\u8fd9\u5a74\u513f\u5df2\u6b7b\uff0c\u4e00\u6012\u4e4b\u4e0b\uff0c \u4e0e\u738b\u540e\u65ad\u7edd\u5f80\u6765\u3002\u4f46\u56fd\u738b\u4e00\u5411\u61e6\u5f31\uff0c\u754f\u60e7\u540e\u515a\u52bf\u529b\uff0c\u5374\u4e5f\u4e0d\u6562\u8ffd\u7a76\u3002 \u3000\u3000\u6ca1\u6709\u591a\u4e45\uff0c\u4fbf\u53d1\u751f\u4e86\u76d6\u6e29\u7684\u53db\u4e71\uff0c\u76d6\u6e29\u7be1\u593a\u4e86\u7389\u4f4d\u4e4b\u540e\uff0c\u63a2\u5f97\u9690\u60c5\uff0c\u6709\u610f\u5229\u7528\u53f6\u51b2 \u9704\u4f5c\u4e3a\u5de5\u5177\uff0c\u53eb\u4ed6\u5192\u5145\u8c37\u4e2d\u83b2\u7684\u5b6a\u751f\u54e5\u54e5\uff0c\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\uff0c\u610f\u56fe\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u8bf1\u9a97\u8c37\u4e2d \u83b2\u524d\u6765\u4e0a\u5f53\u3002 \u3000\u3000\u524d\u56e0\u540e\u679c\u90fd\u5df2\u6e05\u695a\u4e4b\u540e\uff0c\u8c37\u4e2d\u83b2\u53f9\u9053\uff1a\u201c\u8fd9\u5978\u738b\u7684\u5978\u8ba1\uff0c\u5f53\u771f\u6bd2\u8fa3\uff01\u8981\u662f\u6211\u6ca1\u6709\u7236 \u738b\u7684\u7f8a\u76ae\u4e66\uff0c\u5373\u7b97\u5728\u9a6c\u8428\u513f\u56fd\u6ca1\u6709\u78b0\u4e0a\uff0c\u6211\u542c\u5f97\u6c5f\u6e56\u4e0a\u6709\u8fd9\u4e48\u4e00\u4e2a\u81ea\u79f0\u2018\u53f6\u51b2\u9704\u2019\u7684\u4eba\uff0c \u6211\u4e5f\u4e00\u5b9a\u4f1a\u53bb\u627e\u4ed6\u7684\u4e86\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u90a3\u65f6\u6211\u5374\u4e0d\u77e5\u9053\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u66f4\u4e0d\u77e5\u4f60\u5f53\u771f\u5c31\u662f\u6211\u7684\u80de\u59b9\uff0c\u6211\u53ea\u77e5\u9053 \u6548\u5fe0\u5978\u738b\uff0c\u4e00\u5b9a\u4f1a\u8bf1\u4f60\u4f9b\u51fa\u79d8\u5bc6\uff0c\u7136\u540e\u5c06\u4f60\u6bd2\u5bb3\uff0c\u90a3\u6211\u53ef\u771f\u662f\u79bd\u517d\u4e0d\u5982\uff0c\u767e\u6b7b\u83ab\u8d4e\u4e86\uff01 \u5509\uff0c\u4eba\u5fc3\u9669\u6076\uff0c\u4e00\u81f3\u4e8e\u65af\uff0c\u771f\u662f\u96be\u4ee5\u60f3\u8c61\uff01\u201d\u4ed6\u4eec\u7684\u6bcd\u4eb2\u7b11\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6211\u53ef\u5f97\u591a \u8c22\u76d6\u6e29\u5462\u3002\u8981\u4e0d\u662f\u4ed6\u8bbe\u4e0b\u5982\u6b64\u8fd9\u822c\u7684\u6bd2\u8ba1\uff0c\u4eca\u65e5\u7109\u80fd\u5f04\u5047\u6210\u771f\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u8d8a\u60f3\u8d8a\u6068\uff0c\u7f9e\u60ed\u6127\u6094\uff0c\u6d95\u6cea\u4ea4\u6d41\uff0c\u4fef\u4f0f\u4e8e\u5730\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5988\uff0c\u5b69\u513f\u8ba4\u8d3c\u505a\u7236\uff0c\u771f\u4e0d\u914d\u505a\u4f60\u7684\u513f\u5b50\uff1b\u59b9\u59b9\uff0c\u6211\u5bf9\u4e0d\u8d77\u4f60\uff0c\u6211\u4e5f\u60ed\u6127\u4f5c\u4e3a\u4f60\u7684 \u54e5\u54e5\u3002\u201d\u4ed6\u7684\u6bcd\u4eb2\u5c06\u4ed6\u62c9\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u5b69\u513f\uff0c\u4e0d\u662f\u4f60\u7684\u8fc7\u9519\uff0c\u8981\u6068\u53ea\u80fd\u6068\u90a3\u5978\u738b\uff0c\u4f60\u4eec \u5144\u59b9\u91cd\u65b0\u89c1\u8fc7\uff0c\u54b1\u4eec\u4e00\u5bb6\u4eca\u65e5\u56e2\u5706\uff0c\u8fd9\u4e9b\u96be\u582a\u7684\u5f80\u4e8b\uff0c\u4ee5\u540e\u4e0d\u5fc5\u518d\u63d0\u5566\u3002\u201d\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a \u3000\u3000\u201c\u6211\u4ee5\u524d\u65e5\u53e3\u58f0\u58f0\u9a82\u4f60\u662f\u5978\u5f92\uff0c\u9a82\u4f60\u5192\u5145\u6211\u7684\u54e5\u54e5\uff0c\u60f3\u4e0d\u5230\u7adf\u662f\u771f\u7684\u3002\u6211\u4e5f\u8981\u5411\u4f60 \u8d54\u7f6a\u3002\u201d\u4e00\u7b11\u5c06\u53f6\u51b2\u9704\u62c9\u8d77\uff0c\u53f6\u51b2\u9704\u4ecd\u662f\u611f\u5230\u7f9e\u6127\u96be\u5bb9\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u949f\u697c\u5df2\u62a5\u4e94\u66f4\uff0c\u8c37\u4e2d\u83b2\u4f3c\u662f\u7a81\u7136\u60f3\u8d77\u4e00\u4e8b\uff0c\u53eb\u9053\uff1a \u3000\u3000\u201c\u54a6\uff0c\u5947\u602a\uff01\u201d\u5979\u6bcd\u4eb2\u95ee\u9053\uff1a\u201c\u4f55\u4e8b\u5947\u602a\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u8fd9\u65f6\u4ea6\u5df2\u731b\u5730\u7701\u8d77\uff0c\u8bf4\u9053\uff1a \u201c\u5bf9\u5566\uff0c\u6c5f\u5e08\u5144\u4e3a\u4f55\u8fd8\u4e0d\u89c1\u6765\uff1f\u201d\u8981\u77e5\u4ed6\u4eec\u4e09\u4eba\u7ea6\u597d\uff0c\u4ee5\u86c7\u7130\u7bad\u4f5c\u4e3a\u8b66\u53f7\uff0c\u4e00\u89c1\u54ea\u4e00\u65b9 \u5347\u8d77\u86c7\u7130\u7bad\uff0c\u5176\u4ed6\u4e8c\u4eba\u5c31\u7acb\u5373\u8d76\u6765\uff0c\u5982\u4eca\u8ddd\u79bb\u5907\u4e2d\u83b2\u53d1\u51fa\u86c7\u7130\u7bad\u7684\u65f6\u95f4\u5df2\u5c06\u8fd1\u534a\u4e2a\u65f6\u8fb0\uff0c \u6c5f\u6d77\u5929\u5374\u4ecd\u662f\u672a\u89c1\u8e2a\u8ff9\uff01\u8c37\u4e2d\u83b2\u7109\u5f97\u4e0d\u6ee1\u6000\u5fe7\u8651\uff1f \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u90a3\u91cc\u4e5f\u51fa\u4e8b\u4e86\uff1f\u5374\u4f55\u4e3a\u4e0d\u89c1\u86c7\u7130\u7bad\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u5b89\u6170\u59b9\u59b9\u9053\uff1a \u201c\u6c5f\u5e08\u5144\u7684\u672c\u9886\uff0c\u53ea\u6015\u5f53\u4eca\u4e4b\u4e16\uff1a\u9664\u4e86\u5e08\u7236\u4e4b\u5916\uff0c\u5df2\u65e0\u4eba\u80fd\u80dc\u5f97\u8fc7\u4ed6\u4e86\uff0c\u7eb5\u7136\u51fa\u4e8b\uff0c\u6599 \u4ea6\u65e0\u574a\uff01\u201d\u4ed6\u4eec\u7684\u6bcd\u4eb2\u9053\uff1a\u201c\u8fd9\u4f4d\u6c5f\u5e08\u5144\u53c8\u662f\u4f55\u4eba\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u4ed6\u662f\u6211\u540c\u95e8\u5e08\u5144\uff0c\u53c8\u662f\u59b9\u59b9\u9752\u6885\u7af9\u9a6c\u4e4b\u4ea4\u7684\u597d\u53cb\u3002\u4ed6\u7684\u672c\u4e8b\u53ef\u5927 \u5462\uff0c\u6bd4\u6211\u4eec\u5144\u59b9\u90fd\u5f3a\u3002\u4eba\u54c1\u53c8\u597d\uff0c\u4f60\u89c1\u4e86\u4ed6\uff0c\u4e5f\u4e00\u5b9a\u4f1a\u6b22\u559c\u4ed6\u80c1\u3002\u201d\u4ed6\u7684\u6bcd\u4eb2\u4e00\u542c\uff0c\u5df2 \u731c\u5230\u4e86\u51e0\u5206\uff0c\u7b11\u9053\uff1a\u201c\u53ea\u8981\u739b\u513f\u6b21\u559c\u7684\u4eba\uff0c\u5988\u5f53\u7136\u4e5f\u4e00\u6837\u6b22\u559c\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6d77\u54e5\u7684\u672c\u4e8b\u867d\u597d\uff0c\u4f46\u54b1\u4eec\u4e5f\u8981\u627e\u7740\u4e86\u4ed6\uff0c\u624d\u5f97\u653e\u5fc3\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\uff0c\u73b0\u5728\u5929\u5feb\u4eae\u4e86\uff0c\u91d1\u9e70\u5bab\u4e4b\u4f1a\u5c31\u8981\u5f00\u573a\uff0c\u5018\u82e5\u5e08\u5144\u4e0d\u5728 \u573a\uff0c\u5c82\u975e\u8981\u51cf\u5c11\u8bb8\u591a\u70ed\u95f9\uff1f\u201d\u4ed6\u4eec\u8fdb\u5bab\u4e4b\u65f6\uff0c\u7ea6\u597d\u4e86\u7531\u5510\u52aa\u73e0\u7a46\u4e0e\u8c37\u4e2d\u83b2\u5206\u5934\u641c\u7d22\uff0c\u6c5f \u6d77\u5929\u5219\u5728\u5fa1\u82b1\u56ed\u4e2d\u7684\u5c0f\u84ec\u83b1\u5c71\u4e0a\u5b88\u5019\uff0c\u51c6\u5907\u7b56\u5e94\uff0c\u4e0d\u8bba\u7ed3\u679c\u5982\u4f55\uff0c\u90fd\u5f97\u56de\u5230\u5c0f\u84ec\u83b1\u5c71\u805a \u96c6\u3002\u4e8e\u662f\u8c37\u4e2d\u83b2\u9042\u80cc\u8d77\u6bcd\u4eb2\uff0c\u5510\u52aa\u73e0\u7a46\u4e0e\u53f6\u51b2\u9704\u4e24\u4eba\u5728\u524d\u5f00\u8def\uff0c\u4e00\u884c\u4eba\u7b49\uff0c\u5411\u5fa1\u82b1\u56ed\u800c \u53bb\u3002\u5bab\u4e2d\u6b66\u58eb\u7ecf\u8fc7\u4e86\u8fd9\u4e00\u573a\u5927\u6218\uff0c\u90fd\u5413\u7834\u4e86\u80c6\uff0c\u54ea\u6562\u963b\u62e6\uff1f \u3000\u3000\u65e6\u8bf4\u6c5f\u6d77\u5929\u5728\u5c0f\u84ec\u83b1\u5c71\u4e0a\u5b88\u5019\uff0c\u8fd9\u662f\u5bab\u4e2d\u6700\u9ad8\u7684\u5904\u6240\uff0c\u5728\u5c71\u9876\u53ef\u4ee5\u671b\u89c1\u5404\u5904\uff0c\u4f46\u89c1 \u6708\u5f71\u897f\u79fb\uff0c\u4e09\u66f4\u5df2\u8fc7\uff0c\u56db\u4e0b\u91cc\u4ecd\u662f\u9759\u6084\u6084\u7684\uff0c\u4e5f\u4e0d\u89c1\u6709\u86c7\u7130\u7bad\u5347\u8d77\uff0c\u6c5f\u6d77\u5929\u4e0d\u77e5\u8c37\u4e2d\u83b2 \u5144\u59b9\u5728\u5bab\u4e2d\u6709\u5947\u9047\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u867d\u7136\u7ea6\u597d\u7684\u6700\u540e\u65f6\u523b\u4e43\u662f\u4e94\u66f4\uff0c\u4f46\u82e5\u662f\u4e8b\u60c5\u987a\u5229\u7684\u8bdd\uff0c \u8fd9\u65f6\u4e5f\u8be5\u6709\u70b9\u52a8\u9759\u4e86\u3002\u201d\u4e0d\u7981\u6709\u70b9\u60f4\u60f4\u4e0d\u5b89\u3002 \u3000\u3000\u5c06\u8fd1\u56db\u66f4\u65f6\u5206\uff0c\u5ffd\u89c1\u4e1c\u8fb9\u89d2\u843d\uff0c\u6709\u4e2a\u5730\u65b9\u8d77\u706b\uff0c\u4f46\u5374\u4e0d\u89c1\u86c7\u9677\u7bad\u5347\u8d77\u3002\u539f\u6765\u8fd9\u4e2a\u65f6 \u5019\uff0c\u6b63\u662f\u5510\u52aa\u73e0\u7a46\u5728\u53f6\u51b2\u9704\u7684\u5c4b\u5b50\u91cc\u906d\u9047\u5929\u9b54\u6559\u4e3b\u7684\u65f6\u5019\uff0c\u5929\u9b54\u6559\u4e3b\u7684\u6bd2\u96fe\u91d1\u9488\u70c8\u7130\u5f39 \u5f15\u8d77\u4e00\u573a\u5c0f\u706b\uff0c\u800c\u5510\u52aa\u73e0\u7a46\u968f\u5373\u4e5f\u5c31\u628a\u53f6\u51b2\u9704\u6551\u51fa\u53bb\u4e86\uff0c\u6240\u4ee5\u6839\u672c\u7528\u4e0d\u7740\u53d1\u5c04\u86c7\u7130\u7bad\u8bf7 \u6c5f\u6d77\u5929\u5e2e\u5fd9\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e0d\u89c1\u86c7\u7130\u7bad\u5347\u8d77\uff0c\u81ea\u662f\u4e0d\u4fbf\u79bb\u5f00\uff0c\u53ea\u597d\u8010\u5fc3\u5b88\u5019\u3002\u53c8\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5ffd\u89c1\u6709\u51e0\u6761 \u4eba\u5f71\u5411\u8fd9\u8fb9\u8d70\u6765\uff0c\u6708\u5149\u768e\u6d01\uff0c\u8ddd\u79bb\u867d\u8fdc\uff0c\u6c5f\u6d77\u5929\u5c4a\u9ad8\u4e34\u4e0b\uff0c\u5374\u770b\u5f97\u5206\u660e\uff0c\u8fd9\u4e00\u884c\u56db\u4f17\uff0c \u6b63\u662f\u6587\u5ef7\u58c1\u3001\u5929\u9b54\u6559\u4e3b\u3001\u6b27\u9633\u4ef2\u548c\u4ee5\u53ca\u4ed6\u7684\u5973\u513f\u6b27\u9633\u5a49\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u89c1\u6b27\u9633\u5a49\u4e5f\u5728\u5176\u4e2d\uff0c\u5fc3\u5934\u4e0d\u7981\u201c\u6251\u901a\u201d\u4e00\u8df3\u3002\u4ed6\u524d\u65e5\u670d\u98df\u5929\u5fc3\u77f3\u4e4b\u540e\uff0c\u836f\u529b \u53d1\u4f5c\uff0c\u660f\u8ff7\u7684\u90a3\u4e00\u6bb5\u671f\u95f4\uff0c\u6b27\u9633\u5a49\u66fe\u7ecf\u5230\u6765\u770b\u4ed6\uff0c\u800c\u4e14\u4e0d\u6070\u4e0e\u5929\u9b54\u6559\u4e3b\u505a\u5bf9\u820d\u547d\u7ef4\u62a4\u4ed6\uff0c \u8fd9\u4e9b\u4e8b\u60c5\uff0c\u4e8b\u540e\u8c37\u4e2d\u83b2\u90fd\u5bf9\u4ed6\u8bf4\u4e86\u3002\u6c5f\u6d77\u5929\u90a3\u4e00\u7f15\u60c5\u4e1d\uff0c\u5c4b\u7136\u4ecd\u662f\u98d8\u98d8\u8361\u8361\uff0c\u4e0d\u77e5\u8981\u7cfb \u5728\u8c01\u4eba\u8eab\u4e0a\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u56e0\u4e86\u6b64\u4e8b\uff0c\u800c\u51b3\u5b9a\u7231\u6b27\u9633\u5a49\uff0c\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u6b27\u9633\u5a49\u7684\u8fd9\u756a\u597d\u5904\uff0c \u4ed6\u5df2\u662f\u6c38\u94ed\u5fc3\u91cc\uff0c\u51b3\u4e0d\u80fd\u5fd8\u3002 \u3000\u3000\u8fd9\u4e00\u884c\u4eba\u8d8a\u6765\u8d8a\u8fd1\uff0c\u6c5f\u6d77\u5929\u7684\u5fc3\u8df3\u4e5f\u8d8a\u6765\u8d8a\u5267\uff0c\u4ed6\u60f3\u8d77\u6b27\u9633\u5a49\u5f80\u65e5\u5bf9\u4ed6\u7684\u4e00\u7247\u6df1\u60c5\uff0c \u518d\u60f3\u8d77\u8fd9\u4e00\u6b21\u5bf9\u4ed6\u7684\u7ef4\u62a4\uff0c\u51e0\u4e4e\u5fcd\u4e0d\u7740\u60f3\u51fa\u6765\u89c1\u5979\u4e00\u9762\u3002\u4f46\u4ed6\u7684\u6027\u683c\u867d\u7136\u63a5\u53d7\u4e86\u91d1\u4e16\u9057 \u7684\u4e00\u4e9b\u5f71\u54cd\uff0c\u5374\u7a76\u7adf\u4e0d\u5982\u91d1\u4e16\u9057\u7684\u6613\u4e8e\u51b2\u52a8\uff0c\u7ec8\u4e8e\u8fd8\u662f\u5fcd\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u884c\u56db\u4f17\u7684\u8bed\u58f0\u5df2\u6e10\u6e10\u53ef\u95fb\uff0c\u4f3c\u4e4e\u6b63\u5728\u4e89\u5435\u3002\u5ffd\u542c\u5f97\u6587\u5ef7\u58c1\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6b27\u9633\u4eb2 \u5bb6\uff0c\u4f60\u5f97\u62ff\u4e2a\u4e3b\u610f\uff01\u5a49\u59d1\u5a18\u63a5\u8fde\u4e24\u6b21\u80f3\u818a\u5411\u5916\u5f2f\uff0c\u524d\u65e5\u574f\u4e86\u54b1\u4eec\u7684\u5927\u4e8b\uff0c\u4eca\u65e5\u53c8\u8892\u62a4\u90a3 \u53f6\u51b2\u9704\uff0c\u4ee5\u81f4\u8ba9\u4ed6\u5154\u8131\uff0c\u4f60\u53eb\u6211\u5982\u4f55\u5411\u7687\u4e0a\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u6b27\u9633\u4ef2\u548c\u9053\uff1a\u201c\u8fd9\u91ce\u4e2b\u5934\u5e74\u7eaa\u8f7b\uff0c\u4e0d\u61c2\u4e8b\uff0c\u6211\u5e26\u5979\u56de\u53bb\uff1b\u81ea\u4f1a\u597d\u597d\u7684\u7ba1\u6559\u5979\u3002\u6587\u4eb2 \u5bb6\uff0c\u8bf7\u4f60\u770b\u5728\u4eb2\u621a\u4efd\u4e0a\uff0c\u906e\u7792\u4e00\u4e8c\uff0c\u5728\u56fd\u7389\u9762\u524d\uff0c\u4e0d\u63d0\u6b64\u4e8b\uff0c\u4e5f\u5c31\u662f\u4e86\u3002\u201d\u6587\u5ef7\u58c1\u51b7\u7b11 \u9053\uff1a\u201c\u4e0d\u63d0\u6b64\u4e8b\uff1f \u3000\u3000\u6b27\u9633\u4eb2\u5bb6\uff0c\u4f60\u7236\u5973\u4fe9\u53ef\u4ee5\u4e00\u8d70\u4e86\u4e4b\uff0c\u6211\u6587\u67d0\u4eba\u53ef\u8fd8\u5f97\u5728\u8fd9\u513f\u9732\u9762\uff0c\u660e\u65e5\u5728\u91d1\u9e70\u5bab\u4f1a \u4e0a\uff0c\u5018\u82e5\u6709\u4eba\u95ee\u8d77\uff1a\u59d3\u6587\u7684\uff0c\u542c\u8bf4\u91d1\u4e16\u9057\u4e5f\u4e0d\u662f\u4f60\u7684\u5bf9\u624b\uff0c\u600e\u4e48\u5374\u8fde\u53f6\u51b2\u9704\u8fd9\u6837\u7684\u540e\u751f \u5c0f\u5b50\u4e5f\u62fe\u6444\u4e0d\u6765\uff1f \u3000\u3000\u4f60\u53eb\u6211\u8fd9\u9762\u4e8e\u5f80\u54ea\u91cc\u6401\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u4e5f\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6b27\u9633\u5148\u751f\uff0c\u4ee4\u5a9b\u90a3\u53e3\u6bd2\u9488\uff0c\u4fa5\u5e78\u672a\u66fe\u8981\u4e86\u6211\u7684\u6027\u547d\uff0c\u8fd9\u7b14 \u5e10\u6211\u53ef\u4ee5\u4e0d\u5fc5\u518d\u7b97\uff1b\u4f46\u6211\u7b54\u5e94\u4e86\u7687\u989d\u5a18\u7684\u4e8b\u60c5\uff0c\u4eca\u665a\u5374\u7ed9\u4ee4\u5a9b\u5f04\u574f\uff0c\u89e3\u94c3\u8fd8\u9700\u7cfb\u94c3\u4eba\uff0c \u53ea\u6015\u8fd8\u5f97\u7740\u843d\u5728\u4ee4\u5a9b\u8eab\u4e0a\u4e86\u3002\u201d\u6b27\u9633\u5a49\u6012\u9053\uff1a\u201c\u653e\u5c41\uff0c\u4f60\u4e24\u4eba\u672c\u9886\u4e0d\u6d4e\uff0c\u7ed9\u8c37\u4e2d\u83b2\u7684\u54e5 \u54e5\u5c06\u53f6\u51b2\u9704\u653e\u8d70\uff0c\u5173\u6211\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u6b27\u9633\u4ef2\u548c\u5927\u60ca\u5931\u8272\uff0c\u559d\u9053\uff1a\u201c\u91ce\u4e2b\u5934\uff0c\u4f60\u518d\u80e1\u8bf4\uff0c\u6211\u5c31\u4e00\u638c\u6bd9\u4e86\u4f60\u3002\u6559\u4e3b\uff0c\u4eb2\u5bb6\uff0c \u6211\u5411\u4e24\u4f4d\u8d54\u7f6a\uff0c\u8bf7\u4f60\u4eec\u4e24\u4f4d\u5927\u4eba\u5927\u91cf\uff0c\u522b\u4e0e\u5c0f\u5b69\u5b50\u4e00\u822c\u89c1\u8bc6\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9634\u6c89\u6c89\u5730\u8bf4\u9053\uff1a\u201c\u4ee4\u5a9b\u8bf4\u6211\u672c\u9886\u4e0d\u6d4e\uff0c\u90a3\u4e5f\u4e0d\u9519\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u5f53\u65f6\u90a3\u5c0f\u5b50\u5df2\u7ed9\u6587\u6559\u4e3b\u7eca\u4f4f\uff0c\u8981\u4e0d\u662f\u4ee4\u5a9b\u4ece\u4e2d\u963b\u6320\uff0c\u6211\u65e9\u5df2\u628a\u90a3\u53f6\u51b2\u9704\u624b\u5230 \u64d2\u6765\u5566\uff01\u201d\u6b27\u9633\u4ef2\u548c\u5fd9\u8bf4\u9053\uff1a\u201c\u8fd9\u5f53\u7136\u662f\u5979\u7684\u9519\uff0c\u6559\u4e3b\uff0c\u4f60\u522b\u751f\u6c14\uff0c\u6211\u8fd9\u513f\u7ed9\u4f60\u8d54\u7f6a\u5566\uff01\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u4fa7\u8eab\u907f\u8fc7\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u4e0d\u6562\u5f53\uff0c\u6b27\u9633\u5148\u751f\uff0c\u4f60\u4e5f\u662f\u4e00\u4f4d\u6b66\u5b66\u5b97 \u5e08\uff0c\u54b1\u4eec\u5c3d\u53ef\u4ee5\u63a8\u5f00\u7a97\u5b50\u8bf4\u4eae\u8bdd\u3002\u6211\u8981\u62ff\u53f6\u51b2\u9704\u8fd9\u5c0f\u5b50\u5e76\u4e0d\u56f0\u96be\uff0c\u4f46\u8981\u5bf9\u4ed8\u8c37\u4e2d\u83b2\u5144\u59b9 \u5374\u786e\u5b9e\u662f\u672c\u9886\u4e0d\u6d4e\uff0c\u4ee4\u5ad2\u7684\u8bdd\u5e76\u6ca1\u8bf4\u9519\u3002\u6240\u4ee5\uff0c\u6211\u81ea\u77e5\u672c\u9886\u4e0d\u6d4e\uff0c\u8fd9\u5c31\u53ef\u5f97\u8981\u501f\u91cd\u4ee4\u5a9b\uff01\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7f13\u7f13\u9053\u6765\uff0c\u559c\u6012\u4e0d\u5f62\u4e8e\u8272\uff0c\u6b27\u9633\u4ef2\u548c\u542c\u4e86\uff0c\u53ef\u662f\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u6559\u4e3b\uff0c \u6055\u6211\u4e0d\u61c2\u4f60\u7684\u610f\u601d\uff0c\u5979\u4e00\u4e2a\u5c0f\u4e2b\u5934\u53c8\u6d4e\u5f97\u751a\u4e8b\uff0c\u600e\u8bf4\u8981\u501f\u91cd\u4e8e\u5979\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u53f6\u51b2\u9704\u4e0e\u8c37\u4e2d\u83b2\u5144\u59b9\u5982\u4eca\u5df2\u662f\u4e00\u8def\uff0c\u4ee4\u5a9b\u4e8e\u53f6\u51b2\u9704\u6709\u606f\uff0c\u53c8\u66fe\u7ef4\u62a4 \u6c5f\u6d77\u5929\uff0c\u8c37\u4e2d\u83b2\u5bf9\u5979\u60f3\u5fc5\u4e5f\u662f\u611f\u6fc0\u7684\u4e86\u3002 \u3000\u3000\u563f\u563f\uff0c\u6211\u53ea\u8981\u628a\u4ee4\u5a9b\u7559\u4e0b\uff0c\u81ea\u5fc5\u80fd\u628a\u4ed6\u4eec\u5f15\u6765\uff0c\u6211\u4e8c\u4eba\u6253\u4ed6\u4eec\u4e0d\u8fc7\uff0c\u96be\u9053\u5bab\u4e2d\u8fd9\u4e48 \u591a\u4eba\uff0c\u4e5f\u5bf9\u4ed8\u4e0d\u4e86\u4ed6\u4eec\u8fd9\u51e0\u4e2a\u5c0f\u8f88\uff1f \u3000\u3000\u6587\u5ef7\u58c1\u4e5f\u6b63\u662f\u8fd9\u6837\u7684\u5fc3\u601d\uff0c\u4ed6\u4f30\u8ba1\u4ed6\u4e0e\u5929\u9b54\u6559\u4e3b\u8054\u624b\uff0c\u5927\u7ea6\u53ef\u4ee5\u5bf9\u4ed8\u5f97\u4e86\u8c37\u4e2d\u83b2\u5144 \u59b9\uff0c\u518d\u52a0\u4e0a\u5389\u590d\u751f\u3001\u9c81\u6c0f\u5144\u5f1f\u7b49\u4eba\uff0c\u5373\u7b97\u6c5f\u6d77\u5929\u4e5f\u6765\u76f8\u52a9\u5bf9\u65b9\uff0c\u90a3\u4e5f\u4e0d\u8db3\u4e3a\u60e7\u3002\u56e0\u6b64\uff0c \u5f53\u52a1\u4e4b\u6025\uff0c\u53ea\u662f\u5982\u4f55\u5c06\u5bf9\u65b9\u5f15\u6765\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u8bf4\u4e86\u8fd9\u8bdd\uff0c\u6b27\u9633\u4ef2\u548c\u672a\u66fe\u5f00\u53e3\uff0c\u6587\u5ef7\u58c1\u4fbf\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u6b63\u662f\u53eb\u505a \u2018\u89e3\u94c3\u8fd8\u9700\u7cfb\u94c3\u4eba\u2019\uff0c\u6b27\u9633\u4eb2\u5bb6\uff0c\u4f60\u8981\u56de\u53bb\uff0c\u5c3d\u53ef\u81ea\u4fbf\uff0c\u5a49\u59d1\u5a18\u53ef\u5f97\u7559\u4e0b\u6765\uff01\u201d\u6b27\u9633\u4ef2 \u548c\u53d8\u4e86\u9762\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u6587\u4eb2\u5bb6\uff0c\u4f60\u5f85\u628a\u5979\u600e\u5730\uff1f\u201d\u6587\u5ef7\u58c1\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e5f\u4e0d\u600e\u5730\uff0c\u6211\u4eec \u628a\u5979\u4ea4\u7ed9\u56fd\u738b\u5904\u7f6e\uff0c\u5f53\u7136\uff0c\u5047\u620f\u771f\u505a\uff0c\u5c11\u4e0d\u5f97\u4e5f\u8981\u4ee4\u5a49\u59d1\u5a18\u53d7\u70b9\u6298\u78e8\uff01\u201d \u3000\u3000\u6b27\u9633\u4ef2\u548c\u52c3\u7136\u53d8\u8272\uff0c\u6124\u7136\u8bf4\u9053\uff1a\u201c\u6587\u5148\u751f\uff0c\u6211\u90a3\u5927\u4e2b\u5934\u6b7b\u4e86\uff0c\u4f60\u5c31\u4e0d\u518d\u987e\u5ff5\u4eb2\u5bb6\u7684 \u60c5\u4efd\u4e86\u4e48\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u5ffd\u5730\u51b7\u4f20\u8bf4\u9053\uff1a\u201c\u6b27\u9633\u5148\u751f\uff0c\u4f60\u90a3\u4f4d\u5927\u5c0f\u59d0\u53ef\u662f\u4e3a\u4e86\u53f6\u51b2\u9704\u5bb3\u76f8 \u601d\u75c5\u6b7b\u7684\u554a\uff01\u201d \u3000\u3000\u6b27\u9633\u4ef2\u548c\u6c14\u5f97\u53cc\u773c\u53d1\u767d\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u3001\u4f60\u3001\u4f60\uff0c\u4f60\u4eec\u592a\u6b3a\u4fae\u4eba\u5566\uff01\u201d\u6b27\u9633\u5a49\u9053\uff1a \u201c\u7239\uff0c\u59d0\u59d0\u7ed9\u4ed6\u4eec\u6587\u5bb6\u7684\u4eba\u5bb3\u6b7b\u4e86\uff0c\u8fd9\u4eb2\u5bb6\u4e0d\u8ba4\u4e5f\u7f62\uff01\u54b1\u95e8\u7ec8\u5357\u5c71\u6b27\u9633\u5bb6\u66fe\u6015\u8fc7\u8c01\u6765\uff1f\u201d \u3000\u3000\u6587\u5ef7\u58c1\u54c8\u54c8\u4e00\u7b11\uff1a\u201c\u5a49\u59d1\u5a18\uff0c\u8fd9\u53ef\u662f\u4f60\u81ea\u5df1\u8bf4\u7684\uff0c\u81ea\u4eec\u4eb2\u621a\u60c5\u4efd\u5df2\u65ad\uff0c\u53ef\u4f11\u602a\u6211\u65e0 \u793c\u4e86\uff01\u201d\u500f\u5730\u51fa\u638c\uff0c\u5411\u6b27\u9633\u5a49\u80cc\u5fc3\u4fbf\u6293\uff01 \u3000\u3000\u6b27\u9633\u5a49\u53ea\u77e5\u9053\u81ea\u5df1\u7236\u4eb2\u7684\u9739\u96f3\u638c\u4e0e\u96f7\u795e\u6307\u5929\u4e0b\u65e0\u53cc\uff0c\u5374\u4e0d\u77e5\u9053\u6587\u5ef7\u58c1\u66f4\u52a0\u5389\u5bb3\uff0c\u6240 \u4ee5\u5979\u8fd8\u751f\u6015\u7236\u4eb2\u6293\u4e0d\u7834\u8138\u76ae\uff0c\u4e0d\u80af\u548c\u6587\u5ef7\u58c1\u4f5c\u5bf9\u3002\u6b27\u9633\u4ef2\u548c\u5f53\u7136\u77e5\u9053\u6587\u5ef7\u58c1\u7684\u672c\u9886\uff0c\u5374 \u662f\u53eb\u82e6\u4e0d\u8fed\u3002 \u3000\u3000\u4f46\u6b27\u9633\u4ef2\u548c\u7a76\u7adf\u4e5f\u662f\u4e00\u5927\u9b54\u5934\u3001\u867d\u7136\u660e\u77e5\u4e0d\u654c\uff0c\u5374\u4e5f\u4e0d\u7518\u53d7\u8fb1\uff0c\u5f53\u4e0b\u4e00\u638c\u62cd\u51fa\uff0c\u5927 \u58f0\u559d\u9053\uff1a\u201c\u5a49\u513f\u5feb\u8d70\uff01\u201d \u3000\u3000\u6b27\u9633\u5a49\u6b63\u5728\u98de\u5954\uff0c\u5ffd\u89c9\u4e00\u80a1\u5927\u529b\u6293\u6765\uff0c\u7adf\u662f\u4e0d\u7531\u81ea\u5df1\u7684\u5012\u9000\u4e09\u6b65\uff0c\u6587\u5ef7\u58c1\u79bb\u5979\u6700\u5c11 \u6709\u4e00\u4e08\u5f00\u5916\uff0c\u4f46\u8fd9\u865a\u7a7a\u4e00\u6293\uff0c\u6b27\u9633\u5a49\u5df2\u662f\u65e0\u53ef\u62b5\u5fa1\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c \u6b27\u9633\u4ef2\u548c\u4e0e\u6587\u5ef7\u58c1\u53cc\u638c\u76f8\u4ea4\uff0c\u53ea\u89c9\u6c14\u8840\u7ffb\u6d8c\uff0c\u4e94\u810f\u516d\u8151\u90fd\u4f3c\u4e4e\u53d8\u4e86\u4f4d\u7f6e\uff0c\u6b27\u9633\u4ef2\u548c\u6b63\u8981 \u518d\u53d1\u96f7\u795e\u6307\uff0c\u6587\u5ef7\u58c1\u5df2\u662f\u4e00\u6307\u5148\u6233\u8fc7\u6765\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6b27\u9633\u4eb2\u5bb6\uff0c\u5f97\u7f6a\u4e86\uff01\u201d \u3000\u3000\u6b27\u9633\u5a49\u5f97\u5979\u7236\u4eb2\u6321\u4e86\u6587\u5ef7\u58c1\u7684\u4e00\u638c\uff0c\u90a3\u80a1\u51cc\u7a7a\u6293\u6765\u7684\u529b\u9053\u4e1a\u5df2\u79fb\u5f00\uff0c\u624b\u811a\u6d3b\u52a8\uff0c\u53c8 \u5411\u524d\u5954\uff0c\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u5a49\u59d1\u5a18\uff0c\u6211\u518d\u9886\u6559\u4f60\u6bd2\u9488\u7684\u5389\u5bb3\u3002\u201d\u7b11\u58f0\u672a\u6bd5\uff0c\u500f\u5730\u4fbf\u5230\u4e86 \u6b27\u9633\u5a49\u773c\u524d\u3002 \u3000\u3000\u8fd9\u4e00\u5207\u7ecf\u8fc7\u90fd\u770b\u5728\u6c5f\u6d77\u5929\u773c\u4e2d\uff0c\u4ed6\u5fc3\u4e2d\u8f6c\u4e86\u597d\u51e0\u6b21\u5ff5\u5934\uff0c\u731b\u5730\u60f3\u9053\uff1a\u201c\u6628\u65e5\u4f60\u547d\u60ac \u654c\u624b\uff0c\u5979\u4e0d\u987e\u4e00\u5207\u7684\u6765\u6551\u4f60\uff1b\u5982\u4eca\u5979\u4e5f\u662f\u547d\u60ac\u654c\u624b\uff0c\u4f60\u5c82\u53ef\u7f6e\u4e4b\u4e0d\u7406\uff1f\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u5fc3 \u5ff5\u7acb\u51b3\uff0c\u5927\u559d\u4e00\u58f0\uff1a \u3000\u3000\u201c\u4f4f\u624b\uff01\u201d\u51cc\u7a7a\u4e00\u4e2a\u7b4b\u6597\uff0c\u4fbf\u4ece\u5c71\u9876\u4e0a\u76f4\u8df3\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u5f53\u771f\u662f\u98de\u5c06\u519b\u4ece\u5929\u800c\u964d\uff0c\u6b27\u9633\u5a49\u5927\u559c\u5982\u72c2\uff0c\u53eb\u9053\uff1a \u3000\u3000\u201c\u6d77\u54e5\uff01\u201d\u8fd9\u4e2a\u201c\u54e5\u201d\u5b57\u65b9\u624d\u51fa\u53e3\uff0c\u5df2\u7ed9\u5929\u9b54\u6559\u4e3b\u4e00\u628a\u6263\u5728\u624b\u8155\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5c38\u5982\u5de8\u9e70\uff0c\u51cc\u7a7a\u6251\u4e0b\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5929\u9b54\u6559\u4e3b\u5df1\u628a\u6b27\u9633\u5a49\u4e3e\u4e86\u8d77\u6765\uff0c\u906e \u7740\u81ea\u5df1\u7684\u5934\u9876\uff0c\u5f80\u4e0a\u4e00\u6321\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u3000\u3000\u201c\u597d\uff0c\u4f60\u6293\u5427\uff01\u201d \u3000\u3000\u5c0f\u84ec\u83b1\u5c71\u867d\u4e0d\u6839\u9ad8\uff0c\u4e5f\u6709\u4e8c\u4e09\u5341\u4e08\uff0c\u4ece\u8fd9\u6837\u7684\u9ad8\u5904\u8dc3\u4e0b\uff0c\u52b2\u9053\u81ea\u662f\u5927\u5f97\u60ca\u4eba\uff0c\u8fd9\u4e00 \u6293\u82e5\u7136\u6293\u7740\u6b27\u9633\u5a49\u7684\u8eab\u4f53\uff0c\u53ea\u6015\u5f53\u573a\u5c31\u8981\u6293\u5f97\u5979\u8eab\u4f53\u7834\u88c2\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u867d\u662f\u6b66\u529f\u9ad8\u5f3a\uff0c\u4f46\u8981\u5728\u534a\u7a7a\u4e2d\u715e\u4f4f\u8fd9\u6025\u5760\u4e4b\u52bf\uff0c\u5374\u4e5f\u4e0d\u80fd\uff0c\u4ed6\u6b63\u662f\u5411\u5929\u9b54\u6559 \u4e3b\u6251\u6765\uff0c\u773c\u770b\u5c31\u8981\u78b0\u4e0a\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u5343\u94a7\u4e00\u53d1\uff0c\u5371\u673a\u77ac\u606f\u4e4b\u95f4\uff0c\u6c5f\u6d77\u5929\u53cc\u8db3\u5ffd\u5730\u4ea4\u53c9\u8e22\u51fa\uff0c\u5de6\u811a\u5728\u53f3\u811a\u811a\u80cc\u4e00\u8e0f\uff0c \u501f\u7740\u8fd9\u80a1\u529b\u9053\uff0c\u8eab\u5b50\u62d4\u9ad8\u5c11\u8bb8\uff0c\u518d\u843d\u4e0b\u6765\u3002\u5982\u6b64\u4e00\u6765\uff0c\u6025\u5760\u4e4b\u52bf\uff0c\u767b\u65f6\u5927\u51cf\uff0c\u672c\u6765\u8981\u78b0 \u7740\u6b27\u9633\u5a49\u7684\uff0c\u7ecf\u8fc7\u8fd9\u4e48\u4e00\u4e2a\u8f6c\u6298\u3001\u51cf\u901f\uff0c\u843d\u5230\u5730\u4e0a\u7684\u65f6\u5019\uff0c\u4e5f\u5c31\u79bb\u5f00\u5979\u4e00\u4e08\u6709\u591a\u4e86\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u4e00\u89c1\u6709\u673a\u53ef\u4e58\uff0c\u8d81\u7740\u6c5f\u6d77\u5929\u7acb\u811a\u672a\u7a33\uff0c\u7acb\u5373\u4e00\u638c\u51fb\u6765\uff0c\u6c5f\u6d77\u5929\u53cd\u624b\u4e00\u62cd\uff0c\u53ea \u89c9\u5bf9\u65b9\u7684\u638c\u529b\u4f3c\u6709\u5982\u65e0\uff0c\u672c\u8eab\u5374\u88ab\u81ea\u5df1\u7684\u638c\u529b\u5e26\u52a8\uff0c\u6ed1\u51fa\u4e24\u6b65\u3002\u5fc3\u4e2d\u65b9\u81ea\u8b66\u89c9\uff0c\u6587\u5ef7\u58c1 \u731b\u5730\u4e00\u58f0\u5927\u559d\uff0c\u638c\u529b\u4e00\u53d1\u65e0\u9057\uff01 \u3000\u3000\u539f\u6765\u6587\u5ef7\u58c1\u8001\u5978\u5de8\u6ed1\uff0c\u6df1\u77e5\u6c5f\u6d77\u5929\u670d\u98df\u4e86\u5929\u5fc3\u77f3\u4e4b\u540e\uff0c\u529f\u529b\u5927\u589e\uff0c\u96be\u4ee5\u786c\u62fc\uff0c\u53ea\u80fd \u667a\u53d6\u3002\u4ed6\u65e9\u5df2\u7ec3\u6210\u201c\u4e09\u8c61\u5f52\u5143\u201d\u7684\u90aa\u6d3e\u7edd\u9876\u795e\u529f\uff0c\u81ea\u5fd6\u82e5\u7136\u53ea\u5b88\u4e0d\u653b\uff0c\u7528\u4ee5\u9632\u8eab\uff0c\u7edd\u4e0d \u81f3\u4e8e\u7ed9\u6c5f\u6d77\u5929\u4e00\u638c\u51fb\u5012\uff0c\u56e0\u800c\u5728\u51fa\u638c\u4e4b\u65f6\uff0c\u4e00\u65b9\u9762\u7528\u4e86\u4e2a\u201c\u5378\u201d\u3001\u5b57\u8bc0\uff0c\u5c06\u5bf9\u65b9\u7684\u521a\u731b \u4e4b\u52b2\u5378\u53bb\u5c11\u8bb8\uff0c\u4e00\u65b9\u9762\u7f29\u5c0f\u9632\u5fa1\u7684\u8303\u56f4\uff0c\u62c9\u957f\u53cc\u65b9\u7684\u8ddd\u79bb\uff0c\u7528\u610f\u5c31\u5728\u8bf1\u53d1\u6c5f\u6d77\u5929\u7684\u5168\u90e8 \u638c\u529b\u4e4b\u540e\uff0c\u731d\u7136\u53cd\u51fb\u3002\u8fd9\u6b63\u662f\u5175\u6cd5\u4e0a\u201c\u907f\u5176\u671d\u9510\uff0c\u51fb\u5176\u66ae\u5f52\u201d\u7684\u9053\u7406\u3002 \u3000\u3000\u4ed6\u80fd\u591f\u628a\u638c\u529b\u4f7f\u5f97\u865a\u865a\u5b9e\u5b9e\uff0c\u4f3c\u6709\u5982\u65e0\uff0c\u6b66\u5b66\u7684\u9020\u8be3\uff0c\u786e\u5b9e\u7b97\u5f97\u662f\u9ad8\u660e\u4e4b\u6781\uff0c\u5018\u82e5 \u5bf9\u65b9\u7684\u529f\u529b\u6bd4\u4ed6\u9ad8\u51fa\u4e0d\u662f\u592a\u591a\uff0c\u8fd9\u65f6\u5b9a\u5c06\u662f\u5f3a\u5f29\u4e4b\u672a\uff0c\u4e0d\u80fd\u4f24\u4ed6\uff0c\u800c\u53ea\u6709\u4e3a\u4ed6\u6240\u4f24\u4e86\u3002 \u54ea\u77e5\u6c5f\u6d77\u5929\u7684\u5185\u529f\u672c\u6765\u5df2\u6709\u76f8\u5f53\u57fa\u7840\uff0c\u5373\u4f7f\u5728\u672a\u670d\u5929\u5fc3\u77f3\u4e4b\u524d\uff0c\u4ed6\u5df2\u4e0e\u6587\u5ef7\u58c1\u76f8\u5dee\u4e0d\u8fdc\u3002 \u518d\u52a0\u4e0a\u4e09\u9897\u5929\u5fc3\u77f3\u6240\u5e73\u6dfb\u7684\u4e09\u5341\u5e74\u529f\u529b\uff0c\u5185\u529b\u84c4\u79ef\u4e4b\u539a\uff0c\u5b9e\u8fdc\u8fdc\u8d85\u51fa\u6587\u5ef7\u58c1\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u53ea\u9053\u5bf9\u65b9\u4e4b\u52bf\u5df2\u8870\uff0c\u731d\u7136\u53cd\u51fb\uff0c\u54ea\u77e5\u8fd9\u6b63\u662f\u6c5f\u6d77\u5929\u5c06\u8ba1\u5c31\u8ba1\uff0c\u5148\u8bf1\u53d1\u4ed6\u7684\u638c \u529b\u3002\u53cc\u65b9\u52fe\u5fc3\u6597\u89d2\uff0c\u4e0d\u8fc7\u4e00\u77ac\uff0c\u9661\u7136\u95f4\u6587\u5ef7\u58c1\u53ea\u89c9\u5bf9\u65b9\u7684\u638c\u529b\u6392\u5c71\u5012\u6d77\u800c\u6765\uff0c\u800c\u4e14\u7adf\u4f3c \u65e0\u7a77\u65e0\u5c3d\uff0c\u524d\u9762\u4e00\u9053\u52b2\u529b\u672a\u901d\uff0c\u540e\u9762\u4e00\u9053\u52b2\u529b\u53c8\u52a0\u4e0a\u6765\uff0c\u91cd\u91cd\u53e0\u53e0\uff0c\u6c9b\u7136\u83ab\u4e4b\u80fd\u5fa1\uff0c\u6587 \u5ef7\u58c1\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u64a4\u638c\u540e\u8dc3\uff0c\u53ea\u89c9\u53cc\u773c\u53d1\u9ed1\uff0c\u201c\u54c7\u201d\u7684\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff01 \u3000\u3000\u6b64\u65f6\u6c5f\u6d77\u5929\u82e5\u8981\u53d6\u4ed6\u6027\u547d\uff0c\u6613\u5982\u53cd\u638c\uff0c\u4f46\u4ed6\u6025\u4e8e\u6551\u6b27\u9633\u5a49\uff0c\u5374\u65e0\u6687\u53bb\u5bf9\u4ed8\u6587\u5ef7\u58c1\u4e86\u3002 \u8fd9\u65f6\u5929\u9b54\u6559\u4e3b\u6293\u7740\u6b27\u9633\u5a49\u4f5c\u4e3a\u76fe\u724c\uff0c\u53c8\u5df2\u5954\u51fa\u5341\u6570\u4e08\u5730\uff0c\u6b27\u9633\u4ef2\u548c\u5728\u540e\u9762\u7d27\u8feb\uff0c\u59cb\u7ec8\u6ca1 \u6709\u8ffd\u4e0a\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u626c\u58f0\u53eb\u9053\uff1a\u201c\u6559\u4e3b\u6211\u5ff5\u5728\u6614\u65e5\u9999\u706b\u4e4b\u60c5\uff0c\u8bf7\u4f60\u628a\u6b27\u9633\u59d1\u5a18\u653e\u4e0b\uff0c\u4e24\u7f62\u7518\u4f11\u3002 \u5426\u5219\u53ef\u4f11\u602a\u6211\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a \u3000\u3000\u201c\u6d77\u5929\uff0c\u4f60\u5df2\u6709\u4e86\u8c37\u4e2d\u83b2\uff0c\u8fd8\u82e6\u82e6\u8ffd\u6b27\u9633\u59d1\u5a18\u4f5c\u751a\uff1f\u201d\u6c5f\u6d77\u5929\u5927\u6012\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c\u767b \u65f6\u5982\u7bad\u79bb\u5f26\uff0c\u76f4\u5c04\u51fa\u53bb\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u6b27\u9633\u4eb2\u5bb6\uff0c\u4f60\u4e5f\u8be5\u6709\u4e2a\u51b3\u65ad\u4e86\uff01\u201d\u6c5f\u6d77\u5929\u4e0e\u6b27\u9633\u4ef2\u548c\u672c\u662f\u671d\u7740 \u540c\u4e00\u65b9\u5411\u8ffd\u8d76\u5929\u9b54\u6559\u4e3b\u7684\uff0c\u6c5f\u6d77\u5929\u540e\u53d1\u5148\u81f3\u3002\u8fd9\u65f6\u6b63\u597d\u8d8a\u8fc7\u6b27\u9633\u4ef2\u548c\u7684\u524d\u5934\uff0c\u79bb\u5929\u9b54\u6559 \u4e3b\u5df2\u4e0d\u8fc7\u6570\u4e08\u4e4b\u5730\uff0c\u6b27\u9633\u4ef2\u548c\u4e00\u58f0\u4e0d\u54cd\uff0c\u5ffd\u5730\u731b\u529b\u4e00\u6233\uff0c\u4e00\u6307\u6233\u4e2d\u6c5f\u6d77\u5929\u80cc\u810a\u690e\u9aa8\u6b63\u4e2d \u7684\u201c\u7ae0\u95e8\u7a74\u201d\uff01 \u3000\u3000\u539f\u6765\u6b27\u9633\u4ef2\u548c\u521a\u624d\u4e0e\u6587\u5ef7\u58c1\u5bf9\u4e86\u4e00\u638c\u4e00\u6307\uff0c\u90a3\u4e00\u638c\u6253\u5f97\u4ed6\u6c14\u8840\u7ffb\u6d8c\uff0c\u4f46\u672a\u53d7\u5185\u4f24\uff0c \u4e5f\u8fd8\u7f62\u4e86\uff0c\u90a3\u4e00\u6307\u5374\u662f\u70b9\u4e86\u4ed6\u7763\u8109\u7684\u9690\u7a74\uff0c\u8fd9\u9690\u7a74\u6df1\u85cf\u4f53\u5185\uff0c\u88ab\u5bf9\u65b9\u7684\u5185\u529b\u900f\u8fc7\uff0c\u5f53\u573a \u6ca1\u6709\u53d1\u4f5c\uff0c\u65e5\u540e\u5374\u6709\u6027\u547d\u4e4b\u5fe7\uff0c\u6587\u5ef7\u58c1\u7528\u7684\u53c8\u662f\u72ec\u95e8\u624b\u6cd5\uff0c\u9664\u4ed6\u672c\u4eba\u4e4b\u5916\uff0c\u522b\u4eba\u7edd\u96be\u89e3 \u6551\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u4e5f\u662f\u6b66\u5b66\u884c\u5bb6\uff0c\u5f53\u7136\u77e5\u9053\u5389\u5bb3\uff0c\u4e0d\u8fc7\u4ed6\u60f3\u5148\u628a\u5973\u513f\u6551\u4e0b\uff0c\u7136\u540e\u518d\u5411\u6587\u5ef7\u58c1 \u6c42\u60c5\u3002\u54ea\u77e5\u6587\u5ef7\u58c1\u5df2\u5148\u51fa\u8a00\u5a01\u80c1\uff0c\u6b27\u9633\u4ef2\u548c\u56fa\u7136\u662f\u7231\u5973\u60c5\u6df1\uff0c\u4f46\u5bf9\u81ea\u5df1\u7684\u6027\u547d\u5374\u66f4\u52a0\u7231 \u60dc\uff0c\u4e00\u60f3\u5018\u82e5\u4e0d\u52a9\u6587\u5ef7\u58c1\u5bf9\u4ed8\u5916\u654c\uff0c\u5973\u513f\u672a\u5fc5\u5f97\u6551\u56de\u6765\uff0c\u81ea\u5df1\u6027\u547d\u4e5f\u5c06\u4e0d\u4fdd\uff0c\u52ff\u4fc3\u4e4b\u95f4\uff0c \u54ea\u5bb9\u5f97\u4ed6\u4ece\u957f\u8003\u8651\uff0c\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u4fbf\u7acb\u5373\u51fa\u6307\u4f24\u4eba\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u867d\u7136\u77e5\u9053\u6b27\u9633\u4ef2\u548c\u662f\u4e2a\u9b54\u5934\uff0c\u4f46\u4ed6\u73b0\u5728\u662f\u4e3a\u4e86\u6551\u6b27\u9633\u4ef2\u548c\u7684\u5973\u513f\uff0c\u53ef\u8bf4\u662f\u4e0e \u4ed6\u540c\u4ec7\u654c\u5ffe\uff0c\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u6b27\u9633\u4ef2\u548c\u7adf\u4f1a\u5bf9\u4ed6\u6697\u7b97\uff0c\u56e0\u6b64\u4e00\u70b9\u4e5f\u6ca1\u6709\u9632\u5907\u3002 \u3000\u3000\u8fd9\u7ae0\u95e8\u7a74\u662f\u4e09\u7126\u7ecf\u8109\u4ea4\u4f1a\u4e4b\u70b9\uff0c\u4eba\u8eab\u6b7b\u7a74\u4e4b\u4e00\uff0c\u6c5f\u6d77\u5929\u867d\u6709\u62a4\u4f53\u795e\u529f\uff0c\u4f46\u4e8b\u5148\u6ca1\u6709 \u9632\u5907\uff0c\u672a\u66fe\u8fd0\u6c14\u62b5\u5fa1\uff0c\u4e2d\u4e86\u8fd9\u4e00\u6307\uff0c\u4e5f\u4e0d\u7981\u75db\u5f7b\u5fc3\u80ba\uff0c\u4f24\u4e86\u4e00\u70b9\u5143\u6c14\u3002\u8fd9\u8fd8\u662f\u7531\u4e8e\u4ed6\u5df2 \u670d\u98df\u4e86\u5929\u5fc3\u77f3\u7684\u539f\u6545\uff0c\u5426\u5219\u4e0d\u6b7b\u4e5f\u5f97\u91cd\u4f24\u3002\u5f53\u4e0b\u53c8\u60ca\u53c8\u6012\uff0c\u53cd\u624b\u5c31\u662f\u4e00\u638c\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u70b9\u4e2d\u4e86\u6c5f\u6d77\u5929\u7684\u7ae0\u95e8\u7a74\uff0c\u4e5f\u662f\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u4ed6\u5c45\u7136\u4fbf\u80fd\u51fa\u638c\u8fd8\u51fb\uff0c\u4e00\u60ca\u4e4b \u4e0b\uff0c\u6765\u4e0d\u53ca\u8dc3\u5f00\uff0c\u5df2\u7ed9\u6c5f\u6d77\u5929\u7684\u638c\u529b\u7f69\u4f4f\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u53cd\u624b\u53d1\u638c\uff0c\u968f\u5373\u8f6c\u8fc7\u5934\u6765\uff0c\u773c\u5149\u4e00\u77a5\uff0c\u89c1\u4e86\u6b27\u9633\u4ef2\u548c\u60ca\u60f6\u7684\u795e\u60c5\uff0c\u5fc3\u4e2d\u4e0d\u7981 \u4e00\u8f6f\uff0c\u60f3\u9053\uff1a\u201c\u6211\u8981\u6551\u6b27\u9633\u5a49\uff0c\u600e\u597d\u4f24\u4e86\u5979\u7684\u7236\u4eb2\uff1f\u201d\u4ed6\u7684\u638c\u529b\u65e9\u5df2\u5230\u4e86\u968f\u5fc3\u6240\u6b32\uff0c\u6536 \u53d1\u81ea\u5982\u7684\u5883\u754c\uff0c\u5fc3\u5ff5\u7535\u8f6c\uff0c\u5c31\u5728\u638c\u529b\u5c06\u53d1\u672a\u53d1\u4e4b\u95f4\uff0c\u731b\u7684\u6536\u4e86\u56de\u6765\uff0c\u9976\u4e86\u6b27\u9633\u4ef2\u548c\u4e00\u547d\u3002 \u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u4ed6\u4e0e\u5929\u9b54\u6559\u4e3b\u7684\u8ddd\u79bb\u53c8\u62c9\u8fdc\u4e86\u3002\u53ea\u597d\u518d\u53d1\u529b\u8ffd\u8d76\u3002\u7ed5\u8fc7\u4e86\u5c0f\u84ec\u83b1\u5c71\uff0c\u582a \u582a\u53c8\u8981\u8ffd\u4e0a\uff0c\u5ffd\u542c\u5f97\u523a\u8033\u7684\u543c\u58f0\uff0c\u4e24\u5934\u91d1\u5149\u95ea\u95ea\u7684\u602a\u517d\u98ce\u9a70\u7535\u63a3\u800c\u6765\uff0c\u6b63\u662f\u90a3\u4e24\u53ea\u91d1\u6bdb \u72e1\uff0c\u968f\u7740\u6765\u7684\u662f\u4e2a\u957f\u53d1\u62ab\u80a9\u7684\u5c11\u5e74\uff0c\u6c5f\u6d77\u5929\u8ba4\u5f97\u662f\u5929\u9b54\u6559\u7684\u53e6\u4e00\u526f\u6559\u4e3b\u5389\u590d\u751f\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8bc6\u5f97\u8fd9\u4e24\u53ea\u91d1\u6bdb\u5b69\u7684\u6765\u5386\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5b83\u4eec\u662f\u6211\u5e08\u7236\u7684\u670b\u53cb\uff0c\u6211\u53ef\u4e0d\u4fbf\u4f24 \u4e86\u5b83\u4eec\u3002\u201d \u3000\u3000\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u6765\u5f97\u5feb\u6781\uff0c\u4e00\u53ea\u8df3\u8d77\u6765\u6293\u4ed6\u7684\u5934\u76d6\uff0c\u53e6\u4e00\u53ea\u5c31\u5f20\u5f00\u53e3\u54ac\u4ed6\u7684\u5589\u5499\uff0c\u6c5f \u6d77\u592d\u6df1\u77e5\u8fd9\u4e24\u53ea\u91d1\u6bdb\u72e1\u94dc\u76ae\u94c1\u9aa8\uff0c\u5018\u82e5\u81ea\u5df1\u4e0d\u4f7f\u51fa\u5185\u5bb6\u771f\u529b\uff0c\u51b3\u96be\u5c06\u5b83\u4eec\u51fb\u9000\uff0c\u53ef\u662f\u7531 \u4e8e\u4ed6\u7684\u5185\u529b\u4e43\u662f\u670d\u98df\u4e86\u5929\u5fc3\u77f3\u4e4b\u540e\u7a81\u7136\u589e\u957f\u7684\uff0c\u53ea\u6015\u8fd8\u672a\u80fd\u4f7f\u5f97\u6070\u5230\u597d\u5904\uff0c\u201c\u8f7b\u4e86\u7b49\u4e8e \u7ed9\u5b83\u4eec\u6293\u75d2\uff1a\u91cd\u4e86\u53c8\u6015\u5b83\u4eec\u7981\u53d7\u4e0d\u8d77\u3002\u5b83\u4eec\u867d\u7136\u5389\u5bb3\uff0c\u7a76\u7adf\u662f\u755c\u7c7b\uff0c\u4e0d\u6bd4\u6b66\u5b66\u6df1\u6e5b\u4e4b\u58eb \u61c2\u5f97\u8fd0\u529f\u62b5\u5fa1\uff0c\u5185\u529b\u4e00\u900f\u8fc7\u5b83\u4eec\u575a\u97e7\u7684\u76ae\u80a4\uff0c\u5fc5\u5c06\u9707\u88c2\u5b83\u4eec\u7684\u5fc3\u810f\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u65e2\u4e0d\u613f\u4f24\u5bb3\u5b83\u4eec\uff0c\u53ea\u597d\u4f7f\u7528\u5929\u7f57\u6b65\u6cd5\u95ea\u5f00\uff0c\u4f46\u8fd9\u4e24\u53ea\u91d1\u6bdb\u72e1\u77eb\u6377\u4e4b\u6781\uff0c\u6c5f\u6d77 \u5929\u7684\u5929\u7f57\u6b65\u6cd5\u867d\u7136\u795e\u5999\u65e0\u6bd4\uff0c\u4e5f\u9669\u9669\u7ed9\u5b83\u4eec\u6293\u4e2d\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5389\u590d\u751f\u624b\u6325\u7389\u5c3a\uff0c\u4e5f\u5df2\u8dd1\u5230\u773c\u524d\uff0c\u6c5f\u6d77\u5929\u7709\u5934\u4e00\u76b1\uff0c\u9661\u7136\u95f4\u4e00\u4e2a \u7b4b\u6597\u7ffb\u5f00\uff0c\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u8ddf\u8e2a\u6251\u5230\uff0c\u6c5f\u6d77\u5929\u5ffd\u5730\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u638c\u6263\u51fa\uff0c\u5c06\u5047\u5c71\u77f3\u6253\u788e\u4e00 \u5757\uff0c\u788e\u77f3\u4f3c\u6d41\u5f39\u822c\u56db\u9762\u6fc0\u5c04\uff0c\u8fd9\u4e9b\u788e\u77f3\u867d\u7136\u4e0d\u80fd\u4f24\u5bb3\u91d1\u6bdb\u72e1\uff0c\u5374\u4e5f\u6253\u5f97\u5b83\u4eec\u611f\u5230\u75bc\u75db\uff0c \u8fd9\u4e24\u53ea\u91d1\u6bdb\u72e1\u9887\u5177\u7075\u4ed5\uff0c\u8bc6\u5f97\u5389\u5bb3\uff0c\u8fde\u5fd9\u907f\u5f00\u3002 \u3000\u3000\u5389\u590d\u751f\u7389\u5c3a\u4e00\u6325\uff0c\u8d81\u6c5f\u6d77\u5929\u7acb\u8db3\u672a\u7a33\uff0c\u75be\u5373\u70b9\u5230\uff0c\u6c5f\u6d77\u5929\u4f38\u6307\u4e00\u5f39\u3002\u53ea\u542c\u5f97\u201c\u94ee\u201d \u7684\u4e00\u58f0\uff0c\u5389\u590d\u751f\u7684\u7389\u5c3a\u7ed9\u4ed6\u5f39\u5f00\uff0c\u4f46\u6c5f\u6d77\u5929\u7684\u864e\u53e3\u4ea6\u81ea\u53d1\u70ed\uff0c\u4e0d\u7981\u5fc3\u4e2d\u4e00\u51db\uff1a\u201c\u6211\u5df2\u4f7f \u51fa\u5f39\u6307\u795e\u901a\u7684\u529f\u592b\uff0c\u7adf\u672a\u80fd\u5c06\u4ed6\u7684\u7389\u5c3a\u6253\u843d\uff0c\u96be\u9053\u4ed6\u7684\u529f\u592b\u8fd8\u5728\u6587\u5ef7\u58c1\u4e4b\u4e0a\uff1f\u201d \u3000\u3000\u5176\u5b9e\u5389\u590d\u751f\u7684\u529f\u592b\u4e0e\u6587\u5ef7\u58c1\u5b9e\u662f\u4e0d\u5206\u4e0a\u4e0b\uff0c\u5404\u6709\u64c5\u957f\uff0c\u5018\u8bba\u5230\u5185\u529f\u4e4b\u6df1\u539a\uff0c\u6587\u5ef7\u58c1 \u8fd8\u80dc\u4ed6\u4e00\u7b79\u3002\u4f46\u6587\u5ef7\u58c1\u7ed9\u6c5f\u6d77\u5929\u7684\u638c\u529b\u9707\u5f97\u53e3\u55b7\u9c9c\u8840\uff0c\u800c\u5389\u590d\u751f\u5374\u53ef\u5e94\u4ed8\u81ea\u5982\uff0c\u8fd9\u5176\u4e2d \u6709\u4e24\u4e2a\u7f18\u6545\u3002 \u3000\u3000\u4e00\u6765\u662f\u7531\u4e8e\u6c5f\u6d77\u5929\u521a\u521a\u53d7\u4e86\u6b27\u9633\u4ef2\u548c\u7684\u6697\u7b97\uff0c\u5143\u6c14\u7a0d\u7a0d\u53d7\u4f24\uff0c\u4e8c\u6765\u662f\u5389\u590d\u751f\u5360\u4e86\u5175 \u5668\u7684\u4fbf\u5b9c\uff0c\u4ed6\u7684\u90a3\u67c4\u4e94\u5c3a\u4e43\u662f\u4e00\u4ef6\u7389\u7269\uff0c\u4e54\u5317\u6e9f\u5f53\u5e74\u91c7\u53d6\u6d77\u5e95\u5bd2\u7389\u6253\u6210\u7684\uff0c\u957f\u5ea6\u4e0d\u8fc7\u4e00 \u5c3a\u4e8c\u5bf8\uff0c\u5374\u6709\u767e\u591a\u65a4\u91cd\uff0c\u6c5f\u6d77\u5929\u7684\u5185\u529b\u88ab\u8fd9\u67c4\u7389\u5c3a\u63a5\u4e86\u4e00\u534a\uff0c\u8fd8\u6709\u4e00\u534a\u4f20\u5230\u5389\u590d\u751f\u7684\u8eab \u4f53\uff0c\u4ed6\u5f53\u7136\u53ef\u4ee5\u5e94\u4ed8\u81ea\u5982\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u66fe\u542c\u5f97\u4e49\u4f2f\u59ec\u6653\u98ce\u8bf4\u8fc7\uff0c\u8bf4\u8fd9\u5389\u590d\u751f\u591a\u534a\u662f\u5389\u80dc\u7537\u7684\u5bb6\u4eba\uff0c\u5389\u80dc\u7537\u662f\u91d1\u4e16\u9057 \u7684\u59bb\u5b50\uff0c\u6c5f\u6d77\u5929\u770b\u5728\u5e08\u7236\u7684\u4efd\u4e0a\uff0c\u4e5f\u4e0d\u613f\u4f24\u4ed6\u3002\u4ed6\u63a5\u8fde\u5f39\u4e86\u4e09\u6307\uff0c\u90fd\u672a\u80fd\u5c06\u5389\u590d\u751f\u7684\u7389 \u5c3a\u5f39\u843d\uff0c\u4f46\u4ed6\u7684\u5185\u529b\u4e00\u91cd\u91cd\u7684\u52a0\u4e0a\u53bb\uff0c\u5389\u590d\u751f\u4e5f\u81ea\u611f\u5230\u624b\u81c2\u9178\u9ebb\u3002 \u3000\u3000\u5389\u590d\u751f\u5bf9\u5929\u9b54\u6559\u4e3b\u6700\u662f\u5fe0\u5fc3\uff0c\u660e\u77e5\u4e0d\u654c\uff0c\u4e5f\u6b7b\u7f20\u4e0d\u9000\u3002\u4ed6\u4e00\u58f0\u547c\u5578\uff0c\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1 \u53c8\u6251\u4e0a\u6765\u3002\u6c5f\u6d77\u5929\u529b\u654c\u4e00\u4eba\u4e8c\u517d\uff0c\u5fc3\u4e2d\u53c8\u5b58\u6709\u987e\u5fcc\uff0c\u5f04\u5f97\u5341\u5206\u72fc\u72c8\u3002\u5e78\u800c\u6587\u5ef7\u58c1\u4e5f\u53d7\u4e86 \u4f24\uff0c\u4e00\u65f6\u95f4\u672a\u80fd\u6062\u590d\uff0c\u4e0d\u6562\u4e0a\u524d\u76f8\u52a9\uff0c\u5426\u5219\u6c5f\u6d77\u5929\u66f4\u96be\u5e94\u4ed8\uff0c\u6c5f\u6d77\u5929\u7aed\u529b\u5468\u65cb\u4e86\u5341\u6765\u62db\u3001 \u5929\u9b54\u6559\u4e3b\u6108\u8dd1\u6108\u8fdc\uff0c\u80cc\u5f71\u90fd\u51e0\u4e4e\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5927\u4e3a\u7740\u6025\uff0c\u6b63\u5728\u6b64\u65f6\uff0c\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u53c8\u5df2\u6251\u6765\uff0c\u6c5f\u6d77\u5929\u5ffd\u5730\u53cc\u817f\u534a\u5f2f\uff0c\u5f80\u4e0b \u4e00\u8e72\uff0c\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u4f55\u7b49\u77eb\u6377\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u957f\u722a\u65e9\u5df2\u6293\u7740\u4ed6\u7684\u80a9\u5934\u3002\u5389\u590d\u751f\u5927\u559c\uff0c\u624b\u6325 \u7389\u5c3a\uff0c\u6b63\u8981\u70b9\u4ed6\u7a74\u9053\uff0c\u731b\u542c\u5f97\u6c5f\u6d77\u5929\u5927\u559d\u4e00\u58f0\uff0c\u84e6\u5730\u957f\u8eab\u800c\u8d77\uff0c\u4e00\u624b\u6293\u7740\u4e00\u53ea\u91d1\u6bdb\u72e1\uff0c \u9ad8\u9ad8\u4e3e\u8fc7\u5934\u9876\uff0c\u4e00\u4e2a\u65cb\u98ce\u6025\u821e\uff0c\u5c06\u4e24\u53ea\u91d1\u6bdb\u72e1\u629b\u4e0a\u4e86\u534a\u7a7a\u3002 \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u65e2\u4e0d\u60f3\u4f24\u5bb3\u91d1\u6bdb\u72e1\u7684\u6027\u547d\uff0c\u4f46\u53c8\u8981\u6446\u8131\u5b83\u4eec\uff0c\u56e0\u6b64\u53ea\u597d\u5192\u9669\u53d7\u5b83\u4eec\u7684\u4e00 \u6293\uff0c\u7b97\u51c6\u5b83\u4eec\u6251\u6765\u7684\u90e8\u4f4d\uff0c\u8fd0\u8d77\u62a4\u4f53\u795e\u529f\uff0c\u91d1\u6bdb\u9a8f\u7684\u6307\u722a\u8d5b\u5982\u5229\u5203\uff0c\u4f46\u4e5f\u53ea\u4e0d\u8fc7\u6293\u7834\u4ed6 \u4e00\u5c42\u6cb9\u76ae\uff0c\u6c5f\u6d77\u5929\u8d8b\u52bf\u65bd\u5c55\u64d2\u62ff\u624b\u6cd5\uff0c\u53cd\u800c\u6293\u7740\u4e86\u91d1\u6bdb\u72e1\u7684\u540e\u9888\uff0c\u5236\u4f4f\u5b83\u4eec\u7684\u8981\u5bb3\u3002\u91d1 \u6bdb\u72e1\u867d\u7136\u51f6\u731b\u65e0\u6bd4\uff0c\u88ab\u4ed6\u4e00\u63d0\u8d77\u6765\uff0c\u4e5f\u662f\u4e0d\u80fd\u4f5c\u6076\u7684\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u670d\u98df\u4e86\u5929\u5fc3\u77f3\u4e4b\u540e\uff0c\u6c14\u529b\u4e4b\u5927\uff0c\u5929\u4e0b\u65e0\u5339\uff0c\u7aef\u7684\u6709\u9738\u738b\u625b\u9f0e\u4e4b\u80fd\uff0c\u8fd9\u4e24\u53ea\u91d1 \u6bdb\u72e1\u603b\u5171\u6709\u4e09\u767e\u6765\u65a4\uff0c\u4ed6\u63d0\u5728\u624b\u4e2d\uff0c\u4e5f\u4e0d\u8fc7\u662f\u821e\u5f04\u732b\u513f\u4e00\u822c\uff0c\u7528\u529b\u4e00\u629b\uff0c\u7adf\u628a\u90a3\u4e24\u53ea\u91d1 \u6bdb\u72e1\u629b\u5230\u5c71\u4e0a\u3002\u4ed6\u8fd9\u4e00\u629b\uff0c\u7528\u7684\u53ea\u662f\u8d85\u4e4e\u5e38\u4eba\u7684\u6c14\u529b\uff0c\u800c\u5e76\u975e\u7528\u5185\u5bb6\u7684\u91cd\u624b\u6cd5\uff0c\u6599\u60f3\u90a3 \u4e24\u53ea\u91d1\u6bdb\u72e1\u94dc\u76ae\u94c1\u9aa8\uff0c\u78b0\u7740\u77f3\u5c71\uff0c\u4e5f\u6700\u591a\u4e0d\u8fc7\u662f\u6454\u6655\u8fc7\u53bb\uff0c\u7edd\u4e0d\u4f1a\u6b7b\u4ea1\u3002 \u3000\u3000\u5389\u590d\u751f\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6c5f\u6d77\u5929\u4e00\u62db\u201c\u4e8c\u9f99\u62a2\u73e0\u201d\uff0c\u4f38\u51fa\u4e2d\u98df\u4e8c\u6307\uff0c\u6316 \u4ed6\u7684\u773c\u73e0\uff0c\u5389\u590d\u751f\u60ca\u60f6\u672a\u5b9a\uff0c\u672c\u80fd\u7684\u7528\u7389\u5c3a\u5f80\u4e0a\u4e00\u64a9\uff0c\u6c5f\u6d77\u5929\u559d\u4e00\u58f0\u201c\u7740\u201d\uff01\u53cc\u6307\u4e00\u6233\uff0c \u642d\u7740\u4e86\u4ed6\u7684\u7389\u5c3a\uff0c\u5de6\u638c\u4e00\u7a7f\uff0c\u5df2\u5728\u4ed6\u7684\u80a9\u5934\u62cd\u4e86\u4e00\u4e0b\uff0c\u5389\u590d\u751f\u767b\u65f6\u5168\u8eab\u9ebb\u8f6f\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002 \u539f\u6765\u6c5f\u6d77\u5929\u6316\u4ed6\u773c\u775b\u7684\u90a3\u4e00\u62db\u4e0d\u8fc7\u662f\u4e2a\u865a\u62db\uff0c\u8fd9\u4e00\u62cd\u624d\u662f\u4e54\u5317\u6e9f\u79d8\u7c4d\u4e2d\u7684\u5236\u80dc\u7edd\u62db\u3002\u5389 \u590d\u751f\u5018\u82e5\u4e0d\u662f\u60ca\u60f6\u5931\u63aa\uff0c\u4e5f\u8bb8\u8fd8\u4e0d\u81f3\u4e8e\u7ed9\u4ed6\u4e00\u62cd\u5373\u4e2d\uff0c\u5982\u4eca\u7ed9\u4ed6\u62cd\u4e2d\u4e86\u80a9\u4e95\u7a74\uff0c\u90a3\u5c31\u6700 \u5c11\u8981\u5728\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u65b9\u80fd\u8d70\u52a8\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u63a5\u8fde\u4e09\u573a\u6fc0\u6218\uff0c\u51fb\u4f24\u4e86\u6587\u5ef7\u58c1\uff0c\u5413\u9000\u4e86\u6b27\u9633\u4ef2\u548c\uff0c\u6454\u6655\u4e86\u4e24\u53ea\u91d1\u6bdb\u72e1\u6700\u540e\u53c8 \u62cd\u4e2d\u4e86\u5389\u590d\u751f\u7684\u7a74\u9053\uff0c\u4ee4\u4ed6\u4e0d\u80fd\u52a8\u5f39\u3002\u81f3\u6b64\uff0c\u6c5f\u6d77\u5929\u5df2\u65e0\u540e\u987e\u4e4b\u5fe7\uff0c\u4f46\u5929\u9b54\u6559\u4e3b\u4ea6\u5df2\u8d70 \u5f97\u65e0\u8e2a\u65e0\u5f71\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5927\u53eb\u9053\uff1a\u201c\u6b27\u9633\u59d1\u6068\uff0c\u4f60\u5728\u54ea\u513f\uff1f\u201d\u8fdc\u8fdc\u7684\u542c\u5f97\u6b27\u9633\u5a49\u5c16\u53eb\u4e86\u4e00\u58f0\uff0c\u968f\u5373\u4fbf \u4f3c\u7ed9\u4eba\u627c\u7740\u4e86\u5589\u5499\u4e00\u822c\uff0c\u58f0\u97f3\u560e\u7136\u800c\u6b62\uff0c\u60f3\u662f\u88ab\u5929\u9b54\u6559\u4e3b\u70b9\u4e86\u7a74\u9053\u3002\u4f46\u53ea\u8fd9\u4e00\u58f0\uff0c\u6c5f\u6d77 \u5929\u5df2\u80fd\u8fa8\u522b\u5979\u7684\u65b9\u5411\uff0c\u5f53\u4e0b\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u7acb\u5373\u5411\u58f0\u97f3\u7684\u6765\u5904\u8ffd\u8d76\uff01 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u62b1\u7740\u4e00\u4e2a\u6b27\u9633\u5a49\uff0c\u5f53\u7136\u8dd1\u4e0d\u8fc7\u6c5f\u6d77\u5929\uff0c\u8ffd\u4e86\u4e00\u4f1a\uff0c\u4e24\u4eba\u7684\u8ddd\u79bb\u53c8\u6e10\u6e10\u62c9\u8fd1\u3002 \u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u5e78\u4e8f\u5979\u6ca1\u6709\u8eb2\u8d77\u6765\uff0c\u53ea\u662f\u5728\u8fd9\u56ed\u5b50\u91cc\u4e71\u8dd1\uff0c\u5018\u82e5\u5979\u968f\u4fbf\u5728\u4e2a\u5047\u5c71\u6d1e\u91cc\u85cf \u8d77\u6765\uff0c\u6211\u5012\u4e0d\u6613\u5bfb\u627e\u4e86\u3002\u201d\u6b8a\u4e0d\u77e5\u5929\u9b54\u6559\u4e3b\u72e1\u8bc8\u4e4b\u6781\uff0c\u6c5f\u6d77\u5929\u60f3\u5f97\u5230\u7684\u5979\u5c82\u6709\u60f3\u4e0d\u5230\u4e4b \u7406\uff1f\u5979\u8fd9\u6b63\u662f\u8bf1\u654c\u4e4b\u8ba1\uff01\u6b63\u662f\uff1a \u3000\u3000\u7eb5\u6709\u901a\u5929\u5f7b\u5730\u80fd\uff0c\u96be\u5f53\u8986\u96e8\u7ffb\u4e91\u624b\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e03\u56de\u3000\u795e\u9e70\u5c55\u7fc5\u60ca\u5f3a\u654c\u3000\u7389\u5973\u65bd\u9488\u8868\u7d20\u5fc3 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e09\u5341\u4e03\u56de\u3000\u795e\u9e70\u5c55\u7fc5\u60ca\u5f3a\u654c\u3000\u7389\u5973\u65bd\u9488\u8868\u7d20\u5fc3 \u3000\u3000\u524d\u9762\u662f\u4e00\u7247\u7e41\u82b1\u76db\u5f00\u7684\u6811\u6797\uff0c\u90a3\u4e9b\u5947\u82b1\u5f02\u8349\uff0c\u5927\u534a\u662f\u6c5f\u6d77\u5929\u4ece\u672a\u89c1\u8fc7\u7684\uff0c\u9999\u6c14\u99a5\u90c1\uff0c \u707f\u5982\u4e91\u971e\uff0c\u4f46\u6c5f\u6d77\u5929\u6025\u4e8e\u6551\u4eba\uff0c\u5374\u4e5f\u65e0\u5fc3\u6b23\u8d4f\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u56de\u5934\u7b11\u9053\uff1a\u201c\u6d77\u5929\uff0c\u6211\u529d\u4f60\u8fd8\u662f\u4e0d\u8981\u8fd1\u6765\u7684\u597d\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u628a\u6b27\u9633 \u59d1\u5a18\u653e\u4e0b\uff0c\u6211\u4e0d\u4e3a\u96be\u4f60\u3002\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u597d\uff0c\u62ff\u53bb\u5427\uff01\u201d\u5ffd\u5730\u628a\u624b\u4e00\u626c\uff0c\u4e00\u80a1\u6bd2\u70df\u6fc0 \u5c04\u800c\u51fa\uff0c\u6c5f\u6d77\u5929\u65e9\u6709\u63d0\u9632\uff0c\u7acb\u5373\u95ed\u4e86\u547c\u5438\uff0c\u4e00\u8bb0\u5288\u7a7a\u638c\u6253\u51fa\uff0c\u4e2a\u6bd2\u70df\u626b\u8361\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4f60\u522b\u4ee5\u4e3a\u53ef\u4ee5\u5bf9\u4ed8\u6bd2\u70df\uff0c\u4fbf\u80dc\u5f97\u4e86\u6211\u3002\u6211\u8fd8\u6709\u8bb8\u591a\u5389\u5bb3\u7684\u6cd5\u5b9d\u672a\u66fe \u4f7f\u7528\uff0c\u6211\u518d\u529d\u4f60\u4e00\u6b21\uff0c\u8fd8\u662f\u4e0d\u8981\u8ffd\u6765\u7684\u597d\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6012\u9053\uff1a\u201c\u4f60\u6709\u4f55\u4f0e\u4fe9\uff0c\u5c3d\u6570\u4f7f\u51fa\u6765\u5427\uff01\u201d\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u5f53\u771f\u4e0d\u4f2f\uff0c\u90a3\u5c31 \u8ffd\u6765\u5427\uff01\u201d\u7b11\u58f0\u4e2d\u65e9\u5df2\u62b1\u7740\u6b27\u9633\u5a49\u94bb\u5165\u4e86\u6811\u6797\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6280\u9ad8\u80c6\u5927\uff0c\u7d27\u8feb\u4e0d\u820d\uff0c\u201d\u5ffd\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u8165\u81ed\u6251\u9f3b\uff0c\u4e00\u6761\u4e94\u8272\u6591\u70c2\u7684\u957f\u86c7\u7a81 \u7136\u5411\u4ed6\u7a9c\u6765\u3002\u6c5f\u6d77\u5929\u4e00\u624b\u6293\u53bb\uff0c\u5374\u539f\u6765\u4e0d\u662f\u771f\u7684\u86c7\uff0c\u800c\u662f\u4e00\u591a\u5f62\u4f3c\u957f\u86c7\u7684\u8272\u5f69\u5e26\uff0c\u6c5f\u6d77 \u5929\u8fd0\u52b2\u4e00\u593a\uff0c\u53ea\u542c\u5f97\u201c\u549a\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u4e2a\u5973\u4eba\u4ece\u6811\u4e0a\u8dcc\u4e0b\uff0c\u628a\u773c\u4e00\u671b\uff0c\u4f9d\u7a00\u8ba4\u5f97\u8fd9\u5973\u4eba \u5c31\u662f\u4ece\u524d\u5047\u5192\u8fc7\u8c37\u4e2d\u83b2\u6bcd\u4eb2\u7684\u90a3\u4e2a\u201c\u7f2a\u592b\u4eba\u201d\u3002 \u3000\u3000\u8fd9\u6761\u4e94\u8272\u6591\u70c2\u7684\u5f69\u5e26\u8574\u6709\u5947\u6bd2\uff0c\u5e78\u4e8f\u6c5f\u6d77\u5929\u65e9\u6709\u51c6\u5907\uff0c\u771f\u6c14\u51dd\u805a\u638c\u5fc3\uff0c\u4efb\u4f55\u5267\u6bd2\u90fd \u4fb5\u4e0d\u8fdb\u4ed6\u7684\u808c\u80a4\uff0c\u4ed6\u6454\u5f00\u4e86\u6bd2\u5e26\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u57cb\u4f0f\u6709\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u90fd\u4e0b\u6765\u5427\uff01\u201d\u6c5f\u6d77\u5929\u4e00\u638c\u62a4\u8eab\uff0c\u4f38\u624b\u5c31\u6293\u5929\u9b54\u6559\u4e3b\uff0c\u51b7 \u7b11\u9053\uff1a\u201c\u4f60\u5f04\u4ec0\u4e48\u7384\u865a\uff1f\u4e0d\u628a\u6b27\u9633\u59d1\u5a18\u653e\u4e0b\uff0c\u4f60\u7eb5\u7136\u57cb\u4f0f\u4e86\u5343\u519b\u4e07\u9a6c\uff0c\u6211\u4e5f\u8981\u5c06\u4f60\u6293\u4f4f\uff01\u201d \u5929\u9b54\u6559\u4e3b\u90a3\u4e00\u58f0\u53eb\u540e\uff0c\u6811\u6797\u91cc\u4ecd\u662f\u9759\u6084\u6084\u7684\u4e0d\u89c1\u4efb\u4f55\u4eba\u5f71\uff0c\u6c5f\u6d77\u5929\u66f4\u4ee5\u4e3a\u5979\u662f\u865a\u58f0\u606b\u5413\u3002 \u3000\u3000\u773c\u770b\u5c31\u8981\u6293\u7740\u5929\u9e70\u6559\u4e3b\uff0c\u5929\u9b54\u6559\u4e3b\u5ffd\u5730\u53c8\u53eb\u9053\uff1a\u201c\u6c5f\u6d77\u5929\u4f60\u5c0f\u5fc3\u4e86\uff01\u201d\u5979\u8eab\u5f62\u63a0\u8fc7\uff0c \u8863\u8896\u6025\u6325\uff0c\u53ea\u89c1\u82b1\u6735\u7eb7\u7eb7\u843d\u4e0b\uff0c\u82b1\u7c89\u6cbe\u4e86\u6c5f\u6bcf\u5929\u6ee1\u8eab\uff0c\u6c5f\u6d77\u5929\u62a4\u7740\u773c\u775b\uff0c\u53ea\u89c9\u624b\u8db3\u5934\u9888\uff0c \u7a81\u7136\u95f4\u90fd\u9ebb\u75d2\u75d2\u7684\u597d\u4e0d\u96be\u53d7\uff0c\u540c\u65f6\u4e00\u80a1\u6d53\u70c8\u7684\u9999\u6c14\u4e5f\u94bb\u8fdb\u4e86\u4ed6\u7684\u9f3b\u5b54\u3002 \u3000\u3000\u539f\u6765\u5728\u6c5f\u6d77\u5929\u5468\u56f4\u7684\u51e0\u68f5\u82b1\u6811\uff0c\u90fd\u662f\u5929\u9b54\u6559\u4e3b\u6240\u683d\u79cd\u7684\u5947\u79cd\u6bd2\u82b1\uff0c\u4e0d\u4f46\u82b1\u9999\u53ef\u4ee5\u5c06 \u4eba\u8ff7\u5012\uff0c\u82b1\u7c89\u6cbe\u4e0a\u76ae\u80a4\uff0c\u76ae\u80a4\u4e5f\u4f1a\u6e83\u70c2\uff0c\u6c5f\u6d77\u5929\u6709\u62a4\u4f53\u795e\u529f\uff0c\u4f46\u7a76\u7adf\u4e0d\u80fd\u5c06\u771f\u6c14\u904d\u5e03\u5168 \u8eab\uff0c\u4e00\u822c\u5f3a\u5f31\uff0c\u8eab\u4e0a\u67d0\u4e9b\u6297\u62b5\u529b\u4e0d\u8db3\u4e4b\u5904\uff0c\u6cbe\u4e0a\u4e86\u82b1\u7c89\uff0c\u6bd2\u6c14\u4fbf\u8513\u5ef6\u5f00\u6765\uff0c\u5e78\u4e8f\u6c5f\u6d77\u5929 \u7684\u529f\u529b\u5df2\u5927\u80dc\u4ece\u524d\uff0c\u867d\u7136\u4e2d\u4e86\u70b9\u6bd2\uff0c\u5374\u4e5f\u8fd8\u4e0d\u81f3\u4e8e\u6655\u5012\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u95ed\u4e86\u547c\u5438\uff0c\u7eb5\u8eab\u4e00\u8dc3\uff0c\u518d\u5ea6\u6293\u53bb\uff0c\u4f46\u56e0\u4ed6\u53d7\u4e86\u6bd2\u82b1\u7a81\u88ad\u4e4b\u963b\uff0c\u4e0e\u5929\u9b54\u6559\u4e3b\u7684 \u8ddd\u79bb\u53c8\u62c9\u957f\u4e86\u4e00\u6bb5\uff0c\u8fd9\u4e00\u6293\u5374\u6ca1\u6709\u6293\u7532\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8eab\u5f62\u4e00\u843d\uff0c\u6b63\u8981\u518d\u8dc3\u8d77\u6293\u5979\uff0c\u811a\u5c16\u70b9\u7740\u7684\u90a3\u5757\u77f3\u5934\u5ffd\u5730\u4e00\u6c89\uff0c\u5730\u4e0a\u88c2\u5f00\u4e86\u4e00 \u4e2a\u5927\u6d1e\uff0c\u6c5f\u6d77\u5929\u811a\u5c16\u4e00\u70b9\uff0c\u501f\u7740\u90a3\u4e00\u70b9\u7684\u53cd\u5f39\u4e4b\u529b\uff0c\u8eab\u5f62\u5e73\u5730\u62d4\u8d77\u3002\u5929\u9b54\u6559\u4e3b\u6325\u8896\u4e00\u62cd\uff0c \u559d\u9053\uff1a\u201c\u4e0b\u53bb\u5427\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e00\u628a\u626f\u4f4f\u5929\u9b54\u6559\u4e3b\u7684\u8863\u8896\uff0c\u5929\u9b54\u6559\u4e3b\u624b\u81c2\u4e00\u7f29\uff0c\u53ea\u542c\u5f97\u58f0\u5982\u88c2\u5e1b\uff0c\u8863\u8896\u65ad\u4e86 \u4e00\u5e45\uff0c\u6c5f\u6d77\u5929\u5931\u4e86\u51ed\u501f\uff0c\u53c8\u843d\u4e0b\u6765\uff0c\u5929\u9b54\u6559\u4e3b\u90a3\u4e00\u62c2\u4e4b\u529b\uff0c\u52a0\u4e0a\u6c5f\u6d77\u5929\u81ea\u5df1\u90a3\u4e00\u6293\u4e4b\u529b\uff0c \u4e0b\u5760\u4e4b\u52bf\u6781\u901f\uff0c\u5730\u4e0b\u5df2\u88c2\u5f00\u4e00\u4e2a\u5927\u6d1e\uff0c\u6c5f\u6d77\u5929\u8dcc\u5165\u6d1e\u4e2d\uff0c\u518d\u8981\u7528\u53cc\u8db3\u4ea4\u8e0f\u4e4b\u6cd5\u8dc3\u8d77\uff0c\u5df2 \u662f\u529b\u4e0d\u4ece\u5fc3\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5438\u4e86\u53e3\u6c14\uff0c\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7b4b\u6597\u7ffb\u8f6c\u8fc7\u6765\uff0c\u5c06\u6025\u901f\u4e0b\u5760\u4e4b\u52bf\u6d88\u51cf\u4e86\u4e00\u534a\uff0c\u811a\u5c16\u5f80 \u524d\u8f7b\u8f7b\u4e00\u70b9\uff0c\u6491\u7740\u77f3\u58c1\uff0c\u518d\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u5e73\u5e73\u5b98\u7a33\u5730\u843d\u4e0b\u6765\uff0c\u5ffd\u542c\u5f97\u201c\u5495\u549a\u201d\u4e00\u58f0\uff0c \u4f3c\u4e4e\u5c31\u5728\u4ed6\u7684\u8eab\u65c1\uff0c\u4e5f\u6b63\u6709\u4eba\u8dcc\u4e0b\uff0c\u800c\u4e14\u8dcc\u5f97\u6bd4\u4ed6\u66f4\u91cd\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e00\u624b\u6293\u53bb\uff0c\u5374\u6293\u7740\u4e00\u628a\u5229\u9488\uff0c\u523a\u5f97\u4ed6\u4e94\u6307\u9c9c\u8840\u6dcb\u6f13\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u8981\u805a\u62e2\u76ee\u5149\uff0c\u9ed1\u6697\u4e2d\u90a3\u4eba\u5df2\u662f\u9003\u4e4b\u592d\u592d\uff0c\u53ea\u542c\u5f97\u6eda\u52a8\u7684\u58f0\u97f3\u3002 \u3000\u3000\u6d1e\u7a74\u91cc\u9ed1\u9edd\u9edd\u7684\u3001\u4ec0\u4e48\u90fd\u770b\u4e0d\u89c1\uff0c\u4f46\u4ece\u521a\u624d\u6240\u542c\u5230\u7684\u547c\u5438\u58f0\u606f\u3002\u5374\u53ef\u4ee5\u77e5\u662f\u4e24\u4e2a\u4eba \u800c\u4e0d\u662f\u4e00\u4e2a\u4eba\uff0c\u90a3\u5f53\u7136\u662f\u5929\u9b54\u6559\u4e3b\u548c\u6b27\u9633\u5a49\u4e86\u3002\u539f\u6765\u6c5f\u6d77\u5929\u90a3\u4e00\u62c9\u7528\u4e0a\u4e86\u7c98\u9ecf\u4e4b\u52b2\uff0c\u5929 \u9b54\u6559\u4e3b\u7684\u8863\u8896\u867d\u7136\u65ad\u4e86\uff0c\u6c5f\u6d77\u5929\u90a3\u80a1\u5185\u529b\u8fd8\u662f\u4f20\u5230\u4e86\u5979\u7684\u8eab\u4e0a\uff0c\u628a\u5979\u548c\u6b27\u9633\u5a49\u4e00\u5e76\u62c9\u4e86 \u4e0b\u6765\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5b9a\u4e86\u5b9a\u795e\uff0c\u805a\u62e2\u76ee\u5149\uff0c\u6e10\u6e10\u5728\u9ed1\u6697\u4e2d\u5df2\u53ef\u770b\u51fa\u6a21\u7cca\u7684\u5f71\u5b50\uff0c\u6c5f\u6d77\u5929\u6478\u7d22\u7740\u5411 \u524d\u8d70\u53bb\u3002\u90a3\u5f71\u5b50\u4e5f\u5728\u79fb\u52a8\uff0c\u6c5f\u6d77\u5929\u9053\uff1a \u3000\u3000\u201c\u6b27\u9633\u59d1\u5a18\u5462\uff1f\u201d\u90a3\u5f71\u5b50\u7b11\u9053\uff1a\u201c\u6b27\u9633\u59d1\u5a18\u6ca1\u6b7b\uff0c\u4f60\u653e\u5fc3\u3002\u4f46\u4f60\u8981\u89c1\u5979\uff0c\u4e5f\u6015\u5f88\u96be \u4e86\uff0c\u73b0\u5728\u4f60\u77e5\u9053\u6211\u7684\u5389\u5bb3\u4e86\u5427\uff1f\u201d\u8fd9\u51e0\u53e5\u8bdd\u4e00\u8bf4\uff0c\u90a3\u5f71\u5b50\u4fbf\u500f\u7136\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u542c\u4e86\u5929\u9b54\u6559\u4e3b\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u77e5\u9053\u5979\u4e5f\u53d7\u4e86\u4f24\uff0c\u66f4\u662f\u5fe7\u5fc3\uff0c\u5fc3\u60f3\uff1a\u201c\u5979\u5df2\u7136\u53d7 \u4e86\u4f24\uff0c\u6b27\u9633\u5a49\u53ea\u6015\u4f24\u5f97\u66f4\u91cd\u3002\u201d\u770b\u6765\u8fd9\u77f3\u6d1e\u91cc\u85cf\u6709\u673a\u5173\uff0c\u5929\u9b54\u6559\u4e3b\u548c\u6b27\u9633\u5a49\u4e0d\u77e5\u85cf\u5728\u54ea \u4e2a\u6697\u5ba4\u3002\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u8eab\u4e0a\u6709\u5c0f\u8fd8\u4e39\uff0c\u4f60\u628a\u6b27\u9633\u59d1\u5a18\u6276\u51fa\u6765\u3001\u6211\u4e0d\u8ba1\u8f83\u524d\u4ec7\uff0c\u7ed9\u4f60\u6cbb \u4f24\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4e86\u3002\u5c0f\u8fd8\u4e39\u4f60\u7559\u7740\u81ea\u5df1\u7528\u5427\u3002\u4f60\u8dcc\u5f97\u4e0d\u91cd\uff0c\u4e2d\u7684\u6bd2\u53ef\u4e0d\u8f7b\u554a\uff01 \u53ea\u6015\u5c0f\u8fd8\u4e39\u4e5f\u672a\u5fc5\u6551\u5f97\u4e86\u4f60\u3002\u6211\u65e9\u5df2\u529d\u4f60\u4e0d\u8981\u8ffd\u7684\uff0c\u4f60\u53ef\u602a\u4e0d\u5f97\u6211\uff01\u201d\u542c\u58f0\u97f3\u8ddd\u79bb\u4e0d\u8fdc\uff0c \u4eba\u5f71\u5374\u770b\u4e0d\u89c1\u3002\u6c5f\u6d77\u5929\u7684\u624b\u78b0\u7740\u77f3\u58c1\uff0c\u5ffd\u89c9\u624b\u6307\u50f5\u786c\uff0c\u8f6c\u52a8\u4e0d\u7075\u3002\u8eab\u4e0a\u7684\u9ebb\u75d2\u4e5f\u8d8a\u6765\u8d8a \u5389\u5bb3\u4e86\u3002\u6c5f\u6d77\u5929\u6697\u6697\u5403\u60ca\uff1a\u201c\u5929\u9b54\u6559\u4e3b\u679c\u7136\u4e0d\u662f\u865a\u58f0\u606b\u5413\uff0c\u6211\u4e2d\u7684\u6bd2\u786e\u5b9e\u4e0d\u8f7b\u3002\u201d\u53ea\u597d \u76d8\u819d\u5750\u4e0b\uff0c\u9ed8\u8fd0\u7384\u529f\uff0c\u5c06\u771f\u6c14\u8fd0\u5230\u9ebb\u75d2\u4e4b\u5904\uff0c\u9a71\u6bd2\u7597\u4f24\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u80cc\u7740\u6bcd\u4eb2\u3002\u548c\u4e24\u4f4d\u54e5\u54e5\u5395\u5230\u5c0f\u84ec\u83b1\u5c71\u7684\u65f6\u5019\uff0c\u6c5f\u6d77\u5929\u65e9\u5df2\u843d\u8fdb\u9677\u997c\uff0c\u6587\u5ef7\u58c1 \u548c\u6b27\u9633\u4ef2\u548c\u7b49\u4eba\u4e5f\u65e9\u5df2\u8eb2\u8d77\u6765\u4e86\u3002\u8c37\u4e2d\u83b2\u5144\u59b9\u767b\u4e0a\u5c71\u5934\uff0c\u56db\u65b9\u4e86\u671b\uff0c\u56ed\u5b50\u91cc\u9759\u6084\u6084\u7684\u3002 \u54ea\u91cc\u6709\u6c5f\u6d77\u5929\u7684\u5f71\u5b50\uff1f\u8c37\u4e2d\u83b2\u5927\u4e3a\u7740\u6025\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u6d77\u5929\uff0c\u6d77\u5929\uff0c\u4f60\u5728\u54ea\u513f\uff1f\u201d\u5979\u670d \u98df\u4e86\u5929\u5fc3\u77f3\uff0c\u529f\u529b\u4e24\u500d\u4e8e\u524d\uff0c\u4e2d\u6c14\u5145\u6c9b\uff0c\u5c06\u58f0\u97f3\u8fdc\u8fdc\u9001\u51fa\uff0c\u4f30\u91cf\u5728\u6570\u91cc\u4e4b\u5185\uff0c\u90fd\u53ef\u4ee5\u542c \u5230\u5979\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u4f60\u8981\u628a\u654c\u4eba\u5f15\u6765\u5417\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6015\u4ec0\u4e48\uff0c\u627e\u4e0d\u89c1\u6d77\u54e5\uff0c\u54b1\u4eec \u7d22\u6027\u518d\u6740\u8fdb\u5bab\u53bb\uff01\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6c5f\u5e08\u5144\u4f1a\u5929\u9041\u4f20\u97f3\uff1a\u58f0\u97f3\u6bd4\u4f60\u9001\u5f97\u66f4\u8fdc\uff0c\u8981\u662f\u4ed6\u8fd8 \u5728\u5bab\u4e2d\uff0c\u65e9\u5c31\u8be5\u6709\u56de\u58f0\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u4ed6\u5df2\u7ecf\u8d70\u4e86\uff1f\u53ef\u662f\u4ed6\u548c\u54b1\u76bf\u662f\u7ea6\u597d\u4e86\u5728\u6b64\u5730\u76f8\u805a\u7684\u5440\uff01\u201d\u5510\u52aa \u73e0\u7a46\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u4e34\u65f6\u6709\u4e8b\uff0c\u6025\u9700\u6599\u7406\uff0c\u56e0\u6b64\u79bb\u5f00\uff0c\u90a3\u4e5f\u96be\u6599\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4ed6\u4e00\u5fc3 \u4e00\u610f\u8981\u52a9\u54b1\u4eec\u62a5\u4ec7\uff0c\u8fd8\u80fd\u6709\u4ec0\u4e48\u4e8b\u60c5\u6bd4\u8fd9\u4e2a\u66f4\u7d27\u8981\u7684\uff1f\u55ef\uff0c\u6211\u5012\u662f\u62c5\u5fc3\u4ed6\u51fa\u4e86\u610f\u5916\u4e86\u3002 \u54e5\u54e5\uff0c\u54b1\u4eec\u8981\u4e0d\u8981\u5206\u5934\u518d\u641c\u7d22\u4ed6\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u5df2\u662f\u4e1c\u65b9\u5927\u81ea\uff0c\u671d\u9633\u521d\u51fa\u7684\u65f6\u5206\uff0c\u4ece\u5c71\u9876\u671b\u4e0b\u53bb\uff0c\u4f46\u89c1\u4e00\u7247\u91d1\u78a7\uff0c\u90a3\u662f\u5bab\u6bbf\u7684 \u7409\u7483\u74e6\u9762\u53d1\u51fa\u7684\u8272\u5149\uff0c\u5728\u597d\u51e0\u5ea7\u5bab\u6bbf\u7684\u5899\u5934\uff0c\u9690\u9690\u7ea6\u7ea6\u53ef\u4ee5\u770b\u89c1\u6709\u6b66\u571f\u63a2\u51fa\u5934\u6765\u5f20\u671b\uff0c \u60f3\u662f\u88ab\u5907\u4e2d\u83b2\u7684\u58f0\u97f3\u60ca\u52a8\uff0c\u60ca\u5f13\u4e4b\u9e1f\uff0c\u5374\u4e0d\u6562\u51fa\u6765\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u76b1\u7709\u8bf4\u9053\uff1a\u201c\u8981\u641c\u904d\u8fd9\u4e9b\u5bab\u6bbf\uff0c\u5c11\u8bf4\u4e5f\u5f97\u534a\u5929\u5de5\u592b\u3002 \u3000\u3000\u6c5f\u5e08\u5144\u672a\u5fc5\u8fd8\u5728\u5bab\u4e2d\uff0c\u5728\u5bab\u4e2d\u4e5f\u672a\u5fc5\u4fbf\u627e\u5f97\u7740\u4ed6\u3002\u4f55\u51b5\u4ed6\u6b66\u529f\u6bd4\u54b1\u4eec\u90fd\u5f3a\uff0c\u4e5f\u672a\u5fc5 \u4fbf\u9047\u4e0a\u610f\u5916\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u4e00\u8fde\u51e0\u4e2a\u672a\u5fc5\uff0c\u96be\u9053\u54b1\u4eec\u5c31\u4e0d\u7406\u4ed6\u4e86\u4e48\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u8c01\u8bf4\u4e0d\u7406\u4ed6\uff0c\u4f46\u4f9d\u4f60\u4e4b\u89c1\uff0c\u5165\u5bab\u641c\u7d22\uff0c\u5374\u4e0d\u662f\u4e2a\u597d\u529e\u6cd5\u3002\u4f55\u51b5\u2026\u2026\u201d \u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u671b\u4e86\u8c37\u4e2d\u83b2\u4e00\u773c\uff0c\u8c37\u4e2d\u83b2\u731b\u7136\u60ca\u9192\uff0c\u60f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u80cc\u7740\u6bcd\u4eb2\uff0c\u884c\u52a8\u4e0d \u4fbf\uff0c\u8981\u662f\u8bef\u4f24\u4e86\u6bcd\u4eb2\uff0c\u90a3\u5c31\u66f4\u7cdf\u4e86\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u5ffd\u9053\uff1a\u201c\u4e8c\u5f1f\u4e4b\u8a00\u6709\u7406\uff0c\u627e\u4e0d\u7740\u6c5f\u5c0f\u4fa0\u90a3\u5c31\u8fde\u91d1\u9e70\u5bab\u4e4b\u4f1a\u4e5f\u8981\u9519\u8fc7\u4e86\u3002\u4e0d\u5982 \u4f60\u4eec\u5148\u51fa\u53bb\u3002\u628a\u6bcd\u4eb2\u5b89\u987f\u4e86\u7acb\u5373\u8d76\u53bb\u8d74\u4f1a\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u6211\u7559 \u4e0b\u6765\uff0c\u6211\u6bd4\u4f60\u4eec\u66f4\u719f\u6089\u5bab\u91cc\u7684\u60c5\u5f62\u3002\u53ef\u4ee5\u8bbe\u6cd5\u6253\u542c\u3002\u597d\u5728\u6211\u7684\u6b66\u529f\u5df2\u7ecf\u6062\u590d\uff0c\u597d\u738b\u8981\u6740 \u6211\u4e4b\u4e8b\uff0c\u5bab\u4e2d\u77e5\u9053\u7684\u4eba\u4e5f\u4e0d\u591a\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u60f3\u60f3\uff0c\u4e5f\u53ea\u597d\u5982\u6b64\uff01\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u5927\u54e5\uff0c\u4f60\u5c0f\u5fc3\u4e86\uff01\u201d\u8fd9\u662f\u5979\u7b2c\u4e00\u6b21 \u9762\u5bf9\u7740\u53f6\u51b2\u9704\u53eb\u4ed6\u505a\u201c\u5927\u54e5\u201d\uff0c\u53f6\u51b2\u9704\u4e0d\u89c9\u6cea\u73e0\u513f\u5728\u773c\u7736\u91cc\u6253\u8f6c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4fdd\u62a4\u6bcd \u4eb2\uff0c\u4e5f\u8981\u5c0f\u5fc3\u4e86\uff01\u201d\u4ed6\u4e0d\u613f\u610f\u8ba9\u5f1f\u59b9\u770b\u89c1\u4ed6\u7684\u773c\u6cea\uff0c\u5934\u4e5f\u4e0d\u56de\u7684\u4fbf\u8dd1\u4e86\u3002 \u3000\u3000\u518d\u8bf4\u6c5f\u6d77\u5929\u5728\u9ed1\u6697\u4e2d\u9ed8\u8fd0\u7384\u529f\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u771f\u6c14\u62b5\u8fbe\u56db\u80a2\uff0c\u9ebb\u75d2\u4e4b\u611f\u6e10\u6e10\u51cf\u8f7b\uff0c\u624b \u6307\u4e5f\u6e10\u6e10\u6709\u4e86\u611f\u89c9\u3002\u2026\u2026 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u548c\u6b27\u9633\u5a49\u4f3c\u4e4e\u5c1a\u672a\u79bb\u5f00\uff0c\u5bc2\u9759\u4e2d\u6c5f\u6d77\u5929\u53ef\u4ee5\u9690\u7ea6\u542c\u89c9\u5979\u4eec\u7684\u547c\u606f\uff0c\u4ece\u5979\u4eec \u91cd\u6d4a\u7684\u547c\u606f\u542c\u6765\uff0c\u663e\u7136\u4e5f\u662f\u4f24\u5f97\u4e0d\u8f7b\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53ea\u8981\u6211\u80fd\u8d76\u5728\u5929\u9b54\u6559\u4e3b\u4e4b\u524d\u6062\u590d\u6b66\u529f\uff0c\u6211\u5c31\u53ef\u4ee5\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\uff0c \u5c06\u6b27\u9633\u59d1\u5a18\u6551\u51fa\u9669\u5883\uff1b\u4f46\u5018\u82e5\u5979\u6bd4\u6211\u5148\u884c\u6062\u590d\uff0c\u53ea\u6015\u6211\u5c31\u8981\u4e27\u547d\u5728\u5979\u624b\u4e2d\u4e86\u3002\u201d \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u8c37\u4e2d\u83b2\u7684\u58f0\u97f3\u4f20\u4e86\u8fdb\u6765\uff1a\u201c\u6d77\u5929\uff0c\u6d77\u5929\uff0c\u4f60\u5728\u54ea\u513f\uff1f\u201d\u6c5f\u6d77\u5929\u5927\u559c\uff0c\u8fde \u5fd9\u5e94\u9053\uff1a\u201c\u6211\u5728\u8fd9\u513f\uff0c\u6211\u5728\u8fd9\u513f\uff01\u4f60\u5feb\u6765\u5427\uff01\u201d\u58f0\u97f3\u5410\u4e86\u51fa\u6765\uff0c\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c \u7b80\u76f4\u50cf\u662f\u4e2a\u75c5\u4eba\u7684\u547b\u541f\uff0c\u8f6f\u5f31\u800c\u53c8\u6c99\u54d1\uff0c\u5018\u82e5\u4e0d\u662f\u51fa\u826f\u4ed6\u53e3\u3001\u4ed6\u81ea\u5df1\u4e5f\u542c\u4e0d\u6e05\u8fd9\u58f0\u97f3\u8bf4 \u7684\u4ec0\u4e48\u3002\u539f\u6765\u4ed6\u4e0d\u65ad\u7684\u5c06\u6bd2\u6c14\u547c\u51fa\uff0c\u5589\u5499\u53d7\u6bd2\u6c14\u6240\u718f\uff0c\u58f0\u97f3\u5df2\u7136\u54d1\u4e86\uff0c\u4ed6\u8fde\u5927\u58f0\u53eb\u90fd\u53eb \u4e0d\u51fa\u6765\uff0c\u520d\u7136\u66f4\u4e0d\u80fd\u8fd0\u7528\u201c\u5929\u9041\u4f20\u97f3\u201d\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u5728\u5403\u60ca\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u5927\u58f0\u8d4b\u559d\uff0c\u4eba\u58f0\u811a\u6b65\u58f0\u7eb7\u81f3\u6c93\u6765\uff0c\u539f\u6765\u8fd9\u662f\u5bab\u4e2d\u7684\u4e00 \u6761\u79d8\u5bc6\u5730\u9053\uff0c\u5730\u9053\u7684\u53e6\u4e00\u5934\u901a\u5411\u5bab\u5916\uff0c\u5728\u90a3\u4e00\u5934\u51fa\u53e3\uff0c\u6709\u56db\u4e2a\u6b66\u58eb\u628a\u5b88\u3002\u6c5f\u6d77\u5929\u7684\u58f0\u97f3 \u867d\u7136\u4f20\u4e0d\u5230\u8c37\u4e2d\u83b2\u8033\u4e2d\uff0c\u5374\u628a\u8fd9\u56db\u4e2a\u6b66\u58eb\u60ca\u52a8\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5438\u4e86\u53e3\u6c14\uff0c\u5168\u795e\u9ed8\u8fd0\u7384\u529f\uff0c\u5c06\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\u3001\u8fc7\u4e86\u7247\u523b\uff0c\u53ea\u89c9\u706b\u5149\u8000\u773c\uff0c\u90a3 \u56db\u4e2a\u6b66\u58eb\u4e3e\u7740\u706b\u6298\uff0c\u5df2\u7ecf\u5bfb\u5230\u4e86\u6c5f\u6d77\u5929\u7684\u85cf\u8eab\u4e4b\u6240\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u6b66\u58eb\u4e5f\u662f\u5403\u60ca\u975e\u5c0f\uff0c\u4e0d\u6562\u8d38\u7136\u8d70\u8fd1\u3002\u4e00\u4e2a\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u600e\u6837\u8fdb\u6765\u7684\uff1f\u201d \u53e6\u4e00\u4e2a\u9053\uff1a\u201c\u770b\u8fd9\u5c0f\u5b50\u7684\u670d\u9970\uff0c\u51b3\u4e0d\u662f\u5bab\u91cc\u5934\u7684\u4eba\u3002\u201d\u6c5f\u6d77\u5929\u4e0b\u7406\u4e0d\u776c\uff0c\u8fde\u773c\u775b\u4e5f\u4e0d\u7741 \u5f00\u3002 \u3000\u3000\u90a3\u4e3a\u9996\u7684\u6b66\u58eb\u559d\u9053\uff1a\u201c\u5582\uff0c\u4f60\u662f\u54d1\u7684\u5417\uff1f\u201d\u53e6\u4e00\u4e2a\u9053\uff1a\u201c\u8fd9\u5c0f\u4e8e\u88c5\u54d1\uff0c\u5b9a\u7136\u662f\u523a\u5ba2\u3002\u201d \u53c8\u4e00\u4e2a\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u662f\u5426\u523a\u5ba2\uff0c\u5148\u628a\u4ed6\u62ff\u4e0b\u3002\u201d \u3000\u3000\u8fd9\u56db\u4e2a\u6b66\u58eb\u89c1\u6c5f\u6d77\u5929\u52a8\u4e5f\u4e0d\u52a8\uff0c\u653e\u5927\u4e86\u80c6\u5b50\u3002\u6253\u4e86\u4e2a\u624b\u52bf\uff0c\u540c\u65f6\u53d1\u52a8\uff0c\u4e00\u62e5\u800c\u4e0a\uff01 \u6c5f\u6d77\u5929\u4ecd\u7136\u76d8\u819d\u800c\u5750\uff0c\u5934\u4e5f\u4e0d\u62ac\uff0c\u84e6\u5730\u4e00\u624b\u6293\u51fa\uff0c\u6293\u7740\u4e86\u4e00\u4e2a\u6b66\u58eb\u7684\u624b\u8155\uff0c\u5411\u524d\u4e00\u63a8\uff0c \u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u524d\u9762\u8fd9\u4eba\u78b0\u7740\u4e86\u540e\u9762\u7684\u4f19\u4f34\uff0c\u4e24\u4eba\u8dcc\u8dcc\u649e\u649e\u7684\u5954\u51fa\u51e0\u6b65\u3002\u6c5f\u6bcf\u5929\u5fc3\u5934\u4e00 \u51c9\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6211\u5c45\u7136\u8fde\u8fd9\u4e24\u4e2a\u6b66\u571f\u4e5f\u63a8\u4e0d\u5012\uff0c\u770b\u6765\u529f\u529b\u5c1a\u672a\u6062\u590d\u4e00\u6210\uff01\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53e6\u5916\u90a3\u4e24\u4e2a\u6b66\u58eb\u90fd\u5df2\u4eae\u51fa\u5175\u5668\uff0c\u4e00\u67c4\u6d41\u661f\u9524\uff0c\u4e00\u53e3\u5355\u5200\uff0c\u5411\u6c5f\u6d77 \u5929\u540c\u65f6\u7838\u3001\u65ab\uff0c\u6c5f\u6d77\u5929\u5c06\u5c11\u8bb8\u7684\u771f\u529b\u8fd0\u5230\u6307\u5934\u5c16\uff0c\u5728\u9524\u5934\u4e00\u5f39\uff0c\u90a3\u67c4\u6d41\u661f\u9524\u767b\u65f6\u6539\u4e86\u65b9 \u5411\uff0c\u6253\u8fc7\u4e00\u8fb9\uff0c\u201c\u5f53\u201d\u4fa6\u4e00\u58f0\uff0c\u6070\u597d\u628a\u90a3\u67c4\u5355\u5200\u6253\u843d\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e00\u8dc3\u800c\u8d77\uff0c\u6b63\u8981\u6293\u7740\u4e00\u4e2a\u6b66\u58eb\uff0c\u5ffd\u542c\u788d\u55e4\u55e4\u58f0\u54cd\uff0c\u90a3\u4e24\u4e2a\u6b66\u58eb\u7a81\u7136\u5012\u4e0b\uff0c\u706b \u6298\u629b\u843d\uff0c\u4ea6\u5df2\u7184\u706d\u3002\u6c5f\u6d77\u5929\u53eb\u9053\uff1a\u201c\u4e0d\u5999\uff01\u201d\u8fde\u5fd9\u89e3\u4e0b\u8170\u5e26\uff0c\u542c\u98ce\u8fa8\u5668\uff0c\u5411\u524d\u4e00\u6325\uff0c\u53ea \u542c\u5f97\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u4f3c\u662f\u78b0\u843d\u4e86\u4e00\u6839\u6885\u82b1\u9488\u4e4b\u7c7b\u7684\u6697\u5668\uff1b \u3000\u3000\u5148\u524d\u7ed9\u6c5f\u6d77\u5929\u63a8\u5f00\u7684\u90a3\u4e24\u4e2a\u6b66\u58eb\uff0c\u4e00\u4e2a\u521a\u521a\u8f6c\u8fc7\u8eab\u6765\uff0c\u5ffd\u5730\u5927\u53eb\u4e00\u58f0\uff0c\u4e5f\u5012\u4e0b\u4e86\uff0c \u53e6\u4e00\u4e2a\u811a\u6b65\u8e09\u8dc4\uff0c\u7acb\u8db3\u4e0d\u7a33\uff0c\u5934\u5411\u524d\u51b2\uff0c\u773c\u770b\u5c31\u8981\u78b0\u7740\u77f3\u58c1\uff0c\u6c5f\u6d77\u5929\u98de\u8eab\u8d76\u5230\uff0c\u4e00\u628a\u6293 \u7740\u4ed6\u7684\u540e\u5fc3\uff0c\u987a\u624b\u70b9\u4e86\u4ed6\u810a\u690e\u6b63\u4e2d\u7684\u201c\u5929\u67a2\u7a74\u201d\u3002 \u3000\u3000\u6709\u4e00\u628a\u706b\u6298\u5c1a\u672a\u7184\u706d\uff0c\u6c5f\u6d77\u5929\u62ff\u4e86\u8d77\u6765\uff0c\u56db\u4e0b\u5bdf\u770b\uff0c\u5929\u9b54\u6559\u4e3b\u65e9\u5df2\u4e0d\u77e5\u53bb\u5411\uff0c\u56de\u5934 \u4e00\u770b\uff0c\u4e09\u4e2a\u6b66\u58eb\u5df2\u7ecf\u4e03\u7a8d\u6d41\u8840\u800c\u4ea1\uff0c\u53ea\u6709\u7ed9\u4ed6\u6293\u7740\u7684\u90a3\u4e2a\u6b66\u58eb\uff0c\u56e0\u4e3a\u6c5f\u6d77\u5929\u53ca\u65f6\u70b9\u4e86\u4ed6 \u7684\u5929\u67a2\u7a74\uff0c\u8fd9\u5929\u67a2\u7a74\u662f\u6c14\u8840\u901a\u5411\u5fc3\u810f\u7684\u95e8\u6237\u3002\u5c01\u4e86\u6b64\u7a74\uff0c\u53ef\u4ee5\u6682\u65f6\u963b\u6b62\u6bd2\u6c14\u5411\u5fc3\u810f\u8513\u5ef6\uff0c \u56e0\u6b64\u5f97\u4ee5\u4fdd\u5168\u6027\u547d\uff0c\u4f46\u4ea6\u5df2\u662f\u5944\u5944\u4e00\u606f\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6bdb\u9aa8\u609a\u7136\uff0c\u5fc3\u9053\uff1a\u201c\u597d\u72e0\u7684\u624b\u6bb5\u3002\u201d\u8fd9\u5730\u9053\u91cc\u6ca1\u6709\u522b\u4eba\uff0c\u4e0d\u540c\u53ef\u77e5\uff0c\u5f53\u7136\u662f \u5929\u9b54\u6559\u4e3b\u6240\u4e0b\u7684\u8fa3\u4e0b\u4e86\u3002\u6c5f\u6d77\u5929\u6700\u521d\u83ab\u540d\u5176\u5999\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u65b9\u59cb\u604d\u7136\u5927\u609f\uff1a\u201c\u5979\u662f\u8981\u6740 \u8fd9\u4e9b\u4eba\u706d\u53e3\uff0c\u514d\u5f97\u6cc4\u6f0f\u4e86\u5730\u9053\u7684\u673a\u5173\uff0c\u7ed9\u6211\u9003\u51fa\u53bb\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5b9a\u4e86\u5b9a\u7985\uff0c\u518d\u51dd\u795e\u7ec6\u542c\uff0c\u5929\u9b54\u6559\u4e3b\u4e0e\u6b27\u9633\u5a49\u7684\u547c\u606f\u4e5f\u542c\u4e0d\u5230\u4e86\uff0c\u770b\u6765\u8fd9\u5730\u9053 \u4e4b\u4e2d\uff0c\u4e0d\u53ea\u4e00\u95f4\u6697\u5ba4\uff0c\u5929\u9b54\u6559\u4e3b\u5077\u53d1\u6bd2\u9488\u4e4b\u540e\uff0c\u5df2\u85cf\u533f\u5230\u66f4\u9690\u5bc6\u7684\u5730\u65b9\u3002 \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u63d0\u9632\u5929\u9b54\u6559\u4e3b\uff0c\u5929\u9b54\u6559\u4e3b\u4e5f\u5728\u63d0\u9632\u6c5f\u6d77\u5929\uff0c\u5979\u4e0d\u53ea\u662f\u6015\u6c5f\u6d77\u5929\u9003\u51fa\u53bb\uff0c \u66f4\u5bb3\u6015\u7684\u662f\u7ed9\u6c5f\u6d77\u5929\u8bc6\u7834\u4e86\u5730\u9053\u7684\u673a\u5173\uff0c\u627e\u7740\u4e86\u5979\u85cf\u8eab\u7684\u6697\u5ba4\u3002\u5979\u6df1\u77e5\u6c5f\u6bcf\u5929\u529f\u529b\u6df1\u6e5b\uff0c \u4e2d\u7684\u6bd2\u867d\u7136\u5f88\u91cd\uff0c\u5374\u672a\u5fc5\u5c31\u80fd\u8981\u4e86\u4ed6\u7684\u6027\u547d\u3002\u6b63\u56e0\u4e3a\u5979\u4e0d\u77e5\u9053\u6c5f\u6d77\u5929\u7684\u4f24\u52bf\u5982\u4f55\uff0c\u56e0\u6b64 \u4e00\u76f4\u8eb2\u5728\u6697\u5ba4\u4e4b\u4e2d\u7aa5\u4f3a\uff0c\u4e0d\u6562\u8d38\u7136\u53d1\u96be\u3002 \u3000\u3000\u540e\u6765\u90a3\u56db\u4e2a\u6b66\u58eb\u95ef\u4e86\u8fdb\u6765\uff0c\u5411\u6c5f\u6d77\u5929\u5c55\u5f00\u653b\u51fb\uff0c\u5929\u9b54\u6559\u4e3b\u89c1\u6c5f\u964d\u5929\u53ea\u662f\u4e00\u62db\uff0c\u5c31\u628a \u4e24\u4e2a\u6b66\u58eb\u6454\u5f00\uff0c\u66f4\u662f\u5403\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u4e0e\u5176\u7ed9\u4ed6\u6293\u7740\u6d3b\u53e3\uff0c\u4e0d\u5982\u6211\u5148\u628a\u8fd9\u4e9b\u4eba\u6740\u6389\u3002\u201d\u56e0 \u6b64\u8d81\u770b\u6c5f\u6d77\u5929\u5bf9\u4ed8\u8fd9\u4e9b\u6b66\u571f\u7684\u65f6\u5019\uff0c\u4fbf\u5077\u53d1\u6bd2\u9488\uff0c\u987a\u624b\u5411\u6c5f\u6d77\u5929\u4e5f\u5c04\u4e86\u4e00\u679a\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u8fd9\u65f6\u5929\u9b54\u6559\u4e3b\u5018\u82e5\u51fa\u6765\u4e0e\u6c5f\u6d77\u5929\u5149\u660e\u6b63\u5927\u7684\u4ea4\u6218\uff0c\u6c5f\u6d77\u5929\u7684\u529f\u529b\u5728\u4e2d\u6bd2\u4e4b\u540e \u53ea\u5269\u4e0b\u4e00\u4e8c\u6210\uff0c\u51b3\u7136\u4e0d\u662f\u5979\u7684\u5bf9\u624b\u3002\u4f46\u5929\u9b54\u6559\u4e3b\u8dcc\u4e0b\u5730\u9053\uff0c\u4f24\u5f97\u4e5f\u5f88\u4e0d\u8f7b\uff0c\u4e0d\u8fc7\uff0c\u6bd4\u6c5f \u6d77\u5929\u5374\u8981\u7a0d\u597d\u4e00\u4e9b\u3002\u5979\u5bf9\u6c5f\u6d77\u5929\u53c8\u6781\u5fcc\u60ee\uff0c\u54ea\u6562\u51fa\u6765\uff1f \u3000\u3000\u597d\u5728\u6c5f\u6d77\u5929\u8fd8\u6293\u7740\u4e00\u4e2a\u6d3b\u7684\u3002\u4fbf\u95ee\u4ed6\u9053\uff1a\u201c\u8fd9\u5730\u9053\u91cc\u662f\u5426\u6709\u79d8\u5bc6\u7684\u6697\u5ba4\uff0c\u4f60\u7ed9\u6211\u5f00 \u52a8\u673a\u5173\u3002\u201d\u90a3\u6b66\u58eb\u662f\u4e2a\u571f\u4eba\uff0c\u61c2\u5f97\u7684\u6c49\u8bed\u4e0d\u591a\uff0c\u6c5f\u6d77\u5929\u58f0\u97f3\u6697\u54d1\uff0c\u8bf4\u5f97\u53c8\u4e0d\u6e05\u695a\uff0c\u90a3\u6b66 \u58eb\u53ea\u9053\u4ed6\u662f\u8981\u60f3\u51fa\u53bb\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u4fbf\u5f80\u524d\u5e26\u8def\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e00\u8def\u7559\u5fc3\uff0c\u53ea\u89c1\u4e24\u8fb9\u77f3\u58c1\u90fd\u662f\u5149\u6ed1\u5e73\u4eae\uff0c\u4e00\u70b9\u4e5f\u770b\u4e0d\u51fa\u6709\u6697\u95e8\u7684\u75d5\u8ff9\uff0c\u90a3\u6b66 \u571f\u8d8a\u8d70\u811a\u6b65\u8d8a\u662f\u8e52\u8dda\uff0c\u9762\u4e0a\u7684\u9ed1\u6c14\u4e5f\u8d8a\u6765\u8d8a\u91cd\uff0c\u6c5f\u6d77\u5929\u7528\u624b\u638c\u8d34\u7740\u4ed6\u7684\u80cc\u5fc3\uff0c\u53c8\u8017\u6389\u4e86 \u4e00\u70b9\u771f\u6c14\uff0c\u7ed9\u4ed6\u652f\u6301\u3002\u8fd9\u6b66\u58eb\u624d\u4e0d\u81f3\u5012\uff0c\u8d70\u4e86\u4e00\u4f1a\uff0c\u5ffd\u5730\u53d1\u73b0\u4eae\u5149\u3002\u539f\u6765\u5df2\u8d70\u5230\u4e86\u6d1e\u53e3\uff0c \u3000\u3000\u6c5f\u6d77\u5929\u5446\u4e86\u4e00\u5446\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u662f\u6015\u4e86\u5929\u9b54\u6559\u4e3b\uff0c\u4e0d\u6562\u5e26\u6211\u53bb\u641c\u5bfb\u5979\u4e48\uff1f\u201d\u90a3 \u6b66\u571f\u6839\u672c\u5c31\u542c\u4e0d\u6e05\u695a\u4ed6\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u53ea\u89c1\u4ed6\u53cc\u624b\u4e00\u644a\uff0c\u9996\u5782\u80f8\u81c6\uff0c\u7ec8\u4e8e\u652f\u6301\u4e0d\u4f4f\uff0c\u5012\u4e0b\u53bb \u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8017\u4e86\u4e00\u4e9b\u6c14\u529b\uff0c\u7cbe\u795e\u53c8\u89c9\u75b2\u5026\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5018\u8005\u518d\u6709\u51e0\u4e2a\u6b66\u58eb\u8ffd\u6765\uff0c\u90a3\u6211 \u662f\u51b3\u8ba1\u4e0d\u80fd\u518d\u6253\u4e86\u3002\u201d\u800c\u4e14\u5373\u4f7f\u662f\u627e\u7740\u4e86\u5929\u9b54\u6559\u4e3b\uff0c\u6b64\u65f6\u6211\u4e5f\u672a\u5fc5\u51c6\u80fd\u8d62\u5979\u3002\u201d\u65e2\u7136\u6709 \u4e86\u51fa\u8def\uff0c\u4e0d\u5982\u5c31\u5148\u9003\u4e86\u51fa\u53bb\uff0c\u5f85\u6211\u529f\u529b\u6062\u590d\u51e0\u5206\uff0c\u4e0e\u8c37\u4e2d\u83b2\u4f1a\u5408\u4e4b\u540e\uff0c\u518d\u60f3\u529e\u6cd5\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u670d\u4e86\u4e00\u9897\u5c0f\u8fd8\u4e39\uff0c\u8fd0\u6c14\u62a4\u7740\u5fc3\u623f\uff0c\u8d70\u51fa\u5730\u9053\u3002\u597d\u5728\u5916\u9762\u662f\u5757\u8352\u5730\uff0c\u56db\u671b\u65e0\u4eba\uff0c \u8fd9\u65f6\u5df2\u662f\u6e05\u6668\u65f6\u5206\u4e86\u3002\u6c5f\u6d77\u5929\u90c1\u90c1\u4e0d\u4e50\uff0c\u5fc3\u60f3\uff1a\u201c\u6b27\u9633\u5a49\u6551\u4e0d\u51fa\u6765\uff0c\u91d1\u9e70\u5bab\u4e4b\u4f1a\uff0c\u770b\u6765 \u4e5f\u8981\u9519\u8fc7\u4e86\u3002 \u3000\u3000\u54ea\u77e5\u798f\u65e0\u53cc\u81f3\uff0c\u7978\u4e0d\u5355\u884c\uff0c\u521a\u8d70\u5f97\u4e00\u7a0b\uff0c\u5ffd\u53c8\u542c\u5f97\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u7684\u543c\u58f0\uff0c\u56de\u5934\u4e00\u671b\uff0c \u53ea\u89c1\u4e00\u4eba\u4e8c\u517d\uff0c\u98ce\u9a70\u7535\u63a3\u800c\u6765\uff0c\u6c5f\u6d77\u5929\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u54a6\uff0c\u5929\u9b54\u6559\u4e3b\u600e\u80fd\u8fd9\u4e48\u5feb\u5c31\u6cbb\u597d\u4f24 \u4e86\uff1f\u201d\u518d\u5b9a\u775b\u4e00\u770b\uff0c\u539f\u6765\u4e0d\u662f\u5929\u9b54\u6559\u4e3b\u800c\u662f\u5979\u7684\u59d0\u59d0\u7f2a\u592b\u4eba\u3002\u7f2a\u592b\u4eba\u6b66\u529f\u867d\u7136\u4e0d\u5982\u59b9\u59b9\uff0c \u4f46\u5979\u4e1d\u6beb\u672a\u66fe\u53d7\u4f24\uff0c\u90a3\u662f\u66f4\u96be\u5bf9\u4ed8\u7684\u4e86\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u91d1\u6bdb\u72e1\u5df2\u7ecf\u6251\u4e86\u5230\u6765\uff0c\u6c5f\u6d77\u5929\u77aa\u8d77\u773c\u775b\uff0c\u4f5c\u52bf\u4e00\u6293\uff0c\u90a3\u4e24\u53ea\u91d1\u6bdb \u72e1\u5403\u8fc7\u4ed6\u7684\u5927\u4e8f\uff0c\u5728\u4ed6\u8eab\u524d\u4e00\u4e08\u4e4b\u5730\u505c\u4e0b\uff0c\u7adf\u4e0d\u6562\u8d38\u7136\u6251\u4e0a\u3002 \u3000\u3000\u7f2a\u592b\u4eba\u7b11\u9053\uff1a\u201c\u4e56\u513f\u5b50\uff0c\u4f60\u522b\u865a\u5f20\u58f0\u52bf\u5566\uff0c\u6211\u77e5\u9053\u4f60\u53d7\u6bd2\u4e0d\u8f7b\uff0c\u4f60\u8fd8\u8981\u4e0d\u8981\u6027\u547d\uff1f \u5018\u82e5\u606c\u547d\uff0c\u5c31\u4e56\u4e56\u8ddf\u6211\u56de\u53bb\u5427\u3002\u6211\u6709\u89e3\u836f\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e0d\u58f0\u4e0d\u54cd\uff0c\u5f85\u5979\u8d70\u8fd1\uff0c\u5ffd\u5730\u6293\u8d77\u4e00\u628a\u77f3\u5b50\uff0c\u500f\u5730\u5c31\u7528\u201c\u5929\u5973\u6563\u82b1\u201d\u7684\u624b\u6cd5\u6253 \u51fa\uff0c\u7f2a\u592b\u4eba\u60f3\u4e0d\u5230\u4ed6\u5c45\u7136\u8fd8\u80fd\u6253\u51fa\u6697\u5668\uff0c\u800c\u4e14\u6253\u5f97\u6781\u51c6\uff0c\u7f2a\u592b\u4eba\u7684\u4e09\u5904\u7a74\u9053\uff0c\u7ed9\u6253\u4e2a\u6b63 \u7740\u3002 \u3000\u3000\u7f2a\u592b\u4eba\u53ea\u89c9\u4e00\u9635\u9178\u9ebb\uff0c\u5374\u6ca1\u6709\u5012\u4e0b\u3002\u539f\u6765\u6c5f\u6d77\u5929\u6253\u5f97\u867d\u51c6\uff0c\u65e0\u5948\u6c14\u529b\u4e0d\u4f73\uff0c\u529b\u9053\u672a \u80fd\u900f\u8fc7\u5979\u7684\u7a74\u9053\uff0c\u5f53\u7136\u4e0d\u4f1a\u89c1\u6548\u3002 \u3000\u3000\u8fd9\u4e00\u6253\u4e5f\u5c31\u6cc4\u4e86\u5e95\uff0c\u7f2a\u592b\u4eba\u5fc3\u4e2d\u5927\u559c\uff0c\u77e5\u9053\u6c5f\u6d77\u5929\u5df2\u662f\u65e0\u80fd\u4e3a\u529b\uff0c\u66f4\u65e0\u987e\u5fcc\uff0c\u89e3\u4e0b \u675f\u8170\u7684\u7ef8\u5e26\uff0c\u5c31\u5411\u4ed6\u5377\u6765\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7d22\u6027\u76d8\u819d\u5750\u5728\u5730\u4e0a\uff0c\u4ed6\u62a4\u4f53\u795e\u529f\u8fd8\u6709\u51e0\u5206\uff0c\u5f53\u4e0b\u4f7f\u51fa\u91d1\u4e16\u9057\u79d8\u4f20\u7684\u6700\u4e0a\u4e58\u7684 \u5378\u529b\u6d88\u52b2\u529f\u592b\uff0c\u7ef8\u5e26\u89e6\u7740\u4ed6\u7684\u8eab\u4f53\uff0c\u5c31\u98d8\u8fc7\u4e00\u8fb9\uff0c\u7f2a\u592b\u4eba\u8bd5\u4e86\u51e0\u6b21\uff0c\u90fd\u672a\u80fd\u5377\u4e0a\uff0c\u4f3c\u4e4e \u4ed6\u7684\u8eab\u4f53\u6bd4\u7ef8\u5e26\u66f4\u8f6f\uff0c\u6beb\u4e0d\u53d7\u529b\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u73b0\u5728\u529f\u529b\u53ea\u5269\u4e00\u6210\uff0c\u4f46\u4f60\u5018\u82e5\u6562\u8d70\u5230\u6211\u7684\u8ddf\u524d\uff0c\u6211\u4e0e \u4f60\u540c\u5f52\u4e8e\u5c3d\uff0c\u5927\u7ea6\u8fd8\u4e0d\u662f\u96be\u4e8b\uff0c\u4f60\u53ef\u77e5\u9053\u4e54\u5317\u6e9f\u79d8\u7c4d\u4e2d\u6709\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\u4e48\uff1f\u201d \u3000\u3000\u7f2a\u592b\u4eba\u59d0\u59b9\u4ee5\u5389\u80dc\u7537\u7684\u7ee7\u627f\u4eba\u81ea\u5c45\uff0c\u521b\u7acb\u7684\u90aa\u6559\u5c31\u53eb\u505a\u201c\u5929\u9b54\u6559\u201d\uff0c\u5979\u5f53\u7136\u77e5\u9053 \u201c\u5929\u9e70\u89e3\u4f53\u5927\u6cd5\u201d\u7684\u5389\u5bb3\uff0c\u5f53\u5e74\u5389\u80dc\u7537\u5c31\u662f\u7528\u6b64\u90aa\u6cd5\uff0c\u6253\u8d25\u4e86\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\u2014\u2014\u5929\u5c71\u6d3e \u638c\u95e8\u5510\u6653\u6f9c\u7684\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u4eec\u867d\u521b\u7acb\u4e86\u201c\u5929\u9b54\u6559\u201d\uff0c\u5bf9\u8fd9\u201c\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\u201d\uff0c\u5374\u662f\u53ea\u95fb\u5176\u540d\uff0c\u4e1d\u6beb\u4e0d\u4f1a\u3002 \u7f2a\u592b\u4eba\u89c1\u6c5f\u6d77\u5929\u6b66\u529f\u5982\u6b64\u795e\u5947\uff0c\u4e2d\u6bd2\u4e4b\u540e\uff0c\u81ea\u5df1\u7684\u7ef8\u5e26\u8fd8\u4f9d\u7136\u5377\u4e0d\u4e0a\u4ed6\u7684\u8eab\u4f53\uff0c\u5bf9\u4ed6\u7684 \u8bdd\u7109\u6562\u4e0d\u4fe1\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u83ab\u8981\u628a\u4ed6\u903c\u5f97\u6025\u4e86\uff0c\u4ed6\u5f53\u771f\u4f7f\u51fa\u8fd9\u4e2a\u6bd2\u6cd5\u6765\u4e0e\u6211\u540c\u5f52\u4e8e\u5c3d\u3002\u201d \u3000\u3000\u5176\u5b9e\u6c5f\u6d77\u5929\u867d\u7136\u7ec3\u8fc7\u4e54\u5317\u6e9f\u79d8\u7c4d\u4e0a\u7684\u6b66\u529f\uff0c\u4f46\u8fd9\u4e2a\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\u4ed6\u5374\u672a\u80fd\u8fd0\u7528\u81ea\u5982\uff0c \u5c24\u5176\u5728\u53ea\u5269\u4e0b\u4e00\u6210\u529f\u529b\u7684\u65f6\u5019\uff0c\u8fd9\u4e2a\u6700\u8017\u635f\u771f\u6c14\u7684\u90aa\u6cd5\uff0c\u66f4\u662f\u4e0d\u80fd\u8fd0\u7528\u3002 \u3000\u3000\u7f2a\u592b\u4eba\u600e\u77e5\u5176\u4e2d\u5965\u79d8\uff0c\u88ab\u6c5f\u6d77\u5929\u4e00\u5413\uff0c\u679c\u7136\u4e0d\u6562\u8d70\u8fd1\u3001\u8fd9\u6837\u4e00\u6765\uff0c\u5979\u5bf9\u6c5f\u6d77\u5929\u7684\u653b \u51fb\u66f4\u6ca1\u6709\u6548\u529b\u4e86\u3002 \u3000\u3000\u7f2a\u592b\u4eba\u5927\u6012\uff0c\u5411\u91d1\u6bdb\u72e1\u65a5\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u4e2a\u755c\u751f\uff0c\u5bb3\u6015\u4ec0\u4e48\uff1f \u3000\u3000\u8fd8\u4e0d\u4e0a\u53bb\u5c06\u8fd9\u5c0f\u5b50\u6293\u6765\uff01\u201d\u91d1\u6bdb\u72e1\u9887\u5177\u7075\u6027\uff0c\u88ab\u7f2a\u592b\u4eba\u65a5\u8d23\uff0c\u4e0d\u6562\u4e0d\u4ece\uff0c\u800c\u4e14\u6b64\u65f6 \u6c5f\u6d77\u5929\u662f\u76d8\u819d\u5750\u5728\u5730\u4e0a\uff0c\u91d1\u6bdb\u72e1\u7684\u602f\u610f\u4e5f\u51cf\u4e86\u51e0\u5206\uff0c\u4e8e\u662f\u5f20\u7259\u821e\u722a\uff0c\u84e6\u5730\u9f50\u58f0\u543c\u53eb\uff0c\u4e00 \u524d\u4e00\u540e\uff0c\u4fbf\u5411\u6c5f\u6d77\u5929\u6251\u53bb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e8e\u94a7\u4e00\u53d1\u4e4b\u65f6\uff0c\u5ffd\u542c\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u6c99\u98de\u77f3\u8d70\uff0c\u7a7a\u4e2d\u4f20\u6765\u201c\u560e\u560e\u201d\u7684\u523a\u8033\u602a\u58f0\uff0c \u8bf4\u4e5f\u5947\u602a\uff0c\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u542c\u5230\u8fd9\u4e2a\u58f0\u97f3\uff0c\u767b\u65f6\u6709\u5982\u9047\u4e0a\u514b\u661f\uff0c\u5939\u7740\u5c3e\u5df4\u4fbf\u8dd1\u3002 \u3000\u3000\u7f2a\u592b\u4eba\u62ac\u5934\u4e00\u770b\uff0c\u5929\u7a7a\u7a81\u7136\u98de\u6765\u4e86\u4e00\u7247\u9ed1\u4e91\uff0c\u8f6c\u773c\u95f4\u5df2\u5230\u5934\u9876\uff0c\u5374\u539f\u6765\u662f\u4e00\u53ea\u7855\u5927 \u65e0\u670b\u7684\u5140\u9e70\uff0c\u7fc5\u8180\u5f20\u5f00\uff0c\u8db3\u6709\u4e24\u4e08\u591a\u957f\uff0c\u539f\u6765\u6b63\u662f\u534e\u5c71\u533b\u9690\u534a\u5929\u98ce\u6240\u517b\u7684\u90a3\u53ea\u795e\u9e70\u3002 \u3000\u3000\u8fd9\u4e24\u53ea\u91d1\u6bdb\u72e1\u66fe\u5403\u8fc7\u8fd9\u4e2a\u795e\u9e70\u7684\u5927\u4e8f\uff0c\u5728\u534e\u5c71\u4e0a\u88ab\u795e\u9e70\u6293\u8d77\u6765\u6454\u4e2a\u534a\u6b7b\u3002\u6b64\u65f6\u9047\u4e0a \u514b\u57a6\uff0c\u7109\u6562\u4f5c\u5bf9\uff0c\u6709\u4e00\u53ea\u91d1\u6bdb\u72e1\u8d70\u5f97\u7a0d\u6162\uff0c\u88ab\u795e\u9e70\u4e00\u6293\u5c31\u6293\u53bb\u4e86\u5b83\u4e00\u5927\u7247\u76ae\u8089\u3002 \u3000\u3000\u7f2a\u592b\u4eba\u53c8\u60ca\u53c8\u6012\uff0c\u4e00\u628a\u6bd2\u9488\u5411\u4e0a\u5c04\u53bb\uff0c\u5ffd\u542c\u5f97\u53ee\u53ee\u58f0\u54cd\uff0c\u9e70\u80cc\u4e0a\u98de\u51fa\u4e00\u5207\u94f6\u5149\uff0c\u5c06 \u6bd2\u9488\u5168\u90e8\u4e86\u843d\uff0c\u539f\u6765\u8fd8\u6709\u4e00\u4e2a\u5c11\u5973\uff0c\u9a91\u5728\u9e70\u4e0a\u3002 \u3000\u3000\u90a3\u5934\u795e\u9e70\u53cc\u7fc5\u4e00\u6251\uff0c\u5c31\u6293\u4e0b\u6765\uff0c\u7f2a\u592b\u4eba\u7ef8\u5e26\u4e00\u626c\uff0c\u5377\u7740\u4e86\u795e\u9e70\u7684\u5229\u722a\uff0c\u7ef8\u5e26\u767b\u65f6\u6495 \u88c2\uff0c\u4f46\u90a3\u5934\u795e\u9e70\u4e00\u6251\u4e0d\u4e2d\uff0c\u4ea6\u5df2\u98de\u8fc7\u4e86\u5979\u7684\u5934\u9876\u3002\u7f2a\u592b\u4eba\u88ab\u795e\u9e70\u6247\u8d77\u7684\u72c2\u98ce\u5439\u5f97\u5012\u9000\u51e0 \u6b65\uff0c\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u5e78\u800c\u90a3\u5c11\u5973\u5df2\u770b\u89c1\u6c5f\u6d77\u5929\uff0c\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u53eb\u9053\uff1a\u201c\u6d77\u54e5\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u5979\u987e\u4e0d\u5f97 \u9a7e\u9e70\u53bb\u8ffd\u7f2a\u592b\u4eba\uff0c\u8fde\u5fd9\u98de\u5411\u6c5f\u6d77\u5929\u7684\u8eab\u65c1\uff0c\u5f90\u5f90\u964d\u4e0b\u3002\u7f2a\u592b\u4eba\u4fa5\u5e78\u8131\u9669\uff0c\u5f53\u7136\u662f\u6ca1\u547d\u7684 \u5954\u9003\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6b7b\u91cc\u9003\u751f\uff0c\u60ca\u559c\u4ea4\u96c6\uff1a\u53eb\u9053\uff1a\u201c\u78a7\u59b9\uff0c\u662f\u4f60\u5440\uff01\u6211\u4e0d\u662f\u5728\u505a\u68a6\u5427\uff1f\u4f60\u600e\u4e48\u4e5f \u6765\u4e86\uff1f\u201d\u4ed6\u7ad9\u8d77\u8eab\u6765\uff0c\u60f3\u5411\u90a3\u5c11\u5973\u8d70\u53bb\uff1a \u3000\u3000\u53ea\u89c9\u5934\u6655\u76ee\u7729\u3002\u6c14\u529b\u5168\u65e0\uff0c\u53cc\u811a\u5df1\u662f\u4e0d\u542c\u4f7f\u5524\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u6b63\u662f\u534e\u5c71\u533b\u9690\u534e\u5929\u98ce\u7684\u5973\u513f\u534e\u4e91\u78a7\uff0c\u5979\u7cbe\u901a\u533b\u672f\uff0c\u4e00\u773c\u5c31\u770b\u51fa\u4e86\u6c5f\u6d77\u5929\u4e2d\u4e86 \u5267\u6bd2\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u522b\u8981\u8d70\u52a8\uff0c\u5feb\u5750\u4e0b\u6765\uff0c\u6211\u7ed9\u4f60\u9752\u770b\u3002\u201d\u5979\u628a\u4e86\u628a\u8109\uff0c\u597d \u751f\u60ca\u5f02\uff1a\u201c\u4ed6\u4e2d\u7684\u6bd2\u6bd2\u6027\u751a\u70c8\uff0c\u4f46\u4ed6\u7684\u8109\u606f\u5374\u5e76\u4e0d\u7d0a\u4e71\uff0c\u53ea\u662f\u7a0d\u6bd4\u5e38\u4eba\u5fae\u5f31\u800c\u5df2\u3002\u60f3\u4e0d \u5230\u5206\u624b\u4e4b\u540e\uff0c\u53ea\u662f\u8fd9\u51e0\u4e2a\u6708\u7684\u5de5\u592b\u3002\u4ed6\u7684\u5185\u529f\u7adf\u7136\u7cbe\u8fdb\u5982\u65af\uff0c\u4e2d\u4e86\u5982\u6b64\u5267\u6bd2\uff0c\u6bd2\u6c14\u7adf\u4e0d \u80fd\u4fb5\u8fdb\u4ed6\u7684\u5185\u810f\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u4e2d\u7684\u6bd2\u53ef\u6709\u4ec0\u4e48\u836f\u53ef\u4ee5\u89e3\u6551\u4e48\uff1f\u201d\u534e\u4e91\u78a7\u653e\u4e0b\u4e86\u5fc3\u4e0a\u7684\u77f3\u5934\uff0c\u7b11\u9053\uff1a \u201c\u606d\u559c\uff0c\u606d\u559c\uff01\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u606d\u559c\u4ec0\u4e48\uff1f \u3000\u3000\u8fd9\u6bd2\u4e0d\u788d\u4e8b\u4e48\uff1f\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u4e2d\u7684\u6bd2\u975e\u5e38\u5389\u5bb3\uff0c\u4f46\u4f60\u7684\u5185\u529f\u5df2\u6bd4\u4ece\u524d\u80dc\u8fc7 \u4e0d\u6b62\u4e00\u500d\uff0c\u8fd9\u6bd2\u867d\u7136\u5389\u5bb3\uff0c\u4e5f\u65e0\u5948\u4f60\u4f55\u4e86\u3002\u4e0d\u8fc7\uff0c\u53ea\u9760\u8fd0\u529f\u7597\u4f24\uff0c\u90a3\u8fd8\u5f97\u51e0\u5929\u5de5\u592b\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5927\u4e3a\u5931\u671b\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u5c31\u662f\u91d1\u9e70\u5bab\u7684\u4f1a\u671f\uff0c\u6211\u6bd2\u4f24\u672a\u6108\uff0c\u90a3\u662f\u4e0d\u80fd\u53c2\u52a0\u7684 \u4e86\u3002\u201d\u534e\u4e91\u78a7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u62c5\u5fc3\uff0c\u6211\u4e5f\u662f\u8d76\u6765\u53c2\u52a0\u91d1\u9e70\u5bab\u4e4b\u4f1a\u7684\uff0c\u4ee5\u4f60\u73b0\u5728\u7684\u529f\u529b\uff0c \u6211\u65e0\u9700\u89e3\u836f\uff0c\u6700\u591a\u4e24\u4e2a\u65f6\u8fb0\uff0c\u62c5\u4fdd\u53ef\u4ee5\u7ed9\u4f60\u6cbb\u597d\u3002\u54b1\u4eec\u53ef\u4ee5\u4e00\u540c\u524d\u5f80\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u662f\u6765\u53c2\u52a0\u91d1\u9e70\u5bab\u4e4b\u4f1a\u7684\uff0c\u90a3\u4e48\u4f60\u7239\u7239\u8fd8\u5728\u4e91\u5bb6\u5417\uff1f\u4ed6\u8001\u4eba\u5bb6 \u7684\u8eab\u4f53\u53ef\u5b8c\u5168\u5eb7\u590d\u4e86\uff1f\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u5df2\u597d\u4e86\u516b\u4e5d\u6210\u4e86\uff0c\u4ed6\u672c\u6765\u4e5f\u60f3\u6765\u7684\uff0c\u662f\u6211\u4e0d\u653e\u5fc3 \u4ed6\u8dcb\u6d89\u957f\u9014\uff0c\u6240\u4ee5\u66ff\u4ed6\u6765\u7684\u3002\u8fd9\u4e9b\u8bdd\u6162\u6162\u518d\u8bf4\u5427\uff0c\u6211\u5148\u66ff\u4f60\u62d4\u6bd2\u7597\u4f24\u3002\u201d \u3000\u3000\u5f53\u4e0b\u534e\u4e91\u78a7\u53d6\u51fa\u4e00\u53e3\u91d1\u9488\uff0c\u523a\u7834\u4e86\u6c5f\u6d77\u5929\u7684\u4e2d\u6307\uff0c\u53c8\u523a\u4e86\u4ed6\u51e0\u5904\u7a74\u9053\uff0c\u5e2e\u52a9\u4ed6\u6c14\u8840 \u6d41\u901a\uff0c\u6c5f\u6d77\u5929\u518d\u8fd0\u529f\u4e00\u8feb\uff0c\u5c06\u6bd2\u8840\u90fd\u4ece\u4e2d\u6307\u7684\u9488\u5b54\u6324\u4e86\u51fa\u6765\uff0c\u8840\u6db2\u81ea\u6df1\u9ed1\u6e10\u6e10\u53d8\u4e3a\u7d2b\u7ea2\uff0c \u4e0d\u8fc7\u7247\u523b\uff0c\u6bd2\u8840\u653e\u5c3d\uff0c\u8212\u670d\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u534e\u4e91\u78a7\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u78a7\u7075\u4e39\u5417\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd8\u6709\u4e24\u9897\u3002\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u4f60\u670d\u4e00 \u9897\u78a7\u7075\u4e39\uff0c\u4f59\u6bd2\u5c31\u53ef\u4ee5\u66f4\u5feb\u6e05\u9664\u4e86\u3002\u7136\u540e\u4f60\u81ea\u5df1\u8fd0\u529f\u6253\u901a\u7ecf\u8109\uff0c\u5927\u7ea6\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4f60\u7684\u529f \u529b\u5c31\u53ef\u4ee5\u6062\u590d\u5982\u521d\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u91cc\u9760\u8fd1\u738b\u5bab\uff0c\u987b\u5f97\u627e\u4e00\u5904\u50fb\u9759\u6240\u5728\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df1\u9690\u9690\u542c\u5f97\u9a6c\u8e44 \u9a70\u9a8b\u7684\u58f0\u97f3\u3002\u534e\u4e91\u78a7\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e9b\u5175\u9a6c\u867d\u7136\u4e0d\u60e7\uff0c\u4f46\u5230\u5e95\u662f\u907f\u5f00\u4e3a\u5b9c\u3002\u6709\u8fd9\u5934\u795e\u9e70 \u76f8\u52a9\uff0c\u8981\u907f\u5f00\u4ed6\u4eec\u4e5f\u662f\u5bb9\u6613\u5f97\u5f88\u3002\u201d\u4e8e\u662f\u5c06\u6c5f\u6d77\u5929\u62c9\u4e0a\u9e70\u80cc\uff0c\u968f\u624b\u53c8\u5728\u5730\u4e0a\u6361\u4e86\u51e0\u5757\u77f3 \u5934\uff0c\u5f85\u5f97\u90a3\u4e9b\u5175\u9a6c\u8d76\u6765\uff0c\u795e\u9e70\u65e9\u5df2\u98de\u4e0a\u7a7a\u4e2d\uff0c\u534e\u4e91\u78a7\u7ae5\u5fc3\u672a\u9000\uff0c\u5c06\u77f3\u5934\u4ece\u4e0a\u7a7a\u6254\u4e0b\u6765\uff0c \u90a3\u4e9b\u5175\u58eb\uff0c\u51e0\u66fe\u89c1\u8fc7\u8fd9\u6837\u7684\u5927\u9e70\uff0c\u89c1\u4e8c\u4eba\u9a91\u9e70\u98de\u817e\uff0c\u5df1\u662f\u5413\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u534e\u4e91\u78a7\u518d\u628a\u77f3 \u5934\u6254\u4e0b\u6765\uff0c\u90a3\u4e9b\u5175\u58eb\u53d1\u4e00\u58f0\u558a\uff0c\u8fde\u5fd9\u9003\u8dd1\uff0c \u3000\u3000\u795e\u9e70\u9a6e\u7740\u4ed6\u4eec\u4e8c\u4eba\uff0c\u98de\u5f97\u5f88\u662f\u5e73\u7a33\uff0c\u4e0d\u8fc7\u8fd9\u5934\u795e\u9e70\u867d\u7136\u5927\u5f97\u5f02\u4e4e\u5bfb\u5e38\uff0c\u9e70\u80cc\u6bd5\u7adf\u4e0d \u662f\u600e\u4e48\u5bbd\u5e7f\uff0c\u4e24\u4eba\u9760\u5728\u4e00\u8d77\uff0c\u8033\u9b13\u53ae\u78e8\uff0c\u770b\u7740\u767d\u4e91\u6735\u6735\u5728\u811a\u5e95\u98de\u8fc7\uff0c\u5f53\u771f\u4f3c\u662f\u68a6\u5883\u4e00\u822c\uff0c \u6c5f\u6d77\u5929\u7981\u4e0d\u4f4f\u795e\u601d\u98d8\u8361\uff0c\u60f3\u8d77\u4e86\u81ea\u5df1\u7684\u4e24\u6b22\u5947\u9022\uff0c\u5728\u8352\u5c9b\u4e0a\u5de7\u9047\u8c37\u4e2d\u83b2\uff0c\u800c\u8fd9\u4e00\u6b21\u548c\u534e \u4e91\u78a7\u7684\u5947\u9022\uff0c\u53c8\u66f4\u51fa\u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u4ed6\u4eec\u5df2\u5728\u9ad8\u5c71\u9876\u4e0a\u964d\u843d\uff0c\u518d\u4e5f\u4e0d\u7528\u62c5\u5fc3\u6709\u4eba\u9a9a\u6270\u4e86\u3002\u534e\u4e91\u78a7\u9053\uff1a\u201c\u6211\u7ed3 \u4f60\u627e\u70b9\u98df\u7269\u56de\u6765\uff0c\u4f60\u81ea\u884c\u8fd0\u529f\u7597\u4f24\u5427\u3002\u201d\u6c5f\u6d77\u5929\u76d8\u819d\u9759\u5750\uff0c\u5bfc\u6c14\u5f52\u5143\uff0c\u679c\u7136\u4e0d\u8fc7\u4e00\u4e2a\u65f6 \u8fb0\uff0c\u7ecf\u8109\u6253\u901a\u3002\u6c14\u8840\u5df2\u8fd0\u884c\u65e0\u963b\uff0c\u529f\u529b\u6062\u590d\u5982\u521d\u3002\u534e\u4e91\u78a7\u6253\u4e86\u4e00\u53ea\u5c0f\u9ec4\u7f8a\u56de\u6765\uff0c\u4e5f\u5df2\u7ecf \u70e4\u719f\u4e86\u3002\u53e6\u5916\u5979\u8fd8\u91c7\u6458\u4e86\u8bb8\u591a\u91ce\u679c\u548c\u76db\u4e86\u4e00\u76ae\u888b\u6e05\u51bd\u7684\u5c71\u6cc9\u56de\u6765\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u771f\u662f\u4e00\u987f\u4e30\u76db\u7684\u5927\u9910\u3002\u201d\u4ed6\u809a\u5b50\u6b63\u997f\uff0c\u5403\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u534e\u4e91\u78a7\u8d81\u8fd9 \u65f6\u5019\u544a\u8bc9\u4ed6\u522b\u540e\u7684\u60c5\u5f62\u3002 \u3000\u3000\u539f\u6765\u534e\u5929\u98ce\u5728\u4e91\u5bb6\u517b\u75c5\uff0c\u5979\u4e2d\u7684\u662f\u6bd2\u624b\u5929\u5c0a\u84b2\u5362\u864e\u7684\u6bd2\u638c\uff0c\u56e0\u4e3a\u4ed6\u7684\u836f\u56ca\u88ab\u6b27\u9633\u5a49 \u7684\u59d0\u59d0\u6b27\u9633\u6e05\u76d7\u53bb\uff0c\u5728\u4e91\u5bb6\u867d\u7136\u4e5f\u53ef\u4ee5\u914d\u836f\uff0c\u4f46\u6bd5\u7adf\u4e0d\u5982\u81ea\u5df1\u5bb6\u85cf\u7684\u5bf9\u75c7\u826f\u836f\uff0c\u56e0\u6b64\u534e \u4e91\u78a7\u7279\u5730\u8d76\u56de\u534e\u5c71\uff0c\u5c06\u836f\u5e26\u6765\uff0c\u90a3\u5934\u795e\u9e70\u534e\u5929\u98ce\u672c\u662f\u7559\u5728\u5bb6\u4e2d\u5b88\u62a4\u4ed6\u7684\u836f\u5703\u7684\uff0c\u534e\u4e91\u78a7 \u4e3a\u4e86\u8d76\u8def\uff0c\u4e5f\u5c31\u628a\u5b83\u9a91\u6765\u4e86\u3002 \u3000\u3000\u534e\u4e91\u78a7\u9053\uff1a\u201c\u6211\u7239\u7239\u672c\u6765\u662f\u63a5\u4e86\u91d1\u9e70\u5bab\u7684\u8bf7\u5e16\u7684\uff0c\u4ed6\u4e00\u6765\u4e0d\u613f\u5931\u4fe1\u4e8e\u4eba\uff0c\u4e8c\u6765\u4ed6\u4e5f \u60e6\u6302\u7740\u4f60\uff0c\u6240\u4ee5\u4ed6\u7684\u75c5\u4e00\u597d\uff0c\u4fbf\u56b7\u7740\u8981\u8d70\uff0c\u6211\u548c\u4e91\u4f2f\u4f2f\u597d\u4e0d\u5bb9\u6613\u624d\u628a\u4ed6\u529d\u963b\u4e86\u3002\u201d\u8bf4\u5230 \u8fd9\u91cc\uff0c\u7981\u4e0d\u4f4f\u9762\u4e0a\u4e00\u7ea2\uff0c\u56e0\u4e3a\u5728\u5979\u7684\u8bdd\u8bed\u4e2d\u4e0d\u8a00\u800c\u55bb\uff0c\u5979\u4ee3\u7236\u524d\u6765\uff0c\u5176\u4e2d\u4e00\u4e2a\u539f\u56e0\uff0c\u5f53 \u7136\u4e5f\u662f\u4e3a\u7740\u60e6\u5f80\u6c5f\u6d77\u5929\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u60f3\u8d77\u534e\u5bb6\u7236\u5973\u7684\u6069\u60c5\uff0c\u5341\u5206\u611f\u6fc0\uff0c\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u4ed6\u78b0\u5230\u4e86\u534e\u4e91\u78a7\u7684\u76ee\u5149\uff0c\u5374 \u53c8\u89c9\u5f97\u6709\u70b9\u513f\u60f6\u6050\u4e0d\u5b89\uff0c\u4f55\u4ee5\u4f1a\u6709\u8fd9\u6837\u7684\u5fc3\u60c5\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u534e\u4e91\u78a7\u9053\uff1a\u201c\u6211\u7684\u90fd\u5df2\u8bf4\u4e86\uff0c\u4f60\u7684\u5462\uff1f\u522b\u540e\u6709\u4e9b\u4ec0\u4e48\u906d\u9047\uff1f\u201d\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u6211\u7684 \u906d\u9047\u53ef\u591a\u4e86\uff0c\u4e09\u65e5\u4e09\u591c\u4e5f\u8bf4\u4e0d\u5b8c\u3002\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u53ea\u6311\u51e0\u6837\u7d27\u8981\u7684\u4e8b\u60c5\u95ee\u4f60\uff0c \u6b27\u9633\u5a49\u8fd9\u5996\u5973\u540e\u6765\u8fd8\u6709\u7ea0\u7f20\u4f60\u5417\uff1f\u54e6\uff0c\u8fd8\u6709\u4e00\u6837\u6211\u5fd8\u8bb0\u544a\u8bc9\u4f60\uff0c\u4e91\u5bb6\u5144\u59b9\u6240\u53d7\u7684\u5927\u4e58\u822c \u82e5\u638c\u4e4b\u4f24\uff0c\u4e5f\u65e9\u5df2\u597d\u4e86\uff0c\u53ea\u662f\u75c5\u540e\u8eab\u4f53\u865a\u5f31\uff0c\u4e00\u65f6\u8fd8\u672a\u80fd\u5b8c\u5168\u6062\u590d\uff0c\u5979\u4eec\u5207\u9f7f\u75db\u6068\u4e24\u4e2a \u4ec7\u4eba\uff0c\u4e00\u4e2a\u662f\u6253\u4f24\u4ed6\u4eec\u7684\u6076\u8d3c\u53f6\u51b2\u9704\uff0c\u53e6\u4e00\u4e2a\u5c31\u662f\u53f6\u51b2\u9704\u5408\u4f19\u540c\u8c0b\u7684\u5996\u5973\u6b27\u9633\u5a49\uff01\u563b\u563b\uff0c \u6211\u53ef\u4e0d\u6562\u544a\u8bc9\u4ed6\u4eec\uff0c\u4f60\u548c\u8fd9\u5996\u5973\u8fd8\u5f88\u6709\u4ea4\u60c5\u5462\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e0d\u5f97\u4e0d\u8bf4\u9053\uff1a\u201c\u78a7\u59b9\uff0c\u8fd9\u6b27\u9633\u5a49\u4e0d\u662f\u574f\u4eba\uff0c\u4f60\u7239\u7239\u7684\u836f\u56ca\u7684\u786e\u4e0d\u662f\u5979\u5077\u7684\u3002 \u5979\u548c\u5979\u7684\u5bb6\u4eba\u5e76\u4e0d\u4e00\u6837\uff01\u201d \u3000\u3000\u534e\u4e91\u78a7\u5f88\u4e0d\u9ad8\u5174\uff0c\u9762\u8272\u4e0a\u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u4f60\u4e00\u5b9a\u662f\u89c1\u8fc7\u5979\u4e86\uff01\u201d\u6c5f\u6d77\u5929 \u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u521a\u624d\u8fd8\u89c1\u8fc7\u5979\u3002\u201d\u4e8e\u662f\u5c06\u521a\u624d\u53d1\u751f\u7684\u4e8b\u60c5\u7b80\u5355\u8bf4\u4e86\u4e00\u904d\uff0c\u5e76\u63d0\u53ca\u6b27\u9633\u5a49 \u5728\u8352\u5c9b\u4e0a\u66fe\u6551\u8fc7\u4ed6\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u534e\u4e91\u78a7\u542c\u4e86\uff0c\u5bf9\u6b27\u9633\u5a49\u7684\u201c\u6068\u610f\u201d\u51cf\u4e86\u51e0\u5206\uff0c\u4f46\u201c\u5992\u610f\u201d\u5374\u66f4\u52a0\u6d53\u4e86\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u4f4d\u6b27\u9633\u59d1\u5a18\u5bf9\u4f60\u53ef\u771f\u8bf4\u5f97\u662f\u60c5\u6df1\u4e49\u91cd\u54ea\uff01\u201d\u6c5f\u6d77\u5929\u8bda\u6073\u8bf4\u9053\uff1a\u201c\u8bf4\u5230\u6069 \u60c5\uff0c\u4e49\u7236\u548c\u4f60\u65f6\u6211\u7684\u6069\u60c5\u66f4\u6df1\u66f4\u91cd\uff0c\u53ea\u6015\u6211\u518d\u4e16\u4e3a\u4eba\u4e5f\u96be\u62a5\u7b54\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u5fc3\u91cc\u8212\u670d\u4e86\u8bb8\u591a\uff0c\u8138\u4e0a\u53c8\u6cdb\u8d77\u4e00\u7247\u7ea2\u6655\uff0c\u4f46\u5979\u53ef\u6ca1\u6709\u60f3\u5230\uff0c\u6c5f\u6d77\u5929\u6240\u8bf4\u7684 \u201c\u6069\u60c5\u201d\u548c\u5979\u6240\u60f3\u7684\u90fd\u5e76\u975e\u5b8c\u5168\u4e00\u6837\u3002\u534e\u4e91\u78a7\u5ae3\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8c01\u8981\u4f60\u62a5\u7b54\u554a\uff1f\u201d\u63a5 \u7740\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8bf4\u8981\u627e\u5bfb\u4f60\u7684\u5e08\u7236\u3001\u7236\u4eb2\u548c\u4e00\u4f4d\u8c37\u59d1\u5a18\u4e48\uff1f\u90fd\u89c1\u7740\u4e86\u6ca1\u6709\uff1f\u201d\u6c5f\u6d77 \u5929\u9053\uff1a\u201c\u9664\u4e86\u5e08\u7236\u90fd\u89c1\u8fc7\u4e86\u3002\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u4f60\u7239\u7239\u597d\u5417\uff1f\u8c37\u59d1\u5a18\u597d\u5417\uff1f\u201d\u5979\u867d\u7136\u5148\u95ee \u5019\u6c5f\u6d77\u5929\u7684\u7236\u4eb2\uff0c\u4f46\u8bed\u6c14\u4e4b\u95f4\uff0c\u663e\u7136\u6700\u5173\u5fc3\u7684\u8fd8\u662f\u8c37\u4e2d\u83b2\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u90fd\u5f88\u597d\u3002\u54ce\uff0c\u4ed6\u4eec\u7684\u4e8b\u60c5\u4e5f\u591a\u7740\u5462\uff0c\u6162\u6162\u6211\u548c\u4f60\u8bf4\u3002\u201d\u534e\u4e91\u78a7\u7b11\u9053\uff1a \u201c\u6211\u5012\u4e0d\u6025\uff0c\u6709\u4e00\u4e2a\u4eba\u5374\u5f88\u60e6\u6302\u90a3\u4f4d\u8c37\u59d1\u5a18\u3002\u201d\u6c5f\u6d77\u5929\u6014\u4e86\u4e00\u6014\uff0c\u534e\u4e91\u78a7\u7b11\u9053\uff1a\u201c\u4e91\u743c \u4e0d\u662f\u6258\u4f60\u95ee\u5019\u8fd9\u4f4d\u8c37\u59d1\u5a18\u4e48\uff1f\u4ed6\u5bf9\u8c37\u59d1\u5a18\u79c1\u4e0b\u604b\u6155\uff0c\u4ed6\u59b9\u59b9\u90fd\u548c\u6211\u8bf4\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u4e2d\u4f3c\u7ed9\u4eba\u6295\u4e0b\u4e00\u5757\u77f3\u5b50\uff0c\u521a\u521a\u5e73\u9759\u7684\u5fc3\u6e56\u53c8\u8361\u8d77\u4e86\u5f7c\u7eb9\uff0c\u4e91\u743c\u9001\u884c\u7684\u4e00\u5e55\uff0c \u84e6\u5730\u53c8\u91cd\u73b0\u51fa\u6765\uff1a\u4e91\u743c\u90a3\u817c\u8146\u7684\u795e\u6001\uff0c\u6258\u4ed6\u5411\u8c37\u4e2d\u83b2\u201c\u81f4\u610f\u201d\u7684\u4e00\u7247\u60c5\u6000\u3002\u6c5f\u6d77\u5929\u4e0d\u7981 \u4e00\u60ca\uff1a\u201c\u4e91\u743c\u6258\u6211\u7684\u4e8b\u60c5\u6211\u600e\u4e48\u5fd8\u4e86\uff1f\u201d \u3000\u3000\u201c\u539f\u6765\u4ed6\u548c\u8c37\u4e2d\u83b2\u76f8\u5904\u4e86\u8fd9\u8bb8\u591a\u65e5\u5b50\uff0c\u7adf\u7136\u4e00\u76f4\u672a\u66fe\u5c06\u4e91\u743c\u5bf9\u5979\u7684\u7231\u610f\u5411\u5979\u900f\u9732\u3002 \u4ed6\u5fc3\u91cc\u81ea\u95ee\u81ea\u8d23\uff1a\u201c\u6211\u600e\u4e48\u4f1a\u5fd8\u4e86\u8fd9\u4ef6\u4e8b\u60c5\uff1f\u201d\u201c\u6211\u4e0d\u662f\u5b58\u5fc3\u7792\u7740\u5979\u7684\uff0c\u5f53\u65f6\u5728\u90a3\u5c9b\u4e0a\uff0c \u6211\u4eec\u5168\u526f\u7cbe\u795e\u90fd\u7528\u6765\u5bf9\u4ed8\u654c\u4eba\uff0c\u4e00\u4e9b\u65e0\u5173\u7684\u4e8b\u60c5\u81ea\u662f\u4e0d\u4f1a\u653e\u5728\u5fc3\u4e0a\u4e86\u3002\u201d\u201c\u53ef\u662f\u4e91\u743c\u5374 \u8ba4\u4e3a\u5f88\u91cd\u8981\u554a\uff0c\u4ed6\u66fe\u518d\u4e09\u53ee\u5631\u8fc7\u4f60\u7684\u3002\u201d\u6c5f\u6d77\u5929\u8fd9\u65f6\u5fc3\u4e71\u5982\u9ebb\uff0c\u4e0d\u6b62\u662f\u56e0\u4e3a\u81ea\u6127\uff0c\u800c\u4e14 \u662f\u56e0\u4e3a\u53d1\u73b0\u4e86\u81ea\u5df1\u5fc3\u5e95\u7684\u79d8\u5bc6\uff0c\u201c\u6211\u600e\u4e48\u4f1a\u5fd8\u8bb0\u7684\uff1f\u554a\uff0c\u6211\u662f\u4e0d\u613f\u610f\u8ba9\u83b2\u59b9\u77e5\u9053\uff0c\u8fd8\u6709 \u4e00\u4e2a\u4eba\u604b\u6155\u7740\u5979\uff1f\u4e0d\u9519\u3002\u620f\u4e0d\u662f\u5b58\u5fc3\u7792\u7740\u5979\u7684\uff0c\u4f46\u5728\u6211\u5fc3\u4e4b\u6df1\u5904\uff0c\u4e0d\u662f\u786e\u5b9e\u9690\u85cf\u7740\u8fd9\u4efd \u5fc3\u60c5\u5417\uff0c\u8981\u4e0d\u7136\u76f8\u5904\u4e86\u8fd9\u4e48\u591a\u65e5\u5b50\uff0c\u6211\u600e\u4f1a\u4e00\u70b9\u513f\u4e5f\u60f3\u4e0d\u8d77\u4e91\u743c\u7684\u5631\u6258\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8ba4\u8bc6\u4e86\u56db\u4e2a\u5973\u5b50\uff0c\u8fd9\u56db\u4e2a\u5973\u5b50\u90fd\u5bf9\u4ed6\u6216\u591a\u6216\u5c11\u7684\u6709\u4e00\u4efd\u60c5\u8c0a\uff0c\u4ed6\u4e5f\u4ece\u672a\u66fe\u597d \u597d\u60f3\u8fc7\u81ea\u5df1\u7a76\u7adf\u7231\u7684\u662f\u8c01\uff1f\u76f4\u5230\u5982\u4eca\uff0c\u4ed6\u624d\u53d1\u73b0\u81ea\u5df1\u5fc3\u5e95\u7684\u79d8\u5bc6\uff0c\u4ed6\u5bf9\u8c37\u4e2d\u83b2\u7684\u60c5\u610f\u4f3c \u4e4e\u4e0e\u5bf9\u5f85\u522b\u7684\u5973\u5b50\u6709\u6240\u4e0d\u540c\uff01\u4ed6\u5ffd\u5730\u611f\u5230\u5185\u759a\u4e8e\u5fc3\uff0c\u4e91\u58c1\u76f8\u8bc6\u672a\u6df1\uff0c\u4e5f\u8fd8\u7f62\u4e86\uff0c\u534e\u4e91\u78a7 \u548c\u6b27\u9633\u5a49\u5bf9\u4ed6\u5374\u90fd\u662f\u6709\u6df1\u60c5\u539a\u8c0a\u7684\uff0c\u600e\u80fd\u5c06\u5979\u4eec\u4ece\u5fc3\u4e0a\u62b9\u5f00\uff1f \u3000\u3000\u5ffd\u542c\u5f97\u51e0\u6775\u949f\u58f0\uff0c\u5728\u98ce\u4e2d\u9690\u9690\u4f20\u6765\uff0c\u6c5f\u6d77\u5929\u62ac\u5934\u4e00\u671b\uff0c\u65e5\u5934\u5df2\u7ecf\u8fc7\u5348\uff0c\u4ed6\u672c\u6765\u662f\u795e \u601d\u604d\u6064\uff0c\u8111\u7b4b\u660f\u4e71\u7684\uff0c\u4e5f\u4f3c\u7a81\u7136\u95ee\u88ab\u8fd9\u51e0\u6775\u949f\u58f0\u60ca\u9192\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8df3\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u91d1\u9e70\u5bab\u7684\u949f\u58f0\uff0c\u5927\u4f1a\u5df2\u7ecf\u5f00\u59cb\u4e86\u3002\u201d\u534e\u4e91\u78a7\u9053\uff1a \u201c\u91d1\u9e70\u5bab\u5728\u54ea\u513f\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5c31\u5728\u90a3\u8fb9\u5c71\u4e0a\u3002\u201d\u4e24\u5c71\u5bf9\u5cd9\uff0c\u91d1\u9e70\u5bab\u7684\u5c16\u9876\u9690\u7ea6\u53ef\u89c1\uff0c \u4f46\u82e5\u662f\u6b65\u884c\u524d\u5f80\uff0c\u6700\u5c11\u8fd8\u8981\u884c\u51e0\u5341\u91cc\u8def\uff0c\u534e\u4e91\u78a7\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u62c5\u5fc3\uff0c\u8bf7\u8fd9\u5934\u795e\u9e70\u518d\u9001\u54b1 \u4eec\u4e00\u7a0b\u4fbf\u662f\u3002\u201d\u6c5f\u6d77\u5929\u60f3\u8d77\u5373\u5c06\u53ef\u4ee5\u548c\u7236\u4eb2\u89c1\u9762\u3002\u5fc3\u6025\u5982\u711a\uff0c\u6068\u4e0d\u5f97\u90a3\u795e\u9e70\u5c55\u7fc5\u5373\u5230\u3002 \u3000\u3000\u6c5f\u5357\u5374\u4e0d\u77e5\u513f\u5b50\u5df2\u7ecf\u8131\u9669\uff0c\u8fd9\u65f6\u4ed6\u548c\u5510\u7ecf\u5929\u7b49\u4eba\uff0c\u6b63\u5728\u5411\u91d1\u9e70\u5bab\u8d70\u53bb\uff0c\u4e00\u8def\u4e0a\u59ec\u6653 \u98ce\u4e0d\u65ad\u5b89\u6170\u4ed6\uff0c\u8bf4\u662f\u91d1\u4e16\u9057\u5df2\u7ecf\u548c\u4ed6\u8bf4\u597d\uff0c\u4e00\u5b9a\u6765\u53c2\u52a0\u6b64\u4f1a\uff0c\u53ea\u8981\u89c1\u770b\u4e86\u91d1\u4e16\u9057\uff0c\u4ed6\u4e00 \u5b9a\u6709\u529e\u6cd5\u53ef\u60f3\u3002\u6c5f\u5357\u53ea\u597d\u628a\u5fc3\u4e8b\u653e\u8fc7\u4e00\u8fb9\uff0c\u4e00\u5fc3\u4e00\u610f\u968f\u59ec\u6653\u98ce\u8d74\u4f1a\u3002 \u3000\u3000\u9648\u5929\u5b87\u7b11\u9053\uff1a\u201c\u59ec\u5927\u54e5\uff0c\u4f60\u53ef\u4ee5\u5077\u5077\u8fdb\u53bb\uff0c\u6211\u4eec\u53ef\u8fd8\u5f97\u4f60\u5e2e\u5fd9\u60f3\u6cd5\u5462\u3002\u201d\u539f\u6765\u4ed6\u4eec \u4e00\u884c\u516d\u4eba\uff0c\u5510\u7ecf\u5929\u3001\u9648\u5929\u5b87\u4e24\u5bf9\u592b\u5987\uff0c\u518d\u52a0\u4e0a\u6c5f\u5357\u548c\u59ec\u6653\u98ce\uff0c\u516d\u4eba\u4e4b\u4e2d\uff0c\u53ea\u6709\u5510\u7ecf\u5929\u592b \u5987\u662f\u6709\u8bf7\u5e16\u7684\u3002\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u6b64\u4e8b\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\uff0c\u4f60\u770b\u6211\u7684\u624b\u6bb5\u5427\u3002\u201d \u3000\u3000\u8fd9\u65f6\u6b63\u662f\u4f1a\u524d\u7684\u4e00\u523b\uff0c\u8d76\u6765\u8d74\u4f1a\u7684\u4eba\u4eec\u6c47\u6210\u4e86\u4e00\u80a1\u4eba\u6d41\u3002\u6d8c\u8fdb\u91d1\u9e70\u5bab\u7684\u5927\u95e8\u3002\u59ec\u6653 \u98ce\u5728\u4eba\u4e1b\u4e2d\u65bd\u5c55\u7a7a\u7a7a\u5999\u624b\uff0c\u679c\u7136\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\uff0c\u5c31\u628a\u56db\u5f20\u8bf7\u5e16\u5077\u5230\u624b\u4e2d\uff0c\u5206\u7ed9\u9648\u5929\u5b87\u7b49 \u4eba\uff0c\u5f88\u5bb9\u6613\u7684\u5c31\u6df7\u8fdb\u53bb\u4e86\u3002 \u3000\u3000\u4f1a\u573a\u662f\u5728\u91d1\u9e70\u5bab\u7684\u5927\u6bbf\uff0c\u5bbd\u5e7f\u4e4b\u6781\uff0c\u4e2d\u95f4\u7559\u51fa\u4e00\u7247\u7a7a\u5730\uff0c\u56db\u8fb9\u5b89\u6392\u4e86\u4e00\u5343\u5f20\u5ea7\u4f4d\uff0c \u4f46\u4e5f\u5dee\u4e0d\u591a\u5750\u6ee1\u4e86\u3002\u59ec\u6653\u98ce\u88c5\u4f5c\u5bfb\u627e\u5ea7\u4f4d\uff0c\u5728\u4f1a\u573a\u6253\u4e86\u4e00\u8f6c\uff0c\u56db\u4e0b\u7559\u5fc3\uff0c\u5374\u4e0d\u89c1\u91d1\u4e16\u9057\u3002 \u4ed6\u8d81\u7740\u62e5\u6324\uff0c\u5012\u4e58\u673a\u5077\u4e86\u4e0d\u5c11\u4e1c\u897f\u3002 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u5728\u949f\u58f0\u4e2d\u7f13\u7f13\u51fa\u573a\uff0c\u5408\u4ec0\u5f53\u80f8\uff0c\u56db\u65b9\u65bd\u793c\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u591a\u8c22\u5404\u4f4d\u8d4f\u9762\uff0c\u8fdc\u9053\u800c\u6765\uff0c\u62db\u5f85\u4e0d\u5468\uff0c\u8fd8\u671b\u6055\u8fc7\u3002\u4e2d\u534e\u4e0a\u56fd\uff0c\u6b66\u5b66\u660c\u660e\uff0c\u8d2b\u50e7\u7d20 \u6240\u94a6\u4ef0\u3002\u6b64\u6b21\u76db\u4f1a\uff0c\u7528\u610f\u5c31\u5728\u4ee5\u6b66\u4f1a\u53cb\uff0c\u5f7c\u6b64\u89c2\u6469\u3002\u6b66\u529f\u51fa\u4f17\u7684\u524d\u5341\u540d\uff0c\u613f\u610f\u7559\u4e0b\u8005\u56fd \u738b\u5f53\u4ee5\u56fd\u58eb\u4e4b\u672d\u76f8\u4f8d\uff0c\u4e0d\u613f\u7559\u8005\u56fd\u738b\u4e5f\u6709\u5b9d\u7269\u8d4f\u8d50\u3002\u5706\u738b\u4e5f\u77e5\u5404\u4f4d\u9ad8\u8d24\u5fd7\u4e0d\u5728\u6b64\uff0c\u53ea\u662f \u804a\u8868\u5fc3\u610f\u800c\u5df2\u3002\u201d\u4ed6\u7528\u6c49\u8bed\u8bf4\u4e86\u4e00\u904d\uff0c\u63a5\u7740\u7528\u5370\u5ea6\u6700\u6d41\u884c\u7684\u65b9\u8a00\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u7136\u540e\u53c8\u6709\u901a\u8bd1\u5c06\u4ed6\u7684\u8bdd\u7ffb\u8bd1\u6210\u6ce2\u65af\u548c\u5c3c\u6cca\u5c14\u4e24\u56fd\u7684\u8bed\u8a00\uff0c\u539f\u6765\u53c2\u52a0\u8fd9\u6b21\u91d1\u9e70\u5bab\u4e4b\u4f1a \u7684\u6709\u4e2d\u3001\u5370\u3001\u5c3c\u6cca\u5c14\u548c\u6ce2\u65af\u56db\u56fd\u7684\u6b66\u6797\u4eba\u7269\uff0c\u53e6\u5916\u8fd8\u6709\u51e0\u4e2a\u963f\u523a\u4f2f\u6b66\u58eb\uff0c\u53ea\u56e0\u4ed6\u4eec\u4eba\u6570 \u592a\u5c11\uff0c\u5b9d\u8c61\u6cd5\u5e08\u4e8b\u5148\u5df2\u5bf9\u4ed6\u4eec\u8bf4\u4e2a\u6e05\u695a\uff0c\u6b64\u523b\u5c31\u6ca1\u6709\u7279\u522b\u4e3a\u4ed6\u4eec\u800c\u8bbe\u7684\u901a\u8bd1\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u5370\u5ea6\u548c\u5c1a\u9996\u5148\u5df1\u51fa\u573a\uff0c\u8bf4\u9053\uff1a\u201c\u7d20\u95fb\u4e2d\u534e\u6b66\u672f\uff0c\u9996\u63a8\u5c11\u6797\uff0c\u4f46\u5c11\u6797\u6b66\u672f\uff0c\u53c8\u6e90 \u51fa\u655d\u56fd\uff0c\u4e1c\u897f\u5206\u679d\uff0c\u8fc4\u4eca\u5df2\u903e\u5343\u8f7d\uff0c\u5404\u6709\u589e\u76ca\uff0c\u7406\u6240\u5fc5\u7136\uff0c\u5c0f\u50e7\u610f\u6b32\u5411\u4e2d\u534e\u5c11\u6797\u5bfa\u7684\u5e08 \u5144\u4eec\u8bf7\u6559\uff0c\u5370\u8bc1\u4e00\u4e0b\uff0c\u770b\u770b\u540c\u6e90\u5206\u6d41\u4e4b\u540e\uff0c\u5f7c\u6b64\u4e4b\u95f4\uff0c\u6709\u4f55\u5f02\u540c\uff1f\u201d\u4ed6\u8bf4\u5f97\u5f88\u5ba2\u6c14\uff0c\u4f46 \u8bed\u6c14\u4e4b\u95f4\uff0c\u9690\u9690\u5c06\u5c11\u6797\u4e00\u6d3e\u8d2c\u4e3a\u5370\u5ea6\u6b66\u672f\u7684\u65c1\u679d\uff0c\u663e\u7136\u662f\u81ea\u5360\u8eab\u4efd\uff0c\u81ea\u9ad8\u8eab\u4ef7\u3002 \u3000\u3000\u7387\u9886\u5c11\u6797\u95e8\u5f92\u6765\u8d74\u4f1a\u7684\u662f\u5927\u60b2\u7985\u5e08\uff0c\u6b64\u4eba\u4e43\u201c\u5341\u516b\u7f57\u6c49\u201d\u4e4b\u9996\uff0c\u751a\u6709\u6db5\u517b\uff0c\u8d70\u51fa\u573a \u6765\uff0c\u5408\u4ec0\u8bf4\u9053\uff1a\u201c\u5c0f\u5bfa\u8499\u8fbe\u6469\u7956\u5e08\u6069\u6cfd\uff0c\u5bfa\u50e7\u591a\u5c11\u61c2\u70b9\u6b66\u529f\uff0c\u4f46\u5e74\u6df1\u4ee3\u8fdc\uff0c\u7956\u5e08\u7684\u771f\u4f20\uff0c \u81f3\u4eca\u5df1\u662f\u4ec5\u5b58\u4e00\u4e8c\uff0c\u800c\u8fd9\u5341\u4e4b\u4e00\u4e8c\uff0c\u53c8\u4e0e\u4e2d\u534e\u672c\u571f\u7684\u6b66\u672f\u878d\u5408\uff0c\u53ea\u6015\u4f7f\u51fa\u6765\u7684\u5df2\u662f\u9762\u76ee \u5168\u975e\u4e86\uff0c\u4eca\u65e5\u5e78\u9047\u672c\u95e8\u6b63\u5b97\uff0c\u8fd8\u671b\u5927\u5e08\u6307\u6559\u3002\u201d\u8fd9\u756a\u8bdd\u751a\u4e3a\u5f97\u4f53\uff0c\u65e2\u8868\u793a\u4e86\u4e0d\u6562\u5fd8\u672c\uff0c \u4e5f\u8868\u793a\u4e86\u5c11\u6797\u6b66\u672f\u5e76\u975e\u5355\u7eaf\u7531\u5370\u5ea6\u800c\u6765\u3002 \u3000\u3000\u6c5f\u5357\u770b\u8fd9\u4e24\u4e2a\u548c\u5c1a\u90fd\u662f\u4e00\u6d3e\u6b63\u7ecf\u7684\u6837\u5b50\uff0c\u5728\u90a3\u91cc\u5f7c\u6b64\u5ba2\u6c14\uff0c\u89c9\u5f97\u6709\u70b9\u6ed1\u7a3d\uff0c\u201c\u591a\u5634\u201d \u7684\u813e\u6c14\u5ffd\u7136\u53d1\u4f5c\uff0c\u5728\u4eba\u4e1b\u4e2d\u56b7\u9053\uff1a\u201c\u7ba1\u4ed6\u4ec0\u4e48\u6b63\u5b97\u6b6a\u5b97\uff0c\u6253\u5f97\u8d62\u5c31\u662f\u597d\u7684\u3002\u201d\u65c1\u8fb9\u7684\u4eba \u90fd\u7b11\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd8\u662f\u8bf7\u4e24\u4f4d\u5927\u548c\u5c1a\u5feb\u4e9b\u89c1\u4e2a\u9ad8\u4e0b\u5427\uff0c\u522b\u6bd4\u8d5b\u5ff5\u7ecf\u4e86\u3002\u201d \u3000\u3000\u90a3\u5370\u5ea6\u548c\u5c1a\u61c2\u5f97\u6c49\u8bed\uff0c\u4e5f\u61c2\u5f97\u4e2d\u56fd\u6b66\u6797\u7684\u89c4\u77e9\uff0c\u4e3a\u4e86\u81ea\u5360\u8eab\u4efd\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u5f7c\u6b64 \u540c\u6e90\uff0c\u65e0\u987b\u5ba2\u6c14\uff0c\u4fbf\u8bf7\u5e08\u5144\u8d50\u62db\u3002\u201d\u5927\u60b2\u7985\u5e08\u5408\u4ec0\u5f53\u80f8\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8d2b\u50e7\u732e\u62d9\u4e86\u3002\u201d \u6b63\u662f\u8fbe\u6469\u62f3\u4e2d\u7684\u8d77\u624b\u5f0f\u201c\u660e\u5fc3\u793c\u4f5b\u201d\u3002\u53cc\u624b\u5408\u4ec0\uff0c\u8868\u793a\u5bf9\u5bf9\u65b9\u7684\u5c0a\u656c\uff0c\u4f3c\u9759\u5236\u52a8\uff0c\u53ef\u5b88 \u53ef\u653b\u3001\u5168\u770b\u5bf9\u65b9\u7684\u6765\u52bf\u5982\u4f55\uff0c\u518d\u52a0\u53d8\u5316\u3002\u6240\u4ee5\u8fd9\u4e00\u62db\u867d\u662f\u5927\u60b2\u7985\u5e08\u5148\u51fa\uff0c\u5176\u5b9e\u4ecd\u7136\u662f\u8ba9 \u90a3\u5370\u5ea6\u548c\u5c1a\u5148\u884c\u52a8\u624b\u3002 \u3000\u3000\u90a3\u5370\u5ea6\u548c\u5c1a\u5f53\u7136\u8bc6\u5f97\u6b64\u62db\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u4e14\u653b\u4f60\u4e2a\u63aa\u624b\u4e0d\u53ca\uff0c\u770b\u4f60\u5982\u4f55\u4ee5\u9759\u5236\u52a8\uff1f\u201d \u5f53\u4e0b\u5de6\u638c\u865a\u6643\uff0c\u53f3\u62f3\u500f\u5730\u4fbf\u4ece\u638c\u5e95\u7a7f\u8fc7\uff0c\u5927\u60b2\u7985\u5e08\u53cc\u638c\u672a\u5206\uff0c\u4ecd\u4ee5\u5408\u4ec0\u4e4b\u52bf\u5411\u524d\u4e00\u51fb\uff0c \u52bf\u5982\u7834\u7af9\uff0c\u4ece\u90a3\u5370\u5ea6\u548c\u5c1a\u7684\u62f3\u638c\u4e4b\u95f4\u76f4\u201c\u5256\u201d\u4e0b\u53bb\uff0c\u90a3\u5370\u5ea6\u548c\u5c1a\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u54a6\uff0c \u8fd9\u4e00\u62db\u660e\u5fc3\u793c\u4f5b\u7684\u62db\u6570\uff0c\u5374\u539f\u6765\u662f\u8fd9\u6837\u4f7f\u7684\u3002\u201d\u7a81\u7136\u5de6\u81c2\u4e00\u5f2f\uff0c\u5ffd\u5730\u4e00\u62d0\uff0c\u5411\u5927\u60b2\u7985\u5e08 \u5de6\u80c1\u51fb\u5230\u3002\u5728\u5ea7\u7684\u8bb8\u591a\u4e2d\u539f\u9ad8\u624b\uff0c\u773c\u770b\u4ed6\u5df2\u88ab\u5927\u60b2\u7985\u5e08\u7684\u62f3\u52bf\u7f69\u4f4f\uff0c\u5168\u53d7\u514b\u4f4f\uff0c\u5374\u4e0d\u6599 \u4ed6\u7a81\u7136\u4fbf\u80fd\u53cd\u653b\uff0c\u597d\u751f\u8be7\u5f02\u3002 \u3000\u3000\u539f\u6765\u8fbe\u6469\u7956\u5e08\u867d\u7136\u662f\u5370\u5ea6\u4eba\uff0c\u4f46\u4ed6\u7684\u6b66\u529f\u665a\u5e74\u65b9\u59cb\u5927\u6210\uff0c\u6240\u4ee5\u4ed6\u7684\u771f\u4f20\u662f\u5728\u4e2d\u56fd\u800c \u975e\u5370\u5ea6\u3002\u4e0d\u8bba\u62db\u6570\u6216\u5185\u529f\u9020\u8be3\uff0c\u5c11\u6797\u5bfa\u7684\u9ad8\u50e7\u90fd\u6bd4\u5370\u5ea6\u8fbe\u6469\u65e9\u5e74\u6240\u6388\u7684\u90a3\u4e00\u6d3e\u4f20\u4eba\u9ad8\u660e \u5f97\u591a\u3002\u8fd9\u5370\u5ea6\u548c\u5c1a\u4e00\u89c1\u5927\u60b2\u7985\u5e08\u7684\u51fa\u624b\uff0c\u4fbf\u77e5\u5728\u62db\u6570\u4e0a\u96be\u4ee5\u62b5\u654c\uff0c\u56e0\u800c\u5728\u8fbe\u6469\u62f3\u4e2d\u63c9\u5408 \u4e86\u5370\u5ea6\u72ec\u6709\u7684\u745c\u4f3d\u529f\u592b\u3002 \u3000\u3000\u745c\u4f3d\u529f\u592b\u7ec3\u5230\u6df1\u65f6\uff0c\u808c\u8089\u7b4b\u9aa8\u53ef\u968f\u610f\u626d\u66f2\u5f2f\u5f62\uff0c\u8fd9\u5370\u5ea6\u548c\u5c1a\u662f\u6b64\u9053\u9ad8\u624b\uff0c\u4f7f\u7684\u4ecd\u7136 \u662f\u8fbe\u6469\u62f3\u6cd5\uff0c\u4f46\u624b\u81c2\u7a81\u7136\u957f\u51fa\u51e0\u5bf8\uff0c\u4e00\u4e2a\u62d0\u5f2f\uff0c\u4fbf\u4ece\u5927\u60b2\u7985\u5e08\u610f\u60f3\u4e0d\u5230\u7684\u65b9\u4f4d\u6253\u6765\u3002 \u3000\u3000\u5927\u60b2\u7985\u5e08\u7684\u50e7\u888d\u5ffd\u5730\u9f13\u8d77\uff0c\u4fbf\u4f3c\u626c\u8d77\u4e86\u4e00\u9762\u98ce\u5e06\uff0c\u53ea\u542c\u5f97\u84ec\u7684\u4e00\u58f0\uff0c\u5982\u51fb\u8d25\u9769\uff0c\u90a3 \u548c\u5c1a\u7684\u62f3\u5934\u5df2\u88ab\u50e7\u888d\u88f9\u4f4f\uff0c\u6ee1\u9762\u901a\u7ea2\uff0c\u7528\u529b\u4e00\u62d4\uff0c\u8fd9\u624d\u62d4\u5f97\u51fa\u6765\uff0c\u8eab\u5b50\u5df2\u662f\u4e0d\u7531\u81ea\u5df1\u5730 \u8f6c\u4e86\u4e00\u5708\u3002\u5ea7\u4e2d\u4e0b\u4e4f\u6b66\u5b66\u9ad8\u624b\uff0c\u5747\u662f\u6697\u6697\u4f69\u670d\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u7684\u529f\u592b\u4f7f\u5f97\u5982 \u6b64\u795e\u5999\uff0c\u5f53\u771f\u662f\u7f55\u89c1\u7f55\u95fb\uff0c\u4e0d\u6127\u5c11\u6797\u5bfa\u5341\u516b\u7f57\u6c49\u4e4b\u9996\u3002\u201d\u4f46\u90a3\u5370\u5ea6\u548c\u5c1a\u4e5f\u6ca1\u6709\u8dcc\u5012\uff0c\u663e \u7136\u5185\u529f\u9020\u8be3\u4ea6\u975e\u6cdb\u6cdb\u3002 \u3000\u3000\u90a3\u5370\u5ea6\u548c\u5c1a\u8eab\u5f62\u672a\u7a33\uff0c\u5927\u60b2\u7985\u5e08\u6b64\u65f6\u82e5\u662f\u8d81\u52bf\u653b\u51fb\uff0c\u7acb\u5373\u4fbf\u53ef\u5c06\u4ed6\u51fb\u5012\uff0c\u4f46\u5927\u60b2\u7985 \u5e08\u5374\u4f9d\u7136\u5408\u4ec0\u5f53\u80f8\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u5e08\u5144\u518d\u8d50\u9ad8\u62db\u3002\u201d\u90a3\u5370\u5ea6\u548c\u5c1a\u53c8\u60ca\u53c8\u6012\uff0c\u731b\u5730\u4e00\u638c\u5288\u51fa\u3002 \u8bf4\u9053\uff1a\u201c\u4f60\u63a5\u63a5\u6211\u8fd9\u91d1\u521a\u638c\u529b\uff01\u201d \u3000\u3000\u8fd9\u5ea7\u5927\u6bbf\u53ef\u4ee5\u5bb9\u7eb3\u4e00\u5343\u591a\u4eba\uff0c\u5f53\u7136\u662f\u6781\u4e3a\u5bbd\u5e7f\uff0c\u4f46\u5370\u5ea6\u548c\u5c1a\u8fd9\u4e00\u638c\u5288\u51fa\uff0c\u8fde\u7ad9\u5728\u6700 \u540e\u4e00\u6392\u7684\u90fd\u611f\u5230\u52b2\u98ce\u6251\u9762\u9762\u6765\uff0c\u5a01\u52bf\u4e4b\u731b\uff0c\u53ef\u4ee5\u60f3\u89c1\u3002 \u3000\u3000\u5927\u60b2\u7985\u5e08\u8f7b\u98d8\u98d8\u7684\u4e00\u638c\u62cd\u51fa\uff0c\u65e0\u58f0\u65e0\u606f\uff0c\u770b\u4f3c\u6beb\u4e0d\u7528\u529b\uff0c\u4f46\u90a3\u5370\u5ea6\u548c\u5c1a\u5df2\u662f\u500f\u7136\u8272 \u53d8\uff0c\u5934\u4e0a\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\u3002 \u3000\u3000\u539f\u6765\u91d1\u521a\u638c\u529b\u867d\u7136\u662f\u6700\u521a\u731b\u7684\u638c\u529b\uff0c\u4f46\u7ec3\u5230\u6700\u9ad8\u5883\u754c\uff0c\u5374\u53ef\u4ee5\u521a\u67d4\u517c\u6d4e\u3002\u4e00\u65b9\u9762\u7528 \u9633\u521a\u4e4b\u52b2\u653b\u51fb\u654c\u4eba\uff0c\u4e00\u65b9\u9762\u53c8\u53ef\u4ee5\u7528\u9634\u67d4\u4e4b\u529b\u5c06\u5bf9\u65b9\u653b\u6765\u7684\u5185\u529f\u88f9\u5728\uff0c\u4ee4\u4ed6\u4e0d\u80fd\u53d1\u6325\u3002 \u8fd9\u91d1\u521a\u638c\u672c\u662f\u8fbe\u5958\u6240\u4f20\uff0c\u4f46\u5728\u5370\u5ea6\u7684\u8fd9\u4e00\u652f\uff0c\u5374\u53ea\u61c2\u5f97\u8981\u53d1\u6325\u91d1\u521a\u638c\u4e2d\u81f3\u731b\u81f3\u521a\u7684\u5a01\u529b\uff1b \u800c\u5728\u4e2d\u56fd\u7684\u8fd9\u4e00\u652f\uff0c\u5374\u878d\u5408\u4e86\u4e2d\u56fd\u6b66\u5b66\u4e2d\u201c\u521a\u67d4\u517c\u6d4e\u201d\u7684\u9053\u7406\uff0c\u5c06\u8fd9\u91d1\u521a\u638c\u529b\u7ec3\u5230\u4e86\u8d85 \u8fc7\u8fbe\u6469\u79df\u5e08\u5f53\u5e74\u7684\u5883\u754c\uff0c\u67d4\u4e2d\u6709\u521a\uff0c\u521a\u4e2d\u6709\u67d4\uff0c\u80fd\u653b\u80fd\u5b88\uff0c\u5982\u6b64\u5883\u754c\uff0c\u8fd9\u5370\u5ea6\u548c\u5c1a\u6839\u672c \u5c31\u68a6\u60f3\u4e0d\u5230\u3002 \u3000\u3000\u5927\u60b2\u7985\u5e08\u50ac\u52a8\u638c\u529b\uff0c\u4fe8\u5982\u6697\u6d41\u6c79\u6d8c\uff0c\u4e00\u6ce2\u672a\u5e73\uff0c\u4e00\u6ce2\u53c8\u8d77\uff0c\u4e00\u91cd\u91cd\u7684\u638c\u529b\u53e0\u52a0\u4e0a\u53bb\uff0c \u90a3\u5370\u5ea6\u548c\u5c1a\u653b\u51fa\u53bb\u7684\u5185\u529b\u5374\u5982\u6ce5\u725b\u5165\u6d77\uff0c\u6eb6\u89e3\u5728\u5bf9\u65b9\u7684\u9634\u67d4\u638c\u529b\u4e4b\u4e2d\uff0c \u3000\u3000\u8fd9\u5370\u5ea6\u548c\u5c1a\u81f3\u6b64\u6012\u6c14\u5c3d\u6d88\uff0c\u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u4f69\u670d\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u4f7f\u7684\u660e\u660e\u662f\u91d1\u521a \u638c\u529b\uff0c\u4f46\u4e0e\u6211\u6240\u5b66\u7684\u5374\u53c8\u5927\u4e0d\u76f8\u540c\uff0c\u786e\u662f\u9ad8\u51fa\u8bb8\u591a\u3002\u201d\u4ed6\u7528\u5c3d\u5168\u529b\uff0c\u5140\u662f\u62b5\u5fa1\u4e0d\u4f4f\uff0c\u773c \u770b\u5c31\u8981\u7ed9\u5927\u60b2\u7985\u5e08\u7684\u638c\u529b\u63a8\u5012\uff0c\u5927\u60b2\u7985\u5e08\u5ffd\u5730\u53cc\u638c\u6536\u56de\uff0c\u53c8\u518d\u5408\u4ec0\u5f53\u80f8\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u54b1\u4eec\u540c\u6e90\u5206\u6d41\uff0c\u5404\u6709\u64c5\u957f\uff0c\u5f7c\u6b64\u5f02\u540c\uff0c\u5927\u7565\u5df2\u77e5\uff0c\u53ef\u4ee5\u4e0d\u5fc3\u518d\u6bd4\u4e86\u5427\uff1f\u201d \u3000\u3000\u90a3\u5370\u5ea6\u548c\u5c1a\u548c\u91ca\u91cd\u8d1f\uff0c\u8fd8\u600e\u6562\u9053\u534a\u4e2a\u201c\u4e0d\u201d\u5b57\uff1f\u53ef\u662f\u4ed6\u867d\u7136\u5982\u91ca\u91cd\u8d1f\uff0c\u8eab\u4e0a\u6240\u53d7\u7684 \u9707\u8361\u4e00\u65f6\u95f4\u8fd8\u672a\u80fd\u5e73\u9759\u4e0b\u6765\uff0c\u4ecd\u662f\u4e0d\u7531\u81ea\u5df1\u5730\u8f6c\u4e86\u51e0\u5708\u3002\u6b63\u662f\u3002 \u3000\u3000\u7eff\u53f6\u7ea2\u82b1\u662f\u4e00\u5bb6\uff0c\u771f\u4f20\u6bd5\u7adf\u662f\u4e2d\u534e\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u60c5\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e09\u5341\u516b\u56de\u3000\u5f02\u4e10\u7384\u529f\u4f24\u6bd2\u5987\u3000\u795e\u5077\u5999\u624b\u6151\u540c\u884c \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e09\u5341\u516b\u56de\u3000\u5f02\u4e10\u7384\u529f\u4f24\u6bd2\u5987\u3000\u795e\u5077\u5999\u624b\u6151\u540c\u884c \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u80cc\u540e\u6709\u4e03\u4e2a\u548c\u5c1a\uff0c\u5176\u4e2d\u4e8c\u4eba\uff0c\u5ffd\u7136\u8d8a\u4f17\u800c\u51fa\uff0c\u4e00\u4f38\u624b\u5c31\u6276\u7a33\u4e86\u90a3\u4e2a\u6b63\u5728\u65cb \u8f6c\u4e2d\u7684\u548c\u5c1a\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u9000\u4e0b\uff01\u5f85\u6211\u5411\u8fd9\u4f4d\u5927\u5e08\u9886\u6559\u4e00\u573a\u3002\u201d\u5ea7\u4e2d\u7684\u897f\u57df\u6b66\u58eb\u8bc6\u5f97 \u6b64\u4eba\uff0c\u7a83\u7a83\u79c1\u8bae\u9053\uff1a\u201c\u7d22\u95fb\u91d1\u9e70\u5bab\u7684\u4e03\u4e2a\u62a4\u6cd5\u4e2a\u4e2a\u90fd\u6709\u60ca\u4eba\u7684\u6b66\u529f\uff0c\u8fd9\u4f4d\u5409\u7f57\u8fdc\u5927\u5e08\u5728 \u4e03\u5927\u62a4\u6cd5\u4e2d\u540d\u5217\u7b2c\u4e09\uff0c\u4ed6\u8fd9\u4e00\u51fa\u573a\uff0c\u53ef\u6709\u70ed\u95f9\u770b\u5566\uff01\u201d \u3000\u3000\u5927\u60b2\u7985\u5e08\u662f\u4f4d\u6b66\u5b66\u5927\u884c\u5bb6\uff0c\u8fd9\u5409\u7f57\u906e\u4e00\u4f38\u624b\u6276\u7a33\u4e86\u4ed6\u7684\u5e08\u5f1f\uff0c\u5927\u60b2\u7985\u5e08\u5bf9\u4ed6\u529f\u529b\u7684 \u6df1\u6d45\u5df2\u662f\u4e86\u7136\u4e8e\u80f8\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6b64\u4eba\u529f\u529b\u80dc\u4ed6\u5e08\u5f1f\u5341\u500d\uff0c\u4f46\u8981\u51fb\u7834\u6211\u7684\u91d1\u521a\u638c\u529b\u5374\u4e5f \u672a\u5fc5\u80fd\u591f\uff0c\u4ed6\u7684\u6b66\u5b66\u9020\u8be3\u751a\u6df1\uff0c\u96be\u9053\u7adf\u65e0\u81ea\u77e5\u4e4b\u660e\uff0c\u5374\u8fd8\u8981\u4e0e\u6211\u7ea0\u7f20\u4e0d\u4f11\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u5409\u7f57\u906e\u8bf4\u9053\uff1a\u201c\u8fbe\u6469\u7956\u5e08\u7684\u6b66\u529f\u7cbe\u6df1\u535a\u5927\uff0c\u65e0\u6240\u4e0d\u5305\uff0c\u8d35\u6211\u4e24\u6d3e\u540c\u6cbe\u4ed6\u7684\u6069 \u6cfd\uff0c\u4eca\u65e5\u5e78\u5f97\u76f8\u9022\uff0c\u6211\u518d\u9886\u6559\u4f60\u5175\u5203\u4e0a\u7684\u529f\u592b\u3002\u201d\u610f\u601d\u662f\u8bf4\u5927\u60b2\u7985\u5e08\u4e0e\u4ed6\u7684\u5e08\u5f1f\u5df2\u6bd4\u8fc7 \u5185\u529f\uff0c\u8fd9\u4e00\u9879\u5c31\u4e0d\u5fc5\u91cd\u590d\u4e86\u3002 \u3000\u3000\u5927\u60b2\u7985\u5e08\u7cbe\u7814\u5185\u529f\uff0c\u5728\u5175\u5203\u4e0a\u5374\u758f\u5e72\u7ec3\u4e60\uff0c\u4ece\u6765\u4e5f\u4e0d\u643a\u5e26\u5175\u5668\uff0c\u6b63\u81ea\u8e0c\u8e87\uff0c\u9752\u57ce\u6d3e \u540d\u5bbf\u8427\u9752\u5cf0\u8d70\u4e86\u51fa\u6765\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4f1a\uff0c\u662f\u4ee5\u6b66\u4f1a\u53cb\uff0c\u4f60\u4eec\u540c\u6e90\u5f02\u6d41\uff0c\u4efd\u5c5e\u540c\u95e8\uff0c \u5c3d\u662f\u4f60\u4eec\u81ea\u5df1\u4eba\u6bd4\u8bd5\u4e0b\u53bb\uff0c\u8fd8\u6709\u4ec0\u4e48\u610f\u601d\uff1f\u518d\u8bf4\uff0c\u5927\u60b2\u7985\u5e08\u4f60\u5df2\u6bd4\u8fc7\u4e00\u573a\uff0c\u4e5f\u8be5\u6b47\u6b47\u5566\u3002\u201d \u8f6c\u8fc7\u8eab\u6765\u5411\u90a3\u5409\u7f57\u906e\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4e2d\u56fd\u9752\u57ce\u6d3e\u95e8\u4e0b\u5f1f\u5b50\uff0c\u6211\u4eec\u8fd9\u6d3e\u7684\u6b66\u529f\u548c\u8d35\u56fd\u7684\u4efb\u4f55 \u4e00\u4f4d\u7956\u5e08\u90fd\u65e0\u5173\u8054\uff0c\u54b1\u4eec\u6bd4\u8bd5\u6bd4\u8bd5\uff01\u201d \u3000\u3000\u539f\u6765\u8427\u9752\u5cf0\u4e0d\u5fff\u8fd9\u4e24\u4e2a\u5370\u5ea6\u548c\u5c1a\u62ac\u51fa\u8fbe\u6469\uff0c\u81ea\u9ad8\u8eab\u4ef7\uff0c\u770b\u8f7b\u4e2d\u56fd\u6b66\u672f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a \u201c\u4ed6\u53e3\u53e3\u58f0\u58f0\u8bf4\u5c11\u6797\u6d3e\u7684\u6b66\u529f\u51fa\u4e8e\u5370\u5ea6\uff0c\u7eb5\u7136\u5927\u60b2\u7985\u5e08\u628a\u4ed6\u4e5f\u6253\u8d25\u4e86\uff0c\u90a3\u4e5f\u4e0d\u8db3\u706d\u4ed6\u7684 \u5a01\u98ce\u3002\u201d\u662f\u4ee5\u633a\u8eab\u800c\u51fa\u3002 \u3000\u3000\u8427\u9752\u5cf0\u7684\u8bdd\u8bf4\u5f97\u5f88\u8f9b\u8fa3\uff0c\u65e0\u5f02\u4ee5\u4e2d\u56fd\u6b66\u672f\u5411\u4ed6\u7684\u5370\u5ea6\u6b66\u672f\u6311\u6218\uff0c\u5409\u7f57\u906e\u4e0b\u4e0b\u4e86\u53f0\uff0c \u53ea\u5f97\u8bf4\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5f88\u597d\uff0c\u8ba9\u6211\u591a\u89c1\u8bc6\u89c1\u8bc6\u8d35\u56fd\u7684\u5404\u6d3e\u529f\u5927\uff0c\u4e5f\u6b63\u662f\u79c1\u5fc3\u6240\u613f\u3002\u201d \u3000\u3000\u5409\u7f57\u906e\u53f3\u624b\u63d0\u7740\u4e00\u6839\u9752\u7af9\u6756\uff0c\u5de6\u624b\u4e3e\u8d77\u4e00\u4e2a\u91d1\u76c2\u94b5\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6211\u7684\u5175\u5668\u5c31\u662f\u8fd9\u4e24\u4ef6\u968f\u8eab\u6cd5\u5b9d\uff0c\u8bf7\u8fdb\u62db\u5427\uff01\u201d\u8427\u9752\u5cf0\u62d4\u4e0b\u63d2\u5728\u80cc\u540e\u7684\u62c2\u5c18\uff0c\u518d\u5c06 \u8170\u5e26\u89e3\u4e0b\uff0c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u8170\u5e26\u6296\u5f97\u7b14\u76f4\u3002 \u3000\u3000\u7cbe\u8292\u8000\u76ee\uff0c\u5374\u539f\u6765\u662f\u4e00\u628a\u53ef\u4f5c\u7ed5\u6307\u67d4\u7684\u5b9d\u5251\u3002 \u3000\u3000\u8427\u9752\u5cf0\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u5730\u662f\u4e2d\u56fd\u7586\u754c\uff0c\u4f60\u662f\u5ba2\u4eba\uff0c\u4e2d\u534e\u6c49\u5b50\uff0c\u7d20\u8bb2\u793c\u4eea\uff0c\u6211 \u8ba9\u4f60\u4e09\u62db\uff01\u201d \u3000\u3000\u5409\u7f57\u906e\u8eab\u4e3a\u91d1\u9e70\u5bab\u62a4\u6cd5\uff0c\u4e00\u5411\u81ea\u5927\u60ef\u4e86\uff0c\u5fc3\u4e2d\u6709\u6c14\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u4f60\u7adf\u6562\u5c0f\u89c6\u4e8e\u6211\uff0c \u5c45\u7136\u8981\u8ba9\u6211\u4e09\u62db\uff0c\u54fc\uff0c\u54fc\uff0c\u4e14\u53eb\u4f60\u77e5\u9053\u6211\u7684\u5389\u5bb3\uff01\u201d\u5f53\u4e0b\u4e0d\u518d\u6253\u8bdd\u3002\u9752\u7af9\u6756\u4e00\u8d77\uff0c\u4fbf\u70b9 \u8427\u9752\u5cf0\u7684\u7a74\u9053\u3002 \u3000\u3000\u8fd9\u9752\u7af9\u6756\u4e00\u8d77\uff0c\u53ea\u542c\u5f97\u5636\u5636\u58f0\u54cd\uff0c\u5c31\u4f3c\u7a81\u7136\u7a9c\u51fa\u4e86\u4e00\u6761\u9752\u86c7\uff0c\u62e9\u4eba\u800c\u556e\uff0c\u76d8\u65cb\u98de\u821e\uff0c \u6756\u52bf\u98d8\u5ffd\u4e4b\u6781\u3002\u5ea7\u4e2d\u4e0d\u4e4f\u70b9\u7a74\u540d\u5bb6\uff0c\u90fd\u662f\u5fc3\u4e2d\u4e00\u51db\uff0c\u81ea\u6127\u4e0d\u5982\uff0c\u5927\u60b2\u7985\u5e08\u60f3\u9053\uff1a\u201c\u4ed6\u4ee5 \u6756\u4ee3\u7b14\uff0c\u589e\u52a0\u4e86\u8bb8\u591a\u53d8\u5316\uff0c\u679c\u7136\u662f\u6df1\u5f97\u8fbe\u6469\u7956\u5e08\u70b9\u7a74\u6cd5\u7684\u7cbe\u9ad3\uff0c\u867d\u7136\u672a\u5fc5\u5c31\u80dc\u5f97\u8fc7\u5c11\u6797 \u5bfa\uff0c\u5374\u4e5f\u4e0d\u5728\u5c11\u6797\u5bfa\u4e4b\u4e0b\u3002\u201d\u773c\u770b\u90a3\u9752\u7af9\u6756\u5982\u5f71\u968f\u5f62\uff0c\u8427\u9752\u5cf0\u7684\u5168\u8eab\u7a74\u9053\u5df2\u5728\u4ed6\u7684\u6756\u5f71 \u7b3c\u7f69\u4e4b\u4e0b\uff0c\u4e0d\u8bba\u5411\u54ea\u4e2a\u65b9\u5411\u8eb2\u95ea\u90fd\u96be\u4ee5\u95ea\u5f00\uff0c \u3000\u3000\u8427\u9752\u5cf0\u5ffd\u7136\u8de8\u51fa\u4e86\u4e00\u5927\u6b65\uff0c\u4e0d\u4f46\u4e0d\u5411\u65c1\u8fb9\u8eb2\u95ea\u53cd\u800c\u7b14\u76f4\u7684\u5411\u5409\u7f57\u906e\u8d70\u53bb\uff0c\u7b80\u76f4\u5c31\u4f3c \u9001\u4e0a\u53bb\u7ed9\u4ed6\u70b9\u7a74\u4e00\u822c\u3002\u5374\u4e5f\u5947\u602a\uff0c\u5409\u7f57\u906e\u7684\u70b9\u7a74\u4ece\u6765\u767e\u65e0\u4e00\u5931\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u8fd9\u4e00\u6b21\u654c\u4eba \u9020\u5230\u773c\u524d\uff0c\u4ed6\u4e00\u70b9\u5374\u7adf\u7136\u70b9\u4e86\u4e2a\u7a7a\uff0c\u8fde\u81ea\u5df1\u4e5f\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8427\u9752\u5cf0\u5df2\u5230\u4e86\u4ed6\u7684\u9762\u524d\uff0c\u51e0\u4e4e\u662f\u9f3b\u5b50\u78b0\u7740\u9f3b\u5b50\uff0c\u5409\u7f57\u906e\u5927\u5403\u4e00\u60ca\uff0c \u867d\u7136\u8427\u9752\u5cf0\u8bf4\u8fc7\u8ba9\u4ed6\u4e09\u62db\uff0c\u4f46\u654c\u4eba\u7a81\u7136\u6765\u5230\u9762\u524d\uff0c\u4ed6\u4e0d\u80fd\u4e0d\u9632\u5907\u654c\u4eba\u88ad\u51fb\uff0c\u4e00\u60ca\u4e4b\u4e0b\uff0c \u4e0d\u6687\u601d\u7d22\uff0c\u5de6\u624b\u7684\u91d1\u76c2\u94b5\u5c31\u671d\u7740\u8427\u9752\u5cf0\u731b\u7f69\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u7f69\u7528\u529b\u8fc7\u731b\uff0c\u91d1\u94b5\u78b0\u7740\u5730\u9762\uff0c\u94ff\u9535\u6709\u58f0\uff0c\u8427\u9752\u5cf0\u4ece\u4ece\u5bb9\u5bb9\u7684\u4ece\u4ed6\u8eab\u8fb9\u8e0f\u6b65\u800c\u8fc7\u3002 \u5409\u7f57\u906e\u4e00\u58f0\u5927\u543c\uff0c\u8eab\u5f62\u62d4\u8d77\uff0c\u7af9\u6756\u6a2a\u6325\uff0c\u91d1\u4f53\u5f84\u538b\uff0c\u7af9\u6756\u8f7b\u7075\uff0c\u91d1\u94b5\u5a01\u731b\uff0c\u4ed6\u540c\u65f6\u4f7f\u7528 \u4e24\u79cd\u5175\u5668\uff0c\u517c\u5177\u81f3\u67d4\u81f3\u521a\u4e4b\u957f\uff0c\u786e\u5b9e\u53ef\u7b97\u5f97\u662f\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\u3002 \u3000\u3000\u8427\u9752\u5cf0\u8fd9\u6b21\u66f4\u602a\uff0c\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u4ee5\u53f3\u811a\u811a\u8ddf\u4e3a\u8f74\uff0c\u7adf\u5728\u539f\u5730\u4e0a\u8f6c\u4e86\u4e00\u5708\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u201d \u7684\u4e00\u58f0\uff0c\u539f\u6765\u5409\u7f57\u906e\u4e5f\u8ddf\u7740\u4ed6\u8f6c\uff0c\u9752\u7af9\u6756\u5728\u81ea\u5df1\u7684\u91d1\u76c2\u94b5\u4e0a\u91cd\u91cd\u51fb\u4e86\u4e8c\u4e0b\u3002\u8427\u9752\u5cf0\u54c8\u54c8 \u7b11\u9053\uff1a\u201c\u4e09\u62db\u5df2\u8fc7\uff0c\u5c0f\u53ef\u8981\u6295\u673a\u62a5\u674e\u4e86\uff01\u201d\u8f6f\u5251\u6296\u5f97\u7b14\u76f4\uff0c\u5530\u7684\u5c31\u662f\u53cd\u624b\u4e00\u5251\u3002 \u3000\u3000\u539f\u6765\u8427\u9752\u5cf0\u7528\u7684\u662f\u5929\u7f57\u6b65\u6cd5\uff0c\u8fd9\u5929\u7f57\u6b65\u6cd5\u672c\u662f\u6e90\u51fa\u9752\u57ce\uff0c\u540e\u6765\u88ab\u4e54\u5317\u6e9f\u5077\u5b66\u4e86\u53bb\uff0c \u52a0\u4ee5\u6f14\u53d8\uff0c\u7cbe\u76ca\u6c42\u7cbe\uff0c\u5df1\u80dc\u4e8e\u9752\u57ce\u6d3e\u539f\u6765\u7684\u6b65\u6cd5\u3002\u5f53\u4eca\u4e4b\u4e16\uff0c\u5929\u7f57\u6b65\u6cd5\u8fd0\u7528\u5f97\u6700\u5999\u7684\u5f53 \u63a8\u59ec\u6653\u98ce\uff1b\u4f46\u8427\u9752\u5cf0\u867d\u7136\u4e0d\u53ca\u59ec\u6653\u98ce\uff0c\u7528\u6765\u5bf9\u4ed8\u8fd9\u4ece\u672a\u89c1\u8fc7\u5929\u7f57\u6b65\u6cd5\u7684\u5409\u7f57\u906e\uff0c\u5374\u662f\u7ef0 \u6709\u4f59\u88d5\u3002 \u3000\u3000\u8427\u9752\u5cf0\u662f\u9648\u5929\u5b87\u7684\u5f00\u8499\u5e08\u7236\uff0c\u631f\u6570\u5341\u5e74\u529f\u529b\uff0c\u5b9e\u662f\u4e0d\u5728\u5927\u60b2\u63b8\u5e08\u4e4b\u4e0b\uff0c\u8fd9\u4e00\u5251\u523a\u51fa\uff0c \u6fc0\u52a8\u6c14\u6d41\uff0c\u5251\u5c16\u4e0a\u53d1\u51fa\u55e4\u55e4\u58f0\u54cd\uff0c\u5409\u7f57\u906e\u4e3e\u94b5\u4e00\u6321\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u5409\u7f57\u906e\u864e\u53e3\u4e00\u9635\u9178 \u9ebb\uff0c\u4f46\u8427\u9752\u5cf0\u51cc\u5389\u7684\u5251\u62db\u5374\u4e5f\u7ed9\u4ed6\u6321\u4f4f\u4e86\u3002 \u3000\u3000\u5409\u7f57\u906e\u5230\u4e86\u6b64\u65f6\uff0c\u54ea\u91cc\u8fd8\u6562\u6709\u8f7b\u654c\u4e4b\u5fc3\uff0c\u4ee5\u91d1\u94b5\u4f5c\u4e3a\u76fe\u724c\uff0c\u6253\u5b9a\u4e86\u201c\u4e0d\u6c42\u6709\u529f\u4f46\u6c42 \u65e0\u8fc7\u201d\u7684\u7b97\u76d8\uff0c\u4ed6\u9707\u60ca\u4e8e\u8427\u9752\u5cf0\u6b65\u6cd5\u7684\u795e\u5999\uff0c\u751f\u6015\u8de8\u51fa\u4e00\u6b65\uff0c\u5c31\u4f1a\u53d7\u5230\u6697\u7b97\uff0c\u7d22\u6027\u7ad9\u5728 \u539f\u5730\u4e0d\u52a8\uff0c\u89c1\u62db\u62c6\u62db\uff0c\u89c1\u5f0f\u62c6\u5f0f\u3002 \u3000\u3000\u5409\u7f57\u906e\u5728\u8fd9\u4e24\u95e8\u5175\u5668\u4e0a\u786e\u6709\u72ec\u5230\u7684\u529f\u5927\uff0c\u672c\u8eab\u7684\u6b66\u5b66\u9020\u8be3\u5176\u5b9e\u4e5f\u5e76\u4e0d\u5728\u8427\u9752\u5cf0\u4e4b\u4e0b\uff0c \u4ed6\u575a\u5b88\u4e0d\u653b\uff0c\u6025\u5207\u95ee\u8427\u9752\u5cf0\u5012\u4e5f\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u8427\u9752\u5cf0\u811a\u8e0f\u4e5d\u5bab\u516b\u5366\u65b9\u4f4d\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u7ed5\u7740\u5409\u7f57\u906e\u8d70\u4e86\u4e00\u5708\uff0c\u4e0a\u516d\u5251\uff0c\u4e0b\u516d\u5251\uff0c\u5de6 \u516d\u5251\uff0c\u53f3\u516d\u5251\uff0c\u524d\u516d\u5251\uff0c\u540e\u516d\u5251\uff0c\u4e00\u53e3\u6c14\u653b\u51fa\u4e86\u516d\u516d\u4e09\u5341\u516d\u62db\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\u4e0d \u7edd\u4e8e\u8033\uff0c\u6bcf\u4e00\u5251\u90fd\u7ed9\u5409\u7f57\u906e\u7684\u91d1\u94b5\u6321\u56de\uff1b\u8427\u9752\u5cf0\u82e5\u662f\u8feb\u5f97\u592a\u8fd1\uff0c\u4ed6\u7684\u9752\u7af9\u6756\u53c8\u500f\u5730\u70b9\u51fa\uff0c \u4e0d\u4f46\u51fa\u624b\u654f\u6377\uff0c\u800c\u4e14\u8ba4\u7a74\u5947\u51c6\uff0c\u8981\u4e0d\u662f\u8427\u9752\u5cf0\u7684\u6b65\u6cd5\u5947\u5999\uff0c\u9669\u4e9b\u8fd8\u8981\u7740\u4e86\u4ed6\u7684\u9053\u513f\u3002 \u3000\u3000\u8427\u9752\u5cf0\u4e00\u53e3\u6c14\u653b\u51fa\u4e86\u516d\u516d\u4e09\u5341\u516d\u62db\u4e4b\u540e\uff0c\u5df2\u8bd5\u51fa\u5bf9\u65b9\u7684\u529f\u529b\u6bd4\u81ea\u5df1\u7a0d\u6709\u4e0d\u5982\uff0c\u4f46\u53f6 \u65b9\u4ee5\u91d1\u94b5\u4f5c\u4e3a\u76fe\u724c\uff0c\u914d\u5408\u4e0a\u7af9\u6756\u70b9\u7a74\u7684\u53cd\u51fb\uff0c\u4e00\u653b\u4e00\u5b88\uff0c\u914d\u5408\u5f97\u5999\u5230\u6beb\u5dc5\uff0c\u867d\u7136\u529f\u529b\u7a0d \u6709\u4e0d\u5982\uff0c\u4e5f\u8db3\u4ee5\u9632\u5fa1\u3002\u56e0\u4e3a\u8427\u9752\u8702\u4e0d\u6562\u5927\u8fc7\u8feb\u8fd1\uff0c\u6bcf\u4e00\u62db\u90fd\u4f3c\u873b\u8713\u70b9\u6c34\uff0c\u4e00\u60ca\u5373\u8fc7\uff0c\u529f \u529b\u81ea\u662f\u4e0d\u80fd\u5c3d\u6570\u53d1\u6325\uff0c\u867d\u7136\u7a0d\u9ad8\u4e00\u7b79\u3002\u4e5f\u6ca1\u6709\u591a\u5927\u6548\u7528\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u6597\u4e86\u4e00\u4f1a\uff0c\u8fd8\u662f\u76f8\u6301\u4e0d\u4e0b\u4e4b\u5c40\uff0c\u91d1\u9e70\u5bab\u7684\u62a4\u6cd5\u5927\u5f1f\u5b50\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u65d7\u9f13\u76f8\u5f53\uff0c \u53ef\u4ee5\u7f62\u624b\u4e86\u5427\uff1f\u201d\u8427\u9752\u5cf0\u5ffd\u9053\uff1a\u201c\u4e14\u9992\uff0c\u8bf7\u5927\u548c\u5c1a\u518d\u63a5\u4e00\u62db\uff01\u201d\u62c2\u5c18\u4e00\u5c55\uff0c\u628a\u5409\u7f57\u906e\u7684 \u9752\u7af9\u6756\u7f20\u4f4f\uff0c\u5409\u7f57\u906e\u7528\u52b2\u4e00\u70b9\uff0c\u55e4\u55e4\u58f0\u54cd\uff0c\u5c18\u5c3e\u6563\u5f00\uff0c\u773c\u770b\u8fd9\u9752\u7af9\u6756\u6446\u8131\u62c2\u5c18\u7684\u7ea0\u7f20\uff0c \u53ea\u8981\u5f80\u524d\u4e00\u9001\uff0c\u5c31\u8981\u70b9\u4e2d\u8427\u9752\u5cf0\u7684\u201c\u66f2\u6c60\u7a74\u201d\uff0c\u5ffd\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u5409\u7f57\u906e\u7684\u91d1 \u76c2\u94b5\u5df2\u7ed9\u4e00\u5251\u523a\u7a7f\uff0c\u8427\u9752\u5cf0\u54c8\u54c8\u5927\u7b11\uff0c\u500f\u5730\u817e\u8eab\u800c\u8d77\uff0c\u4e00\u4e2a\u9cd0\u5b50\u7ffb\u8eab\uff0c\u8eab\u5f62\u5df2\u843d\u5728\u4e09\u4e08 \u4e4b\u5916\u3002 \u3000\u3000\u539f\u6765\u8427\u9752\u5cf0\u7ecf\u9a8c\u8001\u5230\uff0c\u673a\u667a\u8fc7\u4eba\uff0c\u4ed6\u770b\u51c6\u4e86\u8fd9\u5370\u5ea6\u548c\u5c1a\u80c6\u602f\uff0c\u4e0d\u6562\u632a\u52a8\u4f4d\u7f6e\u653b\u4ed6\uff0c \u4e8e\u662f\u60f3\u51fa\u4e86\u4e00\u4e2a\u5192\u9669\u7684\u7834\u654c\u4e4b\u6cd5\uff0c\u4ee5\u5df2\u4e4b\u957f\uff0c\u653b\u654c\u4e4b\u77ed\uff0c\u5728\u53f3\u624b\u7684\u957f\u5251\u4e0a\u7528\u4e86\u4e5d\u6210\u529f\u529b\uff0c \u5de6\u624b\u62c2\u5c18\u7684\u90a3\u4e00\u62c2\u5374\u53ea\u7528\u4e86\u4e00\u6210\u529f\u529b\uff0c\u5409\u7f57\u906e\u770b\u4e0d\u7834\u4ed6\u8fd9\u4e00\u62c2\u4e43\u662f\u865a\u62db\uff0c\u7af9\u6756\u4e00\u88ab\u7f20\u4e0a\uff0c \u81ea\u8981\u5168\u529b\u7834\u89e3\uff0c\u8427\u9752\u5cf0\u90a3\u4e00\u5251\u500f\u7136\u800c\u6765\u3002\u679c\u7136\u594f\u6548\u3002 \u3000\u3000\u8427\u9752\u5cf0\u8fd9\u4e00\u62db\u5b9e\u662f\u7528\u5f97\u9669\u6781\uff0c\u5018\u82e5\u5409\u7f57\u906e\u8bc6\u7834\u4ed6\u90a3\u4e00\u62c2\u4e43\u662f\u52a9\u653b\u7684\u865a\u62db\uff0c\u6562\u8df3\u4e0a\u524d \u70b9\u4ed6\u7a74\u9053\u7684\u8bdd\uff0c\u53cc\u65b9\u5c31\u8981\u4e24\u8d25\u4ff1\u4f24\u4e86\u3002 \u3000\u3000\u5982\u4eca\u8427\u9752\u5cf0\u6beb\u53d1\u65e0\u4f24\uff0c\u800c\u5409\u7f57\u906e\u7684\u91d1\u94b5\u5374\u5df2\u7834\u635f\uff0c\u52bf\u96be\u518d\u6597\uff0c\u5f53\u7136\u53ea\u597d\u8ba4\u8f93\u3002 \u3000\u3000\u91d1\u9e70\u5bab\u7684\u4e03\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u9762\u4e0a\u65e0\u5149\uff0c\u6b63\u62df\u63a8\u4e00\u4e2a\u4eba\u51fa\u53bb\u6311\u6218\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u4eba\u58f0\u55a7\u95f9\uff0c \u4f3c\u662f\u53d1\u751f\u4e89\u5435\uff0c\u6709\u4e00\u4e2a\u5b88\u536b\u51b2\u8fdb\u6765\u62a5\u9053\uff1a \u3000\u3000\u201c\u5916\u9762\u6709\u56db\u4e2a\u6ca1\u5e26\u8bf7\u675f\u7684\u4eba\uff0c\u8bf4\u662f\u8bf7\u67ec\u4e0d\u77e5\u5982\u4f55\u5931\u53bb\uff0c\u5374\u8981\u8fdb\u6765\uff0c\u51c6\u662f\u4e0d\u51c6\uff0c\u8bf7\u56fd \u5e08\u5b9a\u593a\u3002\u201d\u539f\u6765\u8fd9\u56db\u4e2a\u4eba\u7684\u8bf7\u675f\uff0c\u6b63\u662f\u7ed9\u59ec\u6653\u98ce\u6109\u53bb\u7684\uff0c\u4ed6\u4eec\u5230\u95e8\u53e3\u4e00\u6478\uff0c\u624d\u53d1\u73b0\u4e0d\u89c1\uff0c \u8fde\u81ea\u5df1\u4e5f\u83ab\u540d\u5176\u5999\u3002\u62a4\u6cd5\u5927\u5f1f\u5b50\u95ee\u4e86\u90a3\u56db\u4e2a\u4eba\u7684\u540d\u5b57\uff0c\u4fbf\u53bb\u7980\u544a\u5b9d\u8c61\u6cd5\u5e08\u3002\u5b9d\u8c61\u6cd5\u5e08\u542c \u4e86\uff0c\u5ffd\u5730\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u539f\u6765\u8fd9\u56db\u4e2a\u4eba\u5b9d\u8c61\u6cd5\u5e08\u90fd\u5f88\u719f\u6089\uff0c\u90a3\u56db\u5f20\u8bf7\u67ec\u4e5f\u662f\u4ed6\u81ea\u5df1\u5199\u7684\u3002\u4ed6\u7b11\u7740\u95ee\u90a3\u62a4\u6cd5\u5f1f \u5b50\u9053\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u4ed6\u4eec\u662f\u8c01\u5417\uff1f\u201d\u62a4\u6cd5\u5f1f\u5b50\u9053\uff0c\u201c\u6b63\u8981\u8bf7\u95ee\u5e08\u5c0a\u3002\u201d\u5b9d\u8c61\u6cd5\u5e08\u9053\uff1a\u201c\u6709 \u4e09\u4e2a\u662f\u5a46\u7f57\u95e8\u6559\u7684\u9ad8\u624b\uff0c\u53e6\u5916\u4e00\u4e2a\u662f\u5317\u5929\u7afa\u8457\u540d\u7684\u5999\u624b\u795e\u5077\uff0c\u60f3\u4e0d\u5230\u4ed6\u4e00\u8e0f\u51fa\u56fd\u5883\uff0c\u5c31 \u78b0\u4e0a\u4e86\u5f02\u56fd\u540c\u884c\uff0c\u628a\u4ed6\u538b\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u62a4\u6cd5\u5f1f\u5b50\u4e0d\u7981\u9a87\u7136\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u8981\u4e0d\u8981\u8ffd\u7a76\u76d7\u67ec\u4e4b\u4eba\uff1f\u201d\u5b9d\u8c61\u6cd5\u5e08\u7b11\u9053\uff1a\u201c\u8fd9\u6837 \u7684\u9ad8\u624b\u8bf7\u90fd\u8bf7\u4e0d\u5230\u5462\uff01\u53ea\u4e0d\u77e5\u662f\u54ea\u4f4d\u6240\u4e3a\uff0c\u53ef\u80af\u51fa\u6765\u76f8\u89c1\u4e48\uff1f\u201d \u3000\u3000\u59ec\u6653\u98ce\u5750\u5728\u540e\u5934\uff0c\u6b63\u8981\u7b54\u8bdd\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u5927\u543c\u9053\uff1a\u201c\u91d1\u9e70\u5bab\u4e3b\u4eba\u53ef\u5bbd\u6055\u8fd9\u4e2a\u5c0f\u8d3c\uff0c \u54b1\u4eec\u5144\u5f1f\u53ef\u4e0d\u80fd\u9976\u8fc7\u4ed6\uff01\u59ec\u6653\u98ce\uff0c\u51fa\u6765\uff01\u201d\u8fd9\u4e24\u4e2a\u4eba\u6b63\u662f\u4ee5\u524d\u66fe\u5230\u5c11\u6797\u5bfa\u76d7\u4e66\u7684\u90a3\u4e24\u4e2a \u756a\u50e7\u2014\u2014\u7afa\u6cd5\u5170\u548c\u7afa\u6cd5\u4f11\uff0c\u4ed6\u4eec\u662f\u5b6a\u751f\u5144\u5f1f\uff0c\u5fc3\u610f\u5982\u4e00\uff0c\u4e00\u540c\u7ad9\u8d77\uff0c\u4e00\u540c\u543c\u53eb\uff0c\u53d1\u6012\u7684 \u795e\u60c5\u4e5f\u662f\u4e00\u6a21\u4e00\u6837\u201c \u3000\u3000\u59ec\u6653\u98ce\u54c8\u54c8\u5927\u7b11\uff0c\u8d70\u51fa\u6765\u5148\u5411\u5b9d\u8c61\u6cd5\u5e08\u65bd\u4e86\u4e00\u793c\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5f97\u7f6a\uff0c\u5f97\u7f6a\uff01\u201d\u90a3\u62a4\u6cd5\u5f1f\u5b50\u5949\u547d\u51fa\u53bb\u8fce\u63a5\u90a3\u56db\u4e2a\u4eba\uff0c\u6b63\u7ecf\u8fc7\u59ec\u6653\u98ce\u8eab\u8fb9\uff0c\u4e0d\u89c9\u5b9a \u4e86\u773c\u775b\uff0c\u5411\u4ed6\u6ce8\u89c6\uff0c\u5583\u5583\u81ea\u8bed\u9053\u3002\u201d\u4eba\u4e0d\u53ef\u8c8c\u76f8\uff0c\u6d77\u4e0d\u53ef\u6597\u91cf\u3002\u8fd9\u814c\u81dc\u8001\u513f\u7adf\u6709\u5982\u6b64\u672c \u9886\uff0c\u771f\u662f\u610f\u60f3\u4e0d\u5230\u3002\u201d\u4ed6\u7528\u5370\u5ea6\u65b9\u8a00\u81ea\u8a00\u81ea\u8bed\uff0c\u59ec\u6653\u98ce\u4e0d\u77e5\u4ed6\u8bf4\u4ec0\u4e48\uff0c\u4e5f\u9f87\u7259\u54a7\u5634\u5411\u4ed6 \u4e00\u7b11\u3002 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u5ffd\u5730\u7528\u6c49\u8bed\u5927\u53eb\u9053\uff1a\u201c\u59ec\u5148\u751f\uff0c\u6211\u6ca1\u6709\u7ed9\u4f60\u9001\u53bb\u8bf7\u675f\u662f\u6211\u5931\u793c\uff0c\u4f60\u76d7\u8bf7\u67ec \u6211\u4e0d\u602a\u4f60\u3002\u8fd9\u4e32\u5ff5\u73e0\u662f\u6211\u8d50\u7ed9\u5f1f\u5b50\u4e4b\u7269\uff0c\u8bf7\u4f60\u4ea4\u8fd8\uff01\u201d\u6d3b\u58f0\u672a\u4e86\uff0c\u59ec\u6653\u98ce\u5ffd\u89c9\u864e\u53e3\u4f3c\u4e4e \u7ed9\u5229\u9488\u7a81\u7136\u523a\u4e86\u4e00\u4e0b\uff0c\u4ed6\u672c\u6765\u662f\u63e1\u7740\u62f3\u5934\u7684\uff0c\u8fd9\u4e00\u4e0b\u5c31\u4e0d\u7531\u5f97\u81ea\u5df1\u5f20\u5f00\u4e86\uff0c\u53ea\u542c\u5f97\u54d7\u5566 \u5566\u4e00\u7247\u58f0\u54cd\uff0c\u4e00\u4e32\u5ff5\u73e0\u5760\u4e0b\u5730\u6765\uff0c\u539f\u6765\u4ed6\u51b2\u7740\u90a3\u62a4\u6cd5\u5f1f\u5b50\u54a7\u5634\u4e00\u7b11\u7684\u65f6\u5019\uff0c\u65e9\u5df2\u65bd\u5c55\u795e \u5077\u7edd\u6280\uff0c\u628a\u4ed6\u80f8\u524d\u6240\u6302\u7684\u4e00\u4e32\u5ff5\u73e0\u5077\u5230\u624b\u4e2d\uff0c\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u7adf\u65e0\u4e00\u4eba\u53d1\u73b0\u3002 \u3000\u3000\u90a3\u62a4\u6cd5\u5f1f\u5b50\u548c\u59ec\u6653\u98ce\u90fd\u662f\u9707\u60ca\u4e0d\u5df2\uff0c\u62a4\u6cd5\u5f1f\u5b50\u9707\u60ca\u4e8e\u4ed6\u7684\u795e\u607c\u7edd\u6280\uff0c\u62fe\u8d77\u5ff5\u73e0\uff0c\u614c \u5fd9\u4fbf\u8d70\uff0c\u518d\u4e5f\u4e0d\u6562\u9760\u8fd1\u4ed6\u7684\u8eab\u8fb9\u3002\u59ec\u6653\u98ce\u5219\u9707\u60ca\u4e8e\u5b9d\u8c61\u6cd5\u5e08\u7684\u7edd\u4e16\u795e\u529f\uff0c\u5728\u90a3\u4e48\u8fdc\u7684\u8ddd \u79bb\uff1b\u5c45\u7136\u80fd\u7528\u9694\u7a7a\u70b9\u7a74\u7684\u529f\u5927\u70b9\u4e2d\u81ea\u5df1\u7684\u864e\u53e3\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u62c5\u5fc3\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u7684 \u529f\u592b\u770b\u6765\u4e0d\u5728\u91d1\u5927\u4fa0\u4e4b\u4e0b\uff0c\u8981\u662f\u91d1\u5927\u4fa0\u4e0d\u6765\uff0c\u5c31\u65e0\u4eba\u662f\u4ed6\u5bf9\u624b\u4e86\u3002\u201d \u3000\u3000\u7afa\u6c0f\u5144\u5f1f\u9f50\u58f0\u559d\u9053\uff1a\u201c\u59ec\u6653\u98ce\uff0c\u4f60\u8d3c\u6027\u4e0d\u6539\uff0c\u5403\u6211\u4e00\u62f3\uff01\u201d\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u5f7c\u6b64\uff0c \u5f7c\u6b64\uff0c\u4e0d\u8fc7\u4f60\u4eec\u4e24\u4e2a\u4e43\u662f\u65b0\u4eba\u884c\u7684\u5c0f\u8d3c\uff0c\u53ef\u5f97\u591a\u591a\u5411\u6211\u8bf7\u6559\u8bf7\u6559\u3002\u201d\u7b11\u58f0\u4e2d\u8eab\u5f62\u4e00\u95ea\u3002 \u5df2\u907f\u5f00\u4e86\u7afa\u6c0f\u5144\u5f1f\u7684\u653b\u51fb\u3002 \u3000\u3000\u4e3b\u6301\u6b64\u6b21\u6bd4\u6b66\u7684\u4e00\u4e2a\u88c1\u5224\u9053\uff1a\u201c\u4e14\u6162\uff0c\u4f60\u4eec\u662f\u4e24\u5144\u5f1f\u9f50\u4e0a\uff0c\u59ec\u5148\u751f\u4f60\u8981\u4e0d\u8981\u4eba\u5e2e\u5fd9\uff1f\u201d \u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u6211\u548c\u4ed6\u4eec\u672c\u6765\u662f\u5408\u4f19\u4eba\uff0c\u6253\u6765\u73a9\u73a9\u7684\uff0c\u4e0d\u7528\u5982\u6b64\u8ba4\u771f\uff0c\u5c31\u7531\u6211\u8fd9\u4e2a\u8001\u8d3c \u5bf9\u4ed8\u4ed6\u4eec\u8fd9\u4e24\u4e2a\u5c0f\u8d3c\u597d\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5929\u7afa\u4e8c\u50e7\u5f53\u5e74\u4e0e\u59ec\u6653\u98ce\u7ed3\u4f34\uff0c\u540c\u5230\u5c11\u6797\u5bfa\u76d7\u7ecf\uff0c\u59ec\u6653\u98ce\u6f5c\u5165\u85cf\u7ecf\u9601\uff0c\u8fd9\u4e8c\u4eba\u5728\u5916 \u9762\u7ed9\u4ed6\u628a\u98ce\uff0c\u672c\u6765\u662f\u8bf4\u597d\u4e86\u5018\u82e5\u5f97\u624b\uff0c\u4e09\u4eba\u5171\u4eab\u7684\u3002\u54ea\u77e5\u59ec\u6653\u98ce\u4e00\u8fdb\u5165\u85cf\u7ecf\u9601\uff0c\u4fbf\u7ed9\u5c11 \u6797\u618e\u4eba\u53d1\u73b0\uff0c\u7afa\u6c0f\u5144\u5f1f\u88ab\u64d2\uff0c\u59ec\u6653\u98ce\u4ed7\u7740\u8f7b\u529f\u9ad8\u660e\uff0c\u6109\u5230\u4e86\u4e09\u672c\u5185\u529f\u79d8\u7c4d\uff0c\u4fbf\u5728\u98ce\u96e8\u5bfa \u4e2d\u9003\u8131\u4e86\u3002\u540e\u6765\u5c11\u6797\u65b9\u4e08\u75db\u7985\u4e0a\u4eba\u4e49\u91ca\u4e8c\u50e7\uff0c\u59ec\u6653\u98ce\u53c8\u6539\u90aa\u5f52\u6b63\uff0c\u5c06\u7ecf\u4e66\u4ea4\u8fd8\u5c11\u6797\u5bfa\u3002 \u8fd9\u5929\u7afa\u4e8c\u50e7\u5f97\u4e0d\u5230\u7ecf\u4e66\uff0c\u9042\u4e0e\u59ec\u6653\u98ce\u7ed3\u4e0b\u4e86\u6881\u5b50\uff0c\u5341\u4f59\u5e74\u6765\uff0c\u5230\u5904\u8ffd\u8e2a\uff0c\u4e5f\u66fe\u4e24\u6b21\u78b0\u4e0a\uff0c \u4f46\u4e24\u6b21\u90fd\u7ed9\u59ec\u6653\u98ce\u4fa5\u5e78\u9003\u8131\u3002\uff08\u4e8b\u8be6\u300a\u4e91\u6d77\u7389\u5f13\u7f18\u300b\uff09 \u3000\u3000\u76d7\u7ecf\u88ab\u64d2\u4e4b\u4e8b\uff0c\u7afa\u6c0f\u5144\u5f1f\u4e00\u751f\u5f15\u4ee5\u4e3a\u803b\uff0c\u59ec\u6653\u98ce\u5374\u6beb\u65e0\u987e\u5fcc\uff0c\u201c\u8001\u8d3c\u201d\u201c\u5c0f\u8d3c\u201d\u7684 \u8bf4\u4e2a\u4e0d\u4f11\uff0c\u7afa\u6c0f\u5144\u5f1f\u5927\u6012\uff0c\u500f\u5730\u5206\u5f00\u3001\u4e00\u4e2a\u7ad9\u5728\u4e1c\u9996\uff0c\u4e00\u4e2a\u7ad9\u5728\u897f\u9996\uff0c\u540c\u65f6\u53d1\u638c\u3002 \u3000\u3000\u7afa\u6cd5\u5170\u638c\u529b\u5148\u5230\uff0c\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u4e56\u4e56\uff0c\u597d\u5389\u5bb3\uff01\u201d\u8eab\u5f62\u4e00\u4fa7\uff0c\u907f\u8fc7\u4e00\u8fb9\uff0c\u5374\u4e0d\u6599 \u6b63\u597d\u907f\u5165\u7afa\u6cd5\u4f11\u7684\u638c\u529b\u7b3c\u7f69\u8303\u56f4\u4e4b\u5185\uff0c\u59ec\u6653\u98ce\u7acb\u8db3\u4e0d\u7a33\uff0c\u4e00\u4e2a\u8e09\u8dc4\uff0c\u53cd\u5f39\u51fa\u6765\uff0c\u7afa\u6cd5\u5170 \u7684\u638c\u529b\u53c8\u81ea\u524d\u5fc3\u653b\u5230\u3002 \u3000\u3000\u539f\u6765\u7afa\u6c0f\u5144\u5f1f\u6df1\u77e5\u59ec\u6653\u98ce\u5929\u7f57\u6b65\u6cd5\u7684\u9ad8\u660e\uff0c\u5438\u6536\u4e86\u4e24\u6b21\u6559\u8bad\u4e4b\u540e\uff0c\u59ec\u6653\u98ce\u7b2c\u4e00\u6b21\u662f \u4ed7\u7740\u8f7b\u529f\u9003\u8131\uff0c\u7b2c\u4e8c\u6b21\u662f\u5f97\u91d1\u4e16\u9057\u6697\u4e2d\u76f8\u52a9\uff0c\u4f46\u5728\u7d27\u8981\u5173\u5934\uff0c\u4e5f\u66fe\u7528\u8fc7\u5929\u7f57\u6b65\u6cd5\u89e3\u5371\u3002 \u4e24\u5144\u5f1f\u82e6\u7ec3\u4e86\u4e00\u5957\u9634\u9633\u516b\u5366\u638c\uff0c\u9047\u654c\u65f6\u4e00\u4e1c\u4e00\u897f\uff0c\u638c\u529b\u4e00\u521a\u4e00\u67d4\uff0c\u4e92\u76f8\u914d\u5408\uff0c\u4efb\u654c\u4eba\u8f7b \u529f\u5982\u4f55\u9ad8\u660e\uff0c\u4e5f\u51b3\u96be\u9003\u51fa\u4ed6\u4eec\u7684\u638c\u529b\u8303\u56f4\u4e4b\u5916\u3002 \u3000\u3000\u59ec\u6653\u98ce\u53d1\u89c9\u4e00\u80a1\u5927\u529b\u81ea\u524d\u9762\u649e\u6765\uff0c\u53ea\u597d\u4e5f\u53d1\u51fa\u638c\u529b\u53cd\u51fb\uff0c\u4ed6\u65b0\u8fd1\u7ec3\u6210\u91d1\u521a\u638c\u529b\uff0c\u867d \u7136\u4e0d\u5982\u5927\u60b2\u7985\u5e08\u7684\u529f\u529b\u5353\u7edd\uff0c\u5374\u4e5f\u4e0d\u5728\u5409\u7f57\u906e\u4e4b\u4e0b\uff0c\u54ea\u77e5\u4e00\u638c\u53d1\u51fa\uff0c\u524d\u9762\u7684\u90a3\u80a1\u529b\u9053\u7a81 \u7136\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\uff0c\u4f46\u81ea\u5df1\u8fd9\u80a1\u638c\u529b\u5374\u4e5f\u653b\u4e0d\u8fc7\u53bb\uff1b\u4f3c\u662f\u88ab\u8f7b\u8f6f\u7684\u4e00\u5c42\u68c9\u7d6e\u88f9\u4f4f\uff0c\u6025\u5207 \u95f4\u7adf\u7136\u64a4\u4e0d\u56de\u6765\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7afa\u6cd5\u4f11\u7eed\u53d1\u4e00\u638c\uff0c\u52b2\u98ce\u547c\u547c\uff0c\u5df2\u88ad\u5230\u4e86\u59ec\u6653\u98ce\u7684\u540e\u5fc3\u3002\u539f\u6765\u4ed6\u4eec \u4e24\u5144\u5f1f\u7684\u638c\u529b\u53ef\u4ee5\u521a\u67d4\u4e92\u6613\uff0c\u968f\u5fc3\u53d8\u6362\uff0c\u53ea\u8981\u5176\u4e2d\u6709\u4e00\u4eba\u7528\u67d4\u52b2\u201c\u7c98\u201d\u4e0a\u4e86\u654c\u4eba\u3002\u53e6\u4e00 \u4eba\u5c31\u53ef\u53d1\u52a8\u731b\u70c8\u7684\u653b\u51fb\u3002 \u3000\u3000\u5e78\u800c\u59ec\u6653\u98ce\u5df2\u7ec3\u6210\u4e86\u62a4\u4f53\u795e\u529f\uff0c\u4f46\u540e\u5fc3\u88ab\u7afa\u6cd5\u4f11\u7684\u638c\u529b\u4e00\u649e\uff0c\u4e5f\u89c9\u9690\u9690\u4f5c\u75db\uff0c\u4e0d\u89c9 \u5fc3\u4e2d\u607c\u6012\uff0c\u60f3\u9053\uff1a\u201c\u7eb5\u7136\u6211\u6709\u4e0d\u662f\u3002\u4f60\u4eec\u4e5f\u4e0d\u8be5\u4e0b\u6b64\u8fa3\u624b\u3002\u7adf\u7136\u60f3\u8981\u6211\u8fd9\u6761\u8001\u547d\uff01\u4f55\u51b5 \u6211\u5f53\u5e74\u4e5f\u662f\u4e3a\u52bf\u6240\u8feb\uff0c\u5e76\u975e\u60f3\u72ec\u81ea\u541e\u6ca1\u8d43\u7269\u3002\u201d \u3000\u3000\u4e00\u6012\u4e4b\u4e0b\uff0c\u59ec\u6653\u98ce\u4e0d\u987e\u8017\u635f\u5143\u6c14\uff0c\u4e5f\u4f7f\u51fa\u4e86\u4e24\u79cd\u4e0d\u540c\u7684\u638c\u529b\uff0c\u53f3\u638c\u5411\u524d\u4e00\u62cd\uff0c\u4f7f\u7684 \u662f\u7b2c\u516b\u91cd\u7684\u4fee\u7f57\u9634\u715e\u529f\uff0c\u5de6\u638c\u53cd\u624b\u5411\u540e\u62cd\u51fa\uff0c\u7528\u7684\u5374\u662f\u5927\u4e58\u822c\u82e5\u638c\u529b\uff0c\u8fd9\u4e24\u6837\u6b66\u529f\u90fd\u662f \u4e54\u5317\u6e9f\u79d8\u7c4d\u4e0a\u7684\u4e00\u7b49\u4e00\u7684\u529f\u592b\uff0c\u59ec\u6653\u98ce\u7ec3\u4e60\u6709\u7d20\uff0c\u6bd4\u8d77\u4ed6\u521a\u624d\u6240\u7528\u7684\u65b0\u7ec3\u6210\u7684\u91d1\u521a\u638c\uff0c \u5a01\u529b\u5f3a\u5f31\uff0c\u81ea\u662f\u4e0d\u53ef\u540c\u65e5\u800c\u8bed\u3002\u7afa\u6cd5\u5170\u767b\u65f6\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u7afa\u6cd5\u4f11\u7684\u521a\u731b\u638c\u529b\u4e5f\u88ab\u4ed6\u8ffd \u9000\u3002 \u3000\u3000\u7afa\u6cd5\u5170\u4ece\u524d\u4e5f\u9886\u6559\u8fc7\u4ed6\u7684\u4fee\u7f57\u9634\u715e\u529f\uff0c\u5f53\u65f6\u59ec\u6653\u98ce\u53ea\u7ec3\u5230\u7b2c\u4e03\u91cd\uff0c\u8fdc\u4e0d\u5982\u73b0\u5728\u7684\u5389 \u5bb3\uff0c\u4fee\u7f57\u9634\u715e\u529f\u662f\u7ec3\u5230\u4e86\u7b2c\u4e03\u91cd\u4e4b\u540e\uff0c\u4fbf\u6709\u8d70\u706b\u4eba\u9b54\u7684\u5371\u9669\uff0c\u82e5\u975e\u5df1\u5f97\u6b63\u5b97\u5185\u529f\u5fc3\u6cd5\uff0c \u6216\u6709\u7075\u836f\u76f8\u8f85\uff0c\u4fbf\u96be\u518d\u7ec3\u4e0b\u53bb\uff1b\u4f46\u5018\u82e5\u8fc7\u4e86\u8fd9\u4e00\u5173\uff0c\u6bcf\u591a\u4e00\u91cd\u8fdb\u5c55\uff0c\u529f\u529b\u4fbf\u9661\u589e\u4e00\u500d\u3002 \u3000\u3000\u7afa\u6cd5\u5170\u4ee5\u524d\u4e0e\u59ec\u6653\u98ce\u8f83\u91cf\uff0c\u4e24\u6b21\u90fd\u662f\u4ed6\u5360\u4e86\u7edd\u5bf9\u4e0a\u98ce\uff0c\u8fd9\u6b21\u5374\u611f\u5230\u808c\u80a4\u8d77\u6817\uff0c\u904d\u4f53 \u751f\u5bd2\uff0c\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u51e0\u5e74\u4e0b\u89c1\uff0c\u8fd9\u8001\u8d3c\u7684\u529f\u529b\u7adf\u7cbe\u8fdb\u5982\u65af\uff0c\u771f\u662f\u5947\u602a\uff01\u201d \u3000\u3000\u539f\u6765\u59ec\u6653\u98ce\u81ea\u90a3\u5e74\u5f97\u91d1\u4e16\u9057\u6307\u70b9\u4e4b\u540e\uff0c\u5c06\u5404\u5bb6\u5404\u6d3e\u7684\u6b66\u5b66\u51b6\u4e8e\u4e00\u7089\uff0c\u65e9\u5df2\u7ec3\u6210\u4e86\u6b63 \u90aa\u5408\u4e00\u7684\u5185\u529f\uff0c\u81ea\u662f\u4eca\u975e\u6614\u6bd4\u4e86\u3002\u53e6\u4e00\u8fb9\u7afa\u6cd5\u4f11\u63a5\u4ed6\u7684\u5927\u4e58\u822c\u82e5\u638c\u529b\uff0c\u5374\u8f83\u4ed6\u7684\u54e5\u54e5\u8981 \u597d\u4e00\u4e9b\uff0c\u56e0\u4e3a\u8fd0\u7528\u4fee\u7f57\u9634\u715e\u529f\u9887\u4f24\u5143\u6c14\uff0c\u5927\u4e58\u822c\u82e5\u638c\u529b\u4fbf\u4e0d\u80fd\u5c3d\u91cf\u53d1\u6325\uff0c\u7afa\u6cd5\u4f11\u5168\u529b\u652f \u6491\uff0c\u582a\u582a\u62b5\u6321\u5f97\u4f4f\u3002 \u3000\u3000\u59ec\u6653\u98ce\u89c1\u7afa\u6cd5\u5170\u7259\u5173\u6253\u6218\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ed6\u867d\u7136\u4e0b\u624b\u65e0\u60c5\uff0c\u4f46\u5f53\u5e74\u4e4b\u4e8b\uff0c\u6211\u5230\u5e95\u4e5f \u662f\u6709\u70b9\u5bf9\u4ed6\u4e0d\u4f4f\u3002\u201d\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4fee\u7f57\u9634\u715e\u529f\u64a4\u56de\u4e86\u4e24\u5206\uff0c\u4e0d\u6599\u7afa\u6cd5\u5170\u7684\u638c\u529b\u4e58\u673a\u4fbf\u88ad\u8fc7 \u6765\uff0c\u4e00\u56e2\u67d4\u52b2\uff0c\u5c06\u59ec\u6653\u98ce\u7684\u9634\u715e\u638c\u529b\u88f9\u4f4f\uff0c\u767b\u65f6\u201c\u80f6\u201d\u5728\u4e00\u8d77\uff0c\u53d8\u6210\u4e86\u53cc\u65b9\u6697\u6597\u5185\u529f\uff0c \u8c01\u4e5f\u4e0d\u80fd\u6536\u52b2\u3002 \u3000\u3000\u7afa\u6c0f\u5144\u5f1f\u5728\u5370\u5ea6\u8fbe\u5958\u7956\u5e08\u6240\u4f20\u7684\u90a3\u4e00\u652f\u6d3e\u4e4b\u4e2d\uff0c\u662f\u6709\u6570\u7684\u9ad8\u624b\uff0c\u8f88\u4efd\u6b66\u529f\u90fd\u5728\u5409\u7f57 \u906e\u4e4b\u4e0a\uff0c\u8fd9\u65f6\u4e24\u5144\u5f1f\u8054\u624b\u5408\u6597\uff0c\u8981\u80dc\u59ec\u6653\u98ce\u56fa\u7136\u4e0d\u6613\uff0c\u59ec\u6653\u98ce\u60f3\u8981\u6446\u8131\u4ed6\u4eec\u7684\u638c\u529b\u5374\u4e5f \u4e0d\u80fd\uff0c\u53cc\u65b9\u6210\u4e86\u4e2a\u9a91\u864e\u96be\u4e0b\u7684\u5c40\u9762\uff0c\u4e0d\u7531\u5f97\u90fd\u6697\u6697\u53eb\u82e6\u3002 \u3000\u3000\u773c\u770b\u5c31\u8981\u4e24\u8d25\u4ff1\u4f24\uff0c\u5927\u60b2\u7985\u5e08\u5ffd\u5730\u8d70\u51fa\u6765\u9053\uff1a\u201c\u4e3b\u4eba\u8bf4\u8fc7\uff0c\u4eca\u65e5\u662f\u4ee5\u6b66\u4f1a\u53cb\uff0c\u65e0\u987b \u5206\u4e2a\u5f3a\u5b58\u5f31\u4ea1\uff0c\u6211\u770b\u8fd9\u4e00\u573a\u5c31\u7b97\u4f5c\u6253\u5e73\u4e86\u5427\u3002\u201d\u573a\u4e2d\u7684\u88c1\u5224\u70b9\u4e86\u70b9\u5934\uff0c\u8868\u793a\u540c\u610f\uff0c\u4f46\u5374 \u4e0d\u6562\u4e0a\u524d\u5c06\u4ed6\u4eec\u5206\u5f00\u3002 \u3000\u3000\u5927\u60b2\u7985\u5e08\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u811a\u6b65\u4e0d\u505c\uff0c\u53ea\u89c1\u4ed6\u8d70\u5230\u4e00\u4e2a\u4f4d\u7f6e\uff0c\u8fd9\u4e2a\u4f4d\u7f6e\u6070\u597d\u4e0e\u4e09\u4e2a\u4eba\u7684\u8ddd \u79bb\u76f8\u7b49\uff0c\u53cc\u8896\u4e8c\u6325\uff0c\u968f\u5373\u542c\u5f97\u4e00\u9635\u201c\u55e4\u55e4\u201d\u7684\u6fc0\u52a8\u6c14\u6d41\u7684\u58f0\u54cd\uff0c\u7afa\u6c0f\u5144\u5f1f\u548c\u59ec\u6653\u98ce\u90fd\u5411 \u65c1\u8fb9\u8dc3\u51fa\u4e00\u6b65\uff0c\u539f\u6765\u5927\u60b2\u7985\u5e08\u8fd9\u53cc\u8896\u4e00\u6325\uff0c\u6070\u5230\u597d\u5904\uff0c\u5c06\u4e24\u8fb9\u7684\u638c\u529b\u9694\u65ad\u3002 \u3000\u3000\u7afa\u6c0f\u5144\u5f1f\u72b9\u81ea\u6124\u6124\u4e0d\u5e73\uff0c\u5927\u60b2\u7985\u5e08\u5408\u4ec0\u5f53\u80f8\uff0c\u5ffd\u5730\u5411\u7afa\u6cd5\u5170\u65bd\u4e86\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u4eca \u65e5\u91cd\u9022\u6545\u53cb\uff0c\u6b23\u6170\u4f55\u5982\u3002\u5bb6\u5e08\u75db\u7985\u4e0a\u4eba\u6709\u70b9\u793c\u7269\uff0c\u5631\u6258\u8d2b\u618e\u9001\u7ed9\u8d24\u6606\u4ef2\uff0c\u4ee5\u89e3\u6614\u65e5\u4e4b\u5acc\uff0c \u4e5f\u662f\u7269\u5f52\u539f\u4e3b\u4e4b\u610f\uff0c\u8fd8\u8bf7\u4e24\u4f4d\u6536\u4e0b\u3002\u201d \u3000\u3000\u7afa\u6c0f\u5144\u5f1f\u542c\u5f97\u201c\u7269\u5f52\u539f\u4e3b\u201d\u8fd9\u56db\u5b57\uff0c\u5fc3\u5934\u90fd\u662f\u535c\u901a\u4e00\u8df3\uff0c\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c \u53ea\u89c1\u5927\u60b2\u63b8\u5e08\u5df2\u53d6\u51fa\u4e00\u4e2a\u9ec4\u5e03\u5305\u88b1\uff0c\u4e0a\u9762\u5199\u7740\u4e09\u5377\u7ecf\u540d\uff0c\u6b63\u662f\u59ec\u6653\u98ce\u5f53\u5e74\u4ece\u5c11\u6797\u5bfa\u76d7\u53bb\uff0c \u800c\u5170\u6c0f\u5144\u5f1f\u68a6\u5bd0\u4ee5\u6c42\u7684\u90a3\u4e09\u5377\u8fbe\u6469\u9057\u4e66\u3002\u4ee5\u5927\u60b2\u7985\u5e08\u7684\u8eab\u4efd\uff0c\u4ed6\u4eec\u5f53\u7136\u7528\u4e0d\u7740\u6253\u5f00\u5305\u88b1 \u770b\u4e2a\u771f\u5047\u4e86\u3002 \u3000\u3000\u7afa\u6cd5\u5170\u559c\u51fa\u671b\u5916\uff0c\u5c06\u90a3\u4e09\u5377\u7ecf\u4e66\u6536\u4e0b\uff0c\u8fde\u8fde\u9053\u8c22\uff0c\u5927\u60b2\u7985\u5e08\u9053\uff1a\u201c\u54b1\u4eec\u7ea2\u82b1\u7eff\u53f6\uff0c \u672c\u662f\u4e00\u5bb6\uff1a\u81ea\u5df1\u4eba\u4f55\u987b\u5ba2\u6c14\u3002\u8981\u8c22\u4e5f\u53ea\u80fd\u8c22\u59ec\u65bd\u4e3b\u3002\u201d\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u5927\u5e08\u4f60\u6316\u82e6\u6211\u4e86\u3002 \u4e0d\u9519\uff0c\u6211\u66fe\u2018\u501f\u9605\u2019\u8fc7\u8d35\u5bfa\u8fd9\u4e09\u5377\u7ecf\u4e66\u3002\u4f46\u65e9\u5df2\u5f52\u8fd8\uff0c\u8fd9\u5c31\u4e0e\u6211\u65e0\u5173\u4e86\u3002\u4f60\u6177\u6168\u9001\u793c\uff0c \u6211\u53ef\u4e0d\u6562\u6cbe\u5149\u3002\u201d \u3000\u3000\u5927\u60b2\u7985\u5e08\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u4f60\u5728\u4e66\u4e2d\u6dfb\u52a0\u7684\u6ce8\u91ca\uff0c\u5bf9\u8fd9\u51e0\u95e8\u6b66\u5b66\u5927\u6709\u53d1\u6325\uff0c\u5bb6\u5e08\u8bf4\u4f60\u4e0d \u4f46\u8fd8\u672c\uff0c\u800c\u4e14\u5fd6\u606f\uff0c\u7b97\u8d77\u6765\u8fd8\u662f\u6211\u4eec\u6cbe\u4e86\u4f60\u7684\u60c5\u3002\u4ed6\u77e5\u9053\u4f60\u548c\u4e24\u4f4d\u7afa\u5e08\u5144\u56e0\u4e86\u6b64\u4e8b\u5931\u548c\uff0c \u5f88\u89c9\u8fc7\u610f\u4e0d\u53bb\u3002\u8fd9\u6b21\u9001\u793c\uff0c\u53e6\u4e00\u4e2a\u539f\u56e0\uff0c\u5c31\u662f\u60f3\u4e3a\u59ec\u65bd\u4e3b\u89e3\u5f00\u8fd9\u70b9\u5c0f\u5c0f\u7684\u8fc7\u8282\uff0c\u4ee5\u62a5\u59ec \u65bd\u4e3b\u4e4b\u60c5\u3002\u201d \u3000\u3000\u7afa\u6c0f\u5144\u5f1f\u5f97\u4e86\u7ecf\u4e66\uff0c\u6ee1\u6000\u559c\u60a6\uff0c\u5bf9\u59ec\u6653\u98ce\u7684\u4ec7\u6028\u4e5f\u65e9\u5df2\u70df\u6d88\u4e91\u6563\u4e86\u3002\u4e24\u5144\u5f1f\u9f50\u58f0\u7b11 \u9053\uff1a\u201c\u8bf4\u5f97\u4e0d\u9519\uff0c\u8981\u4e0d\u662f\u59ec\u65bd\u4e3b\u5c06\u7ecf\u4e66\u5f52\u8fd8\u8d35\u5bfa\uff0c\u4eca\u65e5\u4e5f\u5c31\u6ca1\u6709\u8fd9\u4efd\u793c\u7269\u4e86\uff0c\u662f\u8be5\u591a\u8c22 \u59ec\u65bd\u4e3b\u3002\u201d\u59ec\u6653\u98ce\u54c8\u54c8\u5927\u7b11\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u6211\u505a\u5077\u513f\u5012\u4e5f\u505a\u5f97\u4e0d\u574f\u5440\uff01\u201d\u5929\u7afa\u4e8c\u50e7\u548c\u4ed6\u7684 \u5341\u5348\u7ea0\u7eb7\uff0c\u5c31\u5728\u5f7c\u6b64\u7684\u7b11\u58f0\u4e2d\u7ed3\u675f\u3002 \u3000\u3000\u5927\u60b2\u7985\u5e08\u3001\u5929\u7afa\u4e8c\u50e7\u5404\u81ea\u5f52\u5ea7\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u91d1\u9e70\u5bab\u7684\u62a4\u6cd5\u5927\u5f1f\u5b50\u5e26\u9886\u4e09\u4e2a\u5a46\u7f57\u95e8\u6559 \u50e7\u4fa3\u548c\u4e00\u4e2a\u78a7\u773c\u9ec4\u987b\u7684\u6c49\u5b50\u8fdb\u6765\uff0c\u8fd9\u56db\u4e2a\u4eba\u88ab\u59ec\u6653\u98ce\u5077\u4e86\u8bf7\u67ec\uff0c\u672a\u5165\u4f1a\u573a\uff0c\u5148\u5931\u9762\u5b50\uff0c \u6ee1\u809a\u76ae\u90fd\u662f\u6012\u6c14\u3002 \u3000\u3000\u90a3\u4e09\u4e2a\u5a46\u7f57\u95e8\u6559\u50e7\u4fa3\u5411\u59ec\u6653\u98ce\u6012\u76ee\u800c\u89c6\uff0c\u4f46\u4ed6\u4eec\u77e5\u9053\u6bd4\u6b66\u7684\u89c4\u77e9\uff0c\u59ec\u6653\u98ce\u5df2\u6bd4\u4e86\u4e00 \u573a\uff0c\u4ed6\u4eec\u90fd\u662f\u5927\u6709\u6765\u5934\u7684\u4eba\u7269\u3002\u81ea\u662f\u4e0d\u4fbf\u6709\u5931\u8eab\u4efd\uff0c\u4e0a\u524d\u6311\u6218\uff1b\u90a3\u78a7\u773c\u9ec4\u987b\u7684\u6c49\u5b50\u5374\u4e0d \u7406\u4f1a\u4ec0\u4e48\u89c4\u77e9\u4e0d\u89c4\u77e9\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u5c31\u5230\u4e86\u59ec\u6653\u98ce\u9762\u524d\uff0c\u53fd\u53fd\u5495\u5495\u4ed6\u8bf4\u4e86\u51e0\u53e5\u5370\u5ea6\u8bdd\u3002 \u3000\u3000\u59ec\u6653\u98ce\u89c1\u4ed6\u8eab\u624b\u4e0d\u51e1\uff0c\u6697\u6697\u559d\u5f69\uff0c\u5fc3\u60f3\uff1a\u201c\u53ef\u60dc\u4ed6\u662f\u4e2a\u5370\u5ea6\u4eba\uff0c\u8981\u4e0d\u7136\u5012\u53ef\u4ee5\u505a\u6211 \u7684\u52a9\u624b\u3002\u201d\u95ee\u9053\uff1a\u201c\u4ed6\u8bf4\u4ec0\u4e48\uff1f\u201d\u90a3\u62a4\u6cd5\u5927\u5f1f\u5b50\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u655d\u56fd\u7b2c\u4e00\u795e\u5077\uff0c\u4ed6\u5f88\u4f69\u670d \u59ec\u5148\u751f\u7684\u529f\u592b\u3002\u60f3\u4e0e\u59ec\u5148\u751f\u4eb2\u8fd1\u4eb2\u8fd1\uff01\u201d\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u4efd\u5c5e\u540c\u884c\uff0c\u7406\u8be5 \u4eb2\u8fd1\u4eb2\u8fd1\u3002\u201d \u3000\u3000\u4e24\u4eba\u4f38\u624b\u4e00\u63e1\uff0c\u5ffd\u542c\u5f97\u90a3\u5370\u5ea6\u795e\u6109\u201c\u54ce\u54df\u201d\u4e00\u58f0\u5f2f\u4e0b\u4e86\u8170\uff0c\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u4f60\u6478\u9519 \u5730\u65b9\u4e86\uff0c\u6211\u7684\u94b1\u94f6\u653e\u5728\u90a3\u4e00\u8fb9\u888b\u5b50\u3002\u201d\u4f17\u4eba\u770b\u65f6\uff0c\u53ea\u89c1\u90a3\u5370\u5ea6\u795e\u5077\u7684\u4e00\u53ea\u5de6\u624b\u5df2\u4f38\u5165\u59ec \u6653\u98ce\u6000\u4e2d\uff0c\u53ea\u9732\u51fa\u534a\u622a\u624b\u81c2\uff0c\u5374\u4f3c\u88ab\u4ec0\u4e48\u4e1c\u897f\u5939\u7740\uff0c\u62d4\u4e0d\u51fa\u6765\uff0c\u59ec\u6653\u98ce\u53f3\u624b\u4ecd\u7136\u4e0e\u4ed6\u76f8 \u63e1\uff0c\u5de6\u624b\u5219\u8d34\u5728\u81ea\u5df1\u7684\u819d\u8fb9\uff0c\u5e76\u6ca1\u6709\u6349\u7740\u4ed6\u3002\u4f17\u4eba\u4e0d\u89e3\u4ed6\u7684\u624b\u4f55\u4ee5\u62d4\u4e0d\u51fa\u6765\uff0c\u4f46\u89c1\u4ed6\u5f62 \u72b6\u6ed1\u7a3d\uff0c\u90fd\u4e0d\u89c9\u597d\u7b11\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5370\u5ea6\u795e\u5077\u5fc3\u6000\u4e0d\u5fff\uff0c\u6709\u610f\u8f83\u91cf\u59ec\u6653\u98ce\u7684\u529f\u592b\uff0c\u8d81\u7740\u4e0e\u4ed6\u63e1\u624b\u7684\u5f53\u513f\uff0c\u53e6\u4e00\u53ea \u624b\u5c31\u6478\u4ed6\u7684\u8d34\u8eab\u8863\u888b\uff0c\u5374\u4e0d\u6599\u88ab\u59ec\u6653\u98ce\u4f7f\u51fa\u4e0a\u4e58\u5185\u529f\uff0c\u8179\u808c\u4e00\u6536\uff0c\u5c06\u4ed6\u7684\u624b\u5438\u4f4f\u3002 \u3000\u3000\u90a3\u5370\u5ea6\u795e\u6109\u6ee1\u9762\u901a\u7ea2\uff0c\u4f4e\u58f0\u8bf4\u4e86\u4e00\u53e5\u8bdd\uff0c\u65c1\u8fb9\u6709\u4e2a\u901a\u8bd1\u662f\u897f\u85cf\u4eba\uff0c\u7acb\u5373\u5927\u58f0\u8bf4\u9053\uff1a \u201c\u4ed6\u8bf4\u59ec\u5148\u751f\u672c\u9886\u9ad8\u660e\uff0c\u80dc\u4ed6\u5341\u500d\uff0c\u4e0d\u80dc\u4f69\u670d\uff01\u201d \u3000\u3000\u59ec\u6653\u98ce\u8d62\u8db3\u9762\u5b50\uff0c\u4e5f\u4fbf\u4e0d\u4e3a\u5df2\u751a\uff0c\u5f53\u4e0b\u54c8\u54c8\u4e00\u7b11\uff0c\u809a\u76ae\u4e00\u633a\uff0c\u5f39\u5f00\u4e86\u5370\u5ea6\u795e\u6109\u7684\u90a3 \u53ea\u5de6\u624b\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u7684\u672c\u9886\u4e5f\u5f88\u4e0d\u9519\u4e86\uff0c\u6211\u548c\u4f60\u4ea4\u4e2a\u670b\u53cb\u3002\u8fd9\u4e9b\u4e1c\u897f\u8fd8\u7ed9\u4f60\u5427\uff01\u201d\u8863\u8896 \u4e00\u6296\uff0c\u54d7\u5566\u5566\u4e00\u58f0\u54cd\uff0c\u8896\u7ba1\u4e2d\u8dcc\u4e0b\u51e0\u4ef6\u4e1c\u897f\uff0c\u6709\u5c0f\u5200\u3001\u5c0f\u952f\u3001\u5c0f\u632b\u3001\u5c0f\u94bb\u7b49\u7b49\uff0c\u8fd8\u6709\u51e0 \u679a\u94f6\u5e02\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e9b\u7269\u4ef6\u90fd\u662f\u59ec\u6653\u98ce\u4ece\u90a3\u5370\u5ea6\u795e\u5077\u8eab\u4e0a\u5077\u8fc7\u6765\u7684\uff0c\u90a3\u51e0\u67c4\u5200\u3001\u952f\u3001\u632b\u3001\u94bb\u6b63\u662f \u5370\u5ea6\u795e\u5077\u7684\u968f\u8eab\u516b\u5b9d\u3002\u59ec\u6653\u98ce\u5077\u4e86\u4ed6\u8fd9\u4e48\u591a\u4e1c\u897f\uff0c\u4ed6\u7adf\u7136\u6beb\u65e0\u77e5\u89c9\u3002\u8fd9\u4e00\u4e0b\u5f04\u5f97\u90a3\u5370\u5ea6 \u795e\u5077\u76ee\u77aa\u53e3\u5446\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u9b54\u672f\u3001\u9b54\u672f\u3001\u771f\u662f\u9b54\u672f\uff01\u201d \u3000\u3000\u8fd9\u5370\u5ea6\u795e\u5077\u5bf9\u59ec\u6653\u98ce\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\uff0c\u90a3\u4e09\u4e2a\u5a46\u7f57\u95e8\u6559\u4fe1\u4fa3\u5bf9\u59ec\u6653\u98ce\u7684\u5185\u529f\u9020\u8be3\u4e5f \u5f88\u9707\u60ca\uff0c\u5fc3\u4e2d\u5747\u662f\u60f3\u9053\uff1a\u201c\u539f\u6765\u6b64\u4eba\u8fd8\u4e0d\u5355\u662f\u5077\u7a83\u7684\u672c\u4e8b\u9ad8\u660e\uff0c\u6211\u4eec\u82e5\u662f\u5355\u6253\u72ec\u6597\uff0c\u8fd8 \u4e0d\u4e00\u5b9a\u662f\u4ed6\u7684\u5bf9\u624b\u5462\u3002\u201d\u5f53\u4e0b\u4e5f\u5c31\u4e0d\u6562\u591a\u4e8b\u4e86\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u9635\u9f13\u4e50\u58f0\uff0c\u91d1\u9e70\u5bab\u7684\u62a4\u6cd5\u5927\u5f1f\u5b50\u53c8\u63a5\u4e86\u4e24\u4f4d\u5ba2\u4eba\u8fdb\u6765\uff0c\u8981\u594f\u4e50\u8fce\u63a5\u7684\u5f53\u7136 \u4e0d\u662f\u7b49\u95f2\u4eba\u7269\uff0c\u4f17\u4eba\u5b9a\u775b\u770b\u65f6\uff0c\u53ea\u89c1\u8fdb\u6765\u7684\u662f\u4e00\u7537\u4e00\u5973\uff0c\u7537\u7684\u662f\u6b66\u5f53\u6d3e\u638c\u95e8\u96f7\u9707\u5b50\uff0c\u5973 \u7684\u662f\u9099\u5c71\u6d3e\u638c\u95e8\u8c37\u4e4b\u534e\u3002\u5b9d\u8c61\u6cd5\u5e08\u719f\u6089\u4e2d\u56fd\u6b66\u6797\u7684\u60c5\u51b5\uff0c\u6b66\u5f53\u3001\u5c11\u6797\u3001\u9099\u5c71\u3001\u5ce8\u5d4b\u662f\u4e2d \u539f\u56db\u5927\u95e8\u6d3e\uff0c\u5982\u4eca\u6b66\u5f53\u3001\u9099\u5c71\u4e24\u5927\u6d3e\u7684\u638c\u95e8\u4eba\u8054\u8882\u800c\u6765\uff0c\u5f53\u7136\u662f\u8981\u594f\u4e50\u76f8\u8fce\u4e86\u3002 \u3000\u3000\u6c5f\u5357\u5f97\u89c1\u6545\u4eba\uff0c\u5f88\u662f\u6b22\u559c\uff0c\u4f46\u4e5f\u6709\u70b9\u5931\u671b\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u600e\u7684\u4e0d\u89c1\u91d1\u5927\u4fa0\u5462\uff1f\u4ed6\u662f \u5e94\u8be5\u548c\u8c37\u4e4b\u534e\u4e00\u540c\u6765\u7684\u3002\u201d \u3000\u3000\u4e50\u58f0\u672a\u6b62\uff0c\u573a\u4e2d\u5df2\u5f15\u8d77\u4e00\u9635\u9a9a\u52a8\u3002\u539f\u6765\u8c37\u4e4b\u534e\u4e09\u6708\u4e4b\u524d\u7b2c\u4e00\u6b21\u8e0f\u4eba\u9a6c\u8428\u513f\u56fd\u5883\u65f6\uff0c \u66fe\u906d\u9047\u516b\u540d\u6b66\u58eb\u548c\u4e24\u4e2a\u50e7\u4eba\u7684\u88ad\u51fb\uff0c\u5979\u7684\u5f92\u5f1f\u8c37\u4e2d\u83b2\u5c31\u662f\u90a3\u6b21\u88ab\u63b3\u53bb\u7684\u3002\u8fd9\u516b\u540d\u6b66\u58eb\u548c \u90a3\u4e24\u4e2a\u50e7\u4eba\u4eca\u65e5\u4e5f\u5728\u4f1a\u573a\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u773c\u5149\u5411\u90a3\u4e9b\u4eba\u626b\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u5e78\u4f1a\uff0c\u5e78\u4f1a\u3002\u96be\u5f97\u4f60\u4eec\u4e5f\u90fd\u5728\u573a\u3002\u201d\u5b9d\u8c61\u6cd5\u5e08 \u65bd\u793c\u9053\uff1a\u201c\u8c37\u5973\u4fa0\u4f11\u8981\u89c1\u602a\u3002\u4ed6\u4eec\u5f53\u65e5\u662f\u5949\u4e86\u738b\u547d\u800c\u4e3a\uff0c\u4ee4\u5f92\u5982\u4eca\u4e5f\u6b63\u53d7\u56fd\u4e3b\u4f18\u5f85\uff0c\u8bf7 \u4f60\u653e\u5fc3\u3002\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4f60\u4eca\u65e5\u4e4b\u4f1a\u662f\u4ee5\u6b66\u4f1a\u53cb\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8c37\u5973\u4fa0\u6709\u4f55\u6307\u6559\uff1f\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u6211\u60f3\u8bf7\u8fd9\u5341\u4f4d\u9ad8\u624b\u4e00\u9f50\u4e0b \u573a\uff0c\u8ba9\u6211\u518d\u9886\u6559\u9886\u6559\u4ed6\u4eec\u7684\u529f\u592b\u3002\u201d\u5b9d\u8c61\u6cd5\u5e08\u5fc3\u4e2d\u4e0d\u60a6\uff0c\u8bf4\u9053\uff1a\u201c\u4ee5\u6b66\u4f1a\u53cb\uff0c\u603b\u662f\u4ee5\u5355 \u6253\u72ec\u6597\u4e3a\u5b9c\u2026\u2026\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u4e03\u9634\u6559\u7684\u9634\u5723\u59d1\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u8c37\u638c\u95e8\u662f\u5973\u4e2d\u82f1\u4fa0\uff0c\u6211\u8001\u3001 \u5a46\u5b50\u5411\u4f60\u9886\u6559\u5982\u4f55\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u672a\u66fe\u56de\u7b54\uff0c\u5ffd\u5730\u6709\u4e00\u4e2a\u4eba\u602a\u58f0\u602a\u6c14\u4ed6\u8bf4\u9053\uff1a\u201c\u597d\u67f4\u4e0d\u70e7\u70c2\u7076\uff0c\u51c0\u811a\u4e0d\u8e29\u70c2\u6ce5\uff0c \u4f60\u8fd9\u79cd\u4e0b\u4e09\u95e8\u7684\u8001\u5996\u5a46\uff0c\u53ea\u914d\u548c\u4ffa\u81ed\u53eb\u5316\u6597\u6597\u3002\u8c37\u5973\u4fa0\uff0c\u4f60\u4e0d\u8981\u7406\u5979\uff0c\u4e0b\u4e00\u573a\u5f85\u4ffa\u81ed\u53eb \u5316\u672a\u9886\u6559\u5979\u7684\u90a3\u53cc\u6bd2\u722a\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u7684\u662f\u5317\u4e10\u5e2e\u7684\u5e2e\u4e3b\u4ef2\u957f\u7edf\uff0c\u9634\u5723\u59d1\u4ee5\u524d\u66fe\u5403\u8fc7\u4ed6\u4e00\u70b9\u5c0f\u4e8f\uff0c\u5982\u4eca\u53c8\u89c1\u4ed6\u51fa\u5934\u5e72 \u9884\uff0c\u4e0d\u7981\u5fc3\u5934\u706b\u8d77\uff0c\u7acb\u5373\u8bf4\u9053\uff1a\u201c\u597d\u5440\uff0c\u81ed\u53eb\u5316\uff0c\u4ffa\u8001\u5a46\u5b50\u6b63\u8981\u627e\u9645\u7b97\u5e10\u3002\u4f60\u51fa\u6765\u5427\uff0c \u73b0\u5728\u4ea4\u624b\u4e5f\u884c\u3002\u201d\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u4f60\u6025\u4ec0\u4e48\uff1f\u4ffa\u5316\u5b50\u5f20\u5f00\u5e03\u888b\uff0c\u7b49\u4f60\u65bd\u820d\u4fbf\u662f\u3002\u4f60\u51c6\u5907 \u94b1\u7269\u5427\u3002\u201d\u610f\u601d\u4ecd\u662f\u575a\u6301\u8981\u4e0b\u4e00\u573a\u624d\u548c\u5979\u4ea4\u624b\uff0c\u800c\u4e14\u53e3\u6c14\u5145\u6ee1\u8ba5\u523a\uff0c\u6697\u793a\u9634\u5723\u59d1\u5b9a\u8981\u5403 \u4e8f\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u524d\u8f88\u4e0d\u5fc5\u6597\u53e3\uff0c\u53cd\u6b63\u591a\u7b49\u4e00\u573a\uff0c\u4e5f\u65e0\u987b\u591a\u5c11\u65f6\u5019\u3002\u201d\u84e6\u5730\u9762\u8272 \u4e00\u7aef\uff0c\u5bf9\u5b9d\u8c61\u6cd5\u5e08\u9053\uff1a\u201c\u6211\u66fe\u906d\u53d7\u8fd9\u5341\u4f4d\u9ad8\u624b\u56f4\u653b\uff0c\u4ed6\u4eec\u5f53\u65f6\u4e0d\u6015\u88ab\u4eba\u803b\u7b11\u662f\u4ee5\u591a\u6b3a\u5c11\uff0c \u96be\u9053\u73b0\u5728\u53cd\u800c\u6015\u4e86\uff1f\u597d\u5427\uff0c\u4ed6\u4eec\u5018\u82e5\u60c5\u865a\u80c6\u602f\uff0c\u6015\u6211\u62a5\u4ec7\uff0c\u4e0d\u6562\u4ea4\u624b\u7684\u8bdd\uff0c\u90a3\u5c31\u53eb\u4ed6\u4eec \u4e00\u4e2a\u4e2a\u51fa\u6765\u5411\u6211\u78d5\u5934\u8d54\u7f6a\u5427\uff01\u201d \u3000\u3000\u90a3\u516b\u540d\u6b66\u58eb\u6309\u637a\u4e0d\u4f4f\uff0c\u4e00\u9f50\u8df3\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u8981\u5355\u72ec\u548c\u6211\u4eec\u652f\u624b\uff0c\u8fd9\u53ef \u662f\u4f60\u81ea\u5df1\u8bf4\u7684\u3002\u4f60\u662f\u56fd\u738b\u6240\u8981\u7f09\u62ff\u7684\u9003\u72af\uff0c\u6211\u4eec\u4e5f\u4e0d\u5fc5\u548c\u4f60\u8bb2\u4ec0\u4e48\u6b66\u6797\u89c4\u77e9\u3002\u201d\u8c37\u4e4b\u534e \u76ee\u5149\u4e00\u626b\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd8\u6709\u4e24\u4f4d\u5927\u548c\u5c1a\u5462\uff1f\u201d\u5b9d\u8c61\u6cd5\u5e08\u751a\u662f\u5c34\u5c2c\uff0c\u8bf4\u9053\uff1a\u201c\u8c37\u638c\u95e8\uff0c\u4f60\u8d62\u4e86\u8fd9\u573a\uff0c\u6211\u81ea \u5f53\u53eb\u6211\u90a3\u4e24\u4f4d\u52a3\u5f92\u5949\u966a\u3002\u201d\u539f\u6765\u90a3\u4e24\u4e2a\u548c\u5c1a\u662f\u4ed6\u7684\u5f1f\u5b50\u3002\u5b9d\u8c61\u6cd5\u5e08\u4e0d\u6bd4\u90a3\u4e9b\u6b66\u58eb\uff0c\u4ed6\u662f \u4e2a\u8981\u987e\u8eab\u4efd\u3001\u987e\u9762\u5b50\u7684\u4eba\uff0c\u4ece\u524d\u56fd\u738b\u8981\u4ed6\u8fd9\u4e24\u4e2a\u5f1f\u5b50\u4f1a\u540c\u4ed6\u7684\u5b98\u4e2d\u516b\u5927\u9ad8\u624b\u53bb\u56f4\u6355\u8c37\u4e4b \u534e\uff0c\u4ed6\u4e0d\u5f97\u4e0d\u9075\uff0c\u73b0\u5728\u8981\u4ed6\u8fd9\u4e24\u4e2a\u5f1f\u5b50\u516c\u5f00\u51fa\u4e11\uff0c\u4ed6\u5374\u4e0d\u613f\u4e86\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u4e0d\u4e3a\u5df1\u751a\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u5c31\u8ba9\u6211\u5148\u4e86\u7ed3\u8fd9\u573a\u516c\u6848\u3002\u961f\u9762\u5411\u90a3\u516b\u4e2a\u6b66\u58eb\u9053\uff1a \u201c\u4f60\u4eec\u4e0d\u662f\u8981\u6349\u62ff\u9003\u72af\u5417\uff1f\u6211\u5728\u8fd9\u91cc\u7b49\u5019\u4f60\u4eec\u6349\u62ff\uff0c\u4e0a\u6765\u5427\uff01\u201d \u3000\u3000\u90a3\u516b\u4e2a\u6b66\u571f\u89c1\u8c37\u4e4b\u534e\u5484\u5484\u8feb\u4eba\uff0c\u4f3c\u4e4e\u65e9\u5df2\u8dbe\u5238\u5728\u64cd\uff0c\u5fc3\u4e2d\u5012\u4e0d\u7981\u6709\u70b9\u60ca\u7591\u3002\u4f46\u4e00\u60f3 \u5f53\u65e5\u4ea4\u624b\u7684\u60c5\u5f62\uff0c\u4ed6\u4eec\u5341\u4e2a\u4eba\u56f4\u653b\u8c37\u4e4b\u534e\u5e08\u5f92\uff0c\u7ed3\u679c\u662f\u5927\u83b7\u5168\u80dc\uff0c\u64d2\u4e86\u8c37\u4e2d\u83b2\uff0c\u5c06\u8c37\u4e4b \u534e\u6253\u5f97\u843d\u8352\u800c\u9003\u3002 \u3000\u3000\u73b0\u5728\u867d\u7136\u662f\u5c11\u4e86\u4e24\u4e2a\u5f97\u529b\u5e2e\u624b\uff0c\u4f46\u8c37\u4e4b\u534e\u4e5f\u5c11\u4e86\u4e00\u4e2a\u5f92\u5f1f\u5e2e\u5fd9\u3002\u5979\u90a3\u5f92\u5f1f\u5f53\u65f6\u624b\u6301 \u5b9d\u5251\uff0c\u8bba\u5b9e\u529b\u4e5f\u4e0d\u5f31\u4e8e\u90a3\u4e24\u4e2a\u91d1\u9e70\u5bab\u5f1f\u5b50\u8054\u624b\u3002\u8fd9\u516b\u4e2a\u6b66\u58eb\u5982\u6b64\u4e00\u60f3\uff0c\u4fe1\u5fc3\u5927\u5927\u589e\u5f3a\u3002 \u4f46\u4ed6\u4eec\u89c1\u8bc6\u8fc7\u8c37\u4e4b\u534e\u7684\u5389\u5bb3\uff0c\u5374\u4e5f\u4e0d\u6562\u8f7b\u654c\uff0c\u5f53\u4e0b\u5148\u5e03\u6210\u4e86\u9635\u52bf\uff0c\u5c06\u8c37\u4e4b\u534e\u56f0\u5728\u6838\u5fc3\u3002 \u4ed6\u4eec\u5374\u4e0d\u77e5\u9053\uff0c\u8c37\u4e4b\u534e\u7ecf\u8fc7\u90a3\u6b21\u632b\u8d25\uff0c\u4e0d\u4e45\u5c31\u4e0e\u91d1\u4e16\u9057\u89c1\u9762\uff0c\u5df2\u7ec3\u597d\u4e86\u4e00\u5957\u53ef\u4ee5\u4ee5\u4e00\u80dc \u5341\u7684\u5251\u6cd5\u3002 \u3000\u3000\u53cc\u65b9\u5251\u62d4\u5f29\u5f20\uff0c\u6b63\u8981\u4ea4\u624b\uff0c\u5ffd\u542c\u5f97\u6709\u4e2a\u8bf7\u8106\u7684\u58f0\u97f3\u53eb\u9053\uff1a \u3000\u3000\u201c\u5e08\u7236\uff0c\u5272\u9e21\u7109\u7528\u725b\u5200\uff0c\u8bf7\u8ba9\u5f1f\u5b50\u4ee3\u4f60\u63a5\u8fd9\u4e00\u573a\u5427\u3002\u201d\u201c\u4f17\u4eba\u62ac\u5934\u671b\u65f6\uff0c\u53ea\u89c1\u4e00\u4e2a \u767d\u8863\u5c11\u5973\u6b63\u4ece\u5899\u5934\u98de\u8fc7\uff0c\u534a\u7a7a\u4e2d\u4e00\u4e2a\u9e5e\u5b50\u7ffb\u8eab\uff0c\u53cd\u638c\u62cd\u51fa\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u7fe9\u7136\u4e0b\u964d\uff0c\u59ff\u52bf \u7f8e\u5999\u4e4b\u6781\uff01\u8fd9\u5c11\u5973\u6b63\u662f\u8c37\u4e2d\u83b2\u3002 \u3000\u3000\u968f\u5373\u542c\u5f97\u5916\u9762\u201c\u535c\u901a\u201d\u201c\u535c\u901a\u201d\u7684\u91cd\u7269\u5760\u5730\u58f0\uff0c\u53eb\u75db\u58f0\u3002\u539f\u6765\u8fd9\u5835\u5bab\u5899\u9ad8\u8fbe\u4e09\u4e08\u516d \u5c3a\uff0c\u8c37\u4e2d\u83b2\u8d8a\u5899\u800c\u8fdb\uff0c\u5916\u9762\u6709\u4e94\u540d\u5b88\u536b\u8df3\u8d77\u6765\u6293\u5979\uff0c\u5176\u4e2d\u4e09\u4eba\u8df3\u4e0d\u5f97\u8fd9\u4e48\u9ad8\uff0c\u672a\u89e6\u5899\u5934\uff0c \u4fbf\u5df2\u5760\u5730\uff0c\u53e6\u5916\u4e24\u4eba\u5219\u662f\u7ed9\u8c37\u4e2d\u83b2\u90a3\u4e00\u8bb0\u53cd\u624b\u5288\u7a7a\u638c\u6253\u843d\u7684\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u8fd9\u4e00\u7a81\u5982\u5176\u6765\uff0c\u767b\u65f6\u5f15\u8d77\u4e86\u66f4\u5927\u7684\u9a9a\u52a8\uff0c\u8c37\u4e2d\u83b2\u5144\u59b9\u548c\u6c5f\u6d77\u5929\u7b49\u4eba\u6628\u665a\u5728\u7687 \u5bab\u95f9\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u8fd9\u6d88\u606f\u65e9\u5df2\u4f20\u5230\u4e86\u91d1\u9e70\u5bab\uff0c\u8c01\u4e5f\u6599\u4e0d\u5230\u5979\u8fd9\u4e48\u5927\u80c6\uff0c\u5927\u95f9\u4e86\u7687\u5bab\u4e4b\u540e\uff0c \u7adf\u7136\u8fd8\u6562\u5355\u8eab\u8d74\u4f1a\u3002 \u3000\u3000\u8fd9\u516b\u4e2a\u6b66\u58eb\u6628\u665a\u4e0d\u5728\u7687\u5bab\uff0c\u4e1d\u6beb\u4e0d\u77e5\u8c37\u4e2d\u83b2\u5df2\u662f\u4eca\u975e\u6614\u6bd4\uff0c\u5fc3\u4e2d\u5747\u662f\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a \u5973\u5b50\u662f\u5927\u95f9\u7687\u5bab\u7684\u94a6\u72af\uff0c\u53ef\u6bd4\u5979\u5e08\u7236\u8fd8\u91cd\u8981\u5f97\u591a\u3002\u4f46\u5374\u8981\u6bd4\u5979\u5e08\u7236\u5bb9\u6613\u5bf9\u4ed8\u3002\u201d\u4e8e\u662f\u4e0d \u7ea6\u800c\u540c\u7684\u6563\u5f00\uff0c\u6539\u5411\u8c37\u4e2d\u83b2\u91c7\u53d6\u5305\u56f4\u4e4b\u52bf\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u53c8\u60ca\u53c8\u559c\uff0c\u671b\u4e86\u5f92\u5f1f\u4e00\u773c\uff0c\u53ea\u89c1\u5979\u53cc\u773c\u795e\u5149\u6e5b\u7136\uff0c\u8c37\u4e4b\u534e\u6b66\u5b66\u9020\u8be3\u6781\u9ad8\u3002\u4e00 \u773c\u5c31\u770b\u51fa\u4e86\u8c37\u4e2d\u83b2\u7684\u5185\u529f\uff0c\u5df2\u5230\u4e86\u4e0a\u4e58\u5883\u754c\uff0c\u5e76\u7acb\u5373\u731c\u5230\u4e86\u5176\u4e2d\u7684\u7f18\u6545\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c0f \u59ae\u5b50\u60f3\u5fc5\u662f\u5df2\u4e0e\u5979\u7684\u54e5\u54e5\u4f1a\u9762\uff0c\u670d\u98df\u5929\u5fc3\u77f3\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u653e\u4e0b\u4e86\u5fc3\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u5c31\u8ba9\u4f60\u51fa\u573a\u5386\u7ec3\u5386\u7ec3\u5427\u3002\u201d\u4e24\u5e08\u5f92\u4ea4 \u6362\u4e86\u4f4d\u7f6e\uff0c\u8c37\u4e2d\u83b2\u8d70\u5230\u6bd4\u6b66\u573a\u7684\u4e2d\u5fc3\uff0c\u8c37\u4e4b\u534e\u5219\u9000\u5230\u573a\u8fb9\u3002 \u3000\u3000\u90a3\u516b\u4e2a\u6b66\u58eb\u672c\u6765\u8fd8\u6709\u70b9\u5bb3\u6015\u5979\u4eec\u5e08\u5f92\u8054\u624b\uff0c\u5982\u4eca\u89c1\u5e08\u7236\u5df2\u7ecf\u9000\u4e0b\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u4e3a\u9996 \u7684\u6b66\u571f\u540d\u6728\u534e\u9ece\uff0c\u4e00\u58f0\u4ee4\u4e0b\uff0c\u9635\u52bf\u7acb\u5373\u53d1\u52a8\uff0c\u516b\u4e2a\u4eba\u4ece\u516b\u4e2a\u65b9\u5411\u653b\u6765\uff0c\u5c06\u8c37\u4e2d\u83b2\u56f4\u5728\u6838 \u5fc3\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5931\u6389\u7684\u90a3\u53e3\u971c\u534e\u5251\u6b63\u5728\u6728\u534e\u9ece\u624b\u4e2d\uff0c\u4f46\u5979\u73b0\u5728\u6240\u7528\u7684\u8fd9\u53e3\u5251\u4e43\u662f\u6c5f\u6d77\u5929\u501f\u7ed9 \u5979\u7684\u90a3\u628a\u88c1\u4e91\u5b9d\u5251\uff0c\u5251\u8d28\u6bd4\u971c\u534e\u5251\u66f4\u4f73\u3002\u6728\u534e\u9ece\u4e00\u5251\u653b\u5230\uff0c\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u8fd9\u628a\u5251\u4e5f\u5e94 \u8be5\u8fd8\u7ed9\u6211\u4e86\uff01\u201d\u5979\u6015\u635f\u4f24\u4e86\u971c\u534e\u5251\uff0c\u4e0d\u6562\u7528\u88c1\u4e91\u5251\u53bb\u524a\uff0c\u6539\u7528\u957f\u8896\u6325\u51fa\uff0c\u500f\u5730\u5c31\u5377\u4f4f\u4e86 \u5251\u67c4\uff0c\u90a3\u6728\u534e\u9ece\u662f\u5b98\u4e2d\u4e00\u7b49\u4e00\u7684\u597d\u624b\uff0c\u6b66\u529f\u59d4\u5b9e\u4e0d\u5f31\u3002 \u3000\u3000\u88ab\u5979\u8863\u8896\u4e00\u5377\uff0c\u5b9d\u5251\u51e0\u4e4e\u62ff\u634f\u4e0d\u4f4f\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u7528\u5343\u65a4\u5760\u7684\u91cd\u8eab\u6cd5\u7a33\u770b\u8eab\u5f62\uff0c \u8c37\u4e2d\u83b2\u8fd9\u4e00\u5377\u7adf\u672a\u80fd\u5c06\u5b9d\u5251\u593a\u4e0b\u3002 \u3000\u3000\u9635\u52bf\u8fc5\u5373\u5408\u56f4\uff0c\u5c31\u5728\u8c37\u4e2d\u83b2\u4e0e\u6728\u534e\u9ece\u76f8\u5f85\u7684\u8fd9\u4e00\u77ac\u4e4b\u95f4\uff0c\u524d\u540e\u5de6\u53f3\u90fd\u5df2\u6709\u4eba\u653b\u5230\uff0c \u8c37\u4e2d\u83b2\u5c06\u88c1\u4e91\u5251\u4e00\u6325\uff0c\u524d\u65b9\u3001\u5de6\u65b9\u3001\u53f3\u65b9\u90fd\u7ed9\u5251\u5149\u5c01\u4f4f\uff0c\u4f46\u80cc\u540e\u5374\u9732\u51fa\u4e86\u7834\u7efd\uff0c\u767b\u65f6\u6709 \u4e24\u67c4\u957f\u77db\u523a\u4e2d\u4e86\u5979\u7684\u540e\u5fc3\uff01 \u3000\u3000\u4e2d\u539f\u6765\u7684\u7fa4\u96c4\u5927\u60ca\u5931\u8272\uff0c\u6c5f\u5357\u66f4\u662f\u7d27\u5f20\u5f97\u558a\u53eb\u8d77\u6765\uff0c\u5ffd\u542c\u5f97\u201c\u5494\u5693\u201d\u4e24\u58f0\uff0c\u90a3\u4e24\u67c4 \u957f\u77db\u77db\u5934\u6298\u65ad\uff0c\u90a3\u4e24\u4e2a\u6b66\u58eb\u7ed9\u629b\u51fa\u6570\u4e08\u5f00\u5916\uff0c\u6454\u4e86\u4e2a\u4e00\u4f5b\u51fa\u4e16\uff0c\u4e8c\u4f5b\u6d85\u78d0\u3002 \u3000\u3000\u539f\u6765\u8c37\u4e2d\u83b2\u7a7f\u7740\u6c5f\u6d77\u5929\u9001\u7ed9\u5979\u7684\u90a3\u4ef6\u767d\u7389\u7532\uff0c\u5200\u67aa\u4e0d\u5165\uff0c\u8fd9\u4e24\u67c4\u957f\u77db\u600e\u523a\u5f97\u8fdb\u53bb\uff1f \u8c37\u4e2d\u83b2\u670d\u98df\u4e86\u5929\u5fc3\u77f3\u4e4b\u540e\u3002\u62a4\u4f53\u795e\u529f\u4ea6\u5df2\u7ec3\u6210\uff0c\u90a3\u4e24\u4e2a\u6b66\u58eb\u7528\u4e86\u6d51\u8eab\u6c14\u529b\uff0c\u523a\u51fa\u957f\u4e88\uff0c \u7ed3\u679c\u4ed6\u4eec\u6240\u53d1\u51fa\u7684\u52b2\u529b\u5168\u7ed9\u53cd\u5f39\u56de\u6765\u3002\u4ed6\u4eec\u6454\u5728\u5730\u4e0a\uff0c\u672a\u66fe\u78b0\u7740\u77f3\u67f1\uff0c\u53ea\u662f\u6454\u4e2a\u534a\u6b7b\uff0c \u5df2\u662f\u4e0d\u5e78\u4e2d\u4e4b\u5927\u5e78\u4e86\u3002 \u3000\u3000\u8fd9\u56de\u8f6e\u5230\u56fd\u738b\u8fd9\u8fb9\u7684\u4eba\u5927\u60ca\u5931\u8272\uff0c\u4ed6\u4eec\u4e0d\u77e5\u8c37\u4e2d\u83b2\u8eab\u7a7f\u5b9d\u7532\uff0c\u53ea\u9053\u5979\u5df2\u7ec3\u6210\u4e86\u5200\u67aa \u4e0d\u5165\u7684\u7edd\u9876\u5185\u529f\uff0c\u76f8\u987e\u9a87\u7136\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u6728\u534e\u9ece\u53c8\u662f\u4e00\u58f0\u5389\u547c\uff0c\u773c\u8033\u9f3b\u53e3\uff0c\u9c9c\u8840 \u6c69\u6c69\u6d41\u51fa\uff0c\u4ed6\u9093\u6c34\u725b\u822c\u7c97\u58ee\u7684\u8eab\u8eaf\uff0c\u767b\u65f6\u8f6f\u5f97\u4f3c\u662f\u4e00\u56e2\u70c2\u6ce5\uff0c\u5012\u5728\u5730\u4e0a\uff0c\u7f29\u6210\u4e00\u56e2\u3002\u90a3 \u67c4\u971c\u534e\u5b9d\u5251\u5f53\u7136\u4e5f\u5c31\u7ed9\u8c37\u4e2d\u83b2\u593a\u8fc7\u53bb\u4e86\u3002\u539f\u6765\u4ed6\u5f3a\u81ea\u8fd0\u529b\u652f\u6491\uff0c\u867d\u7136\u6491\u5f97\u4e00\u65f6\uff0c\u5374\u600e\u7981 \u5f97\u8d77\u8c37\u4e2d\u83b2\u96c4\u539a\u7684\u5185\u529f\u6e90\u6e90\u800c\u6765\uff0c\u7ec8\u4e8e\u4e94\u810f\u9707\u88c2\uff0c\u8840\u7ba1\u7206\u5f7c\uff0c\u9001\u4e86\u6027\u547d\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u53cc\u5251\u5728\u624b\uff0c\u5982\u864e\u6dfb\u7ffc\uff0c\u53ea\u89c1\u5251\u5149\u76d8\u65cb\u98de\u821e\uff0c\u4e00\u7247\u65ad\u91d1\u560e\u7389\u4e4b\u58f0\uff0c\u5176\u4f59\u516d\u4e2a\u6b66 \u571f\u7684\u5175\u5668\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u4fbf\u5df2\u7ed9\u5979\u5168\u90fd\u524a\u65ad\uff01\u8fd8\u5e78\u8c37\u4e2d\u83b2\u4e0d\u613f\u591a\u6240\u6740\u4f24\uff0c\u53ea\u662f\u524a\u65ad\u4ed6\u4eec\u7684 \u5175\u5668\uff0c\u4fbf\u5373\u6536\u624b\u3002 \u3000\u3000\u56fd\u738b\u8bf7\u6765\u7684\u9ad8\u624b\u5728\u4f1a\u573a\u4e2d\u7684\u4e3a\u6570\u751a\u591a\uff0c\u4ed6\u4eec\u4e4b\u4e2d\uff0c\u672c\u6765\u4e5f\u6709\u4eba\u60f3\u51fa\u53bb\u64d2\u62ff\u8c37\u4e2d\u83b2\u7684\uff0c \u8fd9\u65f6\u4e5f\u5413\u5f97\u7f29\u4e86\u5934\u3002\u5b9d\u8c61\u6cd5\u5e08\u5bf9\u4f8d\u7acb\u65c1\u8fb9\u7684\u5927\u5f1f\u5b50\u6084\u6084\u8bf4\u9053\uff1a\u201c\u8fd9\u5973\u5a03\u5b50\u6b66\u529f\u786e\u662f\u4e0d\u9519\uff0c \u4f46\u8981\u5bf9\u4ed8\u5979\u4ea6\u975e\u96be\u4e8b\u3002\u4e0d\u8fc7\u5979\u90a3\u8fb9\u9ad8\u624b\u4e5f\u5f88\u591a\uff0c\u6700\u5389\u5bb3\u7684\u8fd8\u672a\u51fa\u573a\uff0c\u73b0\u5728\u8fd8\u4e0d\u5b9c\u5f15\u8d77\u6df7 \u6218\uff0c\u5f85\u5230\u5c06\u5979\u90a3\u8fb9\u7684\u9ad8\u624b\u51fb\u8d25\u4e86\u51e0\u4e2a\u4e4b\u540e\uff0c\u81ea\u4e0d\u6015\u8fd9\u5973\u5a03\u5b50\u9003\u5f97\u4e0a\u5929\u3002\u201d\u90a3\u5927\u5f1f\u4e8e\u5c06\u8fd9 \u756a\u8bdd\u4f20\u7ed9\u56fd\u738b\u7684\u6b66\u58eb\u957f\uff0c\u8fd9\u4e9b\u4eba\u624d\u5b89\u5b9a\u4e0b\u6765\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u56de\u5230\u5e08\u7236\u8eab\u8fb9\uff0c\u5979\u6e38\u76ee\u56db\u987e\uff0c\u4e0d\u89c1\u6c5f\u6d77\u5929\u5728\u573a\uff0c\u751a\u662f\u5931\u671b\u3002\u8c37\u4e4b\u534e\u4f4e\u58f0\u95ee\u9053\uff0c \u201c\u4f60\u54e5\u54e5\u5462\uff0c\u4f60\u4eec\u8fd8\u672a\u66fe\u4f1a\u9762\u5417\uff1f\u201d\u5979\u6307\u7684\u662f\u91d1\u4e16\u9057\u7684\u5f1f\u5b50\u5510\u52aa\u73e0\u7a46\u3002\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211 \u7684\u4e24\u4e2a\u54e5\u54e5\u90fd\u89c1\u7740\u4e86\u3002\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u90fd\u5fd9\u7740\u522b\u7684\u4e8b\u60c5\uff0c\u6682\u65f6\u8fd8\u4e0d\u80fd\u6765\u3002\u201d\u8c37\u4e4b\u534e\u8be7\u9053\uff1a \u201c\u4f60\u8fd8\u6709\u4e00\u4f4d\u54e5\u54e5\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u662f\u554a\u3002\u771f\u662f\u610f\u60f3\u4e0d\u5230\u7684\u4e8b\u3002\u201d\u5728\u5e08\u7236\u8033\u8fb9\u6084\u6084\u8bf4\u4e86 \u51e0\u53e5\uff0c\u8c37\u4e4b\u534e\u5fc3\u4e2d\u5927\u559c\u3002\u539f\u6765\u5510\u52aa\u73e0\u7a46\u4e34\u65f6\u6539\u53d8\u8ba1\u5212\uff0c\u8d81\u7fa4\u96c4\u5927\u4f1a\u91d1\u9e70\u5bab\u7684\u65f6\u5019\uff0c\u4ed6\u5df2 \u53bb\u8054\u7edc\u5fe0\u5fc3\u4e8e\u65e7\u738b\u7684\u5927\u81e3\uff0c\u51c6\u5907\u8d77\u5175\u56f4\u653b\u738b\u5bab\u4e86\u3002 \u3000\u3000\u5979\u4eec\u4e24\u5e08\u5f92\u8fd8\u5728\u4ea4\u5934\u63a5\u8033\uff0c\u90a3\u9634\u5723\u59d1\u65e9\u5df2\u6309\u637a\u4e0d\u4f4f\u3002\u8df3\u51fa\u573a\u6765\uff0c\u5411\u4ef2\u957f\u7edf\u6311\u6218\uff0c\u4ef2 \u957f\u7edf\u5927\u7b11\u51fa\u573a\uff0c\u8bf4\u9053\uff1a\u201c\u4ffa\u505a\u5316\u5b50\u7684\u7b49\u5019\u5e03\u65bd\uff0c\u4f60\u6709\u4ec0\u4e48\u6bd2\u7269\uff0c\u5c3d\u7ba1\u62ff\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u9634\u5723\u59d1\u9634\u607b\u607b\u4ed6\u8bf4\u9053\uff1a\u201c\u81ed\u53eb\u5316\u53e3\u51fa\u5927\u8a00\uff0c\u4f60\u5c31\u63a5\u5427\u3002\u201d\u5979\u5341\u6307\u90fd\u5957\u7740\u6307\u73af\uff0c\u4e00\u6296 \u624b\uff0c\u5341\u679a\u6307\u73af\u5168\u90e8\u98de\u51fa\u3001\u545c\u545c\u58f0\u54cd\uff0c\u6709\u7684\u76f4\u7ebf\u5c04\u6765\uff0c\u6709\u7684\u62d0\u5f2f\u6253\u5230\uff0c\u6709\u7684\u98de\u8fc7\u4e86\u4ef2\u957f\u7edf \u7684\u5934\u9876\uff0c\u7a81\u7136\u8f6c\u4e86\u4e2a\u5708\uff0c\u53c8\u98de\u56de\u6765\u6253\u4ef2\u957f\u7edf\u7684\u540e\u5fc3\u3002\u5979\u53ea\u662f\u4e00\u6296\u624b\u4e4b\u95f4\u5c31\u8fd0\u7528\u4e86\u5404\u79cd\u4e0d \u540c\u7684\u6697\u5668\u624b\u6cd5\uff0c\u83ab\u8bf4\u5979\u7684\u6307\u73af\u90fd\u662f\u6dec\u8fc7\u6bd2\u836f\u7684\uff0c\u5373\u7b97\u5b8c\u5168\u65e0\u6bd2\uff0c\u8fd9\u6837\u5947\u5999\u7684\u6697\u5668\u624b\u6cd5\uff0c \u4e5f\u8db3\u4ee5\u9707\u4e16\u9a87\u4fd7\u4e86\uff01 \u3000\u3000\u4ef2\u957f\u7edf\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u9053\u662f\u4ec0\u4e48\u7a00\u5947\u7684\u4e1c\u897f\uff0c\u8fd9\u51e0\u679a\u94dc\u6307\u73af\u6709\u4ec0\u4e48\u7528\uff0c\u8fd8\u6362\u4e0d\u5230\u4e24 \u65a4\u7c73\uff0c\u4f60\u4e5f\u672a\u514d\u592a\u5c0f\u6c14\u4e86\u3002\u8001\u53eb\u5316\u4e0d\u8981\uff01 \u3000\u3000\u53ea\u542c\u5f97\u94ee\u94ee\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u4f46\u89c1\u4ed6\u75be\u8f6c\u4e00\u56f5\uff0c\u5341\u6307\u8fde\u5f39\uff0c\u5341\u679a\u6bd2\u6307\u73af\u5168\u7ed9\u4ed6\u5f39\u843d\u3002 \u3000\u3000\u9661\u7136\u95f4\u8165\u98ce\u6251\u9f3b\uff0c\u9634\u5723\u59d1\u53cc\u638c\u5df2\u7136\u88ad\u5230\uff0c\u539f\u6765\u9634\u5723\u59d1\u6df1\u77e5\u4ef2\u957f\u7edf\u7684\u5389\u5bb3\uff0c\u90a3\u5341\u679a\u6307 \u73af\u7684\u4f5c\u7528\u53ea\u662f\u60f3\u6270\u4ed6\u8033\u76ee\uff0c\u6253\u4ed6\u4e2a\u624b\u5fd9\u811a\u4e71\uff0c\u7136\u540e\u4e58\u673a\u7528\u795e\u86c7\u638c\u4f24\u4ed6\u3002\u8fd9\u795e\u86c7\u638c\u624d\u662f\u9634 \u5723\u59d1\u6700\u5f97\u610f\u7684\u529f\u592b\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u731b\u5730\u4e00\u53e3\u6c14\u5439\u53bb\uff0c\u9634\u5723\u59d1\u7684\u80f8\u53e3\u7a81\u7136\u4f3c\u7ed9\u4eba\u51fb\u4e86\u4e00\u62f3\uff0c\u867d\u7136\u7981\u53d7\u5f97\u8d77\uff0c\u5374\u4e5f \u9000\u4e86\u4e00\u6b65\u3002\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u53ae\u7684\u6df7\u5143\u4e00\u6c14\u529f\u5df2\u7ec3\u5230\u5618\u6c14\u6210\u98ce\uff0c\u6709 \u5f62\u65e0\u8d28\u7684\u5883\u754c\uff0c\u770b\u6765\u4eca\u65e5\u53ea\u6015\u8981\u4e24\u8d25\u4ff1\u4f24\u4e86\u3002\u201d\u4ef2\u957f\u7edf\u4e00\u53e3\u6c14\u5439\u51fa\uff0c\u6700\u540e\u90a3\u4e24\u679a\u6307\u73af\u4ea6 \u5df2\u7ed9\u4ed6\u5f39\u843d\uff0c\u8fd9\u624d\u817e\u51fa\u624b\u6765\uff0c\u8fd8\u4e86\u5979\u4e00\u8bb0\u5288\u7a7a\u638c\uff0c \u3000\u3000\u4ef2\u957f\u7edf\u7684\u638c\u529b\u5f53\u7136\u6bd4\u4ed6\u7684\u201c\u5618\u6c14\u6210\u98ce\u201d\u53c8\u731b\u70c8\u5f97\u591a\uff0c\u9634\u5723\u59d1\u4e0d\u6562\u76f4\u63a5\u5176\u950b\uff0c\u53ea\u597d\u4fa7 \u8eab\u907f\u5f00\u4ed6\u7684\u638c\u529b\u3002\u4ef2\u957f\u7edf\u5411\u4e1c\u5357\u897f\u5317\u8fde\u53d1\u56db\u638c\uff0c\u638c\u529b\u6709\u5982\u6392\u5c71\u5012\u6d77\uff0c\u5411\u56db\u65b9\u6d8c\u51fa\uff0c\u9634\u5723 \u59d1\u54ea\u8fd1\u5f97\u4e86\u8eab\u3002 \u3000\u3000\u9634\u5723\u59d1\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u81ea\u6043\u5185\u529f\u6df1\u539a\uff0c\u5c31\u4ee5\u4e3a\u6211\u65e0\u53ef\u5948\u4f55\u4e86\u4e48\uff1f\u201d\u8896\u4e2d\u5ffd\u5730\u98de\u51fa\u4e00\u6761 \u9752\u86c7\uff0c\u4ef2\u957f\u7edf\u7684\u638c\u529b\u7adf\u7136\u6321\u5b83\u4e0d\u4f4f\uff0c\u539f\u6765\u8fd9\u662f\u4e00\u6761\u5f02\u79cd\u602a\u86c7\uff0c\u53ea\u6709\u4e00\u652f\u9999\u7c97\u7ec6\uff0c\u6c14\u529b\u5374 \u5927\u5f97\u51fa\u5947\uff0c\u72ee\u864e\u7ed9\u5b83\u7f20\u4e0a\uff0c\u4e5f\u96be\u514d\u4e00\u6b7b\u3002\u8fd9\u9752\u86c7\u6709\u9699\u5373\u94bb\uff0c\u7b49\u4e8e\u662f\u4e00\u4ef6\u6d3b\u6697\u5668\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u4e00\u811a\u8e0f\u4e0b\uff0c\u90a3\u9752\u86c7\u6602\u8d77\u5934\u6765\uff0c\u65e9\u5df2\u6e38\u4e0a\u4ed6\u7684\u8eab\u5b50\uff0c\u4e00\u53e3\u54ac\u7740\u4ed6\u7684\u624b\u6307\uff0c\u4ef2\u957f \u7edf\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8001\u53eb\u5316\u4e00\u751f\u6349\u86c7\uff0c\u4eca\u756a\u7b2c\u4e00\u6b21\u88ab\u86c7\u54ac\u4e86\u3002\u201d\u8bdd\u72b9\u6765\u4e86\uff0c\u53ea\u89c1\u90a3\u6761\u9752\u86c7\u5df2 \u6389\u4e0b\u5730\u6765\uff0c\u4e0d\u80fd\u52a8\u5f39\uff0c\u4ef2\u957f\u7edf\u4e00\u811a\u5c06\u5b83\u8e29\u4e2a\u7a00\u70c2\u3002\u539f\u6765\u4ef2\u957f\u7edf\u8bc6\u5f97\u8fd9\u602a\u86c7\u7684\u5389\u5bb3\uff0c\u82e5\u7ed9 \u5b83\u54ac\u7740\u54bd\u5589\uff0c\u6216\u94bb\u5165\u9f3b\u5b54\uff0c\u90a3\u4e48\u591a\u597d\u7684\u5185\u529f\u4e5f\u8981\u6bd9\u547d\uff0c\u4f46\u7ed9\u5b83\u54ac\u7740\u624b\u6307\uff0c\u5374\u662f\u65e0\u59a8\u3002\u4ef2 \u957f\u7edf\u5c31\u662f\u6545\u610f\u5c06\u4e2d\u6307\u9001\u53bb\u7ed9\u5b83\u54ac\u7684\u3002\u4ed6\u795e\u529f\u4e00\u8fd0\uff0c\u4e2d\u6307\u575a\u786c\u5982\u94c1\uff0c\u90a3\u9752\u86c7\u54ac\u4e4b\u4e0d\u52a8\uff0c\u7acb \u5373\u4fbf\u7ed9\u4ef2\u957f\u7edf\u6307\u529b\u6233\u7a7f\uff0c\u518d\u4e5f\u4e0d\u80fd\u4f5c\u6076\u4e86\u3002\u4ef2\u957f\u7edf\u4f4e\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u4e2d\u6307\u867d\u672a\u54ac\u7834\uff0c\u4e5f\u5df2 \u7559\u4e0b\u4e00\u9053\u6de1\u6de1\u7684\u9f7f\u75d5\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u89c9\u9a87\u7136\u3002 \u3000\u3000\u4ece\u90a3\u9752\u86c7\u5f00\u59cb\u4fb5\u88ad\u5230\u4ef2\u957f\u7edf\u5c06\u5b83\u51fb\u6bd9\uff0c\u867d\u7136\u4e3a\u65f6\u6781\u77ed\uff0c\u4f46\u5728\u90a3\u77ed\u4fc3\u7684\u77ac\u95f4\uff0c\u4ef2\u957f\u7edf \u8981\u79fb\u5347\u4e00\u53ea\u624b\u53bb\u5bf9\u4ed8\u9752\u86c7\uff0c\u638c\u529b\u81ea\u662f\u4e0d\u514d\u51cf\u5f31\uff0c\u9634\u5723\u59d1\u8d81\u6b64\u673a\u4f1a\uff0c\u5168\u529b\u8fdb\u653b\uff0c\u7adf\u7a81\u7834\u4e86 \u4ef2\u957f\u7edf\u638c\u529b\u7684\u5c01\u9501\uff0c \u3000\u3000\u4ef2\u957f\u7edf\u5355\u638c\u4e00\u7acb\uff0c\u659c\u9000\u4e09\u6b65\uff0c\u521a\u521a\u5c06\u6c14\u606f\u8c03\u5300\uff0c\u6b63\u62df\u8fd8\u51fb\u3002 \u3000\u3000\u9634\u5723\u59d1\u5ffd\u5730\u5927\u559d\u4e00\u58f0\uff1a\u201c\u7740\uff01\u201d\u5341\u6307\u6307\u7532\u7a81\u7136\u95f4\u66b4\u957f\u6570\u5bf8\uff0c\u539f\u6765\u5979\u7684\u6307\u7532\u5341\u5206\u53e4\u602a\uff0c \u4e0d\u7528\u4e4b\u65f6\u3002\u5377\u6210\u4e00\u56e2\uff0c\u5230\u4e86\u7d27\u6025\u4e4b\u9645\uff0c\u5c06\u6307\u7532\u5f39\u5f00\uff0c\u5c31\u7b49\u4e8e\u5e73\u6dfb\u4e86\u5341\u652f\u5315\u9996\uff0c\u523a\u6293\u654c\u4eba\uff01 \u3000\u3000\u9ad8\u624b\u8fc7\u62db\uff0c\u76f8\u5dee\u4e0d\u8fc7\u6beb\u5398\uff0c\u9634\u5723\u59d1\u7684\u6307\u7532\u7a81\u957f\u6570\u5bf8\uff0c\u4ef2\u957f\u7edf\u7cb9\u4e0d\u53ca\u9632\uff0c\u7adf\u7136\u7ed9\u5979\u6293 \u7740\u4e86\u4e00\u6761\u624b\u81c2\u3002\u9634\u5723\u59d1\u5185\u529b\u76f4\u900f\u6307\u5c16\uff0c\u5979\u7684\u6307\u7532\u6495\u6293\u4e4b\u529b\u6bd4\u90a3\u9752\u86c7\u7684\u6bd2\u7259\u54ac\u556e\u5389\u5bb3\u5341\u500d\uff0c \u4ef2\u957f\u7edf\u7684\u624b\u81c2\u7ed9\u5979\u6495\u5f00\u4e86\u4e00\u6761\u4f24\u53e3\uff0c\u4f46\u5947\u602a\u5f97\u5f88\uff0c\u5374\u4e1d\u6beb\u4e0d\u75db\uff0c\u53cd\u800c\u6709\u4e00\u79cd\u4f3c\u662f\u7ed9\u4eba \u201c\u6293\u7740\u75d2\u5904\u201d\u7684\u8212\u670d\u611f\u89c9\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u662f\u4e2a\u5927\u884c\u5bb6\uff0c\u77e5\u9053\u6240\u4e2d\u7684\u6bd2\u8d8a\u662f\u5389\u5bb3\u5c31\u8d8a\u662f\u611f\u89c9\u8212\u670d\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u5927\u6012\uff0c\u60f3 \u9053\uff1a\u201c\u8fd9\u5996\u5987\u5982\u6b64\u72e0\u6bd2\uff0c\u8feb\u5f97\u6211\u975e\u53d6\u5979\u6027\u547d\u4e0d\u53ef\u4e86\uff01\u201d\u624b\u81c2\u4e00\u6c89\uff0c\u4e94\u6307\u5982\u94a7\uff0c\u767b\u65f6\u4e5f\u52fe \u4f4f\u4e86\u9634\u5723\u59d1\u7684\u6bd2\u624b\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u8fd0\u4e00\u53e3\u6c14\u900f\u8fc7\u638c\u5fc3\uff0c\u5185\u529b\u5982\u6f6e\uff0c\u4e0d\u4f46\u5c06\u6bd2\u6c14\u9a71\u51fa\uff0c\u800c\u4e14\u8feb\u5f97\u90a3\u80a1\u6bd2\u6c14\u5012\u6d41\uff0c \u4fb5\u5165\u9634\u5723\u7ad9\u4f53\u5185\uff01\u5728\u4ed6\u5185\u529b\u63a8\u538b\u4e4b\u4e0b\uff0c\u53ea\u89c1\u4e00\u6761\u9ed1\u7ebf\u4ece\u9634\u5723\u59d1\u7684\u4e2d\u6307\u873f\u8712\u800c\u4e0a\uff0c\u8f6c\u77ac\u95f4 \u5df2\u4ece\u638c\u5fc3\u5347\u5230\u624b\u8155\u3002\u9634\u5723\u59d1\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8981\u77e5\u5979\u867d\u7136\u7ec3\u6210\u6bd2\u638c\uff0c\u4f46\u5185\u810f\u5374\u4ecd\u662f\u5982\u5e38 \u4eba\u4e00\u6837\uff0c\u4e0d\u80fd\u4e2d\u6bd2\u3002\u5018\u82e5\u7ed9\u6bd2\u6c14\u653b\u5230\u5fc3\u5934\uff0c\u5979\u5c31\u8981\u81ea\u53d7\u5176\u5bb3\uff0c\u65e0\u53ef\u6551\u6cbb\u3002 \u3000\u3000\u9634\u5723\u59d1\u60f3\u6446\u8131\u5bf9\u65b9\u624b\u638c\uff0c\u5374\u54ea\u91cc\u6446\u8131\u5f97\u5f00\uff1f\u53ea\u597d\u5168\u529b\u5bf9\u6297\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u7684\u6df7\u5143\u4e00\u6c14\u529f\u5df2\u5230\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u83ab\u8bf4\u4e00\u4e2a\u9634\u5723\u59d1\uff0c\u518d\u591a\u4e24\u4e2a\u4e5f\u62b5\u5fa1\u4e0d\u4f4f\uff0c \u4e0d\u8fc7\u7247\u523b\uff0c\u90a3\u6761\u9ed1\u7ebf\u5df2\u4ece\u864e\u53e3\u5347\u5230\u81c2\u5f2f\u3002 \u3000\u3000\u9634\u5723\u59d1\u53cc\u775b\u51f8\u51fa\uff0c\u773c\u4e2d\u5c31\u4f3c\u8981\u55b7\u51fa\u706b\u6765\uff0c\u5ffd\u5730\u4e00\u54ac\u7259\u6839\uff0c\u4e3e\u8d77\u5de6\u638c\uff0c\u500f\u5730\u671d\u7740\u53f3\u81c2 \u81c2\u5f2f\u65a9\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u201c\u65a9\u201d\u8d5b\u5982\u5200\u524a\uff0c\u7adf\u662f\u786c\u751f\u751f\u7684\u628a\u534a\u6761\u53f3\u81c2\u65a9\u4e86\u4e0b\u6765\uff0c\u4f24\u53e3\u767b\u65f6\u4f3c\u662f\u5f00\u4e86\u4e00\u9053 \u55b7\u6cc9\uff0c\u8840\u6c34\u5982\u7bad\u5c04\u53bb\uff0c\u55b7\u4e86\u4ef2\u957f\u7edf\u6ee1\u5934\u6ee1\u9762\u3002\u4ef2\u957f\u7edf\u6025\u5fd9\u95ed\u4e86\u773c\u775b\uff0c\u4f46\u9f3b\u5b54\u548c\u81c2\u4e0a\u7684\u4f24 \u53e3\u5df2\u88ab\u5979\u7684\u6bd2\u8840\u5c04\u5165\u3002\u4ef2\u957f\u7edf\u53ea\u89c9\u4e00\u9635\u5455\u5fc3\uff0c\u5934\u6655\u76ee\u7729\u3002\u539f\u6765\u8fd9\u662f\u9634\u5723\u59d1\u6700\u540e\u4e00\u624b\u6bd2\u529f\u3002 \u540d\u4e3a\u201c\u6bd2\u8840\u524d\u201d\uff0c\u81ea\u6b8b\u80a2\u4f53\u4e4b\u540e\uff0c\u53ef\u4ee5\u55b7\u51fa\u6bd2\u8840\u4f24\u4eba\uff01 \u3000\u3000\u4ef2\u957f\u7edf\u95ed\u7740\u773c\u775b\uff0c\u4e00\u638c\u62cd\u51fa\uff0c\u9634\u5723\u59d1\u201c\u5495\u549a\u201d\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\u3002\u4ef2\u957f\u7edf\u52c9\u5f3a\u9000\u5230\u573a \u8fb9\uff0c\u4ea6\u5df2\u4f3c\u98ce\u4e2d\u4e4b\u70db\uff0c\u6447\u6447\u6b32\u5760\u3002 \u3000\u3000\u573a\u8fb9\u6709\u4e2a\u4eba\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff0c\u6211\u6b63\u60f3\u6597\u4e00\u6597\u8fd9\u8001\u53eb\u5316\u7684\u6df7\u5143\u4e00\u6c14\u529f\uff0c\u53ef\u60dc\u4ed6\u5374 \u7ed9\u9634\u5723\u59d1\u7684\u6bd2\u8840\u7bad\u4f24\u4e86\u3002\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u5973\u5b50\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4f60\u522b\u8bf4\u98ce\u51c9\u8bdd\u4e86\uff0c\u5feb\u5e2e\u4e00\u5e2e\u5fd9\uff0c\u6551\u6211\u7684\u59d1\u5a46\u5427\u3002\u201d\u8fd9\u4e24 \u4e2a\u4eba\u6b63\u662f\u5929\u9b54\u6559\u4e3b\u548c\u6587\u5ef7\u58c1\uff0c\u65c1\u8fb9\u8fd8\u6709\u4e2a\u5389\u590d\u751f\u3002\u4ed6\u4eec\u662f\u5728\u521a\u624d\u53cc\u65b9\u6fc0\u6218\u4e4b\u65f6\u8fdb\u6765\u7684\uff0c \u8fd9\u4e00\u573a\u60e8\u70c8\u7684\u6fc0\u6218\uff0c\u4eba\u4eba\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u6240\u4ee5\u4ed6\u4eec\u8fdb\u6765\uff0c\u5e76\u6ca1\u6709\u5f15\u8d77\u6ce8\u610f\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u6709\u70b9\u8be7\u5f02\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u8001\u4e5e\u5a46\u4e00\u6b7b\uff0c\u5c82\u4e0d\u6b63\u9042\u4e86\u4f60\u7684\u5fc3\u613f\uff0c\u4f55\u4ee5\u8fd8\u8981\u6551\u5979\uff1f\u201d \u8981\u77e5\u5929\u4e0b\u4e24\u4e2a\u4f7f\u6bd2\u9ad8\u624b\uff0c\u4e00\u4e2a\u662f\u5929\u9b54\u6559\u4e3b\uff0c\u4e00\u4e2a\u662f\u9634\u5723\u59d1\uff0c\u5929\u9b54\u6559\u4e3b\u7531\u4e8e\u4e0a\u4ee3\u6e0a\u6e90\uff0c\u79f0 \u9634\u5723\u7ad9\u201c\u59d1\u5a46\u201d\uff0c\u5176\u5b9e\u4e95\u975e\u4eb2\u5c5e\uff0c\u800c\u4e14\u5728\u4e24\u4eba\u4e4b\u95f4\uff0c\u8fd8\u9887\u6709\u5acc\u9699\uff0c\u5f7c\u6b64\u5992\u5fcc\u3002 \u3000\u3000\u662f\u4ee5\u6587\u5ef7\u58c1\u9887\u89c9\u51fa\u5947\uff0c\u4e0d\u89e3\u592d\u9b54\u6559\u4e3b\u4f55\u4ee5\u5982\u6b64\u597d\u5fc3\u3002\u4f46\u8fd9\u662f\u6559\u4e3b\u7684\u5429\u5490\uff0c\u4ed6\u552f\u6709\u4f9d \u4ece\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5c06\u9634\u5723\u59d1\u6276\u8d77\uff0c\u6587\u5ef7\u58c1\u968f\u5373\u8fd0\u7528\u201c\u4e09\u8c61\u5f52\u5143\u201d\u7684\u90aa\u6d3e\u7edd\u9876\u795e\u529f\uff0c\u5c01\u4e86\u9634\u5723 \u59d1\u201d\u624b\u5c11\u9633\u7ecf\u8109\u201d\u7684\u4e03\u9053\u5927\u7a74\uff0c\u7531\u4e8e\u4f24\u53e3\u5927\u5927\uff0c\u6d41\u8840\u8fd8\u672a\u80fd\u5373\u65f6\u5168\u6b62\uff0c\u4f46\u5df1\u662f\u4e00\u70b9\u4e00\u6ef4 \u7684\u6d41\u4e0b\uff0c\u4e0d\u4f3c\u521a\u624d\u7684\u5982\u6cc9\u72c2\u55b7\u4e86\u3002\u5929\u9b54\u6559\u4e3b\u5728\u4f24\u53e3\u6d12\u4e86\u4e00\u64ae\u836f\u7c89\uff0c\u6495\u4e0b\u9634\u5723\u59d1\u4e00\u5e45\u8863\u88f3\uff0c \u5c31\u7ed9\u5979\u5305\u624e\u8d77\u6765\u3002\u7ec3\u8fc7\u6bd2\u529f\u7684\u4eba\uff0c\u53d7\u4f24\u4e4b\u540e\uff0c\u4e0d\u80fd\u7528\u666e\u901a\u7684\u91d1\u521b\u836f\uff0c\u8fd9\u662f\u5929\u9b54\u6559\u4e3b\u4f9d\u7167 \u6bd2\u7ecf\u79d8\u6cd5\u81ea\u5236\u7684\u836f\u7c89\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5fc3\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u9b54\u5934\u6628\u665a\u90fd\u66fe\u53d7\u4f24\uff0c\u60f3\u4e0d\u5230\u73b0\u5728\u5df2\u7ecf\u5b8c\u5168\u6062\u590d\uff0c\u6839\u57fa\u4e4b\u539a\uff0c \u786e\u662f\u4e0d\u5bb9\u5c0f\u89c6\u3002\u201d\u4e2d\u539f\u7fa4\u8c6a\uff0c\u7b2c\u4e00\u6b21\u89c1\u5230\u6587\u5ef7\u58c1\u7684\u4e09\u8c61\u5f52\u5143\u795e\u529f\uff0c\u66f4\u89c9\u60ca\u5947\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u90a3\u4e00\u64ae\u836f\u7c89\u679c\u7136\u7075\u6548\uff0c\u9634\u5723\u59d1\u4eff\u5f90\u5f20\u5f00\u773c\u775b\uff0c\u60e8\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u73e0\u739b\uff0c \u771f\u6709\u4f60\u7684\uff0c\u4f60\u59d1\u5a46\u4e0d\u4e2d\u7528\u5566\uff0c\u4ece\u4eca\u4e4b\u540e\u4f60\u59d1\u5a46\u51b3\u4e0d\u80fd\u4e0e\u4f60\u518d\u4e89\u80dc\u4e86\u3002\u90a3\u672c\u767e\u6bd2\u771f\u7ecf\uff0c\u4f60 \u62ff\u56de\u53bb\u5427\uff0c\u4e03\u9634\u6559\u7684\u5f1f\u5b50\u4ee5\u540e\u4e5f\u90fd\u542c\u4f60\u53f7\u4ee4\u3002\u597d\u5566\uff0c\u73e0\u739b\uff0c\u6211\u7684\u5bb6\u5f53\u90fd\u4ea4\u7ed9\u4f60\u4e86\uff0c\u4f60\u597d \u81ea\u4e3a\u4e4b\u5427\u3002\u201d \u3000\u3000\u8981\u77e5\u9634\u5723\u59d1\u53d7\u4e86\u91cd\u4f24\uff0c\u7eb5\u80fd\u82df\u5ef6\u6b8b\u5598\uff0c\u529f\u592b\u5df2\u662f\u51b3\u8ba1\u4e0d\u80fd\u6062\u590d\uff0c\u4eca\u540e\u53ea\u6709\u4ef0\u4ed7\u5929\u9b54 \u6559\u4e3b\u5e87\u62a4\uff0c\u56e0\u6b64\u8feb\u4e8e\u5f62\u52bf\uff0c\u5979\u4e0d\u80fd\u4e0d\u81ea\u52a8\u652f\u51fa\u201c\u5bb6\u5f53\u201d\uff1b\u800c\u5929\u9b54\u6559\u4e3b\u4e4b\u6240\u4ee5\u6551\u5979\uff0c\u4e5f\u6b63 \u662f\u4e3a\u4e86\u8fd9\u4e2a\u76ee\u7684\u3002 \u3000\u3000\u8fd9\u4e00\u8fb9\uff0c\u4e10\u5e2e\u7684\u5f1f\u5b50\u4e5f\u628a\u4ef2\u957f\u7edf\u6276\u4e0b\uff0c\u4ed6\u4eec\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u7ed9\u4ef2\u957f\u7edf\u62b9\u5e72\u8eab\u4e0a\u6240\u6cbe\u7684\u6bd2 \u8840\uff0c\u4f46\u4ece\u9f3b\u5b54\u548c\u4f24\u53e3\u6e17\u8fdb\u53bb\u7684\uff0c\u5df2\u548c\u4ef2\u957f\u7edf\u7684\u8840\u6db2\u6df7\u5408\uff0c\u65e0\u6cd5\u6e05\u9664\u4e86\u3002\u4ef2\u957f\u7edf\u8fd0\u529f\u6297\u6bd2\uff0c \u5140\u662f\u4e0d\u505c\u7684\u76f4\u6253\u5bd2\u98a4\uff0c\u8138\u4e0a\u7684\u9ed1\u6c14\u4e5f\u8d8a\u6765\u8d8a\u6d53\u3002\u9634\u5723\u59d1\u7684\u201c\u6bd2\u8840\u7bad\u201d\u5b9e\u5728\u592a\u5389\u5bb3\u4e86\uff0c\u663e \u7136\u4ee5\u4ef2\u957f\u7edf\u7684\u201c\u6df7\u5143\u4e00\u6c14\u529f\u201d\uff0c\u4ecd\u662f\u62b5\u6297\u4e0d\u4f4f\u3002 \u3000\u3000\u5927\u60b2\u7985\u5e08\u3001\u8427\u9752\u5cf0\u3001\u96f7\u9707\u5b50\u7b49\u4eba\u867d\u7136\u662f\u4ed6\u597d\u53cb\uff0c\u4f46\u4ed6\u4eec\u7684\u529f\u529b\uff0c\u6700\u591a\u4e5f\u4e0d\u8fc7\u4e0e\u4ef2\u957f \u7edf\u76f8\u5f53\uff0c\u4ea6\u662f\u675f\u624b\u65e0\u7b56\u3002 \u3000\u3000\u6b63\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u7a7a\u4e2d\u4f20\u6765\u4e86\u5b8f\u4eae\u7684\u201c\u560e\u560e\u201d\u7684\u9e1f\u9e23\u4e4b\u58f0\u3002\u95e8\u5916\u7684\u5b88\u536b \u5927\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u54ea\u91cc\u6765\u7684\u602a\u9e1f\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u521d\u751f\u4e4b\u728a\u4e0d\u754f\u864e\uff0c\u8981\u663e\u795e\u901a\u6597\u6cd5\u5e08\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e5d\u56de\u3000\u51b0\u5f39\u7389\u5251\u8bdb\u7fa4\u4e11\u3000\u94c1\u638c\u7f61\u98ce\u6597\u6cd5\u738b \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e09\u5341\u4e5d\u56de\u3000\u51b0\u5f39\u7389\u5251\u8bdb\u7fa4\u4e11\u3000\u94c1\u638c\u7f61\u98ce\u6597\u6cd5\u738b \u3000\u3000\u53ea\u89c1\u4e00\u53ea\u7855\u5927\u65e0\u670b\u7684\u5140\u9e70\uff0c\u6b63\u5728\u7a7a\u4e2d\u76d8\u65cb\u800c\u4e0b\u3002\u9e70\u80cc\u4e0a\u5750\u7740\u4e00\u65f6\u5c11\u5e74\u7537\u5973\uff0c\u91d1\u9e70\u5bab \u7684\u6b66\u58eb\u4e0d\u77e5\u4ed6\u4eec\u7684\u6765\u5386\uff0c\u89c1\u4ed6\u4eec\u4e58\u5750\u602a\u9e1f\u98de\u6765\uff0c\u90fd\u5927\u4e3a\u60ca\u9a87\u3002 \u3000\u3000\u6b66\u58eb\u4e2d\u6709\u4e2a\u80fd\u633d\u4e94\u77f3\u5f3a\u5f13\u7684\u795e\u7bad\u624b\uff0c\u201c\u55d6\u201d\u7684\u4e00\u7bad\u5c04\u53bb\uff0c\u90a3\u795e\u9e70\u5f20\u7fc5\u4e00\u6251\uff0c\u8fd9\u679d\u7bad \u6fc0\u5c04\u56de\u6765\uff0c\u90a3\u795e\u7bad\u624b\u4e5f\u88ab\u5de8\u9e70\u6380\u8d77\u7684\u72c2\u98ce\u6251\u5012\uff0c\u53ea\u542c\u5f97\u201c\u5693\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u679d\u7bad\u6fc0\u5c04\u56de\u6765\uff0c \u63d2\u5165\u9752\u7816\u5730\u4e2d\uff0c\u6ca1\u81f3\u7bad\u7fbd\uff0c\u5140\u81ea\u98a4\u52a8\u4e0d\u4f11\uff0c\u79bb\u5f00\u90a3\u6b66\u58eb\u7684\u5934\u90e8\u4e0d\u5230\u4e94\u5bf8\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u548c\u534e\u4e91\u78a7\u8df3\u4e86\u4e0b\u6765\uff0c\u6b66\u571f\u4eec\u53d1\u4e00\u58f0\u558a\uff0c\u5200\u67aa\u5251\u621f\u7eb7\u7eb7\u65ab\u53bb\uff0c\u6c5f\u6d77\u5929\u906e\u7740\u534e\u4e91 \u78a7\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u767b\u65f6\u542c\u5f97\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u5982\u96f7\u9707\u8033\uff0c\u90a3\u4e9b\u5200\u67aa\u5251\u621f\u90fd\u98de\u4e0a\u4e86 \u534a\u7a7a\uff0c\u4e92\u76f8\u6fc0\u649e\u3002\u6b66\u571f\u4eec\u7eb7\u7eb7\u8d70\u907f\uff0c\u552f\u6050\u88ab\u8dcc\u4e0b\u6765\u7684\u5175\u5668\u78b0\u7740\u3002 \u3000\u3000\u91d1\u9e70\u5bab\u7684\u62a4\u6cd5\u5927\u5f1f\u5b50\u51fa\u6765\u559d\u9053\uff1a\u201c\u4f55\u5904\u5996\u4eba\uff0c\u6562\u6765\u653e\u8086\uff1f\u201d\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u6211\u4eec\u5949 \u9080\u800c\u6765\uff0c\u5018\u662f\u5996\u4eba\uff0c\u4f60\u4eec\u5c31\u4e0d\u8be5\u9080\u8bf7\u3002\u201d\u5f53\u4e0b\u4e24\u4eba\u4ea4\u51fa\u8bf7\u5e16\uff0c\u6c5f\u6d77\u5929\u8fd9\u5f20\u662f\u4ed6\u4ee3\u8c37\u4e4b\u534e \u63a5\u7684\u3002\u534e\u4e91\u78a7\u8fd9\u5f20\u5219\u662f\u501f\u7528\u5979\u7236\u4eb2\u7684\u3002 \u3000\u3000\u90a3\u62a4\u6cd5\u5927\u5f1f\u5b50\u63a5\u8fc7\u8bf7\u5e16\u4e00\u770b\uff0c\u8ba4\u5f97\u8fd9\u662f\u4ed6\u7684\u5e08\u7236\u5b9d\u8c61\u6cd5\u5e08\u4eb2\u624b\u6240\u5199\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u7acb \u5373\u6539\u5bb9\u76f8\u5411\uff0c\u65bd\u4e86\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u8d35\u5ba2\u8de8\u9e70\u800c\u6765\uff0c\u6211\u4eec\u610f\u60f3\u4e0d\u5230\uff0c\u8bf7\u6055\u65e0\u672d\uff0c\u8bf7\u8fdb \u6765\u5427\uff01\u201d\u534e\u4e91\u78a7\u6325\u4e86\u6325\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u9e70\u54e5\u54e5\uff0c\u4f60\u5413\u574f\u4e86\u4eba\uff0c\u8d76\u5feb\u7ed9\u6211\u8eb2\u8d77\u6765\u5427\u3002\u201d\u90a3\u5934 \u795e\u9e70\u4f3c\u61c2\u5f97\u5979\u7684\u5429\u5490\uff0c\u5c55\u7fc5\u9ad8\u98de\uff0c\u505c\u5728\u91d1\u5854\u5854\u5c16\u3002 \u3000\u3000\u4f1a\u573a\u8f70\u52a8\u8d77\u6765\uff0c\u4eba\u4eba\u4e89\u770b\u8fd9\u5bf9\u8de8\u9e70\u800c\u6765\u7684\u7537\u5973\u3002\u6c5f\u5357\u8fd9\u4e00\u559c\u975e\u540c\u5c0f\u53ef\uff0c\u5927\u53eb\u9053\uff0c \u201c\u6d77\u513f\uff0c\u6d77\u513f\uff01\u201d\u6c5f\u6d77\u5929\u5e94\u4e86\u4e00\u58f0\uff0c\u5411\u7740\u7236\u4eb2\u5750\u7684\u65b9\u5411\u770b\u53bb\uff0c\u89c1\u7740\u4e86\u59ec\u6653\u98ce\u3001\u5510\u7ecf\u5929\u7b49 \u4eba\uff0c\u968f\u540e\u53c8\u770b\u89c1\u4e86\u8c37\u4e2d\u83b2\uff0c\u5fc3\u9053\uff1a\u201c\u5e08\u7236\u5c1a\u672a\u6765\u5230\uff0c\u5510\u52aa\u73e0\u7a46\u4e5f\u672a\u89c1\u6765\uff0c\u4e0d\u77e5\u662f\u4f55\u7f18\u6545\uff1f\u201d \u8c37\u4e2d\u83b2\u5219\u662f\u53c8\u60ca\u53c8\u559c\uff0c\u53c8\u6709\u51e0\u5206\u7591\u60d1\uff0c\u5bfb\u601d\uff1a\u201c\u8fd9\u5973\u5b50\u662f\u54ea\u91cc\u6765\u7684\uff1f\u6d77\u54e5\u600e\u4f1a\u4e0e\u5979\u540c\u6765\uff1f \u770b\u4ed6\u4eec\u7684\u795e\u60c5\uff0c\u4f3c\u4e4e\u662f\u5f88\u8981\u597d\u7684\u670b\u53cb\uff01\u201d \u3000\u3000\u6587\u5ef7\u58c1\u3001\u5929\u9b54\u6559\u4e3b\u7b49\u4eba\u8bc6\u5f97\u6c5f\u6d77\u5929\u7684\u6765\u5386\uff0c\u65e9\u5df2\u5bf9\u5b9d\u8c61\u6cd5\u5e08\u8bf4\u4e86\uff0c\u5b9d\u8c61\u6cd5\u5e08\u4e5f\u4e0d\u7981 \u5fc3\u5934\u5fae\u51db\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u521a\u624d\u9707\u98de\u5175\u5668\u7684\u529f\u592b\u5927\u662f\u4e0d\u51e1\uff0c\u770b\u6765\u4eca\u65e5\u5728\u573a\u7684\u4eba\uff0c\u9664 \u4e86\u6211\u548c\u73ed\u680b\u4e4b\u5916\uff0c\u8c01\u90fd\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\u3002\u91d1\u4e16\u9057\u7684\u5f92\u5f1f\u5c1a\u4e14\u5982\u6b64\uff0c\u82e5\u662f\u91d1\u4e16\u9057\u4eb2\u6765\uff0c\u5c82\u975e \u51f6\u591a\u5409\u5c11\u3002\u201d\u5f53\u4e0b\u4eb2\u81ea\u51fa\u8fce\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u6c5f\u5c0f\u4fa0\uff0c\u4ee4\u5e08\u91d1\u5927\u4fa0\u5462\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5bb6\u5e08\u6765\u662f\u4e0d\u6765\uff0c\u672a\u66fe\u5411\u6211\u8a00\u53ca\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u4ed6\u8001\u4eba\u5bb6\u7684\u610f\u65e8\u3002\u4e0d\u8fc7\u4e3b \u4eba\u5018\u82e5\u6709\u751a\u8d50\u6559\uff0c\u665a\u8f88\u4e5f\u53ef\u4ee5\u4ee3\u5bb6\u5e08\u63a5\u4e0b\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8fd9\u756a\u8bdd\u7684\u610f\u601d\u662f\u8bf4\uff0c\u5018\u82e5\u5b9d\u8c61\u6cd5\u5e08\u60f3\u627e\u4ed6\u7684\u5e08\u7236\u8f83\u91cf\uff0c\u4ed6\u53ef\u4ee5\u4ee3\u66ff\u5e08\u7236\u63a5\u6218\u3002 \u5b9d\u8c61\u6cd5\u5e08\u867d\u7136\u770b\u51fa\u6c5f\u6d77\u5929\u6b66\u529f\u6781\u9ad8\uff0c\u4f46\u4ed6\u600e\u80af\u81ea\u8d2c\u8eab\u4efd\uff0c\u4e0e\u4e00\u4e2a\u540e\u751f\u5c0f\u5b50\u4ea4\u624b\u3002\u5bfb\u601d\uff1a \u201c\u6211\u53ea\u53ef\u4e0e\u5510\u6653\u6f9c\u3001\u91d1\u4e16\u9057\u4e8c\u4eba\u4e89\u96c4\uff0c\u5bf9\u8fd9\u5c0f\u4e8e\u5374\u662f\u80dc\u4e4b\u4e0d\u6b66\uff0c\u4e0d\u80dc\u4e3a\u7b11\uff0c\u201d\u5f53\u4e0b\u7709\u5934 \u4e00\u76b1\uff0c\u4f6f\u4f5c\u4e0d\u89e3\uff0c\u8bf4\u9053\uff1a\u201c\u8d2b\u50e7\u4ef0\u6155\u4ee4\u5e08\u6b66\u529f\uff0c\u9887\u601d\u7ed3\u7f18\uff0c\u522b\u65e0\u4ed6\u610f\u3002\u5c0f\u5feb\u8fdc\u6765\uff0c\u8bf7\u6682 \u6b47\u7247\u65f6\uff0c\u518d\u4f1a\u6b64\u95f4\u9ad8\u624b\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u662f\u51c6\u5907\u53e6\u5916\u9009\u4eba\u4e0e\u6c5f\u6d77\u5929\u8f83\u91cf\uff0c\u6c5f\u6d77\u5929\u9887\u4e3a\u4e0d\u60a6\uff0c \u4f46\u5374\u4e5f\u4e0d\u4fbf\u518d\u5411\u5b9d\u8c61\u6cd5\u5e08\u6311\u6218\u3002 \u3000\u3000\u534e\u4e91\u78a7\u8d70\u5230\u4ef2\u957f\u7edf\u8eab\u8fb9\uff0c\u4ef2\u957f\u7edf\u6b63\u81ea\u8fd0\u529f\u6297\u6bd2\uff0c\u76f4\u6253\u5bd2\u6218\uff0c\u5927\u60b2\u7985\u5e08\u3001\u96f7\u9707\u5b50\u3001\u8427 \u9752\u5cf0\u7b49\u4eba\u7ad9\u5728\u4e00\u65c1\uff0c\u4f46\u662f\u675f\u624b\u65e0\u7b56\u3002\u534e\u4e91\u78a7\u9053\uff1a\u201c\u4ef2\u53d4\u53d4\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u4ef2\u957f\u7edf\u82e6\u7b11\u9053\uff1a \u201c\u4f60\u7239\u7239\u5462\uff1f\u55ef\uff0c\u4f60\u7239\u7239\u4e0d\u6765\uff0c\u8001\u53eb\u5316\u4ee5\u540e\u6015\u6ca1\u6709\u798f\u6c14\u518d\u5403\u4f60\u505a\u7684\u7f8e\u70b9\u4f73\u80b4\u5566\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u7b11\u9053\uff1a\u201c\u4ef2\u53d4\u53d4\uff0c\u4f60\u80af\u6559\u6211\u7ec3\u6df7\u5143\u4e00\u6c14\u529f\u5417\uff1f\u201d\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u534e\u4e91 \u78a7\u9053\uff1a\u201c\u4f60\u80af\u6559\u6211\uff0c\u5728\u4eca\u540e\u7684\u51e0\u5341\u5e74\uff0c\u4f60\u53ef\u4ee5\u5403\u5c3d\u5929\u4e0b\u7f8e\u5473\u3002\u6211\u5f04\u7684\u83dc\uff0c\u90a3\u5f53\u7136\u66f4\u4e0d\u5728 \u8bdd\u4e0b\u4e86\u3002\u201d\u4ef2\u957f\u7edf\u7cbe\u795e\u4e00\u632f\uff0c\u7b11\u9053\uff0c\u201c\u4f60\u662f\u8d81\u673a\u4f1a\u6572\u6211\u4e00\u8bb0\u4e86\uff1f\u4e5f\u597d\uff0c\u8001\u53eb\u5316\u4e5f\u4e0d\u60f3\u518d \u6d3b\u51e0\u5341\u5e74\uff0c\u53ea\u671b\u6d3b\u7740\u518d\u89c1\u5230\u4f60\u7239\u7239\u5c31\u884c\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u53d6\u51fa\u4e09\u652f\u94f6\u9488\uff0c\u63d2\u8fdb\u4ef2\u957f\u7edf\u7684\u201c\u5927\u690e\u7a74\u201d\u3001\u201c\u5929\u67a2\u7a74\u201d\u548c\u201c\u52b3\u5bab\u7a74\u201d\uff0c\u8fd9\u4e09 \u4e2a\u90fd\u662f\u6b7b\u7a74\uff0c\u65c1\u89c2\u8005\u5927\u5403\u4e00\u60ca\u3002\u8bf4\u4e5f\u5947\u602a\uff0c\u8fd9\u4e09\u652f\u94f6\u9488\u4e00\u63d2\u8fdb\u53bb\uff0c\u4ef2\u957f\u7edf\u7acb\u5373\u4fbf\u4f3c\u8212\u670d \u4e86\u8bb8\u591a\uff0c\u4e5f\u4e0d\u518d\u6253\u5bd2\u98a4\u4e86\u3002\u8fc7\u4e86\u7247\u523b\uff0c\u534e\u4e91\u78a7\u5c06\u94f6\u4ec0\u62d4\u51fa\uff0c\u672c\u6765\u4e2d\u7a7a\u7684\u9488\u7ba1\u5145\u6ee1\u4e86\u7d2b\u9ed1 \u7684\u6bd2\u8840\u3002\u534e\u4e91\u78a7\u63a5\u7740\u53d6\u51fa\u4e24\u5305\u836f\u7c89\uff0c\u4e00\u5305\u5185\u670d\uff0c\u4e00\u5305\u5916\u6577\u3002\u4ef2\u957f\u7edf\u5403\u4e86\u836f\uff0c\u8138\u4e0a\u7684\u9ed1\u6c14 \u4e5f\u90fd\u892a\u5c3d\u4e86\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u7239\u7239\u7684\u672c\u9886\u5168\u90fd\u4f20\u7ed9\u4f60\u4e86\u3002\u606d\u559c\uff0c\u606d\u559c\uff0c\u4e16\u4e0a\u53c8\u591a\u4e86\u4e00\u4e2a\u5973 \u795e\u533b\u3002\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u5176\u5b9e\u4e00\u5927\u534a\u8fd8\u662f\u9760\u4ef2\u53d4\u53d4\u4f60\u7684\u5185\u529f\u6df1\u539a\u3002\u4f84\u5973\u53ea\u7528\u4e24\u5305\u836f\u7c89\u5c31\u6362 \u4e86\u4f60\u7684\u6df7\u5143\u4e00\u6c14\u529f\u771f\u662f\u592a\u4fbf\u5b9c\u4e86\u3002\u201c\u65c1\u89c2\u4f17\u4eba\u89c1\u5979\u836f\u5230\u56de\u6625\uff0c\u65e0\u4e0d\u8d5e\u53f9\uff0c\u7ecf\u4ef2\u957f\u7edf\u4e00\u8bf4\uff0c \u8fd9\u624d\u77e5\u9053\u5979\u662f\u534e\u5c71\u533b\u9690\u534e\u5929\u98ce\u7684\u5973\u513f\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5e26\u4e86\u534e\u4e91\u78a7\u5f80\u89c1\u7236\u4eb2\uff0c\u5728\u4ed6\u7236\u4eb2\u8eab\u65c1\u5750\u4e0b\u3002\u6c5f\u5357\u5df2\u7ecf\u77e5\u9053\u534e\u4e91\u78a7\u4ece\u524d\u6551\u8fc7\u4ed6 \u513f\u5b50\u7684\u4e8b\uff0c\u559c\u5f97\u5408\u4e0d\u62e2\u53e3\uff0c\u4e00\u518d\u5411\u534e\u4e91\u78a7\u9053\u8c22\uff0c\u53c8\u4e0d\u4f4f\u53e3\u7684\u79f0\u8d5e\u5979\u3002\u8c37\u4e2d\u83b2\u4e0e\u534e\u4e91\u78a7\u4e5f \u662f\u4e00\u89c1\u5982\u6545\uff0c\u4e24\u4eba\u8c08\u5f97\u5f88\u662f\u6295\u673a\uff0c\u5012\u628a\u6c5f\u6d77\u5929\u51b7\u843d\u4e86\u3002\u6c5f\u6d77\u5929\u5728\u6b22\u559c\u4e4b\u4e2d\u5374\u53c8\u9690\u9690\u611f\u5230 \u4e0d\u5b89\uff0c\u5fc3\u5934\u4e0a\u4f3c\u4e4e\u8499\u4e86\u4e00\u5c42\u9634\u5f71\u3002 \u3000\u3000\u4e00\u4e2a\u9e70\u9f3b\u5377\u53d1\u7684\u963f\u523a\u6015\u6b66\u58eb\u8d70\u4e86\u51fa\u6765\uff0c\u5495\u5495\u565c\u565c\u4ed6\u8bf4\u4e86\u51e0\u53e5\uff0c\u5b9d\u8c61\u6cd5\u5e08\u8d77\u7acb\u8bf4\u9053\uff1a \u201c\u8fd9\u4f4d\u662f\u963f\u523a\u4f2f\u7b2c\u4e00\u9ad8\u624b\u73ed\u680b\u5148\u751f\uff0c\u4ed6\u8bf4\u4ed6\u4e45\u4ef0\u4e2d\u56fd\u7684\u5510\u6653\u6593\u548c\u91d1\u4e16\u9057\u4e24\u4f4d\u6b66\u5b66\u5927\u5e08\u7684 \u83ab\u540d\uff0c\u610f\u6b32\u8ba8\u6559\u3002\u9057\u61be\u7684\u662f\u8fd9\u4e24\u4f4d\u5927\u5e08\u90fd\u6ca1\u6709\u6765\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u4eba\u662f\u4ece\u524d\u963f\u523a\u4f2f\u6b66\u5b66\u5927\u5b97\u5e08\u63d0\u6469\u8fbe\u591a\u7684\u5e08\u5f1f\uff0c\u63d0\u6469\u8fbe\u591a\u5f53\u5e74\u6765\u534e\uff0c\u66fe\u5148\u540e\u8d25 \u4e8e\u91d1\u3001\u5510\u4e8c\u4eba\u4e4b\u624b\uff0c\u540e\u6765\u5728\u6500\u767b\u73e0\u7a46\u6717\u739b\u5cf0\u7684\u4e00\u6b21\u63a2\u9669\u4e2d\uff0c\u9047\u98ce\u66b4\u4e27\u751f\u3002\u867d\u8bf4\u63d0\u6469\u8fbe\u591a \u4e4b\u6b7b\u4e0e\u4eba\u65e0\u5173\uff0c\u4f46\u4ed6\u90a3\u6b21\u6500\u5c71\u5374\u662f\u7531\u4e8e\u6597\u8d25\u4e4b\u540e\uff0c\u4e3a\u4e86\u901e\u4e00\u65f6\u4e4b\u6c14\u800c\u4e0e\u5510\u6653\u6f9c\u8d4c\u8d5b\u7684\uff0c \u4ed6\u7684\u95e8\u4e0b\u5f1f\u5b50\u8fc1\u6012\u4e8e\u5510\u3001\u91d1\u4e8c\u4eba\uff0c\u56de\u56fd\u4e4b\u540e\uff0c\u52a0\u6cb9\u6dfb\u9171\uff0c\u5411\u5e08\u53d4\u7980\u544a\u3002\u6240\u4ee5\u73ed\u680b\u6b64\u6b21\u51fa \u573a\u3002\u6307\u540d\u8bf4\u662f\u60f3\u4f1a\u5510\u3001\u91d1\u4e8c\u4eba\uff0c\u76ee\u7684\u5c31\u662f\u60f3\u4e3a\u6b7b\u53bb\u7684\u5e08\u5144\u62a5\u4ec7\u96ea\u803b\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8bf4\u8fc7\u613f\u610f\u4ee3\u8868\u5e08\u7236\u63a5\u53d7\u4efb\u4f55\u6311\u6218\uff0c\u5f53\u4e0b\u7acb\u5373\u5e94\u58f0\u800c\u51fa\uff0c\u6b63\u8981\u7b54\u8bdd\uff0c\u5510\u7ecf\u5929\u4ea6 \u5df2\u8d70\u5230\u573a\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u5bb6\u7236\u5df2\u95ed\u95e8\u5c01\u5251\u3002 \u3000\u3000\u51b3\u610f\u7ec8\u8001\u5929\u5c71\uff0c\u4e0d\u518d\u6d89\u8db3\u6c5f\u6e56\uff0c\u66f4\u4e0d\u4f1a\u4e0e\u4eba\u4e89\u80dc\u4e86\u3002\u73ed\u5927\u5e08\u82e5\u80af\u8d50\u6559\uff0c\u665a\u8f88\u613f\u4ee3\u5bb6 \u7236\u63a5\u62db\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5510\u53d4\u53d4\uff0c\u8fd8\u662f\u8ba9\u5c0f\u4f84\u5148\u4e0a\u5427\u3002\u5c0f\u4f84\u82e5\u662f\u4e0d\u6210\uff0c\u5510\u53d4\u53d4\u4f60\u518d\u51fa\u573a\u5982\u4f55\uff1f\u201d \u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u6c5f\u8d24\u4f84\uff0c\u4f60\u6015\u6ca1\u673a\u4f1a\u51fa\u573a\u5417\uff1f\u5148\u6b47\u4e00\u6b47\u5427\u3002\u201d\u539f\u6765\u5510\u7ecf\u5929\u77e5\u9053\u6b64\u4eba\u6765\u5386\uff0c \u4e0d\u653e\u5fc3\u8ba9\u6c5f\u6d77\u5929\u5192\u9669\u3002 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u5c06\u4ed6\u4eec\u4e8c\u4eba\u7684\u8bdd\u8bd1\u6210\u963f\u523a\u4f2f\u8bed\uff0c\u544a\u8bc9\u4e86\u73ed\u680b\uff0c\u540c\u65f6\u4e5f\u8bf4\u660e\u4e86\u5510\u7ecf\u5929\u5728\u4e2d\u56fd \u6b66\u6797\u7684\u8eab\u4efd\u3002\u73ed\u680b\u542c\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u5148\u9886\u6559\u5510\u5c11\u638c\u95e8\u5929\u5c71\u4e00\u6d3e\u7684\u7edd\u4e16\u6b66\u529f\uff0c \u8981\u662f\u4fa5\u5e78\u4e0d\u8d25\uff0c\u518d\u5411\u6c5f\u5c0f\u4fa0\u8bf7\u6559\u3002\u201d \u3000\u3000\u8981\u77e5\u5510\u7ecf\u5929\u7684\u8eab\u4efd\u6bd4\u6c5f\u6d77\u5929\u9ad8\u5f97\u591a\uff0c\u73ed\u680b\u81ea\u662f\u4ee5\u5148\u6253\u8d25\u5510\u7ecf\u5929\u4e3a\u8363\u3002\u5b9d\u8c61\u6cd5\u5e08\u7a0d\u5fae \u6709\u70b9\u5931\u671b\uff0c\u4ed6\u539f\u610f\u662f\u60f3\u501f\u73ed\u680b\u4e4b\u624b\u6765\u632b\u8d25\u6c5f\u6d77\u5929\u7684\uff0c\u5982\u4eca\u73ed\u680b\u8981\u5148\u6597\u5510\u7ecf\u5929\uff0c\u5373\u4f7f\u80fd\u80dc\uff0c \u4e5f\u5fc5\u5b9a\u8981\u8017\u5c3d\u6c14\u529b\uff0c\u90a3\u65f6\u518d\u6597\u6c5f\u6d77\u5929\u5c31\u51c6\u662f\u8d25\u591a\u80dc\u5c11\u4e86\uff0c\u4e0d\u8fc7\u4ed6\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5510\u7ecf\u5929\u4e5f\u662f \u4e00\u5927\u52b2\u654c\uff0c\u82e5\u662f\u73ed\u680b\u80fd\u628a\u5510\u7ecf\u5929\u6253\u8d25\uff0c\u518d\u591a\u5c11\u8017\u635f\u6c5f\u6d77\u5929\u4e00\u70b9\u5185\u529b\uff0c\u90a3\u4e5f\u5f88\u597d\u3002\u56e0\u6b64\uff0c \u4e5f\u4fbf\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u5510\u7ecf\u5929\u62d4\u51fa\u4e86\u6e38\u9f99\u5b9d\u5251\uff0c\u4ed6\u4ee5\u665a\u8f88\u81ea\u5c45\uff0c\u629a\u5251\u65bd\u4e86\u4e00\u793c\uff0c\u4fbf\u5373\u8fdb\u62db\u3002\u73ed\u680b\u7528\u7684\u5175\u5668 \u9887\u4e3a\u5947\u602a\uff0c\u770b\u6765\u50cf\u4e00\u6839\u6746\u68d2\uff0c\u4f46\u9ed1\u9edd\u9edd\u7684\uff0c\u975e\u91d1\u975e\u94c1\uff0c\u5374\u4e0d\u77e5\u662f\u4ec0\u4e48\u4e1c\u897f\u3002 \u3000\u3000\u5510\u7ecf\u5929\u51fa\u624b\u7b2c\u4e00\u62db\u662f\u201c\u6267\u7ecf\u95ee\u96be\u201d\uff0c\u5012\u63d0\u5b9d\u5251\uff0c\u5251\u5c16\u6296\u4e86\u4e24\u6296\uff0c\u659c\u7acb\u80f8\u524d\uff0c\u8fd9\u662f\u5929 \u5c71\u5251\u6cd5\u5927\u987b\u5f25\u5251\u5f0f\u4e2d\u7684\u4e00\u62db\uff0c\u662f\u5411\u5bf9\u65b9\u8868\u793a\u606d\u656c\uff0c\u8bf7\u6c42\u6307\u6559\u7684\u4e00\u4e2a\u5251\u5f0f\uff0c\u4f46\u4ee5\u9759\u5236\u52a8\uff0c \u5176\u4e2d\u53d8\u5316\uff0c\u5374\u662f\u6781\u4e3a\u5965\u5999\u3002\u73ed\u680b\u770b\u51fa\u4ed6\u8fd9\u5251\u5f0f\u7684\u610f\u601d\uff0c\u5c31\u5728\u4ed6\u5251\u5c16\u6296\u52a8\u7684\u65f6\u5019\uff0c\u602a\u68d2\u7acb \u5373\u4f38\u51fa\uff0c\u5411\u4e0a\u4e00\u6311\uff0c\u8868\u793a\u4e0d\u6562\u63a5\u53d7\u5bf9\u65b9\u7684\u656c\u793c\uff0c\u4ed6\u8fd9\u4e00\u6311\uff0c\u770b\u4f3c\u5e73\u6de1\u65e0\u5947\uff0c\u5176\u5b9e\u5374\u662f\u5a01 \u529b\u6781\u5927\u7684\u4e00\u62db\u6740\u624b\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u706b\u661f\u84ec\u98de\uff0c\u5510\u7ecf\u5929\u659c\u8dc3\u4e00\u6b65\uff0c\u73ed\u680b\u4e5f\u6643\u4e86\u4e24\u6643\uff0c\u4e24\u4eba\u7684\u5185\u529b \u5927\u81f4\u76f8\u5f53\uff0c\u53cc\u65b9\u7684\u5175\u5668\u4e5f\u90fd\u4e1d\u6beb\u672a\u635f\u3002 \u3000\u3000\u539f\u6765\u7391\u680b\u8fd9\u6839\u201c\u602a\u68d2\u201d\u975e\u91d1\u975e\u94c1\uff0c\u5374\u662f\u4e00\u5757\u9668\u77f3\u6253\u6210\u7684\uff0c\u786c\u5ea6\u80dc\u4e8e\u4efb\u4f55\u91d1\u5c5e\uff0c\u5510\u7ecf \u5929\u7684\u6e38\u9f99\u5b9d\u5251\u7adf\u662f\u524a\u4e4b\u4e0d\u52a8\uff0c\u8981\u4e0d\u662f\u6536\u52b2\u5f97\u5feb\uff0c\u4e3b\u5251\u8fd8\u9669\u4e9b\u53d7\u635f\u3002 \u3000\u3000\u73ed\u680b\u4e5f\u7981\u4e0d\u4f4f\u5fc3\u5934\u5fae\u51db\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6211\u5e08\u5144\u5f53\u5e74\u8d25\u5728\u5510\u6653\u6f9c\u624b\u4e0b\uff0c\u539f\u6765\u4ed6 \u7684\u513f\u5b50\u5df2\u7ecf\u8fd9\u4e48\u5389\u5bb3\u3002\u4e2d\u534e\u6b66\u5b66\u771f\u662f\u4e0d\u53ef\u5c0f\u89d1\u3002\u201d\u4e0d\u8fc7\u5510\u7ecf\u5929\u7684\u6b66\u529f\u867d\u51fa\u4e4e\u4ed6\u7684\u610f\u5916\uff0c \u4ed6\u5374\u4e5f\u4e0d\u60e7\u3002\u4e24\u4eba\u52a8\u4f5c\u90fd\u5feb\uff0c\u8f6c\u773c\u95f4\u6597\u4e86\u4e09\u5341\u6765\u62db\u3002\u5510\u7ecf\u5929\u6539\u7528\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u201c\u8ffd\u98ce\u5251 \u5f0f\u201d\uff0c\u7aef\u7684\u5feb\u5982\u95ea\u7535\uff0c\u75be\u4f3c\u8ffd\u98ce\uff0c\u524d\u62db\u672a\u6536\uff0c\u540e\u62db\u7eed\u53d1\uff0c\u4e00\u6cbe\u5373\u9000\uff0c\u4e00\u9000\u5373\u6536\u3002\u90a3\u8eab\u6cd5 \u5251\u6cd5\uff0c\u53c8\u4f0a\u5982\u6d41\u6c34\u884c\u4e91\uff0c\u6beb\u65e0\u7c98\u6ede\u3002 \u3000\u3000\u539f\u6765\u5510\u7ecf\u5929\u5df2\u8bd5\u51fa\u73ed\u680b\u7684\u90a3\u6839\u602a\u68d2\u662f\u4ef6\u5b9d\u7269\uff0c\u4e0d\u613f\u4ee4\u81ea\u5df1\u7684\u5b9d\u5251\u53d7\u635f\uff0c\u56e0\u800c\u6539\u7528\u4e86 \u8fd9\u5957\u4e58\u6687\u62b5\u9699\u3001\u8fc5\u6377\u5f02\u5e38\u7684\u8ffd\u98ce\u5251\u5f0f\uff0c\u5373\u7b97\u4e24\u4ef6\u5175\u5668\u78b0\u4e0a\u4e86\u4e5f\u662f\u4e00\u63a0\u5373\u8fc7\uff0c\u5f53\u7136\u5f7c\u6b64\u4e5f \u5c31\u4e0d\u4f1a\u53d7\u5230\u635f\u4f24\u4e86\u3002\u4ed6\u4eec\u4e24\u4eba\u7684\u529f\u529b\u65d7\u9f13\u76f8\u5f53\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u5c31\u53d8\u6210\u4e86\u8c01\u7684\u62db\u6570\u7cbe\u5999\uff0c\u8c01 \u5c31\u53ef\u4ee5\u53d6\u80dc\u7684\u5f62\u52bf\u3002 \u3000\u3000\u5929\u5c71\u5251\u6cd5\u662f\u878d\u4f1a\u5404\u6d3e\u4e4b\u957f\u7684\u4e00\u5957\u535a\u5927\u7cbe\u6df1\uff0c\u65e0\u6240\u4e0d\u5305\u7684\u5251\u6cd5\uff0c\u5510\u7ecf\u5929\u4ee5\u201c\u8ffd\u98ce\u5251\u5f0f\u201d \u4e3b\u653b\uff0c\u4f46\u5374\u4e5f\u5e76\u975e\u5168\u7528\u201c\u8ffd\u98ce\u5251\u5f0f\u201d\uff0c\u4e0d\u65f6\u5939\u6742\u7740\u5176\u4ed6\u5251\u5f0f\u4f7f\u51fa\u3002\u73ed\u680b\u89c1\u4ed6\u5947\u62db\u5999\u7740\uff0c \u5c42\u51fa\u4e0d\u7a77\uff0c\u5012\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u82e5\u4e0d\u4f7f\u51fa\u770b\u5bb6\u672c\u9886\uff0c\u53ea\u6015\u8981\u8d25\u5728\u4ed6\u624b\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u4e00\u5251\u523a\u51fa\uff0c\u73ed\u680b\u8eab\u5f62\u4e00\u6643\uff0c\u5ffd\u5730\u4ec6\u5012\uff0c\u4e2d\u539f\u7fa4\u8c6a\u5927\u58f0\u559d\u5f69\uff0c\u5510\u7ecf\u5929\u5374\u662f\u4e00\u6014\uff0c \u539f\u6765\u4ed6\u90a3\u4e00\u5251\u5e76\u6ca1\u6709\u523a\u4e2d\u73ed\u680b\uff0c\u53ea\u56e0\u53cc\u65b9\u653b\u5b88\u8d8b\u907f\u90fd\u662f\u5feb\u5230\u6781\u70b9\uff0c\u7fa4\u96c4\u770b\u4e0d\u6e05\u695a\uff0c\u5374\u4ee5 \u4e3a\u662f\u73ed\u680b\u4e2d\u5251\u53d7\u4f24\u3002 \u3000\u3000\u5ffd\u89c1\u73ed\u680b\u5355\u638c\u652f\u5730\uff0c\u8eab\u4f3c\u98ce\u8f66\u75be\u8f6c\uff0c\u6253\u4e86\u51e0\u4e2a\u5927\u7ffb\uff0c\u624b\u4e2d\u90a3\u6839\u602a\u68d2\uff0c\u767b\u65f6\u4f3c\u53d8\u6210\u4e86 \u6570\u5341\u767e\u6839\uff0c\u68d2\u5f71\u5982\u5c71\u3002\u56db\u9762\u516b\u65b9\u5411\u5510\u7ecf\u5929\u538b\u6765\u3002\u5510\u7ecf\u5929\u4ece\u672a\u89c1\u8fc7\u8fd9\u79cd\u602a\u62db\uff0c\u6253\u5b9a\u4e86\u201c\u4e0d \u6c42\u6709\u529f\uff0c\u5148\u6c42\u65e0\u8fc7\u201d\u7684\u4e3b\u60eb\uff0c\u6539\u7528\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u62a4\u7740\u5168\u8eab\u3002 \u3000\u3000\u73ed\u680b\u7684\u6253\u6cd5\u8d8a\u6765\u8d8a\u602a\uff0c\u5ffd\u800c\u6253\u4e24\u4e2a\u7b4b\u6597\uff0c\u5ffd\u800c\u5750\u5728\u5730\u4e0a\u6253\u4e24\u4e2a\u76d8\u65cb\uff0c\u6709\u65f6\u751a\u81f3\u5168\u8eab \u8eba\u5728\u5730\u4e0a\uff0c\u4f46\u4e0d\u8bba\u662f\u7ad9\u3001\u662f\u5750\u6216\u662f\u5367\u5012\uff0c\u4ed6\u7684\u90a3\u6839\u602a\u68d2\u90fd\u662f\u914d\u5408\u8eab\u6cd5\uff0c\u4f7f\u5f97\u6070\u5230\u597d\u5904\uff0c \u800c\u4e14\u5728\u68d2\u6cd5\u4e4b\u4e2d\uff0c\u53c8\u5939\u7740\u638c\u5288\u811a\u8e22\u7b49\u7b49\u4e94\u82b1\u516b\u95e8\u7684\u602a\u62db\uff0c\u770b\u4f3c\u51cc\u4e71\u65e0\u7ae0\uff0c\u5b9e\u5219\u62db\u62db\u90fd\u662f \u6740\u624b\u3002\u4e2d\u56fd\u6b66\u5b66\u4e2d\u672c\u6765\u4e5f\u6709\u201c\u9189\u516b\u4ed9\u201d\u62f3\u6cd5\uff0c\u5927\u7565\u76f8\u4f3c\uff0c\u4f46\u5374\u4e5f\u6ca1\u6709\u4ed6\u8fd9\u5957\u529f\u592b\u7684\u602a\u5230 \u51fa\u4e4e\u60f3\u8c61\u4e4b\u5916\u3002 \u3000\u3000\u5e78\u800c\u5510\u7ecf\u5929\u7684\u201c\u5927\u987b\u5f25\u5251\u5f0f\u201d\u662f\u5929\u4e0b\u9632\u5b88\u5f97\u6700\u4e25\u5bc6\u7684\u5251\u6cd5\uff0c\u4ed6\u53ea\u5b88\u4e0d\u653b\uff0c\u4fe8\u5982\u5728\u5468 \u56f4\u5e03\u4e0b\u4e86\u4e00\u9053\u94c1\u58c1\u94dc\u5899\uff0c\u73ed\u680b\u7684\u602a\u62db\u867d\u602a\uff0c\u5374\u4e5f\u653b\u4e0d\u8fdb\u53bb\u3002 \u3000\u3000\u53ef\u662f\u5927\u987b\u5f25\u5251\u5f0f\u751a\u4e3a\u8017\u635f\u5185\u529b\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5750\u5728\u573a\u8fb9\u7684\u4eba\u5df2\u9690\u9690\u53ef\u4ee5\u542c\u5230\u5510\u7ecf\u5929\u7684 \u5598\u6c14\u58f0\uff0c\u4e0d\u7981\u6697\u6697\u4e3a\u4ed6\u62c5\u5fc3\u3002\u6b63\u81ea\u6597\u5230\u7d27\u5904\uff0c\u73ed\u680b\u5ffd\u5730\u4e00\u8dc3\u800c\u8d77\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u7a81\u7136\u95f4\u53cc \u65b9\u90fd\u9759\u6b62\u4e0b\u6765\uff0c\u9762\u5bf9\u9762\u7ad9\u7740\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u5c31\u4f3c\u4e24\u5c0a\u77f3\u50cf\uff01 \u3000\u3000\u4f17\u4eba\u5927\u4e3a\u8be7\u5f02\uff0c\u5b9a\u775b\u770b\u65f6\uff0c\u53ea\u89c1\u5510\u7ecf\u5929\u7684\u6e38\u9f99\u5251\u62b5\u7740\u73ed\u680b\u7684\u68d2\u7aef\uff0c\u53cc\u65b9\u53f3\u81c2\u5e73\u4f38\uff0c \u770b\u6765\u4f3c\u662f\u529f\u529b\u6089\u654c\uff0c\u8c01\u90fd\u4e0d\u80fd\u5411\u524d\u79fb\u52a8\u534a\u6b65\u3002 \u3000\u3000\u539f\u6765\u73ed\u680b\u867d\u7136\u6682\u65f6\u5360\u4e86\u4e0a\u98ce\uff0c\u4f46\u4ed6\u4ea6\u81ea\u77e5\uff0c\u53ea\u51ed\u602a\u62db\uff0c\u51b3\u96be\u53d6\u80dc\u3002\u4ed6\u7528\u8fd9\u5957\u602a\u62db\uff0c \u76ee\u7684\u4e0d\u8fc7\u5728\u8017\u635f\u5510\u7ecf\u5929\u6c14\u529b\u800c\u5df2\u3002\u5f85\u542c\u5230\u5510\u7ecf\u65e0\u5fae\u5fae\u5598\u6c14\uff0c\u4ee5\u4e3a\u65f6\u673a\u5df2\u81f3\uff0c\u4e8e\u662f\u7acb\u65f6\u6539 \u53d8\u6218\u672f\uff0c\u5f3a\u8feb\u5510\u7ecf\u592d\u4e0e\u4ed6\u62fc\u6597\u5185\u529b\u3002\u4ed6\u7684\u602a\u68d2\u542b\u6709\u5c11\u91cf\u78c1\u6027\uff0c\u5510\u7ecf\u5929\u7528\u4ee5\u9632\u5b88\u7684\u5927\u987b\u5f25 \u5251\u5f0f\uff0c\u53c8\u4e0d\u53ca\u8ffd\u98ce\u5251\u5f0f\u7684\u8fc5\u6377\uff0c\u5b9d\u5251\u88ab\u4ed6\u7684\u602a\u68d2\u4e00\u7c98\uff0c\u672a\u80fd\u7acb\u5373\u6446\u8131\uff0c\u4ed6\u7684\u5185\u529b\u5df2\u662f\u900f \u8fc7\u68d2\u7aef\uff0c\u8feb\u5f97\u5510\u7ecf\u5929\u518d\u4e5f\u4e0d\u80fd\u64a4\u9000\u4e86\u3002 \u3000\u3000\u73ed\u680b\u81ea\u4ee5\u4e3a\u80dc\u7b97\u5728\u63e1\uff0c\u54ea\u77e5\u4ed6\u7684\u5185\u529b\u9010\u6e10\u52a0\u5f3a\uff0c\u5230\u6700\u540e\u5df1\u662f\u4f7f\u51fa\u4e86\u5341\u6210\u529f\u529b\uff0c\u8fde\u51b2 \u51e0\u6b21\uff0c\u4ecd\u662f\u672a\u80fd\u5c06\u5510\u7ecf\u5929\u8feb\u9000\u4e00\u6b65\u3002\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u539f\u6765\u5510\u7ecf\u5929\u7684\u5598\u6c14\u4e43\u662f\u8bf1\u654c\u4e4b\u8ba1\uff0c\u4ed6\u7684\u5927\u987b\u5f25\u5251\u5f0f\u9887\u8017\u771f\u529b\uff0c\u4ed6\u4e5f\u5bb3\u6015\u73ed\u680b\u7684\u602a\u62db \u5c42\u51fa\u4e0d\u7a77\uff0c\u65f6\u5019\u4e45\u4e86\uff0c\u53ea\u4f2f\u9632\u5fa1\u7a0d\u6709\u758f\u5ffd\uff0c\u4fbf\u4f1a\u7ed9\u4ed6\u653b\u5165\uff1a\u4e0d\u5982\u8d81\u7740\u5185\u529b\u5c1a\u672a\u8017\u635f\u5927\u591a \u4e4b\u65f6\uff0c\u53ca\u65e9\u548c\u4ed6\u89c1\u4e2a\u771f\u7ae0\u3002\u6070\u597d\u73ed\u680b\u4e5f\u5bb3\u6015\u65f6\u5019\u4e45\u4e86\uff0c\u602a\u62db\u7ed9\u5bf9\u65b9\u770b\u51fa\u7834\u7efd\uff0c\u4fbf\u4e0d\u80fd\u7528\u3002 \u56e0\u800c\u53cc\u65b9\u62b1\u7740\u540c\u4e00\u5fc3\u601d\uff0c\u7ec8\u4e8e\u7531\u73ed\u680b\u5148\u884c\u53d1\u96be\uff0c\u51fa\u73b0\u4e86\u6700\u60ca\u9669\u7684\u4e24\u5927\u9ad8\u624b\u8f83\u91cf\u5185\u529f\u7684\u5c40 \u9762\u3002 \u3000\u3000\u5510\u7ecf\u5929\u53ea\u89c9\u5bf9\u65b9\u7684\u5185\u529f\u4fe8\u5982\u6392\u5c71\u5012\u6d77\uff0c\u6c79\u6d8c\u800c\u6765\uff0c\u5c3d\u7ba1\u9632\u5b88\u5f97\u4f4f\uff0c\u4e5f\u4e0d\u7981\u6697\u6697\u5fc3\u60ca\u3002 \u73ed\u680b\u5c61\u653b\u4e0d\u4e0b\uff0c\u4e5f\u611f\u5230\u5bf9\u65b9\u7684\u5185\u529f\u4f3c\u662f\u6df1\u4e0d\u53ef\u6d4b\u3002\u53cc\u65b9\u90fd\u662f\u6697\u91cc\u53eb\u82e6\u3002 \u3000\u3000\u53ea\u542c\u5f97\u55e4\u55e4\u58f0\u54cd\uff0c\u5510\u7ecf\u5929\u7684\u5251\u5c16\u4e0a\u7206\u51fa\u70b9\u70b9\u706b\u82b1\uff0c\u73ed\u680b\u7684\u68d2\u7aef\u4e5f\u53d1\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\u3002 \u770b\u6765\u53cc\u65b9\u7684\u771f\u529b\u90fd\u5728\u5927\u91cf\u6d88\u8017\u4e4b\u4e2d\uff0c\u800c\u4e24\u4ef6\u7a00\u4e16\u5947\u73cd\u3002\u4e5f\u5728\u7531\u4e8e\u4e92\u76f8\u6469\u64e6\u800c\u9010\u6e10\u4f24\u635f\u3002 \u4e24\u65b9\u7684\u4eb2\u53cb\u90fd\u662f\u6035\u76ee\u60ca\u5fc3\uff0c\u53ea\u6015\u4e24\u5927\u9ad8\u624b\uff0c\u4e24\u4ef6\u5947\u73cd\u3001\u90fd\u8981\u906d\u5230\u4e24\u8d25\u4ff1\u4f24\u7684\u52ab\u96be\u3002 \u3000\u3000\u5ffd\u5730\u91cc\u4e00\u6761\u4eba\u5f71\u75be\u5982\u98de\u9e1f\u7684\u201c\u98de\u201d\u5165\u573a\u5fc3\uff0c\u73ed\u680b\u7684\u51e0\u4e2a\u5f1f\u5b50\u5927\u5403\u4e00\u60ca\uff0c\u7eb7\u7eb7\u547c\u559d\uff0c \u5c31\u5728\u559d\u9a82\u58f0\u4e2d\uff0c\u53ea\u89c1\u767d\u5149\u4e00\u95ea\uff0c\u5510\u7ecf\u5929\u4e0e\u73ed\u680b\u5df2\u662f\u500f\u5730\u5206\u5f00\u3002\u5510\u7ecf\u5929\u7eb3\u5251\u5f52\u9798\uff1b\u8bf4\u9053\uff1a \u201c\u8d24\u4f84\uff0c\u591a\u8c22\u4f60\u4e86\uff01\u201d\u73ed\u680b\u6536\u4e86\u602a\u68d2\uff0c\u4e5f\u5728\u5411\u90a3\u4eba\u65bd\u793c\uff0c\u7528\u963f\u523a\u4f2f\u8bed\u8bf4\u4e86\u4e00\u58f0\u201c\u591a\u8c22\u201d\u3002 \u968f\u5373\u65a5\u4ed6\u90a3\u51e0\u4e2a\u5f1f\u5b50\u9053\uff1a\u201c\u4f60\u4eec\u80e1\u95f9\u4ec0\u4e48\uff0c\u5feb\u7ed9\u6211\u6eda\u4e0b\u53bb\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u4eba\u6b63\u662f\u6c5f\u6d77\u5929\uff0c\u4ed6\u7528\u88c1\u4e91\u5b9d\u5251\u5728\u4e24\u4eba\u5175\u5668\u76f8\u4ea4\u4e4b\u5904\u4e00\u6311\uff0c\u7531\u4e8e\u4ed6\u7684\u529f\u529b\u6bd4\u5510\u3001 \u73ed\u4e8c\u4eba\u90fd\u80dc\u4e00\u7b79\uff0c\u7528\u52b2\u53c8\u7528\u5f97\u975e\u5e38\u5de7\u5999\uff0c\u8f7b\u8f7b\u4e00\u6311\uff0c\u4fbf\u628a\u4e24\u4eba\u7684\u5185\u529b\u622a\u65ad\uff0c\u540c\u65f6\u4e5f\u5c31\u628a \u8fd9\u4e24\u6837\u5175\u5668\u5206\u5f00\u3002\u4ed6\u8fd9\u4e00\u6311\uff0c\u53ea\u662f\u60f3\u89e3\u5f00\u4e24\u4eba\u7684\u82e6\u6597\uff0c\u51b3\u4e0d\u504f\u62c5\u4efb\u4f55\u4e00\u65b9\u3002\u73ed\u680b\u7684\u5f1f\u5b50 \u4e0d\u77e5\uff0c\u6545\u6b64\u559d\u9a82\u3002\u73ed\u3001\u5510\u4e8c\u4eba\u8499\u4ed6\u89e3\u6551\uff0c\u5f53\u7136\u80fd\u591f\u5bdf\u89c9\uff0c\u662f\u4ee5\u4e0d\u7ea6\u800c\u540c\u7684\u5411\u4ed6\u9053\u8c22\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5411\u73ed\u680b\u62f1\u4e86\u62f1\u624b\uff0c\u9053\u58f0\uff1a\u201c\u4f69\u670d\uff01\u201d\u4fbf\u5373\u56de\u5ea7\u3002\u4ed6\u548c\u73ed\u680b\u4e4b\u6218\uff0c\u529f\u529b\u6089\u654c\uff0c \u8c01\u90fd\u53ef\u4ee5\u770b\u5f97\u51fa\u6765\uff0c\u6240\u4ee5\u4ed6\u8fd9\u4e00\u58f0\u201c\u4f69\u670d\u201d\uff0c\u8c01\u4e5f\u90fd\u77e5\u9053\u662f\u4e00\u53e5\u5ba2\u6c14\u7684\u8bf4\u8bdd\uff0c\u6ca1\u4eba\u6562\u8bf4 \u4ed6\u80c6\u6cd5\u907f\u6218\u3002\u4f46\u5510\u7ecf\u5929\u53ef\u4ee5\u56de\u5ea7\uff0c\u73ed\u680b\u5374\u4e0d\u80fd\u56eb\u5ea7\uff0c\u5510\u7ecf\u5929\u4e00\u8d70\uff0c\u4ed6\u7ad9\u5728\u573a\u4e2d\uff0c\u795e\u8272\u66f4 \u663e\u5f97\u5c34\u5c2c\u3002 \u3000\u3000\u8981\u77e5\u73ed\u680b\u6709\u8a00\u5728\u5148\uff0c\u4ed6\u662f\u8981\u5148\u6597\u5510\u7ecf\u5929\uff0c\u518d\u6597\u6c5f\u6d77\u5929\u7684\u3002\u8981\u662f\u4ed6\u8d25\u7ed9\u5510\u7ecf\u5929\u90a3\u4e5f\u7f62 \u4e86\uff0c\u5982\u4eca\u5374\u662f\u4e2a\u4e0d\u80dc\u4e0d\u8d25\u7684\u548c\u5c40\uff0c\u4ee5\u4ed6\u7684\u8eab\u4efd\uff0c\u81ea\u7136\u5e94\u5f53\u5c65\u884c\u524d\u7ea6\uff0c\u518d\u548c\u6c5f\u6d77\u5929\u4ea4\u624b\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u521a\u624d\u90a3\u4e48\u6325\u5251\u4e00\u6311\uff0c\u8f7b\u63cf\u6de1\u5199\u7684\u5c31\u5c06\u4ed6\u4eec\u4e8c\u4eba\u5206\u5f00\uff0c\u73ed\u680b\u54ea\u91cc\u8fd8\u6562\u4e1d\u6beb\u8f7b\u6545\uff0c \u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u867d\u7136\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u58f0\u540d\u4e0d\u54cd\uff0c\u4f46\u4ee5\u4ed6\u7684\u529f\u529b\u800c\u8bba\uff0c\u53ea\u6015\u8fd8\u5728\u5510\u7ecf\u5929\u4e4b \u4e0a\uff01\u201d\u4f46\u4ed6\u662f\u4f55\u7b49\u8eab\u4efd\uff0c\u867d\u7136\u5fc3\u4e2d\u9690\u6709\u60e7\u610f\uff0c\u5374\u4e5f\u4e0d\u613f\u81ea\u98df\u524d\u8a00\uff0c\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u4e45\u4ef0\u4ee4 \u5e08\u91d1\u5927\u4fa0\u6b66\u529f\u76d6\u4e16\uff0c\u540d\u5e08\u51fa\u9ad8\u5f92\uff0c\u4eca\u65e5\u4e0e\u6c5f\u5c0f\u4fa0\u5e78\u4f1a\uff0c\u8fd8\u671b\u6307\u6559\u3002\u201d\u4ed6\u5c3d\u91cf\u62ac\u9ad8\u91d1\u4e16\u9057 \u5e08\u5f92\u7684\u8eab\u4efd\uff0c\u4e43\u662f\u9884\u5148\u7559\u4e2a\u5730\u6b65\uff0c\u514d\u5f97\u5931\u6548\u4e4b\u540e\uff0c\u592a\u8fc7\u96be\u582a\u3002 \u3000\u3000\u901a\u8bd1\u7684\u5c06\u4ed6\u7684\u8bdd\u5411\u6c5f\u6d77\u5929\u8bf4\u4e86\uff0c\u6c5f\u6d77\u5929\u5374\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8bf7\u4f60\u544a\u8bc9\u73ed\u5927\u5e08\uff0c\u6211\u4e0d\u60f3\u5360\u4ed6\u7684\u4fbf\u5b9c\uff0c\u4ed6\u5df2\u6253\u4e86\u4e00\u573a\uff0c\u8bf7\u4ed6\u5148\u6b47\u606f\u8fc7\u4e86\uff0c\u5f85\u6211\u4e5f \u6253\u4e86\u4e00\u573a\u4e4b\u540e\uff0c\u90a3\u65f6\u53cc\u65b9\u5404\u4e0d\u5403\u4e8f\uff0c\u6211\u518d\u5411\u4ed6\u8bf7\u6559\u3002\u201d\u73ed\u680b\u542c\u4e86\u4ed6\u7684\u8bdd\uff0c\u5927\u51fa\u610f\u5916\uff0c\u8bf4 \u9053\uff1a\u201c\u597d\uff0c\u6c5f\u5c0f\u4fa0\u679c\u7136\u662f\u82f1\u96c4\u672c\u8272\uff0c\u4f69\u670d\uff0c\u4f69\u670d\uff01\u65e2\u7136\u6c5f\u5c0f\u4fa0\u5b9a\u800d\u5982\u6b64\uff0c\u73ed\u680b\u4e5f\u53ea\u597d\u9075 \u547d\u4e86\u3002\u201d\u5f53\u4e0b\u4fbf\u5373\u56de\u5ea7\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u76ee\u6ce8\u5b9d\u8c61\u6cd5\u5e08\u3001\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u6c5f\u6d77\u5929\uff0c\u8bda\u5fc3\u5411\u524d\u8f88\u9ad8\u4eba\u8ba8\u6559\uff0c\u8bf7\u54ea\u4f4d\u8d50\u62db\uff01\u201d \u5b9d\u8c61\u6cd5\u5e08\u4e0d\u60f3\u4fbf\u5373\u63a5\u53d7\u4ed6\u7684\u6311\u6218\uff0c\u7709\u5934\u4e8c\u76b1\uff0c\u6b63\u81ea\u8e0c\u8e87\uff0c\u4e0d\u77e5\u8981\u9009\u6d3e\u8c01\u4eba\u51fa\u6765\u5e94\u654c\u624d\u597d\uff0c \u5ffd\u542c\u5f97\u4e00\u4e2a\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u518d\u6765\u4f1a\u4e00\u4f1a\u91d1\u4e16\u9057\u7684\u9ad8\u8db3\uff0c\u8fd9\u6b21\u54b1\u4eec\u53ef\u5f97\u5206\u4e2a\u80dc\u8d1f\u4e86\u3002\u201d \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u8eab\u4f53\u9b41\u68a7\uff0c\u6ee1\u9762\u7ea2\u5149\u7684\u5587\u561b\u50e7\u8d70\u51fa\u573a\u6765\uff0c\u5b9d\u8c61\u6cd5\u5e08\u5927\u559c\uff0c\u5fc3\u9053\uff1a\u201c\u600e\u4e48\u60f3 \u4e0d\u8d77\u4ed6\u3002\u201d\u539f\u6765\u8fd9\u5587\u561b\u50e7\u6b63\u662f\u9752\u6d77\u9102\u514b\u6c81\u5b98\u767d\u6559\u6cd5\u4e3b\u7684\u5e08\u5f1f\u5b54\u96c0\u660e\u4f26\u738b\u3002 \u3000\u3000\u767d\u6559\u6cd5\u738b\u66fe\u7ecf\u548c\u91d1\u4e16\u9057\u6253\u8fc7\u5e73\u624b\uff0c\u5b9d\u8c61\u6cd5\u5e08\u5df2\u7ecf\u77e5\u9053\u4e86\u7684\uff0c\u5fc3\u60f3\uff1a\u201c\u5b54\u96c0\u660e\u4f26\u738b\u6b66 \u529f\u7eb5\u7136\u4e0d\u53ca\u5e08\u5144\uff0c\u60f3\u6765\u4e5f\u4e0d\u81f3\u4e8e\u5dee\u5f97\u592a\u8fdc\uff0c\u8bf4\u4e0d\u5b9a\u53ef\u4ee5\u65e0\u9700\u73ed\u680b\uff0c\u53ea\u662f\u4ed6\u5c31\u53ef\u4ee5\u5c06\u91d1\u4e16 \u9057\u7684\u5f92\u5f1f\u6253\u8d25\u4e86\u3002\u201d\u4ed6\u54ea\u91cc\u77e5\u9053\uff0c\u91d1\u4e16\u9057\u73b0\u5728\u7684\u6b66\u529f\uff0c\u6bd4\u8d77\u5f53\u5e74\u6597\u767d\u6559\u6cd5\u738b\u4e4b\u65f6\uff0c\u5df2\u4e0d \u77e5\u9ad8\u4e86\u591a\u5c11\uff0c\u800c\u6c5f\u6bcf\u5929\u7684\u6b66\u529f\uff0c\u4e5f\u5df2\u5dee\u4e0d\u591a\u53ef\u4ee5\u4e0e\u5e08\u7236\u6bd4\u80a9\u4e86\u3002\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u4e24\u4e2a\u6708\u4e4b\u524d\uff0c\u66fe\u7ecf\u4e0e\u6c5f\u6d77\u5929\u5728\u9102\u514b\u6c81\u5bab\u4ea4\u8fc7\u51e0\u62db\uff0c\u968f\u540e\u5510\u7ecf\u5929\u592b\u5987\u5230\u6765\uff0c \u4fbf\u5373\u7f62\u624b\uff0c\u4f46\u5728\u90a3\u51e0\u62db\u4e4b\u4e2d\uff0c\u5374\u662f\u5b54\u96c0\u660e\u4f26\u738b\u5360\u4e86\u4e0a\u98ce\u7684\uff0c\u4ed6\u5fc3\u60f3\u53ea\u6709\u4e24\u4e2a\u6708\u7684\u8ddd\u79bb\uff0c \u6c5f\u6d77\u5929\u6b66\u529f\u5982\u4f55\u7cbe\u8fdb\u4e5f\u51b3\u4e0d\u80fd\u80dc\u8fc7\u4e86\u4ed6\uff0c\u56e0\u800c\u4e5f\u5c31\u4e0d\u600e\u4e48\u628a\u6c5f\u6d77\u5929\u653e\u5728\u773c\u5185\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7ad9\u5728\u4e0b\u9996\uff0c\u53cc\u624b\u8d34\u7740\u819d\u76d6\uff0c\u8fd9\u662f\u4ee5\u540e\u8f88\u81ea\u5c45\uff0c\u5411\u524d\u8f88\u8bf8\u6559\u7684\u610f\u601d\uff0c\u5b54\u96c0\u660e\u4f26 \u738b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u4f60\u4eae\u5251\u5427\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0a\u4eba\u672a\u643a\u6cd5\u6756\uff0c\u665a\u8f88\u7109\u6562\u52a8\u7528\u5175 \u5203\uff1f\u201d\u8bc6\u5f97\u5b54\u96c0\u660e\u4f26\u738b\u6765\u5386\u7684\u4eba\uff0c\u542c\u4e86\u6c5f\u6d77\u5929\u5982\u6b64\u56de\u7b54\uff0c\u90fd\u662f\u5927\u5403\u4e00\u60ca\uff0c\u5728\u4ed6\u4eec\u5fc3\u76ee\u4e4b \u4e2d\uff0c\u6c5f\u6d77\u5929\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u867d\u7136\u662f\u91d1\u4e16\u9057\u5f1f\u5b50\uff0c\u4f46\u80fd\u6709\u51e0\u5e74\u529f\u529b\uff1f\u5f53\u7136\u662f\u51b3\u4e0d\u80fd\u4e0e\u5b54\u96c0\u660e\u4f26 \u738b\u76f8\u6bd4\u3002\u56e0\u6b64\u51e0\u4e4e\u6bcf\u4e00\u4e2a\u4eba\u90fd\u662f\u8fd9\u4e48\u60f3\uff1a\u201c\u5018\u82e5\u4ed6\u52a8\u7528\u5b9d\u5251\uff0c\u6216\u8005\u8fd8\u53ef\u4ee5\u4ed7\u5230\u62a4\u8eab\uff0c\u4e0d \u81f3\u4e8e\u8f93\u5f97\u592a\u60e8\uff0c\u5982\u4eca\u7a7a\u624b\u8fc7\u62db\uff0c\u8fd9\u5c82\u4e0d\u662f\u8981\u81ea\u9001\u4e86\u4e00\u6761\u5c0f\u547d\uff01\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u7684\u6559\u4e3b\u6cd5\u6756\u65e9\u5df2\u88ab\u5e08\u5144\u7f34\u56de\uff0c\u4ed6\u79bb\u5f00\u9102\u514b\u6c81\u5bab\u4e4b\u540e\uff0c\u4e00\u6c14\u4e4b\u4e0b\uff0c\u8fde\u65e5\u5e38 \u6240\u7528\u7684\u4e5d\u73af\u9521\u6756\u4e5f\u629b\u5f03\u4e86\uff0c\u51b3\u5fc3\u5230\u5c3c\u6cca\u5c14\u4e4b\u540e\uff0c\u81ea\u7acb\u4e3a\u6559\u4e3b\uff0c\u518d\u89c5\u7384\u94c1\u7cbe\u91d1\uff0c\u6253\u8fc7\u4e00\u6761 \u53ea\u6709\u6559\u4e3b\u80fd\u7528\u7684\u6cd5\u6756\u3002\u5176\u5b9e\uff0c\u5373\u7b97\u4ed6\u7684\u4e5d\u73af\u9521\u6756\u672a\u66fe\u629b\u5f03\uff0c\u4ed6\u4e5f\u4e0d\u613f\u610f\u7528\u6765\u5bf9\u4ed8\u4e00\u4e2a\u540e \u751f\u5c0f\u5b50\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u63d0\u53ca\u6cd5\u6756\uff0c\u53c8\u89e6\u4e86\u4ed6\u7684\u96f6\u5934\uff0c\u66f4\u4e3a\u6124\u6012\uff0c\u5f53\u4e0b\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5fc3\u9053\uff1a\u201c\u65e0\u77e5 \u5c0f\u5b50\uff0c\u72c2\u5984\u5982\u65af\uff0c\u4e0d\u53eb\u4f60\u5f53\u573a\u51fa\u4e11\u3002 \u3000\u3000\u4f60\u4e5f\u4e0d\u77e5\u6211\u7684\u5389\u5bb3\uff01\u201d\u9762\u8272\u4e00\u6c89\uff0c\u4fbf\u5373\u4f38\u51fa\u98df\u6307\uff0c\u5411\u6c5f\u6d77\u5929\u9065\u9065\u4e00\u6233\u3002 \u3000\u3000\u4ed6\u4eec\u4e8c\u4eba\u76f8\u8ddd\u4e8c\u5927\u6709\u4f59\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u4f38\u6307\u4e00\u6233\uff0c\u53ea\u542c\u5f97\u55e4\u55e4\u58f0\u54cd\uff0c\u52b2\u98ce\u5982\u7bad\uff0c\u76f4\u5c04 \u6c5f\u6d77\u5929\u80f8\u53e3\u7684\u201c\u7487\u7391\u7a74\u201d\u3002\u5b54\u96c0\u660e\u4f26\u738b\u5df2\u7ec3\u6210\u65e0\u5f62\u7684\u7f61\u6c14\uff0c\u53ef\u4ee5\u5728\u6570\u4e08\u4e4b\u5916\u6740\u4eba\uff0c\u7528\u6765 \u70b9\u7a74\uff0c\u90a3\u5c31\u662f\u6700\u5389\u5bb3\u7684\u9694\u7a7a\u70b9\u70b9\u7a74\u529f\u592b\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u795e\u8272\u81ea\u5982\uff0c\u6697\u6697\u597d\u7b11\uff0c\u5374\u4f6f\u4f5c\u4e0d\u77e5\uff0c\u4ecd\u7136\u606d\u606d\u656c\u656c\u4ed6\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6562\uff0c\u8bf7 \u4e0a\u4eba\u5148\u51fa\u9ad8\u62db\uff01\u201d\u5b54\u96c0\u660e\u4f26\u738b\u89c1\u4ed6\u5140\u7136\u4e0d\u52a8\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u70b9\u5341\u6570\u70b9\uff0c\u904d\u88ad\u4ed6\u5468\u8eab\u5404\u5904 \u5927\u7a74\u3002\u6c5f\u6d77\u5929\u9ed8\u8fd0\u7384\u529f\uff0c\u4ed6\u7684\u7f61\u6c14\u54ea\u80fd\u4fb5\u5165\u3002 \u3000\u3000\u6c5f\u5357\u563b\u563b\u7b11\u9053\uff1a\u201c\u6211\u7684\u513f\u5b50\u8bf7\u4f60\u6307\u70b9\uff0c\u4f60\u5f53\u771f\u5c31\u53ea\u662f\u6307\u6307\u70b9\u70b9\u4e48\uff1f\u201d\u59ec\u6653\u98ce\u7b11\u9053\uff1a \u201c\u6211\u770b\u4f60\u4e0d\u5fc5\u88c5\u6a21\u4f5c\u4f6f\u4e86\uff0c\u8fd8\u662f\u597d\u597d\u7684\u8bf7\u6211\u7684\u4f84\u513f\u6307\u70b9\u6307\u70b9\u5427\uff01\u201d \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u8001\u7f9e\u6210\u6012\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u8eab\u5f62\u75be\u8d77\uff0c\u5230\u4e86\u6c5f\u6d77\u5929\u9762\u524d\uff0c\u4e00\u638c\u4fbf\u5288\u8fc7\u53bb\u3002\u6c5f \u6d77\u5929\u7ffb\u638c\u4e00\u8fce\uff0c\u53ea\u89c9\u624b\u5fc3\u5fae\u5fae\u4e00\u70eb\u3002\u5b54\u96c0\u660e\u4f26\u738b\u4ee5\u7f61\u6c14\u51dd\u805a\u638c\u4e0a\uff0c\u6bd4\u6b27\u9633\u4ef2\u548c\u7684\u9739\u96f3\u638c \u548c\u96f7\u795e\u6307\u8fd8\u8981\u5389\u5bb3\u5f97\u591a\uff0c\u6c5f\u6d77\u5929\u4ece\u524d\u548c\u4ed6\u5bf9\u8fc7\u4e00\u638c\uff0c\u5f53\u65f6\u53ea\u89c9\u5982\u89e6\u70e7\u7ea2\u7684\u70d9\u94c1\uff0c\u4f46\u73b0\u5728 \u53ea\u662f\u6709\u70b9\u5fae\u70eb\u7684\u611f\u89c9\uff0c\u8fd9\u662f\u7531\u4e8e\u4ed6\u670d\u98df\u4e86\u5929\u5fc3\u77f3\u4e4b\u540e\uff0c\u529f\u529b\u5927\u5927\u589e\u5f3a\u7684\u7f18\u6545\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u53ea\u662f\u611f\u5230\u5fae\u5fae\u4e00\u70eb\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u5374\u611f\u5230\u5bf9\u65b9\u7684\u5185\u529b\u5982\u6f6e\u6d8c\u6765\uff0c\u7adf\u628a\u4ed6\u7f61\u6c14\u51b2 \u5f97\u5012\u9000\u56de\u53bb\uff0c\u8fd9\u4e00\u60ca\u66f4\u662f\u975e\u540c\u5c0f\u53ef\uff01 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u7528\u4e86\u5341\u5e74\u82e6\u529f\uff0c\u624d\u7ec3\u6210\u8fd9\u65e0\u5f62\u7f61\u6c14\uff0c\u5e73\u65f6\u552f\u6068\u7f61\u6c14\u7ec3\u5f97\u8fd8\u4e0d\u591f\u5f3a\uff0c\u8fd9\u65f6 \u5374\u552f\u6050\u5b83\u53cd\u800c\u4f24\u4e86\u81ea\u5df1\uff0c\u6068\u4e0d\u5f97\u5b83\u8d8a\u5f31\u8d8a\u597d\u3002 \u3000\u3000\u5728\u6c5f\u6d77\u5929\u5185\u529b\u50ac\u8fea\u4e4b\u4e0b\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u7684\u7f61\u6c14\u5982\u6f6e\u5012\u9000\uff0c\u6839\u672c\u5c31\u7531\u4e0d\u5f97\u4ed6\u4f5c\u4e3b\u3002\u4e0d\u8fc7 \u7247\u523b\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u53ea\u89c9\u80f8\u53e3\u80c0\u95f7\u4e0d\u582a\uff0c\u5fc3\u623f\u4f3c\u4e4e\u968f\u65f6\u90fd\u53ef\u7206\u70b8\uff01 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u9762\u8272\u94c1\u9752\uff0c\u53cc\u773c\u706b\u7ea2\uff0c\u7259\u9f7f\u54ac\u5f97\u683c\u683c\u4f5c\u54cd\u3002\u5927\u53eb\u4e00\u58f0\uff0c\u5ffd\u5730\u7528\u529b\u4e00\u54ac\uff0c \u54ac\u65ad\u820c\u5c16\uff0c\u5f20\u5f00\u5927\u53e3\uff1b\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u52b2\u98ce\u75be\u8d77\uff0c\u8840\u82b1\u6e85\u51fa\u6570\u4e08\u5f00\u5916\uff01 \u3000\u3000\u539f\u6765\u5b54\u96c0\u660e\u4f26\u738b\u4e3a\u4e86\u4fdd\u5168\u6027\u547d\uff0c\u53ea\u597d\u81ea\u884c\u6563\u529f\uff0c\u4ed6\u54ac\u7834\u820c\u5c16\uff0c\u7f61\u6c14\u4e00\u6cc4\u65e0\u9057\uff0c\u8fd9\u4e48 \u4e00\u6765\uff0c\u4ed6\u7684\u5341\u5e74\u82e6\u529f\u867d\u662f\u6bc1\u4e00\u65e6\uff0c\u4f46\u4e94\u810f\u516d\u8151\uff0c\u5374\u4e0d\u81f3\u4e8e\u56e0\u53d7\u7f61\u6c14\u7684\u51b2\u51fb\u800c\u788e\u88c2\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u907f\u5f00\u6b63\u9762\uff0c\u8eab\u5f62\u6643\u4e86\u4e00\u6643\uff0c\u968f\u5373\u4e00\u8dc3\u800c\u524d\uff0c\u6276\u4f4f\u4e86\u5b54\u96c0\u660e\u4f26\u738b\uff0c\u626b\u51fa\u4e00\u9897\u836f \u4e38\uff0c\u585e\u8fdb\u4ed6\u7684\u53e3\u5185\uff0c\u5de6\u638c\u8d34\u7740\u4ed6\u7684\u80f8\u53e3\uff0c\u4e00\u80a1\u771f\u6c14\u4ece\u4ed6\u7684\u201c\u7487\u7391\u7a74\u201d\u900f\u8fdb\u53bb\uff0c\u5c06\u4ed6\u7684\u7600 \u8840\u5316\u5f00\uff0c\u4e5f\u5c06\u4ed6\u7684\u5410\u8840\u6b62\u4e86\u3002 \u3000\u3000\u539f\u6765\u6c5f\u4fae\u5929\u5e76\u65e0\u610f\u4f24\u4ed6\u6027\u547d\uff0c\u53ea\u662f\u6068\u4ed6\u6043\u5f3a\u4f5c\u6076\uff0c\u6545\u800c\u7528\u8fd9\u4e2a\u6cd5\u5b50\uff0c\u5f7c\u4ed6\u7684\u7f61\u6c14\uff0c \u8feb\u4ed6\u81ea\u884c\u6bc1\u53bb\u4e0a\u4e58\u7684\u5185\u529f\u3002\u90a3\u9897\u836f\u4e38\u662f\u91d1\u4e16\u9057\u7559\u7ed9\u4ed6\u7684\u78a7\u7075\u4e39\uff0c\u662f\u7528\u5929\u5c71\u96ea\u83b2\u4f5c\u4e3b\u8981\u836f \u6797\u5236\u6210\u7684\uff0c\u4ed6\u4ec5\u5b58\u4e00\u9897\uff0c\u8fd9\u65f6\u4e5f\u7ed9\u4e86\u5b54\u96c0\u660e\u4f26\u738b\uff0c\u514d\u5f97\u4ed6\u53d7\u4f24\u592a\u91cd\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u5728\u54ac\u5f7c\u820c\u5c16\uff0c\u5c06\u7f61\u6c14\u4e0e\u9c9c\u8840\u540c\u65f6\u55b7\u51fa\u6765\u7684\u65f6\u5019\uff0c\u672c\u6765\u8fd8\u5b58\u7740\u4e0e\u6c5f\u6d77\u5929\u4e24 \u8d25\u60e7\u4f24\u7684\u5ff5\u5934\u3002\u54ea\u77e5\u6c5f\u6d77\u5929\u7684\u5185\u529f\u5b9e\u5728\u592a\u9ad8\uff0c\u53ea\u4e0d\u8fc7\u6643\u4e86\u4e00\u6643\u3002\u5b54\u96c0\u660e\u4f26\u738b\u6bd2\u8ba1\u4e0d\u901e\uff0c \u81ea\u8ba4\u5fc5\u6b7b\uff0c\u5374\u4e0d\u6599\u6c5f\u6d77\u5929\u975e\u4f46\u4e0d\u4e58\u673a\u6bd9\u4ed6\uff0c\u53cd\u800c\u5c06\u4ed6\u6551\u4e86\u3002\u5b54\u96c0\u660e\u4f26\u738b\u5bf9\u4ed6\u662f\u53c8\u6068\u53c8\u6015 \u53c8\u6709\u4e00\u4e1d\u611f\u6fc0\uff0c\u65e0\u6d3b\u53ef\u8bf4\uff0c\u53ea\u6709\u795e\u9c7c\u60e8\u7136\uff0c\u8dc4\u8dc4\u8e09\u8e09\u7684\u4e0b\u573a\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u53ea\u4e00\u4e3e\u624b\uff0c\u5c31\u628a\u5b54\u96c0\u670b\u4f26\u738b\u5f04\u5f97\u5982\u6b64\u4e0b\u573a\uff0c\u5ea7\u4e2d\u5404\u56fd\u9ad8\u624b\uff0c\u90fd\u662f\u8038\u7136\u52a8\u5bb9\uff0c \u8fde\u5b9d\u8c61\u6cd5\u5e08\u4e5f\u4e0d\u7531\u5f97\u6697\u6697\u5fc3\u60ca\u3002 \u3000\u3000\u73ed\u680b\u8d70\u51fa\u573a\u6765\uff0c\u5bf9\u6c5f\u6d77\u5929\u9065\u9065\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u5c0f\u4fa0\u795e\u529f\u65e0\u654c\uff0c\u4f69\u670d\uff0c\u4f69\u670d\uff01\u201d\u6c5f \u6d77\u5929\u8fd8\u4e86\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u5fae\u672a\u5c0f\u6280\u3001\u8d3b\u7b11\u5927\u65b9\uff0c\u8fd8\u8bf7\u73ed\u5927\u5e08\u591a\u591a\u6307\u6559\u3002\u201d \u3000\u3000\u4e24\u4eba\u76f8\u4e92\u4e00\u63d6\uff0c\u6c5f\u6d77\u5929\u8863\u8882\u98d8\u98d8\uff0c\u5934\u53d1\u6563\u4e71\uff0c\u8eab\u4f53\u5374\u5140\u7acb\u5982\u5c71\uff0c\u7eb9\u4e1d\u4e0d\u52a8\u3002\u4f46\u90a3\u73ed \u680b\u5374\u4f3c\u7a81\u7136\u77ee\u4e86\u534a\u622a\u3002\u539f\u6765\u4ed6\u662f\u6697\u4e2d\u548c\u6c5f\u6d77\u5929\u8f83\u91cf\u5185\u529f\uff0c\u53cc\u65b9\u5185\u529b\u53d1\u51fa\uff0c\u73ed\u680b\u7981\u53d7\u4e0d\u8d77\uff0c \u53ea\u597d\u7528\u91cd\u8eab\u6cd5\u5b9a\u4f4f\u8eab\u5f62\uff0c\u4ee5\u514d\u9707\u5012\u3002\u8fd9\u5ea7\u5927\u6bbf\u7684\u5730\u677f\u867d\u7136\u662f\u7528\u575a\u786c\u7684\u82b1\u5c97\u77f3\u94fa\u7684\uff0c\u5374\u4e5f \u7ecf\u4e0d\u8d77\u73ed\u680b\u7684\u4e00\u8e0f\uff0c\u73ed\u680b\u7684\u53cc\u8db3\u90fd\u8e0f\u5165\u4e86\u5730\u677f\u4e4b\u4e2d\uff0c\u56e0\u800c\u770b\u8d77\u6765\u5c31\u4f3c\u77ee\u4e86\u534a\u622a\u3002\u4ed6\u529f\u529b \u5982\u6b64\u6df1\u539a\uff0c\u5df2\u662f\u4e16\u95f4\u7f55\u89c1\uff0c\u4f46\u6bd4\u8d77\u6c5f\u6d77\u5929\u6765\u3002\u5374\u53c8\u662f\u76f8\u5f62\u89c1\u62d9\u4e86\u3002 \u3000\u3000\u73ed\u680b\u62d4\u8d77\u53cc\u8db3\uff0c\u4e00\u58f0\u957f\u53f9\uff0c\u795e\u8272\u9eef\u7136\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5230\u9ad8\u5c71\uff0c\u4e0d\u663e\u5e73\u5730\uff0c\u4eca\u65e5\u6765\u5230\u8d35 \u56fd\uff0c\u59cb\u77e5\u5929\u5916\u6709\u5929\u3002\u5f92\u5f1f\u5c1a\u4e14\u5982\u513f\uff0c\u5e08\u7236\u53ef\u77e5\u3002\u6211\u8fd9\u70b9\u5fae\u672a\u4e4b\u6280\uff0c\u5984\u56fe\u4e0e\u91d1\u5927\u4fa0\u8f83\u91cf\uff0c \u90a3\u771f\u662f\u7c73\u7c92\u4e4b\u73e0\uff0c\u8981\u4e0e\u7693\u6708\u4e89\u5149\u4e86\u3002\u201d\u4ed6\u8fd9\u56de\u662f\u771f\u6b63\u7684\u53e3\u773c\u5fc3\u670d\uff0c\u8bf4\u4e86\u8fd9\u8bdd\uff0c\u4fbf\u5373\u79bb\u573a\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8fde\u8d25\u4e24\u5927\u9ad8\u624b\uff0c\u5404\u56fd\u6b66\u58eb\u65e0\u4e0d\u9707\u60ca\uff0c\u867d\u7136\u6c5f\u6d77\u5929\u5df2\u7ecf\u5f52\u5ea7\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u6562\u5355\u6d4a \u51fa\u6765\uff0c\u5411\u4e2d\u539f\u8c6a\u6770\u6311\u6218\uff0c\u4ed6\u4eec\u5fc3\u4e2d\u5747\u662f\u5982\u6b64\u60f3\u9053\uff1a\u201c\u8fd9\u59d3\u6c5f\u7684\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5df2\u7136\u5982\u6b64\u4e86\u5f97\u3002 \u770b\u6765\u4e2d\u56fd\u7684\u6b66\u5b66\u786e\u662f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u96be\u4e0e\u8f83\u91cf\u3002\u201d \u3000\u3000\u5c3c\u6cca\u5c14\u7684\u6b66\u58eb\u805a\u5728\u4e00\u89d2\u53fd\u53fd\u55b3\u55b3\u7684\u5546\u91cf\u4e86\u4e00\u4f1a\uff0c\u63a8\u51fa\u4e24\u4e2a\u4eba\u6765\uff0c\u4e00\u50e7\u4e00\u4fd7\uff0c\u62ab\u7740\u7eaf \u767d\u8888\u88df\u7684\u90a3\u4e2a\u9ad8\u7626\u50e7\u4eba\uff0c\u662f\u5c3c\u6cca\u5c14\u65e7\u738b\u4ece\u6ce2\u65af\u793c\u8058\u6765\u7684\u8884\u6559\u9ad8\u624b\u3002\u6cd5\u53f7\u666f\u6708\u4e0a\u4eba\uff1b\u90a3\u4fd7 \u5bb6\u6b66\u58eb\uff0c\u5219\u662f\u5c3c\u6cca\u5c14\u672c\u56fd\u7684\u7b2c\u4e00\u9ad8\u5343\uff0c\u540d\u53eb\u5b5f\u54c8\u8d64\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u8d70\u51fa\u573a\u6765\uff0c\u5411\u51b0\u5ddd\u5929\u5973\u65bd\u793c\u8bf4\u9053\uff1a\u201c\u8bf7\u516c\u4e3b\u51fa\u573a\uff0c\u6211\u7b49\u6709\u4e8b\u7980\u544a\u3002\u201d\u51b0\u5ddd\u5929 \u5973\u79bb\u5ea7\u800c\u8d77\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u5728\u6b64\u573a\u4e2d\uff0c\u4e0d\u5fc5\u62d8\u5c0a\u5351\u4e4b\u793c\uff0c\u4f60\u4eec\u662f\u610f\u6b32\u4e0e\u6211\u6bd4\u6b66\u4e48\uff1f\u201d \u5b5f\u54c8\u8d64\u9053\uff1a\u201c\u4e0d\u6562\u3002\u6211\u4eec\u662f\u5949\u4e86\u56fd\u738b\u4e4b\u547d\uff0c\u8bf7\u516c\u4e3b\u5f52\u56fd\u7684\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u8d70\u5230\u573a\u4e2d\uff0c\u626b\u4e86\u4ed6\u4eec\u4e00\u773c\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u662f\u5949\u4e86\u54ea\u4e2a\u56fd\u738b\u4e4b\u547d\uff1f\u201d\u5b5f \u54c8\u8d64\u9053\uff1a\u201c\u5929\u65e0\u4e8c\u65e5\uff0c\u6c11\u65e0\u4e8c\u4e3b\uff0c\u6211\u56fd\u53ea\u6709\u4e00\u4e2a\u56fd\u738b\uff0c\u5c31\u662f\u5f53\u4eca\u7687\u4e0a\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a \u201c\u7a76\u7adf\u662f\u8c01\uff0c\u4f60\u4e3a\u4f55\u4e0d\u7b54\u6211\u7684\u95ee\u8bdd\uff1f\u201d\u5b5f\u54c8\u8d64\u53ea\u5f97\u8bf4\u9053\uff1a\u201c\u5f53\u4eca\u7687\u4e0a\u5c31\u662f\u516c\u4e3b\u7684\u8868\u5144\uff0c \u516c\u4e3b\u4f60\u662f\u660e\u77e5\u6545\u95ee\u4e86\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u4e0d\u5bf9\u4e86\uff0c\u6211\u867d\u7136\u8eab\u5904\u5f02\u56fd\uff0c\u672c\u56fd\u7684\u5927\u4e8b\u4ea6\u6709\u6240\u95fb\u3002\u4f60\u4eec\u8fd9 \u4f4d\u2018\u7687\u4e0a\u2019\u65e9\u5df2\u88ab\u5e9f\uff0c\u65b0\u738b\u4ea6\u65e9\u5df2\u5373\u4f4d\uff0c\u4f60\u4eec\u8fd8\u600e\u80fd\u79f0\u4ed6\u4e3a\u2018\u5f53\u4eca\u7687\u4e0a\u2019\uff1f\u201d \u3000\u3000\u5b5f\u54c8\u8d64\u9053\uff1a\u201c\u516c\u4e3b\u6b64\u8a00\u5dee\u77e3\uff0c\u56fd\u4e2d\u867d\u7136\u53d1\u751f\u53db\u4e71\uff0c\u56fd\u738b\u5c1a\u5728\uff0c\u6b63\u7edf\u72b9\u5b58\uff0c\u53db\u515a\u9996\u9886\uff0c \u7109\u5f97\u79f0\u4e3a\u65b0\u738b\uff1f\u4e0d\u7792\u516c\u4e3b\uff0c\u6211\u7b49\u6b63\u662f\u4e3a\u4e86\u6b64\u4e8b\uff0c\u5949\u4e86\u56fd\u738b\u4e4b\u547d\uff0c\u8bf7\u516c\u4e3b\u56de\u53bb\u534f\u52a9\u5e73\u4e71\u7684\u3002\u201d \u51b0\u5ddd\u5929\u5973\u9053\uff1a \u3000\u3000\u201c\u6211\u5df2\u4e45\u77e3\u4e4e\u4e0d\u95ee\u56fd\u4e8b\uff0c\u56fd\u738b\u662f\u8981\u8001\u767e\u59d3\u62e5\u62a4\u7684\uff0c\u8001\u767e\u59d3\u62e5\u62a4\u8c01\uff0c\u8c01\u5c31\u662f\u56fd\u4e3b\u3002\u201d \u3000\u3000\u5b5f\u54c8\u8d64\u6653\u6653\u7f6e\u8fa9\uff0c\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u597d\uff0c\u6211\u6682\u4e14\u4e0d\u4e0e\u4f60\u8fa9\uff0c\u4f60\u8bf4\u8bf4\u770b\uff0c\u4f60\u4eec\u7684\u56fd\u738b\u5984 \u6211\u5982\u4f55\u534f\u52a9\u5e73\u4e71\uff1f\u201d\u5b5f\u54c8\u8d64\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u4f60\u8bf4\u4e0d\u7406\u56fd\u4e8b\uff0c\u4f46\u8bf7\u95ee\u52a0\u6e90\uff0c\u8499\u73e0\u662f\u4e0d\u662f\u516c\u4e3b \u6240\u751f\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5510\u52a0\u6e90\u662f\u6211\u7684\u513f\u5b50\uff0c\u4ed6\u88ab\u4f60\u4eec\u7684\u7687\u4e0a\u7ed1\u67b6\u53bb\u4e86\uff0c\u6211\u8fd8\u672a\u66fe\u5f97\u627e\u4ed6\u7b97\u5e10\u5462\u3002\u201d \u3000\u3000\u5b5f\u54c8\u8d64\u9053\uff1a\u201c\u516c\u4e3b\u8bef\u4f1a\u4e86\u3002\u52a0\u6e90\xb7\u8499\u73e0\u662f\u56fd\u738b\u8bf7\u53bb\u7684\uff0c\u4e00\u76f4\u53d7\u5230\u4f18\u5f85\uff0c\u4e0d\u6599\u4ed6\u5374\u534f \u52a9\u53db\u515a\uff0c\u79f0\u5175\u4f5c\u4e71\uff0c\u5f3a\u5360\u7687\u5bab\uff0c\u6545\u6b64\u7687\u4e0a\u8981\u8bf7\u516c\u4e3b\u56de\u6765\u3002\u5c06\u52a0\u6e90\xb7\u8499\u73e0\u7ba1\u6559\u7ba1\u6559\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5982\u6b64\u3002\u4f60\u4eec\u4e00\u5171\u6765\u4e86\u591a\u5c11\u4eba\uff0c\u90fd\u51fa\u6765\u5427\u3002\u201d\u90a3\u4e9b\u5c3c\u6cca\u5c14\u6b66 \u58eb\u90fd\u628a\u773c\u775b\u671b\u7740\u76c2\u54c8\u8d64\uff0c\u5b5f\u54c8\u8d64\u9053\uff1a\u201c\u516c\u4e3b\u7684\u610f\u601d\u662f\u2014\u2014\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u90fd\u51fa\u6765\u5427\uff0c \u51fa\u6765\u4e86\uff0c\u6211\u5bf9\u4f60\u4eec\u6709\u8bdd\u8bf4\u3002\u201d\u5b5f\u54c8\u8d64\u9053\uff1a\u201c\u65e2\u7136\u516c\u4e3b\u6709\u547d\u3002\u4f60\u4eec\u5c31\u90fd\u51fa\u6765\u542c\u516c\u4e3b\u5429\u5490\u3002\u201d \u3000\u3000\u5c3c\u6cca\u5c14\u6b66\u58eb\u9c7c\u8d2f\u800c\u51fa\uff0c\u4e00\u5171\u662f\u4e09\u5341\u516d\u4eba\uff0c\u6392\u6210\u4e24\u884c\uff0c\u51b0\u5ddd\u5929\u5973\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u7684 \u7687\u4e0a\u60f3\u8bf7\u6211\u56de\u53bb\uff0c\u53ea\u6d3e\u5b5f\u54c8\u8d64\u6765\u4e5f\u5c31\u591f\u4e86\uff0c\u73b0\u5728\u5374\u662f\u6d3e\u4e86\u4e00\u5927\u7fa4\u6765\uff0c\u8fd9\u662f\u4f55\u7528\u610f\uff1f\u662f\u5426 \u51c6\u5907\u6211\u4e0d\u7b54\u5e94\u7684\u8bdd\uff0c\u5c31\u62ff\u6211\u56de\u53bb\uff1f\uff01\u201d\u5b5f\u54c8\u8d64\u6ee1\u9762\u901a\u7ea2\uff0c\u8bb7\u8bb7\u4e0d\u80fd\u51fa\u53e3\u3002 \u3000\u3000\u666f\u6708\u4e0a\u4eba\u81ea\u6043\u662f\u5ba2\u537f\u8eab\u4efd\uff0c\u65e0\u987b\u8fc7\u4efd\u5c0a\u656c\u5c3c\u6cca\u5c14\u7684\u516c\u4e3b\uff0c\u4fbf\u9053\uff1a\u201c\u516c\u4e3b\u660e\u9274\uff0c\u6700\u597d \u662f\u63a5\u53d7\u56fd\u738b\u7684\u5ba3\u53ec\uff0c\u7acb\u5373\u548c\u6211\u4eec\u52a8\u8eab\u56de\u56fd\u3002\u201d\u8fd9\u8bdd\u76f4\u8ba4\u4e0d\u8bb3\uff0c\u5373\u662f\u51b0\u5ddd\u5929\u5973\u82e5\u4e0d\u5949\u53ec\uff0c \u4ed6\u4eec\u5c31\u8981\u62ff\u4eba\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31\u628a\u6211\u7684\u4e3b\u610f\u5bf9\u4f60\u4eec\u8bf4\u4e86\u5427\u3002 \u3000\u3000\u4f60\u4eec\u7684\u7687\u4e0a\u4e0d\u5f97\u6c11\u5fc3\uff0c\u65b0\u738b\u5df2\u7ecf\u5373\u4f4d\uff0c\u6211\u53ea\u627f\u8ba4\u65b0\u738b\uff0c\u5bf9\u4f60\u4eec\u7684\u7687\u4e0a\uff0c\u6211\u628a\u4ed6\u89c6\u540c \u53db\u9006\u3002\u4ed6\u8981\u6211\u7ba1\u6559\u513f\u5b50\uff0c\u6211\u770b\u6211\u7684\u513f\u5b50\u505a\u5f97\u5f88\u597d\uff0c\u8be5\u7ba1\u6559\u7684\u5012\u662f\u4ed6\u3002\u201d \u3000\u3000\u5b5f\u54c8\u8d64\u548c\u666f\u6708\u4e0a\u4eba\u90fd\u53d8\u4e86\u9762\u8272\uff0c\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u516c\u4e3b\u662f\u4e0d\u7b54\u5e94\u56de\u53bb\u4e86\uff1f\u201d\u51b0\u5ddd\u5929 \u5973\u6307\u7740\u90a3\u4e9b\u6b66\u58eb\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u8981\u4f60\u4eec\u90fd\u7ed9\u6211\u6eda\u5f00\uff0c\u4e0d\u8bb8\u4f60\u4eec\u5728\u6b64\u5730\u5174\u98ce\u4f5c\u6d6a\u3002\u201d \u3000\u3000\u5b5f\u54c8\u8d64\u9053\uff1a\u201c\u516c\u4e3b\u65e2\u7136\u5982\u6b64\uff0c\u8bf7\u6055\u6211\u4eec\u5f97\u7f6a\u4e86\u3002\u201d\u90a3\u4e09\u5341\u516d\u540d\u6b66\u58eb\u4e0d\u5f85\u5429\u5490\uff0c\u5df2\u6392 \u6210\u4e24\u4e2a\u6247\u5f62\uff0c\u5408\u6210\u4e00\u4e2a\u5706\u9635\uff0c\u5c06\u51b0\u5ddd\u5929\u5973\u56f4\u5728\u5f53\u4e2d\u3002\u5b5f\u54c8\u8d64\u5374\u4e0d\u7acb\u5373\u52a8\u624b\uff0c\u5148\u8f6c\u8fc7\u9762\u5bf9 \u5b9d\u8c61\u6cd5\u5e08\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u4eec\u672c\u56fd\u7684\u4e8b\u60c5\uff0c\u4e0d\u540c\u4e8e\u5bfb\u5e38\u6bd4\u6b66\uff0c\u8bf7\u6cd5\u5e08\u4e0d\u8981\u89c1\u602a\u6211\u4eec\u6270\u4e71\u4e86 \u4f1a\u573a\u3002\u201d \u3000\u3000\u8981\u77e5\u6bd4\u6b66\u7684\u89c4\u77e9\uff0c\u82e5\u975e\u53cc\u65b9\u540c\u610f\uff0c\u4e00\u822c\u90fd\u662f\u5355\u6253\u72ec\u6597\uff0c\u6545\u6b64\u5b5f\u54c8\u8d64\u5148\u51fa\u8a00\u4ea4\u4ee3\u3002\u51b0 \u5ddd\u5929\u5973\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b64\u4e8b\u4e0e\u8bf8\u4f4d\u65e0\u5173\uff0c\u8bf7\u8bf8\u4f4d\u51b7\u773c\u65c1\u89c2\u3002\u201d\u5b9d\u8c61\u6cd5\u5e08\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\uff0c \u597d\uff01\u4e45\u95fb\u51b0\u5ddd\u5929\u5973\u51b0\u5f39\u7389\u5251\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u8fd9\u4e00\u573a\u867d\u975e\u6b63\u5f0f\u6bd4\u6b66\uff0c\u4e5f\u8db3\u4ee4\u6211\u4eec\u5927\u5f00\u773c\u754c\u4e86\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4f60\u4eec\u65e2\u7136\u90fd\u662f\u5949\u547d\u6765\u8bf7\u6211\u7684\uff0c\u90a3\u5c31\u90fd\u4e0a\u6765\u5427\uff01\u201d\u5b5f\u54c8\u8d64\u62b1\u62f3\u4e00\u63d6\uff0c \u666f\u6708\u4e0a\u4eba\u4e5f\u6253\u4e86\u4e2a\u7a3d\u9996\uff0c\u4e24\u4eba\u9f50\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6211\u4eec\u5148\u6765\u4fc3\u9a7e\uff0c\u8981\u662f\u8bf7\u4e0d\u52a8\u516c\u4e3b\uff0c\u4ed6\u4eec\u518d\u6765\u542c\u5019\u516c\u4e3b\u5dee\u9063\u3002\u201d \u3000\u3000\u666f\u6708\u4e0a\u4eba\u8bdd\u72b9\u672a\u4e86\uff0c\u4f38\u51fa\u5927\u624b\uff0c\u4e00\u6293\u5c31\u5411\u51b0\u5ddd\u5929\u5973\u6293\u53bb\u3002\u51b0\u5ddd\u5929\u5973\u65a5\u9053\uff1a\u201c\u79c3\u9a74\u65e0 \u672d\uff01\u201d\u4e00\u98d8\u4e00\u95ea\uff0c\u666f\u6708\u4e0a\u4eba\u6251\u4e86\u4e2a\u7a7a\uff0c\u8bf4\u65f6\u8fdf\u3002\u90a3\u65f6\u5feb\uff0c\u51b0\u5ddd\u5929\u5973\u7684\u4e24\u9897\u51b0\u9b44\u795e\u5f39\u5df1\u7136 \u53d1\u51fa\uff0c\u5206\u6253\u5b5f\u54c8\u8d64\u4e0e\u666f\u6708\u4e0a\u4eba\u3002 \u3000\u3000\u666f\u6708\u4e0a\u4eba\u5f20\u624b\u4e00\u6293\uff0c\u5c06\u51b0\u9b44\u795e\u5f39\u6293\u5165\u624b\u5fc3\uff0c\u53cc\u638c\u4e00\u6413\uff0c\u51b0\u5f39\u767b\u5bf9\u5316\u4f5c\u4e86\u4e00\u56e2\u5bd2\u96fe\uff0c \u4ece\u4ed6\u7684\u6307\u7f1d\u95f4\u98de\u51fa\u6765\uff0c\u4ed6\u7adf\u7136\u8fde\u5bd2\u5664\u4e5f\u672a\u6253\u4e00\u4e2a\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u51b0\u9b44\u795e\u5f39\uff0c\u539f\u6765\u4e5f\u4e0d\u8fc7 \u5982\u6b64\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u4e95\u86d9\u4e0d\u8bc6\u4e7e\u5764\u5927\uff0c\u7c73\u7c92\u4e4b\u73e0\u4e5f\u6562\u9a84\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u56db\u5341\u56de\u3000\u67d4\u60c5\u871c\u610f\u96be\u6d88\u53d7\u3000\u864e\u6597\u9f99\u4e89\u5404\u901e\u80fd \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u56db\u5341\u56de\u3000\u67d4\u60c5\u871c\u610f\u96be\u6d88\u53d7\u3000\u864e\u6597\u9f99\u4e89\u5404\u901e\u80fd \u3000\u3000\u53e6\u4e00\u9897\u51b0\u5f39\u6253\u5230\u5b5f\u54c8\u8d64\u9762\u524d\uff0c\u5b5f\u54c8\u8d64\u6325\u52a8\u4e00\u9650\u6746\u68d2\uff0c\u68d2\u7aef\u4e00\u6307\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u55b7 \u51fa\u4ee5\u6e9c\u706b\u7130\uff0c\u88f9\u7740\u51b0\u5f39\uff0c\u51b0\u5f39\u5316\u51fa\u4e86\u4e00\u7247\u767d\u8499\u8499\u7684\u6c34\u6c14\uff0c\u706b\u7130\u7184\u706d\uff0c\u5b5f\u54c8\u8d64\u4f46\u89c9\u904d\u4f53\u751f \u51c9\uff0c\u5374\u4e5f\u5e76\u65e0\u635f\u4f24\u3002 \u3000\u3000\u539f\u6765\u4ed6\u4eec\u65e9\u5df2\u77e5\u9053\u51b0\u5ddd\u5929\u5973\u7389\u5251\u51b0\u5f39\u7684\u529f\u80fd\uff0c\u9884\u5148\u6709\u4e86\u9632\u5907\u3002\u666f\u6708\u4e0a\u4eba\u7ec3\u6210\u4e86\u201c\u706b \u9f99\u529f\u201d\uff0c\u4ee5\u7eaf\u9633\u4e4b\u6c14\u51dd\u805a\u638c\u5fc3\uff0c\u4e0d\u6015\u5bd2\u6c14\u4fb5\u88ad\uff0c\u6545\u800c\u4ed6\u6562\u786c\u63a5\u51b0\u5f39\uff0c\u7528\u638c\u5fc3\u7684\u70ed\u529b\u5c06\u5b83 \u878d\u5316\u3002\u5b5f\u54c8\u8d64\u529f\u529b\u8f83\u5f31\uff0c\u5374\u8981\u501f\u52a9\u4e8e\u7279\u5236\u7684\u5175\u5668\uff0c\u4ed6\u90a3\u6839\u6746\u68d2\uff0c\u68d2\u5185\u4e2d\u7a7a\uff0c\u8d2e\u85cf\u6709\u53ef\u4ee5 \u53d1\u51fa\u70ed\u5ea6\u6781\u9ad8\u7684\u6613\u71c3\u836f\u7269\uff0c\u56e0\u800c\u4e0e\u51b0\u5f39\u63a5\u89e6\uff0c\u5bd2\u70ed\u76f8\u6d88\uff0c\u672c\u8eab\u4e5f\u6ca1\u4f24\u635f\u3002 \u3000\u3000\u666f\u6708\u4e0a\u4eba\u5927\u7b11\u58f0\u4e2d\uff0c\u51b0\u5ddd\u5929\u5973\u5df2\u662f\u6325\u52a8\u7389\u5251\uff0c\u4ee5\u62db\u201c\u51b0\u6cb3\u89e3\u52a8\u51bb\u201d\uff0c\u5bd2\u5149\u95ea\u5904\uff0c\u72c2 \u98d9\u9aa4\u8d77\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u904d\u88ad\u666f\u6708\u4e0a\u4eba\u7684\u5341\u4e09\u5904\u5927\u7a74\uff0c \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7684\u7389\u5251\u662f\u4e07\u5e74\u5bd2\u7389\u6240\u9020\uff0c\u7565\u4e00\u6325\u52a8\uff0c\u5947\u5bd2\u4e4b\u6c14\u4fbf\u5411\u56db\u65b9\u5c04\u51fa\uff0c\u4fb5\u5165\u7a74\u9053\uff0c \u6bd4\u51b0\u9b44\u795e\u5f39\u8fd8\u8981\u5389\u5bb3\u51e0\u5206\uff0c\u666f\u6708\u4e0a\u4eba\u95ed\u4e86\u5168\u8eab\u7a74\u9053\uff0c\u6325\u52a8\u4e24\u652f\u5927\u8896\uff0c\u4e5f\u6247\u8d77\u5341\u80a1\u72c2\u98ce\uff0c \u5c06\u51b0\u5ddd\u5929\u5973\u7684\u7389\u5251\u62c2\u5f00\uff0c\u54ea\u77e5\u51b0\u5ddd\u5929\u5973\u7684\u5251\u672f\u4e5f\u6781\u7cbe\u5999\uff0c\u987a\u7740\u98ce\u52bf\uff0c\u8eab\u5f62\u4e00\u65cb\uff0c\u5530\u7684\u4e00 \u521b\uff0c\u5df2\u4ece\u666f\u6708\u4e0a\u4eba\u7684\u8896\u7ba1\u7a7f\u8fc7\uff0c\u5e78\u800c\u5979\u7684\u7389\u5251\u4e0d\u4ee5\u950b\u5229\u89c1\u957f\uff0c\u666f\u6708\u4e0a\u4eba\u88ab\u5251\u5c16\u523a\u4e86\u4e00\u4e0b\uff0c \u4ed7\u7740\u5185\u529f\u6df1\u539a\uff0c\u5e76\u672a\u53d7\u4f24\uff0c\u5251\u5c16\u5c04\u51fa\u7684\u5bd2\u6c14\uff0c\u6709\u4e00\u4e1d\u4fb5\u5165\u4ed6\u7684\u7a74\u9053\uff0c\u4e5f\u7acb\u5373\u88ab\u4ed6\u7684\u201c\u706b \u9f99\u529f\u201d\u70bc\u5316\u4e86\u3002 \u3000\u3000\u5b5f\u54c8\u8d64\u89c1\u666f\u6708\u4e0a\u4eba\u62b5\u6321\u5f97\u4f4f\u51b0\u5ddd\u5929\u5973\u7684\u7389\u5251\u51b0\u5f39\uff0c\u80c6\u6c14\u987f\u58ee\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u7684\u529f\u529b\u6bd4 \u8d77\u666f\u6708\u4e0a\u4eba\u867d\u662f\u7a0d\u6709\u4e0d\u5982\uff0c\u4f46\u6211\u7684\u8eab\u624b\u77eb\u6377\uff0c\u62db\u6570\u53d8\u5316\uff0c\u5374\u662f\u5728\u4ed6\u4e4b\u4e0a\uff0c\u53ea\u8981\u4e0d\u7ed9\u5929\u5251 \u523a\u4e2d\uff0c\u6599\u4e5f\u65e0\u59a8\u3002\u51b0\u5ddd\u5929\u5973\u662f\u6211\u56fd\u516c\u4e3b\uff0c\u8981\u662f\u8ba9\u666f\u6708\u4e0a\u4eba\u5148\u628a\u5979\u64d2\u83b7\uff0c\u6211\u7684\u9762\u5b50\u4e5f\u4e0d\u597d \u770b\u3002\u201d\u5f53\u4e0b\u5b58\u4e86\u4e0e\u666f\u6708\u4e0a\u4eba\u4e89\u80dc\u7684\u5ff5\u5934\uff0c\u7acb\u610f\u8981\u62a2\u5728\u4ed6\u7684\u524d\u9762\uff0c\u64d2\u62ff\u51b0\u5ddd\u5929\u5973\u3002 \u3000\u3000\u5b5f\u54c8\u8d64\u56e0\u51b0\u5ddd\u5929\u5973\u662f\u516c\u4e3b\u8eab\u4efd\uff0c\u4e0d\u6562\u592a\u8fc7\u653e\u8086\uff0c\u8df3\u4e0a\u524d\u6765\u3002 \u3000\u3000\u5148\u8bf4\u4e00\u58f0\uff1a\u201c\u516c\u4e3b\uff0c\u8bf7\u4f60\u8fd8\u662f\u987a\u4ece\u7687\u4e0a\u4e4b\u547d\uff0c\u514d\u5f97\u5c0f\u7684\u4e3a\u96be\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u80fd\u8010\uff0c\u5c3d\u7ba1\u65bd\u5c55\u3002\u201d\u7389\u5251\u4ee5\u626c\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u500f\u7136\u95f4\u4fbf \u523a\u5230\u76c2\u54c8\u8d64\u9762\u524d\uff0c\u5b5f\u54c8\u8d64\u6599\u4e0d\u5230\u5979\u6765\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c\u4ed3\u7687\u95ea\u907f\uff0c\u9669\u4e9b\u6454\u5230\u3002\u666f\u6708\u4e0a\u4eba\u5927\u8896 \u62c2\u6765\uff0c\u5c06\u51b0\u5ddd\u5929\u5973\u963b\u4e86\u4e00\u963b\u3002 \u3000\u3000\u5b5f\u54c8\u8d64\u53eb\u9053\uff1a\u201c\u516c\u4e3b\u4e0d\u80af\u7275\u53ec\uff0c\u8bf7\u6055\u6211\u653e\u8086\u4e86\u3002\u201d\u7ed5\u5230\u51b0\u5ddd\u5929\u5973\u80cc\u540e\uff0c\u4e00\u6309\u6746\u68d2\uff0c \u4e00\u6e9c\u706b\u5149\u53c8\u5c04\u51fa\u6765\uff0c\u51b0\u5ddd\u5929\u5973\u53cd\u624b\u53d1\u51fa\u4e24\u9897\u51b0\u5f39\uff0c\u518d\u6b21\u628a\u4ed6\u53d1\u51fa\u7684\u706b\u7130\u6251\u706d\uff0c\u5468\u56f4\u5341\u6570 \u4e08\u4e4b\u5185\uff0c\u767b\u65f6\u90fd\u5e03\u6ee1\u4e86\u767d\u8499\u8499\u7684\u6c34\u6c14\uff0c\u6709\u5982\u4e00\u56e2\u6d53\u96fe\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5ffd\u5730\u51b7\u7b11\u9053\uff1a\u201c\u53eb\u4f60\u4eec\u8bc6\u5f97\u5389\u5bb3\uff01\u201d\u7b11\u58f0\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u666f\u6708\u4e0a\u4eba\u7259\u5173\u683c\u683c \u4f5c\u54cd\uff0c\u5b5f\u54c8\u8d64\u66f4\u662f\u6d51\u8eab\u53d1\u6296\uff0c\u4fe8\u5982\u5bb3\u4e86\u759f\u75be\u4e00\u822c\u3002 \u3000\u3000\u539f\u6765\u51b0\u5ddd\u5929\u5973\u8d81\u7740\u96fe\u6c14\u5f25\u6f2b\u4e4b\u9645\uff0c\u7a81\u7136\u4ee5\u5947\u5999\u7edd\u4f26\u7684\u624b\u6cd5\uff0c\u53d1\u51fa\u4e24\u679a\u51b0\u9b44\u795e\u5f39\uff0c\u4e00 \u679a\u6253\u8fdb\u666f\u6708\u4e0a\u4eba\u7684\u9f3b\u5b54\uff0c\u4e00\u679a\u6253\u8fdb\u5b5f\u54c8\u8d64\u7684\u8033\u5b54\uff0c\u51b0\u5f39\u6eb6\u5316\uff0c\u5bd2\u6c14\u767b\u65f6\u4fb5\u5165\u5185\u810f\u3002\u666f\u6708 \u4e0a\u4eba\u7ec3\u6709\uff1a\u201c\u706b\u9f99\u529f\u201d\uff0c\u8fd8\u597d\u4e00\u4e9b\uff0c\u4f46\u5bd2\u6c14\u4fb5\u5165\u810f\u8151\uff0c\u4ed6\u4e5f\u4e0d\u80fd\u5373\u65f6\u9a71\u9664\uff1b\u90a3\u5b5f\u54c8\u8d64\u5185 \u529f\u4e0d\u53ca\u666f\u6708\u4e0a\u4eba\uff0c\u5374\u662f\u66f4\u4e3a\u72fc\u72c8\uff0c\u53ea\u89c9\u8840\u6db2\u90fd\u4f3c\u4e4e\u51b7\u5f97\u51dd\u7ed3\uff0c\u6296\u4e2a\u4e0d\u505c\u3002\u201d \u3000\u3000\u6c5f\u5357\u5728\u4eba\u4e1b\u4e2d\u563b\u563b\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u521a\u624d\u8bf4\u51b0\u9b44\u795e\u5f39\u4e5f\u4e0d\u8fc7\u5982\u6b64\uff0c\u73b0\u5728\u5c1d\u5230\u4e86\u51b0\u5f39\u7684\u6ecb \u5473\uff0c\u600e\u4e48\u53cd\u800c\u4e0d\u8bf4\u8bdd\u4e86\uff1f\u201d\u666f\u6708\u4e0a\u4eba\u7259\u5173\u6253\u6218\uff0c\u60f3\u9a82\u4e5f\u9a82\u4e0d\u51fa\u6765\u3002\u4ed6\u6df1\u6015\u51b0\u5ddd\u5929\u5973\u518d\u53d1 \u51b0\u5f39\u88ad\u4ed6\u4e03\u7a8d\uff0c\u6025\u5fd9\u4e24\u8896\u72c2\u6325\uff0c\u5c06\u9762\u524d\u7684\u6d53\u96fe\u62c2\u53bb\u3002\u51b0\u5ddd\u5929\u5973\u54ea\u5bb9\u4ed6\u6b47\u606f\uff0c\u6325\u5251\u53c8\u653b\uff0c \u666f\u6708\u4e0a\u4eba\u4e00\u65b9\u9762\u8981\u8fd0\u529f\u9a71\u9664\u5bd2\u6c14\uff0c\u4e00\u65b9\u9762\u8981\u62b5\u6321\u51b0\u5ddd\u5929\u5973\u7684\u5251\u62db\uff0c\u767b\u65f6\u624b\u5fd9\u811a\u4e71\uff0c\u4e0d\u8fc7 \u51e0\u62db\uff0c\u5df2\u662f\u9669\u8c61\u73af\u751f\u3002 \u3000\u3000\u5b5f\u54c8\u8d64\u628a\u68d2\u4e00\u6325\uff0c\u90a3\u4e09\u5341\u516d\u540d\u6b66\u571f\u6392\u6210\u7684\u4e24\u4e2a\u6247\u5f62\uff0c\u5ffd\u5730\u5408\u6210\u5706\u9635\uff0c\u7a7f\u68ad\u6765\u5f80\uff0c\u5411 \u51b0\u5ddd\u5929\u5973\u5c55\u5f00\u72c2\u98ce\u66b4\u96e8\u822c\u7684\u653b\u51fb\u3002\u51b0\u5ddd\u5929\u5973\u817e\u4e0d\u51fa\u624b\u6765\u53d1\u5c04\u51b0\u5f39\uff0c\u53ea\u80fd\u4ed7\u5251\u5fa1\u654c\uff0c\u5251\u5c16 \u4e0a\u53d1\u51fa\u7684\u5bd2\u6c14\u867d\u7136\u4e5f\u5f88\u5389\u5bb3\uff0c\u4f46\u672a\u66fe\u4fb5\u5165\u5185\u810f\uff0c\u90a3\u4e9b\u4eba\u62ab\u7740\u7279\u5236\u7684\u77f3\u68c9\u8863\u670d\uff0c\u5374\u8fd8\u53ef\u4ee5 \u62b5\u6321\u3002\u51b0\u5ddd\u5929\u5973\u5728\u5706\u9635\u51b2\u51fb\u4e4b\u4e0b\uff0c\u90fd\u662f\u6e10\u611f\u5e94\u4ed8\u4e0d\u6613\u3002 \u3000\u3000\u5510\u7ecf\u5929\u4e00\u58f0\u957f\u5578\uff0c\u8eab\u5f62\u500f\u8d77\uff0c\u4fe8\u5982\u4e00\u5934\u5de8\u4e4c\uff0c\u98de\u5165\u9635\u4e2d\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5510\u52a0\u6e90\u662f\u6211 \u7684\u513f\u5b50\uff0c\u6b64\u4e8b\u4e5f\u4e0e\u6211\u6709\u5173\u3002\u4f60\u4eec\u7684\u56fd\u738b\u8981\u6349\u62ff\u53db\u515a\u5bb6\u5c5e\uff0c\u53ef\u4e0d\u80fd\u5355\u6349\u6211\u7684\u59bb\u5b50\u5440\uff0c\u6211\u5982 \u4eca\u81ea\u52a8\u6765\u62a5\u5230\u4e86\u3002\u201d \u3000\u3000\u666f\u6708\u4e0a\u4eba\u5df2\u628a\u5bd2\u6c14\u9a71\u6563\u4e86\u5341\u4e4b\u4e03\u516b\uff0c\u529f\u529b\u5dee\u4e0d\u591a\u6062\u590d\u5982\u521d\uff0c\u4ed7\u7740\u4eba\u591a\uff0c\u5c06\u5706\u9635\u4e00\u8f6c\uff0c \u4fbf\u5411\u5510\u7ecf\u5929\u51b2\u51fb\uff0c\u559d\u9053\uff1a\u201c\u5f88\u597d\uff0c\u4f60\u65e2\u81ea\u884c\u62a5\u5230\uff0c\u6211\u4e5f\u4e0d\u5fc5\u548c\u4f60\u5ba2\u6c14\u4e86\u3002\u201d\u53cc\u62bd\u5377\u51fa\uff0c \u4fbf\u4f3c\u500f\u7136\u98de\u51fa\u4e86\u4e24\u6761\u957f\u86c7\uff0c\u5411\u5510\u7ecf\u5929\u556e\u6765\u3002 \u3000\u3000\u5510\u7ecf\u5929\u7b11\u9053\uff1a\u201c\u8c01\u8981\u4f60\u5ba2\u6c14\u554a\uff1f\u201d\u53ea\u542c\u5f97\u547c\u7684\u4e00\u58f0\uff0c\u666f\u6708\u4e0a\u4eba\u7684\u957f\u8896\u5df2\u5377\u8d77\u4e00\u4eba\uff0c \u5374\u539f\u6765\u662f\u5510\u7ecf\u5929\u4ee5\u8fc5\u75be\u65e0\u4f26\u7684\u624b\u6cd5\uff0c\u5c06\u4e00\u4e2a\u6b66\u4e0a\u63a8\u8fc7\u53bb\uff0c\u5f85\u5230\u666f\u4e14\u4e0a\u4eba\u5bdf\u89c9\uff0c\u4ed6\u7684\u957f\u8896 \u5df2\u628a\u90a3\u6b66\u58eb\u5377\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5c06\u90a3\u6b66\u58eb\u63a8\u8fc7\u53bb\u7684\u65f6\u5019\uff0c\u5df2\u7528\u4e0a\u4e86\u201c\u9694\u5c71\u6253\u725b\u201d\u7684\u5185\u529b\uff0c\u666f\u6708\u4e0a\u4eba\u88ab\u8fd9\u80a1\u529b \u9053\u4e00\u649e\uff0c\u80f8\u53e3\u5982\u53d7\u94c1\u9524\uff0c\u7acb\u8db3\u4e0d\u7a33\uff0c\u8fde\u9000\u6570\u6b65\uff0c\u6025\u5c06\u90a3\u4eba\u6454\u5f00\uff0c\u5374\u53c8\u649e\u7ffb\u4e86\u51e0\u4e2a\u6b66\u571f\u3002 \u8fd9\u4e25\u5bc6\u65e0\u7f1d\u7684\u5706\u9635\u5f00\u4e86\u4e00\u4e2a\u7f3a\u53e3\uff0c\u9635\u811a\u767b\u65f6\u4e71\u4e86\u3002 \u3000\u3000\u5b5f\u54c8\u8d64\u4e00\u89c1\u4e0d\u5999\uff0c\u7387\u9886\u56db\u540d\u6b66\u58eb\uff0c\u586b\u4e0a\u7f3a\u53e3\u3002\u8fd9\u56db\u540d\u6b66\u571f\u90fd\u662f\u4ed6\u7684\u5f1f\u5b50\uff0c\u6bcf\u4eba\u90fd\u6709 \u4e00\u6839\u706b\u68d2\uff0c\u4e94\u6839\u706b\u68d2\u540c\u65f6\u5411\u5510\u7ecf\u5929\u6307\u53bb\uff0c\u53d1\u51fa\u4e86\u4e94\u9053\u70bd\u70ed\u7684\u706b\u7130\uff01\u5510\u7ecf\u5929\u559d\u9053\uff1a\u201c\u6765\u5f97 \u597d\uff01\u201d\u53cc\u638c\u63a8\uff0c\u53d1\u51fa\u4e86\u6392\u5c71\u5012\u6d77\u822c\u7684\u5288\u7a7a\u638c\u529b\uff0c\u5b5f\u54c8\u8d64\u5927\u53eb\u4e00\u58f0\uff0c\u55b7\u51fa\u4e86\u4e00\u5927\u80a1\u9c9c\u8840\uff0c \u4ed6\u90a3\u56db\u4e2a\u5f1f\u4e8e\u66f4\u60e8\uff0c\u4e00\u9f50\u8dcc\u5012\u5730\u4e0a\uff0c\u5df2\u662f\u4e0d\u80fd\u52a8\u5f39\u3002\u8fd9\u8fd8\u4e0d\u6b62\uff0c\u90a3\u4e94\u9053\u706b\u7130\uff0c\u4e5f\u88ab\u5510\u7ecf \u5929\u7684\u638c\u98ce\uff0c\u5377\u4e86\u56de\u53bb\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u7a7f\u7740\u7279\u5236\u7684\u77f3\u68c9\u8863\u670d\uff0c\u672c\u6765\u662f\u65e2\u53ef\u5fa1\u5bd2\uff0c\u4ea6\u53ef\u9632\u706b\uff0c\u4f46\u773c\u8033\u9f3b\u53e3\uff0c\u6ca1\u6709\u906e\u63a9\uff0c \u5374\u662f\u96be\u9632\uff0c\u53ea\u597d\u4e3e\u8d77\u8896\u5b50\uff0c\u8499\u7740\u9762\u5b54\u3002 \u3000\u3000\u907f\u5f00\u98ce\u5934\u706b\u52bf\uff0c\u767b\u65f6\u4f3c\u4e00\u7fa4\u6ca1\u5934\u82cd\u8747\uff0c\u56db\u5904\u4e71\u7a9c\u3002\u666f\u6708\u4e0a\u4eba\u5927\u6012\uff0c\u4e00\u638c\u5411\u5510\u7ecf\u5929\u5288 \u6765\uff0c\u5510\u7ecf\u5929\u8fd8\u4e86\u4e00\u638c\uff0c\u666f\u6708\u4e0a\u4eba\u5ffd\u5730\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u4f17\u4eba\u90fd\u89c9\u5947\u602a\uff0c\u773c\u89c1\u666f\u6708\u4e0a\u4eba\u62fc\u4e86\u4e00\u638c\uff0c\u5df2\u662f\u6447\u6447\u6643\u6643\uff0c\u770b\u8fd9\u60c5\u5f62\uff0c\u6700\u591a\u662f\u52c9\u5f3a\u652f \u6301\u5f97\u4f4f\uff0c\u5374\u600e\u4e48\u8fd8\u7b11\u5f97\u51fa\u6765\uff1f\u6e10\u6e10\u53d1\u89c9\u4ed6\u7684\u7b11\u58f0\u4e0d\u5bf9\uff0c\u7b11\u58f0\u6709\u5982\u5e72\u53f7\uff0c\u8eab\u4f53\u5374\u4f3c\u50f5\u786c\u4e00 \u822c\uff0c\u8fde\u773c\u775b\u4e5f\u4e0d\u4f1a\u8f6c\u52a8\u3002\u539f\u6765\u5510\u7ecf\u5929\u662f\u4ee5\u201c\u987b\u5f25\u638c\u201d\u5939\u7740\u201c\u91d1\u94a2\u6307\u201d\u7684\u5929\u5c71\u7edd\u6280\uff0c\u4e00\u638c \u9707\u6563\u4ed6\u7684\u62a4\u8eab\u6c14\u529b\uff0c\u968f\u5373\u70b9\u4e86\u4ed6\u7684\u201c\u7b11\u8170\u7a74\u201d\u3002\u666f\u6708\u4e0a\u4eba\u771f\u6c14\u5df2\u6563\u3001\u7a74\u9053\u5f53\u7136\u4f7f\u5c01\u95ed\u4e0d \u4f4f\u4e86\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u63d2\u5251\u5f52\u9798\uff0c\u4ee5\u8fc5\u6377\u65e0\u4f26\u7684\u624b\u6cd5\uff0c\u53cc\u624b\u53d1\u51fa\u51b0\u5f39\uff0c\u8fd9\u4e9b\u6b66\u58eb\u7684\u529f\u529b\u8fdc\u4e0d\u80fd\u4e0e \u666f\u6708\u4e0a\u4eba\u76f8\u6bd4\uff0c\u51b0\u5ddd\u592d\u5973\u7684\u51b0\u5f39\u53c8\u4e13\u6253\u4e03\u7a8d\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u4e09\u5341\u516d\u540d\u6b66\u58eb\uff0c\u9664\u4e86\u4e24\u540d\u5df2\u7ed9 \u5510\u7ecf\u5929\u6253\u4f24\uff0c\u65e9\u5df2\u5012\u4e0b\u7684\u4e4b\u5916\uff0c\u4eba\u4eba\u90fd\u4e2d\u4e86\u4e00\u9897\u51b0\u9b44\u795e\u5f39\uff0c\u51b7\u5f97\u50f5\u4e86\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u5e7d\u840d\uff0c\u4f60\u5e2e\u6211\u62bc\u89e3\u4ed6\u4eec\u56de\u56fd\u3002\u201d\u5b9d\u8c61\u6cd5\u5e08\u5ffd\u9053\uff1a\u201c\u4e14\u6162\uff0c\u6211\u6709\u8bdd\u8bf4\u3002\u201d \u53ea\u89c1\u4ed6\u8d70\u5230\u573a\u4e2d\uff0c\u5728\u666f\u6708\u4e0a\u4eba\u80cc\u5fc3\u8f7b\u8f7b\u4e00\u62cd\uff0c\u666f\u6708\u4e0a\u4eba\u7b11\u58f0\u767b\u65f6\u505c\u6b62\uff0c\u8138\u8272\u4e5f\u6e10\u6e10\u7ea2\u6da6\u3002 \u8fd9\u65f6\u706b\u7130\u65e9\u5df2\u88ab\u51b0\u5f39\u6563\u53d1\u7684\u5bd2\u5149\u51b7\u96fe\u6251\u706d\uff0c\u5b9d\u8c61\u6cd5\u5e08\u811a\u6b65\u4e0d\u505c\uff0c\u5728\u90a3\u4e9b\u50f5\u7acb\u7684\u6b66\u58eb\u4e2d\u95f4 \u7a7f\u6765\u63d2\u53bb\uff0c\u5728\u6bcf\u4e2a\u4eba\u8eab\u4e0a\u90fd\u8f7b\u8f7b\u62cd\u4e86\u4e00\u4e0b\u3002\u4ed6\u6240\u5230\u4e4b\u5904\uff0c\u5bd2\u96fe\u4fbf\u5373\u6d88\u6563\uff0c\u800c\u6bcf\u4e00\u4e2a\u88ab\u4ed6 \u89e6\u53ca\u7684\u6b66\u58eb\uff0c\u4e5f\u767b\u65f6\u80fd\u591f\u52a8\u5f39\u3002 \u3000\u3000\u8fd9\u624b\u672c\u9886\u4e00\u9732\uff0c\u4eba\u4eba\u8038\u7136\u52a8\u5bb9\uff0c\u5510\u7ecf\u5929\u592b\u5987\u4e5f\u4e0d\u7981\u5fc3\u5934\u5fae\u51db\u3002\u8981\u77e5\u62b5\u5fa1\u51b0\u9b44\u795e\u5f39\u7684 \u5bd2\u6c14\u5df2\u4e0d\u5bb9\u6613\uff0c\u800c\u8fd9\u5b9d\u8c61\u6cd5\u5e08\uff0c\u5374\u7adf\u7136\u80fd\u5728\u7247\u523b\u4e4b\u95f4\uff0c\u7528\u672c\u8eab\u529f\u529b\uff0c\u66ff\u4e09\u5341\u4e94\u4eba\uff0c\u4e09\u5341 \u56db\u540d\u6b66\u58eb\u52a0\u4e0a\u5b5f\u54c8\u8d64\u9a71\u9664\u4fb5\u5165\u4f53\u5185\u7684\u5bd2\u6c14\uff0c\u540c\u65f6\u8fd8\u89e3\u4e86\u666f\u6708\u4e0a\u4eba\u7684\u7a74\u9053\uff0c\u5e2e\u52a9\u4ed6\u771f\u6c14\u91cd \u805a\uff0c\u6062\u590d\u529f\u529b\uff0c\u5982\u6b64\u795e\u5947\u672c\u9886\u3001\u5f53\u771f\u662f\u96be\u4ee5\u601d\u8bae\uff01 \u3000\u3000\u5510\u7ecf\u5929\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u624b\u672c\u9886\uff0c\u6211\u7239\u7239\u53ef\u4ee5\u505a\u5f97\u5230\uff0c\u4f46\u5374\u4e5f\u672a\u5fc5\u80fd\u4f3c\u8fd9\u53ae\u7684\u7acb\u7aff\u89c1 \u5f71\uff0c\u5373\u65f6\u751f\u6548\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6709\u8bdd\u8bf7\u8bf4\u3002\u201d\u5b9d\u8c61\u6cd5\u5e08\u9053\uff1a\u201c\u8d2b\u50e7\u5fdd\u5c5e\u6b64\u4f1a\u4e3b\u4eba\uff0c\u60f3\u5411\u516c\u4e3b\u8ba8\u4e00\u4e2a \u60c5\u3002\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5b9d\u8c61\u6cd5\u5e08\u9053\uff1a \u3000\u3000\u201c\u6b64\u4f1a\u7531\u8d2b\u50e7\u53ec\u5f00\uff0c\u5230\u4f1a\u7684\u4fbf\u90fd\u662f\u6211\u7684\u5ba2\u4eba\uff0c\u73b0\u5728\u516c\u4e3b\u8981\u5c06\u8fd9\u4e9b\u4eba\u5e26\u8d70\uff0c\u5c82\u4e0d\u662f\u6559 \u8d2b\u50e7\u4e3a\u96be\u4e86\u4e48\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u5b5f\u54c8\u8d64\u65e9\u5df2\u5bf9\u6cd5\u5e08\u8bf4\u8fc7\uff0c\u8fd9\u662f\u6211\u4eec\u672c\u56fd\u7684\u4e8b\u60c5\uff0c\u5e76\u975e\u5bfb\u5e38\u6bd4\u6b66\u53ef\u6bd4\u3002 \u5f53\u65f6\u6cd5\u5e08\u4e5f\u66fe\u58f0\u8a00\u8896\u624b\u4e0d\u7ba1\u7684\uff0c\u4f55\u4ee5\u5982\u4eca\u53c8\u6709\u5f02\u8bae\uff1f\u201d\u5b9d\u8c61\u6cd5\u5e08\u9053\uff1a\u201c\u4f60\u4eec\u521a\u624d\u52a8\u624b\uff0c \u8d2b\u50e7\u786e\u662f\u672a\u66fe\u591a\u7ba1\u3002\u4f46\u516c\u4e3b\u4f60\u8981\u5c06\u4ed6\u4eec\u5e26\u8d70\uff0c\u8fd9\u5374\u662f\u8981\u8d2b\u50e7\u5bf9\u4e0d\u4f4f\u670b\u53cb\u4e86\u3002\u516c\u4e3b\u662f\u5426\u53ef \u4ee5\u7ed9\u6211\u4e00\u4e2a\u9762\u5b50\uff0c\u5f85\u6b64\u4f1a\u6563\u4e86\u4e4b\u540e\uff0c\u90a3\u65f6\u4fbf\u7531\u5f97\u4f60\u4eec\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u6cd5\u5e08\u7684\u5ba2\u4eba\u53ef\u5206\u4e3a\u51e0\u7b49\uff1f\u662f\u5426\u6839\u636e\u5907\u4eba\u4e0e\u6cd5\u5e08\u7684\u4ea4\u60c5\u800c \u5b9a\uff0c\u4eb2\u758f\u6709\u6240\u4e0d\u540c\uff1f\u201d\u5b9d\u8c61\u6cd5\u5e08\u9762\u8272\u5fae\u53d8\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u5230\u4f1a\u7684\u90fd\u662f\u597d\u670b\u53cb\uff0c\u8d2b\u50e7\u4e00\u89c6 \u540c\u4ec1\uff0c\u90fd\u662f\u4e00\u822c\u770b\u5f85\u3002\u516c\u4e3b\u53e3\u51fa\u6b64\u8a00\uff0c\u4e0d\u77e5\u4f55\u6240\u89c1\u800c\u4e91\u7136\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u521a\u624d\u8fd9\u73ed \u4eba\u6043\u7740\u4eba\u591a\u52bf\u5927\uff0c\u8981\u2018\u8bf7\u2019\u6211\u56de\u56fd\uff0c\u8fd9\u2018\u8bf7\u2019\u5b57\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u6cd5\u5e08\u5f53\u7136\u660e\u767d\u3002\u4f55\u4ee5\u90a3\u65f6 \u6cd5\u5e08\u4e0d\u4f5c\u4e00\u58f0\uff1f\u73b0\u5728\u4ed6\u4eec\u4e0d\u5e78\u4e00\u8d25\u6d82\u5730\uff0c\u8f6e\u5230\u6211\u8981\u2018\u8bf7\u2019\u4ed6\u4eec\u56de\u56fd\u4e86\uff0c\u6cd5\u5e08\u4f60\u8fd9\u624d\u51fa\u6765 \u963b\u6320\uff01\u4f55\u4ee5\u524d\u540e\u4e0d\u540c\uff0c\u6709\u5982\u662f\u8005\uff1f\u6cd5\u5e08\uff0c\u8bf7\u6055\u6211\u4e0b\u611a\u3001\u4e0d\u80fd\u4e0d\u95ee\uff01\u201d \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u7ed9\u5979\u95ee\u5f97\u5927\u662f\u5c34\u5c2c\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u516c\u4e3b\u6709\u6240\u8bef\u4f1a\u4e86\u3002 \u3000\u3000\u6211\u521a\u624d\u4e0d\u62e6\u62e6\u4ed6\u4eec\uff0c\u6b63\u662f\u51fa\u4e8e\u5bf9\u516c\u4e3b\u7684\u5c0a\u656c\u3002\u60f3\u516c\u4e3b\u51b0\u5f39\u7389\u5251\uff0c\u72ec\u6b65\u6b66\u6797\uff0c\u5c82\u662f\u4ed6 \u4eec\u6240\u80fd\u6297\u624b\uff1f\u6211\u4e0d\u52a0\u963b\u62e6\uff0c\u6b63\u662f\u8981\u516c\u4e3b\u6559\u8bad\u6559\u8bad\u4ed6\u4eec\uff0c\u540c\u65f6\u4e5f\u53ef\u4ee5\u8ba9\u6211\u4eec\u5f00\u5f00\u773c\u754c\u5440\uff01\u201d \u8fd9\u8bdd\u8bf4\u5f97\u7275\u5f3a\u4e4b\u6781\uff0c\u51b0\u5ddd\u592d\u5973\u51b7\u7b11\u9053\uff1a\u201c\u7136\u5219\u4f60\u4f55\u4ee5\u73b0\u5728\u53c8\u4e0d\u8bb8\u6211\u6559\u8bad\u4ed6\u4eec\u4e86\uff1f\u201d\u5b9d\u8c61 \u6cd5\u5e08\u9053\uff1a\u201c\u73b0\u5728\u80dc\u8d1f\u5df2\u51b3\uff0c\u8fd9\u5c31\u662f\u4e24\u56de\u4e8b\u4e86\u3002\u6b64\u4f1a\u672a\u6563\uff0c\u6211\u5c31\u6709\u4fdd\u62a4\u5ba2\u4eba\u7684\u8d23\u4efb\u3002\u201d \u3000\u3000\u539f\u6765\u5c3c\u6cca\u5c14\u524d\u738b\u6d3e\u4e48\u4e9b\u4eba\u5230\u9a6c\u8428\u513f\u56fd\u6765\uff0c\u5b9e\u662f\u6000\u7740\u4e24\u4e2a\u76ee\u7684\uff0c\u4e00\u662f\u6349\u62ff\u51b0\u5ddd\u5929\u5973\uff1b \u4e8c\u662f\u5411\u9a6c\u8428\u513f\u56fd\u6c42\u63f4\uff0c\u51c6\u5907\u501f\u5175\u56de\u53bb\u5e73\u201c\u4e71\u201d\u7684\u3002\u5b9d\u8c61\u6cd5\u5e08\u4e0e\u56fd\u738b\u540c\u4e00\u9f3b\u5b54\u51fa\u6c14\uff0c\u5f53\u7136 \u4e0d\u80fd\u8ba9\u8fd9\u4e9b\u4eba\u53cd\u800c\u53d8\u4f5c\u51b0\u5ddd\u5929\u5973\u7684\u4fd8\u864f\u3002 \u3000\u3000\u773c\u770b\u53cc\u65b9\u5c31\u8981\u8bf4\u50f5\uff0c\u5ffd\u542c\u5f97\u949f\u58f0\u5f53\u5f53\uff0c\u8fdc\u8fdc\u4f20\u6765\uff0c\u5728\u949f\u58f0\u95f4\u6b27\u4e4b\u9645\uff0c\u5b9d\u8c61\u6cd5\u5e08\u51dd\u795e \u7ec6\u542c\uff0c\u8fd8\u9690\u7ea6\u53ef\u4ee5\u542c\u5f97\u91d1\u9f13\u4e4b\u58f0\uff0c\u5b9d\u8c61\u6cd5\u5e08\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u6b63\u8981\u6d3e\u9063\u5f1f\u5b50\u51fa\u53bb\u6253\u542c\uff0c \u4ed6\u8fd9\u91d1\u9e70\u5bab\u5854\u9876\u4e0a\u7684\u5927\u949f\u4e5f\u54cd\u8d77\u6765\u4e86\uff0c\u8fd9\u949f\u58f0\u662f\u62a5\u544a\u6709\u7a81\u7136\u7684\u53d8\u6545\u53d1\u751f\uff01 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u5587\u561b\u5306\u5306\u8fdb\u6765\uff0c\u6b63\u662f\u5728\u949f\u697c\u804c\u53f8\u5b88\u671b\u7684\u5587\u561b\u4e4b\u4e00\uff0c\u4ed6\u5df2\u987e\u4e0d\u53ca\u5411\u5b9d\u8c61\u6cd5\u5e08 \u884c\u793c\uff0c\u4fbf\u5373\u7980\u9053\uff1a\u201c\u7687\u5bab\u8d77\u706b\uff0c\u8b66\u949f\u5df2\u4e00\u7ad9\u7ad9\u5730\u6572\u54cd\u4e86\uff0c\u770b\u8fd9\u60c5\u5f62\uff0c\u4f3c\u662f\u5df2\u996d\u53db\u519b\u653b\u5165\uff01\u201d \u7687\u5bab\u4e0e\u91d1\u9e70\u5bab\u76f8\u8ddd\u4e09\u5341\u91cc\uff0c\u4e2d\u95f4\u8bbe\u6709\u4e09\u4e2a\u949f\u9f13\u697c\uff0c\u5018\u82e5\u9047\u5230\u7d27\u6025\u7684\u53d8\u6545\uff0c\u5feb\u9a6c\u9a70\u62a5\u90fd\u6015 \u6765\u4e0d\u53ca\u7684\u6d3b\uff0c\u5c31\u7528\u949f\u58f0\u62a5\u8b66\uff0c\u5411\u91d1\u9e70\u5b98\u547c\u6388\u3002\u4f46\u81ea\u8bbe\u949f\u9f13\u697c\u4ee5\u6765\uff0c\u5374\u4ece\u672a\u66fe\u7528\u8fc7\u3002 \u3000\u3000\u91d1\u9e70\u5bab\u7684\u5f1f\u5b50\u4e00\u95fb\u6b64\u8baf\uff0c\u90fd\u4e71\u8d77\u6765\u3002\u5b9d\u8c61\u6cd5\u5e08\u6545\u4f5c\u9547\u5b9a\uff0c\u559d\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\u5927\u4e0d\u4e86 \u7684\u4e8b\u60c5\uff0c\u53f6\u585e\u7f57\u3001\u798f\u8944\u963f\uff0c\u4f60\u4eec\u7387\u672c\u5bfa\u50e7\u4fa3\uff0c\u5373\u8d74\u7687\u5bab\u3002\u8fd9\u91cc\u5927\u4f1a\u5982\u5e38\u4e3e\u884c\u3002\u201d\u53f6\u3001\u798f \u4e8c\u4eba\u662f\u4ed6\u6700\u5f97\u529b\u7684\u4e24\u4e2a\u5f1f\u5b50\uff0c\u91d1\u9e70\u5bab\u6709\u5343\u4f59\u50e7\u4eba\uff0c\u4e2a\u4e2a\u4e5f\u90fd\u6709\u4e00\u8eab\u6b66\u529f\uff0c\u5b9d\u8c61\u6cd5\u5e08\u6599\u60f3 \u4ed6\u4eec\u81f3\u4e0d\u6d4e\u4e5f\u53ef\u4ee5\u62b5\u6321\u4e00\u65f6\uff0c\u7687\u5bab\u91cc\u6709\u5fa1\u6797\u519b\uff0c\u4eac\u57ce\u8fd8\u6709\u4e5d\u8425\u201c\u864e\u519b\u201d\u90fd\u662f\u5fe0\u4e8e\u56fd\u738b\u7684\uff0c \u53ea\u8981\u5404\u5904\u519b\u961f\u8d76\u6765\uff0c\u7687\u5bab\u81ea\u53ef\u8f6c\u5371\u4e3a\u5b89\uff0c\u5b9d\u8c61\u6cd5\u5e08\u62c5\u5fc3\u7684\u5012\u662f\u76ee\u524d\u7684\u8fd9\u4e2a\u5927\u4f1a\u5f62\u52bf\u3002 \u3000\u3000\u53f6\u3001\u798f\u4e8c\u4eba\u5306\u5306\u51fa\u53bb\u53ec\u96c6\u50e7\u4fa3\uff0c\u91d1\u9f13\u58f0\u6108\u6765\u6108\u8fd1\u3001\u4eba\u5fc3\u6d6e\u52a8\uff0c\u4f1a\u573a\u4e2d\u7684\u79e9\u5e8f\u4e00\u65f6\u95f4 \u54ea\u91cc\u80fd\u591f\u6062\u590d\uff1f\u90a3\u4e9b\u4e0d\u61c2\u9a6c\u8428\u513f\u571f\u8bdd\u7684\uff0c\u66f4\u662f\u5f7c\u6b64\u8be2\u95ee\uff0c\u63a2\u542c\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\u3002 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u9053\uff1a\u201c\u5404\u4f4d\u6bcb\u9700\u60ca\u6050\uff0c\u7687\u57ce\u6709\u5c11\u6570\u53db\u519b\u4f5c\u4e71\uff0c\u5df2\u7ecf\u9547\u5339\u4e0b\u53bb\u4e86\u3002\u201d\u8bdd\u72b9\u672a \u4e86\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u95f9\u58f0\u5982\u96f7\uff0c\u5939\u6742\u6709\u5175\u5668\u78b0\u51fb\u7684\u58f0\u97f3\uff0c\u9a6c\u8e44\u9a70\u9a8b\u7684\u58f0\u97f3\uff0c\u6709\u5982\u66b4\u98ce\u9aa4\u96e8\uff0c \u5b9d\u8c61\u6cd5\u5e08\u53d8\u4e86\u9762\u8272\uff0c\u559d\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff01\u53db\u519b\u76ee\u65e0\u7687\u4e0a\u4e5f\u8fd8\u7f62\u4e86\uff0c\u7adf\u7136\u8fd8\u6562\u6740\u5230\u6211\u7684\u91d1 \u9e70\u5bab\u6765\u5417\uff1f\u201d \u3000\u3000\u62a4\u6cd5\u5927\u5f1f\u5b50\u9053\uff1a\u201c\u5f85\u6211\u51fa\u53bb\u770b\u770b\uff0c\u6709\u53f6\u3001\u798f\u4e24\u4f4d\u5e08\u5f1f\u9632\u5b88\uff0c\u6599\u53ef\u65e0\u59a8\u3002\u201d\u4ed6\u521a\u521a\u8d70 \u5230\u95e8\u8fb9\uff0c\u53ea\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u5927\u95e8\u5df2\u88ab\u6253\u5f00\uff0c\u5728\u5916\u9762\u5b88\u536b\u7684\u6b66\u571f\u5982\u6f6e\u6d8c\u5165\uff0c\u53eb\u9053\uff1a \u201c\u4e0d\u597d\u4e86\uff0c\u53db\u519b\u6740\u6765\u4e86\u5566\uff01\u201d \u3000\u3000\u62a4\u6cd5\u5927\u5f1f\u5b50\u62ac\u773c\u671b\u53bb\uff0c\u5374\u4e0d\u89c1\u6709\u751a\u4e48\u53db\u519b\uff0c\u53ea\u89c1\u4e00\u4e2a\u5e74\u8f7b\u519b\u5b98\uff0c\u4e00\u624b\u631f\u7740\u4e00\u4eba\uff0c\u6b63 \u5728\u5927\u8e0f\u6b65\u8d70\u8fdb\u6765\u3002\u867d\u7136\u53ea\u662f\u4e00\u4eba\uff0c\u5374\u5f15\u8d77\u5168\u573a\u8f70\u52a8\uff0c\u6b22\u547c\u58f0\u60ca\u53eb\u58f0\u521a\u6df7\u6210\u4e00\u7247\u3002\u539f\u6765\u8fd9 \u4e2a\u9752\u5e74\u519b\u5b98\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u5510\u52aa\u73e0\u7a46\uff0c\u88ab\u4ed6\u631f\u7740\u7684\u90a3\u4e24\u4e2a\u4eba\uff0c\u5374\u662f\u521a\u521a\u5949\u547d\u51fa\u53bb\u7684\u3001\u5b9d \u8c61\u6cd5\u5e08\u90a3\u4e24\u4e2a\u5f97\u529b\u5f1f\u5b50\u2014\u2014\u53f6\u585e\u7f57\u548c\u798f\u8944\u963f\u3002\u6c5f\u6d77\u5929\u3001\u8c37\u4e2d\u83b2\u7b49\u4eba\u6b22\u547c\uff0c\u91d1\u9e70\u5bab\u7684\u4e00\u4f17 \u5f1f\u5b50\u5219\u4e0d\u514d\u5931\u58f0\u60ca\u53eb\u4e86\uff01 \u3000\u3000\u62a4\u6cd5\u5927\u5f1f\u5b50\u4e0d\u7981\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u5feb\u628a\u6211\u7684\u5e08\u5f1f\u653e\u4e0b\u6765\uff01\u201d\u6325\u6756\u4fbf\u70b9\u5510\u52aa\u73e0\u7a46\u819d\u76d6\u7684 \u201c\u73af\u8df3\u7a74\u201d\uff0c\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u8981\u4eba\u5bb9\u6613\uff0c\u4f55\u5fc5\u52a8\u7c97\uff1f\u201d\u4e00\u811a\u8e39\u4e0b\uff0c\u8e0f\u4f4f\u6756\u5934\uff0c\u62a4\u6cd5\u5927\u5f1f \u5b50\u7528\u529b\u4e00\u62d4\uff0c\u9762\u7ea2\u8033\u8d64\uff0c\u5140\u662f\u62d4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u4e0d\u542c\u4e49\u519b\u7981\u4ee4\uff0c\u64c5\u81ea\u9a70\u8d74\u738b\u5bab\uff0c\u6545\u6b64\u6211\u628a\u4ed6\u4eec\u63ea\u4e0b\u9a6c \u6765\uff0c\u62ff\u5230\u6b64\u5730\u3002\u65e2\u7136\u662f\u4f60\u7684\u5e08\u5f1f\uff0c\u4f60\u5c31\u9886\u4ed6\u4eec\u56de\u53bb\uff0c\u597d\u597d\u7ba1\u6559\u5427\u3002\u201d\u5c06\u53f6\u3001\u798f\u4e8c\u4eba\u4e00\u629b\uff0c \u968f\u5373\u63d0\u8d77\u811a\u6765\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u90a3\u4e00\u629b\u7528\u7684\u4e43\u662f\u5de7\u52b2\uff0c\u53f6\u3001\u798f\u4e8c\u4eba\u5728\u534a\u7a7a\u4e2d\u7ffb\u4e86\u4e2a\u7b4b\u6597\uff0c\u5e73\u5e73\u7a33\u7a33\u7684\u843d\u5728 \u5730\u571f\uff0c\u5e76\u672a\u53d7\u4f24\uff0c\u90a3\u62a4\u6cd5\u5927\u5f1f\u5b50\u6b63\u5728\u7528\u529b\u62d4\u6756\uff0c\u5510\u52aa\u73e0\u7a46\u7a81\u7136\u79fb\u5f00\u811a\u6b65\uff0c\u4ed6\u4e0d\u80fd\u4fdd\u6301\u5e73 \u8861\uff0c\u5374\u91cd\u91cf\u7684\u6454\u4e86\u4e00\u8de4\u3002 \u3000\u3000\u91d1\u9e70\u5bab\u4f17\u5f1f\u5b50\u5c06\u5510\u52aa\u73e0\u7a46\u56e2\u56e2\u56f4\u4f4f\uff0c\u5b9d\u8c61\u6cd5\u5e08\u770b\u51fa\u5510\u52aa\u73e0\u7a46\u6b66\u529f\u5353\u7edd\uff0c\u60c5\u77e5\u4f17\u5f1f\u5b50 \u51b3\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u4fbf\u5373\u559d\u9053\uff1a\u201c\u4f60\u4eec\u9000\u4e0b\uff0c\u5f85\u6211\u95ee\u4ed6\u3002\u201d \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u95ee\u9053\uff1a\u201c\u4f60\u662f\u53db\u519b\u9996\u9886\u5417\uff0c\u64c5\u95ef\u6211\u7684\u91d1\u9e70\u5bab\u610f\u6b32\u4f55\u4e3a\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a \u201c\u4f60\u5c31\u662f\u5b9d\u8c61\u6cd5\u5e08\u5417\uff1f\u201d\u5b9d\u8c61\u6cd5\u5e08\u50b2\u7136\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6211\u8fd8\u662f\u4f60\u4eec\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u5e08\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u9887\u602a\u5510\u52aa\u73e0\u7a46\u4e0d\u61c2\u793c\u8c8c\uff0c\u89c1\u4e86 \u4ed6\u7adf\u4e0d\u884c\u793c\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u6b63\u6709\u8bdd\u8981\u548c\u4f60\u8bf4\uff0c\u7b2c\u4e00\uff0c\u6211\u8981\u901a\u77e5\u4f60\uff0c\u4ece\u73b0\u5728\u8d77\uff0c\u4f60 \u4e0d\u518d\u662f\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u5e08\u4e86\uff01\u201d \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u4f60\u542f\u4ee5\u4e3a\u662f\u4ec0\u4e48\u4eba\uff0c\u6709\u8fd9\u4e48\u5927\u7684\u6743\u529b\uff1f\u4f60\u662f\u9a6c\u8428\u513f\u56fd\u7684\u65b0 \u7687\u5e1d\u5417\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7687\u5e1d\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7a00\u5947\uff0c\u6211\u5bb6\u4e16\u4e16\u4ee3\u4ee3\u672c\u6765\u5c31\u662f\u9a6c\u8428\u513f\u56fd\u7684\u7687 \u5e1d\u3002\u5c06\u4f60\u8bf7\u6765\u5f53\u56fd\u5e08\u7684\u90a3\u4e2a\u76d6\u6e29\uff0c\u4e0d\u8fc7\u662f\u6211\u7236\u738b\u624b\u4e0b\u7684\u4e00\u4e2a\u4e71\u81e3\u8d3c\u5b50\uff0c\u4ed6\u5f11\u541b\u81ea\u7acb\uff0c\u6b8b \u6c11\u4ee5\u901e\uff0c\u7f6a\u4e0d\u5bb9\u8bdb\u3002\u6211\u4e0d\u662f\u4e3a\u4e86\u8981\u505a\u7687\u5e1d\u800c\u6765\uff0c\u4f46\u5374\u975e\u628a\u4ed6\u94f2\u9664\u4e0d\u53ef\u3002\u4f60\u662f\u4ed6\u8bf7\u6765\u7684\u56fd \u5e08\uff0c\u6211\u4e0d\u540c\u4f60\u5e2e\u51f6\u4e4b\u7f6a\uff0c\u5df2\u662f\u5bbd\u5f85\u4f60\u4e86\uff0c\u96be\u9053\u4f60\u8fd8\u8981\u6211\u4eec\u7ee7\u7eed\u627f\u8ba4\u4f60\u662f\u56fd\u5e08\uff0c\u5c06\u4f60\u6367\u4e0a \u4e09\u5341\u4e09\u5929\uff0c\u5411\u4f60\u819c\u62dc\u4e48\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u8bf4\u51fa\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u91d1\u9e70\u5b98\u90a3\u73ed\u4eba\u66f4\u662f\u5927\u5927\u5403\u60ca\uff0c\u76d6\u6e29\u7684\u5fc3\u8179\u6b66\u58eb\u7eb7\u7eb7\u559d\u9053\uff1a \u201c\u56fd\u5e08\u4f11\u8981\u542c\u4ed6\u80e1\u8bf4\uff0c\u4ed6\u5206\u660e\u662f\u5192\u5145\u524d\u738b\u7684\u513f\u5b50\uff0c\u6765\u6b64\u86ca\u611f\u4eba\u5fc3\uff0c\u5feb\u5feb\u628a\u4ed6\u62ff\u4e0b\uff0c\u6cbb\u4ed6 \u53db\u9006\u4e4b\u7f6a\u3002\u201d \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u6446\u4e86\u6446\u624b\uff0c\u5fcd\u7740\u6012\u6c14\uff0c\u53c8\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6682\u4e14\u4e0d\u7ba1\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c \u4f60\u8bf4\u4e86\u4e2a\u7b2c\u4e00\uff0c\u8fd8\u6709\u7b2c\u4e8c\u5417\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6709\uff0c\u8bf7\u4f60\u628a\u76d6\u6e29\u652f\u51fa\u6765\uff01\u2019 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u5fae\u9732\u559c\u8272\uff0c\u53c8\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u795e\u6c14\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u9020\u53cd \u5df2\u7ecf\u6210\u529f\u4e86\u5462\uff0c\u539f\u6765\u7687\u4e0a\u8fd8\u672a\u66fe\u843d\u5728\u4f60\u7684\u624b\u4e2d\uff01\u90a3\u4f60\u8fd8\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff0c\u8d76\u5feb\u5230\u522b\u5904\u53bb\u627e \u5427\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u51b7\u7b11\u9053\uff1a\u201c\u76d6\u6e29\u5c31\u5728\u4f60\u7684\u91d1\u9e70\u5bab\uff0c\u4f60\u8981\u60f3\u62b5\u8d56\u4e48\uff1f\u201d\u5b9d\u8c61\u6cd5\u5e08\u54fc\u4e86\u4e00\u58f0\uff0c \u8bf4\u9053\uff1a\u201c\u597d\u4e2a\u65e0\u793c\u7684\u5c0f\u5b50\uff0c\u597d\u5427\uff0c\u4f60\u4e00\u5b9a\u8981\u8bf4\u56fd\u738b\u5728\u6211\u8fd9\u513f\uff0c\u5c31\u7b97\u662f\u5427\uff0c\u4f60\u53c8\u5f85\u5982\u4f55\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4f60\u628a\u4ed6\u4ea4\u51fa\uff0c\u6211\u53ef\u4ee5\u51c6\u4f60\u643a\u5e26\u4f60\u7684\u5f1f\u5b50\u5b89\u7136\u56de\u56fd\u3002\u201d\u5b9d\u8c61\u6cd5\u5e08\u51b7\u7b11 \u9053\uff1a\u201c\u8981\u662f\u6211\u4e0d\u7b54\u5141\uff0c\u4f60\u53c8\u5982\u4f55\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4f60\u82e5\u52a9\u7ea3\u4e3a\u8650\uff0c\u90a3\u4e48\u6211\u4eec\u4e5f\u53ea\u6709\u4e0d \u548c\u4f60\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\u91d1\u9e70\u5bab\u7684\u5927\u95e8\u5df2\u7ecf\u6d1e\u5f00\uff0c\u671b\u51fa\u5916\u9762\uff0c\u53ea\u89c1\u96c4\u65d7\u62db\u5c55\uff0c\u9ed1\u538b\u538b\u7684\u90fd\u662f\u519b\u961f\u3002\u539f\u6765 \u5510\u52aa\u73e0\u7a46\u662f\u5e26\u4e86\u4e09\u5343\u7cbe\u9510\u7684\u5927\u5175\u6765\u7684\uff0c\u8fd9\u4e9b\u58eb\u5175\uff0c\u6709\u4e00\u90e8\u4efd\u662f\u4ed6\u65e9\u5df2\u8054\u7edc\u597d\u7684\uff0c\u5fe0\u4e8e\u524d \u738b\u7684\u8001\u81e3\u7684\u5bb6\u4e01\uff0c\u6709\u4e00\u90e8\u4efd\u5219\u662f\u76d6\u6e29\u7684\u5fa1\u6797\u519b\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6253\u8fdb\u4e86\u7687\u5bab\u4e4b\u540e\uff0c\u5fa1\u6797\u519b\u77e5\u9053\u4e86\u4ed6\u7684\u8eab\u4efd\uff0c\u53c8\u89c1\u5927\u52bf\u5df2\u53bb\uff0c\u5012\u6709\u5341\u4e4b\u4e03\u516b \u53db\u4e86\u76d6\u6e29\uff0c\u5f52\u987a\u4e8e\u4ed6\u3002\u53f6\u585e\u7f57\u548c\u798f\u8944\u963f\u6240\u7387\u9886\u7684\u90a3\u4e00\u5343\u50e7\u4eba\uff0c\u5c31\u662f\u56e0\u4e3a\u78b0\u4e0a\u4e86\u5510\u52aa\u73e0\u7a46 \u8fd9\u652f\u519b\u961f\uff0c\u521a\u79bb\u5f00\u91d1\u9e70\u5bab\u4e0d\u8fdc\uff0c\u5c31\u7ed9\u6253\u5f97\u4e03\u96f6\u516b\u843d\u4e86\u7684\u3002 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u89c1\u5510\u52aa\u73e0\u7a46\u5982\u6b64\u58f0\u52bf\uff0c\u4e5f\u81ea\u6697\u6697\u6709\u70b9\u5fc3\u602f\uff0c\u4f46\u5fc3\u91cc\u81ea\u601d\uff1a\u201c\u5018\u82e5\u5c31\u6b64\u8ba4\u8f93\uff0c \u90a3\u5c31\u6c38\u65e0\u5377\u4e0a\u91cd\u6765\u4e4b\u65e5\u3002\u4ed6\u4eec\u56fa\u7136\u662f\u4eba\u6570\u4e0d\u5c11\uff0c\u6211\u8fd9\u91cc\u4e5f\u662f\u9ad8\u624b\u5982\u4e91\uff0c\u4f55\u987b\u60e7\u6015\uff1f\u201d\u5f53 \u4e0b\u4e00\u58f0\u72de\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5c0f\u5b50\u3002\u5c31\u7b97\u4f60\u505a\u4e86\u7687\u5e1d\uff0c\u4f60\u64c5\u95ef\u6211\u7684\u91d1\u9e70\u5bab\uff0c\u6211\u4e5f\u8981\u62ff\u4f60\u95ee\u7f6a\uff01\u201d \u7b11\u58f0\u672a\u4e86\uff0c\u4fbf\u5373\u628a\u624b\u4e00\u4f38\uff0c\u5c31\u5411\u5510\u52aa\u73e0\u7a46\u6293\u4e0b\u6765\uff01 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u4e94\u6307\u4e00\u4f38\uff0c\u6c14\u6c89\u6fc0\u8361\uff0c\u53d1\u51fa\u4e86\u523a\u8033\u7684\u7834\u7a7a\u4e4b\u58f0\uff0c\u4ed6\u548c\u5510\u52aa\u73e0\u7a46\u4e4b\u95f4\uff0c\u672c\u6765 \u8fd8\u6709\u4e08\u8bb8\u8ddd\u79bb\uff0c\u8fd9\u4e00\u6293\u53ea\u662f\u51cc\u7a7a\u4f5c\u52bf\uff0c\u5e76\u672a\u66fe\u771f\u4e2a\u63a5\u89e6\u5230\u5510\u52aa\u73e0\u7a46\u7684\u8eab\u5b50\uff0c\u4f46\u5510\u52aa\u73e0\u7a46 \u5df2\u611f\u5230\u4e00\u80a1\u5927\u529b\u5c06\u4ed6\u7f69\u4f4f\uff0c\u7981\u4e0d\u4f4f\u6643\u4e86\u4e00\u6643\uff0c\u811a\u6b65\u4e5f\u7ad9\u7acb\u4e0d\u7a33\uff0c\u9669\u4e9b\u5c31\u8981\u88ab\u8fd9\u80a1\u5927\u529b\u51cc \u7a7a\u63d0\u8d77\uff01\u5510\u52aa\u73e0\u7a46\u5fc3\u5934\u4e00\u51db\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8981\u4e0d\u662f\u6211\u670d\u98df\u4e86\u90a3\u4e24\u9897\u5929\u5fc3\u77f3\uff0c\u53ea\u6015\u4ec5\u6b64\u4e00 \u62db\uff0c\u5c31\u8981\u8d25\u5728\u4ed6\u624b\u91cc\u4e86\u3002\u201d\u4f46\u4ed6\u6643\u4e86\u4e00\u6643\uff0c\u7ec8\u4e8e\u8fd8\u662f\u7ad9\u7a33\u4e86\u3002 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u662f\u5929\u7afa\u7b2c\u4e00\u9ad8\u50e7\u9f99\u53f6\u4e0a\u4eba\u7684\u9996\u5f92\uff0c\u9f99\u53f6\u4e0a\u4eba\u6709\u4e09\u6d0b\u7edd\u4e16\u795e\u529f\uff0c\u79f0\u4e3a\u201c\u4f5b\u95e8 \u9547\u9b54\u4e09\u7edd\u6280\u201d\uff0c\u5b9d\u8c61\u6cd5\u5e08\u73b0\u5728\u6240\u7528\u7684\u201c\u62ff\u4e91\u624b\u201d\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u4ed6\u8fd9\u4e00\u6293\u7adf\u672a\u66fe\u5c06\u5510\u52aa \u73e0\u7a46\u6293\u8d77\uff0c\u4e5f\u4e0d\u7981\u5fc3\u5934\u4e00\u51db\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5510\u52aa\u73e0\u7a46\u5df2\u662f\u4e00\u638c\u653b\u5230\uff0c\u539f\u6765\u4ed6\u6015\u5b9d\u8c61\u6cd5\u5e08\u518d\u5ea6\u6293\u4e0b\uff0c\u96be\u4ee5\u62b5\u5fa1\uff0c \u6545\u6b64\u5148\u884c\u62a2\u653b\u3002\u5b9d\u8c61\u6cd5\u5e08\u6709\u5fc3\u8bd5\u4ed6\u529f\u529b\uff0c\u6539\u6293\u4e3a\u638c\uff0c\u53cc\u65b9\u786c\u5bf9\u4e86\u4e00\u638c\uff0c\u8fd9\u6b21\u53cc\u638c\u4e00\u4ea4\uff0c \u5510\u52aa\u73e0\u7a46\u201c\u8e6c\uff0c\u8e6c\uff0c\u8e6c\u201d\u7684\u8fde\u9000\u4e09\u6b65\uff0c\u5b9d\u8c61\u6cd5\u5e08\u5374\u201c\u566b\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u539f\u6765\u4ed6\u548c\u5510\u52aa\u73e0\u7a46\u4e0d\u7ea6\u800c\u540c\u7684\u90fd\u662f\u7528\u4e86\u201c\u5927\u4e58\u822c\u82e5\u638c\u201d\u529f\u592b\uff0c\u9f99\u53f6\u4e0a\u4eba\u7684\u201c\u4f5b\u95e8\u9547 \u9b54\u4e09\u7edd\u6280\u201d\uff0c\u4e00\u662f\u201c\u62ff\u4e91\u624b\u201d\uff0c\u4e8c\u662f\u201c\u9f99\u8c61\u529f\u201d\uff0c\u5176\u4e09\u5c31\u662f\u8fd9\u201c\u5927\u4e58\u822c\u82e5\u638c\u201d\u3002 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u6765\u5230\u9a6c\u8428\u513f\u56fd\u4e4b\u540e\uff0c\u6536\u5f92\u751a\u591a\uff0c\u4f46\u5374\u53ea\u6709\u4e00\u4e2a\u53f6\u51b2\u9704\u66fe\u5f97\u4ed6\u4f20\u6388\u201c\u5927\u4e58\u822c \u82e5\u638c\u201d\u7684\u529f\u592b\u3002\u5b9d\u8c61\u6cd5\u5e08\u4ee5\u4e3a\u4e2d\u56fd\u65e0\u4eba\u80fd\u8bc6\u4ed6\u8fd9\u4e09\u5927\u7edd\u6280\uff0c\u54ea\u77e5\u5510\u52aa\u73e0\u7a46\u5c45\u7136\u4e5f\u80fd\u4f7f\u51fa \u201c\u5927\u4e58\u822c\u82e5\u638c\u201d\uff0c\u800c\u4e14\u795e\u529f\u5965\u5999\u4e4b\u5904\u548c\u4ed6\u5b66\u81ea\u9f99\u53f6\u4e0a\u4eba\u7684\uff0c\u7adf\u662f\u5927\u540c\u5c0f\u5f02\uff0c\u5404\u6709\u5343\u79cb\u3002 \u6bd4\u4ed6\u7684\u5f1f\u5b50\u53f6\u51b2\u9704\u4e0d\u77e5\u8981\u9ad8\u51fa\u51e0\u500d\uff01 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u5927\u529b\u7591\u60d1\uff0c\u7b2c\u4e8c\u638c\u505c\u5728\u534a\u7a7a\uff0c\u4e0d\u5373\u51fb\u4e0b\uff0c\u559d\u9053\uff1a \u3000\u3000\u201c\u4f60\u4ece\u54ea\u91cc\u5b66\u6765\u8fd9\u5927\u4e58\u822c\u82e5\u638c\u7684\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u5927\u4e58\u822c\u82e5\u638c\u53c8\u6709\u4ec0\u4e48\u7a00 \u5947\uff0c\u6211\u5e08\u7236\u6b66\u529f\u65e0\u6240\u4e0d\u5305\uff0c\u4ed6\u8bf4\u6211\u8d44\u8d28\u9c81\u949d\uff0c\u4e0d\u914d\u5b66\u6700\u4e0a\u4e58\u7684\u6b66\u529f\uff0c\u53ea\u80fd\u5b66\u70b9\u5fae\u672a\u7684\u9632 \u8eab\u672c\u9886\uff0c\u56e0\u6b64\u5c31\u628a\u8fd9\u5927\u4e58\u822c\u82e5\u638c\u4f20\u7ed9\u6211\u4e86\u3002\u201d \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u5927\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u8fd9\u4f5b\u95e8\u7edd\u4e16\u795e\u529f\uff0c\u4ed6\u5e08\u7236\u7adf\u8ba4\u4e3a\u662f\u5fae\u672b\u4e4b\u6280\uff01\u82e5\u975e\u4fe1\u53e3 \u80e1\u5938\uff0c\u4ed6\u7684\u5e08\u7236\u5c82\u975e\u5929\u4e0b\u65e0\u654c\uff01\u201d\u95ee\u9053\uff1a \u3000\u3000\u201c\u4f60\u5e08\u7236\u662f\u8c01\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u8bf4\u51fa\u6765\u5413\u574f\u4e86\u4f60\uff0c\u6211\u5e08\u7236\u5c31\u662f\u91d1\u2014\u2014\u201d\u5b9d\u8c61\u6cd5\u5e08 \u5927\u53eb\u9053\uff1a\u201c\u91d1\u4e16\u9057\uff01\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u662f\u4ed6\u8001\u4eba\u5bb6\u4e86\u3002\u54c8\u54c8\uff0c\u53ef\u7b11\u5440\uff0c\u53ef\u7b11\uff01\u201d \u5b9d\u8c61\u6cd5\u5e08\u9053\uff1a\u201c\u53ef\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u8fd9\u5341\u51e0\u5e74\u6765\uff0c\u5ff5\u5ff5\u4e0d\u5fd8\u60f3\u4f1a\u4e00\u4f1a\u6211\u7684\u5e08\u7236\uff0c\u6211\u4ee5\u4e3a\u4f60\u6709\u591a\u5927 \u672c\u9886\uff0c\u5374\u539f\u6765\u4e5f\u4e0d\u8fc7\u5982\u6b64\uff01\u4f60\u8fde\u6211\u4e5f\u672a\u5fc5\u5c31\u80dc\u5f97\u4e86\uff0c\u4fbf\u60f3\u4f1a\u6211\u7684\u5e08\u7236\uff0c\u8fd9\u5c82\u4e0d\u592a\u53ef\u7b11\u4e86 \u5417\uff1f\u201d\u5b9d\u8c61\u6cd5\u5e08\u201c\u54fc\u201d\u4e00\u58f0\uff0c\u4e5f\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8d76\u5feb\u53eb\u4f60\u5e08\u7236\u6765\u5427\uff0c\u4f60\u63a5\u4e0b\u4e86\u6211\u7684\u4e09\u638c\uff0c \u4e0d\u4fe1\u4f60\u5c31\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u8ddf\u91d1\u4e16\u9057\u6240\u5b66\u7684\u5927\u4e58\u822c\u82e5\u638c\uff0c\u6e90\u51fa\u4e8e\u4e54\u5317\u6e9f\u7684\u6b66\u529f\u79d8\u8fd9\uff0c\u4e54\u5317\u6e9f\u4e8e\u6b66\u5b66\u65e0 \u6240\u4e0d\u7aa5\uff0c\u5f53\u5e74\u4ed6\u4e0e\u5929\u7afa\u6b66\u5b66\u540d\u5bb6\u9ed1\u767d\u5958\u6cb3\u4e24\u5144\u5f1f\u4ea4\u624b\uff0c\u9ed1\u767d\u6469\u8bc3\u7528\u201c\u5927\u4e58\u822c\u82e5\u638c\u201d\u5bf9\u4ed8 \u4ed6\u7684\u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\uff0c\u7ed3\u679c\u6253\u6210\u5e73\u624b\u3002 \u3000\u3000\u4e54\u5317\u6e9f\u7ecf\u8fc7\u4e86\u8fd9\u6b21\u4ea4\u624b\uff0c\u7adf\u7136\u65e0\u5e08\u81ea\u901a\uff0c\u609f\u51fa\u4e86\u201c\u5927\u4e58\u822c\u82e5\u638c\u201d\u7684\u79d8\u5965\uff0c\u4f46\u4e5f\u6b63\u7531 \u4e8e\u4ed6\u662f\u65e0\u5e08\u81ea\u901a\uff0c\u4ed6\u672c\u8eab\u6240\u4fee\u4e60\u7684\u5185\u529f\u53c8\u662f\u504f\u91cd\u4e8e\u9738\u9053\u7684\u90aa\u6d3e\u5185\u529f\uff0c\u56e0\u4e4b\u4ed6\u7ec3\u6210\u7684\u5927\u4e58 \u822c\u82e5\u638c\uff0c\u4e5f\u4fbf\u4e0e\u592d\u7afa\u4f5b\u95e8\u7684\u6b63\u5b97\u5927\u4e58\u822c\u82e5\u638c\u6709\u6240\u4e0d\u540c\u3002\u7528\u4ee5\u653b\u654c\uff0c\u4ed6\u7684\u638c\u529b\u4e13\u4f24\u5947\u7ecf\u516b \u8109\uff0c\u8981\u5389\u5bb3\u5f97\u591a\uff0c\u4f46\u8bba\u5230\u529f\u529b\u4e4b\u7eaf\uff0c\u90a3\u5374\u662f\u4e0d\u53ca\u5929\u7afa\u4f5b\u95e8\u7684\u6b63\u5b97\u638c\u6cd5\u4e86\u3002 \u3000\u3000\u8fd9\u79d8\u7c4d\u4f20\u5230\u4e86\u91d1\u4e16\u9057\u624b\u4e0a\uff0c\u91d1\u4e16\u9057\u4ee5\u6b63\u6d3e\u7684\u5185\u529f\u4e3a\u57fa\u7840\uff0c\u7ec3\u5230\u4e86\u6b63\u90aa\u5408\u4e00\u7684\u5883\u754c\uff0c \u65f6\u8fd9\u79d8\u7c4d\u4e0a\u7684\u5404\u79cd\u6b66\u529f\uff0c\u4e5f\u90fd\u6709\u4e86\u6539\u8fdb\u3002 \u3000\u3000\u4f46\u8fd9\u5927\u4e58\u822c\u82e5\u638c\u662f\u6700\u6df1\u5965\u7684\u6b66\u5b66\u4e4b\u4e00\uff0c\u867d\u6709\u6539\u8fdb\uff0c\u5374\u8fd8\u672a\u80fd\u5b8c\u5168\u79bb\u5f00\u4e54\u5317\u6e9f\u7684\u8def\u6570\uff0c \u4e0e\u5929\u7afa\u4f5b\u95e8\u7684\u6b63\u5b97\u5927\u4e58\u822c\u82e5\u638c\uff0c\u4ecd\u662f\u6709\u6240\u4e0d\u540c\u3002\u4ed6\u4e5f\u53ef\u4ee5\u8bf4\u788d\u662f\u5404\u6709\u5343\u79cb\uff0c\u96be\u5206\u8f69\u8f7e\u3002 \u3000\u3000\u5018\u82e5\u662f\u91d1\u4e16\u9057\u4eb2\u81ea\u4e0e\u5b9d\u8c61\u6cd5\u5e08\u5bf9\u638c\uff0c\u5b9d\u8c61\u6cd5\u5e08\u81ea\u975e\u5176\u654c\u3002\u4f46\u5510\u52aa\u73e0\u7a46\u7684\u672c\u8eab\u529f\u529b\u672c \u6765\u5c31\u6bd4\u4e0d\u4e0a\u5b9d\u8c61\u6cd5\u5e08\uff0c\u4ed6\u4f7f\u7684\u5927\u4e58\u822c\u82e5\u638c\u53c8\u4ee5\u9738\u9053\u4e3a\u4e3b\uff0c\u540e\u52b2\u96be\u4ee5\u4e3a\u7ee7\uff0c\u53ea\u5bf9\u4e00\u638c\uff0c\u8fd8 \u4e0d\u600e\u4e48\uff0c\u82e5\u662f\u8fde\u63a5\u4e09\u638c\uff0c\u5f31\u70b9\u4fbf\u96be\u514d\u66b4\u9732\u3002\u5b9d\u8c61\u6cd5\u5e08\u6b63\u662f\u770b\u5230\u4ed6\u8fd9\u4e2a\u5f31\u70b9\uff0c\u56e0\u800c\u624d\u6562\u5938 \u4e0b\u5927\u53e3\uff0c\u8981\u5728\u4e09\u638c\u4e4b\u5185\uff0c\u5c06\u4ed6\u51fb\u8d25\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u5b9d\u8c61\u6cd5\u5e08\u8a00\u51fa\u638c\u5230\uff0c\u638c\u529b\u6709\u5982\u6392\u5c71\u5012\u6d77\u800c\u6765\uff0c\u5510\u52aa\u73e0\u7a46\u4e5f\u628a\u771f\u529b\u51dd\u805a\u638c\u5fc3\uff0c \u53c8\u4e0e\u4ed6\u5bf9\u4e86\u4e00\u638c\u3002\u8fd9\u4e00\u638c\uff0c\u5510\u52aa\u73e0\u7a46\u56fa\u7136\u9707\u5f97\u6447\u6447\u6643\u6643\uff0c\u5b9d\u8c61\u6cd5\u5e08\u4e5f\u6c81\u51fa\u6c57\u73e0\u3002\u5510\u52aa\u73e0 \u7a46\u5fc3\u60f3\uff1a\u201c\u53ea\u6709\u4e00\u638c\uff0c\u6211\u770b\u4f60\u600e\u80fd\u5c06\u6211\u51fb\u8d25\uff1f\u201d\u5fc3\u5ff5\u672a\u5df1\uff0c\u5b9d\u8c61\u6cd5\u5e08\u7b2c\u4e09\u638c\u53c8\u5df2\u65e0\u58f0\u65e0 \u606f\u7684\u5288\u6765\uff0c\u5510\u52aa\u73e0\u7a46\u7ffb\u638c\u4e00\u8fce\uff0c\u53ea\u542c\u5f97\u95f7\u96f7\u4f3c\u7684\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u5510\u52aa\u73e0\u7a46\u7684\u638c\u529b\u7adf\u7ed9\u5bf9 \u65b9\u8feb\u5f97\u5012\u9000\u56de\u6765\uff0c\u767b\u65f6\u6c14\u8840\u7ffb\u6d8c\uff0c\u8fde\u9000\u51fa\u4e86\u4e94\u516d\u6b65\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u56fa\u7136\u5927\u5927\u5403\u60ca\uff0c\u5b9d\u8c61\u6cd5\u5e08\u4e5f\u662f\u8be7\u5f02\u4e0d\u5df2\u3002\u8981\u77e5\u5927\u4e58\u822c\u82e5\u638c\u4e13\u4f24\u5947\u7ecf\u516b\u8109\uff0c \u5b9d\u8c61\u6cd5\u5e08\u5df2\u7136\u628a\u5510\u52aa\u73e0\u7a46\u7684\u638c\u529b\u8feb\u56de\u53bb\uff0c\u8bba\u7406\u5510\u52aa\u73e0\u7a46\u4e0d\u6b7b\u4e5f\u8981\u91cd\u4f24\uff0c\u4f46\u5510\u52aa\u73e0\u7a46\u867d\u7136 \u4e0d\u654c\u3002\u5374\u5e76\u672a\u5012\u4e0b\uff0c\u770b\u6765\u4ecd\u662f\u52c9\u5f3a\u652f\u6301\u5f97\u4f4f\uff0c\u8fd9\u5c31\u4e0d\u80fd\u4e0d\u5927\u51fa\u5b9d\u8c61\u6cd5\u5e08\u610f\u6599\u4e4b\u5916\u4e86\u3002 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u54ea\u91cc\u77e5\u9053\uff0c\u91d1\u4e16\u9057\u5df2\u628a\u8fd9\u5927\u4e58\u822c\u82e5\u638c\u7684\u8fd0\u529f\u79d8\u5965\u52a0\u4ee5\u53d8\u5316\uff0c\u51cf\u5c11\u4e86\u51e0\u5206\u9738 \u9053\uff0c\u6e17\u8fdb\u4e86\u6b63\u5b97\u7684\u5185\u529f\u5fc3\u6cd5\uff0c\u5510\u52aa\u73e0\u7a46\u4e00\u89c9\u4e0d\u5999\uff0c\u7acb\u5373\u4f9d\u6cd5\u6210\u4e3a\uff0c\u5c06\u88ab\u8feb\u56de\u6765\u7684\u771f\u529b\uff0c \u7eb3\u5165\u4e39\u7530\uff0c\u4ed6\u670d\u98df\u4e86\u5929\u5fc3\u77f3\u4e4b\u540e\uff0c\u529f\u529b\u53c8\u5927\u5927\u589e\u8fdb\uff0c\u662f\u4ee5\u53ea\u8017\u635f\u4e86\u4e00\u4e9b\u771f\u6c14\uff0c\u5e76\u672a\u4f24\u53ca \u5947\u7ecf\u516b\u8109\u3002 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u4e00\u60ca\u4e4b\u4e0b\uff0c\u6740\u673a\u9661\u8d77\uff0c\u8d81\u7740\u5510\u52aa\u73e0\u7a46\u7acb\u8db3\u672a\u7a33\uff0c\u7b2c\u56db\u638c\u53c8\u60ca\u96f7\u9a87\u7535\u822c\u7684\u75be \u5288\u4e0b\u53bb\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u53eb\u9053\uff1a\u201c\u8fd9\u5df2\u7ecf\u662f\u7b2c\u56db\u638c\u5566\uff0c\u4f60\u8bf4\u7684\u8bdd\u7b97\u4e0d\u7b97\u6570\uff1f\u201d\u8eab\u5f62\u75be\u63a0\u800c\u51fa\uff0c\u58f0\u5230\u4eba \u5230\uff0c\u66ff\u5510\u52aa\u73e0\u7a46\u63a5\u4e86\u8fd9\u638c\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u672a\u5b66\u8fc7\u5927\u4e58\u822c\u82e5\u638c\uff0c\u4f46\u4ed6\u5374\u7ec3\u6709\u80fd\u5fa1\u5927\u4e58\u822c\u82e5\u638c\u7684\u5c11\u9633\u795e\u529f\uff0c\u53cc\u638c\u4e00\u4ea4\uff0c\u5b9d \u8c61\u6cd5\u5e08\u53ea\u89c9\u5bf9\u65b9\u7684\u529b\u9053\u67d4\u548c\u4e4b\u6781\uff0c\u4f46\u5374\u4f3c\u65e0\u6240\u4e0d\u5305\uff0c\u5c31\u50cf\u4e00\u4e2a\u5e73\u9759\u7684\u6d77\u6d0b\uff0c\u4efb\u4f60\u6254\u4e0b\u591a \u5c11\u77f3\u5934\uff0c\u4e5f\u88ab\u6d77\u6c34\u8986\u6ca1\uff0c\u81f3\u591a\u6fc0\u8d77\u4e00\u70b9\u70b9\u6d6a\u82b1\u3002\u5b9d\u8c61\u6cd5\u5e08\u5ffd\u6709\u4e00\u80a1\u6696\u6d0b\u6d0b\u7684\u611f\u89c9\uff0c\u81ea\u5df1 \u90a3\u4e48\u521a\u731b\u7684\u529b\u9053\uff0c\u7adf\u4f3c\u77f3\u5934\u5728\u6d77\u6c34\u4e4b\u4e2d\u8986\u6ca1\uff0c\u51b0\u96ea\u5728\u6625\u98ce\u4e4b\u4e2d\u6eb6\u89e3\uff01 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u6599\u4e0d\u5230\u6c5f\u6d77\u5929\u5185\u529f\u5982\u6b64\u6df1\u539a\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u5de6\u638c\u8fde\u5fd9\u63a8\u51fa\uff0c\u53cc\u638c\u7528\u4e86 \u76f8\u53cd\u7684\u529b\u9053\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u5377\u8d77\u4e86\u4e00\u80a1\u98ce\u67f1\uff0c\u53cc\u65b9\u5185\u529b\u6fc0\u8361\uff0c\u6c5f\u6d77\u5929\u7a76\u7adf\u56e0\u4e3a\u670d\u98df\u4e86\u5929\u5fc3 \u77f3\u4e4b\u540e\uff0c\u65f6\u65e5\u5c1a\u6d45\uff0c\u5c11\u9633\u7384\u529f\u8fd8\u672a\u80fd\u968f\u5fc3\u8fd0\u7528\u3001\u96be\u6570\u53d1\u6325\uff0c\u88ab\u5bf9\u65b9\u521a\u67d4\u4e92\u6613\u7684\u529b\u9053\u4e00\u7ede\uff0c \u4e00\u65f6\u672a\u80fd\u9002\u5e94\u3002\u8fd9\u624d\u7ed9\u5b9d\u8c61\u6cd5\u5e08\u5c06\u638c\u529b\u64a4\u4e86\u56de\u53bb\u3002 \u3000\u3000\u5ea7\u4e2d\u4e0d\u4e4f\u6b66\u5b66\u540d\u5bb6\uff0c\u770b\u5f97\u51fa\u5b9d\u8c61\u6cd5\u5e08\u867d\u7136\u5316\u89e3\u4e86\u8fd9\u4e00\u62db\uff0c\u4f46\u4ea6\u5df2\u662f\u5403\u4e86\u70b9\u4e8f\uff0c\u4eba\u4eba \u5fc3\u4e2d\u9a87\u7136\uff01\u5510\u52aa\u73e0\u7a46\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f55\u987b\u8981\u8bf7\u6211\u7684\u5e08\u7236\uff0c\u4f60\u8d62\u4e86\u6211\u7684\u5e08\u5144\u518d\u5938\u5927\u53e3\uff0c\u4e5f\u8fd8 \u4e0d\u8fdf\u3002\u201d \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u8001\u7f9e\u6210\u6012\uff0c\u5fc3\u60f3\uff1a\u201c\u4e8b\u5230\u5982\u4eca\uff0c\u4e5f\u53ea\u597d\u6765\u4e00\u573a\u6df7\u6218\u4e86\u3002\u201d\u5f53\u4e0b\u5927\u559d\u9053\uff1a \u201c\u8fd9\u5c0f\u5b50\u7387\u4f17\u53db\u7f6a\uff0c\u4e0d\u5fc5\u548c\u4ed6\u8bb2\u4ec0\u4e48\u6bd4\u6b66\u7684\u89c4\u77e9\uff0c\u628a\u4ed6\u62ff\u4e0b\u4e86\uff01\u201d\u4ed6\u7684\u56db\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u4e00 \u62e5\u800c\u4e0a\uff0c\u5c06\u5510\u52aa\u73e0\u7a46\u56f4\u5728\u6838\u5fc3\uff0c\u4fe1\u52aa\u73e0\u7a46\u56e0\u4e3a\u8fde\u63a5\u4e86\u5b9d\u8c61\u6cd5\u5e08\u7684\u4e09\u638c\uff0c\u529f\u529b\u8017\u635f\u4e86\u51e1\u5206\uff0c \u90a3\u56db\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u8981\u64d2\u4ed6\u56fa\u7136\u4e0d\u6613\uff0c\u4ed6\u8981\u5c06\u90a3\u56db\u4eba\u51fb\u8d25\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u4e5f\u4e0d\u80fd\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u559d\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u4f60\u4e5f\u63a5\u6211\u4e00\u638c\uff01\u201d\u5b9d\u8c61\u6cd5\u5e08\u84e6\u5730\u4e00\u58f0\u5927\u543c\uff0c\u53cc\u638c \u9f50\u51fa\uff0c\u6c5f\u6d77\u5929\u62db\u67b6\u4e0d\u4f4f\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u600e\u7684\u5bf9\u65b9\u7684\u529f\u529b\u7adf\u4f3c\u7a81\u7136\u95f4\u589e\u5f3a\u4e86\u4e00\u500d\uff01\u201d \u3000\u3000\u539f\u6765\u5b9d\u8c61\u6cd5\u5e08\u8fd9\u6b21\u7528\u7684\u4e43\u662f\u5929\u7afa\u4f5b\u95e8\u6700\u5389\u5bb3\u7684\u201c\u9f99\u8c61\u529f\u201d\uff0c\u8fd9\u53cc\u638c\u4e00\u53d1\uff0c\u5177\u6709\u65e0\u575a \u4e0d\u6467\u7684\u9f99\u8c61\u4e4b\u529b\uff0c\u914d\u4e0a\u4e86\u4ed6\u7684\u201c\u72ee\u5b50\u543c\u201d\u66f4\u663e\u5f97\u5a01\u529b\u65e0\u4f26\uff01\u4f46\u8fd9\u201c\u9f99\u8c61\u529f\u201d\u6781\u4e3a\u8017\u635f\u771f \u6c14\uff0c\u6240\u4ee5\u975e\u5230\u6700\u540e\u5173\u5934\u51b3\u4e0d\u8f7b\u7528\u3002 \u3000\u3000\u53ea\u542c\u201c\u535c\u901a\u201d\u201c\u535c\u901a\u201d\u4e00\u7247\u58f0\u54cd\uff0c\u5ea7\u4e0a\u529f\u529b\u8f83\u5f31\u7684\u6570\u5341\u4e2a\u4eba\u88ab\u5b9d\u8c61\u6cd5\u5e08\u90a3\u4e00\u58f0\u5927\u543c\uff0c \u9707\u5f97\u629b\u79bb\u5ea7\u4f4d\uff0c\u8dcc\u5012\u5730\u4e0a\uff01\u5176\u4ed6\u4eba\u4f17\uff0c\u81ea\u5fd6\u7981\u53d7\u4e0d\u8d77\u7684\uff0c\u7eb7\u7eb7\u6495\u4e0b\u8863\u88f3\uff0c\u585e\u7740\u8033\u6735\uff0c\u4f1a \u573a\u66f4\u52a0\u6df7\u4e71\uff01 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u4f7f\u51fa\u4e86\u201c\u9f99\u8c61\u529f\u201d\u4ecd\u672a\u80fd\u5c06\u6c5f\u6d77\u5929\u9707\u5012\uff0c\u53ea\u5f97\u62fc\u7740\u8017\u635f\u771f\u6c14\uff0c\u518d\u53d1\u4e00\u638c\uff0c \u6c5f\u6d77\u5929\u4f7f\u51fa\u201c\u5929\u7f57\u6b65\u6cd5\u201d\uff0c\u907f\u5f00\u6b63\u9762\uff0c\u500f\u5730\u7ed5\u5230\u4ed6\u7684\u80cc\u540e\uff0c\u4e00\u6307\u70b9\u4e2d\u4ed6\u7684\u80cc\u5fc3\u201c\u5927\u85cf\u7a74\u201d\uff0c \u54ea\u77e5\u4ed6\u8fd9\u9f99\u8c61\u529f\u4e00\u7ecf\u8fd0\u7528\uff0c\u5468\u56f4\u6570\u4e08\u4e4b\u5185\uff0c\u90fd\u662f\u4ed6\u638c\u529b\u7b3c\u7f69\u7684\u8303\u56f4\uff0c\u800c\u4e14\u53cd\u5e94\u6781\u901f\uff0c\u6c5f \u6d77\u5929\u4e00\u6307\u70b9\u4e2d\u4ed6\u7684\u80cc\u5fc3\uff0c\u4ed6\u7684\u638c\u529b\u4e5f\u7acb\u5373\u4ece\u56db\u65b9\u516b\u9762\u5411\u6c5f\u6d77\u5929\u7ad9\u7acb\u7684\u65b9\u5411\u201c\u6324\u201d\u6765\uff01 \u3000\u3000\u8fd9\u4e00\u77ac\u95f4\uff0c\u6c5f\u6d77\u5929\u5c31\u4f3c\u5904\u5728\u6fc0\u6d41\u6025\u6e4d\u7684\u4e2d\u5fc3\uff0c\u53c8\u4f3c\u9047\u5230\u4e86\u4e00\u80a1\u65e0\u5f62\u65e0\u58f0\u7684\u201c\u9f99\u5377\u98ce\u201d \u4f3c\u7684\uff0c\u9976\u662f\u4ed6\u529f\u529b\u6df1\u539a\uff0c\u4e5f\u81ea\u7acb\u8db3\u4e0d\u7a33\uff0c\u53ea\u542c\u5230\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u6574\u4e2a\u8eab\u5b50\u5c31\u4f3c\u76ae\u7403\u822c\u629b \u4e86\u8d77\u6765\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u4e0e\u534e\u4e91\u78a7\u5927\u60ca\uff0c\u4e0d\u7ea6\u800c\u4f3a\u7684\u90fd\u5411\u4ed6\u5954\u53bb\u3002\u8fd9\u65f6\uff0c\u5b9d\u8c61\u6cd5\u5e08\u867d\u7136\u6ca1\u6709\u7ee7\u7eed\u53d1\u638c\uff0c \u4f46\u4ed6\u7684\u201c\u9f99\u8c61\u529f\u201d\u4f59\u5a01\u672a\u5c3d\uff0c\u5185\u529b\u5377\u8d77\u7684\u98ce\u67f1\u5728\u6570\u4e08\u5468\u56f4\u4e4b\u5185\uff0c\u4ecd\u662f\u5f3a\u52b2\u975e\u5e38\u3002\u8c37\u4e2d\u83b2 \u4e5f\u8fd8\u7f62\u4e86\uff0c\u534e\u4e91\u78a7\u4e00\u8e0f\u5230\u8fd9\u8303\u56f4\u7684\u8fb9\u7f18\uff0c\u5374\u88ab\u8fd9\u80a1\u6697\u529b\u4e00\u9707\uff0c\u7adf\u662f\u8eab\u4e0d\u7531\u5df2\u7684\u5f80\u540e\u76f4\u9000\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u8e0f\u5230\u8fd9\u8303\u56f4\u7684\u4e2d\u5fc3\uff0c\u4fe8\u5982\u5982\u98ce\u4e2d\u4e4b\u70db\uff0c\u7981\u4e0d\u4f4f\u6447\u6447\u6643\u6643\u3002\u6c5f\u6d77\u5929\u5728\u7a7a\u4e2d\u8f6c\u4e86 \u4e24\u5708\u3001\u4e00\u4e2a\u7b4b\u6597\u5012\u7ffb\u4e0b\u6765\uff0c\u6070\u597d\u843d\u5728\u8c37\u4e2d\u83b2\u8eab\u8fb9\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u83b2\u59b9\uff0c\u4f60\u4e0d\u7528\u62c5\u5fc3\uff0c\u6211 \u867d\u7136\u4e0d\u6562\u8a00\u80dc\uff0c\u4f46\u4e5f\u4e0d\u81f3\u4e8e\u5c31\u8f93\u7ed9\u4ed6\u3002\u4f60\u53bb\u52a9\u4f60\u54e5\u54e5\u4e00\u81c2\u4e4b\u529b\u5427\u3002\u201d \u3000\u3000\u539f\u6765\u4ee5\u6c5f\u6d77\u5929\u73b0\u5728\u7684\u52a8\u529b\u800c\u8bba\uff0c\u548c\u5b9d\u8c61\u6cd5\u5e08\u5b9e\u9645\u4e43\u662f\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u53ea\u56e0\u5b9d\u8c61\u6cd5\u5e08\u4e0d \u60dc\u81ea\u8017\u771f\u6c14\uff0c\u4f7f\u51fa\u5929\u7afa\u4f5b\u95e8\u6700\u5389\u5bb3\u7684\u201c\u9f99\u8c61\u529f\u201d\uff0c\u8fd9\u624d\u80dc\u8fc7\u6c5f\u6d77\u5929\u4e00\u7b79\u3002\u800c\u4e14\uff0c\u867d\u7136\u5982 \u6b64\uff0c\u4e5f\u8fd8\u4f24\u4e0d\u4e86\u6c5f\u6d77\u5929\u3002 \u3000\u3000\u6c5f\u6d77\u592d\u521a\u624d\u4e4b\u6240\u4ee5\u88ab\u629b\u8d77\u6765\uff0c\u4e00\u534a\u7684\u539f\u56e0\u56fa\u7136\u662f\u7531\u4e8e\u201c\u9f99\u8c61\u529f\u201d\u7684\u5a01\u529b\u786e\u5b9e\u5f3a\u5927\uff0c \u53e6\u4e00\u534a\u539f\u56e0\u5219\u662f\u56e0\u4e3a\u6c5f\u6d77\u5929\u6b3a\u5230\u4ed6\u7684\u8eab\u524d\uff0c\u53ea\u7528\u4e00\u6307\u53bb\u70b9\u4ed6\u7684\u7a74\u9053\uff0c\u867d\u7136\u6c5f\u6d77\u5929\u4e5f\u7528\u4e0a \u4e86\u5185\u5bb6\u771f\u529b\uff0c\u4f46\u4e00\u6307\u4e4b\u529b\uff0c\u5374\u600e\u80fd\u4e0e\u5b9d\u8c61\u6cd5\u5e08\u4ee5\u638c\u529b\u53d1\u51fa\u7684\u201c\u9f99\u8c61\u529f\u201d\u6297\u8861\uff1f\u6c5f\u6d77\u5929\u662f \u907f\u514d\u7ed9\u4ed6\u7684\u638c\u529b\u6240\u6324\uff0c\u4f24\u4e86\u5143\u6c14\uff0c\u8fd9\u624d\u8df3\u8d77\u6765\u7684\uff0c\u5012\u5e76\u4e0d\u662f\u5b8c\u5168\u4e3a\u4e86\u654c\u4e0d\u4f4f\u201c\u9f99\u8c61\u529f\u201d \u7684\u7f18\u6545\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u6b64\u9645\u4e5f\u770b\u51fa\u4e86\u6c5f\u6d77\u5929\u6ca1\u6709\u53d7\u4f24\uff0c\u5fc3\u4e0a\u7684\u4e00\u5757\u5927\u77f3\u5934\u653e\u4e86\u4e0b\u6765\uff0c\u4f46\u5374\u6ca1\u6709\u9a6c\u4e0a \u5c31\u8d70\uff0c\u5374\u628a\u88c1\u4e91\u5b9d\u5251\u9012\u7ed9\u6c5f\u6d77\u5929\u9053\uff1a \u3000\u3000\u201c\u8fd9\u628a\u5b9d\u5251\u8fd8\u7ed9\u4f60\uff0c\u8fd9\u91cc\u5c31\u53ea\u8fd9\u79c3\u9a74\u6700\u5389\u5bb3\uff0c\u6211\u6709\u767d\u7389\u7532\u9632\u8eab\uff0c\u5176\u4ed6\u4eba\u8c05\u4ed6\u4e0d\u80fd\u4f24 \u6211\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u521a\u624d\u5728\u7a7a\u4e2d\u8f6c\u4e86\u4e24\u8f6c\uff0c\u5b9d\u8c61\u6cd5\u5e08\u5374\u5728\u5730\u4e0a\u8f6c\u4e86\u4e03\u516b\u5708\u3002\u539f\u6765\u6c5f\u6d77\u5929\u4ee5\u5e08\u95e8\u79d8 \u6388\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u6b63\u70b9\u4e2d\u4e86\u5b9d\u8c61\u6cd5\u5e08\u80cc\u5fc3\u7684\u5927\u7a74\uff0c\u91d1\u4e16\u9057\u7ee7\u627f\u6bd2\u9f99\u5c0a\u8005\u7684\u70b9\u7a74\u6cd5\uff0c\u53ef\u8bf4\u5f97 \u662f\u5929\u4e0b\u65e0\u53cc\uff0c\u52a0\u4e0a\u6c5f\u6bcf\u5929\u672c\u8eab\u6240\u5177\u7684\u7edd\u4e16\u795e\u529f\uff0c\u8fd9\u4e00\u6307\u4e5f\u5f53\u771f\u662f\u975e\u540c\u5c0f\u53ef\u3002\u5b9d\u8c61\u6cd5\u5e08\u6709 \u9f99\u8c61\u529f\u62a4\u8eab\uff0c\u867d\u7136\u672a\u6709\u53d7\u4f24\uff0c\u4f46\u4e5f\u8017\u635f\u4e86\u4e00\u4e24\u5206\u771f\u6c14\uff0c\u4ed6\u5728\u5730\u4e0a\u63a5\u8fde\u8f6c\u4e86\u4e03\u516b\u5708\uff0c\u529b\u7684 \u5c31\u662f\u6d88\u9664\u6c5f\u6d77\u5929\u8fd9\u4e00\u6307\u7684\u540e\u52b2\u3002 \u3000\u3000\u5c31\u5728\u8c37\u4e2d\u83b2\u5c06\u5b9d\u5251\u4ea4\u7ed9\u6c5f\u6d77\u5929\u7684\u65f6\u5019\uff0c\u5b9d\u8c61\u6cd5\u5e08\u4ea6\u6062\u590d\u4e86\u7cbe\u795e\uff0c\u7a33\u4f4f\u4e86\u8eab\u5f62\uff0c\u5f53\u4e0b \u5927\u543c\u4e00\u58f0\uff0c\u98de\u6b65\u4e0a\u524d\uff0c\u53c8\u5411\u6c5f\u6d77\u5929\u53d1\u638c\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u65e0\u6687\u591a\u8bf4\uff0c\u53ea\u597d\u63a5\u8fc7\u5b9d\u5251\uff0c\u8fc5\u5373\u5c06\u8c37\u4e2d\u83b2\u4e00\u63a8\u3001\u4ed6\u7528\u7684\u4e43\u662f\u5de7\u52b2\uff01\u8c37\u4e2d\u83b2\u987a \u7740\u8fd9\u4e00\u63a8\u4e4b\u52bf\uff0c\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\u5df2\u201c\u98de\u201d\u51fa\u4e86\u5b9d\u8c61\u6cd5\u5e08\u7684\u638c\u529b\u8303\u56f4\u4e4b\u5916\u3002\u4f46\u5728\u90a3\u4e00\u77ac \u95f4\uff0c\u6c5f\u6d77\u5929\u8fd8\u662f\u5fe7\u8651\u5979\u4f1a\u88ab\u6ce2\u53ca\uff0c\u767e\u5fd9\u4e2d\u8fd8\u4e0d\u7531\u81ea\u5df1\u7684\u77a5\u4e86\u5979\u4e00\u773c\uff0c\u5f85\u89c1\u5230\u5979\u5df2\u201c\u98de\u201d \u79bb\u4e86\u638c\u529b\u8303\u56f4\uff0c\u8fd9\u624d\u653e\u5fc3\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5bf9\u8c37\u4e2d\u83b2\u7684\u6df1\u5207\u5173\u6000\uff0c\u822c\u822c\u60c5\u610f\u3002\u5728\u8fd9\u773c\u5149\u4e00\u77a5\u4e4b\u4e2d\uff0c\u90fd\u5df2\u8868\u9732\u65e0\u9057\u3002 \u3000\u3000\u534e\u4e91\u78a7\u8e0f\u4e0d\u8fdb\u5b9d\u8c61\u6cd5\u5e08\u7684\u638c\u529b\u8303\u56f4\uff0c\u4f46\u4ecd\u662f\u7ad9\u5728\u65c1\u8fb9\uff0c\u6c5f\u6d77\u5929\u548c\u8c37\u4e2d\u83b2\u4e4b\u95f4\u6bcf\u4e00\u4e2a \u52a8\u4f5c\uff0c\u751a\u81f3\u6bcf\u4e00\u4e2a\u7ec6\u5fae\u7684\u8868\u60c5\uff0c\u5979\u90fd\u770b\u5728\u773c\u5185\uff0c\u7a81\u7136\u95f4\u4e0d\u7531\u81ea\u5df1\u7684\u611f\u5230\u5fc3\u5934\u9707\u6817\uff0c\u4e00\u7247 \u832b\u7136\uff1a\u201c\u6d77\u54e5\uff0c\u4ed6\uff0c\u4ed6\u53ef\u4ece\u6765\u6ca1\u6709\u7528\u8fc7\u8fd9\u6837\u7684\u773c\u6ce2\u770b\u6211\uff01\u201d \u3000\u3000\u4e00\u5239\u90a3\u95f4\uff0c\u5979\u4ec0\u4e48\u90fd\u660e\u767d\u4e86\u3002\u6c5f\u6d77\u5929\u4e3a\u4ec0\u4e48\u5ffd\u7565\u4e86\u4e91\u743c\u7684\u5631\u6258\uff0c\u672a\u66fe\u5c06\u4e91\u743c\u5bf9\u8c37\u4e2d \u83b2\u7684\u5fc3\u610f\u4ee3\u4e3a\u8868\u8fbe\uff1b\u521a\u624d\u5f53\u5979\u4e0e\u6c5f\u6d77\u5929\u610f\u5916\u91cd\u9022\uff0c\u6291\u4e0d\u4f4f\u5fc3\u5934\u7684\u559c\u60a6\uff0c\u5bf9\u4ed6\u67d4\u60c5\u4f3c\u6c34\u4e4b \u65f6\uff0c\u4e3a\u4ec0\u4e48\u4ed6\u5374\u56de\u907f\u4e86\u5979\u6df1\u60c5\u7684\u76ee\u5149\u3002\u8fd9\u4e9b\u7591\u56e2\u73b0\u5728\u90fd\u5f97\u5230\u7b54\u6848\u4e86\uff0c\u8fd9\u7b54\u6848\u5c31\u662f\uff1a\u6c5f\u6d77 \u5929\u5fc3\u4e0a\u6b22\u559c\u7684\u4eba\u513f\u4e0d\u662f\u5979\u3001\u662f\u8c37\u4e2d\u83b2\uff01 \u3000\u3000\u534e\u4e91\u78a7\u66fe\u6df1\u6df1\u5992\u5fcc\u8fc7\u6b27\u9633\u5a49\uff0c\u9632\u8303\u8fc7\u6b27\u9633\u5a49\uff0c\u73b0\u5728\u5979\u624d\u77e5\u9053\uff0c\u539f\u6765\u5979\u771f\u6b63\u7684\u201c\u60c5\u654c\u201d \u8fd8\u4e0d\u662f\u6b27\u9633\u5a49\uff0c\u800c\u662f\u8c37\u4e2d\u83b2\uff01\u6b27\u9633\u5a49\u662f\u201c\u90aa\u6d3e\u5996\u5973\u201d\uff0c\u5979\u53ef\u4ee5\u6068\u6b27\u9633\u5a49\uff0c\u53ef\u4ee5\u5c06\u6b27\u9633\u5a49 \u5f53\u4f5c\u654c\u4eba\uff0c\u4f46\u8c37\u4e2d\u83b2\u7684\u60c5\u5f62\u5374\u5b8c\u5168\u4e24\u6837\uff0c\u5979\u662f\u6c5f\u6d77\u5929\u7684\u9752\u6885\u7af9\u9a6c\u4e4b\u4ea4\uff0c\u4ed6\u4eec\u7684\u5e08\u95e8\u6709\u7740 \u6df1\u539a\u7684\u6e0a\u6e90\uff0c\u5979\u662f\u9099\u5c71\u6d3e\u638c\u95e8\u7684\u8863\u94b5\u4f20\u4eba\uff0c\u5979\u53c8\u662f\u9a6c\u8428\u513f\u56fd\u7684\u516c\u4e3b\u2026\u2026\u5979\u51b3\u4e0d\u80fd\u5c06\u8c37\u4e2d \u83b2\u5f53\u4f5c\u654c\u4eba\uff0c\u4e5f\u6ca1\u6709\u6743\u5229\u5992\u6068\u5979\u548c\u6c5f\u6d77\u5929\u76f8\u7231\uff01\u6b63\u662f\u56e0\u6b64\uff0c\u8c37\u4e2d\u83b2\u5bf9\u4e8e\u5979\u7684\u201c\u5a01\u80c1\u201d\uff0c \u90a3\u662f\u6bd4\u6b27\u9633\u5a49\u5927\u5f97\u591a\u4e86\uff01 \u3000\u3000\u573a\u4e2d\u6fc0\u6218\u65b9\u9163\uff0c\u53ae\u6740\u58f0\u5982\u96f7\u6668\u8033\uff0c\u4f46\u534e\u4e91\u78a7\u7684\u773c\u4e2d\u5374\u53ea\u6709\u6c5f\u6d77\u5929\u4e0e\u8c37\u4e2d\u83b2\uff0c\u6e10\u6e10\uff0c \u751a\u81f3\u8fde\u6c5f\u6d77\u5929\u4e0e\u8c37\u4e2d\u83b2\u5979\u4e5f\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4f46\u89c9\u8111\u4e2d\u7a7a\u7a7a\u8361\u8361\uff0c\u773c\u524d\u4e00\u7247\u6a21\u7cca\uff0c\u7adf\u662f\u5446\u4e86\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u8c37\u4e2d\u83b2\u5c16\u53eb\u9053\uff1a\u201c\u534e\u59d0\u59d0\uff0c\u5c0f\u5fc3\uff01\u201d\u539f\u6765\u6709\u51e0\u4e2a\u4e03\u9634\u6559\u7684\u5f1f\u5b50\u5411\u5979\u88ad\u51fb\uff0c\u5200 \u5251\u5df2\u513f\u5979\u80cc\u540e\u6240\u6765\uff0c\u5979\u8fd8\u662f\u832b\u7136\u4e0d\u77e5\uff01\u8c37\u4e2d\u83b2\u8fd9\u4e00\u58f0\u5c16\u53eb\u624d\u628a\u5979\u60ca\u9192\u8fc7\u6765\u3002 \u3000\u3000\u60ca\u6115\u4e2d\u534e\u4e91\u78a7\u5411\u524d\u8e0f\u51fa\u4e00\u6b65\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u540e\u5fc3\u5df2\u611f\u5230\u51b0\u51b7\u7684\u5200\u950b\uff0c\u5e78\u800c\u5979\u8e0f \u5f00\u4e86\u4e00\u6b65\uff0c\u5c31\u5dee\u8fd9\u4e00\u6b65\u8ddd\u79bb\uff0c\u5426\u5219\u5200\u950b\u4e0d\u6b62\u662f\u5212\u7834\u5979\u7684\u8863\u88f3\uff0c\u800c\u662f\u7a7f\u5fc3\u800c\u8fc7\u4e86\uff01 \u3000\u3000\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u8c37\u4e2d\u83b2\u62d4\u4e0b\u5934\u4e0a\u7684\u7389\u9497\uff0c\u5c06\u90a3\u67c4\u5c16\u5200\u6253\u843d\uff0c\u8eab\u5f62\u75be\u63a0\u800c\u6765\uff0c\u8fde\u73af\u53cc \u638c\uff0c\u628a\u4e24\u4e2a\u4e03\u9634\u6559\u7684\u5f1f\u5b50\u6253\u7ffb\uff0c\u534e\u4e91\u78a7\u5413\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u8fd9\u624d\u5b8c\u5168\u6e05\u9192\u8fc7\u6765\uff0c\u6da9\u58f0\u8bf4\u9053\uff1a \u201c\u8c37\u59d0\u59d0\uff0c\u591a\u8c22\u4f60\u5566\uff01\u201d\u62d4\u51fa\u4f69\u5251\uff0c\u4e0e\u8c37\u4e2d\u83b2\u5e76\u80a9\u5fa1\u654c\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5374\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u534e\u4e91\u78a7\u6b63\u5728\u4e3a\u4ed6\u70e6\u607c\u60b2\u4f24\uff0c\u751a\u81f3\u8fde\u8c37\u4e2d\u83b2\u4ed6\u4e5f\u65e0\u6687\u987e\u53ca\u4e86\uff0c \u8fd9\u65f6\uff0c\u4ed6\u6b63\u4e0e\u5b9d\u8c61\u6cd5\u5e08\u5c55\u5f00\u4e86\u7a7a\u524d\u6fc0\u70c8\u3001\u820d\u751f\u5fd8\u6b7b\u7684\u6076\u6218\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6709\u5b9d\u5251\u5728\u624b\uff0c\u5a01\u529b\u5927\u589e\uff0c\u4f46\u5b9d\u8c61\u6cd5\u5e08\u7684\u201c\u9f99\u8c61\u529f\u201d\u4e5f\u4f3c\u60ca\u6d9b\u9a87\u6d6a\uff0c\u4e00\u6ce2\u672a\u5e73\uff0c \u4e00\u6ce2\u53c8\u8d77\uff0c\u4e00\u4e2a\u6d6a\u5934\u8d85\u8fc7\u4e00\u4e2a\u6d6a\u5934\uff01 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u9636\u201c\u9f99\u8c61\u529f\u201d\uff0c\u6bcf\u53d1\u4e00\u638c\uff0c\u5185\u529b\u5c31\u52a0\u4e0a\u4e00\u91cd\uff0c\u6c14\u6d41\u6fc0\u8361\uff0c\u5f53\u771f\u4f3c\u201c\u9f99\u5377\u98ce\u201d \u4e00\u822c\uff0c\u5377\u8d77\u4e86\u4e00\u6761\u98ce\u67f1\uff0c\u5e78\u4e8f\u662f\u6c5f\u6d77\u5929\uff0c\u82e5\u662f\u6362\u4e86\u4ed6\u4eba\uff0c\u522b\u8bf4\u786c\u63a5\u4ed6\u7684\u638c\u529b\uff0c\u53ea\u8981\u5904\u5728 \u8fd9\u98ce\u529b\u7684\u4e2d\u5fc3\uff0c\u53ea\u6015\u5fc3\u810f\u4e5f\u8981\u7834\u88c2\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u624b\u6301\u88c1\u4e91\u5b9d\u5251\uff0c\u4fa6\u51fa\u4e86\u201c\u8ffd\u98ce\u5251\u5f0f\u201d\uff0c\u4ee5\u4ed6\u96c4\u6d51\u4e4b\u6781\u7684\u5185\u529b\uff0c\u4f7f\u7684\u53c8\u662f\u5929\u4e0b \u65e0\u53cc\u7684\u5b9d\u5251\uff0c\u5251\u5c16\u9636\u6307\uff0c\u55e4\u55e4\u6709\u58f0\uff0c\u5b9d\u8c61\u6cd5\u5e08\u7684\u201c\u9f99\u8c61\u529f\u201d\u53ea\u80fd\u9707\u6b6a\u4ed6\u7684\u5251\u70b9\uff0c\u8fd8\u672a\u80fd \u5b8c\u5168\u5c06\u4ed6\u5c01\u4f4f\uff0c\u5b9d\u8c61\u6cd5\u5e08\u4e5f\u4e0d\u80fd\u4e0d\u591a\u4e86\u51e0\u5206\u987e\u5fcc\u3002\u5982\u6b64\u4e00\u6765\uff0c\u4e00\u4e2a\u662f\u5728\u529f\u529b\u4e0a\u54af\u80dc\u4e00\u7b79\uff0c \u4e00\u4e2a\u662f\u5175\u5668\u4e0a\u5360\u4e86\u4fbf\u5b9c\uff0c\u6070\u6070\u6253\u6210\u5e73\u624b\u3002 \u3000\u3000\u90a3\u51e0\u4e2a\u4e03\u9634\u6559\u7684\u5f1f\u5b50\u5374\u4e0d\u662f\u8c37\u3001\u534e\u4e8c\u4eba\u7684\u5bf9\u624b\uff0c\u6709\u7684\u53d7\u4e86\u534e\u4e91\u78a7\u7684\u5251\u4f24\uff0c\u6709\u7684\u7ed9\u8c37 \u4e2d\u83b2\u4ee5\u5288\u7a7a\u638c\u6253\u7ffb\uff0c\u4fa5\u5e78\u672a\u53d7\u4f24\u7684\uff0c\u4e5f\u8fde\u5fd9\u9003\u4e86\u3002\u8c37\u4e2d\u83b2\u6b63\u8981\u8f6c\u79fb\u9635\u5730\uff0c\u76f8\u52a9\u5979\u7684\u54e5\u54e5\uff0c \u5ffd\u542c\u5f97\u4e00\u4e2a\u523a\u8033\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5973\u5a03\u5b50\u6b66\u529f\u53ef\u4e0d\u9519\u5440\uff0c\u96be\u5f97\u53c8\u90fd\u662f\u957f\u5f97\u8fd9\u4e48\u6807\u81f4\uff0c \u54c8\u54c8\uff0c\u4f60\u4eec\u90fd\u8ddf\u4e86\u6211\u5427\uff01\u201d \u3000\u3000\u53ea\u89c1\u6765\u7684\u662f\u4e2a\u76f8\u8c8c\u53e4\u602a\u7684\u866c\u9aef\u6c49\u5b50\uff0c\u8eab\u6750\u53ea\u6709\u4e94\u5c3a\u6765\u9ad8\uff0c\u624b\u81c2\u5374\u6bd4\u5e38\u4eba\u957f\u51fa\u8bb8\u591a\uff0c \u58f0\u5230\u4eba\u5230\uff0c\u4e00\u6293\u5c31\u5411\u8c37\u4e2d\u83b2\u6293\u4e0b\u3002\u8c37\u4e2d\u83b2\u5927\u6012\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u7528\u4e86\u4e5d\u6210\u529f\u529b\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d \u7684\u4e00\u58f0\uff0c\u90a3\u602a\u4eba\u7adf\u7136\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u53cd\u800c\u662f\u8c37\u4e2d\u83b2\u6643\u4e86\u4e00\u6643\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u602a\u4eba\u4e43\u662f\u4e1c\u6d77\u5c60\u9f99\u5c9b\u7684\u5c9b\u4e3b\u7b26\u79bb\u6e10\uff0c\u4ed6\u662f\u5b5f\u795e\u901a\u7684\u597d\u53cb\uff0c\u5bf9\u5b5f\u795e\u901a\u4e14\u66fe\u6709 \u8fc7\u4e00\u70b9\u6069\u60e0\uff0c\u5f53\u5e74\u5b5f\u795e\u901a\u9080\u4ed6\u5230\u4e2d\u539f\u52a9\u9635\uff0c\u76c2\u795e\u901a\u6b7b\u540e\uff0c\u4ed6\u8d2a\u6155\u4e2d\u539f\u7684\u7e41\u534e\uff0c\u5c31\u4e0d\u518d\u56de \u5c60\u9f99\u5c9b\u4e86\u3002\uff08\u4e8b\u89c1\u300a\u4e91\u6d77\u7389\u5f13\u7f18\u300b\uff09\u6587\u5ef7\u58c1\u548c\u4ed6\u76f8\u719f\uff0c\u8fd9\u6b21\u662f\u6587\u5ef7\u58c1\u4ee3\u5b9d\u8c61\u6cd5\u5e08\u9080\u4ed6\u6765 \u53c2\u52a0\u91d1\u9e70\u5bab\u4e4b\u4f1a\u7684\u3002 \u3000\u3000\u7b26\u79bb\u6e10\u6700\u4e3a\u597d\u8272\uff0c\u8c37\u4e2d\u83b2\u6253\u8d25\u4e86\u4e03\u9634\u6559\u7684\u5f1f\u5b50\uff0c\u7b26\u79bb\u6e10\u867d\u770b\u51fa\u5979\u6b66\u529f\u4e0d\u9519\uff0c\u5374\u8fd8\u672a \u653e\u5728\u5fc3\u4e0a\uff0c\u89c1\u5979\u957f\u5f97\u6bd4\u534e\u4e91\u78a7\u4f3c\u4e4e\u8fd8\u6f02\u4eae\u4e00\u4e9b\uff0c\u5c31\u5148\u6765\u6293\u5979\uff0c\u54ea\u77e5\u53cc\u638c\u4e00\u4ea4\uff0c\u7adf\u7981\u4e0d\u4f4f \u5fc3\u5934\u4e00\u9707\uff0c\u867d\u7136\u5b9a\u4f4d\u4e86\u8eab\u5f62\uff0c\u4ecd\u662f\u611f\u5230\u6c14\u8840\u7ffb\u6d8c\uff0c\u8fd9\u624d\u77e5\u9053\u5389\u5bb3\u3002 \u3000\u3000\u4f46\u7b26\u79bb\u6e10\u5374\u54ea\u80af\u7f62\u624b\uff0c\u4e00\u58f0\u5927\u543c\uff0c\u53c8\u5411\u534e\u4e91\u78a7\u6293\u6765\uff0c\u4ed6\u53ea\u9053\u534e\u4e91\u78a7\u4e5f\u4e00\u822c\u5389\u5bb3\uff0c\u8fd9 \u4e00\u6293\u7adf\u7136\u7528\u4e86\u5168\u529b\uff0c\u534e\u4e91\u78a7\u600e\u62b5\u6321\u5f97\u4f4f\uff0c\u500f\u5730\u5c31\u7ed9\u4ed6\u6293\u4e86\u8d77\u6765\uff01 \u3000\u3000\u8c37\u4e2d\u83b2\u5927\u60ca\uff0c\u638c\u6307\u517c\u65bd\uff0c\u5979\u672c\u6765\u957f\u4e8e\u8f7b\u529f\uff0c\u65b0\u8fd1\u53c8\u8ddf\u6c5f\u6d77\u5929\u5b66\u4f1a\u4e86\u5929\u7f57\u6b65\u6cd5\uff0c\u8eab\u6cd5 \u5feb\u5982\u95ea\u7535\uff0c\u7b26\u79bb\u6e10\u6293\u7740\u4e00\u4e2a\u4eba\uff0c\u96be\u53ca\u5979\u7684\u8fc5\u6377\uff0c\u53c8\u6599\u4e0d\u5230\u5979\u6765\u788d\u5982\u6b64\u4e4b\u5feb\uff0c\u7adf\u7ed9\u5979\u4e00\u6307 \u70b9\u4e2d\u4e86\u201c\u66f2\u6c60\u7a74\u201d\uff0c\u624b\u81c2\u4e00\u9ebb\uff0c\u534e\u4e91\u78a7\u7acb\u5373\u6323\u8131\uff0c\u4f46\u80a9\u5934\u4e0a\u5df2\u662f\u73b0\u51fa\u4e94\u9053\u6307\u75d5\uff0c\u9c9c\u8840\u6dcb \u6f13\u3002 \u3000\u3000\u7b26\u79bb\u6e10\u5de6\u81c2\u4e00\u5f2f\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u53c8\u4e0e\u8c37\u4e2d\u83b2\u5bf9\u4e86\u4e00\u638c\u3002\u8fd9\u6b21\u8c37\u4e2d\u83b2\u89e6\u53ca\u5bf9\u65b9\u7684\u624b \u638c\uff0c\u53ea\u89c9\u4e00\u7247\u51b0\u51b7\uff0c\u7adf\u7136\u4e0d\u4f3c\u662f\u8840\u8089\u4e4b\u8eaf\uff0c\u4e00\u60ca\u4e4b\u4e0b\uff0c\u8fde\u9000\u4e09\u6b65\u3002 \u3000\u3000\u539f\u6765\u7b26\u79bb\u6e10\u66fe\u5f97\u5b5f\u795e\u901a\u4e4b\u52a9\uff0c\u7ec3\u6210\u4e86\u4e00\u95e8\u6781\u5389\u5bb3\u7684\u201c\u5927\u7384\u9634\u4e94\u884c\u6c14\u529f\u201d\uff0c\u82e5\u662f\u5230\u4e86 \u6700\u9ad8\u5883\u754c\uff0c\u53ef\u4ee5\u4e0e\u201c\u7f57\u9634\u715e\u529f\u201d\u5f02\u66f2\u540c\u5de5\uff0c\u53ea\u56e0\u4ed6\u5148\u88ab\u8c37\u4e2d\u83b2\u70b9\u4e2d\u4e86\u201c\u66f2\u6c60\u7a74\u201d\uff0c\u5a01\u529b \u51cf\u5c11\u51e0\u5206\uff0c\u8c37\u4e2d\u83b2\u867d\u7136\u53d7\u4e86\u4e00\u60ca\uff0c\u5374\u4e5f\u5e76\u65e0\u4f24\u635f\u3002 \u3000\u3000\u534e\u4e91\u78a7\u6709\u5979\u7236\u4eb2\u79d8\u5236\u7684\u91d1\u521b\u836f\u548c\u5c0f\u8fd8\u4e39\uff0c\u91d1\u521b\u836f\u4e00\u6577\uff0c\u7acb\u5373\u6b62\u8840\uff0c\u5c0f\u8fd8\u4e39\u773c\u4e0b\uff0c\u5143 \u6c14\u4e5f\u6062\u590d\u5982\u521d\u3002\u5f53\u4e0b\u6325\u5251\u518d\u4e0a\uff0c\u4e0e\u8c37\u4e2d\u83b2\u8054\u624b\u5e94\u654c\u3002 \u3000\u3000\u534e\u4e91\u78a7\u7684\u7236\u4eb2\u662f\u6b66\u6797\u4e00\u6d41\u9ad8\u624b\uff0c\u5979\u5bb6\u5b66\u6e0a\u6e90\uff0c\u6b66\u529f\u5176\u5b9e\u4e5f\u5e76\u4e0d\u5f31\uff0c\u4e0d\u8fc7\u5728\u7b26\u79bb\u6e10\u4e0e \u8c37\u4e2d\u83b2\u4e4b\u524d\uff0c\u8fd9\u624d\u76f8\u5f62\u89c1\u62d9\u800c\u5df2\u3002\u4f46\u73b0\u5728\u6709\u8c37\u4e2d\u83b2\u5728\u6b63\u9762\u62b5\u5fa1\u5f3a\u654c\uff0c\u5979\u4ece\u65c1\u52a9\u653b\uff0c\u5374\u8d77 \u4e86\u5f88\u5927\u7684\u5236\u65f6\u4f5c\u7528\u3002 \u3000\u3000\u7b26\u79bb\u6e10\u5df2\u8bd5\u51fa\u534e\u4e91\u78a7\u529f\u529b\u8f83\u5f31\uff0c\u672c\u6765\u60f3\u7a81\u7834\u5979\u8fd9\u4e00\u73af\uff0c\u5148\u628a\u5979\u6293\u53bb\u518d\u8bf4\uff0c\u53ef\u662f\u8c37\u4e2d \u83b2\u8eab\u6cd5\u5947\u5feb\uff0c\u4e0d\u8bba\u7b26\u79bb\u6e10\u8f6c\u5230\u54ea\u4e2a\u65b9\u4f4d\uff0c\u5979\u90fd\u62a2\u5148\u4e00\u6b65\uff0c\u5c06\u4ed6\u7684\u653b\u52bf\u63a5\u4e86\u5341\u4e4b\u4e03\u516b\uff0c\u4ed6 \u60f3\u628a\u534e\u4e91\u78a7\u518d\u6b21\u6293\u5230\u624b\u4e2d\uff0c\u90a3\u662f\u7edd\u96be\u5982\u613f\u4e86\u3002 \u3000\u3000\u534e\u4e91\u78a7\u89c1\u8c37\u4e2d\u83b2\u5904\u5904\u987e\u4f4f\u5979\uff0c\u51fa\u4e86\u5168\u529b\uff0c\u4e3a\u5979\u9632\u62a4\uff0c\u4e0d\u7981\u53c8\u662f\u4f69\u670d\uff0c\u53c8\u662f\u611f\u6fc0\uff0c\u5fc3 \u4e2d\u60f3\u9053\uff1a\u201c\u5979\u548c\u6d77\u54e5\u624d\u662f\u5929\u9020\u5730\u8bbe\u7684\u4e00\u5bf9\uff0c\u6211\u53c8\u4f55\u5fc5\u63d2\u5728\u4ed6\u4eec\u7684\u4e2d\u95f4\uff1f\u201d\u5982\u6b64\u4e00\u60f3\uff0c\u5fc3 \u4e2d\u867d\u7136\u96be\u514d\u4e00\u9635\u60b2\u51c9\uff0c\u4f46\u795e\u667a\u5374\u5df2\u5b8c\u5168\u6e05\u9192\u3002\u67d4\u4e91\u5251\u6cd5\u4f7f\u5f00\uff0c\u5f97\u5fc3\u5e94\u624b\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u6700\u521d\u5341\u4f59\u62db\u9887\u611f\u5e94\u4ed8\u8270\u96be\uff0c\u6e10\u6e10\u4fbf\u89c9\u5f97\u654c\u4eba\u7684\u638c\u529b\u4f3c\u4e4e\u4e0d\u53ca\u6700\u521d\u7684\u5389\u5bb3\uff0c\u53cc \u638c\u76f8\u63a5\u4e4b\u65f6\uff0c\u4e5f\u6ca1\u6709\u90a3\u4e48\u51b0\u51b7\u7684\u611f\u89c9\u4e86\u3002 \u3000\u3000\u539f\u6765\u8c37\u4e2d\u83b2\u662f\u56e0\u4e3a\u670d\u98df\u4e86\u5929\u5fc3\u77f3\u4e4b\u540e\uff0c\u65f6\u65e5\u5143\u591a\uff0c\u5979\u9661\u7136\u589e\u5f3a\u7684\u529f\u529b\u5c1a\u672a\u80fd\u8fd0\u7528\u5982 \u610f\u3002\u73b0\u5728\u5728\u6fc0\u6218\u4e4b\u4e2d\uff0c\u6f5c\u529b\u672c\u80fd\u7684\u53d1\u6325\uff0c\u8fd0\u7528\u4e5f\u9010\u6e10\u7eaf\u719f\uff0c\u5979\u7684\u5185\u529f\u57fa\u7840\u4e43\u662f\u5415\u56db\u5a18\u4e00 \u8109\u76f8\u4f20\u7684\u201c\u5c11\u9633\u7384\u529f\u201d\uff0c\u5415\u56db\u5a18\u665a\u5e74\u6240\u53c2\u900f\u7684\u8fd9\u95e8\u529f\u592b\uff0c\u672c\u6765\u5c31\u662f\u4e3a\u4e86\u5bf9\u4ed8\u5b5f\u795e\u901a\u7684 \u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\u7684\uff0c\u7b26\u79bb\u6e10\u7684\u201c\u5927\u7384\u9634\u4e94\u884c\u6c14\u529f\u201d\u4e0e\u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\u5c5e\u4e8e\u540c\u4e00\u7c7b\u578b\uff0c\u4f46 \u4ed6\u672c\u8eab\u7684\u529f\u529b\u5374\u8fdc\u4e0d\u53ca\u5f53\u5e74\u7684\u5b5f\u795e\u901a\uff0c\u56e0\u6b64\u4e00\u5230\u8c37\u4e2d\u83b2\u80fd\u628a\u201c\u5c11\u9633\u7384\u529f\u201d\u3001\u53d1\u6325\u5f97\u6dcb\u6f13 \u5c3d\u81f4\u4e4b\u65f6\uff0c\u4ed6\u5c31\u4e00\u70b9\u4e5f\u5360\u4e0d\u5230\u4e0a\u98ce\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4f1a\u573a\u5df2\u9677\u5165\u6df7\u6218\u4e4b\u4e2d\u3002\u5510\u52aa\u73e0\u7a46\u4ee5\u5927\u4e58\u822c\u82e5\u638c\u51fb\u4f24\u4e86\u91d1\u9e70\u5bab\u7684\u4e00\u4e2a\u62a4\u6cd5\u5f1f\u5b50\uff0c \u5176\u4ed6\u4e09\u4eba\u4e5f\u7ed9\u4ed6\u8feb\u5f97\u540e\u9000\u3002\u6587\u5ef7\u58c1\u5ffd\u5730\u4ece\u4eba\u4e1b\u4e4b\u4e2d\u6740\u51fa\uff0c\u51b2\u7740\u4ed6\u53eb\u9053\uff1a\u201c\u6628\u665a\u5174\u72b9\u672a\u5c3d\uff0c \u54b1\u4eec\u518d\u6765\u8f83\u91cf\u8f83\u91cf\uff01\u201d\u75be\u7684\u4e00\u638c\u62cd\u51fa\uff0c\u6b63\u62a2\u5728\u90a3\u4e09\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u7684\u524d\u5934\uff0c\u63a5\u4e86\u5510\u52aa\u73e0\u7a46\u7684 \u638c\u529b\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u7684\u5185\u529f\u65e9\u5df2\u5230\u4e86\u201c\u4e09\u8c61\u5f52\u5143\u201d\u7684\u5883\u754c\uff0c\u8bba\u5b9e\u529b\u53ea\u5728\u5b9d\u8c61\u6cd5\u5e08\u4e4b\u4e0b\u800c\u5728\u7b26\u79bb\u6e10 \u4e4b\u4e0a\u3002\u5510\u52aa\u73e0\u7a46\u670d\u4e86\u5929\u5fc3\u77f3\uff0c\u672c\u6765\u53ef\u4ee5\u548c\u4ed6\u65d7\u9f13\u76f8\u5f53\uff0c\u4f46\u56e0\u5148\u6fc0\u6218\u4e86\u4e00\u573a\uff0c\u800c\u90a3\u4e09\u4e2a\u62a4 \u6cd5\u5f1f\u5b50\u529f\u529b\u4e5f\u9887\u4e0d\u5f31\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u5510\u52aa\u73e0\u7a46\u4ee5\u4e00\u5bf9\u56db\uff0c\u5374\u662f\u6e10\u611f\u4e0d\u652f\u3002\u6587\u5ef7\u58c1\u4e00\u638c\u7d27\u8fc7 \u4e00\u638c\uff0c\u518d\u5ea6\u628a\u5510\u52aa\u73e0\u7a46\u56f0\u5728\u6838\u5fc3\u3002\u6b63\u662f\uff1a \u3000\u3000\u5386\u5c3d\u8270\u8f9b\u56de\u6545\u56fd\uff0c\u91d1\u9e70\u5bab\u91cc\u6597\u9b54\u5934\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e00\u56de\u3000\u65a9\u65ad\u65e0\u660e\u6c42\u6b63\u679c\u3000\u91cd\u7ffb\u65e7\u68a6\u60f9\u76f8\u601d \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u56db\u5341\u4e00\u56de\u3000\u65a9\u65ad\u65e0\u660e\u6c42\u6b63\u679c\u3000\u91cd\u7ffb\u65e7\u68a6\u60f9\u76f8\u601d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u89c1\u5510\u52aa\u73e0\u7a46\u9677\u5165\u91cd\u56f4\uff0c\u610f\u6b32\u52a9\u4ed6\u4e00\u81c2\u4e4b\u529b\uff0c\u4e00\u626c\u624b\u4fbf\u53d1\u51fa\u56db\u9897\u51b0\u9b44\u795e\u5f39\uff0c \u5411\u6587\u5ef7\u58c1\u90a3\u73ed\u4eba\u6253\u53bb\u3002\u6587\u5ef7\u58c1\u7684\u5185\u529f\u65e9\u5df2\u5230\u4e86\u201c\u4e09\u8c61\u5f52\u5143\u201d\u5883\u754c\uff0c\u88ab\u51b0\u5f39\u6253\u4e2d\uff0c\u82e5\u65e0\u5176 \u4e8b\u3002\u90a3\u4e09\u4e2a\u62a4\u6cd5\u5f1f\u5b50\uff0c\u5374\u7981\u4e0d\u4f4f\u673a\u4f36\u601c\u5730\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u5fc3\u9053\uff1a\u201c\u6211\u4e14\u5148\u628a\u4ed6\u7684\u7fbd\u7ffc\u526a\u9664\uff0c\u53ea\u5269\u4e0b\u6587\u5ef7\u58c1\u8fd9\u53ae\uff0c\u5510\u52aa\u73e0\u7a46\u4fbf\u4e0d\u96be\u5bf9 \u4ed8\u4ed6\u4e86\u3002\u201d\u518d\u6b21\u626c\u624b\uff0c\u53d1\u51fa\u4e5d\u9897\u51b0\u9b44\u795e\u5f39\uff0c\u5374\u6487\u5f00\u4e86\u6587\u5ef7\u58c1\uff0c\u4e13\u6253\u90a3\u4e09\u4e2a\u62a4\u6cd5\u5f1f\u5b50\uff0c\u4e5d \u9897\u51b0\u5f39\uff0c\u5206\u6210\u4e09\u7ec4\u3002\u800c\u6bcf\u7ec4\u90a3\u4e09\u9897\u51b0\u5f39\uff0c\u53c8\u5206\u6253\u5bf9\u65b9\u4e0a\u4e2d\u4e0b\u4e09\u5904\u4e0d\u540c\u7684\u90e8\u4f4d\u3002 \u3000\u3000\u5ffd\u6709\u4e09\u4e2a\u9ad8\u9f3b\u6df1\u76ee\u7684\u548c\u5c1a\u7a81\u7136\u6740\u51fa\uff0c\u9ad8\u9ad8\u4e3e\u8d77\u4e09\u4e2a\u91d1\u76c2\u94b5\uff0c\u53ea\u4e00\u7f69\uff0c\u90a3\u4e5d\u9897\u51b0\u9b44\u795e \u5f39\u4fbf\u90fd\u843d\u5165\u4ed6\u4eec\u7684\u91d1\u94b5\u4e4b\u4e2d\uff0c\u51b0\u5f39\u77ac\u5373\u5316\u6c34\uff0c\u90a3\u4e09\u4e2a\u548c\u5c1a\u52a8\u4f5c\u5982\u4e00\uff0c\u540c\u58f0\u8bf4\u9053\uff1a\u201c\u591a\u8c22 \u5973\u65bd\u4e3b\u8d50\u4e88\u7518\u6cc9\u89e3\u6e34\u3002\u201d\u7adf\u7136\u5404\u81ea\u628a\u91d1\u94b5\u4e2d\u7684\u51b0\u6c34\u4e00\u53e3\u559d\u5149\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u8d76\u5fd9\u5c04\u51fa\u4e09\u652f\u5929\u5c71\u795e\u8292\uff0c\u5510\u7ecf\u5929\u7684\u529f\u529b\u4f55\u7b49\u6df1\u539a\uff0c\u4f46\u89c1\u4e09\u9053\u4e4c\u91d1 \u5149\u534e\uff0c\u7834\u7a7a\u98de\u51fa\uff0c\u9690\u9690\u5e26\u7740\u98ce\u96f7\u4e4b\u58f0\u3002 \u3000\u3000\u90a3\u4e09\u4e2a\u548c\u5c1a\u53c8\u540c\u58f0\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u65bd\u4e3b\u539a\u8d50\u3002\u201d\u91d1\u76c2\u94b5\u4e00\u4e3e\uff0c\u53ea\u89c1\u706b\u82b1\u98de\u6e85\uff0c\u90a3\u4e09\u652f \u5929\u5c71\u795e\u8292\u4e5f\u90fd\u843d\u5728\u94b5\u4e2d\u3002 \u3000\u3000\u5510\u7ecf\u5929\u5927\u6012\uff0c\u6e38\u9f99\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u4e00\u62db\u201c\u7384\u4e4c\u5212\u6c99\u201d\uff0c\u6a2a\u524a\u8fc7\u53bb\uff0c\u5251\u67c4\u4e00\u6296\uff0c\u867d\u7136\u53ea \u662f\u4e00\u62db\uff0c\u4f46\u524a\u5230\u4e4b\u65f6\uff0c\u5374\u5206\u6210\u4e09\u4e2a\u5251\u70b9\uff0c\u7531\u4e8e\u4ed6\u624b\u6cd5\u8fc5\u75be\u65e0\u4f26\uff0c\u51e0\u4e4e\u53ef\u8bf4\u662f\u5728\u4e00\u65f6\u95f4\u8fde \u88ad\u4e09\u4e2a\u5f3a\u654c\u3002 \u3000\u3000\u90a3\u4e09\u4e2a\u548c\u5c1a\u5404\u81ea\u4e3e\u8d77\u4e86\u53f3\u624b\u7684\u9752\u7af9\u6756\uff0c\u52a8\u4f5c\u6574\u9f50\uff0c\u540c\u65f6\u9012\u51fa\uff0c\u4e0d\u5dee\u6beb\u5398\uff0c\u6e38\u9f99\u5251\u6709 \u65ad\u91d1\u622a\u94c1\u4e4b\u80fd\uff0c\u4f46\u5374\u524a\u4e0d\u65ad\u4ed6\u4eec\u7684\u9752\u7af9\u6756\uff0c\u53ea\u542c\u5f97\u201c\u53ee\u53ee\u53ee\u201d\u4e09\u58f0\u8f7b\u5fae\u7684\u58f0\u54cd\uff0c\u5510\u7ecf\u5929 \u7684\u6e38\u9f99\u5251\u53cd\u800c\u7ed9\u4ed6\u4eec\u7684\u9752\u7af9\u6756\u8361\u5f00\u4e86\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e09\u4e2a\u548c\u5c1a\u4e43\u662f\u5929\u7afa\u5a46\u7f57\u95e8\u6559\u7684\u4e09\u5927\u9ad8\u624b\uff0c\u82e5\u8bba\u672c\u8eab\u529f\u529b\uff0c\u4ed6\u4eec\u672a\u5fc5\u6bd4\u5f97\u4e0a\u5510 \u7ecf\u5929\uff0c\u4f46\u4ed6\u4eec\u5374\u7ec3\u6210\u4e86\u4e00\u5957\u53e4\u602a\u7684\u529f\u592b\uff0c\u4e09\u4eba\u5982\u540c\u4e00\u4f53\uff0c\u5fc3\u610f\u76f8\u901a\uff0c\u52a8\u4f5c\u5982\u4e00\uff1a\u522b\u7684\u4eba \u8054\u624b\u5bf9\u654c\uff0c\u529f\u529b\u8fd8\u662f\u5907\u6709\u5404\u7684\uff0c\u5f3a\u5f31\u4e0d\u540c\uff0c\u4ed6\u4eec\u4e09\u4eba\u6bcf\u51fa\u4e00\u62db\u529f\u529b\u5374\u4f3c\u51dd\u6210\u4e00\u4f53\uff0c\u5999\u5230 \u6beb\u5dc5\uff0c\u8981\u60f3\u5404\u4e2a\u51fb\u7834\uff0c\u7edd\u4e0d\u53ef\u80fd\uff1b\u9664\u975e\u662f\u5c06\u4ed6\u4eec\u4e00\u9f50\u6253\u8d25\u3002 \u3000\u3000\u5510\u7ecf\u5929\u529f\u4e3a\u867d\u9ad8\uff0c\u4f46\u4ed6\u4eec\u4e09\u4eba\u7684\u529f\u529b\u6c47\u5408\u8d77\u6765\uff0c\u5374\u8981\u80dc\u8fc7\u5510\u7ecf\u5929\u5c11\u8bb8\u3002\u5510\u7ecf\u5929\u7684\u5251 \u62db\u88ab\u4ed6\u4eec\u5408\u529b\u5316\u89e3\uff0c\u5251\u950b\u867d\u5229\uff0c\u52b2\u9053\u5df2\u88ab\u5378\u5f00\uff0c\u5b9d\u5251\u7684\u5a01\u529b\u5f53\u7136\u4e5f\u5c31\u4e0d\u80fd\u53d1\u6325\u4e86\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u6325\u5251\u76f8\u52a9\uff0c\u5979\u7684\u51b0\u9b44\u5bd2\u5149\u5251\u53e6\u6709\u5947\u529f\uff0c\u4e0d\u4f46\u5251\u62db\u8865\u5999\uff0c\u5251\u4e0a\u53d1\u51fa\u7684\u5bd2\u6c14\u4e5f \u8db3\u4ee5\u4f24\u4eba\uff0c\u65f6\u95f4\u4e00\u4e45\uff0c\u6bd4\u51b0\u9b44\u795e\u5f39\u7684\u53ea\u662f\u731d\u7136\u4e00\u51fb\uff0c\u66f4\u4e3a\u5389\u5bb3\u3002\u8fd9\u4e09\u4e2a\u5a46\u7f57\u95e8\u9ad8\u624b\u4e0d\u6015 \u5bd2\u6c14\u4fb5\u6270\uff0c\u4f46\u5374\u4e5f\u4e0d\u80fd\u4e0d\u8fd0\u529f\u62b5\u5fa1\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u53cc\u65b9\u624d\u6070\u6070\u6253\u6210\u5e73\u624b\u3002 \u3000\u3000\u666f\u6708\u4e0a\u4eba\u4e0e\u90a3\u5e2e\u5c3c\u6cca\u5c14\u6b66\u58eb\u770b\u51fa\u6709\u673a\u53ef\u4e58\uff0c\u53c8\u8822\u8822\u6b32\u52a8\uff0c\u610f\u6b32\u56f4\u653b\u51b0\u5ddd\u5929\u5973\u3002\u5e7d\u840d \u9053\uff1a\u201c\u597d\uff0c\u6211\u5949\u4e86\u516c\u4e3b\u4e4b\u547d\uff0c\u6b63\u8981\u5c06\u4f60\u4eec\u62ff\u4e0b\u3002\u201d\u666f\u6708\u4e0a\u4eba\u5927\u6012\u9053\uff1a\u201c\u4f60\u4e0d\u4f4d\u662f\u4e2a\u5bab\u5a25\uff0c \u7adf\u6562\u5bf9\u6211\u65e0\u793c\uff0c\u770b\u638c\uff01\u201d \u3000\u3000\u5e7d\u840d\u4e5f\u80fd\u4f7f\u7528\u51b0\u9b44\u795e\u5f39\uff0c\u4f46\u529f\u529b\u624b\u6cd5\u90fd\u8fdc\u8fdc\u4e0d\u53ca\u51b0\u5ddd\u5929\u5973\uff0c\u5979\u53ea\u80fd\u7528\u51b0\u5f39\u6253\u7a74\uff0c\u4f46 \u82e5\u8981\u6253\u5165\u5bf9\u65b9\u4e03\u7a8d\u4e4b\u4e2d\uff0c\u90a3\u5c31\u4e0d\u600e\u4e48\u51c6\u4e86\u3002\u666f\u6708\u4e0a\u4eba\u7ec3\u6709\u201c\u706b\u9f99\u529f\u201d\uff0c\u5e7d\u840d\u4e00\u628a\u51b0\u5f39\u6253 \u53bb\uff0c\u5012\u7ed9\u4ed6\u63a5\u53bb\u4e86\u4e00\u534a\uff0c\u5176\u4ed6\u7684\u6b66\u58eb\u7740\u4e86\u51b0\u5f39\uff0c\u867d\u7136\u4e5f\u5728\u6253\u98a4\uff0c\u5374\u8fd8\u7981\u53d7\u5f97\u8d77\u3002 \u3000\u3000\u666f\u6708\u4e0a\u4eba\u638c\u631f\u52b2\u98ce\uff0c\u5411\u5e7d\u840d\u731b\u653b\u3002\u5ffd\u542c\u5f97\u5530\u5530\u4e24\u58f0\uff1a\u4e00\u67c4\u957f\u5251\u500f\u7136\u800c\u6765\uff0c\u6307\u4e1c\u6253\u897f\uff0c \u6307\u5357\u6253\u5317\uff0c\u9976\u662f\u666f\u6708\u4e0a\u4eba\u8eab\u624b\u90a3\u4e48\u77eb\u6377\uff0c\u7adf\u4e5f\u8eb2\u907f\u4e0d\u53ca\uff0c\u7740\u4e86\u4e00\u5251\uff0c\u5e78\u800c\u4e0d\u662f\u91cd\u4f24\uff0c\u53ea \u662f\u5212\u7834\u4e86\u5c11\u8bb8\u76ae\u8089\u3002 \u3000\u3000\u539f\u6765\u523a\u4f24\u666f\u6708\u4e0a\u4eba\u7684\u6b63\u662f\u5e7d\u840d\u7684\u4e08\u592b\u9648\u5929\u5b87\u3002\u9648\u5929\u5b87\u66fe\u670d\u51b0\u5bab\u5f02\u679c\uff0c\u8eab\u8f7b\u5982\u71d5\uff0c\u5251 \u6cd5\u53c8\u517c\u6570\u5bb6\u4e4b\u957f\uff0c\u8fd1\u5e74\u6765\u529f\u529b\u5927\u8fdb\uff0c\u65e9\u5df2\u6324\u5165\u4e00\u6d41\u9ad8\u624b\u4e4b\u5217\u3002 \u3000\u3000\u5b5f\u54c8\u8d64\u6740\u4e0a\u524d\u6765\uff0c\u66ff\u666f\u6708\u4e0a\u4eba\u63a5\u8fc7\u9648\u5929\u5b87\u7684\u5251\u62db\uff0c\u54c8\u5b5f\u8d64\u662f\u5c3c\u6cca\u5c14\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u529f \u529b\u4e0d\u5f31\u4e8e\u666f\u6708\u4e0a\u4eba\uff0c\u4ed6\u65e0\u987b\u5206\u795e\u5bf9\u4ed8\u5e7d\u840d\uff0c\u4e0e\u9648\u5929\u5b87\u6076\u6597\u8d77\u6765\uff0c\u867d\u7136\u7565\u5904\u4e0b\u98ce\uff0c\u4f46\u9648\u5929 \u5b87\u5728\u6025\u5207\u4e4b\u95f4\uff0c\u5374\u4e5f\u96be\u4ee5\u80dc\u4ed6\u3002 \u3000\u3000\u5b5f\u54c8\u8d64\u5e26\u6765\u7684\u4e00\u5e2e\u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c\u9664\u4e86\u51e0\u4e2a\u65e9\u88ab\u5510\u7ecf\u5929\u6253\u5f97\u91cd\u4f24\u4e4b\u5916\uff0c\u5927\u7ea6\u8fd8\u6709\u4e09\u5341 \u6765\u4e2a\uff0c\u8fd9\u73ed\u6b66\u58eb\u867d\u975e\u4e00\u6d41\u9ad8\u624b\uff0c\u4f46\u5e03\u6210\u4e86\u5706\u9635\uff0c\u540c\u8fdb\u540c\u9000\uff0c\u5f7c\u6b64\u547c\u5e94\uff0c\u5374\u4e5f\u5f88\u96be\u5bf9\u4ed8\u3002 \u9648\u5929\u5b87\u8fd9\u4e00\u5bf9\u592b\u59bb\u5f53\u7136\u6bd4\u4e0d\u4e0a\u5510\u7ecf\u5929\u90a3\u4e00\u5bf9\uff0c\u88ab\u56f4\u5728\u5706\u9635\u4e4b\u4e2d\uff0c\u9669\u8c61\u73af\u751f\u3002 \u3000\u3000\u6c5f\u5357\u53eb\u9053\uff1a\u201c\u5478\uff0c\u4f60\u4eec\u5c31\u4f1a\u6043\u591a\u4e3a\u80dc\uff0c\u597d\u4e0d\u8981\u8138\uff01\u201d\u4ed6\u8ddf\u91d1\u4e16\u9057\u5b66\u8fc7\u51e0\u62db\u602a\u5f02\u7684\u8eab \u6cd5\uff0c\u90a3\u5706\u9635\u672c\u6765\u5c01\u95ed\u5f97\u751a\u662f\u4e25\u5bc6\uff0c\u5374\u4e0d\u77e5\u600e\u7684\uff0c\u7a81\u7136\u88ab\u4ed6\u4e00\u4e2a\u7b4b\u6597\u3002\u5c31\u7ffb\u8fdb\u9635\u4e2d\u3002\u4e24\u4e2a \u6b66\u58eb\u4e3e\u811a\u8e22\u4ed6\uff0c\u6c5f\u5357\u9a82\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\u3002\u4f60\u60f3\u8e22\u6211\u5c41\u80a1\uff1f\u6211\u5148\u6253\u4f60\u5c41\u80a1\uff01\u201d\u4e00\u4e2a\u7b4b\u6597\u7ffb \u8fc7\u53bb\uff0c\u556a\u556a\u4e24\u58f0\u3002\u679c\u7136\u6253\u4e86\u90a3\u4e24\u4e2a\u6b66\u58eb\u7684\u5c41\u80a1\u3002 \u3000\u3000\u6c5f\u5357\u7684\u6b66\u529f\u4e0d\u7b97\u662f\u7b2c\u4e00\u6d41\u9ad8\u624b\uff0c\u4f46\u4ed6\u7684\u70b9\u7a74\u5374\u662f\u7b2c\u4e00\u6d41\u529f\u5927\uff0c\u5728\u6253\u90a3\u4e24\u4e2a\u6b66\u58eb\u5c41\u80a1 \u4e4b\u65f6\uff0c\u4fe1\u624b\u5c31\u70b9\u4e86\u4ed6\u4eec\u7684\u201c\u5c3e\u95fe\u7a74\u201d\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u6b66\u58eb\u767b\u65f6\u4ec6\u5012\uff0c\u5012\u53d8\u6210\u4e86\u540c\u4f34\u7684\u7eca\u811a\u77f3\uff0c\u4f7f\u5f97\u8fd9\u5706\u9635\u53d7\u4e86\u969c\u788d\u3002 \u3000\u3000\u666f\u6708\u4e0a\u4eba\u5927\u6012\uff0c\u5c06\u90a3\u4e24\u4e2a\u6b66\u58eb\u6293\u4e86\u8d77\u6765\uff0c\u4f46\u4ed6\u4e5f\u65e0\u6cd5\u89e3\u5f00\u6c5f\u5357\u6240\u70b9\u7684\u7a74\u9053\uff0c\u53ea\u597d\u5c06 \u90a3\u4e24\u4e2a\u6b66\u571f\u629b\u51fa\u9635\u5916\uff0c\u53cc\u638c\u4fbf\u5411\u6c5f\u5357\u62cd\u5230\u3002\u4ed6\u6293\u4eba\u3001\u6454\u4eba\u3001\u53d1\u638c\uff0c\u51e0\u4e2a\u52a8\u4f5c\u4e00\u6c14\u5475\u6210\uff0c \u5f53\u771f\u662f\u5feb\u901f\u4e4b\u6781\uff01\u4f46\u4ed6\u5feb\u6c5f\u5357\u4e5f\u5feb\uff0c\u53ea\u542c\u5f97\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u6ca1\u6253\u7740\uff01\u8f6e\u5230\u6211\u4e5f\u6253\u4f60\u5c41\u80a1\u4e86\uff01\u201d \u811a\u8ddf\u4e00\u65cb\uff0c\u8f6c\u5230\u666f\u6708\u4e0a\u4eba\u80cc\u540e\uff0c\u54ea\u77e5\u666f\u6708\u4e0a\u4eba\u6d51\u8eab\u90fd\u662f\u529f\u592b\uff0c\u5c41\u80a1\u4e00\u633a\uff0c\u6c5f\u5357\u70b9\u4e0d\u51c6\u4ed6 \u7684\u201c\u5c3e\u95fe\u7a74\u201d\uff0c\u5374\u4f3c\u78b0\u4e86\u4e00\u4e2a\u5927\u76ae\u7403\uff0c\u7adf\u7ed9\u4ed6\u5f39\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u6c5f\u5357\u77e5\u9053\u5389\u5bb3\uff0c\u4e0d\u6562\u518d\u60f9\u666f\u6708\u4e0a\u4eba\uff0c\u53ea\u5728\u6b66\u58eb\u7fa4\u4e2d\uff0c\u7a7f\u6765\u63d2\u53bb\uff0c\u4e00\u6709\u673a\u4f1a\uff0c\u5c31\u65bd\u5c55 \u4ed6\u7684\u72ec\u95e8\u70b9\u7a74\u529f\u592b\uff0c\u5012\u4e5f\u7ed9\u4ed6\u70b9\u5012\u4e86\u51e0\u4e2a\u3002\u4f46\u90a3\u5706\u9635\u8d8a\u6536\u8d8a\u7d27\uff0c\u4e0d\u4e45\u4fbf\u5373\u65e0\u9699\u53ef\u4e58\uff0c\u6c5f \u5357\u7684\u771f\u5b9e\u672c\u9886\u7a76\u7adf\u8fd8\u5acc\u4e0d\u8db3\uff0c\u767b\u65f6\u9669\u8c61\u73af\u751f\u3002 \u3000\u3000\u5ffd\u89c1\u5706\u9635\u5f00\u4e86\u4e00\u4e2a\u7f3a\u53e3\uff0c\u4e8c\u4e2a\u957f\u987b\u8005\u8005\u8fd0\u5251\u5982\u98ce\uff0c\u6740\u4e86\u8fdb\u6765\uff0c\u6b66\u58eb\u4eec\u7adf\u662f\u906e\u62e6\u4e0d\u4f4f\u3002 \u8fd9\u65f6\u5b5f\u54c8\u8d64\u6b63\u81ea\u4e00\u68d2\u5411\u6c5f\u5357\u6253\u4e0b\uff0c\u90a3\u8001\u8005\u559d\u58f0\uff1a\u201c\u770b\u5251\uff01\u201d\u672c\u6765\u8fd8\u5728\u6570\u4e08\u4e4b\u5916\uff0c\u58f0\u72b9\u6765 \u4e86\uff0c\u500f\u7136\u95f4\u5df2\u5230\u4e86\u5b5f\u54c8\u8d64\u8eab\u540e\u3002 \u3000\u3000\u8fd9\u957f\u987b\u8001\u8005\u662f\u9752\u57ce\u6d3e\u540d\u5bbf\u8427\u9752\u5cf0\uff0c\u4ed6\u662f\u9648\u5929\u5b87\u7684\u5f00\u8499\u5e08\u7236\uff0c\u6c5f\u5357\u5c0f\u65f6\u505a\u9648\u5929\u5b87\u7684\u4e66 \u7ae5\uff0c\u4e5f\u66fe\u6109\u5b66\u8fc7\u4ed6\u7684\u529f\u592b\u3002\u9648\u5929\u5b87\u592b\u59bb\u4e0e\u6c5f\u5357\u9047\u9669\uff0c\u4ed6\u7109\u80fd\u5750\u89c6\uff1f\u4f46\u56e0\u4ed6\u662f\u6b66\u6797\u524d\u8f88\uff0c \u5904\u5904\u8981\u987e\u7740\u8eab\u4efd\uff0c\u4ed6\u4e0d\u80af\u5728\u80cc\u540e\u653b\u51fb\u5b5f\u54c8\u8d64\uff0c\u6240\u4ee5\u5728\u53d1\u62db\u4e4b\u524d\uff0c\u5148\u559d\u4e00\u58f0\uff0c\u63d0\u9192\u654c\u4eba\uff0c \u597d\u8ba9\u5bf9\u65b9\u65e9\u4f5c\u51c6\u5907\u3002 \u3000\u3000\u5b5f\u54c8\u8d64\u77e5\u9053\u8427\u9752\u5cf0\u662f\u4e2a\u52b2\u654c\uff0c\u987e\u4e0d\u5f97\u4f24\u5bb3\u6c5f\u5357\uff0c\u6a2a\u68d2\u5148\u6321\u5251\u62db\uff0c\u8427\u9752\u5cf0\u4e00\u62db\u201c\u987a\u624b \u63a8\u821f\u201d\uff0c\u957f\u5251\u8d34\u7740\u4ed6\u7684\u94c1\u68d2\u524a\u4e0a\u3002\u201c\u987a\u624b\u63a8\u821f\u201d\u672c\u6765\u662f\u5f88\u666e\u901a\u7684\u5251\u62db\uff0c\u4f46\u7ecf\u8427\u9752\u5cf0\u4e4b\u624b \u8fd0\u7528\u51fa\u6765\uff0c\u5374\u662f\u51fa\u795e\u5165\u5316\uff0c\u5b5f\u54c8\u8d64\u529f\u529b\u7565\u900a\u4e00\u7b79\uff0c\u8427\u9752\u5cf0\u7684\u957f\u5251\u8d34\u7740\u4ed6\u94c1\u68d2\u524a\u6765\uff0c\u4ed6\u62e8 \u4e0d\u5f00\u957f\u5251\uff0c\u53ea\u597d\u8fde\u5fd9\u64a4\u68d2\uff0c\u53ea\u542c\u5f97\u201c\u5693\u201d\u7684\u4e00\u58f0\uff0c\u9976\u662f\u4ed6\u53ca\u65f6\u6536\u62db\uff0c\u8eb2\u95ea\u5f97\u5feb\uff0c\u4e5f\u88ab\u524a \u53bb\u4e86\u4e00\u6839\u6307\u5934\u3002\u8427\u9752\u8702\u52a0\u5165\u6218\u56e2\u4e4b\u540e\uff0c\u9648\u5929\u5b57\u592b\u59bb\u8fd9\u624d\u8f6c\u5371\u4e3a\u5b89\uff0c\u6c5f\u5357\u4e5f\u5f97\u4ee5\u65bd\u5c55\u6240\u957f \u4e86\u3002 \u3000\u3000\u5408\u4ed6\u4eec\u56db\u4eba\u4e4b\u529b\uff0c\u5bf9\u6297\u666f\u6708\u4e0a\u4eba\u4e0e\u90a3\u4e00\u7fa4\u6b66\u58eb\uff0c\u6070\u6070\u65d7\u9f13\u76f8\u5f53\u3002 \u3000\u3000\u8c37\u3001\u534e\u4e8c\u5973\u529b\u6218\u5c60\u9f99\u5c9b\u4e3b\u7b26\u79bb\u6e10\uff0c\u8fd9\u65f6\u4ea6\u5df2\u6e10\u6e10\u5360\u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u6218\u5230\u5206\u9645\uff01\u8c37\u4e2d\u83b2\u5ffd\u5730\u4e00\u62db\u201c\u7389\u5973\u6295\u68ad\u201d\uff0c\u4fa7\u8eab\u8fdb\u638c\uff0c\u5192\u9669\u62a2\u653b\uff0c\u7b26\u79bb\u6e10\u770b\u51fa\u7834\u7efd\uff0c \u5fc3\u4e2d\u5927\u559c\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5230\u5e95\u662f\u521d\u51fa\u9053\u7684\u96cf\u513f\uff01\u53ea\u987e\u653b\u4eba\uff0c\u4e0d\u987e\u9632\u5df1\u3002\u201d\u4ed6\u672c\u662f\u5df2\u65e0\u80dc \u671b\uff0c\u8fd9\u65f6\u770b\u51fa\u673a\u4f1a\uff0c\u7acb\u5373\u4fbf\u4e0b\u6740\u624b\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u4e00\u6293\u5c31\u6293\u7740\u4e86\u8c37\u4e2d\u83b2\u7684\u7435\u7436\u9aa8\u3002 \u3000\u3000\u8fd9\u7435\u7436\u9aa8\u4e43\u662f\u4eba\u8eab\u8981\u5bb3\uff0c\u7435\u7436\u9aa8\u82e5\u88ab\u634f\u788e\uff0c\u591a\u597d\u6b66\u529f\uff0c\u4e5f\u6210\u6b8b\u5e9f\u3002\u5374\u4e0d\u659c\u8c37\u4e2d\u83b2\u7a7f \u6709\u9632\u8eab\u81f3\u5b9d\u7684\u767d\u7389\u7532\uff0c\u5200\u5251\u5c1a\u4e14\u4e0d\u80fd\u523a\u7a7f\uff0c\u7b26\u79bb\u6e10\u7684\u6307\u7532\u66f4\u662f\u4e0d\u80fd\u6293\u7834\u3002\u8c37\u4e2d\u83b2\u7684\u5c11\u9633 \u7384\u529f\u53c8\u8db3\u4ee5\u9632\u5fa1\u4ed6\u7684\u7384\u9634\u638c\u529b\uff0c\u7b26\u79bb\u6e10\u6293\u7740\u5979\u7684\u7435\u7436\u9aa8\uff0c\u6beb\u65e0\u4f5c\u7528\uff0c\u53cd\u800c\u51cf\u5f31\u4e86\u81ea\u5df1\u7684 \u9632\u5fa1\u529b\u91cf\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u51fa\u624b\u5982\u7535\uff0c\u5c31\u5728\u8fd9\u540c\u4e00\u65f6\u95f4\uff0c\u4e00\u638c\u51fb\u4e2d\u4e86\u7b26\u79bb\u6e10\u80c1\u4e0b\u7684\u201c\u9b42\u95e8\u7a74\u201d\u3002\u7b26\u79bb\u6e10 \u5927\u53eb\u4e00\u58f0\uff0c\u7ed9\u9707\u5f97\u4f3c\u76ae\u7403\u822c\u5730\u629b\u4e86\u8d77\u6765\u3002 \u3000\u3000\u534e\u4e91\u78a7\u8865\u4e0a\u4e00\u5251\uff0c\u523a\u5f97\u4ed6\u8840\u5982\u6cc9\u6d8c\uff0c\u72fc\u72c8\u800c\u9003\uff0c\u62a5\u4e86\u521a\u624d\u90a3\u4e00\u6293\u4e4b\u6068\u3002 \u3000\u3000\u539f\u6765\u8c37\u4e2d\u83b2\u662f\u56e0\u4e3a\u770b\u89c1\u54e5\u54e5\u5f62\u52bf\u4e0d\u5229\uff0c\u6025\u7740\u8981\u4f1a\u76f8\u52a9\u54e5\u54e5\uff0c\u56e0\u6b64\u6545\u610f\u5356\u4e2a\u7834\u7efd\uff0c\u6765 \u8bf1\u7b26\u79bb\u6e10\u4e0a\u5f53\u7684\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u51fb\u8d25\u4e86\u7b26\u79bb\u6e10\uff0c\u8eab\u5f62\u75be\u8d77\uff0c\u4e00\u638c\u4fbf\u5411\u6587\u5ef7\u58c1\u6253\u53bb\uff0c\u6587\u5ef7\u58c1\u53cd\u624b\u4e00\u6325\uff0c\u4e24\u80a1\u5288 \u7a7a\u638c\u529b\u78b0\u4e2a\u6b63\u7740\uff0c\u53d1\u51fa\u4e86\u95ea\u96f7\u4f3c\u7684\u58f0\u54cd\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u529f\u529b\u7a76\u7adf\u662f\u7a0d\u900a\u4e00\u7b79\uff0c\u7981\u4e0d\u4f4f\u4e00\u4e2a\u8e09\u8dc4\uff0c\u5411\u65c1\u8fb9\u6ed1\u51fa\u51e0\u6b65\u3002 \u3000\u3000\u91d1\u9e70\u5bab\u7684\u9996\u5ea7\u62a4\u6cd5\u5f1f\u5b50\u8d81\u7740\u5979\u7acb\u8db3\u672a\u7a33\uff0c\u6325\u52a8\u4e5d\u73af\u9521\u6756\u4fbf\u70b9\u5979\u819d\u76d6\u7684\u201c\u73af\u8df3\u7a74\u201d\uff0c \u8fd9\u9996\u5ea7\u62a4\u6cd5\u5f1f\u5b50\u77e5\u5979\u662f\u524d\u738b\u516c\u4e3b\u7684\u8eab\u4efd\u3002 \u3000\u3000\u610f\u6b32\u5c06\u5979\u751f\u64d2\uff0c\u6545\u800c\u5c06\u9521\u6756\u5f53\u4f5c\u5224\u5b98\u7b14\u4f7f\uff0c\u53ea\u6562\u4f7f\u51fa\u4e94\u516d\u5206\u6c14\u529b\u3002 \u3000\u3000\u54ea\u77e5\u8c37\u4e2d\u83b2\u7684\u529f\u529b\u867d\u662f\u4e0d\u53ca\u6587\u5ef7\u58c1\uff0c\u5374\u80dc\u8fc7\u8fd9\u62a4\u6cd5\u5f1f\u5b50\u8bb8\u591a\u3002\u4e2d\u6307\u4e00\u5f39\uff0c\u201c\u94ee\u201d\u7684 \u4e00\u58f0\uff0c\u5df2\u628a\u4e5d\u73af\u9521\u6756\u5f39\u5f00\uff0c\u90a3\u62a4\u6cd5\u5f1f\u5b50\u864e\u53e3\u53d1\u9ebb\uff0c\u9669\u4e9b\u8fde\u4e5d\u73af\u9521\u6756\u4e5f\u8981\u8131\u624b\u3002 \u3000\u3000\u9996\u5ea7\u62a4\u6cd5\u5f1f\u5b50\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u624d\u77e5\u9053\u5389\u5bb3\uff0c\u8fde\u5fd9\u7528\u8db3\u4e86\u6c14\u529b\uff0c\u518d\u6b21\u53d1\u62db\uff1a\u4ed6\u7684\u4e24\u4e2a\u5e08 \u5f1f\u5404\u81ea\u4e3e\u8d77\u4e5d\u73af\u9521\u6756\uff0c\u4ece\u4e24\u4fa7\u653b\u6765\uff0c\u4e3a\u4ed6\u52a9\u9635\u3002 \u3000\u3000\u90a3\u6587\u5ef7\u58c1\u56e0\u4e3a\u5206\u51fa\u4e86\u4e00\u638c\u4e4b\u529b\u53bb\u5bf9\u4ed8\u8c37\u4e2d\u83b2\uff0c\u5510\u52aa\u73e0\u7a46\u7684\u638c\u529b\u7acb\u5373\u4e58\u865a\u800c\u5165\u3002\u5e78\u800c \u6587\u5ef7\u58c1\u7ecf\u9a8c\u8001\u5230\uff0c\u65e9\u5df2\u6709\u4e86\u9632\u5907\uff0c\u5728\u53d1\u638c\u9065\u51fb\u8c37\u4e2d\u83b2\u4e4b\u65f6\uff0c\u4e5f\u5c31\u7acb\u5373\u4f7f\u51fa\u79fb\u5f62\u6362\u4f4d\u7684\u529f \u592b\uff0c\u907f\u5f00\u4e86\u5510\u52aa\u73e0\u7a46\u6b63\u9762\u653b\u6765\u7684\u529b\u91cf\u3002\u4f46\uff0c\u867d\u7136\u5982\u6b64\uff0c\u4f59\u6ce2\u6240\u53ca\uff0c\u4ecd\u662f\u4e0d\u7981\u8fde\u9000\u51e0\u6b65\u3002 \u8fd9\u4e48\u4e00\u6765\uff0c\u4ed6\u4e0e\u90a3\u4e09\u4e2a\u62a4\u6cd5\u4e1a\u5df2\u9694\u5f00\uff0c\u5206\u6210\u4e86\u4e24\u5806\u53ae\u6740\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7cbe\u795e\u9661\u632f\uff0c\u559d\u9053\uff1a\u201c\u59d3\u6587\u7684\uff0c\u73b0\u5728\u54b1\u4eec\u53ef\u4ee5\u597d\u597d\u8f83\u91cf\u5566\uff01\u201d\u5927\u4e58\u822c\u82e5\u638c\u4e00 \u638c\u63a5\u7740\u4e00\u638c\uff0c\u4fe8\u5982\u957f\u6c5f\u5927\u6cb3\u6eda\u6eda\u800c\u4e0a\uff0c\u767b\u65f6\u628a\u6587\u5ef7\u58c1\u6253\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529f\u3002\u4f46\u6587\u5ef7\u58c1\u7ec3\u6210 \u4e86\u201c\u4e09\u8c61\u5f52\u5143\u201d\u7684\u90aa\u6d3e\u7edd\u9876\u795e\u529f\uff0c\u53ea\u5b88\u4e0d\u653b\uff0c\u4e5f\u662f\u5b88\u5f97\u6781\u4e3a\u6c89\u7a33\u3002\u5510\u52aa\u73e0\u7a46\u60ca\u6d9b\u9a87\u6d6a\u822c \u7684\u638c\u529b\uff01\u7adf\u4e5f\u4e0d\u80fd\u5c06\u4ed6\u6447\u64bc\u3002 \u3000\u3000\u90a3\u4e09\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u8054\u8d77\u624b\u6765\uff0c\u4e09\u652f\u4e5d\u73af\u9521\u6756\u5408\u6210\u4e86\u4e00\u9053\u73af\u5f62\uff0c\u9996\u5c3e\u547c\u5e94\uff0c\u5a01\u529b\u7740\u5b9e\u4e0d \u5f31\u3002\u8c37\u4e2d\u83b2\u5012\u4e5f\u4e0d\u6562\u8f7b\u654c\uff0c\u5f53\u4e0b\u62d4\u51fa\u5979\u4ece\u6728\u534e\u9ece\u624b\u4e2d\u593a\u56de\u7684\u4f69\u5251\uff0c\u5c55\u5f00\u4e86\u7384\u5973\u5251\u6cd5\uff0c\u4e0e \u4e09\u652f\u9521\u6756\u6597\u5728\u4e00\u8d77\u3002\u5979\u8fd9\u67c4\u4f69\u5251\u4e43\u662f\u5415\u56db\u5a18\u5f53\u5e74\u7528\u8fc7\u7684\u90a3\u67c4\u971c\u534e\u5b9d\u5251\uff0c\u5251\u8d28\u867d\u7136\u4e0d\u53ca\u6c5f \u6d77\u5929\u7684\u88c1\u4e91\u5b9d\u5251\uff0c\u5374\u4e5f\u6781\u4e3a\u950b\u5229\u3002 \u3000\u3000\u7384\u5973\u5251\u6cd5\u4ee5\u8f7b\u7075\u7fd4\u52a8\u89c1\u957f\uff0c\u8c37\u4e2d\u83b2\u65b0\u8fd1\u53c8\u7ec3\u6210\u4e86\u5929\u7f57\u6b65\u6cd5\uff0c\u4f7f\u5c06\u51fa\u6765\uff0c\u66f4\u5982\u6d41\u6c34\u884c \u4e91\uff0c\u66f2\u5c3d\u5176\u5999\u3002\u8fd9\u4e09\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u65e2\u5fcc\u60ee\u5979\u7684\u5b9d\u5251\uff0c\u66f4\u5fcc\u60ee\u5979\u90a3\u7528\u7535\u822c\u7684\u8eab\u6cd5\uff0c\u5f53\u4e0b\u4e5f\u662f \u53ea\u80fd\u5b88\uff0c\u4e0d\u6562\u8fdb\u653b\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5168\u573a\u9677\u5165\u6df7\u6218\u4e4b\u4e2d\uff0c\u5206\u6210\u4e86\u4e94\u516d\u5904\u53ae\u6740\uff0c\u6bcf\u4e00\u5904\u90fd\u662f\u6253\u5f97\u96be\u89e3\u96be\u5206\uff0c\u4e00\u65f6\u4e4b \u95f4\uff0c\u5b9e\u662f\u4e0d\u6613\u5206\u51fa\u80dc\u8d1f\uff0c\u5176\u4e2d\u5f53\u7136\u4ee5\u6c5f\u6d77\u5929\u548c\u5b9d\u8c61\u6cd5\u5e08\u8fd9\u4e00\u5bf9\u53c8\u6253\u5f97\u6700\u4e3a\u6fc0\u70c8\uff0c\u4f46\u4e5f\u4ee5 \u6c5f\u6d77\u5929\u7684\u5904\u5883\u6700\u4e3a\u4e0d\u5229\uff0c\u65c1\u4eba\u770b\u4e0d\u51fa\u6765\uff0c\u4ed6\u81ea\u5df1\u5374\u611f\u89c9\u5f97\u5230\u5df2\u662f\u6e10\u5904\u4e0b\u98ce\u3002 \u3000\u3000\u8981\u77e5\u6c5f\u6d77\u5929\u7684\u529f\u529b\u867d\u662f\u6781\u9ad8\uff0c\u4f46\u5374\u662f\u9760\u836f\u7269\u6240\u589e\u957f\u7684\u529f\u529b\uff0c\u800c\u4ed6\u6700\u521d\u624e\u6839\u57fa\u4e4b\u65f6\u53c8 \u8d70\u9519\u4e86\u4e00\u6b65\uff0c\u7ec3\u7684\u662f\u90aa\u6d3e\u5185\u529f\uff0c\u867d\u7136\u4ed6\u73b0\u5728\u4ea6\u5df2\u5230\u4e86\u201c\u6b63\u90aa\u4e00\u5408\u201d\u5883\u754c\uff0c\u4f46\u7a76\u7adf\u4e0e\u8c37\u4e2d \u83b2\u7684\u60c5\u5f62\u4e0d\u540c\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u7531\u4e8e\u4e00\u5f00\u59cb\u5c31\u5f97\u5230\u6b63\u5b97\u5185\u529f\u5fc3\u6cd5\uff0c\u9760\u836f\u7269\u6240\u589e\u957f\u7684\u529f\u529b\u5f88\u5feb\u5c31\u53ef\u4ee5\u4e0e\u672c\u8eab\u539f \u6709\u7684\u529f\u529b\u51dd\u4e3a\u4e00\u4f53\uff0c\u6c34\u4e73\u4ea4\u878d\uff0c\u8fd0\u7528\u5982\u610f\uff1b\u800c\u6c5f\u6d77\u5929\u5219\u5fc5\u5047\u4ee5\u65f6\u65e5\uff0c\u65b9\u80fd\u505a\u5230\uff0c\u6545\u6b64\uff0c \u5728\u8c37\u4e2d\u83b2\u6597\u7b26\u79bb\u6e10\u4e4b\u65f6\uff0c\u662f\u8d8a\u6218\u8d8a\u5f3a\uff0c\u800c\u6c5f\u6d77\u5929\u6597\u5b9d\u8c61\u6cd5\u5e08\uff0c\u5219\u662f\u591a\u6597\u4e00\u523b\uff0c\u5c31\u591a\u51cf\u8017 \u4e00\u5206\uff0c\u90a3\u4e5f\u5c31\u7b49\u4e8e\u8d8a\u6218\u8d8a\u5f31\u4e86\u3002\u5b9d\u8c61\u6cd5\u5e08\u7684\u201c\u9f99\u8c61\u529f\u201d\u662f\u4f5b\u95e8\u7edd\u9876\u795e\u529f\uff0c\u7ecf\u8fc7\u4e86\u6570\u5341\u5e74 \u5bd2\u6691\u4e4b\u529f\u82e6\u7ec3\u6210\u7684\uff0c\u8fe5\u975e\u9760\u836f\u7269\u589e\u957f\u7684\u529f\u529b\u53ef\u6bd4\uff0c\u4ed6\u7684\u638c\u529b\u4e00\u91cd\u91cd\u52a0\u4e0a\u53bb\uff0c\u6597\u5230\u4e94\u5341\u62db \u5f00\u5916\uff0c\u6c5f\u6d77\u592d\u4fbf\u6e10\u6e10\u76f8\u5f62\u89c1\u7ee7\uff0c\u53ea\u89c9\u4ece\u56db\u9762\u516b\u65b9\u800c\u6765\u7684\u963b\u529b\uff0c\u8d8a\u6765\u8d8a\u5927\uff0c\u7adf\u4f3c\u51dd\u6210\u4e86\u5b9e \u8d28\uff0c\u4ee4\u4ed6\u7684\u8ffd\u98ce\u5251\u5f0f\uff0c\u4e5f\u611f\u5230\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u4e0d\u8fc7\u5b9d\u8c61\u6cd5\u5e08\u867d\u7136\u5360\u4e86\u4e0a\u98ce\uff0c\u5fc3\u91cc\u5374\u4e5f\u5728\u6697\u6697\u53eb\u82e6\u3002\u539f\u6765\u201c\u9f99\u8c61\u529f\u201d\u867d\u662f\u4f5b\u95e8\u7684\u65e0 \u4e0a\u795e\u529f\uff0c\u4ed6\u5374\u8fd8\u6765\u7ec3\u5230\u81f3\u9ad8\u65e0\u4e0a\u7684\u5883\u754c\uff0c\u4ed6\u662f\u62fc\u7740\u8017\u635f\u5143\u6c14\u6765\u65bd\u5c55\u8fd9\u7edd\u4e16\u795e\u529f\u7684\uff0c\u65f6\u95f4 \u4e00\u957f\uff0c\u4ed6\u4e5f\u8981\u53d7\u5230\u5927\u5927\u7684\u4f24\u5bb3\uff0c\u540e\u679c\u582a\u865e\u3002\u4ed6\u6700\u521d\u672c\u4ee5\u4e3a\u201c\u9f99\u8c61\u529f\u201d\u4e00\u7ecf\u4f7f\u7528\uff0c\u5c31\u53ef\u4ee5 \u5728\u5341\u62db\u4eba\u62db\u4e4b\u5185\uff0c\u5c06\u6c5f\u6d77\u5929\u6bd9\u4e8e\u638c\u4e0b\u3002 \u3000\u3000\u54ea\u77e5\u9053\u5df1\u8fc7\u4e86\u4e94\u5341\u62db\uff0c\u6c5f\u6d77\u5929\u867d\u5904\u4e0b\u98ce\uff0c\u4ecd\u662f\u8d25\u8c61\u672a\u663e\u3002\u5b9d\u8c61\u6cd5\u5e08\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5018 \u82e5\u518d\u8fc7\u4e94\u5341\u62db\uff0c\u6211\u7eb5\u7136\u51fb\u6bd9\u4e86\u8fd9\u5c0f\u5b50\uff0c\u53ea\u6015\u4e5f\u5f97\u5927\u75c5\u4e00\u573a\uff0c\u51cf\u5bff\u5341\u5e74\u3002\u201d \u3000\u3000\u5168\u573a\u6df7\u6218\u4e4b\u4e2d\uff0c\u6700\u9ad8\u5174\u7684\u521a\u662f\u59ec\u6653\u98ce\u3002\u4ed6\u6709\u4e00\u4e2a\u602a\u7656\uff0c\u559c\u6b22\u5077\u522b\u4eba\u7684\u4e1c\u897f\u505a\u7eaa\u5ff5\u54c1\uff0c \u5c24\u5176\u662f\u5e73\u65e5\u96be\u4ee5\u78b0\u4e0a\uff0c\u4f8b\u5982\u662f\u5916\u56fd\u4eba\u7684\u4e1c\u897f\u3002\u4e1c\u897f\u4e5f\u4e0d\u5fc5\u503c\u94b1\uff0c\u53ea\u8981\u80fd\u4ee3\u8868\u90a3\u4eba\u7684\u8eab\u4efd\uff0c \u8d8a\u7f55\u89c1\u7684\u8d8a\u5999\u3002\u73b0\u5728\u5728\u8fd9\u4f1a\u573a\u4e4b\u4e2d\uff0c\u6709\u5370\u5ea6\u3001\u6ce2\u65af\u3001\u5c3c\u6cca\u5c14\u3001\u963f\u523a\u4f2f\u4e0e\u53ca\u897f\u57df\u5404\u571f\u90a6\u7684 \u6b66\u6797\u4eba\u7269\u5927\u6253\u51fa\u624b\uff0c\u8fd9\u771f\u662f\u5e73\u751f\u96be\u9047\u7684\u826f\u673a\uff0c\u5c82\u80fd\u9519\u8fc7\uff1f \u3000\u3000\u59ec\u6653\u98ce\u6084\u6084\u5730\u8d70\u5230\u90a3\u5370\u5ea6\u795e\u5077\u8eab\u65c1\uff0c\u505a\u4e00\u4e86\u4e2a\u63a2\u56ca\u53d6\u7269\u7684\u624b\u52bf\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u4f60\u60f3 \u4e0d\u60f3\u5b66\u4e2d\u56fd\u7684\u5999\u624b\u7a7a\u7a7a\u672c\u9886\uff1f\u8ddf\u6211\u6765\uff0c\u77a7\u6211\u7684\uff01\u201d\u90a3\u5370\u5ea6\u795e\u5077\u4e0d\u61c2\u4ed6\u7684\u8bdd\u4e5f\u61c2\u5f97\u4ed6\u7684\u624b \u52bf\uff0c\u6115\u4e86\u4e00\u6115\u3001\u53eb\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u80af\u6536\u6211\u505a\u5f92\u5f1f\u4e86\uff1f\u201d\u8bdd\u8fd8\u672a\u6bd5\uff0c\u59ec\u6653\u98ce\u5df2\u6e9c\u5165\u4eba\u4e1b\u4e4b \u4e2d\uff0c\u5927\u5c55\u7a7a\u7a7a\u5999\u624b\u4e86\u3002 \u3000\u3000\u573a\u4e2d\u5c3d\u6709\u6b66\u529f\u6bd4\u4ed6\u9ad8\u660e\u5f97\u591a\u7684\u4eba\uff0c\u4f46\u4eba\u4eba\u5728\u6fc0\u6218\u4e4b\u4e2d\uff0c\u54ea\u8fd8\u6709\u5fc3\u795e\u63d0\u9632\u5c0f\u5077\uff0c\u59ec\u6653 \u98ce\u8eab\u624b\u5982\u7535\uff0c\u4e1c\u6478\u4e00\u628a\uff0c\u897f\u638f\u4e00\u8bb0\uff0c\u5f53\u771f\u662f\u624b\u5230\u62ff\u6765\uff0c\u6709\u5982\u63a2\u56ca\u53d6\u7269\u3002\u4e0d\u8fc7\u53ea\u6709\u5b9d\u8c61\u6cd5 \u5e08\u7684\u4e1c\u897f\u4ed6\u5077\u4e0d\u5230\uff0c\u5b9d\u8c61\u602f\u5e08\u7684\u638c\u529b\u628a\u6570\u4e08\u4e4b\u5185\u90fd\u5c01\u95ed\uff0c\u4ed6\u6839\u672c\u5c31\u8e0f\u4e0d\u8fdb\u90a3\u4e2a\u8303\u56f4\u3002 \u3000\u3000\u6b63\u5728\u59ec\u6653\u98ce\u5077\u5f97\u9ad8\u5174\u3001\u5b9d\u8c61\u6cd5\u5e08\u4e0e\u6c5f\u6d77\u5929\u540c\u611f\u7126\u8e81\u4e4b\u65f6\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u8fdc\u8fdc\u4f20 \u6765\uff01 \u3000\u3000\u5578\u58f0\u5b9b\u5982\u7985\u9f99\u592d\u77eb\uff0c\u5929\u5916\u98de\u6765\uff0c\u521d\u8d77\u4e4b\u65f6\uff0c\u8fd8\u5728\u5f88\u8fdc\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u5c31\u4f3c\u5230\u4e86\u8eab\u8fb9\uff0c \u9707\u5f97\u4eba\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\u3002\u5b9d\u8c61\u6cd5\u5e08\u5fc3\u5934\u4e00\u51db\uff0c\u6b63\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u662f\u4f55\u4eba\uff0c\u6709\u6b64\u529f\u529b\uff1f\u201d\u53ea\u89c1 \u59ec\u6653\u98ce\u559c\u6781\u5fd8\u5f62\uff0c\u624b\u821e\u8db3\u8e48\u8e48\uff0c\u5df2\u5728\u5927\u58f0\u53eb\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u6765\u5566\uff01\u201d \u3000\u3000\u4f17\u4eba\u88ab\u8fd9\u5578\u58f0\u6240\u6151\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u5341\u4e4b\u516b\u4e5d\uff0c\u90fd\u662f\u4e0d\u7ea6\u800c\u540c\u7684\u6682\u65f6\u505c\u4e0b\u624b\u6765\uff0c\u76ee\u5149\u6ce8 \u89c6\u7740\u95e8\u53e3\u3002\u53ea\u89c1\u4e24\u4e2a\u4e2d\u5e74\u6c49\u5b50\uff0c\u8f7b\u88d8\u7f13\u5e26\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u5728\u5200\u5149\u5251\u5f71\u4e4b\u4e2d\u3002\u6c14\u5ea6\u4ece\u5bb9\u5730\u8d70 \u4e86\u8fdb\u6765\u3002\u8d70\u5728\u524d\u9762\u7684\u90a3\u4eba\uff0c\u679c\u7136\u662f\u91d1\u4e16\u9057\u3002 \u3000\u3000\u91d1\u4e16\u9057\u8fd9\u7a81\u7136\u51fa\u73b0\uff0c\u5b9d\u8c61\u6cd5\u5e08\u7b49\u4eba\u5458\u88ab\u4ed6\u5578\u58f0\u6240\u6151\uff0c\u8fd8\u4e0d\u600e\u4e48\uff0c\u6587\u5ef7\u58c1\u4e0e\u4ed6\u6709\u4ec7\uff0c \u8fd9\u4e00\u60ca\u5374\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u7684\u5f92\u5f1f\u6211\u5c1a\u65e6\u6253\u4e0d\u8d62\uff0c\u73b0\u5728\u542c\u8fd9\u5578\u58f0\uff0c\u4ed6\u7684\u6b66\u529f \u4f55\u6b62\u6bd4\u5f92\u5f1f\u9ad8\u51fa\u5341\u500d\uff0c\u771f\u60f3\u4e0d\u5230\u4ec5\u4ec5\u662f\u51e0\u5e74\u529f\u592b\uff0c\u4ed6\u7684\u529f\u529b\u7adf\u5df2\u7cbe\u8fdb\u5982\u65af\uff01\u4eca\u751f\u6211\u8981\u60f3 \u80dc\u8fc7\u4ed6\uff0c\u53ea\u6015\u662f\u7edd\u7136\u65e0\u671b\u4e86\u3002\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u5fc3\u5ff5\u5168\u7070\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u865a\u6643\u4e00\u638c\uff0c\u6446\u8131\u4e86\u5510 \u52aa\u73e0\u7a46\uff0c\u4ece\u53e6\u4e00\u6247\u89d2\u95e8\u4fbf\u9003\u4e86\u51fa\u53bb\u3002\u4ed6\u751f\u6015\u91d1\u4e16\u9057\u62ff\u4ed6\u62a5\u4ec7\uff0c\u8dd1\u5f97\u98de\u5feb\uff0c\u8fde\u5b88\u95e8\u7684\u6b66\u58eb\uff0c \u4e5f\u7ed9\u4ed6\u649e\u7ffb\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5374\u54ea\u6709\u95f2\u5fc3\u53bb\u7406\u4f1a\u4ed6\uff0c\u8e0f\u8fdb\u573a\u4e2d\uff0c\u4fbf\u5373\u7b11\u9053\uff1a\u201c\u4ee5\u6b66\u4f1a\u53cb\uff0c\u53ea\u5b9c\u70b9\u5230\u5373\u6b62\u3002 \u8bf8\u4f4d\u4e5f\u8be5\u6b47\u6b47\u5566\u3002\u201d \u3000\u3000\u90a3\u4e09\u4e2a\u5a46\u7f57\u95e8\u9ad8\u624b\u4e0d\u8bc6\u91d1\u4e16\u9057\u662f\u8c01\uff0c\u540c\u58f0\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u81ea\u4ee5\u4e3a\u662f\u5929\u4e0b\u7b2c\u4e00\u4e48\uff1f \u51ed\u4ec0\u4e48\u6211\u4eec\u8981\u542c\u4f60\u7684\u5429\u5490\uff1f\u597d\uff0c\u9601\u4e0b\u65e2\u7136\u662f\u5f3a\u8981\u51fa\u5934\uff0c\u6211\u4eec\u5c31\u5148\u5411\u4f60\u52df\u5316\uff0c\u4e0d\u8981\u4f60\u7684\u94b1 \u8d22\uff0c\u53ea\u8bf7\u4f60\u65bd\u820d\u4e00\u70b9\u513f\u672c\u9886\u3002\u201d\u4e09\u4eba\u5fc3\u601d\u5982\u4e00\uff0c\u500f\u7136\u95f4\u4e09\u4e2a\u91d1\u94b5\u540c\u65f6\u98de\u51fa\u3002\u8fd9\u4e09\u4eba\u7684\u5185 \u529f\u51dd\u6210\u4e00\u4f53\uff0c\u4e09\u4e2a\u91d1\u94b5\u98de\u51fa\uff0c\u9690\u9690\u5e26\u7740\u98ce\u96f7\u4e4b\u58f0\uff0c\u53c8\u4f3c\u5728\u98ce\u96f7\u4e2d\u5377\u8d77\u4e00\u5927\u7247\u91d1\u971e\u5411\u91d1\u4e16 \u9057\u5f53\u5934\u538b\u4e0b\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5408\u4ec0\u5ff5\u4e86\u4e00\u58f0\u201c\u963f\u5f25\u9640\u6cb8\u201d\u8bf4\u9053\uff1a\u201c\u94b1\u8d22\u3001\u672c\u9886\uff0c\u6211\u662f\u4e24\u8005\u7686\u65e0\uff0c\u53ea\u597d\u53cd\u8fc7 \u6765\u5411\u4f60\u52df\u5316\u4e86\u3002\u201d\u5f85\u90a3\u4e00\u5927\u7247\u91d1\u971e\u98de\u8fd1\uff0c\u8fd9\u624d\u4f38\u624b\u4e00\u62db\uff0c\u8bf4\u4e5f\u5947\u602a\uff0c\u90a3\u4e00\u7247\u91d1\u971e\u6765\u52bf\u4f55 \u7b49\u731b\u70c8\uff0c\u88ab\u4ed6\u8fd9\u4e48\u8f7b\u8f7b\u4e00\u62db\uff0c\u767b\u65f6\u971e\u5149\u6536\u655b\uff0c\u90a3\u4e09\u53ea\u91d1\u94b5\u672c\u6765\u662f\u6df7\u6210\u4e86\u4e00\u7247\u91d1\u971e\u4e86\u7684\uff0c \u73b0\u5728\u4e5f\u91cd\u65b0\u663e\u73b0\u51fa\u6765\uff0c\u91d1\u4e16\u9057\u518d\u4e00\u638c\u62cd\u51fa\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e09\u53ea\u91d1\u94b5\u4f3c\u4e4e\u8fd8\u503c\u5f97\u51e0\u4e2a\u94b1\uff0c\u59ec \u5927\u54e5\uff0c\u6211\u6ca1\u6709\u5e26\u793c\u7269\u7ed9\u4f60\uff0c\u8fd9\u5c31\u501f\u82b1\u732e\u4f5b\uff0c\u9001\u7ed9\u4f60\u5427\u3002\u201d \u3000\u3000\u90a3\u4e09\u4e2a\u5a46\u7f57\u95e8\u9ad8\u6bdb\u6240\u53d1\u52b2\u9053\uff0c\u5df2\u88ab\u91d1\u4e16\u9057\u8fd9\u4e00\u62db\u4e00\u62cd\u5168\u90fd\u5316\u89e3\uff0c\u53ea\u89c1\u8fd9\u4e09\u53ea\u4e3b\u94b5\u6539 \u4e86\u4e00\u4e2a\u65b9\u5411\uff0c\u7f13\u7f13\u843d\u4e0b\uff0c\u90fd\u7ed9\u59ec\u6653\u98ce\u63a5\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4f60\u9001\u7684\u793c\u7269\u4e0d\u5408\u6211\u7528\u3002\u6211\u65e2\u4e0d\u60f3\u505a\u548c\u5c1a\uff0c\u5e26\u4e86\u8fd9\u4e09\u53ea\u91d1\u94b5 \u8d70\uff0c\u53c8\u5acc\u592a\u8fc7\u7d2f\u8d58\uff0c\u6211\u610f\u6b32\u8f6c\u9001\u4e0e\u4eba\uff0c\u4f60\u4e0d\u53cd\u5bf9\u4e48\uff1f\u201d\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u6211\u9001\u7ed9\u4f60\u5c31\u662f\u4f60 \u7684\u4e86\uff0c\u5982\u4f55\u5904\u7f6e\uff0c\u968f\u4f60\u7684\u4fbf\u3002\u201d\u59ec\u6653\u98ce\u5c06\u91d1\u94b5\u53e0\u5728\u4e00\u8d77\uff0c\u968f\u624b\u5c31\u9012\u7ed9\u90a3\u4e2a\u8ddf\u5728\u4ed6\u80cc\u540e\u7684 \u5370\u5ea6\u795e\u5077\u3002 \u3000\u3000\u59ec\u6653\u98ce\u7b11\u9053\uff1a\u201c\u4f60\u4eca\u65e5\u5c1a\u672a\u53d1\u5e02\uff0c\u8fd9\u51e0\u65a4\u91d1\u5b50\uff0c\u9001\u7ed9\u4f60\u4f7f\u7528\u5427\u3002\u201d\u90a3\u5370\u5ea6\u795e\u5077\u5982\u4f55 \u6562\u8981\u8fd9\u4e09\u53ea\u91d1\u94b5\uff0c\u8d76\u5fd9\u53bb\u4ea4\u8fd8\u90a3\u4e09\u4e2a\u5a46\u7f57\u95e8\u9ad8\u624b\uff0c\u53ef\u601c\u90a3\u4e09\u4e2a\u9ad8\u624b\u5df2\u662f\u5413\u5f97\u5446\u4e86\uff0c\u832b\u7136 \u5730\u63a5\u8fc7\u91d1\u94b5\uff0c\u4e00\u53e5\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5728\u5b9d\u8c61\u6cd5\u5e08\u7684\u638c\u529b\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u8131\u8eab\u4e0d\u5f97\uff0c\u5b9d\u8c61\u6cd5\u5e08\u7684\u201c\u9f99\u8c61\u529f\u201d\u6b63\u81ea\u53d1\u6325\u5f97 \u6dcb\u6f13\u5c3d\u81f4\uff0c\u4e5f\u662f\u6b32\u7f62\u4e0d\u80fd\u3002\u4e0e\u91d1\u4e16\u9057\u540c\u6765\u7684\u90a3\u4eba\u4e0a\u524d\u8bf4\u9053\uff1a\u201c\u5b9d\u8c61\u6cd5\u5e08\uff0c\u4ee4\u5e08\u9f99\u53f6\u4e0a\u4eba \u6709\u547d\uff0c\u547d\u4f60\u901f\u901f\u56de\u53bb\uff01\u201d\u8fd9\u4eba\u6b63\u662f\u66fe\u7ecf\u5230\u8fc7\u5370\u5ea6\u90a3\u70c2\u9640\u5bfa\uff0c\u53c2\u89c1\u8fc7\u9f99\u53f6\u4e0a\u4eba\u7684\u9f99\u7075\u77eb\u3002 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u4fe8\u82e5\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\u3002\u539f\u6765\u4ed6\u610f\u6b32\u51fb\u8d25\u4e86\u6c5f\u6d77\u5929\uff0c\u518d\u4e0e\u91d1\u4e16\u9057\u51b3\u6218\uff0c \u91d1\u4e16\u9057\u4e00\u8e0f\u5165\u4f1a\u573a\uff0c\u4ed6\u4fbf\u52a0\u7d27\u5168\u529b\u65bd\u4e3a\uff0c\u8fd9\u65f6\u6b63\u5230\u4e86\u6700\u7d27\u8981\u7684\u5173\u5934\uff0c\u4e1d\u6beb\u4e5f\u5206\u795e\u4e0d\u5f97\uff0c \u786e\u5b9e\u662f\u542c\u4e0d\u89c1\u9f99\u7075\u77eb\u7684\u8bf4\u8bdd\u3002 \u3000\u3000\u9f99\u7075\u77eb\u6b64\u65f6\u5df2\u8e0f\u8fdb\u5b9d\u8c61\u6cd5\u5e08\u638c\u529b\u7684\u8303\u56f4\uff0c\u5b9d\u8c61\u6cd5\u5e08\u76ee\u4e0d\u65c1\u89c6\uff0c\u53ea\u611f\u89c9\u5230\u6709\u4eba\u8d70\u6765\uff0c \u770b\u4e5f\u4e0d\u770b\uff0c\u7259\u6839\u4e00\u54ac\uff0c\u201c\u5c24\u8c61\u529f\u201d\u4fbf\u4e00\u53d1\u65e0\u9057\uff0c\u767b\u65f6\u628a\u9f99\u7075\u77eb\u4e5f\u5377\u8fdb\u4e86\u638c\u529b\u7684\u4e2d\u5fc3\u3002 \u3000\u3000\u9f99\u7075\u77eb\u4e0d\u77e5\u5c31\u91cc\uff0c\u53ea\u9053\u5b9d\u8c61\u6cd5\u5e08\u8f7b\u89c6\u4e8e\u4ed6\uff0c\u4e00\u6012\u4e4b\u4e0b\uff0c\u4fbf\u8981\u51fa\u624b\u3002\u4f46\u4ed6\u7684\u5e74\u7eaa\u867d\u4e0d \u5f88\u8001\uff0c\u5374\u662f\u4e0e\u5510\u6653\u6f9c\u540c\u4e00\u8f88\u4efd\u7684\u4eba\uff0c\u6c5f\u6d77\u5929\u5c1a\u672a\u9000\u4e0b\uff0c\u4ed6\u600e\u80fd\u81ea\u5931\u8eab\u4efd\uff0c\u4ee5\u4e8c\u654c\u4e00\uff1f \u3000\u3000\u9f99\u7075\u77eb\u529f\u592b\u6df1\u539a\uff0c\u4f46\u5728\u4e24\u5927\u9ad8\u624b\u5185\u529b\u6fc0\u6597\u7684\u4e2d\u5fc3\uff0c\u867d\u7136\u4e0d\u81f4\u53d7\u4f24\uff0c\u4e5f\u611f\u5230\u547c\u5438\u56f0\u96be\u3002 \u5fc3\u5934\u4e0d\u7981\u4e00\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u5b9d\u8c61\u6cd5\u5e08\u662f\u9f99\u53f6\u4e0a\u4eba\u7684\u9996\u5ea7\u5f1f\u5b50\uff0c\u53f3\u6b64\u529f\u529b\u5c1a\u4e0d\u8db3\u4e3a\u5947\uff1b\u91d1\u4e16 \u9057\u8fd9\u4e2a\u5f92\u5f1f\u5c45\u7136\u4e5f\u6709\u5982\u6b64\u529f\u529b\uff01\u201d \u3000\u3000\u8fd9\u65f6\u4ed6\u5df2\u6e10\u6e10\u770b\u51fa\u53cc\u65b9\u6b32\u7f62\u4e0d\u80fd\u7684\u5f62\u8c61\uff0c\u4ed6\u6700\u521d\u672c\u6765\u4e5f\u52a8\u8fc7\u5ff5\u5934\uff0c\u60f3\u628a\u8fd9\u4e24\u4eba\u5206\u5f00 \u7684\uff0c\u4f46\u73b0\u5728\u4e00\u5230\u4e86\u8fd9\u4e24\u4eba\u5185\u529b\u6fc0\u6597\u7684\u4e2d\u5fc3\uff0c\u8fd9\u624d\u77e5\u9053\u5b9d\u8c61\u6cd5\u5e08\u7684\u529f\u529b\u56fa\u7136\u662f\u8fdc\u80dc\u4e8e\u4ed6\uff0c \u5373\u6c5f\u6d77\u5929\u7684\u529f\u529b\uff0c\u4e5f\u4e0d\u5728\u4ed6\u4e4b\u4e0b\u3002\u4ed6\u7ad9\u5728\u8fd9\u4e2d\u5fc3\u5730\u70b9\uff0c\u8fde\u652f\u6301\u90fd\u611f\u5230\u6709\u70b9\u56f0\u96be\uff0c\u66f4\u9051\u8bba \u8981\u62c6\u5f00\u8fd9\u4e24\u5927\u9ad8\u624b\u7684\u6fc0\u6597\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5fae\u7b11\u9053\uff1a\u201c\u9f99\u5148\u751f\u8bf7\u6682\u5f85\u7247\u523b\uff0c\u5f85\u6211\u548c\u4ed6\u8bf4\u53bb\u3002\u201d\u5728\u638c\u98ce\u6fc0\u8361\u4e4b\u4e2d\uff0c\u8863\u8882\u98d8 \u98d8\uff0c\u4ece\u5bb9\u4e3e\u6b65\uff0c\u5230\u4e86\u5b9d\u8c61\u6cd5\u5e08\u4e0e\u6c5f\u6d77\u5929\u7684\u8eab\u8fb9\uff0c\u957f\u8896\u4e00\u6325\uff0c\u4fbf\u961f\u4e24\u4eba\u4e4b\u95f4\u201c\u5207\u201d\u4e0b\u3002\u4ed6 \u8fd9\u8863\u8896\u4e00\u6325\uff0c\u751f\u51fa\u7684\u4e00\u80a1\u6697\u529b\uff0c\u7adf\u4f3c\u4e00\u67c4\u65e0\u5f62\u7684\u5b9d\u5251\uff0c\u767b\u65f6\u628a\u53cc\u65b9\u7684\u529b\u9053\u5f53\u4e2d\u622a\u65ad\u3002\u6c5f \u6d77\u5929\u89c1\u5e08\u7236\u6765\u5230\uff0c\u5f53\u7136\u7acb\u5373\u9000\u4e0b\uff0c\u4f46\u4ed6\u8eab\u4e0a\u6240\u53d7\u7684\u529b\u9053\u5c1a\u672a\u6d88\u89e3\uff0c\u4ecd\u662f\u4e0d\u7531\u81ea\u5df1\u7684\u5728\u5730 \u4e0a\u6253\u4e86\u5341\u51e0\u4e2a\u5708\u5708\u3002 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u7684\u201c\u9f99\u8c61\u529f\u2019\u6b63\u81ea\u4e00\u53d1\u65e0\u9057\uff0c\u54ea\u80fd\u715e\u4f4f\uff0c\u53ea\u542c\u5f97\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u78b0\u4e2a\u6b63\u7740\uff0c \u5168\u90e8\u7684\u529b\u91cf\uff0c\u767b\u65f6\u90fd\u6c40\u5230\u91d1\u4e16\u9057\u8eab\u4e0a\u3002 \u3000\u3000\u91d1\u4e16\u9057\u8f7b\u8f7b\u5728\u4ed6\u5eb8\u5934\u4e00\u62cd\uff0c\u7b11\u9053\uff1a\u201c\u5b9d\u8c61\u6cd5\u5e08\uff0c\u4f60\u4e5f\u8be5\u6b47\u6b47\u5566\uff01\u201d\u5b9d\u8c61\u6cd5\u5e08\u5fc3\u5934\u5927 \u9707\uff0c\u4e00\u7247\u832b\u7136\u3002 \u3000\u3000\u539f\u6765\u5b9d\u8c61\u6cd5\u5e08\u7684\u53cc\u638c\u4e00\u78b0\u7740\u91d1\u4e16\u9057\u7684\u8eab\u4f53\uff0c\u7adf\u4f3c\u80f6\u7740\u4e86\u4f3c\u7684\uff0c\u6536\u4e0d\u56de\u6765\u3002\u201c\u9f99\u8c61\u529f\u201d \u662f\u4f5b\u95e8\u65e0\u4e0a\u795e\u529f\uff0c\u4f55\u7b49\u5389\u5bb3\uff1f\u8fd9\u65f6\u4ed6\u53c8\u6b63\u5728\u5168\u529b\u53d1\u6325\uff0c\u52b2\u9053\u4e4b\u5f3a\uff0c\u52bf\u5982\u6392\u5c71\u5012\u6d77\uff0c\u6309\u8bf4 \u5bf9\u65b9\u7eb5\u662f\u94c1\u6d9b\u7684\u8eab\u5b50\uff0c\u4e5f\u4f1a\u5728\u4ed6\u521a\u731b\u65e0\u4f26\u7684\u638c\u529b\u4e4b\u4e0b\u53d8\u4f5c\u4e00\u56e2\u70c2\u6ce5\uff0c\u53ef\u662f\u8bf4\u4e5f\u5947\u602a\uff0c\u4ed6 \u7684\u5185\u529b\u6e90\u6e90\u6d8c\u51fa\uff0c\u4f46\u5374\u4f3c\u6ce5\u725b\u5165\u6d77\uff0c\u4e00\u53bb\u65e0\u8e2a\uff01\u800c\u4e14\u8fd8\u4e0d\u6b62\u6b64\uff0c\u8fd8\u7adf\u5982\u78c1\u77f3\u5438\u94c1\uff0c\u600e\u4e5f \u6446\u8131\u4e0d\u5f00\uff0c\u4ed6\u7684\u5185\u529b\u591a\u53d1\u51fa\u4e00\u5206\uff0c\u5c31\u88ab\u5bf9\u65b9\u591a\u5438\u6536\u4e86\u4e00\u5206\uff0c\u6c42\u80dc\u4e0d\u5f97\uff0c\u6b32\u7f62\u4e0d\u80fd\u3002 \u3000\u3000\u539f\u6765\u201c\u9f99\u8c61\u529f\u201d\u867d\u662f\u4f5b\u95e8\u65e0\u4e0a\u795e\u529f\uff0c\u4f46\u5b9d\u8c61\u6cd5\u5e08\u5c1a\u672a\u7ec3\u5230\u81f3\u9ad8\u65e0\u4e0a\u5883\u754c\uff0c\u5982\u4eca\u78b0\u4e0a \u4e86\u529f\u529b\u8fdc\u80dc\u4e8e\u4ed6\u7684\u91d1\u4e16\u9057\uff0c\u5f53\u7136\u5c31\u4e1d\u6beb\u4e5f\u635f\u4e0d\u4e86\u5bf9\u65b9\uff0c\u800c\u4e14\u8fd8\u88ab\u91d1\u4e16\u9057\u5999\u8fd0\u7384\u529f\uff0c\u4f24\u4ed6 \u7684\u5185\u529b\u6e90\u6e90\u5438\u53bb\u3002 \u3000\u3000\u4f46\u4e0e\u6b64\u540c\u65f6\uff0c\u5b9d\u8c61\u6cd5\u5e08\u4e5f\u611f\u5230\u4e00\u80a1\u70ed\u6d41\uff0c\u4ece\u201c\u80a9\u4e95\u7a74\u201d\u900f\u8fdb\u4f53\u5185\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u5c31\u6d41 \u8fc7\u4e86\u4ed6\u7684\u5947\u7ecf\u516b\u8109\uff0c\u76f4\u6ce8\u4e39\u7530\uff0c\u5728\u5185\u529b\u635f\u5931\u7684\u540c\u65f6\uff0c\u7adf\u4e5f\u611f\u5230\u5341\u5206\u8212\u670d\u3002 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u662f\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u8fd9\u65f6\u4e5f\u81ea\u660e\u767d\u4e86\u91d1\u4e16\u9057\u7684\u7528\u610f\u3002 \u3000\u3000\u539f\u6765\u91d1\u4e16\u9057\u662f\u4e00\u9762\u8981\u6bc1\u53bb\u4ed6\u7684\u201c\u9f99\u8c61\u529f\u201d\uff0c\u53e6\u4e00\u9762\u5374\u53c8\u4ee5\u672c\u8eab\u771f\u6c14\uff0c\u52a9\u4ed6\u7597\u6cbb\u5185\u4f24\uff0c \u5e76\u52a9\u4ed6\u5b88\u62a4\u4e39\u7530\uff0c\u4ee4\u4ed6\u7684\u5143\u6c14\u5f97\u4ee5\u51dd\u805a\u4e0d\u6563\u3002\u672c\u6765\u4ed6\u548c\u6c5f\u6d77\u5929\u7ecf\u8fc7\u4e86\u8fd9\u6b21\u6076\u6597\u4e4b\u540e\uff0c\u6700 \u5c11\u4e5f\u8981\u5927\u75c5\u4e00\u573a\uff0c\u51cf\u5bff\u5341\u5e74\uff0c\u5982\u4eca\u5f97\u91d1\u4e16\u9057\u53ca\u65f6\u76f8\u52a9\uff0c\u201c\u9f99\u8c61\u529f\u201d\u867d\u6bc1\uff0c\u8fd9\u4e00\u573a\u707e\u96be\u5374 \u662f\u53ef\u4ee5\u8eb2\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u4e00\u58f0\u957f\u7b11\uff0c\u624b\u638c\u4ece\u4ed6\u80a9\u5934\u7f13\u7f13\u79fb\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u5b9d\u8c61\u6cd5\u5e08\uff0c\u54b1\u4eec\u53ef\u4ee5\u8c08\u8c08\u4e86\u5427\uff1f\u201d \u8fd9\u65f6\u5b9d\u8c61\u6cd5\u5e08\u7684\u53cc\u638c\u4e5f\u624d\u80fd\u591f\u6536\u56de\uff0c\u4ed6\u8eb2\u8fc7\u4e00\u573a\u707e\u96be\uff0c\u4f46\u5374\u6bc1\u4e86\u6570\u5341\u5e74\u82e6\u7ec3\u800c\u6210\u7684\u201c\u9f99 \u8c61\u529f\u201d\uff0c\u771f\u4e0d\u77e5\u662f\u8be5\u611f\u6fc0\u91d1\u4e16\u9057\u8fd8\u662f\u8981\u75db\u6068\u91d1\u4e16\u9057\u3002 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u54ed\u7b11\u4e0d\u5f97\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u4e16\u9057\uff0c\u6211\u7684\u6570\u5341\u5e74\u529f\u529b\u5df2\u88ab\u4f60\u6bc1\u4e8e\u4e00\u65e6\uff0c\u8fd8\u6709\u4ec0\u4e48 \u597d\u8c08\uff1f\u5982\u4eca\u6211\u662f\u7827\u4e0a\u4e4b\u8089\uff0c\u53ea\u6709\u542c\u4f60\u5bb0\u5272\u4e86\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u6cd5\u5e08\u5b66\u4f5b\u591a\u5e74\uff0c\u5c1a\u81ea\u4e0d\u80fd\u65a9\u65e0\u660e\u3001\u65ad\u6267\u8457\u4e48\uff1f \u3000\u3000\u6211\u6bc1\u4e86\u4f60\u7684\u9f99\u8c61\u529f\uff0c\u5c82\u662f\u4e3a\u4e86\u51cc\u8fb1\u4f60\u5bb0\u5272\u4f60\uff1f\u6070\u6070\u76f8\u53cd\uff0c\u6211\u662f\u6765\u52a9\u4f60\u5f97\u6210\u6b63\u679c\u7684\uff0c \u4f60\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u4e0d\u6562\u53d1\u6012\uff0c\u800c\u4e14\u4ed6\u770b\u91d1\u4e16\u9057\u8bf4\u8bdd\u4e5f\u9887\u8bda\u6073\uff0c\u4e0d\u7981\u95ee\u9053\uff1a\u201c\u8bf7\u6055\u4e0b\u611a\uff0c\u96be\u660e \u6df1\u610f\u3002\u5c1a\u8bf7\u518d\u6307\u70b9\u8ff7\u6d25\u3002\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u9f99\u5148\u751f\uff0c\u4f60\u8bf4\u7ed9\u4ed6\u542c\u3002\u201d \u3000\u3000\u5f85\u9f99\u7075\u77eb\u8bf4\u51fa\u4e86\u9f99\u53f6\u4e0a\u4eba\u62db\u4ed6\u56de\u53bb\u7684\u6cd5\u8c15\u4e4b\u540e\uff0c\u91d1\u4e16\u9057\u624d\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u9f99\u8c61 \u529f\u82e5\u8fd8\u672a\u6bc1\uff0c\u53ea\u6015\u4f60\u8fd8\u8981\u8d2a\u604b\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u5e08\u4e4b\u4f4d\u5427\uff1f\u5373\u7b97\u5728\u9a6c\u8428\u513f\u56fd\u7ad9\u4e0d\u4f4f\u811a\uff0c\u53ea\u6015 \u4f60\u4e5f\u8981\u5230\u522b\u5904\u53bb\u5174\u98ce\u4f5c\u6d6a\u5427\uff1f\u4e8e\u4eca\u5df2\u6bc1\u4e86\u9f99\u8c61\u529f\uff0c\u90a3\u5c31\u53ea\u597d\u65ad\u4e86\u65e0\u660e\u4e4b\u5ff5\uff0c\u91cd\u56de\u5e08\u95e8\uff0c \u7688\u4f9d\u4f5b\u6cd5\u4e86\u3002\u4ee4\u5e08\u662f\u5f53\u4e16\u7b2c\u4e00\u9ad8\u50e7\uff0c\u4ed6\u65e5\u4f60\u53c2\u900f\u4e86\u4e0a\u4e58\u4f5b\u6cd5\uff0c\u5f97\u6210\u6b63\u679c\uff0c\u8fd9\u5c82\u4e0d\u6bd4\u4f60\u5f53 \u4ec0\u4e48\u52b3\u4ec0\u5b50\u7684\u56fd\u5e08\u8981\u5f3a\u5f97\u591a\uff1f \u3000\u3000\u597d\uff0c\u7978\u798f\u8f6c\u79fb\uff0c\u5c31\u5168\u5728\u4f60\u5fc3\u5934\u4e00\u5ff5\u4e86\uff0c\u4f60\u660e\u767d\u4e86\u4e48\uff1f\u201d \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u5fc3\u7070\u610f\u51b7\uff0c\u4e8b\u5df2\u5982\u65af\uff0c\u4ed6\u9664\u4e86\u91cd\u8fd4\u5e08\u95e8\uff0c\u7688\u4f9d\u4f5b\u6cd5\u4e4b\u5916\uff0c\u4e5f\u5b9e\u5728\u6ca1\u6709\u7b2c\u4e8c \u6761\u8def\u53ef\u8d70\u4e86\u3002\u5f53\u4e0b\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u91d1\u5927\u4fa0\u6307\u70b9\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u8d2b\u50e7\u51b3\u4e0d\u518d\u5c65\u7ea2\u5c18\uff01\u201d \u3000\u3000\u521a\u624d\u5728\u91d1\u4e16\u9057\u5230\u6765\u7684\u65f6\u5019\uff0c\u5df2\u6709\u5341\u4e4b\u4e03\u516b\u7f62\u624b\u4e0d\u6597\uff0c\u81f3\u6b64\uff0c\u5b9d\u8c61\u6cd5\u5e08\u4ea6\u5df2\u8ba4\u8f93\uff0c\u4e00 \u4e9b\u96f6\u661f\u7684\u6218\u6597\uff0c\u4ea6\u5c31\u968f\u4e4b\u505c\u6b62\u4e86\u3002\u5b9d\u8c61\u6cd5\u5e08\u53f9\u4e86\u53e3\u6c14\uff0c\u5411\u4ed6\u90a3\u56de\u4e2a\u62a4\u6cd5\u5f1f\u5b50\u62db\u624b\u9053\uff1a \u201c\u4f60\u4eec\u4e5f\u90fd\u968f\u6211\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u5374\u539f\u6765\u662f\u5510\u52aa\u73e0\u7a46\u8d70\u4e0a\u524d\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u5978\u738b\u76d6\u6e29\u4f55\u5728\uff1f \u4f60\u628a\u4ed6\u4ea4\u51fa\u6765\u518d\u8d70\uff01\u201d\u5b9d\u8c61\u6cd5\u5e08\u53cc\u624b\u4e00\u644a\uff0c\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u8d2b\u50e7\u73b0\u5728\u662f\u81ea\u8eab\u96be\u4fdd\uff0c\u600e\u8fd8\u80fd \u5e87\u62a4\u76d6\u6e29\uff1f\u4ed6\u59d4\u5b9e\u6ca1\u6709\u5230\u8fc7\u672c\u5bfa\uff0c\u53eb\u6211\u5982\u4f55\u4ea4\u5f97\u51fa\u6765\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u534a\u4fe1\u534a\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u6d3b\u5f53\u771f\uff1f\u201d\u5b9d\u8c61\u6cd5\u5e08\u607c\u9053\uff1a \u3000\u3000\u201c\u6211\u6b66\u529f\u867d\u7136\u4e0d\u6d4e\uff0c\u5374\u4e5f\u8fd8\u8981\u987e\u4f4f\u4f5b\u95e8\u5f1f\u5b50\u7684\u8eab\u4efd\uff0c\u5c82\u662f\u80af\u6253\u901b\u8bed\u7684\u4eba\uff1f\u5c0f\u738b\u7237\u4f60 \u82e5\u4e0d\u4fe1\uff0c\u90a3\u5c31\u968f\u4f60\u5904\u7f6e\u5427\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6cd5\u5e08\u8bf4\u90a3\u5978\u738b\u6ca1\u6709\u6765\u8fc7\uff0c\u90a3\u5c31\u4e00\u5b9a\u662f\u6ca1\u6709\u6765\u8fc7\u3002 \u3000\u3000\u5f92\u513f\uff0c\u4f60\u4e0d\u53ef\u5bf9\u6cd5\u5e08\u65e0\u793c\u3002\u201d\u5510\u52aa\u73e0\u7a46\u542c\u4e86\u5e08\u7236\u7684\u5429\u5490\uff0c\u4e0d\u6562\u4e0d\u4f9d\uff0c\u53ea\u597d\u5411\u5b9d\u8c61\u6cd5 \u5e08\u8d54\u4e86\u4e0d\u662f\u3002\u8fd9\u65f6\u4ed6\u4e5f\u6709\u51e0\u5206\u76f8\u4fe1\u90a3\u5978\u738b\u4e0d\u5728\u6b64\u5730\u3002\u5fc3\u4e2d\u6697\u6697\u7eb3\u7f55\uff0c\u60f3\u9053\uff1a\u201c\u7687\u5bab\u6211\u90fd \u5df2\u641c\u67e5\u8fc7\u4e86\uff0c\u4ed6\u4e0d\u5728\u6b64\u5730\uff0c\u5374\u53c8\u8eb2\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6216\u8005\u5bab\u4e2d\u5c1a\u6709\u4ec0\u4e48\u79d8\u5bc6\u5730\u9053\uff0c\u54b1\u4eec\u6ca1\u6709\u641c\u67e5\u5230\u7684\uff1f\u60f3\u6b64\u9645\u5927\u54e5\u4e5f\u5f53\u5df2 \u56de\u5230\u5bab\u4e2d\u4e86\uff0c\u4e0d\u5982\u54b1\u4eec\u8d81\u65e9\u56de\u53bb\uff0c\u4f1a\u5408\u4e86\u5927\u54e5\uff0c\u518d\u67e5\u4e00\u67e5\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u662f\uff0c \u4e0d\u8fc7\u8fd9\u91cc\u4e5f\u8fd8\u6709\u4e00\u4e9b\u5584\u540e\u4e4b\u4e8b\uff0c\u9700\u8981\u5b89\u6392\u4e00\u4e0b\u3002\u201d\u5f53\u4e0b\u5c31\u51fa\u4e91\u5524\u90a3\u4e2a\u7edf\u5175\u5b98\u8fdb\u6765\uff0c\u53eb\u4ed6 \u62e8\u51fa\u4e00\u5343\u540d\u58eb\u5175\uff0c\u7531\u4ed6\u7387\u9886\uff0c\u63a5\u7ba1\u91d1\u9e70\u5b98\uff0c\u540c\u65f6\u53c8\u4e0b\u4ee4\u6536\u7f34\u672c\u56fd\u50e7\u4fa3\u7684\u6b66\u5668\uff0c\u5148\u770b\u7ba1\u8d77 \u6765\uff0c\u4ee5\u540e\u518d\u4f5c\u5b89\u6392\u3002\u5176\u4ed6\u524d\u6765\u8d74\u4f1a\u8bf8\u4eba\uff0c\u5219\u4efb\u7531\u4ed6\u4eec\u79bb\u5f00\u3002 \u3000\u3000\u5b9d\u8c61\u6cd5\u5e08\u548c\u4ed6\u7684\u5f1f\u5b50\u4e00\u8d70\uff0c\u4ed6\u6240\u9080\u8bf7\u6765\u7684\u5404\u56fd\u9ad8\u624b\u4e5f\u90fd\u5782\u5934\u4e27\u6c14\uff0c\u9646\u7eed\u79bb\u5f00\u3002\u53ea\u6709 \u5c3c\u6cca\u5c14\u90a3\u7fa4\u6b66\u58eb\u4ee5\u53ca\u666f\u6708\u4e0a\u4eba\uff0c\u518d\u4e00\u6b21\u5168\u90e8\u88ab\u51b0\u5ddd\u5929\u5973\u6240\u64d2\uff0c\u8fd9\u662f\u6d89\u53ca\u5c3c\u6cca\u5c14\u7684\u5185\u4e71\u4e4b \u4e8b\uff0c\u5510\u52aa\u73e0\u7a46\u81ea\u7136\u4e0d\u4fbf\u591a\u7ba1\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u4e0a\u6765\u5411\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u65f6\u5149\u8fc7\u5f97\u771f\u5feb\uff0c\u54b1\u4eec\u6709\u5341\u591a\u5e74\u4e0d\u89c1\u4e86\u5427\uff1f\u4f60\u73b0\u5728\u8fd8 \u662f\u72ec\u81ea\u4e00\u4eba\uff0c\u6d6a\u8361\u6c5f\u6e56\u5417\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a \u3000\u3000\u201c\u4e0d\u9519\uff0c\u51e0\u5341\u5e74\u6765\uff0c\u4e00\u76f4\u90fd\u662f\u8fd9\u6837\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u4ece\u524d\u4f60\u662f\u4eba\u4eba\u8ba8\u538c\u7684\u6bd2\u624b\u75af\u4e10\uff0c\u73b0\u5728\u5219\u662f\u4eba\u4eba\u656c\u7231\u3001\u540d\u526f\u5176\u5b9e\u7684\u91d1 \u5927\u4fa0\u4e86\u3002\u4e00\u4e2a\u4eba\u603b\u5154\u4e0d\u4e86\u6709\u4f24\u5fc3\u4e4b\u4e8b\uff0c\u4f46\u4e5f\u603b\u4e0d\u80fd\u4f24\u5fc3\u4e00\u8f88\u5b50\u3002\u65f6\u95f4\u8fc7\u53bb\u4e86\uff0c\u4eba\u4e5f\u6539\u53d8 \u4e86\uff0c\u90a3\u4e48\u4e00\u4e2a\u4eba\u7684\u5fc3\u60c5\u4e5f\u5e94\u8be5\u53ef\u4ee5\u6539\u53d8\u4e86\u5427\uff1f\u54b1\u4eec\u662f\u8001\u670b\u53cb\u4e86\uff0c\u8bf7\u4f60\u539f\u8c05\u6211\u60f3\u5230\u4ec0\u4e48\u5c31 \u8bf4\u4ec0\u4e48\u3002\u201d\u51b0\u5ddd\u5929\u5973\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u773c\u5149\u5374\u662f\u5411\u8c37\u4e4b\u534e\u671b\u53bb\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7684\u610f\u601d\uff0c\u91d1\u4e16\u9057\u5f53\u7136\u660e\u767d\u3002 \u3000\u3000\u91d1\u4e16\u9057\u548c\u51b0\u5ddd\u5929\u5973\u76f8\u8bc6\u6700\u65e9\uff0c\u8fdc\u5728\u8c37\u4e4b\u534e\u4e0e\u5389\u80dc\u7537\u4e4b\u524d\u3002\u51b0\u5ddd\u5929\u5973\u5e74\u9f84\u6bd4\u4ed6\u7565\u5e7c\uff0c \u4f46\u4e00\u5411\u5bf9\u4ed6\u5173\u6000\uff0c\u5c31\u50cf\u59d0\u59d0\u5bf9\u5f85\u5f1f\u5f1f\u4e00\u822c\u3002\u6240\u4ee5\u4e24\u4eba\u867d\u7136\u5f88\u5c11\u89c1\u9762\uff0c\u4f46\u8fd9\u4efd\u53cb\u8c0a\uff0c\u5374\u662f \u5386\u4e45\u5f25\u575a\u3002 \u3000\u3000\u91d1\u4e16\u9057\u542c\u4e86\u51b0\u5ddd\u5929\u5973\u8fd9\u756a\u8bf4\u8bdd\uff0c\u4e0d\u7981\u559f\u7136\u53f9\u9053\uff1a\u201c\u5f53\u6211\u8fd8\u662f\u88ab\u4eba\u8ba8\u538c\u7684\u2018\u6bd2\u624b\u75af\u4e10\u2019 \u7684\u65f6\u5019\uff0c\u7b2c\u4e00\u4e2a\u5c06\u6211\u5f53\u4f5c\u670b\u53cb\u7684\u5c31\u662f\u4f60\u3002\u55ef\uff0c\u8fd9\u5df2\u7ecf\u662f\u4e8c\u5341\u591a\u5e74\u4e4b\u524d\u7684\u4e8b\u60c5\u4e86\u3002\u5c81\u6708\u4e0d \u5c45\uff0c\u4f60\u8fd8\u662f\u50cf\u4ece\u524d\u4e00\u6837\u5e74\u8f7b\uff0c\u800c\u6211\u5df2\u662f\u4e24\u9b13\u5fae\u971c\u4e86\u3002\u201d\u4ed6\u5e76\u4e0d\u76f4\u63a5\u56de\u7b54\u51b0\u5ddd\u5929\u5973\u7684\u8bf4\u8bdd\uff0c \u4f46\u8a00\u5916\u4e4b\u610f\uff0c\u5219\u662f\u8bf4\u4ed6\u5df2\u7ecf\u8001\u4e86\uff0c\u65e9\u5df2\u6ca1\u6709\u5c11\u5e74\u4eba\u7684\u5fc3\u60c5\u4e86\u3002\u5176\u5b9e\u91d1\u4e16\u9057\u53ea\u4e0d\u8fc7\u662f\u56db\u5341 \u591a\u5c81\uff0c\u6b63\u662f\u4e00\u4e2a\u5728\u5404\u65b9\u9762\u90fd\u6210\u719f\u4e86\u7684\u4e2d\u5e74\u4eba\u3002 \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9887\u60f3\u64ae\u5408\u4ed6\u4e0e\u8c37\u4e4b\u534e\u7684\u59fb\u7f18\uff0c\u4f46\u5979\u8fdc\u884c\u5728\u5373\uff0c\u65f6\u95f4\u65e0\u591a\uff0c\u800c\u4e14\u8fd9\u79cd\u7537\u5973\u4e4b \u95f4\u7684\u4e8b\u60c5\uff0c\u8bf4\u8bdd\u4e5f\u53ea\u80fd\u201c\u70b9\u5230\u4e3a\u6b62\u201d\uff0c\u603b\u4e0d\u6210\u5f53\u4f17\u505a\u5a92\u3002\u5f53\u4e0b\u5979\u542c\u5f97\u91d1\u4e16\u9057\u5982\u6b64\u56de\u7b54\uff0c \u4e5f\u53ea\u5f97\u5fae\u559f\u8bf4\u9053\uff1a\u201c\u4e16\u9057\uff0c\u4f60\u603b\u662f\u559c\u6b22\u81ea\u5df1\u6298\u78e8\u81ea\u5df1\uff0c\u4f46\u4e0d\u77e5\u4f60\u61c2\u4e0d\u61c2\u3002\u4f60\u6298\u78e8\u4e86\u81ea\u5df1 \u4e5f\u5c31\u4f1a\u6298\u78e8\u522b\u4eba\u7684\u3002\u53ef\u60dc\u6211\u5c31\u8981\u8d70\u4e86\uff0c\u8fd8\u662f\u8bf7\u4f60\u4ed4\u7ec6\u60f3\u60f3\u6211\u8fd9\u53e5\u8bf4\u8bdd\u5427\uff0c\u6211\u4e0d\u591a\u8bf4\u4e86\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u5fc3\u5934\u4e00\u98a4\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u4e4b\u534e\u59d0\u59d0\u662f\u6700\u61c2\u5f97\u6211\u7684\u5fc3\u4e8b\u7684\u4eba\uff0c\u5979\u4ec0\u4e48\u90fd\u4f1a\u8c05\u89e3 \u6211\u7684\u3002\u5509\uff0c\u96be\u9053\u6211\u6298\u78e8\u4e86\u81ea\u5df1\u5f53\u771f\u4e5f\u5c31\u6298\u78e8\u4e86\u5979\u5417\uff1f\u201d\u91d1\u4e16\u9057\u672c\u6765\u662f\u4e2a\u5bb9\u6613\u6fc0\u52a8\u7684\u4eba\uff0c \u4f46\u5982\u4eca\u5e74\u5c81\u5df2\u589e\uff0c\u5fc3\u4e2d\u7684\u6fc0\u52a8\u5374\u662f\u4e0d\u5bb9\u6613\u5728\u9762\u4e0a\u8868\u9732\u51fa\u6765\u4e86\u3002\u4ed6\u5b9a\u4e86\u5b9a\u795e\uff0c\u79fb\u8f6c\u8bdd\u9898\uff0c \u95ee\u9053\uff1a\u201c\u6842\u59d0\u59d0\uff0c\u4f60\u5c31\u8981\u8d70\u4e86\uff1f\u96be\u5f97\u8fd9\u8bb8\u591a\u8001\u670b\u53cb\u5728\u6b64\u76f8\u805a\uff0c\u4e3a\u4f55\u53ef\u4e0d\u591a\u7559\u4e24\u5929\uff1f\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6211\u8981\u8d76\u56de\u5c3c\u6cca\u5c14\u53bb\uff0c\u6211\u7684\u56fd\u5bb6\u53d1\u751f\u4e86\u5185\u4e71\uff0c\u4ed6\u4eec\u7b49\u7740\u6211\u56de\u53bb\u5462\u3002\u201d \u6c5f\u5357\u8d70\u6765\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4f60\u8fd8\u672a\u77e5\u9053\u5427\uff1f \u3000\u3000\u5510\u5927\u4fa0\u7684\u513f\u5b50\u73b0\u5728\u6b63\u5728\u5c3c\u6cca\u5c14\u505a\u7740\u4e00\u756a\u5927\u4e8b\uff0c\u548c\u8fd9\u91cc\u6240\u53d1\u751f\u7684\u4e8b\u60c5\u5dee\u4e0d\u591a\u76f8\u540c\uff0c\u4ed6 \u4eec\u628a\u66b4\u8650\u7684\u56fd\u738b\u63a8\u7ffb\u4e86\uff0c\u4f46\u5185\u4e71\u8fd8\u672a\u5e73\u606f\uff0c\u5510\u5c11\u4fa0\u662f\u65b0\u738b\u7684\u5175\u9a6c\u5143\u5e05\u3002\u513f\u5b50\u6709\u4e86\u56f0\u96be\uff0c \u505a\u7236\u6bcd\u7684\u5f53\u7136\u8981\u8d76\u53bb\u5e2e\u5fd9\u4e86\u3002\u201d\u91d1\u4e16\u9057\u8c41\u7136\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5982\u6b64\uff0c\u65f6\u95f4\u8fc7\u5f97\u771f\u5feb\uff01 \u3000\u3000\u970e\u773c\u95f4\u4f60\u7684\u5b69\u5b50\u90fd\u5f53\u8d77\u5143\u5e05\u6765\u4e86\u3002\u4ed6\u4eca\u5e74\u51e0\u5c81\u5566\uff1f\u201d\u51b0\u5ddd\u5929\u5973\u9053\uff1a \u3000\u3000\u201c\u5341\u4e5d\u5c81\u4e86\u3002\u201d\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4f60\u53ea\u77e5\u9053\u8bf4\u65f6\u95f4\u8fc7\u5f97\u5feb\uff0c\u5374\u4e0d\u77e5\u4e3a\u81ea\u5df1\u6253\u7b97\u3002 \u65f6\u95f4\u771f\u662f\u4e0d\u7b49\u4eba\u7684\uff0c\u518d\u8fc7\u51e0\u5e74\uff0c\u6211\u90fd\u8981\u62b1\u5b59\u5b50\u5566\uff01\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f46\u613f\u6211\u56de\u6765\u7684\u65f6\u5019\uff0c\u8d76\u5f97\u4e0a\u559d\u4f60\u513f\u5b50\u7684\u559c\u9152\u3002\u6211\u6b64\u53bb\u65e9\u5219\u534a \u8f7d\uff0c\u8fdf\u5219\u4e00\u5e74\uff0c\u4fbf\u4f1a\u56de\u6765\u3002\u4e16\u9057\uff0c\u4f60\u5728\u6c5f\u6e56\u6d6a\u8361\uff0c\u6211\u4eec\u627e\u4f60\u4e0d\u5bb9\u6613\uff0c\u51e0\u65f6\u4f60\u4e5f\u6765\u51b0\u5bab\u63a2 \u671b\u63a2\u671b\u6211\u4eec\u624d\u662f\u5440\u3002\u4f60\u603b\u4e0d\u6765\u63a2\u671b\u6211\u4eec\uff0c\u96be\u9053\u4f60\u8fd8\u5728\u751f\u7ecf\u5929\u7684\u6c14\u5417\uff1f\u201d\u5510\u7ecf\u5929\u4ee5\u524d\u4e5f\u66fe \u9a82\u8fc7\u91d1\u809a\u9057\u4f5c\u201c\u6bd2\u624b\u75af\u4e10\u201d\uff0c\u5e76\u66fe\u548c\u4ed6\u6253\u8fc7\u4e00\u573a\uff0c\u6545\u6b64\u51b0\u5ddd\u5929\u5973\u6709\u6b64\u4e00\u8bed\u3002 \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u54ea\u91cc\u7684\u8bdd\uff1f\u5c11\u5e74\u65f6\u5019\u7684\u80e1\u95f9\uff0c\u672c\u6765\u5c31\u662f\u6211\u7684\u4e0d\u5bf9\u3002\u201d\u5510\u7ecf\u5929\u54c8\u54c8\u5927 \u7b11\uff0c\u4e0a\u6765\u548c\u4ed6\u62c9\u624b\u3002 \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u7ecf\u5929\u5144\uff0c\u5728\u6211\u8ba4\u8bc6\u7684\u670b\u53cb\u4e2d\uff0c\u771f\u662f\u4ee5\u4f60\u7684\u798f\u6c14\u6700\u597d\u4e86\u3002\u5f53\u5e74\u6211\u5992\u5fcc \u4f60\uff0c\u73b0\u5728\u4e5f\u4e00\u6837\u5992\u5fcc\u4f60\uff0c\u4e0d\u8fc7\uff0c\u4f60\u653e\u5fc3\uff0c\u6211\u53ef\u4e0d\u4f1a\u518d\u627e\u4f60\u6253\u67b6\u4e86\u3002\u201d\u5510\u7ecf\u5929\u4e5f\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u73b0\u5728\u4f60\u627e\u6211\u6253\u67b6\uff0c\u6211\u4e5f\u4e0d\u6562\u518d\u78b0\u4f60\u4e86\u3002\u201d\u51b0\u5ddd\u5929\u5973\u770b\u89c1\u4ed6\u4eec\u524d\u5acc\u5c3d\u91ca\uff0c\u5f88\u662f\u9ad8\u5174\uff0c\u8bf4 \u9053\uff1a\u201c\u4e16\u9057\uff0c\u5176\u5b9e\u4f60\u53ef\u4ee5\u8fc7\u5f97\u6bd4\u6211\u4eec\u66f4\u5feb\u4e50\uff0c\u7528\u4e0d\u4e86\u7fa1\u6155\u522b\u4eba\uff0c\u597d\uff0c\u65f6\u95f4\u4e0d\u65e9\uff0c\u6211\u4eec\u53ef \u8981\u8d70\u5566\u3002\u201d \u3000\u3000\u5510\u7ecf\u5929\u3001\u9648\u5929\u5b87\u4e24\u5bf9\u592b\u5987\u62bc\u89e3\u5c3c\u6cca\u5c14\u90a3\u73ed\u6b66\u58eb\u8d70\u4e86\uff0c\u6c5f\u5357\u9001\u4ed6\u4eec\u51fa\u95e8\u3002\u91d1\u4e16\u9057\u7559\u5728 \u573a\u4e2d\uff0c\u832b\u7136\u81ea\u601d\uff0c\u4e0d\u77e5\u4e0d\u89c9\u8d70\u5230\u8c37\u4e4b\u534e\u8eab\u8fb9\uff0c\u8bf4\u9053\uff1a\u201c\u4e4b\u534e\uff0c\u6211\u60f3\u95ee\u4f60\u4e00\u53e5\u8bdd\u3002\u4f60\u8fc7\u5f97 \u5feb\u6d3b\u5417\uff1f\u201d\u8c37\u4e4b\u534e\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u7b11\u9053\uff1a\u201c\u8fd9\u95ee\u9898\u6211\u4f3c\u4e4e\u65e9\u5df2\u7b54\u590d\u8fc7\u4f60\u4e86\u3002\u53ea\u8981\u4f60\u8fc7\u5f97 \u5feb\u6d3b\uff0c\u6211\u4e5f\u5c31\u8fc7\u5f97\u5feb\u6d3b\u3002\u55ef\uff0c\u6211\u4eca\u5929\u5c24\u5176\u5feb\u6d3b\uff01\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4f60\u8fd8\u8bb0\u5f97\u6211\u5bf9\u4f60\u8bf4\u8fc7\u7684\uff0c\u6211\u7684\u5e08\u7236\u5bf9\u4f60\u7684\u671f\u671b\u5417\uff1f\u5979\u662f\u5f88\u65e9\u5c31\u770b\u51fa\u4f60 \u80fd\u6210\u5927\u5668\u7684\uff0c\u73b0\u5728\u4f60\u7684\u6b66\u529f\u5df2\u7ecf\u662f\u5929\u4e0b\u7b2c\u4e00\uff0c\u4f60\u8bf4\u6211\u8fd8\u80fd\u4e0d\u9ad8\u5174\u5417\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u7531\u4e8e\u4f60\u4eec\u7684\u9f13\u52b1\u3002\u5176\u5b9e\u6211\u73b0\u5728\u867d\u6709\u5bf8\u8fdb\uff0c\u8ddd\u79bb\u2018\u5929\u4e0b\u7b2c \u4e00\u2019\u90a3\u8fd8\u5dee\u5f97\u8fdc\u5462\uff01\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u597d\uff0c\u4f60\u80fd\u591f\u4e0d\u81ea\u6ee1\u90a3\u5c31\u66f4\u597d\uff0c\u4e16\u9057\uff0c\u73b0\u5728\u8f6e\u5230\u6211\u95ee \u4f60\u4e86\uff0c\u4f60\u5feb\u6d3b\u5417\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u832b\u7136\u5982\u6709\u6240\u601d\uff0c\u4e45\u4e45\u672a\u56de\u7b54\u8c37\u4e4b\u534e\u7684\u8bdd\u3002\u539f\u6765\u5728\u4ed6\u8bf4\u51fa\u201c\u4f60\u4eec\u201d\u8fd9\u4e24\u4e2a\u5b57\u7684 \u65f6\u5019\uff0c\u4e0d\u81ea\u7981\u7684\u4fbf\u60f3\u8d77\u4e86\u5389\u80dc\u7537\u6765\uff0c\u4ed6\u6240\u8bf4\u7684\u201c\u4f60\u4eec\u201d\uff0c\u662f\u5305\u62ec\u4e86\u5389\u80dc\u7537\u5728\u5185\u7684\u3002\u4ed6\u53c8 \u4e00\u6b21\u89e6\u52a8\u4e86\u5fc3\u5e95\u7684\u4f24\u75a4\uff0c\u5047\u8d77\u4e86\u5389\u80dc\u7537\u90a3\u6b21\u6b7b\u4ea1\u7684\u5a5a\u793c\uff0c\u5728\u5389\u80dc\u7537\u4e34\u6b7b\u4e4b\u524d\uff0c\u5728\u90a3\u7ea2\u70db \u9ad8\u70e7\u3001\u4f46\u5374\u662f\u5145\u6ee1\u4e86\u51c4\u51c9\u7684\u6c14\u6c1b\u4e2d\uff0c\u5389\u80dc\u7537\u5bf9\u4ed6\u8bf4\u51fa\u4e86\u4e09\u4e2a\u613f\u671b\u3002\u5176\u4e2d\u4e00\u4e2a\u4e0e\u8c37\u4e4b\u534e\u7684 \u76f8\u540c\uff0c\u4e5f\u662f\u5e0c\u671b\u4ed6\u6210\u4e3a\u4e00\u4ee3\u7684\u6b66\u5b66\u5927\u5e08\uff0c\u597d\u8ba9\u5979\u201c\u4e0d\u8bba\u5728\u4ec0\u4e48\u5730\u65b9\u201d\uff0c\u90fd\u53ef\u4ee5\u5f15\u4e3a\u9a84\u50b2\u3002 \u3000\u3000\u91d1\u4e16\u9057\u773c\u524d\u5e7b\u51fa\u4e86\u5389\u80dc\u7537\u7684\u5f71\u5b50\uff0c\u4f46\u53ef\u60dc\u5389\u80dc\u7537\u5df2\u770b\u4e0d\u5230\u4ed6\u7684\u4eca\u5929\u4e86\u3002\u4ed6\u60f3\u4e86\u4e00\u4f1a\uff0c \u8bf4\u9053\uff1a\u201c\u4e4b\u534e\uff0c\u6211\u4eca\u5929\u4e5f\u662f\u5f88\u5feb\u4e50\u7684\u3002\u201d\u8c37\u4e4b\u534e\u51dd\u89c6\u7740\u4ed6\u7684\u773c\u775b\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u4e16\u9057\uff0c \u4f60\u4e0d\u8981\u9a97\u6211\u3002\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u6ca1\u6709\u9a97\u4f60\uff0c\u6211\u4eca\u5929\u662f\u4e3a\u4e86\u522b\u4eba\u7684\u5feb\u4e50\u800c\u5feb\u4e50\u3002\u4f60\u77e5\u9053\u6211 \u5728\u60f3\u4ec0\u4e48\u5417\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u968f\u7740\u4ed6\u7684\u76ee\u5149\u770b\u53bb\uff0c\u53ea\u89c1\u6c5f\u6d77\u5929\u548c\u8c37\u4e2d\u83b2\u5934\u5e76\u7740\u5934\uff0c\u6324\u5728\u4e00\u5904\uff0c\u4f3c\u4e4e\u6b63\u5728\u7ec6 \u7ec6\u79c1\u8bed\u3002\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u6211\u4eec\u7684\u5f92\u5f1f\u90fd\u5df2\u957f\u5927\u6210\u4eba\u4e86\u3002\u4ed6\u4eec\u5c31\u4f3c\u6211\u4eec\u5f53\u5e74\u7684\u5f71\u5b50\uff0c \u4e0d\u8fc7\u4ed6\u4eec\u7684\u547d\u8fd0\u4e00\u5b9a\u4f1a\u6bd4\u6211\u4eec\u597d\u5f97\u591a\uff0c\u4f60\u77a7\uff0c\u4ed6\u4eec\u4e0d\u662f\u5f88\u5feb\u4e50\u5417\uff1f\u201d\u5176\u5b9e\uff0c\u91d1\u4e16\u9057\u5374\u4e0d \u77e5\u9053\uff0c\u6c5f\u6d77\u5929\u548c\u8c37\u4e2d\u83b2\u7684\u5fc3\u5934\uff0c\u73b0\u5728\u4e5f\u6b63\u662f\u8499\u4e86\u4e00\u5c42\u9634\u5f71\uff0c\u90fd\u6ca1\u6709\u611f\u5230\u5feb\u4e50\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u559f\u7136\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u5e94\u8be5\u6bd4\u6211\u4eec\u5feb\u4e50\u7684\u3002\u6211\u770b\u4ed6\u4eec\u7684\u4e8b\u60c5\uff0c\u662f\u4e0d\u7528\u6211\u4eec\u7ba1\u4e86\u3002 \u54a6\uff0c\u4e16\u9057\u4f60\u5b9a\u4e86\u795e\u5728\u770b\u4e9b\u4ec0\u4e48\uff1f\u4f60\u600e\u4e48\u5566\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u65e7\u4e8b\u5c18\u5c01\u4f11\u518d\u5fc6\uff0c\u773c\u524d\u60c5\u666f\u60f9\u601d\u91cf\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e8c\u56de\u3000\u4e2d\u5e74\u5fc3\u4e8b\u6d53\u5982\u9152\u3000\u5c11\u5e74\u60c5\u6000\u603b\u662f\u8bd7 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u56db\u5341\u4e8c\u56de\u3000\u4e2d\u5e74\u5fc3\u4e8b\u6d53\u5982\u9152\u3000\u5c11\u5e74\u60c5\u6000\u603b\u662f\u8bd7 \u3000\u3000\u91d1\u4e16\u9057\u7fdf\u7136\u4e00\u60ca\uff1b\u4f3c\u662f\u4ece\u6076\u68a6\u4e2d\u9192\u6765\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u4e4b\u534e\uff0c\u4f60\u77a7\uff0c\u4f60\u77a7\uff0c\u5979\u7684\u5f71\u5b50\uff01\u201d \u8c37\u4e4b\u534e\u968f\u7740\u4ed6\u6240\u6307\u7684\u65b9\u5411\u770b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u957f\u53d1\u62ab\u80a9\u7684\u80cc\u5f71\uff0c\u6b63\u968f\u7740\u4eba\u6d41\u8d70\u51fa\u4e86\u5927\u95e8\u3002\u4ece \u90a3\u80cc\u5f71\u770b\u6765\uff0c\u7adf\u662f\u548c\u5389\u80dc\u7537\u4e00\u6a21\u4e8c\u6837\uff0c\u8981\u4e0d\u662f\u8c37\u4e4b\u534e\u65e9\u5df2\u77e5\u9053\u6709\u8fd9\u4e48\u4e00\u4e2a\u4eba\uff0c\u8fd8\u51e0\u4e4e\u5f53 \u4f5c\u662f\u5389\u80dc\u7537\u590d\u751f\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5176\u5b9e\u4e5f\u77e5\u9053\u8fd9\u4e2a\u4eba\u662f\u8c01\u7684\uff0c\u8fd9\u51e0\u5e74\u6765\u4ed6\u867d\u7136\u4ea1\u4e8e\u6388\u5f92\uff0c\u5fc3\u4e2d\u4e5f\u4e00\u76f4\u5ff5\u5ff5\u4e0d\u5fd8 \u8981\u6253\u63a2\u8fd9\u4e2a\u4eba\u7684\u6765\u5386\u3002\u4f46\u5c3d\u7ba1\u4ed6\u77e5\u9053\u8fd9\u4e2a\u4eba\u662f\u8c01\uff0c\u5728\u4ed6\u5fc3\u4e2d\u6b63\u60f3\u5ff5\u7740\u5389\u80dc\u7537\u7684\u65f6\u5019\uff0c\u84e6 \u7136\u89c1\u7740\u8fd9\u4eba\u7684\u5f71\u5b50\uff0c\u4ecd\u7136\u4e0d\u7981\u628a\u4ed6\u5f53\u4f5c\u4e86\u5389\u80dc\u7537\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u6b63\u662f\u5389\u590d\u751f\uff0c\u4ed6\u672c\u6765\u4e0d\u613f\u610f\u8fd9\u4e48\u5feb\u8d70\u7684\uff0c\u4f46\u5929\u9b54\u6559\u4e3b\u4e0d\u60f3\u88ab\u91d1\u4e16\u9057\u53d1\u73b0\uff0c\u4e00 \u5b9a\u8981\u5389\u590d\u751f\u548c\u5979\u540c\u8d70\u3002\u5389\u590d\u751f\u5bf9\u5929\u9b54\u6559\u4e3b\u662f\u767e\u4f9d\u767e\u987a\uff0c\u53ea\u597d\u6539\u53d8\u4e86\u81ea\u5df1\u7684\u4e3b\u610f\uff0c\u4e0e\u5979\u7acb \u5373\u79bb\u5f00\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5b9a\u4e86\u5b9a\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u4e4b\u534e\uff0c\u6211\u6709\u4e00\u4ef6\u5fc3\u4e8b\u672a\u4e86\uff0c\u6211\u60f3\u53bb\u5411\u8fd9\u4e2a\u4eba\u95ee\u4e2a\u660e\u767d\u3002\u201d \u8c37\u4e4b\u534e\u5fc3\u91cc\u6697\u6697\u53f9\u606f\uff0c\u91d1\u4e16\u9057\u59cb\u7ec8\u662f\u5fd8\u4e0d\u4e86\u5389\u80dc\u7537\uff0c\u5979\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u53bb\u5427\uff01\u201d\u4f46 \u58f0\u97f3\u5df2\u662f\u5fae\u5fae\u98a4\u6296\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5ffd\u5730\u7ad9\u4f4f\uff0c\u8138\u4e0a\u7684\u795e\u60c5\u9887\u4e3a\u5947\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u4e4b\u534e\uff0c\u4f60\u53ef\u4ee5\u5728\u8fd9\u91cc\u591a\u7559\u51e0\u5929\u5417\uff1f \u6211\u95ee\u6e05\u695a\u4e86\u4e00\u4ef6\u4e8b\u56de\u6765\u5c31\u60f3\u89c1\u4f60\u3002\u201d\u8c37\u4e4b\u534e\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7684\u83b2\u513f\u8eab\u4e16\u5df2\u660e\u3002 \u6211\u4e0d\u77e5\u9053\u5979\u662f\u613f\u610f\u5f53\u516c\u4e3b\u8fd8\u662f\u7ee7\u7eed\u8ddf\u6211\uff0c\u4f46\u6211\u603b\u4f1a\u7559\u5728\u8fd9\u91cc\u966a\u5979\u51e0\u5929\u7684\u3002\u4e0d\u8fc7\uff0c\u6211\u538c\u5f03 \u7e41\u534e\uff0c\u8981\u662f\u83b2\u513f\u8981\u5f53\u516c\u4e3b\uff0c\u6211\u53ef\u4e0d\u613f\u5728\u5bab\u4e2d\u803d\u5f97\u592a\u4e45\u3002\u201d \u3000\u3000\u201c\u91d1\u4e16\u9057\u9053\uff1a\u201c\u8fd9\u4e5f\u65e0\u751a\u6253\u7d27\uff0c\u603b\u4e4b\u6211\u4e86\u5374\u8fd9\u4ef6\u5fc3\u4e8b\u4e4b\u540e\uff0c\u4e0d\u8bba\u4f60\u5728\u54ea\u513f\uff0c\u6211\u90fd\u8d76 \u7740\u53bb\u89c1\u4f60\u5c31\u662f\u3002\u201d\u91d1\u4e16\u9057\u7684\u8bdd\u5f15\u8d77\u4e86\u8c37\u4e4b\u534e\u731c\u7591\uff0c\u5979\u548c\u91d1\u4e16\u9057\u672c\u6765\u53ef\u4ee5\u8bf4\u51e0\u4e4e\u662f\u5fc3\u610f\u76f8 \u901a\u7684\u4e86\uff0c\u91d1\u4e16\u9057\u5fc3\u4e2d\u4e4b\u4e8b\u4e0d\u5f85\u5728\u53e3\u4e2d\u8bf4\u51fa\u5979\u5df2\u660e\u767d\uff0c\u4f46\u8fd9\u4e00\u6b21\u5979\u5374\u662f\u4e00\u7247\u832b\u7136\uff0c\u4e0d\u77e5\u9053 \u91d1\u4e16\u9057\u662f\u5728\u60f3\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u91d1\u4e16\u9057\u4f38\u51fa\u624b\u6765\uff0c\u4ed6\u4eec\u90fd\u662f\u4e2d\u5e74\u4eba\u4e86\uff0c\u4e0d\u50cf\u5c11\u7537\u5c11\u5973\u7684\u7f9e\u6da9\uff0c\u4e5f\u4e0d\u7528\u907f\u5acc\uff0c\u8c37\u4e4b\u534e \u4e0e\u4ed6\u8f7b\u8f7b\u4e8c\u63e1\u3002\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8d70\u5427\u3002 \u3000\u3000\u4f60\u4ec0\u4e48\u65f6\u5019\u60f3\u89c1\u6211\uff0c\u4f60\u5c31\u4ec0\u4e48\u65f6\u5019\u6765\u5427\uff01\u201d\u4ed6\u4eec\u867d\u7136\u8868\u9762\u4e0a\u4e0d\u4f3c\u5c11\u7537\u5c11\u5973\u7684\u5bb9\u6613\u5bb3 \u7f9e\uff0c\u5bb9\u6613\u6fc0\u52a8\uff0c\u4f46\u76f8\u4e92\u4e00\u63e1\uff0c\u5f7c\u6b64\u7684\u5fc3\u5f26\u4ecd\u662f\u7981\u4e0d\u4f4f\u5fae\u5fae\u98a4\u6296\u3002 \u3000\u3000\u8fd9\u65f6\u4f1a\u573a\u91cc\u7684\u5404\u56fd\u6b66\u58eb\u6b63\u5728\u9646\u7eed\u79bb\u5f00\uff0c\u90a3\u4e00\u5343\u5fa1\u6797\u519b\uff0c\u4e5f\u6b63\u5206\u6210\u51e0\u961f\uff0c\u4ece\u5404\u5904\u95e8\u53e3 \u8fdb\u6765\uff0c\u4eba\u6765\u4eba\u5f80\uff0c\u901a\u9053\u62e5\u6324\u4e0d\u582a\u3002\u91d1\u4e16\u9057\u867d\u662f\u6025\u7740\u8981\u627e\u5389\u590d\u751f\uff0c\u4f46\u4ed6\u65e2\u4e0d\u80fd\u8fd0\u7528\u8f7b\u529f\uff0c \u4e5f\u4e0d\u4fbf\u4e0d\u987e\u793c\u8c8c\u7684\u786c\u6324\u5f00\u90a3\u4e9b\u4eba\uff0c\u5374\u4e5f\u4e0d\u5bb9\u6613\u8d70\u5f97\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u521a\u8d70\u5f97\u5341\u6765\u6b65\uff0c\u5ffd\u5730\u6709\u4e2a\u53eb\u5316\u8dc4\u8dc4\u8e09\u8e09\u7684\u6324\u5230\u4ed6\u7684\u773c\u524d\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c \u8001\u53eb\u5316\u60f3\u5411\u4f60\u8ba8\u676f\u559c\u9152\u8d50\u559d\uff0c\u5c31\u4e0d\u77e5\u4f60\u80af\u4e0d\u80af\u7ed9\u8001\u53eb\u5316\u8fd9\u4e2a\u9762\u5b50\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u8ba4\u5f97\u8fd9\u53eb\u5316\u5b50\u662f\u5317\u4e10\u5e2e\u5e2e\u4e3b\u4ef2\u957f\u7edf\uff0c\u4e0d\u89c9\u4e00\u6014\u3002\u4ed6\u4e0e\u4ef2\u957f\u7edf\u4e0d\u8fc7\u89c1\u8fc7\u4e00\u4e24\u6b21 \u9762\uff0c\u4f46\u4ec5\u4ec5\u662f\u76f8\u8bc6\u800c\u5df2\uff0c\u8c08\u4e0d\u4e0a\u751a\u4e48\u6df1\u4ea4\u3002\u5982\u4eca\u4ef2\u957f\u7edf\u7adf\u7136\u5f53\u7740\u4f17\u4eba\uff0c\u62e6\u7740\u4ed6\u5411\u4ed6\u8ba8\u559c \u9152\u559d\uff0c\u82e5\u662f\u51fa\u4e8e\u8bf4\u7b11\u60ef\u7684\u8001\u670b\u53cb\u8fd9\u72b9\u81ea\u53ef\uff0c\u4f46\u4e00\u4e2a\u4ec5\u4ec5\u662f\u76f8\u8bc6\u7684\u4eba\uff0c\u6765\u5411\u4ed6\u8bf4\u8fd9\u6837\u7684\u6d3b\uff0c \u91d1\u4e16\u9057\u5c31\u4e0d\u514d\u611f\u5230\u610f\u5916\u4e86\u3002 \u3000\u3000\u5c3d\u7ba1\u91d1\u4e16\u9057\u7684\u6db5\u517b\u529f\u592b\u5df2\u6bd4\u5c11\u5e74\u65f6\u5019\u597d\u4e86\u4e0d\u77e5\u591a\u5c11\uff0c\u4f46\u7ed9\u4ef2\u957f\u7edf\u8fd9\u4e48\u6765\u4e00\u4e0b\u5b50\uff0c\u8138 \u8272\u4e5f\u5c31\u9887\u4e0d\u81ea\u7136\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u548c\u4e4b\u534e\u7684\u4e8b\u60c5\uff0c\u600e\u7528\u5f97\u7740\u4f60\u6765\u591a\u7ba1\uff1f\u201d\u4fbf\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4ef2 \u5e2e\u4e3b\uff0c\u4f60\u8981\u8ba8\u559c\u9152\u559d\uff0c\u8fd9\u53ef\u662f\u627e\u9519\u4e86\u4eba\u5566\uff0c\u6211\u54ea\u6765\u7684\u559c\u9152\u7ed9\u4f60\u559d\u554a\uff1f\u201d \u3000\u3000\u4ef2\u957f\u7edf\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4f60\u8fd8\u672a\u77e5\u9053\u5417\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a \u3000\u3000\u201c\u77e5\u9053\u4ec0\u4e48\uff1f\u201d\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u534e\u5c71\u533b\u9690\u534e\u5929\u98ce\u4f60\u77e5\u9053\u5417\uff1f\u4ed6\u662f\u6211\u6700\u8981\u597d\u7684\u670b\u53cb\u3002\u201d \u91d1\u4e16\u9057\u9053\uff1a\u201c\u534e\u8001\u524d\u8f88\u533b\u9053\u901a\u795e\uff0c\u540d\u79f0\u5f53\u4eca\u7b2c\u4e00\u56fd\u624b\uff0c\u6211\u662f\u4e45\u4ef0\u7684\u4e86\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u534e\u5929 \u98ce\u662f\u4f60\u7684\u597d\u670b\u53cb\u53c8\u600e\u4e48\u6837\uff1f \u3000\u3000\u8fd9\u5374\u4e0e\u6211\u6709\u4f55\u76f8\u5e72\uff1f\u201d \u3000\u3000\u4ef2\u957f\u7edf\u5174\u81f4\u52c3\u52c3\u4ed6\u8bf4\u4e0b\u53bb\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4f60\u53ef\u77e5\u9053\u534e\u5929\u98ce\u8fd8\u6709\u4e2a\u5973\u513f\uff1f\u8fd9\u4f4d\u5c0f\u59d1\u72fc \u5440\uff0c\u806a\u660e\u601c\u4fd0\uff0c\u80fd\u5e72\u6781\u4e86\u3002\u5979\u7236\u4eb2\u7684\u6b66\u529f\u533b\u672f\uff0c\u5979\u662f\u5168\u90e8\u5b66\u5230\u4e86\u624b\u4e86\u3002\u201d\u91d1\u4e16\u9057\u5927\u4e3a\u8be7 \u5f02\uff0c\u4e0d\u77e5\u4ef2\u957f\u7edf\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u8fd9\u4e2a\u5012\u8fd8\u672a\u77e5\u9053\u3002\u4e0d\u8fc7\u540e\u4e00\u8f88\u7684\u603b \u662f\u8981\u80dc\u8fc7\u524d\u4e00\u8f88\u7684\u624d\u597d\uff0c\u6211\u5c31\u76fc\u671b\u6211\u7684\u5f92\u5f1f\u4ed6\u65e5\u6bd4\u6211\u9ad8\u5f3a\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u5927\u7b11\u9053\uff1a\u201c\u5bf9\uff0c\u8981\u662f\u4f60\u7684\u5f92\u5f1f\u4e0d\u9ad8\u5f3a\uff0c\u6211\u4e5f\u4e0d\u6765\u5411\u4f60\u8ba8\u559c\u9152\u559d\u4e86\u3002\u201d\u91d1\u4e16\u9057 \u9053\uff1a\u201c\u54e6\uff0c\u4f60\u8bf4\u4e86\u534a\u5929\uff0c\u6211\u73b0\u5728\u624d\u6709\u70b9\u660e\u767d\uff0c\u6562\u60c5\u4f60\u662f\u60f3\u7ed9\u6211\u7684\u5f92\u5f1f\u505a\u5a92\uff1f\u201d \u3000\u3000\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u4f60\u731c\u5bf9\u4e86\u3002\u5509\uff0c\u6c5f\u5c0f\u4fa0\u4e5f\u771f\u662f\u8138\u76ae\u8584\uff0c\u539f\u6765\u4ed6\u8fd8\u6ca1\u6709\u5411\u4f60\u63d0\u8fc7\u5440\uff1f \u4ed6\u548c\u534e\u5929\u98ce\u7684\u5973\u513f\u65e9\u5df2\u662f\u60c5\u6295\u610f\u5408\u4e86\uff0c\u4ed6\u4eec\u5f53\u65f6\u76f8\u8bc6\uff0c\u6211\u8001\u53eb\u5316\u4e5f\u662f\u5728\u573a\u7684\uff0c\u8bf4\u8d77\u6765\u8fd9 \u4f4d\u5c0f\u59d1\u5a18\u5bf9\u4ee4\u5f92\u8fd8\u66fe\u6709\u8fc7\u6551\u547d\u4e4b\u6069\u5462\uff01\u201d\u5f53\u4e0b\u5c06\u6c5f\u6d77\u5929\u90a3\u5e74\u53d7\u4e86\u6bd2\u4f24\uff0c\u5de7\u9047\u534e\u5929\u98ce\u7236\u5973 \u4e4b\u4e8b\uff0c\u7ea6\u54af\u5bf9\u91d1\u4e16\u9057\u8bf4\u4e86\u4e00\u904d\uff0c\u7136\u540e\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u96be\u5f97\u9047\u4e0a\u4f60\u3002\u4ed6\u4eec\u5c11\u5e74\u4eba\u8138\u76ae\u8584\uff0c \u8bf4\u4e0d\u51fa\u53e3\uff0c\u54b1\u4eec\u5f53\u957f\u8f88\u7684\uff0c\u53ef\u5f97\u65e9\u4e9b\u7ed9\u4ed6\u4eec\u5c06\u4e8b\u60c5\u5b9a\u593a\u4e0b\u6765\u3002\u5973\u5bb6\u65b9\u9762\uff0c\u534e\u5929\u98ce\u662f\u65e9\u5c31 \u613f\u610f\u7ed3\u8fd9\u95e8\u4eb2\u7684\u4e86\uff0c\u6211\u53ef\u4ee5\u66ff\u4ed6\u4f5c\u4e3b\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u5927\u611f\u610f\u5916\uff0c\u6709\u51e0\u5206\u9ad8\u5174\uff0c\u4e5f\u6709\u51e0\u5206\u5931\u671b\uff0c\u6697\u81ea\u60f3\u9053\uff1a \u3000\u3000\u201c\u6211\u672c\u662f\u60f3\u6d77\u513f\u548c\u8c37\u4e2d\u83b2\u7ed3\u6210\u4e00\u5bf9\u7684\uff0c\u5374\u539f\u6765\u4ed6\u5df2\u53e6\u6709\u4e86\u610f\u4e2d\u4eba\u3002\u5509\uff0c\u4ed6\u559c\u6b22\u8c01\u4e0d \u559c\u6b22\u8c01\uff0c\u8fd9\u662f\u52c9\u5f3a\u4e0d\u6765\u7684\uff0c\u4e5f\u53ea\u597d\u4efb\u7531\u4ed6\u4eec\u4e86\u3002\u201d\u5f53\u4e0b\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u53ea\u8981\u4ed6\u4eec\u4e8c\u4eba\u60c5\u6295 \u610f\u5408\uff0c\u6211\u5f53\u7136\u613f\u610f\u66ff\u4ed6\u4eec\u4e3b\u5a5a\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u5927\u559c\uff0c\u62db\u624b\u53eb\u9053\uff1a\u201c\u78a7\u4f84\u5973\uff0c\u4f60\u8fc7\u6765\u89c1\u8fc7\u91d1\u5927\u4fa0\u5440\uff01\u201d\u4ed6\u8fde\u53eb\u4e09\u58f0\uff0c\u5374\u542c\u4e0d \u5230\u534e\u4e91\u78a7\u7684\u56de\u7b54\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u6414\u4e86\u6414\u5934\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u4e2b\u5934\u600e\u4e48\u5ffd\u7136\u4e0d\u89c1\u4e86\uff1f\u5979\u5fc3\u773c\u73b2\u73d1\uff0c\u83ab\u975e \u662f\u5979\u5df1\u6599\u5230\u6211\u548c\u91d1\u5927\u4fa0\u6b63\u5728\u8bf4\u5979\u7684\u7ec8\u8eab\u4e4b\u4e8b\u3002\u5973\u5b69\u513f\u5bb6\u5bb3\u7f9e\uff0c\u8eb2\u8d77\u6765\u4e86\uff1f\u201d\u5c31\u5728\u8fd9\u65f6\uff0c \u5ffd\u542c\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u7a7a\u4e2d\u4f20\u6765\u201c\u560e\u560e\u201d\u7684\u523a\u8033\u602a\u58f0\uff0c\u5916\u9762\u7684\u58eb\u5175\u4eec\u7eb7\u7eb7\u53eb\u9053\uff1a\u201c\u770b\u5440\uff0c\u597d \u5927\u7684\u4e00\u5934\u5140\u9e70\uff01\u201d\u201c\u54c8\uff0c\u8fd9\u5c0f\u59d1\u5a18\u98de\u8d77\u6765\u4e86\uff01\u201d\u91cc\u9762\u7684\u4eba\u4e5f\u7eb7\u7eb7\u6324\u51fa\u53bb\u770b\uff0c\u6324\u5728\u6700\u524d\u5934 \u7684\u5219\u662f\u6c5f\u6d77\u5929\u548c\u8c37\u4e2d\u83b2\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u5934\u7855\u5927\u65e0\u670b\u7684\u5140\u9e70\u6b63\u5728\u5b9d\u5854\u7684\u91d1\u9876\u76d8\u65cb\u3001\u9e70\u80cc\u4e0a\u7684\u5c11\u5973\u8863\u8882\u98d8\u98d8\uff0c\u9690\u9690\u53ef\u89c1\u3002 \u6c5f\u6d77\u5929\u5927\u53eb\u9053\uff1a\u201c\u78a7\u59b9\uff0c\u4f60\u600e\u4e48\u5c31\u8d70\u4e86\uff1f\u201d\u8c37\u4e2d\u83b2\u4e5f\u5728\u5c16\u58f0\u53eb\u9053\u201c\u534e\u59d0\u59d0\uff0c\u4f60\u56de\u6765\u5440\uff01\u201d \u3000\u3000\u90a3\u5934\u795e\u9e70\uff0c\u4e00\u4e2a\u76d8\u65cb\uff0c\u63a0\u4e0b\u6570\u4e08\uff0c\u6c5f\u6d77\u5929\u4f9d\u7a00\u542c\u5f97\u4e00\u58f0\u53f9\u606f\uff0c\u90a3\u5934\u795e\u9e70\u500f\u5730\u53c8\u5c55\u7fc5 \u9ad8\u98de\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u5929\u7a7a\u53ea\u89c1\u4e00\u4e2a\u9ed1\u70b9\uff0c\u7ec8\u4e8e\u90a3\u9ed1\u70b9\u4e5f\u6d88\u901d\u4e86\u3002\u534e\u4e91\u78a7\u770b\u89c1\u4e86\u4ed6\u4eec\uff0c\u53ef\u662f \u5979\u53ea\u6e9c\u4e0b\u4e86\u4e00\u58f0\u53f9\u606f\uff0c\u5374\u8fde\u534a\u53e5\u8bf4\u8bdd\u4e5f\u6ca1\u6709\u6254\u4e0b\uff0c\u4fbf\u98de\u8d70\u4e86\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u7fd8\u9996\u957f\u7a7a\uff0c\u5446\u7acb\u6709\u5982\u6728\u9e21\uff0c\u4ed6\u7684\u4e00\u7f15\u60c5\u4e1d\uff0c\u867d\u7136\u65e9\u5df2\u7cfb\u5728\u8c37\u4e2d\u83b2\u8eab\u4e0a\uff0c\u4f46\u534e \u4e91\u78a7\u5bf9\u4ed6\u7684\u6df1\u60c5\u539a\u4e49\uff0c\u4ed6\u53c8\u600e\u80fd\u9057\u5fd8\uff1f \u3000\u3000\u5c24\u5176\u534e\u4e91\u78a7\u662f\u5728\u8fd9\u6837\u7684\u60c5\u5f62\u4e0b\u98de\u8d70\u7684\uff0c\u66f4\u4ee4\u4ed6\u96be\u8fc7\u4e07\u5206\uff0c\u4ed6\u5fc3\u4e2d\u81ea\u6028\u81ea\u8d23\uff1a\u201c\u78a7\u59b9 \u662f\u4e3a\u6211\u800c\u6765\uff0c\u6211\u5374\u8f9c\u8d1f\u4e86\u5979\u7684\u60c5\u610f\uff0c\u5509\uff0c\u770b\u6765\u5979\u662f\u518d\u4e5f\u4e0d\u80fd\u539f\u8c05\u6211\u4e86\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u6bd4\u6c5f\u4fae\u5929\u66f4\u8981\u96be\u8fc7\uff0c\u534e\u4e91\u78a7\u6ca1\u6709\u542c\u89c1\u4ef2\u957f\u7edf\u7684\u8bf4\u8bdd\uff0c\u5012\u662f\u5979\u5168\u90fd\u542c\u89c1\u4e86\uff0c\u8fd9 \u5239\u90a3\u95f4\uff0c\u5979\u53ea\u89c9\u4e00\u7247\u832b\u7136\uff0c\u8bb8\u591a\u4ece\u6765\u6ca1\u6709\u60f3\u8fc7\u7684\u4e8b\u60c5\uff0c\u4e5f\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\u90fd\u5230\u4e86\u5fc3\u5934\u3001 \u3000\u3000\u5728\u6b64\u4e4b\u524d\uff0c\u5979\u4ece\u6765\u6ca1\u6709\u60f3\u5230\u5979\u548c\u6c5f\u6d77\u5929\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u5979\u548c\u6c5f\u6d77\u5929\u540c\u5728\u4e00\u8d77\u7684\u65f6\u5019\uff0c \u5f7c\u6b64\u90fd\u5f88\u9ad8\u5174\uff0c\u4f46\u5979\u4ece\u672a\u60f3\u8fc7\uff1a\u8fd9\u5c31\u662f\u7231\u60c5\u3002\u73b0\u5728\u534e\u4e91\u78a7\u7a81\u7136\u98de\u8d70\uff0c\u5979\u8fd9\u624d\u611f\u89c9\u5230\uff0c\u5728 \u534e\u4e91\u78a7\u7684\u773c\u4e2d\uff0c\u5979\u548c\u6c5f\u6d77\u5929\u65e9\u5df2\u662f\u4e00\u5bf9\u60c5\u4fa3\uff0c\u5979\u5fc3\u4e2d\u660e\u767d\uff0c\u534e\u4e91\u78a7\u662f\u4e3a\u5979\u98de\u8d70\u7684\u3002 \u3000\u3000\u201c\u534e\u59d1\u5a18\u5bf9\u6d77\u54e5\u6709\u6781\u5927\u7684\u6069\u4e49\uff0c\u4ed6\u4eec\u672c\u6765\u5e94\u8be5\u662f\u597d\u597d\u7684\u4e00\u5bf9\u7684\u3002\u201d\u201c\u5979\u82e5\u4e0d\u602a\u4f24\u5fc3 \u5230\u4e86\u6781\u70b9\uff0c\u51b3\u4e0d\u80af\u8fd9\u6837\u7a81\u7136\u98de\u8d70\uff01\u201d\u201c\u6211\u4eca\u5929\u521a\u521a\u548c\u5979\u8ba4\u8bc6\uff0c\u60f3\u4e0d\u5230\u7adf\u662f\u6211\u4f24\u4e86\u5979\u7684\u5fc3\uff01\u201d \u201c\u4ef2\u5e2e\u4e3b\u8bf4\u6d77\u54e5\u65e9\u5df2\u4e0e\u5979\u60c5\u6295\u610f\u5408\uff0c\u53ef\u60dc\u6211\u77e5\u9053\u5f97\u592a\u8fdf\u4e86\uff01\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u5979\u5ffd\u5730\u611f\u5230\u4e00 \u9635\u5fc3\u9178\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u5979\u4e5f\u611f\u89c9\u5230\u4e86\uff0c\u5979\u662f\u5728\u7231\u7740\u6c5f\u6d77\u5929\uff01 \u3000\u3000\u5979\u548c\u6c5f\u6d77\u5929\u5f7c\u6b64\u90fd\u6ca1\u6709\u5411\u5bf9\u65b9\u8868\u9732\u8fc7\u7231\u60c5\uff0c\u5979\u80fd\u591f\u57cb\u6028\u6c5f\u6d77\u5929\u5417\uff1f\u4e0d\uff0c\u5979\u8fd9\u65f6\u53ea\u662f \u4e3a\u81ea\u5df1\u96be\u8fc7\uff0c\u66f4\u4e3a\u534e\u4e91\u78a7\u96be\u8fc7\u3002\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5730\u6ef4\u4e0b\u6765\u4e86\uff0c\u6b63\u6ef4\u5728\u6c5f\u6d77\u5929\u7684\u8eab \u4e0a\u3002 \u3000\u3000\u6c5f\u6d77\u65e0\u56de\u8fc7\u5934\u6765\uff0c\u8c37\u4e2d\u83b2\u5df2\u7ecf\u4ece\u4ed6\u7684\u8eab\u8fb9\u8d70\u5f00\u4e86\u3002\u6c5f\u6d77\u5929\u8ffd\u4e0a\u4e24\u6b65\uff0c\u90b9\u4e0d\u77e5\u5bf9\u5979\u8bf4 \u4e9b\u4ec0\u4e48\u8bdd\u597d\uff0c\u53ea\u89c9\u5fc3\u5934\u7ede\u75db\uff0c\u4f3c\u4e4e\u5c31\u8981\u88c2\u79c6\uff0c\u8981\u662f\u771f\u80fd\u628a\u4e00\u9897\u5fc3\u5256\u5f00\u5206\u6210\u4e24\u534a\u90a3\u5012\u5f88\u597d\uff0c \u53ef\u60dc\u4e00\u9897\u5b8c\u6574\u7684\u5fc3\u5374\u662f\u4e0d\u80fd\u5206\u5f00\u7684\u554a\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u8fd8\u672a\u6765\u5f97\u53ca\u62c9\u7740\u8c37\u4e2d\u83b2\uff0c\u65c1\u8fb9\u6709\u4e2a\u4eba\u5374\u4e00\u628a\u63ea\u7740\u4ed6\uff0c\u539f\u6765\u662f\u4ef2\u957f\u7edf\u521a\u521a\u8d76\u5230\u3002 \u4ef2\u957f\u7edf\u6c14\u547c\u547c\u5730\u5927\u58f0\u95ee\u9053\uff1a\u201c\u6c5f\u5c0f\u4fa0\u3002\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u78a7\u59d1\u5a18\u4e3a\u4ec0\u4e48\u7a81\u7136\u8d70\u4e86\uff1f\u201d\u6c5f\u6d77 \u5929\u5931\u9b42\u843d\u9b44\u7684\u6837\u5b50\u8fce\u7740\u4ed6\u7684\u76ee\u5149\uff0c\u6447\u4e86\u6447\u5934\uff0c\u4ef2\u957f\u7edf\u6012\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u77e5\u9053\uff1f\u54fc\uff0c\u4e00\u5b9a\u662f \u4f60\u505a\u4e86\u5bf9\u4e0d\u4f4f\u5979\u7684\u4e8b\uff0c\u628a\u5979\u6c14\u8d70\u4e86\uff0c\u54fc\uff0c\u78a7\u59d1\u5a18\u6709\u54ea\u70b9\u4e0d\u597d\uff0c\u4f60\u600e\u53ef\u5982\u6b64\u8584\u5e78\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u66f4\u4e3a\u96be\u8fc7\uff0c\u4e00\u53e5\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\uff0c\u4ef2\u957f\u7edf\u8fd8\u8981\u518d\u9a82\u4e0b\u53bb\uff0c\u5ffd\u5730\u65c1\u8fb9\u4e5f\u6709\u4e2a\u4eba\u4e00 \u628a\u5c06\u4ed6\u63ea\u7740\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u4ef2\u5e2e\u4e3b\uff0c\u8fd9\u662f\u4ed6\u4eec\u5c11\u5e74\u4eba\u7684\u4e8b\u60c5\uff0c\u54b1\u4eec\u72af\u4e0d\u7740\u4e3a\u4ed6\u4eec\u751f\u6c14\u4e86\u3002\u201d \u8fd9\u4e2a\u4eba\u4e43\u662f\u91d1\u4e16\u9057\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4f60\u7684\u5f92\u5f1f\u5fd8\u6069\u8d1f\u4e49\uff0c\u4f60\u8fd8\u8981\u8892\u62a4\u4ed6\u5417\uff1f\u201d\u91d1\u4e16 \u9057\u7709\u5934\u4e00\u76b1\uff0c\u8bf4\u9053\uff1a\u201c\u4ef2\u5e2e\u4e3b\uff0c\u6211\u662f\u8fc7\u6765\u4eba\u4e86\uff0c\u7537\u5973\u4e4b\u95f4\u7684\u60c5\u4e8b\uff0c\u4f60\u4e0d\u61c2\u7684\u3002\u597d\u5427\uff0c\u4f60 \u8981\u9a82\u5c31\u9a82\u6211\u5427\u3002\u6211\u8bf7\u4f60\u559d\u4e00\u676f\u9152\u53bb\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u89c1\u6c5f\u6d77\u5929\u96be\u8fc7\u7684\u6837\u5b50\uff0c\u5fc3\u91cc\u5df2\u8f6f\u4e86\u4e0b\u6765\uff0c\u5583\u5583\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4ffa\u8001\u53eb\u5316\u8fd9\u4e00\u751f\u4ece\u6ca1\u6709\u548c\u5a18\u513f\u597d\u8fc7\uff0c\u6216\u8bb8\u6211\u662f\u771f\u7684\u4e0d\u61c2\uff0c\u4f46\u4e00\u4e2a\u4eba\u603b\u8981\u672c\u7740\u826f\u5fc3 \u624d\u597d\u3002\u201d\u4ed6\u6454\u8131\u4e86\u91d1\u4e16\u9057\u7684\u624b\uff0c\u5927\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u591a\u8c22\u4e86\uff0c\u4f60\u8fd9\u676f\u9152\u6211\u4e0d\u559d\u4e86\u3002\u6211\u8981\u53bb\u627e\u6211\u7684\u4f84\u5973\u513f\u53bb\u3002\u201d\u91d1\u4e16\u9057\u82e6\u7b11\u9053\uff1a\u201c\u6d77\u513f\uff0c \u4f60\u4f46\u6c42\u5fc3\u4e4b\u6240\u5b89\uff0c\u8981\u5982\u4f55\u4fbf\u5982\u4f55\u5427\u3002\u8fd9\u79cd\u4e8b\u60c5\u539f\u4e5f\u4e0d\u5fc5\u6c42\u4eba\u8c05\u89e3\u3002\u201d\u201c\u597d\uff0c\u4ef2\u5e2e\u4e3b\u4f60\u4e0d \u548c\u6211\u559d\u9152\uff0c\u90a3\u6211\u4e5f\u8981\u8d70\u5566\uff01\u201d\u4e00\u58f0\u957f\u5578\uff0c\u90ce\u58f0\u541f\u9053\uff1a\u201c\u65e7\u68a6\u5c18\u5c01\u4f11\u5189\u542f\uff0c\u6b64\u5fc3\u5982\u6c34\u53ea\u4e1c \u6d41\uff01\u201d\u8fc8\u5f00\u5927\u6b65\u81ea\u53bb\u8ffd\u8e2a\u90a3\u5389\u590d\u751f\u4e86\u3002\u8c37\u4e4b\u534e\u76ee\u9001\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u96be\u9053\u4e24\u4ee3 \u4eba\u90fd\u662f\u540c\u4e00\u547d\u8fd0\uff1f\u201d\u773c\u5149\u4e00\u8f6c\uff0c\u53ea\u89c1\u6c5f\u6d77\u5929\u5df2\u8ffd\u4e0a\u8c37\u4e2d\u83b2\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u4e8c\u4eba\u5e76\u80a9\u540c\u884c\uff0c\u8d70\u4e86\u4e00\u7a0b\uff0c\u5f7c\u6b64\u90fd\u9ed8\u4e0d\u4f5c\u58f0\u3002\u534a\u54cd\uff0c\u8c37\u4e2d\u83b2\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u6d77\u54e5\uff0c \u6211\u4e0d\u613f\u542c\u5230\u522b\u4eba\u9a82\u4f60\uff0c\u4f60\u53bb\u628a\u534e\u59d1\u5a18\u627e\u56de\u6765\u5427\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u4f1a\u53bb\u627e\u5979\u7684\uff0c\u4f46\u4e0d\u662f \u73b0\u5728\u3002\u6211\u521a\u624d\u5f88\u662f\u96be\u8fc7\uff0c\u542c\u4e86\u5e08\u7236\u90a3\u4e00\u53e5\u8bdd\uff0c\u73b0\u5728\u5df2\u662f\u597d\u4e9b\u4e86\uff0c\u4f60\u4e5f\u522b\u96be\u8fc7\u5427\u3002\u201d\u8c37\u4e2d \u83b2\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u4f60\u5f53\u771f\u662f\u50cf\u4ef2\u5e2e\u4e3b\u6240\u8bf4\u7684\u90a3\u6837\u8584\u5e78\u5417\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u81ea\u95ee\u6ca1\u6709\u505a \u9519\u4e8b\u60c5\uff0c\u522b\u4eba\u4e0d\u80af\u539f\u8c05\uff0c\u90a3\u53c8\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u6211\u5e76\u4e0d\u662f\u4e0d\u96be\u8fc7\uff0c\u4f46\u6211\u4e0d\u60f3\u4f60\u966a\u6211\u96be\u8fc7\u3002\u4f60 \u660e\u767d\u5417\uff1f\u201d\u8c37\u4e2d\u83b2\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u6211\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5df2\u5c06\u91d1\u9e70\u5bab\u7684\u5584\u540e\u4e8b\u5b9c\u5b89\u6392\u59a5\u5f53\uff0c\u8d76\u4e86\u51fa\u6765\u3002\u4ed6\u77e5\u9053\u534e\u4e91\u78a7\u5df2\u7ecf\u98de\u8d70\uff0c\u4f46 \u5374\u4e0d\u77e5\u9053\u6c5f\u6d77\u5929\u548c\u534e\u4e91\u78a7\u4e4b\u95f4\u7684\u60c5\u4e8b\uff0c\u89c1\u59b9\u59b9\u548c\u4ed6\u540c\u884c\uff0c\u5fc3\u91cc\u5f88\u662f\u559c\u6b22\u3002 \u3000\u3000\u4e0d\u6599\u4f1a\u9762\u4e4b\u540e\uff0c\u5374\u89c1\u4ed6\u4eec\u795e\u60c5\u6c89\u90c1\uff0c\u59b9\u59b9\u7684\u773c\u89d2\u4e14\u6709\u6cea\u75d5\uff0c\u5510\u52aa\u73e0\u7a46\u5403\u4e86\u4e00\u60ca\uff0c\u95ee \u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u5440\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4f60\u600e\u4e48\u54ed\u4e86\uff01\u201d\u8c37\u4e2d \u83b2\u9053\uff1a\u201c\u6211\u4e0e\u534e\u4e91\u78a7\u59d0\u59d0\u4e00\u89c1\u5982\u6545\uff0c\u5979\u7a81\u7136\u8d70\u4e86\uff0c\u6211\u3001\u6211\u5fc3\u91cc\u96be\u8fc7\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u4e0d\u77e5\u5c31\u91cc\uff0c\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u6211\u8fd8\u5f53\u4f60\u4eec\u5435\u67b6\u4e86\u5462\u3002\u50bb\u4e2b\u5934\uff0c\u90a3\u4f4d\u534e\u59d1 \u5a18\u662f\u6765\u53c2\u52a0\u91d1\u9e70\u5bab\u4e4b\u4f1a\u7684\uff0c\u5927\u4f1a\u5df2\u7ecf\u6563\u4e86\uff0c\u5ba2\u4eba\u4e5f\u90fd\u8d70\u4e86\uff0c\u5979\u5f53\u7136\u4e5f\u8981\u56de\u5bb6\u4e86\u3002\u5929\u4e0b\u54ea \u6709\u6c38\u4e0d\u5206\u624b\u7684\u670b\u53cb\uff0c\u96be\u9053\u5979\u8fd8\u80fd\u7559\u4e0b\u6765\u4f34\u4f60\u4e00\u8f88\u5b50\u4e48\uff1f\u4f60\u60e6\u8bb0\u5979\uff0c\u5f85\u8fd9\u91cc\u4e8b\u60c5\u4e86\u7ed3\uff0c\u4f60 \u4e0d\u4f1a\u53bb\u63a2\u8bbf\u5979\u4e48\uff1f\u53ef\u65e0\u987b\u54ed\u8d77\u6765\u5440\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u542c\u4e86\u201c\u5929\u4e0b\u54ea\u6709\u4e0d\u5206\u624b\u7684\u670b\u53cb\u201d\u8fd9\u53e5\u8bdd\uff0c\u5fc3\u5934\u6005\u89e6\uff0c\u53c8\u4e0d\u7981\u60b2\u4ece\u4e2d\u6765\uff0c\u96be\u4ee5 \u65ad\u7edd\uff0c\u60f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5929\u4e0b\u9664\u4e86\u592b\u5987\u662f\u53ef\u4ee5\u53ae\u5b88\u4e00\u8f88\u5b50\u7684\u4e4b\u5916\uff0c\u4e0d\u8bba\u600e\u6837\u8981\u597d\u7684\u670b\u53cb\uff0c \u90a3\u603b\u662f\u514d\u4e0d\u4e86\u8981\u5206\u79bb\u7684\u3002\u6211\u548c\u6d77\u54e5\u4e5f\u53ea\u662f\u6682\u65f6\u76f8\u805a\u800c\u5df2\uff0c\u603b\u4e0d\u514d\u6709\u5404\u6563\u897f\u4e1c\u7684\u4e00\u5929\u3002\u201d \u539f\u6765\u5979\u5df2\u51b3\u610f\u6210\u5168\u534e\u4e91\u78a7\u4e00\u6bb5\u59fb\u7f18\uff0c\u6709\u5fc3\u53ea\u628a\u6c5f\u6d77\u5929\u5f53\u4f5c\u670b\u53cb\u770b\u5f85\u3002\u53ef\u662f\u611f\u60c5\u5df2\u662f\u4e0d\u80fd \u7531\u5979\u81ea\u4e3b\uff0c\u5f53\u5979\u611f\u5230\u60b2\u4ece\u4e2d\u6765\uff0c\u96be\u4ee5\u65ad\u7edd\u4e4b\u65f6\uff0c\u5979\u4e5f\u611f\u5230\u5bf9\u6c5f\u6d77\u5929\u5df2\u662f\u60c5\u6839\u6df1\u79cd\u4e86\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u62b9\u53bb\u4e86\u6cea\u75d5\uff0c\u5f3a\u7b11\u8bf4\u9053\uff0c\u201c\u54e5\u54e5\uff0c\u4f60\u73b0\u5728\u53ef\u77e5\u9053\u4e86\uff0c\u4f60\u7684\u59b9\u59b9\u5c31\u662f\u8fd9\u4e48\u50bb\u7684\u3002\u201d \u8fd9\u53e5\u8bdd\u4e0d\u4f46\u662f\u8bf4\u7ed9\u5510\u52aa\u73e0\u7a46\u542c\uff0c\u4e5f\u662f\u8bf4\u7ed9\u6c5f\u6d77\u5929\u542c\u7684\uff0c\u6c5f\u6d77\u5929\u9a6e\u7136\u4e0d\u8bed\u3002\u5510\u52aa\u73e0\u7a46\u54c8\u54c8 \u7b11\u9053\uff1a\u201c\u597d\uff0c\u522b\u53d1\u50bb\u5566\uff0c\u54b1\u4eec\u8fd8\u6709\u5927\u4e8b\u8981\u529e\u5462\uff01\u90a3\u5978\u738b\u786e\u662f\u4e0d\u5728\u6b64\u5730\uff0c\u54b1\u4eec\u73b0\u5728\u9a6c\u4e0a\u56de \u738b\u5bab\u53bb\u518d\u4ed4\u7ec6\u641c\u67e5\u3002\u6c5f\u5e08\u5144\uff0c\u5e08\u7236\u5462\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5e08\u7236\u6709\u4e8b\u5148\u8d70\u4e86\uff0c\u6211\u548c\u4f60\u4eec\u4e00\u9053 \u53bb\u5427\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7559\u4e0b\u4e00\u5343\u540d\u638f\u6797\u519b\u63a5\u7ba1\u91d1\u9e70\u5bab\uff0c\u4fbf\u5e26\u9886\u5927\u961f\u518d\u56de\u738b\u5bab\uff0c\u62b5\u8fbe\u4e4b\u65f6\u3002\u5df2\u662f\u5c06 \u8fd1\u9ec4\u660f\u7684\u65f6\u5206\uff0c\u738b\u5bab\u65e9\u5df2\u88ab\u4ed6\u7684\u519b\u961f\u5168\u90e8\u5360\u9886\uff0c\u5978\u738b\u7684\u515a\u7fbd\u6216\u88ab\u6740\u3001\u6216\u88ab\u4fd8\u3001\u6216\u6295\u964d\uff0c \u4e5f\u65e9\u5df2\u5168\u90e8\u8083\u6e05\u3002\u4f46\u7ecf\u8fc7\u5c06\u8fd1\u4e00\u5929\u7684\u641c\u7d22\uff0c\u4ecd\u662f\u672a\u5f97\u90a3\u5978\u738b\u7684\u4e0b\u843d\u3002\u4e09\u4eba\u6b63\u81ea\u95f7\u95f7\u4e0d\u4e50\uff0c \u6c5f\u6d77\u5929\u5ffd\u5730\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4f60\u542c\u8fd9\u662f\u4ec0\u4e48\u58f0\u97f3\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u4eec\u8033\u7ec6\u542c\uff0c\u8bf4\u9053\uff1a\u201c\u54ce\uff0c\u8fd9\u5578 \u58f0\u662f\u4ece\u5730\u5e95\u4f20\u6765\u7684\u3002\u4f3c\u4e4e\u8fd8\u6709\u91d1\u94c1\u78b0\u51fb\u4e4b\u58f0\u3002\u5374\u4e0d\u77e5\u662f\u54ea\u4e00\u6761\u79d8\u5bc6\u5730\u9053\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff0c \u201c\u6211\u542c\u5f97\u51fa\u8fd9\u58f0\u97f3\u7684\u65b9\u5411\u662f\u5728\u4e1c\u5317\u89d2\u79bb\u6b64\u7ea6\u4e09\u91cc\u4e4b\u5730\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u5927\u54e5\u56de \u6765\u4e86\u3002\u597d\uff0c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u54b1\u4eec\u8d76\u5feb\u5bfb\u58f0\u89c5\u8ff9\u5427\uff01\u201d \u3000\u3000\u5230\u4e86\u90a3\u58f0\u97f3\u4f20\u51fa\u4e4b\u5904\uff0c\u53ea\u89c1\u4e00\u5ea7\u5047\u5c71\uff0c\u4f46\u5374\u5e76\u65e0\u5c71\u6d1e\uff0c\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u58f0\u97f3\u662f\u4ece\u5730 \u5e95\u4f20\u51fa\u6765\u7684\uff0c\u8fd9\u5ea7\u5047\u5c71\u4e0b\u9762\uff0c\u4e00\u5b9a\u6709\u6761\u5730\u9053\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u8fd9\u4e9b\u79d8\u5bc6\u5730\u9053\u90fd\u662f\u5978\u738b \u540e\u6765\u5efa\u7b51\u7684\uff0c\u6211\u7684\u5730\u56fe\u4e0a\u627e\u4e0d\u5230\u3002\u201d\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u53ea\u542c\u5f97\u5730\u5e95\u4e0b\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\u66f4\u662f\u8d8a\u6765\u8d8a \u6e05\u695a\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7528\u201c\u5929\u9041\u4f20\u97f3\u201d\u4e4b\u672f\uff0c\u4f0f\u5730\u53eb\u9053\uff1a\u201c\u662f\u53f6\u5927\u54e5\u5417\uff1f\u6211\u4eec\u6765\u4e86\uff01\u201d\u5730\u4e0b\u4f20\u6765\u4e00 \u58f0\u957f\u5578\uff0c\u5510\u52aa\u73e0\u7a46\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u679c\u7136\u662f\u5927\u54e5\u7684\u5578\u58f0\uff0c\u542c\u6765\u4f3c\u4e4e\u662f\u53d7\u4e86\u70b9\u4f24\u3002\u201d\u4ed6 \u4eec\u627e\u4e0d\u5230\u5730\u9053\u7684\u8fdb\u53e3\uff0c\u7a7a\u81ea\u7740\u6025\uff0c\u65e0\u8ba1\u53ef\u65bd\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u91d1\u94c1\u78b0\u51fb\u4e4b\u58f0\u5df2\u7136\u505c\u6b62\uff0c\u5510\u52aa\u73e0\u7a46\u4f0f\u5730\u542c\u58f0\uff0c\u53ea\u9690\u9690\u542c\u5f97\u6709\u65ad\u65ad\u7eed\u7eed\u5730 \u547b\u541f\uff0c\u5374\u96be\u4ee5\u5206\u8fa8\u5230\u5e95\u662f\u8c01\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5fc3\u6025\u5982\u711a\uff0c\u8df3\u8d77\u6765\u9053\uff1a\u201c\u627e\u4e0d\u5230\u5730\u9053\u7684\u8fdb\u53e3\uff0c\u6211\u552f\u6709\u53ec\u96c6\u5fa1\u6797\u519b\u6765\u53d1\u6398\u4e86\u3002\u201d \u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u201c\u8f67\u8f67\u201d\u58f0\u54cd\uff0c\u5047\u5c71\u5f53\u4e2d\u7684\u4e24\u5757\u5927\u77f3\u5ffd\u7136\u5de6\u53f3\u5206\u5f00\uff0c\u73b0\u51fa\u4e00\u4e2a\u5c71\u6d1e\u3002\u4e09 \u4eba\u94bb\u8fdb\u6d1e\u53e3\uff0c\u90a3\u9ed1\u9edd\u9edd\u7684\u5c71\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u6df1\uff0c\u5510\u52aa\u73e0\u7a46\u70b9\u8d77\u706b\u628a\u4e00\u7167\uff0c\u5374\u89c1\u6709\u77f3\u7ea7\u53ef\u4ee5 \u4e0b\u53bb\uff0c\u4f46\u4ecd\u7136\u4e0d\u89c1\u6709\u4eba\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7a0d\u7a0d\u5bbd\u5fc3\uff0c\u4f46\u4ecd\u662f\u4e0d\u514d\u62c5\u5fe7\uff0c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u60f3\u5fc5\u662f\u5927\u54e5\u5728\u91cc\u9762\u5f00\u52a8\u673a\u5173\uff0c \u8ba9\u6211\u4eec\u8fdb\u6765\u7684\u3002\u4f46\u4ed6\u76f4\u5230\u6b64\u65f6\uff0c\u8fd8\u4e0d\u51fa\u6765\uff0c\u53ea\u6015\u662f\u591a\u534a\u53d7\u4e86\u91cd\u4f24\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u53cd \u6b63\u4e0d\u4e45\u5c31\u53ef\u5206\u6653\uff0c\u54b1\u4eec\u8fd8\u662f\u53bb\u770b\u770b\u5427\u3002\u201d \u3000\u3000\u8d70\u4e0b\u4e86\u767e\u591a\u6b65\u77f3\u7ea7\uff0c\u8fce\u9762\u662f\u4e00\u9053\u94c1\u95e8\uff0c\u95e8\u5185\u4f20\u51fa\u4e86\u51e0\u58f0\u54b3\uff0c\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6d3b\u7740\u7684\u4e0d \u6b62\u4e00\u4eba\uff0c\u8fd9\u54b3\u55fd\u58f0\u6709\u70b9\u5947\u602a\u3002\u201d\u5510\u52aa\u73e0\u7a46\u6572\u95e8\u9053\uff0c\u201c\u5927\u54e5\uff0c\u6211\u4eec\u6765\u4e86\u3002\u201d \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u6c99\u54d1\u7684\u58f0\u97f3\u9053\uff1a\u201c\u8bf7\u8fdb\u6765\u5427\uff01\u201d\u8fd9\u9053\u94c1\u95e8\u5927\u7ea6\u662f\u6ca1\u6709\u673a\u5173\u7684\uff0c \u9700\u8981\u91cc\u9762\u7684\u4eba\u7528\u529b\u63a8\u5f00\uff0c\u5510\u52aa\u73e0\u7a46\u7b49\u4eba\u5728\u5916\u9762\u53ef\u4ee5\u9690\u7ea6\u542c\u5f97\u53f6\u51b2\u9704\u7684\u5598\u6c14\u58f0\uff0c\u4f46\u8fc7\u4e86\u4e00 \u4f1a\uff0c\u90a3\u9053\u94c1\u95e8\u4e5f\u7ec8\u4e8e\u6162\u6162\u6253\u5f00\u4e86\u3002 \u3000\u3000\u94c1\u95e8\u4e00\u5f00\uff0c\u4f17\u4eba\u4f46\u89c9\u773c\u775b\u4e00\u4eae\uff0c\u539f\u6765\u91cc\u9762\u73e0\u5b9d\u5806\u79ef\u5982\u5c71\uff0c\u5b9d\u6c14\u73e0\u5149\uff0c\u8000\u773c\u751f\u8f89\u3002\u73e0 \u5149\u5b9d\u6c14\u4e4b\u95f4\uff0c\u53c8\u9690\u7ea6\u6709\u8ff7\u79bb\u7684\u70df\u96fe\uff0c\u6c14\u5473\u96be\u95fb\u3002 \u3000\u3000\u8fd9\u4e9b\u73e0\u5b9d\u8fd8\u4e0d\u8db3\u4ee4\u4ed6\u4eec\u60ca\u5f02\uff0c\u60ca\u5f02\u7684\u662f\u91cc\u9762\u7684\u666f\u8c61\u548c\u4eba\u7269\uff0c\u53ea\u89c1\u65f6\u51b2\u9704\u6276\u7740\u4e00\u4e2a\u5973 \u5b50\u98a4\u5dcd\u5dcd\u7684\u5411\u4ed6\u4eec\u8d70\u6765\uff0c\u6da9\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u6765\u4e86\u5f88\u597d\uff0c\u90a3\u5978\u738b\u5df2\u7ecf\u6b7b\u5728\u8fd9\u513f\u4e86\uff01\u54b1\u5bb6\u7684 \u4ec7\u5df2\u7ecf\u62a5\u4e86\uff0c\u4e8c\u5f1f\uff0c\u4ee5\u540e\u7684\u4e8b\u60c5\u5c31\u662f\u4f60\u7684\u5566\uff01\u201d\u5510\u52aa\u73e0\u7a46\u65e0\u6687\u7ec6\u60f3\u4ed6\u8bdd\u4e2d\u7684\u542b\u610f\uff0c\u5148\u671d \u7740\u4ed6\u6240\u6307\u7684\u65b9\u5411\u770b\u53bb\uff0c\u53ea\u89c1\u90a3\u5978\u738b\u76d6\u6e29\u5012\u5728\u5730\u4e0a\uff0c\u5728\u4ed6\u7684\u8eab\u8fb9\u8fd8\u6709\u4e24\u5177\u5c38\u4f53\uff0c\u662f\u76d6\u6e29\u6700 \u5f97\u529b\u7684\u6b66\u58eb\u9c81\u6c0f\u5144\u5f1f\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9996\u5148\u6ce8\u610f\u90a3\u5978\u738b\u76d6\u6e29\uff0c\u6c5f\u6d77\u5929\u548c\u8c37\u4e2d\u83b2\u5374\u9996\u5148\u6ce8\u610f\u90a3\u4e2a\u5973\u5b50\uff0c\u8fd9\u5973\u5b50\u4e0d\u662f \u522b\u4eba\uff0c\u6b63\u662f\u6b27\u9633\u5a49\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u56fa\u7136\u60ca\u5f02\uff0c\u4f46\u4e5f\u8fd8\u7f62\u4e86\u3002\u6c5f\u6d77\u5929\u5374\u662f\u5fc3\u5934\u4e00\u9707\uff0c\u53c8\u559c\u53c8\u60ca\uff0c\u4ed6\u662f\u6628\u665a\u548c\u6b27\u9633 \u5a49\u540c\u65f6\u5760\u4e0b\u53e6\u4e00\u5904\u5730\u9053\u7684\u9677\u9631\u7684\uff0c\u90a3\u65f6\u5929\u9b54\u6559\u4e3b\u7528\u8be1\u8ba1\u8bf1\u4ed6\u5760\u4e0b\u9677\u9631\uff0c\u4ed6\u548c\u5929\u9b54\u6559\u4e3b\u548c \u6b27\u9633\u5a49\u90fd\u53d7\u4e86\u4f24\u3002\u5760\u4e0b\u9677\u9631\u4e4b\u540e\uff0c\u5929\u9b54\u6559\u4e3b\u5373\u628a\u6b27\u9633\u5a49\u4e0e\u4ed6\u9694\u79bb\uff0c\u4ed6\u77e5\u9053\u6b27\u9633\u5a49\u5df2\u662f\u4e0d \u7701\u4eba\u4e8b\uff0c\u4f46\u5374\u65e0\u6cd5\u6551\u5979\u3002 \u3000\u3000\u540e\u6765\u6c5f\u6d77\u5929\u9003\u51fa\u5730\u9053\uff0c\u5de7\u9047\u534e\u4e91\u78a7\u4ece\u5929\u800c\u964d\uff0c\u66ff\u4ed6\u62d4\u6bd2\u7597\u4f24\uff0c\u4ed6\u624d\u5f97\u4ee5\u53ca\u65f6\u53c2\u52a0\u91d1 \u9e70\u5b98\u4e4b\u4f1a\uff0c\u81f3\u4e8e\u6b27\u9633\u5a49\u5219\u4ecd\u7559\u5728\u5730\u9053\u4e4b\u4e2d\u3002\u6c5f\u6d77\u5929\u4e0d\u77e5\u5979\u751f\u6b7b\u5982\u4f55\uff0c\u5fc3\u4e2d\u4e00\u76f4\u6302\u5ff5\u3002\u60f3 \u4e0d\u5230\u5979\u5374\u662f\u4e0e\u53f6\u51b2\u9704\u4e00\u8d77\uff0c\u540c\u5728\u8fd9\u5b9d\u5e93\u4e4b\u4e2d\u3002 \u3000\u3000\u539f\u6765\u6628\u665a\u53f6\u51b2\u9704\u72ec\u81ea\u7559\u4e0b\u6765\uff0c\u5728\u738b\u5bab\u91cc\u5230\u5904\u63a2\u67e5\u6c5f\u6d77\u5929\u7684\u4e0b\u843d\u3002\u53f6\u51b2\u9704\u77e5\u9053\u5404\u5904\u79d8 \u5bc6\u5730\u9053\u7684\u6240\u5728\uff0c\u7ec8\u4e8e\u627e\u5230\u4e86\u6c5f\u6d77\u5929\u4ed6\u4eec\u9677\u843d\u7684\u90a3\u6761\u5730\u9053\uff0c\u53d1\u73b0\u4e86\u5929\u9b54\u6559\u4e3b\u548c\u6b27\u9633\u5a49\uff0c\u5176 \u65f6\u5929\u9b54\u6559\u4e3b\u5143\u6c14\u672a\u66fe\u6062\u590d\uff0c\u4e0d\u6562\u4e0e\u53f6\u51b2\u9704\u4ea4\u624b\uff0c\u53ea\u597d\u653e\u4e86\u4e2a\u70df\u96fe\u5f39\u4f5c\u4e3a\u63a9\u62a4\uff0c\u5306\u5306\u9003\u53bb\uff0c \u6b27\u9633\u5a49\u5219\u88ab\u4ed6\u62a2\u6551\u51fa\u6765\u3002 \u3000\u3000\u6b27\u9633\u5a49\u53d7\u4f24\u4e0d\u8f7b\uff0c\u5e78\u800c\u53f6\u51b2\u9704\u4ee5\u672c\u8eab\u529f\u529b\u66ff\u5979\u63a8\u8840\u8fc7\u5bab\uff0c\u5979\u624d\u80fd\u591f\u6062\u590d\u884c\u52a8\u3002\u8fd9\u65f6 \u5bab\u4e2d\u6b63\u5728\u6df7\u6218\uff0c\u5730\u9053\u4e0b\u9690\u9690\u53ef\u95fb\u3002\u53f6\u51b2\u9704\u6068\u6781\u90a3\u5978\u738b\u76d6\u6e29\uff0c\u6599\u60f3\u90a3\u76d6\u6e29\u5fc5\u7136\u4e0d\u80af\u820d\u5f03\u73cd \u5b9d\uff0c\u5728\u9003\u4ea1\u7684\u524d\u523b\uff0c\u5b9a\u4f1a\u5230\u90a3\u5b9d\u5e93\u53bb\uff0c\u5e26\u4e00\u4e9b\u6700\u503c\u94b1\u7684\u73cd\u5ba4\uff0c\u7136\u540e\u624d\u4ece\u5b9d\u5e93\u4e2d\u7684\u79d8\u9053\u9003 \u8d70\u3002\u53f6\u51b2\u9704\u65e2\u7136\u6599\u5230\u4ed6\u6709\u6b64\u4e00\u7740\uff0c\u9042\u5148\u53d1\u5236\u4eba\uff0c\u5230\u5b9d\u5e93\u4e2d\u8eb2\u85cf\u8d77\u6765\uff0c\u7b49\u5019\u90a3\u5978\u738b\u81ea\u6295\u7f57 \u7f51\u3002\u6b27\u9633\u5a49\u4f24\u8fd8\u672a\u6108\uff0c\u79bb\u4e0d\u5f00\u4ed6\uff0c\u5f53\u7136\u4e5f\u53ea\u597d\u8ddf\u7740\u4ed6\u4e00\u540c\u85cf\u5728\u5b9d\u5e93\u4e2d\u4e86\u3002 \u3000\u3000\u53f6\u51b2\u9704\u662f\u6709\u5148\u89c1\u4e4b\u660e\u3002\u90a3\u5978\u738b\u679c\u7136\u6765\u4e86\u3002\u4f46\u6709\u4e00\u70b9\u53f6\u51b2\u9704\u5374\u6ca1\u6709\u6599\u5230\uff0c\u90a3\u597d\u738b\u5e26\u4e86 \u4ed6\u7684\u5fc3\u8179\u6b66\u571f\u9c81\u6c0f\u5144\u5f1f\u540c\u6765\uff0c\u522b\u7684\u6b66\u58eb\u53f6\u51b2\u9704\u53ef\u4ee5\u8f7b\u6613\u6253\u53d1\uff0c\u8fd9\u65f6\u9c81\u6c0f\u5144\u5f1f\u90fd\u662f\u975e\u540c\u5c0f \u53ef\uff0c\u5373\u4f7f\u53f6\u51b2\u9704\u672a\u66fe\u6d88\u8017\u529f\u529b\u4e3a\u6b27\u9633\u5a49\u6cbb\u4f24\uff0c\u4e5f\u672a\u5fc5\u662f\u4ed6\u4eec\u5144\u5f1f\u7684\u5bf9\u624b\u3002 \u3000\u3000\u4e00\u573a\u6fc0\u6218\uff0c\u9c81\u6c0f\u5144\u5f1f\u7740\u4e86\u4ed6\u7684\u5927\u4e58\u822c\u82e5\u638c\uff0c\u4ed6\u4e5f\u88ab\u9c81\u6c0f\u5144\u5f1f\u6253\u4f24\uff0c\u53cc\u65b9\u90fd\u5728\u6d74\u8840\u82e6 \u6597\uff0c\u5371\u9669\u4e07\u72b6\u3002\u5e78\u4e8f\u6b27\u9633\u5a49\u4e0d\u987e\u6027\u547d\uff0c\u51fa\u6765\u76f8\u52a9\uff0c\u7528\u6bd2\u96fe\u91d1\u9488\u70c8\u7130\u5f39\u5c06\u9c81\u6c0f\u5144\u5f1f\u6253\u5f97\u91cd \u4f24\uff0c\u53f6\u51b2\u9704\u624d\u8d62\u5f97\u6700\u540e\u7684\u80dc\u5229\u3002\u9c81\u6c0f\u5144\u5f1f\u91cd\u4f24\u6bd9\u547d\uff0c\u597d\u738b\u76d6\u6e29\u5438\u8fdb\u6bd2\u96fe\uff0c\u4e0d\u5f85\u53f6\u51b2\u9704\u6740 \u4ed6\uff0c\u4fbf\u5df2\u7a92\u606f\u800c\u6b7b\u3002 \u3000\u3000\u4e14\u8bf4\u5510\u52aa\u73e0\u7a46\u4e0e\u6c5f\u6d77\u5929\u5728\u5b9d\u5e93\u4e2d\u53d1\u73b0\u4e86\u4ed6\u4eec\uff0c\u90fd\u662f\u53c8\u60ca\u53c8\u559c\u3002\u5510\u52aa\u73e0\u7a46\u662f\u559c\u5927\u4ec7\u5f97 \u62a5\uff0c\u60ca\u5144\u957f\u53d7\u4f24\uff0c\u6c5f\u6d77\u5929\u5219\u662f\u5f97\u89c1\u6b27\u9633\u5a49\u5c1a\u8fd8\u6d3b\u7740\uff0c\u6545\u800c\u559c\u51fa\u671b\u5916\u3002\u53ef\u662f\u4ed6\u89c1\u6b27\u9633\u5a49\u6c14 \u606f\u5944\u5944\uff0c\u5374\u4e5f\u4e0d\u7981\u5185\u759a\u4e8e\u5fc3\uff0c\u540c\u65f6\u521a\u521a\u8d70\u4e86\u4e2a\u534e\u4e91\u78a7\uff0c\u53c8\u78b0\u4e0a\u4e86\u4e2a\u6b27\u9633\u5a49\uff0c\u9ebb\u70e6\u771f\u53ef\u8bf4 \u662f\u8d8a\u6765\u8d8a\u591a\uff0c\u4e5f\u4e0d\u77e5\u8c37\u4e2d\u83b2\u80fd\u5426\u8c05\u89e3\uff1f\u6b64\u65f6\u6b64\u9645\uff0c\u6c5f\u6d77\u5929\u7684\u5fc3\u60c5\u7aef\u7684\u662f\u590d\u6742\u4e4b\u6781\uff0c\u65e2\u5e0c \u671b\u89c1\u5230\u6b27\u9633\u5a49\uff0c\u5374\u53c8\u6709\u70b9\u6015\u89c1\u5230\u5979\u3002 \u3000\u3000\u5b9d\u5e93\u4e2d\u6bd2\u96fe\u5f25\u6f2b\uff0c\u6b27\u9633\u5a49\u867d\u7136\u9884\u5148\u670d\u4e0b\u89e3\u836f\uff0c\u5728\u53d7\u4f24\u4e4b\u540e\uff0c\u4e5f\u81ea\u89c9\u5f97\u547c\u5438\u56f0\u96be\u3002\u5510 \u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6b64\u5730\u4e0d\u53ef\u4e45\u7559\uff0c\u5927\u54e5\uff0c\u54b1\u4eec\u5230\u5916\u9762\u8bf4\u8bdd\u53bb\u3002\u201d\u4ed6\u8d70\u8fc7\u53bb\u6276\u6396\u53f6\u51b2\u9704\uff0c\u8c37\u4e2d \u83b2\u5374\u8d70\u8fc7\u53bb\u6276\u6b27\u9633\u5a49\uff0c\u5411\u6b27\u9633\u5a49\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u6b27\u9633\u59d1\u5a18\uff0c\u4f60\u4e0a\u6b21\u6551\u4e86\u6d77\u5929\uff0c\u8fd9\u6b21\u53c8\u5168\u9760 \u4f60\u7684\u5e2e\u5fd9\uff0c\u6211\u4eec\u624d\u5f97\u4ee5\u62a5\u4e86\u5927\u4ec7\uff0c\u6211\u771f\u4e0d\u77e5\u8be5\u5982\u4f55\u611f\u8c22\u4f60\u624d\u597d\uff01\u201d \u3000\u3000\u6b27\u9633\u5a49\u661f\u7738\u534a\u542f\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u83b2\u59b9\uff0c\u8fd9\u6709\u4ec0\u4e48\u503c\u5f97\u591a\u8c22\u7684\uff1f\u6211\u53d7\u8fc7\u4f60\u4eec\u7684 \u597d\u5904\u4e5f\u4e0d\u5c11\u5462\uff01\u53ea\u6c42\u4f60\u4e0d\u518d\u8bb0\u65e7\u6068\uff0c\u6211\u5df2\u662f\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u89c1\u5979\u4e00\u56e2\u548c\u6c14\uff0c\u548c\u90a3\u65e5\u8981\u7528\u6bd2\u9488\u523a\u5979\u7684\u90a3\u4e2a\u6b27\u9633\u5a49\uff0c\u7b80\u76f4\u5c31\u50cf\u662f\u4e24\u4e2a\u4eba\uff0c\u5fc3 \u4e2d\u81ea\u662f\u5f88\u4e3a\u9ad8\u5174\uff0c\u4f46\u5374\u4e5f\u6709\u70b9\u5947\u602a\uff01\u56e0\u4e3a\u6309\u7167\u5979\u4eec\u4e8c\u4eba\u7684\u5173\u7cfb\u800c\u8bba\uff0c\u867d\u8bf4\u73b0\u5728\u5df2\u7ecf\u5316\u654c \u4e3a\u53cb\uff0c\u4f46\u7a76\u975e\u719f\u7a14\uff0c\u4e5f\u8fd8\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u6df1\u5207\u7684\u4ea4\u60c5\uff0c\u56e0\u6b64\u8c37\u4e2d\u83b2\u624d\u4f1a\u5728\u9ad8\u5174\u4e4b\u4e2d\u4e5f\u611f\u5230\u5947 \u602a\uff0c\u6b27\u9633\u5a49\u90a3\u4e00\u58f0\u201c\u83b2\u59b9\u201d\u4f3c\u4e4e\u672a\u514d\u53eb\u5f97\u201c\u4eb2\u70ed\u201d\u4e86\u4e00\u4e9b\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6276\u7740\u53f6\u51b2\u9704\uff0c\u8c37\u4e2d\u83b2\u6276\u7740\u6b27\u9633\u5a49\uff0c\u4f46\u53f6\u51b2\u9704\u4ecd\u662f\u7d27\u7d27\u63e1\u7740\u6b27\u9633\u5a49\u7684\u624b\uff0c\u59cb \u7ec8\u6ca1\u6709\u5206\u5f00\uff0c\u8fd9\u65f6\u4ed6\u4eec\u5df2\u8d70\u51fa\u5b9d\u5e93\uff0c\u53f6\u51b2\u9704\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u83b2\u59b9\uff0c\u4eca\u540e\u5f7c \u6b64\u90fd\u662f\u4e00\u5bb6\u4eba\u4e86\uff0c\u4f60\u4eec\u4e5f\u4e0d\u5fc5\u4e92\u76f8\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5446\u4e86\u4e00\u5446\uff0c\u84e6\u5730\u604d\u7136\u5927\u609f\uff09\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u4e48\u8bf4\uff0c\u6b27\u9633\u59d1\u5a18\u662f\u6211\u7684\u5ac2\u5b50\u4e86\uff1f\u201d \u53f6\u51b2\u9704\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b27\u9633\u59d1\u5a18\u5df2\u7ecf\u7b54\u5e94\u6211\u4e86\uff01\u6211\u8fc7\u53bb\u505a\u9519\u8bb8\u591a\u4e8b\u60c5\uff0c\u6b27\u9633 \u59d1\u5a18\u4e5f\u505a\u9519\u8bb8\u591a\u4e8b\u60c5\uff0c\u4f46\u662f\u6211\u77e5\u9053\u4f60\u4eec\u4f1a\u539f\u8c05\u6211\u4eec\u7684\u3002\u54c8\uff0c\u5a49\u59b9\uff0c\u4f60\u77a7\u6211\u6ca1\u6709\u8bf4\u9519\u5427\uff01 \u4ed6\u4eec\u4e0d\u662f\u90fd\u53eb\u4f60\u5ac2\u5b50\u4e86\u5417\uff1f\u201d \u3000\u3000\u539f\u6765\u53f6\u51b2\u9704\u6df1\u6df1\u6094\u6068\u81ea\u5df1\u5bf9\u4e0d\u4f4f\u6b27\u9633\u5a49\u7684\u59d0\u59d0\uff0c\u6b27\u9633\u5a49\u4e5f\u5df2\u77e5\u9053\u6c5f\u6d77\u5929\u4e00\u5fc3\u4e00\u610f\u7231 \u7684\u662f\u8c37\u4e2d\u83b2\uff0c\u4e24\u4eba\u540c\u75c5\u76f8\u601c\uff0c\u6240\u4ee5\u5728\u53f6\u51b2\u9704\u5411\u5979\u6c42\u5a5a\u7684\u65f6\u5019\uff0c\u5979\u4fbf\u4e00\u53e3\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u5728\u53f6\u51b2\u9704\u662f\u5c06\u5bf9\u6b27\u9633\u6e05\u7684\u4e00\u7247\u5fcf\u6094\u4e4b\u60c5\uff0c\u79fb\u6765\u7231\u5979\u7684\u59b9\u59b9\u6b27\u9633\u5a49\uff0c\u540c\u65f6\u4e5f\u662f\u4e3a\u4e86\u62a5 \u7b54\u6b27\u9633\u5a49\u5bf9\u4ed6\u7684\u6551\u547d\u4e4b\u6069\u3002\u5728\u6b27\u9633\u5a49\u5219\u662f\u4e3a\u4e86\u8981\u6210\u5168\u6c5f\u6d77\u5929\u4e0e\u8c37\u4e2d\u83b2\u7684\u597d\u4e8b\uff0c\u4e0d\u613f\u518d\u63d2 \u5728\u4ed6\u4eec\u4e2d\u95f4\u505a\u4e00\u4e2a\u201c\u7b2c\u4e09\u8005\u201d\uff0c\u7834\u574f\u4ed6\u4eec\u7684\u7231\u60c5\uff0c\u5f52\u6839\u5230\u5e95\uff0c\u8fd9\u4e5f\u8fd8\u662f\u4e3a\u4e86\u7231\u6c5f\u6d77\u5929\u4e4b \u6545\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u53f6\u51b2\u9704\u4e0e\u6b27\u9633\u5a49\u4e4b\u95f4\u7684\u7231\u60c5\uff0c\u867d\u7136\u6742\u6709\u8bb8\u591a\u56e0\u7d20\uff0c\u4e5f\u4f3c\u4e4e\u6765\u5f97\u5f88\u662f\u7a81\u7136\uff0c\u4f46 \u5176\u5b9e\u4ed6\u4eec\u4e4b\u95f4\u4e5f\u8fd8\u662f\u6709\u5171\u901a\u70b9\u7684\uff0c\u4ed6\u4eec\u90fd\u5e26\u6709\u70b9\u201c\u90aa\u201d\u6c14\u3002\u4f46\u53c8\u540c\u6837\u662f\u6027\u60c5\u4e2d\u4eba\uff0c\u786e\u5b9e \u53ef\u4ee5\u8bf4\u662f\u6c14\u5473\u76f8\u6295\u7684\u3002\u6b27\u9633\u5a49\u5728\u7b54\u5e94\u53f6\u51b2\u9704\u6c42\u5a5a\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u81ea\u5df1\u4e5f\u66fe\u7ecf\u60f3\u8fc7\uff0c\u62ff\u6c5f\u6d77 \u5929\u6765\u4e0e\u53f6\u51b2\u9704\u76f8\u6bd4\uff0c\u53f6\u51b2\u9704\u662f\u4e0e\u5979\u6295\u5408\u591a\u4e86\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u3001\u8c37\u4e2d\u83b2\u90fd\u4e0a\u6765\u5411\u4ed6\u4eec\u9053\u8d3a\uff0c\u6c5f\u6d77\u5929\u8ddf\u7740\u4e5f\u53eb\u4e86\u6b27\u9633\u5a49\u4e00\u58f0\u201c\u5927\u5ac2\u201d\uff0c\u6b27 \u9633\u5a49\u773c\u6ce2\u4e00\u8f6c\uff0c\u4ece\u4ed6\u7684\u8138\u4e0a\u63a0\u8fc7\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5f7c\u6b64\u90fd\u662f\u4e00\u5bb6\u4eba\u4e86\uff0c\u6211\u4e5f\u7b49\u7740\u559d\u4f60\u4e0e\u83b2\u59b9\u7684\u559c\u9152\u5462\uff01\u201d\u5979\u7684\u58f0\u97f3\u5fae\u5fae\u98a4\u6296\uff0c\u773c\u5149 \u4e2d\u6709\u975e\u5e38\u590d\u6742\u7684\u611f\u60c5\u3002\u56e0\u4e3a\u5979\u6b63\u5728\u53d7\u4f24\u4e4b\u540e\uff0c\u58f0\u97f3\u98a4\u6296\uff0c\u4eba\u4eba\u90fd\u4e0d\u4ee5\u4e3a\u610f\uff0c\u5979\u773c\u5149\u4e2d\u6240 \u8574\u85cf\u7684\u590d\u6742\u611f\u60c5\uff0c\u4e5f\u53ea\u6709\u6c5f\u6d77\u5929\u624d\u80fd\u660e\u767d\u3002\u8fd9\u4e00\u77ac\u95f4\uff0c\u6c5f\u6d77\u5929\u4e5f\u4e0d\u7981\u5fc3\u5934\u4e00\u9707\uff0c\u5728\u5fc3\u5e95 \u6df1\u6df1\u611f\u6fc0\u6b27\u9633\u5a49\u3002\u8c37\u4e2d\u83b2\u542c\u4e86\u5979\u7684\u8bf4\u8bdd\uff0c\u8138\u4e0a\u5374\u662f\u4e00\u7247\u6655\u7ea2\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6d77\u54e5\u8bf4\u5f97\u4e0d\u9519\uff0c\u6b27\u9633\u59d1\u5a18\u679c\u7136\u662f\u4e2a\u597d\u4eba\u3002\u201d\u5979\u548c\u53f6\u51b2\u9704\u5404\u81ea\u62c9 \u7740\u6b27\u9633\u5a49\u7684\u4e00\u53ea\u624b\uff0c\u5ffd\u89c9\u5979\u7684\u624b\u5fc3\u51b0\u51b7\uff0c\u8109\u606f\u82e5\u65ad\u82e5\u7eed\uff0c\u8c37\u4e2d\u83b2\u60ca\u9053\uff1a\u201c\u6b27\u9633\u59d0\u59d0\uff0c\u4f60 \u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u5583\u5583\u8bf4\u9053\uff1a\u201c\u6211\u5f88\u9ad8\u5174\uff0c\u6211\u5f88\u9ad8\u5174\uff0c\u4f60\u4eec\u90fd\u5bf9\u6211\u8fd9\u6837\u597d\uff0c\u8fd9\u6837\u597d\uff0c\u6211\u8981\u8d70\u5566\uff0c \u6211\u8981\u8d70\u5566\uff01\u2026\u2026\u201d\u58f0\u97f3\u4f4e\u5f97\u53ea\u6709\u5728\u5979\u65c1\u8fb9\u7684\u53f6\u3001\u8c37\u4e8c\u4eba\u624d\u542c\u5f97\u89c1\uff0c\u76ee\u5149\u65e0\u795e\uff0c\u773c\u76ae\u7f13\u7f13 \u9616\u4e0b\uff0c\u53f6\u51b2\u9704\u53eb\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u6211\u5728\u8fd9\u513f\uff0c\u4f60\u4e0d\u80fd\u8d70\uff01\u201d\u5ffd\u5730\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u5148\u6655\u8fc7 \u53bb\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e0e\u534e\u5929\u98ce\u7236\u5973\u76f8\u5904\u8fc7\u4e00\u4e9b\u65f6\u65e5\uff0c\u7565\u4e3a\u61c2\u5f97\u4e00\u70b9\u533b\u7406\uff0c\u66ff\u65f6\u51b2\u9704\u548c\u6b27\u9633\u5a49\u628a\u4e86 \u628a\u8109\uff0c\u8bf4\u9053\uff1a\u201c\u53f6\u5927\u54e5\u5e76\u65e0\u5927\u788d\uff0c\u4ed6\u662f\u4e45\u6218\u75b2\u52b3\uff0c\u7a81\u7136\u53d7\u4e86\u60ca\u5413\uff0c\u8fd9\u624d\u6655\u5012\u7684\uff0c\u8ba9\u4ed6\u6b47 \u606f\u4e00\u4f1a\uff0c\u5c31\u4f1a\u9192\u8f6c\u3002\u6b27\u9633\u59d1\u5a18\u53d7\u7684\u4f24\u6bd4\u8f83\u91cd\u4e00\u4e9b\uff0c\u8fd8\u4e2d\u4e86\u4e00\u70b9\u6bd2\uff0c\u5e78\u4e8f\u6211\u8eab\u4e0a\u8fd8\u6709\u4e00\u9897 \u5c0f\u8fd8\u4e39\u3002\u201d\u8fd9\u9897\u5c0f\u8fd8\u4e39\uff0c\u6628\u665a\u5728\u90a3\u5730\u9053\u4e4b\u4e2d\uff0c\u4ed6\u672c\u6765\u662f\u51c6\u5907\u7ed9\u6b27\u9633\u5a49\u670d\u7684\uff0c\u4f46\u5f53\u65f6\u88ab\u5929 \u9b54\u6559\u4e3b\u9694\u5f00\uff0c\u672a\u80fd\u5982\u613f\u3002\u5982\u4eca\u4ed6\u638f\u51fa\u4e86\u8fd9\u9897\u5c0f\u8fd8\u4e39\uff0c\u60f3\u8d77\u53ea\u662f\u4e00\u665a\u4e4b\u9694\uff0c\u4eba\u4e8b\u5df1\u662f\u53d8\u5316 \u5f97\u5927\u5927\u51fa\u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\uff0c\u4ed6\u7684\u5fc3\u60c5\u4e5f\u548c\u6628\u665a\u5927\u4e0d\u76f8\u540c\u4e86\u3002 \u3000\u3000\u4ed6\u671b\u4e86\u6b27\u9633\u5a49\u4e00\u773c\uff0c\u5374\u628a\u5c0f\u8fd8\u4e39\u4ea4\u7ed9\u4e86\u8c37\u4e2d\u83b2\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u6316\u5f00\u5979\u7684\u7259\u5173\uff0c\u8ba9\u5979\u670d\u4e0b\uff0c\u7136\u540e\u4f60\u518d\u66ff\u5979\u63a8\u8840\u8fc7\u5bab\u3002\u201d\u8c37\u4e2d\u83b2\u5fc3\u91cc\u6697\u6697\u597d\u7b11\uff1a \u201c\u50bb\u54e5\u54e5\uff0c\u8fd9\u4e2a\u65f6\u5019\u4f60\u8fd8\u4f55\u987b\u907f\u5acc\uff0c\u96be\u9053\u6211\u8fd8\u4f1a\u4e0d\u76f8\u4fe1\u4f60\u5417\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u670d\u4e86\u5c0f\u8fd8\u4e39\uff0c\u53c8\u5f97\u8c37\u4e2d\u83b2\u66ff\u4ed6\u63a8\u8840\u8fc7\u5bcc\uff0c\u9762\u8272\u6e10\u89c1\u7ea2\u6da6\uff0c\u60a0\u60a0\u9192\u8f6c\uff0c\u89c1\u53f6\u51b2 \u9704\u5012\u5728\u5979\u7684\u8eab\u8fb9\uff0c\u53c8\u5403\u4e86\u4e00\u60ca\uff0c\u8c37\u4e2d\u83b2\u8fde\u5fd9\u5bf9\u5979\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u5c31\u4f1a\u9192\u6765\u7684\uff0c\u4f60\u4e0d\u7528\u62c5\u6270\uff0c \u4ed6\u53ea\u662f\u75b2\u52b3\u8fc7\u751a\uff0c\u4e00\u65f6\u865a\u8131\u3002\u201d \u3000\u3000\u6b27\u9633\u5a49\u773c\u5149\u4e00\u8f6c\uff0c\u6c5f\u6d77\u5929\u7684\u773c\u5149\u521a\u521a\u907f\u5f00\uff0c\u6b27\u9633\u5a49\u9053\uff1a\u201c\u6d77\u5929\uff0c\u591a\u8c22\u4f60\u7684\u5c0f\u8fd8\u4e39\u4e86\u3002\u201d \u5979\u8bc6\u5f97\u5c0f\u8fd8\u4e39\u7684\u836f\u6027\uff0c\u9192\u6765\u4e4b\u540e\uff0c\u5df1\u81ea\u611f\u89c9\u5f97\u5230\u3002\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u503c\u5f97\u591a\u8c22\u7684\u3002 \u4ece\u524d\u548c\u6211\u4e49\u7236\u5728\u6c34\u4e91\u5e86\u7684\u65f6\u5019\uff0c\u4f60\u4e0d\u662f\u4e5f\u66fe\u7ed9\u6211\u4eec\u9001\u8fc7\u89e3\u836f\u6765\u5417\uff1f\u201d \u3000\u3000\u6b27\u9633\u5a49\u9053\uff1a\u201c\u554a\uff0c\u4f60\u5df2\u7ecf\u77e5\u9053\u662f\u6211\u4e86\uff1f\u201d\u6c5f\u6d77\u5929\u5fae\u7b11\u9053\uff1a\u201c\u5f53\u7136\u77e5\u9053\uff01\u201d\u6b27\u9633\u5a49\u82b3 \u5fc3\u5927\u6170\uff0c\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u4ed6\u65e9\u5df2\u77e5\u9053\u4e86\u3002\u4ed6\u867d\u7136\u53e6\u6709\u5fc3\u4e0a\u4e4b\u4eba\uff0c\u4f46\u4ed6\u6bd5\u7adf\u4e5f\u8fd8\u662f\u5173\u5fc3\u6211\u7684\u3002 \u55ef\uff0c\u7537\u5973\u4e4b\u95f4\uff0c\u5176\u5b9e\u4e0d\u4e00\u5b9a\u662f\u8981\u7ed3\u4e3a\u592b\u5987\uff0c\u4e00\u6837\u53ef\u4ee5\u505a\u5f88\u597d\u7684\u670b\u53cb\u7684\u3002\u4ece\u4eca\u5929\u8d77\u6211\u624d\u77e5 \u9053\uff0c\u6bcf\u4e2a\u4eba\u6709\u6bcf\u4e2a\u4eba\u7684\u7f18\u4efd\uff0c\u53ea\u8981\u4e0d\u81ea\u5bfb\u70e6\u607c\uff0c\u4fbf\u4f1a\u5f97\u5230\u5feb\u4e50\uff01\u201d\u6b27\u9633\u5a49\u60f3\u81f3\u6b64\u5904\uff0c\u5fc3 \u4e2d\u8c41\u7136\u5f00\u6717\uff0c\u70e6\u607c\u5168\u6d88\u3002 \u3000\u3000\u4e0d\u4e45\uff0c\u53f6\u51b2\u9704\u4e5f\u679c\u7136\u9192\u4e86\u8fc7\u6765\uff0c\u4ed6\u5728\u5bab\u4e2d\u672c\u6765\u6709\u4e2a\u4f4f\u5904\uff0c\u6628\u665a\u88ab\u70e7\u635f\u4e86\u4e00\u4e9b\uff0c\u4e5f\u65e9 \u7ecf\u5510\u52aa\u73e0\u7a46\u53eb\u4eba\u4fee\u590d\u4e86\u3002\u5f53\u4e0b\u5510\u52aa\u73e0\u7a46\u5c06\u4ed6\u9001\u56de\u4ed6\u7684\u5bdd\u5bab\uff0c\u4e3a\u4e86\u4fbf\u4e8e\u7167\u987e\uff0c\u6b27\u9633\u5a49\u4e5f\u4f4f \u5728\u90a3\u5ea7\u5bab\u4e2d\u3002 \u3000\u3000\u5927\u4e71\u521a\u521a\u5e73\u5b9a\uff0c\u6709\u8bb8\u591a\u4e8b\u60c5\u9700\u8981\u6599\u7406\uff0c\u5510\u52aa\u73e0\u7a46\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u65e9\u4e9b\u5b89\u6b47\uff0c\u660e\u5929 \u4e00\u65e9\uff0c\u4f60\u8fd8\u8981\u4e0a\u671d\u4e0e\u7fa4\u81e3\u89c1\u9762\u5462\u3002\u201d\u53f6\u51b2\u9704\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u4eec\u8981\u6211\u505a\u9a6c\u8428 \u513f\u56fd\u7684\u7687\u5e1d\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u56fd\u4e0d\u53ef\u4e00\u65e5\u65e0\u541b\uff0c\u4f60\u662f\u7236\u738b\u7684\u957f\u5b50\uff0c\u4f60\u4e0d\u505a\u7687\u5e1d\uff0c\u8c01\u505a\u7687\u5e1d\uff1f\u201d \u8c37\u4e2d\u83b2\u7ed9\u4ed6\u7aef\u6765\u4e86\u4e00\u7897\u53c2\u6c64\uff0c\u4e5f\u7b11\u7740\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5927\u54e5\uff0c\u54b1\u4eec\u88ab\u90a3\u5978\u738b\u62e8\u5f04\uff0c\u9aa8\u8089\u4e0d\u76f8\u8ba4\u8bc6\uff0c\u4ece\u524d\u6211\u6709\u8bb8\u591a\u5bf9\u4f60\u65e0\u793c\u7684\u5730\u65b9\uff0c\u660e\u5929 \u4f60\u767b\u4e0a\u5b9d\u5ea7\uff0c\u6211\u5148\u5411\u4f60\u78d5\u5934\uff0c\u7136\u540e\u5411\u4f60\u8ba8\u8d4f\u3002\u201d \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u53f6\u51b2\u9704\u5fc3\u4e71\u5982\u9ebb\uff0c\u5589\u5499\u4f3c\u6709\u4ec0\u4e48\u4e1c\u897f\u54fd\u4f4f\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u672c\u6765\u4ed6\u5e73\u65e5\u4e5f \u5b58\u6709\u60f3\u505a\u4e00\u505a\u7687\u5e1d\u7684\u91ce\u5fc3\uff0c\u90a3\u65f6\u4ed6\u5c1a\u672a\u77e5\u9053\u81ea\u5df1\u7684\u8eab\u4e16\uff0c\u8fd8\u662f\u90a3\u5978\u738b\u7684\u201c\u5e72\u6bb4\u4e0b\u201d\uff0c\u4f46 \u4ed6\u5df2\u7ecf\u6697\u91cc\u7ed3\u4ea4\u671d\u81e3\uff0c\u6536\u7f57\u515a\u7fbd\uff0c\u51c6\u5907\u6709\u671d\u4e00\u65e5\uff0c\u4ed6\u7684\u201c\u7236\u738b\u201d\u6b7b\u4e86\uff0c\u4ed6\u5c31\u8981\u81ea\u7acb\u4e3a\u7687\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5df2\u7ecf\u53ef\u4ee5\u540d\u6b63\u8a00\u9890\u5730\u505a\u7687\u5e1d\u4e86\uff0c\u6309\u7406\u8bf4\u4ed6\u5fc3\u613f\u5f97\u507f\uff0c\u5e94\u8be5\u9ad8\u5174\u4e4b\u6781\u624d\u5bf9\u3002\u4f46 \u8bf4\u4e5f\u5947\u602a\uff0c\u6b64\u65f6\u6b64\u9645\uff0c\u4ed6\u542c\u4e86\u5f1f\u59b9\u7684\u8bdd\uff0c\u5fc3\u4e2d\u5374\u53ea\u662f\u60ed\u6127\u61ca\u6094\uff0c\u60f6\u6050\u4e0d\u5b89\u2026\u2026\u773c\u4e2d\u8574\u7740 \u6cea\u73e0\uff0c\u51e0\u4e4e\u6389\u4e86\u4e0b\u6765\u3002\u5f53\u7136\u4ed6\u4e5f\u662f\u5f88\u9ad8\u5174\u7684\uff0c\u4e0d\u8fc7\u5374\u5e76\u4e0d\u662f\u56e0\u4e3a\u4ed6\u8981\u505a\u7687\u5e1d\u800c\u9ad8\u5174\uff0c\u4ed6 \u9ad8\u5174\u662f\u5f1f\u59b9\u5bf9\u4ed6\u7684\u624b\u8db3\u4e4b\u60c5\uff01\u8fd9\u79cd\u60c5\u8c0a\uff0c\u4ed6\u8fc7\u53bb\u505a\u68a6\u4e5f\u83b7\u4e0d\u5f97\u7684\u60c5\u8c0a\uff0c\u6b64\u65f6\u6b64\u9645\uff0c\u5728\u4ed6 \u7684\u5fc3\u4e2d\uff0c\u662f\u8981\u6bd4\u4e00\u9876\u7687\u51a0\u8d35\u91cd\u5343\u500d\u4e07\u500d\u4e86\uff01 \u3000\u3000\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u662f\u5927\u559c\u4e4b\u4e8b\u5440\uff0c\u4f60\u600e\u4e48\u53cd\u800c\u6d41\u6cea\u4e86\uff1f\u201d\u53f6\u51b2\u9704\u63a5\u8fc7\u5979\u624b\u4e0a \u7684\u53c2\u6c64\uff0c\u5477\u4e86\u4e00\u53e3\uff0c\u62b9\u53bb\u4e86\u6cea\u73e0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u553e\u5f03\u6211\u8fd9\u4e2a\u5927\u54e5\uff0c\u6211\u662f\u9ad8\u5174\u5f97\u6d41\u6cea\u4e86\u3002\u201d \u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4ee5\u524d\u4f60\u662f\u88ab\u90a3\u5978\u738b\u611a\u5f04\uff0c\u73b0\u5728\u5978\u738b\u5df2\u9664\uff0c\u96e8\u8fc7\u5929\u6674\uff0c\u8fd9\u4e9b\u65e7\u4e8b\uff0c\u8fd8\u63d0\u5b83\u505a \u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u6211\u7cca\u6d82\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u5e78\u4e8f\u4f60\u4eec\u6765\u4e86\uff0c\u6211\u624d\u5f97\u91cd\u65b0\u4e3a\u4eba\u3002\u73b0\u5728\u6211\u7684\u803b\u540e \u5df2\u7ecf\u96ea\u4e86\uff0c\u6211\u662f\u4ec0\u4e48\u4e5f\u4e0d\u60f3\u8981\u4e86\uff0c\u4f60\u4eec\u53d7\u4e86\u8bb8\u591a\u82e6\uff0c\u73e0\u7a46\u4e8c\u5f1f\uff0c\u6211\u9876\u66ff\u4e86\u4f60\u7684\u540d\u5b57\uff0c\u96be \u9053\u4f60\u8fd8\u8981\u6211\u4eca\u540e\u7ee7\u7eed\u9876\u66ff\u4f60\u505a\u8fd9\u4e2a\u56fd\u738b\u5417\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u7687\u4f4d\u672c\u6765\u662f\u4f60 \u7684\uff0c\u4f60\u53ea\u662f\u6062\u590d\u672c\u6765\u9762\u76ee\uff0c\u5e76\u975e\u9876\u66ff\u5806\u4eba\u3002\u8bf4\u5230\u53d7\u82e6\uff0c\u4f60\u6240\u906d\u53d7\u7684\u75db\u82e6\u548c\u803b\u8fb1\uff0c\u53ea\u6709\u66f4 \u5728\u6211\u4eec\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5b9a\u8981\u5c06\u6211\u63a8\u4e0a\u5b9d\u5ea7\u4e48\uff1f\u4e5f\u7f62\uff0c\u90a3\u5c31\u7559\u5f85\u660e\u5929\u518d\u8bf4\u5427\u3002\u201d\u5510\u52aa \u73e0\u7a46\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4e0d\u7528\u4f60\u8d39\u5fc3\u64cd\u52b3\uff0c\u6211\u5148\u66ff\u4f60\u62df\u597d\u662d\u544a\u590d\u56fd\u7684\u8bcf\u4e66\uff0c\u660e\u5929\u4f60\u53ea\u8981\u76d6\u4e0a \u7389\u73ba\u5c31\u884c\u4e86\u3002\u4f60\u4eca\u665a\u53ef\u5f97\u597d\u597d\u7684\u7761\u4e00\u89c9\uff0c\u517b\u597d\u7cbe\u795e\u3002\u201d\u4ed6\u6b63\u60f3\u544a\u8f9e\uff0c\u53f6\u51b2\u9704\u5ffd\u9053\uff1a \u3000\u3000\u201c\u6709\u4e00\u4ef6\u4e8b\u8fd8\u6ca1\u6709\u4ea4\u6258\u4f60\uff0c\u5b9d\u5e93\u91cc\u6709\u4e00\u90e8\u6b66\u529f\u5b9d\u5178\u540d\u4e3a\u9f99\u529b\u79d8\u85cf\uff0c\u8fd8\u6709\u51e0\u6837\u6b66\u5b66\u4e4b \u58eb\u7528\u5f97\u7740\u7684\u5b9d\u7269\uff0c\u6211\u521a\u624d\u6765\u4e0d\u53ca\u627e\u5bfb\uff0c\u660e\u5929\u4f60\u53ef\u5f97\u4ed4\u7ec6\u7684\u67e5\u67e5\u3002\u201d\u5510\u52aa\u73e0\u7a46\u8bf4\u9053\uff1a\u201c\u6211 \u77e5\u9053\u4e86\uff0c\u5927\u54e5\uff0c\u8fd9\u4e9b\u5c0f\u4e8b\uff0c\u4f60\u4e0d\u5fc5\u6302\u5728\u5fc3\u4e0a\uff0c\u5f85\u4f60\u597d\u4e86\uff0c\u54b1\u4eec\u4e00\u540c\u53bb\u627e\uff0c\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u8fd9\u4e00\u665a\u5510\u52aa\u73e0\u7a46\u6574\u665a\u4e95\u6ca1\u6709\u9616\u8fc7\u773c\u775b\uff0c\u4ed6\u628a\u590d\u56fd\u7684\u8bcf\u4e66\u62df\u597d\uff0c\u5df2\u662f\u6e05\u6668\u65f6\u5206\uff0c\u666f\u9633 \u5bab\u5ba3\u544a\u65e9\u671d\u5f00\u59cb\u7684\u949f\u58f0\u4e5f\u5df2\u7ecf\u6572\u54cd\u4e86\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6000\u4e86\u8bcf\u4e66\uff0c\u5174\u51b2\u51b2\u7684\u4fbf\u8dd1\u6765\u8bf7\u5927\u54e5\u4e0a\u524d\uff0c\u63a5\u53d7\u7fa4\u81e3\u671d\u62dc\u3002 \u3000\u3000\u54ea\u77e5\u53f6\u51b2\u9704\u5df2\u662f\u4eba\u5f71\u4e0d\u89c1\uff0c\u6b27\u9633\u5a49\u4e5f\u8ddf\u7740\u4ed6\u8d70\u4e86\u3002\u623f\u4e2d\u7559\u4e0b\u4e86\u4e00\u5c01\u4fe1\uff0c\u90a3\u662f\u53f6\u51b2\u9704 \u5199\u7ed9\u4ed6\u7684\uff0c\u4fe1\u4e0a\u8bf4\u4ed6\u5b9e\u5728\u65e0\u989c\u518d\u7559\u5728\u56fd\u4e2d\uff0c\u8bf7\u5f1f\u5f1f\u539f\u8c05\u4ed6\uff0c\u4ee3\u4ed6\u6311\u8d77\u56fd\u4e8b\u7684\u91cd\u62c5\uff0c\u65e9\u65e5 \u5373\u4f4d\uff0c\u4ee5\u5b89\u6c11\u5fc3\u3002 \u3000\u3000\u53f6\u51b2\u9704\u548c\u6b27\u9633\u5a49\u4ece\u79d8\u5bc6\u7684\u5730\u9053\u51fa\u8d70\uff0c\u5b88\u95e8\u7684\u536b\u58eb\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u4ed6\u4eec\u4f55\u65f6\u51fa\u5bab\uff0c\u5f53\u7136 \u662f\u627e\u4e0d\u56de\u6765\u7684\u4e86\u3002\u5510\u52aa\u73e0\u7a46\u6ca1\u6cd5\uff0c\u53ea\u597d\u9075\u4ece\u54e5\u54e5\u7684\u610f\u65e8\uff0c\u63a5\u53d7\u7fa4\u81e3\u7684\u62e5\u6234\uff0c\u7ee7\u627f\u4e86\u9a6c\u8428 \u513f\u56fd\u7684\u738b\u4f4d\u3002 \u3000\u3000\u9a6c\u8428\u513f\u56fd\u867d\u7136\u662f\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5c71\u56fd\uff0c\u4f46\u5728\u4e00\u573a\u52a8\u4e71\u4e4b\u540e\uff0c\u5e94\u5174\u5e94\u9769\u7684\u4e8b\u60c5\u4e5f\u7740\u5b9e\u5f88\u591a\uff0c \u7c91\u5510\u52aa\u73e0\u7a46\u5fd9\u4e2a\u4e0d\u4e86\uff0c\u597d\u5728\u6709\u51e0\u4e2a\u5fe0\u5fc3\u803f\u803f\u7684\u8001\u81e3\u5b50\u8f85\u4f50\u4ed6\uff0c\u7ed9\u4ed6\u5206\u52b3\u4e0d\u5c11\u3002 \u3000\u3000\u6c5f\u5357\u548c\u8c37\u4e4b\u534e\u642c\u8fdb\u738b\u5b98\u6765\u5f80\uff0c\u59ec\u6653\u98ce\u4e0d\u60ef\u62d8\u675f\uff0c\u5ffd\u52a8\u6e38\u5174\uff0c\u548c\u90a3\u4e2a\u5370\u5ea6\u795e\u5077\u4f5c\u4ef6\uff0c \u5230\u5370\u5ea6\u6f2b\u6e38\uff0c\u51c6\u5907\u626c\u540d\u5f02\u56fd\uff0c\u65bd\u5c55\u4ed6\u7684\u5999\u624b\u7a7a\u7a7a\u672c\u9886\u53bb\u4e86\u3002 \u3000\u3000\u90a3\u4e2a\u52fe\u642d\u76d6\u6e29\u3001\u5f15\u72fc\u5165\u5ba4\u201c\u7687\u989d\u5a18\u201d\u5728\u5510\u52aa\u73e0\u7a46\u767b\u4f4d\u65f6\u7b2c\u4e8c\u5929\uff0c\u4fbf\u5728\u5bab\u4e2d\u81ea\u7f22\u800c\u4ea1\uff0c \u5510\u52aa\u73e0\u7a46\u5ff5\u5728\u5979\u662f\u7236\u738b\u7684\u6b63\u5ba4\uff0c\u846c\u4ee5\u738b\u540e\u4e4b\u793c\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u5510\u52aa\u6627\u7a46\u9001\u846c\u56de\u6765\uff0c\u5ffd\u5730\u60f3\u8d77\u91d1\u9e70\u5bab\u4e4b\u4f1a\u7684\u524d\u5915\uff0c\u4ed6\u4eba\u5bab\u8c0b\u523a\u5978\u738b\uff0c\u65e0\u610f\u4e2d \u5077\u542c\u5230\u5929\u9b54\u6559\u4e3b\u548c\u90a3\u201c\u7687\u989d\u5a18\u201d\u7684\u4e00\u6bb5\u5bf9\u8bdd\uff0c\u636e\u90a3\u7687\u989d\u5a18\u8bf4\u5b9d\u5e93\u4e2d\u6709\u51e0\u4ef6\u6b66\u6797\u5f02\u5b9d\uff0c\u4f46 \u5979\u5374\u4e0d\u77e5\u5176\u540d\u3002 \u3000\u3000\u5979\u79c1\u85cf\u6709\u5b9d\u5e93\u7684\u9501\u5319\uff0c\u5f53\u65f6\u5979\u66fe\u7b54\u5141\u5929\u9b54\u6559\u4e3b\uff0c\u8981\u662f\u5929\u9b54\u6559\u4e3b\u7ed9\u5979\u9664\u6389\u53f6\u51b2\u9704\uff0c\u5979 \u613f\u610f\u5c06\u5b9d\u5e93\u7684\u9501\u5319\u4ea4\u6362\u53f6\u51b2\u9704\u7684\u6027\u547d\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5fc6\u8d77\u524d\u4e8b\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u73b0\u5728\u5979\u5df2\u6b7b\u4e86\uff0c\u8fd9\u9501\u5319\u5374\u4e0d\u77e5\u85cf\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u83ab \u8981\u843d\u5728\u522b\u4eba\u4e4b\u624b\u624d\u597d\u3002\u5979\u6240\u8bf4\u7684\u548c\u5927\u54e5\u8bf4\u7684\u76f8\u540c\uff0c\u770b\u6765\u662f\u771f\u7684\u4e86\u3002\u6211\u8fd9\u51e0\u65e5\u4e8b\u5fd9\uff0c\u4e00\u76f4 \u6ca1\u6709\u5230\u5b9d\u5e93\u4e2d\u67e5\u8fc7\uff0c\u73b0\u5728\u5012\u662f\u5e94\u8be5\u53bb\u770b\u4e00\u770b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5df2\u8ba4\u5f97\u90a3\u6761\u79d8\u5bc6\u7684\u5730\u9053\uff0c\u5f53\u4e0b\u5c31\u7ea6\u4e86\u59b9\u59b9\uff0c\u4e00\u540c\u8fdb\u5165\u5b9d\u5e93\uff0c\u90a3\u65e5\u4ed6\u4eec\u8d70\u51fa\u5b9d\u5e93\u4e4b \u540e\uff0c\u53ea\u662f\u628a\u95e8\u865a\u63a9\uff0c\u4e00\u63a8\u4fbf\u5f00\u3002\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u8fd9\u5b9d\u5e93\u6709\u4e24\u6761\u9501\u5319\uff0c\u4e00\u6761\u5728\u5978\u738b\u8eab \u4e0a\uff0c\u4e00\u6761\u5219\u662f\u90a3\u7687\u989d\u5a18\u79c1\u85cf\u8d77\u6765\uff0c\u73b0\u5728\u8fd9\u4e24\u4eba\u5df2\u6b7b\uff0c\u4e24\u6761\u9501\u5319\u90fd\u627e\u4e0d\u5230\uff0c\u4f60\u53ef\u5f97\u65e9\u65e5\u627e \u4e2a\u5de7\u624b\u5320\u4eba\uff0c\u53e6\u780c\u8fc7\u4e00\u9053\u673a\u5173\uff0c\u53e6\u914d\u8fc7\u9501\u5319\u624d\u597d\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u8fd9\u5b9d\u5e93\u4ee5\u540e\u4e0d\u4f1a\u518d\u6709\u4e86\uff0c\u6211\u53c8\u4f55\u5fc5\u518d\u8d39\u5fc3\u529b\u53bb\u53e6\u780c\u673a\u5173\u3002\u201d\u8c37\u4e2d \u83b2\u6014\u4e86\u4e00\u6014\uff0c\u540c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4f60\u4e0d\u8981\u8fd9\u5ea7\u5b9d\u5e93\uff1f\u6211\u4e0d\u660e\u767d\u4f60\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u795e\u60c5\u4e25\u8083\uff0c\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u73e0\u5b9d\u6709\u4e00\u5927\u534a\u662f\u54b1\u4eec\u7956\u5148\u8dbe\u4e16\u4ee3\u4ee3\u4e3a\u738b\u79ef\u4e0b \u6765\u7684\uff0c\u6709\u4e00\u5c0f\u534a\u662f\u8fd9\u5978\u738b\u5728\u8fd9\u5341\u591a\u5e74\u4e2d\u641c\u522e\u6765\u7684\uff0c\u4f46\u5c3d\u7ba1\u4ed6\u4eec\u53d6\u5f97\u8fd9\u4e9b\u8d22\u5bcc\u7684\u624b\u6bb5\u4e0d\u540c\uff0c \u603b\u4e4b\u90fd\u662f\u8001\u767e\u59d3\u7684\uff0c\u4f60\u8bf4\u662f\u4e48\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u554a\uff0c\u6211\u660e\u767d\u4e86\uff0c\u4f60\u662f\u8981\u8fd8\u7ed9\u8001\u767e\u59d3\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u6211\u8fd8\u6015\u997f\u6b7b\u5417\uff1f\u8981\u8fd9\u4e9b\u73e0\u5b9d\u6709\u4ec0\u4e48\u7528\uff1f\u4f55\u51b5\u672c\u6765\u5c31\u662f\u8001\u767e \u59d3\u7684\uff0c\u54b1\u4eec\u5f3a\u593a\u8fc7\u6765\uff0c\u636e\u4e3a\u4e00\u5bb6\u6240\u6709\uff0c\u4e5f\u5b9e\u5728\u8bf4\u4e0d\u8fc7\u53bb\u3002\u6211\u662f\u5b81\u613f\u88ab\u9a82\u4e3a\u4e0d\u8096\u5b50\u5b59\uff0c\u6211 \u662f\u51b3\u610f\u8981\u66f4\u6539\u7956\u5b97\u7684\u505a\u6cd5\u4e86\uff01\u201d\u4ed6\u773c\u4e2d\u53d1\u51fa\u5f02\u5f69\uff0c\u6b47\u4e86\u4e00\u6b47\uff0c\u63a5\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u51c6\u5907\u6258\u53ef \u9760\u7684\u4eba\uff0c\u5c06\u8fd9\u4e9b\u73e0\u5b9d\u5e26\u5230\u6ce2\u65af\u3001\u5370\u5ea6\u548c\u4e2d\u539f\u7684\u5404\u57ce\u5e02\u53bb\u53d8\u5356\uff0c\u4f46\u4e5f\u5e76\u4e0d\u662f\u628a\u53d8\u5356\u6240\u5f97\u7684 \u94b1\u5e73\u5206\u7ed9\u767e\u59d3\uff0c\u6211\u8981\u8d77\u5b66\u5802\uff0c\u7ed9\u5e73\u6c11\u5efa\u5c4b\u5b87\uff0c\u5347\u6cb3\u6e20\uff0c\u7b51\u6c34\u575d\uff0c\u8f9f\u7267\u573a\u2026\u2026\u5440\uff0c\u8981\u505a\u7684 \u4e8b\u60c5\u771f\u662f\u591a\u7740\u5462\u3002\u6211\u8fd8\u8981\u8058\u8bf7\u6c49\u65cf\u6709\u5b66\u95ee\u7684\u8d24\u4eba\u5e2e\u6211\u505a\u8fd9\u4e9b\u4e8b\u60c5\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u559c\u6781\u53eb\u9053\uff1a\u201c\u4f60\u771f\u662f\u4e00\u4e2a\u597d\u56fd\u738b\uff0c\u4e5f\u662f\u6211\u7684\u597d\u54e5\u54e5\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6211\u60f3 \u8981\u662f\u5927\u54e5\u4e3a\u738b\uff0c\u5927\u54e5\u4e5f\u4f1a\u8fd9\u6837\u505a\u7684\u3002\u4ed6\u628a\u738b\u4f4d\u8ba9\u7ed9\u6211\uff0c\u5c31\u8db3\u89c1\u4ed6\u4e5f\u5e76\u4e0d\u628a\u8fd9\u4e9b\u73e0\u5b9d\u653e\u5728 \u5fc3\u4e0a\u3002\u8bf4\u8001\u5b9e\u8bdd\uff0c\u6211\u613f\u610f\u7ed9\u767e\u59d3\u505a\u4e9b\u4e8b\u60c5\uff0c\u4f46\u5374\u4e0d\u613f\u505a\u8fd9\u635e\u4ec0\u5b50\u7684\u56fd\u738b\u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a \u201c\u4f46\u4f60\u603b\u5f97\u505a\u4e9b\u65f6\u5019\u518d\u8bf4\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6211\u4ece\u660e\u5929\u8d77\u5c31\u9646\u7eed\u5c06\u8fd9\u4e9b\u73e0\u5b9d\u8fd0\u51fa\u53bb\u53d8\u5356\uff0c\u5e94\u5174\u5e94\u9769\u4e5f\u9646\u7eed\u65bd\u884c\uff0c \u540c\u65f6\u6211\u4e5f\u7269\u8272\u53ef\u4ee5\u6267\u884c\u8fd9\u4e9b\u8ba1\u5212\u7684\u516c\u5e73\u6b63\u76f4\u7684\u5927\u81e3\uff0c\u55ef\uff0c\u7b49\u8fd9\u4e9b\u4e8b\u60c5\u5b89\u6392\u597d\u4e86\uff0c\u6211\u5c31\u8981 \u53bb\u627e\u5927\u54e5\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u8d22\u5bcc\u54b1\u4eec\u4e00\u4e1d\u4e0d\u8981\uff0c\u4f46\u5927\u54e5\u6240\u8bf4\u7684\u90a3\u51e0\u4ef6\u6b66\u6797\u5f02\u5b9d\u548c\u2018\u9f99\u529b\u79d8 \u85cf\u2019\uff0c\u54b1\u4eec\u5374\u662f\u53ef\u4ee5\u7528\u5f97\u7740\u7684\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u5018\u4e0d\u662f\u4e3a\u4e86\u8fd9\u51e0\u6837\u4e1c\u897f\uff0c\u6211\u4eca\u5929\u8fd8\u4e0d \u4f1a\u5230\u8fd9\u5b9d\u5e93\u6765\u5462\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u9645\uff0c\u4ed6\u4eec\u5df2\u8fdb\u5165\u5b9d\u5e93\u4e4b\u4e2d\uff0c\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54a6\uff0c\u54e5\u54e5\uff0c\u4f60\u89c9\u4e0d\u89c9\u5f97\u4f3c\u4e4e\u6709\u4e00\u80a1 \u6de1\u6de1\u7684\u5f02\u9999\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6015\u662f\u90a3\u65e5\u5927\u5ac2\u6240\u53d1\u7684\u4ec0\u4e48\u6bd2\u96fe\u5f39\uff0c\u8fd8\u6709\u4e00\u4e9b\u6c14\u5473\u6b8b\u7559\u5427\uff1f\u201d \u8c37\u4e2d\u83b2\u55c5\u4e86\u53c8\u55c5\uff0c\u8bf4\u9053\uff1a\u201c\u4f3c\u4e4e\u5e76\u4e0d\u76f8\u540c\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u4e5f\u6709\u70b9\u7591\u5fc3\uff0c\u4f46\u5374\u8bf4\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u53ea\u6709\u5927\u54e5\u8ba4\u5f97\u8def\u8fdb\u6765\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a \u201c\u4f60\u5fd8\u8bb0\u4e86\u8fd8\u6709\u4e2a\u5929\u9b54\u6559\u4e3b\u4e48\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a \u3000\u3000\u201c\u90a3\u5929\u9e70\u6559\u4e3b\u8fd8\u672a\u5f97\u5230\u9501\u5319\uff0c\u800c\u4e14\u76d6\u6e29\u548c\u90a3\u2018\u7687\u989d\u5a18\u2019\u53c8\u5df2\u6b7b\u4e86\uff0c\u5979\u4e5f\u51b3\u4e0d\u80fd\u77e5\u9053 \u8fd9\u6761\u79d8\u5bc6\u5730\u9053\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u7136\u5219\u8fd9\u80a1\u5f02\u9999\u53c8\u4ece\u95f4\u800c\u6765\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6216\u8005\u662f\u5b9d\u5e93\u4e2d\u672c\u6765\u85cf\u6709 \u7684\u5f02\u9999\uff0c\u90a3\u65e5\u5927\u54e5\u5728\u8fd9\u91cc\u548c\u90a3\u9c81\u6c0f\u5144\u5f1f\u6076\u6597\uff0c\u8bf4\u4e0d\u5b9a\u662f\u4ed6\u4eec\u8e22\u7ffb\u4e86\u85cf\u9999\u7684\u5668\u76bf\u3002\u54b1\u4eec\u4e14 \u522b\u731c\u7591\uff0c\u5148\u627e\u90a3\u51e0\u6837\u4e1c\u897f\u5427\u3002\u201d \u3000\u3000\u4ed6\u4eec\u867d\u7136\u4e0d\u77e5\u9053\u8fd9\u4e9b\u5b9d\u7269\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u4f46\u89e6\u773c\u6240\u53ca\uff0c\u5374\u90fd\u662f\u91d1\u94f6\u73e0\u5b9d\uff0c\u627e\u4e86\u534a\u5929\uff0c \u4e5f\u6ca1\u53d1\u73b0\u4e00\u4ef6\u53ef\u4ee5\u548c\u6b66\u5b66\u6cbe\u4e0a\u5173\u7cfb\u7684\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u96be\u9053\u53c8\u5929\u5fc3\u77f3\u4e4b\u7c7b\u7684\u7075\u836f\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a \u3000\u3000\u201c\u54ea\u6709\u8fd9\u8bb8\u591a\u7075\u836f\uff1f\u6211\u90a3\u665a\u5077\u542c\u5979\u4eec\u7684\u8bf4\u8bdd\uff0c\u90a3\u7687\u989d\u5a18\u66fe\u63d0\u53ca\u8fd9\u51e0\u4ef6\u5b9d\u7269\u3002\u201d\u8c37\u4e2d \u83b2\u9053\uff1a\u201c\u5979\u53ef\u66fe\u8bf4\u5230\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u5979\u4e5f\u6ca1\u6709\u89c1\u8fc7\u3002\u4e0d\u8fc7\u7236\u738b\u751f\u524d\u66fe\u5411 \u5979\u900f\u9732\u8fc7\u4e00\u70b9\u79d8\u5bc6\uff0c\u4ece\u5979\u8f6c\u8ff0\u7684\u53e3\u6c14\u542c\u6765\uff0c\u90a3\u662f\u62ff\u6765\u7528\u7684\u4e1c\u897f\uff0c\u4f3c\u4e4e\u662f\u5b9d\u5200\u5b9d\u5251\u4e4b\u7c7b\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u82e5\u679c\u662f\u5b9d\u5200\u5b9d\u5251\u4e4b\u7c7b\uff0c\u90a3\u5c31\u6ca1\u6709\u4ec0\u4e48\u5e0c\u671b\u4e86\u3002 \u3000\u3000\u6b66\u529f\u5230\u4e86\u767b\u5cf0\u9020\u6781\u7684\u5883\u754c\uff0c\u6839\u672c\u5c31\u65e0\u9700\u4ec0\u4e48\u5175\u5668\uff0c\u54b1\u4eec\u867d\u7136\u8fdc\u8fdc\u672a\u5230\u8fd9\u4e2a\u5883\u754c\uff0c\u4f46 \u5373\u4ee5\u73b0\u5728\u7684\u529f\u529b\u800c\u8bba\u3001\u5b9d\u5200\u5b9d\u5251\u5bf9\u54b1\u4eec\u7684\u5e2e\u52a9\u5df2\u7ecf\u4e0d\u5927\u3002\u800c\u4e14\u6211\u4e5f\u5df2\u7ecf\u6709\u4e86\u5e08\u7236\u7ed9\u6211\u7684 \u971c\u534e\u5b9d\u5251\u4e86\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u90a3\u2018\u9f99\u529b\u79d8\u85cf\u2019\u5373\u662f\u7236\u738b\u6284\u5728\u7f8a\u76ae\u4e0a\u7684\u90a3\u4e9b\u6b66\u529f\uff0c\u54b1\u4eec\u4e5f\u65e9\u5df2\u7ecf\u5168 \u90e8\u5230\u624b\u4e86\uff0c\u6211\u5e08\u7236\u7684\u6b66\u529f\u7cbe\u6df1\u535a\u5927\uff0c\u5c31\u8fdc\u6bd4\u2018\u9f99\u529b\u79d8\u85cf\u2019\u4e0a\u7684\u6b66\u529f\u9ad8\u660e\u5f97\u591a\uff0c\u6240\u4ee5\u5176\u5b9e \u4e5f\u6ca1\u6709\u4ec0\u4e48\u7a00\u7f55\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f46\u627e\u4e0d\u5230\u539f\u672c\uff0c\u603b\u662f\u6709\u4e9b\u9057\u61be\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a \u3000\u3000\u201c\u8bf4\u4e0d\u5b9a\u7236\u738b\u6284\u4e86\u526f\u672c\u4e4b\u540e\uff0c\u65e9\u5df2\u5c06\u5b83\u6bc1\u4e86\u3002\u81f3\u4e8e\u90a3\u51e0\u79cd\u5b9d\u7269\uff0c\u5927\u54e5\u548c\u90a3\u7687\u989d\u5a18\u4e5f \u53ea\u662f\u95fb\u8bf4\u5b9d\u5e93\u4e2d\u85cf\u6709\uff0c\u7a76\u5176\u5b9e\u4ed6\u706f\u4e5f\u6ca1\u6709\u89c1\u8fc7\u3002\u5230\u5e95\u662f\u771f\u662f\u5047\uff0c\u8c01\u4e5f\u4e0d\u77e5\u3002\u201d \u3000\u3000\u4ed6\u4eec\u5144\u59b9\u4e8c\u4eba\u56e0\u4e3a\u627e\u4e0d\u5230\u5b9d\u7269\uff0c\u90fd\u6015\u5bf9\u65b9\u5931\u671b\uff0c\u6240\u4ee5\u5728\u8a00\u8c08\u4e4b\u95f4\uff0c\u5927\u5bb6\u90fd\u5c3d\u91cf\u8d2c\u4f4e \u8fd9\u4e9b\u4e0d\u77e5\u540d\u7684\u5b9d\u7269\u7684\u4ef7\u503c\uff0c\u8fde\u90a3\u201c\u9f99\u529b\u79d8\u85cf\u201d\u4e5f\u89c6\u4f5c\u7b49\u95f2\u3002\u5176\u5b9e\u4ed6\u4eec\u5fc3\u4e2d\u6216\u591a\u6216\u5c11\u4e5f\u90fd \u662f\u6709\u4e9b\u53ef\u60dc\u7684\u3002\u60c5\u77e5\u8fd9\u4e9b\u5b9d\u7269\u5b9a\u7136\u662f\u7ed9\u4eba\u76d7\u8d70\u4e86\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5ffd\u9053\uff1a\u201c\u6211\u89c9\u5f97\u6709\u70b9\u5947\u602a\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4f60\u8fd8\u5728\u731c\u7591\u662f\u4ec0\u4e48\u4eba\u5077\u8fdb\u8fd9\u5b9d \u5e93\u5417\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u6700\u521d\u662f\u4e0d\u76f8\u4fe1\u6709\u4eba\u80fd\u591f\u8fdb\u6765\uff0c\u4f46\u73b0\u5728\u5df2\u662f\u4e0d\u7531\u5f97\u4ed6\u4e0d\u76f8\u4fe1\u4e86\u3002\u8c37\u4e2d\u83b2 \u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u4ef6\u4e8b\u60c5\u3002\u4ec0\u4e48\u4eba\u5077\u7684\uff0c\u6211\u5df2\u4e0d\u7528\u731c\u7591\u4e86\uff0c\u90a3\u5f53\u7136\u662f\u5929\u9b54\u6559\u4e3b\u3002\u6211\u662f\u5728\u60f3\u53e6 \u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u4e8b\u60c5\u5947\u602a\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54b1\u4eec\u7684\u7956\u5148\u4e16\u4ee3 \u4e3a\u738b\uff0c\u6709\u91d1\u94f6\u73e0\u5b9d\u4e0d\u8db3\u4e3a\u5947\uff0c\u5374\u4f55\u4ee5\u4f1a\u6709\u8fd9\u8bb8\u591a\u6b66\u6797\u5f02\u5b9d\u3002\u201d\u5510\u52aa\u6627\u7a46\u9053\uff1a\u201c\u54b1\u4eec\u7684\u59cb \u7956\u672c\u6765\u5c31\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u90a3\u2018\u9f99\u529b\u79d8\u85cf\u2019\u5c31\u662f\u4e00\u4e2a\u5f02\u4eba\u4f20\u7ed9\u54b1\u4eec\u59cb\u7956\u7684\uff0c\u8fd9\u6bb5\u6545\u4e8b\u3002 \u3000\u3000\u4f60\u4e0d\u662f\u542c\u8fc7\u4e86\u7684\u4e48\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f46\u4f20\u4e86\u8fd9\u4e48\u591a\u4ee3\uff0c\u4e5f\u65e9\u5df2\u4e0d\u5c5e\u4e8e\u6b66\u6797\u4e2d\u4eba\u4e86\u3002 \u3000\u3000\u9664\u4e86\u2018\u9f99\u529b\u79d8\u85cf\u2019\u4e4b\u5916\uff0c\u90a3\u5929\u5fc3\u77f3\u548c\u54b1\u4eec\u672a\u627e\u5230\u7684\u90a3\u51e0\u4ef6\u6b66\u6797\u5f02\u5b9d\u53c8\u662f\u54ea\u91cc\u6765\u7684\uff1f \u6211\u603b\u89c9\u5f97\u54b1\u4eec\u8fd9\u4e2a\u5bb6\u65cf\u603b\u4f3c\u4e4e\u6709\u70b9\u795e\u79d8\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u201c\u5618\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fde\u7956\u5b97\u4e5f\u6000\u7591\u4e86\u4e48\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c \u6211\u627e\u4e0d\u5230\u9002\u5f53\u7684\u5b57\u773c\uff0c\u53ea\u80fd\u7528\u795e\u79d8\u4e8c\u5b57\u3002\u5e76\u975e\u5bf9\u7956\u5b97\u6709\u6240\u4e0d\u656c\u3002\u201d\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u54b1 \u4eec\u8fde\u7236\u738b\u7684\u9762\u90fd\u6ca1\u89c1\u8fc7\uff0c\u4e0a\u4ee3\u7684\u4e8b\u60c5\u5f53\u7136\u66f4\u96be\u77e5\u5f97\u6e05\u695a\u4e86\u3002\u4f60\u95ee\u6211\u6211\u4e5f\u65e0\u4ece\u56de\u7b54\u3002\u6211\u770b \u4f60\u4e0d\u5fc5\u80e1\u601d\u4e71\u60f3\u4e86\uff0c\u54b1\u4eec\u8fd8\u662f\u8d70\u5427\uff01\u201d\u8c37\u4e2d\u83b2\u5ffd\u5730\u62ff\u8d77\u4e00\u4ef6\u4e1c\u897f\uff0c\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u770b\u8fd9 \u4e2a\u76d2\u5b50\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u4e00\u770b\uff0c\u53ea\u89c1\u8c37\u4e2d\u83b2\u62ff\u8d77\u6765\u7684\u4e43\u662f\u4e00\u53ea\u957f\u65b9\u5f62\u7684\u76d2\u5b50\uff0c\u9ed1\u6f06\u6f06\u7684\u6beb\u65e0\u5149\u6cfd\uff0c \u6572\u4e86\u53c8\u6572\uff0c\u94ee\u94ee\u4f5c\u54cd\uff0c\u5927\u7ea6\u662f\u94c1\u76ae\u505a\u7684\uff0c\u603b\u4e4b\u4e0d\u662f\u8d35\u91cd\u7684\u91d1\u5c5e\u3002\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d \u8fc7\u662f\u4e00\u53ea\u5f88\u666e\u901a\u7684\u9996\u9970\u76d2\u5b50\uff0c\u6709\u4ec0\u4e48\u7a00\u5947\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u5b83\u5341\u5206\u666e\u901a\uff0c\u6240\u4ee5\u5728 \u8fd9\u5b9d\u5e93\u4e4b\u4e2d\uff0c\u624d\u662f\u771f\u6b63\u7684\u7a00\u5947\uff01\u5018\u82e5\u5b83\u662f\u8d35\u91cd\u7684\u4e1c\u897f\uff0c\u6211\u624d\u4e0d\u4f1a\u6ce8\u610f\u5b83\u5462\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u8bf4\u7684\u4f3c\u4e4e\u8fdd\u53cd\u201c\u5e38\u7406\u201d\uff0c\u4f46\u5176\u5b9e\u6b63\u662f\u5408\u4e4e\u9053\u7406\uff0c\u5510\u52aa\u73e0\u7a46\u4e00\u60f3\uff0c\u4e5f\u5c31\u660e\u767d\u4e86\u3002 \u8981\u77e5\u5728\u8fd9\u5b9d\u5e93\u4e4b\u4e2d\uff0c\u90fd\u662f\u73ca\u745a\u3001\u739b\u7459\u3001\u73cd\u73e0\u3001\u7389\u77f3\u4e4b\u7c7b\u7684\u5b9d\u8d1d\uff0c\u4e00\u53ea\u666e\u666e\u901a\u901a\u7684\u94c1\u76ae\u76d2 \u5b50\u6df7\u5728\u8fd9\u4e9b\u73cd\u5b9d\u4e4b\u95f4\uff0c\u5f53\u7136\u662f\u663e\u5f97\u6781\u4e0d\u5bfb\u5e38\uff0c\u5927\u4e3a\u51fa\u5947\u4e86\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6c89\u541f\u8bf4\u9053\uff1a\u201c\u96be\u9053\u91cc\u9762\u88c5\u6709\u4ec0\u4e48\u5947\u73cd\u5f02\u5b9d\uff0c\u4f46\u5374\u4e3a\u4ec0\u4e48\u7528\u8fd9\u6837\u666e\u901a\u7684\u76d2\u5b50 \u6765\u88c5\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54b1\u4eec\u4e14\u522b\u80e1\u731c\uff0c\u6253\u5f00\u5b83\u6765\u4e00\u770b\uff0c\u4e0d\u5c31\u660e\u767d\u4e86\uff1f\u201d\u5f53\u4e0b\u626d\u65ad\u90a3\u628a\u5c0f \u5c0f\u7684\u94c1\u9501\uff0c\u6253\u5f00\u6765\u4e00\u770b\uff0c\u53ea\u89c1\u9996\u9970\u76d2\u5185\uff0c\u4ec0\u4e48\u9970\u7269\u4e5f\u6ca1\u6709\uff0c\u53ea\u6709\u4e00\u628a\u68b3\u5b50\uff0c\u4e00\u9762\u955c\u4e8e\uff0c \u68b3\u5b50\u662f\u6728\u5934\u505a\u7684\uff0c\u955c\u5b50\u662f\u94dc\u505a\u7684\uff0c\u5df2\u7ecf\u9eef\u6de1\u65e0\u5149\u4e86\u3002\u8fd9\u79cd\u68b3\u5b50\u955c\u5b50\u90fd\u662f\u666e\u901a\u4eba\u5bb6\u5987\u5973\u7684 \u7528\u54c1\uff0c\u4e00\u70b9\u4e5f\u4e0d\u7a00\u5947\uff0c\u4f46\u5728\u68b3\u5b50\u955c\u5b50\u4e0b\u9762\uff0c\u5374\u538b\u7740\u51e0\u5f20\u53d1\u9ec4\u4e86\u7684\u4fe1\u7b3a\u3002\u5510\u52aa\u73e0\u7a46\u5fc3\u9053\uff1a \u201c\u96be\u9053\u8fd9\u4e0a\u9762\u5199\u7684\u53c8\u662f\u4ec0\u4e48\u6b66\u529f\u79d8\u53d1\u4e4b\u7c7b\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u62bd\u51fa\u4e00\u5f20\u4fe1\u7b3a\uff0c\u770b\u4e86\u4e00\u770b\uff0c\u8bf4\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4e0a\u9762\u7684\u5b57\u6211\u8ba4\u4e0d\u5168\uff0c\u4f60\u8bfb\u7ed9\u6211\u542c \u542c\u3002\u201d\u539f\u6765\u662f\u7528\u9a6c\u8428\u513f\u56fd\u6587\u5b57\u6240\u5199\u7684\uff0c\u4fe1\u7b3a\u6b8b\u7834\uff0c\u58a8\u8ff9\u4ea6\u5df2\u6a21\u7cca\u4e0d\u6e05\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u4ed4\u7ec6\u8fa8\u8ba4\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u5947\u602a\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4e0a\u9762\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4f3c\u4e4e\u662f\u4e2a\u5973\u5b50\u5199\u7ed9\u5979\u7684\u60c5\u90ce\u7684\u4fe1\uff0c\u8bf4\u7684\u65e0\u975e\u662f\u5982\u4f55\u601d\u5ff5\u5bf9\u65b9\u7684\u60c5\u8bdd\u3002\u201d \u8c37\u4e2d\u83b2\u542c\u4e86\uff0c\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u5fc5\u5ff5\u4e86\u3002\u201d\u4f46\u5fc3\u91cc\u5374\u5728\u5947\u602a\uff0c\u4e0d\u77e5\u5979\u7684\u54ea\u4f4d\u7956 \u5148\uff0c\u5374\u628a\u522b\u4eba\u7684\u60c5\u4e66\u73cd\u85cf\u5728\u5b9d\u5e93\u4e4b\u4e2d\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u540e\u9762\u6709\u4e00\u6bb5\u8bdd\u5012\u662f\u503c\u5f97\u6ce8\u610f\uff0c\u90a3\u5973\u5b50\u4e0d\u77e5\u5230\u4e86\u4ec0\u4e48\u5730\u65b9\uff0c\u8bf4\u662f\u4ece\u4eca \u4e4b\u540e\u518d\u4e5f\u4e0d\u80fd\u56de\u6765\uff0c\u8981\u89c1\u9762\u9664\u975e\u6765\u751f\u4e86\u3002 \u3000\u3000\u5979\u53eb\u90a3\u4e2a\u7537\u5b50\u4e0d\u8981\u518d\u60f3\u5ff5\u5979\uff0c\u5b89\u5fc3\u6cbb\u7406\u56fd\u4e8b\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u53ef\u5c31\u771f\u5947\u602a\u4e86\u3002\u8fd9\u4e48\u8bf4\uff0c\u8fd9\u4e2a\u7537\u5b50\u5c82\u4e0d\u662f\u54b1\u4eec\u7684\u54ea\u4f4d\u7956\u5148\uff1f\u4f46 \u65e2\u662f\u8d35\u4e3a\u56fd\u738b\uff0c\u4ed6\u6240\u559c\u6b22\u7684\u5973\u5b50\u5c3d\u53ef\u8fce\u5165\u5bab\u4e2d\uff0c\u8fd8\u6709\u8c01\u80fd\u963b\u62e6\u4ed6\u4eec\u76f8\u597d\uff1f\u4f55\u4ee5\u5374\u53c8\u975e\u5206 \u5f00\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u53c8\u62bd\u51fa\u7b2c\u4e8c\u5f20\u4fe1\u7b3a\u6765\u770b\uff0c\u8fd9\u4f3c\u4e4e\u662f\u8f83\u540e\u5199\u7684\uff0c\u6ca1\u6709\u90a3\u4e48\u6b8b\u7834\uff0c\u58a8\u8ff9\u4e5f\u6ca1\u90a3 \u4e48\u6a21\u7cca\uff0c\u4e0a\u9762\u53ea\u662f\u7b80\u7b80\u5355\u5355\u5730\u5199\u4e86\u51e0\u884c\u3002\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u90a3\u5973\u7684\u5ac1\u4e86\u53e6\u4e00\u4e2a\u4eba\uff0c\u751f\u4e86\u4e00 \u4e2a\u513f\u5b50\u3002\u5979\u8981\u4ed6\u65e7\u65e5\u7684\u60c5\u90ce\u7231\u5c4b\u53ca\u4e4c\uff0c\u4ee5\u540e\u4e0d\u53ef\u4e0e\u5979\u7684\u513f\u5b50\u5728\u6c99\u573a\u76f8\u89c1\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5947\u602a\uff0c\u90a3\u5973\u7684\u4e3a\u4ec0\u4e48\u4f1a\u60f3\u5230\u4ed6\u95e8\u53ef\u80fd\u5728\u6c99\u573a\u76f8\u89c1\uff1f\u4e0d\u77e5\u90a3\u5b69\u5b50\u957f\u5927\u4e4b \u540e\uff0c\u4ed6\u4eec\u679c\u771f\u5982\u6b64\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u8c01\u77e5\u9053\u5462\uff1f\u4f60\u77a7\u8fd9\u4fe1\u7b3a\u5982\u6b64\u6b8b\u7834\uff0c\u58a8\u8ff9\u5982\u6b64\u6a21 \u7cca\uff0c\u81f3\u5c11\u4e5f\u662f\u767e\u5e74\u4ee5\u524d\u6240\u5199\u7684\u4e86\u3002\u90a3\u4e2a\u2018\u5b69\u5b50\u2019\u4e5f\u6050\u6015\u65e9\u5df2\u6b7b\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8fd8\u6709\u6700\u540e\u4e00\u5f20\uff0c\u4f60\u770b\u770b\u8fd9\u5f20\u8bf4\u7684\u53c8\u662f\u4ec0\u4e48\uff1f\u54a6\uff0c\u600e\u4e48\u50cf\u662f\u4e00\u5f20\u6587\u4e66\uff1f\u201d \u539f\u6765\u8fd9\u6700\u540e\u4e00\u5f20\u4fe1\u7b3a\uff0c\u7eb8\u8d28\u751a\u4f73\uff0c\u4e0a\u9762\u76d6\u7740\u4e00\u4e2a\u6731\u7ea2\u5927\u5370\u4e5f\u8fd8\u672a\u600e\u4e48\u892a\u8272\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u63a5\u8fc7\u6765\u77a7\u4e86\u53c8\u77a7\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u731c\u5f97\u4e0d\u9519\uff0c\u8fd9\u7684\u786e\u4e0d\u662f\u79c1\u4eba\u4fe1\u4ef6\uff0c\u662f\u6606\u5e03\u5170 \u56fd\u9001\u6765\u7684\u56fd\u4e66\u3002\u201d\u8c37\u4e2d\u83b2\u8be7\u9053\uff1a\u201c\u56fd\u4e66\uff1f\u90a3\u662f\u6bd4\u4e00\u822c\u6587\u4e66\u91cd\u8981\u5f97\u591a\u7684\u4e86\u3002\u600e\u4e48\u5374\u628a\u5e84\u91cd \u7684\u56fd\u4e66\u4e0e\u79c1\u4eba\u7684\u60c5\u4e66\u653e\u5728\u4e00\u8d77\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u8fd9\u5f20\u56fd\u4e66\u5176\u5b9e\u4e5f\u53ea\u662f\u4e00\u7eb8\u4f8b\u884c\u516c\u4e8b\uff0c\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u91cd\u8981\u7684\u5730\u65b9\u3002\u201d \u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u7a76\u7adf\u8bf4\u7684\u4ec0\u4e48\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a \u3000\u3000\u201c\u6606\u5e03\u5170\u56fd\u7684\u65b0\u541b\u7ee7\u4f4d\uff0c\u901a\u77e5\u54b1\u4eec\u3002\u63a5\u5230\u8fd9\u79cd\u56fd\u4e66\uff0c\u6d3e\u4eba\u53bb\u9053\u8d3a\u4e5f\u5c31\u5b8c\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6606\u5e03\u5170\u56fd\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6b63\u662f\u54b1\u4eec\u7684\u90bb\u56fd\u3002\u54b1\u4eec\u9a6c\u8428 \u513f\u56fd\u5728\u963f\u5c14\u6cf0\u5c71\u5c71\u5357\uff0c\u5b83\u5728\u5c71\u5317\uff0c\u4f46\u4e2d\u95f4\u9694\u7740\u4e00\u5ea7\u5927\u5c71\uff0c\u6700\u5c11\u4e5f\u8981\u8d70\u5341\u5929\u534a\u6708\u3002\u201d\u4ed6\u53c8 \u770b\u4e86\u770b\u90a3\u7eb8\u56fd\u4e66\u4e0a\u586b\u5199\u7684\u65e5\u671f\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6574\u6574\u4e03\u5341\u5e74\u524d\u7684\u4e8b\u60c5\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4eec\u5144\u59b9\u4e8c\u4eba\u90fd\u731c\u60f3\u4e0d\u5230\u4f55\u4ee5\u8fd9\u79cd\u4f8b\u884c\u516c\u4e8b\u7684\u56fd\u4e66\u4e5f\u8981\u5982\u6b64\u73cd\u85cf\u7684\u7f18\u6545\uff0c\u8c37\u4e2d\u83b2\u9690 \u9690\u611f\u5230\u8fd9\u56fd\u4e66\u548c\u90a3\u4e9b\u60c5\u4e66\u4e4b\u95f4\u5927\u7ea6\u6709\u751a\u5173\u8fde\uff0c\u4f46\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u5979\u4e5f\u8fd8\u672a\u60f3\u5f97\u660e\u767d\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u53cd\u6b63\u8fd9\u4e9b\u4eba\u90fd\u65e9\u5df2\u4e0d\u5728\u4e16\u95f4\uff0c\u54b1\u4eec\u4e5f\u65e0\u987b\u8d39\u795e\u63a8\u7a76\u4e86\u3002\u201d\u968f\u624b\u5c06 \u90a3\u9996\u9970\u76d2\u5b50\u85cf\u4e86\u8d77\u6765\uff0c\u4fbf\u8d70\u51fa\u5b9d\u5e93\u3002\u8fd9\u6b21\u4ed6\u4eec\u5728\u5b9d\u5e93\u91cc\u627e\u5bfb\u4e86\u534a\u5929\uff0c\u975e\u4f46\u662f\u4e00\u65e0\u6240\u83b7\uff0c \u53cd\u800c\u6dfb\u4e86\u4e00\u91cd\u7591\u4e91\uff0c\u4e00\u91cd\u6050\u60e7\u3002\u5bf9\u90a3\u4e9b\u4fe1\u4ef6\u6000\u7591\uff0c\u4e3a\u90a3\u4e9b\u5b9d\u7269\u5931\u843d\u800c\u6050\u60e7\u3002\u4e24\u8005\u76f8\u8f83\uff0c \u5b9d\u7269\u7684\u5931\u843d\u548c\u4ed6\u4eec\u6709\u5207\u8eab\u7684\u5173\u7cfb\uff0c\u5f53\u7136\u662f\u66f4\u91cd\u8981\u5f97\u591a\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u51fa\u4e86\u5b9d\u5e93\uff0c\u5fc3\u5934\u6005\u60d8\uff0c\u60f3\u53bb\u627e\u6c5f\u6d77\u5929\u89e3\u95f7\uff0c\u4f46\u60f3\u4e86\u4e00\u60f3\uff0c\u5374\u53c8\u6539\u53d8\u4e86\u4e3b\u610f\uff0c \u8f6c\u8fc7\u65b9\u5411\uff0c\u53bb\u89c1\u5979\u7684\u5e08\u7236\u8c37\u4e4b\u534e\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u6b63\u5728\u51ed\u680f\u9065\u671b\uff0c\u82e5\u6709\u6240\u601d\uff0c\u8c37\u4e2d\u83b2\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u5e08\u7236\u3002\u201d\u8c37\u4e4b\u534e\u629a\u6478\u5979\u7684\u5934 \u53d1\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u83b2\u513f\uff0c\u4f60\u8fd9\u4e24\u5929\u597d\u50cf\u6194\u7cb9\u591a\u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8fd9\u4e24\u5929\u662f\u7a0d\u4e3a\u5fd9\u4e00\u70b9\uff0c \u4f46\u6211\u7684\u7cbe\u795e\u5f88\u597d\u3002\u5e08\u7236\uff0c\u4f60\u5728\u5bab\u4e2d\u8fd8\u4f4f\u5f97\u60ef\u4e48\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u7b11\u9053\uff1a\u201c\u592a\u8212\u670d\u4e86\uff0c\u6211\u771f\u662f\u6709\u70b9\u611f\u5230\u4e0d\u60ef\u5462\u3002\u83b2\u513f\uff0c\u6211\u4e0d\u6253\u7b97\u4f4f\u4e0b\u53bb\u4e86\u3002\u201d \u8c37\u4e2d\u83b2\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u4e0d\u662f\u8981\u7b49\u5f85\u91d1\u5927\u4fa0\u56de\u6765\u5417\uff1f\u201d\u5979\u5c48\u6307\u4e00\u7b97\uff0c\u8bf4\u9053\uff1a \u201c\u65e5\u5b50\u8fc7\u5f97\u5feb\uff0c\u4e0d\u77e5\u4e0d\u89c9\u53c8\u5df2\u7ecf\u4e94\u5929\u5566\u3002\u4e0d\u8fc7\uff0c\u5e08\u7236\u4f60\u53cd\u6b63\u6ca1\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4f55\u4e0d\u591a\u7b49\u51e0 \u5929\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u6b63\u662f\u6709\u4e00\u4ef6\u4e8b\u60c5\uff0c\u4ef2\u5e2e\u4e3b\u4eca\u65e5\u6765\u8fc7\u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54e6\uff0c\u8fd9\u8001\u53eb\u5316 \u6765\u4e86\u4e48\uff0c\u600e\u4e48\u4e0d\u89c1\u6211\u7684\u54e5\u54e5\uff1f\u201d\u8c37\u4e4b\u534e\u7b11\u9053\uff1a\u201c\u8fd9\u8001\u53eb\u5316\u5927\u7ea6\u662f\u4e3a\u4e86\u534e\u59d1\u5a18\u7684\u4e8b\u60c5\uff0c\u5bf9 \u4f60\u4eec\u751a\u4e3a\u4e0d\u6ee1\uff0c\u4ed6\u4e0d\u613f\u610f\u8fdb\u5bab\uff0c\u662f\u53eb\u536b\u5175\u4f20\u8bdd\u8fdb\u6765\uff0c\u8981\u6211\u5230\u5bab\u95e8\u4e4b\u5916\u548c\u4ed6\u89c1\u9762\u7684\u3002 \u3000\u3000\u4e0d\u8fc7\u4f60\u4e5f\u522b\u602a\u4ed6\uff0c\u8fd9\u8001\u53eb\u5316\u7684\u813e\u6c14\u4e00\u5411\u803f\u76f4\uff0c\u4e3a\u4eba\u5012\u662f\u5f88\u70ed\u5fc3\u7684\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e0d\u4f1a\u602a\u4ed6\uff0c\u4ed6\u8d23\u5907\u6d77\u54e5\uff0c\u5176\u5b9e\u4e5f\u662f\u4e00\u7247\u597d\u5fc3\u3002\u6211\u5fc3\u91cc\u53ea \u662f\u89c9\u5f97\u96be\u8fc7\u3002\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u5fc5\u96be\u8fc7\uff0c\u4f60\u7684\u5fc3\u60c5\u6211\u5f88\u660e\u767d\uff0c\u4f60\u662f\u6ca1\u6709\u4e00\u70b9\u8fc7\u9519\u7684\u3002 \u8bb8\u591a\u770b\u6765\u96be\u4ee5\u89e3\u5f00\u7684\u7ed3\uff0c\u5e38\u5e38\u4f1a\u5728\u65f6\u5149\u6d41\u8f6c\u4e4b\u4e2d\uff0c\u4e0d\u77e5\u4e0d\u89c9\u7684\u89e3\u5f00\u3002\u54ce\uff0c\u8bdd\u513f\u53c8\u626f\u5f97\u8fdc \u4e86\uff0c\u8fd8\u662f\u8bf4\u56de\u6765\u5427\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4ef2\u5e2e\u4e3b\u4eca\u65e5\u5012\u4e0d\u662f\u4e3a\u4e86\u4f60\u4eec\u7684\u4e8b\u60c5\u6765\u7684\uff0c\u4ed6\u662f\u66ff\u4f60\u7684\u7ffc\u5e08\u4f2f\u5e26 \u4e2a\u53e3\u8baf\u7ed9\u6211\u7684\u3002\u4f60\u7684\u7ffc\u5e08\u4f2f\u662f\u5357\u4e10\u5e2e\u5e2e\u4e3b\uff0c\u4ed6\u662f\u5317\u4e10\u5e2e\u5e2e\u4e3b\uff0c\u4ed6\u4eec\u4e8c\u4eba\u4e3a\u4e86\u5357\u5317\u4e10\u5e2e\u5408 \u5e76\u4e4b\u4e8b\uff0c\u4e0a\u4e2a\u6708\u66fe\u7ecf\u6709\u8fc7\u4e00\u6b21\u6765\u4f1a\u3002\u7ffc\u5e08\u5144\u5c1a\u672a\u77e5\u9053\u6211\u7684\u884c\u8e2a\uff0c\u4f7f\u62dc\u6258\u4ed6\u63a2\u542c\u6211\u7684\u4e0b\u843d\u3002 \u636e\u8bf4\u671d\u5ef7\u5bf9\u54b1\u4eec\u7684\u9099\u5c71\u6d3e\u4ee5\u53ca\u4e10\u5e2e\u53c8\u6709\u4e0d\u5229\u7684\u4f01\u56fe\uff0c\u7559\u5b88\u9099\u5c71\u7684\u767d\u5e08\u5144\u3001\u8def\u5e08\u5144\u89c1\u6211\u4e45 \u65e0\u97f3\u8baf\uff0c\u90fd\u5f88\u7126\u6025\uff0c\u56e0\u6b64\u5e0c\u671b\u6211\u65e9\u65e5\u5f52\u53bb\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5373\u662f\u5982\u6b64\uff0c\u90a3\u6211\u5c31\u4e0d\u4fbf\u591a\u7559\u4f60\u4e86\u3002\u5e08\u4e49\uff0c\u5e08\u7236\u2026\u2026\u201d\u5979\u62ac\u8d77\u5934\u6765\u770b\u7740 \u5e08\u7236\uff0c\u4f3c\u662f\u6709\u8bdd\u8981\u8bf4\uff0c\u5374\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4f60\u662f\u820d\u4e0d\u5f97\u79bb\u5f00\u6211\u4e48\uff1f\u6211\u4e5f\u6b63\u6709\u4e00\u4ef6\u5fc3\u4e8b\uff0c\u8981\u548c\u4f60\u8bf4\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a \u201c\u8bf7\u5e08\u7236\u5429\u5490\u3002\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u6211\u5148\u95ee\u4f60\uff0c\u4f60\u53ef\u613f\u610f\u653e\u5f03\u505a\u4e2a\u516c\u4e3b\u7684\u5bcc\u8d35\u7e41\u534e\u4e48\uff1f\u201d\u8c37 \u4e2d\u83b2\u9053\uff1a\u201c\u6211\u4e0d\u613f\u610f\u505a\u4ec0\u4e48\u516c\u4e3b\uff0c\u53ea\u662f\u60f3\u8ddf\u968f\u7740\u4f60\u3002\u201d\u8c37\u4e4b\u534e\u5fc3\u5934\u5feb\u6170\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f \u6599\u5230\u4f60\u662f\u4f1a\u5982\u6b64\u56de\u7b54\u7684\u4e86\u3002\u6211\u505a\u4e86\u5341\u591a\u5e74\u7684\u638c\u95e8\uff0c\u65e9\u5df2\u60f3\u5378\u4e0b\u8fd9\u526f\u62c5\u5b50\uff0c\u73b0\u5728\u4f60\u5df2\u957f\u5927 \u6210\u4eba\uff0c\u56de\u53bb\u4e4b\u540e\uff0c\u6211\u60f3\u628a\u638c\u95e8\u4eba\u7684\u4f4d\u5b50\u4f20\u7ed9\u4f60\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u53ea\u662f\u60f3\u6c38\u8fdc\u5728\u4f60\u7684\u8eab\u8fb9\uff0c\u5374\u4e0d\u60f3\u505a\u638c\u95e8\u4eba\uff0c\u5f1f\u5b50\u5e74 \u8f7b\u8bc6\u6d45\uff0c\u8fd9\u6837\u91cd\u7684\u62c5\u5b50\u4e5f\u6311\u4e0d\u8d77\u6765\u3002\u201d\u8c37\u4e4b\u534e\u7b11\u9053\uff1a\u201c\u6211\u5f53\u5e74\u505a\u638c\u95e8\u4eba\u7684\u65f6\u5019\uff0c\u6bd4\u4f60\u4e5f \u5927\u4e0d\u4e86\u591a\u5c11\uff0c\u4e5f\u662f\u51e0\u4e4e\u751a\u4e48\u4e8b\u90fd\u4e0d\u61c2\uff0c\u4f46\u6162\u6162\u4e5f\u5c31\u5b66\u4f1a\u4e86\u3002\u55ef\uff0c\u4f60\u5728\u60f3\u4e9b\u4ec0\u4e48\uff1f\u4f60\u6709\u4ec0 \u4e48\u5fc3\u4e8b\uff0c\u53ef\u4ee5\u5bf9\u5e08\u7236\u8bf4\u4e48\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u662f\u60f3\u6c38\u8fdc\u8ddf\u968f\u7740\u4f60\uff0c\u4f46\u6211\u53c8\u6015\u2014\u2014\u201d\u8c37\u4e4b \u534e\u9053\uff1a\u201c\u6015\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6709\u65f6\u6211\u4e5f\u5728\u60f3\uff0c\u4e0d\u5982\u5c31\u5728\u8fd9\u8fdc\u79bb\u4e2d\u539f\u7684\u5c71\u56fd\u5ea6\u8fc7\u6b64\u751f\uff0c\u514d\u5f97\u3001 \u514d\u5f97\u518d\u62db\u70e6\u607c\u3002\u5509\uff0c\u4f46\u6211\u53c8\u820d\u4e0d\u5f97\u79bb\u5f00\u4f60\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u662f\u8fc7\u6765\u4eba\uff0c\u4e0d\u7528\u8c37\u4e2d\u83b2\u7ec6\u8bf4\uff0c\u7acb\u5373\u61c2\u5f97\u4e86\u5979\u7684\u5fc3\u60c5\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8fdf\u65e9\u662f\u8981\u56de\u53bb\u7684\uff0c\u8c37\u4e2d\u83b2\u8bf4\u8981\u5728\u5c71\u56fd\u4e2d\u5ea6\u8fc7\u6b64\u751f\uff0c\u90a3\u5c31\u662f\u8981\u4e0e\u6c5f\u6d77\u5929\u9694\u5f00\uff0c \u907f\u514d\u548c\u4ed6\u518d\u89c1\u9762\u4e86\u3002\u8fd9\u79cd\u5c11\u5973\u7684\u5fc3\u60c5\uff0c\u8c37\u4e4b\u534e\u5f53\u5e74\u4e5f\u66fe\u6709\u8fc7\uff0c\u5fc3\u91cc\u6697\u6697\u597d\u7b11\uff1a\u201c\u4f60\u4e0d\u4f46 \u662f\u820d\u4e0d\u5f97\u79bb\u5f00\u6211\uff0c\u5176\u5b9e\u66f4\u662f\u820d\u4e0d\u5f97\u79bb\u5f00\u6c5f\u6d77\u5929\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u83b2\u513f\uff0c\u4f60\u548c\u6d77\u5929\u7684\u4e8b\u60c5\u600e\u4e48\u6837\u4e86\uff1f\u201d\u8c37\u4e2d\u83b2\u53cc\u988a\u6655\u7ea2\uff0c\u4f4e\u5934\u8bf4\u9053\uff1a \u201c\u4ed6\u4e3a\u4e86\u534e\u59d1\u5a18\u7a81\u7136\u98de\u8d70\u7684\u4e8b\u60c5\uff0c\u5f88\u662f\u96be\u8fc7\u3002\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u65e9\u5df2\u731c\u60f3\u5f97\u5230\u3002\u6211 \u662f\u95ee\u4ed6\u5bf9\u4f60\u600e\u6837\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\uff0c\u6211\u4e0d\u77e5\u9053\u2026\u2026\u201d\u8c37\u4e4b\u534e\u5fae\u7b11\u9053\uff1a\u201c\u600e\u4f1a\u4e0d\u77e5\u9053\u5462\uff1f \u6211\u4e00\u5411\u628a\u4f60\u5f53\u4f5c\u5973\u513f\uff0c\u4f60\u5728\u6211\u7684\u773c\u524d\uff0c\u4e5f\u7528\u5f97\u770b\u5bb3\u7f9e\u4e48\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4ed6\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u4f46\u6211\u77e5\u9053\u4ed6\uff0c\u4ed6\u5fc3\u91cc\u662f\u559c\u6b22\u6211\u7684\u3002\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4ed6 \u6ca1\u6709\u8bf4\u8fc7\u534a\u53e5\u8bf7\u4f60\u539f\u8c05\u7684\u8bdd\u4e48\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a \u3000\u3000\u201c\u6ca1\u6709\u3002\u4ed6\u5e76\u6ca1\u6709\u505a\u8fc7\u5bf9\u4e0d\u4f4f\u6211\u7684\u4e8b\u60c5\uff0c\u53c8\u4f55\u987b\u8981\u6211\u539f\u8c05\uff1f\u201d\u8c37\u4e4b\u534e\u5401\u4e86\u53e3\u6c14\uff0c\u8bf4 \u9053\uff1a\u201c\u8fd9\u5c31\u597d\u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4ec0\u4e48\u597d\u4e86\uff1f\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4ed6\u5bf9\u90a3\u4f4d\u534e\u59d1\u5a18\u7684\u786e\u5b8c\u5168 \u662f\u5144\u59b9\u4e4b\u60c5\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u662f\u5c06\u5979\u4eec\u4e24\u4ee3\u7684\u906d\u9047\uff0c\u8fde\u8d77\u6765\u60f3\u7684\u3002\u5979\u4eec\u4e24\u4ee3\u4eba\u7684\u906d\u9047\uff0c\u770b\u8d77\u6765\u76f8\u540c\uff0c\u4f46\u628a \u6bcf\u4e00\u4e2a\u7ec6\u8282\u6bd4\u8f83\uff0c\u5374\u53c8\u53ef\u4ee5\u53d1\u73b0\u8bb8\u591a\u4e0d\u540c\u3002\u5f53\u5e74\u91d1\u4e16\u9057\u5728\u5389\u80dc\u7537\u6b7b\u540e\uff0c\u8d70\u5230\u5979\u7684\u75c5\u69bb\u4e4b \u524d\uff0c\u8bf7\u6c42\u5979\u7684\u539f\u8c05\uff0c\u90a3\u662f\u56e0\u4e3a\u91d1\u4e16\u9057\u786e\u5b9e\u662f\u5bf9\u5389\u80dc\u7537\u6709\u96be\u4ee5\u5fd8\u6000\u7684\u611f\u60c5\uff0c\u56e0\u800c\u5bf9\u5979\u611f\u5230 \u5185\u759a\uff0c\u8981\u6c42\u5979\u7684\u539f\u8c05\uff1b\u800c\u73b0\u5728\u6c5f\u6d77\u5929\u5bf9\u8c37\u4e2d\u83b2\u5374\u662f\u4e00\u7247\u5766\u7136\uff0c\u53ef\u89c1\u4ed6\u5bf9\u534e\u4e91\u78a7\u7684\u611f\u60c5\uff0c \u5c31\u5927\u5927\u4e0d\u540c\u4e8e\u91d1\u4e16\u9057\u4e4b\u5bf9\u5389\u80dc\u7537\uff0c\u56e0\u800c\u4ed6\u4e5f\u5c31\u65e0\u987b\u4e4e\u8bf7\u6c42\u8c37\u4e2d\u83b2\u7684\u539f\u8c05\u4e86\u3002\u8fd9\u79cd\u7231\u60c5\u4e2d \u7684\u5fae\u5999\u5fc3\u7406\uff0c\u8c37\u4e4b\u534e\u662f\u65e9\u5df2\u61c2\u4e86\uff0c\u4f46\u8c37\u4e2d\u83b2\u5374\u8fd8\u662f\u672a\u66fe\u660e\u767d\u7684\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5ffd\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u4e5f\u60f3\u95ee\u4f60\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d\u8c37\u4e4b\u534e\u9053\uff1a \u3000\u3000\u201c\u4f60\u8981\u95ee\u4ec0\u4e48\uff0c\u5c3d\u7ba1\u8bf4\u5427\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u5f53\u5e74\u79bb\u5f00\u4f60\u7684\u65f6\u5019\uff0c\u4f60\u96be\u4e0d\u96be\u8fc7\uff1f\u201d \u8c37\u4e4b\u534e\u9053\uff1a\u201c\u6700\u521d\u96be\u8fc7\uff0c\u540e\u6765\u4e5f\u5c31\u4e0d\u96be\u8fc7\u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4eba\u4e4b\u76f8\u77e5\uff0c\u8d35\u76f8\u77e5\u5fc3\u3002\u6211\u660e\u767d\u4ed6\u7684\u5fc3\u60c5\uff0c\u4ed6\u5018\u82e5\u4e0d\u90a3\u4e48\u505a\uff0c\u5fc3\u91cc\u5c31\u4e0d \u80fd\u81ea\u5b89\uff0c\u6211\u61c2\u5f97\u4e86\u8fd9\u4e00\u70b9\uff0c\u6211\u4e5f\u5c31\u4e0d\u613f\u518d\u7ed9\u4ed6\u589e\u6dfb\u70e6\u607c\u4e86\u3002\u55ef\uff0c\u5230\u4e86\u53cc\u65b9\u90fd\u80fd\u4ee5\u5fc3\u76f8\u89c1 \u7684\u65f6\u5019\uff0c\u90a3\u4e48\u4e00\u5207\u70e6\u607c\u4e5f\u5c31\u6d88\u9664\uff0c\u4e5f\u5c31\u4e0d\u4f1a\u6709\u6240\u96be\u8fc7\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u82e5\u6709\u6240\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u73b0\u5728\u4e5f\u5c31\u4e0d\u5fc5\u4e00\u5b9a\u8981\u7b49\u5f85\u91d1\u5927\u4fa0\u56de\u6765\u4e86\uff1f\u201d\u8c37\u4e4b \u534e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u8981\u6765\u7684\u65f6\u5019\u5c31\u4f1a\u6765\u7684\u3002\u201d\u8bf4\u5230\u6b64\u5904\uff0c\u8c37\u4e4b\u534e\u4e5f\u4e0d\u7981\u8138\u4e0a\u5fae\u5fae\u53d1\u70e7\uff0c\u5fc3 \u91cc\u60f3\u9053\uff1a\u201c\u6211\u548c\u4ed6\u5df2\u662f\u4e8c\u5341\u4f59\u5e74\u5982\u4e00\u65e5\uff0c\u6211\u5df2\u7b49\u4e86\u4ed6\u4e8c\u5341\u4f59\u5e74\uff0c\u4e5f\u4e0d\u4e89\u5728\u65e9\u4e00\u5929\u6216\u8fdf\u4e00 \u592d\u548c\u4ed6\u89c1\u9762\u3002\u201d\u8fd9\u8bdd\u5979\u5f53\u7136\u6ca1\u6709\u5bf9\u8c37\u4e2d\u83b2\u8bf4\u51fa\u6765\uff0c\u5f53\u4e0b\u8f7b\u8f7b\u629a\u5f92\u5f1f\u7684\u5934\u53d1\uff0c\u559f\u7136\u8bf4\u9053\uff1a \u201c\u83b2\u513f\uff0c\u4f60\u653e\u5fc3\uff0c\u6211\u8d70\u8fc7\u7684\u8def\uff0c\u4f60\u662f\u4e0d\u4f1a\u91cd\u8d70\u7684\u4e86\u3002\u4f60\u53bb\u5b89\u6b47\u5427\uff0c\u660e\u5929\u4f60\u8fd8\u8981\u6536\u62fe\u884c\u88c5 \u5462\u3002\u201d\u5979\u62ac\u8d77\u5934\u6765\uff0c\u53ea\u89c1\u6708\u4eae\u6b63\u4ece\u4e00\u7247\u4e4c\u4e91\u91cc\u94bb\u51fa\u6765\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u53eb\u5979\u56de\u53bb\u5b89\u5fc3\u7761\u89c9\uff0c\u4f46\u8c37\u4e2d\u83b2\u5374\u5e76\u6ca1\u6709\u542c\u5e08\u7236\u7684\u5429\u5490\uff0c\u5979\u79bb\u5f00\u4e86\u5e08\u7236\uff0c\u4ecd\u7136 \u5728\u5fa1\u82b1\u56ed\u91cc\u5f98\u5f8a\uff0c\u6e10\u6e10\uff0c\u4e0d\u77e5\u4e0d\u89c9\u7684\u4fbf\u5411\u6c5f\u6d77\u5929\u7684\u4f4f\u6240\u8d70\u53bb\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u8fd8\u672a\u8d70\u5230\u6c5f\u6d77\u5929\u7684\u4f4f\u6240\uff0c\u5ffd\u89c1\u6709\u4e2a\u4eba\u5f71\uff0c\u4e5f\u6b63\u81ea\u5206\u82b1\u62c2\u67f3\uff0c\u5411\u5979\u8d70\u6765\uff0c\u5b9a\u775b \u4e00\u770b\uff0c\u53ef\u4e0d\u6b63\u662f\u6c5f\u6d77\u5929\uff1f \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6d77\u54e5\uff0c\u4f60\u600e\u4e48\u8fd8\u672a\u7761\uff1f\u4f60\u53bb\u54ea\u513f\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a \u3000\u3000\u201c\u6b63\u662f\u60f3\u4e0a\u4f60\u90a3\u513f\u53bb\uff0c\u8c01\u77e5\u4f60\u5df2\u6765\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u5728\u51dd\u78a7\u6c60\u8fb9\u505c\u4e0b\u4e86\u811a\u6b65\uff0c\u6708\u4eae\u4e0b\u7761\u83b2\u6447\u66f3\uff0c\u66f4\u663e\u5f97\u5206\u5916\u6e05\u5e7d\uff0c\u6c5f\u6d77\u5929\u4f38\u624b\u60f3\u6458 \u4e00\u6735\u83b2\u82b1\uff0c\u8377\u65f6\u8986\u76d6\u4e0b\u6709\u5bf9\u9e33\u9e2f\uff0c\u4f3c\u662f\u88ab\u4ed6\u60ca\u52a8\uff0c\u5ffd\u5730\u5206\u5f00\uff0c\u6e38\u4e86\u51fa\u6765\uff0c\u6c5f\u6d77\u5929\u82e5\u6709\u6240 \u601d\uff0c\u628a\u624b\u7f29\u4e86\u56de\u6765\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u83b2\u59b9\uff0c\u4f60\u53ef\u662f\u6709\u4ec0\u4e48\u8bdd\u8981\u548c\u6211\u8bf4\u4e48\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u4e5f\u662f\u832b\u7136\u82e5\u6709\u6240\u601d\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u65b9\u59cb\u8bf4\u9053\uff1a\u201c\u6211\u89c1\u4e86\u4f60\u5374\u53c8\u4e0d\u77e5\u4ece\u54ea\u91cc\u8bf4\u8d77 \u4e86\uff0c\u8fd8\u662f\u4f60\u5148\u8bf4\u5427\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u62e8\u4e86\u62e8\u6c60\u6c34\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u7761\u83b2\u771f\u7f8e\u3002\u201d\u8c37\u4e2d\u83b2\uff1a\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u60f3 \u4e86\u534a\u5929\uff0c\u5c31\u60f3\u5230\u4e86\u8fd9\u4e00\u53e5\u8bdd\u548c\u6211\u8bf4\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u51dd\u78a7\u6c60\u91cc\u53ea\u662f\u4e00\u6cd3\u6b62\u6c34\uff0c\u6ca1\u6709\u98ce\u6ce2\uff0c\u6240\u4ee5\u6c60\u91cc\u7684\u9e33\u9e2f\u4e5f\u53ef\u4ee5\u4f18\u6e38\u81ea \u5728\uff0c\u6211\u53ef\u771f\u7fa1\u6155\u5b83\u4eec\u5462\uff1f\u53ef\u60dc\u6211\u660e\u5929\u5df2\u4e0d\u80fd\u770b\u89c1\u5b83\u4eec\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u62ac\u8d77\u5934\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u5feb\u5c31\u8981\u8d70\u4e86\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u7239\u7239\u79bb\u5bb6\u591a\u5e74\uff0c \u5988\u4e00\u76f4\u76fc\u671b\u4ed6\u56de\u53bb\uff0c\u6211\u4e5f\u8bb0\u6302\u7740\u5988\uff0c\u6240\u4ee5\u6211\u51c6\u5907\u660e\u5929\u548c\u4ed6\u4e00\u540c\u56de\u53bb\u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a \u201c\u6e38\u5b50\u601d\u4e61\uff0c\u8fd9\u662f\u4eba\u60c5\u4e4b\u5e38\u3002\u4f46\u9664\u4e86\u60e6\u8bb0\u7740\u4f60\u5988\u4e4b\u5916\uff0c\u53ef\u8fd8\u60e6\u8bb0\u7740\u65c1\u7684\u4eba\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u83b2\u59b9\uff0c\u4f60\u662f\u77e5\u9053\u6211\u7684\u5fc3\u4e8b\u7684\uff0c\u6211\u4e0d\u7792\u4f60\u3002\u5728\u56de\u5bb6\u4e4b\u524d\uff0c\u6211\u53ef\u8fd8\u5f97\u5230\u6c34 \u4e91\u5e86\u8d70\u8d70\uff0c\u770b\u770b\u534e\u59d1\u5a18\u3002\u4f60\u2026\u2026\u201d\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u6211\u6b63\u662f\u8981\u529d\u4f60\u53bb\u770b\u770b\u5979\uff0c\u4f60\u5018\u82e5\u4e0d\u53bb\uff0c \u6211\u8fd8\u8981\u9a82\u4f60\u5462\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5ffd\u9053\uff1a\u201c\u6211\u5fc3\u4e2d\u5f88\u662f\u4e0d\u5b89\uff0c\u603b\u662f\u89c9\u5f97\u6709\u70b9\u5bf9\u4e0d\u4f4f\uff0c\u2026\u2026\u201d\u8c37\u4e2d\u83b2\u60f3\u8d77\u5e08\u7236\u521a \u624d\u548c\u5979\u8bf4\u7684\u8bdd\uff0c\u5fc3\u5934\u4e00\u9707\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u611f\u5230\u5bf9\u4e0d\u4f4f\uff0c\u5bf9\u4e0d\u4f4f\u2026\u2026\u201d\u4e00\u4e2a\u201c\u8c01\u201d\u5b57\u8fd8\u672a\u51fa \u53e3\uff0c\u6c5f\u6d77\u5929\u5df2\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u534e\u59d1\u5a18\u8fd9\u6837\u8d70\u4e86\uff0c\u6211\u603b\u89c9\u5f97\u6709\u70b9\u5bf9\u4e0d\u4f4f\u5979\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u677e\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u534e\u59d1\u5a18\u5bf9\u4f60\u662f\u4e00\u7247\u75f4\u60c5\uff0c\u4f60\u3001\u4f60\u53bb\u770b\u5979\uff0c\u751a\u81f3\uff0c\u751a\u81f3\u2026\u2026 \u55ef\uff0c\u603b\u4e4b\u6211\u662f\u4e0d\u4f1a\u602a\u4f60\u7684\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a \u3000\u3000\u201c\u83b2\u59b9\uff0c\u6211\u6709\u4e2a\u53e4\u602a\u7684\u5ff5\u5934\uff0c\u4f60\u4e0d\u8981\u7b11\u6211\uff0c\u6211\u662f\u60f3\uff0c\u662f\u60f3\u2026\u2026\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u60f3 \u4ec0\u4e48\u6211\u90e8\u4e0d\u4f1a\u7b11\u8bdd\u4f60\uff0c\u4f60\u8bf4\u5427\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u662f\u4ece\u54b1\u4eec\u7684\u5e08\u7236\u60f3\u8d77\u7684\uff0c\u4f60\u8bf4\u4ed6\u4eec\u662f\u4e0d\u662f\u4e00\u5bf9\u6700\u8981\u597d\u7684\u670b\u53cb\uff1f\u201d\u8c37 \u4e2d\u83b2\u9053\uff1a\u201c\u5929\u4e0b\u6050\u6015\u518d\u6ca1\u6709\u53e6\u5916\u4e00\u5bf9\uff0c\u662f\u8fd9\u6837\u7684\u4e8c\u5341\u4f59\u5e74\u59cb\u7ec8\u5982\u4e00\u7684\u53cb\u60c5\u4e86\u3002\u201d\u6c5f\u6d77\u5929 \u559f\u7136\u53f9\u9053\uff1a\u201c\u8fd9\u672c\u6765\u4e0d\u662f\u4eba\u4eba\u505a\u5f97\u5230\u7684\u3002\u201d\u8c37\u4e2d\u83b2\u62ac\u8d77\u5934\u6765\u8bf4\u9053\uff1a\u201c\u6d77\u5929\uff0c\u4f60\u8d70\u5427\uff0c\u6211 \u53ef\u4ee5\u505a\u5f97\u5230\u7684\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u4e0d\u662f\u8981\u4f60\u4e00\u4e2a\u4eba\u8fd9\u6837\u505a\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u6211\u660e\u767d\u4f60\u7684\u5ff5\u5934\u4e86\uff0c\u8981\u662f\u5927\u5bb6\u90fd\u4f3c\u81f3\u4eb2\u7684\u5144\u5f1f\u59d0\u59b9\uff0c\u9ad8\u9ad8\u5174\u5174\u5730\u540c \u5728\u4e00\u8d77\uff0c\u6ca1\u6709\u731c\u7591\uff0c\u6ca1\u6709\u5992\u5fcc\uff0c\u6ca1\u6709\u70e6\u607c\uff0c\u90a3\u5c82\u4e0d\u597d\uff1f\u8fd9\u5ff5\u5934\u5e76\u4e0d\u53e4\u602a\uff0c\u6211\u4e5f\u66fe\u7ecf\u8fd9\u4e48 \u60f3\u8fc7\u7684\u3002\u53ef\u662f\uff0c\u522b\u4eba\u4e0d\u89c1\u5f97\u548c\u54b1\u4eec\u4e00\u822c\u60f3\u6cd5\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4eba\u4e8b\u96be\u6599\uff0c\u6bd4\u5982\u6b27\u9633\u59d1\u5a18\u548c\u5927\u54e5\u7a81\u7136\u7f14\u7ed3\u9e33\u76df\uff0c\u8fd9\u5728\u4e8b\u524d\u53c8\u6709\u63a8\u6599\u60f3 \u5f97\u5230\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u662f\u76fc\u671b\u534e\u59d1\u5a18\u4e5f\u662f\u8fd9\u822c\uff1f\u201d\u968f\u5373\u6447\u4e86\u6447\u5934\uff0c\u7b11\u9053\uff1a\u201c\u5929\u4e0b \u6ca1\u6709\u5b8c\u5168\u76f8\u540c\u7684\u4e8b\u60c5\uff0c\u4f60\u522b\u60f3\u5f97\u592a\u5982\u610f\u4e86\u3002\u6211\u548c\u534e\u59d1\u5a18\u867d\u662f\u521a\u521a\u8ba4\u8bc6\uff0c\u4f46\u6211\u4e5f\u5df2\u9690\u9690\u89c9 \u5f97\u5979\u7684\u6027\u683c\u548c\u6b27\u9633\u59d1\u5a18\u5927\u4e0d\u76f8\u540c\u3002\u201d \u3000\u3000\u6b27\u9633\u5a49\u662f\u4e2a\u4efb\u6027\u800c\u4e3a\uff0c\u7231\u4e0e\u6068\u90fd\u5f88\u5f3a\u70c8\u7684\u5973\u5b50\uff0c\u4f46\u5374\u53c8\u662f\u4e2a\u62ff\u5f97\u8d77\u653e\u5f97\u4e0b\u7684\u5973\u5b50\u3002 \u8fd9\u6027\u683c\u4e0d\u4f46\u4e0e\u534e\u4e91\u78a7\u5927\u4e0d\u76f8\u540c\uff0c\u4e0e\u5389\u80dc\u7537\u4e5f\u5e76\u4e0d\u5b8c\u5168\u4e00\u6837\uff0c\u5389\u80dc\u7537\u60f3\u8981\u7684\u4e1c\u897f\u5c31\u4e00\u5b9a\u8981 \u5f97\u5230\u624b\uff0c\u6b27\u9633\u5a49\u5374\u6bd4\u5979\u591a\u51e0\u5206\u8c41\u8fbe\uff0c\u51e0\u5206\u8d85\u8131\u3002\u548c\u6b27\u9633\u5a49\u6bd4\u8f83\u8d77\u6765\uff0c\u534e\u4e91\u78a7\u5219\u66f4\u662f\u201c\u6267 \u8457\u201d\u5f97\u591a\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u628a\u6211\u6240\u60f3\u7684\u90fd\u5bf9\u4f60\u8bf4\u4e86\u5427\u3002\u8981\u662f\u534e\u59d1\u5a18\u53e6\u6709\u4e86\u7740\u843d\uff0c \u6216\u8005\u5979\u80fd\u591f\u539f\u8c05\u6211\u7684\u8bdd\uff0c\u6211\u5c31\u56de\u6765\uff0c\u56de\u6765\u2026\u2026\u201d\u8c37\u4e2d\u83b2\u8f7b\u8bf4\u9053\uff1a\u201c\u505a\u4ec0\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a \u201c\u966a\u4f60\u5929\u5929\u5728\u8fd9\u91cc\u770b\u9e33\u9e2f\u3002\u201d\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u90a3\u817b\u6b7b\u4eba\u4e86\uff0c\u8981\u662f\u5979\u4e0d\u5462\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u613f\u5979\u592a\u96be\u8fc7\u3002\u6211\u5c31\u5b66\u6211\u7684\u5e08\u7236\u4e00\u6837\uff0c\u4eca\u751f\u4eca\u4e16\uff0c\u6d6a\u8361\u6c5f\u6e56\uff0c \u4ee5\u56db\u6d77\u4e3a\u5bb6\uff0c\u4e0e\u6885\u9e64\u4e3a\u53cb\u3002\u82e5\u7136\u5982\u6b64\uff0c\u6211\u4e5f\u4f46\u613f\u4f60\u548c\u5979\u90fd\u662f\u4e00\u6837\uff0c\u5c06\u6211\u5f53\u4f5c\u54e5\u54e5\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7684\u610f\u601d\u5df2\u8bf4\u5f97\u5f88\u660e\u663e\uff0c\u4ed6\u7231\u7684\u662f\u8c37\u4e2d\u83b2\uff0c\u4f46\u5374\u5148\u8981\u6c42\u53d6\u534e\u4e91\u78a7\u7684\u8c05\u89e3\uff0c\u624d\u80fd \u5a36\u5979\u4e3a\u59bb\u3002\u82e5\u5f97\u4e0d\u5230\u8c05\u89e3\uff0c\u5219\u4ed6\u53ea\u80fd\u548c\u8c37\u4e2d\u83b2\u3001\u534e\u4e91\u78a7\u90fd\u4fdd\u6301\u7740\u7eaf\u6d01\u7684\u53cb\u8c0a\u3002 \u3000\u3000\u8981\u662f\u4e00\u4e2a\u5fc3\u80f8\u72ed\u7a84\u7684\u5973\u5b50\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u4e00\u5b9a\u5927\u4e0d\u9ad8\u5174\uff0c\u4f46\u8c37\u4e2d\u83b2\u5374\u662f\u4e2a\u5fc3\u65e0\u6e23\u6ed3\u3001 \u7eaf\u771f\u4e4b\u6781\u7684\u59d1\u5a18\uff0c\u542c\u4e86\u4e4b\u540e\uff0c\u65e2\u65e0\u5931\u671b\u7684\u8868\u793a\uff0c\u5374\u4e5f\u4e0d\u63a9\u9970\u81ea\u5df1\u7684\u5fc3\u60c5\uff0c\u7b11\u9053\uff1a\u201c\u6211\u662f \u6b22\u559c\u548c\u4f60\u5728\u4e00\u8d77\u7684\uff0c\u4f46\u6211\u4e5f\u51b3\u4e0d\u613f\u610f\u6709\u4eba\u4e3a\u54b1\u4eec\u96be\u8fc7\uff0c\u6240\u4ee5\u53ea\u8981\u4f60\u89c9\u5f97\u600e\u6837\u505a\u5bf9\u534e\u59d1\u5a18 \u597d\u4e9b\uff0c\u6211\u90fd\u6beb\u65e0\u6028\u8a00\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u770b\u770b\u6c60\u4e2d\u7684\u82b1\uff0c\u53c8\u770b\u770b\u773c\u524d\u7684\u4eba\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u83b2\u59b9\u5f53\u771f\u662f\u540d\u526f\u5176\u5b9e\uff0c\u5c31\u4f3c \u8fd9\u83b2\u82b1\u4e00\u6837\u7684\u7eaf\u6d01\u65e0\u7455\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u6458\u4e0b\u4e86\u4e00\u6735\u83b2\u82b1\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u559c\u6b22\u8fd9\u82b1\u513f\uff0c\u4f60\u5c31\u5e26\u4e00\u6735\u53bb\u5427\u3002\u660e\u5929\u6211\u4e0d\u9001\u884c \u4e86\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u54e5\u54e5\u4e8b\u5fd9\uff0c\u660e\u5929\u6211\u4e5f\u4e0d\u51c6\u5907\u53bb\u8f9e\u884c\u4e86\u3002\u4f60\u7ed9\u6211\u8bf4\u4e00\u58f0\u5427\u3002\u201d\u4e24\u4eba \u6267\u624b\u76f8\u770b\uff0c\u773c\u4e2d\u90fd\u6709\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u8fc7\u4e86\u534a\u664c\uff01\u8c37\u4e2d\u83b2\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8d70\u5427\uff01\u201d\u5979\u59cb \u7ec8\u6ca1\u6709\u8bf4\u51fa\u5979\u4e5f\u8981\u4e0e\u5e08\u7236\u79bb\u5f00\u6b64\u5730\uff0c\u56e0\u4e3a\u5979\u6240\u60f3\u7684\u90fd\u662f\u4e3a\u4e86\u6c5f\u6d77\u5929\u3002\u6b63\u662f\uff1a \u3000\u3000\u60c5\u4f3c\u6d6e\u4e91\u65e0\u969c\u788d\uff0c\u5fc3\u5982\u660e\u955c\u4e0d\u6cbe\u5c18\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e09\u56de\u3000\u7adf\u6709\u4f7f\u81e3\u7518\u505a\u8d3c\u3000\u4f55\u6765\u5999\u7b56\u89e3\u5175\u620e \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u56db\u5341\u4e09\u56de\u3000\u7adf\u6709\u4f7f\u81e3\u7518\u505a\u8d3c\u3000\u4f55\u6765\u5999\u7b56\u89e3\u5175\u620e \u3000\u3000\u6708\u8272\u6f84\u660e\uff0c\u8377\u5858\u6cdb\u5f71\uff0c\u4f46\u5374\u662f\u53ea\u5269\u4e0b\u8c37\u4e2d\u83b2\u81ea\u5df1\u7684\u5f71\u5b50\u4e86\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u76ee\u9001\u6c5f\u6d77\u592d\u7684\u80cc\u5f71\u6ca1\u5165\u82b1\u6811\u4e1b\u4e2d\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u518d\u770b\u770b\u8377\u5858\u91cc\u81ea\u5df1\u7684\u5f71\u5b50\uff0c\u53ea \u89c1\u5fae\u6ce2\u8361\u6f3e\uff0c\u90a3\u5bf9\u9e33\u9e2f\u6e38\u56de\u539f\u6765\u7684\u5730\u65b9\uff0c\u5c06\u8c37\u4e2d\u83b2\u7684\u5f71\u5b50\u641e\u4e71\u4e86\uff0c\u8c37\u4e2d\u83b2\u5fc3\u5934\u5e10\u89e6\uff0c\u6697 \u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u5bf9\u9e33\u9e2f\u65e0\u987b\u4f18\u8651\u53d7\u4eba\u5e72\u6270\uff0c\u53ef\u4ee5\u6c38\u4e0d\u79bb\u5206\u3002\u4f46\u6211\u5374\u4e0d\u80fd\u4e0d\u4e0e\u6d77\u54e5\u6682\u65f6\u5206\u624b \u4e86\u3002\u4e0d\u8fc7\u3002\u8fd9\u5374\u662f\u6211\u81ea\u5df1\u613f\u610f\u7684\u3002\u6211\u4e0d\u5e94\u8be5\u4e0e\u4ed6\u540c\u884c\uff0c\u514d\u5f97\u5f71\u54cd\u4ed6\u7684\u5fc3\u60c5\u3002\u5bf9\uff0c\u5c31\u8fd9\u4e48 \u529e\u3002\u6211\u4e0e\u5e08\u7236\u7684\u884c\u671f\u53ef\u4ee5\u538b\u540e\u4e00\u5929\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u72ec\u81ea\u5728\u51dd\u78a7\u6c60\u5934\uff0c\u6084\u7136\u51dd\u601d\uff0c\u6708\u79fb\u82b1\u5f71\uff0c\u6597\u8f6c\u661f\u6a2a\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u542c\u5f97\u56db\u66f4\u9f13 \u54cd\uff0c\u8c37\u4e2d\u83b2\u8fd9\u624d\u56de\u53bb\u3002\u9646\u7761\u4e4b\u524d\uff0c\u8fd8\u5199\u4e86\u4e00\u4e2a\u5b57\u6761\uff0c\u5429\u5490\u5bab\u5973\uff0c\u660e\u5929\u4e00\u65e9\uff0c\u9001\u7ed9\u5979\u7684\u5e08 \u7236\uff0c\u7980\u660e\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5c06\u884c\u671f\u538b\u540e\u4e00\u5929\u3002 \u3000\u3000\u4e00\u89c9\u9192\u6765\uff0c\u5df2\u662f\u5c06\u8fd1\u4e2d\u5348\u65f6\u5206\u3002\u8c37\u4e2d\u83b2\u5403\u8fc7\u5348\u9910\uff0c\u4fbf\u53bb\u89c1\u5979\u7684\u54e5\u54e5\u3002\u5510\u52aa\u73e0\u7a46\u5df2\u7ecf \u77e5\u9053\u6c5f\u6d77\u5929\u8d70\u4e86\uff0c\u95ee\u9053\uff1a\u201c\u6c5f\u5e08\u5144\u4e3a\u4ec0\u4e48\u8fd9\u6837\u52ff\u5306\u5fd9\u5fd9\u5730\u79bb\u5f00\uff0c\u8fde\u6211\u4e5f\u4e0d\u544a\u8bc9\u4e00\u58f0\uff0c\u4f60 \u53ef\u77e5\u9053\u4e48\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4ed6\u662f\u6015\u4f60\u4e8b\u5fd9\uff0c\u5df2\u7ecf\u6258\u6211\u5411\u4f60\u9053\u6b49\u4e86\u3002\u201d\u5510\u52aa\u73e0\u7a46\u8be7\u9053\uff1a \u201c\u4f60\u4eec\u7684\u4e8b\u60c5\u7a76\u7adf\u600e\u6837\uff1f\u600e\u7684\u4f60\u5c31\u653e\u5fc3\u8ba9\u4ed6\u8d70\u4e86\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u4e0d\u653e\u5fc3\u7684\uff0c\u4ed6\u8981\u8bf4\u7684\u8bdd\u90fd\u5df2\u5bf9\u6211\u8bf4\u4e86\u3002\u201d\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a \u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f60\u4e0e\u6c5f\u5e08\u5144\u5df2\u7ecf\u662f\u5fc3\u5fc3\u76f8\u5370\u4e86\u3002\u5927\u7ea6\u4f60\u4eec\u4e0d\u613f\u8fd9\u6837\u5e74\u8f7b\u5c31\u7ed3\u5a5a\u5427\uff1f\u4f46\u5374\u4e3a \u4ec0\u4e48\u4e0d\u8ba2\u4e86\u5a5a\u624d\u8ba9\u4ed6\u8d70\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u674f\u8138\u98de\u971e\uff0c\u8bf4\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u600e\u4e48\u4e00\u60f3\u5c31\u60f3\u5230\u7ed3\u5a5a\u4e0a\u5934\uff0c\u96be\u9053\u7537\u5973\u4e4b\u95f4\uff0c\u5c31 \u53ea\u6709\u592b\u59bb\u7684\u5173\u7cfb\u5417\uff1f\u8bf4\u4e0d\u5b9a\u6211\u4e0e\u6d77\u54e5\u8fd9\u4e00\u751f\u90fd\u4e0d\u4f1a\u7ed3\u5a5a\u7684\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u600e\u4e48\u6709\u8fd9\u4e2a\u60f3\u6cd5\uff1f\u96be\u9053\u4f60\u4e5f\u8981\u5b66\u54b1\u4eec\u5e08\u7236\u7684\u6837\u5b50\uff1f\u4ed6\u4eec\u662f\u65e0 \u53ef\u5948\u4f55\u624d\u8fd9\u6837\u7684\uff0c\u4f60\u4eec\u5374\u53c8\u4e3a\u4e86\u4ec0\u4e48\uff1f\u96be\u9053\u6c5f\u5e08\u5144\u4e5f\u53e6\u5916\u6709\u4eba\uff0c\u50cf\u6211\u5e08\u7236\u5f53\u5e74\u548c\u90a3\u5389\u80dc \u7537\u4e00\u6837\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6709\u4e9b\u7c7b\u4f3c\uff0c\u4f46\u5374\u5e76\u975e\u4e00\u6837\uff0c\u54e5\u54e5\uff0c\u6211\u90fd\u5bf9\u4f60\u8bf4\u4e86\u5427\u3002\u201d\u5f53\u4e0b\u5c06\u534e\u4e91\u78a7 \u7684\u4e8b\u60c5\uff0c\u4ee5\u53ca\u6c5f\u6d77\u5929\u6628\u665a\u5bf9\u5979\u7684\u8bdd\u8bed\uff0c\u4e00\u4e00\u5bf9\u54e5\u54e5\u8bf4\u4e86\u3002\u5c60\u52aa\u73e0\u7a46\u9eef\u7136\u4e0d\u8bed\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c \u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u6c5f\u5e08\u5144\u662f\u4e2a\u5fc3\u5730\u7eaf\u539a\u7684\u4eba\uff0c\u4ed6\u4e0d\u4f1a\u8d1f\u4f60\u7684\u3002\u4f60\u76f8\u4fe1\u4ed6\uff0c\u6211\u4e5f\u4e00\u6837\u76f8\u4fe1\u4ed6\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u660e\u5929\u6211\u4e5f\u8981\u548c\u5e08\u7236\u8d70\u4e86\u3002\u53ef\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u8fdf\u65e9\u90fd\u8981\u79bb\u5f00\u8fd9 \u513f\u7684\uff0c\u4f46\u54b1\u4eec\u5144\u59b9\u96be\u5f97\u76f8\u805a\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u591a\u4f4f\u51e0\u5929\uff1f\u96be\u9053\u4f60\u5fc3\u91cc\u5c31\u53ea\u6709\u4e00\u4e2a\u6d77\u54e5\uff1f\u55ef\uff0c\u6211 \u662f\u548c\u4f60\u8bf4\u7b11\u7684\uff0c\u4f60\u7684\u6d77\u54e5\u8d70\u4e86\uff0c\u4f60\u8ddf\u7740\u8d70\u4e5f\u662f\u5e94\u8be5\u3002\u514d\u5f97\u4f60\u4eec\u79bb\u5f00\u5f97\u592a\u8fdc\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u5012\u5e76\u4e0d\u662f\u53ea\u662f\u4e3a\u4e86\u6d77\u5929\uff0c\u6211\u5e08\u7236\u662f\u4e3a\u4e86\u672c\u95e8\u7684\u4e8b\u60c5\u8981\u8d76\u7740\u56de\u53bb\u7684\u3002\u201d \u5510\u52aa\u73e0\u7a46\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u4f60\u65e9\u8d70\u4e5f\u597d\uff0c\u514d\u5f97\u9ebb\u70e6\u3002\u201d\u8c37\u4e2d\u83b2\u8be7\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u4f60\u5acc \u6211\u5728\u8fd9\u91cc\u7ed9\u4f60\u589e\u6dfb\u9ebb\u70e6\u4e48\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4e0d\u9519\u3002\u8fd9\u9ebb\u70e6\u7684\u786e\u662f\u4f60\u60f9\u6765\u7684\uff0c\u4e5f\u662f\u6211\u4ece\u524d\u6ca1\u6709\u60f3\u5230\u7684\u3002\u201d\u8c37\u4e2d\u83b2 \u9053\uff1a\u201c\u5230\u5e95\u662f\u4ec0\u4e48\u9ebb\u70e6\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a \u3000\u3000\u201c\u4f60\u73b0\u5728\u662f\u516c\u4e3b\u4e86\uff0c\u800c\u4e14\u4eba\u4eba\u77e5\u9053\uff0c\u8fd9\u4f4d\u516c\u4e3b\u53c8\u7f8e\u8c8c\uff0c\u53c8\u806a\u660e\uff0c\u53c8\u61c2\u5f97\u6b66\u827a\u2026\u2026\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u55d4\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u4eca\u5929\u600e\u4e48\u7684\u8001\u662f\u62ff\u6211\u5f00\u73a9\u7b11\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u8fd9\u53ef\u4e0d\u662f \u5f00\u73a9\u7b11\u5462\uff0c\u5c31\u56e0\u4e3a\u4f60\u7684\u58f0\u540d\u5df2\u4f3c\u957f\u4e86\u7fc5\u8180\uff0c\u98de\u8fc7\u4e86\u8349\u539f\uff0c\u5468\u56f4\u7684\u90e8\u843d\u4e5f\u77e5\u9053\u4e86\uff0c\u521a\u624d\u5c31 \u6709\u4e00\u4e2a\u8fdc\u9053\u800c\u6765\u7684\u5ba2\u4eba\u5411\u6211\u63d0\u4eb2\u5462\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54e6\uff0c\u6709\u8fd9\u6837\u7684\u4e8b\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u8fd9\u662f\u5e03\u5c14\u6c81\u65d7\u7684\u4f7f\u8005\uff0c\u6765\u4e3a\u4ed6\u4eec\u7684\u738b\u5b50\u6c42\u5a5a\u7684\u3002\u4ed6\u4eec\u9001\u6765\u4e86\u8d35\u91cd\u7684 \u793c\u7269\uff0c\u8fd8\u9644\u4e86\u4e00\u4efd\u76df\u7ea6\uff0c\u6c42\u6211\u5c06\u516c\u4e3b\u2018\u4e0b\u5ac1\u2019\u4ed6\u4eec\u7684\u738b\u5b50\uff0c\u4ee5\u540e\u5f7c\u6b64\u7ed3\u4e3a\u540c\u76df\u3002\u201d\u8c37\u4e2d \u83b2\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u6211\u770b\u5728\u76df\u7ea6\u7684\u4efd\u4e0a\uff0c\u5df1\u7ecf\u7b54\u5e94\u4e86\uff01\u201d\u8c37\u4e2d\u83b2\u8df3\u8d77\u6765\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u770b\u4e86\u770b\u5510\u52aa\u73e0\u7a46\u7684\u795e\u6c14\uff0c\u5ffd\u5730\u5657\u55e4\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u6545\u610f\u9017\u6211\u7740\u6025\uff0c\u6211\u624d\u4e0d\u76f8\u4fe1 \u5462\uff01\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u6211\u600e\u4e48\u4f1a\u7b54\u5e94\u4ed6\u5462\uff1f\u5f53\u7136\u662f\u59d4\u5a49\u62d2\u7edd\u4ed6\u4e86\u3002 \u3000\u3000\u90a3\u4f7f\u8005\u5f88\u4e0d\u9ad8\u5174\u5730\u6536\u56de\u793c\u7269\uff0c\u548c\u5e03\u5c14\u6c81\u65d7\u7684\u76df\u7ea6\u4e48\u4e5f\u5c31\u5439\u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u607c\u9053\uff1a\u201c\u771f \u60f3\u4e0d\u5230\u6709\u8fd9\u6837\u7684\u9ebb\u70e6\uff0c\u4e3a\u4e86\u6211\u7684\u7f18\u6545\uff0c\u4ee4\u4f60\u548c\u90bb\u90a6\u7ed3\u4e86\u6028\u4e86\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u662f\u6709\u70b9 \u4e0d\u5927\u6109\u5feb\uff0c\u4f46\u4e5f\u4e0d\u81f3\u4e8e\u7ed3\u6028\u90a3\u4e48\u4e25\u91cd\u3002\u8fd9\u4e9b\u9ebb\u70e6\u4ee5\u540e\u6050\u6015\u8fd8\u6709\u5f97\u6765\uff0c\u6240\u4ee5\u6211\u4e5f\u4f46\u613f\u4f60\u65e9 \u65e9\u6709\u4e86\u9a78\u9a6c\uff0c\u8ba9\u6211\u597d\u6709\u4e2a\u54cd\u4eae\u7684\u501f\u53e3\u53ef\u4ee5\u62d2\u7edd\u4eba\u5bb6\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u8fd9\u6050\u6015\u4e0d\u80fd\u5982\u4f60\u6240\u613f\u4e86\u3002\u597d\u5728\u660e\u5929\u5c31\u79bb\u5f00\u4e86\uff0c\u8fd9\u4e9b\u9ebb\u70e6\u4e5f\u4e0d\u4f1a\u627e\u5230 \u6211\u7684\u5934\u4e0a\u4e86\u3002\u201d\u53c8\u95ee\u9053\uff1a\u201c\u4eca\u65e5\u7684\u65e9\u671d\u8fd8\u6709\u522b\u7684\u65b0\u95fb\u4e48\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u51e0\u4e2a\u90bb\u8fd1\u7684\u5c0f\u90a6\u548c\u90e8\u843d\u6d3e\u4eba\u6765\u5411\u6211\u9053\u8d3a\uff0c\u90a3\u6606\u5e03\u5170 \u56fd\u4e5f\u5728\u5176\u5185\uff0c\u8fd9\u5012\u662f\u6709\u70b9\u5947\u602a\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u5947\u602a\uff1f\u4e00\u56fd\u7684\u65b0\u541b\u5373\u4f4d\uff0c\u90bb\u90a6 \u6d3e\u4eba\u9053\u8d3a\uff0c\u4f60\u4e0d\u662f\u8bf4\u8fc7\u8fd9\u6837\u7684\u4e8b\u60c5\u5f88\u666e\u901a\u5417\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6606\u5e03\u5170\u56fd\u548c\u54b1\u4eec\u9694\u7740\u4e00\u5ea7\u5927\u5c71\uff0c\u8def\u7a0b\u867d\u7136\u6ca1\u6709\u5e03\u5c14\u6c81\u65d7\u90a3\u4e48\u8fdc\uff0c\u4f46 \u5374\u96be\u8d70\u5f97\u591a\u3002\u5e03\u5c14\u6c81\u65d7\u548c\u54b1\u4eec\u7684\u8ddd\u79bb\u516b\u767e\u591a\u91cc\uff0c\u4f46\u4e2d\u95f4\u662f\u8349\u539f\uff0c\u5feb\u9a6c\u75be\u9a70\uff0c\u4e09\u56db\u5929\u5c31\u53ef \u4ee5\u5230\u4e86\u3002\u4ece\u6606\u5e03\u5170\u56fd\u6765\u54b1\u4eec\u8fd9\u513f\uff0c\u5c71\u8def\u5d0e\u5c96\u5341\u7a0b\u91cc\u53ea\u6709\u4e00\u7a0b\u662f\u53ef\u4ee5\u9a91\u9a6c\u7684\uff0c\u6240\u4ee5\u8d77\u7801\u8981 \u8d70\u4e2a\u5341\u5929\u534a\u6708\u3002\u6211\u505a\u4e86\u56fd\u738b\uff0c\u4eca\u5929\u624d\u521a\u6ee1\u5341\u5929\u3002\u4ed6\u4eec\u7684\u6d88\u606f\u600e\u4e48\u5f97\u5230\u8fd9\u6837\u5feb\uff1f\u201d\u8fd9\u4e48\u4e00 \u8bf4\uff0c\u8c37\u4e2d\u83b2\u4e5f\u89c9\u5f97\u6709\u70b9\u5947\u602a\u4e86\u3002 \u3000\u3000\u8bf4\uff1a\u201c\u83ab\u9053\u90a3\u4f7f\u8005\u662f\u98de\u6bdb\u817f\u4e0d\u6210\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4f46\u53c8\u662f\u8c01\u7ed9\u4ed6\u4eec\u4f20\u7684\u6d88\u606f\uff0c\u4f20 \u5f97\u5982\u6b64\u4e4b\u5feb\uff1f\u201d\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u4f60\u5fc3\u91cc\u6709\u7591\u60d1\uff0c\u600e\u4e48\u4e0d\u95ee\u95ee\u90a3\u4e2a\u4f7f\u8005\u5374\u6765\u95ee\u6211\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4e00\u4e2a\u56fd\u738b\u63a5\u89c1\u5916\u56fd\u7684\u4f7f\u8005\uff0c\u53ef\u4ee5\u6beb\u65e0\u62d8\u675f\u7684\u8c08\u8bdd\u7684\u4e48\uff1f\u63a5 \u89c1\u7684\u4eea\u793c\u90fd\u662f\u5b89\u6392\u597d\u7684\uff0c\u4ed6\u6765\u5448\u9012\u4ed6\u7684\u56fd\u738b\u7684\u8d3a\u4e66\uff0c\u6211\u8fd9\u91cc\u6709\u4e2a\u5fa1\u524d\u5927\u81e3\u63a5\u4e0b\uff0c\u8f6c\u5448\u7ed9 \u6211\uff0c\u7136\u540e\u6211\u624d\u8d77\u7acb\uff0c\u95ee\u4ed6\u4eec\u56fd\u738b\u7684\u597d\u3002\u8fd8\u793c\u4e4b\u540e\uff0c\u4e92\u76f8\u8bf4\u51e0\u53e5\u795d\u8d3a\u7684\u8bdd\u8bed\uff0c\u4ed6\u5c31\u8981\u544a\u9000 \u4e86\u3002\u6211\u600e\u80fd\u90a3\u6837\u95ee\u4ed6\uff1f\u90a3\u662f\u6709\u5931\u793c\u8c8c\u7684\u554a\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u602a\u4e0b\u5f97\u4f60\u4e00\u76f4\u4e0d\u613f\u610f\u505a\u56fd\u738b\uff0c\u539f\u6765\u505a\u4e86\u56fd\u738b\u5c31\u6709\u90a3\u4e48\u591a\u62d8\u675f\uff0c\u90a3\u4e48 \u591a\u9ebb\u70e6\uff0c\u4e00\u4e3e\u4e00\u52a8\uff0c\u4e00\u8a00\u4e00\u8bed\uff0c\u90fd\u5f97\u5c0f\u5fc3\u5728\u610f\u7684\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4e0d\u8fc7\u6211\u5bf9\u8fd9\u4e2a\u4f7f\u8005\u8fd8\u662f\u591a\u95ee\u4e86\u51e0\u53e5\u3002\u54b1\u4eec\u524d\u5929\u4e0d\u662f\u770b\u8fc7\u6606\u5e03\u5170\u90a3\u5f20 \u56fd\u4e66\u4e48\uff1f\u90a3\u662f\u4e03\u5341\u5e74\u524d\uff0c\u4ed6\u4eec\u7684\u65b0\u738b\u5373\u4f4d\uff0c\u901a\u77e5\u54b1\u4eec\u7684\u3002\u6211\u8bf4\u8d77\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u90a3\u4f7f\u8005\u544a\u8bc9 \u6211\uff0c\u539f\u6765\u4ed6\u4eec\u8fd9\u4f4d\u8001\u738b\uff0c\u5982\u4eca\u8fd8\u5728\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u90a3\u4e0d\u662f\u5f88\u8001\u4e86\u4e48\u4e86\u600e\u8fd8\u80fd\u5904\u7406\u56fd\u4e8b\uff1f\u201d \u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u90a3\u4f4d\u8001\u738b\u662f\u5341\u4e5d\u5c81\u767b\u57fa\u7684\uff0c\u5982\u4eca\u5df2\u662f\u516b\u5341\u4e5d\u5c81\u4e86\u3002\u4ed6\u5728\u5341\u5e74\u4e4b\u524d\u9000\u4f4d\u505a \u592a\u4e0a\u7687\uff0c\u73b0\u5728\u7684\u56fd\u738b\u662f\u4ed6\u7684\u957f\u5b59\uff0c\u4e0d\u8fc7\u56db\u5341\u591a\u5c81\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54b1\u4eec\u7684\u5148\u79df\u73cd\u85cf\u8fd9\u5f20\u56fd\u4e66\uff0c\u4e0d\u77e5\u662f\u4f55\u7528\u610f\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6240\u4ee5\u6211 \u603b\u89c9\u5f97\u6709\u70b9\u5947\u602a\uff0c\u8fd9\u6b21\u6211\u505a\u4e86\u56fd\u738b\uff0c\u4ed6\u4eec\u53c8\u8fd9\u4e48\u5feb\u6765\u5230\u9053\u8d3a\uff0c\u4f3c\u4e4e\u8868\u793a\u54b1\u4eec\u4e24\u56fd\u7684\u90a6\u4ea4 \u4e0e\u4f17\u4e0d\u540c\u3002\u4f46\u6211\u9000\u671d\u4e4b\u540e\uff0c\u79c1\u4e0b\u95ee\u8d77\u51e0\u4f4d\u524d\u671d\u7684\u8001\u81e3\uff0c\u5374\u6070\u597d\u4e0e\u6211\u8fd9\u60f3\u6cd5\u76f8\u53cd\uff0c\u539f\u6765\u5728 \u4ed6\u4eec\u8001\u738b\u5728\u4f4d\u7684\u65f6\u5019\u3002\u53ea\u662f\u767b\u7acb\u548c\u9000\u4f4d\u4e24\u6b21\u7ed9\u54b1\u4eec\u9001\u8fc7\u56fd\u4e66\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u4e00\u76f4\u662f\u6ca1\u6709\u6765 \u5f80\u7684\u3002\u4f46\u5728\u76d6\u6e29\u7be1\u4f4d\u7684\u65f6\u5019\uff0c\u4ed6\u5374\u53c8\u501f\u53e3\u4e0e\u54b1\u4eec\u5510\u52aa\u4e00\u59d3\u4e16\u4ee3\u4ea4\u597d\uff0c\u56e0\u6b64\u4e0d\u627f\u8ba4\u76d6\u6e29\u7684 \u738b\u4f4d\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8fd9\u5c31\u5f88\u4e0d\u9519\u554a\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u5374\u7528\u8fd9\u4e2a\u501f\u53e3\u4e0e\u76d6\u6e29\u6253 \u4e86\u4e00\u4ed7\uff0c\u5360\u4e86\u54b1\u4eec\u5317\u90e8\u4e00\u5757\u5730\u65b9\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u73b0\u5728\u4f60\u505a\u4e86\u56fd\u738b\uff0c\u53ef\u4ee5\u540d\u6b63\u8a00\u987a\u7684\u5411 \u4ed6\u4eec\u7d22\u56de\u9886\u571f\u4e86\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5df2\u62df\u597d\u7d22\u56de\u9886\u571f\u7684\u56fd\u4e66\uff0c\u5c31\u51c6\u5907\u4ea4\u8fd9\u4e2a\u4f7f \u81e3\u5e26\u56de\u53bb\u3002\u201d \u3000\u3000\u6b47\u4e86\u4e00\u6b47\uff0c\u5510\u52aa\u73e0\u7a46\u8bf4\u9053\uff1a\u201c\u4e0d\u8c08\u6606\u5e03\u5170\u56fd\u7684\u4e8b\u60c5\u4e86\uff0c\u4f60\u660e\u5929\u8981\u79bb\u5f00\u4e86\uff0c\u6211\u6258\u4f60\u529e \u4e00\u4ef6\u4e8b\u60c5\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6211\u8ba1\u5212\u5c06\u73e0\u5b9d\u5206\u6279\u53d8\u5356\uff0c\u5df2\u6258 \u4e86\u5fc3\u8179\u5e26\u4e86\u4e24\u6279\u5230\u6ce2\u65af\u548c\u5370\u5ea6\u7684\u73e0\u5b9d\u5e02\u573a\u6c42\u552e\u4e86\u3002\u4f60\u73b0\u5728\u4e0e\u5e08\u7236\u91cd\u56de\u4e2d\u571f\uff0c\u6b63\u597d\u7ed9\u6211\u4e5f \u5e26\u4e00\u6279\u53bb\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u6211\u53ef\u4e0d\u4f1a\u505a\u73e0\u5b9d\u4e70\u5356\u5440\u3002\u201d\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a \u3000\u3000\u201c\u600e\u7528\u4f60\u4eb2\u81ea\u53bb\u505a\u4e70\u5356\u3002\u9099\u5c71\u6d3e\u548c\u4e10\u5e2e\u6709\u8bb8\u591a\u7cbe\u660e\u5e72\u7ec3\u7684\u4eba\uff0c\u4f60\u6258\u7ffc\u5e2e\u4e3b\u6216\u767d\u5e08\u53d4 \u5c31\u51c6\u80fd\u7ed9\u4f60\u529e\u5f97\u59a5\u59a5\u5e16\u5e16\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f46\u6211\u53ef\u4e0d\u80fd\u5e26\u5f97\u592a\u591a\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a \u201c\u6211\u5f53\u7136\u662f\u9009\u51e0\u4ef6\u6700\u73cd\u8d35\u7684\u7ed9\u4f60\u5e26\u53bb\u3002\u201d\u4ed6\u7b11\u4e86\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u559c\u6b22\u7684\uff0c\u4f60\u4e5f\u53ef\u4ee5\u81ea \u5df1\u6311\u9009\u3002\u4ed6\u65e5\u4f60\u7ed3\u5a5a\u7684\u65f6\u5019\uff0c\u6211\u672a\u5fc5\u5728\u573a\uff0c\u5c31\u5f53\u4f5c\u6211\u9001\u7684\u793c\u7269\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u6545\u610f\u677f\u8d77\u8138\u5b54\u9053\uff1a\u201c\u4f60\u8bf4\u8fc7\u8fd9\u4e9b\u73cd\u5b9d\u4e0d\u80fd\u7b97\u662f\u54b1\u4eec\u7684\uff0c\u4f60\u600e\u53ef\u4ee5\u5047\u516c\u6d4e\u79c1\uff1f\u201d \u5510\u52aa\u73e0\u7a46\u54c8\u54c8\u7b11\u76f4\uff1a\u201c\u597d\u5389\u5bb3\u7684\u4e00\u628a\u5c0f\u5634\uff0c\u53ef\u60dc\u4f60\u5c31\u8981\u8d70\u4e86\uff0c\u8981\u4e0d\u7136\u6211\u5012\u53ef\u4ee5\u5c01\u4f60\u505a\u4e2a \u5973\u5fa1\u53f2\uff0c\u4e13\u53f8\u529d\u8c0f\u4e4b\u8d23\u3002\u201d\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u522b\u8bf4\u7b11\u4e86\uff0c\u8981\u53bb\u5c31\u8d76\u5feb\u53bb\u5427\u3002\u201d \u3000\u3000\u90a3\u6761\u79d8\u5bc6\u7684\u5730\u9053\u5728\u4e00\u5ea7\u5047\u5c71\u5e95\u4e0b\uff0c\u8981\u7528\u5f88\u5de7\u5999\u7684\u65b9\u6cd5\u79fb\u5f00\u5f53\u4e2d\u7684\u4e00\u5757\u77f3\u5934\u624d\u80fd\u8fdb\u53bb \u7684\uff0c\u5510\u52aa\u73e0\u7a46\u6b63\u8981\u65cb\u8f6c\u673a\u5173\uff0c\u79fb\u5f00\u90a3\u5757\u77f3\u5934\uff0c\u5ffd\u5730\u5446\u4e86\u4e00\u5446\uff0c\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4f3c\u4e4e\u6709\u4eba\u6765\u8fc7\u3002\u201d\u8c37\u4e2d\u83b2\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a \u201c\u8fd9\u77f3\u5934\u4e0a\u672c\u662f\u957f\u6ee1\u9752\u82d4\u7684\uff0c\u6211\u4e0a\u6b21\u6765\u7684\u65f6\u5019\uff0c\u64e6\u53bb\u4e86\u4e00\u5c0f\u5757\uff0c\u73b0\u5728\u4f60\u77a7\uff0c\u5df2\u662f\u5149\u79c3\u79c3 \u7684\u4e00\u5927\u7247\u4e86\u3002\u4ece\u8fd9\u8ff9\u8c61\u8fd8\u53ef\u4ee5\u770b\u51fa\uff0c\u540e\u6765\u7684\u8fd9\u4e2a\u4eba\u8eab\u4f53\u5b9a\u5f88\u9b41\u68a7\uff0c\u624b\u638c\u6bd4\u6211\u7684\u5927\u5f97\u591a\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u8981\u662f\u5f53\u771f\u6709\u8d3c\u4eba\u5077\u8fdb\u4e86\u5b9d\u5e93\uff0c\u90a3\u635f\u5931\u53ef\u5c31\u592a\u5927\u4e86\u3002\u201d\u4e24\u5144\u59b9\u5fd0 \u5fd1\u4e0d\u5b89\uff0c\u8fdb\u4e86\u5730\u9053\uff0c\u8d70\u4e86\u4e00\u7a0b\uff0c\u5c06\u8fd1\u5b9d\u5e93\uff0c\u8c37\u4e2d\u83b2\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u53c8\u95fb\u5230\u90a3\u80a1\u5f02\u9999\u4e86\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u529f\u529b\u6df1\u6e5b\uff0c\u8033\u76ee\u66f4\u4e3a\u806a\u654f\uff0c\u5728\u59b9\u59b9\u8033\u8fb9\u5c0f\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8d3c\u4eba\u8fd8\u6ca1\u6709\u8d70\uff0c\u6211\u542c\u5f97\u51fa\u91cc\u9762\u7684\u58f0\u606f\u3002\u54b1\u4eec\u95ed\u4e86\u7a74\u9053\uff0c\u4e00\u65b9\u9762\u8fd0\u6c14\u5fa1\u6bd2\uff0c\u4e00\u65b9\u9762 \u63d0\u9632\u6697\u88ad\u3002\u201d\u4e24\u4eba\u5747\u662f\u53c8\u60ca\u53c8\u559c\uff0c\u60ca\u8005\u662f\u7adf\u7136\u8fd8\u6709\u5916\u4eba\u77e5\u9053\u8fd9\u5730\u9053\u7684\u79d8\u5bc6\uff0c\u559c\u8005\u662f\u8d3c\u4eba \u672a\u8d70\uff0c\u53ef\u4ee5\u4e00\u7f51\u6210\u64d2\u3002 \u3000\u3000\u5144\u59b9\u4fe9\u63d0\u4e86\u53e3\u6c14\uff0c\u51e0\u4e4e\u662f\u811a\u4e0d\u6cbe\u5730\u7684\u9a6d\u6c14\u800c\u884c\uff0c\u6084\u65e0\u58f0\u606f\u7684\u5230\u4e86\u5b9d\u5e93\u95e8\u53e3\uff0c\u7a81\u7136\u63a8 \u5f00\u77f3\u95e8\uff0c\u6251\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u91cc\u9762\u7684\u8d3c\u4eba\u84e6\u7136\u60ca\u89c9\uff0c\u53cd\u5e94\u4e5f\u662f\u5feb\u5230\u6781\u70b9\uff0c\u5510\u52aa\u73e0\u7a46\u7acb\u8db3\u672a\u7a33\uff0c\u53ea\u89c9\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c \u5df2\u5230\u8111\u540e\uff0c\u5510\u52aa\u73e0\u7a46\u53cd\u624b\u4e00\u63a8\uff0c\u5df2\u6293\u7740\u90a3\u4eba\u7684\u81c2\u818a\uff0c\u5ffd\u89c9\u6ed1\u4e0d\u7559\u624b\uff0c\u800c\u4e14\u6709\u4e00\u80a1\u5927\u529b\u53cd \u9707\u8fc7\u6765\uff0c\u5510\u52aa\u73e0\u7a46\u7adf\u7136\u62ff\u634f\u4e0d\u4f4f\uff0c\u7ed9\u4ed6\u8d70\u8131\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u4eba\u5df2\u662f\u56de\u8f6c\u5200\u950b\uff0c\u5411 \u5510\u52aa\u73e0\u7a46\u7684\u80f8\u53e3\u731b\u6233\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u6b66\u529f\u5012\u771f\u5f88\u4e0d\u9519\u554a\uff0c\u7adf\u4f3c\u4e0d\u5728\u90a3\u6587\u5ef7\u58c1\u4e4b\u4e0b\u3002\u201d \u90a3\u4eba\u7684\u5175\u5668\u662f\u4e00\u628a\u5bd2\u5149\u95ea\u95ea\u7684\u77ed\u5200\uff0c\u62db\u6570\u72e0\u8fa3\u975e\u5e38\uff0c\u5ba4\u5e93\u91cc\u867d\u65e0\u706f\u706b\uff0c\u4f46\u73cd\u73e0\u5b9d\u77f3\u53d1\u51fa \u7684\u5149\u8292\u4e5f\u53ef\u4ee5\u770b\u5f97\u89c1\u4e1c\u897f\uff0c\u5510\u52aa\u73e0\u7a46\u65bd\u5c55\u5f39\u6307\u795e\u901a\u7684\u529f\u592b\uff0c\u5f39\u5f00\u4e86\u90a3\u4eba\u7684\u77ed\u5200\uff0c\u5b9a\u775b\u4e00 \u77a7\uff0c\u53ea\u89c1\u679c\u7136\u662f\u4e2a\u8eab\u4f53\u9b41\u68a7\u7684\u5927\u6c49\uff0c\u4f46\u7528\u9ed1\u5e03\u8499\u4e86\u9762\u5b54\uff0c\u9762\u8c8c\u5374\u662f\u770b\u4e0d\u6e05\u695a\u3002\u5510\u52aa\u73e0\u7a46 \u8be7\u5f02\u6781\u4e86\uff0c\u539f\u6765\u4ed6\u867d\u7136\u770b\u4e0d\u89c1\u90a3\u4eba\u7684\u9762\u8c8c\uff0c\u4f46\u5374\u9690\u9690\u89c9\u5f97\u4f3c\u66fe\u76f8\u8bc6\uff0c\u597d\u4f3c\u5728\u54ea\u513f\u89c1\u8fc7\u4e00 \u822c\u3002 \u3000\u3000\u5b9d\u5e93\u91cc\u6b63\u5de7\u4e5f\u662f\u4e24\u4e2a\u8499\u9762\u8d3c\u4eba\uff0c\u5510\u52aa\u73e0\u7a46\u4e0e\u90a3\u5927\u6c49\u640f\u6597\u7684\u65f6\u5019\uff0c\u8c37\u4e2d\u83b2\u4e5f\u5df2\u4e0e\u53e6\u5916 \u4e00\u4e2a\u8d3c\u4eba\u4ea4\u4e0a\u4e86\u624b\u3002\u8c37\u4e2d\u83b2\u50cf\u4ed6\u54e5\u54e5\u4e00\u6837\uff0c\u4e5f\u662f\u60ca\u5f02\u4e07\u5206\uff01 \u3000\u3000\u539f\u6765\u8c37\u4e2d\u83b2\u4e5f\u89c9\u5f97\u4e0e\u5979\u4ea4\u624b\u7684\u8fd9\u4e2a\u8499\u9762\u4eba\u597d\u751f\u773c\u719f\uff0c\u4f3c\u662f\u5728\u54ea\u513f\u89c1\u8fc7\u4f3c\u7684\uff0c\u4f46\u7a76\u7adf \u662f\u8c01\uff0c\u4e00\u65f6\u4e4b\u95f4\u5374\u60f3\u4e0d\u8d77\u6765\u8c03\u90a3\u4eba\u7528\u4e24\u9762\u9ed1\u9edd\u9edd\u7684\u4ee4\u724c\uff0c\u975e\u91d1\u975e\u94c1\uff0c\u4e5f\u4e0d\u77e5\u662f\u4ec0\u4e48\u4e1c\u897f \u505a\u7684\uff0c\u4e00\u78b0\u4e0a\u8c37\u4e2d\u83b2\u7684\u5b9d\u5251\uff0c\u4fbf\u53d1\u51fa\u6e05\u60a6\u7684\u91d1\u77f3\u4e4b\u58f0\uff0c\u9707\u5f97\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\u3002 \u3000\u3000\u5b9d\u5e93\u867d\u7136\u5bbd\u5e7f\uff0c\u4f46\u4e00\u5806\u5806\u7684\u91d1\u94f6\u5b9d\u77f3\uff0c\u661f\u7f57\u68cb\u5e03\uff0c\u53ef\u4ee5\u56de\u65cb\u7684\u4f59\u5730\u5374\u5b9e\u5728\u4e0d\u591a\u3002\u8c37 \u4e2d\u83b2\u80dc\u5728\u8eab\u6cd5\u8f7b\u7075\uff0c\u5c55\u5f00\u4e86\u7384\u5973\u5251\u6cd5\uff0c\u5982\u81c2\u4f7f\u6307\uff0c\u4e0d\u8bba\u5728\u5bbd\u655e\u4e4b\u5730\u6216\u72ed\u7a84\u4e4b\u5883\uff0c\u90fd\u662f\u4e00 \u6837\u7684\u8fd0\u7528\u5f97\u795e\u5999\u65e0\u65b9\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u4fbf\u5df2\u5360\u5f97\u4e0a\u98ce\uff0c\u5c06\u90a3\u4eba\u8feb\u5f97\u6b65\u6b65\u540e\u9000\u3002 \u3000\u3000\u548c\u5510\u52aa\u73e0\u7a46\u4ea4\u624b\u7684\u90a3\u4e2a\u5927\u6c49\u529f\u529b\u6df1\u539a\u5f97\u591a\uff0c\u5510\u52aa\u73e0\u7a46\u6700\u521d\u4ee5\u91d1\u521a\u638c\u6cd5\u5bf9\u4ed8\uff0c\u8fde\u53d1\u4e86 \u4e00\u5341\u516b\u638c\uff0c\u90a3\u5927\u6c49\u4ecd\u7136\u652f\u6301\u5f97\u4f4f\uff0c\u4e0d\u8fc7\u5fae\u5fae\u5598\u6c14\u800c\u5df2\u3002\u5510\u52aa\u73e0\u7a46\u5927\u4e3a\u7eb3\u7f55\uff0c\u4ed6\u5728\u8e0f\u8fdb\u5b9d \u5e93\u4e4b\u524d\uff0c\u5fc3\u91cc\u4ee5\u4e3a\u6765\u76d7\u5b9d\u7684\u4eba\u4e00\u5b9a\u662f\u5929\u9b54\u6559\u4e3b\u8fd9\u4e00\u4f19\uff0c\u54ea\u77e5\u5374\u5927\u51fa\u4ed6\u610f\u6599\u4e4b\u5916\u3002\u8fd9\u8499\u9762 \u5927\u6c49\u8eab\u4f53\u9b41\u68a7\uff0c\u6bd4\u6587\u5ef7\u58c1\u9ad8\u5927\u591a\u4e86\u3002\u548c\u8c37\u4e2d\u83b2\u4ea4\u624b\u7684\u90a3\u4e2a\u4eba\uff0c\u8eab\u6750\u5012\u662f\u4e0e\u5929\u9b54\u6559\u4e3b\u5dee\u4e0d \u591a\uff0c\u4f46\u53ef\u4ee5\u770b\u5f97\u51fa\u6765\uff0c\u8fd9\u4eba\u51b3\u975e\u5973\u626e\u7537\u88c5\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u773c\u5149\u4e00\u77a5\uff0c\u89c1\u59b9\u59b9\u5df2\u5360\u4e86\u4e0a\u98ce\uff0c\u653e\u5fc3\u4e0d\u5c11\uff0c\u5f53\u4e0b\u4e00\u58f0\u559d\u9053\uff0c\u201c\u4f60\u4eec\u7a76\u7adf\u662f \u8c01\uff1f\u518d\u4e0d\u8bf4\u8bdd\uff0c\u53ea\u6709\u81ea\u5df1\u5403\u4e8f\uff01\u201d\u90a3\u4e24\u4e2a\u8499\u9762\u4eba\u5140\u662f\u4e00\u58f0\u4e0d\u54cd\uff0c\u62c6\u547d\u54d1\u6597\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6012\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u81ea\u53d6\u5176\u8fb1\uff0c\u53ef\u4f11\u602a\u6211\u624b\u4e0b\u65e0\u60c5\uff01\u201d\u638c\u6cd5\u4e00\u53d8\uff0c\u5ffd\u5730\u53d1\u51fa\u95f7 \u96f7\u4e5f\u4f3c\u7684\u58f0\u97f3\uff0c\u638c\u98ce\u6240\u53ca\uff0c\u9707\u5f97\u73cd\u73e0\u5b9d\u77f3\uff0c\u90fd\u968f\u5730\u4e71\u6eda\uff0c\u4f7f\u7684\u4e43\u662f\u638c\u529b\u6700\u5f3a\u6a2a\u9738\u9053\u7684\u5927 \u4e58\u822c\u82e5\u638c\u3002 \u3000\u3000\u5927\u4e58\u822c\u82e5\u638c\u4e13\u4f24\u5947\u7ecf\u516b\u8109\uff0c\u6328\u4e0a\u4e00\u638c\uff0c\u6027\u547d\u96be\u4fdd\uff0c\u5510\u52aa\u73e0\u7a46\u6700\u521d\u4e0d\u613f\u7528\u8fd9\u6837\u72e0\u8fa3\u7684 \u638c\u6cd5\uff0c\u624d\u8ba9\u90a3\u4eba\u6253\u6210\u5e73\u624b\u3002\u5982\u4eca\u8fd9\u5927\u4e58\u6bb7\u82e5\u638c\u4e00\u4f7f\u51fa\u6765\uff0c\u90a3\u4eba\u53ef\u5c31\u611f\u5230\u5403\u4e0d\u6d88\u4e86\uff0c\u4e0d\u8fc7 \u7247\u523b\u53ea\u89c1\u4ed6\u6c57\u5982\u96e8\u4e0b\uff0c\u5934\u9876\u4e0a\u4f3c\u653e\u4e86\u4e2a\u84b8\u7b3c\u4e00\u822c\uff0c\u53d1\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\u3002\u5510\u52aa\u73e0\u7a46\u559d\u9053\uff1a \u201c\u4f60\u8981\u4e0d\u8981\u6027\u547d\uff1f\u5feb\u5feb\u5410\u9732\u771f\u60c5\u3002\u201d\u90a3\u8499\u9762\u5927\u6c49\u53ea\u662f\u54fc\u4e86\u4e00\u58f0\uff0c\u4f9d\u7136\u4e0d\u80af\u8bf4\u8bdd\u3002 \u3000\u3000\u8fd9\u8499\u9762\u5927\u6c49\u72b9\u81ea\u82e6\u82e6\u652f\u6491\uff0c\u8c37\u4e2d\u83b2\u7684\u90a3\u4e2a\u5bf9\u624b\u5374\u5df2\u652f\u6301\u4e0d\u4f4f\uff0c\u8fd9\u65f6\u8c37\u4e2d\u83b2\u6b63\u4f7f\u5230\u4e00 \u62db\u201c\u9f99\u95e8\u4e09\u53e0\u6d6a\u201d\uff0c\u8fde\u73af\u4e09\u5f0f\uff0c\u4e00\u5f0f\u6bd4\u4e00\u5f0f\u51cc\u5389\uff0c\u5f53\u771f\u5c31\u4f3c\u72c2\u6d9b\u9a87\u6d6a\uff0c\u75be\u5377\u800c\u6765\u3002\u4e00\u4e2a \u6d6a\u5934\u9ad8\u8fc7\u4e00\u4e2a\u6d6a\u5934\uff0c\u90a3\u4eba\u65e0\u6cd5\u62b5\u5fa1\uff0c\u6b65\u6b65\u540e\u9000\uff0c\u6070\u597d\u8e0f\u7740\u4e00\u5806\u5728\u5730\u4e0a\u6eda\u52a8\u7684\u73cd\u73e0\uff0c\u811a\u6b65 \u4e00\u6ed1\u3002\u56db\u811a\u671d\u5929\u5730\u8dcc\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u4e00\u5251\u6307\u7740\u4ed6\u7684\u5589\u5499\uff0c\u559d\u9053\uff1a\u201c\u8ba4\u8f93\u4e86\u4e48\uff1f\u8bf4\u4e0d\u8bf4\u8bdd\uff1f\u201d\u90a3\u4eba\u4e5f\u597d\u751f\u4e86\u5f97\uff0c\u5ffd \u5730\u4e00\u4e2a\u9e5e\u5b50\u7ffb\u8eab\uff0c\u53cc\u811a\u8e22\u8d77\uff0c\u5c45\u7136\u8981\u4e0e\u8c37\u4e2d\u83b2\u62fc\u547d\uff0c\u5b81\u6740\u4e0d\u80af\u8ba4\u8f93\uff01\u8c37\u4e2d\u83b2\u7109\u80fd\u7ed9\u4ed6\u8e22 \u4e2d\uff0c\u67f3\u8170\u4e00\u6446\uff0c\u5251\u5c16\u4e00\u5212\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u4eba\u7684\u8499\u9762\u4e2d\u5df2\u7ed9\u8c37\u4e2d\u83b2\u7684\u5251\u5c16\u6311\u5f00 \u4e86\uff01 \u3000\u3000\u8c37\u4e2d\u83b2\u4e00\u770b\uff0c\u4e0d\u89c9\u6115\u7136\uff0c\u539f\u6765\u6b64\u4eba\u4e0d\u662f\u522b\u4e2a\uff0c\u6b63\u662f\u90a3\u5978\u738b\u76d6\u6e29\u4e4b\u5b50\u76d6\u82cf\uff0c\u8c37\u4e2d\u83b2\u4ece \u524d\u88ab\u76d6\u6e29\u64d2\u83b7\u4e4b\u65f6\uff0c\u76d6\u6e29\u66fe\u7528\u5c3d\u5a01\u8feb\u5229\u8bf1\u7684\u624b\u6bb5\uff0c\u8981\u8c37\u4e2d\u83b2\u5ac1\u7ed9\u4ed6\u7684\u513f\u5b50\uff0c\u5176\u95f4\u8fd8\u5229\u7528 \u4e86\u53f6\u51b2\u9704\u53bb\u4f5c\u8bf4\u5ba2\u3002\u8c37\u4e2d\u83b2\u8a93\u6b7b\u4e0d\u4ece\uff0c\u8fd9\u624d\u88ab\u56da\u7981\u5728\u5b64\u5c9b\u7684\u590f\u5bab\u7684\uff0c\u5176\u540e\u76d6\u82cf\u4e5f\u66fe\u5230\u8fc7 \u590f\u5bab\u4e24\u6b21\uff0c\u4e24\u6b21\u90fd\u88ab\u8c37\u4e2d\u83b2\u9a82\u8d70\uff0c\u76d6\u82cf\u5012\u4e5f\u6ca1\u6709\u52a8\u6012\uff0c\u540e\u6765\u5c31\u6ca1\u6709\u518d\u53bb\u4e86\u3002 \u3000\u3000\u5f85\u5230\u76d6\u6e29\u88ab\u6740\uff0c\u4ed6\u7684\u515a\u7fbd\u4e5f\u5168\u88ab\u8083\u6e05\uff0c\u4f46\u5374\u5355\u5355\u4e0d\u89c1\u76d6\u82cf\u7684\u4e0b\u843d\uff0c\u5510\u52aa\u73e0\u7a46\u5144\u59b9\u53ea \u9053\u4ed6\u5df2\u6b7b\u5728\u4e71\u519b\u4e4b\u4e2d\uff0c\u5374\u4e0d\u6599\u4ed6\u8fd8\u533f\u4f0f\u5728\u738b\u5bab\u91cc\u9762\u3002 \u3000\u3000\u8fd9\u65f6\u6b63\u662f\u4ec7\u4eba\u89c1\u9762\uff0c\u5206\u5916\u773c\u7ea2\uff0c\u76d6\u82cf\u8d81\u7740\u8c37\u4e2d\u83b2\u4e00\u6115\u4e4b\u9645\uff0c\u66ae\u5730\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\uff0c\u7ffb \u8d77\u8eab\u6765\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u624b\u4e2d\u90a3\u4e24\u9762\u4ee4\u724c\uff0c\u5df2\u662f\u5411\u8c37\u4e2d\u83b2\u63b7\u5230\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u6012\u9053\uff1a\u201c\u5978\u8d3c\u8fd8\u60f3\u9003\u5417\uff1f\u201d\u6a2a\u5251\u4e00\u51fb\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\u3002 \u3000\u3000\u628a\u524d\u5934\u90a3\u9762\u4ee4\u724c\u53cd\u51fb\u56de\u53bb\uff0c\u6070\u597d\u4e0e\u540e\u9762\u90a3\u9762\u4ee4\u724c\u78b0\u4e2a\u6b63\u7740\uff0c\u540c\u65f6\u5760\u5730\u3002 \u3000\u3000\u76d6\u82cf\u8fd9\u4e00\u63b7\u7528\u5c3d\u4e86\u5403\u5976\u7684\u6c14\u529b\uff0c\u8c37\u4e2d\u83b2\u867d\u7136\u6253\u4e0b\u4ed6\u8fd9\u4e24\u9762\u4ee4\u724c\uff0c\u864e\u53e3\u4e5f\u4e0d\u7981\u4e00\u9635\u9178 \u9ebb\uff0c\u5c31\u8fd9\u4e48\u7684\u963b\u4e86\u4e00\u963b\uff0c\u76d6\u82cf\u5df2\u9000\u5230\u5899\u8fb9\uff0c\u8c37\u4e2d\u83b2\u98de\u6b65\u62a2\u4e0a\uff0c\u6dae\u7684\u4e00\u5251\u773c\u770b\u5c31\u8981\u628a\u4ed6\u9489 \u5728\u5899\u4e0a\uff0c\u90a3\u5899\u58c1\u5ffd\u5730\u88c2\u5f00\u4e86\u4e00\u4e2a\u6d1e\uff0c\u76d6\u82cf\u5df2\u94bb\u8fdb\u6d1e\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u6d1e\u53e3\u6781\u7a84\uff0c\u53ea\u80fd\u5bb9\u5f97\u4e00\u4e2a\u4eba\u901a\u8fc7\uff0c\u8c37\u4e2d\u83b2\u8fd1\u524d\u4e00\u770b\uff0c\u9ed1\u9edd\u9edd\u7684\u4e5f\u4e0d\u77e5\u6709\u591a\u6df1\uff0c\u5979 \u8eab\u4e0a\u6ca1\u5e26\u6697\u5668\uff0c\u968f\u624b\u62fe\u8d77\u4e24\u952d\u91d1\u5b50\uff0c\u7528\u91cd\u624b\u6cd5\u6253\u8fdb\u6d1e\u4e2d\uff0c\u9690\u9690\u542c\u5f97\u76d6\u82cf\u201c\u54ce\u54df\u201d\u7684\u53eb\u4e86 \u4e00\u58f0\uff0c\u6d1e\u4e2d\u968f\u5373\u5192\u51fa\u4e00\u80a1\u9ed1\u70df\uff0c\u8c37\u4e2d\u83b2\u8fde\u5fd9\u95ee\u5f00\uff0c\u5510\u52aa\u73e0\u7a46\u53eb\u9053\uff1a\u201c\u7a77\u5bc7\u83ab\u8ffd\uff0c\u7531\u4ed6\u53bb \u5427\u3002\u201d\u8c37\u4e2d\u83b2\u4e0d\u77e5\u6d1e\u4e2d\u6709\u4f55\u53e4\u602a\uff0c\u53ea\u597d\u8ba9\u76d6\u82cf\u9003\u8dd1\u3002 \u3000\u3000\u539f\u6765\u8fd9\u662f\u76d6\u6e29\u79d8\u5bc6\u4fee\u7b51\u7684\u53e6\u4e00\u4e2a\u51fa\u53e3\uff0c\u53ea\u6709\u4ed6\u4eec\u7236\u5b50\u77e5\u9053\u3002 \u3000\u3000\u90a3\u65e5\u82e5\u4e0d\u662f\u56e0\u4e3a\u76d6\u6e29\u7740\u4e86\u6b27\u9633\u5a49\u7684\u6bd2\u96fe\u660f\u8ff7\uff0c\u4ed6\u4e5f\u53ef\u4ee5\u4ece\u8fd9\u6d1e\u4e2d\u9003\u8d70\u7684\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6015\u90a3\u8499\u9762\u5927\u6c49\u4e5f\u6b65\u76d6\u82cf\u7684\u540e\u5c18\u9003\u8d70\uff0c\u731b\u5730\u4e00\u58f0\u5927\u559d\uff0c\u5927\u4e58\u822c\u7740\u638c\u529b\u5c3d\u53d1\u65e0 \u9057\uff0c\u90a3\u8499\u9762\u5927\u6c49\u95f7\u54fc\u4e00\u58f0\uff0c\u8eab\u5b50\u6447\u6447\u6b32\u5760\uff0c\u5510\u52aa\u73e0\u7a46\u4e00\u62db\u201c\u5c06\u519b\u593a\u5370\u201d\uff0c\u5df2\u628a\u90a3\u4eba\u7684\u81c2 \u818a\u626d\u4f4f\uff0c\u6b63\u8981\u593a\u4ed6\u624b\u4e2d\u7684\u77ed\u5200\uff0c\u90a3\u8499\u9762\u5927\u6c49\u5ffd\u5730\u5012\u8f6c\u5200\u950b\uff0c\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u77ed\u5200\u5df2\u63d2\u8fdb \u80f8\u53e3\uff0c\u76f4\u6ca1\u81f3\u5200\u67c4\uff01 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6599\u4e0d\u5230\u4ed6\u6709\u6b64\u4e00\u7740\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u6b64\u4eba\u5b81\u6b7b\u4e0d\u5c48\uff0c\u5012\u662f\u4e2a\u597d\u6c49\u5b50\u3002\u201d \u8fde\u5fd9\u70b9\u4e86\u4ed6\u4f24\u53e3\u9644\u8fd1\u7684\u4e09\u5904\u7a74\u9053\uff0c\u5e0c\u671b\u80fd\u7559\u7740\u4e00\u6761\u6d3b\u53e3\u3002\u4ed6\u70b9\u8fd9\u4e09\u5904\u7a74\u9053\u672c\u6765\u662f\u53ef\u4ee5\u6682 \u65f6\u6b62\u8840\u7684\uff0c\u54ea\u77e5\u9c9c\u8840\u4ecd\u662f\u6c69\u6c69\u6d41\u51fa\uff0c\u539f\u6765\u90a3\u8499\u9762\u6c49\u5b50\u5728\u4e3e\u5200\u81ea\u6740\u4e4b\u65f6\u3002\u53c8\u4ee5\u5185\u5bb6\u771f\u529b\u81ea \u65ad\u7ecf\u8109\uff0c\u5510\u52aa\u73e0\u7a46\u7684\u5c01\u7a74\u6cd5\u4e5f\u5df2\u5931\u4e86\u4f5c\u7528\u4e86\u3002\u5510\u52aa\u73e0\u7a46\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u767d\u6253 \u4e86\u4e00\u573a\uff0c\u4ecd\u662f\u5f97\u4e0d\u5230\u4f9b\u8f9e\uff0c\u201d\u968f\u624b\u5c31\u63ed\u53bb\u4e86\u90a3\u6c49\u5b50\u7684\u8c6a\u9762\u4e2d\uff0c\u770b\u4ed6\u662f\u8c01\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u4e00\u770b\u4e4b\u4e0b\uff0c\u4e0d\u7531\u5f97\u9762\u5982\u571f\u8272\uff0c\u5446\u82e5\u6728\u9e21\uff0c\u90a3\u60ca\u6115\u7684\u795e\u60c5\uff0c\u6bd4\u521a\u624d\u8c37\u4e2d\u83b2\u4e4b \u8ba4\u51fa\u76d6\u82cf\uff0c\u66f4\u751a\u767e\u500d\uff01\u8c37\u4e2d\u83b2\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u54e5\u54e5\u4f60\u600e\u4e48\u5566\uff1f\u8fd9\u4eba\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u5b9a\u4e86\u5b9a\u5fc3\u795e\uff0c\u8fd9\u624d\u6da9\u58f0\u8bf4\u9053\uff0c\u201c\u4ed6\u5c31\u662f\u90a3\u4e2a\uff0c\u90a3\u4e2a\u6606\u5e03\u5170 \u56fd\u7684\u4f7f\u81e3\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u8c37\u4e2d\u83b2\u4e5f\u4e0d\u7981\u60ca\u8be7\u4e07\u5206\u3002\u8bd5\u60f3\u4e00\u4e2a\u5802\u5802\u7684\u4f7f\u81e3\uff0c\u7adf\u4f1a\u53d8\u4f5c\u76d7\u5b9d \u7684\u8d3c\u4eba\uff0c\u8fd9\u5c82\u975e\u4e0d\u53ef\u60f3\u8c61\u4e4b\u4e8b\uff1f \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u8c37\u4e2d\u83b2\u5b89\u6170\u5979\u7684\u54e5\u54e5\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u4ed6\u4e0d\u597d\uff0c\u8c01\u53eb\u4ed6\u4e0d\u987e\u4f7f\u81e3\u7684\u8eab\u4efd\uff0c\u79c1 \u81ea\u5077\u8fdb\u54b1\u4eec\u7684\u5b9d\u5e93\u6765\u76d7\u5b9d\uff1f\u4ed6\u662f\u7f6a\u6709\u5e94\u5f97\uff0c\u54e5\u54e5\uff0c\u4f60\u53ef\u5e76\u6ca1\u6709\u6740\u9519\u4e86\u4eba\uff01\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u82e6\u7b11\u9053\uff1a\u201c\u73b0\u5728\u53ef\u4e0d\u662f\u8ffd\u7a76\u4ed6\u6709\u7f6a\u65e0\u7f6a\u7684\u95ee\u9898\uff0c\u800c\u662f\u600e\u6837\u5411\u6606\u5e03\u5170\u56fd\u7684\u56fd \u738b\u4ea4\u4ee3\uff0c\u4ed6\u662f\u4ee3\u8868\u4ed6\u4eec\u7684\u56fd\u4e3b\u6765\u5411\u6211\u9053\u8d3a\u7684\uff0c\u5982\u4eca\u5374\u88ab\u6211\u6740\u4e86\uff0c\u8fd9\u4e8b\u4e00\u6296\u9732\u51fa\u6765\uff0c\u53ea\u6015 \u5c31\u8981\u60f9\u8d77\u5927\u5927\u7684\u98ce\u6ce2\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u5411\u6606\u5e03\u5170\u56fd\u7684\u56fd\u738b\u8bf4\u660e\u771f\u76f8\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u8fd9\u4e8b\u5927\u5927\u6709\u635f \u6606\u5e03\u5170\u56fd\u7684\u4f53\u9762\uff0c\u600e\u80fd\u516c\u7136\u8bf4\u51fa\u6765\uff1f\u8bd5\u60f3\u4ed6\u7684\u56fd\u738b\u5018\u82e5\u8ffd\u67e5\u8fd9\u4f7f\u81e3\u7684\u4e0b\u843d\uff0c\u6211\u597d\u56de\u590d\u4ed6 \u9053\uff1a\u2018\u56e0\u4e3a\u4f60\u7684\u4f7f\u81e3\u4f5c\u8d3c\uff0c\u6545\u800c\u88ab\u6211\u6740\u4e86\u2019\u5417\uff1f\u4f60\u60f3\u6606\u5e03\u5170\u56fd\u7684\u56fd\u738b\u770b\u5230\u6211\u8fd9\u6837\u56de\u590d\uff0c \u4ed6\u4f1a\u600e\u6837\uff1f\u201d\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u4ed6\u6216\u8005\u662f\u4e0d\u76f8\u4fe1\uff0c\u6216\u8005\u662f\u5fc3\u91cc\u76f8\u4fe1\u4e86\uff0c\u4f46\u4e3a\u4e86\u4f53\u9762\uff0c\u53e3\u91cc \u4e00\u5b9a\u62b5\u8d56\uff01\u201d\u5510\u52aa\u73e0\u7a46\u6447\u6447\u5934\u9053\uff1a \u3000\u3000\u201c\u6050\u6015\u8fd8\u4e0d\u4ec5\u5982\u6b64\uff0c\u4ed6\u591a\u534a\u4f1a\u8001\u7f9e\u6210\u6012\uff0c\u6307\u54b1\u4eec\u6c61\u8511\u4ed6\u7684\u56fd\u5bb6\uff0c\u6740\u5bb3\u4ed6\u7684\u4f7f\u81e3\uff0c\u6709 \u610f\u5411\u4ed6\u6311\u8845\u3002\u90a3\u65f6\u53ea\u6015\u4e24\u56fd\u5c31\u8981\u5175\u620e\u76f8\u89c1\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u4e5f\u4e0d\u662f\uff0c\u4e0d\u8bf4\u4e5f\u4e0d\u662f\uff0c\u5206\u660e\u9519\u662f\u5728\u4ed6\uff0c\u5374\u53cd\u800c\u53d8\u6210\u54b1\u4eec\u9519\u4e86\u3002 \u8fd9\u4e0d\u662f\u5929\u5927\u7684\u51a4\u6789\u5417\uff1f\u54e5\u54e5\uff0c\u4f60\u600e\u4e48\u529e\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u6414\u5934\u82e6\u7b11\uff1a\u201c\u6211\u5c31\u6b63\u662f\u60f3\u529e\u6cd5\u554a\uff01\u201d \u8c37\u4e2d\u83b2\u5ffd\u9053\uff1a\u201c\u4f9d\u4f60\u770b\uff0c\u4f1a\u4e0d\u4f1a\u662f\u76d6\u82cf\u4e0e\u6606\u5e03\u5170\u56fd\u7684\u56fd\u738b\u65e9\u6709\u52fe\u7ed3\uff0c\u90a3\u4f7f\u8005\u5230\u54b1\u4eec\u7684\u5b9d \u5e93\u76d7\u5b9d\uff0c\u4e5f\u662f\u5949\u547d\u800c\u4e3a\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u76b1\u8d77\u7709\u5934\u8bf4\u9053\uff1a\u201c\u4f46\u613f\u8fd9\u53ea\u662f\u8fd9\u4f7f\u81e3\u7684\u79c1\u4eba\u884c\u52a8\uff0c\u4e0e\u4ed6\u4eec\u7684\u56fd\u738b\u65e0\u6d89\uff0c\u5426 \u5219\u4e8b\u60c5\u5c31\u66f4\u4e0d\u53ef\u6536\u62fe\u4e86\u3002\u6211\u4e0d\u662f\u6015\u4e86\u6606\u5e03\u5170\u56fd\uff0c\u4f46\u4e3a\u4e86\u8fd9\u7b49\u83ab\u540d\u5176\u5999\u7684\u4e8b\u6253\u8d77\u4ed7\u6765\uff0c\u4ee4 \u4e24\u56fd\u7684\u767e\u59d3\u53d7\u4f24\uff0c\u8fd9\u5b9e\u5728\u662f\u592a\u4e0d\u503c\u5f97\u4e86\uff01\u201d\u8c37\u4e2d\u83b2\u9ed8\u7136\u4e0d\u8bed\uff0c\u4e5f\u662f\u6df1\u611f\u4e3a\u96be\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5f98\u5f8a\u826f\u4e45\uff0c\u5ffd\u4ed6\u8bf4\u9053\uff1a\u201c\u53ea\u6709\u7528\u8fd9\u4e2a\u529e\u6cd5\u8bd5\u8bd5\u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4ec0\u4e48\u529e \u6cd5\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u8bf4\u9053\uff1a\u201c\u4e8b\u60c5\u7684\u771f\u76f8\u603b\u662f\u8981\u8bf4\u660e\u767d\u7684\uff0c\u4f46\u65e2\u4e0d\u80fd\u7528\u6587\u4e66\u56de\u590d\uff0c\u4e5f\u4e0d\u80fd\u5728 \u4e24\u56fd\u7684\u671d\u5ef7\u4e0a\u516c\u5f00\u8bf4\u51fa\u6765\u3002\u6211\u60f3\u7acb\u5373\u6d3e\u4e00\u4e2a\u4f7f\u8005\u5230\u6606\u5e03\u5170\u56fd\u53bb\uff0c\u8981\u6c42\u548c\u4ed6\u7684\u56fd\u738b\u5355\u72ec\u89c1 \u9762\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8fd9\u529e\u6cd5\u4e0d\u9519\u5440\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4f46\u5374\u8fd8\u6709\u4e00\u4e2a\u95ee\u9898\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6709\u4f55\u95ee\u9898\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u662f\u4f60\u5927\u54e5\u8fd8\u5728\u6b64\u5904\uff0c\u90a3 \u6211\u5c31\u4e0d\u4f1a\u8fd9\u6837\u4e3a\u96be\u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u662f\u6311\u4e0d\u51fa\u4e00\u4e2a\u7cbe\u660e\u80fd\u5e72\u800c\u53c8\u6b66\u827a\u9ad8\u5f3a\u7684\u4eba \u53bb\u505a\u4f7f\u8005\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201d\u7cbe\u660e\u80fd\u5e72\u800c\u53c8\u5fe0\u5fc3\u803f\u803f\u7684\u5927\u81e3\u6211\u5012\u6311\u9009\u5f97\u51fa\uff0c\u53ef\u60dc\u4ed6\u4eec\u90fd \u4e0d\u61c2\u6b66\u529f\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6413\u6413\u53cc\u624b\uff0c\u82e5\u6709\u6240\u601d\uff0c\u8fc7\u4e86\u7247\u523b\u3002\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4ece\u79cd\u79cd\u8ff9\u8c61\u770b\u6765\uff0c\u6606\u5e03\u5170 \u56fd\u548c\u54b1\u4eec\u7684\u5173\u7cfb\u5927\u4e0d\u5bfb\u5e38\uff0c\u751a\u81f3\u6709\u70b9\u795e\u79d8\uff0c\u6b64\u5176\u4e00\uff1b\u76d6\u82cf\u548c\u6606\u5e03\u5170\u56fd\u738b\u6709\u5426\u52fe\u7ed3\uff0c\u54b1\u4eec \u867d\u7136\u4e0d\u613f\u610f\u6709\u6b64\u7b49\u4e8b\uff0c\u4f46\u4e5f\u603b\u5f97\u63d0\u9632\uff0c\u6b64\u5176\u4e8c\uff1b\u54b1\u4eec\u6d3e\u9063\u4f7f\u8005\u524d\u5f80\uff0c\u6606\u5e03\u5170\u56fd\u738b\u80af\u4e0d\u80af \u79c1\u4e0b\u4f1a\u89c1\uff1b\u4ea6\u5c1a\u672a\u53ef\u77e5\uff0c\u751a\u6216\u6545\u610f\u7559\u96be\uff0c\u4e5f\u8bf4\u4e0d\u5b9a\uff0c\u6b64\u5176\u4e09\u3002\u603b\u4e4b\u662f\u8981\u9632\u5907\u610f\u5916\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u7684\u786e\u662f\u8981\u50cf\u5927\u54e5\u8fd9\u6837\u7684\u81ea\u5df1\u4eba\uff0c\u624d\u6700\u9002\u5408\u505a\u4f7f\u8005\u4e86\u3002\u8981\u662f \u6709\u4ec0\u4e48\u610f\u5916\uff0c\u4ed6\u51ed\u7740\u4e00\u8eab\u6b66\u529f\u53ef\u4ee5\u9003\u56de\u6765\uff0c\u800c\u4e14\u8fd8\u53ef\u4ee5\u76f8\u673a\u884c\u4e8b\uff0c\u67e5\u7a76\u4f60\u521a\u624d\u6240\u8bf4\u7684\u90a3 \u4e24\u4ef6\u7591\u6848\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u5417\uff1f\u8fd9\u79cd\u79d8\u5bc6\uff0c\u662f\u8fde\u5fc3\u8179\u5927\u81e3\u4e5f\u4e0d\u65b9\u4fbf\u5631\u6258\u4ed6\u4eec\u4ee3 \u529e\u7684\u3002\u53ef\u60dc\u6211\u73b0\u5728\u8eab\u4e3a\u4e00\u56fd\u4e4b\u4e3b\uff0c\u53c8\u4e0d\u65b9\u4fbf\u53bb\u5047\u5192\u4f7f\u81e3\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5ffd\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u770b\u6211\u53ef\u4ee5\u53bb\u5f97\u4e48\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u8be7\u9053\uff1a \u3000\u3000\u201c\u4f60\uff0c\u4f60\u662f\u4e2a\u516c\u4e3b\u7684\u8eab\u4efd\u2014\u2014\u201d\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u5973\u626e\u7537\u88c5\uff0c\u6211\u5e08\u7236\u6709\u6613\u5bb9\u4e39\u3002\u201d \u5510\u52aa\u73e0\u7a46\u82e6\u7b11\u9053\uff1a\u201c\u6b64\u4e8b\u975e\u540c\u513f\u620f\uff0c\u4e00\u56fd\u7684\u4f7f\u81e3\uff0c\u4e07\u4eba\u6ce8\u65e5\uff0c\u8981\u662f\u7ed9\u4eba\u770b\u51fa\u7834\u7efd\uff0c\u90a3\u5c31 \u6709\u5931\u4f53\u9762\u4e86\u3002\u800c\u4e14\u548c\u6606\u5e02\u5170\u738b\u89c1\u9762\uff0c\u8bf4\u660e\u6b64\u4e8b\u771f\u76f8\uff0c\u4ed6\u5f97\u975e\u5e38\u8001\u7ec3\u624d\u884c\u3002\u518d\u8bf4\u4f60\u8fd9\u6837\u5e74 \u8f7b\uff0c\u7eb5\u662f\u6539\u5bb9\u6613\u88c5\uff0c\u4e5f\u5f88\u96be\u626e\u5f97\u50cf\u4e00\u4e2a\u4f7f\u81e3\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u751a\u662f\u82e6\u607c\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u4e0d\u5728\u6b64\u5904\uff0c\u6211\u53bb\u4f60\u53c8\u8bf4\u4e0d\u884c\uff0c\u90a3\u600e\u4e48\u529e\uff1f\u201d\u5979\u5f98\u5f8a \u826f\u4e45\uff0c\u5ffd\u5730\u53c8\u53eb\u8d77\u6765\u9053\uff1a\u201c\u6709\u4e86\uff01\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4f60\u4e49\u6709\u4f55\u5999\u7b56\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8fd8 \u662f\u6211\u53bb\u3002\u4e0d\u8fc7\u6211\u4e0d\u662f\u5145\u5f53\u4f7f\u81e3\uff0c\u800c\u662f\u5f53\u4f5c\u4f7f\u81e3\u7684\u968f\u4ece\u3002\u4f60\u6d3e\u53bb\u7684\u4f7f\u81e3\u591a\u5e26\u4ece\u4eba\uff0c\u6211\u6df7\u5728 \u5176\u4e2d\uff0c\u7edd\u4e0d\u4f1a\u60f9\u4eba\u6ce8\u610f\u3002\u800c\u4e14\u6211\u4f5c\u4e3a\u968f\u4ece\uff0c\u4e5f\u5c31\u53ef\u4ee5\u514d\u4e86\u8bb8\u591a\u62d8\u675f\uff0c\u53ef\u4ee5\u4fbf\u5b9c\u884c\u4e8b\uff0c\u6697 \u4e2d\u67e5\u63a2\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5b9e\u5728\u65e0\u8ba1\u53ef\u65bd\uff0c\u7ed9\u5979\u8bf4\u5f97\u6709\u70b9\u610f\u52a8\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a \u3000\u3000\u201c\u53ef\u662f\u4f60\u4e0d\u662f\u51c6\u5907\u597d\u4e86\u8981\u4e0e\u5e08\u7236\u660e\u5929\u540c\u8d70\u7684\u4e48\uff1f\u8fd8\u6709\uff0c\u4f60\u82e5\u5230\u6606\u5e03\u5170\u56fd\u53bb\u4f5c\u4f7f\u8005\uff0c \u53ea\u6015\u4e5f\u4f1a\u803d\u8bef\u4e86\u4f60\u548c\u6d77\u5929\u7684\u89c1\u9762\u4e4b\u671f\u3002\u800c\u4e14\uff0c\u800c\u4e14\uff0c\u82e5\u6709\u610f\u5916\uff0c\u4f60\u662f\u4e00\u4e2a\u5973\u5b50\uff0c\u5374\u6559\u6211 \u5982\u4f55\u653e\u5fc3\u5f97\u4e0b\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u4e0d\u8981\u8fd9\u4e48\u591a\u2018\u800c\u4e14\u2019\u4e86\uff0c\u54b1\u4eec\u4e00\u6bcd\u6240\u751f\uff0c\u4f60\u6709\u4e3a\u96be\u4e4b\u4e8b\uff0c\u6211\u7406\u5f53\u4e3a \u4f60\u5206\u52b3\u3002\u800c\u4e14\u2014\u2014\u201d\u5979\u7b11\u4e86\u4e00\u7b11\uff0c\u6a21\u4eff\u54e5\u54e5\u7684\u53e3\u6c14\u8bf4\u9053\uff1a\u201c\u800c\u4e14\u6211\u4e5f\u662f\u9a6c\u8428\u513f\u56fd\u7684\u4e00\u4e2a \u56fd\u6c11\uff0c\u6211\u968f\u5e08\u7236\u5357\u5f52\u4e4b\u540e\uff0c\u8bf4\u4e0d\u5b9a\u4ee5\u540e\u5c31\u4e0d\u518d\u56de\u5230\u672c\u56fd\u4e86\u3002\u8d81\u8fd9\u673a\u4f1a\uff0c\u8ba9\u6211\u4e3a\u672c\u56fd\u505a\u70b9 \u4e8b\u60c5\uff0c\u65e5\u540e\u79bb\u5f00\u6545\u571f\uff0c\u4e5f\u5f97\u5fc3\u5b89\u3002\u201d\u5979\u8bf4\u8fd9\u51e0\u53e5\u8bdd\u9762\u5e26\u7b11\u5bb9\uff0c\u4f46\u5374\u8bf4\u5f97\u5341\u5206\u5e84\u91cd\uff01 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5927\u4e3a\u611f\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u771f\u662f\u6211\u7684\u597d\u59b9\u59b9\uff0c\u90a3\u4f60\u5c31\u51c6\u5907\u660e\u5929\u52a8\u8eab\u5427\u3002\u201d \u8c37\u4e2d\u83b2\u89c1\u54e5\u54e5\u7b54\u5141\uff0c\u5f88\u662f\u9ad8\u5174\uff0c\u5ffd\u5730\u60f3\u8d77\u4e00\u4e8b\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4e2a\u76d2\u5b50\uff0c\u4f60\u53ef\u5e26\u5728\u8eab\u4e0a\u5417\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u90a3\u4e2a\u85cf\u6709\u4fe1\u4ef6\u548c\u6606\u5e03\u5170\u56fd\u56fd\u4e66\u7684\u9996\u9970\u76d2\u4e86\u5417\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u6211\u60f3\u628a\u5b83\u5e26\u8d70\uff0c\u5c06\u6765\u6216\u8005\u4f1a\u6709\u7528\u5904\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u73b0\u5728\u5c31\u968f\u6211\u53bb \u62ff\u3002\u4e0d\u8fc7\u54b1\u4eec\u53ef\u5148\u5f97\u5c01\u95ed\u4e86\u8fd9\u53e6\u4e00\u6761\u79d8\u5bc6\u7684\u5730\u9053\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u62ac\u4e86\u4e00\u4e9b\u77f3\u5934\u8fdb\u6765\uff0c\u5c06\u76d6\u82cf\u9003\u51fa\u53bb\u7684\u90a3\u4e2a\u5730\u6d1e\u5835\u585e\u5f97\u5bc6\u4e0d\u900f\u98ce\uff0c\u8fd9\u5730\u6d1e\u6781 \u4e3a\u72ed\u7a84\uff0c\u4ec5\u80fd\u5bb9\u5f97\u4e00\u4e2a\u4eba\u7684\u8eab\u4f53\u94bb\u8fdb\u53bb\uff0c\u591a\u597d\u6b66\u529f\uff0c\u5728\u91cc\u9762\u4e5f\u4e0d\u80fd\u8212\u5c55\u624b\u8db3\uff0c\u7eb5\u4f7f\u76d6\u82cf \u4f24\u6108\u518d\u7c73\uff0c\u8981\u642c\u5f00\u8fd9\u4e9b\u77f3\u5934\uff0c\u90a3\u4e5f\u662f\u51b3\u8ba1\u505a\u4e0d\u5230\u7684\u4e86\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u548c\u59b9\u59b9\u8d70\u56de\u4ed6\u7684\u5bdd\u5bab\uff0c\u79fb\u5f00\u5e8a\u4e0a\u7684\u6795\u5934\uff0c\u5ffd\u5730\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u8d3c\u4eba\u7684 \u80c6\u5b50\u53ef\u771f\u4e0d\u5c0f\uff0c\u6211\u8fd9\u91cc\u4e5f\u6709\u4eba\u6765\u8fc7\u4e86\uff01\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u5510\u52a1\u73e0\u7a46\u9053\uff1a \u201c\u6211\u505a\u4e86\u4e00\u4e2a\u540c\u4e00\u5f0f\u6837\u7684\u9996\u9970\u76d2\u5b50\u653e\u5728\u8fd9\u6795\u5934\u4e0b\u9762\uff0c\u73b0\u5728\u4e0d\u89c1\u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u5401\u4e86\u53e3\u6c14\uff0c \u9053\uff1a\u201c\u90a3\u8fd8\u597d\uff0c\u771f\u7684\u6ca1\u6709\u5931\u6389\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u627e\u51fa\u4e86\u539f\u6765\u90a3\u4e2a\u76d2\u5b50\uff0c\u6253\u5f00\u4e00\u770b\uff0c\u8fd9\u4ef6\u56fd\u4e66\u4e00\u6837\u4e0d\u7f3a\uff0c\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\uff0c\u4ea4 \u7ed9\u59b9\u59b9\uff0c\u8bf4\u9053\uff0c\u201c\u5e78\u4e8f\u6211\u8fd8\u7b97\u8c28\u614e\uff0c\u6211\u60f3\u5230\u8fd9\u4e2a\u76d2\u5b50\u4eba\u4e0d\u5bfb\u5e38\uff0c\u53ea\u6015\u4e5f\u662f\u8d3c\u4eba\u6240\u8981\u76d7\u53d6 \u4e4b\u7269\uff0c\u56e0\u6b64\u53e6\u505a\u4e86\u4e00\u4e2a\u3002\u679c\u7136\u5c31\u6709\u4eba\u6765\u5077\u4e86\u3002\u59b9\u59b9\uff0c\u4f60\u4eca\u540e\u53ef\u5f97\u7279\u522b\u5f53\u5fc3\u624d\u597d\u3002\u201d\u4ed6\u4eec \u5144\u59b9\u4e8c\u4eba\u867d\u7136\u827a\u9ad8\u80c6\u5927\uff0c\u4f46\u53d1\u89c9\u8d3c\u4eba\u5982\u6b64\u795e\u51fa\u9b3c\u6ca1\uff0c\u4e5f\u4e0d\u7981\u6709\u70b9\u60f4\u60f4\u4e0d\u5b89\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u62ff\u4e86\u76d2\u5b50\uff0c\u968f\u5373\u53bb\u89c1\u5e08\u7236\uff0c\u7980\u660e\u8981\u5f80\u6606\u5e03\u5170\u4e4b\u4e8b\u3002\u8c37\u4e4b\u534e\u82e5\u6709\u6240\u601d\uff0c\u95ee\u9053\uff1a \u201c\u6606\u5e03\u5170\u56fd\uff1f\u662f\u4e0d\u662f\u5c31\u5728\u5c71\u7684\u90a3\u8fb9\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5e08\u7236\u5230\u8fc7\u4e48\uff1f\u201d\u8c37\u4e4b\u534e\u9053\uff1a \u201c\u6211\u6ca1\u6709\u5230\u8fc7\uff0c\u91d1\u4e16\u9057\u5374\u662f\u5230\u8fc7\u7684\u3002\u201d\u5979\u60f3\u4e86\u4e00\u60f3\uff0c\u5ffd\u4ed6\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u83b2\u513f\u3002\u6211\u4e5f\u548c\u4f60 \u8d70\u4e00\u8d9f\u5427\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u4e0d\u662f\u6025\u7740\u8981\u56de\u9099\u5c71\u4e48\uff1f\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u5357\u5317\u4e10 \u5e2e\u5df2\u7ecf\u5408\u5e76\uff0c\u6709\u4ef2\u957f\u7edf\u548c\u4f60\u7684\u7ffc\u5e08\u4f2f\u3001\u767d\u5e08\u4f2f\u7b49\u4eba\u4e3b\u6301\u5927\u8ba1\uff0c\u6211\u628a\u884c\u7a0b\u62d6\u5ef6\u5341\u5929\u3001\u534a\u6708\uff0c \u6599\u4ea6\u65e0\u59a8\u3002\u4e0d\u8fc7\u6211\u4e0d\u662f\u548c\u4f60\u4e00\u9053\u8d70\uff0c\u6211\u4eca\u65e5\u5c31\u53bb\uff0c\u5148\u5230\u90a3\u8fb9\u7b49\u4f60\uff0c\u4f46\u4f60\u4e0d\u5fc5\u627e\u6211\uff0c\u5230\u65f6 \u6211\u81ea\u7136\u4f1a\u6765\u89c1\u4f60\u7684\u3002\u201d\u8c37\u4e2d\u83b2\u89c1\u5e08\u7236\u7a81\u7136\u6539\u53d8\u4e3b\u610f\uff0c\u9887\u89c9\u5947\u602a\uff0c\u4f46\u4e5f\u4e0d\u4fbf\u591a\u95ee\uff0c\u5fc3\u60f3\uff1a \u201c\u6709\u5e08\u7236\u6697\u4e2d\u7167\u5e94\uff0c\u6211\u66f4\u53ef\u4ee5\u653e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u5e9c\u52aa\u73e0\u7a46\u9009\u4e86\u4e00\u4e2a\u7cbe\u660e\u7ec3\u8fbe\u7684\u8001\u81e3\u5b50\u4f5c\u4e3a\u4f7f\u8005\uff0c\u5e26\u4e86\u4e8c\u5341\u56db\u4e2a\u4ece\u4eba\u524d\u5f80\u6606 \u5e03\u5170\u56fd\uff0c\u8c37\u4e2d\u83b2\u5c31\u662f\u8fd9\u4e8c\u5341\u56db\u4e2a\u4ece\u4eba\u4e2d\u4e4b\u4e00\u3002\u5510\u52aa\u73e0\u7a46\u4eb2\u81ea\u9001\u51fa\u56fd\u95e8\uff0c\u4e34\u5206\u624b\u65f6\u6084\u6084\u5bf9 \u59b9\u59b9\u8bf4\u9053\uff1a\u201c\u6211\u4f1a\u5c3d\u5feb\u8bbe\u6cd5\u5c06\u4f60\u7684\u6d88\u606f\u4f20\u7ed9\u6c5f\u5e08\u5144\u7684\uff0c\u4f60\u653e\u5fc3\u8d70\u5427\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u542c\u5f97\u54e5\u54e5\u8fd9\u53e5\u8bf4\u8bdd\uff0c\u5012\u89c9\u5f97\u6709\u70b9\u5947\u602a\uff0c\u4e0d\u77e5\u9053\u54e5\u54e5\u4f55\u4ee5\u4f1a\u6709\u8fd9\u4e2a\u4e3b\u610f\uff0c\u8bbe\u7684 \u53c8\u662f\u4ec0\u4e48\u529e\u6cd5\uff0c\u5f53\u4e0b\u9762\u4e0a\u4e00\u7ea2\uff0c\u4f4e\u58f0\u8bf4\u9053\uff0c\u201c\u4f60\u8981\u6d3e\u5feb\u9a6c\u8ffd\u4e0a\u6d77\u54e5\uff0c\u544a\u8bc9\u4ed6\u8fd9\u4e2a\u6d88\u606f\u5417\uff1f \u6211\u770b\u662f\u5927\u53ef\u4e0d\u5fc5\u4e86\uff0c\u8ba9\u4ed6\u77e5\u9053\uff0c\u5f92\u4ee4\u4ed6\u4e3a\u6211\u62c5\u5fc3\u3002\u6211\u3001\u6211\u4e5f\u4e0d\u613f\u4e3a\u4e86\u513f\u5973\u79c1\u60c5\uff0c\u8bef\u4e86\u56fd \u5bb6\u5927\u4e8b\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u6211\u660e\u767d\u4f60\u7684\u5fc3\u60c5\u3002\u4f60\u60e6\u8bb0\u4f60\u7684\u6d77\u54e5\uff0c\u6211\u4e5f\u60f3\u5ff5\u6211\u7684\u6c5f \u5e08\u5144\u7684\u3002\u4e0d\u8fc7\uff0c\u6211\u4e0b\u4f1a\u4ee4\u4ed6\u4e3a\u96be\uff0c\u56fd\u4e8b\u79c1\u60c5\u6211\u90fd\u4f1a\u517c\u987e\u7684\u3002\u201d\u4e34\u884c\u5728\u5373\uff0c\u800c\u4e14\u8c37\u4e2d\u83b2\u7684 \u8eab\u4efd\u53ea\u662f\u968f\u4ece\u4e4b\u4e00\uff0c\u867d\u7136\u90a3\u4f7f\u81e3\u77e5\u9053\u5979\u7684\u771f\u6b63\u8eab\u4efd\uff0c\u5176\u4ed6\u7684\u968f\u4ece\u5374\u662f\u4e0d\u77e5\u9053\u7684\uff0c\u56e0\u6b64\uff0c \u5979\u4e5f\u4e0d\u65b9\u4fbf\u548c\u56fd\u738b\u8c08\u5f97\u592a\u591a\uff0c\u8bf4\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5306\u5306\u4fbf\u5206\u624b\u4e86\u3002 \u3000\u3000\u5979\u8bdd\u867d\u5982\u6b64\uff0c\u5374\u96be\u514d\u4e0d\u60f3\u8d77\u6c5f\u6d77\u5929\u6765\u3002\u60f3\u8d77\u81ea\u5df1\u4e0e\u6c5f\u6d77\u5929\u73b0\u5728\u6b63\u662f\u80cc\u9053\u800c\u9a70\uff0c\u4e00\u4e2a \u5411\u4e1c\uff0c\u4e00\u4e2a\u5411\u897f\uff0c\u8ddd\u79bb\u662f\u8d8a\u6765\u8d8a\u8fdc\u4e86\u3002 \u3000\u3000\u5230\u4e86\u6606\u5e03\u5170\u56fd\uff0c\u4e0d\u77e5\u6709\u4f55\u53d8\u5316\uff0c\u5c06\u6765\u4e5f\u4e0d\u77e5\u80fd\u5426\u518d\u4e0e\u6c5f\u6d77\u5929\u89c1\u9762\uff0c\u4ed6\u5230\u6c34\u4e91\u5e84\u53bb\u63a2 \u671b\u534e\u4e91\u78a7\uff0c\u4e5f\u4e0d\u77e5\u4f1a\u751f\u51fa\u4ec0\u4e48\u679d\u8282\uff0c\u5979\u601d\u5982\u6f6e\u6d8c\uff0c\u6837\u6837\u8426\u6000\uff0c\u5f53\u771f\u662f\u5fc3\u5982\u4e71\u4e1d\uff0c\u201c\u526a\u4e0d \u65ad\uff0c\u7406\u8fd8\u4e71\u201d\uff0c\u7d22\u6027\u628a\u5fc3\u4e00\u6a2a\uff0c\u751a\u4e48\u90fd\u4e0d\u53bb\u60f3\u3002 \u3000\u3000\u6682\u4e14\u6309\u4e0b\u8c37\u4e2d\u83b2\u4e0d\u8868\uff0c\u4e14\u8bf4\u6c5f\u5357\u6c5f\u6d77\u5929\u7236\u5b50\u4e8c\u4eba\uff0c\u79bb\u5f00\u4e86\u9a6c\u8428\u513f\u56fd\uff0c\u65e5\u591c\u517c\u7a0b\u8d76\u8def\u3002 \u8fd9\u65e5\u5230\u4e86\u7518\u8083\u7684\u5929\u6c34\u53bf\uff0c\u5df2\u8e0f\u8fdb\u4e86\u7ec8\u5357\u5c71\u5c71\u533a\u3002\u6c5f\u6d77\u5929\u60f3\u8d77\u6765\u65f6\uff0c\u66fe\u5728\u8fd9\u513f\u78b0\u89c1\u90a3\u6b27\u9633 \u5a49\u7684\u5e08\u5144\u4e8e\u5c11\u9cb2\u4e4b\u4e8b\uff0c\u90a3\u65e5\u6b63\u5de7\u662f\u6b27\u9633\u5a49\u548c\u6587\u9053\u5e84\u7ed3\u5a5a\u7684\u65e5\u5b50\uff0c\u4e8e\u5c11\u9cb2\u9a97\u4ed6\u5230\u6b27\u9633\u5bb6\u4e2d \u5403\u559c\u9152\uff0c\u5927\u95f9\u793c\u5802\uff0c\u4e8e\u5c11\u9cb2\u51fa\u6b64\u6b89\u60c5\uff0c\u4e34\u6b7b\u4e5f\u4e0d\u77e5\u9053\u662f\u6b27\u9633\u6e05\u201c\u59ca\u4ee3\u59b9\u5ac1\u201d\uff0c\u800c\u6b27\u9633\u5a49 \u4e5f\u4e00\u70b9\u4e0d\u77e5\u9053\u5979\u5e08\u5144\u5bf9\u5979\u5982\u6b64\u6df1\u60c5\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6005\u89e6\u524d\u5c18\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u60c5\u4e4b\u4e00\u5b57\uff0c\u5b9e\u662f\u96be\u8a00\uff0c\u53d8\u5316\u7684\u5947\u5999\uff0c\u4e5f\u6bcf\u6bcf\u51fa\u4eba\u610f \u5916\uff0c\u4e8e\u5c11\u9cb2\u5982\u6b64\u6df1\u60c5\uff0c\u6b7b\u4e86\u53ea\u4e0d\u8fc7\u843d\u5f97\u6b27\u9633\u5a49\u4e00\u58f0\u53f9\u606f\u3002\u53f6\u51b2\u9704\u5bf9\u6b27\u9633\u5a49\u7684\u59d0\u59d0\u6b27\u9633\u6e05 \u662f\u5047\u60c5\u5047\u610f\uff0c\u60f3\u4e0d\u5230\u6b27\u9633\u6e05\u4e00\u6b7b\uff0c\u4ed6\u5374\u6094\u6539\u524d\u975e\uff0c\u7adf\u7136\u751f\u4e86\u771f\u60c5\uff0c\u800c\u628a\u8fd9\u4e00\u7247\u771f\u60c5\u4e00\u79fb \u7ed9\u4e86\u59b9\u59b9\u3002\u201d\u60f3\u8d77\u4e86\u53f6\u51b2\u9704\u548c\u6b27\u9633\u5a49\uff0c\u6c5f\u6d77\u5929\u53c8\u4e0d\u7981\u60f3\u9053\uff1a\u201c\u4e0d\u77e5\u6b27\u9633\u5a49\u662f\u5426\u5df2\u4e0e\u53f6\u51b2 \u9704\u56de\u5230\u5979\u7684\u5bb6\u91cc\uff1f\u6211\u5df2\u6765\u5230\u8fd9\u513f\uff0c\u8981\u4e0d\u8981\u53bb\u63a2\u8bbf\u4ed6\u4eec\u5462\uff1f\u201d \u3000\u3000\u6c5f\u5357\u5ffd\u5730\u56de\u5934\uff0c\u7b11\u9053\uff1a\u201c\u6d77\u513f\uff0c\u4f60\u7684\u811a\u7a0b\u6bd4\u6211\u6289\u5f97\u591a\uff0c\u5374\u4e3a\u4f55\u8fdc\u8fdc\u843d\u5728\u540e\u9762\uff1f\u54a6\uff0c \u4f60\u662f\u5728\u60f3\u4ec0\u4e48\u5fc3\u4e8b\u5417\uff1f\u201d\u6c5f\u6d77\u5929\u8fc8\u5f00\u5927\u6b65\uff0c\u8d76\u4e0a\u7236\u4eb2\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u5076\u7136\u60f3\u8d77\u4e00 \u4f4d\u670b\u53cb\u3002\u201d\u6c5f\u5357\u4e5f\u4e0d\u540c\u4ed6\u60f3\u7684\u662f\u8c01\uff0c\u4fbf\u5373\u7b11\u9053\uff1a\u201c\u53c8\u662f\u5728\u60f3\u5ff5\u4f60\u7684\u83b2\u59b9\u5417\uff1f\u5979\u73b0\u5728\u5df2\u7ecf \u662f\u516c\u4e3b\u4e86\uff0c\u54b1\u4eec\u672c\u6765\u9ad8\u6500\u4e0d\u4e0a\uff0c\u8fc7\u53bb\u7684\u5c31\u7b97\u4e86\u5427\uff01\u201d\u6c5f\u6d77\u5929\u8bf4\u9053\uff1a\u201c\u83b2\u59b9\u53ef\u4e0d\u662f\u90a3\u6837\u7684 \u4eba\u3002\u5979\u7684\u54e5\u54e5\u4e5f\u4e0d\u56e0\u505a\u4e86\u56fd\u738b\u5c31\u5bf9\u6211\u51b7\u6de1\u3002\u201d\u6c5f\u5357\u5ffd\u5730\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u6211\u662f\u4e3a\u4f60\u9ad8\u5174\uff0c\u6211\u81ea\u4ee5\u4e3a\u6211\u7684\u8fd0\u6c14 \u5df2\u7ecf\u5f88\u4e0d\u9519\u4e86\uff0c\u60f3\u4e0d\u5230\u4f60\u7684\u8fd0\u6c14\u7adf\u7136\u6bd4\u6211\u8fd8\u597d\uff01\u60f3\u5f53\u5e74\u2014\u2014\u201d\u8fd9\u4e00\u53e5\u662f\u6c5f\u5357\u7684\u53e3\u5934\u7985\uff0c \u6c5f\u6d77\u5929\u5df2\u542c\u5f97\u719f\u4e86\uff0c\u5fc3\u91cc\u6697\u7b11\uff1a\u201c\u7239\u7239\u4e0d\u77e5\u53c8\u8981\u8bf4\u4ed6\u5f53\u5e74\u54ea\u4e00\u6b21\u5f97\u610f\u4e4b\u4e8b\u4e86\uff1f\u4ed6\u7684\u5f97\u610f \u5f80\u4e8b\uff0c\u5176\u5b9e\u6211\u90fd\u5df2\u8033\u719f\u80fd\u8be6\u3002\u201d \u3000\u3000\u4e0d\u6599\u6c5f\u5357\u8bf4\u51fa\u7684\u4ed6\u5e73\u751f\u8fd9\u4ef6\u6700\u5f97\u610f\u7684\u4e8b\u60c5\uff0c\u6c5f\u6d77\u592d\u5374\u672a\u66fe\u542c\u8fc7\u3002\u53ea\u542c\u5f97\u4ed6\u7239\u7239\u7b11\u7740 \u8bf4\u9053\uff1a\u201c\u60f3\u5f53\u5e74\u6211\u662f\u4e00\u4e2a\u4e66\u7ae5\u7684\u8eab\u4efd\u3002 \u3000\u3000\u4f60\u5988\u5988\u662f\u5317\u4e94\u7701\u6b66\u6797\u76df\u4e3b\u94c1\u638c\u91d1\u5200\u6768\u4ef2\u82f1\u7684\u5916\u5b59\u5973\u513f\uff0c\u591a\u5c11\u4eba\u5411\u5979\u63d0\u4eb2\u5979\u90fd\u4e0d\u7b54\u5e94\uff0c \u5355\u5355\u559c\u6b22\u4e0a\u6211\uff0c\u4e0d\u7792\u4f60\u8bf4\uff0c\u8fde\u4f60\u5916\u5a46\u5f53\u521d\u4e5f\u4e0d\u5927\u613f\u610f\u5c06\u5973\u513f\u8bb8\u914d\u6211\u7684\uff0c\u540e\u6765\u62d7\u4e0d\u8fc7\u4f60\u5988\uff0c \u7ec8\u4e8e\u8fd8\u662f\u7b54\u5e94\u4e86\u3002\u4f60\u8bf4\u6211\u7684\u8fd0\u6c14\u4e0d\u662f\u592a\u597d\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u5357\u559c\u6b22\u8bf4\u5634\uff0c\u5bf9\u513f\u5b50\u8bf4\u8bdd\u4e5f\u662f\u60f3\u5230\u4ec0\u4e48\u5c31\u8bf4\u4ec0\u4e48\uff0c\u4ece\u6ca1\u7aef\u8fc7\u4e3a\u7236\u7684\u7ed9\u5b50\u3002\u6c5f\u6d77\u5929 \u5fcd\u7740\u4e86\u7b11\u8bf4\u9053\uff1a\u201c\u7239\u7239\u7684\u8fd0\u6c14\u679c\u7136\u4e0d\u9519\u3002\u201d \u3000\u3000\u6c5f\u5357\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u8bf4\u9053\uff1a\u201c\u4f46\u4f60\u8fd9\u5c0f\u5b50\u7684\u8fd0\u6c14\u6bd4\u6211\u8fd8\u597d\uff0c\u54fc\uff0c\u6211\u5e73\u751f\u4ece\u4e0d\u8ba4\u8f93\uff0c\u8fd9 \u4e00\u6837\u53ef\u5f97\u5411\u513f\u5b50\u8ba4\u8f93\u4e86\uff0c\u4f60\u5988\u662f\u6b66\u6797\u76df\u4e3b\u7684\u5916\u5b59\u5973\u513f\uff0c\u5979\u559c\u6b22\u6211\uff0c\u8fd9\u5df2\u7ecf\u51fa\u4e4e\u8bb8\u591a\u4eba\u610f \u6599\u4e4b\u5916\u4e86\u3002\u54c8\uff0c\u60f3\u4e0d\u5230\u73b0\u5728\u4e00\u56fd\u7684\u516c\u4e3b\u4e5f\u559c\u6b22\u4e0a\u4f60\uff0c\u4f60\u7684\u8fd0\u6c14\u4e0d\u662f\u6bd4\u6211\u66f4\u597d\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u672c\u662f\u6ee1\u6000\u5fc3\u4e8b\uff0c\u88ab\u4ed6\u7236\u4eb2\u8fd9\u4e48\u4e00\u9017\uff0c\u4e5f\u5fcd\u4e0d\u4f4f\u7b11\u8d77\u6765\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u54ea\u91cc\u662f\u4ec0 \u4e48\u8fd0\u6c14\uff0c\u6211\u548c\u83b2\u59b9\u672c\u5c31\u662f\u9752\u6885\u7af9\u9a6c\u4e4b\u4ea4\u3002 \u3000\u3000\u5fc3\u5fc3\u76f8\u5370\u7684\u4e86\u3002\u201d\u4f46\u4ed6\u4eec\u5c0f\u513f\u7684\u604b\u6155\u4e4b\u60c5\uff0c\u4ed6\u5374\u4e0d\u597d\u610f\u601d\u548c\u7236\u4eb2\u6765\u8bf4\u3002 \u3000\u3000\u6c5f\u5357\u5ffd\u5730\u9762\u8272\u4e00\u7aef\uff0c\u53c8\u6307\u7740\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u542c\u7740\uff0c\u60f3\u5f53\u5e74\uff0c\u6211\u77e5\u9053\u4f60\u5988\u559c\u6b22 \u4e0a\u6211\uff0c\u6211\u4e5f\u5c31\u4e00\u5fc3\u4e00\u610f\u5411\u7740\u5979\uff0c\u6211\u751f\u5e73\u975e\u4f46\u4ece\u672a\u6cbe\u60f9\u8fc7\u7b2c\u4e8c\u4e2a\u5973\u5b50\uff0c\u5fc3\u91cc\u8fde\u60f3\u4e5f\u672a\u60f3\u8fd9 \u7b2c\u4e8c\u4e2a\u5973\u5b50\uff0c\u6211\u6837\u6837\u4e0d\u5982\u513f\u5b50\uff0c\u8fd9\u4e00\u6837\u5012\u53ef\u4ee5\u505a\u4f60\u7684\u6a21\u8303\uff0c\u4f60\u5e94\u8be5\u5411\u6211\u5b66\u5b66\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u65e0\u7aef\u7aef\u7ed9\u7236\u4eb2\u4e00\u756a\u8bad\u65a5\uff0c\u5f04\u5f97\u557c\u7b11\u7686\u975e\uff0c\u6ca1\u5948\u4f55\u53ea\u597d\u4f4e\u4e0b\u5934\u53bb\uff0c\u5e94\u4e86\u4e00\u58f0\uff1a \u201c\u662f\uff01\u201d\u6c5f\u5357\u5ffd\u5730\u53c8\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4eba\u5bb6\u516c\u4e3b\u65e2\u662f\u771f\u5fc3\u5bf9\u4f60\uff0c\u4f60\u4e5f\u5c31\u8be5\u4e00\u5fc3\u5411\u7740\u4eba\u5bb6\uff0c\u522b\u518d\u60f3\u522b\u7684\u59d1\u5a18\u4e86\uff0c\u4e5f\u7701\u5f97\u81ea \u60f9\u9ebb\u70e6\u3002\u4f60\u542c\u89c1\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7fdf\u7136\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u89c1\u4e86\u3002\u201d\u539f\u6765\u4ed6\u4eca\u65e5\u65e7\u5730\u91cd\u6e38\uff0c\u6b63\u5728\u60f3\u8d77\u4e0e\u6b27\u9633\u5a49\u7684 \u5f80\u4e8b\uff0c\u542c\u4e86\u7236\u4eb2\u7684\u8bdd\uff0c\u4e0d\u89c9\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u7239\u7239\u8bf4\u7684\u8bdd\u4e5f\u6709\u51e0\u5206\u9053\u7406\uff0c\u6211\u4e0e\u6b27\u9633\u59d1\u5a18\u867d\u7136 \u81ea\u95ee\u65e0\u4ed6\uff0c\u4f46\u4e00\u89c1\u4e86\u9762\uff0c\u603b\u96be\u514d\u5f7c\u6b64\u4f1a\u60f3\u8d77\u5f80\u4e8b\u3002\u5979\u73b0\u5728\u5df2\u4e0e\u53f6\u51b2\u9704\u7f14\u7ed3\u826f\u7f18\uff0c\u6211\u53c8\u4f55 \u82e6\u6270\u4e71\u5979\u7684\u5fc3\u5883\uff0c\u7b97\u4e86\uff0c\u8fd8\u662f\u4e0d\u8981\u53bb\u63a2\u671b\u4ed6\u4eec\u5427\u3002\u201d\u4e3b\u610f\u6253\u5b9a\uff0c\u4e0d\u518d\u8e0c\u8e87\uff0c\u6c5f\u6d77\u5929\u8fc8\u5f00 \u811a\u6b65\uff0c\u4e0d\u4e00\u4f1a\u5c31\u8d76\u8fc7\u7236\u4eb2\u7684\u524d\u5934\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u89c1\u4e24\u9a91\u5feb\u9a6c\u8fce\u9762\u800c\u6765\uff0c\u9a6c\u4e0a\u7684\u4eba\u90fd\u5e26\u6709 \u5175\u5668\uff0c\u4e00\u770b\u5c31\u77e5\u662f\u6c5f\u6e56\u8c6a\u5ba2\u3002\u6b63\u662f\u3002 \u3000\u3000\u65e7\u5730\u91cd\u6e38\u60c5\u6005\u6005\uff0c\u5f53\u5e74\u5947\u9047\u53c8\u76f8\u9022\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u50f6\u76fb\u628e\u57cf\u3014\u893d\u8ffc\u6c5c\u25b2\u68a8\u7855\u70b4\u819b\u7fd8\u25ce\uff02\uff02\u83f4\u4f90\u574b\u4f90\u9699\u3000\u73e8\u7829\u51ef\u501c\u9762\u5566\u5fd2\u3000\u6d18\u6df0\u62f8\u6570\u8d56\u4f77\u8bb2 \u893d\u8ffc\u6c5c\u25b2\u68a8\u7855\u70b4\u819b\u7fd8\u25ce \u83f4\u4f90\u574b\u4f90\u9699\u3000\u73e8\u7829\u51ef\u501c\u9762\u5566\u5fd2\u3000\u6d18\u6df0\u62f8\u6570\u8d56\u4f77\u8bb2 \u3000\u3000\u9976\u8c24\u9a91\u9547\u690d\u852c\u9cc5\u864f\u8d7d\u65ef\u7c65\u558a\u5f7b\u311b\u9547\u6396\u597b\u9976\u8c24\u8dfa\u6e6e\u8366\u52e4\u577b\u8747\u6e56\u8bb2\u5269\u73e8\u72df\u311b\u4f94\u7eb2\u8844\u8438\u838e \u7951\u8154\u6738\u4f0e\u311b\u7b0b\u73e9\u81bb\u5d0b\u7cfb\u311b\u665e\u5f7b\u53bb\u5269\u201d \u3000\u3000\u852c\u9cc5\u6084\u6c52\u4f7d\u800b\u3129\u203b\u6db4\u8c24\u4eba\u6242\u8ba4\u8155\u8154\u311b\u577b\u8747\u5374\u5c86\u6e6e\u57ee\u66ae\u7965\u8d77\u6242\u5c86\u9630\u5269\u201d\xa7\u852c\u6f06\u6bde\u800b\u3129 \u203b\u6db4\u8c24\u8dfa\u6a21\u9e23\u5c86\u59a6\u7cfb\u4eba\uff1f\xa7\u852c\u9cc5\u800b\u3129\u203b\u8bde\u6a7e\u8154\u9976\u8dfa\u5c86\u54d7\u6738\u7c75\u8154\u5447\u83b1\u6820\u4e27\u776b\u311b\u9976\u7b22\u70c2\u4eba\u5c86 \u852c\u7ef6\u597b\u8154\u9ec4\u8910\u6e6e\u8052\u671d\u75a1\uff06\u8d5b\u4ec1\u5e5b\uff0a\u8154\u52bc\u68b2\u311b\u73e9\u5cbd\u5c86\u54d7\u6738\u7c75\u8154\u7ed2\u8ffc\u7733\u73e8\u201d\u7ede\u70c2\u577b\u8747\u5a53\u54d7\u6738 \u7c75\u85b9\u953e\u7733\u72df\u311b\u5a53\u6161\u5213\u5343\u68a4\u576a\u8ff5\u5c7e\u8206\u4f81\u80c0\u8dea\u6e6e\u85af\u5ca9\u52e4\u6ee5\u311b\u7ede\u5940\u6242\u73e9\u5c86\u5a53\u90e8\u6c2a\u7733\u73e8\u201d\u7b0b\u6242\u5c86 \u8dfa\u62f8\u9761\u82e4\u9011\u311b\u577b\u8747\u6e6e\u57ee\u7965\u9887\u8ba4\u8155\u6242\u201d\xa7 \u3000\u3000\u852c\u6f06\u6bde\u6dba\u5269\u73e8\u6dba\u311b\u4f7d\u800b\u3129\u203b\u6db4\u7cfb\u4f7d\u61c2\u311b\u7ed9\u5c86\u8c24\u8dfa\u6e6e\u8844\u61c2\u829b\u8154\u4eba\u661c\u5269\u201d\xa7\u852c\u9cc5\u9ea6\u7a89\u5709 \u5985\u311b\u4f7d\u800b\u3129\u203b\u892b\u7965\u5c86\u7cfb\uff1f\u86a7\u5176\u5c86\u9976\u6820\u4e27\u776b\u311b\u8d7b\u577b\u5447\u5017\u54d7\u6738\u7c75\u4f9a\u7f00\u311b\u6315\u8206\u4eba\u5c2a\u996e\u7aed\u86c1\u7829\u577b \u8154\u4ff4\u8dbf\u311b\u892b\u5c86\u574b\u6320\u70c2\u61c2\u311b\u690d\u81bb\u8844\u4eba\u8155\u772d\u577b\u8154\u79cf\u6d18\u311b\u6e6e\u6a21\u996e\u7715\u5cc8\u577b\u5ff3\u5269\u5447\u5017\u7733\u4f9a\u8154\u8c05\u6551\u311b \u690d\u68ee\u8c56\u5824\u852c\u7ef6\u311b\u79cd\u6c52\u66e9\u6002\u5269\u201d\u7811\u7965\u5584\u8e0f\u65e5\u5374\u8871\u5a53\u6db4\u7235\u5824\u73cb\u201d\xa7 \u3000\u3000\u577b\u8747\u73e8\u7f2d\u8ed7\u53bb\u311b\u7ffb\u54ff\u8871\u8844\u4e03\u5310\u9a91\u9547\u690d\u577b\u8747\u65ef\u665a\u558a\u5f7b\u311b\u892b\u7715\u8258\u8155\u5824\u61c2\u311b\u996e\u5c86\u4e09\u5213\u62fb\u57ec \u8154\u7591\u8366\u311b\u8844\u8673\u852c\u9cc5\u8ba4\u8155\u311b\u5176\u7b22\u8844\u73e8\u8dfa\u900a\u777f\u852c\u9cc5\u6e56\u5269\u8dfa\u6878\u7f51\u311b\u7b0b\u4ff4\u4f0e\u68cd\u68cd\u311b\u73e9\u5e24\u5cbd\u793f\u72df\u61c2 \u8ff5\u852c\u9cc5\u6e58\u8d76\u201d \u3000\u3000\u852c\u9cc5\u800b\u3129\u203b\u5947\u5885\u311b\u6db4\u7b1d\u9cc5\u5213\u73e8\u7803\u4eba\u6002\u5364\u5584\u311b\u5374\u5d0b\u8154\u8e0f\u65e5\u9887\u766b\u597b\u6db4\u52cd\u55e3\u852c\u7ef6\u7470\u8c1b\u201d\xa7 \u852c\u6f06\u6bde\u800b\u3129\u203b\u777f\u6593\u6878\u7f51\u8154\u9976\u4eba\u5c86\u9630\uff1f\xa7\u852c\u9cc5\u800b\u3129\u203b\u5c86\u9752\u6f06\u9699\u901c\u8154\u8e22\u65e5\u7c9f\u201d\xa7\u852c\u6f06\u6bde\u8871\u52cb \u5269\u73e8\u50a7\u311b\u4f7d\u800b\u3129\u203b\u5447\u864f\u5cbd\u6791\u5f7b\u6db4\u8dfa\u4eba\u8154\u9761\u8dbc\u311b\u4f7d\u73e9\u8a27\u7a90\u6124\u69fd\u311b\u4f7d\u8155\u597b\u5c86\u73e8\u5f07\u6315\u8206\u7951\u4eba\u201d\xa7 \u3000\u3000\u852c\u9cc5\u8677\u800b\u3129\u203b\u7ede\u70c2\u5a53\u5343\u68a4\u576a\u7733\u9887\u8154\u524d\u6d40\u311b\u6242\u7831\u5017\u9ebb\u582c\u8dbc\u5cbd\u766b\u597b\u577b\u311b\u9887\u52cb\u5269\u8438\u952f\u201d\u7f00 \u61c2\u6593\u5447\u864f\u505a\u7b22\u7fd1\u6242\u311b\u851a\u577b\u8c78\u5269\u8dfa\u8df5\u987b\u311b\u79ea\u68ee\u577b\u66ae\u8155\u6242\u201d\u8258\u61c2\u577b\u8154\u4ec5\u8bb2\u7ed9\u5c86\u7965\u6e23\u311b\u6242\u900a\u6da8 \u9e67\u577b\u66ae\u8982\u524d\u5403\u7f57\u311b\u9630\u772d\u577b\u5374\u73c2\u7803\u6242\u6e56\u5269\u8dfa\u6878\u7f51\u201d\xa7\u8871\u4f7d\u800b\u3129\u203b\u6db4\u4eba\u892b\u547e\u5c86\u8dfa\u6dcf\u8a04\u7733\u6d01\u8154 \u4eba\u661c\u311b\u5176\u577b\u6320\u8dfa\u6242\u8ba4\u8155\u311b\u5374\u996e\u5c86\u8a04\u5ca9\u7b22\u9887\u8844\u61c2\u829b\u8154\u4eba\u201d\xa7 \u3000\u3000\u8c24\u864f\u8d7d\u73e8\u7f2d\u8ed7\u73e8\u7f2d\u62b6\u311b\u996e\u6a47\u8155\u7aed\u5c86\u5947\u5885\u311b\u8871\u8ed7\u5269\u73e8\u6700\u311b\u5584\u5269\u73e8\u63ed\u4e09\u8236\u7f2d\u8c19\u311b\u6dcf\u5c86\u7ede \u65e5\u852c\u6f06\u6bde\u5a53\u6db4\u7235\u90e3\u7357\u887e\u5c7e\u737f\u8154\u534e\u6e90\u311b\u7eee\u7357\u8844\u8c24\u9a91\u529e\u9547\u6ca7\u558a\u5965\u61c2\u311b\u82bc\u7136\u5a53\u577b\u8747\u91b1\u524d\u793f\u72df\u311b \u8bf7\u8d77\u61c2\u800b\u3129\u203b\u6593\u5c86\u852c\u82e4\u72e8\u7cfb\uff1f\xa7\u61c2\u8154\u5c86\u73e8\u52e4\u70c2\u57ee\u62fb\u608e\u8154\u6e6e\u8700\u201d \u3000\u3000\u852c\u6f06\u6bde\u73e8\u8258\u311b\u8871\u50a7\u8871\u70b0\u311b\u87c0\u7586\u4f7d\u800b\u3129\u203b\u6f88\u6a7e\u524d\u5377\u311b\u7811\u7965\u5584\u8e0f\u65e5\u5a53\u68ee\u7708\u7459\u3110\xa7\u57fb\u61c2\u4f7d \u8d76\u8154\u6db4\u4eba\u5c86\u690d\u524d\u63a7\u4fe6\u6ddc\u5808\u66da\u62e5\u8154\u8ede\u66da\u829b\u6f88\u5648\u311b\u8e2c\u8154\u5c86\u577b\u8154\u59bb\u8d7d\u6cba\u552d\u6812\u6f88\u5aaa\u77d3\u201d\u852c\u6f06\u6bde\u690d \u524d\u5a53\u5384\u5801\u86bd\u5801\u6b38\u6a21\u7b22\u311b\u5cbd\u777f\u577b\u8747\u7357\u5f7b\u91b1\u8154\u201d \u3000\u3000\u852c\u6f06\u6bde\u800b\u3129\u203b\u6ce8\u6ce8\u311b\u6db4\u5f07\u5c86\u63a7\u4fe6\u6ddc\u5808\u66da\u62e5\u6f88\u8ede\u66da\u829b\u201d\xa7\u6f88\u5648\u5b8e\u772d\u852c\u9cc5\u5c86\u852c\u6f06\u6bde\u8154\u864f \u4eb2\u311b\u597b\u524d\u7357\u5f7b\u5269\u72de\u311b\u87c0\u7586\u6d01\u800b\u3129\u203b\u6593\u8747\u5d0b\u8154\u6db4\u7cfb\u529e\u61a9\u8155\u5269\u6345\u6d18\u311b\u8e22\u6e6e\u72e8\u892b\u61c2\u5269\u7cfb\uff1f\xa7\u6f88 \u5648\u7715\u524d\u5465\u7136\u5e24\u5cbd\u7357\u5f7b\u852c\u9cc5\u311b\u7b0b\u852c\u9cc5\u777f\u8e22\u5c8d\u75bb\u8154\u8760\u60c5\u577b\u5374\u5c86\u533c\u8844\u5780\u6053\u8154\u201d \u3000\u3000\u852c\u9cc5\u82a6\u9761\u5176\u9558\u311b\u6dba\u5269\u73e8\u6dba\u311b\u6040\u800b\u3129\u203b\u59a6\u7cfb\u6345\u6d18\u965b\uff1f\xa7\u6f88\u9702\u838e\u800b\u3129\u203b\u6593\u8747\u7965\u5c86\u597b\u97c1\u6820 \u6a21\u53bb\u8154\u7cfb\uff1f\xa7\u852c\u9cc5\u800b\u3129\u203b\u7965\u311b\u6242\u8747\u5c86\u597b\u5384\u5801\u5e86\u53bb\u95ee\u9887\u5801\u86bd\u7fcb\u8154\u201d\u82e4\u5ac1\u8154\u8865\u6ce8\u8c8c\u5213\u74df\u7b10\u8c8c \u6bde\u745e\u5a53\u5384\u5801\u86bd\u6b31\u593c\u201d\xa7\u6f88\u9702\u800b\u3129\u203b\u9791\u311b\u57fb\u61c2\u6593\u8747\u900a\u5e24\u772d\u800b\u79cf\u6d18\u311b\u7965\u5f7b\u311b\u6db4\u73e9\u51cc\u5de7\u311b\u6593\u8747 \u6dcf\u7591\u88d2\u597b\u5269\u201d\u6593\u8747\u7965\u869a\u5584\u5384\u5801\u86bd\u5fbd\u3129\u8e0f\u65e5\u61a9\u892b\u7715\u7357\u8982\u5801\u86bd\u7fcb\u5269\u311b\u577b\u7712\u5e42\u61c2\u5fbd\u3110\xa7 \u3000\u3000\u852c\u9cc5\u7c7b\u7965\u8982\u829b\u9f83\u311b\u6040\u800b\u3129\u203b\u6593\u8747\u6e6e\u9e23\u6320\u88d2\u61c2\u6db4\u5ac1\u311b\u5cc8\u5269\u7761\u5c88\u311b\u892b\u5914\u68f9\u8c62\uff1f\xa7\u6f88\u9702\u6147 \u6147\u8677\u800b\u3129\u203b\u6db4\u7483\u5c88\u60c5\u311b\u6040\u6593\u8154\u9537\u6863\u61a9\u9647\u54d1\u5269\u201d\xa7\u852c\u6f06\u6bde\u927c\u7136\u6e6e\u6633\u311b\u9651\u829b\u73e8\u6dbe\u311b\u4f7d\u800b\u3129 \u203b\u5801\u86bd\u7fcb\u892b\u5c86\u61c2\u6891\u97c1\u6820\u7b2f\u777f\u8154\u977c\u6c14\uff1f\xa7 \u3000\u3000\u6f88\u9702\u800b\u3129\u203b\u6dcf\u8438\u5ac1\u5c86\u9976\u4ff7\u73d4\u8154\u82e4\u5d1e\u311b\u7965\u5f7b\u97c1\u6820\u7b2f\u777f\u6db4\u73e8\u6a21\u4eba\u6242\u85af\u7ede\u7136\u73e9\u5c86\u7965\u5914\u6e29\u5f7b \u8154\u201d\u8e0f\u65e5\u577b\u8747\u9976\u665a\u5824\u91b1\u8154\u61a9\u5c86\u97c1\u6820\u768e\u777f\u777f\u577b\u8154\u83b1\u83b1\u7b2f\u777f\u3001\u629a\u777f\u4e09\u5017\u83b1\u311b\u5a64\u8747\u6db4\u665a\u5c86\u5801\u86bd \u7fcb\u777f\u6242\u201d\u6147\u6147\u201d \u3000\u3000\u6593\u8747\u61c2\u8155\u6dcf\u7591\u311b\u892b\u7965\u869a\u6242\u8082\u8bf7\u6593\u8747\u7fd1\u62f3\u5269\u52d8\uff1f\xa7 \u3000\u3000\u57fb\u61c2\u5384\u5801\u5e86\u8154\u86bd\u7fcb\u5801\u6b38\u5c86\u8de4\u5ac1\u8e2c\u60c6\u5403\u61c2\u8154\u311b\u577b\u8154\u5ac1\u8d7d\u5801\u743c\u3001\u8e2c\u5ac1\u5801\u65cf\u63a9\u73d4\u55b3\u794b\u869a\u6e6e \u6548\u555c\u82e5\u68aa\u9686\u593c\u311b\u6320\u7eb2\u519e\u97ec\u311b\u5037\u8155\u8c8c\u6bde\u745e\u9d03\u9651\u74df\u7b25\u311b\u8871\u8155\u852c\u6f06\u6bde\u8de4\u577b\u8747\u6000\u609b\u6005\u86c2\u554b\u6c14\u311b\u6db4 \u7b26\u53c1\u577b\u8747\u690d\u5be4\u85af\u5bff\u597b\u5cad\u5269\u9699\u61c2\u201d\u90d4\u8f6e\u5801\u743c\u5017\u85e4\u7712\u5e42\u4fc7\u5168\u8469\u57fb\u311b\u5801\u6b38\u73e9\u7712\u8124\u5824\u73d4\u55b3\u794b\u7ede\u65e5 \u5c86\u777f\u97c1\u6820\u7b2f\u777f\u8154\u8e2c\u5ac1\u97c1\u6820\u5249\u5a53\u73e8\u8d77\u8154\u311b\u7b0b\u577b\u900a\u5e24\u772d\u800b\u73d4\u55b3\u794b\u8154\u61c2\u8361\u311b\u79ea\u68ee\u7850\u5914\u6891\u597b\u97c1\u6820 \u7b2f\u777f\u8154\u6a21\u85af\u201d \u3000\u3000\u796b\u887e\u6f88\u5648\u311b\u5bc0\u5c86\u5cc8\u5269\u6ddc\u5808\u66da\u62e5\u9976\u68d2\u63a9\u8c07\u66da\u7733\u5c88\u311b\u9976\u68d2\u577b\u8747\u6005\u5269\u73e8\u6e6e\u8695\u5e5b\u7b2d\u72fb\u7b2c\u311b\u5584 \u5269\u9752\u6f06\u311b\u63a9\u73e8\u8dfa\u8e2c\u5d1e\u5780\u8c07\u311b\u73d4\u55b3\u794b\u5cbd\u5a53\u9976\u8e2c\u5d1e\u8154\u6718\u7b22\u5824\u73cb\u201d\u9976\u68d2\u66da\u62e5\u8154\u4eba\u311b\u58f6\u5269\u8844\u8c24\u8dfa \u66da\u5447\u8155\u73d4\u55b3\u794b\u4f7d\u60c5\u311b\u6e29\u8ed7\u7733\u4fcb\u311b\u5176\u577b\u8154\u5168\u7a92\u63a9\u4f00\u201d\u6ddc\u5808\u66da\u62e5\u5824\u68ee\u5bff\u85af\u201d\u7f00\u61c2\u6f88\u9702\u6dcb\u8700\u5824 \u61c2\u6e7f\u66da\u311b\u90e3\u597b\u73d4\u55b3\u794b\u311b\u73e8\u665f\u7965\u78c1\u311b\u6e56\u851a\u8d77\u61c2\u311b\u6f88\u9702\u8154\u59bb\u8d7d\u8871\u8de4\u73d4\u55b3\u794b\u6e56\u593c\u311b \u3000\u3000\u73d4\u55b3\u794b\u5465\u7136\u7965\u5c86\u8c07\u66da\u8154\u6dcf\u7fcb\u311b\u7b0b\u73e8\u61c2\u577b\u8747\u8ba4\u9685\u73d4\u55b3\u794b\u5c86\u9976\u8e2c\u5d1e\u8154\u80ae\u7ed2\u311b\u5aaa\u61c2\u8871\u8844\u6f88 \u9702\u59bb\u8d7d\u63a9\u73d4\u55b3\u794b\u6e56\u593c\u7733\u5c88\u311b\u561f\u5965\u577b\u8747\u73e9\u7301\u6891\u577b\u60c6\u5403\u201d\u7b0b\u5801\u6b38\u3001\u6f88\u9702\u3001\u97c1\u6820\u7b2f\u777f\u80c0\u4eba\u311b\u996e \u5c86\u852c\u7ef6\u597b\u6e6e\u8844\u65ef\u7237\u8154\u4eba\u661c\u311b\u7965\u90be\u6548\u4eba\u7965\u6398\u311b\u4f4c\u72df\u6246\u5403\u9955\u987b\u311b\u90a7\u6e90\u5498\u9685\u671f\u57ee\u9887\u311b\u8dea\u8d7b\u8082\u8bf7 \u6315\u8206\u63fd\u886d\u311b\u524d\u61c2\u7fd1\u609d\u311b\u65e5\u671f\u665e\u5c86\u8e0f\u6bde\u201d \u3000\u3000\u6db4\u8673\u5c88\u60c5\u8154\u61c2\u97e9\u53bb\u9619\u311b\u852c\u6f06\u6bde\u996e\u5c86\u772d\u800b\u8154\u311b\u7b0b\u5982\u8e0f\u6ced\u5584\u5269\u6db4\u8dfa\u79cf\u6d18\u311b\u5374\u4ecd\u5c86\u7965\u8f7f\u6e6e \u6e6e\u52cb\u50a7\u3110 \u3000\u3000\u7301\u772d\u852c\u6f06\u6bde\u9651\u534e\u66fe\u8c1c\u311b\u5465\u7136\u577b\u73e9\u5cbd\u52cb\u5f7b\u73d4\u55b3\u794b\u8154\u8d56\u829b\u311b\u7b0b\u8d7b\u690d\u8ff5\u73d4\u55b3\u794b\u8d75\u83e9\u5cc8\u886d\u7733 \u7f00\u311b\u7712\u53c1\u577b\u690d\u524d\u9155\u5f7b\u8154\u633d\u5c88\u5168\u996e\u54ad\u5269\u201d \u3000\u3000\u5982\u8e0f\u73e8\u7b52\u6ced\u8155\u6f88\u9702\u5bfc\u5c88\u7b2d\u6791\u311b\u5668\u5c86\u7301\u8ff5\u5801\u6b38\u53bb\u6891\u73d4\u55b3\u794b\u547e\u689b\u311b\u577b\u6338\u8155\u7965\u50a7\uff1f \u3000\u3000\u6f88\u9702\u5827\u8d7b\u5013\u55b3\u55b3\u577b\u4f7d\u800b\u3129\u203b\u6242\u6dcf\u557e\u4eba\u5fd2\u7965\u5282\u7f57\u311b\u892b\u5de7\u90e3\u597b\u5269\u6593\u311b\u6db4\u73e8\u68d2\u892b\u7715\u605b\u7ec2\u5428 \u5238\u5269\u201d\xa7\u852c\u6f06\u6bde\u505a\u8d7b\u7811\u800b\u3129\u203b\u73d4\u6e6e\u8c8a\u73cb\u5a53\u7712\u870a\u8a04\u5be5\u6dcf\u311b\u577b\u8e0f\u65e5\u90e3\u597b\u5cc9\u9762\u311b\u6242\u82e5\u7965\u6262\u6768\u9f89 \u8d31\u311b\u851a\u61c2\u5d0b\u7591\u53bb\u7357\u4e8f\u85e4\uff1f\xa7 \u3000\u3000\u6f88\u9702\u6977\u6a47\u852c\u6f06\u6bde\u8844\u869d\u5539\u8154\u6738\u4f0e\u311b\u838e\u800b\u3129\u203b\u852c\u5c7e\u72e8\u311b\u6593\u8be7\u60c5\u5c86\u7965\u580b\u7829\u53bb\u7cfb\uff1f\u7ede\u65e5\u9976\u4ff7 \u73d4\u8154\u82e4\u5d1e\u6e56\u593c\u5801\u6a21\u5017\u85e4\u7733\u5940\u311b\u6593\u73e9\u5c86\u5cbd\u5a53\u90e8\u8154\u201d\xa7 \u3000\u3000\u852c\u6f06\u6bde\u74fb\u74fb\u800b\u3129\u203b\u7521\u4fc0\u5377\u7733\u7357\u311b\u5540\u6a21\u768a\u8d31\u7965\u768a\u8d4b\u311b\u6db4\u311b\u6db4\u893d\u8d7d\uff03\uff03\xa7\u8d76\u5e24\u4f7d\u4fc7\u311b\u6f88 \u9702\u7712\u5c86\u75d5\u7136\u66f9\u4f0e\u311b\u6e6e\u6c52\u4f7d\u800b\u3129\u203b\u6db4\u893d\u8d7d\u7cfb\u311b\u7301\u8d31\u7fb2\u73e9\u751c\u7965\u9762\u311b\u7850\u7301\u577b\u9765\u6242\u73e8\u80ae\u6ddc\u5808\u66da\u62e5\u311b \u9765\u6242\u66da\u62e5\u8154\u6320\u574b\u6cad\u4ff6\u97ec\uff03\uff03\xa7\u6f88\u5aaa\u77d3\u5085\u8982\u6cba\u5811\u73e8\u7638\u73e8\u5811\u534e\u8ed7\u597b\u524d\u61c2\u311b\u6fee\u8677\u8bff\u800b\u3129 \u3000\u3000\u203b\u4fcb\u697c\u73e8\u90a7\u7591\u866f\u311b\u79f0\u79f0\u311b\u900a\u8844\u6242\u6dcb\u59bb\u85a8\u8154\u73e8\u5c8d\u834e\u9761\u3110\xa7\u5774\u6db4\u90a7\u866f\u6dcf\u5c86\u8de4\u73d4\u55b3\u794b\u6e56\u633d \u8154\u201d \u3000\u3000\u852c\u6f06\u6bde\u8de4\u577b\u8747\u6dcb\u59bb\u85a8\u73e8\u67ab\u62a2\u54d1\u311b\u91b1\u597b\u7965\u6a47\u73e8\u6ddd\u9752\u311b\u73e8\u6ddd\u7efd\u311b\u6333\u8c19\u62f8\u665f\u311b\u505a\u8d7b\u7811\u800b\u3129 \u203b\u73d4\u6e6e\u8c8a\u7ede\u573a\u5c86\u9155\u8155\u602e\u7aef\u5269\u311b\u66da\u62e5\u9976\u6320\u574b\u6cad\u4ff6\u97ec\u311b\u900a\u892b\u7715\u82a2\u4f7d\u7965\u5c86\u577b\u4f00\u8154\u311b\u7b0b\u577b\u6e56\u593c\u5269 \u6f88\u9702\u8154\u59bb\u8d7d\u311b\u6e56\u717b\u5269\u5801\u6b38\u8154\u5ac1\u8e2c\u311b\u6db4\u8dfa\u5540\u5403\u892b\u5c86\u8d4b\u8155\u602e\u6e6e\u5269\u311b\u6315\u8206\u7b22\u4eba\u53c1\u6c52\u9761\u8258\u8155\u6380\u4ff6 \u97ec\u900a\u8e21\u7301\u311b\u7761\u8868\u5c86\u6f88\u9702\u8ede\u89a2\u829b\u3001\u5801\u86bd\u7fcb\u6db4\u80c0\u953e\u51c0\u4eba\u661c\u311b\u5c82\u5914\u5fcd\u5ff3\u5982\u68ee\u6e6e\u952f\uff1f\xa7 \u3000\u3000\u852c\u9cc5\u4f7d\u800b\u3129\u203b\u6db4\u82e4\u6ede\u8d7d\u7965\u96c5\u5c88\u311b\u7eb6\u89de\u4f7d\u8d76\u311b\u6f88\u8ede\u89a2\u829b\u311b\u6593\u6897\u5885\u577b\u201d\u9976\u4ff7\u73d4\u8154\u82e4\u5d1e\u6682 \u7136\u5982\u68ee\u892b\u586b\u311b\u9976\u7ede\u7136\u833c\u86ac\u6891\u577b\u547e\u689b\u201d\u6f06\u9556\u311b\u6593\u5ff3\u5f7b\u5801\u86bd\u7fcb\u8154\u6e6e\u8314\u311b\u5c82\u8844\u7965\u53bb\u7fd1\u62f3\u7733\u70e9\uff1f \u53bb\u311b\u6593\u7965\u53bb\u6242\u73e9\u7301\u53bb\u3110\xa7 \u3000\u3000\u57fb\u61c2\u852c\u9cc5\u6ced\u5269\u5709\u6bde\u311b\u900a\u5e24\u772d\u800b\u577b\u8747\u8c19\u7b22\u8154\u203b\u82e4\u5d1e\xa7\u201d\u61a9\u5c86\u55f7\u7b22\u4e8f\u8154\u8c8a\u8c8a\u73d4\u55b3\u794b\u311b\u79ea \u5cc8\u852c\u6f06\u6bde\u690d\u5e24\u5cbd\u851a\u73d4\u55b3\u794b\u6db4\u8673\u5c88\u8c62\u5482\u577b\u201d \u3000\u3000\u852c\u6f06\u6bde\u8154\u4ff6\u60c5\u6070\u6070\u8ff5\u577b\u864f\u4eb2\u7708\u9d03\u311b\u7965\u4e7e\u55e3\u4f7d\u73bf\u8d76\u311b\u86a7\u5176\u5a53\u73d4\u55b3\u794b\u870a\u8a04\u5be5\u6dcf\u7733\u7f00\u311b\u577b \u8f7d\u7965\u8c0f\u6791\u8d77\u73d4\u55b3\u794b\u8154\u5398\u5c88\u311b\u6db4\u73e9\u5c86\u577b\u203b\u7b10\u586b\u6828\u56e1\xa7\u8154\u73e8\u67ab\u7591\u7829\u201d \u3000\u3000\u6f88\u9702\u8154\u91b1\u4f0e\u5c15\u7357\u9063\u777f\u311b\u73e9\u8d7b\u6a47\u8155\u8d7b\u64a9\u4f7d\u8d76\u8154\u8c19\u6c14\u602e\u7b2d\u311b\u6bc1\u5f7b\u61c2\u7803\u852c\u6f06\u6bde\u9765\u8677\u800b\u3129 \u203b\u852c\u5c7e\u72e8\u311b\u82e5\u5c86\u9065\u5269\u6897\u4eba\u311b\u4f7d\u6db4\u6b34\u8154\u8d76\u311b\u6242\u9685\u7136\u53c1\u577b\u7ede\u91ec\u9976\u82e4\u5d1e\u8154\u4f7d\u8c1b\u311b\u6593\u777f\u9976\u82e4\u5d1e\u73e9 \u5c86\u6e56\u5f7b\u73e8\u90e8\u8154\u311b\u5965\u4e14\u5801\u6a21\u5017\u85e4\u73e9\u900a\u5c86\u6593\u5bf0\u8154\u311b\u6242\u8747\u7ede\u7136\u7965\u9887\u7ae5\u9651\u6593\u5806\u9976\u82e4\u5d1e\u201d\u6242\u772d\u800b\u6593 \u5c86\u73e8\u7247\u7591\u9651\u311b\u6e6e\u57ee\u5c86\u75b6\u6242\u8747\u6e56\u7965\u5f7b\u52e4\u6e90\u311b\u5780\u7715\u7811\u6d18\u5c88\u8c34\u4eba\u52d8\uff1f\u6593\u6e29\u9651\u311b\u52e4\u6e90\u5610\u7136\u5c86\u8be2\u5fd2 \u5982\u5801\u311b\u6242\u8747\u73e9\u7965\u5c86\u543d\u8690\u8151\u311b\u6a7e\u5997\u4f7d\u73e9\u5c86\u8844\u6398\u91b1\u61c2\u8154\u201d\u852c\u5c7e\u72e8\u311b\u6593\u7301\u5c86\u580b\u7829\u533f\u7d6e\u7708\u7fd1\u311b\u9976 \u61a9\u8ff5\u5a64\u8747\u80ae\u53bb\u311b\u7301\u5c86\u7965\u580b\u7cfb\u311b\u9976\u6242\u73e9\u7965\u9887\u8f9e\u5f3a\u6593\u8154\u3110\xa7 \u3000\u3000\u6f88\u9702\u5c86\u8dfa\u6a7e\u852c\u7ef6\u311b\u4f7d\u8d76\u8e74\u7235\u7d32\u6e00\u311b\u8f6f\u7b22\u6e4d\u831e\u311b\u75e8\u6da8\u8155\u7aed\u201d\u7301\u772d\u6315\u8206\u4eba\u5c2a\u311b\u68ab\u86cc\u7708\u8760\u311b \u5bff\u70b5\u8469\u5a38\u311b\u73d4\u55b3\u794b\u6315\u9ae1\u8be2\u5f3a\u311b\u7ede\u7136\u5c86\u6e6e\u8844\u61c2\u829b\u8154\u4eba\u661c\u311b\u5447\u886d\u73e9\u6861\u51c6\u80c0\u73bf\u7733\u5377\u311b\u6f88\u9702\u61a9\u6791 \u706d\u8844\u4eba\u777f\u852c\u6f06\u6bde\u5cad\u597b\u5269\u5bff\u70b5\u311b\u7301\u577b\u5cc8\u73d4\u55b3\u794b\u4f7d\u60c5\u311b\u561f\u5965\u4f7d\u5824\u5269\u6db4\u73e8\u67ab\u8d76\u311b\u73c2\u9ed1\u86c2\u5269\u852c\u6f06 \u6bde\u8154\u90f2\u311b\u577b\u5465\u7136\u4f7d\u4efb\u8695\u852c\u6f06\u6bde\u53bb\u8ff5\u72df\u53bb\u311b\u7b0b\u9630\u73e9\u892b\u7715\u6ced\u8155\u5824\u311b\u577b\u5c86\u869a\u4f7d\u8d76\u62e8\u8feb\u852c\u6f06\u6bde\u201d \u3000\u3000\u852c\u6f06\u6bde\u6ced\u577b\u6791\u8d77\u5bfc\u5c88\u311b\u5374\u7eee\u534e\u9651\u829b\u73e8\u96c4\u311b\u505a\u8d7b\u4f77\u8bb2\u3129\u203b\u6f88\u5648\u6dcb\u8700\u4ff6\u8f96\u5982\u9e22\u311b\u6242\u777f\u577b \u8747\u8871\u7965\u5c86\u65ee\u8760\u311b\u7301\u8f86\u665f\u529d\u8d31\u311b\u9976\u5c86\u7aed\u9762\u5269\u8154\u5269\u201d\u7b0b\u5801\u86bd\u7fcb\u777f\u6242\u8154\u8760\u60c5\u5374\u7965\u6246\u90fd\u311b\u577b\u687d\u8e4b \u6242\u7831\u864f\u311b\u7831\u864f\u8ff5\u6242\u73e9\u5bf0\u5269\u577b\u8154\u5ac1\u8e2c\u311b\u577b\u887e\u6242\u8844\u8314\u311b\u6242\u73e9\u887e\u577b\u8844\u7831\u311b\u577b\u73e8\u7803\u8871\u7aed\u4e7e\u8bf1\u6242\u311b \u7965\u5982\u6d51\u6242\u7357\u5269\u5801\u86bd\u7fcb\u6deb\u7708\u4fac\u8f86\u665f\u52d8\u201d\xa7\u7ede\u72df\u8677\u800b\u3129 \u3000\u3000\u203b\u6242\u6302\u61c2\u61a9\u5c86\u7301\u53bb\u95ee\u7357\u5801\u86bd\u7fcb\u777f\u7831\u864f\u8154\u311b\u6682\u7136\u892b\u7715\u5a53\u68ee\u7708\u7459\u311b\u9610\u8844\u7965\u53bb\u7733\u70e9\uff1f\u796b\u887e \u4f7d\u5584\u7fd1\u62f3\u311b\u8844\u7b32\u5f07\u6a7e\u834e\u502f\u843c\u9887\u311b\u900a\u869a\u8155\u8982\u6242\u9549\uff1f\xa7 \u3000\u3000\u6f88\u9702\u6147\u6147\u8677\u800b\u3129\u203b\u852c\u5c7e\u72e8\u602e\u8c1b\u6c14\u5269\u311b\u6242\u6ced\u4f7d\u6593\u7831\u864f\u73e9\u5cbd\u5ff3\u5f7b\u97c1\u6820\u6a21\u8154\u82e4\u6be6\u8e2c\u7733\u6da8\u311b \u6593\u7831\u864f\u8e0f\u65e5\u7965\u772d\u5c86\u7601\u5914\u88d2\u61c2\u843c\u9887\u311b\u82e5\u5c86\u7965\u61c2\u311b\u6593\u7301\u6778\u6593\u7831\u864f\u5824\u829b\u311b\u7850\u9e67\u7965\u5824\u5fd2\u73e9\u7965\u4ff4\u7f57\u3110\xa7 \u3000\u3000\u852c\u6f06\u6bde\u6ced\u577b\u6791\u631a\u5269\u97c1\u6820\u5249\u311b\u8f7d\u6a47\u9651\u5c81\u311b\u5374\u8871\u7965\u665e\u5cc8\u97c1\u6820\u5249\u6881\u8bf1\u311b\u577b\u86df\u66ae\u8982\u7831\u864f\u311b\u665e \u635e\u6040\u800b\u3129\u203b\u6242\u7831\u864f\u8154\u593c\u597e\u5e24\u75ca\u90db\u9549\uff1f\u7761\u7715\u4f7d\u577b\u8e0f\u65e5\u5e24\u659b\u5914\u5282\u88d2\u61c2\uff1f\xa7\u6f88\u9702\u4f7d\u800b\u3129\u203b\u8c8c\u6a7e \u834e\u502f\u5a4c\u7712\u8469\u57fb\u5269\u311b\u7b0b\u577b\u6e29\u9651\u7965\u72df\u577b\u8154\u72fb\u5703\u311b\u61a9\u5a53\u597b\u8dfa\u580e\u577b\u519e\u8ed7\u577b\u8e2c\u5ac1\u8154\u83f4\u5aaa\u6bde\u311b\u577b\u73e9\u9699 \u8c8c\u5213\u53bb\u5269\u201d\xa7\u4f7d\u5584\u68ee\u63ed\u311b\u7eee\u534e\u8677\u5269\u8d77\u61c2\u311b\u4f7d\u800b\u3129\u203b\u6242\u51cc\u5c86\u6a7e\u7f01\u82a8\u5fbd\u311b\u5668\u7136\u54ad\u66ae\u5269\u6040\u6593\u311b \u6593\u7965\u5c86\u53bb\u7edf\u697c\u8e22\u8308\u50e7\u7733\u9887\u8154\u7cfb\uff1f\u8c8c\u5455\u77d3\u73e9\u6dcf\u5c86\u843c\u6db4\u8dfa\u9887\u8154\u311b\u6593\u8747\u900a\u81bb\u8844\u7357\u8982\u7cfb\uff1f\xa7\u852c\u6f06 \u6bde\u9651\u829b\u73e8\u6cd0\u311b\u6f2a\u7f01\u4f7d\u800b\u3129\u203b\u7357\u5c86\u7357\u8982\u5269\u311b\u7b0b\u8e22\u8308\u50e7\u7733\u9887\u8d4b\u65f0\u7733\u7f00\u201d\u8c8c\u5455\u77d3\u61a9\u68cd\u68cd\u7586\u7586\u534e \u8ed7\u5269\u311b\u6242\u900a\u81bb\u8844\u61c2\u8155\u631a\u777f\u5774\u65ee\u62b6\u201d\xa7 \u3000\u3000\u6f88\u9702\u8438\u8438\u829b\u800b\u3129\u203b\u5c86\u5269\u311b\u6db4\u5f07\u8c8c\u5455\u77d3\u5c86\u8dfa\u82e0\u4f7c\u8e2c\u5ac1\u311b\u5774\u9e67\u864f\u4eb2\u7275\u5883\u311b\u7811\u659b\u73e9\u5c86\u5a4c\u7712 \u88d2\u9699\u8c8c\u5213\u5269\u201d\u5774\u8844\u6738\u8308\u91ec\u5cc8\u91f4\u9a91\u311b\u6380\u4efb\u7761\u7f1f\u9547\u996e\u7301\u529e\u597b\u574b\u6377\u201d\xa7\u8bff\u8982\u4f7d\u800b\u3129\u203b\u8c8c\u6a7e\u834e\u502f \u5c86\u772d\u800b\u8e0f\u65e5\u6db4\u8dfa\u65e5\u671f\u8154\u311b\u8c8c\u5455\u77d3\u6608\u82e5\u7712\u5e42\u9699\u6a21\u311b\u577b\u8747\u864f\u8e2c\u8f90\u8308\u80ae\u61c2\u311b\u73e8\u9685\u892b\u7715\u88d2\u597b\u201d\xa7 \u3000\u3000\u852c\u6f06\u6bde\u9651\u60c5\u8f7d\u5cc8\u9ae6\u89de\u311b\u6302\u61c2\u577b\u73e8\u9651\u7301\u8ff5\u8c8c\u5801\u637a\u9887\u91b1\u311b\u7b0b\u5374\u7965\u580b\u5a53\u6db4\u6b34\u8154\u90e8\u78c1\u7b22\u7708\u7357\u311b \u79ea\u5cc8\u82e5\u5a53\u5384\u5801\u86bd\u7357\u91b1\u311b\u81bb\u8844\u7c65\u4eba\u6e56\u6270\u311b\u8c24\u4eba\u892b\u7715\u9074\u9074\u65ee\u62b6\u311b\u8d7b\u5c86\u8bde\u7722\u6c42\u8155\u8c22\u8d31\uff1b\u7b0b\u6608\u82e5 \u5a53\u97c1\u6820\u6a21\u7b22\u90a7\u6e90\u6380\u6315\u8154\u90e8\u78c1\u72df\u7357\u91b1\u311b\u9976\u61a9\u7965\u5bb9\u577b\u7249\u82c2\u9651\u66f2\u5269\u201d\u7301\u5c86\u8c8c\u5801\u637a\u7965\u8c22\u8d31\u577b\u311b\u8871 \u4f94\u597b\u68d2\u73e8\u6b34\u311b\u7ede\u90e8\u61a9\u8ed7\u311b\u9976\u61a9\u8f7d\u5c86\u9762\u7715\u5cc8\u60c5\u201d\u7b0b\u5584\u6263\u68ee\u5940\u311b\u577b\u73e9\u5cd4\u8844\u831e\u8982\u829b\u76ae\u524d\u5398\u201d\u73d4 \u55b3\u794b\u3001\u97c1\u6820\u5249\u3001\u8c8c\u5801\u637a\u6db4\u4e09\u4eba\u8154\u5c88\u60c5\u58c1\u4f27\u73e8\u7247\u311b\u85e9\u73e8\u7483\u5c88\u60c5\u996e\u5c86\u73e8\u8dfa\u9762\u7715\u8d31\u7fb2\u8154\u8d4b\u311b\u9537 \u5584\u852c\u6f06\u6bde\u9651\u89de\u5982\u9551\u3110 \u3000\u3000\u574b\u55e3\u7235\u7f2d\u6700\u7247\u89ce\u635e\u5584\u311b\u852c\u6f06\u6bde\u5bfc\u534e\u7b2d\u61c2\u311b\u4eba\u5c88\u7712\u870a\u311b\u9651\u7b22\u7783\u63d6\u311b\u9762\u7715\u665f\u54eb\u201d\u7eee\u6ced\u8155 \u8844\u4eba\u8bf7\u3129\u203b\u6db4\u7965\u5c86\u852c\u5c7e\u72e8\u7cfb\uff1f\xa7\u7850\u7357\u5801\u743c\u5017\u85e4\u7712\u88d2\u5824\u85af\u61c2\u8329\u8bff\u201d\u57fb\u61c2\u504c\u687d\u6315\u8206\u5bde\u631e\u311b\u68ee \u68d2\u7733\u9887\u311b\u5465\u5c86\u5a53\u97c1\u6820\u6a21\u7b22\u64bc\u4ff4\u311b\u7b0b\u90a7\u6e90\u52e4\u6ee5\u311b\u5801\u6b38\u777f\u97c1\u6820\u768e\u777f\u80ae\u627d\u7fcb\u4eba\u311b\u5780\u7715\u90a7\u6e90\u996e\u5ca9 \u8844\u772d\u8c1b\u5a53\u85af\u8c19\u8329\u8bff\u64a9\u6e90\u8154\u4eb2\u886d\u201d \u3000\u3000\u5801\u743c\u5017\u85e4\u7357\u5269\u852c\u6f06\u6bde\u311b\u996e\u5c86\u70b0\u5824\u54a1\u4fcb\u311b\u5801\u65cf\u8677\u800b\u3129\u203b\u6593\u8bd5\u690d\u8e22\u8308\u50e7\u9699\u61c2\u52d8\uff1f\u6593\u73e8\u9685 \u5a53\u9887\u597b\u6e6e\u73c6\u65ef\u5fd2\u5269\uff1f\u6ced\u4f7d\u9976\u60d8\u7813\u6768\u5447\u6315\u9ae1\u6861\u5c8d\u311b\u5c86\u9630\u851a\u577b\u6e56\u5556\u8154\uff1f\xa7\u852c\u9cc5\u8155\u7829\u6825\u6825\u577b\u4f7d \u800b\u3129\u203b\u6f06\u6bde\u777f\u577b\u6e56\u5269\u73e8\u90e8\u311b\u7965\u7166\u8be2\u72df\u311b\u7f00\u61c2\u5c86\u8e22\u6e6e\u72e8\u5584\u61c2\u311b\u73e8\u5824\u5fd2\u61a9\u851a\u577b\u6e56\u5556\u5269\u201d\xa7\u5801 \u65cf\u70b0\u800b\u3129\u203b\u6242\u6ce8\u6ce8\u5a4c\u61a9\u8e4b\u9685\u6593\u9887\u6828\u9761\u857e\u52c0\u311b\u522b\u7136\u6593\u81bb\u8844\u532e\u86f9\u6242\u6ce8\u6ce8\u8154\u671f\u54a1\u201d\xa7 \u3000\u3000\u5801\u743c\u7eee\u534e\u91b1\u7efd\u7efd\u534e\u74fb\u74fb\u6040\u800b\u3129\u203b\u6593\u7357\u8982\u5269\u81bb\u8844\uff1f\xa7\u852c\u6f06\u6bde\u9651\u7b22\u73e8\u6cd0\u311b\u6dba\u5269\u73e8\u6dba\u311b\u6040 \u800b\u3129\u203b\u7357\u8982\u59a6\u7cfb\uff1f\xa7\u5801\u65cf\u8677\u800b\u3129\u203b\u577b\u5c86\u6040\u6593\u8844\u81bb\u8844\u7357\u8982\u55f7\u5455\u77d3\uff1f\xa7 \u3000\u3000\u852c\u6f06\u6bde\u7811\u8d77\u5269\u5801\u743c\u52e4\u55f7\u7b22\u4e8f\u8154\u4f4c\u9651\u87cb\u8e85\u311b\u8d7b\u64a9\u73e9\u6a47\u8155\u8844\u8438\u9762\u7715\u5cc8\u60c5\u311b\u7b0b\u68cd\u7586\u7733\u6d01\u311b \u5374\u5c86\u62f8\u690d\u8d31\u5e8b\u311b\u7850\u7591\u6f2a\u7f01\u4f7d\u800b\u3129\u203b\u7357\u8982\u5269\u311b\u6242\u7712\u5e42\u6d4b\u6593\u7803\u5774\u6040\u7d27\u5269\u201d\xa7\u852c\u9cc5\u8871\u8155\u7829\u6825\u6825 \u577b\u4f7d\u800b\u3129\u203b\u577b\u8747\u5a53\u73e8\u8dfa\u82e4\u7eda\u597b\u900a\u73e8\u8d77\u80ae\u86c2\u5269\u5709\u8dfa\u55e3\u580e\u7f57\u3110\u9976\u5f07\u55f7\u5455\u77d3\u73cb\u5a53\u7712\u5c86\u9547\u8428\u5ac1\u5f0a \u8154\u9f20\u7fcb\u5269\u201d\u6147\u311b\u6db4\u8673\u5c88\u60c5\u311b\u4f7d\u8d77\u61c2\u996e\u5c86\u7811\u73e9\u7811\u7965\u5584\u8154\u5947\u5c88\u311b\uff03\uff03\xa7\u852c\u6f06\u6bde\u800b\u3129\u203b\u6ce8\u311b\u9887 \u90e8\u5584\u5fbd\u3110\xa7\u852c\u9cc5\u8677\u800b\u3129\u203b\u892b\u6d07\u73cb\u5a53\u81bb\u8844\u5940\u6d01\u311b\u6d51\u9887\u6c43\u5269\u7733\u7f00\u311b\u6242\u6deb\u8de4\u6593\u8747\u8521\u8521\u561f\u5c88\u311b\u6242 \u6db4\u6ede\u8d7d\u8154\u5947\u90e3\u51cc\u55e3\u311b\u580d\u6c14\u73e9\u51cc\u5c86\u602e\u7591\u5269\u3110\xa7 \u3000\u3000\u5801\u743c\u8258\u5269\u852c\u9cc5\u4f7d\u8d76\u8154\u8155\u7829\u971e\u6013\u311b\u8871\u6ced\u577b\u4f7d\u852c\u6f06\u6bde\u8ff5\u55f7\u7b22\u4e8f\u5cbd\u5a53\u73e8\u63ed\u80ae\u86c2\u5709\u580e\u311b\u9651\u829b \u7965\u8f87\u505a\u505a\u96fa\u55fe\u311b\u7b10\u7b10\u7b28\u5584\u5269\u6320\u7166\u201d\u577b\u4ff6\u60c5\u6380\u8bde\u6da8\u5194\u311b\u852c\u9cc5\u8871\u5c86\u577b\u8154\u9157\u5377\u311b\u577b\u9651\u7235\u7b28\u75b6\u311b \u5374\u7965\u8be7\u6deb\u6040\u5709\u5386\u201d \u3000\u3000\u9887\u90e8\u6262\u5a53\u97c1\u6820\u6a21\u7b22\u9654\u65a9\u8154\u8936\u6315\u90e8\u597b\u311b\u534e\u6e90\u7aed\u5c86\u9075\u5ad8\u311b\u9672\u6639\u8c24\u91b1\u311b\u51cf\u8844\u9157\u67a2\u311b\u5801\u6b38\u6db4 \u6e90\u91b1\u8154\u4eba\u5a53\u9672\u91b1\u311b\u97c1\u6820\u6db4\u665a\u8154\u4eba\u5a53\u6639\u91b1\u311b\u8c24\u91b1\u9157\u9e9f\u996e\u7712\u62e8\u96cf\u5269\u4eba\u201d\u5bde\u8000\u5465\u7136\u7965\u631a\u8e22\u8308\u50e7 \u7733\u9887\u311b\u7b0b\u5584\u8154\u4f27\u9761\u4eba\u661c\u73e9\u7aed\u7965\u5c7e\u201d\u82e5\u7136\u7850\u8e66\u7b22\u57fb\u8154\u6315\u8206\u4eba\u661c\u311b\u5bc0\u5584\u8154\u6380\u8e22\u8308\u50e7\u7733\u9887\u900a\u55e3\u201d \u8e22\u8308\u50e7\u7733\u9887\u5465\u5c86\u8dea\u5f0a\u8be2\u5fd2\u5801\u6469\u201d\u7b0b\u5584\u83c1\u5c86\u534e\u63ed\u311b\u665a\u7d56\u311b\u64d2\u70db\u7b22\u57fb\u602e\u5808\u311b\u5780\u7715\u7b22\u57fb\u6315\u8206\u4eba \u661c\u524d\u5398\u7edf\u697c\u8154\u4eba\u7965\u96cf\u5543\u311b\u5965\u5a53\u6db4\u8dfa\u9887\u90e8\u7733\u597b\u311b\u85e9\u73e8\u91b1\u9157\u9e9f\u311b\u61a9\u7965\u7826\u5543\u4eba\u201d \u3000\u3000\u852c\u6f06\u6bde\u8694\u91b4\u4f90\u5608\u311b\u8174\u6c52\u6040\u800b\u203b\u8c8c\u5455\u77d3\u7f57\uff1f\xa7\u5801\u65cf\u6dba\u5269\u73e8\u6dba\u311b\u4f7d\u800b\u3129\u203b\u6242\u6dcf\u8d26\u6040\u6593\u311b \u6593\u5374\u6040\u6242\u201d\u9263\u311b\u6593\u8154\u7831\u864f\u61c2\u5269\u311b\u6593\u6040\u577b\u52d8\u201d\xa7\u7850\u7357\u8c8c\u6bde\u745e\u9157\u5255\u98d8\u6dcb\u311b\u8ed7\u5824\u9e9f\u61c2\u311b\u90e8\u7b22\u8ba4 \u598e\u852c\u9cc5\u8154\u4eba\u7aed\u55e3\u311b\u7712\u5e42\u8844\u4eba\u8c62\u5482\u5269\u577b\u852c\u9cc5\u5c86\u9630\u311b\u8c8c\u6bde\u745e\u5498\u73c2\u8ff5\u852c\u9cc5\u7357\u5f7b\u5269\u72de\u311b\u7136\u7f00\u6040\u852c \u6f06\u6bde\u800b\u3129\u203b\u5d0b\u7cfb\u7850\u6593\u73e8\u4eba\u9699\u61c2\uff1f\u6593\u8154\u637a\u85e4\u7f57\uff1f\xa7 \u3000\u3000\u852c\u6f06\u6bde\u52cb\u5269\u73e8\u50a7\u311b\u4f7d\u800b\u3129\u203b\u6242\u7715\u5cc8\u637a\u85e4\u7712\u5e42\u9699\u6a21\u5269\uff1f\xa7\u8c8c\u6bde\u745e\u6e6e\u5cc8\u5947\u5885\u311b\u6040\u800b\u3129 \u203b\u6593\u8747\u5d0b\u7cfb\u7965\u5c86\u80ae\u73e8\u7f2d\u8ed7\u8154\uff1f\xa7\u852c\u6f06\u6bde\u74fb\u74fb\u4f7d\u800b\u3129\u203b\u8e22\u8308\u50e7\u7733\u9887\u73e8\u6c43\u311b\u637a\u85e4\u61a9\u9a91\u8982\u6738\u8308 \u6ca7\u8ed7\u5269\u201d\u6242\u73e9\u900a\u61c2\u7965\u631a\u6040\u5774\u8154\u57fb\u79ea\u201d\xa7 \u3000\u3000\u8c8c\u6bde\u745e\u7d36\u8d77\u7fb9\u829b\u311b\u9ea6\u7a89\u800b\u3129\u203b\u6db4\u61a9\u5947\u5269\u311b\u6242\u7712\u715c\u8715\u5774\u311b\u8bf7\u5774\u777f\u6593\u73e8\u800b\u9699\u61c2\u8154\u201d\u8701\u311b \u9ebd\u52cd\u5c86\u5774\u7357\u6593\u900a\u81bb\u8844\u8bfa\u311b\u5780\u7715\u7b49\u9ec4\u9699\u61c2\uff1f \u3000\u3000\u7b0b\u5774\u9a91\u8982\u6738\u8308\u311b\u73e9\u5a4c\u61a9\u833c\u86ac\u5584\u5269\uff1f\xa7\u852c\u6f06\u6bde\u6e6e\u5c86\u52cb\u50a7\u311b\u6682\u5cc8\u8c8c\u5801\u637a\u7ae5\u9651\u311b\u8871\u6a47\u9762\u7715 \u8d31\u5e8b\u201d\u8c8c\u62f8\u745e\u73e9\u5c86\u899c\u5584\u5c88\u8844\u7e87\u6b02\u201d\u7b0b\u577b\u61c2\u7965\u631a\u8c5d\u7249\u652b\u6040\u311b\u7850\u7357\u5801\u6b38\u7712\u6874\u5269\u8d77\u61c2\u311b\u7803\u97c1\u6820 \u768e\u777f\u6977\u8d76\u311b\u5168\u90e8\u857e\u635e\u5488\u5659\u62f8\u6c52\u201d \u3000\u3000\u5801\u6b38\u6c52\u82e5\u7cb9\u7b18\u311b\u784c\u8982\u5801\u743c\u3001\u5801\u65cf\u4f7d\u800b\u3129\u203b\u6db4\u5c86\u82e4\u5ac1\u5801\u743c\u311b\u6db4\u5c86\u82e4\u8e2c\u5801\u65cf\u311b\u8e0f\u70c2\u4e09\u580e\u311b \u577b\u8747\u5a53\u7941\u87c0\u5213\u6e56\u6bc2\u311b\u766b\u597b\u73e8\u8dfa\u9761\u8bf7\u73d4\u55b3\u794b\u8154\u4eba\u311b\u7965\u6040\u60c5\u8695\u311b\u851a\u577b\u8747\u6e56\u8155\u7b2d\u593c\u311b\u7ede\u5940\u6db4\u73d4 \u55b3\u794b\u5c86\u777f\u97c1\u6820\u533d\u7fcb\u8154\u784d\u82e4\u8d26\u97c1\u6820\u5249\u5455\u77d3\u5a53\u73e8\u8d77\u8154\u311b\u6a7e\u6dcb\u68ee\u61c2\u311b\u61a9\u5c86\u7829\u90d7\u953e\u8c05\u6db4\u5f07\u73d4\u834e\u502f \u8154\u8be2\u6878\u3110\xa7 \u3000\u3000\u6f88\u9702\u8bff\u8982\u4f7d\u800b\u3129\u203b\u6242\u5c86\u8dfa\u773b\u4ff6\u8d7d\u8154\u4eba\u311b\u7965\u96c5\u4f7d\u8c1b\u6c14\u8154\u8d76\u201d\u6db4\u4ff7\u73d4\u8154\u82e4\u5d1e\u311b\u8c07\u5269\u6242\u6ddc \u5808\u66da\u62e5\u8154\u66da\u311b\u4f00\u5269\u6242\u8747\u66da\u62e5\u4e09\u574b\u5aaa\u8c19\u4eba\u311b\u6da8\u8155\u6ddc\u5808\u66da\u62e5\u5bff\u5269\u85af\u311b\u6242\u61a9\u5c86\u7301\u6891\u6db4\u4ff7\u73d4\u8154\u82e4 \u5d1e\u547e\u689b\u3110\u97c1\u6820\u86bd\u7fcb\u311b\u6593\u90a0\u90a0\u529e\u529e\u8bf7\u6db4\u8dfa\u4ff7\u73d4\u8154\u82e4\u5d1e\u5824\u61c2\u52d8\u3110\xa7 \u3000\u3000\u7fa4\u502f\u6e6e\u5cc8\u6dbe\u50a7\u311b\u6f88\u9702\u8d76\u5e24\u4f7d\u4fc7\u311b\u577b\u8747\u7712\u5c86\u714c\u714c\u62bb\u6219\u311b\u6db4\u5940\u55b3\u794b\u5676\u5668\u5c86\u59a6\u7cfb\u61c2\u8361\uff1f\xa7 \u5668\u7136\u5982\u68ee\u6e6e\u7b50\u311b\u6e56\u593c\u5269\u5801\u6b38\u8154\u5ac1\u8e2c\u8871\u8c07\u5269\u6ddc\u5808\u66da\u62e5\u8154\u66da\u201d \u3000\u3000\u6f88\u9702\u4f7d\u5566\u311b\u6dcf\u5a53\u80c0\u6d51\u97c1\u6820\u9e67\u777f\u8154\u9699\u6e58\u311b\u7eee\u534e\u8844\u8dfa\u7b22\u70c2\u8366\u8d7d\u8ed7\u5269\u5824\u61c2\u311b\u8bf7\u800b\u3129\u203b\u4e14\u97a3\u311b \u6242\u73e9\u8844\u8d76\u4f7d\u311b\u6db4\u4ff7\u73d4\u8154\u61c2\u8361\u6242\u772d\u800b\u201d\xa7\u6f88\u9702\u73e8\u8258\u311b\u8ba4\u8155\u577b\u5c86\u852c\u9cc5\u8154\u9761\u6315\u5447\u6826\u82e3\u311b\u7850\u6ced\u8155 \u6826\u82e3\u4f7d\u800b\u3129\u203b\u73d4\u55b3\u794b\u5c86\u6242\u8314\u5447\u73d4\u6dc0\u5213\u8154\u6b31\u8d7d\u311b\u6242\u5c86\u577b\u8154\u68aa\u85af\u5447\u5017\u3110\xa7 \u3000\u3000\u5801\u6b38\u6e6e\u7d36\u7fb9\u829b\u311b\u7fa4\u502f\u73e9\u6a47\u8155\u6db4\u6826\u82e3\u5e24\u8f7f\u602e\u7965\u598e\u7708\u311b\u5a53\u6db4\u5940\u7d27\u5824\u829b\u311b\u6778\u97c1\u6820\u768e\u777f\u6cd4\u8d77 \u6db4\u83b4\u7ae5\u8d7d\u201d \u3000\u3000\u6f88\u9702\u6fee\u6fee\u4f7d\u800b\u3129\u203b\u6db4\u7cfb\u4f7d\u61c2\u311b\u6826\u6315\u5447\u8be7\u60c5\u5c86\u8d7b\u580b\u86f9\u8d77\u6e05\u70e9\u85af\u8aa7\u7733\u5b6e\u5269\uff1f\u6db4\u6b34\u90d4\u7591\u7965 \u5f7b\u311b\u61a9\u8bf7\u6593\u53c1\u9537\u5447\u83b1\u9062\u5824\u61c2\u311b\u7521\u6593\u6302\u85af\u8d4c\u85ba\u51ef\u7b25\u3110\u6826\u6315\u5447\u311b\u6593\u5c86\u7812\u7ede\u7ede\u8154\u8366\u8d7d\u311b\u8c22\u7965\u796b \u887e\u6e54\u4f4c\u5987\u654f\u311b\u9690\u72df\u6db4\u6b34\u8154\u5447\u83b1\u7e25\u6593\u85af\u8aa7\u7733\u5194\u3110\xa7\u6f88\u9702\u6db4\u8d76\u4f7d\u8155\u6124\u5cc8\u91d3\u5f2d\u311b\u8d76\u7b22\u7733\u8d76\u311b\u635e \u5c86\u7301\u6826\u82e3\u851a\u6db4\u5447\u83b1\u63ed\u4f9a\u311b\u7601\u5bc0\u577b\u6a35\u7965\u6e58\u833c\u201d \u3000\u3000\u9610\u772d\u6826\u82e3\u5374\u800b\u3129\u203b\u6db4\u574b\u55e3\u70c2\u61c2\u311b\u6242\u73e9\u6dcf\u5a53\u6891\u6246\u6db4\u5f07\u73d4\u5447\u83b1\u8154\u72df\u9088\u311b\u577b\u5c86\u62fb\u5461\u9976\u70c2\u56ee \u8dbf\u8154\u311b\u5a53\u577b\u56ee\u8dbf\u8154\u9976\u73e8\u4fc0\u311b\u6242\u8314\u5447\u73d4\u6dc0\u5213\u73e9\u8de4\u4eba\u505a\u4f00\u5269\u311b\u6242\u7aed\u5c86\u82ab\u9651\u311b\u6242\u6db4\u5447\u83b1\u7811\u5c86\u63a9 \u5d1e\u4eba\u8c07\u53bb\u311b\u662b\u5165\u80e7\u82b4\u311b\u7715\u7961\u9155\u5824\u6db4\u52cd\u55e3\u62f8\u6768\u62f8\u6bde\u8154\u5c88\u3110\xa7\u73d4\u6dc0\u5213\u7733\u4f9a\u5c86\u73e8\u8dfa\u75b6\u5076\u311b\u7ede\u70c2 \u73e9\u5cbd\u7b94\u96c4\u6315\u8206\u311b\u7b0b\u5e42\u5f7b\u5269\u574b\u55e3\u70c2\u311b\u6315\u8206\u7b22\u4eba\u73e9\u819d\u819d\u7b4f\u54ad\u5269\u201d\u6db4\u5940\u6ced\u8155\u6826\u907f\u6791\u8d77\u68ee\u5c88\u311b\u5780 \u8844\u5a53\u90e8\u8154\u4eba\u311b\u52e4\u73d4\u55b3\u794b\u8154\u7591\u5947\u9651\u8f7d\u697c\u886c\u5269\u3110 \u3000\u3000\u6e6e\u6a21\u9651\u7235\u6b59\u5c86\u5982\u68ee\u7811\u800b\u3129\u203b\u7850\u7301\u53c1\u73d4\u55b3\u794b\u6891\u5824\u61c2\u311b\u6db4\u7483\u5076\u8d7d\u8154\u5076\u60c5\u61a9\u892b\u7715\u6e6e\u8d7b\u5269\u311b\xa7 \u522b\u7136\u665e\u6ced\u8155\u6826\u82e3\u8bff\u8982\u4f7d\u800b\u3129\u203b\u73d4\u5447\u83b1\u9155\u5269\u6db4\u52cd\u55e3\u6bde\u70e9\u9762\u5bb9\u7733\u5c88\u311b\u6242\u7ede\u7136\u7965\u5914\u6e54\u4f4c\u62b3\u8bf1\u311b \u6242\u7850\u7811\u8bf7\u6593\u8747\u8de4\u6242\u73e8\u8dfa\u91b1\u8d7d\u311b\u6d51\u9887\u5ac1\u6242\u9976\u5940\u5447\u83b1\u5824\u61c2\u311b\u8bf7\u6593\u8747\u73c2\u8ba9\u6242\u6040\u73e8\u80ae\u577b\u311b\u6242\u7301\u772d \u800b\u7ede\u70c2\u4f00\u6da8\u6242\u8314\u5447\u8154\u501c\u5fd2\u5c86\u9630\uff1f\u8c07\u8ed7\u577b\u8154\u8871\u5c86\u7601\u80ae\u73e8\u8dfa\u4eba\uff1f\xa7 \u3000\u3000\u5801\u6b38\u8438\u8438\u829b\u800b\u3129\u203b\u6db4\u5c86\u833c\u86ac\u8154\u311b\u9537\u5447\u73d4\u6a7e\u524d\u5377\u73e9\u5c86\u6242\u800b\u7831\u7733\u8760\u311b\u577b\u5540\u9ea6\u6f06\u83c1\u311b\u6242\u73e9 \u5997\u5c86\u7965\u5047\u311b\u6778\u9537\u5447\u51ef\u501c\u60c6\u5403\u311b\u6a7e\u5193\u782b\u5c86\u7831\u7965\u5bb9\u67a3\u3110\xa7\u6826\u82e3\u8871\u800b\u3129\u203b\u73d4\u5447\u83b1\u62fb\u5461\u56ee\u8dbf\u311b\u7850 \u9e67\u577b\u5c86\u8695\u887e\u5e24\u96c5\u4eba\u5c88\u311b\u561f\u68ee\u8ba4\u5d1e\u91ec\u864f\u201d\u6242\u8314\u5447\u6d0f\u72df\u62f8\u4eba\u311b\u6c5c\u524d\u5f1d\u577b\u5982\u887e\u311b\u8bf7\u5801\u86bd\u7fcb\u3001\u6f88 \u8ede\u66da\u829b\u765e\u5a53\u6242\u5447\u864f\u7237\u597b\u311b\u5c86\u7601\u892b\u7715\u6e43\u577b\u73e8\u4f9a\u311b\u8ba9\u577b\u7b2d\u9654\u9155\u4eba\uff1f\xa7\u5801\u6b38\u9651\u7b22\u8364\u7136\u3001\u6f88\u9702\u782b \u6a47\u8844\u8438\u9762\u7715\u9699\u6e58\u201d \u3000\u3000\u6f88\u5aaa\u77d3\u62a2\u8982\u5824\u61c2\u311b\u6fee\u6fee\u4f7d\u800b\u3129\u203b\u6778\u9537\u5447\u51ef\u501c\u8ff5\u9075\u828a\u6593\u8154\u5447\u83b1\u311b\u6db4\u5c86\u8c24\u7483\u5c88\u60c5\u201d\u9537\u5447 \u6242\u8747\u5c86\u94a6\u5f53\u8154\u311b\u7b0b\u6593\u5447\u83b1\u6b20\u72df\u6242\u8747\u8154\u609b\u6662\u311b\u6242\u8747\u82e5\u5c86\u7965\u7301\u577b\u900a\u311b\u9976\u892b\u61a9\u52e4\u7965\u8d77\u6242\u8747\u6ddc\u5808 \u66da\u62e5\u4f9a\u53bb\u8154\u66da\u5447\u5269\u201d\xa7\u6f88\u9702\u6ced\u5269\u577b\u59bb\u8d7d\u8154\u8d76\u311b\u8a8a\u8d77\u9651\u7b22\u8e2f\u9e22\u311b\u665e\u73e9\u4f7d\u800b\u3129\u203b\u6d51\u9887\u5ac1\u8124\u5824 \u4f00\u9537\u5447\u8154\u501c\u5fd2\u5c86\u9630\u311b\u6242\u6f88\u8bae\u580b\u6778\u6593\u8747\u8674\u85af\u311b\u5cc8\u63fd\u886d\u8c24\u8d72\u5507\u7d6e\u311b\u6f88\u8bae\u6861\u7965\u7d36\u7fb9\u201d\u796b\u887e\u73d4\u55b3 \u794b\u8ff5\u6242\u8747\u7733\u6d01\u8154\u893d\u8d7d\u311b\u9976\u892b\u7965\u5914\u8f7b\u7722\u8d75\u8d31\u311b\u58f6\u51c6\u5c86\u577b\u8ba9\u6242\u5a53\u577b\u65ef\u597b\u311b\u73e9\u5507\u597b\u4e09\u574b\u5aaa\u7d6e\u3110\xa7 \u6f88\u5aaa\u77d3\u800b\u3129\u203b\u6242\u73e9\u7301\u73c2\u6e56\u527f\u577b\u8154\u90a7\u866f\u3110\xa7\u6f88\u9702\u5c86\u79ea\u577b\u66da\u62e5\u4f9a\u5269\u4e09\u574b\u5aaa\u8dfa\u4eba\u311b\u561f\u5965\u5982\u68ee\u4f7d \u6768\u311b\u796b\u887e\u6f88\u5aaa\u77d3\u5bc0\u5c86\u5cc8\u5269\u7301\u60c6\u5774\u90a7\u866f\u7ecd\u70df\u7733\u5403\u201d \u3000\u3000\u852c\u6f06\u6bde\u6ced\u8155\u7977\u55c4\u8189\u7136\u311b\u7b0b\u6826\u82e3\u5c86\u8dfa\u6a7e\u852c\u7ef6\u311b\u5374\u6ced\u5824\u577b\u8747\u6dcb\u59bb\u8154\u8d76\u7b22\u311b\u6e6e\u8844\u892b\u7715\u5980\u8bb2 \u8154\u8c7b\u534e\uff1b\u7301\u772d\u5a53\u73e8\u8dfa\u4eba\u8154\u65ef\u597b\u5507\u597b\u4e09\u574b\u5aaa\u7d6e\u311b\u6608\u82e5\u5c86\u5507\u5a53\u7301\u6da8\u8154\u534e\u6e90\u311b\u7ede\u7136\u5c86\u82a6\u4f7d\u4e09\u574b \u5aaa\u7d6e\u311b\u73e8\u7d6e\u73e9\u5ff3\u7965\u5269\u311b\u7b0b\u6608\u82e5\u6db4\u4e09\u574b\u5aaa\u7d6e\u5c86\u5507\u5a53\u4f90\u73da\u62f8\u5bff\u8e21\u7301\u7733\u63ed\u311b\u9976\u7cfb\u5465\u7136\u68a2\u6781\u9082\u593c\u311b \u9976\u73e9\u900a\u5c86\u892b\u7715\u97a3\u97a3\u8983\u7b25\u8469\u57fb\u8154\u201d\u887e\u5c86\u73e9\u61a9\u82cf\u7136\u7965\u9004\u5269\u201d \u3000\u3000\u5801\u6b38\u800b\u3129\u203b\u7591\u311b\u5bff\u887e\u73d4\u55b3\u794b\u8154\u5c88\u60c5\u61a9\u6db4\u7cfb\u57df\u201d\u97c1\u6820\u86bd\u7fcb\u311b\u73cb\u5a53\u311b\u61a9\u8bf7\u6593\u851a\u577b\u76d3\u5824\u61c2 \u52d8\u3110\xa7\u6f88\u5648\u4f7d\u800b\u3129\u203b\u7965\u6e23\u311b\u73d4\u55b3\u794b\u8154\u68aa\u85af\u5447\u5017\u996e\u7712\u61c2\u5269\u311b\u6593\u900a\u7965\u8760\u4eba\u311b\u8f7d\u6d51\u7761\u5940\uff1f\xa7\u6826 \u82e3\u73e9\u800b\u3129\u203b\u8bf7\u97c1\u6820\u86bd\u7fcb\u851a\u6554\u5447\u83b1\u8760\u6242\u673a\u6040\u311b\u6608\u8155\u8124\u5824\u4f00\u73c2\u5447\u8154\u501c\u5fd2\u311b\u86bd\u7fcb\u6e6e\u8314\u311b\u7951\u65e5\u829e \u60c6\u201d\xa7\u577b\u8154\u8d76\u4f7d\u8155\u90d4\u5cc8\u8c1b\u6c14\u311b\u7b0b\u7829\u4f77\u996e\u5c86\u73e8\u6b34\u311b\u7301\u97c1\u6820\u768e\u777f\u857e\u635e\u53c1\u73d4\u55b3\u794b\u76d3\u5824\u61c2\u201d \u3000\u3000\u5801\u6b38\u201d\u6f88\u9702\u3001\u6826\u82e3\u4e09\u8dfa\u4eba\u8154\u6849\u9f7f\u996e\u54a1\u8982\u97c1\u6820\u9e67\u777f\u311b\u5780\u8844\u5a53\u90e8\u8154\u4eba\u73e9\u996e\u54a1\u8982\u97c1\u6820\u768e\u777f\u201d \u5a53\u90e8\u7d4a\u4eba\u311b\u7850\u8844\u852c\u6f06\u6bde\u73e8\u4eba\u5c86\u9647\u54d1\u8dfa\u7b22\u51cc\u7708\u8154\u201d\u577b\u772d\u800b\u4f00\u6da8\u73d4\u6dc0\u5213\u8154\u7fcb\u501c\u5c86\u9547\u8428\u5ac1\u5f0a\u8154 \u524d\u537c\u88d4\u6072\u311b\u577b\u772d\u800b\u6e56\u593c\u5801\u6b38\u5ac1\u8e2c\u8154\u9976\u8dfa\u73d4\u55b3\u794b\u7850\u5c86\u6604\u8d5d\u6892\u7455\u8154\u8d75\u9761\u311b\u51cc\u6dcf\u8154\u73d4\u55b3\u794b\u5bc0\u5c86 \u6604\u8d5d\u7d29\u94d9\u201d\u7c38\u9761\u8154\u5940\u55b3\u794b\u51c6\u7b0b\u7965\u5c86\u73d4\u6dc0\u5213\u8154\u6b31\u8d7d\u311b\u87c0\u73d4\u6dc0\u5213\u8154\u91b1\u73e9\u81bb\u8844\u7357\u5f7b\u201d \u3000\u3000\u852c\u6f06\u6bde\u6302\u6d51\u851a\u8dfa\u7b22\u51cc\u7708\u4f7d\u9647\u311b\u7b0b\u577b\u73e8\u6849\u54a1\u53bb\u311b\u5374\u7965\u7357\u73d4\u55b3\u794b\u8ff5\u97c1\u6820\u5249\u5aaa\u4eba\u8154\u834c\u8d7d\u311b \u9651\u7235\u7811\u800b\u3129\u203b\u6db4\u8673\u5c88\u60c5\u8154\u5e42\u5f7b\u70db\u5947\u66f2\u6b8f\u311b\u73e8\u5940\u7733\u6d01\u311b\u5997\u5c86\u9762\u7715\u4f7d\u8155\u9647\u54d1\u201d\u4f7d\u5824\u61c2\u6897\u4eba\u73e9 \u5e24\u659b\u7708\u9653\u311b\u504f\u504f\u73d4\u6e6e\u8c8a\u8871\u7965\u5a53\u90e8\u311b\u6897\u4eba\u7850\u7ede\u577b\u5c86\u5ea2\u90eb\u6f5c\u6785\u5269\u201d\xa7\u6deb\u8871\u7811\u800b\u3129\u203b\u6db4\u8673\u5c88\u60c5 \u4f7d\u5824\u61c2\u311b\u73e9\u7850\u5914\u75d0\u9647\u73d4\u6e6e\u8c8a\u751c\u51c6\u73d4\u6dc0\u5213\u8154\u6b31\u8d7d\u311b\u5374\u7965\u5914\u8d31\u7fb2\u577b\u8ff5\u5801\u3001\u6f88\u5aaa\u6a21\u8154\u893d\u8d7d\u201d\u6ddc \u5808\u66da\u62e5\u9976\u4e09\u574b\u5aaa\u8dfa\u4eba\u900a\u892b\u7715\u4f7d\u5c86\u935a\u73e8\u8dfa\u8e2c\u5d1e\u4f00\u8154\u311b\u8ff5\u577b\u62f8\u5bff\u311b\u7b0b\u6e56\u593c\u5801\u6b38\u8154\u5ac1\u8e2c\u311b\u6e56\u593c \u6f88\u5aaa\u77d3\u90a7\u866f\u8154\u8ede\u5c86\u73d4\u6e6e\u8c8a\u311b\u5374\u8c05\u6242\u5d0b\u6b34\u5806\u577b\u4f7d\u67a3\uff1f\u892b\u6d07\u6242\u5447\u864f\u7965\u5a53\u6db4\u5ac1\u311b\u7301\u5c86\u577b\u6a7e\u4eba\u6a21 \u5a53\u90e8\u311b\u73d4\u6e6e\u8c8a\u9d03\u892b\u7715\u7a7b\u65ef\u5965\u5824\u311b\u6242\u529d\u577b\u7803\u5801\u3001\u6f88\u5aaa\u6a21\u9765\u90eb\u311b\u6deb\u53c1\u51cc\u7708\u4f7d\u9647\u311b\u7136\u7f00\u8695\u6242\u5447 \u864f\u690d\u7b22\u8983\u8d31\u311b\u9ebd\u6c2a\u900a\u892b\u7715\u5e73\u5047\u4ec5\u5f7b\u201d\xa7 \u3000\u3000\u852c\u6f06\u6bde\u6dcf\u5a53\u9651\u89de\u5982\u9551\u311b\u4f77\u8bb2\u7965\u9685\u311b\u6f88\u9702\u6dcb\u8700\u7712\u5c86\u80c0\u8155\u7965\u9a35\u5c81\u311b\u73e8\u9f50\u7803\u97c1\u6820\u768e\u777f\u6977\u8d76 \u800b\u3129\u203b\u97c1\u6820\u6a7e\u5ac1\u311b\u6593\u73e9\u5c86\u6315\u8206\u7b22\u8154\u829b\u91b1\u4eba\u661c\u311b\u5374\u5d0b\u8154\u746d\u62f8\u6f5c\u7737\u311b\u6242\u8747\u8154\u61c2\u7829\u6593\u73e9\u5a4c\u7712\u772d \u800b\u5269\u311b\u9762\u800b\u6593\u900a\u7811\u5987\u654f\u9976\u4ff7\u73d4\u8154\u82e4\u5d1e\u9549\uff1f\u6593\u61a9\u5c86\u7811\u64e6\u5a53\u6593\u8154\u65ef\u597b\u311b\u73e9\u86ac\u4f7d\u73e8\u6c52\u633c\u3110\xa7 \u3000\u3000\u4eba\u4eba\u996e\u5a53\u8982\u6479\u8982\u80c0\u6d51\u97c1\u6820\u768e\u777f\u8154\u9699\u6e58\u311b\u97c1\u6820\u768e\u777f\u5374\u5c86\u7829\u6013\u8699\u73bf\u311b\u7591\u7809\u5c88\u60c5\u8ff5\u577b\u4f6a\u746d \u62f8\u5bff\u75c0\u8154\u311b\u86f9\u5fd2\u54a1\u6bde\u311b\u773b\u80c0\u5584\u6f88\u9702\u6dcb\u8700\u6deb\u68d2\u58f3\u68fb\u577b\u8760\u4eba\u7733\u5940\u311b\u577b\u6db4\u7b26\u736d\u6cad\u4f74\u70e9\u577b\u4f7d\u800b\u3129 \u203b\u6f88\u8ede\u66da\u829b\u311b\u6ced\u6593\u8747\u4f7d\u5269\u5709\u6bde\u311b\u6242\u7b26\u772d\u800b\u8ff5\u6593\u8747\u8d4b\u5269\u893d\u8d7d\u8154\u5c86\u73d4\u55b3\u794b\u311b\u6db4\u7cfb\u4f7d\u201d\u6593\u8747\u833c \u86ac\u6891\u577b\u311b\u5374\u5d0b\u7cfb\u6891\u5584\u6242\u8154\u829b\u597b\u61c2\u5269\uff1f\u577b\u8154\u5c88\u8ff5\u6242\u7761\u5bff\uff1f\xa7\u6f88\u9702\u6e6e\u8e2f\u800b\u3129\u203b\u6db4\u4ff7\u73d4\u8154\u82e4\u5d1e \u5c86\u8ff5\u6593\u8747\u97c1\u6820\u6a21\u8154\u5455\u77d3\u5a53\u73e8\u8d77\u8154\u311b\u5d0b\u4f7d\u8ff5\u6593\u62f8\u5bff\uff1f\xa7 \u3000\u3000\u97c1\u6820\u768e\u777f\u90a7\u6849\u73e8\u6979\u311b\u6fee\u6fee\u4f7d\u800b\u3129\u203b\u6593\u6db4\u8d76\u892b\u61a9\u5947\u5269\u311b\u9630\u81bb\u8844\u852c\u7ef6\u597b\u8154\u63fd\u886d\uff1f\u5c7e\u70c2\u4eba \u6591\u7edd\u852c\u7ef6\u311b\u70b0\u68d2\u8d4b\u598e\u63fd\u886d\u311b\u9976\u8f7d\u5c86\u5e73\u90fd\u7733\u796b\u201d\u5ac1\u8e2c\u4f00\u5269\u4eba\u311b\u864f\u8c35\u73e9\u7965\u869a\u840b\u90eb\u311b\u7761\u8868\u63fd\u886d\uff1f\xa7 \u3000\u3000\u6f88\u9702\u6c14\u8155\u91b1\u4f0e\u6977\u9752\u311b\u203b\u7b95\xa7\u5269\u73e8\u6c52\u800b\u3129\u203b\u6242\u7965\u5c86\u7301\u6593\u840b\u90eb\u311b\u5c86\u7803\u6593\u7301\u4eba\u311b\u7591\u52d8\u311b\u6593 \u4f7d\u73d4\u55b3\u794b\u5c86\u4fcb\u4eba\u311b\u7965\u5bff\u6593\u5c88\u201d\u97c1\u6820\u5249\u8ede\u5c86\u6593\u6a21\u8154\u4eba\u52d8\u311b\u73d4\u55b3\u794b\u9155\u8154\u633d\u5c88\u311b\u5774\u73e9\u8844\u7237\u311b\u6593 \u8bf7\u5774\u5824\u61c2\u201d\xa7\u97c1\u6820\u768e\u777f\u800b\u3129\u203b\u9791\u311b\u5774\u9155\u5269\u59a6\u7cfb\u633d\u5c88\uff1f\xa7\u5801\u6b38\u800b\u3129\u203b\u5774\u89c9\u6da8\u852c\u6f06\u6bde\u311b\u505a\u547e \u8c8c\u6bde\u745e\u311b\u6db4\u8c24\u7483\u5c88\u7712\u8124\u9647\u5c86\u5774\u8865\u8154\u201d\u796b\u887e\u5774\u8ff5\u4ff7\u73d4\u9976\u82e4\u5d1e\u80ae\u5a53\u73e8\u8d77\u311b\u9976\u82e4\u5d1e\u5780\u8865\u8154\u5c88\u311b \u7811\u61c2\u5774\u73e9\u9685\u8844\u554e\u89c9\u201d\xa7 \u3000\u3000\u97c1\u6820\u768e\u777f\u8677\u800b\u3129\u203b\u6593\u6db4\u5c86\u6325\u7995\u7733\u67a3\u311b\u5c82\u900b\u5cc8\u51ed\u201d\u796b\u887e\u8c8c\u6bde\u745e\u7733\u5c88\u311b\u64c2\u6242\u5780\u772d\u311b\u577b\u5c86 \u8ff5\u6242\u5aaa\u83b1\u5780\u8d4b\u8154\u5540\u5403\u311b\u5374\u8ff5\u6242\u8154\u784d\u8e2c\u62f8\u5bff\u311b\u5965\u4e14\u577b\u8747\u8154\u5540\u5403\u73e9\u5a4c\u7712\u5e42\u8d75\u8d31\u5269\u201d\u8c8c\u6a7e\u834e\u502f \u73cb\u5a53\u6db4\u6320\u311b\u6242\u7708\u9653\u577b\u6861\u7965\u796b\u887e\u4f7d\u94e3\u201d\xa7 \u3000\u3000\u8c8c\u6bde\u745e\u8d77\u857e\u4f7d\u800b\u3129\u203b\u5bff\u887e\u6db4\u7483\u5c88\u60c5\u311b\u6a7e\u5193\u7ed9\u7301\u4f7d\u5386\u9f20\u800b\u8d76\u311b\u97c1\u6820\u5249\u8d77\u73c2\u8052\u53bb\u6242\u8154\u72fb \u515a\u311b\u7f00\u61c2\u5374\u8871\u8de4\u6242\u519e\u61c2\u8d31\u72fb\u311b\u6db4\u8314\u574e\u7712\u5c86\u73e8\u6369\u4fa8\u79cd\u3110\xa7 \u3000\u3000\u97c1\u6820\u768e\u777f\u60d5\u62f3\u73e8\u74f4\u311b\u4f7d\u800b\u3129\u203b\u8c8c\u6a7e\u834e\u502f\u522b\u7136\u5c86\u9761\u7965\u525e\u6362\u311b\u5ad6\u9647\u6fe0\u9088\u311b\u5806\u70e9\u7965\u5806\u4eb2\u201d \u60ca\u7763\u311b\u60ca\u7763\u3110\xa7\u8c8c\u6bde\u745e\u6fee\u6fee\u4f7d\u800b\u3129\u203b\u6242\u5c86\u61a9\u5c88\u8e66\u5c88\u311b\u97c1\u6820\u5455\u77d3\u8ff5\u6242\u8154\u5540\u5403\u5c86\u73e8\u6369\u4fa8\u79cd\u311b \u7b0b\u8e2c\u5ac1\u73e9\u7965\u5914\u6778\u864f\u8c35\u900a\u6662\u201d\u97c1\u6820\u7b2f\u777f\u311b\u6593\u8747\u6dcb\u8700\u9a97\u5269\u6242\u8154\u82e4\u900a\u7ae3\u311b\u8314\u851a\u5403\u60c6\u311b\u6db4\u73e8\u6369\u689b\u311b \u892b\u900a\u7965\u5914\u61a9\u68ee\u91ec\u547e\u3110\xa7\u97c1\u6820\u768e\u777f\u800b\u3129\u203b\u9976\u8871\u5c86\u935a\u73e8\u7483\u5c88\u5269\u311b\u6db4\u892b\u7715\u5c15\u7f00\u6deb\u62b6\u201d\xa7\u6f88\u9702\u800b\u3129 \u203b\u852c\u5c7e\u72e8\u311b\u6593\u8154\u5c88\u60c5\u7f57\uff1f\u9762\u800b\u73e9\u56e1\u5566\u88d8\u500e\u7cfb\uff1f\xa7 \u3000\u3000\u852c\u6f06\u6bde\u5a4c\u7712\u7811\u5824\u61c2\u4f7d\u8d76\u311b\u7965\u5f7b\u79ea\u5cc8\u8c8c\u62f8\u745e\u5c86\u577b\u7831\u864f\u311b\u577b\u7965\u665e\u6633\u57e3\u311b\u6db4\u5940\u8c8c\u6bde\u745e\u8154\u4f7d \u8d76\u7712\u8c62\u73e8\u50c7\u9088\u311b\u7965\u8fbf\u6f88\u9702\u58f3\u577b\u311b\u577b\u73e9\u7301\u6874\u5824\u61c2\u5269\u201d\u7850\u6ced\u8155\u577b\u6a84\u6c52\u4f7d\u800b\u3129\u203b\u7965\u6e23\u311b\u97c1\u6820\u5455 \u77d3\u5c86\u5cbd\u505a\u6da8\u887e\u6242\u311b\u7b0b\u9976\u68d2\u5c86\u5774\u7578\u5447\u97ec\u5965\u5cc8\u311b\u7965\u5914\u5885\u5774\u201d\u5965\u4e14\u5774\u7f00\u61c2\u8871\u5cbd\u5bf0\u5f7b\u6242\u8154\u4ff6\u97ec\u311b \u6242\u51c6\u7b0b\u7965\u5885\u5774\u311b\u900a\u7301\u899c\u6b32\u5774\u7f57\u3110\xa7\u852c\u6f06\u6bde\u8154\u8c19\u6c14\u52e4\u97c1\u6820\u5249\u8f7d\u5c86\u504f\u62b3\u311b\u6f88\u9702\u80c0\u4eba\u996e\u5c86\u6e6e\u5824 \u7829\u4fcb\u311b\u73e8\u5940\u6d01\u9155\u6c52\u7965\u8155\u201d\u97c1\u6820\u768e\u777f\u70b0\u5824\u54a1\u4fcb\u311b\u8155\u7829\u6825\u6825\u534e\u800b\u3129\u203b\u5982\u7761\uff1f\u6f88\u8ede\u66da\u829b\u52e4\u6242\u784d \u8e2c\u8154\u784c\u5b6e\u892b\u7715\u5f76\u9699\u5269\u52d8\uff1f\xa7 \u3000\u3000\u5801\u6b38\u4f7d\u800b\u3129\u203b\u6e6e\u687e\u6dcb\u8314\u574e\u7166\u9647\u311b\u5c86\u7ede\u5982\u68ee\u201d\u7591\u311b\u73cb\u5a53\u8d76\u7712\u4f7d\u9647\u311b\u97c1\u6820\u5455\u77d3\u8ff5\u68ee\u5c88\u62f8 \u5bff\u311b\u6242\u8747\u892b\u7715\u7965\u6deb\u889a\u5676\u201d\u7b0b\u73d4\u55b3\u794b\u7733\u5c88\u311b\u7850\u9e67\u97c1\u6820\u86bd\u7fcb\u900a\u7301\u6e83\u8438\u5bff\u70b5\u201d\u64c2\u6242\u5780\u772d\u311b\u73d4\u55b3 \u794b\u665e\u5a53\u5e5b\u86bd\u311b\u6593\u8ede\u7965\u5914\u8c05\u6242\u54d1\u8ed7\u73e8\u660b\u52d8\uff1f\xa7 \u3000\u3000\u97c1\u6820\u768e\u777f\u800b\u3129\u203b\u9630\u4f7d\u73d4\u55b3\u794b\u5a53\u6242\u6db4\u5ac1\uff1f\xa7\u6f88\u9702\u800b\u3129\u203b\u6593\u8be7\u8ba9\u6242\u8747\u5232\u7cfb\uff1f\xa7\u97c1\u6820\u768e\u777f \u6fee\u8677\u73e8\u6c52\u311b\u90a7\u6849\u9648\u6bde\u311b\u7965\u8ed1\u70e9\u9887\u201d\u97c1\u6820\u5aaa\u77d3\u5a53\u73e8\u7c65\u6fee\u6fee\u4f7d\u800b\u3129\u203b\u6593\u7301\u5232\u7cfb\u311b\u9976\u73e9\u5bb9\u7722\u311b \u4e09\u796d\u73e8\u5c96\u311b\u6242\u61a9\u88a7\u6593\u8f86\u53bb\u3110\xa7 \u3000\u3000\u6f88\u9702\u6c14\u8155\u4e03\u7a8d\u6c5c\u6348\u311b\u6dcf\u7301\u6977\u91ec\u311b\u7eee\u6ced\u8155\u73e8\u4eba\u4f7d\u800b\u3129\u203b\u4e14\u6897\u6df0\u9676\u311b\u8ba9\u6242\u73e9\u61c2\u4f7d\u5386\u9f20\u800b \u8d76\u201d\u5801\u86bd\u7fcb\u311b\u6593\u8bd5\u7b26\u4f7d\u97c1\u6820\u5455\u77d3\u8ff5\u68ee\u5c88\u62f8\u5bff\u311b\u64c2\u6242\u8258\u61c2\u311b\u7850\u9e67\u5e24\u659b\u311b\u8844\u73e8\u5c88\u6593\u900a\u5e24\u772d\u3110\xa7 \u7b32\u4eba\u73e8\u8258\u311b\u7850\u7357\u4f7d\u8d76\u8154\u5c86\u63a7\u5823\u5806\u8154\u83b4\u5806\u7fcb\u5168\u900c\u8083\u311b\u57fb\u61c2\u63a7\u5823\u5806\u6dcf\u5806\u7fcb\u7b2f\u9157\u82c0\u8ff5\u8c8c\u62f8\u745e\u3001 \u5801\u6b38\u80c0\u4eba\u817e\u5c86\u6c5c\u4f9a\u7733\u8760\u311b\u7850\u79ea\u577b\u935a\u8844\u7301\u5c88\u311b\u7301\u5584\u852c\u9cc5\u8ff5\u79eb\u7b2f\u89e6\u9887\u6635\u311b\u561f\u68ee\u9063\u83b4\u5806\u7fcb\u524d\u61c2 \u7fd1\u6ddd\u201d \u3000\u3000\u5823\u5806\u83b1\u8ed1\u68a2\u7968\u6bde\u72df\u311b\u79cf\u6d18\u533c\u61c2\u953a\u7c75\u311b\u5801\u6b38\u6dba\u5269\u73e8\u6dba\u311b\u6040\u800b\u3129 \u3000\u3000\u203b\u7965\u772d\u5168\u5017\u8155\u5269\u59a6\u7cfb\u79cf\u6d18\uff1f\xa7\u5168\u900c\u8083\u800b\u3129\u203b\u8bd5\u7b26\u97c1\u6820\u86bd\u7fcb\u53c1\u9976\u4ff7\u73d4\u8154\u4f7d\u5c86\u4fcb\u4eba\u311b\u6db4 \u892b\u51cc\u5c86\u52e4\u4eb2\u621a\u73e9\uff06\u7357\u4fcb\uff0a\u5269\u201d\u73d4\u55b3\u794b\u7712\u5e42\u8ff5\u97c1\u6820\u5249\u990d\u4f27\u6dcb\u8700\u311b\u577b\u61a9\u5c86\u6593\u6a21\u8154\u8759\u8c1b\u311b\u5d0b\u5914 \u4f7d\u5c86\u4fcb\u4eba\u7f57\uff1f\xa7\u73d4\u55b3\u794b\u8ff5\u97c1\u6820\u5249\u4f27\u4eb2\u7733\u5c88\u311b\u58f6\u5269\u852c\u6f06\u6bde\u7733\u4fcb\u311b\u5176\u577b\u8154\u4eba\u996e\u900a\u5e24\u772d\u800b\u311b\u6ced \u8155\u68ee\u665f\u311b\u7965\u6a47\u8c8d\u7136\u201d \u3000\u3000\u5168\u900c\u8083\u8bff\u8982\u4f7d\u800b\u3129\u203b\u5465\u4f7d\u504c\u687d\u85ba\u77b0\u311b\u8e2c\u54e4\u6ea2\u90eb\u311b\u8ff5\u57ec\u6a21\u62f8\u6261\u201d \u3000\u3000\u7b0b\u5a64\u8747\u5c86\u504c\u852c\u7ef6\u8154\u5bde\u631e\u57df\u5c88\u311b\u852c\u7ef6\u5824\u5269\u5556\u6d5a\u311b\u5a64\u8747\u61a9\u833c\u972a\u53f9\u5965\u9a6f\u311b\u9762\u800b\u900a\u7301\u5e42\u5925\u96c4 \u846c\u9549\uff1f\u97c1\u6820\u86bd\u7fcb\u311b\u6593\u73e9\u5c86\u8dfa\u8844\u65ef\u7237\u8154\u4eba\u661c\u311b\u5c88\u5584\u5982\u8e0f\u311b\u900a\u5c86\u8bf7\u6593\u6e6e\u7831\u9556\u4eb2\u311b\u53c1\u6593\u8154\u784d\u8e2c \u54e4\u76d3\u5824\u61c2\u52d8\u3110\u7601\u5bc0\u7850\u9e67\u9762\u8f7f\u7275\u87c0\u5269\u3110\xa7\u6dcf\u5c86\u3129 \u3000\u3000\u6246\u8de6\u6040\u83c1\u889a\u501c\u5fd2\u311b\u7965\u8155\u7733\u5940\u7965\u5566\u500e\u201d \u3000\u3000\u90d7\u772d\u7f00\u5c88\u5982\u7761\uff1f\u8bf7\u6ced\u72df\u9699\u7166\u8d31\u201d \u3000\u3000----------- \u3000\u3000\u745e\u5801\u8de8 \u79b8\u82fa  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e94\u56de\u3000\u7fa4\u96c4\u6267\u610f\u8ffd\u51f6\u624b\u3000\u5c11\u4fa0\u4f55\u582a\u89c1\u8840\u507f \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u56db\u5341\u4e94\u56de\u3000\u7fa4\u96c4\u6267\u610f\u8ffd\u51f6\u624b\u3000\u5c11\u4fa0\u4f55\u582a\u89c1\u8840\u507f \u3000\u3000\u5168\u7956\u5fb7\u8bf4\u8fc7\uff0c\u7fa4\u96c4\u90fd\u9053\u6709\u7406\uff0c\u7eb7\u7eb7\u50ac\u4fc3\u6b27\u9633\u4f2f\u548c\u4ea4\u4eba\uff0c\u6709\u7684\u8fd8\u5728\u7b11\u9a82\u53f6\u51b2\u9704\uff0c\u8bf4\u4ed6 \u65e2\u6709\u80c6\u91cf\u95ef\u7978\uff0c\u4e8b\u5230\u4e34\u5934\uff0c\u5374\u53c8\u4e0d\u6562\u51fa\u6765\u89c1\u4eba\uff0c\u6ca1\u6709\u4e00\u70b9\u82f1\u96c4\u672c\u8272\uff0c\u573a\u4e2d\u95f9\u6210\u4e00\u7247\uff0c\u6c5f \u6d77\u5929\u542c\u4e86\u4e5f\u89c9\u96be\u8fc7\u3002 \u3000\u3000\u6b27\u9633\u4f2f\u548c\u4ecd\u662f\u4e00\u526f\u51b7\u6f20\u7684\u795e\u6c14\uff0c\u6b27\u9633\u4ef2\u548c\u5374\u662f\u9762\u8272\u94c1\u9752\uff0c\u5ffd\u5730\u7ad9\u51fa\u6765\u8bf4\u9053\uff1a\u201c\u5168\u5e2e \u4e3b\uff0c\u4f60\u662f\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d\u77e5\u5176\u4e8c\u3002\u4e0d\u9519\uff0c\u6b27\u9633\u5a49\u662f\u6211\u7684\u5973\u513f\uff0c\u5979\u4e0e\u53f6\u51b2\u9704\u4e5f\u5df2\u6210\u4e86\u4eb2\u3002\u4f46 \u6211\u5e76\u4e0d\u540c\u610f\u8fd9\u95e8\u5a5a\u4e8b\uff0c\u6211\u5df2\u628a\u4ed6\u4eec\u8d76\u51fa\u53bb\u4e86\u3002\u6211\u4e0e\u53f6\u51b2\u9704\u7fc1\u5a7f\u4e4b\u60c5\u5df2\u65ad\uff0c\u4ed6\u7684\u4e8b\u60c5\u4e0e\u6211 \u4e00\u6982\u65e0\u5173\u3002\u201d \u3000\u3000\u6b27\u9633\u4ef2\u548c\u7684\u8bdd\u6c5f\u6d77\u5929\u5012\u662f\u6709\u51e0\u5206\u76f8\u4fe1\uff0c\u4f46\u7fa4\u96c4\u5374\u54ea\u80af\u4fe1\u4ed6\uff1f \u3000\u3000\u6b27\u9633\u4f2f\u548c\u8fd9\u4e00\u5bb6\u4eba\u5e73\u7d20\u51f6\u6a2a\u9738\u9053\uff0c\u5728\u6c5f\u6e56\u4e0a\u5230\u5904\u6811\u654c\uff0c\u4eca\u65e5\u5728\u573a\u7684\u5c31\u6709\u5f88\u591a\u662f\u4ed6\u4eec \u7684\u4ec7\u4eba\uff0c\u5f53\u4e0b\uff0c\u4e00\u9f50\u8d77\u54c4\uff0c\u201c\u54fc\uff0c\u4f60\u8fd9\u5206\u660e\u662f\u5047\u6487\u6e05\uff01\u201d\u201c\u5206\u660e\u662f\u6015\u53d7\u7275\u8fde\uff0c\u65e2\u60f3\u5e87\u62a4 \u5973\u5a7f\uff0c\u5374\u53c8\u4e0d\u6562\u62c5\u5f53\uff01\u201d\u5632\u7b11\u58f0\u8fb1\u9a82\u58f0\u6b64\u8d77\u5f7c\u843d\uff01 \u3000\u3000\u6b27\u9633\u4f2f\u548c\u52c3\u7136\u5927\u6012\uff0c\u5ffd\u5730\u4e00\u58f0\u957f\u5578\uff0c\u5c06\u90a3\u4e9b\u55a7\u95f9\u7684\u58f0\u97f3\u538b\u4e86\u4e0b\u53bb\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211 \u5f1f\u5f1f\u8bf4\u7684\u4e43\u662f\u5b9e\u8bdd\uff0c\u4f60\u4eec\u504f\u504f\u4e0d\u4fe1\u3002\u597d\u5427\uff0c\u514d\u5f97\u7ed9\u4f60\u4eec\u8bf4\u6211\u6015\u4e8b\uff0c\u54ea\u4e00\u4f4d\u8981\u53f6\u51b2\u9704\u7684\u5c3d \u7ba1\u51b2\u7740\u6211\u6765\uff01\u201d \u3000\u3000\u9633\u8d64\u7b26\u4e5f\u7eb5\u58f0\u5927\u7b11\uff0c\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6c5f\u6e56\u4e0a\u80dc\u8005\u4e3a\u5f3a\uff0c\u672c\u6765\u518d\u6ca1\u6709\u4ec0\u4e48\u9053\u7406\u53ef \u8bb2\uff01\u4f60\u4eec\u8fd9\u73ed\u4eba\u81ea\u547d\u82f1\u96c4\u8c6a\u6770\uff0c\u54fc\uff0c\u4f9d\u6211\u770b\u6765\uff0c\u4e0d\u8fc7\u662f\u6043\u591a\u4e3a\u80dc\uff0c\u4ed7\u52bf\u6b3a\u4eba\u800c\u5df2\uff01\u5f53\u5e74 \u4f60\u4eec\u56f4\u653b\u6211\u7684\u5e08\u5144\uff0c\u4eca\u65e5\u53c8\u6765\u6b3a\u538b\u6b27\u9633\u5e84\u4e3b\uff0c\u6211\u7b2c\u4e00\u4e2a\u5148\u770b\u4e0d\u8fc7\u773c\uff0c\u6211\u5012\u8981\u4f1a\u4f1a\u4f60\u4eec\u8fd9 \u73ed\u82f1\u96c4\u3002\u201d \u3000\u3000\u539f\u6765\u9633\u8d64\u7b26\u5df2\u7ec3\u6210\u4e86\u7b2c\u4e5d\u91cd\u7684\u4fee\u7f57\u9634\u715e\u529f\uff0c\u4e1c\u5c71\u590d\u51fa\uff0c\u6709\u610f\u5728\u8fd9\u6b21\u82f1\u96c4\u4f1a\u4e2d\u663e\u663e\u672c \u9886\uff0c\u632b\u6298\u7fa4\u96c4\uff0c\u91cd\u9738\u6b66\u6797\uff0c\u7136\u540e\u518d\u4e0e\u6b27\u9633\u6015\u548c\u7b49\u8054\u5408\uff0c\u53bb\u627e\u5510\u6653\u6f9c\u7b97\u5e10\u3002 \u3000\u3000\u9633\u8d64\u7b26\u8fd9\u756a\u8bdd\u76f4\u622a\u5411\u7fa4\u96c4\u6311\u6218\uff0c\u8bf4\u5f97\u6bd4\u6b27\u9633\u4f2f\u548c\u66f4\u4e3a\u51f6\u72e0\uff0c\u66f4\u4e3a\u96be\u542c\uff0c\u8eab\u4e3a\u4e00\u65b9\u4e3b \u4eba\u7684\u4e91\u53ec\u600e\u53d7\u5f97\u4e86\uff0c\u5f53\u4e0b\u4fbf\u4e5f\u7ad9\u4e86\u8d77\u6765\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u4f60\u4eec\u5230\u7684\u4eba\u4e5f\u5f88\u4e0d\u5c11\uff0c\u8bf4\u4e0d \u4e0a\u662f\u8c01\u6043\u591a\u4e3a\u80dc\u3002 \u3000\u3000\u5f53\u5e74\u4ee4\u5e08\u5144\u7ea0\u96c6\u5996\u90aa\uff0c\u4e3a\u5bb3\u6b66\u6797\uff0c\u8eab\u8d25\u540d\u88c2\uff0c\u90a3\u5b9e\u5728\u4e5f\u602a\u4e0d\u4e86\u8c01\uff01\u8001\u673d\u5f53\u5e74\u672a\u66fe\u53c2 \u4e0e\u5343\u969c\u576a\u4e4b\u4f1a\u3002\u672a\u66fe\u5f97\u89c1\u4ee4\u5e08\u5144\u7684\u7edd\u4e16\u795e\u529f\uff0c\u5982\u4eca\u72b9\u6709\u9057\u61be\u3002\u597d\u5728\u4ed6\u7684\u4fee\u7f57\u9634\u715e\u529f\u5c1a\u672a \u5931\u4f20\uff0c\u5c31\u8bf7\u9633\u5148\u751f\u4f60\u6307\u6559\u6307\u6559\u5427\uff01\u201d \u3000\u3000\u4e91\u53ec\u4ee5\u7259\u8fd8\u7259\uff0c\u76f4\u63a5\u6307\u660e\u5411\u9633\u8d64\u7b26\u6311\u6218\uff0c\u9633\u8d64\u7b26\u50b2\u7136\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e91\u8001\u82f1\u96c4\u80af\u8d50\u6559\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\uff01\u201d\u6b63\u8981\u4e0b\u573a\uff0c\u5ffd\u5730\u6709\u4e2a\u6c49\u5b50\u62a2\u5728\u524d\u5934\uff0c\u8bf4\u9053\uff1a \u201c\u8fd9\u8001\u5339\u592b\u53e3\u51fa\u5927\u8a00\uff0c\u4f8d\u6211\u5148\u6765\u4f1a\u4ed6\u3002\u9633\u5148\u751f\uff0c\u4f60\u548c\u6b27\u9633\u5e84\u4e3b\u662f\u54b1\u4eec\u7684\u4e3b\u5c06\uff0c\u54ea\u6709\u4e3b\u5c06 \u5148\u51fa\u573a\u7684\u9053\u7406\uff01\u4f60\u4e5f\u8be5\u8ba9\u6211\u4eec\u8fd9\u4e9b\u52a9\u62f3\u7684\u670b\u53cb\u5c3d\u70b9\u5fc3\u610f\u624d\u5bf9\u3002\u201d\u4ed6\u4e00\u9762\u8bf4\u4e00\u9762\u5df2\u8d70\u5230\u573a \u5fc3\uff0c\u4f17\u4eba\u770b\u65f6\uff0c\u8ba4\u5f97\u662f\u6c5f\u6e56\u4e0a\u7684\u72ec\u811a\u5927\u76d7\u8d5b\u4ec1\u8d35\u82cf\u6e5b\u3002 \u3000\u3000\u4e91\u53ec\u7aef\u5750\u4e3b\u4f4d\uff0c\u6b63\u773c\u513f\u4e5f\u4e0d\u77a7\u4ed6\u4e00\u773c\uff0c\u82cf\u6e5b\u72ec\u81ea\u4e00\u4eba\uff0c\u7ad9\u5728\u573a\u4e2d\uff0c\u751a\u662f\u5c34\u5c2c\uff0c\u4e91\u743c \u8d70\u51fa\u573a\u6765\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u6562\u5411\u6211\u7239\u7239\u6311\u6218\uff1f\u4e3a\u4e86\u5154\u4f60\u96be\u4ee5\u843d\u53f0\uff0c\u6211\u672a\u966a \u4f60\u8d70\u4e24\u62db\u5427\uff01\u201d \u3000\u3000\u82cf\u6e5b\u5927\u6012\u9053\uff1a\u201c\u9ec4\u53e3\u5c0f\u513f\uff0c\u4e73\u81ed\u672a\u5e72\uff0c\u6562\u51fa\u72c2\u8a00\uff0c\u770b\u621f\uff01\u201d\u4e91\u743c\u63d0\u8d77\u91d1\u5200\uff0c\u4e00\u62db \u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\uff0c\u6a2a\u5288\u51fa\u53bb\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u9707\u8033\u6b32\u804b\uff0c\u4e24\u4eba\u7684\u864e\u53e3\u90fd\u9707\u5f97\u9690\u9690\u4f5c \u75db\u3002 \u3000\u3000\u82cf\u6e5b\u7ef0\u53f7\u201c\u8d5b\u4ec1\u8d35\u201d\uff0c\u621f\u6cd5\u679c\u7136\u6709\u72ec\u5230\u4e4b\u5904\uff0c\u4ed6\u63a5\u4e86\u4e00\u62db\uff0c\u8bd5\u51fa\u4e91\u743c\u529f\u529b\u5728\u4ed6\u4e4b\u4e0a\uff0c \u5fc3\u5934\u4e00\u51db\uff0c\u7acb\u5373\u53d8\u62db\uff0c\u957f\u621f\u6325\u4e86\u4e00\u4e2a\u5706\u5f27\uff0c\u84e6\u5730\u4e00\u62db\u201c\u674e\u5e7f\u5c04\u77f3\u201d\u75be\u523a\u51fa\u53bb\uff0c\u4e91\u743c\u6a2a\u5200 \u4e00\u5c01\uff0c\u54ea\u77e5\u4ed6\u7684\u957f\u621f\u4e00\u6c89\uff0c\u5df2\u662f\u5377\u5730\u626b\u6765\uff0c\u4e91\u743c\u7eb5\u8df3\u907f\u8fc7\uff0c\u91d1\u5200\u5728\u4ed6\u7684\u621f\u8eab\u4e0a\u53c8\u65ab\u4e86\u4e00 \u4e0b\u3002 \u3000\u3000\u4e24\u4eba\u5200\u6765\u621f\u5f80\uff0c\u53ae\u6740\u8d77\u6765\uff0c\u4f46\u82cf\u6e5b\u621f\u957f\uff0c\u4e91\u743c\u5200\u77ed\uff0c\u5728\u5175\u5668\u4e0a\u5374\u662f\u82cf\u6e5b\u5360\u4e86\u4fbf\u5b9c\u3002 \u90a3\u82cf\u6e5b\u4e5f\u786e\u662f\u4e86\u5f97\uff0c\u4e00\u67c4\u4e08\u591a\u957f\u7684\u65b9\u5929\u753b\u621f\uff0c\u5728\u4ed6\u7684\u624b\u4e2d\u821e\u52a8\u8d77\u6765\uff0c\u5c31\u4f3c\u4e00\u6839\u706f\u8349\uff0c\u6307 \u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u5982\u81c2\u4f7f\u6307\uff0c\u8fd0\u7528\u5f97\u7eaf\u719f\u4e4b\u6781\uff0c\u4e1d\u6beb\u4e5f\u4e0d\u7740\u529b\u3002 \u3000\u3000\u4e91\u743c\u7684\u5bb6\u4f20\u5200\u6cd5\u672c\u6765\u4e5f\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u4f46\u4e00\u6765\u4ed6\u5e74\u7eaa\u592a\u8f7b\uff0c\u7ecf\u9a8c\u6b20\u7f3a\uff1b\u4e8c\u6765\u4ed6\u81ea\u5e7c\u52e4 \u4e8e\u7ec3\u4e60\u5927\u529b\u91d1\u521a\u638c\uff0c\u5728\u5200\u6cd5\u4e0a\u5374\u6ca1\u6709\u8fd9\u4e48\u6ce8\u91cd\uff0c\u56e0\u800c\u5728\u5175\u5203\u7684\u8f83\u91cf\u4e0a\uff0c\u78b0\u4e0a\u4e86\u8fd9\u4e48\u4e00\u4e2a \u7ecf\u9a8c\u4e30\u5bcc\u7684\u6c5f\u7322\u5de8\u76d7\uff0c\u5c31\u96be\u514d\u8981\u7a0d\u7a0d\u5403\u4e8f\u3002 \u3000\u3000\u6218\u5230\u5206\u9645\uff0c\u82cf\u6e5b\u84e6\u5730\u4e00\u58f0\u559d\u9053\uff1a\u201c\u64a4\u5200\uff01\u201d\u4e91\u743c\u4e00\u5200\u5288\u53bb\uff0c\u4ed6\u7684\u753b\u621f\u53cd\u5f39\u8d77\u6765\uff0c\u621f \u5c16\u5df2\u662f\u6307\u5230\u4e86\u4e91\u743c\u7684\u864e\u53e3\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u5577\u201d\u58f0\u54cd\uff0c\u4e91\u743c\u7684\u91d1\u5200\u679c\u7136\u8131\u624b\u5815\u5730\u3002 \u3000\u3000\u6b27\u9633\u4f2f\u548c\u8fd9\u8fb9\u7684\u4eba\u89c1\u82cf\u6e5b\u65d7\u5f00\u5f97\u8138\uff0c\u90fd\u5927\u559c\u559d\u5f69\uff0c\u54ea\u77e5\u5f69\u58f0\u65b9\u8d77\uff0c\u53ea\u542c\u5f97\u4e91\u743c\u4e5f\u662f \u4e00\u58f0\u5927\u559d\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u5df2\u662f\u7262\u7262\u5730\u6293\u7740\u82cf\u6e5b\u7684\u753b\u621f\u3002\u90a3\u621f\u5c16\u76f8\u5dee\u4e0d\u5230\u534a\u5bf8\u4fbf\u53ef\u523a\u4e2d\u4ed6\u7684 \u864e\u53e3\uff0c\u5374\u5c31\u662f\u523a\u4e0d\u8fc7\u53bb\uff0c\u4e5f\u6536\u4e0d\u56de\u6765\u3002 \u3000\u3000\u82cf\u6e5b\u6c14\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u6253\u6cd5\uff1f\u4f60\u660e\u660e\u8f93\u4e86\uff0c\u60f3\u6492\u8d56\u4e48\uff1f\u201d\u4e91\u743c\u51b7 \u7b11\u9053\uff1a\u201c\u6211\u7528\u7a7a\u624b\u6253\u8d25\u4f60\u624d\u89c1\u529f\u592b\uff0c\u6bd4\u6b66\u8981\u6253\u5012\u5bf9\u65b9\u624d\u7b97\u5f97\u80dc\uff0c\u4f60\u5f53\u6211\u4e0d\u8bc6\u89c4\u77e9\u4e48\uff1f\u201d \u82cf\u6e5b\u7ed9\u4ed6\u9a73\u5f97\u54d1\u53e3\u65e0\u8a00\uff0c\u53c8\u4e0d\u60f3\u7ed9\u4ed6\u593a\u53bb\u753b\u621f\u3002\u53ea\u597d\u82e6\u82e6\u6491\u6301\u3002 \u3000\u3000\u4e91\u743c\u8fd0\u8db3\u6c14\u529b\uff0c\u84e6\u5730\u53c8\u662f\u4e00\u58f0\u559d\u9053\uff1a\u201c\u64a4\u624b\uff01\u201d\u5728\u4e24\u80a1\u5927\u529b\u4e89\u593a\u4e4b\u4e0b\uff0c\u90a3\u753b\u621f\u201c\u556a\u201d \u7684\u4e00\u58f0\u65ad\u4e86\uff0c\u4e91\u743c\u51b2\u8fc7\u53bb\u4e00\u638c\u62cd\u51fa\uff0c\u4e91\u5bb6\u7684\u5927\u529b\u91d1\u521a\u638c\u5929\u4e0b\u65e0\u5bf9\uff0c\u82cf\u6e5b\u54ea\u91cc\u62b5\u6321\u5f97\u4f4f\uff0c \u53ea\u4e00\u638c\u5c31\u7ed9\u4e91\u743c\u5c06\u4ed6\u7684\u6574\u4e2a\u8eab\u5b50\u6253\u5f97\u98de\u4e86\u8d77\u6765\uff0c\u4ed6\u8fd9\u8fb9\u7684\u9752\u6d77\u4e09\u9a6c\u614c\u5fd9\u8dd1\u51fa\u573a\u6765\uff0c\u624b\u7275 \u7740\u624b\uff0c\u5f20\u6210\u7f51\u72b6\uff0c\u5c06\u4ed6\u63a5\u4e0b\u3002\u82cf\u6e5b\u5e78\u672a\u6454\u6b7b\uff0c\u4f46\u4e5f\u5df2\u53d7\u4e86\u91cd\u4f24\uff0c\u9762\u5982\u91d1\u7eb8\u4e86\u3002 \u3000\u3000\u8fd9\u201c\u9752\u6d77\u4e09\u9a6c\u201d\u4e43\u662f\u4e00\u6bcd\u6240\u751f\u7684\u540c\u80de\u5144\u5f1f\uff0c\u5927\u54e5\u9a6c\u826f\uff0c\u4e8c\u54e5\u9a6c\u9a8f\uff0c\u4e09\u5f1f\u9a6c\u9a70\uff0c\u6bcf\u4eba \u76f8\u5dee\u6070\u597d\u4e00\u5c81\uff0c\u81ea\u5e7c\u4e00\u540c\u7ec3\u6b66\uff0c\u7ec3\u6210\u4e86\u4e00\u5957\u4e09\u624d\u5251\u6cd5\uff0c\u6bcf\u9022\u51fa\u9635\uff0c\u4e0d\u8bba\u5bf9\u65b9\u4eba\u6570\u591a\u5be1\uff0c \u603b\u662f\u4e09\u4eba\u540c\u4e0a\u3002\u4ed6\u4eec\u628a\u82cf\u6e5b\u6551\u4e86\u4e0b\u6765\uff0c\u4fbf\u5411\u4e91\u53ec\u8fd9\u8fb9\u6311\u6218\u3002 \u3000\u3000\u4e91\u53ec\u77e5\u9053\u4ed6\u4eec\u5144\u5f1f\u5bf9\u654c\u7684\u89c4\u77e9\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u9752\u6d77\u4e09\u9a6c\u7684\u4e09\u624d\u5251\u9635\u975e\u540c\u5c0f\u53ef\uff0c\u6211\u65b9 \u867d\u7136\u5c3d\u6709\u6bd4\u4ed6\u4eec\u9ad8\u660e\u7684\u4eba\uff0c\u4f46\u8981\u9009\u51fa\u4e09\u4e2a\u50cf\u4ed6\u4eec\u4e00\u6837\u914d\u5408\u6709\u7d20\u7684\u4eba\uff0c\u90a3\u5374\u96be\u4e86\u3002\u201d\u6b63\u5728 \u8e0c\u8e87\uff0c\u53ea\u542c\u5f97\u97e9\u7487\u5df2\u5728\u8bf4\u9053\uff1a\u201c\u8001\u4f34\u513f\uff0c\u4eba\u5bb6\u662f\u4e0a\u9635\u4e0d\u79bb\u4eb2\u5144\u5f1f\uff0c\u54b1\u4eec\u4e5f\u6765\u4e2a\u592b\u5531\u5987\u968f\u3002\u201d \u3000\u3000\u4e91\u53ec\u5fc3\u91cc\u6697\u6697\u597d\u7b11\uff1a\u201c\u6211\u60f3\u6765\u60f3\u53bb\uff0c\u771f\u662f\u7cca\u6d82\uff0c\u65e9\u5c31\u5e94\u8be5\u60f3\u5230\u4ed6\u4eec\u4e24\u592b\u59bb\u4e86\u3002\u201d\u7b11 \u9053\uff1a\u201c\u5bf9\uff0c\u4e8c\u54e5\u4e8c\u5ac2\u597d\u4e2a\u592b\u5531\u5987\u968f\uff01\u201d\u97e9\u4e8c\u5a18\u201c\u5550\u201d\u4e86\u4e00\u53e3\u9053\uff1a\u201c\u4ec0\u4e48\u592b\u5531\u5987\u968f\uff0c\u4ed6\u8981 \u6253\u67b6\u7684\u65f6\u5019\u624d\u60f3\u8d77\u8981\u627e\u6211\u3002\u201d\u4f46\u6bd5\u7adf\u8fd8\u662f\u548c\u4e08\u592b\u4e00\u9053\u51fa\u573a\u3002 \u3000\u3000\u97e9\u7487\u662f\u9547\u8fdc\u9556\u5c40\u7684\u603b\u9556\u5934\uff0c\u5a01\u540d\u7d20\u8457\uff0c\u4ed6\u7684\u59bb\u5b50\u4e5f\u4ee5\u201c\u94c1\u9e33\u9e2f\u201d\u9a70\u8a89\u6c5f\u6e56\u3002\u201c\u9752\u6d77 \u4e09\u9a6c\u201d\u89c1\u662f\u4ed6\u4eec\u592b\u59bb\u51fa\u9635\uff0c\u4e5f\u4e0d\u7981\u5fc3\u4e2d\u5fae\u51db\uff0c\u4f46\u4ed6\u4eec\u7d20\u6765\u81ea\u8d1f\uff0c\u968f\u5373\u60f3\u9053\uff1a\u201c\u542c\u8bf4\u8fd9\u4e24 \u592b\u59bb\u8fde\u53f6\u51b2\u9704\u90a3\u5c0f\u5b50\u4e5f\u6253\u4e0d\u8fc7\uff0c\u53ea\u6015\u662f\u6d6a\u5f97\u865a\u540d\u3002\u201d \u3000\u3000\u9a6c\u826f\u5e26\u5934\uff0c\u629a\u5251\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u5f97\u97e9\u603b\u9556\u5934\u4f09\u4fea\u8d50\u62db\uff0c\u4f55\u5e78\u5982\u4e4b\uff01\u201d\u97e9\u4e8c\u5a18\u51b7\u51b7\u8bf4 \u9053\uff1a\u201c\u6211\u4eec\u7684\u9547\u8fdc\u9556\u5c40\u65e9\u5173\u95e8\u4e86\uff0c\u4f60\u522b\u7b11\u8bdd\u6211\u8fd9\u8001\u4f34\u513f\u5566\u3002\u5e26\u523a\u7684\u8bdd\u513f\u522b\u591a\u8bf4\u4e86\uff0c\u52a8\u624b \u5427\uff01\u201d\u9a6c\u826f\u8d54\u7b11\u9053\uff1a \u3000\u3000\u201c\u97e9\u592b\u4eba\u8bef\u4f1a\u4e86\u3002\u201d\u4ed6\u7684\u5f1f\u5f1f\u9a6c\u9a70\u706b\u6c14\u6700\u5927\uff0c\u5374\u5fcd\u4e0d\u7740\u9a82\u9053\uff1a\u201c\u4f60\u7684\u9556\u5c40\u592b\u95e8\uff0c\u8981 \u627e\u6211\u4eec\u51fa\u6c14\u5417\uff1f\u770b\u5251\uff01\u201d\u5530\u7684\u4e00\u5251\uff0c\u5c31\u5411\u97e9\u4e8c\u5a18\u523a\u53bb\u3002 \u3000\u3000\u97e9\u4e8c\u5a18\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u53ea\u602a\u6211\u4eec\u672c\u9886\u4e0d\u6d4e\uff0c\u4f46\u6211\u4eec\u4e0d\u80fd\u4f0f\u864e\uff0c\u9a71\u725b\u8d76\u9a6c\u5927\u7ea6\u603b\u8fd8\u80fd \u591f\u3002\u201d\u5979\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u5e95\u4e1d\u6beb\u4e0d\u7f13\uff0c\u94c1\u62d0\u5728\u5730\u4e0a\u4e00\u70b9\uff0c\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u5df2\u5230\u4e86\u9a6c\u9a70\u8eab\u4fa7\uff0c \u907f\u5251\u8fd8\u5200\uff0c\u4e00\u62db\u201c\u795e\u9f99\u6389\u9996\u201d\uff0c\u5200\u5149\u5982\u96ea\uff0c\u5df2\u5288\u5230\u4ed6\u7684\u80c1\u4e0b\u3002 \u3000\u3000\u84e6\u542c\u5f97\u91d1\u5203\u5288\u98ce\uff0c\u9a6c\u826f\u3001\u9a6c\u9a8f\u4e24\u67c4\u957f\u5251\u540c\u65f6\u4ece\u4e24\u4fa7\u653b\u6765\uff0c\u97e9\u7487\u53eb\u9053\uff1a\u201c\u8001\u4f34\u513f\uff0c\u5c0f \u5fc3\u4e86\uff01\u201d\u4e00\u5200\u67b6\u5f00\u4e86\u9a6c\u826f\u7684\u957f\u5251\uff0c\u97e9\u4e8c\u5a18\u53cd\u624b\u4e00\u62db\u201c\u82cf\u79e6\u80cc\u5370\u201d\uff0c\u4e5f\u628a\u9a6c\u9a8f\u7684\u5175\u5203\u8361\u5f00\uff0c \u5200\u950b\u5728\u53cd\u624b\u5288\u51fa\u4e4b\u65f6\uff0c\u500f\u5730\u5212\u8fc7\uff0c\u628a\u9a6c\u9a70\u7684\u8863\u8896\u524a\u53bb\u4e86\u4e00\u622a\u3002\u8fd9\u8fd8\u662f\u56e0\u4e3a\u5979\u8981\u5bf9\u4ed8\u9a6c\u9a8f\uff0c \u8981\u4e0d\u7136\u9a6c\u9a70\u7684\u80c1\u4e0b\u53ea\u6015\u8981\u88ab\u5979\u6233\u4e2a\u900f\u660e\u7a9f\u7abf\u3002 \u3000\u3000\u9a6c\u9a70\u5413\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u8fd9\u624d\u77e5\u9053\u97e9\u7487\u592b\u59bb\u540d\u4e0b\u65e0\u865a\uff0c\u54ea\u91cc\u8fd8\u6562\u8f7b\u654c\uff1f\u4ed6\u4eec\u4e09\u5144\u5f1f\u5355 \u6253\u72ec\u6597\u7684\u529f\u592b\u5e76\u4e0d\u600e\u6837\u9ad8\u5f3a\uff0c\u4f46\u4e09\u4eba\u8054\u624b\uff0c\u65bd\u5c55\u5f00\u90a3\u5957\u201c\u4e09\u624d\u5251\u6cd5\u201d\uff0c\u5374\u662f\u5de7\u5999\u975e\u5e38\u3002 \u53ea\u89c1\u4ed6\u4eec\u4e92\u6210\u7284\u89d2\u4e4b\u52bf\uff0c\u4e09\u67c4\u957f\u5251\u4ea4\u68ad\u7a7f\u63d2\uff0c\u540c\u8fdb\u540c\u9000\uff0c\u914d\u5408\u5f97\u5999\u5230\u6beb\u5dc5\u3002\u4e0d\u4f46\u95e8\u6237\u5c01 \u95ed\u8c28\u4e25\uff0c\u653b\u52bf\u4e5f\u6781\u4e4b\u51cc\u5389\u3002\u97e9\u7487\u592b\u5987\u80cc\u9760\u7740\u80cc\uff0c\u4e00\u67c4\u957f\u5200\u4e00\u67c4\u77ed\u5200\uff0c\u7eb5\u6a2a\u6325\u970d\uff0c\u5374\u4e5f\u5e94 \u4ed8\u5f97\u7ef0\u7ef0\u6709\u4f59\u3002 \u3000\u3000\u53cc\u65b9\u6597\u4e86\u6570\u5341\u56de\u5408\uff0c\u5140\u81ea\u4e0d\u5206\u9ad8\u4e0b\u3002\u97e9\u7487\u8fd9\u8fb9\u5360\u4e86\u516d\u6210\u653b\u52bf\uff0c\u4f46\u97e9\u4e8c\u5a18\u8ddb\u4e86\u4e00\u8db3\uff0c \u8df3\u8dc3\u4e0d\u7075\uff0c\u8981\u4f9d\u9760\u62d0\u6756\u652f\u6491\uff0c\u4fbf\u4e0d\u80fd\u653e\u624b\u653b\u51fb\uff0c\u56e0\u6b64\u867d\u7136\u7565\u5360\u4f18\u52bf\uff0c\u53d6\u80dc\u5374\u96be\u3002 \u3000\u3000\u201c\u9752\u6d77\u4e09\u9a6c\u201d\u7684\u8001\u5927\u9a6c\u826f\uff0c\u6b66\u529f\u6700\u9ad8\uff0c\u4e5f\u6700\u9634\u6c89\uff0c\u770b\u51fa\u7834\u7efd\uff0c\u84e6\u5730\u4e00\u58f0\u53e3\u54e8\uff0c\u6253\u51fa \u6697\u53f7\uff0c\u9a6c\u9a8f\u3001\u9a6c\u9a70\u4e24\u8fb9\u653b\u6765\uff0c\u4ed6\u5219\u4ece\u5f53\u4e2d\u4e00\u5251\u5288\u4e0b\uff01\u9a6c\u9a70\u67b6\u4f4f\u97e9\u7487\u7684\u957f\u5200\uff0c\u9a6c\u9a8f\u67b6\u4f4f\u97e9 \u4e8c\u5a18\u7684\u77ed\u5200\uff0c\u4ed6\u4eec\u4e8c\u4eba\u7684\u672c\u9886\u4ee5\u4e00\u654c\u4e00\uff0c\u867d\u7136\u4e0d\u53ca\u97e9\u65cb\u592b\u5987\uff0c\u4f46\u62fc\u4e86\u5168\u529b\uff0c\u62b5\u6321\u4e00\u62db\uff0c \u5374\u8fd8\u662f\u62b5\u6321\u5f97\u4f4f\uff0c\u9a6c\u826f\u5c31\u662f\u8981\u4e89\u8fd9\u4e00\u62db\u7684\u65f6\u95f4\uff0c\u4e58\u865a\u800c\u5165\uff0c\u81f3\u5c11\u4e5f\u8981\u6253\u4e71\u4ed6\u4eec\u592b\u5987\u7684\u9635 \u811a\uff0c\u8feb\u5f97\u4ed6\u4eec\u5404\u81ea\u4e3a\u6218\u3002 \u3000\u3000\u9a6c\u826f\u8fd9\u4e00\u5251\u5f53\u4e2d\u5288\u4e0b\uff0c\u97e9\u7487\u592b\u5987\u62bd\u4e0d\u51fa\u5175\u5203\u62db\u67b6\u3002\u679c\u7136\u5982\u4ed6\u6240\u6599\uff0c\u53ea\u597d\u659c\u8dc3\u907f\u5f00\u3002 \u97e9\u4e8c\u5a18\u672c\u6765\u4e0e\u4e08\u592b\u80cc\u9760\u7740\u80cc\uff0c\u9760\u7740\u4e08\u592b\u7684\u63a9\u62a4\uff0c\u8fd9\u624d\u80fd\u53d1\u6325\u653b\u52bf\u7684\uff0c\u4e00\u65e6\u4e0e\u4e08\u592b\u5206\u5f00\uff0c \u540e\u5fc3\u9732\u51fa\u7834\u7efd\uff0c\u5979\u8df3\u8dc3\u4e0d\u7075\uff0c\u8fd9\u5c31\u7ed9\u654c\u4eba\u4ee5\u5927\u597d\u7684\u653b\u51fb\u673a\u4f1a\u4e86\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u9a6c\u826f \u4e00\u58f0\u5927\u559d\uff0c\u4e09\u5144\u5f1f\u5168\u90fd\u5411\u7740\u97e9\u4e8c\u5a18\u653b\u6765\uff0c\u9a6c\u9a8f\u3001\u9a6c\u9a70\u53cc\u5251\u653b\u51fb\u5979\u7684\u4e24\u80c1\uff0c\u9a6c\u826f\u7684\u957f\u5251\u5219 \u75be\u523a\u5979\u7684\u80cc\u5fc3\u5927\u7a74\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u97e9\u4e8c\u5a18\u94c1\u62d0\u4e00\u6254\uff0c\u78b0\u5f00\u4e86\u9a6c\u9a8f\u3001\u9a6c\u9a70\u7684\u5175\u5203\uff0c\u4f46\u5979\u5931\u53bb\u62d0\u6756\uff0c \u4e5f\u7acb\u5373\u8dcc\u5012\u5730\u4e0a\u3002\u9a6c\u826f\u5927\u559c\uff0c\u4e00\u5251\u671d\u5979\u540e\u5fc3\u523a\u4e0b\uff0c\u7fa4\u96c4\u5927\u60ca\u5931\u8272\uff0c\u53ea\u9053\u97e9\u4e8c\u5a18\u6027\u547d\u96be\u4fdd\u3002 \u54ea\u77e5\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u5ffd\u542c\u5f97\u51c4\u5389\u7684\u53eb\u58f0\u9a87\u4eba\u5fc3\u9b44\uff0c\u201c\u9752\u6d77\u4e09\u9a6c\u201d\u7adf\u7136\u540c\u65f6\u6454\u5728\u5730\u4e0a\uff0c \u53d8\u4e86\u4e09\u4e2a\u6eda\u5730\u846b\u82a6\uff0c\u8eab\u8fb9\u90fd\u662f\u4e00\u6ee9\u9c9c\u8840\u3002 \u3000\u3000\u539f\u6765\u97e9\u4e8c\u5a18\u662f\u5728\u6254\u4e0b\u62d0\u6756\u7684\u65f6\u5019\uff0c\u53d1\u51fa\u5979\u7684\u6210\u540d\u6697\u5668\u201c\u94c1\u9e33\u9e2f\u201d\uff0c\u9752\u6d77\u4e09\u9a6c\u672c\u6765\u4e5f \u77e5\u9053\u5979\u7684\u6697\u5668\u5389\u5bb3\uff0c\u4f46\u89c1\u5979\u4e00\u624b\u6301\u5200\uff0c\u4e00\u5343\u6491\u7740\u62d0\u6756\uff0c\u8c05\u5979\u817e\u4e0d\u51fa\u624b\u6765\uff0c\u800c\u4e14\u4ed6\u4eec\u592a\u8fc7 \u81ea\u4fe1\u4ed6\u4eec\u4e09\u624d\u5251\u9635\u7684\u5389\u5bb3\uff0c\u5c24\u5176\u5728\u97e9\u4e8c\u5a18\u5df2\u7ecf\u8dcc\u5012\u4e4b\u65f6\uff0c\u4ed6\u4eec\u5c31\u66f4\u758f\u4e8e\u9632\u5907\u4e86\u3002 \u3000\u3000\u54ea\u77e5\u97e9\u4e8c\u5a18\u624b\u6cd5\u5feb\u5982\u95ea\u7535\uff0c\u5979\u6454\u90a3\u4e00\u8de4\uff0c\u6b63\u662f\u8981\u4f7f\u654c\u4eba\u4e0a\u5f53\u7684\uff0c\u5979\u6254\u6756\uff0c\u6454\u5012\uff0c\u907f \u5251\uff0c\u8fd8\u51fb\uff0c\u4e00\u6c14\u5475\u6210\uff01\u4e09\u679a\u94c1\u9e33\u9e2f\u5206\u6253\u4e09\u4e2a\u4e0d\u540c\u7684\u65b9\u5411\uff0c\u6bcf\u4e00\u679a\u94c1\u9e33\u9e2f\u90fd\u6253\u4e2a\u6b63\u7740\uff0c\u524a \u53bb\u4e86\u654c\u4eba\u7684\u4e00\u8fb9\u819d\u76d6\uff0c\u5728\u573a\u8bf8\u4eba\u8fde\u770b\u90fd\u672a\u66fe\u770b\u5f97\u6e05\u695a\uff0c\u5f53\u771f\u662f\u8db3\u4ee5\u9707\u4e16\u9a87\u4fd7\u7684\u6697\u5668\u529f\u592b\uff01 \u3000\u3000\u97e9\u4e8c\u5a18\u62cd\u4e00\u62cd\u624b\uff0c\u7f13\u7f13\u5730\u722c\u4e86\u8d77\u6765\uff0c\u62fe\u8d77\u62d0\u6756\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u4eec\u6b3a\u8d1f\u4f60\u5976\u5976\u811a\u8dbe\uff0c\u6211\u5c31\u53eb\u4f60\u4eec\u4e5f\u5c1d\u5c1d\u80a2\u811a\u7684\u6ecb\u5473\uff0c\u770b\u4f60\u4eec\u4ee5\u540e\u8fd8\u6562\u4e0d\u6562\u803b\u7b11 \u6b8b\u5e9f\u4e4b\u4eba\uff01\u201d\u539f\u6765\u97e9\u4e8c\u5a18\u6700\u521d\u8fd8\u4e0d\u60f3\u4e0b\u8fd9\u6837\u8fa3\u624b\u7684\uff0c\u5f85\u5230\u654c\u4eba\u6b3a\u5979\u6b8b\u5e9f\uff0c\u4e58\u865a\u653b\u51fb\uff0c\u7adf \u8981\u53d6\u5979\u6027\u547d\u7684\u65f6\u5019\uff0c\u5979\u8fd9\u624d\u52a8\u4e86\u6012\u6c14\uff0c\u53d1\u51fa\u6697\u5668\u62a5\u590d\uff0c\u5c06\u4e09\u4e2a\u654c\u4eba\u7684\u819d\u76d6\u5168\u90fd\u524a\u4e86\u3002 \u3000\u3000\u7fa4\u96c4\u89c1\u97e9\u4e8c\u5a18\u6697\u5668\u5982\u6b64\u5389\u5bb3\uff0c\u90fd\u4e0d\u7981\u9a87\u7136\uff0c\u4f46\u5fc3\u91cc\u4e5f\u90fd\u662f\u60f3\u9053\uff1a\u201c\u5979\u5982\u6b64\u5389\u5bb3\uff0c\u4e0e \u4e08\u592b\u8054\u4e4e\uff0c\u4ecd\u7136\u8981\u88ab\u53f6\u51b2\u9704\u6253\u8ddb\u4e00\u817f\uff0c\u8fd9\u65f6\u51b2\u9704\u7684\u6b66\u529f\u5982\u4f55\uff0c\u4e5f\u5c31\u53ef\u4ee5\u60f3\u89c1\u4e86\u3002\u4f46\u53f6\u51b2 \u9704\u65e2\u6709\u8fd9\u6837\u9ad8\u5f3a\u7684\u672c\u9886\uff0c\u5374\u53c8\u4f55\u4ee5\u4e0b\u6562\u51fa\u6765\uff1f\u201d \u3000\u3000\u4f17\u4eba\u6b63\u5728\u8bae\u8bba\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u53eb\u9053\uff1a\u201c\u97e9\u603b\u9556\u5934\u6162\u8d70\uff0c\u6211\u6765\u4e5f\uff01\u201d\u58f0\u97f3\u5e76\u4e0d\u600e\u4e48\u54cd\u4eae\uff0c \u4f46\u5374\u9707\u5f97\u4f17\u4eba\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\u3002\u97e9\u7487\u5403\u4e86\u4e00\u60ca\uff0c\u56de\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u6765\u7684\u662f\u4e2a\u5934\u7f20\u767d\u5e03\u3001\u624b\u957f \u817f\u957f\u7684\u56de\u4eba\uff0c\u53cc\u76ee\u7cbe\u5149\u95ea\u95ea\uff0c\u62ff\u7740\u4e00\u6839\u4e5f\u662f\u5149\u95ea\u95ea\u7684\u602a\u68d2\uff0c\u4e00\u770b\u5c31\u77e5\u4ed6\u5185\u529f\u6df1\u539a\u975e\u5e38\u3002 \u97e9\u7487\u9053\uff1a\u201c\u9601\u4e0b\u6709\u4f55\u6307\u6559\uff1f\u201d \u3000\u3000\u90a3\u56de\u4eba\u6de1\u8c08\u8bf4\uff1a\u201c\u6211\u662f\u53f6\u51b2\u9704\u7684\u670b\u53cb\uff0c\u4ed6\u4eca\u65e5\u7f3a\u573a\uff0c\u6211\u7279\u6765\u66ff\u4ed6\u63a5\u8fd9\u573a\u5b50\uff0c\u5148\u8bf7\u97e9 \u603b\u9556\u5934\u4f09\u4fea\u6307\u6559\u3002\u201d\u7fa4\u8c6a\u5fc3\u9053\uff1a\u201c\u53f6\u51b2\u9704\u4e0d\u6765\uff0c\u4ed6\u7684\u670b\u53cb\u5374\u66ff\u4ed6\u51fa\u5934\u6765\u4e86\u3002\u5374\u4e0d\u77e5\u6b64\u4eba \u662f\u8c01\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u97e9\u73bb\u95ee\u9053\uff1a\u201c\u9601\u4e0b\u65e2\u66ff\u90a3\u59d3\u53f6\u7684\u51fa\u5934\uff0c\u611a\u592b\u5987\u6562\u4e0d\u5949\u966a\u3002\u8bf7\u95ee\u9601\u4e0b\u9ad8\u59d3\u5927\u540d\u3002\u201d \u90a3\u56de\u4eba\u50b2\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u672a\u5b66\u540e\u8fdb\u5929\u6c34\u91d1\u65e5\u5355\uff01\u201d \u3000\u3000\u91d1\u65e5\u5355\u4e00\u62a5\u59d3\u540d\uff0c\u6709\u8bc6\u5f97\u4ed6\u7684\u6765\u5386\u7684\u90fd\u662f\u5927\u5403\u4e00\u60ca\u3002\u539f\u6765\u8fd9\u91d1\u65e5\u5355\u4e43\u662f\u56de\u65cf\u7b2c\u4e00\u9ad8 \u624b\uff0c\u7d20\u6709\u201c\u5927\u6f20\u5947\u4eba\u201d\u4e4b\u79f0\uff0c\u5f53\u5e74\u4ed6\u4e5f\u66fe\u5e94\u5b5f\u795e\u901a\u4e4b\u7ea6\uff0c\u6765\u8d74\u5343\u969c\u576a\u4e4b\u4f1a\uff0c\u5728\u9014\u4e2d\u66fe\u4e0e \u5510\u7ecf\u5929\u6253\u4e86\u4e00\u67b6\uff0c\u5510\u7ecf\u5929\u8fd8\u7a0d\u7a0d\u5403\u4e86\u70b9\u4e8f\uff0c\u540e\u6765\u91d1\u4e16\u9057\u6697\u52a9\u6c5f\u5357\uff1a\u5c06\u4ed6\u6454\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c \u8fd9\u624d\u5c06\u4ed6\u5413\u8dd1\uff0c\u90a3\u6b21\u5343\u969c\u576a\u4e4b\u4f1a\uff0c\u4ed6\u4e5f\u5c31\u6ca1\u6709\u51fa\u573a\u4e86\uff0c\u56e0\u6b64\u8ba4\u8bc6\u4ed6\u7684\u4eba\u4e0d\u591a\uff0c\u4e0d\u8fc7\u4ed6\u7684 \u540d\u5934\uff0c\u573a\u4e2d\u8bf8\u4eba\uff0c\u5341\u4e4b\u516b\u4e5d\uff0c\u5374\u90fd\u662f\u77e5\u9053\u7684\u3002 \u3000\u3000\u91d1\u65e5\u5355\u8bdd\u4f3c\u8c26\u865a\uff0c\u5b9e\u5728\u5374\u662f\u50b2\u6162\u5f97\u7d27\uff0c\u4ed6\u4e00\u8bf4\u8981\u66ff\u53f6\u51b2\u9704\u63a5\u8fd9\u573a\u5b50\uff0c\u4e8c\u8bf4\u8981\u5148\u5411\u97e9 \u7487\u592b\u5987\u9886\u6559\uff0c\u8a00\u4e0b\u4e4b\u610f\uff0c\u7adf\u662f\u8981\u628a\u53f6\u51b2\u9704\u7684\u4e8b\u60c5\u63fd\u5230\u81ea\u5df1\u8eab\u4e0a\u3001\u8bf7\u53f6\u51b2\u9704\u7684\u4ec7\u4eba\u90fd\u51b2\u770b \u4ed6\u6765\uff0c\u4ed6\u8bf4\u201c\u5148\u201d\u5411\u97e9\u7487\u592b\u59bb\u9886\u6559\uff0c\u8fd9\u4e00\u4e2a\u201c\u5148\u201d\u5b57\u5c31\u663e\u5f97\u9a84\u50b2\u4e4b\u6781\uff0c\u8981\u77e5\u6709\u201c\u5148\u201d\u5fc5 \u6709\u201c\u540e\u201d\u4ed6\u82e5\u4e0d\u662f\u628a\u97e9\u7487\u592b\u59bb\u6253\u8d25\uff0c\u90a3\u8fd8\u80fd\u5411\u522b\u4eba\u201c\u9886\u6559\u201d\u5417\uff1f \u3000\u3000\u97e9\u7487\u592b\u59bb\u4e0d\u7531\u5f97\u6c14\u5f80\u4e0a\u51b2\uff0c\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u91d1\u5148\u751f\uff0c\u4e45\u95fb\u5927\u540d\uff0c\u5982\u96f7\u8d2f\u8033\uff0c\u4eca \u65e5\u4f55\u5e78\u76f8\u9022\uff0c\u611a\u592b\u5987\u6b63\u60f3\u89c1\u8bc6\u91d1\u5148\u751f\u5947\u4eba\u5947\u6280\u3002\u8bf7\u8d50\u62db\u5427\uff01\u201d\u4f3c\u97e9\u7487\u592b\u59bb\u7684\u8eab\u4efd\uff0c\u672c\u4e0d \u6b32\u4ee5\u4e8c\u654c\u4e00\uff0c\u5982\u4eca\u4ed6\u4eec\u7adf\u4e0d\u501f\u81ea\u8d2c\u8eab\u4efd\uff0c\u592b\u59bb\u540c\u65f6\u51fa\u573a\uff0c\u53ef\u89c1\u4ed6\u4eec\u5bf9\u91d1\u65e5\u5355\u4e5f\u751a\u4e3a\u5c0a\u91cd\uff0c \u751a\u4e3a\u5fcc\u60ee\u3002 \u3000\u3000\u91d1\u65e5\u5355\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u4f60\u4eec\u4e0e\u53f6\u51b2\u9704\u4e4b\u95f4\u7684\u662f\u975e\u66f2\u76f4\uff0c\u4f46\u542c\u97e9\u603b\u9556\u5934\u6240\u8bf4\uff0c\u4f3c\u4e4e\u662f\u53f6 \u51b2\u9704\u5148\u5bf9\u4f60\u4eec\u4e0d\u4f4f\u3002\u6211\u66ff\u670b\u53cb\u63a5\u573a\uff0c\u90a3\u662f\u4e3a\u4e86\u987e\u5168\u4e49\u6c14\u3002\u4f46\u4e5f\u8be5\u4e3a\u670b\u53cb\u7565\u8868\u6b49\u610f\uff0c\u65e0\u4ee5 \u4e3a\u656c\uff0c\u6211\u5148\u8ba9\u4f60\u4eec\u4e09\u62db\u5427\uff01\u201d\u8fd9\u8bdd\u8868\u9762\u8c26\u865a\uff0c\u9aa8\u5b50\u91cc\u66f4\u9a84\u50b2\u4e86\u3002\u97e9\u4e8c\u5a18\u6309\u637a\u4e0d\u4f4f\uff0c\u559d\u9053\uff1a \u201c\u597d\uff0c\u4f60\u65e2\u8981\u8ba9\uff0c\u90a3\u5c31\u8ba9\u5427\uff01\u522b\u7f57\u55e6\u4e86\uff01\u201d\u77ed\u5200\u4e00\u5212\uff0c\u4e00\u62db\u201c\u98ce\u51f0\u5c55\u7fc5\u201d\uff0c\u5df2\u662f\u75be\u524a\u51fa \u53bb\u3002 \u3000\u3000\u97e9\u7487\u592b\u59bb\u56e0\u4ed6\u662f\u66ff\u53f6\u51b2\u9704\u51fa\u5934\uff0c\u6bd4\u201c\u9752\u6d77\u4e09\u9a6c\u201d\u90a3\u573a\u5927\u5927\u4e0d\u540c\uff0c\u56e0\u6b64\u97e9\u4e8c\u5a18\u4e00\u51fa\u624b\uff0c \u4e5f\u5c31\u662f\u6027\u547d\u76f8\u640f\u7684\u6740\u624b\u7edd\u62db\uff0c\u90a3\u4e00\u5200\u201c\u51e4\u51f0\u5c55\u7fc5\u201d\u52bf\u6377\u529b\u6c89\uff0c\u6b3a\u8eab\u76f4\u8feb\uff0c\u7adf\u662f\u8981\u4e00\u5200\u5c31 \u628a\u91d1\u65e5\u5355\u7684\u624b\u81c2\u5378\u4e0b\u3002\u91d1\u65e5\u5355\u8d5e\u4e86\u4e00\u58f0\uff1a\u201c\u597d\u5200\u6cd5\uff01\u201d\u4ed6\u5206\u660e\u662f\u5728\u97e9\u4e8c\u5a18\u524d\u9762\uff0c\u4f46\u4e00\u5200 \u524a\u8fc7\uff0c\u5ffd\u7136\u95f4\u5374\u4eba\u5f71\u6773\u7136\uff0c\u91d1\u65e5\u5355\u7684\u58f0\u97f3\u5df2\u5230\u4e86\u5979\u7684\u80cc\u540e\u3002 \u3000\u3000\u97e9\u6f29\u662f\u603b\u9556\u5934\u7684\u8eab\u4efd\uff0c\u592b\u5987\u8054\u624b\uff0c\u5bf9\u4ed8\u4e00\u4eba\uff0c\u5df2\u611f\u9762\u4e0a\u65e0\u5149\uff0c\u4f55\u51b5\u5bf9\u65b9\u8fd8\u8981\u8ba9\u4ed6\u4eec \u4e09\u62db\uff1f\u56e0\u6b64\u4ed6\u6253\u5b9a\u4e3b\u610f\uff0c\u5148\u8ba9\u59bb\u5b50\u51fa\u624b\uff0c\u5f85\u4e09\u62db\u8fc7\u540e\uff0c\u8981\u662f\u59bb\u5b50\u4e0d\u80fd\u53d6\u80dc\uff0c\u4ed6\u518d\u4e0a\u524d\u76f8 \u52a9\uff0c\u54ea\u5378\u91d1\u65e5\u5355\u8eab\u6cd5\u5feb\u5f97\u51fa\u5947\uff0c\u97e9\u4e8c\u5a18\u4e00\u5200\u65a9\u53bb\uff0c\u8fde\u4ed6\u7684\u8863\u88f3\u8fd8\u6ca1\u6cbe\u7740\uff0c\u5c31\u7ed9\u4ed6\u95ea\u5230\u4e86 \u80cc\u540e\uff0c\u97e9\u7487\u89c1\u4e86\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff01\u91d1\u65e5\u5355\u867d\u7136\u8bf4\u8fc7\u5148\u8ba9\u4e09\u62db\uff0c\u4f46\u97e9\u7487\u5374\u4e0d\u80fd\u4e0d\u63d0\u9632\u654c \u4eba\u4e34\u65f6\u53d8\u5366\uff0c\u4e58\u673a\u4f24\u4e86\u4ed6\u7684\u59bb\u5b50\uff0c\u4e00\u89c1\u4e0d\u5999\uff0c\u65e0\u6687\u601d\u7d22\uff0c\u53ea\u597d\u6539\u4e86\u4e3b\u610f\uff0c\u7acb\u5373\u4e00\u5200\u524a\u51fa\u3002 \u3000\u3000\u97e9\u7487\u7684\u5200\u6cd5\u6bd4\u59bb\u5b50\u66f4\u4e3a\u9ad8\u660e\uff0c\u800c\u4e14\u4ed6\u6ca1\u6709\u6b8b\u5e9f\uff0c\u8eab\u624b\u81ea\u7136\u4e5f\u8f83\u4e3a\u77eb\u6377\uff0c\u8fd9\u4e00\u5200\u5f0f\u4e2d \u5957\u5f0f\uff0c\u524a\u81c2\u5207\u808b\uff0c\u5f53\u771f\u662f\u53c8\u5feb\u53c8\u72e0\u3002\u91d1\u65e5\u5355\u53eb\u9053\uff1a\u201c\u7cdf\u7cd5\uff01\u201d\u8eab\u5b50\u7a81\u7136\u77ee\u4e86\u4e00\u622a\uff0c\u9640\u87ba \u822c\u7684\u76f4\u8f6c\u51fa\u53bb\uff0c\u97e9\u7487\u8fd9\u4e00\u5200\u6070\u6070\u4ece\u4ed6\u7684\u5934\u9876\u524a\u8fc7\uff0c\u5374\u6ca1\u6709\u5c06\u4ed6\u65ab\u770b\u3002\u91d1\u65e5\u5355\u907f\u5f00\u8fd9\u4e00\u5200\uff0c \u8fd9\u624d\u633a\u8d77\u8170\u6765\uff0c\u5401\u4e86\u53e3\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u5e78\u597d\uff0c\u6ca1\u6709\u65ab\u7740\uff01\u201d \u3000\u3000\u97e9\u7487\u592b\u59bb\u89c1\u654c\u4eba\u5fc3\u5b58\u620f\u5f04\uff0c\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5927\u59bb\u4fe9\u65e9\u5df2\u5fc3\u610f\u5982\u4e00\uff0c \u53cc\u5200\u9f50\u51fa\uff0c\u540c\u65f6\u5411\u91d1\u65e5\u5355\u65ab\u4e0b\u3002 \u3000\u3000\u53cc\u5200\u5408\u58c1\uff0c\u5c82\u6bd4\u5bfb\u5e38\uff1f\u53ea\u89c1\u4e24\u9053\u94f6\u8679\uff0c\u592d\u77eb\u5982\u9f99\uff0c\u500f\u5730\u5408\u6210\u4e86\u4e00\u9053\u5706\u5708\uff0c\u5f53\u5934\u7f69\u4e0b\uff0c \u5c06\u91d1\u65e5\u5355\u7684\u524d\u540e\u5de6\u53f3\u5168\u90fd\u5c01\u95ed\uff0c\u5411\u54ea\u4e00\u65b9\u95ea\u8eb2\uff0c\u90fd\u5df2\u4e0d\u80fd\uff01\u7fa4\u96c4\u770b\u5f97\u7d27\u5f20\u4e4b\u6781\uff0c\u51e0\u4e4e\u8fde \u5927\u6c14\u4e5f\u4e0d\u6562\u900f\u51fa\uff0c\u5ffd\u7136\u95f4\u53ea\u542c\u5f97\u53ee\u53ee\u4e24\u58f0\uff0c\u53cc\u5200\u5206\u660e\u5df2\u780d\u5230\u91d1\u65e5\u5355\u8eab\u4e0a\uff0c\u5374\u4e0d\u77e5\u600e\u7684\uff0c \u53ea\u89c1\u4ed6\u4e00\u7529\u8896\u5b50\uff0c\u5df2\u8d70\u51fa\u5708\u5916\uff0c\u7adf\u7136\u4e1d\u6beb\u4e5f\u6ca1\u53d7\u4f24\u3002\u539f\u6765\u4ed6\u53cc\u624b\u7b3c\u5728\u8896\u4e2d\uff0c\u9ed8\u8fd0\u7384\u529f\uff0c \u8863\u8896\u626c\u8d77\uff0c\u5378\u5f00\u4e86\u5bf9\u65b9\u7684\u52b2\u529b\uff0c\u90a3\u53ee\u53ee\u4e24\u58f0\uff0c\u5374\u662f\u4ed6\u4eec\u81ea\u5df1\u7684\u5200\u950b\u76f8\u89e6\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u91d1\u65e5\u5355\u867d\u7136\u6ca1\u6709\u907f\u5f00\uff0c\u4f46\u4ed6\u53ea\u662f\u5378\u53bb\u5bf9\u65b9\u7684\u52b2\u529b\uff0c\u5e76\u672a\u8fd8\u51fb\uff0c\u6240\u4ee5\u672a\u7b97\u8fdd\u80cc \u8bfa\u8a00\uff0c\u4ecd\u662f\u4e0d\u6298\u4e0b\u6263\u7684\u8ba9\u4e86\u5bf9\u65b9\u4e09\u62db\u3002\u91d1\u65e5\u5355\u4f4e\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u8863\u8896\u4e0a\u73b0\u51fa\u4e24\u9053\u6de1\u6de1\u7684\u5200 \u75d5\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u7981\u9a87\u7136\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5018\u82e5\u4ed6\u4eec\u592b\u59bb\u4e00\u4e0a\u6765\u5c31\u662f\u53cc\u5200\u5408\u58c1\uff0c\u6211\u8ba9\u8fd9\u4e09\u62db\uff0c \u53ea\u6015\u591a\u5c11\u4e5f\u8981\u53d7\u70b9\u4f24\u4e86\u3002\u201d \u3000\u3000\u97e9\u7487\u592b\u59bb\u5403\u60ca\u66f4\u751a\uff0c\u4ed6\u4eec\u662f\u6210\u540d\u4eba\u7269\uff0c\u672c\u6765\u5230\u6b64\u5730\u6b65\uff0c\u5df2\u5e94\u8ba4\u8f93\u3002\u4f46\u97e9\u4e8c\u5a18\u6027\u8e81\u6c14 \u521a\uff0c\u5bf9\u65b9\u4e14\u53c8\u662f\u58f0\u660e\u4e86\u66ff\u53f6\u51b2\u9704\u51fa\u5934\u7684\uff0c\u5979\u53c8\u600e\u80fd\u5931\u4e86\u8fd9\u4e2a\u9762\u5b50\uff1f\u5f53\u4e0b\u6068\u6068\u8bf4\u9053\uff1a\u201c\u8001 \u4f34\u513f\uff0c\u54b1\u4eec\u8c41\u51fa\u53bb\u5427\uff01\u201d\u94c1\u62d0\u4e00\u6491\uff0c\u8eab\u5f62\u9aa4\u8d77\uff0c\u4e1a\u5df2\u4e00\u5200\u5288\u4e0b\uff0c\u97e9\u7487\u5f53\u7136\u4e0d\u80fd\u8ba9\u59bb\u5b50\u4e00 \u4eba\u53d7\u654c\uff0c\u53ea\u597d\u4e5f\u8ddf\u7740\u4e00\u5200\uff0c\u4ed6\u8fd9\u4e00\u5200\u5374\u662f\u5411\u4e0b\u76d8\u780d\u6765\uff0c\u53cc\u5200\u4e00\u4e0a\u4e00\u4e0b\uff0c\u653b\u52bf\u66f4\u89c1\u72e0\u8fa3\uff01 \u3000\u3000\u91d1\u65e5\u5355\u53eb\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u8bf7\u6055\u665a\u8f88\u8fd8\u62db\u4e86\uff01\u201d\u4ed6\u4e00\u624b\u6267\u5728\u68d2\u7684\u4e2d\u95f4\uff0c\u4e00\u62db \u201c\u6307\u5929\u5212\u5730\u201d\u4e0a\u7aef\u78b0\u7740\u97e9\u4e8c\u5a18\u7684\u5200\u53e3\uff0c\u4e0b\u7aef\u89e6\u7740\u97e9\u65cb\u7684\u5200\u53f6\uff0c\u8bf4\u4e5f\u5947\u602a\uff0c\u5c31\u5728\u8fd9\u540c\u4e00\u65f6 \u95f4\uff0c\u97e9\u7487\u592b\u59bb\u90fd\u611f\u5230\u4e00\u80a1\u5927\u529b\u5c06\u4ed6\u4eec\u7684\u5175\u5200\u7275\u5f15\uff0c\u5ffd\u5730\u4e24\u67c4\u5200\u90fd\u88ab\u5438\u5230\u90a3\u602a\u68d2\u4e4b\u4e0a\uff0c\u7262 \u7262\u9644\u7740\uff0c\u7adf\u7136\u6ca1\u6709\u6389\u4e0b\u6765\u3002 \u3000\u3000\u97e9\u4e8c\u5a18\u5931\u4e86\u5355\u5200\uff0c\u7acb\u5373\u91cd\u65bd\u7edd\u6280\uff0c\u53ea\u89c1\u5979\u94c1\u62d0\u4e00\u6491\uff0c\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c\u5012\u7eb5\u51fa\u53bb\uff0c \u811a\u5c16\u8fd8\u672a\u7740\u5730\uff0c\u4e24\u5bf9\u94c1\u9e33\u9e2f\u5df2\u662f\u75be\u6253\u51fa\u53bb\u3002\u8fd9\u4e00\u6b21\u5979\u7684\u6697\u5668\u96c6\u4e2d\u653b\u51fb\u4e00\u4e2a\u654c\u4eba\uff0c\u624b\u6cd5\u66f4 \u4e3a\u5947\u5999\uff0c\u4e24\u5bf9\u94c1\u9e33\u9e2f\u5206\u5f00\u56db\u4e2a\u65b9\u5411\uff0c\u5411\u4e0a\u76d8\u7684\u4e00\u5bf9\u94c1\u9e33\u9e2f\u6253\u5bf9\u65b9\u4e24\u80a9\u7684\u7435\u7436\u9aa8\uff0c\u5411\u4e0b\u76d8 \u7684\u4e00\u5bf9\u94c1\u9e33\u9e2f\u5219\u524a\u5bf9\u65b9\u53cc\u817f\u7684\u819d\u76d6\u3002\u4f3c\u8fd9\u6837\u7684\u6253\u6cd5\uff0c\u591a\u597d\u7684\u63a5\u6697\u5668\u529f\u592b\uff0c\u4e5f\u51b3\u4e0d\u80fd\u540c\u65f6 \u63a5\u4e86\u56db\u4e2a\u4e0d\u540c\u65b9\u5411\u7684\u6697\u5668\uff0c\u9664\u975e\u4ed6\u80fd\u957f\u51fa\u56db\u6761\u624b\u81c2\u3002 \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u91d1\u65e5\u5355\u53ea\u662f\u5c06\u90a3\u602a\u68d2\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u602a\u68d2\u7adf\u4f3c\u751f\u51fa\u4e00\u80a1\u65e0\u5f62\u7684\u5438\u529b\uff0c\u97e9\u4e8c \u5a18\u7684\u4e24\u5bf9\u94c1\u9e33\u9e2f\u7adf\u7136\u6539\u4e86\u65b9\u5411\uff0c\u90fd\u5411\u4ed6\u7684\u602a\u68d2\u98de\u6765\uff0c\u88ab\u5438\u5728\u602a\u68d2\u4e4b\u4e0a\uff0c\u7262\u7262\u9644\u7740\uff0c\u5c31\u4f3c \u90a3\u4e24\u67c4\u5355\u5200\u4e00\u822c\u3002 \u3000\u3000\u91d1\u65e5\u5355\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u5c06\u4e24\u67c4\u5355\u5200\u4e24\u5bf9\u94c1\u9e33\u9e2f\u6458\u4e86\u4e0b\u6765\uff0c\u4ea4\u8fd8\u97e9\u7487\u592b\u5987\uff0c\u8bf4\u58f0\uff1a\u201c\u627f \u8ba9\u4e86\uff01\u201d\u97e9\u4e8c\u5a18\u5f85\u8981\u4e0d\u63a5\uff0c\u4f46\u90a3\u5200\u67c4\u5df2\u585e\u5230\u5979\u7684\u624b\u4e2d\uff0c\u5018\u518d\u63a8\u62d2\uff0c\u66f4\u4e0d\u597d\u770b\uff0c\u53ea\u597d\u63a5\u4e86\u3002 \u5fc3\u91cc\u4e00\u7247\u832b\u7136\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u3002\u5168\u7956\u5fb7\u6025\u5fd9\u51fa\u573a\uff0c\u5c06\u4ed6\u4eec\u62c9\u4e86\u56de\u53bb\uff0c\u529d\u6170\u4ed6\u4eec\u9053\uff1a\u201c\u80dc \u8d1f\u5175\u5bb6\u5e38\u4e8b\uff0c\u4f55\u8db3\u4ecb\u6000\uff1f\u8d24\u6881\u5b5f\u80dc\u4e86\u4e8c\u573a\uff0c\u8d25\u56de\u4e00\u573a\uff0c\u90a3\u4e5f\u4e0d\u8fc7\u662f\u6253\u4e2a\u5e73\u624b\u3002\u201d \u3000\u3000\u539f\u6765\u5168\u7956\u5fb7\u662f\u6015\u4ed6\u4eec\u592b\u59bb\u54bd\u4e0d\u4e0b\u8fd9\u53e3\u6c14\uff0c\u4e00\u65f6\u770b\u4e0d\u5f00\uff0c\u4fbf\u4f1a\u81ea\u5bfb\u77ed\u89c1\u3002\u97e9\u4e8c\u5a18\u9ed8\u7136 \u4e0d\u8bed\uff0c\u97e9\u7487\u4f3c\u751a\u4e3a\u51b7\u9759\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u6211\u4eca\u65e5\u5230\u6765\uff0c\u672c\u5c31\u4e0d\u51c6\u5907\u6d3b\u7740\u56de\u53bb\u4e86\u3002\u201d\u5168\u7956\u5fb7\u5403\u4e86\u4e00\u60ca\uff0c\u751f\u6015 \u4ed6\u518d\u53bb\u62fc\u547d\uff0c\u4f46\u89c1\u97e9\u7487\u4ecd\u662f\u8ddf\u7740\u4ed6\u8d70\uff0c\u5e76\u6ca1\u6709\u518d\u53bb\u62fc\u547d\u7684\u610f\u601d\uff0c\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\uff0c\u53ea\u9053\u4ed6 \u65b0\u8d25\u4e4b\u4f59\uff0c\u6545\u6b64\u8bed\u65e0\u4f26\u6b21\uff0c\u5374\u4e0d\u77e5\u97e9\u7487\u5fc3\u91cc\u5df2\u662f\u6697\u6697\u6253\u4e86\u4e00\u4e2a\u4e3b\u610f\u3002 \u3000\u3000\u91d1\u65e5\u5355\u4ecd\u662f\u7ad9\u5728\u573a\u4e2d\uff0c\u5e76\u672a\u9000\u4e0b\uff0c\u4e91\u53ec\u5fc3\u60f3\uff1a\u201c\u52a9\u62f3\u7684\u670b\u53cb\u867d\u591a\uff0c\u53ea\u6015\u65e0\u4eba\u662f\u4ed6\u5bf9 \u624b\uff0c\u8bf4\u4e0d\u5f97\u53ea\u597d\u6211\u4eb2\u81ea\u51fa\u53bb\u4e0e\u4ed6\u51b3\u4e2a\u96cc\u96c4\u4e86\u3002\u201d\u6b63\u81ea\u6b20\u8eab\u6b32\u8d77\uff0c\u53ea\u542c\u5f97\u90a3\u91d1\u65e5\u5355\u5df2\u5728\u6717 \u58f0\u53d1\u8bdd\uff1a\u201c\u5168\u5e2e\u4e3b\u8bf4\u5f97\u5bf9\uff0c\u80dc\u8d25\u4e43\u5175\u5bb6\u5e38\u4e8b\uff0c\u4f55\u8db3\u4ecb\u6000\uff1f\u6c5f\u5927\u4fa0\uff0c\u5f53\u5e74\u91d1\u67d0\u591a\u8499\u4f60\u7684\u6307 \u6559\uff0c\u4eca\u65e5\u5e78\u5f97\u5728\u6b64\u76f8\u9022\uff0c\u4e0d\u77e5\u6c5f\u5927\u4fa0\u53ef\u80af\u518d\u8d50\u6559\u4e00\u573a\u4e48\uff1f\u201d\u539f\u6765\u91d1\u65e5\u5355\u5728\u6253\u8d25\u97e9\u7487\u592b\u59bb \u4e4b\u540e\uff0c\u672c\u6765\u4e5f\u5c31\u60f3\u5411\u4e91\u53ec\u6311\u6218\u7684\uff0c\u4f46\u542c\u4e86\u5168\u7956\u5fb7\u90a3\u53e5\u8bdd\uff0c\u53ea\u9053\u5168\u7956\u5fb7\u662f\u8bbd\u523a\u4ed6\u5f53\u5e74\u8f93\u7ed9 \u6c5f\u5357\u4e4b\u4e8b\uff0c\u5fcd\u4e0d\u7740\uff0c\u4fbf\u5148\u5411\u6c5f\u5357\u6311\u6218\u4e86\u3002 \u3000\u3000\u6c5f\u5357\u6414\u6414\u5934\u76ae\uff0c\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u662f\u5728\u5411\u6211\u6253\u62db\u547c\u4e48\uff1f\u201d\u4ece\u6765\u6ca1\u4eba\u79f0\u4ed6\u505a\u4ec0\u4e48\u201c\u5927\u4fa0\u201d\uff0c \u56e0\u6b64\u4ed6\u76f4\u81f3\u542c\u5b8c\u4e86\u91d1\u65e5\u5355\u7684\u8bf4\u8bdd\uff0c\u8fd9\u624d\u77e5\u9053\u8bf4\u7684\u662f\u4ed6\u3002\u91d1\u65e5\u5355\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\uff0c\u4f60\u4e0d\u5c51\u8d50 \u6559\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u5357\u82e6\u7b11\u9053\uff1a\u201c\u6211\u662f\u5192\u724c\u7684\u5927\u4fa0\uff1a\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u4f46\u4f60\u4e00\u5b9a\u8981\u6211\u732e\u4e11\uff0c\u90a3\u3001\u90a3\u3001\u90a3\u3001 \u90a3\u2026\u2026\u201d\u5e95\u4e0b\u90a3\u534a\u53e5\u201c\u6211\u4e5f\u53ea\u597d\u5949\u966a\u4e86\u3002\u201d\u8fd8\u672a\u8bf4\u51fa\uff0c\u6c5f\u6d77\u5929\u5df2\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c \u6211\u4ee3\u4f60\u53bb\u3002\u201d \u3000\u3000\u6c5f\u5357\u7acb\u5373\u6539\u53e3\u8bf4\u9053\uff1a\u201c\u90a3\u6211\u5c31\u53ea\u597d\u53eb\u5c0f\u513f\u9886\u6559\u4f60\u7684\u9ad8\u62db\u4e86\u3002 \u3000\u3000\u6211\u4e0a\u4e86\u51e0\u5c81\u5e74\u7eaa\uff0c\u5df2\u975e\u590d\u5f53\u5e74\u4e4b\u52c7\uff0c\u6211\u7684\u529f\u592b\u5df2\u5168\u4f20\u4e86\u5c0f\u513f\uff0c\u4f60\u53ea\u8981\u6253\u8d25\u4e86\u4ed6\uff0c\u6211 \u4e5f\u5c31\u7518\u5fc3\u670d\u8f93\u4e86\uff01\u201d \u3000\u3000\u4e91\u53ec\u662f\u77e5\u9053\u6c5f\u6d77\u5929\u7684\u6b66\u529f\u8fdc\u80dc\u4e8e\u4ed6\u8001\u5b50\u7684\uff0c\u4f46\u4ed6\u8fd8\u672a\u77e5\u9053\u6c5f\u6d77\u5929\u66fe\u670d\u98df\u4e86\u5929\u5fc3\u77f3\u4e4b \u4e8b\uff0c\u5140\u81ea\u653e\u5fc3\u4e0d\u4e0b\uff0c\u53ee\u5631\u6c5f\u6d77\u5929\u9053\uff1a \u3000\u3000\u201c\u4f60\u7559\u795e\u90a3\u53ae\u7684\u602a\u68d2\uff0c\u90a3\u602a\u68d2\u751a\u662f\u90aa\u95e8\uff01\u201d\u6c5f\u5357\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u62c5\u5fe7\uff0c\u5c0f\u513f\u7528\u7684\u662f\u91d1 \u5927\u4fa0\u7ed9\u4ed6\u7684\u90a3\u628a\u88c1\u4e91\u5b9d\u5251\uff0c\u5728\u5175\u5668\u4e0a\u7edd\u4e0d\u4f1a\u5403\u4e8f\u3002\u201d \u3000\u3000\u91d1\u65e5\u5355\u89c1\u6c5f\u5357\u5dee\u9063\u513f\u5b50\u51fa\u573a\uff0c\u53ea\u5f53\u662f\u6c5f\u5357\u8f7b\u89c6\u4e8e\u4ed6\uff0c\u5fc3\u91cc\u751a\u4e3a\u607c\u6012\uff0c\u4f46\u4ed6\u4e5f\u662f\u4e2a\u4ed4 \u7ec6\u7684\u4eba\uff0c\u968f\u5373\u60f3\u9053\uff1a\u201c\u5929\u4e0b\u6ca1\u6709\u4e0d\u7231\u60dc\u513f\u5b50\u7684\u7236\u4eb2\uff0c\u6c5f\u5357\u53c8\u4e0d\u662f\u4e0d\u77e5\u6211\u7684\u5389\u5bb3\uff0c\u82e5\u4e0d\u662f \u8fd9\u5c0f\u5b50\u5f53\u771f\u6709\u51e0\u5206\u672c\u4e8b\uff0c\u4ed6\u600e\u80af\u53eb\u513f\u5b50\u524d\u6765\u9001\u547d\uff1f\u201d\u4ed6\u672c\u6765\u662f\u4e0d\u628a\u6c5f\u6d77\u5929\u653e\u5728\u773c\u5185\u7684\uff0c \u8fd9\u4e48\u4e00\u60f3\uff0c\u4e5f\u5c31\u4e0d\u6562\u600e\u4e48\u8f7b\u89c6\u4e86\u3002 \u3000\u3000\u5f85\u5230\u4ed6\u4e0e\u6c5f\u6d77\u5929\u6253\u4e86\u4e00\u4e2a\u7167\u9762\uff0c\u89c1\u6c5f\u6d77\u5929\u82f1\u534e\u5185\u655b\uff0c\u53cc\u76ee\u70af\u70af\u6709\u795e\uff0c\u4e0d\u7981\u5fc3\u5934\u5fae\u51db\uff0c \u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u600e\u7684\u5c31\u7ec3\u6210\u4e86\u4e0a\u4e58\u7684\u5185\u529f\uff1f\u201d\u518d\u4e00\u770b\u65f6\uff0c\u53c8\u89c1\u4ed6\u8170\u95f4\u7684\u5b9d\u5251\uff0c \u9690\u9690\u900f\u51fa\u9752\u5149\uff0c\u91d1\u65e5\u5355\u8ba4\u5f97\u662f\u91d1\u4e16\u9057\u4ece\u524d\u7528\u8fc7\u7684\u90a3\u628a\u88c1\u4e91\u5b9d\u5251\uff0c\u66f4\u662f\u5403\u60ca\uff0c\u540c\u9053\uff1a\u201c\u4f60 \u662f\u91d1\u4e16\u9057\u7684\u4ec0\u4e48\u4eba\uff0c\u4ed6\u7684\u5b9d\u5251\u600e\u4e48\u5230\u4e86\u4f60\u7684\u8eab\u4e0a\uff1f\u201d\u6c5f\u6d77\u5929\u65bd\u4e86\u4e00\u793c\uff0c\u606d\u606d\u656c\u656c\u5730\u8bf4\u9053\uff1a \u201c\u91d1\u5927\u4fa0\u6b63\u662f\u5bb6\u5e08\u3002\u201d \u3000\u3000\u91d1\u65e5\u5355\u5fc3\u5934\u5600\u5495\uff1a\u201c\u539f\u6765\u662f\u91d1\u4e16\u9057\u7684\u5f1f\u5b50\uff0c\u602a\u4e0d\u5f97\u6c5f\u5357\u653e\u5fc3\u8ba9\u4ed6\u51fa\u573a\u3002\u6211\u8fd9\u5438\u661f\u68d2 \u4eca\u65e5\u53ef\u8981\u9047\u4e0a\u514b\u661f\u4e86\u3002\u201d\u539f\u6765\u4ed6\u8fd9\u6839\u602a\u68d2\u4e43\u662f\u9668\u77f3\u6253\u6210\uff0c\u8574\u85cf\u6709\u6781\u5f3a\u7684\u78c1\u6027\uff0c\u80fd\u5438\u91d1\u5c5e\u3002 \u4f46\u6c5f\u6d77\u5929\u7684\u88c1\u4e91\u5b9d\u5251\u5e76\u975e\u91d1\u5c5e\uff0c\u4e43\u662f\u6d77\u5e95\u5bd2\u7389\u6240\u94f8\uff0c\u8584\u5982\u8749\u7ffc\u800c\u53c8\u950b\u5229\u975e\u5e38\uff0c\u201c\u5438\u661f\u68d2\u201d \u78b0\u4e0a\u4e86\u5b83\uff0c\u90a3\u662f\u6beb\u65e0\u4f5c\u7528\u7684\u4e86\u3002 \u3000\u3000\u91d1\u65e5\u5355\u867d\u6709\u51e0\u5206\u5fcc\u60ee\uff0c\u4f46\u4ed6\u662f\u524d\u8f88\u8eab\u4efd\uff0c\u5c82\u80fd\u793a\u5f31\uff0c\u5f53\u4e0b\u4fbf\u9053\uff1a\u201c\u539f\u6765\u662f\u91d1\u5927\u4fa0\u7684 \u9ad8\u8db3\u3002\u4e45\u4ef0\u8fd9\u628a\u5b9d\u5251\u4e43\u662f\u5929\u4e0b\u65e0\u53cc\u7684\u795e\u7269\u5229\u5668\uff0c\u4eca\u65e5\u96be\u5f97\u76f8\u9022\uff0c\u4fbf\u8bf7\u6c5f\u5c0f\u4fa0\u4eae\u5251\uff0c\u8ba9\u6211 \u957f\u957f\u89c1\u8bc6\u5427\u3002\u201d\u6c5f\u6d77\u5929\u4ecd\u662f\u606d\u606d\u656c\u656c\u4ed6\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u662f\u4e3a\u8ba8\u6559\u800c\u6765\uff0c\u600e\u6562\u5728\u524d\u8f88\u9762\u524d\u52a8 \u7528\u5175\u5203\uff1f\u201d\u5f53\u4e0b\u610f\u6001\u60a0\u95f2\uff0c\u5728\u4e0b\u9996\u7acb\u5b9a\uff0c\u90a3\u662f\u4ee5\u665a\u8f88\u81ea\u5c45\u7684\u793c\u8282\u3002 \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u8fd9\u6b21\u51fa\u573a\uff0c\u9664\u4e86\u8981\u4e3a\u7236\u4eb2\u4e89\u4e2a\u9762\u5b50\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u5c42\u7528\u610f\u3002\u4ed6\u542c\u5e08\u7236\u8bf4\u8fc7\uff0c \u8fd9\u91d1\u65e5\u5355\u662f\u4e2a\u6b66\u5b66\u5947\u4eba\uff0c\u5728\u5185\u529f\u4e0a\u9887\u6709\u72ec\u7279\u7684\u9020\u8be3\uff0c\u884c\u4e8b\u5728\u6b63\u90aa\u4e4b\u95f4\uff0c\u5374\u662f\u4e2a\u6709\u8840\u6027\u7684 \u6c49\u5b50\u3002\u540e\u6765\u53c8\u542c\u5f97\u91d1\u65e5\u5355\u662f\u53f6\u51b2\u9704\u7684\u670b\u53cb\uff0c\u5bf9\u4ed6\u53c8\u591a\u4e86\u51e0\u5206\u597d\u611f\uff0c\u56e0\u6b64\u6c5f\u6d77\u5929\u662f\u6709\u5fc3\u5bf9 \u4ed6\u624b\u4e0b\u7559\u60c5\uff0c\u4e0d\u80af\u8ba9\u4e91\u53ec\u4e0e\u4ed6\u62fc\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u8fd9\u624d\u4e89\u770b\u51fa\u573a\u7684\u3002 \u3000\u3000\u91d1\u65e5\u5355\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u597d\u4e2a\u80c6\u5927\u5305\u5929\u7684\u5c0f\u5b50\uff0c\u7adf\u6562\u7a7a\u624b\u5bf9\u4ed8\u6211\u7684\u795e\u68d2\uff1f\u201d\u4f46\u6c5f \u6d77\u5929\u4e0d\u80af\u7528\u5251\uff0c\u4e5f\u6b63\u5408\u4e4e\u4ed6\u7684\u5fc3\u610f\uff0c\u5f53\u4e0b\u54c8\u54c8\u4e00\u7b11\u3002\u8bf4\u9053\uff1a\u201c\u679c\u7136\u662f\u540d\u5bb6\u5f1f\u5b50\uff0c\u6c14\u9b44\u4e0d \u51e1\u3002\u597d\uff0c\u597d\uff0c\u597d\uff01 \u3000\u3000\u6211\u4e5f\u5c31\u7a7a\u624b\u548c\u4f60\u8bd5\u8bd5\u51e0\u62db\u5427\u3002\u201d\u5f53\u4e0b\u628a\u90a3\u602a\u68d2\u63d2\u5728\u80cc\u540e\uff0c\u7b49\u5f85\u6c5f\u6d77\u5929\u8fdb\u62db\uff0c\u54ea\u77e5\u6c5f \u6d77\u5929\u4ecd\u662f\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6562\u65e0\u793c\uff0c\u8bf7\u524d\u8f88\u5148\u53d1\u4e09\u62db\uff01\u201d \u3000\u3000\u91d1\u65e5\u5355\u6115\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u8ba9\u6211\u4e09\u62db\uff1f\u201d\u6c5f\u5357\u5728\u5ea7\u4e0a\u7b11\u9053\uff1a\u201c\u5c0f\u513f\u662f\u5b66\u4f60\u7684\u699c\u6837\u3002\u201d \u91d1\u65e5\u5355\u8d5e\u9053\uff1a\u201c\u597d\uff1b\u5f53\u771f\u662f\u864e\u7236\u65e0\u72ac\u5b50\uff0c\u540d\u5e08\u51fa\u9ad8\u5f92\uff01\u6211\u5c31\u6210\u5168\u4f60\u7684\u5fd7\u5411\u5427\u3002\u201d\u5de6\u638c\u5212 \u4e86\u4e00\u9053\u5706\u5f27\uff0c\u7f13\u7f13\u5411\u6c5f\u6d77\u5929\u63a8\u53bb\uff0c\u6c5f\u5357\u542c\u91d1\u65e5\u5355\u8bdd\u8bed\u4e2d\u5c06\u4ed6\u8d5e\u4e3a\u201c\u864e\u7236\u201d\uff0c\u6781\u4e3a\u53d7\u7528\uff0c \u6b63\u81ea\u5f97\u610f\uff0c\u5ffd\u89c1\u6c5f\u6d77\u5929\u4e00\u4e2a\u8e09\u8dc4\uff0c\u51e0\u4e4e\u8dcc\u5012\u3002 \u3000\u3000\u539f\u6765\u91d1\u65e5\u5355\u8fd9\u4e00\u638c\u867d\u7136\u53bb\u52bf\u7f13\u6162\uff0c\u4f46\u5374\u85cf\u7740\u4e00\u80a1\u5f3a\u5927\u7684\u5438\u529b\u3002\u6c5f\u6d77\u5929\u60f3\u4e0d\u5230\u4ed6\u7684\u638c \u529b\u5982\u6b64\u602a\u5f02\uff0c\u4e8b\u5148\u672a\u66fe\u9632\u5907\uff0c\u51e0\u4e4e\u88ab\u90a3\u80a1\u5438\u5f15\u529b\u7275\u52a8\u3002\u5012\u9000\u8f6c\u6765\uff0c\u4f46\u7ec8\u4e8e\u8fd8\u662f\u6323\u8131\uff0c\u4e00 \u6b65\u8de8\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u91d1\u65e5\u5355\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u5947\u602a\uff0c\u4ed6\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u600e\u7684\u4fbf\u6709\u5982\u6b64\u529f\u529b\uff1f\u201d\u539f\u6765\u5728\u6b64\u4e4b \u524d\uff0c\u4ed6\u867d\u7136\u770b\u51fa\u6c5f\u6d77\u5929\u8eab\u5177\u4e0a\u4e58\u5185\u529f\uff0c\u4f46\u603b\u4ee5\u4e3a\u6c5f\u6d77\u5929\u5e74\u7eaa\u592a\u8f7b\uff0c\u529f\u529b\u518d\u9ad8\uff0c\u4e5f\u51b3\u4e0d\u80fd \u5728\u81ea\u5df1\u4e4b\u4e0a\uff0c\u4ed6\u4e00\u6765\u4e3a\u4e86\u60dc\u624d\uff0c\u4e8c\u6765\u4e3a\u4e86\u4e0d\u60f3\u7ed3\u6028\u4e8e\u91d1\u4e16\u9057\uff0c\u8fd9\u4e00\u638c\u4e0d\u8fc7\u7528\u4e86\u4e03\u5206\u529b\u9053\uff0c \u5fc3\u4e2d\u8fd8\u9887\u6709\u987e\u5fcc\uff0c\u6015\u4f24\u4e86\u6c5f\u6d77\u5929\u5462\uff0c\u54ea\u77e5\u6c5f\u6d77\u5929\u5728\u8eab\u4f53\u5df2\u5931\u4e86\u91cd\u5fc3\u7684\u60c5\u5f62\u4e4b\u4e0b\u3002\u4ecd\u7136\u80fd \u4ece\u5bb9\u6323\u8131\u4ed6\u7684\u638c\u529b\u3002\u91d1\u65e5\u5355\u8fd9\u624d\u77e5\u9053\u6c5f\u6d77\u5929\u7684\u529f\u529b\u53ea\u6709\u5728\u4ed6\u4e4b\u4e0a\uff0c\u51b3\u4e0d\u5728\u4ed6\u4e4b\u4e0b\u3002 \u3000\u3000\u91d1\u65e5\u5355\u65e2\u5df2\u8bd5\u51fa\u6c5f\u6d77\u5929\u7684\u529f\u529b\uff0c\u7b2c\u4e8c\u638c\u3001\u7b2c\u4e09\u638c\u4fbf\u5168\u529b\u8111\u4e3a\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c \u4e24\u638c\u9996\u5c3e\u76f8\u8854\uff0c\u8baf\u82e5\u5954\u96f7\u3002\u6c5f\u6d77\u5929\u8fd9\u65f6\u5df2\u6709\u51c6\u5907\uff0c\u8fd0\u8d77\u62a4\u4f53\u795e\u529f\uff0c\u8e0f\u51fa\u5929\u7f57\u6b65\u6cd5\uff0c\u773c\u770b \u8fd9\u4e24\u638c\u582a\u582a\u5c31\u8981\u6253\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u4ed6\u5374\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u53ea\u662f\u4e00\u98d8\u4e00\u95ea\uff0c\u5c31\u4ece\u91d1\u65e5\u5355\u7684\u8eab \u8fb9\u8de8\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u91d1\u65e5\u5355\u9053\uff1a\u201c\u597d\uff0c\u540d\u5bb6\u5b50\u5f1f\uff0c\u679c\u662f\u4e0d\u51e1\uff0c\u8f7b\u529f\u5185\u529f\uff0c\u4e24\u81fb\u4f73\u5999\u3002\u91d1\u67d0\u672c\u5f53\u8ba4\u8f93\uff0c\u4f46 \u673a\u4f1a\u96be\u9022\uff0c\u91d1\u67d0\u8fd8\u60f3\u89c1\u8bc6\u89c1\u8bc6\u91d1\u5927\u4fa0\u6240\u4f20\u7684\u7edd\u5b66\u795e\u529f\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u524d\u8f88\u5ba2\u6c14\u4e86\u3002\u201d \u5fc3\u60f3\uff1a\u201c\u4f60\u65e2\u82e6\u82e6\u76f8\u8feb\uff0c\u6211\u4e5f\u53ea\u597d\u8ba9\u4f60\u77e5\u96be\u800c\u9000\u4e86\u3002\u201d\u5f53\u4e0b\u53cd\u624b\u62cd\u51fa\u4e00\u638c\uff0c\u786c\u63a5\u91d1\u65e5\u5355 \u7684\u638c\u529b\u3002 \u3000\u3000\u53cc\u638c\u4e00\u4ea4\uff0c\u53cc\u65b9\u90fd\u662f\u5403\u60ca\u4e0d\u5c0f\u3002\u6c5f\u6d77\u5929\u53ea\u89c9\u5bf9\u65b9\u7684\u638c\u5fc3\u70ed\u547c\u547c\u7684\uff0c\u4ed6\u7684\u5185\u529b\u7adf\u4f3c\u7ea6 \u675f\u4e0d\u4f4f\uff0c\u8981\u88ab\u5bf9\u65b9\u5438\u53bb\uff0c\u5fc3\u60f3\uff1a\u201c\u602a\u4e0d\u5f97\u5e08\u7236\u8bf4\u4ed6\u7684\u5185\u529f\u602a\u5f02\uff0c\u679c\u7136\u662f\u6b63\u90aa\u5907\u6d3e\u6240\u65e0\u3002\u201d \u91d1\u65e5\u5355\u66f4\u662f\u60ca\u7591\u4e0d\u5b9a\uff0c\u4ed6\u5df2\u7ecf\u628a\u5185\u529b\u4e00\u91cd\u91cd\u52a0\u5f3a\uff0c\u4f46\u6c5f\u6d77\u5929\u59cb\u7ec8\u4e0d\u4e3a\u6240\u64bc\uff0c\u4ed6\u7ec3\u7684\u72ec\u95e8 \u201c\u5438\u661f\u638c\u201d\u5728\u6c5f\u6d77\u5929\u8eab\u4e0a\u7adf\u4f3c\u5931\u4e86\u4f5c\u7528\uff0c\u4f46\u4e5f\u4e0d\u89c1\u6c5f\u6d77\u5929\u8fd0\u529b\u53cd\u51fb\uff0c\u7adf\u662f\u8bd5\u4e0d\u51fa\u6c5f\u6d77\u5929 \u7684\u6df1\u6d45\u3002 \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u7ecf\u8fc7\u4e86\u4e00\u4e2a\u591a\u6708\u7684\u82e6\u7ec3\uff0c\u7531\u4e8e\u670d\u98df\u5929\u5fc3\u77f3\u6240\u589e\u8fdb\u7684\u529f\u529b\u5df2\u4e0e\u4ed6\u672c\u8eab\u539f\u5177 \u7684\u529f\u529b\u5408\u4e3a\u4e00\u4f53\uff0c\u53ef\u987a\u7528\u81ea\u5982\u4e86\u3002\u4ed6\u4e0d\u60f3\u4f24\u5bb3\u91d1\u65e5\u5355\uff0c\u4fbf\u968f\u7740\u5bf9\u65b9\u638c\u529b\u7684\u5f3a\u5f31\u800c\u53d8\u5316\uff0c \u7528\u5f97\u6070\u5230\u597d\u5904\uff0c\u65e2\u4e0d\u8ba9\u5bf9\u65b9\u4fb5\u8fdb\u6765\uff0c\u4ed6\u4e5f\u4e0d\u653b\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u6837\u76f8\u5f85\u7247\u523b\uff0c\u91d1\u65e5\u5355\u7684\u8138\u8272\u7531\u9752\u8f6c\u7ea2\uff0c\u7531\u7ea2\u8f6c\u7d2b\uff0c\u5934\u7b4b\u66b4\u8d77\uff0c\u5f62\u72b6\u5df2\u662f\u72fc\u72c8\u4e0d\u582a\uff0c \u6c5f\u6d77\u5929\u5219\u4ecd\u662f\u795e\u8272\u81ea\u5982\uff0c\u7eb9\u4e1d\u672a\u52a8\u3002 \u3000\u3000\u573a\u4e2d\u6b66\u5b66\u9ad8\u660e\u4e4b\u58eb\uff0c\u65e9\u5df2\u770b\u5f97\u51fa\u6765\uff0c\u6c5f\u6d77\u5929\u662f\u80dc\u8fc7\u91d1\u65e5\u7891\u4e0d\u6b62\u4e00\u7b79\u4e86\u3002 \u3000\u3000\u91d1\u65e5\u5355\u6240\u7ec3\u7684\u201c\u5438\u661f\u638c\u201d\u672c\u6765\u6781\u4e3a\u53e4\u602a\uff0c\u4e00\u89e6\u7740\u5bf9\u65b9\u7684\u8eab\u4f53\uff0c\u5c31\u53ef\u4ee5\u5c06\u5bf9\u65b9\u7684\u5185\u529b \u5438\u6536\uff0c\u589e\u5f3a\u81ea\u5df1\uff0c\u5176\u5389\u5bb3\u4e4b\u5904\uff0c\u5b9e\u4e0d\u4e9a\u4e8e\u5b5f\u795e\u901a\u7684\u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\u3002\u4ed6\u82e6\u7ec3\u4e86\u4e8c\u5341\u5e74\uff0c \u6700\u8fd1\u65b9\u59cb\u5927\u529f\u544a\u6210\u3002\u4eca\u65e5\u5230\u6765\uff0c\u6709\u610f\u627e\u51e0\u4e2a\u6210\u540d\u4eba\u7269\uff0c\u4f8b\u5982\u4e91\u53ec\u3001\u5168\u7956\u5fb7\u3001\u534e\u5929\u98ce\u7b49\u4eba \u8bd5\u8bd5\u4ed6\u8fd9\u95e8\u529f\u5927\u3002\u5bf9\u6c5f\u6d77\u592d\u4ed6\u6700\u521d\u8fd8\u662f\u4e0d\u60f3\u4f7f\u7528\u7684\uff0c\u540e\u6765\u89c1\u6c5f\u6d77\u5929\u529f\u529b\u4e0d\u51e1\uff0c\u8fd9\u624d\u62ff\u51fa \u6765\u8bd5\u8bd5\uff0c\u54ea\u77e5\u4e00\u8bd5\u518d\u8bd5\uff0c\u6c5f\u6d77\u5929\u7684\u5185\u529b\u975e\u6052\u6ca1\u6709\u7ed9\u4ed6\u5438\u53bb\uff0c\u4ed6\u81ea\u5df1\u7684\u5185\u529b\u53cd\u800c\u7ea6\u675f\u4e0d\u4f4f\uff0c \u6d8c\u5c06\u51fa\u6765\uff0c\u7adf\u4f3c\u662f\u88ab\u5bf9\u65b9\u5438\u53bb\u4e00\u822c\u3002 \u3000\u3000\u91d1\u65e5\u5355\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u5fc3\u60f3\uff1a\u201c\u96be\u9053\u8fd9\u5c0f\u5b50\u4e5f\u7ec3\u6210\u4e86\u5438\u661f\u638c\uff1f\u4f46\u8fd9\u95e8\u529f\u592b\uff0c\u975e\u6709 \u4e8c\u5341\u5e74\u4ee5\u4e0a\u7684\u82e6\u7ec3\uff0c\u8bc0\u8ba1\u4e0d\u80fd\u7ec3\u6210\uff0c\u8fd9\u5c0f\u5b50\u770b\u6765\uff0c\u5374\u6700\u591a\u4e0d\u8fc7\u5341\u516b\u4e5d\u5c81\uff01\u201d \u3000\u3000\u91d1\u65e5\u5355\u7684\u5185\u529b\u6e90\u6e90\u6d8c\u51fa\uff0c\u4fe8\u5982\u6ce5\u725b\u5165\u6d77\uff0c\u4e00\u53bb\u65e0\u8e2a\uff0c\u4f46\u5947\u602a\u7684\u662f\uff0c\u4e1d\u6beb\u4e5f\u4e0d\u611f\u5230\u5bf9 \u65b9\u653b\u51fb\u7684\u529b\u9053\u3002\u91d1\u65e5\u5355\u5927\u4e3a\u7740\u6025\uff0c\u8981\u6536\u638c\u56de\u6765\uff0c\u4f46\u5374\u53c8\u88ab\u5bf9\u65b9\u7262\u7262\u5438\u7740\uff0c\u6446\u8131\u4e0d\u5f00\uff0c\u8d8a \u7528\u529b\u6323\u624e\uff0c\u5219\u5438\u5f97\u8d8a\u7262\u3002\u5f04\u5f97\u91d1\u65e5\u5355\u72fc\u72c8\u4e0d\u582a\u3002 \u3000\u3000\u5e78\u4e8f\u91d1\u65e5\u5355\u6bd5\u7adf\u662f\u4e2a\u6b66\u5b66\u5927\u884c\u5bb6\uff0c\u7247\u523b\u7684\u60ca\u614c\u8fc7\u540e\uff0c\u4fbf\u609f\u51fa\u5176\u4e2d\u9053\u7406\uff0c\u539f\u6765\u6c5f\u6d77\u5929 \u5e76\u975e\u7ec3\u6210\u5438\u661f\u638c\uff0c\u4e5f\u5e76\u975e\u6709\u610f\u5438\u53d6\u4ed6\u7684\u5185\u529b\uff0c\u53ea\u56e0\u6c5f\u6d77\u5929\u7684\u529f\u529b\u8fdc\u8fdc\u5728\u4ed6\u4e4b\u4e0a\uff0c\u53ea\u662f\u968f \u7740\u4ed6\u6240\u7528\u7684\u529b\u9053\u5f3a\u5f31\u800c\u53d8\u5316\uff0c\u4ed6\u5168\u529b\u65bd\u4e3a\uff0c\u6c5f\u6d77\u5929\u7684\u53cd\u5e94\u4e5f\u6108\u5f3a\u70c8\uff0c\u8fd9\u5c31\u662f\u4ed6\u7528\u529b\u6323\u8131\uff0c \u53cd\u800c\u6323\u8131\u4e0d\u5f00\u7684\u9053\u7406\u3002\u91d1\u65e5\u5355\u4e00\u60f3\u901a\u4e86\u8fd9\u4e2a\u9053\u7406\uff0c\u628a\u5185\u529b\u6e10\u6e10\u51cf\u5f31\uff0c\u7ec8\u81f3\u4e8e\u65e0\uff0c\u679c\u7136\u8f7b \u8f7b\u4e00\u6536\uff0c\u4fbf\u6446\u8131\u4e86\u6c5f\u6d77\u5929\u7684\u624b\u638c\u3002\u4f46\u4ed6\u672c\u8eab\u7684\u5185\u529b\u5374\u5df2\u6d88\u8017\u4e86\u4e09\u5206\u4e4b\u4e00\u4e86\uff01 \u3000\u3000\u91d1\u65e5\u5355\u795e\u8272\u6cae\u4e27\uff0c\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u6c5f\u5c11\u4fa0\u624b\u4e0b\u7559\u60c5\uff0c\u91d1\u67d0\u53e3\u670d\u5fc3\u670d\u3002\u8fd9\u513f\u7684\u4e8b\uff0c \u91d1\u67d0\u662f\u65e0\u98a4\u518d\u7ba1\u4e86\u3002\u201d\u8bf4\u7f62\uff0c\u4fbf\u5373\u51fa\u573a\uff0c\u5934\u4e5f\u4e0d\u56de\u5730\u8d70\u4e86\u3002\u6c5f\u6d77\u5929\u65e0\u610f\u4e2d\u8017\u635f\u4e86\u4ed6\u4e09\u5206 \u4e4b\u4e00\u7684\u529f\u529b\uff0c\u5fc3\u91cc\u751a\u662f\u62b1\u6b49\uff0c\u4f46\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u4ed6\u82e5\u8ffd\u4e0a\u53bb\u9053\u6b49\uff0c\u90a3\u53ea\u6709\u66f4\u635f\u4f24\u91d1\u65e5 \u5355\u7684\u989c\u9762\uff0c\u53ea\u597d\u7531\u4ed6\u81ea\u53bb\u3002\u6c5f\u6d77\u5929\u672c\u6765\u8fd8\u60f3\u5411\u4ed6\u63a2\u8be2\u53f6\u51b2\u9704\u7684\u6d88\u606f\u7684\uff0c\u4ed6\u8fd9\u4e48\u5306\u5306\u4fbf\u53bb\uff0c \u6c5f\u6d77\u5929\u4e5f\u6ca1\u6709\u673a\u4f1a\u5411\u4ed6\u53d1\u95ee\u4e86\u3002 \u3000\u3000\u9633\u8d64\u7b26\u6b64\u6b21\u51fa\u51fa\uff0c\u610f\u56fe\u91cd\u9738\u6b66\u6797\uff0c\u672c\u662f\u628a\u91d1\u65e5\u5355\u501a\u4e3a\u5de6\u53f3\u624b\u7684\u3002\u54ea\u77e5\u91d1\u65e5\u5355\u7adf\u632b\u8d25 \u5728\u6c5f\u6d77\u5929\u4e4b\u624b\uff0c\u8ba4\u8f93\u79bb\u573a\uff0c\u9633\u8d64\u7b26\u53c8\u662f\u5931\u671b\u53c8\u662f\u5403\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u91d1\u65e5\u5355\u7684\u6b66\u529f\u548c\u6211 \u4e43\u662f\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u8fd9\u5c0f\u5b50\u65e2\u80fd\u632b\u8d25\u91d1\u65e5\u5355\uff0c\u6211\u4e5f\u672a\u5fc5\u662f\u4ed6\u5bf9\u624b\uff0c\u522b\u7684\u4eba\u66f4\u4e0d\u7528\u8bf4\u4e86\u3002\u8fd9\u53ef \u5982\u4f55\u662f\u597d\uff1f\u201d\u4ed6\u4e0d\u613f\u8ba4\u8f93\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u8ba1\u4e0a\u5fc3\u6765\uff0c\u7acb\u5373\u51fa\u573a\u8bf4\u9053\uff1a\u201c\u65f6\u5019\u4e0d\u65e9\uff0c\u54b1\u4eec\u4eca \u65e5\u4e4b\u4f1a\uff0c\u4e43\u662f\u4e3a\u4e86\u6b27\u9633\u5e84\u4e3b\u548c\u4e91\u5e84\u4e3b\u4e24\u5bb6\u7684\u6881\u5b50\uff0c\u5e76\u975e\u4ee5\u6b66\u4f1a\u53cb\u3002\u4e00\u573a\u573a\u7684\u6bd4\u4e0b\u53bb\uff0c\u90a3 \u4e5f\u6ca1\u6709\u4ec0\u4e48\u610f\u601d\u3002\u4e0d\u5982\u7531\u9996\u8111\u4eba\u7269\uff0c\u4e00\u51b3\u96cc\u96c4\uff0c\u66f4\u4e3a\u723d\u5feb\u3002\u9633\u67d0\u4e0d\u81ea\u91cf\u529b\uff0c\u610f\u56fe\u5c31\u8bf7\u4e91 \u5e84\u4e3b\u51fa\u573a\u6307\u6559\uff01\u201d \u3000\u3000\u9633\u8d64\u7b26\u662f\u4e3a\u4e86\u8981\u628a\u6c5f\u6d77\u5929\u6487\u5f00\uff0c\u8fd9\u624d\u76f4\u63a5\u5411\u4e91\u53ec\u6311\u6218\u7684\u3002\u4f46\u4ee5\u4ed6\u7684\u8eab\u4efd\u3001\u6b66\u529f\uff0c\u5728 \u573a\u8bf8\u4eba\uff0c\u4e5f\u53ea\u6709\u4e91\u53ec\u624d\u914d\u5f97\u4e0a\u662f\u4ed6\u5bf9\u624b\uff0c\u65c1\u4eba\u51b3\u4e0d\u4f1a\u6000\u7591\u4ed6\u662f\u5bf9\u6c5f\u6d77\u5929\u907f\u6218\uff0c\u53ea\u8ba4\u4e3a\u4ed6 \u662f\u4e0d\u5c51\u548c\u6c5f\u6d77\u5929\u4ea4\u624b\u3002\u6c5f\u6d77\u5929\u4e5f\u4e0d\u613f\u592a\u9732\u950b\u8292\uff0c\u65e2\u7136\u5bf9\u65b9\u58f0\u660e\u8981\u4f1a\u201c\u4e3b\u8111\u4eba\u7269\u201d\uff0c\u4ed6\u5f53 \u7136\u4e5f\u4e0d\u4fbf\u51fa\u6218\u4e86\u3002 \u3000\u3000\u4e91\u53ec\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u6b63\u8981\u89c1\u8bc6\u89c1\u8bc6\u9633\u5148\u751f\u53f7\u79f0\u6b66\u6797\u7edd\u5b66\u7684\u4fee\u7f57\u9634\u715e\u529f\uff01\u201d\u4ed6\u7ad9 \u4e86\u8d77\u6765\uff0c\u5ffd\u5730\u53c8\u8fdf\u7591\u7247\u523b\uff0c\u56de\u5934\u5429\u5429\u5973\u513f\u9053\uff1a\u201c\u58c1\u513f\uff0c\u4f60\u51fa\u53bb\u5e2e\u5fd9\u5b57\u6587\u5e08\u5144\u62db\u547c\u8fdf\u6765\u7684 \u5ba2\u4eba\u3002\u201d\u539f\u6765\u4ed6\u9884\u6599\u8fd9\u573a\u51b3\u6597\uff0c\u5b9a\u7136\u5341\u5206\u60e8\u70c8\uff0c\u591a\u534a\u4e0d\u662f\u4f60\u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff0c\u6050\u6015\u5973\u513f\u8106 \u5f31\uff0c\u5018\u82e5\u89c1\u4ed6\u53d7\u4f24\uff0c\u53ea\u6015\u8981\u53d7\u4e0d\u8d77\u523a\u6fc0\uff0c\u6240\u4ee5\u501f\u6545\u9063\u5979\u51fa\u573a\u3002\u4e91\u58c1\u867d\u4e0d\u613f\u610f\uff0c\u4f46\u4e0d\u6562\u8fdd \u80cc\u7236\u547d\uff0c\u53ea\u597d\u5feb\u5feb\u79bb\u573a\u3002 \u3000\u3000\u4e91\u53ec\u5f85\u5230\u5973\u513f\u8d70\u4e86\u51fa\u53bb\uff0c\u8fd9\u624d\u8e0f\u8fdb\u573a\u5fc3\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u9633\u5148\u751f\u8bf7\u8d50\u62db\u5427\uff01\u201d\u9633\u8d64\u7b26 \u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u662f\u6b66\u6797\u9886\u8896\uff0c\u533a\u533a\u4e5f\u8584\u6709\u5fae\u540d\u3002\u8c01\u90fd\u4e0d\u5fc5\u8ba9\u8c01\uff0c\u540c\u65f6\u53d1\u62db\u5427\uff01\u201d\u50b2\u6c14\u89c1 \u4e8e\u8f9e\u8272\u3002\u7389\u53ec\u9053\uff1a \u3000\u3000\u201c\u597d\uff01\u201d\u53cc\u65b9\u4e00\u4e2a\u76d8\u65cb\uff0c\u5ffd\u5730\u5f7c\u6b64\u90fd\u662f\u4e00\u58f0\u5927\u559d\uff0c\u6325\u638c\u62cd\u51fa\uff0c\u679c\u7136\u662f\u540c\u65f6\u53d1\u62db\uff0c\u96be \u5206\u524d\u540e\u3002 \u3000\u3000\u9633\u8d64\u7b26\u7684\u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\u5df2\u7ec3\u5230\u6700\u540e\u4e00\u91cd\uff0c\u4e00\u638c\u62cd\u51fa\uff0c\u5bd2\u98d9\u5377\u5730\uff0c\u4e24\u8fb9\u68da\u5b50\u91cc\u7684\u4eba \u79bb\u573a\u751a\u8fdc\uff0c\u90fd\u611f\u5230\u51b7\u610f\u6c81\u808c\uff0c\u529f\u529b\u7a0d\u5f31\u7684\uff0c\u7259\u5173\u90fd\u683c\u683c\u4f5c\u54cd\u3002\u4e91\u53ec\u9996\u5f53\u5176\u51b2\uff0c\u5168\u8eab\u88ab\u9634 \u715e\u4e4b\u6c14\u6240\u5305\uff0c\u66f4\u662f\u611f\u5230\u8840\u6db2\u90fd\u4f3c\u4e4e\u8981\u51bb\u7ed3\u8d77\u6765\uff0c\u4f46\u4ed6\u7ec3\u7684\u662f\u7eaf\u9633\u5185\u529f\uff0c\u5374\u4e5f\u8fd8\u7981\u53d7\u5f97\u8d77\u3002 \u3000\u3000\u4e91\u53ec\u7684\u201c\u5927\u529b\u91d1\u521a\u638c\u201d\u5929\u4e0b\u65e0\u53cc\uff0c\u82e5\u5355\u8bba\u638c\u529b\u4e4b\u5a01\u731b\uff0c\u5b5f\u795e\u901a\u590d\u751f\uff0c\u4e5f\u672a\u5fc5\u53ca\u5f97\u4e0a \u4ed6\u3002\u9633\u8d64\u7b26\u867d\u7136\u4ea6\u5df1\u7ec3\u5230\u4e86\u7b2c\u4e5d\u91cd\u7684\u4fee\u7f57\u9634\u715e\u529f\uff0c\u603b\u8fd8\u662f\u4e0d\u53ca\u4ed6\u5f53\u5e74\u7684\u5e08\u5144\u3002\u53cc\u638c\u76f8\u4ea4\uff0c \u201c\u84ec\u201d\u7684\u4e00\u58f0\u5de8\u9707\uff0c\u4e91\u53ec\u7eb9\u4e1d\u672a\u52a8\uff0c\u9633\u8d64\u7b26\u5374\u5df2\u201c\u767b\u3001\u767b\u3001\u767b\u201d\u7684\u9000\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u573a\u4e2d\u5f69\u58f0\u5982\u96f7\uff0c\u6c5f\u5357\u66f4\u662f\u624b\u821e\u8db3\u8e48\u5730\u53eb\u9053\uff1a\u201c\u679c\u7136\u59dc\u662f\u8001\u7684\u8fa3\uff01\u201d\u9633\u8d64\u7b26\u9762\u8272\u94c1\u9752\uff0c \u4e00\u58f0\u4e0d\u54cd\uff0c\u53cd\u624b\u53c8\u662f\u4e00\u638c\u3002\u4e91\u53ec\u6325\u638c\u76f8\u8fce\uff0c\u8fd9\u4e00\u56de\uff0c\u9633\u8d64\u7b26\u53ea\u9000\u4e86\u4e24\u6b65\uff0c \u3000\u3000\u53cc\u65b9\u5404\u4ee5\u5e73\u751f\u7edd\u5b66\u640f\u6597\uff0c\u6bcf\u4e00\u6b21\u638c\u5fc3\u4e00\u78b0\uff0c\u4fbf\u53d1\u51fa\u95f7\u96f7\u4e5f\u4f3c\u7684\u58f0\u97f3\u3002\u7247\u523b\u4e4b\u95f4\uff0c\u53cc \u65b9\u5df2\u786c\u62fc\u4e86\u4e03\u6570\u638c\uff0c\u53ea\u89c1\u5728\u4ed6\u4eec\u5468\u56f4\u6570\u4e08\u65b9\u5706\u4e4b\u5185\uff0c\u7b3c\u7f69\u7740\u4e00\u5c42\u767d\u8499\u8499\u7684\u96fe\u6c14\uff0c\u90a3\u5f53\u7136 \u662f\u56e0\u4e3a\u5468\u56f4\u7684\u7a7a\u6c14\u9aa4\u7136\u53d8\u51b7\u6240\u81f4\uff0c\u9633\u8d64\u7b26\u4fee\u7f57\u9634\u715e\u529f\u7684\u5389\u5bb3\uff0c\u4e8e\u5179\u53ef\u89c1\u3002\u7fa4\u96c4\u90fd\u770b\u5f97\u77a0 \u76ee\u7ed3\u820c\uff0c\u8fde\u559d\u5f69\u4e5f\u5fd8\u8bb0\u4e86\u3002 \u3000\u3000\u518d\u62fc\u6570\u638c\uff0c\u53ea\u89c1\u4e91\u53ec\u5927\u6c57\u6dcb\u6f13\uff0c\u867d\u7136\u8fd8\u672a\u9732\u51fa\u8d25\u8c61\uff0c\u4f46\u6bcf\u6b21\u53cc\u638c\u76f8\u4ea4\uff0c\u5df2\u662f\u4e0d\u80fd\u518d \u628a\u9633\u8d64\u7b26\u8feb\u9000\u4e86\u3002\u539f\u6765\u4e91\u53ec\u7684\u529f\u529b\u867d\u662f\u7a0d\u80dc\u4e00\u7b79\uff0c\u4f46\u4ed6\u8981\u540c\u65f6\u8fd0\u529f\u62b5\u5fa1\u4fb5\u5165\u4f53\u5185\u7684\u5bd2\u6c14\uff0c \u65f6\u95f4\u4e00\u957f\uff0c\u6b64\u6d88\u5f7c\u957f\uff0c\u4e3b\u5ba2\u4e4b\u52bf\uff0c\u4fbf\u5df2\u7ed9\u9633\u8d64\u7b26\u626d\u8f6c\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u573a\u4e2d\u6b66\u5b66\u9ad8\u660e\u4e4b\u58eb\u90fd\u5df2\u770b\u5f97\u51fa\u6765\uff0c\u9633\u8d64\u7b26\u5df2\u662f\u7a33\u64cd\u80dc\u5238\uff0c\u53ea\u5dee\u8fdf\u65e9\u7f62\u4e86\u3002\u6b27\u9633 \u4f2f\u548c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u9633\u5148\u751f\u7684\u4fee\u7f57\u9634\u715e\u529f\u679c\u662f\u6b66\u6797\u7edd\u5b66\uff0c\u540d\u4e0d\u865a\u4f20\uff01\u201d \u3000\u3000\u6c5f\u5357\u542c\u4e86\uff0c\u6c14\u6124\u4e0d\u8fc7\uff0c\u8bf4\u9053\uff1a\u201c\u6d77\u513f\uff0c\u4f60\u53bb\u628a\u4e91\u5e84\u4e3b\u66ff\u4e0b\u6765\uff0c\u53eb\u90a3\u8001\u9b54\u5934\u77e5\u9053\u5389\u5bb3\u3002\u201d \u5374\u4e0d\u89c1\u6c5f\u6d77\u5929\u56de\u7b54\uff0c\u4ed6\u672c\u6765\u662f\u5728\u7236\u4eb2\u8eab\u65c1\u7684\uff0c\u5374\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u6e9c\u5230\u573a\u8fb9\uff0c\u6df7\u5728\u4eba\u4e1b\u4e4b\u4e2d \u89c2\u6218\u4e86\u3002 \u3000\u3000\u5c31\u5728\u6b27\u9633\u4f2f\u548c\u559d\u5f69\u58f0\u4e2d\uff0c\u9633\u8d64\u7b26\u6709\u610f\u5356\u5f04\u795e\u901a\uff0c\u8fd0\u8db3\u4e86\u7b2c\u4e5d\u91cd\u7684\u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\uff0c \u53cc\u638c\u4e00\u9f50\u62cd\u51fa\uff0c\u767b\u65f6\u5377\u8d77\u4e86\u4e00\u80a1\u51b0\u51b7\u523a\u9aa8\u7684\u65cb\u98ce\uff0c\u4f46\u89c1\u767d\u96fe\u8ff7\u6f2b\uff0c\u9ec4\u6c99\u6eda\u6eda\uff0c\u4e24\u4eba\u7684\u8eab \u5f62\uff0c\u90fd\u5df2\u7f69\u6ca1\u5728\u98ce\u6c99\u4e4b\u5185\uff0c\u65c1\u89c2\u7684\u4eba\uff0c\u53ea\u89c1\u4e00\u7247\u6a21\u7cca\u7684\u4eba\u5f71\uff0c\u4e5f\u5206\u4e0d\u51fa\u8c01\u662f\u9633\u8d64\u7b26\uff0c\u8c01 \u662f\u4e91\u53ec\u4e86\u3002 \u3000\u3000\u4e91\u53ec\u88ab\u90a3\u80a1\u51b0\u51b7\u7684\u65cb\u98ce\u538b\u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u5fc3\u5934\u4ea6\u5df2\u662f\u4e00\u7247\u51b0\u51c9\uff0c\u5fc3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6211 \u4eca\u65e5\u7adf\u4e27\u5728\u9633\u8d64\u7b26\u4e4b\u624b\uff01\u201d\u62fc\u4e86\u4e2a\u4e0e\u654c\u5055\u4ea1\u7684\u5ff5\u5934\uff0c\u4e5f\u8fd0\u8db3\u529f\u529b\uff0c\u4e00\u638c\u62cd\u5c06\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u8fd8\u672a\u66fe\u6253\u5230\u9633\u8d64\u7b26\u8eab\u4e0a\uff0c\u5ffd\u542c\u5f97\u9633\u8d64\u7b26\u4e00\u58f0\u5927\u53eb\uff0c\u6574\u4e2a\u8eab\u8eaf\u4f3c\u76ae\u7403\u822c\u5730\u629b\u4e86 \u8d77\u6765\uff0c\u8dcc\u51fa\u4e86\u6570\u4e08\u5f00\u5916\uff0c\u767b\u65f6\u98ce\u5e73\u6c99\u9759\uff0c\u9633\u8d64\u7b26\u54c7\u7684\u4e00\u58f0\u60e8\u53eb\uff0c\u5410\u51fa\u4e86\u4e00\u5927\u53e3\u9c9c\u8840\uff0c\u6323 \u624e\u8d77\u6765\uff0c\u5934\u4e5f\u4e0d\u56de\u5730\u8dd1\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u5927\u51fa\u4f17\u4eba\u610f\u5916\uff0c\u6b27\u9633\u4f2f\u548c\u8fd9\u8fb9\u7684\u4eba\u9a87\u7136\u5931\u8272\uff0c\u5446\u7740\u6728\u9e21\u3002\u4e91\u53ec\u8fd9\u8fb9\u7684\u4eba\u4e5f\u90fd \u5446\u4e86\u4e00\u5446\uff0c\u8fd9\u624d\u7206\u53d1\u51fa\u6625\u96f7\u4e00\u822c\u7684\u559d\u5f69\u3002 \u3000\u3000\u4e0d\u4f46\u4f17\u4eba\u611f\u5230\u610f\u5916\uff0c\u4e91\u53ec\u5728\u5f69\u58f0\u4e4b\u4e2d\uff0c\u4e5f\u81ea\u89c9\u5f97\u4e00\u7247\u832b\u7136\uff01 \u3000\u3000\u4ed6\u521a\u624d\u867d\u8bf4\u662f\u62fc\u4e86\u4e0e\u654c\u5055\u4ea1\u7684\u5ff5\u5934\uff0c\u5176\u5b9e\u81ea\u5df1\u4e5f\u5e76\u65e0\u628a\u63e1\uff0c\u56e0\u4e3a\u90a3\u65f6\u4ed6\u7684\u529f\u529b\u5df2\u662f \u5927\u5927\u51cf\u5f31\uff0c\u4e0d\u53ca\u5bf9\u65b9\u4e86\u3002\u800c\u4e14\u90a3\u4e00\u638c\u4e5f\u672a\u66fe\u6253\u5230\u9633\u8d64\u7b26\u8eab\u4e0a\uff0c\u82e5\u8bf4\u53ea\u51ed\u5288\u7a7a\u638c\u529b\uff0c\u5c31\u80fd \u5c06\u9633\u8d64\u7b26\u9707\u5f97\u91cd\u4f24\uff0c\u90a3\u662f\u4e91\u53ec\u4e5f\u4e0d\u6562\u76f8\u4fe1\u7684\u3002 \u3000\u3000\u539f\u6765\u8fd9\u662f\u6c5f\u6d77\u592d\u5728\u573a\u8fb9\u6697\u52a9\u4e86\u4e91\u53ec\u4e00\u201c\u6307\u201d\u4e4b\u529b\u3002\u4ed6\u8d81\u7740\u98ce\u6c99\u8ff7\u7740\u4f17\u4eba\u89c6\u7ebf\u4e4b\u9645\uff0c \u5077\u5077\u4ee5\u201c\u9694\u7a7a\u70b9\u7a74\u201d\u7684\u7edd\u9876\u795e\u529f\uff0c\u5411\u9633\u8d64\u7b26\u9065\u6233\u4e00\u6307\u3002\u6c5f\u6d77\u5929\u7684\u65e0\u5f62\u7f61\u6c14\u65e9\u5df2\u7ec3\u6210\uff0c\u8fd9 \u65f6\u4e1a\u5df2\u53ef\u4ee5\u8fd0\u7528\u81ea\u5982\uff0c\u4e00\u6307\u6233\u51fa\uff0c\u4e00\u6761\u7ec6\u5982\u6e38\u4e1d\u7684\u65e0\u5f62\u7f61\u6c14\u95ea\u7535\u822c\u523a\u8fdb\u4e86\u9633\u8d64\u7b26\u638c\u5fc3\u7684 \u201c\u52b3\u5bab\u7a74\u201d\u3002\u9633\u8d64\u7b26\u51dd\u805a\u5728\u638c\u5fc3\u7684\u9634\u715e\u4e4b\u6c14\u767b\u65f6\u6563\u4e86\uff0c\u4e00\u70b9\u4e5f\u53d1\u6325\u4e0d\u51fa\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u9633\u8d64\u7b26\u5374\u7684\u786e\u662f\u88ab\u4e91\u53ec\u7684\u91d1\u521a\u638c\u529b\u629b\u8d77\u800c\u4e14\u9707\u4f24\u7684\uff0c\u56e0\u4e3a\u51d1\u5de7\u5728\u9093\u4e00\u5239\u90a3\uff0c \u4ed6\u7684\u4fee\u7f57\u9634\u715e\u529f\u5df2\u88ab\u6c5f\u6d77\u5929\u6240\u7834\uff0c\u5341\u6210\u529f\u529b\u53ea\u5269\u4e0b\u4e00\u6210\uff0c\u5f53\u7136\u5c31\u62b5\u5fa1\u4e0d\u4f4f\u4e91\u53ec\u7684\u91d1\u521a\u638c \u529b\u4e86\u3002\u65c1\u89c2\u7684\u4eba\u90fd\u6ce8\u76ee\u573a\u4e2d\u7684\u6076\u6597\uff0c\u83ab\u8bf4\u6839\u672c\u5c31\u6ca1\u4eba\u53d1\u73b0\u6c5f\u6d77\u5929\u7684\u52a8\u4f5c\uff0c\u5373\u7b97\u6709\u4eba\u770b\u89c1 \u4ed6\u9065\u6233\u4e00\u6307\uff0c\u4e5f\u53ea\u5f53\u4ed6\u662f\u4e00\u79cd\u65e0\u610f\u8bc6\u7684\u52a8\u4f5c\uff0c\u51b3\u8ba1\u4e0d\u4f1a\u60f3\u8c61\u5230\u6709\u8fd9\u7b49\u795e\u5947\u5965\u5999\u7684\u529f\u592b\u3002 \u573a\u4e2d\u5c06\u8fd1\u5343\u4eba\uff0c\u53ea\u6709\u4e91\u53ec\u4e00\u4eba\u8d77\u4e86\u601d\u7591\uff0c\u9690\u9690\u60f3\u5230\u662f\u6709\u9ad8\u624b\u5728\u65c1\u76f8\u52a9\u3002 \u3000\u3000\u9633\u8d64\u7b26\u4e0e\u91d1\u65e5\u5355\u662f\u6b27\u9633\u4f2f\u548c\u8fd9\u8fb9\u9876\u513f\u5c16\u513f\u7684\u4eba\u7269\uff0c\u4ed6\u4eec\u4e8c\u4eba\u76f8\u7ee7\u8d25\u8d70\uff0c\u81ea\u662f\u4eba\u4eba\u60f6 \u6050\uff0c\u8fd8\u6709\u8c01\u6562\u51fa\u5934\uff1f \u3000\u3000\u4e10\u5e2e\u7684\u526f\u5e2e\u4e3b\u5168\u7956\u5fb7\u8bf4\uff1a\u201c\u6b27\u9633\u5e84\u4e3b\uff0c\u4e8b\u5230\u5982\u4eca\uff0c\u4f60\u8fd8\u8981\u5305\u5e87\u90a3\u59d3\u53f6\u7684\u5c0f\u8d3c\u4e48\uff1f\u201d \u4e91\u53ec\u9053\uff1a\u201c\u6b27\u9633\u5e84\u4e3b\uff0c\u53ea\u8981\u4f60\u628a\u59d3\u53f6\u7684\u5c0f\u8d3c\u4ea4\u51fa\u6765\uff0c\u5c31\u6ca1\u4f60\u4eec\u7684\u4e8b\u3002\u201d \u3000\u3000\u6b27\u9633\u4f2f\u548c\u9762\u8272\u94c1\u9752\uff0c\u51fa\u573a\u8bf4\u9053\uff1a\u201c\u4e91\u5e84\u4e3b\uff0c\u591a\u8c22\u4f60\u7f51\u5f00\u4e00\u9762\u3002 \u3000\u3000\u4f46\u8981\u4eba\u4e48\uff0c\u53ef\u662f\u6ca1\u6709\uff01\u4f60\u52a8\u624b\u5427\uff01\u201d\u6b27\u9633\u4ef2\u548c\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u51b2\u9704\u662f\u6211\u7684\u5973\u5a7f\uff0c\u4ed6 \u60f9\u51fa\u7684\u7978\uff0c\u7406\u8be5\u7531\u6211\u62c5\u627f\u3002\u4e91\u53ec\uff0c\u6211\u592b\u5987\u4fe9\u9886\u6559\u4f60\u7684\u9ad8\u62db\uff01\u201d\u6b27\u9633\u4f2f\u548c\u9053\uff1a\u201c\u4e8c\u5f1f\uff0c\u4f60 \u4e0e\u5f1f\u5987\u9000\u5f00\uff0c\u6211\u662f\u4e00\u5bb6\u4e4b\u4e3b\uff0c\u4e8b\u60c5\u8fd8\u8f6e\u4e0d\u5230\u4f60\u7ba1\uff01\u201d \u3000\u3000\u4f2f\u548c\u4ef2\u548c\u4e24\u5144\u5f1f\u4e89\u7740\u51fa\u5934\uff0c\u4e3a\u7684\u90fd\u662f\u60f3\u4fdd\u5168\u5bf9\u65b9\u3002\u9633\u8d64\u7b26\u8fd9\u7b49\u6b66\u529f\uff0c\u90fd\u8d25\u5728\u4e91\u53ec\u638c \u4e0b\uff0c\u4ed6\u4eec\u5144\u5f1f\u660e\u77e5\uff0c\u5373\u7b97\u5408\u4e09\u4eba\u4e4b\u529b\uff0c\u4e0e\u4e91\u53ec\u52a8\u624b\uff0c\u4e5f\u96be\u4fdd\u5f97\u6027\u547d\uff0c\u6545\u6b64\u4ed6\u4eec\u90fd\u4e89\u7740\u628a \u4e8b\u60c5\u63fd\u5230\u81ea\u5df1\u7684\u8eab\u4e0a\uff0c\u514d\u5f97\u7275\u8fde\u4e86\u5144\u5f1f\u3002 \u3000\u3000\u534e\u5929\u98ce\u5ffd\u5730\u51fa\u573a\u8bf4\u9053\uff1a\u201c\u6b27\u9633\u8001\u4e8c\uff0c\u54b1\u4eec\u8fd8\u6709\u4e00\u6bb5\u8fc7\u8282\u5462\uff01\u201d\u539f\u6765\u534e\u5929\u98ce\u89c1\u6b64\u60c5\u5f62\uff0c \u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u867d\u662f\u90aa\u6d3e\u9b54\u5934\uff0c\u5144\u5f1f\u4e4b\u95f4\uff0c\u5012\u5f88\u6709\u4e49\u6c14\u3002\u7f62\uff0c\u7f62\uff0c\u5c31\u770b\u5728\u4ed6\u4eec\u8fd9\u70b9\u53ef \u53d6\u4e4b\u5904\uff0c\u6211\u5012\u8981\u8bbe\u6cd5\u4fdd\u5168\u4ed6\u4eec\u4e86\u3002\u201d\u4ed6\u5df2\u6253\u5b9a\u4e3b\u610f\uff0c\u52a8\u624b\u4e4b\u65f6\uff0c\u6697\u6697\u7559\u60c5\uff0c\u4e0d\u4f24\u4ed6\u4eec\u6027 \u547d\u3002 \u3000\u3000\u6b27\u9633\u4ef2\u548c\u592b\u5987\u5374\u4e0d\u77e5\u534e\u5929\u98ce\u7684\u5fc3\u610f\uff0c\u4ed6\u4eec\u66fe\u662f\u534e\u5929\u98ce\u624b\u4e0b\u8d25\u5c06\uff0c\u60c5\u77e5\u8ba8\u4e0d\u4e86\u597d\uff0c\u4f46 \u534e\u5929\u98ce\u65e2\u5df2\u51fa\u8a00\u6311\u6218\uff0c\u4ed6\u4eec\u81ea\u662f\u4e0d\u80fd\u907f\u5f00\u3002\u5f53\u4e0b\u6b27\u9633\u4ef2\u548c\u60e8\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u505a \u5144\u5f1f\u7684\u5148\u8d70\u4e00\u6b65\u4e86\u3002\u534e\u5929\u98ce\uff0c\u6765\uff0c\u6765\uff0c\u6765\uff01\u54b1\u4eec\u5c31\u5148\u6597\u4e00\u573a\u3002\u201d \u3000\u3000\u773c\u770b\u5251\u62d4\u5f29\u5f20\uff0c\u5373\u5c06\u52a8\u624b\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u5b9b\u82e5\u9f99\u541f\uff0c\u4e00\u6761\u4eba\u5f71\uff0c\u75be\u9a70\u800c\u6765\uff0c\u5728 \u534e\u5929\u98ce\u4e0e\u6b27\u9633\u4ef2\u548c\u4e4b\u95f4\u505c\u4e0b\uff0c\u6717\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e14\u6162\u52a8\u624b\uff0c\u4ffa\u53f6\u51b2\u9704\u6765\u4e86\uff01\u201d \u3000\u3000\u767b\u65f6\u5168\u573a\u8038\u52a8\uff0c\u97e9\u7487\u592b\u59bb\u4e00\u770b\uff0c\u8ba4\u5f97\u679c\u7136\u662f\u53f6\u51b2\u9704\uff0c\u53cc\u53cc\u51fa\u573a\uff0c\u622a\u4f4f\u4ed6\u7684\u540e\u8def\u3002\u53eb \u9053\uff1a\u201c\u597d\u5440\uff0c\u7b97\u4f60\u6709\u79cd\uff0c\u7adf\u6562\u51fa\u5934\uff0c\u9547\u8fdc\u9556\u5c40\u7684\u5e10\uff0c\u5148\u4e0e\u4f60\u7b97\u7b97\u3002\u201d\u6c5f\u6d77\u5929\u5fc3\u91cc\u5fd0\u5fd1\u4e0d \u5b89\uff0c\u4e0d\u77e5\u5982\u4f55\u6536\u62fe\u3002\u60f3\u9053\uff1a\u201c\u6b27\u9633\u5a49\u5374\u600e\u4e48\u4e0d\u89c1\uff1f\u201d \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u9762\u8272\u4e00\u53d8\uff0c\u65a5\u9053\uff1a\u201c\u53f6\u51b2\u9704\uff0c\u4f60\u8fd8\u8981\u4e0d\u8981\u8138\uff0c\u6211\u5df2\u5c06\u4f60\u8d76\u51fa\u53bb\u4e86\u3002\u4f60\u8fd8\u56de \u6765\u4f5c\u751a\uff1f\u5feb\u6eda\uff0c\u5feb\u6eda\uff01\u201d \u3000\u3000\u53f6\u51b2\u9704\u7b11\u9053\uff1a\u201c\u6211\u65e9\u6599\u5230\u6709\u4eca\u65e5\u4e4b\u4e8b\u4e86\u3002\u4f60\u4e0d\u8ba4\u6211\u4f5c\u5973\u5a7f\uff0c\u4e5f\u65e0\u975e\u662f\u4e3a\u4e86\u4eca\u65e5\u4e4b\u4e8b\u3002 \u4f46\u5927\u4e08\u592b\uff0c\u7537\u5b50\u6c49\uff0c\u4e00\u4eba\u505a\u4e8b\u4e00\u4eba\u5f53\uff0c\u5c82\u80fd\u8fde\u7d2f\u5c40\u5916\u4e4b\u4eba\u3002\u5988\uff0c\u4f60\u9000\u4e0b\u53bb\u5427\uff01\u201d \u3000\u3000\u4e91\u53ec\u8bf4\u9053\uff1a\u201c\u597d\u3002\u4f60\u8bf4\u5f97\u6709\u5fd7\u6c14\u3002\u8001\u592b\u5c31\u6210\u5168\u4f60\u5427\uff01\u6765\uff0c\u6765\uff0c\u6765\uff0c\u53ea\u8981\u4f60\u5728\u6211\u638c\u5e95 \u8fc7\u5f97\u4e09\u62db\uff0c\u4f60\u4f24\u4e86\u6211\u513f\u5973\u4e4b\u4ec7\uff0c\u5c31\u7b97\u4e86\u7ed3\uff01\u201d\u539f\u6765\u4e91\u53ec\u662f\u6015\u97e9\u7487\u592b\u59bb\u4e0d\u654c\uff0c\u6545\u6b64\u8981\u5148\u51fa \u573a\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5bfb\u601d\uff1a\u201c\u4e91\u8001\u82f1\u96c4\u7684\u5927\u529b\u91d1\u521a\u638c\u5929\u4e0b\u65e0\u654c\uff0c\u4f46\u53f6\u5927\u54e5\u5bf9\u4ed6\u4e09\u638c\uff0c\u6599\u60f3\u8fd8\u4e0d\u81f3 \u4e8e\u6709\u4f55\u5927\u788d\uff1f\u55ef\uff0c\u6211\u660e\u767d\u4e86\uff0c\u4e91\u8001\u82f1\u96c4\u5927\u7ea6\u662f\u60f3\u4ee4\u4ed6\u53d7\u4e00\u70b9\u4f24\uff0c\u51fa\u53e3\u6028\u6c14\uff0c\u4e5f\u5c31\u7b97\u4e86\u3002\u201d \u3000\u3000\u4e91\u53ec\u5fb7\u9ad8\u671b\u91cd\uff0c\u97e9\u7487\u5927\u59bb\u4e00\u5411\u670d\u4ed6\uff0c\u4f46\u8fd9\u6b21\u4ed6\u4eec\u5374\u4e0d\u80af\u9000\u8ba9\u3002\u97e9\u4e8c\u5a18\u9996\u5148\u53d1\u8bdd\uff1a \u201c\u4e91\u5e84\u4e3b\uff0c\u522b\u7684\u4e8b\u6211\u542c\u4f60\u7684\uff0c\u8fd9\u4e00\u56de\u4f60\u53ef\u5f97\u5148\u8ba9\u6211\u4eec\uff0c\u6211\u8fd9\u53cc\u817f\u88ab\u8fd9\u5c0f\u8d3c\u5bb3\u5f97\u6b8b\u5e9f\uff0c\u6211 \u62fc\u7740\u518d\u628a\u6027\u547d\u4ea4\u4ed8\u4e0e\u4ed6\uff0c\u4e5f\u5f97\u4e0e\u4ed6\u5148\u62fc\u4e00\u573a\uff01\u201d\u97e9\u7487\u4e5f\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u9547\u8fdc\u9556\u5c40\u7684\u4e09\u5341 \u516d\u6761\u547d\u503a\uff0c\u8fd9\u51a4\u4ec7\u53ef\u6bd4\u4f60\u7684\u5927\u5f97\u591a\uff0c\u4f60\u5c31\u5148\u8ba9\u6211\u4eec\u4e00\u573a\u5427\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u97e9\u7487\u592b\u59bb\u610f\u6b32\u4e0e\u53f6\u5927\u54e5\u6027\u547d\u76f8\u535a\uff0c\u8fd9\u53ef\u5982\u4f55\u662f\u597d\uff1f\u201d \u8981\u77e5\u97e9\u7487\u592b\u59bb\u7684\u6b66\u529f\u867d\u7136\u8fdc\u900a\u53f6\u51b2\u9704\u4e00\u7b79\uff0c\u4f46\u4ed6\u4eec\u7684\u94c1\u9e2f\u9e33\u6674\u5668\u5341\u5206\u5389\u5bb3\uff0c\u8ba4\u771f\u6027\u547d\u76f8 \u640f\uff0c\u53ea\u6015\u8981\u4e24\u8d25\u4ff1\u4f24\u3002\u800c\u4e14\u8bba\u9053\u7406\u4e43\u662f\u53f6\u51b2\u9704\u5bf9\u4ed6\u4eec\u4e0d\u4f4f\uff0c\u6c5f\u6d77\u5927\u51b3\u4e0d\u80fd\u5728\u4ed6\u4eec\u6027\u547d\u76f8 \u640f\u4e4b\u4e2d\u6697\u52a9\u53f6\u51b2\u9704\u3002 \u3000\u3000\u4e91\u53ec\u8e0c\u8e87\u672a\u51b3\uff0c\u6c5f\u6d77\u5929\u4e5f\u6b63\u5728\u5fd1\u5fd0\u4e0d\u5b89\uff0c\u53f6\u51b2\u9704\u5f53\u4e2d\u4e00\u7ad9\uff0c\u5ffd\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d \u4e0d\u5fc5\u4e89\u8bba\u4e86\uff0c\u6211\u4e0d\u4e0e\u4f60\u4eec\u52a8\u624b\u3002\u201d\u4e91\u53ec\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u4e0e\u6211\u4eec\u52a8\u624b\uff0c\u90a3\u4f60\u6765\u4f5c\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u633a\u8d77\u80f8\u812f\uff0c\u4ef0\u5929\u4e00\u7b11\uff0c\u8fd9\u4e00\u7b11\u6709\u51e0\u5206\u51c4\u51c9\u4e5f\u5e26\u7740\u51e0\u5206\u50b2\u6c14\uff0c\u968f\u5373\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u6740\u4eba\u507f\u547d\uff0c\u6b20\u503a\u8fd8\u94b1\uff0c\u6211\u79cd\u4e0b\u6076\u56e0\uff0c\u5f53\u53d7\u6076\u62a5\uff0c\u4eca\u65e5\u5230\u6b64\uff0c\u4e43\u662f\u4e3a\u8fd8\u503a\u800c\u6765\u3001\u4efb\u51ed\u4f60 \u4eec\u5982\u4f55\u62a5\u590d\uff0c\u5343\u5200\u4e07\u5250\uff0c\u6211\u53f6\u51b2\u9704\u4e5f\u51b3\u4e0d\u6297\u62d2\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u5168\u573a\u9759\u9ed8\u65e0\u58f0\uff0c\u672c\u6765\u5927\u5bb6\u90fd\u4ee5\u4e3a\u53f6\u51b2\u9704\u4e00\u6765\uff0c\u5b9a\u5c06\u6709\u4e00\u573a\u6076\u6597\uff0c\u54ea\u77e5\u4ed6 \u7adf\u662f\u4fef\u9996\u8d34\u8033\uff0c\u7518\u613f\u53d7\u622e\uff0c\u4eba\u4eba\u90fd\u611f\u5230\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\u3002\u5c11\u6797\u5bfa\u5341\u516b\u7f57\u6c49\u4e4b\u9996\u7684\u5927\u60b2\u7985\u5e08 \u5408\u4ec0\u5ff5\u9053\uff1a\u201c\u5584\u54c9\uff0c\u5584\u54c9\uff01\u653e\u4e0b\u5c60\u5200\uff0c\u7acb\u5730\u6210\u4f5b\uff01\u201d \u3000\u3000\u4e91\u53ec\u795e\u8272\u9eef\u7136\uff0c\u9000\u540e\u51e0\u6b65\uff0c\u97e9\u7487\u592b\u59bb\u5374\u4e00\u8dc3\u800c\u524d\uff0c\u4e00\u4eba\u4e00\u8fb9\uff0c\u6267\u7740\u53f6\u51b2\u9704\u7684\u53cc\u81c2\uff0c \u97e9\u7487\u5c06\u53f6\u51b2\u9704\u690e\u4e0a\u4e24\u6b65\uff0c\u9762\u5411\u7fa4\u96c4\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u53f6\u51b2\u9704\uff0c\u4f60\u4e0d\u6127\u662f\u82f1\u96c4\u672c\u8272\uff0c\u97e9\u67d0\u4eca \u65e5\u4ea4\u4e86\u4f60\u8fd9\u4f4d\u670b\u53cb\u4e86\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u5728\u6b22\u559c\uff0c\u54ea\u77e5\u97e9\u7487\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4f46\u6211\u4e0e\u4f60\u8840\u6d77\u6df1\u4ec7\uff0c\u9547\u8fdc\u9547\u5c40\u7684\u4e09\u5341\u516d\u6761 \u6027\u547d\uff0c\u6211\u82e5\u4e0d\u7d22\u8fd8\uff0c\u96be\u4ee5\u5bf9\u6211\u8fd9\u73ed\u5144\u5f1f\u7684\u51a4\u9b42\u4e8e\u5730\u4e0b\uff0c\u53f6\u670b\u53cb\uff0c\u4f60\u5148\u8d70\u4e00\u6b65\uff0c\u97e9\u67d0\u4e5f\u8ddf \u7740\u966a\u4f60\uff0c\u8fd9\u603b\u5bf9\u5f97\u4f4f\u4f60\u4e86\u5427\uff01\u8001\u4f34\u513f\uff0c\u4f60\u8bf4\u600e\u6837\uff1f\u201d\u97e9\u4e8c\u72fc\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u8be5\u8fd9 \u6837\u3002\u62a5\u4ec7\u4e4b\u540e\uff0c\u767e\u4e8b\u4ff1\u4e86\uff0c\u8fd8\u7559\u5728\u4eba\u4e16\u505a\u4ec0\u4e48\uff1f\u53f6\u670b\u53cb\uff0c\u6211\u548c\u5f53\u5bb6\u7684\u90fd\u966a\u4f60\uff0c\u4f60\u4e5f\u53ef\u4ee5 \u6b7b\u800c\u65e0\u6028\u4e86\u3002\u201d\u539f\u6765\u97e9\u7487\u592b\u59bb\u65e9\u5df2\u6253\u5b9a\u4e3b\u610f\uff0c\u5728\u6740\u4e86\u53f6\u51b2\u9704\u4e4b\u540e\uff0c\u5373\u4ee5\u8eab\u76f8\u6b89\u3002\u56e0\u4e3a\u53f6 \u51b2\u9704\u65e2\u7136\u6177\u6168\u5c31\u622e\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u80af\u5931\u4e86\u597d\u6c49\u672c\u8272\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u67c4\u957f\u5200\uff0c\u4e00\u67c4\u77ed\u5200\uff0c\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u4e91\u53ec\u53eb\u9053\uff1a\u201c\u97e9\u4e8c\u54e5\uff0c\u4e0d\u53ef\uff01\u201d\u97e9\u7487\u7684\u957f\u5200 \u5728\u534a\u7a7a\u4e2d\u505c\u4e86\u4e00\u4e0b\uff0c\u4f46\u97e9\u4e8c\u5a18\u7684\u77ed\u5200\u5374\u4ecd\u7136\u5bf9\u51c6\u53f6\u51b2\u9704\u7684\u80f8\u819b\u63d2\u4e86\u4e0b\u53bb\uff01\u4e91\u53ec\u8981\u60f3\u963b\u62e6\uff0c \u4e5f\u6765\u4e0d\u53ca\u4e86\uff01\u6b63\u662f\u3002 \u3000\u3000\u8840\u503a\u8840\u507f\u65e0\u53ef\u6055\uff0c\u54ea\u77e5\u5185\u91cc\u6709\u56e0\u7531\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u95ee\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u56db\u5341\u516d\u56de\u3000\u6b32\u8d4e\u524d\u5b7d\u6765\u820d\u547d\u3000\u8ba4\u6e05\u9996\u6076\u89e3\u4ec7\u51a4 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u56db\u5341\u516d\u56de\u3000\u6b32\u8d4e\u524d\u5b7d\u6765\u820d\u547d\u3000\u8ba4\u6e05\u9996\u6076\u89e3\u4ec7\u51a4 \u3000\u3000\u5c31\u5728\u97e9\u4e8c\u5a18\u7684\u77ed\u5200\u8ddd\u79bb\u53f6\u51b2\u9704\u7684\u80f8\u53e3\u8fd8\u4e0d\u5230\u534a\u5bf8\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u6025\u4fc3\u5cfb\u5ced\u7684\u58f0\u97f3 \u53eb\u9053\uff1a\u201c\u4e14\u6162\uff01\u4ed6\u4e0d\u662f\u53f6\u51b2\u9704\uff0c\u6211\u624d\u662f\u53f6\u51b2\u9704\uff01\u201d\u97e9\u4e8c\u5a18\u542c\u5f97\u8fd9\u6837\u53e4\u602a\u7684\u8bf4\u8bdd\uff0c\u4e0d\u7531\u5f97 \u628a\u77ed\u5200\u7684\u53bb\u52bf\u786c\u751f\u751f\u715e\u4f4f\uff0c\u4e24\u5927\u59bb\u62ac\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u4e00\u4e2a\u5c11\u5e74\uff0c\u6b63\u5728\u5411\u4ed6\u4eec\u8dd1\u6765\u3002 \u3000\u3000\u76f8\u8c8c\u548c\u53f6\u51b2\u9704\u5341\u5206\u76f8\u4f3c\uff0c\u82e5\u4e0d\u662f\u8863\u7740\u4e0d\u540c\uff0c\u5b9e\u96be\u5206\u8fa8\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u677e\u4e86\u53e3\u6c14\uff0c\u539f\u6765\u4ed6\u6b63\u51c6\u5907\u5728\u97e9\u4e8c\u5a18\u77ed\u5200\u523a\u4e0b\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u4fbf\u53d1\u51fa\u65e0\u5f62\u7f61\u6c14\u70b9 \u97e9\u4e8c\u5a18\u7684\u7a74\u9053\uff0c\u5373\u4f7f\u662f\u8981\u4ee4\u97e9\u4e8c\u5a18\u53d7\u4e00\u70b9\u4f24\uff0c\u90a3\u4e5f\u987e\u4e0d\u5f97\u4e86\u3002\u5982\u4eca\u5510\u52aa\u73e0\u7a46\u5df2\u7ecf\u6765\u5230\uff0c \u4ed6\u5df2\u65e0\u9700\u7528\u8fd9\u4e00\u7740\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u8fd9\u4e00\u51fa\u73b0\uff0c\u5df2\u7ecf\u662f\u4ee4\u5168\u573a\u60ca\u8be7\uff0c\u4f46\u8fd8\u6709\u4ee4\u5f97\u7fa4\u96c4\u8be7\u5f02\u7684\u4e8b\u60c5\u662f\uff0c\u5728\u5510\u52aa\u73e0 \u7a46\u80cc\u540e\uff0c\u8fd8\u6709\u4e09\u4e2a\u4eba\u7d27\u7d27\u76f8\u968f\uff0c\u4e00\u4e2a\u662f\u4e91\u53ec\u7684\u5973\u513f\u4e91\u58c1\uff0c\u5979\u8863\u886b\u7834\u70c2\uff0c\u9762\u6709\u8840\u75d5\uff0c\u4f3c\u4e4e \u662f\u521a\u521a\u548c\u4eba\u6253\u4e86\u4e00\u67b6\uff1b\u4e00\u4e2a\u662f\u4e91\u53ec\u7684\u5927\u5f1f\u5b50\u5b57\u6587\u6717\uff0c\u4ed6\u53f3\u624b\u62d6\u7740\u4e00\u4e2a\u5987\u4eba\uff0c\u7ea6\u6709\u4e09\u5341\u591a \u5c81\uff0c\u59ff\u5bb9\u5996\u51b6\uff0c\u8f6f\u7ef5\u7ef5\u7684\u8ba9\u5b57\u6587\u6717\u62d6\u7740\u5979\u8d70\uff0c\u4f3c\u4e4e\u662f\u5df2\u88ab\u70b9\u4e86\u7a74\u9053\u3002 \u3000\u3000\u4e91\u53ec\u5927\u4e3a\u5403\u60ca\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u58c1\u513f\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u662f\u4ed6\u6253\u4f24\u4e86\u4f60\uff1f\u201d\u624b\u6307\u6307\u7740\u5510 \u52aa\u73e0\u7a46\u3002\u4e91\u58c1\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u662f\u8fd9\u5973\u8d3c\u8981\u6765\u5bb3\u6211\uff0c\u662f\u4ed6\uff0c\u662f\u4ed6\u6551\u4e86\u6211\u3002\u201d\u4e91\u58c1\u8d77\u521d\u4e5f\u628a\u5510 \u52aa\u73e0\u7a46\u5f53\u4f5c\u53f6\u51b2\u9704\uff0c\u5982\u4eca\u89c1\u573a\u4e2d\u53c8\u6709\u4e00\u4e2a\u53f6\u51b2\u9704\uff0c\u5fc3\u91cc\u4e5f\u751a\u60f6\u60d1\uff0c\u4f46\u5510\u52aa\u73e0\u7a46\u66fe\u7ecf\u6551\u5979\uff0c \u5979\u8fd8\u662f\u8bf4\u4e86\u5b9e\u8bf7\u3002 \u3000\u3000\u5b57\u6587\u6717\u8865\u5145\u8bf4\u9053\uff1a\u201c\u6211\u548c\u5e08\u59b9\u5728\u95e8\u53e3\u63a5\u5f85\u5ba2\u4eba\uff0c\u8fd9\u5973\u8d3c\u7a81\u5982\u5176\u6765\uff0c\u4e00\u51fa\u624b\u5c31\u64d2\u4e86\u5e08 \u59b9\uff0c\u6211\u4e5f\u88ab\u5979\u70b9\u4e86\u7a74\u9053\u3002\u5e78\u4e8f\u8fd9\u4f4d\u82f1\u96c4\u4e5f\u6070\u6070\u6765\u5230\uff0c\u95ea\u7535\u822c\u5236\u4f0f\u4e86\u8fd9\u4e2a\u5973\u8d3c\uff0c\u8fd9\u5973\u8d3c\u624d \u4e0d\u53ca\u4f24\u5bb3\u5e08\u59b9\uff0c\u4ed6\u64d2\u83b7\u4e86\u8fd9\u4e2a\u5973\u8d3c\uff0c\u53c8\u89e3\u5f00\u4e86\u6211\u7684\u7a74\u9053\uff0c\u5c06\u5973\u8d3c\u4ea4\u4e86\u7ed9\u6211\u3002\u201d \u3000\u3000\u5f53\u5e74\u9547\u8fdc\u9556\u5c40\u5728\u9752\u6d77\u9102\u5c14\u6c81\u65d7\u88ab\u52a8\uff0c\u532a\u9996\u662f\u4e2a\u5973\u8d3c\uff0c\u9556\u5c40\u7684\u4eba\u5168\u6570\u88ab\u4fd8\uff0c\u53ea\u6709\u4e24\u4e2a \u9556\u5e08\u5f97\u53f6\u51b2\u9704\u8bf4\u60c5\uff0c\u5f97\u4ee5\u751f\u8fd8\uff0c\u5176\u4ed6\u7684\u4eba\u5168\u88ab\u6740\u6389\uff0c\u8fd9\u5c31\u662f\u9547\u8fdc\u9556\u5c40\u4e09\u5341\u516d\u6761\u547d\u6848\u7684\u7531 \u6765\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u5e78\u5f97\u751f\u8fd8\u7684\u9556\u5e08\uff0c\u8fd9\u6b21\u4e5f\u968f\u4e86\u603b\u9556\u5934\u97e9\u7487\u6765\u6b64\uff0c\u6b63\u5728\u573a\u4e2d\uff0c\u5ffd\u5730\u8d70\u51fa\u6765\u53eb\u9053\uff1a \u201c\u97e9\u603b\u9556\u5934\uff0c\u5f53\u5e74\u6740\u5bb3\u54b1\u4eec\u5f1f\u5144\u7684\u6b63\u662f\u8fd9\u4e2a\u5973\u8d3c\uff01\u201d\u97e9\u7487\u9053\uff1a\u201c\u5404\u4f4d\u82f1\u96c4\uff0c\u6709\u8c01\u8ba4\u5f97\u8fd9 \u4e2a\u5973\u8d3c\u4e48\uff1f\u201c\u573a\u4e2d\u201c\u6d77\u9633\u5e2e\u201d\u7684\u5e2e\u4e3b\u5bb4\u6e90\u8bf4\u9053\uff1a\u201c\u6211\u8ba4\u5f97\u5979\uff0c\u5979\u662f\u5929\u9b54\u6559\u7684\u9999\u4e3b\u4e4b\u4e00\u532a \u53f7\u4e5d\u5c3e\u5996\u72d0\u7684\u7a46\u4e5d\u5a18\u3002\u201d\u6d77\u9633\u5e2e\u662f\u9760\u8fd0\u79c1\u76d0\u4e3a\u751f\u7684\uff0c\u6240\u4ee5\u5bb4\u6e90\u8ba4\u5f97\u8bb8\u591a\u90aa\u6d3e\u4e2d\u4eba\u3002 \u3000\u3000\u97e9\u7487\u8ff7\u60d1\u6781\u4e86\uff0c\u5728\u6b64\u4e4b\u524d\uff0c\u4ed6\u4e00\u76f4\u628a\u53f6\u51b2\u9704\u5f53\u4f5c\u8fd9\u5973\u8d3c\u7684\u540c\u515a\uff0c\u56e0\u800c\u624d\u628a\u9547\u8fdc\u9556\u5c40 \u7684\u4e09\u5341\u516d\u6761\u547d\u503a\u4e5f\u7b97\u5728\u4ed6\u7684\u8eab\u4e0a\u4e86\u3002\u54ea\u77e5\u5982\u4eca\u5ffd\u5730\u53c8\u8dd1\u6765\u4e86\u4e00\u4e2a\u53eb\u53f6\u51b2\u9704\u7684\u4eba\uff0c\u5374\u64d2\u4e86 \u8fd9\u4e2a\u5973\u8d3c\uff0c\u53c8\u6551\u4e86\u4e91\u58c1\u3002\u97e9\u7487\u77aa\u7740\u773c\u775b\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u7a76\u7adf\u8c01\u662f\u53f6\u51b2\u9704\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u4e0e\u53f6\u51b2\u9704\u9f50\u58f0\u7b54\u9053\uff1a\u201c\u662f\u6211\uff01\u201d\u4ed6\u4eec\u4e8c\u4eba\u76f8\u8c8c\u867d\u7136\u6781\u4e4b\u76f8\u4f3c\uff0c\u4f46\u7a76\u7adf\u6709\u4e9b \u5dee\u5f02\uff0c\u58f0\u97f3\u795e\u6c14\u66f4\u662f\u6709\u6240\u4e0d\u540c\u3002\u97e9\u4e8c\u5a18\u66fe\u88ab\u53f6\u51b2\u9704\u6253\u8ddb\u53cc\u817f\uff0c\u94ed\u6068\u4e8e\u5fc3\uff0c\u5bf9\u4ed6\u7684\u4e00\u5207\u7279 \u5f81\u90fd\u7262\u7262\u8bb0\u7740\uff0c\u8fd9\u65f6\u5df2\u770b\u51fa\u4e86\u51e0\u5206\uff0c\u6084\u6084\u5bf9\u97e9\u7487\u8bf4\u9053\uff1a\u201c\u6211\u770b\u5c31\u662f\u548c\u54b1\u4eec\u52a8\u624b\u8fd9\u4e2a\uff1f\u201d \u4f46\u4e00\u65f6\u4e4b\u95f4\uff0c\u5979\u4e5f\u8fd8\u4e0d\u6562\u80af\u5b9a\uff0c\u6545\u6b64\u8981\u5f81\u6c42\u4e08\u592b\u7684\u610f\u601d\u3002 \u3000\u3000\u97e9\u7487\u8fd8\u672a\u53ca\u56de\u7b54\uff0c\u53ea\u542c\u5f97\u6709\u4e2a\u4eba\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5f85\u6211\u6765\u770b\u770b\uff01\u201d\u8fd9\u4e2a\u4eba\u6b63\u662f\u65f6\u541b\u5c71\u7684 \u5927\u5f1f\u5b50\u6768\u7498\u3002\u97e9\u7487\u592b\u59bb\u5927\u559c\uff0c\u5fc3\u4e2d\u4ff1\u662f\u60f3\u9053\uff1a\u201c\u6768\u7498\u662f\u53f6\u51b2\u9704\u7684\u5e08\u5144\uff0c\u6709\u4ed6\u5728\u6b64\u3002\u5b9a\u7136 \u53ef\u4ee5\u5206\u8fa8\uff01\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u662f\u5728\u4e94\u5c81\u90a3\u5e74\uff0c\u59cb\u88ab\u8d3c\u4eba\u63b3\u53bb\u7684\uff0c\u5c0f\u65f6\u5019\u6768\u7498\u51e0\u4e4e\u5929\u5929\u90fd\u9017\u7740\u4ed6\u73a9\uff0c\u4f9d\u7a00 \u8fd8\u80fd\u8bb0\u5fc6\u3002\u6768\u7498\u5230\u4e86\u4ed6\u4eec\u7684\u9762\u524d\uff0c\u53f6\u51b2\u9704\u77a0\u76ee\u76f8\u5411\uff0c\u4e0d\u77e5\u4ed6\u662f\u4f55\u4eba\uff1b\u5510\u52aa\u73e0\u7a46\u5b9a\u775b\u4e00\u770b\uff0c \u5374\u5ffd\u5730\u53eb\u8d77\u6765\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u6768\u5e08\u5144\u5417\uff1f\u201d \u3000\u3000\u6768\u7498\u4e5f\u4e0d\u6562\u8d38\u7136\u76f8\u8ba4\uff0c\u8d70\u4e0a\u524d\u53bb\uff0c\u63e1\u4f4f\u5510\u52aa\u73e0\u7a46\u7684\u624b\u81c2\uff0c\u5ffd\u5730\u6495\u5f00\u4ed6\u7684\u8863\u8896\uff0c\u624b\u81c2 \u4e0a\u9732\u51fa\u4e00\u9897\u9c9c\u7ea2\u7684\u6731\u7802\u75e3\uff0c\u6768\u7498\u8fd9\u624d\u6ca1\u6709\u6000\u7591\uff0c\u559c\u6781\u800c\u6ce3\uff0c\u62b1\u7740\u5510\u52aa\u73e0\u7a46\u53eb\u9053\uff1a\u201c\u53f6\u5e08 \u5f1f\u3002\u6211\u7ec8\u4e8e\u627e\u7740\u4f60\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u53f6\u51b2\u9704\u7a81\u7136\u5148\u6765\uff0c\u4f46\u6768\u7498\u770b\u6765\u770b\u53bb\uff0c\u603b\u89c9\u5f97\u6709\u70b9\u4e0d\u50cf\uff0c\u6240\u4ee5\u4ed6\u4e00\u76f4\u5fc3\u6709\u6240\u7591\uff0c \u4e0d\u6562\u76f8\u8ba4\u3002\u5982\u4eca\u89c1\u4e86\u5510\u52aa\u73e0\u7a46\uff0c\u8fd9\u624d\u8ba4\u51fa\u5510\u52aa\u73e0\u7a46\u624d\u662f\u4ed6\u7684\u771f\u6b63\u5e08\u5f1f\u3002 \u3000\u3000\u97e9\u7487\u592b\u59bb\u5927\u51fa\u610f\u5916\uff0c\u97e9\u4e8c\u5a18\u53eb\u9053\uff1a\u201c\u600e\u4e48\u662f\u4ed6\uff1f\u4f46\u6253\u4f24\u6211\u7684\u90a3\u4e2a\u5c0f\u8d3c\uff0c\u6211\u5374\u8ba4\u5f97\u662f \u4ed6\uff01\u201d\u8bf4\u5230\u6700\u540e\u90a3\u4e2a\u201c\u4ed6\u201d\u5b57\uff0c\u5979\u7684\u624b\u6307\u6307\u7684\u662f\u53f6\u51b2\u9704\u3002 \u3000\u3000\u53f6\u51b2\u9704\u8bf4\u9053\uff1a\u201c\u8bf8\u4f4d\uff0c\u4ed6\u662f\u6211\u7684\u5144\u5f1f\uff0c\u4ed6\u5c0f\u65f6\u5019\u662f\u66fe\u53eb\u8fc7\u53f6\u51b2\u9704\uff0c\u4f46\u4e94\u5c81\u4e4b\u540e\uff0c\u4ed6 \u5df2\u7ecf\u4e0d\u662f\u53f6\u51b2\u9704\u4e86\uff0c\u4ed6\u4e0e\u4eca\u65e5\u4e4b\u4e8b\uff0c\u5168\u7136\u65e0\u6d89\u3002\u4f5c\u6076\u591a\u7aef\uff0c\u6b20\u4e0b\u4f60\u4eec\u8840\u503a\u7684\u90a3\u4e2a\u53f6\u51b2\u9704\uff0c \u4e0d\u662f\u4ed6\uff0c\u662f\u6211\uff01\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u67aa\u7740\u8bf4\u9053\uff1a\u201c\u4e0d\u5bf9\uff01\u7b2c\u4e00\uff0c\u6211\u624d\u662f\u771f\u6b63\u7684\u53f6\u51b2\u9704\uff1b\u7b2c\u4e8c\uff0c\u6211\u7684\u5927\u54e5\u76f4\u5230\u6700 \u8fd1\u624d\u77e5\u9053\u4ed6\u7684\u672c\u6765\u9762\u76ee\uff0c\u4ece\u524d\u4ed6\u662f\u7cca\u91cc\u7cca\u6d82\uff0c\u88ab\u4eba\u5229\u7528\u7684\u3002\u8bf4\u5230\u9547\u8fdc\u9556\u5c40\u7684\u771f\u6b63\u51f6\u624b\uff0c \u4e5f\u4e0d\u662f\u4ed6\u2026\u2026\u201d\u90a3\u4e24\u4e2a\u5f53\u5e74\u5e78\u5f97\u751f\u8fd8\u7684\u9556\u5e08\u611f\u6fc0\u53f6\u51b2\u9704\u653e\u4ed6\u7684\u60c5\u4e49\uff0c\u63d2\u53e3\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a \u6211\u4eec\u77e5\u9053\uff0c\u4e3b\u51f6\u5b9e\u5728\u662f\u8fd9\u4e2a\u5973\u8d3c\u7a46\u4e5d\u5a18\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4e5f\u8fd8\u6709\u4e9b\u4e0d\u5bf9\uff0c\u52a8\u624b\u6740\u4eba\u7684 \u662f\u7a46\u4e5d\u5a18\uff0c\u4f46\u6307\u4f7f\u4e4b\u4eba\uff0c\u771f\u6b63\u7684\u51f6\u624b\uff0c\u5374\u4e5f\u8fd8\u4e0d\u662f\u5979\uff01\u201d \u3000\u3000\u4f17\u4eba\u8d8a\u672a\u8d8a\u89c9\u7cca\u6d82\uff0c\u8bae\u8bba\u7eb7\u8d77\uff0c\u201c\u600e\u4e48\u4ed6\u4eec\u4e24\u5144\u5f1f\u90fd\u53eb\u505a\u53f6\u51b2\u9704\u7684\uff1f\u201d\u53ea\u542c\u8bf4\u53f6\u541b \u5c71\u6709\u4e00\u4e2a\u513f\u5b50\uff0c\u5374\u600e\u7684\u53c8\u94bb\u51fa\u4e00\u4e2a\u6765\uff1f\u201d \u3000\u3000\u6768\u7498\u548c\u97e9\u7487\u4e5f\u62a2\u770b\u53d1\u540c\uff0c\u6768\u7498\u95ee\u9053\uff1a\u201c\u6211\u5e08\u7236\u7a76\u7adf\u662f\u88ab\u8c01\u5bb3\u6b7b\u7684\uff1f\u5e08\u5f1f\uff0c\u4f60\u53c8\u662f\u88ab \u8c01\u62a2\u53bb\u7684\uff1f\u5728\u54ea\u91cc\u8fc7\u4e86\u8bb8\u591a\u5e74\uff1f\u201d\u97e9\u7487\u5219\u5728\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u4e3b\u51f6\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e00\u8fde\u4e32\u7684\u95ee\u9898\uff0c\u5510\u52aa\u73e0\u7a46\u4e5f\u4e0d\u77e5\u5148\u7b54\u54ea\u4e2a\u3002\u6c5f\u6d77\u5929\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8ba9\u6211\u6765\u8bf4\uff0c\u8fd9\u5176\u4e2d \u7684\u539f\u59d4\u6211\u90fd\u77e5\u9053\u3002\u201d\u4ed6\u4ee5\u4e0a\u4e58\u5185\u529f\u5c06\u58f0\u97f3\u9001\u51fa\uff0c\u767b\u65f6\u628a\u573a\u4e2d\u5608\u4e71\u7684\u6742\u58f0\u538b\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6307\u7740\u5510\u52aa\u73e0\u7a46\u8bf4\u9053\uff1a\u201c\u4ed6\u662f\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u738b\u3002\u4ece\u524d\u7684\u56fd\u738b\u540d\u53eb\u76d6\u6e29\uff0c\u662f\u4ed6\u7236 \u4eb2\u624b\u4e0b\u7684\u5927\u5c06\uff0c\u7be1\u593a\u4e86\u4ed6\u7236\u4eb2\u7684\u738b\u4f4d\u3002\u6674\u6740\u53f6\u541b\u5c71\uff0c\u6307\u4f7f\u7a46\u4e5d\u5a18\u52ab\u9547\u8fdc\u9556\u5c40\u7684\u9556\uff0c\u90fd\u662f \u76d6\u6e29\u5e72\u7684\u597d\u4e8b\u3002\u65f6\u51b2\u9704\u56e0\u4e0d\u660e\u8eab\u4e16\uff0c\u53d7\u76d6\u6e29\u6240\u9a97\uff0c\u88ab\u76d6\u6e29\u5229\u7528\uff0c\u5b9e\u5728\u8bf4\u6765\uff0c\u7f6a\u4e0d\u5728\u4ed6\uff0c \u4ed6\u53ea\u662f\u4ee3\u4eba\u53d7\u8fc7\u800c\u5df2\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4eba\u4eba\u66f4\u662f\u60ca\u8be7\u4e07\u5206\uff0c\u97e9\u7487\u592b\u59bb\u9762\u9762\u76f8\u89d1\uff0c\u60f3\u4e0d\u5230\u4ed6\u4eec\u9547\u8fdc\u9556\u5c40\u7684\u547d\u6848\uff0c \u7adf\u662f\u7275\u8fde\u5230\u9a6c\u8428\u513f\u56fd\u7684\u653f\u5c40\uff0c\u800c\u771f\u6b63\u7684\u53f6\u51b2\u9704\uff08\u5373\u5510\u52aa\u73e0\u7a46\uff09\uff0c\u7adf\u7136\u662f\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u738b\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8bf4\u4e86\u5c06\u8fd1\u534a\u4e2a\u65f6\u8fb0\uff0c\u65b9\u59cb\u628a\u524d\u56e0\u540e\u679c\u4ea4\u4ee3\u6e05\u695a\uff0c\u97e9\u7487\u95ee\u9053\uff0c\u201c\u6211\u8fd8\u6709\u4e00\u4e8b\u4e0d \u660e\uff0c\u90a3\u76d6\u6e29\u5f53\u5e74\u65e2\u7136\u662f\u4e00\u56fd\u4e4b\u738b\uff0c\u4e3a\u4f55\u8981\u52ab\u6211\u4eec\u9547\u8fdc\u9556\u5c40\u7684\u9556\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u8fd9\u4e2a\u53ef\u5f97\u6211\u6765\u8bf4\u4e2a\u660e\u767d\u4e86\u3002\u5f53\u5e74\u4f60\u4eec\u6240\u4fdd\u7684\u90a3\u652f\u9556\uff0c\u4e43\u662f\u4e00\u6279\u8d35\u91cd\u7684 \u836f\u6750\uff0c\u662f\u8981\u8fd0\u5230\u9102\u5c14\u6c81\u65d7\u53bb\u7684\uff0c\u662f\u4e48\uff1f\u201d\u97e9\u7487\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u6279\u836f\u6750\u4e5f\u662f\u521a\u8e0f\u8fdb\u9102\u5c14\u6c81 \u65d7\u8349\u539f\u7684\u65f6\u5019\u88ab\u52ab\u7684\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u5f53\u65f6\u9102\u5c14\u6c81\u65d7\u53d1\u751f\u761f\u75ab\uff0c\u8fd9\u6279\u836f\u6750\u662f\u533b\u6cbb\u75ab\u75c7\u7684\u3002\u76d6\u6e29\u60f3\u4e58\u673a\u5e76\u541e\u9102 \u5c14\u6c81\u65d7\u7684\u571f\u5730\uff0c\u6545\u6b64\u4e0d\u613f\u8fd9\u6279\u836f\u6750\u5230\u8fbe\u571f\u738b\u4e4b\u624b\u3002\u5f53\u65f6\u6211\u5949\u547d\u4e0e\u7a46\u4e5d\u5a18\u6765\u52ab\u4f60\u4eec\u8fd9\u652f\u9556\uff0c \u6700\u521d\u8fd8\u4e0d\u77e5\u9053\u6240\u52ab\u7684\u4e43\u662f\u6551\u6cbb\u761f\u75ab\u7684\u836f\u6750\uff0c\u540e\u6765\u65b9\u59cb\u77e5\u9053\u3002\u56e0\u6b64\u52ab\u9556\u4e4b\u65f6\uff0c\u6211\u6ca1\u6709\u52a8\u624b\uff0c \u4f46\u6211\u4e5f\u6ca1\u6709\u62e6\u963b\uff0c\u6b64\u4e8b\u4e43\u662f\u6211\u751f\u5e73\u6240\u505a\u7684\u6700\u5927\u9519\u4e8b\uff0c\u5b9e\u5728\u662f\u6b7b\u6709\u4f59\u8f9c\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u540e\u6765\u7684\u4e8b\u60c5\u4f60\u8fd8\u6ca1\u6709\u8bf4\uff0c\u6211\u4ee3\u4f60\u8bf4\u4e86\u5427\u3002\u4f60\u5185\u759a\u4e8e\u5fc3\uff0c\u540e\u6765\u6697\u4e2d\u628a \u6d88\u606f\u6cc4\u6f0f\u7ed9\u9102\u5c14\u6c81\u65d7\u7684\u571f\u738b\u77e5\u9053\uff0c\u90a3\u6279\u836f\u6750\u6ca1\u6709\u8fd0\u5230\u9a6c\u8428\u513f\u56fd\uff0c\u5728\u4e2d\u9014\u53c8\u7ed9\u9102\u5c14\u6c81\u65d7\u7684 \u519b\u961f\u622a\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u8be7\u9053\uff1a\u201c\u8fd9\u4e8b\u6211\u6ca1\u5bf9\u4f60\u8bf4\u8fc7\uff0c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6211\u5373\u4f4d\u4e4b\u540e\uff0c \u9102\u5c14\u6c81\u65d7\u6709\u4f7f\u8005\u524d\u6765\u9053\u8d3a\uff0c\u90a3\u4f7f\u8005\u5c31\u662f\u5f53\u5e74\u9886\u519b\u622a\u56de\u8fd9\u6279\u836f\u6750\u7684\u4eba\uff0c\u4ed6\u628a\u6211\u8bef\u8ba4\u662f\u4f60\uff0c \u4e00\u518d\u5411\u6211\u9053\u8c22\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u867d\u7136\u5982\u6b64\uff0c\u4f46\u5df2\u7ecf\u803d\u8bef\u4e86\u4e0d\u5c11\u65f6\u95f4\uff0c\u9102\u5c14\u6c81\u65d7\u53c8\u5df2\u65e0\u8f9c\u6b7b\u4e86\u4e0d \u77e5\u591a\u5c11\u4eba\u4e86\u3002\u76d6\u6e29\u4e5f\u7ec8\u4e8e\u541e\u5e76\u4e86\u9102\u5c14\u6c81\u65d7\u7684\u4e00\u90e8\u5206\u5730\u65b9\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u90a3\u5757\u571f\u5730\uff0c \u6211\u5df2\u7ecf\u8fd8\u7ed9\u4ed6\u4eec\u4e86\u3002\u201d\u53f6\u51b2\u9704\u53c8\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u4e8b\u8fc7\u540e\uff0c\u6211\u5df2\u7ecf\u77e5\u9053\u76d6\u6e29\u7684\u72e0\u6bd2\u624b\u6bb5\uff0c \u4f46\u6211\u8d2a\u604b\u8363\u534e\u5bcc\u8d35\uff0c\u53c8\u8ba4\u4e3a\u4ed6\u662f\u6211\u7684\u517b\u7236\uff0c\u6069\u6df1\u4e49\u91cd\uff0c\u8fd8\u820d\u4e0d\u5f97\u2018\u53db\u2019\u4ed6\uff0c\u73b0\u5728\u60f3\u6765\uff0c \u5f53\u771f\u4e0d\u662f\u4e2a\u4eba\uff01\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u90fd\u8fc7\u53bb\u4e86\uff0c\u4f60\u867d\u7136\u660e\u767d\u5f97\u8fdf\u4e86\u4e00\u4e9b\uff0c\u4f46\u76d6\u6e29\u4e5f\u6bd5\u7adf\u662f\u7ed9\u4f60\u4eb2 \u624b\u6740\u4e86\u3002\u8bf4\u8d77\u6765\u4f60\u5df2\u7ecf\u662f\u5c06\u529f\u8d4e\u7f6a\uff0c\u4e5f\u53ef\u4ee5\u65e0\u6127\u4e8e\u5fc3\u4e86\u3002\u201d\u97e9\u7487\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u89c9\u5446\u4e86\u3002 \u3000\u3000\u4e8b\u60c5\u7ecf\u8fc7\u79bb\u5947\u66f2\u6298\u8c03\u5018\u975e\u662f\u6c5f\u6d77\u5929\u5728\u573a\u52a0\u4ee5\u8bc1\u5b9e\uff0c\u97e9\u4e8c\u5a18\u7b49\u4eba\u8fd8\u672a\u5fc5\u4f1a\u76f8\u4fe1\u5462\u3002\u8fd9 \u65f6\u771f\u76f8\u5df1\u81ea\uff0c\u97e9\u7487\u55d2\u7136\u82e5\u4e27\uff1b\u6084\u58f0\u5bf9\u59bb\u5b50\u8bf4\u9053\uff1a\u201c\u8001\u4f34\u51e0\uff0c\u8fd9\u56de\u54b1\u4eec\u53ef\u53c8\u662f\u627e\u9519\u4eba\u4e86\u3002 \u8fd9\u4e48\u8bf4\u6765\uff0c\u8fd9\u4f4d\u53f6\u670b\u53cb\u975e\u4f46\u4e0d\u662f\u4e3b\u51f6\uff0c\u54b1\u4eec\u9547\u8fdc\u9556\u5c40\u7684\u5927\u4ec7\u4eba\u8fd8\u662f\u4ed6\u6740\u4e86\u7684\u3002\u201d\u97e9\u4e8c\u5a18 \u9053\uff1a\u201c\u4f9d\u4f60\u4e4b\u89c1\u5982\u4f55\uff1f\u201d\u97e9\u7487\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u8bf4\u7684\uff1f\u54b1\u4eec\u4e0e\u8fd9\u4f4d\u53f6\u670b\u53cb\u4e4b\u95f4\u7684\u6069\u4ec7\u4e00\u7b14 \u52fe\u9500\uff0c\u4ed6\u66ff\u54b1\u4eec\u6740\u4e86\u4ec7\u4eba\uff0c\u54b1\u4eec\u683d\u7ed9\u4ed6\u7684\u90a3\u4e2a\u8ddf\u5934\u4e5f\u7b97\u662f\u503c\u5f97\u7684\u4e86\uff01\u201d \u3000\u3000\u97e9\u4e8c\u5a18\u9053\uff0c\u201c\u597d\uff0c\u54b1\u4eec\u628a\u8fd9\u59d3\u7a46\u7684\u5973\u8d3c\u5bb0\u4e86\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u95ed\u95e8\u5c01\u5200\uff0c\u518d\u4e5f\u4e0d\u5e72\u6c5f\u6e56 \u4e0a\u7684\u8425\u751f\u4e86\u3002\u201d\u5979\u6b63\u8981\u53bb\u6740\u90a3\u7a46\u4e5d\u5a18\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u60e8\u547c\uff0c\u539f\u6765\u90a3\u7a46\u4e5d\u5a18\u65e9\u5df2\u81ea\u65ad\u7ecf\u8109\u800c \u4ea1\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u97e9\u8001\u82f1\u96c4\u6162\u8d70\u3002\u201d\u97e9\u7487\u9053\uff0c\u201c\u600e\u4e48\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4eba\u6b7b\u4e0d\u80fd\u590d \u751f\uff0c\u8d35\u9556\u5c40\u7684\u4e09\u5341\u516d\u6761\u6027\u547d\uff0c\u90a3\u662f\u65e0\u6cd5\u8d54\u507f\u7684\u4e86\u3002\u4f46\u90a3\u6b21\u8d35\u9556\u5c40\u906d\u52ab\uff0c\u7d2f\u5f97\u97e9\u8001\u82f1\u96c4\u503e \u5bb6\u8361\u4ea7\uff0c\u9556\u5c40\u4e5f\u53d7\u62d6\u7d2f\u800c\u5173\u4e86\u95e8\u3002\u6211\u4eec\u82e5\u4e0d\u7565\u8868\u5bf8\u5fc3\uff0c\u5b9e\u5728\u8fc7\u610f\u4e0d\u53bb\u3002\u8fd9\u662f\u4e8c\u767e\u4e07\u4e24\u5317 \u4eac\u94b1\u5e84\u6240\u51fa\u7684\u94f6\u7968\uff0c\u5176\u4e2d\u4e00\u767e\u4e07\u4e24\u662f\u8d54\u507f\u4f60\u4eec\u90a3\u6b21\u635f\u5931\u7684\uff0c\u53e6\u5916\u4e00\u767e\u4e07\u4e24\uff0c\u8bf7\u8001\u82f1\u96c4\u4ee3 \u4e3a\u5206\u8d60\u90a3\u4e09\u5341\u516d\u5bb6\u6b7b\u96be\u7684\u9556\u5e08\u5bb6\u5c5e\uff0c\u4f5c\u4e3a\u6064\u8001\u629a\u5e7c\u4e4b\u8d44\u3002\u201d \u3000\u3000\u97e9\u7487\u5f85\u8981\u4e0d\u63a5\uff0c\u5168\u7956\u5fb7\u8bf4\u9053\uff1a\u201c\u8fd9\u5230\u662f\u53ef\u4ee5\u8981\u5f97\u7684\uff0c\u603b\u4e0d\u80fd\u53eb\u4f60\u5e73\u7a7a\u53d7\u7d2f\u3002\u8fd9\u9556\u65e2 \u662f\u9a6c\u8428\u513f\u56fd\u524d\u738b\u6240\u52ab\u3002\u73b0\u5728\u4e5f\u7531\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u738b\u4ee3\u4e3a\u507f\u8fd8\uff0c\u4ea6\u662f\u540d\u6b63\u8a00\u987a\u3002\u4ffa\u8001\u53eb\u5316\u5012 \u5e0c\u671b\u4f60\u628a\u9547\u8fdc\u9556\u5c40\u91cd\u65b0\u6062\u590d\u5462\u3002\u201d\u97e9\u7487\u53ea\u597d\u63a5\u4e86\u3002 \u3000\u3000\u97e9\u4e8c\u5a18\u4e00\u8df7\u4e00\u62d0\u5730\u8d70\u5230\u53f6\u51b2\u9704\u8ddf\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u9547\u8fdc\u9556\u5c40\u7684\u547d\u6848\u4e0d\u5173\u4f60\u4e8b\uff0c\u6211\u8fd9\u53cc\u817f \u53ef\u662f\u4f60\u6253\u8ddb\u7684\uff0c\u8fd9\u53e3\u6028\u6c14\u53ef\u4e0d\u80fd\u4e0d\u51fa\uff01\u201d\u4f17\u4eba\u76f8\u987e\u6115\u7136\uff0c\u4e0d\u610f\u97e9\u4e8c\u5a18\u8282\u5916\u751f\u679d\uff0c\u4e91\u53ec\u3001 \u534e\u5929\u98ce\u7b49\u4eba\u6b63\u5f85\u529d\u89e3\uff0c\u53ea\u89c1\u97e9\u4e8c\u5a18\u201c\u5478\u201d\u7684\u553e\u4e86\u53f6\u51b2\u9704\u4e00\u53e3\uff0c\u8fd9\u624d\u6491\u7740\u94c1\u62d0\u548c\u97e9\u7487\u79bb\u573a\u3002 \u53f6\u51b2\u9704\u4ef0\u9762\u53d7\u8fb1\uff0c\u4e1d\u6beb\u4e0d\u52a8\uff0c\u8ba9\u90a3\u553e\u6cab\u81ea\u5e72\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u4ee5\u6211\u4ece\u524d\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u53d7\u5979 \u4e00\u553e\uff0c\u8fd9\u8d23\u7f5a\u8fd8\u7b97\u662f\u592a\u8f7b\u4e86\u3002 \u3000\u3000\u4e91\u5e84\u4e3b\uff0c\u73b0\u5728\u8f6e\u5230\u4f60\u4e86\u3002\u201d \u3000\u3000\u4e91\u53ec\u89c1\u53f6\u51b2\u9704\u5df2\u662f\u771f\u8bda\u6094\u609f\uff0c\u5982\u4f55\u8fd8\u80fd\u4e0b\u624b\u62a5\u590d\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u5c0f\u5973\u4eca\u65e5\u591a\u8499\u4f60\u7684 \u5144\u5f1f\u6551\u4e86\u6027\u547d\uff0c\u4f60\u4ece\u524d\u6253\u4e86\u6211\u7684\u513f\u5973\u4e24\u638c\uff0c\u4e24\u638c\u6362\u4e00\u547d\uff0c\u8fd9\u7b14\u503a\u5df2\u7531\u4f60\u5144\u5f1f\u4ee3\u8fd8\uff0c\u4e5f\u5c31 \u4e0d\u5fc5\u518d\u7b97\u4e86\u3002\u201d\u5f53\u4e0b\uff0c\u53f6\u51b2\u9704\u5411\u4e91\u5bb6\u5144\u59b9\u8d54\u4e86\u7f6a\uff0c\u4e91\u743c\u4e5f\u5411\u5510\u52aa\u73e0\u7a46\u9053\u4e86\u8c22\u3002 \u3000\u3000\u4e00\u5929\u4e91\u96fe\u6d88\u9664\uff0c\u4f17\u4eba\u7686\u5927\u6b22\u559c\u3002\u6b27\u9633\u4f2f\u548c\u9053\uff1a\u201c\u4eca\u65e5\u5e78\u5f97\u6881\u5b50\u89e3\u5f00\uff0c\u5404\u4f4d\u8fdc\u9053\u800c\u6765\uff0c \u8fd8\u8bf7\u5728\u655d\u5e84\u559d\u4e00\u676f\u6c34\u9152\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a \u3000\u3000\u201c\u6211\u8fd8\u6709\u4e8b\u60c5\u8d76\u7740\u56de\u53bb\u5462\uff01\u201d\u6b27\u9633\u4e8c\u5a18\u9053\uff1a\u201c\u4e5f\u4e0d\u4e89\u5728\u803d\u6401\u8fd9\u4e48\u4e00\u665a\uff0c\u54b1\u4eec\u5df2\u7136\u505a \u4e86\u4eb2\u621a\uff0c\u60f3\u6765\u4f60\u4eec\u4e5f\u4e0d\u4f1a\u518d\u8bb0\u524d\u4ec7\u4e86\u3002\u201d\u5168\u7956\u5fb7\u7b11\u76f4\uff1a\u201c\u4f60\u53c8\u8bf4\u4e0d\u8ba4\u8fd9\u4e2a\u5973\u5a7f\u7684\uff1f\u201d\u6b27 \u9633\u4e8c\u5a18\u7b11\u9053\uff1a\u201c\u73b0\u5728\u6ca1\u4e8b\u4e86\uff0c\u6211\u600e\u4e48\u8fd8\u4e0d\u8ba4\u3002\u201d\u56de\u8fc7\u5934\u6765\u4fbf\u95ee\u53f6\u51b2\u9704\u9053\uff1a\u201c\u6211\u90a3\u5a49\u513f\u5462\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u5a49\u59b9\u5df2\u7ecf\u5230\u9a6c\u8428\u513f\u56fd\u53bb\u4e86\u3002\u201d\u6b27\u9633\u4e8c\u5a18\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u604d\u7136\u5927\u609f\uff0c\u8bf4 \u9053\uff1a\u201c\u8fd9\u662f\u4f60\u8981\u5979\u53bb\u7684\uff1f\u4f60\u662f\u610f\u6b32\u6551\u5979\u4e00\u547d\uff1f\u201d\u53f6\u51b2\u9704\u82e6\u7b11\u9053\uff1a\u201c\u6211\u65e9\u6599\u5230\u6709\u4eca\u65e5\u4e4b\u4e8b\uff0c \u6211\u4e0d\u613f\u610f\u62d6\u7d2f\u4e8e\u5979\uff0c\u6240\u4ee5\u7528\u4e00\u4e2a\u501f\u53e3\uff0c\u54c4\u5979\u56de\u8f6c\u9a6c\u8428\u513f\u56ed\uff0c\u8bf7\u6211\u7684\u5f1f\u5f1f\u7167\u987e\u5979\u3002 \u3000\u3000\u5979\u5374\u662f\u4e0d\u77e5\u4eca\u65e5\u4e4b\u4e8b\u7684\u3002\u201d \u3000\u3000\u539f\u6765\u53f6\u51b2\u9704\u65e9\u5df2\u62fc\u4e86\u4e00\u6b7b\uff0c\u8fd8\u6e05\u8840\u503a\uff0c\u4f46\u4ed6\u4e0d\u613f\u59bb\u5b50\u4f24\u5fc3\uff0c\u6545\u800c\u5b8c\u5168\u7792\u7740\u6b27\u9633\u5a49\u3002 \u4ed6\u5728\u59bb\u5b50\u8d70\u4e86\u4e4b\u540e\uff0c\u4fbf\u5728\u5cb3\u5bb6\u9644\u8fd1\u9690\u85cf\uff0c\u5f85\u5230\u7fa4\u96c4\u5230\u6b64\u5bfb\u4ec7\uff0c\u4ed6\u4fbf\u8d76\u67ec\u9732\u9762\u4e86\u3002\u4ed6\u6ca1\u60f3 \u5230\u4e8b\u60c5\u7adf\u4f1a\u51fa\u4e4e\u610f\u5916\u7684\u89e3\u51b3\uff0c\u5c45\u7136\u9022\u51f6\u5316\u5409\u3001\u9047\u96be\u6210\u7965\uff0c\u9664\u4e86\u53d7\u97e9\u4e8c\u5a18\u4e00\u53e3\u553e\u6cab\u4e4b\u5916\uff0c \u4ec0\u4e48\u635f\u4f24\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u6b27\u9633\u4e8c\u5a18\u773c\u5708\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u8d24\u5a7f\u4f60\u771f\u662f\u4e00\u7247\u82e6\u5fc3\u3002\u73b0\u5728\u4f60\u53ef\u4ee5\u53eb\u5a49\u513f\u56de\u5bb6\u4e86\u3002\u201d \u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u6211\u6b63\u662f\u8981\u627e\u4f60\u56de\u540c\uff0c\u73b0\u5728\u5ac2\u4e8e\u5df2\u7ecf\u56de\u53bb\uff0c\u4f60\u5c31\u66f4\u5e94\u8be5\u53bb\u4e86\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u662f\u51b3\u5bf9\u4e0d\u56de\u53bb\u7684\u4e86\u3002\u5f1f\u5f1f\uff0c\u6211\u60f3\u4e0d\u5230\u4f60\u4f1a\u51fa\u6765\u627e\u6211\uff0c\u597d\u5728\u4f60\u5c31 \u8981\u56de\u53bb\u7684\uff0c\u5c31\u6258\u4f60\u6d88\u4e2a\u53e3\u4fe1\uff0c\u544a\u8bc9\u4f60\u7684\u5ac2\u4e8e\uff0c\u5c31\u8bf4\u5bb6\u91cc\u5df2\u7ecf\u5e73\u5b89\u65e0\u4e8b\u3002\u53eb\u5979\u56de\u6765\u597d\u4e86\u3002 \u6211\u5728\u5bb6\u91cc\u7b49\u5979\u3002\u201d\u6b27\u9633\u4e8c\u5a18\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u6837\u4e5f\u597d\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u6211\u8fd8\u6709\u4e9b\u4e8b\u60c5\u8981\u548c\u4f60\u8bf4\u3002\u8bf7\u501f\u4e00\u95f4\u9759\u5ba4\u4e00\u7528\u3002\u201d\u6b27\u9633\u4e8c\u5a18\u4e0d \u77e5\u4ed6\u4eec\u6709\u4ec0\u4e48\u79d8\u5bc6\u8981\u7792\u7740\u5979\uff0c\u5fc3\u91cc\u6709\u70b9\u4e0d\u5927\u9ad8\u5174\uff0c\u4f46\u5374\u4e5f\u53ea\u5f97\u7b54\u5141\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u597d\u5427\uff0c \u4f60\u4eec\u54e5\u513f\u4fe9\u65e2\u7136\u6709\u79c1\u8bdd\u8981\u8c08\uff0c\u90a3\u5c31\u8bf7\u8fdb\u53bb\u5427\uff0c\u201d\u5510\u52aa\u73e0\u7a46\u62db\u624b\u9053\uff1a\u201c\u6c5f\u5e08\u5144\uff0c\u4f60\u4e5f\u6765\u3002\u201d \u3000\u3000\u8fdb\u4e86\u5bc6\u5ba4\uff0c\u5510\u52aa\u73e0\u7a46\u5173\u4e0a\u623f\u95e8\uff0c\u53f6\u51b2\u9704\u60ca\u7591\u4e0d\u5b9a\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5f1f\u5f1f\uff0c\u7a76\u7adf\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4e0d\u80fd\u8ba9\u5916\u4eba\u542c\u89c1\u7684\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u8fd8\u662f\u90a3\u4e00\u53e5\u8bdd\uff0c\u54e5 \u54e5\uff0c\u660e\u5929\u4e00\u65e9\uff0c\u4f60\u4e00\u5b9a\u8981\u548c\u6211\u56de\u53bb\uff01\u201d \u3000\u3000\u65f6\u51b2\u9704\u51c4\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5f1f\u5f1f\uff0c\u4f60\u8fd8\u4e0d\u660e\u767d\u6211\u7684\u5fc3\u60c5\u5417\uff1f \u3000\u3000\u6211\u66fe\u8ba4\u8d3c\u4f5c\u7236\uff0c\u5c3d\u7ba1\u4f60\u4eec\u539f\u8c05\u6211\uff0c\u6211\u5374\u4e0d\u80fd\u539f\u8c05\u81ea\u5df1\u3002\u6211\u6ca1\u6709\u9762\u76ee\u518d\u89c1\u56fd\u4e2d\u7236\u8001\uff0c \u6211\u662f\u51b3\u8ba1\u4e0d\u518d\u8e0f\u8fdb\u672c\u56fd\u4e00\u6b65\u4e86\u3002\u4f60\u7684\u5ac2\u5b50\uff0c\u4f60\u53eb\u5979\u56de\u6765\u5427\uff0c\u4f55\u5fc5\u5f3a\u6211\u6240\u96be\uff0c\u8981\u6211\u518d\u53bb\u5462\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u4e0d\u5355\u662f\u4e3a\u4e86\u8981\u4f60\u8bf7\u5ac2\u5ac2\u56de\u6765\u3002\u54e5\u54e5\uff0c\u6211\u95ee\u4f60\u4e00\u53e5\uff0c\u4f60\u89c9\u5f97\u5bf9 \u4e0d\u4f4f\u56fd\u4e2d\u767e\u59d3\uff0c\u90a3\u4e48\u672c\u540c\u906d\u9047\u5371\u673a\uff0c\u767e\u59d3\u9762\u4e34\u707e\u96be\uff0c\u4f60\u662f\u4e0d\u662f\u4e5f\u4e0d\u613f\u8e0f\u56fd\u95e8\u4e00\u6b65\uff0c\u8896\u624b \u65c1\u89c2\uff1f\u201d\u53f6\u51b2\u9704\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5f1f\u5f1f\uff0c\u54b1\u4eec\u9a6c\u8428\u513f\u56fd\u906d\u5230\u4ec0\u4e48\u610f\u5916\uff1f\u5018\u82e5\u771f\u4f3c\u4f60\u6240 \u8bf4\u7684\u90a3\u6837\u4e25\u91cd\uff0c\u6211\u5f53\u7136\u4e0d\u80fd\u8896\u624b\u65c1\u89c2\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u662f\u8981\u4f60\u8fd9\u4e00\u53e5\u8bdd\u3002\u201d\u5f53\u4e0b\u5c06\u4e0e\u6606\u5e03\u5170\u56fd\u7684\u7ea0\u7eb7\u8bf4\u4e86\u51fa\u6765\uff0c \u8bf4\u5230\u6606\u5e03\u5170\u56fd\u7684\u4f7f\u81e3\u540c\u90a3\u76d6\u6e29\u7684\u513f\u5b50\u5728\u5b9d\u5e93\u51fa\u73b0\uff0c\u6606\u5e03\u5170\u56fd\u7684\u4f7f\u81e3\u7adf\u7136\u6b7b\u5728\u4ed6\u7684\u5251\u4e0b\uff0c \u53f6\u51b2\u9704\u548c\u6c5f\u6d77\u5929\u90fd\u662f\u60ca\u6115\u4e0d\u5df2\u3002\u53f6\u51b2\u9704\u54fa\u54fa\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u54b1\u4eec\u4e0e\u6606\u5e03\u5170\u56fd\u5f53\u771f\u662f \u6709\u5175\u620e\u76f8\u89c1\u7684\u5371\u673a\uff01\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6240\u4ee5\u6211\u5fc5\u8981\u8bbe\u6cd5\uff0c\u628a\u8fd9\u573a\u6218\u7978\u6d88\u6d31\u3002\u83b2\u59b9\u4e5f\u662f\u8fd9\u4e2a\u5fc3\u601d\uff0c\u5979\u5df2\u7ecf\u5192 \u5145\u6211\u56fd\u4f7f\u8005\u7684\u968f\u4ece\uff0c\u5230\u6606\u5e03\u5170\u56fd\u53bb\u4e86\u3002\u201d\u5f53\u4e0b\uff0c\u5c06\u4ed6\u4eec\u5144\u59b9\u90a3\u53e3\u6240\u5b9a\u7684\u8ba1\u5212\u8bf4\u4e86\u3002\u6c5f\u6d77 \u5929\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff0c\u201c\u83b2\u59b9\u867d\u6709\u5979\u5e08\u7236\u6697\u4e2d\u4fdd\u62a4\uff0c\u4f46\u6606\u5e03\u5170\u56fd\u8981\u662f\u5f53\u771f\u5bf9\u4f60\u4eec\u542b\u6709\u654c\u610f \u7684\u8bdd\uff0c\u8fd9\u4e00\u884c\u53ef\u4e5f\u5f88\u5371\u9669\u554a\uff01\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6240\u4ee5\u6211\u624d\u8981\u6765\u627e\u5927\u54e5\u56de\u53bb\u3002\u201d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u53bb\u540e\uff0c\u4e00\u76f4\u6ca1\u6709\u6d88 \u606f\u56de\u62a5\u3002\u4ece\u6211\u7684\u738b\u5bab\u66fe\u6709\u8fc7\u98de\u8d3c\u6765\u8fc7\u4e00\u6b21\u7684\u4e8b\u60c5\u770b\u6765\uff0c\u5bf9\u65b9\u4e5f\u9887\u6709\u80fd\u4eba\u3002\u6211\u653e\u5fc3\u4e0d\u4e0b\uff0c \u610f\u6b32\u4eb2\u81ea\u5230\u6606\u5e03\u5170\u56fd\u53bb\u4e00\u884c\u3002\u4f46\u56fd\u4e8b\u65e0\u4eba\u6599\u7406\uff0c\u5927\u54e5\uff0c\u6211\u6ca1\u6709\u53ef\u4ee5\u4fe1\u6258\u7684\u4eba\uff0c\u53ea\u6709\u627e\u4f60 \u56de\u53bb\u4e86\u3002\u6211\u8fd9\u6b21\u5e26\u4e86\u51e0\u5339\u5343\u91cc\u9a6c\u6765\uff0c\u54b1\u4eec\u660e\u65e5\u4e00\u65e9\u52a8\u8eab\uff0c\u4e09\u5929\u4e4b\u5185\uff0c\u5c31\u53ef\u4ee5\u56de\u5230\u672c\u56fd\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff0c\u201c\u5f1f\u5f1f\uff0c\u65e2\u7136\u53d1\u751f\u5982\u6b64\u610f\u5916\uff0c\u6211\u7406\u8be5\u56de\u53bb\u3002\u4f46\u6211\u5374\u6709\u4e00\u4ef6 \u4e8b\u60c5\u6c42\u4f60\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u5927\u54e5\u4f55\u5fc5\u7528\u4e2a\u2018\u6c42\u2019\u5b57\uff0c\u4f60\u8bf4\u5427\uff01\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4e0d\uff0c\u8fd9 \u4ef6\u4e8b\u975e\u5e38\u91cd\u8981\uff0c\u8981\u662f\u4f60\u4e0d\u7b54\u5e94\uff0c\u90a3\u6211\u5c31\u5b81\u613f\u88ab\u56fd\u4eba\u553e\u9a82\uff0c\u4e5f\u4e0d\u56de\u53bb\u4e86\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a \u201c\u597d\uff0c\u6211\u7b54\u5e94\u4f60\uff0c\u8bf4\u5427\u3002\u201d\u53f6\u51b2\u9704\u9053\uff1a\u201c\u6211\u51b3\u8ba1\u4e0d\u505a\u56fd\u738b\uff0c\u5728\u4f60\u79bb\u5f00\u7684\u671f\u95f4\uff0c\u6211\u6700\u591a\u80fd \u6682\u5c45\u6444\u653f\u5927\u81e3\u7684\u540d\u4f4d\u3002\u201d\u5510\u52aa\u73e0\u7a46\u672c\u610f\u662f\u60f3\u8ba9\u4f4d\u7ed9\u4ed6\u54e5\u54e5\u7684\uff0c\u4f46\u542c\u53f6\u51b2\u9704\u8bf4\u5f97\u5982\u6b64\u51b3\u7edd\u3002 \u4e5f\u5c31\u4e0d\u597d\u63d0\u4e86\u3002\u5f53\u4e0b\u540c\u610f\u4e86\u4ed6\u7684\u4e3b\u5f20\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u4eec\u6709\u4e8b\uff0c\u6211\u4e5f\u4e0d\u80fd\u5750\u795d\uff0c\u6211\u6682\u7f13\u5357\u5f52\uff0c\u548c\u4f60\u4e00\u540c\u5230\u6606\u5e03\u5170\u56fd\u8d70\u4e00\u906d \u5427\u3002\u201d\u5510\u52aa\u73e0\u7a46\u8bf7\u4ed6\u53c2\u4e0e\u673a\u5bc6\u3002\u6b63\u662f\u8981\u4ed6\u5982\u6b64\u8868\u793a\uff0c\u6b23\u7136\u8bf4\u9053\uff1a\u201c\u5e08\u5144\u540c\u53bb\uff0c\u90a3\u662f\u6c42\u4e4b \u4e0d\u5f97\u3002\u201d \u3000\u3000\u8ba1\u8bae\u5df2\u5b9a\uff0c\u4e09\u4eba\u8d70\u51fa\u5bc6\u5ba4\uff0c\u6b27\u9633\u4f2f\u548c\u4e5f\u5df2\u7ecf\u6446\u597d\u7b75\u5e2d\u4e86\u3002\u8fd9\u6b21\u6765\u5230\u4ed6\u5bb6\u7684\u4e24\u65b9\u5ba2\u4eba \u4eba\u6570\u903e\u5343\uff0c\u867d\u7136\u6563\u53bb\u4e0d\u5c11\uff0c\u4f46\u4e5f\u8981\u7b75\u5f00\u4e94\u5341\u591a\u5e2d\uff0c\u624d\u591f\u5ea7\u4f4d\u3002\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u8fd9\u4e00\u5e2d\u5374\u90fd\u662f \u81ea\u5df1\u4eba\uff0c\u53e6\u8bbe\u5185\u5802\u3002\u5176\u4e2d\u6709\u6b27\u9633\u4ef2\u548c\u592b\u59bb\uff0c\u6760\u5357\u7236\u5b50\uff0c\u53f6\u51b2\u9704\u5144\u5f1f\uff0c\u4e91\u53ec\u4e00\u5bb6\u4e09\u4eba\uff0c\u53e6 \u5916\u8fd8\u6709\u534a\u5929\u98ce\u548c\u5168\u7956\u5fb7\u3002\u81f3\u4e8e\u6b27\u9633\u4f2f\u548c\u5219\u5728\u5916\u5802\u966a\u5ba2\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5750\u5728\u4e49\u7236\u65c1\u8fb9\uff0c\u534e\u5929\u98ce\u518d\u4ed4\u7ec6\u95ee\u4ed6\u534e\u4e91\u78a7\u90a3\u65e5\u98de\u8d70\u7684\u60c5\u5f62\uff0c\u6c5f\u6d77\u5929\u671f\u671f\u827e\u827e\uff0c \u4e0d\u6562\u5410\u9732\u5e95\u8574\u3002\u534e\u5929\u98ce\u95ee\u4e0d\u51fa\u6240\u4ee5\u7136\u6765\uff0c\u751a\u4e3a\u7eb3\u95f7\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u4e5f\u771f\u662f\u4e0d\u901a\u4eba\u60c5\u4e16 \u6545\uff0c\u7eb5\u7136\u6025\u7740\u56de\u6765\u770b\u6211\uff0c\u4e5f\u5e94\u8be5\u5411\u4f60\u4eec\u544a\u8f9e\u4e00\u58f0\u624d\u662f\uff0c\u6211\u8fd8\u4ee5\u4e3a\u5979\u8981\u548c\u4f60\u4e00\u540c\u56de\u6765\u7684\u5462\uff01 \u73b0\u5728\u4f60\u4eec\u90fd\u5df2\u7ecf\u6765\u5230\u6b64\u5730\u4e86\uff0c\u5979\u9a91\u7740\u795e\u9e70\uff0c\u5374\u8fd8\u662f\u8e2a\u5f71\u4e0d\u89c1\uff0c\u53c8\u4e0d\u77e5\u51fa\u4e86\u4ec0\u4e48\u4e8b\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e5f\u662f\u95f7\u95f7\u4e0d\u4e50\uff0c\u4ed6\u672c\u662f\u8981\u5230\u6c34\u4e91\u5e84\u89c1\u534e\u4e91\u78a7\u7684\uff0c\u54ea\u77e5\u534e\u4e91\u78a7\u5374\u4e0d\u77e5\u51fa\u4e86\u4ec0\u4e48 \u610f\u5916\uff0c\u800c\u8c37\u4e2d\u83b2\u73b0\u5728\u53c8\u6df1\u5165\u654c\u56fd\uff0c\u968f\u65f6\u90fd\u53ef\u80fd\u6709\u4e0d\u6d4b\u4e4b\u7978\uff0c\u771f\u662f\u4e8b\u4e8b\u4e0d\u5982\u4eba\u610f\uff0c\u4ee4\u5f97\u6c5f \u6d77\u5929\u5fe7\u8651\u91cd\u91cd\u3002 \u3000\u3000\u5e2d\u4e0a\u8bf8\u4eba\uff0c\u5404\u6000\u5fc3\u4e8b\uff0c\u90c1\u90c1\u5be1\u6b22\uff0c\u4f46\u4e3b\u4eba\u5bb6\u5219\u56e0\u4e00\u573a\u707e\u96be\u4e1a\u5df1\u5316\u89e3\uff0c\u5374\u662f\u5174\u9ad8\u91c7\u70c8 \u7684\u9891\u9891\u529d\u9152\uff0c\u5c06\u5fe7\u90c1\u7684\u6c14\u6c1b\u51b2\u6de1\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u78a7\u59b9\u4e0d\u77e5\u4e0b\u843d\uff0c\u53c8\u65e0\u7ebf\u7d22\u53ef\u5bfb\uff0c\u6025\u4e5f\u6025\u4e0d\u6765\u4e86\u3002\u83b2\u59b9\u76ee\u524d\u8eab\u9677 \u864e\u7a74\uff0c\u53ea\u597d\u5148\u628a\u5979\u6551\u51fa\u6765\u518d\u8bf4\u3002\u53f6\u5927\u54e5\u4eca\u65e5\u5f97\u8131\u5371\u96be\uff0c\u4ee5\u540e\u53ef\u4ee5\u91cd\u65b0\u505a\u4eba\u4e86\uff0c\u6211\u5e94\u8be5\u4e3a \u4ed6\u6b22\u559c\u624d\u5bf9\u3002\u201d\u5fc3\u795e\u7a0d\u5b9a\uff0c\u5fc3\u4e8b\u629b\u5f00\uff0c\u4e5f\u5c31\u653e\u6000\u559d\u8d77\u9152\u6765\u3002 \u3000\u3000\u9152\u5e2d\u5c06\u6563\uff0c\u5ffd\u6709\u4e2a\u4eba\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u4e91\u5e84\u4e3b\uff0c\u4f60\u5e84\u4e0a\u6709\u4eba\u8d76\u6765\uff0c\u8bf4\u662f\u6709\u4e8b\u60c5\u8981\u5411\u4f60\u7980 \u62a5\u3002\u201d\u4e91\u53ec\u9887\u4e3a\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u53eb\u5b57\u6587\u6717\u51fa\u53bb\u5148\u8ba4\u4e00\u8ba4\u4eba\uff0c\u679c\u7136\u662f\u6211\u5e84\u4e0a\u7684\u5c31\u5e26 \u4ed6\u8fdb\u6765\u5427\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4e91\u53ec\u7684\u5927\u5f1f\u5b50\u5b57\u6587\u6717\u5e26\u4e86\u4e00\u4e2a\u8001\u6c49\u8fdb\u6765\uff0c\u6b63\u662f\u4e91\u53ec\u7684\u8001\u4ec6\u4eba\u4e91\u5b89\uff0c\u4ed6\u4e00 \u751f\u8ddf\u968f\u4e91\u53ec\uff0c\u5728\u6c34\u4e91\u5e84\u7684\u5730\u4f4d\u4ec5\u6b21\u4e8e\u7ba1\u5bb6\uff0c\u4e3a\u4eba\u5e72\u7ec3\uff0c\u6b66\u529f\u4e5f\u5f88\u4e0d\u5f31\uff0c\u4ed6\u6025\u6025\u5fd9\u5fd9\u8fdb\u6765\uff0c \u6ee1\u8138\u98ce\u5c18\u4e4b\u8272\uff0c\u4e91\u53ec\u66f4\u662f\u5403\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u54b1\u4eec\u5e84\u4e0a\u51fa\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f\u8981\u4f60\u8d76\u6765\u89c1\u6211\uff1f\u201d \u3000\u3000\u4e91\u5b89\u8bf7\u4e86\u4e2a\u5b89\uff0c\u8bf4\u9053\uff1a\u201c\u534e\u8001\u7237\u5b50\u5728\u6b64\uff0c\u6210\u5c31\u653e\u5fc3\u4e86\u3002\u4e0d\u662f\u54b1\u4eec\u5e84\u4e0a\u51fa\u4e86\u4e8b\u60c5\uff0c\u662f \u534e\u8001\u7237\u5b50\u7684\u4e8b\u60c5\u3002\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u53ef\u662f\u6709\u4eba\u77e5\u9053\u6211\u5728\u4f60\u5e84\u4e0a\u517b\u75c5\uff0c\u5374\u8fd8\u672a\u77e5\u9053\u6211\u5df2\u79bb\u5f00\uff0c \u5230\u4f60\u4eec\u90a3\u513f\u627e\u6211\u4e48\uff1f\u201d\u4e91\u5b89\u9053\uff1a\u201c\u8001\u7237\u5b50\u731c\u5bf9\u4e86\uff0c\u4f46\u6765\u627e\u4f60\u7684\u5374\u4e0d\u662f\u4eba\u3002\u201d\u534e\u5929\u98ce\u9053\uff1a \u201c\u4ec0\u4e48\uff1f\u4e0d\u662f\u4eba\uff01\u201d\u4e91\u5b89\u9053\u5bab\u201c\u662f\u4f60\u8001\u7684\u90a3\u5934\u795e\u9e70\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8bf4\u9053\uff1a\u201c\u5c31\u53ea\u662f\u6211\u90a3\u5934\u795e\u9e70\u4e48\uff1f\u201d\u4e91\u5b89\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5e76\u6ca1 \u6709\u4eba\u9a91\u7740\u5b83\u3002\u201d\u534e\u5929\u98ce\u9053\uff0c\u201c\u90a3\u5934\u795e\u9e70\u5462\uff1f\u4f60\u5e26\u5b83\u6765\u4e86\u6ca1\u6709\uff1f\u201d\u90a3\u5934\u795e\u9e70\u9887\u5177\u7075\u6027\uff0c\u534e \u5929\u98ce\u5fc3\u60f3\uff0c\u4e91\u5b89\u7cbe\u660e\u5e72\u7ec3\uff0c\u7eb5\u7136\u4e0d\u6562\u9a91\u5b83\uff0c\u4e5f\u5e94\u8be5\u628a\u5b83\u5e26\u6765\u7684\uff0c\u662f\u4ee5\u6709\u6b64\u4e00\u540c\u3002\u54ea\u77e5\u4e91 \u5b89\u7b54\u9053\uff1a\u201c\u90a3\u5934\u7985\u9e70\u53d7\u4f24\u9887\u91cd\uff0c\u6b63\u5728\u5e84\u4e0a\u517b\u4f24\u3002\u6211\u4e5f\u672a\u80fd\u786e\u77e5\u4f60\u8001\u7237\u5b50\u51c6\u5728\u8fd9\u513f\uff0c\u6240\u4ee5 \u672a\u66fe\u5c06\u5b83\u5e26\u6765\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u66f4\u662f\u5403\u60ca\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u5b83\u53d7\u4e86\u4ec0\u4e48\u4f24\uff1f\u201d\u4e91\u5b89\u9053\uff1a \u3000\u3000\u201c\u5b83\u7684\u4e24\u8fb9\u7fc5\u8180\u90fd\u5e26\u7740\u4e00\u679d\u77ed\u7bad\uff0c\u73b0\u5728\u7bad\u5df2\u653e\u4e0b\uff0c\u6211\u4eec\u4e5f\u5df2\u7ed9\u5b83\u6577\u4e0a\u4e86\u597d\u7684\u91d1\u521b\u836f \u4e86\u3002\u8fc7\u51e0\u5929\u5c31\u4f1a\u597d\u7684\uff0c\u8001\u7237\u5b50\u8bf7\u653e\u5fc3\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u65c1\u4eba\u8fd8\u4e0d\u600e\u4e48\uff0c\u6c5f\u6d77\u5929\u662f\u77e5\u9053\u8fd9\u5934\u795e\u9e70\u7684\u672c\u9886\u7684\uff0c\u53ef\u662f\u5927\u5927\u5403\u60ca\uff0c\u5fc3\u60f3\uff1a \u201c\u8fd9\u795e\u9e70\u53ef\u4ee5\u6293\u88c2\u72ee\u864e\uff0c\u8fde\u91d1\u6bdb\u72e1\u90fd\u4e0d\u662f\u5b83\u7684\u5bf9\u624b\uff0c\u4e14\u53c8\u662f\u5728\u5929\u4e0a\u98de\u884c\uff0c\u5c45\u7136\u80fd\u6709\u4eba\u5c04 \u4f24\u4e86\u5b83\uff01\u8fd9\u4e2a\u4eba\u662f\u8c01\u5462\uff1f\u5b83\u53d7\u4e86\u4f24\uff0c\u78a7\u59b9\u53c8\u4e0d\u77e5\u5982\u4f55\u4e86\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u5f53\u7136\u4e5f\u662f\u7acb\u5373\u60f3\u5230\u4e86\u4ed6\u5973\u513f\u7684\u5b89\u5371\uff0c\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u9664\u4e86\u90a3\u4e24\u679d\u77ed\u7bad\uff0c\u8fd8\u6709\u4ec0 \u4e48\u4e1c\u897f\uff1f\u53ef\u5e26\u6709\u4fe1\u4ef6\u6765\u4e48\uff1f\u201d \u3000\u3000\u4e91\u5b89\u9053\uff1a\u201c\u4fe1\u4ef6\u6ca1\u6709\uff0c\u4f46\u5374\u6709\u4e00\u5b97\u7269\u4e8b\u3002\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u5feb\u62ff\u51fa\u6765\u3002\u201d\u4e91\u5b89\u638f\u51fa\u4e00 \u4e2a\u5c0f\u5305\uff0c\u89e3\u5f00\u5305\u88f9\uff0c\u91cc\u9762\u6709\u4e00\u7247\u7834\u5e03\uff0c\u7834\u5e03\u4e0a\u7528\u4e00\u6839\u9488\u9489\u7740\u4e00\u6735\u67af\u840e\u7684\u82b1\u6735\uff0c\u8bf4\u9053\uff1a \u201c\u8fd9\u7247\u7834\u5e03\u662f\u7f1a\u5728\u9e70\u722a\u4e0a\u7684\uff0c\u5c0f\u4eba\u4e0d\u6562\u4e71\u52a8\uff0c\u4f9d\u7740\u539f\u6837\uff0c\u53e6\u7528\u56f4\u5dfe\u5305\u597d\u7684\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u63a5\u4e86\u8fc7\u6765\uff0c\u4ed4\u7ec6\u5ba1\u89c6\uff0c\u5148\u62d4\u4e0b\u4e86\u90a3\u6839\u9488\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u78a7\u513f\u7528\u7684\u6885\u82b1\u9488\u3002\u201d \u7834\u5e03\u4e0a\u6709\u51e0\u70b9\u8840\u6e0d\u3002\u6c5f\u6d77\u5929\u5fc3\u5934\u201c\u535c\u535c\u201d\u8df3\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5b9a\u7136\u662f\u78a7\u59b9\u7528\u6765\u5411\u5979\u7236\u4eb2\u62a5 \u4fe1\u7684\u4e86\u3002\u8fd9\u51e0\u70b9\u8840\u6e0d\u4e0d\u77e5\u662f\u4e0d\u662f\u5979\u523a\u7834\u6307\u5934\uff0c\u60f3\u5199\u8840\u4e66\u7684\uff1f\u4f46\u4f55\u4ee5\u4e0d\u89c1\u6587\u5b57\uff1f\u662f\u6765\u4e0d\u53ca \u5462\uff0c\u8fd8\u662f\u5e76\u975e\u81ea\u5df1\u523a\u7834\u7684\u6307\u8840\uff0c\u800c\u662f\u8eab\u4e0a\u53d7\u4e86\u654c\u4eba\u7684\u4f24\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u518d\u62ff\u8d77\u90a3\u6735\u67af\u840e\u7684\u82b1\u6735\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8138\u4e0a\u60ca\u5f02\u7684\u7985\u8272\u66f4\u6d53\u4e86\u3002\u4f17\u4eba\u4ed4\u7ec6 \u770b\u65f6\uff0c\u53ea\u89c1\u8fd9\u4e00\u6735\u82b1\u82b1\u74e3\u5206\u4e3a\u4e09\u8272\uff0c\u82b1\u4f3c\u8299\u84c9\uff0c\u4f46\u5374\u6bd4\u8299\u84c9\u5927\u5f97\u591a\u3002\u867d\u7136\u67af\u840e\uff0c\u90a3\u4e09\u79cd \u989c\u8272\u8fd8\u5f88\u9c9c\u660e\uff0c\u5916\u9762\u4e00\u5c42\u82b1\u74e3\u6d01\u767d\u5982\u96ea\uff0c\u4e2d\u95f4\u4e00\u5c42\u53d8\u4f5c\u5ae9\u9ec4\uff0c\u6700\u91cc\u9762\u4e00\u5c42\u6709\u51e0\u7247\u82b1\u74e3\u7c89 \u767d\u4e2d\u5e26\u4e00\u4e9b\u7ea2\u6655\uff0c\u5b9b\u5982\u5c11\u5973\u53cc\u988a\uff0c\u82e5\u662f\u672a\u66fe\u67af\u840e\uff0c\u4e00\u5b9a\u66f4\u4e3a\u597d\u770b\u3002 \u3000\u3000\u8fd9\u79cd\u5947\u82b1\uff0c\u8c01\u4e5f\u6ca1\u6709\u89c1\u8fc7\uff0c\u4f46\u8c01\u4e5f\u6ca1\u6709\u5fc3\u60c5\u6b23\u8d4f\uff0c\u90fd\u5728\u6697\u6697\u5600\u5495\uff1a\u201c\u534e\u5929\u98ce\u7684\u5973\u513f \u8981\u795e\u9e70\u5e26\u8fd9\u6735\u82b1\u7ed9\u5979\u7684\u7236\u4eb2\uff0c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u5c06\u82b1\u6735\u7ffb\u6765\u8986\u53bb\u5730\u770b\u4e86\u53c8\u770b\u3002\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\u3002\u201d\u4e91\u53ec\u3001\u6c5f\u6d77\u5929\u7b49\u4eba \u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u8fd9\u662f\u53ea\u6709\u5728\u963f\u5c14\u6cf0\u5c71\u7075\u9e6b\u5cf0\u4e0a\u624d\u80fd\u89c1\u5230\u7684\u4e09\u8272\u5947\u82b1\uff01\u5b83\u6709\u4e2a\u540d\u5b57\u53eb\u505a \u2018\u96ea\u91cc\u7ea2\u5986\u2019\uff0c\u82e5\u662f\u5e38\u670d\u6b64\u82b1\uff0c\u53ef\u4ee5\u6c38\u4fdd\u5bb9\u989c\u4e0d\u53d8\u3002\u201d\u539f\u6765\u534e\u5929\u98ce\u867d\u7136\u6ca1\u6709\u5230\u8fc7\u7075\u9e6b\u5cf0\uff0c \u4e5f\u4ece\u672a\u66fe\u89c1\u8fc7\u8fd9\u79cd\u5947\u82b1\uff0c\u4f46\u4ed6\u73cd\u85cf\u7684\u4e00\u90e8\u836f\u4e66\u4e0a\uff0c\u5374\u7ed8\u6709\u6b64\u82b1\u7684\u5f62\u72b6\u3002\u5e76\u6ce8\u660e\u5b83\u7684\u7528\u9014 \u7684\uff0c\u6240\u4ee5\u534e\u5929\u98ce\u7ec8\u4e8e\u8ba4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u534e\u5929\u98ce\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u78a7\u513f\u66fe\u8ddf\u6211\u5b66\u8fc7\u8ba4\u8bc6\u836f\u7269\uff0c\u5979\u5bf9\u8fd9\u2018\u96ea\u91cc\u7ea2\u5986\u2019\u751a\u611f\u5174\u8da3\uff0c\u4e5f \u66fe\u60f3\u8fc7\u8981\u5230\u7075\u9e6b\u5cf0\u91c7\u51e0\u6735\u56de\u6765\uff0c\u8bd5\u5728\u82b1\u5703\u683d\u690d\uff0c\u6211\u8ba4\u4e3a\u8fd9\u79cd\u5947\u82b1\u867d\u7136\u80fd\u4fdd\u5bb9\u989c\uff0c\u5374\u5e76\u65e0 \u533b\u7597\u4ef7\u503c\uff0c\u56e0\u6b64\u4e0d\u613f\u5192\u9669\u53bb\u91c7\u3002\u4e0d\u8fc7\uff0c\u8fd9\u6b21\u5979\u5927\u7ea6\u4e5f\u5e76\u975e\u662f\u60f3\u91c7\u8fd9\u79cd\u82b1\uff0c\u5979\u53eb\u795e\u9e70\u5c06\u8fd9 \u6735\u82b1\u5e26\u7ed9\u6211\uff0c\u4e43\u662f\u8981\u6211\u77e5\u9053\u5979\u662f\u5728\u7075\u9a9b\u5cf0\u4e0a\u9047\u7684\u9669\u3002 \u3000\u3000\u770b\u6765\u662f\u56e0\u795e\u9e70\u53d7\u4f24\u4e4b\u540e\uff0c\u6070\u597d\u964d\u843d\u7075\u9e6b\u5cf0\u5934\uff0c\u9644\u8fd1\u5c31\u6709\u8fd9\u79cd\u5947\u82b1\uff0c\u654c\u4eba\u5df2\u7ecf\u8feb\u8fd1\uff0c \u5979\u6765\u4e0d\u53ca\u5199\u8840\u4e66\uff0c\u6545\u800c\u53ea\u6709\u7528\u8fd9\u79cd\u529e\u6cd5\u62a5\u4fe1\uff0c\u4f46\u963f\u5c14\u6cf0\u5c71\u7ef5\u5ef6\u5343\u91cc\uff0c\u6211\u53ea\u77e5\u9053\u963f\u5c14\u6cf0\u5c71 \u6709\u4e2a\u7075\u9e6b\u5cf0\uff0c\u5374\u4e0d\u77e5\u9053\u5b83\u9760\u8fd1\u4f55\u65b9\uff0c\u5bfb\u627e\u8d77\u6765\u8fd8\u771f\u4e0d\u5bb9\u6613\u5462\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5ffd\u9053\uff0c\u201c\u6211\u77e5\u9053\u7075\u9e6b\u5cf0\u7684\u6240\u5728\u3002\u9a6c\u8428\u513f\u56fd\u5728\u963f\u5c14\u6cf0\u5c71\u4e4b\u5357\uff0c\u6606\u5e03\u5170\u56fd\u5728\u963f \u5c14\u6cf0\u5c71\u4e4b\u5317\uff0c\u4e2d\u95f4\u5c31\u662f\u4ee5\u8fd9\u5ea7\u7075\u9e6b\u5cf0\u5206\u754c\u7684\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u6b63\u597d\u4e00\u540c\u5230\u6606\u5e03\u5170\u56fd\u4e86\u3002\u201d\u6c5f\u5357\u5c1a\u672a\u77e5\u9053\u8c37\u4e2d\u83b2\u7684\u4e8b\u60c5\uff1a\u8bf4 \u9053\uff1a\u201c\u4f60\u4e49\u59b9\u9047\u96be\uff0c\u4f60\u73b0\u8be5\u5e2e\u4f60\u4e49\u7236\u627e\u5bfb\u3002\u201d\u6c5f\u6d77\u5929\u65e2\u611f\u5185\u759a\uff0c\u53c8\u89c9\u6101\u70e6\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a \u201c\u78a7\u59b9\u90a3\u5929\u82e5\u4e0d\u662f\u4e3a\u4e86\u751f\u6211\u7684\u6c14\uff0c\u5c31\u4e0d\u4f1a\u7a81\u7136\u98de\u8d70\uff0c\u8981\u662f\u5979\u548c\u6211\u4eec\u540c\u8d70\uff0c\u90a3\u5c31\u4e0d\u4f1a\u906d\u6b64 \u4e0d\u6d4b\u4e4b\u7978\u4e86\u3002\u5509\uff0c\u8fd9\u90fd\u662f\u6211\u5bb3\u4e86\u5979\u3002\u201d\u518d\u53c8\u60f3\u9053\uff1a\u201c\u83b2\u59b9\u4e5f\u5728\u6606\u5e03\u5170\u56fd\uff0c\u6211\u8fd9\u6b21\u524d\u5f80\uff0c \u4f46\u613f\u5c06\u5979\u4eec\u4e24\u4eba\u90fd\u6551\u4e86\u51fa\u6765\u3002\u4f46\u6211\u4e0e\u83b2\u59b9\u7684\u4e8b\u60c5\u53ef\u5c31\u4e0d\u80fd\u7792\u7740\u4e49\u7236\u4e86\uff0c\u5509\uff0c\u4ed6\u77e5\u9053\u4e86\uff0c \u4e0d\u77e5\u9053\u4f1a\u591a\u4f24\u5fc3\u5462\u3002\u5509\uff0c\u90a3\u53ea\u6709\u5230\u65f6\u518d\u8bf4\u4e86\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u4f17\u4eba\u4fbf\u5373\u5206\u9053\u626c\u9573\u3002\u53f6\u51b2\u9704\u4e0d\u6562\u6cc4\u6f0f\u673a\u5bc6\uff0c\u53ea\u8bf4\u662f\u8981\u56de\u56fd\u53bb\u6309\u59bb\u5b50\uff0c \u4ed6\u5cb3\u7236\u5cb3\u6bcd\u5f53\u7136\u662f\u6b23\u7136\u540c\u610f\uff0c\u6c5f\u6d77\u5929\u4e5f\u4e0e\u7236\u4eb2\u5206\u624b\uff0c\u6c5f\u5357\u5c06\u4ed6\u62c9\u8fc7\u4e00\u8fb9\uff0c\u6084\u6084\u53ee\u5631\u4ed6\u9053\uff1a \u201c\u53ea\u6709\u4e00\u592b\u4e00\u59bb\uff0c\u624d\u80fd\u548c\u8c10\u5230\u8001\uff0c\u4f60\u6551\u534e\u59d1\u5a18\u662f\u2018\u4e49\u2019\uff0c\u4f60\u5bf9\u8c37\u59d1\u5a18\u662f\u2018\u60c5\u2019\uff0c\u4f60\u53ef\u4e0d \u8981\u4e09\u5fc3\u4e8c\u610f\u624d\u597d\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6ee1\u9762\u901a\u7ea2\uff0c\u53ea\u597d\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\u3002\u201d\u6c5f\u5357\u9053\uff1a \u3000\u3000\u201c\u4f60\u4e8b\u6bd5\u4e4b\u540e\uff0c\u65e9\u65e9\u56de\u6765\uff0c\u6700\u597d\u662f\u540c\u8c37\u59d1\u5a18\u4e00\u540c\u56de\u6765\uff0c\u4e5f\u597d\u53eb\u4f60\u5988\u6b22\u559c\u3002\u201d\u6c5f\u6d77\u5929 \u5e94\u4e86\u4e00\u58f0\uff1a\u201c\u662f\u3002\u201d\u5fc3\u91cc\u5374\u60f3\uff1a\u201c\u672a\u6765\u4e4b\u4e8b\uff0c\u8c01\u80fd\u9884\u6599\uff1f\u8981\u662f\u78a7\u59b9\u5c1a\u5728\u4eba\u95f4\uff0c\u5979\u4e0d\u80af\u539f \u8c05\u6211\u7684\u8bdd\uff0c\u6211\u7d2f\u5979\u53d7\u4e86\u8fd9\u573a\u5927\u96be\uff0c\u6211\u53c8\u5c82\u80fd\u53e6\u5a36\uff0c\u53ea\u597d\u5b66\u6211\u5e08\u7236\u4e00\u6837\uff0c\u7ec8\u751f\u98d8\u8361\u6c5f\u6e56\u4e86\u3002\u201d \u3000\u3000\u4e91\u53ec\u4e0e\u534e\u5929\u98ce\u7684\u4ea4\u60c5\u975e\u6bd4\u5bfb\u5e38\uff0c\u534e\u5929\u98ce\u5411\u4ed6\u9053\u522b\uff0c\u4e91\u53ec\u63e1\u7740\u4ed6\u7684\u624b\u9053\uff1a\u201c\u534e\u5929\u54e5\uff0c \u6211\u7684\u513f\u5973\u662f\u4f60\u6551\u6d3b\u7684\uff0c\u4f60\u5973\u513f\u73b0\u5728\u9047\u96be\uff0c\u6211\u672c\u6765\u4e0d\u5e94\u8896\u624b\u65c1\u89c2\uff0c\u4f46\u2026\u2026\u201d\u534e\u5929\u98ce\u6253\u65ad\u4ed6 \u7684\u8bdd\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4f60\u9080\u6765\u7684\u5ba2\u4eba\uff0c\u8def\u8fc7\u5b9d\u5e84\uff0c\u4f60\u8fd8\u8981\u7565\u5c3d\u5730\u4e3b\u4e4b\u8c0a\u7684\uff0c\u4e0d\u53ef\u4e3a\u4e86\u6211\u7684 \u4e8b\u60c5\uff0c\u5931\u4e86\u793c\u6570\u3002\u6211\u6709\u6d77\u5929\u540c\u5728\uff0c\u7eb5\u7136\u78b0\u4e0a\u5f3a\u654c\uff0c\u5927\u7ea6\u4e5f\u603b\u53ef\u4ee5\u5bf9\u4ed8\u4e86\u3002\u201d \u3000\u3000\u4e91\u53ec\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u867d\u7136\u4e0d\u80fd\u524d\u5f80\uff0c\u4f46\u4ed6\u4eec\u5144\u59b9\u8fd8\u662f\u8981\u968f\u4f60\u4e00\u8d77\u53bb\u7684\u3002\u201d\u534e\u5929\u98ce\u9053\uff1a \u201c\u963f\u5c14\u6cf0\u5c71\u662f\u82e6\u5bd2\u4e4b\u5730\uff0c\u4e0d\u5fc5\u8ba9\u4ed6\u4eec\u5c0f\u8f88\u5192\u9669\u4e86\u3002\u201d\u4e91\u5885\u8bf4\u9053\uff1a\u201c\u6211\u548c\u4e91\u59d0\u59d0\u6bd4\u4eb2\u59d0\u59b9 \u8fd8\u4eb2\uff0c\u6211\u867d\u7136\u6b66\u529f\u4f4e\u5fae\uff0c\u5e2e\u4e0d\u4e86\u8001\u6015\u7684\u5fd9\uff0c\u4f46\u4f60\u603b\u8be5\u8ba9\u6211\u4e3a\u4e91\u59d0\u59d0\u5c3d\u4e00\u70b9\u5fc3\u3002\u201d\u4e91\u743c\u4e5f \u9053\uff1a\u201c\u6211\u4eec\u5144\u59b9\u7684\u6027\u547d\u662f\u8001\u4f2f\u4f60\u6551\u6d3b\u7684\uff0c\u4f60\u8981\u662f\u4e0d\u8ba9\u6211\u4eec\u540c\u53bb\uff0c\u6211\u4eec\u600e\u5f97\u5fc3\u5b89\u3002\u201d\u4e91\u53ec \u7b11\u9053\uff1a\u201c\u534e\u5929\u54e5\uff0c\u4f60\u5c31\u5e26\u4f60\u4e24\u4e2a\u4f84\u513f\u53bb\u5386\u7ec3\u5386\u7ec3\u5427\u3002\u201d\u534e\u5929\u98ce\u65e0\u6cd5\u518d\u63a8\uff0c\u53ea\u597d\u7b54\u5141\u4e86\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u5317\u884c\u8bf8\u4eba\u6362\u4e58\u4e86\u5510\u52aa\u73e0\u7a46\u5e26\u6765\u7684\u9a8f\u9a6c\uff0c\u4e00\u8def\u75be\u9a70\uff0c\u4e0d\u8fc7\u4e09\u5929\u5c31\u5230\u4e86\u9a6c\u8428\u513f\u56fd \u56fd\u5883\u3002\u53f6\u51b2\u9704\u79bb\u5f00\u5927\u961f\uff0c\u81ea\u5f80\u4eac\u57ce\u3002\u5510\u52aa\u73e0\u7a46\u8d76\u7740\u53bb\u6551\u59b9\u59b9\uff0c\u5c31\u4ece\u56fd\u5883\u7ed5\u8fc7\uff0c\u5e26\u8def\u524d\u884c\uff0c \u76f4\u5165\u963f\u5c14\u6cf0\u5c71\u5c71\u533a\u3002\u5c71\u5761\u9661\u62d4\uff0c\u5c71\u8def\u5d0e\u5c96\uff0c\u6709\u4e9b\u5730\u65b9\u6839\u672c\u65e0\u8def\u53ef\u901a\u901a\u3001\u5510\u52aa\u73e0\u7a46\u5c06\u9a6c\u5339 \u5708\u7ed9\u8fb9\u5883\u9a7b\u519b\uff0c\u6539\u4f5c\u6b65\u884c\u3002\u5510\u52aa\u73e0\u7a46\u4e0e\u4e91\u743c\u5144\u59b9\u90fd\u662f\u5e74\u7eaa\u76f8\u82e5\u7684\u5c11\u5e74\uff0c\u6570\u65e5\u540c\u884c\uff0c\u610f\u6c14 \u76f8\u6295\u3002\u4e91\u58c1\u5c24\u5176\u56e0\u4e3a\u5510\u52aa\u73e0\u7a46\u4e8e\u5979\u6709\u6551\u547d\u4e4b\u6069\u3002\u5bf9\u4ed6\u751a\u81f3\u6bd4\u5bf9\u6c5f\u6d77\u5929\u8fd8\u8981\u4eb2\u8fd1\u3002 \u3000\u3000\u4e91\u58c1\u5df2\u77e5\u9053\u5510\u52aa\u73e0\u7a46\u4e0e\u8c37\u4e2d\u83b2\u4e43\u662f\u5144\u59b9\uff0c\u8bf4\u8d77\u8c37\u4e2d\u83b2\u548c\u5979\u7684\u5e08\u7236\u4ece\u524d\u66fe\u5728\u6c34\u4e91\u5e84\u4f4f \u8fc7\u7684\u4e8b\u60c5\u3002\u5230\u4e86\u6b64\u65f6\uff0c\u5510\u52aa\u73e0\u7a46\u5df2\u65e0\u9700\u518d\u5bf9\u4ed6\u4eec\u9690\u7792\u4e86\uff0c\u4fbf\u9053\uff1a\u201c\u820d\u59b9\u6b63\u5728\u6606\u5e03\u5170\u56fd\uff0c \u6211\u6b64\u884c\u5c31\u662f\u53bb\u770b\u4ed6\u7684\u3002\u820d\u59b9\u8981\u662f\u77e5\u9053\u4f60\u4eec\u6765\u4e86\uff0c\u4e00\u5b9a\u4e5f\u662f\u5f88\u9ad8\u5174\u7684\u3002\u201d\u5f53\u4e0b\u5c06\u4ed6\u4eec\u9a6c\u8428 \u513f\u56fd\u78b0\u5230\u7684\u9ebb\u70e6\uff0c\u4ee5\u53ca\u8c37\u4e2d\u83b2\u5192\u5145\u672c\u56fd\u4f7f\u8005\u7684\u968f\u4ece\uff0c\u524d\u5f80\u6606\u5e03\u5170\u56fd\u7684\u524d\u56e0\u540e\u679c\u4e00\u4e00\u8bf4\u4e86\u3002 \u534e\u5929\u98ce\u8fd9\u624d\u77e5\u9053\u5510\u52aa\u73e0\u7a46\u539f\u6765\u662f\u4e3a\u4e86\u59b9\u59b9\u7684\u4e8b\u60c5\uff0c\u5e76\u975e\u53ea\u662f\u4e3a\u4ed6\u5e26\u8def\u3002 \u3000\u3000\u4f46\u5510\u52aa\u73e0\u7a46\u4ee5\u56fd\u738b\u7684\u8eab\u4efd\uff0c\u4e00\u8def\u966a\u4f34\u4ed6\u4eec\uff0c\u7ed9\u4ed6\u4eec\u6307\u5f15\u9053\u8def\uff0c\u534e\u5929\u98ce\u4e5f\u662f\u611f\u6fc0\u5f97\u5f88\uff0c \u8bf4\u9053\uff1a\u201c\u539f\u6765\u4ee4\u59b9\u5c31\u662f\u9099\u5c71\u8c37\u638c\u95e8\u7684\u9ad8\u8db3\uff0c\u8001\u673d\u5c11\u65f6\uff0c\u66fe\u53d7\u8fc7\u9099\u5c71\u6d3e\u4e0a\u4ee3\u638c\u95e8\u5415\u5973\u4fa0\u5415 \u56db\u5a18\u7684\u6307\u70b9\uff0c\u9099\u5c71\u6d3e\u4e2d\u7684\u5357\u4e10\u5e2e\u5e2e\u4e3b\u7ffc\u4ef2\u725f\u4e0e\u8001\u673d\u7684\u4ea4\u60c5\u4e5f\u975e\u4e00\u65e5\uff0c\u8bf4\u6765\u90fd\u4e0d\u662f\u5916\u4eba\u3002 \u8fd9\u6b21\u6211\u4eec\u4ece\u7075\u9e6b\u5cf0\u7ecf\u8fc7\uff0c\u4e0d\u8bba\u662f\u5426\u627e\u5f97\u7740\u5c0f\u5973\uff0c\u6211\u90fd\u968f\u4f60\u4eec\u5230\u6606\u5e03\u5170\u56fd\u8d70\u4e00\u906d\u5427\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u77e5\u9053\u534e\u5929\u98ce\u4e43\u662f\u5f53\u4ee3\u7b2c\u4e00\u795e\u533b\uff0c\u8bf4\u4e0d\u5b9a\u6709\u8981\u4ed6\u5e2e\u5fd9\u4e4b\u5904\uff0c\u5927\u559c\u8bf4\u9053\uff1a\u201c\u5f97 \u534e\u8001\u524d\u8f88\u540c\u4f4f\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u53ea\u662f\u592a\u8fc7\u9ebb\u70e6\u8001\u524d\u8f88\u4e86\u3002\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u54ea\u91cc\u8bdd\u6765\uff1f\u8fd9 \u6b21\u5c0f\u5973\u9047\u96be\uff0c\u4e5f\u662f\u5168\u9760\u965b\u4e0b\u6307\u70b9\u9053\u8def\uff0c\u8981\u4e0d\u7136\u6211\u8fd8\u4e0d\u77e5\u9053\u7075\u9e6b\u5cf0\u5750\u843d\u4f55\u5904\u5462\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u53ea\u5e8f\u5c0a\u5351\u4e4b\u522b\uff0c\u665a\u8f88\u5bb6\u5e08\u4e0e\u534e\u8001\u524d\u8f88\u4e43\u662f\u540c\u4e00\u8f88\u4efd\uff0c \u8bf7\u8001\u524d\u8f88\u5207\u52ff\u4ee5\u2018\u965b\u4e0b\u2019\u4e8c\u5b57\u76f8\u79f0\u3002\u201d\u534e\u5929\u98ce\u6027\u60c5\u723d\u6717\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e16\u5144\u65e2\u7136\u4ee5\u6b66\u6797 \u4e2d\u4eba\u81ea\u5c45\uff0c\u90a3\u5c31\u8bf7\u6055\u8001\u673d\u6258\u5927\uff0c\u79f0\u4f60\u4e00\u58f0\u4e16\u5144\u5427\u3002\u201d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5c0f\u5973\u5728\u7075\u9e6b\u5cf0\u9047\u96be\uff0c \u6b64\u523b\u5374\u4e0d\u4e00\u5b9a\u8fd8\u5728\u7075\u9e6b\u5cf0\u4e0a\uff0c\u591a\u534a\u662f\u78b0\u4e0d\u89c1\u7684\u4e86\u3002\u8fc7\u4e86\u7075\u9e6b\u5cf0\uff0c\u5728\u6606\u5e03\u5170\u56fd\uff0c\u6211\u8fd8\u8981\u7ee7 \u7eed\u67e5\u63a2\u5979\u7684\u4e0b\u843d\u5462\u3002\u6240\u4ee5\u6211\u966a\u4e16\u5144\u524d\u5f80\uff0c\u6b63\u662f\u4e00\u4e3e\u4e24\u5f97\u3002\u201d\u4ed6\u60f3\u8d77\u5973\u513f\u751f\u6b7b\u672a\u535c\uff0c\u51f6\u5409 \u96be\u77e5\uff0c\u867d\u662f\u6027\u60c5\u723d\u6717\uff0c\u8a00\u4e0b\u4e5f\u4e0d\u7981\u6709\u70b9\u9eef\u7136\u3002 \u3000\u3000\u4e91\u58c1\u7b11\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u4e0d\u662f\u5f88\u60f3\u5ff5\u8c37\u5973\u4fa0\u5417\uff1f\u8fc7\u4e86\u6b64\u5cf0\uff0c\u5c31\u662f\u6606\u5e03\u5170\u56fd\u4e86\uff0c\u8bf4\u4e0d\u5b9a \u4f60\u4eec\u5c31\u53ef\u4ee5\u89c1\u9762\u5462\u3002\u201d\u4e91\u743c\u6027\u60c5\u7f9e\u602f\uff0c\u8981\u662f\u5e73\u65e5\u542c\u4ed6\u59b9\u59b9\u5982\u6b64\u4e00\u8bf4\uff0c\u5b9a\u4f1a\u7f9e\u5f97\u8138\u7ea2\uff0c\u6b64 \u65f6\u5374\u662f\u843d\u843d\u5927\u65b9\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u6b66\u529f\u4f4e\u5fae\uff0c\u53ea\u6015\u5e2e\u4e0d\u4e86\u4ec0\u4e48\u5fd9\uff0c\u5230\u4e86\u6606\u5e03\u5170\u56fd\uff0c\u90a3 \u5c31\u8981\u9760\u6c5f\u5927\u54e5\u51fa\u529b\u4e86\u3002\u201d\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u6c5f\u5e08\u5144\u662f\u81ea\u5df1\u4eba\uff0c\u8fd9\u662f\u4e0d\u7528\u8bf4\u7684\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u4e91\u743c\u66fe\u6258\u6c5f\u6d77\u5929\u4ee3\u4ed6\u5411\u8c37\u4e2d\u83b2\u95ee\u5019\uff0c\u6c5f\u6d77\u5929\u5728\u8def\u4e0a\u5df2\u548c\u4ed6\u8bf4\u4e86\uff0c\u6c5f\u6d77\u5929\u867d\u7136\u6ca1 \u6709\u660e\u767d\u8bf4\u51fa\u4ed6\u548c\u8c37\u4e2d\u83b2\u7684\u5173\u7cfb\uff0c\u4f46\u8bed\u6c14\u795e\u6001\u4e4b\u95f4\uff0c\u603b\u662f\u6709\u70b9\u4e0d\u5927\u81ea\u7136\u3002\u4e91\u743c\u6027\u60c5\u5185\u5411\uff0c \u5584\u4e8e\u89c2\u8a00\u5bdf\u8272\uff0c\u8fd9\u51e0\u65e5\u4e0e\u5510\u52aa\u73e0\u7a46\u3001\u6c5f\u6d77\u5929\u4e8c\u4eba\u4e00\u8def\u540c\u884c\uff0c\u6709\u5fc3\u4eba\u542c\u4ed6\u4eec\u65e0\u5fc3\u7684\u8bf4\u8bdd\uff0c \u4e5f\u65e9\u5df2\u731c\u5230\u51e0\u5206\u4e86\u3002\u6700\u521d\u5fc3\u91cc\u867d\u6709\u70b9\u96be\u8fc7\uff0c\u4f46\u4ed6\u548c\u6c5f\u6d77\u5929\u662f\u5144\u5f1f\u822c\u7684\u60c5\u8c0a\uff0c\u6c5f\u6d77\u5929\u4e8e\u4ed6 \u53c8\u6709\u6551\u547d\u4e4b\u6069\uff0c\u56e0\u800c\u53ea\u4e0d\u8fc7\u96be\u53d7\u7247\u65f6\uff0c\u8fc7\u540e\u53cd\u800c\u4e3a\u8c37\u4e2d\u83b2\u800c\u611f\u5230\u9ad8\u5174\u4e86\u3002 \u3000\u3000\u963f\u5c14\u6cf0\u5c71\u662f\u4e16\u754c\u8457\u540d\u7684\u5c71\u8109\u4e4b\u4e00\uff0c\u5730\u52bf\u9ad8\u7ea7\uff0c\u5c71\u8def\u96be\u884c\uff0c\u5012\u8fd8\u7f62\u4e86\uff0c\u9ad8\u539f\u7a7a\u6c14\u7a00\u8584\uff0c \u5230\u4e86\u6d77\u62d4\u4e00\u4e07\u5c3a\u4ee5\u4e0a\uff0c\u547c\u5438\u4e5f\u611f\u56f0\u96be\u3002 \u3000\u3000\u800c\u4e14\u7531\u4e8e\u7a7a\u6c14\u7a00\u8584\u7684\u7f18\u6545\uff0c\u65e5\u5934\u76f4\u5c04\u4e0b\u6765\uff0c\u4e5f\u70ed\u5f97\u9a87\u4eba\uff0c\u4f46\u4e00\u5230\u592a\u9633\u7167\u5c04\u4e0d\u5230\u7684\u9634 \u5f71\u4e4b\u5904\uff0c\u6216\u662f\u5230\u4e86\u7ea2\u65e5\u6c89\u897f\u4e4b\u540e\uff0c\u5374\u53c8\u662f\u51b7\u6c14\u6c81\u4eba\uff0c\u4e25\u5bd2\u71ac\u9aa8\u3002\u4f3c\u8fd9\u6837\u7684\u66b4\u51b7\u66b4\u70ed\uff0c\u5f53 \u771f\u662f\u94dc\u76ae\u94c1\u9aa8\uff0c\u4e5f\u611f\u96be\u6328\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u3001\u5510\u52aa\u73e0\u7a46\u3001\u534e\u5929\u98ce\u4e09\u4eba\u5185\u529f\u6df1\u539a\uff0c\u8fd8\u53ef\u4ee5\u52c9\u5f3a\u652f\u6301\uff0c\u4e91\u743c\u5144\u59b9\u4e8c\u4eba\uff0c\u5230\u4e86 \u5c71\u8170\uff0c\u5df2\u7981\u4e0d\u4f4f\u7259\u5173\u6253\u6218\u3002\u5e78\u4e8f\u534e\u5929\u98ce\u65e9\u6709\u51c6\u5907\uff0c\u914d\u6709\u4e24\u670d\u201c\u9633\u548c\u4e38\u201d\uff0c\u6bcf\u670d\u5341\u4e8c\u9897\uff0c \u8ba9\u4ed6\u4eec\u65e9\u665a\u4e24\u6b21\uff0c\u6bcf\u6b21\u670d\u98df\u4e09\u9897\uff0c\u8fd9\u9633\u548c\u4e5d\u53ef\u4ee5\u5e2e\u52a9\u8840\u8109\u8fd0\u884c\uff0c\u53d1\u70ed\u5fa1\u51b7\uff0c\u4f30\u8ba1\u5728\u4e24\u65e5 \u4e4b\u5185\uff0c\u5c31\u53ef\u4ee5\u7ed5\u8fc7\u7075\u9e6b\u5cf0\uff0c\u8d70\u51fa\u9634\u98ce\u5cea\uff0c\u90a3\u65f6\u5230\u4e86\u5c71\u9633\uff0c\u518d\u51cf\u4f4e\u767b\u5c71\u7684\u9ad8\u5ea6\uff0c\u4fbf\u53ef\u65e0\u59a8 \u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u5348\u95f4\uff0c\u8fd9\u4e00\u884c\u4eba\u5df2\u5230\u4e86\u7075\u9e6b\u5cf0\u4e0a\uff0c\u7075\u9e6b\u5cf0\u5f62\u5982\u5927\u9e1f\uff0c\u4e2d\u95f4\u4e3b\u5cf0\u9ad8\u5165\u4e91\u9704\uff0c\u4e24 \u8fb9\u5c55\u5f00\uff0c\u5f62\u5982\u9e1f\u7ffc\uff0c\u5176\u95f4\u51b0\u5ddd\u4ea4\u9519\uff0c\u53c8\u5c38\u82e5\u94f6\u86c7\u5728\u5c71\u95f4\u6d41\u7a9c\u3002\u534e\u5929\u98ce\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u963f\u5c14 \u6cf0\u5c71\u4e09\u5927\u9ad8\u5cf0\uff0c\u7075\u9e6b\u5cf0\u8fd8\u4e0d\u5728\u5176\u5185\uff0c\u5df2\u7ecf\u662f\u8fd9\u6837\u96be\u4e0a\u4e86\u3002\u6211\u6240\u4f4f\u7684\u534e\u5c71\u3001\u53f7\u79f0\u2018\u5929\u9669\u2019\uff0c \u5982\u4eca\u5230\u4e86\u7075\u9e6b\u5cf0\u524d\uff0c\u624d\u77e5\u534e\u5c71\u5929\u9669\uff0c\u5b9e\u5728\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002 \u3000\u3000\u53e4\u4eba\u6240\u8bf4\u7684\u2018\u4e00\u5c71\u8fd8\u6709\u4e00\u5c71\u9ad8\u2019\uff0c\u5f53\u771f\u662f\u81f3\u7406\u540d\u8a00\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u963f\u5c14\u6cf0\u5c71\u7684\u6700\u9ad8\u5cf0\u8fd8\u8fdc\u8fdc\u6bd4\u4e0d\u4e0a\u559c\u9a6c\u62c9\u96c5\u5c71\u7684\u73e0\u7a46\u6717\u739b\u5cf0\uff0c\u73e0\u7a46\u6717 \u739b\u5cf0\uff0c\u5f53\u5e74\u6211\u7684\u5e08\u7236\u4e5f\u4e0d\u80fd\u6500\u767b\u7edd\u9876\u3002\u201d\u534e\u5929\u98ce\u9eef\u7136\u4e0d\u8bed\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u7684\u78a7\u51e0\u5018\u82e5 \u662f\u5728\u7075\u9e6b\u5cf0\u7684\u7edd\u9876\u9047\u9669\uff0c\u83ab\u8bf4\u9047\u4e0a\u5f3a\u654c\uff0c\u5373\u7b97\u6beb\u65e0\u5916\u7269\u4fb5\u6270\uff0c\u5979\u4e5f\u8981\u51b7\u6b7b\u7684\u3002\u9664\u975e\u5f53\u65f6 \u5c31\u6709\u4eba\u6551\u5979\u3002\u5509\uff0c\u4f46\u54ea\u6709\u8fd9\u6837\u5de7\u4e8b\uff1f\u770b\u6765\u5979\u662f\u51f6\u591a\u5409\u5c11\u4e86\uff01\u53ea\u6709\u5c3d\u4eba\u4e8b\u800c\u542c\u5929\u547d\u5427\uff01\u201d \u3000\u3000\u534e\u5929\u98ce\u4e0e\u6c5f\u6d77\u5929\u5206\u5934\u5728\u4e24\u9762\u4fa7\u5cf0\u641c\u7d22\uff0c\u4f46\u89c1\u79ef\u96ea\u7691\u7691\uff0c\u8fde\u517d\u8e44\u9e1f\u8ff9\u4e5f\u6ca1\u53d1\u73b0\uff0c\u66f4\u522b \u8bf4\u6709\u4eba\u4e86\u3002\u4e24\u4eba\u56de\u5230\u4e2d\u95f4\u7684\u4e3b\u5cf0\uff0c\u90fd\u662f\u610f\u5174\u8427\u7d22\uff0c\u76f8\u5bf9\u65e0\u8a00\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u5510\u52aa\u73e0\u7a46\u9053\uff1a \u201c\u5927\u7ea6\u8fd8\u6709\u534a\u4e2a\u65f6\u8fb0\uff0c\u5c31\u53ef\u4ee5\u7a7f\u8fc7\u4e3b\u5cf0\uff0c\u54b1\u4eec\u518d\u5f80\u4e0a\u8d70\u3002\u201d\u534e\u5929\u98ce\u82e6\u7b11\u9053\uff1a \u3000\u3000\u5ce8\u5df2\u7ecf\u611f\u5230\u6709\u70b9\u5403\u4e0d\u6d88\u4e86\u3001\u6211\u770b\u4e0d\u5fc5\u5230\u4e3b\u5cf0\u7684\u6700\u9ad8\u5904\u4e86\uff0c\u5f85\u6211\u7528\u4f20\u97f3\u5165\u5bc6\u4e4b\u6cd5\u547c\u5524 \u5979\u5427\u3002\u201d \u3000\u3000\u539f\u6765\u534e\u5929\u98ce\u5012\u8fd8\u652f\u6301\u5f97\u4f4f\uff0c\u53ea\u662f\u4ed6\u4e0d\u613f\u4e91\u743c\u5144\u59b9\u966a\u4ed6\u53d7\u82e6\uff0c\u6545\u800c\u5982\u6b64\u8bf4\u6cd5\u3002\u6c5f\u6d77\u5929 \u9053\uff1a\u201c\u4e0d\u5982\u8ba9\u6211\u7528\u5929\u9041\u4f20\u97f3\u4e4b\u6cd5\u8bd5\u8bd5\u3002\u201d\u5f53\u4e0b\u5c06\u58f0\u97f3\u51dd\u6210\u4e00\u7ebf\uff0c\u8fdc\u8fdc\u5730\u9001\u51fa\u53bb\uff0c\u53eb\u9053\uff1a \u201c\u78a7\u59b9\uff0c\u6211\u4eec\u6765\u627e\u4f60\u4e86\uff0c\u4f60\u8981\u662f\u542c\u89c1\u7684\u6d3b\uff0c\u8bf7\u629b\u4e00\u9897\u77f3\u5b50\u4e0b\u6765\uff01\u201d \u3000\u3000\u5929\u9041\u4f20\u97f3\u4e4b\u672f\uff0c\u7ec3\u5230\u6700\u9ad8\u5883\u754c\uff0c\u53ef\u4ee5\u628a\u58f0\u97f3\u76f4\u9001\u5230\u5bf9\u65b9\u8033\u4e2d\uff0c\u65c1\u4eba\u90fd\u542c\u4e0d\u89c1\uff0c\u4f46\u9876 \u591a\u4e5f\u4e0d\u8fc7\u9001\u5230\u4e09\u91cc\u4e4b\u5185\u3002\u73b0\u5728\u6c5f\u6d77\u5929\u8ba9\u58f0\u97f3\u51dd\u6210\u4e00\u7ebf\u53d1\u51fa\uff0c\u65c1\u4eba\u4e5f\u53ef\u4ee5\u542c\u89c1\uff0c\u4f46\u5374\u53ef\u9001 \u5230\u4e94\u91cc\u4e4b\u5916\uff0c\u4f30\u8ba1\u5c3d\u53ef\u4ee5\u8fbe\u5230\u5cf0\u9876\u4e86\u3002 \u3000\u3000\u534e\u5929\u98ce\u5fc3\u9053\uff1a\u201c\u6d77\u513f\u7684\u5929\u9041\u4f20\u97f3\u679c\u7136\u6bd4\u6211\u7684\u4f20\u97f3\u4eba\u5bc6\u8fd8\u8981\u9ad8\u660e\u5f97\u591a\uff0c\u800c\u4e14\u53ef\u4ee5\u514d\u53bb \u96ea\u5d29\u7684\u5371\u9669\u3002\u4f46\u8fd9\u4e5f\u53ea\u662f\u59d1\u4e14\u4e00\u8bd5\u7f62\u4e86\u3002\u201d \u3000\u3000\u53eb\u4e86\u51e0\u58f0\uff0c\u6beb\u65e0\u53cd\u5e94\uff0c\u534e\u3001\u6c5f\u4e8c\u4eba\u672c\u6765\u5c31\u4e0d\u6562\u5b58\u6709\u5962\u671b\uff0c\u6ca1\u6709\u53cd\u5e94\u4e43\u662f\u5728\u4ed6\u4eec\u610f\u6599 \u4e4b\u4e2d\uff0c\u4e8e\u662f\u7ee7\u7eed\u5411\u524d\u8d70\u53bb\u3002\u524d\u9762\u662f\u4e00\u5757\u5de8\u5927\u7684\u51b0\u5ca9\uff0c\u5e73\u5730\u62d4\u8d77\uff0c\u5149\u6ed1\u5982\u955c\uff0c\u5728\u9633\u5149\u4e0b\u53d1 \u51fa\u95ea\u95ea\u5bd2\u5149\u3002 \u3000\u3000\u5ffd\u7136\u6709\u9897\u77f3\u5b50\u4ece\u8fd9\u4fe8\u5982\u5ced\u58c1\u7684\u51b0\u5ca9\u4e0a\u6eda\u4e86\u4e0b\u6765\uff0c\u6c5f\u6d77\u5929\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c \u5446\u4e86\u4e00\u5446\uff0c\u53eb\u9053\uff1a\u201c\u662f\u78a7\u59b9\u542c\u5230\u54b1\u4eec\u53eb\u5979\u4e86\uff01\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u53ea\u6015\u8fd9\u9897\u77f3\u5b50\u662f\u5076\u7136\u6389\u843d \u7684\u3002\u201d\u4ed6\u4e5f\u4e0d\u6562\u76f8\u4fe1\u8fd9\u77f3\u5b50\u5f53\u771f\u5c31\u662f\u4ed6\u7684\u5973\u513f\u6240\u63b7\u3002\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u7b2c\u4e8c\u9897\u7b2c\u4e09\u9897\u77f3\u5b50 \u53c8\u63a5\u7eed\u6eda\u4e86\u4e0b\u6765\u3002\u6c5f\u6d77\u5929\u5927\u559c\u9053\uff1a\u201c\u8fd9\u51b3\u4e0d\u662f\u5076\u7136\u7684\u4e86\uff01\u78a7\u59b9\uff0c\u78a7\u59b9\uff0c\u6211\u6765\u5566\uff01\u201d \u3000\u3000\u51b0\u5ca9\u5149\u6ed1\u5982\u955c\uff0c\u6beb\u65e0\u53ef\u4ee5\u7740\u624b\u6500\u7f18\u4e4b\u5904\uff0c\u6c5f\u6d77\u5929\u4e0d\u987e\u900f\u9aa8\u5947\u5bd2\uff0c\u5c06\u8eab\u5b50\u8d34\u7740\u51b0\u5ca9\uff0c \u4fbf\u7528\u201c\u58c1\u864e\u6e38\u5899\u529f\u201d\u201c\u6e38\u201d\u4e86\u4e0a\u53bb\uff0c\u4f46\u8fd9\u51b0\u5ca9\u5374\u4e0d\u6bd4\u5899\u58c1\uff0c\u6709\u51e0\u5904\u5fae\u5fae\u51f8\u51fa\u7684\u4e43\u662f\u8f83\u8584 \u7684\u51b0\u68f1\uff0c\u88ab\u4ed6\u7684\u8eab\u4f53\u4e00\u538b\uff0c\u51b0\u7247\u788e\u88c2\uff0c\u51e0\u4e4e\u5c06\u4ed6\u8dcc\u4e86\u4e0b\u6765\uff0c\u5e78\u800c\u6c5f\u6d77\u5929\u8f7b\u529f\u8d85\u5353\uff0c\u4e00\u89c9 \u4e0d\u5999\uff0c\u7acb\u5373\u62d4\u51fa\u5b9d\u5251\uff0c\u63d2\u8fdb\u51b0\u5c42\uff0c\u5b9a\u7740\u8eab\u5b50\u3002\u4ee5\u201c\u58c1\u864e\u6e38\u5899\u529f\u201d\u4e0e\u5251\u63d2\u51b0\u5ca9\u4e4b\u6cd5\u4ea4\u66ff\u8fd0 \u7528\uff0c\u6e10\u6e10\u8d8a\u4e0a\u8d8a\u9ad8\uff0c\u5df2\u7ecf\u9690\u9690\u53ef\u4ee5\u770b\u5f97\u89c1\u5cf0\u9876\u6709\u4e00\u95f4\u5c4b\u5b50\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u96ea\u5cf0\u9ad8\u5904\u7adf\u7136\u4f4f\u6709\u4eba\u5bb6\uff01\u201d\u503e\u5373\u60f3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u78a7\u59b9\u5b9a\u7136 \u662f\u88ab\u8fd9\u5c4b\u5b50\u7684\u4e3b\u4eba\u6551\u4e86\uff0c\u8981\u4e0d\u7136\u5979\u600e\u80fd\u7981\u53d7\u5cf0\u9876\u7684\u4e25\u5bd2\u3002\u201d\u4f46\u4ed6\u5168\u795e\u8d2f\u6ce8\uff0c\u6500\u767b\u51b0\u5ca9\uff0c \u5df2\u4e0d\u80fd\u518d\u7528\u5929\u9041\u4f20\u97f3\u4e4b\u672f\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u53c8\u542c\u5f97\u4e00\u7f15\u8427\u58f0\u968f\u7740\u5c71\u98ce\u98d8\u6765\uff0c\u5982\u6ce3\u5982\u8bc9\uff0c\u5982\u6028\u5982\u6155\uff0c\u97f3\u7ec6\u800c\u6e05\uff0c\u4f3c \u662f\u6ee1\u6000\u5fc3\u4e8b\uff0c\u8981\u627e\u77e5\u97f3\u503e\u5410\u3002\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u539f\u6765\u517d\u59b9\u5439\u5f97\u8fd9\u6837\u597d\u7684\u6d1e\u8427\u3002\u542c\u8fd9\u8427\u58f0\uff0c \u6562\u60c5\u5979\u5bf9\u6211\u4ecd\u662f\u4e00\u8154\u5e7d\u6028\uff1f\u201d\u5fc3\u5934\u751a\u611f\u5185\u759a\uff0c\u4f46\u4ed6\u5386\u5c3d\u8270\u96be\uff0c\u7ec8\u4e8e\u53d1\u73b0\u4e86\u534e\u4e91\u78a7\u7684\u4e0b\u843d\uff0c \u542c\u5230\u4e86\u5979\u7684\u8427\u58f0\uff0c\u8fd9\u4efd\u9ad8\u5174\u4e5f\u662f\u96be\u4ee5\u5f62\u5bb9\uff0c\u5411\u4e0a\u6500\u767b\u66f4\u5feb\u4e86\u3002 \u3000\u3000\u4e0d\u4e45\uff0c\u53ea\u6709\u6570\u4e08\u9ad8\u5ea6\uff0c\u5c31\u53ef\u4ee5\u767b\u4e0a\u8702\u9876\u4e86\uff0c\u5ffd\u7136\u53c8\u6709\u9897\u77f3\u5b50\u629b\u4e86\u4e0b\u6765\uff0c\u77f3\u5b50\u4ece\u4ed6\u8eab \u8fb9\u6d6a\u8fc7\uff0c\u6253\u88c2\u4e86\u51e0\u7247\u51b0\u68f1\uff0c\u6c5f\u6d77\u5929\u811a\u5c16\u7528\u529b\u5728\u51b0\u5ca9\u4e00\u8e6c\uff0c\u500f\u5730\u98de\u8d77\uff0c\u76f4\u4e0a\u5dc5\u5cf0\uff0c\u53eb\u9053\uff1a \u201c\u78a7\u59b9\uff0c\u6211\u6765\u5566\uff01\u4f60\u4e0d\u8981\u629b\u77f3\u5b50\u4e86\uff01\u201d \u3000\u3000\u5ffd\u542c\u5f97\u201c\u545c\u201d\u7684\u4e00\u58f0\u602a\u53eb\uff0c\u5728\u4ed6\u9762\u524d\u7684\u7adf\u662f\u4e00\u53ea\u602a\u517d\uff0c\u90a3\u602a\u517d\u540e\u8e44\u4eba\u7acb\uff0c\u524d\u8e44\u8fd8\u6293 \u7740\u4e00\u9897\u77f3\u5b50\uff0c\u5728\u5b83\u4fa7\u9762\u8fd8\u6709\u4e00\u53ea\u540c\u6837\u7684\u602a\u517d\uff0c\u9f87\u7259\u9732\u9f7f\u7684\u5411\u7740\u4ed6\u3002\u6c5f\u6d77\u5929\u5b9a\u775b\u4e00\u770b\uff0c\u8ba4 \u5f97\u662f\u5389\u590d\u751f\u90a3\u4e24\u5934\u91d1\u6bdb\u72e1\u3002\u6c5f\u6d77\u5929\u597d\u4e0d\u5bb9\u6613\u624d\u5230\u8fbe\u51b0\u5ca9\u7edd\u9876\uff0c\u54ea\u77e5\u89c1\u4e0d\u7740\u534e\u4e91\u78a7\u5374\u89c1\u4e86 \u8fd9\u4e24\u5934\u91d1\u6bdb\u72e1\uff0c\u4e0d\u7981\u5927\u6012\u9a82\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u8fd9\u4e24\u5934\u755c\u7272\u620f\u5f04\u4e8e\u6211\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8ba4\u5f97\u8fd9\u4e24\u5934\u91d1\u6bdb\u72e1\uff0c\u8fd9\u4e24\u5934\u91d1\u6bdb\u72e1\u4e5f\u8ba4\u5f97\u6c5f\u6d77\u5929\uff0c\u5b83\u4eec\u66fe\u5728\u6c5f\u6d77\u5929\u624b\u4e0b\u5403\u8fc7 \u51e0\u6b21\u5927\u4e8f\uff0c\u7109\u80fd\u4e0d\u6015\uff1f\u4e00\u89c1\u662f\u6c5f\u6d77\u5929\u4e0a\u6765\uff0c\u545c\u545c\u7684\u53eb\u4e86\u4e24\u58f0\uff0c\u5939\u7740\u5c3e\u5df4\u4fbf\u8dd1\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u91d1\u6bdb\u72e1\u65e2\u7136\u5728\u8fd9\u91cc\u53d1\u73b0\uff0c\u5389\u590d\u751f\u60f3\u5fc5\u4e5f\u5728\u8fd9\u513f\u4e86\u3002\u6211\u4e14\u5230\u5c4b\u4e2d\u770b\u770b\u3002\u201d \u8fd9\u65f6\u4ed6\u521a\u521a\u7ad9\u7a33\u811a\u8ddf\uff0c\u8fd8\u672a\u6765\u5f97\u53ca\u56db\u5468\u5bdf\u770b\uff0c\u5ffd\u542c\u5f97\u6709\u4e2a\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4f60\u522b\u9a82\u8fd9\u4e24\u5934\u755c \u7272\uff0c\u662f\u6211\u53eb\u5b83\u4eec\u5c06\u77f3\u5b50\u6254\u4e0b\u53bb\u7684\u3002\u201d\u58f0\u97f3\u5c31\u5728\u4ed6\u7684\u8eab\u8fb9\uff0c\u6c5f\u6d77\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u62ac\u5934\u770b\u65f6\uff0c \u53ea\u89c1\u662f\u4e00\u4e2a\u62ab\u7740\u767d\u72d0\u88d8\u7684\u5c11\u5e74\uff0c\u62ff\u770b\u4e00\u652f\u6d1e\u8427\uff0c\u7ad9\u5728\u4e00\u5757\u78e8\u76d8\u4f3c\u7684\u51b0\u5757\u4e0a\uff0c\u51b7\u51b7\u7684\u770b\u7740 \u4ed6\u3002\u90a3\u5c11\u5e74\u62ab\u7740\u767d\u88d8\uff0c\u53c8\u662f\u7ad9\u5728\u51b0\u5757\u4e4b\u4e0a\uff0c\u8981\u4e0d\u662f\u5730\u4e0a\u6709\u4ed6\u7684\u5f71\u5b50\uff0c\u7b80\u76f4\u770b\u4e0d\u51fa\u90a3\u91cc\u6709 \u4e00\u4e2a\u4eba\u3002\u6c5f\u6d77\u5929\u521d\u4e0a\u6765\u65f6\uff0c\u53ea\u89c1\u90a3\u4e24\u5934\u91d1\u6bdb\u72e1\uff0c\u7adf\u672a\u53d1\u73b0\u90a3\u5c11\u5e74\u5c31\u5728\u4ed6\u8eab\u7554\u7684\u51b0\u5757\u4e0a\uff0c \u5c31\u662f\u8fd9\u4e2a\u7f18\u6545\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8fd9\u624d\u77e5\u9053\uff0c\u521a\u624d\u5439\u8427\u7684\u90a3\u4eba\u4e5f\u5e76\u4e0d\u662f\u534e\u4e91\u78a7\u800c\u662f\u8fd9\u4e2a\u5c11\u5e74\uff0c\u4e0d\u7981\u5fc3\u5934\u6709\u6c14\uff0c \u4fbf\u5373\u95ee\u9053\uff1a\u201c\u9601\u4e0b\u4f55\u4eba\uff1f\u56e0\u4f55\u76f8\u620f\uff1f\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u8fd9\u53ae\u662f\u4e0d\u662f\u91d1\u4e16\u9057\u90a3\u4e2a\u59d3\u6c5f\u7684\u5f92\u5f1f\uff1f\u201d \u4ed6\u4e0d\u7b54\u6c5f\u6d77\u5929\u7684\u95ee\u8bdd\uff0c\u5374\u5411\u7740\u53e6\u4e00\u4e2a\u65b9\u5411\u53d1\u95ee\u3002\u6c5f\u6d77\u5929\u671d\u7740\u90a3\u4e2a\u65b9\u5411\u671b\u53bb\uff0c\u8fd9\u624d\u89c1\u5230\u5389 \u590d\u751f\u5728\u90a3\u95f4\u602a\u5c4b\u7684\u4fa7\u8fb9\u9732\u51fa\u9762\u6765\u3002 \u3000\u3000\u5389\u590d\u751f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u662f\u4ed6\u4e86\uff01\u201d\u90a3\u5c11\u5e74\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6709\u5176\u5e08\u5fc5\u6709 \u5176\u5f92\uff0c\u4f60\u4eec\u5e08\u5f92\u4fe9\u90fd\u662f\u4e00\u4e18\u4e4b\u8c89\uff01\u201d\u6c5f\u6d77\u5929\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6211\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f55\u4ee5\u4f60 \u4e00\u89c1\u9762\u5c31\u5f20\u53e3\u9a82\u4eba\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u8ba4\u5f97\u4f60\uff0c\u96be\u9053\u4f60\u505a\u7684\u4e8b\u60c5\u6211\u4e5f\u4e0d\u77e5\u9053\u5417\uff1f\u54fc\uff0c\u54fc\uff0c\u4f60\u548c\u4f60\u7684 \u5e08\u7236\u662f\u540c\u4e00\u4e2a\u6a21\u5b50\u94f8\u51fa\u6765\u7684\uff0c\u5be1\u60c5\u8584\u4e49\uff0c\u6beb\u6ca1\u5fc3\u809d\uff01\u4ec0\u4e48\u5927\u4fa0\u5c0f\u4fa0\uff0c\u7b80\u76f4\u6b3a\u4e16\u76d7\u540d\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7ed9\u4ed6\u9a82\u5f97\u4e00\u4f5b\u51fa\u4e16\uff0c\u4e8c\u4f5b\u6d85\uff0c\u60ca\u6115\u4e4b\u4f59\uff0c\u5ffd\u5730\u5fc3\u5934\u4e00\u52a8\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u8fd9 \u8bdd\u610f\u4f55\u6240\u6307\u3002\u6562\u60c5\u4f60\u5df2\u89c1\u7740\u4e86\u534e\u59d1\u5a18\uff1f\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u8138\u6765\u95ee\u534e\u59d1\u5a18\uff1f\u201d\u6c5f\u6d77\u5929 \u53eb\u9053\uff1a\u201c\u5feb\u8bf4\uff0c\u5feb\u8bf4\uff0c\u5979\u5728\u54ea\u513f\uff1f\u6211\u65e0\u6687\u4e0e\u4f60\u4e89\u8fa9\uff01\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u602a\u5c4b\u5185\u6709\u4eba\u547b\u541f\uff0c\u63a5\u7740\u53e0\u58f0\u53eb\u9053\uff1a\u201c\u6d77\u54e5\uff0c\u6d77\u54e5\uff01\u201d\u58f0\u97f3\u5982\u540c\u868a \u53eb\uff0c\u4f46\u6c5f\u6d77\u5929\u8fd8\u662f\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002\u6c5f\u6d77\u5929\u53c8\u60ca\u53c8\u559c\uff0c\u4e0d\u518d\u7406\u4f1a\u90a3\u4e2a\u5c11\u5e74\uff0c\u5f84\u81ea\u4fbf\u5411\u90a3\u602a \u5c4b\u5954\u53bb\uff0c\u5927\u58f0\u53eb\u9053\uff1a \u3000\u3000\u201c\u78a7\u59b9\uff0c\u6211\u6765\u5566\uff01\u201d \u3000\u3000\u90a3\u602a\u5c4b\u662f\u7528\u575a\u539a\u7684\u51b0\u5757\u780c\u6210\u7684\uff0c\u56db\u9762\u5149\u6ed1\u5982\u955c\u3002\u4f46\u56e0\u51b0\u5757\u592a\u539a\uff0c\u5374\u770b\u4e0d\u89c1\u91cc\u9762\u7684\u666f \u7269\uff0c\u8fd8\u6709\u4e00\u6837\u5947\u602a\u7684\u662f\uff0c\u7adf\u627e\u4e0d\u5230\u8fdb\u51fa\u7684\u95e8\u6237\u3002\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u78a7\u59b9\u663e\u7136\u662f\u75c5\u4f53\u672a\u6108\uff0c \u600e\u80fd\u4f4f\u5728\u8fd9\u51b0\u5c4b\u4e4b\u4e2d\uff1f\u201d\u5f53\u4e0b\u5feb\u6b65\u98de\u5954\uff0c\u6068\u4e0d\u80fd\u4e00\u62f3\u6253\u7834\u8fd9\u95f4\u51b0\u5c4b\uff0c\u5c06\u534e\u4e91\u78a7\u6551\u51fa\u6765\u3002 \u6b63\u662f\uff1a \u3000\u3000\u8c01\u4eba\u8425\u5c4b\u51b0\u5c4b\u4f4f\uff1f\u5f15\u51fa\u5947\u60c5\u53c8\u4e00\u6869\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e03\u56de\u3000\u5fc3\u4e8b\u96be\u8a00\u6325\u7389\u7b1b\u3000\u98ce\u4e91\u4e0d\u6d4b\u9677\u51b0\u6cb3 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u56db\u5341\u4e03\u56de\u3000\u5fc3\u4e8b\u96be\u8a00\u6325\u7389\u7b1b\u3000\u98ce\u4e91\u4e0d\u6d4b\u9677\u51b0\u6cb3 \u3000\u3000\u90a3\u767d\u88d8\u5c11\u5e74\u5ffd\u5730\u4e00\u58f0\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5bb3\u5f97\u5979\u8fd8\u4e0d\u591f\u5417\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5df2\u662f\u7fe9\u5982\u98de \u9e1f\u822c\u5730\u6251\u4e86\u5230\u6765\uff0c\u6d1e\u8427\u4e00\u6325\uff0c\u75be\u70b9\u6c5f\u6d77\u5929\u80c1\u4e0b\u7684\u201c\u6108\u6c14\u7a74\u201d\u3002 \u3000\u3000\u8fd9\u201c\u6108\u6c14\u7a74\u201d\u4e43\u662f\u4eba\u8eab\u6b7b\u7a74\u4e4b\u4e00\uff0c\u6c5f\u6d77\u5929\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u7528\u201c\u5929\u7f57\u6b65\u6cd5\u201d\u95ea\u5f00\uff0c \u4e0d\u7981\u5fc3\u5934\u706b\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u534e\u59d1\u5a18\u662f\u6211\u4e49\u59b9\uff0c\u5979\u521a\u624d\u53eb\u7684\u5c31\u662f\u6211\uff0c\u4f60\u600e\u4e48\u53ef\u4ee5\u4e0d\u8bb8\u6211\u53bb\u770b \u5979\uff1f\u6211\u6709\u8bdd\u81ea\u7136\u4f1a\u5bf9\u4ed6\u53bb\u8bf4\uff0c\u4e0d\u5fc5\u4f60\u6765\u591a\u7ba1\u95f2\u4e8b\uff0c\u4f60\u662f\u5979\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u5389\u58f0\u559d\u9053\uff1a\u201c\u6eda\u5f00\uff0c\u4f60\u548c\u5979\u662f\u4e49\u5144\u4e49\u59b9\u53c8\u600e\u4e48\u6837\uff1f\u4f60\u5bf9\u4e0d\u4f4f\u5979\uff0c\u8fd9\u5c31\u5df2\u7ecf\u6069 \u65ad\u4e49\u7edd\u4e86\u3002\u5979\u73b0\u5728\u662f\u6211\u7684\u5ba2\u4eba\uff0c\u6211\u4e0d\u8bb8\u4f60\u89c1\u5c31\u4e0d\u8bb8\u4f60\u89c1\uff01\u201d\u4ed6\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u5e95\u4e1d\u6beb\u4e0d\u7ea7\uff0c \u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u662f\u904d\u88ad\u6c5f\u6d77\u5929\u7684\u4e09\u5341\u516d\u9053\u5927\u7a74\u3002\u6c5f\u6d77\u5929\u4ee5\u5929\u7f57\u6b65\u6cd5\u914d\u5408\u4e0a\u4e58\u8f7b\u529f\uff0c\u867d\u7136\u4e00 \u4e00\u907f\u5f00\uff0c\u4f46\u5374\u51b2\u4e0d\u8fc7\u53bb\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6012\u9053\uff1a\u201c\u4f60\u518d\u4e0d\u8ba9\u8def\u3002\u6211\u53ef\u8981\u4e0d\u5ba2\u6c14\u5566\uff01\u201d\u90a3\u5c11\u5e74\u4ecd\u662f\u559d\u9053\uff1a\u201c\u6eda\u5f00\uff01\u8c01\u4e0e \u4f60\u8bb2\u4ea4\u60c5\uff0c\u4f60\u4e0d\u5ba2\u6c14\uff0c\u6211\u66f4\u4e0d\u5ba2\u6c14\uff01\u201d\u6c5f\u6d77\u5929\u9ed8\u8fd0\u7384\u529f\uff0c\u4e00\u638c\u62cd\u51fa\uff0c\u4f46\u4ed6\u89c1\u8fd9\u5c11\u5e74\u7684\u8bf4 \u8bdd\u4f3c\u4e4e\u662f\u5904\u5904\u5e2e\u7740\u534e\u4e91\u78a7\uff0c\u60f3\u6765\u4e0d\u662f\u574f\u4eba\uff0c\u6545\u800c\u8fd9\u4e00\u638c\u53ea\u7528\u4e86\u4e09\u6210\u529f\u529b\uff0c\u610f\u6b32\u5c06\u4ed6\u63a8\u8fc7 \u4e00\u8fb9\uff0c\u4fbf\u51b2\u8fc7\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u5c11\u5e74\u53ea\u662f\u6643\u4e86\u4e00\u6643\uff0c\u4f9d\u7136\u62e6\u5728\u4ed6\u7684\u9762\u524d\uff0c\u6c5f\u6d77\u5929\u5927\u611f\u610f\u5916\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u4ed6 \u7ec3\u6709\u91d1\u521a\u4e0d\u574f\u795e\u529f\uff1f\u201d\u8981\u77e5\u6c5f\u6d77\u5929\u81ea\u670d\u98df\u4e86\u5929\u5fc3\u77f3\u4e4b\u540e\uff0c\u529f\u529b\u4e4b\u6df1\u539a\uff0c\u9664\u4e86\u4ed6\u5e08\u7236\u91d1\u4e16 \u9057\u4e4b\u5916\uff0c\u65e9\u5df2\u65e0\u4eba\u80fd\u53ca\uff0c\u4ed6\u7528\u4e86\u4e09\u6210\u529f\u529b\uff0c\u5fc3\u91cc\u8fd8\u5728\u5bb3\u6015\u90a3\u5c11\u5e74\u7981\u53d7\u4e0d\u8d77\uff0c\u53ea\u56e0\u4ed6\u89c1\u90a3 \u5c11\u5e74\u7684\u70b9\u7a74\u624b\u6cd5\u6781\u662f\u9ad8\u660e\uff0c\u8fd9\u624d\u7528\u5230\u4e09\u6210\u529f\u529b\u7684\u3002\u5728\u4ed6\u610f\u60f3\u4e4b\u4e2d\uff0c\u90a3\u5c11\u5e74\u4e0d\u662f\u4e00\u8de4\u6454\u5012\uff0c \u5c31\u5b9a\u8981\u8fdc\u8fdc\u907f\u5f00\uff0c\u54ea\u77e5\u8fd9\u5c11\u5e74\u6b63\u9762\u786c\u63a5\u4e86\u4ed6\u7684\u4e00\u638c\uff0c\u53ea\u4e0d\u8fc7\u6643\u4e86\u4e00\u6643\u3002 \u3000\u3000\u4f46\u90a3\u5c11\u5e74\u6643\u4e86\u4e00\u6643\uff0c\u5df2\u662f\u6ee1\u9762\u901a\u7ea2\uff0c\u539f\u6765\u8fd9\u5c11\u5e74\u4e5f\u662f\u9a84\u50b2\u5f97\u7d27\uff0c\u4e00\u5411\u81ea\u8d1f\u7684\u4eba\uff0c\u4ed6 \u8fd8\u4e0d\u77e5\u9053\u6c5f\u6d77\u5929\u4ec5\u4ec5\u662f\u7528\u4e86\u4e09\u6210\u529f\u529b\uff0c\u5403\u4e86\u70b9\u4e8f\uff0c\u53c8\u7f9e\u53c8\u6012\uff0c\u5fc3\u4e2d\u4e5f\u662f\u5927\u611f\u610f\u5916\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u515a\u4e00\u80a1\u70ed\u98ce\u8fce\u9762\u5439\u6765\uff0c\u539f\u6765\u662f\u90a3\u5c11\u5e74\u5728\u6d1e\u8427\u4e2d\u5439\u51fa\u4e00\u80a1\u7eaf\u9633\u771f \u6c14\u3002\u8fc7\u5c11\u5e74\u7684\u5185\u529f\u867d\u7136\u6765\u5230\u6700\u4e0a\u4e58\u7684\u5883\u754c\uff0c\u4e5f\u8fd8\u672a\u7ec3\u6210\u65e0\u5f62\u7f61\u6c14\uff0c\u4f46\u4ed6\u7ec3\u7684\u72ec\u95e8\u5185\u529f\u9887 \u4e3a\u602a\u5f02\u3002\u4ed6\u6240\u7528\u7684\u6d1e\u8427\u540d\u4e3a\u201c\u6e29\u7389\u8427\u201d\uff0c\u4e5f\u662f\u4e00\u4ef6\u5f02\u5b9d\uff0c\u8fd9\u80a1\u7eaf\u9633\u771f\u6c14\u4ece\u201c\u6e29\u7389\u8427\u201d\u4e2d \u5439\u51fa\uff0c\u7adf\u7136\u70ed\u6d6a\u8feb\u4eba\uff0c\u89e6\u8138\u5982\u70eb\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6709\u62a4\u4f53\u795e\u529f\uff0c\u5f53\u7136\u4e0d\u6015\u4ed6\u8fd9\u80a1\u7eaf\u9633\u771f\u6c14\uff0c\u4f46\u5374\u4e5f\u4e0d\u80fd\u4e0d\u63d0\u9632\u4f24\u53ca\u773c\u775b\uff0c\u5f53\u4e0b \u53cc\u773c\u4e00\u95ed\uff0c\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u4fa7\u8eab\u4e00\u95ea\uff0c\u542c\u98ce\u8fa8\u5668\uff0c\u5de6\u638c\u4ece\u65f6\u5e95\u7a7f\u51fa\uff0c\u5c31\u8981\u786c\u62a2\u90a3 \u5c11\u5e74\u7684\u7389\u8427\u3002\u90a3\u5c11\u5e74\u4e5f\u786e\u662f\u4e86\u5f97\uff0c\u8d81\u7740\u6c5f\u6d77\u5929\u95ed\u773c\u7684\u5239\u90a3\u4e4b\u95f4\u5148\u53d1\u5236\u4eba\uff0c\u7389\u8427\u500f\u7136\u4e2d\u9014 \u8f6c\u5411\uff0c\u5df2\u70b9\u4e2d\u4e86\u6c5f\u6d77\u5929\u8170\u80cc\u7684\u201c\u7cbe\u4fc3\u7a74\u201d\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u7684\u62a4\u4f53\u795e\u529f\uff0c\u7acb\u65f6\u751f\u51fa\u53cd\u5e94\uff0c\u53ea\u542c\u5f97\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u7389\u7bab\u89e6\u4f53\uff0c\u5982\u51fb\u8d25\u8349\uff0c \u90a3\u5c11\u5e74\u864e\u53e3\u53d1\u70ed\uff0c\u624b\u81c2\u9178\u9ebb\uff0c\u4e0d\u7531\u5f97\u8e09\u8e09\u8dc4\u8dc4\u5730\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u56fa\u7136\u5403\u4e86\u4e8f\uff0c\u4f46\u6c5f\u6d77\u5929\u4e5f\u4e0d\u80fd\u5982\u5fc3\u6240\u613f\u3002\u539f\u6765\u8fd9\u4e00\u6233\u65e9\u5728\u6c5f\u6d77\u5929\u610f\u6599\u4e4b\u4e2d\uff0c \u4ed6\u6043\u7740\u62a4\u4f53\u795e\u529f\uff0c\u6709\u5fc3\u8ba9\u90a3\u5c11\u5e74\u70b9\u4e2d\u4ed6\u7684\u7a74\u9053\uff0c\u597d\u4e58\u673a\u593a\u53d6\u4ed6\u7684\u7389\u8427\u7684\u3002\u4ed6\u4e5f\u77e5\u90a3\u5c11\u5e74 \u529f\u529b\u975e\u51e1\uff0c\u4f46\u5374\u6599\u60f3\u4e0d\u5230\u8fd8\u5728\u4ed6\u4f30\u8ba1\u4e4b\u4e0a\uff0c\u8fd9\u4e00\u6233\u867d\u7136\u672a\u80fd\u4f24\u53ca\u4ed6\u7684\u7ecf\u8109\uff0c\u4f46\u5728\u8fd9\u5239\u90a3 \u95f4\uff0c\u7adf\u4f3c\u6709\u80a1\u7535\u6d41\u500f\u7136\u901a\u8fc7\u4e00\u822c\uff0c\u6c5f\u6d77\u5929\u4e5f\u4e0d\u5360\u5f97\u5fc3\u5934\u4e00\u9707\uff0c\u904d\u4f53\u9165\u9ebb\uff0c\u52b2\u529b\u53d1\u6325\u4e0d\u51fa\uff0c \u4ed6\u610f\u6b32\u593a\u53d6\u7389\u8427\u7684\u90a3\u4e00\u6293\u4e5f\u5c31\u843d\u7a7a\u4e86\uff01 \u3000\u3000\u90a3\u5c11\u5e74\u7ed9\u6c5f\u6d77\u5929\u7684\u62a4\u4f53\u795e\u529f\u9707\u9000\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u4f46\u4ed6\u4e5f\u662f\u4e2a\u6b66\u5b66\u884c\u5bb6\uff0c\u770b\u51fa\u6c5f\u6d77\u5929\u6b63 \u5728\u8fd0\u6c14\u901a\u5173\uff0c\u529f\u529b\u5728\u4e00\u65f6\u4e4b\u95f4\uff0c\u5b9a\u7136\u4e0d\u80fd\u8fd0\u7528\u81ea\u5982\uff0c\u4e00\u9000\u5373\u4e0a\uff0c\u624b\u6325\u7389\u7bab\uff0c\u53c8\u6765\u70b9\u6c5f\u6d77 \u592d\u7684\u7a74\u9053\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u904d\u4f53\u9165\u9ebb\uff0c\u6025\u5207\u95f4\u786e\u662f\u4e0d\u80fd\u6062\u590d\uff0c\u89c1\u90a3\u5c11\u5e74\u4e58\u5371\u8fdb\u88ad\uff0c\u4e0d\u7981\u52a8\u4e86\u6012\u6c14\uff0c\u559d\u9053\uff1a \u201c\u597d\u5440\uff0c\u4f60\u65e2\u4e0d\u77e5\u8fdb\u9000\uff0c\u6211\u4e14\u5148\u8ba9\u4f60\u5c1d\u70b9\u5389\u5bb3\uff01\u201d\u201c\u55d6\u201d\u7684\u4e00\u58f0\uff0c\u5b9d\u5251\u51fa\u9798\uff0c\u4fe8\u5982\u4e00\u9053 \u94f6\u8679\uff0c\u84e6\u7136\u98de\u51fa\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u7684\u529f\u529b\u867d\u7136\u4e0d\u80fd\u5168\u526f\u7528\u6765\u5bf9\u4ed8\u90a3\u4e2a\u5c11\u5e74\u3002\u4f46\u53ea\u8981\u80fd\u4f7f\u51fa\u4e09\u5206\uff0c\u53ea\u662f\u5f3a\u52b2\u975e\u5e38\uff0c \u4f55\u51b5\u4ed6\u7684\u7cbe\u5999\u5251\u62db\uff0c\u4f9d\u7136\u8fd8\u662f\u80fd\u591f\u968f\u5fc3\u8fd0\u7528\u3002\u8fd9\u4e00\u62db\u62ff\u634f\u65f6\u5019\uff0c\u6070\u5230\u597d\u5904\uff0c\u4e14\u542c\u5f97\u201c\u5f53\u201d \u7684\u4e00\u58f0\uff0c\u7389\u7bab\u94f6\u5251\uff0c\u5df2\u662f\u78b0\u5728\u4e00\u8d77\uff1a \u3000\u3000\u88c1\u4e91\u5b9d\u5251\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u6c5f\u6d77\u5929\u62df\u8fd9\u4e00\u5251\u5b9a\u7136\u628a\u90a3\u5c11\u5e74\u7684\u7389\u8427\u622a\u65ad\uff0c\u54ea\u77e5\u201c\u5f53\u201d\u7684\u4e00 \u58f0\u54cd\u8fc7\uff0c\u90a3\u5c11\u5e74\u7684\u7389\u8427\u4f9d\u7136\u65e0\u635f\uff0c\u4e58\u6687\u62b5\u9699\uff0c\u7ed5\u8fc7\u4ed6\u7684\u80cc\u540e\uff0c\u53c8\u6765\u70b9\u4ed6\u7684\u201c\u98ce\u5e9c\u7a74\u201d\u3002 \u6c5f\u6d77\u5929\u53cd\u624b\u4e00\u5251\uff0c\u52a0\u4e86\u4e00\u4e24\u5206\u529b\u9053\uff0c\u90a3\u5c11\u5e74\u6e9c\u6ed1\u975e\u5e38\uff0c\u8fd9\u4e00\u6b21\u5374\u4e0d\u4e0e\u4ed6\u786c\u78b0\uff0c\u500f\u5730\u4e2d\u9014 \u53d8\u62db\uff0c\u518d\u70b9\u4ed6\u8170\u80cc\u7684\u201c\u5f52\u85cf\u7a74\u201d\uff0c\u800c\u4e14\u8fd8\u51b7\u7b11\u8bf4\u9053\uff1a \u3000\u3000\u201c\u9886\u6559\u4e86\uff0c\u4e5f\u4e0d\u89c1\u788d\u600e\u4e48\u5389\u5bb3\u5440\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u542c\u98ce\u8fa8\u5668\uff0c\u5c31\u5982\u80cc\u540e\u957f\u4e86\u773c\u775b\u4f3c\u7684\uff0c\u5530\u3001\u5530\u3001\u5530\uff0c\u8fde\u73af\u4e09\u5251\uff0c\u628a\u90a3\u5c11\u5e74\u7684\u653b \u52bf\u4e00\u4e00\u5316\u89e3\uff0c\u5728\u8fd9\u7247\u523b\u4e4b\u95f4\uff0c\u4ed6\u5df2\u529f\u884c\u767e\u7a74\uff0c\u6c14\u900f\u91cd\u5173\uff0c\u9165\u9ebb\u4e4b\u611f\uff0c\u5c3d\u90fd\u6d88\u5931\uff0c\u529f\u529b\u6062 \u590d\uff0c\u5927\u559d\u9053\uff1a\u201c\u4f60\u8ba9\u4e0d\u8ba9\u5f00\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u53e3\u5934\u867d\u662f\u8c03\u4f83\u6c5f\u6d77\u5929\uff0c\u5fc3\u91cc\u5374\u5b9e\u662f\u77e5\u9053\u4ed6\u7684\u5389\u5bb3\uff0c\u89c1\u4ed6\u5251\u5149\u66b4\u957f\uff0c\u5251\u5c16\u5c1a\u81ea \u79bb\u8eab\u6570\u5c3a\uff0c\u4e00\u80a1\u52b2\u529b\u5df2\u662f\u65e0\u58f0\u65e0\u606f\u7684\u88ad\u6765\uff0c\u4e0d\u7531\u5f97\u602f\u610f\u6697\u751f\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5251\u638c\u517c\u65bd\uff0c\u5c06\u529f\u529b\u9010\u6e10\u589e\u52a0\uff0c\u52a0\u5230\u4e86\u4e03\u6210\u529b\u9053\u7684\u65f6\u5019\uff0c\u90a3\u5c11\u5e74\u5df1\u662f\u65e0\u6cd5\u9632\u5fa1\uff0c \u53ea\u5f97\u95ea\u5f00\uff0c\u6c5f\u6d77\u5929\u75be\u51b2\u8fc7\u53bb\uff0c\u201c\u7830\u201d\u7684\u4e00\u638c\uff0c\u5728\u90a3\u51b0\u5899\u4e0a\u91cd\u91cd\u4e00\u51fb\uff0c\u6253\u5f97\u5c42\u51b0\u788e\u88c2\uff0c\u58f0 \u5982\u96f7\u9e23\uff0c\u4f46\u56e0\u51b0\u5899\u592a\u539a\uff0c\u5374\u8fd8\u672a\u80fd\u7834\u5c4b\u800c\u5165\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u53eb\u9053\uff1a\u201c\u78a7\u59b9\uff0c\u4f60\u522b\u5bb3\u4f2f\uff0c\u662f\u6211\u6765\u4e86\uff01\u201d\u8bf4\u4e5f\u5947\u602a\uff0c\u53ea\u5728\u6c5f\u6d77\u5929\u521a\u521a\u4e0a\u5230\u8fd9 \u7075\u9e6b\u5cf0\u5934\u7684\u65f6\u5019\uff0c\u534e\u4e91\u78a7\u66fe\u53eb\u8fc7\u4ed6\u4e24\u58f0\uff0c\u4ee5\u540e\u5c31\u4e00\u76f4\u6ca1\u6709\u542c\u5230\u5979\u7684\u58f0\u97f3\u4e86\u3002\u73b0\u5728\u6c5f\u6d77\u5929 \u5df2\u7ecf\u6765\u5230\u51b0\u5c4b\u4e4b\u524d\uff0c\u800c\u4e14\u638c\u51fb\u51b0\u5899\uff0c\u51c6\u5907\u7834\u5c4b\u800c\u5165\u4e86\uff0c\u8bba\u7406\u534e\u4e91\u78a7\u662f\u8be5\u51fa\u58f0\u547c\u5524\u7684\uff0c\u4f46 \u51b0\u5c4b\u91cc\u4ecd\u662f\u4e00\u70b9\u52a8\u9759\u4e5f\u6ca1\u6709\u3002\u8fd9\u65f6\u6c5f\u6d77\u5929\u4ee5\u5b8f\u539a\u7684\u91d1\u521a\u638c\u529b\uff0c\u51fb\u788e\u4e86\u4e00\u5927\u5757\u575a\u51b0\uff0c\u51b0\u5899 \u51cf\u8584\uff0c\u51b0\u5757\u900f\u660e\uff0c\u5c4b\u5185\u7684\u666f\u7269\u5df2\u9690\u7ea6\u53ef\u89c1\uff0c\u4e2d\u95f4\u6709\u4e00\u5f20\u5927\u5e8a\uff0c\u5f62\u5f0f\u53e4\u602a\uff0c\u989c\u8272\u58a8\u7eff\uff0c\u4e5f \u4e0d\u77e5\u662f\u4ec0\u4e48\u4e1c\u897f\u505a\u7684\u3002\u5e8a\u4e0a\u7761\u7740\u4e00\u4e2a\u4eba\uff0c\u8138\u5411\u7740\u91cc\u9762\u7684\u51b0\u58c1\uff0c\u9690\u9690\u7ea6\u7ea6\u770b\u5f97\u51fa\u662f\u4e2a\u5973\u5b50\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u60ca\u7591\u4e0d\u5b9a\uff1a\u201c\u96be\u9053\u4e0d\u662f\u78a7\u59b9\uff0c\u4f46\u5979\u7684\u58f0\u97f3\u6211\u7edd\u4e0d\u4f1a\u542c\u9519\uff0c\u8fd9\u5c4b\u5185\u7684\u4eba\u5206\u660e\u662f \u5979\uff0c\u5979\u521a\u624d\u8fd8\u5728\u53eb\u6211\uff0c\u600e\u7684\u73b0\u5728\u53cd\u800c\u719f\u7761\u4e86\uff1f\u54ce\u5440\uff0c\u83ab\u975e\u2014\u2014\u201d\u8981\u77e5\u6c5f\u6d77\u5929\u5728\u5916\u9762\u6253\u5f97 \u5730\u52a8\u5c71\u6447\uff0c\u5982\u4eca\u53c8\u638c\u51fb\u51b0\u5899\uff0c\u58f0\u5982\u96f7\u9707\uff0c\u7eb5\u7136\u534e\u4e91\u78a7\u662f\u5728\u719f\u7761\u4e2d\uff0c\u4e5f\u4f1a\u60ca\u9192\uff0c\u4f55\u51b5\u5979\u5728 \u6c5f\u6d77\u5929\u521d\u521d\u6765\u5230\u7684\u65f6\u5019\uff0c\u8fd8\u4f1a\u51fa\u58f0\u547c\u5524\uff1f\u8fd9\u53ea\u6709\u4e00\u4e2a\u89e3\u91ca\uff0c\u9664\u975e\u534e\u4e91\u78a7\u5df2\u7ecf\u6c14\u7edd\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u4e0d\u6562\u518d\u60f3\u4e0b\u53bb\uff0c\u5c31\u5728\u8fd9\u65f6\u3002\u53ea\u542c\u5f97\u90a3\u767d\u88d8\u5c11\u5e74\u559d\u9053\uff1a \u3000\u3000\u201c\u4f60\u60f3\u5bb3\u6b7b\u5979\u5417\uff1f\u201d\u624b\u6325\u7389\u8427\uff0c\u53c8\u5df2\u70b9\u5230\uff0c\u6c5f\u6d77\u5929\u53cd\u624b\u4e00\u5251\uff0c\u5c06\u4ed6\u683c\u5f00\uff0c\u8bf4\u9053\uff1a \u201c\u4f60\u8d76\u5feb\u8ba9\u6211\u8fdb\u53bb\u6551\u5979\uff0c\u8981\u4e0d\u7136\u53ea\u6015\u5979\u5f53\u771f\u4f1a\u7ed9\u4f60\u5bb3\u6b7b\u4e86\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u5927\u6012\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u5979\u73b0\u5728\u597d\u7aef\u7aef\u7684\uff0c\u8981\u4f60\u6551\u5979\uff1f\u201d\u6c5f\u6d77\u5929\u4e0d\u5047\u601d\u7d22\uff0c\u51b2 \u53e3\u800c\u51fa\uff0c\u95ee\u9053\uff1a\u201c\u5979\u5728\u5e8a\u4e0a\u52a8\u4e5f\u4e0d\u52a8\uff0c\u4f60\u77e5\u9053\u5979\u771f\u662f\u6ca1\u6709\u6b7b\u4e48\uff1f\u201d\u90a3\u5c11\u5e74\u5389\u58f0\u65a5\u9053\uff1a \u201c\u4f60\u518d\u4e71\u6253\u8fd9\u5835\u51b0\u5899\uff0c\u90a3\u5c31\u662f\u5f53\u771f\u8981\u5bb3\u6b7b\u5979\u4e86\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u731b\u5730\u4e00\u60ca\uff0c\u8fd9\u624d\u60f3\u8d77\u51b0\u5899\u4e0d\u6bd4\u666e\u901a\u7684\u5899\u58c1\uff0c\u666e\u901a\u7684\u5899\u58c1\u6253\u7a7f\u4e00\u4e2a\u5927\u6d1e\uff0c\u623f\u5c4b \u4e0d\u4f1a\u5012\u584c\uff1b\u51b0\u5899\u88ab\u4ed6\u7684\u638c\u529b\u6240\u9707\uff0c\u90a3\u53ef\u5c31\u4e0d\u6562\u4fdd\u9669\u4e86\u3002\u6c5f\u6d77\u5929\u8fde\u5fd9\u4f4f\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\uff0c \u4f60\u544a\u8bc9\u6211\u600e\u6837\u8fdb\u53bb\u5c38\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u6211\u53eb\u4f60\u6eda\u5f00\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u600e\u80af\u7f62\u4f11\uff0c\u6539\u4e3a\u6c42\u6073\u7684\u8bed\u6c14\u9053\uff1a\u201c\u4f60\u53ea\u8ba9\u6211\u770b\u4e00\u770b\u5979\u90fd\u4e0d\u6210\u5417\uff1f\u201d\u90a3\u5c11\u5e74\u9053\uff1a \u201c\u5979\u4e0d\u8981\u89c1\u4f60\uff01\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u5979\u4eb2\u53e3\u5bf9\u6211\u8bf4\u7684\u3002\u201d\u6c5f\u6d77 \u5929\u9053\uff1a\u201c\u5979\u4e3a\u4ec0\u4e48\u521a\u624d\u8fd8\u5728\u53eb\u6211\uff1f\u201d\u90a3\u5c11\u5e74\u6012\u9053\uff1a\u201c\u6211\u4e0d\u4e0e\u4f60\u80e1\u7f20\u4e86\uff0c\u4f60\u8d70\u4e0d\u8d70\uff1f\u201d\u7389 \u8427\u6325\u821e\uff0c\u53c8\u662f\u4e00\u9635\u72c2\u653b\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u4e2d\u9690\u9690\u4f5c\u75db\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5979\u80cc\u5411\u7740\u6211\uff0c\u96be\u9053\u5f53\u771f\u662f\u4e0d\u60f3\u89c1\u6211\u5417\uff1f\u201c\u4e0d\uff0c \u6211\u4e00\u5b9a\u975e\u89c1\u5979\u4e0d\u53ef\uff01\u201d\u7a81\u7136\u5f97\u4e86\u4e2a\u4e3b\u610f\uff0c\u6539\u4ee5\u5288\u7a7a\u638c\u529b\uff0c\u5c06\u90a3\u5c11\u5e74\u6321\u5728\u4e8c\u5c3a\u4e4b\u5916\uff0c\u5374\u7528 \u5b9d\u5251\u53bb\u7a7f\u523a\u51b0\u5899\uff0c\u8fd9\u6837\u9707\u529b\u4e0d\u5927\uff0c\u4e0d\u81f3\u4e8e\u6709\u5012\u584c\u7684\u5371\u9669\u3002 \u3000\u3000\u5389\u590d\u751f\u4e00\u76f4\u8896\u624b\u65c1\u89c2\uff0c\u8fd9\u65f6\u5ffd\u5730\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u771f\u662f\u4e0d\u8bc6\u597d\u6b79\uff0c\u786c\u8981\u6b7b\u8d56\u80e1\u7f20\uff01 \u597d\uff0c\u90a3\u5c31\u53ea\u597d\u628a\u4f60\u6253\u8dd1\u4e86\uff01\u201d\u62bd\u51fa\u7389\u5c3a\uff0c\u4e0a\u524d\u52a9\u6218\uff0c\u6c14\u51b2\u51b2\u7684\u5c31\u671d\u7740\u6c5f\u6d77\u5929\u7684\u8111\u95e8\u7838\u4e0b\uff0c \u3000\u3000\u5389\u590d\u751f\u7684\u7389\u5c3a\u4e5f\u662f\u4ef6\u5b9d\u7269\uff0c\u4e0d\u60e7\u6c5f\u6d77\u5929\u7684\u88c1\u4e91\u5b9d\u5251\uff0c\u4e24\u4e0b\u4e00\u78b0\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u5404\u65e0\u4f24 \u635f\uff0c\u4f46\u5389\u590d\u751f\u5374\u591a\u9000\u4e86\u4e00\u6b65\u3001\u8fd9\u8fd8\u662f\u56e0\u4e3a\u6c5f\u6d77\u592d\u53ea\u80fd\u7528\u4e00\u534a\u529f\u529b\u53bb\u5bf9\u4ed8\u4ed6\u7684\u7f18\u6545\u3002\u6c5f\u6d77 \u592d\u9053\uff1a\u201c\u5389\u53d4\u53d4\uff0c\u6211\u770b\u5728\u5e08\u7236\u548c\u8fc7\u4e16\u7684\u5e08\u6bcd\u4efd\u4e0a\uff0c\u4e00\u5411\u4e0d\u613f\u4e0e\u4f60\u4e3a\u654c\uff0c\u4f60\u5374\u53c8\u4f55\u82e6\u4e0e\u6211 \u4e3a\u96be\uff1f\u201d\u6c5f\u6bcf\u5929\u867d\u6ca1\u6709\u542c\u5e08\u7236\u76f4\u63a5\u8bf4\u8fc7\uff0c\u4f46\u4ece\u4ed6\u7236\u4eb2\u548c\u59ec\u6653\u98ce\u7b49\u4eba\u7684\u53e3\u4e2d\uff0c\u4ed6\u4e5f\u77e5\u9053\u8fd9 \u5389\u590f\u751f\u548c\u4ed6\u6b7b\u53bb\u7684\u5e08\u6bcd\u5389\u80dc\u7537\u5b9a\u6709\u6e0a\u6e90\uff0c\u653e\u800c\u5982\u6b64\u8bf4\u6cd5\u3002 \u3000\u3000\u5389\u590d\u751f\u6012\u9053\uff1a\u201c\u4f60\u8fd8\u63d0\u4f60\u7684\u5e08\u7236\uff0c\u4f60\u4eec\u5e08\u5f92\u4fe9\u90fd\u4e0d\u662f\u597d\u4e1c\u897f\uff01\u201d\u4e00\u9000\u590d\u4e0a\uff0c\u7389\u5c3a\u62a1 \u5706\uff0c\u5f3a\u653b\u731b\u6253\uff0c\u5389\u590d\u751f\u7684\u52a8\u529b\u867d\u7136\u4e0d\u53ca\u6c5f\u6d77\u5929\uff0c\u5374\u4e5f\u751a\u4e3a\u4e0d\u51e1\uff0c\u800c\u4e14\u4ed6\u7684\u7389\u5c3a\u53ef\u4ee5\u786c\u78b0 \u5b9d\u5251\uff0c\u6c5f\u6d77\u5929\u53c8\u4e0d\u613f\u5c06\u4ed6\u6740\u4f24\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u5389\u590d\u751f\u4e0e\u90a3\u5c11\u5e74\u4ee5\u4e8c\u654c\u4e00\uff0c\u5df2\u4e0e\u6c5f\u6bcf\u5929\u6253\u6210 \u4e86\u5e73\u624b\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5f97\u5389\u590d\u751f\u4e4b\u52a9\uff0c\u7389\u7bab\u70b9\u7a74\u7684\u624b\u6cd5\u5f97\u4ee5\u5c3d\u5c55\u6240\u957f\uff0c\u5947\u8be1\u53d8\u5316\u3001\u5c42\u51fa\u4e0d\u7a77\uff0c\u6c5f\u6d77 \u5929\u66fe\u5403\u8fc7\u4ed6\u7684\u4e8f\uff0c\u4e5f\u4e0d\u5f97\u4e0d\u5c0f\u5fc3\u9632\u5907\u3002 \u3000\u3000\u53cc\u65b9\u4f60\u9000\u6211\u8fdb\uff1b\u6709\u5b88\u6709\u653b\uff0c\u4f46\u6c5f\u6d77\u5929\u56e0\u4e3a\u7a76\u7adf\u4e0d\u6562\u5168\u529b\u62a2\u653b\u3002\u6253\u4e86\u7ea6\u534a\u4f4f\u9999\u7684\u65f6\u523b\uff0c \u7adf\u7ed9\u4ed6\u4eec\u8feb\u5f97\u9010\u6b65\u540e\u9000\uff0c\u4e0e\u90a3\u95ee\u51b0\u5c4b\u7684\u8ddd\u79bb\u4e5f\u6e10\u6e10\u8fdc\u4e86\u3002 \u3000\u3000\u6b63\u81ea\u6253\u5230\u7d27\u5904\uff0c\u5ffd\u542c\u5f97\u5510\u52aa\u73e0\u7a46\u548c\u534e\u5929\u98ce\u7684\u58f0\u97f3\u51e0\u4e4e\u662f\u540c\u65f6\u95f4\u9053\uff1a\u201c\u6c5f\u5e08\u5144\uff0c\u4f60\u5728 \u4e0a\u9762\u662f\u548c\u8c01\u6253\u67b6\u5417\uff1f\u201d\u201c\u6d77\u513f\uff0c\u4f60\u7684\u78a7\u59b9\u53ef\u662f\u5728\u4e0a\u9762\u5417\uff1f\u201d\u539f\u6765\u4ed6\u4eec\u4e8c\u4eba\u76f8\u7ee7\u767b\u5c71\uff0c\u4f46 \u56e0\u529f\u529b\u4e0d\u53ca\u6c5f\u6d77\u5929\uff0c\u6240\u4ee5\u6b64\u65f6\u65b9\u5230\u3002\u6c5f\u6d77\u5929\u5927\u559c\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u6765\uff0c\u78a7\u59b9\u662f\u5728\u8fd9 \u91cc\uff01\u201d \u3000\u3000\u90a3\u767d\u88d8\u5c11\u5e74\u4e5f\u662f\u4e2a\u6b66\u5b66\u884c\u5bb6\uff0c\u4e00\u542c\u5c31\u77e5\u662f\u53c8\u6765\u4e86\u4e24\u4e2a\u52b2\u654c\uff0c\u4e0d\u6562\u604b\u6218\uff0c\u5ffd\u5730\u4e00\u58f0\u547c \u5578\uff0c\u4fbf\u4e0e\u5389\u590d\u751f\u53cc\u53cc\u9003\u8dd1\u3002\u6c5f\u6d77\u5929\u672c\u6765\u53ea\u662f\u60f3\u6551\u534e\u4e91\u78a7\uff0c\u5f53\u7136\u4e0d\u4f1a\u8ffd\u8d76\u4ed6\u4eec\uff0c\u5f84\u81ea\u4fbf\u53bb \u8bbe\u6cd5\u7834\u90a3\u51b0\u5c4b\u3002 \u3000\u3000\u54ea\u77e5\u5c31\u5728\u4ed6\u8d70\u8fd1\u51b0\u5c4b\u4e4b\u65f6\u3002\u5ffd\u89c1\u51b0\u5c4b\u5185\u6709\u4e24\u5934\u6bdb\u8338\u8338\u7684\u755c\u751f\uff0c\u6b63\u662f\u5389\u590d\u751f\u90a3\u4e24\u53ea\u91d1 \u6bdb\u72e1\uff0c\u6c5f\u6d77\u5929\u5927\u5403\u4e00\u60ca\uff0c\u8f6c\u773c\u95f4\uff0c\u90a3\u4e24\u5934\u91d1\u6bdb\u72e1\u7a81\u7136\u6d88\u5931\uff0c\u5e8a\u4e0a\u7684\u90a3\u4e2a\u5c11\u5973\u4e5f\u4e0d\u89c1\u4e86\u3002 \u6c5f\u6d77\u5929\u5446\u4e86\u4e00\u5446\uff0c\u968f\u5373\u604d\u7136\u5927\u609f\uff1a\u201c\u60f3\u5fc5\u662f\u6709\u5730\u9053\u901a\u8fdb\u5c4b\u5185\uff0c\u8fd9\u4e24\u53ea\u91d1\u6bdb\u72e1\u662f\u4ece\u5730\u9053\u8fdb \u53bb\u7684\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u679c\u7136\u4ece\u6570\u5341\u4e08\u5916\u7684\u5730\u9762\u7a81\u7136\u94bb\u4e86\u51fa\u672a\uff0c\u767d\u88d8\u5c11\u5e74\u548c\u5389 \u590d\u751f\u65e9\u5df2\u5728\u90a3\u513f\u7b49\u5019\uff0c\u91d1\u6bdb\u72e1\u4e00\u94bb\u51fa\u6765\uff0c\u767d\u88d8\u5c11\u5e74\u7acb\u5373\u5c06\u534e\u4e91\u78a7\u63a5\u4e86\u8fc7\u53bb\uff0c\u8ddf\u7740\u4fbf\u548c\u5389 \u590d\u751f\u90fd\u8de8\u4e0a\u4e86\u91d1\u6bdb\u72e1\u80cc\u810a\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8fd9\u4e00\u6025\u975e\u540c\u5c0f\u53ef\uff0c\u8fde\u5fd9\u5927\u53eb\u9053\uff1a\u201c\u78a7\u59b9\uff0c\u78a7\u59b9\uff0c\u4f60\u7239\u7239\u6765\u5566\uff01\u201d\u594b\u8d77\u795e\u529b\uff0c \u4e00\u63a0\u6570\u4e08\uff0c\u4e09\u8d77\u4e09\u4f0f\uff0c\u4fe8\u5982\u5f29\u7bad\u7a7f\u7a7a\uff0c\u7adf\u7136\u8ffd\u5230\u4e86\u91d1\u6bdb\u72e1\u7684\u80cc\u540e\uff0c\u51e0\u4e4e\u6293\u7740\u4e86\u5b83\u7684\u5c3e\u5df4\uff0c \u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u89c1\u534e\u4e91\u78a7\u4f0f\u5728\u90a3\u5c11\u5e74\u7684\u80a9\u5934\u4e0a\uff0c\u4f3c\u4e4e\u52a8\u4e86\u4e00\u4e0b\uff0c\u7528\u5fae\u5f31\u7684\u58f0\u97f3\uff0c\u53c8\u65ad\u65ad\u7eed \u7eed\u5730\u53eb\u4e86\u4e24\u58f0\uff1a\u201c\u6d77\u54e5\uff0c\u6d77\u54e5\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u53ef\u4ee5\u770b\u89c1\u5979\u7684\u5934\u90e8\uff0c\u5947\u602a\u7684\u662f\uff0c\u53ea\u89c1\u5979\u5634\u5507\u5fae\u5fae\u5f00\u9616\uff0c\u773c\u775b\u5374\u6ca1\u6709\u5f20\u5f00\uff0c\u5979 \u90a3\u65ad\u7eed\u7684\u547c\u5524\uff0c\u5c31\u4f3c\u68a6\u4e2d\u7684\u5453\u8bed\u3002\u6c5f\u6d77\u5929\u53c8\u559c\u53c8\u60ca\uff0c\u5927\u53eb\u9053\uff1a\u201c\u78a7\u59b9\uff0c\u4f60\u600e\u4e48\u5566\uff01\u201d\u534e \u4e91\u78a7\u6ca1\u6709\u56de\u7b54\uff0c\u4f3c\u662f\u4e00\u4e2a\u4eba\u8bf4\u4e86\u68a6\u8bdd\u4e4b\u540e\uff0c\u53c8\u56de\u5230\u6c89\u6c89\u719f\u7761\u4e4b\u4e2d\u53bb\u4e86\u3002\u6c5f\u6d77\u5929\u594b\u529b\u4e00\u8dc3\uff0c \u4f38\u624b\u53bb\u62c9\u91d1\u6bdb\u72e1\u7684\u6606\u5df2\uff0c\u90a3\u767d\u88d8\u5c11\u5e74\u56de\u8fc7\u5934\u6765\uff0c\u201c\u4e54\u201d\u7684\u4e00\u58f0\uff0c\u4ece\u7389\u7bab\u4e2d\u5439\u51fa\u4e00\u80a1\u7eaf\u9633 \u771f\u6c14\uff0c\u6c5f\u6d77\u5929\u6015\u8bef\u4f24\u534e\u4e91\u78a7\uff0c\u4e0d\u6562\u53d1\u638c\u8fd8\u51fb\uff0c\u53ea\u597d\u4fa7\u8eab\u4e00\u95ea\uff0c\u90a3\u80a1\u771f\u6c14\u5439\u5230\u6c5f\u6d77\u5929\u7684\u864e \u53e3\uff0c\u4fe8\u5982\u706b\u70eb\u4e00\u822c\uff0c\u6c5f\u6d77\u5929\u4e00\u6293\u6293\u7a7a\uff0c\u90a3\u5934\u91d1\u6bdb\u72e1\u5df2\u8fc8\u5f00\u56db\u8e44\uff0c\u5982\u98de\u75be\u8d70\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5c3d\u5176\u6240\u80fd\uff0c\u4e5f\u4e0d\u8fc7\u5728\u77ed\u8ddd\u79bb\u5185\u4e0e\u91d1\u6bdb\u72e1\u7adf\u8d70\uff0c\u65f6\u95f4\u7a0d\u957f\uff0c\u91d1\u6bdb\u72e1\u5176\u884c\u5982\u98ce\uff0c \u90a3\u5df1\u662f\u975e\u4eba\u529b\u6240\u53ef\u8ffd\u4e0a\u4e86\u3002 \u3000\u3000\u534e\u5929\u98ce\u4e0e\u5510\u52aa\u73e0\u7a46\u5206\u4e24\u8def\u4e0a\u5c71\uff0c\u8fd9\u65f6\u4e5f\u90fd\u5df2\u5230\u4e86\u5c71\u4e0a\u3002\u534e\u5929\u98ce\u8ddd\u79bb\u90a3\u767d\u88d8\u5c11\u5e74\u8f83\u8fd1\uff0c \u5927\u53eb\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u5feb\u628a\u6211\u5973\u513f\u653e\u4e0b\u6765\uff01\u201d\u5510\u52aa\u73e0\u7a46\u634f\u4e86\u4e00\u56e2\u96ea\uff0c\u4ee5\u5927\u4e58\u822c\u82e5\u638c\u529b\u53d1\u4e86 \u51fa\u53bb\uff0c\u6253\u90a3\u91d1\u6bdb\u72e1\u3002\u90a3\u767d\u88d8\u5c11\u5e74\u4f3c\u4e4e\u5446\u4e86\u4e00\u5446\uff0c\u4f46\u5374\u5e76\u6ca1\u6709\u6b62\u4f4f\u91d1\u6bdb\u72e1\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u56e2\u96ea\u5757\u5df1\u4f3c\u5f39\u4e38\u5c04\u5230\uff0c\u547c\u547c\u5e26\u98ce\uff0c\u90a3\u767d\u88d8\u5c11\u5e74\u53c8\u4ece\u7389\u7bab\u4e2d\u5439\u51fa \u4e00\u80a1\u70ed\u98ce\uff0c\u96ea\u5757\u5728\u70ed\u98ce\u4e2d\u6eb6\u5316\uff0c\u8d77\u4e86\u4e00\u91cd\u767d\u8499\u8499\u7684\u6c34\u6c14\uff0c\u53cc\u65b9\u90fd\u4e3a\u5bf9\u65b9\u7684\u529f\u529b\u800c\u611f\u5230\u60ca \u5947\u3002 \u3000\u3000\u90a3\u767d\u88d8\u5c11\u5e74\u5439\u5316\u4e86\u96ea\u5757\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u4f60\u7eb5\u662f\u5979\u7684\u7236\u4eb2\uff0c\u6211\u4e5f\u4e0d\u653e\u5fc3\u73b0\u5728\u5c31\u628a\u4f60\u7684 \u5973\u513f\u4ea4\u8fd8\u7ed9\u4f60\u3002\u666e\u5929\u4e4b\u4e0b\uff0c\u53ea\u6709\u6211\u80fd\u533b\u5979\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u8bf4\u5230\u6700\u540e\u4e00\u53e5\uff0c\u4e24\u5934\u91d1\u6bdb\u72e1\u5728 \u96ea\u5730\u4e0a\u5df2\u53d8\u6210\u4e86\u4e24\u70b9\u9ed1\u70b9\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u8fde\u9ed1\u70b9\u4e5f\u4e0d\u89c1\u4e86\u3002\u5728\u767d\u832b\u832b\u7684\u96ea\u5730\u571f\uff0c\u6781\u76ee\u9762\u671b\uff0c \u53ea\u4f9d\u7a00\u53ef\u89c1\u4e00\u7ebf\u91d1\u5149\uff0c\u98ce\u9a70\u7535\u63a3\uff01 \u3000\u3000\u767d\u88d8\u5c11\u5e74\u4eba\u5f71\u5df2\u6773\uff0c\u4f46\u4ed6\u7684\u7bab\u58f0\u5374\u5728\u8fdc\u8fdc\u4f20\u6765\uff0c\u534e\u5929\u98ce\u6742\u5b66\u9887\u4e30\uff0c\u533b\u535c\u661f\u76f8\uff0c\u97f3\u5f8b \u8bcd\u7ae0\u5747\u6240\u901a\u6653\uff0c\u542c\u4ed6\u5439\u7684\uff0c\u662f\u5510\u4eba\u5f20\u4e5d\u9f84\u4e00\u76f2\u4e94\u8a00\u53e4\u8bd7\u8c31\u6210\u7684\u66f2\u5b50\uff0c\u539f\u8bd7\u662f\uff1a\u201c\u5170\u53f6\u9752 \u8473\u96cd\uff0c\u6842\u534e\u79cb\u768e\u6d01\u3002\u6b23\u6b23\u4e3a\u751f\u610f\uff0c\u81ea\u5c14\u4e3a\u4f73\u8282\u3002\u8c01\u5207\u6797\u6816\u8005\uff0c\u95fb\u98ce\u5750\u76f8\u60a6\u3002\u8349\u6728\u6709\u672c\u5fc3\uff0c \u4f55\u6c42\u7f8e\u4eba\u6298\u3002\u201d\u8bd7\u53e5\u9ad8\u96c5\uff0c\u8c31\u6210\u66f2\u5b50\uff0c\u97f3\u97f5\u60a0\u626c\uff0c\u4ee4\u4eba\u6709\u5c18\u4fd7\u987f\u6e05\u4e4b\u611f\u3002 \u3000\u3000\u534e\u5929\u98ce\u4e0d\u89c9\u5fc3\u4e2d\u4e00\u52a8\uff0c\u7ec6\u5473\u8bd7\u4e2d\u610f\u601d\uff1b\u4f3c\u4e4e\u4e5f\u53ef\u4ee5\u4fea\u91ca\u4e3a\u90a3\u5c11\u5e74\u7684\u81ea\u6211\u8868\u767d\uff0c\u90a3\u662f \u8bf4\u4ed6\u5fd7\u884c\u9ad8\u6d01\uff0c\u5bf9\u534e\u4e91\u78a7\u51b3\u65e0\u90aa\u5ff5\uff0c\u4f46\u76f8\u9047\u4e4b\u540e\uff0c\u5374\u6709\u6240\u503e\u6155\uff0c\u4ed6\u5e76\u4e0d\u6c42\u5355\u4e91\u78a7\u5bf9\u4ed6\u5982 \u4f55\u62a5\u7b54\uff0c\u53ea\u662f\u51fa\u4e8e\u4ed6\u7684\u672c\u5fc3\uff0c\u56e0\u800c\u4ed6\u8981\u770b\u62a4\u534e\u4e91\u78a7\u3002 \u3000\u3000\u534e\u5929\u98ce\u5fc3\u9053\uff1a\u201c\u5018\u82e5\u6211\u6240\u63e3\u5ea6\u7684\u5f53\u771f\u662f\u4ed6\u7684\u672c\u610f\uff0c\u90a3\u4e48\u8fd9\u5c11\u5e74\u5012\u4e5f\u4e0d\u4fd7\u3002\u201d\u5bf9\u8fd9\u5c11 \u5e74\u7684\u654c\u610f\u4e0d\u89c9\u6d88\u706d\u4e86\u51e0\u5206\uff0c\u4f46\u968f\u5373\u60f3\u9053\uff1a \u3000\u3000\u201c\u5c11\u5e74\u4eba\u77e5\u597d\u8272\u5219\u6155\u5c11\u827e\uff0c\u8fd9\u5c11\u5e74\u7eb5\u7136\u6ca1\u6709\u574f\u610f\uff0c\u4f46\u8ba9\u4ed6\u548c\u78a7\u513f\u6db8\u5904\uff0c\u603b\u662f\u4e0d\u59a5\u3002 \u5509\uff0c\u53ef\u522b\u8981\u95f9\u51fa\u4e8b\u624d\u597d\u3002\u201d \u3000\u3000\u8981\u77e5\u5728\u534e\u5929\u98ce\u7684\u5fc3\u76ee\u4e4b\u4e2d\uff0c\u65e9\u5df2\u628a\u6c5f\u6d77\u5929\u4e0e\u4ed6\u5973\u513f\u8fde\u5728\u4e00\u8d77\uff0c\u770b\u6210\u4e00\u5bf9\u52bf\u76f8\u7ed3\u5408\u7684 \u4f73\u5076\uff1b\u56e0\u800c\u89c1\u90a3\u5c11\u5e74\u5c06\u4ed6\u5973\u513f\u5e26\u8d70\uff0c\u5c31\u96be\u514d\u591a\u4e86\u8bb8\u591a\u987e\u8651\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u672a\u80fd\u5c06\u534e\u4e91\u78a7\u5bfb\u56de\uff0c\u867d\u7136\u4e5f\u5f88\u5931\u671b\uff0c\u4f46\u5728\u5931\u671b\u4e4b\u4e2d\uff0c\u4e5f\u6709\u51e0\u5206\u6b23\u6170\u3002\u5728\u6b64\u4e4b \u524d\uff0c\u4ed6\u662f\u4e3a\u534e\u4e91\u78a7\u7684\u5b58\u4ea1\u672a\u535c\u800c\u62c5\u5fe7\uff0c\u73b0\u5728\u8fd9\u5757\u5fc3\u4e0a\u7684\u5927\u77f3\u5934\u662f\u653e\u4e0b\u6765\u4e86\uff0c\u800c\u4e14\u5c3d\u7ba1\u90a3 \u5c11\u5e74\u65e5\u53e3\u58f0\u58f0\u8d23\u9a82\u4e8e\u4ed6\uff0c\u4f46\u53ef\u4ee5\u542c\u5f97\u51fa\u6765\uff0c\u8fd9\u4e5f\u662f\u51fa\u4e8e\u7231\u62a4\u534e\u4e91\u78a7\u7684\u4e00\u756a\u597d\u610f\u3002 \u3000\u3000\u5f53\u4e0b\u6c5f\u6d77\u5929\u5c06\u53d1\u73b0\u90a3\u4e2a\u5c11\u5e74\u4ee5\u53ca\u52a8\u624b\u7684\u7ecf\u8fc7\uff0c\u90fd\u5bf9\u4e49\u7236\u8bf4\u4e86\uff0c\u53ea\u662f\u5c06\u90a3\u5c11\u5e74\u8d23\u5907\u4ed6 \u7684\u8bf4\u8bdd\u7565\u53bb\u4e0d\u63d0\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u542c\u8fd9\u5c11\u5e74\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u4ed6\u6b63\u5728\u4e3a\u78a7\u59b9\u6cbb\u75c5\u7597\u4f24\uff0c\u5374\u53ef\u7b11\u4ed6\u6709\u773c\u4e0d\u8bc6 \u6cf0\u5c71\uff0c\u4e0d\u77e5\u5e72\u7239\u5c31\u662f\u5f53\u4ee3\u795e\u533b\u3002\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u5929\u4e0b\u7684\u5947\u96be\u6742\u75c7\u5f88\u591a\uff0c\u6211\u4e5f\u672a\u5fc5\u90fd\u80fd\u533b \u62ac\u3002\u54b1\u4eec\u4e14\u8fdb\u8fd9\u51b0\u5c4b\u77a7\u77a7\uff0c\u6211\u8981\u770b\u78a7\u513f\u662f\u600e\u4e48\u8fc7\u6d3b\u7684\uff0c\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7528\u5b9d\u5251\u5f00\u4e86\u4e00\u4e2a\u7a9f\u7abf\uff0c\u4e09\u4eba\u4f9d\u6b21\u94bb\u4e86\u8fdb\u53bb\u3002\u534e\u5929\u98ce\u89c1\u4e86\u90a3\u5f20\u58a8\u7eff\u8272\u7684\u602a\u5e8a\u3002 \u5931\u58f0\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u51b0\u5c4b\u91cc\u8fd8\u6709\u8fd9\u4ef6\u5b9d\u7269\uff0c\u602a\u4e0d\u5f97\u78a7\u513f\u80fd\u53d7\u4e25\u5bd2\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9 \u662f\u4ec0\u4e48\u505a\u7684\uff1f\u201d\u534e\u65e0\u98ce\u9053\uff1a\u201c\u4f60\u6478\u4e00\u6478\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7528\u624b\u4e00\u6478\uff0c\u53ea\u89c1\u4e00\u7247\u6e29\u6696\uff0c\u6709\u8bf4\u4e0d\u51fa\u7684\u8212\u670d\u3002\u534e\u5929\u98ce\u9053\uff1a\u201c\u8fd9\u662f\u6606\u4ed1\u5c71\u7279\u4ea7 \u7684\u6e29\u51c9\u7389\u505a\u7684\u5e8a\uff0c\u51ac\u6e29\u590f\u51c9\uff0c\u5bf9\u75c5\u4eba\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u6e29\u51c9\u7389\u5728\u6606\u4ed1\u5c71\u4e0a\u8fd8\u4e0d\u7b97\u592a\u96be\u5f97\uff0c\u4f46 \u8981\u91c7\u96c6\u8fd9\u8bb8\u591a\u6765\u505a\u4e00\u5f20\u5e8a\uff0c\u5374\u4e0d\u77e5\u8981\u8d39\u5c3d\u51e0\u8bb8\u5fc3\u529b\uff0c\u90a3\u5f53\u7136\u4e5f\u662f\u4e00\u4ef6\u5f02\u5b9d\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6697\u6697\u540e\u6094\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u54b1\u4eec\u5c06\u90a3\u5c11\u5e74\u8d76\u8dd1\uff0c\u4e0d\u662f\u53cd\u800c\u5bf9\u78a7\u59b9\u7684\u75c5\u4f53 \u4e0d\u5229\u4e48\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u8fd9\u4e5f\u4e0d\u5c3d\u7136\uff0c\u53ea\u8981\u5979\u5371\u9669\u65f6\u671f\u5df2\u8fc7\uff0c\u5728\u5c71\u4e0b\u517b\u75c5\uff0c\u90a3\u5c31\u66f4\u597d\u5f97 \u591a\uff0c\u4e5f\u65e0\u987b\u8fd9\u5f20\u5e8a\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u64a4\u624b\u5c31\u8dd1\uff0c\u5374\u653e\u5fc3\u5f97\u4e0b\u8fd9\u5f20\u5b9d\u5e8a\uff1f\u201d\u534e\u5929 \u98ce\u7b11\u9053\uff1a\u201c\u4e0a\u5f97\u8fd9\u7075\u9e6b\u5cf0\u9876\u7684\u80fd\u6709\u51e0\u4eba\uff1f\u80fd\u6765\u6b64\u95f4\u7684\u81ea\u5fc5\u662f\u9ad8\u4eba\u5f02\u58eb\uff0c\u4e5f\u5c31\u672a\u5fc5\u4f1a\u5e0c\u7f55 \u4ed6\u8fd9\u5b9d\u7269\u3002\u800c\u4e14\u7eb5\u4f7f\u6709\u4eba\u52a8\u4e86\u8d2a\u5ff5\uff0c\u8981\u628a\u8fd9\u5f20\u5e8a\u642c\u4e0b\u53bb\u4ea6\u4e0d\u5927\u5bb9\u6613\u5462\uff01\u54b1\u4eec\u5012\u4e0d\u5fc5\u4e3a\u4ed6 \u62c5\u5fe7\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u4e0d\u77e5\u662f\u4ec0\u4e48\u6765\u5386\uff0c\u72ec\u81ea\u4f4f\u5728\u8fd9\u9ad8\u5cf0\u4e4b\u4e0a\u3001\u51b0\u5c4b\u4e4b\u4e2d\uff0c\u5c4b\u4e2d\u53c8\u6709 \u8fd9\u6837\u4e00\u5f20\u5b9d\u5e8a\uff0c\u5012\u662f\u53e4\u602a\u5f97\u7d27\uff01\u4ed6\u8fd9\u5f20\u5b9d\u5e8a\u4e5f\u4e0d\u77e5\u5f53\u521d\u662f\u600e\u6837\u642c\u4e0a\u7684\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u53bb\u662f\u642c\u4e0a\u6765\u7684\uff0c\u60f3\u5fc5\u662f\u5728\u6606\u4ed1\u5c71\u4e0a\u91c7\u96c6\u4e86\u6e29\u51c9\u7389\u4e4b\u540e\uff0c\u624d\u5728\u8fd9 \u91cc\u9020\u7684\u3002\u4ed6\u5728\u8fd9\u91cc\u5c45\u4f4f\uff0c\u6709\u8fd9\u6837\u4e00\u5f20\u5b9d\u5e8a\uff0c\u7eb5\u4f7f\u4e0d\u662f\u4e3a\u4e86\u907f\u5bd2\uff0c\u5bf9\u4ed6\u4fee\u7ec3\u5185\u529f\uff0c\u4e5f\u5927\u6709 \u88e4\u76ca\uff0c\u53ea\u662f\u4ed6\u4e3a\u4f55\u8981\u5728\u8fd9\u91cc\u72ec\u5b88\u8352\u5c71\uff0c\u5012\u662f\u4ee4\u4eba\u731c\u60f3\u4e0d\u900f\u3002\u201d \u3000\u3000\u4ed6\u4eec\u867d\u7136\u731c\u4e0d\u900f\u8fd9\u5c11\u5e74\u7684\u6765\u5386\uff0c\u4f46\u6709\u4e00\u70b9\u53ef\u4ee5\u80af\u5b9a\u7684\u662f\uff0c\u8fd9\u5c11\u5e74\u5bf9\u534e\u4e91\u78a7\u786e\u662f\u6bb7\u52e4 \u5475\u62a4\uff0c\u51b3\u65e0\u574f\u610f\uff0c\u56e0\u800c\u4ed6\u4eec\u4e5f\u5c31\u5bbd\u5fc3\u4e0d\u5c11\u4e86\u3002 \u3000\u3000\u8d70\u51fa\u51b0\u5c4b\uff0c\u5c71\u98ce\u5439\u6765\uff0c\u534e\u5929\u98ce\u5438\u4e86\u53e3\u6c14\uff0c\u5ffd\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u55c5\u5230\u98ce\u4e2d\u6709\u6de1\u6de1\u7684\u5e7d \u9999\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f46\u8fd9\u6c14\u5473\u597d\u5947\u602a\uff0c\u4f3c\u4e4e\u5728\u9999\u6c14\u4e4b\u4e2d\u8fd8\u6df7\u6742\u7740\u4e00\u80a1\u8165\u5473\uff0c\u4ee4\u4eba \u53c8\u662f\u8212\u670d\uff0c\u53c8\u662f\u538c\u70e6\uff0c\u8fd9\u611f\u89c9\u7b80\u76f4\u96be\u4ee5\u8a00\u8bed\u5f62\u5bb9\u3002\u201d\u521a\u624d\u4ed6\u4eec\u6765\u5230\u7684\u65f6\u5019\uff0c\u5927\u5bb6\u90fd\u56e0\u4e3a \u5fc3\u4e2d\u6709\u4e8b\uff0c\u672a\u66fe\u7559\u610f\uff0c\u5982\u4eca\u547c\u5438\u5c71\u98ce\uff0c\u5f7c\u6b64\u5370\u8bc1\u6240\u5f97\u7684\u611f\u89c9\uff0c\u679c\u7136\u90fd\u611f\u5230\u662f\u6709\u8fd9\u4e48\u4e00\u80a1 \u5947\u602a\u7684\u6c14\u5473\u3002 \u3000\u3000\u534e\u5929\u98ce\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u80a1\u6c14\u5473\uff0c\u4e00\u6e05\u4e00\u6d4a\uff0c\u6df7\u5728\u4e00\u8d77\uff0c\u786e\u662f\u5947\u602a\u3002\u6211\u53ea\u53ef \u4ee5\u5224\u65ad\u90a3\u6e05\u6de1\u7684\u5e7d\u9999\u662f\u4e09\u8272\u5947\u82b1\u7684\u9999\u6c14\uff0c\u4f46\u90a3\u80a1\u8165\u6627\u5374\u4e0d\u77e5\u662f\u4ec0\u4e48\u4e1c\u897f\u6240\u53d1\u4e86\u3002\u90a3\u4e09\u8272 \u5947\u82b1\uff0c\u53ea\u6709\u8fd9\u7075\u9e6b\u5cf0\u4e0a\u624d\u6709\uff0c\u65e2\u5230\u6b64\u5730\uff0c\u5012\u4e0d\u59a8\u524d\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u8fd9\u79cd\u4e09\u8272\u5947\u82b1\u6709\u4e2a\u540d\u5b57\u53eb\u505a\u201c\u96ea\u91cc\u7ea2\u5986\u201d\uff0c\u670d\u98df\u4e4b\u540e\uff0c\u53ef\u4ee5\u767d\u53d1\u53d8\u9ed1\uff0c\u70bc\u6210\u836f\u4e38\uff0c \u7ecf\u5e38\u670d\u98df\uff0c\u66f4\u53ef\u4ee5\u6c38\u4fdd\u5bb9\u989c\uff0c\u9752\u6625\u957f\u9a7b\u3002 \u3000\u3000\u4e0d\u8fc7\u5b83\u4e5f\u53ea\u662f\u80fd\u602a\u7740\u5bb9\u989c\uff0c\u5374\u4e0d\u80fd\u5ef6\u957f\u5bff\u547d\uff0c\u4e0d\u80fd\u533b\u7597\u75be\u75c5\u3002\u4f3a\u6b64\u534e\u5929\u98ce\u4ece\u836f\u4e66\u4e0a \u867d\u7136\u65e9\u5df2\u77e5\u9053\u7075\u9e6b\u5cf0\u4e0a\u6709\u6b64\u79cd\u5947\u82b1\uff0c\u5374\u56e0\u5b83\u533b\u7597\u4ef7\u503c\u4e0d\u5927\uff0c\u4e0d\u613f\u5192\u9669\u6765\u91c7\u3002\u4f46\u4ed6\u4e00\u751f\u7814 \u7a76\u836f\u7269\uff0c\u65e2\u7136\u6765\u5230\u4e86\u6b64\u82b1\u7684\u552f\u4e00\u4ea7\u5730\uff0c\u4e5f\u5c31\u60f3\u53bb\u89c1\u8bc6\u89c1\u8bc6\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e0e\u5510\u52aa\u78a7\u7a46\u4e5f\u52a8\u4e86\u597d\u5947\u4e4b\u5fc3\uff0c\u5f53\u4e0b\u4e00\u884c\u4e09\u4f17\u3002\u671d\u7740\u98ce\u5411\u8d70\u53bb\u3002\u90a3\u4e24\u80a1\u6c14\u5473\u8d8a \u6765\u8d8a\u6d53\uff0c\u5230\u4e86\u4e00\u4e2a\u6240\u5728\uff0c\u53ea\u89c1\u5728\u4e00\u5757\u51b0\u5ca9\u4e4b\u4e0a\uff0c\u5b64\u96f6\u96f6\u5730\u957f\u7740\u4e00\u68f5\u6811\uff0c\u6811\u4e0a\u6ca1\u6709\u82b1\u6735\uff0c \u6811\u4e0b\u5374\u8fd8\u6709\u51e0\u7247\u96f6\u843d\u7684\u82b1\u74e3\uff0c\u6709\u7684\u6d01\u767d\uff0c\u6709\u7684\u5ae9\u9ec4\uff0c\u8fd8\u6709\u4e00\u7247\u5728\u7c89\u767d\u4e4b\u4e2d\u5e26\u7740\u7ea2\u6655\u3002 \u3000\u3000\u534e\u5929\u98ce\u5c06\u795e\u9e70\u5e26\u6765\u7684\u90a3\u6735\u201c\u96ea\u91cc\u7ea2\u5986\u201d\u62ff\u6765\u4e00\u6bd4\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u5728\u8fd9\u6811\u4e0a\u5f00\u7684\u5c31\u662f\u4e09\u8272\u5947\u82b1\u4e86\u3002\u770b\u7559\u4e0b\u6765\u7684\u75d5\u8ff9\uff0c\u672c\u6765\u5df2\u5f00\u4e86\u4e09\u6735\uff0c \u90fd\u7ed9\u4eba\u6458\u4e0b\u6765\u4e86\uff0c\u8fd9\u51e0\u7247\u82b1\u74e3\uff0c\u5927\u7ea6\u662f\u90a3\u5934\u795e\u9e70\u90a3\u65e5\u6251\u4e0b\u6765\u65f6\uff0c\u717d\u8d77\u4e00\u80a1\u72c2\u98ce\uff0c\u5439\u6765\u4e86 \u8fd9\u51e0\u7247\u7684\u3002\u201d\u4ed6\u62ac\u8d77\u90a3\u51e0\u7247\u82b1\u74e3\uff0c\u55c5\u4e86\u53c8\u55c5\uff0c\u53ea\u89c9\u5f97\u6e05\u9999\u6251\u9f3b\u6c81\u4eba\u813e\u8151\uff0c\u8bf4\u9053\uff1a\u201c\u5947\u602a\uff0c \u90a3\u80a1\u8165\u6c14\u5374\u53c8\u662f\u4ece\u4f55\u800c\u6765\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5728\u51b0\u5ca9\u4e0b\u4fe1\u6b65\u5f98\u5f8a\uff0c\u5ffd\u7136\u89e6\u7740\u4e00\u7269\uff0c\u53ea\u89c9\u5947\u5bd2\u5f7b\u9aa8\uff0c\u6bd4\u51b0\u96ea\u66f4\u4e3a\u5bd2\u51b0\uff0c\u5403\u4e86 \u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u4ef6\u602a\u7269\uff01\u201d\u7528\u5b9d\u5251\u6311\u8d77\u6765\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u4e00\u6761\u901a\u4f53\u900f\u660e\u7684\u602a\u86c7\uff0c \u5934\u9888\u597d\u4f3c\u88ab\u5229\u722a\u6293\u4e86\u4e00\u9053\u88c2\u75d5\uff0c\u65e9\u5df2\u6b7b\u4e86\u591a\u65f6\uff0c\u56e0\u5b83\u901a\u4f53\u900f\u660e\uff0c\u800c\u53c8\u5367\u5728\u51b0\u96ea\u4e4b\u4e2d\uff0c\u8981 \u4e0d\u662f\u5076\u7136\u89e6\u7740\uff0c\u6839\u672c\u5c31\u770b\u4e0d\u51fa\u6765\u3002\u8fd9\u6761\u602a\u86c7\u4e00\u6311\u8d77\u6765\uff0c\u8165\u6c14\u5f25\u6f2b\uff0c\u767b\u65f6\u76d6\u8fc7\u4e86\u82b1\u9999\u3002 \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u5feb\u5feb\u628a\u5b83\u8fdc\u8fdc\u6454\u5f00\u3002\u201d\u8fd9\u80a1\u8165\u6c14\u4f7f\u4eba\u6b32\u5455\uff0c\u6c5f\u6d77\u5929\u95fb\u4e86\u4e5f\u4e0d\u8212\u670d\uff0c\u5f53 \u4e0b\u5c06\u90a3\u6761\u602a\u86c7\u6454\u5165\u51b0\u5ddd\u3002\u534e\u5929\u98ce\u63a9\u9f3b\u8bf4\u9053\uff1a\u201c\u5e78\u4e8f\u8fd9\u6761\u602a\u86c7\u5df2\u6b7b\uff0c\u5426\u5219\u53ea\u6015\u9664\u4e86\u6d77\u513f\u4e4b \u5916\u3002\u6211\u4e0e\u73e0\u7a46\u4e16\u5144\u6015\u90fd\u8981\u4e2d\u6bd2\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u9a87\u7136\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u6bd2\u86c7\uff0c\u5982\u6b64\u5389\u5bb3\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u8fd9\u602a\u86c7\u540d\u53eb\u96ea\u7ec3\u86c7\uff0c\u672c\u8eab\u7684\u6bd2\u6027\u5012\u5e76\u4e0d\u600e\u6837\u5389\u5bb3\uff0c\u5389\u5bb3\u7684\u662f\u5b83\u6240\u55b7\u7684 \u5bd2\u96fe\uff0c\u6cbe\u4e0a\u4e00\u70b9\u5bd2\u6bd2\u4fbf\u4fb5\u8fdb\u8840\u8109\u4e4b\u4e2d\uff0c\u4f46\u5374\u5e76\u4e0d\u5373\u65f6\u8eab\u6b7b\uff0c\u53ea\u662f\u4ee5\u540e\u4f1a\u4e0d\u65f6\u53d1\u4f5c\uff0c\u4e00\u6b21 \u5389\u5bb3\u8fc7\u4e8c\u6b21\uff0c\u53d1\u4f5c\u7684\u65f6\u5019\uff0c\u6bd4\u6700\u4e25\u91cd\u7684\u759f\u75be\u8fd8\u7d27\u8981\u767e\u500d\uff0c\u5168\u8eab\u5982\u5760\u8fdb\u706b\u7a9f\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u8fd9\u4e48\u5389\u5bb3\uff0c\u4e00\u4e2a\u4eba\u80fd\u7981\u5f97\u8d77\u51e0\u6b21\u714e\u71ac\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u636e\u53e4\u8001\u7684\u85cf \u6587\u533b\u4e66\u8bb0\u8f7d\uff0c\u9ec4\u6559\u59cb\u7956\u516b\u601d\u5df4\u6709\u4e2a\u5f1f\u5b50\uff0c\u6b66\u529f\u5f88\u597d\uff0c\u5192\u9669\u5230\u8fd9\u7075\u9e6b\u5cf0\u6765\u91c7\u201c\u96ea\u91cc\u7ea2\u5986\u201d\uff0c \u4e0d\u63d0\u9632\u88ab\u8fd9\u96ea\u7ec3\u86c7\u55b7\u4e86\u4e00\u53e3\u6bd2\u96fe\uff0c\u7ed3\u679c\u5947\u82b1\u662f\u91c7\u5230\u4e86\uff0c\u8eab\u4e0a\u4e5f\u53d7\u4e86\u5947\u6bd2\uff0c\u4ed6\u7981\u4e0d\u8d77\u714e\u71ac\uff0c \u672a\u66fe\u4e0b\u5c71\uff0c\u5c31\u6295\u8fdb\u51b0\u6cb3\u4e4b\u4e2d\u81ea\u5c3d\u4e86\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u6709\u8fd9\u4e2a\u6545\u4e8b\uff0c\u516b\u601d\u5df4\u548c\u4ed6\u5341\u4e2a\u5f1f\u5b50\u7684\u5851\u50cf\uff0c\u7f3a\u5c11\u4e86\u4e00\u4e2a\u5f1f \u5b50\uff0c\u5c31\u56e0\u4e3a\u8fd9\u4e2a\u5f1f\u5b50\u662f\u81ea\u6740\u7684\uff0c\u8fdd\u53cd\u6559\u89c4\uff0c\u6240\u4ee5\u4e0d\u80fd\u7ed9\u4ed6\u7acb\u50cf\u3002\u4f46\u6211\u5374\u4e0d\u77e5\u9053\u8fd9\u5f1f\u5b50\u662f \u56e0\u88ab\u96ea\u7ec3\u86c7\u55b7\u4e86\u6bd2\u96fe\u800c\u81ea\u6740\u7684\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u79cd\u96ea\u7ec3\u86c7\u603b\u662f\u4f34\u6625\u90a3\u4e09\u8272\u5947\u82b1\u7684\u5417\uff1f\u201d \u3000\u3000\u534a\u5929\u98ce\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5929\u5730\u95f4\u5927\u51e1\u6709\u4e00\u5947\u73cd\u5f02\u5b9d\u6216\u8005\u7075\u836f\u4ed9\u82b1\uff0c\u5728\u5b83\u7684\u5468\u56f4\u603b\u4f1a\u6709\u8fd9 \u79cd\u6216\u90a3\u79cd\u9669\u963b\uff0c\u770b\u6765\u8fd9\u79cd\u96ea\u7ec3\u86c7\u751f\u6765\u5c31\u662f\u4fdd\u62a4\u90a3\u4e09\u8272\u5947\u82b1\u7684\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u90a3\u4e48\u5929 \u5730\u4e4b\u95f4\u53ef\u6709\u514b\u5236\u96ea\u7ec3\u86c7\u7684\u4e1c\u897f\u5417\uff1f\u662f\u4e0d\u662f\u4e2d\u4e86\u86c7\u7684\u5bd2\u96fe\u5c31\u65e0\u836f\u53ef\u533b\uff1f\u201d\u534e\u65e0\u98ce\u9053\uff1a\u201c\u53ea \u6709\u4e00\u6837\u4e1c\u897f\u53ef\u4ee5\u514b\u5236\u5b83\u3002\u90a3\u662f\u5410\u9c81\u756a\u706b\u7130\u5c71\u9644\u8fd1\u6240\u4ea7\u7684\u2018\u706b\u7ec3\u86c7\u2019\uff0c\u8fd9\u79cd\u86c7\u8eab\u8574\u5947\u6bd2\uff0c \u7528\u5b83\u7684\u80c6\u914d\u836f\uff0c\u53ef\u89e3\u96ea\u7ec3\u86c7\u7684\u5bd2\u6bd2\u3002 \u3000\u3000\u53ea\u662f\u8fd9\u79cd\u706b\u7ec3\u86c7\u85cf\u5728\u706b\u7130\u5c71\u5c71\u811a\u7684\u7194\u5ca9\u6d46\u4e2d\uff0c\u66f4\u96be\u6355\u6349\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5982\u6709\u6240\u601d\uff0c\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u5e72\u7239\uff0c\u4f60\u521a\u624d\u8bf4\u90a3\u5bd2\u6bd2\u53d1\u4f5c\u4e4b\u65f6\uff0c\u6bd4\u6700\u4e25\u91cd\u7684\u759f\u75be \u8fd8\u7d27\u8981\u767e\u500d\uff0c\u90a3\u4e48\u75c5\u4eba\u4e5f\u5fc5\u5b9a\u662f\u660f\u8ff7\u7684\u4e86\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u8fd9\u8fd8\u7528\u8bf4\uff0c\u5f53\u7136\u662f\u795e\u667a\u4e0d\u6e05\uff0c \u660f\u8ff7\u8fc7\u53bb\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u90a3\u4e48\u4ed6\u8fd8\u4f1a\u8bf4\u68a6\u8bdd\u5417\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u78a7\u513f\u4e2d\u7684\u5c31\u662f\u8fd9\u79cd\u5bd2\u6bd2\uff01\u201d\u6c5f\u6d77\u5929\u521a\u624d\u542c\u4ed6 \u4e49\u7236\u89e3\u8bf4\u8fd9\u79cd\u201c\u96ea\u7ec3\u86c7\u201d\u7684\u6bd2\u6027\uff0c\u5fc3\u4e2d\u5df2\u6709\u7591\u8651\uff0c\u5982\u4eca\u542c\u4e49\u7236\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5df2\u662f\u8bc1\u5b9e\u4e86\u4ed6 \u7684\u7591\u8651\uff0c\u4e0d\u7981\u614c\u5f97\u4e00\u9897\u9897\u51b7\u6c57\u8ff8\u4e86\u51fa\u6765\uff0c\u5446\u82e5\u6728\u9e21\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u4e86\u3002 \u3000\u3000\u534e\u5929\u98ce\u5ffd\u9053\uff1a\u201c\u4f60\u521a\u624d\u95ee\u7684\u4ec0\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u5b9a\u4e86\u5b9a\u795e\uff0c\u62b9\u53bb\u4e86\u989d\u4e0a\u7684\u51b7\u6c57\uff0c\u8bf4\u9053\uff1a \u201c\u8eab\u53d7\u8fd9\u79cd\u5bd2\u6bd2\u7684\u75c5\u4eba\uff0c\u662f\u4e0d\u662f\u4f1a\u8bf4\u68a6\u8bdd\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u4f60\u542c\u89c1\u78a7\u513f\u8bf4\u4ec0\u4e48\u68a6\u6d3b\u4e86\uff1f\u201d \u6c5f\u6d77\u5929\u987e\u4e0d\u5f97\u9762\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u521d\u4e0a\u7075\u9e6b\u5cf0\u5934\u7684\u65f6\u5019\uff0c\u542c\u5f97\u5979\u53eb\u6211\u4e24\u58f0\u3002\u540e\u6765\u90a3\u5c11\u5e74\u5c06 \u5979\u5e26\u8d70\uff0c\u6211\u8ffd\u5230\u91d1\u6bdb\u72e1\u7684\u540e\u9762\uff0c\u53c8\u542c\u5f97\u5979\u53eb\u6211\u4e24\u58f0\u3002\u4f46\u5979\u4e00\u76f4\u6ca1\u6709\u5f20\u773c\u775b\uff0c\u6211\u60f3\u8fd9\u591a\u534a \u662f\u68a6\u4e2d\u7684\u5453\u8bed\u4e86\u3002\u201d\u534e\u5929\u98ce\u4e00\u62cd\u8111\u888b\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u540e\u6765\u90a3\u4e24\u58f0\u6211\u4e5f\u542c\u5230\u7684\u4e86\uff01\u201d \u3000\u3000\u534e\u65e0\u98ce\u8bf4\u4e86\u8fd9\u4e24\u53e5\u8bdd\uff0c\u8d1f\u624b\u5f98\u5f8a\uff0c\u82e5\u6709\u6240\u601d\uff0c\u5510\u52aa\u73e0\u7a46\u548c\u6c5f\u6d77\u5929\u77e5\u9053\u4ed6\u6b63\u5728\u7528\u5fc3\u63a8 \u7a76\u534e\u4e91\u78a7\u7684\u75c5\u8c61\uff0c\u90fd\u4e0d\u6562\u6253\u6405\u4ed6\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\u3002\u534e\u65e0\u98ce\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u8fd8\u597d\uff01\u201d\u6c5f \u6d77\u5929\u8fde\u5fd9\u95ee\u9053\uff1a \u3000\u3000\u201c\u600e\u4e48\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u6211\u4e0d\u4f46\u77e5\u9053\u78a7\u513f\u5df2\u8131\u4e86\u9669\u5883\uff0c\u800c\u4e14\u524d\u56e0\u540e\u679c\uff0c\u6211\u4e5f\u90fd\u660e\u767d \u4e86\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5e72\u7239\uff0c\u4f60\u662f\u600e\u4e48\u690e\u7a76\u51fa\u6765\u7684\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u4f60\u521a\u624d\u7528\u5b9d\u5251\u6311\u8d77\u90a3\u96ea \u7ec3\u86c7\u7684\u65f6\u4faf\uff0c\u5b83\u9888\u9aa8\u4e0a\u662f\u5426\u6709\u9053\u88c2\u75d5\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u8fd9\u96ea\u7ec3\u86c7\u8eab\u575a\u5982\u94c1\uff0c\u5bfb\u5e38\u7684\u5200\u5251\u4e5f\u4e0d\u80fd\u5c06\u5b83\u5256\u88c2\uff0c\u78a7\u513f\u8eab\u4e0a\u6ca1\u6709\u5b9d\u5251\uff0c \u8fd9\u662f\u88ab\u795e\u9e70\u7684\u5229\u722a\u6240\u6293\u88c2\u7684\u3002\u5176\u5b9e\u6211\u65e9\u5c31\u5e94\u8be5\u60f3\u5230\u4e86\uff0c\u78a7\u513f\u8dcc\u843d\u5728\u8fd9\u4e09\u8272\u5947\u82b1\u4e4b\u65c1\uff0c\u6240 \u4ee5\u5979\u624d\u80fd\u5728\u660f\u8ff7\u4e4b\u524d\uff0c\u6458\u4e0b\u4e00\u6735\u96ea\u91cc\u7ea2\u5986\uff0c\u53eb\u795e\u9e70\u7ed9\u6211\u62a5\u8baf\u3002\u73b0\u5728\u6211\u8fde\u5979\u5f53\u65f6\u4f55\u4ee5\u8dcc\u4e0b \u7684\u539f\u56e0\uff0c\u4e5f\u77e5\u9053\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u5fc3\u4e0a\u6709\u4e2a\u7591\u56e2\uff0c\u95ee\u9053\uff0c\u201c\u78a7\u59b9\u8dcc\u843d\u5728\u8fd9\u513f\uff0c\u8fd9\u662f\u4e00\u5b9a\u7684\u4e86\u3002 \u4f46\u6211\u60f3\u4e0d\u901a\u7684\u662f\uff0c\u795e\u9e70\u98de\u5728\u5929\u4e0a\uff0c\u96be\u9053\u96ea\u7ec3\u86c7\u7684\u6bd2\u96fe\u80fd\u591f\u55b7\u5230\u5b83\u7684\u8eab\u4e0a\uff0c\u6ce2\u53ca\u4e86\u78a7\u59b9\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u79bd\u517d\u86c7\u866b\u90fd\u6709\u751f\u514b\u7684\u4e60\u60ef\uff0c\u4f8b\u5982\u732b\u8981\u6349\u8001\u9f20\uff0c\u86c7\u8981\u541e\u9752\u86d9\uff0c \u732b\u53ea\u8981\u95fb\u5230\u8001\u9f20\u7684\u6c14\u5473\u3002\u5c31\u4f1a\u6251\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u6211\u8fd9\u5934\u795e\u9e70\u4e13\u9664\u6bd2\u7269\uff0c\u8fd9\u96ea\u7ec3\u86c7\u6709\u80a1\u4f8d\u6b8a\u7684\u8165\u5473\uff0c\u795e\u9e70\u98de\u8fc7\uff0c\u55c5\u5230\u8fd9\u80a1\u6c14\u5473\uff0c\u4f7f\u6251 \u4e86\u4e0b\u6765\uff0c\u5c06\u5b83\u9888\u9aa8\u6293\u88c2\u3002\u96ea\u7ec3\u86c7\u7684\u5bd2\u96fe\u4f24\u4e0d\u4e86\u795e\u9e70\uff0c\u5374\u4f24\u4e86\u6211\u7684\u78a7\u513f\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a \u201c\u795e\u9e70\u8eab\u4e0a\u4e2d\u7684\u90a3\u4e24\u679d\u77ed\u7bad\u5462\uff1f\u8fd9\u53c8\u5982\u4f55\u89e3\u91ca\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u662f\u90a3\u767d\u88d8\u5c11\u5e74\u5c04\u7684\u3002\u201d \u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u6211\u53c8\u60f3\u4e0d\u901a\u4e86\u3002\u4ed6\u5bf9\u78a7\u59b9\u5c0f\u5fc3\u7167\u6599\uff0c\u5f53\u65f6\u4f55\u4ee5\u53c8\u8981\u4f24\u5bb3\u78a7\u59b9\u7684\u6797\u9e70\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u4f9d\u6211\u60f3\u6765\uff0c\u90a3\u5c11\u5e74\u72ec\u81ea\u5728\u8fd9\u7075\u9e6b\u5cf0\u4e0a\u5c45\u4f4f\uff0c\u4e3a\u7684\u5c31\u662f\u5b88\u5019\u2018\u96ea\u91cc\u7ea2\u5986\u2019 \u5f00\u82b1\uff0c\u8fd9\u2018\u96ea\u91cc\u7ea2\u5986\u2019\u6bcf\u516d\u5341\u5e74\u5f00\u82b1\u4e00\u6b21\uff0c\u60f3\u5fc5\u662f\u5c11\u5e74\u7684\u4e0a\u4e00\u4ee3\u5df2\u53d1\u73b0\u4e86\u8fd9\u91cc\u6709\u4e00\u682a \u2018\u96ea\u91cc\u7ea2\u5986\u2019\uff0c\u5374\u4e0d\u77e5\u5b83\u4f55\u65f6\u5f00\u82b1\uff0c\u56e0\u800c\u5728\u8fd9\u5c71\u4e0a\u9020\u4e86\u4e00\u5ea7\u51b0\u5c4b\uff0c\u53c8\u91c7\u96c6\u4e86\u4ed1\u6606\u5c71\u7684\u6e29 \u51c9\u7389\uff0c\u9020\u4e86\u5c4b\u4e2d\u7684\u90a3\u5f20\u5b9d\u5e8a\uff0c\u5b9a\u5c45\u4e0b\u6765\uff0c\u65e2\u53ef\u5b88\u5019\u82b1\u5f00\uff0c\u53c8\u53ef\u60dc\u7740\u8fd9\u9ad8\u5cf0\u6c14\u5019\uff0c\u548c\u8fd9\u5f20 \u5b9d\u5e8a\u7684\u529f\u80fd\uff0c\u7ec3\u4ed6\u4eec\u72ec\u95e8\u7684\u5185\u529f\u3002\u7236\u4f20\u5b50\uff0c\u751a\u6216\u662f\u7956\u4f20\u5b59\uff0c\u4f20\u5230\u4e86\u521a\u624d\u6240\u89c1\u7684\u90a3\u4e2a\u5c11\u5e74 \u8fd9\u4ee3\uff0c\u2018\u96ea\u91cc\u7ea2\u5986\u2019\u65b9\u59cb\u5f00\u82b1\uff01\u201d \u3000\u3000\u534e\u5929\u51e4\u5c06\u90a3\u51e0\u7247\u82b1\u74e3\u62fe\u4e86\u8d77\u6765\uff0c\u63a5\u7eed\u8bf4\u9053\uff1a\u201c\u795e\u9e70\u98de\u8fc7\u90a3\u5929\uff0c\u6070\u5de7\u6811\u4e0a\u7684\u4e09\u6735\u2018\u96ea \u91cc\u7ea2\u5986\uff0c\u5168\u90fd\u5f00\u4e86\uff0c\u90a3\u5c11\u5e74\u5728\u6b64\u5b88\u5019\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u6458\u8fd9\u4e09\u6735\u7ea2\u82b1\uff0c\u4ed6\u600e\u80af\u8ba9\u795e\u9e70\u4f24\u6b8b\u4e86 \u82b1\u6735\uff0c\u56e0\u6b64\u4ed6\u8fdc\u8fdc\u7684\u4e00\u89c1\u795e\u9e70\u6251\u4e0b\uff0c\u4fbf\u53d1\u51fa\u4e86\u90a3\u4e24\u679d\u77ed\u7bad\uff0c\u8fc7\u540e\u624d\u77e5\u9053\u9e70\u80cc\u4e0a\u8fd8\u6709\u4e2a\u4eba\u3002\u201d \u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u78a7\u59b9\u8dcc\u4e0b\u6765\u7684\u65f6\u5019\uff0c\u90a3\u5c11\u5e74\u8fd8\u672a\u53ca\u8d76\u6765\uff0c\u78a7\u59b9\u89c1\u4ed6\u5c04\u4f24\u795e\u9e70\uff0c \u5b9a\u7136\u662f\u5c06\u4ed6\u5f53\u4f5c\u4e86\u654c\u4eba\u3002\u795e\u9e70\u5df2\u4f24\uff0c\u4e0d\u80fd\u518d\u9a6e\u5979\u9ad8\u98de\uff0c\u5979\u5728\u660f\u8ff7\u4e4b\u524d\uff0c\u6458\u4e0b\u82b1\u6735\uff0c\u7f1a\u5728 \u795e\u9e70\u722a\uff0c\u7eb5\u5b83\u98de\u56de\u3002\u53ef\u601c\uff0c\u8fd9\u5934\u795e\u9e70\u5e26\u7bad\u98de\u884c\uff0c\u4e00\u5b9a\u662f\u98de\u4e00\u4f1a\u6b47\u4e00\u4f1a\uff0c\u56e0\u800c\u6bd4\u6211\u7684\u6b65\u884c \u7adf\u5feb\u4e0d\u4e86\u591a\u5c11\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u4e0d\u60dc\uff0c\u4f60\u63a8\u65ad\u5f97\u5408\u60c5\u5408\u7406\u3002\u7b97\u8d77\u65e5\u671f\u3002\u795e\u9e70\u98de\u5230\u6c34\u4e91\u5e84\u7684\u65f6\u5019\u662f\u54b1\u4eec \u5230\u7ec8\u5357\u5c71\u7684\u524d\u4e24\u5929\u3002\u201d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u7167\u8fd9\u60c5\u5f62\u770b\u6765\u3002\u90a3\u5c11\u5e74\u968f\u540e\u8d76\u5230\uff0c\u8fd9\u624d\u53d1\u89c9\u4e86\u4f60\u7684 \u78a7\u59b9\u8eab\u4e2d\u5bd2\u6bd2\uff0c\u4e8e\u662f\u5c06\u5979\u642c\u5230\u51b0\u5c4b\u4e4b\u5185\uff0c\u5c06\u90a3\u5f20\u5b9d\u5e8a\u4e5f\u8ba9\u4e86\u7ed9\u5979\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u8fd9\u5c11\u5e74\u5012\u662f\u4e2a\u597d\u4eba\u5462\uff01\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4ed6\u80fd\u591f\u5c04\u4f24\u795e\u9e70\uff0c\u529f\u529b\u4e5f\u5927 \u662f\u4e0d\u51e1\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4ed6\u662f\u6211\u78b0\u5230\u7684\u7b2c\u4e8c\u4e2a\u52b2\u654c\uff0c\u6b66\u529f\u4ec5\u6b21\u4e8e\u5b9d\u8c61\u6cd5\u4e3b\u3002\u6211\u7684\u5185\u529f\u662f\u9760\u836f\u7269 \u589e\u8fdb\u7684\uff0c\u4ed6\u5374\u662f\u81ea\u5df1\u7ec3\u6210\u529f\u7684\u3002\u5b9e\u5728\u8bf4\u6765\uff0c\u4ed6\u7684\u672c\u9886\u8fd8\u5728\u6211\u4e4b\u4e0a\u3002\u201d\u5176\u5b9e\u90a3\u5c11\u5e74\u4e0e\u6c5f\u6d77 \u5929\u76f8\u5dee\u4f55\u6b62\u4e00\u7b79\uff0c\u5373\u4f7f\u6c5f\u6d77\u5929\u672a\u98df\u5929\u5fc3\u77f3\u4e4b\u524d\uff0c\u90a3\u5c11\u5e74\u81f3\u591a\u4e5f\u4e0d\u8fc7\u548c\u4ed6\u6253\u6210\u5e73\u624b\uff0c\u51b3\u4e0d \u80fd\u80dc\u5f97\u8fc7\u4ed6\u3002\u6c5f\u6d77\u5929\u6709\u610f\u5938\u5f20\u90a3\u5c11\u5e74\u7684\u6b66\u529f\uff0c\u5b9e\u662f\u60f3\u4f7f\u81ea\u5df1\u7684\u4e49\u7236\u5bf9\u8fd9\u5c11\u5e74\u66f4\u5177\u597d\u611f\u3002 \u3000\u3000\u534e\u5929\u98ce\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u4e16\u4ee3\u5728\u8fd9\u91cc\u5b88\u5019\u82b1\u5f00\uff0c\u5f53\u7136\u4e5f\u65e9\u5df2\u77e5\u9053\u6709\u8fd9\u4e48\u4e00\u79cd\u5389\u5bb3 \u7684\u96ea\u7ec3\u86c7\uff0c\u770b\u5b88\u7740\u90a3\u96ea\u91cc\u7ea2\u5986\u3002\u60f3\u5fc5\u4e5f\u65e9\u5df2\u77e5\u9053\u514b\u5236\u8fd9\u6bd2\u86c7\u7684\u6cd5\u5b50\uff0c\u5907\u6709\u706b\u7130\u5c71\u6240\u4ea7\u7684 \u706b\u7ec3\u86c7\u7684\u86c7\u80c6\uff0c\u914d\u6210\u89e3\u836f\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4ed6\u6562\u5728\u4f60\u7684\u9762\u524d\u5938\u53e3\uff0c\u8bf4\u662f\u78a7\u59b9\u7684\u75c5\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u53ea\u6709\u4ed6\u4e00 \u4eba\u80fd\u591f\u533b\u6cbb\u3002\u4e49\u7236\uff0c\u4f60\u8bf4\u78a7\u59b9\u5df2\u662f\u8131\u79bb\u5371\u9669\uff0c\u53ef\u662f\u6839\u636e\u8fd9\u4e2a\u7406\u7531\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u671b\u6c5f\u6d77\u5929\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u4ec5\u4ec5\u662f\u5355\u51ed\u63a8\u65ad\uff0c\u800c\u662f\u56e0\u4e3a\u5979\u5728\u660f\u8ff7\u4e4b\u4e2d\uff0c\u8fd8\u4f1a \u53eb\u4f60\u3002\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u8bf4\u9053\uff1a\u201c\u82e5\u662f\u8eab\u53d7\u7684\u5bd2\u6bd2\u5341\u5206\u5389\u5bb3\uff0c\u660f\u8ff7\u4e4b\u65f6\uff0c\u5c31\u5982\u6b7b\u53bb\u4e00\u822c\uff0c\u6839 \u672c\u5c31\u4e1d\u6beb\u4e0d\u80fd\u601d\u60f3\uff0c\u8fde\u68a6\u4e5f\u4e0d\u4f1a\u6709\u7684\u3002\u6709\u68a6\u5373\u662f\u8fd8\u80fd\u601d\u60f3\uff0c\u68a6\u4e2d\u800c\u80fd\u8bf4\u8bdd\uff0c\u90a3\u66f4\u662f\u5728\u4e00 \u79cd\u534a\u9192\u534a\u7761\u7684\u72b6\u6001\u4e2d\uff0c\u75c5\u5f97\u6781\u6c89\u91cd\u7684\u4eba\u662f\u4e0d\u4f1a\u8bf4\u68a6\u6d3b\u7684\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u653e\u4e0b\u4e86\u5fc3\u5934\u7684\u77f3\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u597d\u4e86\u3002\u65e5\u540e\u6211\u5018\u82e5\u78b0\u7740\u90a3\u4e2a\u5c11\u5e74\uff0c\u6211\u8981\u5411 \u4ed6\u9053\u6b49\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u7ed9\u78a7\u59b9\u6cbb\u75c5\uff0c\u662f\u8be5\u611f\u8c22\u3002\u4f46\u6211\u5374\u4e0d\u89e3\uff0c\u4ed6\u4f55\u4ee5\u4e0d\u80af\u5c06 \u78a7\u59b9\u4ea4\u8fd8\u4f60\u4eec\uff1f\u751a\u81f3\u77e5\u9053\u4e86\u534e\u8001\u4f2f\u662f\u5979\u7236\u4eb2\u4e86\uff0c\u4e5f\u8fd8\u8981\u5c06\u5979\u5e26\u8d70\uff1f\u201d\u534e\u5929\u98ce\u4e0e\u6c5f\u6d77\u5929\u8bb7 \u8bb7\u8bf4\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u7684\u884c\u5f84\u662f\u6709\u4e9b\u53e4\u602a\uff0c\u4f46\u603b\u662f\u4e00\u7247\u597d\u5fc3\u3002\u201d \u3000\u3000\u534e\u65e0\u98ce\u7b11\u9053\uff1a\u201c\u6211\u7684\u78a7\u513f\u4e0d\u68a6\u89c1\u6211\u800c\u68a6\u89c1\u4f60\uff0c\u770b\u6765\u5979\u6700\u8bb0\u6302\u7684\u4eba\u8fd8\u662f\u4f60\u5462\uff01\u201d\u6c5f\u6d77 \u5929\u4e0d\u7981\u5fc3\u5934\u4e8c\u9707\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u767d\u88d8\u5c11\u5e74\u5341\u5206\u6b22\u559c\u78a7\u59b9\uff0c\u90a3\u662f\u65e0\u7591\u7684\u4e86\u3002\u4f46\u613f\u78a7\u59b9\u5b8c \u5168\u9192\u6765\u4e4b\u540e\uff0c\u4f1a\u611f\u4ed6\u8fd9\u7247\u6069\u60c5\u3002\u201d\u968f\u5373\u53c8\u60f3\uff1a\u201c\u5355\u662f\u611f\u6069\uff0c\u90a3\u8fd8\u4e0d\u6210\u3002\u78a7\u59b9\u4f55\u5c1d\u4e0d\u5bf9\u6211 \u6709\u6069\uff0c\u4f46\u6211\u5374\u53ea\u613f\u5a36\u83b2\u59b9\u4e3a\u59bb\u3002\u201d\u4e00\u65f6\u6b22\u559c\uff0c\u4e00\u65f6\u6101\u8651\uff0c\u4e0d\u89c9\u5fc3\u5982\u4e71\u9ebb\u3002 \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u4e8b\u60c5\u7684\u7ecf\u8fc7\u6211\u5dee\u4e0d\u591a\u90fd\u63a8\u7a76\u51fa\u6765\u4e86\uff0c\u53ea\u662f\u6709\u4e00\u4ef6\u4e8b\u6211\u8fd8\u611f\u5230\u6709\u70b9\u610f\u5916\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u95ee\u9053\uff1a\u201c\u662f\u54ea\u4e00\u4ef6\u4e8b\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u5389\u590d\u751f\u662f\u5929\u9b54\u6559\u7684\u526f\u6559\u4e3b\uff0c\u60f3\u4e0d\u5230\u4ed6 \u4f1a\u5728\u7075\u9e6b\u5cf0\u4e0a\u51fa\u73b0\uff0c\u800c\u4e14\u548c\u8fd9\u5c11\u5e74\u540c\u5728\u4e00\u8d77\u3002\u6211\u672c\u6765\u53ef\u4ee5\u653e\u5fc3\u7684\uff0c\u4f46\u60f3\u5230\u8fd9\u5c11\u5e74\u548c\u5929\u9b54 \u6559\u7684\u5996\u4eba\u6765\u5f80\uff0c\u6211\u53c8\u6709\u70b9\u62c5\u5fe7\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u5389\u590d\u751f\u53ea\u662f\u7740\u4e86\u5929\u9b54\u6559\u4e3b\u7684\u8ff7\uff0c\u6211\u5374\u4ece\u6ca1\u542c\u8bf4\u4ed6\u505a\u8fc7\u4ec0\u4e48\u574f\u4e8b\u3002 \u6211\u5e08\u7236\u4e5f\u5f88\u770b\u91cd\u4ed6\uff0c\u8bf4\u4ed6\u662f\u4e2a\u597d\u4eba\u5462\uff01\u201d\u5176\u5b9e\u91d1\u4e16\u9057\u4e3a\u4e86\u7231\u5c4b\u53ca\u4e4c\uff0c\u5bf9\u5389\u590d\u751f\u53e6\u773c\u76f8\u770b \u5012\u662f\u771f\u7684\uff0c\u8bf4\u4ed6\u662f\u4e2a\u597d\u4eba\u7684\u8bdd\uff0c\u90a3\u5374\u6c5f\u6d77\u5929\u60f3\u5f53\u7136\u8033\u3002 \u3000\u3000\u534e\u5929\u98ce\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u662f\u4f60\u5e08\u7236\u8fd9\u4e48\u8bf4\uff0c\u90a3\u60f3\u5fc5\u662f\u597d\u4eba\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a \u201c\u5389\u590d\u751f\u6709\u5426\u505a\u8fc7\u597d\u4e8b\u6211\u4e0d\u77e5\u9053\uff0c\u4f46\u8fd9\u5c11\u5e74\u6551\u62a4\u78a7\u59b9\uff0c\u5982\u4eca\u54b1\u4eec\u90fd\u77e5\u9053\u4e86\uff0c\u4ed6\u66f4\u662f\u4e2a\u5927 \u5927\u7684\u597d\u4eba\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u7814\u7a76\u8c01\u662f\u597d\u4eba\u8c01\u662f\u574f\u4eba\u4e86\uff0c\u4e8b\u60c5\u5df2\u7ecf\u660e\u767d\uff0c\u54b1\u4eec\u8be5\u4e0b\u53bb\u5566\uff0c \u4e91\u743c\u5144\u59b9\u53ea\u6015\u4e5f\u7b49\u5f97\u5fc3\u7126\u4e86\u3002\u201d\u534e\u5929\u98ce\u5fc3\u91cc\u867d\u7136\u8fd8\u6709\u8bb8\u591a\u987e\u8651\uff0c\u4f46\u603b\u7b97\u77e5\u9053\u4e86\u5973\u513f\u7684\u4e0b \u843d\uff0c\u800c\u4e14\u5e76\u65e0\u6027\u547d\u4e4b\u5fe7\uff0c\u5fc3\u60c5\u4e5f\u5c31\u8f7b\u677e\u8bb8\u591a\u4e86\u3002 \u3000\u3000\u51b0\u5cf0\u9661\u5ced\uff0c\u4e0b\u5c71\u6bd4\u4e0a\u5c71\u66f4\u96be\uff0c\u4e00\u4e0d\u5c0f\u5fc3\uff0c\u5c31\u4f1a\u8dcc\u5f97\u7c89\u8eab\u788e\u9aa8\u3002\u534e\u5929\u98ce\u529f\u529b\u867d\u5f88\u6df1\u539a\uff0c \u7a76\u7adf\u6bd4\u4e0d\u4e0a\u6c5f\u6d77\u5929\uff0c\u53c8\u662f\u4e0a\u4e86\u4e00\u70b9\u5e74\u7eaa\uff0c\u6c5f\u6d77\u5929\u6015\u51fa\u610f\u5916\uff0c\u7d27\u7d27\u8ddf\u5728\u4ed6\u7684\u8eab\u8fb9\uff0c\u5c0f\u5fc3\u7ffc \u7ffc\u7684\u7167\u987e\u4ed6\uff0c\u8d70\u5230\u9669\u5cfb\u4e4b\u5904\uff0c\u5c31\u6276\u4ed6\u8fc7\u53bb\u3002\u8fd9\u4e48\u4e00\u6765\u3002\u6c5f\u6d77\u5929\u529f\u529b\u6700\u9ad8\u5374\u53cd\u800c\u843d\u540e\uff0c\u5510 \u52aa\u73e0\u7a46\u5374\u8d70\u5728\u524d\u5934\u3002 \u3000\u3000\u8d70\u5230\u5cf0\u8170\uff0c\u5510\u52aa\u73e0\u7a46\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u6c5f\u5e08\u5144\uff0c\u4f60\u542c\uff0c\u8fd9\u662f\u4e0d\u662f\u5175\u5668\u78b0\u51fb\u7684\u58f0\u97f3\uff1f\u201d\u6c5f \u6d77\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u4fa7\u8033\u4e00\u542c\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4e0d\u9519\uff0c\u662f\u56db\u4e2a\u4eba\u5206\u6210\u4e24\u5bf9\u53ae\u6740\uff0c\u60f3\u5fc5\u662f\u4e91\u743c\u5144\u59b9\u9047\u4e0a\u4e86\u654c\u4eba\uff0c\u4f60\u5feb\u70b9\u4e0b\u53bb\uff01\u201d\u5510 \u52aa\u73e0\u7a46\u65bd\u5c55\u795e\u529f\uff0c\u63d0\u4e86\u53e3\u6c14\uff0c\u811a\u4e0d\u6cbe\u5730\uff0c\u51e0\u4e4e\u662f\u5fa1\u6c14\u800c\u884c\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u548c\u6c5f\u3001\u534e\u4e8c\u4eba\u7684 \u8ddd\u79bb\u5df1\u62c9\u957f\u4e86\u6570\u5341\u4e08\u3002 \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u6d77\u513f\uff0c\u4f60\u4e0d\u5fc5\u7167\u987e\u6211\u4e86\u3002\u73b0\u5728\u5df1\u8fc7\u4e86\u4e00\u534a\uff0c\u5e95\u4e0b\u5df2\u6ca1\u90a3\u4e48\u9669\u5cfb\u4e86\u3002\u201d \u6c5f\u6d77\u5929\u9053\uff1a\u201c\u73e0\u7a46\u7684\u5927\u4e58\u822c\u82e5\u638c\uff0c\u5728\u6b66\u6797\u4e2d\u80fd\u53d7\u5f97\u4ed6\u4e00\u638c\u7684\uff0c\u53ea\u6015\u4e5f\u8fd8\u4e0d\u591a\u5462\u3002\u6709\u4ed6\u4e0b \u53bb\uff0c\u5df2\u8db3\u53ef\u5e94\u4ed8\u5f97\u7ef0\u7ef0\u6709\u4f59\u3002\u201d \u3000\u3000\u5c31\u5728\u6c5f\u6d77\u5929\u8bf4\u8bdd\u7684\u65f6\u4faf\uff0c\u5510\u52aa\u73e0\u7a46\u5df2\u770b\u5f97\u89c1\u4e86\u654c\u4eba\u3002\u6c5f\u6d77\u5929\u4ee5\u4e3a\u5510\u52aa\u73e0\u7a46\u662f\u53ef\u5e94\u4ed8 \u6709\u4f59\uff0c\u5510\u52aa\u73e0\u7a46\u5374\u662f\u5927\u5403\u4e00\u60ca\u3002\u8fd9\u4e24\u4e2a\u654c\u4eba\u90fd\u662f\u4ed6\u8ba4\u8bc6\u7684\uff0c\u4e00\u4e2a\u662f\u6587\u5ef7\u58c1\uff0c\u4e00\u4e2a\u662f\u90a3\u597d \u738b\u76d6\u6e29\u4e4b\u5b50\u76d6\u82cf\u3002 \u3000\u3000\u4e91\u743c\u62fc\u4e86\u6027\u547d\uff0c\u4ee5\u91d1\u521a\u638c\u529b\u654c\u6587\u5ef7\u58c1\uff0c\u4f46\u7a76\u7adf\u662f\u529f\u529b\u76f8\u5dee\u5927\u8fdc\uff0c\u7ed9\u6587\u5ef7\u58c1\u6253\u5f97\u6b65\u6b65 \u540e\u9000\uff0c\u8fd8\u5e78\u4e91\u5bb6\u7684\u91d1\u521a\u638c\u5929\u4e0b\u65e0\u53cc\uff0c\u4e91\u743c\u5e74\u8f7b\u529b\u58ee\uff0c\u4f7f\u8fd9\u79cd\u5a01\u731b\u7684\u638c\u529b\uff0c\u5728\u6700\u521d\u4e09\u5341\u62db \u4e4b\u5185\uff0c\u6bcf\u4e00\u638c\u90fd\u662f\u53ef\u88c2\u77f3\u5f00\u7891\uff0c\u56e0\u800c\u8fd8\u53ef\u4ee5\u52c9\u5f3a\u62b5\u5fa1\u6587\u5ef7\u58c1\u7684\u4e09\u8c61\u795e\u529f\uff0c\u4f46\u4ea6\u5df2\u662f\u5c8c\u5c8c \u53ef\u5371\u3002\u53e6\u4e00\u8fb9\u4e91\u58c1\u4e0e\u76d6\u82cf\u5404\u4ee5\u5200\u5251\u4ea4\u950b\uff0c\u5374\u662f\u76f8\u5dee\u4e0d\u8fdc\uff0c\u4e91\u58c1\u53ea\u662f\u7565\u5904\u4e0b\u98ce\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u53eb\u9053\uff1a\u201c\u58c1\u59b9\u522b\u614c\uff0c\u6211\u6765\u4e86\uff01\u201d\u4e91\u58c1\u542c\u5f97\u4ed6\u7684\u58f0\u97f3\uff0c\u7cbe\u795e\u4e00\u632f\uff0c\u5530\u5530\u5530\uff0c \u8fde\u73af\u6570\u5251\uff0c\u5c06\u76d6\u82cf\u7684\u653b\u52bf\u904f\u6b62\uff0c\u6253\u6210\u4e86\u5e73\u624b\u3002\u53e6\u4e00\u8fb9\uff0c\u6587\u5ef7\u58c1\u5374\u52a0\u7d27\u8fdb\u653b\uff0c\u5c06\u4e91\u743c\u8feb\u5f97 \u6b65\u6b65\u540e\u9000\u3002\u5510\u52aa\u73e0\u7a46\u62a2\u4e0b\u5c71\u6765\uff0c\u89c1\u4e91\u58c1\u4e1a\u5df2\u8f6c\u5371\u4e3a\u5b89\uff0c\u4e91\u743c\u5374\u662f\u9669\u8c61\u73af\u751f\uff0c\u4ed6\u7565\u4e00\u8e0c\u8e87\uff0c \u5fc3\u60f3\uff1a\u201c\u76d6\u82cf\u672c\u9886\u6709\u9650\uff0c\u8c05\u4ed6\u9003\u4e0d\u51fa\u6211\u7684\u638c\u5fc3\uff0c\u8fd8\u662f\u5148\u6551\u4e91\u743c\u8981\u7d27\u3002\u201d \u3000\u3000\u6587\u5ef7\u58c1\u7684\u638c\u529b\u5df2\u628a\u4e91\u743c\u7f69\u4f4f\uff0c\u773c\u770b\u4e91\u743c\u5c31\u8981\u652f\u6301\u4e0d\u4e86\uff0c\u5510\u52aa\u73e0\u7a46\u6765\u5f97\u6070\u662f\u65f6\u5019\uff0c \u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u6587\u5ef7\u58c1\u9000\u540e\u4e00\u6b65\uff0c\u5510\u52aa\u73e0\u7a46\u4e5f\u6643\u4e86\u4e00\u6643\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u5b9b\u82e5\u9f99\u541f\uff0c\u9707\u5f97\u4f17\u4eba\u8033\u9f13\u90fd\u55e1\u55e1\u4f5c\u54cd\u3002\u6587\u5ef7\u58c1\u5403\u4e86\u4e00 \u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u6c5f\u6d77\u5929\u8fd9\u5c0f\u5b50\u4e5f\u6765\u4e86\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5510\u52aa\u6627\u7a46\u7b2c\u4e8c\u638c\u53c8\u5df2\u5288 \u5230\uff0c\u6587\u5ef7\u58c1\u9762\u8272\u7070\u767d\uff0c\u63a5\u4e86\u8fd9\u638c\uff0c\u95f7\u54fc\u4e00\u58f0\uff0c\u6447\u6447\u6b32\u5760\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u4e0a\u6b21\u5728\u9a6c\u8428\u513f\u738b\u5bab\u4e0e\u6587\u5ef7\u58c1\u5bf9\u638c\uff0c\u53cc\u65b9\u529f\u529b\u6089\u654c\uff0c\u8c01\u90fd\u80dc\u4e0d\u4e86\u8c01\uff0c\u4eca\u6b21\u53ea \u662f\u4ec5\u4ec5\u4e24\u638c\uff0c\u4fbf\u628a\u6587\u5ef7\u58c1\u6253\u5f97\u8fde\u62db\u67b6\u4e4b\u529b\u4f3c\u4e4e\u4e5f\u6ca1\u6709\u4e86\uff0c\u5fc3\u91cc\u4e0d\u7981\u6709\u70b9\u5947\u602a\uff0c\u8fd8\u53ea\u9053\u662f \u7531\u4e8e\u6587\u5ef7\u58c1\u5148\u6076\u6597\u4e86\u4e00\u573a\uff0c\u529f\u529b\u5df2\u662f\u6709\u6240\u635f\u8017\u7684\u7f18\u6545\uff0c\u5f53\u4e0b\u4e5f\u5c31\u65e0\u6687\u63a8\u6572\uff0c\u7b2c\u4e09\u638c\u53c8\u5373 \u8fc5\u901f\u53d1\u51fa\u3002\u8fd9\u4e00\u638c\u5510\u52aa\u73e0\u7a46\u8fd0\u8db3\u529f\u529b\uff0c\u638c\u529b\u6709\u5982\u6392\u5c71\u5012\u6d77\u800c\u6765\uff0c\u6587\u5ef7\u58c1\u5927\u53eb\u4e00\u58f0\uff0c\u5510\u52aa \u73e0\u7a46\u5c1a\u672a\u6253\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u53ea\u89c1\u6c60\u5df2\u4f3c\u76ae\u7403\u4e00\u822c\u5730\u629b\u4e86\u8d77\u6765\uff0c\u4eba\u5728\u534a\u7a7a\uff0c\u4e00\u53e3\u9c9c\u8840\u5c31\u55b7\u4e86 \u51fa\u6765\uff0c\u76f4\u98de\u51fa\u4e86\u4e09\u4e08\u5f00\u5916\uff0c\u773c\u770b\u662f\u4e0d\u80fd\u6d3b\u547d\u4e86\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u76d6\u82cf\u4e5f\u662f\u4e00\u58f0\u5927\u53eb\uff0c\u62d4\u6b65\u4fbf\u9003\uff0c\u6562\u60c5\u662f\u56e0\u89c1\u6587\u5ef7\u58c1\u8eab\u4ea1\uff0c\u5413\u5f97\u53ea \u6068\u7239\u5a18\u751f\u5c11\u4e86\u4e24\u6761\u817f\u3002\u76d6\u82cf\u662f\u597d\u738b\u76d6\u6e29\u4e4b\u5b50\uff0c\u53c8\u662f\u4e0e\u6606\u5e03\u5170\u56fd\u7684\u4f7f\u81e3\u540c\u6765\u9a6c\u8428\u513f\u56fd\u76d7\u5b9d \u7684\u4eba\uff0c\u5173\u7cfb\u91cd\u5927\uff0c\u5510\u52aa\u73e0\u7a46\u7109\u80fd\u5bb9\u4ed6\u9003\u8d70\uff1f\u5f53\u4e0b\u4e00\u58f0\u559d\u9053\uff1a\u201c\u5978\u8d3c\u8fd8\u60f3\u9003\u4e48\uff1f\u201d\u98de\u6b65\u4fbf \u8ffd\uff01 \u3000\u3000\u770b\u770b\u5c31\u8981\u8ffd\u4e0a\uff0c\u5ffd\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u540e\u9762\u4f3c\u662f\u6709\u4eba\u8dcc\u5012\uff0c\u968f\u5373\u542c\u5f97\u6587\u5ef7\u58c1\u54c8\u54c8\u5927 \u7b11\uff0c\u4e91\u58c1\u5219\u5728\u5c16\u58f0\u60ca\u53eb\uff01\u5510\u52aa\u73e0\u7a46\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u56de\u5934\u770b\u65f6\uff0c\u53ea\u662f\u4e91\u743c\u8dcc\u5728\u5730\u4e0a\uff0c\u8fd8 \u672a\u66fe\u722c\u5f97\u8d77\u6765\uff0c\u6587\u5ef7\u58c1\u5df2\u628a\u4e91\u58c1\u631f\u5728\u80c1\u4e0b\uff0c\u5411\u7740\u4e0e\u4ed6\u76f8\u53cd\u7684\u65b9\u5411\u9003\u4e86\uff01 \u3000\u3000\u539f\u6765\u6587\u5ef7\u58c1\u7684\u5410\u8840\u3001\u8dcc\u5012\uff0c\u90fd\u662f\u5047\u88c5\u51fa\u6765\u7684\uff0c\u5510\u52aa\u73e0\u7a46\u4e0e\u76d6\u82cf\u90fd\u4ee5\u4e3a\u4ed6\u662f\u6b7b\u4e86\uff0c\u5176 \u5b9e\u4ed6\u53ea\u662f\u53d7\u4e86\u4e00\u70b9\u70b9\u8f7b\u4f24\u800c\u5df2\uff0c\u5510\u52aa\u73e0\u7a46\u670d\u98df\u5929\u5fc3\u77f3\u4e4b\u540e\uff0c\u8ddd\u4eca\u5df2\u4e24\u6708\u6709\u591a\uff0c\u529f\u529b\u786e\u662f \u6bd4\u6587\u5ef7\u58c1\u9ad8\u51fa\u5c11\u8bb8\uff0c\u4f46\u4ea6\u4ec5\u662f\u9ad8\u51fa\u5c11\u8bb8\u800c\u5df2\uff0c\u8fd8\u672a\u8db3\u5236\u4ed6\u6b7b\u547d\u3002\u4ed6\u4e4b\u6240\u4ee5\u8981\u8bc8\u6b7b\uff0c\u4e43\u662f \u4e3a\u4e86\u6050\u60e7\u6c5f\u6d77\u5929\u7684\u7f18\u6545\u3002\u4ed6\u6b63\u662f\u8981\u5510\u52aa\u73e0\u7a46\u4ee5\u4e3a\u4ed6\u4e0d\u6b7b\u4ea6\u5df2\u91cd\u4f24\uff0c\u4ed6\u8fd9\u201c\u8c03\u864e\u79bb\u5c71\u201d\u4e4b \u8ba1\u624d\u80fd\u5b9e\u73b0\uff0c\u5510\u52aa\u73e0\u7a46\u4e8c\u8d70\u5f00\uff0c\u4ed6\u5c31\u53ef\u4ee5\u5728\u6c5f\u6d77\u5929\u672a\u5230\u4e4b\u524d\uff0c\u65bd\u5c55\u4ed6\u7684\u8fa3\u624b\u4e86\u3002 \u3000\u3000\u672c\u6765\u4ee5\u4e91\u743c\u5144\u59b9\u7684\u672c\u9886\uff0c\u867d\u8bf4\u662f\u5728\u6fc0\u6218\u4e4b\u540e\uff0c\u529b\u7aed\u7b4b\u75b2\uff0c\u4e5f\u7edd\u4e0d\u81f3\u4e8e\u88ab\u6587\u5ef7\u58c1\u5982\u6b64 \u8f7b\u6613\u5f97\u624b\uff0c\u53ea\u56e0\u4ed6\u4eec\u4e5f\u662f\u540c\u4e00\u5fc3\u601d\uff0c\u4ee5\u4e3a\u6587\u5ef7\u58c1\u4e0d\u6b7b\u4ea6\u5df2\u91cd\u4f24\uff0c\u538b\u6839\u513f\u5c31\u6ca1\u6709\u6212\u5907\uff0c\u4ee5 \u81f4\u4e91\u743c\u88ab\u4ed6\u4e00\u638c\u51fb\u5012\uff0c\u968f\u5373\u4e91\u58c1\u4e5f\u88ab\u4fd8\u4e86\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u542c\u5f97\u53eb\u58f0\uff0c\u56de\u5934\u4e00\u770b\uff0c\u89c1\u4e91\u58c1\u843d\u5728\u654c\u4eba\u624b\u4e2d\uff0c\u8fd9\u4e00\u60ca\u81ea\u662f\u975e\u540c\u5c0f\u53ef\u3001\u53ea\u597d \u653e\u8d70\u76d6\u82cf\uff0c\u8f6c\u8fc7\u8eab\u6765\u3002\u5411\u6587\u5ef7\u58c1\u8ffd\u53bb\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u8f7b\u529f\u672c\u6765\u5c31\u6bd4\u6587\u5ef7\u58c1\u7565\u80dc\u4e00\u7b79\uff0c\u6587\u5ef7\u58c1\u53c8\u631f\u7740\u4e2a\u4eba\uff0c\u4e0d\u6d88\u7247\u523b\u3002\u5510\u52aa\u73e0\u7a46 \u5df2\u7136\u8ffd\u4e0a\uff0c\u770b\u770b\u5c31\u8981\u5230\u4e86\u4ed6\u7684\u80cc\u540e\uff0c\u6587\u5ef7\u58c1\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u6015\u4f24\u4e86\u8fd9\u599e\u513f\u7684\u6027\u547d\uff0c\u5c31 \u4e0a\u6765\u5427\uff01\u201d\u58f0\u51fa\u638c\u53d1\uff0c\u53cd\u624b\u4e00\u62cd\uff0c\u52b2\u98ce\u547c\u547c\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u867d\u7136\u4e0d\u60e7\uff0c\u4f46\u4ed6\u5374\u4e0d\u6562\u4ee5\u5288\u7a7a\u638c\u8fd8\u51fb\uff0c\u53ea\u5f97\u8fd0\u8d77\u62a4\u4f53\u795e\u529f\uff0c\u907f\u5f00\u4e86\u5bf9\u65b9\u8fd9 \u4e00\u638c\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u4e24\u4eba\u7684\u8ddd\u79bb\u53c8\u62c9\u5f00\u4e86\u51e0\u6b65\u3002 \u3000\u3000\u4e91\u743c\u722c\u8d77\u8eab\u6765\uff0c\u4e5f\u5411\u524d\u8ffd\u8d76\uff0c\u5510\u52aa\u73e0\u7a46\u56e0\u4e3a\u4e0d\u65f6\u8981\u8eb2\u907f\u6587\u5ef7\u58c1\u7684\u5288\u7a7a\u638c\u529b\uff0c\u4e0d\u591a\u4e00 \u4f1a\uff0c\u4e91\u743c\u4e5f\u5df2\u4e0e\u4ed6\u4f1a\u5408\u3002\u4f46\u4e91\u58c1\u843d\u5728\u654c\u4eba\u624b\u4e2d\uff0c\u4ed6\u4eec\u4e24\u4eba\u90fd\u662f\u65e0\u8ba1\u53ef\u65bd\uff0c\u53ea\u597d\u9532\u800c\u4e0d\u820d\uff0c \u7d27\u7d27\u8ddf\u4f4f\u6587\u5ef7\u58c1\u80cc\u540e\u3002\u6587\u5ef7\u58c1\u9003\u4e0b\u51b0\u8c37\uff0c\u4ed6\u4eec\u4e5f\u8ffd\u8fdb\u4e86\u51b0\u8c37\u3002 \u3000\u3000\u51b0\u8c37\u5728\u4e24\u9762\u51b0\u5cf0\u5939\u5cd9\u4e4b\u4e0b\uff0c\u8ffd\u4e86\u4e00\u4f1a\uff0c\u8f6c\u8fc7\u4e00\u5904\u51b0\u5773\uff0c\u5ffd\u89c1\u524d\u9762\u90a3\u5ea7\u51b0\u5cf0\uff0c\u55b7\u51fa\u4e00 \u56e2\u56e2\u84b8\u6c14\uff0c\u9644\u8fd1\u5c42\u51b0\u6eb6\u89e3\uff1b\u707c\u70ed\u7684\u6c34\u82b1\uff0c\u98de\u6e85\u7a7a\u4e2d\uff0c\u5728\u6de1\u6de1\u7684\u659c\u6656\u6620\u7167\u4e4b\u4e0b\uff0c\u5f62\u6210\u4e00\u5708 \u5708\u5f69\u8272\u7eda\u70c2\u7684\u5149\u73af\uff0c\u6bd4\u5143\u5bb5\u6240\u653e\u7684\u70df\u82b1\u66f4\u4e3a\u60a6\u76ee\u3002 \u3000\u3000\u539f\u6765\u5728\u897f\u5317\u7684\u9ad8\u539f\u5730\u5e26\uff0c\u5730\u4e0b\u5230\u5904\u90fd\u6709\u706b\u5c71\uff0c\u706b\u5c71\u9644\u8fd1\uff0c\u5728\u5f80\u6709\u707c\u70ed\u7684\u55b7\u6cc9\uff0c\u6210\u4e3a \u9ad8\u539f\u7684\u4e00\u79cd\u5929\u7136\u5947\u666f\uff0c\u4f46\u8fd9\u4e9b\u706b\u5c71\u5341\u4e5d\u90fd\u662f\u5730\u6c14\u5df2\u7ecf\u5ba3\u6cc4\uff0c\u5373\u5c06\u201c\u8870\u8001\u201d\u7684\u706b\u5c71\uff0c\u5730\u4e0b \u6eb6\u6d46\u6240\u5e03\u7684\u8303\u56f4\u4e5f\u5927\u90fd\u5f88\u5c0f\uff0c\u4e0d\u662f\u90a3\u79cd\u7a81\u7136\u4f1a\u7206\u53d1\u7684\u5927\u706b\u5c71\uff0c\u4eba\u4eec\u4e60\u4ee5\u4e3a\u5e38\uff0c\u4e5f\u5c31\u4e0d\u4ee5 \u4e3a\u5947\u4e86\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u4e3a\u4e86\u6551\u56de\u4e91\u58c1\uff0c\u5373\u7b97\u524d\u9762\u6709\u5ea7\u201c\u6d3b\u706b\u5c71\u201d\uff0c\u4ed6\u4e5f\u8981\u8ffd\u8fc7\u53bb\uff0c\u4f55\u51b5\u6839\u672c\u5c31\u6ca1 \u6709\u89c1\u5230\u5192\u70df\u7684\u6d3b\u706b\u5c71\uff0c\u800c\u53ea\u662f\u770b\u5230\u707c\u70ed\u7684\u55b7\u6cc9\uff0c\u66f4\u4f55\u51b5\u8c37\u5e95\u4e0e\u5c71\u4e0a\u55b7\u6cc9\u7684\u6240\u5728\uff0c\u5c11\u8bf4\u4e5f \u6709\u767e\u6570\u5341\u4e08\u7684\u9ad8\u5ea6\u8ddd\u79bb\uff0c\u4ed6\u5f53\u7136\u66f4\u4e0d\u4ee5\u4e3a\u610f\u4e86\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u8e0f\u4e0a\u4e00\u5757\u5f62\u5982\u5c16\u5200\u7684\u51b0\u5757\uff0c\u5ffd\u5730\u811a\u6b65\u4e00\u4e2a\u8dc4\u8e09\uff0c\u9669\u4e9b\u8dcc\u5012\u3002\u5510\u52aa\u73e0\u7a46\u5927\u559c\uff0c \u8d81\u6b64\u65f6\u673a\uff0c\u4f7f\u51fa\u201c\u9694\u7a7a\u70b9\u7a74\u201d\u7684\u529f\u592b\uff0c\u5c06\u771f\u6c14\u51dd\u6210\u4e00\u7ebf\uff0c\u5411\u6587\u5ef7\u58c1\u80cc\u540e\u7684\u201c\u5c3e\u95fe\u7a74\u201d\u9065 \u9065\u4e00\u6233\uff0c\u6587\u5ef7\u58c1\u4e00\u58f0\u5927\u53eb\uff0c\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u5947\u4e8b\u51fa\u73b0\uff0c\u4ed6\u811a\u4e0b\u7684\u51b0\u5757\u7a81\u7136\u88c2\u5f00\u3002\u63a5\u7740\u4e00 \u8fde\u4e32\u7684\u7206\u70b8\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u7094\u5982\u95ea\u7535\uff0c\u8f6c\u773c\u95f4\u5468\u56f4\u6570\u5341\u4e08\u7684\u51b0\u5757\u5168\u90fd\u88c2\u5f00\uff0c\u4e00\u80a1\u6d2a\u6d41 \u7a81\u7136\u6d8c\u4e0a\uff0c\u6587\u5ef7\u58c1\u5413\u5f97\u9b44\u6563\u9b42\u98de\uff0c\u8fde\u5fd9\u6094\u4e91\u58c1\u4e00\u6454\uff0c\u970d\u5730\u4fbf\u8df3\u8d77\u6765\uff0c\u60f3\u8df3\u51fa\u8fd9\u5371\u9669\u7684\u5730 \u5e26\u3002 \u3000\u3000\u5728\u8fd9\u540c\u4e00\u65f6\u5019\uff0c\u5510\u52aa\u73e0\u7a46\u4e5f\u970d\u5730\u8df3\u4e86\u8d77\u6765\uff0c\uff1b\u4f46\u4ed6\u5e76\u4e0d\u662f\u60f3\u9003\u51fa\u8fd9\u5371\u9669\u5730\u5e26\uff0c\u800c\u662f \u98de\u8eab\u5411\u4e91\u58c1\u6251\u53bb\u3002\u4e91\u58c1\u88ab\u6454\u5728\u51b0\u5757\u4e0a\uff0c\u51b0\u5757\u7a81\u7136\u88c2\u5f00\uff0c\u6d2a\u6d41\u6d8c\u4e0a\uff0c\u5979\u5168\u8eab\u5df2\u6d78\u5165\u6c34\u4e2d\uff0c \u53ea\u9732\u51fa\u4e00\u5934\u5934\u53d1\u548c\u4e00\u53cc\u9ad8\u9ad8\u4e3e\u8d77\u7684\u624b\u81c2\u3002 \u3000\u3000\u539f\u6765\u5728\u4ed6\u4eec\u811a\u4e0b\u6b63\u662f\u4e00\u6761\u51b0\u6cb3\uff0c\u6cb3\u9762\u7ed3\u51b0\uff0c\u4e0b\u9762\u5219\u4ecd\u662f\u6697\u6d41\u6c79\u6d8c\u3002\u53ea\u56e0\u9644\u8fd1\u6709\u706b\u5c71 \u55b7\u6cc9\uff0c\u5730\u6c14\u6e29\u6696\uff0c\u6cb3\u9762\u6240\u7ed3\u7684\u51b0\u5c42\u4e0d\u539a\uff0c\u6587\u5ef7\u58c1\u8e0f\u788e\u4e86\u4e00\u5757\u51b0\u5757\uff0c\u7acb\u65f6\u5f15\u8d77\u8fde\u7410\u53cd\u5e94\u3002 \u5468\u56f4\u7684\u51b0\u5757\u5168\u90fd\u88c2\u5f00\u4e86\uff0c\u970e\u65f6\u95f4\u6062\u590d\u4e86\u672c\u6765\u7684\u9762\u76ee\uff0c\u6210\u4e86\u4e00\u6761\u5e26\u7740\u6d6e\u51b0\uff0c\u6c34\u6d41\u6e4d\u6025\u7684\u51b0 \u6cb3\uff01 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u594b\u8d77\u795e\u529b\uff0c\u4e00\u63a0\u6570\u4e08\uff0c\u4ece\u7a7a\u4e2d\u6251\u4e0b\uff0c\u6293\u7740\u4e86\u4e91\u58c1\u7684\u624b\u81c2\uff0c\u5c06\u5979\u63d0\u4e86\u8d77\u6765\u3001 \u53ef\u662f\u6ca1\u6709\u53ef\u4ee5\u843d\u811a\u7684\u5730\u65b9\uff0c\u5468\u56f4\u5341\u4e08\u4e4b\u5185\u90fd\u662f\u4e00\u7247\u6025\u6d41\u3002\u5510\u52aa\u73e0\u7a46\u5de6\u811a\u5728\u53f3\u811a\u811a\u80cc\u4e00\u8e0f\uff0c \u52c9\u5f3a\u53c8\u62d4\u9ad8\u5c11\u8bb8\uff0c\u4f46\u5728\u534a\u7a7a\u4e2d\u7a76\u7adf\u662f\u4f7f\u4e0d\u51fa\u6c14\u529b\uff0c\u4f55\u51b5\u4ed6\u53c8\u63d0\u7740\u4e2a\u4eba\uff0c\u600e\u4e48\u98de\u51fa\u5341\u4e08\u4e4b \u5916\uff1f\u4ed6\u7aed\u5c3d\u6240\u80fd\uff0c\u53cc\u8db3\u4ea4\u8e0f\uff0c\u4e09\u843d\u4e09\u8d77\uff0c\u7ec8\u4e8e\u8fd8\u662f\u4e0e\u4e91\u58c1\u53cc\u53cc\u8dcc\u8fdb\u4e86\u51b0\u6cb3\uff0c\u88ab\u90a3\u6d2a\u6d41\u5377 \u53bb\u3002\u5510\u52aa\u73e0\u7a46\u4e0d\u4f1a\u6e38\u6c34\uff0c\u53ea\u80fd\u4ed7\u7740\u7cbe\u7eaf\u7684\u5185\u529f\uff0c\u95ed\u4f4f\u4e86\u547c\u5438\uff0c\u540c\u65f6\u7d27\u7d27\u5730\u62b1\u7740\u4e86\u4e91\u58c1\uff0c \u514d\u5f97\u88ab\u6fc0\u6d41\u51b2\u5f00\u3002\u4ed6\u53cc\u811a\u4e0d\u80fd\u7740\u5730\uff0c\u5929\u5927\u7684\u672c\u4e8b\u4e5f\u6ca1\u6709\u7528\uff0c\u5230\u4e86\u6b64\u65f6\uff0c\u4e5f\u53ea\u597d\u542c\u5929\u7531\u547d \u4e86\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u4e5f\u6ca1\u80fd\u8df3\u4e0a\u9646\u5730\uff0c\u4ed6\u843d\u4e0b\u4e4b\u65f6\uff0c\u811a\u5c16\u70b9\u7740\u4e00\u5757\u6d6e\u51b0\uff0c\u6070\u597d\u843d\u5728\u4e91\u743c\u7684\u8eab\u8fb9\uff0c \u4e91\u743c\u6293\u7740\u4e00\u5757\u6bd4\u8f83\u539a\u7684\u51b0\u5757\uff0c\u5c1a\u672a\u6c89\u6ca1\uff0c\u817e\u51fa\u624b\u6765\uff0c\u5411\u4ed6\u62cd\u4e86\u4e00\u638c\uff0c\u6587\u5ef7\u58c1\u78b0\u4e0a\u8fd9\u7a81\u5982 \u5176\u6765\u7684\u707e\u7978\uff0c\u65e9\u5df2\u5413\u5f97\u516d\u795e\u65e0\u4e3b\uff0c\u6839\u672c\u5c31\u4e0d\u77e5\u5e95\u4e0b\u6709\u4eba\uff0c\u800c\u4e14\u4ed6\u811a\u5c16\u70b9\u7740\u6d6e\u51b0\uff0c\u4e5f\u65e0\u6cd5 \u4f7f\u52b2\u8fd8\u624b\uff0c\u767b\u65f6\u88ab\u4e91\u743c\u8fd9\u4e00\u638c\u6253\u7ffb\uff0c\u5728\u6d2a\u6d41\u4e4b\u4e2d\u6ca1\u9876\uff01\u4e91\u743c\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6076\u8d3c\uff0c \u6211\u603b\u7b97\u62a5\u4e86\u4f60\u8fd9\u4e00\u638c\u4e4b\u4ec7\uff01\u201d\u7b11\u58f0\u672a\u7edd\uff0c\u4ed6\u6293\u7740\u7684\u90a3\u5757\u51b0\u5757\u5df2\u662f\u788e\u6210\u7247\u7247\u4e86\u3002\u4e91\u743c\u5931\u4e86 \u51ed\u501f\uff0c\u767b\u65f6\u4e5f\u88ab\u6d2a\u6d41\u5377\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u7a81\u5982\u5176\u6765\u7684\u707e\u7978\uff0c\u8c01\u4e5f\u6599\u60f3\u4e0d\u5230\u3002\u6c5f\u6d77\u5929\u521a\u4e0b\u4e86\u7075\u9e6b\u5cf0\uff0c\u4fbf\u542c\u5230\u90a3\u51b0\u5757\u70b8\u88c2\u7684\u58f0 \u97f3\uff0c\u614c\u5fd9\u8d76\u53bb\uff0c\u5230\u4e86\u51b0\u8c37\u5e95\u4e0b\uff0c\u53ea\u89c1\u4e00\u7247\u6c6a\u6d0b\uff0c\u5510\u52aa\u73e0\u7a46\u3001\u4e91\u743c\u5144\u59b9\u5168\u90fd\u4e0d\u89c1\u4e86\uff01\u6c5f\u6d77 \u5929\u8fde\u58f0\u547c\u558a\u3002 \u3000\u3000\u53ea\u542c\u5f97\u6025\u6d41\u51b2\u51fb\u5ca9\u77f3\u7684\u8f70\u8f70\u53d1\u53d1\u4e4b\u58f0\uff0c\u54ea\u91cc\u6709\u4eba\u56de\u7b54\uff1f\u6c5f\u6d77\u592d\u53d1\u72c2\u4f3c\u7684\uff0c\u6cbf\u7740\u51b0\u6cb3 \u4e00\u53e3\u6c14\u8dd1\u4e86\u4e03\u516b\u91cc\u8def\uff0c\u524d\u9762\u7684\u5ce1\u8c37\u8d8a\u6765\u8d8a\u7a84\uff0c\u51b0\u6cb3\u4e24\u8fb9\u5df2\u662f\u77f3\u58c1\uff0c\u6839\u672c\u5c31\u65e0\u8def\u53ef\u901a\u4e86\u3002 \u6c5f\u6d77\u5929\u6b32\u54ed\u65e0\u6cea\uff0c\u5446\u82e5\u6728\u9e21\uff0c\u501a\u7740\u77f3\u58c1\uff0c\u770b\u770b\u90a3\u6ed4\u6ed4\u7684\u6c5f\u6d41\uff0c\u53ea\u89c9\u773c\u775b\u53d1\u9ed1\uff0c\u5fc3\u91cc\u832b\u7136\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u6148\u548c\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6d77\u513f\uff0c\u79bb\u5f00\u8fd9\u513f\u5427\u3002\u201d\u534e\u5929 \u98ce\u5c06\u4ed6\u62c9\u5f00\u4e86\u51e0\u6b65\uff0c\u6c5f\u6d77\u5929\u8fd9\u624d\u53d1\u89c9\u6cb3\u6c34\u5df2\u6d78\u4e0a\u5ca9\u77f3\uff0c\u6e7f\u4e86\u4ed6\u7684\u53cc\u811a\u3002\u6c5f\u6d77\u592d\u7566\u7684\u4e00\u58f0 \u54ed\u4e86\u51fa\u6765\uff1a\u201c\u4ed6\u4eec\uff0c\u4ed6\u4eec\u90fd\u5df2\u88ab\u6025\u6d41\u5377\u53bb\u4e86\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u4e16\u4e8b\u832b\u832b\u96be\u9884\u6599\uff0c\u53d8\u751f\u4e0d\u6d4b\u5948\u4f55\u5929\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u56db\u5341\u516b\u56de\u3000\u540c\u547d\u76f8\u601c\u7591\u5e7b\u68a6\u3000\u5e7d\u60c5\u4e92\u6170\u7ed3\u77e5\u4ea4 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u56db\u5341\u516b\u56de\u3000\u540c\u547d\u76f8\u601c\u7591\u5e7b\u68a6\u3000\u5e7d\u60c5\u4e92\u6170\u7ed3\u77e5\u4ea4 \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u5929\u6709\u4e0d\u6d4b\u4e4b\u98ce\u4e91\uff0c\u4eba\u6709\u65e6\u5915\u4e4b\u7978\u798f\uff0c\u4e5f\u53ea\u80fd\u5e0c\u671b\u4ed6\u4eec\u9022\u51f6\u5316\u5409\uff0c\u9047\u96be \u6210\u7965\u4e86\uff0c\u4ed6\u4eec\u90fd\u6709\u4e00\u8eab\u6b66\u529f\uff0c\u867d\u7136\u4e0d\u4e60\u52a0\u6027\uff0c\u603b\u6bd4\u5e38\u4eba\u4f53\u8d28\u5f3a\u58ee\uff0c\u80fd\u591f\u5fcd\u53d7\u9965\u5bd2\uff0c\u672a\u5fc5 \u5c31\u4f1a\u4e27\u547d\u3002\u53ea\u8981\u88ab\u6c34\u6d41\u51b2\u5230\u5cb8\u8fb9\uff0c\u6216\u8005\u78b0\u5230\u8fc7\u8def\u7684\u4eba\uff0c\u90a3\u5c31\u6709\u6551\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u5fc3\u60f3\uff0c\u5728 \u8fd9\u8352\u5c71\u51b0\u8c37\u4e4b\u4e2d\uff0c\u54ea\u4f1a\u6709\u8fc7\u8def\u7684\u884c\u4eba\uff0c\u81f3\u4e8e\u5e0c\u671b\u6c34\u6d41\u628a\u4ed6\u4eec\u51b2\u5230\u5cb8\u8fb9\uff0c\u8fd9\u66f4\u5c5e\u6e3a\u832b\u4e4b\u4e8b\u3002 \u4f46\u4e8b\u5df2\u5982\u65af\uff0c\u6025\u4e5f\u6025\u4e0d\u6765\u4e86\u3002 \u3000\u3000\u534e\u5929\u98ce\u53e3\u91cc\u5b89\u6170\u6c5f\u6d77\u5929\uff0c\u5fc3\u4e2d\u4e5f\u662f\u96be\u8fc7\u4e4b\u6781\uff0c\u4e91\u743c\u5144\u59b9\u662f\u8ddf\u4ed6\u51fa\u6765\u7684\uff0c\u5018\u6709\u4e09\u957f\u4e24 \u77ed\uff0c\u4ed6\u5c06\u6765\u6709\u4f55\u9762\u76ee\u518d\u89c1\u4e91\u53ec\uff1f\u4e91\u743c\u5144\u59b9\u7684\u5185\u529f\u8fdc\u4e0d\u5982\u5510\u52aa\u73e0\u7a46\uff0c\u5f97\u6551\u7684\u5e0c\u671b\u6bd4\u5510\u52aa\u73e0 \u7a46\u8fd8\u8981\u6e3a\u832b\u3002 \u3000\u3000\u8bf4\u5230\u8d23\u4efb\u7684\u91cd\u5927\uff0c\u4e91\u53ec\u662f\u5c06\u513f\u5973\u4ed8\u6258\u7ed9\u4ed6\u7684\uff01\u5fc3\u60c5\u7684\u6c89\u91cd\uff0c\u534e\u65e0\u98ce\u6bd4\u6c5f\u6d77\u5929\u66f4\u751a\uff0c \u4e0d\u8fc7\u4ed6\u662f\u5386\u5c3d\u6ca7\u6851\uff0c\u9971\u7ecf\u5fe7\u60a3\u7684\u8001\u5e74\u4eba\uff0c\u9047\u4e0a\u4e8b\u60c5\uff0c\u5374\u8981\u6bd4\u6c5f\u6d77\u5929\u8fd9\u6837\u7684\u521d\u51fa\u8305\u5e90\u7684\u5c11 \u5e74\u9547\u5b9a\u591a\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u65b9\u5bf8\u5df2\u4e71\uff0c\u4e00\u5207\u90fd\u7531\u534e\u5929\u98ce\u4f5c\u4e3b\uff0c\u79bb\u5f00\u4e86\u9669\u5730\u4e4b\u540e\uff0c\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u96be\u9053\u54b1\u4eec \u5c31\u53ea\u80fd\u542c\u51ed\u5929\u547d\uff0c\u4e00\u70b9\u529e\u6cd5\u90fd\u6ca1\u6709\u4e86\u5417\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u4f60\u610f\u4e0b\u5982\u4f55\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a \u201c\u6c42\u4e49\u7236\u6307\u70b9\u3002\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u4f60\u5f97\u628a\u4fe1\u606f\u544a\u8bc9\u5510\u52aa\u73e0\u7a46\u7684\u5bb6\u4eba\u3002\u201d\u6c5f\u6d77\u5929\u5b9a\u4e86\u5b9a\u795e\uff0c \u60f3\u8d77\u4e86\u8c37\u4e2d\u83b2\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u73e0\u7a46\u672c\u662f\u4e3a\u4ed6\u7684\u59b9\u59b9\u800c\u6765\u7684\uff0c\u4e0d\u7ba1\u6211\u8fd9\u5e08\u5f1f\u662f\u751f\u662f\u6b7b\uff0c \u6211\u90fd\u5e94\u8be5\u5148\u628a\u4ed6\u7684\u59b9\u59b9\u627e\u5230\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u5206\u9053\u626c\u9573\uff0c\u8fd9\u91cc\u65e0\u8def\u53ef\u901a\uff0c\u6211\u8981\u7ffb\u8fc7\u8fd9\u5ea7\u5c71\u8ffd\u8e2a\u51b0\u6cb3\u7684\u6e90\u5934\uff0c \u4e07\u4e00\u53d1\u73b0\u4ed6\u4eec\uff0c\u6211\u53ef\u4ee5\u7acb\u65f6\u5c06\u4ed6\u4eec\u6551\u6cbb\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5e72\u7239\uff0c\u4f60\u4e00\u4e2a\u4eba\u7ffb\u5c71\u8fc7\u5cad\uff0c\u6211 \u4e0d\u5927\u653e\u5fc3\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u9053\uff1a\u201c\u6211\u5e73\u65e5\u91c7\u836f\uff0c\u5c71\u8def\u662f\u8d70\u60ef\u4e86\u7684\uff0c\u7ffb\u8fc7\u8fd9\u5ea7\u5c71\u4e0d\u6bd4\u521a\u624d\u4e0a\u7075\u9e6b\u5cf0\u90a3\u6837\u8981 \u5192\u5947\u9669\uff0c\u4f60\u5c3d\u53ef\u653e\u5fc3\u3002\u6d77\u513f\uff0c\u4f60\u4e00\u4e2a\u4eba\u5230\u6606\u5e03\u5170\u56fd\uff0c\u4eba\u5730\u751f\u758f\uff0c\u4ed6\u4eec\u4e24\u56fd\u53c8\u6b63\u6709\u7740\u7ea0\u7eb7\uff0c \u4f60\u8981\u627e\u7684\u662f\u9a6c\u8428\u513f\u56fd\u7684\u516c\u4e3b\uff0c\u5230\u4e86\u6606\u5e03\u5170\u56fd\uff0c\u53ea\u6015\u5468\u56f4\u90fd\u662f\u4f60\u7684\u654c\u4eba\uff0c\u4f60\u66f4\u5f97\u5c0f\u5fc3\u5728\u610f\uff01 \u5509\uff0c\u672c\u6765\u4f60\u5148\u56de\u5230\u9a6c\u8428\u513f\u56fd\uff0c\u628a\u8fd9\u6d88\u606f\u5e26\u7ed9\u73e0\u7a46\u7684\u5927\u54e5\u53f6\u51b2\u9704\u8981\u597d\u4e00\u4e9b\uff0c\u4f46\u4ed6\u59b9\u59b9\u7684\u4e8b \u60c5\u4e5f\u662f\u6025\u4e0d\u5bb9\u7f13\u7684\uff0c\u800c\u4e14\u6b64\u5730\u53c8\u5df2\u662f\u6606\u5e03\u5170\u56fd\u7684\u56fd\u5883\u4e86\uff0c\u6211\u4e5f\u4e0d\u60f3\u963b\u62e6\u4f60\u4e86\u3002\u603b\u4e4b\uff0c\u4f60 \u4e00\u5207\u90fd\u5f97\u81ea\u5df1\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u4e24\u4eba\u5f7c\u6b64\u5173\u5fc3\uff0c\u4e92\u76f8\u53ee\u5631\uff0c\u6700\u540e\u4e5f\u53ea\u80fd\u5206\u9053\u626c\u9573\uff0c\u534e\u5929\u98ce\u4e34\u5206\u624b\u65f6\u8bf4\u9053\uff1a\u201c\u6211\u8981\u662f \u627e\u5230\u4ed6\u4eec\u4f1a\u8d76\u5230\u6606\u5e03\u5170\u56fd\u7684\u4eac\u57ce\u4e0e\u4f60\u76f8\u4f1a\u3002\u4f60\u6b64\u53bb\u4e5f\u8bf7\u987a\u4fbf\u6253\u542c\u6253\u542c\u4f60\u78a7\u59b9\u7684\u6d88\u606f\u3002\u201d \u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\u52b3\u5631\u5490\uff0c\u6211\u81ea\u4f1a\u7559\u5fc3\u3002\u201d\u4f46\u4ed6\u5fc3\u91cc\u53ef\u611f\u5230\u4e00\u9635\u60ed\u6127\uff1a\u201c\u4e49\u7236\u5bf9\u6211\u5982\u6b64\u5173 \u6000\uff0c\u5b9e\u5728\u6bd4\u6211\u5bf9\u78a7\u59b9\u7684\u5173\u6000\u8fd8\u591a\u5f97\u591a\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6005\u5e10\u60c6\u60d8\u3002\u72ec\u81ea\u524d\u5f80\u6606\u5e03\u5170\u56fd\uff0c\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u4e14\u8bf4\u5510\u52aa\u73e0\u7a46\u5728\u6d2a\u6d41\u4e4b\u4e2d\u6323\u624e\uff0c\u597d\u4e0d\u5bb9\u6613\u7a9c\u51fa\u6c34\u9762\uff1a\u5438\u4e86\u53e3\u6c14\uff0c\u4e00\u4e2a\u6d6a\u5934\u6253\u6765\uff0c\u53c8 \u906d\u6ca1\u9876\u3002\u5e78\u4e8f\u4ed6\u5185\u529f\u6df1\u539a\uff0c\u6362\u4e86\u53e3\u6c14\uff0c\u4fbf\u53ef\u4ee5\u652f\u6301\u4e0d\u5c11\u65f6\u5019\uff0c\u5728\u6fc0\u6d41\u6025\u6e4d\u4e4b\u4e2d\uff0c\u4ed6\u59cb\u7ec8 \u7d27\u7d27\u5730\u62b1\u7740\u4e91\u58c1\uff0c\u4e0d\u6562\u653e\u677e\uff0c\u6bcf\u6b21\u7a9c\u4e0a\u6c34\u9762\u6362\u6c14\u4e4b\u65f6\uff0c\u4e5f\u5e26\u7740\u4e91\u58c1\u9732\u51fa\u5934\u6765\u3002 \u3000\u3000\u6c34\u5e95\u6bd4\u6c34\u9762\u6696\u548c\u5f97\u591a\uff0c\u56e0\u4e3a\u6df1\u6c34\u6700\u5bd2\u51b7\u7684\u65f6\u5019\u4e5f\u7ecf\u5e38\u4fdd\u6301\u7740\u6444\u6c0f\u56db\u5ea6\uff0c\u6240\u4ee5\u53ea\u6709\u4e0a \u5c42\u7ed3\u51b0\uff0c\u4e0b\u9762\u662f\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u7ed3\u51b0\u7684\u3002\u56e0\u6b64\u4e91\u58c1\u867d\u7136\u529f\u529b\u8f83\u5f31\uff0c\u8fd8\u4e0d\u81f3\u4e8e\u51bb\u50f5\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u4e0d\u61c2\u6c34\u6027\uff0c\u5728\u6fc0\u6d41\u6025\u6e4d\u4e4b\u4e2d\uff0c\u5fc3\u91cc\u53d1\u614c\uff0c\u529f\u529b\u867d\u9ad8\uff0c\u6e10\u6e10\u4e5f\u611f\u5230\u7b4b\u75b2\u529b\u7aed\uff0c \u8179\u4e2d\u53c8\u9965\u53c8\u6e34\uff0c\u53cc\u773c\u4e5f\u6e10\u6e10\u53d1\u9ed1\u4e86\u3002 \u3000\u3000\u4e91\u58c1\u5728\u4ed6\u6000\u62b1\u4e4b\u4e2d\uff0c\u6700\u521d\u8fd8\u4f1a\u6323\u624e\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u7adf\u662f\u5168\u65e0\u52a8\u5f39\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5fc3\u5934\u53d1\u51b7\uff0c\u201c\u96be\u9053\u6211\u4eec\u4e8c\u4eba\u7adf\u7136\u547d\u4e27\u4e8e\u65af\uff1f\u6211\u6b7b\u4e86\u4e0d\u6253\u7d27\u3002\u5374\u7d2f\u4e86\u4e91\u59d1\u5a18 \u4e86\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u89c9\u6c34\u6d41\u6e10\u7f13\uff0c\u539f\u6765\u5df2\u6d41\u51fa\u4e86\u4e24\u5cf0\u5939\u5cd9\u7684\u7a84\u8c37\uff0c\u6cb3\u9762\u5bbd\u5e7f\uff0c\u5730\u52bf\u5e73\u5766\uff0c \u6c34\u6d41\u5f53\u7136\u5c31\u6ca1\u6709\u90a3\u4e48\u6e4d\u6025\u4e86\u3002 \u3000\u3000\u6df1\u6c34\u4e2d\u6709\u6761\u602a\u9c7c\u6e38\u6765\uff0c\u53d1\u51fa\u95ea\u95ea\u7684\u8424\u5149\uff0c\u539f\u6765\u662f\u51b0\u6cb3\u4e2d\u72ec\u6709\u7684\u4e00\u79cd\u9c7c\u7c7b\uff0c\u540d\u4e3a\u201c\u73e0 \u9c7c\u201d\uff0c\u8eab\u957f\u4e0d\u53ca\u4e00\u5c3a\uff0c\u5374\u6709\u4e8c\u767e\u7c92\u5de6\u53f3\u80fd\u653e\u5149\u7684\u9aa8\u73e0\uff0c\u5c31\u5982\u904d\u5e03\u660e\u706f\u4e00\u822c\u3002\u5510\u52aa\u73e0\u7a46\u6b63 \u611f\u9965\u6e34\uff0c\u4f46\u5374\u4e0d\u6562\u751f\u5403\u8fd9\u79cd\u602a\u9c7c\uff0c\u4ed6\u501f\u7740\u201c\u73e0\u9c7c\u201d\u6240\u53d1\u7684\u5149\uff0c\u6293\u5230\u4e86\u4e00\u6761\u6cb3\u9cd7\uff0c\u6323\u624e\u7740 \u7a9c\u51fa\u6c34\u9762\uff0c\u5ffd\u89c1\u9762\u524d\u6709\u4e00\u5757\u5de8\u5927\u7684\u6d6e\u51b0\uff0c\u518d\u5f80\u524d\u9762\uff0c\u6d6e\u51b0\u66f4\u591a\uff0c\u4e00\u5757\u63a5\u7740\u4e00\u5757\uff0c\u5c31\u4f3c\u6c34 \u9762\u4e0a\u642d\u8d77\u4e86\u4e00\u6761\u6d6e\u6865\uff0c\u4e00\u76f4\u8fde\u5230\u9646\u5730\u3002\u539f\u6765\u51b0\u6cb3\u5230\u4e86\u6b64\u5904\uff0c\u5df2\u8fdc\u79bb\u4e86\u90a3\u5ea7\u706b\u5c71\uff0c\u7a7a\u6c14\u5bd2 \u51b7\u3002\u6d6e\u51b0\u5df2\u662f\u751a\u4e3a\u575a\u539a\uff0c\u518d\u5728\u524d\u9762\uff0c\u8fde\u6cb3\u9762\u90fd\u7ed3\u51b0\u4e86\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5fc3\u4e2d\u5927\u559c\uff0c\u7cbe\u795e\u4e00\u632f\uff0c\u54ac\u7740\u6cb3\u9cd7\uff0c\u817e\u51fa\u4e8c\u53ea\u624b\u6765\uff0c\u6293\u7740\u90a3\u5757\u5f62\u4f3c\u78e8\u76d8\u7684\u575a \u51b0\uff0c\u731b\u5730\u4e00\u4e2a\u7ffb\u8eab\uff0c\u5c06\u4e91\u58c1\u5e26\u4e0a\u4e86\u6d6e\u51b0\uff0c\u5f53\u4e0b\u5438\u4e86\u53e3\u6c14\uff0c\u65bd\u5c55\u8f7b\u529f\uff0c\u597d\u5728\u6cb3\u9762\u6d6e\u51b0\u904d\u5e03\uff0c \u4e24\u5757\u6d6e\u51b0\u4e4b\u95f4\uff0c\u6700\u591a\u7684\u8ddd\u79bb\u4e5f\u4e0d\u8fc7\u4e00\u4e08\uff0c\u5510\u52aa\u73e0\u7a46\u867d\u7136\u7cbe\u75b2\u529b\u7aed\uff0c\u817e\u8eab\u7eb5\u8dc3\uff0c\u8fd8\u52c9\u5f3a\u53ef \u4ee5\u5bf9\u4ed8\u8fc7\u53bb\uff0c\u4e0d\u6d88\u591a\u4e45\uff0c\u4ed6\u62b1\u7740\u4e91\u58c1\uff0c\u5df1\u662f\u5b89\u5168\u5230\u4e86\u9646\u5730\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5c06\u4e91\u58c1\u653e\u4e86\u4e0b\u6765\uff0c\u5524\u4e86\u4e24\u58f0\u201c\u58c1\u59b9\u201d\uff0c\u4e91\u58c1\u53cc\u773c\u7d27\u95ed\uff0c\u9762\u8272\u9752\u7d2b\uff0c\u4e1d\u6beb\u4e5f \u6ca1\u53cd\u5e94\uff0c\u53ea\u89c1\u5979\u5c0f\u8179\u9f13\u80c0\uff0c\u663e\u7136\u662f\u88ab\u704c\u8fdb\u4e86\u8bb8\u591a\u51b0\u6c34\u4e86\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u987e\u4e0d\u5f97\u75b2\u5026\uff0c\u66f4\u987e\u4e0d\u5f97\u7537\u5973\u4e4b\u5acc\uff0c\u5f53\u4e0b\u5c31\u7ed9\u4e91\u58c1\u63a8\u62ff\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4e91\u58c1\u5589 \u5934\u54af\u54af\u4f5c\u54cd\uff0c\u5510\u52aa\u73e0\u7a46\u63e1\u770b\u5979\u53cc\u811a\uff0c\u5012\u63d0\u8d77\u6765\uff0c\u4e91\u58c1\u5410\u51fa\u4e86\u8179\u4e2d\u79ef\u6c34\uff0c\u53cc\u773c\u4e5f\u624d\u6162\u6162\u5f20 \u5f00\uff0c\u4f46\u4ecd\u662f\u6c14\u606f\u5944\u5944\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u3002\u5510\u52aa\u73e0\u7a46\u4e00\u638c\u8d34\u7740\u5979\u7684\u80cc\u5fc3\uff0c\u4ee5\u672c\u8eab\u771f\u6c14\u8f93\u9001\u8fdb\u53bb\uff0c \u52a9\u5979\u8840\u8109\u6d41\u901a\uff0c\u53c8\u8fc7\u4e86\u534a\u54cd\uff0c\u4e91\u58c1\u8fd9\u624d\u201c\u5624\u201d\u7136\u4e00\u58f0\uff0c\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u67d4\u58f0\u5524\u9053\uff1a\u201c\u58c1\u59b9\uff0c\u9192\u6765\uff0c\u9192\u6765\uff01\u201d\u4e91\u58c1\u5f20\u5f00\u4e86\u773c\u775b\uff0c\u832b\u7136\u95ee\u9053\uff1a\u201c\u6211\u8fd9 \u662f\u505a\u68a6\u4e48\uff1f\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a \u3000\u3000\u201c\u662f\u50cf\u4e00\u573a\u6076\u68a6\uff0c\u597d\u5728\u5df2\u7ecf\u8fc7\u53bb\u4e86\u3002\u201d\u4e91\u58c1\u6e10\u6e10\u6062\u590d\u4e86\u8bb0\u5fc6\uff0c\u8bf4\u9053\uff1a\u201c\u554a\uff0c\u6211\u8bb0\u8d77 \u6765\u4e86\uff0c\u6211\u88ab\u90a3\u59d3\u6587\u7684\u6076\u8d3c\u6240\u64d2\uff0c\u5730\u9762\u7a81\u7136\u88c2\u5f00\uff0c\u6d2a\u6c34\u6d8c\u51fa\u2026\u2026\u54e6\uff0c\u7a46\u54e5\uff0c\u6211\u660e\u767d\u4e86\uff0c\u662f \u4f60\u5c06\u6211\u6551\u8d77\u6765\u7684\uff01\u4f60\u628a\u90a3\u6076\u8d3c\u6253\u6b7b\u4e86\u4e48\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4e0d\u7528\u6211\u52a8\u624b\u9664\u4ed6\uff0c\u4ed6\u5df2\u7ecf\u906d \u5230\u62a5\u5e94\u4e86\uff01\u6211\u770b\u7740\u4ed6\u88ab\u4f60\u7684\u54e5\u54e5\u4e00\u638c\u6253\u7ffb\uff0c\u968f\u5373\u6c89\u6ca1\u5728\u6f29\u6da1\u4e4b\u4e2d\u4e86\u3002\u201d \u3000\u3000\u4e91\u58c1\u542c\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u4e91\u58c1\u9053\uff1a\u201c\u4f60 \u53ef\u6709\u770b\u5230\u6211\u7684\u54e5\u54e5\u4e48\uff1f\u5728\u54b1\u4eec\u88ab\u6d2a\u6d41\u5377\u4e86\u4e4b\u540e\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff0c\u201c\u54b1\u4eec\u5df2\u7136\u8131\u9661\uff0c\u6211\u60f3 \u4f60\u54e5\u54e5\u5927\u7ea6\u4e5f\u4f1a\u5b89\u7136\u65e0\u4e8b\u7684\u3002\u201d\u4e91\u58c1\u5fe7\u5fc3\u5fe1\u5fe1\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u54e5\u54e5\u4e0d\u4f1a\u6e38\u6c34\u7684\uff0c\u9664\u975e\u6709\u4eba \u6551\u4ed6\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u662f\u4e0d\u4f1a\u6e38\u6c34\u5417\uff1f\u4f46\u54b1\u4eec\u6bd5\u7adf\u8fd8\u662f\u4e0a\u4e86\u5cb8\u4e86\u3002\u201d\u4e91\u58c1\u5fc3\u60c5\u7a0d \u7a0d\u653e\u677e\uff0c\u8bf4\u9053\uff1a\u201c\u4f46\u613f\u4ed6\u4e5f\u50cf\u54b1\u4eec\u4e00\u822c\u9022\u51f6\u5316\u5409\u3002\u201d\u5979\u795e\u667a\u5c1a\u672a\u5b8c\u5168\u6e05\u9192\uff0c\u4e00\u65f6\u95f4\u8fd8\u672a \u60f3\u5230\u5510\u52aa\u73e0\u7a46\u4e4b\u6240\u4ee5\u80fd\u591f\u8131\u9669\uff0c\u4e43\u662f\u7531\u4e8e\u4ed6\u672c\u8eab\u529f\u529b\u6df1\u6e5b\u7684\u7f18\u6545\uff0c\u800c\u5510\u52aa\u73e0\u7a46\u4e3e\u81ea\u5df1\u4e3a \u4f8b\uff0c\u4e5f\u662f\u6709\u610f\u8981\u5982\u6b64\u5b89\u6170\u5979\u7684\u3002 \u3000\u3000\u4e91\u58c1\u9192\u6765\u4e4b\u540e\uff0c\u5510\u52aa\u73e0\u7a46\u5f53\u7136\u4e0d\u597d\u610f\u601d\u518d\u62b1\u7740\u5979\u4e86\uff0c\u5979\u79bb\u5f00\u4e86\u5510\u52aa\u73e0\u7a46\u7684\u6000\u62b1\uff0c\u53c8 \u51b7\u53c8\u997f\uff0c\u8eab\u4f53\u6e10\u6e10\u53d1\u6296\uff0c\u5510\u52aa\u73e0\u7a46\u9053\uff1a \u3000\u3000\u201c\u6211\u6349\u5230\u4e00\u6761\u6cb3\u9cd7\uff0c\u8fd9\u91cc\u65e0\u6cd5\u4e3e\u706b\uff0c\u4f60\u5fcd\u7740\u70b9\u513f\uff0c\u628a\u5b83\u751f\u5403\u4e86\u5427\u3002\u201d\u4e91\u58c1\u98a4\u58f0\u8bf4\u9053\uff1a \u201c\u6211\uff0c\u6211\u4e0d\u6562\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u4f60\u95ed\u4e86\u773c\u775b\uff0c\u634f\u7740\u9f3b\u5b50\u3002\u201d\u4ed6\u5c06\u90a3\u6761\u6cb3\u9cd7\u4e00\u7247\u7247\u6495\u5f00\uff0c\u9001\u8fdb\u4e91\u58c1 \u53e3\u4e2d\uff0c\u8ba9\u5979\u6162\u6162\u5480\u56bc\u3002\u6cb3\u9cd7\u529f\u80fd\u8865\u6c14\u884c\u8840\uff0c\u4e91\u58c1\u867d\u7136\u89c9\u5f97\u8165\u5473\u96be\u53d7\uff0c\u4f46\u997f\u4e0d\u62e9\u98df\uff0c\u4e5f\u53ea \u597d\u95ed\u7740\u773c\u775b\uff0c\u628a\u5b83\u751f\u98df\u4e86\uff0c\u8179\u4e2d\u4e00\u9971\uff0c\u7cbe\u529b\u6e10\u6e10\u6062\u590d\uff0c\u4fbf\u89c9\u5f97\u6696\u548c\u4e86\u8bb8\u591a\uff0c\u53ea\u662f\u8863\u88f3\u5168 \u6e7f\uff0c\u602a\u4e0d\u597d\u53d7\u3002 \u3000\u3000\u4e91\u58c1\u5f20\u5f00\u773c\u775b\uff0c\u89c1\u90a3\u6761\u6cb3\u9cd7\u5df2\u53ea\u5269\u4e0b\u4e00\u5806\u9c7c\u9aa8\uff0c\u6b49\u7136\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u600e\u4e48\u4e0d\u5403\u4e00\u70b9\u513f\uff0c\u5168\u90fd\u7ed9\u6211\u4e86\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6211\u4e0d\u997f\u3002\u201d\u5176\u5b9e\u4ed6\u4e5f\u611f\u5230\u8179 \u4e2d\u9965\u997f\uff0c\u53ea\u662f\u4ed6\u5185\u529f\u6df1\u6e5b\uff0c\u5374\u8fd8\u53ef\u4ee5\u52c9\u5f3a\u652f\u6301\u5f97\u4f4f\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7559\u5fc3\u89c2\u5bdf\u5468\u56f4\u7684\u73af\u5883\uff0c\u53ea\u89c1\u662f\u4e00\u7247\u767d\u832b\u832b\u7684\u51b0\u5929\u96ea\u5730\uff0c\u9664\u4e86\u51b0\u96ea\u548c\u77f3\u5934\u4e4b \u5916\uff0c\u4ec0\u4e48\u90fd\u6ca1\u6709\u3002\u5510\u52aa\u73e0\u7a46\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u53ef\u5230\u54ea\u91cc\u53bb\u627e\u98df\u7269\u5145\u9965\uff1f\u8fd8\u6709\u58c1\u59b9\u7684\u8863 \u88f3\u4e5f\u8981\u66ff\u6362\u3002\u201d\u4ed6\u77e5\u9053\u6cb3\u4e2d\u6709\u9c7c\uff0c\u4f46\u4ed6\u521a\u521a\u8131\u9669\uff0c\u601d\u4e4b\u72b9\u6709\u4f59\u60b8\uff0c\u4e14\u53c8\u5df2\u662f\u529b\u7aed\u7cbe\u75b2\uff0c \u600e\u6562\u518d\u8df3\u8fdb\u51b0\u6cb3\u5192\u9669\uff1f \u3000\u3000\u6b63\u5728\u5fc3\u91cc\u53d1\u614c\u7684\u65f6\u5019\uff0c\u5ffd\u542c\u5f97\u4e00\u7f15\u7bab\u58f0\uff0c\u9690\u9690\u7ea6\u7ea6\u7684\u4e0d\u77e5\u4ece\u4ec0\u4e48\u5730\u65b9\u4f20\u6765\uff0c\u97f3\u7ec6\u800c \u6e05\uff0c\u6291\u626c\u987f\u632b\uff0c\u5341\u5206\u52a8\u542c\u3002\u4e91\u58c1\u7cbe\u795e\u4e00\u723d\u3002\u8bf4\u9053\uff1a\u201c\u4f60\u542c\uff0c\u6709\u4eba\u5439\u7bab\uff0c\u8bf4\u4e0d\u5b9a\u8fd9\u91cc\u6709\u4eba \u5bb6\u5462\uff01\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5374\u4e0d\u7531\u5f97\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u7bab\u58f0\u8fdc\u8fdc\u4f20\u6765\uff0c\u5374\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u663e\u7136\u8fd9\u4eba\u7684\u5185\u529f \u975e\u6bd4\u5bfb\u5e38\u3002\u8fd9\u4e5f\u8fd8\u7f62\u4e86\uff0c\u66f4\u4ee4\u5f97\u5510\u52aa\u73e0\u7a46\u60ca\u5947\u7684\u662f\uff0c\u90a3\u4eba\u6240\u5439\u594f\u7684\u66f2\u5b50\uff0c\u6b63\u662f\u5510\u52aa\u73e0\u7a46 \u5728\u7075\u9e6b\u5cf0\u4e0a\u6240\u66fe\u7ecf\u542c\u8fc7\u7684\uff0c\u90a3\u767d\u88d8\u5c11\u5e74\u4e34\u8d70\u65f6\u6240\u5439\u8fc7\u7684\u90a3\u652f\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u795e\u79d8\u5c11\u5e74\u4e5f\u5728\u8fd9\u513f\uff0c\u5374\u4e0d\u77e5\u534e\u59d1\u5a18\u662f\u5426\u4e5f\u7ed9\u4ed6\u5e26\u5230 \u6b64\u95f4\u6765\u4e86\uff1f\u201d\u968f\u53c8\u60f3\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u4e0d\u77e5\u662f\u53cb\u662f\u654c\uff1f\u867d\u7136\u4ece\u79cd\u79cd\u8ff9\u8c61\u770b\u6765\uff0c\u4ed6\u5bf9\u534e\u59d1\u5a18\u662f \u5c3d\u5fc3\u770b\u62a4\uff0c\u5e94\u8be5\u662f\u4e2a\u597d\u4eba\uff0c\u4f46\u7a76\u7adf\u6c5f\u672a\u6478\u6e05\u4ed6\u7684\u5e95\u7ec6\uff0c\u4eba\u5fc3\u96be\u6d4b\uff0c\u603b\u8fd8\u662f\u5c0f\u5fc3\u4e00\u70b9\u4e3a\u5999\u3002 \u4f55\u51b5\u4ed6\u53c8\u662f\u548c\u5929\u9b54\u6559\u7684\u526f\u6559\u4e3b\u540c\u5728\u4e00\u8d77\uff0c\u6211\u82e5\u8d38\u8d38\u7136\u53bb\u6c42\u4ed6\u4eec\u76f8\u52a9\uff0c\u5018\u82e5\u4ed6\u4eec\u5ffd\u7136\u7ffb\u8138\uff0c \u6c47\u5e08\u5144\u4e0d\u5728\u8fd9\u513f\uff0c\u6211\u7684\u6c14\u529b\u53c8\u672a\u66fe\u6062\u590d\uff0c\u51b3\u8ba1\u4e0d\u662f\u4ed6\u4eec\u5bf9\u624b\uff0c\u6211\u5403\u4e8f\u4e0d\u6253\u7d27\uff0c\u53ea\u6015\u8fd8\u8981 \u8fde\u7d2f\u4e86\u4e91\u59d1\u5a18\u3002\u201d \u3000\u3000\u4e91\u58c1\u9053\uff1a\u201c\u7a46\u54e5\uff0c\u4f60\u5728\u60f3\u4e9b\u4ec0\u4e48\uff1f\u8fd9\u91cc\u82e5\u6709\u4eba\u5bb6\uff0c\u90a3\u54b1\u4eec\u5c31\u662f\u7edd\u5904\u9022\u751f\u4e86\uff0c\u4f60\u8fd8\u4e0d \u9ad8\u5174\u5417\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u58c1\u59b9\uff0c\u90a3\u8fb9\u6709\u4e2a\u5ca9\u6d1e\uff0c\u4f60\u8eb2\u8fdb\u6d1e\u91cc\uff0c\u7b49\u6211\u56de\u6765\u3002\u6211\u8981\u770b\u6e05\u695a \u662f\u751a\u4eba\u5bb6\uff0c\u624d\u653e\u5fc3\u8ba9\u4f60\u4e5f\u53bb\u3002\u201d\u4e91\u58c1\u7b11\u9053\uff0c\u201c\u6211\u7684\u8863\u88f3\u91cc\u5916\u5168\u90fd\u6e7f\u4e86\uff0c\u8fd9\u4e2a\u6837\u5b50\u600e\u597d\u53bb \u89c1\u751f\u4eba\uff1f\u4f60\u53eb\u6211\u53bb\u6211\u90fd\u4e0d\u53bb\u5462\u3002\u4f60\u81f3\u7d27\u8981\u7ed9\u6211\u501f\u4e00\u5957\u8863\u88f3\u56de\u6765\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u966a\u4e91\u58c1\u8fdb\u5165\u5ca9\u6d1e\uff0c\u6d1e\u91cc\u5012\u5f88\u6d01\u51c0\uff0c\u53ea\u662f\u98ce\u4ece\u6d1e\u53e3\u5439\u4eba\uff0c\u98ce\u52bf\u5f88\u5927\uff0c\u5510\u52aa\u73e0 \u7a46\u9053\uff1a\u201c\u58c1\u59b9\uff0c\u4f60\u89c9\u5f97\u597d\u4e9b\u5417\uff01\u51b7\u4e0d\u51b7\uff1f\u201d\u4e91\u58c1\u76d8\u819d\u6253\u5750\uff0c\u7b11\u9053\uff1a\u201c\u6211\u5403\u4e86\u90a3\u6761\u6cb3\u9cd7\uff0c \u6696\u548c\u591a\u4e86\uff0c\u6211\u6b63\u60f3\u5439\u5e72\u6211\u7684\u8863\u88f3\u3002\u7a46\u54e5\uff0c\u4f60\u6ca1\u6709\u5403\u8fc7\u4e1c\u897f\uff1a\u6211\u5012\u662f\u62c5\u5fc3\u4f60\u8dd1\u4e0d\u52a8\u5462\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u4f60\u987e\u8651\u5f97\u662f\uff0c\u90a3\u4e48\u6211\u4e5f\u5148\u7ec3\u4e00\u4f1a\u529f\u5427\u3002\u514d\u5f97\u534a\u8def\u6655\u5012\uff0c\u4f60\u5728\u8fd9\u91cc \u7a7a\u7b49\u3002\u53ef\u5c31\u8981\u5e72\u7740\u6025\u4e86\u3002\u201d\u5176\u5b9e\u5510\u52aa\u73e0\u7a46\u529f\u529b\u6df1\u539a\uff0c\u5373\u7b97\u7edd\u98df\u51e0\u5929\uff0c\u4ed6\u4e5f\u8fd8\u7981\u53d7\u5f97\u8d77\uff0c \u201c\u4e0d\u8fc7\uff0c\u4ed6\u8981\u63d0\u9632\u610f\u5916\uff0c\u5374\u4e0d\u80fd\u4e0d\u5148\u6062\u590d\u51e0\u5206\u529f\u529b\u3002 \u3000\u3000\u91d1\u4e16\u9057\u4f20\u7ed9\u4ed6\u7684\u5185\u529f\u751a\u662f\u795e\u5947\u3001\u5c24\u5176\u5728\u4ed6\u670d\u98df\u4e86\u5929\u5fc3\u77f3\u4e4b\u540e\uff0c\u5929\u5fc3\u77f3\u662f\u70ed\u6027\u6700\u70c8\u7684 \u836f\u7269\uff0c\u4e0e\u4ed6\u672c\u8eab\u7684\u5185\u529f\u7ed3\u5408\uff0c\u65e9\u5df2\u7ec3\u6210\u4e86\u4e00\u80a1\u7eaf\u9633\u4e4b\u6c14\u3002\u4ed6\u76d8\u819d\u800c\u5750\uff0c\u9ed8\u8fd0\u7384\u529f\u3001\u4e0d\u6d88 \u591a\u4e45\uff0c\u5934\u9876\u4e0a\u5c31\u4f3c\u6709\u4e00\u4e2a\u84b8\u7b3c\u4e00\u822c\uff0c\u6563\u53d1\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\u3002\u8863\u88f3\u6e10\u6e10\u5e72\u4e86\uff0c\u867d\u7136\u8fd8\u662f\u611f \u89c9\u9965\u997f\uff0c\u4f46\u5df2\u8fdc\u8fdc\u4e0d\u4f3c\u521a\u624d\u7684\u96be\u53d7\uff0c\u529f\u529b\u4e5f\u6062\u590d\u4e86\u51e0\u5206\u3002\u4e91\u58c1\u5728\u65c1\uff0c\u597d\u751f\u7fa1\u6155\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6d3b\u52a8\u6d3b\u52a8\u4e86\u624b\u8db3\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5728\u8fd9\u91cc\u7b49\u6211\uff0c\u6211\u53bb\u53bb\u5c31\u6765\u3002\u201d\u8fd9\u65f6\u5df2\u662f \u5348\u591c\u65f6\u5206\uff0c\u767d\u96ea\u7691\u7691\uff0c\u6620\u7740\u6708\u5149\uff0c\u5468\u56f4\u666f\u7269\uff0c\u4f3c\u662f\u8499\u4e86\u4e00\u5c42\u8584\u96fe\u8f7b\u7eb1\uff0c\u867d\u7136\u9690\u7ea6\u6726\u80e7\uff0c \u5374\u4e5f\u53ef\u4ee5\u770b\u89c1\u3002\u5510\u52aa\u73e0\u7a46\u5728\u96ea\u5730\u4e0a\u65bd\u5c55\u8f7b\u529f\uff0c\u8fc7\u4e86\u4e00\u4e2a\u5c71\u62d7\uff0c\u5730\u6c14\u4f3c\u4e4e\u6696\u548c\u8bb8\u591a\uff0c\u524d\u9762 \u6709\u5341\u51e0\u682a\u6811\u6728\uff0c\u518d\u8fc7\u4e00\u4f1a\uff0c\u6811\u6728\u540e\u9762\u7684\u623f\u5c4b\u4e5f\u770b\u5f97\u6e05\u6e05\u695a\u695a\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u6392\u623f\u5c4b\u501a\u5c71\u4fee\u5efa\uff0c\u6c14\u52bf\u4e0d\u51e1\uff0c\u5c4b\u9876\u6240\u6109\u7684\u5168\u662f\u7409\u7483\u74e6\uff0c\u5728\u660e\u6708\u79ef\u96ea\u4e4b\u4e0b\uff0c\u66f4\u663e \u5f97\u91d1\u78a7\u8f89\u714c\u3002\u5510\u52aa\u73e0\u7a46\u6697\u6697\u7eb3\u7f55\uff0c\u5bfb\u601d\uff1a \u3000\u3000\u201c\u8fd9\u5c11\u5e74\u4e0d\u77e5\u4ec0\u4e48\u6765\u5386\uff0c\u771f\u662f\u53e4\u602a\u5f97\u7d27\uff01\u5728\u7075\u9e6b\u5cf0\u4e0a\u4ed6\u72ec\u4f4f\u51b0\u5c4b\uff0c\u5728\u8fd9\u91cc\u5374\u53c8\u6709\u738b \u5bab\u4e00\u822c\u7684\u5c4b\u5b87\u3002\u6211\u4e14\u5148\u6478\u4e00\u6478\u4ed6\u7684\u5e95\u7ec6\u518d\u8bf4\u3002\u201d \u3000\u3000\u8427\u58f0\u518d\u8d77\uff0c\u5510\u52aa\u73e0\u7a46\u867d\u7136\u4e0d\u751a\u901a\u6653\u97f3\u5f8b\uff0c\u4e5f\u542c\u5f97\u51fa\u90a3\u662f\u4e00\u652f\u54c0\u6028\u7f20\u7ef5\u7684\u66f2\u5b50\uff0c\u5fc3\u60f3\uff1a \u201c\u8fd9\u5c11\u5e74\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff1f\u83ab\u975e\u4ed6\u662f\u4e3a\u534e\u59d1\u5a18\u8d77\u4e86\u76f8\u601d\uff1f\u201d\u8fd9\u7bab\u58f0\u7b49\u4e8e\u7ed9\u4ed6\u5f15\u8def\uff0c\u5f53\u4e0b\u5510\u52aa \u73e0\u7a46\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u98de\u8fdb\u4e86\u56f4\u5899\uff0c\u5f88\u5bb9\u6613\u7684\u5c31\u627e\u5230\u4e86\u5439\u7bab\u4eba\u7684\u6240\u5728\u3002 \u3000\u3000\u90a3\u5ea7\u623f\u5b50\u7684\u540e\u9762\u6709\u4e00\u68f5\u5927\u6811\uff0c\u867d\u7136\u679d\u53f6\u5149\u79c3\u79c3\u7684\uff0c\u4f46\u4e5f\u8fd8\u53ef\u4ee5\u85cf\u8eab\u3002\u5510\u52aa\u73e0\u7a46\u8eb2\u5728 \u6811\u4e0a\uff0c\u5f80\u4e0b\u4e00\u77a7\uff0c\u4e0d\u89c9\u6115\u7136\uff01 \u3000\u3000\u53ea\u89c1\u5439\u7bab\u7684\u7adf\u7136\u662f\u4e2a\u5973\u5b50\uff0c\u8fd9\u65f6\u5979\u6b63\u5728\u653e\u4e0b\u7389\u7bab\uff0c\u66fc\u58f0\u541f\u9053\uff1a\u201c\u975e\u5173\u501f\u522b\u4e3a\u601c\u624d\uff0c \u51e0\u5ea6\u7ea2\u7b3a\u624b\u81ea\u88c1\uff0c\u6e56\u6d77\u6709\u5fc3\u968f\u9896\u58eb\uff0c\u98ce\u60c5\u8fd1\u65e5\u8feb\u65b9\u56de\uff1a\u65e0\u591a\u63a9\u5e54\u7559\u9999\u4f4f\uff0c\u4f9d\u65e7\u7aa5\u4eba\u6709\u71d5 \u6765\uff0c\u81ea\u53e4\u540c\u5fc3\u7ec8\u4e0d\u61c8\uff0c\u7f57\u6d6e\u51a2\u6811\u81f3\u4eca\u54c0\u3002\u201d\u8fd9\u662f\u5f53\u4ee3\u8bd7\u4eba\u9ec4\u4ef2\u5219\u7684\u4e00\u9996\u540d\u8bd7\uff0c\u5510\u52aa\u73e0\u7a46 \u4e60\u8fc7\u6c49\u5b66\uff0c\u867d\u4e0d\u751a\u7cbe\uff0c\u4e5f\u7ea6\u7565\u542c\u5f97\u61c2\u90a3\u8bd7\u4e2d\u4e4b\u610f\uff0c\u8bd7\u4e2d\u8bf4\u7684\u662f\u4e00\u4e2a\u75f4\u60c5\u5973\u5b50\uff0c\u4e00\u5fc3\u8981\u8ffd \u968f\u4ed6\u7684\u5fc3\u4e0a\u4eba\uff1b\u4f46\u5374\u7ec8\u4e8e\u4e0d\u80fd\u4e0d\u5206\u79bb\u3002\u8bd7\u4eba\u6000\u5ff5\u4ed6\u7684\u7ea2\u989c\u77e5\u5df1\uff0c\u76fc\u671b\u5979\u65e7\u71d5\u5f52\u6765\uff0c\u53ef\u662f \u5374\u53ea\u6015\u672a\u5fc5\u80fd\u5982\u6240\u613f\u4e86\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5fc3\u5934\u4e00\u9707\uff0c\u770b\u4e86\u53c8\u770b\uff0c\u5c3d\u7ba1\u8fd9\u5973\u5b50\u6539\u4e86\u670d\u88c5\uff0c\u72d9\u4ece\u58f0\u97f3\u7b11\u8c8c\uff0c\u5374\u4ecd\u7136\u53ef\u4ee5 \u8ba4\u5f97\u51fa\u6765\uff0c\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u90a3\u7075\u9e6b\u5cf0\u4e0a\u6240\u89c1\u7684\u5c11\u5e74\uff0c\u8fde\u5979\u624b\u4e2d\u6240\u6301\u7684\u90a3\u652f\u7389\u7bab\u4e5f\u662f\u4e00\u6a21 \u4e00\u6837\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u521a\u4ece\u4e00\u4e2a\u6076\u68a6\u4e2d\u9192\u6765\uff0c\u5982\u4eca\u53c8\u4f3c\u5760\u8fdb\u4e00\u4e2a\u604d\u60da\u8ff7\u79bb\u7684\u68a6\u5883\u4e2d\u4e86\uff1a\u201c\u60f3\u4e0d\u5230 \u2018\u4ed6\u2019\u3001\u2018\u4ed6\u2019\u7adf\u7136\u662f\u4e2a\u5973\u5b50\uff01\u201d \u3000\u3000\u8981\u77e5\u5510\u52aa\u73e0\u7a46\u4e3a\u4e86\u4ed6\u59b9\u59b9\u7684\u7f18\u6545\uff0c\u4e5f\u4f3c\u6c5f\u6d77\u5929\u4e00\u6837\uff0c\u5e0c\u671b\u534e\u4e91\u78a7\u53e6\u7f14\u826f\u7f18\uff0c\u800c\u90a3\u7075 \u9e6b\u5cf0\u4e0a\u7684\u5c11\u5e74\uff0c\u6b63\u662f\u4ed6\u4eec\u5e0c\u671b\u4e4b\u6240\u5bc4\uff0c\u90a3\u5c11\u5e74\u6551\u4e86\u534e\u4e91\u78a7\uff0c\u8981\u662f\u4ed6\u4eec\u4e8c\u4eba\u7231\u4e0a\uff0c\u90a3\u4e5f\u6b63 \u662f\u987a\u7406\u6210\u7ae0\u4e4b\u4e8b\uff0c\u6c5f\u6d77\u5929\u7684\u96be\u9898\u4e5f\u5c31\u53ef\u4ee5\u4e0d\u89e3\u81ea\u89e3\u4e86\u3002\u54ea\u77e5\u4ed6\u4eec\u60f3\u5f97\u5f88\u7f8e\uff0c\u5374\u4e0d\u6599\u90a3 \u201c\u5c11\u5e74\u201d\u7adf\u662f\u5973\u513f\u8eab\uff01\u5510\u52aa\u73e0\u7a46\u4e0d\u7981\u832b\u7136\u82e5\u5931\uff0c\u51e0\u4e4e\u4ece\u6811\u4e0a\u6389\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u610f\u5916\u7684\u53d1\u73b0\u4ee4\u5f97\u5510\u52aa\u73e0\u7a46\u5fc3\u795e\u4e0d\u5b9a\uff0c\u4e00\u65f6\u95f4\u4e0d\u77e5\u662f\u8fdb\u53bb\u89c1\u5979\u7684\u597d\u8fd8\u662f\u5c31\u6b64\u8d70\u5f00\u3002 \u90a3\u5973\u5b50\u653e\u4e0b\u7389\u7bab\uff0c\u53eb\u9053\uff1a\u201c\u739b\u4f9d\uff01\u201d\u4e00\u4e2a\u4f8d\u5973\u4ece\u91cc\u95f4\u63ed\u5e18\u800c\u51fa\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4f60\u6df1\u591c \u4e0d\u7761\uff0c\u8fd8\u662f\u5728\u4e3a\u90a3\u4f4d\u534e\u59d1\u5a18\u64cd\u5fc3\u4e48\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u604d\u7136\u5927\u609f\uff0c\u8fd9\u624d\u60f3\u5230\u90a3\u4e00\u9996\u8bd7\u6240\u8bf4\u7684\u90a3 \u4e2a\u75f4\u60c5\u5973\u5b50\uff0c\u4e0e\u534e\u4e91\u78a7\u76ee\u524d\u7684\u60c5\u51b5\u6b63\u662f\u751a\u4e3a\u76f8\u4f3c\u3002\u90a3\u5973\u5b50\u51c4\u7136\u8bf4\u9053\uff1a\u201c\u534e\u59d1\u5a18\u662f\u5f88\u53ef\u601c\uff0c \u4f46\u5929\u4e0b\u53ef\u601c\u7684\u5973\u5b50\uff0c\u4e5f\u4e0d\u6b62\u5979\u4e00\u4e2a\u3002\u201d \u3000\u3000\u90a3\u4f8d\u5973\u9053\uff1a\u201c\u55ef\uff0c\u5c0f\u59d0\uff0c\u6211\u77e5\u9053\u4f60\u4e5f\u6709\u7740\u5fc3\u4e8b\u3002\u516c\u4e3b\u600e\u4e48\u6837\u4e86\uff1f\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u6211 \u6b63\u5728\u7b49\u5f85\u5979\u7684\u6d88\u606f\uff0c\u8bf4\u4e0d\u5b9a\u7b49\u4e0b\u4f1a\u6709\u4eba\u6765\u3002 \u3000\u3000\u739b\u4f9d\uff0c\u6211\u4e0d\u65b9\u4fbf\u8d70\u5f00\uff0c\u4f60\u7ed9\u6211\u53bb\u4e00\u8d9f\u3002\u201d\u90a3\u4f8d\u5973\u9053\uff1a\u201c\u8bf7\u5c0f\u59d0\u5429\u5490\u3002\u201d\u90a3\u5973\u5b50\u9053\uff1a \u201c\u4f60\u53bb\u770b\u770b\u4ed6\u4eec\u9192\u4e86\u6ca1\u6709\uff0c\u53ef\u4e0d\u8bb8\u60ca\u52a8\u4ed6\u4eec\u3002\u201d \u3000\u3000\u90a3\u4f8d\u5973\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u8981\u662f\u4ed6\u4eec\u9192\u4e86\uff1f\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u4f60\u5077\u5077\u542c\u4ed6\u4eec\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u56de \u6765\u544a\u8bc9\u6211\u3002\u201d\u90a3\u4f8d\u5973\u201c\u6251\u54e7\u201d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u6837\u7684\u5999\u4e8b\uff0c\u4e8f\u5c0f\u59d0\u60f3\u5f97\u51fa\uff0c\u5c06\u6765\u53ef\u4e0d\u77e5\u9053\u534e\u59d1\u5a18\u662f\u57cb\u6028\u4f60\u5462\uff0c\u8fd8\u662f\u611f\u6fc0\u4f60\u5462\uff1f\u201d \u90a3\u5973\u5b50\u7b11\u9053\uff1a\u201c\u4f60\u8981\u77e5\u9053\uff0c\u90a3\u5c31\u5feb\u4e9b\u513f\u53bb\u770b\u770b\u4ed6\u4eec\u7684\u52a8\u9759\u5427\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u542c\u4e86\uff0c\u7591\u4e91\u9aa4\u8d77\uff0c\u4e0d\u7981\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u5979\u7528\u7684\u662f\u2018\u4ed6\u4eec\u2019\u4e8c\u5b57\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a \u5df2\u77e5\u9053\u662f\u534e\u59d1\u5a18\u4e86\uff0c\u53ef\u4e0d\u77e5\u9053\u53e6\u5916\u4e00\u4e2a\u5374\u53c8\u662f\u8c01\u4e86\uff1f\u201d\u597d\u5947\u4e4b\u5fc3\u5927\u8d77\u3002\u5f85\u90a3\u4f8d\u5973\u8d70\u4e86\u4e00 \u6bb5\u8def\uff0c\u4ed6\u5c31\u4ece\u6811\u4e0a\u4e0b\u6765\uff0c\u6084\u6084\u7684\u8ddf\u5728\u5979\u7684\u540e\u9762\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6015\u5979\u53d1\u73b0\uff0c\u4e0d\u6562\u8d70\u5f97\u592a\u8fd1\u3002\u53ea\u89c1\u90a3\u4f8d\u5973\u7a7f\u8fc7\u56de\u5eca\uff0c\u7ed5\u8fc7\u5047\u5c71\uff0c\u8d70\u5230\u4e86\u56ed\u5b50 \u5f53\u4e2d\u7684\u4e00\u4e2a\u5c0f\u6e56\u65c1\u8fb9\uff0c\u505c\u4e0b\u4e86\u811a\u6b65\u3002\u8fd9\u5c0f\u6e56\u662f\u4eba\u5de5\u5f00\u8f9f\u51fa\u6765\u7684\uff0c\u6e56\u4e2d\u6709\u4e2a\u5c0f\u5c9b\uff0c\u5c9b\u4e0a\u6709 \u95f4\u5c4b\u5b50\u3002\u6e56\u4e0a\u6709\u6d6e\u51b0\u7247\u7247\uff0c\u4f46\u5374\u4e5f\u6709\u6735\u6735\u9752\u83b2\u3002\u90a3\u662f\u4e00\u79cd\u5f02\u79cd\u83b2\u82b1\uff1b\u5728\u51b0\u5929\u96ea\u5730\u4e4b\u4e2d\u4e5f \u80fd\u5f00\u653e\u7684\u3002 \u3000\u3000\u51b0\u6e56\u4e4b\u4e2d\u9752\u83b2\u76db\u5f00\uff0c\u5012\u662f\u4e00\u79cd\u7f55\u89c1\u7684\u5947\u666f\u3002\u4f46\u5510\u52aa\u73e0\u7a46\u5374\u662f\u65e0\u5fc3\u89c2\u8d4f\uff0c\u53ea\u662f\u60f3\u9053\uff1a \u201c\u65e2\u6ca1\u6709\u8239\uff0c\u4e5f\u6ca1\u6709\u6865\uff0c\u53ef\u600e\u4e48\u8fc7\u53bb\uff1f \u3000\u3000\u534e\u59d1\u5a18\u60f3\u5fc5\u5c31\u662f\u88ab\u56da\u5728\u90a3\u95f4\u5c4b\u5b50\u91cc\u9762\u4e86\u3002\u90a3\u5973\u5b50\u5c06\u5979\u56da\u5728\u8fd9\u513f\uff0c\u81ea\u662f\u9632\u5907\u5979\u9003\u8d70\u7684\uff0c \u53ef\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\u5462\uff1f\u5728\u7075\u9e6b\u5cf0\u4e0a\uff0c\u5979\u5bf9\u534e\u59d1\u5a18\u5c0f\u5fc3\u770b\u62a4\uff0c\u5230\u4e86\u8fd9\u513f\uff0c\u5374\u53c8\u4f3c\u654c\u4eba\u4e00\u822c \u9632\u8303\uff0c\u771f\u662f\u53e4\u602a\u900f\u9876\u3001\u4ee4\u4eba\u83ab\u89e3\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u90a3\u4f8d\u5973\u638f\u51fa\u4e00\u6761\u7ef3\u7d22\uff0c\u632f\u81c2\u4e00\u6325\u3002\u547c\u7684\u4e00\u58f0\uff0c\u90a3\u6761\u7ef3\u7d22\u6a2a\u8fc7\u6e56\u9762\uff0c \u7d22\u7aef\u6709\u4e2a\u5c16\u94a9\uff0c\u94a9\u7740\u4e86\u5bf9\u9762\u7684\u4e00\u68f5\u6811\u3002 \u3000\u3000\u90a3\u4f8d\u5973\u5c06\u7ef3\u7d22\u7684\u53e6\u4e00\u7aef\u5728\u8fd9\u8fb9\u7684\u4e00\u7aef\u6811\u4e0a\u6253\u4e86\u4e2a\u7ed3\uff0c\u624b\u6500\u7ef3\u7d22\uff0c\u6377\u82e5\u733f\u7334\uff0c\u5f88\u5feb\u7684 \u5c31\u5230\u4e86\u5bf9\u5cb8\u3002\u5510\u52aa\u73e0\u7a46\u5fc3\u9053\uff1a\u201c\u8fd9\u4f8d\u5973\u7684\u6b66\u529f\u5012\u4e5f\u4e0d\u5f31\u3002\u201d\u90a3\u4f8d\u5973\u4e00\u5230\u5bf9\u5cb8\uff0c\u5c31\u628a\u7ef3\u7d22 \u6536\u56de\u53bb\u4e86\u3002\u90a3\u6761\u7ef3\u7d22\u672c\u6765\u662f\u5728\u6811\u4e0a\u6253\u4e86\u4e2a\u7ed3\u7684\uff0c\u90a3\u4f8d\u5973\u7684\u624b\u6cd5\u751a\u4e3a\u5947\u5999\uff0c\u5230\u4e86\u90a3\u8fb9\uff0c\u53ea \u89c1\u5979\u5c06\u7ef3\u5b50\u8f7b\u8f7b\u4e8c\u6296\uff0c\u7ed3\u5b50\u4fbf\u5373\u89e3\u5f00\uff0c\u957f\u8679\u4e00\u822c\u7684\u63a3\u4e86\u56de\u53bb\u3002 \u3000\u3000\u6e56\u9762\u867d\u7136\u4e0d\u662f\u5341\u5206\u5bbd\u5e7f\uff0c\u4f46\u4ece\u5cb8\u8fb9\u5230\u90a3\u5c0f\u5c9b\uff0c\u4e5f\u6709\u516d\u4e03\u4e08\u5bbd\uff0c\u5510\u52aa\u73e0\u7a46\u5373\u7b97\u529f\u529b\u6062 \u590d\uff0c\u4e5f\u4e0d\u80fd\u4e00\u8dc3\u800c\u8fc7\uff0c\u4f55\u51b5\u4ed6\u73b0\u5728\u7531\u4e8e\u9965\u997f\u7684\u7f18\u6545\uff0c\u6c14\u529b\u53ea\u53ca\u539f\u6765\u7684\u4e09\u6210\uff1f\u8fd9\u65f6\u90a3\u4f8d\u5973 \u5df2\u8d70\u5230\u90a3\u5ea7\u5c4b\u5b50\u95e8\u524d\uff0c\u5979\u6839\u672c\u4e0d\u77e5\u540e\u9762\u6709\u4eba\uff0c\u6beb\u65e0\u8b66\u6212\uff0c\u5168\u795e\u8d2f\u6ce8\u7684\u5c06\u8033\u6735\u8d34\u7740\u7a97\u5b50\u5077 \u542c\u3002\u5510\u52aa\u73e0\u7a46\u66ae\u5730\u5f97\u4e86\u4e2a\u4e3b\u610f\uff0c\u6298\u4e0b\u4e24\u679d\u6811\u679d\uff0c\u5148\u628a\u4e00\u679d\u629b\u8fdb\u6e56\u4e2d\uff0c\u7acb\u5373\u817e\u8eab\u98de\u8d77\uff0c\u5728 \u534a\u7a7a\u4e2d\u6253\u4e86\u4e2a\u7b4b\u6597\uff0c\u843d\u4e0b\u6765\u65f6\uff0c\u811a\u5c16\u6b63\u70b9\u7740\u90a3\u679d\u6811\u679d\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u4ed7\u7740\u8d85\u5999\u7684\u8f7b\u529f\uff0c\u811a\u5c16\u4e00\u70b9\u6811\u679d\uff0c\u978b\u5e95\u672a\u6e7f\uff0c\u8eab\u5f62\u5df2\u662f\u8fc5\u53c8\u63a0\u8d77\uff0c\u518d\u629b\u4e0b \u7b2c\u4e8c\u679d\u6811\u679d\u3002\u539f\u6765\u4ed6\u662f\u6015\u6e56\u5546\u7684\u6d6e\u51b0\u592a\u8584\uff0c\u96be\u4ee5\u501f\u529b\uff0c\u6545\u800c\u6539\u7528\u6811\u679d\u57ab\u811a\u7684\uff0c\u8fd9\u4e24\u679d\u6811 \u679d\u5c31\u7b49\u4e8e\u4e24\u5757\u8e0f\u677f\uff0c\u5510\u52aa\u73e0\u7a46\u4e24\u4e2a\u8d77\u843d\uff0c\u4f7f\u98de\u8fc7\u4e86\u8fd9\u516d\u4e03\u4e08\u5bbd\u7684\u6e56\u9762\u3002\u5f53\u4ed6\u7b2c\u4e8c\u6b21\u8dc3\u8d77\uff0c \u4eba\u5728\u534a\u7a7a\uff0c\u811a\u5c16\u5c1a\u672a\u7740\u5730\u4e4b\u65f6\uff0c\u4fbf\u542c\u5f97\u5c4b\u5185\u4f20\u51fa\u4e00\u4e2a\u60ca\u559c\u4ea4\u96c6\u7684\u5973\u5b50\u7684\u58f0\u97f3\uff0c\u53e0\u58f0\u53eb\u9053\uff1a \u201c\u6d77\u54e5\uff0c\u6d77\u54e5\uff01\u201d\u6b63\u662f\u534e\u4e91\u78a7\u7684\u58f0\u97f3\uff0c\u8fd9\u867d\u5728\u5510\u52aa\u73e0\u7a46\u610f\u6599\u4e4b\u4e2d\uff0c\u4e5f\u81ea\u597d\u751f\u6b22\u559c\uff0c\u5fc3\u60f3\uff1a \u201c\u8fd9\u6b21\u7ec8\u4e8e\u627e\u7740\u534e\u59d1\u5a18\u4e86\u3002\u201d \u3000\u3000\u90a3\u4f8d\u5973\u5168\u795e\u8d2f\u6ce8\u7684\u5077\u542c\u5c4b\u5185\u7684\u52a8\u9759\uff0c\u5510\u52aa\u73e0\u7a46\u5dee\u4e0d\u591a\u8d70\u8fd1\u5979\u7684\u8eab\u8fb9\uff0c\u5979\u624d\u84e6\u7136\u53d1\u89c9\uff0c \u8fd8\u672a\u66fe\u53eb\u5f97\u51fa\u58f0\uff0c\u5510\u52aa\u73e0\u7a46\u51fa\u624b\u5982\u7535\uff0c\u5df2\u662f\u8fc5\u5373\u4ee5\u201c\u9694\u7a7a\u70b9\u7a74\u201d\u7684\u529f\u592b\uff0c\u70b9\u4e86\u5979\u7684\u7a74\u9053\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u5e26\u7740\u82e6\u6da9\u5473\u9053\u7684\u7537\u5b50\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4e91\u743c\uff0c\u534e\u59d1\u5a18\uff0c\u4f60\u8fd8 \u8ba4\u5f97\u6211\u4e48\uff1f\u201d\u534e\u4e91\u78a7\u201c\u554a\u5440\u201d\u4e00\u58f0\u53eb\u4e86\u8d77\u6765\uff0c\u201c\u600e\u4e48\u662f\u4f60\uff1f\u54a6\uff0c\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u6211\u662f\u5728 \u505a\u68a6\u4e48\uff1f\u4f60\u53c8\u662f\u600e\u4e48\u5230\u4e86\u8fd9\u51e0\u6765\u7684\uff1f\u201d\u6562\u60c5\u662f\u4e91\u743c\u5df2\u9192\u4e86\u591a\u65f6\uff0c\u800c\u534e\u4e91\u78a7\u5219\u521a\u521a\u624d\u9192\u3002 \u4e91\u743c\u5927\u7ea6\u662f\u56e0\u4e3a\u6d78\u5728\u51b0\u6cb3\u4e4b\u4e2d\uff0c\u4e3a\u65f6\u8fc7\u4e45\uff0c\u8bf4\u8bdd\u5e26\u7740\u91cd\u4f24\u98ce\u7684\u9f3b\u97f3\uff0c\u56e0\u800c\u5c31\u663e\u5f97\u6709\u4e9b\u82e6 \u6da9\u7684\u5473\u9053\u3002\u4e0d\u8fc7\uff0c\u4e5f\u8bb8\u662f\u56e0\u4e3a\u534e\u4e91\u78a7\u4e00\u9192\u6765\u5c31\u5c06\u4ed6\u9519\u8ba4\u4f5c\u6c5f\u6d77\u5929\uff0c\u4ed6\u611f\u5230\u6ee1\u4e0d\u662f\u5473\u513f\u3002 \u3000\u3000\u534e\u4e91\u78a7\u5728\u8fd9\u5c4b\u5185\u90a3\u662f\u5510\u52aa\u73e0\u7a46\u65e9\u5df2\u6599\u5230\u4e86\u7684\u3002\u4f46\u4e91\u743c\u4e5f\u5728\u8fd9\u513f\uff0c\u5374\u662f\u5927\u5927\u51fa\u4e4e\u4ed6\u7684 \u610f\u6599\u4e4b\u5916\u4e86\uff01\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u4e5f\u51e0\u4e4e\u7591\u5fc3\u81ea\u5df1\u662f\u5728\u505a\u68a6\u3002 \u3000\u3000\u4e91\u743c\u9053\uff1a\u201c\u53ea\u6015\u8fd9\u771f\u662f\u4e00\u4e2a\u8352\u5510\u7684\u602a\u68a6\uff0c\u6211\u5206\u660e\u8bb0\u5f97\u6211\u662f\u6389\u8fdb\u51b0\u6cb3\u4e86\u7684\uff0c\u7cca\u91cc\u7cca\u6d82 \u7684\u4e00\u89c9\u9192\u6765\uff0c\u6211\u5c31\u8eba\u5728\u8fd9\u91cc\u4e86\u3002\u5947\u602a\u7684\u662f\u6211\u7684\u8863\u88f3\u5df2\u6362\u4e86\u5957\u5e72\u7684\uff0c\u4f60\u53c8\u5728\u8fd9\u513f\uff0c\u6211\u4ee5\u4e3a \u4f60\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff0c\u539f\u6765\u4f60\u4e5f\u4e00\u70b9\u4e0d\u77e5\uff01\u534e\u59d1\u5a18\uff0c\u6211\u4e0d\u662f\u6709\u5fc3\u95ef\u8fdb\u4f60\u7684\u5c4b\u5b50\u7684\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u9053\uff1a\u201c\u8fd9\u5e76\u4e0d\u662f\u6211\u7684\u5c4b\u5b50\uff0c\u54a6\uff0c\u8fd9\u4e48\u770b\u6765\u54b1\u4eec\u90fd\u662f\u53d7\u4e86\u4eba\u5bb6\u7684\u6446\u5e03\u4e86\u3002\u201d\u4e91 \u743c\u9053\uff1a\u201c\u4f60\u6253\u5f00\u95e8\u770b\u770b\uff0c\u5916\u9762\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4ed6\u81ea\u5df1\u5728\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u5947\u602a\uff0c \u8fd9\u623f\u5b50\u662f\u6ca1\u6709\u95e8\u7684\u3002\u201d \u3000\u3000\u8fd9\u662f\u4e00\u95f4\u5341\u5206\u575a\u56fa\u7684\u77f3\u5c4b\uff0c\u91cc\u5916\u90fd\u627e\u4e0d\u5230\u95e8\u53f0\u5510\u52aa\u73e0\u7a46\u5fc3\u60f3\uff1a\u201d\u6562\u60c5\u8fd9\u53c8\u662f\u50cf\u7075\u9e6b \u5cf0\u4e0a\u7684\u90a3\u95f4\u51b0\u5c4b\u4e00\u6837\uff0c\u662f\u8981\u4ece\u5730\u9053\u8fdb\u53bb\u7684\u3002\u201d\u4ed6\u672c\u5f85\u51fa\u58f0\u53eb\u5524\uff0c\u4f46\u542c\u5f97\u534e\u4e91\u78a7\u8bf4\u5230\u90a3 \u201c\u6446\u5e03\u201d\u4e8c\u5b57\u3002\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5374\u53c8\u5fcd\u7740\u4e86\u3002\u5fc3\u60f3\uff1a\u201c\u602a\u4e0d\u5f97\u90a3\u4f8d\u5973\u8bf4\u5979\u5c0f\u59d0\uff0c\u8fd9\u6837\u7684\u6076 \u4f5c\u5c45\u4e5f\u4e8f\u5979\u60f3\u5f97\u51fa\uff0c\u55ef\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e5f\u672a\u5fc5\u662f\u6076\u4f5c\u5267\u5462\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u534e\u4e91\u78a7\u8bf4\u9053\uff1a\u201c\u6211\u5012\u6709\u70b9\u60f3\u8d77\u6765\u4e86\uff01\u201d\u4e91\u743c\u8fde\u5fd9\u95ee\u9053\uff1a \u3000\u3000\u201c\u600e\u4e48\uff1f\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u4f3c\u4e4e\u6709\u4e00\u4e2a\u767d\u8863\u59d1\u5a18\u662f\u65f6\u65f6\u5728\u6211\u8eab\u8fb9\u7684\uff01\u201d \u3000\u3000\u4e91\u743c\u751a\u662f\u7eb3\u7f55\u95ee\u9053\uff0c\u201c\u600e\u4e48\u8bf4\u662f\u4f3c\u4e4e\uff1f\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u6211\u4e00\u76f4\u8ff7\u8ff7\u6805\u7cca\u7684\uff0c\u4e5f\u4e0d\u77e5 \u8fc7\u4e86\u591a\u5c11\u65f6\u5019\u4e86\uff0c\u4f3c\u4e4e\u6709\u8bb8\u591a\u4eba\u6765\u770b\u8fc7\u6211\uff0c\u6709\u6211\u7239\u7239\uff0c\u6709\u4ef2\u53d4\u53d4\uff0c\u6709\u4f60\u4eec\u5144\u59b9\uff0c\u8fd8\u6709\uff0c \u6709\u6c5f\u6d77\u5929\u3002\u201d\u4e91\u743c\u7b11\u9053\uff1a\u201c\u8fd9\u5168\u662f\u68a6\u5883\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u767d\u8863\u59d1\u5a18\u4e5f\u662f\u8fd9\u4e48\u544a\u8bc9\u6211\u7684\uff0c\u8bf4\u8fd9\u662f\u68a6\u5883\uff01\u5509\uff0c\u6211\u662f\u50cf\u5728\u505a \u7740\u65e0\u4f11\u65e0\u6b62\u7684\u68a6\uff0c\u4ec0\u4e48\u90fd\u5f04\u7cca\u6d82\u4e86\u3002\u662f\u68a6\u662f\u5e7b\uff1f\u662f\u5047\u662f\u771f\uff1f\u6211\u90fd\u5df2\u4e0d\u80fd\u5206\u8fa8\uff0c\u90a3\u767d\u8863\u59d1 \u5a18\u5230\u5e95\u662f\u771f\u4eba\u8fd8\u662f\u5e7b\u5f71\uff0c\u6211\u4e5f\u4e0d\u6562\u65ad\u5b9a\uff0c\u6240\u4ee5\u53ea\u80fd\u52a0\u4e0a\u2018\u4f3c\u4e4e\u2019\u4e8c\u5b57\u3002\u201d \u3000\u3000\u5979\u5ffd\u5730\u201c\u54ce\u54df\u201d\u4e00\u58f0\u53eb\u4e86\u8d77\u6765\uff0c\u4e91\u743c\u5413\u4e86\uff1d\u8df3\uff0c\u539f\u6765\u662f\u5979\u7528\u529b\u54ac\u4e86\u4e00\u54ac\u81ea\u5df1\u7684\u6307\u5934\uff0c \u8bf4\u9053\uff1a\u201c\u5f88\u75db\uff0c\u5f88\u75db\uff0c\u73b0\u5728\u5927\u7ea6\u4e0d\u662f\u68a6\u4e86\uff01\u201d \u3000\u3000\u4e91\u743c\u9053\uff1a\u201c\u4f60\u7684\u7239\u7239\uff0c\u4f60\u7684\u6d77\u54e5\uff0c\u8fd9\u4e9b\u4eba\u90fd\u662f\u4f60\u68a6\u4e2d\u6240\u89c1\u7684\u5e7b\u5f71\uff0c\u552f\u6709\u90a3\u4e2a\u767d\u8863\u59d1 \u5a18\uff0c\u6211\u770b\u4e00\u5b9a\u662f\u4e2a\u771f\u4eba\uff0c\u5c31\u662f\u5979\u6551\u4e86\u4f60\u7684\u3002\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201d\u4e0d\u9519\uff0c\u6211\u4e5f\u662f\u8fd9\u4e48\u60f3\u3002\u6211\u8fd8 \u60f3\u8d77\u4e86\uff0c\u5979\u66fe\u7ecf\u5bf9\u6211\u8bf4\u8fc7\u597d\u4e9b\u8bdd\u3002\u201d\u4e91\u743c\u8fde\u5fd9\u95ee\u9053\uff0c\u201c\u4f60\u8fd8\u8bb0\u5f97\u5979\u5bf9\u4f60\u8bf4\u7684\u90a3\u4e9b\u8bdd\u5417\uff1f\u201d \u3000\u3000\u5728\u8fd9\u77ac\u95f4\uff0c\u534e\u4e91\u78a7\u53c8\u662f\u60a0\u7136\u5b58\u601d\uff0c\u832b\u7136\u82e5\u68a6\uff0c\u4f3c\u4e4e\u6839\u672c\u542c\u4e0d\u89c1\u4e91\u743c\u95ee\u5979\u4ec0\u4e48\uff0c\u4e91\u743c \u4e00\u65f6\u6025\u4e86\uff0c\u4e5f\u987e\u4e0d\u5f97\u5192\u6627\uff0c\u4e0d\u81ea\u89c9\u7684\u4fbf\u6447\u4e86\u6447\u5979\u7684\u624b\u81c2\uff0c\u8bf4\u9053\uff1a\u201c\u534e\u59d1\u5a18\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u623f\u4e2d\u6709\u65f6\u7ea2\u707f\uff0c\u7528\u73bb\u7483\u7684\u706f\u7b3c\u7f69\u4f4f\uff0c\u70db\u5149\u5410\u8273\uff0c\u534e\u4e91\u78a7\u7684\u53cc\u988a\u4e5f\u663e\u5f97\u4e00\u7247\u6655\u7ea2\uff0c\u5979 \u5ffd\u5730\u4f3c\u662f\u5728\u68a6\u4e2d\u9192\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u7684\u7239\u7239\u548c\u6d77\u5929\u4ed6\u4eec\u90fd\u662f\u5e7b\u5f71\uff1f\u201d\u4e91\u51c9\u7b11\u9053\uff1a \u201c\u56e0\u4e3a\u8fd9\u534a\u4e2a\u6708\u672a\uff0c\u6211\u5929\u5929\u90fd\u548c\u4ed6\u4eec\u5728\u4e00\u8d77\u3002\u201d\u534e\u4e91\u78a7\u4f3c\u559c\u4f3c\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u548c\u4ed6\u4eec\u5728 \u4e00\u8d77\u7684\uff1f\u55ef\uff0c\u4ed6\u4eec\u600e\u4e48\u5566\uff1f\u201d\u4e91\u743c\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\u2014\u2014\u201d\u6b63\u5f85\u5c06\u6240\u7ecf\u5386\u7684\u4e8b\u60c5\u7ec6\u8bf4\u3002\u534e \u4e91\u78a7\u5ffd\u53c8\u9732\u51fa\u604d\u60da\u8ff7\u79bb\u7684\u795e\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u662f\u5e7b\u5f71\uff0c\u600e\u4e48\u5c31\u4f3c\u4e0d\u4e45\u4e4b\u524d\u3002\u6211\u5206\u660e\u542c\u5f97 \u6d77\u5929\u5728\u5927\u58f0\u53eb\u6211\uff0c\u90a3\u597d\u50cf\u4e0d\u662f\u68a6\uff1f\u201d \u3000\u3000\u4e91\u743c\u9053\uff1a\u201c\u90a3\u7684\u786e\u4e0d\u662f\u68a6\u3002\u6211\u4e0d\u77e5\u4eca\u5929\u662f\u4ec0\u4e48\u65e5\u5b50\uff0c\u6211\u4e5f\u4e0d\u77e5\u6211\u81ea\u5df1\u660f\u8ff7\u4e86\u591a\u5c11\u65f6 \u5019\uff0c\u4f46\u6700\u8fd1\u7684\u4e8b\u60c5\u6211\u8fd8\u662f\u8bb0\u5f97\u7684\uff0c\u4e5f\u8bb8\u662f\u6628\u5929\uff0c\u4e5f\u8bb8\u662f\u524d\u5929\uff0c\u4f60\u7684\u7239\u7239\u548c\u6c5f\u6d77\u5929\u66fe\u7ecf\u4e0a \u8fc7\u7075\u9e6b\u5cf0\uff0c\u4ed6\u4eec\u6000\u7591\u4f60\u5728\u90a3\u7075\u9e6b\u5cf0\u4e0a\u3002\u6562\u60c5\u8fd9\u662f\u771f\u7684\uff0c\u4f60\u662f\u5728\u7075\u9e6b\u5cf0\u4e0a\u542c\u5230\u6c5f\u6d77\u5929\u7684\u58f0 \u97f3\u4e86\uff01\u201d \u3000\u3000\u534e\u4e91\u78a7\u9053\uff0c\u201c\u4f60\u4eec\u600e\u4f1a\u5230\u90a3\u513f\u6765\u7684\uff1f\u90a3\u7075\u9e6b\u5cf0\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u4e91\u743c\u9053\uff1a\u201c\u6211\u548c\u6d77\u5929 \u4ed6\u4eec\u4e00\u540c\u5230\u6606\u5e03\u5170\u56fd\u6765\u7684\uff0c\u6211\u542c\u8bf4\u90a3\u7075\u9e6b\u5cf0\u662f\u5728\u6606\u5e03\u5170\u56fd\u4e0e\u9a6c\u8428\u513f\u56fd\u4ea4\u754c\u7684\u5730\u65b9\u2026\u2026\u201d \u534e\u4e91\u78a7\u5ffd\u53c8\u6253\u65ad\u4ed6\u7684\u8bdd\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\uff0c\u6c5f\u6d77\u5929\u4ed6\u662f\u8981\u5f80\u6606\u5e03\u5170\u56fd\u53bb\u770b\u4ed6\u7684\u83b2\u59b9\u7684\u3002\u201d \u58f0\u8c03\u82cd\u51c9\uff0c\u5510\u52aa\u73e0\u7a46\u5728\u5916\u9762\u5077\u542c\uff0c\u867d\u7136\u770b\u4e0d\u5230\u5979\uff0c\u4e5f\u60f3\u8c61\u5f97\u51fa\uff0c\u5979\u8fd9\u65f6\u5019\u4e00\u5b9a\u662f\u4e00\u8138\u5931 \u671b\u7684\u795e\u60c5\u3002\u4f46\u5510\u52aa\u73e0\u7a46\u4e5f\u5728\u5947\u602a\uff1a\u201c\u5979\u600e\u4e48\u4f1a\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u4e91\u743c\u5df2\u662f\u5c06\u4ed6\u5fc3\u91cc\u60f3\u95ee\u7684\u8bf4\u8bdd\u95ee\u4e86\u51fa\u6765\uff1a\u201c\u4f60\u662f\u600e\u4e48\u77e5 \u76f4\u7684\uff1f\u201d \u3000\u3000\u534e\u4e91\u78a7\u76ee\u4e2d\u8574\u7740\u6cea\u5149\uff0c\u54fd\u54bd\u8bf4\u9053\uff1a\u201c\u6d77\u54e5\u7684\u5fc3\u4e0a\u53ea\u6709\u4ed6\u7684\u83b2\u59b9\uff0c\u8fd9\u662f\u6211\u65e9\u5df2\u77e5\u9053\u7684 \u4e86\u3002\u201d\u4e91\u743c\u60f3\u95ee\u7684\u4e43\u662f\u5979\u600e\u4e48\u77e5\u9053\u8c37\u4e2d\u83b2\u73b0\u5728\u6606\u5e03\u5170\u56fd\uff0c\u5374\u4e0d\u6599\u534e\u4e91\u78a7\u7b54\u975e\u6240\u95ee\uff0c\u5410\u9732 \u4e86\u5979\u5fc3\u5e95\u7684\u54c0\u4f24\u3002 \u3000\u3000\u4e91\u743c\u5446\u4e86\u4e00\u5446\uff0c\u987f\u65f6\u95f4\u4e5f\u7ed9\u89e6\u52a8\u4e86\u6101\u6000\uff0c\u53ea\u89c9\u60b2\u4ece\u4e2d\u6765\uff0c\u96be\u4ee5\u65ad\u7edd\u3002\u534e\u4e91\u78a7\u4e00\u77a7\uff0c \u53ea\u89c1\u4e91\u743c\u773c\u89d2\u4e5f\u8574\u6709\u6cea\u73e0\uff0c\u66f4\u589e\u4f24\u611f\uff0c\u4e0d\u89c9\u95ee\u9053\uff1a\u201c\u4f60\u662f\u966a\u4f34\u6d77\u5929\u53bb\u5bfb\u8c37\u4e2d\u83b2\u7684\u5417\uff1f\u201d \u4e91\u743c\u6da9\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u548c\u59b9\u59b9\u90fd\u662f\u6765\u627e\u5bfb\u4f60\u7684\u3002\u6211\u3001\u6211\u65e9\u5df2\u662f\u4e0d\u60f3\u518d\u89c1\u8c37\u4e2d\u83b2\u4e86\u3002\u201d \u534e\u4e91\u78a7\u773c\u6cea\u6ef4\u4e86\u4e0b\u6765\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u3002\u6211\u53ea\u9053\u8fd9\u4e16\u4e0a\u9664\u4e86\u6211\u7239\u7239\u4e4b\u5916\uff0c\u5df2\u662f\u65e0\u4eba\u518d \u8bb0\u5f97\u6211\u4e86\u3002\u201d \u3000\u3000\u6d41\u6cea\u773c\u89c2\u6d41\u6cea\u773c\uff0c\u4f24\u5fc3\u4eba\u5bf9\u4f24\u5fc3\u4eba\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u4e24\u4eba\u90fd\u662f\u540c\u6837\u5fc3\u60c5\u6fc0\u52a8\u3002\u4e91\u743c\u4e0d\u77e5 \u4e0d\u89c9\u7684\u53c8\u63e1\u7740\u4e86\u5979\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4e00\u68f5\u8349\u6709\u4e00\u6ef4\u9732\u73e0\uff0c\u4e00\u628a\u9501\u6709\u4e00\u628a\u9501\u5319\uff0c\u5929\u5730\u4e07\u7269\u90fd \u662f\u5404\u81ea\u6709\u5404\u81ea\u7684\u7f18\u4efd\uff0c\u5982\u4eca\u6211\u662f\u61c2\u5f97\u4e86\u3002\u4f60\u6216\u8bb8\u4e5f\u4f1a\u77e5\u9053\u3001\u6211\u66fe\u7ecf\u5bf9\u8c37\u4e2d\u83b2\u6709\u8fc7\u6df1\u6df1\u7684 \u503e\u6155\uff0c\u4e0d\u7792\u4f60\u8bf4\uff0c\u5f53\u6211\u77e5\u9053\u5979\u5fc3\u4e0a\u53e6\u6709\u4e86\u4e00\u4e2a\u4eba\u7684\u65f6\u5019\uff0c\u6211\u4e5f\u66fe\u7ecf\u662f\u5f88\u96be\u8fc7\u7684\uff0c\u4f46\u73b0\u5728 \u6211\u5374\u662f\u4e3a\u4ed6\u4eec\u9ad8\u5174\u4e86\uff0c\u8981\u662f\u4ed6\u4eec\u90fd\u611f\u5230\u5e78\u798f\uff0c\u6211\u4e5f\u5c31\u611f\u5230\u5e78\u798f\u4e86\u3002\u201d\u4e91\u743c\u662f\u4e3a\u4e86\u5b89\u6170\u534e \u4e91\u78a7\uff0c\u4e5f\u662f\u4e3a\u4e86\u5b89\u6170\u81ea\u5df1\uff0c\u4f46\u8fd9\u5374\u4e5f\u662f\u4ed6\u5fc3\u4e2d\u4e0d\u77e5\u60f3\u4e86\u591a\u5c11\u904d\u7684\u8bf4\u8bdd\uff0c\u8bf4\u6765\u7aef\u7684\u771f\u60c5\u6d41 \u9732\uff0c\u8bda\u631a\u611f\u4eba\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u534e\u4e91\u78a7\u5b9b\u5982\u53d7\u4e86\u5f53\u5934\u68d2\u559d\uff0c\u5fc3\u4e2d\u867d\u7136\u8fd8\u662f\u96be\u8fc7\uff0c\u4f46\u5374\u8c41\u7136\u201c\u609f\u201d\u4e86\u3002\u672c \u6765\u8fd9\u79cd\u611f\u60c5\u7684\u201c\u6b7b\u7ed3\u201d\uff0c\u662f\u6700\u96be\u89e3\u5f00\u7684\uff0c\u5de7\u7684\u662f\u4e91\u743c\u548c\u5979\u6b63\u662f\u540c\u6837\u7684\u906d\u9047\uff0c\u540c\u6837\u7684\u5fc3\u60c5\uff0c \u8bf4\u51fa\u7684\u8bdd\u6765\uff0c\u4e5f\u5c31\u683c\u5916\u80fd\u591f\u542c\u5f97\u5165\u8033\uff0c\u94bb\u8fdb\u4e86\u5979\u7684\u5fc3\u7075\u6df1\u5904\u3002 \u3000\u3000\u534e\u4e91\u78a7\u6cea\u5982\u96e8\u4e0b\uff0c\u4e5f\u4e0d\u81ea\u89c9\u7684\u7d27\u7d27\u63e1\u7740\u4e91\u743c\u7684\u624b\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u6307\u70b9\u8ff7\u6d25\uff0c\u4f60\u77a7\u3002 \u6211\u73b0\u5728\u4e5f\u5f88\u9ad8\u5174\u4e86\u3002\u201d\u5979\u6ee1\u8138\u6cea\u75d5\uff0c\u4f46\u4e91\u743c\u5374\u53ef\u4ee5\u611f\u89c9\u5f97\u5230\uff0c\u8fd9\u5df2\u7ecf\u662f\u201c\u96e8\u8fc7\u5929\u6674\u201d\u4e86\u3002 \u9634\u973e\u5e03\u6ee1\u7684\u5929\u7a7a\uff0c\u672c\u6765\u662f\u5e94\u8be5\u6709\u4e00\u573a\u5927\u96e8\uff0c\u624d\u80fd\u4f7f\u5f97\u4e4c\u4e91\u6d88\u6563\uff0c\u6062\u590d\u6674\u660e\u7684\u3002 \u3000\u3000\u5c4b\u5916\u7684\u5510\u52aa\u73e0\u7a46\u8fd9\u65f6\u4e5f\u5ffd\u5730\u604d\u7136\u5927\u609f\uff1a\u201c\u539f\u6765\u90a3\u767d\u8863\u5973\u5b50\u5982\u6b64\u6446\u5e03\uff0c\u662f\u6709\u7740\u8fd9\u822c\u7684 \u6df1\u610f\u5b58\u5728\u3002\u59d1\u4e0d\u8bba\u4ed6\u4eec\u5c06\u6765\u5982\u4f55\uff0c\u6700\u5c11\u4ed6\u4eec\u73b0\u5728\u5df2\u662f\u5e76\u4e0d\u5b64\u72ec\u4e86\uff0c\u5728\u611f\u60c5\u8f6f\u5f31\u7684\u65f6\u523b\uff0c \u6700\u9700\u8981\u540c\u75c5\u76f8\u601c\u7684\u4eba\u4e92\u76f8\u5b89\u6170\uff0c\u4ed6\u4eec\u7684\u82e6\u607c\uff0c\u4e5f\u5fc5\u5c06\u5927\u5927\u6d88\u51cf\u4e86\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u521d\u6765\u7684\u65f6\u5019\uff0c\u672c\u662f\u60f3\u4e0e\u4ed6\u4eec\u89c1\u9762\u7684\u3002\u6b64\u9645\u4ed6\u660e\u767d\u4e86\u90a3\u767d\u8863\u5973\u5b50\u7684\u7528\u5fc3\uff0c\u53cd \u800c\u4e0d\u613f\u60ca\u52a8\u4ed6\u4eec\u4e86\uff0c\u4ed6\u5728\u5730\u4e0a\u62fe\u8d77\u4e86\u90a3\u6761\u7ef3\u7d22\uff0c\u8f7b\u8f7b\u7684\u5c31\u79bb\u5f00\u4e86\u8fd9\u95f4\u5c4b\u5b50\u3002\u90a3\u4f8d\u5973\u7ed9\u4ed6 \u6240\u70b9\u7684\u7a74\u9053\uff0c\u662f\u8fc7\u4e86\u4e00\u4e2a\u65f6\u8fb0\u4fbf\u53ef\u4ee5\u81ea\u89e3\u7684\uff0c\u6682\u65f6\u4e5f\u4e0d\u5fc5\u7406\u4f1a\u3002\u4ed6\u7528\u90a3\u4f8d\u5973\u521a\u624d\u7528\u8fc7\u7684 \u65b9\u6cd5\uff0c\u6325\u7d22\u98de\u8fc7\u6e56\u9762\uff0c\u8fc5\u5373\u56de\u5230\u5bf9\u5cb8\u3002\u6b63\u662f\uff1a \u3000\u3000\u5929\u4e0b\u6709\u60c5\u6210\u7737\u5c5e\uff0c\u59fb\u7f18\u51d1\u5408\u5de7\u5b89\u6392\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e5d\u56de\u3000\u63a5\u82b1\u79fb\u6728\u65bd\u5999\u8ba1\u3000\u53d8\u5bb9\u6613\u8c8c\u51fa\u5947\u8c0b \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u56db\u5341\u4e5d\u56de\u3000\u63a5\u82b1\u79fb\u6728\u65bd\u5999\u8ba1\u3000\u53d8\u5bb9\u6613\u8c8c\u51fa\u5947\u8c0b \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u5973\u5b50\u6551\u4e86\u534e\u4e91\u78a7\uff0c\u53c8\u6551\u4e86\u4e91\u743c\uff0c\u60f3\u6765\u51b3\u8ba1\u4e0d\u662f\u574f\u4eba\u7684\u4e86\u3002 \u770b\u5979\u8fd9\u756a\u5e03\u7f6e\uff0c\u5fc3\u5730\u5012\u8fd8\u771f\u4e0d\u9519\u5462\uff01\u201d\u8fd9\u65f6\u5df2\u662f\u8fc7\u4e86\u5348\u591c\u7684\u65f6\u5206\uff0c\u5510\u52aa\u73e0\u7a46\u5fc3\u60f3\u90a3\u5973\u5b50 \u8981\u7b49\u5f85\u5979\u4f8d\u5973\u7684\u56de\u97f3\uff0c\u5927\u7ea6\u8fd8\u672a\u66fe\u5165\u7761\uff0c\u5c31\u51b3\u610f\u53bb\u6572\u95e8\u6c42\u89c1\u3002\u5ffd\u542c\u5f97\u6709\u91ce\u517d\u7684\u568e\u53eb\uff0c\u5510 \u52aa\u73e0\u7a46\u8ba4\u5f97\u662f\u91d1\u6bdb\u72e1\u7684\u543c\u58f0\uff0c\u5fc3\u4e2d\u5fae\u5fae\u4e00\u51db\uff1a\u201c\u6211\u5012\u5fd8\u8bb0\u4e86\u90a3\u5929\u9b54\u6559\u7684\u5389\u526f\u6559\u4e3b\u4e5f\u5728\u8fd9 \u513f\u4e86\u3002\u201d\u4ed6\u5e76\u975e\u5bb3\u6015\u91d1\u6bdb\u649e\uff0c\u4f46\u56e0\u5bf9\u5389\u590d\u751f\u8fd8\u662f\u6709\u6240\u7591\u5fcc\uff0c\u9042\u53c8\u6539\u4e86\u4e3b\u610f\uff1a\u201c\u8fd8\u662f\u518d\u770b \u4e00\u770b\u518d\u8bf4\u3002\u201d \u3000\u3000\u5f53\u4e0b\uff0c\u4ed6\u4ecd\u7136\u65bd\u5c55\u8d85\u5353\u7684\u8f7b\u529f\uff0c\u86c7\u884c\u5154\u4f0f\uff0c\u501f\u7269\u969c\u5f62\uff0c\u6298\u56de\u539f\u8def\u3002\u521a\u7ed5\u8fc7\u4e86\u82b1\u56ed\u5f53 \u4e2d\u7684\u4e00\u5ea7\u5047\u5c71\uff0c\u5ffd\u89c1\u6709\u4e2a\u6c49\u5b50\u8fce\u9762\u800c\u6765\uff0c\u81ea\u8a00\u81ea\u8bed\uff0c\u5600\u5600\u5495\u5495\uff0c\u201c\u8fd9\u755c\u7272\u4e09\u66f4\u534a\u591c\u624d\u6765\uff0c \u5012\u8981\u6211\u6765\u670d\u4f8d\u5b83\u3002\u80c3\u53e3\u4e5f\u771f\u5927\uff0c\u5403\u4e86\u4e24\u53ea\u5154\u5b50\u8fd8\u4e0d\u591f\uff0c\u597d\u5728\u53a8\u623f\u6709\u73b0\u6210\u7684\u725b\u8089\uff0c\u5c31\u8ba9\u5b83 \u4eab\u4eab\u798f\u5427\u3002\u201d \u3000\u3000\u5510\u5974\u73e0\u7a46\u6b63\u81ea\u9965\u997f\uff0c\u95fb\u5f97\u8089\u9999\u56db\u6ea2\uff0c\u6562\u60c5\u90a3\u725b\u8089\u8fd8\u662f\u521a\u70e4\u719f\u7684\uff0c\u4e0d\u7981\u998b\u6d8e\u6b32\u6ef4\uff0c\u5fc3 \u91cc\u6697\u7b11\uff1a\u201c\u6765\u5f97\u6b63\u597d\uff01\u201d\u4f7f\u51fa\u201c\u9694\u7a7a\u70b9\u7a74\u201d\u7684\u529f\u592b\uff0c\u4e00\u70b9\u5c31\u70b9\u5012\u4e86\u90a3\u6761\u5927\u6c49\uff0c\u62a2\u4e86\u4ed6\u624b \u4e0a\u7684\u725b\u8089\u3002 \u3000\u3000\u725b\u8089\u679c\u7136\u8fd8\u662f\u70ed\u6c14\u817e\u817e\uff0c\u5510\u52aa\u73e0\u7a46\u5fc3\u60f3\uff1a\u201c\u662f\u4e86\uff0c\u90a3\u5973\u5b50\u8bf4\u8fc7\uff0c\u4eca\u665a\u53ef\u80fd\u6709\u5ba2\u4eba\u6765 \u5230\uff0c\u53a8\u623f\u91cc\u7ed9\u5ba2\u4eba\u4eec\u51c6\u5907\u4e86\u8bb8\u591a\u98df\u7269\uff0c\u8fd9\u6c49\u5b50\u8d2a\u61d2\uff0c\u79c1\u81ea\u62ff\u4e86\u4e24\u65a4\u70e4\u725b\u8089\u53bb\u5582\u91d1\u6bdb\u72e1\u3002\u201d \u3000\u3000\u8fd9\u662f\u897f\u85cf\u4e00\u79cd\u91ce\u725b\u7684\u817f\u8089\uff0c\u5012\u662f\u751a\u4e3a\u9c9c\u7f8e\uff0c\u6bd4\u666e\u901a\u7684\u725b\u8089\u8fd8\u8981\u597d\u5403\u3002\u5510\u52aa\u73e0\u7a46\u5403\u5f97 \u6d25\u5f8b\u6709\u5473\uff0c\u4e00\u7247\u4e0d\u7559\u3002\u53ea\u89c9\u6bd4\u4ed6\u5728\u738b\u5bab\u91cc\u6240\u4eab\u53d7\u8fc7\u7684\u90a3\u4e9b\u73cd\u80b4\u7f8e\u5473\uff0c\u8fd8\u8981\u80dc\u8fc7\u767e\u500d\uff0c\u5403 \u5b8c\u4e4b\u540e\uff0c\u542e\u542e\u820c\u5934\uff0c\u4e0d\u7981\u54d1\u7136\u5931\u7b11\uff1a\u201c\u6211\u4ee5\u4e00\u56fd\u4e4b\u4e3b\u7684\u8eab\u4efd\uff0c\u60f3\u4e0b\u5230\u4eca\u665a\u7adf\u4e0e\u755c\u7272\u4e89\u98df\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5403\u9971\u4e4b\u540e\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u5fc3\u60f3\uff1a\u201c\u5373\u7b97\u90a3\u5389\u590d\u751f\u4e0e\u6211\u4f5c\u5bf9\uff0c\u6211\u4e5f\u8db3\u53ef\u5e94\u4ed8\u4ed6 \u4e86\u3002\u201d\u90a3\u4e00\u5927\u5757\u725b\u8089\u5403\u5b8c\uff0c\u4ed6\u4e5f\u5df2\u56de\u5230\u4e86\u539f\u6765\u7684\u5730\u65b9\uff0c\u4ecd\u7136\u8df3\u4e0a\u90a3\u68f5\u5927\u6811\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c \u5077\u770b\u623f\u4e2d\u60c5\u666f\u3002 \u3000\u3000\u5389\u590d\u751f\u679c\u7136\u662f\u5728\u623f\u4e2d\u548c\u90a3\u767d\u8863\u5973\u5b50\u8bf4\u8bdd\uff0c\u53ea\u542c\u5f97\u4ed6\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u5012\u5584\u4e8e\u66ff\u4eba\u64ae\u5408 \u5a5a\u7f18\uff01\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u6210\u4e0d\u6210\u8fd8\u8981\u5f80\u540e\u77a7\u5462\u4e25\u5389\u590d\u751f\u9053\uff1a\u201c\u771f\u4e0d\u77e5\u90a3\u59d3\u6c5f\u7684\u5c0f\u4e8e\u6709\u4ec0\u4e48 \u597d\u5904\uff0c\u8fd9\u4e9b\u5973\u5b69\u5b50\u90fd\u5bf9\u4ed6\u8fd9\u6837\u75f4\u60c5\u3002\u201d\u90a3\u5973\u4e8e\u5657\u55e4\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4e3a\u4e86\u4ed6\u5e08\u7236\u7684\u7f18 \u6545\u624d\u8fd9\u6837\u6068\u4ed6\u5427\uff1f\u201d\u5389\u590d\u751f\u9053\uff1a\u201c\u90a3\u4f4d\u534e\u59d1\u5a18\u4e5f\u771f\u53ef\u601c\u3002\u201d\u4ed6\u987e\u5de6\u53f3\u800c\u8a00\u4ed6\uff0c\u663e\u7136\u662f\u4e0d \u613f\u63d0\u8d77\u91d1\u4e16\u9057\u548c\u5389\u80dc\u7537\u7684\u65e7\u4e8b\u3002 \u3000\u3000\u90a3\u5973\u5b50\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5c06\u5fc3\u6bd4\u5fc3\uff0c\u6211\u5c31\u662f\u89c9\u5f97\u534e\u59d1\u5a18\u7740\u5b9e\u53ef\u601c\uff0c\u8fd9\u624d\u591a\u7ba1\u95f2\u4e8b \u7684\u3002\u201d\u5389\u590d\u751f\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u90a3\u7537\u5b50\u662f\u8c01\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u521a\u521a\u53f9\u8fc7\u53e3\u6c14\uff0c\u542c\u4ed6\u8fd9\u4e48\u4e00\u95ee\uff0c\u4e0d\u7981\u53c8\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5f53\u6211\u662f\u7cca\u91cc\u7cca \u6d82\uff0c\u4e71\u70b9\u9e33\u9e2f\u8c31\u7684\u5417\uff1f\u6211\u5f53\u7136\u662f\u65e9\u5df2\u6253\u542c\u5f97\u6e05\u695a\u7684\u4e86\u3002\u90a3\u7537\u5b50\u662f\u6c34\u4e91\u5e84\u5e84\u4e3b\u4e91\u53ec\u7684\u513f\u5b50\uff0c \u4f60\u4e0d\u77e5\u9053\u90a3\u4e91\u53ec\u8fd8\u662f\u6211\u7239\u7239\u751f\u524d\u7684\u597d\u670b\u53cb\u5462\uff01\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6697\u6697\u6b22\u559c\uff1a\u201c\u539f\u6765\u5979\u7684\u5e08\u95e8\u4e0e\u4e91\u5e84\u4e3b\u9887\u6709\u6e0a\u6e90\uff0c\u602a\u4e0d\u5f97\u5979\u5bf9\u4e91\u743c\u90a3\u4e48\u597d\uff0c \u6551\u4e86\u4ed6\u7684\u6027\u547d\uff0c\u53c8\u4e3a\u4ed6\u64ae\u5408\u826f\u7f18\u3002\u5979\u82e5\u662f\u77e5\u9053\u6211\u4e0e\u4e91\u743c\u7684\u59b9\u59b9\u540c\u6765\uff0c\u60f3\u5fc5\u4e5f\u662f\u4f1a\u6b22\u8fce\u7684\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u90a3\u5973\u5b50\u53c8\u5728\u7b11\u9053\uff1a\u201c\u5389\u53d4\u53d4\uff0c\u4f60\u53ef\u4e5f\u8981\u6211\u7ed9\u4f60\u505a\u5a92\u4e48\uff1f\u201d\u5389\u590d\u751f \u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u7389\u59d1\u5a18\uff0c\u4f60\u8bf4\u7b11\u4e86\u3002\u201d\u90a3\u5973\u5b50\u201c\u5657\u7766\u201d\u4e00\u7b11\uff0c\u5c0f\u6307\u5934\u5728\u8138\u4e0a\u4e00\u7f9e\uff0c \u8bf4\u9053\uff1a\u201c\u5389\u53d4\u53d4\u4f60\u4f55\u5fc5\u7792\u6211\uff0c\u4f60\u6562\u8bf4\u4f60\u5bf9\u5361\u5170\u59ae\u59d1\u59d1\uff0c\u4e0d\u662f\u4e00\u5fc3\u4e00\u610f\u7684\u60f3\u7740\u5979\u4e48\uff1f\u201d \u3000\u3000\u5361\u5170\u59ae\u4e43\u662f\u5929\u9b54\u6559\u4e3b\u7684\u5c0f\u540d\uff0c\u5389\u590d\u751f\u75f4\u604b\u5929\u9b54\u6559\u4e3b\u4e4b\u4e8b\uff0c\u6b66\u6797\u4e2d\u4eba\u65e9\u5df2\u8033\u8bed\u76f8\u4f20\uff0c \u4f46\u5510\u52aa\u73e0\u7a46\u5374\u8fd8\u662f\u7b2c\u4e00\u6b21\u542c\u89c1\uff0c\u5fc3\u4e2d\u6697\u6697\u8be7\u5f02\uff0c\u53c8\u6697\u6697\u597d\u7b11\uff1a\u201c\u5389\u590d\u751f\u867d\u7136\u5df2\u6709\u4e8c\u5341\u591a \u5c81\uff0c\u4f46\u884c\u4e8b\u8fd8\u4f3c\u5929\u771f\u672a\u51ff\u7684\u5c0f\u5b69\uff1b\u5929\u9b54\u6559\u4e3b\u4e0d\u4f46\u5e74\u7eaa\u6bd4\u4ed6\u5927\uff0c\u800c\u4e14\u7cbe\u660e\u3001\u8001\u7ec3\u3002 \u3000\u3000\u72e0\u8fa3\u3001\u6837\u6837\u90fd\u4e0e\u5389\u590d\u751f\u622a\u7136\u4e0d\u540c\u3002\u4efb\u8c01\u90fd\u4e0d\u80fd\u60f3\u8c61\u8fd9\u4e24\u4e2a\u4eba\u53ef\u4ee5\u914d\u6210\u4e00\u5bf9\uff0c\u4f46\u5389\u590d \u751f\u5374\u5c45\u7136\u4f1a\u7231\u4e0a\u4e86\u5979\uff0c\u7231\u60c5\u8fd9\u4e2a\u4e1c\u897f\u4e5f\u771f\u662f\u96be\u4ee5\u601d\u8bae\u7684\u4e86\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u6ee1\u8138\u5c34\u5c2c\u7684\u795e\u8272\uff0c\u4ed6\u672c\u6765\u957f\u5f97\u50cf\u4e2a\u5973\u5b50\uff0c\u8fd9\u4e00\u6765\u66f4\u663e\u5f97\u5ff8\u6029\u4e07\u72b6\uff0c\u6d3b\u50cf\u4e2a\u5bb3 \u7f9e\u7684\u95fa\u5973\u3002\u90a3\u5973\u5b50\u7b11\u9053\uff1a\u201c\u5389\u53d4\u53d4\uff0c\u4f60\u8fd9\u6b21\u5e2e\u4e86\u6211\u7684\u5927\u5fd9\uff0c\u6211\u4e5f\u8be5\u5e2e\u5e2e\u4f60\u7684\u5fd9\uff0c\u6211\u7ed9\u4f60 \u505a\u5a92\u597d\u5417\uff1f\u201d \u3000\u3000\u5389\u590d\u751f\u5ffd\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u7389\u59d1\u5a18\uff0c\u6ca1\u6709\u7528\u7684\u3002\u5979\uff0c\u5979\u4f55\u5c1d\u4e0d\u77e5\u9053\u6211\u7684\u5fc3\u4e8b\uff0c \u4f46\u5979\u8bf4\u662f\u5c06\u6211\u5f53\u4f5c\u5979\u7684\u5f1f\u5f1f\u2026\u2026\u54b3\uff0c\u6211\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u8ba8\u5979\u6b22\u5fc3\u3002\u6211\u6015\u3001\u6015\u5979\u4e0a\u4e86\u59d3\u6587\u90a3 \u4e2a\u9a97\u5b50\u7684\u5f53\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u7b11\u9053\uff0c\u201c\u4e0d\u4f1a\u7684\uff0c\u5361\u5170\u59ae\u59d1\u59d1\u7684\u5fc3\u4e8b\uff0c\u6211\u5012\u77e5\u9053\u4e0d\u5c11\u3002 \u3000\u3000\u5979\u8981\u632f\u5174\u5929\u9b54\u6559\uff0c\u60f3\u501f\u91cd\u6587\u5ef7\u58c1\u4e4b\u529b\u800c\u5df2\u3002\u6587\u5ef7\u58c1\u4eba\u54c1\u4e4b\u574f\uff0c\u5979\u662f\u65e9\u5c31\u77e5\u9053\u7684\u4e86\u3002 \u6211\u770b\uff0c\u5979\u771f\u6b63\u6b22\u559c\u7684\u8fd8\u662f\u4f60\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u8138\u4e0a\u66f4\u7ea2\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u5374\u53c8\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5979\u559c\u6012\u96be\u6d4b\uff0c\u5979\u4e5f\u4e0d\u613f\u610f\u522b\u4eba\u77e5\u9053 \u6211\u548c\u5979\u7684\u4e8b\u60c5\u3002\u7389\u59d1\u5a18\uff0c\u591a\u8c22\u4f60\u7684\u597d\u610f\uff0c\u4f46\u4f60\u8fd8\u662f\u4e0d\u8981\u3001\u4e0d\u8981\u2026\u2026\u201d \u3000\u3000\u90a3\u5973\u5b50\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u5728\u60f3\uff0c\u53c8\u4e0d\u6562\u8981\u6211\u505a\u5a92\u3002\u597d\u5427\uff0c\u90a3\u4f60\u5c31\u81ea\u5df1\u53bb\u8fdb\u884c\u5427\u3002\u6211\u6559\u4f60 \u4e00\u4e2a\u6cd5\u513f\uff0c\u4f60\u9001\u4e00\u4ef6\u5979\u6700\u559c\u6b22\u7684\u4e1c\u897f\u7ed9\u5979\uff0c\u5979\u4e00\u9ad8\u5174\u2014\u2014\u201d\u5389\u590d\u751f\u5fd9\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u5979 \u6700\u559c\u6b22\u4ec0\u4e48\uff1f\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u6211\u77e5\u9053\u5979\u6700\u7231\u60dc\u81ea\u5df1\u7684\u7ea2\u989c\uff0c\u751f\u6015\u7ea2\u989c\u8001\u53bb\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u773c\u775b\u4e00\u4eae\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u80af\u9001\u6211\u4e00\u6735\u2018\u96ea\u91cc\u7ea2\u5986\u2019\u5417\uff1f \u3000\u3000\u4e0d\u6ee1\u4f60\u8bf4\uff0c\u6211\u4e0a\u7075\u9e6b\u5cf0\u6765\uff0c\u672c\u5c31\u662f\u60f3\u91c7\u4e00\u6735\u9001\u5979\u7684\u3002\u4f46\u8fd9\u662f\u4f60\u7684\u4e1c\u897f\uff0c\u8fd9\u5947\u82b1\u53c8\u6781 \u4e3a\u96be\u5f97\uff0c\u6211\u5c31\u4e0d\u6562\u5f00\u53e3\u4e86\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\u6211\u672c\u6765\u4e5f\u8981\u9001\u4f60\u7684\u3002\u8fd9\u6b21\u5e78\u8fd0\u5f97\u5f88\uff0c\u90a3\u4e09\u8272\u5947\u82b1\u4e00\u5f00\u5c31\u662f\u4e09 \u6735\uff0c\u6211\u54e5\u54e5\u8981\u4e86\u4e00\u6735\uff0c\u6211\u8fd8\u6709\u4e24\u6735\u5462\u3002\u55ef\uff0c\u8bf4\u5230\u6211\u7684\u54e5\u54e5\uff0c\u6211\u5374\u53c8\u6709\u70b9\u62c5\u5fc3\u4e86\uff0c\u4e0d\u77e5\u4ed6 \u8fd9\u6b21\u4e8b\u60c5\u80fd\u4e0d\u80fd\u987a\u5229\uff1f\u201d\u5389\u590d\u751f\u9053\uff1a\u201c\u4e00\u5b9a\u4f1a\u987a\u5229\u7684\uff0c\u6211\u5df2\u628a\u4e00\u5934\u91d1\u6bdb\u72e1\u501f\u7ed9\u4ed6\u4e86\u3002\u201d \u90a3\u5973\u5b50\u9053\uff1a\u201c\u91d1\u6bdb\u72e1\u542c\u4ed6\u7684\u8bdd\u5417\uff1f\u201d\u5389\u590d\u751f\u9053\uff1a\u201c\u91d1\u6bdb\u72e1\u6781\u6709\u7075\u6027\uff0c\u6211\u5f53\u7740\u4f60\u54e5\u54e5\u7684\u9762\uff0c \u5429\u5490\u4e86\u5b83\uff0c\u5b83\u4e00\u5b9a\u4f1a\u542c\u8bdd\u7684\u3002\u4f60\u54e5\u54e5\u6709\u4e86\u2018\u96ea\u91cc\u7ea2\u5986\u2019\uff0c\u53c8\u6709\u91d1\u6bdb\u72e1\u542c\u4ed6\u4f7f\u5524\uff0c\u4f55\u6101\u597d \u4e8b\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u4e0d\u77e5\u9053\u4ed6\u4eec\u8bf4\u7684\u662f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4f46\u4ece\u4ed6\u4eec\u8c08\u8bdd\u4e4b\u4e2d\uff0c\u5374\u53ef\u4ee5\u77e5\u9053\u8fd9\u5973\u5b50\u548c\u5929 \u9b54\u6559\u4e3b\u751a\u4e3a\u4eb2\u8fd1\uff0c\u5bf9\u5979\u7684\u6765\u5386\u66f4\u662f\u611f\u5230\u795e\u79d8\u83ab\u607b\u3002 \u3000\u3000\u90a3\u5973\u5b50\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f46\u613f\u5982\u4f60\u6240\u8a00\u3002\u6211\u4e5f\u795d\u4f60\u597d\u4e8b\u80fd\u8c10\uff0c\u8fd9\u6735\u2018\u96ea\u91cc\u7ea2\u5986\u2019\u4f60\u62ff\u53bb \u5427\u3002\u201d\u5389\u590d\u751f\u559c\u5b5c\u5b5c\u7684\u63a5\u8fc7\u4e86\u90a3\u6735\u4e09\u8272\u5947\u82b1\uff0c\u4fbf\u5373\u5411\u90a3\u5973\u5b50\u544a\u8f9e\u3002\u90a3\u5973\u5b50\u72ec\u501a\u7a97\u524d\u3001\u770b \u5389\u590d\u751f\u7684\u80cc\u5f71\u53bb\u5f97\u8fdc\u4e86\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u613f\u5929\u4e0b\u6709\u60c5\u4eba\u90fd\u6210\u4e86\u7737\u5c5e\uff0c\u662f\u524d\u751f\u6ce8\u5b9a\u4e8b\u83ab\u9519 \u8fc7\u59fb\u7f18\u3002\u73b0\u5728\uff0c\u5c31\u53ea\u5269\u4e0b\u534e\u59d1\u5a18\u548c\u4e91\u516c\u5b50\u8fd9\u5bf9\u4e0d\u77e5\u5982\u4f55\u4e86\u3002\u54a6\uff0c\u739b\u4f9d\u8fd9\u4e2b\u5934\u53bb\u4e86\u8fd9\u4e48\u4e45\uff0c \u600e\u5730\u8fd8\u4e0d\u89c1\u56de\u6765\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6b63\u5f85\u4e0b\u6765\u4ee5\u793c\u6c42\u89c1\uff0c\u5ffd\u542c\u5f97\u91d1\u6bdb\u72e1\u543c\u58f0\u53c8\u8d77\uff0c\u4e14\u662f\u5411\u7740\u8fd9\u65b9\u5411\u800c\u6765\uff0c\u521a\u81ea \u60f3\u9053\uff1a\u201c\u5389\u590d\u751f\u600e\u7684\u53bb\u800c\u590d\u56de\uff1f\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u4e00\u5934\u91d1\u6bdb\u72e1\u5df2\u662f\u5982\u98de\u800c\u81f3\uff0c\u9a6e\u7740\u4e00\u7537 \u4e00\u5973\uff0c\u8fd9\u624d\u77e5\u9053\u662f\u53e6\u4e00\u5934\u91d1\u6bdb\u72e1\uff0c\u53e6\u5916\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u90a3\u5973\u5b50\u5927\u559c\u53eb\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u4eec\u6765\u5566\uff01\u516c\u4e3b\uff0c\u4f60\u53d7\u60ca\u4e86\u3002\u201d\u8fd9\u7537\u5b50\u662f\u90a3\u5973\u5b50\u7684\u54e5\u54e5\uff0c \u5510\u52aa\u73e0\u7a46\u65e9\u5df2\u731c\u60f3\u5f97\u5230\uff0c\u4f46\u542c\u5f97\u90a3\u5973\u5b50\u79f0\u547c\u540e\u6765\u7684\u8fd9\u4e2a\u5973\u5b50\u505a\u201c\u516c\u4e3b\u201d\uff0c\u5374\u662f\u5927\u611f\u60ca\u5947\u3002 \u6b64\u5730\u662f\u6606\u5e03\u5170\u56fd\u56fd\u571f\uff0c\u90a3\u4e48\u5f53\u7136\u662f\u6606\u5e03\u5170\u56fd\u7684\u516c\u4e3b\u4e86\uff0c\u4e00\u56fd\u7684\u516c\u4e3b\uff0c\u4e09\u66f4\u534a\u591c\uff0c\u548c\u4e00\u4e2a \u7537\u5b50\u79c1\u81ea\u9003\u5230\u8fd9\u8352\u8c37\u7684\u602a\u5c4b\u6765\uff0c\u5f53\u771f\u662f\u96be\u4ee5\u601d\u8bae\u7684\u5947\u4e8b\u3002 \u3000\u3000\u90a3\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u8fd9\u5934\u5f02\u517d\u8dd1\u5f97\u771f\u5feb\uff0c\u5c31\u4f3c\u817e\u4e91\u9a7e\u96fe\u4e00\u822c\u3002\u4e00\u7728\u8ddf\u95ee\u5c31\u628a\u90a3\u4e9b\u8ffd\u5175\u90fd \u6254\u5728\u80cc\u540e\u4e86\u3002\u201d\u90a3\u7537\u5b50\u62cd\u62cd\u91d1\u6bdb\u72e1\u7684\u80cc\u810a\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u665a\u591a\u4e8f\u4f60\u4e86\uff0c\u4f60\u81ea\u53bb\u9971\u9910\u4e00\u987f\uff0c \u518d\u8ddf\u4f60\u7684\u4e3b\u4eba\u8d70\u5427\u3002\u201d\u9063\u8d70\u4e86\u91d1\u6bdb\u72e1\uff0c\u63a5\u7740\u95ee\u4ed6\u7684\u59b9\u59b9\u9053\uff1a\u201c\u6211\u4eec\u6765\u7684\u65f6\u5019\u521a\u521a\u78b0\u5230\u5389 \u526f\u6559\u4e3b\u79bb\u5f00\uff0c\u4ed6\u4e3a\u4f55\u8fd9\u6837\u5306\u5306\u5c31\u8d70\uff1f\u201d\u90a3\u5973\u5b50\u7b11\u9053\uff1a\u201c\u4ed6\u4e5f\u50cf\u4f60\u4e00\u6837\uff0c\u8d76\u7740\u8981\u628a\u4e00\u6735 \u2018\u96ea\u91cc\u7ea2\u5986\u2019\u9001\u7ed9\u4ed6\u7684\u610f\u4e2d\u4eba\u5462\u3002\u201d \u3000\u3000\u90a3\u7537\u5b50\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u90a3\u6735\u2018\u96ea\u91cc\u7ea2\u5986\u2019\u53ef\u662f\u9001\u7ed9\u4e00\u4e2a\u6211\u4eec\u6240\u6700\u8ba8\u538c\u7684\u4eba\uff01\u201d\u90a3\u5973 \u5b50\u8be7\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e0d\u662f\u9001\u7ed3\u516c\u4e3b\uff1f\u201d\u90a3\u7537\u5b50\u9053\uff1a\u201c\u662f\u9001\u7ed9\u770b\u5b88\u516c\u4e3b\u7684\u90a3\u4e2a\u5996\u5a46\u3002\u4e0d\u8fc7\uff0c \u4e5f\u5e78\u4e8f\u9001\u4e86\u5979\u8fd9\u4efd\u539a\u793c\uff0c\u5979\u624d\u51c6\u8bb8\u6211\u89c1\u516c\u4e3b\u4e00\u9762\u3002\u201d \u3000\u3000\u4e09\u4eba\u5728\u5c4b\u5185\u5750\u5b9a\u4e4b\u540e\uff0c\u90a3\u516c\u4e3b\u5401\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u9669\uff0c\u8981\u662f\u4eca\u665a\u4f60\u54e5\u54e5\u4e0d\u6765\u6551\u6211\uff0c \u660e\u5929\u6211\u5c31\u8981\u7ed9\u4ed6\u4eec\u8feb\u5ac1\u4e86\u3002\u201d\u90a3\u7537\u5b50\u9053\uff1a \u3000\u3000\u201c\u5c3c\u6cca\u5c14\u90a3\u66b4\u541b\u88ab\u4ed6\u7684\u767e\u59d3\u63a8\u4e0b\u4e86\u5b9d\u5ea7\uff0c\u5374\u9003\u5230\u4e86\u54b1\u4eec\u56fd\u4e2d\uff0c\u8fd8\u60f3\u5174\u98ce\u4f5c\u6d6a\u5462\uff0c\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u539f\u6765\u56fd\u738b\u662f\u8981\u8feb\u4f60\u5ac1\u7ed9\u90a3\u5c3c\u6cca\u5c14\u738b\u5b50\uff1f\u201d\u90a3\u516c\u4e3b\u9053\uff1a\u201c\u4e0d\u9519\uff0c \u90a3\u5e9f\u738b\u4e49\u5b50\u4e8c\u4eba\u5e26\u4e86\u8bb8\u591a\u6b66\u58eb\u5230\u6765\uff0c\u6211\u54e5\u54e5\u548c\u4ed6\u4eec\u8ba2\u4e86\u76df\u7ea6\uff0c\u4ed6\u4eec\u5148\u5e2e\u6606\u5e03\u5170\u541e\u5e76\u9a6c\u8428 \u513f\u56fd\uff0c\u7136\u540e\u6211\u54e5\u54e5\u5e2e\u4ed6\u590d\u4f4d\u3002\u201d\u5510\u52aa\u73e0\u7a46\u542c\u4e86\u6697\u6697\u5403\u60ca\uff1a\u201c\u60f3\u4e0d\u5230\u8fd8\u6709\u8fd9\u6837\u9634\u8c0b\uff01\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u5384\u6cca\u5c14\u60c5\u5f62\u5982\u4f55\uff0c\u516c\u4e3b\u53ef\u6709\u6240\u95fb\uff1f\u7ee7\u4f4d\u7684\u65b0\u738b\u4e0d\u77e5\u662f\u8c01\uff1f\u201d\u516c\u4e3b\u7b11\u9053\uff1a \u201c\u8fd9\u4e9b\u65e5\u5b50\uff0c\u6211\u4e00\u76f4\u90fd\u662f\u95f7\u6079\u6079\u7684\uff0c\u5bf9\u4ec0\u4e48\u90fd\u5931\u4e86\u5174\u8da3\uff0c\u54ea\u6709\u5fc3\u60c5\u6253\u542c\u5c3c\u6cca\u5c14\u7684\u60c5\u5f62\uff1f \u597d\u59b9\u5b50\uff0c\u4f60\u4e3a\u4f55\u8981\u77e5\u9053\u65b0\u738b\u662f\u8c01\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u60f3\u77e5\u9053\u4ed6\u662f\u4e0d\u662f\u6c49\u4eba\uff1f\u201d\u516c\u4e3b\u7b11\u9053\uff1a \u3000\u3000\u201c\u54ea\u6709\u8fd9\u6837\u7684\u602a\u4e8b\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u662f\u7684\uff01\u5018\u4f7f\u771f\u662f\u6c49\u4eba\u505a\u4e86\u5c3c\u6cca\u5c14\u7684\u65b0\u7389\uff0c\u5bab\u4e2d\u65e9\u5c31\u5f53 \u4f5c\u65b0\u9c9c\u4e8b\u513f\u5230\u5904\u8c08\u8bb2\u4e86\uff0c\u8fd8\u7528\u5f97\u7740\u6211\u6253\u542c\u5417\uff1f\u201d\u90a3\u7537\u5b50\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u4f60\u53ef\u662f\u53c8\u5728\u60f3\u5ff5\u5510 \u52a0\u6e90\u4e86\uff1f\u201d\u516c\u4e3b\u9053\uff1a\u201c\u5510\u52a0\u6e90\u662f\u8c01\uff1f\u201d \u3000\u3000\u90a3\u7537\u5b50\u9053\uff1a\u201c\u662f\u5510\u7ecf\u5929\u548c\u51b0\u5ddd\u5929\u5973\u7684\u513f\u5b50\uff0c\u51b0\u5ddd\u5929\u5973\u672c\u662f\u5c3c\u6cca\u5c14\u7684\u516c\u4e3b\u3002\u5510\u52a0\u6e90\u4e09 \u5e74\u524d\u5230\u4e86\u5c3c\u6cca\u5c14\uff0c\u542c\u8bf4\u5c3c\u6cca\u5c14\u767e\u59d3\u8fd9\u6b21\u63a8\u7ffb\u66b4\u541b\uff0c\u5c31\u662f\u5510\u52a0\u6e90\u9996\u5148\u53d1\u96be\u7684\u3002\u4e09\u5e74\u524d\u4ed6\u8def \u8fc7\u6b64\u5730\uff0c\u59b9\u59b9\u66fe\u4e0e\u4ed6\u6709\u4e00\u9762\u4e4b\u7f18\uff0c\u81ea\u4ece\u89c1\u4e86\u4e00\u6b21\u9762\u540e\uff0c\u59b9\u59b9\u5c31\u5fd8\u4e0d\u4e86\u4ed6\u4e86\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u988a\u4e0a\u73b0\u51fa\u4e00\u7247\u7ea2\u6655\uff0c\u55d4\u9053\uff1a\u201c\u6211\u53ea\u662f\u5173\u5fc3\u4e00\u4e2a\u670b\u53cb\uff0c\u4f60\u5374\u62ff\u6765\u53d6\u7b11\u4e86\u3002\u201d\u5979 \u54e5\u54e5\u7b11\u9053\uff1a\u201c\u516c\u4e3b\u8bf4\u5f97\u6709\u7406\uff0c\u5c3c\u6cca\u5c14\u7684\u65b0\u738b\u7edd\u4e0d\u4f1a\u662f\u5510\u52a0\u6e90\uff0c\u90a3\u4f60\u5c31\u53ef\u4ee5\u653e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u7b11\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u54b1\u4eec\u8fd9\u91cc\u6765\u4e86\u4e00\u4e2a\u5ba2\u4eba\uff0c\u4f60\u4e00\u5411\u4e5f\u5f88\u60e6\u8bb0\u4ed6\u7684\uff0c\u4f60\u53ef\u731c\u5f97\u5230 \u662f\u8c01\uff1f\u201d\u90a3\u7537\u5b50\u7b11\u9053\uff1a\u201c\u6211\u6700\u6015\u731c\u54d1\u8c1c\uff0c\u8fd8\u662f\u4f60\u8bf4\u51fa\u6765\u5427\uff0c\u662f\u8c01\uff1f\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u662f\u6c34 \u4e91\u5e84\u7684\u5c11\u5e84\u4e3b\u4e91\u743c\uff0c\u90a3\u5e74\u4f60\u5949\u7239\u7239\u4e4b\u547d\uff0c\u5230\u6c34\u4e91\u5e84\u53bb\u62dc\u89c1\u4e91\u8001\u524d\u8f88\uff0c\u56de\u6765\u4e4b\u540e\u4e0d\u662f\u5e38\u5e38 \u548c\u6211\u63d0\u53ca\u90a3\u4e91\u5c11\u5e84\u4e3b\u4e48\uff1f\u201d\u90a3\u7537\u5b50\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u662f\u7ed9\u4f60\u505a\u5a92\u7684\uff0c\u4f46\u4f60\u5df2\u53e6\u6709\u4e86\u610f\u4e2d\u4eba\uff0c \u6211\u53ea\u597d\u4e0d\u63d0\u4e86\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u7b11\u9053\uff1a\u201c\u4f60\u600e\u4e5f\u731c\u60f3\u4e0d\u5230\uff0c\u8fd9\u56de\u5374\u662f\u6211\u7ed9\u4ed6\u505a\u5a92\u5462\u3002\u201d\u90a3\u7537\u5b50\u9053\uff1a\u201c\u8fd9\u662f\u600e \u4e48\u56de\u4e8b\uff1f\u4e91\u743c\u600e\u5730\u4f1a\u5230\u6b64\u95ee\uff0c\u4f60\u7ed9\u4ed6\u505a\u5a92\uff0c\u90a3\u5973\u7684\u53c8\u662f\u8c01\uff1f\u201d\u4ed6\u59b9\u59b9\u9053\uff1a\u201c\u4e91\u743c\u9677\u5165\u51b0 \u6cb3\uff0c\u6211\u5c06\u4ed6\u6551\u4e86\u8d77\u6765\u3002 \u3000\u3000\u73b0\u5728\u6211\u5c06\u4ed6\u653e\u5728\u534e\u59d1\u5a18\u7684\u623f\u91cc\u3002\u201d\u90a3\u7537\u5b50\u9053\uff1a\u201c\u534e\u59d1\u5a18\u5df2\u7ecf\u597d\u4e86\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5973\u7684\u9053\uff1a\u201c\u6211\u6628\u5929\u5df2\u5c06\u5979\u4ece\u5c71\u4e0a\u642c\u5230\u6b64\u5730\uff0c\u6309\u7167\u89e3\u836f\u7684\u7597\u6548\u6765\u8bf4\uff0c\u5979\u4eca\u665a\u5e94\u8be5\u6e05 \u9192\uff0c\u8fd9\u6b21\u6e05\u9192\u4e4b\u540e\uff0c\u5bd2\u6bd2\u5c31\u4e0d\u4f1a\u518d\u53d1\u4e86\u3002 \u3000\u3000\u5979\u4e00\u9192\u6765\uff0c\u5c31\u53d1\u73b0\u4e91\u743c\u5728\u5979\u8eab\u8fb9\uff0c\u4e0d\u77e5\u8be5\u591a\u8be7\u5f02\u5462\uff01\u201d\u8bf4\u7740\u5495\u5495\u5730\u7b11\u4e86\u8d77\u6765\uff0c\u751a\u662f \u5f97\u610f\u3002 \u3000\u3000\u5979\u54e5\u54e5\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u73a9\u7b11\u4e5f\u672a\u514d\u5f00\u5f97\u592a\u8352\u5510\u4e86\uff01\u201d\u90a3\u5973\u7684\u9053\uff1a\u201c\u534e\u59d1\u5a18 \u4e5f\u771f\u53ef\u601c\uff0c\u8fd9\u51e0\u65e5\u6211\u4e00\u76f4\u5728\u5e8a\u524d\u770b\u62a4\u5979\uff0c\u542c\u5f97\u5979\u5728\u68a6\u4e2d\u7684\u8be3\u8bed\uff0c\u8fd8\u662f\u5fd8\u8bb0\u4e0d\u4e86\u90a3\u59d3\u6c5f\u7684 \u5c0f\u5b50\u3002\u4f46\u4ece\u5979\u7684\u6a90\u8bed\u542c\u6765\uff0c\u90a3\u59d3\u6c5f\u7684\u5c0f\u5b50\u5374\u53c8\u53ea\u77e5\u9053\u9a6c\u8428\u513f\u56fd\u7684\u516c\u4e3b\u5462\u3002\u6211\u5c31\u662f\u89c1\u5979\u53ef \u601c\uff0c\u5b81\u613f\u7ed9\u4f60\u8bf4\u6211\u8352\u5510\uff0c\u6211\u4e5f\u8981\u8bd5\u4e00\u8bd5\u505a\u8fd9\u4e2a\u5a92\u4e86\u3002\u201d \u3000\u3000\u90a3\u516c\u4e3b\u4e0d\u77e5\u9053\u4e91\u743c\u3001\u534e\u59d1\u5a18\u8fd9\u4e9b\u4eba\u662f\u8c01\uff0c\u4e00\u76f4\u6ca1\u6709\u8bf4\u8bdd\uff0c\u8fd9\u65f6\u5ffd\u5730\u8be7\u9053\uff1a\u201c\u4f60\u8bf4\u7684 \u662f\u9a6c\u8428\u513f\u56fd\u90a3\u4f4d\u6c49\u540d\u53eb\u505a\u8c37\u4e2d\u83b2\u7684\u516c\u4e3b\u5417\uff1f\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u516c\u4e3b\uff0c\u6211\u6b63\u60f3\u95ee\u4f60\uff0c \u90a3\u4f4d\u9a6c\u8428\u513f\u56fd\u7684\u516c\u4e3b\u73b0\u5728\u662f\u5982\u4f55\u4e86\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u542c\u4ed6\u4eec\u8c08\u53ca\u81ea\u5df1\u7684\u59b9\u59b9\uff0c\u683c\u5916\u7559\u795e\u3002\u90a3\u516c\u4e3b\u9053\uff1a \u3000\u3000\u201c\u6211\u54e5\u54e5\u610f\u56fe\u5c06\u5979\u4f5c\u4e3a\u4eba\u8d28\uff0c\u4e0d\u6218\u800c\u5c48\u9a6c\u8428\u513f\u56fd\u4e4b\u5175\uff0c\u8d77\u521d\u5c06\u5979\u56da\u5728\u51b7\u5bab\uff0c\u540e\u6765\u7ed9 \u6211\u7237\u7237\u77e5\u9053\u4e86\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u4ed6\u8001\u4eba\u5bb6\u5bf9\u8fd9\u4f4d\u516c\u4e3b\u662f\u975e\u5e38\u601c\u60dc\uff0c\u4eb2\u81ea\u6253\u5f00\u4e86\u51b7\u5bab\uff0c\u5c06\u5979\u653e \u4e86\u51fa\u6765\u3001\u8fd8\u8ba4\u5979\u4f5c\u5b59\u5973\u513f\u5462\u3002\u6211\u54e5\u54e5\u6ca1\u6709\u529e\u6cd5\uff0c\u53ea\u80fd\u4e25\u52a0\u9632\u8303\uff0c\u9664\u4e86\u4e0d\u8bb8\u5979\u8d70\u51fa\u5bab\u5916\uff0c \u5728\u5bab\u91cc\u5934\u5012\u662f\u51c6\u5979\u968f\u610f\u884c\u52a8\u7684\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5fc3\u5934\u5927\u9707\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u59b9\u59b9\u662f\u5df2\u7ed9\u4ed6\u4eec\u6349\u4f4f\u4e86\u3002\u201d\u540c\u65f6\u53c8\u89c9\u5f97\u5947\u602a\uff0c \u201c\u6606\u5e03\u5170\u56fd\u7684\u592a\u4e0a\u7687\u4e3a\u4ec0\u4e48\u5bf9\u5979\u8fd9\u4e48\u597d\uff1f\u59b9\u59b9\u6b66\u529f\u4e0d\u5f31\uff0c\u5979\u65e2\u80fd\u81ea\u7531\u884c\u52a8\uff0c\u53c8\u4f55\u4ee5\u9003\u4e0d \u51fa\u6765\uff1f\u96be\u9053\u4ed6\u4eec\u56fd\u4e2d\u4e5f\u6709\u4e00\u4e2a\u50cf\u5b9d\u8c61\u6cd5\u5e08\u90a3\u6837\u7684\u80fd\u4eba\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u90a3\u516c\u4e3b\u53c8\u9053\uff1a\u201c\u6211\u66fe\u548c\u8fd9\u4f4d\u9a6c\u8428\u513f\u56fd\u7684\u516c\u4e3b\u89c1\u8fc7\u9762\uff0c\u8c08\u5f97\u751a\u662f\u6295\u673a\uff0c\u6211\u7237\u7237 \u8ba4\u5979\u4f5c\u5b59\u5973\u513f\uff0c\u6211\u4e5f\u548c\u5979\u8ba4\u4e86\u59d0\u59b9\u5462\u3002\u5979\u66fe\u6258\u6211\u5077\u5077\u7ed9\u5979\u5e26\u4e2a\u6d88\u606f\u7ed9\u5979\u7684\u54e5\u54e5\u2014\u2014\u9a6c\u8428 \u513f\u56fd\u7684\u56fd\u738b\uff0c\u53ef\u662f\u5979\u5374\u4e0d\u77e5\u6211\u4e5f\u662f\u88ab\u54e5\u54e5\u8f6f\u7981\uff0c\u5904\u5883\u6bd4\u5979\u66f4\u4e0d\u81ea\u7531\u3002\u5f53\u65f6\u6211\u4e0d\u6562\u7b54\u5e94\uff0c \u5982\u4eca\u6211\u5df2\u7ecf\u51fa\u6765\u4e86\uff0c\u5012\u60f3\u4e3a\u5979\u9001\u4e00\u4e2a\u53e3\u4fe1\u4e86\u3002\u201d\u90a3\u7537\u5b50\u7b11\u9053\uff1a\u201c\u8fd9\u8fd8\u4e0d\u5bb9\u6613\u5417\uff1f\u6211\u9a91\u91d1 \u6bdb\u72e1\u53bb\uff0c\u660e\u5929\u5c31\u53ef\u4ee5\u5230\u9a6c\u8428\u513f\u56ed\u7684\u4eac\u57ce\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6697\u6697\u597d\u7b11\uff1a\u201c\u6211\u65e9\u5df2\u7ecf\u6765\u4e86\u3002\u201d\u6b63\u8981\u4e0b\u53bb\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u6025\u4fc3\u5c16 \u9510\u7684\u89d2\u58f0\uff0c\u6709\u4eba\u53eb\u9053\uff1a\u201c\u6709\u8d3c\u4eba\u5077\u8fdb\u56ed\u5b50\uff0c\u5feb\u6765\u6349\u8d3c\uff01\u201d\u539f\u6765\u88ab\u5510\u52aa\u73e0\u7a46\u62a2\u4e86\u725b\u8089\uff0c\u70b9 \u4e86\u7a74\u9053\u7684\u90a3\u4e2a\u4eba\uff0c\u5df2\u7ed9\u5de1\u591c\u7684\u4ec6\u4eba\u53d1\u73b0\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u662f\u2026\u2026\u201d\u4e00\u4e2a\u201c\u8d3c\u201d\u5b57\u8fd8\u6765\u8bf4\u5f97\u51fa\u53e3\uff0c\u53ea\u542c\u5f97\u91d1\u6bdb\u72e1\u4e00\u58f0\u5927 \u543c\uff0c\u5e26\u8d77\u4e86\u4e00\u80a1\u72c2\u98ce\uff0c\u5df2\u662f\u6251\u4e0a\u6811\u6765\u3002\u5510\u52aa\u73e0\u7a46\u53ea\u5f97\u4e00\u638c\u62cd\u51fa\uff0c\u91d1\u6bdb\u72e1\u867d\u7136\u5389\u5bb3\uff0c\u5374\u600e \u654c\u5f97\u4ed6\u7684\u795e\u529b\uff0c\u5510\u52aa\u73e0\u7a46\u4e0d\u60f3\u4f24\u5bb3\u5b83\uff0c\u53ea\u7528\u4e86\u4e94\u516d\u5206\u6c14\u529b\uff0c\u53ea\u4e00\u638c\u5c31\u628a\u5b83\u6253\u4e86\u4e0b\u53bb\uff0c\u4f46 \u91d1\u6bdb\u72e1\u7684\u524d\u722a\u4ea6\u5df2\u6495\u7834\u4e86\u4ed6\u7684\u88e4\u7ba1\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u4e00\u8dc3\u800c\u4e0b\uff0c\u7acb\u8db3\u672a\u7a33\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c4b\u5185\u90a3\u7537\u5b50\u65e9\u5df2\u6251\u6765\uff0c\u4e94\u6307\u5982\u94a9\uff0c \u5411\u5510\u52aa\u73e0\u7a46\u6402\u5934\u4fbf\u6293\uff01\u8fd9\u4e00\u6293\u52b2\u9053\u5341\u8db3\uff0c\u5510\u52aa\u73e0\u7a46\u4ee5\u5c0f\u661f\u638c\u529b\u62e8\u5f00\uff0c\u56e0\u4ed6\u672a\u7528\u5168\u529b\uff0c\u8fd9 \u4e00\u6293\u867d\u662f\u62e8\u5f00\u3002\u624b\u8155\u4ea6\u5df2\u9690\u9690\u4f5c\u75db\u3002\u5fc3\u5934\u5fae\u51db\uff1a\u201c\u8fd9\u4eba\u7684\u672c\u9886\u5927\u662f\u4e0d\u5f31\u3002\u201d \u3000\u3000\u90a3\u7537\u5b50\u559d\u9053\uff1a\u201c\u4f60\u60f3\u628a\u516c\u4e3b\u518d\u62a2\u56de\u53bb\uff0c\u4e07\u4e07\u4e0d\u80fd\uff01\u201d \u3000\u3000\u539f\u6765\u4ed6\u4ee5\u4e3a\u5510\u52aa\u73e0\u7a46\u4e43\u662f\u672c\u56fd\u56fd\u738b\u6d3e\u6765\u7684\u6b66\u58eb\uff0c\u7acb\u5373\u4e00\u4e2a\u201c\u53cc\u649e\u638c\u201d\u63a8\u51fa\uff0c\u4ed6\u662f\u5728 \u51b0\u5cf0\u4e0a\u7ec3\u7684\u5185\u529f\uff0c\u638c\u98ce\u53d1\u51fa\u3001\u5bd2\u610f\u88ad\u4eba\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u4e0d\u6562\u6020\u6162\uff0c\u8fd8\u4e86\u4e00\u638c\uff0c\u8fd9\u4e00\u638c\u7528\u5230\u4e86\u516b\u6210\u529f\u529b\uff0c\u6070\u5230\u597d\u5904\uff0c\u5c06\u90a3\u7537\u5b50\u9707\u5f97 \u8fde\u9000\u4e09\u6b65\uff0c\u5374\u672a\u8dcc\u5230\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6765\u62a2\u4f60\u7684\u516c\u4e3b\u7684\uff0c\u6211\u662f\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u738b\uff01\u6211\u5df2\u7ecf\u6765\u4e86\u3001 \u4e0d\u7528\u4f60\u9001\u4fe1\u4e86\u3002\u201d\u90a3\u7537\u5b50\u559d\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u738b\u600e\u4f1a\u6765\u5230\u6b64\u95f4\uff1f\u201d\u7adf\u7136\u4e0d \u4fe1\uff0c\u53c8\u662f\u4e00\u6293\u6293\u6765\uff01 \u3000\u3000\u90a3\u6606\u5e03\u5170\u56fd\u7684\u516c\u4e3b\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u5927\u54e5\u4f4f\u624b\uff0c\u5feb\u5feb\u8d42\u7f6a\uff01\u201d\u90a3\u7537\u5b50\u6b63\u8981\u4e00\u6293\u6293\u4e0b\uff0c\u95fb \u8bed\u6115\u7136\uff0c\u4e2d\u9014\u505c\u6b62\uff0c\u6781\u662f\u5c34\u5c2c\uff0c\u95ee\u9053\uff1a \u3000\u3000\u201c\u4ed6\u5f53\u771f\u662f\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u738b\u4e48\uff1f\u201d\u5fc3\u91cc\u4ecd\u662f\u4e0d\u6562\u76f8\u4fe1\uff0c\u5bfb\u601d\uff1a\u201c\u516c\u4e3b\u51e0\u65f6\u89c1\u8fc7\u9a6c\u8428 \u513f\u56fd\u7684\u56fd\u738b\uff1f\u5979\u600e\u4e48\u77e5\u9053\uff1f\u201d\u53ea\u89c1\u516c\u4e3b\u5df2\u662f\u8f7b\u79fb\u83b2\u6b65\uff0c\u8d70\u4e0a\u524d\u6765\uff0c\u76c8\u76c8\u4e0b\u62dc\u3002\u90a3\u7537\u5b50\u8fd9 \u624d\u614c\u5fd9\u65bd\u672d\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u965b\u4e0b\u9a7e\u4e34\uff0c\u591a\u6709\u5192\u72af\u4e86\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u5fae\u5fae\u4e00\u7b11\uff0c\u53cc\u62f3\u4e00\u62f1\uff0c\u4f5c\u4e2a\u624b\u52bf\uff0c\u5411\u4e0a\u4e00\u6258\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5192\u6627\u767b\u95e8\uff0c\u5931\u793c \u65e0\u4eea\uff0c\u8fd8\u5f97\u8bf7\u4f60\u4eec\u4e0d\u8981\u89c1\u602a\u5462\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u662f\u843d\u96be\u6c42\u63f4\u4e4b\u4eba\uff0c\u5e76\u975e\u4ee5\u56fd\u738b\u7684\u8eab\u4efd\u6765\u7684\u3002 \u54b1\u4eec\u53ea\u5b9c\u53d9\u5bbe\u4e3b\u4e4b\u8c0a\uff0c\u51b3\u4e0d\u53ef\u884c\u541b\u81e3\u4e4b\u672d\uff0c\u8fc7\u4efd\u5ba2\u6c14\uff0c\u90a3\u5c31\u53cd\u800c\u89c1\u5916\u4e86\u3002\u8bf7\u8d77\u6765\u5427\u3002\u201d \u4ed6\u53ea\u662f\u4f5c\u52bf\u6b32\u6276\uff0c\u4e95\u6ca1\u6709\u89e6\u7740\u90a3\u7537\u5b50\u7684\u8eab\u4f53\uff0c\u90a3\u7537\u5b50\u5df2\u662f\u89c9\u5f97\u4e00\u80a1\u5927\u529b\u5c06\u4ed6\u6258\u4e86\u8d77\u6765\uff0c \u516c\u4e3b\u4e5f\u662f\u67f3\u8170\u5fae\u5f2f\uff0c\u4fbf\u7ed9\u90a3\u80a1\u65e0\u5f62\u7684\u529b\u91cf\u6240\u963b\uff0c\u62dc\u4e0d\u4e0b\u53bb\u3002\u90a3\u7537\u5b50\u4e43\u662f\u884c\u5bb6\uff0c\u8fd9\u4e00\u6765\u66f4 \u662f\u60ca\u5947\u4e0d\u5df2\uff1a\u201c\u60f3\u4e0d\u5230\u4e00\u4e2a\u56fd\u738b\uff0c\u7adf\u7136\u6709\u6b64\u7edd\u4e16\u795e\u529f\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u516c\u4e3b\u53ef\u662f\u89c9\u5f97\u6211\u597d\u751f\u9762\u719f\u4e48\uff1f\u201d\u90a3\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u6b63\u662f\u5462\uff0c\u965b\u4e0b\u548c \u5fa1\u59b9\u7b80\u76f4\u662f\u4e00\u6a21\u4e00\u6837\u3002\u201d\u90a3\u7537\u5b50\u8fd9\u624d\u660e\u767d\uff0c\u516c\u4e3b\u539f\u6765\u662f\u7531\u4e8e\u5510\u52aa\u73e0\u7a46\u5144\u59b9\u7684\u76f8\u8c8c\u76f8\u540c\uff0c \u65ad\u5b9a\u4ed6\u662f\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u738b\u7684\u3002 \u3000\u3000\u90a3\u767d\u8863\u5973\u5b50\u4e5f\u8d70\u4e86\u51fa\u6765\uff0c\u5c31\u4f3c\u516c\u4e3b\u521a\u624d\u6a21\u6837\uff0c\u76ee\u4e0d\u8f6c\u775b\u7684\u6253\u91cf\u5510\u52aa\u73e0\u7a46\u3002\u5510\u52aa\u73e0\u7a46 \u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u54b1\u4eec\u53c8\u5728\u6b64\u5904\u76f8\u9022\uff0c\u591a\u8c22\u4f60\u6551\u4e86\u6211\u7684\u4e24\u4e2a\u670b\u53cb\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u4f60\u5c31\u662f\u548c\u6c5f\u6d77\u5929\u6628\u65e5\u540c\u4e0a\u7075\u9e6b\u5cf0\u7684\u90a3\u4e2a\u4eba\u3002\u201d\u5ffd\u5730\u60f3\u8d77\u4ed6\u662f \u56fd\u738b\u8eab\u4efd\uff0c\u201c\u4f60\u201d\u201c\u6211\u201d\u76f8\u79f0\uff0c\u5927\u662f\u4e0d\u656c\uff0c\u8fde\u5fd9\u6539\u4e86\u79f0\u547c\uff0c\u4e00\u65bd\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u6628\u65e5\u591a\u6709 \u4e0d\u77e5\uff0c\u8fd8\u8bf7\u965b\u4e0b\u6055\u7f6a\u3002\u201d\u5510\u52aa\u73e0\u7a46\u91cd\u65bd\u6545\u6280\u3002\u4ee5\u65e0\u5f62\u7f61\u6c14\u963b\u6b62\u5979\u4e0b\u62dc\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u80af\u62db \u5f85\u6211\u8fd9\u4e2a\u4e0d\u901f\u4e4b\u5ba2\uff0c\u6211\u5df2\u662f\u611f\u6fc0\u4e0d\u5c3d\u3002\u8981\u662f\u4f60\u4eec\u4e0d\u5acc\u5f03\u7684\u540e\uff0c\u628a\u6211\u5f53\u4f5c\u670b\u53cb\u5982\u4f55\uff1f\u6211\u540d \u53eb\u5510\u52aa\u73e0\u7a46\uff0c\u6211\u59b9\u59b9\u53eb\u8c37\u4e2d\u83b2\uff0c\u4ec0\u4e48\u965b\u4e0b\u5fa1\u59b9\u7684\u79f0\u53f7\uff0c\u90fd\u8bf7\u6536\u8d77\u6765\u5427\uff0c\u90a3\u4e9b\u79f0\u547c\u4e0d\u662f\u5927 \u4fd7\u6c14\u4e86\u5417\uff1f\u201d \u3000\u3000\u90a3\u4e9b\u4ec6\u4eba\u8fd9\u65f6\u65b9\u59cb\u8d76\u5230\uff0c\u6709\u4e00\u4e2a\u5e26\u7740\u51e0\u5206\u50bb\u6c14\u7684\u5192\u5192\u5931\u5931\u5c31\u53eb\u8d77\u6765\u9053\uff1a\u201c\u8d3c\u4eba\u5728\u8fd9 \u91cc\u4e86\uff01\u201d\u90a3\u5973\u5b50\u559d\u9053\uff1a\u201c\u522b\u80e1\u8bf4\uff0c\u8fd9\u4f4d\u662f\uff0c\u662f\u2026\u2026\u6211\u4eec\u7684\u670b\u53cb\u3002\u4f60\u4eec\u9000\u4e0b\u53bb\u5427\u3002\u201d\u5510\u52aa \u73e0\u7a46\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u5bf9\u4e86\uff0c\u591a\u8c22\u4f60\u4eec\u5c06\u6211\u5f53\u4f5c\u670b\u53cb\u3002\u201d \u3000\u3000\u8fd9\u5bf9\u5144\u59b9\u548c\u6606\u5e02\u5170\u56fd\u7684\u516c\u4e3b\u89c1\u5510\u52aa\u73e0\u7a46\u8a00\u8bed\u8c26\u548c\uff0c\u4e3e\u6b62\u6d12\u8131\uff0c\u4e1d\u6beb\u6ca1\u6709\u56fd\u738b\u7684\u67b6\u5b50\uff0c \u5927\u5bb6\u90fd\u5f88\u9ad8\u5174\u3002\u6606\u5e03\u5170\u56fd\u7684\u516c\u4e3b\u7b11\u9053\uff0c\u201c\u6211\u73b0\u5728\u4e5f\u4e0d\u662f\u516c\u4e3b\u4e86\uff0c\u6211\u540d\u53eb\u7f57\u68a6\u5a1c\u3002\u201d\u90a3\u7537 \u7684\u9053\uff1a\u201c\u6211\u540d\u53eb\u7389\u6606\u4ed1\u3002\u6211\u59b9\u59b9\u53eb\u7389\u73b2\u73d1\u3002\u201d\u8fd9\u4e24\u4e2a\u540d\u5b57\u751a\u662f\u7279\u522b\uff0c\u5510\u52aa\u73e0\u7a46\u5ffd\u5730\u5fc3\u5934 \u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u6709\u4e00\u4f4d\u6b66\u6797\u524d\u8f88\uff0c\u4eba\u79f0\u6606\u4ed1\u9690\u4fa0\u7684\u7389\u9e23\u73c2\uff0c\u4e0d\u77e5\u53ef\u662f\u7389\u5144\u7684\u672c\u5bb6\u4e48\uff1f\u201d \u7389\u6606\u4ed1\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6b63\u662f\u5bb6\u7236\u3002\u965b\u4e0b\u5982\u4f55\u77e5\u9053\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u505a\u8fd9\u56fd\u738b\uff0c\u4e0d\u8fc7\u662f\u6700\u8fd1\u7684\u4e8b\uff0c\u8fd8\u4e0d\u5230\u4e00\u4e2a\u6708\u65f6\u95f4\uff0c\u5728\u6b64\u4e4b\u524d \u4e00\u76f4\u662f\u8ddf\u5bb6\u5e08\u5b66\u827a\uff0c\u52c9\u5f3a\u7b97\u5f97\u662f\u6b66\u6797\u4e2d\u4eba\u3002\u201d\u7389\u6606\u4ed1\u9053\uff1a\u201c\u4ee4\u5e08\u662f\u2014\u2014\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a \u201c\u6211\u8ddf\u6c5f\u6d77\u5929\u662f\u540c\u5bf8\u4e2a\u5e08\u7236\uff0c\u7389\u59d1\u5a18\u60f3\u5fc5\u77e5\u9053\u662f\u8c01\u4e86\u3002\u201d\u7389\u73b2\u73d1\u5927\u4e3a\u60ca\u8be7\uff0c\u8bf4\u9053\u201c\u539f\u6765 \u91d1\u3001\u91d1\u5927\u4fa0\u91d1\u4e16\u9057\u5c31\u662f\u965b\u4e0b\u7684\u5e08\u7236\uff0c\u602a\u4e0d\u5f97\u965b\u4e0b\u6b66\u529f\u5982\u6b64\u9ad8\u660e\u3002\u201d\u7389\u73b2\u73d1\u5728\u7075\u9e6b\u5cf0\u9a82\u8fc7 \u6c5f\u6d77\u5929\uff0c\u8fde\u5e26\u628a\u91d1\u4e16\u9057\u4e5f\u5632\u8bbd\u4e86\uff0c\u5982\u4eca\u542c\u5f97\u5510\u52aa\u73e0\u7a46\u4e5f\u662f\u91d1\u4e16\u9057\u7684\u5f1f\u5b50\uff0c\u9887\u89c9\u5c34\u5c2c\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u5f7c\u6b64\u90fd\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u66f4\u5e94\u8be5\u4e0d\u62d8\u793c\u8282\u4e86\uff0c\u600e\u7684\u4f60\u4eec\u8fd8\u662f\u4ee5\u965b\u4e0b\u76f8 \u79f0\uff1f\u201d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5bb6\u5e08\u8db3\u8ff9\u904d\u5929\u4e0b\uff0c\u4ed6\u6700\u559c\u6b22\u7ed3\u8bc6\u6b66\u6797\u9ad8\u58eb\u3002\u542c\u8bf4\u4ee4\u5c0a\u6614\u5e74\u66fe\u4e0e\u85cf\u8fb9 \u7684\u5927\u9b54\u5934\u8d64\u795e\u5b50\u6076\u6597\u8fc7\u4e00\u573a\uff0c\u90a3\u5927\u9b54\u5934\u4e5f\u8d25\u5728\u4ee4\u5c0a\u624b\u4e0b\u3002\u5bb6\u5e08\u77e5\u9053\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u751a\u4e3a\u94a6\u4f69\uff0c \u610f\u56fe\u7ed3\u8bc6\uff0c\u66fe\u4e09\u4e0a\u6606\u4ed1\u62dc\u8bbf\uff0c\u53ef\u60dc\u59cb\u7ec8\u6ca1\u6709\u9047\u4e0a\u4ee4\u5c0a\u3002\u201d \u3000\u3000\u7389\u6606\u4ed1\u6027\u60c5\u76f4\u723d\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u965b\u4e0b\u540c\u5c5e\u6b66\u6797\u4e2d\u4eba\uff0c\u4e0d\u9ad8\u5174\u6211\u7528\u4e16\u4fd7\u7684\u79f0\u547c\uff0c\u90a3\u5c31 \u8bf7\u6055\u50ed\u8d8a\uff0c\u53eb\u4f60\u4e00\u58f0\u73e0\u7a46\u5927\u54e5\u5427\u3002\u73e0\u7a46\u5927\u54e5\uff0c\u5b9e\u4e0d\u76f8\u7792\uff0c\u4f60\u521a\u624d\u6240\u8bf4\u90a3\u4ef6\u4e8b\u60c5\uff0c\u6709\u662f\u6709 \u7684\uff0c\u53ea\u662f\u4ee4\u5e08\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d\u77e5\u5176\u4e8c\uff0c\u4ee4\u5e08\u662f\u5f53\u4eca\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\uff0c\u636e\u5bb6\u7236\u8a00\u9053\uff0c\u8d64\u795e\u5b50\u90a3 \u5927\u9b54\u5934\u540e\u6765\u4e5f\u662f\u4ee4\u5e08\u4e0e\u51b0\u5ddd\u5929\u5973\u9664\u6389\u7684\uff08\u4e8b\u8be6\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\uff09\uff0c\u4f60\u662f\u91d1\u5927\u4fa0\u7684\u5f1f\u5b50\uff0c \u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4e5f\u5c31\u4e0d\u6015\u5bf9\u4f60\u8bf4\u4e86\u3002\u201d\u5510\u52aa\u73e0\u7a46\u5fd9\u9053\uff1a\u201c\u8981\u662f\u4e0d\u65b9\u4fbf\u8bf4\uff0c\u90a3\u5c31\u4e0d\u8bf4\u4e5f\u7f62\u3002\u201d \u3000\u3000\u5176\u5b9e\u5510\u52aa\u73e0\u7a46\u4e5f\u4e0d\u60f3\u77e5\u9053\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u53ea\u662f\u4e3a\u4e86\u793c\u8c8c\u5173\u7cfb\uff0c\u8fd9\u624d\u63d0\u53ca\u81ea\u5df1\u7684\u5e08\u7236\u66fe\u4e09 \u4e0a\u6606\u4ed1\uff0c\u62dc\u8bbf\u7389\u9e23\u73c2\u4e4b\u4e8b\uff0c\u4ee5\u8868\u793a\u5bf9\u4ed6\u4eec\u7236\u4eb2\u7684\u5c0a\u656c\u3002\u4e0d\u6599\u5374\u60f9\u51fa\u4e86\u7389\u6606\u4ed1\u7684\u4e00\u6bb5\u8bdd\u6765\uff0c \u5510\u52aa\u73e0\u7a46\u672c\u60f3\u5c3d\u5feb\u79fb\u8f6c\u8bdd\u9898\u3002\u95ee\u81ea\u5df1\u59b9\u59b9\u7684\u6d88\u606f\u7684\uff0c\u4e5f\u53ea\u597d\u770b\u6401\u4e00\u8fb9\u4e86\u3002 \u3000\u3000\u7389\u73b2\u73d1\u5df2\u5728\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u4e5f\u6ca1\u6709\u4ec0\u4e48\uff0c\u6b66\u6797\u4e2d\u80dc\u8d1f\u4e8b\u5c5e\u5bfb\u5e38\uff0c\u4f55\u51b5\u5bf9\u624b\u662f\u90a3\u5927\u9b54\u5934\uff0c \u6211\u7239\u7239\u8f93\u4e86\uff0c\u4e5f\u4e0d\u7b97\u5f97\u803b\u8fb1\u3002\u201d\u7389\u6606\u4ed1\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5f53\u65f6\u6211\u7239\u53c2\u548c\u8d64\u795e\u5b50\u4e00\u573a\u6076\u6597\uff0c\u6211 \u7239\u7239\u4e0a\u4e86\u5e74\u7eaa\uff0c\u4ece\u6e05\u6668\u6597\u5230\u5348\u591c\uff0c\u6c14\u529b\u6e10\u8870\uff0c\u4e2d\u4e86\u90a3\u9b54\u5934\u7684\u8d64\u795e\u638c\u3002\u8ddf\u770b\u5c31\u6709\u6027\u547d\u4e4b\u5fe7\uff0c \u5e78\u4e8f\u6c34\u4e91\u5e84\u5e84\u4e3b\u4e91\u53ec\u8def\u8fc7\uff0c\u4ee5\u4ed6\u7684\u5927\u529b\u91d1\u521a\u638c\u76f8\u52a9\uff0c\u8fd9\u624d\u628a\u90a3\u9b54\u5934\u6253\u8dd1\u7684\u3002\u6211\u7239\u7239\u4e2d\u4e86 \u8d64\u795e\u638c\u7684\u70ed\u6bd2\uff0c\u65e0\u836f\u53ef\u533b\uff0c\u540e\u6765\u5f97\u9ad8\u4eba\u6307\u70b9\uff0c\u8981\u5728\u51b0\u5cf0\u4fee\u70bc\u5185\u529f\uff0c\u501f\u5929\u7136\u7684\u5bd2\u6c14\u4e0e\u672c\u8eab \u7684\u5185\u529f\u7ed3\u5408\uff0c\u624d\u53ef\u4ee5\u9a71\u9664\u4f53\u4e2d\u8574\u79ef\u7684\u70ed\u6bd2\u3002\u6211\u7239\u7239\u5c31\u662f\u56e0\u6b64\uff0c\u4ece\u6606\u4ed1\u5c71\u642c\u5230\u7075\u9e6b\u5cf0\u6765\uff0c \u5728\u51b0\u6cb3\u9644\u8fd1\u5efa\u8d77\u51b0\u5c4b\u5c45\u4f4f\u7684\u3002\u5168\u9760\u8fd9\u6837\u3002\u6211\u7239\u7239\u624d\u5f97\u591a\u6d3b\u5341\u591a\u5e74\uff0c\u5e76\u5c06\u6211\u4eec\u5144\u59b9\u629a\u517b\u6210 \u4eba\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u8fd9\u624d\u77e5\u9053\u4ed6\u5bb6\u4e0e\u4e91\u53ec\u539f\u6765\u662f\u6709\u8fd9\u6bb5\u6e0a\u6e90\uff0c\u602a\u4e0d\u5f97\u7389\u73b2\u73d1\u8981\u6551\u4e91\u743c\uff0c\u5e76\u8981\u4e3a \u4ed6\u64ae\u5408\u70df\u7f18\u4e86\u3002\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u6c34\u4e91\u5e84\u5e84\u4e3b\u4e91\u53ec\u548c\u6211\u4eec\u4e5f\u6709\u4e00\u70b9\u70b9\u4ea4\u60c5\u3002\u820d\u59b9\u548c\u5979\u5e08\u7236\u9099 \u5c71\u8c37\u638c\u95e8\u8c37\u4e4b\u534e\u5973\u4fa0\uff0c\u53bb\u5e74\u66fe\u5728\u4ed6\u5bb6\u4f5c\u5ba2\uff0c\u4ed6\u7684\u4e00\u5bf9\u513f\u5973\uff0c\u8fd9\u6b21\u4e5f\u662f\u548c\u6211\u4eec\u4e00\u8d77\u6765\u7684\u3002\u201d \u3000\u3000\u7389\u73b2\u73d1\u9053\uff1a\u201c\u4e91\u59d0\u59d0\u5462\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u5979\u5728\u4e00\u4e2a\u5c71\u6d1e\u7b49\u6211\u6211\u4eec\u4e0a\u5cb8\u7684\u65f6\u5019\uff0c\u5979 \u6c14\u529b\u672a\u66fe\u6062\u590d\uff0c\u6240\u4ee5\u6ca1\u6709\u540c\u6765\u3002\u201d\u7389\u73b2\u73d1\u8fd9\u65f6\u65b9\u59cb\u770b\u51fa\u5510\u52aa\u73e0\u7a46\u8863\u88f3\u4e0a\u8fd8\u6709\u6c34\u6e0d\uff0c\u604d\u7136 \u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u4e5f\u662f\u6389\u8fdb\u51b0\u6cb3\uff0c\u88ab\u6fc0\u6d41\u51b2\u5230\u6b64\u95f4\u6765\u4e86\u3002\u600e\u7684\u4f60\u4eec\u8fd9\u6837\u4e0d\u5c0f\u5fc3\uff0c \u4e09\u4e2a\u4eba\u90fd\u9677\u843d\u51b0\u6cb3\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff0c\u201c\u4e0d\u6b62\u6211\u4eec\u4e09\u4e2a\uff0c\u8fd8\u6709\u4e00\u4e2a\u5929\u9b54\u6559\u7684\u526f\u6559\u4e3b\u6587\u5ef7\u58c1\u5462\u3002\u707e\u7978\u7a81\u5982\u5176 \u6765\u4e4b\u9645\uff0c\u6211\u548c\u4e91\u59d1\u5a18\u4e00\u8d77\uff0c\u4e91\u5927\u54e5\u5219\u548c\u90a3\u6587\u526f\u6559\u4e3b\u76f8\u53bb\u4e0d\u8fdc\uff0c\u6211\u9690\u7ea6\u8fd8\u770b\u89c1\u4ed6\u4eec\u62fc\u4e86\u4e00 \u638c\uff0c\u540c\u65f6\u6389\u4e0b\u53bb\u7684\u3002\u201d\u5f53\u4e0b\u5c06\u6628\u65e5\u9047\u96be\u7684\u60c5\u5f62\uff0c\u4ee5\u53ca\u548c\u6587\u5ef7\u58c1\u62fc\u6597\u7684\u7ecf\u8fc7\uff0c\u7b80\u7565\u8c08\u4e86\u51e0 \u53e5\uff0c\u7136\u540e\u95ee\u9053\uff1a\u201c\u4f60\u6551\u8d77\u4e91\u5927\u54e5\u7684\u65f6\u5019\uff0c\u53ef\u66fe\u53d1\u73b0\u90a3\u6587\u5ef7\u58c1\u5417\uff1f\u201d \u3000\u3000\u7389\u73b2\u73d1\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u5c31\u662f\u53d1\u73b0\u4e86\u6211\u4e5f\u4e0d\u4f1a\u6551\u4ed6\u3002\u5361\u5170\u59ae\u59d1\u59d1\u548c\u4ed6\u6df1\u76f8\u7ed3\u7eb3\uff0c\u8fd8\u8981\u4ed6 \u505a\u5929\u9b54\u6559\u7684\u526f\u6559\u4e3b\uff0c\u8fd9\u4ef6\u4e8b\u6211\u662f\u65e9\u5c31\u4e0d\u6ee1\u7684\u4e86\u3002\u201d\u5510\u52aa\u73e0\u7a46\u6697\u6697\u5947\u602a\uff0c\u5fc3\u60f3\uff1a\u201c\u7389\u9e23\u73c2 \u662f\u6b63\u6d3e\u4fa0\u5ba2\uff0c\u4ed6\u4eec\u5144\u59b9\u53c8\u662f\u81ea\u5c0f\u5728\u7075\u9e6b\u5cf0\u4e0a\u5c45\u4f4f\uff0c\u4f8d\u5949\u7236\u4eb2\u7684\uff0c\u5374\u600e\u7684\u548c\u5929\u9b54\u6559\u4e3b\u5982\u6b64 \u719f\u7edc\uff1f\u201d\u4f46\u56e0\u521d\u521d\u76f8\u8bc6\uff0c\u5374\u4e5f\u4e0d\u4fbf\u591a\u95ee\u3002 \u3000\u3000\u7389\u73b2\u73d1\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u53bb\u4e86\uff0c\u6211\u53eb\u4e00\u4e2a\u4e2b\u9b1f\u53bb\u5c06\u4e91\u59d0\u59d0\u8bf7\u6765\u5427\u3002\u201d\u5f53\u4e0b\u5c06\u4e00\u4e2a\u4f8d\u5973\u5524 \u6765\uff0c\u5429\u5490\u5979\u9053\uff1a\u201c\u5728\u6211\u68b3\u5986\u53f0\u7684\u9996\u9970\u7bb1\u91cc\u6709\u4e00\u628a\u6247\u5b50\uff0c\u4f60\u628a\u90a3\u6247\u5b50\u62ff\u5728\u624b\u4e2d\uff0c\u627e\u5230\u4e86\u4e91 \u59d1\u5a18\u4e4b\u540e\uff0c\u7acb\u5373\u8bf4\u51fa\u6211\u7684\u540d\u5b57\uff0c\u5e76\u628a\u8fd9\u6247\u5b50\u4ea4\u7ed9\u5979\u770b\uff0c\u5979\u5c31\u4f1a\u653e\u5fc3\u6765\u4e86\u3002\u201d\u63a5\u7740\u5411\u5510\u52aa \u73e0\u7a46\u89e3\u91ca\u9053\uff1a\u201c\u524d\u5e74\u6211\u54e5\u54e5\u66fe\u5949\u7239\u7239\u4e4b\u547d\uff0c\u5230\u8fc7\u6c34\u4e91\u5e84\u62dc\u8bbf\u4e91\u8001\u4f2f\uff0c\u6211\u867d\u7136\u6ca1\u6709\u56de\u53bb\uff0c \u4f46\u4ed6\u4eec\u4e00\u5bb6\u4e5f\u90fd\u77e5\u9053\u6211\u7684\u540d\u5b57\u7684\u3002\u6211\u54e5\u54e5\u4e34\u8d70\u65f6\uff0c\u4e91\u743c\u5144\u59b9\u90fd\u6709\u9001\u4ed6\u793c\u7269\uff0c\u4e91\u59d1\u5a18\u8fd8\u7279 \u522b\u9001\u4e86\u4e00\u628a\u5979\u624b\u753b\u7684\u6247\u5b50\uff0c\u53eb\u54e5\u54e5\u5e26\u7ed9\u6211\u3002\u6240\u4ee5\u5979\u89c1\u4e86\u8fd9\u628a\u6247\u5b50\uff0c\u4e00\u5b9a\u4f1a\u653e\u5fc3\u6765\u7684\u3002\u201d \u3000\u3000\u90a3\u4f8d\u5973\u5e94\u4e86\u4e00\u58f0\uff0c\u56de\u5934\u4fbf\u8d70\u3002\u7389\u73b2\u73d1\u53c8\u628a\u5979\u5524\u4f4f\u9053\uff1a\u201c\u8fd8\u6709\uff0c\u4f60\u628a\u6211\u7684\u4e00\u5957\u8863\u88f3\u5e26 \u53bb\u7ed9\u4e91\u59d1\u5a18\u3002\u6211\u548c\u5979\u5e74\u7eaa\u5dee\u4e0d\u591a\uff0c\u60f3\u6765\u53ef\u4ee5\u5408\u7a7f\u3002\u201d\u5510\u52aa\u73e0\u7a46\u6697\u6697\u8d5e\u53f9\uff1a\u201c\u679c\u7136\u540d\u526f\u5176 \u5b9e\uff0c\u662f\u4e2a\u5fc3\u773c\u73b2\u73d1\u7684\u59d1\u5a18\u3002\u201d \u3000\u3000\u90a3\u4e2b\u9b1f\u8d70\u540e\uff0c\u7389\u73b2\u73d1\u9053\uff1a\u201c\u4e91\u5927\u54e5\u548c\u534e\u59d1\u5a18\u90fd\u5728\u6b64\u95f4\uff0c\u73b0\u5728\u53ef\u4ee5\u8bf7\u4ed6\u4eec\u6765\u548c\u4f60\u89c1\u9762 \u4e86\u3002\u54a6\uff0c\u739b\u4f9d\u53bb\u4e86\u8fd9\u4e48\u4e9b\u65f6\u5019\uff0c\u8fd8\u4e0d\u89c1\u56de\u6765\uff0c\u6211\u53ea\u6709\u518d\u53eb\u4e00\u4e2a\u4e2b\u9b1f\u53bb\u4e86\u3002\u201d\u6309\u7740\u7b11\u9053\uff1a \u201c\u4f60\u4e0d\u8981\u7b11\u6211\u5b69\u5b50\u6c14\uff0c\u6211\u662f\u53eb\u739b\u4f9d\u53bb\u5077\u542c\u4ed6\u4eec\u8c08\u8bdd\u7684\u3002\u73b0\u5728\u8fd8\u672a\u56de\u6765\uff0c\u60f3\u662f\u4ed6\u4eec\u8c08\u5f97\u6781 \u4e3a\u6295\u673a\uff0c\u739b\u4f9d\u4e5f\u542c\u5f97\u51fa\u795e\uff0c\u820d\u4e0d\u5f97\u79bb\u5f00\u4e86\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u7389\u59d1\u5a18\uff0c\u6211\u8981\u5411\u4f60\u544a\u7f6a\u3002\u739b\u4f9d\u662f\u88ab\u6211\u70b9\u4e86\u7a74\u9053\u3002\u73b0\u5728\u53ef\u80fd\u8fd8\u672a\u9192 \u6765\u5462\u3002\u201d\u7389\u73b2\u73d1\u9053\uff1a\u201d\u539f\u6765\u4f60\u5df2\u7ecf\u53bb\u770b\u8fc7\u4ed6\u4eec\u4e86\u5417\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4f60\u4e5f\u522b\u7b11\u6211\u5b69\u5b50 \u6c14\uff0c\u6211\u4e5f\u5077\u542c\u4ed6\u4eec\u7684\u8c08\u8bdd\u4e86\uff0c\u4ed6\u4eec\u7684\u786e\u662f\u8c08\u5f97\u5f88\u6295\u673a\uff0c\u6240\u4ee5\u6211\u624d\u4e0d\u60f3\u6253\u6270\u4ed6\u4eec\u3002\u201d\u63a5\u7740 \u544a\u8bc9\u4e86\u7389\u73b2\u73d1\u4ed6\u6240\u70b9\u7684\u7a74\u9053\uff0c\u7389\u73b2\u73d1\u5c31\u53eb\u4e00\u4e2a\u61c2\u5f97\u70b9\u7a74\u6cd5\u7684\u4e2b\u9b1f\u53bb\u7ed9\u739b\u4f9d\u89e3\u7a74\u3002 \u3000\u3000\u7389\u73b2\u73d1\u5429\u5490\u90a3\u4e2b\u9b1f\u9053\uff1a\u201c\u4f60\u628a\u739b\u4f9d\u7684\u7a74\u9053\u89e3\u5f00\uff0c\u53eb\u5979\u4e0d\u53ef\u58f0\u5f20\u3002\u7136\u540e\u4f60\u4eec\u8fdb\u53bb\u8bf7\u4e91 \u76f8\u516c\u548c\u534e\u59d1\u5a18\uff0c\u4f46\u4e0d\u5fc5\u8bf4\u51fa\u6211\u7684\u540d\u5b57\uff0c\u4ed6\u82e5\u95ee\u4f60\uff0c\u4f60\u5c31\u8bf4\u89c1\u4e86\u9762\u81ea\u7136\u77e5\u9053\u3002\u597d\u53eb\u4ed6\u4eec\u60ca \u559c\u4e00\u756a\u3002\u201d\u7389\u6606\u4ed1\u4e0d\u4f4f\u6447\u5934\u7b11\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u4f60\u8fd9\u73a9\u7b11\uff0c\u771f\u662f\u5f00\u5f97\u8352\u5510\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u8fd9\u624d\u5f97\u6709\u673a\u4f1a\uff0c\u5411\u90a3\u6606\u5e03\u5170\u56fd\u7684\u516c\u4e3b\u95ee\u9053\uff0c\u201c\u6211\u7684\u59b9\u59b9\u662f\u5982\u4f55\u88ab\u64d2\u7684\uff0c\u8fd8 \u6709\u6211\u6d3e\u5f80\u8d35\u56fd\u7684\u4f7f\u8005\uff0c\u73b0\u5728\u53c8\u662f\u5982\u4f55\uff1f\u201d \u3000\u3000\u6606\u5e03\u5170\u56fd\u7684\u516c\u4e3b\u8bf4\u9053\uff1a\u201c\u6211\u88ab\u8f6f\u7981\u6df1\u5bab\uff0c\u77e5\u9053\u5f97\u4e5f\u4e0d\u5f88\u6e05\u695a\u3002 \u3000\u3000\u4f46\u542c\u5f97\u4eba\u8bf4\uff1a\u6211\u54e5\u54e5\u542c\u4e86\u76d6\u82cf\u7684\u6311\u62e8\u3002\u5728\u4f60\u4eec\u7684\u4f7f\u8005\u5448\u9012\u56fd\u4e66\u7684\u65f6\u5019\uff0c\u6211\u54e5\u54e5\u5c31\u8d23 \u5907\u4ed6\uff0c\u8bf4\u662f\u4f60\u4eec\u8c0b\u5bb3\u4e86\u6211\u56fd\u7684\u4f7f\u8005\uff0c\u975e\u5411\u4f60\u4eec\u5ba3\u6218\u4e0d\u53ef\u3002\u5c31\u8fd9\u6837\uff0c\u4fbf\u628a\u8d35\u56fd\u7684\u4f7f\u8005\u62ff\u4f5c \u4fd8\u864f\u4e86\u3002\u8ddf\u7740\u53c8\u53bb\u641c\u6355\u8d35\u56fd\u4f7f\u8005\u7684\u968f\u4ece\uff0c\u542c\u8bf4\u53d1\u751f\u4e86\u4e00\u573a\u6fc0\u6218\uff0c\u53ea\u6709\u4e00\u4e2a\u4eba\u9003\u8131\u3002\u201d\u5510 \u52aa\u73e0\u7a46\u8bf4\u9053\uff1a\u201c\u8fd9\u4e00\u5b9a\u662f\u6211\u7684\u59b9\u59b9\u4e86\u3002\u201d \u3000\u3000\u6606\u5e03\u5170\u56fd\u7684\u516c\u4e3b\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u7684\u59b9\u59b9\u672c\u6765\u53ef\u4ee5\u9003\u8dd1\u7684\uff0c\u4f46\u5979\u5374\u4e0d\u80af\u9003\u8dd1\uff0c\u8fd9\u4e00 \u665a\u53c8\u5077\u8fdb\u5bab\u6765\uff0c\u8981\u627e\u6211\u7684\u54e5\u54e5\u8bb2\u7406\uff0c\u672a\u66fe\u89c1\u7740\u6211\u7684\u54e5\u54e5\uff0c\u5c31\u7ed9\u7ae5\u59e5\u59e5\u6d3b\u64d2\u4e86\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9a87\u7136\u95ee\u9053\uff1a\u201c\u8fd9\u7ae5\u59e5\u59e5\u662f\u4ec0\u4e48\u4eba\uff1f\u662f\u5979\u5355\u72ec\u5c06\u6211\u59b9\u59b9\u64d2\u4e0b\u7684\u4e48\uff1f\u201d\u6606\u5e03\u5170 \u56fd\u7684\u516c\u4e3b\u8bf4\u9053\uff1a\u201c\u8fd9\u7ae5\u59e5\u59e5\u7684\u6765\u5386\u8c01\u4e5f\u4e0d\u77e5\u9053\u3002\u6709\u4e00\u5e74\u6211\u7239\u7239\u5f97\u4e86\u4e00\u79cd\u602a\u75c5\uff0c\u7fa4\u533b\u675f\u624b \u65e0\u7b56\uff0c\u53ea\u597d\u5f20\u699c\u6c42\u533b\u3002\u662f\u7ae5\u59e5\u59e5\u63ed\u4e86\u699c\uff0c\u5c06\u6211\u7684\u7239\u7239\u533b\u597d\u7684\u3002\u5979\u8bf4\u5979\u65e0\u4f9d\u65e0\u9760\uff0c\u6211\u7239\u7239 \u5341\u5206\u611f\u6fc0\u5979\uff0c\u5c31\u628a\u5979\u7559\u5728\u5bab\u91cc\uff0c\u540e\u6765\u624d\u77e5\u9053\u5979\u672c\u9886\u6781\u5927\uff0c\u4e0d\u4f46\u533b\u535c\u661f\u76f8\uff0c\u65e0\u6240\u4e0d\u901a\uff0c\u6b66 \u529f\u66f4\u662f\u65e0\u4eba\u80fd\u654c\uff0c\u5bab\u4e2d\u7684\u6b66\u58eb\u4e2a\u4e2a\u7ed9\u5979\u6253\u8d25\uff0c\u4eba\u4eba\u5bf9\u5979\u4e94\u4f53\u6295\u5730\u3002\u6211\u7239\u7239\u89c1\u5979\u672c\u9886\u5982\u6b64 \u9ad8\u5f3a\uff0c\u66f4\u4e3a\u6b22\u559c\uff0c\u5c01\u5979\u505a\u91d1\u8f6e\u5723\u6bcd\uff0c\u8fd8\u4e3a\u5979\u9020\u4e86\u4e00\u5ea7\u5b98\u6bb4\uff0c\u5c06\u5979\u5f53\u4f5c\u517b\u6bcd\u4e00\u6837\u4f8d\u5949\u3002\u53ef \u662f\u6211\u5374\u4e0d\u9ad8\u5174\u5979\uff0c\u89c1\u9762\u65f6\u5c0a\u5979\u4e00\u58f0\u201c\u5723\u6bcd\u201d\uff0c\u79c1\u5e95\u4e0b\u5374\u53eb\u5979\u505a\u8ba8\u538c\u7684\u7ae5\u59e5\u59e5\u3002\u201d\u7389\u73b2\u73d1 \u7b11\u9053\uff1a\u201c\u51c6\u662f\u4f60\u54e5\u54e5\u77e5\u9053\u4f60\u8ba8\u538c\u5979\uff0c\u6240\u4ee5\u7279\u5730\u53eb\u5979\u770b\u7ba1\u4f60\u3002\u201d \u3000\u3000\u6606\u5e03\u5170\u56fd\u7684\u516c\u4e3b\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u54e5\u54e5\u7ee7\u4f4d\u4e4b\u540e\uff0c\u5bf9\u7ae5\u59e5\u59e5\u66f4\u4e3a\u5c0a\u656c\uff0c\u8a00\u542c\u8ba1\u4ece\u3002\u90a3 \u665a\u7ae5\u59e5\u59e5\u548c\u4f60\u59b9\u59b9\u6076\u6597\u4e86\u4e00\u573a\uff0c\u5448\u662f\u628a\u4f60\u59b9\u59b9\u64d2\u4e0b\uff0c\u4f46\u542c\u8bf4\u5979\u4e5f\u53d7\u4e86\u70b9\u4f24\u3002\u4f9d\u5979\u4e4b\u610f\uff0c \u672c\u6765\u8981\u628a\u4f60\u59b9\u59b9\u5904\u6b7b\u7684\uff0c\u540e\u6765\u5ba1\u95ee\u51fa\u662f\u4f60\u4eec\u9a6c\u8428\u513f\u56fd\u7684\u516c\u4e3b\uff0c\u54e5\u54e5\u8981\u62ff\u6765\u8981\u80c1\u4f60\u4eec\uff0c\u8fd9 \u624d\u6539\u53d8\u4e86\u4e3b\u610f\uff0c\u628a\u5979\u56da\u5165\u51b7\u5bab\u3002\u540e\u6765\u6211\u7237\u7237\u51fa\u5934\uff0c\u628a\u4f60\u59b9\u59b9\u653e\u4e86\u51fa\u6765\uff0c\u8ba9\u5979\u53ef\u4ee5\u5728\u5bab\u4e2d \u81ea\u7531\u8d70\u52a8\u3002\u7ae5\u59e5\u59e5\u6c14\u5f97\u8981\u6b7b\uff0c\u4f46\u5979\u53ef\u4e0d\u6562\u8fdd\u6297\u6211\u7684\u7237\u7237\uff1b\u8bf4\u4e5f\u5947\u602a\uff0c\u6211\u7239\u7239\u3001\u6211\u54e5\u54e5\u5bf9 \u8fd9\u7ae5\u59e5\u59e5\u5c0a\u656c\u975e\u5e38\u3002\u6211\u8fd9\u8001\u7237\u7237\u5374\u548c\u6211\u4e00\u6837\uff0c\u4e00\u5411\u4e0d\u5927\u6b21\u559c\u8fd9\u7ae5\u59e5\u59e5\u7684\u3002\u4e0d\u8fc7\u4ed6\u5e74\u7eaa\u592a \u8001\uff0c\u65e9\u5df2\u4e0d\u7ba1\u4e8b\u60c5\uff0c\u4e5f\u5c31\u4efb\u5979\u4f4f\u5728\u5bab\u91cc\u4e86\u3002 \u3000\u3000\u6216\u8bb8\uff0c\u4ed6\u89c1\u7ae5\u59e5\u59e5\u672c\u9886\u592a\u5f3a\uff0c\u6709\u6240\u987e\u5fcc\uff0c\u56e0\u6b64\u4e0d\u6562\u516c\u7136\u8868\u793a\u618e\u6076\uff0c\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u4f60\u7237\u7237\u5e74\u7eaa\u5f88\u5927\u4e86\u5417\uff1f\u201d\u6606\u5e03\u5170\u56fd\u516c\u4e3b\u9053\uff1a \u3000\u3000\u201c\u4eca\u5e74\u5df2\u516b\u5341\u4e5d\u5c81\u4e86\u3002\u6211\u54e5\u54e5\u662f\u4ed6\u957f\u5b59\uff0c\u76f4\u63a5\u7ee7\u627f\u7956\u7236\u7684\u7687\u4f4d\u7684\u3002\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a \u201c\u4f60\u7239\u7239\u5462\uff1f\u201d\u516c\u4e3b\u9053\uff1a\u201c\u6211\u7239\u7239\u5bff\u547d\u4e0d\u957f\uff0c\u53ea\u6d3b\u5230\u4e94\u5341\u591a\u5c81\uff0c\u90a3\u65f6\u7956\u7236\u8fd8\u5728\u4f4d\u3002\u201d\u5510 \u52aa\u73e0\u7a46\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u7237\u7237\u5728\u4f4d\u7684\u65f6\u95f4\u5f88\u957f\u554a\uff01\u201d\u516c\u4e3b\u9053\uff1a\u201c\u7237\u7237\u5341\u4e5d\u5c81\u767b\u57fa\u3002\u6574\u6574 \u505a\u4e86\u516d\u5341\u5e74\u7687\u5e1d\u3002\u5341\u5e74\u524d\u4ed6\u624d\u900a\u4f4d\uff0c\u8ba9\u7ed9\u6211\u54e5\u54e5\u7684\u3002\u201d\u516c\u4e3b\u6709\u70b9\u5947\u602a\uff0c\u4e0d\u89e3\u5510\u52aa\u73e0\u7a46\u4f55 \u4ee5\u8fd9\u6837\u5173\u5fc3\u5979\u7956\u7236\u7684\u5e74\u9f84\uff0c\u5bf9\u5979\u7684\u5bb6\u4e8b\u4e5f\u95ee\u5f97\u8fd9\u6837\u4ed4\u7ec6\u3002 \u3000\u3000\u539f\u6765\u5510\u52aa\u73e0\u7a46\u6b64\u9645\u6b63\u5728\u60f3\u8d77\u5b9d\u5e93\u4e2d\u6240\u53d1\u73b0\u7684\u90a3\u4e9b\u5bc6\u4ef6\uff0c\u90a3\u662f\u4ed6\u7684\u4e0d\u77e5\u54ea\u4f4d\u7956\u5148\u5bc6\u5c01 \u5728\u4e00\u4e2a\u5bfb\u5e38\u7684\u9996\u9970\u76d2\u91cc\u7684\uff0c\u5176\u4e2d\u4e4b\u4e00\u5c31\u662f\u4e00\u5f20\u6606\u5e03\u5170\u56fd\u65b0\u738b\u5373\u4f4d\u901a\u77e5\u90bb\u56fd\u7684\u56fd\u4e66\uff0c\u5510\u52aa \u73e0\u7a46\u8fd8\u8bb0\u5f97\u90a3\u5f20\u56fd\u4e66\u4e0a\u6240\u586b\u5199\u7684\u65e5\u671f\uff0c\u6b63\u662f\u8ddd\u4eca\u516d\u5341\u5e74\u524d\u4e4b\u4e8b\uff0c\u6697\u81ea\u5bfb\u601d\uff1a \u3000\u3000\u201c\u539f\u6765\u90a3\u4f4d\u7687\u5e1d\u5c31\u662f\u5979\u7684\u7956\u7236\u3002\u8fd9\u79cd\u901a\u77e5\u5373\u4f4d\u7684\u56fd\u4e66\u672c\u6765\u662f\u5f88\u666e\u901a\uff0c\u6211\u7684\u7956\u5148\u5374\u628a \u5b83\u73cd\u85cf\u8d77\u6765\uff0c\u663e\u5f97\u6781\u4e3a\u91cd\u89c6\uff0c\u6211\u4e00\u76f4\u4e0d\u89e3\u662f\u4f55\u7f18\u6545\uff0c\u73b0\u5728\u60f3\u6765\uff0c\u4e5f\u8bb8\u5176\u4e2d\u771f\u6709\u4e00\u4e9b\u4ec0\u4e48 \u7279\u522b\u7684\u56e0\u7531\uff1f\u4ed6\u7237\u7237\u5bf9\u6211\u59b9\u59b9\u529b\u52a0\u4fdd\u62a4\uff0c\u55ef\uff0c\u770b\u6765\u8fd9\u4e24\u4ef6\u4e8b\u4f1a\u6709\u5173\u8054\u3002\u201d \u3000\u3000\u90a3\u9996\u9970\u76d2\u91cc\u9664\u4e86\u90a3\u5f20\u56fd\u4e66\u4e4b\u5916\uff0c\u8fd8\u6709\u4e24\u5c01\u4fe1\uff0c\u7b2c\u4e00\u5c01\u662f\u4e2a\u5973\u5b50\u5199\u7684\u60c5\u4e66\uff0c\u7b2c\u4e8c\u5c01\u4e5f \u662f\u90a3\u5973\u5b50\u5199\u7684\uff0c\u544a\u8bc9\u5979\u7684\u65e7\u65e5\u60c5\u4eba\uff0c\u5979\u5df2\u7ecf\u751f\u4e86\u4e00\u4e2a\u513f\u5b50\u3002\u5e0c\u671b\u4ed6\u4eec\u6c38\u8fdc\u4e0d\u8981\u5728\u6c99\u573a\u76f8 \u89c1\uff0c\u8fd9\u662f\u4e00\u53e5\u5f88\u7279\u522b\u7684\u8bf4\u8bdd\uff0c\u5510\u52aa\u73e0\u7a46\u5f53\u65f6\u66fe\u5927\u60d1\u4e0d\u89e3\u7684\uff0c\u73b0\u5728\u60f3\u6765\uff0c\u8fd9\u51e0\u4ef6\u4e8b\u90fd\u4f3c\u4e4e \u9690\u9690\u6709\u4ec0\u4e48\u5173\u8054\uff0c\u5510\u52aa\u73e0\u7a46\u731c\u5230\u4e86\u51e0\u5206\uff0c\u5374\u4e0d\u6562\u8bf4\u51fa\u6765\u3002\u4ed6\u60f3\u4e86\u4e00\u4f1a\uff0c\u65b9\u518d\u95ee\u9053\uff1a\u201c\u6211 \u59b9\u59b9\u88ab\u64d2\u7684\u65f6\u5019\uff0c\u4f60\u54e5\u54e5\u53ef\u66fe\u641c\u8fc7\u5979\u7684\u8eab\u4e48\uff1f\u201d\u516c\u4e3b\u6120\u9053\uff1a\u201c\u8fd9\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\uff0c\u4f9d\u6211\u60f3\uff0c \u6211\u54e5\u54e5\u867d\u7136\u884c\u4e3a\u4e0d\u6b63\uff0c\u5927\u7ea6\u8fd8\u4e0d\u6562\u8d2a\u56fe\u522b\u4eba\u7684\u73cd\u6016\u5b9d\u7269\u3002\u201d\u5979\u8bef\u4f1a\u4e86\u5510\u52aa\u73e0\u7a46\u7684\u610f\u601d\uff0c \u8a00\u8bed\u4e4b\u95f4\u4e0d\u5927\u9ad8\u5174\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8bf4\u4f60\u54e5\u54e5\u4f1a\u641c\u5979\u7684\u73e0\u5b9d\uff0c\u5979\u8eab\u4e0a\u4e5f\u5b9e\u5728\u6ca1\u6709\u4ec0\u4e48\u5b9d\u7269\uff0c \u4f46\u5374\u6709\u4e00\u5f20\u4f60\u4eec\u6606\u5e03\u5170\u56fd\u5728\u4e03\u5341\u5e74\u524d\u7ed9\u6211\u4eec\u7684\u56fd\u4e66\u3002\u90a3\u662f\u4f60\u7237\u7237\u5373\u4f4d\uff0c\u901a\u544a\u90bb\u56fd\u7684\u56fd\u4e66\u3002\u201d \u3000\u3000\u516c\u4e3b\u5927\u4e3a\u5947\u602a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4fdd\u5b58\u4e86\u8fd9\u4e48\u4e45\uff01\u8fd9\u6b21\u53c8\u4e3a\u4f55\u8ba9\u4f60\u59b9\u59b9\u5e26\u6765\uff1f\u662f\u4f55\u7528\u610f\uff1f\u201d \u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u6211\u59b9\u59b9\u53d1\u73b0\u8fd9\u5f20\u56fd\u4e66\uff0c\u5076\u7136\u5e26\u5728\u8eab\u4e0a\u7684\u3002\u6211\u60f3\u5979\u5e26\u53bb\u4e5f\u597d\uff0c\u8fd9\u53ef\u4ee5\u8868 \u793a\u8d35\u6211\u4e24\u56fd\u6709\u6df1\u8fdc\u7684\u4ea4\u8c0a\u3002\u201d\u5176\u5b9e\u8fd9\u662f\u5510\u52aa\u73e0\u7a46\u6709\u610f\u8ba9\u59b9\u59b9\u5c06\u90a3\u9996\u9970\u76d2\u5b50\u5e26\u53bb\u7684\uff0c\u9664\u4e86 \u56fd\u4e66\u4e4b\u5916\uff0c\u90a3\u5973\u5b50\u5199\u7684\u4e24\u5c01\u60c5\u7237\u4e5f\u5728\u5176\u4e2d\u5462\u3002\u53ea\u56e0\u5176\u4e2d\u7591\u70b9\u751a\u591a\uff0c\u5728\u8c1c\u5e95\u8fd8\u672a\u80fd\u63ed\u6653\u4e4b \u524d\uff0c\u5510\u52aa\u73e0\u7a46\u4e0d\u60f3\u591a\u8bf4\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u53c8\u518d\u95ee\u9053\uff1a\u201c\u90a3\u7ae5\u59e5\u59e5\u5728\u4f60\u4eec\u5bab\u4e2d\u4f4f\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u4ece\u6765\u6ca1\u6709\u5979\u7684\u5bb6\u4eba\u6216\u8005 \u670b\u53cb\u6765\u770b\u8fc7\u5979\u4e48\uff1f\u201d\u516c\u4e3b\u8bf4\u9053\uff1a\u201c\u5361\u5170\u59ae\u59d1\u59d1\u8fd9\u51e0\u5e74\u5c31\u66fe\u7ecf\u6765\u770b\u8fc7\u5979\u597d\u51e0\u6b21\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u53ef\u5c31\u662f\u90a3\u5929\u9b54\u6559\u4e3b\uff1f\u201d\u516c\u4e3b\u9053\uff1a\u201c\u4ec0\u4e48\u5929\u9b54\u6559\u4e3b\uff1f\u201d\u7389\u73b2\u73d1\u7b11\u9053\uff1a \u201c\u8fd9\u662f\u5361\u5170\u59ae\u59d1\u59d1\u5728\u4e2d\u539f\u5f00\u521b\u7684\u6559\u6d3e\uff0c\u5979\u81ea\u5c01\u6559\u4e3b\u3002\u5979\u5927\u7ea6\u56e0\u4e3a\u4f60\u4e0d\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u6240\u4ee5 \u6ca1\u6709\u548c\u4f60\u8bf4\u53ca\u3002\u5979\u6559\u53f7\u5929\u9b54\uff0c\u4e3a\u4eba\u5012\u662f\u5f88\u548c\u6c14\u7684\u3002\u201d\u5510\u52aa\u73e0\u7a46\u5fc3\u60f3\uff1a\u201c\u5979\u7684\u6bd2\u8fa3\u624b\u6bb5\uff0c \u4f60\u8fd8\u672a\u89c1\u8fc7\u54e9\u3002\u201d\u4f46\u56e0\u5f7c\u6b64\u521d\u521d\u76f8\u8bc6\uff0c\u800c\u542c\u8fd9\u53e3\u6c14\uff0c\u7389\u5bb6\u5144\u59b9\u548c\u5929\u9b54\u6559\u4e3b\u53c8\u662f\u751a\u4e3a\u4eb2\u8fd1\uff0c \u4e5f\u5c31\u4e0d\u4fbf\u591a\u8a00\u4e86\u3002 \u3000\u3000\u516c\u4e3b\u53c8\u9053\uff1a\u201c\u6211\u5c0f\u65f6\u5019\uff0c\u6709\u4e2a\u4f0a\u58c1\u73e0\u739b\u4e5f\u66fe\u6765\u63a2\u8fc7\u7ae5\u59e5\u59e5\uff0c\u5979\u5c31\u662f\u5361\u5170\u59ae\u7684\u59d0\u59d0\uff0c \u540e\u7c73\u542c\u8bf4\u5728\u4e2d\u539f\u7ed3\u4e86\u5a5a\uff0c\u5c31\u6ca1\u6709\u518d\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u5973\u4eba\u9634\u9633\u602a\u6c14\u7684\uff0c\u548c\u5979\u7684\u59b9\u59b9\u5f88\u4e0d\u76f8\u540c\uff0c\u6211\u53ef\u4e0d\u5927\u9ad8\u5174\u5979\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9887\u611f\u8be7\u5f02\u3002\u5fc3\u60f3\uff1a\u201c\u6211\u542c\u59b9\u59b9\u8bf4\u8fc7\uff0c\u8fd9\u5929\u9b54\u6559\u4e3b\u7684\u59d0\u59d0\u662f\u6e05\u671d\u7684\u4e8c\u54c1\u8bf0\u547d \u5927\u4eba\uff0c\u6cb3\u5357\u63d0\u7763\u7f28\u5357\u5ef7\u7684\u59bb\u5b50\u3002\u59b9\u59b9\u5c0f\u65f6\u5019\uff0c\u5979\u66fe\u4e0a\u8fc7\u9099\u5c71\u5192\u5145\u662f\u6211\u4eec\u7684\u6bcd\u4eb2\uff0c\u60f3\u9a97\u59b9 \u59b9\u90a3\u4ef6\u4ee5\u5929\u5fc3\u77f3\u4f5c\u94ae\u6263\u7684\u68c9\u8884\u3002\u6211\u7236\u738b\u88ab\u76d6\u6e29\u7be1\u4f4d\u4e4b\u540e\uff0c\u6211\u5ae1\u6bcd\u4f3c\u7687\u540e\u4e4b\u5c0a\uff0c\u81ea\u7518\u5815\u843d\uff0c \u53db\u592b\u4ece\u8d3c\uff0c\u53c8\u505a\u4e86\u76d6\u6e29\u7684\u2018\u7687\u989d\u5a18\u2019\uff0c\u5929\u9b54\u6559\u4e3b\u59d0\u59b9\u548c\u8fd9\u5996\u5987\u4e5f\u6709\u5f80\u6765\u3002\u60f3\u4e0d\u5230\u5979\u4eec\u548c \u6606\u5e03\u5170\u56fd\u7684\u7ae5\u59e5\u59e5\u4e5f\u662f\u6709\u52fe\u7ed3\u7684\u3002\u770b\u6765\uff0c\u5929\u9b54\u6559\u4e3b\u548c\u6211\u8fd9\u4e00\u5bb6\u8fd8\u9887\u6709\u5173\u7cfb\u54e9\uff0c\u4e0d\u8fc7\u5979\u4eec \u4e00\u76f4\u662f\u4e0d\u6000\u597d\u610f\u7f62\u4e86\u3002\u6211\u8fd9\u6b21\u5230\u6606\u5e03\u5170\u56fd\uff0c\u5012\u8981\u63a2\u4e2a\u6c34\u843d\u77f3\u51fa\u624d\u884c\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u6709\u811a\u6b65\u58f0\u8d70\u6765\uff0c\u7389\u73b2\u73d1\u5077\u5077\u7b11\u9053\uff1a\u201c\u4e14\u770b\u770b\u6211\u8fd9\u4e2a\u5a92\u505a\u5f97\u6210\u4e0d\u6210 \u529f\uff1f\u201d\u95e8\u5916\u90a3\u4e2b\u9b1f\u9ad8\u58f0\u62a5\u9053\uff1a\u201c\u4e91\u76f8\u516c\u548c\u534e\u59d1\u5a18\u6765\u4e86\u3002\u201d\u7389\u73b2\u73d1\u6253\u5f00\u623f\u95e8\uff0c\u7b11\u9053\uff1a\u201c\u4e24 \u4f4d\u8bf7\u8fdb\uff0c\u8d35\u53cb\u5df2\u5728\u6b64\u7b49\u5019\u591a\u65f6\u4e86\u3002\u201d \u3000\u3000\u4e91\u743c\u8fdb\u4e86\u5c4b\u5185\uff0c\u89c1\u4e86\u5510\u52aa\u73e0\u7a46\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f \u4f60\u4e5f\u6765\u4e86\uff1f\u6211\u59b9\u59b9\u5462\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a \u3000\u3000\u201c\u58c1\u59b9\u5c31\u8981\u6765\u4e86\uff0c\u4f60\u5148\u8c22\u8fc7\u6b64\u95f4\u4e3b\u4eba\u5427\uff0c\u662f\u4ed6\u6551\u4f60\u7684\u3002\u201d \u3000\u3000\u7389\u6606\u4ed1\u7b11\u9053\uff1a\u201c\u4e91\u5927\u54e5\uff0c\u8fd8\u8ba4\u5f97\u5c0f\u5f1f\u4e48\uff1f\u201d\u4e91\u743c\u5b9a\u4e86\u773c\u775b\uff0c\u8ba4\u51fa\u4e86\u7389\u6606\u4ed1\uff0c\u66f4\u662f\u60ca \u559c\u4ea4\u96c6\uff0c\u4e24\u4eba\u62e5\u62b1\u8d77\u6765\uff0c\u4e91\u743c\u9053\uff1a\u201c\u7389\u5927\u54e5\uff0c\u539f\u6765\u662f\u4f60\u6551\u6211\u7684\u5440\uff01\u201d\u7389\u6606\u4ed1\u9053\uff1a\u201c\u662f\u6211 \u59b9\u59b9\u6551\u4e86\u4f60\u4eec\u7684\u3002\u73b2\u73d1\uff0c\u5feb\u6765\u548c\u4e91\u5927\u54e5\u91cd\u65b0\u89c1\u8fc7\u4e2a\u672d\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u8fdb\u95e8\u4e4b\u540e\uff0c\u4e00\u76f4\u76ee\u4e0d\u8f6c\u775b\u7684\u6ce8\u89c6\u7740\u7389\u73b2\u73d1\uff0c\u5ffd\u5730\u54ac\u4e86\u54ac\u6307\u5934\uff0c\u201c\u54ce\u5537\u201d\u5730\u53eb \u4e86\u4e00\u58f0\uff0c\u7389\u73b2\u73d1\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u68a6\uff0c\u534e\u59d1\u5a18\u3002\u4f60\u8ba4\u5f97\u6211\u4e48\uff1f\u201d \u3000\u3000\u534e\u4e91\u78a7\u5341\u5206\u60f6\u60d1\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u4e0d\u662f\u5e38\u5e38\u5728\u6211\u8eab\u8fb9\u7684\u90a3\u4f4d\u767d\u8863\u59d1\u5a18\u4e48\uff1f\u8fd9\u4e48 \u8bf4\uff0c\u7adf\u4e0d\u662f\u68a6\u4e86\u3002\u201d\u7389\u73b2\u73d1\u7b11\u9053\uff1a\u201c\u4f60\u4e2d\u4e86\u90a3\u96ea\u7ec3\u86c7\u7684\u6bd2\uff0c\u662f\u6211\u5c06\u4f60\u653e\u5728\u90a3\u7eff\u7389\u5e8a\u4e0a\u7684\u3002 \u73b0\u5728\u53ef\u4ee5\u653e\u5fc3\u4e86\uff0c\u4f60\u7684\u5bd2\u6bd2\u5df2\u7ecf\u6d88\u5c3d\uff0c\u4ee5\u540e\u4e5f\u4e0d\u4f1a\u53d1\u4f5c\u4e86\u3002\u201d\u534e\u4e91\u78a7\u8fd9\u624d\u77e5\u9053\u662f\u6551\u547d\u6069 \u4eba\uff0c\u8fde\u5fd9\u9053\u8c22\u3002\u7389\u73b2\u73d1\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u548c\u4e91\u5927\u54e5\u662f\u719f\u8bc6\u7684\uff0c\u6240\u4ee5\u8ba9\u4f60\u4eec\u4f5c\u4f34\u3002\u4f60\u4eec\u8be5 \u4e0d\u4f1a\u602a\u6211\u6545\u5f04\u7384\u865a\u4e86\u5427\uff1f\u201d \u3000\u3000\u534e\u4e91\u78a7\u53cc\u988a\u6655\u7ea2\uff0c\u5979\u662f\u4e2a\u4e03\u7a8d\u73b2\u73d1\u3001\u51b0\u96ea\u806a\u660e\u7684\u5973\u5b50\uff0c\u4e00\u542c\u6b64\u8a00\uff0c\u4fbf\u77e5\u9053\u7389\u73b2\u73d1\u5982 \u6b64\u5b89\u6392\uff0c\u5176\u4e2d\u5927\u6709\u7528\u5fc3\u3002\u4e00\u65f6\u95f4\u4e5f\u4e0d\u77e5\u662f\u607c\u5979\u597d\uff0c\u8fd8\u662f\u611f\u6fc0\u5979\u597d\uff1f\u4f46\u7389\u73b2\u73d1\u4e8e\u5730\u6709\u6551\u547d \u4e4b\u6069\uff0c\u5979\u7eb5\u7136\u7740\u607c\uff0c\u4e5f\u4e0d\u597d\u8bf4\u51fa\u6765\u3002\u4f55\u51b5\u8fd8\u672a\u5fc5\u662f\u7740\u607c\u5462\uff1f\u5f53\u4e0b\u7d22\u6027\u5927\u5927\u65b9\u65b9\u4ed6\u8bf4\u9053\uff1a \u201c\u591a\u8c22\u4f60\u5b89\u6392\u6211\u548c\u4e91\u5927\u54e5\u5148\u89c1\u4e86\u9762\uff0c\u8ba9\u6211\u4e00\u9192\u6765\u5c31\u5f97\u77e5\u6211\u7239\u7239\u7684\u6d88\u606f\uff0c\u6211\u5f88\u611f\u6fc0\u3002\u201d\u4f46 \u5979\u867d\u7136\u4f5c\u51fa\u5766\u7136\u7684\u795e\u6c14\uff0c\u8138\u4e0a\u7684\u7ea2\u6655\u5374\u9a97\u4e0d\u4e86\u4eba\uff0c\u663e\u51fa\u4e86\u5979\u7684\u60c5\u6000\u6fc0\u8361\u3002 \u3000\u3000\u7389\u73b2\u73d1\u548c\u5510\u52aa\u73e0\u7a46\u4f5c\u4e86\u4e2a\u4f1a\u5fc3\u5fae\u7b11\uff0c\u4e00\u4e2a\u5fc3\u60f3\uff1a\u201c\u6211\u8fd9\u676f\u8c22\u5a92\u9152\u5927\u7ea6\u662f\u53ef\u4ee5\u559d\u6210\u7684 \u4e86\u3002\u201d\u4e00\u4e2a\u5bfb\u601d\uff1a\u201c\u6c5f\u5e08\u5144\u5fc3\u5934\u4e0a\u7684\u6b7b\u7ed3\u770b\u6765\u5012\u662f\u6709\u5e0c\u671b\u53ef\u4ee5\u89e3\u5f00\u4e86\u3002\u201d \u3000\u3000\u4e91\u743c\u5012\u6ca1\u6709\u6ce8\u610f\u4ed6\u4eec\u7684\u8c08\u8bdd\uff0c\u4ed6\u4e0e\u7389\u6606\u4ed1\u6545\u53cb\u91cd\u9022\uff0c\u662f\u8fd9\u6837\u6020\u60f3\u4e0d\u5230\u7684\u9645\u9047\uff0c\u4e0d\u7531 \u5f97\u60ca\u559c\u4ea4\u96c6\uff0c\u6000\u7740\u6ee1\u8179\u7591\u56e2\uff0c\u8fde\u8fde\u5411\u7389\u6606\u4ed1\u53d1\u95ee\u3002\u7389\u6606\u4ed1\u9053\uff1a\u201c\u6211\u548c\u4f60\u5206\u624b\u4e4b\u540e\uff0c\u4e5f\u6709 \u4e00\u6bb5\u5947\u9047\uff0c\u540e\u6765\u5c31\u5230\u6606\u5e03\u5170\u56fd\u7684\u4eac\u57ce\u53bb\u4e86\u3002\u201d\u4e91\u743c\u9053\uff1a\u201c\u4e14\u6162\uff0c\u4e14\u6162\uff0c\u5230\u5e95\u662f\u4ec0\u4e48\u5947\u9047 \u554a\uff1f\u201d\u4e94\u6606\u4ed1\u671b\u4e86\u516c\u4e3b\u4e00\u773c\uff0c\u541e\u541e\u5410\u5410\u3002 \u3000\u3000\u4e91\u743c\u77a7\u4e86\u4ed6\u8fd9\u4e2a\u795e\u6c14\uff0c\u5fc3\u91cc\u5df2\u660e\u767d\u51e0\u5206\uff0c\u53ea\u542c\u5f97\u738b\u73b2\u73d1\u7b11\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u6211\u4ee3\u4f60\u4eec\u8bf4 \u5427\uff1f\u201d\u6606\u5e03\u5170\u56fd\u7684\u516c\u4e3b\u672a\u66fe\u53d7\u8fc7\u6c49\u65cf\u90a3\u4e00\u5957\u793c\u6cd5\u85b0\u9676\uff0c\u5012\u662f\u6ca1\u6709\u4e00\u70b9\u5ff8\u6029\u4e4b\u6001\uff0c\u683c\u683c\u7b11 \u9053\uff1a\u201c\u4f60\u5c31\u8bf4\u5427\uff0c\u8fd9\u4e5f\u7528\u4e0d\u4e86\u4ec0\u4e48\u907f\u5fcc\u554a\uff01\u201d \u3000\u3000\u8981\u77e5\u6606\u5e03\u5170\u56fd\u4e43\u662f\u4e2a\u6e38\u7267\u6c11\u65cf\uff0c\u5c11\u5e74\u7537\u5973\u7ecf\u5e38\u4e00\u540c\u6253\u730e\uff0c\u5230\u4e86\u77e5\u9053\u7231\u6155\u5f02\u6027\u7684\u5e74\u9f84\uff0c \u5c31\u7528\u6b4c\u58f0\u8868\u8fbe\u60c5\u610f\uff0c\u6311\u9009\u5fc3\u4e0a\u4e4b\u4eba\uff0c\u56e0\u6b64\u5bf9\u4e8e\u7537\u5973\u76f8\u60a6\uff0c\u8ba4\u4e3a\u662f\u518d\u4e5f\u81ea\u7136\u4e0d\u8fc7\u7684\u4e8b\u3002\u6e38 \u7267\u6c11\u65cf\u7684\u5973\u4e8e\u603b\u662f\u559c\u6b22\u4f53\u9b44\u5065\u58ee\u3001\u672c\u9886\u9ad8\u5f3a\u7684\u7537\u5b50\u7684\uff0c\u516c\u4e3b\u6b63\u4ee5\u6709\u7740\u7389\u6606\u4ed1\u8fd9\u6837\u7684\u60c5\u90ce \u800c\u611f\u5230\u9a84\u50b2\u5462\u3002 \u3000\u3000\u7389\u73b2\u73d1\u9053\uff1a\u201c\u5c31\u5728\u6211\u54e5\u54e5\u4ece\u4f60\u4eec\u7684\u6c34\u4e91\u5e84\u56de\u6765\u4e4b\u540e\u4e0d\u4e45\uff0c\u6709\u4e00\u5929\uff0c\u516c\u4e3b\u5e26\u4e86\u4e00\u961f\u6b66 \u58eb\uff0c\u5728\u5c71\u4e0b\u6253\u730e\uff0c\u78b0\u4e0a\u4e00\u53ea\u6781\u4e3a\u51f6\u6076\u7684\u7280\u725b\uff0c\u7280\u725b\u76ae\u7c97\u8089\u539a\uff0c\u5200\u7bad\u96be\u5165\uff0c\u53d1\u8d77\u813e\u6c14\u6765\uff0c \u4e24\u53ea\u5c16\u89d2\u53ef\u4ee5\u89e6\u788e\u77f3\u5934\uff0c\u6bd4\u8001\u864e\u72ee\u5b50\uff0c\u8fd8\u8981\u53ef\u6015\u5f97\u591a\uff0c\u6b66\u58eb\u4eec\u614c\u5fd9\u4fdd\u62a4\u516c\u4e3b\uff0c\u7bad\u5982\u96e8\u4e0b\uff0c \u8981\u5c06\u5b83\u9a71\u8d70\uff0c\u54ea\u77e5\u66f4\u89e6\u6012\u4e86\u5b83\uff0c\u7adf\u7136\u5411\u516c\u4e3b\u8fd9\u8fb9\u51b2\u6765\uff0c\u63a5\u8fde\u4f24\u4e86\u51e0\u4e2a\u6b66\u58eb\u3002\u6b63\u5728\u8fd9\u5371\u9669 \u4e4b\u6781\u7684\u65f6\u5019\uff0c\u6070\u5de7\u6211\u54e5\u54e5\u90a3\u5929\u4e0b\u5c71\u8d2d\u7269\uff0c\u56de\u6765\u7684\u65f6\u5019\u78b0\u4e0a\u4e86\uff0c\u6211\u54e5\u54e5\u7a7a\u624b\u5236\u670d\u4e86\u90a3\u53ea\u51f6 \u6076\u7684\u7280\u725b\uff0c\u516c\u4e3b\u5341\u5206\u9ad8\u5174\u3002\u5c31\u8bf7\u6211\u54e5\u54e5\u505a\u5979\u7684\u968f\u4ece\u6b66\u58eb\uff0c\u6211\u54e5\u54e5\u7adf\u7136\u5fcd\u5fc3\u6487\u4e0b\u4e86\u6211\uff0c\u4e5f \u4e0d\u56de\u6765\u544a\u8bc9\u6211\uff0c\u5f53\u573a\u5c31\u7b54\u5e94\u4e86\u3002\u201d \u3000\u3000\u516c\u4e3b\u9053\uff1a\u201c\u597d\u59b9\u59b9\uff0c\u4f60\u73b0\u5728\u8fd8\u5728\u751f\u4f60\u54e5\u54e5\u7684\u6c14\u5417\uff1f\u201d\u7389\u73b2\u73d1\u7b11\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u7ed9\u6211\u5e26 \u6765\u8fd9\u6837\u4e00\u4f4d\u597d\u5ac2\u5ac2\uff0c\u6211\u662f\u9ad8\u5174\u8fd8\u6765\u4e0d\u53ca\u5462\u3002\u201d\u516c\u4e3b\u53f9\u98ce\u6c14\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u505a\u4e86\u6211\u7684\u968f\u4ece\u6b66 \u58eb\uff0c\u6211\u5374\u8fde\u7d2f\u4ed6\u53d7\u4e86\u4e00\u573a\u5927\u7978\u3002\u201d\u7389\u6606\u4ed1\u9053\uff1a\u201c\u4e0d\uff0c\u8fd9\u662f\u6211\u8fde\u7d2f\u4e86\u4f60\u3002\u201d\u7389\u73b2\u73d1\u7b11\u9053\uff1a \u201c\u597d\u5728\u73b0\u5728\u707e\u96be\u5df2\u6210\u8fc7\u53bb\uff0c\u4f60\u4eec\u4e5f\u4e0d\u5fc5\u5f7c\u6b64\u5f15\u548e\u4e86\u3002\u201d \u3000\u3000\u516c\u4e3b\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u8bf4\u8d77\u8fd9\u6b21\u707e\u96be\uff0c\u5361\u5170\u59ae\u59d1\u59d1\u7740\u5b9e\u5e2e\u4e86\u6211\u4e00\u628a\u5927\u5fd9\uff0c\u6211\u548c\u7389\u5927\u54e5\u8981 \u597d\uff0c\u6211\u54e5\u54e5\u672c\u6765\u662f\u4e0d\u77e5\u9053\u7684\u3002\u540e\u6765\u5c3c\u6cca\u5c14\u7684\u5e9f\u738b\u6765\u4e86\uff0c\u968f\u4ed6\u6765\u7684\u8fd8\u6709\u4e00\u5927\u6279\u6b66\u58eb\uff0c\u6211\u54e5 \u54e5\u4ef0\u4ed7\u4ed6\u4eec\u7684\u52a9\u529b\uff0c\u4e0d\u4f46\u548c\u90a3\u5e9f\u738b\u8ba2\u4e86\u8054\u76df\u7684\u5bc6\u7ea6\uff0c\u7ea6\u5b9a\u7531\u4ed6\u5148\u52a9\u6211\u54e5\u54e5\u541e\u5e76\u9a6c\u8428\u513f\u56fd\uff0c \u7136\u540e\u6211\u54e5\u54e5\u4e5f\u51fa\u53bb\u52a9\u4ed6\u590d\u56fd\uff1b\u800c\u4e14\u8fd8\u5f3a\u8feb\u6211\u5ac1\u7ed9\u90a3\u5e9f\u738b\u4f2a\u5e9f\u592a\u5b50\u3002 \u3000\u3000\u201c\u6211\u575a\u51b3\u4e0d\u80af\u7b54\u5e94\uff0c\u6211\u54e5\u54e5\u5341\u5206\u6c14\u6012\uff0c\u4e00\u9762\u7c91\u6211\u8f6f\u7981\u8d77\u6765\uff0c\u4e00\u9762\u6697\u5730\u8c03\u67e5\uff0c\u4e0d\u4e45\u5c31 \u7ed9\u4ed6\u67e5\u51fa\u4e86\u6211\u4eec\u7684\u79d8\u5bc6\u3002\u4ed6\u60f3\u51fa\u4e86\u4e00\u4e2a\u91dc\u5e95\u62bd\u85aa\u4e4b\u8ba1\uff0c\u4e00\u65e5\uff0c\u6545\u610f\u53eb\u7389\u5927\u54e5\u8ddf\u968f\u4ed6\u53bb\u6253 \u730e\uff0c\u7389\u5927\u54e5\u5c04\u6740\u4e86\u4e00\u53ea\u731b\u864e\uff0c\u6211\u54e5\u54e5\u5047\u501f\u916c\u8c22\u4ed6\u7684\u529f\u52b3\u4e3a\u540d\uff0c\u8d4f\u8d50\u4ed6\u4e00\u676f\u5fa1\u9152\uff0c\u9152\u4e2d\u4e0b \u4e86\u4fee\u7f57\u9165\u9aa8\u6563\uff0c\u8fd9\u662f\u6211\u4eec\u5185\u5e93\u7684\u79d8\u836f\uff0c\u670d\u98df\u4e4b\u540e\uff0c\u6c14\u529b\u7acb\u5373\u6d88\u5931\uff0c\u591a\u597d\u6b66\u529f\u4e5f\u4f7f\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u201c\u6211\u54e5\u54e5\u5c06\u4ed6\u62ff\u4e0b\u4e4b\u540e\uff0c\u8fd9\u624d\u5bf9\u4ed6\u8bf4\u9053\uff1a\u2018\u73b0\u5728\u6709\u4e24\u6761\u8def\u4efb\u4f60\u9009\u62e9\uff0c\u4e00\u6761\u662f\u751f\uff0c\u4e00 \u6761\u662f\u6b7b\u3002\u4f60\u82e5\u8981\u8d70\u751f\u8def\uff0c\u6211\u9001\u4e00\u4e2a\u5bab\u5973\u4e0e\u4f60\u6210\u5a5a\uff0c\u4f60\u987b\u4e0e\u516c\u4e3b\u4e00\u5200\u4e24\u65ad\u3002\u5426\u5219\u6211\u5c31\u628a\u4f60 \u5904\u6b7b\u3002\u2019\u7389\u5927\u54e5\u5f53\u573a\u6beb\u4e0d\u8e0c\u8e87\uff0c\u5c31\u9009\u62e9\u4e86\u6b7b\u8def\u3002\u6211\u54e5\u54e5\u672c\u662f\u60f3\u7528\u4ed6\u6765\u529d\u6211\u4f9d\u4ece\uff0c\u5e76\u65ad\u4e86 \u6211\u7684\u5ff5\u5934\u7684\u3002\u4e00\u65f6\u5012\u4e0d\u80af\u5c31\u5c06\u4ed6\u5904\u6b7b\uff0c\u4e8e\u662f\u628a\u4ed6\u6253\u5165\u4e86\u5929\u7262\u3002\u5e0c\u671b\u4ed6\u53d7\u4e0d\u8fc7\u62c6\u78e8\uff0c\u7ec8\u4e8e \u4ece\u547d\u3002 \u3000\u3000\u201c\u4e8b\u60c5\u8fc7\u4e86\u6ca1\u51e0\u5929\uff0c\u6070\u597d\u5361\u5170\u59ae\u59d1\u59d1\u53c8\u6765\u4e86\u3002\u8fd9\u65f6\u6211\u5df2\u5f97\u77e5\u7389\u5927\u54e5\u88ab\u6253\u4e0b\u5929\u7262\u7684\u4fe1 \u606f\uff0c\u5411\u5979\u6c42\u52a9\u3002\u5979\u8bf4\uff1a\u5979\u4e0d\u613f\u5f97\u7f6a\u7ae5\u59e5\u59e5\uff0c\u56e0\u6b64\u4e5f\u4e0d\u80fd\u5e2e\u52a9\u6211\u79c1\u9003\u51fa\u53bb\uff0c\u4f46\u5374\u53ef\u4ee5\u8bbe\u6cd5 \u628a\u7389\u5927\u54e5\u6551\u51fa\u6765\uff0c\u5979\u8981\u6211\u628a\u5185\u5e93\u7684\u89e3\u836f\u5077\u6765\u7ed9\u5979\uff0c\u5f53\u665a\u5979\u5c31\u5077\u5165\u5929\u7262\u9001\u7ed9\u7389\u5927\u54e5\uff0c\u4e0d\u8fc7 \u5979\u5374\u7559\u4e0b\u5b57\u6761\uff0c\u53eb\u7389\u5927\u54e5\u7b2c\u4e8c\u5929\u624d\u597d\u8d8a\u72f1\u3002\u540e\u6765\u6211\u4eec\u624d\u77e5\u9053\u5979\u7684\u7528\u610f\uff0c\u672c\u6765\u5979\u542f\u5df2\u4e5f\u53ef \u4ee5\u53bb\u5077\u89e3\u836f\uff0c\u5e76\u4e14\u5f53\u65f6\u5c31\u53ef\u4ee5\u628a\u7389\u5927\u54e5\u6551\u51fa\u6765\u7684\u3002\u5979\u4e0d\u80af\u8fd9\u6837\u505a\uff0c\u90a3\u662f\u56e0\u4e3a\u5979\u4e0d\u60f3\u5f15\u8d77 \u7ae5\u59e5\u59e5\u7591\u5fc3\u7684\u7f18\u6545\u3002\u5979\u7684\u672c\u9886\u4e5f\u771f\u662f\u96be\u4ee5\u601d\u8bae\uff0c\u4e0d\u77e5\u5979\u7528\u7684\u662f\u4ec0\u4e48\u6cd5\u5b50\uff0c\u5077\u5165\u5929\u7262\uff0c\u7adf \u662f\u65e0\u4eba\u77e5\u89c9\u3002 \u3000\u3000\u201c\u7389\u5927\u54e5\u8bd9\u590d\u4e86\u529f\u529b\u4e4b\u540e\uff0c\u8d8a\u72f1\u6613\u5982\u53cd\u638c\uff0c\u7b2c\u4e8c\u5929\u4fbf\u5f53\u7740\u7262\u5934\u7684\u9762\uff0c\u626d\u65ad\u4e86\u7262\u95e8\u7684 \u5927\u94c1\u9501\uff0c\u5927\u6447\u5927\u6446\u7684\u51fa\u6765\u4e86\u3002\u5929\u7262\u7684\u5b88\u536b\uff0c\u4eba\u4eba\u77e5\u9053\u4ed6\u7684\u5389\u5bb3\uff0c\u89c1\u4ed6\u529f\u529b\u5df2\u7ecf\u6062\u590d\uff0c\u54ea \u4e2a\u6562\u62e6\u963b\u4ed6\uff1f \u3000\u3000\u201c\u7389\u5927\u54e5\u51fa\u6765\u4e4b\u540e\uff0c\u5361\u5170\u59ae\u59d1\u59d1\u5077\u5077\u548c\u4ed6\u4f1a\u4e86\u4e00\u6b21\u9762\uff0c\u529d\u4ed6\u5343\u4e07\u4e0d\u53ef\u5192\u9669\u5165\u5bab\u6551\u6211\u3002 \u7389\u5927\u54e5\u5236\u5df2\u4e5f\u77e5\u9053\u4e0d\u662f\u7ae5\u59e5\u59e5\u7684\u5bf9\u624b\uff0c\u4fbf\u5411\u5979\u6c42\u8ba1\u3002\u7ed9\u7ae5\u59e5\u59e5\u9001\u672d\uff0c\u5e76\u5229\u7528\u90a3\u91d1\u6bdb\u72e1\u5c06 \u6211\u52ab\u51fa\u6df1\u5bab\u7684\u8ba1\u5212\uff0c\u4fbf\u662f\u5361\u5170\u59ae\u59d1\u59d1\u60f3\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u7389\u6606\u4ed1\u7b11\u9053\uff1a\u201c\u8bf4\u6765\u6709\u8da3\uff0c\u6211\u672c\u6765\u662f\u56fd\u7389\u6240\u8981\u7f09\u6355\u7684\u5fa1\u72af\uff0c\u4f46\u5f53\u6211\u6d3e\u4eba\u9001\u4fe1\u7ed9\u7ae5\u59e5 \u59e5\uff0c\u8bf4\u662f\u8981\u9001\u534f\u4e00\u6735\u2018\u96ea\u91cc\u7ea2\u5986\u2019\uff0c\u53ea\u6c42\u89c1\u516c\u4e3b\u4e00\u9762\u7684\u65f6\u5019\uff0c\u5979\u7acb\u5373\u4fbf\u7b54\u5e94\u4e86\u3002\u8fd8\u7b54\u5e94 \u51b3\u4e0d\u4e0e\u6211\u4e3a\u96be\uff0c\u7ed9\u4e86\u6211\u8bb8\u591a\u65b9\u4fbf\uff0c\u63a9\u62a4\u6211\u8fdb\u5b98\u5462\uff01\u201d \u3000\u3000\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u90a3\u662f\u5979\u81ea\u6043\u8fc7\u9ad8\uff0c\u6839\u672c\u6ca1\u628a\u4f60\u653e\u5728\u773c\u5185\u7684\u7f18\u6545\uff1a \u3000\u3000\u5374\u60f3\u4e0d\u5230\u4f60\u5e26\u4e86\u91d1\u6bdb\u72e1\u6765\u4f5c\u5e2e\u624b\uff0c\u5c45\u7136\u5728\u5979\u7684\u773c\u76ae\u5e95\u4e0b\uff0c\u5c06\u6211\u62a2\u8d70\u3002\u201d\u7389\u73b2\u73d1\u9053\uff1a \u201c\u4eba\u4eba\u90fd\u8bf4\u90a3\u8001\u5996\u5a46\u6b66\u529f\u65e0\u6545\uff0c\u54e5\u54e5\uff0c\u4f60\u548c\u5979\u4ea4\u4e86\u624b\u6ca1\u6709\uff1f\u201d \u3000\u3000\u738b\u6606\u4ed1\u9053\uff1a\u201c\u4ea4\u4e86\u4e00\u62db\uff0c\u679c\u7136\u5389\u5bb3\u3002\u4f60\u770b\uff01\u201d\u634b\u9ad8\u8896\u5b50\uff0c\u53ea\u89c1\u624b\u81c2\u4e0a\u4e94\u9053\u4f24\u75d5\uff0c\u6709 \u5982\u70d9\u8fc7\u4e00\u822c\uff0c\u4f17\u4eba\u770b\u4e86\uff0c\u4e0d\u7981\u9a87\u7136\u3002\u7389\u6606\u4ed1\u9053\uff1a\u201c\u5e78\u4e8f\u90a3\u91d1\u6bdb\u72e1\u6765\u5f97\u5feb\uff0c\u6211\u548c\u5979\u5bf9\u4e86\u4e00 \u638c\uff0c\u7acb\u5373\u4fbf\u62b1\u8d77\u516c\u4e3b\u8de8\u4e0a\u91d1\u6bdb\u72e1\u8dd1\u4e86\u3002\u5979\u53d7\u4e86\u6211\u4e00\u8bb0\u7384\u51b0\u795e\u638c\uff0c\u5927\u7ea6\u4e5f\u603b\u5f97\u8c03\u606f\u51e0\u4e2a\u65f6 \u8fb0\u3002\u201d \u3000\u3000\u7389\u73b2\u73d1\u7b11\u9053\uff1a\u201c\u4e5f\u5e78\u4e8f\u8fd9\u2018\u96ea\u91cc\u7ea2\u5986\u2019\uff0c\u521a\u597d\u5728\u524d\u5929\u5f00\u82b1\u3002\u8981\u4e0d\u7136\u9001\u522b\u6837\u793c\u7269\uff0c\u90a3 \u8001\u5996\u5a46\u5c31\u672a\u5fc5\u653e\u5728\u5fc3\u4e0a\u4e86\u3002\u201d\u7389\u6606\u4ed1\u9053\uff1a \u3000\u3000\u201c\u6211\u5374\u4e0d\u89e3\uff0c\u8fd9\u8001\u5996\u5a46\u8fd9\u4e48\u4e00\u5927\u628a\u5e74\u7eaa\u4e86\uff0c\u8fd8\u8981\u8fd9\u2018\u96ea\u91cc\u7ea2\u5986\u2019\u4f5c\u751a\uff1f\u201d \u3000\u3000\u516c\u4e3b\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u5012\u77e5\u9053\u3002\u4e0d\u662f\u5979\u8981\u7684\uff0c\u5979\u662f\u60f3\u9001\u7ed9\u4f0a\u58c1\u73e0\u739b\u7684\u3002\u8bf4\u4e5f\u5947\u602a\u3002\u4f0a\u58c1 \u73e0\u739b\u548c\u5361\u5170\u59ae\u662f\u5bf9\u59d0\u59b9\uff0c\u4f0a\u58c1\u73e0\u739b\u53ea\u6765\u770b\u8fc7\u5979\u4e00\u6b21\uff0c\u5361\u5170\u59ae\u59d1\u59d1\u5219\u5e38\u5e38\u6765\u770b\u5979\uff0c\u4f46\u5979\u4f3c \u4e4e\u7279\u522b\u559c\u6b22\u4f0a\u58c1\u73e0\u739b\uff0c\u65f6\u65f6\u90fd\u5728\u63d0\u8d77\u5979\uff0c\u5f97\u4e86\u4ec0\u4e48\u5b9d\u8d1d\uff0c\u4e5f\u603b\u662f\u8bf4\u8981\u7559\u8d77\u6765\u5f85\u5c06\u6765\u7ed9\u4f0a \u58c1\u73e0\u739b\u3002\u5bf9\u4e24\u59d0\u59b9\u7684\u6001\u5ea6\u5982\u6b64\u4e0d\u540c\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u7f18\u6545\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u95ee\u9053\uff1a\u201c\u5361\u5170\u59ae\u59d0\u59b9\u548c\u7ae5\u59e5\u59e5\u662f\u4ec0\u4e48\u5173\u7cfb\uff0c\u4f55\u4ee5\u5982\u6b64\u4eb2\u5bc6\uff0c\u516c\u4e3b\u53ef\u77e5\u9053\u4e48\uff1f\u201d \u516c\u4e3b\u9053\uff1a\u201c\u5361\u5170\u59ae\u59d1\u59d1\u6bcf\u6b21\u6765\u65f6\uff0c\u90fd\u662f\u5728\u5979\u623f\u91cc\u5173\u4e0a\u4e86\u623f\u95e8\u8bf4\u8bdd\u7684\u3002\u6709\u65f6\u738b\u5bab\u4e2d\u5176\u4ed6\u5730 \u65b9\uff0c\u6211\u4e5f\u5076\u7136\u548c\u5979\u4eec\u4e00\u8d77\uff0c\u53ea\u77e5\u9053\u5361\u5170\u59ae\u59d1\u5495\u79f0\u5979\u505a\u59e5\u59e5\uff0c\u5bf9\u4ed6\u5f88\u4e3a\u606d\u656c\u3002 \u3000\u3000\u81f3\u4e8e\u662f\u4ec0\u4e48\u5173\u7cfb\uff0c\u6211\u53ef\u4e0d\u77e5\u4e86\u3002\u201d \u3000\u3000\u7389\u6606\u4ed1\u9053\uff1a\u201c\u5361\u5170\u59ae\u53bb\u5e74\u548c\u5389\u590d\u751f\u66fe\u4e0a\u8fc7\u7075\u9e6b\u5cf0\uff0c\u505a\u8fc7\u6211\u4eec\u5144\u59b9\u7684\u5ba2\u4eba\u3002\u5f53\u65f6\u6211\u5e26 \u5979\u53bb\u770b\u90a3\u2018\u96ea\u91cc\u7ea2\u5986\u2019\u82b1\u6811\uff0c\u544a\u8bc9\u5979\u8fd9\u4e09\u8272\u5947\u82b1\u7684\u5947\u5999\u4e4b\u5904\u3002\u5979\u7fa1\u6155\u5f97\u5f88\u3002\u8fd9\u6b21\u5979\u732e\u8ba1 \u6211\u7ed9\u7ae5\u59e5\u59e5\u9001\u8fd9\u793c\u7269\uff0c\u6211\u731c\u5979\u81ea\u5df1\u5fc3\u91cc\u4e5f\u4e00\u5b9a\u60f3\u8981\u4e00\u6735\u3002\u4f46\u6211\u4e0d\u77e5\u5f00\u4e86\u51e0\u6735\uff1a \u3000\u3000\u6240\u4ee5\u5f53\u65f6\u4e0d\u6562\u7b54\u5e94\u3002\u201d\u7389\u73b2\u73d1\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u4e5f\u65e9\u5df2\u60f3\u5230\u4e86\u3002\u4e0d\u8fc7\uff0c\u6211\u5374\u628a\u8fd9\u4efd\u4eba \u60c5\uff0c\u8ba9\u4e86\u7ed9\u5389\u590d\u751f\u3002\u201d\u7389\u6606\u4ed1\u8c0a\uff1a\u201c\u5bf9\uff0c\u8fd9\u6837\u6700\u597d\u3002\u65e2\u916c\u8c22\u4e86\u5361\u5170\u59ae\u59d1\u59d1\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u53ef \u64ae\u5408\u5979\u548c\u5389\u590d\u751f\u7684\u59fb\u7f18\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u84e6\u5730\u60f3\u8d77\u4e00\u4e8b\uff0c\u95ee\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u4f60\u521a\u624d\u8bf4\u5230\u90a3\u4fee\u7f57\u9165\u9aa8\u6563\uff0c\u8bf4\u662f\u4e2d\u6bd2\u4e4b\u540e\uff0c \u6c14\u529b\u7acb\u5373\u6d88\u5931\uff0c\u4e0d\u77e5\u4f60\u8eab\u4e0a\u53ef\u8fd8\u6709\u591a\u4f59\u7684\u89e3\u836f\u4e48\uff1f\u201d\u516c\u4e3b\u9053\uff1a\u201c\u6211\u5f53\u65f6\u5077\u4e86\u5c0f\u534a\u74f6\uff0c\u7528 \u662f\u6ca1\u6709\u7528\u5b8c\uff0c\u53ef\u662f\u7389\u5927\u54e5\u8d8a\u72f1\u4e4b\u540e\uff0c\u6211\u54e5\u54e5\u7acb\u5373\u731c\u5230\u662f\u6211\u5077\u7684\u89e3\u836f\uff0c\u5269\u4e0b\u7684\u7b2c\u4e8c\u5929\u5c31\u88ab \u641c\u53bb\u4e86\u3002\u4e5f\u662f\u56e0\u6b64\uff0c\u4ed6\u624d\u53eb\u7ae5\u59e5\u59e5\u5c06\u6211\u4e25\u52a0\u7ba1\u675f\u7684\u3002\u4f60\u4e3a\u4ec0\u4e48\u95ee\u8d77\u89e3\u836f\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u6211\u6000\u7591\u6211\u59b9\u59b9\u4e5f\u662f\u53d7\u4e86\u6b64\u6bd2\u3002\u516c\u4e3b\uff0c\u4f60\u89c1\u8fc7\u6211\u59b9\u59b9\u7684\u5915\u53ef\u770b\u5f97\u51fa\u6765 \u4e48\uff1f\u201d\u516c\u4e3b\u9053\uff1a\u201c\u4e2d\u4e86\u4fee\u7f57\u9165\u9aa8\u6563\u7684\u6bd2\uff0c\u9762\u8272\u6beb\u65e0\u5f02\u6837\uff0c\u662f\u770b\u4e0d\u51fa\u6765\u7684\u3002\u54ce\u5440\uff0c\u4f60\u59b9\u59b9 \u6b66\u529f\u6781\u9ad8\uff0c\u7ae5\u59e5\u59e5\u548c\u6211\u54e5\u54e5\u5374\u653e\u5fc3\u8ba9\u5979\u5728\u5bab\u4e2d\u5230\u5904\u884c\u8d70\uff0c\u4e5d\u6210\u662f\u4e2d\u4e86\u6b64\u6bd2\u4e86\uff01\u201d \u3000\u3000\u4e91\u743c\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u73b0\u653e\u7740\u4e00\u4e2a\u5973\u56fd\u624b\u5728\u6b64\uff0c\u8981\u89e3\u6b64\u6bd2\uff0c\u53c8\u6709\u4f55\u96be\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u84e6\u5730 \u7701\u8d77\uff0c\u4fbf\u5230\u534e\u4e91\u78a7\u8ddf\u524d\u65bd\u4e86\u4e00\u793c\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5bf9\u5566\uff0c\u4ee4\u5c0a\u662f\u5f53\u4ee3\u795e\u533b\uff0c\u5584\u89e3\u767e\u6bd2\uff0c\u534e\u59d1\u5a18\u5bb6\u5b66\u6e0a\u6e90\uff0c\u5018\u80af\u8d50\u52a9\uff0c\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u53c8\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u610f\u56fe\u62ff\u6211\u7684\u59b9\u59b9\u6765\u8981\u631f\u6211\uff0c\u6211\u82e5\u65e0\u9700\u6c42\u4ed6\u4eec\u7684\u89e3\u836f\uff0c\u90a3\u5c31\u53ea\u8981\u8bbe\u6cd5\u5c06\u6211 \u7684\u59b9\u59b9\u5f04\u51fa\u6765\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u9053\uff1a\u201c\u6211\u7684\u836f\u56ca\u5012\u8fd8\u6ca1\u4e22\u5931\uff0c\u4f46\u5fc5\u987b\u7ecf\u8fc7\u8bca\u65ad\u3002\u624d\u80fd\u5bf9\u75c7\u4e0b\u836f\u3002\u201d\u4e91\u743c\u7b11\u9053\uff1a \u201c\u90a3\u4f60\u5c31\u5e94\u8be5\u548c\u6211\u4eec\u4e00\u8d77\u53bb\u554a\u3002\u201d\u534e\u4e91\u78a7\u9053\uff1a\u201c\u4f60\u521a\u624d\u53ef\u6ca1\u6709\u65f6\u6211\u8bf4\u8fc7\u8981\u53bb\u6606\u5e03\u5170\u56fd\u90fd\uff1f\u201d \u3000\u3000\u4e91\u743c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u5144\u59b9\u672c\u662f\u548c\u4f60\u7684\u7239\u7239\u6765\u627e\u4f60\u7684\uff0c\u5982\u4eca\u65e2\u7136\u627e\u7740\u4f60\u4e86\uff0c\u4f60\u7684\u75c5 \u4e5f\u5df2\u597d\u4e86\uff0c\u96be\u9053\u54b1\u4eec\u5c31\u6b64\u56de\u5bb6\u4e48\uff1f\u6c5f\u6d77\u5929\u4e8e\u6211\u6709\u6551\u547d\u4e4b\u6069\uff0c\u6211\u53ea\u53ef\u60dc\u81ea\u5df1\u4e0d\u61c2\u533b\u672f\uff0c\u5e2e \u4e0d\u4e86\u4ed6\u7684\u5fd9\uff0c\u4f46\u53bb\u603b\u662f\u8981\u53bb\u7684\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5373\u662f\u8bf4\uff0c\u5c31\u53ea\u770b\u5728\u6c5f\u6d77\u5929\u7684\u4efd\u4e0a\uff0c\u4e5f\u5e94\u8be5 \u53bb\u6551\u8c37\u4e2d\u83b2\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u8fd9\u624d\u60f3\u8d77\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u77e5\u8fd9\u4f4d\u534e\u59d1\u5a18\u5bf9\u6211\u59b9\u59b9\u662f\u5426\u8fd8\u6709\u5992\u610f\uff1f\u54ce\u5440\uff0c\u4e91\u5927 \u54e5\u5374\u5728\u8fd9\u65f6\u5019\u63d0\u8d77\u4e86\u6c5f\u5e08\u5144\u6765\uff0c\u4e5f\u672a\u514d\u5927\u4e0d\u8bc6\u65f6\u5b9c\u4e86\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u534e\u4e91\u78a7\u988a\u6cdb\u7ea2 \u6655\uff0c\u8bf4\u9053\uff1a\u201c\u96be\u9053\u4f60\u4ee5\u4e3a\u6211\u4e0d\u60f3\u6551\u4eba\u4e48\uff1f\u6211\u53ea\u6015\u81ea\u5df1\u7684\u533b\u672f\u4e0d\u591f\u9ad8\u660e\u800c\u5df2\u3002\u597d\uff0c\u4f60\u65e2\u7136 \u8fd9\u4e48\u8bf4\uff0c\u6211\u5c31\u966a\u4f60\u4e00\u9053\u53bb\u6551\u8c37\u59d1\u5a18\u5c31\u662f\u3002\u201d \u3000\u3000\u539f\u6765\u534e\u4e91\u78a7\u5012\u5e76\u975e\u5fc3\u80f8\u72ed\u7a84\uff0c\u4f46\u5979\u5374\u4e5f\u7684\u786e\u6709\u8fc7\u8fd9\u6837\u7684\u5ff5\u5934\uff0c\u4e3a\u4e86\u907f\u514d\u6311\u8d77\u521b\u4f24\uff0c \u6253\u7b97\u4ece\u4eca\u4e4b\u540e\uff0c\u4e0d\u518d\u89c1\u6c5f\u6d77\u5929\u548c\u8c37\u4e2d\u83b2\u4e8c\u4eba\u3002\u4f46\u5982\u4eca\u662f\u4e91\u743c\u6c42\u5979\u540c\u53bb\uff0c\u60c5\u5f62\u4fbf\u53c8\u4e0d\u540c\uff0c \u867d\u7136\u5979\u8fd8\u672a\u66fe\u5c06\u5bf9\u6c5f\u6d77\u5929\u7684\u611f\u60c5\uff0c\u5b8c\u5168\u79fb\u5230\u4e91\u743c\u8eab\u4e0a\uff0c\u4f46\u5df2\u89c9\u5f97\u548c\u4e91\u743c\u5728\u4e00\u8d77\uff0c\u4e5f\u5c31\u4e0d \u6015\u9762\u5bf9\u6c5f\u6d77\u5929\u548c\u8c37\u4e2d\u83b2\u4e86\u3002\u8fd9\u662f\u4e00\u79cd\u5fae\u5999\u7684\u5c11\u5973\u5fc3\u7406\uff0c\u53ea\u6709\u5728\u5979\u627e\u5230\u4e86\u7537\u53cb\u4e4b\u540e\uff0c\u624d\u6562 \u5766\u7136\u9762\u5bf9\u4ece\u524d\u7684\u604b\u4eba\u3002\u5510\u52aa\u73e0\u7a46\u548c\u4e91\u743c\u90fd\u4e0d\u61c2\u5f97\u5979\u5982\u6b64\u5fae\u5999\u7684\u5fc3\u60c5\uff0c\u4f46\u542c\u5f97\u5979\u6168\u7136\u7b54\u5e94 \u540c\u53bb\uff0c\u7686\u662f\u5927\u559c\uff1a \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e91\u58c1\u4ea6\u5df2\u6765\u5230\uff0c\u89c1\u4e86\u5979\u7684\u54e5\u54e5\uff0c\u81ea\u6709\u4e00\u756a\u9ad8\u5174\uff0c\u4e0d\u5fc5\u7ec6\u8868\u3002\u7389\u73b2\u73d1\u7b11\u9053\uff1a \u201c\u4eba\u5230\u9f50\u4e86\uff0c\u8bf7\u5bb9\u8bb8\u6211\u7565\u5c3d\u5730\u4e3b\u4e4b\u8c0a\uff0c\u656c\u4f60\u4eec\u51e0\u676f\u6de1\u9152\u3002\u201d\u9152\u83dc\u90fd\u662f\u51c6\u5907\u597d\u4e86\u7684\uff0c\u7acb\u5373 \u4f7f\u9001\u4e0a\u6765\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u7389\u59d1\u5a18\u771f\u662f\u4f4d\u597d\u4e3b\u4eba\uff0c\u4f46\u6211\u5374\u662f\u4e2a\u6076\u5ba2\uff0c\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u521a\u624d\u9965\u4e0d \u62e9\u98df\uff0c\u65e9\u5df2\u62a2\u4e86\u4f60\u4eec\u8981\u7ed9\u91d1\u6bdb\u72e1\u7684\u4e00\u5927\u5757\u725b\u8089\u4e86\u3002\u201d\u8bf4\u51fa\u6b64\u4e8b\uff0c\u4f17\u4eba\u65e0\u4e0d\u5931\u7b11\u3002 \u3000\u3000\u5e2d\u4e0a\u4eba\u4eba\u5174\u9ad8\u91c7\u70c8\uff0c\u53ea\u6709\u6606\u5e03\u5170\u56fd\u516c\u4e3b\u53cc\u7709\u7d27\u8e59\uff0c\u5982\u6709\u9690\u5fe7\uff0c\u7389\u6606\u4ed1\u9053\uff1a\u201c\u54b1\u4eec\u5df2 \u7ecf\u9003\u51fa\u7262\u7b3c\uff0c\u516c\u4e3b\uff0c\u4f60\u8fd8\u5bb3\u6015\u4ec0\u4e48\uff1f\u201d\u516c\u4e3b\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u4e0d\u662f\u4e45\u5c45\u4e4b\u5730\uff0c\u7389\u5927\u54e5\uff0c\u54b1\u4eec \u5f97\u627e\u4e2a\u5bb9\u8eab\u4e4b\u5730\u624d\u884c\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u5730\u65b9\u672c\u662f\u5979\u7237\u7237\u7684\u4e00\u5ea7\u884c\u5bab\uff0c\u5979\u7237\u7237\u75bc\u60dc\u5979\uff0c\u900a\u4f4d\u4e4b\u540e\uff0c\u5c06\u8fd9\u884c\u5bab\u8d50\u7ed9\u5979\u4f5c \u522b\u5885\u3002\u8fd9\u91cc\u7684\u4f8d\u5973\u4e5f\u90fd\u662f\u5e73\u65e5\u670d\u4f8d\u4ed6\u7684\u5bab\u5a25\u3002\u56e0\u5979\u548c\u7389\u5bb6\u5144\u59b9\u7684\u7279\u6b8a\u4ea4\u60c5\uff0c\u5979\u4e0d\u4f4f\u7684\u65f6 \u5019\uff0c\u7389\u73b2\u73d1\u4fbf\u7b49\u4e8e\u662f\u6b64\u95f4\u7684\u4e3b\u4eba\u4e86\u3002 \u3000\u3000\u7389\u6606\u4ed1\u7fdf\u7136\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u54e5\u54e5\u662f\u77e5\u9053\u8fd9\u4e2a\u5904\u6240\u7684\uff0c\u8fdf\u65e9\u603b\u4f1a\u6d3e\u4eba\u5230\u8fd9\u91cc \u6765\u5bfb\u4f60\u3002\u6211\u7684\u8001\u5bb6\u5728\u7075\u9e6b\u5cf0\u571f\uff0c\u4f46\u5374\u4e0d\u9002\u5b9c\u4e8e\u4f60\u5c45\u4f4f\u3002\u516c\u4e3b\uff0c\u54b1\u4eec\u4e0d\u5982\u8fdc\u8d70\u9ad8\u98de\uff0c\u5230\u4e00 \u4e2a\u6ca1\u4eba\u8ba4\u8bc6\u54b1\u4eec\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u516c\u4e3b\u53f9\u4e86\u53e3\u6c14\uff0c\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u6211\u820d\u4e0d\u5f97\u6211\u7684\u7237\u7237\uff0c\u6211\u4e5f\u820d\u4e0d\u5f97\u79bb\u5f00\u6211\u7684\u56fd\u571f\u3002\u4f46\u65e2 \u7136\u65e0\u5904\u53ef\u4ee5\u5bb9\u8eab\uff0c\u6211\u4e5f\u53ea\u597d\u968f\u4f60\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u60f3\u4e86\u4e00\u4f1a\uff0c\u5ffd\u4ed6\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u4f60\u548c\u6211\u4eec\u4e00\u9053\u8d70\u597d\u5417\uff1f\u201d\u516c\u4e3b\u6014\u4e86\u4e00\u6014\uff0c \u8bf4\u9053\uff1a\u201c\u548c\u4f60\u4eec\u4e00\u9053\u8d70\uff1f\u4f60\u4eec\u4e0d\u6b63\u662f\u8981\u524d\u5f80\u6211\u56fd\u4eac\u90fd\u5417\uff1f\u8fd9\u4e0d\u662f\u53c8\u56de\u53bb\u81ea\u6295\u7f57\u7f51\uff1f\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u8bf4\u9053\uff1a\u201c\u8d35\u6211\u4e24\u56fd\uff0c\u4e16\u4ee3\u76f8\u597d\u3002\u4f60\u54e5\u54e5\u867d\u7136\u4e00\u65f6\u7cca\u6d82\uff0c\u53d7\u4eba\u62e8\u5f04\uff0c\u610f\u56fe\u541e \u5e76\u6211\u56fd\uff0c\u6211\u5374\u51b3\u4e0d\u60f3\u548c\u8d35\u56fd\u6253\u4ed7\u3002\u6211\u6b64\u53bb\u5c31\u662f\u8981\u7aed\u5c3d\u6240\u80fd\uff0c\u6d88\u6d31\u5175\u620e\u7684\u3002\u5c3c\u6cca\u5c14\u5e9f\u738b\u5728 \u4f60\u4eec\u90a3\u91cc\u5174\u98ce\u4f5c\u6d6a\uff0c\u82e5\u4e0d\u5c06\u4ed6\u4eec\u9010\u8d70\uff0c\u5bf9\u4f60\u4eec\u4e5f\u662f\u5fc3\u8179\u4e4b\u60a3\uff0c\u6240\u4ee5\u6211\u6b64\u53bb\u4e5f\u60f3\u529d\u544a\u4f60\u7684 \u54e5\u54e5\uff0c\u6211\u81ea\u4fe1\u6709\u51e0\u5206\u628a\u63e1\u53ef\u4ee5\u4ee4\u4ed6\u9192\u609f\u3002\u201d \u3000\u3000\u516c\u4e3b\u8bf4\u9053\uff1a\u201c\u679c\u80fd\u5982\u6b64\uff0c\u90a3\u662f\u6211\u56fd\u5927\u5e78\uff0c\u6211\u4e5f\u53ef\u4ee5\u653e\u5fc3\u56de\u53bb\u4e86\u3002\u4f46\u4e8b\u60c5\u6210\u8d25\uff0c\u672a\u53ef \u9884\u535c\uff0c\u6211\u4e00\u56de\u5230\u4eac\u57ce\uff0c\u53ea\u6015\u5c31\u6709\u5371\u9669\u53d1\u751f\u3002\u83ab\u8bf4\u6211\u54e5\u54e5\u624b\u4e0b\u6b66\u571f\u5982\u4e91\uff0c\u53ea\u90a3\u7ae5\u59e5\u59e5\u4e00\u4eba\uff0c \u5df2\u96be\u5bf9\u4ed8\u3002\u201d\u5510\u52aa\u73e0\u7a46\u7b11\u9053\uff1a\u201c\u6211\u6709\u4e2a\u5e08\u5144\uff0c\u6b64\u65f6\u5df2\u7ecf\u5148\u5230\u4f60\u4eec\u7684\u4eac\u57ce\u4e86\u3002\u5018\u82e5\u771f\u662f\u975e \u7528\u6b66\u529b\u4e0d\u53ef\u7684\u8bdd\uff0c\u90a3\u91cd\u59e5\u59e5\u51b3\u4e0d\u662f\u6211\u5e08\u5144\u5bf9\u624b\uff0c\u4f60\u5927\u53ef\u653e\u5fc3\uff0c\u201d \u3000\u3000\u534e\u4e91\u78a7\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4e2a\u529e\u6cd5\uff0c\u516c\u4e3b\uff0c\u6211\u66ff\u4f60\u7565\u65bd\u5c0f\u672f\uff0c\u53d8\u5bb9\u6613\u8c8c\uff0c\u5305\u4f60\u6ca1\u4eba\u8ba4\u5f97\u4f60\u3002\u201d \u5510\u52aa\u73e0\u7a46\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u66f4\u597d\u4e86\uff0c\u90a3\u54b1\u4eec\u8fdb\u57ce\u4e4b\u540e\uff0c\u4fbf\u53ef\u4ee5\u4ece\u5bb9\u4e0d\u8feb\uff0c\u76f8\u673a\u884c\u4e8b\u4e86\u3002\u514d\u5f97 \u4e00\u5230\u5c31\u60f9\u51fa\u9ebb\u70e6\u3002\u201d\u7389\u6606\u4ed1\u5144\u59b9\u8bf4\u9053\uff1a\u201c\u534e\u59d1\u5a18\u65e2\u64c5\u4e8e\u53d8\u5bb9\u6613\u8c8c\u4e4b\u672f\uff0c\u90a3\u6211\u4eec\u5144\u59b9\u4e5f\u53ef \u4ee5\u4f1a\u4e86\u3002\u8bf7\u534e\u59d1\u5a18\u4e00\u4e95\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u534e\u4e91\u78a7\u95ee\u7389\u73b2\u73d1\u53d6\u56de\u836f\u56ca\uff0c\u836f\u56ca\u91cc\u8fd8\u6709\u51e0\u9897\u6613\u5bb9\u4e39\uff0c\u518d\u8981\u4e86\u4e00\u4e9b\u666e\u901a\u7684\u5316\u5986\u54c1\u3001\u679c \u7136\u7ecf\u8fc7\u5979\u65bd\u672f\u4e4b\u540e\uff0c\u7389\u5bb6\u5144\u59b9\u548c\u516c\u4e3b\u90fd\u4f3c\u6362\u4e86\u4e2a\u4eba\u3002\u516c\u4e3b\u7167\u4e86\u7167\u955c\uff0c\u5927\u559c\u8bf4\u9053\uff1a\u201c\u6b64\u523b\uff0c \u6211\u5c31\u662f\u7ad9\u5728\u6211\u54e5\u54e5\u9762\u524d\uff0c\u53ea\u6015\u4ed6\u672a\u5fc5\u8ba4\u5f97\u6211\u4e86\u3002\u8fd9\u51e0\u65e5\u6b63\u662f\u4eac\u90fd\u795e\u5e99\u4e3e\u884c\u4e00\u5e74\u4e00\u5ea6\u7684\u5f00 \u5149\u5927\u5178\u671f\u95f4\uff0c\u8bb8\u591a\u9999\u5ba2\u8fdb\u57ce\uff0c\u54b1\u4eec\u8fd9\u4e2a\u65f6\u5019\u524d\u5f80\uff0c\u6700\u4e0d\u6613\u60f9\u4eba\u6ce8\u610f\u3002\u201d \u3000\u3000\u5927\u5bb6\u90fd\u5728\u79f0\u8d5e\u534e\u4e91\u78a7\u7684\u5999\u672f\uff0c\u5e76\u5b9a\u4e86\u660e\u65e5\u4e00\u65e9\u4fbf\u5373\u52a8\u8eab\u3002\u534e\u4e91\u78a7\u5374\u662f\u5fc3\u4e8b\u5982\u6f6e\uff0c\u5bfb \u601d\uff1a\u201c\u6c5f\u6d77\u5929\u4e0d\u77e5\u8fd8\u4f1a\u5fc6\u5ff5\u6211\u5426\uff1f\u5509\uff0c\u8fd9\u65f6\uff0c\u4ed6\u6216\u8bb8\u5df2\u7ecf\u89c1\u770b\u8c37\u4e2d\u83b2\u4e86\uff1f\u5509\uff0c\u4f46\u613f\u4ed6\u4eec \u8fc7\u5f97\u5feb\u5feb\u6d3b\u6d3b\uff0c\u6211\u4e5f\u65e0\u987b\u70e6\u607c\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6b64\u65f6\u6b63\u5728\u8def\u4e0a\uff0c\u5df2\u7ecf\u53ef\u4ee5\u671b\u5f97\u89c1\u6606\u5e03\u5170\u56fd\u4eac\u57ce\u7684\u57ce\u5899\u4e86\u3002\u4ed6\u7ecf\u8fc7\u4e86\u8bb8\u591a\u610f\u5916\uff0c \u53c8\u672a\u77e5\u4e91\u743c\u7b49\u4eba\u5df2\u7ecf\u83b7\u6551\uff0c\u5fc3\u91cc\u6781\u4e3a\u4f24\u75db\u3002\u534e\u4e91\u78a7\u5728\u601d\u5ff5\u4ed6\uff0c\u4ed6\u4e5f\u8fd8\u5728\u601d\u5ff5\u534e\u4e91\u78a7\u7684\uff0c \u4ed6\u4e5f\u6b63\u5728\u5fc3\u60f3\uff1a\u201c\u4f46\u613f\u78a7\u59b9\u4e0e\u90a3\u767d\u8863\u5c11\u5e74\u80fd\u6210\u7737\u5c5e\uff0c\u6211\u4e5f\u65e0\u987b\u70e6\u607c\u4e86\u3002\u201d\u6b63\u662f\uff1a \u3000\u3000\u4f46\u613f\u77e5\u4ea4\u80fd\u5e78\u798f\uff0c\u4e24\u4eba\u5fc3\u4e8b\u4e00\u822c\u540c\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e94\u5341\u56de\u3000\u5f02\u56fd\u95ef\u5bab\u9047\u5996\u5987\u3000\u51b0\u6cb3\u6bd4\u5251\u7ed3\u65b0\u4ea4 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e94\u5341\u56de\u3000\u5f02\u56fd\u95ef\u5bab\u9047\u5996\u5987\u3000\u51b0\u6cb3\u6bd4\u5251\u7ed3\u65b0\u4ea4 \u3000\u3000\u963f\u5c14\u6cf0\u5c71\u873f\u8712\u800c\u6765\uff0c\u5230\u4e86\u6b64\u5730\uff0c\u4e0e\u5929\u5c71\u5317\u51fa\u7684\u652f\u8109\u4f1a\u5408\uff0c\u4fe8\u4f3c\u5de8\u4eba\u7684\u53cc\u81c2\uff0c\u62e5\u62b1\u7740 \u4e00\u5927\u7247\u5e73\u539f\uff0c\u8fd9\u5c31\u662f\u6606\u5e03\u5170\u56e0\u7684\u56fd\u571f\u4e86\u3002 \u3000\u3000\u6606\u5e03\u5170\u56fd\u7684\u4eac\u57ce\u540d\u53eb\u5e0c\u5580\u4ec0\u5c14\uff0c\u6b63\u5750\u843d\u5728\u4e24\u6761\u5c71\u8109\u4f1a\u5408\u4e4b\u5904\uff0c\u501a\u5c71\u4fee\u5efa\uff0c\u5f62\u52bf\u9669\u8981\uff0c \u771f\u6709\u4e00\u592b\u5f53\u5173\uff0c\u4e07\u592b\u96be\u8d8a\u4e4b\u52bf\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u60f3\uff1a\u201c\u6bd4\u5730\u82e5\u662f\u95ed\u5173\u81ea\u5b88\uff0c\u65e0\u6b8a\u4e16\u5916\u6843\u6e90\uff0c\u6606\u5e03\u5170\u56fd\u7684\u56fd\u738b\u5374\u4ecd\u4e0d\u6ee1\u8db3\uff0c \u5984\u56fe\u5411\u90bb\u56fd\u5927\u52a8\u5e72\u6208\uff0c\u5b9e\u662f\u611a\u6627\u6781\u4e86\u3002\u9a6c\u8428\u513f\u56fd\u6bd4\u5b83\u5f3a\u5927\u5f97\u591a\uff0c\u5e78\u4e8f\u662f\u73e0\u7a46\u5e08\u5f1f\u4e3a\u738b\uff0c \u4ed6\u4e00\u5fc3\u8981\u6d88\u6d31\u5175\u620e\u3002\u5426\u5219\u53ea\u6015\u8fd9\u4e16\u5916\u6843\u6e90\uff0c\u4e5f\u8981\u53d8\u6210\u7126\u571f\u3002\u5509\uff0c\u4f46\u613f\u5e08\u5f1f\u80fd\u591f\u9022\u51f6\u5316\u5409\uff0c \u9047\u96be\u6210\u4f6f\uff0c\u90a3\u5c31\u662f\u4e24\u56fd\u767e\u59d3\u4e4b\u798f\u4e86\u3002\u201d\u4ed6\u672a\u77e5\u5510\u52aa\u73e0\u7a46\u4e1a\u5df1\u8131\u4fed\uff0c\u5230\u4e86\u6606\u5e03\u5170\u56fd\u7684\u4eac\u57ce\uff0c \u5fc3\u5934\u66f4\u4e3a\u6df1\u91cd\uff0c\u5bfb\u601d\uff1a\u201c\u82e5\u662f\u73e0\u7a46\u5e08\u5f1f\u6709\u751a\u4e0d\u6d4b\uff0c\u6211\u4e0d\u4f46\u8981\u6551\u83b2\u59b9\u3002\u8fd8\u8981\u66ff\u4ed6\u6311\u8d77\u8fd9\u526f \u91cd\u62c5\uff0c\u8bbe\u6cd5\u6d88\u9975\u4e24\u56fd\u7684\u5175\u620e\u4e86\u3002\u201d\u4ece\u5510\u52aa\u73e0\u7a46\u53c8\u60f3\u5230\u8c37\u4e2d\u83b2\uff0c\u4ed6\u5bf9\u8c37\u4e2d\u83b2\u7684\u906d\u9047\u6beb\u65e0\u6240 \u77e5\uff0c\u66f4\u662f\u60f4\u60f4\u4e0d\u5b89\u3002 \u3000\u3000\u8fd9\u51e0\u65e5\u662f\u5e0c\u5580\u4ec0\u5c14\u4eac\u90fd\u795e\u5e99\u4e3e\u884c\u5f00\u5149\u5927\u5178\u7684\u671f\u95f4\uff0c\u5404\u5730\u9999\u5ba2\u7edc\u7ece\u4e0d\u7edd\uff0c\u5176\u4e2d\u4e5f\u6709\u6c49 \u65cf\u7684\u9999\u5ba2\u3002\u6c5f\u6d77\u5929\u4e70\u4e86\u4e00\u675f\u85cf\u9999\uff0c\u4e5f\u626e\u6210\u9999\u5ba2\u7684\u6a21\u6837\u8fdb\u57ce\uff0c\u5012\u4e5f\u65e0\u4eba\u6ce8\u610f\u3002\u6606\u5e03\u5170\u56fd\u662f \u4f5b\u6559\u56fd\u5bb6\u3002\u4eac\u57ce\u91cc\u5bfa\u5e99\u5f88\u591a\uff0c\u8fd9\u51e0\u65e5\u9664\u4e86\u4eac\u90fd\u795e\u5e99\u4e4b\u5916\uff0c\u5176\u4ed6\u5927\u5c0f\u5bfa\u5e99\u4e00\u5f8b\u5f00\u653e\uff0c\u4efb\u7531 \u9999\u5ba2\u501f\u5bbf\u3002\u6c5f\u6d77\u5929\u501f\u5bbf\u7684\u90a3\u95f4\u5bfa\u5e99\uff0c\u6b63\u597d\u662f\u6700\u9760\u8fd1\u738b\u5bab\u7684\u4e00\u95f4\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6025\u7740\u8981\u6253\u63a2\u53e4\u4e2d\u83b2\u4e0b\u843d\uff0c\u4e0d\u5f85\u5510\u52aa\u73e0\u7a46\u6765\u5230\uff0c\u5f53\u665a\u5c31\u5355\u4eba\u5339\u9a6c\uff0c\u72ec\u63a2\u738b\u5bab\u3002 \u8fd9\u662f\u4e00\u4e2a\u65e0\u661f\u65e0\u6708\u7684\u665a\u4e0a\uff0c\u4e94\u6b65\u4e4b\u5916\uff0c\u4e0d\u89c1\u666f\u7269\uff0c\u6c5f\u6d77\u5929\u6697\u6697\u6b22\u559c\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u6b63\u662f\u591c \u884c\u4eba\u6700\u597d\u7684\u65f6\u673a\u3002\u201d \u3000\u3000\u738b\u5bab\u5efa\u5728\u5c71\u811a\uff0c\u867d\u7136\u662f\u6f06\u9ed1\u7684\u665a\u4e0a\uff0c\u4f46\u5c71\u4e0a\u51b0\u6cb3\u4ea4\u9519\uff0c\u5b9b\u82e5\u6e38\u9f99\uff0c\u5728\u5c71\u9876\u6cdb\u51fa\u4e00\u7247 \u767d\u832b\u832b\u7684\u666f\u8272\uff0c\u51b0\u96ea\u6620\u7167\uff0c\u867d\u8bf4\u662f\u8ddd\u79bb\u5927\u8fdc\uff0c\u4e5f\u6709\u4e00\u70b9\u70b9\u5fae\u5149\uff0c\u6c5f\u6d77\u5929\u6b66\u529f\u6df1\u6e5b\uff0c\u76ee\u529b \u5f02\u4e8e\u5e38\u4eba\uff0c\u501f\u7740\u8fd9\u4e00\u70b9\u70b9\u51b0\u96ea\u5fae\u5149\uff0c\u5df2\u7ecf\u53ef\u4ee5\u770b\u6e05\u9053\u8def\u3002\u795e\u4e0d\u77e5\u9b3c\u4e0d\u515a\u7684\u8fdb\u4e86\u6606\u5e03\u5170\u56fd \u7684\u738b\u5bab\u7981\u82d1\u3002 \u3000\u3000\u4f46\u7acb\u5373\u5c31\u78b0\u5230\u4e00\u4e2a\u96be\u9898\uff0c\u6606\u5e03\u5170\u56fd\u7684\u738b\u5bab\uff0c\u89c4\u6a21\u4e4b\u5927\u867d\u4e0d\u53ca\u9a6c\u8428\u513f\u56fd\uff0c\u4f46\u4e5f\u6709\u6570\u767e \u5e62\u5bab\u6bbf\u5efa\u7b51\uff0c\u53c2\u5dee\u9519\u843d\uff0c\u661f\u7f57\u68cb\u5e03\u3002\u8c37\u4e2d\u83b2\u662f\u5426\u88ab\u56da\u5728\u5bab\u4e2d\uff0c\u6c5f\u6d77\u5929\u56fa\u7136\u4e0d\u77e5\uff0c\u5373\u4f7f\u786e \u662f\u56da\u5728\u5bab\u4e2d\uff0c\u8981\u5728\u8fd9\u51e0\u767e\u5e62\u5bab\u6bbf\u5efa\u7b51\u4e2d\u627e\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u90a3\u4e5f\u662f\u96be\u5230\u6781\u70b9\uff0c\u65e0\u6b8a\u6d77\u91cc\u635e\u9488 \u4e86\u3002\u4ece\u524d\u4ed6\u548c\u5510\u52aa\u73e0\u7a46\u5144\u59b9\u5077\u8fdb\u9a6c\u8428\u513f\u56fd\u738b\u5bab\uff0c\u627e\u90a3\u5978\u738b\u76d6\u6e29\u62a5\u4ec7\u4e4b\u65f6\uff0c\u8fd8\u6709\u5510\u52aa\u5473\u7a46 \u7684\u4e00\u5f20\u5730\u56fe\u6307\u8def\uff0c\u5982\u4eca\u4ed6\u5355\u8eab\u4e00\u4eba\uff0c\u5374\u662f\u6beb\u65e0\u51ed\u501f\uff0c\u53ea\u80fd\u76f2\u6478\u778e\u649e\uff0c\u7948\u6c42\u4e0a\u5929\u4fdd\u4f51\uff0c\u5e0c \u671b\u80fd\u6709\u5947\u8ff9\u51fa\u73b0\u4e86\u3002 \u3000\u3000\u5947\u8ff9\u672a\u66fe\u51fa\u73b0\uff0c\u5374\u51fa\u73b0\u4e86\u654c\u4eba\u3002\u6c5f\u6d77\u5929\u6b63\u5728\u524d\u884c\uff0c\u5ffd\u89c9\u80cc\u540e\u6709\u547c\u5438\u7684\u6c14\u606f\u3002 \u3000\u3000\u547c\u5438\u7684\u6c14\u606f\uff0c\u82e5\u975e\u9760\u5f97\u5f88\u8fd1\uff0c\u672c\u6765\u5f88\u96be\u542c\u51fa\uff0c\u4f46\u4e00\u6765\u6c5f\u6d77\u5929\u5185\u529f\u6df1\u6e5b\uff0c\u542c\u89c9\u7279\u522b\u7075 \u654f\uff1b\u4e8c\u6765\u53c8\u662f\u5728\u5bc2\u9759\u7684\u665a\u4e0a\uff0c\u4e00\u6709\u4e9b\u58f0\u606f\uff0c\u4e5f\u4f1a\u5f15\u8d77\u6ce8\u610f\u3002\u6c5f\u6d77\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u505c\u4e0b\u811a\u6b65\uff0c \u51dd\u795e\u9759\u542c\u3002\u53ea\u89c9\u8fd9\u6c14\u606f\u8282\u594f\u7f13\u6162\uff0c\u4f3c\u6709\u5982\u65e0\uff0c\u4e0e\u5e38\u4eba\u7684\u91cd\u6d4a\u547c\u5438\uff0c\u622a\u7136\u4e0d\u540c\uff0c\u4ee5\u6c5f\u6d77\u5929 \u7684\u6b66\u5b66\u9020\u8be3\uff0c\u4e00\u542c\u5c31\u77e5\u6b64\u4eba\u5185\u529f\u6781\u6709\u706b\u5019\uff0c\u5927\u7ea6\u662f\u5728\u79bb\u4ed6\u5341\u6b65\u4e4b\u5916\u7684\u5730\u65b9\u57cb\u4f19\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5bfb\u601d\uff1a\u201c\u6b64\u4eba\u5b9a\u662f\u5185\u5bb6\u9ad8\u624b\uff0c\u6211\u53d1\u89c9\u4e86\u4ed6\uff0c\u60f3\u6765\u4ed6\u4e5f\u5df2\u7ecf\u53d1\u89c9\u6211\u4e86\u3002\u4ed6\u57cb\u4f0f \u6697\u5904\uff0c\u610f\u6b32\u4f55\u4e3a\uff1f\u55ef\uff0c\u5927\u7ea6\u56e0\u4e3a\u4ed6\u672a\u770b\u89c1\u6211\u7684\u9762\u8c8c\uff0c\u4e0d\u77e5\u6211\u662f\u4ece\u5916\u9762\u6765\u7684\u5427\uff1f\u82e5\u662f\u7ed9\u4ed6 \u770b\u51fa\uff0c\u58f0\u5f20\u8d77\u6765\uff0c\u90a3\u5c31\u9ebb\u70e6\u4e86\uff01\u201d\u601d\u5ff5\u53ca\u6b64\uff0c\u7acb\u5373\u62b1\u4e86\u201c\u5148\u53d1\u5236\u4eba\u201d\u7684\u6253\u7b97\uff0c\u500f\u5730\u56de\u8eab\uff0c \u5411\u90a3\u4eba\u8eb2\u85cf\u65b9\u5411\u4e00\u6307\u6233\u51fa\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7528\u7684\u662f\u6700\u4e0a\u4e58\u7684\u201c\u7f61\u6c14\u70b9\u7a74\u201d\u7684\u529f\u592b\uff0c\u4ed6\u4ece\u90a3\u4eba\u7684\u547c\u5438\u6c14\u606f\uff0c\u8fa8\u522b\u65b9\u5411\uff0c\u70b9 \u4ed6\u9f3b\u7aef\u7684\u201c\u95fb\u9999\u7a74\u201d\u548c\u773c\u95f4\u7684\u201c\u9633\u767d\u7a74\u201d\u3002\u6c5f\u6d77\u5929\u7684\u65e0\u5f62\u7f61\u6c14\uff0c\u5df2\u7ecf\u7ec3\u5230\u53ef\u4ee5\u5728\u4e09\u4e08\u4e4b \u5185\u4f24\u4eba\u7684\u5883\u754c\uff0c\u5373\u4f7f\u5728\u9ed1\u591c\u4e4b\u4e2d\u6ca1\u6709\u70b9\u51c6\u7a74\u9053\uff0c\u4e5f\u8db3\u4ee5\u4ee4\u654c\u4eba\u6d51\u8eab\u9178\u8f6f\uff0c\u96be\u4ee5\u52a8\u5f39\u3002 \u3000\u3000\u4e00\u6307\u70b9\u51fa\uff0c\u55e4\u7136\u6709\u58f0\uff0c\u5ffd\u542c\u5f97\u90a3\u4eba\u5fae\u5fae\u4e00\u201c\u566b\u201d\u500f\u5730\u4e00\u6761\u9ed1\u5f71\u98de\u4e86\u8d77\u6765\u3002\u5012\u7eb5\u51fa\u53bb\u3002 \u8fd9\u4eba\u7adf\u7136\u6ca1\u6709\u7ed9\u4ed6\u70b9\u5012\uff0c\u800c\u4e14\u8fd8\u80fd\u65bd\u5c55\u8f7b\u529f\u8eb2\u95ea\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u8981\u8ddf\u8e2a\u8ffd\u51fb\uff0c\u90a3\u4eba\u5df2\u5148\u53d1\u96be\uff0c\u53ea\u542c\u5f97\u4e00\u58f0\u6781\u4e3a\u523a\u8033\u7684\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\uff0c\u500f\u7136 \u95f4\uff0c\u4e00\u9053\u4e4c\u8d64\u8272\u7684\u5149\u534e\uff0c\u7535\u5c04\u800c\u81f3\uff0c\u9976\u662f\u6c5f\u6d77\u5929\u6280\u9ad8\u80c6\u5927\uff0c\u89c1\u8fd9\u6697\u5668\u6765\u52bf\u5982\u6b64\u51f6\u731b\uff0c\u4e5f \u4e0d\u6562\u7528\u624b\u53bb\u63a5\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8fd9\u9053\u4e4c\u91d1\u5149\u6cfd\uff0c\u5df2\u5411\u7740\u4ed6\u7684\u54bd\u5589\u5c04\u5230\uff0c\u5e78\u4e8f\u6c5f\u6d77\u5929\u52a8\u4f5c\u4e5f\u5feb\uff0c\u88c1 \u4e91\u5b9d\u5251\u3001\u4ea6\u5df2\u51fa\u9798\uff0c\u5f53\u4e0b\u4e00\u62db\u201c\u4e3e\u706b\u64a9\u5929\u201d\uff0c\u5411\u4e0a\u4fbf\u524a\uff0c\u53ea\u542c\u5f97\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u98de \u6e85\uff0c\u8fd9\u4e00\u679d\u4f3c\u662f\u77ed\u7bad\u6a21\u4f6f\u7684\u6697\u5668\uff0c\u767b\u65f6\u5760\u5730\u3002\u6c5f\u6d77\u5929\u864e\u53e3\u4e5f\u5fae\u89c9\u9178\u9ebb\uff0c\u4e0d\u7981\u6697\u6697\u5403\u60ca\uff1a \u201c\u5929\u4e0b\u95f4\u7adf\u6709\u8fd9\u4f6f\u9738\u9053\u7684\u6697\u5668\uff01\u6211\u7684\u5b9d\u5251\u524a\u94c1\u5982\u6ce5\uff0c\u7adf\u4e5f\u524a\u5b83\u4e0d\u65ad\uff01\u8fd9\u662f\u4ec0\u4e48\u5b9d\u8d1d\uff1f\u201d \u3000\u3000\u52b2\u654c\u5f53\u524d\uff0c\u6c5f\u6d77\u5929\u65e0\u6687\u62fe\u8d77\u90a3\u679d\u6697\u5668\u7814\u7a76\uff0c\u4fbf\u5411\u90a3\u4eba\u8ffd\u53bb\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u90a3\u4eba\u5374\u5e76\u4e0d\u58f0\u5f20\uff0c\u53ea\u662f\u4e00\u5473\u8eb2\u8eb2\u85cf\u85cf\uff0c\u597d\u50cf\u6709\u610f\u548c\u6c5f\u6d77\u5929\u5728\u9ed1\u591c\u4e2d\u6349\u8ff7 \u85cf\u4f3c\u7684\u3002\u6c5f\u6d77\u5929\u7684\u8f7b\u529f\u6bd4\u90a3\u4eba\u867d\u662f\u9ad8\u51fa\u4e00\u7b79\uff0c\u4f46\u4e00\u6765\u4ed6\u8d77\u6b65\u5728\u540e\uff0c\u4e8c\u6765\u4ed6\u5730\u5f62\u4e0d\u719f\uff0c\u88ab \u90a3\u4eba\u5728\u5047\u5c71\u4e71\u77f3\u4e4b\u4e2d\uff0c\u515c\u4e86\u51e0\u4e2a\u5708\u5b50\uff0c\u7adf\u4e0d\u77e5\u4ed6\u8eb2\u5728\u4ec0\u4e48\u5730\u65b9\u4e86\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u949f\u58f0\u5f53\u5f53\uff0c\u53f7\u89d2\u9e23\u545c\uff0c\u60f3\u662f\u5bab\u4e2d\u7684\u4f8d\u536b\uff0c\u542c\u5f97\u90a3\u6697\u5668\u7684\u5578\u58f0\uff0c\u5df2 \u77e5\u6709\u4eba\u5077\u8fdb\u3002 \u3000\u3000\u4e00\u4e2a\u82cd\u8005\u7684\u5987\u4eba\u58f0\u97f3\u559d\u9053\uff1a\u201c\u4e0d\u7528\u60ca\u614c\uff0c\u6765\u7684\u53ea\u662f\u4e24\u4e2a\u5c0f\u8d3c\u3002 \u3000\u3000\u5361\u5170\u59ae\uff0c\u4f60\u641c\u90a3\u8fb9\u7684\u5047\u5c71\uff0c\u6211\u6765\u6349\u8fd9\u8fb9\u7684\u5c0f\u8d3c\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u8001\u5a46\u5a46\u597d\u4e0d\u5389\u5bb3\uff0c\u5c45\u7136\u80fd\u5728\u5608\u5608\u6742\u6742\u7684\u58f0\u4e4b\u4e2d\uff0c\u542c\u5f97 \u51fa\u6211\u7684\u65b9\u5411\uff01\u5bab\u4e2d\u6709\u8fd9\u6837\u80fd\u4eba\uff0c\u518d\u52a0\u4e0a\u521a\u624d\u90a3\u4e2a\u6c49\u5b50\uff0c\u770b\u6765\u6211\u4eca\u665a\u662f\u51b3\u96be\u5982\u613f\u4e86\u3002\u201d \u3000\u3000\u8981\u77e5\u90a3\u8001\u5a46\u5a46\u53e3\u4e2d\u8bf4\u7684\u867d\u7136\u662f\u201c\u4e24\u4e2a\u5c0f\u8d3c\u201d\uff0c\u4f46\u6c5f\u6d77\u5929\u5374\u4e00\u76f4\u4ee5\u4e3a\u521a\u624d\u90a3\u4e2a\u6c49\u5b50\u662f \u5bab\u4e2d\u536b\u58eb\uff0c\u8001\u5a46\u5a46\u628a\u4ed6\u4e5f\u5217\u4e3a\u201c\u5c0f\u8d3c\u201d\uff0c\u90a3\u662f\u56e0\u4e3a\u5728\u9ed1\u591c\u4e2d\u672a\u66fe\u8ba4\u51fa\u662f\u81ea\u5df1\u4eba\u7684\u7f18\u6545\uff0c \u6c5f\u6d77\u5929\u521a\u624d\u63a5\u4e86\u90a3\u4eba\u7684\u6697\u5668\uff0c\u5df2\u77e5\u53cc\u65b9\u6b66\u529f\u76f8\u5dee\u4e0d\u8fdc\uff0c\u800c\u8fd9\u8001\u5a46\u5a46\u7684\u672c\u9886\uff0c\u770b\u6765\u53c8\u4f3c\u8fd8 \u5728\u81ea\u5df1\u4e4b\u4e0a\uff0c\u5018\u82e5\u7ed9\u8fd9\u4e24\u4eba\u8054\u624b\u56f4\u653b\uff0c\u53ea\u6015\u8981\u9003\u4e5f\u4e0d\u5bb9\u6613\u3002\u6697\u81ea\u601d\u91cf\uff1a\u201c\u4e3a\u4e86\u514d\u5403\u773c\u524d \u4e4b\u4e8f\uff0c\u53ea\u597d\u5148\u9003\u51fa\u53bb\uff0c\u7b49\u5e08\u5f1f\u5230\u6765\u4e4b\u540e\uff0c\u518d\u4f5c\u6253\u7b97\u4e86\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u679d\u86c7\u7130\u7bad\u5df1\u5411\u4ed6\u5c04\u6765\uff0c\u4e00\u56e2\u84dd\u706b\uff0c\u5728\u4ed6\u5934\u9876\u4e0a \u7a7a\u63a0\u8fc7\uff0c\u8fd9\u86c7\u7130\u7bad\u4e43\u662f\u4f5c\u7167\u660e\u4e4b\u7528\u7684\u3002\u6c5f\u6d77\u5929\u521a\u521a\u65bd\u5c55\u8f7b\u529f\u3002\u4ece\u5047\u5c71\u4e71\u77f3\u4e4b\u4e2d\u98de\u51fa\uff0c\u5230 \u4e86\u5e73\u5730\uff0c\u86c7\u7130\u7bad\u4e00\u53d1\uff0c\u76ee\u6807\u767b\u65f6\u663e\u9732\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e00\u8bb0\u5288\u7a7a\u638c\u5c06\u90a3\u86c7\u7130\u7bad\u6253\u843d\uff0c\u84dd\u706b\u4e5f\u8fc5\u5373\u6251\u706d\u4e86\u3002\u4f46\u5df2\u662f\u8fdf\u4e86\u4e00\u6b65\uff0c\u5728\u90a3\u706b \u5149\u4e00\u95ea\u4e4b\u4e2d\uff0c\u53ea\u89c1\u4e00\u4e2a\u6ee1\u5934\u94f6\u53d1\u7684\u8001\u5a46\u5a46\u5df2\u662f\u65cb\u98ce\u822c\u5411\u4ed6\u6251\u6765\u3002\u4eba\u8fd8\u6765\u5230\uff0c\u6697\u5668\u5148\u53d1\uff0c \u6697\u5668\u7834\u7a7a\u547c\u5578\u4e4b\u58f0\uff0c\u4e25\u5982\u6d77\u6f6e\u9707\u8033\uff0c\u4e00\u542c\u5c31\u77e5\u662f\u7528\u201c\u5929\u5973\u6563\u82b1\u201d\u7684\u624b\u6cd5\u6253\u6765\u3002 \u3000\u3000\u6700\u5c11\u4e5f\u6709\u5341\u51e0\u4ef6\u4e4b\u591a\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u521a\u624d\u5c1d\u8fc7\u4e86\u90a3\u4e2a\u6c49\u5b50\u6697\u5668\u7684\u5389\u5bb3\u3001\u5982\u4eca\u542c\u8fd9\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\uff1a\u6bd4\u521a\u624d\u7684\u58f0\u52bf\u8fd8 \u8981\u5a01\u731b\uff0c\u9976\u662f\u4ed6\u827a\u9ad8\u80c6\u5927\uff0c\u4e5f\u4e0d\u7981\u5fc3\u5934\u4e00\u9707\uff0c\u201c\u6211\u521a\u624d\u6321\u90a3\u4eba\u7684\u4e00\u6280\u6697\u5668\uff0c\u5df1\u662f\u5982\u6b64\u5403 \u529b\uff0c\u5018\u82e5\u8fd9\u8001\u5a46\u5a46\u542c\u53d1\u7684\u6697\u5668\uff0c\u90fd\u6709\u521a\u624d\u90a3\u4eba\u6240\u53d1\u7684\u5a01\u529b\uff0c\u8fd9\u5341\u51e0\u4ef6\u6697\u5668\uff0c\u6211\u53ef\u8981\u5403\u4e0d \u6d88\u4e86\u3002\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u91d1\u5149\u707f\u70c2\uff0c\u5df2\u662f\u4ece\u56db\u9762\u516b\u65b9\u98de\u6765\uff0c\u539f\u6765\u662f\u5341\u4e8c\u4e2a\u91d1\u73af\uff0c\u8fde\u7fe9 \u800c\u81f3\uff0c\u6709\u7684\u659c\u98de\uff0c\u6709\u7684\u76f4\u5c04\uff0c\u6709\u7684\u5728\u4ed6\u5934\u9876\u76d8\u65cb\uff0c\u5374\u672a\u5373\u843d\u4e0b\u3002\u6c5f\u6d77\u5929\u8fd0\u8d77\u795e\u529b\uff0c\u5b9d\u5251 \u4e00\u6325\uff0c\u5251\u5149\u4e5f\u5316\u4f5c\u4e86\u4e00\u9053\u957f\u8679\u5411\u90a3\u5341\u51e0\u5708\u91d1\u5149\u626b\u8361\uff0c\u53ea\u542c\u5f97\u4e00\u7247\u65ad\u91d1\u788e\u7389\u4e4b\u58f0\uff0c\u91d1\u73af\u78b0 \u7740\u4e86\u4ed6\u7684\u5b9d\u5251\uff0c\u90fd\u788e\u6210\u7247\u7247\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u5934\u4e00\u677e\uff0c\u539f\u6765\u8fd9\u8001\u5a46\u5a46\u7684\u6697\u5668\u6570\u91cf\u867d\u591a\uff0c\u52bf\u58f0\u4e5f\u6781\u9a87\u4eba\uff0c\u4f46\u5a01\u529b\u4e4b\u5f3a\uff0c\u5374 \u8fd8\u8fdc\u8fdc\u4e0d\u5982\u521a\u624d\u90a3\u4eba\u6240\u53d1\u7684\u4e00\u6280\u6697\u5668\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u5fc3\u60c5\u7565\u4e00\u677e\u61c8\u4e4b\u9645\uff0c\u5728\u4ed6\u5934\u9876\u4e0a\u76d8\u65cb\u7684\u4e09\u4e2a\u91d1\u73af\u7a81\u7136\u98de\u4e0b\uff0c\u900f\u8fc7\u4e86\u4ed6\u5251\u5149\u7684 \u5c01\u9501\u3002\u6c5f\u6d77\u5929\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u80cc\u5411\u4e0a\u62f1\uff0c\u4e09\u679a\u91d1\u73af\uff0c\u90fd\u6253\u4e2d\u4e86\u4ed6\u7684\u80cc\u5fc3\u3002\u6c5f\u6d77\u5929 \u6709\u62a4\u4f53\u795e\u529f\uff0c\u4e09\u679a\u91d1\u73af\u4e5f\u767b\u65f6\u9707\u843d\u3002\u53ef\u662f\u90a3\u529b\u9053\u4e5f\u9887\u4e0d\u5f31\uff0c\u6c5f\u6d77\u5929\u53ea\u89c9\u4f3c\u88ab\u94c1\u9524\u91cd\u91cd\u6572 \u51fb\u4e86\u4e09\u4e0b\uff0c\u867d\u672a\u53d7\u4f24\uff0c\u4e5f\u89c9\u75db\u5f7b\u5fc3\u80ba\u3002\u8fd9\u8001\u5a46\u5a46\u6240\u53d1\u7684\u6697\u5668\uff0c\u529b\u9053\u4e4b\u5f3a\uff0c\u4e5f\u8fd8\u7f62\u4e86\uff0c\u624b \u6cd5\u7684\u5947\u5999\u3002\u5374\u786e\u662f\u6c5f\u6d77\u5929\u89c1\u6240\u672a\u89c1\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5374\u4e0d\u77e5\u9053\uff0c\u4ed6\u56fa\u7136\u5403\u51c9\uff0c\u90a3\u8001\u5a46\u5a46\u5374\u6bd4\u4ed6\u5403\u60ca\u66f4\u751a\uff01 \u3000\u3000\u8fd9\u8001\u5a46\u5a46\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u90a3\u6606\u5e03\u5170\u56fd\u56fd\u738b\u5949\u4e3a\u91d1\u8f6e\u5723\u6bcd\u7684\u7ae5\u59e5\u59e5\uff0c\u5979\u81ea\u8d1f\u5e73\u751f\u65e0\u654c\uff0c \u8fd9\u5341\u4e8c\u53ea\u91d1\u73af\u66f4\u662f\u5979\u6700\u5389\u5bb3\u7684\u72ec\u95e8\u6697\u5668\uff0c\u5374\u60f3\u4e0d\u5230\u7adf\u7ed9\u4e00\u4e2a\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u4e0d\u77e5\u6765\u5386\u7684\u5c11\u5e74 \u4eba\uff0c\u4e3e\u624b\u4e4b\u95f4\uff0c\u4fbf\u628a\u8fd9\u5341\u4e8c\u53ea\u91d1\u73af\u5168\u90fd\u7834\u4e86\u3002\u4e5d\u53ea\u91d1\u73af\u662f\u7ed9\u5b9d\u5251\u524a\u65ad\u7684\u8fd8\u4e0d\u7b97\u7a00\u5947\uff0c\u53e6 \u5916\u90a3\u4e09\u53ea\u91d1\u73af\u88ab\u6c5f\u6d77\u5929\u4ee5\u62a4\u4f53\u795e\u529f\u9707\u843d\uff0c\u7ae5\u59e5\u59e5\u53ef\u4e0d\u80fd\u4e0d\u5927\u5927\u5403\u60ca\u4e86\u3002\u8fd9\u5341\u4e8c\u53ea\u91d1\u73af\uff0c \u5979\u672c\u662f\u8f7b\u6613\u4e0d\u80af\u65bd\u5c55\u7684\uff0c\u53ea\u56e0\u89c1\u6c5f\u6d77\u5929\u6251\u706d\u90a3\u86c7\u7130\u7bad\u7684\u529f\u529b\u9887\u4f3c\u4e0d\u51e1\uff0c\u5979\u8fd9\u624d\u4f7f\u7528\u51fa\u6765\uff0c \u5fc3\u4e2d\u8fd8\u4e0d\u65e0\u201c\u725b\u5200\u6740\u9e21\u201d\u4e4b\u611f\u3002\u60f3\u4e0d\u5230\u6c5f\u6d77\u5929\u7684\u529f\u529b\u7adf\u8fdc\u8fdc\u8d85\u51fa\u5979\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u82e5\u5bb9\u8fd9\u5c0f\u5b50\u518d\u8fc7\u51e0\u5e74\uff0c\u90a3\u8fd8\u4e86\u5f97\uff1f\u201d\u6740\u673a\u9661\u8d77\uff0c\u8d81\u7740\u6c5f\u6d77\u5929\u7acb \u8db3\u672a\u7a33\uff0c\u4e00\u638c\u4fbf\u5288\u8fc7\u6765\uff0c\u6c5f\u6d77\u5929\u8fd8\u4e86\u4e00\u638c\uff0c\u4e24\u65b9\u8ddd\u79bb\u5df2\u5728\u4e00\u4e08\u4e4b\u5185\uff0c\u638c\u98ce\u6fc0\u8361\uff0c\u6c99\u98de\u77f3 \u8d70\uff0c\u58f0\u5982\u90c1\u96f7\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u8fde\u9000\u4e09\u6b65\uff0c\u8fd9\u4e00\u60ca\u66f4\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u6025\u5fd9\u518d\u53d1\u4e00\u638c\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6643\u4e86\u6643\uff0c\u6b63\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u8001\u5996\u5a46\u867d\u4e0d\u53ca\u90a3\u5b9d\u8c61\u6cd5\u5e08\uff0c\u8fd9\u4e00\u8eab\u6b66\u529f\uff0c\u4e5f\u8db3\u4ee5\u9707 \u4e16\u9a87\u4fd7\u4e86\u3002\u82e5\u5728\u6211\u672a\u670d\u5929\u5fc3\u77f3\u4e4b\u524d\uff0c\u51b3\u8ba1\u6321\u4e0d\u4e86\u5979\u8fd9\u4e00\u638c\u3002\u201d\u5ffd\u89c9\u771f\u6c14\u8fd0\u8f6c\uff0c\u6709\u963b\u6ede\u4e4b \u611f\uff0c\u7b2c\u4e8c\u638c\u53d1\u51fa\u529f\u529b\u4fbf\u51cf\u4e86\u51e0\u5206\uff0c\u8fd9\u4e00\u6b21\u53cc\u65b9\u5185\u529b\u78b0\u649e\uff0c\u8f6e\u5230\u4e86\u6c5f\u6d77\u5929\u8fde\u9000\u4e09\u6b65\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7ae5\u59e5\u59e5\u7b2c\u4e09\u638c\u53c8\u5230\uff0c\u8fd9\u4e00\u6b21\u66f4\u4e3a\u602a\u5f02\uff0c\u70ed\u98ce\u547c\u547c\uff0c\u5c31\u4f3c\u4e00\u628a\u65e0\u5f62 \u7684\u706b\u7130\u70e7\u5230\u4e86\u8eab\u4e0a\uff0c\u6c5f\u6d77\u5929\u5728\u5589\u7126\u820c\u71e5\u7684\u611f\u89c9\u4e2d\uff0c\u53c8\u9690\u9690\u95fb\u5230\u4e00\u80a1\u6de1\u6de1\u7684\u8165\u5473\uff0c\u4e0d\u7531\u5f97 \u5fc3\u5934\u4e00\u9707\uff1a\u201c\u539f\u6765\u8fd9\u5996\u5a46\u4e0d\u4f46\u638c\u529b\u6df1\u539a\uff0c\u800c\u4e14\u8fd8\u7ec3\u6709\u6bd2\u529f\u3002\u201d\u6c5f\u6d77\u5929\u672c\u6765\u5c31\u6015\u521a\u624d\u6240\u9047 \u7684\u90a3\u6c49\u5b50\u53c8\u518d\u51fa\u6765\uff0c\u4e0e\u8fd9\u8001\u5996\u5a46\u8054\u624b\u653b\u4ed6\uff0c\u6b64\u65f6\u53c8\u5bdf\u89c9\u8fd9\u8001\u5996\u5a46\u96be\u4ee5\u529b\u654c\uff0c\u81ea\u66f4\u65e0\u5fc3\u604b \u6218\u3002\u5f53\u4e0b\u865a\u6643\u4e00\u62db\uff0c\u62d4\u811a\u4fbf\u9003\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u4f7f\u51fa\u5e73\u751f\u7edd\u5b66\uff0c\u6697\u5668\u3001\u638c\u529b\u3001\u6bd2\u529f\u5168\u90fd\u7528\u4e0a\u4e86\uff0c\u89c1\u5bf9\u65b9\u6beb\u65e0\u4f24\u635f\uff0c\u8fd8\u80fd\u65bd\u5c55 \u8d85\u5353\u4e4b\u6781\u7684\u8f7b\u529f\uff0c\u5fc3\u91cc\u4e5f\u662f\u60ca\u7591\u4e0d\u5b9a\u3002 \u3000\u3000\u201c\u8fd9\u5c0f\u5b50\u80fd\u6709\u591a\u5927\u5e74\u7eaa\uff1f\u5373\u4f7f\u4ed6\u5728\u5a18\u80ce\u91cc\u5c31\u5b66\u6b66\u529f\uff0c\u4e5f\u4e0d\u5e94\u6709\u5982\u6b64\u529f\u529b\u3002\u600e\u7684\u8fde\u6211 \u7684\u5316\u8840\u795e\u529f\u90fd\u4f24\u4ed6\u4e0d\u5f97\uff0c\u96be\u9053\u4ed6\u5df2\u7ec3\u6210\u4e86\u91d1\u521a\u4e0d\u574f\u4e4b\u8eab\uff1f\u201d\u5979\u600e\u77e5\u9053\u6c5f\u6d77\u5929\u662f\u91d1\u4e16\u9057\u6089 \u5fc3\u8c03\u6559\u51fa\u6765\u7684\u5f1f\u5b50\uff0c\u53c8\u5de7\u670d\u4e86\u4e09\u9897\u5929\u5fc3\u77f3\uff0c\u8db3\u5f53\u5f97\u4eba\u5bb6\u6570\u5341\u5e74\u7684\u529f\u529b\uff0c\u867d\u672a\u7ec3\u6210\u91d1\u521a\u4e0d \u574f\u4e4b\u8eab\uff0c\u4f46\u53ea\u8bba\u529f\u529b\u5df2\u6bd4\u7ae5\u59e5\u59e5\u7a0d\u80dc\u4e00\u7b79\uff0c\u4e0d\u8fc7\u7ae5\u59e5\u59e5\u4f7f\u51fa\u6b79\u6bd2\u4e4b\u6781\u7684\u5316\u8840\u795e\u529f\uff0c\u4ed6\u5374 \u8fd8\u4e0d\u77e5\u5982\u4f55\u62b5\u5fa1\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u5fc3\u5b58\u602f\u610f\uff0c\u4e0d\u6562\u8ffd\u5f97\u592a\u8fd1\u3002\u5176\u5b9e\u6c5f\u6d77\u5929\u6b64\u65f6\u8981\u8fd0\u529f\u9a71\u6bd2\uff0c\u5018\u82e5\u7ae5\u59e5\u59e5\u5168\u529b\u6251 \u51fb\uff0c\u6c5f\u6d77\u5929\u53ea\u6015\u4e5f\u96be\u9003\u8131\uff0c\u4f46\u7ae5\u59e5\u59e5\u6478\u4e0d\u5230\u6c5f\u6d77\u5929\u7684\u5e95\u7ec6\uff0c\u53ea\u89c9\u8fd9\u5c11\u5e74\u7684\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\uff0c \u5b9e\u662f\u592a\u51fa\u4e4e\u60c5\u7406\u4e4b\u5916\uff0c\u751f\u6015\u516b\u5341\u5c81\u8001\u5a18\u5012\u7ef7\u5b69\u513f\uff0c\u4e0d\u7531\u5f97\u4e0d\u5c0f\u5fc3\u7ffc\u7ffc\u3002\u6c5f\u6d77\u5929\u53d1\u529b\u72c2\u5954\uff0c \u4e5f\u5728\u63d0\u5fc3\u540a\u963b\u3002\u5947\u602a\u7684\u662f\uff0c\u521a\u624d\u6240\u9047\u7684\u90a3\u4e2a\u6c49\u5b50\u4e00\u76f4\u4e0d\u89c1\u51fa\u6765\uff0c\u201d\u4e0d\u77e5\u8eb2\u5230\u54ea\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u5728\u52a0\u5feb\u811a\u6b65\uff0c\u5f80\u524d\u98de\u5954\uff0c\u5ffd\u542c\u5f97\u547c\u7684\u4e00\u58f0\uff0c\u4e00\u679a\u77f3\u5b50\uff0c\u8fce\u9762\u6253\u6765\uff0c\u542c\u90a3\u6697 \u5668\u7834\u7a7a\u4e4b\u58f0\u3002\u52b2\u9053\u9887\u662f\u4e0d\u5f31\uff0c\u6c5f\u6d77\u5929\u6b63\u5f85\u6325\u5251\u8c28\u62e6\uff0c\u90a3\u679a\u77f3\u5b50\u672a\u66fe\u6253\u5230\uff0c\u5df2\u5728\u4ed6\u9762\u524d\u843d \u4e0b\uff0c\u53ea\u542c\u5f97\u201c\u549a\u201d\u7684\u4e00\u58f0\uff0c\u6ce5\u6c34\u56db\u6e85\u3002\u539f\u6765\u5728\u4ed6\u524d\u9762\uff0c\u6b63\u662f\u4e00\u4e2a\u6ce5\u5858\uff0c\u53ea\u56e0\u4e71\u8349\u4e1b\u751f\uff0c \u9ed1\u591c\u4e4b\u4e2d\uff0c\u6b8a\u96be\u53d1\u89c9\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6697\u6697\u53eb\u58f0\u201c\u4fa5\u5e78\u201d\uff0c\u8981\u4e0d\u662f\u8fd9\u679a\u77f3\u5b50\u6070\u597d\u53ca\u65f6\u800c\u6765\uff0c\u4ed6\u5728\u72c2\u5954\u4e4b\u4e2d\uff0c\u4e00\u5b9a\u4f1a \u9677\u5165\u6ce5\u5858\u53bb\u4e86\u3002\u867d\u8bf4\u4ed6\u4e00\u8eab\u529f\u592b\uff0c\u9677\u5165\u6ce5\u5858\uff0c\u4e5f\u51b3\u4e0d\u81f3\u4e8e\u4fbf\u906d\u6ca1\u9876\uff0c\u4f46\u603b\u662f\u9ebb\u70e6\uff0c\u6700\u5c11 \u4e5f\u4f1a\u7ed9\u90a3\u7ae5\u59e5\u59e5\u8ffd\u4e0a\u3002\u6c5f\u6d77\u5929\u89c9\u5f97\u6709\u70b9\u5947\u602a\uff1a\u201c\u4ee5\u90a3\u6697\u5668\u7684\u52b2\u9053\u800c\u8bba\uff0c\u90a3\u4eba\u662f\u8db3\u591f\u529b\u6c14 \u6253\u5f97\u66f4\u8fdc\u7684\uff0c\u96be\u9053\u662f\u4ed6\u6709\u610f\u7ed3\u6211\u6307\u8def\uff1f\u201d\u4f46\u8fd9\u65f6\u4ed6\u6025\u4e8e\u9003\u8dd1\uff0c\u4e5f\u65e0\u6687\u4ed4\u7ec6\u63a8\u6572\uff0c\u7acb\u5373\u4f7f \u4ece\u6ce5\u5858\u65c1\u8fb9\u7ed5\u8fc7\uff0c\u5728\u6c34\u5149\u7684\u6620\u5c04\u4e0b\uff0c\u9690\u7ea6\u89c1\u5230\u5858\u8fb9\u6709\u4e2a\u4eba\u5f71\uff0c\u534a\u8fb9\u8eab\u5b50\u85cf\u5728\u4e71\u8349\u4e1b\u4e2d\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u773c\u5229\uff0c\u4e00\u773c\u8ba4\u51fa\u662f\u4e2a\u5b50\uff0c\u518d\u8d70\u8fd1\u4e24\u6b65\uff0c\u8fde\u9762\u8c8c\u4e5f\u53ef\u4ee5\u7ea6\u7565\u8fa8\u8ba4\u4e86\uff0c\u6c5f\u6d77\u5929\u731b \u5730\u4e00\u60ca\uff0c\u539f\u6765\u8fd9\u5973\u5b50\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u5929\u9b54\u6559\u4e3b\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u65e0\u6687\u6253\u8bdd\uff0c\u201c\u5530\u201d\u7684\u4e00\u5251\u523a\u51fa\uff0c\u5929\u9b54\u6559\u4e3b\u98de\u8d77\u4e00\u6761\u7ef8\u5e26\uff0c\u8fd8\u4e86\u4e00\u62db\uff0c\u4f4e\u58f0\u8bf4 \u9053\uff1a\u201c\u4f60\u8fd9\u4e0d\u8bc6\u597d\u6b79\u7684\u5c0f\u5b50\uff0c\u8fd8\u4e0d\u5feb\u9003\uff01\u201d\u5251\u5149\u8fc7\u5904\uff0c\u7ef8\u5e26\u88ab\u524a\u53bb\u4e86\u4e00\u6bb5\uff0c\u6c5f\u6d77\u5929\u53c8\u590d \u4e00\u638c\u63a8\u51fa\uff0c\u5929\u9b54\u6559\u4e3b\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u54ce\u54df\uff0c\u597d\u5389\u5bb3\uff01\u201d\u8eab\u5f62\u4e00\u4fa7\uff0c\u95ea\u8fc7\u4e00\u8fb9\uff0c\u6c5f\u6d77\u592d\u4e00\u63a0 \u5373\u8fc7\uff0c\u8fd8\u6015\u5979\u7528\u6bd2\u836f\u6697\u5668\u5077\u88ad\uff0c\u4e00\u9762\u8dd1\u4e00\u9762\u821e\u5251\u9632\u8eab\uff0c\u5929\u9b54\u6559\u4e3b\u5374\u5e76\u65e0\u6697\u5668\u6253\u6765\u3002\u53ea\u542c \u5f97\u7ae5\u59e5\u59e5\u5728\u540e\u9762\u53eb\u9053\uff1a\u201c\u5170\u513f\uff0c\u4f60\u53d7\u4e86\u4f24\u4e48\uff1f\u201d \u3000\u3000\u7ae5\u59e5\u59e5\u8f7b\u529f\u672c\u6765\u5c31\u6bd4\u6c5f\u6d77\u5929\u7a0d\u900a\u4e00\u7b79\uff0c\u5979\u5c1a\u672a\u6478\u5230\u6c5f\u6d77\u5929\u6b66\u529f\u7684\u6df1\u6d45\uff0c\u8fd9\u65f6\u53c8\u6015\u5929 \u9b54\u6559\u4e3b\u5df2\u53d7\u4e86\u4f24\uff0c\u54ea\u91cc\u8fd8\u6562\u518d\u8ffd\u3002\u4e0d\u6d88\u7247\u523b\u3002\u6c5f\u6d77\u5929\u5df2\u8d8a\u8fc7\u4e86\u56f4\u5899\uff0c\u9003\u51fa\u4e86\u738b\u5bab\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9003\u662f\u9003\u51fa\u6765\uff0c\u4f46\u61ca\u70ed\u70e6\u95f7\u7684\u611f\u89c9\u8fd8\u662f\u6ca1\u6709\u6d88\u9664\uff0c\u5fc3\u91cc\u4e5f\u6697\u6697\u5403\u60ca\uff1a\u201c\u90a3\u8001\u5996 \u5a46\u4e0d\u77e5\u7528\u7684\u662f\u4ec0\u4e48\u53e4\u602a\u638c\u529b\uff0c\u5728\u8fd9\u6837\u5bd2\u51b7\u7684\u5929\u6c14\uff0c\u5c45\u7136\u80fd\u4ee4\u6211\u5168\u8eab\u53d1\u6eda\u3002\u6bd4\u8d77\u6b27\u9633\u4ef2\u548c \u7684\u9739\u96f3\u638c\u4e0e\u96f7\u795e\u6307\uff0c\u90a3\u662f\u5389\u5bb3\u5f97\u592a\u591a\u4e86\uff01\u201d\u5f53\u4e0b\u9ed8\u8fd0\u7384\u529f\uff0c\u5bfc\u6c14\u5f52\u5143\uff0c\u4e0d\u6d88\u591a\u4e45\uff0c\u5df2\u628a \u6240\u53d7\u7684\u70ed\u6bd2\u5c3d\u90fd\u5316\u53bb\u3002 \u3000\u3000\u738b\u5bab\u5efa\u5728\u5c71\u4e0b\uff0c\u6c5f\u6d77\u5929\u662f\u5411\u5c71\u4e0a\u9003\u53bb\uff0c\u4ece\u5df2\u6761\u51b0\u5ddd\u65c1\u8fb9\u7ecf\u8fc7\uff0c\u51b7\u98ce\u5439\u6765\uff0c\u5728\u61ca\u70ed\u4e4b \u540e\uff0c\u5206\u5916\u611f\u5230\u4e00\u7247\u6e05\u51c9\uff0c\u5341\u5206\u8212\u670d\u3002\u6062\u590d\u6e05\u9192\u4e4b\u540e\uff0c\u5ffd\u5730\u8d77\u7591\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u521a\u624d\u4e0e \u5929\u9b54\u6559\u4e3b\u4ea4\u624b\u4e4b\u65f6\uff0c\u529f\u529b\u672a\u66fe\u5b8c\u5168\u6062\u590d\uff0c\u867d\u7136\u4e5f\u672a\u5fc5\u5c31\u4f1a\u8d25\u7ed9\u5979\uff0c\u4f46\u5979\u8981\u62b5\u654c\u4e09\u4e94\u5341\u62db\uff0c \u603b\u8fd8\u80fd\u591f\uff0c\u5979\u4f55\u4ee5\u8981\u90a3\u6837\u5927\u58f0\u60ca\u543c\uff0c\u96be\u9053\u662f\u6545\u610f\u88c5\u51fa\u6765\u56b7\u7ed9\u90a3\u8001\u5996\u5a46\u542c\u7684\uff1f\u5979\u662f\u6709\u5fc3\u5c06 \u6211\u653e\u8d70\uff1f\u201d\u518d\u60f3\u4e00\u60f3\uff0c\u7ed9\u6211\u6307\u8def\u7684\u90a3\u679a\u77f3\u5b50\u6562\u60c5\u4e5f\u662f\u5979\u63b7\u7684\uff1f\u5858\u8fb9\u6ca1\u6709\u522b\u4eba\uff0c\u55ef\uff0c\u90a3\u4e00 \u5b9a\u662f\u5979\u4e86\uff01\u5947\u602a\uff0c\u5979\u4e3a\u4ec0\u4e48\u8981\u52a9\u6211\u8131\u9669\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6697\u5730\u5bfb\u601d\uff1a\u201c\u6211\u5c0f\u65f6\u5019\u66fe\u88ab\u5979\u62a2\u53bb\uff0c\u5979\u5bf9\u6211\u6781\u4e3a\u75bc\u7231\uff0c\u83ab\u975e\u5979\u662f\u5728\u5ff5\u7740\u8fd9\u6bb5 \u60c5\u8c0a\uff1f\u4f46\u6211\u5728\u9a6c\u8428\u513f\u56fd\u7684\u65f6\u5019\uff0c\u5979\u53c8\u4e3a\u4f55\u4e0d\u5ff5\u65e7\u60c5\uff0c\u5bf9\u6211\u4e0b\u6bd2\uff1f\u201d\u767e\u601d\u4e0d\u5f97\u5176\u89e3\uff0c\u53ea\u597d \u6682\u4e14\u6401\u4e4b\u8111\u540e\uff0c\u7eed\u5411\u524d\u884c\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6cbf\u7740\u51b0\u6cb3\u8d70\u53bb\uff0c\u60f3\u4ece\u53e6\u4e00\u9762\u843d\u5c71\uff0c\u8d70\u5f97\u4e0d\u8fdc\uff0c\u5ffd\u542c\u5f97\u545c\u545c\u7684\u5578\u58f0\uff0c\u4e00\u9053\u4e4c\u91d1 \u5149\u534e\uff0c\u53c8\u5411\u7740\u4ed6\u8fce\u9762\u5c04\u6765\uff0c\u6b63\u662f\u4ed6\u521a\u624d\u78b0\u8fc7\u7684\u90a3\u79cd\u4e0d\u77e5\u540d\u5b57\u7684\u6697\u5668\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u65e2\u662f\u6124\u6012\uff0c\u53c8\u662f\u5947\u602a\uff0c\u201c\u6b64\u4eba\u521a\u624d\u4e3a\u4ec0\u4e48\u4e0d\u4e0e\u90a3\u5996\u5a46\u8054\u624b\u5939\u653b\uff0c\u5374\u5230\u5982\u4eca\u624d \u6765\u8fd9\u91cc\u4f0f\u51fb\uff1f\u54fc\uff0c\u770b\u6765\u4ed6\u662f\u6709\u610f\u4f38\u91cf\u6211\u4e86\u3002\u201d\u5f53\u4e0b\u8fd0\u8db3\u4e86\u5341\u6210\u529f\u529b\uff0c\u6325\u5251\u4e00\u6321\uff0c\u201c\u53ee\u201d \u7684\u4e00\u58f0\uff0c\u706b\u82b1\u56db\u6e85\uff0c\u4ecd\u7136\u672a\u80fd\u5c06\u90a3\u679d\u6697\u5668\u524a\u65ad\uff0c\u53ea\u662f\u5728\u6746\u4e0a\u73b0\u51fa\u4e00\u9053\u88c2\u75d5\uff0c\u6c5f\u6d77\u5929\u8fd0\u5251 \u6d88\u53bb\u4e86\u90a3\u4eba\u7684\u52b2\u9053\uff0c\u7acb\u5373\u4f7f\u628a\u90a3\u679d\u6697\u5668\u6284\u5230\u624b\u4e2d\uff0c\u53ea\u89c1\u5f62\u5982\u77ed\u7bad\uff0c\u975e\u91d1\u975e\u94c1\uff0c\u4e5f\u4e0d\u77e5\u662f \u4ec0\u4e48\u4e1c\u897f\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u6761\u9ed1\u5f71\u5df2\u95ea\u7535\u822c\u5730\u6251\u51fa\u8fc7\u6765\uff0c\u559d\u9053\uff1a \u3000\u3000\u201c\u597d\u5440\uff0c\u4f60\u65e2\u82e6\u82e6\u76f8\u8feb\uff0c\u54b1\u4eec\u5c31\u6765\u8f83\u91cf\u8f83\u91cf\uff01\u201d\u4e00\u5251\u524a\u5230\uff0c\u9690\u9690\u5e26\u6625\u98ce\u96f7\u4e4b\u58f0\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6012\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u5206\u660e\u662f\u4f60\u5c61\u6b21\u6311\u8845\uff0c\u5374\u98a0\u5012\u8bf4\u6211\u8feb\u4f60\uff01\u201d\u88c1\u4e91\u5b9d\u5251\u4e00\u62db \u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\uff0c\u4e5f\u6a2a\u524a\u8fc7\u53bb\uff0c\u53cc\u5251\u76f8\u4ea4\uff0c\u5bd2\u5149\u56db\u5c04\uff0c\u58f0\u7740\u9f99\u541f\uff0c\u9707\u5f97\u8033\u9f13\u90fd\u55e1\u55e1\u4f5c\u54cd\uff0c \u53cc\u65b9\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u90fd\u5148\u770b\u81ea\u5df1\u7684\u5251\u6709\u6ca1\u6709\u4f24\u635f\uff0c\u5f85\u89c1\u5168\u597d\u65e0\u7f3a\uff0c\u8fd9\u624d\u653e\u5fc3\uff0c\u53c8 \u5404\u826f\u4e0d\u7ea6\u800c\u540c\u7684\u201c\u566b\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u51b0\u6cb3\u53d1\u51fa\u7684\u4eae\u5149\u6709\u5982\u7693\u6708\uff0c\u6c5f\u6d77\u5929\u8fd9\u624d\u53d1\u73b0\uff0c\u539f\u6765\u5bf9\u65b9\u662f\u4e2a\u5e74\u7eaa\u4e0e\u4ed6\u4e0d\u76f8\u4e0a\u4e0b\u7684\u5c11 \u5e74\u3002\u867d\u7136\u662f\u80e1\u4eba\u88c5\u675f\uff0c\u4f46\u4ece\u5f62\u8c8c\u4e0a\u5374\u53ef\u4ee5\u770b\u5f97\u51fa\u662f\u4e2a\u6c49\u4eba\uff0c\u4e24\u9053\u5251\u7709\uff0c\u82f1\u6c14\u52c3\u52c3\uff0c\u9aa4\u773c \u4e00\u770b\uff0c\u7adf\u662f\u4f3c\u9996\u76f8\u8bc6\uff01\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u5947\u602a\uff0c\u6211\u4f3c\u662f\u5728\u54ea\u91cc\u89c1\u8fc7\u6b64\u4eba\uff1f\u201d\u4f46\u5728\u4ed6\u76f8\u8bc6\u7684 \u4eba\u4e2d\u3002\u51b3\u8ba1\u6ca1\u6709\u4e00\u4f4d\u6b66\u827a\u9ad8\u5f3a\u7684\u5c11\u5e74\uff0c\u6c5f\u6d77\u5929\u7591\u60d1\u4e0d\u5b9a\uff0c\u6b63\u8981\u52a8\u95ee\uff0c\u90a3\u5c11\u5e74\u5df2\u662f\u53c8\u6251\u8fc7 \u6765\uff01 \u3000\u3000\u90a3\u5c11\u5e74\u8bd5\u4e86\u4e00\u62db\uff0c\u77e5\u9053\u6c5f\u6d77\u5929\u529f\u529b\u8fdc\u80dc\u4e8e\u4ed6\uff0c\u610f\u6b32\u5728\u62db\u6570\u4e0a\u53d6\u80dc\uff0c\u8fd0\u5251\u5982\u98ce\u3002\u6307\u4e1c \u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u6c5f\u6d77\u5929\u7684\u5b9d\u5251\u7adf\u78b0\u5b83\u4e0d\u7740\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u78b0\u5230\u52b2\u654c\uff0c\u7cbe\u795e\u9661\u632f\uff0c\u5fc3\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u548c\u4f60\u6597\u5feb\uff01\u201d\u987f\u65f6\u95f4\u53cc\u65b9\u51fa\u5251\u90fd\u662f \u5feb\u5982\u95ea\u7535\uff0c\u53ea\u89c1\u4e24\u9053\u5251\u5149\u77eb\u82e5\u6e38\u9f99\uff0c\u76d8\u65cb\u6765\u5f80\uff0c\u524d\u62db\u672a\u5c3d\uff0c\u540e\u62db\u53c8\u53d1\uff0c\u4e00\u53d1\u5373\u6536\uff0c\u4e00\u6cbe \u5373\u9000\uff0c\u4e0d\u5230\u534a\u70b7\u9999\u7684\u65f6\u523b\uff0c\u5df2\u6597\u4e86\u767e\u6570\u5341\u62db\uff0c\u53cc\u65b9\u4ecd\u662f\u672a\u66fe\u78b0\u649e\u3002 \u3000\u3000\u4f46\u8fd9\u6837\u4ee5\u5feb\u5982\u95ea\u7535\u7684\u5251\u6cd5\u8f83\u91cf\uff0c\u5374\u6bd4\u786c\u6253\u786c\u62fc\u66f4\u60ca\u9669\u767e\u500d\uff0c\u56e0\u53cc\u65b9\u90fd\u5728\u4e58\u6687\u62b5\u9699\uff0c \u54ea\u4e00\u65b9\u7a0d\u6709\u758f\u5931\uff0c\u4fbf\u8981\u8840\u67d3\u7384\u51b0\u4e86\uff1b\u53c8\u56e0\u53cc\u65b9\u90fd\u662f\u5b9d\u5251\uff0c\u53cc\u5251\u4e0d\u4ea4\uff0c\u529f\u529b\u9ad8\u7684\u90a3\u65b9\u867d\u7136 \u8fd8\u662f\u7a0d\u5360\u4fbf\u5b9c\uff0c\u4f46\u4e5f\u5360\u4e0d\u5230\u591a\u5927\u7684\u4fbf\u5b9c\u4e86\u3002\u8fd9\u5c11\u5e74\u7684\u5251\u6cd5\u7cbe\u5999\u975e\u5e38\uff0c\u7edd\u4e0d\u5728\u6c5f\u6d77\u5929\u4e4b\u4e0b\uff0c \u6597\u5230\u4e86\u4e00\u767e\u62db\u4e4b\u5916\uff0c\u53cc\u65b9\u90fd\u6709\u70b9\u611f\u5230\u5947\u602a\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5947\u602a\uff0c\u8fd9\u5c0f\u5b50\u7684\u5251\u6cd5\uff0c\u5176\u4e2d\u6709\u51e0\u62db\u6781\u4e3a\u7cbe\u5999\u7684\u62db\u6570\uff0c\u7adf\u4f3c\u662f\u4ece \u6211\u8fd9\u5957\u8ffd\u98ce\u5251\u5f0f\u4e2d\u53d8\u5316\u51fa\u6765\uff1f\u201d\u6c5f\u6d77\u5929\u4e5f\u5728\u5fc3\u91cc\u601d\u7591\uff1a\u201c\u6211\u5e08\u7236\u6240\u521b\u7684\u72ec\u95e8\u5251\u672f\uff0c\u4ed6\u600e \u7684\u4f3c\u4e4e\u4e5f\u61c2\uff1f\u201d\u539f\u6765\u53cc\u65b9\u7684\u5251\u62db\u53d8\u5316\uff0c\u867d\u7136\u6709\u5f88\u5927\u4e0d\u540c\uff0c\u4f46\u4ee5\u6c5f\u6d77\u5929\u7684\u6b66\u5b66\u9020\u8be3\uff0c\u5374\u9690 \u9690\u53ef\u4ee5\u5bdf\u89c9\u5f97\u5230\u4e43\u662f\u540c\u51fa\u4e00\u6e90\uff0c\u800c\u4e14\u5bf9\u65b9\u7684\u5251\u672f\u8fd8\u4f3c\u662f\u201c\u6e90\u5934\u201d\uff0c\u800c\u81ea\u5df1\u7684\u8fd9\u5957\u5251\u672f\uff0c \u5219\u662f\u8fd9\u201c\u6e90\u5934\u201d\u7684\u201c\u5206\u652f\u201d\u3002 \u3000\u3000\u6597\u5230\u767e\u62db\u5f00\u5916\uff0c\u90a3\u5c11\u5e74\u6e10\u89c9\u6c14\u529b\u4e0d\u52a0\uff0c\u5251\u6cd5\u7a81\u7136\u4e00\u53d8\uff0c\u5316\u6210\u4e86\u4e00\u9053\u5149\u5e62\uff0c\u5251\u5c16\u4e0a\u5c31 \u4f3c\u633d\u4e86\u5343\u65a4\u91cd\u7269\u4f3c\u7684\uff0c\u4e1c\u4e00\u6307\u3001\u897f\u4e00\u5212\uff0c\u6162\u541e\u541e\u7684\u597d\u4f3c\u5341\u5206\u5403\u529b\u3002\u4f46\u90a3\u5251\u5149\u7f2d\u7ed5\uff0c\u5374\u8019 \u5168\u8eab\u5c01\u95ed\u5f97\u98ce\u96e8\u4e0d\u900f\u3002\u800c\u4e14\u62db\u6570\u867d\u7136\u7f13\u6162\u4e0b\u6765\uff0c\u4f46\u62db\u91cc\u5957\u62db\uff0c\u5f0f\u4e2d\u5957\u5f0f\uff0c\u6bcf\u4e00\u62db\u4e4b\u4e2d\uff0c \u90fd\u85cf\u7740\u6781\u4e3a\u590d\u6742\u7684\u65e0\u7a77\u53d8\u5316\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u66f4\u662f\u60ca\u5947\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e0d\u662f\u6700\u4e0a\u4e58\u5251\u672f\u4e2d\u7684\u5927\u987b\u5f25\u5251\u5f0f\u5417\uff1f\u5e78\u597d\u6211\u5e08\u7236\u4e5f\u66fe \u7ecf\u6559\u8fc7\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u90a3\u5c11\u5e74\u7684\u5b9d\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u4f46\u89c1\u5251\u5f71\u5343\u91cd\uff0c\u5bd2\u5149\u4e07\u9053\uff0c\u5f25\u7a7a\u531d\u5730\u7684 \u75be\u5377\u8fc7\u6765\uff0c\u8fd9\u662f\u201c\u5927\u987b\u5f25\u5251\u5f0f\u201d\u4e2d\u4e00\u62db\u56f0\u654c\u5999\u62db\uff0c\u540d\u4e3a\u201c\u516b\u65b9\u98ce\u96e8\u201d\uff0c\u82e5\u662f\u5f85\u4ed6\u5251\u62db\u7528 \u5b9e\uff0c\u654c\u4eba\u5c31\u8981\u88ab\u56f0\u5728\u5251\u5149\u5708\u91cc\uff0c\u518d\u4e5f\u4e0d\u80fd\u7a81\u56f4\uff0c\u7eb5\u4f7f\u529f\u529b\u80dc\u8fc7\u5bf9\u65b9\uff0c\u81f3\u591a\u80fd\u9003\u51fa\u6027\u547d\uff0c \u96be\u514d\u53d7\u4f24\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7cbe\u795e\u9661\u632f\uff0c\u559d\u58f0\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u5251\u5149\u4e00\u51dd\uff0c\u5339\u7ec3\u822c\u7684\u523a\u51fa\uff0c\u8fd9\u4e00\u62db\u540d\u4e3a\u201c\u5f3a \u5f29\u7a7f\u4e91\u201d\uff0c\u6b63\u662f\u91d1\u4e16\u9057\u6240\u521b\u7684\u7834\u201c\u516b\u65b9\u98ce\u96e8\u201d\u7684\u4e00\u62db\u7edd\u62db\uff0c\u90a3\u5c11\u5e74\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u5251\u6cd5 \u7acb\u5373\u6536\u655b\uff0c\u60f3\u4e0d\u5230\u6c5f\u6d77\u5929\u8fd9\u6837\u5316\u89e3\u4ed6\u7684\u62db\u6570\uff0c\u6bd4\u4ed6\u7684\u201c\u5927\u987b\u5f25\u5251\u5f0f\u201d\u4e2d\u539f\u6765\u7684\u89e3\u6cd5\u8fd8\u66f4 \u7cbe\u5999\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5251\u5f0f\u4e00\u53d8\uff0c\u4ece\u201c\u516b\u65b9\u98ce\u96e8\u201d\u500f\u5730\u53d8\u4e3a\u201c\u575a\u57ce\u5fa1\u654c\u201d\uff0c\u8fd9\u4e00\u62db\u91d1\u4e16\u9057\u5374\u672a\u66fe\u6559 \u8fc7\u7834\u6cd5\uff0c\u6c5f\u6d77\u5929\u7684\u5251\u62db\u4fbf\u7ed9\u5c01\u4e86\u51fa\u6765\uff0c\u653b\u4e0d\u8fdb\u53bb\uff0c\u5230\u4e86\u6b64\u65f6\uff0c\u4e24\u4eba\u4e0d\u7ea6\u800c\u540c\u7684\u540c\u65f6\u6536\u624b\uff0c \u53eb\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u62b1\u5251\u65bd\u672d\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u6c5f\u6d77\u5929\uff0c\u5bb6\u5e08\u91d1\u4e16\u9057\u3002\u8bf7\u95ee\u5929\u5c71\u8001\u638c\u95e8\u5510\u6653\u6f9c\u662f\u9601 \u4e0b\u4f55\u4eba\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff1f\u539f\u6765\u662f\u91d1\u5927\u4fa0\u7684\u9ad8\u8db3\u3002 \u3000\u3000\u6211\u540d\u53eb\u5510\u52a0\u6e90\uff0c\u4f60\u6240\u95ee\u6b63\u662f\u6211\u7684\u7237\u7237\u3002\u201d\u6c5f\u6d77\u5929\u8fd9\u624d\u7701\u8d77\uff0c\u602a\u4e0d\u5f97\u81ea\u5df1\u89c9\u5f97\u4ed6\u7684\u9762 \u8c8c\u4f3c\u66fe\u76f8\u8bc6\u3002\u539f\u6765\u662f\u56e0\u4e3a\u4ed6\u957f\u5f97\u8ddf\u4ed6\u7684\u7236\u4eb2\u9887\u4e3a\u76f8\u4f3c\u3002\u5510\u52a0\u6e90\u7684\u7236\u4eb2\u5510\u7ecf\u5929\uff0c\u6c5f\u6d77\u5929\u662f \u89c1\u8fc7\u7684\u3002 \u3000\u3000\u5929\u5c71\u6d3e\u5251\u672f\u5999\u7edd\u5929\u4e0b\uff0c\u91d1\u4e16\u9057\u535a\u91c7\u767e\u5bb6\uff0c\u4ee5\u4e54\u5317\u6e9f\u7684\u6b66\u529f\u79d8\u7c4d\u4e3a\u57fa\u77f3\uff0c\u4ee5\u5929\u5c71\u6d3e\u7684 \u6b63\u5b97\u5185\u529f\u5fc3\u6cd5\u4e3a\u6881\u5f80\uff0c\u5efa\u7acb\u4e86\u81ea\u5df1\u7684\u6b66\u5b66\uff0c\u5f00\u521b\u4e86\u81ea\u5df1\u7684\u95e8\u6237\u3002\u800c\u5176\u4e2d\u5251\u672f\u4e00\u9879\uff0c\u91c7\u81ea \u5929\u5c71\u5251\u6cd5\u7684\u66f4\u591a\u3002\u4f46\u91d1\u4e16\u9057\u81ea\u5df1\u4e5f\u6709\u8bb8\u591a\u53d8\u5316\u589e\u76ca\uff0c\u90a3\u62db\u7834\u89e3\u201c\u516b\u65b9\u98ce\u96e8\u201d\u7684\u201c\u5f3a\u5f29\u7a7f \u4e91\u201d\uff0c\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\u3002 \u3000\u3000\u5510\u52a0\u6e90\u662f\u6b66\u5b66\u4e16\u5bb6\uff0c\u55dc\u6b66\u6210\u8ff7\uff0c\u4e0d\u6687\u5bd2\u55a7\uff0c\u4fbf\u5373\u95ee\u9053\uff1a\u201c\u6c5f\u5144\u8fd9\u4e00\u628a\u5251\uff0c\u60f3\u662f\u4e54\u5317 \u6e9f\u4e09\u5b9d\u4e4b\u4e00\u7684\u88c1\u4e91\u5b9d\u5251\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5144\u53f0\u8fd9\u4e00\u628a\u5251\u60f3\u5fc5\u4e5f\u5c31\u662f\u8d35\u6d3e\u9547\u4e1a\u4e4b\u5b9d\u7684\u6e38\u9f99\u5251\u4e86\uff1f\u201d\u5510\u52a0\u6e90 \u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u8fd9\u4e24\u53e3\u5b9d\u5251\u662f\u7b2c\u4e8c\u6b21\u76f8\u4f1a\u4e86\u3002\u4e0a\u4e00\u6b21\u6211\u7237\u7237\u66fe\u7528\u8fd9\u628a\u6e38\u9f99\u5251\u4e0e\u5973\u9b54 \u5934\u5389\u80dc\u7537\u7684\u88c1\u4e91\u5251\u8f83\u91cf\uff0c\u7ed3\u679c\u5728\u5251\u672f\u4e0a\u80dc\u4e86\u5979\u4e00\u62db\uff1b\u4f46\u5728\u6bd4\u8d5b\u6697\u5668\u7684\u65f6\u5019\uff0c\u6211\u7237\u7237\u7684\u5929 \u5c71\u795e\u8292\u5374\u7ed9\u5979\u7684\u5b9d\u5251\u524a\u65ad\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u8fd9\u624d\u77e5\u9053\uff0c\u539f\u6765\u5510\u52a0\u6e90\u5219\u624d\u6240\u53d1\u7684\u90a3\u79cd\u5947\u5f62\u6697\u5668\uff0c \u5c31\u662f\u5929\u4e0b\u6697\u5668\u4e2d\u5a01\u529b\u6700\u5f3a\u7684\u5929\u5c71\u795e\u8292\u3002 \u3000\u3000\u5510\u52a0\u6e90\u9053\uff1a\u201c\u4ee4\u5e08\u91d1\u5927\u4fa0\u548c\u6211\u5bb6\u662f\u4e24\u4ee3\u4ea4\u60c5\uff0c\u6211\u7237\u7237\u5e38\u5e38\u8c08\u53ca\u4ed6\u7684\u3002\u53ef\u60dc\u6211\u8fd8\u672a\u6709 \u673a\u7f18\u62dc\u89c1\u4ee4\u5e08\u3002\u201d\u53c8\u9053\uff1a\u201c\u90a3\u6b21\u6211\u7237\u7237\u548c\u5389\u80dc\u7537\u8f83\u91cf\u7684\u65f6\u5019\uff0c\u6211\u8fd8\u5728\u8941\u8913\u4e4b\u4e2d\uff0c\u4ec0\u4e48\u90fd \u4e0d\u77e5\u9053\uff0c\u540e\u6765\u624d\u542c\u8bf4\u6211\u7237\u7237\u6709\u751f\u4ee5\u6765\uff0c\u5c31\u662f\u8f93\u4e86\u90a3\u4e00\u573a\u3002\u7ecf\u8fc7\u90a3\u6b21\u8f83\u91cf\uff0c\u6211\u7237\u7237\u5728\u5251\u672f \u4e0a\u7cbe\u76ca\u6c42\u7cbe\uff0c\u524d\u51e0\u5e74\u66fe\u6709\u610f\u601d\u8bf7\u4ee4\u5e08\u524d\u6765\u5929\u5c71\u5207\u78cb\u5251\u672f\uff0c\u53ef\u60dc\u4e0d\u77e5\u4ee4\u5e08\u53bb\u5411\u3002\u4eca\u65e5\u5f97\u9047 \u6c5f\u5144\uff0c\u5e78\u4f55\u5982\u4e4b\uff0c\u4f60\u7834\u6211\u5927\u987b\u5f25\u5251\u5f0f\u2018\u516b\u65b9\u98ce\u96e8\u2019\u90a3\u4e00\u62db\uff0c\u771f\u662f\u7cbe\u5999\u7edd\u4f26\uff0c\u5c0f\u5f1f\u4e0d\u80dc\u4f69 \u670d\uff01\u201d\u8a00\u4e0b\u53c8\u662f\u9ad8\u5174\uff0c\u53c8\u89c9\u6709\u70b9\u60d8\u7136\u3002 \u3000\u3000\u539f\u6765\u5510\u52a0\u6e90\u4ee5\u4e3a\u4ed6\u5bb6\u7684\u5929\u5c71\u5251\u6cd5\uff0c\u7ecf\u8fc7\u5386\u4ee3\u7956\u5e08\u4ee5\u53ca\u4ed6\u7684\u7956\u7236\u4e0d\u65ad\u7684\u6539\u8fdb\u4e4b\u540e\uff0c\u5df2 \u7ecf\u5230\u8fbe\u5c3d\u5584\u5c3d\u7f8e\uff0c\u65e0\u4ee5\u590d\u52a0\u7684\u5883\u754c\uff0c\u54ea\u77e5\u8fd8\u662f\u8f93\u7ed9\u4e86\u6c5f\u6d77\u5929\u4e00\u62db\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u5e08\u7236\u8fd9\u5957\u5251\u672f\uff0c\u5176\u5b9e\u5c31\u662f\u4ece\u8d35\u6d3e\u7684\u5251\u672f\u4e2d\u6f14\u53d8\u51fa\u6765\u7684\u3002\u5728\u672a\u9047\u5144\u53f0 \u4e4b\u524d\uff0c\u6211\u4e5f\u4ee5\u4e3a\u5bf9\u5927\u987b\u5f25\u5251\u5f0f\u7684\u7cbe\u534e\uff0c\u5df2\u7ecf\u5c3d\u5f97\u65e0\u9057\u4e86\u3002\u54ea\u77e5\u4eca\u65e5\u4e00\u89c1\uff0c\u5374\u539f\u6765\u6211\u4e5f\u8fd8 \u662f\u4e95\u86d9\u7aa5\u5929\uff0c\u4e0d\u77e5\u5929\u5730\u4e4b\u5e7f\u3002\u5144\u53f0\u5316\u89e3\u6211\u2018\u5f3a\u5f29\u7a7f\u4e91\u2019\u90a3\u4e00\u62db\uff0c\u66f4\u662f\u7cbe\u5999\u7edd\u4f26\uff0c\u5c0f\u5f1f\u5341 \u5206\u4f69\u670d\u3002\u201d\u5510\u52a0\u6e90\u9ad8\u5174\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u540d\u4e3a\u2018\u575a\u57ce\u5fa1\u654c\u2019\uff0c\u662f\u6211\u7237\u7237\u548c\u5389\u80dc\u7537\u6bd4 \u6b66\u4e4b\u540e\uff0c\u6240\u521b\u7684\u65b0\u62db\uff0c\u4ece\u6765\u6ca1\u6709\u7528\u8fc7\u7684\u3002\u201d \u3000\u3000\u4e24\u4eba\u8c08\u5f97\u5f88\u662f\u6295\u673a\uff0c\u5510\u52a0\u6e90\u89c1\u6c5f\u6d77\u5929\u6b66\u529f\u53c8\u9ad8\uff0c\u4eba\u53c8\u8c26\u865a\uff0c\u6709\u5fc3\u7ed3\u7eb3\uff0c\u8bf4\u9053\uff1a\u201c\u4ee4 \u5e08\u548c\u6211\u5bb6\u662f\u4e24\u4ee3\u4ea4\u60c5\uff0c\u54b1\u4eec\u4e43\u662f\u7b2c\u4e09\u4ee3\u7684\u652f\u60c5\u4e86\u3002\u6211\u610f\u6b32\u4e0e\u6c5f\u5144\u7ed3\u4e3a\u5f02\u59d3\u5144\u5f1f\uff1a\u4eca\u540e\u4e5f \u597d\u65f6\u5e38\u8bf7\u76ca\uff0c\u4e0d\u77e5\u6c5f\u5144\u610f\u4e0b\u5982\u4f55\uff1f\u201d\u6c5f\u6d77\u5929\u559c\u9053\uff1a\u201c\u662f\u6240\u613f\u4e5f\uff0c\u4e0d\u6562\u8bf7\u8033\u3002\u5982\u6b64\u5c0f\u5f1f\u9ad8 \u6500\u4e86\u3002\u201d\u5f53\u4e0b\u64ae\u4e0a\u4e3a\u9999\uff0c\u4e24\u4eba\u76f8\u4e92\u516b\u62dc\u5b9a\u76df\uff0c\u53d9\u8d77\u5e74\u9f84\uff0c\u5510\u52a0\u6e90\u8f83\u957f\u4e00\u5c81\uff0c\u505a\u4e86\u5927\u54e5\u3002 \u3000\u3000\u4e24\u4eba\u7ed3\u62dc\u4e4b\u540e\uff0c\u8fd9\u624d\u5404\u81ea\u53d9\u8ff0\u6765\u5230\u6b64\u7684\u7ecf\u8fc7\u3002\u539f\u6765\u5510\u52a0\u6e90\u662f\u5949\u4e86\u5c3c\u6cca\u5c14\u65b0\u738b\u4e4b\u547d\uff0c \u6765\u5230\u6b64\u95ee\u4fa6\u67e5\u65e7\u738b\u7684\u4e0b\u843d\u3002\u5510\u52a0\u6e90\u9053\uff1a \u3000\u3000\u201c\u6709\u4eba\u62a5\u8baf\uff0c\u8bf4\u662f\u90a3\u66b4\u541b\u9003\u5230\u4e86\u6606\u5e02\u5170\u56fd\uff0c\u4e0e\u56fd\u738b\u52fe\u7ed3\uff0c\u6b64\u4eba\u4e0d\u9664\uff0c\u7ec8\u662f\u5c3c\u6cca\u5c14\u7684 \u5fc3\u8179\u4e4b\u60a3\uff0c\u6211\u6765\u4e86\u51e0\u5929\uff0c\u82e6\u4e8e\u65e0\u8def\u6253\u542c\uff0c\u4e5f\u4e0d\u77e5\u6d88\u606f\u662f\u5426\u5c5e\u5b9e\uff0c\u6545\u6b64\u4eca\u665a\u5192\u9669\u5165\u5bab\u4e00\u63a2\u3002 \u60f3\u4e0d\u5230\u4e0e\u6c5f\u5144\u8bef\u4f1a\uff0c\u52a8\u8d77\u624b\u6765\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f2f\u7236\u4f2f\u6bcd\u3001\u90fd\u5df2\u5230\u5c3c\u6cca\u5c14\u53bb\u4e86\u3002\u5927\u54e5\u8fd8 \u6765\u89c1\u7740\u7236\u6bcd\u4e48\uff1f\u201d \u3000\u3000\u5510\u52a0\u6e90\u6014\u4e86\u4e00\u6014\uff0c\u95ee\u9053\uff1a\u201c\u8d24\u5f1f\u600e\u4e48\u77e5\u9053\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u5728\u9752\u6d77\u767d\u6559\u6cd5\u738b\u9102\u514b \u6c81\u5bab\uff0c\u66fe\u89c1\u8fc7\u4ee4\u5c0a\u5927\u4eba\u3002\u540e\u6765\u53c8\u4e00\u540c\u8d74\u9a6c\u8428\u513f\u56fd\u5b9d\u8c61\u6cd5\u5e08\u7684\u91d1\u9e70\u4e4b\u4f1a\u3002\u4f1a\u4e00\u7ed3\u675f\uff0c\u4ee4\u5c0a\u3001 \u4ee4\u5802\uff0c\u8fd8\u6709\u9648\u5929\u5b87\u4f2f\u4f2f\u548c\u9648\u4f2f\u6bcd\uff0c\u5c31\u7acb\u523b\u542f\u7a0b\u5728\u5c3c\u6cca\u5c14\u4e86\u3002\u201d\u5f53\u4e0b\u5c06\u8fd9\u4e24\u4ef6\u4e8b\u60c5\u7b80\u8981\u8bf4 \u4e86\u4e00\u904d\u3002 \u3000\u3000\u5510\u52a0\u6e90\u9053\uff1a\u201c\u840d\u59e8\u672c\u662f\u5bb6\u6bcd\u7684\u4f8d\u5973\uff0c\u5979\u662f\u5949\u4e86\u5c3c\u6cca\u5c14\u65b0\u738b\u4e4b\u547d\uff0c\u56de\u4e2d\u56fd\u6765\u8bf7\u6211\u6bcd\u4eb2 \u7684\u3002\u4f46\u76f4\u5230\u6211\u52a8\u8eab\u4e4b\u6bd4\u8fd8\u672a\u89c1\u4ed6\u4eec\u6765\u5230\uff0c\u5f88\u53ef\u80fd\u662f\u5f7c\u6b64\u90fd\u5728\u8def\u4e0a\uff0c\u5374\u6ca1\u6709\u78b0\u5934\u3002\u201d \u3000\u3000\u5510\u52a0\u6e90\u60f3\u4e86\u4e00\u60f3\uff1b\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u5230\u4e86\u52a0\u5fb7\u6ee1\u90fd\uff0c\u7acb\u5373\u5c31\u4f1a\u77e5\u9053\u6211\u7684\u6d88\u606f\u3002\u5c3c\u6cca \u5c14\u738b\u8bf7\u5bb6\u6bcd\u524d\u5f80\uff0c\u672c\u662f\u8981\u5979\u5e2e\u52a9\u5e73\u5b9a\u5185\u4e71\u7684\u3002\u5982\u4eca\u90a3\u66b4\u541b\u5df2\u5230\u4e86\u8fd9\u513f\uff0c\u6211\u4e5f\u5949\u4e86\u56fd\u738b\u4e4b \u547d\uff0c\u6765\u5230\u8fd9\u513f\u8ffd\u8e2a\u4ed6\u4e86\u3002\u5bb6\u7236\u5bb6\u6bcd\u5f97\u77e5\u6b64\u4e8b\uff0c\u5b9a\u4e5f\u4f1a\u8d76\u6765\u7684\u3002\u7b97\u7b97\u65e5\u7a0b\uff0c\u6211\u56de\u56fd\u7684\u524d\u4e24 \u5929\uff0c\u4ed6\u4eec\u5df2\u7ecf\u4ece\u9a6c\u8428\u513f\u56fd\u51fa\u53d1\uff1a\u4ed6\u4eec\u5230\u4e86\u52a0\u5fb7\u6ee1\u90fd\u4e4b\u540e\uff0c\u5373\u4f7f\u6709\u51e0\u5929\u803d\u6401\uff0c\u4e0d\u4e45\u4e5f\u4f1a\u6765 \u5230\u6b64\u5730\u7684\u3002\u6211\u7236\u6bcd\u4e00\u6765\uff0c\u518d\u591a\u4e00\u4e2a\u5996\u5a46\uff0c\u4e5f\u80fd\u5bf9\u4ed8\u3002\u54b1\u4eec\u4e0d\u5982\u591a\u7b49\u51e0\u5929\uff0c\u5f85\u6211\u7239\u5a18\u6765\u4e86\uff0c \u518d\u5546\u5927\u8ba1\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6c89\u541f\u4e0b\u8bed\uff0c\u5510\u52a0\u6e90\u9053\uff1a\u201c\u8d24\u5f1f\u6709\u4f55\u5fc3\u4e8b\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a \u3000\u3000\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u5c0f\u5f1f\u6709\u4f4d\u597d\u53cb\uff0c\u5979\u662f\u6211\u5e08\u5f1f\u7684\u59b9\u59b9\uff0c\u5728\u6606\u5e03\u5170\u56fd\u906d\u9047\u4e0d\u6d4b\u4e4b\u7978\uff0c\u6b64\u523b \u591a\u534a\u662f\u5df2\u88ab\u56da\u5728\u5bab\u4e2d\uff0c\u5979\u4e00\u65e5\u672a\u79bb\u9669\u5883\uff0c\u6211\u603b\u662f\u96be\u4ee5\u5fc3\u5b89\u3002\u201d\u5f53\u4e0b\u53c8\u5c06\u9a6c\u8428\u513f\u56fd\u4e0e\u6606\u5e03 \u5170\u56fd\u7684\u7ea0\u7eb7\uff0c\u4ee5\u53ca\u8c37\u4e2d\u83b2\u600e\u6837\u5931\u9677\u5728\u6606\u5e03\u5170\u56fd\u7684\u7ecf\u8fc7\uff0c\u2014\u2014\u544a\u8bc9\u4e86\u5510\u52a0\u6e90\uff0c\u5510\u52a0\u6e90\u60f3\u4e0d \u5230\u5185\u60c5\u5982\u6b64\u590d\u6742\uff0c\u60ca\u5947\u4e0d\u5df1\u3002 \u3000\u3000\u5510\u52a0\u6e90\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u9a6c\u8428\u513f\u56fd\u7684\u516c\u4e3b\u539f\u6765\u5c31\u662f\u9099\u5c71\u638c\u95e8\u8c37\u4e4b\u534e\u7684\u5f92\u5f1f\uff0c\u548c\u6211 \u4eec\u5929\u5c71\u4e00\u6d3e\u4e5f\u662f\u6781\u6709\u6e0a\u6e90\u7684\u4e86\u3002\u8d24\u5f1f\u65e2\u7136\u6025\u4e8e\u6551\u5979\u8131\u9669\uff0c\u611a\u5144\u53c8\u5c82\u80fd\u7f6e\u8eab\u4e8b\u5916\uff1f\u8fd9\u4e48\u6837 \u5427\uff0c\u4eca\u665a\u662f\u4e0d\u80fd\u53bb\u4e86\uff0c\u54b1\u4eec\u56de\u53bb\u6b47\u606f\u4e00\u53e3\uff0c\u660e\u5929\u665a\u4e0a\uff0c\u518d\u95ef\u5b83\u4e00\u6b21\u864e\u7a74\u9f99\u6f6d\uff01\u201d\u6c5f\u6d77\u5929 \u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u8fd8\u662f\u7b49\u5f85\u4f2f\u7236\u4f2f\u6bcd\u6765\u4e86\u518d\u8bf4\u5427\u3002\u660e\u5929\u665a\u4e0a\uff0c\u8ba9\u6211\u72ec\u81ea\u53bb\u63a2 \u4e00\u8d9f\u3002\u201d \u3000\u3000\u5510\u52a0\u6e90\u751a\u662f\u4e0d\u60a6\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u65e2\u7136\u4e49\u7ed3\u91d1\u5170\uff0c\u7406\u8be5\u6709\u798f\u540c\u4eab\uff0c\u6709\u7978\u540c\u5f53\uff0c\u6211\u600e\u80fd \u8ba9\u4f60\u4e00\u4eba\u5355\u72ec\u5192\u9669\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u8eab\u8d1f\u91cd\u4efb\uff0c\u5c0f\u5f1f\u4e0d\u613f\u4e3a\u4e86\u79c1\u4eba\u4e4b\u4e8b\uff0c\u5728\u7d2f\u4e8e\u4f60\u3002\u201d \u5510\u52a0\u6e90\u9053\uff1a\u201c\u4f60\u548c\u90a3\u5996\u5a46\u4ea4\u8fc7\u624b\uff0c\u7a76\u7adf\u662f\u600e\u4e48\u4e2a\u5389\u5bb3\uff1f\u54b1\u4eec\u4e24\u4eba\u90fd\u4e0d\u662f\u5979\u7684\u5bf9\u624b\u5417\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u82e5\u8bba\u771f\u5b9e\u6b66\u529f\uff0c\u5c0f\u5f1f\u867d\u7136\u6ca1\u5fc5\u80dc\u628a\u63e1\uff0c\u5012\u4e5f\u8fd8\u4e0d\u4f1a\u60e7\u5979\u3002\u53ea\u662f\u5979\u4e00\u53cc \u6bd2\u638c\u786e\u662f\u6781\u4e3a\u5389\u5bb3\uff0c\u522b\u8bf4\u7ed9\u5979\u6253\u4e2d\uff0c\u53ea\u662f\u90a3\u4e00\u80a1\u8165\u98ce\uff0c\u5df2\u4ee4\u4eba\u5fc3\u5934\u70e6\u95f7\uff0c\u6d51\u8eab\u61ca\u70ed\uff0c\u529f \u529b\u4e5f\u5c31\u4e0d\u77e5\u4e0d\u89c9\u7684\u96be\u4ee5\u53d1\u6325\u4e86\u3002\u201d \u3000\u3000\u5510\u52a0\u6e90\u542c\u4e86\u5ffd\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d\u5510\u52a0\u6e90\u9053\uff1a\u201c\u6211\u672c \u6765\u6709\u4e00\u6735\u5929\u5c71\u96ea\u83b2\u7684\uff0c\u8fd9\u5929\u5c71\u96ea\u83b2\uff0c\u80fd\u89e3\u767e\u6bd2\uff0c\u53ef\u60dc\u6211\u5728\u5c3c\u6cca\u5c14\u5df2\u9001\u4e86\u4eba\u4e86\u3002\u201d \u3000\u3000\u6597\u8f6c\u661f\u79fb\uff0c\u5df2\u662f\u4e94\u66f4\u65f6\u5206\uff0c\u5510\u52a0\u6e90\u9053\uff1a\u201c\u54b1\u4eec\u56de\u53bb\u518d\u4ece\u957f\u8ba1\u8bae\u5427\u3002\u53cd\u6b63\u4f60\u4e5f\u5f97\u5230\u660e \u5929\u665a\u4e0a\uff0c\u65b9\u80fd\u884c\u4e8b\uff0c\u8bf4\u4e0d\u5b9a\u660e\u5929\u6211\u7239\u7239\u5df2\u7ecf\u5230\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u5fc3\u60f3\uff1a\u201c\u73e0\u7a46\u5e08\u5f1f\u5185\u529f\u6df1\u6e5b\uff0c \u4ed6\u548c\u4e91\u5bb6\u5144\u59b9\uff0c\u9677\u5165\u51b0\u6cb3\uff0c\u672a\u5fc5\u4fbf\u4f1a\u9001\u547d\u3002\u6211\u5e72\u7239\u5df2\u6cbf\u7740\u90a3\u6761\u51b0\u6cb3\u53bb\u627e\u5bfb\u4ed6\u4eec\u4e86\u3002\u8981\u662f \u4e8e\u7239\u5bfb\u7740\u4ed6\u4eec\uff0c\u90a3\u56fa\u7136\u6700\u597d\uff0c\u5373\u4f7f\u4e0d\u89c1\uff0c\u5e72\u7239\u4e5f\u8981\u5230\u8fd9\u91cc\u6765\u7ed9\u6211\u62a5\u8baf\u7684\u3002\u4ed6\u662f\u5929\u4e0b\u7b2c\u4e00 \u795e\u533b\uff0c\u4e5f\u8db3\u4ee5\u5bf9\u4ed8\u90a3\u4e2a\u5996\u5a46\u3002 \u3000\u3000\u5927\u54e5\u8bf4\u5f97\u4e0d\u9519\uff0c\u591a\u7b49\u4e00\u5929\uff0c\u4e0d\u8bba\u662f\u8c01\u6765\u5230\uff0c\u4e8b\u60c5\u4fbf\u6709\u8f6c\u673a\u3002\u201d\u4e8e\u662f\uff0c\u4fbf\u5bf9\u5510\u52a0\u6e90\u7684 \u63d0\u8bae\u8868\u793a\u540c\u610f\uff0c\u95ee\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u4f4f\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u5510\u52a0\u6e90\u9053\uff1a\u201c\u6211\u5bc4\u5bd3\u5728\u4e00\u95f4\u5587\u561b\u5e99\u91cc\uff0c\u79bb\u6b64\u7ea6\u6709\u4e8c\u5341\u91cc\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u4e5f\u662f\u5728 \u5728\u4e00\u95f4\u5bfa\u5e99\u91cc\uff0c\u79bb\u6b64\u66f4\u8fd1\uff0c\u5c31\u5728\u90a3\u8fb9\u5c71\u4e0b\uff0c\u53ef\u4ee5\u671b\u89c1\u738b\u5bab\u7684\u3002\u4e0d\u5982\u5230\u6211\u90a3\u513f\u5427\u3002\u201d \u3000\u3000\u5f53\u4e0b\u4e24\u4eba\u8054\u8882\u4e0b\u5c71\uff0c\u4e00\u8def\u6307\u70b9\u5c71\u5ddd\uff0c\u8c08\u8bf4\u770b\u95fb\uff0c\u5510\u52a0\u6e90\u8bf4\u9053\uff1a\u201c\u963f\u5c14\u6cf0\u5c71\uff0c\u51b0\u6cb3\u6700 \u591a\u3002\u5929\u5c71\u6bd4\u5b83\u8fd8\u8981\u9ad8\u4e9b\uff0c\u5374\u6ca1\u6709\u8fd9\u6837\u5947\u666f\u3002\u4e0d\u8fc7\u5929\u5c71\u4e5f\u6709\u5b83\u7684\u7279\u6b8a\u98ce\u8c8c\uff0c\u5929\u5c71\u96ea\u83b2\uff0c\u5c31 \u662f\u5929\u4e0b\u7b2c\u4e00\u5947\u82b1\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u963f\u5c14\u6cf0\u5c71\u4e5f\u6709\u4e00\u79cd\u5947\u82b1\uff0c\u540d\u53eb\u2018\u96ea\u91cc\u7ea2\u5986\u2019\uff0c\u80fd\u4f7f\u4eba \u957f\u6625\u4e0d\u8001\uff0c\u4e5f\u662f\u5947\u5999\u5f97\u5f88\u3002\u201d\u5510\u52a0\u6e90\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u9053\uff1a \u3000\u3000\u201c\u8d24\u5f1f\uff0c\u4f60\u66fe\u4e0a\u8fc7\u7075\u9e6b\u5cf0\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u524d\u5929\u624d\u4ece\u90a3\u91cc\u7ecf\u8fc7\uff0c\u8fd9\u4e48\u8bf4\uff0c\u5927\u54e5\uff0c\u4f60\u4e5f\u662f\u4e0a\u8fc7\u7075\u9e6b\u5cf0\u7684\u4e86\uff1f\u201d\u5510 \u52a0\u6e90\u9053\uff1a\u201c\u7075\u9e6b\u5cf0\u4e0a\u6709\u95f4\u51b0\u5c4b\uff0c\u5c4b\u4e2d\u6709\u4e2a\u5973\u5b50\uff0c\u4f60\u53ef\u66fe\u89c1\u5230\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u5927\u662f\u60ca\u5947\uff0c\u5fc3 \u60f3\uff1a\u201c\u5927\u54e5\u5728\u6ce5\u6cca\u5c14\u51e0\u5e74\uff0c\u6700\u8fd1\u65b9\u59cb\u56de\u56fd\u3002\u78a7\u59b9\u5728\u7075\u9e6b\u5cf0\u5931\u4e8b\uff0c\u4ed6\u600e\u4f1a\u77e5\u9053\uff1f\u54e6\uff0c\u662f\u4e86\uff0c \u4ed6\u8bf4\u7684\u5927\u7ea6\u662f\u6307\u51b0\u5c4b\u7684\u4e3b\u4eba\uff0c\u51b3\u4e0d\u4f1a\u662f\u8bf4\u78a7\u59b9\u3002\u201d\u4ed6\u4e0d\u613f\u591a\u63d0\u534e\u4e91\u78a7\u7684\u4e8b\u60c5\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a \u201c\u6211\u53ea\u89c1\u4e00\u4e2a\u62ab\u7740\u767d\u88d8\u7684\u5c11\u5e74\uff0c\u4e0d\u89c1\u6709\u4f55\u5973\u5b50\u3002\u201d\u5510\u52a0\u6e90\u6709\u70b9\u5931\u671b\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u4e48\u8bf4\uff0c \u4f60\u89c1\u7684\u662f\u90a3\u5973\u5b50\u7684\u54e5\u54e5\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u6b63\u60f3\u77e5\u9053\u90a3\u767d\u88d8\u5c11\u5e74\u7684\u6765\u5386\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u5927\u54e5\uff0c \u4f60\u8ba4\u5f97\u4ed6\u7684\u5417\uff1f\u4ed6\u662f\u8c01\uff1f\u201d\u5510\u52a0\u6e90\u9053\uff1a\u201c\u8fd9\u4eba\u540d\u53eb\u7389\u6606\u4ed1\uff0c\u4ed6\u59b9\u59b9\u7389\u73b2\u73d1\uff0c\u662f\u4e00\u4f4d\u6b66\u6797 \u5947\u4eba\u7684\u513f\u5973\u3002\u51e0\u5e74\u524d\u6211\u8def\u8fc7\u7075\u9e6b\u5cf0\uff0c\u66fe\u9047\u89c1\u4ed6\u4eec\u5144\u59b9\u3002\u201d \u3000\u3000\u5510\u52a0\u6e90\u548c\u7389\u73b2\u73d1\u6709\u8fc7\u4e00\u6bb5\u5f88\u6df1\u7684\u4ea4\u60c5\u3002\u4f46\u4e5f\u4e0d\u613f\u591a\u8c08\u3002\u53ea\u8bf4\u4e86\u4e00\u4e9b\u7389\u5bb6\u5144\u59b9\u7684\u6765\u5386\uff0c \u548c\u4ed6\u4eec\u7236\u4eb2\u7684\u4e8b\u8ff9\u3002\u6c5f\u6d77\u5929\u4e0d\u77e5\u90a3\u767d\u88d8\u5c11\u5e74\uff0c\u5b9e\u5728\u5c31\u662f\u7389\u73b2\u73d1\u5973\u626e\u7537\u88c5\uff0c\u53ea\u9053\u679c\u771f\u662f\u7389 \u6606\u4ed1\uff0c\u5fc3\u91cc\u6697\u6697\u4e3a\u534e\u4e91\u78a7\u6b22\u559c\u3002\u201c\u4ed6\u4e8e\u78a7\u59b9\u6709\u6551\u547d\u4e4b\u6069\uff0c\u53c8\u540c\u662f\u6b66\u6797\u4e16\u5bb6\uff0c\u4f46\u613f\u78a7\u59b9\u4f1a \u6162\u6162\u6b22\u559c\u4ed6\u3002\u201d\u4ed6\u54ea\u6599\u5230\u4e16\u4e8b\u53d8\u5e7b\uff0c\u6bcf\u6bcf\u51fa\u4eba\u610f\u5916\uff0c\u534e\u4e91\u78a7\u540e\u6765\u679c\u7136\u662f\u6e10\u6e10\u6b22\u559c\u4e86\u53e6\u4e00 \u4e2a\u4eba\uff0c\u4f46\u90a3\u4eba\u5374\u4e0d\u662f\u7389\u6606\u4ed1\u3002 \u3000\u3000\u4e24\u4eba\u90fd\u662f\u4e00\u7b49\u4e00\u7684\u8f7b\u529f\uff0c\u66d9\u5149\u5fae\u73b0\uff0c\u5929\u8272\u8fd8\u672a\u5927\u4eae\uff0c\u4ed6\u4eec\u5df2\u56de\u5230\u6c5f\u6d77\u5929\u5bc4\u5c45\u7684\u90a3\u95f4 \u5bfa\u9662\uff0c\u4e5f\u5c1a\u672a\u6709\u4eba\u8d77\u6765\u3002\u6c5f\u6d77\u5929\u5e26\u9886\u4e49\u5144\uff0c\u6084\u6084\u7684\u56de\u5230\u623f\u4e2d\uff0c\u521a\u8e0f\u8fdb\u95e8\uff0c\u5ffd\u89c9\u4e00\u80a1\u6e05\u9999\uff0c \u6c81\u5165\u80ba\u8151\uff0c\u5b9a\u775b\u4e00\u770b\uff0c\u623f\u4e2d\u591a\u4e86\u4e00\u4e2a\u82b1\u6a3d\uff0c\u6709\u4e00\u6735\u7897\u53e3\u5927\u7684\u767d\u83b2\u82b1\u63d2\u5728\u6a3d\u4e2d\uff0c\u6b63\u662f\uff1a \u3000\u3000\u6b63\u6101\u65e0\u8ba1\u9664\u90aa\u6bd2\uff0c\u5ffd\u89c1\u4ed9\u82b1\u5c4b\u5185\u5f00\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e94\u5341\u4e00\u56de\u3000\u6b8b\u7b3a\u7834\u955c\u85cf\u5e7d\u79d8\u3000\u540c\u6c14\u8fde\u679d\u662f\u4e00\u5bb6 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e94\u5341\u4e00\u56de\u3000\u6b8b\u7b3a\u7834\u955c\u85cf\u5e7d\u79d8\u3000\u540c\u6c14\u8fde\u679d\u662f\u4e00\u5bb6 \u3000\u3000\u6c5f\u6d77\u5929\u60ca\u5947\u4e0d\u5df2\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u662f\u8c01\u5f00\u7684\u73a9\u7b11\uff0c\u7ed9\u6211\u9001\u6765\u4e86\u8fd9\u6837\u7f8e\u4e3d\u7684\u9c9c\u82b1\uff1f\u201d\u54ea\u77e5 \u6709\u4eba\u6bd4\u4ed6\u8fd8\u8981\u60ca\u5947\uff0c\u53ea\u89c1\u5510\u52a0\u6e90\u76ee\u77aa\u53e3\u5446\uff0c\u6ce8\u89c6\u7740\u90a3\u4e00\u6735\u82b1\uff0c\u5ffd\u5730\u201c\u554a\u5440\u201d\u4e00\u58f0\u53eb\u8d77\u6765 \u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u4f55\u4e0d\u65e9\u8bf4\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201d\u8bf4\u4ec0\u4e48\uff1f\u201d\u5510\u52a0\u6e90\u9053\uff1a\u201c\u539f\u6765\u4f60\u65e9\u5df2\u6709\u4e86\u4e00 \u6735\u5929\u5c71\u96ea\u83b2\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u8bf4\u8fd9\u6735\u82b1\uff0c\u8fd9\u6735\u82b1\u2026\u2026\u5c31\u662f\u2014\u2014\u201d\u5510\u52a0\u6e90 \u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u6735\u82b1\u5c31\u662f\u5929\u5c71\u96ea\u83b2\u4e86\uff0c\u4f60\u4ece\u54ea\u513f\u5f97\u6765\u7684\uff1f\u600e\u4e48\u4f60\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\uff1f\u201d\u6c5f\u6d77 \u5929\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u6211\u7684\uff0c\u8fde\u8fd9\u82b1\u6a3d\u90fd\u662f\u521a\u521a\u53d1\u73b0\u7684\u3002\u201d \u3000\u3000\u5510\u52a0\u6e90\u9053\uff1a\u201c\u8fd9\u5c31\u5947\u602a\u4e86\uff0c\u5929\u5c71\u96ea\u83b2\u662f\u6b66\u6797\u4e2d\u4eba\u68a6\u5bd0\u4ee5\u6c42\u7684\u5b9d\u7269\uff0c\u600e\u80af\u8f7b\u6613\u9001\u4eba\u3002\u201d \u6c5f\u6d77\u5929\u8df3\u4e0a\u5c4b\u9876\u4e00\u671b\uff0c\u53ea\u89c1\u66d9\u8272\u6726\u80e7\uff0c\u6b8b\u661f\u660e\u706d\uff0c\u54ea\u91cc\u6709\u4eba\u7684\u5f71\u4e8e\uff1f\u4ed6\u8fd0\u7528\u201c\u5929\u9041\u4f20\u97f3\u201d \u4e4b\u672f\uff0c\u5c06\u58f0\u97f3\u51dd\u6210\u4e00\u7ebf\uff0c\u8fdc\u8fdc\u9001\u51fa\uff1a\u201c\u662f\u54ea\u4f4d\u9ad8\u4eba\u8d60\u6211\u4ed9\u82b1\uff0c\u53ef\u5bb9\u62dc\u89c1\u4e48\uff1f\u201d\u4ed6\u5185\u529f\u7cbe \u8fdb\u4e4b\u540e\uff0c\u201c\u5929\u9041\u4f20\u97f3\u201d\u4e4b\u672f\u5df2\u662f\u8fd0\u7528\u5f97\u795e\u5999\u65e0\u65b9\uff0c\u58f0\u97f3\u8fdc\u8fdc\u9001\u51fa\uff0c\u53ea\u6709\u4fee\u4e60\u8fc7\u4e0a\u4e58\u5185\u529f \u7684\u4eba\u624d\u542c\u5f97\u89c1\uff0c\u4ed6\u6240\u5728\u4e4b\u5904\u7684\u5bfa\u9662\u50e7\u4eba\u5374\u53cd\u800c\u6beb\u65e0\u60ca\u6270\u3002\u4f46\u4ed6\u63a5\u8fde\u4e09\u6b21\u4f20\u97f3\uff0c\u5374\u5140\u662f\u6beb \u65e0\u53cd\u5e94\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5e9f\u7136\u800c\u9000\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u53ef\u771f\u662f\u5374\u4e4b\u4e0b\u606d\uff0c\u53d7\u4e4b\u6709\u6127\u4e86\u3002\u201d\u5510\u52a0\u6e90\u9053\uff1a\u201c\u8fd8\u6709 \u4e00\u6837\u5947\u602a\u4e4b\u5904\uff0c\u4f60\u770b\u770b\u8fd9\u6735\u96ea\u83b2\u989c\u8272\u5a07\u8273\uff0c\u50cf\u4e0d\u50cf\u521a\u6458\u4e0b\u6765\u7684\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u521a\u624d\u6ca1\u6709\u60f3\u5230\u8fd9\u5c42\uff0c\u5f97\u5510\u52a0\u6e90\u63d0\u9192\uff0c\u679c\u7136\u89c9\u5f97\u795e\u5947\uff0c\u8bf4\u9053\uff1a\u201c\u96be\u9053\u90a3\u4eba\u7adf\u662f \u795e\u4ed9\uff0c\u61c2\u5f97\u7f29\u5730\u7684\u4ed9\u6cd5\uff1f\u201d \u3000\u3000\u5510\u52a0\u6e90\u9053\uff1a\u201c\u5929\u5c71\u96ea\u83b2\u79bb\u5f00\u679d\u5934\u4e4b\u540e\uff0c\u53ef\u4ee5\u4fdd\u6301\u5341\u65e5\uff0c\u65b9\u624d\u67af\u840e\uff0c\u4f46\u5929\u5c71\u79bb\u6b64\u6570\u5341 \u91cc\u4e4b\u9065\uff0c\u5373\u4f7f\u662f\u6458\u4e0b\u5df2\u7ecf\u5341\u65e5\uff0c\u90a3\u4eba\u7684\u811a\u7a0b\u4e4b\u5feb\uff0c\u4e5f\u662f\u5e76\u4e16\u65e0\u53cc\u4e86\u3002\u201d\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u96be \u9053\u662f\u6211\u59e8\u5a46\u7ed9\u6211\u5f00\u7684\u73a9\u7b11\uff1f\u201d\u5510\u52a0\u6e90\u7684\u59e8\u5a46\u5373\u662f\u4ed6\u7956\u6bcd\u51af\u745b\u7684\u59b9\u59b9\u51af\u7433\uff0c\u4eca\u5e74\u5df2\u662f\u4e03\u5341 \u5f00\u5916\uff0c\u4f46\u5e74\u867d\u8001\u8fc8\uff0c\u5c1a\u6709\u7ae5\u5fc3\uff0c\u6545\u800c\u5510\u52a0\u6e90\u7591\u5fc3\u662f\u5979\u3002\u6c5f\u6d77\u5929\u5219\u5728\u5fc3\u60f3\uff1a\u201c\u6211\u5e08\u7236\u6709\u9b3c \u795e\u83ab\u6d4b\u4e4b\u673a\uff0c\u901a\u5929\u5f7b\u5730\u4e4b\u80fd\uff0c\u83ab\u975e\u662f\u4ed6\u8001\u4eba\u5bb6\u6765\u4e86\u3002\u201d \u3000\u3000\u5510\u52a0\u6e90\u9053\uff1a\u201c\u4e0d\u7ba1\u8fd9\u96ea\u83b2\u662f\u8c01\u9001\u6765\u7684\uff0c\u5374\u6b63\u5408\u54b1\u4eec\u4eca\u665a\u4e4b\u7528\u3002 \u3000\u3000\u4f60\u53ea\u8981\u628a\u4e00\u7247\u82b1\u74e3\u542b\u5728\u53e3\u4e2d\uff0c\u5c31\u4e0d\u5fc5\u60e7\u6015\u90a3\u5996\u5a46\u7684\u6bd2\u638c\u8165\u98ce\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5929 \u5c71\u96ea\u83b2\u662f\u5426\u4efb\u4f55\u6bd2\u836f\u5747\u80fd\u6d88\u89e3\uff1f\u201d \u3000\u3000\u5510\u52a0\u6e90\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u5c3d\u7136\u3002\u5929\u5730\u95f4\u6709\u4e03\u79cd\u81f3\u6bd2\u4e4b\u7269\uff0c\u9ed1\u5fc3\u83b2\u3001\u8150\u9aa8\u8349\u3001\u65ad\u80a0\u82b1\u3001\u9e64 \u9876\u7ea2\u3001\u5b54\u96c0\u80c6\u3001\u91d1\u8695\u86ca\u548c\u4fee\u7f57\u82b1\uff0c\u82e5\u662f\u8fd9\u4e03\u79cd\u6bd2\u7269\uff0c\u6df7\u5408\u8d77\u6765\uff0c\u7814\u6210\u7c89\u672a\uff0c\u5929\u5c71\u96ea\u83b2\u4e5f \u5c31\u4e0d\u80fd\u89e3\u4e86\u3002\u542c\u8bf4\u4e8c\u5341\u5e74\u524d\uff0c\u5b5f\u795e\u901a\u5c31\u66fe\u7528\u8fc7\u8fd9\u79cd\u6bd2\u9152\u96be\u8fc7\u6211\u7684\u7237\u7237\uff0c\u540e\u6765\u5389\u80dc\u7537\u4e5f\u66fe \u7528\u8fc7\u8fd9\u79cd\u6bd2\u836f\u5bb3\u8fc7\u9099\u5c71\u6d3e\u7684\u638c\u95e8\u8c37\u4e4b\u534e\u3002\u4e0d\u8fc7\u96ea\u83b2\u867d\u4e0d\u80fd\u89e3\uff0c\u4e5f\u80fd\u4fdd\u5168\u6027\u547d\uff0c\u5018\u82e5\u53d7\u6bd2 \u4e4b\u4eba\uff0c\u5185\u529f\u5df2\u5230\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u518d\u52a0\u4e0a\u5929\u5c71\u96ea\u83b2\uff0c\u90a3\u5c31\u8fde\u8fd9\u4e03\u79cd\u6bd2\u7269\uff0c\u4e5f\u51b3\u8ba1\u4e0d\u80fd\u4f24\u5bb3 \u4ed6\u4e86\u3002\u8c37\u4e4b\u534e\u662f\u4f60\u5e08\u7236\u7684\u597d\u53cb\uff0c\u4f60\u4e00\u5b9a\u77e5\u9053\u4ed6\u4eec\u7684\u6545\u4e8b\u7684\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9099\u5c71\u8c37\u638c\u95e8\u5f53\u5e74\u4e2d\u4e86\u5389\u80dc\u7537\u7684\u5267\u6bd2\u3002\u5c31\u662f\u4ed7\u5929\u5c71\u96ea\u83b2\u4fdd\u5168\u4e86\u6027 \u547d\u3002\u540e\u6765\u53c8\u5f97\u6211\u5e08\u7236\u4ee5\u7edd\u9876\u5185\u529f\u76f8\u52a9\uff0c\u8fd9\u624d\u590d\u539f\u7684\u3002\u201d \u3000\u3000\u5510\u52a0\u6e90\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u73b0\u5728\u7684\u529f\u529b\uff0c\u4f9d\u6211\u770b\u6765\uff0c\u5df2\u4e0d\u900a\u4e8e\u4f60\u5e08\u7236\u5f53\u5e74\u3002\u518d\u6709\u4e86\u8fd9 \u6735\u5929\u5c71\u96ea\u83b2\uff0c\u4f60\u5373\u4f7f\u8fd8\u672a\u7ec3\u6210\u91d1\u521a\u4e0d\u574f\u4e4b\u8eab\uff0c\u4e5f\u5df2\u662f\u767e\u6bd2\u4e0d\u4fb5\u4e86\u3002\u4eca\u665a\u518d\u53bb\u3001\u5305\u4fdd\u4f60\u5b9a \u80fd\u62a5\u90a3\u5996\u5a46\u4e00\u638c\u4e4b\u4ec7\u3002\u201d\u6c5f\u6d77\u5929\u5fc3\u60f3\uff1a\u201c\u62a5\u4ec7\u8fd8\u5728\u5176\u6b21\uff0c\u4f46\u6c42\u5f97\u89c1\u83b2\u59b9\uff0c\u6211\u5df2\u662f\u4e8e\u613f\u8db3 \u77e3\u3002\u201d\u85cf\u597d\u96ea\u83b2\uff0c\u5fc3\u4e2d\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u7126\u6025\uff0c\u6068\u4e0d\u5f97\u5feb\u5feb\u5929\u9ed1\uff0c\u597d\u4e0e\u5510\u52a0\u6e90\u518d\u53bb\u95ef\u5bab\u591c \u63a2\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u6b64\u65f6\u4e5f\u6b63\u5728\u4e3a\u7740\u6c5f\u6d77\u5929\u800c\u60f4\u60f4\u4e0d\u5b89\uff0c\u5979\u6628\u665a\u542c\u5f97\u5fa1\u82b1\u56ed\u4e2d\u6709\u53ae\u6740\u4e4b\u58f0\uff0c\u4e00\u591c \u4e0d\u80fd\u5b89\u6795\u3002\u5929\u4eae\u4e4b\u540e\uff0c\u7acb\u5373\u627e\u4e00\u4e2a\u548c\u5979\u6bd4\u8f83\u8981\u597d\u7684\u5bab\u5a25\u6253\u63a2\u6d88\u606f\u3002\u8fd9\u5bab\u5a25\u672c\u662f\u516c\u4e3b\u7684\u4f8d \u5973\uff0c\u5bf9\u90a3\u7ae5\u59e5\u59e5\u751a\u4e3a\u50e7\u6068\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u95ee\u6628\u665a\u4e4b\u4e8b\u4e48\uff0c\u542c\u8bf4\u662f\u6765\u4e86\u4e24\u4e2a\u5c0f\u8d3c\u3002 \u3000\u3000\u53ef\u7b11\u90a3\u7ae5\u59e5\u59e5\u81ea\u5938\u5929\u4e0b\u65e0\u654c\uff0c\u6628\u665a\u90a3\u4e48\u591a\u4eba\uff0c\u5374\u8fde\u4e24\u4e2a\u5c0f\u8d3c\u90fd\u4e0d\u80fd\u64d2\u83b7\uff0c\u542c\u8bf4\u7ae5\u59e5 \u59e5\u8fd8\u5f88\u5403\u4e86\u70b9\u4e8f\u5462\uff01\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u53ef\u77e5\u9053\u90a3\u4e24\u4e2a\u5c0f\u8d3c\u662f\u4ec0\u4e48\u4eba\u4e48\uff1f\u201d\u90a3\u5bab\u5a25\u9053\uff1a\u201c\u4e0d \u77e5\u9053\uff0c\u53ea\u77e5\u9053\u90fd\u662f\u5e74\u7eaa\u5f88\u8f7b\u7684\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u88ab\u8f6f\u7981\u5bab\u4e2d\uff0c\u5df2\u6709\u5341\u591a\u592d\u4e86\uff0c\u65e0\u65f6\u65e0\u523b\uff0c\u4e0d\u5728\u76fc\u671b\u5979\u54e5\u54e5\u5feb\u6765\u6551\u5979\u3002\u542c\u4e86\u8fd9 \u4e2a\u6d88\u606f\uff0c\u5fc3\u4e2d\u4e00\u559c\u4e00\u5fe7\uff0c\u60f3\u9053\uff1a\u201c\u662f\u4e24\u4e2a\u672c\u9886\u6781\u597d\u7684\u2018\u5c0f\u8d3c\u2019\uff0c\u8fd9\u4e24\u4eba\u80fd\u5728\u7ae5\u59e5\u59e5\u624b\u4e2d \u9003\u8131\uff0c\u8fd9\u4e00\u5b9a\u662f\u6211\u7684\u54e5\u54e5\u548c\u6c5f\u6d77\u5929\u4e86\u3002\u6d77\u5929\u4e0d\u662f\u8981\u53bb\u6c34\u4e91\u5e84\u63a2\u8bbf\u534e\u4e91\u78a7\u7684\u4e48\uff1f\u600e\u7684\u5374\u53c8 \u4e0e\u6211\u54e5\u54e5\u540c\u6765\u6b64\u5730\uff1f\u4f46\u9664\u4e86\u4ed6\u4e4b\u5916\uff0c\u53c8\u8fd8\u6709\u8c01\u80fd\u4ee4\u90a3\u5996\u5a46\u5403\u4e8f\uff1f\u201d \u3000\u3000\u5979\u8d8a\u60f3\u8d8a\u89c9\u5f97\u662f\u6c5f\u6d77\u5929\u65e0\u7591\uff0c\u8fd9\u610f\u5916\u7684\u6d88\u606f\u7ed9\u5979\u5e26\u6765\u4e86\u610f\u5916\u7684\u559c\u60a6\uff0c\u4f46\u4e5f\u7ed9\u5979\u5e26\u6765 \u4e86\u610f\u5916\u7684\u4f18\u8651\uff0c\u201c\u54e5\u54e5\u7684\u672c\u9886\u867d\u5f3a\uff0c\u4f46\u90a3\u5996\u5a46\u7684\u6bd2\u529f\u66f4\u662f\u5389\u5bb3\uff0c\u6628\u665a\u4ed6\u4eec\u867d\u7136\u4e0d\u81f3\u88ab\u64d2\uff0c \u4f46\u6bd5\u7adf\u8fd8\u662f\u7ed9\u90a3\u5996\u5a46\u8d76\u8dd1\u4e86\u3002\u8981\u662f\u4ed6\u4eec\u90fd\u6551\u4e0d\u4e86\u6211\uff0c\u90a3\u6211\u8fd8\u6709\u4f55\u65b9\u6cd5\u53ef\u4ee5\u9003\u51fa\u751f\u5929\uff1f\u5509\uff0c \u4e0d\u77e5\u4ed6\u4eec\u4eca\u665a\u8fd8\u6765\u4e0d\u6765\uff1f\u201d\u5979\u5fc3\u91cc\u6e34\u671b\u80fd\u518d\u89c1\u6c5f\u6d77\u5929\uff0c\u4f46\u53c8\u6015\u6c5f\u6d77\u5929\u6765\u5f97\u591a\u4e86\uff0c\u603b\u6709\u4e00 \u6b21\u7ed9\u90a3\u5996\u5987\u4f24\u4e86\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5f53\u65e5\u88ab\u64d2\u4e4b\u540e\uff0c\u7ae5\u59e5\u59e5\u5c31\u7528\u201c\u4fee\u7f57\u9165\u9aa8\u6563\u201d\u6697\u4e2d\u4e0b\u6bd2\uff0c\u8fd9\u79cd\u6bd2\u836f\u5bf9\u4eba\u8eab\u5e76\u65e0 \u4f24\u5bb3\uff0c\u4f46\u4e2d\u6bd2\u4e4b\u540e\uff0c\u5374\u662f\u7b4b\u75b2\u9aa8\u8f6f\uff0c\u529f\u529b\u6d88\u5931\uff0c\u591a\u597d\u7684\u672c\u9886\u4e5f\u4f7f\u4e0d\u51fa\u6765\u3002\u8c37\u4e2d\u83b2\u8d77\u521d\u88ab \u56da\u5728\u51b7\u5bab\uff0c\u540e\u6765\u5f97\u592a\u4e0a\u7687\u89e3\u6551\uff0c\u653e\u5979\u51fa\u6765\uff0c\u8ba9\u5979\u5728\u5bab\u4e2d\u81ea\u7531\u8d70\u52a8\uff0c\u53ea\u662f\u5979\u529f\u529b\u5df2\u7ecf\u6d88\u5931\uff0c \u8981\u60f3\u9003\u51fa\u5bab\u53bb\uff0c\u90a3\u5374\u662f\u4e07\u4e07\u4e0d\u80fd\u4e86\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u6b63\u5728\u80e1\u601d\u4e71\u60f3\uff0c\u53ea\u89c1\u53c8\u4e00\u4e2a\u5bab\u5a25\u8d70\u4e86\u8fdb\u6765\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u592a\u4e0a\u7687\u6709\u8bf7\u516c\u4e3b\uff0c\u201d\u8c37\u4e2d\u83b2\u81ea\u51fa\u51b7\u5bab\u4e4b\u540e\uff0c\u4e00\u76f4\u5947\u602a\u6606\u5e03\u5170\u56fd\u7684\u592a\u4e0a\u7687\u4e3a\u4ec0\u4e48\u5bf9 \u5979\u8fd9\u4e48\u597d\uff0c\u8fd9\u51e0\u5929\u6765\uff0c\u5927\u4e0a\u7687\u4e5f\u66fe\u89c1\u8fc7\u5979\u4e24\u6b21\uff0c\u4e24\u6b21\u90fd\u6709\u4f8d\u536b\u5728\u65c1\uff0c\u592a\u4e0a\u7687\u4f3c\u662f\u6709\u6240\u987e \u5fcc\uff0c\u53ea\u662f\u8981\u8c37\u4e2d\u83b2\u966a\u4ed6\u4e0b\u68cb\uff0c\u8bf4\u4e9b\u95f2\u8bdd\u3002\u8c37\u4e2d\u83b2\u7591\u56e2\u585e\u80f8\uff0c\u5374\u8fd8\u6ca1\u6709\u673a\u4f1a\u95ee\u4ed6\u3002 \u3000\u3000\u5f53\u4e0b\u8c37\u4e2d\u83b2\u4fbf\u968f\u7740\u90a3\u4e2a\u5bab\u5a25\uff0c\u524d\u5f80\u5927\u4e0a\u7687\u6240\u4f4f\u7684\u4e07\u5bff\u5bab\uff0c\u521a\u8e0f\u8fdb\u5bab\u95e8\uff0c\u5ffd\u89c1\u4e24\u4e2a\u6b66 \u58eb\uff0c\u795e\u60c5\u6cae\u4e27\uff0c\u6b63\u8d70\u51fa\u6765\uff0c\u770b\u89c1\u8c37\u4e2d\u83b2\uff0c\u8fd8\u53cc\u53cc\u5411\u5979\u77aa\u4e86\u4e00\u773c\u3002\u8c37\u4e2d\u83b2\u8ba4\u5f97\u8fd9\u4e24\u4e2a\u6b66\u58eb \u6b63\u662f\u592a\u4e0a\u7687\u7684\u4f8d\u536b\uff0c\u524d\u4e24\u6b21\u5979\u966a\u592a\u4e0a\u7687\u4e0b\u68cb\u7684\u65f6\u5019\uff0c\u8fd9\u4e24\u4e2a\u6b66\u58eb\u81ea\u59cb\u81f3\u7ec8\u90fd\u5b88\u5728\u4e00\u65c1\u7684\u3002 \u8c37\u4e2d\u83b2\u89c1\u6b64\u60c5\u5f62\uff0c\u5fc3\u4e2d\u9887\u89c9\u5947\u602a\u3002 \u3000\u3000\u90a3\u5bab\u5a25\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u6b66\u58eb\u662f\u7ed9\u8001\u7956\u5b97\u8d76\u51fa\u6765\u7684\u3002\u201d\u8c37\u4e2d\u83b2\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u90a3 \u5bab\u5a25\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u662f\u5f53\u4eca\u7687\u4e0a\u6d3e\u6765\u7ed9\u8001\u79df\u5b97\u505a\u4f8d\u536b\u7684\uff0c\u8001\u7956\u5b97\u4eca\u5929\u65e9\u4e0a\u53d1\u4e86\u4e00\u987f\u813e\u6c14\uff0c \u8bf4\u4ed6\u900a\u4f4d\u800c\u505a\u592a\u4e0a\u7687\u5c31\u662f\u8981\u56fe\u4e2a\u6e05\u51c0\uff0c\u4e0d\u4e50\u610f\u6709\u4eba\u8ddf\u8fdb\u8ddf\u51fa\uff0c\u597d\u50cf\u628a\u4ed6\u5f53\u4f5c\u72af\u4eba\u770b\u5f85\u4f3c \u7684\uff0c\u6545\u800c\u4e0b\u4e86\u4e00\u9053\u547d\u4ee4\u7ed9\u5927\u5185\u603b\u7ba1\uff0c\u8981\u4ed6\u628a\u8fd9\u4e24\u4e2a\u4f8d\u536b\u7acb\u5373\u8c03\u8d70\u3002\u4ece\u4eca\u4e4b\u540e\uff0c\u4e5f\u4e0d\u8bb8\u518d \u6709\u4f8d\u536b\u5230\u4ed6\u7684\u4e07\u5bff\u5bab\u6765\u3002\u201d\u8c37\u4e2d\u83b2\u660e\u767d\u4e86\u51e0\u5206\uff0c\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5982\u6b64\u3002\u201d \u3000\u3000\u90a3\u5bab\u5a25\u53c8\u7b11\u8bf4\uff0c\u201c\u54b1\u4eec\u7684\u8001\u7956\u5b97\u53ef\u771f\u662f\u75bc\u4f60\u5462\uff0c\u53ef\u60dc\u4f60\u662f\u9a6c\u8428\u513f\u56fd\u7684\u516c\u4e3b\uff0c\u603b\u6709\u4e00 \u5929\u8981\u56de\u53bb\u7684\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u771f\u4e0d\u660e\u767d\u3002 \u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u8981\u5c06\u6211\u6551\u51fa\u51b7\u5bab\uff0c\u53c8\u5bf9\u6211\u5982\u6b64\u4e4b\u597d\uff1f\u201d\u90a3\u5bab\u5a25\u9053\uff1a\u201c\u8fd9\u5927\u7ea6\u662f\u7f18\u4efd\u5427\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u5230\u4e86\u592a\u4e0a\u7687\u7684\u5fa1\u4e66\u623f\uff0c\u90a3\u5bab\u5a25\u7980\u62a5\u9053\uff1a\u201c\u9a6c\u8428\u513f\u56fd\u516c\u4e3b\u83b2\u9a7e\u5df2\u5230\u3002\u201d \u592a\u4e0a\u7687\u6253\u5f00\u4e86\u623f\u95e8\uff0c\u5bf9\u90a3\u5bab\u5a25\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e5f\u53ef\u4ee5\u9000\u4e0b\u53bb\u4e86\u3002\u201d\u968f\u624b\u592b\u4e0a\u623f\u95e8\uff0c\u7b11\u9053\uff1a \u201c\u4eca\u5929\u6ca1\u6709\u6253\u6270\uff0c\u54b1\u4eec\u53ef\u4ee5\u597d\u597d\u4e00\u8c08\u4e86\u3002\u201d \u3000\u3000\u4e66\u623f\u9648\u8bbe\u96c5\u81f4\uff0c\u58c1\u4e0a\u6302\u6709\u4e00\u5e45\u753b\u56fe\uff0c\u770b\u6765\u6700\u5c11\u4e5f\u662f\u767e\u5e74\u4ee5\u4e0a\u7684\u53e4\u753b\u4e86\u3002\u753b\u4e2d\u662f\u4e2a\u5bab \u88c5\u7684\u4e2d\u5e74\u5973\u5b50\uff0c\u6e05\u4e3d\u7edd\u4fd7\uff0c\u6c14\u5ea6\u96cd\u5bb9\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u4e00\u8e0f\u8fdb\u4e66\u623f\u5c31\u7ed3\u8fd9\u5e45\u753b\u56fe\u5438\u5f15\u4e86\uff0c\u8fd9\u5012\u4e0d\u662f\u56e0\u4e3a\u753b\u4e2d\u5973\u5b50\u7684\u7f8e\u8c8c\uff0c\u800c\u662f\u56e0\u4e3a \u8d8a\u770b\u8d8a\u89c9\u5f97\u4f3c\u66fe\u76f8\u8bc6\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u6b63\u8981\u884c\u793c\uff0c\u592a\u4e0a\u7687\u6446\u4e86\u6446\u624b\uff0c\u7b11\u9053\uff1a\u201c\u4eca\u5929\u6ca1\u6709\u65c1\u4eba\uff0c\u54b1\u4eec\u7956\u5b59\u4fe9\u53ef\u4e0d\u5fc5\u5ba2 \u5957\u4e86\u3002\u6211\u8fd9\u4e00\u5927\u628a\u5e74\u7eaa\uff0c\u8db3\u591f\u505a\u4f60\u7684\u7237\u7237\uff0c\u4f60\u5c31\u53eb\u6211\u4e00\u58f0\u7237\u7237\u5427\u3002\u201d\u8c37\u4e2d\u83b2\u4f9d\u8a00\u53eb\u4e86\u4ed6 \u4e00\u58f0\u201c\u7237\u7237\u201d\uff0c\u4e50\u5f97\u90a3\u5927\u4e0a\u7687\u7709\u5f00\u773c\u7b11\uff0c \u3000\u3000\u592a\u4e0a\u7687\u7b11\u9053\uff1a\u201c\u4f60\u77a7\u8fd9\u753b\u4e2d\u4eba\u53ef\u6709\u51e0\u5206\u4f3c\u4f60\u4e48\uff1f\u201d\u8c37\u4e2d\u83b2\u731b\u7136\u4e00\u7701\uff0c\u5bfb\u601d\uff1a\u201c\u602a\u602a \u6211\u89c9\u5f97\u4f3c\u66fe\u76f8\u8bc6\u3002\u60f3\u6765\u60f3\u53bb\u3001\u5374\u60f3\u4e0d\u8d77\u662f\u548c\u6211\u54ea\u4e00\u4e2a\u8ba4\u8bc6\u7684\u4eba\u76f8\u4f3c\uff0c\u5374\u539f\u6765\u5c31\u662f\u50cf\u6211\u81ea \u5df1\u3002\u201d\u95ee\u9053\uff1a\u201c\u753b\u4e2d\u4e4b\u4eba\u662f\u8c01\uff1f\u201d\u592a\u4e0a\u7687\u9053\uff1a\u201c\u662f\u6211\u6bcd\u540e\u3002\u201d\u8c37\u4e2d\u83b2\u8fde\u5fd9\u606d\u606d\u656c\u656c\u7684\u884c \u4e86\u4e00\u793c\u3002 \u3000\u3000\u592a\u4e0a\u7687\u9053\uff1a\u201c\u6211\u5e7c\u5e74\u5931\u7236\uff0c\u662f\u6bcd\u540e\u629a\u517b\u6211\u6210\u4eba\u7684\u3002\u5979\u6559\u6211\u6cbb\u7406\u56fd\u4e8b\uff0c\u6559\u6211\u4e0e\u90bb\u90a6\u8981 \u548c\u7766\u53cb\u597d\uff0c\u5c24\u5176\u5bf9\u4f60\u4eec\u9a6c\u8428\u513f\u56fd\uff0c\u66f4\u8981\u89c6\u540c\u5144\u5f1f\u4e4b\u90a6\u3002\u6211\u5c0f\u65f6\u5019\u662f\u6bcd\u540e\u5782\u5e18\u542c\u653f\u7684\uff0c\u6211 \u5341\u4e5d\u5c81\u90a3\u5e74\uff0c\u59cb\u6b63\u5f0f\u52a0\u5195\u767b\u57fa\uff0c\u6211\u8fd8\u8bb0\u5f97\u6bcd\u540e\u8981\u6211\u4eb2\u624b\u5199\u7b2c\u4e00\u5c01\u56fd\u4e66\uff0c\u8fd9\u7b2c\u4e00\u5c01\u56fd\u4e66\u5c31 \u662f\u9001\u7ed9\u4f60\u4eec\u9a6c\u8428\u513f\u56fd\u56fd\u738b\u7684\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u7237\u7237\uff0c\u4f60\u770b\u662f\u5426\u5c31\u662f\u8fd9\u5f20\uff1f\u201d \u3000\u3000\u592a\u4e0a\u7687\u5c06\u90a3\u5f20\u56fd\u4e66\u63a5\u8fc7\uff0c\u5c55\u5f00\u4e00\u770b\uff0c\u867d\u662f\u8001\u773c\u660f\u82b1\uff0c\u5c1a\u4f9d\u7a00\u8ba4\u5f97\u5f53\u5e74\u7b14\u8ff9\uff0c\u4e0d\u7981\u53f9 \u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u6b63\u662f\u6211\u4eb2\u624b\u5199\u7684\u7b2c\u4e00\u5c01\u56fd\u4e66\uff0c\u90a3\u5e74\u6211\u5c1a\u672a\u8e0f\u5165\u5f31\u51a0\u4e4b\u5e74\uff0c\u4eca\u5e74\u6211\u5df1\u662f\u516b\u5341 \u6709\u4e5d\uff0c\u5c81\u6708\u60a0\u60a0\uff0c\u8f6c\u773c\u95f4\u5c31\u662f\u4e03\u5341\u5e74\u8fc7\u53bb\u4e86\uff01\u5947\u602a\uff0c\u8fd9\u5f20\u56fd\u4e66\uff0c\u5176\u5b9e\u4e0d\u8fc7\u662f\u901a\u544a\u65b0\u541b\u5373 \u4f4d\u7684\u4e00\u7eb8\u4f8b\u884c\u516c\u4e8b\uff0c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u4fdd\u5b58\u4e86\u4e03\u5341\u5e74\u4e4b\u4e45\uff0c\u8fd8\u672a\u629b\u5f03\uff1f\u83b2\u513f\uff0c\u4f60\u53c8\u4e3a\u4f55\u5c06\u5b83\u968f \u8eab\u5e26\u6765\uff1f\u4f60\u77e5\u9053\u8fd9\u662f\u6211\u5ba3\u544a\u767b\u4f4d\u7684\u56fd\u4e66\u4e48\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8fd9\u5f20\u56fd\u4e66\u662f\u6211\u4eec\u5728\u5b9d\u5e93\u4e4b\u4e2d\u53d1\u73b0\u7684\uff0c\u6211\u4e8b\u5148\u5e76\u4e0d\u77e5\u9053\u8fd9\u5c31\u662f\u7237\u7237\u4f60\u4eb2 \u7b14\u5199\u7684\u767b\u4f4d\u56fd\u4e66\uff0c\u4f46\u6211\u60f3\uff0c\u8fd9\u9002\u8db3\u8bc1\u660e\uff0c\u957f\u8fdc\u4ee5\u6765\uff0c\u6211\u4eec\u7684\u5217\u7956\u5217\u5b97\uff0c\u5c31\u662f\u5982\u4f55\u91cd\u89c6\u8d35 \u56fd\u7684\u53cb\u8c0a\uff0c\u56e0\u6b64\u6211\u5c31\u5c06\u5b83\u5e26\u6765\u4e86\u3002\u201d \u3000\u3000\u592a\u4e0a\u7687\u9053\uff1a\u201c\u4f60\u4eec\u5728\u5b9d\u5e93\u4e2d\u53d1\u73b0\u7684\uff1f\u5947\u602a\uff01\u55ef\uff0c\u4f60\u53ef\u4ee5\u7ed9\u6211\u8bf4\u8bf4\u5f53\u65f6\u662f\u600e\u6837\u53d1\u73b0\u7684 \u4e48\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u8fd9\u5f20\u56fd\u4e66\u662f\u653e\u5728\u4e00\u4e2a\u9996\u9970\u76d2\u91cc\u9762\u7684\uff0c\u8fd9\u9996\u9970\u76d2\u975e\u5e38\u666e\u901a\uff0c\u5806\u5728\u73cd\u5b9d \u4e4b\u4e2d\uff0c\u5c31\u7279\u522b\u60f9\u4eba\u6ce8\u76ee\uff0c\u6545\u800c\u6211\u4eec\u5c31\u6253\u5f00\u6765\u770b\u4e86\uff0c\u60f3\u4e0d\u5230\u91cc\u9762\u6709\u8fd9\u5f20\u56fd\u4e66\uff0c\u8fd8\u6709\u4e00\u4e9b\u522b \u7684\u4e1c\u897f\uff0c\u4e5f\u90fd\u662f\u5f88\u51fa\u4eba\u610f\u5916\u7684\u3002\u2026\u2026\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u592a\u4e0a\u7687\u5df2\u662f\u500f\u5730\u5f20\u5f00\u53cc\u773c\uff0c\u663e\u7136\u662f\u9887\u53d7\u9707\u52a8\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u8fd9\u9996\u9970\u76d2\u4f60 \u53ef\u6709\u5e26\u6765\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5c31\u5728\u6211\u7684\u8eab\u4e0a\uff0c\u592a\u4e0a\u7687\u9053\uff1a\u201c\u53ef\u4ee5\u8ba9\u6211\u770b\u770b\u4e48\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a \u201c\u6211\u6b63\u60f3\u8bf7\u6559\u7237\u7237\u3002\u201d\u5f53\u4e0b\u638f\u51fa\u90a3\u53ea\u9996\u9970\u76d2\u5b50\uff0c\u53cc\u624b\u5949\u4e0a\u3002 \u3000\u3000\u592a\u4e0a\u7687\u5c1a\u672a\u6253\u5f00\u76d2\u5b50\uff0c\u5c31\u53e0\u58f0\u8bf4\u9053\uff1a\u201c\u5947\u602a\uff0c\u5947\u602a\uff01\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u600e\u4e48\u5947\u602a\uff1f\u201d \u592a\u4e0a\u7687\u4e0d\u8a00\u4e0b\u8bed\uff0c\u9ed8\u60f3\u4e00\u4f1a\uff0c\u5ffd\u5730\u8d70\u8fc7\u53bb\u62c9\u5f00\u4e00\u53ea\u62bd\u5c55\uff0c\u62ff\u51fa\u53e6\u4e00\u53ea\u76d2\u5b50\u6765\uff0c\u9053\uff1a\u201c\u4f60 \u77a7\u662f\u4e0d\u662f\u4e00\u6a21\u4e00\u6837\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5440\uff0c\u679c\u7136\u662f\u4e00\u6a21\u4e00\u6837\u3002\u7237\u7237\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e5f\u6536\u85cf\u8fd9\u6837\u7684\u4e00\u53ea\u666e\u666e\u901a\u901a\u7684 \u6c11\u95f4\u9996\u9970\u76d2\u5b50\uff1f\u201d\u592a\u4e0a\u7687\u9053\uff1a\u201c\u8fd9\u662f\u6211\u6bcd\u540e\u7684\u9057\u7269\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u4e2d\u7591\u4e91\u9635\u9635\u3002\u592a\u4e0a\u7687\u5c06\u76d2\u5b50\u6253\u5f00\uff0c\u9996\u5148\u5c06\u90a3\u628a\u68b3\u5b50\u548c\u90a3\u9762\u955c\u5b50 \u62ff\u4e86\u51fa\u6765\uff0c\u68b3\u5b50\u662f\u6728\u5934\u505a\u7684\uff0c\u955c\u5b50\u662f\u4e2a\u94dc\u505a\u7684\uff0c\u5df2\u7ecf\u9eef\u7136\u65e0\u5149\u4e86\u3002\u8fd9\u79cd\u68b3\u5b50\u955c\u5b50\u90fd\u662f\u666e \u901a\u4eba\u5bb6\u5987\u5973\u7684\u7528\u7269\uff0c\u4e00\u70b9\u4e5f\u4e0d\u7a00\u5947\u3002\u592a\u4e0a\u7687\u62ff\u5728\u624b\u4e0a\uff0c\u5374\u6014\u6014\u7684\u51fa\u4e86\u795e\uff0c\u534a\u664c\u8bf4\u9053\uff1a \u201c\u6211\u8bb0\u5f97\u5c0f\u65f6\u5019\u6211\u8ddf\u5728\u6bcd\u540e\u8eab\u65c1\uff0c\u770b\u5979\u68b3\u5986\uff0c\u6bcd\u540e\u7528\u7684\u5c31\u662f\u8fd9\u79cd\u6c11\u95f4\u60ef\u7528\u7684\u68b3\u5b50\u548c\u955c\u5b50\u3002 \u6211\u53ea\u9053\u6bcd\u540e\u662f\u6c11\u95f4\u6765\u7684\uff0c\u7528\u60ef\u4e86\u7684\u4e1c\u897f\u5c31\u4e0d\u60f3\u66f4\u6362\u2026\u2026\u54b3\uff0c\u73b0\u5728\u60f3\u6765\u2026\u2026\u201d\u4ed6\u201c\u73b0\u5728\u201d \u60f3\u7684\u662f\u4ec0\u4e48\uff0c\u4ed6\u53ef\u6ca1\u6709\u5f80\u4e0b\u518d\u8bf4\uff0c\u4f46\u8c37\u4e2d\u83b2\u5df2\u53ef\u4ee5\u731c\u60f3\u5f97\u5230\uff1a\u592a\u4e0a\u7687\u7684\u5fc3\u4e0a\u4ea6\u5df2\u6709\u4e86\u7591 \u56e2\u3002 \u3000\u3000\u53ea\u89c1\u592a\u4e0a\u7687\u53c8\u62ff\u8d77\u4e86\u90a3\u4e24\u5f20\u4fe1\u7b3a\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u4eba\u7684\u4fe1\uff0c\u53ef\u4ee5\u770b\u5417\uff1f\u201d\u8c37\u4e2d\u83b2 \u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u5199\u4fe1\u7684\u4eba\u662f\u8c01\uff1f\u542c\u54e5\u54e5\u8bf4\uff0c\u4f3c\u4e4e\u662f\u4e00\u4e2a\u5973\u5b50\u5199\u7684\u60c5\u4e66\u3002\u201d \u3000\u3000\u592a\u4e0a\u7687\u624b\u6307\u5fae\u5fae\u98a4\u6296\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u662f\u5c06\u8fd1\u4e5d\u5341\u5c81\u7684\u8001\u4eba\u4e86\uff0c\u8fd9\u60c5\u4e66\u4e48\u2026\u2026\u201d\u4ed6\u672c \u6765\u8981\u8bf4\u7684\u662f\u201c\u4e0d\u770b\u4e5f\u7f62\u201d\uff0c\u8fd9\u56db\u4e2a\u5b57\u5c1a\u672a\u51fa\u53e3\uff0c\u8c37\u4e2d\u83b2\u5df2\u662f\u201c\u683c\u683c\u201d\u4e00\u7b11\uff0c\u6253\u65ad\u4ed6\u7684\u8bdd \u9053\uff1a\u201c\u770b\u4e5f\u65e0\u59a8\u3002\u201d\u592a\u4e0a\u7687\u542c\u5f97\u5979\u8fd9\u4e48\u8bf4\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u8fdf\u7591\u4e86\u4e00\u4f1a\uff0c\u7ec8\u4e8e\u6162\u6162\u5c55\u5f00\u4e86\u4fe1 \u7b3a\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u624b\u6307\u98a4\u6296\u5f97\u66f4\u4e3a\u5389\u5bb3\uff0c\u51e0\u4e4e\u8fde\u90a3\u5f20\u8584\u8584\u7684\u4fe1\u7b3a\u4e5f\u62ff\u634f\u4e0d\u7262\uff0c\u8bfb\u5b8c\u4e86\u8fd9\u4e24\u5c01\u4fe1\uff0c \u4e00\u6ef4\u6676\u83b9\u7684\u6cea\u73e0\u4ece\u4ed6\u5e72\u67af\u7684\u8001\u773c\u4e2d\u6ef4\u4e86\u4e0b\u6765\u3002\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u7237\u7237\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u592a\u4e0a\u7687 \u5401\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e00\u53ea\u811a\u5df2\u7ecf\u5feb\u8981\u8de8\u8fdb\u575f\u5893\u4e86\uff0c\u76f4\u5230\u4eca\u5929\u6211\u624d\u77e5\u9053\u81ea\u5df1\u7684\u6765\u5386\u3002\u83b2 \u513f\uff0c\u591a\u8c22\u4f60\u7ed9\u6211\u63ed\u5f00\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u6211\u4e0d\u662f\u96be\u8fc7\uff0c\u6211\u662f\u9ad8\u5174\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u60ca\u7591\u4e0d\u5b9a\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u7237\u7237\uff0c\u8fd9\u4e24\u5c01\u4fe1\u8bf4\u7684\u662f\u4ec0\u4e48\uff1f\u201d\u592a\u4e0a\u7687\u9053\uff1a\u201c\u83b2\u513f\uff0c \u4f60\u8fc7\u6765\u3002\u201d\u58f0\u97f3\u5145\u6ee1\u6148\u7231\u3002\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u7237\u7237\uff0c\u4f60\u522b\u54ed\u5566\uff0c\u6211\u77a7\u7740\u96be\u8fc7\u3002\u201d\u4e3e\u8d77\u8863\u8896\uff0c \u7ed9\u4ed6\u62b9\u53bb\u4e86\u8138\u4e0a\u7684\u6cea\u75d5\u3002 \u3000\u3000\u592a\u4e0a\u7687\u5c06\u8c37\u4e2d\u83b2\u8f7b\u8f7b\u6402\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u83b2\u513f\u3002\u54b1\u4eec\u5f53\u771f\u662f\u4e00\u5bb6\u4eba\uff0c\u4f60\u60f3\u4e0d\u5230\u5427\uff1f\u201d\u8c37 \u4e2d\u83b2\u5446\u5446\u5730\u671b\u7740\u592a\u4e0a\u7687\uff0c\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\u3002\u592a\u4e0a\u7687\u9053\uff1a\u201c\u8fd9\u4e24\u5c01\u4fe1\u662f\u6211\u6bcd\u540e\u5199 \u7684\uff0c\u6536\u4fe1\u8fd9\u4e2a\u7537\u5b50\u662f\u4f60\u66fe\u7956\uff0c\u6211\u548c\u4f60\u7684\u7956\u7236\u662f\u540c\u7236\u5f02\u6bcd\u5144\u5f1f\uff0c\u4f60\u660e\u767d\u4e86\u4e48\uff1f\u201d \u3000\u3000\u592a\u4e0a\u7687\u53c8\u9053\uff1a\u201c\u73b0\u5728\u6211\u624d\u660e\u767d\uff0c\u602a\u4e0d\u5f97\u6bcd\u540e\u5c61\u6b21\u53ee\u549b\u5631\u5490\uff0c\u8981\u54b1\u4eec\u4e24\u56fd\u4e16\u4ee3\u548c\u597d\uff0c \u539f\u6765\u54b1\u4eec\u771f\u6b63\u662f\u5ae1\u4eb2\u7684\u5144\u5f1f\u4e4b\u90a6\u3002\u201d\u8c37\u4e2d\u83b2\u8fd9\u4e5f\u624d\u660e\u767d\uff0c\u5728\u7b2c\u4e8c\u5c01\uff0c\u90a3\u5973\u5b50\u8bf4\u5979\u5df2\u517b\u4e86 \u4e00\u4e2a\u513f\u5b50\uff0c\u8981\u5979\u60c5\u90ce\u5207\u4e0d\u53ef\u4e0e\u513f\u5b50\u5728\u7586\u573a\u76f8\u89c1\uff0c\u539f\u6765\u8fd9\u4e2a\u513f\u5b50\u5c31\u662f\u773c\u524d\u7684\u8fd9\u4e2a\u201c\u8001\u7237\u7237\u201d\uff0c \u800c\u5979\u7684\u60c5\u90ce\u5c31\u662f\u81ea\u5df1\u7684\u66fe\u7956\uff0c\u4e5f\u5c31\u662f\u76ee\u524d\u6606\u5e03\u5170\u56fd\u592a\u4e0a\u7687\u7684\u751f\u8eab\u4e4b\u7236\u3002\u90a3\u5973\u5b50\u662f\u4e0d\u8bb8\u4ed6 \u4eec\u7236\u5b50\u4ea4\u5175\u3002 \u3000\u3000\u4f46\u8c37\u4e2d\u83b2\u8fd8\u6709\u8bb8\u591a\u4e0d\u660e\u767d\u7684\u5730\u65b9\uff0c\u95f4\u9053\uff1a\u201c\u7237\u7237\u3002\u4f60\u6bcd\u540e\u5f53\u5e74\u65e2\u662f\u4e0e\u6211\u66fe\u7956\u76f8\u7231\uff0c \u4e3a\u4f55\u5979\u4e0d\u505a\u9a6c\u8428\u513f\u56fd\u7684\u7687\u540e\uff0c\u5374\u505a\u4e86\u6606\u5e03\u5170\u56fd\u7684\u7687\u540e\uff1f\u201d \u3000\u3000\u592a\u4e0a\u7687\u9053\uff1a\u201c\u8be6\u60c5\u6211\u4e5f\u4e0d\u5f88\u6e05\u695a\uff0c\u4f46\u6211\u5374\u77e5\u9053\u5f53\u5e74\u5386\u53f2\uff0c\u591a\u5c11\u53ef\u4ee5\u63a8\u60f3\u5f97\u5230\u4e00\u4e9b\u5b9e \u60c5\uff0c\u8fd9\u662f\u8ddd\u4eca\u767e\u5e74\u6709\u591a\u7684\u4e8b\u60c5\u4e86\uff0c\u90a3\u65f6\u5929\u7afa\u7684\u86ee\u65cf\u4eba\u4fb5\uff0c\u8d35\u6211\u4e24\u56fd\uff0c\u540c\u53d7\u5176\u5bb3\u3002\u9a6c\u8428\u513f \u56fd\u9886\u571f\u5168\u88ab\u4fb5\u5360\uff0c\u6606\u5e03\u5170\u56fd\u7687\u57ce\u4e5f\u53d7\u56f4\u653b\uff0c\u4f46\u60c5\u5f62\u7a0d\u5fae\u597d\u4e9b\uff0c\u7687\u57ce\u88ab\u653b\u4e09\u5e74\uff0c\u5c1a\u672a\u653b\u4e0b\u3002 \u6211\u5916\u7956\u7236\u662f\u6606\u5e03\u5170\u56fd\u7684\u4e00\u4e2a\u9690\u58eb\uff0c\u6587\u6b66\u5168\u624d\uff0c\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u738b\u9003\u5230\u6606\u5e03\u5170\u56fd\u907f\u96be\uff0c\u4ed6\u77e5 \u9053\u6211\u5916\u7956\u7236\u662f\u4e2a\u9ad8\u4eba\uff0c\u9042\u9690\u59d3\u57cb\u540d\uff0c\u627e\u5230\u6211\u5916\u7956\u7236\u5bb6\u4e2d\uff0c\u62dc\u4ed6\u529b\u5e08\u3002\u6211\u63a8\u60f3\u6211\u7684\u7236\u6bcd\u5c31 \u662f\u90a3\u65f6\u76f8\u597d\u7684\u3002\u540e\u6765\u9a6c\u8428\u513f\u56fd\u767e\u59d3\u4e0d\u582a\u5f02\u65cf\u7684\u82db\u653f\uff0c\u7fa4\u8d77\u53cd\u6297\uff0c\u5929\u7afa\u86ee\u65cf\u53c8\u56e0\u56f4\u653b\u6606\u5e03 \u5170\u56fd\u7687\u57ce\uff0c\u4e45\u653b\u4e0d\u4e0b\uff0c\u58eb\u6c14\u5df2\u8870\uff0c\u6606\u5e03\u5170\u56fd\u7684\u52e4\u738b\u4e4b\u5e08\u4e0e\u9a6c\u8428\u513f\u56fd\u7684\u4e49\u519b\u4f1a\u5408\uff0c\u7ec8\u4e8e\u5c06 \u86ee\u65cf\u9a71\u9010\u51fa\u53bb\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u7684\u66fe\u7956\u5728\u90a3\u9690\u58eb\u5bb6\u4e2d\u907f\u96be\uff0c\u4e00\u76f4\u6ca1\u6709\u8868\u660e\u4ed6\u7684\u8eab\u4efd\u5417\uff1f\u201d\u5927\u4e0a\u7687\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u76f4\u5230\u9a6c\u8428\u513f\u56fd\u7684\u4e49\u519b\u8d77\u6765\u4e4b\u540e\uff0c\u627e\u5230\u90a3\u9690\u58eb\u7684\u5bb6\u4e2d\uff0c\u8fce\u63a5\u4ed6\u4eec\u7684\u56fd\u738b\uff0c\u5984\u4ed6\u4eec \u7684\u56fd\u738b\u7edf\u7387\u4ed6\u4eec\u548c\u654c\u4eba\u4f5c\u6218\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u624d\u4f20\u51fa\u53bb\u3002\u4e0d\u8fc7\uff0c\u5728\u6b64\u4e4b\u524d\uff0c\u6211\u60f3\u6211\u7684\u6bcd\u540e\u5927\u7ea6 \u662f\u65e9\u5df2\u77e5\u9053\u7684\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u540e\u6765\u4f60\u7684\u6bcd\u540e\u600e\u4e48\u53c8\u5ac1\u4e86\u6606\u5e03\u5170\u56fd\u7684\u56fd\u738b\uff1f\u201d\u592a\u4e0a\u7687\u9053\uff1a\u201c\u6218\u4e8b\u8fc7\u540e\uff0c \u6606\u5e03\u5170\u56fd\u56fd\u738b\u4ef0\u6155\u90a3\u9690\u58eb\u7684\u5973\u513f\uff0c\u6d3e\u9063\u4f7f\u8005\u6c42\u5a5a\uff0c\u8fd9\u5a5a\u4e8b\u5c31\u5b9a\u4e0b\u6765\u4e86\u3002\u5176\u65f6\u9a6c\u8428\u513f\u56fd\u7684 \u56fd\u738b\u5df2\u7ecf\u56de\u56fd\uff0c\u6b63\u5fd9\u4e8e\u6218\u540e\u7684\u6062\u590d\u5de5\u4f5c\u3002\u4f9d\u6211\u63a8\u60f3\uff0c\u6211\u6bcd\u4eb2\u4e4b\u6240\u4ee5\u7b54\u5e94\u8fd9\u4ef6\u5a5a\u4e8b\u3002\u4e00\u6765 \u662f\u56e0\u4e3a\u6c42\u5a5a\u7684\u662f\u672c\u56fd\u7684\u56fd\u738b\uff0c\u4e8c\u6765\u4e0d\u613f\u56e0\u6b64\u800c\u9020\u6210\u4e24\u56fd\u7684\u4e0d\u548c\u3002\u8fd9\u6b21\u4e24\u56fd\u5171\u540c\u6297\u654c\uff0c\u9a6c \u8428\u513f\u56fd\u7684\u56fd\u738b\u4e5f\u4e0d\u613f\u7834\u574f\u4e0e\u6606\u5e03\u5170\u56fd\u56fd\u738b\u5728\u6218\u4e89\u4e2d\u7ed3\u6210\u7684\u53cb\u8c0a\uff0c\u6545\u800c\u4e5f\u5c31\u53ea\u597d\u628a\u8fd9\u79d8\u5bc6 \u7684\u604b\u60c5\uff0c\u6c38\u8fdc\u957f\u57cb\u5fc3\u5e95\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4ece\u90a3\u4e24\u5c01\u4fe1\u770b\u6765\uff0c\u4ed6\u4eec\u4e24\u4eba\u53ef\u90fd\u662f\u5f88\u75db\u82e6\u554a\uff01\u201d\u5927\u4e0a\u7687 \u9053\uff0c\u201c\u6211\u6bcd\u540e\u957f\u5e74\u6291\u90c1\u4e0d\u6b22\uff0c\u53ea\u6709\u6211\u5728\u5979\u7684\u9762\u524d\u9017\u4ed6\u9ad8\u5174\u7684\u65f6\u5019\uff0c\u5979\u624d\u5076\u5c14\u9732\u51fa\u7b11\u5bb9\u3002 \u8fd9\u79d8\u5bc6\u6211\u73b0\u5728\u65b9\u59cb\u660e\u767d\u3002\u6211\u505a\u4e86\u516d\u5341\u5e74\u7687\u5e1d\uff0c\u5728\u6606\u5e03\u5170\u56fd\u7684\u5386\u4ee3\u7687\u5e1d\u4e4b\u4e2d\uff0c\u6211\u662f\u5728\u4f4d\u6700 \u957f\u7684\u4e00\u4f4d\u541b\u7687\uff0c\u4f46\u6211\u4e5f\u662f\u6700\u75db\u82e6\u7684\u4e00\u4f4d\u541b\u7687\u3002\u6211\u6bcd\u540e\u5165\u5bab\u4e4b\u540e\uff0c\u672a\u6ee1\u4e03\u4e2a\u6708\u5c31\u751f\u4e0b\u4e86\u6211\u3002 \u5f53\u65f6\u5df1\u6709\u4e00\u4e9b\u98ce\u8a00\u98ce\u8bed\uff0c\u6307\u6bcd\u540e\u4e0d\u8d1e\uff0c\u8bf4\u6211\u4e0d\u662f\u56fd\u738b\u7684\u4eb2\u751f\u9aa8\u8089\u3002 \u3000\u3000\u201c\u540e\u6765\u6211\u505a\u4e86\u7687\u5e1d\uff0c\u6700\u521d\u90a3\u51e0\u5e74\u8fd8\u6709\u738b\u5ba4\u5b97\u65cf\u60f3\u628a\u6211\u8d76\u8d70\u5462\u3002\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u5bf9\u81ea\u5df1 \u7684\u6765\u5386\u4e5f\u66fe\u7ecf\u6709\u8fc7\u7591\u5fc3\uff0c\u4e0d\u8fc7\u76f4\u5230\u4eca\u5929\uff0c\u6211\u65b9\u624d\u5b8c\u5168\u660e\u767d\u4e86\u3002\u4e0d\u8fc7\uff0c\u6211\u4e00\u70b9\u4e5f\u6ca1\u6709\u6028\u6068 \u6211\u7684\u6bcd\u540e\uff0c\u5373\u4f7f\u5728\u6211\u7591\u5fc3\u7684\u65f6\u5019\uff0c\u6211\u4e5f\u8fd8\u662f\u540c\u60c5\u5979\uff0c\u53ef\u601c\u5979\u7684\u3002\u5979\u6bd4\u6211\u53d7\u7684\u75db\u82e6\u66f4\u6df1\uff0c \u5979\u4e3a\u4e86\u4e24\u56fd\u56fd\u4ea4\uff0c\u820d\u5f03\u4e86\u5fc3\u4e0a\u4eba\uff0c\u727a\u7272\u4e86\u81ea\u5df1\u7684\u5e78\u798f\uff0c\u53c8\u4e3a\u6211\u542b\u8f9b\u8339\u82e6\uff0c\u629a\u517b\u6211\u6210\u4eba\uff0c \u5e2e\u52a9\u6211\u6cbb\u7406\u56fd\u5bb6\uff0c\u53c8\u8981\u5fcd\u53d7\u5185\u5185\u5916\u5916\u654c\u4eba\u7684\u6d41\u8a00\u871a\u8bed\u2026\u2026\u5509\uff0c\u548c\u5979\u6bd4\u8d77\u6765\uff0c\u6211\u6240\u53d7\u7684\u75db \u82e6\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u4e0d\u7981\u70ed\u6cea\u76c8\u7736\uff0c\u53eb\u9053\uff1a\u201c\u7237\u7237\uff0c\u6bd4\u8d77\u4f60\u6765\uff0c\u6211\u7684\u75db\u82e6\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u4e86\u3002\u201d\u8fd9 \u56de\u8f6e\u5230\u592a\u4e0a\u7687\u7528\u8863\u8896\u7ed9\u8c37\u4e2d\u83b2\u8f7b\u8f7b\u62b9\u53bb\u4e86\u6cea\u75d5\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5728\u8fd9\u4e9b\u90fd\u8fc7\u53bb\u4e86\uff0c\u6211\u521a\u624d\u7ed9 \u4f60\u6240\u8bf4\u7684\u6545\u4e8b\uff0c\u5728\u6606\u5e03\u5170\u56fd\u4e5f\u65e9\u5df2\u6ca1\u6709\u51e0\u4e2a\u4eba\u77e5\u9053\u4e86\uff0c\u83b2\u513f\uff0c\u6211\u73b0\u5728\u6240\u96be\u8fc7\u7684\uff0c\u53ea\u662f\u4f60 \u6240\u53d7\u7684\u59d4\u5c48\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u53d7\u7684\u59d4\u5c48\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u4e0d\u8fc7\uff0c\u6211\u60f3\u6211\u4eec\u4e24\u56fd\u5728\u767e\u5e74\u4e4b\u524d\uff0c\u66fe\u7ecf\u5171\u540c \u5fa1\u654c\uff0c\u600e\u80fd\u5728\u4eca\u65e5\u5374\u53d8\u4e3a\u654c\u4eba\uff1f\u7237\u7237\uff0c\u4f60\u8981\u60f3\u6cd5\u5316\u89e3\u624d\u597d\u3002\u201d\u592a\u4e0a\u7687\u9053\uff1a\u201c\u8bdd\u8bf4\u5f97\u5bf9\u554a\uff0c \u53ef\u662f\u6211\u6709\u4e00\u4ef6\u4e8b\u60c5\u5f88\u4e0d\u660e\u767d\uff0c\u4f60\u8bf4\u4f60\u4eec\u9a6c\u8428\u513f\u56fd\u975e\u5e38\u91cd\u89c6\u6211\u56fd\u7684\u60c5\u8c0a\uff0c\u6211\u4e5f\u76f8\u4fe1\u4f60\u7684\u8bf4 \u8bdd\uff0c\u4f46\u4f60\u4eec\u4e3a\u4ec0\u4e48\u6740\u4e86\u6211\u56fd\u7684\u4f7f\u8005\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u7237\u7237\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u6211\u65e9\u5c31\u60f3\u548c\u4f60\u8bf4\u4e86\uff0c\u4e0d\u9519\uff0c\u8fd9\u4f7f\u8005\u662f\u6211\u54e5\u54e5\u6240\u6740\u7684\uff0c \u4f46\u5728\u6211\u54e5\u54e5\u52a8\u624b\u7684\u65f6\u5019\uff0c\u5374\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u4ed6\u5c31\u662f\u8d35\u56fd\u7684\u4f7f\u8005\uff0c\u8fd9\u662f\u4e00\u4ef6\u975e\u5e38\u610f\u5916\u7684\u4e8b\u60c5\uff01\u2026\u2026\u201d \u3000\u3000\u521a\u521a\u8981\u8bf4\u5230\u6b63\u6587\uff0c\u5ffd\u542c\u5f97\u95e8\u73af\u54cd\u4e86\u4e09\u4e0b\uff0c\u592a\u4e0a\u7687\u559d\u9053\uff1a\u201c\u8c01\uff1f\u201d\u95e8\u5916\u7684\u5bab\u5a25\u7980\u62a5\u9053\uff1a \u201c\u7687\u4e0a\u6765\u95ee\u5019\u8001\u7956\u5b97\u3002\u201d\u592a\u4e0a\u7687\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u5c31\u8ba9\u4ed6\u77e5\u9053\u5427\uff0c\u53eb\u4ed6 \u8fdb\u6765\u3002\u201d\u56de\u5934\u5bf9\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u6682\u4e14\u5230\u90a3\u8fb9\u53a2\u623f\u56de\u907f\u4e00\u4e0b\uff0c\u4e14\u542c\u4ed6\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u56fd\u738b\u8d70\u4e86\u8fdb\u6765\uff0c\u8bf7\u4e86\u4e2a\u5b89\uff0c\u8bf4\u9053\uff1a\u201c\u7237\u7237\uff0c\u4f60\u597d\u3002\u201d\u592a\u4e0a\u7687\u9053\uff1a\u201c\u6211\u6ca1 \u6709\u4ec0\u4e48\u4e0d\u597d\u7684\uff0c\u4f60\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u56fd\u738b\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u7237\u7237\u5c06\u90a3\u4e24\u4e2a\u4f8d\u536b\u8d76\u51fa\u53bb\u4e86\uff1f\u201d\u592a\u4e0a \u7687\u9053\uff1a\u201c\u6211\u628a\u7687\u4f4d\u7ed9\u4e86\u4f60\uff0c\u56fe\u7684\u5c31\u662f\u4eab\u4e2a\u6e05\u798f\uff0c\u4f60\u5374\u4e0d\u80af\u8ba9\u6211\u6e05\u6e05\u9759\u9759\u8fc7\u4e00\u8fc7\u5b89\u95f2\u7684\u65e5 \u5b50\u4e48\uff1f\u201d \u3000\u3000\u56fd\u738b\u8bf4\u9053\uff1a\u201c\u6211\u6d3e\u90a3\u4e24\u4e2a\u4eba\u6765\u4f3a\u5019\u4f60\u8001\u4eba\u5bb6\uff0c\u6b63\u662f\u4e3a\u4e86\u8981\u4fdd\u62a4\u4f60\u554a\u3002\u201d\u592a\u4e0a\u7687\u9053\uff1a \u201c\u6211\u5df1\u662f\u5e74\u5c06\u5c31\u6728\u7684\u8001\u4eba\u4e86\uff0c\u8fd8\u6709\u8c01\u4f24\u5bb3\u6211\u4e0d\u6210\uff1f\u6211\u4f4f\u5728\u81ea\u5df1\u7684\u5bab\u4e2d\uff0c\u4f55\u9700\u7279\u522b\u4fdd\u62a4\uff1f \u9000\u4f4d\u4e4b\u540e\u8fd9\u5341\u5e74\u6765\uff0c\u6211\u4e5f\u6ca1\u7528\u8fc7\u4f8d\u536b\u3002\u201d \u3000\u3000\u56fd\u738b\u8de8\u4e0a\u4e00\u6b65\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7237\u7237\uff0c\u4eca\u65f6\u4e0d\u540c\u5f80\u65e5\uff0c\u54b1\u4eec\u548c\u9a6c\u8428\u513f\u56fd\u4ea4\u6076\uff0c\u542c\u8bf4\u4ed6 \u4eec\u7684\u56fd\u738b\u662f\u4e2a\u672c\u9886\u975e\u5e38\u9ad8\u5f3a\u7684\u4eba\uff0c\u624b\u4e0b\u80fd\u4eba\u53c8\u591a\uff0c\u6015\u2026\u2026\u201d\u592a\u4e0a\u7687\u9053\uff1a\u201c\u4f60\u662f\u6015\u9a6c\u8428\u513f \u56fd\u6d3e\u523a\u5ba2\u6765\u8c0b\u5bb3\u6211\uff1f\u201d \u3000\u3000\u56fd\u738b\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u90a3\u4f4d\u9a6c\u8428\u513f\u56fd\u7684\u516c\u4e3b\uff0c\u7237\u7237\uff0c\u4f60\u4e5f\u4e0d\u5b9c\u8fc7\u4efd\u548c\u5979\u4eb2\u8fd1\u3002\u201d\u5927\u4e0a\u7687 \u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u8fd8\u8981\u7ba1\u8d77\u6211\u6765\u4e86\uff1f\u201d\u56fd\u738b\u5782\u624b\u8bf4\u9053\uff1a\u201c\u5b59\u513f\u4e0d\u6562\u3002\u4f46\u8fd9\u4f4d\u59d1\u5a18\uff0c\u6bd5\u7adf\u662f \u54b1\u4eec\u654c\u56fd\u7684\u516c\u4e3b\uff0c\u54b1\u4eec\u5c31\u8981\u548c\u9a6c\u8428\u513f\u56fd\u5f00\u6218\u4e86\uff0c\u8ba9\u4e00\u4e2a\u654c\u56fd\u7684\u516c\u4e3b\u5e38\u5e38\u5728\u4f60\u7684\u8eab\u8fb9\uff0c\u5b59 \u513f\u603b\u662f\u62c5\u5fc3\uff0c\u201d \u3000\u3000\u592a\u4e0a\u7687\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u5c31\u8981\u548c\u9a6c\u8428\u513f\u56fd\u5f00\u6218\u4e86\uff1f\u201d\u56fd\u738b\u8bf4\u9053\uff0c\u201c\u6211\u5df2\u4e0b\u4e86\u547d\u4ee4\uff0c\u4e09 \u519b\u5f85\u53d1\uff0c\u5c31\u5728\u8fd9\u51e0\u5929\u4e4b\u5185\uff0c\u4fbf\u8981\u6740\u8fc7\u5c71\u5357\u3002\u201d \u3000\u3000\u592a\u4e0a\u7687\u52c3\u7136\u53d8\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5373\u4f4d\u4e4b\u65f6\uff0c\u5728\u6211\u624b\u4e2d\u63a5\u8fc7\u7389\u73ba\uff0c\u6211\u5bf9\u4f60\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff0c \u4f60\u5168\u5fd8\u4e86\uff1f\u201d\u56fd\u738b\u8bf4\u9053\uff1a\u201c\u5b59\u513f\u672a\u66fe\u5fd8\u8bb0\uff0c\u53ea\u662f\u5982\u4eca\u5df2\u662f\u7bad\u5728\u5f26\u4e0a\uff0c\u548c\u9a6c\u8428\u513f\u56fd\u8fd9\u573a\u5e72 \u6208\uff0c\u662f\u51b3\u8ba1\u4e0d\u80fd\u907f\u514d\u7684\u4e86\u3002\u90a3\u4f4d\u516c\u4e3b\uff0c\u8bf7\u7237\u7237\u8fd8\u662f\u4ea4\u7ed9\u6211\u5427\u3002\u201d \u3000\u3000\u592a\u4e0a\u7687\u9053\uff1a\u201c\u5b59\u513f\uff0c\u4f60\u53ef\u77e5\u9053\u8fd9\u4f4d\u9a6c\u8428\u513f\u56fd\u7684\u516c\u4e3b\u662f\u4f60\u7684\u4ec0\u4e48\u4eba\uff1f\u201d\u56fd\u738b\u6014\u4e86\u4e00\u6014\uff0c \u8bf4\u9053\uff1a\u201c\u7237\u7237\uff0c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u5979\u4e0d\u662f\u6211\u7684\u654c\u4eba\u4e48\uff1f\u201d \u3000\u3000\u5927\u4e0a\u7687\u9053\uff1a\u201c\u5979\u662f\u548c\u4f60\u540c\u4e00\u66fe\u7956\u7684\u5802\u59b9\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u592a\u4e0a\u7687\u4ee5\u4e3a\u4ed6\u7684\u5b59\u513f\u5b9a\u7136\u60ca \u6115\u65e0\u6bd4\uff0c\u54ea\u77e5\u56fd\u738b\u867d\u7136\u662f\u9732\u51fa\u4e86\u8be7\u5f02\u7684\u795e\u60c5\uff0c\u5374\u4e5f\u4e0d\u77e5\u4ed6\u7956\u7236\u6240\u60f3\u8c61\u7684\u90a3\u6837\u60ca\u6115\uff0c\u53ea\u89c1 \u4ed6\u8d70\u5230\u7a97\u53e3\u4e00\u671b\uff0c\u968f\u5373\u7d27\u95ed\u7a97\u6237\uff0c\u5401\u4e86\u53e3\u6c14\uff0c\u6084\u6084\u8bf4\u9053\uff1a\u201c\u7237\u7237\uff0c\u5c0f\u58f0\u4e00\u70b9\u3002\u8fd9\u662f\u771f\u7684 \u4e48\uff1f\u4f60\u4e3a\u4ec0\u4e48\u4e00\u76f4\u6ca1\u6709\u548c\u6211\u8bf4\uff1f\u201d \u3000\u3000\u592a\u4e0a\u7687\u9053\uff1a\u201c\u6211\u4e5f\u662f\u76f4\u5230\u4eca\u5929\u65b9\u59cb\u77e5\u9053\uff0c\u4f60\u77a7\uff0c\u8fd9\u662f\u4f60\u66fe\u7956\u6bcd\u5f53\u5e74\u5199\u7ed9\u9a6c\u8428\u513f\u56fd\u738b \u9636\u4e24\u5c01\u4fe1\uff0c\u90a3\u4f4d\u56fd\u738b\u5b9e\u5728\u5c31\u662f\u6211\u751f\u8eab\u4e4b\u7236\uff0c\u4e5f\u5c31\u662f\u4f60\u771f\u6b63\u7684\u66fe\u7956\u7236\u4e86\u3002\u201d\u56fd\u738b\u63a5\u8fc7\u4e86\u90a3 \u4e24\u5c01\u4fe1\uff0c\u5ffd\u5730\u64e6\u71c3\u706b\u77f3\u3002\u4e00\u628a\u706b\u70e7\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u8981\u770b\uff0c\u8fd9\u4e24\u5c01\u4fe1\u4e5f\u4e0d\u5e94\u7559\u5728\u4eba\u95f4\uff01\u201d \u3000\u3000\u592a\u4e0a\u7687\u9053\uff1a\u201c\u9a6c\u8428\u513f\u56fd\u73b0\u5728\u7684\u56fd\u738b\u662f\u4f60\u7684\u5ae1\u4eb2\u5802\u5f1f\uff0c\u4f60\u4e0d\u80af\u5f52\u5b97\u8ba4\u4eb2\u4e48\uff1f\u201d\u56fd\u738b\u9053\uff1a \u201c\u7237\u7237\uff0c\u8fd9\u4e24\u5c01\u4fe1\u662f\u4e0d\u662f\u90a3\u4f4d\u516c\u4e3b\u7ed9\u4f60\u7684\uff1f\u201d\u592a\u4e0a\u7687\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f60\u610f\u6b32\u5982\u4f55\uff1f\u201d\u56fd\u738b \u9053\uff1a\u201c\u9664\u4e86\u5979\u4e4b\u5916\u8fd8\u6709\u8c01\u77e5\u9053\u8fd9\u4e2a\u79d8\u5bc6\uff1f\u201d\u592a\u4e0a\u7687\u9053\uff1a\u201c\u518d\u65e0\u4ed6\u4eba\u3002\u201d\u56fd\u738b\u9053\uff1a\u201c\u597d\uff0c \u4f60\u628a\u5979\u4ea4\u7ed9\u6211\u3002\u8ba9\u6211\u8bbe\u6cd5\u5904\u7f6e\u5979\u3002\u8fd9\u79d8\u5bc6\u51b3\u4e0d\u80fd\u6cc4\u9732\u51fa\u53bb\uff01\u201d\u592a\u4e0a\u7687\u9762\u8272\u4e00\u53d8\uff0c\u8bf4\u9053\uff1a \u201c\u600e\u4e48\uff1f\u4f60\u662f\u4e0d\u613f\u5f52\u5b97\u8ba4\u4eb2\uff0c\u8fd8\u610f\u56fe\u6740\u4eba\u706d\u53e3\uff1f\u201d \u3000\u3000\u56fd\u738b\u82e6\u607c\u975e\u5e38\uff0c\u6414\u4e86\u6414\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u7237\u7237\uff0c\u4f60\u597d\u7cca\u6d82\uff01\u6211\u600e\u4e48\u80fd\u5f52\u5b97\u8ba4\u4eb2\uff1f\u4f60\u60f3\u60f3 \u8fd9\u79d8\u5bc6\u4e00\u65e6\u8ba9\u5916\u4eba\u77e5\u9053\uff0c\u6211\u662f\u9a6c\u8428\u513f\u56fd\u7684\u4eba\uff0c\u8fd8\u600e\u80fd\u505a\u8fd9\u6606\u5e03\u5170\u56fd\u7684\u56fd\u738b\uff1f\u201d\u592a\u4e0a\u7687\u9053\uff1a \u201c\u4f60\u624d\u7cca\u6d82\uff0c\u8c01\u8981\u4f60\u5f20\u626c\u51fa\u53bb\uff1f\u4f60\u4e0d\u80af\u5f52\u5b97\u8ba4\u4eb2\uff0c\u90a3\u4e5f\u7f62\u4e86\uff0c\u4f46\u4f60\u603b\u4e0d\u80fd\u4f24\u5bb3\u4f60\u7684\u5ae1\u4eb2 \u5802\u59b9\u3002\u6211\u7ed9\u4f60\u51fa\u4e2a\u4e3b\u610f\uff0c\u4f60\u5047\u4f5c\u6253\u730e\uff0c\u5c06\u5979\u85cf\u5728\u8f66\u4e2d\uff0c\u6084\u6084\u7684\u628a\u5979\u9001\u51fa\u53bb\u5427\u3002\u201d \u3000\u3000\u56fd\u738b\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u884c\uff01\u5979\u7a81\u7136\u5931\u8e2a\uff0c\u522b\u4eba\u600e\u4e0d\u731c\u7591\uff0c\u7ae5\u59e5\u59e5\u6700\u5c11\u5c31\u8981\u67e5\u95ee\uff01\u201d \u592a\u4e0a\u7687\u6012\u9053\uff1a\u201c\u4f60\u5c31\u53ea\u77e5\u9053\u5bb3\u6015\u7ae5\u59e5\u59e5\uff1f \u3000\u3000\u56fd\u738b\u9053\uff1a\u201c\u4e0d\u5355\u662f\u7ae5\u59e5\u3002\u7237\u7237\uff0c\u4f60\u5728\u6df1\u5bab\u5341\u5e74\uff0c\u4e0d\u95ee\u653f\u4e8b\u3002 \u3000\u3000\u4f60\u4e0d\u77e5\u9053\u6cf0\u6e05\u738b\u8fd9\u4e00\u6d3e\u4eba\u52bf\u529b\u5f88\u5927\uff0c\u800c\u4e14\u6cf0\u6e05\u738b\u4e5f\u5728\u89ca\u89ce\u738b\u4f4d\u3002 \u3000\u3000\u5018\u82e5\u4e8b\u60c5\u6cc4\u9732\uff0c\u8fd9\u5c31\u6b63\u662f\u7ed9\u4ed6\u4e00\u4e2a\u7edd\u597d\u7684\u628a\u67c4\u4e86\u3002\u8fd8\u6709\uff0c\u9a6c\u8428\u513f\u56fd\u4e5f\u6bd5\u7adf\u8fd8\u662f\u54b1\u4eec \u7684\u654c\u56fd\uff0c\u4f60\u53c8\u600e\u80fd\u62c5\u4fdd\u90a3\u4f4d\u516c\u4e3b\u4e0d\u5c06\u8fd9\u4ef6\u4e8b\u60c5\u4f5c\u4e3a\u8981\u631f\uff1f\u653e\u8d70\u4e86\u5979\uff0c\u53ea\u6015\u540e\u60a3\u65e0\u7a77\uff01\u7237 \u7237\uff0c\u5979\u662f\u4e0d\u662f\u5728\u4f60\u8fd9\u513f\uff1f\u201d\u592a\u4e0a\u7687\u9053\uff1a\u201c\u4f60\u5f85\u600e\u6837\uff1f\u4f60\u6562\u5728\u6211\u8fd9\u91cc\u4eba\uff1f\u201d \u3000\u3000\u5ffd\u542c\u5f97\u201c\u5440\u201d\u7684\u4e00\u58f0\uff0c\u53a2\u623f\u623f\u95e8\u6253\u5f00\uff0c\u8c37\u4e2d\u83b2\u8d70\u4e86\u51fa\u6765\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u7237\u7237\uff0c\u4f60\u4eec \u4e0d\u5fc5\u4e3a\u6211\u4e89\u5435\u3002\u54e5\u54e5\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u4e3a\u6211\u70e6\u607c\u3002 \u3000\u3000\u6211\u77e5\u9053\u4f60\u662f\u6709\u8bb8\u591a\u4e3a\u96be\u4e4b\u5904\uff0c\u6211\u613f\u610f\u89e3\u9664\u4f60\u7684\u987e\u8651\uff0c\u4fdd\u5168\u4f60\u7684\u738b\u4f4d\uff0c\u53ea\u8981\u4f60\u7b54\u5e94\u6211 \u4e0e\u9a6c\u8428\u513f\u56fd\u7f62\u6218\u4f11\u5175\uff0c\u6211\u7acb\u5373\u5728\u4f60\u9762\u524d\u81ea\u5c3d\uff0c\u8fd9\u6837\uff0c\u4f60\u4e5f\u5c31\u4e0d\u5fc5\u5bb3\u6015\u8fd8\u6709\u8c01\u77e5\u9053\u4f60\u7684\u79d8 \u5bc6\u4e86\u3002\u201d \u3000\u3000\u592a\u4e0a\u7687\u9053\uff1a\u201c\u4e0d\u80fd\u8fd9\u6837\u3002\u201d\u8c37\u4e2d\u83b2\u5fae\u7b11\u9053\uff1a\u201c\u53bb\u4e86\u6211\u4e00\u4e2a\u4eba\uff0c\u80fd\u4fdd\u5168\u8bb8\u591a\u4eba\u7684\u6027\u547d\uff0c \u90a3\u53c8\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u201d \u3000\u3000\u56fd\u738b\u5446\u4e86\u4e00\u5446\uff0c\u4f3c\u4e4e\u9887\u53d7\u8c37\u4e2d\u83b2\u7684\u611f\u52a8\uff0c\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u4f60\u54e5\u54e5\u4e5f\u4e0d\u77e5\u9053\u8fd9\u4e2a\u79d8\u5bc6\uff1f\u201d \u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u9664\u6211\u4e4b\u5916\uff0c\u65e0\u4eba\u77e5\u9053\u3002\u6211\u4e5f\u8fd8\u662f\u521a\u521a\u77e5\u9053\u7684\uff0c\u56e0\u4e3a\u90a3\u4e24\u5c01\u4fe1\u7684\u7b14\u8ff9\u53ea\u6709\u7237 \u7237\u8ba4\u5f97\u3002\u201d\u56fd\u738b\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u9a6c\u8428\u513f\u56fd\u5f53\u771f\u662f\u4e0d\u60f3\u4e0e\u6211\u56fd\u4e3a\u654c\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5f53\u7136\u3002 \u8981\u4e0d\u6211\u4eec\u600e\u4f1a\u6d3e\u9063\u4f7f\u8005\u56e2\u6765\uff0c\u8981\u6c42\u548c\u89e3\u3002\u201d\u6606\u5e03\u5170\u738b\u9762\u4e0a\u9732\u51fa\u7591\u60d1\u7684\u795e\u60c5\uff0c\u6014\u6014\u5730\u671b\u7740 \u8c37\u4e2d\u83b2\u3002 \u3000\u3000\u592a\u4e0a\u7687\u9053\uff1a\u201c\u83b2\u513f\uff0c\u4f60\u521a\u624d\u8bf4\u5230\u90a3\u4f7f\u8005\u88ab\u6740\u4e4b\u4e8b\uff0c\u8bf4\u662f\u5185\u60c5\u590d\u6742\u3002\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u56fd\u738b\u63d2\u53e3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u6b63\u662f\u8981\u95ee\u8fd9\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u671b\u4e86\u56fd\u738b\u4e00\u773c\uff0c\u8fdf\u7591\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u6055\u6211\u65e0\u793c\uff0c\u6211\u60f3\u5148\u95ee\u965b\u4e0b\u4e00\u4e2a\u95ee\u9898\u3002\u201d \u56fd\u738b\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u95ee\u6211\u4ec0\u4e48\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a \u3000\u3000\u201c\u54b1\u4eec\u6311\u5f00\u4e86\u5929\u7a97\u8bf4\u4eae\u8bdd\uff0c\u4f60\u6d3e\u9063\u7684\u90a3\u4f4d\u4f7f\u81e3\uff0c\u662f\u5426\u53ea\u662f\u4e3a\u4e86\u8d3a\u6211\u54e5\u54e5\u767b\u4f4d\u7684\uff1f\u201d \u56fd\u738b\u8be7\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u8fd8\u6709\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f46\u6211\u4eec\u5728\u8d35\u56fd\u4f7f\u81e3\u5448\u9012\u4e86\u8d3a\u8868\u4e4b\u540e\u7684\u7b2c\u4e8c\u5929\uff0c\u53c8\u5728\u53e6\u4e00\u4e2a\u573a\u6240\u53d1\u73b0\u4e86 \u4ed6\u3002\u201d\u56fd\u738b\u9053\uff1a\u201c\u4ec0\u4e48\u573a\u6240\uff1f\u2018\u8c37\u4e2d\u83b2\u9053\uff1a \u3000\u3000\u201c\u5728\u6211\u4eec\u7684\u5b9d\u5e93\u4e4b\u4e2d\uff0c\u5f53\u65f6\u4ed6\u8499\u7740\u8138\uff0c\u8fd8\u6709\u53e6\u4e00\u4e2a\u4eba\u548c\u4ed6\u540c\u5728\u4e00\u8d77\uff0c\u90a3\u4e2a\u4eba\u662f\u6211\u56fd \u7be1\u4f4d\u5978\u8d3c\u76d6\u6e29\u4e4b\u5b50\u76d6\u82cf\u3002\u6211\u54e5\u54e5\u6740\u4e86\u4ed6\uff0c\u63ed\u5f00\u4ed6\u7684\u8499\u9762\u4e2d\uff0c\u624d\u77e5\u9053\u4ed6\u662f\u8d35\u56fd\u4f7f\u81e3\u3002\u53ef\u60dc \u90a3\u76d6\u82cf\u5374\u5df2\u9003\u53bb\uff0c\u4e0d\u80fd\u62ff\u4f5c\u5bf9\u8bc1\u3002\u4f46\u6211\u8bf4\u7684\u53ef\u5168\u662f\u5b9e\u8bdd\uff01\u201d \u3000\u3000\u56fd\u738b\u5446\u4e86\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u4e8b\u60c5\uff1f\u201d\u8c37\u4e2d\u83b2\u4e5f\u662f\u5927\u4e3a\u5947\u602a\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\uff0c \u539f\u6765\u4e0d\u662f\u2026\u2026\u201d\u56fd\u738b\u5fae\u6709\u6012\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u662f\u6211\u6307\u4f7f\u7684\u4e48\uff1f\u6606\u5e03\u5170\u56fd\u867d\u662f\u4e0d\u5982\u4f60\u4eec \u5bcc\u6709\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u5230\u4f60\u4eec\u90a3\u513f\u76d7\u5b9d\u3002\u201d\u592a\u4e0a\u7687\u9053\uff1a\u201c\u4e0d\u7528\u95ee\u4e86\uff0c\u8fd9\u4e00\u5b9a\u662f\u76d6\u82cf\u610f\u56fe\u76d7\u5b9d\uff0c \u6211\u65e9\u5c31\u529d\u4f60\u4e0d\u53ef\u6536\u5bb9\u8fd9\u53ae\u7684\uff0c\u4f60\u504f\u4e0d\u542c\u2026\u2026\u54a6\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u53ea\u89c1\u56fd\u738b\u9762\u8272\u7070\u767d\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u8fd9\u4f7f\u8005\u662f\u6cf0\u6e05\u738b\u7684\u513f\u5b50\u54c8\u63d0\u3002\u4ed6\u662f\u738b\u5ba4\uff0c \u53c8\u662f\u5927\u81e3\uff0c\u8363\u534e\u5bcc\u8d35\uff0c\u6837\u6837\u4ff1\u5168\uff0c\u7eb5\u4f7f\u89ca\u89ce\u5b9d\u7269\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u51fa\u6b64\u4e0b\u7b56\uff0c\u53ea\u6050\u5176\u4e2d\u53e6\u6709\u539f \u56e0\uff01\u7237\u7237\uff0c\u6055\u6211\u76f4\u8bf4\uff0c\u2026\u2026\u201d\u592a\u4e0a\u7687\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d\u4ed6\u5df2\u6599\u5230\u51e0\u5206\uff0c\u58f0\u97f3\u4ea6\u5df2\u5fae\u5fae\u98a4\u6296\u3002 \u3000\u3000\u56fd\u738b\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u66fe\u542c\u5230\u6709\u4eba\u5bc6\u62a5\uff0c\u8bf4\u662f\u6cf0\u6e05\u738b\u89ca\u89ce\u738b\u4f4d\uff0c\u5236\u9020\u8c23\u8a00\uff0c\u2026\u2026\u201d\u592a\u4e0a \u7687\u9053\uff1a\u201c\u4ec0\u4e48\u8c23\u8a00\uff1f\u56fd\u738b\u8fdf\u8fdf\u7591\u7591\u5730\u8bf4\u9053\uff1a\u201c\u8c23\u8a00\u8bf4\u7237\u7237\u662f\u6000\u5b55\u4e03\u4e2a\u6708\u5c31\u751f\u4e0b\u6765\u7684\uff0c\u56e0 \u800c\u6cf0\u6e05\u738b\u60f3\u91cd\u7ffb\u65e7\u6848\uff0c\u6000\u7591\u54b1\u4eec\u7684\u8840\u7edf\u3002\u201d \u3000\u3000\u592a\u4e0a\u7687\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u8c23\u8a00\uff0c\u73b0\u5728\u4f60\u5df2\u7ecf\u77e5\u9053\u4e86\u3002\u4f46\u6cf0\u6e05\u738b\u7eb5\u6709\u7be1\u4f4d\u4e4b\u5fc3\uff0c\u8c05\u4ed6\u4e5f\u8fd8 \u4e0d\u6562\u3002\u800c\u4e14\u4e8b\u60c5\u5df2\u8fc7\u4e86\u5c06\u8fd1\u767e\u5e74\uff0c\u548c\u6211\u540c\u65f6\u7684\u4eba\uff0c\u4e5f\u90fd\u6b7b\u5149\u4e86\u3002\u91cd\u7ffb\u65e7\u6848\uff0c\u4e5f\u4e0d\u4f1a\u6709\u4eba \u76f8\u4fe1\u3002\u96be\u9053\u8fd8\u6709\u4eba\u6562\u6765\u95ee\u6211\uff0c\u662f\u5728\u5a18\u809a\u91cc\u51e0\u4e2a\u6708\u51fa\u751f\u7684\u5417\uff1f\u201d\u56fd\u738b\u9053\uff1a\u201c\u4e0d\u7136\uff0c\u4ed6\u82e5\u662f \u6293\u7740\u8bc1\u636e\uff0c\u90a3\u5c31\u53ef\u4ee5\u660e\u6b63\u8a00\u987a\u7684\u8d76\u8d70\u54b1\u4eec\u4e86\u3002\u6211\u6000\u7591\u54c8\u63d0\u5230\u5b9d\u5e93\u4e4b\u4e2d\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u627e\u8bc1 \u636e\uff0c\u4f8b\u5982\u90a3\u4e24\u5c01\u4fe1\u3002\u201d\u592a\u4e0a\u7687\u9053\uff1a \u3000\u3000\u201c\u597d\u5728\u4fe1\u4e5f\u70e7\u4e86\u3002\u201d \u3000\u3000\u56fd\u738b\u8bf4\u9053\uff1a\u201c\u4f46\u6cf0\u6e05\u738b\u8fd8\u5728\uff0c\u603b\u662f\u5fc3\u8179\u4e4b\u60a3\u3002\u4ed6\u7231\u5b50\u88ab\u6740\uff0c\u5c82\u80fd\u5e72\u4f11\uff01\u83b2\u59b9\u867d\u662f\u6307 \u51fa\u4ed6\u662f\u5728\u5b9d\u5e93\u4e2d\u88ab\u6740\uff0c\u4f46\u6b7b\u65e0\u5bf9\u8bc1\uff0c\u6b64\u4e8b\u5927\u8fc7\u79bb\u5947\uff0c\u8bf4\u51fa\u6765\u4e5f\u6ca1\u4eba\u76f8\u4fe1\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f9d\u4f60\u6240\u8bf4\uff0c\u4f60\u662f\u4e3a\u52bf\u6240\u8feb\uff0c\u4e0d\u80fd\u4e0d\u5bf9\u9a6c\u8428\u513f\u56fd\u7528\u5175\u4e86\uff1f\u201d \u3000\u3000\u56fd\u738b\u5ffd\u9053\uff1a\u201c\u83b2\u59b9\uff0c\u4f60\u80af\u4e0d\u80af\u5e2e\u6211\u4e2a\u5fd9\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u56fd\u738b\u9053\uff1a\u201c\u5e2e\u5fd9 \u6211\u5bf9\u4ed8\u90a3\u6cf0\u6e05\u738b\u3002\u201d\u592a\u4e0a\u7687\u9053\uff1a\u201c\u4f60\u662f\u610f\u56fe\u5c06\u4ed6\u6740\u4e86\u706d\u53e3\uff1f\u201d\u56fd\u738b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b64\u4e8b\u4e0d \u80fd\u6c42\u4e4b\u4e8e\u7ae5\u59e5\u59e5\u3002\u4f46\u54b1\u4eec\u6084\u6084\u5c06\u4ed6\u6740\u4e86\uff0c\u4e8b\u60c5\u5df2\u7ecf\u505a\u4e86\u51fa\u6765\uff0c\u6599\u7ae5\u59e5\u59e5\u4e5f\u4e0d\u4f1a\u56e0\u6cf0\u6e05\u738b \u7684\u7f18\u6545\u4e0e\u6211\u7ffb\u8138\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u82e6\u7b11\u9053\uff1a\u201c\u6211\u6b66\u529f\u5df2\u5931\uff0c\u5bb0\u4e00\u53ea\u9e21\u53ea\u6015\u4ea6\u5df2\u4e0d\u6210\u3002\u201d\u56fd\u738b\u9053\uff1a\u201c\u4f60\u4e2d\u7684\u662f\u4fee \u7f57\u9165\u9aa8\u6563\u7684\u6bd2\uff0c\u6211\u8fd9\u91cc\u6709\u89e3\u836f\uff0c\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u4f60\u5c31\u53ef\u4ee5\u6062\u590d\u6b66\u529f\u3002\u4eca\u665a\u521d\u66f4\u65f6\u5206\uff0c\u4f60 \u6084\u6084\u6765\u6211\u5bab\u4e2d\uff0c\u4f60\u7684\u5b9d\u5251\u5230\u65f6\u6211\u81ea\u4f1a\u4ea4\u8fd8\u7ed9\u4f60\u3002\u201d \u3000\u3000\u592a\u4e0a\u7687\u9053\uff1a\u201c\u6cf0\u6e05\u738b\u6b66\u529f\u4e0d\u9519\uff0c\u515a\u7fbd\u53c8\u591a\uff0c\u5979\u2026\u2026\u201d\u8a00\u4e0b\u9887\u662f\u5fe7\u8651\uff0c\u56fd\u738b\u7b11\u9053\uff1a \u201c\u83b2\u59b9\u672c\u9886\u66f4\u5f3a\u3002\u4f9d\u6211\u770b\u6765\uff0c\u9664\u4e86\u7ae5\u59e5\u59e5\u4e4b\u5916\uff0c\u54b1\u4eec\u7684\u6b66\u58eb\u8c01\u90fd\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u53ea\u8981\u5979 \u6062\u590d\u4e86\u6b66\u529f\uff0c\u6740\u6cf0\u6e05\u738b\u90a3\u662f\u6613\u5982\u53cd\u638c\u3002\u81f3\u4e8e\u4ee5\u540e\u7684\u4e8b\uff0c\u90a3\u5c31\u4ee5\u540e\u518d\u8bf4\u5427\u3002\u6211\u73b0\u5728\u662f\u5934\u75db \u533b\u5934\uff0c\u811a\u75db\u533b\u811a\u3002\u6cf0\u6e05\u738b\u8db3\u4ee5\u52a8\u6447\u6211\u7684\u738b\u4f4d\uff0c\u6211\u4e0d\u6740\u4ed6\u3002 \u3000\u3000\u5c82\u80fd\u5b89\u6795\uff1f\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u597d\uff0c\u6211\u53ef\u4ee5\u5e2e\u4f60\u6740\u4eba\u4e00\u4f46\u4f60\u53ef\u5f97\u7b54\u5e94\u6211\u4ece\u4eca\u4e4b\u540e\uff0c\u54b1\u4eec\u4e24\u56fd\u518d\u4e0d\u4ea4\u5175\u3002\u201d \u56fd\u738b\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u3002\u53ea\u8981\u4f60\u628a\u8fd9\u4e2a\u79d8\u5bc6\u7edd\u4e0d\u6cc4\u6f0f\uff0c\u54b1\u4eec\u53ef\u4ee5\u79c1\u4e0b\u8ba4\u4eb2\uff0c\u6c38\u4e3a\u5144\u5f1f\u4e4b \u90a6\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u63a5\u8fc7\u4e24\u9897\u89e3\u836f\uff0c\u4fbf\u5373\u541e\u4e0b\u53bb\u3002\u592a\u4e0a\u7687\u5ffd\u9053\uff1a\u201c\u5b59\u513f\uff0c\u4f60\u7ed9\u7684\u662f\u4ec0\u4e48\u89e3\u836f\uff1f\u201d \u56fd\u738b\u9053\uff1a\u201c\u4e0d\u5c31\u662f\u4fee\u7f57\u9165\u9aa8\u6563\u7684\u89e3\u836f\u5417\uff1f\u201d\u592a\u4e0a\u7687\u53cc\u773c\u4e00\u77aa\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u8fd9\u4e24\u9897\u89e3 \u836f\u600e\u4e48\u662f\u4e00\u7ea2\u4e00\u767d\uff1f \u3000\u3000\u6211\u867d\u7136\u662f\u8001\u7cca\u6d82\u4e86\uff0c\u4e5f\u8fd8\u8bb0\u5f97\u8fd9\u79cd\u89e3\u836f\u4f3c\u4e4e\u53ea\u6709\u7ea2\u8272\u7684\u4e00\u79cd\u3002\u5feb\u8bf4\uff0c\u4f60\u7ed9\u4e86\u5979\u4ec0\u4e48 \u836f\uff1f\u8fde\u7237\u7237\u90fd\u60f3\u54c4\u9a97\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5927\u5403\u4e00\u60ca\uff0c\u9000\u5f00\u4e24\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u4e0d\u60f3\u6d3b\u7740\u56de\u53bb\u7684\uff0c\u4f60\u8981\u5bb3\u6211\uff0c\u4e5f\u7528 \u4e0d\u7740\u8fd9\u7b49\u624b\u6bb5\uff01\u201d \u3000\u3000\u56fd\u738b\u9762\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u7ea2\uff0c\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u7237\u7237\uff0c\u59b9\u59b9\uff0c\u4f60\u4eec\u4e0d\u7528\u60ca\u7591\uff0c\u6211\u5bf9\u4f60\u4eec\u5b9e\u8bf4 \u4e86\u5427\u3002\u4e00\u9897\u7897\u662f\u89e3\u836f\uff0c\u53e6\u4e00\u9897\u561b\uff0c\u563f\u563f\uff0c\u4f60\u6740\u4e86\u6cf0\u6e05\u738b\u4e4b\u540e\uff0c\u79cb\u81ea\u4f1a\u7ed9\u4f60\u89e3\u6bd2\uff0c\u90a3\u662f\u53e6 \u4e00\u79cd\u4e09\u65e5\u4e4b\u5185\u81f4\u547d\u7684\u6bd2\u836f\u3002\u201d \u3000\u3000\u592a\u4e0a\u7687\u6012\u9053\uff1a\u201c\u4f60\u600e\u4e48\u53ef\u4ee5\u7528\u8fd9\u7b49\u9634\u72e0\u7684\u624b\u6bb5\u5bf9\u4ed8\u4f60\u7684\u59b9\u5b50\u3002\u201d\u8c37\u4e2d\u83b2\u5374\u53cd\u800c\u653e\u4e0b \u5fc3\u6765\uff0c\u7b11\u9053\uff1a\u201c\u54e5\u54e5\u662f\u6015\u6211\u6062\u590d\u529f\u529b\u4e4b\u540e\uff0c\u4e0d\u7ed8\u4ed6\u505a\u4e8b\uff0c\u5374\u81ea\u6e9c\u4e86\u3002\u54b1\u4eec\u4e24\u56fd\u73b0\u5728\u8fd8\u5904 \u5728\u654c\u5bf9\u4f0f\u6001\u4e2d\uff0c\u4e5f\u96be\u602a\u4f60\u4e0d\u80fd\u5b8c\u5168\u4fe1\u6211\u3002\u597d\u5427\uff0c\u6211\u5c31\u5728\u6740\u4e86\u6cf0\u6e05\u5de5\u4e4b\u540e\uff0c\u518d\u95ee\u4f60\u8ba8\u53e6\u4e00 \u79cd\u89e3\u836f\u5427\uff0c\u4f60\u8981\u6211\u5982\u4f55\u884c\u4e8b\uff0c\u8bf7\u5429\u5490\u5427\uff01\u201d \u3000\u3000\u56fd\u738b\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u6211\u65e9\u6709\u5de7\u8ba1\u2026\u2026\u201d\u201c\u5b89\u6392\u201d\u4e8c\u5b57\u672a\u66fe\u51fa\u53e3\uff0c\u58f0\u97f3\u5ffd\u5730\u505c\u4e0b\uff0c\u5c31 \u5728\u6b64\u65f6\uff0c\u53ea\u89c1\u4e00\u4e2a\u8001\u5a46\u5a46\u5b9a\u4e86\u8fdb\u6765\u3002\u9634\u607b\u607b\u5730\u7b11\u9053\uff1a\u201c\u5de7\u6781\u4e86\uff0c\u539f\u6765\u7687\u4e0a\u548c\u516c\u4e3b\u90fd\u5728\u8fd9 \u513f\u3002\u201d\u8fd9\u8001\u5a46\u5a46\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u90a3\u201c\u91d1\u8f6e\u5723\u6bcd\u201d\u7ae5\u59e5\u59e5\u3002\u592a\u4e0a\u7687\u5fc3\u91cc\u6ee1\u4e0d\u9ad8\u5174\u3002 \u3000\u3000\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7687\u513f\uff0c\u5723\u6bcd\u627e\u4f60\uff0c\u4f60\u53ef\u4ee5\u4e0d\u7528\u4f3a\u5019\u6211\u4e86\u3002\u201d\u7ae5\u59e5\u59e5\u7b11\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u662f \u7279\u6765\u62dc\u5019\u8001\u7956\u5b97\u7684\u3002\u201d \u3000\u3000\u592a\u4e0a\u7687\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u7a00\u5ba2\uff0c\u7a00\u5ba2\u3002\u96be\u5f97\uff0c\u96be\u5f97\uff0c\u5723\u6bcd\u7adf\u4f1a\u60f3\u5230\u6765\u770b\u6211\u8fd9\u4e2a\u4e0d\u4e2d\u7528\u7684 \u8001\u5934\u513f\u3002\u201d\u4ed6\u5bf9\u7ae5\u59e5\u59e5\u662f\u8ba8\u538c\u5df2\u6781\uff0c\u4e0d\u81ea\u7981\u7684\u4fbf\u51fa\u8a00\u5632\u8bbd\uff0c\u4f46\u5374\u4e5f\u6709\u51e0\u5206\u5fcc\u60ee\uff0c\u6545\u800c\u4e0d \u6562\u8d76\u5979\u51fa\u53bb\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u660e\u77e5\u592a\u4e0a\u7687\u5bf9\u5979\u4e0d\u6ee1\uff0c\u5374\u4f6f\u4f5c\u4e0d\u77e5\u3002\u6d0b\u6d0b\u81ea\u5f97\uff0c\u5927\u9a6c\u91d1\u5200\u7684\u5750\u4e86\u4e0b\u6765\uff0c\u7b11\u9053\uff1a \u201c\u54ea\u91cc\u8bdd\u6765\uff0c\u8001\u7956\u5b97\u8001\u5f53\u76ca\u58ee\uff0c\u8fdc\u80dc\u5e38\u4eba\u3002\u6211\u8fd8\u8981\u5411\u8001\u7956\u5b97\u8bf7\u6559\u5462\u3002\u201d\u592a\u4e0a\u7687\u9053\uff1a\u201c\u8bf7 \u6559\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7ae5\u59e5\u59e5\u9053\uff1a\u201c\u60f3\u8bf7\u6559\u8001\u7956\u5b97\u9f99\u529b\u79d8\u85cf\u7684\u5185\u529f\u5fc3\u6cd5\u3002\u201d\u592a\u4e0a\u7687\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0 \u4e48\u9f99\u529b\u79d8\u85cf\u3001\u5185\u529f\u5fc3\u6cd5\uff1f\u6211\u8fde\u542c\u4e5f\u6ca1\u6709\u542c\u8fc7\uff01\u201d\u8c37\u4e2d\u83b2\u6697\u6697\u8be7\u5f02\uff1a\u201c\u8fd9\u5996\u5a46\u4e5f\u77e5\u9053\u6709\u9f99 \u529b\u79d8\u85cf\uff1f\u201d \u3000\u3000\u7ae5\u59e5\u59e5\u9f87\u7259\u4e00\u7b11\uff0c\u7b11\u5bb9\u663e\u5f97\u5341\u5206\u8be1\u79d8\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u8001\u7956\u5b97\u7684\u5916\u7956\u7236\u662f\u767e\u591a\u5e74 \u524d\u6b66\u6797\u7684\u4e00\u4ee3\u5947\u4eba\uff0c\u5f53\u5e74\u6709\u4e00\u4f4d\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u738b\u8fd8\u66fe\u7ecf\u505a\u8fc7\u4ed6\u95e8\u4e0b\u5f1f\u5b50\u5462\u3002\u9f99\u529b\u79d8\u85cf\u662f \u9a6c\u8428\u513f\u56fd\u4f20\u56fd\u4e4b\u5b9d\u7684\u4e00\u90e8\u6b66\u5b66\u79d8\u7c4d\uff0c\u636e\u8bf4\u8fd9\u4f4d\u9690\u7792\u4e86\u8eab\u4efd\u7684\u56fd\u738b\u5f1f\u5b50\uff0c\u5c31\u56e0\u4e3a\u81ea\u5df1\u770b\u4e0d \u61c2\u8fd9\u90e8\u79d8\u7c4d\uff0c\u6545\u800c\u6295\u5230\u90a3\u4f4d\u5f02\u4eba\u95e8\u4e0b\uff0c\u610f\u6b32\u501f\u4e43\u5e08\u6307\u70b9\uff0c\u5e08\u5f92\u5171\u4fee\u79d8\u85cf\u4e0a\u7684\u6b66\u529f\uff0c\u4e0d\u77e5 \u53ef\u6709\u6b64\u4e8b\uff1f\u201d \u3000\u3000\u592a\u4e0a\u7687\u53d8\u4e86\u9762\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4ece\u54ea\u91cc\u542c\u5230\u8fd9\u4e9b\u65e0\u7a3d\u4e4b\u8c08\u7684\uff1f \u3000\u3000\u6211\u662f\u4e00\u6982\u4e0d\u77e5\u3002\u201d\u5fc3\u91cc\u5374\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u96be\u9053\u6bcd\u540e\u6559\u6211\u7684\u5410\u7eb3\u529f\u592b\uff0c\u5c31\u662f\u4ec0\u4e48\u5185\u529f\u5fc3 \u6cd5\uff1f\u54fc\uff0c\u8fd9\u5996\u5a46\u95ee\u8d77\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u5206\u660e\u662f\u4e0d\u6000\u597d\u610f\u3002\u6211\u5bf9\u5979\u4e0d\u7406\u4e0d\u776c\uff0c\u4e14\u770b\u5979\u6562\u628a\u6211\u600e\u4e48 \u6837\uff1f\u201d\u539f\u6765\u592a\u4e0a\u7687\u7684\u786e\u662f\u66fe\u7ecf\u5b66\u8fc7\u4e00\u4e9b\u4e0a\u4e58\u5185\u529f\u7684\u57fa\u672c\u5410\u7eb3\u529f\u592b\uff0c\u662f\u4ee5\u5f97\u4eab\u9ad8\u9f84\uff0c\u4e0d\u8fc7 \u4ed6\u81ea\u5df1\u5374\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u9053\uff1a\u201c\u8001\u7956\u5b97\u8bf4\u8fd9\u662f\u65e0\u7a3d\u4e4b\u8c08\u4e48\uff1f\u4f46\u90a3\u4f4d\u9a6c\u8428\u513f\u56fd\u738b\uff0c\u66fe\u662f\u4f60\u5916\u7956\u7236\u7684\u5f97\u610f \u5f1f\u5b50\uff0c\u8fd9\u603b\u662f\u771f\u7684\u5427\uff1f\u8001\u7956\u5b97\u5c0f\u65f6\u5019\u4e0d\u66fe\u542c\u4f60\u5916\u7956\u7236\u8bf4\u8fc7\u4e48\uff1f\u5509\uff0c\u6b64\u4e8b\u5df2\u8fc7\u4e86\u767e\u5e74\uff0c\u77e5 \u9053\u5f53\u5e74\u4e4b\u4e8b\u7684\u5df2\u6ca1\u6709\u51e0\u4eba\u4e86\uff0c\u6240\u4ee5\u6211\u624d\u6765\u5411\u8001\u7956\u5b97\u6253\u542c\u6253\u542c\uff0c\u8fd9\u4e5f\u65e0\u975e\u662f\u4e00\u7247\u597d\u5947\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u592a\u4e0a\u7687\u518d\u4e5f\u4e0d\u80fd\u5fcd\u8010\uff0c\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u6211\u5e74\u8001\u5065\u5fd8\uff0c\u516b\u5341\u591a\u5e74\u4e4b\u524d\u6211\u5916\u7956\u7236\u5bf9\u6211\u8bf4\u8fc7 \u4e9b\u4ec0\u4e48\u8bdd\uff0c\u6211\u8fd8\u600e\u80fd\u8bb0\u5f97\uff1f\u5bf9\u4e0d\u4f4f\uff0c\u6211\u7cbe\u795e\u75b2\u5026\uff0c\u6055\u4e0d\u5949\u966a\u4e86\uff0c\u738b\u513f\uff0c\u7ed9\u6211\u9001\u5ba2\uff01\u201d \u3000\u3000\u9010\u5ba2\u4ee4\u4e0b\uff0c\u56fd\u738b\u751a\u662f\u5c34\u5c2c\uff0c\u53cd\u800c\u662f\u90a3\u7ae5\u59e5\u59e5\u5374\u4f3c\u4e1d\u6beb\u4e0d\u4ee5\u4e3a\u610f\uff0c\u4fbf\u5411\u592a\u4e0a\u7687\u65bd\u793c\u9053\uff1a \u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u4e5f\u4e0d\u597d\u6253\u6270\u8001\u7956\u5b97\u4e86\u3002\u8001\u7956\u5b97\u7cbe\u795e\u75b2\u5026\uff0c\u90a3\u5c31\u8bf7\u5b89\u6b47\u5427\u3002\u201d\u8bf4\u4e5f\u5947\u602a\uff0c \u8bdd\u58f0\u672a\u4e86\uff0c\u592a\u4e0a\u7687\u679c\u7136\u4f3c\u7740\u4e86\u50ac\u7720\u4f3c\u7684\uff0c\u773c\u76ae\u5782\u4e0b\uff0c\u4f0f\u5728\u6848\u4e0a\uff0c\u547c\u547c\u565c\u565c\u7684\u5c31\u6253\u8d77\u9f3e\u6765\u3002 \u3000\u3000\u56fd\u738b\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u5723\u6bcd\uff0c\u4f60\u2026\u2026\u201d\u7ae5\u59e5\u59e5\u7b11\u9053\uff1a\u201c\u6211\u662f\u7565\u65bd\u5c0f\u672f\uff0c\u8ba9\u4ed6\u8001\u4eba \u5bb6\u597d\u597d\u7684\u7761\u4e00\u89c9\u3002\u5bf9\u4ed6\u8eab\u4f53\u6709\u76ca\u65e0\u635f\u3002\u201d\u8c37\u4e2d\u83b2\u9a82\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u8fd9\u5996\u5a46\uff01\u2026\u2026\u201d \u7ae5\u59e5\u59e5\u54c8\u54c8\u4e00\u7b11\uff0c\u4e00\u4f38\u624b\u5c31\u62ff\u7740\u4e86\u8c37\u4e2d\u83b2\u7684\u624b\u8155\u3002\u6b63\u662f\uff1a \u3000\u3000\u5fc3\u6000\u53f5\u6d4b\u65bd\u5996\u672f\uff0c\u62d2\u864e\u8fce\u72fc\u6094\u5df2\u8fdf\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e94\u5341\u4e8c\u56de\u3000\u613f\u89c5\u6843\u6e90\u540c\u6bd4\u7ffc\u3000\u4f55\u582a\u6bd2\u624b\u62c6\u9e33\u9e2f \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e94\u5341\u4e8c\u56de\u3000\u613f\u89c5\u6843\u6e90\u540c\u6bd4\u7ffc\u3000\u4f55\u582a\u6bd2\u624b\u62c6\u9e33\u9e2f \u3000\u3000\u8c37\u4e2d\u83b2\u6b66\u529f\u5c1a\u672a\u6062\u590d\uff0c\u88ab\u7ae5\u59e5\u59e5\u4e09\u6307\u6263\u7740\u5979\u7684\u8155\u8109\uff0c\u767b\u65f6\u52a8\u5f39\u4e0d\u5f97\uff0c\u7ae5\u59e5\u59e5\u51b7\u7b11\u9053\uff1a \u201c\u4f60\u4ee5\u4e3a\u4f60\u6709\u4e86\u592a\u4e0a\u7687\u5e87\u62a4\uff0c\u6211\u5c31\u5948\u4f55\u4e0d\u4e86\u4f60\u4e48\uff1f\u5feb\u4e0e\u6211\u51fa\u53bb\uff01\u201d\u56fd\u738b\u5fc3\u4e2d\u6253\u9f13\uff0c\u98a4\u58f0 \u8bf4\u9053\uff1a\u201c\u91d1\u8f6e\u5723\u6bcd\uff0c\u8fd9\u3001\u8fd9\u5973\u5b50\u2026\u2026\u201d\u7ae5\u59e5\u59e5\u53cc\u773c\u4e00\u7ffb\uff0c\u9053\uff1a\u201c\u965b\u4e0b\u6709\u4f55\u5429\u5490\uff1f\u201d\u56fd\u738b \u9053\uff1a\u201c\u8fd9\u5973\u5b50\u7559\u7740\u8fd8\u6709\u7528\u5904\uff0c\u8bf7\u5723\u6bcd\u4e0d\u53ef\u5c06\u5979\u4f24\u4e86\u3002\u4e0d\u5982\uff0c\u4e0d\u5982\u4ecd\u7531\u6211\u5c06\u5979\u5173\u8fdb\u51b7\u5bab\u53bb \u5427\u3002\u201d \u3000\u3000\u7ae5\u59e5\u59e5\u9053\uff1a\u201c\u4f60\u628a\u5979\u5173\u8fdb\u51b7\u5bab\uff0c\u4f60\u7237\u7237\u8fd8\u662f\u4f1a\u628a\u5979\u653e\u51fa\u6765\u7684\u3002 \u3000\u3000\u6211\u66ff\u4f60\u770b\u7ba1\uff0c\u4f60\u5c06\u4e8b\u60c5\u63a8\u5728\u6211\u7684\u8eab\u4e0a\uff0c\u4e0d\u662f\u6b63\u53ef\u4ee5\u514d\u5f97\u4f60\u5728\u4f60\u7237\u7237\u9762\u524d\u4e3a\u96be\u4e48\uff1a\u6211 \u77e5\u9053\u4f60\u7559\u7740\u5979\u6709\u7528\u5904\uff0c\u4f46\u6211\u7559\u7740\u5979\u66f4\u6709\u7528\u5904\uff0c\u6240\u4ee5\u4f60\u4e5f\u5c3d\u53ef\u653e\u5fc3\uff0c\u9664\u975e\u5979\u5f53\u771f\u4e0d\u8bc6\u597d\u6b79\u3002 \u5426\u5219\u6211\u53c8\u600e\u4f1a\u4f24\u5bb3\u4e8e\u5979\uff1f\u201d\u56fd\u738b\u65e0\u8a00\u53ef\u9a73\uff0c\u867d\u7136\u62c5\u5fc3\u5728\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u8c37\u4e2d\u83b2\u83b7\u5f97\u89e3\u836f \u7684\u4e8b\u60c5\u5c31\u4f1a\u88ab\u53d1\u73b0\uff0c\u4f46\u4e5f\u53ea\u597d\u5230\u65f6\u518d\u8bf4\u4e86\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u628a\u8c37\u4e2d\u83b2\u62d6\u51fa\u4e86\u5bab\u95e8\uff0c\u6840\u6840\u7b11\u9053\uff1a\u201c\u6c5f\u6d77\u5929\u662f\u4f60\u7684\u60c5\u90ce\u4e0d\u662f\uff1f\u201d\u8c37\u4e2d\u83b2\u95ed\u53e3 \u4e0d\u7b54\u3002\u7ae5\u59e5\u59e5\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u5bb3\u7f9e\uff0c\u6211\u90fd\u5df2\u77e5\u9053\u4e86\u3002\u54ea\u4e2a\u5973\u5b50\u4e0d\u76fc\u671b\u60c5\u90ce\uff1f\u597d\uff0c\u4eca\u665a \u82e5\u662f\u8fd9\u59d3\u6c5f\u7684\u5c0f\u5b50\u518d\u6765\uff0c\u6211\u5c31\u7ed9\u4f60\u884c\u4e2a\u65b9\u4fbf\uff0c\u8ba9\u4f60\u89c1\u4ed6\u3002\u5230\u65f6\uff0c\u4f60\u53ef\u5f97\u4e00\u5207\u4f9d\u4ece\u6211\u7684\u6446 \u5e03\uff0c\u5426\u5219\u53ef\u4f11\u602a\u6211\u5fc3\u72e0\u624b\u8fa3\u3002\u201d\u539f\u6765\u7ae5\u59e5\u59e5\u7ecf\u8fc7\u6628\u665a\u4e00\u6218\uff0c\u5bf9\u6c5f\u6d77\u5929\u4e5f\u9887\u6709\u51e0\u5206\u5fcc\u60ee\uff0c \u6545\u800c\u8981\u628a\u8c37\u4e2d\u83b2\u7262\u7262\u63e1\u5728\u624b\u4e2d\uff0c\u597d\u7528\u6765\u631f\u5236\u6c5f\u6d77\u5929\u3002 \u3000\u3000\u8d70\u5f97\u4e0d\u8fdc\uff0c\u8fce\u9762\u6765\u4e86\u4e00\u4eba\uff0c\u7b11\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u7ec8\u4e8e\u628a\u8fd9\u4e2b\u5934\u6349\u51fa\u6765\u4e86\u3002\u201d\u6765\u7684\u6b63\u662f \u5929\u9b54\u6559\u4e3b\u3002\u8c37\u4e2d\u83b2\u8fd9\u624d\u77e5\u9053\u5979\u4eec\u4e8c\u4eba\u4e43\u662f\u5e08\u5f92\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u9053\uff1a\u201c\u5361\u5170\u59ae\uff0c\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u8fd9\u4e2b\u5934\u5c31\u4ea4\u7ed9\u4f60\u770b\u7ba1\u5427\u3002 \u3000\u3000\u53ef\u5f97\u591a\u52a0\u51e0\u5206\u5c0f\u5fc3\uff0c\u522b\u8ba9\u5979\u8dd1\u4e86\u3002\u201d\u539f\u6765\u6628\u665a\u7ae5\u59e5\u59e5\u4e0e\u6c5f\u6d77\u5929\u786c\u62fc\u4e86\u6570\u638c\uff0c\u771f\u6c14\u8017 \u635f\u4e0d\u5c11\uff0c\u8fd9\u65f6\u6b63\u8981\u56de\u9759\u5ba4\u7ec3\u529f\uff0c\u7528\u8c37\u4e2d\u83b2\u6765\u631f\u5236\u6c5f\u6d77\u5929\uff0c\u8fd9\u6b63\u662f\u5929\u9b54\u6559\u4e3b\u6240\u732e\u7684\u8ba1\u7b56\uff0c \u7ae5\u59e5\u59e5\u5c06\u8c37\u4e2d\u83b2\u4ea4\u7ed9\u5f92\u5f1f\uff0c\u5f53\u7136\u662f\u653e\u5fc3\u4e0d\u8fc7\u4e86\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u653e\u5fc3\uff0c\u4ea4\u5230\u6211\u7684\u624b\u4e0a\u4e07\u65e0\u4e00\u5931\u3002\u201d\u4ece\u7ae5\u59e5\u59e5\u7684\u624b\u4e2d\u63a5\u8fc7 \u4e86\u8c37\u4e2d\u83b2\uff0c\u7b11\u9053\uff1a\u201c\u8c37\u59d1\u5a18\u3002\u4f55\u5fc5\u8fd9\u6837\u6012\u6c14\uff0c\u6211\u4e0d\u4f1a\u4e8f\u5f85\u4f60\u7684\uff0c\u8d70\u5427\uff01\u201d\u8c37\u4e2d\u83b2\u767d\u773c\u659c \u7768\uff0c\u5bf9\u5929\u9b54\u6559\u4e3b\u4e0d\u7406\u4e0d\u776c\uff0c\u4f46\u5979\u65e0\u529b\u62b5\u6297\uff0c\u53ea\u597d\u4efb\u51ed\u5929\u9b54\u4e3b\u62d6\u7740\u5979\u8d70\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5728\u5bab\u4e2d\u53e6\u6709\u4f4f\u6240\uff0c\u5e76\u4e0d\u662f\u548c\u7ae5\u59e5\u59e5\u540c\u5728\u4e00\u8d77\u7684\u3002\u5979\u62d6\u7740\u8c37\u4e2d\u83b2\u56de\u5230\u4e86\u81ea\u5df1 \u7684\u4f4f\u6240\uff0c\u8fdb\u4e86\u5bc6\u5ba4\uff0c\u5173\u4e0a\u4e86\u95e8\uff0c\u8fd9\u624d\u653e\u5f00\u8c37\u4e2d\u83b2\uff0c\u7b11\u9053\uff1a\u201c\u8c37\u59d1\u5a18\uff0c\u4f60\u4e00\u5b9a\u6068\u6211\u4e4b\u6781\u4e86\uff0c \u662f\u4e48\uff1f\u201d\u8c37\u4e2d\u83b2\u51b7\u51b7\u7684\u770b\u4e5f\u4e0d\u770b\u5979\u3002\u5929\u9b54\u6559\u4e3b\u5e7d\u5e7d\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5fc3\u91cc\u6068\u6211\uff0c\u6211 \u5fc3\u91cc\u5374\u5728\u7fa1\u6155\u7740\u4f60\u5462\uff01\u6c5f\u6d77\u5929\u6628\u665a\u4e3a\u4f60\u800c\u6765\u3002\u60f3\u5fc5\u4f60\u4e5f\u5df2\u7ecf\u662f\u77e5\u9053\u7684\u4e86\uff1f\u4f60\u6709\u8fd9\u4e48\u4e00\u4e2a \u771f\u5fc3\u5b9e\u610f\u7231\u4f60\u7684\u4eba\uff0c\u4f60\u8fd8\u4e0d\u503c\u5f97\u9ad8\u5174\u4e48\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5185\u5fc3\u7740\u5b9e\u4e3a\u6c5f\u6d77\u5929\u800c\u611f\u5230\u9a84\u505a\uff0c\u542c\u5f97\u4ed6\u7684\u540d\u5b57\uff0c\u4e0d\u7531\u5f97\u5fc3\u91cc\u751c\u4e1d\u4e1d\u7684\uff0c\u8138\u8272 \u4e5f\u5c31\u7f13\u548c\u591a\u4e86\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5f53\u6211\u662f\u4e0d\u8fd1\u4eba\u60c5\u7684\u602a\u7269\u4e48\uff1f \u3000\u3000\u6d77\u5929\u5c0f\u65f6\u5019\uff0c\u6211\u4e5f\u66fe\u629a\u517b\u8fc7\u4ed6\uff0c\u867d\u7136\u65e5\u5b50\u65e0\u591a\uff0c\u4f46\u4f60\u53ef\u4ee5\u95ee\u95ee\u4ed6\uff0c\u5728\u90a3\u4e9b\u65e5\u5b50\u91cc\uff0c \u6211\u662f\u4e0d\u662f\u5bf9\u4ed6\u771f\u5fc3\u75bc\u7231\uff1f\u201d\u8c37\u4e2d\u83b2\u51b7\u7b11\u4e00\u58f0\uff0c\u5374\u4e0d\u8a00\u8bed\uff0c\u5fc3\u9053\uff1a\u201c\u5c0f\u65f6\u5019\u4f60\u75bc\u4ed6\uff0c\u4ed6\u957f \u5927\u4e86\u4f60\u5374\u628a\u4ed6\u5f53\u4f5c\u4ec7\u4eba\u4e86\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u4f3c\u4e4e\u77e5\u9053\u8c37\u4e2d\u83b2\u7684\u5fc3\u610f\uff0c\u53c8\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u957f\u5927\u4e86\uff0c\u4ed6\u662f\u6b63\u6d3e\u95e8\u4eba\uff0c\u6211\u662f\u9b54 \u6559\u6559\u4e3b\uff0c\u597d\u51e0\u6b21\u6211\u548c\u4ed6\u4ea4\u624b\uff0c\u90a3\u662f\u4e3a\u4e86\u5f62\u52bf\u6240\u8feb\uff0c\u4e0d\u5f97\u4e0d\u7136\uff0c\u4f46\u6211\u53ef\u4e5f\u6ca1\u6709\u5bf9\u4ed6\u4e0b\u8fc7\u8fa3 \u624b\u3002\u4f60\u8fd8\u8bb0\u5f97\u90a3\u6b21\u5728\u9a6c\u8428\u513f\u56fd\uff0c\u4f60\u548c\u4ed6\u4e00\u540c\u88ab\u56da\u5728\u5b64\u5c9b\u4e4b\u4e0a\uff0c\u4ed6\u521a\u521a\u670d\u98df\u4e86\u5929\u5fc3\u77f3\uff0c\u4f60 \u53c8\u88ab\u5b9d\u8c61\u6cd5\u5e08\u7684\u5f1f\u5b50\u70b9\u4e86\u7a74\u9053\uff0c\u90a3\u65f6\u6211\u82e5\u8981\u53d6\u4f60\u4eec\u6027\u547d\uff0c\u90a3\u662f\u6613\u5982\u53cd\u638c\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5fc3\u60f3\uff1a\u201c\u90a3\u65f6\u4f60\u662f\u60f3\u8feb\u6211\u4ea4\u51fa\u5929\u5fc3\u77f3\u548c\u9f99\u529b\u79d8\u85cf\uff0c\u8fd9\u624d\u624b\u4e0b\u7559\u60c5\u3002\u201d\u4e0d\u8fc7\uff0c \u8f6c\u5ff5\u4e00\u60f3\uff0c\u7eb5\u7136\u5982\u6b64\uff0c\u5979\u4e5f\u7684\u786e\u662f\u5bf9\u6c5f\u6d77\u5929\u8fd8\u6709\u51e0\u5206\u60c5\u4efd\uff0c\u8fd9\u4e48\u4e00\u60f3\uff0c\u5979\u5bf9\u5929\u9b54\u6559\u4e3b\u7684 \u6076\u611f\uff0c\u4e5f\u5c31\u4e0d\u77e5\u4e0d\u89c9\u7684\u51cf\u4e86\u51e0\u5206\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u597d\u542c\uff0c\u8c01\u77e5\u9053\u4f60\u5fc3\u91cc\u53c8\u5728\u6253\u4ec0\u4e48 \u574f\u4e3b\u610f\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u662f\u5728\u6253\u4ec0\u4e48\u574f\u4e3b\u610f\u3002\u201d\u8c37\u4e2d\u83b2\u4e0d\u7981\u6012\u6c14\u53c8\u8d77\uff0c\u51b7\u51b7 \u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5e08\u5f92\u4fe9\u628a\u6211\u4ece\u592a\u4e0a\u7687\u90a3\u513f\u593a\u8fc7\u6765\uff0c\u4e0d\u662f\u5c31\u4e3a\u4e86\u8981\u7528\u6211\u6765\u4f5c\u4e2a\u5708\u5957\uff0c\u4ee4\u6c5f\u6d77 \u5929\u4e0a\u94a9\u4e48\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5ffd\u5730\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8c37\u59d1\u5a18\uff0c\u4f60\u60f3\u77e5\u9053\u6211\u6253\u7684\u662f\u4ec0\u4e48\u2018\u574f\u4e3b\u610f\u2019\u4e48\uff1f \u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u6211\u60f3\u4e3a\u4f60\u76d7\u53d6\u89e3\u836f\uff0c\u5c06\u4f60\u653e\u4e86\uff01\u201d\u8c37\u4e2d\u83b2\u6014\u4e86\u4e00\u5f81\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u8981 \u6551\u6211\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u6211\u5fc3\u91cc\u5b9e\u662f\u628a\u6d77\u5929\u5f53\u4f5c\u5f1f\u5f1f\uff0c\u628a\u4f60\u4e5f\u5f53\u4f5c\u5c0f \u59b9\u59b9\u4e00\u822c\uff1f\u6211\u4e5f\u662f\u613f\u610f\u4f60\u4eec\u505a\u4e00\u5bf9\u6069\u7231\u592b\u59bb\u7684\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u72d0\u7591\u4e0d\u5b9a\uff0c\u5bfb\u601d\uff1a\u201c\u5979\u8bf4\u5f97\u5012\u50cf\u5f88\u662f\u8bda\u6073\uff0c\u4f46\u5374\u53eb\u6211\u600e\u80fd\u76f8\u4fe1\u5979\uff1f\u201d\u5929\u9b54\u6559 \u4e3b\u53c8\u9053\uff1a\u201c\u6211\u8bda\u5fc3\u52a9\u4f60\uff0c\u4e5f\u4e0d\u6c42\u4f60\u611f\u6fc0\u3002\u53ea\u8981\u4f60\u4e0d\u628a\u6211\u5f53\u4f5c\u4ec7\u4eba\u5c31\u884c\u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u5fc3\u773c \u73b2\u73d1\uff0c\u95fb\u5f26\u6b4c\u800c\u77e5\u96c5\u610f\uff0c\u5929\u9b54\u6559\u4e3b\u53e3\u5934\u8bf4\u4e0d\u6c42\u4ed6\u611f\u6fc0\uff0c\u5fc3\u4e2d\u6b63\u8981\u60f3\u5979\u77e5\u6069\u62a5\u6069\uff0c\u5f53\u4e0b\u51b7 \u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u4e3a\u6211\u76d7\u53d6\u89e3\u836f\uff0c\u53ef\u662f\u8981\u6211\u62ff\u4ec0\u4e48\u4e1c\u897f\u4ea4\u6362\u4e48\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u55ef\uff0c\u4f60\u600e\u4e48\u8fd9\u6837\u591a\u7591\uff1f\u201d\u8c37\u4e2d\u83b2\u51b7\u7b11 \u9053\uff1a\u201c\u591a\u8c22\u4e86\uff0c\u6211\u4e0d\u60f3\u9886\u4f60\u7684\u60c5\uff0c\u6cbe\u4f60\u7684\u6069\u3002\u4f60\u8981\u600e\u4e48\u6837\u7b97\u8ba1\u6211\uff0c\u90a3\u5c31\u8bf7\u4fbf\u5427\u3002\u201d\u5929\u9b54 \u6559\u4e3b\u5446\u4e86\u4e00\u5446\uff0c\u4e0d\u7531\u5f97\u53c8\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4e5f\u96be\u602a\u4f60\u4e0d\u76f8\u4fe1\u6211\uff0c\u5509\uff0c\u4eba\u4e0e\u4eba\u4e4b\u95f4\u7684\u8bef\u89e3\u672c\u6765 \u5c31\u96be\u6d88\u9664\uff0c\u5374\u6559\u6211\u5982\u4f55\u80fd\u4e0e\u4f60\u8bf4\u5f97\u660e\u767d\u3002\u201d\u5929\u9b54\u6559\u4e3b\u5c1a\u672a\u77e5\u9053\u8c37\u4e2d\u83b2\u65e9\u5df2\u5f97\u4e86\u89e3\u836f\u3002 \u3000\u3000\u6b63\u8bf4\u5230\u6b64\u5904\uff0c\u5ffd\u542c\u5f97\u95e8\u5916\u6709\u811a\u6b65\u58f0\uff0c\u4e0d\u4e45\uff0c\u5c31\u6709\u4eba\u8f7b\u8f7b\u53e9\u95e8\uff0c\u7528\u4f20\u97f3\u5165\u5bc6\u7684\u5185\u529f\u5c06 \u8bdd\u58f0\u9001\u8fdb\uff1a\u201c\u5361\u5170\u59ae\u59d0\u59d0\uff0c\u662f\u6211\u6765\u4e86\uff01\u201d \u3000\u3000\u8fd9\u662f\u5389\u590d\u751f\u7684\u58f0\u97f3\uff0c\u5929\u9b54\u6559\u4e3b\u4f3c\u4e4e\u9887\u611f\u610f\u5916\uff0c\u4f46\u559c\u60a6\u4e4b\u60c5\u5374\u4ece\u7709\u5b57\u4e2d\u900f\u4e86\u51fa\u6765\uff0c\u5fc3 \u91cc\u60f3\u9053\uff1a\u201c\u751f\u5f1f\u5bf9\u6211\u7684\u75f4\u60c5\uff0c\u5b9e\u4e5f\u4e0d\u5728\u6c5f\u6d77\u5929\u5bf9\u8c37\u4e2d\u83b2\u4e4b\u4e0b\uff0c\u53ea\u662f\uff0c\u5509\uff0c\u53ea\u662f\u6211\u4eec\u7684\u9645 \u9047\u5374\u5dee\u5f97\u592a\u8fdc\u4e86\u3002\u201d\u5f53\u4e0b\u4f4e\u58f0\u5631\u53ee\u8c37\u4e2d\u83b2\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u6b47\u6b47\uff0c\u4e0d\u8bba\u6709\u4ec0\u4e48\u4e8b\u60c5\u53d1\u751f\uff0c\u4f60 \u90fd\u4e0d\u8981\u51fa\u6765\u3002\u201d\u8c37\u4e2d\u83b2\u5fc3\u9053\uff1a\u201c\u6211\u624d\u6ca1\u5de5\u592b\u7406\u4f60\u4eec\u7684\u95f2\u4e8b\u5462\u3002\u201d\u8fd9\u65f6\u5979\u7684\u529f\u529b\u5df2\u6e10\u6e10\u6062 \u590d\uff0c\u5929\u9b54\u6559\u4e3b\u4e00\u8d70\uff0c\u5979\u6b63\u597d\u9759\u5750\u8fd0\u529f\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u8d70\u51fa\u5bc6\u5ba4\u3002\u6253\u5f00\u5ba2\u5385\u7684\u5927\u95e8\uff0c\u53ea\u89c1\u5389\u590d\u751f\u7709\u5f00\u773c\u7b11\u5730\u80de\u4e86\u8fdb\u6765\uff0c\u624b\u4e2d\u6367\u7740 \u4e00\u4e2a\u7389\u5323\uff0c\u8bf4\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u6211\u7ed9\u4f60\u9001\u4ef6\u597d\u4e1c\u897f\u6765\u5566\uff01\u201d\u5929\u9b54\u6559\u4e3b\u8bf4\u9053\uff1a\u201c\u5664\u58f0\uff0c\u4f60\u4e32\u901a \u7389\u6606\u4ed1\uff0c\u628a\u7f57\u66fc\u5a1c\u516c\u4e3b\u5f04\u8d70\uff0c\u6211\u5e08\u7236\u6b63\u5728\u751f\u4f60\u7684\u6c14\u5462\uff01\u201d \u3000\u3000\u5389\u590d\u751f\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4e0d\u5982\u54b1\u4eec\u4e5f\u8d70\u4e86\u5427\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u6211\u600e\u4e48\u80fd\u4e00\u8d70\u4e86 \u4e4b\uff1f\u4f60\u522b\u8bf4\u5b69\u5b50\u6d3b\u4e86\uff0c\u5feb\u70b9\u544a\u8bc9\u6211\uff0c\u7f57\u66fc\u5a1c\u516c\u4e3b\u600e\u4e48\u6837\u4e86\uff1f\u201d\u5389\u590d\u751f\u9053\uff1a\u201c\u5979\u597d\u5f97\u5f88\u3002 \u4ed6\u4eec\u5df2\u7ecf\u5230\u4e86\u516c\u4e3b\u90a3\u5ea7\u884c\u5bab\uff0c\u7389\u6606\u4ed1\u5144\u59b9\u4e5f\u5df2\u7ecf\u4f1a\u9762\u4e86\u3002\u8fd8\u6709\u4e00\u4e9b\u610f\u5916\u7684\u4e8b\u60c5\u5462\u3002 \u3000\u3000\u4f60\u5148\u770b\u770b\u6211\u9001\u4f60\u7684\u793c\u7269\uff0c\u6211\u518d\u4e00\u6869\u6869\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4ec0\u4e48\u793c\u7269\uff0c\u5982\u6b64\u90d1\u91cd\uff1f\u201d\u5389\u590d\u751f\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u518d\u4e5f\u731c\u60f3\u4e0d\u5230\u3002\u8fd9 \u662f\uff0c\u2026\u2026\u55ef\u3002\u4f60\u8fd8\u662f\u81ea\u5df1\u6253\u5f00\u6765\u770b\u770b\u5427\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u6253\u5f00\u4e00\u770b\uff0c\u53ea\u89c1\u91cc\u9762\u4e00\u6735\u4e09\u8272\u5947\u82b1\uff0c\u5929\u9b54\u6559\u4e3b\u9053\u201c\u54e6\uff0c\u539f\u6765\u4f60\u7ed9\u6211\u5f04\u6765\u4e86 \u4e00\u6735\u2018\u96ea\u91cc\u7ea2\u5986\u2019\uff0c\u8fd9\u662f\u7389\u73b2\u73d1\u7ed9\u4f60\u7684\u5427\uff1f\u201d\u5389\u590d\u751f\u9053\uff1a\u201c\u7389\u5bb6\u5144\u59b9\u5f88\u591a\u8c22\u4f60\u7ed9\u4ed6\u4eec\u5e2e \u4e86\u5927\u5fd9\u3002\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u6211\u4e0d\u8fc7\u662f\u8bbe\u8c0b\u5b9a\u8ba1\uff0c\u51fa\u529b\u7684\u5168\u662f\u4f60\uff0c\u597d\uff0c\u591a\u8c22\u4f60\u7ed9\u6211\u8d39\u5fc3\u5566\u3002\u201d \u5389\u590d\u751f\u9053\uff1a\u201c\u54a6\uff0c\u5361\u5170\u59ae\u59d0\u59d0\uff0c\u4f60\u600e\u4e48\u4f3c\u4e4e\u4e0d\u5f88\u9ad8\u5174\uff1f\u4f60\u8fd9\u4e48\u7f8e\u8c8c\uff0c\u5c31\u50cf\u4e00\u6735\u5a07\u8273\u7684\u9c9c \u82b1\u2026\u2026\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4f60\u4eca\u5929\u662f\u600e\u4e48\u5566\uff1f\u6211\u4e0d\u662f\u8981\u4f60\u5949\u627f\u7684\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u8fd9\u6735\u4e09\u8272\u5947\u82b1\u7684\u5947\u6548\uff1f\u4f60\u628a\u5b83\u670d\u4e0b\uff0c\u4f60\u5c31\u53ef\u4ee5\u6c38\u4fdd\u9752\u6625\uff0c\u4ee5 \u540e\u4e5f\u4e00\u76f4\u50cf\u4e00\u6735\u6c38\u4e0d\u4f1a\u67af\u840e\u7684\u9c9c\u82b1\u5566\u3002\u4f60\u8001\u662f\u5acc\u6211\u5e74\u7eaa\u6bd4\u4f60\u5c0f\uff0c\u8bf4\u662f\u53ea\u53ef\u4ee5\u4f5c\u4f60\u7684\u5f1f\u5f1f\uff0c \u4f46\u53ea\u8981\u4f60\u670d\u98df\u4e86\u8fd9\u6735\u5947\u82b1\uff0c\u6211\u8001\u4e86\u4f60\u90fd\u672a\u8001\u5462\u3002\u54a6\u3002\u5361\u5170\u59ae\u59d0\u59d0\uff0c\u4f60\u600e\u4e48\u4e0d\u9ad8\u5174\uff0c\u53cd\u800c \u54ed\u4e86\uff1f\u201d \u3000\u3000\u5389\u590d\u751f\u6ee1\u6000\u9ad8\u5174\uff0c\u5374\u4e0d\u6599\u5929\u9b54\u6559\u4e3b\u6367\u7740\u8fd9\u6735\u4e09\u8272\u5947\u82b1\uff0c\u773c\u76f8\u53cd\u800c\u4e00\u6ef4\u6ef4\u7684\u6389\u4e0b\u6765\uff1a \u5389\u590d\u751f\u5413\u5f97\u624b\u8db3\u65e0\u63aa\uff0c\u9053\uff1a\u201c\u662f\u6211\u8bf4\u9519\u4e86\u8bdd\u4e48\uff1f\u652f\u59d0\u59d0\uff0c\u4f60\u3001\u4f60\u4e0d\u6b22\u559c\u6211\u4e48\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4e0d\uff0c\u8fd9\u4e16\u4e0a\u53ea\u6709\u4f60\u4e00\u4e2a\u4eba\u5bf9\u6211\u662f\u5982\u6b64\u4e4b\u597d\uff0c\u6211\u662f\u6b22\u559c\u5f97\u6d41\u6cea\u4e86\u3002\u201d \u5389\u590d\u751f\u9053\uff1a\u201c\u8fd9\u5c31\u597d\u4e86\uff0c\u5509\uff0c\u4e0d\u5bf9\uff0c\u4f60\u4e0d\u50cf\u662f\u6b22\u559c\u800c\u6d41\u6cea\u5462\uff01\u4f60\u7709\u5934\u6df1\u9501\uff0c\u4e00\u5b9a\u662f\u6709\u4ec0 \u4e48\u5fc3\u4e8b\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u8d39\u5c3d\u5fc3\u673a\u7ed9\u6211\u5f04\u6765\u4e86\u8fd9\u6735\u82b1\uff0c\u53ef\u60dc\u6211\u8981\u6765\u4e5f\u6ca1 \u6709\u4ec0\u4e48\u7528\u5904\u4e86\uff01\u201d \u3000\u3000\u5389\u590d\u751f\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u3001\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u51c4\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd1 \u65e5\u7ec3\u529f\u7684\u65f6\u5019\uff0c\u5df2\u6709\u5f81\u5146\uff0c\u53ea\u6015\u3001\u53ea\u6015\u6211\u7684\u5927\u9650\u5230\u4e86\u3002\u201d\u5389\u590d\u751f\u9762\u8272\u82cd\u81ea\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a \u201c\u8d70\u706b\u5165\u9b54\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ece\u7ec3\u529f\u6240\u611f\u5230\u7684\u5f81\u5146\u770b\u6765\uff0c\u8fdf\u5219\u4e00\u5e74\uff0c\u5c11\u5219\u4e09\u6708\uff0c \u6211\u7ec8\u662f\u96be\u9003\u8d70\u706b\u5165\u9b54\u4e4b\u52ab\uff01\u201d \u3000\u3000\u539f\u6765\u4fee\u7ec3\u90aa\u6d3e\u5185\u529f\u7684\u4eba\uff0c\u529f\u529b\u6108\u589e\uff0c\u5371\u673a\u6108\u5927\uff0c\u5230\u4e86\u67d0\u4e00\u5173\u5934\uff0c\u4fbf\u8981\u906d\u9022\u201c\u8d70\u706b\u5165 \u9b54\u201d\u4e4b\u52a8\uff0c\u91cd\u5219\u6bd9\u547d\uff0c\u8f7b\u5219\u534a\u8eab\u4e0d\u9042\uff0c\u6210\u4e86\u5e9f\u4eba\u3002\u4e0d\u8fc7\uff0c\u82e5\u80fd\u51b2\u7834\u6b64\u5173\uff0c\u6b66\u529f\u4fbf\u53ef\u4ee5\u6709 \u767b\u5cf0\u9020\u6781\u7684\u5e0c\u671b\u3002\u5f53\u5e74\u91d1\u4e16\u9057\u5c31\u66fe\u9669\u906d\u201c\u8d70\u706b\u5165\u9b54\u201d\u4e4b\u52ab\uff0c\u540e\u6765\u5f97\u5510\u6653\u6f9c\u6388\u4ee5\u6b63\u5b97\u7684\u5185 \u529f\u5fc3\u6cd5\uff0c\u53c8\u5f97\u4e86\u4e54\u5317\u6e9f\u7684\u6b66\u529f\u79d8\u7c4d\uff0c\u7ec3\u6210\u4e86\u6b63\u90aa\u5408\u4e00\u7684\u5185\u529f\uff0c\u8fd9\u624d\u9003\u8fc7\u6b64\u96be\u7684\u3002 \u3000\u3000\u5389\u590d\u751f\u5446\u4e86\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u65e2\u7136\u6709\u4e86\u8d70\u706b\u5165\u9b54\u4e4b\u5146\uff0c\u90a3\u5c31\u66f4\u5e94\u8be5\u4ece\u901f\u79bb\u5f00\u6b64 \u5730\u4e86\u3002\u6211\u4e0d\u4fe1\u5929\u4e0b\u4e4b\u5927\uff0c\u5c31\u6ca1\u6709\u80fd\u52a9\u59d0\u59d0\u8131\u96be\u4e4b\u4eba\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u6709\u662f\u6709\u7684\uff0c\u4f46\u4e5f\u53ea\u662f\u6709\u9650\u51e0\u4eba\u3002\u8fd9\u51e0\u4eba\u6216\u8005\u662f\u6b63\u6d3e\u4e2d\u9876\u513f\u5c16\u513f\u7684\u4eba \u7269\uff0c\u6216\u8005\u662f\u548c\u6211\u6709\u51a4\u4ec7\u7684\u4eba\u3002\u6211\u5f53\u5e74\u4e00\u5ff5\u4e4b\u5dee\uff0c\u53ea\u60f3\u7ee7\u627f\u5389\u7956\u5e08\u7684\u9057\u5fd7\u3002\u5728\u6b66\u6797\u4e2d\u5f02\u519b \u7a81\u8d77\uff0c\u4e0e\u6b63\u6d3e\u4e89\u96c4\uff0c\u505a\u4e86\u9b54\u6559\u6559\u4e3b\uff0c\u5f04\u5f97\u4eba\u4eba\u628a\u6211\u5f53\u5996\u90aa\uff0c\u6211\u8fd8\u6709\u4f55\u9762\u76ee\u5411\u6b63\u6d3e\u4e2d\u7684\u6b66 \u6797\u5b97\u5e08\u6c42\u52a9\uff1f\u4f60\u662f\u77e5\u9053\u6211\u7684\u813e\u6c14\u7684\uff0c\u6211\u662f\u5b81\u53ef\u5316\u9aa8\u626c\u7070\uff0c\u4e5f\u51b3\u4e0d\u80fd\u5931\u4e86\u8fd9\u4e2a\u9762\u5b50\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u77e5\u9053\u5979\u6240\u8bf4\u7684\u51e0\u4e2a\u4eba\uff0c\u8bf4\u5f97\u66f4\u786e\u5207\u4e9b\uff0c\u90a3\u5c31\u662f\u5f53\u4eca\u6b66\u6797\u4e2d\u56db\u4e2a\u9876\u513f\u5c16\u513f\u7684\u4eba \u7269\uff0c\u5ce8\u5d4b\u6d3e\u7684\u91d1\u5149\u5927\u5e08\uff0c\u5c11\u6797\u6d3e\u7684\u75db\u7985\u4e0a\u4eba\uff0c\u5929\u5c71\u6d3e\u7684\u5510\u6653\u6f9c\uff0c\u8fd8\u6709\u4e00\u4e2a\u5219\u662f\u5979\u6240\u8bf4\u7684 \u6709\u51a4\u4ec7\u7684\u91d1\u4e16\u9057\u4e86\u3002 \u3000\u3000\u5389\u590d\u751f\u6124\u7136\u9053\uff1a\u201c\u4e54\u5317\u6e9f\u7684\u6b66\u529f\u79d8\u7c4d\u672c\u662f\u6211\u5bb6\u4e4b\u7269\uff0c\u53ef\u60dc\u5df2\u843d\u5728\u91d1\u4e16\u9057\u4e4b\u624b\u3002\u5509\uff0c \u9664\u975e\u2026\u2026\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u9664\u975e\u662f\u6c42\u91d1\u4e16\u9057\uff1f\u4e0d\uff0c\u6211\u4e0d\u613f\u4f60\u4e3a\u6211\u4e4b\u6545\u800c\u59d4\u5c48\u4e86\u81ea\u5df1\uff01\u201d \u539f\u6765\u5389\u590d\u751f\u4e0e\u91d1\u4e16\u9057\u51e0\u6b21\u76f8\u9047\uff0c\u5176\u4e2d\u6709\u4e24\u6b21\u8fd8\u66fe\u4ea4\u624b\uff0c\u4ed6\u4e5f\u9690\u9690\u611f\u89c9\u5230\u91d1\u4e16\u9057\u5bf9\u4ed6\u4f3c\u6709 \u4e00\u79cd\u201c\u7231\u5c4b\u53ca\u4e4c\u201d\u4e4b\u60c5\uff0c\u4e0d\u8fc7\uff0c\u5389\u590d\u751f\u662f\u4e00\u4e2a\u611f\u60c5\u504f\u6fc0\u7684\u4eba\uff0c\u59cb\u7ec8\u89c9\u5f97\u91d1\u4e16\u9057\u5bf9\u4e0d\u4f4f\u4ed6 \u7684\u59d0\u59d0\uff0c\u56e0\u800c\u4e5f\u5c31\u603b\u662f\u5bf9\u91d1\u4e16\u9057\u6000\u7740\u6545\u610f\u3002 \u3000\u3000\u5389\u590d\u751f\u9053\uff1a\u201c\u82e5\u7136\u53ea\u662f\u4e3a\u6211\u81ea\u5df1\uff0c\u6211\u662f\u51b3\u8ba1\u4e0d\u4f1a\u6c42\u91d1\u4e16\u9057\u7684\uff0c\u4f46\u82e5\u662f\u4e3a\u4e86\u59d0\u59d0\uff0c\u6211 \u4ec0\u4e48\u59d4\u5c48\u4e5f\u53d7\u5f97\u4e86\u3002\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u4e0d\u613f\u610f\u4f60\u8fd9\u6837\u3002\u9664\u975e\u54b1\u4eec\u6709\u672c\u4e8b\u80fd\u4ece\u4ed6\u624b \u4e2d\u593a\u56de\u4e54\u5317\u6e9f\u7684\u6b66\u529f\u79d8\u7c4d\u3002\u5426\u5219\u6211\u51b3\u4e0d\u80fd\u8ba9\u4f60\u6c42\u4ed6\u3002\u201d\u5389\u590d\u751f\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u96be\u4e86\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4f55\u51b5\u4f60\u7eb5\u6709\u6b64\u5fc3\uff0c\u4f60\u53c8\u5230\u54ea\u91cc\u53bb\u627e\u4ed6\u5c38\u8981\u77e5\u91d1\u4e16\u9057\u4e0d\u6bd4\u75db\u7985\u4e0a\u4eba\uff0c \u8981\u627e\u75db\u7985\u4e0a\u4eba\uff0c\u5230\u5c11\u6797\u5bfa\u4fbf\u53ef\u627e\u7740\uff0c\u91d1\u4e16\u9057\u5374\u662f\u884c\u8e2a\u65e0\u5b9a\u7684\u3002\u5389\u590d\u751f\u53f9\u4e86\u53e3\u6c14\uff0c\u9ed8\u7136\u4e0d \u8bed\u3002\u8c37\u4e2d\u83b2\u5728\u5bc6\u5ba4\u4e4b\u4e2d\uff0c\u542c\u5230\u6b64\u5904\uff0c\u5374\u604d\u7136\u5927\u609f\uff0c\u4e0d\u7981\u8d77\u4e86\u4e00\u4e1d\u601c\u60af\u4e4b\u60c5\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5fc3\u9053\uff1a\u201d\u539f\u6765\u5979\u5c61\u6b21\u5baa\u593a\u6211\u7684\u4e3a\u53e3\u767e\u548c\u9f99\u529b\u79d8\u85cf\uff0c\u5176\u4e2d\u6709\u6b64\u7f18\u6545\uff0c\u662f\u4e3a\u4e86\u8981 \u89e3\u9664\u5979\u8d70\u706b\u5165\u9b54\u4e4b\u52ab\u3002\u53ea\u662f\u4e8b\u5230\u5982\u4eca\uff0c\u5373\u4f7f\u6211\u628a\u6b63\u5b97\u7684\u5185\u529f\u5fc3\u6cd5\u4f20\u6388\u4e0e\u5979\uff0c\u4ea6\u5df2\u8fdf\u4e86\u3002\u201d \u3000\u3000\u8981\u77e5\u6539\u4e60\u4e00\u79cd\u95e8\u5f84\u622a\u7136\u4e0d\u540c\u7684\u5185\u529f\u3001\u51b3\u975e\u4e00\u5e74\u534a\u8f7d\u53ef\u80fd\u89c1\u6548\u3002\u800c\u4ee5\u8c37\u4e2d\u83b2\u73b0\u5728\u7684\u529f \u529b\uff0c\u53c8\u51b3\u4e0d\u80fd\u52a9\u5929\u9b54\u6559\u4e3b\u9003\u8fc7\u6b64\u52ab\uff0c\u8c37\u4e2d\u83b2\u6b64\u65f6\u5bf9\u5929\u9b54\u6559\u4e3b\u7684\u6076\u610f\u5df2\u6d88\u51cf\u51e0\u5206\uff0c\u4ed4\u7ec6\u4e00 \u60f3\uff0c\u5929\u9b54\u6559\u4e3b\u867d\u8bf4\u662f\u884c\u4e8b\u90aa\u50fb\uff0c\u4e5f\u66fe\u5c61\u6b21\u4e0e\u6b63\u6d3e\u4e3a\u96be\uff0c\u4f46\u82e5\u8bf4\u5230\u5927\u5978\u5927\u6076\u8fd8\u6ca1\u6709\u3002\u56e0\u800c \u8c37\u4e2d\u83b2\u4e5f\u5c31\u4e0d\u7981\u8d77\u4e86\u4e00\u4e1d\u601c\u60af\u4e4b\u60c5\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u5929\u9b54\u6559\u4e3b\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5f1f\u5f1f\uff0c\u6211\u591a\u8c22\u4f60\u7684\u597d\u610f\uff0c\u4eca\u540e\u6211 \u662f\u4e0d\u80fd\u548c\u4f60\u4e00\u9053\u4e86\uff0c\u4f60\u5feb\u5feb\u8d70\u5427\u3002\u8981\u4e0d\u7136\uff0c\u7ed9\u6211\u7684\u5e08\u7236\u77e5\u9053\u4f60\u5728\u6b64\u5904\uff0c\u53ea\u6015\u5979\u4f1a\u5c06\u4f60\u96be \u4e3a\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u5ffd\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u6211\u6709\u51e0\u53e5\u5fc3\u91cc\u7684\u8bdd\uff0c\u4f60\u8ba9\u6211\u8bf4\u5b8c\u4e86\u518d\u8d70\uff0c\u884c\u5417\uff1f\u201d\u5929\u9b54\u6559\u4e3b \u7b97\u7b97\u65f6\u95f4\uff0c\u5fc3\u60f3\uff1a\u201c\u5e08\u7236\u6b63\u5728\u9759\u5ba4\u7ec3\u529f\uff0c\u5728\u8fd9\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u5927\u7ea6\u4e0d\u4f1a\u5230\u6765\u627e\u6211\u3002\u201d\u4fbf\u9053\uff1a \u201c\u597d\u90a3\u4f60\u5c31\u5feb\u70b9\u8bf4\u5427\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u9053\uff1a\u201c\u54b1\u4eec\u4ece\u524d\u60f3\u4e0e\u6b63\u6d3e\u4e89\u96c4\uff0c\u73b0\u5728\u60f3\u6765\uff0c\u867d\u662f\u4e0d\u81ea\u91cf\u529b\uff0c\u6bd5\u7adf\u4e5f\u8fd8\u4e0d\u662f\u7f6a \u5b7d\uff0c\u4f46\u5018\u82e5\u5bb3\u53ca\u65e0\u8f9c\u767e\u59d3\uff0c\u90a3\u5c31\u662f\u5927\u5927\u7684\u7f6a\u5b7d\u4e86\u3002\u59d0\u59d0\uff0c\u6211\u548c\u4f60\u4e00\u6837\uff0c\u4e0d\u4fe1\u5584\u6076\u679c\u62a5\u4e4b \u8bf4\uff0c\u4f46\u7f6a\u5b7d\u6df1\u91cd\uff1a\u603b\u662f\u4e8e\u5fc3\u96be\u5b89\u3002\u54b1\u4eec\u53c8\u4f55\u82e6\u4ee5\u6709\u9650\u7684\u5c81\u6708\uff0c\u53bb\u5e72\u90a3\u4e9b\u4ee4\u81ea\u5df1\u826f\u5fc3\u96be\u8fc7 \u7684\u4e8b\u60c5\uff1f\u59d0\u59d0\uff0c\u4f60\u53ef\u602a\u6211\u76f4\u8a00\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u6211\u4e0d\u602a\u4f60\uff0c\u4f60\u8bf4\u5427\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u79bb\u95f4\u4f60\u4eec\u5e08\u5f92\uff0c\u4f46\u4f60\u5e08\u7236\u7684\u884c\u4e3a\uff0c\u6211\u5374\u5b9e\u662f\u4e0d\u6562\u8d5e\u540c\uff0c\u5979\u4f3c\u4e4e \u662f\u5728\u5343\u65b9\u767e\u8ba1\uff0c\u4fc3\u4f7f\u6606\u5e03\u5170\u56fd\u4e0e\u9a6c\u8428\u513f\u56fd\u5f00\u6218\uff0c\u8fd9\u4e00\u6765\u8981\u5bb3\u6b7b\u591a\u5c11\u65e0\u8f9c\u767e\u59d3\uff1f\u963b\u59d0\uff0c\u6709 \u4e00\u4ef6\u4e8b\u6211\u8981\u544a\u8bc9\u4f60\uff0c\u8fd9\u6b21\u6211\u5728\u9014\u4e2d\u66fe\u9047\u4e0a\u9a6c\u8428\u513f\u56fd\u7684\u65b0\u738b\u5510\u52aa\u73e0\u7a46\u4e0e\u6c5f\u6d77\u5929\u3002 \u3000\u3000\u2026\u2026\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u77e5\u9053\u4e86\uff0c\u4ed6\u4eec\u4e24\u4eba\u6628\u665a\u6765\u8fc7\uff0c\u4f60\u600e\u7684\u5ffd\u7136\u8bf4\u5230\u4ed6\u4eec\u8eab \u4e0a\uff1f\u5929\u9b54\u6559\u4e3b\u5c1a\u672a\u77e5\u9053\u6628\u665a\u548c\u6c5f\u6d77\u5929\u4e00\u540c\u6765\u7684\u5e76\u975e\u5510\u52aa\u73e0\u7a46\uff0c\u800c\u662f\u5510\u52a0\u6e90\u3002 \u3000\u3000\u5389\u590d\u751f\u6709\u70b9\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u600e\u7684\u8dd1\u5f97\u6bd4\u6211\u7684\u91d1\u6bdb\u72e1\u8fd8\u5feb\uff1f\u201d\u4f46\u6b64\u65f6\u4ed6\u5df2\u65e0\u5fc3\u67e5\u7a76\u3002 \u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5510\u52aa\u73e0\u7a46\u662f\u6765\u8981\u6c42\u548c\u89e3\u7684\uff0c\u662f\u5426\u80fd\u5316\u5e72\u6208\u800c\u4e3a\u7389\u5e1b\uff0c\u90a3\u5c31\u5f97\u5168\u770b\u6606\u5e03\u5170\u56fd \u7684\u56fd\u738b\u4e86\uff0c\u4f46\u6606\u5e03\u5170\u56fd\u7684\u56fd\u738b\u53c8\u662f\u542c\u4f60\u5e08\u7236\u6446\u5e03\u7684\u3002\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u8981\u6211 \u529d\u529d\u5e08\u7236\uff1f\u4f60\u8fd8\u4e0d\u77e5\u9053\u6211\u5e08\u7236\u7684\u813e\u6c14\u5462\uff0c\u4ed6\u662f\u51b3\u4e0d\u4f1a\u542c\u4eba\u529d\u544a\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u529d\u544a\uff0c\u6700\u5c11\u4e5f\u4e0d\u8981\u52a9\u7ea3\u4e3a\u8650\u3002\u59d0\u59d0\uff0c\u5982\u4eca\u6211\u53ef\u8981\u5bf9\u4f60\u8bf4\u51fa\u6211\u5fc3 \u5e95\u7684\u8bdd\u4e86\u3002\u59d0\u59d0\uff0c\u4f60\u559c\u6b22\u6211\u5417\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u559c\u6b22\u53c8\u600e\u6837\uff0c\u4e0d\u559c\u6b22\u53c8\u600e\u6837\uff1f\u201d\u5389\u590d \u751f\u9053\uff0c\u201c\u82e5\u662f\u4e0d\u559c\u6b22\uff0c\u8fd9\u4e9b\u8bdd\u6211\u4e5f\u5c31\u4e0d\u987b\u8bf4\u4e86\u3002\u201d\u5929\u9b54\u6559\u4e3b\u559f\u7136\u53f9\u9053\uff1a\u201c\u5509\uff0c\u6211\u53ea\u6015\u62d6 \u7d2f\u4e86\u4f60\u3002\u201d\u90a3\u610f\u601d\u5373\u662f\u8bf4\u559c\u6b22\u4ed6\u4e86\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u95f4\uff0c\u4e24\u4eba\u7684\u624b\u638c\u5df2\u662f\u7d27\u7d27\u76f8\u63e1\uff0c\u5389\u590d\u751f\u67d4\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u59d0\u59d0\uff0c\u8d70\u5427\u3002\u54b1\u4eec\u5230\u6d77\u5916\u4e00\u4e2a\u5b64\u5c9b\u53bb\uff0c\u5c9b\u4e0a\u6709\u56db\u65f6\u4e0d\u8c22\u4e4b\u82b1\uff0c\u516b\u8282\u957f\u9752\u4e4b\u8349\uff0c\u5c31 \u53ea\u662f\u54b1\u4eec\u4e24\u4e2a\u4eba\uff0c\u5c18\u4e16\u7684\u7eb7\u4e89\uff0c\u4ece\u6b64\u518d\u4e5f\u4e0e\u54b1\u4eec\u65e0\u6d89\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u4f60\u5012\u60f3\u5f97\u7f8e\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u6211\u82e5\u906d\u53d7\u4e86\u8d70\u706b\u5165\u9b54\u4e4b\u52ab\uff0c\u5373\u4f7f\u4fa5\u5e78\u4e0d \u6b7b\uff0c\u4e5f\u8981\u53d8\u6210\u6b8b\u5e9f\uff1f\u201d \u3000\u3000\u5389\u590d\u751f\u9053\uff1a\u201c\u90a3\u6211\u5c31\u4e00\u751f\u670d\u4f8d\u4f60\uff0c\u6211\u7ed9\u4f60\u9020\u4e00\u8f86\u5c0f\u8f66\uff0c\u6e05\u6668\u7684\u65f6\u5019\uff0c\u6211\u548c\u4f60\u53bb\u770b\u6d77 \u4e0a\u65e5\u51fa\uff0c\u770b\u90a3\u4e91\u5f71\u6ce2\u5149\u662f\u600e\u6837\u53d8\u5316\u65e0\u7a77\u3002 \u3000\u3000\u9ec4\u660f\u7684\u65f6\u5019\uff0c\u770b\u6d77\u9e25\u4ece\u665a\u971e\u4e4b\u4e2d\u56de\u5de2\uff0c\u542c\u90a3\u6d9b\u58f0\u9e1f\u8bed\u5408\u594f\u6210\u4e00\u66f2\u7f8e\u5999\u65e0\u6bd4\u7684\u4ed9\u4e50\u3002 \u665a\u4e0a\uff0c\u6211\u548c\u4f60\u770b\u6708\u4eae\uff0c\u6570\u661f\u661f\uff0c\u6211\u7ed9\u4f60\u6458\u4e00\u6735\u5e26\u9732\u6c34\u7684\u591c\u767e\u5408\u82b1\uff0c\u63d2\u5728\u4f60\u7684\u9b13\u8fb9\uff0c\u8ba9\u4f60 \u505a\u4e00\u4e2a\u751c\u871c\u7684\u68a6\u3002 \u3000\u3000\u4e00\u89c9\u7761\u5230\u5929\u4eae\uff0c\u4e8e\u662f\u54b1\u4eec\u53c8\u53bb\u770b\u5927\u6d77\u6f6e\u751f\uff0c\u9526\u9cde\u6e38\u6cf3\uff01\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u542c\u5f97\u60a0\u7136\u795e\u5f80\uff0c\u7b11\u9053\uff1a\u201c\u542c\u4f60\u8fd9\u4e48\u4e00\u8bf4\uff0c\u54b1\u4eec\u7b80\u76f4\u662f\u5728\u8fc7\u795e\u4ed9\u822c\u7684\u751f\u6d3b\u4e86\u3002 \u8fd9\u6837\u7684\u65e5\u5b50\uff0c\u5373\u4f7f\u53ea\u8fc7\u4e00\u5929\u90a3\u4e5f\u662f\u6b7b\u4e5f\u503c\u5f97\u4e86\u3002\u201d\u5389\u590d\u751f\u5927\u559c\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u7b54\u5e94\u4e86\uff1f\u201d \u5929\u9b54\u6559\u4e3b\u5ffd\u5730\u53c8\u53f9\u4e86\u53e3\u6c14\u8bf4\u9053\uff1a\u201c\u53ea\u6015\u6211\u96be\u4ee5\u4eab\u53d7\u5982\u6b64\u6e05\u798f\uff0c\u54b1\u4eec\u8d70\u5f97\u5230\u7684\u5730\u65b9\uff0c\u522b\u4eba \u4e5f\u8d70\u5f97\u5230\uff0c\u6211\u53c8\u4f55\u82e6\u8fde\u7d2f\u4f60\u5462\uff1f\u201d \u3000\u3000\u5389\u590d\u751f\u9053\uff1a\u201c\u4f60\u662f\u6015\u4f60\u7684\u5e08\u7236\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4e5f\u8fd8\u6709\u522b\u4eba\u3002\u201d\u5389\u590d\u751f\u9053\uff1a\u201c\u54e6\uff0c \u6211\u8fd8\u6ca1\u6709\u544a\u8bc9\u4f60\u4e00\u6869\u4e8b\u60c5\uff0c\u6587\u5ef7\u58c1\u8fd9\u53ae\u4e1a\u5df2\u6b7b\u4e86\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5927\u611f\u610f\u5916\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u4eca\u4e4b\u4e16\uff0c\u6b66\u529f\u80fd\u80dc\u8fc7\u4ed6\u7684\u4e5f\u4e0d\u8fc7\u6709\u9650\u51e0\u4eba\uff0c\u4ed6\u662f\u600e \u4e48\u6b7b\u7684\uff1f\u201d\u5389\u590d\u751f\u9053\uff1a\u201c\u4ed6\u5728\u7075\u9e6b\u5cf0\u4e0b\uff0c\u4e0e\u4e91\u743c\u6076\u6597\uff0c\u811a\u4e0b\u51b0\u6cb3\u5ffd\u7136\u88c2\u5f00\uff0c\u4ed6\u6389\u8fdb\u51b0\u6cb3 \u6b7b\u4e86\u3002\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a \u3000\u3000\u201c\u4f60\u4eb2\u773c\u89c1\u7684\uff1f\u201d\u5389\u590d\u751f\u9053\uff1a\u201c\u7389\u73b2\u73d1\u8bf4\u5f97\uff0c\u60f3\u5979\u4e0d\u4f1a\u9a97\u6211\u3002\u59d0\u59d0\uff0c\u6211\u4e0d\u662f\u5e78\u707e\u4e50 \u7978\uff0c\u4f46\u6211\u4e5f\u4e0d\u60f3\u9690\u7792\u6211\u7684\u5fc3\u60c5\uff0c\u6211\u4e00\u5411\u8ba8\u538c\u8fd9\u53ae\u3002 \u3000\u3000\u59d0\u59d0\uff0c\u4f60\u662f\u5728\u4e3a\u4ed6\u4f24\u5fc3\u4e48\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u6211\u4ece\u6765\u4e5f\u6ca1\u6709\u559c\u6b22\u8fc7\u4ed6\uff0c\u4f46\u4ed6\u6bd5\u7adf\u4e5f\u8fd8\u662f\u6211\u7684\u4e00\u4e2a\u670b\u53cb\u3002\u201d\u5389\u590d\u751f \u9053\uff1a\u201c\u90a3\u4ee5\u5373\u4f7f\u662f\u6587\u5ef7\u58c1\u5728\u751f\u2014\u2014\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u6211\u4e5f\u4e00\u6837\u7684\u53ea\u662f\u559c\u6b22\u6211\u7684\u5f1f\u5f1f\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u559c\u6b22\u5f97\u5982\u540c\u732a\u516b\u6212\u5403\u4e86\u4eba\u53c2\u679c\uff0c\u516b\u4e07\u56db\u5343\u4e2a\u6bdb\u5b54\uff0c\u65e0\u4e00\u4e2a\u6bdb\u5b54\u4e0d\u8212\u670d\uff0c\u7d27\u7d27 \u5730\u63e1\u7740\u5929\u9b54\u6559\u4e3b\u7684\u53cc\u624b\uff0c\u8bf4\u9053\uff0c\u201c\u90a3\u4e48\u4f60\u8fd8\u5728\u8e0c\u8e87\u4ec0\u4e48\uff0c\u8d70\u5427\u3002\u91d1\u6bdb\u72e1\u5c31\u7b49\u5728\u5916\u8fb9\uff0c\u4e09 \u65e5\u4e4b\u540e\uff0c\u54b1\u4eec\u5c31\u53ef\u4ee5\u5230\u4e1c\u6d77\u4e4b\u6ee8\u4e86\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5fc3\u4e8b\u5982\u6f6e\uff0c\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u6211\u672c\u60f3\u501f\u52a9\u5e08\u7236\u4e4b\u529b\uff0c\u4ece\u91d1\u4e16\u9057\u624b\u4e2d\uff0c\u593a\u56de\u4e54\u5317 \u6e9f\u90a3\u672c\u6b66\u529f\u79d8\u7c4d\uff0c\u4f46\u4ece\u6628\u665a\u6211\u5e08\u7236\u5bf9\u6c5f\u6d77\u5929\u7684\u4e00\u6218\u770b\u6765\uff0c\u5979\u8fde\u6c5f\u6d77\u5929\u4e5f\u672a\u5fc5\u6253\u5f97\u8fc7\uff0c\u66f4 \u4e0d\u7528\u8bf4\u91d1\u4e16\u9057\u4e86\u3002\u6587\u5ef7\u58c1\u5982\u4eca\u53c8\u5df1\u6b7b\u4e86\uff0c\u5373\u4f7f\u4e0d\u6b7b\uff0c\u4ed6\u4e5f\u65e0\u6b64\u529f\u529b\u52a9\u6211\u8131\u96be\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u8d70\u5427\uff0c\u54b1\u4eec\u8d70\u5230\u5929\u6daf\u6d77\u89d2\uff0c\u8d70\u5230\u4f60\u5e08\u7236\u627e\u4e0d\u5230\u7684\u5730\u65b9\u3002\u5979\u53c8\u4e0d \u4f1a\u822a\u6d77\uff0c\u51b3\u8ba1\u96be\u4ee5\u6765\u627e\u54b1\u4eec\u7684\u9ebb\u70e6\uff01\u201d\u5929\u9b54\u6559\u4e3b\u5fc3\u610f\u5df2\u51b3\uff0c\u5a07\u58f0\u7b11\u9053\uff1a\u201c\u597d\u5427\uff0c\u5f1f\u5f1f\uff0c \u6211\u542c\u4f60\u7684\u8bdd\uff0c\u4eca\u540e\u6211\u7684\u4e00\u751f\u5c31\u4ea4\u6258\u4f60\u4e86\uff01\u201d \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u771f\u662f\u592a\u4e0d\u5de7\u4e86\uff0c\u6211\u521a\u521a\u56de\u6765\uff0c\u4f60\u4eec\u5c31\u8981\u8d70\u4e86\uff1f\u201d \u7b11\u58f0\u4e2d\u90a3\u4eba\u5df2\u662f\u63a8\u95e8\u9762\u5165\u3002 \u3000\u3000\u5389\u590d\u751f\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u6587\u5ef7\u58c1\uff0c\u4f60\uff0c\u4f60\u2014\u2014\u201d\u6587\u5ef7\u58c1\u7b11\u9053\uff1a\u201c\u5c0f\u5389\uff0c\u4f60\u9ad8\u5174\u5f97 \u592a\u65e9\u4e86\uff0c\u6211\u8fd8\u6ca1\u6709\u6b7b\u54e9\uff01\u6211\u6587\u67d0\u4eba\u6709\u901a\u5929\u5f7b\u5730\u4e4b\u80fd\uff0c\u533a\u533a\u51b0\u6cb3\uff0c\u5c82\u80fd\u5948\u6211\u4f55\u54c9\uff1f\u201d \u3000\u3000\u539f\u6765\u6587\u5ef7\u58c1\u4ed7\u7740\u5185\u529f\u5df2\u7ec3\u5230\u201c\u4e09\u8c61\u5f52\u5143\u201d\u5883\u754c\uff0c\u5728\u51b0\u6cb3\u4e2d\u95ed\u4e86\u547c\u5438\uff0c\u8fc7\u4e86\u4e00\u65e5\u4e00\u591c\uff0c \u88ab\u6fc0\u6d41\u51b2\u5230\u5cb8\u8fb9\uff0c\u7ed9\u82a6\u82c7\u7eca\u4f4f\uff0c\u6070\u597d\u6709\u4e2a\u6e14\u592b\u7ecf\u8fc7\uff0c\u9042\u628a\u4ed6\u6551\u4e86\u8d77\u6765\uff0c\u5979\u81ea\u5938\u6709\u201c\u901a\u5929 \u5f7b\u5730\u201d\u4e4b\u80fd\uff0c\u5176\u5b9e\u4ed6\u90a3\u65f6\u4e5f\u65e9\u5df2\u51bb\u5f97\u534a\u6b7b\uff0c\u53ea\u5269\u5fc3\u5934\u4e00\u53e3\u6c14\u4e86\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u4e5f\u771f\u662f\u7684\uff0c\u600e\u7684\u4e00\u89c1\u4e86\u9762\u603b\u8981\u5435\u5634\uff1f\u5c0f\u5389\u5e76\u6ca1\u76fc\u4f60\u6b7b\uff0c \u4f60\u522b\u8bef\u4f1a\u3002\u6587\u5148\u751f\uff0c\u4f60\u5927\u96be\u4e0d\u6b7b\uff0c\u53ef\u559c\u53ef\u8d3a\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6587\u5148\u751f\uff0c\u4f60\u662f\u6b7b\u662f\u6d3b\uff0c\u90fd\u4e0d\u5173\u6211\u7684\u4e8b\uff0c\u4eca\u540e\u54b1\u4eec\u4e5f\u4e0d\u4f1a\u518d\u5728\u4e00 \u8d77\u4e86\u3002\u59d0\u59d0\uff0c\u8d70\u5427\uff01\u201d \u3000\u3000\u6587\u5ef7\u58c1\u6321\u4f4f\u95e8\u53e3\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u5389\uff0c\u4f55\u5fc5\u8fd9\u6837\u5306\u5fd9\uff1f\u4f60\u8981\u548c\u6559\u4e3b\u4e0a\u54ea\u513f\u53bb\uff1f\u201d\u5389\u590d\u751f \u6012\u9053\uff1a\u201c\u4e0d\u7528\u4f60\u7ba1\uff0c\u6587\u5ef7\u58c1\u4f60\u6562\u62e6\u963b\u6211\u4e48\uff1f\u201d\u6587\u5ef7\u58c1\u7b11\u9053\uff1a\u201c\u5c0f\u5389\uff0c\u4f60\u8981\u8d70\u4f60\u5c31\u4e8c\u4e2a\u4eba \u8d70\u3002\u4e0d\u7528\u4f60\u66ff\u6559\u4e3b\u51fa\u4e3b\u610f\u3002\u201d\u5389\u590d\u751f\u6c14\u5f97\u9762\u8272\u94c1\u9752\uff0c\u559d\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c \u7adf\u6562\u4ee5\u4e0b\u72af\u4e0a\uff0c\u6263\u7559\u6559\u4e3b\u4e48\uff1f\u201d \u3000\u3000\u6587\u5ef7\u58c1\u8c08\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u662f\u5bf9\u6559\u4e3b\u4e00\u756a\u597d\u610f\uff0c\u4f60\u624d\u662f\u60f3\u4e58\u6559\u4e3b\u4e4b\u5371\u3002\u201c\u5929\u9b54\u6559\u4e3b\u9053\uff1a \u201c\u6587\u5148\u751f\uff0c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u6587\u5ef7\u58c1\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u7684\u8bdd\u6211\u90fd\u5df2\u542c\u5230\u4e86\uff0c\u6559\u4e3b\uff0c\u4f60\u6709\u8d70\u706b\u5165\u9b54\u4e4b\u5371\uff0c\u4e3a\u4f55\u4e0d\u4e0e\u6211\u65e9 \u8bf4\uff1f\u201d\u5389\u590d\u751f\u51b7\u7b11\u9053\uff1a\u201c\u6587\u5ef7\u58c1\uff0c\u96be\u9053\u51ed\u4f60\u90a3\u70b9\u529f\u529b\uff0c\u4f60\u5c31\u53ef\u4ee5\u52a9\u6559\u4e3b\u8131\u96be\u4e48\uff1f\u201d \u3000\u3000\u6587\u5ef7\u58c1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ee5\u6211\u73b0\u5728\u7684\u5185\u529f\u9020\u8be3\uff0c\u7684\u786e\u8fd8\u672a\u80fd\u52a9\u6559\u4e3b\u8131\u96be\u5ea6\u52ab\uff0c\u6240\u4ee5\u6211\u624d \u8981\u4f60\u6682\u4e14\u7559\u4e0b\uff0c\u54b1\u4eec\u5546\u91cf\u5546\u91cf\u3002\u201d\u5389\u590d\u751f\u9053\uff1a\u201c\u5546\u91cf\u4ec0\u4e48\uff1f\u201d\u6587\u5ef7\u58c1\u9053\uff1a\u201c\u6211\u6709\u529e\u6cd5\u52a9 \u6559\u4e3b\u9003\u8fc7\u6b64\u96be\uff0c\u4f46\u53ea\u8981\u4f60\u4eec\u4f9d\u4ece\u6211\u4e09\u6837\u4e8b\u60c5\uff0c\u4f60\u53ef\u613f\u610f\uff1f\u201d\u5389\u590d\u751f\u534a\u4fe1\u534a\u7591\u8bf4\u9053\uff1a\u201c\u662f \u54ea\u4e09\u4f6f\u4e8b\u60c5\uff0c\u4f60\u65e6\u8bf4\u8bf4\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u9053\uff1a\u201c\u7b2c\u4e00\u4ef6\uff0c\u4f60\u628a\u4f60\u7956\u4f20\u7684\u6b66\u5b66\u79d8\u5178\u5c3d\u6570\u7ed9\u6211\uff0c\u6211\u77e5\u9053\u4f60\u6240\u5f97\u4e0d\u5168\uff0c\u4f46\u52a0 \u4e0a\u6211\u7684\u806a\u660e\u624d\u667a\uff0c\u6211\u81ea\u4fe1\u4e0d\u51fa\u4e09\u6708\uff0c\u5c31\u53ef\u4ee5\u7ec3\u6210\u6b63\u90aa\u5408\u4e00\u7684\u5185\u529f\uff0c\u90a3\u65f6\u7531\u6211\u6765\u6551\u6559\u4e3b\uff0c \u5c31\u8f83\u6709\u628a\u63e1\u4e86\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u51b7\u7b11\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u53ea\u662f\u8981\u6211\u7ed9\u4f60\uff1f\u201d\u6587\u5ef7\u58c1\u9053\uff1a\u201c\u4f60\u61c2\u5f97\u4ec0\u4e48\uff1f\u6211\u6240\u4e60\u7684\u5185 \u529f\u7cbe\u6df1\u535a\u5927\uff0c\u8457\u662f\u7531\u6211\u6765\u4f20\u6388\u7ed9\u4f60\uff0c\u4f60\u6700\u5c11\u5f97\u5316\u4e09\u5e74\u529f\u592b\uff0c\u65b9\u80fd\u4e0e\u4f60\u539f\u6765\u7684\u6240\u5b66\u878d\u5408\uff0c \u90a3\u5df2\u662f\u8fdc\u6c34\u4e0d\u6551\u8fd1\u706b\u4e86\uff0c\u6211\u6240\u7ec3\u7684\u4e09\u8c61\u795e\u529f\uff0c\u975e\u6b63\u975e\u90aa\uff0c\u4f46\u6700\u5c11\u6bd4\u4f60\u6240\u5b66\u66f4\u63a5\u8fd1\u5343\u6b63\u6d3e \u5185\u529f\u3002\u201d\u5389\u590d\u751f\u5fc3\u60f3\uff1a\u201c\u6b64\u8bdd\u5012\u4e5f\u4e0d\u5047\u3002\u201d\u5fc3\u610f\u8e0c\u8e87\uff0c\u4e00\u65f6\u83ab\u51b3\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u7b2c\u4e8c\u4ef6\u5462\uff1f\u201d\u6587\u5ef7\u58c1\u9053\uff1a\u201c\u8bf7\u6559\u4e3b\u628a\u90a3\u90e8\u767e\u6bd2\u771f\u7ecf\u4e5f\u4ea4\u4e0e\u6211\u3002\u6211\u53ef \u4ee5\u7528\u4ee5\u6bd2\u653b\u6bd2\u4e4b\u6cd5\uff0c\u914d\u5408\u672c\u8eab\u529f\u529b\uff0c\u8fd9\u6837\u52a9\u4f60\u8131\u96be\u5ea6\u52ab\uff0c\u5c31\u6709\u5341\u4e8c\u5206\u628a\u63e1\u4e86\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u9053\uff1a\u201c\u4ee5\u836f\u7269\u4f5c\u4e3a\u8f85\u52a9\uff0c\u89e3\u9664\u2018\u8d70\u706b\u4eba\u9b54\u2019\uff0c\u636e\u6211\u6240\u77e5\uff0c\u53ea\u6709\u4e00\u4e2a\u524d\u4f8b\uff1a\u4e09 \u767e\u5e74\u524d\uff0c\u4e54\u7956\u5e08\u5728\u672a\u7ec3\u6210\u6b63\u90aa\u5408\u4e00\u7684\u5185\u529f\u4e4b\u524d\uff0c\u66fe\u7528\u8fc7\u8fd9\u4e2a\u6cd5\u5b50\u3002\u4f60\u600e\u4e48\u80fd\u4e0e\u5f53\u5e74\u7684\u4e54 \u7956\u5e08\u76f8\u6bd4\uff1f\u201d \u3000\u3000\u6587\u5ef7\u58c1\u7b11\u9053\uff1a\u201c\u73b0\u5728\u662f\u4e0d\u80fd\u6bd4\uff0c\u6240\u4ee5\u6211\u624d\u8981\u4f60\u7684\u7956\u4f20\u6b66\u529f\u79d8\u5178\uff0c\u4e09\u4e2a\u6708\u540e\u3002\u4e54\u7956\u5e08 \u5f53\u5e74\u505a\u5f97\u5230\u7684\uff0c\u6211\u4e5f\u4e00\u5b9a\u53ef\u4ee5\u505a\u5f97\u5230\u3002\u201d\u5389\u590d\u751f\u5fc3\u5730\u5355\u7eaf\uff0c\u542c\u4ed6\u8bf4\u5f97\u5982\u6b64\u80af\u5b9a\uff0c\u867d\u7136\u5fc3 \u6709\u6240\u7591\uff0c\u4f46\u4ea6\u5df1\u662f\u534a\u7591\u534a\u4fe1\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u6697\u6697\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5012\u6253\u5f97\u5982\u610f\u7b97\u76d8\uff0c\u6b64\u4e8b\u4e0d\u7ba1\u6210\u4e0e\u4e0d\u6210\uff0c\u4f60\u5df2\u628a\u6211\u4e24\u4eba\u7684 \u770b\u5bb6\u672c\u9886\u5168\u9a97\u53bb\u4e86\uff0c\u90a3\u65f6\u6211\u8fd8\u9003\u5f97\u51fa\u4f60\u7684\u638c\u5fc3\u5417\uff1f\u201d\u5f53\u4e0b\u4e0d\u9732\u58f0\u8272\u7684\u53c8\u518d\u95ee\u9053\uff1a\u201c\u7b2c\u4e09 \u4ef6\u5462\uff1f\u201d \u3000\u3000\u6587\u5ef7\u58c1\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6559\u4e3b\uff0c\u60f3\u6211\u6587\u67d0\u4ee5\u5c9b\u4e3b\u4e4b\u5c0a\uff0c\u5728\u6d77\u5916\u4f55\u7b49\u900d\u9065\uff0c\u6211\u7518\u613f \u6765\u5230\u4e2d\u539f\uff0c\u505a\u4f60\u7684\u526f\u624b\uff0c\u6240\u4e3a\u4f55\u6765\uff0c\u60f3\u4f60\u4e5f\u662f\u65e9\u5df2\u660e\u767d\u7684\u4e86\uff01\u54b1\u4eec\u7684\u4e8b\u803d\u6401\u4e86\u5df2\u6709\u5341\u5e74\uff0c \u4eca\u65e5\u4e5f\u8be5\u6709\u4e2a\u660e\u767d\u7684\u4e86\u65ad\u4e86\uff01\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u610f\u6b32\u5982\u4f55\uff1f\u201d\u6587\u5ef7\u58c1\u5b09\u76ae\u7b11\u8138\u4ed6\u8bf4\u9053\uff1a\u201c\u6587\u67d0\u5192\u6627\uff0c \u6562\u8bf7\u6559\u4e3b\u4e0b\u5ac1\u3002\u6210\u5a5a\u4e4b\u540e\uff0c\u54b1\u4eec\u662f\u592b\u5987\u4e4b\u4eb2\uff0c\u6211\u81ea\u4f1a\u529b\u4f60\u5c3d\u5fc3\u5c3d\u529b\u3002\u6211\u7684\u65e0\u540d\u5c9b\u4e5f\u6709\u56db \u65f6\u4e0d\u8c22\u4e4b\u82b1\uff0c\u516b\u8282\u957f\u9752\u4e4b\u8349\uff0c\u5c0f\u5389\u591f\u7b54\u5e94\u4f60\u7684\uff0c\u6211\u6bd4\u4ed6\u66f4\u80dc\u51e0\u5206\uff01\u201d \u3000\u3000\u5389\u590d\u751f\u201c\u8e66\u201d\u5730\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u6012\u9a82\u9053\uff1a\u201c\u4e27\u5fc3\u75c5\u72c2\u7684\u6587\u5ef7\u58c1\uff0c\u4f60\u8fd9\u624d\u662f\u4e58\u4eba\u4e4b\u5371\uff01\u201d \u6587\u5ef7\u58c1\u7b11\u9053\uff1a\u201c\u5c0f\u5389\u3002\u4f55\u5fc5\u8fd9\u6837\u7740\u607c\uff1f\u4f60\u82e5\u662f\u771f\u5fc3\u559c\u6b22\u6559\u4e3b\uff0c\u90a3\u5c31\u8be5\u4e3a\u6559\u4e3b\u7740\u60f3\uff0c\u4f60\u6ca1 \u6709\u628a\u63e1\u6551\u5979\uff0c\u6211\u5374\u6709\u628a\u63e1\u6551\u5979\uff0c\u4f60\u5c31\u8be5\u8ba9\u6211\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u5854\u7136\u7740\u4e27\uff0c\u5077\u6109\u4e00\u770b\uff0c\u89c1\u5929\u9b54\u6559\u4e3b\u4ecd\u662f\u795e\u8272\u5982\u5e38\uff0c\u770b\u4e0d\u51fa\u6709\u4ec0\u4e48\u7279\u522b\u7684\u8868\u60c5\uff0c \u5389\u590d\u751f\u5fc3\u5934\u9178\u75db\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u6587\u5ef7\u58c1\uff0c\u4f60\u80af\u8d77\u8a93\u4e48\uff1f\u201d\u6587\u5ef7\u58c1\u9053\uff1a\u201c\u8d77\u4ec0\u4e48\u8a93\uff1f\u201d\u5389 \u590d\u751f\u9053\uff1a\u201c\u4f60\u8981\u662f\u771f\u5fc3\u5b9e\u610f\u7684\u52a9\u6559\u4e3b\u8131\u96be\uff0c\u6211\u5c31\u6210\u5168\u4e86\u4f60\u3002\u201d\u6587\u5ef7\u58c1\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5f53\u7136 \u53ef\u4ee5\u53d1\u8a93\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u5ffd\u5730\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u773c\u4e2d\u8fd8\u6709\u6211\u4e48\uff1f\u8fd9\u662f\u6211\u81ea\u5df1\u7684\u4e8b\u60c5\uff0c\u8ba9\u6211\u81ea\u5df1\u6765\u8bf4\u3002\u201d \u6587\u5ef7\u58c1\u9053\uff1a\u201c\u662f\uff0c\u6211\u542c\u6559\u4e3b\u5429\u5490\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u6211\u4e5f\u6709\u4e09\u4ef6\u4e8b\u60c5\uff0c\u73b0\u5728\u5c31\u8981\u544a\u8bc9\u4f60\u3002\u7b2c\u4e00\u725b\uff0c\u5929\u9b54\u6559\u4ece\u4eca\u65e5\u8d77\u4e0d\u518d \u6709\u4e86\u3002\u201d\u6587\u5ef7\u58c1\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u51e0\u7ecf\u8270\u82e6\u521b\u7acb\u7684\u5929\u9b54\u6559\uff0c\u5c31\u8fd9\u6837\u8f7b\u6613\u7684\u89e3\u6563\u4e86\u5417\uff1f\u201d \u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7531\u6211\u521b\u7acb\uff0c\u4e5f\u5c31\u53ef\u4ee5\u7531\u6211\u53d6\u6d88\u3002\u4ece\u4eca\u65e5\u8d77\uff0c\u4f60\u4e5f\u4e0d\u518d\u662f\u5929\u9b54\u6559\u7684 \u526f\u6559\u4e3b\u4e86\u3002\u4f60\u613f\u610f\u7559\u5728\u4e2d\u539f\u4e5f\u597d\uff0c\u56de\u4f60\u7684\u65e0\u540d\u5c9b\u4e5f\u597d\uff0c\u6089\u968f\u5c0a\u610f\u3002\u201d \u3000\u3000\u6587\u5ef7\u58c1\u6697\u6697\u7740\u614c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u5ea6\u8fc7\u6b64\u52ab\uff0c\u6b66\u529f\u4fbf\u6709\u767b\u5cf0\u9020\u6781\u4e4b\u671b\uff0c\u90a3\u65f6\u4f60\u6211\u8054\u624b\uff0c \u8db3\u53ef\u79f0\u9738\u6b66\u6797\uff0c\u4f55\u5fc5\u8981\u628a\u5929\u9b54\u6559\u6bc1\u4e86\uff1f\u201d\u4f60\u9053\u6587\u5ef7\u58c1\u4f55\u4ee5\u7740\u614c\uff0c\u539f\u6765\u4ed6\u81ea\u5df1\u5fc3\u91cc\u4e5f\u5f88\u660e \u767d\uff0c\u5929\u9b54\u6559\u4e3b\u7684\u4e00\u7247\u82b3\u5fc3\u5e76\u4e0d\u662f\u5411\u7740\u4ed6\uff0c\u8fc7\u53bb\u4e4b\u6240\u4ee5\u5bf9\u4ed6\u5047\u4ee5\u8f9e\u8272\uff0c\u4e43\u662f\u4e3a\u4e86\u8981\u501f\u91cd\u4e8e \u4ed6\uff0c\u6269\u5f20\u5929\u9b54\u6559\u7684\u52bf\u529b\u4e4b\u6545\u3002\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u5979\u81ea\u6bc1\u57fa\u4e1a\uff0c\u8fd9\u662f\u8868\u793a\u8981\u548c\u6211\u51b3\u7edd\u4e48\uff1f\u597d\u5728 \u5979\u8fd8\u8981\u6211\u52a9\u5979\u5ea6\u52ab\uff0c\u4e8b\u60c5\u4e5f\u8bb8\u8fd8\u672a\u7edd\u671b\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u6587\u5148\u751f\u597d\u610f\uff0c\u6211\u65e2\u7136\u4e0d\u60f3\u518d\u5f53\u6559\u4e3b\uff0c\u672c\u8eab\u7684\u6b66\u529f\u80fd\u5426\u4fdd \u5168\u3002\u90a3\u4e5f\u5c31\u4e0d\u653e\u5728\u5fc3\u4e0a\u4e86\u3002\u201d\u6587\u5ef7\u58c1\u6115\u7136\u95ee\u9053\uff1a\u201c\u4f60\u7518\u5192\u2018\u8d70\u706b\u5165\u9b54\u2019\u7684\u707e\u96be\uff0c\u4e0d\u60f3\u5ea6 \u8fc7\u8fd9\u4e00\u5173\u4e48\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u671b\u7740\u5389\u590d\u751f\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u751f\u5f1f\uff0c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u4e00\u4e2a\u4eba\u7684\u5feb\u4e50\u4e0e\u5426\uff0c\u5e76\u4e0d\u5728\u4e8e \u6b66\u529f\u7684\u6709\u65e0\uff0c\u6211\u5373\u4f7f\u6210\u4e86\u5e9f\u4eba\uff0c\u6709\u4f60\u5728\u6211\u8eab\u8fb9\uff0c\u4e5f\u4e00\u6837\u53ef\u4ee5\u8fc7\u5f97\u5feb\u6d3b\uff0c\u751a\u81f3\u6bd4\u4ece\u524d\u66f4\u52a0 \u5feb\u6d3b\u3002\u6587\u5148\u751f\uff0c\u6211\u8981\u544a\u8bc9\u4f60\u7684\u7b2c\u4e8c\u4ef6\u4e8b\u4fbf\u662f\uff0c\u8bf7\u4f60\u4e0d\u5fc5\u518d\u4e3a\u6211\u8d39\u5fc3\uff0c\u6211\u4e5f\u65e0\u987b\u4f60\u52a9\u6211\u5ea6 \u52ab\u4e86\uff01\u201d \u3000\u3000\u5389\u590d\u751f\u559c\u6781\u800c\u6ce3\uff0c\u53eb\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u771f\u662f\u5bf9\u6211\u592a\u597d\u4e86\uff01\u201d\u4e0d\u7406\u4f1a\u6587\u5ef7\u58c1\u8fd8\u5728\u8eab\u8fb9\uff0c \u4fbf\u6251\u8fc7\u6765\u7d27\u7d27\u63e1\u7740\u5929\u9b54\u6559\u4e3b\u7684\u53cc\u624b\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u9762\u8272\u94c1\u9752\uff0c\u563f\u563f\u51b7\u7b11\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u5bf9\u5c0f\u5389\u5982\u6b64\u6df1\u60c5\uff0c\u5f53\u771f\u662f\u8fde\u6027\u547d\u4e5f\u4e0d\u987e\u4e86\uff1f\u201d \u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6240\u4ee5\u6211\u8981\u544a\u8bc9\u4f60\u7684\u7b2c\u4e09\u4ef6\u4e8b\u60c5\u4fbf\u662f\uff1a\u6211\u5df2\u51b3\u610f\u4e0e\u5c0f\u5389\u6210\u5a5a\uff0c\u4f60\u662f \u6211\u4eec\u7684\u597d\u670b\u53cb\uff0c\u5230\u65f6\u8fd8\u5f97\u4f60\u6765\u559d\u4e00\u676f\u559c\u9152\uff01\u201d \u3000\u3000\u6587\u5ef7\u58c1\u5446\u7686\u6728\u9e21\uff0c\u5389\u590d\u751f\u5219\u559c\u5b5c\u5b5c\u4ed6\u8bf4\u9053\uff1a\u201c\u6587\u5148\u751f\uff0c\u4f60\u4e0d\u7ed9\u6211\u4eec\u9053\u559c\u4e48\uff1f\u8bf7\u4f60\u8ba9 \u5f00\u5427\uff0c\u6211\u4eec\u53ef\u4ee5\u8d70\u4e86\u3002\u201d \u3000\u3000\u6587\u5ef7\u58c1\u84e6\u5730\u53cc\u773c\u4e00\u7ffb\uff0c\u773c\u4e2d\u5c31\u5982\u540c\u8981\u55b7\u51fa\u706b\u6765\uff0c\u563f\u563f\u7b11\u9053\uff1a \u3000\u3000\u201c\u597d\u670b\u53cb\uff0c\u54c8\u54c8\uff0c\u597d\u670b\u53cb\uff01\u4eca\u5929\u4f60\u4eec\u63d0\u628a\u6211\u5f53\u4f5c\u4e09\u5c3a\u5c0f\u7ae5\u6765\u620f\u800d\u4e86\uff01\u201d\u5929\u9b54\u6559\u4e3b\u6de1 \u8c08\u8bf4\u9053\uff1a\u201c\u6587\u5148\u751f\uff0c\u4f60\u662f\u8fd9\u6837\u60f3\u4e48\uff1f\u90a3\u4e5f\u7531\u5f97\u4f60\u5427\uff0c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u4eec\u53ef\u771f\u8981\u8d70\u4e86\uff0c\u4f60\u522b\u62e6 \u5728\u95e8\u53e3\uff0c\u597d\u4e48\uff1f\u201d \u3000\u3000\u6587\u5ef7\u58c1\u4e00\u58f0\u5927\u543c\uff0c\u84e6\u5730\u4e00\u638c\u4fbf\u5411\u5389\u590d\u751f\u6253\u53bb\uff0c\u559d\u9053\uff1a\u201c\u6559\u4e3b\u6267\u8ff7\u4e0d\u609f\uff0c\u90fd\u662f\u4e3a\u4e86\u4f60 \u7684\u7f18\u6545\uff0c\u6211\u4e0d\u80fd\u8ba9\u4f60\u5bb3\u6b7b\u6559\u4e3b\uff0c\u4eca\u65e5\u4e0e\u4f60\u62fc\u4e86\uff01\u201d\u5389\u590d\u751f\u559c\u6781\u5fd8\u5f62\uff0c\u6587\u5ef7\u58c1\u638c\u529b\u53d1\u51fa\uff0c \u4ed6\u65b9\u624d\u7701\u89c9\uff0c\u5e78\u4e8f\u5929\u9b54\u6559\u4e3b\u5374\u65e9\u6709\u9632\u5907\uff0c\u8863\u8896\u4e00\u626c\uff0c\u4e00\u84ec\u6bd2\u9488\u98de\u4e86\u51fa\u6765\uff0c\u6587\u5ef7\u58c1\u8bc6\u5f97\u5389 \u5bb3\uff0c\u53ea\u597d\u79fb\u8f6c\u638c\u529b\uff0c\u5148\u628a\u6bd2\u9488\u9707\u843d\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u67f3\u7709\u4e00\u7ad6\uff0c\u65a5\u9053\uff1a\u201c\u6587\u5148\u751f\uff0c\u4f60\u80c6\u6562\u6697\u7b97\u6211\u7684\u4e08\u592b\uff0c\u6211\u4eec\u592b\u5987\u4e5f\u4e0d\u80fd\u548c\u4f60 \u5ba2\u6c14\u4e86\u3002\u201d\u8fd9\u65f6\u5389\u590d\u751f\u5df2\u7ecf\u6e05\u9192\u8fc7\u6765\uff0c\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u6587\u5ef7\u58c1\uff0c\u4f60\u6eda\u4e0d\u6eda\u5f00\uff1f\u201d\u5c31 \u5728\u5929\u9b54\u6559\u4e3b\u518d\u6b21\u6325\u8896\u4e4b\u65f6\uff0c\u4ed6\u4e5f\u62bd\u51fa\u7389\u5c3a\uff0c\u671d\u7740\u6587\u5ef7\u58c1\u6402\u5934\u4fbf\u6253\uff01 \u3000\u3000\u6587\u5ef7\u58c1\u53cc\u638c\u5408\u62e2\uff0c\u5de6\u53f3\u4e00\u5206\uff0c\u4f7f\u51fa\u201c\u9634\u9633\u53cc\u649e\u638c\u201d\u7684\u62db\u5f0f\uff0c\u5206\u51fb\u4e24\u4eba\uff0c\u5de6\u865a\u53f3\u5b9e\uff0c \u7528\u4e86\u4e03\u6210\u4ee5\u4e0a\u7684\u529f\u529b\uff0c\u5bf9\u5389\u590d\u751f\u75db\u4e0b\u6740\u624b\uff01\u4ed6\u7684\u201c\u4e09\u8c61\u795e\u529f\u201d\u786e\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u638c\u529b\u4e00\u53d1\uff0c \u5389\u590d\u751f\u7684\u7389\u5c3a\uff0c\u7adf\u4f3c\u88ab\u4e00\u5835\u65e0\u5f62\u7684\u5899\u58c1\u963b\u6b62\uff0c\u653b\u4e0d\u8fc7\u53bb\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4ee5\u53f3\u638c\u7684\u4e03\u6210\u529f\u529b\u5835\u4f4f\u4e86\u5389\u590d\u751f\u7684\u53bb\u8def\uff0c\u5de6\u638c\u7684\u4e09\u6210\u529f\u529b\u5374\u5e94\u4ed8\u4e0d\u4e86\u5929\u9b54\u6559 \u4e3b\u7684\u201c\u94c1\u8896\u795e\u529f\u201d\uff0c\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u624b\u81c2\u5df2\u7ed9\u6253\u4e2d\u3002\u4e09\u4eba\u4e4b\u4e2d\uff0c\u5929\u9b54\u6559\u4e3b\u529f\u529b\u8f83\u5f31\uff0c\u4f46 \u4e5f\u76f8\u5dee\u4e0d\u8fdc\uff0c\u8fd9\u4e00\u51fb\u867d\u672a\u80fd\u4f24\u53ca\u6587\u5ef7\u58c1\u7b4b\u8109\uff0c\u4ea6\u5df2\u4ee4\u4ed6\u611f\u5230\u706b\u8fa3\u8fa3\u7684\u4f5c\u75db\u3002\u800c\u4e14\u4e00\u80a1\u6de1 \u6de1\u7684\u5e7d\u9999\uff0c\u4e5f\u968f\u7740\u8896\u98ce\u900f\u8fdb\u4e86\u6587\u5ef7\u58c1\u7684\u8205\u89c2\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u5f53\u7136\u77e5\u9053\u5929\u9b54\u6559\u4e3b\u7684\u4f7f\u6bd2\u672c\u9886\uff0c\u8fde\u5fd9\u95ed\u4e86\u547c\u5438\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u201c\u6ce2\u201d\u7684 \u4e00\u58f0\uff0c\u5389\u590d\u751f\u7684\u7389\u5c3a\u5df2\u6233\u7834\u4e86\u90a3\u80a1\u65e0\u5f62\u7684\u6c14\u6d41\uff0c\u6307\u5411\u6587\u5ef7\u58c1\u814b\u4e0b\u7684\u201c\u671f\u95e8\u7a74\u201d\uff0c\u6587\u5ef7\u58c1 \u4ecd\u4e0d\u8ba9\u6b65\uff0c\u8eab\u5f62\u4e00\u7f29\uff0c\u907f\u5f00\u8fd9\u62db\uff0c\u7a81\u7136\u5316\u638c\u4e3a\u62f3\uff0c\u4e00\u62db\u201c\u6a2a\u8eab\u6253\u864e\u201d\uff0c\u731b\u6363\u51fa\u53bb\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u4e2d\u6307\u4e00\u7fd8\uff0c\u6307\u4e0a\u6234\u7740\u5957\u73af\uff0c\u5c16\u7aef\u662f\u4e00\u679a\u9ed1\u5f97\u53d1\u4eae\u7684\u6bd2\u9488\uff0c\u6587\u5ef7\u58c1\u4e0d\u60e7\u5929\u9b54 \u6559\u4e3b\u7684\u6307\u529b\u70b9\u7a74\uff0c\u4f46\u5bf9\u5979\u5c42\u51fa\u4e0d\u7a77\u7684\u4f7f\u6bd2\u529f\u592b\uff0c\u5374\u4e0d\u6562\u4e0d\u9632\u3002\u8fde\u5fd9\u7f29\u624b\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6 \u5feb\uff0c\u5389\u590d\u751f\u7684\u7389\u5c3a\u5df2\u662f\u6b3a\u8eab\u76f4\u8fdb\uff0c\u4e00\u62db\u4e4b\u95f4\uff0c\u8fde\u88ad\u6587\u5ef7\u58c1\u7684\u4e03\u5904\u5927\u7a74\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u5728\u4ed6\u4eec\u4e24\u4eba\u8054\u624b\u5939\u653b\u4e4b\u4e0b\uff0c\u907f\u65e0\u53ef\u907f\uff0c\u53ea\u5f97\u95ed\u4e86\u5168\u8eab\u7a74\u9053\uff0c\u632f\u81c2\u4e00\u6325\uff0c\u786c\u63a5 \u5389\u590d\u751f\u7684\u7389\u5c3a\u3002\u5389\u590d\u751f\u8fd9\u6839\u7389\u5c3a\u662f\u4ef6\u5b9d\u7269\uff0c\u53ea\u6709\u4e09\u5c3a\u6765\u957f\uff0c\u5374\u6709\u767e\u65a4\u4e4b\u91cd\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d \u7684\u4e00\u58f0\uff0c\u5982\u51fb\u8d25\u9769\uff0c\u9976\u662f\u6587\u5ef7\u58c1\u6709\u62a4\u4f53\u795e\u529f\uff0c\u4e5f\u7ed9\u4ed6\u6253\u5f97\u75db\u5f7b\u5fc3\u80ba\u3001\u773c\u775b\u53d1\u9ed1\u3002\u5929\u9b54\u6559 \u4e3b\u76c8\u76c8\u7b11\u9053\uff1a\u201c\u8001\u670b\u53cb\u5f53\u771f\u662f\u62fc\u4e2a\u4f60\u6b7b\u6211\u6d3b\u4e48\uff1f\u201d \u3000\u3000\u6587\u5ef7\u58c1\u5927\u543c\u4e00\u58f0\uff0c\u5012\u7eb5\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u5389\u590d\u751f\u88ab\u4ed6\u7684\u53cd\u9707\u4e4b\u529b\u4e00\u9707\uff0c\u4e5f\u8dc4\u8dc4\u8e09\u8e09\u7684\u9000 \u4e86\u51e0\u6b65\uff0c\u5929\u9b54\u6559\u4e3b\u5c06\u4ed6\u62c9\u7740\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u5f1f\u5f1f\uff0c\u5feb\u8d70\uff0c\u8fd9\u7b14\u5e10\u4ee5\u540e\u518d\u7b97\u4e0d\u8fdf\uff01\u201d \u3000\u3000\u6587\u5ef7\u58c1\u6c14\u5f97\u9762\u8272\u94c1\u9752\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8981\u60f3\u628a\u6211\u6487\u4e0b\uff0c\u53ea\u6015\u4e5f\u6ca1\u90a3\u4e48\u5bb9\u6613\uff01\u201d\u84e6 \u5730\u4e00\u58f0\u957f\u5578\uff0c\u5c06\u58f0\u97f3\u8fdc\u8fdc\u9001\u4e86\u51fa\u53bb\uff0c\u8ddf\u7740\u558a\u9053\uff1a\u201c\u91d1\u8f6e\u5723\u6bcd\uff0c\u4f60\u7684\u597d\u5f92\u5f1f\u8ddf\u4eba\u8dd1\u5566\uff0c\u4f60 \u662f\u7ba1\u4e5f\u4e0d\u7ba1\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u6700\u6015\u7684\u5c31\u662f\u5979\u7684\u5e08\u7236\u8d76\u6765\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u5f1f\u5f1f\uff0c\u5feb\u5524\u91d1\u6bdb\u72e1\u6765\uff01\u201d \u5979\u4e00\u7b97\u65f6\u523b\uff0c\u5979\u7684\u5e08\u7236\u5728\u9759\u5ba4\u7ec3\u529f\uff0c\u5927\u7ea6\u4e0d\u4e45\u5c31\u53ef\u7ae3\u4e8b\uff0c\u5979\u5fc5\u987b\u5728\u8fd9\u5c11\u8bb8\u7684\u65f6\u523b\u4e4b\u5185\uff0c \u9003\u51fa\u5bab\u95e8\u3002 \u3000\u3000\u5389\u590d\u751f\u56e0\u4e3a\u6765\u65f6\u5c1a\u672a\u77e5\u9053\u5929\u9b54\u6559\u4e3b\u80af\u4e0b\u51b3\u5fc3\uff0c\u8ddf\u4ed6\u79c1\u9003\uff0c\u6545\u800c\u628a\u4e24\u53ea\u91d1\u6bdb\u72e1\u90fd\u7559\u5728 \u5bab\u5899\u4e4b\u5916\u3002\u4ed6\u662f\u5982\u6b64\u60f3\u7684\uff0c\u91d1\u6bdb\u72e1\u884c\u52a8\u5982\u98ce\uff0c\u5230\u4e86\u5929\u9b54\u6559\u4e3b\u7b54\u5e94\u4e86\u4ed6\uff0c\u90a3\u65f6\u518d\u628a\u5b83\u4eec\u62db \u6765\uff0c\u4e5f\u8fd8\u4e0d\u8fdf\uff0c\u514d\u5f97\u8fc7\u65e9\u5e26\u5165\u5bab\u4e2d\uff0c\u82e5\u4eba\u6ce8\u610f\u3002 \u3000\u3000\u5389\u590d\u751f\u4e00\u58f0\u957f\u5578\uff0c\u7acb\u5373\u4fbf\u542c\u5230\u91d1\u6bdb\u72e1\u7684\u543c\u58f0\uff0c\u5929\u9b54\u6559\u4e3b\u653e\u4e0b\u4e86\u5fc3\u4e0a\u4e00\u5757\u77f3\u5934\uff0c\u60f3\u9053\uff1a \u201c\u91d1\u6bdb\u72e1\u7247\u523b\u4e4b\u95f4\u4fbf\u53ef\u5230\u6765\uff0c\u53ea\u8981\u8de8\u4e0a\u4e86\u91d1\u6bdb\u72e1\uff0c\u5e08\u7236\u7eb5\u6709\u5929\u5927\u795e\u901a\uff0c\u90a3\u4e5f\u662f\u8ffd\u4e4b\u4e0d\u53ca \u4e86\u3002\u201d\u5f53\u4e0b\u4fbf\u4e0e\u5389\u590d\u751f\u4e00\u8d77\uff0c\u4e24\u4eba\u52a0\u5feb\u811a\u6b65\uff0c\u51c6\u5907\u4e0a\u524d\u8fce\u63a5\u91d1\u6bdb\u72e1\u3002 \u3000\u3000\u542c\u5230\u4e86\u91d1\u6bdb\u72e1\u7684\u543c\u58f0\uff0c\u6587\u5ef7\u58c1\u53c8\u6c14\u53c8\u6025\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c\u4fbf\u5411\u5389\u590d\u751f\u6251\u6765\uff0c\u811a\u672a\u843d\u5730\uff0c \u4eba\u5c1a\u60ac\u7a7a\uff0c\u5df2\u662f\u4e00\u62db\u201c\u9e4f\u640f\u4e5d\u9704\u201d\u51cc\u7a7a\u51fb\u4e0b\u3002\u5389\u590d\u751f\u4e0e\u5929\u9b54\u6559\u4e3b\u53cc\u638c\u9f50\u51fa\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d \u7684\u4e00\u58f0\uff0c\u6587\u5ef7\u58c1\u654c\u4e0d\u8fc7\u4ed6\u4eec\u7684\u5408\u529b\uff0c\u767b\u65f6\u53c8\u4f3c\u76ae\u7403\u822c\u7684\u7ed9\u629b\u4e86\u8d77\u6765\uff0c\u4f46\u6587\u5ef7\u58c1\u4e5f\u597d\u751f\u4e86 \u5f97\uff0c\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7ffb\u8eab\uff0c\u501f\u4ed6\u4eec\u4e8c\u4eba\u7684\u638c\u529b\uff0c\u53bb\u52bf\u5982\u4fde\uff0c\u5728\u516d\u4e03\u4e08\u5916\u843d\u4e86\u4e0b\u6765\uff0c\u4ecd\u7136\u62e6 \u7740\u4ed6\u4eec\u7684\u53bb\u8def\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u6012\u9053\uff1a\u201c\u6587\u5ef7\u58c1\uff0c\u4f60\u771f\u662f\u592a\u4e0d\u8bc6\u597d\u6b79\u4e86\uff0c\u4e8b\u5230\u5982\u4eca\u3002 \u3000\u3000\u4f60\u8fd8\u60f3\u963b\u6320\uff1f\u201d\u8863\u8896\u4e00\u626c\uff0c\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u679a\u6bd2\u96fe\u91d1\u9488\u70c8\u7130\u5f39\u53d1\u4e86\u51fa\u6765\u3002\u6587\u5ef7\u58c1 \u72de\u7b11\u9053\uff1a\u201c\u5361\u5170\u59ae\uff0c\u6211\u8bf4\u4f60\u624d\u662f\u4e0d\u77e5\u597d\u6b79\uff01\u201d\u4e00\u8bb0\u5288\u7a7a\u638c\u626b\u51fa\uff0c\u6bd2\u96fe\u6d88\u6563\uff0c\u91d1\u9488\u843d\u5730\uff0c \u90a3\u4e00\u56e2\u706b\u5149\u53cd\u5411\u5929\u9b54\u6559\u4e3b\u5377\u6765\uff0c\u5389\u590d\u751f\u8fde\u5fd9\u4e5f\u4ee5\u5288\u7a7a\u638c\u529b\uff0c\u5c06\u90a3\u56e2\u706b\u5149\uff0c\u53c8\u63a8\u8fc7\u53bb\u3002\u5389 \u590d\u751f\u6709\u5929\u9b54\u6559\u4e3b\u76f8\u52a9\uff0c\u5408\u4e24\u4eba\u4e4b\u529b\uff0c\u5f53\u7136\u662f\u80dc\u8fc7\u6587\u5ef7\u58c1\u597d\u591a\u3002\u53ef\u662f\u8fd9\u4e48\u4e00\u6765\uff0c\u53c8\u88ab\u4ed6\u803d \u6401\u4e86\u4e00\u4e9b\u65f6\u5019\u3002 \u3000\u3000\u5389\u590d\u751f\u5fc3\u91cc\u6697\u6697\u5947\u602a\uff1a\u201c\u600e\u4e48\u91d1\u6bdb\u72e1\u8fd8\u4e0d\u89c1\u6765\uff1f\u201d\u6025\u5fd9\u518d\u4ee5\u5578\u58f0\u50ac\u4fc3\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c \u624d\u9690\u9690\u542c\u5230\u91d1\u6bdb\u72e1\u7684\u4e00\u58f0\u543c\u58f0\uff0c\u6bd4\u8d77\u521a\u624d\u7684\u543c\u58f0\u51cf\u5f31\u8bb8\u591a\u3002\u4f3c\u662f\u6709\u6c14\u6ca1\u529b\u7684\u6837\u5b50\uff0c\u5389\u590d \u751f\u72d0\u7591\u4e0d\u5b9a\uff0c\u90a3\u6587\u5ef7\u58c1\u62e6\u5728\u524d\u5934\uff0c\u4ee5\u201c\u4e09\u8c61\u795e\u529f\u201d\u9707\u584c\u4e86\u4e00\u5ea7\u5047\u5c71\uff0c\u53c8\u963b\u4e86\u4ed6\u4eec\u4e00\u963b\u3002 \u5389\u590d\u751f\u6012\u9053\uff1a\u201c\u5148\u628a\u8fd9\u53ae\u6740\u4e86\uff01\u201d \u3000\u3000\u6587\u5ef7\u58c1\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8981\u6740\u6211\uff0c\u53ea\u6015\u4e5f\u6ca1\u90a3\u4e48\u5bb9\u6613\uff1f\u201d\u53cc\u65b9\u4e14\u6218\u4e14\u8d70\uff0c\u6587\u5ef7\u58c1\u867d\u7136 \u5904\u5728\u4e0b\u98ce\uff0c\u80c6\u4ed6\u5341\u5206\u673a\u7075\uff0c\u53ea\u91c7\u9a9a\u6270\u7684\u6218\u672f\uff0c\u5374\u4e0d\u604b\u6218\uff0c\u907f\u514d\u7ed9\u5bf9\u65b9\u56f4\u653b\uff0c\u6709\u65f6\u629b\u4e00\u5757 \u77f3\u5934\uff0c\u6709\u65f6\u65ad\u4e00\u68f5\u5927\u886c\uff0c\u603b\u4e4b\u767e\u8ba1\u5343\u65b9\uff0c\u963b\u5ef6\u65f6\u523b\uff0c\u5389\u590d\u751f\u4e0e\u5929\u9b54\u6559\u4e3b\u4e5f\u6709\u987e\u5fcc\uff0c\u4e00\u65f6 \u4e4b\u95f4\uff0c\u5012\u4e5f\u65e0\u5948\u4ed6\u4f55\u3002\u597d\u5728\u91d1\u6bdb\u72e1\u867d\u7136\u4e0d\u89c1\u5230\u6765\uff0c\u90a3\u7ae5\u59e5\u59e5\u4e5f\u672a\u89c1\u51fa\u73b0\u3002 \u3000\u3000\u4e14\u8bf4\u8c37\u4e2d\u83b2\u5728\u90a3\u5bc6\u5ba4\u4e4b\u4e2d\uff0c\u5929\u9b54\u6559\u4e3b\u3001\u5389\u590d\u751f\u548c\u6587\u5ef7\u58c1\u7684\u4e89\u5435\uff0c\u5979\u90fd\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c \u4e0d\u77e5\u4e0d\u89c9\uff0c\u5bf9\u5929\u9b54\u6559\u4e3b\u7684\u6076\u611f\u53c8\u6d88\u4e86\u51e0\u5206\uff0c\u53cd\u800c\u6709\u70b9\u540c\u60c5\u5979\u4e86\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u73b0\u5728\u5df2\u6ca1 \u6709\u4eba\u76d1\u89c6\u6211\u4e86\uff0c\u6b64\u65f6\u4e0d\u8d70\uff0c\u66f4\u5f85\u4f55\u65f6\uff1f\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u79bb\u5f00\u5979\u670d\u89e3\u836f\u7684\u65f6\u95f4\uff0c\u5df2\u6709\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u53c8\u7ecf\u8fc7\u5979\u8fd0\u529f\u50ac\u884c\u836f\u529b\uff0c\u529f\u592b\u867d\u672a \u5b8c\u5168\u6062\u590d\uff0c\u4e5f\u5df2\u6062\u590d\u4e86\u4e03\u516b\u6210\u3002\u5979\u4ece\u5bc6\u5ba4\u91cc\u8d70\u4e86\u51fa\u6765\uff0c\u5411\u5916\u5f20\u671b\uff0c\u53ea\u89c1\u5df2\u662f\u66ae\u972d\u6c89\u6c89\u7684 \u9ec4\u660f\u65f6\u5206\uff0c\u5468\u56f4\u5341\u5206\u5bc2\u9759\uff0c\u8fdc\u5904\u867d\u7136\u6709\u5e62\u5e62\u9ed1\u5f71\uff0c\u5374\u4e0d\u662f\u5411\u7740\u5979\u8fd9\u8fb9\u65b9\u5411\u800c\u6765\u3002\u6587\u5ef7\u58c1 \u4e0e\u5389\u590d\u751f\u7684\u9ad8\u547c\u9163\u6597\u4e4b\u58f0\uff0c\u4e5f\u5df2\u5728\u5f88\u8fdc\u7684\u8ddd\u79bb\u4e4b\u5916\u3002\u770b\u6765\u90a3\u4e00\u4e9b\u4eba\u662f\u88ab\u4ed6\u4eec\u7684\u53ae\u6740\u58f0\u60ca \u52a8\uff0c\u4ed6\u53bb\u770b\u4e2a\u7a76\u7adf\u7684\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u9003\u8dd1\u7684\u6700\u597d\u65f6\u673a\uff0c\u8c37\u4e2d\u83b2\u5374\u5ffd\u5730\u60f3\u8d77\u4e00\u4e8b\uff0c\u5fc3\u9053\uff1a \u3000\u3000\u201c\u4e0d\u53ef\uff0c\u6211\u662f\u4e3a\u4e86\u6d88\u6d31\u6218\u7978\u800c\u6765\uff0c\u5c82\u53ef\u4e00\u8d70\u4e86\u4e4b\uff1f\u56fd\u738b\u66fe\u4e0e\u6211\u7ea6\u5b9a\uff0c\u8981\u6211\u4eca\u665a\u4e8c\u66f4 \u65f6\u5206\uff0c\u5230\u4ed6\u5bab\u4e2d\uff0c\u52a9\u4ed6\u9664\u53bb\u4ed6\u7684\u653f\u654c\u6cf0\u6e05\u738b\uff0c\u53ea\u8981\u6b64\u4eba\u4e00\u9664\uff0c\u6606\u5e03\u5170\u56fd\u7684\u4e3b\u6218\u6d3e\u4fbf\u592b\u4e86 \u9886\u8896\uff0c\u53ef\u671b\u5316\u5e72\u6208\u800c\u4e3a\u7389\u5e1b\u4e86\u3002\u6211\u5df2\u7ecf\u7b54\u5e94\u4e86\u56fd\u738b\uff0c\u82e5\u7136\u8eab\u5728\u56da\u7b3c\uff0c\u90a3\u662f\u65e0\u8bdd\u53ef\u8bf4\uff1b\u5982 \u4eca\u65e2\u5df2\u8131\u9669\uff0c\u7109\u80fd\u5931\u4fe1\u4e8e\u4ed6\uff1f\u201d\u601d\u5ff5\u53ca\u6b64\uff0c\u5fc3\u610f\u7acb\u51b3\uff0c\u6253\u6d88\u4e86\u9003\u8dd1\u7684\u5ff5\u5934\uff0c\u6084\u6084\u7684\u907f\u5f00 \u4eba\u591a\u4e4b\u5904\uff0c\u7ed5\u8def\u800c\u884c\uff0c\u501f\u7269\u969c\u5f62\uff0c\u524d\u5f80\u56fd\u738b\u7684\u5bdd\u5bab\u8df5\u7ea6\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u4e3a\u4e86\u907f\u514d\u7ed9\u4eba\u53d1\u73b0\uff0c\u6240\u8d70\u7684\u65b9\u5411\u548c\u5929\u9b54\u6559\u4e3b\u6240\u8d70\u7684\u65b9\u5411\u76f8\u53cd\uff0c\u4f46\u90a3\u91d1\u94c1\u4ea4\u9e23\u3001 \u9ad8\u547c\u9163\u6597\u4e4b\u58f0\u3001\u4ecd\u662f\u65ad\u7eed\u4f20\u6765\uff0c\u8c37\u4e2d\u83b2\u5fc3\u4e2d\u60ac\u60ac\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5929\u9b54\u6559\u4e3b\u867d\u7136\u5fc3\u672f\u4e0d\u6b63\uff0c \u884c\u4e8b\u90aa\u50fb\uff0c\u4f46\u6bd5\u7adf\u4e5f\u8fd8\u4e0d\u662f\u7a77\u51f6\u6781\u6076\u4e4b\u5f92\uff0c\u66f4\u96be\u5f97\u7684\u662f\u5979\u5bf9\u5389\u590d\u751f\u4e5f\u6709\u4e00\u7247\u771f\u5fc3\uff0c\u4e0d\u8d1f \u5389\u590d\u751f\u5bf9\u5979\u7684\u75f4\u604b\u3002\u5509\uff0c\u4f46\u613f\u4ed6\u4eec\u80fd\u591f\u8131\u9669\uff0c\u53ea\u8981\u5979\u4eca\u540e\u771f\u7684\u662f\u6539\u90aa\u5f52\u6b63\uff0c\u6211\u5012\u8981\u8bf7\u6d77 \u54e5\u52a9\u5979\u4e00\u81c2\u4e4b\u529b\uff0c\u6d77\u54e5\u7684\u5185\u529f\u9020\u8be3\uff0c\u5df2\u5dee\u4e0d\u591a\u53ef\u4ee5\u53ca\u5f97\u4e0a\u4ed6\u7684\u5e08\u7236\u4e86\uff0c\u8bf4\u4e0d\u5b9a\u53ef\u4ee5\u52a9\u5979 \u9003\u8fc7\u4e86\u2018\u8d70\u706b\u5165\u9b54\u2019\u4e4b\u707e\u3002\u201d\u5fc3\u5ff5\u672a\u5df1\uff0c\u5ffd\u7136\u542c\u5f97\u4e00\u58f0\u523a\u8033\u4e4b\u6781\u7684\u5c16\u53eb\uff0c\u6b63\u662f\u5929\u9b54\u6559\u4e3b \u7684\u58f0\u97f3\u3002\u8c37\u4e2d\u83b2\u5fc3\u5934\u4e00\u51c9\uff0c\u4e0d\u7981\u5931\u58f0\u53eb\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u7cdf\u7cd5\uff0c\u4ed6\u4eec\u4ecd\u662f\u672a\u80fd\u8131\u9669\uff01\u201d \u3000\u3000\u5ffd\u5730\u6709\u4eba\u9634\u607b\u607b\u7684\u5e94\u58f0\u63a5\u9053\uff1a\u201c\u4ed6\u4eec\u672a\u80fd\u8131\u9669\uff0c\u4f60\u5c31\u80fd\u8131\u9669\u4e86\u4e48\uff1f\u563f\u563f\uff0c\u6211\u770b\u4f60\u662f \u6ce5\u83e9\u8428\u8fc7\u6c5f\uff0c\u81ea\u8eab\u96be\u4fdd\uff01\u8fd8\u53ea\u987e\u66ff\u522b\u4eba\u62c5\u5fc3\uff1f\u201d\u6de1\u6708\u758f\u661f\u4e4b\u4e0b\uff0c\u53ea\u89c1\u5047\u5c71\u80cc\u540e\u3001\u7a81\u7136\u73b0 \u51fa\u4e00\u4eba\uff0c\u6b63\u662f\u5929\u9b54\u6559\u4e3b\u7684\u59d0\u59d0\uff0c\u6614\u5e74\u66fe\u5192\u8ba4\u8c37\u4e2d\u83b2\u4f5c\u5973\u513f\u7684\u90a3\u4e2a\u7f2a\u592b\u4eba\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u7f2a\u592b\u4eba\u5df1\u6296\u5f00\u4e86\u4e00\u7ea2\u7ef8\uff0c\u5411\u8c37\u4e2d\u83b2\u5f53\u5934\u7f69 \u4e0b\uff0c\u8c37\u4e2d\u83b2\u5927\u6012\uff0c\u4e00\u624b\u6293\u53bb\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u7ea2\u7ef8\u7ed9\u5979\u6293\u88c2\uff0c\u6495\u4f5c\u4e24\u6761\u3002\u53ef\u60dc\u8c37\u4e2d\u83b2\u7684 \u529f\u529b\u8fd8\u6765\u5b8c\u5168\u6062\u590d\uff0c\u7ea2\u7ef8\u867d\u7136\u7ed9\u5979\u6293\u88c2\uff0c\u5374\u8fd8\u672a\u80fd\u593a\u4e86\u8fc7\u6765\u3002\u5c06\u7ea2\u7ef8\u6495\u4f5c\u4e24\u6761\uff0c\u4e5f\u8fd8\u672a \u5b8c\u5168\u5206\u5f00\uff0c\u53e6\u4e00\u7aef\u5c1a\u63e1\u5728\u7f2a\u592b\u4eba\u624b\u4e2d\u3002 \u3000\u3000\u7f2a\u592b\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u6708\u529b\u4e00\u6296\uff0c\u4e24\u6761\u7ea2\u7ef8\u5de6\u53f3\u4e00\u5206\uff0c\u7f20\u4e0a\u4e86\u8c37\u4e2d\u83b2\u53cc\u81c2\u3002\u5979\u90a3\u6761\u7ea2\u7ef8 \u662f\u6cbe\u6709\u836f\u7c89\u7684\uff0c\u7ea2\u7ef8\u4e00\u6296\uff0c\u836f\u7c89\u98de\u626c\uff0c\u6c14\u5473\u82ac\u82b3\uff0c\u4e2d\u4eba\u6b32\u9189\u3002\u8c37\u4e2d\u83b2\u4e00\u53e3\u6c14\u5439\u51fa\u53bb\uff0c\u5c06 \u836f\u626e\u5439\u5f00\u3002\u4f46\u5979\u65e2\u8981\u8fd0\u6c14\u5439\u5f00\u6bd2\u7c89\u3002\u5df2\u65e0\u4f59\u529b\u6323\u8131\u675f\u7f1a\uff0c\u90a3\u4e24\u6761\u7ea2\u7ef8\u6253\u4e86\u51e0\u531d\uff0c\u5c06\u5979\u7684 \u624b\u81c2\u7262\u7262\u7f1a\u4f4f\u3002 \u3000\u3000\u7f2a\u592b\u4eba\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u6709\u610f\u8ba4\u4f60\u4f5c\u6211\u5973\u513f\uff0c\u4f60\u653e\u5fc3\u5427\uff0c\u53ea\u8981\u4f60\u4e56\u4e56\u7684\u542c\u6211\u7684\u8bdd\uff0c \u6211\u51b3\u4e0d\u4f1a\u5c06\u4f60\u96be\u4e3a\u3002\u201d\u8c37\u4e2d\u83b2\u5927\u9a82\u9053\uff1a \u3000\u3000\u201c\u4f60\u8fd9\u5996\u5987\uff0c\u80e1\u8bf4\u516b\u9053\uff0c\u7b80\u76f4\u662f\u606c\u4e0d\u77e5\u803b\uff01\u201d\u7f2a\u592b\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u4e0d\u542c\u8bdd\uff0c\u6211 \u5c31\u4e0d\u8981\u4f60\u4f5c\u5973\u513f\uff0c\u8981\u628a\u4f60\u5f53\u4f5c\u4e2b\u5934\u4e86\uff0c\u54fc\uff0c\u8fc7\u6765\u5427\uff01\u201d\u7ea2\u7ef8\u6536\u675f\uff0c\u5c06\u8c37\u4e2d\u83b2\u4e00\u6b65\u4e00\u6b65\u5730 \u62c9\u4e86\u8fc7\u6765\u3002\u6b63\u662f\uff1a \u3000\u3000\u6253\u7834\u7389\u7b3c\u98de\u5f69\u51e4\uff0c\u8c01\u77e5\u53c8\u6709\u4f0f\u5175\u6765\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e94\u5341\u4e09\u56de\u3000\u51b7\u7130\u641c\u9b42\u65bd\u8fa3\u624b\u3000\u91d1\u676f\u656c\u9152\u901e\u673a\u8c0b \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e94\u5341\u4e09\u56de\u3000\u51b7\u7130\u641c\u9b42\u65bd\u8fa3\u624b\u3000\u91d1\u676f\u656c\u9152\u901e\u673a\u8c0b \u3000\u3000\u8c37\u4e2d\u83b2\u6b63\u5728\u7834\u53e3\u5927\u9a82\uff0c\u5ffd\u542c\u5f97\u7f2a\u592b\u4eba\u559d\u9053\uff1a\u201c\u8c01\uff1f\u2026\u2026\u201d\u8fd9\u4e00\u4e2a\u201c\u8c01\u201d\u5b57\u521a\u521a\u51fa\u53e3\uff0c \u4fbf\u5373\u201c\u535c\u901a\u201d\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\u3002\u82b1\u6811\u4e1b\u4e2d\u8df3\u51fa\u4e00\u4eba\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u7f1a\u5728\u8c37\u4e2d\u83b2\u81c2\u4e0a\u7684\u7ea2\u7ef8\uff0c \u767b\u65f6\u5316\u4f5c\u4e86\u7247\u7247\u8774\u8776\uff0c\u5bf8\u5bf8\u88c2\u5f00\uff0c\u4f46\u5374\u4e1d\u6beb\u6ca1\u6709\u4f24\u7740\u8c37\u4e2d\u83b2\uff0c\u5251\u672f\u4e4b\u5999\uff0c\u771f\u662f\u5999\u5230\u6beb\u5dc5\uff01 \u3000\u3000\u8c37\u4e2d\u83b2\u60ca\u559c\u4ea4\u96c6\uff0c\u987f\u65f6\u95f4\u4e24\u4eba\u7d27\u7d27\u76f8\u62b1\uff0c\u4e00\u4eba\u53eb\u9053\uff1a\u201c\u5e08\u7236\u3002\u201d\u4e00\u4eba\u53eb\u9053\uff1a\u201c\u83b2\u513f\uff01\u201d \u539f\u6765\u6765\u7684\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u8c37\u4e2d\u83b2\u7684\u5e08\u7236\u8c37\u4e4b\u534e\u3002\u5979\u66fe\u51e0\u6b21\u5077\u5165\u738b\u5bab\uff0c\u4eca\u665a\u65b9\u59cb\u5e08\u5f92\u76f8\u9047\u3002 \u3000\u3000\u5e08\u5f92\u4fe9\u672a\u53ca\u53d9\u8bdd\uff0c\u5df2\u6709\u4eba\u95fb\u58f0\u8d76\u6765\uff0c\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5e08\u7236\uff0c\u968f\u6211\u6765\uff01\u201d\u8c37\u4e2d\u83b2\u719f\u6089\u9053 \u8def\uff0c\u8f7b\u529f\u53c8\u9ad8\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u5df2\u5e26\u5f15\u5e08\u7236\u8df3\u8fc7\u4e00\u9762\u9ad8\u5899\uff0c\u8fdb\u5165\u4e00\u5ea7\u5bab\u6bbf\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u600e\u4e48\u5e26\u6211\u5230\u56fd\u738b\u7684\u5bdd\u5bab\u6765\u4e86\uff1f\u201d\u8c37\u4e2d\u83b2\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u6700 \u597d\u7684\u907f\u96be\u4e4b\u6240\uff0c\u987a\u4fbf\u6211\u8fd8\u8981\u505a\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d\u8fd9\u65f6\u5df2\u662f\u4e8c\u66f4\u65f6\u5206\uff0c\u56fd\u738b\u548c\u5979\u7ea6\u4f1a\u7684\u65f6\u523b\u5df2 \u5230\uff0c\u5979\u4e5f\u65e0\u6687\u987e\u53ca\u65c1\u4eba\u7684\u4e8b\u60c5\u4e86\u3002 \u3000\u3000\u82b1\u5f00\u4e24\u6735\uff0c\u5404\u8868\u4e00\u6280\u3002\u6682\u4e14\u6309\u4e0b\u8c37\u4e2d\u83b2\u5e08\u5f92\u4e0d\u8868\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u518d\u53d9\u5929\u9b54\u6559\u4e3b\u4e4b\u4e8b\u3002 \u4e14\u8bf4\u5979\u548c\u5389\u590d\u751f\u4e14\u6218\u4e14\u8d70\uff0c\u5389\u590d\u751f\u8fde\u8fde\u53d1\u5578\uff0c\u62db\u5524\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\uff0c\u4e0d\u4e45\u53c8\u542c\u5f97\u91d1\u6bdb\u72e1\u4e00 \u58f0\u543c\u58f0\uff0c\u543c\u58f0\u51c4\u5389\uff0c\u4f3c\u662f\u5df2\u53d7\u4e86\u4f24\uff0c\u5389\u590d\u751f\u60ca\u5f02\u4e0d\u5b9a\uff0c\u6025\u5fd9\u671d\u7740\u90a3\u58f0\u97f3\u7684\u65b9\u5411\u8dd1\u53bb\uff0c\u524d \u9762\u662f\u4e00\u5757\u8349\u5730\uff0c\u6708\u5149\u4e0b\u9690\u7ea6\u53ef\u89c1\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u6b63\u4f0f\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5389\u590d\u751f\u53eb\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u5feb\u6765\uff0c\u91d1\u6bdb\u72e1\u5728\u8fd9\u513f\u5566\uff01\u201d\u5929\u9b54\u6559\u4e3b\u53d1\u51fa\u4e00\u84ec\u6bd2\u9488\uff0c\u7528\u5de7\u5999 \u7684\u624b\u6cd5\uff0c\u6253\u5728\u6587\u5ef7\u58c1\u7684\u524d\u5934\uff0c\u514d\u5f97\u4ed6\u8dd1\u8fc7\u53bb\u4f24\u5bb3\u91d1\u6bdb\u72e1\u3002\u6587\u5ef7\u58c1\u88ab\u8feb\u95ea\u8fc7\u4e00\u8fb9\u3002\u5389\u590d\u751f \u89c1\u90a3\u4e24\u53ea\u91d1\u6bdb\u72e1\u7adf\u4e0d\u8d77\u6765\u8fce\u63a5\u4e3b\u4eba\uff0c\u5fc3\u4e2d\u5df2\u9690\u9690\u611f\u5230\u4e0d\u5999\u3002\u8dd1\u4e0a\u53bb\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u4e24\u53ea\u91d1 \u6bdb\u72e1\u762b\u4f5c\u4e00\u56e2\uff0c\u6d51\u8eab\u7600\u9ed1\uff0c\u5389\u590d\u751f\u7528\u811a\u5c16\u8f7b\u8f7b\u78b0\u89e6\u5b83\u4eec\uff0c\u7adf\u662f\u52a8\u4e5f\u4e0d\u4f1a\u52a8\u4e86\u3002\u5389\u590d\u751f\u53eb \u58f0\uff1a\u201c\u82e6\u4e5f\uff01\u201d\u4ed6\u6307\u671b\u8fd9\u4e24\u53ea\u91d1\u6bdb\u72e1\u5e26\u4ed6\u4eec\u9003\u8d70\uff0c\u60f3\u4e0d\u5230\u91d1\u6bdb\u72e1\u5df2\u5148\u7ed9\u4eba\u6bd2\u6b7b\u4e86\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u89c1\u72b6\u5927\u60ca\uff0c\u5fd9\u5c06\u5389\u590d\u751f\u4e00\u628a\u62c9\u4f4f\uff0c\u53eb\u9053\uff1a\u201c\u5feb\u9000\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u6709 \u4eba\u9634\u607b\u607b\u5730\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u8fd8\u60f3\u8dd1\u4e48\uff1f\u201d\u82b1\u6811\u4e1b\u4e2d\uff0c\u73b0\u51fa\u4e00\u4eba\u3001\u6b63\u662f\u5929\u9b54\u6559\u4e3b\u7684\u5e08\u7236\u7ae5\u59e5 \u59e5\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7ae5\u59e5\u59e5\u5df2\u662f\u628a\u624b\u4e00\u573a\uff0c\u6ce2\u7684\u4e00\u58f0\uff0c\u4e00\u9053\u78a7\u83b9\u83b9\u7684\u51b7\u7130\u5bd2\u5149\u767b\u65f6\u5728 \u4ed6\u4eec\u7684\u9762\u524d\u94fa\u5c55\u5f00\u6765\u3002\u5389\u590d\u751f\u8fd8\u60f3\u786c\u95ef\u8fc7\u53bb\uff0c\u5929\u9b54\u6559\u4e3b\u53eb\u9053\uff1a\u201c\u4e0d\u53ef\uff01\u201d\u521a\u62c9\u5f97\u5389\u590d\u751f \u8f6c\u8fc7\u8eab\u6765\uff0c\u53ea\u542c\u5f97\u53c8\u662f\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u80cc\u540e\u4e5f\u6d8c\u73b0\u4e86\u4e00\u9053\u51b7\u7130\u5bd2\u5149\uff0c\u8f6c\u773c\u95f4\u5408\u6210\u4e86\u4e00\u9053 \u5149\u73af\uff0c\u8000\u773c\u751f\u8f89\uff0c\u5c06\u4ed6\u4eec\u4e8c\u4eba\u56f0\u5728\u5f53\u4e2d\u3002 \u3000\u3000\u8fd9\u56e2\u7130\u706b\u5e76\u4e0d\u662f\u771f\u6b63\u7684\u706b\u7130\uff0c\u7740\u7269\u5e76\u4e0d\u71c3\u70e7\uff0c\u5374\u4f3c\u6ce2\u6d6a\u4e00\u822c\u5728\u8349\u5730\u4e0a\u63a8\u8fdb\uff0c\u4f46\u7ecf\u8fc7 \u4e4b\u5904\uff0c\u8349\u6728\u7acb\u5373\u67af\u840e\uff0c\u5e76\u53d1\u51fa\u4e00\u80a1\u8150\u81ed\u7684\u6c14\u5473\u3002\u539f\u6765\u8fd9\u662f\u91cd\u59e5\u59e5\u91c7\u96c6\u53e4\u5893\u7684\u78f7\u8d28\u70bc\u6210\u7684 \u6bd2\u7130\uff0c\u78f7\u706b\u6240\u53d1\u7684\u5149\u662f\u89e6\u4f53\u51b0\u51c9\u7684\uff0c\u56e0\u9762\u5979\u8fd9\u624b\u6bd2\u529f\u6709\u4e2a\u53e4\u602a\u7684\u540d\u5b57\u53eb\u201c\u51b7\u7130\u641c\u9b42\u201d\uff0c \u9664\u975e\u5bf9\u65b9\u5df2\u7ec3\u6210\u91d1\u521a\u4e0d\u574f\u4e4b\u8eab\uff0c\u5426\u5219\u82e5\u88ab\u6cbe\u4e0a\uff0c\u5c31\u8981\u76ae\u8089\u6e83\u70c2\uff0c\u4e00\u65f6\u4e09\u523b\u4e4b\u5185\uff0c\u5168\u8eab\u5316 \u4e3a\u8840\u6c34\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u51b7\u7b11\u9053\uff1a\u201c\u5361\u5170\u59ae\uff0c\u4f60\u771f\u662f\u6211\u6559\u51fa\u6765\u7684\u597d\u5f92\u5f1f\uff0c\u4f60\u4e5f\u4e0d\u5ff5\u5ff5\u5e08\u7236\u7684\u6069\u60c5\uff0c\u6709 \u4e86\u60c5\u90ce\uff0c\u5c31\u8981\u53db\u79bb\u5e08\u7236\u4e86\u4e48\uff1f\u201d\u5389\u590d\u751f\u4ea2\u58f0\u8bf4\u9053\uff1a\u201c\u7537\u5a5a\u5973\u5ac1\uff0c\u8fd9\u4e5f\u7b97\u4e0d\u4e86\u662f\u53db\u79bb\u5e08\u7236\u3002\u201d \u3000\u3000\u7ae5\u59e5\u59e5\u65a5\u9053\uff1a\u201c\u591a\u5634\uff0c\u6211\u95e8\u4e0b\u7684\u5f1f\u5b50\u7ec8\u8eab\u5927\u4e8b\u5c31\u8981\u7531\u6211\u4f5c\u4e3b\u3002 \u3000\u3000\u5979\u5ac1\u4ec0\u4e48\u4eba\u90fd\u53ef\u4ee5\uff0c\u5c31\u662f\u4e0d\u8bb8\u5979\u5ac1\u4f60\u3002\u54fc\uff0c\u4f60\u524d\u65e5\u5c06\u6211\u620f\u800d\uff0c\u628a\u7f57\u66fc\u5a1c\u516c\u4e3b\u62a2\u53bb\u3002 \u6211\u8fd8\u672a\u60e9\u7f5a\u4f60\u5462\uff0c\u5982\u4eca\u53c8\u60f3\u6765\u52fe\u5f15\u6211\u7684\u5f92\u5f1f\u4e86\u3002\u4e14\u53eb\u4f60\u77e5\u9053\u4e00\u70b9\u5389\u5bb3\uff01\u201d\u4e00\u638c\u62cd\u51fa\uff0c\u4e00 \u56e2\u51b7\u7130\u7f69\u5728\u91d1\u6bdb\u72e1\u8eab\u4e0a\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u4e24\u53ea\u8eab\u8eaf\u7c97\u58ee\u7684\u91d1\u6bdb\u72e1\u5df2\u5728\u773c\u524d\u6d88\u5931\uff0c\u5730\u4e0a\u53ea\u9057\u4e0b \u4e00\u5806\u76ae\u6bdb\uff0c\u4e00\u6ee9\u8840\u6c34\u3002\u7ae5\u59e5\u59e5\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u90a3\u65e5\u4ed7\u7740\u91d1\u6bdb\u72e1\u8131\u8eab\uff0c\u5982\u4eca\u6211\u770b\u4f60\u8fd8\u80fd\u4ed7\u5b83 \u4e4b\u529b\u4e48\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u8fd9\u662f\u6211\u81ea\u613f\u8ddf\u968f\u4ed6\u7684\uff0c\u6709\u9519\u9519\u53ea\u5728\u6211\uff0c\u5e08\u7236\uff0c\u8bf7\u4f60\u653e\u8fc7\u4e86\u4ed6\uff0c\u8981\u5904 \u7f5a\u5c31\u5904\u7f5a\u6211\u5427\u3002\u201d\u7ae5\u59e5\u59e5\u9053\uff1a\u201c\u4f60\u77e5\u9053\u53db\u5e08\u79c1\u9003\uff0c\u8be5\u5f53\u4f55\u7f6a\uff1f\u201d\u5929\u9b54\u6559\u4e3b\u9053\uff1a\u201c\u6211\u7518\u613f \u88ab\u6bd2\u7130\u70bc\u5316\u3002\u201d\u5389\u590d\u751f\u53eb\u9053\uff1a\u201c\u4e0d\uff0c\u662f\u6211\u5192\u72af\u4e86\u4f60\uff0c\u6709\u7f6a\u6211\u4e00\u4eba\u627f\u62c5\u3002\u201d\u7ae5\u59e5\u59e5\u51b7\u7b11\u9053\uff1a \u3000\u3000\u201c\u4f60\u4eec\u5012\u662f\u6069\u7231\u5f97\u5f88\u554a\uff01\u201d\u4f38\u624b\u4e00\u6307\uff0c\u4e00\u6735\u7130\u706b\u98de\u4e86\u8fc7\u6765\u3002\u5929\u9b54\u6559\u4e3b\u4e00\u58f0\u5c16\u53eb\uff0c\u4e0d \u5047\u601d\u7d22\u7684\u5c31\u628a\u5389\u590d\u751f\u62b1\u4f4f\uff0c\u8981\u4e3a\u4ed6\u63a9\u853d\uff0c\u5389\u590d\u751f\u54ea\u80af\u8ba9\u5979\u5982\u6b64\uff0c\u53cd\u624b\u5c06\u5979\u62b1\u4f4f\uff0c\u5374\u628a\u81ea \u5df1\u7684\u8eab\u8eaf\u6321\u5728\u5979\u7684\u8eab\u524d\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u5ffd\u5730\u628a\u624b\u4e00\u62db\uff0c\u628a\u90a3\u6735\u7130\u706b\u62db\u56de\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u504f\u4e0d\u8ba9\u4f60\u4eec\u79f0\u5fc3\u5982\u610f\uff0c\u540c \u751f\u5171\u6b7b\u3002\u201d\u53cc\u624b\u9f50\u626c\uff0c\u547c\u547c\u58f0\u54cd\uff0c\u98de\u51fa\u4e86\u4e24\u6761\u7ef3\u7d22\uff0c\u5c06\u5929\u9b54\u6559\u4e3b\u548c\u5389\u590d\u751f\u90fd\u7f1a\u4e86\u8d77\u6765\uff0c \u53cc\u624b\u6267\u7740\u7ef3\u7d22\uff0c\u5c06\u4ed6\u4eec\u540a\u4e86\u8d77\u6765\uff0c\u53ec\u5f00\u4e86\u90a3\u5708\u7130\u706b\u3002\u8fd9\u4e24\u6761\u7ef3\u7d22\u4e43\u662f\u5929\u8327\u4e1d\u6240\u5236\uff0c\u575a\u97e7 \u975e\u5e38\uff0c\u4e00\u88ab\u7f1a\u4e0a\uff0c\u591a\u597d\u6b66\u529f\uff0c\u4e5f\u6323\u8131\u4e0d\u5f00\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u6349\u4e86\u4ed6\u4eec\u4e8c\u4eba\uff0c\u6b63\u81ea\u563f\u563f\u51b7\u7b11\uff0c\u5ffd\u6709\u4e2a\u6b66\u571f\u4e0a\u6765\u62a5\u9053\uff1a\u201c\u7980\u5723\u6bcd\uff0c\u9a6c\u8428\u513f\u56fd \u7684\u516c\u4e3b\u5df2\u7ecf\u9003\u8dd1\u5566\u3002\u201d\u7ae5\u59e5\u59e5\u53cc\u76ee\u4e00\u77aa\uff0c\u559d\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u90a3\u6b66\u58eb\u9053\uff1a\u201c\u7f2a\u592b\u4eba\u5012\u5728\u5730 \u4e0a\uff0c\u4f3c\u662f\u88ab\u4eba\u70b9\u4e86\u7a74\u9053\u3002\u6211\u4eec\u4e0d\u61c2\u89e3\u7a74\uff0c\u4e0d\u6562\u5c06\u5979\u79fb\u52a8\uff0c\u60c5\u5723\u6bcd\u4eb2\u81ea\u53bb\u770b\u3002\u201d \u3000\u3000\u7ae5\u59e5\u59e5\u9053\uff1a\u201c\u6587\u5148\u751f\uff0c\u8bf7\u968f\u6211\u6765\u3002\u201d\u627e\u5230\u4e86\u7f2a\u592b\u4eba\uff0c\u6587\u5ef7\u58c1\u770b\u4e86\u4e00\u773c\uff0c\u4fbf\u9053\uff1a\u201c\u4e0d \u9519\uff0c\u8fd9\u662f\u9099\u5c71\u6d3e\u7684\u70b9\u7a74\u624b\u6cd5\u3002\u54a6\uff0c\u8fd9\u53ef\u5947\u602a\u4e86\uff0c\u90a3\u4e2b\u5934\u4e0d\u662f\u7740\u4e86\u4fee\u7f57\u9165\u9aa8\u6563\u7684\u5417\uff0c\u600e\u7684 \u8fd8\u6709\u5982\u6b64\u529f\u529b\uff1f\u201d\u91cd\u59e5\u59e5\u9762\u8272\u94c1\u9752\uff0c\u5728\u7f2a\u5927\u4eba\u80cc\u5fc3\u4e00\u62cd\uff0c\u89e3\u5f00\u4e86\u5979\u7684\u7a74\u9053\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u600e\u7684\u5982\u6b64\u4e0d\u6d4e\u3002\u7740\u4e86\u90a3\u4e2b\u5934\u7684\u6697\u7b97\uff1f\u201d\u7f2a\u592b\u4eba\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e2b\u5934\u7684\u5e08 \u7236\u6765\u4e86\u3002\u201d \u3000\u3000\u5728\u56ed\u4e2d\u5404\u5904\u641c\u5bfb\u7684\u6b66\u4e0a\u9646\u7eed\u56de\u6765\uff0c\u90fd\u8bf4\u6ca1\u6709\u53d1\u73b0\u8c37\u4e2d\u83b2\u7684\u8e2a\u8ff9\u3002\u7ae5\u59e5\u59e5\u5fc3\u4e2d\u5df2\u7ecf\u96ea \u4eae\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u7528\u60ca\u614c\uff0c\u6211\u5df2\u7ecf\u77e5\u9053\u5979\u85cf\u533f\u4e4b\u6240\u4e86\u3002\u4f60\u4eec\u90fd\u56de\u53bb\u6b47\u606f\u5427\uff0c\u8c05\u90a3 \u5c0f\u4e2b\u5934\u9003\u4e0d\u51fa\u6211\u7684\u624b\u5fc3\u3002\u54fc\uff0c\u5979\u7684\u5e08\u7236\u4e5f\u6765\u4e86\uff1f\u6765\u5f97\u6b63\u597d\uff01\u542c\u8bf4\u5979\u5c31\u662f\u91d1\u4e16\u9057\u7684\u8001\u60c5\u4eba\uff1f\u201d \u3000\u3000\u6587\u5ef7\u58c1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u8001\u4eba\u5bb6\u82e5\u662f\u628a\u8c37\u4e4b\u534e\u64d2\u83b7\uff0c\u7ba1\u6559\u90a3\u91d1\u4e16\u9057\u8981\u670d\u670d\u5e16\u5e16\u5730\u542c\u4f60 \u7684\u8bdd\u3002\u201d\u8bdd\u51fa\u4e4b\u540e\uff0c\u53d1\u89c9\u7ae5\u59e5\u59e5\u9762\u8272\u6709\u70b9\u4e0d\u5bf9\uff0c\u6587\u5ef7\u58c1\u4f55\u7b49\u673a\u7075\uff0c\u8fde\u5fd9\u628a\u8bdd\u9898\u518d\u515c\u56de\u6765 \u8bf4\u9053\uff1a\u201c\u5176\u5b9e\u4ee5\u4f60\u8001\u4eba\u5bb6\u7684\u795e\u901a\uff0c\u91d1\u4e16\u9057\u4e5f\u7edd\u4e0d\u662f\u4f60\u7684\u5bf9\u624b\u3002\u4e0d\u8fc7\uff0c\u5148\u628a\u4ed6\u7684\u60c5\u4eba\u62ff\u4e86 \u4e0b\u6765\uff0c\u6c14\u4ed6\u4e00\u6c14\uff0c\u4e5f\u662f\u597d\u7684\u3002\u201d \u3000\u3000\u7ae5\u59e5\u59e5\u9053\uff1a\u201c\u4f0a\u58c1\u73e0\u739b\uff0c\u6211\u76f8\u4fe1\u4f60\u4e0d\u4f1a\u53db\u6211\uff0c\u4f60\u7684\u59b9\u59b9\uff0c\u6211\u5c31\u4ea4\u7ed9\u4f60\u770b\u7ba1\u4e86\u3002\u201d\u7f28 \u592b\u4eba\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u653e\u5fc3\uff0c\u6211\u7edd\u4e0d\u4f1a\u5f87\u60c5\u3002\u6211\u7684\u59b9\u5b50\u4e0d\u77e5\u7f9e\u803b\uff0c\u4e0e\u4eba\u79c1\u5954\uff0c\u8fd9\u672c\u6765\u662f \u5979\u7684\u4e0d\u597d\u3002\u5e08\u7236\uff0c\u5373\u4f7f\u4f60\u8981\u9976\u5979\uff0c\u6211\u8fd9\u4e2a\u505a\u59d0\u59d0\u7684\u4e5f\u8981\u6267\u884c\u5bb6\u6cd5\u7684\u3002\u201d \u3000\u3000\u7ae5\u59e5\u59e5\u70b9\u4e86\u70b9\u5934\uff0c\u5f88\u662f\u6ee1\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u8fd9\u4e2a\u59d3\u5389\u7684\u5c0f\u5b50\u2014\u2014\u201d\u56de\u8fc7\u5934\u6765\uff0c\u5bf9\u6587 \u5ef7\u58c1\u9053\uff1a\u201c\u6587\u5148\u751f\uff0c\u4f60\u4e5f\u5e2e\u5fd9\u6211\u770b\u7ba1\u770b\u7ba1\u4ed6\u4eec\u3002\u201d\u6587\u5ef7\u58c1\u6c42\u4e4b\u4e0d\u5f97\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u7aed \u529b\u6548\u52b3\u3002\u201d\u7ae5\u59e5\u59e5\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f46\u6211\u4e5f\u4e0d\u8bb8\u6709\u8c01\u516c\u62a5\u79c1\u4ec7\uff0c\u5fc5\u987b\u7b49\u6211\u56de\u6765\uff0c\u518d\u884c\u5904\u7f6e\u3002 \u3000\u3000\u597d\uff0c\u4f0a\u58c1\u73e0\u739b\uff0c\u4f60\u548c\u6587\u5148\u751f\u5c06\u4ed6\u4eec\u4e8c\u4eba\u62bc\u56de\u6211\u7684\u5bab\u4e2d\u5427\u3002\u201d \u3000\u3000\u7ae5\u59e5\u59e5\u9063\u5f00\u4f17\u4eba\u540e\uff0c\u4fbf\u72ec\u81ea\u53bb\u89c1\u56fd\u738b\uff0c\u539f\u6765\u5979\u65e9\u5df2\u6599\u5230\u8c37\u4e2d\u83b2\u662f\u8eb2\u5728\u56fd\u738b\u7684\u5bdd\u5bab\u4e86\u3002 \u3000\u3000\u4e14\u8bf4\u8c37\u4e2d\u83b2\u8fdb\u4e86\u5bdd\u5bab\u4e4b\u540e\uff0c\u4fbf\u5e26\u5f15\u5979\u7684\u5e08\u7236\uff0c\u6084\u6084\u5730\u76f4\u5954\u96c6\u8d24\u9601\u3002\u56fd\u738b\u7684\u5bdd\u5bab\u5305\u62ec \u6709\u5341\u51e0\u5e62\u5efa\u7b51\uff0c\u8fd9\u96c6\u8d24\u9601\u662f\u56fd\u738b\u7528\u4f5c\u4f1a\u5ba2\u7684\u5730\u65b9\u3002\u5e08\u5f92\u4fe9\u5728\u5c4b\u9876\u4e0a\u884c\u8d70\uff0c\u98de\u8d8a\u4e86\u5341\u51e0\u91cd \u74e6\u9762\uff0c\u7ecf\u8fc7\u4e4b\u5904\uff0c\u53ea\u89c1\u5047\u5c71\u80cc\u540e\uff0c\u82b1\u6811\u4e1b\u4e2d\uff0c\u5f71\u7ef0\u7ef0\u7684\u4f3c\u4e4e\u6709\u4e0d\u5c11\u4eba\u57cb\u4f0f\u5176\u95f4\uff0c\u5c06\u5230\u96c6 \u8d24\u9601\uff0c\u53c8\u89c1\u6709\u4e00\u5c0f\u961f\u9ed1\u8863\u4eba\u6b63\u81ea\u5411\u7740\u96c6\u8d24\u9601\u8d70\u6765\uff0c\u5e78\u800c\u5979\u4eec\u4e24\u5e08\u5f92\u8f7b\u529f\u8d85\u5999\uff0c\u4ece\u6570\u4e08\u5916 \u7684\u5c4b\u9876\u8dc3\u8fc7\u96c6\u8d24\u9601\uff0c\u4fe8\u5982\u4e24\u7247\u6811\u53f6\uff0c\u8f7b\u98d8\u98d8\u5730\u843d\u4e0b\u6765\uff0c\u6beb\u65e0\u58f0\u54cd\uff0c\u7adf\u662f\u65e0\u4eba\u53d1\u89c9\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u6084\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u770b\u60c5\u5f62\u4f3c\u4e4e\u662f\u6709\u4e2a\u9e3f\u95e8\u5bb4\uff0c\u53ea \u6015\u6211\u4e5f\u8981\u88ab\u5377\u5165\u6f29\u6da1\uff0c\u7b49\u4e0b\u82e5\u7136\u6709\u4e8b\uff0c\u5e08\u7236\uff0c\u4f60\u7ed9\u6211\u628a\u98ce\u3002\u201d \u3000\u3000\u4e24\u5e08\u5f92\u521a\u5728\u6a90\u69fd\u5185\u4f0f\u4e0b\u6765\uff0c\u53ea\u542c\u5f97\u9601\u5916\u5df2\u6709\u4eba\u62a5\u9053\uff1a\u201c\u6cf0\u6e05\u738b\u5230\uff01\u201d\u8c37\u4e2d\u83b2\u5fc3\u9053\uff1a \u201c\u6211\u6765\u5f97\u6070\u662f\u65f6\u5019\u3002\u4e14\u522b\u5fd9\u7740\u8fdb\u53bb\uff0c\u5148\u770b\u770b\u5c4b\u5185\u662f\u600e\u4e48\u4e2a\u60c5\u5f62\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u63ed\u5f00\u4e00\u5757\u7409\u7483\u74e6\uff0c\u7528\u6307\u529b\u5728\u5c4b\u9876\u6233\u7a7f\u4e00\u4e2a\u5c0f\u6d1e\uff0c\u8d34\u7740\u773c\u775b\uff0c\u5077\u671b\u8fdb\u53bb\uff0c\u53ea\u89c1 \u5c4b\u4e2d\u53ea\u6709\u56fd\u738b\u4e00\u4eba\uff0c\u4f46\u8c37\u4e2d\u83b2\u542c\u89c9\u7075\u654f\uff0c\u5374\u9690\u9690\u611f\u5230\u6709\u8bb8\u591a\u4eba\u547c\u5438\u7684\u6c14\u606f\uff0c\u6709\u65f6\u8fd8\u6742\u6709 \u6781\u8f7b\u5fae\u7684\u6089\u6089\u7d22\u7d22\u7684\u8863\u88f3\u6469\u64e6\u4e4b\u58f0\uff0c\u8c37\u4e2d\u83b2\u6014\u4e86\u4e00\u6014\uff0c\u7acb\u5373\u7701\u89c9\uff1a\u201c\u539f\u6765\u56fd\u738b\u5728\u590d\u58c1\u4e4b \u5185\u3002\u4ea6\u5df2\u4f0f\u4e0b\u7532\u5175\uff1a\u201d\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u4ed6\u65e2\u51c6\u5907\u5f97\u8fd9\u4e48\u5468\u5bc6\uff0c\u82e5\u975e\u5fc5\u8981\uff0c\u6211\u4e5f\u65e0\u987b\u9732\u9762\u4e86\u3002 \u514d\u5f97\u6253\u8349\u60ca\u86c7\uff0c\u62db\u5f15\u90a3\u5996\u5987\u8ffd\u6765\u3002\u201d \u3000\u3000\u56fd\u738b\u5429\u5490\u90a3\u901a\u62a5\u7684\u6b66\u58eb\u9053\uff1a\u201c\u8bf7\u6cf0\u6e05\u738b\u8fdb\u6765\uff0c\u4ed6\u7684\u968f\u4ece\uff0c\u4f60\u4eec\u7ed9\u6211\u62db\u5f85\u3002\u201d\u6084\u6084\u6253 \u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u90a3\u6b66\u58eb\u5fc3\u9886\u795e\u4f1a\uff0c\u5e94\u4e86\u4e00\u58f0\u201c\u9075\u65e8\u201d\uff0c\u4fbf\u50f5\u9000\u4e0b\u3002 \u3000\u3000\u56fd\u738b\u867d\u6709\u5e03\u7f6e\uff0c\u5fc3\u4e2d\u5374\u4e5f\u60f4\u60f4\u4e0d\u5b89\uff0c\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u6cf0\u6e05\u738b\u6b66\u529f\u975e\u540c\u5c0f\u53ef\uff0c\u504f\u504f\u6211\u90a3 \u5802\u59b9\u59b9\u53c8\u7ed9\u5723\u6bcd\u62ff\u53bb\u4e86\uff0c\u82e5\u7136\u4e07\u4e00\u6349\u864e\u4e0d\u6210\uff0c\u53ea\u6015\u53cd\u800c\u8981\u88ab\u8001\u864e\u6240\u54ac\u3002\u7f62\uff0c\u7f62\uff0c\u6211\u4e14\u89c1 \u673a\u9762\u4f5c\uff0c\u5148\u5957\u5957\u4ed6\u7684\u53e3\u98ce\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u90a3\u6cf0\u6e05\u738b\u5df2\u8d70\u4e86\u8fdb\u6765\uff0c\u5916\u9762\u7acb\u5373\u6709\u4eba\u628a\u95e8\u5173\u4e86\u3002 \u3000\u3000\u6cf0\u6e05\u738b\u89c1\u5c4b\u4e2d\u53ea\u662f\u56fd\u738b\u4e00\u4eba\uff0c\u5fae\u9732\u8be7\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u965b\u4e0b\u6df1\u591c\u5ba3\u53ec\uff0c\u53ef\u662f\u6709\u4ec0\u4e48\u673a\u5bc6 \u4e4b\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u56fd\u738b\u9053\uff1a\u201c\u7687\u53d4\u8bf7\u5750\u3002\u6211\u6b63\u662f\u542c\u5230\u4e86\u4e00\u6869\u7a00\u5947\u53e4\u602a\u7684\u4e8b\u60c5\uff0c\u60f3\u8bf7\u95ee\u7687\u53d4\u3002\u201d\u6cf0\u6e05\u738b \u9053\uff1a\u201c\u54e6\uff0c\u600e\u4e48\u6837\u7a00\u5947\u53e4\u602a\uff1f\u201d\u56fd\u738b\u538b\u4f4e\u58f0\u97f3\u9053\uff1a\u201c\u542c\u8bf4\u6211\u56fd\u51fa\u4f7f\u9a6c\u8428\u513f\u56fd\u7684\u4f7f\u81e3\uff0c\u662f \u56e0\u4e3a\u5077\u5165\u4ed6\u4eec\u7684\u5b9d\u5e93\uff0c\u5f7c\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u738b\u53d1\u73b0\uff0c\u56e0\u800c\u88ab\u6740\u7684\u3002\u4f60\u662f\u4ed6\u7684\u7236\u4eb2\u3002\u53ef\u77e5\u9053\u4ed6 \u5077\u5165\u5b9d\u5e93\u7684\u7f18\u6545\u4e48\uff1f\u201d\u6cf0\u6e05\u738b\u9762\u8272\u4e00\u53d8\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6d88\u606f\u53ef\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u56fd\u738b\u9053\uff1a\u201c\u6d88\u606f\u6765\u6e90\u53ef\u9760\uff0c\u591a\u534a\u4e0d\u4f1a\u662f\u5047\u3002\u201d\u6cf0\u6e05\u738b\u9053\uff1a\u201c\u8fd9\u6d88\u606f\u662f\u8c01\u544a\u8bc9\u965b\u4e0b\u7684\uff1f\u201d \u56fd\u738b\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u7687\u53d4\u4f60\u5c31\u4e0d\u5fc5\u95ee\u4e86\u3002\u201d \u3000\u3000\u6cf0\u6e05\u738b\u5f90\u5f90\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u542c\u5230\u4e86\u4e00\u6869\u7a00\u5947\u53e4\u602a\u7684\u4e8b\u60c5\u3002\u201d\u56fd\u738b\u9053\uff1a\u201c\u54e6\uff0c\u600e\u4e48\u6837\u7a00 \u5947\u53e4\u602a\uff1f\u201d\u6cf0\u6e05\u4e3b\u9053\uff1a\u201c\u542c\u8bf4\u6709\u4e00\u4e2a\u56fd\u5bb6\uff0c\u56fd\u738b\u5374\u662f\u5916\u56fd\u4eba\u3002\u4ed6\u505a\u4e86\u51e0\u5341\u5e74\u56fd\u738b\uff0c\u4e00\u76f4 \u4f20\u5230\u4e86\u4ed6\u7684\u5b59\u5b50\uff0c\u81e3\u6c11\u7adf\u7136\u8fd8\u672a\u77e5\u9053\u8fd9\u4e2a\u79d8\u5bc6\u3002\u201d\u56fd\u738b\u52c3\u7136\u53d8\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u54ea \u4e2a\u56fd\u5bb6\uff1f\u201d\u6cf0\u6e05\u738b\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u965b\u4e0b\u4f60\u5c31\u4e0d\u5fc5\u95ee\u4e86\uff0c\u5927\u7ea6\u603b\u4e0d\u662f\u54b1\u4eec\u6606\u5e03\u5170\u56fd\u5427\uff1f\u201d \u3000\u3000\u56fd\u738b\u5ffd\u5730\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u7687\u53d4\uff0c\u54b1\u4eec\u4e0d\u7528\u5f7c\u6b64\u731c\u5fcc\u4e86\uff0c\u4f60\u53ef\u77e5\u9053\u6211\u8bf7\u4f60\u6765\u6b64\u7684\u7528\u610f\u4e48\uff1f\u201d \u6cf0\u6e05\u738b\u9053\uff1a\u201c\u6b63\u8981\u8bf7\u6559\u3002\u201d\u56fd\u738b\u9053\uff1a\u201c\u6211\u660e\u5929\u66f4\u8981\u4e0b\u4ee4\u5174\u5175\uff0c\u60f3\u8bf7\u4f60\u505a\u4e09\u519b\u7edf\u5e05\uff0c\u517c\u4efb \u6444\u653f\u738b\u3002\u672c\u56fd\u7684\u519b\u653f\u5927\u6743\u4ece\u6b64\u90fd\u4ea4\u7ed9\u4f60\u4e86\u3002\u201d \u3000\u3000\u6cf0\u6e05\u738b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u965b\u4e0b\u4f55\u4ee5\u5982\u6b64\u91cd\u7528\u8001\u81e3\uff0c\u5374\u6559\u8001\u81e3\u600e\u751f\u62c5\u5f53\u5f97\u8d77\uff1f\u201d\u56fd\u738b\u9053\uff1a \u201c\u6211\u5e74\u8f7b\u8bc6\u6d45\uff0c\u6b63\u8981\u7687\u53d4\u8fd9\u6837\u7cbe\u660e\u5e72\u7ec3\u7684\u4eba\u8f85\u4f50\uff0c\u65b9\u80fd\u4fdd\u4f4f\u6c5f\u5c71\u3002\u8bf7\u7687\u53d4\u5207\u52ff\u63a8\u8f9e\u3002\u8fd9 \u662f\u6211\u4e00\u756a\u8bda\u610f\u3002\u201d \u3000\u3000\u6cf0\u6e05\u738b\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u8fd9\u91ce\u5c0f\u5b50\u6015\u6211\u63ed\u7a7f\u4ed6\u7684\u79d8\u5bc6\uff0c\u6545\u800c\u7528\u6743\u4f4d\u6765\u8d3f\u8d42\u6211\u3002\u4e5f\u7f62\uff0c\u6211 \u82e5\u73b0\u5728\u7be1\u4f4d\uff0c\u53ea\u6015\u65f6\u673a\u4e5f\u5c1a\u672a\u6210\u719f\uff0c\u4e0d\u5982\u5148\u505a\u4e86\u6444\u653f\u738b\u4e5f\u597d\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u965b\u4e0b \u8bda\u5fc3\u4ed8\u6258\uff0c\u8001\u81e3\u4e5f\u4e0d\u6562\u63a8\u8f9e\u4e86\u3002\u201d \u3000\u3000\u56fd\u738b\u5927\u4e3a\u9ad8\u5174\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4ece\u6b64\u6211\u4e0e\u53d4\u7236\uff0c\u4e24\u4eba\u4fbf\u662f\u4e00\u4eba\u4e86\u3002\u795d\u53d4\u7236\u65d7\u5f00\u5f97\u80dc\uff0c \u505a\u4f84\u513f\u7684\u656c\u4f60\u4e00\u676f\u3002\u201d\u8131\u7f62\uff0c\u5728\u4e00\u4e2a\u58f6\u4e2d\u659f\u51fa\u4e86\u4e24\u676f\u9152\uff0c\u6cf0\u6e05\u738b\u9053\uff1a\u201c\u505a\u81e3\u5b50\u7684\u4e0d\u6562\u50ed \u8d8a\u3002\u201d\u56fd\u738b\u7b11\u9053\uff1a\u201c\u53d4\u4f84\u4e4b\u95f4\uff0c\u4f55\u5fc5\u62d8\u6267\u541b\u81e3\u4e4b\u793c\u3002\u4e5f\u597d\uff0c\u6211\u5c31\u5148\u5e72\u4e3a\u656c\u5427\u3002\u201d\u4e3e\u8d77\u9152 \u676f\uff0c\u4e00\u996e\u800c\u5c3d\uff1a\u8bf4\u9053\uff1a\u201c\u7687\u53d4\uff0c\u8bf7\uff01\u201d \u3000\u3000\u6cf0\u6e05\u738b\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f9d\u7167\u541b\u81e3\u4e4b\u793c\uff0c\u6211\u8fd8\u9886\u5148\u656c\u965b\u4e0b\u4e00\u676f\uff0c\u965b\u4e0b\uff0c\u8bf7\uff01\u201d\u5c31 \u5c06\u4ed6\u9762\u524d\u8fd9\u676f\u9012\u8fc7\u53bb\u3002\u56fd\u738b\u9053\uff1a\u201c\u8fd9\u676f\u53d4\u7236\u559d\u5427\uff0c\u6211\u81ea\u5df1\u518d\u659f\u4e00\u676f\uff0c\u5949\u966a\u4fbf\u662f\u3002\u201d\u6cf0\u6e05 \u738b\u5ffd\u5730\u5c06\u9152\u676f\u4e00\u6454\uff0c\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u965b\u4e0b\uff0c\u4f60\u771f\u662f\u8ba1\u8c0b\u591a\u7aef\uff0c\u5374\u53ef\u60dc\u7792\u4e0d\u8fc7\u6211\uff01\u201d \u3000\u3000\u9152\u676f\u63b7\u5730\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u788e\u6210\u56db\u7247\uff0c\u540c\u65f6\u8d77\u4e86\u4e00\u56e2\u706b\u7130\u3002 \u3000\u3000\u539f\u6765\u56fd\u738b\u90a3\u4e2a\u9152\u58f6\uff0c\u4e43\u662f\u5de7\u5320\u6253\u9020\uff0c\u5185\u6709\u673a\u5173\uff0c\u5206\u4e3a\u4e24\u683c\uff0c\u4e0a\u4e00\u5e45\u662f\u6bd2\u9152\uff0c\u4e0b\u4e00\u683c \u662f\u4f73\u917f\uff0c\u56fd\u738b\u624b\u6309\u58f6\u67c4\uff0c\u64cd\u7eb5\u673a\u5173\uff0c\u56e0\u800c\u867d\u662f\u540c\u4e00\u4e2a\u58f6\u5b50\u659f\u51fa\u6765\u7684\u9152\uff0c\u5374\u662f\u4e00\u676f\u6709\u6bd2\uff0c \u4e00\u676f\u65e0\u6bd2\u3002\u4ed6\u81ea\u5df1\u996e\u7684\u662f\u4f73\u917f\uff0c\u8d50\u7ed9\u6cf0\u6e05\u738b\u7684\u5219\u662f\u6bd2\u9152\u3002 \u3000\u3000\u5374\u4e0d\u6599\u6cf0\u6e05\u738b\u8bc6\u7834\u673a\u5173\uff0c\u6bd2\u9152\u4e00\u6cfc\uff0c\u767b\u65f6\u5c31\u628a\u56fd\u738b\u4e00\u628a\u6293\u4f4f\uff0c\u590d\u58c1\u4e2d\u7684\u6b66\u58eb\u542c\u5f97\u9152 \u676f\u6454\u5730\u7684\u58f0\u54cd\uff0c\u7acb\u5373\u8df3\u4e86\u51fa\u6765\uff0c\u4f46\u4e5f\u5df2\u7ecf\u8fdf\u4e86\u3002 \u3000\u3000\u6cf0\u6e05\u738b\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u4e0d\u8981\u6027\u547d\uff1f\u6211\u8bf4\u4e00\u53e5\uff0c\u4f60\u4f9d\u4e00\u53e5\uff01\u201d\u56fd\u738b\u98a4\u58f0\u8bf4\u9053\uff1a \u201c\u53d4\u7236\u6709\u547d\uff0c\u5c0f\u4f84\u6562\u4e0d\u4f9d\u4ece\uff1f\u201d\u6cf0\u6e05\u738b\u5550\u4e86\u4e00\u53e3\u9053\uff1a\u201c\u5478\uff0c\u8c01\u662f\u4f60\u7684\u53d4\u7236\uff1f\u4f60\u82e5\u8981\u9976\u547d\uff0c \u5148\u628a\u4f60\u7684\u8eab\u4e16\u6765\u5386\u5bf9\u4f60\u624b\u4e0b\u8bf4\u4e86\u51fa\u6765\uff01\u201d\u56fd\u738b\u9762\u5982\u6b7b\u7070\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2014\u2014\u201d \u6cf0\u6e05\u738b\u4e94\u6307\u7528\u529b\u4e00\u94b3\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8bf4\u4e0d\u8bf4\uff1f\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u559d\u9053\uff1a\u201c\u677e\u624b\uff01\u201d\u8fd9\u4eba\u7684\u8bf4\u8bdd\u6bd4\u4ec0\u4e48\u5723\u65e8\u90fd\u6709\u6548\uff0c\u6cf0\u6e05\u738b\u7a81 \u7136\u4f3c\u7ed9\u4eba\u7528\u5229\u9488\u523a\u4e86\u4e00\u4e0b\u4f3c\u7684\uff0c\u767b\u65f6\u6d51\u8eab\u9178\u8f6f\uff0c\u6c14\u529b\u6beb\u65e0\uff0c\u53cc\u624b\u8f6f\u7ef5\u7ef5\u5730\u5782\u4e86\u4e0b\u6765\uff0c\u679c \u7136\u677e\u4e86\u5bf9\u56fd\u738b\u7684\u675f\u7f1a\u3002\u539f\u6765\u662f\u8c37\u4e2d\u83b2\u4ece\u7a97\u5b50\u8df3\u4e86\u8fdb\u6765\uff0c\u4ee5\u9694\u7a7a\u70b9\u7a74\u7684\u529f\u592b\uff0c\u70b9\u4e86\u6cf0\u6e05\u738b \u7684\u7a74\u9053\u3002 \u3000\u3000\u56fd\u738b\u57cb\u4f0f\u5728\u590d\u58c1\u4e2d\u7684\u6b66\u58eb\uff0c\u90fd\u662f\u5bf9\u4ed6\u5fe0\u5fc3\u803f\u803f\u7684\u5fc3\u8179\u4e4b\u4eba\uff0c\u8d77\u5148\u56e0\u4e3a\u6295\u9f20\u5fcc\u5668\uff0c\u4e0d \u6562\u5984\u52a8\uff0c\u6b64\u65f6\u4e00\u89c1\u6cf0\u6e05\u738b\u677e\u624b\uff0c\u7acb\u5373\u62a1\u5200\u9773\u6765\u3002\u6b66\u58eb\u4eec\u4e00\u62e5\u800c\u4e0a\uff0c\u5012\u628a\u8c37\u4e2d\u83b2\u963b\u4f4f\u4e86\u3002 \u3000\u3000\u5176\u4e2d\u4ee5\u7fbd\u6797\u519b\u603b\u7ba1\u6765\u5f97\u6700\u5feb\uff0c\u773c\u770b\u957f\u5200\u5c31\u8981\u5288\u5230\u6cf0\u6e05\u738b\u8eab\u4e0a\uff0c\u5ffd\u5730\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c \u535c\u901a\u8dcc\u5012\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u4e00\u4e2a\u9ed1\u8863\u4eba\u5df2\u662f\u7834\u95e8\u800c\u5165\uff0c\u53cc\u638c\u6325\u821e\uff0c\u638c\u98ce\u6240\u53ca\uff0c\u65e0 \u4e0d\u62ab\u9761\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u9664\u4e86\u8c37\u4e2d\u83b2\u4e4b\u5916\uff0c\u5c4b\u5b50\u91cc\u7684\u5341\u51e0\u540d\u6b66\u58eb\uff0c\u90fd\u5df2\u7ed9\u4ed6\u6253\u7ffb\uff01 \u3000\u3000\u8c37\u4e2d\u83b2\u4e5f\u4e0d\u7981\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u6b66\u529f\u4e16\u6240\u7f55\u89c1\uff0c\u6cf0\u6e05\u738b\u4e0d\u8fc7\u662f\u4e00\u4e2a\u5c0f\u56fd\u85e9\u738b\uff0c \u968f\u4ece\u4e2d\u600e\u7684\u6709\u5982\u6b64\u4eba\u7269\uff1f\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u6cf0\u6e05\u738b\u5df2\u722c\u4e86\u8d77\u6765\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u6709\u57cb\u4f0f\uff0c \u6211\u96be\u9053\u5c31\u6ca1\u6709\u57cb\u4f0f\u5417\uff1f\u201d \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u6240\u5f97\u96c6\u8d24\u9601\u5916\uff0c\u4e5f\u5df2\u662f\u6740\u58f0\u56db\u8d77\u3002\u8c37\u4e2d\u83b2\u4e00\u638c\u5411\u90a3\u9ed1\u8863\u4eba\u62cd\u53bb\uff0c\u638c\u98ce \u6fc0\u8361\u4e4b\u4e2d\uff0c\u90a3\u9ed1\u8863\u4eba\u7684\u5e3d\u5b50\u7ed9\u8c37\u4e2d\u83b2\u6253\u843d\uff0c\u9732\u51fa\u4e86\u4e00\u4e2a\u5149\u5934\uff0c\u4f46\u8c37\u4e2d\u83b2\u63a5\u4e86\u90a3\u4eba\u4e00\u638c\uff0c \u4e5f\u662f\u5341\u5206\u96be\u53d7\uff0c\u53ea\u89c9\u6c14\u8840\u7ffb\u6d8c\uff0c\u51e0\u4e4e\u7ad9\u4e0d\u7a33\u811a\u6b65\u3002\u8fd9\u65f6\u8c37\u4e2d\u83b2\u624d\u770b\u51fa\u90a3\u9ed1\u8863\u4eba\u662f\u4e2a\u5587\u561b\u3002 \u3000\u3000\u90a3\u5587\u561b\u50e7\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u5c31\u662f\u9a6c\u8428\u513f\u56fd\u7684\u516c\u4e3b\u5417\uff1f\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u6b66\u529f\u5012\u8fd8\u771f\u4e0d\u9519\u5440\uff01\u201d \u6cf0\u6e05\u738b\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u8fd8\u6709\u7528\u5904\uff0c\u8bf7\u6cd5\u738b\u7559\u5979\u4e00\u547d\uff01\u201d\u90a3\u5587\u561b\u50e7\u7b11\u9053\uff1a\u201c\u6211\u6700\u7231\u60dc\u6709\u672c\u9886 \u7684\u5c11\u5e74\u7537\u5973\uff0c\u4f60\u4e0d\u5982\u505a\u6211\u6559\u4e2d\u7684\u4fee\u5973\u5427\uff0c\u8fd8\u80dc\u8fc7\u4f60\u5f53\u4ec0\u4e48\u516c\u4e3b\u3002\u201d\u7b11\u58f0\u672a\u4e86\uff0c\u53c8\u662f\u4e00\u6293 \u5411\u8c37\u4e2d\u83b2\u6293\u6765\uff01 \u3000\u3000\u8c37\u4e2d\u83b2\u67f3\u7709\u5012\u7ad6\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u54fc\uff0c\u539f\u6765\u662f\u4f60\uff01\u6211\u6c5f\u5e08\u5144\u9976\u4e86\u4f60\u7684\u6027\u547d\uff0c\u6307\u671b\u4f60\u6539 \u8fc7\u81ea\u65b0\uff0c\u60f3\u4e0d\u5230\u4f60\u8fd8\u662f\u5982\u6b64\u539a\u989c\u65e0\u803b\uff0c\u53c8\u6765\u5174\u98ce\u4f5c\u6d6a\uff01\u201d\u90a3\u5587\u561b\u50e7\u672c\u6765\u662f\u8a00\u7b11\u81ea\u5982\uff0c\u4ece \u5bb9\u6de1\u5b9a\uff0c\u4e00\u6d3e\u6b66\u5b66\u5927\u5e08\u3001\u4f5b\u95e8\u9ad8\u50e7\u7684\u8eab\u4efd\uff0c\u542c\u4e86\u8fd9\u6d3b\uff0c\u9762\u8272\u767b\u65f6\u53d8\u4e86\uff0c\u4e00\u58f0\u6012\u543c\uff0c\u731b\u6251 \u8fc7\u6765\uff0c\u90a3\u4e00\u6293\u4e4b\u52bf\uff0c\u66f4\u5982\u96f7\u8f70\u7535\u63a3\uff0c\u8fc5\u731b\u65e0\u4f26\uff01 \u3000\u3000\u773c\u770b\u8c37\u4e2d\u83b2\u52bf\u96be\u62b5\u5fa1\uff0c\u90a3\u5587\u561b\u50e7\u5ffd\u89c9\u80cc\u540e\u6709\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u6765\u52bf\u51cc\u5389\u4e4b\u6781\uff0c\u90a3\u5587\u561b \u618e\u5403\u4e86\u4e00\u60ca\uff0c\u53cc\u638c\u6025\u5fd9\u5206\u5f00\uff0c\u524d\u9762\u4e00\u638c\u4ecd\u7136\u5411\u8c37\u4e2d\u83b2\u6253\u53bb\uff0c\u540e\u9762\u4e00\u638c\u5219\u53cd\u624b\u6253\u51fa\uff0c\u963b\u6b62 \u6765\u654c\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u811a\u6b65\u672a\u7a33\uff0c\u4f3c\u53cc\u638c\u4e4b\u529b\u654c\u4ed6\u4e00\u638c\uff0c\u582a\u582a\u62b5\u654c\u5f97\u4f4f\u3002\u4f46\u5587\u561b\u50e7\u7684\u53e6\u4e00\u638c\u4e4b\u529b\uff0c \u5374\u963b\u904f\u4e0d\u4e86\u65b0\u6765\u7684\u654c\u4eba\u3002\u53ea\u542c\u5f97\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u4fe8\u5982\u6233\u7834\u4e86\u4e00\u4e2a\u6c14\u7403\uff0c\u63a5\u7740\u201c\u55e4\u201d\u7684\u4e00 \u54cd\uff0c\u5251\u5149\u8fc7\u5904\uff0c\u90a3\u5587\u561b\u50e7\u8eab\u4e0a\u7684\u8888\u88df\u5df2\u88ab\u6233\u7a7f\u4e86\u65e0\u6570\u5c0f\u5b54\uff0c\u5e78\u800c\u4ed6\u89c1\u673a\u5f97\u65e9\uff0c\u4e00\u89c9\u4e0d\u5999\uff0c \u7acb\u5373\u541e\u80f8\u5438\u8179\uff0c\u8eab\u5b50\u51ed\u7a7a\u632a\u540e\u534a\u5c3a\uff0c\u8888\u88df\u7247\u7247\u788e\u88c2\uff0c\u76ae\u8089\u4f9d\u7136\u6beb\u65e0\u635f\u4f24\u3002 \u3000\u3000\u5587\u561b\u50e7\u6012\u9053\uff1a\u201c\u539f\u6765\u662f\u9099\u5c71\u6d3e\u7684\u8c37\u638c\u95e8\u6765\u4e86\u3002\u8d35\u6d3e\u76ee\u524d\u6b63\u6709\u4e8b\u4e8e\u4e2d\u539f\uff0c\u4f60\u7adf\u7136\u8fd8\u6709 \u95f2\u7cbe\u9038\u81f4\u5230\u8fd9\u8fb9\u585e\u7684\u5c0f\u56fd\u6765\u591a\u7ba1\u7528\u4e8b\uff01\u6211\u5ff5\u5728\u4f60\u4e0a\u4ee3\u638c\u95e8\u53ec\u56db\u5a18\u4e0e\u6211\u6559\u7684\u4ea4\u60c5\uff0c\u4f60\u901f\u901f \u5e26\u4f60\u7684\u5f92\u5f1f\u8d70\u4e86\u5427\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u51b7\u7b11\u9053\uff1a\u201c\u6211\u6b63\u662f\u770b\u5728\u4f60\u5e08\u5144\u7684\u4efd\u4e0a\uff0c\u624d\u6765\u529d\u4f60\uff0c\u6211\u529d\u4f60\u4f11\u8981\u4e3a\u975e\u4f5c\u6b79\uff0c\u6cbe \u8fb1\u4e86\u4f5b\u95e8\u3002\u4f60\u5e08\u5144\u5bf9\u4f60\u7684\u8bad\u6d77\uff0c\u4f60\u5168\u90fd\u5fd8\u8bb0\u4e86\u5417\uff1f\u901f\u901f\u5e26\u4f60\u90a3\u51e0\u4e2a\u5f92\u5f1f\u56de\u9102\u514b\u6c81\u5bab\uff0c\u5411 \u4f60\u7684\u5e08\u5144\u5fcf\u6094\u5427\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u5587\u561b\u50e7\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u9752\u6d77\u9102\u514b\u6c81\u5bab\u767d\u6559\u6cd5\u738b\u7684\u5e08\u5f1f\u5b54\u96c0\u660e\u4f26\u738b\u3002\u4ed6\u4e3a\u4e86 \u8d2a\u56fe\u5c3c\u6cca\u5c14\u56fd\u56fd\u5e08\u7684\u865a\u8363\uff0c\u4e0e\u767d\u6559\u6cd5\u738b\u5206\u9053\u626c\u9573\uff0c\u5404\u884c\u5176\u662f\u3002\u540e\u6765\u5728\u91d1\u9e70\u5bab\u4e4b\u4f1a\uff0c\u5f7c\u6c5f \u6d77\u5929\u7834\u4e86\u4ed6\u7684\u7f61\u6c14\uff0c\u4f46\u5f53\u65f6\u6c5f\u6d77\u5929\u4e5f\u56e0\u4e3a\u770b\u5728\u767d\u6559\u6cd5\u738b\u7684\u4efd\u4e0a\uff0c\u4e0d\u613f\u4f24\u4ed6\u6027\u547d\uff0c\u53cd\u800c\u9001 \u4e86\u4ed6\u4e00\u9897\u78a7\u7075\u4e39\u7597\u4f24\u3002\u5b54\u96c0\u660e\u4f26\u738b\u4ed7\u7740\u6839\u57fa\u6df1\u539a\uff0c\u95ed\u5173\u767e\u65e5\uff0c\u518d\u5ea6\u7ec3\u6210\u7f61\u6c14\uff0c\u6062\u590d\u4e86\u4ed6 \u7684\u4e0a\u4e58\u5185\u529f\u3002 \u3000\u3000\u95ed\u5173\u767e\u65e5\uff0c\u4e16\u5c40\u63a8\u8350\uff0c\u5916\u9762\u5df2\u662f\u8d77\u4e86\u7ffb\u5929\u8986\u5730\u7684\u53d8\u5316\uff0c\u5c3c\u6cca\u5c14\u6c5f\u5c71\u6613\u4e3b\uff0c\u5e9f\u738b\u4e5f\u65e9 \u5df2\u6f5c\u9003\uff0c\u5230\u4e86\u6606\u5e03\u5170\u56fd\u6765\u907f\u96be\u4e86\u3002\u5b54\u96c0\u660e\u4f26\u738b\u56e0\u4e3a\u4e0e\u5e08\u5144\u95f9\u7ffb\uff0c\u65e0\u989c\u518d\u56de\u9102\u514b\u6c81\u5bab\uff0c\u660e \u77e5\u5e9f\u738b\u5df2\u7ecf\u5931\u52bf\uff0c\u4e5f\u552f\u6709\u5bc4\u671b\u4e8e\u4ed6\uff0c\u4ecd\u7136\u505a\u4ed6\u7684\u201c\u56fd\u5e08\u201d\uff0c\u9634\u8c0b\u52a9\u4ed6\u590d\u56fd\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u5e9f\u738b\u5e26\u6765\u4e86\u6570\u767e\u6b66\u58eb\uff0c\u5728\u6606\u5e03\u5170\u8fd9\u6837\u7684\u5c0f\u56fd\u4e2d\uff0c\u5df1\u5f62\u6210\u4e86\u5f3a\u5bbe\u538b\u4e3b\u4e4b\u52bf\u3002\u6606 \u5e03\u5170\u56fd\u738b\u4e5f\u662f\u4e2a\u91ce\u5fc3\u52c3\u52c3\u7684\u4eba\uff0c\u9042\u4e0e\u4ed6\u5b9a\u4e0b\u5bc6\u7ea6\uff0c\u4e92\u76f8\u5229\u7528\u3002 \u3000\u3000\u6cf0\u6e05\u738b\u5728\u56fd\u4e2d\u7684\u6743\u4f4d\u4e4b\u91cd\uff0c\u4ec5\u6b21\u4e8e\u56fd\u738b\uff0c\u5c3c\u6cca\u5c14\u5e9f\u738b\u8981\u501f\u7528\u6606\u5e03\u5170\u56fd\u7684\u5175\u529b\uff0c\u56e0\u800c \u4e0e\u6cf0\u6e05\u738b\u4e5f\u6df1\u76f8\u7ed3\u7eb3\u3002 \u3000\u3000\u6cf0\u6e05\u738b\u63a2\u77e5\u56fd\u738b\u5c06\u6709\u4e0d\u5229\u4e8e\u4ed6\u7684\u4f01\u56fe\uff0c\u9042\u5411\u5c3c\u6cca\u5c14\u5e9f\u7389\u8fdb\u8c17\uff0c\u63ed\u7834\u56fd\u738b\u7684\u79d8\u5bc6\uff0c\u8bf4 \u662f\u56fd\u738b\u5df2\u6539\u53d8\u4e3b\u60eb\uff0c\u51c6\u5907\u4e0e\u9a6c\u8428\u513f\u56fd\u8054\u5408\uff0c\u6bc1\u76df\u788e\u7ea6\uff0c\u53cd\u800c\u8981\u5c06\u5e9f\u738b\u8fd9\u4e00\u73ed\u4eba\u62d8\u7981\u8d77\u6765\uff0c \u4f5c\u4e3a\u5bf9\u5c3c\u6cca\u5c14\u65b0\u738b\u7684\u732e\u672d\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u5e9f\u738b\u542c\u4e86\uff0c\u81ea\u662f\u53c8\u60ca\u53c8\u6012\uff0c\u4e8e\u662f\u8f6c\u800c\u4e0e\u6cf0\u6e05\u738b\u8ba2\u4e86\u5bc6\u7ea6\uff0c\u613f\u610f\u8d5e\u52a9\u6cf0\u6e05\u738b\u767b \u4f4d\uff0c\u5728\u65f6\u673a\u672a\u6210\u719f\u4e4b\u524d\uff0c\u5148\u652f\u6301\u4ed6\u593a\u53d6\u519b\u653f\u5927\u6743\u3002 \u3000\u3000\u4eca\u665a\u6cf0\u6e05\u738b\u5165\u5bab\u8d74\u56fd\u738b\u4e4b\u4f1a\uff0c\u6697\u4e2d\u4e5f\u65e9\u5df2\u6709\u4e86\u5468\u5bc6\u7684\u5e03\u7f6e\u3002 \u3000\u3000\u4ed6\u5e26\u6765\u7684\u90a3\u961f\u968f\u4ece\uff0c\u5176\u4e2d\u8fc7\u534a\u6570\u4fbf\u662f\u5c3c\u6cca\u5c14\u56fd\u7684\u6b66\u58eb\u4e54\u88c5\u7684\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u4e5f\u5728\u5176\u4e2d\u3002 \u53e6\u5916\u53c8\u7ea6\u597d\u4e86\uff0c\u82e5\u662f\u6709\u4e8b\u53d1\u751f\uff0c\u6cf0\u6e05\u738b\u7684\u968f\u4ece\u4fbf\u53d1\u51fa\u6d41\u661f\u7130\u706b\uff0c\u4f5c\u4e3a\u8baf\u53f7\uff0c\u5e9f\u738b\u4e00\u89c1\u706b \u7bad\u5347\u7a7a\uff0c\u4fbf\u5e26\u9886\u4ed6\u7684\u5168\u90e8\u6b66\u58eb\uff0c\u4e0e\u6cf0\u6e05\u738b\u7684\u5bb6\u81e3\u4f1a\u5408\uff0c\u653b\u5165\u5bab\u4e2d\uff0c\u53d1\u52a8\u653f\u53d8\u3002 \u3000\u3000\u53cc\u65b9\u5404\u6709\u5e03\u7f6e\uff0c\u5f7c\u6b64\u52fe\u5fc3\u6597\u89d2\u3002\u4f46\u6cf0\u6e05\u738b\u5374\u60f3\u4e0d\u5230\u6709\u8c37\u4e4b\u534e\u5e08\u5f92\uff0c\u7adf\u5728\u5173\u952e\u7684\u65f6\u523b \u7a81\u7136\u51fa\u73b0\uff0c\u654c\u4f4f\u4e86\u5b54\u96c0\u660e\u4f26\u738b\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u662f\u5415\u56db\u5a18\u7684\u8863\u94b5\u4f20\u4eba\uff0c\u4e00\u624b\u7384\u5973\u5251\u6cd5\u65e9\u5df2\u5230\u4e86\u51fa\u795e\u5165\u5316\u4e4b\u5883\uff0c\u5979\u7ec3\u7684\u662f\u7384\u95e8 \u6b63\u5b97\u5185\u529f\uff0c\u5c24\u5176\u8fd1\u5e74\u5f97\u4e86\u91d1\u4e16\u9057\u7684\u6307\u70b9\uff0c\u529f\u529b\u66f4\u662f\u5927\u4e3a\u589e\u8fdb\uff0c\u867d\u7136\u6bd4\u8d77\u5b54\u96c0\u660e\u4f26\u738b\u8fd8\u662f \u7a0d\u7a0d\u4e0d\u5982\uff0c\u4f46\u914d\u4e0a\u5979\u90a3\u624b\u7cbe\u5999\u7684\u7384\u5973\u5251\u6cd5\uff0c\u5373\u4f7f\u5355\u6253\u72ec\u6597\uff0c\u4e5f\u8db3\u53ef\u4ee5\u4e0e\u5b54\u96c0\u660e\u4f26\u738b\u6253\u4e2a \u5e73\u624b\u3002\u4f55\u51b5\u5979\u8fd8\u6709\u5f92\u5f1f\u8c37\u4e2d\u83b2\u76f8\u52a9\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u6b64\u65f6\u529f\u529b\u5df2\u5b8c\u5168\u6062\u590d\uff0c\u4e24\u5e08\u5f92\u5251\u638c\u9f50\u65bd\uff0c\u6253\u5f97\u5b54\u96c0\u660e\u4f26\u738b\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb \u65e0\u8fd8\u624b\u4e4b\u529b\u3002\u56fd\u738b\u60ca\u9b42\u752b\u5b9a\uff0c\u4fbf\u60f3\u4e58\u673a\u6536\u62fe\u6cf0\u6e05\u6b63\u3002\u53ea\u89c1\u6cf0\u6e05\u7389\u5750\u5728\u5730\u4e0a\uff0c\u76ee\u9732\u51f6\u5149\uff0c \u6b63\u5728\u6076\u72e0\u72e0\u5730\u76ef\u7740\u4ed6\u3002\u539f\u6765\u6cf0\u6e05\u4e3b\u867d\u5f97\u5b54\u96c0\u660e\u4f26\u738b\u66ff\u4ed6\u89e3\u4e86\u7a74\u9053\uff0c\u4f46\u8840\u8109\u5c1a\u672a\u901a\u7545\uff0c\u884c \u52a8\u4e5f\u8fd8\u672a\u80fd\u81ea\u5982\u3002 \u3000\u3000\u56fd\u738b\u5728\u4ed6\u51f6\u72e0\u7684\u76ee\u5149\u6ce8\u89c6\u4e4b\u4e0b\uff0c\u4e0d\u7981\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u6cf0\u6e05\u738b\u6b66\u529f\u8fdc\u80dc \u4e8e\u6211\uff0c\u867d\u7136\u770b\u8fd9\u6837\u5b50\uff0c\u4ed6\u4f3c\u662f\u5df2\u5931\u4e86\u62b5\u6297\u7684\u80fd\u529b\uff0c\u4f46\u5374\u4e5f\u8fd8\u672a\u77e5\u662f\u771f\u662f\u5047\u3002\u8bf4\u4e0d\u5b9a\u4ed6\u662f \u6545\u610f\u5750\u5728\u5730\u4e0a\uff0c\u88c5\u4f5c\u722c\u4e0d\u8d77\u6765\uff0c\u8bf1\u6211\u4e0a\u5f53\u3002\u6211\u82e5\u5192\u6627\u52a8\u624b\uff0c\u4e07\u4e00\u6740\u4ed6\u4e0d\u6210\uff0c\u53ef\u5c31\u8981\u5148\u8d54 \u4e0a\u81ea\u5df1\u7684\u4e00\u6761\u6027\u547d\u4e86\u3002\u201d \u3000\u3000\u6b63\u81ea\u4e3e\u68cb\u4e0d\u5b9a\uff0c\u5c3c\u6cca\u5c14\u5e9f\u738b\u5e26\u9886\u51e0\u4e2a\u6b66\u58eb\u5df2\u6740\u4e86\u8fdb\u6765\uff01\u559d\u9053\uff1a\u201c\u7ed9\u6211\u5c06\u8fd9\u65e0\u4fe1\u65e0\u4e49 \u4e4b\u4eba\u62ff\u4e0b\uff01\u201d\u6606\u5e03\u5170\u738b\u614c\u5fd9\u53eb\u9053\uff1a\u201c\u8d24\u738b\u8bf7\u5bb9\u7980\u544a\u2026\u2026\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c \u4e00\u4e2a\u6301\u5200\uff0c\u4e00\u4e2a\u821e\u68d2\uff0c\u5df2\u662f\u6076\u72e0\u72e0\u7684\u5411\u4ed6\u6251\u6765\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u62fe\u8d77\u4e86\u5730\u4e0a\u4e00\u67c4\u5f2f\u5200\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u62a2\u5feb\u4e00\u6b65\uff0c\u62e6\u5728\u56fd\u738b\u524d\u9762\uff0c\u4e00\u62db\u201c\u96c1\u843d\u5e73 \u6c99\u201d\uff0c\u5200\u5149\u95ea\u95ea\uff0c\u540c\u65f6\u653b\u51fb\u4e24\u4e2a\u654c\u4eba\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u4f7f\u68d2\u7684\u90a3\u4e2a\u6b66\u58eb\u68d2\u7aef\u4e00\u6b6a\uff0c \u7acb\u5373\u53d8\u62db\uff0c\u6572\u51fb\u8c37\u4e2d\u83b2\u7684\u819d\u76d6\uff0c\u4f7f\u5200\u7684\u6b66\u58eb\uff0c\u5200\u950b\u5374\u88ab\u524a\u65ad\uff0c\u8e6c\u3001\u8e6c\u3001\u8e6c\u7684\u9000\u51fa\u51e0\u6b65\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u4f7f\u68d2\u7684\u6b66\u571f\u4e43\u662f\u5c3c\u6cca\u5c14\u5e9f\u738b\u4ece\u524d\u7684\u5fa1\u6797\u519b\u7edf\u9886\u5b5f\u54c8\u8d64\uff0c\u6b66\u529f\u5341\u5206\u4e86\u5f97\u3002\u4f7f \u5200\u7684\u90a3\u4e2a\u6b66\u58eb\u662f\u4ed6\u7684\u526f\u624b\u9ea6\u7ef4\uff0c\u6b66\u529f\u867d\u4e5f\u4e0d\u5f31\uff0c\u4f46\u6bd4\u8d77\u8c37\u4e2d\u83b2\u6765\uff0c\u5374\u5c31\u5dee\u5f97\u5f88\u8fdc\u4e86\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u7109\u80fd\u7ed9\u4ed6\u6253\u4e2d\uff0c\u4e00\u4e2a\u201c\u6e38\u8eab\u6ed1\u6b65\u201d\uff0c\u907f\u62db\u8fdb\u62db\uff0c\u5f2f\u5200\u659c\u5288\u5b5f\u54c8\u8d64\u53cc\u80a9\uff0c\u5b5f\u54c8 \u8d64\u4e00\u4e2a\u201c\u96ea\u82b1\u76d6\u9876\u201d\uff0c\u5c06\u94c1\u68d2\u62a1\u5706\uff0c\u821e\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u8c37\u4e2d\u83b2\u8fde\u5288\u6570\u5200\uff0c\u90fd\u65ab\u4e0d\u8fdb\u53bb\uff0c\u9ea6 \u7ef4\u548c\u4e24\u4e2a\u6b66\u58eb\u53c8\u6251\u4e0a\u6765\uff0c\u628a\u8c37\u4e2d\u83b2\u56f4\u5728\u6838\u5fc3\u3002\u4f46\u8c37\u4e2d\u83b2\u8eab\u6cd5\u8f7b\u7075\uff0c\u4e00\u67c4\u5f2f\u5200\u6307\u4e1c\u6253\u897f\uff0c \u6307\u5357\u6253\u5317\uff0c\u90a3\u51e0\u4e2a\u6b66\u58eb\u5374\u4e5f\u51b2\u4e0d\u8fc7\u53bb\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u53bb\u4e86\u4e00\u4e2a\u5f3a\u654c\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u5ffd\u5730\u8131\u4e0b\u8888\u88df\uff0c\u5f53\u6210\u5175\u5668\uff0c\u6697\u8fd0\u7384\u529f\uff0c\u8888\u88df \u4e00\u6296\uff0c\u4fe8\u5982\u5e73\u5730\u98de\u8d77\u4e86\u4e00\u7247\u7ea2\u971e\uff0c\u6321\u4f4f\u4e86\u8c37\u4e4b\u534e\u7684\u5b9d\u5251\uff0c\u51e0\u4e2a\u56de\u5408\u4e00\u8fc7\uff0c\u8c37\u4e4b\u534e\u867d\u4e0d\u81f3 \u4e8e\u843d\u8d25\uff0c\u4ea6\u5df2\u6709\u70b9\u76f8\u5f62\u900a\u8272\uff0c\u5251\u950b\u4e00\u5230\u654c\u4eba\u8eab\u524d\u4e94\u5c3a\u4e4b\u5185\uff0c\u5c31\u7ed9\u8888\u88df\u8361\u5f00\uff0c\u523a\u4e0d\u8fc7\u53bb\u3002 \u4f46\u5b54\u96c0\u660e\u4f26\u738b\u4e5f\u6df1\u77e5\u8c37\u4e4b\u534e\u7684\u5251\u6cd5\u5389\u5bb3\uff0c\u53ea\u80fd\u4ee5\u5b88\u4e3a\u653b\uff0c\u4ed7\u7740\u529f\u529b\u7a0d\u9ad8\uff0c\u5148\u7acb\u4e8e\u4e0d\u8d25\u4e4b \u5730\uff0c\u518d\u4e00\u6b65\u6b65\u7684\u8fdb\u8feb\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u62ac\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u7247\u7ea2\u971e\u88f9\u7740\u4e00\u9053\u94f6\u5149\uff0c\u4e0a\u4e0b\u7ffb\u817e\uff0c\u76d8\u65cb\u98de\u821e\uff0c \u770b\u90a3\u60c5\u5f62\uff0c\u7ea2\u971e\u662f\u5728\u6e10\u6e10\u6269\u5c55\uff0c\u94f6\u5149\u5374\u5728\u6e10\u6e10\u9000\u7f29\uff0c\u8c37\u4e2d\u83b2\u4e0e\u5e08\u7236\u4f11\u621a\u76f8\u5173\uff0c\u4e0d\u7981\u6697\u6697 \u5403\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e45\u6218\u4e0b\u53bb\uff0c\u53ea\u6015\u5e08\u7236\u8981\u5403\u90a3\u79c3\u9a74\u7684\u4e8f\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u82e5\u8981\u7a81\u56f4\uff0c\u5e76\u4e0d\u56f0\u96be\uff0c\u5979\u4e5f\u77e5\u9053\uff0c\u82e5\u7136\u5979\u4e0e\u5e08\u7236\u8054\u624b\uff0c\u8db3\u4ee5\u6253\u8d25\u5b54\u96c0\u660e\u4f26\u4e3b\uff0c \u518d\u56de\u8fc7\u5934\u6765\uff0c\u6536\u62fe\u8fd9\u6279\u6b66\u58eb\uff0c\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\u3002\u4f46\u82e6\u4e8e\u5206\u8eab\u4e4f\u672f\u3002\u82e5\u7ed9\u5e08\u7236\u6253\u63f4\uff0c\u5c31\u4e0d\u80fd \u540c\u65f6\u4fdd\u62a4\u56fd\u738b\u3002\u5f62\u52bf\u5206\u660e\uff0c\u5374\u662f\u96be\u4e88\u53d6\u820d\u3002 \u3000\u3000\u6606\u5e02\u5170\u738b\u745f\u7f29\u4e00\u9685\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u5c0f\u738b\u6709\u751a\u5192\u72af\u4e4b\u5904\uff0c\u81f3\u52b3\u8d24\u738b\u5174\u5e08\u95ee\u7f6a\uff1f\u201d \u5c3c\u6cca\u5c14\u5e9f\u738b\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u8fd8\u4e0d\u660e\u767d\u5417\uff1f \u3000\u3000\u6211\u4e14\u95ee\u4f60\uff0c\u8fd9\u5973\u5b50\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u6606\u5e03\u5170\u738b\u9053\uff1a\u201c\u8fd9\u3001\u8fd9\u2026\u2026\u201d \u3000\u3000\u6cf0\u6e05\u738b\u5f97\u610f\u4e4b\u6781\uff0c\u4ef0\u5929\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u6562\u8bf4\u51fa\u6765\u4e48\uff1f\u8fd9\u5973\u5b50\u4e0d\u662f\u522b\u4eba\uff0c \u6b63\u662f\u9a6c\u8428\u513f\u56fd\u7684\u516c\u4e3b\u3002\u201d\u5c3c\u6cca\u5c14\u5e9f\u738b\u9053\uff1a \u3000\u3000\u201c\u8bf7\u95ee\u965b\u4e0b\uff0c\u4f60\u6211\u8ba2\u4e0b\u76df\u7ea6\uff0c\u6b63\u662f\u8981\u5408\u529b\u5148\u53d6\u9a6c\u8428\u513f\u56fd\uff0c\u4f60\u5374\u4e3a\u4f55\u4e0e\u654c\u4eba\u8054\u5408\uff1f\u4f60 \u65e2\u4e0d\u4ec1\uff0c\u53c8\u7109\u80fd\u602a\u6211\u4e0d\u4e49\uff1f\u201d \u3000\u3000\u6606\u5e03\u5170\u738b\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u8d24\u738b\u6709\u6240\u8bef\u4f1a\u4e86\uff0c\u5c0f\u738b\u51b3\u65e0\u80cc\u76df\u4e4b\u5fc3\uff0c\u6211\u5df2\u51c6\u5907\u505c\u59a5\uff0c\u660e\u65e5 \u4fbf\u5373\u53d1\u5175\u3002\u81f3\u4e8e\u8fd9\u5973\u5b50\u4e48\uff0c\u5979\u3001\u5979\u672c\u662f\u88ab\u8f6f\u7981\u5bab\u4e2d\uff0c\u6211\u4e5f\u60f3\u4e0d\u5230\u5979\u7a81\u7136\u6765\u5230\u6b64\u95f4\u7684\u3002\u5176 \u4e2d\u7f18\u6545\uff0c\u5bb9\u540e\u7ec6\u7980\u3002\u201d\u6cf0\u6e05\u738b\u51b7\u7b11\u9053\uff0c\u201c\u4f60\u8fd9\u9b3c\u8bdd\u6709\u8c01\u76f8\u4fe1\uff1f\u4f60\u4e0d\u80fd\u81ea\u5706\u5176\u8bf4\u4e86\u4e48\uff1f\u5f85 \u6211\u548c\u4f60\u8bf4\u4e86\u5427\uff01\u201d\u56fd\u738b\u6012\u9053\uff1a\u201c\u4f60\u4e0d\u8fc7\u60f3\u628a\u6211\u53d6\u800c\u4ee3\u4e4b\u7f62\u4e86\u3002\u4f46\u53ea\u6015\u6211\u7684\u5927\u5c0f\u4e09\u519b\uff0c\u4f60 \u5374\u672a\u5fc5\u6307\u6325\u5f97\u52a8\uff1f\u201d\u6cf0\u6e05\u738b\u6b63\u8981\u5f53\u4f17\u63ed\u7a7f\u4ed6\u7684\u79d8\u5bc6\uff0c\u5c3c\u6cca\u5c14\u5e9f\u738b\u5374\u9053\uff1a\u201c\u4f60\u4e14\u8ba9\u4ed6\u5148\u8bf4\u3002\u201d \u3000\u3000\u6606\u5e03\u5170\u738b\u770b\u51fa\u4e8b\u6709\u8f6c\u673a\uff0c\u8fde\u5fd9\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u965b\u4e0b\u82f1\u660e\u3002\u5f53\u6709\u51b3\u65ad\u3002\u4f60\u82e5\u542c\u4fe1\u6b64\u4eba\uff0c \u5bf9\u4f60\u672a\u5fc5\u6709\u5229\u3002\u4f60\u82e5\u4fe1\u6211\uff0c\u6211\u613f\u652f\u51fa\u5175\u7b26\uff0c\u542c\u4f60\u9a71\u7b56\uff01\u201d\u5c3c\u6cca\u5c14\u5e9f\u738b\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u770b\u6765 \u6b64\u4eba\u662f\u4e2a\u5fd7\u5927\u624d\u758f\uff0c\u8d2a\u751f\u6015\u6b7b\u4e4b\u8f88\uff0c\u5012\u6bd4\u6cf0\u6e05\u4e3b\u5bb9\u6613\u5bf9\u4ed8\u3002\u53ea\u8981\u5728\u6211\u638c\u63e1\u4e4b\u4e2d\uff0c\u6211\u53c8\u4f55 \u5fc5\u7406\u4ed6\u662f\u5426\u6606\u5e03\u5170\u56fd\u7684\u7687\u5ba4\u8840\u7edf\uff1f\u201d \u3000\u3000\u5c3c\u6cca\u5c14\u5e9f\u738b\u9053\uff0c\u201c\u4f60\u5f53\u771f\u6709\u8bda\u610f\u4e0e\u6211\u8054\u76df\uff1f\u201d\u56fd\u738b\u5fd9\u9053\uff1a\u201c\u51b3\u65e0\u5f02\u5fc3\uff01\u201d\u5c3c\u6cca\u5c14\u5e9f \u738b\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u5148\u4f9d\u6211\u4e00\u4e8b\uff01\u201d\u6606\u5e03\u5170\u738b\u9053\uff1a \u3000\u3000\u201c\u8d24\u738b\u4f46\u8bf7\u5429\u5490\u3002\u201d\u5c3c\u6cca\u5c14\u5e9f\u738b\u9053\uff1a\u201c\u4f60\u65e2\u6709\u8bda\u610f\uff0c\u5c31\u4e0d\u8be5\u8fd8\u4e0e\u6211\u4eec\u5bf9\u654c\u3002\u5feb\u53eb\u8fd9 \u4e24\u4e2a\u5973\u5b50\u653e\u4e0b\u5175\u5668\uff0c\u770b\u5728\u4f60\u7684\u4efd\u4e0a\uff0c\u6211\u4e5f\u4e0d\u4f1a\u5c06\u5979\u4eec\u4e3a\u96be\u3002\u201d\u8981\u77e5\u4ed6\u8fd9\u4e00\u8fb9\u867d\u7136\u7a0d\u5360\u4e0a \u98ce\uff0c\u4f46\u4e00\u65f6\u95f4\u4e5f\u96be\u53d6\u80dc\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u5e9f\u738b\u8bda\u6050\u591c\u957f\u68a6\u591a\uff0c\u6545\u800c\u5bf9\u6606\u5e03\u5170\u738b\u8f6f\u786c\u517c\u65bd\uff0c\u8981\u4ed6\u6765\u529d\u8c37\u4e2d\u83b2\u653e\u4e0b\u6b66\u5668\u3002 \u3000\u3000\u6606\u5e03\u5170\u738b\u9677\u5728\u654c\u4eba\u7684\u5305\u56f4\u4e4b\u4e2d\uff0c\u80c6\u5b50\u5148\u5df2\u602f\u4e86\uff0c\u4e0d\u7531\u5f97\u4fbf\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u83b2\u3001\u83b2\u2026\u2026 \u516c\u4e3b\uff0c\u987d\u6297\u65e0\u76ca\uff0c\u4f60\uff0c\u4f60\u542c\u5f97\u8fd9\u4f4d\u8d24\u738b\u7684\u8bf4\u8bdd\u4e86\u5417\uff1f\u4ed6\u4eec\u4e0d\u4f1a\u5c06\u4f60\u4e3a\u96be\uff0c\u4f60\u5c31\u4e0d\u8981\u518d\u625b \u4e86\u5427\uff01\u201d\u8fd9\u5239\u90a3\u95f4\uff0c\u8c37\u4e2d\u83b2\u5fc3\u91cc\u96be\u8fc7\u5230\u4e86\u6781\u70b9\uff0c\u5979\u820d\u4e86\u6027\u547d\u60f3\u4fdd\u62a4\u56fd\u738b\uff0c\u5c82\u6599\u56fd\u738b\u53cd\u800c \u529d\u5979\u5411\u654c\u4eba\u6295\u964d\uff01\u800c\u8fd9\u4e2a\u654c\u4eba\u53c8\u6b63\u662f\u8981\u4fb5\u5bb3\u4ed6\u7956\u56fd\u7684\u7f6a\u9b41\u7978\u9996\uff01 \u3000\u3000\u8c37\u4e2d\u83b2\u54ac\u4e86\u54ac\u7259\uff0c\u628a\u5fc3\u4e00\u6a2a\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u65e2\u754f\u654c\u5982\u864e\uff0c\u90a3\u6211\u4e5f\u53ea\u552f\u6709\u5411\u4f60 \u8fd9\u4f4d\u8d24\u738b\u732e\u5200\u8bf7\u7f6a\u5566\uff01\u201d\u8eab\u5f62\u4e00\u8d77\uff0c\u9661\u7136\u4f7f\u51fa\u7edd\u9876\u8f7b\u529f\uff0c\u629b\u4e0b\u4e86\u56fd\u738b\uff0c\u4ece\u5b5f\u54c8\u8d64\u7b49\u4eba\u5934 \u9876\u98de\u8fc7\uff0c\u4e0a\u62db\u201c\u9e70\u51fb\u957f\u7a7a\u201d\uff0c\u5f2f\u5200\u671d\u7740\u5c3c\u6cca\u5c14\u5e9f\u738b\u7684\u5934\u9876\u76f4\u5288\u4e0b\u53bb\u3002 \u3000\u3000\u5200\u950b\u79bb\u5e9f\u738b\u5934\u9876\u4e0d\u5230\u4e94\u5bf8\uff0c\u5ffd\u5730\u6709\u4e00\u5708\u9ec4\u5149\u95ea\u7535\u822c\u7684\u98de\u6765\uff0c\u9690\u9690\u4fa0\u7740\u51e4\u96f7\u4e4b\u58f0\uff0c\u539f \u6765\u662f\u5e9f\u738b\u8eab\u540e\u7684\u4e00\u4e2a\u548c\u5c1a\uff0c\u98de\u8d77\u4e86\u4e00\u9762\u94dc\u94b9\u3002 \u3000\u3000\u8fd9\u548c\u5c1a\u662f\u5c3c\u6cca\u5c14\u5e9f\u738b\u4ece\u6ce2\u65af\u672d\u8058\u6765\u7684\u8884\u6559\u9ad8\u624b\uff0c\u6cd5\u53f7\u666f\u6708\u4e0a\u4eba\uff0c\u6b66\u529f\u5c1a\u5728\u76c2\u54c8\u8d64\u4e4b \u4e0a\u3002\u8c37\u4e2d\u83b2\u4e00\u5200\u5288\u4e2d\u94dc\u94b9\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u9707\u8033\u6b32\u804b\uff0c\u4e24\u4e2a\u5c3c\u6cca\u5c14\u6b66\u58eb\u201c\u535c\u901a\u201d\u8dcc\u5012\u3001 \u539f\u6765\u662f\u7ed9\u58f0\u97f3\u9707\u7834\u4e86\u8033\u819c\uff0c\u5413\u5f97\u6655\u4e86\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u6574\u4e2a\u8eab\u5b50\u53cd\u5f39\u8d77\u6765\uff0c\u4e00\u4e2a\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\uff0c\u5728\u7a7a\u4e2d\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u8fde\u4eba\u5e26\u5200\uff0c \u4ecd\u7136\u731b\u5288\u4e0b\u6765\uff0c\u4f46\u56e0\u5979\u7ed9\u8fd9\u9762\u94dc\u94b9\u4e00\u9707\uff0c\u843d\u4e0b\u4e4b\u65f6\uff0c\u65b9\u4f4d\u5dee\u4e86\u5c11\u8bb8\uff0c\u5c3c\u6cca\u5c14\u5e9f\u738b\u548c\u8863\u4e00 \u6eda\uff0c\u5f2f\u5200\u4ece\u4ed6\u8eab\u8fb9\u5288\u8fc7\uff0c\u780d\u4e2d\u4e86\u4ed6\u8eab\u65c1\u7684\u77f3\u67f1\u3002 \u3000\u3000\u5b5f\u54c8\u8d64\u89c1\u8c37\u4e2d\u83b2\u7adf\u4f3c\u4e0d\u987e\u6027\u547d\u4f3c\u7684\uff0c\u751f\u6015\u666f\u6708\u4e0a\u4eba\u62e6\u5979\u4e0d\u4f4f\uff0c\u4e5f\u987e\u4e0d\u5f97\u80a9\u4e0a\u7684\u521b\u4f24\uff0c \u4fbf\u6765\u4e0e\u666f\u6708\u4e0a\u4eba\u53cc\u6218\u8c37\u4e2d\u83b2\u3002\u53e6\u5916\u51e0\u540d\u6b66\u58eb\u4e5f\u6025\u6025\u5fd9\u5fd9\u8fc7\u6765\u56f4\u62e5\u5e9f\u738b\uff0c\u8fd9\u65f6\u4ed6\u4eec\u4fdd\u62a4\u4e3b \u516c\u8981\u7d27\uff0c\u5012\u6ca1\u6709\u4eba\u518d\u53bb\u7406\u4f1a\u6606\u5e03\u5170\u738b\u4e86\u3002\u6606\u5e03\u5170\u738b\u521a\u521a\u677e\u4e86\u53e3\u6c14\uff0c\u5ffd\u89c1\u6cf0\u6e05\u738b\u5df2\u7ecf\u8d77\u7acb\uff0c \u7f13\u7f13\u5411\u4ed6\u8d70\u6765\uff0c\u6b65\u5c65\u7792\u8dda\uff0c\u6bd4\u8d77\u5e73\u65f6\u7684\u77eb\u5065\uff0c\u90a3\u662f\u5dee\u5f97\u8fdc\u4e86\uff0c\u4f46\u773c\u4e2d\u51f6\u5149\u66b4\u9732\uff0c\u5374\u662f\u8d8a \u53d1\u9a87\u4eba\u3002 \u3000\u3000\u6606\u5e03\u5170\u4e3b\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u6709\u6211\u6ca1\u4ed6\uff0c\u6709\u4ed6\u6ca1\u6211\u3002\u4e5f\u53ea\u6709 \u5c06\u4ed6\u9664\u6389\uff0c\u65b9\u80fd\u4f7f\u5f97\u5c3c\u6cca\u5c14\u738b\u975e\u5f97\u5b8c\u5168\u9760\u6211\u4e0d\u53ef\u3002\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u6740\u673a\u9661\u8d77\uff0c\u795e\u8272\u5374\u4e1d\u6beb \u4e0d\u9732\uff0c\u53cd\u800c\u6ee1\u9762\u5806\u6b22\uff0c\u8bf4\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f84\u513f\u6b63\u60f3\u628a\u56fd\u73ba\u4e0e\u5175\u7b26\u652f\u4ed8\u4e0e\u4f60\uff0c\u4f9d\u6211\u770b\u6765\uff0c\u4f60 \u6211\u53ea\u5b9c\u548c\u8877\u540c\u6d4e\uff0c\u51b3\u4e0d\u53ef\u540c\u5ba4\u64cd\u6208\u3002\u6211\u6ca1\u6709\u4f60\uff0c\u7b49\u4e8e\u5931\u4e86\u680b\u6881\uff1b\u4f60\u6ca1\u6709\u6211\uff0c\u8c01\u6765\u5c01\u4f60\u6444 \u653f\uff1f\u201d \u3000\u3000\u6cf0\u6e05\u738b\u4e00\u60f3\uff0c\u8fd9\u6d3b\u4e5f\u8bf4\u5f97\u6709\u51e0\u5206\u9053\u7406\uff1a\u201c\u6211\u5982\u4eca\u82e5\u8981\u6740\u4ed6\uff0c\u90a3\u662f\u6613\u5982\u53cd\u638c\uff1b\u4f46\u6740\u4e86 \u4ed6\u4e4b\u540e\uff0c\u6587\u6b66\u767e\u5b98\uff0c\u4e09\u519b\u5c06\u58eb\uff0c\u53ea\u6015\u672a\u5fc5\u90fd\u80af\u5411\u6211\u4f4e\u9996\u79f0\u81e3\uff1f\u5012\u4e0d\u5982\u6682\u4e14\u7559\u4ed6\u4e00\u547d\uff0c\u6211 \u4e5f\u597d\u631f\u5929\u5b50\u4ee5\u4eca\u8bf8\u4faf\u3002\u5f85\u5f97\u6743\u67c4\u90fd\u5df2\u5230\u6211\u624b\u4e2d\uff0c\u8fd9\u7687\u4f4d\u8fd8\u6015\u5b83\u98de\u4e86\uff1f\u201d\u8fd9\u4e48\u4e00\u60f3\uff0c\u773c\u4e2d \u51f6\u5149\u7a0d\u655b\uff0c\u5230\u4e86\u56fd\u738b\u9762\u524d\uff0c\u4fbf\u5373\u505c\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u56fd\u73ba\u5175\u7b26\u73b0\u5728\u4f55\u5904\uff1f\u201c\u56fd\u738b\u8bf4\u9053\uff1a\u201c\u5728 \u6211\u8eab\u4e0a\u3002\u201d\u6cf0\u6e05\u738b\u9053\uff1a\u201c\u65e2\u5728\u8eab\u4e0a\uff0c\u90a3\u5c31\u5feb\u5feb\u62ff\u6765\u3002\u201d \u3000\u3000\u56fd\u738b\u8bf4\u9053\uff1a\u201c\u8bf7\u7687\u53d4\u63a5\u4e0b\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u4e00\u9053\u94f6\u5149\uff0c\u7a81\u7136\u88c2\u8863\u5c04\u51fa\uff0c\u6cf0\u6e05\u738b\u4e00\u58f0\u60e8 \u547c\uff0c\u5c0f\u8179\u5df2\u4e2d\u4e86\u4e00\u5251\u3002 \u3000\u3000\u539f\u6765\u56fd\u738b\u8eab\u4e0a\u85cf\u7684\u4e0d\u662f\u56fd\u73ba\u5175\u7b26\uff0c\u800c\u662f\u8c37\u4e2d\u83b2\u90a3\u628a\u971c\u534e\u5b9d\u5251\u3002\u8fd9\u628a\u5b9d\u5251\u8584\u5982\u8749\u7ffc\uff0c \u5229\u53ef\u65ad\u91d1\uff0c\u85cf\u5728\u8eab\u4e0a\uff0c\u4e00\u70b9\u4e5f\u770b\u4e0d\u51fa\u6765\u3002\u6cf0\u6e05\u738b\u81ea\u6043\u6b66\u827a\u9ad8\u5f3a\uff0c\u53ea\u9053\u56fd\u738b\u5df2\u88ab\u4ed6\u634f\u5728\u624b \u5fc3\uff0c\u8981\u5706\u5c31\u5706\uff0c\u8981\u6241\u5c31\u6241\uff0c\u51b3\u8ba1\u662f\u4e0d\u6562\u53cd\u6297\u7684\u4e86\u3002\u54ea\u77e5\u6606\u5e03\u5170\u738b\u4ea6\u975e\u5584\u7c7b\uff0c\u8feb\u5f97\u7d27\u4e86\uff0c \u4e5f\u4f1a\u51fa\u5176\u4e0d\u610f\u7684\u5bf9\u4ed6\u6697\u7b97\u3002 \u3000\u3000\u56fd\u738b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u770b\u4f60\u8fd8\u80fd\u591f\u4e0e\u6211\u4e89\u593a\u7687\u4f4d\u4e48\uff1f\u201d\u6cf0\u6e05\u738b\u84e6\u5730\u5927\u543c\u4e00\u58f0\uff0c\u5c06\u56fd\u738b\u4e00 \u628a\u62b1\u4f4f\uff0c\u56fd\u738b\u521a\u521a\u62bd\u51fa\u5b9d\u5251\uff0c\u88ab\u6cf0\u6e05\u738b\u62ff\u7740\u4ed6\u624b\u8098\uff0c\u7528\u529b\u4e00\u63a8\uff0c\u6cf0\u6e05\u738b\u867d\u53d7\u91cd\u4f24\uff0c\u4f46\u8fd9 \u4e00\u63a8\u4e43\u662f\u4ed6\u4e34\u7ec8\u524d\u5168\u8eab\u6c14\u529b\u4e4b\u6240\u805a\uff0c\u56fd\u738b\u7109\u80fd\u7981\u53d7\u5f97\u8d77\uff0c\u53ea\u542c\u5f97\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u5b9d\u5251\u671d \u5185\u523a\u51fa\uff0c\u63d2\u5165\u4e86\u56fd\u738b\u80f8\u4e2d\u3002\u4e24\u53d4\u4f84\u6210\u4e86\u51a4\u5bb6\u5bf9\u5934\uff0c\u540c\u65f6\u6bd9\u547d\u3002 \u3000\u3000\u8fd9\u4ef6\u610f\u5916\u4e4b\u4e8b\u7a81\u7136\u53d1\u751f\uff0c\u4ea4\u6218\u53cc\u65b9\uff0c\u90fd\u4e0d\u7531\u5f97\u84e6\u5730\u4e00\u60ca\u3002\u8c37\u4e2d\u83b2\u53eb\u58f0\u4e0d\u597d\uff0c\u614c\u5fd9\u8df3 \u51fa\u5708\u5b50\uff0c\u8fc7\u53bb\u4e00\u770b\uff0c\u53ea\u89c1\u4e24\u4eba\u90fd\u662f\u809a\u8179\u6d1e\u7a7f\uff0c\u8840\u6d41\u6ee1\u5730\uff0c\u65e9\u5df2\u6b7b\u4e86\u3002\u5c3c\u6cca\u5c14\u90a3\u51e0\u4e2a\u6b66\u58eb \u4e0d\u53ca\u8c37\u4e2d\u83b2\u8fc5\u901f\uff0c\u4f46\u8fd9\u65f6\u4e5f\u5df2\u8dd1\u4e86\u8fc7\u6765\u3002\u8c37\u4e2d\u83b2\u65e0\u6687\u54c0\u4f24\u3002\u4e00\u773c\u77a5\u89c1\u81ea\u5df1\u90a3\u628a\u5b9d\u5251\u5c1a\u5728 \u56fd\u738b\u624b\u4e2d\uff0c\u8fde\u5fd9\u5c06\u5b83\u62ff\u4e0b\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u5c06\u5f2f\u4e07\u4e00\u629b\uff0c\u649e\u5012\u4e86\u4e24\u4e2a\u6b66\u58eb\uff0c\u8fc5\u5373\u6539\u7528\u5b9d\u5251\uff0c \u4e00\u5251\u4fbf\u5411\u5b5f\u54c8\u8d64\u65a9\u53bb\u3002\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u84ec\u98de\uff0c\u5b5f\u54c8\u8d64\u624b\u4e2d\u7684\u94c1\u68d2\uff0c\u767b\u65f6\u53ea\u5269 \u4e0b\u77ed\u77ed\u7684\u4e00\u622a\uff01 \u3000\u3000\u666f\u6708\u4e0a\u4eba\u53cc\u94b1\u6253\u6765\uff0c\u8c37\u4e2d\u83b2\u5df2\u662f\u8fde\u4eba\u5e26\u5251\uff0c\u5316\u6210\u4e86\u4e00\u9053\u94f6\u8679\uff0c\u75be\u5377\u8fc7\u53bb\uff0c\u53ea\u542c\u5f97 \u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u666f\u6708\u4e0a\u4eba\u53cc\u94b9\u5408\u4e0b\uff0c\u5c06\u8c37\u4e2d\u83b2\u7684\u5b9d\u5251\u5939\u4f4f\uff0c\u5b5f\u54c8\u8d64\u5927\u559c\u9053\uff1a\u201c\u597d\uff0c\u5148\u593a \u4e0b\u5979\u7684\u5b9d\u5251\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u4e00\u7247\u65ad\u91d1\u788e\u7389\u4e4b\u58f0\uff0c\u9707\u5f97\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u666f\u6708\u4e0a\u4eba\u7684 \u4e00\u9762\u94dc\u94a6\uff0c\u5df2\u88ab\u7834\u4f5c\u4e24\u8fb9\uff01 \u3000\u3000\u666f\u6708\u4e0a\u4eba\u7684\u94dc\u94b9\u6709\u4e94\u5bf8\u591a\u539a\uff0c\u52a0\u4e0a\u4ed6\u672c\u8eab\u7684\u5185\u529f\u4ea6\u9887\u4e0d\u5f31\uff0c\u53ea\u9053\u5b9d\u5251\u4e5f\u65e0\u5948\u4ed6\u4f55\uff0c \u54ea\u77e5\u8fd8\u662f\u7ed9\u971c\u534e\u5251\u7834\u4e86\u3002\u8c37\u4e2d\u83b2\u5b9d\u5251\u8f6c\u4e86\u4e2a\u5708\uff0c\u4ecd\u7136\u5411\u5c3c\u6cca\u5c14\u5e9f\u738b\u524a\u51fa\uff0c\u4f46\u5979\u7834\u4e86\u90a3\u9762 \u94dc\u94b9\uff0c\u867d\u7136\u6613\u4e8e\u53cd\u638c\uff0c\u6bd5\u7adf\u4e5f\u662f\u7a0d\u7a0d\u53d7\u963b\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u629b\u8d77\u8888\u88df\uff0c\u5df2\u5982 \u5de8\u9e70\u5c55\u7ffc\uff0c\u5f53\u5934\u7f69\u4e0b\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u4e00\u5251\u523a\u51fa\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u7684\u8888\u88df\u7a7f\u4e86\u4e00\u5b54\uff0c\u8c37\u4e2d\u83b2\u4e5f\u7ed9\u4ed6\u6247\u8d77 \u7684\u90a3\u80a1\u98ce\u529b\uff0c\u538b\u5f97\u51e0\u4e4e\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u8eab\u4e0d\u7531\u5df1\u7684\u8fde\u9000\u6570\u6b65\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8c37\u4e4b\u534e\u4ea6 \u5df2\u8d76\u5230\uff0c\u4e00\u62db\u201c\u767d\u9e64\u5254\u7fce\u201d\uff0c\u5251\u631f\u52b2\u98ce\uff0c\u523a\u5230\u4e86\u5b54\u96c0\u660e\u4f26\u738b\u8eab\u524d\uff0c\u5835\u4f4f\u4e86\u4ed6\u5411\u8c37\u4e2d\u83b2\u7684 \u8ffd\u51fb\u3002 \u3000\u3000\u5e08\u5f92\u4fe9\u53cc\u5251\u5408\u58c1\uff0c\u5927\u663e\u795e\u5a01\uff0c\u4fe8\u5982\u7389\u9f99\u592d\u77eb\uff0c\u88f9\u4f4f\u4e86\u4e00\u7247\u7ea2\u971e\uff0c\u6740\u5f97\u5b54\u96c0\u660e\u4f26\u738b\u53ea \u6709\u62db\u67b6\u4e4b\u529f\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u90a3\u4ef6\u8888\u88df\u5df2\u662f\u904d\u4f53\u9cde\u4f24\uff0c\u53c8\u6dfb\u4e86\u5341\u51e0\u4e2a\u7834\u6d1e\u3002\u5b5f\u54c8\u8d64\u821e\u52a8\u8f7b\u4e86 \u51e0\u65a4\u7684\u94c1\u68d2\uff0c\u666f\u6708\u4e0a\u4eba\u4e5f\u62ff\u8d77\u4e86\u4ed6\u90a3\u4e24\u9762\u7834\u94b1\uff0c\u518d\u6765\u52a0\u5165\u6218\u56e2\uff0c\u4ee5\u4e09\u654c\u4e8c\uff0c\u5140\u662f\u53ea\u6709\u62db \u67b6\u7684\u4efd\u51e0\u3002\u5c3c\u6cca\u5c14\u7684\u6b66\u58eb\uff0c\u51e0\u66fe\u89c1\u8fc7\u5982\u6b64\u6076\u6218\uff0c\u53ea\u6015\u79e7\u53ca\u6c60\u9c7c\uff0c\u907f\u4e4b\u552f\u6050\u4e0d\u8fdc\uff0c\u66f4\u83ab\u8bf4 \u63d2\u624b\u4e86\u3002\u5c3c\u6cca\u5c14\u5e9f\u738b\u6b7b\u91cc\u9003\u751f\uff0c\u66f4\u662f\u5413\u5f97\u9762\u9752\u5507\u767d\u745f\u7f29\u4e00\u9685\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u5e9f\u738b\u6b63\u60f3\u9000\u51fa\u8fd9\u95f4\u5c4b\u5b50\uff0c\u521a\u5230\u95e8\u8fb9\uff0c\u5ffd\u5730\u88ab\u4eba\u5f53\u80f8\u4e00\u628a\u6293\u4f4f\uff0c\u90a3\u4eba\u9634\u607b\u607b\u4ed6 \u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u628a\u6211\u7684\u5e72\u5b59\u513f\u6740\u4e86\uff1f\u201d \u3000\u3000\u5c3c\u6cca\u5c14\u5e9f\u738b\u5927\u5403\u4e00\u60ca\uff0c\u5b9a\u775b\u770b\u65f6\uff0c\u5374\u8ba4\u5f97\u662f\u6606\u5e03\u5170\u56fd\u7684\u91d1\u8f6e\u5723\u6bcd\u7ae5\u59e5\u59e5\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a \u201c\u5723\u6bcd\u4f11\u8981\u8bef\u4f1a\uff0c\u4f60\u7684\u5e72\u5b59\u513f\u662f\u548c\u6cf0\u6e05\u738b\u81ea\u76f8\u6b8b\u6740\u7684\u3002\u201d \u3000\u3000\u7ae5\u59e5\u59e5\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u9a97\u5f97\u4e86\u8c01\uff0c\u6cf0\u6e05\u738b\u6562\u6765\u8feb\u5bab\uff0c\u8fd8\u4e0d\u662f\u53d7\u4f60\u7684\u6307\u4f7f\uff1f\u51b0\u5bb3\u6b7b\u6211\u7684 \u5e72\u5b59\u513f\uff0c\u53eb\u6211\u8fd9\u8001\u5e74\u4eba\u65e0\u4f9d\u65e0\u9760\uff0c\u4f60\u81ea\u5df1\u8bf4\u8bf4\uff0c\u4f60\u5e94\u8be5\u53d7\u4f55\u60e9\u5904\uff1f\u201d\u5c3c\u6cca\u5c14\u5e9f\u738b\u542c\u51fa\u4e8b \u6709\u8f6c\u673a\uff0c\u201c\u535c\u901a\u201d\u8dea\u5012\uff0c\u201c\u549a\u3001\u549a\u3001\u549a\u201d\u7684\u5c31\u53e9\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u7ae5\u59e5\u59e5\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u5e72\u5417\u3002 \u4e09\u4e2a\u664c\u5934\u5c31\u8d54\u5f97\u4e24\u4eb2\u4eba\u547d\u5417\uff1f\u201d \u3000\u3000\u5c3c\u6cca\u5c14\u5e9f\u738b\u9053\uff0c\u201c\u82e5\u8499\u5723\u6bcd\u4e0d\u5f03\uff0c\u8bf7\u6536\u6211\u4f5c\u4f60\u7684\u5e72\u513f\uff0c\u6211\u6709\u4e24\u4e2a\u5b69\u5b50\uff0c\u4e5f\u5c31\u662f\u4f60\u7684 \u5e72\u5b59\u513f\u4e86\u3002\u201d\u7ae5\u59e5\u59e5\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u6211\u5931\u4e86\u4e00\u4e2a\u5e72\u5b59\u513f\uff0c\u5374\u5f97\u56de\u4e24\u4e2a\uff0c\u5916\u52a0\u4e00\u4e2a\u5e72\u513f\uff0c \u4e5f\u8fd8\u4e0d\u81f3\u4e8e\u4e8f\u672c\uff0c\u4e0d\u8fc7\u2014\u2014\u201d \u3000\u3000\u5c3c\u6cca\u5c14\u5e9f\u738b\u5fd9\u9053\uff1a\u201c\u6606\u5e03\u5170\u56fd\u548c\u9a6c\u8428\u513f\u56fd\u4e24\u56fd\u7684\u5b9d\u85cf\u5b8c\u5168\u5f52\u4f60\u6240\u6709\uff0c\u5c06\u6765\u4e24\u56fd\u5408\u5e76\uff0c \u4f60\u5c31\u662f\u4e24\u56fd\u5171\u540c\u62e5\u6234\u7684\u5723\u6bcd\u3002\u5594\uff0c\u6211\u8fd8\u8bf4\u6f0f\u4e86\u4e00\u4e2a\uff0c\u82e5\u662f\u6211\u80fd\u56de\u56fd\u590d\u4f4d\uff0c\u5c3c\u6cca\u5c14\u4e5f\u2026\u2026\u201d \u7ae5\u59e5\u59e5\u9053\uff1a\u201c\u957f\u8fdc\u4e4b\u4e8b\u4e14\u5148\u83ab\u8bf4\uff0c\u53ea\u8bf4\u76ee\u524d\u4e4b\u4e8b\u3002\u4f60\u4eec\u8fde\u9a6c\u8428\u513f\u56fd\u7684\u516c\u4e3b\u90fd\u6253\u4e0d\u8fc7\u5462\uff0c \u8fd8\u6562\u8bf4\u8981\u541e\u5e76\u5979\u7684\u4e00\u4e2a\u56fd\u5bb6\uff1f\u201d\u5c3c\u6cca\u5c14\u5e9f\u738b\u9053\uff1a\u201c\u6b63\u662f\u8981\u6c42\u5723\u6bcd\u76f8\u52a9\u3002\u201d \u3000\u3000\u8bdd\u8bf4\u81f3\u6b64\uff0c\u7ae5\u59e5\u59e5\u624d\u54c8\u54c8\u4e00\u7b11\uff0c\u653e\u5f00\u4e86\u5e9f\u738b\uff0c\u6413\u4e00\u6413\u624b\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u8d77 \u6765\u4f60\u5012\u8981\u6bd4\u6211\u8fd9\u4e2a\u5e72\u5b59\u513f\u6709\u70b9\u826f\u5fc3\uff0c\u6211\u8fd9\u4e2a\u5e72\u5b59\u513f\u5440\uff0c\u5509\uff0c\u5509\uff0c\u771f\u662f\u5728\u6211\u767d\u75bc\u4e86\u4ed6\u4e00\u573a\uff0c \u4ed6\u7adf\u6562\u7792\u8fc7\u4e86\u6211\uff0c\u5077\u5077\u5c06\u89e3\u836f\u7ed9\u4e86\u8fd9\u4e2a\u4e2b\u5934\uff01\u201d \u3000\u3000\u5e9f\u738b\u5927\u559c\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u7980\u544a\u4f60\u5462\uff0c\u4f60\u8fd9\u4e2a\u5e72\u5b59\u513f\u548c\u8fd9\u4f4d\u9a6c\u8428\u513f\u56fd\u516c\u4e3b\u5176\u5b9e\u4e43\u662f\u5144 \u59b9\u3002\u201d\u7ae5\u59e5\u59e5\u9053\uff1a\u201c\u6211\u65e9\u5df2\u77e5\u9053\u4e86\u3002\u4f60\u522b\u7f57\u55e6\uff0c\u6211\u52a9\u4f60\u4e00\u81c2\u4e4b\u529b\u4fbf\u662f\u3002\u201d\u8fc8\u6b65\u4e0a\u524d\uff0c\u559d \u9053\uff1a\u201c\u9000\u5f00\uff0c\u5f85\u6211\u62ff\u5979\uff01\u201d \u3000\u3000\u666f\u6708\u4e0a\u4eba\u4e0e\u76c2\u54c8\u8d64\u5df4\u4e0d\u5f97\u6709\u4eba\u63a5\u66ff\u3002\u8fde\u5fd9\u9000\u4e0b\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u867d\u7136\u4e0d\u9ad8\u5174\u7ae5\u59e5\u59e5\u8fd9\u526f \u9a84\u505a\u7684\u795e\u6c14\uff0c\u5fc3\u91cc\u4ea6\u81ea\u60f3\u9053\uff0c\u201c\u4e5f\u597d\uff0c\u6211\u4e50\u5f97\u5750\u5c71\u89c2\u864e\u6597\u3002\u201d\u8888\u88df\u4e00\u64a4\uff0c\u8ddf\u7740\u4e5f\u5c31\u9000\u5f00\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u7684\u8888\u88df\u4e00\u64a4\uff0c\u4e24\u9053\u5251\u5149\u767b\u65f6\u5411\u7ae5\u59e5\u59e5\u5377\u6765\u3002\u7ae5\u59e5\u59e5\u957f\u8896\u4e00\u6325\uff0c\u4f7f\u51fa\u4e0a\u4e58 \u7684\u5378\u52b2\u529f\u592b\uff0c\u5c06\u8c37\u4e2d\u83b2\u7684\u5b9d\u5251\u4e00\u5f15\uff0c\u201c\u53ee\u201d\u7684\u4e00\u58f0\u3002\u6559\u8c37\u4e2d\u83b2\u7684\u5b9d\u5251\u78b0\u4e0a\u5979\u5e08\u7236\u90a3\u628a\u5251\u3002 \u3000\u3000\u5c82\u77e5\u8c37\u4e4b\u534e\u7684\u201c\u7384\u5973\u5251\u6cd5\u201d\u795e\u5999\u65e0\u65b9\uff0c\u4e24\u5e08\u5f92\u53c8\u662f\u914d\u5408\u5f97\u5341\u5206\u7eaf\u719f\uff0c\u5fc3\u610f\u76f8\u901a\uff0c\u7ae5 \u59e5\u59e5\u7684\u529f\u529b\u867d\u7136\u9ad8\u8fc7\u5979\u4eec\u4e8c\u4eba\uff0c\u5374\u4e5f\u9ad8\u4e0d\u4e86\u592a\u591a\uff0c\u4e24\u5e08\u5f92\u7684\u5b9d\u5251\u4e00\u78b0\uff0c\u53cd\u800c\u89e3\u5f00\u4e86\u7ae5\u59e5 \u59e5\u90a3\u80a1\u7c98\u9ecf\u4e4b\u52b2\uff0c\u4e24\u53e3\u5b9d\u5251\u767b\u65f6\u53cd\u5f39\u524a\u51fa\uff0c\u53cd\u800c\u53d8\u51fa\u4e86\u53e6\u4e00\u65b0\u62db\uff0c\u53cc\u5251\u2026\u5408\uff0c\u53ea\u89c1\u7ae5\u59e5 \u59e5\u7684\u4e00\u84ec\u4e71\u53d1\uff0c\u5df1\u968f\u7740\u5251\u5149\u98de\u8d77\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u53c8\u60ca\u53c8\u6012\uff0c\u4e94\u6307\u75be\u5f39\uff0c\u53ea\u542c\u5f97\u201c\u94ee\u94ee\u201d\u4e4b\u58f0\uff0c\u604d\u5982\u7e41\u5f26\u5bc6\u594f\uff0c\u539f\u6765\u5979\u6bcf\u6839\u624b \u6307\u4e0a\u90fd\u5957\u7740\u4e00\u53ea\u94c1\u6307\u73af\uff0c\u53ef\u4ee5\u5f53\u4f5c\u5175\u5668\u672a\u4f7f\uff0c\u7531\u4e8e\u52a8\u4f5c\u5feb\u5982\u95ea\u7535\uff0c\u8fd0\u52b2\u53c8\u6070\u5230\u597d\u5904\uff0c\u8c37 \u4e2d\u83b2\u5b9d\u5251\u7684\u5a01\u529b\u672a\u53ca\u53d1\u6325\uff0c\u5251\u5c16\u5df2\u7ed9\u5979\u5f39\u6b6a\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u4e00\u62db\u201c\u957f\u6cb3\u843d\u65e5\u201d\uff0c\u5251\u5149\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u8fc5\u901f\u8865\u4e0a\u7f3a\u53e3\uff0c\u963b\u6b47\u4e86\u7ae5\u59e5\u59e5\u7684\u653b \u52bf\u3002\u5e08\u5f92\u4fe9\u53cc\u5251\u5408\u58c1\uff0c\u653b\u4e2d\u5e26\u5b88\uff0c\u91cd\u59e5\u59e5\u7adf\u662f\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u4e4b\u8fde\u53d1\u4e09\u6b21\u6697\u5668\uff0c\u5148\u662f\u593a\u547d\u795e\u7802\uff0c\u63a5\u7740\u662f\u900f\u9aa8\u9489\u548c\u5316\u8840\u9488\uff0c\u6bcf\u4e00\u79cd\u6697\u5668\u90fd\u662f \u542b\u6709\u5267\u6bd2\u8db3\u4ee5\u7f6e\u4eba\u6b7b\u547d\u7684\u6697\u5668\u3002\u4f46\u8c37\u4e4b\u534e\u5e08\u5f92\u4fe9\u6240\u4f7f\u7684\u90fd\u662f\u4e16\u6240\u7f55\u89c1\u7684\u5b9d\u5251\uff0c\u53cc\u5251\u5408\u58c1\uff0c \u5316\u6210\u4e86\u4e00\u9053\u5149\u5e62\uff0c\u4efb\u4f55\u6697\u5668\uff0c\u6295\u5165\u5149\u5e62\u4e4b\u5185\uff0c\u90fd\u5316\u6210\u7c89\u788e\uff0c\u6839\u672c\u5c31\u6253\u4e0d\u5230\u5979\u4eec\u8eab\u4e0a\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u6697\u5668\u65e0\u529f\uff0c\u6bd2\u7c89\u5931\u6548\uff0c\u5fc3\u4e2d\u7126\u6025\uff0c\u53ea\u597d\u5192\u9669\u8fdb\u62db\uff0c\u89d1\u4e2a\u771f\u5207\uff0c\u8fd0\u6307\u8fde\u5f39\uff0c\u94ee\u3001 \u94ee\u4e24\u58f0\uff0c\u53c8\u5728\u4e24\u5251\u7684\u5251\u810a\u4e0a\u5404\u5f39\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u5ffd\u89c9\u4e00\u7f15\u963b\u5bd2\u4e4b\u6c14\uff0c\u5ffd\u5730\u4ece\u5251\u67c4\u4f20\u5165\u5979\u7684\u624b\u5fc3\uff0c\u4e0e\u6b64\u540c\u65f6\uff0c\u8c37\u4e2d\u83b2\u5219\u611f\u5230\u4e00 \u7247\u7099\u70ed\u3002\u539f\u6765\u7ae5\u59e5\u59e5\u7ec3\u6709\u9634\u9633\u4e8c\u6c14\uff0c\u201c\u9694\u7269\u4f20\u529f\u201d\uff0c\u56e0\u4eba\u800c\u65bd\uff0c\u540c\u65f6\u5411\u8c37\u4e4b\u534e\u548c\u8c37\u4e2d\u83b2 \u653b\u88ad\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u53d1\u89c9\u4e0d\u5999\uff0c\u7acb\u5373\u62b1\u5143\u5b88\u4e00\uff0c\u5bfc\u5f15\u771f\u6c14\uff0c\u4ece\u624b\u5c11\u9633\u7ecf\u8109\u987a\u6d41\u800c\u4e0b\uff0c\u4e0e\u5bf9\u65b9\u653b\u6765 \u7684\u56f0\u715e\u4e4b\u6c14\u76f8\u6297\u3002\u5979\u529f\u529b\u4e4b\u539a\uff0c\u4e0d\u53ca\u7ae5\u59e5\u59e5\uff0c\u4f46\u529f\u529b\u4e4b\u7eaf\uff0c\u5374\u5728\u7ae5\u59e5\u59e5\u4e4b\u4e0a\uff0c\u867d\u7136\u4e0d\u80fd \u7acb\u65f6\u9a71\u51fa\u6bd2\u8d28\uff0c\u4f46\u90a3\u80a1\u963b\u715e\u4e4b\u6c14\uff0c\u53ea\u653b\u5230\u4e86\u864e\u53e3\u7684\u201c\u5173\u5143\u7a74\u201d\u9644\u8fd1\uff0c\u5c31\u518d\u4e5f\u5347\u4e0d\u4e0a\u53bb\u4e86\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u670d\u98df\u4e86\u5929\u5fc3\u77f3\u4e4b\u540e\uff0c\u62a4\u4f53\u795e\u529f\u6700\u8fd1\u4e5f\u521a\u521a\u7ec3\u6210\uff0c\u81ea\u7136\u751f\u51fa\u53cd\u5e94\uff0c\u6070\u6070\u4e5f\u8db3\u4ee5 \u4e0e\u5bf9\u65b9\u7684\u6bd2\u6c14\u76f8\u6297\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u53cc\u65b9\u5404\u6709\u987e\u5fcc\u3002\u8c37\u4e4b\u534e\u5e08\u5f92\u8981\u5206\u795e\u6297\u6bd2\uff0c\u4f46\u7ae5\u59e5\u59e5\u7531\u4e8e \u4e0d\u65ad\u8981\u65bd\u5c55\u201c\u9694\u7269\u4f20\u529f\u201d\u7684\u7edd\u6280\uff0c\u4e5f\u662f\u5371\u9669\u6781\u5927\uff0c\u53ea\u8981\u4e00\u4e2a\u5f39\u5f97\u4e0d\u51c6\uff0c\u5fc5\u7136\u4e5f\u8981\u7ed9\u5b9d\u5251 \u6240\u4f24\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u5ffd\u542c\u5f97\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u6c14\u547c\u547c\u5730\u56b7\u9053\uff1a\u201c\u5c0f\u987a\u5b50\uff0c\u4f60\u773c\u4e2d\u8fd8\u6709\u4f60\u8fd9\u4e2a\u7237\u7237\u5417\uff1f \u4f60\u8981\u5bb3\u6b7b\u6211\u7684\u83b2\u513f\uff0c\u5148\u628a\u6211\u6740\u4e86\u5427\uff01\u54a6\uff0c\u4f60\u4eec\u90fd\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u6211\u7684\u5b59\u513f\u54ea\u91cc\u53bb\u4e86\uff1f\u201d\u539f \u6765\u662f\u6606\u5e03\u5170\u56fd\u7684\u592a\u4e0a\u7687\uff0c\u6276\u7740\u4e00\u6839\u62d0\u6756\uff0c\u98a4\u5dcd\u5dcd\u5730\u8d70\u4e86\u8fdb\u6765\u3002\u201c\u5c0f\u987a\u5b50\u201d\u4e43\u662f\u4ed6\u5b59\u513f\u7684 \u5c0f\u540d\uff0c\u4ed6\u53ea\u9053\u56fd\u738b\u8981\u6740\u8c37\u4e2d\u83b2\u706d\u53e3\uff0c\u6545\u800c\u8d76\u6765\u76f8\u6551\uff0c\u5374\u4e0d\u77e5\u9053\u4ed6\u7684\u5b59\u513f\u65e9\u5df2\u6b7b\u4e86\u3002\u4ed6\u5e74 \u7eaa\u8001\u8fc8\uff0c\u89c6\u529b\u6a21\u7cca\uff0c\u4e00\u65f6\u95f4\u8fd8\u672a\u8ba4\u51fa\u5c3c\u6cca\u5c14\u5e9f\u738b\uff0c\u4f46\u5df2\u9690\u9690\u89c9\u5bdf\u8fd9\u4e9b\u4eba\u7684\u88c5\u675f\uff0c\u4e0d\u4f3c\u4ed6 \u672c\u56fd\u7684\u6b66\u58eb\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u5e9f\u738b\u563f\u563f\u51b7\u7b11\u9053\uff1a\u201c\u8001\u7237\u7237\uff0c\u4f60\u8981\u627e\u4f60\u7684\u5b59\u513f\u4e48\u2026\u2026\u201d\u592a\u4e0a\u7687\u5927\u53eb\u9053\uff0c\u201c\u7ae5 \u59e5\u59e5\uff0c\u5feb\u5feb\u4f4f\u624b\uff0c\u4f60\u8981\u4ec0\u4e48\u4e1c\u897f\u6211\u90fd\u53ef\u4ee5\u7ed9\u4f60\uff0c\u4f60\u5207\u83ab\u4f24\u4e86\u6211\u7684\u83b2\u513f\uff01\u597d\u5440\uff0c\u4f60\u8fd8\u4e0d\u4f4f \u624b\uff0c\u6211\u628a\u6211\u8fd9\u6761\u8001\u547d\u4e0e\u4f60\u62fc\u4e86\uff01\u201d\u4ed6\u53d1\u73b0\u4e86\u7ae5\u59e5\u59e5\u6b63\u5728\u5411\u8c37\u4e2d\u83b2\u6b65\u6b65\u8fdb\u8feb\uff0c\u4fbf\u53ea\u987e\u671d\u7740 \u7ae5\u59e5\u59e5\u5927\u53eb\u5927\u56b7\uff0c\u6839\u672c\u5c31\u542c\u4e0d\u5230\u5c3c\u6cca\u5c14\u5e9f\u738b\u5728\u5411\u4ed6\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u5c31\u5728\u8fd9\u65f6\u53c8\u542c\u5f97\u545c\u545c\u58f0\u54cd\uff0c \u4e00\u58f0\u6bd4\u4e00\u58f0\u6025\u4fc3\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u9762\u8272\u500f\u53d8\uff0c\u539f\u6765\u8fd9\u6b63\u662f\u4ece\u5979\u90a3\u5ea7\u91d1\u8f6e\u5bab\u4f20\u672a\u7684\u8b66\u62a5\uff0c\u7ae5\u59e5\u59e5\u5bfb\u601d\uff1a\u201c\u739b\u513f\u4e0d \u77e5\u662f\u78b0\u4e0a\u4ec0\u4e48\u5f3a\u654c\uff0c\u8b66\u62a5\u6765\u5f97\u5982\u6b64\u7d27\u8feb\uff1f \u3000\u3000\u91d1\u8f6e\u5bab\u662f\u6211\u7684\u6839\u672c\u91cd\u5730\uff0c\u6211\u53ef\u4e0d\u80fd\u4e0d\u987e\u3002\u8fd9\u4e24\u4e2a\u5973\u5b50\u6211\u4e5f\u6ca1\u6709\u5fc5\u80dc\u5979\u4eec\u7684\u628a\u63e1\uff0c\u4e0d \u5982\u5c31\u7ed9\u592a\u4e0a\u7687\u505a\u4e2a\u987a\u6c34\u4eba\u60c5\u5427\uff01\u201d\u5f53\u4e0b\u4e00\u58f0\u957f\u5578\uff0c\u56de\u7b54\u7f28\u5927\u4eba\u7684\u8baf\u53f7\uff0c\u4e00\u8f6c\u8eab\u5c31\u4ece\u592a\u4e0a \u7687\u8eab\u8fb9\u63a0\u8fc7\uff0c\u5728\u592a\u4e0a\u7687\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u8981\u90a3\u672c\u9f99\u529b\u79d8\u85cf\u3002\u56de\u5934\u4f60\u7ed9\u6211\u9001\u6765\u3002\u201d\u5979 \u6700\u8fd1\u786e\u5b9e\u77e5\u9053\u4e86\u592a\u4e0a\u7687\u8eab\u4e16\u7684\u79d8\u5bc6\uff0c\u53ea\u9053\u8fd9\u672c\u9f99\u529b\u79d8\u85cf\u5728\u592a\u4e0a\u7687\u90a3\u513f\uff0c\u5979\u80af\u4e70\u5e10\uff0c\u8fd9\u4e5f \u662f\u539f\u56e0\u4e4b\u4e00\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u7a81\u7136\u64a4\u9000\uff0c\u5c3c\u6cca\u5c14\u5e9f\u738b\u8fd9\u8fb9\u7684\u4eba\u4e2a\u4e2a\u5403\u60ca\uff0c\u751f\u6015\u8c37\u4e4b\u534e\u5e08\u5f92\u53cc\u5251\u5408\u58c1\uff0c\u53c8\u518d \u6740\u6765\u3002\u8fde\u5b54\u96c0\u660e\u4f26\u738b\u4e5f\u6253\u5b9a\u4e86\u53ea\u987e\u81ea\u5df1\u7684\u4e3b\u610f\uff0c\u6084\u6084\u7684\u4ece\u5e9f\u738b\u8eab\u8fb9\u79fb\u5f00\uff0c\u9760\u8fd1\u4e86\u95e8\u53e3\u3002 \u5b5f\u54c8\u8d64\u662f\u5e9f\u738b\u7684\u5fe0\u4ec6\uff0c\u4e0d\u5fcd\u79bb\u5f00\uff0c\u4f46\u4ea6\u5df2\u624b\u98a4\u811a\u9707\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u7237\u7237\uff01\u201d\u592a\u4e0a\u7687\u6325\u624b\u53eb\u9053\uff1a\u201c\u5feb\u8d70\uff01\u5feb\u8d70\uff01 \u3000\u3000\u6211\u5df2\u4e0d\u80fd\u4fdd\u62a4\u4f60\u4e86\uff0c\u4f60\u5feb\u5feb\u51fa\u5bab\u53bb\u5427\uff01\u5c0f\u987a\u5b50\u4ed6\u773c\u4e2d\u5df2\u6ca1\u6709\u6211\u8fd9\u4e2a\u7237\u7237\u4e86\u3002\u201d\u8c37\u4e2d \u83b2\u4e0b\u5fcd\u628a\u4ed6\u5b59\u513f\u7684\u6b7b\u8baf\u4fbf\u544a\u8bc9\u4ed6\uff0c\u773c\u6cea\u4e0d\u7981\u593a\u7736\u9762\u51fa\uff0c\u53c8\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u7237\u7237\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u542c\u51fa\u8c37\u4e2d\u83b2\u58f0\u97f3\u6709\u5f02\uff0c\u4ed4\u7ec6\u4e00\u77a7\uff0c\u53ea\u89c1\u8c37\u4e2d\u83b2\u7709\u5fc3\u9690\u9690\u73b0\u51fa\u4e00\u80a1\u9ed1\u6c14\uff01\u8c37\u4e4b \u534e\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u5979\u600e\u7684\u906d\u4e86\u90a3\u5996\u5a46\u7684\u6bd2\u624b\u3002\u521a\u624d\u53cc\u5251\u5408\u58c1\uff0c\u9632\u5fa1\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u6bd2 \u836f\u600e\u7684\u4f1a\u6cbe\u5230\u5979\u7684\u8eab\u4e0a\uff1f\u5979\u73b0\u5728\u7684\u529f\u529b\u5df2\u662f\u4e0d\u900a\u4e8e\u6211\uff0c\u4f55\u4ee5\u6211\u53c8\u4e0d\u89c9\u5f97\u4ec0\u4e48\uff1f\u201d\u8c37\u4e4b\u534e \u54ea\u91cc\u77e5\u9053\uff0c\u8c37\u4e2d\u83b2\u5e76\u4e0d\u662f\u906d\u4e86\u7ae5\u59e5\u59e5\u7684\u6bd2\u624b\uff0c\u800c\u662f\u56fd\u738b\u9a97\u5979\u773c\u4e0b\u7684\u90a3\u9897\u6bd2\u836f\uff0c\u73b0\u5728\u5f00\u59cb \u53d1\u4f5c\u4e86\u3002\u6b63\u662f\uff0c \u3000\u3000\u540c\u6c14\u8fde\u679d\u65bd\u6bd2\u624b\uff0c\u53ea\u77e5\u5229\u5bb3\u4e0d\u77e5\u4eb2\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e94\u5341\u56db\u56de\u3000\u540c\u5ba4\u64cd\u6208\u4f55\u60e8\u9177\u3000\u4e34\u6b67\u5206\u624b\u6697\u4f24\u5fc3 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e94\u5341\u56db\u56de\u3000\u540c\u5ba4\u64cd\u6208\u4f55\u60e8\u9177\u3000\u4e34\u6b67\u5206\u624b\u6697\u4f24\u5fc3 \u3000\u3000\u8c37\u4e4b\u534e\u5728\u5f92\u5f1f\u8033\u8fb9\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u7389\u5973\u6295\u68ad\uff0c\u91d1\u9e70\u5c55\u7fc5\uff01\u201d\u8c37\u4e2d\u83b2\u81ea\u5c0f\u8ddf\u968f\u5e08\u7236\u7ec3\u4e60 \u5251\u6728\uff0c\u5e08\u7236\u8bf4\u4ec0\u4e48\uff0c\u5979\u5c31\u7ec3\u4ec0\u4e48\uff0c\u8fd9\u5df2\u7ecf\u6210\u4e3a\u4e60\u60ef\uff0c\u5f53\u4e0b\u4e0d\u5047\u601d\u7d22\u7684\u7acb\u5373\u51fa\u62db\uff0c\u5251\u5149\u5982 \u7ec3\uff0c\u5411\u524d\u523a\u51fa\uff0c\u679c\u7136\u5148\u662f\u201c\u7389\u5973\u6295\u68ad\u201d\u968f\u5373\u53d8\u4e3a\u201c\u91d1\u9e70\u5c55\u7fc5\u201d\u3002 \u3000\u3000\u5b54\u96c0\u660e\u4f26\u738b\u7ad9\u5728\u95e8\u53e3\uff0c\u4ed6\u662f\u4e2a\u6b66\u5b66\u5927\u884c\u5bb6\uff0c\u672c\u6765\u4e5f\u770b\u51fa\u8c37\u4e2d\u83b2\u6709\u4e9b\u5f02\u6837\uff0c\u4f46\u5374\u4e0d\u6562 \u65ad\u5b9a\u5979\u662f\u5426\u771f\u7684\u4e2d\u6bd2\uff0c\u5373\u4f7f\u4e2d\u6bd2\uff0c\u4e5f\u8fd8\u4e0d\u80fd\u65ad\u5b9a\u5979\u53d7\u6bd2\u7684\u6df1\u6d45\uff0c\u4e00\u89c1\u53cc\u5251\u5408\u58c1\uff0c\u4e24\u9053\u5251 \u5149\uff0c\u604d\u5982\u4e8c\u9f99\u62a2\u73e0\uff0c\u6765\u52bf\u5341\u5206\u51cc\u5389\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u5413\u5f97\u8fde\u5fd9\u8eb2\u907f\uff0c\u54ea\u91cc\u8fd8\u6562\u963b\u62e6\u3002\u8f6c\u773c\u95f4 \u8c37\u4e4b\u534e\u5e08\u5f92\u4fe9\u5df2\u662f\u95ef\u51fa\u5bab\u5916\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u8d70\u4e86\u4e4b\u540e\uff0c\u592a\u4e0a\u7687\u653e\u4e0b\u5fc3\u5934\u5927\u77f3\uff0c\u677e\u4e86\u53e3\u6c14\uff0c\u8fd9\u624d\u8ba4\u51fa\u4e86\u9762\u524d\u7684\u8fd9\u4eba\u662f\u66fe\u7ecf \u4ee5\u5b50\u4f84\u4e4b\u793c\uff0c\u7531\u4ed6\u5b59\u513f\u5e26\u9886\u6765\u62dc\u89c1\u8fc7\u4ed6\u7684\u90a3\u4e2a\u5c3c\u6cca\u5c14\u5e9f\u738b\u3002\u5927\u4e0a\u7687\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a \u201c\u4f60\u600e\u4e48\u5e26\u9886\u8fd9\u4e48\u591a\u4eba\u79c1\u81ea\u95ef\u8fdb\u6211\u7684\u5185\u5bab\uff1f\u6211\u7684\u5b59\u513f\u5462\uff1f\u201d\u5e9f\u738b\u9053\uff1a\u201c\u8001\u7956\u5b97\uff0c\u4f60\u522b\u4f24 \u5fc3\uff0c\u6211\u770b\u4f60\u8eab\u4f53\u5f88\u597d\uff0c\u8fd8\u53ef\u4ee5\u4eb2\u81ea\u6267\u653f\u51e0\u5e74\u3002\u6211\u613f\u4ee5\u5ba2\u537f\u7684\u5730\u4f4d\uff0c\u5e2e\u4f60\u7684\u5fd9\u3002\u201d \u3000\u3000\u539f\u6765\u5c3c\u6cca\u5c14\u5e9f\u738b\u89c1\u6606\u5e03\u5170\u56fd\u7684\u56fd\u4e3b\u548c\u6cf0\u6e05\u738b\u540c\u65f6\u6b7b\u4e86\uff0c\u5fc3\u91cc\u6b63\u5728\u7740\u6025\uff0c\u4e0d\u77e5\u5982\u4f55\u6536 \u62fe\u6b8b\u5c40\u3002\u8fd9\u65f6\u5ffd\u7136\u5f97\u4e86\u4e2a\u4e3b\u610f\uff0c\u610f\u56fe\u5229\u7528\u592a\u4e0a\u7687\u4f5c\u4e3a\u6127\u5121\uff0c\u8ba9\u4ed6\u64cd\u7eb5\u6606\u5e03\u5170\u56fd\u7684\u653f\u4e8b\uff0c \u8fd9\u4e48\u4e00\u6765\uff0c\u4ed6\u5c31\u4ecd\u7136\u53ef\u4ee5\u8fd0\u7528\u6606\u5e03\u5170\u56fd\u7684\u5175\u529b\u4e86\u3002 \u3000\u3000\u592a\u4e0a\u7687\u83ab\u540d\u5176\u5999\uff0c\u65a5\u9053\uff1a\u201c\u6211\u7684\u5bb6\u52a1\u4e8b\u4e0d\u7528\u4f60\u6765\u63d2\u53e3\u3002\u201d\u5e9f\u738b\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u662f\u4e00\u7247 \u597d\u5fc3\uff0c\u4f60\u7684\u5b59\u513f\uff0c\u4f60\u770b\u2014\u2014\u201d\u592a\u4e0a\u7687\u968f\u7740\u4ed6\u6240\u6307\u7684\u65b9\u5411\u770b\u53bb\uff0c\u53ea\u89c1\u6709\u4e24\u4e2a\u4eba\u5012\u5728\u8840\u6cca\u4e4b \u4e2d\u3002\u5927\u4e0a\u7687\u5b9a\u775b\u4e00\u77a7\uff0c\u8fd9\u624d\u53d1\u73b0\u662f\u4ed6\u5b59\u513f\u548c\u6cf0\u6e05\u738b\u4e24\u4eba\u7684\u5c38\u4f53\u3002 \u3000\u3000\u7a81\u7136\u95f4\u592a\u4e0a\u7687\u77aa\u5f97\u53c8\u5927\u53c8\u5706\u7684\u53cc\u773c\u5e03\u6ee1\u4e86\u7ea2\u4e1d\uff0c\u5c3c\u6cca\u5c14\u5e9f\u738b\u7ed9\u4ed6\u77aa\u5f97\u5fc3\u91cc\u53d1\u6bdb\uff0c\u8fde \u5fd9\u8bf4\u9053\uff1a\u201c\u8001\u7237\u7237\uff0c\u8fd9\uff0c\u8fd9\u4e0d\u5173\u2026\u2026\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u592a\u4e0a\u7687\u5927\u543c\u4e00\u58f0\uff0c\u4fbf\u4f3c\u53d1\u4e86\u72c2 \u7684\u6012\u6c49\u4e00\u822c\uff0c\u4e3e\u8d77\u62d0\u6756\uff0c\u4e0d\u7531\u5206\u8bf4\uff0c\u5288\u5934\u4fbf\u6253\uff0c\u53ea\u4e00\u4e0b\u5b50\uff0c\u5c31\u628a\u5e9f\u738b\u8eab\u8fb9\u7684\u4e24\u4e2a\u6b66\u58eb\u6253 \u7ffb\uff01\u5e9f\u738b\u7684\u80eb\u9aa8\u4e5f\u7740\u4e86\u4e00\u4e0b\uff0c\u75db\u5f97\u5728\u5730\u4e0a\u6253\u6eda\u3002 \u3000\u3000\u5b5f\u54c8\u8d64\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u7cca\u6d82\u5acc\u547d\u957f\u4e48\uff1f\u201d\u4e00\u6293\u6293\u7740\u6756\u5934\uff0c\u54ea\u77e5\u592a\u4e0a\u7687\u4e5f\u4e0d\u77e5\u54ea\u91cc\u6765 \u7684\u4e00\u8eab\u6c14\u529b\uff0c\u5b5f\u54c8\u8d64\u4e00\u6293\u7adf\u6ca1\u6293\u7262\uff0c\u201c\u535c\u201d\u7684\u4e00\u4e0b\uff0c\u819d\u76d6\u4e5f\u88ab\u6572\u4e86\u4e00\u8bb0\uff0c\u767b\u65f6\u77ee\u4e86\u534a\u622a\u3002 \u539f\u6765\u592a\u4e0a\u7687\u867d\u6ca1\u4e60\u8fc7\u6b66\u529f\uff0c\u4f46\u81ea\u5e7c\u4fbf\u5f97\u4ed6\u6bcd\u540e\u4f20\u8fc7\u9759\u5750\u5410\u7eb3\u7684\u65b9\u6cd5\uff0c\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u8fd9 \u4fbf\u662f\u4fee\u4e60\u5185\u529f\u7684\u57fa\u672c\u529f\u592b\uff0c\u53ea\u89c9\u6301\u4e4b\u6709\u6052\uff0c\u8eab\u8f7b\u4f53\u5065\uff0c\u4fbf\u6570\u5341\u5e74\u5982\u4e00\u65e5\u7684\u884c\u4e4b\u4e0d\u8f8d\u3002\u8fd9 \u6570\u5341\u5e74\u7684\u529f\u529b\u5c82\u6bd4\u5bfb\u5e38\uff0c\u5b5f\u54c8\u8d64\u8981\u593a\u4ed6\u7684\u62d0\u6756\uff0c\u7b49\u4e8e\u4e0e\u4ed6\u786c\u62fc\u529f\u529b\uff0c\u7109\u80fd\u62fc\u5f97\u8fc7\u4ed6\uff1f \u3000\u3000\u666f\u6708\u4e0a\u4eba\u53d1\u89c9\u4e0d\u5999\uff0c\u8fde\u5fd9\u4ece\u80cc\u540e\u6109\u88ad\uff0c\u4f38\u6307\u70b9\u592a\u4e0a\u7687\u7684\u7a74\u9053\uff0c\u592a\u4e0a\u7687\u672a\u5b66\u8fc7\u6b66\u827a\uff0c \u5185\u529b\u867d\u5f3a\uff0c\u5374\u4e0d\u61c2\u5f97\u8fd0\u7528\uff0c\u4e5f\u4e0d\u77e5\u9053\u8d8b\u907f\uff0c\u88ab\u4ed6\u4e00\u6307\u70b9\u4e2d\u4e86\u201c\u8c37\u865a\u7a74\u201d\uff0c\u767b\u65f6\u52a8\u5f39\u4e0d\u5f97\uff0c \u62d0\u6756\u4e5f\u5c31\u6389\u4e0b\u6765\u4e86\u3002\u5176\u5b9e\u82e5\u53ea\u8bba\u672c\u8eab\u529f\u529b\uff0c\u666f\u6708\u4e0a\u4eba\u8fd8\u8fdc\u4e0d\u5982\u4ed6\u3002 \u3000\u3000\u5b5f\u54c8\u8d64\u88ab\u4e00\u4e2a\u201c\u884c\u5c06\u5c31\u6728\u201d\u7684\u8001\u4eba\u6253\u4e86\u4e00\u62d0\uff0c\u53c8\u7f9e\u53c8\u6012\uff0c\u62a2\u4e86\u6b66\u58eb\u7684\u4e00\u67c4\u5f2f\u5200\uff0c\u4fbf \u8fc7\u6765\u8981\u6740\u592a\u4e0a\u7687\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u5e9f\u738b\u521a\u4ece\u5730\u4e0a\u722c\u8d77\uff0c\u8fde\u5fd9\u559d\u9053\uff1a\u201c\u5b5f\u54c8\u8d64\uff0c\u4ed6\u8001\u7cca\u6d82\uff0c\u4f60\u4e5f\u8ddf\u7740\u7cca\u6d82\u4e86\u4e48\uff1f \u4f60\u6740\u4e86\u4ed6\uff0c\u54b1\u4eec\u8fd8\u80fd\u5728\u6606\u5e03\u5170\u56fd\u7acb\u8db3\u4e48\uff1f\u5feb\u5feb\u4f4f\u624b\uff0c\u5feb\u5feb\u4f4f\u624b\uff01\u201d\u4ed6\u88ab\u592a\u4e0a\u7687\u6253\u4e86\u4e00\u62d0\uff0c \u80eb\u9aa8\u788e\u4e86\u4e24\u7247\uff0c\u4e5f\u987e\u4e0d\u5f97\u75bc\u75db\uff0c\u4fbf\u8fc7\u6765\u5411\u592a\u4e0a\u7687\u5927\u732e\u6bb7\u52e4\uff0c\u7b11\u563b\u563b\u8bf4\u9053\uff1a\u201c\u8001\u7956\u5b97\uff0c\u4f60 \u53d7\u60ca\u4e86\uff0c\u4f60\u653e\u5fc3\u5427\uff0c\u4f60\u6b7b\u4e86\u5b59\u5b50\uff0c\u6211\u7ed9\u4f60\u505a\u5b59\u5b50\u3002\u201d \u3000\u3000\u592a\u4e0a\u7687\u95ed\u4e86\u773c\u775b\uff0c\u4e0d\u7406\u4e0d\u776c\uff0c\u6697\u4e2d\u7977\u544a\uff1a\u201c\u4f5b\u7956\u6148\u60b2\uff0c\u8bf8\u795e\u4fdd\u4f51\uff0c\u8ba9\u6211\u7684\u83b2\u513f\u65e9\u65e9 \u8131\u79bb\u9669\u5730\u3002\u6211\u7684\u5c0f\u987a\u5b50\u5df2\u7ecf\u6b7b\u4e86\uff0c\u83b2\u513f\uff0c\u6211\u53ea\u6709\u6307\u671b\u4f60\u7ed9\u6211\u62a5\u4ec7\u5566\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u8fd9\u65f6\u6b63\u8d8a\u8fc7\u5bab\u5899\uff0c\u8c37\u4e4b\u534e\u62d6\u7740\u5979\u98de\u8dd1\u3002\u8c37\u4e2d\u83b2\u5ffd\u9053\uff1a \u3000\u3000\u201c\u54a6\uff0c\u4f3c\u662f\u6709\u4eba\u53eb\u6211\uff01\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u90a3\u662f\u654c\u4eba\u7684\u5406\u559d\uff0c\u83b2\u513f\uff0c\u54b1\u4eec\u8fd8\u672a\u66fe\u8fdc\u79bb\u9669 \u5730\u5462\u3002\u4f60\u8981\u54ac\u4f4f\u7259\u6839\uff0c\u518d\u652f\u6301\u4e00\u4f1a\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u8fd9\u4e0d\u662f\u654c\u4eba\u7684\u5406\u559d\uff0c\u6211\u5f53\u771f \u542c\u89c1\u4e86\uff0c\u542c\u89c1\u2026\u2026\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u542c\u89c1\u4ec0\u4e48\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u662f\u6d77\u54e5\u4ed6\u5728\u53eb\u6211\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u4e00\u770b\uff0c\u8c37\u4e2d\u83b2\u5df2\u662f\u6ee1\u9762\u9ed1\u6c14\uff0c\u53cc\u773c\u65e0\u5149\uff0c\u5fc3\u4e2d\u597d\u751f\u601c\u606b\uff0c\u67d4\u58f0\u8bf4\u9053\uff0c\u201c\u83b2\u513f\uff0c \u4f60\u522b\u80e1\u601d\u4e71\u60f3\uff0c\u4f60\u597d\u4e86\u4e4b\u540e\u3001\u6211\u4f1a\u66ff\u4f60\u628a\u6c5f\u6d77\u5929\u627e\u6765\u7684\u3002\u201d\u5979\u89c1\u8c37\u4e2d\u83b2\u811a\u6b65\u865a\u6d6e\uff0c\u7d22\u6027 \u628a\u5979\u80cc\u4e86\u8d77\u6765\uff0c\u4f7f\u51fa\u4e86\u5168\u526f\u8f7b\u529f\uff0c\u5982\u98de\u75be\u8dd1\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u4ee5\u4e3a\u662f\u5f92\u5f1f\u5fc3\u6709\u6240\u601d\uff0c\u6545\u751f\u5e7b\u89c9\uff0c\u54ea\u77e5\u8c37\u4e2d\u83b2\u542c\u5230\u7684\u786e\u5b9e\u662f\u6c5f\u6d77\u5929\u7684\u58f0\u97f3\uff0c \u4e0d\u8fc7\u6c5f\u6d77\u5929\u7528\u7684\u662f\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u4e4b\u672f\uff0c\u58f0\u97f3\u51dd\u6210\u4e00\u7ebf\uff0c\u6742\u5728\u53ae\u6740\u58f0\u4e2d\uff0c\u4e14\u53c8\u662f\u8fdc\u8fdc\u4f20\u6765\uff0c \u5e7d\u5fae\u96be\u8fa8\uff0c\u8c37\u4e2d\u83b2\u542c\u60ef\u4e86\u4ed6\u7684\u58f0\u97f3\uff0c\u5fc3\u610f\u76f8\u901a\uff0c\u7acb\u751f\u611f\u5e94\uff1b\u8c37\u4e4b\u534e\u5374\u5728\u5168\u526f\u5fc3\u795e\u5bf9\u4ed8\u654c \u4eba\uff0c\u6240\u60f3\u7684\u53ea\u662f\u5982\u4f55\u8131\u9669\uff0c\u56e0\u800c\u5bf9\u8fd9\u58f0\u97f3\u4e5f\u5c31\u542c\u800c\u4e0d\u95fb\uff0c\u9519\u8fc7\u4e86\u4e0e\u6c5f\u6d77\u5929\u76f8\u89c1\u7684\u673a\u4f1a\u3002 \u3000\u3000\u4e14\u8bf4\u7ae5\u59e5\u59e5\u8d76\u56de\u5979\u7684\u91d1\u8f6e\u5bab\uff0c\u53ea\u89c1\u6587\u5ef7\u58c1\u4e0e\u7f2a\u592b\u4eba\u6b63\u5728\u9662\u5b50\u91cc\u548c\u6c5f\u6d77\u5929\u6076\u6597\uff0c\u6c5f\u6d77 \u5929\u5c06\u4ed6\u4eec\u8feb\u5f97\u6b65\u6b65\u540e\u9000\uff0c\u5373\u5c06\u95ef\u8fdb\u5927\u95e8\u3002\u53e6\u5916\u8fd8\u6709\u4e00\u4e2a\u9ed1\u8863\u5c11\u5e74\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u5c06\u5bab\u4e2d\u4f8d \u536b\u6740\u5f97\u4e1c\u5954\u897f\u8dd1\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u559d\u9053\uff1a\u201c\u54ea\u91cc\u6765\u7684\u81ed\u5c0f\u5b50\uff0c\u6562\u5728\u6211\u5bab\u4e2d\u6492\u91ce\uff01\u201d\u90a3\u9ed1\u8863\u5c11\u5e74\u5530\u7684\u4e00\u5251\u5411\u5979\u523a \u6765\uff0c\u7ae5\u59e5\u59e5\u957f\u8896\u4e00\u6325\uff0c\u4fbf\u8981\u593a\u4ed6\u7684\u5b9d\u5251\u3002 \u3000\u3000\u8fd9\u9ed1\u8863\u5c11\u5e74\u6b63\u662f\u5510\u52a0\u6e90\uff0c\u4ed6\u517c\u6709\u7236\u6bcd\u4e24\u5bb6\u5251\u6cd5\u4e4b\u957f\uff0c\u5251\u950b\u4e00\u98a4\uff0c\u9aa4\u7136\u95ee\u4ece\u7ae5\u59e5\u59e5\u8896 \u5e95\u7a7f\u8fc7\uff0c\u70b9\u5979\u80f8\u53e3\u7684\u201c\u7487\u7391\u7a74\u201d\uff0c\u7ae5\u59e5\u59e5\u80fc\u6307\u4e00\u5f39\uff0c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u628a\u5510\u52a0\u6e90\u7684\u6e38\u9f99\u5251 \u5f39\u5f00\uff0c\u5510\u52a0\u6e90\u4e00\u4e2a\u6e38\u8eab\u6ed1\u6b65\uff0c\u95ea\u8fc7\u4e00\u8fb9\uff0c\u6b63\u8981\u65bd\u5c55\u8ffd\u98ce\u5251\u6cd5\uff0c\u5ffd\u89c9\u864e\u53e3\u6709\u4e00\u80a1\u9ebb\u75d2\u75d2\u7684 \u611f\u89c9\u3002 \u3000\u3000\u539f\u6765\u7ae5\u59e5\u59e5\u5728\u4ed6\u5251\u810a\u4e0a\u90a3\u4e00\u5f39\uff0c\u5df2\u4f7f\u7528\u201c\u9694\u7269\u4f20\u529f\u201d\u7684\u7edd\u6280\uff0c\u628a\u5267\u6bd2\u4f20\u4e86\u8fc7\u53bb\u3002\u5510 \u52a0\u6e90\u53d1\u89c9\u4e0d\u5999\uff0c\u53ea\u597d\u5148\u95ea\u8fc7\u4e00\u8fb9\uff0c\u6682\u505c\u653b\u51fb\u3002\u5e78\u800c\u4ed6\u53e3\u4e2d\u542b\u6709\u4e00\u74e3\u5929\u5c71\u96ea\u83b2\uff0c\u80fd\u89e3\u767e\u6bd2\uff0c \u5f53\u4e0b\u9ed8\u8fd0\u7384\u529f\uff0c\u5c06\u5929\u5c71\u96ea\u83b2\u7684\u836f\u529b\u6563\u5f00\uff0c\u6d88\u89e3\u521a\u6cbe\u4e0a\u624b\u8155\u7684\u5267\u6bd2\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u773c\u5149\u4e00\u77a5\uff0c\u89c1\u8a79\u52a0\u6e90\u5e76\u672a\u5012\u4e0b\uff0c\u5fc3\u91cc\u4e5f\u597d\u751f\u60ca\u9a87\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u201c\u7830\u201d \u7684\u4e00\u58f0\uff0c\u539f\u6765\u662f\u6587\u5ef7\u58c1\u88ab\u6c5f\u6d77\u5929\u7684\u5288\u7a7a\u638c\u529b\u9707\u5012\uff0c\u4ece\u53f0\u9636\u4e0a\u6eda\u4e0b\u6765\u3002\u7ae5\u59e5\u59e5\u987e\u4e0d\u5f97\u518d\u4f24 \u5bb3\u5510\u52a0\u6e90\uff0c\u8d81\u7740\u5510\u52a0\u6e90\u5df2\u7ecf\u95ea\u5f00\uff0c\u98de\u8eab\u4e00\u63a0\uff0c\u4e00\u638c\u4fbf\u5411\u6c5f\u6d77\u5929\u80cc\u5fc3\u51fb\u53bb\u3002\u6c5f\u6d77\u5929\u4e5f\u987e\u4e0d \u5f97\u518d\u6253\u6587\u5ef7\u58c1\uff0c\u542c\u5f97\u80cc\u540e\u52b2\u98ce\u6251\u5230\uff0c\u7acb\u5373\u53cd\u638c\u76f8\u8fce\u3002 \u3000\u3000\u53cc\u65b9\u529f\u529b\u65d7\u9f13\u76f8\u5f53\u3002\u638c\u529b\u78b0\u51b2\uff0c\u58f0\u5982\u90c1\u96f7\uff0c\u5404\u81ea\u9000\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u7ec3\u7684\u201c\u5316\u8840\u795e\u529f\u201d\u662f\u90aa\u6d3e\u4e09\u5927\u6bd2\u529f\u4e4b\u4e00\uff0c\u8db3\u4ee5\u4e0e\u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\u3001\u201c\u5927\u4e58\u822c \u82e5\u638c\u201d\u5e76\u9a7e\u9f50\u9a71\uff0c\u9f0e\u8db3\u800c\u4e09\u3002\u53ea\u8981\u4e2d\u4e86\u5979\u7684\u4e00\u638c\uff0c\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u4fbf\u8981\u574f\u8840\u800c\u4ea1\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u786c\u63a5\u4e86\u5979\u7684\u4e00\u638c\uff0c\u867d\u7136\u6ca1\u6709\u6628\u665a\u7684\u96be\u53d7\uff0c\u4f46\u4e5f\u7565\u611f\u6655\u7729\uff0c\u5fc3\u4e2d\u4f5c\u95f7\uff0c\u8fde\u5fd9\u8fd0 \u4e86\u53e3\u6c14\uff0c\u4ed6\u53e3\u4e2d\u4e5f\u542b\u6709\u4e00\u74e3\u5929\u5c71\u96ea\u83b2\uff0c\u96ea\u83b2\u7684\u6e05\u9999\u968f\u7740\u4ed6\u771f\u6c14\u7684\u8fd0\u884c\uff0c\u6c81\u4eba\u813e\u80ba\uff0c\u8165\u95f7 \u4e4b\u611f\uff0c\u767b\u65f6\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u4f0e\u4fe9\uff0c\u5c3d\u7ba1\u65bd\u5c55\uff0c\u770b\u4f60\u80fd\u5948\u6211\u4f55\uff1f\u201d\u58f0\u53d1\u638c\u5230\uff0c\u8f6c\u77ac \u4e4b\u95f4\uff0c\u53c8\u4e0e\u7ae5\u59e5\u59e5\u786c\u5bf9\u4e86\u4e09\u638c\u3002\u7ae5\u59e5\u59e5\u6bd2\u529f\u5931\u6548\uff0c\u5927\u4e3a\u60ca\u8be7\uff0c\u53ea\u597d\u51ed\u7740\u771f\u5b9e\u6b66\u529f\u4e0e\u6c5f\u6d77 \u5929\u6076\u6218\u3002 \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u529f\u529b\u4e4b\u9ad8\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u80fd\u80dc\u8fc7\u4ed6\u7684\uff1b\u9664\u4e86\u5e08\u7236\u4e4b\u5916\uff0c\u53ea\u4e0d\u8fc7\u6709\u9650\u51e0\u4eba\u800c \u5df2\u3002\u4ee5\u4ed6\u7684\u529f\u529b\u518d\u542b\u4e86\u5929\u5c71\u96ea\u83b2\uff0c\u4e16\u95f4\u5df1\u6ca1\u6709\u4ec0\u4e48\u6bd2\u836f\uff0c\u53ef\u4ee5\u4f24\u5bb3\u5f97\u4ed6\u3002\u6c5f\u6d77\u5929\u8fd0\u6c14\u4e09 \u8f6c\uff0c\u8bd5\u51fa\u5e76\u65e0\u4e2d\u6bd2\u7684\u8ff9\u8c61\uff0c\u653e\u4e0b\u4e86\u5fc3\u4e0a\u7684\u77f3\u5934\uff0c\u66f4\u662f\u7cbe\u795e\u6296\u64de\uff0c\u638c\u6cd5\u4f7f\u5f00\uff0c\u4fbf\u5982\u957f\u6c5f\u6d6a \u6d8c\uff0c\u4e00\u638c\u7d27\u8fc7\u4e00\u638c\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u722c\u4e86\u8d77\u6765\uff0c\u5411\u7f2a\u592b\u4eba\u629b\u4e86\u4e2a\u773c\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u56de\u53bb\u770b\u5b88\u72af\u4eba\u8981\u7d27\u3002\u201d\u8fd9\u65f6 \u5510\u52a0\u6e90\u5df2\u6d88\u89e3\u4e86\u6cbe\u4e0a\u624b\u8155\u7684\u5267\u6bd2\uff0c\u6b63\u8981\u518d\u52a0\u5165\u6218\u56e2\uff0c\u7f2a\u592b\u4eba\u53d1\u51fa\u4e00\u679a\u6bd2\u96fe\u91d1\u8ba1\u70c8\u7130\u5f39\uff0c \u963b\u4e86\u5510\u52a0\u6e90\u4e00\u963b\uff0c\u70df\u96fe\u5f25\u6f2b\u4e2d\uff0c\u5979\u4e0e\u6587\u5ef7\u58c1\u5df2\u9003\u5165\u5185\u5bab\u3002 \u3000\u3000\u5510\u52a0\u6e90\u4ee5\u5288\u7a7a\u638c\u529b\u626b\u8361\u4e86\u5996\u70df\u90aa\u96fe\uff0c\u6325\u52a8\u6e38\u9f99\u5b9d\u5251\uff0c\u4fbf\u62df\u5939\u653b\u7ae5\u59e5\u59e5\uff0c\u6c5f\u6d77\u5929\u4e0d\u613f \u4ee5\u4e8c\u654c\u4e00\uff0c\u8bf4\u9053\uff1a\u201c\u5510\u5144\uff0c\u6211\u542c\u5f97\u897f\u5317\u89d2\u4f3c\u6709\u53ae\u6740\u4e4b\u58f0\uff0c\u4f60\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u7ae5\u59e5\u59e5\u7684\u672c\u8eab\u529f\u529b\u672c\u6765\u4e0e\u6c5f\u6d77\u5929\u4e5f\u662f\u65d7\u9f13\u76f8\u5f53\u3001\u4f46\u8bba\u5230\u638c\u6cd5\u7684\u7cbe\u5999\uff0c\u5374\u662f\u6c5f\u6d77\u5929\u8981 \u80dc\u5979\u4e00\u7b79\u4e86\u3002\u6c5f\u6d77\u5929\u7684\u5927\u987b\u5f25\u638c\u5f0f\uff0c\u4e5d\u4e5d\u516b\u5341\u4e00\u62db\uff0c\u8fd8\u672a\u4f7f\u5230\u4e00\u534a\uff0c\u5df2\u628a\u7ae5\u59e5\u59e5\u7684\u8eab\u5f62 \u7f69\u4f4f\u3002\u7ae5\u59e5\u59e5\u6bd2\u529f\u5931\u6548\uff0c\u5185\u529b\u53c8\u80dc\u4e0d\u8fc7\u5bf9\u65b9\uff0c\u62db\u6570\u4e0a\u518d\u4e00\u843d\u4e0b\u98ce\uff0c\u90a3\u5c31\u53ea\u6709\u62db\u67b6\u7684\u4efd\u513f \u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8d8a\u6218\u8d8a\u52c7\uff0c\u773c\u770b\u5c31\u53ef\u53d6\u80dc\uff0c\u7ae5\u59e5\u59e5\u5ffd\u9053\uff1a\u201c\u4f60\u5fc3\u4e0a\u7684\u4eba\u513f\uff0c\u4f60\u662f\u8981\u5979\u8fd8\u662f\u4e0d \u8981\uff1f\u201d\u6c5f\u6d77\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u7ae5\u59e5\u59e5\u9053\uff1a\u201c\u8c37\u4e2d\u83b2\u5df2\u843d\u5728\u6211\u7684\u624b\u4e2d\uff0c\u4f60\u82e5 \u8fd8\u4e0d\u77e5\u8fdb\u9000\uff0c\u6211\u9a6c\u4e0a\u4f20\u4ee4\u5c06\u5979\u5904\u6b7b\u3002\u54fc\uff0c\u4f60\u8fd8\u4e0d\u4f4f\u624b\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e0d\u7531\u4e0d\u4fe1\uff0c\u53ea\u597d\u9000\u4e86\u4e09\u6b65\uff0c\u505c\u6b62\u653b\u51fb\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8ba9\u6211\u548c\u5979\u89c1\u4e0a\u4e00\u9762\uff0c\u6211\u5c31 \u4e0d\u4e0e\u4f60\u96be\u4e3a\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u7ae5\u59e5\u59e5\u5ffd\u5730\u4e00\u58f0\u72de\u7b11\uff0c\u628a\u624b\u4e00\u573a\uff0c\u4e00\u5927\u7247\u7eff\u5e7d\u5e7d\u7684\u7130\u706b\uff0c\u767b \u65f6\u56db\u9762\u94fa\u5f00\uff0c\u628a\u6c5f\u6d77\u5929\u56f0\u5728\u5f53\u4e2d\u3002 \u3000\u3000\u539f\u6765\u7ae5\u59e5\u59e5\u6b63\u662f\u8981\u9a97\u4ed6\u6682\u65f6\u4f4f\u624b\uff0c\u4e24\u4eba\u8eab\u5f62\u5206\u5f00\u4e4b\u540e\uff0c\u8fd9\u624d\u597d\u7528\u201c\u51b7\u7130\u641c\u9b42\u201d\u7684\u90aa \u672f\u6765\u4fb5\u5bb3\u4ed6\u3002\u8fd9\u79cd\u201c\u51b7\u7130\u201d\u4e43\u662f\u7ae5\u59e5\u59e5\u6536\u96c6\u53e4\u5893\u7684\u6bd2\u78f7\u6240\u70bc\uff0c\u6cbe\u4e0a\u4e00\u70b9\uff0c\u5c31\u8db3\u4ee5\u4ee4\u4eba\u5f62 \u9500\u9aa8\u6bc1\uff0c\u800c\u4e14\u4e00\u53d1\u5c31\u662f\u4e00\u5927\u7247\u7130\u706b\uff0c\u4ece\u56db\u9762\u516b\u65b9\u4fb5\u6765\uff0c\u6bd4\u8d77\u4ed6\u7684\u5316\u8840\u795e\u529f\uff0c\u8981\u5389\u5bb3\u591a\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u51b7\u7130\u5377\u8fc7\u4e4b\u5904\uff0c\u8349\u6728\u7126\u67af\uff0c\u77f3\u5934\u7206\u88c2\uff0c\u6c5f\u6d77\u5929\u89c1\u5f97\u5982\u6b64\u5389\u5bb3\uff0c\u4e5f\u4e0d\u7981\u6697\u6697\u5403 \u60ca\uff0c\u8f6c\u773c\u95f4\u90a3\u4e00\u5927\u7247\u51b7\u7130\u5bd2\u5149\uff0c\u5df2\u662f\u5982\u6f6e\u5377\u5230\u3002\u6c5f\u6d77\u5929\u5927\u559d\u4e00\u58f0\uff0c\u201c\u547c\u201d\u7684\u4e00\u638c\u62cd\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u662f\u6c5f\u6d77\u65e0\u529f\u529b\u4e4b\u6240\u805a\uff0c\u53ea\u89c1\u7130\u706b\u6d41\u6563\u3001\u4fe8\u5982\u6c79\u6d8c\u800c\u6765\u7684\u6d77\u6f6e\uff0c\u7a81\u7136\u78b0\u7740\u4e86\u4e00 \u9053\u65e0\u5f62\u7684\u5824\u9632\uff0c\u6d6a\u82b1\u56db\u6e85\uff0c\u5012\u6d41\u5165\u6d77\uff0c\u6765\u5f97\u5feb\u53bb\u5f97\u4e5f\u5feb\u3002\u6c5f\u6d77\u5929\u4e1c\u5357\u897f\u5317\uff0c\u8fde\u53d1\u56db\u638c\uff0c \u628a\u90a3\u4e00\u5927\u7247\u5bd2\u5149\u51b7\u7130\uff0c\u56db\u9762\u8361\u5f00\u3002\u53ef\u662f\u4ecd\u7136\u672a\u80fd\u51b2\u51fa\u6bd2\u7130\u7684\u5305\u56f4\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u7684\u529f\u529b\u548c\u4ed6\u65d7\u9f13\u76f8\u5f53\uff0c\u5f53\u4e0b\u4e5f\u53d1\u51fa\u5288\u7a7a\u638c\u529b\uff0c\u5f85\u90a3\u7130\u706b\u5377\u6765\u3002\u7531\u4e8e\u6c5f\u6d77\u5929\u662f \u5904\u5728\u6bd2\u7130\u7684\u5305\u56f4\u4e4b\u4e2d\uff0c\u56db\u9762\u53d7\u654c\uff0c\u4ed6\u7684\u638c\u529b\u4ea6\u5fc5\u987b\u56db\u65b9\u5206\u6563\uff0c\u59cb\u80fd\u9632\u5fa1\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u53cc \u65b9\u867d\u662f\u529f\u529b\u76f8\u5f53\uff0c\u5bf9\u6c5f\u6d77\u5929\u5374\u662f\u5927\u5927\u4e0d\u5229\u3002\u90a3\u4e00\u5927\u7247\u51b7\u7130\u5bd2\u5149\uff0c\u6f6e\u6c34\u822c\u500f\u8ff8\u500f\u9000\uff0c\u8d8a\u8feb \u8d8a\u8fd1\uff0c\u5708\u5b50\u4e5f\u8d8a\u7f29\u8d8a\u5c0f\uff0c\u597d\u5728\u5708\u4e88\u7f29\u5c0f\u4e4b\u540e\uff0c\u6c5f\u6d77\u5929\u7684\u9632\u5fa1\u529b\u91cf\u4e5f\u76f8\u5e94\u52a0\u5f3a\uff0c\u5728\u4ed6\u8eab\u4f53 \u65b9\u5706\u4e03\u5c3a\u4e4b\u5185\uff0c\u7130\u706b\u59cb\u7ec8\u65e0\u6cd5\u7231\u5165\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u81ea\u82e6\u82e6\u652f\u6491\uff0c\u5ffd\u542c\u5f97\u5510\u52a0\u6e90\u4e00\u7247\u60ca\u559c\u7684\u58f0\u97f3\u53eb\u9053\u201c\u7239\u7239\uff0c\u662f\u6211\uff0c\u6211\u5728\u8fd9\u513f\uff01 \u5a18\uff0c\u4f60\u4e5f\u7c73\u5566\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u95e8\u5916\u73b0\u51fa\u4e24\u4e2a\u4eba\u6765\uff0c\u6b63\u662f\u5510\u52a0\u6e90\u7684\u7236\u6bcd\u2014\u2014\u5510\u7ecf\u5929\u548c\u51b0\u5ddd \u5927\u5973\uff0c\u4ed6\u4eec\u5df2\u4ece\u5c3c\u6cca\u5c14\u8d76\u56de\u6765\u4e86\u3002 \u3000\u3000\u5510\u7ecf\u5929\u51b7\u7b11\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff0c\u539f\u6765\u662f\u4f60\u8fd9\u8001\u4e0d\u6b7b\u7684\u5996\u5987\uff0c\u5f53\u5e74\u6211\u7239\u5a18\u4e00\u5ff5\u6148\u60b2\uff0c\u672a \u66fe\u5c06\u4f60\u8bdb\u6389\uff0c\u4f60\u5374\u8eb2\u5230\u8fd9\u91cc\u4f5c\u6076\u3002\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u9053\uff1a\u201c\u6211\u4e14\u628a\u4f3d\u6bd2\u7130\u7834\u4e86\uff0c\u770b\u4f60\u8fd8\u6709\u4ec0\u4e48\u4f0e\u4fe9\uff01\u201d\u4e00\u626c\u624b\uff0c\u98de\u51fa\u4e00\u4e32\u51b0\u9b44 \u795e\u5f39\uff0c\u4e5f\u5316\u6210\u4e86\u4e00\u5927\u7247\u5bd2\u5149\u51b7\u96fe\uff0c\u7f69\u5728\u7ae5\u59e5\u59e5\u7684\u51b7\u7130\u4e4b\u4e0a\uff0c\u51b0\u9b44\u795e\u5f39\u662f\u4ece\u4e07\u8f7d\u7384\u51b0\u63d0\u70bc \u51fa\u6765\u7684\u7cbe\u534e\uff0c\u6070\u597d\u662f\u7ae5\u59e5\u59e5\u7684\u6bd2\u78f7\u6240\u70bc\u7684\u51b7\u7130\u514b\u661f\uff0c\u51b0\u9b44\u795e\u5f39\u6240\u5316\u7684\u5bd2\u5149\u51b7\u96fe\u8986\u7f69\u4e0b\u6765\uff1b \u767b\u65f6\u628a\u7ae5\u59e5\u59e5\u7684\u6bd2\u7130\u6d88\u706d\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u5411\u5510\u7ecf\u5929\u592b\u5987\u53d1\u51fa\u4e00\u84ec\u6bd2\u9488\uff0c\u56de\u8eab\u4fbf\u8dd1\u3002\u5510\u7ecf\u5929 \u6325\u5251\u7ede\u788e\u6bd2\u9488\uff0c\u559d\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u672d\u4e5f\uff0c\u4f60\u4e5f\u63a5\u6211\u4e00\u652f\u795e\u8292\uff01\u201d \u3000\u3000\u5510\u7ecf\u5929\u7684\u529f\u529b\u81ea\u662f\u8981\u6bd4\u4ed6\u7684\u513f\u5b50\u66f4\u80dc\u4e00\u7b79\uff0c\u7ae5\u59e5\u59e5\u5957\u7740\u94c1\u73af\u7684\u4e2d\u6307\u4e00\u5f39\uff0c\u53ea\u542c\u5f97 \u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u94c1\u73af\u5df2\u88ab\u5929\u5c71\u795e\u8292\u7a7f\u8fc7\uff0c\u7ae5\u59e5\u59e5\u7684\u4e2d\u6307\u4e5f\u53bb\u4e86\u4e00\u622a\uff0c\u4f46\u5979\u4ecd\u7136\u662f\u75be\u8dd1\u5982 \u98de\uff0c\u8fdb\u5165\u5bab\u5185\u3002 \u3000\u3000\u5bab\u5185\u91cd\u95e8\u53e0\u6237\uff0c\u56de\u5eca\u66f2\u6298\uff0c\u6c5f\u6d77\u5929\u6b63\u4e0d\u77e5\u5411\u4f55\u5904\u8ffd\u8e2a\uff0c\u5ffd\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u6c5f\u6d77 \u5929\u5faa\u58f0\u89c5\u8ff9\uff0c\u8d76\u8fc7\u53bb\u4e00\u770b\uff0c\u53ea\u89c1\u7ae5\u59e5\u59e5\u62ab\u5934\u6563\u53d1\uff0c\u6b63\u5728\u7528\u529b\u51fb\u6253\u4e00\u5835\u5899\u58c1\uff0c\u4e00\u9762\u6253\u4e00\u9762 \u5492\u9a82\uff0c\u542c\u5f97\u662f\u9a6c\u8428\u513f\u56fd\u571f\u8bdd\uff0c\u5374\u4e0d\u77e5\u5979\u9a82\u7684\u4ec0\u4e48\u3002 \u3000\u3000\u8fd9\u662f\u7528\u82b1\u5c97\u77f3\u6240\u780c\u7684\u4e00\u5835\u77f3\u5899\uff0c\u5728\u7ae5\u59e5\u59e5\u638c\u529b\u6253\u51fb\u4e4b\u4e0b\uff0c\u788e\u77f3\u7eb7\u843d\u5982\u96e8\uff0c\u5899\u4e0a\u73b0\u51fa \u4e00\u9053\u94c1\u95e8\uff0c\u94c1\u95e8\u7d27\u95ed\uff0c\u7ae5\u59e5\u59e5\u7684\u638c\u529b\u867d\u6709\u5f00\u7891\u88c2\u77f3\u4e4b\u80fd\uff0c\u5bf9\u8fd9\u9053\u575a\u539a\u7684\u94c1\u95e8\u5374\u662f\u65e0\u6cd5\u51fb \u7834\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u559d\u9053\uff1a\u201c\u4f60\u628a\u8c37\u59d1\u5a18\u85cf\u5728\u54ea\u513f\uff1f\u201d\u7ae5\u59e5\u59e5\u89c1\u6c5f\u6d77\u5929\u8ffd\u5230\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u84e6\u5730\u8dc3 \u8d77\uff0c\u53ea\u542c\u5f97\u53c8\u662f\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u5c4b\u9876\u7ed9\u5979\u649e\u7a7f\u4e86\u4e00\u4e2a\u7a9f\u7abf\uff0c\u7ae5\u59e5\u59e5\u4e00\u5934\u5c31\u94bb\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u5916\u9762\u5ffd\u5730\u4f20\u6765\u51e0\u58f0\u957f\u5578\uff0c\u8fd8\u6742\u7740\u60ca\u9a87\u7684\u53eb\u58f0\uff0c\u5578\u58f0\u900f\u5165\u91cd\u95e8\uff0c\u56de \u58f0\u55e1\u55e1\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u663e\u5f97\u529f\u529b\u5341\u5206\u6df1\u539a\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u9053\uff1a\u201c\u83ab\u975e\u662f\u6211\u7684\u73e0\u7a46\u5e08\u5f1f\u6765\u4e86\uff1f\u201d\u4ed6\u672c\u6765\u8e0c\u8e87\u672a\u51b3\uff0c\u4e0d\u77e5\u662f \u7ee7\u7eed\u8ffd\u8d76\u7ae5\u59e5\u59e5\uff0c\u8fd8\u662f\u5148\u53bb\u641c\u67e5\u94c1\u95e8\u4e4b\u5185\u7684\u79d8\u5bc6\uff0c\u6b64\u65f6\u542c\u5f97\u5578\u58f0\uff0c\u5fc3\u610f\u7acb\u51b3\uff0c\u5f53\u4e0b\u65bd\u5c55 \u51fa\u4e00\u9e64\u51b2\u5929\u7684\u8f7b\u529f\uff0c\u4e5f\u8ddf\u7740\u7ae5\u59e5\u59e5\u4ece\u90a3\u4e2a\u7a9f\u7abf\u8df3\u51fa\u3002 \u3000\u3000\u4e0b\u9762\u662f\u5fa1\u82b1\u56ed\uff0c\u6708\u8272\u867d\u7136\u4e0d\u5f88\u660e\u4eae\uff0c\u4f46\u6c5f\u6d77\u5929\u4ece\u5c4b\u9876\u4e0a\u671b\u4e0b\u53bb\uff0c\u51ed\u7740\u4ed6\u9510\u5229\u7684\u76ee\u529b\uff0c \u4e00\u773c\u5df2\u8ba4\u51fa\u51e0\u4e2a\u719f\u4eba\uff01 \u3000\u3000\u53ea\u89c1\u5728\u90a3\u7247\u8349\u5730\u4e0a\u73b0\u51fa\u4e00\u7fa4\u7537\u5973\uff0c\u5171\u6709\u4e03\u4eba\u4e4b\u591a\u3002\u4e0d\u4f46\u6709\u5510\u52aa\u73e0\u7a46\uff0c\u8fd8\u6709\u4e91\u743c\u5144\u59b9 \u548c\u534e\u4e91\u78a7\u3002\u6c5f\u6d77\u5929\u8fd9\u4e00\u559c\u975e\u540c\u5c0f\u53ef\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u73e0\u7a46\u5e08\u5f1f\uff0c\u78a7\u59b9\uff0c\u4e91\u5927\u54e5\uff0c\u4e91\u59d0\u59d0\uff0c \u4f60\u95e8\u90fd\u8131\u9669\u5566\uff01\u201d \u3000\u3000\u8fd8\u6709\u4e00\u7537\u4e8c\u5973\uff0c\u6c5f\u6d77\u5929\u5374\u4e0d\u8ba4\u5f97\u3002\u8fd9\u65f6\u7ae5\u59e5\u59e5\u5df2\u5148\u5230\u4e86\u4e0b\u9762\u90a3\u7247\u8349\u5730\uff0c\u84e6\u5730\u559d\u9053\uff1a \u201c\u7389\u5c0f\u5b50\uff0c\u4f60\u62d0\u5e26\u4e86\u516c\u4e3b\uff0c\u5c45\u7136\u8fd8\u6562\u56de\u6765\uff1f\u201d\u4e00\u6293\u5c31\u5411\u6c5f\u6d77\u5929\u4e0d\u8ba4\u5f97\u7684\u90a3\u4e2a\u5973\u5b50\u6293\u53bb\u3002 \u539f\u6765\u8fd9\u4e00\u7537\u4e8c\u5973\u6b63\u662f\u6606\u5e03\u5170\u56fd\u7684\u7f57\u66fc\u632a\u516c\u4e3b\u548c\u7389\u6606\u4ed1\u3001\u7389\u73b2\u73d1\u5144\u59b9\uff0c\u4ed6\u4eec\u548c\u5510\u52aa\u73e0\u7a46\u7b49 \u4eba\u4e00\u8d77\uff0c\u524d\u65e5\u4ece\u90a3\u5ea7\u9ad8\u5bab\u52a8\u8eab\uff0c\u6070\u6070\u4eca\u65e5\u8d76\u5230\u4eac\u90fd\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u610f\u56fe\u4e0e\u6606\u5e03\u5170\u56fd\u56fd\u738b\u79d8\u5bc6\u4f1a\u6664\uff0c\u89e3\u91ca\u8bef\u6740\u4f7f\u81e3\u4e4b\u4e8b\uff0c\u9042\u8fde\u591c\u5165\u5bab\uff0c\u62df\u8bf7\u7f57 \u867d\u5a1c\u516c\u4e3b\u4ee3\u4e3a\u5f15\u89c1\uff0c\u62dc\u8bbf\u56fd\u738b\u3002\u54ea\u77e5\u5bab\u4e2d\u6b63\u95f9\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u8fd8\u672a\u5f97\u89c1\u56fd\u738b\uff0c\u4fbf\u78b0\u4e0a\u4e86\u7ae5 \u59e5\u59e5\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u6b63\u82e6\u4e8e\u65e0\u6cd5\u8131\u8eab\uff0c\u4e00\u773c\u77a5\u89c1\u7f57\u66fc\u5a1c\u516c\u4e3b\uff0c\u6076\u5ff5\u9661\u751f\u3002 \u3000\u3000\u4fbf\u60f3\u628a\u7f57\u66fc\u5a1c\u516c\u4e3b\u63b3\u4e3a\u4eba\u8d28\uff0c\u4ee5\u5229\u9003\u8d70\u3002 \u3000\u3000\u7389\u6606\u4ed1\u5144\u59b9\u6b63\u5728\u516c\u4e3b\u8eab\u8fb9\uff0c\u7109\u80fd\u5bb9\u5979\u5f97\u901e\uff1f\u53ea\u542c\u5f97\u53f1\u54a4\u58f0\u4e2d\uff0c\u7389\u6606\u4ed1\u4e00\u62f3\u6363\u51fa\uff0c\u7389 \u73b2\u73d1\u5219\u6325\u821e\u7389\u8427\uff0c\u4e00\u62db\u4e4b\u95f4\uff0c\u904d\u88ad\u7ae5\u59e5\u59e5\u7684\u4e03\u5904\u7a74\u9053\u3002 \u3000\u3000\u7389\u5bb6\u5144\u59b9\u5bb6\u5b66\u6e0a\u6e90\uff0c\u6b66\u529f\u539f\u975e\u6cdb\u6cdb\uff0c\u72d9\u6bd4\u8d77\u7ae5\u59e5\u59e5\u6765\uff0c\u5c1a\u8fd8\u662f\u6709\u6240\u4e0d\u53ca\u3002\u53cc\u65b9\u653b\u5b88 \u5947\u5feb\uff0c\u7ae5\u59e5\u59e5\u811a\u5c16\u521a\u521a\u7740\u5730\uff0c\u80f8\u53e3\u7684\u201c\u7487\u7391\u7a74\u201d\u5df2\u7ed9\u7389\u73b2\u73d1\u7684\u7389\u8427\u70b9\u4e2d\uff0c\u4f46\u7389\u8427\u89e6\u5904\uff0c \u5374\u5982\u4e00\u56f0\u68c9\u7d6e\uff0c\u7ae5\u59e5\u59e5\u72de\u7b11\u9053\uff1a\u201c\u597d\u4e2a\u4e0d\u77e5\u6b7b\u6d3b\u7684\u4e2b\u5934\uff0c\u7adf\u6562\u4e0e\u6211\u52a8\u624b\uff01\u201d\u4e94\u6307\u5982\u94a9\uff0c \u5288\u624b\u5c31\u6293\u4f4f\u4e86\u7389\u8427\uff0c\u5de6\u638c\u4e00\u7ffb\uff0c\u53c8\u6293\u7740\u4e86\u7389\u6606\u4ed1\u7684\u62f3\u5934\u3002 \u3000\u3000\u5510\u52aa\u73e0\u7a46\u559d\u9053\uff1a\u201c\u4f60\u5c31\u662f\u4ec0\u4e48\u91d1\u8f6e\u5723\u6bcd\u5417\uff0c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u53d7\u738b\u5ba4\u4f9b\u517b\uff0c\u7adf\u6562\u6b3a\u4fae\u516c \u4e3b\uff1f\u201d\u4eba\u8fd8\u672a\u5230\uff0c\u4e2d\u6307\u4e00\u5f39\uff0c\u5df2\u662f\u53d1\u51fa\u4e00\u80a1\u65e0\u5f62\u7f61\u6c14\uff0c\u75be\u70b9\u7ae5\u59e5\u59e5\u80cc\u5fc3\u5927\u7a74\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u4eba\u6765\u5f97\u6bd4\u5510\u52aa\u73e0\u7a46\u66f4\u5feb\uff0c\u51e0\u4e4e\u662f\u8eab\u5251\u5408\u4e00\uff0c\u5316\u6210\u4e86\u4e00\u9053\u94f6\u8679\uff0c\u5411\u524d\u51b2\u523a\uff01\u8fd9 \u4eba\u6b63\u662f\u5510\u52a0\u6e90\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u80cc\u5fc3\u5927\u7a74\u4e00\u9ebb\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u53c8\u89c1\u5510\u52a0\u6e90\u653b\u5230\uff0c\u4e0d\u6562\u604b\u6218\uff0c\u5ffd\u5730\u628a\u7389\u8427\u4e00\u5f39\uff0c \u968f\u5373\u79fb\u5f62\u6362\u4f4d\uff0c\u9000\u51fa\u5708\u5b50\u3002\u5979\u8fd9\u4e00\u5f39\uff0c\u7528\u7684\u662f\u80a1\u5de7\u52b2\uff0c\u7389\u8427\u5f39\u8d77\uff0c\u6070\u6070\u8fce\u4e0a\u4e86\u5510\u52a0\u6e90\u7684 \u5b9d\u5251\u3002\u5e78\u800c\u5510\u52a0\u6e90\u5251\u672f\u7cbe\u5999\uff0c\u6536\u53d1\u968f\u5fc3\uff0c\u542c\u5f97\u94ee\u7684\u4e00\u58f0\uff0c\u8fde\u5fd9\u5378\u52b2\u6536\u5251\uff0c\u8fd9\u624d\u6ca1\u6709\u8bef\u4f24 \u4e86\u7389\u73b2\u73d1\u3002 \u3000\u3000\u7389\u73b2\u73d1\u60ca\u559c\u4ea4\u96c6\uff0c\u811a\u6b65\u4e00\u4e2a\u8e09\u8dc4\uff0c\u51e0\u4e4e\u5012\u4eba\u5510\u52a0\u6e90\u6000\u4e2d\uff0c\u53eb\u9053\uff1a\u201c\u6e90\u54e5\uff0c\u4f60\u56de\u6765\u4e86\uff01\u201d \u5510\u52a0\u6e90\u5c06\u5979\u6276\u7a33\uff0c\u4e5f\u51e0\u4e4e\u7591\u662f\u68a6\u4e2d\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7389\u59b9\uff0c\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u548c\u4f60\u89c1\u9762\u3002\u201d\u4e24 \u4eba\u4e45\u522b\u91cd\u9022\uff0c\u60c5\u610f\u7f20\u7ef5\uff0c\u5df2\u65e0\u6687\u518d\u53bb\u7406\u4f1a\u90a3\u7ae5\u59e5\u59e5\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u8eab\u6cd5\u5982\u7535\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u53cc\u624b\u4fbf\u5411\u5510\u52aa\u73e0\u7a46\u6293\u4e0b\uff0c\u5510\u52aa\u73e0\u7a46\u4ee5\u5927\u4e58\u822c\u82e5\u638c\u529b\u8fd8 \u4e86\u4e00\u638c\uff0c\u5510\u52aa\u73e0\u7a46\u529f\u529b\u7565\u900a\u4e00\u7b79\uff0c\u4f46\u4ea6\u76f8\u5dee\u4e0d\u8fdc\uff0c\u8fd9\u4e00\u638c\u628a\u7ae5\u59e5\u59e5\u7684\u53cc\u6293\u8361\u5f00\uff0c\u4fbf\u5373\u9000 \u5f00\u51e0\u6b65\uff0c\u6d88\u89e3\u4e86\u7ae5\u59e5\u59e5\u90a3\u4e00\u62db\u7684\u52b2\u5ea6\u3002 \u3000\u3000\u5ba3\u59e5\u59e5\u8bd5\u4e86\u4e00\u62db\uff0c\u5df2\u77e5\u5510\u52aa\u73e0\u7a46\u5e76\u975e\u597d\u60f9\uff0c\u5979\u5fd7\u5728\u62ff\u4e2a\u4fd8\u864f\uff0c\u4f5c\u4e3a\u4eba\u8d28\uff0c\u5fd9\u5373\u8f6c\u4e2a \u65b9\u5411\uff0c\u4e00\u58f0\u72de\u7b11\uff0c\u53c8\u5411\u534e\u4e91\u78a7\u6251\u53bb\u3002 \u3000\u3000\u4e91\u743c\u5144\u5f1f\u4e0e\u534e\u4e91\u78a7\u540c\u5728\u4e00\u8d77\uff0c\u4e24\u5144\u59b9\u4e00\u638c\u62a4\u80f8\uff0c\u4e00\u638c\u8fce\u654c\uff0c\u7528\u7684\u662f\u4e91\u5bb6\u72ec\u6b65\u5929\u4e0b\u7684 \u5927\u529b\u91d1\u521a\u638c\uff0c\u534e\u4e91\u78a7\u5219\u65bd\u5c55\u67d4\u4e91\u5251\u6cd5\uff0c\u4ee5\u5b88\u4e3a\u653b\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u4e00\u62db\u201c\u5927\u9e4f\u5c55\u7fc5\u201d\uff0c\u53cc\u638c\u4e00\u5206\uff0c\u7535\u5149\u77f3\u706b\u4e4b\u95f4\uff0c\u5df2\u4e0e\u4e91\u5bb6\u5144\u59b9\u5404\u81ea\u5bf9\u4e86\u4e00\u638c\u3002 \u4e91\u743c\u529f\u529b\u8f83\u9ad8\uff0c\u4e5f\u7981\u4e0d\u4f4f\u4e00\u4e2a\u8e09\u8dc4\uff0c\u8eab\u5f62\u503e\u4fa7\uff1b\u4e91\u58c1\u63a5\u4e86\u5979\u7684\u4e00\u638c\uff0c\u53ea\u89c9\u6c14\u8840\u7ffb\u6d8c\uff0c\u767b \u65f6\u4f3c\u76ae\u7403\u822c\u629b\u4e86\u8d77\u6765\uff0c\u5e78\u800c\u91cd\u59e5\u59e5\u662f\u53cc\u638c\u5206\u51fb\uff0c\u6bcf\u4e00\u638c\u53ea\u6709\u4e94\u6210\u529f\u529b\uff0c\u8981\u4e0d\u7136\u810f\u8151\u4e5f\u8981 \u9707\u4f24\u3002\u4e91\u58c1\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7b4b\u6597\u7ffb\u4e0b\u6765\uff0c\u811a\u5c16\u5c1a\u672a\u843d\u5730\uff0c\u5df2\u88ab\u4eba\u53cc\u81c2\u62b1\u4f4f\uff0c\u539f\u6765\u662f\u5510\u52aa\u73e0 \u7a46\u8d76\u6765\uff0c\u5c06\u5979\u63a5\u4e0b\u3002 \u3000\u3000\u91cd\u59e5\u59e5\u6253\u5f00\u7f3a\u53e3\uff0c\u957f\u81c2\u75be\u4f38\uff0c\u4fbf\u5411\u534e\u4e91\u78a7\u80f8\u524d\u6293\u5230\uff0c\u534e\u4e91\u78a7\u6a2a\u5251\u4e00\u5c01\uff0c\u53ea\u542c\u5f97\u201c\u94ee\u201d \u7684\u4e00\u58f0\uff0c\u90a3\u53e3\u9752\u94a2\u5251\u7adf\u88ab\u7ae5\u59e5\u59e5\u5f53\u4e2d\u4e00\u5f39\uff0c\u65ad\u4e3a\u4e24\u6bb5\uff01\u534e\u4e91\u78a7\u7981\u4e0d\u4f4f\u90a3\u80a1\u53cd\u5f39\u7684\u529b\u9053\u3002 \u671d\u5929\u8dcc\u5012\uff01 \u3000\u3000\u7ae5\u59e5\u59e5\u6b63\u8981\u4e00\u6293\u4e4b\u4e0b\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u5927\u559d\uff0c\u5c31\u4f3c\u5934\u9876\u4e0a\u7a7a\u54cd\u8d77\u4e00\u4e2a\u7126\u96f7\uff0c\u539f\u6765\u662f\u6c5f\u6d77 \u5929\u5df2\u7ecf\u4ece\u5c4b\u9876\u4e0a\u8df3\u4e86\u4e0b\u6765\uff0c\u4eba\u5728\u534a\u7a7a\uff0c\u5df2\u662f\u4e00\u638c\u51fb\u4e0b\uff0c\u7ae5\u59e5\u59e5\u53ea\u5f97\u53cc\u638c\u9f50\u51fa\uff0c\u6321\u4ed6\u4e00\u51fb\uff0c \u4e24\u4eba\u529f\u529b\u76f8\u5f53\uff0c\u4f46\u6c5f\u6d77\u5929\u662f\u4ece\u9ad8\u7a7a\u51b2\u4e0b\uff0c\u52a0\u4e0a\u4e86\u4e00\u80a1\u51b2\u529b\uff0c\u7ae5\u59e5\u59e5\u62b5\u654c\u4e0d\u4f4f\uff0c\u811a\u5c16\u4e00\u70b9\uff0c \u4fbf\u662f\u4e00\u4e2a\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\u5012\u7eb5\u51fa\u53bb\uff0c\u5979\u8f7b\u529f\u672c\u6765\u4e0d\u5f31\uff0c\u501f\u7740\u6c5f\u6d77\u5929\u8fd9\u4e00\u51b2\u51fb\u7684\u529b\u9053\uff0c\u53bb \u52bf\u5982\u7bad\uff0c\u8f6c\u773c\u95f4\u4e0d\u89c1\u8e2a\u5f71\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8bb0\u6302\u7740\u534e\u4e91\u7f72\uff0c\u65e0\u6687\u8ffd\u8d76\uff0c\u6253\u9000\u4e86\u7ae5\u59e5\u59e5\u4e4b\u540e\uff0c\u8fde\u5fd9\u8dd1\u8fc7\u53bb\u770b\uff0c\u5374\u89c1\u4e91\u743c\u65e9 \u5df2\u628a\u534e\u4e91\u78a7\u4ece\u5730\u4e0a\u62c9\u8d77\uff0c\u534e\u4e91\u78a7\u6b63\u81ea\u9760\u7740\u4ed6\u7684\u80a9\u818a\uff0c\u5a07\u5598\u5401\u5401\u3002 \u3000\u3000\u4e91\u743c\u89c1\u6c5f\u6d77\u5929\u8d70\u6765\uff0c\u4f3c\u4e4e\u6709\u70b9\u5c34\u5c2c\uff0c\u8f7b\u8f7b\u628a\u534e\u4e91\u78a7\u62c9\u5347\uff0c\u8bf4\u9053\uff1a\u201c\u5e78\u4e8f\u4f60\u51fa\u624b\u5f97\u5feb\uff0c \u78a7\u59b9\u672a\u66fe\u53d7\u4f24\u3002\u201d\u6c5f\u6d77\u5929\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u60ca\u559c\u4ea4\u96c6\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u597d\u4e86\uff0c\u8fd9\u5c31\u597d\u4e86\uff01\u201d\u8fd9 \u8bdd\u8868\u9762\u542c\u6765\u662f\u5e86\u5e78\u534e\u4e91\u78a7\u6ca1\u6709\u53d7\u4f24\uff0c\u5176\u5b9e\u4ed6\u5fc3\u4e2d\u6240\u60f3\u4e43\u662f\u53e6\u5916\u4e00\u79cd\u610f\u601d\uff0c\u8fd9\u4e5f\u4e0d\u5fc5\u7ec6\u8868 \u4e86\u3002 \u3000\u3000\u534e\u4e91\u78a7\u767e\u611f\u4ea4\u96c6\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u77e5\u662f\u751c\u662f\u82e6\u8fd8\u662f\u8f9b\u9178\uff1f\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u8c37\u59d0\u59d0\u9047\u96be\uff0c \u6211\u662f\u4e3a\u8c37\u59d0\u59d0\u6765\u7684\uff0c\u4f60\u8fd8\u6ca1\u6709\u5c06\u5979\u6551\u51fa\u6765\u5417\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u6b63\u5728\u627e\u5979\uff0c\u8fd9\u5ea7\u5bab\u4e2d\u6709 \u9053\u94c1\u95e8\uff0c\u6211\u60f3\u73b0\u5728\u5c31\u53bb\u641c\u67e5\u3002\u201d \u3000\u3000\u516c\u4e3b\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u6837\u5206\u5934\u641c\u7d22\u66f4\u597d\uff0c\u53cd\u6b63\u90a3\u5996\u5a46\u5df2\u88ab\u6253\u8dd1\uff0c\u8c37\u59d1\u5a18\u603b\u53ef\u4ee5\u627e\u5f97 \u51fa\u6765\u3002\u6211\u5148\u53bb\u89c1\u6211\u7684\u54e5\u54e5\u5427\u3002\u201d\u5510\u52aa\u73e0\u7a46\u7b49\u4eba\u4e5f\u6025\u4e8e\u8981\u89c1\u6606\u5e03\u5170\u738b\uff0c\u5f53\u4e0b\u9664\u4e86\u6c5f\u6d77\u5929\u4e4b \u5916\uff0c\u5176\u4ed6\u7684\u4eba\u90fd\u968f\u7740\u516c\u4e3b\u53bb\u89c1\u56fd\u738b\u3002 \u3000\u3000\u5bab\u4e2d\u6b66\u58eb\u90fd\u8ba4\u5f97\u516c\u4e3b\uff0c\u7eb7\u7eb7\u8d70\u6765\u7980\u62a5\uff0c\u8bf4\u662f\u56fd\u738b\u90a3\u8fb9\u4f3c\u4e4e\u51fa\u4e86\u4e8b\u60c5\uff0c\u5c3c\u6cca\u5c14\u6b66\u58eb\u5df2 \u628a\u5bdd\u5bab\u5305\u56f4\u8d77\u6765\uff0c\u5927\u4e0a\u7687\u4e5f\u5df2\u5230\u90a3\u8fb9\u53bb\u4e86\uff0c\u7f57\u66fc\u5a1c\u516c\u4e3b\u5927\u4e3a\u7740\u6025\uff0c\u8fde\u5fd9\u52a0\u5feb\u811a\u6b65\uff0c\u5306\u5306 \u8d76\u53bb\u3002 \u3000\u3000\u4e14\u8bf4\u592a\u4e0a\u7687\u88ab\u64d2\u4e4b\u540e\uff0c\u5c3c\u6cca\u5c14\u5e9f\u738b\u6b63\u5728\u9f13\u5176\u5982\u7c27\u4e4b\u820c\uff0c\u5bf9\u592a\u4e0a\u7687\u5a01\u80c1\u5229\u8bf1\uff0c\u60f3\u8981\u4ed6 \u505a\u4e2a\u5080\u5121\uff0c\u51fa\u6765\u6536\u62ac\u6b8b\u5c40\uff0c\u5ffd\u542c\u5f97\u6740\u58f0\u9707\u5929\uff0c\u5b5f\u54c8\u8d64\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u4e0d\u597d\u4e86\uff0c\u7f57\u66fc\u5a1c\u516c\u4e3b \u5e26\u9886\u4e00\u7fa4\u6b66\u58eb\u6740\u6765\uff0c\u54b1\u4eec\u7684\u4eba\u62b5\u654c\u4e0d\u4f4f\uff0c\u5df2\u88ab\u4ed6\u4eec\u653b\u8fdb\u6765\u4e86\u3002\u201d \u3000\u3000\u5c3c\u6cca\u5c14\u5e9f\u738b\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u7f57\u66fc\u5a1c\u516c\u4e3b\u4e0d\u662f\u542c\u8bf4\u5df2\u7ecf\u548c\u60c5\u90ce\u79c1\u9003\u4e86\u7684\u5417\uff0c\u600e\u7684 \u53c8\u56de\u6765\u4e86\uff1f\u91d1\u8f6e\u5723\u6bcd\u4f55\u5728\uff0c\u4e3a\u4f55\u4e0d\u963b\u6b62\u5979\uff1f\u5b54\u96c0\u660e\u4f26\u738b\uff0c\u8bf7\u3001\u8bf7\u4f60\u2026\u2026\u201d\u201c\u8bf7\u4f60\u51fa\u53bb\u62b5 \u6321\u4e00\u9635\u201d\u8fd9\u53e5\u8bdd\u8fd8\u6765\u8bf4\u5f97\u5b8c\u5168\uff0c\u53ea\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u5927\u95e8\u5df1\u88ab\u6253\u5f00\u3002\u5510\u7ecf\u5929\u592b\u5987\u548c\u5510 \u52aa\u73e0\u7a46\u9996\u5148\u6740\u5165\uff0c\u8ddf\u7740\u662f\u7389\u5bb6\u5144\u59b9\u4fdd\u62a4\u4f4f\u7f57\u66fc\u5a1c\u516c\u4e3b\uff0c\u4e5f\u6740\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u5e9f\u738b\u89c1\u4e86\u51b0\u5ddd\u5929\u5973\uff0c\u5413\u5f97\u9762\u5982\u4e0a\u8272\uff0c\u8fde\u5fd9\u6293\u7740\u592a\u4e0a\u7687\uff0c\u4e00\u624b\u62d4\u51fa\u4f69\u5200\uff0c\u60f3\u628a \u592a\u4e0a\u7687\u5f53\u4f5c\u76fe\u724c\uff0c\u5fcd\u89c9\u5947\u5bd2\u5f7b\u9aa8\uff0c\u4f69\u5200\u62ff\u634f\u4e0d\u7262\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\u3001\u6389\u5728\u5730\u4e0a\u3002\u539f\u6765\u662f\u51b0 \u5ddd\u5929\u5973\u53d1\u51fa\u4e86\u51b0\u9b44\u795e\u5f39\uff0c\u6070\u6070\u6253\u4e2d\u4ed6\u7684\u864e\u53e3\u3002 \u3000\u3000\u7f57\u66fc\u5a1c\u516c\u4e3b\u53eb\u9053\uff1a\u201c\u7237\u7237\uff01\u201d\u7956\u5b59\u76f8\u62e5\uff0c\u60b2\u559c\u4ea4\u96c6\uff0c\u4e24\u4eba\u90fd\u4e0d\u7981\u653e\u58f0\u54ed\u6ce3\u3002\u666f\u6708\u4e0a \u4eba\u521a\u8df3\u8fc7\u6765\uff0c\u5510\u52a0\u6e90\u559d\u9053\uff1a\u201c\u8d3c\u79c3\uff0c\u8ba4\u5f97\u6211\u4e48\uff1f\u201d \u3000\u3000\u5510\u52a0\u6e90\u66fe\u662f\u5c3c\u6cca\u5c14\u4e49\u519b\u5143\u5e05\uff0c\u666f\u6708\u4e0a\u4eba\u3001\u5b5f\u54c8\u8d64\u7b49\u90fd\u662f\u4ed6\u624b\u4e0b\u8d25\u5c06\uff0c\u4e00\u89c1\u662f\u4ed6\uff0c\u5148 \u81ea\u5413\u5f97\u614c\u4e86\u3002\u5510\u52a0\u6e90\u62d4\u51fa\u6e38\u9f99\u5b9d\u5251\uff0c\u626c\u7a7a\u4e00\u95ea\uff0c\u53ea\u662f\u4e00\u62db\uff0c\u4fbf\u628a\u666f\u6708\u4e0a\u4eba\u4ec5\u5b58\u7684\u4e00\u9762\u94dc \u94b9\u7834\u4f5c\u4e24\u8fb9\uff0c\u518d\u4e00\u62db\u5c31\u523a\u4e2d\u4e86\u4ed6\u7684\u7a74\u9053\u3002\u53e6\u4e00\u8fb9\uff0c\u7389\u6606\u4ed1\u4e5f\u53ea\u662f\u4e00\u62f3\u4fbf\u628a\u5b5f\u54c8\u8d64\u6253\u5012\u3002 \u3000\u3000\u5510\u52a0\u6e90\u770b\u51fa\u5927\u4e0a\u7687\u662f\u6781\u70b9\u4e86\u7a74\u9053\u3002\u4fbf\u5373\u7ed9\u4ed6\u89e3\u5f00\u3002\u5c3c\u6cca\u5c14\u5e9f\u738b\u53eb\uff1a\u201c\u8868\u59b9\uff0c\u9976\u547d\uff01 \u8001\u7956\u5b97\uff0c\u9976\u547d\uff01\u201d\u51b0\u5ddd\u5929\u5973\u65a5\u9053\uff1a\u201c\u4f60\u5728\u672c\u56fd\u6b8b\u66b4\u4e0d\u4ec1\uff0c\u53c8\u6765\u90bb\u56fd\u5174\u98ce\u4f5c\u6d6a\uff0c\u8c01\u662f\u4f60\u7684 \u8868\u59b9\uff1f\u4f60\u5927\u7f6a\u96be\u9976\uff0c\u6e90\u51e1\uff0c\u628a\u4ed6\u7f1a\u4e86\uff01\u201d \u3000\u3000\u5510\u52a0\u6e90\u8fd8\u672a\u52a8\u624b\uff0c\u592a\u4e0a\u7687\u5df1\u62fe\u8d77\u62d0\u6756\uff0c\u5927\u6012\u9a82\u9053\uff1a\u201c\u4f60\u51e0\u4e4e\u5f04\u5f97\u6211\u56fd\u7834\u5bb6\u4ea1\uff0c\u8fd8\u60f3 \u6211\u9976\u4f60\u4e48\uff1f\u201d\u6c14\u6068\u4e07\u5206\uff0c\u4f7f\u5c3d\u4e86\u5e73\u751f\u6c14\u529b\uff0c\u4e00\u62d0\u51fb\u4e0b\uff0c\u767b\u65f6\u628a\u5c3c\u6cca\u5c14\u5e9f\u738b\u4e24\u6761\u817f\u90fd\u6253\u65ad \u4e86\uff0c\u592a\u4e0a\u7687\u6c14\u547c\u547c\u5730\u8fd8\u8981\u518d\u6253\uff0c\u7f57\u66fc\u5a1c\u516c\u4e3b\u529d\u9053\uff1a\u201c\u7237\u7237\uff0c\u7701\u70b9\u6c14\u529b\u3002\u4ed6\u662f\u5c3c\u6cca\u5c14\u56fd\u8d3c\uff0c \u54b1\u4eec\u9001\u4ed6\u56de\u53bb\uff0c\u8ba9\u4ed6\u53d7\u4ed6\u672c\u56fd\u7684\u56fd\u6cd5\u5236\u88c1\uff0c\u4e0d\u66f4\u597d\u4e48\uff1f\u201d\u5510\u52a0\u6e90\u8fc7\u6765\uff0c\u4fbf\u5373\u5c06\u4ed6\u7f1a\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u53ea\u5269\u4e0b\u4e00\u4e2a\u5b54\u96c0\u660e\u4f26\u738b\u8fd8\u5728\u4f5c\u56e0\u517d\u4e4b\u6597\uff0c\u5510\u7ecf\u5929\u9053\uff1a \u3000\u3000\u201c\u4f60\u4e0d\u542c\u4f60\u5e08\u5144\u826f\u8a00\uff0c\u5982\u4eca\u6094\u4e4b\u665a\u77e3\u3002\u201d\u5b54\u96c0\u660e\u4f26\u738b\u6296\u8d77\u8888\u88df\u4e00\u6251\uff0c\u5510\u7ecf\u5929\u4f7f\u51fa\u987b \u5f25\u638c\u529b\uff0c\u638c\u98ce\u4e00\u9707\uff0c\u8888\u88df\u53cd\u5377\u8fc7\u6765\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u72b6\u7c7b\u75af\u72c2\uff0c\u5927\u58f0\u543c\u53eb\uff1a\u201c\u5510\u7ecf\u5929\uff0c\u6211\u4e0e \u4f60\u98ce\u9a6c\u725b\u4e0d\u76f8\u53ca\uff0c\u4f60\u4f11\u5f97\u6b3a\u4eba\u592a\u751a\uff01\u6765\uff0c\u6765\uff0c\u6765\uff0c\u6211\u628a\u8fd9\u6761\u6027\u547d\u4e0e\u4f60\u62fc\u4e86\uff01\u201d\u5510\u7ecf\u5929\u7709 \u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u53ef\u662f\u8bf4\u9519\u4e86\uff01\u2026\u2026\u201d \u3000\u3000\u51b0\u5ddd\u5929\u5973\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u5728\u9102\u514b\u6c81\u5bfa\uff0c\u6211\u4eec\u53ea\u6709\u5bf9\u4f60\u5c0a\u91cd\uff0c\u5c82\u4f1a\u4e0e\u4f60\u4e3a\u96be\uff1b\u4f46\u4f60 \u5374\u504f\u8981\u505a\u4ec0\u4e48\u5c3c\u6cca\u5c14\u7684\u2018\u56fd\u5e08\u2019\uff0c\u7978\u5ef6\u6211\u56fd\u767e\u59d3\uff0c\u6211\u5c31\u4e0d\u80fd\u4e0d\u7ba1\u4e86\uff01\u4eca\u65e5\u6211\u662f\u4ee5\u4f5b\u95e8\u62a4 \u6cd5\u4e0e\u5c3c\u6cca\u5c14\u516c\u4e3b\u7684\u53cc\u91cd\u8eab\u4efd\u6355\u4f60\uff0c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u53ef\u4e0d\u80fd\u4e0e\u4f60\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u4e0a\u7684\u89c4\u77e9\u4e86\u3002\u201d\u5f53 \u4e0b\u4e0e\u4e08\u592b\u8054\u624b\u800c\u653b\uff0c\u98de\u51fa\u4e86\u4e00\u9897\u51b0\u9b44\u795e\u5f39\uff0c\u5b54\u96c0\u660e\u4f26\u738b\u88ab\u5510\u7ecf\u5929\u7684\u638c\u529b\u80f6\u7740\uff0c\u8eb2\u95ea\u4e0d\u5f00\uff0c \u51b0\u5f39\u6070\u6070\u6253\u8fdb\u4ed6\u7684\u8033\u7a8d\uff0c\u767b\u65f6\u7834\u53bb\u4e86\u4ed6\u7684\u62a4\u8eab\u6c14\u529f\u3002\u5510\u7ecf\u5929\u4e00\u638c\u5c06\u4ed6\u6253\u7ffb\uff0c\u8bf4\u9053\uff1a\u201c\u770b \u5728\u4f60\u5e08\u5144\u4efd\u4e0a\uff0c\u6211\u9976\u4f60\u4e00\u547d\u3002\u201d\u5f53\u4e0b\u5c06\u5b54\u96c0\u660e\u4f26\u738b\u4ea4\u7ed9\u6606\u5e03\u5170\u56fd\u7684\u5fa1\u6797\u519b\u7edf\u9886\u770b\u7ba1\uff0c\u7b49 \u5f85\u5e73\u4e8b\u4e4b\u540e\uff0c\u518d\u9001\u4ed6\u56de\u9102\u514b\u6c81\u5bfa\uff0c\u7531\u4ed6\u5e08\u5144\u81ea\u6559\u6cd5\u738b\u53d1\u843d\u3002 \u3000\u3000\u8fd9\u65f6\u4f17\u4eba\u5df2\u7ecf\u53d1\u73b0\u4e86\u6606\u5e03\u5170\u738b\u7684\u5c38\u4f53\uff0c\u592a\u4e0a\u7687\u9053\uff1a\u201c\u4f60\u54e5\u54e5\u5f15\u72fc\u5165\u5ba4\uff0c\u906d\u6b64\u6a2a\u7978\u3002 \u8fd8\u5e78\u4ec7\u4eba\u90fd\u5df2\u843d\u7f51\uff0c\u6211\u4e5f\u53ef\u4ee5\u7791\u76ee\u4e86\u3002\u201d \u3000\u3000\u7f57\u66fc\u5a1c\u516c\u4e3b\u4e0e\u54e5\u54e5\u867d\u7136\u4e00\u5411\u611f\u60c5\u4e0d\u597d\uff0c\u4e5f\u5f88\u4f24\u5fc3\uff0c\u6b63\u5728\u75db\u54ed\uff0c\u5ffd\u542c\u5f97\u7956\u7236\u6b64\u8a00\uff0c\u5fc3 \u5934\u4e00\u9707\uff0c\u8fde\u5fd9\u62ed\u6cea\u8bf4\u9053\uff1a\u201c\u7237\u7237\uff0c\u4f60\u80e1\u4e3a\u51fa\u6b64\u4e0d\u7965\u4e4b\u8a00\uff01\u5982\u4eca\u56fd\u4e2d\u65e0\u4e3b\uff0c\u6b63\u8981\u7237\u7237\u591a\u591a \u4fdd\u91cd\u3002\u201d \u3000\u3000\u592a\u4e0a\u7687\u8f7b\u8f7b\u629a\u6478\u516c\u4e3b\u7684\u5934\u53d1\uff0c\u8bf4\u9053\uff1a\u201c\u4eba\u751f\u4e0d\u8fc7\u767e\u5e74\uff0c\u6211\u5df1\u6d3b\u5230\u4e5d\u5341\uff0c\u60f3\u6765\u4e5f\u4e0d\u80fd \u966a\u4f60\u591a\u5c11\u65e5\u5b50\u4e86\u3002\u6211\u8fd9\u6837\u8001\u8fc8\uff0c\u96be\u9053\u4f60\u8fd8\u8981\u6211\u64cd\u5fc3\u56fd\u4e8b\u5417\uff1f\u56fd\u4e0d\u53ef\u4e00\u65e5\u65e0\u541b\uff0c\u66fc\u5a1c\uff0c\u4f60 \u4e14\u5fcd\u60b2\u4f24\uff0c\u4eca\u540e\u8fd9\u4e2a\u62c5\u5b50\uff0c\u662f\u8981\u7531\u4f60\u6765\u6311\u8d77\u4e86\u3002\u201d \u3000\u3000\u7f57\u66fc\u5a1c\u516c\u4e3b\u5413\u4e86\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u7237\u7237\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u592a\u4e0a\u7687\u9053\uff1a \u3000\u3000\u201c\u54b1\u4eec\u5c0f\u56fd\u4e60\u4fd7\u4e0e\u4e2d\u534e\u5927\u56fd\u4e0d\u540c\uff0c\u7537\u5973\u5e73\u7b49\uff0c\u90fd\u53ef\u7ee7\u4f4d\uff0c\u4ece\u73b0\u5728\u8d77\uff0c\u4f60\u5c31\u662f\u6606\u5e03\u5170 \u56fd\u7684\u5973\u738b\u4e86\uff01\u201d\u516c\u4e3b\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u4f7f\u5f97\uff1f\u201d \u3000\u3000\u592a\u4e0a\u7687\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u5144\u5f1f\uff0c\u4f60\u4e0d\u4e3a\u738b\uff0c\u96be\u9053\u8981\u672c\u56fd\u9677\u4e8e\u6df7\u4e71\u5417\uff1f\u4f60\u6027\u60c5\u521a\u6bc5\uff0c\u5374\u53c8 \u4ec1\u6148\uff0c\u80dc\u8fc7\u4f60\u7684\u54e5\u54e5\u591a\u77e3\uff01\u5f53\u521d\u82e5\u4e0d\u662f\u56e0\u4e3a\u957f\u5e7c\u6709\u522b\uff0e\u7ee7\u627f\u6cd5\u53c8\u4ee5\u7537\u5b50\u5f53\u5148\uff0c\u6211\u65e9\u5df2\u60f3 \u7acb\u4f60\u4e3a\u738b\u4e86\uff01\u201d\u516c\u4e3b\u53ea\u597d\u542b\u6cea\u5e94\u627f\uff0c\u4f17\u4eba\u4e5f\u90fd\u4e0a\u6765\u5411\u516c\u4e3b\u9053\u8d3a\u3002 \u3000\u3000\u592a\u4e0a\u7687\u9053\uff1a\u201c\u963f\u5a1c\uff0c\u4f60\u505a\u4e86\u56fd\u738b\uff0c\u6709\u4e00\u4ef6\u4e8b\u60c5\u5fc5\u987b\u7262\u7262\u8bb0\u4f4f\uff01\u201d\u7f57\u66fc\u5a1c\u516c\u4e3b\u9053\uff1a \u201c\u8bf7\u7237\u7237\u5429\u5490\u3002\u201d\u592a\u4e0a\u7687\u9053\uff1a\u201c\u9a6c\u8428\u513f\u56fd\u662f\u54b1\u4eec\u5144\u5f1f\u4e4b\u90a6\uff0c\u4f60\u5fc5\u987b\u4e0e\u9a6c\u8428\u513f\u56fd\u7f62\u5175\u4fee\u597d\uff0c \u4ece\u4eca\u4e4b\u540e\uff0c\u53ea\u80fd\u7389\u5e1b\u5f80\u6765\u3002\u51b3\u4e0d\u53ef\u5e72\u6208\u76f8\u5411\uff01\u201d\u516c\u4e3b\u9053\uff1a\u201c\u6211\u6b63\u8981\u7980\u544a\u7237\u7237\uff0c\u90a3\u9a6c\u8428\u513f \u56fd\u56fd\u738b\u5df2\u7ecf\u6765\u4e86\uff0c\u5c31\u5728\u8fd9\u513f\uff01\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u6ee1\u6000\u559c\u60a6\uff0c\u8fde\u5fd9\u4e0a\u6765\u884c\u672d\uff0c\u8bf4\u9053\uff1a\u201c\u7237\u7237\uff0c\u6211\u6b63\u8981\u5411\u4f60\u7980\u660e\u6b64\u4e8b\uff0c\u539f\u6765\u4f60 \u90fd\u5df2\u660e\u767d\u4e86\u3002\u201d\u592a\u4e0a\u7687\u6014\u4e86\u4e00\u6014\uff0c\u9053\u3002 \u3000\u3000\u201c\u4f60\u4e5f\u53eb\u6211\u7237\u7237\uff0c\u4f60\u5df2\u7ecf\u89c1\u8fc7\u4f60\u7684\u59b9\u59b9\u4e86\uff1f\u5440\uff0c\u4f60\u4eec\u4e24\u5144\u59b9\u957f\u5f97\u771f\u4f3c\uff01\u201d\u5510\u52aa\u73e0\u7a46 \u9053\uff1a\u201c\u542c\u8bf4\u7237\u7237\u8ba4\u4e86\u83b2\u59b9\u505a\u5b59\u5973\u513f\uff0c\u6211\u4e5f\u6597\u80c6\u8ddf\u7740\u59b9\u59b9\u53eb\u4e86\u3002\u201d \u3000\u3000\u592a\u4e0a\u7687\u559c\u6781\u800c\u4f4d\uff0c\u62e5\u7740\u5510\u52aa\u73e0\u7a46\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u771f\u662f\u6211\u7684\u597d\u5b59\u513f\uff01\u201d\u4f17\u4eba\u867d\u89c9\u592a \u4e0a\u7687\u4e3e\u52a8\u4f3c\u5931\u5e38\u6001\uff0c\u5374\u90fd\u4ee5\u4e3a\u4ed6\u662f\u56e0\u4e3a\u5931\u4e86\u5b59\u51e0\u4e4b\u540e\uff0c\u60b2\u4f24\u8fc7\u5ea6\u6240\u81f3\uff0c\u4e5f\u5c31\u4e0d\u600e\u4e48\u5947\u602a \u4e86\u3002\u53ea\u6709\u5510\u52aa\u73e0\u7a46\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9690\u9690\u731c\u5230\u4e86\u51e0\u5206\u3002 \u3000\u3000\u516c\u4e3b\u9053\uff1a\u201c\u8fd9\u6b21\u5168\u662f\u9760\u4e86\u73e0\u7a46\u5927\u54e5\u3002\u8981\u4e0d\u7136\u6211\u8fd8\u4e0d\u6562\u56de\u6765\u5462\u3002\u201d\u5f53\u4e0b\u5c06\u4e8b\u60c5\u7ecf\u8fc7\uff0c \u7b80\u7565\u9648\u660e\uff0c\u7389\u6606\u4ed1\u5144\u59b9\u7b49\u4eba\u4e5f\u90fd\u4e0a\u6765\u4e0e\u592a\u4e0a\u7687\u89c1\u8fc7\u4e86\u3002\u592a\u4e0a\u7687\u66f4\u662f\u559c\u6b22\uff0c\u4e00\u624b\u62c9\u7740\u5510\u52aa \u73e0\u7a46\uff0c\u4e00\u624b\u62c9\u7740\u516c\u4e3b\uff0c\u8bf4\u9053\uff1a\u201c\u4ece\u4eca\u4e4b\u540e\uff0c\u4f60\u4eec\u8981\u4f3c\u4eb2\u5144\u59b9\u4e00\u822c\uff0c\u8ba9\u4e24\u56fd\u767e\u59d3\u6c38\u4eab\u592a\u5e73 \u4e4b\u798f\uff01\u201d\u4e24\u4eba\u9f50\u58f0\u7b54\u9053\uff1a\u201c\u8fd9\u6b63\u662f\u6211\u4eec\u7684\u5fc3\u613f\u3002\u201d \u3000\u3000\u5510\u52aa\u73e0\u7a46\u9053\uff1a\u201c\u7237\u7237\uff0c\u6211\u59b9\u59b9\u5462\uff1f\u201d\u592a\u4e0a\u7687\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u6709\u89c1\u7740\uff1f\u201d\u5510\u52aa\u73e0\u7a46\u9053\uff1a \u201c\u5173\u4e8e\u83b2\u59b9\u5728\u4f60\u8fd9\u91cc\u7684\u60c5\u5f62\uff0c\u90fd\u662f\u66fc\u5a1c\u59b9\u5b50\u544a\u8bc9\u6211\u7684\u3002\u6211\u8fd8\u6ca1\u6709\u89c1\u7740\u5979\u3002\u201d\u592a\u4e0a\u7687\u8d6b\u7136 \u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4eec\u8d76\u5feb\u53bb\u628a\u5979\u627e\u6765\u3002\u5979\u548c\u5979\u7684\u5e08\u7236\uff0c\u4ece\u8fd9\u91cc\u8dd1\u51fa\u53bb\u5927\u7ea6\u8fd8\u4e0d\u5230\u534a \u4e2a\u65f6\u8fb0\u3002\u201d \u3000\u3000\u516c\u4e3b\u548c\u5510\u52aa\u73e0\u7a46\u7b49\u4eba\u521a\u521a\u8d70\u51fa\u96c6\u8d24\u9601\uff0c\u53ea\u89c1\u6c5f\u6d77\u5929\u5df2\u52ff\u52ff\u8dd1\u6765\uff0c\u95ee\u9053\uff1a\u201c\u83b2\u59b9\u5462\uff1f\u201d \u539f\u6765\u6c5f\u6d77\u5929\u7528\u5b9d\u5251\u7834\u5f00\u90a3\u9053\u94c1\u95e8\uff0c\u53ea\u89c1\u4e00\u6761\u957f\u957f\u7684\u96a7\u9053\uff0c\u53e6\u4e00\u5934\u95e8\u901a\u5230\u5bab\u5916\u3002\u96a7\u9053\u4e2d\u4ec0 \u4e48\u4eba\u90fd\u6ca1\u89c1\u7740\uff0c\u53ea\u53d1\u73b0\u4e86\u4e00\u4e9b\u96f6\u4e71\u7684\u811a\u5370\u3002 \u3000\u3000\u516c\u4e3b\u53eb\u4f17\u6b66\u571f\u90fd\u6765\u5e2e\u5fd9\u5bfb\u627e\uff0c\u53ef\u662f\u641c\u904d\u4e86\u5bab\u4e2d\u6bcf\u4e2a\u89d2\u843d\uff0c\u90fd\u6ca1\u6709\u53d1\u73b0\u8c37\u4e2d\u83b2\u3002 \u3000\u3000\u4e14\u8bf4\u8c37\u4e4b\u534e\u80cc\u7740\u8c37\u4e2d\u83b2\u9003\u51fa\u738b\u5bab\uff0c\u5fc3\u4e2d\u76d8\u76d8\uff1a\u201c\u83b2\u513f\u4e2d\u6bd2\u4e0d\u8f7b\uff0c\u76ee\u524d\u4e4b\u8ba1\uff0c\u53ea\u80fd\u627e \u4e2a\u50fb\u9759\u7684\u5730\u65b9\u6682\u4e14\u8eb2\u85cf\uff0c\u518d\u8bbe\u6cd5\u7ed9\u5979\u89e3\u6bd2\u3002\u201d\u4e8e\u662f\u5411\u7740\u96ea\u5c71\u9ad8\u5904\u8dd1\u53bb\u3002 \u3000\u3000\u96ea\u5c71\u4e0a\u51b0\u6cb3\u4ea4\u9519\uff0c\u5bd2\u6c14\u6251\u4eba\uff0c\u4f46\u8c37\u4e2d\u83b2\u547c\u51fa\u6765\u7684\u6c14\u606f\uff0c\u5374\u8d8a\u6765\u8d8a\u89c9\u7099\u70ed\uff0c\u5979\u5df1\u5931\u4e86 \u77e5\u89c9\uff0c\u8f6f\u7ef5\u7ef5\u7684\u4f0f\u5728\u8c37\u4e4b\u534e\u7684\u80a9\u5934\uff0c\u547c\u4e5f\u6765\u7684\u70ed\u6c14\uff0c\u4e0e\u8c37\u4e4b\u534e\u9888\u80cc\u63a5\u89e6\uff0c\u5f53\u771f\u662f\u6709\u5982\u706b \u70eb\u4e00\u822c\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u6b63\u81ea\u53eb\u82e6\uff0c\u5ffd\u89c1\u5c71\u5773\u5904\u6709\u4e2a\u4eba\u8d70\u51fa\uff0c\u4e09\u7edc\u957f\u987b\uff0c\u80cc\u8d1f\u846b\u82a6\uff0c\u989c\u5bb9\u6709\u4e9b\u513f\u7901\u60b4\uff0c \u4f46\u53cc\u76ee\u70af\u70af\u6709\u5149\uff0c\u663e\u7136\u662f\u4e2a\u529f\u529b\u4e0d\u51e1\u7684\u8001\u524d\u8f88\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u6014\u4e86\u4e00\u6014\uff0c\u84e6\u5730\u91cc\u5fc3\u5934\u5927\u559c\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u6765\u7684\u53ef\u662f\u534e\u5c71\u533b\u9690\u534e\u8001\u524d\u8f88\u4e48\uff1f\u201d \u90a3\u8001\u5934\u513f\u4e5f\u540c\u65f6\u53eb\u9053\uff1a\u201c\u6765\u7684\u53ef\u662f\u9099\u5c71\u638c\u95e8\u8c37\u5973\u4fa0\u4e48\uff1f\u201d\u539f\u6765\u534e\u5929\u98ce\u5f53\u5415\u56db\u5a18\u8fd8\u5728\u751f\u4e4b \u65f6\uff0c\u66fe\u5230\u8fc7\u9099\u5c71\uff0c\u89c1\u8fc7\u8c37\u4e4b\u534e\u4e00\u9762\u3002\u90a3\u65f6\u8c37\u4e4b\u534e\u8fd8\u662f\u4e2a\u5341\u4e03\u516b\u5c81\u7684\u5c0f\u59d1\u5a18\u5462\u3002\u4e8b\u9694\u591a\u5e74\uff0c \u597d\u5728\u4e24\u4eba\u76f8\u8c8c\u65e0\u591a\u6539\u53d8\uff0c\u534e\u5929\u98ce\u53c8\u8ba4\u5f97\u8c37\u4e4b\u534e\u90a3\u53e3\u971c\u534e\u5b9d\u5251\u662f\u5415\u56db\u5a18\u5f53\u5e74\u7684\u9547\u5c71\u4e4b\u5b9d\uff0c \u662f\u4ee5\u7acb\u5373\u4e0a\u524d\u76f8\u8ba4\u3002 \u3000\u3000\u534e\u65e0\u98ce\u81ea\u4ece\u90a3\u65e5\u4e0e\u6c5f\u6d77\u5929\u5206\u624b\u4e4b\u540e\uff0c\u5373\u6cbf\u7740\u51b0\u6cb3\uff0c\u5bfb\u8bbf\u5510\u52aa\u73e0\u7a46\u4e0e\u4e91\u5bb6\u5144\u59b9\uff0c\u5374\u4e0d \u77e5\u4ed6\u4eec\u5df2\u7ecf\u8131\u9669\uff0c\u4e00\u76f4\u6cbf\u7740\u90a3\u6761\u51b0\u6cb3\u4e0a\u6eaf\u6e90\u53c8\uff0c\u6765\u5230\u4e86\u8fd9\u5ea7\u96ea\u5c71\u4e4b\u4e0a\u3002\u4ed6\u627e\u4e0d\u5230\u6240\u8981\u5bfb \u627e\u7684\u4eba\uff0c\u5374\u610f\u5916\u7684\u5de7\u9047\u4e86\u8c37\u4e4b\u534e\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u89c1\u7740\u4e86\u534e\u5929\u98ce\uff0c\u5f53\u771f\u662f\u6709\u5982\u4ece\u5929\u4e0a\u6389\u4e0b\u4e86\u5b9d\u8d1d\uff0c\u4e0d\u6687\u53d9\u8bdd\uff0c\u4fbf\u5373\u8bf4\u9053\uff1a\u201c\u534e \u8001\u524d\u8f88\uff0c\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u8bf7\u4f60\u6551\u6551\u6211\u7684\u5f92\u513f\u3002\u201d\u534e\u5929\u98ce\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u7684\u5f92\u513f\uff1f\u90a3\u4e0d \u662f\u8c37\u4e2d\u83b2\u5417\uff1f\u542c\u8bf4\u5979\u9677\u5728\u6606\u5e03\u5170\u56fd\u738b\u5bab\uff0c\u4f60\u5df2\u7ecf\u5c06\u5979\u6551\u51fa\u6765\u4e86\uff1f\u5979\u600e\u4e48\u5566\uff1f\u201d\u8c37\u4e4b\u534e\u9053\uff1a \u201c\u6551\u662f\u6551\u51fa\u6765\u4e86\uff0c\u4f46\u5979\u8eab\u4e2d\u5267\u6bd2\uff0c\u5982\u4eca\u6b63\u662f\u547d\u5728\u987b\u81fe\u3002\u201d \u3000\u3000\u9644\u8fd1\u6709\u4e2a\u5ca9\u6d1e\uff0c\u534e\u5929\u98ce\u5c06\u8c37\u4e2d\u83b2\u642c\u4e86\u8fdb\u53bb\uff0c\u7ed9\u5979\u8bca\u4e86\u4e00\u628a\u8109\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u62ac\u8d77\u5934\u6765\uff0c \u9762\u6709\u8be7\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u5947\u602a\uff0c\u8fd9\u5973\u5b69\u5b50\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u4e0d\u5e94\u6709\u5982\u6b64\u529f\u529b\uff0c\u662f\u4e0d\u662f\u66fe\u670d\u4e86\u4ec0\u4e48\u836f \u7269\uff1f\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5979\u66fe\u7ecf\u670d\u4e86\u5929\u5fc3\u77f3\u3002\u2026\u2026\u201d \u3000\u3000\u534e\u5929\u98ce\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u5c31\u5bf9\u4e86\u3002\u5929\u5fc3\u77f3\u662f\u6700\u80fd\u589e\u8fdb\u529f\u529b\u7684\u5947\u836f\uff0c\u4f46\u5374\u4e5f\u662f\u2018\u70ed\u6027\u2019 \u6700\u5f3a\u7684\u4e00\u79cd\u836f\u7269\uff0c\u5979\u5f53\u65f6\u867d\u56e0\u5185\u529f\u7eaf\u6b63\uff0c\u5bfc\u5f15\u5f97\u5b9c\uff0c\u672a\u53d7\u5176\u5bb3\uff0c\u4f46\u4e5f\u672a\u66fe\u53bb\u5f97\u51c0\u5c3d\uff0c\u6070 \u5de7\u5979\u73b0\u5728\u4e2d\u7684\u6bd2\u53c8\u662f\u4e00\u79cd\u6781\u5389\u5bb3\u7684\u70ed\u6bd2\uff0c\u8fd9\u5c31\u7b49\u4e8e\u706b\u4e0a\u52a0\u6cb9\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u62c5\u5fc3\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u5f88\u3001\u5f88\u96be\u533b\u5417\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u5e78\u597d\u6211\u836f\u56ca\u91cc\u6709\u96ea\u6a90\u6563\uff0c \u8fd9\u662f\u89e3\u70ed\u6bd2\u6700\u6709\u6548\u7684\u836f\u7269\uff0c\u6211\u8fd8\u53ef\u4ee5\u7528\u91d1\u9488\u523a\u7a74\u4e4b\u6cd5\uff0c\u7ed9\u4ed6\u53d1\u6563\u3002\u4f46\u533b\u597d\u4e4b\u540e\uff0c\u6700\u5c11\u4e5f \u8fd8\u5f97\u9759\u517b\u4e09\u5929\u3002\u201d \u3000\u3000\u5f53\u4e0b\u534e\u5929\u98ce\u64ac\u5f00\u8c37\u4e2d\u83b2\u7684\u5634\u5df4\uff0c\u5c06\u846b\u58f0\u4e2d\u7684\u836f\u9152\u8c03\u5300\u4e86\u96ea\u8d61\u6563\uff0c\u7ed9\u5979\u704c\u4e86\u8fdb\u53bb\uff0c\u968f \u5373\u53d6\u51fa\u4e00\u6839\u91d1\u9488\uff0c\u523a\u5979\u7684\u5168\u8eab\u4e09\u5341\u516d\u9053\u5927\u7a74\uff0c\u6bcf\u523a\u4e09\u9488\uff0c\u4e00\u7f15\u70ed\u6c14\u5c31\u968f\u7740\u6563\u53d1\u51fa\u6765\uff0c\u523a \u904d\u4e86\u4e09\u5341\u516d\u9053\u5927\u7a74\uff0c\u534e\u5929\u98ce\u4e5f\u5df2\u7d2f\u5f97\u5927\u6c57\u6dcb\u6f13\u3002\u8f7b\u5feb\u8bf4\u9053\uff1a\u201c\u597d\u4e86\uff0c\u5927\u7ea6\u518d\u8fc7\u534a\u4e2a\u591a\u65f6 \u8fb0\uff0c\u5979\u5c31\u53ef\u4ee5\u9192\u6765\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u677e\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u534e\u8001\u524d\u8f88\uff0c\u591a\u8c22\u4f60\u4e86\uff0c\u4f60\u6b47\u4e00\u4f1a\u513f\uff0e\u6211\u53bb\u627e\u70b9\u98df\u7269\u3002\u201d \u8c37\u4e4b\u534e\u8d70\u4e86\u4e0d\u591a\u4e00\u4f1a\uff0c\u8c37\u4e2d\u83b2\u5c31\u4ece\u8ff7\u8ff7\u7cca\u7cca\u4e2d\u9192\u4e86\u8fc7\u6765\uff0c\u5f20\u5f00\u773c\u775b\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u9053\uff1a \u201c\u6211\u7684\u5e08\u7236\u5462\uff1f\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff0c\u4f60\u53c8\u662f\u8c01\uff1f\u201d\u534e\u5929\u98ce\u8f7b\u8f7b\u629a\u6478\u5979\u7684\u5934\u53d1\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u597d\u4e86\uff0c\u597d\u5b69\u5b50\uff0c\u4f60\u9192\u8fc7\u6765\u4e86\u3002\u4f60\u4e0d\u7528\u5bb3\u6015\uff0c\u4f60\u5e08\u7236\u7ed9\u4f60\u627e\u98df\u7269\u53bb\u4e86\u3002\u6211\u662f\u4f60\u5e08\u7236 \u7684\u670b\u53cb\u3002\u201c \u3000\u3000\u8c37\u4e2d\u83b2\u795e\u667a\u6e10\u6e10\u6062\u590d\uff0c\u89c1\u534e\u5929\u98ce\u76ee\u5149\u6148\u7965\u3001\u6ee1\u6000\u5173\u5207\u7684\u6a21\u6837\u770b\u7740\u5979\uff0c\u5fc3\u4e2d\u660e\u767d\u4e86\u51e0 \u5206\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u4e08\uff0c\u662f\u4f60\u5c06\u6211\u6551\u4e86\u7684\uff1f\u201d\u534e\u65e0\u98ce\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u4f60\u4e0d\u8ba4\u8bc6\u6211\uff0c\u4f46 \u6211\u4e00\u8bf4\u8d77\u6765\uff0c\u4f60\u5c31\u77e5\u9053\u4e0d\u662f\u5916\u4eba\u4e86\u3002\u6211\u662f\u6c5f\u6d77\u5929\u7684\u5e72\u7239\uff0c\u534e\u4e91\u78a7\u662f\u6211\u5973\u513f\uff0c\u4f60\u4e00\u5b9a\u542c\u6c5f \u6d77\u5929\u8bf4\u8fc7\u6211\u7684\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u4e00\u7247\u8ff7\u832b\uff0c\u5453\u8bed\u822c\u7684\u5583\u5583\u8bf4\u9053\uff1a\u201c\u6c5f\u6d77\u5929\u3001\u534e\u4e91\u78a7\u3001\u6c5f\u6d77\u5929\u3001\u534e\u4e91\u78a7\u2026\u2026\u201d \u534e\u5929\u98ce\u53ea\u5f53\u5979\u662f\u795e\u667a\u521d\u590d\uff0c\u8981\u7528\u5fc3\u56de\u5fc6\u5f80\u4e8b\uff0c\u4e5f\u5c31\u4e0d\u4ee5\u4e3a\u610f\uff0c\u5f53\u4e0b\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c \u4f60\u60f3\u8d77\u6765\u4e86\u3002\u55ef\uff0c\u8fd8\u6709\u4f60\u7684\u54e5\u54e5\u2026\u2026\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u7fdf\u7136\u4e00\u60ca\uff0c\u62ac\u8d77\u5934\u9053\uff1a\u201c\u6211\u54e5\u54e5\u53c8\u600e\u4e48\u5566\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u4f60\u54e5\u54e5\u548c\u6211\u8fd8\u6709 \u6d77\u5929\uff0c\u8981\u6253\u542c\u4f60\u7684\u6d88\u606f\u548c\u627e\u5bfb\u6211\u7684\u78a7\u513f\uff0c\u5509\u2014\u2014\u201d\u8c37\u4e2d\u83b2\u5b9a\u4e86\u5b9a\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u554a\uff0c\u5bf9\u4e86\uff0c \u6211\u60f3\u8d77\u6765\u4e86\uff0c\u542c\u8bf4\u78a7\u59d0\u59d0\u5931\u4e86\u8e2a\uff0c\u53ef\u6709\u5979\u7684\u6d88\u606f\u4e48\uff1f\u201d\u534e\u5929\u98ce\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u6211\u5df2\u7ecf\u89c1\u7740\u5979\u4e86\uff0c\u4f46\u53c8\u7ed9\u5979\u8d70\u4e86\uff0c\u81f3\u4eca\u8fd8\u672a\u77e5\u9053\u5979\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u6700\u6302\u5ff5\u7684\u4e00\u4ef6\u4e8b\u60c5\uff0c\u5c31\u662f\u8981\u77e5\u9053\u6c5f\u6d77\u5929\u548c\u534e\u4e91\u78a7\u7684\u7ed3\u679c\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u8fd9\u662f \u600e\u4e48\u56de\u4e8b\uff1f\u6d77\u5929\u4ed6\u53ef\u6709\u89c1\u7740\u78a7\u59d0\u4e48\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u4f60\u8eba\u4e0b\u6765\uff0c\u4e0d\u8981\u591a\u8bf4\u8bdd\uff0c\u8ba9\u6211\u6162\u6162 \u544a\u8bc9\u4f60\u3002\u201d\u8bf4\u5230\u4e86\u5728\u7075\u9e6b\u5cf0\u4e0a\u78b0\u5230\u534e\u4e91\u78a7\u7684\u4e00\u5e55\uff0c\u534e\u5929\u98ce\u957f\u957f\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a \u3000\u3000\u201c\u53ef\u601c\u6211\u7684\u78a7\u513f\uff0c\u5979\u68a6\u91cc\u9192\u91cc\uff0c\u90fd\u5fd8\u4e0d\u4e86\u5979\u7684\u6d77\u54e5\u3002\u90a3\u65f6\u5979\u6b63\u5728\u795e\u667a\u660f\u8ff7\u4e4b\u4e2d\uff0c\u5c31 \u50cf\u4f60\u521a\u624d\u4e00\u6837\uff0c\u5979\u89c1\u4e86\u6211\u4e5f\u6beb\u65e0\u77e5\u89c9\uff0c\u53ea\u662f\u8bb0\u5f97\u5979\u7684\u6d77\u54e5\uff0c\u53e3\u4e2d\u4e0d\u65ad\u53eb\u7740\u7684\uff0c\u5c31\u662f\u6c5f\u6d77 \u5929\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u6cea\u6c34\u76f4\u5728\u91cc\u54bd\uff0c\u5fc3\u5934\u4e00\u7247\u6df7\u4e71\uff0c\u53ea\u542c\u5f97\u534e\u5929\u98ce\u63a5\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u4e00\u751f\u6cbb\u8fc7\u65e0\u6570 \u602a\u75c5\uff0c\u5c31\u53ea\u5fc3\u75c5\u6700\u662f\u96be\u533b\u3002\u602a\u4e5f\u602a\u6211\uff0c\u6211\u65e9\u5df2\u77e5\u9053\u4ed6\u4eec\u4e8c\u4eba\u662f\u4e92\u76f8\u7231\u6155\u7684\u4e86\uff0c\u5374\u4e0d\u53ca\u65e9 \u628a\u4ed6\u4eec\u7684\u5a5a\u4e8b\u5b9a\u593a\u4e0b\u6765\u3002\u4ed6\u4eec\u4e24\u5c0f\u53e3\u5b50\u4e5f\u4e0d\u77e5\u95f9\u4ec0\u4e48\u522b\u626d\uff0c\u91d1\u9e70\u5b98\u4e4b\u4f1a\u8fc7\u540e\uff0c\u78a7\u51e0\u7adf\u4e0d \u4e0e\u4ed6\u540c\u8d70\uff0c\u4ee5\u81f4\u906d\u4e86\u8fd9\u573a\u707e\u96be\u3002\u5509\uff0c\u6211\u53ea\u6709\u8fd9\u4e00\u4e2a\u5973\u513f\uff0c\u4f46\u613f\u5979\u80fd\u5f97\u5e73\u5b89\uff0c\u89c1\u7740\u4e86\u6d77\u5929\u3002 \u5c31\u4f1a\u597d\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5fc3\u75db\u5982\u7ede\uff0c\u597d\u51e0\u6b21\u5c31\u8981\u51b2\u53e3\u800c\u51fa\uff0c\u201c\u4e0d\uff0c\u8fd9\u90fd\u5e94\u8be5\u602a\u6211\u3002\u201d\u4f46\u5374\u6ca1\u6709\u52c7\u6c14\u5728 \u534e\u5929\u98ce\u9762\u524d\u8bf4\u51fa\u6765\u3002\u534e\u5929\u98ce\u9053\uff1a\u201c\u6d77\u5929\u60f3\u6765\u65e9\u5df2\u5230\u4e86\u6606\u5e03\u5170\u7684\u4eac\u90fd\u4e86\uff0c\u4ed6\u53ef\u6709\u5077\u5165\u738b\u5b98 \u67e5\u8bbf\u4f60\u4e48\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5728\u8ff7\u8ff7\u7cca\u7cca\u4e2d\u53c8\u662f\u7fdf\u7136\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u534e\u5929\u98ce\u9053\uff1a\u201c\u6211\u662f\u95ee\u4f60\u53ef \u66fe\u89c1\u8fc7\u6d77\u5929\uff1f\u201d\u8c37\u4e2d\u83b2\u6da9\u58f0\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u3002 \u3000\u3000\u5401\uff0c\u6709\u2014\u2014\u6211\u6628\u665a\u4f3c\u4e4e\u542c\u5f97\u4ed6\u7684\u58f0\u97f3\u3002\u90a3\u65f6\u5e08\u7236\u6b63\u80cc\u770b\u6211\u8d70\u51fa\u6765\u3002\u201d \u3000\u3000\u534e\u5929\u98ce\u89c1\u5979\u9762\u8272\u7070\u81ea\uff0c\u6478\u4e86\u6478\u5979\u7684\u989d\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u70ed\u5df2\u9000\u4e86\u8bb8\u591a\u4e86\uff0c\u5374\u600e\u7684\u949f \u60c5\u5982\u6b64\u59d4\u987f\u3002\u201d\u53ea\u9053\u5979\u662f\u4e3a\u81ea\u5df1\u5973\u513f\u7684\u4e8b\u60c5\u62c5\u5fe7\uff0c\u8c37\u4e2d\u83b2\u5374\u53cd\u800c\u5148\u5b89\u6170\u4ed6\u9053\uff1a\u201c\u534e\u8001\u7237 \u5b50\u4f60\u653e\u5fc3\uff0c\u4ed6\u4eec\u4e24\u4eba\u8fdf\u65e9\u603b\u4f1a\u89c1\u7740\u7684\uff0c\u6d77\u5929\uff0c\u4ed6\uff0c\u4ed6\u662f\u4e2a\u6781\u91cd\u60c5\u4e49\u7684\u4eba\uff0c\u4ed6\u4e00\u5b9a\u4e0d\u4f1a\u8f9c \u8d1f\u78a7\u59d0\u59d0\u5bf9\u4ed6\u7684\u60c5\u610f\u7684\uff0c\u55ef\uff0c\u6211\u5fd8\u4e86\u95ee\u4f60\uff0c\u4f60\u8bf4\u548c\u6211\u7684\u54e5\u54e5\u540c\u6765\uff0c\u4ed6\u53c8\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u534e\u5929\u98ce\u6015\u5979\u75c5\u4e2d\u53d7\u60ca\u3002\u4e0d\u6562\u628a\u5510\u52aa\u73e0\u7a46\u5760\u5165\u51b0\u6cb3\u4e4b\u4e8b\u544a\u8bc9\u5979\uff0c\u542b\u7cca\u8bf4\u9053\uff1a\u201c\u6211\u5728\u7075\u9e6b\u5cf0 \u4e0b\u4e0e\u4ed6\u5206\u624b\uff0c\u60f3\u5fc5\u4ed6\u4e5f\u5df2\u7ecf\u5230\u4e86\u6606\u5e03\u5170\u56fd\u7684\u4eac\u90fd\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u54e5\u54e5\u6765\u4e86\uff0c\u90a3\u5c31\u597d\u4e86\u3002\u201d\u5fc3\u4e2d\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u60b2\u4f24\uff0c\u6697\u81ea\u60f3 \u9053\uff1a\u201c\u6d77\u5929\u548c\u6211\u54e5\u54e5\u90fd\u5df2\u6765\u4e86\uff0c\u4ee5\u4ed6\u4eec\u4e8c\u4eba\u4e4b\u529b\uff0c\u5b9a\u80fd\u5236\u4f0f\u90a3\u4e2a\u5996\u5a46\uff0c\u6606\u5e03\u5170\u56fd\u7684\u56fd\u738b \u548c\u4e3b\u6218\u7684\u6cf0\u6e05\u738b\u53c8\u5df2\u540c\u5f52\u4e8e\u5c3d\uff0c\u6709\u592a\u4e0a\u7687\u4f5c\u4e3b\uff0c\u4e24\u56fd\u7684\u7ea0\u7eb7\u4e5f\u5fc5\u7136\u80fd\u5f97\u5230\u89e3\u51b3\uff0c\u6211\u7684\u62c5 \u5b50\u53ef\u4ee5\u653e\u4e0b\u6765\u4e86\u3002\u201d \u3000\u3000\u4f46\u9762\u5bf9\u7740\u534e\u5929\u98ce\u6194\u60b4\u7684\u989c\u5bb9\u3001\u4f18\u90c1\u7684\u76ee\u5149\uff0c\u53c8\u4e0d\u7981\u6697\u6674\u60b2\u4f24\uff1a\u201c\u5509\uff0c\u6d77\u54e5\u4f60\u4e3a\u6211\u800c \u6765\uff0c\u4f46\u6211\u53c8\u5c82\u80fd\u53ea\u987e\u81ea\u5df1\u7684\u5e78\u798f\uff0c\u5fcd\u5fc3\u8ba9\u4ed6\u4eec\u7236\u5973\u540c\u53d7\u60b2\u82e6\uff1f\u4f60\u7684\u5e72\u7239\u66fe\u6551\u8fc7\u4f60\u7684\u6027\u547d\uff0c \u73b0\u5728\u53c8\u6559\u4e86\u6211\u7684\u6027\u547d\uff0c\u6211\u9664\u4e86\u6210\u5168\u4ed6\u7684\u5fc3\u613f\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef\u4ee5\u62a5\u7b54\u4e8e\u4ed6\uff1f\u201d \u3000\u3000\u534e\u5929\u98ce\u89c1\u5979\u9762\u8272\u82cd\u767d\uff0c\u795e\u6001\u75b2\u5026\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u521a\u521a\u9000\u4e86\u70e7\uff0c\u4e0d\u8981\u8bf4\u8bdd\u592a\u591a\u4e86\u3002 \u597d\u597d\u7684\u518d\u7761\u4e00\u89c9\u5427\u3002\u201d\u8f7b\u8f7b\u70b9\u4e86\u8c37\u4e2d\u83b2\u7684\u660f\u7761\u7a74\uff0c\u4e00\u822c\u7684\u70b9\u7a74\u5bf9\u8eab\u4f53\u6709\u5bb3\uff0c\u4f46\u8fd9\u662f\u534e\u5929 \u98ce\u72ec\u5bb6\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u4ee5\u70b9\u7a74\u4e0e\u533b\u7597\u7ed3\u5408\uff0c\u53ef\u4ee4\u75c5\u4eba\u5f97\u5230\u5145\u5206\u7684\u4f11\u606f\uff0c\u975e\u4f46\u65e0\u5bb3\uff0c\u4e14\u6709\u52a9 \u4e8e\u5065\u5eb7\u7684\u4f55\u590d\u3002 \u3000\u3000\u8fc7\u4e86\u4e0d\u4e45\uff0c\u8c37\u4e4b\u534e\u5df2\u730e\u4e86\u4e24\u53ea\u96ea\u9e21\u56de\u6765\uff0c\u8fd8\u6458\u4e86\u8bb8\u591a\u96ea\u6843\uff0c\u4e00\u89c1\u8c37\u4e2d\u83b2\u5c1a\u5728\u6c89\u7761\u7684 \u72b6\u6001\u4e2d\uff0c\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\u3002\u95ee\u9053\uff0c\u201c\u600e\u4e48\uff0c\u5979\u8fd8\u672a\u9192\u6765\uff1f\u201d\u534e\u5927\u98ce\u7b11\u9053\uff1a\u201c\u5979\u7684\u70ed\u6bd2\u90fd\u5df2 \u6563\u53d1\u51c0\u5c3d\u4e86\uff0c\u521a\u624d\u5df2\u7ecf\u9192\u6765\uff0c\u662f\u6211\u6015\u5979\u52b3\u7d2f\uff0c\u8981\u5979\u518d\u7761\u4e00\u4f1a\u7684\u3002\u201d\u5f53\u4e0b\u5e2e\u5fd9\u8c37\u4e4b\u534e\u751f\u706b \u70e4\u90a3\u4e24\u53ea\u96ea\u9e21\uff0c\u8fb9\u5403\u8fb9\u8bf4\u9053\uff0c\u201c\u70ed\u6bd2\u6563\u53d1\u4e4b\u540e\uff0c\u53ef\u4ee5\u65e0\u9700\u5403\u836f\uff0c\u5c31\u53ea\u8981\u9759\u517b\u4e86\u3002\u5927\u7ea6\u9759 \u517b\u4e09\u5929\uff0c\u4fbf\u53ef\u6062\u590d\u5982\u521d\u3002\u6211\u628a\u62a4\u7406\u75c5\u4eba\u5e94\u6ce8\u610f\u7684\u4e8b\u9879\u544a\u8bc9\u4f60\uff0c\u4f60\u591a\u591a\u7167\u6599\u5979\u5427\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u5f97\u534e\u8001\u524d\u8f88\u6551\u4e86\u5979\u7684\u6027\u547d\uff0c\u6211\u5df2\u662f\u611f\u6fc0\u4e0d\u5c3d\uff0c\u7167\u6599\u75c5\u4eba\u4e4b\u4e8b\uff0c\u600e\u6562\u518d \u52b3\u70e6\u4f60\u8001\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u534e\u706d\u98ce\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u672c\u6765\u5e94\u8be5\u966a\u4f34\u4f60\u7684\uff0c\u4f46\u6211\u7684\u5973\u513f\u8fd8\u672a\u77e5\u9053\u4e0b\u843d\uff0c\u6211\u8981\u53bb\u5bfb\u8bbf \u5979\u3002\u201d\u8c37\u4e4b\u534e\u4e5f\u77e5\u9053\u4e00\u4e9b\u534e\u4e91\u78a7\u548c\u6c5f\u6d77\u5929\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u4f46\u8fd9\u65f6\u5374\u662f\u4e0d\u4fbf\u63d0\u8d77\u3002\u534e\u5929\u98ce\u884c \u8272\u5306\u5306\uff0c\u53ea\u80fd\u5c3d\u91cf\u7b80\u5355\u660e\u4e86\u7684\u6559\u5979\u5982\u4f55\u62a4\u7406\u75c5\u4eba\uff0c\u5176\u4ed6\u7684\u4e5f\u6765\u4e0d\u53ca\u6d0b\u8c08\u4e86\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u4e00\u89c9\u9192\u6765\uff0c\u5df2\u662f\u9ec4\u660f\u65f6\u5206\uff0c\u4e00\u9192\u6765\u5c31\u6293\u7740\u8c37\u4e4b\u534e\u7684\u8863\u8896\u9053\uff1a\u201c\u534e\u8001\u524d\u8f88\uff0c\u4f60 \u522b\u62c5\u5fe7\u2026\u2026\u554a\uff0c\u5e08\u7236\u662f\u4f60\u56de\u6765\u4e86\uff1f\u201d\u8c37\u4e4b\u534e\u9053\uff0c\u201c\u534e\u8001\u524d\u8f88\u5df2\u7ecf\u8d70\u4e86\u3002\u4f60\u997f\u4e86\u5427\uff1f\u6211\u7ed9 \u4f60\u6458\u6765\u4e86\u96ea\u6843\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u534e\u8001\u524d\u8f88\u5df2\u7ecf\u8d70\u4e86\uff1f\u5509\uff0c\u6211\u2026\u2026\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4f60\u6709\u4ec0 \u4e48\u8bdd\u8981\u548c\u4ed6\u8bf4\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u6ca1\u6709\u4ec0\u4e48\u4e86\u3002\u55ef\uff0c\u6211\u4e0d\u60f3\u5403\u4e1c\u897f\u3002\u201d \u3000\u3000\u8fd9\u4e00\u665a\uff0c\u8c37\u4e2d\u83b2\u5ffd\u5730\u4e49\u53d1\u9ad8\u70e7\uff0c\u4e0d\u65ad\u7684\u5728\u5453\u8bed\uff0c\u4e00\u4f1a\u513f\u53eb\u7740\u6c5f\u6d77\u5929\u7684\u540d\u5b57\uff0c\u4e00\u4f1a\u513f \u53eb\u7740\u534e\u4e91\u78a7\u7684\u540d\u5b57\uff01\u8c37\u4e4b\u534e\u5927\u4e3a\u7740\u6025\uff0c\u5fc3\u9053\uff1a\u201c\u534e\u5929\u98ce\u8bf4\u5979\u5df1\u597d\u4e86\u7684\uff0c\u600e\u7684\u5374\u4e0d\u7075\u9a8c\uff1f\u201d \u5e78\u597d\u5230\u4e94\u66f4\u65f6\u5206\uff0c\u8c37\u4e2d\u83b2\u53c8\u5b89\u5b89\u9759\u9759\u7684\u719f\u7761\u8fc7\u53bb\uff0c\u7b2c\u4e8c\u592d\u4e00\u65e9\u9192\u6765\uff0c\u7adf\u662f\u795e\u6e05\u6c14\u723d\uff0c\u5224 \u82e5\u4e24\u4eba\u3002 \u3000\u3000\u8fd9\u573a\u9ad8\u70e7\u6765\u5f97\u5feb\u53bb\u5f97\u4e5f\u5feb\uff0c\u8c37\u4e4b\u534e\u867d\u662f\u4e0d\u660e\u533b\u7406\u3002\u5fc3\u4e2d\u4e5f\u81ea\u6697\u6697\u7eb3\u7f55\uff0c\u201c\u4f55\u4ee5\u5979\u5728 \u70ed\u6bd2\u6563\u53d1\u4e4b\u540e\uff0c\u8fd8\u6709\u8fd9\u573a\u9ad8\u70e7\uff0c\u83ab\u975e\u8fd9\u662f\u4e00\u573a\u5fc3\u75c5\uff1f\u201d\u4e8b\u5b9e\u786e\u662f\u5982\u6b64\uff0c\u8c37\u4e2d\u83b2\u8fd9\u573a\u9ad8\u70e7 \u662f\u56e0\u523a\u6fc0\u800c\u8d77\uff0c\u5f85\u5230\u5fc3\u4e2d\u6709\u4e86\u4e3b\u610f\uff0c\u91cd\u65b0\u83b7\u5f97\u5b89\u5b81\uff0c\u75c5\u533f\u4e5f\u5c31\u8fdc\u4f1a\u4e86\u3002 \u3000\u3000\u4f9d\u8c37\u4e2d\u83b2\u7684\u610f\u601d\uff0c\u5f53\u65e5\u4fbf\u8981\u4e0b\u5c71\uff0c\u8c37\u4e4b\u534e\u9075\u4ece\u534e\u5929\u98ce\u7684\u5429\u5490\uff0c\u4e00\u5b9a\u8981\u5979\u517b\u597d\u8eab\u5b50\uff0c \u624d\u8bb8\u4e0b\u5c71\u3002\u8c37\u4e2d\u83b2\u529f\u529b\u6df1\u539a\uff0c\u6bd2\u6563\u4e4b\u540e\uff0c\u6062\u590d\u6781\u5feb\uff0c\u534e\u5929\u98ce\u539f\u6765\u4f30\u8ba1\u5979\u8981\u9759\u517b\u4e09\u5929\u7684\uff0c \u7ed3\u679c\u53ea\u5230\u4e86\u7b2c\u4e8c\u5929\uff0c\u5979\u7684\u529f\u529b\u5df2\u6062\u590d\u4e03\u516b\u6210\u4e86\u3002 \u3000\u3000\u7b2c\u4e09\u95e9\u4e00\u65e9\uff0c\u8c37\u4e4b\u534e\u89c1\u5979\u5df2\u662f\u6beb\u65e0\u75c5\u5bb9\uff0c\u4fbf\u4e5f\u653e\u5fc3\u5e26\u5979\u4e0b\u5c71\u3002\u5929\u6c14\u5f88\u597d\uff0c\u6ee1\u5c71\u4ea4\u9519 \u7684\u51b0\u6cb3\uff0c\u5728\u9633\u5149\u4e0b\u6cdb\u8d77\u6ee1\u5929\u971e\u5f69\u3002\u5e08\u5f92\u4fe9\u8e0f\u7740\u671d\u9633\uff0c\u8fce\u7740\u6653\u98ce\uff0c\u7cbe\u795e\u4e3a\u4e4b\u4e00\u723d\u3002\u8d70\u5230\u534a \u5c71\uff0c\u5ffd\u542c\u5f97\u7b11\u8bed\u55a7\u55a7\uff0c\u6b4c\u58f0\u9635\u9635\uff0c\u6709\u4e00\u7fa4\u9752\u5e74\u7537\u5973\uff0c\u8bc1\u81ea\u8f7d\u6b4c\u8f7d\u821e\u800c\u6765\u3002\u8c37\u4e4b\u534e\u9053\uff1a \u201c\u54a6\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8fd9\u6837\u9ad8\u5174\uff1f\u8fd9\u6b4c\u58f0\u597d\u542c\u6781\u4e86\uff01\u201d \u3000\u3000\u9a6c\u8428\u513f\u56fd\u4e0e\u6606\u5e03\u5170\u56fd\u662f\u540c\u7529\u4e00\u79cd\u65b9\u8a00\uff0c\u8c37\u4e2d\u83b2\u5f52\u56fd\u5df2\u534a\u5e74\u6709\u591a\uff0c\u8fd9\u7fa4\u9752\u5e74\u7537\u5973\u5531\u7684 \u8fd9\u4e00\u652f\u6b4c\uff0c\u7528\u7684\u90fd\u662f\u666e\u901a\u5b81\u773c\uff0c\u5e76\u4e0d\u96be\u61c2\uff0c\u53ea\u542c\u5f97\u4ed6\u4eec\u5531\u7684\u662f\uff1a \u3000\u3000\u201c\u70fd\u70df\u6563\u5c3d\u3001\u51b0\u6cb3\u5982\u955c\uff0c \u3000\u3000\u6211\u8981\u5728\u51b0\u6cb3\u6d17\u51c0\u6211\u5b9d\u5251\u7684\u8840\u8165\uff0c \u3000\u3000\u4ece\u4eca\u540e\u6c38\u4eab\u592a\u5e73\u3002 \u3000\u3000\u5e74\u8f7b\u4eba\u5f97\u5230\u7231\u60c5\uff0c \u3000\u3000\u8001\u5e74\u4eba\u5f97\u5230\u5b89\u5b81\u3002 \u3000\u3000\u518d\u6ca1\u6709\u9065\u76fc\u5f81\u4eba\u7684\u6028\u5987\uff0c \u3000\u3000\u518d\u6ca1\u6709\u501a\u95fe\u5f85\u5b50\u7684\u6bcd\u4eb2\u3002 \u3000\u3000\u54bf\u5440\uff01\u70fd\u70df\u6563\u5c3d\uff0c\u51b0\u6cb3\u5982\u955c\u3002 \u3000\u3000\u6211\u8981\u5728\u51b0\u6cb3\u6d17\u51c0\u6211\u5b9d\u5251\u7684\u8840\u8165\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u542c\u4e86\u5927\u559c\uff0c\u4e0a\u524d\u95ee\u9053\uff1a\u201c\u600e\u4e48\u4f60\u4eec\u8fd9\u6837\u9ad8\u5174\uff0c\u662f\u4e0d\u7528\u6253\u4ed7\u4e86\u5417\uff1f\u201d\u90a3\u7fa4\u9752\u5e74 \u9053\uff1a\u201c\u54a6\uff0c\u4f60\u600e\u4e48\u8fd8\u4e0d\u77e5\u9053\uff1b\u8fd9\u662f\u4e24\u4ef6\u5929\u5927\u7684\u559c\u4e8b\u5440\uff0c\u5168\u56fd\u767e\u59d3\uff0c\u7537\u5973\u8001\u5e7c\uff0c\u8c01\u4e0d\u6b22\u6b23\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u4eec\u6bcd\u5973\u662f\u56db\u5929\u524d\u4e0a\u5c71\u91c7\u836f\u7684\uff0c\u4eca\u65e5\u65b9\u624d\u4e0b\u5c71\uff0c\u90a3\u4e24\u4ef6\u559c\u4e8b\u662f\u4ec0\u4e48\u5440\uff1f\u201d \u90a3\u7fa4\u9752\u5e74\u9053\uff1a\u201c\u5934\u4e00\u4ef6\u559c\u4e8b\uff0c\u662f\u5973\u738b\u4eca\u65e5\u5373\u4f4d\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u54e6\uff0c\u5973\u738b\u5373\u4f4d\uff1f\u662f\u7f57\u66fc \u5a1c\u516c\u4e3b\u5417\uff1f\u201d\u90a3\u7fa4\u9752\u5e74\u9053\uff0c\u201c\u4e0d\u9519\uff0c\u542c\u8bf4\u56fd\u738b\u66b4\u6bd9\uff0c\u73b0\u5728\u7531\u592a\u4e0a\u7687\u4f5c\u4e3b\uff0c\u5411\u81e3\u6c11\u5ba3\u544a\uff0c \u7acb\u516c\u4e3b\u4f5c\u4e3a\u5973\u738b\uff0c\u516c\u4e3b\u5bf9\u767e\u59d3\u4e00\u5411\u4ec1\u6148\uff0c\u6bd4\u5979\u54e5\u54e5\u597d\u5f97\u591a\u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u7b2c\u4e8c\u4ef6\u559c \u4e8b\u53c8\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u7fa4\u9752\u5e74\u9053\uff1a\u201c\u4f60\u4e0d\u542c\u89c1\u6211\u4eec\u5531\u7684\u6b4c\u5417\uff1f\u6b63\u662f\u4ece\u4eca\u4e4b\u540e\u4e0d\u7528\u6253\u4ed7\u4e86\u3002\u9a6c\u8428\u513f\u56fd\u7684\u56fd \u738b\u4eb2\u81ea\u5230\u4e86\u8fd9\u513f\uff0c\u548c\u5973\u738b\u8ba2\u4e86\u76df\u7ea6\u5462\uff01\u201d\u8c37\u4e2d\u83b2\u5927\u559c\u9053\uff1a\u201c\u9a6c\u8428\u513f\u56fd\u7684\u56fd\u738b\u6765\u4e86\uff1f\u4f60\u4eec \u600e\u4e48\u77e5\u9053\uff1f\u201d\u90a3\u7fa4\u9752\u5e74\u7b11\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u4f46\u77e5\u9053\uff0c\u8fd8\u89c1\u7740\u4e86\u4ed6\u5462\u3002\u4ed6\u548c\u5973\u5de5\u6628\u65e5\u5728\u671d\u5929\u95e8 \u516c\u5f00\u9732\u9762\uff0c\u5ba3\u5e03\u8fd9\u4e2a\u76df\u7ea6\uff0c\u8ba9\u767e\u59d3\u77bb\u4ef0\uff01\u201d \u3000\u3000\u4e24\u9897\u6676\u83b9\u7684\u6cea\u73e0\u6302\u5728\u773c\u8fb9\uff0c\u8c37\u4e2d\u83b2\u560e\u54bd\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u6253\u4ed7\u4e86\uff0c\u8fd9\u5c31\u597d\u5566\uff0c\u8fd9\u5c31\u597d\u5566\u3002 \u96be\u602a\u4f60\u4eec\u8fd9\u6837\u9ad8\u5174\u4e86\uff01\u201d\u4e00\u4e2a\u7a1a\u6c14\u672a\u6d88\u7684\u9752\u5e74\u8be7\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u600e\u4e48\u54ed\u8d77\u6765\u4e86\uff1f\u4e0d\u7528\u6253\u4ed7\uff0c \u5f53\u7136\u4eba\u4eba\u9ad8\u5174\uff0c\u5206\u4ec0\u4e48\u4f60\u4eec\u6211\u4eec\uff0c\u96be\u9053\u4f60\u4e0d\u9ad8\u5174\u5417\uff1f\u201d\u4ed6\u65c1\u8fb9\u4e00\u4e2a\u5c11\u5973\u7b11\u9053\uff1a\u201c\u4e00\u4e2a\u4eba \u6b63\u662f\u56e0\u4e3a\u592a\u9ad8\u5174\u4e86\uff0c\u624d\u4f1a\u54ed\u7684\uff0c\u6211\u731c\u8fd9\u4f4d\u59d0\u59d0\u7684\u60c5\u90ce\u5927\u7ea6\u662f\u88ab\u62bd\u4e86\u53bb\u5f53\u5175\u7684\u5462\uff1f\u201d\u8c37\u4e2d \u83b2\u62b9\u4e86\u773c\u6cea\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4ece\u4eca\u4e4b\u540e\uff0c\u54b1\u4eec\u90fd\u662f\u4e00\u5bb6\u4eba\u4e86\u3002\u51b0\u6cb3\u6d17\u5251\uff0c\u6c38\u4eab\u5b89\u5b81\uff0c\u6211 \u600e\u80fd\u4e0d\u9ad8\u5174\u5462\uff1f\u6211\u662f\u592a\u9ad8\u5174\u4e86\uff01\u201d \u3000\u3000\u8fd9\u4e9b\u4eba\u600e\u77e5\u5979\u662f\u9a6c\u8428\u513f\u56fd\u516c\u4e3b\u7684\u8eab\u4efd\uff0c\u5f53\u7136\u4e5f\u542c\u4e0d\u61c2\u5979\u6240\u8bf4\u7684\u201c\u4e00\u5bb6\u4eba\u201d\u7684\u610f\u601d\uff0c \u53ea\u9053\u5979\u6b22\u559c\u5f97\u75af\u4e86\uff0c\u662f\u4ee5\u609f\u65e0\u4f26\u6b21\u3002\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u57ce\u91cc\u8fd8\u8981\u70ed\u95f9\u5462\uff0c\u4e2d\u5348\u5973\u738b\u8981\u7ed9\u9a6c \u8428\u513f\u56fd\u56fd\u738b\u9001\u884c\uff0c\u6761\u6761\u8857\u9053\u642d\u8d77\u724c\u697c\uff0c\u5904\u5904\u6709\u6b4c\u6709\u821e\u3002\u4f60\u5feb\u5feb\u8fdb\u57ce\uff0c\u627e\u7740\u4f60\u7684\u60c5\u90ce\uff0c\u770b \u70ed\u95f9\u53bb\u5427\uff01\u201d \u3000\u3000\u8fd9\u7fa4\u9752\u5e74\u7537\u5973\u7b11\u7740\u56b7\u7740\uff0c\u5531\u7740\u6b4c\u513f\u8d70\u8fc7\u53bb\u4e86\u3002\u8c37\u4e2d\u83b2\u5ffd\u9053\uff1a \u3000\u3000\u201c\u5e08\u7236\uff0c\u4f60\u4e0d\u662f\u8981\u8d76\u56de\u9099\u5c71\u7684\u4e48\uff1f\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6b64\u95f4\u8bf8\u4e8b \u5df2\u4e86\uff0c\u6211\u53ef\u4ee5\u548c\u4f60\u4e00\u540c\u8d70\u4e86\u3002\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4f60\u4e0d\u518d\u8fdb\u5bab\u4e86\u4e48\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u5df2\u77e5\u9053\u592a\u4e0a\u7687\u5b89\u7136\u65e0\u6059\uff0c\u6211\u4e5f\u53ef\u4ee5\u653e\u5fc3\u4e86\u3002\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u90a3\u4e48\uff0c \u4f60\u4e5f\u4e0d\u60f3\u89c1\u89c1\u4f60\u7684\u54e5\u54e5\u548c\u6c5f\u6d77\u5929\u4e48\uff1f\u201d\u8c37\u4e2d\u83b2\u5fcd\u7740\u5fc3\u4e2d\u7684\u9178\u695a\uff0c\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u54e5\u54e5\u5728 \u6606\u5e03\u5170\u56fd\u4eac\u57ce\u516c\u5f00\u9732\u9762\uff0c\u534e\u8001\u524d\u8f88\u6b64\u65f6\u60f3\u5fc5\u4e5f\u65e9\u5df2\u89c1\u7740\u4ed6\u4e86\u3002\u6211\u7684\u5e73\u5b89\u8baf\u606f\uff0c\u534e\u8005\u524d\u8f88 \u5f53\u7136\u4f1a\u5e26\u7ed9\u6211\u7684\u54e5\u54e5\uff0c\u54b1\u4eec\u65e2\u5fd9\u7740\u8981\u8d76\u56de\u9099\u5c71\uff0c\u6211\u4f3c\u4e4e\u4e5f\u4e0d\u5fc5\u591a\u8d70\u8fd9\u4e00\u8d77\u4e86\u3002\u81f3\u4e8e\u6d77\u5929\uff0c \u6b64\u65f6\u8fd8\u662f\u4e0d\u89c1\u7684\u597d\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u662f\u8fc7\u6765\u4eba\uff0c\u65e0\u987b\u518d\u95ee\uff0c\u5df2\u77e5\u8c37\u4e2d\u83b2\u5fc3\u610f\uff0c\u8f7b\u8f7b\u53f9\u606f\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u6d77\u5929\u82e5 \u662f\u5bf9\u4f60\u771f\u60c5\uff0c\u4ed6\u81ea\u4f1a\u56de\u5230\u4f60\u7684\u8eab\u8fb9\u3002\u201d\u8a00\u4e0b\u795e\u8272\u9eef\u7136\uff0c\u539f\u6765\u5979\u4e0d\u4f46\u662f\u4e3a\u5f92\u5f1f\u53f9\u606f\uff0c\u4e5f\u4e3a \u81ea\u5df1\u4f24\u5fc3\u3002\u5979\u8fd9\u6b21\u6401\u4e0b\u9099\u5c71\u7684\u4e8b\u60c5\uff0c\u6765\u5230\u6606\u5e03\u5170\u56fd\uff0c\u56fa\u7136\u662f\u4e3a\u4e86\u4fdd\u62a4\u5f92\u5f1f\uff0c\u4f46\u53e6\u5916\u4e00\u4e2a \u5fc3\u613f\uff0c\u4e5f\u662f\u5e0c\u671b\u5728\u6606\u5e03\u5170\u56fd\u80fd\u89c1\u770b\u91d1\u4e16\u9057\u3002\u53ef\u662f\u7adf\u7136\u6ca1\u6709\u89c1\u7740\uff01 \u3000\u3000\u5979\u8bb0\u5f97\u5728\u91d1\u9e70\u5bab\u5927\u4f1a\u4e4b\u540e\uff0c\u91d1\u4e16\u9057\u548c\u5979\u5206\u624b\u4e4b\u65f6\uff0c\u66fe\u9690\u9690\u7ea6\u7ea6\u6709\u6240\u6697\u793a\uff0c\u8981\u5f85\u4e00\u4ef6 \u5fc3\u4e8b\u4e86\u7ed3\u4e4b\u540e\uff0c\u4ed6\u5c31\u4f1a\u56de\u5230\u5979\u7684\u8eab\u8fb9\u3002 \u3000\u3000\u5979\u77e5\u9053\u8fd9\u5fc5\u662f\u4e0e\u5389\u80dc\u7537\u6709\u5173\u7684\u5fc3\u4e8b\uff0c\u5979\u4e5f\u77e5\u9053\u5389\u590d\u751f\u548c\u5929\u9b54\u6559\u4e3b\u5df2\u5728\u6606\u5e03\u5170\u56fd\u51fa\u73b0\uff0c \u6599\u60f3\u91d1\u4e16\u9057\u5341\u4e5d\u662f\u53bb\u8ffd\u8e2a\u5389\u590d\u751f\u53bb\u4e86\uff0c\u53ef\u662f\u5982\u4eca\u5df2\u8bc1\u5b9e\u4e86\u5389\u590d\u751f\u5728\u6606\u5e03\u5170\u56fd\u3002\u4f46\u5979\u5374\u59cb \u7ec8\u6ca1\u6709\u89c1\u7740\u91d1\u4e16\u9057\uff01 \u3000\u3000\u8c37\u4e4b\u534e\u5fc3\u5934\u6005\u6005\uff0c\u6697\u81ea\u601d\u91cf\uff1a\u201c\u96be\u9053\u662f\u4e16\u9057\u6839\u672c\u6ca1\u6765\uff0c\u6291\u6216\u662f\u4ed6\u628a\u6211\u5fd8\u4e86\uff1f\u201d\u5ffd\u5730 \u60f3\u8d77\u81ea\u5df1\u4ece\u524d\u9001\u7ed9\u91d1\u4e16\u9057\u7684\u4e24\u53e5\u8bd7\u201c\u7eb5\u4f7f\u6d6e\u4e91\u80fd\u853d\u65e5\uff0c\u9634\u973e\u4ea6\u4ec5\u662f\u987b\u81fe\u3002\u201d\u4e0d\u7981\u54d1\u7136\u5931 \u7b11\uff0c\u201c\u6211\u77e5\u9053\u529d\u4ed6\uff0c\u600e\u7684\u81ea\u5df1\u53cd\u800c\u6267\u8457\u4e86\uff1f\u7537\u5973\u4e4b\u95f4\uff0c\u96be\u9053\u53ea\u6709\u592b\u59bb\u4e4b\u7231\u4e48\uff1f\u6211\u4e0e\u4ed6\u4e8c \u5341\u8f7d\u4ea4\u60c5\uff0c\u5fc3\u5fc3\u76f8\u5370\uff0c\u6211\u5fd8\u4e0d\u4e86\u4ed6\uff0c\u4ed6\u5f53\u7136\u4e5f\u5fd8\u4e0d\u4e86\u6211\u3002\u8fd9\u4e00\u4efd\u77e5\u5df1\u7684\u611f\u60c5\uff0c\u96be\u9053\u8fd8\u4e0d \u8db3\u73cd\u8d35\uff1f\u201d\u4f46\u8bdd\u867d\u5982\u6b64\uff0c\u8fd9\u201c\u4e8c\u5341\u8f7d\u4ea4\u60c5\u201d\u6bd5\u7adf\u4e5f\u4f34\u540c\u7740\u201c\u4e8c\u5341\u8f7d\u76f8\u601d\u201d\uff0c\u8fd9\u201c\u65e0\u4e86\u671f\u201d \u7684\u76f8\u601d\uff0c\u521a\u521a\u6709\u4e86\u8f6c\u673a\u7684\u5f81\u5146\uff0c\u5230\u5934\u6765\u5374\u53c8\u662f\u4e00\u573a\u5931\u671b\uff0c\u5373\u4f7f\u8c37\u4e4b\u534e\u5982\u4f55\u8c41\u8fbe\uff0c\u5fc3\u4e2d\u4e5f \u96be\u514d\u6709\u4e00\u70b9\u4e91\u7ff3\u4e86\u3002 \u3000\u3000\u90a3\u7fa4\u9752\u5e74\u7684\u6b4c\u58f0\u7b11\u8bed\u72b9\u81ea\u8fdc\u8fdc\u4f20\u672a\uff1a\u201c\u70fd\u70df\u6563\u5c3d\uff0c\u51b0\u6cb3\u5982\u955c\u3002 \u3000\u3000\u6211\u8981\u5728\u51b0\u6cb3\u6d17\u51c0\u6211\u5b9d\u5251\u7684\u8840\u8165\uff0c\u4ece\u4eca\u540e\u6c38\u4ead\u592a\u5e73\u3002\u2026\u2026\u201d\u8c37\u4e4b\u534e\u5ffd\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u662f \u4ec0\u4e48\u58f0\u97f3\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4e0d\u662f\u4ed6\u4eec\u5728\u5531\u521a\u624d\u90a3\u652f\u6b4c\u5417\uff1f\u201d\u8c37\u4e4b\u534e\u9053\u201c\u6b4c\u58f0\u4e2d\u597d\u4f3c\u8fd8\u6742\u6709\u522b\u7684\u58f0 \u97f3\uff0c\u597d\u50cf\uff0c\u597d\u50cf\u662f\u6709\u4eba\u53eb\u4f60\uff1f\u201d\u8c37\u4e2d\u83b2\u7b11\u9053\uff0c\u201c\u4e0d\u4f1a\u7684\uff0c\u5e08\u7236\u4e00\u5b9a\u662f\u4f60\u542c\u9519\u4e86\u3002\u8d81\u7740\u6e05 \u6668\u51c9\u723d\uff0c\u5feb\u8d76\u8def\u5427\u3002\u201d\u8c37\u4e4b\u534e\u6df1\u60c5\u5730\u671b\u4e86\u5979\u5f92\u5f1f\u4e00\u773c\uff0c\u5fc3\u91cc\u6697\u6697\u53f9\u6c14\uff0c\u5374\u4e0d\u518d\u8a00\u8bed\uff0c\u4fbf \u548c\u5979\u8d70\u4e86\u3002 \u3000\u3000\u5176\u5b9e\u8c37\u4e2d\u83b2\u6bd4\u5979\u5e08\u7236\u8fd8\u8981\u542c\u5f97\u6e05\u695a\u3002\u8fd9\u662f\u6c5f\u6d77\u5929\u5728\u7528\u201c\u5927\u9041\u4f20\u97f3\u201c\u547c\u5524\u5979\u3002\u539f\u6765\u6c5f \u6d77\u5929\u548c\u534e\u5929\u98ce\u6b64\u65f6\u6b63\u5728\u5979\u4eec\u521a\u521a\u79bb\u5f00\u7684\u90a3\u4e2a\u77f3\u7a9f\uff0c\u8c37\u4e2d\u83b2\u4e5f\u60f3\u5230\u4e86\u4e00\u5b9a\u662f\u534e\u5929\u98ce\u5e26\u4ed6\u6765 \u627e\u5bfb\u81ea\u5df1\u7684\uff0c\u5979\u4e3a\u4e86\u6210\u5168\u534e\u5929\u98ce\u7236\u5973\u7684\u5fc3\u613f\uff0c\u6545\u4f5c\u542c\u800c\u4e0d\u95fb\u3002 \u3000\u3000\u4eac\u57ce\u5c31\u5efa\u5728\u5c71\u811a\uff0c\u5979\u4eec\u4e0b\u5c71\u4e4b\u540e\uff0c\u907f\u514d\u8fdb\u57ce\uff0c\u7ed5\u8fc7\u4e1c\u95e8\uff0c\u4e0b\u6599\u5973\u738b\u7684\u8f66\u9a7e\u6b63\u4ece\u4e1c\u95e8 \u51fa\u6765\uff0c\u539f\u6765\u662f\u9001\u5510\u52aa\u73e0\u7a46\u56de\u56fd\u3002\u597d\u5728\u6ee1\u57ce\u767e\u59d3\uff0c\u90fd\u6765\u9001\u884c\uff0c\u8c37\u4e2d\u83b2\u62c9\u7740\u5e08\u7236\uff0c\u7ad9\u5728\u4eba\u4e1b \u4e4b\u4e2d\uff1a\u5e76\u6ca1\u6709\u7ed9\u719f\u4eba\u53d1\u73b0\u3002 \u3000\u3000\u5973\u738b\u7684\u8f66\u9a7e\u5728\u524d\uff0c\u5510\u52aa\u73e0\u7a46\u548c\u4ed6\u7684\u968f\u4ece\u4e58\u4e00\u8f86\u655e\u7bf7\u7684\u5927\u9a6c\u8f66\u5728\u540e\uff0c\u968f\u4ece\u4e2d\u6709\u4e91\u5bb6\u5144 \u59b9\uff0c\u6709\u5510\u52a0\u6e90\u548c\u7389\u73b2\u73d1\uff0c\u8fd9\u4e24\u4eba\u662f\u5e94\u5510\u52aa\u73e0\u7a46\u4e4b\u8bf7\uff0c\u5230\u9a6c\u8428\u513f\u56fd\u4f5c\u5ba2\u7684\u3002\u53e6\u5916\u8fd8\u6709\u4e00\u4e2a \u610f\u60f3\u4e0d\u5230\u7684\u4eba\u662f\u534e\u4e91\u78a7\u3002\u4f46\u5374\u4e0d\u89c1\u6c5f\u6d77\u5929\u548c\u534e\u5929\u98ce\u3002\u539f\u6765\u534e\u4e91\u78a7\u4e5f\u968f\u7740\u4ed6\u4eec\u5357\u5f52\uff0c\u5979\u7236 \u4eb2\u548c\u6c5f\u6d77\u5929\u5219\u6b63\u5728\u627e\u8c37\u4e2d\u83b2\uff0c\u4e09\u65e5\u4e4b\u671f\u672a\u6ee1\uff0c\u534e\u5929\u98ce\u4ee5\u4e3a\u8c37\u4e2d\u83b2\u8fd8\u5728\u90a3\u77f3\u7a9f\u517b\u75c5\uff0c\u4e00\u627e \u4fbf\u7740\uff0c\u8fd8\u53ef\u4ee5\u8d76\u6765\u548c\u4ed6\u4eec\u540c\u884c\u7684\uff0c\u54ea\u77e5\u5df2\u662f\u8fdf\u4e86\u4e00\u6b65\u4e86\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u5fc3\u9178\uff0c\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u4e91\u59d0\u59d0\u5df2\u7ecf\u627e\u7740\u4e86\uff0c\u77a7\u5979\u5df2\u662f\u5168\u65e0\u75c5\u5bb9\uff0c \u60f3\u5fc5\u662f\u89c1\u4e86\u6d77\u5929\uff0c\u5fc3\u75c5\u5df2\u7ecf\u597d\u4e86\u3002\u90a3\u6211\u66f4\u4e0d\u5b9c\u9732\u9762\uff0c\u60f9\u8d77\u6ce2\u6f9c\u4e86\u3002\u201d\u5979\u600e\u77e5\u9053\uff0c\u534e\u4e91\u78a7 \u7684\u5fc3\u75c5\u5e76\u4e0d\u662f\u6c5f\u6d77\u5929\u6cbb\u597d\u7684\uff0c\u800c\u662f\u4e91\u743c\u6cbb\u597d\u7684\u3002\u8c37\u4e2d\u83b2\u5728\u60f3\u7740\u201c\u6210\u5168\u201d\u5979\uff0c\u5979\u4e5f\u6b63\u5728\u60f3 \u7740\u8981\u6210\u5168\u8c37\u4e2d\u83b2\u5462\u3002\u53ef\u60dc\u8c37\u4e2d\u83b2\u4e0d\u80af\u9732\u9762\uff0c\u53cc\u65b9\u5fc3\u4e8b\u3002\u7adf\u662f\u5f7c\u6b64\u96be\u660e\uff01\u6b63\u662f\uff1a \u3000\u3000\u853d\u65e5\u6d6e\u4e91\u539f\u5df2\u6563\uff0c\u9e33\u9e2f\u4ecd\u81ea\u5404\u5206\u98de\uff01 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e94\u5341\u4e94\u56de\u3000\u7ea6\u4f1a\u9099\u5c71\u6000\u654c\u610f\u3000\u76f8\u9022\u9b54\u7a9f\u8bc9\u524d\u56e0 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e94\u5341\u4e94\u56de\u3000\u7ea6\u4f1a\u9099\u5c71\u6000\u654c\u610f\u3000\u76f8\u9022\u9b54\u7a9f\u8bc9\u524d\u56e0 \u3000\u3000\u5e08\u5f92\u4fe9\u517c\u7a0b\u8d76\u8def\uff0c\u4ece\u98ce\u96ea\u6f2b\u5929\u7684\u585e\u5317\u56de\u5230\u6625\u5149\u6ee1\u5730\u7684\u6c5f\u5357\uff0c\u6b63\u597d\u8d76\u4e0a\u6e05\u660e\u65f6\u8282\u3002\u9099\u5c71\u4e0a \u6742\u82b1\u751f\u6811\uff0c\u7fa4\u9e3e\u4e71\u98de\uff0c\u7011\u5e03\u6d41\u6cc9\uff0c\u98de\u73e0\u6e85\u7389\uff0c\u8bf4\u4e0d\u5c3d\u5b9c\u4eba\u666f\u8272\uff0c\u604d\u5982\u8eab\u5728\u753b\u56fe\u3002\u4f46\u5728\u8fd9\u5927\u597d \u6625\u5149\u7684\u4f73\u65e5\uff0c\u9099\u5c71\u4e0a\u7684\u6c14\u6c1b\u5374\u663e\u5f97\u6709\u70b9\u5f02\u6837\u7d27\u5f20\u3002 \u3000\u3000\u5728\u9876\u5cf0\u7384\u5973\u5bab\u4e0b\u9762\uff0c\u662f\u4e00\u6761\u4e24\u884c\u69d0\u6811\u5939\u7740\u7684\u5893\u9053\uff0c\u5893\u9053\u5c3d\u5934\u5c31\u662f\u9099\u5c71\u6d3e\u5228\u6d3e\u7956\u5e08\u72ec\u81c2\u795e \u5c3c\u7684\u5893\u56ed\uff0c\u7b2c\u4e8c\u4ee3\u638c\u95e8\u5415\u56db\u5a18\u7684\u575f\u5893\u4e5f\u5728\u91cc\u9762\uff0c\u5893\u9053\u4e24\u8fb9\uff0c\u9099\u5c71\u6d3e\u7684\u5f1f\u5b50\u7a7f\u68ad\u6765\u5f80\uff0c\u62f1\u536b\u68ee \u4e25\u3002 \u3000\u3000\u9099\u5c71\u6d3e\u89c4\u77e9\uff0c\u6bcf\u5e74\u6e05\u660e\uff0c\u5404\u5730\u5f1f\u5b50\uff0c\u90fd\u8981\u8d76\u6765\u7ed9\u7956\u5e08\u626b\u5893\u3002\u8c37\u4e4b\u534e\u4e00\u7b97\u65e5\u5b50\uff0c\u4e09\u65e5\u540e\u624d \u662f\u6e05\u660e\u6b63\u65e5\uff0c\u4e0d\u89c9\u7a0d\u7a0d\u6709\u70b9\u8be7\u5f02\uff1a\u201c\u600e\u7684\u4ed6\u4eec\u4eca\u5e74\u6765\u5f97\u7279\u522b\u65e9\uff1f\u8fd8\u672a\u5230\u6b63\u65e5\uff0c\u4f55\u4ee5\u5c31\u7fa4\u96c6\u5893 \u56ed\uff1f\u201d \u3000\u3000\u9099\u5c71\u6d3e\u4f17\u5f1f\u4e8e\u89c1\u638c\u95e8\u56de\u6765\uff0c\u4e2a\u4e2a\u6b22\u6b23\uff0c\u4eba\u4eba\u96c0\u8dc3\uff0c\u90fd\u662f\u8bf4\u9053\uff1a\u201c\u638c\u95e8\uff0c\u4f60\u56de\u6765\u5f97\u6b63\u597d\u3002 \u7ffc\u5e08\u4f2f\u65e5\u65e5\u90fd\u5728\u76fc\u671b\u4f60\u5462\uff01\u201d\u8c37\u4e4b\u534e\u79bb\u5c71\u4e4b\u65f6\uff0c\u638c\u95e8\u804c\u52a1\u4ea4\u4e0e\u7ffc\u4ef2\u725f\u4ee3\u7406\uff0c\u767d\u82f1\u6770\u4e3a\u8f85\u3002\u8fd9 \u65f6\uff0c\u7ffc\u5fe1\u725f\u4e5f\u65e9\u5df2\u788d\u4e86\u901a\u62a5\uff0c\u4e0e\u9099\u5c71\u6d3e\u7b2c\u4e09\u4ee3\u56db\u5927\u5f1f\u5b50\uff0c\u5373\u8c37\u4e4b\u534e\u7684\u5e73\u8f88\u5e08\u5144\u767d\u82f1\u6770\u3001\u8def\u82f1 \u8c6a\u3001\u7a0b\u6d69\u3001\u6797\u7afa \u3000\u3000\u8fdb\u4e86\u8bae\u4e8b\u5385\u5750\u5b9a\u4e4b\u540e\uff0c\u7ffc\u4ef2\u725f\u53d6\u51fa\u4e00\u4e2a\u62dc\u5323\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u770b\u4e86\u8fd9\u5f20\u5e16\u5b50\uff0c\u5c31\u660e\u767d\u4e86\u3002\u201d \u8c37\u4e4b\u534e\u62bd\u51fa\u62dc\u5e16\u4e00\u770b\uff0c\u9887\u89c9\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u5929\u9b54\u6559\u5411\u54b1\u4eec\u6311\u8845\uff0c\u8981\u62a5\u4ed6\u4eec\u6559\u4e3b\u5f53 \u5e74\u88ab\u9010\u4e4b\u4ec7\u3002\u201d\u5fc3\u91cc\u81ea\u601d\uff1a\u201c\u8fd9\u5929\u9b54\u6559\u4e3b\uff0c\u6211\u53bb\u5e74\u4e2d\u79cb\u5728\u91d1\u9e70\u5bab\u4e4b\u4f1a\u4e2d\u8fd8\u89c1\u8fc7\u5979\uff0c\u600e\u7684\u5979\u4f1a \u5728\u91cd\u9633\u8282\u6d3e\u4eba\u9001 \u3000\u3000\u7ffc\u4ef2\u725f\u9053\uff1a\u201c\u8fd9\u5f20\u5e16\u5b50\u662f\u53bb\u5e74\u91cd\u9633\u8282\u4ed6\u4eec\u6d3e\u4eba\u9001\u6765\u7684\u3002\u7ea6\u4e0b\u4eca\u5e74\u6e05\u660e\u6765\u6b64\u62dc\u5c71\uff0c\u5b9e\u662f\u4e0d \u6000\u597d\u610f\uff0c\u6211\u4eec\u6b63\u5728\u4e3a\u6b64\u7126\u8651\u5462\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u62dc\u5c71\u5373\u662f\u6bd4\u6b66\u7684\u522b\u540d\uff0c\u4f55\u51b5\u5979\u4ea6\u5df2\u8a00\u660e\u662f\u4e3a\u4e86\u8981\u62a5\u590d\u5f53\u5e74\u4e4b\u4ec7\u800c\u6765\u7684\u4e86\uff0c \u8fd9\u5f53\u7136\u662f\u4e0d\u6000\u597d\u610f\uff0c\u4f55\u4ee5\u4e24\u4f4d\u5e08\u5144\u8fd8\u518d\u4e09\u63d0\u53ca\u6b64\u70b9\uff1f\u201d \u3000\u3000\u7ffc\u4ef2\u725f\u9053\uff1a\u201c\u6211\u6240\u8bf4\u7684\u4e0d\u6000\u597d\u610f\uff0c\u8fd8\u4e0d\u53ea\u5982\u6b64\u7b80\u5355\uff0c\u5176\u4e2d\u6709\u4e2a\u79d8\u5bc6\u3002\u96cd\u6b63\u7687\u5e1d\u662f\u4f60\u5e08\u7236 \u6740\u7684\uff0c\u8fd9\u4e8b\u60c5\u4f60\u5f53\u7136\u77e5\u9053\u3002\u201d\u8c37\u4e4b\u534e\u806a\u660e\u7edd\u9876\uff0c\u4e00\u70b9\u4fbf\u900f\uff0c\u604d\u7136\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u6211\u660e\u767d\u4e86\uff0c\u662f \u671d\u5ef7\u5bf9\u54b1\u4eec\u4e0d\u6000\u597d\u610f\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9605\u5386\u5c1a\u6d45\uff0c\u4e00\u65f6\u60f3\u4e0d\u660e\u767d\uff0c\u95ee\u9053\uff1a\u201c\u671d\u5ef7\u82e5\u662f\u5bf9\u4ed8\u54b1\u4eec\uff0c\u4f55\u987b\u501f\u7528\u5929\u9b54\u6559\u7684\u540d\u4e49 \uff1f\u201d\u7ffc\u4ef2\u725f\u9053\uff1a\u201c\u7687\u5e1d\u88ab\u4eba\u523a\u6740\uff0c\u5c38\u9996\u4e0d\u5168\uff0c\u5018\u82e5\u4f20\u626c\u51fa\u53bb\uff0c\u7687\u5bb6\u4f53\u9762\u4f55\u5b58\uff1f\u6240\u4ee5\u9791\u5b50\u7687\u5e1d \uff0c\u867d\u7136\u6068\u900f\u4e86\u54b1\u4eec\u9099\u5c71\u6d3e\uff0c\u627e\u4e0d\u7740\u501f\u53e3\uff0c\u8fd8\u771f\u4e0d\u6562\u5174\u5175\u8ba8\u4f10\u5462\u3002\u201d \u3000\u3000\u767d\u82f1\u6770\u9053\uff1a\u201c\u8fd9\u5929\u9b54\u6559\u4e3b\u7684\u59d0\u59d0\uff0c\u5c31\u662f\u90a3\u5e74\u5192\u8ba4\u83b2\u4f84\u7684\u6bcd\u4eb2\uff0c\u4e0a\u5c71\u80e1\u95f9\u8fc7\u4e00\u573a\u7684\u90a3\u4e2a\u7f2a \u592b\u4eba\u3002\u5979\u7684\u5927\u592b\u7f2a\u5357\u5ead\uff0c\u65b0\u8fd1\u4ece\u6cb3\u5357\u8c03\u5230\u5c71\u4e1c\uff0c\u5347\u4efb\u5de1\u629a\uff0c\u6211\u4eec\u4f30\u8ba1\uff0c\u6b64\u4e8b\u60f3\u5fc5\u662f\u7f2a\u5357\u5ead\u5411 \u9791\u5b50\u7687\u5e1d\u732e\u7b56\uff0c\u501f\u7528\u5929\u9b54\u6559\u7684\u540d\u4e49\u5411\u54b1\u4eec\u6311\u8845\uff0c\u6697\u4e2d\u5374\u8c03\u6765\u4e00\u6279\u5927\u5185\u9ad8\u624b\uff0c\u5192\u5145\u662f\u5929\u9b54\u6559\u7684 \u4eba\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c \u3000\u3000\u8c37\u4e4b\u534e\u51b7\u7b11\u9053\uff0c\u201c\u4ed6\u4eec\u5012\u662f\u6253\u5f97\u5982\u610f\u7b97\u76d8\uff0c\u9009\u5b9a\u6e05\u660e\u90a3\u5929\u52a8\u624b\uff0c\u60f3\u8d81\u7740\u54b1\u4eec\u9099\u5c71\u6d3e\u5f1f\u5b50 \u9f50\u96c6\u626b\u5893\u7684\u65f6\u5019\uff0c\u4e00\u7f51\u6253\u5c3d\uff01\u54fc\uff0c\u54fc\uff0c\u53ea\u6015\u6ca1\u6709\u90a3\u4e48\u5bb9\u6613\uff01\u201d \u3000\u3000\u7ffc\u4ef2\u725f\u9053\uff1a\u201c\u90a3\u4e48\u638c\u95e8\u7684\u610f\u601d\u662f\u51b3\u5b9a\u5175\u6765\u5c06\u6321\uff0c\u6c34\u6765\u571f\u63a9\u4e86\uff1f\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u9664\u6b64\u4e4b\u5916 \uff0c\u8fd8\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u96be\u9053\u54b1\u4eec\u8fd8\u80fd\u820d\u4e86\u9099\u5c71\uff0c\u4ea1\u547d\u6c5f\u6e56\uff0c\u7f6e\u7956\u5e08\u575f\u5893\u4e0d\u987e\uff0c\u8ba9\u9791\u5b50\u7687\u5e1d\u4e00\u4e2a\u4e2a \u7684\u6536\u62fe\u54b1\u4eec\u4e48\uff1f\u5373\u4f7f\u4ed6\u4eec\u8c03\u9f50\u4e86\u5927\u5185\u9ad8\u624b\u524d\u6765\uff0c\u54b1\u4eec\u4e5f\u5f97\u62fc\u4ed6\u4e00\u573a\uff01\u201d \u3000\u3000\u7ffc\u4ef2\u725f\u5fc3\u60f3\uff1a\u201c\u8c37\u5e08\u59b9\u5e73\u7d20\u67d4\u5f31\uff0c\u4e8b\u5230\u4e34\u5934\uff0c\u5374\u5982\u6b64\u575a\u5f3a\uff01 \u3000\u3000\u5f53\u771f\u4e0d\u6127\u662f\u7ee7\u627f\u5415\u5e08\u53d4\u8863\u94b5\u7684\u5f1f\u5b50\uff0c\u4e0d\u6127\u662f\u54b1\u4eec\u9099\u5c71\u6d3e\u7684\u638c\u95e8\uff0c\u66f9\u5e08\u59d0\u5f53\u5e74\u4f20\u4f4d\u7ed9\u5979\uff0c \u662f\u4f5c\u5f97\u5bf9\u4e86\uff01\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u6709\u4e86\u4e00\u4e9b\u51c6\u5907\uff0c\u4e10\u5e2e\u5f1f\u5b50\u5230\u65f6\u53ef\u4f5c\u5916\u63f4\u3002\u4e0d\u8fc7\u8fd8\u5f97\u8bf7\u638c\u95e8 \u4e3b\u6301\u5927\u8ba1\u3002\u201d\u8fd9\u6b21\u56e0\u4e3a\u5929\u9b54\u6559\u5728\u540d\u4e49\u4e0a\u53ea\u662f\u5411\u9099\u5c71\u6d3e\u6311\u6218\uff0c\u6545\u6b64\u9099\u5c71\u6d3e\u4e5f\u4e0d\u4fbf\u9080\u8bf7\u5176\u4ed6\u95e8\u6d3e \u5e2e\u5fd9\uff0c\u4f46\u7ffc\u4ef2\u725f \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u8fd8\u6709\u4e24\u5929\u65f6\u95f4\uff0c\u6211\u82e5\u60f3\u5230\u4ec0\u4e48\uff0c\u518d\u8bf7\u5404\u4f4d\u5e08\u5144\u5546\u8bae\u3002\u201d\u7ffc\u4ef2\u725f\u3001\u81ea\u82f1\u6770\u7b49 \u4eba\u544a\u9000\u4e4b\u540e\uff0c\u8c37\u4e2d\u83b2\u5ffd\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u770b\u6b64\u4e8b\u53ef\u7591\u4e4b\u5904\u751a\u591a\uff01\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u90a3\u62dc\u5e16 \u9001\u6765\u7684\u65f6\u95f4\u4e0d\u5bf9\uff1f\u5f53\u65f6\u5929\u9b54\u6559\u4e3b\u5df2\u8fdc\u79bb\u5979\u7684\u5de2\u7a74\u3002\u4f46\u4e5f\u8bf4\u4e0d\u5b9a\u662f\u5979\u5148\u7559\u4e0b\u5e16\u5b50\uff0c\u518d\u5230\u6606\u5e03\u5170 \u56fd\u5411\u5979\u5e08\u7236\u6c42\u63f4 \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u6000\u7591\u8fd9\u6839\u672c\u4e0d\u662f\u5929\u9b54\u6559\u4e3b\u7684\u4e3b\u610f\uff0c\u8bf4\u4e0d\u5b9a\u5979\u5168\u4e0d\u77e5\u60c5\uff01\u201d\u8c37\u4e4b\u534e\u9053 \uff1a\u201c\u5373\u4f7f\u662f\u671d\u5ef7\u901a\u8fc7\u5979\u7684\u59d0\u59d0\u3002\u501f\u7528\u5979\u7684\u540d\u4e49\uff0c\u4e5f\u603b\u5f97\u5979\u70b9\u5934\u540c\u610f\u624d\u884c\u3002\u4f55\u4ee5\u4f60\u4f1a\u6709\u6b64\u6000\u7591 \uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u77e5\u5929\u9b54\u6559\u4e3b\u65e9\u5df2\u6709\u610f\u5f03\u90aa\u5f52\u6b63\u3001\u6539\u6076\u4ece\u5584\u4e4b\u5fc3\uff0c\u800c\u4e14\u53ea\u6015\u5c31\u5728\u8fd9\u51e0 \u5929\u4e4b\u5185\uff0c\u5979\u4fbf\u8981 \u3000\u3000\u8c37\u4e4b\u534e\u8be7\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u5728\u6606\u5e03\u5170\u56fd\u4e4b\u65f6\uff0c\u66fe\u53d7\u5929\u9b54\u6559\u4e3b\u770b\u7ba1\uff0c \u65e0\u610f\u4e2d\u77e5\u9053\u4e86\u5979\u7684\u79d8\u5bc6\u3002\u201d\u5f53\u4e0b\u5c06\u6240\u89c1\u6240\u95fb\u8bf4\u4e86\u51fa\u6765\uff0c\u8c37\u4e4b\u534e\u542c\u5230\u5389\u590d\u751f\u4e0e\u5929\u9b54\u6559\u4e3b\u7518\u613f\u540c \u751f\u5171\u6b7b\u7684\u90a3\u6bb5\u604b\u60c5\uff0c\u4e5f\u4e0d\u7981\u6cea\u76c8\u4e8e\u776b\uff0c\u559f\u7136\u8bf4\u9053\uff1a\u201c\u5389\u590d\u751f\u5929\u771f\u65e0\u90aa\uff0c\u75f4\u60c5\u7737\u604b\uff0c\u4e5f\u8fd8\u4e0d\u8db3 \u4e3a\u5947\u3002\u5374\u60f3\u4e0d\u5230 \u3000\u3000\u8c37\u4e2d\u83b2\u5ffd\u5730\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u60f3\u5230\u8bc5\u7b26\u5c71\u63a2\u671b\u5929\u9b54\u6559\u4e3b\u3002\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4f60\u8981\u5355\u8eab\u6f5c\u5165\u72d9 \u5f95\u5c71\u79c1\u4f1a\u5979\uff1f\u8fd9\u3001\u8fd9\u4e0d\u592a\u5371\u9669\u4e86\u4e48\uff1f\u201d\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u82e5\u662f\u62e3\u660e\u771f\u76f8\uff0c\u8bf4\u4e0d\u5b9a\u53ef\u4ee5\u5316\u89e3\u4e00\u573a \u707e\u7978\u3002\u518d\u8bf4\u5929\u9b54\u6559\u4e3b\u66fe\u5bf9\u6211\u8868\u793a\u8fc7\u4e00\u756a\u597d\u610f\uff0c\u6211\u5f53\u65f6\u867d\u6ca1\u9886\u5979\u7684\u60c5\uff0c\u4f46\u6295\u6843\u62a5\u674e\uff0c\u6b64\u65f6\u5979\u5728 \u5371\u96be\u4e4b\u4e2d\uff0c\u6211\u5df2 \u3000\u3000\u8c37\u4e4b\u534e\u5728\u5f92\u5f1f\u8eab\u4e0a\u4f9d\u7a00\u770b\u5230\u81ea\u5df1\u5c11\u5e74\u65f6\u5019\u7684\u5f71\u5b50\uff0c\u6ee1\u8154\u70ed\u8840\uff0c\u4e00\u7247\u67d4\u60c5\uff1b\u53ea\u77e5\u4fa0\u4e49\u4e3a\u6000 \uff0c\u5173\u5fc3\u5927\u4f53\uff1b\u4ece\u4e0d\u77bb\u524d\u987e\u540e\uff0c\u53ea\u7ba1\u81ea\u8eab\u3002\u6240\u4e0d\u540c\u7684\u662f\uff0c\u5979\u6bd4\u81ea\u5df1\u5c11\u5e74\u65f6\u5019\uff0c\u4f3c\u4e4e\u8fd8\u8981\u575a\u5f3a\uff0c \u66f4\u80fd\u7ecf\u5f97\u8d77\u98ce\u6d6a\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u6df1\u4e3a\u611f\u52a8\uff0c\u6697\u81ea\u601d\u91cf\uff1a\u201c\u83b2\u513f\u8fd9\u51e0\u4e2a\u6708\u6765\uff0c\u4e00\u8def\u4e0a\u52e4\u7ec3\u90a3\u9f99\u529b\u79d8\u85cf\u4e0a\u7684\u7cbe\u5965\u5185\u529f \uff0c\u5979\u670d\u98df\u4e86\u5929\u5fc3\u77f3\u6240\u589e\u6dfb\u7684\u529f\u529b\u4ea6\u5df2\u4e0e\u672c\u8eab\u539f\u6709\u7684\u529f\u529b\u76f8\u5408\u4e3a\u4e00\uff0c\u5373\u4f7f\u5c1a\u672a\u80fd\u9752\u51fa\u4e8e\u84dd\uff0c\u81f3 \u5c11\u4ea6\u8db3\u4ee5\u4e0e\u6211\u6bd4\u80a9\u4e86\u3002\u5018\u82e5\u8981\u9009\u4e00\u4e2a\u4eba\u53bb\u7956\u5f95\u5c71\u63a2\u542c\u865a\u5b9e\uff0c\u786e\u662f\u975e\u5979\u5960\u5c5e\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u53ea\u6015\u5e08\u7236\u4e0d\u80af\u5e94\u5141\uff0c\u53c8\u7533\u8ff0\u9053\uff1a\u201c\u6211\u6709\u767d\u7389\u7532\u9632\u8eab\uff0c\u53c8\u6709\u971c\u534e\u5b9d\u5251\uff0c\u81f3\u4e0d\u6d4e\u4e5f\u80fd \u9003\u8dd1\u51fa\u6765\uff0c\u800c\u4e14\u6211\u5bf9\u72d9\u5f95\u5c71\u4e5f\u5e76\u4e0d\u964c\u751f\u3002\u201d\u8c37\u4e4b\u534e\u8be7\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u53bb\u8fc7\u72d9\u5f95\u5c71\u7684\uff1f\u201d\u8c37\u4e2d\u83b2 \u9762\u4e0a\u4e00\u7ea2\u3002\u8bf4\u9053\uff1a\u201c\u867d\u6ca1\u53bb\u8fc7\uff0c\u4f46\u6709\u4eba\u548c\u6211\u8bf4\u8fc7\u3002\u201d\u8c37\u4e4b\u534e\u604d\u7136\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6c5f\u6d77\u5929\u5c0f\u65f6 \u5019\u662f\u5728\u72d9\u5f95\u5c71\u4f4f \u3000\u3000\u8c37\u4e4b\u534e\u62ac\u8d77\u5934\u6765\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u9099\u5c71\u4e00\u8109\uff0c\u4fa0\u4e49\u4f20\u5bb6\uff0c\u96be\u5f97\u4f60\u6709\u5982\u6b64\u62b1\u8d1f\u3002\u597d\uff0c\u90a3 \u4f60\u5c31\u53bb\u5427\uff0c\u53ef\u5f97\u4e00\u5207\u5c0f\u5fc3\u4e86\uff01\u201d \u3000\u3000\u72d9\u5f95\u5c71\u79bb\u9099\u5c71\u7ea6\u6709\u4e94\u767e\u91cc\u4e4b\u9065\uff0c\u8c37\u4e2d\u83b2\u5e2d\u672a\u6687\u6696\uff0c\u4fbf\u5373\u52a8\u8eab\uff0c\u7b2c\u4e8c\u65e5\u9ec4\u660f\u65f6\u5019\uff0c\u5df2\u5230\u4e86 \u5929\u9b54\u6559\u5de2\u7a74\u6240\u5728\u7684\u7956\u889c\u5c71\u3002\u8c37\u4e2d\u83b2\u4ece\u524d\u4e0e\u6c5f\u6d77\u5929\u88ab\u56f0\u5728\u8352\u5c9b\u4e4b\u65f6\uff0c\u957f\u65e5\u65e0\u804a\uff0c\u4f1a\u8c08\u7ecf\u5386\uff0c\u51e0 \u4e4e\u662f\u4ec0\u4e48\u7410\u4e8b\u90fd\u8c08\u5230\u4e86\u3002\u8c37\u4e2d\u83b2\u8bb0\u5f97\u6c5f\u6d77\u5929\u66fe\u5bf9\u5979\u63cf\u7ed8\u8fc7\u5929\u9b54\u6559\u5728\u72d9\u5f95\u5c71\u7684\u5efa\u7b51\uff0c\u90a3\u662f\u4e00\u4e2a \u5c0f\u578b\u7684\u57ce\u5821\uff0c\u4e1c \u3000\u3000\u5c31\u662f\u5929\u9b54\u6559\u4e3b\u4f4f\u5b85\u7684\u540e\u56ed\uff0c\u6c5f\u6d77\u5929\u548c\u5979\u540c\u4f4f\u7684\u65f6\u5019\uff0c\u662f\u5e38\u5e38\u5728\u56ed\u4e2d\u73a9\u7684\uff0c \u3000\u3000\u5f53\u4e0b\u8c37\u4e2d\u83b2\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u76f4\u5954\u5c71\u5317\uff0c\u8d8a\u8fc7\u51e0\u91cd\u5c97\u5ce6\uff0c\u8d70\u4e0a\u4e00\u5ea7\u5ca9\uff0c\u53ea\u89c1\u524d\u9762\u4e00\u5c42\u5ced\u58c1 \u62d4\u5730\u800c\u8d77\uff0c\u4ece\u9876\u81f3\u5e95\u3002\u5929\u7136\u5982\u5ced\uff0c\u6beb\u65e0\u53ef\u4ee5\u501f\u529b\u6500\u63f4\u4e4b\u5904\uff0c\u8c37\u4e2d\u83b2\u5fc3\u9053\uff1a\u201c\u5e78\u800c\u6211\u5e26\u6709\u971c\u534e \u5b9d\u5251\u3002\u201d\u98de\u8eab\u8dc3\u8d77\uff0c\u4e00\u8dc3\u4e09\u4e08\u6765\u9ad8\uff0c\u7acb\u5373\u7528\u5251\u63d2\u5165\u77f3\u58c1\uff0c\u6316\u4e2a\u5c0f\u5c0f\u7684\u7a9f\u7abf\uff0c\u4f5c\u4e3a\u7acb\u8db3\u4e4b\u70b9\uff0c \u7f13\u8fc7\u53e3\u6c14\uff0c\u53c8\u518d \u3000\u3000\u8c37\u4e2d\u83b2\u6293\u7740\u98d8\u6765\u7684\u91ce\u85e4\uff0c\u53ea\u4e00\u8361\u5c31\u8361\u5230\u4e86\u56ed\u4e2d\uff0c\u63a5\u4e0a\u4e86\u4e00\u68f5\u53c2\u5929\u8001\u6811\u3002 \u3000\u3000\u4ece\u6811\u9876\u671b\u53bb\uff0c\u53ef\u4ee5\u671b\u89c1\u5c0f\u697c\u4e00\u89d2\u3002\u8c37\u4e2d\u83b2\u77e5\u9053\u5929\u9b54\u6559\u4e3b\u5c31\u662f\u4f4f\u5728\u697c\u4e2d\uff0c\u5f53\u4e0b\u51dd\u795e\u9759\u542c\uff0c \u53ea\u542c\u5f97\u90a3\u4e2a\u623f\u5b50\u91cc\u6b63\u6709\u7740\u4e00\u4e2a\u8001\u5987\u563f\u563f\u7684\u5728\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u771f\u662f\u5bf9\u5f97\u4f4f\u6211\u554a\uff0c\u5c45\u7136\u8fd8\u6709\u9762 \u5b50\u6765\u6c42\u6211\u5e2e\u5fd9\uff1f\u4f0a\u58c1\u73e0\u739b\uff0c\u4f60\u4e5f\u771f\u662f\u6211\u7684\u597d\u5f92\u5f1f\uff0c\u5371\u96be\u4e4b\u65f6\uff0c\u5374\u53ea\u77e5\u81ea\u5df1\u9003\u4ea1\uff01\u563f\u563f\uff0c\u5982\u4eca \u53c8\u6709\u7528\u5f97\u7740\u5e08\u7236 \u3000\u3000\u8c37\u4e2d\u83b2\u5403\u4e86\u4e00\u60ca\uff0c\u5927\u611f\u610f\u5916\uff0c\u539f\u6765\u6b63\u662f\u90a3\u6606\u5e03\u5170\u56fd\u7684\u91d1\u8f6e\u5723\u6bcd\u7ae5\u59e5\u59e5\u5728\u8fd9\u623f\u4e2d\uff0c\u4e0e\u6587\u5ef7 \u58c1\u3001\u7f2a\u592b\u4eba\u4e8c\u4eba\u8bf4\u8bdd\uff0c\u5374\u6ca1\u6709\u5929\u9b54\u6559\u4e3b\u3002 \u3000\u3000\u7f2a\u592b\u4eba\u9053\uff1a\u201c\u5e08\u7236\u5bb9\u7980\uff0c\u5e76\u975e\u5f1f\u5b50\u4e34\u5371\u5f03\u5e08\uff0c\u662f\u5f1f\u5b50\u4e0d\u6562\u505a\u5e08\u7236\u7684\u7d2f\u8d58\u3002\u5f53\u65f6\u5f1f\u5b50\u5949\u4e86 \u5e08\u7236\u4e4b\u547d\uff0c\u770b\u5b88\u72af\u4eba\uff0c\u654c\u4eba\u5df2\u7ecf\u6740\u5165\u5bab\u4e2d\uff0c\u5f1f\u5b50\u8bda\u6050\u6709\u5931\uff0c\u6545\u800c\u4e0d\u80fd\u4e0d\u6682\u907f\u4e00\u65f6\u3002\u201d \u3000\u3000\u6587\u5ef7\u58c1\u9053\uff1a\u201c\u5f53\u65f6\u6211\u4e0e\u7f2a\u592b\u4eba\u90fd\u662f\u5982\u6b64\u60f3\u6cd5\uff0c\u4f60\u8001\u4eba\u5bb6\u795e\u529f\u76d6\u4e16\uff0c\u51b3\u8ba1\u65e0\u987b\u6211\u4eec\u665a\u8f88\u76f8 \u52a9\uff0c\u6211\u4eec\u5374\u6015\u72af\u4eba\u88ab\u5bf9\u65b9\u52ab\u53bb\uff0c\u6709\u8d1f\u4f60\u8001\u4eba\u5bb6\u7684\u5631\u6258\uff0c\u662f\u4ee5\u5148\u8d70\u4e00\u6b65\u3002\u81f3\u4e8e\u540e\u6765\u4e4b\u4e8b\uff0c\u90a3\u5374 \u662f\u8c01\u4e5f\u60f3\u4e0d\u5230\u7684\u4e86\u3002\u201d \u3000\u3000\u7ae5\u59e5\u59e5\u6700\u559c\u5949\u627f\uff0c\u7ed9\u4e5f\u4eec\u4e8c\u4eba\u5de6\u4e00\u53e5\u201c\u795e\u529f\u76d6\u4e16\u201d\u53f3\u4e00\u53e5\u201c\u76d6\u4e16\u795e\u529f\u201d\uff0c\u9ad8\u5e3d\u5b50\u6234\u5230\u5934 \u4e0a\uff0c\u6012\u6c14\u767b\u65f6\u6d88\u4e86\u4e03\u516b\u5206\uff0c\u4f46\u4ecd\u662f\u5bf9\u6587\u5ef7\u58c1\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7b97\u4f60\u4f1a\u8bb2\u8bdd\uff0c\u6211\u53eb\u4f60\u770b\u5b88\u72af\u4eba\uff0c\u4f60 \u5f97\u7684\u597d\u5904\u53ef\u4e5f\u4e0d\u5c0f\u554a\uff0c\u4f60\u5982\u4eca\u5df2\u7ec3\u6210\u4e86\u6b63\u53cd\u4e94\u884c\u7684\u4e09\u8c61\u795e\u529f\uff0c\u8fd8\u7528\u5f97\u4e0a\u6211\u7ed9\u4f60\u5e2e\u5fd9\u4e48\uff1f\u201d \u3000\u3000\u539f\u6765\u90a3\u65e5\u5728\u6606\u5e03\u5170\u56fd\uff0c\u6b63\u5f53\u5ba3\u59e5\u59e5\u4e0e\u6c5f\u6d77\u5929\u6076\u6597\u7684\u65f6\u5019\uff0c\u6587\u5ef7\u58c1\u4e0e\u7f2a\u592b\u4eba\u5c31\u5c06\u5929\u9b54\u6559\u4e3b \u4e0e\u5389\u590d\u751f\u63b3\u8d70\uff0c\u4ece\u5bab\u4e2d\u7684\u79d8\u9053\u79c1\u9003\u51fa\u53bb\u3002\u6587\u5ef7\u58c1\u53d6\u4e86\u5929\u9b54\u6559\u4e3b\u7684\u767e\u6bd2\u771f\u7ecf\uff0c\u5df2\u7ec3\u6210\u4e86\u6b63\u90aa\u5408 \u4e00\uff0c\u5185\u529f\u4e0e\u6bd2\u529f\u76f8\u7ed3\u5408\u7684\u4e00\u79cd\u6781\u5389\u5bb3\u529f\u592b\u3002 \u3000\u3000\u6700\u5403\u4e8f\u7684\u5374\u662f\u7ae5\u59e5\u59e5\uff0c\u90a3\u65e5\u5979\u8f6e\u756a\u4e0e\u6c5f\u6d77\u5929\u53ca\u5510\u7ecf\u5929\u592b\u5987\u7b49\u9ad8\u624b\u6076\u6597\uff0c\u867d\u662f\u4fa5\u5e78\u9003\u4e86\u51fa \u6765\uff0c\u5143\u6c14\u5df2\u635f\u4f24\u4e0d\u5c11\uff0c\u7ecf\u8fc7\u4e86\u51e0\u4e2a\u6708\u7684\u8c03\u6cbb\uff0c\u65b9\u59cb\u6062\u590d\u539f\u6765\u7684\u516b\u6210\u529f\u529b\uff0c\u968f\u540e\u4e5f\u8ffd\u5230\u4e86\u7956\u5f95 \u5c71\u6765\u3002\u5230\u4e86\u72d9\u5f95\u5c71\u540e\uff0c\u4e00\u6765\u662f\u4e3a\u4e86\u4e0e\u6587\u5ef7\u58c1\u5229\u5bb3\u76f8\u540c\uff0c\u4e8c\u6765\u5979\u635f\u5931\u4e86\u4e24\u6210\u529f\u529b\uff0c\u800c\u6587\u5ef7\u58c1\u5374 \u7ec3\u6210\u4e86\u6b63\u53cd\u4e94\u884c \u3000\u3000\u6587\u5ef7\u58c1\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u91d1\u8f6e\u5723\u6bcd\uff0c\u4f60\u8fd9\u6b21\u82e5\u80af\u51fa\u624b\uff0c\u5bf9\u4f60\u597d\u5904\u53ef\u4e5f\u4e0d\u5c0f\u554a\uff01\u201d\u7ae5\u59e5\u59e5\u9053\uff1a \u201c\u597d\u5904\u4f55\u5728\uff1f\u201d\u6587\u5ef7\u58c1\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u4e0d\u662f\u5e2e\u5fd9\u6211\u4eec\uff0c\u800c\u662f\u5e2e\u5fd9\u5927\u6e05\u671d\uff0c\u8fd9\u4f60\u8fd8\u4e0d\u660e\u767d\u4e48\uff1f\u5728 \u6606\u5e03\u5170\u56fd\uff0c\u4f60\u6781\u5176\u91cf\u505a\u4e2a\u5723\u6bcd\uff0c\u4e00\u4e2a\u5c0f\u56fd\u7684\u5723\u6bcd\uff0c\u4f8b\u5982\u5728\u5317\u4eac\u4eab\u53d7\u4e2d\u534e\u5927\u56fd\u7687\u5e1d\u7684\u4f9b\u5949\uff1f\u201d \u3000\u3000\u4e00\u756a\u8bdd\u8bf4\u5f97\u7ae5\u59e5\u59e5\u8fde\u8fde\u70b9\u5934\uff0c\u7f2a\u592b\u4eba\u9053\uff1a\u201c\u8fd9\u6b21\u5df2\u4ece\u5927\u5185\u8c03\u6765\u4e86\u4e8c\u5341\u56db\u540d\u9ad8\u624b\uff0c\u6240\u5fcc\u8005 \u5c31\u662f\u91d1\u4e16\u9057\u5e08\u5f92\u5018\u82e5\u5f97\u77e5\u98ce\u58f0\uff0c\u53ea\u6015\u4f1a\u8d76\u5230\u9099\u5c71\u76f8\u52a9\u4ed6\u4eec\u7684\u60c5\u4eba\uff0c\u6211\u4eec\u8ba1\u5212\u7531\u5e08\u7236\u62b5\u654c\u6c5f\u6d77 \u5929\uff0c\u6587\u526f\u6559\u4e3b\u5408\u4f17\u9ad8\u624b\u4e4b\u529b\uff0c\u5927\u7ea6\u4e5f\u603b\u53ef\u4ee5\u5bf9\u4ed8\u5f97\u4e86\u91d1\u4e16\u9057\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u6b63\u5728\u542c\u5f97\u5165\u795e\uff0c\u5ffd\u542c\u5f97\u7ae5\u59e5\u59e5\u53eb\u9053\uff1a\u201c\u5916\u9762\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u6587\u5ef7\u58c1\u5df2\u662f \u4e00\u638c\u51fb\u7834\u7a97\u6237\uff0c\u8df3\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u7834\u7a97\u51b2\u51fa\uff0c\u8c37\u4e2d\u83b2\u4e5f\u6b63\u5728\u4ece\u4e0a\u8dc3\u4e0b\uff0c\u8fd8\u672a\u6765\u5f97\u53ca\u62d4\u5251\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u4e24\u4eba\u5c31\u5bf9 \u4e86\u4e00\u638c\uff0c\u8c37\u4e2d\u83b2\u53ea\u89c9\u638c\u5fc3\u9ebb\u75d2\u75d2\u7684\uff0c\u6c14\u529b\u7adf\u4f3c\u4f7f\u4e0d\u51fa\u6765\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u614c\u5fd9\u5012\u7eb5\u51fa\u4e00\u4e08\u5f00\u5916\uff0c \u500f\u5730\u62d4\u51fa\u4e86\u971c\u534e\u5b9d\u5251\uff0c\u821e\u8d77\u4e86\u4e00\u9053\u62a4\u8eab\u5251\u5149\uff0c\u4e14\u6218\u4e14\u8d70\u3002\u6587\u5ef7\u58c1\u4e0e\u5979\u5bf9\u4e86\u4e00\u638c\uff0c\u8eab\u5f62\u4e5f\u4e0d\u7981 \u6643\u4e86\u4e00\u6643\uff0c\u5403\u4e86 \u3000\u3000\u539f\u6765\u8c37\u4e2d\u83b2\u6df1\u77e5\u5929\u9b54\u6559\u5584\u4e8e\u4f7f\u6bd2\uff0c\u65e9\u6709\u51c6\u5907\uff0c\u5979\u53e3\u4e2d\u542b\u4e86\u4e00\u9897\u5929\u5c71\u96ea\u83b2\u70ae\u5236\u7684\u78a7\u7075\u4e39\uff0c \u518d\u4ed7\u7740\u8fd9\u6570\u6708\u6765\u589e\u8fdb\u7684\u529f\u529b\uff0c\u8fd0\u6c14\u4e09\u8f6c\uff0c\u5df2\u81ea\u5316\u89e3\u4e86\u6587\u5ef7\u58c1\u90a3\u4e00\u638c\u6240\u65bd\u7684\u6bd2\u529f\uff0c\u4f46\u4ece\u8fd9\u4e00\u5bf9 \u638c\u4e4b\u95f4\uff0c\u5979\u4e5f\u8bd5\u51fa\u4e86\u6587\u5ef7\u58c1\u7684\u771f\u5b9e\u529f\u529b\uff0c\u786e\u4e5f\u6bd4\u4ece\u524d\u589e\u52a0\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u5373\u4f7f\u4ed6\u4e0d\u7528\u6bd2\u529f\uff0c\u81ea\u5df1\u4e5f\u672a\u5fc5\u662f\u4ed6\u5bf9\u624b\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u563f\u563f\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u5f53\u771f\u662f\u5929\u7f51\u6062\u6062\uff0c\u758f\u800c\u4e0d\u6f0f\uff0c\u4e0a\u6b21\u5728\u6606\u5e03\u5170\u56fd\u7ed9\u4f60\u4fa5\u5e78\u6f0f\u7f51 \uff0c\u8fd9\u6b21\u4f60\u5374\u81ea\u5df1\u9001\u4e0a\u95e8\u6765\u4e86\uff01\u8fd9\u53eb\u505a\uff1a\u5929\u5802\u6709\u8def\u4f60\u4e0d\u8d70\uff0c\u5730\u72f1\u65e0\u95e8\u4f60\u504f\u8fdb\u6765\uff01\u8fd8\u60f3\u9003\u4e48\uff1f\u201d \u5927\u8896\u98de\u626c\uff0c\u5f3a\u52b2\u7684\u8896\u98ce\u626b\u8fc7\uff0c\u4e58\u673a\u8fd8\u5f39\u51fa\u4e86\u4e09\u79cd\u6bd2\u7c89\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u867d\u7136\u542b\u6709\u78a7\u7075\u4e39\uff0c\u4e5f\u4e0d\u7981\u9635\u9635\u6655\u7729\uff0c\u8fde\u5fd9\u5012\u7eb5\u95ea\u907f\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7ae5\u59e5\u59e5\u5df2\u4e00\u624b\u6293\u6765\uff0c\u8c37\u4e2d\u83b2\u5728\u6655\u7729\u4e4b\u4e2d\uff0c\u7acb\u8db3\u4e5f\u5c1a\u672a\u7a33\uff0c\u81ea\u662f\u65e0\u529b\u76f8 \u6297\uff0c\u88ab\u7ae5\u59e5\u59e5\u4e00\u628a\u5c31\u62ff\u7740\u5979\u7684\u8155\u8109\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u62ff\u4e86\u8c37\u4e4b\u534e\u7684\u5f1f\u5b50\uff0c\u540e\u65e5\u9099\u5c71\u4e4b\u4f1a\uff0c\u66f4\u662f\u53ef\u64cd\u80dc\u5238\u4e86\u3002\u201d\u7ae5\u59e5 \u59e5\u5219\u51b7\u7b11\u9053\uff1a\u201c\u5979\u662f\u79c1\u4f1a\u5361\u5170\u59ae\u7684\u3002\u5728\u6606\u5e03\u5170\u56fd\u4e4b\u65f6\uff0c\u6211\u65e9\u5df2\u6000\u7591\u5361\u5170\u59ae\u548c\u5979\u6697\u4e2d\u52fe\u7ed3\uff0c\u679c \u7136\u4e0d\u9519\u3002\u597d\u5427\uff0c\u5c31\u5c06\u5979\u4e0e\u5361\u5170\u59ae\u5173\u5728\u4e00\u8d77\uff0c\u8ba9\u5979\u4eec\u53d9\u53d9\u65e7\u60c5\u3002\u201d \u3000\u3000\u539f\u6765\u5929\u9b54\u6559\u4e3b\u4e0e\u5389\u590d\u751f\u5df2\u88ab\u56da\u5728\u5bc6\u5ba4\uff0c\u5374\u7531\u7f2a\u592b\u4eba\u5192\u5145\u6559\u4e3b\u3002\u7f2a\u592b\u4eba\u662f\u5929\u9b54\u6559\u4e3b\u7684\u59d0\u59d0 \uff0c\u4e24\u4eba\u76f8\u8c8c\u672c\u6765\u6709\u4e03\u516b\u5206\u76f8\u4f3c\uff0c\u66f4\u52a0\u4ee5\u5929\u9b54\u6559\u6559\u89c4\u68ee\u4e25\uff0c\u6559\u5f92\u8fdb\u8c12\uff0c\u53ea\u80fd\u5728\u9636\u4e0b\u7ad9\u7acb\uff0c\u77bb\u4ef0 \u989c\u8272\u3002 \u3000\u3000\u7f28\u592b\u4eba\u81ea\u5df1\u53c8\u6709\u8d34\u8eab\u4e2b\u5934\uff0c\u4e0d\u987b\u5929\u9b54\u6559\u4e3b\u539f\u6765\u7684\u4f8d\u5973\u670d\u4f8d\uff0c\u6545\u6b64\u4e0d\u4f46\u7792\u8fc7\u4e86\u5916\u4eba\uff0c\u8fde\u5929 \u9b54\u6559\u4e2d\u7684\u5927\u5c0f\u9996\u9886\uff0c\u4e5f\u90fd\u7792\u8fc7\u4e86\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u8fd9\u65f6\u6b63\u5728\u5fcd\u53d7\u7740\u201c\u8d70\u706b\u5165\u9b54\u201d\u7684\u714e\u71ac\uff0c\u5389\u590d\u751f\u4e5f\u88ab\u7ae5\u59e5\u59e5\u7528\u201c\u9165\u9aa8\u6563\u201d\u6bd2\u5bb3\u4e86 \uff0c\u4ed6\u5185\u529f\u6d88\u5931\uff0c\u867d\u7136\u8fd8\u80fd\u8d70\u52a8\uff0c\u6c14\u529b\u5df2\u662f\u8fde\u4e00\u4e2a\u666e\u901a\u4eba\u90fd\u6bd4\u4e0b\u4e0a\u4e86\u3002\u6587\u5ef7\u58c1\u5c06\u4ed6\u4eec\u4e24\u4eba\u5173\u5728 \u4e00\u5ba4\uff0c\u8981\u8feb\u5389\u590d\u751f\u5c06\u4ed6\u7684\u5bb6\u4f20\u5185\u529f\u5fc3\u6cd5\u5199\u51fa\u6765\u3002\u5389\u590d\u751f\u7518\u613f\u4e0e\u5929\u9b54\u6559\u4e3b\u540c\u751f\u5171\u6b7b\uff0c\u5bf9\u6587\u5ef7\u58c1 \u5374\u662f\u4e0d\u80af\u5c48\u4ece\u3002 \u3000\u3000\u4e24\u4eba\u6b63\u5728\u9ed1\u6697\u4e2d\u4e92\u76f8\u504e\u501a\uff0c\u5ffd\u542c\u5f97\u8f67\u8f67\u58f0\u54cd\uff0c\u77f3\u95e8\u6253\u5f00\uff0c\u5149\u7ebf\u900f\u4e86\u8fdb\u6765\uff0c\u5929\u9b54\u6559\u4e3b\u5b9a\u775b \u4e00\u770b\uff0c\u53ea\u89c1\u662f\u4e2a\u5c11\u5973\u88ab\u63a8\u8fdb\u6765\uff0c\u5929\u9b54\u6559\u4e3b\u770b\u6e05\u695a\u4e86\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u5c06\u8c37\u4e2d\u83b2\u62c9\u5230\u8eab\u8fb9\uff0c\u54fd\u54bd\u8bf4 \u9053\uff1a\u201c\u8c37\u59d1\u5a18\uff0c\u4f60\u4f55\u82e6\u524d\u6765\u770b\u6211\uff1f\u6211\u662f\u4e2a\u82e6\u547d\u4eba\uff0c\u73b0\u4eca\u53c8\u6b63\u906d\u53d7\u8d70\u706b\u5165\u9b54\u4e4b\u52ab\uff0c\u6b7b\u4e0d\u8db3\u60dc\uff0c \u4f60\u8fd9\u4e00\u6765\uff0c\u5374\u662f \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u4f60\u5b58\u5fc3\u5411\u5584\uff0c\u5fc5\u5b9a\u80fd\u591f\u9022\u51f6\u5316\u5409\uff0c\u9047\u96be\u6210\u7965\u3002\u201d\u5929\u9b54\u6559\u4e3b\u82e6\u7b11\u9053\uff1a \u201c\u6211\u5df2\u662f\u88ab\u638c\u63e1\u5728\u5e08\u7236\u624b\u5fc3\uff0c\u547d\u5728\u987b\u81fe\uff0c\u54ea\u8fd8\u6709\u91cd\u51fa\u751f\u5929\u4e4b\u671b\uff1f\u201d \u3000\u3000\u5389\u590d\u751f\u6124\u7136\u8bf4\u9053\uff1a\u201c\u90a3\u8001\u5996\u5a46\u5982\u6b64\u72e0\u6bd2\uff0c\u4e8f\u4f60\u8fd8\u53eb\u5979\u5e08\u7236\u3002 \u3000\u3000\u59d0\u59d0\uff0c\u4f60\u82e5\u6b7b\u4e86\uff0c\u6211\u4e5f\u4e49\u4e0d\u72ec\u751f\u3002\u201d\u5929\u9b54\u6559\u4e3b\u6cea\u5149\u83b9\u7136\uff0c\u4e5f\u987e\u4e0d\u5f97\u8c37\u4e2d\u83b2\u5c31\u5728\u65c1\u8fb9\uff0c\u4fbf \u4e0e\u5389\u590d\u751f\u7d27\u7d27\u76f8\u62b1\uff0c\u8bf4\u9053\uff1a\u201c\u5f1f\u5f1f\uff0c\u4f60\u5bf9\u6211\u8fd9\u6837\u597d\uff0c\u6211\u6b7b\u4e5f\u7791\u76ee\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5728\u65c1\u8fb9\u5341\u5206\u96be\u8fc7\uff0c\u5374\u60f3\u4e0d\u51fa\u4ec0\u4e48\u5b89\u6170\u7684\u8a00\u8bcd\u3002\u518d\u4e00\u60f3\uff0c\u5982\u4eca\u81ea\u5df1\u7684\u547d\u8fd0\u4e5f\u6b63\u662f\u4e0e \u5929\u9b54\u6559\u4e3b\u76f8\u540c\uff0c\u5979\u8fd8\u6709\u4e00\u4e2a\u5389\u590d\u751f\u5728\u5979\u8eab\u8fb9\uff0c\u4e0e\u5979\u540c\u751f\u5171\u6b7b\uff0c\u800c\u81ea\u5df1\u5fc3\u4e0a\u7684\u60c5\u90ce\u5374\u4e0d\u77e5\u8eab\u5728 \u4f55\u5904\uff1f\u8bf4\u4e0d\u5b9a\u6b63\u5728\u53e6\u4e00\u4e2a\u5973\u5b50\u7684\u8eab\u8fb9\uff1f\u8c37\u4e2d\u83b2\u867d\u7136\u6709\u5fc3\u5c06\u6c5f\u6d77\u5929\u8ba9\u7ed9\u534e\u4e91\u78a7\uff0c\u4f46\u5230\u4e86\u6b64\u65f6\uff0c \u601d\u60f3\u8d77\u6765\uff0c\u4e5f\u4e0d \u3000\u3000\u5389\u590d\u751f\u5ffd\u9053\uff1a\u201c\u8c37\u59d1\u5a18\uff0c\u6211\u6709\u4e00\u4e2a\u7591\u56e2\uff0c\u4e34\u6b7b\u4e4b\u524d\u60f3\u8981\u77e5\u9053\u3002 \u3000\u3000\u4f60\u5e08\u7236\u6068\u4e0d\u6068\u91d1\u4e16\u9057\uff1a\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u6068\u4ed6\uff1f\u201d \u3000\u3000\u5389\u590d\u751f\u9053\uff1a\u201c\u4ed6\u8f9c\u8d1f\u4e86\u4f60\u5e08\u7236\u7684\u4e00\u4e16\u6df1\u60c5\uff01\u201d\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5389\u80dc\u7537\u662f\u4f60\u4ec0\u4e48\u4eba\uff1f\u201d\u5389\u590d \u751f\u9053\uff1a\u201c\u662f\u6211\u59d1\u59d1\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\uff1a\u201c\u4f60\u59d1\u59d1\u7684\u5168\u90e8\u6545\u4e8b\uff0c\u4f60\u90fd\u77e5\u9053\u4e86\u4e48\uff1f\u201d\u5389\u590d\u751f\u9053\uff1a \u3000\u3000\u201c\u542c\u8bf4\u5979\u662f\u7ed9\u91d1\u4e16\u9057\u5bb3\u6b7b\u7684\uff0c\u4f46\u5185\u4e2d\u8be6\u60c5\uff0c\u6211\u5374\u8fd8\u4e0d\u5341\u5206\u6e05\u695a\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u59d1\u59d1\u6027\u60c5\u504f\u6fc0\uff0c\u5979\u8981\u5f97\u5230\u91d1\u5927\u4fa0\u7684\u5fc3\uff0c\u4e8e\u662f\u5411\u6211\u5e08\u7236\u4e0b\u4e86\u6bd2\u624b\uff0c\u7136\u540e\u4ee5 \u89e3\u836f\u4f5c\u4e3a\u8981\u80c1\uff0c\u8feb\u91d1\u5927\u4fa0\u548c\u5979\u6210\u5a5a\u3002 \u3000\u3000\u5979\u662f\u5728\u6d1e\u623f\u4e4b\u591c\u81ea\u6740\u7684\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5c06\u5979\u542c\u8fc7\u7684\u91d1\u3001\u5389\u5f53\u5e74\u4e4b\u4e8b\uff0c\u590d\u8ff0\u51fa\u6765\uff0c\u7136\u540e\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u81ea\u89c9\u5bf9\u4f60\u59d1\u59d1 \u4e0d\u4f4f\uff0c\u662f\u4ee5\u4f60\u59d1\u59d1\u6b7b\u540e\uff0c\u5230\u5982\u4eca\u5df2\u6709\u4e8c\u5341\u591a\u5e74\uff0c\u4ed6\u59cb\u7ec8\u6ca1\u6709\u7ed3\u5a5a\u3002\u6211\u5e08\u7236\u660e\u767d\u4ed6\u7684\u5fc3\u4e8b\uff0c\u5979 \u65e2\u6ca1\u6709\u6028\u6068\u91d1\u5927\u4fa0\uff0c\u4e5f\u6ca1\u6709\u6028\u6068\u4f60\u7684\u59d1\u59d1\uff0c\u5979\u8ba4\u4e3a\u8fd9\u53ea\u662f\u4e00\u573a\u60c5\u5b7d\u3002 \u3000\u3000\u5176\u5b9e\u4f60\u59d1\u59d1\u4e34\u6b7b\u4e4b\u524d\uff0c\u5374\u662f\u66fe\u6709\u9057\u8a00\u7559\u7ed9\u91d1\u5927\u4fa0\uff0c\u8981\u4ed6\u548c\u6211\u5e08\u7236\u6210\u5a5a\u7684\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u5446\u4e86\u534a\u54cd\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u521d\u5230\u4e2d\u539f\u7684\u90a3\u51e0\u5e74\uff0c\u542c\u4fe1\u4eba\u8a00\uff0c\u5bf9\u91d1\u4e16\u9057\u5207\u9f7f\u75db\u6068\uff0c\u540e\u6765 \u6e10\u6e10\u8d77\u4e86\u7591\u5fc3\uff0c\u73b0\u5728\u662f\u5b8c\u5168\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4f60\u4ece\u524d\u7591\u5fc3\u4ec0\u4e48\uff1f\u73b0\u5728\u53c8\u660e\u767d\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5389\u590d\u751f\u9053\uff0c\u201c\u6700\u521d\u6211\u5bf9\u91d1\u4e16\u9057\u5207\u9f7f\u75db\u6068\uff0c\u53ea\u9053\u662f\u4ed6\u5bb3\u4e86\u6211\u7684\u59d1\u59d1\u3002\u540e\u6765\u89c1\u4e86\u91d1\u4e16\u9057\u7ed9\u6211 \u59d1\u59d1\u6240\u7acb\u7684\u5893\u7891\uff0c\u53c8\u77e5\u9053\u4e86\u4f60\u7684\u5e08\u7236\u672c\u6765\u662f\u4ed6\u4ece\u524d\u7684\u7231\u4fa3\uff0c\u8fd8\u5728\u6211\u59d1\u59d1\u4e4b\u524d\u3002\u6211\u5c31\u4e0d\u7531\u5f97\u8fd9 \u6837\u60f3\u9053\uff0c\u4ee5\u9099\u5c71\u6d3e\u638c\u95e8\u8c37\u5973\u4fa0\u7684\u4eba\u54c1\u624d\u8c8c\uff0c\u5730\u4f4d\u6b66\u529f\uff0c\u54ea\u4e00\u6837\u4e0d\u662f\u4e0a\u4e0a\u4e4b\u9009\uff1f \u3000\u3000\u201c\u4e00\u822c\u4eba\u6b7b\u4e86\u59bb\u5b50\uff0c\u7eed\u5f26\u4e5f\u662f\u5e73\u5e38\u4e4b\u4e8b\uff0c\u4f55\u51b5\u91d1\u4e16\u9057\u548c\u6211\u7684\u59d1\u59d1\u4e0d\u8fc7\u662f\u6302\u540d\u592b\u59bb\uff1f\u4ed6\u5374 \u5b81\u613f\u820d\u5f03\u4e86\u53ef\u4ee5\u7ed3\u5408\u7684\u5982\u82b1\u7f8e\u7737\uff0c\u72ec\u81ea\u98d8\u6cca\u6c5f\u7322\uff0c\u8f7b\u5ea6\u4e86\u4f3c\u6c34\u6d41\u5e74\uff1b\u8fd9\u4e48\u770b\u6765\uff0c\u4ed6\u5bf9\u6211\u7684\u59d1 \u59d1\uff0c\u4f3c\u4e4e\u4e5f\u4e0d\u80fd\u8bf4\u662f\u65e0\u60c5\u65e0\u4e49\u3002\u90a3\u4e9b\u4eba\u5bf9\u91d1\u4e16\u9057\u8bfd\u8c24\u7684\u8a00\u8f9e\uff0c\u5927\u62b5\u662f\u548c\u4ed6\u672c\u6765\u5c31\u6709\u4ec7\u6068\uff0c\u56e0 \u800c\u8981\u6311\u62e8\u6211\u548c\u4ed6 \u3000\u3000\u8c37\u4e2d\u83b2\u559c\u9053\uff1a\u201c\u4f60\u7ec8\u4e8e\u60f3\u660e\u767d\u4e86\uff0c\u90a3\u4e48\u4f60\u8fd8\u6068\u91d1\u5927\u4fa0\u4e48\uff1f\u201d\u5389\u590d\u751f\u9053\uff1a\u201c\u6211\u53ea\u89c9\u5f97\u60ed\u6127 \uff0c\u6211\u4ece\u524d\u672a\u660e\u771f\u76f8\uff0c\u66fe\u5c61\u6b21\u8fb1\u9a82\u4e8e\u4ed6\uff0c\u4ed6\u5fc3\u91cc\u4e00\u5b9a\u662f\u96be\u8fc7\u6781\u4e86\u3002\u53ef\u60dc\u6211\u5982\u4eca\u4ea6\u662f\u547d\u5728\u987b\u81fe\uff0c \u518d\u4e5f\u6ca1\u6709\u673a\u4f1a\u5411\u4ed6\u8868\u767d\u6211\u5fc3\u4e2d\u7684\u6b49\u610f\u4e86\u3002\u201d \u3000\u3000\u4e09\u4eba\u4e92\u8c08\u5fc3\u4e8b\uff0c\u6b63\u5728\u550f\u5618\u53f9\u606f\u4e4b\u95f4\uff0c\u5ffd\u89c9\u6709\u4e00\u7f15\u6de1\u6de1\u7684\u5e7d\u9999\uff0c\u4e2d\u4eba\u5982\u9189\uff0c\u5929\u9b54\u6559\u4e3b\u60ca\u9053 \uff1a\u201c\u4e0d\u597d\uff0c\u6211\u5e08\u7236\u6765\u4e0b\u6bd2\u624b\u4e86\u3002\u201d\u5389\u590d\u751f\u6012\u9053\uff1a\u201c\u597d\uff0c\u6211\u548c\u5979\u62fc\u53bb\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u4e5f\u8fd8\u6765\u4e0d \u53ca\u65bd\u5c55\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\uff0c\u5495\u549a\u4e00\u58f0\uff0c\u5c31\u6655\u5012\u5730\u4e0a\uff0c\u5929\u9b54\u6559\u4e3b\u6643\u4e86\u4e24\u6643\uff0c\u4e5f\u76f8\u7ee7\u6655\u9053\u3002\u5374\u53ea\u6709\u8c37 \u4e2d\u83b2\u4e00\u4eba\u8fd8\u4fdd\u6301 \u3000\u3000\u539f\u6765\u5929\u9b54\u6559\u4e3b\u548c\u5389\u590d\u751f\u529f\u529b\u5df2\u5931\uff0c\u5373\u4f7f\u662f\u5bfb\u5e38\u7684\u8ff7\u9999\u4e5f\u7981\u53d7\u4e0d\u8d77\uff0c\u4f55\u51b5\u6765\u4eba\u6240\u7528\u7684\u4e43\u662f \u4e00\u79cd\u4e16\u4e0a\u6700\u795e\u5947\u7684\u8ff7\u9999\uff1f\u4f46\u8c37\u4e2d\u83b2\u5219\u56e0\u53e3\u4e2d\u542b\u6709\u78a7\u7075\u4e39\uff0c\u6070\u6070\u53ef\u4ee5\u89e3\u8fd9\u8ff0\u9999\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u6b63\u5728\u60ca\u614c\uff0c\u5ffd\u89c1\u94c1\u7a97\u4e0a\u7684\u94c1\u679d\u5bf8\u5bf8\u788e\u88c2\uff0c\u8c37\u4e2d\u83b2\u4e0d\u89c9\u5947\u602a\u8d77\u6765\uff0c\u201c\u90a3\u5996\u5a46\u82e5\u8981\u8fdb \u6765\uff0c\u4e3a\u4f55\u4e0d\u8d70\u6b63\u95e8\uff1f\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u4e00\u6761\u9ed1\u5f71\u5df2\u4ece\u94c1\u7a97\u7a9c\u8fdb\uff0c\u6e05\u51b7\u7684\u6708\u5149\u4e5f\u900f\u8fc7\u4e86\u6253\u5f00\u7684 \u94c1\u7a97\uff0c\u867d\u7136\u4e0d\u5f88\u660e\u4eae\uff0c\u8c37\u4e2d\u83b2\u5df2\u8ba4\u5f97\u8fd9\u4e0d\u901f\u4e4b\u5ba2\uff0c\u6b63\u662f\u4ed6\u4eec\u521a\u521a\u8c08\u53ca\u7684\u91d1\u4e16\u9057\uff01 \u3000\u3000\u8c37\u4e2d\u83b2\u60ca\u559c\u4ea4\u5e76\uff0c\u53eb\u9053\uff1a\u201c\u91d1\u5927\u5feb\uff0c\u4f60\u6765\u4e86\uff0c\u8fd9\u53ef\u597d\u4e86\u3002\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u5148\u6551\u4ed6\u4eec\u51fa\u53bb \uff0c\u6682\u4e14\u4e0d\u5fc5\u60ca\u52a8\u90a3\u4e2a\u5996\u5a46\uff0c\u7559\u5230\u9099\u5c71\u4e4b\u4f1a\uff0c\u518d\u4e0e\u5979\u6162\u6162\u7b97\u5e10\u3002\u4f60\u6765\u80cc\u5929\u9b54\u6559\u4e3b\u5427\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u6b63\u60f3\u8bf4\u5979\u6c14\u529b\u6d88\u5931\uff0c\u96be\u4ee5\u65bd\u5c55\u8f7b\u529f\uff0c\u91d1\u4e16\u9057\u5df2\u5728\u5979\u80cc\u540e\u8f7b\u8f7b\u62cd\u4e86\u4e00\u4e0b\uff0c\u8c37\u4e2d\u83b2\u77ac \u606f\u4e4b\u95f4\uff0c\u53ea\u89c9\u4e00\u80a1\u9633\u548c\u4e4b\u6c14\uff0c\u5df2\u8d2f\u901a\u4e86\u5979\u7684\u5947\u7ecf\u516b\u8109\uff0c\u767b\u65f6\u7cbe\u795e\u6296\u6402\uff0c\u66f4\u80dc\u4ece\u524d\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u80cc\u8d77\u5929\u9b54\u6559\u4e3b\uff0c\u968f\u7740\u91d1\u4e16\u9057\u8df3\u51fa\u94c1\u7a97\uff0c\u4e00\u8def\u8d70\u53bb\uff0c\u53ea\u89c1\u752c\u9053\u4e0a\u6a2a\u4e03\u7ad6\u516b\u7684\u5929\u9b54\u6559 \u5f92\u5012\u4e86\u6ee1\u5730\uff0c\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u5929\u9b54\u6559\u5584\u7528\u8ff7\u9999\uff0c\u6211\u8fd9\u6b21\u662f\u4ee5\u5176\u4eba\u4e4b\u9053\uff0c\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\u3002\u201d\u539f \u6765\u91d1\u4e16\u9057\u7528\u7684\uff0c\u4e43\u662f\u5f97\u81ea\u897f\u85cf\u5bc6\u5b97\u4ee5\u963f\u4fee\u7f57\u82b1\u6240\u79d8\u5236\u7684\u8ff7\u9999\uff0c\u836f\u529b\u4e4b\u5f3a\uff0c\u5728\u4efb\u4f55\u4e00\u79cd\u8ff7\u9999\u4e4b \u4e0a\uff0c\u91d1\u4e16\u9057\u662f\u56e0 \u3000\u3000\u8d8a\u8fc7\u540e\u56f4\u56f4\u5899\uff0c\u5230\u4e86\u90a3\u5ced\u58c1\u4e4b\u4e0b\uff0c\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u5148\u4e0a\u53bb\uff0c\u518d\u540a\u4f60\u4eec\u4e0a\u6765\u3002\u201d\u4ed6\u80cc\u7740\u5389 \u590d\u751f\uff0c\u7eb5\u8eab\u800c\u4e0a\uff0c\u53ea\u987b\u811a\u5c16\u8f7b\u8f7b\u4e00\u78b0\u77f3\u58c1\uff0c\u4fbf\u5373\u817e\u7a7a\u800c\u8d77\uff0c\u5728\u5ced\u58c1\u4e0a\u7eb5\u8dc3\u5982\u98de\uff0c\u5982\u5c65\u5e73\u5730\uff0c \u8f6c\u773c\u95f4\u5df2\u5230\u4e86\u4e0a\u9762\u3002\u8c37\u4e2d\u83b2\u5fc3\u9053\uff0c\u201c\u4e16\u4e0a\u7adf\u6709\u5982\u6b64\u5353\u8d8a\u7684\u8f7b\u529f\uff0c\u82e5\u975e\u4eb2\u773c\u89c1\u5230\uff0c\u5f53\u771f\u96be\u4ee5\u601d \u8bae\u3002\u6211\u4e00\u4e2a\u4eba\u501f \u3000\u3000\u91d1\u4e16\u9057\u626f\u4e0b\u51e0\u6761\u957f\u85e4\uff0c\u8fde\u7ed3\u8d77\u6765\uff0c\u629b\u4e0b\u53bb\u7ed9\u8c37\u4e2d\u83b2\uff0c\u8c37\u4e2d\u83b2\u4e00\u624b\u631f\u7740\u5929\u9b54\u6559\u4e3b\uff0c\u4e00\u624b\u6293 \u7262\u957f\u85e4\uff0c\u8ba9\u91d1\u4e16\u9057\u5c06\u5979\u4eec\u626f\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5929\u9b54\u6559\u4e3b\u5df2\u6709\u6539\u6076\u4ece\u5584\u4e4b\u5fc3\uff0c\u5979\u73b0\u5728\u6b63\u906d\u53d7\u8d70\u706b\u5165\u9b54\u4e4b\u52ab\u3002\u2026\u2026\u201d\u91d1\u4e16\u9057 \u9053\uff1a\u201c\u6211\u5df2\u7ecf\u77e5\u9053\u4e86\u3002\u6211\u6b64\u6765\u6b63\u662f\u4e3a\u4e86\u6551\u5979\uff0c\u597d\u8ba9\u5979\u4e0e\u5389\u590d\u751f\u5f97\u5168\u5fc3\u613f\u3002\u201d\u5f53\u4e0b\u53eb\u8c37\u4e2d\u83b2\u5c06 \u5929\u9b54\u6559\u4e3b\u653e\u4e86\u4e0b\u6765\uff0c\u5148\u628a\u4e00\u9897\u78a7\u7075\u4e39\u585e\u8fdb\u5979\u7684\u5634\u5df4\uff0c\u968f\u5373\u65bd\u5c55\u7edd\u4e16\u795e\u529f\uff0c\u70b9\u5929\u9b54\u6559\u4e3b\u7684\u4e09\u5341 \u516d\u9053\u5927\u7a74\u3002\u4ed6\u6bcf \u3000\u3000\u91d1\u4e16\u9057\u6ee1\u5934\u5927\u6c57\uff0c\u6c14\u5598\u5401\u5401\uff0c\u7b11\u9053\uff1a\u201c\u5e78\u4e8f\u5979\u8d70\u706b\u5165\u9b54\u4e0d\u8fc7\u4e24\u5929\uff0c\u82e5\u662f\u65f6\u95f4\u518d\u957f\u3002\u6211\u5c31 \u8981\u591a\u8017\u51e0\u5e74\u529f\u529b\u4e86\u3002\u201d\u4ee5\u91d1\u4e16\u9057\u8fd9\u7b49\u4e16\u4e0a\u65e0\u53cc\u7684\u529f\u529b\uff0c\u52a9\u4eba\u5ea6\u52ab\uff0c\u5c1a\u4e14\u5982\u6b64\u5403\u529b\uff0c\u906d\u53d7\u8d70\u706b \u5165\u9b54\u7684\u51f6\u9669\uff0c\u53ef\u60f3\u800c\u77e5\u3002\u8c37\u4e2d\u83b2\u4e0d\u7981\u9a87\u7136\u3002 \u3000\u3000\u91d1\u4e16\u9057\u7a0d\u6b47\u4e00\u4f1a\uff0c\u518d\u4ee5\u672c\u8eab\u771f\u529b\uff0c\u52a9\u5389\u590d\u751f\u6d88\u89e3\u90a3\u201c\u9165\u9aa8\u6563\u201d\u4e4b\u6bd2\uff0c\u8fd9\u5bf9\u4e8e\u91d1\u4e16\u9057\u6765\u8bf4 \uff0c\u5374\u662f\u4e3e\u624b\u4e4b\u52b3\uff0c\u65e0\u987b\u4f3c\u521a\u624d\u7684\u8d39\u4e3a\u4e86\u3002 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u529f\u529b\u6062\u590d\uff0c\u78a7\u7075\u4e39\u4e5f\u5373\u89c1\u6548\uff0c\u6070\u597d\u4e0e\u5389\u590d\u751f\u540c\u65f6\u9192\u6765\u3002\u4e24\u4eba\u4e00\u7741\u773c\u5c31\u770b\u89c1\u91d1\u4e16 \u9057\u7ad9\u5728\u4ed6\u4eec\u7684\u9762\u524d\uff0c\u90fd\u662f\u5403\u4e86\u4e00\u60ca\u3002\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5389\u590d\u751f\u53c8\u60ca\u53c8\u559c\uff0c\u987e\u4e0d\u5f97\u4e0e\u91d1\u4e16\u9057\u5bd2\u6684\uff0c\u4fbf\u5373\u56b7\u9053\uff1a\u201c\u54a6\uff0c\u59d0\u59d0\uff0c\u4f60\u600e\u4e48\u8df3\u5f97\u8d77\u6765\u4e86\uff1f \u201d\u5929\u9b54\u6559\u4e3b\u5446\u4e86\u4e00\u5446\uff0c\u5df2\u662f\u604d\u7136\u5927\u609f\uff0c\u6cea\u76c8\u4e8e\u776b\uff0c\u4fbf\u5411\u91d1\u4e16\u9057\u88e3\u887d\u65bd\u793c\uff0c\u54fd\u54bd\u8bf4\u9053\uff1a\u201c\u91d1\u5927 \u4fa0\uff0c\u591a\u8c22\u4f60\u4e86\u3002\u6211\u662f\u4eba\u4eba\u9119\u5f03\u7684\u90aa\u6d3e\u4e2d\u4eba\uff0c\u4f60\u5374\u8d39\u4e86\u5982\u8bb8\u6c14\u529b\u6551\u6211\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u7978\u798f\u65e0\u95e8\u552f\u81ea\u53ec\uff0c\u4e00\u5fc3\u5411\u5584\u4fbf\u975e\u9b54\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000----------- \u3000\u3000\u98ce\u4e91\u9601 \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\u2014\u2014\u7b2c\u4e94\u5341\u516d\u56de\u3000\u7389\u5323\u8fd8\u4e66\u6d88\u5bbf\u6028\u3000\u51b0\u6cb3\u6d17\u5251\u5e86\u5347\u5e73 \u6881\u7fbd\u751f\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b \u7b2c\u4e94\u5341\u516d\u56de\u3000\u7389\u5323\u8fd8\u4e66\u6d88\u5bbf\u6028\u3000\u51b0\u6cb3\u6d17\u5251\u5e86\u5347\u5e73 \u3000\u3000\u91d1\u4e16\u9057\u6005\u89e6\u524d\u5c18\uff0c\u559f\u7136\u53f9\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u524d\uff0c\u6211\u4e5f\u66fe\u9669\u53d7\u8d70\u706b\u5165\u9b54\u4e4b\u52ab\uff0c\u662f\u5929\u5c71\u5510\u8001\u638c\u95e8 \u5c06\u6211\u6551\u4e86\uff0c\u90a3\u65f6\u6211\u4e5f\u662f\u6b63\u6d3e\u4e2d\u4eba\u6240\u9119\u5f03\u7684\u5996\u90aa\uff0c\u4f60\u4e0d\u5fc5\u8c22\u6211\uff0c\u4f46\u613f\u4f60\u65e5\u540e\u4e5f\u6548\u6cd5\u524d\u8f88\u82f1\u5939\u7684 \u6240\u4e3a\u3002 \u3000\u3000\u5c3d\u4e00\u5df1\u4e4b\u529b\uff0c\u4e0e\u4eba\u4e3a\u5584\uff0c\u90a3\u5c31\u662f\u62a5\u7b54\u4e86\u6211\uff0c\u529f\u5fb7\u65e0\u91cf\u4e86\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u5927\u53d7\u611f\u6fc0\uff0c\u4e0a\u524d\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u6211\u73b0\u5728\u624d\u77e5\u9053\u4f60\u662f\u4e2a\u597d\u4eba\uff0c\u597d\u5230\u51fa\u4e4e\u6211\u7684\u60f3\u8c61 \u4e4b\u5916\u3002\u6211\u51e0\u6b21\u5192\u72af\u4e86\u4f60\uff0c\u6211\u5f88\u60ed\u6127\uff0c\u771f\u4e0d\u77e5\u3001\u4e0d\u77e5\u8bf4\u4e9b\u4ec0\u4e48\u8bdd\u597d\uff1f\u2026\u2026\u201d \u3000\u3000\u91d1\u4e16\u9057\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u65e0\u987b\u518d\u8bf4\uff0c\u4f60\u7684\u5fc3\u4e8b\u6211\u5df2\u7ecf\u660e\u767d\uff0c\u4f60\u521a\u624d\u5728\u7262\u91cc\u548c\u4f60\u6559\u4e3b\u59d0\u59d0\u6240\u8bf4 \u7684\u8bdd\uff0c\u6211\u90fd\u5df2\u542c\u89c1\u4e86\u3002\u4f60\u53ef\u77e5\u9053\uff0c\u6211\u4e3a\u4e86\u627e\u4f60\uff0c\u4ece\u6606\u5e03\u5170\u56fd\u4e00\u76f4\u8ffd\u8e2a\u5230\u8fd9\u513f\uff1f\u201d\u5389\u590d\u751f\u6cea\u4e0b \u5982\u96e8\uff0c\u54fd\u54bd\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4f60\u4e3a\u4ec0\u4e48\u5bf9\u6211\u8fd9\u6837\u597d\uff1f\u4f60\u65e9\u5df2\u77e5\u9053\u6211\u662f\u5389\u5bb6\u7684\u5b64\u513f\u4e86\u4e48\uff1f\u201d\u539f \u6765\u5389\u590d\u751f\u662f\u5389\u5bb6 \u3000\u3000\u91d1\u4e16\u9057\u4e0d\u81ea\u7981\u7684\u773c\u89d2\u4e5f\u6c81\u51fa\u4e86\u6cea\u73e0\uff0c\u8bf4\u9053\uff1a\u201c\u81ea\u4ece\u4f60\u59d1\u59d1\u6b7b\u540e\uff0c\u4e8c\u5341\u5e74\u6765\uff0c\u6211\u4e00\u76f4\u4e3a\u4e86 \u6b64\u4e8b\u4f24\u5fc3\u96be\u8fc7\u3002\u6211\u4e0d\u4f46\u6127\u5bf9\u4f60\u7684\u59d1\u59d1\uff0c\u4e5f\u4f24\u5fc3\u4f60\u4eec\u5389\u5bb6\u7684\u906d\u9047\u4e4b\u60e8\uff0c\u5728\u4e2d\u539f\u4e00\u8109\uff0c\u7adf\u662f\u65e0\u4e00 \u5b58\u7559\u3002 \u3000\u3000\u65e0\u53ef\u601c\u89c1\uff0c\u5389\u5bb6\u6bd5\u7adf\u7559\u4e0b\u4f60\u8fd9\u682a\u6839\u82d7\u3002\u590d\u751f\uff0c\u4f60\u4e0d\u66fe\u89c1\u8fc7\u4f60\u7684\u59d1\u59d1\uff0c\u4f46\u4f60\u7684\u76f8\u8c8c\u6027\u60c5\uff0c \u90fd\u6709\u51e0\u5206\u4e0e\u4f60\u59d1\u59d1\u76f8\u4f3c\u3002\u6211\u7b2c\u4e00\u6b21\u89c1\u4f60\uff0c\u5c31\u4f3c\u770b\u89c1\u4f60\u7684\u59d1\u59d1\u590d\u6d3b\u5728\u6211\u7684\u773c\u524d\uff0c\u8fd9\u8bb8\u591a\u5e74\u6765\uff0c \u6211\u5ff5\u5ff5\u4e0d\u5fd8\u7684\u5c31\u662f\u60f3\u67e5\u660e\u4f60\u7684\u8eab\u4e16\uff0c\u4e3a\u4e86\u4e86\u7ed3\u4e00\u6869\u5fc3\u613f\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u91d1\u4e16\u9057\u7a81\u7136\u62ff\u51fa\u4e86\u4e00\u65b9\u7389\u5323\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u5389\u5bb6\u7684\u540e\u4ee3\uff0c\u4f60\u4eec\u5389\u5bb6\u5148\u7956\u7684\u6545 \u4e8b\uff0c\u60f3\u5fc5\u4f60\u662f\u77e5\u9053\u7684\u4e86\uff0c\u4f60\u7684\u5148\u7956\u5389\u6297\u5929\u662f\u660e\u672b\u6b66\u5b66\u5927\u5e08\u4e54\u5317\u6e9f\u7684\u5f1f\u5b50\uff0c\u4e54\u5317\u6e9f\u5f53\u5e74\u8d25\u5728\u5f20 \u4e39\u67ab\u5251\u4e0b\uff0c\u9003\u4ea1\u6d77\u5916\uff0c\u533f\u5c45\u8352\u5c9b\uff0c\u77e2\u5fd7\u62a5\u4ec7\uff0c\u7ec3\u6210\u4e86\u7edd\u4e16\u795e\u529f\uff0c\u5374\u672a\u80fd\u751f\u8fd8\u4e2d\u571f\u3002\u8fd9\u79d8\u5bc6\u53ea \u6709\u4f60\u4eec\u5389\u5bb6\u77e5\u9053 \u3000\u3000\u201c\u8fd9\u79d8\u7c4d\u540e\u6765\u4e3a\u4f60\u59d1\u59d1\u6240\u5f97\uff0c\u6700\u540e\u53c8\u4f20\u5230\u4e86\u6211\u7684\u624b\u4e0a\u3002\u90a3\u65f6\u4f60\u59d1\u59d1\u5df2\u6b7b\uff0c\u6211\u4ee5\u4e3a\u5389\u5bb6\u5df2 \u6ca1\u4e86\u540e\u4eba\uff0c\u53c8\u56e0\u4e3a\u8fd9\u79d8\u7c4d\u6240\u8f7d\u7684\u6b66\u529f\u867d\u7136\u90fd\u662f\u4eba\u95f4\u7f55\u89c1\u7684\u4e0a\u4e0a\u6b66\u529f\uff0c\u6bd5\u7adf\u8fd8\u662f\u90aa\u6d3e\uff0c\u6545\u800c\u6211 \u628a\u8fd9\u79d8\u7c4d\u5728\u4f60\u59d1\u59d1\u5893\u524d\u711a\u4e86\u3002\u4e0d\u8fc7\uff0c\u6211\u5374\u628a\u8fd9\u79d8\u7c4d\u7684\u6b66\u529f\u4f5c\u4e3a\u57fa\u77f3\uff0c\u4ee5\u6b63\u5b97\u7684\u5185\u529f\u5fc3\u6cd5\u4f5c\u4e3a \u6881\u67f1\uff0c\u53e6\u521b\u4e86\u4e00 \u3000\u3000\u5389\u590d\u751f\u60f6\u7136\u8bf4\u9053\uff0c\u201c\u91d1\u5927\u4fa0\uff0c\u8fd9\u662f\u4f60\u6bd5\u751f\u5fc3\u8840\uff0c\u6211\u5982\u4f55\u6562\u53d7\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u6709\u4eca\u65e5 \u7684\u6210\u5c31\uff0c\u8bf4\u6765\u4e5f\u662f\u53e8\u4f60\u5389\u5bb6\u7684\u5149\u3002\u4e54\u5317\u6e9f\u7684\u6b66\u529f\u79d8\u7c4d\u6211\u5df2\u711a\u4e86\uff0c\u65e0\u6cd5\u8fd8\u4f60\uff0c\u53ea\u6709\u5c06\u8fd9\u672c\u4e66\u66ff \u4ee3\u4e86\u3002\u4f60\u59d1\u59d1\u4e34\u7ec8\u5c06\u90a3\u79d8\u7c4d\u4ea4\u6211\uff0c\u5631\u6211\u5c06\u4e4b\u53d1\u626c\u5149\u5927\uff0c\u6211\u4fa5\u5e78\u4e0d\u8d1f\u6240\u6258\uff0c\u5982\u4eca\u6211\u5c06\u6b64\u4e66\u4ea4\u4f60 \uff0c\u4f60\u5c31\u8ba9\u6211\u4e86\u5374 \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5389\u53d4\u53d4\uff0c\u4f60\u5c31\u63a5\u4e0b\u6765\u5427\uff0c\u4f60\u82e5\u4e0d\u53d7\uff0c\u91d1\u5927\u4fa0\u5fc3\u91cc\u66f4\u8981\u96be\u8fc7\u7684\u3002\u201d\u8bdd\u8bf4\u81f3\u6b64 \uff0c\u5389\u590d\u751f\u96be\u518d\u63a8\u8f9e\uff0c\u5f53\u4e0b\u6cea\u6d41\u6ee1\u9762\uff0c\u63a5\u8fc7\u4e86\u90a3\u65b9\u7389\u5323\uff0c\u5fc3\u4e2d\u7684\u611f\u6fc0\uff0c\u5b9e\u662f\u96be\u4ee5\u8a00\u8bed\u5f62\u5bb9\u3002 \u3000\u3000\u91d1\u4e16\u9057\u4e86\u5374\u4e86\u591a\u5e74\u7684\u5fc3\u613f\uff0c\u4e5f\u662f\u767e\u611f\u4ea4\u96c6\uff0c\u8ffd\u601d\u524d\u4e8b\uff0c\u4e00\u7247\u60d8\u7136\u3002\u4ed6\u5401\u4e86\u53e3\u6c14\uff0c\u9065\u671b\u4e91 \u5929\uff0c\u60f3\u8d77\u4e86\u6614\u65e5\u4e0e\u5389\u80dc\u7537\u51fa\u6d77\u5bfb\u4e66\u7684\u79cd\u79cd\u7ecf\u5386\uff0c\u60f3\u8d77\u4e86\u5389\u80dc\u7537\u4e34\u7ec8\u7684\u5631\u5490\uff0c\u60f3\u8d77\u4e86\u81ea\u5df1\u5f53\u5e74 \u5728\u5389\u80dc\u7537\u5893\u524d\u711a\u4e66\u7684\u5fc3\u5883\u2026\u2026\u4e8c\u5341\u5e74\u6765\u7ecf\u8fc7\u4e86\u591a\u5c11\u4f24\u5fc3\u4e4b\u4e8b\uff0c\u5982\u4eca\u5f97\u89c1\u5389\u5bb6\u8fd8\u6709\u8fd9\u682a\u6839\u82d7\uff0c \u5fc3\u5934\u4e0a\u7684\u91cd\u538b\u8fd9 \u3000\u3000\u5389\u590d\u751f\u5ffd\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4f60\u7684\u5fc3\u613f\u5df2\u4e86\uff0c\u6211\u4e5f\u6709\u4e00\u6869\u5fc3\u613f\uff0c\u6211\u60f3\u6c42\u4f60\u4e00\u4ef6\u4e8b\uff0c\u4e0d\u77e5\u4f60\u53ef \u80af\u7b54\u5e94\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60\u5c3d\u7ba1\u8bf4\u5427\uff0c\u4e0d\u7ba1\u5982\u4f55\u8270\u96be\uff0c\u6211\u90fd\u4e00\u5b9a\u7ed9\u4f60\u505a\u5230\u3002\u201d \u3000\u3000\u5389\u590d\u751f\u8bf4\u9053\uff1a\u201c\u6211\u7684\u5fc3\u613f\u4e5f\u5c31\u662f\u6211\u59d1\u59d1\u7684\u5fc3\u613f\uff0c\u91d1\u5927\u4fa0\uff0c\u6211\u6c42\u4f60\u4e0d\u8981\u8d1f\u4e86\u6211\u59d1\u59d1\u7684\u4e00\u756a \u5fc3\u4e8b\uff0c\u4e34\u522b\u9057\u8a00\uff01\u201d\u91d1\u4e16\u9057\u5446\u4e86\u4e00\u5446\uff0c\u6115\u7136\u8bf4\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u2026\u2026\u201d \u3000\u3000\u5389\u590d\u751f\u9053\uff1a\u201c\u6211\u867d\u7136\u6ca1\u6709\u89c1\u8fc7\u6211\u7684\u59d1\u59d1\uff0c\u4f46\u5979\u5f53\u5e74\u7684\u4f8d\u5973\uff0c\u5982\u4eca\u8fd8\u5728\u5929\u9b54\u6559\u4e2d\uff0c\u6211\u4ece\u5979 \u7684\u53e3\u4e2d\uff0c\u4e5f\u5f97\u77e5\u4e00\u4e8c\uff0c\u81f3\u4e8e\u4f60\u548c\u9099\u5c71\u638c\u95e8\u8c37\u5973\u4fa0\u4e8c\u5341\u5e74\u6765\u7684\u523b\u9aa8\u94ed\u5fc3\u4e4b\u7231\uff0c\u81ea\u4f24\u81ea\u82e6\u4e4b\u60c5\uff0c \u521a\u624d\u8c37\u59d1\u5a18\u4e5f\u544a\u8bc9\u6211\u4e86\u3002\u201d \u3000\u3000\u4e8c\u5341\u5e74\u6765\uff0c\u4ece\u6ca1\u4eba\u5bf9\u91d1\u4e16\u9057\u8bf4\u8fc7\u8fd9\u6837\u7684\u8bdd\uff0c\u867d\u7136\u4ed6\u7684\u82e5\u5e72\u597d\u53cb\uff0c\u5982\u6c5f\u5357\u3001\u59ec\u6653\u98ce\u7b49\u4eba\uff0c \u4e5f\u90fd\u66fe\u529d\u8fc7\u4ed6\u548c\u8c37\u4e4b\u534e\u91cd\u7eed\u9e3e\u80f6\uff0c\u4f46\u4ed6\u4eec\u90fd\u4e0d\u662f\u4ece\u5389\u80dc\u7537\u8fd9\u65b9\u9762\u6765\u7740\u60f3\u7684\uff0c\u8bf4\u7684\u8bdd\u4e5f\u6ca1\u6709\u6293 \u7740\u91d1\u4e16\u9057\u7684\u75d2\u5904\uff0c\u800c\u4eca\u5389\u590d\u751f\u4ee5\u5389\u5bb6\u7684\u9057\u5b64\u8eab\u4efd\u6765\u8bf4\uff0c\u5374\u662f\u53e5\u53e5\u8bdd\u90fd\u8bf4\u5230\u4e86\u91d1\u4e16\u9057\u7684\u5fc3\u574e\u4e0a \u3002\u662f\u554a\uff0c\u201c\u8fd9\u4e8c \u3000\u3000\u6a21\u7cca\u6cea\u5f71\uff0c\u4e00\u7247\u8ff7\u832b\uff0c\u773c\u524d\u73b0\u51fa\u4e86\u5389\u80dc\u7537\u7684\u5f71\u5b50\uff0c\u91d1\u4e16\u9057\u604d\u60da\u53c8\u4f3c\u56de\u5230\u4e8c\u5341\u5e74\u524d\u90a3\u51c4\u51c9 \u7684\u6d1e\u623f\u4e4b\u591c\u4e86\uff0c\u5389\u80dc\u7537\u4e34\u7ec8\u7684\u4e00\u5e55\u91cd\u73b0\u4ed6\u7684\u5fc3\u5934\uff0c\u5979\u90a3\u82cd\u767d\u7684\u7136\u800c\u53c8\u662f\u6ee1\u8db3\u7684\u7b11\u5bb9\uff0c\u5979\u90a3\u51c4 \u60cb\u7684\u7136\u800c\u53c8\u662f\u8bed\u91cd\u5fc3\u957f\u7684\u5631\u5490\u2026\u2026 \u3000\u3000\u5389\u80dc\u7537\u662f\u5728\u6700\u540e\u4e00\u523b\uff0c\u83b7\u5f97\u4e86\u91d1\u4e16\u9057\u7684\u7231\u60c5\u542b\u7b11\u800c\u901d\u7684\uff0c\u91d1\u4e16\u9057\u76f8\u4fe1\u5979\u4e34\u7ec8\u7684\u5631\u5490\u4e5f\u90fd \u662f\u51fa\u81ea\u771f\u5fc3\uff0c\u53ef\u662f\u91d1\u4e16\u9057\u7531\u4e8e\u5185\u759a\u4e8e\u5fc3\uff0c\u5374\u6ca1\u6709\u4f9d\u4ece\u5979\u7684\u5631\u5490\u3002 \u3000\u3000\u5f80\u4e8b\u5386\u5386\uff0c\u90fd\u4e0a\u5fc3\u5934\uff0c\u5389\u80dc\u7537\u90a3\u756a\u8a00\u8bed\uff0c\u4e5f\u8fd8\u7559\u5728\u4ed6\u7684\u8033\u8fb9\uff0c\u4ed6\u4e00\u4e2a\u5b57\u4e5f\u672a\u5fd8\u8bb0\u3002\u5389\u80dc \u7537\u4e34\u7ec8\u5631\u5490\u4e8e\u4ed6\u7684\u662f\u4e09\u4ef6\u4e8b\uff0c\u7b2c\u4e00\u4ef6\u662f\u8981\u4ed6\u63a5\u53d7\u4e54\u5317\u6e9f\u7684\u6b66\u529f\u79d8\u7c4d\uff0c\u6f5c\u5fc3\u6b66\u5b66\uff0c\u505a\u4e00\u4e2a\u8d85\u8fc8 \u524d\u4eba\u7684\u6b66\u5b66\u5927\u5e08\uff1b\u7b2c\u4e8c\u4ef6\u662f\u8981\u4ed6\u5728\u5979\u6b7b\u540e\u4e0d\u53ef\u4f24\u5fc3\uff0c\u597d\u597d\u4fdd\u91cd\u81ea\u5df1\uff1b\u7b2c\u4e09\u4ef6\u662f\u8981\u4ed6\u548c\u8c37\u4e4b\u534e \u7ed3\u6210\u7737\u5c5e\uff0c\u597d\u8ba9 \u3000\u3000\u6cea\u5f71\u6a21\u7cca\u4e2d\uff0c\u5389\u80dc\u7537\u4f3c\u662f\u5411\u4ed6\u8d70\u6765\uff0c\u5bf9\u4ed6\u8bf4\u9053\uff1a\u201c\u4e16\u9057\uff0c\u4f60\u5982\u4eca\u5df2\u6210\u4e3a\u8d85\u8fc8\u524d\u4eba\u7684\u6b66\u5b66 \u5927\u5e08\u4e86\uff0c\u4f46\u6211\u6240\u671b\u4e8e\u4f60\u7684\u5176\u4ed6\u4e24\u4ef6\u4e8b\u60c5\uff0c\u4f60\u90fd\u6ca1\u6709\u505a\u5230\u3002\u4f60\u8d1f\u4e86\u6211\u4e5f\u8d1f\u4e86\u8c37\u59d0\u59d0\u4e86\uff01\u201d\u91d1\u4e16 \u9057\u53eb\u9053\uff1a\u201c\u80dc\u7537\uff0c\u4f60\u3001\u4f60\u542c\u2014\u2014\u201d\u5389\u80dc\u7537\u7684\u5f71\u5b50\u5ffd\u5730\u6d88\u5931\uff0c\u5728\u4ed6\u773c\u524d\u7684\u662f\u5389\u590d\u751f\u3002\u5389\u590d\u751f\u7f13 \u7f13\u8bf4\u9053\uff1a\u201c\u91d1\u5927 \u3000\u3000\u91d1\u4e16\u9057\u5b9a\u4e86\u5b9a\u795e\u3001\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u662f\u8981\u7ed9\u4f60\u505a\u5a92\u7684\uff0c\u4f60\u5374\u8981\u7ed9\u6211\u505a\u8d77\u5a92\u4eba\u6765\u4e86\u3002\u201d \u5389\u590d\u751f\u62c9\u7740\u5929\u9b54\u6559\u4e3b\uff0c\u7ad9\u5728\u91d1\u4e16\u9057\u9762\u524d\uff0c\u7b11\u9053\uff1a\u201c\u591a\u8c22\u91d1\u5927\u4fa0\uff0c\u4f60\u89e3\u9664\u4e86\u5979\u7684\u8d70\u706b\u5165\u9b54\u4e4b\u52ab \uff0c\u6211\u4eec\u4e24\u4eba\u4e4b\u4e8b\uff0c\u5df2\u65e0\u987b\u91d1\u5927\u4fa0\u518d\u64cd\u5fc3\u4e86\u3002\u6211\u76f8\u4fe1\u4f60\u548c\u8c37\u5973\u4fa0\u4e5f\u7528\u4e0d\u7740\u522b\u4eba\u505a\u5a92\u3002\u91d1\u5927\u4fa0\uff0c \u54b1\u4eec\u73b0\u5728\u8be5\u5230\u9099 \u3000\u3000\u91d1\u4e16\u9057\u3001\u5389\u590d\u751f\u7b49\u4eba\u7684\u5fc3\u5934\u79ef\u90c1\u90fd\u5982\u96e8\u8fc7\u5929\u6674\uff0c\u9099\u5c71\u4e4b\u4f1a\u7684\u4e3b\u4e8b\u53cc\u65b9\u5374\u662f\u5404\u62c5\u5fc3\u4e8b\uff0c\u9677 \u5165\u4e86\u7591\u4e91\u8ff7\u96fe\u4e4b\u4e2d\u3002\u8c37\u4e4b\u534e\u4e0d\u89c1\u5f92\u5f1f\u5f52\u6765\uff0c\u5f53\u7136\u662f\u6781\u4e3a\u6302\u8651\uff1b\u7ae5\u59e5\u59e5\u3001\u6587\u5ef7\u58c1\u7b49\u4eba\u53d1\u73b0\u201c\u56da \u72af\u201d\u88ab\u4eba\u6551\u53bb\uff0c\u4e5f\u662f\u5927\u5927\u60ca\u7591\uff0c\u8fd9\u624d\u77e5\u9053\u662f\u4e0a\u4e86\u5929\u9b54\u6559\u4e3b\u7684\u5f53\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u4e5f\u8fd8\u672a\u60f3\u5230\u6765\u6551\u51fa\u5929\u9b54\u6559\u4e3b\u7684\u7adf\u662f\u91d1\u4e16\u9057\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u867d\u7136\u662f\u906d\u9022\u4e86\u610f\u5916\u7684\u632b\u6298 \uff0c\u4ed7\u7740\u6709\u671d\u5ef7\u4f5c\u9760\u5c71\uff0c\u4ed7\u7740\u6709\u7ae5\u59e5\u59e5\u76f8\u52a9\uff0c\u4ecd\u4ee5\u4e3a\u662f\u80dc\u5238\u5728\u64cd\uff0c\u6309\u7167\u7740\u539f\u6765\u7684\u8ba1\u5212\u8d74\u4f1a\u3002 \u3000\u3000\u4e24\u65e5\u65f6\u5149\u8f6c\u773c\u5373\u8fc7\uff0c\u5230\u4e86\u7ea6\u4f1a\u4e4b\u671f\uff0c\u8fd9\u4e00\u65e5\u4e5f\u6b63\u662f\u6e05\u660e\u4f73\u8282\u3002\u6e05\u660e\u65f6\u8282\u672c\u6765\u591a\u96e8\uff0c\u8fd9\u4e00 \u5e74\u5374\u662f\u4f8b\u5916\uff0c\u8c37\u4e4b\u534e\u4e00\u65e9\u8d77\u6765\uff0c\u4f46\u89c1\u78a7\u5929\u5982\u6d17\uff0c\u6ca1\u6709\u4e00\u70b9\u4e4c\u4e91\uff0c\u7adf\u662f\u4e2a\u65e5\u4e3d\u98ce\u548c\u7684\u597d\u5929\u6c14\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u7684\u5fc3\u60c5\u521a\u597d\u4e0e\u5929\u6c14\u76f8\u53cd\uff0c\u5fc3\u5934\u5c31\u5982\u538b\u4e86\u94c5\u5757\u4e00\u822c\u7684\u6c89\u91cd\uff0c\u5bfb\u601d\uff1a\u201c\u83b2\u513f\u8fd8\u672a\u56de\u6765 \uff0c\u53ea\u6015\u662f\u5df2\u88ab\u5bf9\u65b9\u64d2\u4e0b\u6765\u4e86\u3002\u8981\u662f\u4ed6\u4eec\u62ff\u83b2\u513f\u6765\u5a01\u80c1\u6211\uff0c\u8fd9\u5374\u5982\u4f55\u5e94\u4ed8\uff1f\u201d\u6b63\u81ea\u5fc3\u7eea\u4e0d\u5b81\uff0c \u767d\u82f1\u6770\u5df2\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u5929\u9b54\u6559\u4e3b\u5df2\u6765\u5230\u5c71\u524d\uff0c\u8bf7\u638c\u95e8\u524d\u5f80\u8d74\u4f1a\u3002\u201d\u8c37\u4e4b\u534e\u7cbe\u795e\u4e00\u632f\uff0c\u8bf4\u9053\uff1a \u201c\u679c\u771f\u662f\u5929\u9b54\u6559 \u3000\u3000\u4f1a\u573a\u5c31\u662f\u5893\u56ed\u4e2d\u7684\u90a3\u4e2a\u5927\u8349\u576a\uff0c\u9099\u5c71\u6d3e\u672c\u6765\u5df2\u6709\u534a\u6570\u4ee5\u4e0a\u7684\u5f1f\u4e8e\u5728\u90a3\u91cc\u5b88\u5019\uff0c\u8c37\u4e4b\u534e\u7559 \u4e0b\u672a\u6210\u5e74\u7684\u5f1f\u5b50\uff0c\u5c06\u5176\u4f59\u7684\u4eba\u5c3d\u6570\u5e26\u53bb\u3002\u53ea\u89c1\u8349\u576a\u4e0a\u9ed1\u538b\u538b\u7684\u5c3d\u662f\u4eba\u5934\uff0c\u8fde\u5c71\u5761\u4e0a\u90fd\u662f\u4e00\u5c42 \u5c42\u7684\u7ad9\u6ee1\u4e86\u4eba\uff0c \u3000\u3000\u539f\u6765\u9099\u5c71\u6d3e\u867d\u6ca1\u9080\u8bf7\u5916\u4eba\u52a9\u62f3\uff0c\u4f46\u6309\u7167\u60ef\u4f8b\uff0c\u6bcf\u5e74\u6e05\u660e\uff0c\u90fd\u6709\u4e0d\u5c11\u6b66\u6797\u540c\u9053\uff0c\u6765\u7ed9\u72ec\u81c2 \u795e\u5c3c\u4ee5\u53ca\u5415\u56db\u5a18\u626b\u5893\uff0c\u4eca\u5e74\u56e0\u4e3a\u542c\u5f97\u98ce\u58f0\uff0c\u6765\u7684\u66f4\u591a\uff0c\u8fd8\u6709\u7ffc\u4ef2\u725f\u7684\u4e10\u5e2e\u4e2d\u6709\u8eab\u4efd\u7684\u5f1f\u5b50\uff0c \u4e5f\u5dee\u4e0d\u591a\u5168\u90fd\u6765\u4e86\u3002\u4f46\u5929\u9b54\u6559\u8fd9\u65b9\u9762\u7684\u4eba\u6570\uff0c\u5374\u6bd4\u9099\u5c71\u6d3e\u52a0\u4e0a\u4e10\u5e2e\u5f1f\u5b50\u518d\u52a0\u4e0a\u524d\u6765\u626b\u5893\u7684\u5bbe \u5ba2\u8fd8\u591a\u3002\u4e24\u65b9\u5404 \u3000\u3000\u8c37\u4e4b\u534e\u8fdb\u5165\u4f1a\u573a\uff0c\u53ea\u89c1\u5929\u9b54\u6559\u8fd9\u8fb9\uff0c\u6709\u4e00\u8f86\u5bab\u8f66\uff0c\u5e18\u5e55\u4f4e\u5782\uff0c\u63d2\u6709\u5929\u9b54\u6559\u7684\u65d7\u5e1c\uff0c\u8c37\u4e4b \u534e\u7591\u5fc3\u5927\u8d77\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u96be\u5f97\u6559\u4e3b\u83b2\u9a7e\u5230\u6765\u3002\u9099\u5c71\u638c\u95e8\u8c37\u4e4b\u534e\u7387\u9886\u4e24\u4ee3\u95e8\u4eba\u5728\u6b64\u5019\u9a7e\u4e86\u3002 \u6559\u4e3b\u6709\u4f55\u6307\u6559\uff0c\u8bf7\u6765\u76f8\u4f1a\uff01\u201d \u3000\u3000\u5e37\u5e55\u8f7b\u8212\uff0c\u5bab\u8f66\u63ed\u5f00\u4e00\u89d2\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u679c\u7136\u662f\u5929\u9b54\u6559\u4e3b\u5750\u5728\u5f53\u4e2d\uff0c\u53ea\u89c1\u5979\u628a\u624b\u4e00\u62db\uff0c\u5c06 \u6587\u5ef7\u58c1\u53ec\u5230\u773c\u524d\uff0c\u4f3c\u662f\u5728\u5429\u5490\u4ec0\u4e48\uff0c\u4f46\u8bf4\u8bdd\u7684\u58f0\u97f3\u5f88\u4f4e\uff0c\u65c1\u4eba\u53ea\u770b\u5f97\u89c1\u5979\u51b7\u50b2\u7684\u7b11\u5bb9\uff0c\u5374\u542c \u4e0d\u6e05\u6240\u8bf4\u7684\u8a00\u8bed\u3002 \u3000\u3000\u8fd9\u4e2a\u201c\u6559\u4e3b\u201d\uff0c\u5f53\u7136\u662f\u7f2a\u592b\u4eba\u5047\u626e\u7684\u4e86\uff0c\u5979\u548c\u5929\u9b54\u6559\u4e3b\u4e43\u662f\u59b9\u59b9\uff0c\u76f8\u8c8c\u672c\u6765\u76f8\u4f3c\uff0c\u518d\u52a0 \u4ee5\u523b\u610f\u6253\u626e\uff0c\u52a1\u6c42\u4ee5\u5047\u4e71\u771f\uff0c\u65c1\u4eba\u81ea\u662f\u66f4\u96be\u5206\u8fa8\u3002\u8c37\u4e4b\u534e\u8d77\u4e86\u7591\u5fc3\uff0c\u4f46\u5374\u4e5f\u4e0d\u6562\u65ad\u5b9a\u5979\u5c31\u662f \u5047\u5192\u7684\u6559\u4e3b\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u83ab\u975e\u5979\u662f\u56e0\u4e3a\u8d70\u706b\u5165\u9b54\u3001\u534a\u8eab\u4e0d\u9042\u7684\u7f18\u6545\uff0c\u4e0d\u80fd\u4e0b\u8f66\uff1f\u4f46\u770b\u5979\u7684 \u8865\u60c5\uff0c\u4e0d\u4f3c\u4e25\u91cd \u3000\u3000\u6587\u5ef7\u58c1\u8d70\u51fa\u573a\u5fc3\uff0c\u6602\u9996\u5411\u5929\uff0c\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4f1a\uff0c\u80dc\u8005\u4e3a\u5f3a\uff0c\u6559\u4e3b\u53ea\u53eb\u6211\u95ee\u4f60\uff0c\u4f60 \u662f\u613f\u5584\u7f62\u7518\u4f11\uff0c\u8fd8\u662f\u539a\u5e72\u6208\u76f8\u89c1\uff1f\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u5584\u7f62\u7518\u4f11\u53c8\u5982\u4f55\uff1f\u5e72\u6208\u76f8\u89c1\u53c8\u5982\u4f55\uff1f\u8d35\u6559 \u8fdc\u6765\u662f\u5ba2\uff0c\u8bf7\u4f60\u5212\u51fa\u9053\u6765\uff01\u201d \u3000\u3000\u6587\u5ef7\u58c1\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5018\u6b32\u5584\u7f62\u7518\u4f11\uff0c\u987b\u5f97\u4f9d\u4ece\u4e24\u4e8b\uff01\u201d\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u54ea\u4e24\u4ef6\u4e8b\uff1f\u201d\u6587\u5ef7 \u58c1\u9053\uff1a\u201c\u7b2c\u4e00\u4ef6\u4f60\u987b\u788d\u5f53\u4f17\u8d54\u7f6a\uff0c\u5411\u6211\u5929\u9b54\u6559\u6559\u4e3b\u78d5\u5934\u3002\u7b2c\u4e8c\u4ef6\u4e8b\uff0c\u4f60\u5f53\u5e74\u66fe\u9010\u6211\u6559\u6559\u4e3b\u4e0b \u5c71\uff0c\u5982\u4eca\u987b\u5f97\u8ba9\u51fa\u9099\u5c71\uff0c\u7ed9\u6211\u5929\u9b54\u6559\u4f5c\u4e3a\u5206\u8235\u3002\u4e0d\u8fc7\uff0c\u82e5\u7136\u8ba9\u51fa\u9099\u5c71\uff0c\u4f60\u4eec\u7956\u5e08\u7684\u575f\u5893\uff0c\u4e5f \u4e0d\u80fd\u846c\u5728\u6b64\u5730\uff0c \u3000\u3000\u72ec\u81c2\u795e\u5c3c\u4e0e\u5415\u56db\u5a18\u4e43\u662f\u6b66\u6797\u4e2d\u4eba\u6700\u666f\u4ef0\u7684\u4eba\u7269\uff0c\u6240\u4ee5\u6b7b\u4e86\u591a\u5e74\uff0c\u4ecd\u7136\u5e74\u5e74\u6709\u4eba\u6765\u7ed9\u5979\u4eec \u626b\u5893\uff0c\u4e08\u5ef7\u58c1\u6b64\u8a00\u4e00\u51fa\uff0c\u8c37\u4e4b\u534e\u5c1a\u672a\u53d1\u4f5c\uff0c\u524d\u6765\u626b\u5893\u7684\u5ba2\u4eba\u5df2\u662f\u7eb7\u7eb7\u559d\u9a82\uff1a\u201c\u4f55\u7269\u5996\u90aa\uff0c\u80c6 \u6562\u53e3\u51fa\u72c2\u8a00\uff0c\u51cc\u8fb1\u524d\u8f88\u5b97\u5e08\uff1f\u201d\u6587\u5ef7\u58c1\u51b7\u7b11\u9053\uff1a\u201c\u8bf8\u4f4d\u82e5\u662f\u770b\u4e0d\u987a\u773c\uff0c\u7b49\u4e0b\u5c3d\u53ef\u7ed9\u9099\u5c71\u6d3e\u52a9 \u62f3\uff01\u4f46\u5f97\u5148\u8bf7\u9099 \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\u65e2\u662f\u56e0\u6211\u60f9\u607c\u4e86\u8d35\u6559\u6559\u4e3b\u800c\u8d77\uff0c\u6559\u4e3b\u4e0d\u80af\u89c1\u8c05\uff0c\u8c37\u67d0\u613f\u4e00\u4eba\u627f\u5f53 \uff01\u201d\u610f\u601d\u5373\u662f\u8981\u4e0e\u5929\u9b54\u6559\u4e3b\u5355\u6253\u72ec\u6597\uff0c\u80dc\u8d25\u4e0d\u6d89\u4ed6\u4eba\u3002 \u3000\u3000\u54ea\u77e5\u8bdd\u72b9\u672a\u4e86\uff0c\u7ae5\u59e5\u59e5\u5df2\u662f\u4e00\u58f0\u602a\u53eb\uff0c\u98de\u8eab\u4fbf\u6251\u8fc7\u6765\uff0c\u559d\u9053\uff1a\u201c\u4f60\u65e2\u613f\u627f\u5f53\uff0c\u6211\u4fbf\u5148\u628a \u4f60\u62ff\u4e0b\uff01\u4e0a\u6b21\u7ed9\u4f60\u4fa5\u5e78\u8131\u9003\uff0c\u8fd9\u6b21\u4e14\u770b\u4f60\u8fd8\u6709\u4ec0\u4e48\u672c\u9886\uff0c\u9003\u5f97\u51fa\u6211\u7684\u624b\u5fc3\uff1f\u201d \u3000\u3000\u8c37\u4e4b\u534e\u4e24\u65c1\u662f\u5e08\u5ac2\u8c22\u4e91\u771f\u548c\u5e08\u5144\u5362\u9053\u7498\uff0c\u8c22\u4e91\u771f\u5916\u53f7\uff1a\u201c\u68d8\u624b\u4ed9\u5a18\u201d\uff0c\u672c\u662f\u5ce8\u5d4b\u6d3e\u5f1f\u5b50 \uff0c\u56e0\u5979\u5df2\u53bb\u4e16\u7684\u4e08\u592b\u201c\u94c1\u62d0\u4ed9\u201d\u5468\u9752\u662f\u524d\u4efb\u4e10\u5e2e\u5e2e\u4e3b\uff0c\u4ea6\u5373\u7ffc\u4ef2\u725f\u7684\u5e08\u5144\uff0c\u6545\u800c\u4e5f\u7b97\u5f97\u662f\u9099 \u5c71\u6d3e\u7684\u4eba\u3002\u8c22\u4e91\u771f\u53f7\u79f0\u201c\u8fa3\u624b\u4ed9\u5a18\u201d\uff0c\u6027\u60c5\u6700\u4e3a\u66b4\u8e81\uff0c\u95fb\u8a00\u5927\u6012\uff0c\u9a82\u9053\uff1a \u3000\u3000\u201c\u54ea\u91cc\u6765\u7684\u8001\u4e5e\u5a46\uff1f\u201d\u98de\u8eab\u8fce\u4e0a\uff0c\u5530\u7684\u4fbf\u662f\u4e00\u5251\uff01 \u3000\u3000\u7ae5\u59e5\u59e5\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6709\u591a\u5927\u80fd\u529b\uff0c\u6562\u4e3a\u8c37\u4e4b\u534e\u66ff\u6b7b\uff1f\u201d\u8c22\u4e91\u771f\u5251\u6cd5\u4ee5\u72e0\u8fa3\u8fc5\u6377\u89c1\u957f\uff0c\u970e \u5ffd\u4e4b\u95f4\uff0c\u8fde\u523a\u4e03\u5251\uff0c\u5251\u5251\u6307\u5411\u7ae5\u59e5\u59e5\u7684\u8981\u5bb3\u7a74\u9053\uff0c\u54ea\u77e5\u5251\u5251\u523a\u7a81\uff0c\u521a\u5230\u7b2c\u4e03\u5251\uff0c\u7ae5\u59e5\u59e5\u5df2\u662f \u957f\u8896\u4e00\u6325\uff0c\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u4f7f\u628a\u8c22\u4e91\u771f\u957f\u5251\u5377\u53bb\uff0c\u968f\u624b\u518d\u62c2\uff0c\u957f\u8896\u4e00\u5377\u4e00\u7ffb\uff0c\u767b\u65f6\u628a\u8c22\u4e91\u771f \u6454\u4e86\u4e2a\u7b4b\u6597\u3002 \u3000\u3000\u5362\u9053\u7498\u662f\u5f53\u5e74\u6c5f\u5357\u4e03\u4fa0\u4e2d\u66f9\u4ec1\u7236\u7684\u5f1f\u5b50\uff0c\u7528\u4e00\u628a\u94c1\u7435\u7436\uff0c\u7435\u7436\u4e2d\u7a7a\uff0c\u5185\u85cf\u900f\u9aa8\u9489\u4e09\u5341\u516d \u679a\uff0c\u89c1\u72b6\u592a\u60ca\uff0c\u4e00\u6309\u7435\u7436\uff0c\u4e09\u5341\u516d\u679a\u900f\u9aa8\u9489\u5168\u90fd\u53d1\u51fa\u3002\u7ae5\u59e5\u59e5\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u7c73\u7c92\u4e4b\u73e0\uff0c\u4e5f\u653e \u5149\u534e\uff01\u201d\u90a3\u4e09\u5341\u516d\u679a\u900f\u9aa8\u9489\u4ece\u56db\u9762\u516b\u65b9\u6253\u6765\uff0c\u8bf4\u4e5f\u5947\u602a\uff0c\u770b\u6765\u5df2\u6253\u5230\u4e86\u5979\u7684\u8eab\u4e0a\uff0c\u90fd\u5ffd\u5982\u6ce5 \u725b\u5165\u6d77\uff0c\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u53ea\u56e0\u52a8\u4f5c\u592a\u5feb\uff0c\u65c1\u89c2\u8005\u8fde\u770b\u90fd\u770b\u4e0d\u5f97\u6e05\u695a\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u5362\u5e08\u5144\u9000\u4e0b\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u971c\u534e\u5b9d\u5251\uff0c\u5df2\u662f\u5316\u6210\u4e00\u9053\u94f6\u8679\uff0c \u62a2\u5230\u4e86\u5362\u9053\u7498\u7684\u524d\u5934\uff0c\u6321\u4f4f\u4e86\u7ae5\u59e5\u59e5\u3002\u53ea\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u534a\u7a7a\u4e2d\u6d12\u4e0b\u4e86\u4e00\u84ec \u5149\u96e8\uff0c\u539f\u6765\u662f\u7ae5\u59e5\u59e5\u5c06\u90a3\u4e09\u5341\u516d\u679a\u900f\u9aa8\u9489\u53cd\u6253\u51fa\u6765\uff0c\u88ab\u8c37\u4e4b\u534e\u7684\u5251\u5149\u7ede\u6210\u7c89\u788e\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u559d\u9053\uff1a\u201c\u9099\u5c71\u6d3e\u51b3\u610f\u8981\u5e72\u6208\u76f8\u89c1\uff0c\u5927\u4f19\u513f\u4e0a\u5427\uff01\u201d\u4ed6\u4eec\u65e9\u5df2\u5b89\u6392\u4e86\u6df7\u6218\u7684\u8ba1\u5212\uff0c \u4e00\u58f0\u4ee4\u4e0b\uff0c\u5929\u9b54\u6559\u5f92\u4e0e\u5192\u5145\u6559\u5f92\u7684\u5927\u5185\u9ad8\u624b\u5df2\u5e03\u6210\u9635\u52bf\uff0c\u4e00\u62e5\u800c\u524d\uff0c\u5bf9\u9099\u5c71\u6d3e\u8fd9\u8fb9\u7684\u4eba\u91c7\u53d6 \u4e86\u5927\u5305\u56f4\u7684\u5f62\u52bf\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u5148\u5728\u573a\u5fc3\uff0c\u4e0e\u9099\u5c71\u6d3e\u77ed\u5175\u76f8\u63a5\uff0c\u4e00\u51fa\u624b\u4fbf\u6253\u7ffb\u4e86\u51e0\u4e2a\u9099\u5c71\u6d3e\u5f1f\u5b50\u3002\u7ffc\u4ef2\u725f\u5927\u6012\u9053 \uff1a\u201c\u4f55\u7269\u5996\u90aa\uff0c\u5403\u6211\u4e00\u62d0\uff1f\u201d\u4ed6\u7684\u4f0f\u9b54\u6756\u6cd5\u5a01\u731b\u65e0\u4f26\uff0c\u4e00\u62d0\u626b\u6765\u3002\u6c99\u98de\u77f3\u8d70\u3002\u6587\u5ef7\u58c1\u53cd\u624b\u5288 \u53bb\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u7ffc\u4ef2\u725f\u7adf\u7136\u864e\u53e3\u6d41\u8840\uff0c\u62d0\u6756\u9669\u8138\u8131\u624b\u3002\u6587\u5ef7\u58c1\u4e5f\u89c9\u624b\u81c2\u9178\u9ebb\uff0c\u5fc3\u5934 \u5fae\u51db\uff1a\u201c\u8fd9\u8001\u53eb \u3000\u3000\u7ffc\u4ef2\u725f\u7acb\u8db3\u672a\u7a33\uff0c\u773c\u770b\u8fd9\u4e00\u638c\u5c31\u8981\u5288\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u659c\u523a\u91cc\u5ffd\u5730\u6709\u4e00\u4ef6\u6697\u5668\u98de\u6765\uff0c\u6587\u5ef7\u58c1 \u5df2\u7ec3\u6210\u5927\u4e94\u884c\u4e09\u8c61\u795e\u529f\uff0c\u6d51\u8eab\u5200\u67aa\u4e0d\u5165\uff0c\u533a\u533a\u6697\u5668\uff0c\u6839\u672c\u5c31\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u8fd9\u4e00\u638c\u4ecd\u7136\u5411\u524d\u5288 \u53bb\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u4ef6\u201c\u6697\u5668\u201d\u7ed9\u4ed6\u6253\u788d\u7a00\u70c2\uff0c\u4f46\u90a3\u201c\u6697\u5668\u201d\u7684\u52b2\u9053\u5374\u4e5f\u4e0d\u5c0f\uff0c\u6587 \u5ef7\u58c1\u8fd9\u4e00\u638c\u7ed9\u5b83\u4e00\u649e\uff0c\u767b\u65f6\u6253\u6b6a\uff0c\u638c\u5fc3\u6709\u4e00\u79cd\u6e7f\u6f09\u6f09\u7684\u611f\u89c9\uff0c\u540c\u65f6\u95fb\u5230\u4e00\u80a1\u81ed\u5473\uff0c\u539f\u6765\u8fd9\u6697 \u5668\u662f\u4e00\u53ea\u6cbe\u6ee1\u70c2\u6ce5\u7684\u8349\u978b\u3002\u8fd9\u8349\u978b\u7684\u4e3b\u4eba\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u5317\u4e10\u5e2e\u5e2e\u4e3b\u4ef2\u957f\u7edf\uff0c\u4ed6\u98de\u51fa\u8349\u978b\uff0c \u66ff\u7ffc\u4ef2\u725f\u89e3\u4e86\u4e00 \u3000\u3000\u4ef2\u957f\u7edf\u7ec3\u7684\u6df7\u5143\u4e00\u6c14\u529f\uff0c\u5a01\u529b\u4e0d\u5728\u6587\u5ef7\u58c1\u7684\u4e09\u8c61\u795e\u529f\u4e4b\u4e0b\uff0c\u7ffc\u4ef2\u725f\u867d\u7136\u4e0d\u53ca\u6587\u5ef7\u58c1\uff0c\u5374 \u4e5f\u5dee\u4e0d\u4e86\u5927\u8fdc\uff0c\u5357\u5317\u4e24\u4e10\u5e2e\u5e2e\u4e3b\u8054\u624b\uff0c\u767b\u65f6\u53cd\u5b88\u4e3a\u653b\u3002 \u3000\u3000\u5929\u9b54\u6559\u8fd9\u8fb9\u4e00\u4e2a\u9ed1\u8863\u6c49\u5b50\u5982\u98de\u800c\u81f3\uff0c\u7528\u7684\u5175\u5668\u751a\u4e3a\u53e4\u602a\uff0c\u662f\u4e00\u5bf9\u91d1\u5149\u95ea\u95ea\u7684\u53cc\u8f6e\u3002\u7ffc\u4ef2 \u725f\u4e00\u62d0\u6253\u53bb\uff0c\u88ab\u4ed6\u53cc\u8f6e\u4e00\u9501\uff0c\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u62d0\u6756\u4e0a\u51fa\u73b0\u4e00\u6392\u9f7f\u5370\uff0c\u94c1\u5c51\u7eb7\u98de\uff0c\u8584\u4e86\u51e0\u5206\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u4e00\u638c\u62cd\u51fa\uff0c\u5c06\u4ed6\u53cc\u8f6e\u63a8\u5f00\uff0c\u559d\u9053\uff1a\u201c\u9601\u4e0b\u662f\u8c01\uff1f\u77a7\u4f60\u7ec3\u7684\u4e5f\u662f\u540d\u95e8\u6b63\u6d3e\u529f\u592b\uff0c\u4e3a \u4f55\u6765\u52a9\u90aa\u6559\uff1f\u201d\u90a3\u9ed1\u8863\u6c49\u5b50\u559d\u9053\uff1a \u3000\u3000\u201c\u9099\u5c71\u6d3e\u5927\u9006\u4e0d\u9053\uff0c\u4eba\u4eba\u5f97\u800c\u8bdb\u4e4b\uff0c\u4f60\u8fd9\u81ed\u5316\u5b50\u624d\u662f\u4e0d\u5206\u987a\u9006\uff01\u201d \u3000\u3000\u4ef2\u957f\u7edf\u5927\u6012\uff0c\u6b63\u8981\u518d\u53d1\u4e00\u638c\uff0c\u5ffd\u89c1\u4e24\u9053\u5251\u5149\uff0c\u5de6\u53f3\u5408\u51fb\uff0c\u62b5\u4f4f\u4e86\u90a3\u9ed1\u8863\u6c49\u5b50\u7684\u65e5\u6708\u53cc\u8f6e \u3002\u8fd9\u4e24\u4e2a\u4eba\u90fd\u662f\u6765\u7ed9\u72ec\u81c2\u795e\u5c3c\u626b\u5893\u7684\u5ba2\u4eba\uff0c\u4e00\u4e2a\u662f\u9752\u57ce\u6d3e\u540d\u5bbf\u8427\u9752\u5cf0\uff0c\u4e00\u4e2a\u662f\u6b66\u5f53\u6d3e\u638c\u95e8\u96f7 \u9707\u5b50\u7684\u5e08\u5f1f\u62b1\u62d9\u9053\u4eba\u3002\u8fd9\u4e24\u4eba\u548c\u9099\u5c71\u6d3e\u90fd\u6709\u6df1\u539a\u7684\u6e0a\u6e90\u3002 \u3000\u3000\u8427\u9752\u5cf0\u559d\u9053\uff1a\u201c\u4e0d\u8bc6\u7f9e\u7684\u6ee1\u5974\u9e70\u72ac\uff0c\u4e5f\u6765\u6df7\u6c34\u6478\u9c7c\u3002\u770b\u5251\uff01\u201d\u539f\u6765\u8fd9\u9ed1\u8863\u6c49\u5b50\u540d\u53eb\u4ef2\u5143 \u5947\uff0c\u662f\u5d06\u5cd2\u540d\u5bbf\u9f50\u5929\u4e50\u7684\u5f1f\u5b50\uff0c\u8d2a\u56fe\u5bcc\u8d35\uff0c\u6295\u6548\u671d\u5ef7\uff0c\u5b98\u5c45\u5927\u5185\u526f\u603b\u7ba1\u4e4b\u804c\uff0c\u6b66\u529f\u5374\u6bd4\u6b63\u603b \u7ba1\u6ee1\u6d32\u4eba\u989d\u94a6\u5fb7\u8fd8\u9ad8\uff0c\u5b9e\u662f\u5927\u5185\u7b2c\u4e00\u9ad8\u624b\u3002 \u3000\u3000\u8427\u9752\u5cf0\u4e0e\u62b1\u62d9\u9053\u4eba\u53cc\u5251\u9f50\u51fa\uff0c\u582a\u582a\u548c\u4ed6\u6253\u6210\u5e73\u624b\u3002\u8fd9\u65f6\uff0c\u6587\u5ef7\u58c1\u7684\u4e09\u8c61\u795e\u529f\u4e0e\u4ef2\u957f\u7edf\u7684 \u6df7\u5143\u4e00\u6c14\u529f\u90fd\u5df2\u53d1\u6325\u5f97\u6dcb\u6f13\u5c3d\u81f4\uff0c\u7ffc\u4ef2\u725f\u7684\u4f0f\u9b54\u6756\u6cd5\uff0c\u4e5f\u4f7f\u5230\u4e86\u7b2c\u4e8c\u6bb5\u4e09\u5341\u516d\u62db\u5f00\u5916\uff0c\u5468\u56f4 \u6570\u4e08\u4e4b\u5185\u6cfc\u6c34\u4e0d\u8fdb\uff0c\u4f5f\u5143\u5947\u4e00\u88ab\u53cc\u5251\u8361\u5f00\uff0c\u5df2\u662f\u4e0d\u80fd\u4e0e\u6587\u5ef7\u58c1\u4e92\u76f8\u547c\u5e94\uff0c\u4e8e\u662f\u5728\u8fd9\u4e2a\u5c0f\u5708\u5b50 \u5185\uff0c\u516d\u4e2a\u4eba\u53c8\u5206 \u3000\u3000\u4ef2\u3001\u7ffc\u4e8c\u4eba\u5408\u6218\u6587\u5ef7\u58c1\u672c\u6765\u7a0d\u5360\u4e0a\u98ce\uff0c\u4e0d\u6599\u6587\u5ef7\u58c1\u7684\u638c\u98ce\u4e2d\u5374\u6709\u4e00\u80a1\u8165\u5473\uff0c\u539f\u6765\u4ed6\u5f97\u4e86 \u5929\u9b54\u6559\u4e3b\u7684\u767e\u6bd2\u771f\u7ecf\uff0c\u4e09\u8c61\u795e\u529f\u5df2\u662f\u4e0e\u6bd2\u529f\u7ed3\u5408\uff0c\u4e0d\u4f46\u638c\u529b\u5927\u589e\uff0c\u53d1\u51fa\u4f2a\u638c\u4e5f\u8db3\u4ee5\u4ee4\u4eba\u4e2d\u6bd2 \u3002\u4ef2\u3001\u7ffc\u4e8c\u4eba\u867d\u662f\u529f\u529b\u6df1\u6e5b\uff0c\u4e00\u65f6\u4e0d\u81f3\u53d7\u4ed6\u4f24\u5bb3\uff0c\u4f46\u4e5f\u4e0d\u80fd\u4e0d\u95ed\u4e86\u547c\u5438\uff0c\u624d\u5f97\u514d\u4e8e\u4e2d\u6bd2\u3002 \u3000\u3000\u8f6c\u773c\u4e4b\u95f4\uff0c\u9099\u5c71\u6d3e\u5f1f\u5b50\u81ea\u7ffc\u4ef2\u725f\u4ee5\u4e0b\uff0c\u4eba\u4eba\u90fd\u5df2\u53d7\u5230\u5f3a\u654c\u7684\u653b\u51fb\u3002\u4f46\u5904\u5883\u6700\u9669\u7684\u5374\u8fd8\u662f \u638c\u95e8\u4eba\u8c37\u4e4b\u534e\u3002\u8c37\u4e4b\u534e\u7684\u5e08\u5144\u7a0b\u704f\uff0c\u6797\u7b19\uff0c\u5bbe\u5ba2\u4e2d\u7684\u8f9b\u9690\u519c\u548c\u970d\u5b9d\u915e\u7b49\u4eba\uff0c\u89c1\u7ae5\u59e5\u59e5\u5341\u5206\u5389 \u5bb3\u3002 \u3000\u3000\u7eb7\u7eb7\u8d76\u6765\u3002 \u3000\u3000\u91cd\u59e5\u59e5\u4e00\u58f0\u51b7\u7b11\uff0c\u8896\u4e2d\u98de\u51fa\u4e00\u56e2\u51b7\u7130\uff0c\u5728\u5468\u56f4\u4e94\u4e08\u4e4b\u5185\uff0c\u7130\u706b\u56f4\u6210\u4e86\u4e00\u4e2a\u5708\u5708\u3002\u6797\u7b19\u6536 \u52bf\u4e0d\u53ca\uff0c\u811a\u6b65\u8e0f\u5165\u5708\u4e2d\uff0c\u767b\u65f6\u53d1\u51fa\u4e00\u58f0\u88c2\u4eba\u5fc3\u80ba\u7684\u547c\u558a\uff0c\u6447\u6447\u6b32\u5760\uff0c\u8f9b\u9690\u519c\u5927\u60ca\uff0c\u8fde\u5fd9\u4e00\u628a \u6293\u7740\u4ed6\u7684\u80cc\u5fc3\uff0c\u5c06\u4ed6\u62c9\u4e86\u51fa\u6765\u3002\u53ea\u89c1\u6797\u7b19\u7684\u4e00\u6761\u5927\u817f\uff0c\u8840\u8089\u90fd\u5df1\u5316\u51c0\uff0c\u53ea\u5269\u4e0b\u4e00\u6839\u67af\u9aa8\u3002\u7a0b \u704f\u8feb\u5f97\u4e00\u5200\u65a9\u4e0b\u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u4ee5\u51b7\u7130\u6bd2\u706b\u9694\u65ad\u4f17\u4eba\uff0c\u5708\u5b50\u4e2d\u5c31\u53ea\u5269\u4e0b\u5979\u548c\u8c37\u4e4b\u534e\u3002 \u3000\u3000\u539f\u6765\u5979\u8fd9\u51b7\u7130\u653b\u8fdc\u4e0d\u653b\u8fd1\uff0c\u8fd1\u5219\u81ea\u5df1\u4e5f\u4f1a\u6ce2\u53ca\uff0c\u5979\u81ea\u5fd6\u6b66\u529f\u5728\u8c37\u4e4b\u534e\u4e4b\u4e0a\uff0c\u7528\u6b64\u6cd5\u622a\u65ad \u5979\u7684\u540e\u63f4\uff0c\u7528\u610f\u5c31\u662f\u8981\u628a\u8c37\u4e4b\u534e\u751f\u64d2\uff0c\u597d\u74e6\u89e3\u90ce\u5c71\u6d3e\u7684\u6218\u610f\u3002 \u3000\u3000\u5e78\u800c\u8c37\u4e4b\u534e\u5df2\u5f97\u4e86\u5415\u56db\u5a18\u7684\u8863\u94b5\u771f\u4f20\uff0c\u53c8\u5728\u5185\u529f\u4e0a\u5f97\u8fc7\u91d1\u4e16\u9057\u7684\u6307\u70b9\uff0c\u7384\u5973\u5251\u6cd5\u795e\u5999\u65e0 \u65b9\uff0c\u7ae5\u59e5\u59e5\u4e00\u65f6\u95f4\u5012\u4e5f\u672a\u80fd\u5c06\u5979\u64d2\u4e0b\uff0c\u4f46\u8fc7\u4e86\u4e09\u5341\u62db\u4e4b\u540e\uff0c\u7ae5\u59e5\u59e5\u9891\u9891\u4f7f\u51fa\u9694\u7269\u4f20\u529f\u7684\u7edd\u6280 \uff0c\u5148\u540e\u5728\u8c37\u4e4b\u534e\u7684\u5b9d\u5251\u4e0a\u5f39\u4e86\u5341\u516d\u516b\u4e0b\uff0c\u8c37\u4e4b\u534e\u53ea\u89c9\u6d51\u8eab\u7126\u8e81\uff0c\u5251\u62db\u4f7f\u51fa\uff0c\u5df2\u662f\u529b\u4e0d\u4ece\u5fc3\uff0c \u4ec5\u6709\u62db\u67b6\u4e4b\u529f\uff0c \u3000\u3000\u9099\u5c71\u5f1f\u5b50\u89e6\u76ee\u60ca\u5fc3\uff0c\u53ef\u662f\u4e00\u6765\u4ed6\u4eec\u4e5f\u5df2\u5728\u654c\u4eba\u5305\u56f4\u4e4b\u4e2d\uff0c\u4e8c\u6765\u90a3\u51b7\u7130\u6bd2\u706b\u592a\u8fc7\u5389\u5bb3\uff0c\u8c01 \u4e5f\u4e0d\u6562\u51b2\u8fdb\u53bb\u767d\u9001\u6027\u547d\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5f62\u52bf\u5341\u5206\u7d27\u5f20\uff0c\u9099\u5c71\u6d3e\u5373\u5c06\u4e00\u8d25\u6d82\u5730\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u5929\u9b54\u6559\u7684\u9635\u52bf\u5728\u5578 \u58f0\u4e2d\u4e25\u5982\u6ce2\u5206\u6d6a\u88c2\uff0c\u4e00\u4e2a\u5c11\u5e74\u638c\u5288\u5251\u6233\uff0c\u6740\u8fdb\u8349\u576a\u3002\u8fd9\u5c11\u5e74\u4e0d\u662f\u522b\u4e2a\uff0c\u6b63\u662f\u4ece\u6606\u5e03\u5170\u56fd\u8fdc\u9053 \u8d76\u56de\u6765\u7684\u6c5f\u6d77\u5929\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5927\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u5996\u602a\u53c8\u5728\u8fd9\u91cc\u5ba2\u4eba\uff01\u201d\u53cc\u638c\u4e00\u5206\uff0c\u5de6\u53f3\u62cd\u51fa\uff0c\u90a3\u4e00\u5708\u51b7\u7130\u6bd2\u706b \uff0c\u767b\u65f6\u4e5f\u968f\u7740\u4e24\u8fb9\u5206\u5f00\uff0c\u8ba9\u51fa\u4e86\u4e00\u6761\u8def\u3002\u6c5f\u6d77\u5929\u8fc5\u901f\u8dc3\u5165\u5708\u5b50\uff0c\u7830\u7684\u4e00\u638c\uff0c\u5c31\u5411\u7ae5\u59e5\u59e5\u6253\u4e0b \u3002 \u3000\u3000\u7ae5\u59e5\u59e5\u4f7f\u51fa\u5316\u8840\u795e\u529f\uff0c\u4e3e\u638c\u76f8\u8fce\uff0c\u5979\u7684\u6bd2\u529f\u5389\u5bb3\uff0c\u6c5f\u6d77\u5929\u5219\u5185\u529f\u8f83\u6df1\uff0c\u7ae5\u59e5\u59e5\u7684\u6bd2\u529f\u4f24 \u4e0d\u4e86\u6c5f\u6d77\u5929\uff0c\u4f46\u6c5f\u6d77\u5929\u56e0\u8981\u8fd0\u529f\u62a4\u8eab\uff0c\u4e5f\u5c31\u53ea\u80fd\u4e0e\u7ae5\u59e5\u59e5\u6253\u6210\u5e73\u624b\u3002\u8c37\u4e4b\u534e\u7f13\u8fc7\u53e3\u6c14\uff0c\u8fd0\u529f \u9a71\u6bd2\u4e4b\u540e\uff0c\u5251\u5149\u53c8\u662f\u970d\u970d\u5c55\u5f00\uff0c\u6062\u590d\u4e86\u539f\u5148\u7684\u5a01\u529b\u3002\u767b\u65f6\u4e3b\u5ba2\u6613\u52bf\uff0c\u628a\u7ae5\u59e5\u59e5\u8feb\u788d\u53ea\u6709\u62db\u67b6 \u4e4b\u529f\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u767e\u5fd9\u4e2d\u62bd\u6687\u95ee\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u89c1\u83b2\u59b9\uff1f\u201d\u539f\u6765\u4ed6\u5728\u6606\u5e03\u5170\u56fd\u4e0e\u534e\u5929\u98ce\u89c1\u9762\u4e4b\u540e\uff0c\u5df2 \u77e5\u8c37\u4e2d\u83b2\u9047\u6551\u4e4b\u4e8b\uff0c\u8fd9\u6b21\u8d76\u6765\u9099\u5c71\uff0c\u5c31\u662f\u6025\u4e8e\u8981\u89c1\u8c37\u4e2d\u83b2\u7684\u3002\u8c37\u4e4b\u534e\u5fc3\u5934\u4e00\u9178\uff0c\u8bf4\u9053\uff1a\u201c\u83b2 \u513f\u5df2\u843d\u5728\u4ed6\u4eec\u624b\u4e2d\uff0c\u54b1\u4eec\u53ea\u6709\u6293\u7740\u8fd9\u4e2a\u5996\u5987\uff0c\u597d\u4e0e\u4ed6\u4eec\u4ea4\u6362\u3002\u201d \u3000\u3000\u4f46\u6c5f\u3001\u8c37\u4e8c\u4eba\u867d\u5360\u4e0a\u98ce\uff0c\u4e0b\u65f6\u4e4b\u95f4\uff0c\u5374\u4e5f\u672a\u80fd\u5c06\u7ae5\u59e5\u59e5\u62ff\u4e0b\u3002\u8fd9\u65f6\u4ef2\u957f\u7edf\u4e0e\u7ffc\u4ef2\u725f\u8fd9\u8fb9 \uff0c\u5374\u6e10\u6e10\u611f\u5230\u96be\u4ee5\u62b5\u654c\u3002\u6587\u5ef7\u58c1\u4ee5\u4e09\u8c61\u795e\u529f\u4e0e\u6bd2\u529f\u6df7\u5408\u8fd0\u7528\uff0c\u4e00\u53d1\u638c\u4fbf\u662f\u4e00\u80a1\u8165\u98ce\uff0c\u4ef2\u3001\u7ffc \u4e8c\u4eba\u4e0d\u80fd\u4e0d\u95ed\u4e86\u547c\u5438\uff0c\u79cd\u957f\u7edf\u6709\u6df7\u5143\u4e00\u6c14\u529f\u62a4\u8eab\uff0c\u8fd8\u597d\u4e00\u4e9b\uff0c\u7ffc\u4ef2\u725f\u5e74\u7eaa\u8001\u8fc8\uff0c\u5185\u529f\u4e5f\u7a0d\u900a \u4e00\u7b79\uff0c\u65f6\u95f4\u957f\u4e86 \u3000\u3000\u4f5f\u5143\u5947\u5524\u6765\u4e86\u4e03\u540d\u624b\u4e0b\uff0c\u8fd9\u4e03\u4eba\u90fd\u662f\u6e05\u5bab\u7684\u4e00\u7b49\u5f85\u536b\uff0c\u5404\u6709\u72ec\u5230\u6b66\u529f\uff0c\u56f4\u7740\u4e86\u8427\u80b2\u5cf0\u4e0e \u62b1\u62d9\u9053\u4eba\uff0c\u4f5f\u5143\u5947\u4fbf\u4e5f\u653e\u5fc3\u8df3\u51fa\u5708\u5b50\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u9053\uff1a\u201c\u64d2\u8d3c\u64d2\u738b\uff0c\u53ea\u8981\u628a\u8c37\u4e4b\u534e\u4e0e\u8fd9\u59d3\u6c5f\u7684\u5c0f\u5b50\u62ff\u4e0b\uff0c\u54b1\u4eec\u4fbf\u53ef\u5927\u83b7\u5168\u80dc\u3002\u201d\u4f5f \u5143\u5947\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u5408\u529b\u95ef\u8fdb\u706b\u7130\u5708\u4e2d\uff0c\u52a9\u7ae5\u59e5\u59e5\u4e00\u81c2\u4e4b\u529b\u3002\u201d\u53cc\u8f6e\u4ea4\u4e8e\u4e00\u624b\uff0c\u4e24\u4eba\u540c\u65f6 \u53d1\u638c\uff0c\u5c06\u51b7\u7130\u6bd2\u706b\u4e24\u8fb9\u5206\u5f00\uff0c\u8fdb\u5165\u90a3\u5708\u5b50\u4e4b\u5185\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u4e00\u638c\u5411\u6c5f\u6d77\u5929\u5288\u53bb\uff0c\u4f5f\u5143\u5947\u7684\u53cc\u8f6e\u5219\u5411\u8c37\u4e4b\u534e\u7838\u6765\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\uff0c\u53cc\u638c\u9f50\u98de\uff0c\u5de6\u638c\u8fce\u51fb\u6587\u5ef7\u58c1\uff0c\u53f3\u638c\u8fce\u51fb\u7ae5\u59e5\u59e5\uff0c\u6587\u5ef7\u58c1\u8eab\u5f62 \u5fae\u6643\uff0c\u7ae5\u59e5\u59e5\u5219\u5927\u6b65\u8de8\u524d\uff0c\u5c06\u6c5f\u6d77\u5929\u8feb\u9000\u4e09\u6b65\u3002\u4ed6\u4eec\u4e24\u4eba\u5408\u529b\u3002\u81ea\u662f\u8981\u6bd4\u6c5f\u6d77\u5929\u80dc\u8fc7\u4e00\u4e9b\uff0c \u5f62\u52bf\u767b\u65f6\u53c8\u626d\u8f6c\u8fc7\u6765\uff0c\u53d8\u5f97\u5bf9\u4ed6\u4eec\u6709\u5229\u4e86\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u54c8\u54c8\u5927\u7b11\uff1a\u201c\u59d3\u6c5f\u7684\u5c0f\u5b50\uff0c\u4eca\u65e5\u770b\u4f60\u8fd8\u6562\u901e\u5f3a\uff1f\u201d\u638c\u529b\u50ac\u7d27\uff0c\u4e0e\u7ae5\u59e5\u59e5\u5de6\u53f3\u5939\u653b \uff0c\u638c\u638c\u5288\u5411\u6c5f\u6d77\u5929\u8981\u5bb3\u3002\u6c5f\u6d77\u5929\u6c89\u4f4f\u4e86\u6c14\uff0c\u4f7f\u51fa\u5927\u987b\u5f25\u638c\u6cd5\uff0c\u8fd9\u4e00\u5957\u638c\u6cd5\u5965\u5999\u65e0\u7a77\uff0c\u521a\u67d4\u517c \u6d4e\uff0c\u7528\u4ee5\u9632\u8eab\uff0c\u6700\u5999\u4e0d\u8fc7\u3002\u6587\u5ef7\u58c1\u4e0e\u7ae5\u59e5\u59e5\u5168\u529b\u5f3a\u653b\uff0c\u7adf\u5982\u78b0\u5230\u4e86\u94dc\u5899\u94c1\u58c1\u4e00\u822c\uff0c\u653b\u4e0d\u8fdb\u53bb \uff0c\u4f46\u6c5f\u6d77\u5929\u60f3\u8981 \u3000\u3000\u8c37\u4e4b\u534e\u5355\u72ec\u5e94\u4ed8\u4f5f\u5143\u5947\uff0c\u5219\u6070\u6070\u6253\u6210\u5e73\u624b\u3002\u4f5f\u5143\u5947\u7684\u65e5\u6708\u53cc\u8f6e\u4e13\u514b\u5200\u5251\uff0c\u4ed6\u8fd9\u53cc\u8f6e\u53c8\u662f \u7eaf\u91d1\u94f8\u9020\uff0c\u8c37\u4e4b\u534e\u7684\u5b9d\u5251\u524a\u5b83\u4e0d\u52a8\uff0c\u4f5f\u5143\u5947\u5728\u5175\u5668\u4e0a\u5148\u5360\u4e86\u4fbf\u5b9c\u3002\u5e78\u4e8f\u8c37\u4e4b\u534e\u5728\u8f7b\u529f\u4e0a\u6709\u7279 \u6b8a\u7684\u9020\u8be3\uff0c\u5251\u6cd5\u8f7b\u7075\u7fd4\u52a8\uff0c\u968f\u6b65\u6362\u62db\uff0c\u6709\u9699\u5373\u653b\uff0c\u4e00\u6cbe\u5373\u9000\uff0c\u4f5f\u5143\u5947\u4e5f\u5360\u4e0d\u4e86\u4fbf\u5b9c\u3002\u4f46\u6c5f\u3001 \u8c37\u4e8c\u4eba\u88ab\u5bf9\u65b9\u9694 \u3000\u3000\u8fd9\u65f6\u5df2\u662f\u5168\u9762\u6df7\u6218\u7684\u5f62\u52bf\uff0c\u9099\u5c71\u6d3e\u5f1f\u5b50\u4f17\u5be1\u4e0d\u654c\uff0c\u5df2\u6709\u591a\u4eba\u53d7\u4f24\uff0c\u5f62\u52bf\u5341\u5206\u5371\u6025\u3002\u8c37\u4e4b \u534e\u5fc3\u5185\u5982\u711a\uff0c\u6697\u6697\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u60f3\u4e0d\u5230\u9099\u5c71\u6d3e\u7adf\u4e27\u9001\u5728\u6211\u7684\u624b\u4e0a\uff01\u201d\u6b63\u8981\u4e0b\u4ee4\uff0c\u53eb\u4f17\u5f1f\u5b50\u4e0d \u8981\u987e\u5979\uff0c\u5404\u81ea\u9003\u8d70\uff0c\u5ffd\u542c\u5f97\u654c\u4eba\u60ca\u53eb\u4e4b\u58f0\u56db\u8d77\uff0c\u9635\u52bf\u5927\u4e71\uff01 \u3000\u3000\u6700\u5148\u662f\u5929\u9b54\u6559\u4e3b\u90a3\u51e0\u540d\u4f8d\u5973\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u6559\u4e3b\uff0c\u6559\u4e3b\uff01\u201d\u968f\u540e\u662f\u5929\u9b54\u6559\u4f17\u5f92\u6b64\u8d77\u5f7c\u843d\u7684 \u8be7\u5f02\u4e4b\u58f0\uff0c\u201c\u54a6\uff0c\u600e\u4e48\u53c8\u6765\u4e86\u4e00\u4e2a\u6559\u4e3b\uff1f\u201d \u3000\u3000\u539f\u6765\u5728\u5c71\u4e0a\u7a81\u7136\u8d70\u4e0b\u56db\u4eba\uff0c\u524d\u9762\u4e00\u5bf9\u662f\u5929\u9b54\u6559\u4e3b\u4e0e\u5389\u590d\u751f\uff0c\u7b2c\u4e09\u4e2a\u662f\u91d1\u4e16\u9057\uff0c\u7b2c\u56db\u4e2a\u662f \u8c37\u4e2d\u83b2\u3002\u8c37\u4e4b\u534e\u4e0e\u6c5f\u6d77\u5927\u90fd\u662f\u60ca\u559c\u4ea4\u96c6\uff0c\u52c7\u6c14\u500d\u589e\u3002\u6c5f\u6d77\u5929\u5728\u7ae5\u59e5\u59e5\u4e0e\u6587\u5ef7\u58c1\u56f4\u653b\u4e4b\u4e0b\uff0c\u672c \u5df2\u6709\u70b9\u652f\u6301\u4e0d\u4f4f\uff0c\u6b64\u65f6\u89c1\u5e08\u7236\u5230\u6765\uff0c\u7cbe\u795e\u9661\u632f\uff0c\u867d\u7136\u4ecd\u5904\u4e0b\u98ce\uff0c\u5341\u62db\u4e4b\u4e2d\uff0c\u5df2\u662f\u53ef\u4ee5\u8fd8\u51fb\u4e09 \u62db\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5929\u9b54\u6559\u4e3b\u5df1\u662f\u6765\u5230\u90a3\u8f86\u5bab\u8f66\u524d\u9762\uff0c\u63ed\u5f00\u5e18\u5e55\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60 \u505a\u4f60\u7684\u4e00\u54c1\u592b\u4eba\uff0c\u5bcc\u8d35\u8363\u534e\uff0c\u90fd\u5df2\u6709\u4e86\uff0c\u8fd8\u4e0d\u5fc3\u6ee1\u610f\u8db3\u4e48\uff1f\u4f55\u5fc5\u5192\u5145\u4f60\u7684\u59b9\u5b50\uff0c\u6765\u6b64\u591a\u4e8b\uff1f \u59d0\u59d0\uff0c\u6211\u8bf7\u4f60\u8fd8\u662f\u56de\u53bb\u505a\u4f60\u7684\u4e00\u54c1\u592b\u4eba\u5427\uff01\u201d\u7f2a\u592b\u4eba\u6ee1\u9762\u7f9e\u60ed\uff0c\u8d70\u51fa\u5bab\u8f66\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u4fbf\u5373 \u63a9\u9762\u98de\u5954\uff0c\u4e0b\u5c71 \u3000\u3000\u5929\u9b54\u6559\u4e3b\u6717\u58f0\u559d\u9053\uff1a\u201c\u672c\u6559\u5f1f\u5b50\u542c\u7740\uff0c\u4e0d\u8bb8\u518d\u5728\u8fd9\u91cc\u6ecb\u4e8b\uff0c\u9650\u4f60\u4eec\u7acb\u523b\u56de\u7956\u682a\u5c71\u53bb\uff0c\u542c \u5019\u6211\u7684\u547d\u4ee4\u3002\u201d\u8fd9\u65f6\u5929\u6559\u95e8\u4e0b\u5f1f\u5b50\uff0c\u4e5f\u90fd\u5df2\u77e5\u9053\u662f\u4e0a\u4e86\u671d\u5ef7\u7684\u5f53\uff0c\u542c\u4e86\u6559\u4e3b\u7684\u547d\u4ee4\uff0c\u81ea\u662f\u4e2a \u4e2a\u4f9d\u4ece\uff0c\u5f53\u4e0b\u4e00\u54c4\u800c\u6563\u3002 \u3000\u3000\u91d1\u4e16\u9057\u8d70\u5230\u8349\u576a\u4e2d\u5fc3\uff0c\u84e6\u5730\u559d\u9053\uff1a\u201c\u72ec\u81c2\u795e\u5c3c\u4e0e\u5415\u5973\u5feb\u7684\u575f\u5893\u5728\u6b64\uff0c\u8c01\u6562\u5728\u6b64\u80e1\u95f9\u7684\u5c31 \u662f\u548c\u6211\u91d1\u4e16\u9057\u8fc7\u4e0d\u53bb\uff01\u5484\uff0c\u4f60\u4eec\u8fd9\u73ed\u6ca1\u957f\u7740\u773c\u775b\u7684\u81ed\u8d3c\uff0c\u8fd8\u4e0d\u7ed9\u6211\u7acb\u5373\u6eda\u4e0b\u5c71\u53bb\uff01\u201d\u58f0\u4f3c\u6d2a \u949f\uff0c\u9707\u5f97\u4f17\u4eba\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\u3002\u91d1\u4e16\u9057\u4ece\u524d\u53f7\u79f0\u6bd2\u624b\u75af\u4e10\uff0c\u8fd9\u4e9b\u6765\u7ed9\u5929\u9b54\u6559\u52a9\u62f3\u7684\u6c5f\u6e56\u4eba\u7269\uff0c \u8c01\u4e0d\u77e5\u9053\u91d1\u4e16\u9057 \u3000\u3000\u5929\u9b54\u6559\u5f1f\u5b50\u548c\u90a3\u4e9b\u6c5f\u6e56\u4eba\u7269\u8d70\u5f97\u51c0\u5c3d\uff0c\u53ea\u5269\u4e0b\u51e0\u5341\u540d\u6e05\u5bab\u4f8d\u536b\uff0c\u5176\u4e2d\u6709\u4e9b\u662f\u4e16\u88ad\u7684\u6b66\u5b98 \uff0c\u4ece\u672a\u8d70\u8fc7\u6c5f\u6e56\uff0c\u4e0d\u8bc6\u5f97\u91d1\u4e16\u9057\u662f\u4f55\u8bb8\u4eba\u7269\uff0c\u6709\u4e9b\u662f\u6295\u6548\u671d\u5ef7\u7684\u5de8\u76d7\uff0c\u542c\u8fc7\u91d1\u4e16\u9057\u7684\u540d\u5934\uff0c \u5fc3\u4e2d\u4e5f\u662f\u5341\u5206\u6050\u60e7\uff0c\u4f46\u4ed6\u4eec\u65e2\u662f\u53d7\u4e86\u671d\u5ef7\u7684\u4ff8\u7984\uff0c\u4e14\u7136\u6050\u60e7\uff0c\u5374\u4e5f\u4e0d\u6562\u4fbf\u5373\u523b\u9003\u8d70\u3002 \u3000\u3000\u90a3\u4e03\u4e2a\u56f4\u653b\u8427\u9752\u5cf0\u4e0e\u62b1\u62d9\u9053\u4eba\u7684\u5927\u5185\u9ad8\u624b\uff0c\u7279\u7740\u6b66\u827a\u9ad8\u5f3a\uff0c\u52c3\u7136\u5927\u6012\u4e00\u8427\u3001\u62b1\u4e8c\u4eba\u5df2\u7ecf \u7f62\u624b\u4e0d\u6597\uff0c\u8df3\u51fa\u5708\u5b50\uff0c\u8fd9\u4e03\u4e2a\u4e0d\u77e5\u6b7b\u6d3b\u7684\u5927\u5185\u9ad8\u624b\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u7acb\u5373\u6392\u6210\u9635\u52bf\uff0c\u5c06\u91d1\u4e16\u9057\u56f4 \u4f4f\uff0c\u9f50\u58f0\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u80c6\u6562\u5728\u8fd9\u91cc\u53d1\u53f7\u65bd\u4ee4\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u597d\u6574\u4ee5\u6687\uff0c\u8d1f\u624b\u95f2\u7acb\uff0c\u5c06\u4ed6\u4eec\u6392\u597d\u9635\u52bf\uff0c\u8fd9\u624d\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u8bc6\u5f97\u6211\u4e48\uff1f \u8fd9\u53ea\u80fd\u602a\u4f60\u4eec\u6709\u773c\u65e0\u73e0\uff01\u201d\u5927\u7b11\u58f0\u4e2d\uff0c\u51fa\u624b\u5982\u7535\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u7528\u4ec0\u4e48\u624b\u6cd5\uff0c\u7b11\u58f0\u672a\u6b47\uff0c\u5730\u4e0a\u5df1 \u6563\u843d\u4e86\u5341\u56db\u9897\u8840\u6dcb\u6dcb\u7684\u773c\u73e0\u3002\u539f\u6765\u4ed6\u5728\u4e3e\u624b\u4e4b\u95f4\uff0c\u4fbf\u628a\u8fd9\u4e03\u540d\u5927\u5185\u9ad8\u624b\u7684\u773c\u73e0\u5168\u90fd\u6316\u4e86\u3002\u8fd9 \u4e00\u4e0b\u53ea\u5413\u5f97\u90a3\u4e9b \u3000\u3000\u5e72\u6208\u6b62\uff0c\u6218\u4e91\u6d88\uff0c\u9099\u5c71\u4e4b\u4e0a\uff0c\u5c31\u53ea\u5269\u4e0b\u7ae5\u59e5\u59e5\u7b49\u4eba\uff0c\u8fd8\u5728\u90a3\u6bd2\u706b\u5708\u4e2d\uff0c\u4f5c\u56f0\u517d\u4e4b\u6597\u4e86\u3002 \u7ae5\u59e5\u59e5\u89c1\u91d1\u4e16\u9057\u5230\u6765\uff0c\u5df2\u77e5\u4e00\u8d25\u6d82\u5730\uff0c\u96be\u4ee5\u633d\u56de\uff0c\u4f46\u5fc3\u4e2d\u8fd8\u5b58\u7740\u4fa5\u5e78\u7684\u5ff5\u5934\uff0c\u731b\u5730\u559d\u9053\uff1a\u201c \u91d1\u4e16\u9057\uff0c\u6211\u4e0e\u4f60\u62fc\u4e86\uff01\u201d\u628a\u624b\u4e00\u626c\uff0c\u6ce2\u6ce2\u4e24\u58f0\uff0c\u98de\u51fa\u4e86\u4e24\u56e2\u6bd2\u706b\uff0c\u5979\u4e5f\u77e5\u9053\u51b7\u7130\u6bd2\u706b\u4e5f\u672a\u5fc5 \u5c31\u80fd\u4f24\u5bb3\u5f97\u4e86\u91d1 \u3000\u3000\u90a3\u4e24\u56e2\u6bd2\u706b\u98de\u5230\u91d1\u4e16\u9057\u5934\u9876\uff0c\u500f\u5730\u4f26\u4e3a\u4e00\u7247\u7ea2\u4e91\uff0c\u5411\u91d1\u4e16\u9057\u5f53\u5934\u7f69\u4e0b\uff0c\u5a01\u52bf\u4e4b\u731b\uff0c\u65e0\u4ee5 \u590d\u52a0\u3002\u91d1\u4e16\u9057\u5bbd\u888d\u5927\u8896\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u795e\u8272\u81ea\u5982\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u7c73\u7c92\u4e4b\u73e0\uff0c\u4e5f\u653e\u5149\u534e\uff01\u201d\u8bf4\u4e5f \u5947\u602a\uff0c\u90a3\u7247\u7ea2\u4e91\uff0c\u7ed9\u4ed6\u4e00\u6307\uff0c\u767b\u65f6\u5316\u4f5c\u4e86\u4e00\u9053\u706b\u5149\uff0c\u5012\u9000\u56de\u53bb\uff0c\u5c04\u5165\u90a3\u6bd2\u706b\u5708\u4e2d\u3002\u91d1\u4e16\u9057\u6b65 \u5c65\u4ece\u5bb9\uff0c\u4e5f\u8d70\u4e86 \u3000\u3000\u5708\u4e2d\u7684\u4e94\u4e2a\u4eba\u89c1\u6bd2\u706b\u5c04\u6765\uff0c\u7eb7\u7eb7\u95ea\u907f\uff0c\u4f46\u90a3\u4e00\u9053\u706b\u5149\uff0c\u7adf\u4f3c\u957f\u7740\u773c\u775b\u4f3c\u7684\uff0c\u4e0d\u70e7\u522b\u4eba\uff0c \u53ea\u70e7\u7ae5\u59e5\u59e5\uff0c\u53ea\u542c\u5f97\u4e00\u58f0\u60e8\u547c\uff0c\u7ae5\u59e5\u59e5\u5728\u6bd2\u706b\u8986\u7f69\u4e4b\u4e2d\uff0c\u9877\u523b\u5316\u4e3a\u7070\u70ec\uff01 \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u5bb3\u4eba\u7ec8\u987b\u5bb3\u5df1\uff0c\u53ef\u6212\u53ef\u6212\uff01\u201d\u8eab\u5f62\u4e00\u4e2a\u76d8\u65cb\uff0c\u5411\u4e1c\u5357\u897f\u5317\uff0c\u62cd\u51fa\u56db\u638c\uff0c\u5c06 \u6b8b\u4f59\u7684\u51b7\u7130\u6bd2\u706b\uff0c\u5c3d\u90fd\u6251\u706d\u3002 \u3000\u3000\u6587\u5ef7\u58c1\u4e0e\u90a3\u5927\u5185\u7b2c\u4e00\u9ad8\u624b\u4f5f\u5143\u5947\u90fd\u6253\u5b9a\u4e86\u4e0d\u662f\u654c\u6b7b\u4fbf\u662f\u6211\u4ea1\u7684\u5ff5\u5934\uff0c\u4e0d\u7ea6\u800c\u540c\uff0c\u4e00\u9f50\u5411 \u91d1\u4e16\u9057\u6251\u53bb\u3002\u6587\u5ef7\u58c1\u5148\u5230\uff0c\u72e0\u72e0\u5730\u4e00\u54ac\u54ac\u7834\u820c\u5c16\uff0c\u8fd0\u8db3\u4e86\u4e09\u8c61\u795e\u529f\uff0c\u638c\u529b\u6709\u5982\u6392\u5c71\u5012\u6d77\uff0c\u5411 \u91d1\u4e16\u9057\u75db\u4e0b\u6740\u624b\u3002\u91d1\u4e16\u9057\u80cc\u8d1f\u53cc\u624b\uff0c\u6447\u5934\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d\u53ea\u542c\u5f97\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u6587\u5ef7 \u58c1\u90a3\u4e00\u638c\u5df2\u662f\u7ed3 \u3000\u3000\u91d1\u4e16\u9057\u7adf\u7136\u6beb\u4e0d\u62b5\u5fa1\uff0c\u8ba9\u6587\u5ef7\u58c1\u6253\u4e86\u4e00\u638c\uff0c\u6b64\u4e8b\u5927\u51fa\u4f17\u4eba\u610f\u5916\uff0c\u4e0d\u7981\u54d7\u7136\u60ca\u547c\uff0c\u60ca\u547c\u4e4b \u58f0\u72b9\u672a\u5e73\u9759\uff0c\u53ea\u89c1\u6587\u5ef7\u58c1\u5df2\u662f\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u5410\u51fa\u4e86\u4e00\u5927\u53e3\u9c9c\u8840\uff0c\u9762\u5982\u91d1\u7eb8\uff0c\u6447\u6447\u6b32\u5760\u3002 \u3000\u3000\u4f5f\u5143\u5947\u5927\u5403\u4e00\u60ca\uff0c\u4f46\u4ed6\u53cc\u8f6e\u5df2\u7ecf\u63a8\u51fa\uff0c\u6536\u52bf\u4e0d\u53ca\uff0c\u91d1\u4e16\u9057\u9053\uff1a\u201c\u5bf9\u4f60\u8fd9\u72d7\u817f\u5b50\uff0c\u5904\u7f5a\u5e94 \u8be5\u4e25\u5389\u4e00\u4e9b\uff01\u201d\u6325\u8896\u4e00\u62c2\uff0c\u4f5f\u5143\u5947\u7684\u65e5\u6708\u53cc\u8f6e\u6253\u56de\u81ea\u8eab\uff0c\u4e24\u6761\u817f\u4ece\u819d\u76d6\u4ee5\u4e0b\u7adf\u7ed9\u81ea\u5df1\u7684\u53cc\u8f6e \u622a\u65ad\uff0c\u91d1\u4e16\u9057\u4e00\u624b\u5c06\u4ed6\u6293\u4e86\u8d77\u6765\uff0c\u6454\u5c06\u51fa\u53bb\uff0c\u987a\u624b\u53c8\u634f\u788e\u4e86\u4ed6\u7684\u7435\u7436\u9aa8\uff0c\u8bf4\u9053\uff1a\u201c\u72d7\u817f\u6253\u65ad \uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u4f60 \u3000\u3000\u91d1\u4e16\u9057\u6253\u53d1\u4e86\u4f5f\u5143\u5947\uff0c\u8fd9\u624d\u56de\u8fc7\u5934\u6765\uff0c\u5bf9\u6587\u5ef7\u58c1\u9053\uff1a\u201c\u4f60\u5728\u6b66\u5b66\u4e0a\u53e6\u8f9f\u8e4a\u5f84\uff0c\u7ec3\u6210\u4e86\u4e09 \u8c61\u795e\u529f\uff0c\u8bf4\u6765\u4e5f\u5927\u662f\u4e0d\u6613\u3002\u53ef\u60dc\u4f60\u4e0d\u7528\u4e8e\u6b63\u9014\uff0c\u6211\u53ea\u5f97\u628a\u4f60\u7684\u6b66\u529f\u5e9f\u4e86\u3002\u4f60\u56de\u53bb\u5427\uff0c\u4f46\u613f\u4f60 \u4ece\u4eca\u4ee5\u540e\uff0c\u6f5c\u5fc3\u6b66\u5b66\uff0c\u867d\u662f\u4e0d\u80fd\u4e89\u5f3a\u6597\u80dc\uff0c\u4e5f\u8fd8\u53ef\u4ee5\u6210\u4e3a\u4e00\u6d3e\u5b97\u5e08\u3002\u201d \u3000\u3000\u6587\u5ef7\u58c1\u60f3\u4e0d\u5230\u91d1\u4e16\u9057\u5bf9\u4ed6\u5982\u6b64\u5bbd\u5bb9\uff0c\u6ee1\u9762\u7f9e\u60ed\uff0c\u4e5f\u81ea\u4e0b\u5c71\u53bb\u4e86\u3002 \u3000\u3000\u8840\u96e8\u8165\u98ce\u8fc7\u540e\uff0c\u9099\u5c71\u4e0a\u53c8\u662f\u4e3d\u65e5\u6674\u5929\uff0c\u4f17\u4eba\u6b22\u58f0\u96f7\u52a8\uff0c\u9099\u5c71\u6d3e\u5f1f\u5b50\u66f4\u662f\u4e89\u7740\u4e0a\u6765\u5411\u91d1\u4e16 \u9057\u9053\u8c22\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5bf9\u8fd9\u4e00\u5207\u5374\u4f3c\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u540c\uff0c\u6b64\u65f6\u6b64\u9645\uff0c\u4ed6\u773c\u4e2d\u5fc3\u5e95\uff0c\u53ea\u6709\u7740\u4e00\u4e2a\u8c37\u4e4b\u534e \u3002\u4e8c\u5341\u5e74\u6765\uff0c\u5f3a\u81ea\u6291\u5236\u3001\u6c89\u57cb\u5fc3\u5e95\u7684\u70ed\u60c5\u71c3\u70e7\u8d77\u6765\u4e86\uff0c\u6839\u672c\u5c31\u4e0d\u7406\u4f1a\u6709\u591a\u5c11\u53cc\u773c\u775b\u5728\u770b\u7740\u4ed6 \u4eec\uff0c\u5728\u6b22\u547c\u96f7\u52a8\u4e4b\u4e2d\uff0c\u91d1\u4e16\u9057\u7f13\u7f13\u7684\u5411\u8c37\u4e4b\u534e\u8d70\u53bb\u3002 \u3000\u3000\u7ffc\u4ef2\u725f\u6084\u6084\u6253\u4e86\u4e00\u4e2a\u624b\u52bf\uff0c\u90b1\u5c71\u6d3e\u5f1f\u5b50\u4e5f\u4e2a\u4e2a\u660e\u767d\u4e86\u4ed6\u7684\u610f\u601d\uff0c\u5fc3\u4e2d\u4f46\u662f\u60f3\u9053\uff1a\u201c\u6211\u4eec \u8d1f\u4e8e\u91d1\u5927\u4fa0\u7684\u5b9e\u5728\u592a\u591a\uff0c\u638c\u95e8\u4eba\u4e3a\u672c\u6d3e\u4e5f\u865a\u5ea6\u4e86\u4e8c\u5341\u8f7d\u9752\u6625\uff0c\u4f46\u613f\u4ed6\u4eec\u6709\u60c5\u4eba\u7ec8\u6210\u7737\u5c5e\u3002\u6211 \u4eec\u4e5f\u4e0d\u5e94\u518d\u53bb\u5e72\u6270\u4ed6\u4eec\u4e86\u3002\u201d\u91d1\u4e16\u9057\u4e0e\u8c37\u4e4b\u534e\u4e4b\u95f4\u7684\u60b2\u6b22\u79bb\u5408\uff0c\u65e0\u5c3d\u76f8\u601d\uff0c\u9099\u5c71\u6d3e\u7684\u5f1f\u5b50\u90fd \u662f\u77e5\u9053\u7684\u3002 \u3000\u3000\u4e8c\u5341\u5e74\u524d\uff0c\u4e5f\u6b63\u662f\u6e05\u660e\u65f6\u8282\uff0c\u91d1\u4e16\u9057\u7b2c\u4e00\u6b21\u4e0a\u9099\u5c71\u6765\u7ed9\u5415\u56db\u5a18\u626b\u5893\uff0c\u90a3\u65f6\u662f\u66f9\u9526\u513f\u505a\u638c \u95e8\u4eba\uff0c\u5979\u5c06\u91d1\u4e16\u9057\u4fae\u8fb1\u4e00\u756a\uff0c\u9a82\u4ed6\u662f\u5927\u9b54\u5934\uff0c\u51e0\u4e4e\u8981\u628a\u4ed6\u8d76\u4e0b\u5c71\u53bb\u3002\u5728\u4e00\u6bb5\u76f8\u5f53\u957f\u7684\u65f6\u95f4\u4e4b \u5185\uff0c\u7531\u4e8e\u66f9\u9526\u513f\u5bf9\u91d1\u4e16\u9057\u4e0d\u80fd\u8c05\u89e3\uff0c\u9099\u5c71\u6d3e\u8fd8\u66fe\u5c06\u4ed6\u5f53\u4f5c\u654c\u4eba\uff0c\u751a\u81f3\u5bf9\u8c37\u4e4b\u534e\u4e0e\u4ed6\u7684\u5f80\u6765\u4e5f \u9887\u591a\u975e\u8bae\u3002\u5374\u60f3 \u3000\u3000\u8c37\u4e2d\u83b2\u548c\u6c5f\u6d77\u5929\u4e5f\u5728\u4f5c\u7740\u4f1a\u5fc3\u7684\u5fae\u7b11\uff0c\u6c5f\u6d77\u5929\u793a\u610f\u53eb\u5979\u4e0d\u53ef\u6253\u6270\u5e08\u7236\uff0c\u6084\u6084\u7684\u4e5f\u6e9c\u4e86\u51fa \u6765\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u770b\u89c1\u6c5f\u6d77\u5929\u5411\u5979\u8d70\u6765\uff0c\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u8f9b\u9178\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u5e08\u7236\u7684\u4e8c\u5341\u8f7d\u76f8\u601d \u5012\u662f\u6709\u4e86\u7ed3\u679c\u4e86\u3002\u4ece\u524d\u6211\u62c5\u5fc3\u6211\u548c\u5e08\u7236\u7684\u547d\u8fd0\u76f8\u540c\uff0c\u540c\u662f\u7ea2\u989c\u8584\u547d\uff0c\u60f3\u4e0d\u5230\u5374\u7ec8\u4e8e\u4e24\u6837\uff0c\u5979 \u6709\u4e86\u7ed3\u679c\uff0c\u800c\u6211\u8fd8\u662f\u6c34\u6708\u955c\u82b1\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u5bb9\u6ee1\u9762\uff0c\u638f\u51fa\u8c37\u4e2d\u83b2\u5c0f\u65f6\u5019\u9001\u7ed9\u4ed6\u7684\u90a3\u65b9\u7ee3\u6709\u83b2\u82b1\u7684\u624b\u5e15\uff0c\u8f7b\u8f7b\u7684\u7ed9\u5979\u62ed\u53bb\u4e86 \u8138\u4e0a\u7684\u6cea\u73e0\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u5230\u5e95\u53c8\u5728\u4e00\u8d77\u4e86\uff0c\u83b2\u59b9\uff0c\u4f60\u8be5\u6b22\u559c\u624d\u662f\uff0c\u8fd8\u4f24\u5fc3\u4ec0\u4e48\uff1f\u90a3\u65e5 \u5728\u6606\u5e03\u5170\u56fd\uff0c\u4f60\u4e0d\u80af\u51fa\u6765\u4e0e\u6211\u89c1\u9762\uff0c\u771f\u662f\u628a\u6211\u6025\u6b7b\u4e86\uff1f\u201d\u8c37\u4e2d\u83b2\u9053\uff1a \u3000\u3000\u201c\u4f60\u8d76\u56de\u6765\u505a\u4ec0\u4e48\uff1f\u4f60\u4e0d\u662f\u89c1\u7740\u4e86\u534e\u59d0\u59d0\u4e86\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u89c1\u7740\u4e86\u3002\u201d\u8c37 \u4e2d\u83b2\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u8be5\u7559\u5728\u5979\u7684\u8eab\u8fb9\uff0c\u600e\u53ef\u4e00\u89c1\u4e86\u5979\uff0c\u53c8\u628a\u5979\u629b\u4e0b\uff1f\u4f60\u7eb5\u8981\u89c1\u6211\uff0c\u4e5f\u8be5\u3001\u4e5f\u8be5 \u2026\u2026\u201d\u5979\u60f3\u8bf4\u7684\u9053\uff1a\u201c\u4e5f\u8be5\u5728\u4f60\u4eec\u7ed3\u5a5a\u4e4b\u540e\u3002\u201d\u4f46\u8fd9\u53e5\u8bdd\u5230\u4e86\u53e3\u8fb9\uff0c\u5374\u53c8\u7981\u4e0d\u4f4f\u4f24\u5fc3\u6cea\u4e0b\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u518d\u4e00\u6b21\u7ed9\u5979\u62ed\u53bb\u6cea\u73e0\uff0c\u5fcd\u4e0d\u7740\u5c31\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5fc3\u4e2d\u7740\u607c\uff1a\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u731c\u6211\u4e3a\u4ec0\u4e48\u8d76\u7740\u56de\u6765\uff1f\u201d\u8c37\u4e2d\u83b2 \u6014\u4e86\u4e00\u6014\uff0c\u201c\u96be\u9053\u4e0d\u662f\u4e3a\u6211\uff1f\u201d\u832b\u7136\u95ee\u9053\uff1a \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u662f\u7ed9\u4f60\u9001\u4e24\u5f20\u559c\u67ec\u6765\u7684\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u5fc3\u5934\u201c\u535c\u901a\u201d\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u559c\u67ec\uff1f\u201d\u63a5\u4e86\u8fc7\u6765\uff0c\u6253\u5f00\u7b2c\u4e00\u5c01\u6765\u770b\uff0c\u53ea\u89c1\u662f\u53f6 \u51b2\u9704\u4e0e\u6c34\u4e91\u4e3b\u5e84\u4e3b\u4e91\u53ec\u4ee3\u8868\u7537\u5973\u53cc\u65b9\u5bb6\u957f\u5177\u540d\u7684\u8bf7\u67ec\uff0c\u65b0\u90ce\u662f\u5510\u52aa\u73e0\u7a46\uff0c\u65b0\u5a18\u662f\u4e91\u58c1\uff0c\u65e5\u671f \u662f\u4eca\u5e74\u7684\u4e2d\u79cb\uff0c\u5730\u70b9\u662f\u9a6c\u8428\u513f\u56fd\u7684\u738b\u5bab\u3002\u6309\u7167\u56fd\u738b\u7684\u5927\u5a5a\u4eea\u793c\uff0c\u8fd9\u8bf7\u67ec\u672c\u6765\u5e94\u7531\u9a6c\u8428\u513f\u56fd\u7684 \u5185\u52a1\u5e9c\u53d1\u51fa\u7684\uff0c \u3000\u3000\u8c37\u4e2d\u83b2\u559c\u9053\uff1a\u201c\u6211\u53c8\u6dfb\u4e86\u4e00\u4f4d\u65b0\u5ac2\u5ac2\u4e86\u3002\u4e91\u59d1\u5a18\u7aef\u5e84\u6148\u539a\uff0c\u6b63\u5408\u505a\u6211\u56fd\u7684\u738b\u540e\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u518d\u770b\u8fd9\u5f20\u3002\u201d \u3000\u3000\u7b2c\u4e00\u5c01\u559c\u67ec\u8fd8\u5728\u8c37\u4e2d\u83b2\u610f\u6599\u4e4b\u4e2d\uff0c\u7b2c\u4e8c\u5c01\u559c\u67ec\u5374\u5927\u51fa\u5979\u610f\u6599\u4e4b\u5916\u3002\u8fd9\u4e5f\u662f\u4e00\u5f20\u7531\u7537\u5973\u53cc \u65b9\u5bb6\u957f\u5177\u540d\u7684\u8bf7\u67ec\uff0c\u6c34\u4e91\u5e84\u5e84\u4e3b\u4e91\u53ec\u53d8\u4e86\u7537\u65b9\u7684\u5bb6\u957f\uff0c\u5973\u65b9\u7684\u5bb6\u957f\u5219\u662f\u534e\u5929\u98ce\u3002\u8fd9\u662f\u4e91\u743c\u548c \u534e\u4e91\u78a7\u7ed3\u5a5a\u7684\u8bf7\u67ec\uff0c\u65e5\u671f\u4e5f\u662f\u516b\u6708\u5341\u4e94\u65e5\uff0c\u5730\u70b9\u4e5f\u662f\u9a6c\u8428\u513f\u56fd\u7684\u738b\u5bab\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u5446\u4e86\u4e00\u5446\uff0c\u559c\u51fa\u671b\u5916\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u771f\u662f\u610f\u60f3\u4e0d\u5230\uff0c\u610f\u60f3\u4e0d\u5230\uff01\u4e24\u5bf9\u65b0\u4eba\u540c\u5728\u4eba \u6708\u56e2\u5706\u7684\u4e2d\u79cb\u8282\u7ed3\u5a5a\uff0c\u771f\u6709\u610f\u601d\u3002\u771f\u6709\u610f\u601d\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u516b\u6708\u5341\u4e94\u8fd9\u5929\u662f\u6c49\u4eba\u7684\u4e2d\u79cb\u8282\uff0c\u5728\u4f60\u4eec\u9a6c\u8428\u513f\u56fd\uff0c\u53c8\u662f\u4e00\u4e2a\u65b0\u7684\u8282\u65e5\u3002\u201d \u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u4ec0\u4e48\u8282\u65e5\uff0c\u6211\u600e\u4e48\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u9a6c\u8428\u513f\u548c\u6606\u5e03\u5170\u4e24\u56fd\u767e\u59d3\uff0c\u4e3a\u4e86\u540c\u5e86\u5347\u5e73\uff0c\u9009\u5b9a\u8fd9\u4e00\u5929\u4e3a\u6d17\u5251\u8282\u3002\u4f60\u8fd8\u8bb0 \u5f97\u5417\uff1f\u8fd9\u4e00\u5929\u662f\u91d1\u9e70\u5bab\u5927\u4f1a\u7684\u65e5\u5b50\uff0c\u4e5f\u662f\u9a6c\u8428\u513f\u66b4\u541b\u88ab\u63a8\u7ffb\u7684\u65e5\u5b50\u3002\u7531\u4e8e\u4f60\u54e5\u54e5\u505a\u4e86\u56fd\u738b\uff0c \u90bb\u56fd\u4e5f\u5f97\u6cbe\u6069\u6cfd\uff0c\u5171\u4eab\u592a\u5e73\u3002\u767e\u59d3\u4eec\u8fd8\u904d\u4e86\u4e00\u652f\u6b4c\u3002\u2026\u2026\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6211\u542c\u8fc7\u8fd9\u652f\u6b4c\u3002\u662f\u4e0d\u662f\u8fd9\u6837\u5531\u7684\uff1a\u70fd\u70df\u6563\u51c0\uff0c\u51b0\u6cb3\u5982\u955c\uff0c\u6211\u8981\u5728\u51b0\u53ef\u6d17\u51c0 \u6211\u5b9d\u5251\u7684\u8840\u8165\uff0c\u4ece\u4eca\u540e\u6c38\u4eab\u592a\u5e73\u3002\u5e74\u8f7b\u4eba\u5f97\u5230\u7231\u60c5\uff0c\u8001\u5e74\u4eba\u5f97\u5230\u5b89\u5b81\u3002\u2026\u2026\u201d\u5531\u5230\u201c\u5e74\u8f7b\u4eba \u5f97\u5230\u7231\u60c5\u201d\u8fd9\u53e5\uff0c\u4e24\u9897\u5fc3\u5df2\u662f\u6026\u7136\u8df3\u52a8\uff0c\u4e0d\u89c9\u624b\u513f\u76f8\u63e1\uff0c\u8138\u513f\u76f8\u89d1\uff0c\u773c\u4e2d\u90fd\u71c3\u70e7\u7740\u7231\u60c5\u7684\u559c \u60a6\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5728\u52a8\u8eab\u53bb\u559d\u559c\u9152\u4e4b\u524d\uff0c\u6211\u5e26\u4f60\u53bb\u89c1\u6211\u7684\u53cc\u4eb2\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u505a\u4ec0\u4e48\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u7684\u4e24\u4f4d\u54e5\u54e5\u5df2\u5b89\u6392\u597d\u4e86\u2014\u2014\u8981\u53d1\u7b2c\u4e09\u5f20\u8bf7\u67ec\uff0c \u540c\u6837\u7684\u65e5\u671f\uff0c\u540c\u6837\u7684\u5730\u70b9\uff0c\u8fd9\u5f20\u8bf7\u67ec\u662f\u54b1\u4eec\u7684\u559c\u67ec\uff0c\u6211\u8981\u7980\u544a\u7239\u7239\uff0c\u8bf7\u4ed6\u4e3b\u5a5a\u3002\u83b2\u59b9\uff0c\u4f60\u4e0d \u4f1a\u57cb\u6028\u6211\u4eec\u64c5\u4f5c\u4e3b\u5f20\u5427\uff0c\u4e8b\u5148\u5c1a\u672a\u66fe\u5f97\u4f60\u70b9\u5934\uff1f\u73b0\u5728\u6211\u662f\u6c42\u4f60\u70b9\u5934\u6765\u4e86\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u7ea2\u6655\u6ee1\u9762\uff0c\u65e2\u4e0d\u70b9\u5934\uff0c\u4e5f\u4e0d\u6447\u5934\u3002\u6c5f\u6d77\u5929\u7740\u6025\u9053\uff1a \u3000\u3000\u201c\u83b2\u59b9\uff0c\u4f60\u4e0d\u613f\u610f\uff1f\u201d\u8c37\u4e2d\u83b2\u4f4e\u58f0\u9053\uff1a\u201c\u4e0d\u613f\u610f\u6211\u5c31\u6447\u5934\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u5927\u559c\u9053\uff1a\u201c\u54b1\u4eec \u53bb\u7980\u544a\u5e08\u7236\u53bb\uff0c\u54a6\uff0c\u5e08\u7236\u5462\uff1f\u4ed6\u4eec\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u4e0e\u8c37\u4e4b\u534e\u53c8\u662f\u4e00\u756a\u60c5\u666f\u3002\u9099\u5c71\u4e0a\u5c71\u82b1\u904d\u5730\uff0c\u6709\u5927\u7ea2\u739b\u7459\u822c\u7684\u8336\u82b1\uff0c\u6709\u7f28\u7edc\u62ab\u5782\u7684 \u675c\u9e43\u82b1\uff0c\u6709\u9752\u4e1d\u82b1\u854a\u9576\u7740\u4e73\u767d\u82b1\u74e3\u7684\u62a5\u6625\u82b1\uff0c\u767e\u6001\u5e72\u59ff\uff0c\u4e89\u598d\u6597\u4e3d\uff0c\u5bc6\u5bc6\u4e1b\u4e1b\uff0c\u6ee1\u773c\u90fd\u662f\u3002 \u91d1\u4e16\u9057\u4e0e\u8c37\u4e4b\u534e\u6b63\u5728\u82b1\u4e1b\u4e2d\u643a\u624b\u540c\u884c\u3002\u4e5f\u6b63\u662f\u4e8c\u5341\u5e74\u524d\u7684\u4eca\u65e5\uff0c\u91d1\u4e16\u9057\u5728\u8fd9\u6761\u8def\u4e0a\u9001\u8c37\u4e4b\u534e \u4e0b\u5c71\uff0c\u90a3\u65f6\u4ed6\u4eec \u3000\u3000\u91d1\u4e16\u9057\u6458\u4e0b\u4e00\u6735\u91ce\u82b1\uff0c\u559f\u7136\u8bf4\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u82b1\u5f00\u82b1\u843d\uff0c\u4e4b\u534e\uff0c\u4f60\u5acc\u6211\u6765\u8fdf\u4e86\u4e48\uff1f\u201d\u8c37\u4e4b \u534e\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u82b1\u548c\u4e8c\u5341\u5e74\u524d\u5e76\u6ca1\u4e24\u6837\uff0c\u54b1\u4eec\u4e8c\u4eba\u4e5f\u662f\u4e00\u822c\u3002\u4f60\u518d\u8fdf\u4e8c\u5341\u5e74\uff0c\u6211\u4e5f\u662f\u7167\u6837 \u7b49\u4f60\u7684\u3002\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60\u8fd8\u8bb0\u5f97\u4f60\u9001\u6211\u7684\u90a3\u4e24\u53e5\u8bd7\u5417\uff0c\u7eb5\u6709\u6d6e\u4e91\u80fd\u853d\u65e5\uff0c\u9634\u973e\u4ea6\u4ec5\u662f\u987b\u81fe \u3002\u4e8c\u5341\u5e74\u6765\u51e0\u756a \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u6765\u54b1\u4eec\u867d\u662f\u4f1a\u5c11\u79bb\u591a\uff0c\u4f46\u5c31\u5728\u4f60\u6d77\u5916\u98d8\u6d41\u3001\u5929\u6daf\u8fdc\u9694\u7684\u65f6\u5019\uff0c\u6211\u4e5f \u603b\u662f\u89c9\u5f97\u4f60\u5c31\u4f3c\u5728\u6211\u8eab\u8fb9\u3002\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u54a6\uff0c\u5947\u602a\uff0c\u6211\u4e5f\u662f\u8fd9\u6837\u3002\u8fd9\u4e9b\u5e74\u6765\uff0c\u6bcf\u5f53\u6211\u90c1\u95f7 \u96be\u582a\u7684\u65f6\u5019\uff0c\u4f60\u5c31\u50cf\u51fa\u73b0\u5728\u6211\u7684\u773c\u524d\u3002\u201d\u8c37\u4e4b\u534e\u5ae3\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u54b1\u4eec\u53ef\u771f\u662f \u4e24\u5fc3\u5982\u4e00\u7684\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u4ece\u672a\u89c1\u8fc7\u8c37\u4e4b\u534e\u7b11\u5f97\u8fd9\u6837\u6b22\u4e50\uff0c\u53ea\u89c9\u5f97\u5979\u7b11\u9765\u5982\u82b1\uff0c\u6bd4\u8d77\u5979\u4e8c\u5341\u5e74\u524d\u662f\u5c11\u5973\u7684\u65f6 \u5019\u66f4\u53ef\u7231\uff0c\u4e0d\u89c9\u7d27\u7d27\u63e1\u7262\u5979\u7684\u53cc\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4ece\u4eca\u4e4b\u540e\uff0c\u54b1\u4eec\u518d\u4e5f\u4e0d\u4f1a\u5206\u5f00\uff0c\u8fdc\u9694\u5929\u6daf\u4e86\u3002 \u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e0e\u8c37\u4e2d\u83b2\u8fdc\u8fdc\u671b\u89c1\u4ed6\u4eec\u7684\u5e08\u7236\u5728\u82b1\u4e1b\u4e2d\u76f8\u504e\u76f8\u4f9d\uff0c\u4e24\u4eba\u4e5f\u4e0d\u7981\u76f8\u89c6\u800c\u7b11\u4e86\u3002\u6c5f\u6d77 \u5929\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u5fc5\u5fd9\u7740\u7980\u544a\u5e08\u7236\u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u4e5f\u7b11\u9053\uff1a\u201c\u770b\u6765\u4e0d\u4e45\u8fd8\u4f1a\u6709\u7b2c\u56db\u5f20\u559c\u67ec\uff0c\u66f4\u8981 \u8f70\u52a8\u6b66\u6797\uff0c\u4f20\u4e3a\u4f73\u8bdd\uff01\u201d \u3000\u3000\u8c37\u4e4b\u534e\u9053\uff1a\u201c\u4f3c\u662f\u6709\u4eba\u5411\u8fd9\u8fb9\u8d70\u6765\uff1f\u201d\u91d1\u4e16\u9057\u62ac\u8d77\u5934\u6765\uff0c\u9690\u7ea6\u8fd8\u53ef\u4ee5\u89c1\u5230\u5f92\u5f1f\u7684\u80cc\u5f71\uff0c \u7b11\u9053\uff1a\u201c\u662f\u6d77\u513f\u548c\u83b2\u513f\uff0c\u5411\u90a3\u8fb9\u8d70\u8fc7\u53bb\u4e86\uff0c\u4e0d\u8981\u60ca\u52a8\u4ed6\u4eec\u3002\u201d \u3000\u3000\u8c37\u4e4b\u534e\u7b11\u9053\uff1a\u201c\u4ece\u524d\u6211\u5f88\u62c5\u5fc3\u4ed6\u4eec\u91cd\u8e48\u6211\u4eec\u7684\u8986\u8f99\uff0c\u5982\u4eca\u6211\u5374\u662f\u9ad8\u5174\u4ed6\u4eec\u7684\u547d\u8fd0\u548c\u6211\u4eec \u76f8\u540c\u4e86\u3002\u4e16\u9057\uff0c\u5f85\u6211\u5c06\u6765\u4f20\u4e86\u638c\u95e8\u4e4b\u4f4d\u7ed9\u5979\uff0c\u6211\u5c31\u53ef\u4ee5\u548c\u4f60\u6e56\u6d77\u76f8\u968f\uff0c\u8fc7\u795e\u4ed9\u822c\u7684\u65e5\u5b50\u4e86\u3002 \u4e16\u9057\uff0c\u4f60\u9ad8\u5174\u5417\uff1f\u201d\u91d1\u4e16\u9057\u9053\uff1a\u201c\u8fd9\u6b63\u662f\u6211\u68a6\u5bd0\u4ee5\u6c42\u4e4b\u4e8b\u3002\u4eba\u751f\u5982\u6b64\uff0c\u8fd8\u6709\u4f55\u61be\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u4eba\u95f4\u81ea\u662f\u6709\u60c5\u75f4\uff0c\u6b64\u6068\u4e0d\u5173\u98ce\u4e0e\u6708\u3002 \u3000\u3000\uff08\u5168\u4e66\u5b8c\uff09"}),showOnHomepage:!0}]}}]);