"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[502],{7518:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u6563\u82b1\u5973\u4fa0",content:(0,t.jsx)(t.Fragment,{children:"\u7b2c\u4e00\u56de\u3000\u53bb\u9053\u5c71\u5c71\u6751\u3000\u987d\u7ae5\u60ca\u4fa0\u58eb\u3000\u6df1\u5bb5\u77f3\u5ba4\u3000\u79d8\u8bcf\u5413\u9556\u5e08 \u3000\u3000\u4e07\u91cc\u6c5f\u5c71\u4e00\u671b\u6536\uff0c\u4e7e\u5764\u8c01\u4e2a\u4e3b\u6c89\u6d6e\uff0c\u7a7a\u4f59\u738b\u6c14\u62b9\u9675\u79cb\uff1b\u767d\u8349\u65b0\u8bcd\u6d88\u6ede\u9152\uff1b\u4efb\u51ed\u77ed\u68a6\u9010\u5bd2 \u9e25\uff0c\u6563\u82b1\u4eba\u58eb\u5269\u95f2\u6101\u3002 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000 \u2014\u2014\u8c03\u5bc4\u6d63\u6eaa\u7eb1 \u3000\u3000\u5360\u9053\u5c71\u6751\uff0c\u4e00\u7fa4\u987d\u7ae5\u563b\u7b11\u7684\u58f0\u97f3\uff0c\u51b2\u7834\u4e86\u5c71\u8c37\u7684\u5bc2\u9759\u3002\u4e0d\u77e5\u662f\u56e0\u4e3a\u6709\u7956\u5f95\u5c71\u6321\u4f4f\u4e86\u897f\u5317 \u7684\u5bd2\u98ce\uff0c\u8fd8\u662f\u4eca\u5e74\u7684\u6625\u5929\u6765\u5f97\u7279\u522b\u65e9\uff0c\u5143\u5bb5\u624d\u8fc7\u4e86\u4e09\u5929\uff0c\u5c71\u5761\u4e0a\u5c31\u5f00\u904d\u4e86\u8336\u82b1\u548c\u675c\u9e43\u82b1\uff0c\u6709 \u6843\u7ea2\u82b1\u74e3\u5305\u8a8a\u91d1\u4e1d\u82b1\u854a\u7684\uff0c\u6709\u9752\u4e1d\u82b1\u854a\u9576\u7740\u4e73\u767d\u82b1\u74e3\u7684\uff0c\u8fd8\u6709\u7ea2\u91cc\u53c2\u767d\u576a\u82e5\u5927\u7ea2\u739b\u7476\u7684\uff0c\u628a \u8fd9\u5c71\u6751\u70b9\u7f00\u5f97\u82b1\u56e2\u9526\u7ee3\uff0c\u6625\u610f\u76ce\u7136\uff0c\u7956\u5f95\u5c71\u867d\u5728\u957f\u6c5f\u4ee5\u5317\u7684\u5c71\u4e1c\u5883\u5185\uff0c\u8fd9\u5c71\u6751\u7684\u666f\u8272\uff0c\u5374\u6709 \u70b9\u50cf\u6c5f\u5357\u7684\u65e9\u6625\u4e86\u3002 \u3000\u3000\u5c71\u6751\u91cc\u6709\u758f\u758f\u843d\u843d\u7684\u4eba\u5bb6\uff0c\u6751\u5b50\u80fd\u9762\u6709\u4e2a\u5927\u6c60\u5858\uff0c\u5b64\u96f6\u96f6\u7684\u9694\u5728\u5c71\u7684\u5916\u8fb9\uff0c\u4e0d\u77e5\u662f\u5c5e\u4e8e \u54ea\u4e2a\u4eba\u5bb6\u7684\uff0c\u5c71\u6751\u5730\u52bf\u53ea\u6709\u8fd9\u91cc\u8f83\u4e3a\u5e73\u5766\uff0c\u6240\u4ee5\u867d\u7136\u5185\u5916\u76f8\u9694\uff0c\u5c71\u6751\u91cc\u7684\u4eba\u5bb6\u8fd8\u662f\u5728\u8fd9\u91cc\u8f9f \u5858\u517b\u9c7c\u3002 \u3000\u3000\u4e0b\u5348\u7684\u9633\u5149\u7167\u5f97\u5b69\u5b50\u4eec\u6696\u6d0b\u6d0b\u7684\u597d\u4e0d\u8212\u670d\uff0c\u4ed6\u4eec\u6b63\u5728\u5858\u8fb9\u5b09\u620f\uff0c\u6709\u7684\u8131\u4e0b\u68c9\u8884\u6349\u8671\u5b50\uff0c \u6709\u7684\u76f8\u4e92\u8ffd\u9010\u6349\u8ff7\u85cf\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u5b69\u5b50\uff0c\u7ea6\u6a21\u5341\u4e8c\u4e09\u5c81\u7684\u6837\u5b50\uff0c\u9ed1\u6f06\u7684\u9762\u4e0a\u53d1\u7740\u6cb9\u5149\uff0c\u6253\u7740 \u4e00\u53cc\u8d64\u811a\uff0c\u5c0f\u811a\u9732\u51fa\u9752\u7b4b\uff0c\u4e2a\u5b50\u867d\u7136\u4e0d\u5927\uff0c\u5374\u957f\u5f97\u6781\u4e3a\u7ed3\u5b9e\uff0c\u8138\u4e0a\u73b0\u51fa\u4e00\u526f\u6d0b\u6d0b\u81ea\u5f97\u7684\u795e \u6c14\uff0c\u4f3c\u4e4e\u662f\u8fd9\u7fa4\u987d\u7ae5\u7684\u9886\u8896\u3002\u5b69\u5b50\u4eec\u6b63\u95f9\u5f97\u6b22\uff0c\u4ed6\u5ffd\u7136\u8131\u4e86\u4e0a\u8863\uff0c\u53ea\u7a7f\u7740\u4e00\u6761\u725b\u5934\u77ed\u88e4\uff0c\u8d64 \u7740\u534a\u4e2a\u8eab\u5b50\uff0c\u53eb\u56b7\u9053\uff1a\u201c\u5582\u5582\uff0c\u8c01\u8ddf\u6211\u4e0b\u5858\u6478\u9c7c\u53bb\uff1f\u201d\u6625\u9633\u867d\u6696\uff0c\u4f46\u8131\u4e0b\u68c9\u7946\u8fd8\u662f\u611f\u5230\u5bd2 \u51b7\uff0c\u5b69\u5b50\u4eec\u4f60\u671b\u6211\uff0c\u6211\u671b\u4f60\uff0c\u6ca1\u6709\u4e00\u4e2a\u51fa\u58f0\u7b54\u8bdd\u3002\u4e00\u4e2a\u987d\u7ae5\u4f38\u624b\u8fdb\u6c60\u5858\u91cc\u4e00\u8bd5\uff0c\u201c\u5478\u201d\u4e86\u4e00 \u58f0\u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff0c\u4f60\u53d1\u795e\u7ecf\uff0c\u5858\u6c34\u8fd8\u662f\u51c9\u6c81\u6c81\u7684\uff0c\u4e00\u70b9\u513f\u90fd\u6ca1\u6709\u6696\uff0c\u4f60\u8981\u53bb\u81ea\u4e2a\u513f\u53bb\u3002\u201d \u3000\u3000\u90a3\u88ab\u53eb\u505a\u201c\u5c0f\u864e\u5b50\u201d\u7684\u987d\u7ae5\u563b\u563b\u51b7\u7b11\uff0c\u53cc\u773c\u4e00\u626b\uff0c\u56b7\u9053\uff1a\u201c\u90fd\u662f\u6015\u51b7\u7684\u80c6\u5c0f\u9b3c\uff01\u5c31\u6ca1\u4e00 \u4e2a\u6562\u4e0b\u53bb\u5417\uff1f\u201d\u4f17\u987d\u7ae5\u90fd\u6447\u624b\u9053\uff1a\u201c\u4e0d\u53bb\uff0c\u4e0d\u53bb\uff01\u201d\u5c0f\u864e\u5b50\u7684\u773c\u5149\u843d\u5728\u4e00\u4e2a\u5b69\u5b50\u7684\u8eab\u4e0a\uff0c\u53eb \u9053\uff1a\u201c\u5c0f\u9f99\uff0c\u4f60\u548c\u6211\u53bb\uff01\u201d\u90a3\u53eb\u505a\u5c0f\u9f99\u7684\u5b69\u5b50\u9053\uff1a\u201c\u6211\u5b81\u613f\u7ed9\u4f60\u78d5\u4e09\u4e2a\u54cd\u5934\uff01\u201d\u5c0f\u864e\u5b50\u9053\uff1a \u201c\u597d\uff0c\u90a3\u4f60\u5c31\u8fc7\u6765\u3002\u201d\u7a81\u7136\u4e00\u628a\u63ea\u7740\u5c0f\u9f99\uff0c\u7528\u529b\u4e00\u63a8\uff0c\u53ea\u542c\u5f97\u201c\u535c\u901a\u201d\u4e00\u58f0\uff0c\u5c0f\u9f99\u8dcc\u4e0b\u6c60 \u5858\uff0c\u5c0f\u864e\u5b50\u8ddf\u7740\u8df3\u4e0b\u53bb\uff0c\u638f\u8d77\u5858\u6ce5\uff0c\u5c31\u62b9\u5c0f\u9f99\u7684\u9762\uff0c\u6c60\u5858\u8fb9\u7684\u987d\u7ae5\u5927\u62cd\u624b\u638c\uff0c\u563b\u563b\u54c8\u54c8\u5730\u7b11 \u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u5c0f\u9f99\u9053\uff1a\u201c\u5582\uff0c\u51b7\u6b7b\u6211\u5566\uff01\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u7a7f\u7740\u5927\u68c9\u7946\u8fd8\u53eb\u51b7\uff0c\u71ac\u4e00\u4f1a\u5c31\u4e0d\u51b7\uff01\u201d\u5c0f\u9f99 \u54ed\u4e27\u7740\u9762\u9053\uff1a\u201c\u8fd9\u68c9\u8884\u8fd8\u662f\u5988\u65b0\u7ed9\u6211\u7f1d\u7684\u3002\u201d\u5c0f\u864e\u5b50\u4e00\u4e2a\u52b2\u513f\u4e0d\u7406\uff0c\u4ecd\u7136\u638f\u5858\u6ce5\u7cca\u4ed6\u7684\u8138\uff0c \u7cca\u4ed6\u7684\u65b0\u8863\u3002\u6b63\u5728\u80e1\u95f9\uff0c\u5ffd\u89c1\u5cb8\u4e0a\u7684\u5b69\u5b50\u4eec\u80cc\u8f6c\u4e86\u8eab\uff0c\u7b11\u58f0\u7a81\u7136\u505c\u6b62\u2026\u2026\u201d \u3000\u3000\u5c0f\u864e\u5b50\u4ece\u6c34\u91cc\u5192\u51fa\u6765\uff0c\u53ea\u89c1\u4ece\u5c71\u8c37\u5916\u9762\u8fdb\u6765\u4e86\u4e09\u9a91\u964c\u751f\u7684\u65c5\u5ba2\u3002 \u3000\u3000\u7956\u5f95\u5c71\u897f\u9762\u6709\u4e00\u6761\u9a7f\u9053\u76f4\u901a\u6d4e\u5357\uff0c\u4ece\u8fd9\u6761\u9a7f\u9053\u5f15\u51fa\u4e00\u6761\u652f\u8def\uff0c\u672c\u6765\u53ef\u4ee5\u901a\u5230\u8fd9\u4e2a\u5c71\u6751\uff0c \u53ea\u56e0\u5e74\u4e45\u5931\u4fee\uff0c\u8def\u57fa\u88ab\u5c71\u6d2a\u51b2\u574f\uff0c\u6751\u4eba\u8d70\u8fd9\u6761\u8def\u51fa\u5916\u8d76\u96c6\u8fd8\u6ca1\u6709\u4ec0\u4e48\uff0c\u9a6c\u5339\u53ef\u662f\u5341\u5206\u96be\u8d70\uff0c \u8fd9\u6761\u8def\u53c8\u5728\u7fa4\u5c71\u73af\u7ed5\u4e4b\u4e2d\uff0c\u5e73\u7d20\u53ea\u6709\u6751\u4eba\u5f80\u5916\u9762\u8d76\u96c6\uff0c\u53ef\u6ca1\u6709\u5916\u9762\u7684\u5ba2\u4eba\u8fdb\u6751\u91cc\u6765\uff0c\u800c\u8fd9\u4e09 \u4e2a\u9a91\u5ba2\uff0c\u5176\u4e2d\u4e24\u4e2a\u8fd8\u662f\u519b\u5b98\uff0c\u957f\u7edf\u9a6c\u9774\u8e0f\u5728\u9a6c\u8e6c\u4e0a\u9500\u9500\u4f5c\u54cd\uff0c\u5b69\u5b50\u4eec\u66f4\u662f\u6ca1\u6709\u89c1\u8fc7\u3002\u53e6\u4e00\u4e2a \u662f\u7ea6\u6a21\u4e09\u5341\u591a\u5c81\u7684\u6c49\u5b50\uff0c\u6ee1\u8138\u866c\u987b\u5982\u671d\uff0c\u53cc\u76ee\u70af\u70af\u6709\u795e\uff0c\u957f\u5f97\u6bd4\u90a3\u4e24\u4e2a\u519b\u5b98\u8fd8\u8981\u5a01\u731b\u3002\u5b69\u5b50 \u4eec\u9aa4\u7136\u89c1\u5230\u8fd9\u4e09\u4e2a\u751f\u5ba2\uff0c\u8fde\u5c0f\u864e\u5b50\u5728\u5185\uff0c\u90fd\u7ed9\u4ed6\u4eec\u5438\u5f15\u7740\u4e86\u3002 \u3000\u3000\u5b69\u5b50\u4eec\u53ef\u4e0d\u77e5\u9053\uff0c\u4ed6\u4eec\u770b\u5230\u8fd9\u4e09\u4e2a\u751f\u5ba2\u611f\u5230\u60ca\u8be7\uff0c\u90a3\u4e09\u4e2a\u5ba2\u4eba\u770b\u5230\u4ed6\u76ef\uff0c\u66f4\u662f\u60ca\u8be7\uff0c\u5c24 \u5176\u662f\u5f53\u4ed6\u4eec\u770b\u5230\u5c0f\u864e\u5b50\u6c34\u6dcb\u6dcb\u5730\u4ece\u6c60\u5858\u91cc\u94bb\u51fa\u6765\uff0c\u9732\u51fa\u4e0a\u534a\u8eab\u7684\u65f6\u5019\u3002 \u3000\u3000\u8fd9\u4e09\u4e2a\u5ba2\u4eba\u867d\u7136\u90fd\u7a7f\u7740\u6751\u5b50\u91cc\u4ece\u672a\u89c1\u8fc7\u7684\u5462\u7ed2\u8863\u6599\uff0c\u4f46\u5374\u662f\u8863\u88f3\u7834\u88c2\uff0c\u6ee1\u8eab\u6ce5\u571f\uff0c\u4f3c\u4e4e \u662f\u521a\u521a\u548c\u4eba\u6253\u8fc7\u4e00\u573a\u5927\u67b6\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u8863\u895f\u4e0a\u8fd8\u6709\u6591\u6591\u7684\u8840\u8ff9\uff0c\u663e\u5f97\u5341\u5206\u72fc\u72c8\u3002 \u3000\u3000\u90a3\u6761\u5c71\u9053\uff0c\u56e0\u88ab\u5c71\u6d2a\u51b2\u6bc1\uff0c\u9760\u8fd1\u6751\u53e3\u4e4b\u5904\uff0c\u88c2\u5f00\u4e86\u4e8c\u4e08\u5de6\u53f3\u7684\u5927\u7f3a\u53e3\uff0c\u4e00\u65f6\u672a\u80fd\u4fee\u590d\uff0c \u4e0a\u9762\u53ea\u67b6\u4e86\u4e00\u6761\u4ec5\u53ef\u4f9b\u4e00\u4eba\u884c\u8d70\u7684\u6728\u677f\uff0c\u5c71\u98ce\u5439\u6765\uff0c\u4e0a\u9762\u65e0\u4eba\u8fd8\u81ea\u6447\u6447\u6643\u6643\uff0c\u8981\u5e26\u7740\u9a6c\u5339\u8d70 \u8fc7\u90a3\u662f\u7edd\u4e0d\u53ef\u80fd\u3002\u4e09\u4e2a\u9a91\u5ba2\u5728\u8fd9\u7f3a\u53e3\u524d\u9762\uff0c\u8df3\u4e0b\u4e86\u9a6c\uff0c\u6b63\u6253\u7b97\u7275\u7740\u9a6c\u513f\u6d89\u6c34\u800c\u8fc7\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u8e0f\u7740\u5858\u6c34\uff0c\u8f7d\u6d6e\u8f7d\u6c89\uff0c\u77aa\u7740\u4e00\u53cc\u5927\u773c\u775b\uff0c\u76ef\u7740\u8fd9\u4e09\u4e2a\u964c\u751f\u7684\u5ba2\u4eba\uff0c\u773c\u775b\u7728\u5440\u7728\u5440 \u4e8c\u5730\uff0c\u4f3c\u4e4e\u6b63\u5728\u60f3\u7740\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5c0f\u9f99\u4e5f\u7ed9\u4ed6\u8fd9\u80a1\u795e\u6c14\u6014\u7740\u4e86\uff0c\u7a7f\u7740\u65b0\u68c9\u8884\u6ce1\u5728\u6c34\u4e2d\uff0c\u7adf\u7136\u5fd8 \u8bb0\u4e86\u8d81\u6b64\u65f6\u673a\uff0c\u722c\u4e0a\u5cb8\u53bb\u9003\u907f\u5c0f\u864e\u5b50\u7684\u8ffd\u9010\u3002 \u3000\u3000\u884c\u5728\u524d\u5934\u7684\u90a3\u4e2a\u519b\u5b98\u770b\u4e86\u5c0f\u864e\u5b50\u4e00\u773c\uff0c\u8f6c\u8fc7\u5934\u6765\u5bf9\u90a3\u4e2a\u866c\u987b\u6c49\u5b50\u7b11\u9053\uff1a\u201c\u8001\u6a0a\uff0c\u771f\u6709\u4f60 \u7684\uff0c\u8bf4\u5b9e\u5728\u7684\uff0c\u8d77\u5934\u6211\u53ef\u4e0d\u6562\u76f8\u4fe1\u8fd9\u5c71\u6751\u91cc\u80fd\u6709\u4ec0\u4e48\u9ad8\u4eba\uff0c\u73b0\u5728\u770b\u6765\uff0c\u6562\u60c5\u8fd9\u91cc\u9762\u771f\u6709\u85cf\u9f99 \u5367\u864e\uff1f\u201d \u3000\u3000\u90a3\u88ab\u53eb\u505a\u201c\u8001\u6a0a\u201d\u7684\u6c49\u5b50\u7b11\u4e86\u4e00\u7b11\uff0c\u6b63\u5f85\u7275\u9a6c\u6d89\u6c34\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\uff0c\u4e00\u58f0\u9a6c\u5636\uff0c\u542c\u6765\u8fd8\u5728 \u534a\u91cc\u4e4b\u5916\uff0c\u500f\u7684\u5c31\u5954\u5230\u4e86\u80cc\u540e\u3002\u201c\u8001\u6a0a\u201d\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8fd9\u9a6c\u597d\u5feb\uff01\u672a\u53ca\u56de\u5934\uff0c\u4f46\u89c9\u4e00\u80a1\u52b2\u98ce\uff0c \u4e00\u56e2\u5e9e\u5927\u7684\u9ed1\u5f71\uff0c\u540e\u9762\u6765\u7684\u90a3\u4e2a\u9a91\u58eb\uff0c\u7adf\u7136\u8fde\u4eba\u5e26\u9a6c\uff0c\u4ece\u4ed6\u4eec\u7684\u5934\u9876\u98de\u8fc7\u4e86\u90a3\u4e00\u9053\u4e24\u4e08\u957f\u7684 \u201c\u6728\u677f\u6865\u201d\u3002 \u3000\u3000\u4e24\u4e2a\u519b\u5b98\u548c\u90a3\u4e2a\u201c\u8001\u6a0a\u201d\u76f8\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u5728\u5b69\u5b50\u547c\u558a\u54d7\u53eb\u58f0\u4e2d\uff0c\u90a3\u4e58\u5ba2\u5df2\u5b89\u5b89\u7a33\u7a33\u5730\u843d \u5728\u5bf9\u5cb8\uff0c\u8df3\u4e0b\u9a6c\u80cc\u3002\u90a3\u5339\u5b9d\u9a6c\u56db\u8e44\u5982\u96ea\uff0c\u9a6c\u8eab\u4e0a\u6ee1\u662f\u767d\u8272\u7684\u6591\u70b9\uff0c\u8fd9\u4e24\u4e2a\u519b\u5b98\u90fd\u662f\u4e45\u5386\u620e\u884c \u4e4b\u58eb\uff0c\u89c1\u8fc7\u4e0d\u77e5\u51e0\u5343\u5339\u6218\u9a6c\uff0c\u53ef\u4ece\u6ca1\u6709\u89c1\u8fc7\u8fd9\u6837\u795e\u9a8f\u7684\u5b9d\u9a6c\uff01\u8001\u6a0a\u5fc3\u4e2d\u4e00\u52a8\uff1a\u83ab\u975e\u662f\u90a3\u4e2a\u4eba \u53c8\u518d\u51fa\u5c71\uff0c\u5728\u6c5f\u6e56\u4e0a\u9732\u9762\u4e86\uff1f \u3000\u3000\u770b\u6e05\u695a\u65f6\uff0c\u8fd9\u4e09\u4eba\u90fd\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u89c1\u90a3\u4e2a\u9a91\u5ba2\u53ea\u662f\u4e00\u4e2a\u7ea6\u6478\u5341\u516d\u4e03\u5c81\u7684\u5c11\u5e74\uff0c\u8eab\u6750 \u7626\u524a\uff0c\u76f8\u8c8c\u6e05\u79c0\u4e4b\u6781\uff0c\u7f8a\u8102\u767d\u7389\u822c\u7684\u8138\u4e0a\u4e24\u9053\u6de1\u6de1\u7684\u7709\u6bdb\uff0c\u770b\u4ed6\u7275\u7740\u9a6c\u513f\uff0c\u7f13\u6b65\u5411\u90a3\u7fa4\u987d\u7ae5 \u8d70\u53bb\uff0c\u6e29\u6587\u6f47\u6d12\uff0c\u82e5\u7136\u4ed6\u4e0d\u662f\u7a7f\u7740\u6b66\u58eb\u7684\u670d\u9970\uff0c\u4e4d\u773c\u4e00\u770b\uff0c\u51e0\u4e4e\u8fd8\u7591\u5fc3\u4ed6\u662f\u5973\u626e\u7537\u88c5\u7684\u5927\u5bb6 \u95fa\u79c0\u3002 \u3000\u3000\u201c\u8001\u6a0a\u201d\u5fc3\u4e2d\u6697\u6697\u5600\u5495\uff1a\u8fd9\u5c11\u5e74\u548c\u8fd9\u5339\u795e\u9a8f\u7684\u5b9d\u9a6c\u6b8a\u4e0d\u76f8\u79f0\uff0c\u4ed6\u8d77\u521d\u4ee5\u4e3a\u8fd9\u4e2a\u9a91\u5ba2\u662f\u90a3 \u4f4d\u9690\u59d3\u57cb\u540d\u7684\u5927\u4fa0\uff0c\u8c01\u77e5\u5374\u5b8c\u5168\u4e0d\u662f\uff0c\u8fd9\u5c31\u4ee4\u4ed6\u66f4\u662f\u60ca\u7591\u3002 \u3000\u3000\u90a3\u6e05\u79c0\u7684\u5c11\u5e74\u4eba\u7f13\u7f13\u5411\u6c60\u5858\u8d70\u53bb\uff0c\u5728\u6c60\u5858\u8fb9\u5b09\u620f\u7684\u8fd9\u7fa4\u987d\u7ae5\u521a\u624d\u7ed9\u767d\u9a6c\u5413\u5f97\u56db\u4e0b\u95ea\u8eb2\uff0c \u8fd9\u65f6\u89c1\u8fd9\u5c11\u5e74\u4eba\u6bd4\u4ed6\u4eec\u4e5f\u5927\u4e0d\u4e86\u591a\u5c11\uff0c\u8138\u4e0a\u8fd8\u5806\u7740\u7b11\u5bb9\uff0c\u795e\u8272\u751a\u662f\u53ef\u4eb2\uff0c\u4e0d\u77e5\u4e0d\u89c9\u53c8\u805a\u62e2 \u6765\u3002\u90a3\u5c11\u5e74\u4eba\u5728\u5858\u8fb9\u62db\u624b\u9053\uff1a\u201c\u5582\uff0c\u5c0f\u670b\u53cb\uff0c\u8bf7\u4f60\u4e0a\u6765\uff01\u201d \u3000\u3000\u5c0f\u864e\u5b50\u201c\u547c\u201d\u5730\u4e00\u58f0\u8df3\u51fa\u6c34\u9762\uff0c\u722c\u4e0a\u5cb8\u6765\uff0c\u4ed6\u53ef\u6ca1\u6709\u540c\u4f34\u4eec\u5bf9\u90a3\u5c11\u5e74\u4eba\u8868\u793a\u7684\u597d\u611f\uff0c\u77aa \u7740\u4e24\u53ea\u773c\u775b\u95ee\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u8ba4\u8bc6\u4f60\uff0c\u4f60\u53eb\u6211\u505a\u4ec0\u4e48\uff1f\u201d\u5c0f\u864e\u5b50\u957f\u5f97\u9ad8\uff0c\u4ec5\u4ec5\u6bd4\u90a3\u5c11\u5e74\u4eba\u4f4e\u534a \u4e2a\u5934\uff0c\u90a3\u5c11\u5e74\u4eba\u770b\u4ed6\u5982\u6b64\u795e\u6c14\uff0c\u5657\u55e4\u4e00\u7b11\uff0c\u7b11\u58f0\u5b9b\u82e5\u94f6\u94c3\uff0c\u5341\u5206\u60a6\u8033\uff0c\u5c0f\u864e\u5b50\u6014\u4e86\u4e00\u6014\uff0c \u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u4f60\u7b11\u6211\u96be\u770b\u662f\u4e0d\u662f\uff1f\u201d\u4ed6\u8d64\u7740\u4e0a\u534a\u8eab\u6e7f\u6dcb\u6dcb\u7684\uff0c\u725b\u5934\u77ed\u88e4\uff0c\u5927\u7ea6\u662f\u5728\u6c34\u91cc \u6ce1\u4e45\u4e86\uff0c\u892a\u4e86\u534a\u622a\uff0c\u5c0f\u864e\u5b50\u8df3\u98de\u4e0a\u5cb8\u8fd9\u624d\u53d1\u73b0\uff0c\u6025\u5fd9\u7528\u624b\u4e00\u6321\uff0c\u89e3\u5f00\u4e86\u88e4\u5e26\u518d\u6253\u4e2a\u6b7b\u7ed3\u5c06\u5b83 \u7f1a\u7262\uff0c\u5c11\u5e74\u4eba\u8138\u4e0a\u5ffd\u7136\u6cdb\u8d77\u4e00\u5c42\u7ea2\u6655\uff0c\u626d\u8f6c\u4e4f\u4e86\u5934\uff0c\u5f85\u5c0f\u864e\u5b50\u7ed3\u597d\u4e86\u88e4\u5e26\uff0c\u8fd9\u624d\u56de\u5934\u7b11\u9053\uff1a \u201c\u8c01\u8bf4\u4f60\u96be\u770b\uff0c\u4f60\u633a\u60f9\u4eba\u559c\u6b22\uff0c\u4f60\u5728\u6c60\u5858\u91cc\u6478\u9c7c\uff0c\u4e0d\u6015\u51b7\u4e48\uff1f\u201d\u5c0f\u864e\u5b50\u6ee1\u795e\u6c14\u5730\u9053\uff1a\u201c\u4e00\u70b9 \u4e5f\u4e0d\u51b7\uff0c\u53ea\u6709\u80c6\u5c0f\u9b3c\u624d\u6015\u51b7\uff0c\u54fc\uff0c\u54fc\uff0c\u6211\u53ef\u89c9\u5f97\u70ed\u5462\uff01\u201d\u5c11\u5e74\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u987a\u7740\u4ed6\u7684\u53e3\u6c14 \u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4e5f\u89c9\u5f97\u70ed\u5462\u3002\u597d\u6c49\u5b50\u4e0d\u6015\u51b7\u3002\u201d\u53d6\u51fa\u4e00\u67c4\u63cf\u91d1\u6247\u5b50\uff0c\u62b9\u4e00\u62b9\u8138\u4e0a\u7684\u6c57\u73e0\uff0c\u8f7b \u8f7b\u6325\u52a8\u6247\u5b50\u6247\u51c9\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u9f87\u7259\u9732\u9f7f\uff0c\u51b2\u7740\u4ed6\u563b\u563b\u5730\u7b11\uff0c\u4f3c\u4e4e\u89c9\u5f97\u8fd9\u5ba2\u4eba\u5e76\u4e0d\u8ba8\u538c\u4e86\uff0c\u5c0f\u864e\u5b50\u95ee\u9053\uff1a\u201c\u55ef\uff0c \u7b97\u4f60\u4e5f\u662f\u597d\u6c49\u5b50\uff0c\u4f60\u5524\u6211\u4f5c\u4ec0\u4e48\uff1f\u201d\u5c11\u5e74\u4eba\u9053\uff1a\u201c\u6211\u95ee\u4f60\uff0c\u4f60\u53ef\u77e5\u9053\u5f20\u5927\u53d4\u7684\u5bb6\u5728\u54ea\u91cc \u5417\uff1f\u201d\u65c1\u8fb9\u7684\u987d\u7ae5\u4e00\u9635\u8f70\u7b11\uff0c\u201c\u5f20\u5927\u53d4\uff1f\u5f20\u5927\u53d4\u5c31\u662f\u4ed6\u7684\u7239\u5440\uff0c\u4ed6\u8fd8\u80fd\u4e0d\u77e5\u9053\uff1f\u201d\u5c11\u5e74\u4eba\u53cc \u7709\u4e00\u5c55\uff0c\u559c\u5b5c\u5b5c\u5730\u9053\uff1a\u201c\u55ef\uff0c\u6211\u679c\u7136\u731c\u5bf9\u4e86\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\uff08\u987d\u7ae5\u63d2\u53e3\u53eb\u9053\uff1a\u201c\u4ed6\u53eb\u5f20\u864e \u5b50\u3002\u5c0f\u864e\u5b50\u5440\uff01\u201d\uff09\u4f60\u662f\u5c0f\u864e\u5b50\uff0c\u5c0f\u864e\u5b50\uff0c\u90a3\u5c31\u70e6\u4f60\u5e26\u6211\u53bb\u89c1\u4f60\u7684\u7239\u3002\u201d \u3000\u3000\u5c0f\u864e\u5b50\u500f\u5730\u53c8\u4e0d\u7b11\u4e86\uff0c\u4e24\u53ea\u773c\u7741\u5f97\u5927\u5927\u7684\uff0c\u9053\uff1a\u201c\u4f60\u8981\u89c1\u6211\u7684\u7239\uff1f\u201d\u5c11\u5e74\u4eba\u9053\uff1a\u201c\u4e0d \u9519\uff0c\u4f60\u5e26\u6211\u53bb\uff0c\u6211\u7ed9\u4f60\u7cd6\u5403\u3002\u201d\u5c0f\u864e\u5b50\u5ffd\u5730\u53cc\u624b\u4e00\u626c\uff0c\u4ed6\u53cc\u624b\u6cbe\u6ee1\u6c61\u6ce5\uff0c\u6e7f\u6dcb\u6dcb\u7684\u672a\u66fe\u63e9 \u62ed\uff0c\u8fd9\u4e00\u626c\u5c31\u8fde\u6ce5\u5e26\u6c34\u90fd\u5411\u90a3\u5c11\u5e74\u4eba\u8fce\u9762\u98de\u6765\uff0c\u987d\u7ae5\u4eec\u54d7\u7136\u5927\u53eb\uff0c\u5c0f\u864e\u5b50\u867d\u7136\u987d\u76ae\u5f97\u4e0d\u53ef\u7406 \u55bb\uff0c\u4f46\u5bf9\u4e00\u4e2a\u751f\u5ba2\u5982\u6b64\u5143\u793c\uff0c\u53ef\u8fd8\u662f\u5927\u51fa\u4ed6\u540c\u4f34\u7684\u610f\u5916\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u8fd9\u4e00\u4e0b\u7a81\u5982\u5176\u6765\uff0c\u90a3\u5c11\u5e74\u4eba\u4e5f\u5413\u4e86\u4e00\u8df3\uff0c\u4f46\u968f\u5373\u7b11\u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff0c\u6211\u53ef\u6ca1\u5de5\u592b\u548c \u4f60\u620f\u800d\uff01\u201d\u53ea\u89c1\u4ed6\u5c55\u5f00\u6298\u6247\uff0c\u8fce\u51e4\u4e00\u6247\uff0c\u90a3\u80a1\u6ce5\u6c34\u7ed9\u6247\u5f97\u56de\u5934\u5c04\u53bb\uff0c\u6e85\u4e86\u5c0f\u864e\u5b50\u6ee1\u9762\uff0c\u90a3\u4e24 \u4e2a\u519b\u5b98\u548c\u201c\u8001\u6a0a\u201d\u8fd9\u65f6\u5df2\u6d89\u6c34\u8fc7\u6765\uff0c\u9a7b\u8db3\u800c\u89c2\uff0c\u89c1\u6b64\u60c5\u72b6\uff0c\u90fd\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u4ed6\u4eec\u600e\u6837\u4e5f\u60f3 \u4e0d\u5230\uff0c\u8fd9\u4e00\u4e2a\u5341\u516d\u4e03\u5c81\uff0c\u8fd8\u672a\u8131\u5b69\u5b50\u6c14\u7684\u5c11\u5e74\u4eba\u7adf\u7136\u6709\u8fd9\u6837\u7684\u529f\u529f\uff0c\u80fd\u591f\u6325\u6247\u6210\u98ce\uff0c\u6240\u7528\u7684 \u4e5f\u662f\u6b66\u6797\u6b63\u5b97\u7684\u62e8\u6697\u5668\u624b\u6cd5\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u535c\u901a\u201d\u4e00\u58f0\uff0c\u5c0f\u864e\u5b50\u53c8\u8df3\u4e0b\u6c60\u5858\uff0c\u5411\u5c11\u5e74\u4eba\u77aa\u773c\u53eb\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u6709\u5de5\u592b\u966a\u4f60\uff0c \u54fc\uff0c\u54fc\uff0c\u6211\u7684\u7239\u8c01\u4e5f\u4e0d\u89c1\u3002\u66f4\u4e0d\u8981\u89c1\u4f60\u3002\u201d\u5c11\u5e74\u4eba\u5fae\u7b11\u9053\uff1a\u201c\u4e5f\u8bb8\u4f60\u7239\u613f\u89c1\u6211\u5462\uff1f\u201d\u5c0f\u864e\u5b50 \u53eb\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff01\u6211\u7684\u7239\u8c01\u4e5f\u4e0d\u89c1\u3002\u4f60\u8d70\uff0c\u4f60\u5feb\u8d70\uff01\u201d\u5c11\u5e74\u4eba\u9053\uff1a\u201c\u5c0f\u864e\u5b50\u4e0d\u8981\u987d\u76ae\uff0c\u5e26\u6211 \u53bb\u5427\uff0c\u4f60\u77a7\uff0c\u6211\u6709\u51b0\u7cd6\u846b\u82a6\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u51b0\u7cd6\u846b\u82a6\uff0c\u5c31\u5e0c\u7f55\u4e48\uff1f\u504f\u4e0d\u57cb\u4f60\uff0c\u6709\u80c6\u7684\u5c31\u8df3 \u4e0b\u6765\uff01\u201d\u53c8\u9f87\u7259\u88c2\u5634\u5730\u51b2\u7740\u5c11\u5e74\u4eba\u51b7\u7b11\uff0c\u4e24\u624b\u62cd\u6253\u5858\u6c34\uff0c\u50cf\u4e00\u6761\u5927\u9c7c\u822c\u5730\u6e38\u6765\u6e38\u53bb\uff0c\u597d\u50cf\u5728 \u8bf4\uff1a\u201c\u6211\u62ff\u7a33\u4f60\u4e0d\u6562\u4e0b\u6765\uff0c\u4f60\u518d\u6709\u672c\u4e8b\u4e5f\u5948\u6211\u4e0d\u4f55\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u4eba\u76b1\u4e86\u76b1\u7709\uff0c\u7b11\u53c8\u4e0d\u662f\uff0c\u6c14\u53c8\u4e0d\u662f\uff0c\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u5c0f\u864e\u5b50\u4f60\u4e0d\u542c\u8bdd\uff0c\u6211\u53ef\u8981\u8feb\u4f60 \u4e56\u4e56\u5730\u81ea\u5df1\u4e0a\u6765\uff01\u201d\u5c0f\u864e\u5b50\u77a7\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5c0f\u9b3c\u5934\uff0c\u5439\u5927\u6c14\uff0c\u4f60\u8001\u5b50\u8bf4\u4e0d\u4e0a\u5c31\u4e0d\u4e0a\u3002\u201d\u5c11 \u5e74\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\uff1f\u6211\u8bf4\u8981\u4f60\u4e0a\u4f60\u5c31\u8981\u4e0a\u3002\u201d\u5ffd\u7136\u8e72\u4e86\u4e0b\u6765\uff0c\u6361\u8d77\u5858\u8fb9\u7684\u788e\u77f3\u5b50\uff0c\u201c\u556a\u201d \u7684\u4e00\u58f0\uff0c\u63b7\u4e0b\u6c60\u5858\uff0c\u8fd9\u5c11\u5e74\u7626\u7626\u5c0f\u5c0f\u7684\uff0c\u624b\u52b2\u5374\u662f\u5927\u5f97\u51fa\u5947\uff0c\u77f3\u5b50\u63b7\u4e0b\u6c60\u5858\uff0c\u7acb\u523b\u6fc0\u8d77\u4e00\u80a1 \u6d4a\u6c34\u5411\u5c0f\u864e\u5b50\u9732\u51fa\u6765\u7684\u5934\u9762\u731b\u5c04\uff0c\u5c0f\u864e\u5b50\u4e00\u4e0b\u6f5c\u5165\u6c34\u4e2d\uff0c\u5c11\u5e74\u4eba\u5f85\u4ed6\u5192\u51fa\u5934\u65f6\uff0c\u53c8\u662f\u4e00\u9897\u77f3 \u5b50\uff0c\u770b\u6765\u5c31\u50cf\u4e24\u4e2a\u987d\u7ae5\uff0c\u4e00\u5728\u5cb8\u4e0a\uff0c\u4e00\u5728\u5858\u4e2d\uff0c\u4e92\u76f8\u5b09\u620f\uff0c\u5374\u662f\u5404\u6597\u5fc3\u673a\uff0c\u5c0f\u864e\u5b50\u6f5c\u6c34\u4e0d\u80fd \u8010\u4e45\uff0c\u800c\u4e14\u5728\u6c34\u5e95\u4e5f\u8981\u907f\u4ed6\u7684\u77f3\u5b50\uff0c\u6e10\u6e10\u5730\u88ab\u4ed6\u63b7\u77f3\u6240\u8feb\uff0c\u6162\u6162\u907f\u5230\u5858\u8fb9\uff0c\u770b\u770b\u5c31\u8981\u88ab\u4ed6\u903c \u5f97\u8df3\u4e0a\u5cb8\u3002 \u3000\u3000\u5c0f\u9f99\u60ca\u5f97\u5446\u4e86\uff0c\u5c11\u5e74\u4eba\u63b7\u7684\u77f3\u5b50\u867d\u4e0d\u662f\u8ffd\u9010\u4ed6\uff0c\u4ed6\u53ef\u4e3a\u597d\u53cb\u62c5\u5fc3\u5bb3\u6015\uff0c\u5ffd\u89c1\u5c0f\u864e\u5b50\u5411\u4ed6 \u62db\u624b\uff0c\u5c0f\u9f99\u4e0d\u987e\u77f3\u5b50\u7684\u5a01\u80c1\uff0c\u6e38\u5230\u5c0f\u864e\u5b50\u8eab\u8fb9\uff0c\u90a3\u5c11\u5e74\u4eba\u4f3c\u4e4e\u4e0d\u613f\u8bef\u6253\u5c0f\u9f99\uff0c\u7f13\u4e86\u7f13\u624b\uff0c\u5c0f \u864e\u5b50\u4e00\u628a\u63fd\u7740\u5c0f\u9f99\uff0c\u4f3c\u662f\u5728\u4ed6\u8033\u8fb9\u8bf4\u4e86\u4e24\u53e5\u4ec0\u4e48\u8bdd\uff0c\u5ffd\u5730\u628a\u4ed6\u4e3e\u8d77\uff0c\u63b7\u4e0a\u5cb8\u6765\uff0c\u81ea\u5df1\u5374\u53c8\u547c \u5730\u4e00\u4e0b\u5b50\u6f5c\u5165\u6c34\u4e2d\uff0c\u6e38\u51fa\u4e08\u8bb8\uff0c\u53c8\u5192\u51fa\u5934\u6765\u53eb\u9053\uff1a\u201c\u6211\u504f\u4e0d\u4e0a\u5cb8\uff01\u201d \u3000\u3000\u5c11\u5e74\u4eba\u9053\uff1a\u201c\u6211\u504f\u8981\u53eb\u4f60\u4e0a\u5cb8\u3002\u201d\u5858\u4e2d\u53ea\u6709\u5c0f\u864e\u5b50\u4e00\u4eba\uff0c\u5c11\u5e74\u4eba\u7684\u77f3\u5b50\u63b7\u5f97\u66f4\u65e0\u987e\u5fcc\uff0c \u6bcf\u4e00\u9897\u90fd\u662f\u6070\u6070\u843d\u5728\u5c0f\u864e\u5b50\u7684\u8eab\u8fb9\uff0c\u8feb\u5f97\u4ed6\u53c8\u5411\u5cb8\u8fb9\u9003\u907f\u3002 \u3000\u3000\u5c11\u5e74\u4eba\u6b63\u81ea\u63b7\u5f97\u9ad8\u5174\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u559d\u9053\uff1a\u201c\u6b3a\u8d1f\u5b69\u5b50\uff0c\u4e0d\u8981\u8138\uff01\u201d\u53ea\u89c1\u4e00\u4e2a\u866c\u987b\u6c49\u5b50\u51b2 \u7740\u4ed6\u6765\uff0c\u8fd9\u4eba\u5c31\u662f\u201c\u8001\u6a0a\u201d\u3002 \u3000\u3000\u8001\u6a0a\u7a81\u7136\u51fa\u5934\u5e72\u9884\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u90fd\u611f\u5230\u51fa\u5947\uff0c\u5176\u4e2d\u4e00\u4e2a\u9053\uff1a\u201c\u8001\u6a0a\u8fd9\u5bb6\u4f19\u662f\u600e\u4e48\u641e\u7684\uff1f \u54b1\u4eec\u7684\u9ebb\u70e6\u8fd8\u4e0d\u591f\u4e48\uff1f\u4ed6\u53c8\u8981\u53bb\u62db\u60f9\u4e00\u4e2a\u5f3a\u654c\u3002\u201d\u53ef\u662f\u201c\u8001\u6a0a\u201d\u5df2\u7ecf\u51fa\u624b\uff0c\u62e6\u963b\u4e5f\u6765\u4e0d\u53ca\u3002 \u4ed6\u548c\u90a3\u5c11\u5e74\u5df2\u662f\u9762\u5bf9\u9762\u5730\u4e92\u76f8\u77aa\u89c6\u4e86\u3002 \u3000\u3000\u5c11\u5e74\u4eba\u9053\uff1a\u201c\u6211\u81ea\u548c\u4ed6\u620f\u800d\uff0c\u4f60\u77a7\u6211\u4f24\u4e86\u4ed6\u4e00\u6839\u6beb\u53d1\u4e48\uff1f\u8981\u4f60\u591a\u4e8b\uff01\u201d\u8001\u6a0a\u9053\uff1a\u201c\u4ed6\u662f \u987d\u7ae5\u4f60\u4e5f\u662f\u987d\u7ae5\u4e48\uff1f\u5582\uff0c\u5c0f\u864e\u5b50\uff0c\u4f60\u8bf4\u8981\u4e0d\u8981\u63cd\u4ed6\uff1f\u201d\u5c0f\u864e\u5b50\u6068\u4e0d\u5f97\u4e24\u4eba\u72e0\u72e0\u6253\u4e0a\u4e00\u573a\uff0c\u8ba9 \u4ed6\u77a7\u77a7\u70ed\u95f9\uff0c\u53c8\u5728\u6c34\u4e2d\u5192\u51fa\u5934\u6765\uff0c\u62cd\u624b\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u63cd\u4ed6\uff01\u201d\u5c11\u5e74\u4eba\u4e00\u58f0\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u4f60 \u5145\u54ea\u95e8\u5b50\u7684\u597d\u6c49\uff1f\u662f\u597d\u6c49\u4e5f\u4e0d\u7528\u5230\u8fd9\u91cc\u6765\u6c42\u4eba\u5bb6\u4e86\uff0c\u54fc\uff0c\u4e5f\u4e0d\u77e5\u662f\u8c01\u63cd\u8c01\u5462\uff1f\u843d\u6c64\u9e21\u624d\u4e0a\u5cb8 \u53c8\u5594\u5594\u5730\u557c\u4e86\uff1f\u54c8\uff0c\u8fd9\u624d\u771f\u53eb\u4e0d\u8981\u8138\u5462\uff1f\u201d\u8001\u6a0a\u9762\u8272\u4e00\u53d8\uff0c\u9a82\u9053\uff1a\u201c\u5c0f\u987d\u7ae5\uff0c\u800d\u8d2b\u5634\u3002\u201d\u547c \u5730\u4e00\u62f3\uff0c\u5f53\u80f8\u6363\u51fa\uff0c\u7adf\u662f\u5c11\u6797\u6d3e\u7684\u957f\u62f3\u67b6\u5f0f\u3002 \u3000\u3000\u5c11\u5e74\u4eba\u6298\u6247\u4e00\u6325\uff0c\u5728\u8001\u6a0a\u7684\u624b\u81c2\u4e0a\u4e00\u642d\uff0c\u53c8\u89c1\u8001\u6a0a\u4e00\u4e2a\u6c89\u8170\u5750\u9a6c\uff0c\u624b\u81c2\u4e00\u62a1\uff0c\u5c11\u5e74\u4eba\u7684 \u6298\u6247\u8f6c\u4e86\u4e00\u4e2a\u5708\u5708\uff0c\u5ffd\u5730\u5411\u524d\u4e00\u9001\uff0c\u8001\u6a0a\u5411\u540e\u9000\u4e86\u4e00\u6b65\uff0c\u5de6\u624b\u4e00\u62db\u201c\u63a8\u7a97\u671b\u6708\u201d\uff0c\u5410\u6c14\u626c \u58f0\uff0c\u201c\u5413\u201d\u5730\u4e00\u58f0\uff0c\u5e73\u63a8\u51fa\u53bb\uff0c\u4e24\u4eba\u8fd9\u4e00\u4ea4\u624b\uff0c\u5c11\u5e74\u4eba\u7684\u6298\u6247\u6309\u4e0d\u7740\u8001\u6a0a\u7684\u94c1\u81c2\uff0c\u8001\u6a0a\u7684\u957f \u62f3\u4e5f\u62c9\u4e0d\u5f00\u67b6\u5f0f\uff0c\u8fd8\u7ed9\u903c\u5f97\u9000\u4e86\u4e00\u6b65\uff0c\u90fd\u77e5\u9053\u5bf9\u65b9\u7684\u529f\u592b\u4e86\u5f97\uff0c\u4f46\u6bd4\u8f83\u8d77\u6765\uff0c\u5374\u662f\u8001\u6a0a\u7a0d\u7a0d \u5403\u4e8f\uff0c\u6240\u4ee5\u8001\u6a0a\u8fd9\u4e00\u638c\u7edd\u4e0d\u5bb9\u60c5\uff0c\u7adf\u7136\u62fc\u4e0a\u4e86\u5185\u52b2\uff0c\u7528\u7684\u662f\u5927\u6454\u7891\u624b\u7684\u529f\u592b\u3002 \u3000\u3000\u987d\u7ae5\u4eec\u4e0d\u77e5\u51f6\u9669\uff0c\u56db\u5904\u6563\u5f00\uff0c\u8fdc\u8fdc\u7684\u56f4\u6210\u4e00\u4e2a\u5708\u5b50\uff0c\u53c8\u7b11\u53c8\u56b7\u62cd\u624b\u987f\u8db3\u5730\u5728\u77a7\u70ed\u95f9\uff0c\u90a3 \u624d\u722c\u4e0a\u5cb8\u7684\u5c0f\u9f99\uff0c\u4e00\u8eab\u65b0\u68c9\u8884\u90fd\u6cbe\u6ee1\u4e86\u6c61\u6ce5\uff0c\u6e7f\u6dcb\u6dcb\u5730\u51b7\u5f97\u76f4\u53d1\u6296\uff0c\u4ed6\u672c\u6765\u4e5f\u6742\u5728\u8fd9\u7fa4\u987d\u7ae5 \u4e2d\u95f4\uff0c\u5ffd\u89c1\u6ce1\u5728\u6c60\u5858\u91cc\u7684\u5c0f\u864e\u5b50\u53c8\u5411\u4ed6\u72e0\u72e0\u5730\u77aa\u4e86\u4e00\u773c\uff0c\u5c0f\u9f99\u7a81\u7136\u201c\u54c7\u201d\u5730\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff1a \u201c\u6211\u56de\u5bb6\u544a\u8bc9\u5988\u5988\u53bb\uff0c\u8981\u5c0f\u864e\u5b50\u8d54\u6211\u7684\u65b0\u8863\uff01\u201d\u8fb9\u53eb\u8fb9\u8dd1\uff0c\u8fde\u6253\u67b6\u4e5f\u4e0d\u77a7\u4e86\u3002\u6709\u4e9b\u548c\u5c0f\u9f99\u76f8 \u597d\u7684\u987d\u7ae5\u611f\u5230\u5947\u602a\uff0c\u5c0f\u9f99\u867d\u4e0d\u50cf\u5c0f\u864e\u5b50\u90a3\u6837\u5929\u4e0d\u6015\u5730\u4e0d\u6015\uff0c\u4f46\u4e5f\u662f\u4e00\u526f\u786c\u6027\u5b50\uff0c\u8dcc\u5012\u5c31\u722c \u8d77\uff0c\u6328\u6253\u4e0d\u76b1\u7709\uff0c\u8981\u4e0d\u7136\u5c0f\u864e\u5b50\u4e5f\u4e0d\u4f1a\u548c\u4ed6\u90a3\u822c\u597d\u4e86\u3002\u4ed6\u4eec\u4ece\u4e0d\u66fe\u89c1\u5c0f\u9f99\u4f3c\u4eca\u5929\u8fd9\u6837\u7684\u201c\u8113 \u5305\u201d\uff0c\u54fc\u54fc\u8fd8\u597d\u610f\u601d\u53eb\u5c0f\u864e\u5b50\u7ed9\u4ed6\u8d54\u65b0\u8863\u5462\uff01\u4f46\u90a3\u4e9b\u987d\u7ae5\u867d\u89c9\u5947\u602a\uff0c\u5374\u4e0d\u4f1a\u50cf\u5927\u4eba\u4eec\u90a3\u6837 \u201c\u6df1\u7a76\u201d\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u4ed6\u4eec\u53c8\u5728\u7d27\u5f20\u5730\u770b\u8001\u6a0a\u548c\u90a3\u5c11\u5e74\u4eba\u6253\u67b6\u4e86\u3002 \u3000\u3000\u8001\u6a0a\u8fde\u5288\u4e09\u638c\u90fd\u7ed9\u5c11\u5e74\u4eba\u6321\u4e86\u56de\u6765\uff0c\u90a3\u5c11\u5e74\u8fde\u70b9\u4e86\u51e0\u6b21\u8001\u6a0a\u7684\u7a74\u9053\u4e5f\u6ca1\u6709\u70b9\u7740\uff0c\u8001\u6a0a\u4e0a \u524d\u4e24\u6b65\uff0c\u9000\u540e\u4e09\u6b65\uff0c\u5c11\u5e74\u4eba\u6bcf\u51b2\u4e0a\u4e09\u6b65\u4e5f\u8981\u88ab\u903c\u9000\u4e24\u6b65\uff0c\u867d\u662f\u7a0d\u5360\u4e0a\u98ce\uff0c\u6574\u4e2a\u5c40\u52bf\uff0c\u4ecd\u662f\u76f8 \u6301\u4e0d \u3000\u3000\u8001\u6a0a\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\uff0c\u4ed6\u5728\u6c5f\u6e56\u4e0a\u603b\u7b97\u662f\u4e2a\u6210\u540d\u4eba\u7269\uff0c\u54ea\u77e5\u9053\u8fde\u4e00\u4e2a\u5341\u516d\u4e03\u5c81\u7684\u5c0f\u5b50\u4e5f\u6253 \u4e0d\u8fc7\uff0c\u6b63\u62df\u53d8\u62db\uff0c\u4f7f\u51fa\u5c11\u6797\u6d3e\u7684\u7f57\u6c49\u62f3\u548c\u8fd9\u5c11\u5e74\u4eba\u72e0\u72e0\u62fc\u4e00\u62fc\uff0c\u90a3\u5c11\u5e74\u4eba\u7a81\u7136\u64ae\u5507\u4e00\u5578\uff0c\u6298 \u6247\u4e00\u6536\uff0c\u9053\uff1a\u201c\u6211\u624d\u4e0d\u548c\u4f60\u4e00\u822c\u89c1\u8bc6\uff0c\u6211\u6ca1\u5de5\u592b\u966a\u4f60\u6253\u67b6\u5566\uff01\u201d\u4e00\u4e2a\u98de\u8eab\uff0c\u8df3\u4e0a\u9a6c\u80cc\uff0c\u90a3\u5339 \u9a6c\u653e\u5f00\u56db\u8e44\uff0c\u7a7f\u6797\u8df3\u6da7\uff0c\u5728\u5c71\u5761\u9661\u8def\u4e0a\u4e5f\u5982\u5c65\u5e73\u5730\uff0c\u500f\u5ffd\u4e4b\u95f4\uff0c\u5df2\u8f6c\u8fc7\u5c71\u5773\uff0c\u671b\u4e0d\u89c1\u4e86\u3002\u90a3 \u4e24\u4e2a\u65c1\u89c2\u7684\u519b\u5b98\u90fd\u5927\u611f\u5947\u602a\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u4eba\u660e\u660e\u5360\u7740\u4e0a\u98ce\uff0c\u5374\u5ffd\u7136\u9003\u8dd1\uff0c\u4e0d\u4f46\u65c1\u89c2\u7684\u519b\u5b98\u83ab\u540d\u5176\u5999\uff0c\u8fde\u8001\u6a0a\u4e5f\u89c9\u5f97\u51fa\u4e4e\u610f \u6599\u3002\u5c0f\u864e\u5b50\u4ece\u6c60\u5858\u91cc\u722c\u4e0a\u6765\uff0c\u6296\u4e00\u6296\u8eab\u4e0a\u7684\u6c34\u73e0\uff0c\u62cd\u638c\u7b11\u9053\uff1a\u201c\u6253\u5f97\u597d\uff0c\u6253\u5f97\u597d\u3002\u201d\u8001\u6a0a\u8138 \u4e0a\u4e00\u7ea2\uff0c\u95ee\u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff0c\u4f60\u7239\u5728\u5bb6\u4e48\uff1f\u201d\u5c0f\u864e\u5b50\u4e00\u77aa\u773c\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u95ee\u6211\u7239\uff1f\u201d\u5c0f\u624b\u4e00 \u4f38\uff0c\u5c31\u5728\u8001\u6a0a\u7684\u80f8\u53e3\u4e00\u62b9\uff0c\u8001\u6a0a\u624b\u81c2\u4e00\u62ac\uff0c\u5c06\u5c0f\u864e\u5b50\u7684\u8098\u5c16\u4e00\u6258\uff0c\u811a\u5e95\u4e00\u7eca\uff0c\u5c0f\u864e\u5b50\u56db\u811a\u671d \u5929\u5730\u6454\u4e86\u4e00\u8de4\uff0c\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\uff0c\u7acb\u523b\u4ece\u5730\u4e0a\u8df3\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4f60\u662f\u6a0a\u5927\u54e5\uff1f\u201d\u8001\u6a0a\u70b9\u5934\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u8fd9\uff0c\u4f60\u53ef\u8bb0\u5f97\u6211\u4e86\uff1f\u201d\u5c0f\u864e\u5b50\u8bb0\u8d77\u56db\u5e74\u4e4b\u524d\uff0c\u8fd9\u4e2a\u4eba\u66fe\u5230\u4ed6\u5bb6\u4e2d\u4f4f\u8fc7\u4e00\u665a\u3002\u6559\u8fc7\u4ed6 \u4e00\u62db\u201c\u864e\u5c3e\u811a\u201d\uff0c\u90a3\u65f6\u4ed6\u8fd8\u53ea\u6709\u516b\u5c81\uff0c\u82e5\u4e0d\u662f\u8001\u6a0a\u7167\u6837\u5730\u7eca\u4ed6\u4e00\u8de4\uff0c\u4ed6\u53ef\u8ba4\u4e0d\u51fa\u8fd9\u4e2a\u6ee1\u9762\u866c \u987b\u7684\u6c49\u5b50\u5c31\u662f\u90a3\u4e2a\u6a0a\u5927\u54e5\uff0c\u90a3\u65f6\u6a0a\u5927\u54e5\u53ef\u6ca1\u6709\u8fd9\u4e48\u53c8\u6d53\u53c8\u9ed1\u7684\u987b\u5b50\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u4e0d\u518d\u77aa\u773c\uff0c\u7b11\u563b\u563b\u5730\u9053\uff1a\u201c\u6a0a\u5927\u54e5\uff0c\u4f60\u521a\u624d\u4e00\u8fde\u5288\u90a3\u4e09\u638c\uff0c\u4f7f\u5f97\u771f\u597d\uff0c\u6211\u4ee5\u4e3a\u4f60 \u7684\u8111\u888b\u4e00\u5b9a\u7ed9\u4ed6\u6253\u7740\u4e86\uff0c\u54ea\u77e5\u8fd9\u4e09\u638c\u8fde\u5288\uff0c\u7adf\u7136\u4e0d\u7528\u8f6c\u8eab\u9632\u5b88\uff0c\u654c\u4eba\u5c31\u8981\u8df3\u5f00\uff0c\u771f\u662f\u5999\u6781 \u4e86\u3002\u6a0a\u5927\u54e5\uff0c\u8fd9\u56de\u4f60\u5c31\u6559\u6211\u8fd9\u8fde\u5288\u4e09\u638c\u7684\u624b\u6cd5\u3002\u201d\u8001\u6a0a\u770b\u7740\u80f8\u524d\u7684\u638c\u5370\uff0c\u90a3\u662f\u5c0f\u864e\u5b50\u7684\u6ce5\u624b \u62b9\u4e0a\u7684\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff0c\u771f\u6709\u4f60\u7684\uff01\u518d\u8fc7\u4e24\u5e74\uff0c\u6a0a\u5927\u54e5\u53ef\u6ca1\u8d44\u683c\u6559\u4f60\u5566\u3002\u597d\u5566\uff0c\u73b0\u5728 \u4f60\u5c31\u548c\u6211\u4eec\u8d70\u5427\u3002\u201d\u5c0f\u864e\u5b50\u7728\u7728\u773c\u775b\u9053\uff1a\u201c\u4f60\u4eec\uff1f\u201d\u8001\u6a0a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e24\u4f4d\u5927\u4eba\u90fd\u662f\u6211\u7684 \u670b\u53cb\u3002\u201d\u90a3\u4e24\u4e2a\u519b\u5b98\u542c\u5c0f\u864e\u5b50\u521a\u624d\u548c\u8001\u6a0a\u7684\u90a3\u756a\u95ee\u8bdd\uff0c\u7adf\u662f\u6df1\u660e\u62f3\u7406\uff0c\u90fd\u5927\u4e3a\u8be7\u5f02\uff0c\u653e\u5bbd\u7b11 \u8138\uff0c\u53cc\u53cc\u4e0a\u6765\uff0c\u8981\u548c\u5c0f\u864e\u5b50\u62c9\u624b\uff0c\u5c0f\u864e\u5b50\u7a81\u7136\u4e00\u77aa\u773c\u775b\uff0c\u7ed9\u4ed6\u4eec\u4e2a\u4e0d\u7406\u4e0d\u776c\uff0c\u5bf9\u8001\u6a0a\u9053\uff1a \u201c\u597d\uff0c\u51b2\u7740\u4f60\u7684\u9762\u5b50\uff0c\u6211\u5e26\u4f60\u4eec\u53bb\uff0c\u6211\u7239\u82e5\u7136\u4e0d\u89c1\u90a3\u53ef\u4f11\u8981\u602a\u6211\u5c0f\u864e\u5b50\uff0c\u8fd9\u4ea4\u60c5\u6211\u5df2\u5356\u4e0e\u4f60 \u5566\uff01\u201d\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u8bf4\u8bdd\u5c45\u7136\u4e00\u526f\u6c5f\u6e56\u53e3\u543b\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u78b0\u4e86\u4e00\u4e2a\u9489\u5b50\uff0c\u597d\u751f\u6ca1\u8da3\uff0c\u4f46\u5bf9\u65b9\u662f \u8fd9\u6837\u4e00\u4e2a\u5b69\u5b50\uff0c\u5374\u662f\u6c14\u607c\u4e0d\u5f97\u3002 \u3000\u3000\u8001\u6a0a\u548c\u4e24\u4e2a\u519b\u5b98\u7275\u7740\u9a6c\u8ddf\u5728\u5c0f\u864e\u5b50\u540e\u9762\uff0c\u5728\u5f2f\u5f2f\u66f2\u66f2\u7684\u5c71\u8def\u8f6c\u6765\u8f6c\u53bb\uff0c\u8d70\u4e86\u5927\u534a\u4e2a\u65f6 \u8fb0\uff0c\u53ea\u89c1\u4e00\u5ea7\u77f3\u5c4b\uff0c\u5efa\u5728\u534a\u5c71\uff0c\u8fd9\u5ea7\u77f3\u5c4b\u5360\u5730\u9887\u5bbd\uff0c\u524d\u540e\u4e09\u8fdb\uff0c\u7ea6\u6709\u4e00\u4e08\u4e03\u516b\u9ad8\uff0c\u50cf\u4e2a\u5c0f\u5c0f \u7684\u7889\u5821\uff0c\u5c4b\u5b50\u524d\u9762\u6709\u597d\u51e0\u682a\u82cd\u677e\uff0c\u5927\u53ef\u5408\u62b1\uff0c\u4e09\u4eba\u7cfb\u597d\u4e86\u9a6c\uff0c\u53ea\u89c1\u77f3\u95e8\u8651\u63a9\uff0c\u5c0f\u864e\u5b50\u8e66\u8e66\u8df3 \u8df3\u5730\u8dd1\u8fdb\u5bb6\u95e8\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u7239\uff0c\u5927\u987b\u5b50\u6a0a\u5927\u54e5\u6765\u770b\u4f60\u3002\u201d\u91cc\u9762\u5bc2\u7136\u65e0\u58f0\uff0c\u5c0f\u864e\u5b50\u7a81\u7136\u53eb \u9053\uff1a\u201c\u6a0a\u5927\u54e5\uff0c\u5feb\u6765\uff0c\u5feb\u6765\uff01\u201d \u3000\u3000\u53ea\u89c1\u5927\u5385\u7684\u77f3\u58c1\u4e0a\u5370\u7740\u4e09\u6735\u9c9c\u7ea2\u7684\u6885\u82b1\uff0c\u4e5f\u4e0d\u77e5\u662f\u96d5\u51fa\u6765\u7684\u8fd8\u662f\u7528\u6a21\u578b\u538b\u51fa\u6765\u7684\uff0c\u5165\u77f3 \u6570\u5206\uff0c\u5a07\u8273\u53ef\u7231\uff0c\u8001\u6a0a\u5403\u4e86\u4e00\u60ca\uff0c\u7a7f\u623f\u5165\u5ba4\u641c\u4e86\u4e2a\u904d\uff0c\u65e2\u4e0d\u89c1\u4e3b\u4eba\uff0c\u4ea6\u4e0d\u89c1\u5176\u4ed6\u75d5\u8ff9\uff0c\u5c4b\u5185 \u7684\u4e00\u5207\u4e1c\u897f\uff0c\u4e5f\u4e0d\u4f3c\u6709\u4eba\u52a8\u8fc7\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u561f\u561f\u5495\u5495\u8c08\u8bba\uff0c\u4e00\u4e2a\u9053\uff1a\u201c\u8fd9\u662f\u6c5f\u6e56\u4eba\u7269\u7559\u4e0b\u7684 \u6807\u8bb0\uff0c\u6211\u77a7\uff0c\u8d70\u662f\u4e2a\u6781\u5389\u5bb3\u7684\u5f3a\u76d7\u3002\u201d\u5c0f\u864e\u5b50\u6b6a\u5634\u4e00\u6487\uff0c\u4f3c\u662f\u9053\uff1a\u201c\u8fd9\u8fd8\u7528\u4f60\u8bf4\uff0c\u5f53\u7136\u662f\u6c5f \u6e56\u5ba2\u7684\u6807\u8bb0\u3002\u201d\u53c8\u4e00\u4e2a\u9053\uff1a\u201c\u6562\u60c5\u5c31\u662f\u90a3\u5c0f\u5b50\u62a2\u5148\u4e00\u6b65\uff0c\u5728\u8fd9\u91cc\u7559\u4e0b\u7684\u3002\u201d \u3000\u3000\u8001\u6a0a\u4e00\u60f3\u90a3\u5c11\u5e74\u4eba\u7a81\u7136\u9003\u8d70\u60c5\u5f62\uff0c\u62cd\u638c\u53eb\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e5d\u6210\u662f\u4ed6\uff01\u201d\u5148\u5934\u90a3\u519b\u5b98\u9053\uff1a \u201c\u8fd9\u5c0f\u5b50\u529f\u592b\u90aa\u6c14\u9738\u9053\u5f97\u7d27\uff0c\u4f60\u7684\u670b\u53cb\u83ab\u975e\u662f\u7ed9\u4ed6\u5f04\u6b7b\u3001\u6bc1\u5c38\u706d\u8ff9\u4e86\uff1f\u201d\u5c0f\u864e\u5b50\u4e00\u77aa\u773c\u775b\uff0c \u9a82\u9053\uff1a\u201c\u653e\u5c41\uff0c\u6211\u7239\u7239\u662f\u6253\u4e0d\u6b7b\u7684\u597d\u6c49\u5b50\uff0c\u90a3\u5c0f\u5b50\u7684\u672c\u4e8b\uff0c\u518d\u591a\u4e24\u4e2a\u4e5f\u4e0d\u5728\u6211\u7239\u7239\u773c\u5185\uff0c\u4f60 \u6562\u635f\u4ed6\u3002\u201d\u90a3\u519b\u5b98\u6c14\u5f97\u51e0\u4e4e\u53d1\u4f5c\uff0c\u8001\u6a0a\u6025\u5fd9\u5c06\u5c0f\u864e\u5b50\u62c9\u5f00\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u4eba\u662f\u4e00\u7247\u597d\u5fc3\uff0c\u4ed6 \u6ca1\u6709\u8bf4\u4f60\u7239\u4e0d\u884c\u3002\u201d\u5c0f\u864e\u5b50\u5140\u81ea\u6c14\u9f13\u9f13\u5730\u4e0d\u7406\u90a3\u4e2a\u519b\u5b98\uff0c\u8001\u6a0a\u7b11\u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff0c\u53bb\u77a7\u77a7\u4f60\u7239 \u56de\u6765\u6ca1\u6709\uff0c\u6211\u4eec\u5728\u8fd9\u91cc\u7b49\u4ed6\u3002\u660e\u513f\u4e00\u65e9\uff0c\u6211\u5c31\u6559\u4f60\u90a3\u8fde\u5288\u4e09\u638c\u7684\u624b\u6cd5\u3002\u5440\uff0c\u5c0f\u864e\u5b50\uff0c\u5927\u54e5\u6765 \u4e86\uff0c\u4f60\u4e5f\u4e0d\u5f04\u70b9\u4e1c\u897f\u62db\u5f85\u6211\u5417\uff1f\u4f60\u518d\u751f\u6c14\uff0c\u6211\u4ee5\u540e\u53ef\u4e0d\u6562\u6765\u5566\u3002\u201d\u5c0f\u864e\u5b50\u7ed9\u8001\u6a0a\u903c\u5f97\u683c\u683c\u4e00 \u7b11\uff0c\u9053\uff1a\u201c\u6a0a\u5927\u54e5\uff0c\u6211\u8bb0\u5f97\u4f60\u559c\u6b22\u559d\u9152\uff0c\u90a3\u4e00\u5e74\u4f60\u5077\u5077\u6559\u6211\u559d\u9152\uff0c\u9669\u4e9b\u7ed9\u7239\u77e5\u9053\u3002\u597d\uff0c\u6211\u7ed9 \u4f60\u5f04\u4e24\u74f6\u9152\uff0c\u518d\u5f04\u4e09\u65a4\u814a\u864e\u8089\u7ed9\u4f60\u5c1d\u5c1d\uff0c\u8fd9\u53ea\u8001\u864e\u8fd8\u662f\u6211\u6253\u7684\u5462\uff01\u201d\u8001\u6a0a\u4e00\u7ad6\u62c7\u6307\uff0c\u9053\uff1a \u201c\u5c0f\u864e\u6253\u8001\u864e\uff0c\u597d\uff0c\u771f\u6210\uff01\u201d\u5c0f\u864e\u5b50\u88ab\u4eba\u5938\u5956\uff0c\u5341\u5206\u53d7\u7528\uff0c\u7b11\u563b\u563b\u5730\u8dd1\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u90a3\u519b\u5b98\u6447\u6447\u5934\u9053\uff1a\u201c\u8fd9\u5c0f\u86ee\u725b\u813e\u6c14\u771f\u5927\uff0c\u5582\uff0c\u8001\u6a0a\u4f60\u8bf4\u7684\u90a3\u4f4d\u8001\u82f1\u96c4\u5c31\u662f\u4ed6\u7684\u7236\u4eb2\uff1f\u201d \u8001\u6a0a\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f60\u77a7\uff0c\u4ed6\u513f\u5b50\u5df2\u7ecf\u5982\u6b64\u4e86\u5f97\uff0c\u4f60\u603b\u53ef\u4ee5\u653e\u5fc3\u4e86\u5427\uff1f\u201d\u53e6\u4e00\u4f4d\u519b\u5b98\u9053\uff1a\u201c\u4ed6 \u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u4e3a\u4ec0\u4e48\u4f60\u603b\u4e0d\u80af\u8bf4\u3002\u201d\u8001\u6a0a\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u82f1\u96c4\u516b\u5e74\u524d\u5df2\u95ed\u95e8\u5c01\u5200\uff0c\u4ed6\u53ef\u4e0d\u613f\u522b \u4eba\u518d\u5728\u6c5f\u6e56\u4e0a\u63d0\u8d77\u4ed6\u7684\u540d\u5b57\u3002\u7b49\u4ed6\u7b54\u5141\u4e4b\u540e\uff0c\u90a3\u65f6\u4ed6\u81ea\u7136\u4f1a\u5bf9\u4f60\u8bf4\u3002\u201d\u90a3\u519b\u5b98\u9053\uff1a\u201c\u65e2\u7136\u4ed6 \u5df2\u95ed\u95e8\u5c01\u5200\uff0c\u4f60\u8fd8\u5e26\u6211\u4eec\u6765\u505a\u4ec0\u4e48\uff1f\u54b1\u4eec\u4e4b\u4e8b\u6025\u5982\u661f\u706b\uff0c\u82e5\u4ed6\u4e0d\u5e94\u5141\uff0c\u5c82\u4e0d\u53cd\u800c\u5ef6\u8bef\u4e86\u3002\u201d \u8001\u6a0a\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u80af\u4e3a\u6211\u7834\u4e00\u7834\u4f8b\u3002\u4e24\u4f4d\u5927\u4eba\u82e5\u662f\u77a7\u7740\u4e0d\u884c\uff0c\u90a3\u5c31\u8bf7\u4e24\u4f4d\u5927\u4eba\u53e6\u8bf7\u80fd\u4eba\uff0c\u6211 \u59d3\u6a0a\u7684\u53ef\u6ca1\u6709\u6cd5\u5b50\u5566\u3002\u201d\u90a3\u4e24\u4f4d\u519b\u5b98\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u4f60\u660e\u660e\u77e5\u9053\u6211\u4eec\u6ca1\u6709\u6cd5\u5b50\uff0c \u5c31\u50cf\u6eba\u6c34\u7684\u4eba\u627e\u7740\u4e00\u6839\u7a3b\u8349\u4e5f\u597d\uff0c\u4f60\u8fd9\u4e0d\u662f\u6545\u610f\u6c14\u6211\u4eec\u5417\uff1f\u201d\u53c8\u60f3\u9053\uff1a\u201c\u542c\u8001\u6a0a\u7684\u53e3\u6c14\uff0c\u4f3c \u4e4e\u548c\u8fd9\u91cc\u7684\u4e3b\u4eba\u6709\u7279\u522b\u7684\u4ea4\u60c5\uff0c\u54ce\uff0c\u4e5f\u53ea\u6709\u9760\u4ed6\u5566\u3002\u201d \u3000\u3000\u7b49\u4e86\u4e00\u9635\uff0c\u5c0f\u864e\u5b50\u8fd8\u672a\u8fdb\u6765\uff0c\u4e24\u4e2a\u519b\u5b98\u89e3\u4e0b\u4e0a\u8863\uff0c\u7ed9\u81ea\u5df1\u80a9\u4e0a\u7684\u4f24\u53e3\u6362\u836f\uff0c\u4e00\u4e2a\u519b\u5b98 \u9053\uff1a\u201c\u90a3\u8499\u9762\u5f3a\u76d7\u5389\u5bb3\u5f97\u5f88\uff0c\u8001\u6a0a\uff0c\u54b1\u4eec\u51e0\u767e\u4eba\u6050\u6015\u5c31\u4f60\u4e00\u4e2a\u4eba\u6ca1\u53d7\u4f24\u4e86\uff1f\u201d\u8001\u6a0a\u9053\uff1a\u201c\u6211 \u4e5f\u51e0\u4e4e\u6328\u4ed6\u4e00\u68d2\u3002\u201d\u90a3\u519b\u5b98\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u82f1\u96c4\u5355\u8eab\u4e00\u4eba\u80fd\u6210\u4e8b\u4e48\uff1f\u201d\u8001\u6a0a\u9053\uff1a\u201c\u53ea\u8981\u4ed6\u7b54 \u5e94\uff0c\u80dc\u4e8e\u5343\u519b\u4e07\u9a6c\u3002\u201d\u4e24\u4e2a\u519b\u5b98\u8c08\u8d77\u90a3\u8499\u9762\u5f3a\u76d7\u72b9\u6709\u4f59\u6016\uff0c\u4e00\u4e2a\u9053\uff1a\u201c\u82e5\u662f\u4e0d\u6210\uff0c\u54b1\u4eec\u7684\u8eab \u5bb6\u6027\u547d\u5c31\u5168\u5b8c\u5566\uff01\u201d\u4e00\u4e2a\u9053\uff1a\u201c\u54b1\u4eec\u73b0\u5728\u5c31\u53ea\u6709\u9760\u4ed6\uff0c\u4e8e\u5927\u54e5\uff0c\u4f60\u522b\u8bf4\u4e0d\u5409\u5229\u7684\u8bdd\u5566\u3002\u201d\u8001 \u6a0a\u4e00\u58f0\u4e0d\u54cd\uff0c\u5bf9\u4e24\u4e2a\u519b\u5b98\u60a3\u5f97\u60a3\u5931\u7684\u5fc3\u60c5\u4f3c\u662f\u751a\u4e0d\u9ad8\u5174\u3002\u5ffd\u89c1\u90a3\u865a\u63a9\u7684\u77f3\u95e8\u4e00\u5f00\uff0c\u5c0f\u864e\u5b50\u8df3 \u4e86\u8fdb\u6765\uff0c\u54ac\u7d27\u53e3\u5507\uff0c\u9762\u8272\u5341\u5206\u96be\u770b\uff0c\u8001\u6a0a\u5fc3\u4e2d\u4e00\u51db\uff0c\u5c0f\u864e\u5b50\u53cc\u624b\u7a7a\u7a7a\uff0c\u6839\u672c\u6ca1\u6709\u5e26\u4efb\u4f55\u9152 \u98df\uff0c\u4e00\u5f00\u53e3\u5c31\u9053\uff1a\u201c\u6a0a\u5927\u54e5\uff0c\u4f60\u53ef\u4e0d\u591f\u670b\u53cb\uff01\u201d \u3000\u3000\u8001\u6a0a\u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u4f60\u82e5\u591f\u670b\u53cb\uff0c\u5c31\u5c06\u4eca\u65e5\u7684\u6765\u610f\u544a\u8bc9\u6211 \u77e5\uff0c\u8981\u4e0d\u7136\u6211\u5c31\u8dd1\u53bb\u544a\u8bc9\u6211\u7239\uff0c\u53eb\u4ed6\u4e0d\u8981\u7406\u4f60\u3002\u201d\u8001\u6a0a\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4f60\u7239\u53bb\u4e86\u54ea\u91cc\uff1f\u201d\u5c0f\u864e \u5b50\u9053\uff1a\u201c\u5f53\u7136\u77e5\u9053\uff0c\u4f60\u5feb\u4e9b\u8bf4\uff0c\u4f60\u8981\u9080\u4ed6\u548c\u8c01\u4f5c\u5bf9\uff1f\u201d\u5176\u5b9e\u5c0f\u864e\u5b50\u5e76\u4e0d\u77e5\u9053\u4ed6\u7239\u4e3a\u4f55\u7a81\u7136\u4e0d \u89c1\uff0c\u4ed6\u7239\u4e03\u516b\u5e74\u6765\uff0c\u5728\u8fd9\u4e2a\u65f6\u5206\uff0c\u4ece\u4e0d\u4f1a\u51fa\u95e8\uff0c\u5c0f\u864e\u5b50\u9690\u7ea6\u89c9\u5f97\u8fd9\u662f\u4eca\u65e5\u6765\u7684\u8fd9\u51e0\u4e2a\u964c\u751f\u4eba \uff08\u8fde\u90a3\u4e2a\u5c11\u5e74\u4eba\u5728\u5185\uff09\u60f9\u6765\u7684\uff0c\u4ed6\u521a\u624d\u5077\u542c\u4e86\u4e00\u9635\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u603b\u611f\u5230\u8fd9\u4e00\u73ed\u4eba\u5c06\u5bf9\u4ed6\u7239\u7239 \u4e0d\u5229\uff0c\u56e0\u6b64\u7acb\u5fc3\u8981\u9a97\u8001\u6a0a\u7684\u8bdd\u3002 \u3000\u3000\u8001\u6a0a\u7565\u4e00\u8e0c\u8e87\uff0c\u770b\u770b\u90a3\u4e24\u4e2a\u519b\u5b98\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u5c0f\u864e\u5b50\uff0c\u4f60\u4e0d\u662f\u666e\u901a\u7684\u513f\u7ae5\uff0c\u6211 \u5c31\u8bf4\u7ed9\u4f60\u542c\uff0c\u4f60\u53ef\u5f97\u5356\u6211\u7684\u4ea4\u60c5\u5440\uff01\u201d\u6307\u6307\u90a3\u4e2a\u519b\u5b98\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u4e8e\u7edf\u9886\uff0c\u8fd9\u4f4d\u662f\u9646\u7ba1\u5e26\uff0c \u6211\u66ff\u4ed6\u4eec\u4fdd\u9556\uff0c\u4ece\u6e56\u5317\u62bc\u89e3\u4e09\u5341\u4e07\u4e24\u6f15\u8fd0\u8fdb\u4eac\uff0c\u6f15\u8fd0\u4f60\u4e0d\u61c2\uff0c\u603b\u4e4b\u662f\u4e09\u5341\u4e07\u4e24\u94f6\u5b50\u7684\u5b98\u664c\u5c31 \u662f\u4e86\uff0c\u5230\u4e86\u5c71\u4e1c\uff0c\u5c31\u5728\u524d\u5929\uff0c\u5728\u6cf0\u5c71\u7684\u5357\u9762\uff0c\u7ed9\u4e00\u4e2a\u8499\u9762\u5f3a\u76d7\u52ab\u53bb\u5566\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u6a0a\u5927 \u54e5\uff0c\u4f60\u4e5f\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff1f\u201d\u8001\u6a0a\u82e6\u7b11\u9053\uff1a\u201c\u82e5\u7136\u6211\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u6211\u5c31\u4e0d\u7528\u5230\u4f60\u5bb6\u6765\u5566\u3002\u8fd9\u4e24 \u4f4d\u5927\u4eba\u90fd\u53d7\u4e86\u4f24\uff0c\u6211\u4eec\u5e26\u7684\u51e0\u767e\u540d\u5b98\u5175\u90fd\u7ed9\u90a3\u5f3a\u76d7\u6349\u7684\u6349\u6740\u7684\u6740\u4e86\uff0c\u5c31\u6211\u4eec\u4e09\u4eba\u9003\u51fa\u6765\u3002\u201d \u5c0f\u864e\u5b50\u542c\u5f97\u51fa\u795e\uff0c\u9053\uff1a\u201c\u54c8\uff0c\u8fd9\u5f3a\u76d7\u597d\u672c\u4e8b\uff01\u662f\u4e2a\u5927\u5927\u7684\u597d\u6c49\uff01\u201d\u4e24\u4e2a\u519b\u5b98\u5927\u4e3a\u607c\u6012\uff0c\u76ef\u4e86 \u5c0f\u864e\u5b50\u4e00\u773c\uff0c\u8001\u6a0a\u5e72\u7b11\u4e00\u58f0\uff0c\u62c9\u7740\u5c0f\u864e\u5b50\u7684\u624b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8981\u4e0d\u662f\u90a3\u5f3a\u76d7\u5389\u5bb3\uff0c\u6211\u600e\u6562\u60ca\u52a8 \u4f60\u7239\u3002\u6211\u662f\u6765\u8bf7\u4f60\u7239\u53bb\u6349\u90a3\u4e2a\u5f3a\u76d7\uff0c\u593a\u56de\u8fd9\u4e09\u5341\u4e07\u4e24\u94f6\u5b50\u3002\u201d\u5c0f\u864e\u5b50\u8d77\u521d\u542c\u5f97\u8001\u6a0a\u6367\u4ed6\u7684\u7239 \u7239\uff0c\u54a7\u5f00\u5c0f\u5634\u4e00\u7b11\uff0c\u542c\u5b8c\u4e4b\u540e\uff0c\u7a81\u7136\u4e00\u4e0b\u6454\u5f00\u8001\u6a0a\u7684\u624b\uff0c\u9053\uff1a\u201c\u6a0a\u5927\u54e5\uff0c\u4f60\u53ef\u4e0d\u591f\u670b\u53cb \u4e86\uff01\u201d\u8001\u6a0a\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u591f\u670b\u53cb\u4e86\uff1f\u201d\u5c0f\u864e\u5b50\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u7239\u7239\u6700\u8ba8\u538c\u72d7\u5b98\uff0c\u4f60\u5374\u8981\u8bf7 \u4ed6\u51fa\u5c71\uff0c\u518d\u53bb\u505a\u5b98\u5e9c\u7684\u5974\u624d\uff0c\u54fc\uff0c\u54fc\uff01\u6211\u5c31\u4e0d\u7b54\u5e94\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u8001\u6a0a\u4e0e\u90a3\u4e24\u4e2a\u519b\u5b98\u90fd\u610f\u6599 \u4e0d\u5230\uff0c\u4e0d\u89c9\u5140\u7136\uff0c\u5ffd\u542c\u5f97\u201c\u7830\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u539f\u6765\u5c0f\u864e\u5b50\u5ffd\u5730\u8df3\u51fa\u95e8\u5916\uff0c\u5c06\u90a3\u4e24\u6247\u77f3\u95e8\u5173\u4e0a \u4e86\u3002\u8fd9\u4e24\u6247\u77f3\u95e8\u90fd\u662f\u534a\u5c3a\u539a\u7684\u6574\u5757\u5de8\u77f3\u4f5c\u7684\uff0c\u82e5\u975e\u4e24\u81c2\u6709\u4e09\u4e94\u767e\u65a4\u529b\u6c14\uff0c\u4e5f\u4f11\u60f3\u5173\u5f97\u4e0a\u8fd9\u4e24 \u6247\u77f3\u95e8\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5c0f\u864e\u5b50\u5728\u5916\u9762\u7b11\u9053\uff1a\u201c\u6a0a\u5927\u54e5\uff0c\u5bf9\u4e0d\u4f4f\u5566\u3002\u6211\u544a\u8bc9\u7239\u53bb\uff0c\u4ed6\u82e5\u80af\u653e\u4f60\u4eec\uff0c\u6211\u518d\u7ed9 \u4f60\u8d54\u7f6a\u3002\u201d\u4e00\u9635\u8e22\u54d2\u8e22\u54d2\u7684\u58f0\u8d70\u51fa\u5c4b\u5916\uff0c\u5c0f\u864e\u5b50\u4f3c\u4e4e\u8dd1\u5f97\u5f88\u6b22\uff0c\u5634\u91cc\u8fd8\u54fc\u7740\u5c71\u6b4c\u3002 \u3000\u3000\u4e24\u4e2a\u519b\u5b98\u9a82\u9053\uff1a\u201c\u5c0f\u5f3a\u76d7\uff01\u201d\u8df3\u4e0a\u524d\u53bb\u63a8\u95e8\uff0c\u54ea\u91cc\u63a8\u5f97\u52a8\uff0c\u77f3\u95e8\u5df2\u7ed9\u5c0f\u864e\u5b50\u5728\u5916\u9762\u53cd\u9501 \u4e86\u3002\u8fd9\u95f4\u77f3\u5ba4\u6ca1\u6709\u7a97\u6237\uff0c\u53ea\u6b63\u9762\u6709\u51e0\u4e2a\u901a\u98ce\u7684\u6c14\u5b54\uff0c\u8001\u9f20\u4e5f\u94bb\u4e0d\u8fc7\uff0c\u4e24\u4e2a\u519b\u5b98\u6c14\u5f97\u6cfc\u53e3\u5927 \u9a82\uff0c\u8fde\u8001\u6a0a\u4e5f\u57cb\u6028\u4e86\u3002\u201c\u54fc\uff0c\u54fc\uff0c\u539f\u6765\u4f60\u7684\u670b\u53cb\u8fd9\u6837\u618e\u6068\u671d\u5ef7\u547d\u5bab\uff0c\u4f60\u600e\u4e48\u5e26\u6211\u4eec\u8fdb\u8fd9\u72d7\u5f3a \u76d7\u7a9d\u6765\uff1f\u201d\u201c\u4e00\u5b9a\u4e5f\u662f\u5f3a\u76d7\uff01\u6a0a\u82f1\uff0c\u4f60\u8fd9\u662f\u4ec0\u4e48\u7528\u610f\uff1f\u201d\u8001\u6a0a\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u4eba\u4e14 \u522b\u9a82\uff0c\u8fd9\u5c4b\u5b50\u4e3b\u4eba\uff0c\u4ed6\u505a\u8fc7\u7684\u5b98\u6bd4\u4f60\u4eec\u7684\u4e0a\u53f8\u8fd8\u5927\u5f97\u591a\uff01\u201d \u3000\u3000\u4e24\u4e2a\u519b\u5b98\u500f\u5730\u505c\u53e3\u4e0d\u9a82\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u5148\u540e\u95ee\u9053\uff1a\u201c\u4ed6\u662f\u8c01\uff1f\u201d\u201c\u4ed6\u600e\u4e48\u4f4f\u5728\u8fd9\u91cc\uff1f\u201d \u201c\u600e\u4e48\u6709\u8fd9\u6837\u4e00\u4e2a\u91ce\u5b69\u5b50\uff0c\u54fc\uff0c\u4e0d\u6b62\u91ce\uff0c\u7b80\u76f4\u662f\u5934\u5c0f\u86ee\u725b\uff01\u201d\u201c\u4ed6\u65e2\u505a\u8fc7\u5927\u5b98\uff0c\u4e3a\u4f55\u53cd\u800c\u6068 \u505a\u5b98\u7684\uff1f\u201d\u4e24\u4eba\u4e03\u5634\u516b\u820c\uff0c\u8a00\u4e0b\u4e4b\u610f\uff0c\u65e2\u662f\u4e0d\u4fe1\uff0c\u4f46\u90fd\u4e0d\u50cf\u5148\u524d\u90a3\u6837\u5730\u653e\u6063\uff0c\u5c06\u5c4b\u4e3b\u4eba\u80e1\u9a82 \u4e00\u901a\u4e86\u3002 \u3000\u3000\u8001\u6a0a\u5fae\u5fae\u4e00\u7b11\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fd9\u5c4b\u5b50\u7684\u4e3b\u4eba\u4ee5\u524d\u66fe\u505a\u8fc7\u5fa1\u6797\u519b\u7684\u7edf\u9886\uff0c\u53c8\u505a\u8fc7\u9526\u8863\u536b\u7684 \u603b\u6307\u6325\uff0c\u5341\u5e74\u4e4b\u524d\uff0c\u53f7\u79f0\u4eac\u5e08\u7b2c\u4e00\u9ad8\u624b\uff0c\u4ed6\uff0c\u4ed6\u5c31\u662f\u5f20\u51e4\u5e9c\uff0c\u5f20\u5927\u4eba\uff01\u201d\u4fe9\u519b\u5b98\u4e0d\u7ea6\u800c\u540c\u5730 \u60ca\u53eb\u9053\uff1a\u201c\u4eac\u5e08\u7b2c\u4e00\u9ad8\u624b\u5f20\u98ce\u5e9c\uff1f\u201d\u8001\u6a0a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4eac\u5e08\u7b2c\u4e00\u9ad8\u624b\u5f20\u98ce\u5e9c\uff01\u201d\u4e24\u4e2a\u519b\u5b98\u542c \u540e\u201c\u5530\u201d\u5730\u4e00\u4e0b\uff0c\u9762\u8272\u5168\u90e8\u53d8\u4e86\uff0c\u867d\u5728\u6c81\u51c9\u7684\u77f3\u5ba4\u4e4b\u4e2d\uff0c\u4e5f\u5413\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u662f\u6b63\u7edf\u5e74\u95f4\uff08\u5373\u660e\u82f1\u5b97\u7948\u9547\uff09\uff0c\u7687\u5e1d\u6700\u501a\u91cd\u7684\u7b2c\u4e00\u540d\u9ad8\u624b\uff0c\u4e0d\u4f46\u7edf\u7387\u8fc7\u5fa1\u6797\u519b\u3001 \u9526\u8863\u536b\uff0c\u800c\u4e14\u66fe\u5c61\u7acb\u6218\u529f\uff0c\u5a01\u9707\u4e2d\u5916\uff0c\u5f53\u5e74\u548c\u74e6\u523a\u5728\u571f\u6728\u5821\u4e4b\u6218\uff0c\u660e\u519b\u5168\u90e8\u8986\u706d\uff0c\u7948\u9547\u88ab \u4fd8\uff0c\u4ed6\u5374\u5355\u4eba\u5339\u9a6c\uff0c\u4e03\u8fdb\u4e03\u51fa\uff0c\u867d\u7136\u6551\u4e0d\u4e86\u7687\u5e1d\uff0c\u5374\u4ee4\u80e1\u4eba\u95fb\u540d\u80c6\u6218\uff0c\u5929\u4e0b\u82f1\u96c4\uff0c\u65e0\u4e0d\u666f \u4ef0\u3002 \u3000\u3000\u5176\u540e\u660e\u9601\u90e8\u5927\u81e3\u4e8e\u8c26\u6d3e\u9063\u4e91\u91cd\u51fa\u4f7f\u74e6\u523a\uff0c\u4e24\u56fd\u8c08\u548c\uff0c\u5c06\u7948\u9547\u63a5\u56de\uff0c\u7948\u9547\u7684\u5f1f\u5f1f\u7948\u94ae\uff08\u660e \u5c3a\u5b97\uff09\u4e0d\u80af\u8ba9\u4f4d\uff0c\u5c06\u54e5\u54e5\u56da\u7981\u5357\u5b98\uff0c\u201c\u664b\u53f7\u201d\u592a\u4e0a\u7687\uff0c\u5f20\u98ce\u5e9c\u7acb\u5373\u6302\u51a0\u800c\u53bb\uff0c\u4ece\u6b64\u4e0d\u77e5\u4e0b \u843d\uff0c\u6709\u4eba\u8bf4\u4ed6\u662f\u7737\u604b\u6545\u4e3b\uff0c\u4e0d\u80af\u5728\u65b0\u7687\u5e1d\u624b\u4e0b\u505a\u5b98\uff1b\u6709\u7684\u4eba\u8bf4\u4ed6\u770b\u6de1\u529f\u540d\uff0c\u9690\u5c45\u4fee\u9053\u3002\u5176\u5b9e \u4ed6\u5374\u662f\u53d7\u597d\u53cb\u5f20\u4e39\u67ab\u6240\u529d\uff0c\u770b\u900f\u4e86\u7687\u5ba4\u7684\u8150\u70c2\uff0c\u66f4\u517c\u5978\u4f5e\u5f53\u671d\uff0c\u8d24\u4eba\u4e0d\u7528\uff08\u4e00\u5982\u529f\u52cb\u76d6\u4e16\u7684 \u4e8e\u8c26\uff0c\u671d\u5ef7\u5c31\u53ea\u51c6\u4ed6\u505a\u4e00\u4e2a\u6302\u540d\u7684\u5175\u90e8\u5c1a\u4e66\uff0c\u4e0d\u8bb8\u4ed6\u5e72\u9884\u671d\u653f\u3002\uff09\u6545\u6b64\u4ed6\u5fc3\u7070\u610f\u51b7\uff0c\u95ed\u95e8\u5c01 \u5200\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u519b\u5b98\u4e07\u4e07\u6599\u4e0d\u5230\uff0c\u5a01\u9707\u4e2d\u5916\u7684\u4ee5\u524d\u7684\u4eac\u5e08\u7b2c\u4e00\u9ad8\u624b\u5f20\u98ce\u5e9c\uff0c\u7adf\u7136\u5c31\u662f\u8fd9\u95f4\u5c4b\u7684\u4e3b \u4eba\uff0c\u60f3\u8d77\u9002\u624d\u8fd8\u9a82\u4ed6\u662f\u201c\u72d7\u5f3a\u76d7\u201d\uff0c\u867d\u7136\u660e\u77e5\u5f20\u98ce\u5e9c\u4e0d\u5728\u5c4b\u5185\uff0c\u4ea6\u81ea\u60f4\u60f4\u4e0d\u5b89\uff0c\u8001\u6a0a\u5fae\u5fae\u4e00 \u7b11\uff0c\u659c\u501a\u5899\u58c1\uff0c\u518d\u4e0d\u8a00\u8bed\u3002\u4e24\u4e2a\u519b\u5b98\u671b\u4ed6\u4e00\u773c\uff0c\u7591\u5fc3\u5927\u8d77\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u9053\uff1a\u201c\u6a0a\u5144\uff0c\u54b1\u4eec\u662f \u6709\u773c\u4e0d\u8bc6\u6cf0\u5c71\uff0c\u539f\u6765\u6a0a\u5144\u7adf\u662f\u2018\u771f\u4eba\u4e0d\u9732\u76f8\uff0c\u9732\u76f8\u975e\u771f\u4eba\u3002\u2019\u4e00\u8def\u4e4b\u4e0a\uff0c\u54b1\u4eec\u591a\u591a\u5931\u656c \u4e86\u3002\u201d\u539f\u6765\u8fd9\u4e09\u5341\u4e07\u94f6\u5b50\u5b98\u664c\uff0c\u662f\u4e24\u6e56\u76d0\u8fd0\u4f7f\u8d2f\u5c45\u59d4\u6258\u6e56\u5317\u5de1\u629a\u6d3e\u4ed6\u4eec\u62bc\u89e3\u4e0a\u4eac\u7684\uff0c\u8fd9\u4e24\u4e2a \u519b\u5b98\u662f\u6e56\u5317\u5de1\u629a\u624b\u4e0b\u6700\u5f97\u529b\u7684\u4e24\u4e2a\u5c06\u9886\uff0c\u4ed6\u4eec\u70b9\u4e86\u4e94\u767e\u540d\u7cbe\u58ee\u52b2\u5352\u62bc\u89e3\u5b98\u94f6\uff0c\u81ea\u4ee5\u4e3a\u4e07\u5143\u4e00 \u5931\uff0c\u4e0d\u613f\u6709\u4eba\u5206\u529f\uff0c\u4e0d\u60f3\u52a8\u8eab\u4e4b\u524d\uff0c\u76d0\u8fd0\u4f7f\u90e8\u53c8\u8350\u4e86\u4e00\u4e2a\u9556\u5e08\u6765\uff0c\u8fd9\u9556\u5e08\u4fbf\u662f\u6a0a\u82f1\u3002\u4e24\u4e2a\u519b \u5b98\u4e00\u6253\u542c\uff0c\u5357\u65b9\u51e0\u7701\u6709\u540d\u7684\u9556\u5c40\uff0c\u90fd\u6ca1\u6709\u6a0a\u82f1\u8fd9\u4e2a\u4eba\u7269\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u5230\u5e95\u662f\u4e0d\u662f\u9556\u5e08\uff0c\u53ea\u56e0\u4ed6 \u662f\u76d0\u8fd0\u4f7f\u8350\u6765\u7684\u4eba\uff0c\u4e0d\u5f97\u4e0d\u7eb3\uff0c\u5fc3\u4e2d\u53ef\u662f\u4e0d\u5927\u9ad8\u5174\u3002\u54ea\u77e5\u8fd9\u8001\u6a0a\u7adf\u662f\u631f\u6709\u60ca\u4eba\u6280\u4e1a\uff0c\u5b98\u94f6\u88ab \u52ab\u4e4b\u65f6\uff0c\u53ea\u6709\u4ed6\u4e00\u4eba\u80fd\u6321\u90a3\u8499\u9762\u5927\u76d7\u6570\u5341\u62db\uff0c\u6ca1\u6709\u53d7\u4f24\uff0c\u8fd9\u8fd8\u7f62\u4e86\uff0c\u4ed6\u5c45\u7136\u8fd8\u548c\u5f20\u98ce\u5e9c\u5927\u6709 \u6e0a\u6e90\uff0c\u4e24\u4e2a\u519b\u5b98\u90fd\u4e0d\u89c9\u522e\u76ee\u76f8\u770b\uff0c\u540c\u65f6\u7591\u5fc3\u5927\u8d77\uff0c\u6478\u4e0d\u6e05\u662f\u4f55\u6765\u5386\u3002 \u3000\u3000\u4e24\u4e2a\u519b\u5b98\u4e0d\u7ea6\u800c\u540c\u5730\u5c0f\u5fc3\u8d54\u7f6a\uff0c\u6a0a\u82f1\u53ea\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u201c\u4e24\u4f4d\u5927\u4eba\u8a00\u91cd\u4e86\uff0c\u6a0a\u67d0 \u53ea\u662f\u4e00\u4e2a\u5bfb\u5e38\u7684\u9556\u5e08\u800c\u5df2\uff0c\u54ea\u6562\u5f53\u662f\u4ec0\u4e48\u771f\u4eba\uff1f\u201d\u8bf4\u5b8c\u4e4b\u540e\u53c8\u659c\u501a\u77f3\u58c1\uff0c\u7adf\u81ea\u95ed\u76ee\u517b\u795e\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u519b\u5b98\u8baa\u8baa\u5730\u597d\u751f\u6ca1\u8da3\uff0c\u60f3\u63a2\u542c\u6a0a\u82f1\u4e0e\u5f20\u98ce\u5e9c\u6709\u4f55\u5173\u7cfb\uff0c\u5374\u53c8\u4e0d\u4fbf\u5f00\u53e3\uff0c\u53ea\u597d\u5577\u5577 \u5495\u5495\u5730\u518d\u4e09\u8c08\u8bba\u5b98\u94f6\u88ab\u52ab\u4e4b\u4e8b\uff0c\u4e00\u4f1a\u513f\u5509\u58f0\u53f9\u6c14\uff0c\u8bf4\u662f\u5b98\u94f6\u82e5\u4e0d\u80fd\u8ffd\u56de\uff0c\u5927\u5bb6\u90fd\u6709\u6284\u5bb6\u4e4b \u7978\uff1b\u4e00\u4f1a\u513f\u53c8\u5c06\u5f20\u98ce\u5e9c\u606d\u7ef4\u5907\u81f3\uff0c\u8bf4\u4ed6\u4e0d\u6b62\u662f\u4eac\u5e08\u7b2c\u4e00\u9ad8\u624b\uff0c\u800c\u4e14\u5929\u4e0b\u65e0\u654c\uff0c\u5c31\u53ea\u4f2f\u4ed6\u4e0d\u80af \u51fa\u5c71\u76f8\u52a9\uff0c\u4e00\u4f1a\u513f\u53c8\u6084\u6084\u5730\u8c08\u8bba\u6a0a\u82f1\uff0c\u6545\u610f\u8ba9\u4ed6\u542c\u89c1\uff0c\u8bf4\u6a0a\u82f1\u4e00\u8eab\u6b66\u827a\uff0c\u4e0d\u5e94\u8be5\u57cb\u6ca1\u9556\u884c\uff0c \u4f5c\u4e2a\u9556\u5e08\uff0c\u53c8\u8bf4\u82e5\u6a0a\u82f1\u6b64\u6b21\u8bf7\u5f97\u5f20\u98ce\u5e9c\u51fa\u5c71\uff0c\u8ba8\u56de\u5b98\u94f6\uff0c\u4ed6\u4e24\u4eba\u5fc5\u5b9a\u8981\u636e\u5b9e\u594f\u7980\uff0c\u8ba9\u6a0a\u82f1\u7acb \u523b\u53ef\u4ee5\u4e3a\u5b98\uff0c\u6700\u5c11\u4e5f\u662f\u4e2a\u6b63\u4e94\u54c1\u5b88\u5907\u3002 \u3000\u3000\u6a0a\u82f1\u542c\u5f97\u6697\u6697\u597d\u7b11\uff0c\u4f46\u5fc3\u4e2d\u5374\u662f\u6c89\u91cd\u5982\u94c5\uff0c\u4ed6\u4e5f\u60f3\u4e0d\u5230\u5f20\u98ce\u5e9c\u5f52\u9690\u4e4b\u540e\uff0c\u7adf\u7136\u5bf9\u5b98\u573a\u5982 \u6b64\u6df1\u6076\u75db\u7edd\u3002\u6a0a\u82f1\u5fc3\u9053\uff1a\u201c\u5176\u5b9e\u6211\u53c8\u4f55\u5c1d\u613f\u610f\u5f53\u8fd9\u4e2a\u5dee\u4f7f\uff0c\u8fd9\u56de\u5f04\u5f97\u4e0d\u597d\uff0c\u4e0d\u4f46\u6559\u6c5f\u6e56\u540c\u9053 \u7591\u5fc3\uff0c\u53ea\u6015\u5f20\u4e16\u4f2f\u4e5f\u6000\u7591\u6211\u8ffd\u6c42\u529f\u540d\u5229\u7984\u4e86\u3002\u201d\u8fd9\u5239\u90a3\u95f4\uff0c\u76d0\u8fd0\u4f7f\u8d2f\u5c45\u9080\u4ed6\u76f8\u52a9\uff0c\u8499\u9762\u5927\u76d7 \u52ab\u94f6\u7b49\u4e00\u5e55\u5e55\u5f80\u4e8b\uff0c\u90fd\u91cd\u73b0\u51fa\u6765\u3002 \u3000\u3000\u201c\u5509\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u51fa\u6765\u66ff\u5b98\u94f6\u4fdd\u9556\uff0c\u81ea\u8ba8\u82e6\u5403\uff1f\u8fd9\u4fe9\u5bb6\u4f19\u4e0d\u77e5\u6211\u7684\u6765\u5386\uff0c\u4f46\u6c5f\u6e56\u4e0a\u7684\u670b \u53cb\uff0c\u591a\u5c11\u4e16\u77e5\u9053\u6211\u5ba3\u82b1\u65a7\u6a0a\u82f1\u7684\u5c0f\u5c0f\u540d\u5934\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u7518\u5fc3\u66ff\u5b98\u5e9c\u5f53\u5dee\uff1f\u201d\u6a0a\u82f1\u5fc3\u4e2d\u81ea\u8a00\u81ea \u8bed\uff1a\u201c\u8c01\u53eb\u6211\u662f\u6a0a\u5fe0\u7684\u4f84\u513f\uff01\u800c\u90a3\u76d0\u8fd0\u4f7f\u8d2f\u5c45\u5374\u662f\u6211\u7684\u4e16\u4ea4\u5144\u5f1f\uff0c\u539f\u6765\u5f53\u5e74\u5f20\u98ce\u5e9c\u4e0e\u6a0a\u5fe0\u3001 \u8d2f\u4ef2\u4e8c\u4eba\u5408\u79f0\u4eac\u5e08\u4e09\u5927\u9ad8\u624b\uff0c\u5f20\u98ce\u5e9c\u4e0e\u660e\u671d\u7687\u5e1d\u7684\u4e16\u4ec7\u5f20\u4e39\u67ab\u76f8\u4ea4\uff0c\u8d2f\u4ef2\u6697\u4e2d\u51fa\u5356\u76df\u5144\uff0c\u7528 \u5bc6\u6298\u7980\u594f\u7687\u5e1d\uff0c\u5374\u88ab\u5f20\u4e39\u67ab\u622a\u83b7\uff0c\u5c06\u4ed6\u6740\u4e86\uff0c\u8fd9\u4e8b\u60c5\u5f53\u65f6\u8fd8\u5f15\u8d77\u5f20\u98ce\u5e9c\u7684\u4e00\u573a\u8bef\u4f1a\u3002\u81f3\u4e8e\u6a0a \u5fe0\u5219\u662f\u5728\u571f\u6728\u5821\u88ab\u56f4\u4e4b\u65f6\uff0c\u4e00\u9524\u51fb\u6b7b\u5356\u56fd\u7684\u5978\u5ba6\u738b\u632f\uff0c\u7136\u540e\u6218\u6b7b\u7684\u3002\u8d2f\u4ef2\u7684\u513f\u5b50\u8d2f\u5c45\u9760\u7740\u4e43 \u7236\u7684\u4f59\u836b\u5728\u5b98\u573a\u4e2d\u6df7\uff0c\u7adf\u6df7\u5230\u4e86\u4e24\u6e56\u76d0\u8fd0\u4f7f\u7684\u80a5\u7f3a\uff0c\u6a0a\u5fe0\u7684\u5f1f\u5f1f\u6a0a\u4fca\u672c\u6765\u4e5f\u662f\u5927\u5185\u536b\u58eb\uff0c\u54e5 \u54e5\u6b89\u56fd\u4e4b\u540e\uff0c\u4ed6\u4e5f\u5b66\u5f20\u98ce\u5e9c\u6240\u4e3a\uff0c\u5f03\u5b98\u4e0d\u505a\uff0c\u5f52\u9690\u6e56\u5317\u8001\u5bb6\u3002\u5f20\u3001\u6a0a\u3001\u8d2f\u4e09\u4eba\u5f53\u5e74\u7ed3\u4e3a\u5144 \u5f1f\uff0c\u8d2f\u4ef2\u867d\u7136\u51fa\u5356\u76df\u5144\uff0c\u5176\u4e8b\u53ea\u6709\u5f20\u4e39\u67ab\u4e0e\u5f20\u98ce\u5e9c\u4e8c\u4eba\u77e5\u9053\uff0c\u4e8c\u4eba\u9690\u6076\u626c\u5584\uff0c\u6b64\u4e8b\u4ece\u6765\u4e0d\u4e0e \u5916\u4eba\u8bf4\u8d77\uff08\u5305\u62ec\u6a0a\u4fca\u5728\u5185\uff09\uff0c\u4e09\u5bb6\u540e\u4ee3\u4ea4\u60c5\u4ecd\u5728\u3002\u6b64\u6b21\u8d2f\u4ef2\u7684\u513f\u5b50\u8d2f\u5c45\uff0c\u505a\u4e24\u6e56\u76d0\u8fd0\u4f7f\uff0c\u6070 \u6070\u9a7b\u8282\u6b66\u660c\uff0c\u56e0\u8981\u62bc\u89e3\u4e09\u5341\u4e07\u5b98\u94f6\u4e0a\u4eac\uff0c\u8d23\u4efb\u91cd\u5927\uff0c\u4ed6\u4fe1\u4e0d\u8fc7\u6e56\u5317\u5de1\u629a\u624b\u4e0b\u7684\u6b66\u5c06\uff0c\u6545\u6b64\u518d \u4e09\u6073\u6c42\u4e16\u53d4\u6a0a\u4fca\u76f8\u52a9\uff0c\u6a0a\u4fca\u5e74\u8001\uff0c\u4e0d\u613f\u51fa\u5c71\uff0c\u6240\u4ee5\u6d3e\u4e86\u513f\u5b50\u6a0a\u82f1\u4fdd\u9556\u3002\u6a0a\u82f1\u4e0e\u9ed1\u9053\u4e0a\u7684\u6210\u540d \u4eba\u7269\u5927\u534a\u90fd\u6709\u4ea4\u60c5\uff0c\u6697\u4e2d\u758f\u901a\uff0c\u4e00\u8def\u5e73\u5b89\u65e0\u4e8b\uff0c\u60f3\u4e0d\u5230\u8e0f\u5165\u4e86\u5c71\u4e1c\u5883\u5185\uff0c\u7adf\u5728\u6cf0\u5c71\u4e4b\u5357\uff0c\u88ab \u4e00\u4e2a\u8499\u9762\u5927\u76d7\u6240\u52ab\u3002\u90a3\u4e00\u5e55\u60ca\u5fc3\u6035\u76ee\u7684\u52ab\u6848\u8fd8\u5386\u5386\u5982\u5728\u76ee\u524d\u3002 \u3000\u3000\u90a3\u662f\u65b0\u5e74\u8fc7\u540e\u6ca1\u6709\u51e0\u5929\u7684\u4e8b\uff0c\u4e8e\u3001\u9646\u4e24\u4f4d\u519b\u5b98\u62bc\u89e3\u4e09\u5341\u4e07\u4e24\u5b98\u94f6\uff0c\u5df2\u8e0f\u5165\u5c71\u4e1c\u5883\u5185\uff0c\u82e5 \u8fc7\u4e86\u5c71\u4e1c\uff0c\u4e00\u5230\u6cb3\u5317\uff0c\u5c31\u662f\u4eac\u5e08\u5175\u529b\u53ef\u53ca\u8303\u56f4\uff0c\u66f4\u4e0d\u6101\u51fa\u4e8b\u4e86\u3002\u4e24\u4e2a\u519b\u5b98\u5174\u9ad8\u91c7\u70c8\uff0c\u4e00\u8def\u81ea \u7ba1\u81ea\u8d5e\uff0c\u4ee5\u4e3a\u662f\u5b98\u519b\u7684\u5a01\u98ce\uff0c\u5413\u5012\u4e86\u6c5f\u6e56\u8f9f\u76d7\uff0c\u5374\u4e0d\u77e5\u90a3\u662f\u6a0a\u82f1\u6697\u4e2d\u7684\u758f\u901a\u3002 \u3000\u3000\u90a3\u4e00\u65e5\u591c\u8ddd\u79bb\u8499\u9634\u4e94\u5341\u91cc\u7684\u4e00\u4e2a\u5c0f\u9547\u6b47\u5bbf\uff0c\u6709\u51e0\u4e2a\u53eb\u5316\u5b50\u80fd\u6765\u4e5e\u8ba8\uff0c\u88ab\u9646\u7ba1\u5e26\u53eb\u5b98\u519b\u6253 \u4e86\u4e00\u987f\uff0c\u9a71\u9010\u51fa\u53bb\uff0c\u90a3\u51e0\u4e2a\u4e5e\u4e10\uff0c\u4e34\u8d70\u4e4b\u65f6\u5374\u54c8\u54c8\u5927\u7b11\uff0c\u6a0a\u82f1\u4fbf\u77e5\u4e8b\u60c5\u4e0d\u5999\uff0c\u679c\u7136\u7b2c\u4e8c\u65e5\u5230 \u4e86\u6cf0\u5c71\u4e4b\u5357\uff0c\u5ffd\u542c\u4e00\u58f0\u7c97\u65f7\u7684\u5927\u7b11\uff0c\u4e00\u7fa4\u5f3a\u76d7\u6d8c\u4e86\u51fa\u6765\uff0c\u5f53\u524d\u7684\u5c31\u662f\u90a3\u51e0\u4e2a\u53eb\u5316\u5b50\uff0c\u7eb5\u9a6c\u4e00 \u51b2\uff0c\u7acb\u523b\u628a\u5b98\u519b\u7684\u961f\u5f62\u51b2\u4e71\u3002 \u3000\u3000\u6a0a\u82f1\u8fd8\u6765\u4e0d\u53ca\u5957\u6c5f\u6e56\u4e0a\u7684\u4ea4\u60c5\uff0c\u90a3\u51e0\u4e2a\u53eb\u5316\u5b50\u5df2\u5c06\u4e8e\u3001\u9646\u4e24\u4e2a\u519b\u5b98\u6253\u5012\uff0c\u6a0a\u82f1\u903c\u5f97\u51fa \u624b\uff0c\u5c06\u4e24\u4e2a\u4e5e\u4e10\u6253\u4f24\uff0c\u5ffd\u542c\u5f97\u90a3\u7c97\u65f7\u7684\u7b11\u58f0\u9707\u8033\u6b32\u804b\uff0c\u53ea\u89c1\u4e00\u4e2a\u8499\u9762\u5f3a\u76d7\uff0c\u7eb5\u9a6c\u5982\u98ce\uff0c\u624b\u8d77 \u6367\u843d\uff0c\u7acb\u523b\u5c06\u4e00\u4e2a\u519b\u5b98\u6253\u5f97\u8111\u6d46\u8ff8\u51fa\uff0c\u4e8e\u3001\u9646\u4e24\u4e2a\u519b\u5b98\u6b66\u827a\u8f83\u9ad8\uff0c\u53c8\u89c1\u673a\u5f97\u5feb\uff0c\u7acb\u523b\u4fbf\u9003\uff0c \u9976\u662f\u5982\u6b64\uff0c\u80a9\u5934\u4e0a\u4e5f\u90fd\u5403\u4e86\u4e00\u68d2\uff0c\u6a0a\u82f1\u6325\u65a7\u529b\u6218\uff0c\u63a5\u4e86\u90a3\u5f3a\u76d7\u4e09\u5341\u591a\u62db\uff0c\u90a3\u5f3a\u76d7\u624b\u4e2d\u7684\u6746\u68d2 \u4e5f\u4e0d\u77e5\u662f\u4ec0\u4e48\u505a\u7684\uff0c\u6a0a\u82f1\u7528\u767e\u70bc\u7cbe\u94a2\u6240\u94f8\u7684\u5ba3\u82b1\u5927\u65a7\uff0c\u78b0\u7740\u6746\u68d2\u5c31\u53d1\u51fa\u5982\u5de8\u9524\u51fb\u949f\u7684\u8f70\u8f70\u4e4b \u58f0\uff0c\u63a5\u4e86\u4e09\u5341\u591a\u62db\uff0c\u5ba3\u82b1\u5927\u65a7\u7684\u65a7\u53e3\u90fd\u5012\u5377\u4e86\uff0c\u90a3\u5f3a\u76d7\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4e5f\u7b97\u5f97\u662f\u6761\u597d \u6c49\uff0c\u8d70\u5427\uff01\u201d\u53ea\u89c1\u4ed6\u4e00\u63d0\u9a6c\u7f30\uff0c\u5de8\u68d2\u7167\u7740\u88c5\u8fd0\u5b98\u94f6\u7684\u94c1\u7532\u8f66\u4e71\u6253\uff0c\u51e0\u5bf8\u539a\u7684\u94c1\u76ae\uff0c\u4e5f\u4e0d\u8fc7\u6328 \u4e86\u4e09\u68d2\u4fbf\u90fd\u88c2\u5f00\uff0c\u4ed6\u8fde\u788e\u4e09\u8f86\u94f6\u8f66\uff0c\u6307\u6325\u7fa4\u76d7\uff0c\u5c06\u91cc\u9762\u7684\u94f6\u9798\uff0c\u5168\u90e8\u9a7c\u4e0a\u9a6c\u80cc\u8fd0\u8d70\u3002\u90a3\u4e94\u767e \u519b\u5b98\uff0c\u6253\u6b7b\u7684\u5360\u5341\u4e4b\u516d\u4e03\uff0c\u6253\u4f24\u7684\u5360\u5341\u4e4b\u4e00\u4e8c\uff0c\u8fd8\u6709\u4e00\u4e9b\u6700\u7cbe\u58ee\u7684\u5168\u7ed9\u7fa4\u76d7\u864f\u53bb\u3002\u53ea\u6709\u6a0a\u82f1 \u548c\u4e8e\u3001\u9646\u4e24\u4e2a\u519b\u5b98\u80fd\u591f\u9003\u751f\u3002\u90a3\u8499\u9762\u5927\u76d7\u7c97\u65f7\u7684\u7b11\u58f0\uff0c\u624b\u8d77\u68d2\u843d\u7684\u5a01\u731b\u59ff\u6001\uff0c\u4e0d\u4f46\u4ee4\u5f97\u90a3\u4e24 \u4e2a\u519b\u5b98\u8fd9\u51e0\u5929\u6765\u5e38\u5728\u68a6\u4e2d\u60ca\u9192\uff0c\u5373\u6a0a\u82f1\u60f3\u8d77\uff0c\u4e5f\u89c9\u5fc3\u60b8\u3002 \u3000\u3000\u8fd9\u8499\u9762\u5927\u76d7\u7684\u6765\u5386\uff0c\u6a0a\u82f1\u5168\u7136\u4e0d\u77e5\uff0c\u601d\u91cf\u518d\u4e09\uff0c\u53ea\u6709\u5f20\u98ce\u5e9c\u53ef\u4ee5\u5c06\u4ed6\u5236\u4f0f\uff0c\u53ef\u662f\u5f20\u98ce\u5e9c \u5374\u5ffd\u7136\u5931\u8e2a\uff0c\u800c\u5c0f\u864e\u5b50\u7adf\u628a\u4ed6\u4eec\u9501\u5728\u8fd9\u4e2a\u77f3\u5ba4\u4e4b\u5185\uff01 \u3000\u3000\u6a0a\u82f1\u6b63\u5728\u95ed\u76ee\u9050\u601d\uff0c\u5ffd\u542c\u5f97\u90a3\u4e24\u4e2a\u519b\u5b98\u9053\uff1a\u201c\u90a3\u5c0f\u3001\u5c0f\u3001\u5c0f\u987d\u7ae5\u8fd8\u6ca1\u6709\u56de\u6765\uff0c\u54b1\u4eec\u53ef\u8981 \u997f\u6b7b\u5566\uff01\u201d\u4ed6\u4eec\u672c\u6765\u60f3\u9a82\u201c\u5c0f\u86ee\u725b\u201d\u201c\u5c0f\u5f3a\u76d7\u201d\u7684\uff0c\u8bdd\u5230\u53e3\u8fb9\uff0c\u5374\u6539\u79f0\u4e86\u201c\u5c0f\u987d\u7ae5\u201d\uff0c\u6a0a\u82f1 \u7981\u4e0d\u4f4f\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u7741\u773c\u4e00\u77a7\uff0c\u4f46\u89c1\u5ba4\u4e2d\u6f06\u9ed1\uff0c\u5899\u58c1\u4e0a\u7684\u6c14\u5b54\u900f\u8fdb\u4e00\u4e1d\u4eae\u5149\uff0c\u60f3\u6765\u5916\u9762\u7684 \u5929\u8272\u5df2\u9ed1\u4e86\uff0c\u6a0a\u82f1\u4e5f\u89c9\u8179\u4e2d\u6709\u4e9b\u9965\u997f\uff0c\u53ea\u597d\u9759\u5750\u8fd0\u6c14\uff0c\u4e0d\u53bb\u60f3\u5b83\u3002\u90a3\u4e24\u4e2a\u519b\u5b98\u53ef\u662f\u997f\u5f97\u809a\u4e2d \u5495\u5495\u4f5c\u54cd\uff0c\u867d\u7136\u4e0d\u6562\u518d\u9a82\uff0c\u5374\u662f\u4f4e\u58f0\u57cb\u6028\u3002 \u3000\u3000\u6a0a\u82f1\u5fc3\u4e2d\u7591\u56e2\u57c2\u585e\uff1a\u8fd9\u5c71\u6751\u80fd\u6709\u591a\u5927\uff1f\u5c0f\u864e\u5b50\u4e3a\u4ec0\u4e48\u6ca1\u6709\u627e\u5230\u4ed6\u7684\u7236\u4eb2\u56de\u6765\uff1f\u96be\u9053\u5f20\u98ce \u5e9c\u4e5f\u906d\u4e86\u610f\u5916\uff1f\u4e0d\uff0c\u4e0d\uff01\u5f20\u98ce\u5e9c\u5728\u767e\u4e07\u519b\u4e2d\u72b9\u81ea\u53ef\u4ee5\u8fdb\u51fa\u81ea\u5982\uff0c\u4ed6\u7edd\u4e0d\u4f1a\u906d\u4e86\u610f\u5916\uff0c\u4f46\u662f\u4ed6 \u4e3a\u4ec0\u4e48\u8fd8\u6ca1\u56de\u6765\u5462\uff1f \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u4f46\u89c9\u51c9\u610f\u8d8a\u6d53\uff0c\u60f3\u5df2\u662f\u591c\u6df1\u65f6\u5206\uff0c\u4e24\u4e2a\u519b\u5b98\u53c8\u997f\u53c8\u51b7\uff0c\u745f\u7f29\u5899 \u89d2\uff0c\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u6a0a\u5927\u54e5\uff0c\u6a0a\u5927\u54e5\uff01\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u59d3\u4e8e\u7684\u90a3\u4e2a\u519b\u5b98\u9053\uff1a\u201c\u4f60\u548c\u5f20\u5927 \u4eba\u7684\u4ea4\u60c5\u5230\u5e95\u5982\u4f55\uff1f\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u56db\u5e74\u524d\u6211\u66fe\u89c1\u8fc7\u4ed6\u3002\u201d\u4e24\u4e2a\u519b\u5b98\u53eb\u58f0\u201c\u82e6\u4e5f\uff01\u201d\u540c\u58f0\u57cb\u6028 \u9053\uff1a\u201c\u539f\u6765\u4f60\u548c\u4ed6\u4e0d\u662f\u6df1\u4ea4\uff0c\u53ea\u6015\u4ed6\u975e\u4f46\u4e0d\u80af\u51fa\u624b\u76f8\u52a9\uff0c\u8fd8\u8981\u5c06\u6211\u4eec\u5173\u5728\u8fd9\u91cc\u6d3b\u6d3b\u997f\u6b7b\u3002\u4f60 \u542c\u90a3\u5c0f\u3001\u5c0f\u3001\u5c0f\u987d\u7ae5\u7684\u53e3\u6c14\uff0c\u4ed6\u4e0d\u77e5\u4e3a\u4f55\u5982\u6b64\u6028\u6068\u671d\u5ef7\uff0c\u53ea\u6015\u4ed6\u7acb\u5fc3\u8981\u5c06\u6211\u4eec\u5f04\u6b7b\u4e86\u3002\u201d\u6a0a \u82f1\u53c8\u597d\u6c14\uff0c\u53c8\u597d\u7b11\uff0c\u9053\uff1a\u201c\u5f20\u5927\u4eba\u5149\u660e\u78ca\u843d\uff0c\u4ed6\u7eb5\u662f\u8981\u5f04\u6b7b\u4f60\u4eec\uff01\u4e5f\u4e0d\u7528\u4f7f\u8fd9\u5978\u8ba1\u3002\u201d\u4e24\u4e2a \u519b\u5b98\u66f4\u5413\u5f97\u624b\u98a4\u811a\u98a4\uff0c\u9053\uff1a\u201c\u90a3\u4f60\u662f\u8bf4\uff0c\u4ed6\u771f\u8981\u5f04\u6b7b\u6211\u4eec\u4e86\u3002\u201d\u6a0a\u82f1\u7b11\u9053\uff1a\u201c\u5728\u4ed6\u624b\u4e0b\u4e27\u751f \u7684\u90fd\u662f\u6210\u540d\u4e4b\u8f88\uff0c\u54b1\u4eec\u53ea\u6050\u8fd8\u6ca1\u6709\u8fd9\u4e2a\u8d44\u683c\u3002\u201d\u59d3\u9646\u7684\u90a3\u4e2a\u519b\u5b98\u9053\uff1a\u201c\u90a3\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u56de\u6765\u653e \u6211\u4eec\u51fa\u53bb\uff1f\u8fde\u90a3\u5c0f\u987d\u7ae5\u4e5f\u6ca1\u89c1\u56de\u6765\u3002\u201d\u6a0a\u82f1\u5fc3\u4e2d\u7126\u8e81\uff0c\u9053\uff1a\u201c\u4f60\u95ee\u6211\uff0c\u6211\u600e\u4e48\u77e5\u9053\uff1f\u201d\u4e24\u4e2a \u519b\u5b98\u6b63\u60f3\u8bf4\u8bdd\uff0c\u5ffd\u89c1\u5899\u4e0a\u7684\u6c14\u5b54\u900f\u8fdb\u4eae\u5149\uff0c\u4e09\u4eba\u7cbe\u795e\u4e00\u632f\uff0c\u5ffd\u542c\u5f97\u4e00\u9635\u6840\u6840\u7684\u602a\u7b11\uff0c\u7f42\u5ba4\u4e4b \u4e2d\uff0c\u5982\u95fb\u9b3c\u53eb\uff0c\u4e0d\u89c9\u6bdb\u9aa8\u609a\u7136\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u5664\u58f0\u4e0d\u6562\u8bf4\u8bdd\uff0c\u7b11\u58f0\u8fc7\u540e\uff0c\u4e00\u4e2a\u4eba\u8bf4\u9053\uff1a\u201c\u5f20\u5927 \u4eba\uff0c\u4f60\u9690\u5c45\u8fd9\u91cc\u4eab\u5f97\u597d\u6e05\u798f\u5475\uff0c\u53ea\u662f\u82e6\u4e86\u54b1\u4eec\u5144\u5f1f\u627e\u5bfb\u4e86\u3002\u201d\u6a0a\u82f1\u5fc3\u4e2d\u4e00\u51db\uff0c\u539f\u6765\u5f20\u98ce\u5e9c\u5df2 \u7ecf\u56de\u6765\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u7b11\u58f0\u548c\u8bf4\u8bdd\u600e\u4e48\u8fd9\u822c\u96be\u542c\uff1f\u96be\u9053\u662f\u5f20\u4e16\u4f2f\u7684\u4ec7\u5bb6\uff1f\u4ed6\u4e45\u5386\u6c5f\u6e56\uff0c\u6df1 \u77e5\u51f6\u9669\uff0c\u634f\u4e86\u90a3\u4e24\u4e2a\u519b\u5b98\u4e00\u628a\uff0c\u793a\u610f\u53eb\u4ed6\u4eec\u4e0d\u8981\u4f5c\u58f0\uff0c\u968f\u5373\u65bd\u5c55\u201c\u58c1\u864e\u6e38\u5899\u201d\u7684\u529f\u592b\uff0c\u9644\u5728 \u5899\u4e0a\uff0c\u773c\u775b\u8d34\u7740\u5899\u4e0a\u7684\u4e00\u4e2a\u6c14\u5b54\u3002 \u3000\u3000\u9694\u5ba4\u50cf\u662f\u4e00\u95f4\u4e66\u623f\uff0c\u5f53\u4e2d\u4e00\u5f20\u5706\u5f62\u7684\u77f3\u684c\uff0c\u5750\u7740\u4e09\u4eba\uff0c\u9762\u5411\u7740\u6a0a\u82f1\u7684\u6b63\u662f\u5f20\u98ce\u5e9c\uff0c\u8fd9\u65f6 \u4ed6\u5df2\u662f\u5e74\u8fc7\u4e94\u65ec\uff0c\u4f46\u5251\u7709\u864e\u76ee\uff0c\u4e0d\u6012\u81ea\u5a01\uff0c\u4ecd\u4f3c\u5f53\u5e74\u6a21\u6837\u3002\u5de6\u8fb9\u5750\u7684\u90a3\u4eba\uff0c\u4e00\u4e2a\u6597\u5927\u7684\u5934 \u9885\uff0c\u8eab\u8eaf\u5374\u751a\u77ee\u5c0f\uff0c\u751f\u6210\u4e00\u526f\u602a\u76f8\u3002\u53f3\u8fb9\u5750\u7684\u5374\u662f\u4e00\u5f20\u9634\u9633\u9762\uff0c\u4e24\u989d\u592a\u9633\u7a74\u7a81\u8d77\uff0c\u4e00\u770b\u4fbf\u77e5 \u662f\u5185\u529f\u7cbe\u6df1\u4e4b\u58eb\u3002\u77f3\u684c\u540e\u9762\u662f\u4e24\u5f20\u4e66\u6a71\uff0c\u6bd4\u4e00\u4e2a\u4eba\u8fd8\u8981\u9ad8\uff0c\u5f20\u98ce\u5e9c\u672c\u6765\u53ea\u662f\u7c97\u8bc6\u6587\u5b57\uff0c\u53ea\u56e0 \u53d7\u4e86\u5f20\u4e39\u67ab\u7684\u5f71\u54cd\uff0c\u5f52\u9690\u4e4b\u540e\uff0c\u5012\u8bfb\u4e86\u4e0d\u5c11\u8bd7\u4e66\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5f20\u98ce\u5e9c\u201c\u54fc\u201d\u4e86\u53e3\u58f0\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u4eba\u6709\u4f55\u89c1\u6559\uff1f\u201d\u90a3\u9634\u9633\u9762\u6c49\u8bf4\u9053\uff1a\u201c\u5f20\u5927\u4eba \u5f52\u9690\u516b\u5e74\uff0c\u7687\u4e0a\u53ef\u6302\u5ff5\u5f97\u7d27\u5475\uff01\u5144\u5f1f\u4e5f\u66fe\u5bfb\u8fc7\u4e09\u6b21\uff0c\u5374\u539f\u6765\u5f20\u5927\u4eba\u5728\u8fd9\u91cc\u7eb3\u798f\u3002\u5f20\u5927\u4eba\u73b0\u5728 \u662f\u65e0\u5b98\u4e00\u8eab\u8f7b\uff0c\u4f46\u65e2\u5df2\u4eab\u4e86\u516b\u5e74\u6e05\u798f\uff0c\u4f3c\u4e4e\u4e5f\u8be5\u4e3a\u7687\u4e0a\u5206\u5fe7\u624d\u662f\u3002\u201d\u5f20\u98ce\u5e9c\u53cc\u773c\u95ea\u95ea\u53d1\u5149\uff0c \u4f3c\u4e4e\u76f4\u53ef\u770b\u7a7f\u5bf9\u65b9\u7684\u80ba\u8151\uff0c\u90a3\u5927\u5934\u6c49\u5b50\u7b11\u563b\u563b\u5730\u5e2e\u8154\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u73b0\u5728\u6b63\u662f\u56fd\u5bb6\u591a\u4e8b\u4e4b \u79cb\uff0c\u7687\u4e0a\u95fb\u9f13\u58f0\u800c\u601d\u826f\u5c06\uff0c\u53ea\u6015\u4e0d\u80fd\u4efb\u7531\u5f20\u5927\u4eba\u900d\u9065\u81ea\u5728\u4e86\u3002\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u4eba\u4e4b\u8a00 \u5dee\u77e3\uff0c\u5f53\u4eca\u6ee1\u671d\u6587\u6b66\uff0c\u4eba\u6750\u6d4e\u6d4e\uff0c\u50cf\u4e24\u4f4d\u5927\u4eba\u5c31\u662f\u680b\u6881\u4e4b\u6750\uff0c\u60f3\u5f20\u67d0\u5e74\u7eaa\u8001\u8fc8\uff0c\u5c1a\u6709\u4f55\u80fd \u4e3a\uff0c\u6709\u52b3\u7687\u4e0a\u6302\u5ff5\uff1f\u800c\u4eca\u5929\u4e0b\u592a\u5e73\u65e0\u4e8b\uff0c\u74e6\u523a\u56fd\u4e2d\u5185\u4e71\uff0c\u4e5f\u5148\u65e9\u5df2\u88ab\u9664\uff0c\u7109\u5f97\u8c13\u4e3a\u2018\u591a\u4e8b\u4e4b \u79cb\u2019\uff1f\u4e24\u4f4d\u5927\u4eba\u6240\u8a00\uff0c\u6211\u5b9e\u5728\u4e0d\u660e\u5176\u610f\u3002\u201d\u53cc\u65b9\u8bf4\u8bdd\u5ba2\u6c14\u975e\u5e38\uff0c\u5176\u5b9e\u5374\u662f\u9488\u950b\u76f8\u5bf9\u3002 \u3000\u3000\u90a3\u9634\u9633\u9762\u6c49\u5b50\u5ffd\u5730\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u62ac\u5934\u8bf4\u9053\uff1a\u201c\u5f20\u5927\u4eba\uff0c\u54b1\u4eec\u90fd\u662f\u76f4\u80a0\u76f4\u809a\u7684\u6c49\u5b50\uff0c\u8bf4 \u8bdd\u4e0d\u5fc5\u6587\u7ec9\u7ec9\u5730\u515c\u5708\u5b50\u4e86\uff01\u4f60\u53ef\u77e5\u9053\u592a\u4e0a\u7687\u56fe\u8c0b\u590d\u8f9f\uff0c\u8fd1\u5e74\u7fbd\u6bdb\u6e10\u4e30\uff0c\u5df2\u7ed3\u6210\u4e86\u515a\u7fbd\u5417\uff1f\u201d \u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u6211\u5982\u4eca\u662f\u4e00\u4ecb\u5c71\u91ce\u5c0f\u6c11\uff0c\u4e45\u5df2\u4e0d\u95fb\u5916\u4e8b\uff0c\u7687\u5bb6\u5927\u4e8b\uff0c\u66f4\u4e0d\u6562\u8fc7\u95ee\u3002\u201d\u90a3\u9634\u9633\u9762 \u6c49\u5b50\u9053\uff1a\u201c\u6709\u8bf4\u5f20\u5927\u4eba\u5f53\u5e74\u6302\u51a0\u800c\u53bb\uff1b\u4e3a\u7684\u5c31\u662f\u7737\u604b\u6545\u4e3b\uff0c\u56e0\u6b64\u4e0d\u80af\u66ff\u5f53\u4eca\u7687\u4e0a\u5f53\u5dee\uff1f\u201d\u5f20 \u98ce\u5e9c\u624b\u6309\u5706\u684c\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\u82e5\u7136\u7591\u5fc3\u5f20\u67d0\uff0c\u5c3d\u53ef\u7528\u4e00\u7eb8\u8bcf\u4e66\u8d50\u6b7b\uff0c\u4f55\u52b3\u4e24\u4f4d\u660e\u67e5\u6697 \u8bbf\u3002\u201d\u5f20\u98ce\u5e9c\u60f3\u8d77\u524d\u671d\u5fe0\u81e3\u4e91\u9756\u88ab\u8d50\u6b7b\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u6fc0\u6124\uff0c\u8bf4\u5230\u540e\u6765\uff0c\u8bdd\u58f0\u9ad8\u4ea2\uff0c\u90a3\u9634\u9633\u9762\u6c49 \u5b50\u9053\uff1a\u201c\u5f20\u5927\u4eba\u8a00\u91cd\uff0c\u5f53\u4eca\u7687\u4e0a\uff0c\u6b63\u662f\u56e0\u4e3a\u5bf9\u4f60\u4fe1\u8d56\uff0c\u6240\u4ee5\u624d\u518d\u4e09\u53eb\u5144\u5f1f\u8bbf\u5bfb\uff0c\u8fd9\u662f\u5723\u4e0a\u6c42 \u5b9d\uff0c\u53ef\u4e0d\u7528\u8bf4\u662f\u4ec0\u4e48\u660e\u67e5\u6697\u8bbf\u5475\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u7eed\u9053\uff1a\u201c\u9002\u624d\u95fb\u7edf\u9886\u6240\u8bf4\u7684\u2018\u56fd\u5bb6\u591a\u4e8b\u4e4b \u79cb\u2019\u6240\u6307\u7684\u5e76\u4e0d\u662f\u756a\u90a6\u4f5c\u4e71\uff0c\u800c\u662f\u8981\u9632\u8427\u5899\u4e4b\u5185\uff0c\u592a\u4e0a\u7687\u7684\u4f5c\u4e71\u3002\u5f20\u5927\u4eba\uff0c\u4f60\u77a7\uff0c\u7687\u4e0a\u82e5\u7136 \u4e0d\u5c06\u4f60\u4ecd\u5f53\u4e3a\u5fc3\u8179\uff0c\u4ed6\u80af\u5c06\u8fd9\u4e9b\u8bdd\u90fd\u53eb\u5144\u5f1f\u8f6c\u544a\u4e8e\u4f60\uff1f\u201d\u5f20\u98ce\u5e9c\u538c\u70e6\u4e4b\u6781\uff0c\u7aef\u5750\u4e0d\u8a00\uff0c\u90a3\u5927 \u5934\u6c49\u5b50\u6447\u5934\u6446\u8111\u5730\u563b\u563b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ee5\u524d\u5f20\u5927\u4eba\u4e0d\u80af\u51fa\u5c71\uff0c\u5144\u5f1f\u4eec\u53ea\u597d\u6ee5\u828b\u5145\u6570\uff0c\u6b64\u6b21\u5f20\u5927 \u4eba\u590d\u51fa\uff0c\u6211\u4e0e\u6218\u8001\u5144\u53ef\u4ee5\u5378\u4e0b\u62c5\u5b50\uff0c\u4f55\u5e78\u5982\u4e4b\uff01\u5f20\u5927\u4eba\uff0c\u8fd9\u53ef\u7528\u4e0d\u7740\u5ba2\u6c14\u63a8\u8ba9\uff0c\u4f60\u77a7\uff0c\u8fd9\u662f \u7687\u4e0a\u7684\u5bc6\u8bcf\uff0c\u8bcf\u4e66\u4e0a\u5199\u5f97\u660e\u660e\u767d\u81ea\uff0c\u2018\u8457\u5f20\u98ce\u5e9c\u5b98\u590d\u539f\u4e8c\u804c\uff0c\u4efb\u5fa1\u6797\u519b\u7edf\u9886\u517c\u9526\u8863\u536b\u603b\u6307 \u6325\u3002\u2019\u5f20\u5927\u4eba\u4f60\u77a7\uff0c\u54b1\u5144\u5f1f\u4fe9\u53ef\u6709\u534a\u53e5\u8c0e\u8a00\uff1f\u7687\u4e0a\u5bf9\u4f60\uff0c\u53ef\u771f\u662f\u501a\u82e5\u957f\u57ce\uff0c\u6069\u5178\u5982\u5c71\u54ea\uff01\u201d \u3000\u3000\u6a0a\u82f1\u4e09\u4eba\u5728\u9694\u5899\u542c\u5f97\u9a87\u7136\uff0c\u5ba4\u4e2d\u8fd9\u4e24\u4e2a\u6c49\u5b50\u7adf\u7136\u662f\u4eac\u5e08\u7684\u5fa1\u6797\u519b\u7edf\u9886\u548c\u9526\u8863\u536b\u603b\u6307\u6325\uff0c \u90fd\u662f\u5f53\u4eca\u58f0\u540d\u6b63\u76db\u7684\u4e00\u7b49\u9ad8\u624b\uff0c\u90a3\u9634\u9633\u9762\u6c49\u5b50\u540d\u53eb\u6218\u4e09\u5c71\uff0c\u4ed6\u7ec3\u7684\u5206\u7b4b\u9519\u9aa8\u624b\u662f\u6b66\u6797\u4e00\u7edd\uff0c \u73b0\u5c45\u9526\u8863\u536b\u603b\u6307\u6325\u4e4b\u804c\uff0c\u521d\u5230\u4eac\u5e08\u4e4b\u65f6\uff0c\u66fe\u5728\u5fa1\u82d1\u6bd4\u6b66\uff0c\u4e00\u65e5\u4e4b\u95f4\uff0c\u8fde\u7528\u5206\u7b4b\u9519\u9aa8\u624b\u626d\u65ad\u5341 \u4e8c\u540d\u4e00\u7ea7\u6b66\u58eb\u7684\u81c2\u818a\uff0c\u540d\u9707\u4e00\u65f6\u3002\u90a3\u5927\u5934\u6c49\u5b50\u540d\u53eb\u95fb\u94c1\u58f0\uff0c\u522b\u770b\u4ed6\u6837\u5b50\u6ed1\u7a3d\uff0c\u624b\u5e95\u4e0b\u53ef\u771f\u6709 \u60ca\u4eba\u7684\u6280\u4e1a\uff0c\u4ed6\u7cbe\u4e8e\u4e94\u884c\u5251\uff0c\u80fd\u7528\u5251\u5c16\u523a\u7a74\uff0c\u53c8\u64c5\u6253\u6b79\u6bd2\u6697\u5668\uff0c\u8fd8\u6709\u4e00\u8eab\u72ec\u5230\u7684\u5317\u6d3e\u5730\u8eba\u62f3 \u7684\u529f\u592b\uff0c\u73b0\u5c45\u5fa1\u6797\u519b\u7edf\u9886\u4e4b\u804c\u3002\u5f53\u4eca\u7687\u4e0a\u7adf\u7136\u6d3e\u4ed6\u4eec\u4e24\u4e2a\u4e00\u540c\u51fa\u9a6c\uff0c\u529d\u5f20\u98ce\u5e9c\u56de\u671d\uff0c\u4ed6\u4e24\u4eba \u6240\u8bf4\u7684\u8bdd\uff0c\u60f3\u6765\u4e0d\u5047\u3002 \u3000\u3000\u53ea\u89c1\u5f20\u98ce\u5e9c\u9762\u8272\u4e00\u6c89\uff0c\u5f90\u5f90\u8bf4\u9053\uff1a\u201c\u8fd9\u8bcf\u4e66\u6211\u4e0d\u6562\u63a5\u3002\u201d\u95fb\u94c1\u58f0\u9053\uff1a\u201c\u5f20\u5927\u4eba\u8fd8\u5acc\u5b98\u5c0f \u4e48\uff1f\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u4e3a\u81e3\u5b50\u7684\u4e0d\u6562\u9022\u541b\u4e4b\u6076\uff0c\u800c\u5e94\u5bfc\u541b\u4e8e\u5584\uff0c\u8bf7\u95ee\u4e24\u4f4d\u5927\u4eba\uff0c\u5047\u5982\u4f60\u89c1\u4eba\u5bb6 \u9aa8\u8089\u76f8\u6b8b\uff0c\u624b\u8db3\u76f8\u4e89\uff0c\u4f60\u4eec\u662f\u529d\u963b\u7684\u5462\uff1f\u8fd8\u662f\u53bb\u717d\u98ce\u70b9\u706b\uff0c\u4e3a\u4ed6\u4eec\u52a9\u62f3\u5462\uff1f\u201d\u6218\u3001\u95fb\u4e8c\u4eba\u60f3 \u4e0d\u5230\u5f20\u51e4\u5e9c\u8bf4\u8bdd\u5982\u6b64\u5766\u7387\uff0c\u7adf\u7136\u76f4\u8bae\u7687\u4e0a\u4e4b\u975e\uff0c\u90fd\u4e0d\u89c9\u4e00\u6014\uff0c\u95fb\u94c1\u58f0\u5ffd\u5730\u7b11\u563b\u563b\u5730\u9053\uff1a\u201c\u60f3 \u4e0d\u5230\u5f20\u5927\u4eba\u7adf\u7136\u5f03\u6b66\u4fee\u6587\uff0c\u5b66\u4e86\u4e00\u5957\u8150\u5112\u7684\u53e3\u543b\u4e86\uff1f\u5f20\u5927\u4eba\uff0c\u4f60\u4f11\u602a\u6211\u76f4\u8bf4\uff0c\u4f60\u7684\u9ad8\u8bba\u53ef\u662f \u8fc2\u9614\u4e0d\u8fd1\u4eba\u60c5\u3002\u201d\u5f20\u98ce\u5e9c\u7ffb\u773c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u95fb\u94c1\u58f0\u9053\uff1a\u201c\u592a\u4e0a\u7687\u4e0e\u7687\u4e0a\u4e89\u4f4d\uff0c\u4f60\u6211\u5c82\u80fd\u529d \u963b\uff1f\u4e3a\u81e3\u5b50\u7684\u53ea\u80fd\u6548\u5fe0\u4e00\u4eba\uff0c\u5f20\u98ce\u5e9c\u4f60\u5230\u5e95\u8ba4\u8c01\u662f\u4f60\u7684\u4e3b\u5b50\uff1f\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u662f\u4e00\u4e2a\u5c71\u91ce\u5c0f\u6c11\uff0c\u54ea\u4e00\u4e2a\u7687\u5e1d\u767b\u57fa\u6211\u7167\u6837\u7eb3\u79df\u7f34\u7a0e\u3002\u201d\u95fb \u94c1\u58f0\u6414\u5934\u6293\u8111\uff0c\u4f5c\u51fa\u4e00\u4e2a\u65e0\u53ef\u5948\u4f55\u7684\u795e\u6c14\u9053\uff1a\u201c\u5f20\u5927\u4eba\u4f60\u5012\u8bf4\u5f97\u8f7b\u677e\uff0c\u53ef\u6559\u54b1\u4eec\u5144\u5f1f\u5982\u4f55\u590d \u547d\uff1f\u201d\u6218\u4e09\u5c71\u5ffd\u5730\u9634\u607b\u607b\u5730\u7b11\u9053\uff1a\u201c\u592a\u4e0a\u7687\u82e5\u662f\u590d\u8f9f\u6210\u529f\uff0c\u522b\u7684\u4e0d\u77e5\uff0c\u6709\u4e00\u4e2a\u4eba\u53ef\u662f\u96be\u9003\u6027 \u547d\uff01\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u8c01\uff1f\u201d\u6218\u4e09\u5c71\u9053\uff1a\u201c\u90a3\u81ea\u7136\u662f\u4e8e\u9601\u8001\u4e86\uff01\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u5927\u660e\u7684\u6c5f\u5c71\u9760 \u4e8e\u9601\u8001\u53ea\u624b\u633d\u56de\uff0c\u5929\u4e0b\u8c01\u4eba\u4e0d\u77e5\uff1f\u201d\u95fb\u94c1\u58f0\u563b\u563b\u7b11\u9053\uff1a\u201c\u5f53\u4eca\u4e3b\u4e0a\u662f\u4e8e\u8c26\u6240\u7acb\uff0c\u592a\u7687\u56e0\u6b64\u4e22 \u4e86\u7687\u4f4d\uff0c\u6b64\u4e8b\u53c8\u8c01\u4eba\u4e0d\u77e5\uff1f\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u90a3\u65f6\u592a\u4e0a\u7687\u8499\u5c18\u5f02\u56fd\uff0c\u56fd\u5bb6\u4e0d\u53ef\u4e00\u65e5\u65e0\u541b\uff0c\u4e8e\u9601 \u8001\u6240\u4e3a\uff0c\u56fd\u4eba\u7686\u8c05\u3002\u201d\u6218\u4e09\u5c71\u9634\u607b\u607b\u5730\u9053\uff1a\u201c\u53ef\u662f\u6709\u4e00\u4eba\u5fc5\u7136\u4e0d\u8c05\uff0c\u8fd9\u4e2a\u4eba\u5c31\u662f\u592a\u4e0a\u7687\uff01\u201d \u95fb\u94c1\u58f0\u4e5f\u7b11\u9053\uff1a\u201c\u5f20\u5927\u4eba\uff0c\u4f60\u5728\u8fd9\u513f\u66ff\u4e8e\u9601\u8001\u8fa9\u89e3\uff0c\u53ef\u662f\u6beb\u65e0\u7528\u5904\u3002\u9664\u975e\u4f60\u63a5\u4e86\u7687\u4e0a\u7684\u8bcf \u4e66\uff0c\u66ff\u7687\u4e0a\u6548\u5fe0\uff0c\u5236\u6b62\u592a\u4e0a\u7687\u7684\u590d\u8f9f\uff0c\u90a3\u624d\u80fd\u4fdd\u5f97\u4f4f\u4e8e\u8c26\u7684\u6027\u547d\u3002\u201d\u5f20\u98ce\u5e9c\u5185\u5fc3\u4ea4\u6218\uff0c\u9762\u8272 \u60e8\u767d\uff0c\u5fc3\u9053\uff1a\u201c\u4e8e\u9601\u90e8\u8001\u6210\u8c0b\u56fd\uff0c\u5929\u4e0b\u6240\u94a6\uff0c\u592a\u4e0a\u7687\u7eb5\u7136\u590d\u8f9f\u6210\u529f\uff0c\u4e5f\u672a\u5fc5\u6562\u5192\u5929\u4e0b\u4e4b\u5927\u4e0d \u97ea\uff0c\u5c06\u4ed6\u9664\u6389\u3002\u201d\u9661\u7136\u60f3\u8d77\u5f20\u4e39\u67ab\u6240\u8bf4\u7684\u8bdd\uff0c\u5f20\u4e39\u67ab\u662f\u5f53\u5e74\u548c\u4e91\u91cd\u4e00\u540c\u5230\u74e6\u523a\u53bb\u63a5\u592a\u4e0a\u7687\u56de \u56fd\u4e4b\u4eba\uff0c\u636e\u5f20\u4e39\u67ab\u4e4b\u89c1\uff0c\u592a\u4e0a\u7687\u5b9e\u662f\u5fd8\u6069\u8d1f\u4e49\u7684\u4eba\uff0c\u4ee5\u4eca\u665a\u6240\u95fb\uff0c\u5219\u5f53\u4eca\u7687\u4e0a\u4e5f\u662f\u5929\u6027\u51c9\u8584 \u4e4b\u8f88\u3002\u5f20\u98ce\u5e9c\u66fe\u5728\u5927\u5185\u591a\u5e74\uff0c\u6df1\u77e5\u7687\u5ba4\u7684\u5fc3\u72e0\u624b\u8fa3\uff0c\u8fd9\u65f6\u542c\u51fa\u4e24\u4eba\u7684\u53e3\u6c14\uff0c\u7adf\u7136\u4ee5\u4e8e\u8c26\u7684\u6027 \u547d\u4f5c\u4e3a\u8981\u631f\uff0c\u4e0d\u7981\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u5fc3\u4e2d\u8e0c\u8e87\u96be\u51b3\u3002 \u3000\u3000\u9634\u9633\u9762\u6218\u4e09\u5c71\u51b7\u51b7\u5730\u76ef\u4e86\u5f20\u98ce\u5e9c\u4e00\u773c\uff0c\u5c06\u8bcf\u4e66\u644a\u5728\u684c\u4e0a\uff0c\u9053\uff1a\u201c\u5f20\u5927\u4eba\uff0c\u4f60\u8fd8\u662f\u63a5\u4e86 \u5427\u3002\u201d\u5ffd\u89c1\u5f20\u98ce\u5e9c\u9762\u8272\u6709\u5f02\uff0c\u6218\u4e09\u5c71\u4fa7\u8033\u4e00\u542c\uff0c\u5f20\u98ce\u5e9c\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6211\u5012\u4ea4\u4e86\u8001\u8fd0\uff0c \u4e00\u665a\u4e4b\u4e2d\u7adf\u7136\u6709\u4e24\u62e8\u4eba\u6765\u76f8\u8bbf\u3002\u201d \u3000\u3000\u6a0a\u82f1\u5728\u9694\u5899\u6b63\u542c\u5f97\u51fa\u795e\uff0c\u5ffd\u89c1\u6218\u4e09\u5c71\u4e0e\u95fb\u94c1\u58f0\u4e00\u628a\u6293\u8d77\u8bcf\u4e66\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5f20\u5927\u4eba\uff0c\u4e3a \u7978\u4e3a\u798f\u90fd\u5168\u5728\u4f60\u4e00\u5ff5\u4e4b\u95f4\u4e86\u3002\u201d\u4e24\u4eba\u4e00\u4e2a\u8f6c\u8eab\uff0c\u85cf\u5230\u4e66\u6a71\u540e\u9762\uff0c\u6a0a\u82f1\u5927\u611f\u5947\u602a\uff0c\u53ea\u89c1\u5f20\u98ce\u5e9c \u6253\u5f00\u4e86\u95e8\uff0c\u5728\u5899\u89d2\u7684\u677e\u679d\u706b\u628a\u7167\u8000\u4e4b\u4e0b\uff0c\u9762\u8272\u663e\u5f97\u4efd\u5916\u9634\u6c89\uff0c\u5ffd\u542c\u5f97\u8f7b\u8f7b\u4e00\u54cd\uff0c\u95e8\u5916\u7a81\u7136\u8dc3 \u8fdb\u4e24\u4e2a\u4eba\u6765\uff0c\u4e00\u8eab\u9ed1\u8272\u7684\u6b66\u58eb\u670d\u9970\uff0c\u770b\u4ed6\u4f3c\u65cb\u98ce\u4e00\u6837\u7684\u5165\u95e8\u6765\uff0c\u90a3\u4e00\u8dc3\u4e00\u7eb5\u7684\u8eab\u6cd5\uff0c\u77eb\u6377\u4e4b \u6781\uff0c\u529f\u592b\u4e0d\u5728\u6218\u3001\u95fb\u4e8c\u4eba\u4e4b\u4e0b\u3002\u6a0a\u82f1\u5fc3\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6211\u7ec3\u4e86\u5341\u591a\u5e74\u7684\u63a5\u6697\u5668\u529f \u592b\uff0c\u6765\u4eba\u5230\u4e86\u95e8\u524d\uff0c\u8fd9\u624d\u53d1\u73b0\uff0c\u4e0d\u4f46\u8fdc\u8fdc\u4e0d\u5982\u5f20\u4e16\u4f2f\uff0c\u5373\u6218\u3001\u95fb\u4e8c\u4eba\u4e16\u6bd4\u6211\u5f3a\u5f97\u591a\u3002\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u8fce\u95e8\u4e00\u63d6\u3002\u53ea\u542c\u5f97\u6765\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8001\u670b\u53cb\u5566\uff0c\u8fd8\u62d8\u793c\u4e48\uff1f\u201d\u53e6\u4e00\u4eba\u5374\u9053\uff1a\u201c\u4e45 \u4ef0\u5f20\u5927\u4eba\u7684\u5a01\u540d\uff0c\u4eca\u65e5\u59cb\u6709\u7f18\u76f8\u4f1a\u3002\u201d\u6a0a\u82f1\u8d34\u7740\u5899\u5b54\uff0c\u5b9a\u775b\u4e00\u77a7\uff0c\u5148\u5165\u95e8\u7684\u90a3\u4eba\uff0c\u4e94\u77ed\u8eab \u6750\uff0c\u6837\u5b50\u5341\u5206\u7cbe\u608d\uff0c\u53ea\u89c1\u5f20\u98ce\u5e9c\u8bf4\u9053\uff1a\u201c\u9646\u5144\uff0c\u8fd9\u4f4d\u670b\u53cb\u662f\u8c01\uff1f\u8bf7\u6055\u4ffa\u773c\u62d9\uff0c\u8ba4\u4e0d\u51fa\u6765 \u4e86\u3002\u201d\u53e6\u5916\u90a3\u4eba\u4f53\u683c\u9b41\u68a7\uff0c\u4e0e\u4ed6\u7684\u540c\u4f34\u521a\u597d\u6210\u4e3a\u5bf9\u6bd4\uff0c\u53cc\u638c\u8f7b\u8f7b\u4e00\u62cd\uff0c\u9053\uff1a\u201c\u4ffa\u4e0e\u5c55\u9e4f\u5144\u662f \u591a\u5e74\u65e7\u53cb\uff0c\u4e0e\u5f20\u5927\u4eba\u5374\u662f\u521d\u4f1a\uff0c\u5c55\u9e4f\u5144\u60f3\u6765\u4e5f\u66fe\u9f7f\u53ca\u8d31\u540d\u3002\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u201c\u563f\u563f\u201d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u9739\u96f3\u624b\u7ae5\u4e09\u54e5\uff0c\u5728\u4e0b\u4e45\u4ef0\u4e86\u3002\u201d\u9694\u5899\u7684\u6a0a\u82f1\u53c8\u662f\u4e00 \u60ca\uff01\u8fd9\u4e24\u4eba\u7adf\u662f\u5927\u6709\u6765\u5934\uff0c\u90a3\u4e94\u77ed\u8eab\u6750\u7684\u7cbe\u608d\u6c49\u5b50\u540d\u53eb\u9646\u5c55\u9e4f\uff0c\u662f\u6b63\u7edf\u5e74\u95f4\u5927\u5185\u603b\u7ba1\u5eb7\u8d85\u6d77 \u7684\u5e08\u5f1f\uff0c\u6b63\u7edf\u5341\u4e09\u5e74\u90a3\u5e74\uff0c\u5f00\u8003\u8bd5\u6b66\u7279\u79d1\uff0c\u4ed6\u66fe\u51fb\u8d25\u65e0\u6570\u9ad8\u624b\uff0c\u6700\u540e\u5728\u64c2\u53f0\u4e4b\u4e0a\uff0c\u4e0e\u4e91\u91cd\u51b3 \u6218\uff0c\u4e89\u593a\u6b66\u72b6\u5143\uff08\u4e8b\u89c1\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\uff09\uff0c\u5927\u6218\u6570\u767e\u56de\u5408\uff0c\u4e0d\u5206\u80dc\u8d1f\uff0c\u540e\u6765\u4e8f\u4e86\u5f20\u4e39\u67ab\u7684\u6697 \u52a9\uff0c\u4e91\u91cd\u624d\u593a\u5f97\u6b66\u72b6\u5143\u3002\u9646\u5c55\u9e4f\u867d\u7136\u5931\u8d25\uff0c\u4f46\u4ea6\u56e0\u6b64\u800c\u626c\u540d\u56db\u6d77\uff0c\u540e\u6765\u88ab\u7687\u5e1d\u7948\u9547\u6536\u4e3a\u5927\u5185 \u536b\u58eb\uff0c\u7b97\u6765\u4e43\u662f\u5f20\u98ce\u5e9c\u7684\u540c\u50da\uff1b\u90a3\u9b41\u68a7\u7684\u5927\u6c49\u540d\u53eb\u7ae5\u5bb6\u9a8f\uff0c\u5728\u9646\u5c55\u9e4f\u672a\u5165\u7687\u5bab\u4e4b\u524d\uff0c\u4e24\u4eba\u662f \u5bf9\u8001\u642d\u6863\uff0c\u7eb5\u6a2a\u6c5f\u6dee\u9053\u4e0a\uff0c\u5e76\u9a7e\u9f50\u540d\uff0c\u53f7\u79f0\u201c\u6c5f\u6dee\u4e8c\u9738\u201d\uff0c\u4ed6\u7684\u6bd2\u7802\u638c\u517c\u6709\u91d1\u521a\u624b\u7684\u529f\u592b\uff0c \u5728\u9ed1\u9053\u4e0a\u662f\u4e2a\u6709\u540d\u7684\u6b79\u6bd2\u4eba\u7269\u3002 \u3000\u3000\u53ea\u542c\u5f97\u7ae5\u5bb6\u9a8f\u4e5f\u201c\u563f\u563f\u201d\u7b11\u9053\uff1a\u201c\u5f20\u5927\u4eba\uff0c\u54b1\u4eec\u4eca\u540e\u90fd\u662f\u4e00\u6bbf\u4e4b\u81e3\uff0c\u5144\u5f1f\u8fd8\u5f97\u8bf7\u5f20\u5927\u4eba \u591a\u591a\u63d0\u96bd\u7167\u987e\uff0c\u5144\u5f1f\u6b64\u6765\u53c2\u89c1\uff0c\u8fd9\u53a2\u6709\u793c\u4e86\uff01\u201d\u5f20\u98ce\u5e9c\u6014\u4e86\u4e00\u6014\uff0c\u95ea\u8fc7\u4e00\u8fb9\uff0c\u4e0d\u63a5\u4ed6\u8fd9\u4e00 \u793c\uff0c\u8be7\u7136\u95ee\u9053\uff1a\u201c\u7ae5\u5e08\u7236\uff0c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u9646\u5c55\u9e4f\u9053\uff1a\u201c\u7687\u4e0a\u5bc6\u8bcf\u5728\u6b64\uff0c\u8bf7\u5f20\u5927\u54e5\u63a5 \u8bcf\u3002\u201d\u6a0a\u82f1\u542c\u5f97\u83ab\u540d\u5176\u5999\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u4eec\u4e24\u4e2a\u4e5f\u6709\u5bc6\u8bcf\uff1f\u9002\u624d\u90a3\u6218\u4e09\u5c71\u4e0e\u95fb\u94c1\u58f0\u4e0d\u662f\u6765\u8fc7\u4e86 \u4e48\uff1f\u201d\u53ea\u89c1\u5f20\u98ce\u5e9c\u6367\u8d77\u8bcf\u4e66\uff0c\u606d\u606d\u656c\u656c\u5730\u62dc\u4e86\u4e09\u62dc\u9053\uff1a\u201c\u6055\u5f20\u67d0\u4e0d\u80fd\u63a5\u8bcf\uff0c\u6073\u6c42\u9646\u5144\u5728\u592a\u4e0a \u7687\u9762\u524d\u5584\u4e3a\u89e3\u91ca\u3002\u201d\u6a0a\u82f1\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u8fd9\u4e24\u4eba\u53e3\u4e2d\u6240\u79f0\u7684\u201c\u7687\u4e0a\u201d\uff0c\u4e0d\u662f\u5f53\u4eca\u7684\u5929\u5b50 \u7948\u94ae\uff0c\u800c\u662f\u6307\u88ab\u7948\u94ae\u8f6f\u7981\u5357\u5bab\u7684\u201c\u592a\u4e0a\u7687\u201d\u7948\u9547\u3002 \u3000\u3000\u9646\u5c55\u9e4f\u4f5c\u4e86\u4e00\u4e2a\u60ca\u8bb6\u7684\u795e\u60c5\uff0c\u9053\uff1a\u201c\u4e00\u65e5\u4e3a\u81e3\uff0c\u7ec8\u8eab\u662f\u4ec6\u3002\u5982\u4eca\u4e3b\u516c\u6709\u4e8b\uff0c\u4ec5\u8981\u5f20\u5144\u6276 \u52a9\uff0c\u62d2\u4e0d\u63a5\u8bcf\uff0c\u8fd9\u662f\u4e3a\u4f55\uff1f\u201d\u8981\u77e5\u53e4\u4ee3\u541b\u81e3\u4e4b\u793c\u6700\u4e25\uff0c\u5f20\u98ce\u5e9c\u662f\u7948\u9547\u7684\u65e7\u81e3\uff0c\u800c\u4e14\u662f\u5f53\u5e74\u8d1f \u6709\u4fdd\u62a4\u7948\u9547\u4e4b\u8d23\u7684\u9526\u8863\u536b\u603b\u6307\u6325\u517c\u5fa1\u6797\u519b\u7edf\u9886\uff0c\u6309\u7167\u5f53\u65f6\u7684\u793c\u6cd5\uff0c\u5f20\u51e4\u5e9c\u7eb5\u7136\u65e9\u5df2\u6302\u51a0\uff0c\u6545 \u4e3b\u6709\u547d\uff0c\u4ea6\u4e0d\u80fd\u4e0d\u63a5\u8bcf\u4e66\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u4e3b\u516c\u73b0\u5728\u662f\u5929\u4e0b\u81f3\u5c0a\uff0c\u53d7\u7687\u5e1d\u8c62\u517b\uff0c\u5c1a\u6709\u4f55\u4e8b\u4e0d\u8db3\uff0c\u8981\u52b3\u4e24\u4f4d\u591c\u987e\u8349 \u5e90\uff1f\u201d\u9646\u5c55\u9e4f\u51b7\u7b11\u9053\uff1a\u201c\u5f20\u5927\u4eba\u662f\u771f\u4e0d\u77e5\u8fd8\u662f\u5047\u4e0d\u77e5\uff0c\u8fd9\u7687\u4f4d\u672c\u6765\u662f\u54b1\u4eec\u4e3b\u516c\u7684\uff0c\u6210\u738b\uff08\u7948 \u94ae\u672a\u88ab\u4e8e\u8c26\u7acb\u4e3a\u7687\u5e1d\u4ee5\u524d\u7684\u201c\u5c01\u53f7\u201d\u3002\uff09\u62d2\u4e0d\u9000\u8ba9\uff0c\u9738\u5360\u5b9d\u5ea7\uff0c\u5f62\u540c\u7be1\u4f4d\uff0c\u5c06\u4e3b\u516c\u56da\u5728\u5357 \u5bab\uff0c\u662f\u53ef\u5fcd\u5b70\u4e0d\u53ef\u5fcd\uff1f\u54b1\u4eec\u66fe\u4e3a\u65e7\u81e3\u5b50\u7684\uff0c\u7406\u5f53\u52a9\u4e3b\u516c\u518d\u593a\u56de\u7687\u4f4d\uff0c\u90a3\u624d\u4e0d\u8d1f\u541b\u81e3\u4e4b\u4e49\u3002\u201d \u6a0a\u82f1\u5728\u9694\u5899\u4e5f\u542c\u5f97\u5927\u60ca\uff0c\u5fc3\u60f3\u5982\u6b64\u4e00\u6765\uff0c\u5bab\u5ef7\u4e4b\u5185\uff0c\u773c\u89c1\u53c8\u662f\u4e00\u573a\u5200\u5149\u5251\u5f71\uff0c\u53ea\u6015\u5144\u5f1f\u5185 \u8ba7\uff0c\u53c8\u6388\u5916\u654c\u4ee5\u53ef\u4e58\u4e4b\u673a\u4e86\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u4e00\u76b1\u7709\u5934\uff0c\u538c\u70e6\u4e4b\u6781\uff0c\u53ea\u89c9\u5f97\u4e3a\u4e00\u5bb6\u4e00\u59d3\u4e89\u6743\u593a\u4f4d\uff0c\u751a\u662f\u65e0\u804a\u3002\u4e8e\u662f\u8083\u5bb9\u8bf4\u9053\uff1a \u201c\u975e\u662f\u98ce\u5e9c\u6562\u5fd8\u4e86\u65e7\u65e5\u541b\u6069\uff0c\u5b9e\u662f\u4e0d\u6562\u8fc7\u95ee\u7687\u5bb6\u7684\u79c1\u4e8b\u3002\u201d\u7ae5\u5bb6\u9a8f\u201c\u563f\u563f\u201d\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u662f \u79c1\u4e8b\uff1f\u201d\u9646\u5c55\u9e4f\u5374\u628a\u8bcf\u4e66\u4e00\u5c55\uff0c\u9053\uff1a\u201c\u5f20\u5927\u4eba\u4f60\u4e14\u770b\u4e86\u8bcf\u4e66\u518d\u8bf4\u3002\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u59d1\u4e14\u4e00\u770b\uff0c\u53ea\u89c1\u8bcf\u4e66\u4e0a\u5199\u660e\u8d50\u4ed6\u201c\u5b98\u590d\u539f\u804c\u201d\uff0c\u5e76\u52a0\u5c01\u4e3a\u2018\u82f1\u6b66\u4f2f\u2019\uff0c\u8981\u4ed6\u7acb\u523b \u8fdb\u4eac\u201c\u965b\u89c1\u201d\uff0c\u5f20\u98ce\u5e9c\u5fc3\u4e2d\u6697\u7b11\u9053\uff1a\u201c\u539f\u6765\u4e5f\u662f\u4ee5\u5b98\u804c\u76f8\u8bf1\uff0c\u9664\u4e86\u52a0\u5c01\u4e3a\u82f1\u6b66\u4f2f\u4e4b\u5916\uff0c\u6240\u6388 \u7684\u5b98\u804c\u548c\u9002\u624d\u5bc6\u8bcf\uff0c\u5b8c\u5168\u4e00\u6837\u3002\u6211\u82e5\u60f3\u4e3a\u5bab\uff0c\u96be\u9053\u73b0\u949f\u4e0d\u6253\u53cd\u53bb\u70bc\u94dc\u5417\uff1f\u201d\u9646\u5c55\u9e4f\u9053\uff1a\u201c\u5f20 \u5927\u4eba\uff0c\u4f60\u53ef\u77a7\u6e05\u695a\u4e86\uff1f\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u591a\u8c22\u592a\u4e0a\u7687\u9686\u6069\uff0c\u5fae\u81e3\u4e0d\u6562\u63a5\u8bcf\u3002\u201d\u9646\u5c55\u9e4f\u9053\uff1a\u201c\u8fd8\u662f\u4e0d\u63a5\u5417\uff1f\u201d\u5f20\u98ce\u5e9c \u9053\uff1a\u201c\u671d\u5ef7\u81ea\u6709\u4f53\u5236\uff0c\u9526\u8863\u536b\u603b\u6307\u6325\u4e0e\u5fa1\u6797\u519b\u7edf\u9886\u90fd\u5df2\u6709\u4eba\uff0c\u98ce\u5e9c\u4e0d\u6562\u6311\u8d77\u5185\u4e71\u3002\u201d\u7ae5\u5bb6\u9a8f \u51b7\u7b11\u9053\uff1a\u201c\u5f20\u5927\u4eba\uff0c\u4f60\u771f\u4e2a\u77a7\u6e05\u695a\u4e86\uff1f\u201d\u5f20\u51e4\u5e9c\u89c1\u4ed6\u4eec\u8fde\u95ee\u4e09\u6b21\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u8be7\u9053\uff1a\u201c\u600e \u4e48\uff1f\u201d\u9646\u5c55\u9e4f\u51b7\u7b11\u9053\uff1a\u201c\u4e3b\u516c\u65e9\u5df2\u4e0d\u662f\u592a\u4e0a\u7687\u5566\uff0c\u5b9e\u544a\u8bc9\u4f60\uff0c\u4e3b\u516c\u6628\u65e5\u5df2\u53d7\u7fa4\u81e3\u62e5\u6234\uff0c\u518d\u51fa \u590d\u4f4d\u4e86\uff01\u201d\u5f20\u98ce\u5e9c\u5b9a\u4e00\u5b9a\u795e\uff0c\u6014\u6014\u5730\u671b\u7740\u9646\u5c55\u9e4f\u4e0e\u7ae5\u5bb6\u9a8f\uff0c\u9646\u5c55\u9e4f\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\u4e48\uff1f\u4f60\u5fc3\u4e2d \u5b9a\u662f\u60f3\u9053\uff0c\u4ece\u4eac\u57ce\u5230\u6b64\uff0c\u5feb\u9a6c\u4e5f\u5f97\u4e09\u5929\u3002\u6628\u65e5\u4e4b\u4e8b\uff0c\u54b1\u4eec\u5144\u5f1f\u5982\u4f55\u77e5\u9053\u5f97\u5982\u662f\u4e4b\u5feb\uff1f\u201d\u5f20\u98ce \u5e9c\u4e0e\u9694\u5899\u5077\u542c\u7684\u6a0a\u82f1\uff0c\u679c\u7136\u90fd\u662f\u5982\u6b64\u60f3\u6cd5\uff0c\u53ea\u542c\u5f97\u9646\u5c55\u9e4f\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u7687\u4e0a\u795e\u673a\u5999\u7b97\uff0c\u5c82\u662f \u4f60\u8f88\u5f97\u77e5\uff1f\u4ed6\u65e9\u5df2\u5e03\u7f6e\u5f97\u4e07\u65e0\u4e00\u5931\uff0c\u8fd9\u624d\u5dee\u9063\u6211\u7b49\u51fa\u4eac\u3002\u8981\u4e0d\u7136\u8bcf\u4e66\u4e0a\u5c82\u80fd\u5199\u660e\u4ee4\u4f60\u5b98\u590d\u65e7 \u804c\uff1f\u5f20\u98ce\u5e9c\uff0c\u4f60\u8fd8\u4e0d\u8dea\u4e0b\u63a5\u8bcf\u4e48\uff1f\u201d\u9694\u5899\u7684\u6a0a\u82f1\u542c\u5f97\u60ca\u9a87\u4e4b\u6781\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u592a\u4e0a\u7687\u7adf\u7136 \u5982\u6b64\u6bd2\u8fa3\uff01\u9002\u624d\u90a3\u4e24\u4eba\u4f20\u7687\u4e0a\u4e4b\u547d\u53ec\u5f20\u4e16\u4f2f\u201c\u52e4\u738b\u201d\uff0c\u660e\u660e\u662f\u6545\u610f\u8bd5\u63a2\uff0c\u770b\u5f20\u4e16\u4f2f\u613f\u5426\u6548\u5fe0 \u7684\u4e86\u3002\u201d \u3000\u3000\u7ae5\u3001\u9646\u4e8c\u4eba\u644a\u5f00\u8bcf\u4e66\uff0c\u76ee\u5149\u903c\u5c04\uff0c\u9759\u5f85\u5f20\u98ce\u5e9c\u56de\u7b54\u3002\u53ea\u89c1\u5f20\u98ce\u5e9c\u5446\u82e5\u6728\u9e21\uff0c\u4e00\u526f\u5931\u9b42\u843d \u9b44\u7684\u6837\u5b50\uff0c\u9646\u5c55\u9e4f\u5fc3\u4e2d\u6697\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u6709\u5bb3\u6015\u7684\u65f6\u5019\uff1f\u201d\u5ffd\u542c\u5f97\u5f20\u98ce\u5e9c\u51b2\u53e3\u95ee\u9053\uff1a\u201c\u4e8e \u9601\u8001\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u9646\u5c55\u9e4f\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u201c\u563f\u563f\u201d\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u5fc3\u76ee\u4e4b\u4e2d\uff0c\u5c31\u53ea\u6709\u4e00\u4e2a\u4e8e\u8c26\u3002\u201d\u4e0e\u7ae5 \u5bb6\u9a8f\u4ea4\u6362\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u9053\uff1a\u201c\u8fd9\u4e8b\u4f60\u4eb2\u81ea\u53bb\u95ee\u7687\u4e0a\u5427\u3002\u6211\u4eec\u53ea\u662f\u95ee\u4f60\uff0c\u4f60\u5230\u5e95\u63a5\u8bcf\u4e66\u8fd8\u662f\u4e0d \u63a5\uff1f\u201d\u5f20\u98ce\u5e9c\u6602\u5934\u5411\u5929\uff0c\u9053\uff1a\u201c\u4e0d\u63a5\uff01\u201d\u9646\u5c55\u9e4f\u9053\uff1a\u201c\u5f20\u5927\u54e5\u679c\u7136\u662f\u8bf4\u4e00\u4e0d\u4e8c\u7684\u786c\u6c49\u5b50\u3002\u9752 \u5c71\u7eff\u6c34\uff0c\u76f8\u89c1\u65e0\u671f\uff0c\u54b1\u4eec\u5144\u5f1f\u8d70\u4e86\uff0c\u4f60\u597d\u597d\u4fdd\u91cd\u5475\uff01\u201d\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u751a\u4f3c\u597d\u53cb\u8bc0\u522b\u4e4b\u8a00\uff0c\u5f20 \u98ce\u5e9c\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u9646\u5c55\u9e4f\u4e0e\u6211\u7d20\u6765\u4e0d\u5408\uff0c\u539f\u6765\u4ed6\u5374\u4e5f\u662f\u6027\u60c5\u4e2d\u4eba\u3002\u201d\u53ea\u89c1\u9646\u5c55\u9e4f\u5c06 \u8bcf\u4e66\u6162\u6162\u5377\u8d77\uff0c\u5f20\u98ce\u5e9c\u773c\u7736\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u9646\u5144\uff0c\u62dc\u6258\u4f60\u66ff\u6211\u95ee\u5019\u4e8e\u5927\u4eba\u3002\u5728\u7687\u4e0a\u8ddf\u524d\uff0c\u66ff\u4e8e \u5927\u4eba\u7f8e\u8a00\u4e24\u53e5\u3002\u201d\u9646\u5c55\u9e4f\u62f1\u624b\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u3002\u201d\u5c31\u5728\u5f20\u98ce\u5e9c\u4e0e\u9646\u5c55\u9e4f\u4e92\u76f8\u63d6\u522b\u4e4b\u65f6\uff0c\u7ae5\u5bb6 \u9a8f\u7a81\u7136\u547c\u7684\u4e00\u638c\uff0c\u62cd\u5728\u5f20\u98ce\u5e9c\u80a9\u4e0a\u3002\u539f\u6765\u4ed6\u4eec\u4fe9\u65e9\u5df2\u53d7\u4e86\u7948\u9547\u7684\u5bc6\u4ee4\uff1a\u5f20\u98ce\u5e9c\u82e5\u7136\u4e0d\u80af\u63a5\u8bcf \u6548\u5fe0\uff0c\u5c31\u5f97\u7acb\u523b\u5c06\u4ed6\u5904\u6b7b\uff01 \u3000\u3000\u53ea\u542c\u5f97\u201c\u5495\u549a\u2019\u4e00\u58f0\uff0c\u5f20\u98ce\u5e9c\u80a9\u5934\u4e00\u649e\uff0c\u5c06\u7ae5\u5bb6\u9a8f\u629b\u51fa\u95e8\u5916\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u65e0\u803b\u5c0f\u4eba\uff0c \u6562\u65bd\u6697\u7b97\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u9646\u5c55\u9e4f\u5df2\u4eae\u51fa\u4e86\u4ed6\u7684\u5947\u95e8\u5175\u5668\u91d1\u4e1d\u8f6f\u97ad\uff0c\u5530\u7684\u4e00\u97ad\uff0c\u5411\u5f20\u98ce\u5e9c\u80a9\u5934 \u75be\u626b\uff01\u6b63\u662f\uff1a \u3000\u3000\u5f52\u9690\u5c71\u6751\u96be\u907f\u7978\uff0c\u6df1\u5bb5\u53c8\u89c1\u5251\u5149\u5bd2\u3002 \u3000\u3000\u6b32\u77e5\u5f20\u98ce\u5e9c\u6027\u547d\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u56de\u3000\u5251\u5f71\u5200\u5149\u3000\u597d\u4eba\u620f\u4e49\u58eb\u3000\u5929\u6101\u5730\u6697\u3000\u7687\u5ba4\u6740\u5fe0\u81e3 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e8c\u56de\u3000\u5251\u5f71\u5200\u5149\u3000\u597d\u4eba\u620f\u4e49\u58eb\u3000\u5929\u6101\u5730\u6697\u3000\u7687\u5ba4\u6740\u5fe0\u81e3 \u3000\u3000\u6a0a\u82f1\u5728\u9694\u5899\u770b\u5f97\u8840\u8109\u7d27\u5f20\uff0c\u6068\u4e0d\u5f97\u8fc7\u53bb\u76f8\u52a9\uff0c\u53ea\u89c1\u90a3\u7ae5\u5bb6\u9a8f\u5728\u5730\u4e0a\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\u8df3 \u4e86\u8d77\u6765\uff0c\u201c\u55e4\uff0c\u55e4\uff0c\u55e4\u201d\u58f0\u5982\u7092\u8c46\uff0c\u53d1\u51fa\u6b79\u6bd2\u7684\u6697\u5668\u201c\u4e94\u6bd2\u9488\u201d\uff0c\u9762\u8272\u72f0\u72de\uff0c\u5389\u58f0\u9a82\u9053\uff1a \u201c\u5f20\u98ce\u5e9c\uff0c\u9976\u4f60\u6709\u901a\u5929\u672c\u9886\uff0c\u4eca\u665a\u4e5f\u96be\u9003\u6027\u547d\uff01\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u5de6\u624b\u4e00\u538b\u97ad\u68a2\uff0c\u53f3\u624b\u53cd\u8896\u4e00\u4f5b\uff0c\u5c06\u5341\u51e0\u679a\u4e94\u6bd2\u9488\u90fd\u62c2\u5f97\u53cd\u5c04\u56de\u53bb\uff0c\u9646\u5c55\u9e4f\u7684\u8f6f\u97ad \u662f\u7528\u91d1\u4e1d\u7f20\u4e0a\u864e\u7b4b\u518d\u7ed5\u4e0a\u5343\u5e74\u5c71\u85e4\uff0c\u575a\u97e7\u975e\u5e38\uff0c\u88ab\u5f20\u98ce\u5e9c\u4e00\u538b\u4e00\u626f\uff0c\u8f6f\u97ad\u4e0d\u65ad\uff0c\u9646\u5c55\u9e4f\u864e\u53e3 \u5374\u5df2\u6d41\u8840\uff0c\u5ffd\u542c\u5f97\u201c\u55e4\u55e4\u201d\u58f0\u54cd\uff0c\u6025\u5fd9\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\u75be\u907f\u4e4b\u65f6\uff0c\u80a9\u818a\u4e0a\u5df2\u88ab\u4e00\u679a\u4e94\u6bd2\u9488\u900f \u9aa8\u7a7f\u8fc7\uff01 \u3000\u3000\u9646\u5c55\u9e4f\u5927\u5403\u4e00\u60ca\uff0c\u60f3\u4e0d\u5230\u516b\u5e74\u4e0d\u89c1\uff0c\u5f20\u98ce\u5e9c\u529f\u5200\u53c8\u5f3a\u4e86\u4e00\u500d\uff0c\u7ae5\u5bb6\u9a8f\u5927\u53eb\u9053\uff1a\u201c\u9646\u5144\uff0c \u5e76\u80a9\u5b50\u4e0a\u5475\uff01\u8fd9\u53ae\u4e2d\u4e86\u6211\u7684\u6bd2\u638c\uff0c\u54b1\u4eec\u7f20\u6b7b\u4ed6\uff01\u201d\u5f20\u98ce\u5e9c\u9661\u89c9\u80a9\u4e0a\u9ebb\u6728\uff0c\u624b\u81c2\u4e0d\u7075\uff0c\u6025\u5fd9\u8fd0 \u4e00\u53e3\u6c14\uff0c\u963b\u6b62\u6bd2\u6c14\u4e0a\u884c\uff0c\u7ae5\u5bb6\u9a8f\u4e00\u4e2a\u864e\u8df3\uff0c\u5de6\u81c2\u4e00\u5708\uff0c\u53f3\u638c\u5e73\u8212\uff0c\u201c\u5413\u201d\u7684\u4e00\u58f0\uff0c\u53c8\u662f\u4e00\u4e0b \u6bd2\u638c\uff0c\u5f20\u98ce\u5e9c\u4f55\u7b49\u6837\u4eba\uff0c\u8fd9\u6b21\u7109\u80fd\u7ed9\u4ed6\u6253\u4e2d\u3002\u6545\u610f\u5356\u4e2a\u7834\u7efd\uff0c\u8ba9\u4ed6\u6b3a\u8fd1\u8eab\u8fb9\uff0c\u9661\u7684\u53cd\u624b\u4e00 \u638c\uff0c\u7ae5\u5bb6\u9a8f\u6025\u5fd9\u7f29\u6b65\uff0c\u5374\u5df2\u88ab\u638c\u950b\u626b\u4e2d\u624b\u8155\uff0c\u767b\u65f6\u8d77\u4e86\u4e94\u9053\u7ea2\u5370\uff0c\u624b\u8155\u540a\u4e86\u4e0b\u6765\u3002\u9646\u5c55\u9e4f\u75be \u626b\u4e09\u97ad\uff0c\u56de\u8eab\u6b32\u8d70\uff0c\u7ae5\u5bb6\u9a8f\u9053\uff1a\u201c\u4e0d\u80fd\u8ba9\u4ed6\u6709\u5598\u606f\u7684\u673a\u4f1a\uff0c\u4eca\u65e5\u4ed6\u82e5\u7136\u4e0d\u6b7b\uff0c\u54b1\u4eec\u5144\u5f1f\u65e5\u540e \u4e5f\u96be\u9003\u6027\u547d\uff01\u201d\u968f\u5373\u5c06\u4e24\u9897\u836f\u4e38\u4e00\u5f39\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u89e3\u836f\uff0c\u4f60\u5feb\u63a5\u7740\uff01\u201d\u5f20\u98ce\u5e9c\u4e00\u4e2a\u864e\u543c\uff0c\u9661 \u5730\u98de\u8eab\u8dc3\u8d77\uff0c\u53f3\u638c\u659c\u659c\u5288\u4e0b\uff0c\u5de6\u624b\u4e00\u6321\uff0c\u7ae5\u5bb6\u9a8f\u53cc\u62f3\u4e00\u67b6\uff0c\u9646\u5c55\u9e4f\u8f6f\u97ad\u4e00\u626b\uff0c\u582a\u582a\u62b5\u654c\u5f97 \u4f4f\uff0c\u4f46\u90a3\u89e3\u836f\u5df2\u7ed9\u4ed6\u62a2\u53bb\u4e00\u9897\u3002 \u3000\u3000\u9646\u5c55\u9e4f\u4e2d\u4e86\u4e00\u679a\u4e94\u6bd2\u9488\uff0c\u81c2\u818a\u6b63\u81ea\u53d1\u9ebb\uff0c\u6025\u5c06\u89e3\u836f\u670d\u4e0b\uff0c\u53ea\u89c1\u5f20\u98ce\u5e9c\u4e5f\u541e\u4e0b\u4e86\u89e3\u836f\uff0c\u7adf \u7136\u5835\u4f4f\u4e86\u4ed6\u4eec\u7684\u9000\u8def\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u56e0\u4f55\u6697\u7b97\u4e8e\u6211\uff0c\u5feb\u8bf4\u51fa\u4e2a\u9053\u7406\u6765\uff0c\u8981\u4e0d\u7136\u53eb\u4f60 \u7b49\u96be\u9003\u516c\u9053\uff01\u201d\u9646\u5c55\u9e4f\u5413\u5f97\u9762\u9752\u5507\u767d\uff0c\u53ea\u89c1\u7ae5\u5bb6\u9a8f\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u539f\u6765\u662f\u4ed6\u628a\u8131\u4e86\u81fc\u7684\u624b\u8155 \u5f3a\u81ea\u63a5\u4e0a\uff0c\u75db\u5f97\u6c57\u51fa\u5982\u6d46\uff0c\u9646\u5c55\u9e4f\u76ee\u5149\u95ea\u70c1\uff0c\u793a\u610f\u53eb\u7ae5\u5bb6\u9a8f\u9000\u540e\uff0c\u4fbf\u60f3\u593a\u95e8\u9003\u547d\uff0c\u7ae5\u5bb6\u9a8f\u53eb \u9053\uff1a\u201c\u9646\u5144\uff0c\u54b1\u4eec\u4e07\u4e07\u653e\u677e\u4e0d\u5f97\uff0c\u5b81\u53ef\u4e09\u4eba\u90fd\u6b7b\uff0c\u4e0d\u80fd\u53eb\u4ed6\u72ec\u751f\uff01\u8fd9\u89e3\u836f\u662f\u4e94\u6bd2\u9488\u7684\u89e3\u836f\uff0c \u5bf9\u6bd2\u7802\u638c\u53ef\u662f\u4e0d\u80fd\u6d4e\u4e8b\uff0c\u54b1\u4eec\u7f20\u6b7b\u4ed6\uff01\u201d\u9646\u5c55\u9e4f\u6df1\u77e5\u5f20\u98ce\u5e9c\u7684\u5389\u5bb3\uff0c\u56de\u5fc3\u4e00\u60f3\uff0c\u82e5\u662f\u73b0\u5728\u9003 \u8d70\uff0c\u7eb5\u7136\u6682\u65f6\u80fd\u593a\u95e8\u5954\u547d\uff0c\u4f46\u5bb9\u5f97\u5f20\u98ce\u5e9c\u81ea\u5df1\u4ece\u5bb9\u7597\u6cbb\uff0c\u4ee5\u4ed6\u6df1\u6e5b\u7684\u5185\u529f\uff0c\u4e0d\u51fa\u5341\u65e5\uff0c\u5b9a\u80fd \u590d\u539f\uff0c\u90a3\u65f6\u4ed6\u80fd\u6765\u5bfb\u4ec7\u7d22\u547d\uff0c\u81ea\u5df1\u4e0e\u7ae5\u5bb6\u9a8f\u90fd\u662f\u51c6\u6b7b\u65e0\u7591\uff0c\u5012\u4e0d\u5982\u7167\u7ae5\u5bb6\u9a8f\u6240\u8bf4\uff0c\u6700\u591a\u4e0e\u4ed6 \u4e09\u4eba\u4e00\u9f50\u6218\u6b7b\uff01 \u3000\u3000\u7ae5\u5bb6\u9a8f\u7684\u6bd2\u7802\u638c\u4e0e\u4e94\u6bd2\u9488\uff0c\u867d\u7136\u90fd\u662f\u7528\u540c\u6837\u7684\u6bd2\u836f\u71ac\u6c41\u6240\u70bc\uff0c\u4f46\u529f\u529b\u5374\u81ea\u4e0d\u540c\uff0c\u6bd2\u9488\u7ec6 \u5c0f\uff0c\u4e13\u6253\u7a74\u9053\uff0c\u6bd2\u638c\u56e0\u5939\u6709\u91d1\u521a\u638c\u529b\uff0c\u5374\u53ef\u4ee5\u4ee4\u654c\u4eba\u540c\u65f6\u5185\u5916\u53d7\u4f24\uff0c\u800c\u4e14\u624b\u638c\u7684\u9762\u79ef\u6bd4\u6bd2\u9488 \u5927\u6570\u5341\u767e\u500d\uff0c\u6bd2\u529b\u81ea\u662f\u5389\u5bb3\u5f97\u591a\uff0c\u5f20\u51e4\u5e9c\u867d\u541e\u4e0b\u89e3\u836f\uff0c\u676f\u6c34\u8f66\u85aa\uff0c\u65e0\u6d4e\u4e8e\u4e8b\uff0c\u867d\u4ed7\u7740\u4e00\u80a1\u771f \u5143\u4e4b\u6c14\uff0c\u62a4\u7740\u5fc3\u5934\uff0c\u5e76\u7aed\u529b\u963b\u904f\u6bd2\u529b\u53d1\u4f5c\uff0c\u4f46\u529f\u592b\u5374\u56e0\u6b64\u53d7\u4e86\u5f71\u54cd\uff0c\u7ae5\u3001\u9646\u4e8c\u4eba\u4ee5\u4e8c\u654c\u4e00\uff0c \u867d\u7136\u8fd8\u662f\u5904\u5728\u4e0b\u98ce\uff0c\u5f20\u98ce\u5e9c\u4ea6\u5403\u529b\u975e\u5e38\u3002 \u3000\u3000\u500f\u5ffd\u4e4b\u95f4\uff0c\u6597\u4e86\u5341\u591a\u4e8c\u5341\u62db\uff0c\u53cc\u65b9\u9669\u62db\u8fed\u89c1\uff0c\u9646\u5c55\u9e4f\u6e9c\u6ed1\u975e\u5e38\uff0c\u5c55\u5f00\u817e\u632a\u95ea\u5c55\u7684\u5c0f\u5de7\u8eab \u6cd5\uff0c\u4e00\u5473\u6e38\u6597\uff0c\u53e3\u4e2d\u53d1\u8bdd\u9053\uff1a\u201c\u5f20\u98ce\u5e9c\u4f60\u82e5\u662f\u597d\u6c49\uff0c\u5e94\u81ea\u884c\u4e86\u7ed3\uff0c\u5154\u88ab\u5929\u4e0b\u82f1\u96c4\u6240\u7b11\u3002\u201d\u5f20 \u51e4\u5e9c\u559d\u9053\uff1a\u201c\u653e\u5c41\uff01\u675f\u624b\u4efb\u4f60\u5bb0\u5272\uff0c\u53cd\u800c\u662f\u597d\u6c49\u4e86\u5417\uff1f\u4f60\u8fd9\u4e2a\u8bdd\u662f\u90a3\u95e8\u5b50\u7684\u9053\u7406\uff1f\u201d\u9646\u5c55\u9e4f \u9053\uff1a\u201c\u5f20\u98ce\u5e9c\uff0c\u4f60\u987b\u77e5\u4eca\u665a\u4e4b\u4e8b\uff0c\u54b1\u4eec\u4e43\u662f\u5949\u7687\u4e0a\u7684\u5dee\u9063\uff0c\u4f60\u662f\u81e3\u5b50\uff0c\u4e3b\u4e0a\u8981\u8d50\u4f60\u4e00\u6b7b\uff0c\u4f60 \u4e0d\u9075\u547d\uff0c\u5374\u53cd\u800c\u8981\u6211\u4eec\u966a\u4f60\u6b7b\uff0c\u54c8\u54c8\uff0c\u8fd9\u9053\u7406\u53c8\u8bf4\u5f97\u8fc7\u53bb\u5417\uff1f\u201d\u53e4\u4ee3\u4e4b\u65f6\uff0c\u541b\u8981\u81e3\u6b7b\uff0c\u4e0d\u5f97 \u4e0d\u6b7b\uff1b\u7236\u8981\u5b50\u4ea1\uff0c\u4e0d\u5f97\u4e0d\u4ea1\u3002\u9646\u5c55\u9e4f\u7684\u8bdd\uff0c\u5012\u5e76\u4e0d\u662f\u5f3a\u8f9e\u593a\u7406\u3002\u4f46\u9646\u5c55\u9e4f\u5374\u6ca1\u60f3\u5230\uff0c\u5f20\u98ce\u5e9c \u81ea\u4ece\u542c\u5f20\u4e39\u67ab\u4e0a\u529d\uff0c\u5f52\u9690\u4ee5\u6765\uff0c\u6df1\u53d7\u5f20\u4e39\u67ab\u7684\u5f71\u54cd\uff0c\u65e9\u5df2\u628a\u4e3a\u4e00\u5bb6\u4e00\u59d3\u611a\u5fe0\u6548\u6b7b\u7684\u89c2\u5ff5\u629b\u4e4b \u8111\u540e\uff0c\u53ea\u89c1\u4ed6\u864e\u76ee\u5706\u7741\uff0c\u6012\u6781\u6124\u6781\uff0c\u53cd\u800c\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u9646\u5c55\u9e4f\uff0c\u4f60\u8fd9\u65e0\u803b\u5339\u592b\uff0c\u539f\u6765\u4f60\u662f \u8981\u6211\u6210\u5168\u4e8e\u4f60\uff0c\u501f\u6211\u9888\u4e2d\u7684\u70ed\u8840\uff0c\u67d3\u7ea2\u4f60\u5934\u4e0a\u7684\u4e4c\u7eb1\uff0c\u54fc\u3001\u54fc\uff0c\u8fd9\u6837\u7684\u8bdd\uff0c\u4f60\u5c45\u7136\u4e5f\u8bf4\u5f97 \u53e3\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u638c\u6cd5\u8d8a\u53d1\u8d8a\u5389\uff0c\u53ea\u542c\u5f97\u201c\u5495\u549a\u201d\u4e00\u58f0\u7ae5\u5bb6\u9a8f\u88ab\u4ed6\u638c\u98ce\u6240\u8feb\uff0c\u81ea\u5df1\u649e\u5728\u77f3\u5899 \u4e4b\u4e0a\uff0c\u9669\u9669\u6655\u5012\uff01 \u3000\u3000\u9646\u5c55\u9e4f\u4e00\u62db\u201c\u4e91\u9ebe\u4e09\u821e\u201d\uff0c\u5c06\u5f20\u98ce\u5e9c\u6321\u4e86\u4e00\u6321\uff0c\u53c8\u53d1\u8bdd\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u7687\u4e0a\u65e9\u770b\u51fa\u4f60\u8111\u6709 \u53cd\u9aa8\uff0c\u4f60\u679c\u7136\u53d1\u51fa\u8fd9\u7b49\u65e0\u7236\u65e0\u541b\u4e4b\u8a00\u3002\u5f20\u98ce\u5e9c\uff0c\u4f60\u53ef\u77e5\u53db\u9006\u4e4b\u7f6a\u4e48\uff1f\u4f60\u82e5\u675f\u624b\u5c31\u64d2\uff0c\u53ea\u4f60\u4e00 \u4eba\u8eab\u6b7b\uff0c\u82e5\u8fd8\u6297\u62d2\uff0c\u5b9a\u5fc5\u4e5d\u65cf\u7686\u8bdb\uff01\u201d\u5f20\u98ce\u5e9c\u4e3a\u7948\u9547\u62a4\u9a7e\u5341\u6709\u4f59\u5e74\uff0c\u5728\u571f\u6728\u5821\u4e00\u6218\uff0c\u5a01\u9707\u4e2d \u5916\uff0c\u66f4\u662f\u5177\u89c1\u5fe0\u809d\u4e49\u80c6\uff0c\u9aa4\u7136\u88ab\u52a0\u662f\u201c\u53db\u9006\u201d\u4e4b\u540d\uff0c\u5fc3\u4e2d\u5927\u6124\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u8fde\u5288\u4e09\u638c\uff0c\u5c06 \u7ae5\u3001\u9646\u4e8c\u4eba\u903c\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4e5f\u5148\u5165\u5bc7\u4e4b\u65f6\uff0c\u4f60\u5728\u54ea\u513f\uff1f\u54fc\uff0c\u800c\u4eca\u53cd\u800c\u4f60\u662f\u5fe0 \u81e3\uff0c\u6211\u662f\u53db\u9006\u4e86\uff1f\u201d\u9646\u5c55\u9e4f\u9053\uff1a\u201c\u5f20\u98ce\u5e9c\u4f60\u8fd8\u4e0d\u670d\u5417\uff0c\u82e5\u8981\u4eba\u4e0d\u77e5\uff0c\u9664\u975e\u5df2\u83ab\u4e3a\uff0c\u4f60\u4e0e\u5f20\u4e39 \u67ab\u4ea4\u597d\uff0c\u7687\u4e0a\u65e9\u5df2\u77e5\u9053\uff0c\u5f20\u4e39\u67ab\u662f\u4f55\u4eba\uff1f\u4f60\u4e0d\u77e5\u9053\u5417\uff1f\u671d\u5ef7\u5f8b\u4f8b\u5b9a\u5f97\u5206\u660e\uff0c\u4e0e\u53db\u9006\u540c\u8c0b\u4fbf\u4e0e \u53db\u9006\u540c\u7f6a\uff0c\u4f60\u8fd8\u6709\u4f55\u8fa9\u8bf4\uff1f\u518d\u8bf4\uff0c\u5f53\u5e74\u4e8e\u8c26\u64c5\u7acb\u7687\u5e1d\uff0c\u4f60\u7edf\u7387\u5fa1\u6797\u519b\u505a\u4e8e\u8c26\u7684\u5fc3\u8179\uff0c\u542c\u4e8e\u8c26 \u7684\u6307\u4f7f\uff0c\u8fd9\u8fd8\u4e0d\u662f\u53db\u9006\uff0c\u5c1a\u6709\u4f55\u7b49\u4e8b\u60c5\u79f0\u5f97\u53db\u9006\uff1f\u201d\u5f20\u98ce\u5e9c\u5706\u7741\u53cc\u76ee\uff0c\u5927\u559d\u9053\uff1a\u201c\u5982\u6b64\u8bf4 \u6765\uff0c\u4e8e\u9601\u8001\u4e5f\u662f\u53db\u9006\u4e86\uff01\u201d\u9646\u5c55\u9e4f\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u8fd8\u7528\u8bf4\uff1f\u7687\u4e0a\u65e9\u5df2\u5b89\u6392\u59a5\u5f53\uff0c\u4e00\u767b\u4f4d\u4fbf\u5c06\u4e8e \u8c26\u4e0b\u72f1\uff0c\u7531\u4e09\u53f8\u4f1a\u5ba1\uff0c\u516c\u5e03\u5176\u7f6a\uff0c\u660e\u6b63\u5178\u5211\uff0c\u54c8\u54c8\uff0c\u5f20\u98ce\u5e9c\uff0c\u4f60\u7684\u4e8e\u9601\u8001\u6b64\u523b\u53ea\u6015\u5df2\u7ecf\u8eab\u9996 \u5f02\u5904\u5566\uff01\u201d\u5f20\u98ce\u5e9c\u5fc3\u80c6\u6b32\u88c2\uff0c\u773c\u4e00\u95ed\uff0c\u9646\u5c55\u9e4f\u8f6f\u97ad\u548c\u7ae5\u5bb6\u9a8f\u7684\u94c1\u638c\u7acb\u523b\u5982\u72c2\u98ce\u66b4\u96e8\u822c\u5730\u75be\u653b \u800c\u4e0a\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u7a81\u7136\u53cc\u773c\u4e00\u7741\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff01\u4e8e\u9601\u8001\u4e5f\u662f\u53db\u9006\uff0c\u90a3\u6211\u4e07\u6b7b\u4f55\u8f9e\uff1f\u597d \u5440\uff01\u53db\u9006\u6765\u4e86\uff0c\u5413\uff0c\u5413\uff01\u5148\u6740\u4f60\u8fd9\u4e24\u4e2a\u72d7\u624d\uff01\u201d\u72b6\u82e5\u75af\u72c2\uff0c\u5de6\u6253\u4e00\u62f3\uff0c\u53f3\u5288\u4e00\u638c\uff0c\u7ae5\u5bb6\u9a8f\u5c1a \u4e14\u4e0d\u77e5\u5389\u5bb3\uff0c\u53cc\u638c\u6a2a\u80f8\u4e00\u6321\uff0c\u88ab\u5f20\u98ce\u5e9c\u4e00\u638c\u659c\u5288\uff0c\u7a81\u7136\u4e00\u4e2a\u53cd\u624b\u64d2\u62ff\uff0c\u7528\u529b\u4e00\u62d7\uff0c\u4ed6\u521a\u521a\u6388 \u597d\u7684\u53f3\u81c2\uff0c\u7adf\u88ab\u62d7\u5f97\u5728\u80a9\u818a\u4e4b\u4e0b\uff0c\u9f50\u6839\u65ad\u4e86\uff01 \u3000\u3000\u7ae5\u5bb6\u9a8f\u4e5f\u786e\u662f\u51f6\u608d\u4e4b\u6781\uff0c\u65ad\u4e86\u53f3\u81c2\uff0c\u8840\u6d41\u5982\u6ce8\uff0c\u4ecd\u7136\u5636\u58f0\u53eb\u9053\uff1a\u201c\u7f20\u6b7b\u4ed6\uff0c\u4ed6\u7684\u6bd2\u4f24\u5df2 \u7ecf\u53d1\u4f5c\u5566\uff01\u201d\u9646\u5c55\u9e4f\u4f7f\u7684\u8f6f\u97ad\u53ef\u8fbe\u4e00\u4e08\u5f00\u5916\uff0c\u4ed6\u7ed5\u7740\u5ba4\u4e2d\u7684\u5bb6\u5177\u6e38\u8d70\uff0c\u50fb\u50fb\u556a\u556a\u5730\u6325\u7740\u8f6f \u97ad\uff0c\u7167\u7740\u5f20\u98ce\u5e9c\u6ca1\u5934\u6ca1\u8138\u5730\u4e71\u6253\uff0c\u5f20\u98ce\u5e9c\u7109\u80fd\u7ed9\u4ed6\u6253\u4e2d\uff0c\u4f46\u9646\u5c55\u9e4f\u4ed7\u7740\u957f\u5175\u5668\u7684\u4fbf\u5229\uff0c\u4f7f\u7528 \u5982\u6b64\u72e1\u733e\u7684\u6218\u6cd5\uff0c\u5f20\u98ce\u5e9c\u5728\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e5f\u6293\u4ed6\u4e0d\u7740\u3002 \u3000\u3000\u7ae5\u5bb6\u9a8f\u7684\u6bd2\u7802\u638c\u5389\u5bb3\u975e\u5e38\uff0c\u5f20\u98ce\u5e9c\u4e2d\u4e86\u4e00\u638c\uff0c\u867d\u4ed7\u7740\u7cbe\u7eaf\u7684\u5185\u529f\uff0c\u8fd0\u6c14\u62a4\u7740\uff0c\u4f46\u65f6\u95f4\u4e00 \u957f\uff0c\u53f3\u81c2\u66f4\u89c9\u9ebb\u6728\uff0c\u8f6c\u52a8\u4e0d\u7075\u3002\u9646\u5c55\u9e4f\u770b\u51fa\u4ed6\u5df2\u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5f20\u98ce\u5e9c\uff0c\u4f60\u8fd8\u6709 \u4ec0\u4e48\u540e\u4e8b\u8981\u4ea4\u4ee3\u4e48\uff1f\u5ff5\u5728\u591a\u5e74\u540c\u50da\u4e4b\u60c5\uff0c\u6211\u4e00\u5b9a\u80fd\u66ff\u4f60\u529e\u5230\u3002\u201d\u9646\u5c55\u9e4f\u7684\u7528\u610f\u662f\u60f3\u6fc0\u4ed6\u6012\u706b \u653b\u5fc3\uff0c\u6bd2\u53d1\u66f4\u5feb\uff0c\u5f20\u98ce\u5e9c\u9661\u5730\u4e00\u58f0\u5927\u559d\uff0c\u4e00\u811a\u5c06\u5706\u684c\u8e22\u7ffb\uff0c\u6321\u7740\u95e8\u53e3\uff0c\u63a5\u7740\u50fb\u50fb\u556a\u556a\u7684\u4e00\u9635 \u4e71\u54cd\uff0c\u5f20\u98ce\u5e9c\u5c06\u5ba4\u4e2d\u7684\u5c4f\u98ce\u684c\u6905\u7b49\u7269\uff0c\u5c3d\u90fd\u63a8\u5012\uff0c\u98de\u8eab\u4fbf\u6765\u8ffd\u51fb\uff0c\u9646\u5c55\u9e4f\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u9661 \u542c\u5f97\u5f20\u98ce\u5e9c\u53c8\u662f\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u624b\u6293\u7740\u4e86\u9646\u5c55\u9e4f\u7684\u8f6f\u97ad\uff0c\u9646\u5c55\u9e4f\u6025\u5fd9\u677e\u624b\uff0c\u4f0f\u5730\u4e00\u6eda\uff0c\u76f4\u6eda\u5230 \u4e86\u4e66\u6a71\u7684\u5e95\u4e0b\uff0c\u5f20\u98ce\u5e9c\u4e00\u811a\u8e22\u51fa\uff0c\u53ea\u542c\u5f97\u201c\u8f70\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u63a5\u7740\u6709\u4eba\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3\uff01\u201d \u3000\u3000\u4e66\u6a71\u5012\u584c\u58f0\u4e2d\uff0c\u9634\u9633\u9762\u6218\u4e09\u5c71\u4e0e\u77ee\u51ac\u74dc\u95fb\u94c1\u58f0\u9aa4\u7136\u7a9c\u51fa\uff0c\u5ffd\u95fb\u5f97\u6218\u4e09\u5c71\u4e00\u58f0\u602a\u7b11\uff0c\u84b2\u6247 \u822c\u7684\u5927\u624b\u4e00\u6293\u5c31\u6293\u7740\u4e86\u5f20\u98ce\u5e9c\u7684\u80a9\u813e\u9501\u9aa8\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u95fb\u5144\u5f1f\uff0c\u4fa0\u5c06\u4ed6\u6bd9\u4e86\uff01\u201d\u8fd9\u4e00\u4e0b\u5f20 \u98ce\u5e9c\u505a\u68a6\u4e5f\u6599\u60f3\u4e0d\u5230\uff0c\u6218\u3001\u95fb\u4e8c\u4eba\u662f\u5f53\u4eca\u7687\u4e0a\u7684\u5fa1\u6797\u519b\u7edf\u9886\u4e0e\u9526\u8863\u536b\u6307\u6325\uff0c\u9646\u5c55\u9e4f\u4e0e\u7ae5\u5bb6\u9a8f \u5219\u662f\u201c\u592a\u4e0a\u7687\u201d\u7684\u4eb2\u4fe1\uff1b\u4e24\u7687\u4e89\u4f4d\uff0c\u6309\u8bf4\u53cc\u65b9\u4e43\u662f\u654c\u5bf9\u4e4b\u4eba\uff0c\u4ed6\u4eec\u9002\u624d\u8eb2\u5728\u6a71\u540e\uff0c\u5f20\u98ce\u5e9c\u867d \u4e0d\u671b\u4ed6\u4eec\u76f8\u52a9\uff0c\u4f46\u600e\u6837\u4e5f\u6599\u4e0d\u5230\u4ed6\u4eec\u5374\u53cd\u52a9\u5bf9\u65b9\uff0c\u7a81\u65bd\u88ad\u51fb\u3002 \u3000\u3000\u6218\u4e09\u5c71\u7684\u201c\u5206\u7b4b\u9519\u9aa8\u624b\u201d\u9a70\u540d\u6b66\u6797\uff0c\u8fd9\u4e00\u6293\u8d5b\u5982\u4e94\u628a\u94c1\u94b3\uff0c\u5f20\u98ce\u5e9c\u4e0a\u534a\u8eab\u987f\u65f6\u9ebb\u8f6f\uff0c\u4f7f \u4e0d\u51fa\u52b2\u6765\uff0c\u53ea\u89c1\u95fb\u94c1\u58f0\u950b\u5730\u4e00\u58f0\uff0c\u5f39\u51fa\u8170\u95f4\u8f6f\u5251\uff0c\u5bd2\u5149\u95ea\u95ea\uff0c\u7167\u7740\u5f20\u98ce\u5e9c\u7684\u5fc3\u5934\u4fbf\u6233\uff0c\u53e3\u4e2d \u5374\u563b\u563b\u7b11\u9053\uff1a\u201c\u5f20\u5927\u4eba\uff0c\u4eca\u65e5\u662f\u4f60\u7684\u6b7b\u671f\u5230\u5566\uff01\u201d\u9646\u5c55\u9e4f\u4ea6\u5df2\u722c\u4e86\u8d77\u6765\uff0c\u62fe\u8d77\u8f6f\u97ad\uff0c\u626c\u97ad\u4fbf \u626b\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6218\u3001\u95fb\u4e8c\u5144\uff0c\u8bc6\u65f6\u52a1\u8005\u4e3a\u4fca\u6770\uff0c\u54b1\u4eec\u4eca\u540e\u662f\u4e00\u6bbf\u4e4b\u81e3\u5566\uff01\u201d \u3000\u3000\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u5f20\u98ce\u5e9c\u5df2\u8fde\u7528\u51e0\u79cd\u8eab\u6cd5\uff0c\u54ea\u6599\u6218\u4e09\u5c71\u7684\u5206\u7b4b\u9519\u9aa8\u624b\u786e\u6709\u72ec\u5230\u7684\u624b\u6cd5\uff0c\u4e00 \u88ab\u642d\u4e0a\uff0c\u5373\u5982\u9644\u9aa8\u4e4b\u75bd\uff0c\u7adf\u7136\u6446\u8131\u4e0d\u5f00\uff0c\u773c\u89c1\u95fb\u94c1\u58f0\u7684\u8f6f\u5251\u4e0e\u9646\u5c55\u9e4f\u7684\u8f6f\u97ad\u90fd\u540c\u65f6\u6253\u5230\uff0c\u5f20 \u98ce\u5e9c\u9661\u7136\u53c8\u5927\u559d\u4e00\u58f0\uff0c\u4fe8\u5982\u6674\u5929\u6253\u4e86\u4e2a\u9739\u96f3\uff0c\u731b\u864e\u5728\u7b3c\uff0c\u96c4\u98ce\u4ecd\u5728\uff01\u8fd9\u4e00\u559d\u5413\u5f97\u95fb\u3001\u9646\u4e8c\u4eba \u80c6\u6218\u5fc3\u60ca\uff0c\u957f\u97ad\u8f6f\u5251\u7adf\u7136\u505c\u5728\u534a\u9014\uff0c\u731b\u7136\u4e4b\u95f4\uff0c\u7adf\u662f\u7ed9\u5413\u7740\u4e86\uff0c\u8bf4\u65f6\u8fdf\u90a3\u65f6\u5feb\uff0c\u5f20\u98ce\u5e9c\u817e\u5730 \u98de\u8d77\u5de6\u811a\uff0c\u63a5\u7740\u98de\u8d77\u5728\u811a\uff0c\u5c06\u95fb\u3001\u9646\u4e8c\u4eba\u90fd\u8e22\u4e86\u4e2a\u7b4b\u6597\uff01\u5de6\u8098\u4e00\u649e\uff0c\u5de6\u624b\u7ffb\u8fc7\u80a9\u5934\uff0c\u731b\u5730\u4e00 \u6293\u3002 \u3000\u3000\u6218\u4e09\u5c71\u6700\u5de5\u4e8e\u5fc3\u8ba1\uff0c\u4ed6\u9002\u624d\u8eb2\u5728\u4e66\u6a71\u4e4b\u540e\uff0c\u542c\u5230\u4e86\u9646\u5c55\u9e4f\u4e0e\u5f20\u98ce\u5e9c\u7684\u8bf4\u8bdd\uff0c\u77e5\u9053\u592a\u4e0a\u7687 \u5df2\u7ecf\u590d\u8f9f\uff0c\u4fbf\u7acb\u65f6\u51b3\u5b9a\u5f03\u6389\u6545\u4e3b\uff0c\u6539\u6295\u65b0\u541b\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u592a\u4e0a\u7687\u6700\u5fcc\u4e8e\u8c26\u3001\u5f20\u4e39\u67ab\u3001\u5f20\u98ce \u5e9c\u4e09\u4eba\uff0c\u4e8e\u8c26\u5df2\u64d2\uff0c\u5f20\u4e39\u67ab\u5728\u91ce\uff0c\u672c\u4e8b\u6700\u5927\uff0c\u4e00\u65f6\u6349\u62ff\u4e0d\u5230\uff0c\u5269\u4e0b\u7684\u5f20\u98ce\u5e9c\uff0c\u592a\u4e0a\u7687\u7528\u5b98\u4f4d \u7b3c\u7edc\u4ed6\uff0c\u4ed6\u53c8\u4e0d\u80af\u4e3a\u592a\u4e0a\u7687\u6240\u7528\uff0c\u96be\u602a\u592a\u4e0a\u7687\u8981\u6740\u6b7b\u4ed6\u3002\u6211\u82e5\u80fd\u5c06\u5f20\u98ce\u5e9c\u6740\u4e86\uff0c\u6539\u6295\u65b0\u541b\uff0c \u90a3\u5c31\u662f\u6700\u597d\u7684\u8d4e\u7f6a\u7acb\u529f\u4e4b\u793c\u3002\u201d\u4f46\u5fcc\u60ee\u5f20\u98ce\u5e9c\u7684\u6b66\u529f\u4e86\u5f97\uff0c\u5fc3\u4e2d\u53c8\u60f3\u9053\uff1a\u201c\u4e0d\u5982\u5148\u4f5c\u5750\u5c71\u89c2 \u864e\u6597\uff0c\u5f85\u4ed6\u4eec\u4e24\u8d25\u4ff1\u4f24\uff0c\u6211\u518d\u51fa\u800c\u6536\u62fe\u6b8b\u5c40\uff0c\u90a3\u5c82\u4e0d\u662f\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\uff0c\u9646\u3001\u7ae5\u4e8c\u4eba\u6076\u6597\u4e4b \u540e\uff0c\u4e0d\u6b7b\u4ea6\u5c06\u6b8b\u5e9f\uff0c\u8fd9\u5fa1\u6797\u519b\u7684\u7edf\u9886\uff0c\u820d\u6211\u5176\u8c01\uff1f\u54c8\u54c8\u8fd9\u4e00\u77f3\u4e09\u9e1f\u4e4b\u8ba1\uff0c\u5c82\u4e0d\u5999\u54c9\uff01\u201d\u4ed6\u76d8 \u7b97\u518d\u4e09\uff0c\u8c0b\u5b9a\u800c\u52a8\uff0c\u773c\u89c1\u5f20\u98ce\u5e9c\u53f3\u80a9\u4e2d\u4e86\u6bd2\u638c\uff0c\u4e0d\u80fd\u8f6c\u52a8\uff0c\u9002\u9022\u4ed6\u4eec\u6253\u8fd1\u4e66\u6a71\uff0c\u9042\u4e00\u628a\u6293\u7740 \u5f20\u98ce\u5e9c\u5de6\u80a9\u811a\u9aa8\uff0c\u6559\u4ed6\u4e24\u81c2\u90fd\u4e0d\u80fd\u52a8\u5f39\uff0c\u81ea\u7136\u4efb\u7531\u5bb0\u5272\u3002 \u3000\u3000\u6218\u4e09\u5c71\u5fc3\u8ba1\u867d\u5de5\uff0c\u5374\u60f3\u4e0d\u5230\u5f20\u98ce\u5e9c\u8fd8\u6709\u8fd9\u4e00\u624b\u62fc\u4e86\u6027\u547d\u7684\u53cd\u51fb\uff0c\u7ed9\u4ed6\u5de6\u8098\u4e00\u649e\uff0c\u75db\u5f7b\u5fc3 \u80ba\uff0c\u53f3\u624b\u4e00\u6293\uff0c\u53c8\u6263\u7740\u4e86\u8109\u95e8\uff0c\u6218\u4e09\u5c71\u5927\u53eb\u4e00\u58f0\uff0c\u4e94\u6307\u4e00\u52fe\u4e4b\u540e\uff0c\u6025\u5fd9\u677e\u624b\uff0c\u53ea\u542c\u5f97\u7bf7\u3001\u84ec \u4e24\u58f0\uff0c\u5f20\u98ce\u5e9c\u4e0e\u6218\u4e09\u5c71\u90fd\u8dcc\u5012\u5730\u4e0a\u3002\u540c\u65f6\u9694\u5ba4\u4e5f\u542c\u5f97\u5495\u549a\u4e00\u58f0\uff0c\u4f3c\u662f\u6709\u4eba\u5815\u5730\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u9694\u5899\u5077\u770b\u7684\u6a0a\u82f1\uff0c\u521a\u624d\u4e00\u58f0\u201c\u5c0f\u5fc3\u201d\u4e5f\u662f\u4ed6\u53d1\u51fa\u7684\uff0c\u5374\u4e0d\u6599\u8fd9\u4e00\u53eb\u7acb\u523b\u7ed9\u9694\u5ba4\u7684 \u654c\u4eba\u53d1\u89c9\uff0c\u7ae5\u5bb6\u9a8f\u65ad\u4e86\u4e00\u81c2\uff0c\u5c1a\u6709\u4e00\u81c2\u80fd\u591f\u4f7f\u7528\uff0c\u4ed6\u662f\u6697\u5668\u540d\u5bb6\uff0c\u5584\u80fd\u95fb\u58f0\u8fa8\u5f71\uff0c\u7acb\u523b\u671d\u7740 \u5899\u5934\u7684\u6c14\u5b54\uff0c\u5f39\u51fa\u4e86\u4e00\u679a\u201d\u4e94\u6bd2\u9488\u201d\uff0c\u9976\u662f\u6a0a\u82f1\u95ea\u907f\u5f97\u5feb\uff0c\u6ca1\u6709\u7ed9\u4ed6\u5c04\u778e\u773c\u775b\uff0c\u4f46\u5374\u4e2d\u4e86\u4e2d \u6307\u6307\u5c16\uff0c\u652f\u6301\u4e0d\u4e86\u7247\u523b\uff0c\u4fbf\u4ece\u5899\u4e0a\u8dcc\u4e0b\u3002 \u3000\u3000\u7ae5\u5bb6\u9a8f\u5636\u58f0\u53eb\u9053\uff1a\u201c\u9694\u5899\u57cb\u4f0f\u6709\u4eba\u3002\u201d\u9646\u5c55\u9e4f\u5728\u5730\u4e0a\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\u6025\u8df3\u8d77\u6765\uff0c\u731b\u542c\u5f97\u4e00 \u58f0\u559d\u9053\uff1a\u201c\u8fd8\u60f3\u9003\u751f\uff1f\u201d\u53ea\u89c1\u5f20\u98ce\u5e9c\u795e\u5a01\u51db\u51db\uff0c\u5835\u5728\u7a97\u53e3\u7684\u4e00\u638c\uff0c\u6a2a\u626b\u8fc7\u53bb\uff0c\u9646\u5c55\u9e4f\u56de\u8eab\u4e00 \u7a9c\uff0c\u811a\u80ef\u5df2\u4e2d\u4e86\u4e00\u638c\uff0c\u5f20\u98ce\u5e9c\u7684\u638c\u529b\u6709\u5f00\u780c\u88c2\u77f3\u4e4b\u529f\uff0c\u9646\u5c55\u9e4f\u4e2d\u4e86\u4e00\u638c\uff0c\u75db\u5f97\u773c\u775b\u53d1\u9ed1\uff0c\u5927 \u53eb\u4e00\u58f0\uff1a\u201c\u6211\u547d\u4f11\u77e3\uff01\u201d\u5ffd\u542c\u5f97\u95fb\u94c1\u58f0\u563b\u563b\u7b11\u9053\uff1a\u201c\u9646\u5144\u4f11\u6015\uff0c\u4ed6\u4e5f\u53d7\u4e86\u91cd\u4f24\uff0c\u65e0\u80fd\u4e3a\u529b \u4e86\uff01\u518d\u71ac\u4e00\u65f6\uff0c\u5408\u529b\u653b\u4ed6\uff01\u201d \u3000\u3000\u9646\u5c55\u9e4f\u81ea\u5206\u5fc5\u6b7b\uff0c\u6d51\u8eab\u65e0\u529b\uff0c\u542c\u4e86\u95fb\u94c1\u58f0\u4e4b\u8a00\uff0c\u5ffd\u89c9\u5f20\u98ce\u5e9c\u7684\u638c\u529b\u5e76\u4e0d\u5982\u60f3\u8c61\u4e4b\u5927\uff0c\u867d \u7136\u75bc\u75db\u4e4b\u6781\uff0c\u4ecd\u53ef\u6323\u624e\uff0c\u6025\u5fd9\u8fd0\u4e00\u53e3\u6c14\uff0c\u53c8\u722c\u8d77\u6765\uff0c\u53ea\u89c1\u5f20\u98ce\u5e9c\u7684\u53f3\u81c2\u5df2\u540a\u4e0b\u6765\uff0c\u80a9\u8863\u88ab\u8840 \u67d3\u5f97\u9c9c\u7ea2\uff0c\u5de6\u81c2\u867d\u7136\u80fd\u591f\u8f6c\u52a8\uff0c\u4f46\u638c\u6cd5\u4ea6\u89c9\u8fdf\u949d\u4e0d\u7075\uff0c\u5927\u975e\u6614\u6bd4\u3002\u539f\u6765\u5f20\u98ce\u5e9c\u7684\u53f3\u81c2\u4e2d\u4e86\u6bd2 \u638c\uff0c\u53f3\u624b\u672c\u5df2\u8f6c\u52a8\u4e0d\u7075\uff0c\u9002\u624d\u62fc\u547d\u4e00\u51fb\uff0c\u867d\u7136\u89e3\u4e86\u6218\u4e09\u5c71\u7684\u5206\u7b4b\u9519\u9aa8\u624b\uff0c\u90a3\u6761\u53f3\u81c2\u4ea6\u56e0\u6b64\u8131 \u81fc\uff0c\u518d\u4e5f\u4e0d\u80fd\u4f7f\u7528\u3002\u800c\u5de6\u81c2\u7684\u7b4b\u9aa8\u88ab\u6218\u4e09\u5c71\u634f\u788e\u51e0\u6761\uff0c\u52b2\u529b\u4ea6\u51cf\u4e86\u4e00\u534a\uff0c\u6b63\u662f\u5982\u6b64\uff0c\u6240\u4ee5\u9646 \u5c55\u9e4f\u624d\u5e78\u5f97\u4e0d\u6b7b\u3002 \u3000\u3000\u9646\u5c55\u9e4f\u89c1\u72b6\u5927\u559c\uff0c\u518d\u6b21\u62fe\u8d77\u8f6f\u97ad\uff0c\u71ac\u7740\u75bc\u75db\uff0c\u4e0a\u524d\u518d\u653b\uff0c\u53ea\u89c1\u6218\u4e09\u5c71\u9762\u8272\u60e8\u767d\uff0c\u6447\u6447\u6643 \u6643\uff0c\u95fb\u94c1\u58f0\u4e5f\u4e00\u62d0\u4e00\u62d0\u5730\u4e0d\u6562\u7eb5\u8dc3\u3002\u539f\u6765\u5ba4\u4e2d\u4e94\u4eba\u90fd\u53d7\u4e86\u4f24\uff0c\u7ae5\u5bb6\u9a8f\u65ad\u4e86\u4e00\u81c2\uff0c\u73b0\u5728\u5df2\u662f\u5944 \u5944\u4e00\u606f\uff0c\u4e0d\u5fc5\u8bf4\u4e86\u3002\u4f59\u4e0b\u7684\u56db\u4eba\uff0c\u95fb\u94c1\u58f0\u7ed9\u8e22\u7834\u4e86\u811a\uff0c\u6218\u4e09\u5c71\u7ed9\u649e\u65ad\u4e86\u808b\u9aa8\uff0c\u9646\u5c55\u9e4f\u7ed9\u9707\u4f24 \u4e86\u5185\u810f\uff0c\u4f46\u76f8\u6bd4\u8d77\u6765\uff0c\u8fd8\u662f\u5f20\u98ce\u5e9c\u4f24\u5f97\u6700\u91cd\uff01 \u3000\u3000\u8fd9\u4e00\u756a\u5404\u81ea\u8d1f\u4f24\u8840\u6218\uff0c\u66f4\u89c1\u51f6\u9669\uff0c\u5f20\u98ce\u5e9c\u5355\u638c\u5e94\u654c\uff0c\u6e10\u89c9\u4e0d\u652f\uff0c\u5176\u4e2d\u95fb\u94c1\u58f0\u4f24\u5f97\u6700\u8f7b\uff0c \u4ed6\u8df3\u8dc3\u4e0d\u4fbf\uff0c\u7d22\u6027\u4f0f\u5730\u4e00\u6eda\uff0c\u65bd\u5c55\u5317\u6d3e\u7684\u201c\u6eda\u5730\u5802\u201d\u529f\u592b\uff0c\u7528\u8f6f\u5251\u524a\u5f20\u98ce\u5e9c\u7684\u53cc\u811a\uff0c\u5f20\u98ce\u5e9c \u5ffd\u5730\u548c\u8eab\u4e00\u6251\uff0c\u5c06\u6218\u4e09\u5c71\u649e\u5012\uff0c\u6218\u4e09\u5c71\u6025\u5fd9\u65bd\u5c55\u5206\u7b4b\u9519\u9aa8\u624b\u548c\u4ed6\u8089\u640f\uff0c\u5f20\u98ce\u5e9c\u624b\u6cd5\u4f55\u7b49\u8fc5 \u6377\uff0c\u4e94\u6307\u4e00\u62ff\uff0c\u7acb\u523b\u5c06\u4ed6\u7684\u624b\u8155\u4e00\u626d\uff0c\u53eb\u9053\uff1a\u201c\u53eb\u4f60\u4e5f\u5c1d\u5c1d\u65ad\u81c2\u7684\u6ecb\u5473\uff01\u201d\u6218\u4e09\u5c71\u60e8\u53eb\u4e00 \u58f0\uff0c\u4f0f\u5730\u4e09\u6eda\uff0c\u6eda\u5230\u5899\u8fb9\uff0c\u6367\u7740\u624b\u81c2\uff0c\u9635\u9635\u547c\u75db\uff0c\u90a3\u6761\u624b\u81c2\u7adf\u7ed9\u5f20\u98ce\u5e9c\u786c\u751f\u751f\u5730\u5f3a\u626d\u4e0b\u6765\uff0c \u53ea\u7c98\u8fde\u7740\u5c11\u8bb8\u76ae\u9aa8\uff01 \u3000\u3000\u53ea\u89c1\u5f20\u98ce\u5e9c\u4e00\u8dc3\u800c\u8d77\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u628a\u5bd2\u5149\u95ea\u95ea\u7684\u5b9d\u5200\uff0c\u8fd9\u628a\u5b9d\u5200\u4ed6\u5df2\u591a\u5e74\u4e0d\u7528\uff0c\u6302\u5728 \u4e66\u6a71\u5185\u9762\uff0c\u5982\u4eca\u53d6\u51fa\uff0c\u5982\u864e\u6dfb\u7ffc\uff0c\u9646\u5c55\u9e4f\u5413\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u5f20\u98ce\u5e9c\u5927\u559d\u9053\uff1a\u201c\u4eca\u65e5\u82e5\u6559\u4f60\u7b49\u751f \u51fa\u6b64\u95e8\uff0c\u6211\u5f20\u98ce\u5e9c\u4e09\u5b57\u5012\u5199\uff01\u201d\u8de8\u6b65\u63d0\u5200\uff0c\u624b\u8d77\u5200\u843d\uff0c\u9646\u5c55\u9e4f\u9661\u89c9\u80cc\u5fc3\u4e00\u7247\u51c9\u610f\uff0c\u8863\u88f3\u5df2\u88ab \u5200\u950b\u5272\u88c2\uff0c\u6b63\u5728\u751f\u6b7b\u5173\u5934\uff0c\u5ffd\u542c\u5f97\u5f20\u98ce\u5e9c\u5927\u543c\u4e00\u58f0\uff0c\u9646\u5c55\u9e4f\u56de\u8eab\u62db\u67b6\u4e4b\u65f6\uff0c\u53ea\u89c1\u5f20\u98ce\u5e9c\u8e09\u8e09 \u8dc4\u8dc4\u5730\u5012\u9000\u51e0\u6b65\uff0c\u5ffd\u5730\u559d\u9053\uff1a\u201c\u9f20\u8f88\uff0c\u4f60\u8fd8\u672a\u6b7b\u4e48\uff1f\u201d\u4e00\u811a\u5f80\u5730\u4e0a\u8e39\u4e0b\uff0c\u4f46\u542c\u5f97\u7ae5\u5bb6\u9a8f\u4e00\u58f0 \u60e8\u53eb\uff0c\u6eda\u4e86\u4e24\u6eda\uff0c\u5bc2\u7136\u4e0d\u52a8\uff0c\u60f3\u662f\u7ed9\u5f20\u98ce\u5e9c\u8e0f\u6b7b\u4e86\u3002 \u3000\u3000\u539f\u6765\u9002\u624d\u5f20\u98ce\u5e9c\u8ffd\u6240\u9646\u5c55\u9e4f\u4e4b\u65f6\uff0c\u6ca1\u60f3\u5230\u7ae5\u5bb6\u9a8f\u8eba\u5728\u5730\u4e0a\uff0c\u4ed6\u8fd8\u6709\u4e00\u624b\u5c1a\u80fd\u4f7f\u7528\uff0c\u89c1\u5f20 \u98ce\u5e9c\u5728\u4ed6\u8eab\u8fb9\u8de8\u8fc7\uff0c\u4ed6\u624b\u5fc3\u6263\u4e86\u5341\u51e0\u53e3\u6bd2\u9488\uff0c\u7528\u529b\u4e00\u63d2\uff0c\u5168\u90e8\u63d2\u5165\u5f20\u98ce\u5e9c\u7684\u5c0f\u817f\uff01 \u3000\u3000\u95fb\u94c1\u58f0\u5927\u559c\u53eb\u9053\uff1a\u201c\u6218\u5144\uff0c\u6218\u5144\uff0c\u5feb\u6765\u52a9\u4e00\u81c2\u4e4b\u529b\uff01\u201d\u6218\u4e09\u5c71\u65ad\u4e86\u4e00\u53ea\u6709\u81c2\uff0c\u52c9\u5f3a\u7ad9 \u8d77\uff0c\u5f53\u771f\u662f\u53ea\u80fd\u201c\u52a9\u4e00\u81c2\u4e4b\u529b\u201d\u4e86\uff01\u4f46\u6b64\u65f6\u6b64\u9645\uff0c\u5f20\u98ce\u5e9c\u624b\u811a\u4ff1\u4f24\uff0c\u6bd2\u4e0a\u52a0\u6bd2\uff0c\u6bd2\u6c14\u653b\u5fc3\uff0c \u8fd9\u201c\u4e00\u81c2\u4e4b\u529b\u201d\uff0c\u5c31\u7b49\u4e8e\u7ed9\u5f20\u98ce\u5e9c\u6dfb\u4e86\u4e00\u4e2a\u52b2\u654c\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u54ac\u4e00\u54ac\u7259\uff0c\u4e00\u62db\u201c\u591c\u6218\u516b\u65b9\u201d\uff0c\u5c06\u4e09\u4e2a\u654c\u624b\u90fd\u8feb\u5f00\u6570\u6b65\uff0c\u987f\u5982\u75af\u864e\u4e00\u822c\uff0c\u5c55\u5f00 \u201c\u4e94\u864e\u65ad\u95e8\u5200\u6cd5\u201d\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u5f3a\u653b\u731b\u6253\u3002\u6218\u4e09\u5c71\u6c89\u58f0\u559d\u9053\uff1a\u201c\u4e0d\u8981\u786c\u63a5\uff0c\u4ed6\u8fc7 \u4e0d\u4e86\u534a\u4e2a\u65f6\u8fb0\uff01\u201d\u5f20\u98ce\u5e9c\u4f55\u5c1d\u4e0d\u77e5\u6bd2\u6c14\u653b\u5fc3\uff0c\u4e0d\u80fd\u7528\u529b\uff0c\u4f46\u8fd9\u65f6\u4ed6\u5df2\u62b1\u7740\u4e0e\u654c\u5055\u4ea1\u7684\u5fc3\u60c5\uff0c \u518d\u65e0\u987e\u5fcc\uff0c\u4f46\u654c\u624b\u4e09\u4eba\uff0c\u95fb\u94c1\u58f0\u4f24\u5f97\u6700\u8f7b\uff0c\u8fd8\u80fd\u62db\u67b6\uff0c\u6218\u3001\u9646\u4e8c\u4eba\u5728\u95fb\u94c1\u58f0\u63a9\u62a4\u4e4b\u4e0b\uff0c\u7ed5\u5ba4 \u800c\u8d70\uff0c\u4e24\u4eba\u90fd\u662f\u51b7\u4e0d\u9632\u5730\u4f60\u53d1\u4e00\u97ad\uff0c\u6211\u53d1\u4e00\u638c\uff0c\u8981\u7528\u7f20\u6597\u7684\u65b9\u6cd5\uff0c\u5c06\u5f20\u98ce\u5e9c\u6d3b\u6d3b\u62d6\u6b7b\u3002\u5f20\u98ce \u5e9c\u529b\u4e0d\u4ece\u5fc3\uff0c\u53ea\u89c1\u773c\u524d\u4eba\u5f71\u6a21\u7cca\uff0c\u8d8a\u6765\u8d8a\u9ed1\u3002 \u3000\u3000\u518d\u8bf4\u9694\u5ba4\u7684\u6a0a\u82f1\uff0c\u4ece\u5899\u5934\u8dcc\u4e0b\u4e4b\u540e\uff0c\u53ea\u89c9\u4e2d\u6307\u6307\u5c16\uff0c\u9690\u9690\u53d1\u9ebb\uff0c\u77e5\u9053\u5389\u5bb3\uff0c\u6025\u5fd9\u89e3\u4e0b\u4f69 \u5200\uff0c\u5f80\u6307\u5c16\u4e0a\u8f7b\u8f7b\u4e00\u5272\uff0c\u5148\u628a\u6bd2\u8840\u6324\u51fa\uff0c\u518d\u6495\u4e0b\u8863\u895f\uff0c\u7d27\u7d27\u5305\u624e\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u745f\u7f29\u4e00\u9685\uff0c\u98a4 \u58f0\u95ee\u9053\uff1a\u201c\u8001\u6a0a\uff0c\u54b1\u4eec\u600e\u4e48\u529e\uff1f\u201d\u201c\u5f20\u98ce\u5e9c\u7adf\u662f\u53db\u9006\uff0c\u8fd9\u5982\u4f55\u662f\u597d\uff1f\u201d\u201c\u5440\uff0c\u54b1\u4eec\u5c82\u4e0d\u662f\u8981 \u6d3b\u6d3b\u997f\u6b7b\u5728\u8fd9\u77f3\u5ba4\u4e4b\u4e2d\u3002\u201d\u6a0a\u82f1\u534a\u53e5\u4e0d\u7b54\uff0c\u6478\u5230\u5899\u8fb9\uff0c\u542c\u9694\u5ba4\u9ad8\u547c\u9163\u6597\uff0c\u5200\u5251\u9f50\u9e23\uff0c\u4e0d\u77e5\u8c01 \u80dc\u8c01\u8d25\uff0c\u5fc3\u4e2d\u7126\u6025\u975e\u5e38\uff0c\u53c8\u60f3\u8d77\u4e8e\u8c26\u4e0b\u7262\uff0c\u5f20\u98ce\u5e9c\u88ab\u653b\uff0c\u5fcd\u4e0d\u7740\u8840\u8109\u55b7\u5f20\uff0c\u66f4\u4e3a\u60b2\u6124\uff0c\u7528\u5200 \u529b\u65ab\u5899\u58c1\uff0c\u6068\u4e0d\u5f97\u65ab\u7a7f\u77f3\u5899\uff0c\u8fc7\u53bb\u52a9\u6218\u3002 \u3000\u3000\u9694\u5ba4\u4e24\u65b9\uff0c\u6b63\u5230\u4e86\u751f\u6b7b\u8089\u640f\u7684\u65f6\u5019\uff0c\u95fb\u94c1\u58f0\u7b49\u4eba\u53ef\u4e0d\u77e5\u9694\u5ba4\u7684\u77f3\u95e8\u5df2\u7ed9\u5c0f\u864e\u5b50\u9501\u4e0a\uff0c\u542c \u5f97\u77f3\u58c1\u4f3c\u64c2\u9f13\u822c\u541f\u541f\u58f0\u54cd\uff0c\u53ea\u9053\u662f\u5f20\u98ce\u5e9c\u6240\u57cb\u4f0f\u7684\u9ad8\u624b\u6b63\u6b32\u7834\u95e8\u800c\u5165\uff0c\u9646\u5c55\u9e4f\u80c6\u5b50\u6700\u602f\uff0c\u9996 \u5148\u5413\u4e86\u4e00\u8df3\uff0c\u8651\u6643\u4e00\u97ad\uff0c\u53c8\u6b32\u5954\u5230\u7a97\u53e3\uff0c\u7a7f\u7a97\u9003\u547d\uff0c\u5f20\u98ce\u5e9c\u5438\u4e00\u53e3\u6c14\uff0c\u7a81\u7136\u53cc\u773c\u4e00\u7741\uff0c\u7cbe\u5149 \u5916\u5c04\uff0c\u9661\u7136\u4e00\u559d\uff0c\u6a2a\u5200\u4e00\u5288\uff0c\u624b\u8d77\u5200\u843d\uff0c\u9646\u5c55\u9e4f\u5728\u5f20\u98ce\u5e9c\u624b\u4e0b\u9003\u4e86\u4e24\u6b21\u6027\u547d\uff0c\u6700\u540e\u8fd9\u4e00\u5200\u77e5 \u9003\u4e0d\u8fc7\u4e86\uff0c\u5200\u950b\u4ece\u80a9\u4e0a\u659c\u659c\u5288\u4e0b\uff0c\u7adf\u628a\u4ed6\u5288\u6210\u4e24\u534a\uff01 \u3000\u3000\u6218\u4e09\u5c71\u60ca\u5446\u4e86\uff0c\u53ea\u89c1\u5f20\u98ce\u5e9c\u5200\u672a\u62bd\u51fa\uff0c\u9661\u5730\u53c8\u4e00\u58f0\u5927\u559d\uff0c\u5de6\u811a\u4e00\u4e2a\u201c\u8de8\u864e\u767b\u5c71\u201d\uff0c\u515c\u5fc3 \u76f4\u8e22\uff0c\u6218\u4e09\u5c71\u53eb\u9053\uff1a\u201c\u95fb\u5144\u3001\u95fb\u5144\u2026\u2026\u201d\u53eb\u58f0\u672a\u7edd\uff0c\u80f8\u53e3\u7a81\u5982\u4e2d\u4e86\u5343\u65a4\u94c1\u9524\uff0c\u4ef0\u5929\u4fbf\u5012\u3002\u95fb \u94c1\u58f0\u4e00\u5251\u63d2\u4e2d\u5f20\u98ce\u5e9c\u7684\u80cc\u5fc3\uff0c\u5251\u950b\u521a\u521a\u5272\u7834\u76ae\u8089\uff0c\u6b63\u60f3\u5411\u524d\u4e00\u9001\uff0c\u542c\u5f97\u6218\u4e09\u5c71\u7684\u60e8\u53eb\uff0c\u5fc3\u4e2d \u4e00\u5bd2\uff0c\u5f20\u98ce\u5e9c\u5411\u524d\u4e00\u8dc3\uff0c\u53cd\u8f6c\u8eab\u6765\uff0c\u53eb\u9053\uff1a\u201c\u73b0\u5728\u53ea\u6709\u4f60\u4e86\uff01\u201d\u95fb\u94c1\u58f0\u53eb\u9053\uff1a\u201c\u5f20\u5927\u4eba\u9976 \u547d\uff01\u201d\u5f20\u98ce\u5e9c\u53cd\u624b\u4e00\u63b7\uff0c\u90a3\u53e3\u7f05\u5200\u631f\u7740\u4e00\u9053\u5bd2\u5149\uff0c\u5530\u7684\u4e00\u58f0\uff0c\u4ece\u95fb\u94c1\u58f0\u7684\u524d\u5fc3\u6876\u5165\uff0c\u76f4\u7a7f\u8fc7 \u540e\u5fc3\uff0c\u545b\u8dc4\u4e00\u58f0\uff0c\u8dcc\u4e8e\u5730\u4e0a\u3002 \u3000\u3000\u5f20\u98ce\u5e9c\u54c8\u54c8\u5927\u7b11\uff0c\u62fe\u8d77\u7f05\u5200\uff0c\u63a8\u5f00\u77f3\u684c\uff0c\u8d70\u51fa\u53bb\u5f00\u4e86\u9694\u5ba4\u7684\u77f3\u95e8\uff0c\u559d\u9053\uff1a\u201c\u8c01\u5728\u91cc\u9762\uff0c \u90fd\u7ed9\u6211\u6eda\u51fa\u6765\uff01\u201d\u4e24\u4e2a\u519b\u5b98\u6296\u6296\u7d22\u7d22\uff0c\u7ed9\u6a0a\u82f1\u63a8\u4e86\u51fa\u6765\uff0c\u5f20\u98ce\u5e9c\u4e00\u89c1\uff0c\u6a2a\u5200\u559d\u9053\uff1a\u201c\u6a0a\u82f1\uff0c \u4f60\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u8fd9\u4e24\u4e2a\u519b\u5b98\u662f\u8c01\u5dee\u9063\u6765\u7684\uff1f\u201d\u90a3\u4e24\u4e2a\u519b\u5b98\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u53e9\u5934\u53eb\u9053\uff1a \u201c\u6211\uff0c\u6211\u662f\u6765\u6c42\u5f20\u5927\u4eba\u6551\u547d\u7684\uff01\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u6709\u8fd9\u4e48\u5bb9\u6613\uff1f\u6211\u5f20\u5bb6\u662f\u968f\u4fbf\u53ef\u4ee5\u95ef\u8fdb \u7684\u4e48\uff1f\u201d\u4ed6\u53ea\u9053\u8fd9\u4e24\u4e2a\u519b\u5b98\u4e5f\u662f\u671d\u5ef7\u6d3e\u6765\u7684\u4eba\uff0c\u6a2a\u5200\u77aa\u76ee\uff0c\u9f13\u8d77\u4f59\u52c7\uff0c\u5c1a\u6b32\u518d\u6218\uff0c\u5ffd\u542c\u5f97 \u201c\u549a\u549a\u201d\u4e24\u58f0\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u90e8\u5413\u5f97\u6655\u5012\u5730\u4e0a\u4e86\uff01 \u3000\u3000\u6a0a\u82f1\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5f20\u98ce\u5e9c\u5df2\u6210\u4e86\u4e00\u4e2a\u8840\u4eba\uff1b\u72b9\u81ea\u795e\u5a01\u51db\u51db\uff0c\u6a0a\u82f1\u5fcd\u4e0d\u4f4f\u70ed\u6cea\u76c8\u7736\uff0c\u6276 \u7740\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u5f20\u4f2f\u4f2f\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u5f20\u98ce\u5e9c\u5389\u58f0\u65a5\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff1f\u4f60\u4f2f\u7236\u662f\u600e\u4e48\u6b7b\u7684\uff1f \u4f60\u5374\u5e26\u4eba\u5230\u8fd9\u671b\u6765\uff01\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u4f2f\u4f2f\uff0c\u4f60\u5148\u6b47\u6b47\uff0c\u5bb9\u6211\u7ec6\u8bf4\uff01\u201d\u5f20\u98ce\u5e9c\u8d70\u56de\u77f3\u5ba4\uff0c\u76d8\u819d\u4e00 \u5750\uff0c\u62db\u624b\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6765\uff01\u201d \u3000\u3000\u6a0a\u82f1\u638f\u51fa\u91d1\u521b\u836f\uff0c\u6b32\u66ff\u5f20\u98ce\u5e9c\u6599\u7406\u4f24\u53e3\uff0c\u5f20\u98ce\u5e9c\u77aa\u4e86\u6a0a\u82f1\u4e00\u773c\uff0c\u9053\uff1a\u201c\u653e\u4e0b\uff0c\u8c01\u8981\u4f60\u8fd9 \u4e48\u5a46\u5a46\u5988\u5988\uff0c\u5feb\u8bf4\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u6a0a\u82f1\u65bd\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u6240\u8bf4\u662f\u771f\uff0c\u4ed6\u4eec\u4ece \u6e56\u5317\u62bc\u89e3\u9556\u94f6\u5165\u4eac\uff0c\u4e09\u5341\u4e07\u4e24\u94f6\u5b50\uff0c\u5728\u4e2d\u9014\u7ed9\u5f3a\u4eba\u52ab\u4e86\uff0c\u4ed6\u4eec\u662f\u6765\u6c42\u5f20\u4f2f\u4f2f\u642d\u6551\u7684\u3002\u201d\u5f20\u98ce \u5e9c\u9053\uff1a\u201c\u5173\u4f60\u4ec0\u4e48\u4e8b\uff1f\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u6211\u662f\u8fd9\u5b98\u94f6\u7684\u4fdd\u9556\u3002\u201d\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fd9\u6837\u6ca1\u51fa \u606f\uff01\u201d\u6a0a\u82f1\u53e9\u5934\u9053\uff1a\u201c\u8fd9\u662f\u8d2f\u5bb6\u4e09\u5f1f\u7684\u9556\u94f6\uff0c\u6211\u770b\u5728\u5148\u4eba\u60c5\u4efd\u2026\u2026\u5f20\u4f2f\u4f2f\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u5f20\u98ce\u5e9c\u9002\u624d\u672a\u77e5\u6a0a\u82f1\u6765\u610f\uff0c\u4e00\u53e3\u6c14\u5f3a\u81ea\u6491\u4f4f\uff0c\u6b64\u65f6\u5df2\u77e5\u4ed6\u548c\u90a3\u4e24\u4e2a\u519b\u5b98\u5e76\u975e\u654c\u4eba\uff0c\u5fc3\u5934 \u4e00\u677e\uff0c\u771f\u6c14\u4fbf\u6cc4\uff0c\u9762\u8272\u6e10\u6e10\u7070\u767d\uff0c\u6a0a\u82f1\u6025\u5fd9\u4e0a\u524d\u6599\u7406\uff0c\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u4e0d\u7528\u5566\uff0c\u8d81\u6211\u8fd8\u6709\u53e3 \u6c14\uff0c\u5feb\u542c\u6211\u8bf4\u3002\u201d\u6a0a\u82f1\u5fc3\u5934\u4e0d\u5fcd\uff0c\u5c1a\u6b32\u5c3d\u529b\uff0c\u5f20\u98ce\u5e9c\u65a5\u9053\uff1a\u201c\u4f60\u542c\u4e0d\u542c\u8bdd\uff1f\u55ef\uff0c\u4f60\u4e5f\u4e2d\u4e86\u4e94 \u6bd2\u9488\u4e86\uff1f\u5feb\u53bb\u641c\u90a3\u8463\u5bb6\u9a8f\u7684\u8eab\u5b50\uff0c\u5c06\u89e3\u836f\u62ff\u51fa\u6765\u3002\u201d \u3000\u3000\u6a0a\u82f1\u4f4e\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e2d\u6307\u7ea2\u80bf\uff0c\u4e00\u6761\u7ea2\u7ebf\u5df2\u5347\u5230\u638c\u5fc3\uff0c\u60f3\u4e0d\u5230\u6324\u51fa\u6bd2\u8840\u4e4b\u540e\uff0c\u8fd8\u8fd9\u6837\u5389 \u5bb3\uff0c\u53c8\u60f3\u8d77\u5f20\u98ce\u5e9c\u4e2d\u7684\u4e5f\u662f\u8fd9\u79cd\u6bd2\uff0c\u6025\u5fd9\u641c\u7ae5\u5bb6\u9a8f\u7684\u8eab\u5b50\uff0c\u5f20\u98ce\u5e9c\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4e00\u5305\u836f\u4e38\uff0c \u4f60\u541e\u5b83\u4e09\u9897\u3002\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u5f20\u4f2f\u62cd\uff0c\u4f60\u4e5f\u5feb\u541e\uff01\u201d\u5f20\u98ce\u5e9c\u60e8\u7b11\u9053\uff1a\u201c\u65e9\u4e00\u4e2a\u65f6\u8fb0\u6216\u8bb8\u80fd\u6d3b\uff0c \u73b0\u5728\u561b\uff0c\u7eb5\u6709\u8d77\u6b7b\u56de\u751f\u7684\u4ed9\u836f\uff0c\u4e5f\u96be\u6551\u6211\uff01\u201d \u3000\u3000\u6a0a\u82f1\u4e5f\u662f\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u62ac\u5934\u4e00\u77a7\uff0c\u53ea\u89c1\u5f20\u98ce\u5e9c\u7684\u9762\u8272\u5df2\u4ece\u7070\u767d\u53d8\u4e3a\u7600\u9ed1\uff0c\u5fc3\u4e2d\u60b2 \u53f9\uff0c\u90a3\u5305\u89e3\u836f\u8dcc\u4e8e\u5730\u4e0b\uff0c\u53e9\u5934\u9053\uff1a\u201c\u5f20\u4f2f\u4f2f\uff0c\u4f60\u6709\u4ec0\u4e48\u4e8b\u60c5\u8981\u4ea4\u4ee3\u5c0f\u4f84\u7684\uff1f\u201d\u5f20\u98ce\u5e9c\u7b11\u4e00 \u7b11\uff0c\u9053\uff1a\u201c\u6211\u4ec7\u5df2\u4e86\uff0c\u6709\u4ec0\u4e48\u4e8b\u60c5\u6ca1\u6709\u4ea4\u4ee3\u7684\uff1f\u55ef\uff0c\u5c31\u662f\u4f60\u8fd9\u6869\u4e86\uff01\u542c\u7740\uff01\u201d\u5587\u5730\u6495\u4e0b\u534a\u5e45 \u8840\u8863\uff0c\u8bf4\u9053\uff1a\u201c\u62ff\u8fd9\u534a\u5e45\u8840\u8863\u4e0e\u6211\u7684\u5b9d\u5200\u53bb\u89c1\u5f20\u4e39\u67ab\uff0c\u53d6\u56de\u5b98\u94f6\u4e4b\u540e\uff0c\u53eb\u8d2f\u5c45\u9a6c\u4e0a\u8f9e\u5b98\uff01\u201d \u3000\u3000\u6a0a\u82f1\u63a5\u8fc7\u8840\u8863\u5b9d\u5200\uff0c\u95ee\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d\u5f20\u98ce\u5e9c\u53cc\u773c\u4e00\u7741\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5230\u8fd9\u91cc\uff0c \u6ca1\u89c1\u7740\u5c0f\u864e\u5b50\u4e48\uff1f\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u5c0f\u864e\u5b50\u627e\u4f60\u53bb\u4e86\u3002\u201d\u5f20\u98ce\u5e9c\u4e00\u9635\u98a4\u6296\uff0c\u751f\u6b7b\u76f8\u640f\u4e4b\u65f6\uff0c\u4ed6\u6beb \u65e0\u534a\u70b9\u60e7\u610f\uff0c\u542c\u4e86\u6a0a\u82f1\u7684\u8bdd\uff0c\u5374\u7981\u4e0d\u4f4f\u51b7\u610f\u76f4\u900f\u5fc3\u5934\uff0c\u6a0a\u82f1\u9053\uff1a\u201c\u5c0f\u864e\u5b50\u4e00\u5411\u673a\u7075\u2026\u2026\u201d\u5f20 \u98ce\u5e9c\u4e00\u9635\u98a4\u6296\uff0c\u53cc\u773c\u4e00\u5f20\uff0c\u65ad\u65ad\u7eed\u7eed\u4ed6\u8bf4\u9053\uff1a\u201c\u82e5\u7136\u5c0f\u864e\u5b50\u6ca1\u6b7b\uff0c\u4f60\u627e\u7740\u4ed6\uff0c\u5c06\u5b9d\u5200\u4ea4\u4e0e \u4ed6\uff0c\u53eb\u4ed6\u62dc\u5f20\u4e39\u67ab\u4e3a\u5e08\u3002\u201d\u6325\u6325\u624b\u9053\uff1a\u201c\u6211\u4e0e\u4e61\u4eba\u4ea4\u597d\uff0c\u540e\u4e8b\u81ea\u6709\u4e61\u4eba\u6599\u7406\uff0c\u4f60\u53ef\u4ee5\u8d70\u5566\u3002 \u6211\u751f\u62a5\u8840\u4ec7\uff0c\u6b7b\u800c\u65e0\u61be\uff0c\u552f\u4e00\u89c9\u5f97\u9057\u61be\u7684\u5c31\u662f\u6ca1\u6709\u89c1\u7740\u4e8e\u9601\u8001\u548c\u5f20\u4e39\u67ab\uff01\u201d \u3000\u3000\u58f0\u97f3\u8d8a\u8bf4\u8d8a\u5f31\uff0c\u8bf4\u5b8c\u4e4b\u540e\uff0c\u53cc\u76ee\u4e00\u95ed\uff0c\u6a0a\u82f1\u4e0a\u524d\u4e00\u63a2\uff0c\u5df2\u662f\u6ca1\u4e86\u6c14\u606f\uff0c\u6a0a\u82f1\u4e0d\u7531\u5f97\u629a\u5c38 \u5927\u6078\uff0c\u60f3\u4e0d\u5230\u8fd9\u4f4d\u540d\u9707\u4e2d\u5916\u7684\u4eac\u5e08\u7b2c\u4e00\u9ad8\u624b\uff0c\u7adf\u7136\u6b7b\u5728\u5c71\u6751\u77f3\u5ba4\u4e4b\u4e2d\uff0c\u4e34\u6b7b\u4e4b\u65f6\uff0c\u8fde\u4eb2\u751f\u513f \u5b50\u90fd\u6ca1\u89c1\u4e00\u9762\u3002 \u3000\u3000\u6a0a\u82f1\u54ed\u4e86\u4e00\u9635\uff0c\u542c\u89c1\u95e8\u5916\u6089\u6089\u7d22\u7d22\u7684\u58f0\u97f3\uff0c\u5fc3\u5934\u4e00\u9192\uff0c\u60f3\u9053\uff1a\u201c\u6211\u4e0d\u5e94\u518d\u803d\u6401\u5566\uff01\u201d\u85cf \u4e86\u8840\u8863\uff0c\u63d0\u8d77\u5f20\u98ce\u5e9c\u7684\u5b9d\u5200\uff0c\u8d70\u51fa\u95e8\u5916\uff0c\u53ea\u89c1\u90a3\u4e24\u4e2a\u519b\u5b98\u5df2\u7ecf\u9192\u8f6c\uff0c\u6b63\u5728\u63a2\u5934\u63a2\u8111\u5730\u5f20\u671b\uff0c \u731b\u7136\u770b\u89c1\u6a0a\u82f1\u63d0\u7740\u5bd2\u5149\u95ea\u95ea\u7684\u5b9d\u5200\uff0c\u51b2\u51fa\u95e8\u6765\uff0c\u4e24\u4e2a\u519b\u5b98\u5413\u4e86\u4e00\u8df3\uff0c\u53eb\u9053\uff1a\u201c\u8001\u6a0a\uff0c\u600e\u4e48 \u5566\uff1f\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u4e00\u6708\u4e4b\u540e\uff0c\u4f60\u4eec\u5230\u592a\u6e56\u65c1\u8fb9\u7b49\u6211\u3002\u201d\u4e24\u4e2a\u519b\u5b98\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u6a0a\u82f1\u9053\uff1a \u201c\u5f20\u5927\u4eba\u5df2\u5e94\u5141\u5566\uff0c\u4e00\u6708\u4e4b\u540e\uff0c\u5728\u592a\u6e56\u8fb9\u4f60\u542c\u6211\u7684\u6d88\u606f\u3002\u201d\u4e24\u4e2a\u519b\u5b98\u9053\uff1a\u201c\u4e00\u6708\u4e4b\u540e\uff0c\u600e\u80fd \u7b49\u5230\u4e00\u6708\u4e4b\u540e\uff1f\u201d\u6a0a\u82f1\u5fc3\u5934\u706b\u8d77\uff0c\u5c06\u4e24\u4e2a\u519b\u5b98\u4e00\u63a8\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u80fd\u7b49\u5c31\u53e6\u60f3\u6cd5\u53bb\uff0c \u8001\u5b50\u4e0d\u80fd\u5949\u966a\u5566\uff01\u201d\u4e24\u4e2a\u519b\u5b98\u8dcc\u8dcc\u649e\u61a7\u5730\u8ffd\u51fa\u6765\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8001\u6a0a\uff0c\u8001\u6a0a\uff01\u201d\u6708\u5149\u4e4b\u4e0b\uff0c \u9a6c\u58f0\u957f\u5636\uff0c\u6a0a\u82f1\u5df2\u8de8\u4e0a\u9a6c\u80cc\u98de\u8dd1\u4e86\u3002\u8fd9\u4e24\u4e2a\u519b\u5b98\u4e0d\u6562\u56de\u5f20\u98ce\u5e9c\u7684\u77f3\u5c4b\uff0c\u6025\u5fd9\u4e5f\u9a91\u4e86\u9a6c\u53bb\u8ffd\uff0c \u8ffd\u51fa\u6751\u5916\uff0c\u53ea\u89c1\u6a0a\u82f1\u5df2\u5954\u4e0a\u5b98\u9053\uff0c\u75be\u9a70\u800c\u53bb\uff0c\u4e24\u4e2a\u519b\u5b98\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u65e2\u8bf4\u5728\u5927\u6e56\u4e4b \u8fb9\u76f8\u5019\uff0c\u4f55\u4ee5\u4e0d\u5357\u4e0b\u53cd\u800c\u5317\u4e0a\u5462\uff1f\u8fd9\u4e0d\u662f\u6210\u5fc3\u5f00\u73a9\u7b11\u5417\uff1f\u201d\u6a0a\u82f1\u9a6c\u8dd1\u5982\u98ce\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u5c31\u53ea \u770b\u89c1\u4e00\u4e2a\u9ed1\u70b9\uff0c\u4e24\u4e2a\u519b\u5b98\u5446\u7740\u6728\u9e21\uff0c\u8ddf\u5728\u540e\u9762\uff0c\u600e\u6837\u4e5f\u731c\u4e0d\u5230\u6a0a\u82f1\u7684\u5fc3\u610f\u3002 \u3000\u3000\u56db\u5929\u4e4b\u540e\uff0c\u4eac\u57ce\u6765\u4e86\u4e00\u4e2a\u6ee1\u8eab\u98ce\u6c99\u7684\u5ba2\u4eba\uff0c\u8fd9\u4eba\u5c31\u662f\u6a0a\u82f1\u3002\u4ed6\u9a6c\u4e0d\u505c\u8e44\uff0c\u8d76\u4e86\u56db\u65e5\u56db \u591c\uff0c\u5230\u5f97\u4eac\u57ce\uff0c\u53ea\u89c1\u5317\u4eac\u8857\u9053\uff0c\u5230\u5904\u642d\u6709\u811a\u574a\uff0c\u57ce\u697c\u4e0a\u4e5f\u5f20\u706f\u7ed3\u5f69\uff0c\u5199\u7740\u201c\u4e0a\u7687\u590d\u4f4d\uff0c\u666e\u5929 \u540c\u5e86\u201d\u7684\u5b57\u6837\uff0c\u53ef\u662f\u8857\u5934\u884c\u4eba\u5be5\u843d\uff0c\u4eba\u4eba\u9762\u8272\u9634\u6c89\uff0c\u8bf4\u50cf\u529e\u846c\u4e8b\u5012\u5dee\u4e0d\u591a\uff0c\u54ea\u6709\u4e00\u70b9\u559c\u5e86\u7684 \u6837\u513f\u3002 \u3000\u3000\u6a0a\u82f1\u8d70\u4e0a\u9152\u697c\uff0c\u9152\u697c\u56db\u58c1\u90fd\u8d34\u6709\u201c\u83ab\u8c08\u56fd\u4e8b\u201d\u7684\u7eb8\u6761\uff0c\u9152\u697c\u4e0a\u53ea\u6709\u7a00\u7a00\u758f\u758f\u7684\u51e0\u53f0\u5ba2 \u4eba\uff0c\u90fd\u5728\u53fd\u53fd\u55b3\u55b3\u5730\u4f4e\u58f0\u8c08\u8bba\uff0c\u6a0a\u82f1\u53eb\u4e86\u4e00\u58f6\u767d\u9152\uff0c\u4e24\u65a4\u725b\u8089\uff0c\u51dd\u795e\u9759\u542c\uff0c\u53ea\u542c\u5f97\u4eba\u4eba\u90fd\u5728 \u4e92\u76f8\u6253\u63a2\u4e8e\u8c26\u7684\u6d88\u606f\uff0c\u58c1\u4e0a\u867d\u8d34\u6709\u201c\u83ab\u8c08\u56fd\u4e8b\u201d\u7684\u5b57\u6761\uff0c\u8fd9\u4e9b\u4eba\u5374\u6beb\u4e0d\u5728\u610f\uff0c\u4e3a\u4e86\u6253\u542c\u4e8e\u8c26 \u7684\u6d88\u606f\uff0c\u4ed6\u4eec\u7adf\u5b81\u613f\u5192\u6027\u547d\u4e4b\u5fe7\u3002 \u3000\u3000\u6a0a\u82f1\u5728\u9152\u697c\u542c\u4e86\u4e00\u4f1a\uff0c\u53c8\u5230\u5404\u5904\u5e73\u65e5\u70ed\u95f9\u7684\u57f8\u6240\uff0c\u5982\u5929\u6865\u7b49\u5730\u6e9c\u4e86\u4e00\u8d9f\uff0c\u5bf9\u4eac\u5e08\u65b0\u4e8b\uff0c \u7ea6\u7565\u77e5\u9053\u4e86\u4e00\u70b9\u6897\u6982\u3002 \u3000\u3000\u9646\u5c55\u9e4f\u4e4b\u8a00\u4e0d\u5047\uff0c\u7948\u9547\u679c\u7136\u662f\u8c0b\u5b9a\u800c\u52a8\uff0c\u4ed6\u672c\u6765\u662f\u88ab\u5f1f\u5f1f\u7948\u94ae\u56da\u7981\u5728\u7687\u57ce\u91cc\u7684\u5357\u5bab\u5185\uff0c \u7948\u94ae\u8fd8\u7279\u522b\u6d3e\u4e86\u4e00\u8d1f\u5927\u5c06\u9756\u8fdc\u4f2f\u738b\u9aa5\u5b88\u5907\uff0c\u54ea\u77e5\u7948\u9547\u5904\u5fc3\u79ef\u8651\uff0c\u52fe\u7ed3\u671d\u81e3\uff0c\u56fe\u8c0b\u590d\u8f9f\uff0c\u5230\u4e86 \u540e\u6765\uff0c\u8fde\u738b\u9aa5\u4e5f\u6210\u4e86\u4ed6\u7684\u515a\u7fbd\uff0c\u5c31\u5728\u666f\u6cf0\uff08\u660e\u4ee3\u5b97\u7948\u94ae\u56fd\u53f7\uff09\u516b\u5e74\uff0c\u5143\u5bb5\u4e4b\u540e\u7684\u7b2c\u4e8c\u65e5\u665a \u95f4\uff0c\u738b\u9aa5\u6253\u5f00\u5357\u5bab\uff0c\u7eb3\u5165\u4eac\u519b\uff0c\u653b\u8fdb\u7687\u5bab\uff0c\u95ef\u5165\u4e1c\u534e\u95e8\uff0c\u7b2c\u4e8c\u65e5\u65e9\u671d\uff0c\u767e\u5b98\u4e0a\u671d\uff0c\u53ea\u89c1\u7948\u9547 \u5df2\u7ecf\u590d\u767b\u7687\u4f4d\uff0c\u540c\u65f6\u5ba3\u5e03\u7948\u94ae\u5df2\u7ecf\u201c\u9a7e\u5d29\u201d\u4e86\uff0c\u7948\u9547\u6539\u5143\u201c\u5929\u987a\u201d\u5927\u8d66\u5929\u4e0b\uff0c\u4f46\u4e5f\u5c31\u5728\u8fd9\u4e00 \u5929\uff0c\u5c31\u5728\u4e0b\u201c\u5927\u8d66\u5929\u4e0b\u201d\u8bcf\u4e66\u7684\u540c\u65f6\uff0c\u5374\u5c06\u4e8e\u8c26\u6253\u5165\u4e86\u5929\u7262\u3002 \u3000\u3000\u4eac\u57ce\u5185\u4eba\u4eba\u55df\u53f9\uff0c\u4e2a\u4e2a\u6028\u6124\u3002\u65e0\u6570\u6c11\u5bb6\u711a\u9999\u7977\u544a\u5929\u5730\uff0c\u76fc\u4e0a\u5929\u4fdd\u4f51\u4e8e\u8c26\u3002\u4eac\u57ce\u5185\u8fd8\u4f20\u51fa \u4e00\u4e2a\u98ce\u58f0\uff0c\u8bf4\u662f\u6709\u8bb8\u591a\u4fa0\u58eb\uff0c\u56fe\u8c0b\u52ab\u72f1\u3002 \u3000\u3000\u5c31\u5728\u5929\u7262\u4e25\u5bc6\u6212\u5907\u7684\u665a\u4e0a\uff0c\u6709\u4e00\u4e2a\u591c\u884c\u4eba\u6084\u6084\u6e9c\u5230\u5929\u7262\u9644\u8fd1\uff0c\u8fd9\u4eba\u4fbf\u662f\u6a0a\u82f1\u3002 \u3000\u3000\u5929\u7262\u5916\u8b66\u536b\u7a7f\u68ad\u5f80\u6765\uff0c\u6a0a\u82f1\u6b63\u81ea\u601d\u91cf\uff1a\u5982\u4f55\u80fd\u591f\u8fdb\u53bb\uff1f\u5ffd\u542c\u5f97\u91cc\u9762\u4e00\u58f0\u53f7\u89d2\uff0c\u767b\u65f6\u74e6\u9762 \u4e0a\u73b0\u51fa\u5e62\u5e62\u9ed1\u5f71\uff0c\u5411\u897f\u5317\u89d2\u8702\u62e5\u800c\u53bb\uff0c\u6a0a\u82f1\u6697\u6697\u7eb3\u7f55\uff0c\u4f46\u8fd9\u6b63\u662f\u5343\u8f7d\u4e00\u65f6\u7684\u65f6\u673a\uff0c\u4e0d\u53ef\u60dc \u8fc7\uff0c\u5728\u6697\u5668\u56ca\u4e2d\u53d6\u51fa\u4e24\u9897\u98de\u8757\u77f3\uff0c\u5411\u5929\u4e00\u63b7\uff0c\u4e24\u77f3\u76f8\u649e\uff0c\u53d1\u51fa\u58f0\u97f3\uff0c\u5899\u89d2\u7684\u4e24\u4e2a\u536b\u58eb\u6025\u5fd9\u8df3 \u51fa\u5bdf\u770b\uff0c\u6a0a\u82f1\u98de\u8eab\u4e00\u63a0\uff0c\u7acb\u523b\u8dc3\u4e0a\u5899\u5934\u3002\u8fd9\u665a\u661f\u6708\u5143\u5149\uff0c\u6a0a\u82f1\u7a7f\u7740\u4e00\u8eab\u9ed1\u8272\u7684\u591c\u884c\u8863\uff0c\u4ed6\u7684 \u8f7b\u529f\u63d0\u7eb5\u672f\u53c8\u6781\u9ad8\u660e\uff0c\u4e24\u4e2a\u5b88\u95e8\u7684\u536b\u571f\u4e0d\u8fc7\u4e09\u6d41\u89d2\u8272\uff0c\u7adf\u7136\u6ca1\u6709\u53d1\u73b0\u3002 \u3000\u3000\u6a0a\u82f1\u5728\u74e6\u9762\u4e0a\u86c7\u884c\u5154\u4f0f\uff0c\u9690\u9690\u542c\u5f97\u8fdc\u5904\u6709\u547c\u5578\u4e4b\u58f0\uff0c\u521a\u722c\u8fc7\u4e24\u91cd\u74e6\u9762\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u4f4e\u58f0 \u53eb\u9053\uff1a\u201c\u5929\u987a\u201d\uff0c\u6a0a\u82f1\u77e5\u662f\u7262\u4e2d\u8fa8\u8ba4\u81ea\u5df1\u4eba\u7684\u6697\u53f7\uff0c\u542b\u7cca\u8bf4\u4e86\u4e24\u4e2a\u5b57\uff0c\u90a3\u4eba\u559d\u9053\uff1a\u201c\u4ec0\u4e48\uff1f \u8bf4\u6e05\u695a\u70b9\uff01\u201d\u6a0a\u82f1\u4e00\u8dc3\u800c\u51fa\uff0c\u4e00\u652f\u8896\u7bad\u5c04\u5165\u4ed6\u7684\u5589\u5499\uff0c\u90a3\u4eba\u8fd8\u672a\u558a\u5f97\u51fa\u58f0\uff0c\u767b\u65f6\u4e86\u7ed3\uff0c\u6a0a\u82f1 \u5265\u4e0b\u4ed6\u7684\u8863\u88f3\u6362\u4e0a\uff0c\u8df3\u4e0b\u53bb\u4f0f\u5728\u8fc7\u9053\u6697\u89d2\u3002\u4e0d\u4e45\u4fbf\u6709\u4e00\u540d\u72f1\u5352\u63d0\u706f\u8d70\u8fc7\uff0c\u6a0a\u82f1\u4e00\u8df3\u800c\u51fa\uff0c\u5c06 \u5200\u5c16\u5728\u72f1\u5352\u9762\u95e8\u4e00\u6643\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u4e8e\u9601\u8001\u56da\u5728\u54ea\u513f\uff1f\u201d\u90a3\u72f1\u5352\u5413\u4e86\u4e00\u8df3\uff0c\u5374\u7acb\u523b\u7709\u5f00\u773c \u7b11\uff0c\u9053\uff1a\u201c\u4f60\u662f\u6551\u4e8e\u9601\u8001\u7684\u5417\uff1f\u4ed6\u5728\u516b\u53f7\u6b7b\u7262\u3002\u4ece\u8fd9\u513f\u76f4\u8d70\uff0c\u5230\u8f6c\u89d2\u4e4b\u5904\uff0c\u5411\u53f3\u8fb9\u8d70\uff0c\u8d70\u5230 \u7b2c\u516b\u95f4\u623f\u5b50\u4fbf\u662f\u4e86\u3002\u201d\u6a0a\u82f1\u6536\u4e86\u5b9d\u5200\uff0c\u6b63\u60f3\u4e3e\u6b65\uff0c\u90a3\u72f1\u5352\u9053\uff1a\u201c\u5582\uff0c\u4eca\u665a\u7684\u53e3\u53f7\u662f\u5929\u987a\u4e07 \u5e74\uff0c\u8bb0\u7740\u4e86\uff01\u201d \u3000\u3000\u6a0a\u82f1\u4f9d\u8a00\u4fbf\u8d70\uff0c\u6cbf\u9014\u6709\u4eba\u559d\u95ee\u53e3\u53f7\uff0c\u6a0a\u82f1\u5bf9\u7b54\u5982\u6d41\uff0c\u65e0\u751a\u963b\u6ede\uff0c\u5176\u4e2d\u6709\u4e00\u4e24\u4e2a\u8001\u72f1\u5352\uff0c \u53d1\u73b0\u58f0\u97f3\u964c\u751f\uff0c\u5374\u4e5f\u4e0d\u95ee\uff0c\u8d70\u5230\u7b2c\u516b\u53f7\u6b7b\u7262\uff0c\u53ea\u89c1\u95e8\u524d\u4e00\u4e2a\u6301\u8861\u63d0\u7740\u4e00\u53e3\u957f\u5251\uff0c\u6a0a\u82f1\u51b7\u4e0d\u9632 \u5730\u4e00\u6251\u800c\u4e0a\uff0c\u63d0\u5200\u4fbf\u62b9\uff0c\u90a3\u5b88\u536b\u8eab\u624b\u77eb\u6377\u4e4b\u6781\uff0c\u4e00\u95ea\u95ea\u5f00\uff0c\u6a0a\u82f1\u4e00\u51fb\u4e0d\u4e2d\uff0c\u6697\u53eb\u7cdf\u4e86\uff0c\u90a3\u536b \u58eb\u56de\u8fc7\u5934\u6765\u3002\u5374\u5e76\u4e0d\u8fd8\u51fb\uff0c\u53cd\u800c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5feb\u5728\u6211\u4e0d\u81f4\u547d\u7684\u5730\u65b9\u63b7\u4e00\u5200\uff01\u201d\u6a0a\u82f1\u6014\u4e86 \u4e00\u6014\u4e86\u7acb\u523b\u604d\u7136\u5927\u609f\uff0c\u8fd9\u4f8d\u536b\u662f\u6709\u5fc3\u8ba9\u81ea\u5df1\u6551\u51fa\u4e8e\u8c26\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u53cd\u800c\u4e0d\u5fcd\u4e0b\u624b\uff0c\u90a3\u5b88\u536b \u9053\uff1a\u201c\u5feb\u4e9b\uff0c\u518d\u8fc7\u534a\u4e2a\u65f6\u8fb0\uff0c\u6211\u4fbf\u6362\u73ed\u4e86\uff01\u201d\u6a0a\u82f1\u4e3e\u5200\u4e00\u63b7\uff0c\u628a\u5b88\u536b\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u5212\u6df1\u4e00 \u4e9b\uff01\u201d\u62c9\u7740\u6a0a\u82f1\u7684\u624b\u5728\u817f\u4e0a\u91cd\u91cd\u4e00\u5212\uff0c\u53c8\u81ea\u5df1\u70b9\u4e86\u8170\u95f4\u7684\u54d1\u7a74\uff0c\u77aa\u7740\u4e24\u773c\u775b\uff0c\u71ac\u7740\u75bc\u75db\uff0c\u9762 \u4e0a\u5374\u89c4\u51fa\u7b11\u5bb9\u3002 \u3000\u3000\u6a0a\u82f1\u5fc3\u4e2d\u6168\u53f9\uff0c\u524a\u5f00\u94c1\u9501\uff0c\u53ea\u542c\u5f97\u91cc\u9762\u6709\u4e00\u4e2a\u82cd\u51c9\u7684\u58f0\u97f3\uff0c\u4f4e\u58f0\u541f\u9053\uff1a\u201c\u5343\u9524\u4e07\u51fb\u51fa\u6df1 \u5c71\uff0c\u70c8\u706b\u711a\u70e7\u82e5\u7b49\u95f2\uff0c\u7c89\u9aa8\u788e\u8eab\u90fd\u4e0d\u6015\uff0c\u8981\u7559\u6e05\u767d\u5728\u4eba\u95f4\u3002\u201d\u8fd9\u6b63\u662f\u5929\u4e0b\u4f20\u8bf5\u7684\uff0c\u4e8e\u8c26\u5728\u571f \u6728\u5821\u4e8b\u53d8\u524d\u5915\uff0c\u501f\u548f\u77f3\u7070\u800c\u8868\u767d\u80f8\u4e2d\u62b1\u8d1f\u7684\u540d\u8bd7\u3002\u6b63\u662f\uff1a \u3000\u3000\u80f8\u4e2d\u5b58\u6b63\u6c14\uff0c\u4e00\u6b7b\u53c8\u4f55\u8f9e\u3002 \u3000\u3000\u6b32\u77e5\u6a0a\u82f1\u80fd\u5426\u6551\u51fa\u4e8e\u8c26\uff0c\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e09\u56de\u3000\u5927\u68d2\u5be8\u65d7\u3000\u7981\u57ce\u6765\u5927\u76d7\u3000\u6563\u82b1\u62d2\u654c\u3000\u591c\u534a\u5931\u4eba\u5934 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e09\u56de\u3000\u5927\u68d2\u5be8\u65d7\u3000\u7981\u57ce\u6765\u5927\u76d7\u3000\u6563\u82b1\u62d2\u654c\u3000\u591c\u534a\u5931\u4eba\u5934 \u3000\u3000\u6a0a\u82f1\u8f7b\u8f7b\u63a8\u5f00\u94c1\u95e8\uff0c\u6478\u8fdb\u7262\u5185\uff0c\u53ea\u542c\u5f97\u4e8e\u8c26\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u73e0\u513f\u4e48\uff1f\u4f60\u600e\u4e48\u4e0d\u542c\u4e3a\u7236\u4e4b \u8a00\u53c8\u56de\u6765\u4e86\u3002\u201d\u6a0a\u82f1\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4f46\u65f6\u673a\u7d27\u8feb\uff0c\u65e0\u6687\u95ee\u4ed6\u8c01\u662f\u201c\u73e0\u513f\u201d\uff0c\u51e0\u65f6\u6765\u8fc7\uff1f\u6025\u5fd9\u64e6\u71c3 \u706b\u77f3\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u4e8e\u9601\u8001\uff0c\u4f60\u6ca1\u53d7\u4f24\u4e48\uff1f\u6211\u80cc\u4f60\u51fa\u53bb\u3002\u201d \u3000\u3000\u706b\u77f3\u7684\u5fae\u5149\u5212\u7834\u4e86\u7262\u623f\u7684\u9ed1\u6697\uff0c\u53ea\u89c1\u4e8e\u8c26\u767d\u53d1\u82cd\u82cd\uff0c\u62ab\u67b7\u5e26\u9501\uff0c\u76d8\u819d\u7aef\u5750\u5730\u4e0a\uff0c\u53cc\u7738\u70af \u70af\uff0c\u72b9\u81ea\u9732\u51fa\u51db\u7136\u4e0d\u53ef\u4fb5\u72af\u7684\u795e\u5149\uff0c\u6c89\u58f0\u542c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u6a0a\u82f1\u6cea\u54bd\u5fc3\u9178\uff0c\u5c48\u4e0b\u534a\u819d\uff0c\u4f4e \u58f0\u7980\u9053\uff1a\u201c\u5bb6\u7236\u662f\u4ee5\u524d\u670d\u4f8d\u8fc7\u4f60\u7684\u5e26\u5200\u4f8d\u536b\u6a0a\u4fca\u3002\u201d\u4e8e\u8c26\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u662f\u6a0a\u5fe0\u4e4b\u4f84\uff0c\u6a0a \u4fca\u4e4b\u5b50\uff0c\u4f60\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u6211\u6765\u6551\u4f60\u51fa\u72f1\u3002\u201d\u62d4\u51fa\u5f20\u98ce\u5e9c\u7559\u7ed9\u4ed6\u7684\u7f05\u5200\uff0c\u4fbf\u60f3\u4e0a\u524d \u65a9\u65ad\u4e8e\u8c26\u8eab\u4e0a\u7684\u67b7\u9501\u3002\u4e8e\u8c26\u9053\uff1a\u201c\u8fd9\u662f\u671d\u5ef7\u7684\u5211\u5177\uff0c\u5c82\u53ef\u80e1\u6765\uff01\u201d\u6a0a\u82f1\u5927\u6025\uff0c\u9053\uff1a\u201c\u4e0d\u628a\u8fd9 \u52b3\u4ec0\u5b50\u5f04\u65ad\uff0c\u54b1\u4eec\u5982\u4f55\u80fd\u591f\u8d8a\u72f1\uff1f\u201d\u4e8e\u8c26\u53cc\u773c\u4e00\u5f20\uff0c\u65ad\u7136\u8bf4\u9053\uff1a\u201c\u6211\u662f\u671d\u5ef7\u5927\u81e3\uff0c\u4e34\u5927\u8282\u800c \u5fd7\u4e0d\u53ef\u593a\uff0c\u5c82\u80fd\u505a\u8d8a\u72f1\u7684\u9003\u72af\uff1f\u201d\u6a0a\u82f1\u6599\u4e0d\u5230\u4ed6\u5982\u6b64\u201c\u8fc2\u8150\u201d\uff0c\u6025\u9053\uff1a\u201c\u5927\u4eba\u82e5\u4e0d\u8d8a\u72f1\uff0c\u8fd9 \u51a4\u72f1\u8981\u60f3\u5e73\u53cd\uff0c\u53ef\u662f\u7edd\u96be\u6307\u671b\u3002\u201d\u4e8e\u8c26\u54c8\u54c8\u4e00\u7b11\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u82e5\u987e\u60dc\u6027\u547d\uff0c\u5f53\u521d\u4e5f\u4e0d\u6d3e \u9063\u4e91\u91cd\u5230\u74e6\u523a\u53bb\u8fce\u7687\u4e0a\u56de\u6765\u4e86\u3002\u6211\u65e9\u5df2\u6599\u5230\u4eca\u65e5\u3002\u6a0a\u8d24\u4f84\uff0c\u4f60\u8d70\u5427\uff01\u201d\u6a0a\u82f1\u54ea\u80af\u4fbf\u8d70\uff0c\u4e8e\u8c26 \u6012\u9053\uff1a\u201c\u6211\u610f\u5df2\u51b3\uff0c\u8a93\u4e0d\u8d8a\u72f1\uff01\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u5927\u4eba\uff0c\u4f60\u5c31\u4e0d\u4e3a\u5929\u4e0b\u82cd\u751f\u7740\u60f3\uff1f\u201d\u4e8e\u8c26\u9053\uff1a \u201c\u6211\u5e74\u8fc7\u516d\u65ec\uff0c\u5373\u7b97\u4e0d\u6b7b\uff0c\u4e5f\u5df2\u7ecf\u662f\u6cb9\u5c3d\u706f\u67af\uff0c\u65e0\u80fd\u4e3a\u529b\u4e86\u3002\u4e2d\u534e\u513f\u5973\uff0c\u4ee3\u6709\u82f1\u8c6a\uff0c\u6b7b\u4e86\u4e00 \u4e2a\u4e8e\u8c26\uff0c\u8fd8\u6709\u5343\u767e\u4e2a\u4e8e\u8c26\uff0c\u4f55\u987b\u4f60\u4e3a\u5929\u4e0b\u82cd\u751f\u4f5c\u675e\u4eba\u4e4b\u5fe7\u3002\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u5982\u6b64\u6b7b\u6cd5\uff0c\u5c82\u975e\u4e0d \u503c\uff1f\u201d\u4e8e\u8c26\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u4e0d\u503c\uff1f\u82e5\u8bf4\u4e0d\u503c\uff0c\u5cb3\u6b66\u7a46\u738b\u5f53\u65f6\u4ee5\u83ab\u987b\u6709\u7684\u7f6a\u540d\u5c48\u6b7b\uff0c\u53c8\u8be5\u5982 \u4f55\uff1f\u4ed6\u624b\u63e1\u767e\u4e07\u519b\u7b26\uff0c\u5c1a\u81ea\u4e0d\u80af\u574f\u4e86\u671d\u5ef7\u5236\u5ea6\uff0c\u7518\u613f\u53d7\u5211\uff0c\u6211\u867d\u4e0d\u6562\u6bd4\u62df\u524d\u8d24\uff0c\u4ea6\u5f53\u6548 \u6cd5\uff01\u201d\u8981\u77e5\u4e8e\u8c26\u82f1\u5e74\u51fa\u4ed5\uff0c\u76f4\u505a\u81f3\u9601\u90e8\u5c1a\u4e66\uff0c\u51e0\u5341\u5e74\u6765\uff0c\u90a3\u6b63\u7edf\u7684\u5fe0\u541b\u89c2\u5ff5\u5df2\u6df1\u5165\u8111\u6d77\uff0c\u6a0a \u82f1\u60f3\u5728\u7acb\u8c08\u4e4b\u95f4\uff0c\u5c06\u4ed6\u8bf4\u670d\uff0c\u90a3\u662f\u4e07\u4e07\u4e0d\u80fd\u3002 \u3000\u3000\u6a0a\u82f1\u5c1a\u6b32\u8fdb\u8a00\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u90a3\u4e2a\u4e3a\u81ea\u5df1\u6240\u4f24\uff0c\u53c8\u81ea\u5df1\u70b9\u4e86\u54d1\u7a74\u7684\u4f8d\u536b\u5728\u5730\u4e0a\u6eda\u6765\u6eda\u53bb\u7684 \u58f0\u97f3\uff0c\u6a0a\u82f1\u77e5\u662f\u4ed6\u6545\u610f\u793a\u8b66\uff0c\u6025\u9053\uff1a\u201c\u5927\u4eba\uff0c\u5927\u4eba\uff01\u201d\u4e8e\u8c26\u559d\u9053\uff1a\u201c\u5feb\u8d70\uff0c\u4f60\u82e5\u4e0d\u8d70\uff0c\u6211\u5c31 \u5148\u78b0\u6b7b\u5728\u4f60\u9762\u524d\uff01\u201d\u6a0a\u82f1\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u9601\u8001\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d\u4e8e\u8c26\u9053\uff1a\u201c\u6211\u65e0\u61be\u4e8e \u5929\uff0c\u65e0\u6028\u4e8e\u4eba\uff0c\u6b7b\u5f97\u5176\u6240\uff0c\u5c1a\u6709\u4f55\u8a00\uff1f\u5feb\u8d70\uff01\u201d\u6a0a\u82f1\u63a9\u9762\u8f6c\u8eab\uff0c\u53ea\u542c\u5f97\u4e8e\u8c26\u5728\u80cc\u540e\u8bf4\u9053\uff1a \u201c\u53ea\u6709\u4e00\u4e8b\uff0c\u8bf7\u4f60\u4ee3\u52b3\u3002\u201d\u6a0a\u82f1\u505c\u4e0b\u811a\u6b65\uff0c\u542c\u5f97\u4e8e\u8c26\u8bf4\u9053\uff1a\u201c\u4f60\u53bb\u592a\u6e56\u5bfb\u89c5\u5f20\u4e39\u67ab\uff0c\u53eb\u4ed6\u8d76 \u5feb\u9003\u547d\u3002\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u9601\u8001\u653e\u5fc3\uff0c\u6b64\u4e8b\u6211\u8d70\u5f53\u505a\u5230\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5916\u9762\u7684\u7262\u95e8\u5df2\u88ab\u4eba\u4e00\u811a\u8e22 \u5f00\uff0c\u7eb7\u7eb7\u53eb\u9053\uff1a\u201c\u5feb\u6765\u5440\uff01\u6709\u4eba\u52ab\u72f1\uff01\u201d\u6a0a\u82f1\u5c06\u7f05\u5200\u6325\u534a\u4e2a\u5706\u5f27\uff0c\u4e00\u62db\u201c\u591c\u6218\u516b\u65b9\u201d\uff0c\u593a\u95e8 \u6b7b\u95ef\uff0c\u53ea\u542c\u5f97\u545b\u5577\u5577\u4e00\u7247\u65ad\u91d1\u664f\u7389\u4e4b\u58f0\uff0c\u90a3\u7f05\u5200\u950b\u5229\u4e4b\u6781\uff0c\u5916\u9762\u6765\u7684\u4e0d\u8fc7\u662f\u4e8c\u4e09\u6d41\u7684\u89d2\u8272\uff0c \u624b\u4e2d\u5175\u5668\u88ab\u7f05\u5200\u622a\u65ad\uff0c\u5413\u5f97\u6025\u5fd9\u540e\u9000\u3002\u6a0a\u82f1\u7eb5\u8eab\u4e00\u8dc3\uff0c\u7acb\u5373\u8df3\u4e0a\u74e6\u9762\u3002\u84e6\u542c\u5f97\u4e00\u58f0\u5927\u559d\uff1a \u201c\u54ea\u91cc\u8d70\uff1f\u201d\u91d1\u5200\u5288\u98ce\u4e4b\u58f0\uff0c\u5df2\u5230\u8111\u540e\u3002 \u3000\u3000\u6a0a\u82f1\u659c\u8eab\u6ed1\u6b65\uff0c\u53cd\u624b\u4e00\u5200\uff0c\u53ea\u542c\u5f97\u201c\u54e8\u201d\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u6a0a\u82f1\u7684\u7f05\u5200\uff0c\u5e76\u6ca1\u6709\u5c06\u654c \u4eba\u7684\u5175\u5203\u622a\u65ad\uff0c\u864e\u53e3\u53cd\u800c\u7ed9\u9707\u5f97\u53d1\u70ed\uff0c\u5b9a\u775b\u770b\u65f6\uff0c\u53ea\u89c1\u6765\u7684\u662f\u4e00\u4e2a\u9ed1\u8863\u536b\u571f\uff0c\u4f7f\u7684\u662f\u4e00\u67c4\u539a \u80cc\u65ab\u5c71\u5200\uff0c\u8db3\u6709\u56db\u4e94\u5341\u65a4\uff0c\u8fd9\u79cd\u5927\u5200\uff0c\u672c\u6765\u662f\u5728\u51b2\u950b\u9677\u9635\u4e4b\u65f6\uff0c\u9a6c\u4e0a\u4ea4\u950b\u4e4b\u7528\uff0c\u8fd9\u536b\u571f\u7adf\u7136 \u4e3e\u91cd\u82e5\u8f7b\uff0c\u5e26\u7740\u8fd9\u6837\u6c89\u91cd\u7684\u65ab\u5c71\u5200\uff0c\u7eb5\u9ad8\u8dc3\u4f4e\uff0c\u62ff\u6765\u5f53\u4f5c\u591c\u884c\u4eba\u7684\u8f7b\u4fbf\u5175\u5668\uff0c\u53ea\u8fd9\u4e00\u70b9\uff0c\u5df2 \u8db3\u89c1\u529f\u529b\u975e\u51e1\u3002 \u3000\u3000\u6a0a\u82f1\u6697\u6697\u5403\u60ca\uff0c\u90a3\u9ed1\u8863\u536b\u58eb\u66f4\u662f\u8be7\u5f02\u4e0d\u5c0f\uff0c\u8fd9\u9ed1\u8863\u536b\u58eb\u672c\u662f\u5fa1\u524d\u7684\u4e00\u7b49\u5e26\u5200\u4f8d\u536b\uff0c\u7279\u522b \u8c03\u6765\u770b\u5b88\u5929\u7262\u7684\uff0c\u88ab\u6a0a\u82f1\u7684\u5b9d\u5200\u4e00\u78d5\uff0c\u90a3\u539a\u80cc\u65ab\u5c71\u5200\u88ab\u65ab\u4e86\u4e00\u9053\u7f3a\u53e3\uff0c\u864e\u53e3\u4e5f\u7ed9\u9707\u5f97\u75bc\u75db\u96be \u5f53\uff0c\u5175\u5668\u4e5f\u51e0\u4e4e\u628a\u63e1\u4e0d\u4f4f\uff0c\u6025\u5fd9\u5927\u58f0\u53eb\u9053\uff1a\u201c\u70b9\u5b50\u5728\u8fd9\u513f\u4e86\uff0c\u5feb\u6765\u4eba\u5475\uff01\u201d \u3000\u3000\u6a0a\u82f1\u4e00\u62db\u201c\u957f\u86c7\u51fa\u6d1e\u201d\uff0c\u7f05\u5200\u5411\u524d\u4e00\u5410\uff0c\u659c\u8eab\u6e38\u8d70\uff0c\u4fa7\u9762\u4e00\u58f0\u5927\u559d\uff0c\u4e24\u9897\u5706\u5ffd\u5ffd\u7684\u94c1 \u7403\uff0c\u6251\u9762\u6253\u6765\uff0c\u6a0a\u82f1\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u6a2a\u5200\u4e00\u78d5\uff0c\u201c\u5f53\u5f53\u201d\u4e24\u58f0\uff0c\u90a3\u4e24\u9897\u94c1\u7403\uff0c\u53c8\u7f29\u4e86 \u56de\u53bb\uff0c\u6a0a\u82f1\u4e3e\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5de6\u9762\u53c8\u6765\u4e86\u4e00\u540d\u9ed1\u8863\u536b\u58eb\uff0c\u90a3\u4e24\u9897\u94c1\u7403\u5e76\u4e0d\u662f\u6697\u5668\uff0c\u5374\u662f\u94fe\u5b50\u9524 \u7684\u9524\u5934\uff0c\u94fe\u5b50\u9524\u662f\u4e00\u95e8\u5f88\u96be\u4f7f\u7528\u7684\u5175\u5668\uff0c\u8fd9\u4eba\u80fd\u73a9\u5f97\u5982\u6b64\u7eaf\u719f\u5de7\u5999\uff0c\u672c\u9886\u81ea\u4ea6\u4e0d\u51e1\u3002 \u3000\u3000\u6a0a\u82f1\u8fde\u5288\u4e09\u5200\uff0c\u90a3\u4f7f\u94fe\u5b50\u9524\u7684\u536b\u58eb\u5728\u79bb\u4e08\u4f59\u4e4b\u5916\uff0c\u821e\u52a8\u94c1\u94fe\uff0c\u5de6\u906e\u5de6\u62e6\uff0c\u94fe\u5b50\u9524\u517c\u6709\u957f \u77ed\u5175\u5668\u4e4b\u957f\uff0c\u90a3\u6761\u94c1\u94fe\u957f\u8fbe\u516b\u5c3a\u6709\u591a\uff0c\u821e\u52a8\u8d77\u6765\uff0c\u5468\u56f4\u4e08\u8bb8\u4e4b\u5730\uff0c\u90fd\u662f\u9524\u5934\u53ef\u53ca\u7684\u8303\u56f4\uff0c\u6a0a \u82f1\u5728\u8feb\u5207\u4e4b\u95f4\u51b2\u4e0d\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u4f7f\u539a\u80cc\u65ab\u5c71\u5200\u7684\u536b\u58eb\u8fc5\u5373\u8d76\u4e0a\uff0c\u547c\u7684\u4e00\u5200\uff0c\u62e6\u8170\u622a\u65a9\uff0c\u6a0a\u82f1\u4f0f\u8eab\u4e00\u95ea\uff0c\u6a2a\u5200\u67b6\u5f00\uff0c\u84e6 \u542c\u5f97\u53f3\u8fb9\u53c8\u662f\u4e00\u58f0\u5927\u559d\uff0c\u4e00\u6761\u9ed1\u5f71\u500f\u5730\u6253\u5165\u6218\u56f4\uff0c\u624b\u4e2d\u5175\u5668\u4e00\u4e3e\uff0c\u7adf\u631f\u7740\u4e24\u80a1\u52b2\u98ce\uff0c\u6307\u7740\u6a0a \u82f1\u5de6\u53f3\u4e24\u80a9\u7684\u80a9\u4e95\u7a74\u3002\u6a0a\u82f1\u6025\u5fd9\u4f7f\u4e2a\u201c\u56de\u98ce\u6446\u67f3\u201d\u7684\u8eab\u6cd5\uff0c\u4e00\u5f2f\u8170\uff0c\u659c\u63d2\u67f3\uff0c\u5200\u78d5\u94fe\u5b50\u9524\uff0c \u811a\u8e22\u65ab\u5c71\u5200\uff0c\u582a\u582a\u907f\u8fc7\u3002\u518d\u633a\u8170\u770b\u65f6\uff0c\u53ea\u89c1\u6765\u7684\u662f\u4e2a\u77ed\u5c0f\u7cbe\u608d\u7684\u6c49\u5b50\uff0c\u4f7f\u7684\u662f\u4e00\u5bf9\u4e8c\u5c3a\u516b\u5bf8 \u957f\u7684\u5224\u5b98\u7b14\u3002\u4f7f\u8fd9\u79cd\u77ed\u5c0f\u5175\u5668\u70b9\u7a74\u7684\u4eba\uff0c\u90a3\u81ea\u7136\u662f\u4e00\u4f4d\u70b9\u7a74\u7684\u9ad8\u624b\u4e86\u3002 \u3000\u3000\u6a0a\u82f1\u7eb5\u7136\u672c\u4e8b\u518d\u52a0\u4e00\u500d\uff0c\u6b64\u65f6\u6b64\u9645\uff0c\u4e5f\u96be\u4ee5\u51b2\u51fa\u5305\u56f4\uff0c\u8fd9\u4e09\u540d\u4e00\u7b49\u5fa1\u524d\u4f8d\u536b\uff0c\u6240\u7528\u7684\u5175 \u5668\u90fd\u5404\u6709\u72ec\u7279\u7684\u529f\u80fd\uff0c\u539a\u80cc\u65ab\u5c71\u5200\u662f\u91cd\u5175\u5668\uff0c\u4e0d\u5fcc\u5b9d\u5200\uff1b\u94fe\u5b50\u9524\u662f\u8fdc\u8ddd\u79bb\u653b\u51fb\uff0c\u76d8\u65cb\u98ce\u821e\uff0c \u517c\u6709\u6697\u5668\u4e4b\u957f\uff1b\u90a3\u5bf9\u5224\u5b98\u7b14\u5219\u4e13\u70b9\u4eba\u8eab\u5927\u7a74\uff0c\u4e09\u79cd\u5175\u5668\uff0c\u4e09\u79cd\u6218\u6cd5\uff0c\u6a0a\u82f1\u4e8f\u5f97\u6709\u53e3\u5b9d\u5200\uff0c\u8981 \u4e0d\u7136\u66f4\u96be\u62db\u67b6\u3002 \u3000\u3000\u4f46\u65f6\u95f4\u4e00\u4e45\uff0c\u4ea6\u662f\u96be\u4ee5\u62b5\u654c\uff0c\u6a0a\u82f1\u6321\u4e86\u4e8c\u4e09\u5341\u62db\uff0c\u9669\u8c61\u73af\u751f\uff0c\u4e0b\u9762\u547c\u559d\u58f0\u3001\u811a\u6b65\u58f0\u5608\u6210 \u4e00\u7247\uff0c\u80fd\u4e0a\u9ad8\u7684\u4e5f\u8df3\u4e0a\u4e86\u5341\u6765\u4e2a\u4eba\uff0c\u5728\u56db\u5468\u74e6\u9762\u57cb\u4f0f\uff0c\u6a0a\u82f1\u54ac\u7259\u529b\u6218\uff0c\u5df2\u662f\u6253\u7b97\u8c41\u51fa\u6027\u547d\u3001 \u5ffd\u89c1\u5bf9\u9762\u74e6\u80cc\uff0c\u4eba\u5f71\u4e00\u95ea\uff0c\u767d\u8863\u98d8\u98d8\uff0c\u90a3\u80cc\u5f71\u597d\u751f\u773c\u719f\u3002 \u3000\u3000\u6a0a\u82f1\u5fc3\u4e2d\u4e00\u52a8\uff0c\u90a3\u4f7f\u5224\u5b98\u7b14\u7684\u536b\u58eb\u559d\u4e00\u58f0\u201c\u7740\uff01\u201d\u4e00\u62db\u201c\u5cfb\u5cad\u5206\u6d41\u201d\uff0c\u53cc\u7b14\u6b3a\u8eab\u75be\u70b9\uff0c \u6a0a\u82f1\u7684\u7f05\u5200\u6b63\u88ab\u94fe\u5b50\u9524\u7f20\u7740\uff0c\u65e0\u6cd5\u62db\u67b6\uff0c\u6025\u5fd9\u95ed\u6c14\u62a4\u7a74\uff0c\u53ea\u89c9\u8170\u80ef\u4e4b\u5904\u9aa4\u5730\u4e00\u9635\u9178\u9ebb\uff0c\u201c\u8d1e \u767d\u7a74\u201d\u5df2\u7ed9\u4ed6\u7684\u7b14\u950b\u70b9\u4e86\u4e00\u4e0b\uff0c\u6a0a\u82f1\u4f7f\u4e86\u4e00\u62db\u201c\u4e09\u8f6c\u6cd5\u8f6e\u201d\uff0c\u7f05\u5200\u4e00\u7ede\uff0c\u8131\u51fa\u624b\u6765\uff0c\u90a3\u4f7f\u539a \u80cc\u65ab\u5c71\u5200\u7684\u536b\u58eb\uff0c\u4e00\u4e2a\u864e\u8df3\uff0c\u8fd4\u8eab\u73b0\u5200\uff0c\u547c\u5730\u4e00\u5200\uff0c\u8fce\u5934\u4fbf\u5288\u3002 \u3000\u3000\u6a0a\u82f1\u867d\u7136\u61c2\u5f97\u95ed\u6c14\u62a4\u7a74\uff0c\u5185\u529f\u672a\u81fb\u4e0a\u4e58\uff0c\u88ab\u5224\u5b98\u7b14\u70b9\u4e2d\uff0c\u9178\u9ebb\u672a\u8fc7\uff0c\u624b\u81c2\u4e4f\u529b\uff0c\u90a3\u539a\u80cc \u65ab\u5c71\u5200\u91cd\u8fbe\u56db\u4e94\u5341\u65a4\uff0c\u8fd9\u8fce\u5934\u4e00\u5288\uff0c\u5a01\u52bf\u731b\u6781\uff0c\u6a0a\u82f1\u660e\u77e5\u62db\u67b6\u4e0d\u4f4f\uff0c\u4e5f\u53ea\u597d\u6325\u5200\u8fce\u6321\uff0c\u5fc3\u4e2d \u547c\u547c\uff1a\u201c\u6211\u547d\u4f11\u77e3\uff01\u201d\u5c31\u5728\u8fd9\u4e24\u5200\u76f8\u63a5\u672a\u63a5\u4e4b\u9645\uff0c\u90a3\u4f7f\u539a\u80cc\u65ab\u5c71\u5200\u7684\u536b\u58eb\u5ffd\u7136\u5927\u53eb\u4e00\u58f0\uff0c\u624b \u4e2d\u90a3\u4e00\u5927\u5200\u5ffd\u7136\u8131\u624b\u98de\u53bb\uff0c\u521a\u597d\u8fd1\u7740\u5de6\u8fb9\u6253\u6765\u7684\u90a3\u5bf9\u94fe\u5b50\u9524\uff0c\u5f53\u5f53\u4e24\u58f0\u8f70\u96f7\u822c\u7684\u5de8\u54cd\uff0c\u90a3\u5bf9 \u94fe\u5b50\u9524\u4e5f\u7ed9\u649e\u5f97\u8dcc\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u5bf9\u9762\u74e6\u80cc\u4e0a\u90a3\u5c11\u5e74\u54c8\u54c8\u5927\u7b11\uff0c\u7b11\u58f0\u5b9b\u82e5\u94f6\u94c3\uff0c\u5341\u5206\u597d\u542c\uff0c\u4f46\u89c1\u4ed6\u53cd\u624b\u4e00\u626c\uff0c\u591c\u7a7a\u4e2d \u987f\u65f6\u73b0\u51fa\u5341\u6570\u6735\u91d1\u82b1\uff0c\u4e0a\u6709\u6de1\u6708\u758f\u661f\uff0c\u4e0b\u6709\u677e\u679d\u706b\u628a\uff0c\u8fd9\u5341\u6570\u6735\u91d1\u82b1\u500f\u5730\u6563\u5f00\uff0c\u8fce\u7a7a\u6d12\u4e0b\uff0c \u597d\u770b\u4e4b\u6781\uff0c\u536b\u58eb\u4eec\u505a\u68a6\u4e5f\u6599\u4e0d\u5230\u6709\u8fd9\u822c\u5389\u5bb3\u7684\u6697\u5668\uff0c\u91d1\u82b1\u63a0\u8fc7\uff0c\u53ea\u8981\u88ab\u522e\u7740\u4e00\u4e0b\uff0c\u5168\u8eab\u7acb\u523b \u9ebb\u9178\u3002\u57cb\u4f0f\u5728\u74e6\u9762\u4e0a\u7684\u536b\u58eb\uff0c\u6709\u534a\u6570\u4ee5\u4e0a\u90fd\u88ab\u91d1\u82b1\u6253\u4f24\u3002\u6a0a\u82f1\u5446\u4e86\u4e00\u5446\uff0c\u90a3\u91d1\u82b1\u8ba4\u4e0d\u51fa\u53cb \u654c\uff0c\u6a0a\u82f1\u7684\u81c2\u4e0a\u4e5f\u7ed9\u522e\u4e86\u4e00\u4e0b\uff0c\u4e00\u6761\u81c2\u818a\u767b\u65f6\u540a\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u4f7f\u5224\u5b98\u7b14\u7684\u536b\u58eb\u5927\u547c\uff1a\u201c\u5feb\u53eb\u9633\u5927\u4eba\u6765\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u773c\u524d\u91d1\u661f\u4e00\u6643\uff0c\u90a3\u4f7f\u5224\u5b98 \u7b14\u7684\u536b\u58eb\u53c8\u662f\u4e00\u58f0\u5927\u53eb\uff0c\u5012\u8dc3\u4e09\u6b65\uff0c\u6a0a\u82f1\u8d81\u6b64\u65f6\u673a\uff0c\u5200\u4ea4\u5de6\u624b\uff0c\u63d0\u53e3\u6c14\uff0c\u75be\u5fd9\u63a0\u8fc7\u4e24\u91cd\u74e6 \u9762\uff0c\u95ef\u51fa\u5929\u7262\uff0c\u56de\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u74e6\u9762\u4e0a\u4e24\u6761\u9ed1\u5f71\uff0c\u4e92\u76f8\u8ffd\u9010\uff0c\u5251\u9e23\u4e4b\u58f0\uff0c\u55e1\u55e1\u9707\u8033\uff0c\u90a3\u4e24\u6761 \u9ed1\u5f71\u8eab\u6cd5\u5feb\u6781\uff0c\u6a0a\u82f1\u4f9d\u7a00\u8ba4\u51fa\u4ea4\u6218\u7684\u4e00\u65b9\u5c31\u662f\u90a3\u624b\u6563\u91d1\u82b1\u7684\u5c11\u5e74\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u5316\u6210\u4e86\u4e24\u6e9c\u9ed1 \u70df\uff0c\u5411\u897f\u5317\u89d2\u75be\u6eda\u53bb\u4e86\uff01 \u3000\u3000\u8fd9\u94f6\u94c3\u4f3c\u7684\u7b11\u58f0\uff0c\u8fd9\u95ea\u7535\u822c\u7684\u8eab\u6cd5\uff0c\u8fd9\u4f3c\u66fe\u76f8\u8bc6\u7684\u80cc\u5f71\uff0c\u51e0\u4e2a\u5f62\u8c61\u9aa4\u7136\u4ea4\u7ed3\uff0c\u6a0a\u82f1\u731b\u5730 \u4e00\u9192\uff0c\u539f\u6765\u8fd9\u624b\u6563\u91d1\u82b1\u7684\u5c11\u5e74\u5c31\u662f\u76ee\u524d\u620f\u5f04\u5f20\u864e\u5b50\u7684\u90a3\u4e2a\u767d\u9a6c\u4e66\u751f\uff01 \u3000\u3000\u5929\u7262\u4e4b\u5185\uff0c\u547c\u559d\u9163\u6597\uff0c\u9ed1\u5f71\u5e62\u5e62\uff0c\u5728\u74e6\u9762\u4e0a\u5954\u6765\u9010\u53bb\uff0c\u4e14\u5df2\u6709\u4eba\u5411\u6a0a\u82f1\u8ffd\u6765\uff0c\u6a0a\u82f1\u53f9\u4e86 \u53e3\u6c14\uff0c\u5fc3\u60f3\u81ea\u5df1\u7eb5\u4e0d\u53d7\u4f24\uff0c\u672c\u4e8b\u4e5f\u76f8\u5dee\u592a\u8fdc\uff0c\u53ea\u597d\u5c06\u6551\u4e8e\u8c26\u4e4b\u671b\uff0c\u5bc4\u6258\u4e8e\u90a3\u4e2a\u6563\u82b1\u5c11\u5e74\uff0c\u62fc \u4e00\u53e3\u6c14\uff0c\u4f7f\u51fa\u201c\u9646\u5730\u98de\u817e\u201d\u7684\u529f\u592b\uff0c\u5954\u79bb\u864e\u7a74\u9f99\u6f6d\uff0c\u6084\u6084\u6e9c\u56de\u5ba2\u5e97\u3002 \u3000\u3000\u56de\u5230\u5ba2\u5e97\uff0c\u5df2\u662f\u56db\u66f4\u65f6\u5206\uff0c\u6a0a\u82f1\u89e3\u8863\u4e00\u770b\uff0c\u53ea\u89c1\u53f3\u81c2\u9669\u4e9b\u8131\u81fc\uff0c\u5e78\u672a\u4f24\u53ca\u7b4b\u9aa8\uff0c\u6a0a\u82f1\u54ac \u4e00\u54ac\u7259\uff0c\u81ea\u5df1\u5c06\u624b\u81c2\u63a5\u597d\uff0c\u6577\u4e0a\u4e86\u91d1\u521b\u836f\u3002\u521a\u521a\u5f04\u597d\uff0c\u53ea\u89c9\u5934\u6655\u773c\u82b1\uff0c\u518d\u4e5f\u62b5\u53d7\u4e0d\u4f4f\uff0c\u4e00\u8eba \u4e0a\u5e8a\uff0c\u7acb\u523b\u660f\u6c89\u6c89\u5730\u6655\u8ff7\u8fc7\u53bb\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u7741\u5f00\u773c\u65f6\uff0c\u53ea\u89c1\u5ba4\u5185\u4e00\u706f\u5982\u8c46\uff0c\u5e97\u5c0f \u4e8c\u62ab\u9ebb\u5e26\u5b5d\uff0c\u9762\u6302\u6cea\u75d5\uff0c\u7ad9\u5728\u5e8a\u5934\u3002 \u3000\u3000\u6a0a\u82f1\u5947\u9053\uff1a\u201c\u6211\u53c8\u672a\u6b7b\uff0c\u54ed\u4ec0\u4e48\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u4e8e\u5927\u4eba\uff0c\u4e8e\u5927\u4eba\u5df2\u7ecf\u5f52\u5929\u4e86\uff01\u201d\u6a0a\u82f1 \u53cc\u773c\u4e00\u7741\uff0c\u53eb\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u4ed6\u662f\u4eca\u671d\u4e00\u65e9\u5728\u5348\u95e8\u5f52\u5929\u7684\uff0c\u73b0\u4e0b\u5317\u4eac\u4e4b\u4eba\uff0c\u9664 \u4e86\u5978\u81e3\u8d3c\u5b50\u4e4b\u5916\uff0c\u4eba\u4eba\u90fd\u5728\u5bb6\u4e2d\u62ab\u9ebb\u5e26\u5b5d\u3002\u201d\u6a0a\u82f1\u5927\u53eb\u4e00\u58f0\uff0c\u53c8\u6655\u53a5\u8fc7\u53bb\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u6a0a\u82f1\u60a0\u60a0\u9192\u8f6c\uff0c\u90a3\u5e97\u5c0f\u4e8c\u4ecd\u5750\u5728\u5e8a\u5934\uff0c\u66ff\u4ed6\u6376\u80cc\uff0c\u6a0a\u82f1\u9053\uff1a\u201c\u73b0\u4e0b\u662f\u4ec0\u4e48\u65f6 \u5019\u4e86\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u4f60\u5df2\u660f\u8ff7\u4e86\u4e00\u65e5\u534a\u591c\uff0c\u73b0\u5728\u5df2\u662f\u7b2c\u4e8c\u65e5\u7684\u591c\u95f4\u4e86\u3002\u201d\u6a0a\u82f1\u5fc3\u75db \u5982\u5272\uff0c\u60f3\u4e0d\u5230\u5927\u660e\u671d\u5ef7\uff0c\u5c45\u7136\u6562\u5192\u5929\u4e0b\u4e4b\u5927\u4e0d\u97ea\uff0c\u8fd9\u6837\u5feb\u4fbf\u6740\u4e86\u4e8e\u8c26\u3002\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u6a0a\u4e49 \u58eb\uff0c\u4f60\u89c9\u5f97\u5982\u4f55\uff1f\u82e5\u80fd\u8d70\u52a8\uff0c\u65e9\u65e9\u79bb\u5f00\u4e86\u4eac\u57ce\u5427\u3002\u201d\u5e97\u5c0f\u4e8c\u6539\u53e3\u79f0\u4ed6\u201c\u6a0a\u4e49\u571f\u201d\uff0c\u6a0a\u82f1\u5403\u4e86 \u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u4e49\u58eb\u4e0d\u5fc5\u591a\u8651\uff0c\u4f60\u6628\u591c\u56de\u6765\uff0c\u5200\u4e0a\u7684\u8840\u8fd8\u672a\u63e9\u5e72\u51c0 \u5462\u3002\u201d\u539f\u6765\u6628\u665a\u4e00\u7fa4\u4fa0\u5ba2\u5927\u95f9\u5929\u7262\uff0c\u65e5\u95f4\u65e9\u5df2\u4f20\u904d\u5317\u4eac\u3002\u5e97\u5c0f\u4e8c\u89c1\u6a0a\u82f1\u660f\u8ff7\u4e0d\u9192\uff0c\u5200\u4e0a\u8840\u6e0d \u72b9\u5b58\uff0c\u8054\u60f3\u8d77\u4ed6\u6295\u5e97\u4e4b\u65f6\uff0c\u7acb\u523b\u4fbf\u95ee\u4e8e\u8c26\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u65e9\u5df2\u604d\u7136\uff0c\u5f53\u65f6\u4fbf\u8bf7\u4e86\u4e00\u4f4d\u9760\u5f97\u4f4f\u7684\u8dcc \u6253\u533b\u751f\u7ed9\u4ed6\u533b\u6cbb\uff0c\u6a0a\u82f1\u53d7\u7684\u53ea\u662f\u5916\u4f24\uff0c\u6240\u4ee5\u6655\u8ff7\uff0c\u5168\u662f\u56e0\u4e3a\u75b2\u52b3\u8fc7\u5ea6\u6240\u81f3\uff0c\u7761\u4e86\u4e00\u5929\u534a\u591c\uff0c \u7cbe\u529b\u5df2\u662f\u6e10\u6e10\u6062\u590d\u3002 \u3000\u3000\u6a0a\u82f1\u53d6\u8fc7\u5b9d\u5200\uff0c\u62ed\u4e86\u8840\u75d5\uff0c\u6068\u6068\u8bf4\u9053\uff1a\u201c\u6068\u4e0d\u80fd\u591a\u6740\u51e0\u4e2a\u5978\u81e3\u8d3c\u5b50\uff01\u201d\u5176\u5b9e\u6740\u4e8e\u8c26\u7684\u4e3b \u51f6\uff0c\u6b63\u662f\u5f53\u4eca\u7684\u5929\u5b50\uff0c\u4e8e\u8c26\u8d39\u5c3d\u5fc3\u673a\uff0c\u4ece\u74e6\u523a\u6551\u56de\u6765\u7684\u7948\u9547\u3002\u5e97\u5c0f\u4e8c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5916\u9762\u8c23\u8a00 \u751a\u591a\uff0c\u51e1\u662f\u548c\u4e8e\u9601\u8001\u6709\u5f80\u6765\u7684\u4eba\u542c\u8bf4\u90fd\u5df2\u88ab\u6355\u4e86\uff0c\u4e49\u58eb\uff0c\u4f60\u8fd8\u662f\u5feb\u8d70\u4e86\u5427\u3002\u201d\u6a0a\u82f1\u629a\u5200\u53f9 \u9053\uff1a\u201c\u5927\u95f9\u5929\u7262\uff0c\u6551\u4e0d\u4e86\u4e8e\u5927\u4eba\uff0c\u53cd\u800c\u4fc3\u4ed6\u5f52\u5929\uff0c\u54ce\uff0c\u6211\u6d3b\u5728\u8fd9\u4e16\u4e0a\u8fd8\u6709\u4f55\u7528\uff1f\u201d\u5e97\u5c0f\u4e8c \u9053\uff1a\u201c\u4e49\u58eb\u4f11\u5f97\u5982\u6b64\u60f3\u6cd5\uff0c\u6b63\u4eba\u541b\u5b50\uff0c\u591a\u6b7b\u4e00\u4eba\uff0c\u56fd\u5bb6\u4fbf\u591a\u635f\u4e00\u5206\u5143\u6c14\uff0c\u4e8e\u9601\u8001\u5df2\u6b7b\uff0c\u96be\u4ee5 \u633d\u56de\uff0c\u4e49\u58eb\uff0c\u4f60\u8fd8\u8981\u4fdd\u91cd\u3002\u201d\u6a0a\u82f1\u970d\u7136\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u6211\u662f\u8fd9\u5ba2\u5e97 \u7684\u5c0f\u4f19\u8ba1\u3002\u201d\u6a0a\u82f1\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u671d\u4e2d\u5927\u8001\u591a\u8c04\u5a9a\uff0c\u53cd\u800c\u662f\u5c60\u6cbd\u8d31\u5f79\u4e4b\u4e2d\uff0c\u6709\u8bc6\u89c1\u6062\u5b8f \u4e4b\u58eb\u3002\u201d\u95ee\u9053\uff1a\u201c\u4e8e\u5927\u4eba\u7684\u5c38\u9996\u6709\u4eba\u6536\u6b93\u6ca1\u6709\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u542c\u8bf4\u4e8e\u5927\u4eba\u7684\u9057\u9ab8\uff0c\u7687\u4e0a\u5df2 \u6069\u51c6\u6307\u6325\u9648\u901e\u4ee3\u4e3a\u6536\u6b93\u3002\u4e8e\u5927\u4eba\u7684\u9996\u7ea7\u73b0\u5728\u8fd8\u6302\u5728\u4e1c\u95e8\u3002\u201d\u6a0a\u82f1\u53c8\u5927\u53eb\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5feb\u5f04\u70b9 \u9152\u98df\u7ed9\u6211\u3002\u201d\u5e97\u5c0f\u4e8c\u7ed9\u4ed6\u4e00\u65a4\u767d\u9152\uff0c\u4e24\u65a4\u725b\u8089\uff0c\u6a0a\u82f1\u5168\u90e8\u5403\u4e86\uff0c\u63d0\u8d77\u5b9d\u5200\uff0c\u7ed3\u4e86\u9152\u94b1\uff0c\u9053\uff1a \u201c\u591a\u8c22\u4f60\u7684\u6069\u4e49\uff0c\u54b1\u4eec\u518d\u89c1\u5566\u3002\u201d\u8bd5\u8fd0\u624b\u8db3\uff0c\u53ea\u89c9\u6c14\u529b\u5df2\u5b8c\u5168\u6062\u590d\uff0c\u7acb\u523b\u7a7f\u7a97\u98de\u8d70\uff0c\u80cc\u540e\u53ea \u542c\u5f97\u90a3\u5e97\u5c0f\u4e8c\u55df\u53f9\u4e4b\u58f0\u3002 \u3000\u3000\u6a0a\u82f1\u5c55\u5f00\u591c\u884c\u672f\uff0c\u76f4\u5954\u4e1c\u95e8\uff0c\u662f\u665a\u6708\u6697\u65e0\u5149\uff0c\u5230\u4e86\u4e1c\u95e8\u57ce\u5899\u4e4b\u4e0b\uff0c\u4e3e\u5934\u9065\u671b\uff0c\u53ea\u89c1\u57ce\u5899 \u4e0a\u7ad6\u7740\u4e00\u6761\u65d7\u7aff\uff0c\u65d7\u7aff\u4e0a\u6302\u7740\u4e00\u4e2a\u5706\u5ffd\u5ffd\u7684\u4e1c\u897f\uff0c\u4f9d\u7a00\u8fa8\u5f97\u51fa\u90a3\u662f\u5934\u9885\uff0c\u6a0a\u82f1\u5927\u6078\uff0c\u4e5f\u987e\u4e0d \u5f97\u57ce\u5899\u4e0a\u662f\u5426\u57cb\u4f0f\uff0c\u7acb\u5373\u4fbf\u8dc3\u4e0a\u5899\u5934\uff0c\u7f05\u5200\u4e00\u6325\uff0c\u4fbf\u60f3\u65ab\u65ad\u90a3\u6839\u65d7\u7aff\u3002 \u3000\u3000\u7687\u5e1d\u5c06\u4e8e\u8c26\u7684\u9996\u7ea7\u60ac\u4e4b\u4e1c\u95e8\uff0c\u5b9e\u662f\u4e00\u79cd\u8bf1\u654c\u4e4b\u8ba1\uff0c\u7109\u80fd\u5982\u6b64\u8f7b\u6613\u88ab\u6a0a\u82f1\u53d6\u53bb\u3002\u6a0a\u82f1\u7f05\u5200 \u521a\u521a\u626c\u8d77\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u51b7\u7b11\uff0c\u4e24\u6761\u9ed1\u5f71\u84e6\u7136\u7a9c\u4e86\u51fa\u6765\uff0c\u91d1\u5200\u5288\u98ce\uff0c\u4e00\u5bf9\u94a9\u9570\u67aa\u5df2\u5411\u4e0b\u4e09\u8def\u5377 \u5230\uff0c\u6a0a\u82f1\u6d8c\u8eab\u4e00\u8dc3\uff0c\u6a2a\u5200\u4e00\u64a9\uff0c\u53c8\u4e0e\u4fa7\u9762\u63b7\u6765\u7684\u4e00\u6839\u94c1\u5c3a\u78b0\u4e2a\u6b63\u7740\uff0c\u53ea\u542c\u5f97\u90a3\u4e24\u4eba\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u9633\u5927\u4eba\u597d\u89c1\u8bc6\uff0c\u81ed\u86e4\u87c6\u679c\u7136\u843d\u7f51\u4e86\uff01\u201d \u3000\u3000\u6a0a\u82f1\u5927\u6012\uff0c\u8de8\u6b65\u63d0\u5200\uff0c\u4e00\u62db\u201c\u767d\u9e64\u4eae\u7fc5\u201d\uff0c\u55d6\u55d6\u4e24\u5200\uff0c\u4e00\u62db\u4e24\u5f0f\uff0c\u5de6\u64a9\u53f3\u6ed1\uff0c\u90a3\u4f7f\u94a9\u9570 \u67aa\u7684\u9053\uff1a\u201c\u597d\u4e00\u53e3\u5b9d\u5200\uff0c\u770b\u5728\u8fd9\u53e3\u5200\u7684\u4efd\u4e0a\uff0c\u4f60\u732e\u5200\u6295\u964d\uff0c\u9976\u4f60\u4e0d\u6b7b\uff01\u201d\u6a0a\u82f1\u559d\u9053\uff1a\u201c\u4f60\u8981 \u5200\uff1f\u597d\uff0c\u5c31\u7ed9\u4f60\u4e00\u5200\uff01\u201d\u547c\u5730\u4e00\u5200\u5288\u53bb\uff0c\u90a3\u4f7f\u94a9\u9570\u67aa\u7684\u53eb\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u8fd9\u5c0f\u5b50\u771f\u4e2a\u62fc\u547d\u3002\u201d \u84e6\u5730\u4f0f\u8eab\u4e00\u6eda\uff0c\u4f7f\u94c1\u5c3a\u7684\u4ed7\u7740\u5668\u68b0\u6c89\u91cd\uff0c\u4e0d\u6015\u5b9d\u5200\uff0c\u594b\u8d77\u62db\u67b6\uff0c\u53ea\u542c\u5f97\u5f53\u7684\u4e00\u58f0\uff0c\u90a3\u6839\u94c1\u5c3a \u51e0\u4e4e\u7ed9\u6a0a\u82f1\u9707\u98de\uff0c\u6a0a\u82f1\u4e00\u5200\u659c\u5288\uff0c\u63d0\u811a\u4e00\u8e0f\uff0c\u5ffd\u89c9\u817f\u4e0a\u5267\u75db\uff0c\u4f0f\u5728\u5730\u4e0b\u90a3\u540d\u5f85\u536b\uff0c\u4e00\u6839\u94a9\u9570 \u67aa\u5df2\u52fe\u7740\u4e86\u4ed6\u7684\u5c0f\u817f\u3002 \u3000\u3000\u6a0a\u82f1\u820d\u4e86\u6027\u547d\uff0c\u7ffb\u8eab\u4e00\u8dc3\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u4f7f\u94a9\u9570\u67aa\u7684\u6599\u4e0d\u5230\u4ed6\u51fa\u6b64\u6076\u62db\uff0c\u53f3\u624b\u4e00\u677e\uff0c\u8d76\u5fd9 \u8df3\u5f00\uff0c\u6a0a\u82f1\u5e26\u7740\u90a3\u6839\u94a9\u9570\u67aa\u4e00\u8dc3\u4e08\u8bb8\uff0c\u54ac\u7740\u7259\u6839\u5c06\u94a9\u9570\u67aa\u4e00\u62d4\uff0c\u8840\u6dcb\u6dcb\u5730\u62d4\u4e86\u51fa\u6765\uff0c\u5728\u7a7a\u4e2d \u821e\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u5411\u90a3\u4f7f\u94c1\u5c3a\u7684\u63b7\u53bb\uff0c\u4f7f\u94c1\u5c3a\u7684\u54ea\u91cc\u6562\u63a5\uff0c\u53ea\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u6839\u94a9\u9570\u67aa \u7ed9\u6a0a\u82f1\u63b7\u843d\u57ce\u5899\uff0c\u60f3\u662f\u78b0\u5230\u4e86\u4e0b\u9762\u7684\u77f3\u5934\uff0c\u55e1\u55e1\u4e4b\u58f0\uff0c\u4f20\u4e86\u4e0a\u6765\uff0c\u4e0d\u7edd\u4e8e\u8033\u3002 \u3000\u3000\u90a3\u4f7f\u94c1\u5c3a\u7684\u89c1\u6a0a\u82f1\u72b9\u5982\u4e00\u5934\u8d1f\u4f24\u7684\u731b\u864e\uff0c\u51f6\u795e\u715e\u6c14\uff0c\u5484\u5484\u8feb\u4eba\uff0c\u4e0d\u89c9\u80c6\u5bd2\uff0c\u90a3\u4f7f\u94a9\u9570\u67aa \u7684\u559d\u9053\uff1a\u201c\u8ddb\u811a\u81ed\u86e4\u87c6\uff0c\u8fd8\u6015\u4ec0\u4e48\uff1f\u5e76\u80a9\u5b50\u4e0a\u5475\uff01\u201d\u4ed6\u53ea\u5269\u4e0b\u4e00\u6839\u94a9\u9570\u67aa\uff0c\u4f46\u5de6\u53f3\u76d8\u65cb\uff0c\u9f99 \u98de\u51e4\u821e\uff0c\u6216\u52fe\u6216\u523a\uff0c\u6216\u64a9\u6216\u622a\uff0c\u653b\u52bf\u4ecd\u662f\u5341\u5206\u51cc\u5389\uff0c\u90a3\u4f7f\u94c1\u5c3a\u7684\u6b66\u529f\u4ea6\u662f\u4e0d\u5f31\uff0c\u4e00\u5bf9\u94c1\u5c3a\u4ea6 \u81ea\u821e\u5f97\u864e\u864e\u751f\u98ce\uff0c\u6a0a\u82f1\u811a\u4e0a\u53d7\u4f24\uff0c\u8df3\u8dc3\u4e0d\u4fbf\uff0c\u6e10\u6e10\u53ea\u6709\u88ab\u653b\u7684\u4efd\u513f\u3002 \u3000\u3000\u90a3\u4f7f\u94a9\u9570\u62a2\u7684\u4e00\u5360\u4e0a\u98ce\uff0c\u53c8\u901e\u53e3\u89d2\uff0c\u563f\u563f\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u8981\u4e8e\u8c26\u7684\u4eba\u5934\uff0c\u54fc\u54fc\uff0c\u8fde\u4f60\u7684 \u4e5f\u7559\u4e0b\u6765\u5427\u3002\u201d\u6a0a\u82f1\u6c14\u7ea2\u4e86\u773c\uff0c\u5356\u4e2a\u7834\u7efd\uff0c\u5c06\u5200\u659c\u6302\u94c1\u5c3a\uff0c\u6545\u610f\u5f04\u5f97\u95e8\u6237\u5927\u5f00\uff0c\u4f7f\u94a9\u9570\u67aa\u7684 \u6840\u6840\u602a\u7b11\uff0c\u4e00\u62db\u201c\u6bd2\u86c7\u5410\u7cbe\u201d\uff0c\u633d\u4e86\u6597\u5927\u4e00\u4e2a\u67aa\u82b1\uff0c\u5288\u80f8\u523a\u4e86\u8fdb\u6765\uff01\u6a0a\u82f1\u9661\u5730\u5927\u559d\u4e00\u58f0\uff0c\u5c06 \u5200\u4e00\u5408\uff0c\u8fd9\u4e00\u5200\u7528\u4e86\u5341\u6210\u529b\u91cf\uff0c\u53ea\u542c\u5f97\u8f70\u7684\u4e00\u58f0\uff0c\u6709\u5982\u5de8\u9524\u8d70\u949f\uff0c\u90a3\u6839\u94a9\u9570\u67aa\u767b\u65f6\u5f2f\u66f2\uff0c\u952f \u9f7f\u5012\u52fe\u67aa\u5934\uff0c\u51e0\u4e4e\u6298\u65ad\u3002\u90a3\u536b\u571f\u4e5f\u771f\u4e86\u5f97\uff0c\u864e\u53e3\u6d41\u8840\uff0c\u5140\u81ea\u63e1\u7740\u4e0d\u653e\u3002 \u3000\u3000\u6a0a\u82f1\u864e\u543c\u4e00\u58f0\uff0c\u6a2a\u5200\u75be\u4e0a\uff0c\u53ea\u89c1\u65d7\u7aff\u4e0b\u53c8\u8df3\u51fa\u4e00\u4eba\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u4e2a\u8113\u5305\uff0c\u4e00\u4e2a\u8ddb\u811a \u7684\u81ed\u86e4\u87c6\u4e5f\u6536\u62fe\u4e0d\u4e86\uff0c\u5feb\u7ed9\u6211\u9000\u4e0b\uff0c\u51c6\u5907\u7f1a\u4eba\u5427\u3002\u201d\u6a0a\u82f1\u4e00\u7740\uff0c\u53ea\u89c1\u6765\u4eba\u7a7f\u7684\u5fa1\u6797\u519b\u670d\u9970\uff0c \u624b\u63d0\u4e00\u53e3\u963f\u62c9\u4f2f\u6708\u7259\u5f2f\u5200\uff0c\u770b\u4e86\u4e00\u773c\uff0c\u5ffd\u9053\uff1a\u201c\u54a6\uff0c\u5f20\u98ce\u5e9c\u7684\u7f05\u5200\u600e\u4e48\u5230\u4e86\u4f60\u7684\u624b\u4e2d\uff1f\u201d \u3000\u3000\u6a0a\u82f1\u9053\uff1a\u201c\u5f20\u98ce\u5e9c\u501f\u5200\u53eb\u6211\u6740\u4f60\uff01\u201d\u4e0a\u9a6c\u4e03\u661f\u6b65\uff0c\u547c\u5730\u4e00\u5200\u5288\u53bb\uff0c\u90a3\u519b\u5b98\u6012\u9053\uff1a\u201c\u6b7b\u5230 \u4e34\u5934\uff0c\u80e1\u8bf4\u516b\u9053\uff01\u201d\u6a0a\u82f1\u6740\u5f97\u6027\u8d77\uff0c\u547c\u547c\u547c\u8fde\u5288\u4e09\u5200\uff0c\u90a3\u519b\u5b98\u51b7\u7b11\u9053\uff1a\u201c\u597d\u4e00\u628a\u5b9d\u5200\uff0c\u53ef\u60dc \u843d\u5728\u4f60\u8fd9\u83bd\u6c49\u624b\u4e2d\u3002\u201d\u6a0a\u82f1\u559d\u9053\uff1a\u201c\u53eb\u4f60\u5c1d\u5c1d\u6211\u8fd9\u83bd\u6c49\u7684\u5b9d\u5200\u6ecb\u5473\uff01\u201d\u5de6\u53f3\u659c\u5288\uff0c\u6a2a\u7a7a\u53c8\u65a9 \u51e0\u5200\uff0c\u970e\u90a3\u4e4b\u95f4\uff0c\u5148\u540e\u5288\u4e86\u516d\u5200\uff0c\u5374\u90fd\u88ab\u90a3\u519b\u5b98\u4e00\u4e00\u5316\u4e86\uff0c\u53ea\u542c\u5f97\u90a3\u519b\u5b98\u53c8\u662f\u4e00\u58f0\u51b7\u7b11\uff0c \u9053\uff1a\u201c\u4e0d\u53eb\u4f60\u89c1\u8bc6\u89c1\u8bc6\uff0c\u4f60\u4e5f\u4e0d\u77e5\u9053\u6211\u4e1c\u65b9\u6d1b\u7684\u5389\u5bb3\uff0c\u4f60\u518d\u65a9\u5427\uff0c\u770b\u4f60\u7684\u5b9d\u5200\u80fd\u5948\u6211\u4f55\u3002\u201d \u6a0a\u82f1\u5927\u6012\uff0c\u8fd0\u8db3\u5185\u529b\uff0c\u53c8\u662f\u4e00\u5200\u6a2a\u65a9\uff0c\u90a3\u540d\u53eb\u4e1c\u65b9\u6d1b\u7684\u519b\u5b98\u4e3e\u8d77\u6708\u7259\u5f2f\u5200\u8f7b\u8f7b\u4e00\u67b6\uff0c\u6a0a\u82f1\u6c14 \u529b\u6781\u5927\uff0c\u9002\u624d\u90a3\u94a9\u9570\u67aa\u4e5f\u88ab\u4ed6\u4e00\u5200\u65a9\u65ad\uff0c\u5fc3\u60f3\u8fd9\u4e00\u5200\u5982\u4f55\u80fd\u591f\u62db\u67b6\uff0c\u5374\u4e0d\u6599\u4e00\u5200\u5288\u53bb\uff0c\u7a81\u7136 \u611f\u5230\u6beb\u65e0\u7740\u529b\u4e4b\u5904\uff0c\u4e1c\u65b9\u6cbb\u90a3\u53e3\u6708\u7259\u5f2f\u5200\u7adf\u50cf\u4e00\u7247\u8584\u7eb8\u4e00\u6837\u9644\u5728\u4ed6\u7684\u5200\u4e0a\uff0c\u6a0a\u82f1\u9aa4\u5931\u91cd\u5fc3\uff0c \u6251\u4e86\u4e2a\u7a7a\uff0c\u6536\u52bf\u4e0d\u53ca\uff0c\u9669\u9669\u8dcc\u5012\uff0c\u90a3\u519b\u5b98\u54c8\u54c8\u5927\u7b11\uff0c\u6708\u7259\u5f2f\u5200\u5de6\u53f3\u7ede\u8f6c\uff0c\u6a0a\u82f1\u5bf9\u4e8e\u5200\u6cd5\u672a\u7ecf \u82e6\u7ec3\uff0c\u4e0d\u8bc6\u8fd9\u201c\u7ede\u5200\u201d\u7684\u7834\u6cd5\uff0c\u624b\u4e2d\u7684\u7f05\u5200\u968f\u7740\u6025\u8f6c\uff0c\u53ea\u89c9\u5934\u6655\u76ee\u7729\uff0c\u770b\u770b\u7f05\u5200\u5c31\u8981\u8131\u624b\u98de \u53bb\u3002\u6a0a\u82f1\u5927\u6025\uff0c\u7a81\u7136\u53cc\u811a\u9f50\u98de\uff0c\u5de6\u638c\u4e00\u62db\u201c\u4e94\u4e01\u5f00\u5c71\u201d\uff0c\u987a\u7740\u5200\u52bf\u5288\u4e0b\uff0c\u6a0a\u82f1\u672c\u6765\u7528\u7684\u5175\u5668 \u662f\u5ba3\u82b1\u65a7\uff0c\u8fd9\u4e00\u638c\u6709\u5982\u5de8\u65a7\u5288\u4e0b\uff0c\u6b63\u662f\u4ed6\u7684\u6740\u624b\u7edd\u62db\uff0c\u52a0\u4e0a\u90a3\u9e33\u9e2f\u8fde\u73af\u817f\uff0c\u5a01\u52bf\u731b\u6781\uff0c\u4e1c\u65b9 \u6d1b\u6599\u4e0d\u5230\u4ed6\u5982\u6b64\u62fc\u547d\uff0c\u4e0d\u7531\u5f97\u5446\u4e86\u4e00\u5446\u3002 \u3000\u3000\u8fd9\u4e1c\u65b9\u6d1b\u4e43\u662f\u5fa1\u6797\u519b\u7684\u526f\u7edf\u9886\uff0c\u4ea6\u5373\u662f\u88ab\u5f20\u98ce\u5e9c\u6740\u6b7b\u7684\u9646\u5c55\u9e4f\u7684\u526f\u624b\uff0c\u6b66\u529f\u4e0d\u5728\u9646\u5c55\u9e4f \u4e4b\u4e0b\uff0c\u6bd4\u6a0a\u82f1\u9ad8\u51fa\u8bb8\u591a\u3002\u8fd9\u65f6\u4ed6\u672c\u53ef\u7528\u4e2a\u201c\u5b5f\u5fb7\u732e\u5200\u201d\u4e4b\u5f0f\uff0c\u987a\u7740\u6a0a\u82f1\u6251\u6765\u4e4b\u52bf\uff0c\u53cd\u624b\u4e00 \u5200\uff0c\u5288\u4ed6\u9888\u9879\uff0c\u4f46\u82e5\u7528\u8fd9\u4e00\u5f0f\uff0c\u6a0a\u82f1\u975e\u6b7b\u4e0d\u53ef\u3002\u4e1c\u65b9\u6d1b\u5446\u4e86\u4e00\u5446\uff0c\u5fc3\u601d\u4e00\u8f6c\uff0c\u5374\u987a\u624b\u4e00\u5e26\uff0c \u5c06\u6a0a\u82f1\u7684\u811a\u6b65\u5e26\u52a8\u4e24\u6b65\uff0c\u8f7b\u8f7b\u95ea\u8fc7\u4e00\u8fb9\u3002 \u3000\u3000\u5e76\u975e\u4e1c\u65b9\u6d1b\u6709\u610f\u9976\u4ed6\u6027\u547d\uff0c\u5374\u662f\u8fd9\u53e3\u7f05\u5200\u53eb\u4e1c\u65b9\u6d1b\u8d77\u4e86\u601d\u7591\u3002\u4e1c\u65b9\u6d1b\u5fc3\u4e2d\u60f3\u9053\uff0c\u201c\u8fd9\u7f05 \u5200\u4e43\u5f20\u98ce\u5e9c\u968f\u8eab\u4e4b\u5b9d\uff0c\u7edd\u4e0d\u4f1a\u501f\u4e0e\u7a77\u4eba\uff0c\u9664\u975e\u662f\u4ed6\u6b7b\u4e86\uff0c\u843d\u5728\u6b64\u4eba\u5f03\u4e2d\u3002\u201d\u9646\u5c55\u9e4f\u4ed6\u4eec\u53bb\u9080 \u8bf7\u5f20\u98ce\u5e9c\u4e4b\u4e8b\uff0c\u4e1c\u65b9\u6d1b\u5f53\u7136\u77e5\u9053\uff0c\u5fc3\u5ff5\uff1a\u5f20\u98ce\u5e9c\u82e5\u662f\u6b7b\u4e86\uff0c\u90a3\u4e5f\u5b9a\u7136\u662f\u9646\u5c55\u9e4f\u4ed6\u4eec\u6740\u6b7b\u7684\uff0c \u8fd9\u53e3\u7f05\u5200\u4fbf\u5f53\u5728\u9646\u5c65\u9e4f\u624b\u4e0a\uff0c\u600e\u7684\u5230\u4e86\u6b64\u4eba\u624b\u4e2d\uff1f\u9646\u5c55\u9e4f\u76f4\u5230\u73b0\u5728\u8fd8\u672a\u56de\u4eac\uff0c\u83ab\u975e\u5176\u4e2d\u6709\u751a \u53d8\u6545\uff1f\u4e1c\u65b9\u6d1b\u767e\u601d\u4e0d\u89e3\uff0c\u6240\u4ee5\u5b58\u5fc3\u5c06\u6a0a\u82f1\u6d3b\u64d2\uff0c\u95ee\u51fa\u4e2a\u6240\u4ee5\u7136\u6765\u3002 \u3000\u3000\u6a0a\u82f1\u6b66\u529f\u6b8a\u975e\u6cdb\u6cdb\uff0c\u4e1c\u65b9\u6d1b\u60f3\u5c06\u4ed6\u6d3b\u6349\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u4e5f\u4e0d\u80fd\uff0c\u4e24\u4eba\u53c8\u62fc\u4e86\u4e8c\u5341\u672a\u62db\uff0c \u4e1c\u65b9\u6d1b\u559d\u4e00\u58f0\u201c\u7740\uff01\u201d\u6708\u7259\u5f2f\u5200\u4e00\u6302\uff0c\u5728\u6a0a\u82f1\u80a9\u5934\u4e0a\u62c9\u4e86\u4e00\u9053\u53e3\u5b50\uff0c\u7a81\u8d77\u4e00\u811a\uff0c\u8e22\u4e2d\u4e86\u6a0a\u82f1 \u7684\u819d\u76d6\uff0c\u6a0a\u82f1\u4f0f\u5730\u4e00\u6eda\uff0c\u4e1c\u65b9\u6d1b\u53eb\u9053\uff1a\u201c\u5c06\u4ed6\u7f1a\u4e86\uff01\u201d \u3000\u3000\u6a0a\u82f1\u5728\u5730\u4e0a\u4e00\u6eda\uff0c\u5ffd\u542c\u5f97\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u6709\u5982\u63a8\u91d1\u949f\u5012\u7389\u67f1\u7684\u58f0\u97f3\uff0c\u53ea\u89c1\u4e00\u6761\u9ed1\u5f71\u75be\u5982 \u95ea\u7535\uff0c\u4e00\u4e0b\u5b50\u5c31\u63a0\u4e0a\u4e86\u5899\u5934\uff0c\u624b\u8d77\u6367\u843d\uff0c\u5c06\u90a3\u65d7\u7aff\u4e00\u6367\u5288\u4e3a\u4e24\u6bb5\uff01 \u3000\u3000\u8fd9\u65d7\u7aff\u4e43\u9ec4\u94dc\u6240\u94f8\uff0c\u65a7\u65ab\u4e0d\u5012\uff0c\u5374\u88ab\u6765\u4eba\u4e00\u68d2\u6253\u6298\uff0c\u795e\u529b\u786e\u662f\u60ca\u4eba\uff01\u9002\u624d\u90a3\u4e24\u4e2a\u536b\u58eb\u6b63 \u6b32\u4e0a\u524d\u64d2\u6349\u6a0a\u82f1\uff0c\u88ab\u8fd9\u8f70\u7136\u5de8\u54cd\u5413\u4f4f\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u6a0a\u82f1\u5df2\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u4ece\u5730\u4e0a\u8dc3\u4e86\u8d77 \u6765\uff0c\u594b\u529b\u4e00\u5288\uff0c\u5374\u4e0d\u6599\u90a3\u6761\u81c2\u818a\u7adf\u7136\u4e0d\u542c\u4f7f\u5524\uff0c\u4e00\u9635\u5267\u75db\uff0c\u9ebb\u8f6f\u8f6f\u5730\u53d1\u4e0d\u51fa\u529b\u6765\uff0c\u90a3\u4e24\u540d\u536b \u58eb\uff0c\u4e00\u4e2a\u62a1\u7740\u534a\u622a\u94a9\u9570\u67aa\uff0c\u4e00\u4e2a\u821e\u7740\u4e00\u5bf9\u94c1\u5c3a\uff0c\u8fce\u5934\u4fbf\u78d5\uff0c\u6a0a\u82f1\u524d\u665a\u7684\u624b\u81c2\u8131\u81fc\uff0c\u521a\u521a\u63a5 \u597d\uff0c\u4eca\u665a\u80a9\u5934\u53c8\u53d7\u4e86\u5200\u4f24\uff0c\u8ddf\u7741\u7741\u5730\u770b\u7740\u654c\u4eba\u5175\u5668\u626b\u6765\uff0c\u65e0\u6cd5\u62b5\u6321\uff0c\u90a3\u4e24\u540d\u536b\u58eb\u5ffd\u5730\u4e00\u58f0\u5389 \u53eb\uff0c\u7a81\u7136\u5012\u5730\uff0c\u6a0a\u82f1\u628a\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u4e1c\u65b9\u6d1b\u6b63\u4e0e\u4e00\u4e2a\u8499\u9762\u6c49\u5b50\u5728\u65d7\u7aff\u4e0b\u5636\u6740\uff0c\u6367\u5f71\u5200\u5149\uff0c\u6df7 \u6210\u4e00\u7247\uff0c\u6a0a\u82f1\u83ab\u540d\u5176\u5999\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u4ed6\u600e\u80fd\u817e\u51fa\u624b\u6765\u53d1\u653e\u6697\u5668\uff1f\u201d\u8981\u77e5\u6a0a\u82f1\u7ad9\u7acb\u4e4b \u5730\uff0c\u79bb\u65d7\u7aff\u6709\u4e09\u56db\u4e08\u8fdc\uff0c\u5373\u7b97\u6697\u5668\u53ef\u653e\uff0c\u4ea6\u5df2\u4e4f\u529b\uff0c\u600e\u80fd\u4e00\u4e3e\u4fbf\u5c06\u4e24\u4eba\u6740\u4e86\uff1f\u800c\u4e14\u9ad8\u624b\u6bd4 \u6b66\uff0c\u4e00\u5fc3\u96be\u80fd\u4e24\u7528\uff0c\u4ed6\u600e\u6562\u5728\u4e1c\u65b9\u6d1b\u5200\u5149\u7b3c\u7f69\u4e4b\u4e0b\u817e\u51fa\u624b\u6765\uff1f\u4f46\u82e5\u4e0d\u662f\u6b64\u4eba\uff0c\u53c8\u662f\u8c01\u4eba\u6709\u90a3 \u4e48\u9ad8\u7684\u672c\u9886\u3002 \u3000\u3000\u6a0a\u82f1\u9f13\u8d77\u4f59\u52c7\uff0c\u5200\u4ea4\u5de6\u624b\uff0c\u6b63\u60f3\u4e0a\u524d\u52a9\u6218\uff0c\u5ffd\u542c\u5f97\u4e1c\u65b9\u6d1b\u53c8\u5927\u53eb\u4e00\u58f0\uff0c\u98de\u8eab\u4e00\u8dc3\uff0c\u8df3\u5230 \u57ce\u5899\u4e0b\u8fb9\uff0c\u7a81\u7136\u4e0d\u89c1\uff0c\u90a3\u9ed1\u5f71\u7eb5\u58f0\u957f\u7b11\uff0c\u53f3\u624b\u63d0\u7740\u94c1\u68d2\uff0c\u5de6\u624b\u63d0\u7740\u4e8e\u8c26\u7684\u4eba\u5934\uff0c\u7eb5\u8eab\u4e00\u8df3\uff0c \u4e5f\u8df3\u4e0b\u5899\u5934\uff0c\u5982\u98ce\u8dd1\u4e86\uff01 \u3000\u3000\u6a0a\u82f1\u5403\u4e86\u4e00\u60ca\uff0c\u542c\u8fd9\u7b11\u58f0\uff0c\u770b\u8fd9\u80cc\u5f71\uff0c\u8fd9\u8499\u9762\u4eba\u770b\u6765\u7adf\u7136\u5c31\u662f\u90a3\u4e2a\u5728\u5c71\u4e1c\u5883\u5185\uff0c\u52ab\u8d70\u4ed6 \u4e09\u5341\u4e07\u4e24\u5b98\u94f6\u7684\u8499\u9762\u5927\u76d7\uff01\u60f3\u4e0d\u5230\u4ed6\u7adf\u7136\u4e5f\u5343\u91cc\u8fe2\u8fe2\uff0c\u8d76\u5230\u4eac\u57ce\u6765\u76d7\u53d6\u4e8e\u8c26\u7684\u5934\u9885\uff01\u6a0a\u82f1\u53c8 \u4f4e\u5934\u4e00\u770b\uff0c\u8fd9\u4e00\u770b\u66f4\u662f\u5403\u60ca\uff0c\u53ea\u89c1\u90a3\u4e24\u540d\u536b\u58eb\u7684\u592a\u9633\u7a74\u4e0a\uff0c\u90fd\u5370\u7740\u4e00\u6735\u4e94\u74e3\u91d1\u82b1\uff0c\u8fd9\u91d1\u82b1\u6b63 \u662f\u524d\u665a\u90a3\u767d\u8863\u5c11\u5e74\u7684\u72ec\u95e8\u6697\u5668\uff1b\u96be\u9053\u8fd9\u767d\u8863\u5c11\u5e74\u5c31\u662f\u90a3\u8499\u9762\u5927\u76d7\uff1f\u53ef\u662f\u4e24\u4eba\u7684\u8eab\u6750\u5374\u7edd\u4e0d\u76f8 \u7c7b\uff0c\u96be\u9053\u8fd9\u8499\u9762\u5927\u76d7\u4e5f\u4f1a\u7528\u8fd9\u79cd\u6697\u5668\uff1f\u6a0a\u82f1\u6ee1\u8179\u72d0\u7591\uff0c\u63d0\u5200\u56db\u987e\uff0c\u4e00\u7247\u832b\u7136\u3002 \u3000\u3000\u53ea\u542c\u5f97\u7a7a\u4e2d\u53c8\u662f\u4e00\u58f0\u602a\u5578\uff0c\u4e24\u6735\u91d1\u82b1\u5728\u7a7a\u4e2d\u4e00\u649e\uff0c\u500f\u5730\u98de\u5f00\uff0c\u6a0a\u82f1\u773c\u524d\u4e00\u4eae\uff0c\u9762\u524d\u7ad9\u7684 \u53ef\u4e0d\u6b63\u5c31\u662f\u90a3\u4e2a\u767d\u8863\u5c11\u5e74\uff0c\u6a0a\u82f1\u4e45\u5386\u6c5f\u6e56\uff0c\u7adf\u770b\u4e0d\u51fa\u4ed6\u662f\u4ece\u54ea\u513f\u7a81\u7136\u7a9c\u51fa\u6765\u7684\uff0c\u7adf\u50cf\u662f\u968f\u7740 \u91d1\u82b1\u4e00\u540c\u6d8c\u73b0\uff0c\u8eab\u6cd5\u4e4b\u5feb\uff0c\u5b9e\u5728\u96be\u4ee5\u5f62\u5bb9\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u7684\u58f0\u97f3\u5b9b\u82e5\u94f6\u94c3\uff0c\u4f46\u6e05\u8106\u67d4\u6da6\u4e4b\u4e2d\u5374\u53c8\u9690\u9690\u542b\u6709\u4e00\u79cd\u5484\u5484\u903c\u4eba\u7684\u58f0\u52bf\uff0c\u90a3\u5c11\u5e74\u95ee \u9053\uff1a\u201c\u90a3\u8499\u9762\u4eba\u662f\u4f60\u7684\u670b\u53cb\u5417\uff1f\u201d\u6a0a\u82f1\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d\u90a3\u767d\u8863\u5c11\u5e74\u9762\u8272\u4e00\u53d8\uff0c \u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u7acb\u523b\u8fd4\u8eab\u4fbf\u8d70\uff0c\u6a0a\u82f1\u5fd9\u9053\uff1a\u201c\u4fa0\u5ba2\u8bf7\u7559\u4e0b\u59d3\u540d\uff01\u201d\u53ea\u89c1\u90a3\u767d\u8863\u5c11\u5e74\u5df2\u4e00\u8dc3\u8df3 \u4e0b\u57ce\u5899\uff0c\u767d\u8863\u98d8\u98d8\uff0c\u5728\u832b\u832b\u591c\u8272\u4e4b\u4e2d\u9690\u6ca1\u4e86\uff01 \u3000\u3000\u8fd9\u767d\u8863\u5c11\u5e74\u7a81\u5982\u5176\u6765\u53c8\u7a81\u7136\u800c\u53bb\uff0c\u7528\u6697\u5668\u6551\u4e86\u6a0a\u82f1\u5374\u53c8\u4e0d\u80af\u7559\u4e0b\u59d3\u540d\uff0c\u9976\u662f\u6a0a\u82f1\u9605\u5386\u751a \u4e30\uff0c\u4e5f\u731c\u4e0d\u900f\u4ed6\u7684\u6765\u5386\u3002\u6b63\u662f\uff1a \u3000\u3000\u6563\u82b1\u5973\u4fa0\u65e0\u4eba\u8bc6\uff0c\u534a\u591c\u5077\u5934\u8d77\u5927\u6ce2\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u56db\u56de\u3000\u9a8f\u9a6c\u5636\u98ce\u3000\u5c11\u5e74\u663e\u8eab\u624b\u3000\u9ad8\u4eba\u9001\u5e16\u3000\u5e84\u4e3b\u8350\u9f99\u5934 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u56db\u56de\u3000\u9a8f\u9a6c\u5636\u98ce\u3000\u5c11\u5e74\u663e\u8eab\u624b\u3000\u9ad8\u4eba\u9001\u5e16\u3000\u5e84\u4e3b\u8350\u9f99\u5934 \u3000\u3000\u7b2c\u4e8c\u65e5\u5929\u521a\u62c2\u6653\uff0c\u6a0a\u82f1\u5df2\u5339\u9a6c\u5355\u5200\uff0c\u98de\u9a70\u5728\u4eac\u90ca\u9a7f\u9053\u4e4b\u4e0a\u3002\u4e8e\u8c26\u7684\u9996\u7ea7\u5df2\u88ab\u4eba\u76d7\u53bb\uff0c\u4ed6 \u9042\u542c\u4ece\u5e97\u5c0f\u4e8c\u4e4b\u529d\uff0c\u7acb\u523b\u79bb\u5f00\u5317\u4eac\uff0c\u51c6\u5907\u5230\u592a\u6e56\u53bb\u627e\u5f20\u4e39\u67ab\u3002 \u3000\u3000\u4ed6\u7684\u5750\u9a91\u662f\u5343\u4e2d\u9009\u4e00\u7684\u9ec4\u8198\u9a8f\u9a6c\uff0c\u811a\u7a0b\u751a\u5feb\uff0c\u4e2d\u5348\u65f6\u5206\uff0c\u5df2\u8d70\u4e86\u4e00\u767e\u591a\u91cc\uff0c\u8fc7\u4e86\u5357\u82d1 \u4e86\u3002\u901a\u5f80\u4eac\u57ce\u7684\u5927\u9053\uff0c\u5f80\u6765\u5ba2\u5546\uff0c\u591a\u5982\u8fc7\u6c5f\u4e4b\u9cab\uff0c\u6709\u4e00\u4e2a\u5355\u8eab\u5ba2\u5546\uff0c\u9a91\u7740\u4e00\u5339\u9752\u9b03\u4e94\u82b1\u9a6c\uff0c \u9a6c\u978d\u4e0a\u6302\u6709\u4e24\u4e2a\u4e0d\u5927\u4e0d\u5c0f\u7684\u76ae\u7bb1\uff0c\u60f3\u662f\u968f\u8eab\u643a\u5e26\u7684\u8d35\u91cd\u8d27\u7269\uff0c\u6a0a\u82f1\u521d\u65f6\u6beb\u4e0d\u5728\u610f\uff0c\u9ec4\u660f\u65f6 \u5019\uff0c\u5230\u4e86\u5c0f\u9547\u7409\u7483\u6cb3\uff0c\u4f30\u8ba1\u79bb\u5f00\u5317\u4eac\u5df2\u6709\u4e8c\u767e\u4e94\u5341\u591a\u91cc\uff0c\u6a0a\u82f1\u7b56\u9a6c\u5165\u9547\uff0c\u62df\u89c5\u5ba2\u5e97\u6295\u5bbf\uff0c\u65e0 \u610f\u95f4\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u90a3\u4e2a\u5355\u8eab\u5ba2\u5546\uff0c\u8fdc\u8fdc\u8ddf\u5728\u540e\u9762\uff0c\u6a0a\u82f1\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u51db\uff1a\u8fd9\u5ba2\u5546\u7684\u9a6c\u770b\u6765 \u5e76\u975e\u795e\u9a8f\uff0c\u4e5f\u5c45\u7136\u6709\u6b64\u811a\u529b\uff0c\u6a0a\u82f1\u8fdb\u5165\u5ba2\u5e97\u4e4b\u65f6\uff0c\u6697\u81ea\u7559\u5fc3\uff0c\u77e5\u89c1\u90a3\u5ba2\u5546\u6295\u522b\u7684\u5ba2\u5e97\uff0c\u6a0a\u82f1 \u8fd9\u624d\u8212\u4e86\u53e3\u6c14\uff0c\u6697\u7b11\u81ea\u5df1\u591a\u7591\u3002 \u3000\u3000\u6a0a\u82f1\u662f\u4e2a\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u5fc3\u60f3\u8fd9\u5ba2\u5546\u867d\u7136\u6ca1\u6709\u4ec0\u4e48\u5f02\u8ff9\uff0c\u4f46\u8fd8\u662f\u8c28\u614e\u4e00\u4e9b\uff0c\u907f\u4ed6\u4e3a \u5999\u3002\u4e8e\u662f\u5728\u665a\u4e0a\u7565\u7565\u517b\u795e\uff0c\u518d\u6577\u4e86\u4e00\u6b21\u4f24\uff0c\u6a0a\u82f1\u6b63\u5f53\u58ee\u5e74\uff0c\u8eab\u5b50\u9aa8\u597d\uff0c\u6240\u53d7\u7684\u4f24\u53ea\u662f\u76ae\u8089\u4e4b \u4f24\uff0c\u5e76\u65e0\u5927\u788d\uff0c\u53ea\u662f\u811a\u4e0a\u6328\u7684\u90a3\u5200\uff0c\u8fd8\u672a\u75ca\u6108\uff0c\u8df3\u8dc3\u4e4b\u65f6\uff0c\u6709\u70b9\u4e0d\u4fbf\uff0c\u4f46\u4e00\u8def\u4e58\u9a6c\uff0c\u4e5f\u6ca1\u89c9 \u7740\u4ec0\u4e48\u3002\u6a0a\u82f1\u6795\u5200\u517b\u795e\uff0c\u672a\u4ea4\u4e94\u9f13\uff0c\u5373\u4fbf\u8d77\u8eab\uff0c\u7ed3\u4e86\u5e97\u5e10\uff0c\u9e21\u9e23\u4fbf\u8d70\u3002\u65b9\u65f6\u7684\u884c\u8def\u4e4b\u4eba\u6709\u4e24 \u53e5\u8bdd\u9053\uff1a\u201c\u672a\u665a\u5148\u6295\u5bbf\uff0c\u9e21\u9e23\u65e9\u770b\u5929\u3002\u201d\u5e97\u5c0f\u4e8c\u89c1\u4ed6\u5929\u8fd8\u672a\u4eae\uff0c\u5373\u4fbf\u767b\u7a0b\uff0c\u5012\u4e5f\u5e76\u65e0\u8be7\u5f02\u3002 \u4f46\u90a3\u4e9b\u5728\u4eac\u6d25\u4e00\u5e26\u5f80\u6765\u7684\u884c\u5546\uff0c\u8212\u670d\u60ef\u4e86\uff0c\u4e0d\u6bd4\u4e00\u822c\u5728\u5c0f\u57ce\u9547\u8d38\u6613\u7684\u5ba2\u5546\uff0c\u8fd9\u65f6\u5374\u90fd\u5728\u547c\u547c \u719f\u7761\u4e4b\u4e2d\uff0c\u5e76\u65e0\u4e00\u4eba\u4e0e\u4ed6\u540c\u8d70\u3002 \u3000\u3000\u6a0a\u82f1\u8d70\u51fa\u5c0f\u9547\uff0c\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u6b8b\u6708\u6b8b\u661f\u4e4b\u4e0b\uff0c\u56db\u5468\u9759\u6084\u6084\u7684\u8fde\u9e1f\u513f\u4e5f\u6ca1\u79bb\u5de2\uff0c\u6a0a\u82f1\u5fae \u5fae\u4e00\u7b11\uff0c\u50ac\u9a6c\u6025\u8d70\uff0c\u5230\u4e86\u4e2d\u5348\u65f6\u5206\uff0c\u79bb\u5f00\u7409\u7483\u6cb3\u6700\u5c11\u4ea6\u6709\u4e00\u767e\u4e94\u5341\u91cc\uff0c\u65e0\u610f\u95f4\u56de\u5934\u4e00\u770b\uff0c\u5ffd \u89c1\u90a3\u5ba2\u5546\u53c8\u8ddf\u5728\u540e\u9762\uff0c\u6a0a\u82f1\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u8fd9\u53ae\u7684\u9a6c\u600e\u4e48\u5982\u6b64\u5feb\u6377\uff1f\u96be\u9053\u4ed6\u662f\u6709\u610f\u8ddf\u8e2a\u6211 \u7684\u4e0d\u6210\uff1f\u90a3\u5ba2\u5546\u56fd\u5b57\u8138\uff0c\u6234\u4e00\u9876\u76ae\u5e3d\uff0c\u62ab\u4e00\u4ef6\u6597\u7bf7\uff0c\u8138\u4e0a\u53d1\u7740\u6cb9\u5149\uff0c\u770b\u4ed6\u7684\u795e\u6c14\uff0c\u770b\u4ed6\u7684\u9a91 \u9a6c\u59ff\u52bf\uff0c\u5b8c\u5168\u50cf\u4e00\u4e2a\u666e\u901a\u7684\u5546\u4eba\u3002\u6a0a\u82f1\u6349\u6478\u4e0d\u5b9a\uff0c\u731c\u4e0d\u900f\u4ed6\u662f\u6709\u610f\u8ddf\u8e2a\uff0c\u8fd8\u662f\u56e0\u4e3a\u4ed6\u7684\u9a6c\u7279 \u522b\u5feb\uff0c\u800c\u53c8\u6070\u5de7\u540c\u8def\uff1f \u3000\u3000\u6a0a\u82f1\u770b\u770b\u90a3\u5ba2\u5546\u4e00\u773c\uff0c\u7acb\u523b\u6325\u52a8\u76ae\u97ad\uff0c\u628a\u90a3\u5339\u9ec4\u8198\u9a6c\u6253\u5f97\u72c2\u5636\u75be\u8d70\uff0c\u8e39\u7684\u662f\u56db\u8e44\u5954\u4e91\uff0c \u6c99\u51e4\u98de\u8d77\uff0c\u90a3\u5ba2\u5546\u4ecd\u662f\u5b89\u95f2\u5730\u9a91\u5728\u9a6c\u80cc\uff0c\u624b\u4e0d\u626c\u97ad\uff0c\u770b\u6837\u5b50\u53c8\u4e0d\u4f3c\u6709\u610f\u8ddf\u8e2a\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u6a0a \u82f1\u5df2\u628a\u90a3\u5ba2\u5546\u8fdc\u8fdc\u5730\u7529\u5728\u540e\u9762\u3002 \u3000\u3000\u6a0a\u82f1\u8212\u4e86\u53e3\u6c14\uff0c\u4ed6\u4e3a\u4eba\u8c28\u614e\uff0c\u6545\u610f\u6487\u5f00\u5927\u8def\uff0c\u4e13\u62e3\u5c0f\u8def\u6765\u8d70\uff0c\u508d\u665a\u65f6\u5206\u5230\u4e86\u4fdd\u8d70\u4e1c\u8fb9\u767e \u4f59\u91cc\u7684\u767d\u6c9f\uff0c\u8fd9\u662f\u6bd4\u7409\u7483\u6cb3\u66f4\u5c0f\u7684\u5c0f\u9547\uff0c\u9547\u4e0a\u53ea\u6709\u4e00\u95f4\u50cf\u6837\u7684\u5ba2\u5e97\uff0c\u6a0a\u82f1\u6295\u5bbf\u4e4b\u3002 \u3000\u3000\u5403\u8fc7\u665a\u6273\uff0c\u5929\u8272\u5df2\u9ed1\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u8fd9\u5ba2\u5546\u603b\u4e0d\u4f1a\u5230\u8fd9\u4e2a\u5c0f\u5730\u65b9\u4e86\u3002\u54ea\u77e5\u5ff5\u5934\u624d\u52a8\uff0c\u95e8\u5916 \u4e00\u58f0\u9a6c\u5636\uff0c\u90a3\u5ba2\u5546\u5df2\u5728\u5ba2\u5e97\u95e8\u524d\u4e0b\u9a6c\u3002 \u3000\u3000\u6a0a\u82f1\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u4e00\u4e0b\u518d\u65e0\u7591\u95ee\uff1a\u8fd9\u5ba2\u5546\u5b9a\u7136\u662f\u8ffd\u8e2a\u81ea\u5df1\u7684\u4e86\u3002\u6a0a\u82f1\u8d81\u4ed6\u8fd8\u6ca1\u6709\u8d70\u8fdb\u5e97 \u95e8\uff0c\u614c\u5fd9\u6084\u68a2\u5730\u6e9c\u8fdb\u623f\u5185\uff0c\u53ea\u542c\u5f97\u90a3\u5ba2\u5546\u5728\u5916\u9762\u5429\u5490\u8981\u9152\u8981\u8089\uff0c\u6253\u6c34\u6d17\u8138\uff0c\u548c\u666e\u901a\u6295\u5bbf\u4eba\u5b8c \u5168\u4e00\u6837\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u77a7\u89c1\u4e86\u6ca1\u6709\u3002 \u3000\u3000\u8fd9\u5ba2\u5546\u5403\u996d\u4e4b\u540e\uff0c\u81ea\u53bb\u6b47\u606f\uff0c\u6b63\u5728\u6a0a\u82f1\u659c\u5bf9\u9762\u7684\u623f\u5b50\uff0c\u6a0a\u82f1\u60f4\u60f4\u4e0d\u5b89\uff0c\u629a\u5200\u5047\u5bd0\uff0c\u5b88\u5230 \u534a\u591c\uff0c\u5374\u65e0\u4e00\u70b9\u58f0\u606f\u3002\u6a0a\u82f1\u60f3\u9053\uff1a\u201c\u82e5\u7136\u4ed6\u662f\u6076\u610f\uff0c\u8ddf\u4e86\u4e24\u65e5\uff0c\u5e94\u8be5\u65e9\u5c31\u52a8\u624b\uff0c\u8fc7\u4e86\u4e09\u66f4\uff0c \u5916\u9762\u4ecd\u662f\u9759\u6084\u6084\u7684\uff0c\u53ea\u9690\u9690\u542c\u5230\u90bb\u623f\u7684\u6253\u9f3e\u58f0\u97f3\u3002\u6a0a\u82f1\u5ffd\u7136\u5185\u6025\u8d77\u6765\uff0c\u96be\u4ee5\u5fcd\u53d7\uff0c\u53ea\u597d\u63d0\u8d77 \u5b9d\u5200\uff0c\u51fa\u53bb\u89e3\u624b\u3002\u5395\u6240\u5728\u5916\u9762\u7684\u9662\u5b50\u659c\u89d2\uff0c\u6a0a\u82f1\u89e3\u4e86\u4e00\u534a\uff0c\u4ece\u865a\u63a9\u7684\u95e8\u7f1d\u4e2d\u7aa5\u51fa\uff0c\u5ffd\u89c1\u5bf9\u9762 \u5c4b\u9876\uff0c\u4f9d\u7a00\u6709\u6761\u4eba\u5f71\uff0c\u4f0f\u5728\u74e6\u810a\u4e0a\u5077\u4f3a\uff0c\u6a0a\u82f1\u5fc3\u5934\u4e00\u51db\uff0c\u8d76\u5fd9\u8349\u8349\u4e86\u4e8b\uff0c\u95ea\u8eab\u8d70\u51fa\uff0c\u53ea\u89c1\u758f \u661f\u6de1\u6708\uff0c\u591c\u8272\u6726\u80e7\uff0c\u90a3\u9ed1\u5f71\u4e00\u95ea\u4e0d\u89c1\uff0c\u82e5\u4e0d\u662f\u50cf\u6a0a\u82f1\u90a3\u6837\u7ec3\u8fc7\u6697\u5668\uff0c\u773c\u529b\u6781\u597d\u7684\u4eba\uff0c\u8fd8\u771f\u4ee5 \u4e3a\u662f\u4e00\u53ea\u9e1f\u513f\u63a0\u8fc7\u5c4b\u9876\u3002 \u3000\u3000\u6a0a\u82f1\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u662f\u54ea\u4f4d\u597d\u670b\u53cb\uff0c\u8bf7\u51fa\u6765\u76f8\u4f1a\u3002\u201d\u53cc\u6307\u4e00\u5f39\uff0c\u6253\u51fa\u4e00\u9897\u77f3\u5b50\uff0c\u90a3\u9ed1\u5f71\u5df2 \u4e0d\u77e5\u8eb2\u5230\u4ec0\u4e48\u5730\u65b9\uff0c\u518d\u4e0d\u51fa\u6765\uff0c\u5168\u4e0d\u7406\u4f1a\u4ed6\u8fd9\u4e00\u5957\u62db\u547c\u3002\u6a0a\u82f1\u72d0\u7591\u4e0d\u5b9a\uff0c\u4e09\u6b65\u5e76\u4f5c\u4e24\u6b65\uff0c\u8d70 \u56de\u5c4b\u5185\u5254\u4eae\u6cb9\u706f\uff0c\u53ea\u89c1\u5c4b\u5185\u5e76\u65e0\u5f02\u72b6\uff0c\u6a0a\u82f1\u518d\u4ed4\u7ec6\u4e00\u770b\uff0c\u731b\u5730\u4e00\u60ca\uff0c\u4ed6\u653e\u5728\u684c\u4e0a\u7684\u5305\u88f9\u672c\u662f \u653e\u5728\u6b63\u4e2d\u7684\uff0c\u73b0\u5728\u5df2\u7565\u7565\u79fb\u5411\u5de6\u8fb9\uff0c\u5305\u88f9\u4e0a\u7684\u7ed3\uff0c\u662f\u4ed6\u7279\u522b\u7ed3\u6210\u505a\u4e86\u8bb0\u53f7\u7684\uff0c\u5982\u4eca\u90a3\u7ed3\u7684\u5f62 \u5f0f\u4ea6\u5df2\u6539\u4e86\u3002\u6a0a\u82f1\u662f\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u4ed6\u623f\u4e2d\u5404\u7269\uff0c\u90fd\u653e\u5728\u4e00\u5b9a\u7684\u4f4d\u7f6e\uff0c\u6709\u4e9b\u5e76\u4f5c\u4e86\u8bb0\u53f7\uff0c \u4e00\u89c1\u53d8\u52a8\uff0c\u4fbf\u77e5\u6709\u6545\uff0c\u6562\u60c5\u90a3\u4eba\u7adf\u7136\u5c31\u5728\u8fd9\u7247\u523b\u4e4b\u95f4\uff0c\u641c\u4e86\u4ed6\u7684\u884c\u674e\u3002\u6a0a\u82f1\u6253\u5f00\u5305\u88f9\u4e00\u770b\uff0c \u5305\u88f9\u4e2d\u53ea\u6709\u51e0\u4ef6\u8863\u7269\uff0c\u73b0\u5728\u4f9d\u7136\u662f\u6309\u7740\u539f\u6765\u7684\u6837\u5f0f\u53e0\u653e\uff0c\u60f3\u89c1\u641c\u4ed6\u884c\u674e\u7684\u4eba\u4e5f\u662f\u6781\u4e3a\u7ec6\u5fc3\uff0c \u8fd9\u4eba\u5982\u6b64\u4ece\u5bb9\u4e0d\u8feb\uff0c\u65e2\u641c\u4ed6\u7684\u884c\u674e\uff0c\u53c8\u53bb\u7aa5\u4f3a\u4ed6\u7684\u884c\u8e2a\uff0c\u663e\u89c1\u662f\u4e2a\u96be\u4ee5\u5bf9\u4ed8\u7684\u52b2\u654c\u3002 \u3000\u3000\u6a0a\u82f1\u60f3\u4e86\u4e00\u4f1a\uff0c\u4e09\u5341\u516d\u8ba1\uff0c\u8d70\u4e3a\u4e0a\u8ba1\uff0c\u4e8e\u662f\u5728\u623f\u4e2d\u653e\u4e86\u4e00\u952d\u94f6\u5b50\uff0c\u4f5c\u4e3a\u5ba2\u5e97\u7684\u623f\u996d\u94b1\uff0c \u6084\u6084\u8d70\u51fa\u95e8\u5916\uff0c\u8de8\u4e0a\u5750\u9a91\uff0c\u8fde\u591c\u98de\u5954\u3002 \u3000\u3000\u591c\u95f4\u5c0f\u8def\u96be\u8fa8\uff0c\u5e78\u559c\u6a0a\u82f1\u7684\u5750\u9a91\u662f\u4e00\u5339\u597d\u9a6c\uff0c\u7a9c\u9ad8\u7eb5\u4f4e\uff0c\u5e76\u6ca1\u6709\u5c06\u6a0a\u82f1\u63bc\u4e0b\u6765\u3002\u8dd1\u4e86\u534a \u4e2a\u65f6\u8fb0\uff0c\u524d\u9762\u4e00\u7247\u7a7a\u6797\uff0c\u906e\u7740\u53bb\u8def\uff0c\u6a0a\u82f1\u8df3\u4e0b\u9a6c\u80cc\uff0c\u7d22\u6027\u7275\u9a6c\u8d70\u5165\u6797\u4e2d\uff0c\u51c6\u5907\u7a7f\u8fc7\u8fd9\u7247\u6797 \u5b50\uff0c\u518d\u89c5\u53bb\u8def\u3002\u5ffd\u542c\u5f97\u540e\u9762\u9a6c\u58f0\u957f\u5636\uff0c\u90a3\u5ba2\u5546\u7adf\u7136\u4e5f\u5728\u6df1\u591c\u4e4b\u4e2d\u7b56\u9a6c\u8ffd\u5230\uff0c\u800c\u4e14\u4e1d\u6beb\u4e0d\u987e\u6c5f \u6e56\u4e0a\u201c\u9022\u6797\u83ab\u5165\u201d\u7684\u7981\u5fcc\uff0c\u653e\u9a6c\u76f4\u5165\u6797\u4e2d\uff0c\u5728\u9a6c\u80cc\u4e0a\u62e8\u5f97\u4e24\u8fb9\u6811\u679d\u5580\u5587\u5587\u5730\u4f5c\u54cd\u3002\u6a0a\u82f1\u89c1\u4ed6 \u53ea\u662f\u4e00\u4eba\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u53cd\u6b63\u8981\u89c1\u4e2a\u6c34\u843d\u77f3\u51fa\uff0c\u6015\u4ed6\u4f55\u6765\uff1f\u201d\u6a2a\u5200\u5728\u624b\uff0c\u53cd\u800c\u8fce\u4e0a\u53bb\u9053\uff1a \u201c\u5c0a\u9a7e\u82e6\u6614\u8ffd\u903c\uff0c\u8fd9\u662f\u4e3a\u4f55\uff1f\u201d \u3000\u3000\u90a3\u4eba\u201c\u563f\u563f\u201d\u5e72\u7b11\uff0c\u5de6\u624b\u4e00\u6643\uff0c\u5c06\u624b\u4e2d\u7684\u706b\u6298\u70e7\u71c3\uff0c\u7a81\u7136\u5411\u811a\u8fb9\u7684\u8305\u8349\u4e00\u63b7\uff0c\u767b\u65f6\u70e7\u4e86 \u8d77\u6765\uff0c\u5de6\u53f3\u626b\u4e86\u4e00\u773c\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u5404\u8d70\u5404\u8def\uff0c\u5ba2\u5b98\u4f55\u6545\u76f8\u7591\uff1f\u201d\u6a0a\u82f1\u89c1\u4ed6\u51fa\u624b\uff0c\u5206\u660e\u662f\u987e \u5fcc\u81ea\u5df1\u6797\u4e2d\u53e6\u6709\u7406\u4f0f\uff0c\u6240\u4ee5\u70b9\u71c3\u8305\u8349\uff0c\u4ee5\u907f\u6697\u7b97\uff0c\u8fd9\u4e00\u624b\u82e5\u975e\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u6025\u4fc3\u4e4b\u95f4\uff0c \u5b9e\u662f\u96be\u4ee5\u60f3\u5230\u3002\u6a0a\u82f1\u54c8\u54c8\u4e00\u7b11\uff0c\u6a2a\u5200\u62a4\u80f8\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5c0a\u9a7e\u5728\u9ed1\u591c\u4e4b\u4e2d\u7b56\u9a6c\u8d76\u8def\uff0c\u8fd9\u4e5f\u672a \u514d\u592a\u5947\u602a\u4e86\u3002\u201d\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u7136\u5219\u5c0a\u9a7e\u5728\u9ed1\u591c\u4e4b\u4e2d\u8d76\u8def\uff0c\u5c31\u4e0d\u5947\u602a\u4e86\u4e48\uff1f\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u5f7c\u6b64 \u5f7c\u6b64\uff0c\u6240\u4ee5\u54b1\u4eec\u8fd8\u662f\u655e\u5f00\u4e86\u80f8\uff0c\u8bf4\u4e2a\u6e05\u695a\u7684\u597d\uff01\u6211\u662f\u9003\u72af\uff0c\u4f60\u662f\u4f55\u4eba\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u4f60\u662f\u9003 \u72af\uff0c\u6211\u662f\u8ddf\u7740\u9003\u72af\u8d70\u7684\u4eba\uff01\u201d\u6a0a\u82f1\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u662f\u516c\u5dee\uff0c\u4ffa\u5012\u8d70\u4e86\u773c\u4e86\u3002\u597d\u5440\uff0c\u6211\u5c31\u5728\u8fd9\u513f \u7b49\u7740\u5c0a\u9a7e\u52a8\u624b\uff01\u201d\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u81ea\u5df1\u8bf4\u7684\uff0c\u8c01\u8981\u548c\u4f60\u52a8\u624b\uff0c\u4f60\u65e2\u662f\u9003\u72af\uff0c\u4e3a\u4f55\u8fd8\u4e0d\u5feb \u8d70\uff1f\u201d \u3000\u3000\u6a0a\u82f1\u6014\u4e86\u4e00\u6014\uff0c\u559d\u9053\uff1a\u201c\u4f60\u7aef\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u90a3\u5ba2\u5546\u9053\uff1a\u201c\u771f\u4eba\u9762\u524d\u4e0d\u8bf4\u5047\u8bdd\uff0c\u4f60\u4e5f\u7aef \u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5bf9\u4f60\u660e\u8bf4\u4e86\u5417\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u4f60\u72af\u7684\u662f\u4ec0\u4e48\u7f6a\uff1f\u201d\u6a0a\u82f1 \u9053\uff1a\u201c\u6211\u662f\u591c\u95ef\u5929\u7262\uff0c\u56fe\u52ab\u4e8e\u8c26\u7684\u4eba\uff01\u201d\u90a3\u4eba\u9053\uff1a\u201c\u4e8e\u8c26\u7684\u4eba\u5934\u8c01\u5077\u53bb\u4e86\uff1f\u201d\u6a0a\u82f1\u9053\uff1a \u201c\u597d\uff0c\u6211\u5df2\u8bf4\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4f60\u662f\u4f55\u4eba\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u6211\u662f\u6697\u4e2d\u4fdd\u62a4\u4f60\u7684\u4eba\uff0c\u54b1\u4eec\u90fd\u662f\u4e00\u6761\u8def \u4e0a\u7684\u670b\u53cb\uff0c\u6211\u4e5f\u60f3\u89c1\u90a3\u4f4d\u5077\u5934\u7684\u4e49\u58eb\uff0c\u82e5\u627f\u4f60\u77a7\u5f97\u8d77\u670b\u53cb\u7684\u8bdd\uff0c\u5c31\u70e6\u4f60\u5f15\u89c1\u5982\u4f55\uff1f\u201d \u3000\u3000\u6a0a\u82f1\u773c\u73e0\u4e00\u8f6c\uff0c\u72d0\u7591\u4e0d\u5b9a\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u770b\u6765\u4ed6\u4e0d\u50cf\u662f\u8ffd\u6355\u6211\u7684\uff0c\u4f46\u600e\u4e48\u8ba4\u5b9a\u6211\u662f\u8981\u53bb \u89c1\u90a3\u5077\u5934\u7684\u4e49\u58eb\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u8fd8\u662f\u7591\u5fc3\u4e48\uff1f\u4f60\u8bd5\u60f3\u6211\u82e5\u662f\u516c\u5dee\uff0c\u4f55\u4ee5\u8ddf\u4e86\u4f60\u4e24\u65e5 \u4e24\u591c\uff0c\u8fd8\u4e0d\u4e0b\u624b\uff1f\u201d\u6a0a\u82f1\u4e0d\u58f0\u4e0d\u54cd\uff0c\u7a81\u7136\u8d70\u8fd1\u90a3\u5ba2\u5546\u7684\u5750\u9a91\uff0c\u90a3\u5339\u9a6c\u6b63\u5728\u5403\u8349\uff0c\u89c1\u751f\u4eba\u8d70 \u8fd1\uff0c\u84e6\u7136\u4e00\u58f0\u957f\u5636\uff0c\u6a0a\u82f1\u9053\uff1a\u201c\u5c0a\u9a7e\u8fd9\u5339\u5750\u9a91\uff0c\u76f8\u8c8c\u4e0d\u626c\uff0c\u786e\u662f\u795e\u9a8f\u4e4b\u6781\uff01\u201d\u4f38\u624b\u4e00\u62c9\uff0c\u90a3 \u4eba\u559d\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u90a3\u5339\u9a6c\u89c1\u6a0a\u82f1\u6765\u62c9\uff0c\u957f\u5636\u4eba\u7acb\uff0c\u4e3e\u8e44\u4fbf\u8e22\uff0c\u6a0a\u82f1\u4f0f\u8eab\u4e00\u6258\u9a6c\u8e44\uff0c\u53ea \u89c1\u9a6c\u8e44\u94c1\u4e0a\u70d9\u7740\u201c\u5927\u5185\u5fa1\u9a6c\u201d\u56db\u5b57\uff0c\u6a0a\u82f1\u4f0f\u8eab\u4e00\u6eda\uff0c\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u95f4\uff0c\u5728\u9a6c\u8e44\u4e4b\u4e0b\u9003\u5f00\uff0c\u54c8 \u54c8\u5927\u7b11\u9053\uff1a\u201c\u5982\u4eca\u6211\u8ba4\u5f97\u5c0a\u9a7e\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u6a0a\u82f1\u673a\u8b66\u5f02\u5e38\uff0c\u89c1\u8fd9\u5339\u9a6c\u4f3c\u7d20\u7ecf\u8bad\u7ec3\uff0c\u8d77\u4e86\u7591\u5fc3\uff0c\u4ed6\u77e5\u9053\u5fa1\u9a6c\u8eab\u4e0a\u5fc5\u6709\u8bb0\u53f7\uff0c\u8fd9\u4e00 \u8bd5\u679c\u7136\u8bd5\u4e86\u51fa\u6765\uff0c\u8fd9\u4e00\u4e0b\u7acb\u523b\u771f\u76f8\u5927\u767d\uff0c\u539f\u6765\u8fd9\u4eba\u7adf\u662f\u5927\u5185\u9ad8\u624b\uff0c\u6697\u4e2d\u8ffd\u8e2a\uff0c\u6240\u4ee5\u4e0d\u65e9\u52a8\u624b \u7684\u539f\u56e0\uff0c\u4e43\u662f\u4ed6\u8ba4\u4e3a\u5077\u5934\u4e4b\u4eba\uff0c\u5fc5\u662f\u6a0a\u82f1\u4e00\u515a\uff0c\u6240\u4ee5\u60f3\u4ece\u6a0a\u82f1\u8eab\u4e0a\u8ffd\u51fa\u90a3\u5077\u5934\u7684\u4e49\u58eb\u6765\uff0c\u770b \u4ed6\u6562\u5355\u9a91\u8ffd\u8e2a\uff0c\u800c\u4e14\u957f\u7ebf\u653e\u9e5e\uff0c\u628a\u6a0a\u82f1\u4f5c\u4e3a\u7ebf\u7d22\uff0c\u4f01\u56fe\u4e00\u7f51\u6253\u5c3d\uff0c\u8fd9\u4eba\u53ea\u6050\u8fd8\u4e0d\u53ea\u662f\u4e00\u4e2a\u666e \u901a\u7684\u536b\u58eb\u800c\u5df2\uff01 \u3000\u3000\u679c\u7136\u90a3\u4eba\u4e00\u70b9\u4e5f\u4e0d\u60ca\u614c\uff0c\u88ab\u6a0a\u82f1\u8bc6\u7834\u884c\u85cf\uff0c\u53cd\u800c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u597d\u773c\u529b\uff0c\u51ed\u8fd9\u4e00\u70b9\uff0c \u6211\u5c31\u503c\u5f97\u4e0e\u4f60\u4ea4\u4e2a\u670b\u53cb\u3002\u201d\u84e6\u5730\u6c89\u58f0\u559d\u9053\uff1a\u201c\u4f60\u542c\u8fc7\u9633\u5b97\u6d77\u7684\u540d\u5b57\u6ca1\u6709\uff1f\u4f60\u82e5\u60f3\u6211\u5251\u4e0b\u7559 \u6e05\uff0c\u5c31\u4e56\u4e56\u5730\u9886\u6211\u53bb\u6349\u90a3\u5077\u5934\u7684\u53db\u9006\uff01\u201d \u3000\u3000\u6a0a\u82f1\u5403\u4e86\u4e00\u60ca\uff0c\u5f53\u65f6\u5929\u4e0b\u51e0\u4f4d\u8457\u540d\u7684\u5251\u5ba2\uff0c\u5357\u6709\u5f20\u4e39\u67ab\uff0c\u5317\u6709\u4e4c\u8499\u592b\uff0c\u897f\u6709\u9633\u5b97\u6d77\uff0c\u4e1c \u6709\u77f3\u60ca\u6d9b\uff0c\u5176\u4e2d\u5f20\u4e39\u67ab\u4e0e\u4e4c\u8499\u592b\u5df2\u9690\u5c45\u591a\u5e74\uff0c\u77f3\u60ca\u6d9b\u56e0\u76d7\u5927\u5185\u5b9d\u5251\uff0c\u72af\u4e86\u91cd\u6848\uff0c\u9003\u4ea1\u6d77\u5916\uff0c \u4ea6\u662f\u4e45\u5df2\u4e0d\u95fb\u6d88\u606f\uff0c\u53ea\u6709\u9633\u5b97\u6d77\u7eb5\u6a2a\u897f\u5357\uff0c\u6c5f\u6e56\u4e0a\u4e0d\u65ad\u4f20\u51fa\u4ed6\u5fc3\u72e0\u624b\u8fa3\u7684\u52fe\u5f53\u3002\u8fd9\u9633\u5b97\u6d77\u636e \u8bf4\u662f\u8d64\u57ce\u6d3e\u7684\u540e\u8d77\u4e4b\u79c0\uff0c\u4f46\u8d64\u57ce\u6d3e\u7684\u524d\u8f88\u5374\u4ece\u4e0d\u7ba1\u4ed6\uff0c\u800c\u4e14\u9a91\u7684\u662f\u5927\u5185\u5fa1\u9a6c\uff0c\u60f3\u6765\u4ed6\u5df2\u53d7\u4e86 \u5f53\u4eca\u7687\u4e0a\u4e4b\u8058\uff0c\u90a3\u4e9b\u536b\u58eb\u6240\u79f0\u7684\u201c\u9633\u5927\u4eba\u201d\uff0c\u5927\u7ea6\u5c31\u662f\u6307\u4ed6\u4e86\u3002 \u3000\u3000\u6a0a\u82f1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u9547\u6444\u5fc3\u795e\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u9886\u4f60\u53bb\uff01\u201d\u8fc8\u524d\u4e00\u6b65\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u84e6\u5730\u4e00\u5200 \u5288\u4e0b\uff0c\u8fd9\u4e00\u5200\u51fa\u5176\u4e0d\u610f\uff0c\u6765\u5f97\u8fc5\u6377\u65e0\u6bd4\uff0c\u53ea\u542c\u5f97\u9633\u6d77\u5b97\u201c\u563f\u563f\u201d\u5730\u4e00\u58f0\u51b7\u7b11\uff0c\u53cc\u6307\u4e00\u642d\uff0c\u642d \u7740\u5200\u80cc\uff0c\u5f80\u524d\u4e00\u637a\uff0c\u6a0a\u82f1\u8fd9\u4e00\u5200\u8db3\u6709\u6570\u767e\u65a4\u6c14\u529b\uff0c\u7ade\u88ab\u4ed6\u53cc\u6307\u4e00\u637a\uff0c\u5200\u950b\u53cd\u5288\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3 \u65f6\u5feb\uff01\u9633\u5b97\u6d77\u5df2\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u53cd\u624b\u62d4\u51fa\u957f\u5251\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4e5f\u5403\u6211\u4e00\u5251\uff01\u201d\u6a0a\u82f1\u4e45\u7ecf\u5927 \u654c\uff0c\u5200\u67c4\u4e00\u65cb\uff0c\u9633\u5b97\u6d77\u53cc\u6307\u4e00\u677e\uff0c\u4e00\u5251\u5241\u5230\uff0c\u6a0a\u82f1\u865a\u6643\u4e00\u5200\uff0c\u53f3\u811a\u75be\u8d77\uff0c\u5200\u65ab\u638c\u5288\uff0c\u5b8c\u5168\u662f \u62fc\u547d\u7684\u62db\u6570\uff0c\u90a3\u4e00\u811a\u773c\u770b\u8e22\u5230\u9633\u6d77\u5b97\u6301\u5251\u7684\u624b\u8155\uff0c\u53ea\u542c\u5f97\u9633\u5b97\u6d77\u53c8\u662f\u201d\u563f\u563f\u201d\u5730\u4e00\u58f0\u51b7\u7b11\uff0c \u8eab\u5f62\u4e00\u95ea\uff0c\u53ea\u542c\u5f97\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u5251\u5df2\u4ece\u6a0a\u82f1\u7684\u80a9\u5934\u5241\u8fc7\uff0c\u8fd9\u8fd8\u662f\u9633\u5b97\u6d77\u6709\u5fc3\u8981\u7559\u201c\u6d3b \u53e3\u201d\uff0c\u6240\u4ee5\u8fd9\u4e00\u5251\u53ea\u662f\u523a\u7a7f\u4e86\u6a0a\u82f1\u7684\u57ab\u80a9\uff0c\u8981\u4e0d\u7136\u518d\u4f4e\u4e24\u5bf8\uff0c\u6a0a\u82f1\u7684\u7435\u7436\u9aa8\u5c31\u8981\u7a7f\u4e2a\u900f\u660e\u7a9f \u7abf\u3002 \u3000\u3000\u6a0a\u82f1\u7684\u4f2f\u7236\uff0c\u5f53\u5e74\u4e0e\u5f20\u4e39\u67ab\u9f50\u540d\uff0c\u53f7\u79f0\u201c\u4eac\u5e08\u4e09\u5927\u9ad8\u624b\u201d\uff0c\u5bb6\u4f20\u6b66\u827a\uff0c\u4ea6\u662f\u6781\u4e3a\u4e86\u5f97\uff0c \u9633\u5b97\u6d77\u8fd9\u4e00\u5251\u7a0d\u4e3a\u7559\u60c5\uff0c\u6a0a\u82f1\u8f6c\u8eab\u6025\u9000\uff0c\u9633\u5b97\u6d77\u6b63\u5f85\u6536\u5251\u518d\u523a\uff0c\u6a0a\u82f1\u9661\u5730\u5927\u559d\u4e00\u58f0\uff0c\u53cd\u624b\u4e00 \u5200\uff0c\u540e\u811a\u4e00\u8e6c\uff0c\u8fd9\u4e00\u62db\u6709\u4e2a\u540d\u5802\uff0c\u53eb\u505a\u201c\u864e\u5c3e\u811a\u56de\u9a6c\u5200\u201d\u3002\u907f\u5f97\u4e86\u5200\uff0c\u907f\u4e0d\u5f00\u811a\uff0c\u9633\u5b97\u6d77\u662f \u6d77\u5185\u6709\u6570\u7684\u9ad8\u624b\uff0c\u7109\u80fd\u7ed9\u4ed6\u8e22\u4e2d\uff0c\u4f46\u4ea6\u4e0d\u80fd\u4e0d\u5012\u9000\u4e09\u6b65\uff0c\u907f\u5176\u51f6\u950b\u3002\u6a0a\u82f1\u201c\u864e\u5c3e\u811a\u201d\u4e00\u8e6c\uff0c \u4e00\u8e22\u4e00\u65ab\uff0c\u5e76\u4e0d\u56de\u5934\uff0c\u53cd\u800c\u5f80\u524d\u4e00\u6251\uff0c\u7a81\u7136\u51b2\u8fc7\u706b\u5806\uff0c\u62fe\u8d77\u4e24\u5757\u70e7\u5f97\u706b\u5149\u718a\u718a\u7684\u5e72\u67f4\uff0c\u5411\u9633 \u5b97\u6d77\u731b\u63b7\u3002 \u3000\u3000\u539f\u6765\u6a0a\u82f1\u81ea\u77e5\u4e0d\u654c\uff0c\u90a3\u4e00\u5200\u4e00\u811a\uff0c\u770b\u4f3c\u53cd\u653b\uff0c\u5b9e\u662f\u8d70\u52bf\uff0c\u9633\u5b97\u6d77\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4eca\u65e5\u82e5\u6559 \u4f60\u9003\u51fa\u638c\u5fc3\uff0c\u6211\u9633\u67d0\u6c38\u4e0d\u5728\u6c5f\u6e56\u884c\u8d70\u3002\u201d\u90a3\u5e72\u67f4\u5e26\u7740\u706b\u5149\uff0c\u5288\u9762\u98de\u6765\uff0c\u9633\u5b97\u6d77\u547c\u7684\u4e00\u638c\uff0c\u5288 \u7a7a\u6253\u51fa\uff0c\u7adf\u5728\u79bb\u8eab\u4e03\u5c3a\u4e4b\u5916\uff0c\u5c06\u5e72\u67f4\u6253\u98de\uff0c\u706b\u5149\u7184\u706d\uff0c\u4f46\u90a3\u4e24\u5339\u9a6c\u53d7\u60ca\uff0c\u72c2\u5636\u4e71\u649e\uff0c\u9633\u5b97\u6d77 \u5c06\u9a6c\u5236\u4f0f\uff0c\u6a0a\u82f1\u5df2\u9003\u5165\u6797\u5b50\u3002 \u3000\u3000\u9633\u5b97\u6d77\u827a\u9ad8\u660e\u5927\uff0c\u4e0d\u987e\u201c\u9022\u6797\u83ab\u5165\u201d\u7684\u7981\u5fcc\uff0c\u501f\u7740\u706b\u5149\uff0c\u7d27\u7d27\u8ffd\u8d76\uff0c\u6a0a\u82f1\u53eb\u9053\uff1a\u201c\u5e76\u80a9 \u5b50\u7684\u51fa\u6765\u5475\uff01\u201d\u9633\u5b97\u6d77\u9053\uff1a\u201c\u4f60\u7eb5\u6709\u7406\u4f0f\uff0c\u6211\u4ea6\u4e0d\u4ff1\u3002\u201d\u5ffd\u542c\u5f97\u6797\u5b50\u5916\u9690\u9690\u6709\u9a6c\u5636\u4e4b\u58f0\uff0c\u9633 \u5b97\u6d77\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u4ee5\u4e3a\u6a0a\u82f1\u771f\u6709\u540c\u515a\uff0c\u98de\u8eab\u731b\u6251\uff0c\u63d0\u5200\u4fbf\u65ab\u3002\u4ed6\u662f\u60f3\u5148\u628a\u6a0a\u82f1\u4f24\u4e86\uff0c\u518d\u8fce \u6218\u6765\u654c\u3002 \u3000\u3000\u6a0a\u82f1\u7ed5\u6811\u75be\u8d70\uff0c\u9633\u5b97\u6d77\u4e00\u65f6\u4e4b\u95f4\u7adf\u4e5f\u65ab\u4ed6\u4e0d\u7740\uff0c\u8ffd\u5f97\u6025\u4e86\uff0c\u6a0a\u82f1\u53c8\u62db\u67b6\u4e00\u4e24\u5200\uff0c\u9633\u5b97\u6d77 \u6b66\u529f\u867d\u8fdc\u8f83\u6a0a\u82f1\u4e3a\u9ad8\uff0c\u4f46\u60f3\u5728\u4e09\u62db\u4e24\u5f0f\u4e4b\u5185\u5c06\u6a0a\u82f1\u6253\u5012\uff0c\u5374\u4e5f\u4e0d\u80fd\u3002\u9633\u5b97\u6d77\u5929\u6012\uff0c\u90a3\u53e3\u957f\u5251 \u5de6\u7a7f\u53f3\u523a\uff0c\u8ffd\u7740\u6a0a\u82f1\u7684\u8eab\u5f62\uff0c\u6beb\u4e0d\u653e\u677e\uff0c\u5de6\u624b\u5374\u5728\u6697\u5668\u56ca\u4e2d\u638f\u51fa\u4e86\u4e00\u628a\u94c1\u83b2\u5b50\uff0c\u4e00\u9897\u9897\u5730\u5f39 \u51fa\u53bb\uff0c\u4e13\u53d6\u6a0a\u82f1\u7684\u5341\u4e8c\u9ebb\u7a74\uff0c\u6a0a\u82f1\u9760\u7740\u6811\u6728\u906e\u853d\uff0c\u8eb2\u8eb2\u95ea\u95ea\uff0c\u7f20\u4e86\u4e00\u4f1a\uff0c\u9633\u5b97\u6d77\u559d\u58f0 \u201c\u7740\u201d\uff0c\u4e00\u811a\u8e22\u6298\u4e86\u4e00\u68f5\u5c0f\u6811\uff0c\u6a0a\u82f1\u6b63\u7ed5\u6811\u6253\u5708\uff0c\u5c0f\u6811\u4e00\u5012\uff0c\u73b0\u51fa\u7a7a\u9699\uff0c\u4f46\u542c\u5f97\u201c\u55d6\u201d\u7684\u4e00 \u58f0\uff0c\u4e00\u9897\u94c1\u83b2\u5b50\u5df2\u6253\u7740\u4e86\u6a0a\u82f1\u80cc\u5fc3\u7684\u201c\u5929\u656c\u7a74\u201d\u3002\u6a0a\u82f1\u8eab\u4e0a\u7a7f\u6709\u62a4\u5fc3\u8f6f\u7532\uff0c\u9976\u662f\u5982\u6b64\uff0c\u80cc\u5fc3 \u4e5f\u9178\u9ebb\u53d1\u75db\u3002 \u3000\u3000\u6a0a\u82f1\u5927\u53eb\u4e00\u58f0\uff0c\u731b\u7136\u6251\u51fa\uff0c\u53cd\u624b\u4e00\u78d5\uff0c\u53c8\u5c06\u4e24\u9897\u94c1\u83b2\u5b50\u6253\u98de\uff0c\u8fd9\u65f6\u5df2\u8d70\u5230\u5bc6\u6797\u6df1\u5904\uff0c\u706b \u5149\u5728\u8fdc\uff0c\u751a\u4e3a\u5fae\u5f31\uff0c\u6797\u4e2d\u8346\u68d8\u751a\u591a\uff0c\u6a0a\u82f1\u659c\u8eab\u4e00\u6251\uff0c\u7ade\u51b2\u5165\u4e00\u5806\u8346\u68d8\u9769\u83bd\u4e4b\u4e2d\uff0c\u62e9\u52a8\u5b9d\u5200\u56db \u5904\u4e71\u626b\uff0c\u62ab\u8346\u65a9\u68d8\uff0c\u5288\u5f00\u4e00\u6761\u9003\u8def\uff0c\u9633\u5b97\u6d77\u7684\u5251\u8fdc\u4e0d\u5982\u6a0a\u82f1\u7f05\u5200\u7684\u950b\u5229\uff0c\u8ffd\u5165\u8346\u68d8\u4e1b\u4e2d\uff0c\u88ab \u52fe\u7740\u8863\u88f3\uff0c\u5230\u62d4\u5f00\u4e4b\u65f6\uff0c\u6a0a\u82f1\u5df2\u8d8a\u5165\u8d8a\u6df1\uff0c\u6811\u6797\u91cc\u9ed1\u9edd\u9edd\u7684\u51e0\u4e4e\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u9633\u5b97\u6d77\u5929\u4e3a\u607c\u6012\uff0c\u7a81\u7136\u5c06\u706b\u6298\u5b50\u4e00\u71c3\uff0c\u7528\u529b\u4e00\u63b7\uff0c\u706b\u6298\u5b50\u5e76\u4e0d\u53d7\u529b\uff0c\u7adf\u4e5f\u7ed9\u4ed6\u63b7\u51fa\u4e24\u4e08 \u5f00\u5916\uff0c\u843d\u5730\u5373\u71c3\uff0c\u9633\u5b97\u6d77\u821e\u8d77\u957f\u5251\uff0c\u65bd\u5c55\u201c\u767b\u840d\u6e21\u6c34\u201d\u7684\u8f7b\u529f\uff0c\u7eb5\u8eab\u8dc3\u8d70\uff0c\u8db3\u8e0f\u8346\u68d8\uff0c\u867d\u7136 \u4ecd\u65f6\u65f6\u88ab\u52fe\u7740\u624b\u8db3\uff0c\u4f46\u9633\u5b97\u6d77\u5df2\u5168\u4e0d\u987e\u8fd9\u70b9\u76ae\u8089\u4e4b\u4f24\uff0c\u5168\u529b\u8ffd\u8d76\uff0c\u8f6c\u77ac\u4fbf\u51b2\u51fa\u8346\u68d8\u906e\u8fe2\u7684\u5bc6 \u84c4\u8349\u83bd\uff0c\u968f\u65f6\u62fe\u8d77\u71c3\u70e7\u7684\u5e72\u679d\uff0c\u5411\u524d\u731b\u63b7\uff0c\u4e0d\u591a\u65f6\u6811\u6797\u4e2d\u5df2\u8d77\u4e86\u5341\u591a\u5904\u706b\u5934\uff0c\u7167\u89c1\u4e86\u6a0a\u82f1\u7684 \u80cc\u5f71\u3002 \u3000\u3000\u8d8a\u8ffd\u8d8a\u8fd1\uff0c\u5ffd\u53c8\u542c\u5f97\u9a6c\u58f0\u5636\u9e23\uff0c\u770b\u4f3c\u751a\u8fdc\uff0c\u8f6c\u77ac\u4fbf\u8fd1\uff0c\u90a3\u7247\u6797\u5b50\u4e0d\u8fc7\u4e09\u671b\u591a\u957f\uff0c\u6a0a\u82f1\u4e00 \u9f13\u4f5c\u6c14\uff0c\u51b2\u51fa\u6797\u5916\uff0c\u9633\u5b97\u6d77\u78d4\u78d4\u7b11\u9053\uff1a\u201c\u8fd8\u60f3\u9003\u4e48\uff1f\u201d\u4e00\u6296\u624b\u53c8\u6253\u51fa\u4e09\u9897\u94c1\u83b2\u5b50\uff0c\u8fd9\u65f6\u5168\u65e0 \u906e\u853d\uff0c\u6a0a\u82f1\u7528\u5200\u80cc\u78d5\u98de\u4e00\u9897\uff0c\u95ea\u8eab\u907f\u5f00\u6253\u54bd\u5589\u7684\u4e00\u9897\uff0c\u7b2c\u4e09\u9897\u94c1\u83b2\u5b50\u5370\u907f\u4e0d\u5f00\uff0c\u6b63\u6b63\u88ab\u6253\u4e2d \u817f\u5f2f\u7a74\u9053\uff0c\u201c\u535c\u201d\u5730\u8dea\u5012\u5730\u4e0a\u3002 \u3000\u3000\u6797\u4e2d\u706b\u5149\u718a\u718a\uff0c\u6797\u5916\u591c\u7a7a\uff0c\u6d6e\u4e91\u5df2\u6563\uff0c\u9732\u51fa\u4e00\u8f6e\u660e\u6708\uff0c\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u9633\u5b97\u6d77\u54c8\u54c8\u5927 \u7b11\uff0c\u4e0a\u524d\u64d2\u6349\uff0c\u5ffd\u542c\u5f97\u9a8f\u9a6c\u957f\u5636\uff0c\u9a6c\u8e44\u58f0\u6709\u52a0\u5bc6\u96f7\u75be\u54cd\uff0c\u9633\u5b97\u6d77\u5403\u4e86\u4e00\u60ca\uff0c\u542c\u8fd9\u58f0\u97f3\uff0c\u4e0d\u77e5 \u6765\u4eba\u5982\u4f55\uff0c\u8fd9\u5339\u9a6c\u5374\u662f\u5929\u4e0b\u7f55\u6709\u7684\u795e\u9a6c\uff01 \u3000\u3000\u4f46\u89c1\u4e00\u56e2\u767d\u5f71\uff0c\u75be\u98de\u800c\u6765\uff0c\u9a6c\u5636\u58f0\u560e\u7136\u800c\u6b62\uff0c\u4e00\u5339\u767d\u9a6c\u5df2\u5230\u4e86\u773c\u524d\uff0c\u9a6c\u80cc\u4e0a\u8df3\u4e0b\u4e00\u4e2a\u767d \u8863\u5c11\u5e74\uff0c\u770b\u6765\u4e0d\u8fc7\u5341\u516d\u4e03\u5c81\u7684\u6837\u5b50\uff0c\u8eab\u8eaf\u7ec6\u5c0f\uff0c\u6e05\u79c0\u975e\u5e38\uff0c\u4e4d\u773c\u4e00\u770b\uff0c\u8fd8\u50cf\u4e2a\u521a\u51fa\u4e66\u623f\u7684\u5c0f \u5b66\u751f\uff0c\u8fd9\u767d\u8863\u5c11\u5e74\u770b\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u539f\u6765\u7adf\u662f\u9633\u5927\u603b\u7ba1\uff0c\u9633\u5927\u4eba\u54ea\uff01\u4f60\u8ffd\u4ed6\u505a\u4ec0\u4e48\uff1f\u201d\u9633\u5b97 \u6d77\u5fc3\u4e2d\u4e00\u51db\uff0c\u8fd9\u767d\u8863\u5c11\u5e74\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u7adf\u7136\u4e00\u53e3\u5c31\u9053\u7834\u4e86\u4ed6\u7684\u6765\u5386\u3002 \u3000\u3000\u9633\u5b97\u6d77\u60ca\u7591\u4e0d\u5b9a\uff0c\u957f\u5251\u4e00\u6307\uff0c\u53d1\u8bdd\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff0c\u6562\u6765\u591a\u7ba1\u95f2\u4e8b\uff1f\u201d\u90a3\u5c11\u5e74\u51b7\u51b7\u5730\u77aa \u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5929\u4e0b\u4e8b\u5929\u4e0b\u4eba\u7ba1\uff0c\u4f60\u5c0f\u7237\u6700\u53d7\u7684\u5c31\u662f\u6253\u62b1\u4e0d\u5e73\uff01\u201d\u5b8c\u5168\u662f\u5145\u5927\u4eba\u53e3\u6c14\u7684\u5b69\u5b50 \u53e3\u543b\uff0c\u9633\u5b97\u6d77\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u7ba1\u4ed6\u662f\u8c01\uff0c\u4ed6\u5c31\u662f\u4e00\u51fa\u5a18\u80ce\u4fbf\u7ec3\u6b66\u529f\uff0c\u4e5f\u5f3a\u4e0d\u5230\u54ea\u91cc \u53bb\uff01\u201d\u7b11\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u5e73\uff0c\u8981\u4f60\u6253\u62b1\u5566\uff1f\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u4ee5\u5927\u538b\u5c0f\uff0c\u6b3a\u4fae\u4eba\uff01\u201d\u9633\u5b97\u6d77 \u7b11\u9053\uff1a\u201c\u4ed6\u53c8\u4e0d\u662f\u50cf\u4f60\u8fd9\u822c\u7684\u5c0f\u5b69\u5b50\uff0c\u600e\u80fd\u8bf4\u6211\u662f\u4ee5\u5927\u538b\u5c0f\uff1f\u201d\u9633\u5b97\u6d77\u89c1\u8fd9\u5c11\u5e74\u4eba\u7a1a\u6c14\u672a \u6d88\uff0c\u5341\u5206\u6709\u8da3\uff0c\u5fc3\u60f3\u90a3\u5927\u7684\u5df2\u4e2d\u4e86\u6211\u7684\u6697\u5668\uff0c\u4e0d\u80fd\u8fdc\u9003\uff0c\u4e14\u4e50\u5f97\u9017\u8fd9\u5b69\u5b50\u4e00\u9017\u3002\u90a3\u767d\u8863\u5c11\u5e74 \u89c1\u9633\u5b97\u6d77\u53cd\u95ee\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4ee5\u4f60\u9633\u5927\u4eba\u7684\u6210\u540d\u5251\u5ba2\u8eab\u4efd\uff0c\u5374\u7528\u6697\u5668\u4f24\u4e86\u4e00\u4e2a\u5e73\u5e38\u7684\u9556\u5ba2\uff0c \u8fd9\u8fd8\u80fd\u8bf4\u4e0d\u662f\u4ee5\u5f3a\u6b3a\u5f31\u3002\u4ee5\u5927\u538b\u5c0f\u5417\uff1f\u8fd9\u6837\u7684\u4e0d\u5e73\u4e4b\u4e8b\uff0c\u8ddf\u975e\u6211\u4e0d\u77a7\u89c1\uff0c\u77a7\u89c1\u4e86\u6211\u4fbf\u8981 \u7ba1\uff01\u201d \u3000\u3000\u6a0a\u82f1\u5728\u5730\u4e0b\u81ea\u884c\u63c9\u6413\u817f\u5f2f\u7a74\u9053\uff0c\u8212\u7b4b\u6d3b\u7edc\uff0c\u4ed6\u5df2\u662f\u77a7\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u8fd9\u767d\u8863\u5c11\u5e74\u6b63\u5c31\u662f\u620f \u5f04\u5c0f\u864e\u5b50\u90a3\u4e2a\u5c11\u5e74\uff0c\u4e5f\u6b63\u662f\u90a3\u665a\u5728\u57ce\u5899\u4e4b\u4e0a\u7528\u91d1\u82b1\u6697\u5668\u7528\u4e86\u4e24\u540d\u5927\u5185\u4f8d\u536b\u7684\u90a3\u4e2a\u5c11\u5e74\u3002\u542c\u4ed6 \u8bf4\u8bdd\uff0c\u4e0d\u89c9\u5fc3\u4e2d\u6697\u547c\u60ed\u6127\u3002\u8fd9\u767d\u8863\u5c11\u5e74\u7adf\u7136\u628a\u4ed6\u5f53\u4f5c\u4e00\u4e2a\u666e\u901a\u7684\u9556\u5ba2\u3002 \u3000\u3000\u9633\u5b97\u6d77\u66f4\u6700\u597d\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u6253\u62b1\u4e0d\u5e73\uff0c\u6211\u82e5\u4e0e\u4f60\u52a8\u624b\uff0c\u8fd9\u5c82\u4e0d\u66f4\u662f\u4ee5\u5927\u538b\u5c0f\u5417\uff1f\u201d \u90a3\u5c11\u5e74\u77aa\u8d77\u53cc\u773c\uff0c\u9053\uff1a\u201c\u5728\u4f60\u662f\u4e2a\u6210\u540d\u7684\u5251\u5ba2\uff0c\u8fde\u8fd9\u70b9\u89c1\u8bc6\u4e5f\u6ca1\u6709\u3002\u201d\u9633\u5b97\u6d77\u9053\uff1a\u201c\u600e \u4e48\uff1f\u201d\u90a3\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u751f\u5f97\u725b\u9ad8\u9a6c\u5927\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u5f3a\u5f31\u5927\u5c0f\uff0c\u662f\u7528\u5e74\u7eaa\u6765\u5ea6\u91cf\u7684\u4e48\uff1f\u8001\u5b9e \u8bf4\uff0c\u82e5\u975e\u4f60\u662f\u9633\u5927\u603b\u7ba1\uff0c\u6211\u8fd8\u4e0d\u5c51\u4e0e\u4f60\u52a8\u624b\u5462\uff01\u201d\u9633\u5b97\u6d77\u4e00\u542c\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u53e3\u6c14\u597d\u5927\uff0c \u7adf\u7136\u8981\u4e0e\u6211\u6273\u5e73\u8eab\u4efd\u4e86\u3002\u201d\u8d8a\u662f\u8fd9\u6837\uff0c\u4ed6\u8d8a\u89c9\u5f97\u4e0d\u4fbf\u52a8\u624b\u3002\u8981\u77e5\u6b66\u6797\u4e4b\u4e2d\uff0c\u6700\u8bb2\u8eab\u4efd\uff0c\u82e5\u7136 \u4f20\u5c06\u51fa\u53bb\uff0c\u8bf4\u662f\u9633\u5b97\u6d77\u548c\u4e00\u4e2a\u4e73\u81ed\u672a\u9664\u7684\u5927\u5b69\u5b50\u52a8\u624b\uff0c\u5c82\u975e\u7b11\u8bdd\uff1f \u3000\u3000\u767d\u8863\u5c11\u5e74\u55d6\u5730\u62d4\u51fa\u4e00\u628a\u77ed\u5251\uff0c\u559d\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fd8\u4e0d\u8fdb\u62db\uff1f\u201d\u5b9d\u5251\u51fa\u9798\uff0c\u5bd2\u5149\u8000\u76ee\uff0c\u9633\u5b97 \u6d77\u53c8\u662f\u4e00\u60ca\uff0c\u82e5\u975e\u773c\u89c1\uff0c\u771f\u4e0d\u6562\u76f8\u4fe1\u8fd9\u7a1a\u6c14\u672a\u6d88\u7684\u5c0f\u5b50\u5c45\u7136\u62e5\u6709\u4e16\u95f4\u7f55\u89c1\u7684\u540d\u9a6c\u5b9d\u5251\uff0c\u800c\u4e14 \u80c6\u91cf\u5927\u5f97\u51fa\u5947\u3002\u9633\u5b97\u6d77\u867d\u7136\u60ca\u5947\u4e8e\u4ed6\u7684\u540d\u9a6c\u5b9d\u5251\uff0c\u5374\u8fd8\u4e0d\u66fe\u5c06\u8fd9\u5c11\u5e74\u653e\u5728\u5fc3\u5185\uff0c\u7b11\u9053\uff1a\u201c\u4f60 \u771f\u4e2a\u8981\u7ba1\uff1f\u201d\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u5e9f\u8bdd\u5c11\u8bf4\uff0c\u8fdb\u62db\uff01\u201d\u9633\u5b97\u6d77\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u56de\u5230\u5e08\u5a18\u8eab\u8fb9\u591a \u5b66\u51e0\u5e74\u5427\uff0c\u6211\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u5c82\u80fd\u4e0e\u4f60\u4e00\u822c\u89c1\u8bc6\u3002\u201d\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u8fdb\u4e0d\u8fdb\u62db\uff1f\u4f60\u4e0d\u52a8\u624b\uff0c \u6211\u5c31\u4e0d\u518d\u8ba9\u4e86\u3002\u201d\u9633\u5b97\u6d77\u9053\uff1a\u201c\u4f60\u4f7f\u4e00\u8def\u5251\u6cd5\u5f85\u6211\u770b\u770b\uff0c\u770b\u4f60\u7684\u5e08\u7236\u662f\u8c01\uff1f\u201d\u9633\u5b97\u6d77\u6253\u8d70\u4e3b \u610f\u7edd\u4e0d\u8fd8\u624b\uff0c\u60f3\u4ece\u4ed6\u7684\u5251\u6cd5\u4e2d\u770b\u51fa\u4ed6\u7684\u5e08\u95e8\u5b97\u6d3e\u3002\u90a3\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5c31\u770b\u5427\uff0c\u770b \u5251\uff01\u201d\u5251\u67c4\u4e00\u6296\uff0c\u5530\u5730\u5c31\u662f\u4e00\u5251\uff0c\u9633\u5b97\u6d77\u9a73\u8d77\u53cc\u6307\uff0c\u5f85\u63a8\u5f00\u4ed6\u7684\u5251\u5203\uff0c\u54ea\u77e5\u8fd9\u4e00\u5251\u770b\u4f3c\u4e4e\u4e4e \u65e0\u5947\uff0c\u7adf\u7136\u5201\u94bb\u4e4b\u6781\uff0c\u523a\u5230\u4e2d\u9014\uff0c\u7a81\u7136\u4e00\u4e2a\u56de\u5200\u53cd\u524a\uff0c\u9633\u5b97\u6d77\u82e5\u662f\u4ecd\u7136\u63a8\u53bb\uff0c\u90a3\u4e24\u6839\u624b\u6307\u5c31 \u5fc5\u7136\u8981\u88ab\u524a\u65ad\u3002 \u3000\u3000\u9633\u5b97\u6d77\u771f\u4e0d\u6127\u662f\u4e45\u7ecf\u5927\u654c\u7684\u6210\u540d\u5251\u5ba2\uff0c\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u7684\u5239\u90a3\u4e4b\u95f4\uff0c\u5251\u950b\u53ea\u5dee\u4e94\u5bf8\u5c31\u5272 \u5230\u6307\u5934\u4e4b\u9645\uff0c\u624b\u638c\u4e00\u7ffb\uff0c\u4e00\u62db\u201c\u9f99\u5f62\u7a7f\u638c\u201d\u7adf\u7136\u5728\u5251\u8eab\u4e0a\u9762\u5c11\u8bb8\u4e4b\u5904\uff0c\u51e0\u4e4e\u8d34\u7740\u5251\u67c4\uff0c\u5f3a\u884c \u53cd\u624b\u64d2\u62ff\uff0c\u90a3\u767d\u8863\u5c11\u5e74\u7684\u5251\u62db\u5df2\u7ecf\u53d1\u51fa\uff0c\u201c\u5530\u201d\u5730\u4e00\u5251\u4ece\u9633\u5b97\u6d77\u8033\u4fa7\u5241\u8fc7\uff0c\u63b7\u4e86\u4e2a\u7a7a\uff0c\u9633\u5b97 \u6d77\u7684\u638c\u7f18\u5df2\u5207\u5230\u4ed6\u5c0f\u81c2\u7684\u201c\u66f2\u6c60\u7a74\u201d\u3002\u987b\u77e5\u9ad8\u624b\u76f8\u6597\uff0c\u53ea\u5dee\u6beb\u5398\uff0c\u8fd9\u4e00\u4e0b\u7a81\u7136\u7ed9\u9633\u5b97\u6d77\u53cd\u5ba2 \u4e3a\u4e3b\uff0c\u53ea\u8981\u4ed6\u638c\u529b\u4e00\u53d1\uff0c\u767d\u8863\u5c11\u5e74\u8fd9\u6761\u624b\u81c2\uff0c\u5c31\u7b97\u5356\u4e0e\u4ed6\u4e86\u3002\u6a0a\u82f1\u5728\u5730\u4e0b\u770b\u5f97\u9a87\u7136\u5fc3\u8df3\uff0c \u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u987e\u4e0d\u5f97\u5c0f\u817f\u8fd8\u662f\u9178\u9ebb\u4e4f\u529b\uff0c\u638c\u5fc3\u4e00\u6309\uff0c\u6491\u5730\u98de\u8d77\uff0c\u5ffd\u542c\u5f97\u9633\u5b97\u6d77\u201c\u566b\u201d\u4e86\u4e00 \u58f0\uff0c\u53ea\u89c1\u90a3\u767d\u8863\u5c11\u5e74\u5251\u67c4\u5f80\u91cc\u4e00\u649e\uff0c\u649e\u7684\u4e5f\u662f\u9633\u5b97\u6d77\u5c0f\u81c2\u4e0a\u7684\u201c\u66f2\u6c60\u7a74\u201d\uff0c\u9633\u5b97\u6d77\u82e5\u4e0d\u6536 \u624b\uff0c\u4e24\u4eba\u7684\u624b\u81c2\u90fd\u8981\u540c\u65f6\u6298\u65ad\uff0c\u9633\u5b97\u6d77\u54ea\u80af\u4e0e\u4ed6\u4e24\u8d25\u4ff1\u4f24\uff0c\u624b\u5fc3\u5f80\u5916\u4e00\u767b\uff0c\u5f3a\u628a\u8eab\u5f62\u5e26\u52a8\u4e24 \u6b65\uff0c\u4e24\u4eba\u4e00\u5408\u5373\u5206\uff0c\u5404\u8131\u9669\u5883\uff0c\u6a0a\u82f1\u677e\u4e86\u53e3\u6c14\uff0c\u53c8\u4e00\u8de4\u8dcc\u5728\u5730\u4e0a\u3002 \u3000\u3000\u54ea\u77e5\u4e00\u6ce2\u672a\u5e73\uff0c\u4e00\u6ce2\u53c8\u8d77\uff0c\u6a0a\u82f1\u4ee5\u4e3a\u4e24\u4eba\u5206\u5f00\u4e4b\u540e\uff0c\u5fc5\u5f53\u91cd\u6574\u65d7\u9f13\uff0c\u518d\u884c\u76f8\u6597\u3002\u54ea\u77e5\u9633 \u5b97\u6d77\u4e0e\u767d\u8863\u5c11\u5e74\u90fd\u62b1\u7740\u540c\u4e00\u5fc3\u613f\uff0c\u8981\u8d81\u654c\u4eba\u5598\u606f\u672a\u5b9a\u4e4b\u9645\uff0c\u7acb\u4e0b\u6740\u624b\u3002\u4e24\u4eba\u653b\u52bf\u90fd\u662f\u4e0d\u4f9d\u5e38 \u8f68\uff0c\u4f46\u9633\u5b97\u6d77\u60ef\u7ecf\u7a74\u654c\uff0c\u62a2\u5148\u4e86\u4e00\u6b65\uff0c\u767d\u8863\u5c11\u5e74\u5251\u5c16\u521a\u521a\u6446\u52a8\uff0c\u4ed6\u53cc\u638c\u5df2\u6253\u4e86\u4e2a\u5708\u5708\uff0c\u500f\u5730 \u8feb\u8fdb\u4e86\u767d\u8863\u5c11\u5e74\u9632\u536b\u7684\u5185\u5708\uff0c\u767d\u8863\u5c11\u5e74\u7684\u53cc\u81c2\u7acb\u5373\u88ab\u4ed6\u5c01\u4f4f\u3002\u9633\u5b97\u6d77\u638c\u6cd5\u4e43\u662f\u9752\u57ce\u6d3e\u7684\u4e0d\u4f20 \u4e4b\u79d8\uff0c\u638c\u52bf\u6089\u4eff\u592a\u6781\u56fe\u5f62\uff0c\u521a\u67d4\u5e76\u6d4e\uff0c\u6b64\u65f6\u53ea\u8981\u4ed6\u5c06\u4e24\u624b\u7684\u5708\u5b50\u7a0d\u7a0d\u653e\u5927\uff0c\u4fbf\u80fd\u4ee5\u624b\u8155\u5236\u6b7b \u5bf9\u65b9\u5173\u8282\uff0c\u654c\u4eba\u7eb5\u6709\u5229\u5203\u5728\u624b\uff0c\u4ea6\u65e0\u80fd\u4e3a\u529b\u3002\u6a0a\u82f1\u867d\u7136\u4e0d\u8bc6\u9752\u57ce\u6d3e\u7684\u638c\u6cd5\uff0c\u4f46\u4ed6\u7a76\u662f\u4e2a\u5927\u884c \u5bb6\uff0c\u770b\u51fa\u5176\u4e2d\u7684\u5965\u5999\uff1b\u8bbe\u8eab\u5904\u5730\uff0c\u4ea6\u65e0\u89e3\u6551\u4e4b\u6cd5\uff0c\u4e0d\u7981\u53c8\u662f\u201c\u5509\u5440\u201d\u4e00\u58f0\u53eb\u5c06\u8d77\u6765\u3002 \u3000\u3000\u6a0a\u82f1\u8fd8\u672a\u6765\u5f97\u53ca\u8df3\u8d77\uff0c\u4f46\u542c\u5f97\u9633\u5b97\u6d77\u4e0e\u90a3\u767d\u8863\u5c11\u5e74\u51e0\u4e4e\u662f\u540c\u58f0\u5c16\u53eb\uff0c\u6a0a\u82f1\u773c\u82b1\u64a9\u4e71\uff0c\u672a \u66fe\u770b\u5f97\u6e05\u695a\uff0c\u7adf\u4e0d\u77e5\u90a3\u5c11\u5e74\u7528\u7684\u662f\u4ec0\u4e48\u624b\u6cd5\uff0c\u9633\u5b97\u6d77\u7684\u8863\u8896\u5df2\u7ed9\u4ed6\u5272\u65ad\u4e86\u534a\u622a\uff0c\u8dc4\u8dc4\u8e09\u8e09\u5730 \u5012\u9000\u51e0\u6b65\uff0c\u6a0a\u82f1\u72c2\u559c\u53eb\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u771f\u884c\uff01\u201d\u4ed6\u5374\u4e0d\u77e5\u90a3\u5c11\u5e74\u7684\u624b\u8155\u4e5f\u7ed9\u9633\u5b97\u6d77\u5f39\u4e86\u4e00 \u4e0b\uff0c\u767b\u65f6\u6cdb\u8d77\u4e86\u4e2a\u7ea2\u5708\uff0c\u6bd4\u8d77\u6765\u8fd8\u662f\u767d\u8863\u5c11\u5e74\u5403\u4e8f\u8f83\u5927\u3002 \u3000\u3000\u4f46\u9633\u5b97\u6d77\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u4ed6\u51fa\u9053\u4ee5\u6765\uff0c\u8fd8\u672a\u66fe\u78b0\u8fc7\u654c\u624b\uff0c\u800c\u4e14\u7adf\u88ab\u4e00\u4e2a\u7a1a\u6c14\u672a\u6d88\u7684\u5c0f\u5b69\u5b50 \u5272\u53bb\u4e86\u534a\u622a\u8863\u8896\uff0c\u8138\u4e0a\u5df2\u662f\u70ed\u8fa3\u8fa3\u5730\u6302\u4e0d\u4f4f\u4e86\uff0c\u767d\u8863\u5c11\u5e74\u8d81\u770b\u4ed6\u607c\u6012\u6c14\u6d6e\u4e4b\u9645\uff0c\u6325\u5251\u4e00\u9635\u72c2 \u653b\uff0c\u9633\u5b97\u6d77\u672c\u6765\u4ee5\u53cc\u638c\u4e4b\u529b\uff0c\u8db3\u53ef\u4e0e\u90a3\u5c11\u5e74\u5468\u65cb\uff0c\u4f46\u4ed6\u4e00\u52a8\u4e86\u6c14\uff0c\u5fc3\u795e\u5927\u4e71\uff0c\u7ade\u88ab\u90a3\u5c11\u5e74\u5236 \u4e86\u5148\u673a\uff0c\u5251\u70b9\u6d12\u843d\u5982\u96e8\uff0c\u5251\u5251\u4e0d\u79bb\u8981\u5bb3\uff0c\u6740\u5f97\u4ed6\u7adf\u7136\u4e0d\u80fd\u8fd1\u8eab\u3002\u9633\u5b97\u6d77\u53c8\u60ca\u53c8\u6025\uff0c\u518d\u4e5f\u4e0d\u987e \u8eab\u4efd\uff0c\u4e00\u4e2a\u8f6c\u8eab\u4e5f\u62d4\u51fa\u4e86\u8170\u95f4\u7684\u957f\u5251\uff0c\u767d\u8863\u5c11\u5e74\u563b\u563b\u7b11\u9053\uff1a\u201c\u53eb\u4f60\u65e9\u65e9\u62d4\u5251\u4f60\u4e0d\u542c\u8bdd\uff0c\u73b0\u5728 \u5982\u4f55\uff1f\u201d\u9633\u5b97\u6d77\u51e0\u4e4e\u7ed9\u4ed6\u6c14\u70b8\u5fc3\u80ba\uff0c\u90a3\u767d\u8863\u5c11\u5e74\u4e00\u53d1\u58f0\u51b7\u7b11\uff0c\u624b\u5e95\u5374\u662f\u4e1d\u6beb\u4e0d\u7f13\uff0c\u8bdd\u58f0\u672a \u4e86\uff0c\u5530\u5730\u4e00\u5251\uff0c\u53c8\u6307\u5230\u9633\u5b97\u6d77\u7684\u54bd\u5589\u3002 \u3000\u3000\u9633\u5b97\u6d77\u4e5f\u4e0d\u7981\u6697\u6697\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u767d\u8863\u5c11\u5e74\u90a3\u4e00\u5251\u5243\u5f97\u5feb\uff0c\u9633\u5b97\u6d77\u4e5f\u95ea\u5f97\u5feb\uff0c\u53ea\u89c1 \u5251\u5149\u4e00\u95ea\uff0c\u9633\u5b97\u6d77\u5df2\u662f\u8eab\u79fb\u6b65\u6362\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u7ed5\u5230\u767d\u8863\u5c11\u5e74\u8eab\u540e\uff0c\u5530\u5730\u4e00\u5251\uff0c\u5c31\u671d\u767d\u8863\u5c11\u5e74 \u540e\u5fc3\u7684\u201c\u98ce\u5e9c\u7a74\u201d\u63b7\u6765\uff0c\u8fd9\u4e00\u62db\u7528\u5f97\u72e0\u6bd2\u4e4b\u6781\uff0c\u767d\u8863\u5c11\u5e74\u4e0d\u8bba\u5411\u5de6\u53eb\u53f3\u8eb2\u95ea\uff0c\u80cc\u5fc3\u7684\u7a74\u9053\u8981 \u5bb3\u90fd\u5168\u5728\u654c\u4eba\u7684\u5251\u5c16\u5a01\u80c1\u4e4b\u4e0b\uff0c\u8fdf\u65e9\u90fd\u5c06\u88ab\u4ed6\u523a\u7740\uff0c\u6446\u8131\u4e0d\u4e86\u3002\u6a0a\u82f1\u770b\u5f97\u624b\u5fc3\u6dcc\u6c57\uff0c\u53ea\u89c1\u90a3 \u5c11\u5e74\u5fae\u5fae\u4e00\u95ea\uff0c\u8eab\u6cd5\u602a\u5f02\u4e4b\u6781\uff0c\u770b\u770b\u9633\u5b97\u6d77\u7684\u5251\u5c16\u5df2\u582a\u582a\u89e6\u7740\u4ed6\u7684\u80cc\u5fc3\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u4e00\u4e0b\u5b50 \u5c31\u7ed9\u4ed6\u9003\u4e86\u51fa\u6765\uff0c\u8eab\u5f62\u4e00\u52a8\uff0c\u7acb\u523b\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u53cd\u8f6c\u5230\u9633\u5b97\u6d77\u8eab\u540e\uff0c\u5bd2\u5149\u4e00\u95ea\uff0c\u4e00\u62db\u201c\u731b\u9e21\u5544 \u7c9f\u201d\uff0c\u53cd\u523a\u9633\u5b97\u6d77\u80a9\u540e\u7684\u201c\u5929\u67f1\u7a74\u201d\u3002\u9633\u5b97\u6d77\u4e00\u62db\u63b7\u7a7a\uff0c\u65b9\u4f4d\u7acb\u53d8\uff0c\u53ea\u89c1\u4ed6\u8eab\u968f\u5251\u8d70\uff0c\u5251\u968f \u8eab\u8f6c\uff0c\u5ffd\u5730\u4e00\u62db\u201c\u82cf\u79e6\u80cc\u5251\u201d\uff0c\u957f\u5251\u6296\u52a8\uff0c\u55e1\u55e1\u4f5c\u54cd\uff0c\u767b\u65f6\u98de\u8d77\u4e09\u6735\u5251\u82b1\uff0c\u5c06\u767d\u8863\u5c11\u5e74\u7684\u4e0a \u4e2d\u4e0b\u4e09\u8def\uff0c\u5168\u90e8\u5c01\u7740\u3002\u767d\u8863\u5c11\u5e74\u53eb\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u53cd\u800c\u811a\u8e0f\u6d2a\u95e8\uff0c\u4e00\u62db\u201c\u674e\u5e7f \u5c04\u77f3\u201d\uff0c\u5f3a\u653b\u654c\u4eba\u4e2d\u8def\uff0c\u8fd9\u4e00\u4e0b\u53ef\u5927\u51fa\u9633\u5b97\u6d77\u7684\u610f\u6599\u4e4b\u5916\uff0c\u6309\u7167\u4e00\u822c\u5251\u6cd5\u7684\u5e38\u89c4\uff0c\u65ad\u65e0\u4e0d\u6551 \u81ea\u8eab\u4e4b\u7406\uff0c\u767d\u8863\u5c11\u5e74\u5374\u5c45\u7136\u5728\u5251\u52bf\u88ab\u5c01\uff0c\u6027\u547d\u5371\u9669\u4e4b\u9645\uff0c\u4e0d\u987e\u4e00\u5207\u5730\u5f3a\u653b\uff0c\u9633\u5b97\u6d77\u4e0d\u7531\u5f97\u51db \u7136\u4e00\u60ca\uff0c\u9192\u8d77\u8fd9\u5c11\u5e74\u7684\u5251\u4e43\u4e00\u53e3\u5b9d\u5251\uff0c\u82e5\u4f9d\u5251\u6cd5\u7684\u514b\u5236\u4e4b\u7406\uff0c\u9633\u5b97\u6d77\u7684\u5251\u53ea\u8981\u6321\u4e2d\u4e00\u622a\uff0c\u767d \u8863\u5c11\u5e74\u7684\u5251\u5c31\u8981\u7ed9\u4ed6\u5288\u843d\uff0c\u4f46\u5b97\u6d77\u7684\u5251\u4e0d\u662f\u5b9d\u5251\uff0c\u4e24\u5251\u76f8\u4ea4\uff0c\u4e5f\u5fc5\u7136\u6298\u65ad\u65e0\u7591\u3002\u9633\u5b97\u6d77\u662f\u6b66 \u6797\u4e2d\u6709\u540d\u7684\u9ad8\u624b\uff0c\u7eb5\u4f7f\u80fd\u628a\u767d\u8863\u5c11\u5e74\u91cd\u521b\uff0c\u82e5\u7136\u81ea\u5df1\u7684\u5251\u6298\u4e86\uff0c\u4f20\u51fa\u53bb\u53ef\u662f\u5929\u5927\u7684\u7b11\u8bdd\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u4e24\u4eba\u8eab\u5f62\u500f\u5730\u5206\u5f00\uff0c\u539f\u6765\u9633\u5b97\u6d77\u907f\u65e0\u53ef\u907f\uff0c\u5728\u4e24\u5251\u76f8\u89e6\u4e4b\u9645\uff0c\u5f3a \u628a\u9633\u521a\u4e4b\u529b\u64a4\u4e86\u56de\u6765\uff0c\u5251\u950b\u4e00\u8f6c\uff0c\u6539\u7528\u9634\u67d4\u4e4b\u529b\uff0c\u957f\u5251\u5728\u767d\u8863\u5c11\u5e74\u7684\u5251\u4e0a\u8f7b\u8f7b\u4e00\u64e6\u800c\u8fc7\uff0c\u9976 \u662f\u5982\u6b64\uff0c\u4e5f\u6e85\u8d77\u4e86\u4e00\u6e9c\u706b\u82b1\uff0c\u5251\u4e0a\u7ed9\u5212\u4e86\u4e00\u4e2a\u7f3a\u53e3\u3002\u90a3\u767d\u8863\u5c11\u5e74\u5360\u4e86\u4fbf\u5b9c\uff0c\u4e0d\u77e5\u8fdb\u9000\uff0c\u5530\u5730 \u53c8\u662f\u4e00\u5251\uff01 \u3000\u3000\u8fd9\u4e00\u56de\u4e24\u5251\u76f8\u4ea4\uff0c\u5374\u4e0d\u95fb\u534a\u70b9\u58f0\u606f\uff0c\u6a0a\u82f1\u5927\u4e3a\u5947\u602a\uff0c\u7741\u773c\u770b\u65f6\uff0c\u4f46\u89c1\u5c11\u5e74\u7684\u5251\u7ade\u4f3c\u7ed9\u9633 \u5b97\u6d77\u7684\u5251\u5438\u7740\uff0c\u8fde\u7528\u51e0\u79cd\u8eab\u6cd5\uff0c\u90fd\u6446\u8131\u4e0d\u4e86\u3002\u539f\u6765\u9633\u5b97\u6d77\u8fd9\u6b21\u5168\u7528\u9634\u67d4\u4e4b\u529b\uff0c\u4f7f\u4e00\u4e2a\u201c\u7c98\u201d \u5b57\u8bc0\uff0c\u5c06\u767d\u8863\u5c11\u5e74\u7684\u5251\u8d8a\u626f\u8d8a\u8fd1\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u989d\u4e0a\u6c81\u51fa\u6c57\u73e0\uff0c\u9633\u5b97\u6d77\u7b11\u9053\uff1a\u201c\u5982\u4f55\uff1f\u201d\u767d\u8863\u5c11\u5e74\u5ffd\u5730\u4e00\u58f0\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u4e5f\u6ca1 \u600e\u6837\uff01\u201d\u4e5f\u4e0d\u77e5\u4ed6\u7528\u7684\u662f\u4ec0\u4e48\u624b\u6cd5\uff0c\u500f\u5730\u53c8\u8131\u51fa\u8eab\u6765\u3002\u539f\u6765\u9633\u5b97\u6d77\u4e00\u65f6\u8f7b\u654c\uff0c\u8bf4\u8bdd\u5206\u4e86\u5fc3 \u795e\uff0c\u90a3\u5c11\u5e74\u5251\u6cd5\u7cbe\u5999\u4e4b\u6781\uff0c\u77ed\u5251\u5411\u524d\u4e00\u63a2\uff0c\u7acb\u523b\u89e3\u4e86\u4ed6\u7c98\u8fde\u7684\u9634\u67d4\u4e4b\u52b2\uff0c\u7ed5\u5230\u4ed6\u7684\u4fa7\u8fb9\uff0c\u5530 \u5730\u53c8\u662f\u4e00\u5251\u3002 \u3000\u3000\u9633\u5b97\u6d77\u4e00\u4e2a\u201c\u9000\u6b65\u8fde\u73af\u201d\uff0c\u5148\u907f\u4e00\u907f\u90a3\u5c11\u5e74\u7684\u653b\u52bf\uff0c\u957f\u5251\u4e00\u6307\uff0c\u53c8\u60f3\u4f9d\u6837\u753b\u846b\u82a6\uff0c\u518d\u5438 \u7740\u4ed6\u7684\u77ed\u5251\uff0c\u54ea\u77e5\u767d\u8863\u5c11\u5e74\u7adf\u662f\u6e9c\u6ed1\u4e4b\u6781\uff0c\u518d\u4e0d\u4e0a\u5f53\uff0c\u5374\u7ed5\u7740\u9633\u5b97\u6d77\u75be\u8dd1\u8d77\u6765\uff0c\u5de6\u4e00\u5251\uff0c\u53f3 \u4e00\u5251\uff0c\u524d\u4e00\u5251\uff0c\u540e\u4e00\u5251\uff0c\u5b9b\u5982\u7a7f\u82b1\u8774\u8776\uff0c\u770b\u5f97\u6a0a\u82f1\u773c\u82b1\u64a9\u4e71\u3002 \u3000\u3000\u9633\u5b97\u6d77\u6697\u8fd0\u5185\u529b\uff0c\u4e00\u5fc3\u60f3\u627e\u90a3\u5c11\u5e74\u7684\u5251\uff0c\u4f46\u90a3\u5c11\u5e74\u7684\u8eab\u6cd5\u8f7b\u7075\u4e4b\u6781\uff0c\u968f\u610f\u6325\u6d12\uff0c\u6709\u5982\u6d41 \u6c34\u884c\u4e91\uff0c\u597d\u51e0\u6b21\u4e24\u5251\u9669\u9669\u76f8\u4ea4\uff0c\u5374\u603b\u662f\u4e00\u60ca\u5373\u8fc7\uff0c\u78b0\u4ed6\u4e0d\u7740\u3002\u9633\u5b97\u6d77\u4e0d\u7531\u5f97\u6697\u6697\u60ca\u5947\uff0c\u731b\u5730 \u5fc3\u5934\u4e00\u9707\uff0c\u770b\u8fd9\u5251\u6cd5\uff0c\u7adf\u4f3c\u6c5f\u6e56\u4e0a\u4f20\u95fb\u7684\u4e00\u4e2a\u9690\u5c45\u5927\u4fa0\u7684\u5ae1\u4f20\u5b97\u6d3e\uff01 \u3000\u3000\u9633\u5b97\u6d77\u8d77\u5148\u8ddf\u4ed6\u75be\u8f6c\uff0c\u78b0\u4e0d\u7740\u4ed6\u7684\u5251\uff0c\u53cd\u800c\u8fed\u9047\u9669\u62db\uff0c\u8fd9\u65f6\u5fc3\u4e2d\u4e00\u609f\uff0c\u811a\u6b65\u500f\u505c\uff0c\u62b1\u5b88 \u6536\u4e00\uff0c\u957f\u5251\u5c01\u7740\u95e8\u6237\uff0c\u53ea\u5b88\u4e0d\u653b\uff0c\u5176\u5b9e\u6bcf\u4e00\u62db\u90fd\u662f\u5bfb\u627e\u7a7a\u9699\uff0c\u6697\u85cf\u7740\u6781\u5389\u5bb3\u7684\u53cd\u51fb\u62db\u6570\uff0c\u767d \u8863\u5c11\u5e74\u6e10\u89c9\u53d1\u51fa\u53bb\u7684\u62db\u6570\u6bcf\u53d7\u7275\u5236\uff0c\u4f46\u5374\u53c8\u4e0d\u80fd\u6539\u53d8\u6218\u672f\uff0c\u53ea\u5f97\u4e00\u80a1\u52b2\u5730\u4ecd\u7528\u201c\u7a7f\u82b1\u7ed5\u6811\u201d \u7684\u8eab\u6cd5\u548c\u4ed6\u6e38\u6597\uff0c\u65f6\u5019\u4e00\u4e45\uff0c\u4f46\u89c9\u5fc3\u8df3\u6c14\u5598\uff0c\u8d8a\u6765\u8d8a\u662f\u96be\u4ee5\u652f\u6301\u3002 \u3000\u3000\u6a0a\u82f1\u770b\u5f97\u5fc3\u60ca\u76ee\u7729\uff0c\u8fd9\u4e24\u4eba\u5404\u4ee5\u4e0a\u4e58\u5251\u6cd5\u76f8\u6251\uff0c\u7a0d\u4e00\u4e0d\u614e\uff0c\u4fbf\u6709\u6027\u547d\u4e4b\u5371\u3002\u6a0a\u82f1\u5bf9\u5251\u6cd5 \u867d\u7136\u6ca1\u6709\u7cbe\u7814\uff0c\u5374\u4e5f\u770b\u51fa\u90a3\u5c11\u5e74\u6e10\u8d8b\u52a3\u52bf\uff0c\u8fd9\u65f6\u6a0a\u82f1\u8fd0\u6c14\u6d3b\u8840\uff0c\u7a74\u9053\u7684\u9178\u9ebb\u5df2\u7ecf\u6b62\u4e86\uff0c\u731b\u5730 \u4e00\u58f0\u5927\u559d\uff0c\u63d0\u8d77\u7f05\u5200\uff0c\u4fbf\u60f3\u4e0a\u524d\u52a9\u6218\u3002 \u3000\u3000\u9633\u5b97\u6d77\u60ef\u7ecf\u9635\u4ed7\uff0c\u81ea\u662f\u773c\u89c2\u516d\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u6a0a\u82f1\u8eab\u5f62\u4e00\u52a8\uff0c\u4ed6\u5df2\u500f\u5730\u5251\u4ea4\u5de6\u624b\uff0c\u957f\u5251 \u4e00\u9707\uff0c\u5c06\u767d\u8863\u5c11\u5e74\u903c\u9000\u4e24\u6b65\uff0c\u53f3\u624b\u4e00\u626c\uff0c\u53d1\u51fa\u4e86\u4e00\u628a\u94c1\u83b2\u5b50\uff0c\u5206\u6253\u6a0a\u82f1\u548c\u90a3\u767d\u8863\u5c11\u5e74\u3002\u8fd9\u65f6 \u4ed6\u518d\u4e5f\u987e\u4e0d\u5f97\u4e00\u6d3e\u5b97\u5e08\u7684\u8eab\u5206\uff0c\u4e3a\u4e86\u6015\u88ab\u4e24\u4eba\u5408\u624b\u56f4\u653b\u3002\u5f97\u8fde\u6697\u5668\u4e5f\u7528\u51fa\u6765\u4e86\u3002 \u3000\u3000\u6a0a\u82f1\u811a\u4e0a\u53d7\u4f24\uff0c\u7eb5\u8dc3\u4e0d\u7075\uff0c\u6a2a\u5200\u78d5\u98de\u4e86\u5954\u5411\u4e0a\u76d8\u7684\u51e0\u9897\u94c1\u83b2\u5b50\uff0c\u80da\u9aa8\u5374\u53c8\u4e2d\u4e86\u4e24\u9897\uff0c\u5173 \u8282\u4e00\u9ebb\uff0c\u53c8\u6454\u5230\u5730\u4e0a\uff0c\u5ffd\u542c\u5f97\u90a3\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u6a0a\u82f1\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\u8df3\u8d77\u6765\u65f6\uff0c \u8033\u8fb9\u4f46\u542c\u5f97\u6ee1\u7a7a\u547c\u5578\u4e4b\u58f0\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u5341\u51e0\u6735\u91d1\u5149\u95ea\u95ea\uff0c\u5f62\u4f3c\u6885\u82b1\u7684\u6697\u5668\uff0c\u5b9b\u5982\u6d12\u4e0b\u4e86 \u6ee1\u5929\u82b1\u96e8\uff0c\u5c06\u9633\u5b97\u6d77\u5168\u8eab\u7f69\u7740\u3002 \u3000\u3000\u6a0a\u82f1\u5927\u559c\u53eb\u9053\uff1a\u201c\u597d\u554a\uff01\u201d\u53ea\u89c1\u9633\u5b97\u6d77\u9661\u5730\u4e00\u4e2a\u201c\u767d\u9e64\u51b2\u5929\u201d\uff0c\u8eab\u5f62\u51ed\u7a7a\u62d4\u8d77\uff0c\u957f\u5251\u4e00 \u6325\uff0c\u5728\u534a\u7a7a\u5212\u4e86\u4e00\u4e2a\u5f27\u5f62\uff0c\u987f\u65f6\u4e00\u7247\u68b5\u97f3\u7a7a\u54cd\uff0c\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u91d1\u82b1\u56db\u9762\u98de\u5c04\uff0c\u767d \u8863\u5c11\u5e74\u559d\u9053\uff1a\u201c\u7740\uff01\u201d\u6251\u524d\u53c8\u662f\u4e00\u5251\uff01 \u3000\u3000\u53ea\u542c\u5f97\u201c\u55e4\u55e4\u201d\u4e24\u58f0\uff0c\u9633\u5b97\u6d77\u7684\u4e24\u80a9\u5df2\u5404\u5370\u4e0a\u4e00\u6735\u91d1\u82b1\uff0c\u53eb\u9053\uff1a\u201c\u597d\u5bb6\u4f19\uff01\u201d\u80a9\u5934\u4e00 \u6446\uff0c\u90a3\u4e24\u6735\u91d1\u82b1\u88ab\u4ed6\u6697\u8fd0\u5185\u52b2\uff0c\u9707\u843d\u4e8e\u5730\uff0c\u957f\u5251\u4e00\u6446\uff0c\u626c\u7a7a\u4e00\u95ea\uff0c\u7adf\u662f\u82e5\u65e0\u5176\u4e8b\u5730\u8fce\u6218\u90a3\u4e2a \u767d\u8863\u5c11\u5e74\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u5403\u4e86\u4e00\u60ca\uff0c\u4ed6\u90a3\u4e00\u624b\u5341\u4e8c\u6735\u91d1\u82b1\u88ab\u9633\u5b97\u6d77\u51fb\u843d\u4e86\u5341\u6735\uff0c\u5df2\u662f\u96be\u6781\uff0c\u60f3\u4e0d\u5230\u6253\u4e2d \u4e86\u4ed6\u80a9\u5934\u7684\u4e24\u6735\uff0c\u7adf\u4e5f\u65e0\u6d4e\u4e8e\u4e8b\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u9633\u5b97\u6d77\u679c\u662f\u540d\u4e0d\u865a\u4f20\uff0c\u602a\u4e0d\u5f97\u5728\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u7adf \u80fd\u4e0e\u6211\u7684\u5e08\u7236\u9f50\u540d\u3002\u201d \u3000\u3000\u6a0a\u82f1\u89c1\u52bf\u5371\u9669\uff0c\u987e\u4e0d\u5f97\u817f\u4e0a\u7684\u75bc\u75db\uff0c\u6325\u5200\u53c8\u4e0a\uff0c\u767d\u8863\u5c11\u5e74\u5ffd\u5730\u4e00\u58f0\u957f\u5578\uff0c\u90a3\u767d\u8863\u5c11\u5e74\u4ece \u6797\u4e2d\u5954\u51fa\uff0c\u5feb\u5982\u95ea\u7535\u3002\u6a0a\u82f1\u8fd8\u672a\u51b2\u5230\u6b64\u4eba\u7684\u8ddf\u524d\uff0c\u90a3\u767d\u8863\u5c11\u5e74\u7a81\u7136\u865a\u6643\u4e00\u5251\uff0c\u8eab\u5f62\u98de\u8d77\uff0c\u4e00 \u628a\u6293\u7740\u6a0a\u82f1\u7684\u8863\u9886\uff0c\u6070\u597d\u843d\u5230\u767d\u9a6c\u80cc\u4e0a\uff0c\u767d\u9a6c\u4e00\u58f0\u957f\u5636\uff0c\u56db\u8e44\u75be\u8dd1\u3002 \u3000\u3000\u9633\u5b97\u6d77\u4e00\u58f0\u547c\u5578\uff0c\u5c06\u5750\u9a91\u5524\u4e86\u51fa\u6765\uff0c\u7acb\u523b\u4e0a\u9a6c\u4fbf\u8ffd\uff0c\u9633\u5b97\u6d77\u7684\u5750\u9a91\u4e43\u5fa1\u82d1\u540d\u9a6c\uff0c\u81ea\u662f\u975e \u540c\u5c0f\u53ef\uff0c\u4f46\u6bd4\u8d77\u5c11\u5e74\u7684\u90a3\u5339\u767d\u9a6c\uff0c\u5374\u53c8\u662f\u671b\u5c18\u83ab\u53ca\uff0c\u8fd9\u65f6\u5df2\u662f\u62c2\u6653\u65f6\u5206\uff0c\u8ffd\u4e86\u4e00\u56de\uff0c\u8d77\u521d\u8fd8 \u80fd\u89c1\u5230\u80cc\u5f71\uff0c\u518d\u8fc7\u7247\u5230\uff0c\u4fbf\u53ea\u662f\u4e00\u70b9\u767d\u70b9\uff0c\u6e10\u6e10\u6ca1\u4e86\u3002\u9633\u5b97\u6d77\u53f9\u4e86\u53e3\u6c14\uff0c\u5ffd\u89c9\u80a9\u4e0a\u5fae\u5fae\u75bc \u75db\uff0c\u4ed6\u8df3\u4e0b\u4e86\u9a6c\uff0c\u8d70\u5230\u6eaa\u8fb9\uff0c\u89e3\u8863\u4e00\u770b\uff0c\u53ea\u89c1\u53cc\u80a9\u4e0a\u6709\u4e24\u6735\u6de1\u6de1\u7684\u82b1\u75d5\u3002\u9633\u5b97\u6d77\u5403\u4e86\u4e00\u60ca\uff0c \u5e78\u559c\u8fd9\u79cd\u6697\u5668\u6ca1\u6bd2\uff0c\u5426\u5219\u4e24\u6761\u80a9\u818a\u4fbf\u8981\u5e9f\u4e86\u3002\u5fc3\u60f3\uff1a\u518d\u8fc7\u4e24\u5e74\uff0c\u8fd9\u5c11\u5e74\u7684\u529f\u529b\u957f\u8fdb\uff0c\u90a3\u8fd8\u5f97 \u4e86\u3002 \u3000\u3000\u518d\u8bf4\u6a0a\u82f1\u88ab\u63b7\u4e8e\u9a6c\u80cc\uff0c\u90a3\u5339\u9a6c\u957f\u5636\u75be\u8dd1\uff0c\u770b\u5982\u817e\u4e91\u9a7e\u96fe\uff0c\u6a0a\u82f1\u6697\u6697\u5fc3\u60ca\uff0c\u89c9\u7740\u90a3\u5c11\u5e74\u5c31 \u5728\u8eab\u540e\uff0c\u6a0a\u82f1\u4fbf\u60f3\u56de\u5934\u81f4\u8c22\uff0c\u5fc3\u4e2d\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u5c0a\u9a7e\u76f8\u6551\uff0c\u6562\u95ee\u5c0a\u59d3\u5927\u540d\u3002\u201d\u90a3\u5339\u9a6c\u7a81\u8d77\u8df3 \u8fc7\u4e00\u9053\u5c71\u6da7\uff0c\u6a0a\u82f1\u5de6\u8db3\u53d7\u4f24\uff0c\u631f\u5b83\u4e0d\u7a33\uff0c\u9669\u4e9b\u7ed9\u9a6c\u629b\u4e86\u8d77\u6765\uff0c\u6025\u5fd9\u7528\u529b\u631f\u4f4f\uff0c\u4e0d\u6562\u56de\u5934\uff0c\u53ea \u542c\u5f97\u90a3\u5c11\u5e74\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u8bf4\u8bdd\uff0c\u5c0f\u5fc3\u9a91\u9a6c\u3002\u201d\u53f1\u54a4\u4e00\u58f0\uff0c\u5411\u7a7a\u4e2d\u6325\u4e86\u4e00\u97ad\uff0c\u90a3\u5339\u9a6c\u8d8a\u53d1 \u8dd1\u5f97\u5feb\u4e86\u3002 \u3000\u3000\u4f46\u89c1\u6653\u8272\u4e91\u5f00\uff0c\u671d\u9633\u6e10\u6e10\u5347\u8d77\uff0c\u5c11\u5e74\u500f\u5730\u52d2\u4f4f\u9a6c\u50f5\uff0c\u9053\uff1a\u201c\u53ef\u4ee5\u4e86\uff01\u201d\u8df3\u4e0b\u9a6c\u6765\uff0c\u9762\u4e0d \u7ea2\uff0c\u6c14\u4e0d\u5598\uff0c\u4e00\u53cc\u5999\u76ee\uff0c\u6ce8\u89c6\u7740\u6a0a\u82f1\uff0c\u6a0a\u82f1\u8d70\u4e86\u8d70\u795e\uff0c\u9053\uff1a\u201c\u8fd9\u771f\u662f\u5929\u4e0b\u7f55\u89c1\u7684\u5b9d\u9a6c\u3002\u5c0a\u59d3 \u5927\u540d\uff0c\u53ef\u4ee5\u89c1\u544a\u4e86\u5427\uff1f\u201d\u90a3\u5c11\u5e74\u773c\u73e0\u4e00\u8f6c\uff0c\u5ffd\u5730\u8eab\u5f62\u4e00\u957f\uff0c\u4e00\u4f38\u624b\uff0c\u500f\u5730\u5c31\u628a\u6a0a\u82f1\u8170\u95f4\u7684\u90a3 \u53e3\u5b9d\u5200\u62ff\u53bb\uff0c\u4e60\u6b66\u4e4b\u4eba\uff0c\u4fdd\u62a4\u5175\u5203\u5df2\u6210\u4e60\u60ef\uff0c\u6a0a\u82f1\u672c\u80fd\u5730\u4f38\u624b\u4e00\u683c\uff0c\u60f3\u6a0a\u82f1\u6b66\u529f\u4ea6\u975e\u6cdb\u6cdb\uff0c \u8fd9\u4e00\u683c\u4e00\u62ff\uff0c\u4e43\u662f\u64d2\u62ff\u624b\u7684\u6076\u62db\uff0c\u5374\u8fde\u5c11\u5e74\u7684\u624b\u6307\u90fd\u6ca1\u6709\u78b0\u5230\uff0c\u5f85\u5f97\u6a0a\u82f1\u53d1\u89c9\uff0c\u53ea\u89c1\u90a3\u5c11\u5e74 \u5df2\u68d2\u7740\u5ba4\u5200\uff0c\u9762\u4e0a\u9732\u51fa\u7591\u60d1\u7684\u795e\u8272\u3002 \u3000\u3000\u6a0a\u82f1\u4ea6\u662f\u60ca\u7591\u4e0d\u5b9a\uff0c\u53ea\u542c\u5f97\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u8fd9\u5b9d\u5200\u4ece\u4f55\u5904\u5f97\u6765\uff1f\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u8fd9\u662f\u5f20\u98ce \u5e9c\u7684\u5b9d\u5200\u3002\u201d\u5c11\u5e74\u9053\uff1a\u201c\u5f20\u98ce\u5e9c\u4e3a\u4f55\u5c06\u4ed6\u7684\u5b9d\u5200\u7ed9\u4f60\uff1f\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u6069\u4eba\u5bb9\u7980\u2026\u2026\u201d\u5c06\u5f20\u98ce \u5e9c\u90a3\u665a\u58ee\u70c8\u4e4b\u6b7b\uff0c\u7b80\u5355\u8bf4\u4e86\uff0c\u8bf4\u7740\uff0c\u8bf4\u7740\uff0c\u6d41\u4e0b\u6cea\u6765\u3002\u9053\uff1a\u201c\u53ea\u6068\u6211\u6a0a\u82f1\u65e0\u80fd\uff0c\u773c\u770b\u5f20\u4f2f\u4f2f \u5f52\u5929\uff1b\u5230\u4e86\u4eac\u57ce\u53c8\u773c\u89c1\u4e8e\u9601\u8001\u6210\u4ec1\uff0c\u8fde\u4ed6\u7684\u516d\u9633\u9b41\u9996\u4e5f\u7ed9\u522b\u4eba\u53d6\u53bb\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u62d4\u5200\u9798\u5411\u7a7a\u4e2d\u865a\u5288\u4e24\u5200\uff0c\u5ffd\u5730\u4ef0\u5929\u72c2\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u5f20\u51e4\u5e9c\u4e5f\u7b97\u6b7b\u5f97\u5176\u65f6\uff0c\u4e0d \u8d1f\uff0c\u4e0d\u8d1f\u4e8e\u9601\u8001\u5bf9\u4ed6\u4e00\u756a\u8d4f\u8bc6\u3002\u201d\u8fd9\u7b11\u58f0\u82cd\u51c9\u4e4b\u6781\uff0c\u6a0a\u82f1\u7981\u4e0d\u4f4f\u5fc3\u5934\u4e00\u9707\uff0c\u773c\u6cea\u5374\u81ea\u7136\u6b62 \u4e86\u3002\u7ec6\u60f3\u90a3\u5c11\u5e74\u8bdd\u8bed\uff0c\u4f3c\u4e4e\u548c\u4e8e\u8c26\u3001\u5f20\u51e4\u5e9c\u90fd\u6709\u6781\u6df1\u7684\u6e0a\u6e90\u3002 \u3000\u3000\u4f46\u89c1\u90a3\u5c11\u5e74\u5c06\u5200\u63d2\u56de\u9798\u4e2d\uff0c\u5374\u60ac\u6302\u5728\u81ea\u5df1\u7684\u8170\u95f4\u3002\u6a0a\u82f1\u9053\uff1a\u201c\u8bf7\u76f8\u516c\u5c06\u8fd9\u53e3\u5b9d\u5200\u8fd8 \u6211\u3002\u201d\u90a3\u5c11\u5e74\u77aa\u773c\u9053\uff1a\u201c\u4e3a\u4f55\u8981\u8fd8\u7ed9\u4f60\uff1f\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u6069\u4eba\u7231\u8fd9\u53e3\u5200\uff0c\u81ea\u4e3b\u9053\uff1a\u5b9d\u5200\u8d60\u58ee \u58eb\uff0c\u7ea2\u7c89\u8d60\u4f73\u4eba\u3002\u6069\u4eba\u4e5f\u914d\u7528\u8fd9\u628a\u5b9d\u5200\u3002\u65e0\u5948\u8fd9\u53e3\u5200\uff0c\u5f20\u4f2f\u4f2f\u5df1\u6258\u6211\u9001\u4e0e\u4ed6\u4eba\uff0c\u800c\u4e14\u8fd9\u5176\u4e2d \u6709\u6781\u5927\u7684\u5173\u7cfb\uff0c\u201d\u767d\u8863\u5c11\u5e74\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u6a0a\u82f1\u8bf4\u9053\uff1a\u201c\u8fd9\u5b9d\u5200\u6211\u662f\u8981\u9001\u7ed9\u5f20\u5927\u4fa0\u5f20\u4e39\u67ab\u7684\uff01\u201d\u5f20\u4e39\u67ab\u7684\u540d\u5934\u5f53\u65f6\u6700\u54cd\uff0c\u5929\u4e0b\u4e60\u6b66 \u4e4b\u4eba\uff0c\u65e0\u4e0d\u77e5\u9053\uff0c\u82e5\u662f\u522b\u4eba\u542c\u4e86\uff0c\u5c31\u7b97\u662f\u6709\u540d\u671b\u7684\uff0c\u4e5f\u6050\u6015\u8981\u5fc5\u606d\u5fc5\u656c\uff0c\u5c06\u5b9d\u5200\u5949\u9001\u8fc7\u6765\u3002 \u90a3\u5c11\u5e74\u773c\u7709\u4e00\u626c\uff0c\u5374\u4ecd\u662f\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u9001\u7ed9\u5f20\u5927\u4fa0\u505a\u4ec0\u4e48\uff1f\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u8fd8\u6709\u4e2d\u6bd2\u8840\u8863\uff0c\u5f20 \u98ce\u5e9c\u548c\u5f20\u4e39\u67ab\u4e43\u662f\u81f3\u4ea4\uff0c\u5f20\u98ce\u5e9c\u6b7b\u65f6\u4ee5\u4e0d\u80fd\u89c1\u7740\u5f20\u4e39\u67ab\u4e3a\u61be\uff0c\u6240\u4ee5\u8fd9\u5e45\u8840\u8863\u662f\u7559\u7ed9\u5f20\u4e39\u67ab\uff0c \u8ba9\u4ed6\u5982\u89c1\u4ea1\u53ca\uff1b\u8fd9\u53e3\u5b9d\u5200\u5374\u662f\u4ed6\u7559\u4e0e\u5f20\u4e39\u67ab\uff0c\u8bf7\u5f20\u4e39\u67ab\u4ee3\u4ed6\u5bfb\u89c5\u513f\u5b50\uff0c\u82e5\u5e78\u800c\u5bfb\u83b7\uff0c\u5219\u8bf7\u5f20 \u4e39\u67ab\u6536\u4ed6\u4e3a\u5f92\uff0c\u8fd9\u53e3\u5b9d\u5200\u5c31\u4ea4\u4e0e\u4ed6\u7684\u513f\u5b50\u3002\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u5f20\u98ce\u5e9c\u7684\u513f\u5b50\u662f\u4e0d\u662f\u90a3\u65e5\u5728\u6c34\u5858 \u8fb9\u620f\u6c34\u7684\u987d\u7ae5\uff1f\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u53eb\u5f20\u864e\u5b50\u3002\u201d\u5c11\u5e74\u9053\uff1a\u201c\u90a3\u5e45\u8840\u8863\u5462\uff1f\u201d\u6a0a\u82f1\u9053\uff1a \u201c\u55ef\uff0c\u5728\u8fd9\u513f\u3002\u201d\u53d6\u4e86\u51fa\u6765\uff0c\u644a\u5728\u624b\u5fc3\uff0c\u5728\u6a0a\u82f1\u4e4b\u610f\uff0c\u4ee5\u4e3a\u5c11\u5e74\u5c1a\u672a\u76f8\u4fe1\uff0c\u6240\u4ee5\u62ff\u7ed9\u4ed6\u770b\uff0c \u4e0d\u8651\u6709\u4ed6\u3002\u90a3\u5c11\u5e74\u9053\u4e86\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u5ffd\u5730\u624b\u81c2\u4e00\u62ac\uff0c\u95ea\u7535\u822c\u5730\u628a\u90a3\u5e45\u8840\u8863\u53c8\u62a2\u4e86\u53bb\u3002 \u3000\u3000\u6a0a\u82f1\u60ca\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u4f60\u662f\u6211\u7684\u6069\u4eba\uff0c\u4f46\u8981\u8fd9\u5b9d\u5200\u8840\u8863\u5374\u662f\u4e07\u4e07\u4e0d \u80fd\uff01\u201d\u90a3\u767d\u8863\u5c11\u5e74\u5c06\u8840\u8863\u6298\u597d\uff0c\u653e\u5165\u6000\u4e2d\uff0c\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u4e0d\u89c1\u5916\u4eba\uff0c\u8fd9\u8840\u8863\u5b9d\u5200\uff0c\u6211\u4e0e\u4f60\u4ea4 \u7ed9\u4ed6\u3002\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u8fd9\uff0c\u8fd9\u2014\u2014\u201d\u767d\u8863\u5c11\u5e74\u7a81\u7136\u53cd\u624b\u4e00\u63a8\uff0c\u5de6\u811a\u4e00\u52fe\uff0c\u6a0a\u82f1\u4e00\u4e2a\u8e09\u8dc4\uff0c\u5411\u540e \u8dcc\u5012\uff1b\u5c11\u5e74\u8f6c\u8eab\u4e00\u8dc3\uff0c\u5728\u6a0a\u82f1\u8eab\u5c06\u89e6\u5730\u4e4b\u9645\uff0c\u53c8\u8f7b\u4e00\u63a8\uff0c\u5c06\u6a0a\u82f1\u63a8\u5f97\u8f6c\u4e86\u4e2a\u5708\u5708\uff0c\u8eab\u5b50\u5374\u56e0 \u6b64\u633a\u76f4\u8d77\u6765\uff0c\u4ecd\u7136\u7ad9\u5230\u539f\u6765\u7684\u65b9\u4f4d\uff0c\u8fd9\u4e24\u4e2a\u624b\u6cd5\uff0c\u7cbe\u5999\u7edd\u4f26\uff0c\u6a0a\u82f1\u53c8\u60ca\u53c8\u6012\uff0c\u53ea\u542c\u5f97\u5c11\u5e74\u51b7 \u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u7384\u673a\u638c\u6cd5\uff0c\u4f60\u672a\u89c1\u8fc7\u4e5f\u8be5\u542c\u8fc7\u5427\uff1f\u201d\u6a0a\u82f1\u731b\u7136\u4e00\u60ca\uff0c\u8bb0\u8d77\u5f20\u98ce\u5e9c\u66fe\u548c\u4ed6\u8bf4\u8fc7\u5f20 \u4e39\u67ab\u7684\u7384\u673a\u638c\u6cd5\uff0c\u6709\u5185\u516b\u5708\u548c\u5916\u516b\u5708\u4e4b\u5206\uff0c\u80fd\u5728\u6700\u5c0f\u7684\u5708\u5b50\u91cc\u628a\u638c\u529b\u8fd0\u7528\u5f97\u6536\u653e\u81ea\u5982\uff0c\u8981\u653b \u654c\u4eba\u54ea\u4e00\u90e8\u5206\uff0c\u65e0\u4e0d\u5f97\u5fc3\u5e94\u624b\uff0c\u770b\u6765\u8fd9\u5c11\u5e74\u521a\u624d\u6240\u9732\u7684\u8fd9\u4e00\u624b\uff0c\u5fc5\u662f\u7384\u673a\u638c\u4e2d\u7684\u5185\u516b\u5708\u65e0 \u7591\u3002\u6a0a\u82f1\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u8bf7\u95ee\u4f60\u4e0e\u5f20\u5927\u4fa0\u5982\u4f55\u79f0\u547c\uff1f\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u5374\u4e0d\u7b54\u8fd9\u8bdd\u3002\u53cd\u95ee\u9053\uff1a\u201c\u51ed\u8fd9\u4e00\u624b\uff0c\u4f60\u603b\u4fe1\u5f97\u8fc7\u4e86\u5427\uff1f\u8fd9\u5b9d\u5200\u8840\u8863\u6211\u4ee3\u4f60\u9001 \u53bb\uff0c\u4f60\u4e0d\u5fc5\u591a\u8dd1\u4e00\u8d9f\u4e86\u3002\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u8fd9\uff0c\u8fd9\u2014\u2014\u201d\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u8fd9\u4ec0\u4e48\uff1f\u201d\u6a0a\u82f1\u9053\uff1a \u201c\u6211\u8981\u5c06\u8fd9\u8840\u8863\u5b9d\u5200\u4e3a\u51ed\uff0c\u8bf7\u5f20\u5927\u4fa0\u4ee3\u6211\u7d22\u56de\u5b98\u94f6\u3002\u201d\u767d\u8863\u5c11\u5e74\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u5b98 \u94f6\uff1f\u201d\u6a0a\u82f1\u53ea\u597d\u8010\u5fc3\u5c06\u5b98\u94f6\u88ab\u8499\u9762\u5927\u76d7\u6240\u52ab\u4e4b\u4e8b\u548c\u76d8\u6258\u51fa\uff0c\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u5c71\u4e1c\u9053\u4e0a\uff0c\u5c45\u7136 \u6709\u5982\u6b64\u8fd9\u822c\u7684\u8499\u9762\u5927\u76d7\u4e48\uff1f\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u8fd9\u8499\u9762\u5927\u76d7\u4e5f\u5c31\u662f\u90a3\u665a\u5077\u8d70\u4e8e\u5927\u4eba\u5934\u9885\u7684\u4eba\uff0c\u6211\u77a7 \u4e0d\u51fa\u4ed6\u7684\u8def\u6570\uff0c\u6b64\u4e8b\u975e\u8bf7\u5f20\u5927\u4fa0\u6765\u529e\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u767d\u8863\u5c11\u5e74\u9762\u8272\u7a81\u53d8\uff0c\u8df3\u8d77\u6765\u9053\uff1a\u201c\u5077\u8d70\u5934\u9885\u7684\u4eba\u5c31\u662f\u4ed6\uff0c\u597d\uff0c\u6b64\u4e8b\u4e5f\u5728\u6211\u7684 \u8eab\u4e0a\u3002\u4f60\u548c\u6211\u53bb\u627e\u4ed6\u3002\u4e0a\u9a6c\uff01\u201d\u6a0a\u82f1\u4e00\u9635\u8fdf\u7591\uff0c\u5df2\u88ab\u4ed6\u63a8\u5230\u9a6c\u80cc\u4e0a\uff0c\u4e2d\u5348\u65f6\u5206\uff0c\u5230\u4e86\u4e00\u4e2a\u5c0f \u9547\uff0c\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u6b64\u5730\u5df2\u662f\u5c71\u4e1c\u5883\u5185\u3002\u5230\u8499\u53e4\u7528\u4e0d\u4e86\u4e09\u5929\uff0c\u6211\u7ed9\u4f60\u4e70\u4e00\u5339\u9a6c\u3002\u201d\u6a0a\u82f1\u6b63\u60f3\u8bf4 \u8bdd\uff0c\u90a3\u5c11\u5e74\u5631\u4ed6\u5728\u5ba2\u5e97\u7b49\u5019\uff0c\u65cb\u98ce\u822c\u8dd1\u51fa\u95e8\u53bb\u3002\u5f85\u6a0a\u82f1\u5403\u8fc7\u4e86\u996d\uff0c\u5c11\u5e74\u5df2\u53e6\u4e58\u4e86\u4e00\u5339\u9a6c\u56de \u6765\u3002 \u3000\u3000\u770b\u90a3\u5339\u9a6c\u8e44\u6591\u767d\uff0c\u6bdb\u8272\u5149\u6cfd\uff0c\u867d\u7136\u8fdc\u4e0d\u53ca\u5c11\u5e74\u90a3\u5339\u767d\u9a6c\uff0c\u4e5f\u4e0d\u53ca\u9633\u5b97\u6d77\u90a3\u5339\u5fa1\u9a6c\uff0c\u4f46\u82e5 \u6bd4\u8d77\u6a0a\u82f1\u539f\u6765\u90a3\u5339\u9ec4\u8198\u9a6c\uff0c\u5374\u4e5f\u5e76\u4e0d\u900a\u8272\u3002\u6a0a\u82f1\u6b63\u5728\u51fa\u5947\uff1a\u8fd9\u5c11\u5e74\u7adf\u7136\u80fd\u5728\u8fd9\u6837\u77ed\u4fc3\u7684\u65f6 \u95f4\uff0c\u4e70\u6765\u4e86\u4e00\u5339\u597d\u9a6c\u3002\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u6a0a\u5927\u54e5\uff0c\u65e2\u7136\u5230\u4e86\u6b64\u95f4\uff0c\u6211\u4eec\u4e5f\u4e0d\u8fdf\u5728\u8fd9\u4e00\u4e24\u5929\uff0c\u6211\u4eec \u5408\u4e58\u4e00\u9a91\uff0c\u672c\u65e0\u4e0d\u53ef\uff0c\u5b98\u9053\u4e0a\u6765\u5f80\u4eba\u591a\uff0c\u7ed9\u4eba\u89c1\u4e86\uff0c\u5374\u6015\u8981\u8bf4\u6211\u4eec\u5c0f\u76f8\u3002\u201d\u6a0a\u82f1\u5fc3\u4e2d\u672c\u65e0\u82a5 \u8482\uff0c\u4e5f\u5e76\u975e\u8d70\u8981\u4e0e\u90a3\u5c11\u5e74\u5408\u4e58\u4e00\u9a6c\uff0c\u89c1\u8fd9\u5c11\u5e74\u5982\u6b64\u90d1\u91cd\u5730\u89e3\u91ca\uff0c\u53cd\u800c\u611f\u5230\u597d\u7b11\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u4e0e\u6a0a\u82f1\u540c\u884c\u6570\u65e5\uff0c\u4efb\u6a0a\u82f1\u5982\u4f55\u8f6c\u5f2f\u62b9\u89d2\u5730\u8bd5\u63a2\uff0c\u603b\u662f\u4e0d\u80af\u8bf4\u51fa\u81ea\u5df1\u7684\u59d3\u540d\u6765\u5386\u3002 \u6a0a\u82f1\u662f\u4e2a\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u719f\u77e5\u6c5f\u6e56\u4e0a\u7684\u7981\u5fcc\uff0c\u89c1\u8fd9\u5c11\u5e74\u4e0d\u8bf4\uff0c\u4fbf\u4e5f\u4e0d\u6562\u591a\u95ee\u3002 \u3000\u3000\u7b2c\u4e09\u65e5\u5230\u4e86\u8499\u9634\uff0c\u90a3\u662f\u6a0a\u82f1\u5f53\u65e5\u78b0\u7740\u8499\u9762\u5927\u76d7\uff0c\u88ab\u52ab\u53bb\u5b98\u94f6\u7684\u5730\u65b9\uff0c\u6a0a\u82f1\u518d\u4e09\u89e3\u91ca\uff0c\u90a3 \u8499\u9762\u5927\u76d7\u5728\u6b64\u505a\u4e86\u6848\u5b50\u4e4b\u540e\uff0c\u65ad\u65e0\u518d\u5b88\u5728\u6b64\u5730\u4e4b\u7406\uff0c\u90a3\u5c11\u5e74\u5374\u8fd8\u662f\u8981\u6765\u5bfb\u627e\uff0c\u679c\u7136\u63a2\u67e5\u4e86\u4e24 \u4e09\u5929\uff0c\u4e00\u70b9\u76d7\u8e2a\u4e5f\u63a2\u4e0d\u5230\u3002\u7b2c\u56db\u65e5\uff0c\u5c11\u5e74\u8fd8\u60f3\u5230\u9644\u8fd1\u660e\u67e5\u6697\u8bbf\uff0c\u6a0a\u82f1\u7b11\u9053\uff1a\u201c\u518d\u5f85\u5728\u8fd9\u513f\uff0c \u5c82\u4e0d\u662f\u5b88\u682a\u5f85\u5154\u5417\uff1f\u201d\u5c11\u5e74\u4e00\u7ffb\u773c\u76ae\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u5e26\u6211\u627e\u4ed6\u53bb\u3002\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u4f3c\u8fd9 \u7b49\u5de8\u76d7\uff0c\u884c\u8e2a\u65e0\u5b9a\uff0c\u6211\u600e\u80fd\u77e5\u9053\u4ed6\u7684\u53bb\u5904\uff1f\u201d\u5c11\u5e74\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u54b1\u4eec\u5c31\u518d\u5230\u4f60\u88ab\u52ab\u9556 \u7684\u5730\u65b9\u8d70\u4e00\u8d9f\u3002\u201d\u6a0a\u82f1\u53ea\u5f97\u4f9d\u4ed6\u3002\u88ab\u52ab\u9556\u7684\u5730\u65b9\u662f\u4e00\u4e2a\u6797\u5b50\u65c1\u8fb9\uff0c\u90a3\u6761\u9ec4\u6ce5\u8def\u4e0a\uff0c\u8fde\u5f53\u65e5\u7684 \u9a6c\u8e44\u75d5\u90fd\u5df2\u6ca1\u4e86\u3002\u5c11\u5e74\u62d4\u51fa\u5251\u6765\uff0c\u62e3\u90a3\u9760\u8fd1\u8def\u8fb9\u7684\u5927\u6811\uff0c\u523b\u4e86\u51e0\u884c\u5927\u5b57\uff0c\u6a0a\u82f1\u4e00\u770b\uff0c\u51e0\u4e4e\u7b11 \u51fa\u58f0\u6765\u3002\u90a3\u51e0\u884c\u5b57\u662f\u201c\u53f7\u79f0\u5927\u76d7\uff0c\u5b9e\u5219\u9f20\u5077\uff0c\u505a\u4e86\u6848\u5b50\uff0c\u4e0d\u6562\u51fa\u5934\u3002\u201d\u5982\u6b64\u505a\u6cd5\uff0c\u7b49\u4e8e\u5b69\u5b50 \u5435\u5634\uff0c\u6545\u610f\u6fc0\u6012\u5bf9\u65b9\u3002\u6a0a\u82f1\u60f3\u90a3\u5927\u76d7\uff0c\u65e2\u6562\u505a\u4e0b\u5de8\u6848\uff0c\u81ea\u5fc5\u8001\u8c0b\u6df1\u8651\uff0c\u5c82\u80fd\u50cf\u5b69\u5b50\u822c\u5730\u4e0d\u582a \u4e00\u6fc0\uff1f \u3000\u3000\u8fd9\u4e00\u65e5\u7684\u67e5\u63a2\uff0c\u81ea\u7136\u53c8\u662f\u843d\u7a7a\u3002\u7b2c\u4e94\u65e5\u4e00\u65e9\uff0c\u5c11\u5e74\u5ffd\u9053\uff1a\u201c\u6b64\u5730\u5728\u6cf0\u5c71\u4e4b\u5357\uff0c\u636e\u6211\u770b \u6765\uff0c\u90a3\u8499\u9762\u5927\u76d7\u7684\u5de2\u7a74\uff0c\u591a\u534a\u5728\u6cf0\u5c71\u4e4b\u4e0a\u3002\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u6cf0\u5c71\u77d7\u7acb\u4e2d\u5dde\uff0c\u9644\u8fd1\u90fd\u662f\u5e73\u539f\uff0c\u5c71 \u867d\u9ad8\u5374\u65e0\u9669\u53ef\u5b88\uff0c\u5386\u6765\u5927\u76d7\uff0c\u6781\u5c11\u5728\u6b64\u5b89\u8425\u7acb\u5be8\u7684\uff0c\u4f60\u82e5\u8981\u5230\u6cf0\u5c71\u53bb\u770b\u540d\u80dc\u98ce\u666f\uff0c\u90a3\u591a\u7684 \u662f\uff0c\u82e5\u8981\u53bb\u627e\u76d7\u8e2a\uff0c\u90a3\u6050\u6015\u53c8\u662f\u843d\u7a7a\u3002\u201d\u5c11\u5e74\u4e0d\u542c\uff0c\u6a0a\u82f1\u53c8\u53ea\u597d\u4f9d\u4ed6\u3002\u5fc3\u4e2d\u6697\u7b11\uff0c\u8fd9\u5c11\u5e74\u6b66 \u529f\u867d\u9ad8\uff0c\u5374\u662f\u4e00\u70b9\u4e0d\u61c2\u6c5f\u6e56\u4e8b\u4f53\u3002 \u3000\u3000\u6cf0\u5c71\u53f7\u79f0\u4e94\u5cb3\u4e4b\u4e00\uff0c\u5b54\u5b50\u5e76\u6709\u201c\u767b\u6cf0\u5c71\u800c\u5c0f\u5929\u4e0b\u201d\u4e4b\u8a00\uff0c\u5176\u5b9e\u6bd4\u8d77\u4e2d\u56fd\u7684\u5404\u5927\u540d\u5c71\uff0c\u6cf0 \u5c71\u5374\u5e76\u4e0d\u7b97\u9ad8\uff0c\u53ea\u56e0\u5c71\u4e1c\u5730\u52bf\u5e73\u5766\uff0c\u6709\u8fd9\u4e48\u4e00\u5ea7\u5927\u5c71\uff0c\u4fbf\u663e\u5f97\u7279\u522b\u96c4\u4f1f\u7f62\u4e86\u3002\u4f46\u6b63\u56e0\u5176\u5982 \u6b64\uff0c\u6cf0\u5c71\u4e0a\u7684\u5bfa\u89c2\u5efa\u7b51\u4fbf\u6bd4\u522b\u7684\u540d\u5c71\u591a\uff0c\u98ce\u666f\u540d\u80dc\u4e5f\u5c61\u7ecf\u4eba\u4fee\u7f2e\uff0c\u6bcf\u5e74\u6e38\u4eba\u751a\u591a\uff0c\uff08\u82e5\u50cf\u5929 \u5c71\u559c\u9a6c\u62c9\u96c5\u5c71\u7b49\u4e4b\u9ad8\u51fa\u4e91\u9704\uff0c\u90a3\u5c31\u4e0d\u53ef\u80fd\u6709\u6e38\u5ba2\u4e86\u3002\uff09\u6cf0\u5c71\u811a\u4e0b\uff0c\u4e5f\u5f00\u6709\u5ba2\u5e97\uff0c\u63a5\u5f85\u767b\u5c71\u6e38 \u5ba2\uff0c\u5c11\u5e74\u4e0e\u6a0a\u82f1\u6295\u5bbf\uff0c\u8981\u4e86\u4e24\u95f4\u623f\uff0c\u5e97\u5c0f\u4e8c\u4fbf\u8fc7\u6765\u515c\u63fd\u751f\u610f\u3002 \u3000\u3000\u5c11\u5e74\u4e00\u5f00\u53e3\u4fbf\u95ee\u9053\uff1a\u201c\u6cf0\u5c71\u4e0a\u53ef\u5e73\u9759\u4e48\uff1f\u201d\u5e97\u5c0f\u4e8c\u6014\u4e86\u4e00\u6014\uff0c\u7b54\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u5e73\u9759\uff0c\u82e5 \u4e0d\u5e73\u9759\uff0c\u6211\u4eec\u8fd8\u80fd\u5728\u6b64\u5730\u5f00\u5e97\u4e48\uff1f\u4e24\u4f4d\u662f\u4e0d\u662f\u8981\u4e0a\u5c71\u6e38\u73a9\u3002\u6211\u4eec\u8fd9\u91cc\u6709\u4eba\u53ef\u4ee5\u966a\u4f60\u4eec\u53bb\u73a9\uff0c \u53ea\u8981\u4e94\u94b1\u94f6\u5b50\uff0c\u7701\u5f97\u4f60\u8dd1\u51a4\u6789\u8def\u3002\u55ef\uff0c\u6cf0\u5c71\u4e0a\u53ef\u770b\u7684\u5730\u65b9\u53ef\u771f\u591a\u5462\uff0c\u6709\u4eba\u5e26\u53bb\uff0c\u62c5\u4fdd\u4f60\u4e0d\u4f1a \u6f0f\u4e86\u4e00\u5904\u3002\u201d\u6a0a\u82f1\u70b9\u4e86\u70b9\u5934\uff0c\u5411\u5c11\u5e74\u5fae\u5fae\u4e00\u7b11\uff0c\u5c11\u5e74\u4e0d\u89e3\u4ed6\u662f\u6697\u542b\u8ba5\u7b11\uff0c\u4e5f\u70b9\u5934\u8bf4\u9053\uff1a\u201c\u597d \u6781\uff0c\u597d\u6781\uff01\u201d \u3000\u3000\u5f53\u65f6\u521d\u6625\uff0c\u6cf0\u5c71\u4e0a\u6742\u82b1\u76db\u5f00\uff0c\u6d53\u9999\u6ee1\u8c37\uff0c\u5c71\u666f\u679c\u7136\u79c0\u4e3d\uff0c\u4e24\u4eba\u8ddf\u7740\u5411\u5bfc\uff0c\u4e0a\u201c\u5cb1\u5b97 \u5773\u201d\uff0c\u4e0a\u4e2d\u5929\u95e8\uff0c\u90a3\u5411\u5bfc\u4e0d\u4f4f\u5730\u6307\u70b9\u540d\u80dc\u53e4\u8ff9\uff0c\u6ed4\u6ed4\u4e0d\u7edd\u5730\u89e3\u91ca\uff1a\u8fd9\u662f\u516b\u4ed9\u6865\uff0c\u8fd9\u662f\u738b\u6bcd \u5730\uff0c\u90a3\u662f\u201c\u5b54\u5b50\u767b\u4e34\u5904\u201d\uff0c\u90a3\u662f\u6c34\u5e18\u6d1e\uff0c\u90a3\u662f\u6b47\u9a6c\u5ca9\uff0c\u90a3\u662f\u5143\u5b9d\u5cf0\uff0c\u5c11\u5e74\u4e0e\u6a0a\u82f1\u65e0\u5fc3\u89c2\u8d4f\uff0c \u4e0d\u4f4f\u5730\u50ac\u90a3\u5411\u5bfc\u5feb\u8d70\u3002 \u3000\u3000\u8fc7\u4e2d\u5929\u95e8\uff0c\u770b\u4e86\u201c\u4e94\u5927\u592b\u677e\u201d\uff0c\u636e\u8bf4\u90a3\u662f\u79e6\u59cb\u7687\u767b\u5c71\u5c01\u7985\uff0c\u66fe\u5728\u6811\u4e0b\u907f\u9633\uff0c\u6240\u4ee5\u628a\u4e94\u68f5 \u677e\u6811\u5c01\u4e3a\u5927\u592b\uff0c\u542c\u8bf4\u539f\u6765\u7684\u6811\u5df2\u7ecf\u6b7b\u4e86\uff0c\u540e\u4eba\u8865\u79cd\u7684\u4e5f\u53ea\u5269\u4e0b\u4e09\u682a\uff0c\u5176\u5b9e\u6ca1\u6709\u4ec0\u4e48\u770b\u5934\uff0c\u6e38 \u5ba2\u5374\u6700\u591a\uff0c\u5c11\u5e74\u66f4\u4e0d\u8010\u70e6\uff0c\u770b\u4e86\u4e00\u773c\u4fbf\u8fc7\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\u6709\u51b7\u7b11\u4e4b\u58f0\uff0c\u6a0a\u82f1\u56de\u5934\u4e00\u671b\uff0c\u89c1\u4e00\u4e2a \u9053\u58eb\u966a\u7740\u4e00\u4e2a\u5bcc\u5546\u6a21\u6837\u7684\u4eba\uff0c\u6307\u624b\u5212\u811a\u5730\u4f3c\u4e4e\u5728\u90a3\u513f\u8bb2\u89e3\u4e94\u5927\u592b\u677e\u7684\u6765\u5386\uff0c\u90a3\u5bcc\u5546\u7b11\u9053\uff1a \u201c\u6709\u4eba\u767b\u5c71\uff0c\u72b9\u5982\u8d76\u96c6\uff0c\u5982\u6b64\u6e38\u5c71\uff0c\u771f\u4e0d\u5982\u8eba\u5728\u5bb6\u91cc\uff0c\u7761\u4ed6\u5988\u7684\u6625\u79cb\u5927\u89c9\u8fd8\u597d\uff0c\u5143\u4efb\u5144\uff0c\u4f60 \u8bf4\u662f\u4e0d\u662f\uff1f\u201d\u540e\u4e00\u53e5\u8bdd\u662f\u5bf9\u53e6\u4e00\u4e2a\u540c\u4f34\u8bf4\u7684\uff0c\u90a3\u4e2a\u53eb\u505a\u5143\u4efb\u7684\u6447\u5934\u6446\u5c3e\u4ed6\u8bf4\u9053\uff1a\u201c\u5bf9\u6781\uff0c\u5bf9 \u6781\u3002\u5077\u5f97\u6d6e\u751f\u534a\u6708\uff0c\u5ffd\u95fb\u6625\u5c3d\u5f3a\u767b\u5c71\u3002\u65e2\u4e0a\u5c71\u6765\uff0c\u4fbf\u5f53\u5c3d\u60c5\u6e38\u89c8\u3002\u201d\u6a0a\u82f1\u770b\u8fd9\u4e24\u4eba\u6240\u4f5c\u7684\u9644 \u5eb8\u98ce\u96c5\u4e4b\u72b6\uff0c\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u7b11\uff0c\u767d\u8863\u5c11\u5e74\u5374\u72e0\u72e0\u5730\u77aa\u4e86\u90a3\u4e24\u4eba\u4e00\u773c\uff0c\u5ffd\u9053\uff1a\u201c\u6211\u53bb\u4e00\u4f1a\u513f\u3002\u201d \u6a0a\u82f1\u5fd9\u9053\uff1a\u201c\u4e0d\u53ef\u591a\u4e8b\u4e86\u3002\u201d\u5c11\u5e74\u4e00\u6e9c\u54bd\u5730\u8dd1\u4e86\uff0c\u5374\u5e76\u975e\u53bb\u627e\u90a3\u5e72\u4eba\u7684\u6666\u6c14\uff0c\u800c\u662f\u5230\u53e6\u4e00\u5904 \u4e71\u77f3\u4e4b\u540e\u7684\u9690\u50fb\u6240\u5728\uff0c\u5411\u5bfc\u9053\uff1a\u201c\u5728\u5c71\u4e0a\u5c0f\u89e3\u4e0d\u59a8\u4e8b\u7684\u3002\u201d\u80cc\u8f6c\u4e86\u8eab\uff0c\u6a0a\u82f1\u5077\u671b\uff0c\u89c1\u4e71\u77f3\u5806 \u4e2d\uff0c\u9690\u6709\u706b\u661f\u98de\u8d77\uff0c\u5fc3\u4e2d\u4e49\u6697\u6697\u597d\u7b11\uff0c\u660e\u77e5\u8fd9\u5c11\u5e74\u54ea\u91cc\u662f\u53bb\u5c0f\u89e3\uff0c\u6562\u60c5\u53c8\u662f\u5728\u77f3\u5934\u6b62\u523b\u5b57\u53bb \u4e86\u3002 \u3000\u3000\u5c11\u5e74\u56de\u6765\u628a\u6a0a\u82f1\u62c9\u540e\u4e24\u6b65\uff0c\u6084\u6084\u95ee\u9053\uff1a\u201c\u4f60\u770b\u90a3\u4e24\u4eba\u662f\u4ec0\u4e48\u8def\u9053\uff1f\u201d\u6a0a\u82f1\u7b11\u9053\uff1a\u201c\u4f9d\u4f60 \u770b\u6765\uff0c\u4eba\u4eba\u90fd\u4e0e\u90a3\u8499\u9762\u5927\u76d7\u6709\u5173\u4e86\u3002\u4f60\u521a\u624d\u53c8\u662f\u53bb\u7559\u5b57\u9a82\u4eba\u662f\u9f20\u5077\uff0c\u4e0d\u6562\u9732\u5934\u662f\u4e0d\u662f\uff1f\u201d\u5c11 \u5e74\u7b11\u4e86\u4e00\u7b11\uff0c\u610f\u4f3c\u9ed8\u8ba4\uff0c\u9053\uff1a\u201c\u4eba\u4e0d\u53ef\u8c8c\u76f8\u3002\u90a3\u9633\u5b97\u6d77\u96be\u9053\u4e0d\u662f\u626e\u6210\u81c3\u8eab\u5eb8\u4fd7\u7684\u5546\u4eba\u6a21\u6837 \u5417\uff1f\u201d\u6a0a\u82f1\u5fc3\u4e2d\u4e00\u51db\uff0c\u518d\u770b\u65f6\u90a3\u5e72\u4eba\u5df2\u4e0d\u77e5\u5230\u54ea\u91cc\u6e38\u89c8\u4e86\u3002\u6a0a\u82f1\u81ea\u5df1\u5f00\u89e3\uff1a\u4e16\u95f4\u54ea\u80fd\u6709\u51e0\u4e2a \u9633\u5b97\u6d77\uff1f \u3000\u3000\u8fc7\u4e86\u5357\u5929\u95e8\uff0c\u4e0a\u5929\u67f1\u5cf0\uff0c\u90a3\u4fbf\u662f\u6cf0\u5c71\u6700\u9ad8\u5904\u7684\u7389\u7687\u9876\u4e86\uff0c\u5c71\u9876\u6709\u4e2a\u7389\u7687\u89c2\uff0c\u95e8\u9762\u76f8\u5f53\u6574 \u9f50\uff0c\u6e38\u4eba\u591a\u5230\u8fd9\u91cc\u501f\u5bbf\u3002\u8fd9\u65f6\u5df2\u8fd1\u9ec4\u660f\uff0c\u6a0a\u82f1\u548c\u767d\u8863\u5c11\u5e74\u4e5f\u501f\u5bbf\u89c2\u4e2d\uff0c\u6a0a\u82f1\u7779\u6697\u7559\u5fc3\u3002\u5374\u4e0d \u89c1\u90a3\u4e00\u5e72\u4eba\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\u8d77\u6765\uff0c\u4f9d\u767d\u8863\u5c11\u5e74\u4e4b\u610f\uff0c\u4fbf\u8981\u56de\u53bb\u3002\u5411\u5bfc\u8a00\u9053\uff0c\u51e1\u6709\u767b\u6cf0\u5c71\u4e4b\u4eba\uff0c\u672a\u6709\u4e0d\u770b \u65e5\u51fa\u7684\uff0c\u6a0a\u82f1\u4e5f\u9053\uff0c\u65e2\u7136\u6765\u5230\uff0c\u90a3\u4e5f\u4e0d\u8fdf\u5728\u8fd9\u4e00\u4e9b\u65f6\u5019\uff0c\u5c11\u5e74\u60f3\u4e86\u4e00\u60f3\uff0c\u4e5f\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u5728\u6cf0\u5c71\u7edd\u9876\u770b\u65e5\u51fa\uff0c\u679c\u7136\u522b\u6709\u4f73\u8da3\uff0c\u4e1c\u65b9\u521a\u73b0\u9c7c\u809a\u767d\uff0c\u4e91\u5c42\u4e0b\u9762\u4fbf\u62b9\u4e0a\u4e00\u5c42\u6de1\u7ea2\u7684\u671d \u971e\uff0c\u8fdc\u773a\u4e1c\u6d77\uff0c\u4e00\u6761\u6761\u767d\u8272\u7684\u6c34\u7eb9\uff0c\u50cf\u5fae\u98ce\u4e2d\u98d8\u52a8\u7684\u5f69\u5e26\uff0c\u7a81\u7136\u4e00\u8f6e\u7ea2\u65e5\uff0c\u4f3c\u5ffd\u5730\u4ece\u5927\u6d77\u4e2d \u8df3\u51fa\u6765\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5c04\u51fa\u4e07\u4e08\u5149\u8292\uff0c\u5c71\u6cb3\u5927\u5730\u90fd\u50cf\u88ab\u4e0a\u4e86\u65b0\u5a18\u7ea2\u8272\u7684\u5934\u7eb1\uff0c\u6a0a\u82f1\u957f\u8d70\u6c5f\u6e56\uff0c \u5374\u4e5f\u672a\u66fe\u89c1\u8fc7\u5982\u6b64\u5947\u666f\uff0c\u5076\u4e00\u56de\u5934\uff0c\u53ea\u89c1\u90a3\u767d\u8863\u5c11\u5e74\u51dd\u671b\u4e91\u6d77\uff0c\u5982\u6709\u6240\u601d\uff0c\u773c\u89d2\u5ffd\u7136\u6389\u4e0b\u4e24 \u9897\u6cea\u73e0\uff0c\u6084\u7136\u541f\u9053\uff1a\u201c\u65e5\u51fa\u4e1c\u5357\u9685\uff0c\u5927\u6d77\u8000\u660e\u73e0\uff0c\u8c01\u77e5\u6e38\u5b50\u610f\uff0c\u96be\u62a5\u4e09\u6625\u6656\u3002\u201d\u6a0a\u82f1\u7565\u901a\u6587 \u58a8\uff0c\u5374\u4e0d\u89e3\u5176\u4e2d\u6df1\u610f\uff0c\u53ea\u9053\u662f\u5c11\u5e74\u601d\u5ff5\u4ed6\u7684\u7236\u6bcd\uff0c\u5fc3\u4e2d\u5140\u81ea\u6697\u7b11\uff1a\u8fd9\u5c11\u5e74\u5230\u5e95\u662f\u672a\u51fa\u8fc7\u8fdc\u95e8 \u7684\u96cf\u513f\u3002\u5ffd\u542c\u5f97\u4fa7\u9762\u8a00\u7b11\u55a7\u55a7\uff0c\u539f\u6765\u662f\u53e6\u4e00\u7fa4\u6e38\u5ba2\u5728\u53f3\u624b\u8fb9\u7684\u201c\u8fce\u65ed\u4eab\u201d\u4e0b\u9762\u770b\u65e5\u51fa\uff0c\u5176\u4e2d \u4fbf\u6709\u6628\u65e5\u6240\u89c1\u7684\u90a3\u50cf\u5546\u4eba\u6a21\u6837\u7684\u4eba\uff0c\u6a0a\u82f1\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6ce8\u89c6\u90a3\u4e9b\u4eba\uff0c\u5374\u662f\u5e76\u65e0\u5f02\u72b6\uff0c\u6e10\u6e10\u722c\u4e0a \u66f4\u9ad8\u7684\u5cf0\u9876\u53bb\u770b\u65e5\u51fa\u4e86\u3002 \u3000\u3000\u5230\u7ea2\u65e5\u5347\u8d77\uff0c\u767d\u8863\u5c11\u5e74\u5df2\u662f\u610f\u5174\u9611\u73ca\uff0c\u5306\u5306\u5403\u8fc7\u65e9\u70b9\uff0c\u4fbf\u5373\u4e0b\u5c71\uff0c\u56de\u5230\u4e86\u5ba2\u5e97\uff0c\u6070\u662f\u9ec4 \u660f\u65f6\u5206\uff0c\u5e97\u5c0f\u4e8c\u51fa\u6765\u8fce\u63a5\uff0c\u95ee\u9053\uff1a\u201c\u5ba2\u5b98\u6e38\u5f97\u5982\u4f55\uff0c\u6211\u7ed9\u4f60\u4fdd\u8350\u7684\u5411\u5bfc\u53ef\u6ca1\u9519\u5427\uff1f\u201d\u767d\u8863\u5c11 \u5e74\u54fc\u4e86\u4e00\u58f0\uff0c\u6a0a\u82f1\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u8fd8\u597d\uff01\u201d\u4e24\u4eba\u8981\u4e86\u4e24\u95f4\u623f\uff0c\u5429\u5490\u5e97\u5bb6\u51c6\u5907\u665a\u81b3\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u56de\u5230\u623f\u4e2d\uff0c\u4fbf\u9a82\u90a3\u201c\u4e0d\u6562\u9732\u9762\u201d\u7684\u8499\u9762\u5927\u76d7\uff0c\u6a0a\u82f1\u8d70\u8fc7\u6765\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u6b66\u529f \u662f\u9ad8\u660e\u6781\u4e86\uff0c\u4f46\u5728\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u4f3c\u4e4e\u4e0d\u591a\u884c\u8d70\u5427\uff1f\u5e38\u8a00\u9053\u5f97\u597d\uff1a\u987b\u9632\u9694\u5899\u6709\u8033\uff0c\u2026\u2026\u201d\u8bdd\u4e0d\u8bf4 \u5b8c\uff0c\u767d\u8863\u5c11\u5e74\u4fbf\u62a2\u767d\u4ed6\u9053\uff1a\u201c\u54fc\uff0c\u6211\u82e5\u6015\u4ed6\uff0c\u4e5f\u5c31\u4e0d\u6765\u5bfb\u627e\u4ed6\u4e86\uff0c\u90a3\u53f7\u79f0\u5927\u76d7\u7684\u9f20\u7a83\u72d7\u5077\u4e4b \u8f88\uff0c\u6211\u5df4\u4e0d\u5f97\u4ed6\u542c\u5230\u6211\u9a82\u4ed6\u7684\u8bf4\u8bdd\u3002\u201d\u8d8a\u8bf4\u8d8a\u5927\u58f0\uff0c\u6a0a\u82f1\u53ea\u597d\u82e6\u7b11\u3002\u5ffd\u542c\u5f97\u5916\u9762\u4e5f\u6709\u5435\u95f9\u4e4b \u58f0\uff0c\u6a0a\u82f1\u9053\uff1a\u201c\u54a6\uff0c\u600e\u4e48\u6709\u8fd9\u6837\u51f6\u7684\u5ba2\u4eba\uff0c\u54b1\u4eec\u51fa\u53bb\u77a7\u7901\u3002\u201d\u4ed6\u662f\u60f3\u85c9\u6b64\u673a\u4f1a\uff0c\u8f6c\u79fb\u90a3\u767d\u8863 \u5c11\u5e74\u7684\u6ce8\u610f\uff0c\u53eb\u4ed6\u4e0d\u8981\u80e1\u9a82\u3002 \u3000\u3000\u5916\u9762\u6765\u7684\u4e09\u4e2a\u5ba2\u4eba\uff0c\u7adf\u7136\u662f\u4e00\u4e2a\u9053\u58eb\u548c\u4e24\u4e2a\u4e5e\u4e10\uff0c\u6562\u60c5\u662f\u5e97\u5bb6\u4e0d\u8ba9\u4ed6\u4eec\u6295\u5bbf\uff0c\u53ea\u542c\u5f97\u90a3 \u9053\u58eb\u9a82\u9053\uff1a\u201c\u5f00\u9986\u5b50\u7684\u4e0d\u6015\u809a\u5b50\u5927\uff0c\u5f00\u5ba2\u5e97\u7684\u4e0d\u6015\u80ae\u810f\u5ba2\uff0c\u4f60\u662f\u770b\u8863\u88f3\u4e0d\u770b\u4eba\u7684\u5417\uff1f\u4e3a\u4f55\u4e0d \u8ba9\u6211\u4eec\u6295\u5bbf\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u9053\u7237\uff0c\u4f60\u8981\u4f4f\u623f\u5c3d\u7ba1\u5429\u5490\uff0c\u8fd9\u4e24\u4f4d\u82b1\u5b50\u7237\uff0c\u54b1\u4eec\u7684\u5e97\u89c4\u662f\u4e0d\u6536 \u7559\u7684\u3002\u201d\u90a3\u9053\u58eb\u9a82\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u5929\u4e0b\u54ea\u6709\u8fd9\u6837\u7684\u5e97\u89c4\uff1f\u201d\u90a3\u4e24\u4e2a\u53eb\u5316\u5b50\u5ffd\u7136\u7b11\u563b\u563b\u5730\u9053\uff1a \u201c\u9053\u957f\uff0c\u4fd7\u8bdd\u8bf4\u72d7\u773c\u770b\u4eba\u4f4e\uff0c\u679c\u7136\u8bf4\u5f97\u4e0d\u9519\u3002\u201d\u5ffd\u5730\u9762\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u5bb6\u82b1\u5b50\u7237\u4e0d\u7231\u7a7f\u51cc \u7f57\u7ef8\u7f0e\uff0c\u4f60\u7ba1\u5f97\u7740\u4e48\uff1f\u201d\u201c\u556a\u201d\u5730\u5c06\u4e00\u952d\u5927\u94f6\u63b7\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u82b1\u5b50\u7237\u7684\u94f6\u5b50\u4e5f\u662f\u767d\u82b1\u82b1 \u7684\uff0c\u5e76\u4e0d\u6bd4\u5927\u7237\u4eec\u7684\u94f6\u5b50\u7f3a\u4e86\u6210\u8272\uff0c\u4f60\u77a7\u6e05\u695a\u53bb\uff01\u201d \u3000\u3000\u666e\u5929\u4e0b\u7684\u5ba2\u5e97\uff0c\u867d\u7136\u6ca1\u6709\u8ba2\u660e\u8981\u4f55\u7b49\u6837\u7684\u5ba2\u4eba\u624d\u80af\u62db\u6536\uff0c\u4f46\u4e0d\u6b22\u8fce\u4e5e\u4e10\u6295\u5bbf\uff0c\u90a3\u5374\u662f\u95f4 \u95f4\u5982\u6b64\uff0c\u4e0d\u987b\u8bf4\u660e\u7684\uff0c\u800c\u4e8b\u5b9e\u4e0a\u4e5f\u4ece\u672a\u66fe\u6709\u8fc7\u4e5e\u4e10\u6295\u5bbf\u5ba2\u5e97\u4e4b\u4e8b\uff0c\u90a3\u53eb\u5316\u5b50\u4e00\u51fa\u624b\u5c31\u662f\u4e00\u952d \u96ea\u767d\u7684\u94f6\u5b50\uff0c\u770b\u6765\u8db3\u6709\u5341\u4e24\uff0c\u5e97\u5c0f\u4e8c\u4e0d\u89c9\u5446\u4e86\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u7237\u65e2\u8d70\u8981\u5149\u987e\u5c0f\u5e97\uff0c\u90a3 \u4e5f\u53ef\u4ee5\u901a\u878d\u901a\u878d\u3002\u201d\u90a3\u53eb\u5316\u5b50\u53c8\u9a82\u9053\uff1a\u201c\u4ec0\u4e48\u901a\u878d\uff1f\u5e72\u8106\u8bf4\u4f60\u613f\u4e0d\u613f\u670d\u4f8d\u5927\u7237\u3002\u201d\u773c\u775b\u4e00 \u77aa\uff0c\u90a3\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u670d\u4f8d\uff0c\u670d\u4f8d\uff01\u201d\u8d76\u5feb\u7ed9\u90a3\u9053\u58eb\u548c\u4e24\u4e2a\u4e5e\u4e10\u51c6\u5907\u4e0a\u7b49\u623f\u95f4\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u770b\u5f97\u751a\u662f\u597d\u7b11\uff0c\u548c\u6a0a\u82f1\u56de\u5230\u623f\u5b50\uff0c\u51fb\u684c\u8bf4\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u4e5e\u4e10\u5012\u662f\u5999\u4eba\uff0c\u9a82\u5f97\u75db \u5feb\u3002\u201d\u6a0a\u82f1\u8fe2\uff1a\u201c\u8fd9\u4e00\u5e72\u4eba\u82e5\u975e\u4fa0\u5ba2\u5c31\u8d70\u662f\u5f3a\u76d7\uff0c\u54b1\u4eec\u4e0d\u8981\u5728\u80cc\u540e\u8bae\u8bba\u4ed6\u4eec\u3002\u201d\u767d\u8863\u5c11\u5e74 \u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u8bf4\u4ed6\u4eec\u662f\u8499\u9762\u5927\u76d7\u7684\u4e00\u4f19\u5417\uff1f\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u8fd9\u4e5f\u672a\u53ef\u6599\u3002\u201d\u5c11\u5e74\u9053\uff1a\u201c\u597d\uff0c \u90a3\u4e48\u6211\u5c31\u8981\u9a82\u4ed6\u4eec\u4e86\u3002\u201d\u6a0a\u82f1\u5fd9\u9053\uff1a\u201c\u5929\u4e0b\u5f02\u4eba\u751a\u591a\uff0c\u4e5f\u672a\u5fc5\u5c31\u662f\u90a3\u8499\u9762\u5927\u76d7\u7684\u515a\u7fbd\u3002\u201d\u767d \u8863\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8bf4\u8bdd\u8001\u662f\u6a21\u68f1\u4e24\u53ef\uff01\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u6211\u59d4\u5b9e\u662f\u4e0d\u77e5\u9053\u5440\u3002\u4f60\u4e0d\u8981\u9a82\u9519\u4eba \u4e86\u3002\u201d\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u4e0d\u9a82\u4ed6\u4eec\uff0c\u4e13\u9a82\u90a3\u53f7\u79f0\u5927\u76d7\u7684\u9f20\u7a83\u72d7\u5077\u3002\u201d\u6a0a\u82f1\u62e6\u963b\u4e0d\u4f4f\uff0c \u53c8\u53ea\u597d\u82e6\u7b11\u3002\u90a3\u5c11\u5e74\u9a82\u4e86\u4e00\u9635\uff0c\u89c1\u6ca1\u4eba\u7b54\u7406\uff0c\u4e5f\u5c31\u7f62\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\u8d77\u6765\uff0c\u5e97\u5c0f\u4e8c\u8fdb\u6765\u7ed3\u5e10\uff0c\u767d\u8863\u5c11\u5e74\u6b63\u5f85\u95ee\u4ed6\uff0c\u90a3\u4e24\u4e2a\u53eb\u5316\u5b50\u5982\u4f55\uff1f\u6a0a\u82f1\u8fd9\u65f6 \u65e9\u5df2\u62fe\u597d\u884c\u56ca\uff0c\u8fc7\u5230\u5c11\u5e74\u623f\u95f4\u7b49\u5019\u4ed6\u4e00\u540c\u8d77\u7a0b\uff0c\u90a3\u5e97\u5c0f\u4e8c\u5374\u5ffd\u5730\u6367\u51fa\u4e00\u4e2a\u5927\u7ea2\u62dc\u76d2\uff0c\u8bf4\u9053\uff1a \u201c\u4eca\u513f\u4e00\u65e9\uff0c\u6709\u4eba\u5c06\u8fd9\u4e2a\u62dc\u76d2\u9001\u6765\uff0c\u53eb\u6211\u8f6c\u5448\u4e24\u4f4d\u5927\u7237\uff0c\u8bf4\u662f\u8981\u8bf7\u4f60\u4eec\u4e24\u4f4d\u8d4f\u5149\u3002\u201d\u6a0a\u82f1 \u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u9001\u6765\u7684\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u4ed6\u4eec\u8bf4\u662f\u6b66\u5bb6\u5e84\u7684\u5e84\u4e01\u3002\u201d\u6a0a\u82f1\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u5374\u4e0d \u6253\u5f00\u62dc\u76d2\uff0c\u5148\u628a\u5e97\u94b1\u7ed3\u4e86\uff0c\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u591a\u8c22\uff0c\u591a\u8c22\uff0c\u4e00\u8def\u987a\u98ce\uff0c\u8fd8\u6709\u4ec0\u4e48\u8981\u5c0f\u7684\u505a\u4e48\uff1f\u201d \u6a0a\u82f1\u6325\u624b\u9053\uff1a\u201c\u4e0d\u7528\u4e86\u3002\u201d\u5e97\u5c0f\u4e8c\u6b63\u8981\u9000\u51fa\uff0c\u767d\u8863\u5c11\u5e74\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u53eb\u5316\u5b50\u8fd8\u5728\u5e97\u4e2d \u5417\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u82b1\u5b50\u7237\u4e00\u65e9\u5c31\u8d70\u4e86\u3002\u5440\uff0c\u6211\u53ef\u8fd8\u771f\u7684\u6ca1\u6709\u89c1\u8fc7\u8fd9\u6837\u9614\u6c14\u7684\u5ba2\u4eba\uff01 \u5341\u4e24\u94f6\u5b50\uff0c\u4e0d\u8981\u627e\u8d4e\uff0c\u5168\u8d4f\u7ed9\u6211\u4eec\u4e86\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5b9e\u662f\u60f3\u5411\u4e8c\u4eba\u591a\u8ba8\u8d4f\u94f6\uff0c\u767d\u8863\u5c11\u5e74\u5374\u542c \u4e0d\u51fa\u6765\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u4f60\u4eec\u53d7\u4ed6\u4e00\u987f\u9a82\uff0c\u4e5f\u8fd8\u503c\u5f97\u3002\u201d\u5e97\u5c0f\u4e8c\u5c34\u5c2c\u82e6\u7b11\uff0c\u4e00\u53cc\u773c\u775b\u5374\u5c3d\u671b\u7740\u767d \u8863\u5c11\u5e74\uff0c\u4e0d\u80af\u9000\u51fa\uff0c\u5c11\u5e74\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u8fd8\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u5f85\u5019\u4f60\u5927\u7237\u3002\u201d\u5c11 \u5e74\u6b63\u60f3\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u65e9\u8bf4\u8fc7\u6ca1\u4e8b\u4e86\u5417\uff1f\u8c01\u8981\u4f60\u4f8d\u5019\u3002\u201d\u5374\u89c1\u6a0a\u82f1\u6478\u4e86\u4e00\u952d\u94f6\u5b50\u51fa\u6765\uff0c\u9053\uff1a \u201c\u8fd9\u8d4f\u7ed9\u4f60\uff0c\u4e0d\u5fc5\u4f8d\u5019\u5566\uff01\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u9000\u51fa\u4e4b\u540e\uff0c\u767d\u8863\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u6a0a\u5927\u54e5\uff0c\u4f60\u8981\u548c\u5316\u5b50\u6bd4\u9614\u6c14\u5417\uff1f\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u54b1\u4eec \u5bfb\u8bbf\u90a3\u8499\u9762\u5927\u76d7\u4ee5\u6765\uff0c\u8fd9\u4e24\u65e5\u624d\u78b0\u5230\u4e00\u4e9b\u5f02\u4eba\u5f02\u4e8b\uff0c\u6211\u77a7\u662f\u6709\u70b9\u7709\u76ee\u4e86\u3002\u201d\u4e0d\u7b54\u5c11\u5e74\u9002\u624d\u90a3 \u8bdd\uff0c\u5374\u6367\u7740\u62dc\u76d2\u77a7\u6765\u77a7\u53bb\uff0c\u767d\u8863\u5c11\u5e74\u56b7\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fd8\u4e0d\u6253\u5f00\uff1f\u201d \u3000\u3000\u6a0a\u82f1\u5173\u4e0a\u623f\u95e8\uff0c\u5c06\u62dc\u76d2\u653e\u5728\u684c\u4e0a\uff0c\u62c9\u767d\u8863\u5c11\u5e74\u9000\u5230\u5c4b\u89d2\uff0c\u6478\u51fa\u4e00\u628a\u5315\u9996\uff0c\u5c11\u5e74\u9053\uff1a\u201c\u6a0a \u5927\u54e5\uff0c\u4f60\u8fd9\u662f\u5e72\u4e48\uff1f\u201d\u6a0a\u82f1\u624b\u5fc3\u4e00\u65cb\uff0c\u90a3\u628a\u5315\u9996\u6253\u4e86\u4e00\u4e2a\u5f27\u5f62\uff0c\u659c\u98de\u51fa\u53bb\uff0c\u8f70\u7136\u58f0\u54cd\uff0c\u5c06\u90a3 \u62dc\u76d2\u5212\u5f00\uff0c\u76d2\u76d6\u8dcc\u5728\u4e00\u65c1\uff0c\u767d\u8863\u5c11\u5e74\u83ab\u540d\u5176\u5999\uff0c\u5fc3\u9053\uff1a\u201c\u5f00\u8fd9\u62dc\u76d2\uff0c\u4f55\u7528\u8d39\u5982\u8bb8\u529b\u6c14\uff1f\u201d\u53ea \u89c1\u6a0a\u82f1\u8d70\u53bb\uff0c\u5c06\u76d2\u4e2d\u62dc\u5e16\u62ff\u8d77\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u771f\u7684\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u4ec0\u4e48\u771f\u7684\u5047\u7684\uff1f\u8c01\u7684\u5e16\u5b50\uff1f\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u8fd9\u662f\u5c0f\u91d1\u9f99\u6b66\u632f\u4e1c\u7684\u5e16\u5b50\uff0c\u6211 \u4e0e\u4ed6\u4e0d\u8fc7\u6cdb\u6cdb\u4e4b\u4ea4\uff0c\u4ed6\u5374\u6d3e\u4eba\u8bf7\u6211\u5230\u4ed6\u5e84\u5b50\u53bb\uff0c\u8fd8\u8bf7\u4e86\u4f60\uff0c\u8fd9\u5012\u5947\u602a\u4e86\u3002\u201d\u6b66\u632f\u4e50\u662f\u5c71\u4e1c\u5357 \u9762\u4e00\u4e2a\u5e84\u4e3b\uff0c\u636e\u8bf4\u4ed6\u5c11\u5e74\u65f6\u5019\u66fe\u505a\u8fc7\u72ec\u811a\u5927\u76d7\uff0c\u4e2d\u5e74\u65f6\u5019\uff0c\u6d17\u624b\u5f52\u9690\uff0c\u5728\u4e61\u4e0b\u7f6e\u4e86\u4ea7\u4e1a\uff0c\u5efa \u4e86\u4e00\u5ea7\u597d\u5927\u7684\u5e84\u56ed\uff0c\u5bcc\u7532\u4e00\u65b9\uff0c\u4eba\u8a00\u5982\u662f\uff0c\u662f\u5426\u5c5e\u5b9e\uff0c\u4e0d\u5f97\u800c\u77e5\uff0c\u8fd9\u6b66\u632f\u4e1c\u6781\u4e3a\u4ed7\u4e49\u758f\u8d22\uff0c \u5e38\u5e74\u56db\u5b63\uff0c\u90fd\u6709\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u5728\u4ed6\u5e84\u56ed\u5bc4\u98df\uff0c\u6240\u4ee5\u4eba\u79f0\u201c\u5c0f\u91d1\u9f99\u201d\uff0c\u53d6\u9f99\u80fd\u5410\u6c34\uff0c\u6da6\u6cfd\u5929\u4e0b \u4e4b\u610f\u3002\u767d\u8863\u5c11\u5e74\u4e5f\u4f3c\u66fe\u542c\u8fc7\u6b66\u632f\u4e1c\u7684\u540d\u5b57\uff0c\u9053\uff1a\u201c\u65e2\u7136\u662f\u5c0f\u91d1\u9f99\u7684\u5e16\u5b50\uff0c\u90a3\u8fd8\u6709\u5047\u7684\u5417\uff1f\u201d \u6a0a\u82f1\u9053\uff1a\u201c\u8001\u5f1f\u6709\u6240\u4e0d\u77e5\uff0c\u6b66\u632f\u4e1c\u5f53\u7136\u4e0d\u4f1a\u505a\u5047\u3002\u4f46\u6050\u6709\u4eba\u5192\u6b66\u632f\u4e1c\u4e4b\u540d\u9001\u62dc\u5e16\u6765\uff0c\u90a3\u5c82\u53ef \u4e0d\u9632\uff1f\u6240\u4ee5\u6211\u8eb2\u5728\u5c4b\u89d2\uff0c\u7528\u98de\u5200\u5212\u5f00\u62dc\u76d2\uff0c\u82e5\u7136\u6709\u4eba\u5f04\u9b3c\uff0c\u90a3\u76d2\u4e2d\u5fc5\u5b9a\u85cf\u6709\u673a\u5173\u6697\u5668\uff0c\u62dc\u76d2 \u4e00\u5f00\uff0c\u6697\u5668\u4fbf\u53d1\u3002\u5982\u4eca\u4e00\u65e0\u6240\u6709\uff0c\u56e0\u6b64\u6211\u624d\u6562\u8bf4\u8fd9\u662f\u771f\u7684\u3002\u201d\u767d\u8863\u5c11\u5e74\u542c\u4e86\uff0c\u6697\u81ea\u4f69\u670d\u6a0a\u82f1 \u7684\u7ec6\u5fc3\u3002 \u3000\u3000\u6a0a\u82f1\u9053\uff1a\u201c\u4f46\u4ecd\u6709\u4e00\u4e8b\u53ef\u7591\u3002\u201d\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u4f55\u4e8b\u53ef\u7591\uff1f\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u6b66\u5bb6\u5e84\u79bb\u6b64\u4e00 \u767e\u516b\u5341\u91cc\uff0c\u4ed6\u7684\u5e16\u5b50\u7ea6\u6211\u4eec\u4eca\u65e5\u5230\u4ed6\u5bb6\u8d74\u9080\uff0c\u4ed6\u600e\u77e5\u9053\u6211\u4eec\u6709\u4e24\u5339\u597d\u9a6c\uff1f\u8001\u5f1f\uff0c\u4f60\u7684\u9a6c\u65e5\u884c \u5343\u91cc\u4e0d\u8db3\u4e3a\u5947\uff0c\u4f46\u901a\u5e38\u7684\u9a6c\uff0c\u8d70\u4e00\u767e\u516b\u5341\u91cc\uff0c\u53ef\u5f97\u4e24\u5934\u89c1\u9ed1\u3002\u201d\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u65e2\u7136\u662f\u8fd9\u5e16\u5b50 \u4e0d\u662f\u5047\u7684\uff0c\u5c0f\u91d1\u9f99\u6b66\u632f\u4e1c\u96be\u9053\u8fd8\u4f1a\u65e0\u7f18\u65e0\u6545\u5730\u8bbe\u4e0b\u9677\u9631\uff0c\u6446\u5e03\u6211\u4eec\u5417\uff1f\u6211\u8bf4\uff0c\u7ec6\u5fc3\u56fa\u597d\uff0c\u4ea6 \u4e0d\u5fc5\u65e0\u8c13\u731c\u7591\uff0c\u54b1\u4eec\u9a6c\u4e0a\u8d76\u8def\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u7ed9\u6a0a\u82f1\u4e70\u7684\u90a3\u5339\u9a6c\u867d\u7136\u7b97\u4e0d\u5f97\u662f\u5b9d\u9a6c\uff0c\u4f46\u4ea6\u751a\u4e3a\u5065\u9a8f\uff0c\u4e0d\u5fc5\u6a0a\u82f1\u600e\u4e48\u97ad\u7b56\uff0c\u5c31 \u653e\u8e44\u75be\u8dd1\uff0c\u4e00\u523b\u4e0d\u505c\uff0c\u6e05\u6668\u52a8\u8eab\uff0c\u65e5\u5934\u672a\u843d\uff0c\u4fbf\u8d76\u5230\u4e86\u6b66\u5bb6\u5e84\uff0c\u6a0a\u82f1\u5728\u79bb\u5e84\u4e09\u91cc\u4e4b\u5730\uff0c\u5373\u4fbf \u4e0b\u9a6c\uff0c\u8fd9\u662f\u6c5f\u6e56\u4e0a\u7684\u89c4\u77e9\uff0c\u8868\u793a\u606d\u656c\u4e4b\u610f\uff0c\u767d\u8863\u5c11\u5e74\u4ea6\u4f9d\u7740\u505a\u4e86\u3002\u4f46\u89c1\u8def\u4e0a\u6709\u8bf8\u8272\u4eba\u7b49\uff0c\u90fd \u7275\u7740\u9a6c\u8d70\u5411\u6b66\u5bb6\uff0c\u6a0a\u82f1\u5fc3\u4e2d\u6697\u81ea\u8be7\u5f02\uff0c\u770b\u8fd9\u60c5\u5f62\uff0c\u83ab\u975e\u662f\u6b66\u5bb6\u5e84\u5927\u6446\u7b75\u5e2d\uff0c\u5e7f\u5bb4\u5bbe\u5ba2\uff0c\u4e00\u62ac \u5934\uff0c\u5ffd\u89c1\u524d\u65e5\u5728\u6cf0\u5c71\u4e4b\u4e0a\u6240\u78b0\u5230\u7684\u90a3\u4e2a\u5546\u4eba\u6a21\u6837\u7684\u4eba\u548c\u90a3\u4e2a\u201c\u5143\u4efb\u5144\u201d\uff0c\u4ee5\u53ca\u6628\u665a\u5728\u5ba2\u5e97\u95f9 \u4e8b\u7684\u90a3\u4e2a\u9053\u58eb\u4ee5\u53ca\u90a3\u4e24\u4e2a\u4e5e\u4e10\u90fd\u5728\u5176\u5185\u3002\u767d\u8863\u5c11\u5e74\u4e0d\u7531\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u6a0a\u82f1\u6025\u5fd9\u6084\u6084\u8bf4\u9053\uff1a \u201c\u4e0d\u53ef\u5927\u60ca\u5c0f\u602a\u3002\u201d\u767d\u8863\u5c11\u5e74\u6a2a\u4ed6\u4e00\u773c\uff0c\u610f\u601d\u662f\u8bf4\uff1a\u8fd9\u4e2a\u6211\u8fd8\u4e0d\u61c2\uff1f\u90a3\u4e00\u4e8e\u4eba\u5374\u5e76\u4e0d\u56de\u5934\uff0c \u597d\u50cf\u5e76\u4e0d\u77e5\u9053\u4ed6\u4eec\u6765\u4e86\u4f3c\u7684\uff0c\u8d70\u8fdb\u5e84\u5185\u3002 \u3000\u3000\u6a0a\u82f1\u4e0e\u767d\u8863\u5c11\u5e74\u8fdb\u5165\u5e84\u5185\uff0c\u81ea\u6709\u7ba1\u4e8b\u7684\u62db\u5f85\uff0c\u5c06\u4ed6\u4eec\u5e26\u5230\u4e00\u4e2a\u82b1\u56ed\u4e4b\u5185\uff1b \u3000\u3000\u82b1\u56ed\u751a\u5927\uff0c\u6446\u4e86\u6570\u5341\u5e2d\u9152\uff0c\u8fd8\u662f\u7ef0\u6709\u4f59\u88d5\uff0c\u4e2d\u95f4\u8fd8\u6709\u4e2a\u7ec3\u6b66\u573a\uff0c\u4e24\u65c1\u72b9\u6709\u5175\u5668\u67b6\u5b50\uff0c\u573a \u4e0a\u6446\u6709\u77f3\u62c5\u77f3\u9501\u4e4b\u7c7b\u3002\u90a3\u7ba1\u4e8b\u7684\u5c06\u4e24\u4eba\u5b89\u7f6e\u5728\u4e1c\u53a2\u7684\u4e00\u4e2a\u623f\u5185\uff0c\u540c\u5e2d\u7684\u4eba\u90fd\u4e0d\u76f8\u8bc6\uff0c\u4f46\u542c\u5f97 \u4ed6\u4eec\u561f\u561f\u55b3\u55b3\u5730\u8c08\u8bba\uff0c\u4e92\u76f8\u63a2\u95ee\u5c0f\u91d1\u9f99\u6b66\u632f\u4e1c\u4e3a\u4f55\u5728\u4eca\u65e5\u5927\u5bb4\u5bbe\u5ba2\uff1f \u3000\u3000\u4ed6\u4eec\u5750\u7684\u8fd9\u5e2d\u79bb\u5f00\u4e3b\u5e2d\u751a\u8fdc\uff0c\u770b\u6765\u4e0d\u8fc7\u662f\u5c06\u4ed6\u4eec\u5f53\u4f5c\u5bbe\u5ba2\uff0c\u968f\u4fbf\u5b89\u7f72\uff0c\u5750\u4e0d\u591a\u4e45\uff0c\u7b75\u5e2d \u4fbf\u5f00\uff0c\u53ea\u89c1\u4e00\u4e2a\u5e74\u7ea6\u516d\u65ec\uff0c\u957f\u7740\u4e09\u7f0e\u957f\u987b\uff0c\u58ee\u8001\u7ec5\u58eb\u7684\u4e00\u4e2a\u8001\u8001\uff0c\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u627f\u8499\u5404\u4f4d\u8d4f \u7ed9\u8001\u673d\u8584\u9762\uff0c\u8fd9\u6b21\u53d1\u51fa\u7684\u82f1\u96c4\u5e16\uff0c\u9664\u4e86\u5143\u6db5\u957f\u8001\u6709\u4e8b\uff0c\u67f3\u5b9a\u5eb5\u5e08\u7236\u56e0\u75c5\uff0c\u5bd2\u6c5f\u9053\u957f\u5728\u6e56\u5357\u8fd8 \u672a\u53ca\u8d74\u56de\u4e4b\u5916\uff0c\u5176\u4f59\u7684\u5168\u90e8\u6765\u4e86\u3002\u4eca\u65e5\u7b97\u5f97\u662f\u54b1\u4eec\u5317\u4e94\u7701\u82f1\u96c4\u7684\u5927\u96c6\u4f1a\u4e86\u3002\u627f\u5404\u4f4d\u8d4f\u9762\uff0c\u8bf7 \u5148\u5c3d\u4e09\u676f\u3002\u201d\u6a0a\u82f1\u5403\u4e86\u4e00\u60ca\uff1a\u64a4\u82f1\u96c4\u5e16\u8fd9\u662f\u975e\u540c\u5c0f\u53ef\u4e4b\u4e8b\uff0c\u60f3\u8fd9\u6b66\u632f\u4e1c\u65e9\u5df2\u517b\u8001\u7eb3\u798f\uff0c\u96be\u9053 \u4ed6\u8fd8\u6709\u4ec0\u4e48\u56fe\u8c0b\uff1f \u3000\u3000\u9152\u8fc7\u4e09\u5de1\uff0c\u6b66\u632f\u4e1c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5728\u5ea7\u7684\u90fd\u662f\u597d\u670b\u53cb\uff0c\u6211\u6b66\u67d0\u4eba\u5c11\u5e74\u4e4b\u65f6\uff0c\u4e5f\u66fe\u505a\u8fc7\u6ca1\u672c \u94b1\u7684\u751f\u610f\uff0c\u4e0d\u5fc5\u5fcc\u8bb3\u3002\u8fd1\u6765\u542c\u8bf4\u5404\u5be8\u4e4b\u4e3b\uff0c\u591a\u6709\u7eb7\u4e89\uff0c\u8fd9\u5f88\u4e0d\u597d\u3002\u4f9d\u6211\u4e4b\u610f\uff0c\u86c7\u65e0\u5934\u800c\u4e0d \u884c\uff0c\u56e0\u6b64\u8bf7\u5404\u4f4d\u82f1\u96c4\u5230\u6b64\uff0c\u5171\u63a8\u4e00\u4f4d\u2018\u5927\u9f99\u5934\u2019\uff0c\u54b1\u4eec\u90fd\u542c\u4ed6\u7684\u53f7\u4ee4\uff0c\u4e00\u6765\u662f\u4ece\u6b64\u53ef\u907f\u514d\u7eb7 \u4e89\uff0c\u4e8c\u6765\u4e0d\u6015\u5b98\u519b\u5404\u4e2a\u51fb\u7834\uff0c\u4e09\u6765\u662f\u5f53\u4eca\u4e4b\u52bf\uff0c\u74e6\u523a\u5916\u6270\u53c8\u672a\u9664\uff0c\u5c1a\u4e3a\u9690\u60a3\uff0c\u4e1c\u5357\u502d\u5bc7\u53c8 \u8d77\uff0c\u800c\u4e1c\u5317\u7684\u5973\u771f\u65cf\u4ea6\u8822\u8822\u6b32\u52a8\uff0c\u610f\u56fe\u5185\u4fb5\uff0c\u54b1\u4eec\u6709\u4e86\u9f99\u5934\uff0c\u82e5\u4e07\u4e00\u6709\u5916\u7978\u5165\u4fb5\uff0c\u4ea6\u53ef\u5404\u81ea \u4fdd\u5883\u3002\u4e0d\u77e5\u8bf8\u4f4d\u610f\u4e0b\u5982\u4f55\uff1f\u201d\u5728\u5ea7\u7684\u5341\u4e4b\u4e03\u516b\u662f\u7eff\u6797\u4e2d\u4eba\uff0c\u4f46\u4ea6\u6709\u4ece\u4e8b\u6b63\u5f53\u8425\u751f\u7684\u6b66\u6797\u4eba \u7269\uff0c\u751a\u81f3\u8fd8\u6709\u51e0\u4e2a\u6210\u540d\u7684\u6355\u5934\u5728\u5185\uff0c\u542c\u4e86\u4e4b\u540e\uff0c\u6709\u4eba\u53eb\u597d\uff0c\u6709\u4eba\u4ea4\u5934\u63a5\u8033\u5730\u8bae\u8bba\uff0c\u6709\u4eba\u6c89\u541f \u4e0d\u8bed\u3002\u6b66\u632f\u4e1c\u53cc\u76ee\u73af\u626b\u5168\u573a\uff0c\u53cc\u624b\u4e00\u6309\uff0c\u5c06\u5608\u5608\u6742\u6742\u7684\u58f0\u97f3\u6309\u4e86\u4e0b\u53bb\uff0c\u53c8\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u6b21 \u63a8\u4e3e\u9f99\u5934\uff0c\u867d\u7136\u662f\u4ee5\u7eff\u6797\u8c6a\u6770\u52a0\u76df\u4e3a\u4e3b\uff0c\u5176\u4ed6\u767d\u9053\u4e0a\u7684\u670b\u53cb\uff0c\u5404\u968f\u5176\u610f\u52a0\u76df\u4e4b\u540e\uff0c\u5927\u9f99\u5934\u4ea6 \u7edd\u4e0d\u5f3a\u8feb\u4ed6\u4eba\u4f19\uff0c\u53ea\u662f\u518d\u4e0d\u8bb8\u4e0e\u7eff\u6797\u4e2d\u7684\u8c6a\u6770\u4e3a\u96be\uff0c\u4e95\u6c34\u4e0d\u72af\u4f55\u6c34\uff0c\u6709\u4e8b\u90fd\u53ef\u4e0e\u5927\u9f99\u5934\u5546 \u91cf\uff0c\u7edd\u4e0d\u8ba9\u54ea\u65b9\u5403\u4e86\u4e8f\u3002\u201d\u90a3\u51e0\u4e2a\u6210\u540d\u7684\u6355\u5934\u542c\u4e86\uff0c\u5fc3\u4e2d\u6697\u601d\uff0c\u82e5\u7136\u5982\u6b64\uff0c\u5012\u4e5f\u4e0d\u9519\u3002\u82e5\u6709 \u4e86\u975e\u8ffd\u56de\u4e0d\u53ef\u7684\u8d43\u7269\uff0c\u8fd9\u5c31\u4e0d\u5fc5\u81ea\u5df1\u5356\u547d\u4e86\u3002\u8981\u77e5\u6210\u540d\u7684\u6355\u5934\uff0c\u672c\u8eab\u56fa\u7136\u5f97\u6709\u60ca\u4eba\u7684\u6280\u4e1a\uff0c \u4f46\u591a\u534a\u4ea6\u8981\u4e0e\u7eff\u6797\u4e2d\u9876\u5c16\u513f\u7684\u4eba\u7269\u6709\u4ea4\u60c5\uff0c\u8fd9\u624d\u80fd\u5728\u4e0d\u53ef\u8f6c\u56fe\u4e4b\u65f6\uff0c\u5957\u4e2a\u9762\u5b50\u3002\u4f9d\u6b66\u632f\u4e1c\u4e4b \u8a00\uff0c\u4e3e\u4e86\u201c\u5927\u9f99\u5934\u201d\u4e4b\u540e\uff0c\u5373\u662f\u5317\u4e94\u7701\u7684\u7eff\u6797\uff0c\u6709\u4eba\u603b\u8d1f\u5176\u8d23\uff0c\u5bf9\u6355\u5934\u4ea6\u6709\u5229\u4fbf\u4e4b\u5904\uff0c\u56e0\u6b64 \u7acb\u523b\u540c\u58f0\u53eb\u597d\uff0c\u518d\u5143\u5f02\u8bae\u3002 \u3000\u3000\u5f53\u4e0b\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u5927\u9f99\u5934\u81ea\u7136\u662f\u6b66\u8001\u5e84\u4e3b\u5f53\u4ec1\u4e0d\u8ba9\u4e86\u3002\u201d\u6b66\u632f\u4e1c\u62c8\u987b\u7b11\u9053\uff1a\u201c\u8001\u673d\u4e8c \u5341\u5e74\u524d\u5df2\u95ed\u95e8\u5c01\u5200\uff0c\u54ea\u8fd8\u6709\u96c4\u5fc3\u58ee\u5fd7\u3002\u8001\u673d\u5fc3\u76ee\u4e2d\u5012\u6709\u4e00\u4eba\uff0c\u8db3\u4ee5\u80dc\u4efb\uff0c\u6bd5\u8001\u5f1f\uff0c\u8bf7\u51fa\u6765\u4e0e \u5404\u8def\u82f1\u96c4\u76f8\u89c1\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u5168\u573a\u8f70\u52a8\u3002 \u3000\u3000\u5404\u8def\u82f1\u96c4\u4e0d\u7ea6\u800c\u540c\u5730\u90fd\u8e1e\u8d77\u811a\u6765\uff0c\u4f38\u957f\u9888\u5b50\uff0c\u8981\u770b\u8fd9\u4f4d\u7eff\u6797\u4e2d\u7684\u8001\u82f1\u96c4\uff0c\u5c0f\u91d1\u9f99\u6b66\u632f\u4e1c \u4fdd\u4e3e\u7684\u662f\u4f55\u7b49\u6837\u4eba\u7269\u3002\u4f46\u89c1\u5728\u6b66\u632f\u4e1c\u8eab\u8fb9\uff0c\u4e00\u4e2a\u8eab\u6750\u9b41\u4f1f\u7684\u6c49\u5b50\uff0c\u5e94\u58f0\u800c\u8d77\uff0c\u6d53\u7709\u5927\u773c\uff0c\u77ed \u9b13\u5982\u671d\uff0c\u5e74\u7eaa\u4f3c\u4e4e\u8fd8\u672a\u5230\u4e09\u5341\u5c81\uff0c\u53cc\u773c\u95ea\u95ea\u6709\u5149\u3002\u5728\u573a\u4e4b\u4eba\uff0c\u8fc7\u534a\u6570\u90fd\u6014\u4e86\u4e00\u6014\uff0c\u6b64\u4eba\u662f \u8c01\uff1f\u600e\u4e48\u4ece\u672a\u542c\u8fc7\uff1f\u6a0a\u82f1\u5374\u662f\u5403\u60ca\u4e0d\u5c0f\uff0c\u770b\u8fd9\u4eba\u7684\u8eab\u6750\u795e\u6001\uff0c\u4e0d\u662f\u90a3\u8499\u9762\u5927\u76d7\u8fd8\u662f\u8c01\u4eba\uff1f \u3000\u3000\u53ea\u542c\u5f97\u6b66\u632f\u4e1c\u8bf4\u9053\uff1a\u201c\u6bd5\u8001\u5f1f\u867d\u7136\u5728\u7eff\u6797\u9053\u4e0a\u4e0d\u5230\u4e24\u5e74\uff0c\u4f46\u5df2\u58f0\u540d\u5927\u9707\uff0c\u5e72\u4e0b\u8bb8\u591a\u60ca\u5929 \u52a8\u5730\u4e4b\u4e8b\u3002\u4ed6\u66fe\u68d2\u6253\u6ca5\u6cb3\u4e09\u9f99\uff0c\u72ec\u81ea\u6740\u8d25\u97e9\u5e84\u4e8c\u864e\uff0c\u4e00\u624b\u63a5\u4e86\u632f\u5a01\u9556\u5c40\u603b\u9556\u5934\u7684\u5341\u4e8c\u628a\u98de\u5200 \u6697\u5668\uff0c\u52ab\u4e86\u6210\u4eb2\u738b\u7684\u4e8c\u5341\u4e07\u73e0\u5ba4\u3002\u4e0d\u8fc7\u8fd9\u4f4d\u8001\u5f1f\u4e0d\u6b22\u559c\u9732\u9762\uff0c\u516c\u95e8\u4e2d\u4eba\u95fb\u540d\u4e27\u80c6\u7684\u8499\u9762\u5927\u76d7 \u5c31\u662f\u4ed6\uff01\u201d\u4f17\u4eba\u8f70\u7136\u5927\u53eb\uff1a\u201c\u5c31\u662f\u4ed6\uff0c\u5c31\u662f\u4ed6\uff01\u201d\u6562\u60c5\u7eff\u6797\u4e2d\u4eba\uff0c\u89c1\u8fc7\u4ed6\u771f\u9762\u76ee\u7684\u4ea6\u4e3a\u6570\u751a \u5c11\u3002\u6b66\u632f\u4e1c\u53c8\u9053\uff1a\u201c\u6700\u8fd1\u4ed6\u53c8\u5e72\u4e86\u4e24\u6869\u60ca\u4eba\u7684\u4e8b\u4ef6\uff0c\u4e00\u4ef6\u662f\u52ab\u4e86\u6e56\u5317\u89e3\u4eac\u7684\u4e09\u5341\u4e07\u4e24\u9556\u8fd0\uff0c \u5f04\u5f97\u90a3\u4f4d\u8d2a\u5bcc\u8d35\u7684\u6b66\u6797\u8d25\u7c7b\u8d2f\u5c45\uff0c\u73b0\u5728\u8981\u4e0b\u4e0d\u4e86\u53f0\uff01\u201d\u6a0a\u82f1\u5fc3\u5934\u4e00\u9707\uff0c\u6b64\u4e8b\u8bf4\u7684\u6b63\u5c31\u662f\u4ed6\u8fd9 \u4e00\u6869\uff0c\u6b66\u632f\u4e1c\u9a82\u7684\u90a3\u4f4d\u201c\u8d2a\u56fe\u5bcc\u8d35\u7684\u6b66\u6797\u8d25\u7c7b\u8d2f\u5c45\u201d\uff0c\u6b63\u5c31\u662f\u73b0\u5c45\u76d0\u8fd0\u4f7f\u4e4b\u804c\u7684\u4ed6\u7684\u4e49\u5f1f\uff0c \u6b66\u632f\u4e1c\u867d\u6ca1\u6307\u540d\u9a82\u4ed6\uff0c\u6a0a\u82f1\u4e5f\u89c9\u9762\u4e0a\u70ed\u8fa3\u8fa3\u7684\u597d\u4e0d\u60ed\u6127\u3002 \u3000\u3000\u6b66\u632f\u4e1c\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u7b2c\u4e8c\u4ef6\u4e8b\uff0c\u66f4\u662f\u60ca\u5929\u52a8\u5730\uff0c\u4e8e\u8c26\u7cbe\u5fe0\u4e3a\u56fd\uff0c\u60e8\u906d\u6740\u622e\uff0c \u5929\u4e0b\u4e49\u58eb\uff0c\u65e0\u4e0d\u6c14\u6124\u3002\u6211\u4eec\u7684\u6bd5\u8001\u5f1f\u4e3a\u6b64\u5927\u95f9\u4eac\u5e08\uff0c\u8fde\u65a9\u5927\u5185\u536b\u571f\u4e03\u540d\uff0c\u5c06\u4e8e\u8c26\u7684\u516d\u9633\u9b41\u9996 \u4e5f\u76d7\u4e86\u6765\uff0c\u867d\u7136\u6551\u4e0d\u4e86\u4e8e\u8c26\u4e4b\u547d\uff0c\u597d\u574f\u4e5f\u6559\u4ed6\u80fd\u591f\u5168\u5c38\u800c\u846c\uff0c\u53ea\u6b64\u4e00\u4e8b\uff0c\u5c31\u8db3\u53ef\u4ee5\u505a\u6211\u4eec\u5317 \u4e94\u7701\u5927\u9f99\u5934\uff01\u201d\u6a0a\u82f1\u5077\u773c\u4e00\u77a5\uff0c\u53ea\u89c1\u767d\u8863\u5c11\u5e74\u9762\u4e0a\u53d8\u8272\uff0c\u624b\u6478\u5251\u67c4\uff0c\u6a0a\u82f1\u5fd9\u9053\uff1a\u201c\u8d24\u5f1f\u522b \u5fd9\uff0c\u4e14\u770b\u4ed6\u600e\u4e48\u8bf4\uff1f\u201d\u540c\u5e2d\u4e4b\u4eba\uff0c\u90fd\u5728\u542c\u6b66\u632f\u4e1c\u7684\u8bdd\uff0c\u559d\u5f69\u58f0\u54cd\u6210\u4e00\u7247\uff0c\u8c01\u4e5f\u6ca1\u7559\u610f\u6a0a\u82f1\u548c \u90a3\u767d\u8863\u5c11\u5e74\uff0c\u90a3\u767d\u8863\u5c11\u5e74\u653e\u677e\u4e86\u624b\uff0c\u7aef\u5750\u5e2d\u4e0a\uff0c\u76ee\u4e0d\u8f6c\u775b\u5730\u76ef\u7740\u90a3\u59d3\u6bd5\u7684\u4eba\uff0c\u9762\u8272\u51dd\u91cd\u4e4b \u6781\uff0c\u5e73\u65e5\u90a3\u8138\u4e0a\u603b\u662f\u6d41\u9732\u7740\u7684\u90a3\u80a1\u5b69\u5b50\u6c14\uff0c\u5df2\u4e1d\u6beb\u4e0d\u89c1\u3002\u6a0a\u82f1\u4e0d\u7531\u5f97\u5fc3\u5934\u4e00\u9707\uff0c\u770b\u8fd9\u767d\u8863\u5c11 \u5e74\u6570\u65e5\u6765\u7684\u795e\u60c5\uff0c\u53c8\u60f3\u8d77\u4ed6\u5728\u4eac\u57ce\u5077\u5934\u4e4b\u65f6\uff0c\u5306\u5306\u800c\u6765\uff0c\u5306\u5306\u800c\u53bb\u7684\u4e8b\u60c5\uff0c\u8fd9\u5c11\u5e74\u662f\u5341\u5206\u6025 \u4e8e\u8981\u89c5\u56de\u4e8e\u8c26\u7684\u5934\u9885\uff0c\u770b\u6765\u4ed6\u4e4b\u8981\u627e\u201c\u8499\u9762\u5927\u76d7\u201d\uff0c\u6562\u60c5\u5c31\u662f\u56e0\u4e3a\u4ed6\u4e0d\u77e5\u9053\u8499\u9762\u5927\u76d7\u5077\u5934\u7684 \u7528\u610f\u5982\u4f55\uff1f\u8fd9\u5c11\u5e74\u548c\u4e8e\u8c26\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u6a0a\u82f1\u5bf9\u8fd9\u5c11\u5e74\u7684\u8eab\u4e16\u4e4b\u8c1c\uff0c\u66f4\u662f\u731c\u4e0d\u900f\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5f97\u6b66\u632f\u4e1c\u53c8\u9053\uff1a\u201c\u8fd9\u4f4d\u6bd5\u8001\u5f1f\u867d\u7136\u5728\u7eff\u6797\u672a\u4e45\uff0c\u4f46\u5374\u4e5f\u4e0d\u662f\u6ca1\u6709\u6765\u5386\u4e4b\u4eba\uff0c\u4ed6\u7684\u7236 \u4eb2\uff0c\u60f3\u5728\u573a\u4e4b\u4eba\u8c01\u90fd\u542c\u8fc7\u3002\u201d\u4f17\u4eba\u7eb7\u7eb7\u53eb\u9053\uff1a\u201c\u8c01\uff1f\u201d\u201c\u8c01\uff1f\u201d\u6b66\u632f\u4e1c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4ed6\u7684\u7236 \u4eb2\u5c31\u662f\u4e09\u5341\u5e74\u524d\u5df2\u7ecf\u540d\u9a70\u6c5f\u6e56\u7684\u9707\u4e09\u754c\u6bd5\u9053\u51e1\uff01\u800c\u4eca\u4ed6\u7ee7\u627f\u4e86\u4ed6\u7684\u7236\u4eb2\u662f\u897f\u5317\u4e10\u5e2e\u7684\u5c11\u5e2e \u4e3b\uff0c\u53c8\u662f\u96c1\u95e8\u5173\u5916\u91d1\u5200\u5c11\u5be8\u4e3b\u5468\u5c71\u6c11\u7684\u4e49\u5f1f\uff0c\u4ed6\u7684\u540d\u5b57\uff0c\u53eb\u505a\u6bd5\u64ce\u5929\uff01\u201d\u542c\u5230\u6b64\u5904\uff0c\u53ea\u89c1\u767d \u8863\u5c11\u5e74\u773c\u775b\u95ea\u4e86\u4e24\u95ea\uff0c\u9762\u6709\u5f02\u8272\u3002\u6b63\u662f\uff1a \u3000\u3000\u6570\u5ea6\u76f8\u9022\u672a\u8bc6\u8346\uff0c\u800c\u4eca\u4e4d\u542c\u6697\u5fc3\u60ca\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e94\u56de\u3000\u58ee\u5fd7\u51cc\u4e91\u3000\u68d2\u60ca\u540d\u5251\u5ba2\u3000\u5984\u8a00\u60f9\u6012\u3000\u5251\u523a\u5927\u9f99\u5934 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e94\u56de\u3000\u58ee\u5fd7\u51cc\u4e91\u3000\u68d2\u60ca\u540d\u5251\u5ba2\u3000\u5984\u8a00\u60f9\u6012\u3000\u5251\u523a\u5927\u9f99\u5934 \u3000\u3000\u6a0a\u82f1\u89c1\u8fd9\u60c5\u5f62\uff0c\u66f4\u589e\u7591\u60d1\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u4ed6\u5417\uff1f\u201d\u767d\u8863\u5c11\u5e74\u597d\u50cf\u5168\u526f\u7cbe\u795e\u90fd\u5728\u6ce8 \u89c6\u90a3\u4e2a\u6bd5\u64ce\u5929\uff0c\u5fc3\u4e0d\u5728\u7109\u5730\u7b54\u975e\u6240\u95ee\u9053\uff1a\u201c\u55ef\uff0c\u539f\u6765\u4ed6\u662f\u9707\u4e09\u754c\u513f\u5b50\uff0c\u600e\u4e48\u4ed6\u4e0d\u505a\u548c\u5c1a\uff0c\u5374 \u8981\u5f53\u4ec0\u4e48\u5927\u9f99\u5934\u5462\uff1f\u201d\u9707\u4e09\u754c\u6bd5\u9053\u51e1\u7684\u5bb6\u4f20\u89c4\u77e9\uff0c\u51e1\u662f\u7537\u4e01\uff0c\u5728\u6210\u5e74\u4e4b\u540e\uff0c\u5fc5\u8981\u5148\u5f53\u5341\u5e74\u53eb \u5316\uff0c\u518d\u5f53\u5341\u5e74\u548c\u5c1a\uff0c\u7136\u540e\u624d\u80fd\u84c4\u53d1\u8fd8\u4fd7\uff0c\u5a36\u59bb\u751f\u5b50\uff0c\u6bd5\u64ce\u5929\u770b\u6765\u672a\u5230\u4e09\u5341\u5c81\uff0c\u82e5\u662f\u6309\u7167\u4ed6\u7684 \u201c\u5bb6\u89c4\u201d\uff0c\u73b0\u5728\u8fd8\u6b63\u8be5\u662f\u5f53\u548c\u5c1a\u7684\u671f\u95f4\u3002\u6a0a\u82f1\u5927\u5947\uff1a\u8fd9\u767d\u8863\u5c11\u5e74\u6070\u50cf\u662f\u521d\u51fa\u9053\u7684\u96cf\u513f\uff0c\u5bf9\u6c5f \u6e56\u4e4b\u4e8b\uff0c\u4e00\u7a8d\u4e0d\u901a\uff0c\u5374\u53c8\u504f\u8bc6\u5f97\u8bb8\u591a\u6210\u540d\u4eba\u7269\u7684\u6765\u5386\uff1f \u3000\u3000\u9707\u4e09\u754c\u6bd5\u9053\u51e1\u867d\u5df2\u901d\u4e16\u591a\u5e74\uff0c\u82f1\u540d\u72b9\u5728\uff0c\u6b66\u5e84\u4e3b\u70b9\u51fa\u4e86\u6bd5\u64ce\u5929\u7684\u5bb6\u53f2\u6765\u5386\u4e4b\u540e\uff0c\u56db\u5ea7\u7eb7 \u7eb7\u8c08\u8bba\uff0c\u5bf9\u9707\u4e09\u754c\u90a3\u662f\u4eba\u4eba\u4f69\u670d\uff0c\u4f46\u5bf9\u4ed6\u7684\u513f\u5b50\uff0c\u867d\u8bf4\u662f\u5e72\u4e86\u8bb8\u591a\u60ca\u4eba\u7684\u4e8b\u4e1a\uff0c\u5374\u5230\u5e95\u662f\u8fd9 \u4e24\u5e74\u624d\u5728\u7eff\u6797\u201c\u7acb\u579b\u201d\u7684\u540e\u8f88\uff0c\u6709\u8bb8\u591a\u4eba\u5c31\u4e0d\u7518\u5fc3\u4e86\u3002\u6a0a\u82f1\u60f3\u9053\uff1a\u201c\u7eff\u6797\u4e2d\u4eba\u4eba\u4e3a\u5c0a\uff0c\u4e0d\u8f7b \u6613\u670d\u4eba\uff0c\u770b\u6765\u8fd9\u6bd5\u64ce\u5929\u65e0\u975e\u5f97\u6296\u9732\u4e00\u70b9\u672c\u4e8b\u4e0d\u53ef\u3002\u201d \u3000\u3000\u53ea\u89c1\u7531\u64ce\u5929\u53cc\u76ee\u4e00\u5f20\uff0c\u73af\u626b\u5168\u573a\uff0c\u5251\u7709\u864e\u76ee\uff0c\u987e\u76fc\u751f\u5a01\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5f53\u4eca\u5929\u4e0b\uff0c\u4e71\u8c61 \u5df2\u840c\uff0c\u81ea\u4e3b\u4e91\u82f1\u96c4\u51fa\u4e8e\u8349\u83bd\uff0c\u8089\u98df\u591a\u4e3a\u9119\u592b\uff0c\u82e5\u8981\u6307\u671b\u671d\u5ef7\u5b89\u90a6\u8d70\u56fd\uff0c\u53ea\u6050\u6709\u4fdf\u6cb3\u4e4b\u6e05\u3002\u56e0 \u6b64\u6b66\u8001\u5e84\u4e3b\u4e4b\u8a00\uff0c\u8981\u63a8\u4e3e\u4e00\u4f4d\u9886\u8896\u7eff\u6797\u7684\u9f99\u5934\uff0c\u90a3\u786e\u662f\u4e8b\u4e0d\u5bb9\u7f13\u3002\u4f46\u8bf4\u5230\u8981\u5728\u4e0b\u62c5\u5f53\uff0c\u5374\u662f \u60f9\u4eba\u7b11\u8bdd\uff0c\u60f3\u5ea7\u4e2d\u591a\u5c11\u82f1\u8c6a\uff0c\u51e0\u65f6\u8f6e\u5230\u5728\u4e0b\u3002\u201d\u8fd9\u8bdd\u8bf4\u6765\u4f3c\u662f\u8c26\u865a\uff0c\u4f46\u90a3\u53e3\u6c14\uff0c\u5374\u662f\u8c01\u90fd\u542c \u5f97\u51fa\u6765\uff0c\u6bd5\u64ce\u5929\u5fc3\u4e2d\u7684\u5927\u9f99\u5934\u4e0e\u6b66\u632f\u4e50\u6240\u8bf4\u7684\u53c8\u4e0d\u5c3d\u76f8\u540c\uff0c\u90a3\u7b80\u76f4\u662f\u9690\u9690\u4ee5\u5929\u4e0b\u4e3a\u5df2\u4efb\u4e86\u3002 \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u573a\u4e2d\u66f4\u662f\u8f70\u52a8\uff0c\u6b66\u632f\u4e1c\u53eb\u9053\uff1a\u201c\u6bd5\u8001\u5f1f\u4f55\u5fc5\u8c26\u8ba9\uff1f\u201d\u524d\u9762\u90a3\u90a3\u51e0\u5e2d\u7684\u4e00\u5927\u7fa4 \u4eba\u4e5f\u7eb7\u5429\u53eb\u9053\uff1a\u201c\u81ea\u53e4\u9053\u82f1\u96c4\u51fa\u5c11\u5e74\uff0c\u8fd9\u9f99\u5934\u4e00\u804c\uff0c\u6b63\u8be5\u6bd5\u5be8\u4e3b\u62c5\u5f53\u3002\u201d\u201c\u6709\u8c01\u6562\u72ec\u529b\u52ab\u6e56 \u5317\u76d0\u8fd0\u4f7f\u7684\u9556\u94f6\uff1f\u66f4\u6709\u8c01\u6562\u5927\u95f9\u4eac\u5e08\uff0c\u9707\u60ca\u6d77\u5185\uff1f\u6b66\u5e84\u4e3b\u8bf4\u5f97\u5bf9\uff0c\u53ea\u51ed\u8fd9\u4e24\u4ef6\u4e8b\uff0c\u5c31\u8be5\u4ed6\u505a \u6211\u4eec\u7684\u9f99\u5934\u3002\u201d\u4e5f\u6709\u4eba\u53eb\u9053\uff1a\u201c\u9f99\u5934\u5927\u4f4d\uff0c\u5317\u540c\u5c0f\u53ef\uff0c\u6bd5\u5be8\u4e3b\u867d\u7136\u5e74\u5c11\u82f1\u96c4\uff0c\u5728\u7eff\u6797\u4f3c\u4e4e\u8d44 \u5386\u8fd8\u6d45\uff01\u201d\u53c8\u6709\u4eba\u53eb\u9053\uff1a\u201c\u8c01\u4e0d\u670d\u7684\u51b2\u7740\u6211\u6765\u3002\u201d\u770b\u6765\u90a3\u4e9b\u4eba\u662f\u6781\u529b\u63a8\u6234\u6bd5\u64ce\u5929\u7684\u4e2d\u575a\u4eba \u7269\u3002 \u3000\u3000\u5608\u6742\u4e2d\u5ffd\u6709\u4e00\u4eba\u8d8a\u4f17\u800c\u51fa\uff0c\u7b11\u563b\u563b\u5730\u9053\uff1a\u201c\u8c01\u505a\u9f99\u5934\u6211\u90fd\u9a6c\u9996\u662f\u77bb\uff0c\u4f46\u5c0f\u5f1f\u662f\u4e2a\u751f\u610f \u4eba\uff0c\u8981\u6211\u7518\u5fc3\u60c5\u613f\u5730\u505a\u4f19\u6c41\uff0c\u4e5f\u5f97\u8ba9\u6211\u5374\u9053\u4ed6\u6709\u591a\u5c11\u672c\u94b1\u3002\u201d\u6a0a\u82f1\u4e00\u77a7\uff0c\u6b63\u662f\u7684\u65e5\u5728\u6cf0\u5c71\u6240 \u9047\u7684\u90a3\u4e2a\u5546\u4eba\u6a21\u6837\u7684\u4eba\u3002\u8fd9\u4eba\u521a\u4e00\u8bf4\u5b8c\uff0c\u7acb\u523b\u6709\u4eba\u8df3\u4e0a\u524d\u9053\uff1a\u201c\u94b1\u8d22\u4e0d\u53ef\u9732\u773c\uff0c\u6709\u5927\u672c\u94b1\u7684 \u4eba\u5c82\u80af\u968f\u4fbf\u644a\u7ed9\u4f60\u77a7\uff1f\u4ffa\u82b1\u5b50\u7237\u8eab\u4e0a\u6709\u4e24\u4e2a\u4eba\u94dc\u94b1\uff0c\u591f\u4f60\u5403\u70e7\u997c\u62c2\u996d\uff0c\u4f60\u8981\u4e0d\u8981\u77a7\u3002\u201d\u8fd9\u4eba \u6b63\u662f\u6628\u65e5\u5927\u95f9\u5ba2\u5e97\u7684\u90a3\u4e2a\u53eb\u5316\u5b50\u4e4b\u4e00\uff0c\u6b64\u8a00\u4e00\u51fa\uff0c\u5168\u573a\u90fd\u7b11\u4e86\u8d77\u6765\uff0c\u5546\u4eba\u5927\u6012\uff0c\u53eb\u9053\uff1a\u201c\u597d \u5440\uff0c\u6709\u4e24\u4e2a\u5c0f\u94b1\u5c45\u7136\u4e5f\u6562\u8bf7\u5ba2\u4e86\uff1f\u201d\u7acb\u523b\u4eae\u51fa\u5175\u5203\u548c\u4ed6\u52a8\u624b\u3002 \u3000\u3000\u5546\u4eba\u7684\u5175\u5203\u51fa\u624b\uff0c\u5168\u573a\u4eba\u7b49\uff0c\u90fd\u89c9\u773c\u775b\u4e00\u4eae\uff0c\u53ea\u89c1\u91d1\u5149\u95ea\u95ea\uff0c\u539f\u6765\u4ed6\u4f7f\u7684\u7adf\u7136\u662f\u9ec4\u91d1\u6240 \u94f8\u7684\u4e00\u628a\u5927\u7b97\u76d8\uff0c\u6709\u4eba\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u600e\u4e48\u4ed6\u4e5f\u51fa\u6765\u4e86\uff1f\u201d\u3002\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u4ed6\u662f\u8c01\uff1f\u201d\u8bf4\u8bdd \u7684\u90a3\u4eba\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u5c0f\u54e5\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u602a\u4e0d\u5f97\u8ba4\u4e0d\u51fa\u4ed6\u3002\u4ed6\u4e5f\u50cf\u6b66\u5e84\u4e3b\u4e00\u6837\uff0c\u66fe\u7ecf\u662f\u4e2a\u72ec\u811a\u5927 \u76d7\uff0c\u505a\u4e86\u51e0\u7968\u5927\u7684\uff0c\u5374\u5ffd\u7136\u6d17\u624b\u4e0d\u5e72\uff0c\u62ff\u53bb\u7ecf\u5546\uff0c\u4ed6\u505a\u5f3a\u76d7\u4e0d\u9519\uff0c\u505a\u751f\u610f\u66f4\u4e0d\u9519\uff0c\u4e0d\u5230\u5341\u5e74 \u516b\u5e74\uff0c\u5c31\u8eab\u5bb6\u767e\u4e07\uff0c\u8fde\u77e5\u53bf\u77e5\u5e9c\u90fd\u5df4\u7ed3\u4ed6\uff0c\u77e5\u9053\u4ed6\u505a\u8fc7\u5f3a\u76d7\u7684\u672c\u6765\u5c31\u5c11\uff0c\u5982\u4eca\u66f4\u662f\u4ee4\u4eba\u90e8\u53eb \u4ed6\u505a\u94b1\u767e\u4e07\u3002\u6ca1\u4eba\u6562\u8bf4\u4ed6\u662f\u5f3a\u76d7\u4e86\u3002\u4ed6\u5440\uff0c\u4ed6\u53eb\u505a\u94b1\u901a\u6d77\u3002\u201d\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u662f\u5440\uff0c\u8fd9\u771f\u5947 \u602a\uff0c\u4ed6\u6709\u4e86\u90a3\u4e48\u591a\u94b1\uff0c\u5982\u4e0d\u5728\u5bb6\u7eb3\u798f\uff0c\u5230\u8fd9\u91cc\u4e89\u8fd9\u6666\u6c14\u4f5c\u751a\uff1f\u201d\u767d\u8863\u5c11\u5e74\u542c\u4e86\uff0c\u5bf9\u6a0a\u82f1\u5fae\u5fae \u4e00\u7b11\uff0c\u6a0a\u82f1\u5fc3\u4e2d\u60ed\u6127\uff0c\u51ed\u4ed6\u591a\u5e74\u6c5f\u6e56\u7684\u7ecf\u5386\uff0c\u5728\u6cf0\u5c71\u9876\u4e0a\u5bf9\u8fd9\u4e2a\u94b1\u901a\u6d77\uff0c\u7adf\u7136\u4e5f\u770b\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u8fd9\u53eb\u5316\u5b50\u53c8\u662f\u6dee\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u8fd9\u53eb\u5316\u5b50\u662f\u4e10\u5e2e\u7684\u526f\u5e2e\u4e3b\u6bd5\u613f\u7a77\uff0c\u662f\u6bd5 \u9053\u51e1\u7684\u758f\u5802\u4f84\u5b50\u3002\u201d\u767d\u8863\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u8fd9\u540d\u5b57\u5230\u6709\u7406\uff0c\u4e0d\u613f\u7a77\u5374\u504f\u504f\u7a77\u4e86\u3002\u201d\u53eb\u5316\u5b50\u4f7f\u7684\u662f \u4e00\u6839\u7af9\u68d2\uff0c\u6562\u60c5\u90a3\u662f\u884c\u4e5e\u4e4b\u65f6\uff0c\u6253\u72d7\u7528\u7684\u51fa\u5165\u5175\u5668\uff0c\u4e00\u4e2a\u8c6a\u534e\u4e4b\u6781\uff0c\u4e00\u4e2a\u5bd2\u9178\u4e4b\u6781\u3002\u76f8\u6620\u6210 \u8da3\u3002\u94b1\u901a\u6d77\u7684\u91d1\u7b97\u76d8\u5584\u80fd\u9501\u62ff\u5175\u5203\uff0c\u62db\u6fc0\u751a\u4e3a\u602a\u5f02\uff0c\u6bd5\u613f\u7a77\u7684\u7af9\u68d2\u4e5f\u4f7f\u5f97\u6e9c\u6ed1\u975e\u5e38\uff0c\u4e24\u4eba\u6597 \u4e86\u4e8c\u4e09\u5341\u56de\u5408\uff0c\u94b1\u901a\u6d77\u5411\u524d\u4e00\u7838\u4e00\u62c9\uff0c\u7b97\u76d8\u73e0\u5b50\u54d7\u5566\u5566\u4f5c\u54cd\uff0c\u6bd5\u613f\u7a77\u201c\u5478\u201d\u4e86\u4e00\u53e3\u9053\uff1a\u201c\u6709 \u51e0\u4e2a\u94b1\u81ed\u4e48\uff1f\u201d\u94b1\u901a\u6d77\u7684\u7b97\u76d8\u4e00\u7838\uff0c\u770b\u770b\u5c31\u8981\u628a\u6bd5\u613f\u7a77\u7684\u7af9\u68d2\u62c9\u51fa\u624b\u53bb\uff0c\u5374\u4e0d\u6599\u62f1\u613f\u7a77\u7a81\u7136 \u201c\u5478\u201d\u7684\u4e00\u53e3\u6d53\u75f0\uff0c\u94b1\u901a\u6d77\u505a\u60ef\u5bcc\u5546\uff0c\u4e0d\u6bd4\u6614\u65e5\u5728\u6c5f\u6e56\u884c\u8d70\u4e4b\u65f6\uff0c\u5de8\u79fd\u4e0d\u60e7\uff0c\u4e00\u89c1\u6d53\u75f0\u98de \u5230\uff0c\u751f\u6015\u88ab\u6e85\u53ca\u4e2d\u7531\u5f97\u8d76\u7d27\u628a\u7b97\u76d8\u64a4\u56de\uff0c\u98de\u8eab\u6025\u95ea\uff0c\u53ea\u542c\u5f97\u54e8\u7684\u4e00\u58f0\uff0c\u7af9\u68d2\u5728\u7b97\u76d8\u4e0a\u6253\u4e86\u4e00 \u4e0b\uff0c\u7b97\u76d8\u7684\u67f1\u5b50\u672c\u6765\u662f\u6df1\u5d4c\u5728\u8475\u91d1\u4e4b\u5185\uff0c\u88ab\u7af9\u68d2\u4e00\u6572\uff0c\u7adf\u7136\u9707\u52a8\u8d77\u6765\uff0c\u94b1\u901a\u6d77\u53cd\u624b\u4e00\u7838\uff0c\u6bd5 \u613f\u7a77\u201c\u5478\u201d\u7684\u53c8\u5b9a\u4e00\u53e3\u6d53\u75f0\uff0c\u5f85\u5f97\u94b1\u901a\u6d77\u95ea\u8eab\u8eb2\u907f\u4e4b\u65f6\uff0c\u4ed6\u53c8\u5728\u7b97\u76d8\u201c\u6687\u201d\u5730\u6572\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u548c\u6a0a\u82f1\u90fd\u5df2\u770b\u4e86\u51fa\u6765\uff0c\u8bba\u62db\u6570\u7684\u7cbe\u5947\uff0c\u90a3\u662f\u94b1\u901a\u6d77\u9ad8\u660e\u5f97\u591a\uff0c\u4e0d\u8fc7\u6bd5\u613f\u7a77\u7684\u6c14 \u529b\u8f83\u5927\uff0c\u800c\u4e14\u4ed6\u4e00\u5230\u7af9\u7b77\u5c06\u8981\u88ab\u593a\u4e4b\u9645\uff0c\u5c31\u6765\u90a3\u4e48\u4e00\u53e3\u6d53\u75f0\uff0c\u5f80\u5f80\u53cd\u8d25\u4e3a\u80dc\uff0c\u767d\u8863\u5c11\u5e74\u7b11 \u9053\uff1a\u201c\u8fd9\u5c82\u4e0d\u662f\u800d\u65e0\u8d56\u4e48\uff1f\u201d\u5148\u524d\u8bf4\u8bdd\u7684\u90a3\u4eba\u9053\uff1a\u201c\u5bf9\u4ed8\u94b1\u901a\u6d77\uff0c\u8fd9\u6837\u620f\u5f04\u4ed6\u4e00\u4e0b\u6b63\u662f\u75db \u5feb\u3002\u201d\u6a0a\u82f1\u542c\u5468\u56f4\u8bf4\u8bdd\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u5bf9\u94b1\u901a\u6d77\u5f88\u5c11\u597d\u611f\u3002 \u3000\u3000\u53c8\u6597\u4e86\u5341\u56de\u5408\uff0c\u6bd5\u613f\u7a77\u4ecd\u7136\u662f\u5982\u6b64\u8fd9\u822c\u5730\u800d\u8d56\uff0c\u94b1\u901a\u6d77\u8d8a\u6765\u8d8a\u6012\uff0c\u5f85\u5f97\u6bd5\u613f\u7a77\u53c8\u4f7f\u52b2\u5730 \u5728\u4ed6\u7b97\u76d8\u4e0a\u6572\u4e00\u8bb0\u65f6\uff0c\u4ed6\u5ffd\u7136\u628a\u7b97\u76d8\u4e00\u9707\uff0c\u4e5f\u4e0d\u77e5\u662f\u4f7f\u7684\u4ec0\u4e48\u624b\u6cd5\uff0c\u7adf\u6709\u4e24\u7c92\u7b97\u76d8\u73e0\u5b50\u98de\u4e86 \u8d77\u6765\uff0c\u4f59\u5149\u95ea\u95ea\uff0c\u6d41\u661f\u98de\u51fa\uff0c\u53ea\u542c\u5f97\u90a3\u53eb\u5316\u5b50\u54ce\u54df\u4e00\u58f0\uff0c\u53cc\u817f\u4e00\u5f2f\uff0c\u8dea\u5012\u5730\u4e0a\uff0c\u539f\u6765\u662f\u7ed9\u6253 \u4e2d\u4e86\u817f\u7a84\u7684\u7a74\u9053\uff0c\u94b1\u901a\u6d77\u51b7\u7b11\u9053\uff1a\u201c\u6c42\u9976\u4e86\u5427\uff1f\u201d\u4e00\u811a\u8e39\u4e0b\uff0c\u60f3\u628a\u6bd5\u613f\u7a77\u518d\u8e22\u4e00\u4e2a\u7b4b\u6597\uff0c\u7136 \u540e\u624d\u597d\u53d6\u56de\u90a3\u4e24\u7c92\u91d1\u73e0\u3002 \u3000\u3000\u6bd5\u613f\u7a77\u5ffd\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u5de6\u624b\u628a\u90a3\u4e24\u7c92\u91d1\u73e0\u5b50\u629b\u4e0a\u629b\u4e0b\uff0c\u53f3\u624b\u6491\u7740\u7af9\u6746\uff0c\u4e00\u8df3\u4e00\u62d0\u7684\uff0c\u500f \u5ffd\u5c31\u94bb\u8fdb\u5165\u5806\u4e4b\u4e2d\uff0c\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u4e16\u4e0a\u591a\u5c11\u4eba\u89c1\u94b1\u5c31\u62dc\uff0c\u6211\u770b\u91d1\u5b50\u7684\u9762\u4e0a\u4e5f\u8dea\u4f60\u4e00\u8dea\uff0c\u90a3 \u8fd8\u662f\u6211\u6709\u4fbf\u5b9c\u3002\u201d\u4f17\u4eba\u89c1\u4ed6\u5206\u660e\u88ab\u6253\u4e86\u7a74\u9053\u5c45\u7136\u8fd8\u80fd\u7eb5\u8dc3\uff0c\u65e0\u4e0d\u79f0\u5947\u3002 \u3000\u3000\u53ea\u89c1\u573a\u4e2d\u4eba\u5f71\u4e00\u95ea\uff0c\u4e00\u4e2a\u9ec4\u888d\u9053\u58eb\u500f\u5730\u4ece\u5e2d\u4e2d\u8dc3\u8d77\uff0c\u8df3\u8fdb\u573a\u5fc3\uff0c\u8eab\u6cd5\u4e4b\u5feb\u6377\u5229\u843d\uff0c\u6bd4\u90a3 \u53eb\u5316\u5b50\u66f4\u80dc\u51e0\u5206\u3002\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u6a0a\u5927\u54e5\uff0c\u4f60\u770b\uff0c\u6628\u65e5\u5728\u5ba2\u5e97\u6295\u5bbf\u7684\u8fd9\u4e2a\u83ab\u9053\u58eb\u539f\u4e5f\u662f\u80fd \u4eba\u3002\u201d \u3000\u3000\u94b1\u901a\u6d77\u5fc3\u4e2d\u4e00\u51db\uff0c\u91d1\u7b97\u76d8\u5f53\u524d\u4e00\u7acb\uff0c\u672a\u53d1\u62db\u5148\u9632\u654c\uff0c\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u7384\u745b\u9053\u957f\uff0c\u6765\u51d1\u70ed\u95f9 \u4e48\uff1f\u201d\u94b1\u901a\u6d77\u53eb\u51fa\u8fd9\u9053\u4eba\u7684\u540d\u5b57\uff0c\u5728\u573a\u4eba\u7b49\u9664\u4e86\u51e0\u4e2a\u8ba4\u8bc6\u7384\u745b\u9053\u4eba\u7684\u4e4b\u5916\uff0c\u4f59\u4f17\u90fd\u662f\u5927\u5403\u4e00 \u60ca\uff0c\u8fd9\u7384\u745b\u9053\u4eba\u662f\u5c71\u4e1c\u4e0a\u6e05\u89c2\u7684\u89c2\u4e3b\uff0c\u6b66\u529f\u636e\u8bf4\u6df1\u4e0d\u53ef\u6d4b\uff0c\u4f46\u65e0\u4eba\u89c1\u8fc7\uff0c\u4ed6\u5728\u5c71\u4e2d\u4e3b\u6301\u9053 \u89c2\uff0c\u6839\u672c\u672a\u66fe\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u8fc7\uff0c\u8bf4\u5f97\u4e0a\u662f\u4e2a\u8df3\u51fa\u7ea2\u5c18\u7684\u4e16\u5916\u9ad8\u4eba\uff0c\u5374\u4e0d\u6599\u4eca\u65e5\u4e5f\u6765\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u7384\u745b\u9053\u4eba\u4ef0\u5929\u4e00\u7b11\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8d2b\u9053\u662f\u5316\u7f18\u6765\u7684\u3002\u8fd9\u91cc\u7684\u4eba\u8981\u6570\u4f60\u8001\u54e5\u6700\u6709\u94b1\uff0c \u6ca1\u5948\u4f55\u53ea\u597d\u5411\u4f60\u5316\u7f18\u4e86\u3002\u201d\u94b1\u901a\u6d77\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff0c\u9053\u957f\u8981\u94f6\u5b50\u7528\u4e48\uff1f\u201d\u7384\u745b\u9053\u4eba\u9053\uff1a \u201c\u4f60\u8001\u54e5\u51fa\u624b\u8c6a\u9614\uff0c\u94f6\u5b50\u6709\u4ec0\u4e48\u7a00\u5947\uff0c\u627e\u8981\u91d1\u5b50\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u56de\u5b9d\u53f7\u53bb\u53d6\u3002\u8fd9\u7b97\u76d8\u7684\u73e0\u5b50\u7ed9\u4e86 \u6211\u4fbf\u6210\u3002\u201d\u94b1\u901a\u6d77\u77e5\u4ed6\u5b58\u5fc3\u8f83\u91cf\uff0c\u51b7\u51b7\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u9053\u957f\u65e2\u8981\u5316\u7f18\uff0c\u90a3\u5c31\u81ea\u6765\u53d6\u5427\u3002\u201d\u91d1 \u7b97\u76d8\u626c\u7a7a\u4e00\u8361\uff0c\u73e0\u5b50\u4e0a\u4e0b\u8d70\u52a8\uff0c\u54d7\u5566\u5566\u4e00\u7247\u58f0\u54cd\u3002 \u3000\u3000\u7384\u745b\u9053\u4eba\u9053\uff1a\u201c\u597d\uff0c\u4f60\u65e2\u5982\u6b64\u6177\u6168\uff0c\u6211\u4e5f\u5c31\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d\u53d6\u4e0b\u4e00\u67c4\u4f5b\u5c18\uff0c\u8fce\u9762\u5c31\u662f\u4e00 \u62c2\uff0c\u94b1\u901a\u6d77\u628a\u7b97\u76d8\u7ffb\u4e86\u4e00\u7ffb\uff0c\u610f\u6b32\u5c06\u4ed6\u7684\u62c2\u5c18\u7ed5\u4e0a\u7b97\u76d8\u7684\u67f1\u5b50\uff0c\u7384\u745b\u4eba\u7b11\u9053\uff1a\u201c\u597d\u5475\uff0c\u591a\u8c22 \u4e86\uff01\u201d\u62c2\u5c18\u4e00\u7f29\uff0c\u94b1\u901a\u6d77\u53ea\u89c9\u864e\u53e3\u53d1\u70ed\uff0c\u773c\u524d\u91d1\u5149\u95ea\u95ea\uff0c\u5df2\u6709\u4e09\u7c92\u91d1\u73e0\u7ed9\u4ed6\u5377\u53bb\u3002 \u3000\u3000\u94b1\u901a\u6d77\u5929\u5403\u4e00\u60ca\uff0c\u8fd9\u7384\u745b\u9053\u4eba\u624b\u6cd5\u7684\u602a\u5f02\uff0c\u7adf\u662f\u5e73\u751f\u4ec5\u89c1\u3002\u5fc3\u865a\u80c6\u602f\uff0c\u4e0d\u6562\u8fdb\u62db\uff0c\u53ea\u662f \u7d27\u7d27\u5c01\u95ed\u95e8\u6237\uff0c\u94b1\u901a\u6d77\u5728\u8fd9\u7b97\u76d8\u4e0a\u4e0b\u8fc7\u51e0\u5341\u5e74\u529f\u592b\uff0c\u53ea\u5b88\u4e0d\u653b\uff0c\u5de6\u907f\u77f3\u95ea\uff0c\u9053\u4eba\u7684\u62c2\u5c18\u62c2\u4e0d \u5230\u5730\u7b97\u76d8\u67f1\u5b50\u4e2d\u95f4\uff0c\u94b1\u901a\u6d77\u5fc3\u4e2d\u7a0d\u5b9a\uff0c\u5ffd\u542c\u5f97\u7384\u745b\u9053\u4eba\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u51fa\u4e86\u51e0\u7c92\u91d1\u73e0\u5c31\u5fc3\u75db\u4e86 \u4e48\u4e86\u4e0d\u884c\uff0c\u4e0d\u884c\uff01\u201d\u5012\u8f6c\u62c2\u5c18\uff0c\u5411\u4ed6\u7709\u5c16\u4e00\u70b9\uff0c\u52bf\u5982\u95ea\u7535\uff0c\u8fd9\u7709\u5c16\u82e5\u7ed9\u4ed6\u70b9\u4e2d\uff0c\u53cc\u76ee\u7acb\u5373\u5931 \u660e\uff0c\u94b1\u901a\u6d77\u77e5\u9053\u5389\u5bb3\uff0c\u6025\u5fd9\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u7b97\u76d8\u659c\u8361\u3002\u5c82\u77e5\u9053\u8fd9\u4e00\u62db\u5374\u662f\u865a\u62db\uff0c\u6545\u610f \u903c\u5f97\u4ed6\u5c06\u7b97\u76d8\u8fce\u4e0a\uff0c\u53ea\u89c1\u4ed6\u62c2\u5c18\u4e00\u62c2\uff0c\u53c8\u662f\u4e24\u7c92\u91d1\u73e0\u98de\u51fa\uff0c\u4ed6\u5c55\u8896\u4e00\u63a5\uff0c\u91d1\u73e0\u6070\u6070\u843d\u5728\u4ed6\u7684 \u624b\u4e2d\u3002\u94b1\u901a\u6d77\u8981\u60f3\u7f62\u624b\uff0c\u65e0\u5948\u7ed9\u90a3\u9053\u4eba\u7f20\u7740\uff0c\u8131\u8eab\u4e0d\u5f97\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u53c8\u7ed9\u4ed6\u5377\u53bb\u4e86\u5341\u591a\u7c92\uff0c \u53ea\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u7384\u745b\u9053\u4eba\u4e0d\u4f4f\u53e3\u5730\u6570\u9053\uff1a\u201c\u4e00\u3001\u4e8c\u3001\u4e09\u3001\u56db\u3001\u2026\u2026\u201d\u4e0d\u591a \u4e00\u4f1a\uff0c\u5df2\u6570\u5230\u56db\u5341\u4e5d\uff0c\u7b97\u76d8\u6709\u5341\u4e09\u67f1\uff0c\u6bcf\u67f1\u4e03\u7c92\u73e0\u5b50\uff0c\u9664\u4e86\u88ab\u6bd5\u613f\u7a77\u6536\u53bb\u4e24\u7c92\u5916\uff0c\u5c1a\u4f59\u516b\u5341 \u4e5d\u7c92\uff0c\u5982\u4eca\u5374\u88ab\u8fd9\u9053\u4eba\u5728\u7247\u8346\u4e4b\u95f4\u53d6\u53bb\u56db\u5341\u4e5d\u7c92\u4e86\u3002 \u3000\u3000\u94b1\u901a\u6d77\u6c14\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u731b\u5730\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211\u4e0e\u4f60\u62fc\u4e86\uff01\u201d\u7b97\u76d8\u4e00\u63a8\uff0c\u7528\u529b\u4e00\u9707\uff0c\u5269\u4e0b \u7684\u90a3\u56db\u5341\u7c92\u9ec4\u91d1\u73e0\u5b50\u7eb7\u7eb7\u98de\u8d77\uff0c\u56db\u9762\u516b\u65b9\uff0c\u4e00\u9f50\u5411\u7384\u73bb\u9053\u4eba\u6fc0\u5c04\uff0c\u8fd9\u662f\u201c\u6ee1\u5929\u82b1\u94dc\u6d12\u91d1\u94b1\u201d \u7684\u624b\u6cd5\uff0c\u94b1\u901a\u6d77\u7adf\u7136\u80fd\u628a\u7b97\u76d8\u7684\u5341\u4e09\u67f1\u73e0\u5b50\u5982\u6b64\u8fd0\u7528\uff0c\u8fd9\u6697\u5668\u529f\u592b\u786e\u662f\u522b\u5f00\u751f\u9762\uff0c\u8bb8\u591a\u8ba8\u538c \u94b1\u901a\u6d77\u7684\u4eba\uff0c\u4e5f\u7981\u4e0d\u4f4f\u5927\u58f0\u559d\u5f69\uff01 \u3000\u3000\u4f46\u89c1\u7384\u745b\u9053\u4eba\u98de\u8eab\u4e00\u63a0\uff0c\u5e76\u4e0d\u9000\u907f\uff0c\u53cd\u800c\u5411\u7740\u91d1\u73e0\u8fce\u53bb\uff0c\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u94b1\u5927\u7237\uff0c\u5982\u6b64 \u6177\u6168\uff0c\u8d2b\u9053\u4e5f\u5c31\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d\u53cc\u8896\u9f50\u6325\uff0c\u4e00\u4f38\u4e00\u7f29\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u5c06\u6ee1\u7a7a\u4e71\u98de\u7684\u9ec4\u91d1\u73e0\u5b50\u5377\u5f97 \u5e72\u5e72\u51c0\u51c0\uff0c\u7adf\u65e0\u4e00\u7c92\u7559\u4e0b\uff0c\u94b1\u901a\u6d77\u9762\u8272\u7070\u767d\uff0c\u7ad9\u5728\u573a\u8fb9\uff0c\u63d0\u7740\u90a3\u628a\u6ca1\u6709\u73e0\u5b50\u7684\u7b97\u76d8\uff0c\u505a\u58f0\u4e0d \u5f97\uff01 \u3000\u3000\u7384\u745b\u9053\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u6b63\u5f85\u8bf4\u51e0\u53e5\u95e8\u9762\u8bf4\u8bdd\uff0c\u5728\u6ee1\u573a\u559d\u5f69\u58f0\u4e2d\uff0c\u5ffd\u542c\u5f97\u4e00\u4eba\u9634\u607b\u607b\u5730\u8bf4 \u9053\uff1a\u201c\u5982\u6b64\u5f3a\u5316\uff0c\u6559\u4eba\u8840\u672c\u65e0\u5f52\uff0c\u6211\u5c31\u770b\u4e0d\u8fc7\u773c\uff01\u201d\u58f0\u97f3\u4e0d\u9ad8\uff0c\u4f46\u5374\u523a\u8033\u975e\u5e38\uff0c\u6ee1\u573a\u559d\u5f69\u4e4b \u58f0\uff0c\u90fd\u538b\u5b83\u4e0d\u4f4f\uff0c\u7384\u745b\u9053\u4eba\u4e00\u6115\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4f46\u89c1\u4eba\u4e1b\u4e4b\u4e2d\uff0c\u7a81\u7136\u98de\u8d77\u4e00\u4e2a\u5e9e\u5927\u7684\u8eab \u8eaf\uff0c\u7adf\u4ece\u65e0\u6570\u4eba\u5934\u4e0a\u98de\u8fc7\uff0c\u500f\u5730\u843d\u5230\u8ddf\u7684\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u94b1\u8001\u5f1f\uff0c\u4f60\u522b\u8d70\uff0c\u6211\u7ed9\u4f60\u8ba8\u8fd8\u91d1 \u73e0\uff01\u201d \u3000\u3000\u53ea\u89c1\u90a3\u4eba\u62ab\u7740\u4e00\u4ef6\u72d0\u88d8\uff0c\u5934\u6234\u98ce\u5e3d\uff0c\u4fe8\u7136\u4e5f\u662f\u4e00\u4e2a\u767e\u4e07\u5bcc\u5546\u7684\u6253\u626e\uff0c\u6a0a\u82f1\u5fc3\u5934\u4e00\u9707\uff0c\u53ea \u542c\u5f97\u6b66\u632f\u4e1c\u5df2\u5148\u56b7\u51fa\u6765\u9053\uff1a\u201c\u9633\u5927\u54e5\uff0c\u600e\u4e48\u4f60\u4e5f\u6765\u4e86\uff1f\u8fd9\u4f4d\u7384\u745b\u9053\u957f\u662f\u597d\u670b\u53cb\uff01\u201d\u8fd9\u4eba\u975e \u4ed6\uff0c\u6b63\u662f\u66fe\u4e0e\u767d\u8863\u5c11\u5e74\u4ea4\u8fc7\u624b\u7684\u90a3\u4e2a\u9633\u5b97\u6d77\uff01\u4f46\u89c1\u767d\u8863\u5c11\u5e74\u4e5f\u5fae\u5fae\u4e00\u9707\uff0c\u624b\u6478\u5251\u67c4\uff0c\u4f46\u968f\u5373 \u53c8\u6ce8\u76ee\u6597\u573a\u3002 \u3000\u3000\u9633\u5b97\u6d77\u662f\u5f53\u65f6\u7684\u56db\u5927\u5251\u5ba2\u4e4b\u4e00\uff0c\u6c5f\u6e56\u4e0a\u8c01\u4eba\u4e0d\u77e5\uff0c\u4f46\u56e0\u4ed6\u5411\u5728\u56db\u5ddd\u4e91\u8d35\u4e00\u5e26\uff0c\u4e2d\u539f\u7684\u6b66 \u6797\u4eba\u7269\uff0c\u8ba4\u8bc6\u4ed6\u7684\u5374\u4e0d\u591a\uff0c\u8fd9\u65f6\u542c\u5f97\u6b66\u5e84\u4e3b\u56b7\u51fa\u4ed6\u7684\u540d\u5b57\uff0c\u90fd\u4e0d\u7981\u6115\u7136\u3002\u53ea\u542c\u5f97\u9633\u5b97\u6d77\u51b7\u51b7 \u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u597d\u670b\u53cb\uff0c\u94b1\u8001\u5f1f\u662f\u6b63\u7ecf\u7684\u751f\u610f\u4eba\uff0c\u6211\u53ea\u77e5\u9053\u8981\u66ff\u4ed6\u8ba8\u56de\u672c\u94b1\uff01\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5530 \u5730\u5c31\u662f\u4e00\u5251\uff01 \u3000\u3000\u7384\u745b\u9053\u4eba\u6599\u4e0d\u5230\u9633\u5b97\u6d77\u5982\u6b64\u4e0d\u7ed9\u9762\u5b50\uff0c\u8bf4\u52a8\u624b\u4fbf\u52a8\u624b\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u60f3\u9053\uff1a\u201c\u4f60\u867d\u662f\u95fb\u540d \u7684\u5927\u5251\u5ba2\uff0c\u96be\u9053\u6211\u5c31\u6015\u4f60\u4e86\uff1f\u201d\u62c2\u5c18\u4e00\u7ed5\uff0c\u8fce\u7740\u4ed6\u7684\u5251\u67c4\u4e00\u7f20\uff0c\u8fd9\u4e00\u62db\u540d\u53eb\u201c\u4e4c\u9f99\u7ed5\u67f1\u201d\uff0c \u662f\u7384\u745b\u9053\u4eba\u4e09\u5341\u516d\u624b\u5929\u7f61\u62c2\u5c18\u624b\u7684\u5f97\u610f\u62db\u6570\u4e4b\u4e00\uff0c\u5584\u80fd\u593a\u4eba\u5200\u5251\uff0c\u4e0d\u6599\u9633\u5b97\u6d77\u5251\u628a\u4e00\u7ffb\uff0c\u4f3c \u5de6\u4f3c\u77f3\uff0c\u98d8\u5ffd\u4e4b\u6781\uff0c\u7384\u745b\u9053\u4eba\u7a0d\u4e00\u8fdf\u7591\uff0c\u7a81\u89c1\u9752\u5149\u4e00\u95ea\uff0c\u5feb\u903e\u98d8\u98ce\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\u201c\uff0c\u7384\u745b \u9053\u4eba\u53f3\u8fb9\u7684\u8863\u8896\u5df2\u7ed9\u523a\u7a7f\uff0c\u85cf\u5728\u8896\u7ba1\u4e4b\u4e2d\u7684\u91d1\u73e0\u54d7\u5566\u5566\u5730\u64a4\u6ee1\u4e00\u5730\uff01 \u3000\u3000\u7384\u745b\u9053\u4eba\u5927\u6012\uff0c\u4f7f\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\u7684\u8eab\u6cd5\uff0c\u62a2\u5411\u9633\u5b97\u6d77\u7684\u53f3\u4fa7\u53d1\u62db\uff0c\u9633\u5b97\u6d77\u51b7\u7b11\u9053\uff1a \u201c\u725b\u9f3b\u5b50\u9053\u58eb\uff0c\u4f60\u62a2\u6765\u7684\u8d22\u7269\u4e5f\u820d\u4e0d\u5f97\u5417\uff1f\u201d\u53cd\u624b\u4e00\u5251\uff0c\u523a\u4ed6\u53f3\u80a9\u7684\u7435\u7436\u9aa8\uff0c\u7384\u73bb\u9053\u4eba\u5fae\u5fae \u4e00\u8ba9\uff0c\u62c2\u5c18\u8fce\u4e0a\uff0c\u54ea\u77e5\u9633\u5b97\u6d77\u7684\u5251\u6cd5\u7aef\u7684\u602a\u5f02\u975e\u5e38\uff0c\u5251\u5230\u4e2d\u9014\uff0c\u7a81\u7136\u4e00\u8f6c\uff0c\u53ea\u542c\u5f97\u201c\u5530\u5566\u201d \u4e00\u58f0\uff0c\u7384\u745b\u9053\u4eba\u5de6\u8fb9\u7684\u8863\u8896\u53c8\u7ed9\u4ed6\u523a\u7a7f\u4e86\u3002 \u3000\u3000\u54d7\u5566\u5566\u4e00\u7247\u58f0\u54cd\uff0c\u85cf\u5728\u7384\u745b\u9053\u4eba\u5de6\u8fb9\u8896\u7ba1\u4e2d\u7684\u91d1\u73e0\u53c8\u6492\u6ee1\u4e86\u4e00\u5730\uff0c\u5168\u573a\u4eba\u7b49\u65e0\u4e0d\u5403\u60ca\uff0c \u8981\u77e5\u7384\u745b\u9053\u4eba\u7684\u6b66\u529f\uff0c\u5728\u4ed6\u4eec\u773c\u4e2d\uff0c\u5df2\u7ecf\u7b97\u5f97\u662f\u7b2c\u4e00\u6d41\u4eba\u7269\uff0c\u54ea\u77e5\u78b0\u4e0a\u4e86\u9633\u5b97\u6d77\uff0c\u8fde\u63a5\u4e24 \u62db\uff0c\u5251\u65e0\u865a\u53d1\uff0c\u5c31\u628a\u4ed6\u7684\u4e24\u4e2a\u8896\u7ba1\u523a\u7a7f\u3002\u7384\u745b\u9053\u4eba\u6012\u6c14\u66f4\u589e\uff0c\u4f46\u5374\u529b\u6301\u9547\u5b9a\uff0c\u811a\u8e0f\u4e94\u884c\u516b\u5366 \u65b9\u4f4d\uff0c\u4e00\u67c4\u62c2\u5c18\uff0c\u4e0d\u4f4f\u5730\u906e\u62e6\u62db\u67b6\u3002\u672c\u6765\u7384\u745b\u9053\u4eba\u7684\u6b66\u529f\uff0c\u4e0e\u9633\u5b97\u6d77\u867d\u6709\u8ddd\u79bb\uff0c\u4f46\u5374\u4e0d\u81f3\u4e8e \u76f8\u5dee\u5f97\u5982\u6b64\u4e4b\u8fdc\uff0c\u53ea\u56e0\u4ed6\u53cc\u8896\u85cf\u6709\u91d1\u73e0\uff0c\u8df3\u8dc3\u4e0d\u4fbf\uff0c\u6545\u6b64\u4e00\u4ea4\u624b\u4fbf\u5403\u4e86\u5927\u4e8f\uff0c\u800c\u4eca\u6492\u4e86\u91d1 \u73e0\uff0c\u53cd\u800c\u80fd\u591f\u6709\u653b\u6709\u5b88\u4e86\u3002 \u3000\u3000\u7384\u745b\u9053\u4eba\u4e3a\u7740\u8981\u633d\u56de\u9762\u5b50\uff0c\u4e0d\u505c\u5730\u89d1\u7740\u673a\u4f1a\u8fdb\u88ad\uff0c\u9633\u5b97\u6d77\u5ffd\u5730\u559d\u58f0\u201c\u7740\u201d\uff0c\u5251\u5c16\u4e00\u6311\uff0c \u7384\u745b\u9053\u4eba\u6025\u5fd9\u8df3\u8d77\uff0c\u5fc3\u4e2d\u6b63\u81ea\u60ca\u8bb6\uff0c\u8fd9\u4e00\u5251\u7684\u6765\u52bf\uff0c\u5e76\u975e\u523a\u4ed6\u8981\u5bb3\uff0c\u5b9e\u662f\u751a\u6613\u8eb2\u907f\uff0c\u4f55\u4ee5\u4ed6 \u53e3\u51fa\u5927\u8a00\uff0c\u5148\u884c\u559d\u201c\u7740\u201d\uff0c\u5ffd\u89c1\u91d1\u5149\u4e00\u95ea\uff0c\u539f\u6765\u9633\u5b97\u6d77\u5df2\u6311\u8d77\u4e00\u7c92\u91d1\u73e0\uff0c\u5411\u94b1\u901a\u6d77\u63b7\u53bb\uff0c\u94b1 \u901a\u6d77\u7ad9\u5728\u573a\u8fb9\uff0c\u4f38\u624b\u4e00\u63a5\uff0c\u5c06\u91d1\u73e0\u88c5\u5165\u7b97\u76d8\uff0c\u9633\u5b97\u6d77\u8fd9\u4e24\u4e0b\u624b\u6cd5\uff0c\u5e72\u51c0\u5229\u843d\uff0c\u7adf\u80fd\u7528\u5251\u5c16\u7684 \u52b2\u529b\uff0c\u6311\u8d77\u5730\u4e0a\u6eda\u52a8\u7684\u91d1\u73e0\uff0c\u8fd9\u5185\u529b\u6536\u53d1\u81ea\u5982\uff0c\u786e\u662f\u96be\u80fd\u4e4b\u6781\uff01 \u3000\u3000\u573a\u4e2d\u54cd\u8d77\u4e00\u7247\u559d\u5f69\u58f0\uff0c\u9633\u5b97\u6d77\u8d8a\u53d1\u5356\u5f04\uff0c\u4f46\u89c1\u4ed6\u6325\u5251\u5982\u98ce\u8feb\u5f97\u7384\u745b\u9053\u4eba\u4e0d\u4f4f\u5730\u5de6\u907f\u53f3 \u95ea\uff0c\u800c\u4ed6\u5219\u6bcf\u53d1\u4e00\u62db\uff0c\u8d81\u7384\u73bb\u9053\u4eba\u4e00\u95ea\u4e4b\u65f6\uff0c\u4ed6\u5c31\u6311\u8d77\u4e00\u7c92\u91d1\u73e0\uff0c\u7384\u745b\u9053\u4eba\u867d\u7136\u660e\u77e5\u4ed6\u7684\u7528 \u610f\uff0c\u4f46\u5374\u65e0\u6cd5\u62e6\u6321\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u5c31\u5982\u521a\u624d\u7384\u745b\u9053\u4eba\u5377\u53bb\u94b1\u901a\u6d77\u7684\u91d1\u73e0\u4e00 \u6837\uff0c\u53ea\u662f\u5982\u4eca\u4e3b\u5ba2\u6613\u52bf\uff0c\u9633\u5b97\u6d77\u6311\u8d77\u4e00\u7c92\uff0c\u5c31\u9707\u5251\u5f39\u7ed9\u94b1\u901a\u6d77\uff0c\u4e00\u6536\u4e00\u53d1\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5730\u4e0a \u7684\u516b\u5341\u4e5d\u7c92\u91d1\u73e0\uff0c\u90fd\u7269\u5f52\u539f\u4e3b\uff0c\u5d4c\u5165\u4e86\u94b1\u901a\u6d77\u7684\u7b97\u76d8\u4e0a\uff0c\u7384\u745b\u9053\u4eba\u9762\u8272\u94c1\u9752\uff0c\u6536\u4e86\u62c2\u5c18\uff0c\u5954 \u5230\u6bd5\u64ce\u5929\u9762\u524d\uff0c\u7a3d\u9996\u8bf4\u9053\uff1a\u201c\u8d2b\u9053\u65e0\u80fd\uff0c\u53cd\u4e22\u4e86\u5be8\u4e3b\u7684\u9762\u5b50\uff0c\u8bf7\u6055\u6211\u5148\u8d70\u4e86\uff01\u201d\u4e0d\u542c\u6b66\u632f\u4e1c \u548c\u6bd5\u64ce\u5929\u7684\u62e6\u963b\uff0c\u8ff3\u81ea\u8d70\u4e86\u3002 \u3000\u3000\u7384\u745b\u9053\u4eba\u8fd9\u51e0\u53e5\u8bdd\uff0c\u81ea\u7136\u662f\u542b\u6709\u8981\u6bd5\u64ce\u5929\u66ff\u4ed6\u633d\u56de\u9762\u5b50\u7684\u610f\u601d\uff0c\u5168\u573a\u4eba\u7b49\u53c8\u4e0d\u7ea6\u800c\u540c\u5730 \u6ce8\u89c6\u6bd5\u64ce\u5929\uff0c\u770b\u4ed6\u5982\u4f55\u8bf4\u8bdd\uff0c\u5b97\u6d77\u5374\u4f3c\u6beb\u4e0d\u5728\u610f\u5730\u5f39\u5251\u957f\u5578\uff0c\u5ffd\u5730\u5411\u94b1\u901a\u6d77\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60 \u7684\u672c\u94b1\u90fd\u6536\u56de\u4e86\u5417\uff1f\u201d\u6bd5\u613f\u7a77\u54c8\u54c8\u4e00\u7b11\uff0c\u94bb\u51fa\u4eba\u4e1b\u53eb\u9053\uff1a\u201c\u8981\u6709\u94b1\u4eba\u6316\u8170\u5305\u771f\u662f\u96be\u4e8e\u767b\u5929\uff0c \u597d\u5427\uff0c\u65e2\u7136\u6709\u9633\u5927\u7237\u51fa\u5934\uff0c\u6211\u8fd9\u7a77\u5316\u5b50\uff0c\u53ea\u597d\u628a\u5230\u53e3\u7684\u4e1c\u897f\u4e5f\u5410\u51fa\u6765\u4e86\u201d\u53cc\u6307\u4e00\u5f39\uff0c\u4e24\u7c92\u91d1 \u73e0\u7834\u7a7a\u98de\u51fa\uff0c\u94b1\u901a\u6d77\u6b66\u529f\u5728\u6bd5\u613f\u7a77\u4e4b\u4e0a\uff0c\u8d81\u52bf\u5356\u5f04\uff0c\u5c06\u7b97\u76d8\u5f80\u4e0a\u4e00\u8fce\uff0c\u4e24\u7c92\u91d1\u73e0\u7aef\u7aef\u6b63\u6b63\u5730 \u843d\u5728\u4e00\u6839\u67f1\u5b50\u4e0a\uff0c\u4ed6\u987a\u624b\u4e00\u63a5\uff0c\u5c06\u73e0\u5b50\u7a7f\u5165\u91d1\u67f1\uff0c\u91d1\u7b97\u76d8\u6062\u590d\u539f\u72b6\u3002 \u3000\u3000\u6bd5\u613f\u7a77\u563b\u563b\u7b11\u9053\uff1a\u201c\u6709\u94b1\u7684\u5927\u7237\uff0c\u672c\u94b1\u5df2\u6536\u56de\u4e86\uff0c\u4f60\u96be\u9053\u8fd8\u8981\u5229\u94b1\u5417\uff1f\u201d\u8fd9\u8bdd\u5176\u5b9e\u662f\u8bf7 \u9633\u5b97\u6d77\u65e9\u8d70\u7684\u610f\u601d\uff0c\u9633\u5b97\u6d77\u4f38\u51fa\u53cc\u6307\uff0c\u5728\u957f\u5251\u4e0a\u950b\u5730\u5f39\u4e86\u4e00\u4e0b\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u505a \u751f\u610f\u7684\u5f53\u7136\u662f\u8fd8\u8981\u5229\u94b1\uff01\u201d \u3000\u3000\u6b64\u8bdd\u4e00\u51fa\uff0c\u5168\u573a\u9707\u52a8\uff0c\u6b66\u632f\u4e1c\u5fc3\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u4e5f\u60f3\u4e89\u8fd9\u5927\u9f99\u5934\u7684\u5ea7\u4f4d\uff1f\u4ed6\u6b66\u529f\u867d\u9ad8\uff0c\u884c \u4e8b\u5374\u662f\u4e0d\u5927\u6b63\u6d3e\uff0c\u82e5\u6559\u4ed6\u505a\u4e86\u5317\u4e94\u7701\u7eff\u6797\u7684\u5927\u9f99\u5934\uff0c\u5927\u4e8b\u53ef\u5c31\u7cdf\u4e86\u3002\u201d\u573a\u4e2d\u62b1\u7740\u540c\u6837\u5fc3\u601d\u7684 \u4eba\u5927\u7ea6\u8fd8\u771f\u4e0d\u5c11\uff0c\u6240\u4ee5\u5728\u9633\u5b97\u6d77\u9732\u4e86\u8fd9\u51e0\u624b\u60ca\u4eba\u7684\u6b66\u529f\u4e4b\u540e\uff0c\u6240\u83b7\u5f97\u7684\u5f69\u58f0\u53cd\u800c\u5be5\u843d\uff0c\u559d\u5f69 \u7684\u5c11\u6570\u4eba\uff0c\u6562\u60c5\u591a\u662f\u4ed6\u7684\u515a\u7fbd\u3002 \u3000\u3000\u53ea\u89c1\u6bd5\u64ce\u5929\u7f13\u7f13\u800c\u51fa\uff0c\u8d70\u5230\u9633\u5b97\u6d77\u7684\u8ddf\u524d\uff0c\u53cc\u773c\u4e00\u5f20\uff0c\u76ee\u5149\u5982\u5251\uff0c\u76f4\u5c04\u5230\u9633\u5b97\u6d77\u9762\u4e0a\uff0c \u9633\u5b97\u6d77\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5927\u9f99\u5934\u6709\u4f55\u89c1\u6559\uff1f\u201d\u6bd5\u64ce\u5929\u4ef0\u5929\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u8a00\u9053\uff1a\u201c\u60f3\u6bd5\u67d0\u672b\u5b66\u540e \u8f88\uff0c\u54ea\u6562\u5f53\u8fd9\u5927\u9f99\u5934\u4e4b\u4efb\uff1f\u53ea\u662f\u6211\u8fd9\u4f4d\u5144\u5f1f\u4e43\u662f\u4e00\u4e2a\u7a77\u53eb\u5316\u5b50\uff0c\u4ed6\u54ea\u6709\u5229\u94b1\u7ed9\u4f60\uff1f\u6ca1\u5948\u4f55\u53ea \u597d\u6211\u66ff\u4ed6\u4ed8\u4e86\u3002\u201d\u9633\u5b97\u6d77\u5929\u7b11\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u90a3\u6211\u5c31\u4e0d\u5ba2\u6c14\u5411\u4f60\u8ba8\u4e86\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5530 \u5730\u4e00\u5251\uff0c\u5c31\u76f4\u523a\u6bd5\u64ce\u5929\u54bd\u5589\u4e0b\u4e09\u5bf8\u7684\u201c\u7487\u7391\u7a74\u201d\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u56de\u8eab\u5f39\u8d77\uff0c\u547c\u5730\u4e00\u58f0\uff0c\u6296\u8d77\u4e00\u4e2a\u7897\u53e3\u5927\u7684\u68cd\u82b1\uff0c\u5c06\u9633\u5b97\u6d77\u7684\u957f\u5251\u683c\u5f00\uff0c\u624b\u8d77\u68d2 \u843d\uff0c\u8eab\u5f62\u672a\u6362\uff0c\u5c31\u662f\u4e00\u62db\u201c\u6b66\u677e\u6253\u864e\u201d\uff0c\u5288\u80a9\u626b\u80ef\uff0c\u9633\u5b97\u6d77\u7b11\u9053\uff1a\u201c\u597d\u5feb\uff01\u201d\u957f\u5251\u4e00\u6311\uff0c\u5251 \u5149\u68d2\u5f71\u4e4b\u4e0b\uff0c\u53ea\u89c1\u6bd5\u64ce\u5929\u8dc4\u8dc4\u8e09\u8e09\u5730\u5411\u524d\u76f4\u6251\u51e0\u6b65\uff0c\u8fd9\u624d\u6536\u5f97\u4f4f\u68d2\u52bf\uff0c\u800c\u9633\u5b97\u6d77\u4e5f\u5411\u540e\u8fde\u9000 \u51e0\u6b65\uff0c\u624d\u7a33\u5f97\u4f4f\u8eab\u5f62\u3002\u539f\u6765\u9633\u5b97\u6d77\u60f3\u7528\u9634\u67d4\u7684\u7c98\u6cbe\u4e4b\u52b2\uff0c\u501f\u4ed6\u7684\u9633\u521a\u4e4b\u529b\uff0c\u5c06\u4ed6\u7684\u68cd\u68d2\u626d\u8fc7 \u6765\uff0c\u53eb\u4ed6\u91cd\u91cd\u5730\u8dcc\u4e00\u8de4\uff0c\u82e5\u7136\u4e24\u4eba\u6240\u7528\u7684\u52b2\u9053\u76f8\u5dee\u65e0\u51e0\uff0c\u6216\u8005\u6bd5\u64ce\u5929\u7684\u52b2\u529b\u867d\u5927\uff0c\u4f46\u5374\u4e0d\u80fd \u4f7f\u7528\u5de7\u52b2\uff0c\u90a3\u5c31\u975e\u5927\u5403\u5176\u4e8f\u4e0d\u53ef\uff0c\u5374\u4e0d\u6599\u6bd5\u64ce\u5929\u5929\u751f\u795e\u529b\uff0c\u8fd9\u4e00\u62db\u201c\u6b66\u677e\u6253\u864e\u201d\uff0c\u6709\u82e5\u91d1\u521a \u731b\u6251\uff0c\u52c7\u4e0d\u53ef\u5f53\uff0c\u9633\u5b97\u6d77\u867d\u628a\u4ed6\u626f\u4e86\u8fc7\u6765\uff0c\u4f46\u81ea\u5df1\u4ea6\u7981\u4e0d\u4f4f\u8fd9\u80a1\u795e\u529b\uff0c\u7ed9\u4ed6\u9707\u9000\uff0c\u800c\u6bd5\u64ce\u5929 \u89c1\u52bf\u4e0d\u597d\uff0c\u5728\u68d2\u5251\u4e00\u89e6\u4e4b\u9645\uff0c\u7acb\u5373\u68cd\u5c16\u4e00\u5f39\uff0c\u5411\u5de6\u7a0d\u6b6a\uff0c\u7528\u5de7\u52b2\u6b63\u6b62\u4f4f\u4e86\u90a3\u524d\u503e\u4e4b\u52bf\uff0c\u6545\u6b64 \u4e24\u4eba\u867d\u5404\u7ed9\u5bf9\u65b9\u5e26\u52a8\uff0c\u4f46\u4e00\u4e2a\u524d\u6251\uff0c\u4e00\u4e2a\u540e\u9000\uff0c\u53c8\u90fd\u4e0d\u80fd\u8d81\u654c\u4eba\u8eab\u5f62\u672a\u7a33\u4e4b\u9645\uff0c\u5373\u65bd\u653b\u8d70\uff0c \u6545\u6b64\u8fd9\u4e00\u62db\u867d\u662f\u9669\u6781\uff0c\u4f46\u5374\u672a\u5206\u51fa\u8f93\u8d62\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u4e2a\u76d8\u65cb\uff0c\u53c8\u662f\u5251\u98de\u68d2\u8d77\uff0c\u53ea\u89c1\u5251\u4f3c\u6e38\u9f99\uff0c\u68cd\u5982\u98de\u51e4\uff0c\u6740\u5f97\u4e2a\u96be\u89e3\u96be\u5206\u3002\u5728\u573a\u7684\u5404 \u8def\u82f1\u8c6a\uff0c\u770b\u5f97\u60ca\u5fc3\u9a87\u76ee\uff0c\u53c8\u90fd\u4e0d\u7981\u6697\u6697\u60ca\u5947\uff1a\u770b\u8fd9\u6bd5\u64ce\u5929\u8fd8\u4e0d\u5230\u4e09\u5341\u5c81\u7684\u5e74\u7eaa\uff0c\u5c45\u7136\u80fd\u4e0e\u6210 \u540d\u7684\u5927\u5251\u5ba2\u8d4c\u80dc\u4e89\u950b\uff0c\u8d70\u4e86\u4e94\u5341\u6765\u62db\uff0c\u4e1d\u6beb\u672a\u663e\u8d25\u8c61\u3002 \u3000\u3000\u4f46\u542c\u5f97\u9633\u5b97\u6d77\u4e00\u58f0\u957f\u5578\uff0c\u5251\u6cd5\u500f\u53d8\uff0c\u53ea\u89c1\u5251\u5149\u7ee6\u7ed5\uff0c\u6709\u5982\u6c34\u94f6\u6cfb\u5730\uff0c\u82b1\u96e8\u7f24\u7eb7\uff0c\u4e00\u53e3\u5251 \u5c31\u5982\u5316\u4e86\u6570\u5341\u767e\u53e3\u4e00\u822c\uff0c\u5728\u6bd5\u64ce\u5929\u7684\u8eab\u524d\u8eab\u540e\uff0c\u8eab\u5de6\u8eab\u53f3\uff0c\u4ea4\u53c9\u7a7f\u63d2\uff0c\u6bd5\u64ce\u5929\u867d\u662f\u68cd\u91cd\u529b \u6c89\uff0c\u5982\u4f3c\u662f\u7ed9\u4ed6\u8fd9\u8def\u5251\u6cd5\u6240\u56f0\uff0c\u6e10\u6e10\u6709\u70b9\u5e94\u4ed8\u4e0d\u6687\uff0c\u94b1\u901a\u6d77\u5728\u573a\u8fb9\u563b\u563b\u51b7\u7b11\uff0c\u628a\u7b97\u76d8\u73e0\u5b50\u62e8 \u4e0a\u62d4\u4e0b\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u8fd9\u5229\u94b1\u662f\u4ed8\u5b9a\u4e86\u201d\uff01 \u3000\u3000\u6bd5\u613f\u7a77\u5728\u573a\u8fb9\u4e5f\u563b\u563b\u51b7\u7b11\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u8fd9\u5229\u94b1\u662f\u4ed8\u5b9a\u4e86\uff0c\u4f46\u5374\u4e0d\u77e5\u662f\u8c01\u4ed8\u5462\uff1f\u201d\u94b1 \u901a\u6d77\u6012\u76ee\u6a2a\u89c6\uff0c\u6bd5\u613f\u7a77\u7b11\u9053\uff1a\u201c\u6709\u94b1\u7684\u5927\u7237\uff0c\u6211\u53ef\u60f9\u4f60\u4e0d\u8d77\uff01\u201d\u62b1\u5934\u4e00\u7f29\uff0c\u6324\u5165\u4e86\u4eba\u4e1b\u4e4b \u4e2d\u3002 \u3000\u3000\u94b1\u901a\u6d77\u7ed9\u5730\u8fd9\u4e48\u6253\u79d1\u63d2\u6d51\u5730\u6405\u4e86\u4e00\u9635\uff0c\u518d\u770b\u6597\u573a\uff0c\u53ea\u89c1\u5f62\u52bf\u53c8\u53d8\uff0c\u9633\u5b97\u6d77\u7684\u5251\u52bf\u867d\u7136\u4ecd \u662f\u51cc\u5389\u4e4b\u6781\uff0c\u4f46\u90a3\u6bd5\u64ce\u5929\u4e5f\u6539\u4e86\u68d2\u6cd5\uff0c\u9002\u624d\u4ed6\u51fa\u624b\u5168\u7528\u9633\u521a\u4e4b\u529b\uff0c\u5982\u4eca\u5982\u4f46\u89c1\u4ed6\u821e\u52a8\u6746\u4ff8\uff0c \u65cb\u8f6c\u7ed5\u8eab\uff0c\u597d\u50cf\u5168\u662f\u9632\u5b88\uff0c\u5e76\u65e0\u4e00\u62db\u8fdb\u653b\uff0c\u4f46\u5728\u573a\u7684\u884c\u5bb6\u770b\u6765\uff0c\u4ed6\u8fd9\u67c4\u6746\u6367\u76d8\u65cb\u8d77\u4f0f\uff0c\u4f5c\u7684 \u90fd\u662f\u67d4\u52b2\u7684\u5706\u5f62\u6210\u534a\u5706\u5f62\uff0c\u7adf\u628a\u4e00\u6761\u6746\u68d2\u4f7f\u5f97\u5982\u540c\u8f6f\u97ad\u4e00\u6837\uff0c\u8fd9\u53ef\u662f\u975e\u540c\u5c0f\u53ef\u3002\u6b66\u5b66\u6709\u4e91\uff1a \u201c\u67aa\u6015\u5706\uff0c\u97ad\u6015\u76f4\u3002\u201d\u67aa\u6746\u662f\u540c\u4e00\u8def\u6570\uff0c\u5373\u662f\u8bf4\u82e5\u6709\u4eba\u80fd\u628a\u67aa\u6266\u8fd0\u7528\u5f97\u5982\u540c\u8f6f\u97ad\u4e00\u6837\uff0c\u6210\u4e3a \u5706\u5f62\uff0c\u90a3\u5c31\u975e\u6781\u5ea6\u5c0f\u5fc3\uff0c\u8c28\u614e\u5c06\u4e8b\u6765\u5e94\u4ed8\u4e0d\u53ef\u4e86\u3002\u679c\u7136\u5982\u6b64\u4e00\u6765\uff0c\u9633\u5b97\u6d77\u767b\u65f6\u51cf\u5c11\u4e86\u56a3\u5f20\u4e4b \u52bf\uff0c\u5251\u62db\u6e10\u8d8b\u7f13\u6162\uff0c\u4e1c\u4e00\u6307\u897f\u4e00\u5212\u5730\u597d\u50cf\u633d\u7740\u5343\u65a4\u91cd\u7269\u4f3c\u7684\uff0c\u767d\u8863\u5c11\u5e74\u6084\u6084\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u9633 \u5927\u603b\u7ba1\u5c45\u7136\u8fd0\u7528\u8d77\u6700\u4e0a\u4e58\u7684\u5185\u5bb6\u52b2\u529b\u4e86\uff0c\u4e14\u770b\u4ed6\u5982\u4f55\u7834\u8fd9\u8def\u68d2\u6cd5\u3002\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u548b\u55d3\u4e00\u58f0\uff0c\u5251\u68d2\u76f8\u4ea4\uff0c\u706b\u661f\u4e71\u53d1\uff0c\u6bd5\u64ce\u5929\u7684\u68cd\u68d2\u8131\u624b\u98de\u51fa\uff0c\u4f17\u4eba\u54d7\u7136 \u5927\u547c\uff0c\u4f46\u5c31\u5728\u8fd9\u4e00\u77ac\u4e4b\u95f4\uff0c\u53ea\u89c1\u9633\u5b97\u6d77\u4e5f\u6014\u4e86\u4e00\u6014\uff0c\u51dd\u7acb\u4e0d\u52a8\uff0c\u7adf\u4e0d\u6562\u4e58\u673a\u653b\u88ad\uff0c\u6bd5\u64ce\u5929\u8eab \u624b\u4f55\u7b49\u5feb\u6377\uff0c\u4e5f\u5c31\u5728\u8fd9\u4e00\u77ac\u4e4b\u95f4\uff0c\u98de\u8eab\u4e00\u63a0\uff0c\u4fbf\u628a\u68cd\u68d2\u6284\u5728\u624b\u4e2d\uff0c\u5c31\u5728\u534a\u7a7a\u4e2d\u821e\u8d77\u4e00\u4e2a\u6597\u5927 \u7684\u68cd\u82b1\uff0c\u82d1\u5982\u5de8\u9e70\u98de\u5544\uff0c\u547c\u5730\u4e00\u6367\u5f53\u5934\u5288\u4e0b\u3002 \u3000\u3000\u539f\u6765\u82e5\u8bba\u672c\u8eab\u7684\u6c14\u529b\uff0c\u90a3\u662f\u6bd5\u64ce\u5929\u5927\u5f97\u591a\uff0c\u4f46\u8bba\u5230\u5185\u529f\u7684\u4fee\u517b\uff0c\u5374\u662f\u9633\u5b97\u6d77\u6df1\u539a\uff0c\u800c\u4e14 \u9633\u5b97\u6d77\u6bd4\u4ed6\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u5584\u80fd\u501f\u529b\u7834\u529b\uff01\u9002\u624d\u90a3\u4e00\u62db\uff0c\u4ed6\u987a\u7740\u6bd5\u64ce\u5929\u7684\u68d2\u52bf\u4e00\u622a\uff0c\u7528\u4e0a\u4e86\u516b\u4e5d \u5206\u6c14\u529b\uff08\u9ad8\u624b\u6bd4\u6b66\uff0c\u6c14\u529b\u4e0d\u80fd\u4f7f\u5c3d\uff0c\u5426\u5219\u654c\u4eba\u8d81\u673a\u53cd\u6251\uff0c\u4fbf\u65e0\u6cd5\u6301\u7eed\uff0c\u7528\u5230\u516b\u4e5d\u5206\u6c14\u5200\uff0c\u90a3 \u5df2\u7ecf\u662f\u5230\u4e86\u6781\u9650\u4e86\uff09\uff0c\u672c\u4ee5\u4e3a\u6bd5\u64ce\u5929\u7684\u8fd9\u6761\u6266\u68d2\u975e\u6298\u4e3a\u4e24\u6bb5\u4e0d\u53ef\uff0c\u54ea\u77e5\u7531\u64ce\u5929\u7684\u8fd9\u6761\u68cd\u68d2\uff0c \u4e43\u662f\u4ed6\u7236\u4eb2\u9057\u7ed9\u4ed6\u7684\uff0c\u4e16\u4ee3\u76f8\u4f20\u7684\u5b9d\u7269\uff0c\u8fd9\u6761\u6746\u68d2\u540d\u4e3a\u201c\u964d\u9f99\u68d2\u201d\uff0c\u662f\u7528\u5357\u5929\u5c71\u4e4b\u4e0a\u7684\u964d\u9f99 \u6811\u6240\u9020\uff0c\u575a\u5982\u91d1\u94c1\uff0c\u5f53\u5e74\u5f20\u4e39\u67ab\u548c\u6bd5\u9053\u51e1\u6bd4\u6b66\uff0c\u5f20\u4e39\u67ab\u6240\u4f7f\u7684\u662f\u4e00\u53e3\u5b9d\u5251\uff0c\u5c1a\u81ea\u4e0d\u80fd\u524a\u65ad\u6b64 \u4ff8\uff08\u4e8b\u89c1\u62d9\u4f5c\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\uff09\u4f55\u51b5\u9633\u5b97\u6d77\u6240\u7528\u7684\u53ea\u662f\u4e00\u628a\u6bd4\u666e\u901a\u5200\u5251\u8f83\u4e3a\u950b\u5229\u7684\u5175\u5203\uff0c\u6240 \u4ee5\u8fd9\u4e00\u62db\uff0c\u9633\u5b97\u6d77\u867d\u7136\u80fd\u7528\u5185\u529b\u628a\u6746\u68d2\u9707\u98de\uff0c\u4f46\u4ed6\u7684\u5229\u5251\u4ea6\u7ed9\u6746\u68d2\u78b0\u4e86\u4e00\u4e2a\u7f3a\u53e3\uff0c\u6bd5\u64ce\u5929\u7684 \u6c14\u529b\u53c8\u5927\uff0c\u4e24\u5200\u4e00\u649e\uff0c\u68d2\u65e2\u4e0d\u65ad\uff0c\u5251\u4fbf\u56de\u65cb\uff0c\u9633\u5b97\u6d77\u7684\u864e\u53e3\u4e5f\u7ed9\u9707\u5f97\u6d41\u8840\u3002\u8fd9\u4e00\u62db\u662f\u6bd5\u64ce\u5929 \u5360\u4e86\u5175\u5668\u7684\u4fbf\u5b9c\uff0c\u4f46\u4ed6\u7684\u6746\u68d2\u8131\u624b\u9707\u98de\uff0c\u4eba\u6240\u5171\u89c1\uff0c\u9633\u5b97\u6d77\u864e\u53e3\u6d41\u8840\uff0c\u5374\u65e0\u4eba\u77e5\u9053\uff0c\u6240\u4ee5\u8bf4 \u6765\u8fd8\u662f\u4ed6\u8f83\u5403\u4e8f\uff0c\u53ea\u662f\u63a5\u7740\u8fd9\u4e00\u68d2\u6253\u4e0b\uff0c\u7acb\u523b\u53c8\u4f7f\u5f97\u6ee1\u573a\u7686\u60ca\u3002 \u3000\u3000\u53ea\u89c1\u9633\u5b97\u6d77\u957f\u5251\u4e00\u633a\uff0c\u5251\u5c16\u62b5\u7740\u68d2\u7aef\uff0c\u6bd5\u64ce\u5929\u8fd9\u51cc\u7a7a\u4e00\u51fb\u3002\u4f55\u7b49\u5389\u5bb3\uff0c\u5728\u534a\u7a7a\u4e2d\u5df1\u631f\u7740 \u547c\u547c\u7684\u98ce\u58f0\uff0c\u4f17\u4eba\u90fd\u4ee5\u4e3a\u8fd9\u4e00\u6b21\u5251\u68d2\u76f8\u4ea4\uff0c\u5fc5\u5b9a\u6bd4\u4e0a\u4e00\u6b21\u8fd8\u8981\u6fc0\u70c8\uff0c\u54ea\u77e5\u53cc\u65b9\u7684\u5175\u5668\u4e00\u6444\uff0c \u7adf\u662f\u5bc2\u7136\u65e0\u58f0\uff0c\u6bd5\u64ce\u5929\u7684\u964d\u9f99\u68d2\u5c31\u50cf\u7c98\u5728\u9633\u5b97\u6d77\u7684\u5251\u5c16\u4e0a\u4f3c\u7684\uff0c\u4eba\u4e5f\u843d\u4e0d\u4e0b\u6765\uff0c\u53ea\u542c\u5f97\u9633\u5b97 \u6d77\u5929\u559d\u4e00\u58f0\uff0c\u8de8\u524d\u4e09\u6b65\uff0c\u957f\u5251\u4e00\u7529\uff0c\u6bd5\u64ce\u5929\u8fde\u4eba\u5e26\u68d2\uff0c\u7c98\u5728\u4ed6\u7684\u957f\u5251\u4e4b\u4e0a\uff0c\u8eab\u5b50\u60ac\u7a7a\uff0c\u7adf\u4f3c \u9640\u87ba\u65cb\u8f6c\u4e0d\u505c\uff0c\u5728\u573a\u7fa4\u8c6a\uff0c\u83ab\u540d\u6240\u4ee5\uff0c\u65e0\u4e0d\u60ca\u8be7\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u4e0e\u6b66\u632f\u4e1c\u7b49\u6b66\u5b66\u6df1\u6e5b\u4e4b\u58eb\u81ea\u7136\u660e\u767d\uff0c\u8fd9\u662f\u9633\u5b97\u6d77\u6709\u5fc3\u548c\u6bd5\u64ce\u5929\u8f83\u91cf\u5185\u529b\uff0c\u5c06 \u201c\u7c98\u52b2\u201d\u548c\u201c\u68da\u52b2\u201d\u8fde\u540c\u4f7f\u7528\uff0c\u4ee5\u529b\u501f\u529b\uff0c\u4ee5\u5de7\u964d\u529b\uff0c\u8fd9\u6b63\u662f\u6700\u4e0a\u4e58\u5185\u5bb6\u529f\u5929\uff0c\u54ea\u77e5\u6bd5\u64ce\u5929 \u7684\u529f\u529b\u867d\u8f83\u9633\u5b97\u6d77\u7a0d\u900a\uff0c\u4f46\u4ed6\u8fd9\u51cc\u7a7a\u4e00\u51fb\uff0c\u81ea\u4e0a\u800c\u4e0b\uff0c\u52b2\u9053\u8f83\u5728\u4e4e\u5730\u4e0a\u53d1\u62db\u51e0\u4e4e\u5f3a\u4e86\u4e00\u534a\uff0c \u518d\u52a0\u4e0a\u4ed6\u672c\u8eab\u7684\u91cd\u91cf\uff0c\u4f7f\u52b2\u4e0b\u538b\uff0c\u8fd9\u4e00\u68d2\u4e4b\u529b\uff0c\u4f55\u6b62\u5343\u65a4\uff01\u9633\u5b97\u6d77\u867d\u7136\u5c06\u4ed6\u7684\u6765\u52bf\u7528\u5de7\u52b2\u5378 \u5f00\u3002\u4f46\u5230\u5e95\u8fd8\u662f\u611f\u53d7\u7740\u5f53\u5934\u91cd\u538b\uff0c\u7c98\u662f\u7c98\u4f4f\u4e86\uff0c\u201c\u68da\u201d\u5374\u68da\u4e0d\u5f00\uff0c\u7adf\u53d8\u6210\u4e86\u50f5\u6301\u4e4b\u52bf\u3002 \u3000\u3000\u4f46\u662f\u9633\u5b97\u6d77\u4e0d\u505c\u5730\u5728\u573a\u4e2d\u7ed5\u7740\u5706\u5708\uff0c\u5b9d\u5251\u4e00\u4f38\u4e00\u7f29\uff1b\u6bd5\u64ce\u5929\u5728\u4e0a\u9762\u4e5f\u4e0d\u505c\u5730\u6253\u8f6c\uff0c\u9633\u5b97 \u6d77\u7529\u4ed6\u4e0d\u52a8\uff0c\u4ed6\u4e5f\u6ca1\u529e\u6cd5\u81ea\u5df1\u4e0b\u6765\uff0c\u4e0d\u591a\u65f6\uff0c\u4e24\u4eba\u90fd\u662f\u6ee1\u8eab\u5927\u6c57\u3002 \u3000\u3000\u6b66\u632f\u4e1c\u6697\u547c\u4e0d\u5999\uff0c\u770b\u8fd9\u60c5\u5f62\uff0c\u867d\u7136\u6682\u65f6\u8fd8\u662f\u76f8\u6301\u4e4b\u540c\u3002\u4f46\u65f6\u95f4\u4e00\u4e45\uff0c\u90a3\u5374\u8d70\u662f\u6bd5\u64ce\u5929\u5403 \u4e8f\uff0c\u56e0\u4e3a\u6bd5\u64ce\u5929\u5389\u5bb3\u4e4b\u5904\uff0c\u4e43\u5728\u521a\u624d\u7684\u51cc\u7a7a\u4e00\u51fb\uff0c\u4e00\u51fb\u672a\u80fd\u6536\u52b2\uff0c\u8eab\u5b50\u60ac\u7a7a\uff0c\u5c31\u4e0d\u6613\u4f7f\u529b \u4e86\u3002 \u3000\u3000\u6b66\u632f\u4e1c\u7709\u5934\u4e00\u76b1\uff0c\u8d70\u51fa\u573a\u4e2d\uff0c\u5bf9\u9633\u3001\u6bd5\u4e8c\u4eba\u4e00\u63d6\u8bf4\u9053\uff1a\u201c\u4e24\u864e\u76f8\u6597\uff0c\u5fc5\u6709\u4e00\u4f24\uff0c\u9633\u5927\u54e5 \u548c\u6bd5\u8d24\u5f1f\u90fd\u53ef\u4ee5\u7f62\u624b\u4e86\u3002\u201d\u4e24\u4eba\u4f3c\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\uff0c\u770b\u60c5\u5f62\u662f\u4e24\u5bb6\u90fd\u5728\u503e\u5c3d\u5168\u529b\uff0c\u8fd0\u52b2 \u76f8\u6301\uff0c\u7f62\u624b\u4e0d\u80fd\u3002\u6b66\u632f\u4e1c\u53c8\u9053\uff1a\u201c\u9633\u5927\u54e5\uff0c\u4f60\u662f\u6210\u540d\u7684\u5251\u5ba2\uff0c\u6bd5\u8d24\u5f1f\u4e43\u662f\u540e\u8f88\u7684\u82f1\u96c4\uff0c\u9633\u5927 \u54e5\u4f60\u4e00\u5411\u5728\u897f\u5357\u53d1\u8ff9\uff0c\u82e5\u7136\u662f\u6709\u610f\u5230\u5317\u65b9\u5730\u5934\u5f00\u4e00\u5c71\u7acb\u5be8\uff0c\u8fd9\u5927\u9f99\u5934\u4e4b\u4e8b\u53ef\u4ee5\u597d\u597d\u5546\u91cf \u5440\uff01\u201d\u6b66\u632f\u4e1c\u5e76\u4e0d\u77e5\u9053\u9633\u5b97\u6d77\u5df2\u7ecf\u505a\u5f53\u4eca\u7684\u5927\u5185\u603b\u7ba1\uff0c\u53ea\u4ee5\u4e3a\u4ed6\u6709\u610f\u548c\u6bd5\u64ce\u5929\u4e89\u5927\u9f99\u5934\u4e4b \u4f4d\uff0c\u6545\u51fa\u6b64\u8a00\u76f8\u529d\uff0c\u7528\u8bf4\u8bdd\u70b9\u9192\u9633\u5b97\u6d77\uff0c\u8bf7\u4ed6\u6ce8\u610f\u81ea\u5df1\u6210\u540d\u5251\u5ba2\u7684\u8eab\u4efd\u3002 \u3000\u3000\u54ea\u77e5\u9633\u5b97\u6d77\u5168\u7136\u4e0d\u7406\uff0c\u4ed6\u5982\u4eca\u5df2\u5360\u4e86\u4e0a\u98ce\uff0c\u90a3\u80af\u6536\u624b\uff0c\u53ea\u89c1\u4ed6\u7684\u5708\u5b50\u8d8a\u7ed5\u8d8a\u6025\uff0c\u6bd5\u64ce\u5929 \u8fde\u4eba\u5e26\u68d2\u9644\u5728\u4ed6\u7684\u957f\u5251\u4e4b\u4e0a\uff0c\u5c31\u50cf\u4e00\u53f6\u8f7b\u821f\uff0c\u5728\u72c2\u6d9b\u9a87\u6d6a\u4e4b\u4e2d\u98a0\u7c38\u8d77\u4f0f\uff0c\u60c5\u52bf\u8d8a\u6765\u8d8a\u9669\uff0c\u6b66 \u632f\u4e1c\u62ff\u4ed6\u6ca1\u6cd5\uff0c\u60f3\u51fa\u624b\u89e3\u5f00\uff0c\u81ea\u5fd6\u53c8\u6ca1\u6709\u8fd9\u4efd\u529f\u529b\u3002 \u3000\u3000\u6b63\u5f53\u5168\u573a\u4eba\u7b49\u60ca\u5fc3\u6ce8\u76ee\uff0c\u6b66\u632f\u4e1c\u8e0c\u8e87\u65e0\u8ba1\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u6e05\u8106\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4eba\u5bb6\u662f \u9633\u5927\u603b\u7ba1\uff0c\u624d\u4e0d\u7a00\u7f55\u4f60\u7684\u5927\u9f99\u5934\u5462\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u53ea\u89c1\u4e00\u6735\u91d1\u82b1\uff0c\u5728\u7a7a\u4e2d\u4e00\u95ea\uff0c\u201c\u94ee\u201d\u4e00 \u58f0\uff0c\u6070\u6070\u6253\u4e2d\u4e86\u9633\u5b97\u6d77\u7684\u5251\u5c16\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u6070\u5230\u597d\u5904\uff0c\u9633\u5b97\u6d77\u7684\u5251\u5c16\u4e00\u6b6a\uff0c\u6bd5\u64ce\u5929\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u76d8\u65cb\uff0c\u98de\u8eab\u63a0\u4e0b\uff0c\u53ea\u89c1\u4e00\u4e2a \u767d\u8863\u5c11\u5e74\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u8d8a\u4f17\u800c\u51fa\uff0c\u90a3\u91d1\u82b1\u6697\u5668\u81ea\u7136\u662f\u4ed6\u6240\u53d1\u7684\u4e86\uff0c\u573a\u4e2d\u7fa4\u8c6a\uff0c\u8fde\u6b66\u8001\u5e84\u4e3b\u5728 \u5185\uff0c\u90fd\u65e0\u4e0d\u60ca\u8be7\uff0c\u77a7\u8fd9\u5c11\u5e74\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u7adf\u5c45\u7136\u6709\u8fd9\u4efd\u529f\u529b\uff01\u5176\u5b9e\u767d\u8863\u5c11\u5e74\u8fd9\u4e00\u624b\u98de\u82b1\u89e3\u56f0\uff0c \u6240\u7528\u7684\u5168\u662f\u5de7\u52b2\uff0c\u8d81\u7740\u9633\u6bd5\u4e8c\u4eba\u7684\u5185\u529b\u76f8\u63a8\u76f8\u63a5\u4e4b\u9645\uff0c\u4ed6\u7684\u6697\u5668\u6070\u6070\u5728\u8fd9\u4e24\u80a1\u5927\u529b\u4e4b\u95f4\u8f7b\u8f7b \u4e00\u78b0\uff0c\u6240\u7528\u7684\u6b63\u662f\u6b66\u5b66\u4e2d\u201c\u56db\u4e24\u62e8\u5343\u65a4\u201d\u7684\u9053\u7406\uff0c\u6545\u6b64\u4fbf\u80fd\u5c06\u4e24\u5927\u9ad8\u624b\u4e00\u4e0b\u5206\u5f00\uff0c\u5176\u5b9e\u8bba\u8d77 \u529f\u529b\uff0c\u4ed6\u6bd4\u9633\u3001\u6bd5\u4e8c\u4eba\u76f8\u5dee\u5c1a\u8fdc\uff0c\u6bd5\u64ce\u5927\u81ea\u662f\u660e\u767d\u5176\u4e2d\u9053\u7406\uff0c\u4f46\u89c1\u90a3\u5c11\u5e74\u8fd0\u7528\u5f97\u5982\u6b64\u5de7\u5999\uff0c \u6697\u5668\u6253\u5f97\u5982\u6b64\u4e4b\u51c6\uff0c\u7adf\u7136\u4e0d\u5dee\u6beb\u5398\uff0c\u5fc3\u4e2d\u4e5f\u662f\u6781\u4e3a\u4f69\u670d\u3002 \u3000\u3000\u90a3\u767d\u8863\u5c11\u5e74\u7f13\u7f13\u8d70\u51fa\uff0c\u4e00\u53cc\u4fca\u76ee\u5728\u573a\u4e2d\u4e00\u626b\uff0c\u6700\u540e\u76ef\u7740\u9633\u5b97\u6d77\u4f55\u9053\uff1a\u201c\u9633\u5927\u603b\u7ba1\uff0c\u6211\u53ef \u6ca1\u6709\u8bf4\u9519\u4f60\u5427\uff1f\u4f60\u670d\u4f8d\u7687\u4e0a\u53ea\u6015\u8fd8\u5206\u4e0d\u8fc7\u8eab\u6765\u5462\uff0c\u54ea\u6709\u5de5\u592b\u505a\u5317\u4e94\u7701\u7eff\u6797\u4e2d\u7684\u5927\u9f99\u5934\uff1f\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u5168\u573a\u9707\u52a8\uff0c\u987b\u77e5\u9633\u5b97\u6d77\u63a5\u53d7\u7948\u9547\u4e4b\u8058\uff0c\u505a\u5927\u5185\u7684\u603b\u7ba1\uff0c\u8fd8\u672a\u591f\u4e00\u6708\uff0c\u53ea\u662f\u5728 \u7948\u9547\u590d\u8f9f\u7684\u524d\u5915\uff0c\u5f53\u65f6\u7948\u9547\u8fd8\u88ab\u56da\u5357\u5bab\uff0c\u6210\u8d25\u5c1a\u672a\u53ef\u77e5\uff0c\u6240\u4ee5\u8058\u8bf7\u9633\u5b97\u6d77\u4e4b\u4e8b\uff0c\u6781\u4e3a\u79d8\u5bc6\uff0c \u9664\u4e86\u9646\u5c55\u9e4f\u7b49\u6709\u9650\u51e0\u4eba\u4e4b\u5916\uff0c\u6c5f\u6e56\u4e4b\u4e0a\u65e0\u4eba\u5f97\u77e5\uff0c\u8fd9\u5c11\u5e74\u4e00\u53e3\u5c06\u9633\u5b97\u6d77\u7684\u6765\u5386\u9053\u7834\uff0c\u6b66\u632f\u4e1c \u9996\u5148\u95ee\u9053\uff1a\u201c\u9633\u5927\u54e5\uff0c\u8fd9\u662f\u771f\u7684\uff1f\u201d\u573a\u4e2d\u5404\u5be8\u5be8\u4e3b\u66f4\u662f\u7eb7\u7eb7\u8bae\u8bba\uff0c\u6709\u7684\u8868\u793a\u6000\u7591\uff0c\u6709\u7684\u52c3\u7136 \u52a8\u6012\uff0c\u6709\u7684\u53d1\u8bed\u51b7\u8ba5\uff0c\u6709\u7684\u5411\u65c1\u4eba\u63a2\u95ee\uff0c\u573a\u4e2d\u767b\u65f6\u6df7\u4e71\u3002 \u3000\u3000\u9633\u5b97\u6d77\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u91cc\u63a8\u4e3e\u9f99\u5934\u5e2e\u4e3b\uff0c\u5f3a\u8005\u4e3a\u738b\uff0c\u6211\u505a\u4ec0\u4e48\uff0c\u4e0e\u6b64\u4e8b\u4f55\u6d89\uff1f\u201d\u6b66\u632f \u4e1c\u52c3\u7136\u53d8\u8272\uff0c\u4ef0\u5929\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\u9053\uff1a\u201c\u4e95\u6c34\u4e0d\u72af\u6cb3\u6c34\uff0c\u5c71\u91ce\u4e4b\u592b\u4e0d\u6562\u966a\u4f34\u8d35\u4eba\uff0c\u9633\u5927\u603b\u7ba1\uff0c \u6055\u6211\u5931\u656c\uff0c\u4e5f\u8bf7\u6055\u6211\u4e0d\u6562\u62db\u5f85\u4f60\u4e86\u3002\u201d\u9633\u5b97\u6d77\u4e00\u770b\uff0c\u53ea\u89c1\u5404\u5be8\u5be8\u4e3b\u90fd\u624b\u6309\u5175\u5668\uff0c\u6012\u76ee\u800c\u89c6\uff0c \u5fc3\u77e5\u6b66\u632f\u4e1c\u867d\u4e0d\u6562\u660e\u76ee\u5f20\u80c6\u53cd\u6297\u671d\u5ef7\uff0c\u4f46\u5404\u5be8\u5be8\u4e3b\u90fd\u662f\u4ea1\u547d\u4e4b\u5f92\uff0c\u4ec0\u4e48\u4e8b\u505a\u4e0d\u51fa\u6765\uff1f\u4ed6\u867d\u6b66 \u529f\u9ad8\u5f3a\uff0c\u5728\u4f17\u76ee\u6012\u89c6\u4e4b\u4e0b\uff0c\u4e5f\u4e0d\u89c9\u5fc3\u4e8f\u80c6\u602f\uff0c\u5f53\u4e0b\u5c06\u957f\u5251\u4e00\u6536\uff0c\u5e72\u7b11\u4e24\u58f0\uff0c\u63a9\u9970\u7a98\u6001\uff0c\u5bf9\u94b1 \u901a\u6d77\u9053\uff1a\u201c\u597d\u5475\uff0c\u539f\u6765\u8fd9\u91cc\u7684\u5927\u9f99\u5934\u4e0d\u662f\u4ee5\u6280\u538b\u5f53\u573a\uff0c\u6ca1\u672c\u94b1\u7684\u4e5f\u505a\u5927\u751f\u610f\uff0c\u54b1\u4eec\u8fd8\u5728\u8fd9\u91cc \u505a\u4ec0\u4e48\uff1f\u54b1\u4eec\u662f\u6b63\u7ecf\u7684\u751f\u610f\u4eba\uff0c\u53ea\u597d\u8d70\u4e86\uff01\u201d\u6bd5\u613f\u7a77\u5728\u573a\u8fb9\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u6b63\u7ecf\u7684\u751f\u610f \u4eba\uff1f\u6367\u7740\u7687\u5e1d\u8001\u5b50\u7684\u817f\u60f3\u5347\u5b98\u53d1\u8d22\u90a3\u662f\u771f\u7684\u3002\u201d\u4e5f\u6709\u4eba\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u6280\u538b\u5f53\u573a\uff1f\u4f60\u4e5f\u6ca1\u8d62\u4e86 \u6bd5\u5be8\u4e3b\uff0c\u54fc\u54fc\uff0c\u8fd8\u662f\u6210\u540d\u7684\u5927\u5251\u5ba2\u5462\uff1f\u7559\u4e0b\u6765\u54b1\u4eec\u518d\u6bd4\u5212\u6bd4\u5212\uff01\u201d\u559d\u9a82\u58f0\u51b7\u7b11\u58f0\u54cd\u6210\u4e00\u7247\uff0c \u9633\u5b97\u6d77\u4e0d\u6562\u56de\u5934\uff0c\u643a\u7740\u94b1\u901a\u6d77\u7b49\u4e00\u73ed\u515a\u7fbd\u8d70\u4e86\u3002\u4f17\u4eba\u8fd9\u624d\u660e\u767d\uff0c\u94b1\u901a\u6d77\u867d\u7136\u8eab\u5bb6\u767e\u4e07\uff0c\u53f7\u79f0 \u5bcc\u5546\uff0c\u539f\u6765\u5fc3\u8fd8\u672a\u8db3\uff0c\u53c8\u5df4\u7ed3\u4e0a\u4e86\u5927\u5185\u603b\u7ba1\uff0c\u6562\u60c5\u4ed6\u662f\u5bcc\u5219\u6c42\u8d35\uff0c\u8fd8\u60f3\u505a\u5b98\u5462\u3002 \u3000\u3000\u6b66\u8001\u5e84\u4e3b\u6b63\u60f3\u8bf4\u8bdd\uff0c\u5ffd\u89c1\u90a3\u767d\u8863\u5c11\u5e74\u62d4\u51fa\u4e00\u628a\u7cbe\u8292\u56db\u5c04\u7684\u77ed\u5251\uff0c\u5411\u7740\u6bd5\u64ce\u5929\u4e00\u6307\uff01 \u3000\u3000\u6b66\u632f\u4e1c\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u96be\u9053\u8fd9\u4e73\u81ed\u672a\u5e72\u7684\u5c0f\u54e5\u513f\u4e5f\u8981\u4e89\u593a\u5927\u9f99\u5934\u4e4b\u4f4d\uff1f\u53ea\u542c\u5f97\u90a3\u767d\u8863 \u5c11\u5e74\u9053\uff1a\u201c\u4f60\u505a\u9f99\u5934\u6211\u4e0d\u7ba1\u4f60\uff0c\u4f46\u4f60\u505a\u9f99\u5934\u4e4b\u524d\u53ef\u5f97\u628a\u5077\u53bb\u7684\u4e1c\u897f\u4ea4\u8fd8\u51fa\u6765\uff01\u201d\u6b66\u632f\u4e1c\u5927 \u5947\uff0c\u5fc3\u9053\uff1a\u201c\u6bd5\u64ce\u5929\u53ef\u5077\u4e86\u4ec0\u4e48\u4e1c\u897f\uff1f\u6bd5\u64ce\u5929\u4e0d\u505a\u6848\u5219\u5df2\uff0c\u4e00\u505a\u6848\u975e\u6709\u4e0a\u4e07\u4e24\u7684\u94f6\u5b50\u624d\u80af\u52a8 \u624b\uff0c\u90a3\u4e0d\u662f\u5077\uff0c\u800c\u662f\u660e\u76ee\u5f20\u80c6\u7684\u2018\u52ab\u2019\uff0c\u83ab\u975e\u8fd9\u5c11\u5e74\u53d7\u4e86\u54ea\u4e2a\u4e8b\u4e3b\u6240\u6258\uff0c\u8981\u5411\u6bd5\u64ce\u5929\u8ba8\u8fd8\u88ab \u52ab\u7684\u94f6\u4e24\u4e48\uff1f\u201d\u5fd9\u9053\uff1a\u201c\u8fd9\u4e8b\u597d\u529e\uff0c\u90fd\u5728\u6211\u7684\u8eab\u4e0a\uff0c\u8fd8\u4f60\u597d\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u6b20\u6211\u4e00\u9897\u4eba\u5934\uff0c\u4f60\u8fd8\u5f97\u4e86\u4e48\uff1f\u201d\u6b66\u632f\u4e1c\u83ab\u540d\u6240\u4ee5\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u6bd5 \u64ce\u5929\u9053\uff1a\u201c\u4eba\u5934\u662f\u4f60\u7684\u4e48\uff1f\u201d\u767d\u8863\u5c11\u5e74\u5ffd\u5730\u773c\u5708\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u8fd8\uff1f\u201d\u6bd5\u64ce\u5929\u53cc\u624b\u4e00 \u644a\uff0c\u9053\uff1a\u201c\u73b0\u5728\u8981\u8fd8\u7ed9\u4f60\uff0c\u53ef\u4e5f\u771f\u96be\uff01\u201d\u767d\u8863\u5c11\u5e74\u9762\u8272\u500f\u53d8\uff0c\u5530\u5730\u5c31\u662f\u4e00\u5251\uff0c\u6bd5\u64ce\u5929\u8f7b\u8f7b\u4e00 \u67b6\uff0c\u4e0d\u6599\u767d\u8863\u5c11\u5e74\u5251\u6cd5\u8fc5\u6377\u65e0\u4f26\uff0c\u970e\u65f6\u4e4b\u95f4\uff0c\u5c31\u5728\u4e0a\u4e0b\u4e2d\u4e09\u8def\uff0c\u63a5\u8fde\u523a\u4e86\u4e5d\u5251\uff0c\u6bd5\u64ce\u5929\u4e00\u88ab \u4ed6\u4ed6\u4e86\u5148\u624b\uff0c\u767b\u65f6\u53d7\u56f0\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u89e3\u6210\u5e73\u624b\uff0c\u4f46\u89c1\u90a3\u767d\u8863\u5c11\u5e74\u5251\u52bf\u5982\u8679\uff0c\u53d8\u5e7b\u83ab\u6d4b\uff0c\u7740\u7740 \u8fdb\u653b\uff0c\u82e5\u7136\u53ea\u8bba\u5251\u6cd5\uff0c\u7ade\u6bd4\u9633\u5b97\u6d77\u8fd8\u8981\u7cbe\u5999\u5f97\u591a\uff01 \u3000\u3000\u6b66\u632f\u4e1c\u53eb\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u54e5\uff0c\u4f60\u6709\u4ec0\u4e48\u8fc7\u4e0d\u53bb\u4e4b\u4e8b\uff0c\u8bf4\u51fa\u6765\u5927\u5bb6\u542c\u542c\u3002\u6740\u4eba\u4e0d\u8fc7\u5934\u70b9\u5730\uff0c \u6211\u53eb\u6bd5\u5be8\u4e3b\u5411\u4f60\u6446\u9152\u8d54\u7f6a\uff0c\u66ff\u4f60\u4e3b\u6301\u516c\u9053\u4fbf\u662f\u4e86\u3002\u201d\u5728\u6b66\u632f\u4e1c\u4e4b\u610f\uff0c\u8fd8\u4ee5\u4e3a\u6bd5\u64ce\u5929\u662f\u771f\u7684\u6740 \u4e86\u4ec0\u4e48\u4eba\uff0c\u800c\u8fd9\u4eba\u548c\u767d\u8863\u5c11\u5e74\u6709\u5173\u7cfb\uff0c\u6545\u6b64\u524d\u6765\u5bfb\u4ec7\uff0c\u8fd9\u4e5f\u662f\u6c5f\u6e56\u4e0a\u5e38\u6709\u4e4b\u4e8b\uff0c\u4e0d\u8db3\u4e3a\u5947\uff0c \u6240\u4ee5\u51fa\u8a00\u529d\u89e3\u3002 \u3000\u3000\u4e0d\u6599\u767d\u8863\u5c11\u5e74\u6beb\u4e0d\u7b54\u8bdd\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u4ecd\u662f\u5f3a\u653b\u731b\u640f\uff0c\u6bd5\u64ce\u5929\u4f7f\u4e2a\u201c\u91d1\u9f99\u620f\u6c34\u201d\u7684\u62db\u6570\uff0c \u964d\u9f99\u68d2\u5de6\u53f3\u4e00\u4e2a\u76d8\u65cb\uff0c\u5c06\u767d\u8863\u5c11\u5e74\u7684\u77ed\u5251\u903c\u4f4f\uff0c\u5927\u7b11\u9053\uff1a\u201c\u4f60\u73b0\u5728\u8fd8\u5f53\u6211\u662f\u9f20\u7a83\u72d7\u5077\u4e48\uff1f\u201d \u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u5077\u4e86\u4e1c\u897f\u4fbf\u8dd1\uff0c\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\u597d\u6c49\u3002\u4eba\u5934\u4f60\u5230\u5e95\u8fd8\u662f\u4e0d\u8fd8\uff1f\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53c8 \u8fc7\u4e86\u6570\u62db\uff0c\u6bd5\u64ce\u5929\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u4f60\u8981\u4e00\u9897\u4eba\u5934\u6709\u4f55\u7528\u5904\uff0c\u627e\u8fd8\u4f60\u5168\u5c38\uff0c\u4f60\u8981\u529e\u7684\u4e8b\u60c5\u6211\u65e9 \u5df2\u66ff\u4f60\u529e\u4e86\u3002\u201d\u767d\u8863\u5c11\u5e74\u77ed\u5251\u4e00\u6536\uff0c\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u6211\u820d\u4e86\u6027\u547d\uff0c\u62ff\u6765\u4eba\u5934\uff0c \u96be\u9053\u662f\u5f53\u800d\u7684\u5417\uff1f\u201d\u767d\u8863\u5c11\u5e74\u773c\u5708\u53c8\u662f\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u90a3\u4f60\u4fbf\u662f\u6211\u7684\u6069\u4eba\uff0c\u54b1\u4eec\u4e0d \u52c7\u6597\u4e86\u3002\u201d \u3000\u3000\u5728\u573a\u4eba\u7b49\uff0c\u4e0d\u660e\u5176\u4e2d\u7f18\u6545\uff0c\u65e0\u4e0d\u5947\u602a\u3002\u6b66\u632f\u4e1c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4eba\u5934\u5927\u4e8b\uff0c\u600e\u4e48\u5ffd\u7136\u53c8\u7f62\u624b \u4e86\uff1f\u201d\u4f46\u5929\u8272\u5df2\u665a\uff0c\u5148\u63a8\u5b9a\u5927\u9f99\u5934\u4e4b\u4e8b\u6700\u4e3a\u8981\u7d27\uff0c\u800c\u4e14\u4e0e\u90a3\u767d\u8863\u5c11\u5e74\u521a\u521a\u76f8\u8bc6\uff0c\u4e5f\u4e0d\u4fbf\u591a \u95ee\uff0c\u4fbf\u9053\uff1a\u201c\u6bd5\u5be8\u4e3b\u89c1\u8bc6\u8fc7\u4eba\uff0c\u6b66\u827a\u9ad8\u5f3a\uff0c\u9002\u624d\u5927\u5bb6\u90fd\u89c1\u7740\u4e86\u3002\u4ed6\u505a\u5927\u9f99\u5934\uff0c\u53ef\u6709\u4eba\u4e0d\u670d\u7684 \u4e48\uff1f\u201d\u5728\u573a\u7684\u5404\u8def\u82f1\u96c4\uff0c\u8f70\u7136\u53eb\u597d\uff0c\u6bd5\u64ce\u5929\u8fd8\u5f85\u63a8\u8ba9\uff0c\u6b66\u632f\u4e1c\u9053\uff1a\u201c\u4f17\u5bb6\u5be8\u4e3b\u4e00\u81f4\u63a8\u6234\uff0c\u8d24 \u5f1f\u4f60\u4e5f\u4e0d\u5fc5\u5ba2\u6c14\u4e86\u3002\u201d\u767d\u8863\u5c11\u5e74\u5ffd\u7136\u53c8\u62d4\u51fa\u77ed\u5251\uff0c\u9053\uff1a\u201c\u4e14\u6162\uff0c\u6211\u8fd8\u6709\u8bdd\u8bf4\u3002\u201d \u3000\u3000\u6b66\u632f\u4e1c\u7709\u5934\u4e00\u76b1\uff0c\u751a\u6015\u8fd9\u767d\u8863\u5c11\u5e74\u53c8\u751f\u4e8b\u7aef\uff0c\u679c\u7136\u542c\u5f97\u767d\u8863\u5c11\u5e74\u4e00\u5f00\u53e3\u4fbf\u9053\uff1a\u201c\u5927\u9f99\u53c8 \u6211\u8fd8\u6709\u4e00\u7b14\u5e10\u8981\u4e0e\u4f60\u7b97\u7b97\u3002\u201d\u6bd5\u64ce\u5929\u773c\u775b\u4e00\u7741\uff0c\u5927\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u54e5\u513f\uff0c\u53ef\u4e5f\u771f\u7b97\u5f97\u591a\u4e8b\uff0c \u51a4\u6709\u5934\uff0c\u503a\u6709\u4e3b\uff0c\u4e8b\u4e3b\u5c31\u5728\u8fd9\u513f\uff0c\u8981\u4f60\u66ff\u4ed6\u8bf4\u8bdd\uff1f\u201d\u6b66\u632f\u4e1c\u53c8\u662f\u4e00\u6014\uff0c\u6bd5\u64ce\u5929\u4f3c\u4e4e\u65e9\u5df2\u77e5\u9053 \u8fd9\u662f\u4ec0\u4e48\u5e10\uff0c\u6307\u660e\u8981\u5f53\u4e8b\u4eba\u51fa\u6765\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u7c97\u8c6a\u5927\u6c49\u5e94\u58f0\u800c\u51fa\uff0c\u6ee1\u8138\u866c\u987b\u5982\u88c1\uff0c\u53cc\u76ee\u70af\u70af\u6709\u795e\uff0c\u573a\u4e2d\u65e9\u6709\u8ba4\u8bc6\u4ed6\u7684\u4eba\u53eb \u9053\uff1a\u201c\u5ba3\u82b1\u65a7\u6a0a\u82f1\uff01\u201d\u4f46\u89c1\u6a0a\u82f1\u53cc\u62f3\u4e00\u62f1\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6bd5\u5be8\u4e3b\uff0c\u54b1\u4eec\u5728\u6cf0\u5c71\u5357\u9762\u5df2\u4f1a\u8fc7 \u4e86\uff0c\u4eca\u5e78\u8bc6\u8346\uff0c\u90a3\u4e00\u7b14\u4e09\u5341\u4e07\u4e24\u767d\u94f6\u53ef\u5426\u8d4f\u9762\u8d50\u8fd8\u5417\uff1f\u201d\u6b64\u8bed\u4e00\u51fa\uff0c\u573a\u4e2d\u7fa4\u8c6a\u767b\u65f6\u53c8\u9a9a\u52a8\u8d77 \u6765\uff0c\u600e\u4e48\u5ba3\u82b1\u65a7\u6a0a\u82f1\u5c31\u662f\u90a3\u7b14\u5b98\u94f6\u7684\u4fdd\u9556\uff1f\u201c\u8fd9\u4e8b\u60c5\u53ef\u771f\u662f\u610f\u6599\u4e0d\u5230\uff0c\u54e6\uff0c\u539f\u6765\u6b66\u5e84\u4e3b\u521a\u624d \u6240\u8bf4\u7684\u90a3\u7b14\u6e56\u5317\u76d0\u8fd0\u4f7f\u89e3\u4eac\u7684\u9556\u8fd0\uff0c\u5c31\u662f\u6bd5\u64ce\u5929\u5728\u6a0a\u82f1\u624b\u4e2d\u52ab\u53bb\u7684\uff0c\u8fd9\u53ef\u6709\u70ed\u95f9\u77a7\u4e86\u3002\u201d\u6a0a \u82f1\u662f\u6b66\u5b66\u540d\u5bb6\u4e4b\u540e\uff0c\u4e3a\u4eba\u6b63\u6d3e\uff0c\u5728\u6c5f\u6e56\u4e0a\u4e5f\u9887\u6709\u540d\u6c14\uff0c\u6b64\u4e8b\u4e00\u7ecf\u8bf4\u51fa\uff0c\u4f17\u4eba\u4ee3\u6bd5\u64ce\u5929\u8bbe\u60f3\uff0c \u90fd\u89c9\u5927\u662f\u4e3a\u96be\u3002\u6309\u8bf4\u4e3a\u4e86\u7ed3\u4ea4\u6a0a\u82f1\u8fd9\u4e48\u4e00\u4e2a\u670b\u53cb\uff0c\u90a3\u4e09\u5341\u4e07\u4e24\u94f6\u5b50\u5e94\u8be5\u4ea4\u8fd8\uff0c\u53ef\u662f\u7167\u7eff\u6797\u7684 \u89c4\u77e9\uff0c\u8fd9\u79cd\u5b98\u94f6\u65e2\u7136\u52ab\u5230\u4e86\u624b\uff0c\u5c31\u4e0d\u80fd\u5410\u51fa\uff0c\u4f55\u51b5\u6e56\u5317\u76d0\u8fd0\u4f7f\u8d2f\u5c45\u53c8\u662f\u8d2a\u56fe\u5229\u7984\u7684\u6b66\u6797\u8d25 \u7c7b\u3002\u82e5\u7136\u56e0\u6b64\u4f9d\u5faa\u60c5\u9762\uff0c\u5c06\u94f6\u4e24\u4ea4\u8fd8\uff0c\u5c82\u975e\u529e\u4e8b\u4e0d\u516c\uff0c\u6709\u5931\u7eff\u6797\u5a01\u671b\uff1f \u3000\u3000\u4f17\u4eba\u90fd\u5728\u770b\u7740\u6bd5\u64ce\u5929\uff0c\u770b\u4ed6\u5982\u4f55\u53d1\u4ed8\uff0c\u6a0a\u82f1\u9762\u4e0a\u4e00\u9635\u7ea2\u4e00\u9635\u767d\uff0c\u89c1\u6bd5\u64ce\u5929\u4e45\u4e45\u4e0d\u8bed\uff0c\u8bb7 \u8bb7\u8bf4\u9053\uff1a\u201c\u6b64\u4e8b\u8bf4\u6765\u6709\u6127\uff0c\u4f46\u5c0f\u5f1f\u5b9e\u662f\u53e6\u6709\u82e6\u8877\uff0c\u6211\u672c\u6258\u4e86\u5f20\uff0c\u5f20\u2026\u2026\u201d\u6bd5\u64ce\u5929\u53cc\u76ee\u4e00\u5f20\uff0c \u5ffd\u5730\u7eb5\u58f0\u5927\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u90a3\u72d7\u5b98\u662f\u5f20\u98ce\u5e9c\u7684\u628a\u4f84\uff0c\u4f46\u6b64\u4e8b\u82e5\u8bf4\u4e0e\u5f20\u98ce\u5e9c\u5f97\u77e5\uff0c\u5f20\u98ce\u5e9c\u4e5f\u672a \u5fc5\u8ba4\u4ed6\u662f\u4f84\u5b50\u3002\u51b5\u4e14\u6211\u6bd5\u67d0\u4eba\u8fd8\u6709\u4e00\u4e2a\u813e\u6c14\uff0c\u6211\u505a\u7684\u6848\u5b50\uff0c\u4f60\u5c31\u662f\u6258\u4e86\u6b66\u6797\u4e2d\u9876\u5c16\u513f\u7684\u4eba\u7269 \u524d\u6765\u8bf4\u9879\uff0c\u6211\u6bd5\u67d0\u4eba\u7edd\u4e0d\u5403\u8fd9\u4e00\u5957\uff0c\u4f60\u5c31\u662f\u6258\u4e86\u6cf0\u5c71\u6765\u538b\u6211\uff0c\u6211\u4e5f\u4e0d\u670d\uff01\u201d\u6a0a\u82f1\u672c\u6765\u60f3\u8bf4\u7684 \u662f\u5f20\u4e39\u67ab\uff0c\u6bd5\u64ce\u5929\u5374\u8bef\u4ee5\u4e3a\u662f\u5f20\u98ce\u5e9c\uff0c\u53cd\u800c\u8bf4\u4e86\u6a0a\u82f1\u4e00\u987f\uff0c\u6a0a\u82f1\u66f4\u662f\u5c34\u5c2c\uff0c\u767d\u8863\u5c11\u5e74\u9762\u4e0a\u53d8 \u8272\uff0c\u624b\u6307\u53c8\u6478\u5251\u4e24\u3002\u5ffd\u542c\u5f97\u6bd5\u64ce\u5929\u53c8\u662f\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u4f46\u6211\u770b\u5728\u4f60\u80fd\u63a5\u6211\u4e09\u68d2\u5de8\u7075\u68d2\u7684\u8eab\u4efd \u4e0a\uff0c\u8fd9\u4e8b\u60c5\u5012\u662f\u6709\u5f97\u5546\u91cf\u3002\u201d\u6a0a\u82f1\u5fd9\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u6211\u5c31\u542c\u585e\u4e3b\u793a\u4e0b\u4e86\u3002\u201d \u3000\u3000\u6bd5\u64ce\u5929\u53cc\u638c\u4e00\u62cd\uff0c\u53eb\u9053\uff1a\u201c\u5c06\u4eba\u5e26\u6765\uff01\u201d\u4f17\u4eba\u90fd\u5728\u770b\u7740\u6bd5\u3001\u6a0a\u4e8c\u4eba\uff0c\u4e0d\u7559\u795e\u90a3\u6bd5\u613f\u7a77\u4e0d \u77e5\u5728\u4ec0\u4e48\u65f6\u5019\uff0c\u5df2\u5e26\u4e86\u4e00\u4e2a\u9876\u6234\u6574\u9f50\u7684\u5b98\u513f\u4ece\u4eba\u4e1b\u4e2d\u94bb\u4e86\u51fa\u6765\uff1b\u563b\u563b\u7b11\u9053\uff1a\u201c\u5347\u5802\uff0c\u5347\u5802\uff01 \u6e56\u5317\u76d0\u8fd0\u4f7f\u5927\u8001\u7237\u6765\u4e86\uff01\u201d \u3000\u3000\u6a0a\u82f1\u5403\u4e86\u4e00\u60ca\uff0c\u90a3\u5b98\u513f\u53ef\u4e0d\u6b63\u662f\u81ea\u5df1\u7684\u628a\u5f1f\u8d2f\u5c45\uff01\u53ea\u89c1\u8d2f\u5c45\u9762\u5982\u6b7b\u7070\uff0c\u8eab\u8eaf\u98a4\u6296\uff0c\u5931\u60ca \u65e0\u795e\u5730\u5728\u4f17\u76ee\u77aa\u77aa\u4e4b\u4e0b\uff0c\u770b\u770b\u6bd5\u64ce\u5929\uff0c\u53c8\u770b\u770b\u6a0a\u82f1\u3002\u90a3\u60c5\u5f62\u5c31\u50cf\u4e00\u4e2a\u88ab\u62bc\u4e0a\u6cd5\u573a\u7684\u6b7b\u56da\u4e00 \u6837\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u5927\u7b11\u8fd9\uff1a\u201c\u6a0a\u5927\u54e5\uff0c\u6211\u5c06\u4f60\u7684\u628a\u5f1f\u4ece\u76d0\u8fd0\u4f7f\u7684\u8859\u95e8\u91cc\u8bf7\u6765\u4e86\uff0c\u8fd9\u53ef\u591f\u670b\u53cb\u4e86 \u5427\uff1f\u201d\u6a0a\u82f1\u53c8\u60ca\u53c8\u6c14\uff0c\u60ca\u8005\u662f\u8d2f\u5c45\u7684\u6b66\u529f\u4ea6\u975e\u6cdb\u6cdb\uff0c\u8859\u95e8\u91cc\u66f4\u662f\u9632\u536b\u68ee\u4e25\uff0c\u6bd5\u64ce\u5929\u7adf\u7136\u80fd\u4ece \u6570\u5343\u91cc\u5916\u7684\u6e56\u5317\u76d0\u8fd0\u4f7f\u8859\u95e8\u4e2d\u5c06\u4ed6\u7f1a\u4e86\u6765\u3002\u8fd9\u53ef\u771f\u6bd4\u5728\u5927\u5185\u76d7\u5b9d\u8fd8\u4e0d\u5bb9\u6613\uff01\u6c14\u8005\u662f\u4ed6\u7ade\u4e00\u70b9 \u4e0d\u7559\u60c5\u9762\uff0c\u5b98\u94f6\u672a\u89c1\u4ea4\u8fd8\uff0c\u53cd\u800c\u5c06\u8d2f\u5c45\u4e5f\u62bc\u6765\u4e86\uff0c\u8fd9\u5c82\u4e0d\u662f\u8981\u4ed6\u4eec\u5f53\u573a\u4e22\u8138\uff01 \u3000\u3000\u6bd5\u64ce\u5927\u7b11\u9053\uff1a\u201c\u8d2f\u5927\u4eba\uff0c\u8fd9\u51e0\u5929\u53ef\u59d4\u5c48\u4e86\u4f60\u5475\uff01\u201d\u8d2f\u5c45\u89c1\u6b64\u60c5\u5f62\uff0c\u81ea\u6599\u96be\u514d\uff0c\u53cd\u800c\u6bd4\u524d \u9547\u5b9a\uff0c\u6297\u58f0\u53eb\u9053\uff1a\u201c\u6211\u662f\u671d\u5ef7\u547d\u5b98\uff0c\u5b81\u6b7b\u4e0d\u8fb1\uff0c\u4f60\u8981\u6740\u4fbf\u6740\uff0c\u4f55\u5fc5\u591a\u4e8b\uff01\u6a0a\u5927\u54e5\uff0c\u540e\u4e8b\u6211\u6258 \u4ed8\u4f60\u4e86\uff0c\u5f20\u4e16\u4f2f\u90a3\u513f\uff0c\u4e5f\u70e6\u4f60\u53bb\u62a5\u8baf\u4e86\u3002\u201d\u4ed6\u4e34\u6b7b\u4e4b\u524d\u8fd8\u6258\u51fa\u5f20\u98ce\u5e9c\u7684\u540d\u5934\u60f3\u5413\u5413\u6bd5\u64ce\u5929\uff0c \u5982\u4e0d\u77e5\u5f20\u98ce\u5e9c\u65e9\u5df2\u8840\u6e85\u8352\u6751\uff0c\u4e0e\u56db\u540d\u5927\u5185\u9ad8\u624b\u540c\u5f52\u4e8e\u5c3d\u3002 \u3000\u3000\u6a0a\u82f1\u5e73\u7d20\u4e0d\u76f4\u8d2f\u5c45\u7684\u6240\u4e3a\uff0c\u4f46\u5230\u5e95\u662f\u51e0\u4ee3\u4e16\u4ea4\uff0c\u7981\u4e0d\u4f4f\u6cea\u54bd\u5fc3\u9178\uff0c\u6b63\u60f3\u53d1\u8bdd\uff0c\u4e0e\u6bd5\u64ce\u5929 \u4e00\u62fc\uff0c\u5ffd\u542c\u5f97\u6bd5\u64ce\u5929\u53c8\u5927\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u671d\u5ef7\u547d\u5b98\uff1f\u671d\u5ef7\u6b63\u5728\u8ffd\u7a76\u4f60\u5462\uff01\u6211\u5982\u4eca\u82e5\u653e\u4f60\u56de\u53bb\uff0c \u4f60\u4ea4\u4e0d\u51fa\u90a3\u4e09\u5341\u4e07\u4e24\u5b98\u94f6\uff0c\u53ef\u5f97\u5168\u5bb6\u5904\u65a9\uff01\u54c8\uff0c\u4f60\u6b7b\u4e0d\u8db3\u60dc\uff0c\u7d2f\u4e86\u4f60\u7684\u59bb\u513f\uff0c\u8fd9\u53ef\u662f\u4f60\u671d\u5ef7 \u7684\u2018\u6069\u5178\u2019\u54ea\uff01\u201d\u8d2f\u5c45\u7ed9\u4ed6\u4e00\u5413\uff0c\u77e5\u9053\u671d\u5ef7\u6cd5\u5236\u6781\u4e25\uff0c\u4ed6\u7684\u8bf4\u8bdd\u53ef\u662f\u4e00\u70b9\u4e0d\u5047\uff0c\u7f34\u4e0d\u51fa\u5b98\u94f6 \u90a3\u771f\u662f\u6284\u5bb6\u706d\u95e8\u4e4b\u7978\uff0c\u4e0d\u7981\u53c8\u5413\u5f97\u9762\u9752\u5507\u767d\uff0c\u4e0d\u7531\u81ea\u5df1\u5730\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8bf7\u5be8\u4e3b\u5f00\u6069\uff0c\u6211\u8c22\u5be8 \u4e3b\u7684\u6069\u5178\u3002\u201d \u3000\u3000\u6bd5\u64ce\u5929\u770b\u4e86\u6a0a\u82f1\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u505a\u4e86\u4e09\u5e74\u76d0\u8fd0\u4f7f\uff0c\u79ef\u4e0b\u7684\u94b1\u4e5f\u4e0d\u5c11\u5475\uff01\u201d\u8d2f\u5c45\u9053\uff1a \u201c\u54ea\uff0c\u54ea\uff0c\u54ea\u6709\u4ec0\u4e48\u94b1\uff0c\u4e0d\u591a\uff0c\u4e0d\u591a\u3002\u201d\u4ed6\u6599\u4e0d\u5230\u6bd5\u64ce\u5929\u6709\u6b64\u4e00\u95ee\uff0c\u8bed\u65e0\u4f26\u6b21\u3002\u6bd5\u64ce\u5929\u5927\u7b11 \u9053\uff1a\u201c\u4f60\u7684\u8eab\u5bb6\u4e00\u5171\u662f\u5341\u4e94\u4e07\u516d\u5343\u56db\u767e\u4e24\u94f6\u5b50\uff0c\u4e0d\u8fde\u4f60\u5728\u6545\u4e61\u65b0\u8d77\u7684\u90a3\u95f4\u5927\u5c4b\u5728\u5185\uff0c\u8fd9\u6570\u76ee \u6211\u6ca1\u8bf4\u9519\u5427\uff1f\u201d\u8d2f\u5c45\u5927\u5403\u4e00\u60ca\uff0c\u6599\u4e0d\u5230\u4ed6\u6bd4\u81ea\u5df1\u8fd8\u8981\u6e05\u695a\uff0c\u53ea\u5f97\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\u3002\u201d\u6bd5 \u64ce\u5929\u7b11\u9053\uff1a\u201c\u6211\u5982\u4eca\u770b\u5728\u4f60\u6a0a\u5927\u54e5\u7684\u9762\u4e0a\uff0c\u8fd9\u7b14\u5b98\u94f6\uff0c\u6211\u5df2\u66ff\u4f60\u7f34\u5230\u4eac\u5e08\u53bb\u4e86\uff0c\u4f60\u6ca1\u4e8b \u5566\uff01\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\u8ba9\u5176\u559c\u51fa\u671b\u5916\uff0c\u8d2f\u5c45\u5446\u5728\u573a\u4e2d\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u5ffd\u89c1\u6bd5\u64ce\u5929\u9762\u8272\u53c8\u662f\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4f46\u4f60\u90a3\u4e9b\u4e0d\u4e49\u4e4b\u8d22\uff0c\u4e5f\u4e0d\u80fd\u5c31\u6b64\u7531\u4f60\u4eab\u7528\uff0c\u8fd9\u4e09\u5341\u4e07\u4e24\u5b98\u94f6\uff0c\u6211\u5b9e\u662f\u66ff\u4f60\u7f34\u4e86\u4e00\u534a\uff0c\u90a3\u53e6 \u4e00\u534a\u5c31\u662f\u62ff\u4f60\u81ea\u5df1\u7684\u8eab\u5bb6\u53bb\u586b\u8865\u7684\u3002\u6211\u8ba9\u4f60\u7559\u4e0b\u4e00\u5ea7\u5927\u5c4b\uff0c\u53e6\u5916\u516d\u5343\u56db\u767e\u4e24\u94f6\u5b50\uff0c\u4e5f\u8db3\u591f\u4f60 \u4e0b\u534a\u4e16\u8fc7\u6d3b\u4e86\u3002\u4f60\u7684\u76d0\u8fd0\u4f7f\u80a5\u7f3a\u65e9\u5df2\u88ab\u671d\u5ef7\u5f00\u9769\uff0c\u8c05\u671d\u5ef7\u4eca\u540e\u4e5f\u4e0d\u4f1a\u518d\u7528\u4f60\u4e3a\u5b98\u4e86\u3002\u8fd9\u5012\u662f \u6551\u4e86\u4f60\u5475\uff0c\u4f60\u670d\u4e0d\u670d\uff1f\u201d \u3000\u3000\u8fd9\u8bdd\u662f\u5411\u8d2f\u5c45\u6240\u8bf4\uff0c\u5176\u5b9e\u5374\u662f\u8bf4\u7ed9\u6a0a\u82f1\u542c\u7684\u3002\u6a0a\u82f1\u5927\u4e3a\u5fc3\u670d\uff0c\u4ed6\u66fe\u597d\u51e0\u6b21\u529d\u8d2f\u5c45\u4e0d\u8981\u4e3a \u5b98\uff0c\u8d2f\u5c45\u603b\u662f\u4e0d\u542c\uff0c\u60f3\u4e0d\u5230\u6bd5\u64ce\u5929\u5374\u7528\u8fd9\u79cd\u91dc\u5e95\u62bd\u85aa\u7684\u624b\u6bb5\uff0c\u53eb\u5730\u6c38\u4e0d\u80fd\u4e3a\u5b98\uff0c\u8fd9\u786e\u662f \u201c\u6551\u201d\u4e86\u4ed6\u3002\u8d2f\u5c45\u867d\u7136\u5fc3\u75db\uff0c\u4f46\u5f97\u4fdd\u5168\u6027\u547d\uff0c\u4ea6\u5df2\u559c\u51fa\u671b\u5916\uff0c\u4e0d\u4f4f\u4ef7\u5730\u70b9\u5934\u9053\uff1a\u201c\u670d\u4e86\uff0c\u670d \u4e86\uff01\u201d \u3000\u3000\u4e0d\u4f46\u8d2f\u5c45\u4eb2\u53e3\u8bf4\u51fa\u201c\u670d\u201d\u5b57\uff0c\u573a\u4e2d\u5404\u8def\u82f1\u96c4\u4ea6\u65e0\u4e0d\u5fc3\u6298\uff0c\u6bd5\u64ce\u5929\u7b11\u9053\uff1a\u201c\u8d2f\u5927\u4eba\uff0c\u4f60\u53ef \u4ee5\u8d70\u4e86\uff0c\u4e0d\u8fc7\u4f60\u8fd9\u4e00\u8eab\u4e8c\u54c1\u5927\u5458\u7684\u9876\u6234\u670d\u9970\uff0c\u4e00\u5230\u5916\u9762\uff0c\u8fd8\u662f\u6362\u4e86\u7684\u597d\u3002\u613f\u7a77\uff0c\u4f60\u9001\u4ed6\u51fa \u95e8\u3002\u201d\u8d2f\u5c45\u5728\u5b98\u573a\u6df7\u5f97\u4e45\u4e86\uff0c\u4e0d\u81ea\u89c9\u7684\u53cc\u817f\u5e76\u62e2\uff0c\u5782\u624b\u5e94\u9053\uff1a\u201c\u662f\uff0c\u8c22\u671d\u5ef7\uff0c\u4e0d\uff0c\u8c22\u5be8\u4e3b\u6069 \u5178\uff01\u201d\u7adf\u662f\u4e00\u526f\u4e0b\u5c5e\u5bf9\u4e0a\u53f8\u7684\u53e3\u543b\uff0c\u7eff\u6797\u7fa4\u8c6a\uff0c\u65e0\u4e0d\u5931\u7b11\u3002\u6bd5\u613f\u7a77\u563b\u563b\u54c8\u54c8\uff0c\u4e24\u624b\u4f5c\u51fb\u9f13\u4e4b \u72b6\uff0c\u53e3\u4e2d\u559d\u9053\uff1a\u201c\u54c8\uff0c\u54c8\uff0c\u54c8\uff01\u201d\u5531\u4e00\u58f0\uff0c\u6253\u4e00\u4e0b\uff0c\u5927\u53eb\u9053\uff1a\u201c\u5927\u8001\u7237\u9000\u5802\u5566\uff0c\u54c8\uff0c\u54c8\uff01\u201d \u8d2f\u5c45\u54ed\u7b11\u4e0d\u5f97\uff0c\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u522b\u95f9\u5566\uff01\u201d\u6a0a\u82f1\u9053\uff1a\u201c\u6211\u4e5f\u9001\u4e8c\u5f1f\u4e00\u7a0b\u3002\u201d\u6bd5\u64ce\u5929\u76ef\u4e86\u6a0a\u82f1\u4e00 \u773c\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u8001\u6a0a\uff0c\u4f60\u4eec\u54e5\u513f\u4fe9\u53ef\u4e0d\u8981\u8d70\u5230\u4e00\u8def\u5475\uff0c\u6211\u8fd8\u5728\u8fd9\u4e9b\u7b49\u4f60\u56de\u6765\u3002\u201d\u6a0a\u82f1\u5fc3\u4e2d\u4e00 \u51db\uff0c\u6b64\u8bdd\u5927\u6709\u6df1\u610f\uff0c\u4e8e\u662f\u4e5f\u4ef0\u5929\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u7b11\u9053\uff1a\u201c\u6211\u5f53\u7136\u8fd8\u8981\u56de\u6765\uff0c\u6bd5\u5be8\u4e3b\uff0c\u4f60\u653e\u5fc3 \u597d\u5566\uff01\u201d \u3000\u3000\u6a0a\u82f1\u4e0e\u6bd5\u613f\u7a77\u9001\u5230\u95e8\u5916\uff0c\u6a0a\u82f1\u6267\u7740\u8d2f\u5c45\u7684\u624b\uff0c\u542b\u6cea\u8bf4\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u8fd9\u56de\u56e0\u7978\u5f97\u798f\uff0c\u4ee5 \u540e\u597d\u597d\u505a\u4eba\u624d\u662f\u5475\u3002\u201d\u8d2f\u5c45\u89c1\u6a0a\u82f1\u5982\u6b64\u4e3a\u4ed6\u51fa\u529b\uff0c\u5fc3\u4e2d\u4e0d\u65e0\u611f\u52a8\uff0c\u9053\uff1a\u201c\u5c0f\u5f1f\u542c\u5927\u54e5\u7684\u8bad \u8bf2\u3002\u201d\u6bd5\u613f\u7a77\u563b\u563b\u54c8\u54c8\u5730\u5531\u4e86\u4e2a\u8bfa\uff0c\u9053\uff1a\u201c\u8bf7\u5927\u4eba\u66f4\u8863\u3002\u201d\u88c5\u6a21\u4f5c\u6837\u5730\u5448\u4e0a\u4e00\u4e2a\u5305\u88b1\uff0c\u8d2f\u5c45 \u5c34\u5c2c\u4e4b\u6781\uff0c\u6253\u5f00\u4e00\u770b\uff0c\u5185\u4e2d\u662f\u4e00\u5957\u5e73\u6c11\u7684\u4fbf\u670d\uff0c\u8d2f\u5c45\u7684\u5b98\u5df2\u88ab\u671d\u5ef7\u5f00\u9769\uff0c\u518d\u7a7f\u5b98\u670d\uff0c\u90a3\u4fbf\u662f \u72af\u4e86\u5f8b\u4f8b\uff0c\u56e0\u6b64\u8fd9\u5957\u4fbf\u670d\u6b63\u5408\u4ed6\u7528\u3002\u5fc3\u4e2d\u867d\u5f88\u96be\u8fc7\uff0c\u5374\u4e5f\u4e0d\u80fd\u4e0d\u611f\u6fc0\u6bd5\u64ce\u5929\u66ff\u4ed6\u8bbe\u60f3\u5f97\u5468 \u5230\u3002 \u3000\u3000\u6a0a\u82f1\u9001\u4e86\u8d2f\u5c45\u56de\u6765\uff0c\u6bd5\u64ce\u5929\u5df2\u6b63\u5f0f\u5c31\u4e86\u5927\u9f99\u5934\u4e4b\u4f4d\uff0c\u6709\u82e5\u5e72\u7ea0\u7eb7\uff0c\u4e5f\u5f53\u573a\u89e3\u51b3\u4e86\u3002\u5176\u4e2d \u6709\u4e00\u5b97\u662f\u6cb3\u5357\u7684\u72ec\u884c\u5927\u76d7\u9c81\u4e0d\u90aa\u5077\u4e86\u6210\u4eb2\u738b\u7684\u4e00\u9876\u73cd\u73e0\u51a0\uff0c\u6210\u4eb2\u738b\u8d23\u6210\u4e00\u4e2a\u8001\u6355\u5934\u8ffd\u6355\uff0c\u8fd9 \u8001\u6355\u5934\u5411\u6bd5\u64ce\u5929\u7980\u660e\u4e86\u82e6\u54c0\uff0c\u6bd5\u64ce\u5929\u7acb\u523b\u66ff\u4ed6\u53d6\u56de\uff0c\u8fd8\u6709\u51e0\u6869\u4e8b\u60c5\uff0c\u4e5f\u5904\u7406\u5f97\u751a\u4e3a\u516c\u5e73\u5408 \u7406\uff0c\u679c\u7136\u6709\u5927\u9f99\u5934\u7684\u98ce\u5ea6\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u6a0a\u82f1\u548c\u90a3\u767d\u8863\u5c11\u5e74\u4fbf\u5728\u5e84\u4e2d\u4f4f\u5bbf\uff0c\u6a0a\u82f1\u4e00\u665a\u6ca1\u6709\u597d\u7761\uff0c\u601d\u6765\u60f3\u53bb\uff0c\u53ea\u89c9\u8bb8\u591a\u4e8b\u60c5 \u90fd\u602a\u4e0d\u53ef\u89e3\uff0c\u4f8b\u5982\u767d\u8863\u5c11\u5e74\u4e3a\u4f55\u8981\u5343\u91cc\u8ffd\u8e2a\uff0c\u4e00\u5b9a\u8981\u53d6\u56de\u4e8e\u8c26\u7684\u9996\u7ea7\uff1f\u4ed6\u7684\u8eab\u4e16\uff0c\u4e3a\u4f55\u534a\u70b9 \u4e0d\u80af\u900f\u9732\uff1f\u6bd5\u64ce\u5929\u4e0e\u4ed6\u4f3c\u662f\u76f8\u8bc6\uff0c\u4f46\u53c8\u4e0d\u4f3c\u67cf\u8bc6\uff0c\u6bd5\u64ce\u5929\u5047\u501f\u6b66\u5e84\u4e3b\u4e4b\u540d\uff0c\u5c06\u4ed6\u4eec\u8bf7\u4e86\u6765\uff0c \u76ee\u7684\u662f\u4e0d\u662f\u5c31\u53ea\u4e3a\u7740\u4e86\u8fd9\u4e24\u6869\u516c\u6848\uff1f \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\u8d77\u6765\uff0c\u6bd5\u64ce\u5929\u5df2\u6d3e\u4eba\u524d\u6765\u76f8\u8bf7\uff0c\u6a0a\u82f1\u968f\u7740\u6765\u4eba\uff0c\u8d70\u8fdb\u6b66\u5bb6\u5e84\u56ed\uff0c\u53ea\u89c1\u6bd5\u64ce\u5929 \u548c\u767d\u8863\u5c11\u5e74\u5df2\u5728\u90a3\u91cc\u76f8\u5019\uff0c\u53e6\u5916\u8fd8\u6709\u6b66\u8001\u5e84\u4e3b\u548c\u51e0\u4f4d\u6b66\u6797\u4e2d\u7684\u6210\u540d\u4eba\u7269\uff0c\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u6211\u8bf7 \u5404\u4f4d\u6765\u505a\u4e2a\u89c1\u8bc1\u3002\u8fd9\u4f4d\u5c0f\u54e5\u8981\u6211\u8fd8\u4ed6\u4e00\u9897\u4eba\u5934\uff0c\u4eba\u5934\u662f\u6211\u62ff\u4e86\uff0c\u4f46\u5982\u4eca\u4e0d\u4fbf\u53d6\u4e0b\uff0c\u6211\u5206\u5916\u8fd8 \u4ed6\u4e00\u5177\u88c5\u6709\u5168\u5c38\u7684\u68fa\u6750\uff0c\u8fd9\u4f4d\u5c0f\u54e5\u8981\u662f\u8fd8\u4e0d\u6ee1\u610f\uff0c\u90a3\u6211\u5c31\u6ca1\u6709\u529e\u6cd5\u4e86\u3002\u201d\u5728\u573a\u7684\u9664\u4e86\u6a0a\u82f1\u4e0e \u6b66\u632f\u4e1c\u4e4b\u5916\uff0c\u5176\u4ed6\u90fd\u83ab\u660e\u5176\u5999\u3002 \u3000\u3000\u4f17\u4eba\u968f\u6bd5\u64ce\u5929\u8d70\u8fc7\u4e00\u6761\u66f2\u66f2\u6298\u6298\u7684\u7528\u9053\uff0c\u5230\u4e86\u82b1\u56ed\u7684\u5c3d\u5934\u3002\u4e00\u95f4\u7070\u767d\u7684\u5c0f\u5c4b\u5b50\u5b64\u96f6\u96f6\u5730 \u9760\u5728\u89d2\u843d\uff0c\u7a97\u6237\u4e4b\u95f4\u6709\u8885\u8885\u9999\u70df\u98d8\u51fa\uff0c\u4f17\u4eba\u90fd\u662f\u4e00\u6014\uff0c\u4f46\u89c1\u6bd5\u64ce\u5929\u63a8\u5f00\u4e86\u95e8\uff0c\u6df1\u6c89\u5730\u5bf9\u767d\u8863 \u5c11\u5e74\u8bf4\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u6211\u4e0d\u662f\u90fd\u66ff\u4f60\u529e\u59a5\u4e86\u5417\uff1f\u201d \u3000\u3000\u53ea\u89c1\u5c4b\u5185\u4e00\u5177\u94dc\u68fa\uff0c\u5f53\u4e2d\u4e00\u5f20\u4f9b\u6848\uff0c\u7089\u9999\u8885\u8885\uff0c\u4e0a\u9762\u6709\u4e00\u5757\u5199\u7740\u201c\u9601\u90e8\u5927\u81e3\u4e8e\u8c26\u201d\u7684\u7075 \u4f4d\uff0c\u68fa\u524d\u4e00\u4e2a\u8001\u592a\u76d1\uff0c\u767d\u53d1\u8427\u8427\uff0c\u89c1\u4f17\u4eba\u8fdb\u6765\uff0c\u6b8a\u65e0\u60ca\u8be7\u4e4b\u610f\uff0c\u53ea\u662f\u5f53\u4ed6\u7684\u773c\u5149\u626b\u5230\u4e86\u767d\u8863 \u5c11\u5e74\u9762\u4e0a\u4e4b\u65f6\uff0c\u5374\u5ffd\u5730\u8f7b\u8f7b\u201c\u566b\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u9762\u5bb9\u6c89\u8083\uff0c\u7f13\u7f13\u4e0a\u524d\uff0c\u5c06\u94dc\u68fa\u63ed\u8d77\uff0c\u539f\u6765\u91cc\u9762\u8fd8\u6709\u4e00\u5177\u6c34\u6676\u68fa\u6750\uff0c\u5341\u5206\u7cbe\u81f4\uff0c\u90a3 \u94dc\u68fa\u56db\u8fb9\u90fd\u53ef\u4ee5\u5f00\u5173\uff0c\u7b49\u4e8e\u90a3\u6c34\u6676\u68fa\u7684\u68fa\u7f69\uff0c\u6bd5\u64ce\u5929\u5c06\u94dc\u68fa\u892a\u4e0b\uff0c\u4f46\u89c1\u6c34\u6676\u68fa\u5185\uff0c\u8eba\u7740\u4e00\u5177 \u5c38\u4f53\uff0c\u87d2\u888d\u7389\u5e26\uff0c\u9876\u6234\u6781\u54c1\u671d\u51a0\uff0c\u60f3\u662f\u5185\u4e2d\u653e\u6709\u9632\u8150\u7684\u5999\u836f\uff0c\u9762\u76ee\u72b9\u81ea\u6829\u6829\u5982\u751f\uff0c\u53ea\u662f\u9888\u9879 \u4e4b\u95f4\u6709\u4e00\u6761\u7ea2\u7ebf\uff0c\u770b\u5f97\u51fa\u662f\u65ad\u9996\u4e4b\u540e\u7f1d\u4e0a\u53bb\u7684\uff0c\u8fd9\u6b63\u662f\u53cc\u624b\u633d\u56de\u5927\u660e\u56fd\u8fd0\uff0c\u5374\u88ab\u4ed6\u6551\u56de\u6765\u7684 \u5f53\u4eca\u7687\u4e0a\u60e8\u6740\u4e86\u7684\u9601\u90e8\u5927\u81e3\u4e8e\u8c26\u3002 \u3000\u3000\u6a0a\u82f1\u4e00\u76f4\u62e5\u5728\u7559\u5fc3\u90a3\u767d\u8863\u5c11\u5e74\uff0c\u8fd9\u65f6\u53ea\u89c1\u4ed6\u5ffd\u5730\u9762\u5bb9\u5927\u53d8\uff0c\u4e00\u8dc3\u4e0a\u524d\uff0c\u530d\u4f0f\u5728\u68fa\u6750\u524d \u9762\uff0c\u5927\u653e\u60b2\u58f0\uff0c\u54ed\u9053\uff1a\u201c\u597d\u82e6\u547d\u7684\u7239\u7239\u5440\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u90fd\u662f\u610f\u6599\u4e0d\u5230\uff0c\u539f\u6765\u8fd9\u767d\u8863\u5c11\u5e74\uff0c\u7adf\u662f\u4e8e\u8c26\u7684\u513f\u5b50\uff01\u5373\u7b97\u6a0a\u82f1\uff0c\u867d\u7136\u65e9 \u5c31\u6599\u5230\u767d\u8863\u5c11\u5e74\u4e0e\u4e8e\u8c26\u5927\u6709\u5173\u7cfb\u3002\u5982\u4e5f\u731c\u4e0d\u5230\u4ed6\u4eec\u7adf\u662f\u7236\u5b50\u4e4b\u4eb2\u3002\u970e\u65f6\u95f4\u6709\u597d\u51e0\u4e2a\u7591\u95ee\u4ece\u5fc3 \u5934\u5347\u8d77\uff0c\u4e8e\u8c26\u4f4d\u6781\u4eba\u81e3\uff0c\u4ed6\u7684\u513f\u5b50\u5374\u600e\u5730\u5728\u6c5f\u6e56\u98d8\u8361\uff1f\u90a3\u8eab\u60ca\u4eba\u7684\u6b66\u529f\u53c8\u662f\u8c01\u4eba\u6240\u6388\uff1f \u3000\u3000\u4e8e\u8c26\u7cbe\u5fe0\u62a5\u56fd\uff0c\u5929\u4e0b\u540c\u94a6\uff0c\u4f17\u4eba\u90fd\u4e0d\u81ea\u7981\u5730\u968f\u7740\u767d\u8863\u5c11\u5e74\u5411\u4e8e\u8c26\u7684\u9063\u4f53\u8dea\u4e0b\u884c\u793c\uff0c\u540c\u653e \u60b2\u58f0\u3002\u767d\u8863\u5c11\u5e74\u66f4\u662f\u54ed\u5f97\u6b7b\u53bb\u6d3b\u6765\uff0c\u6e10\u6e10\u54ed\u58f0\u5636\u7aed\u6cea\u73e0\u5982\u7ebf\uff0c\u731b\u5730\u62ac\u5934\uff0c\u5ffd\u89c1\u7075\u4f4d\u4e0a\u8fb9\u7684\u5899 \u58c1\uff0c\u6302\u7740\u4e00\u5f20\u6761\u5e45\uff0c\u5199\u7684\u662f\u4e00\u9996\u4e03\u8a00\u7edd\u53e5\uff0c\u8bd7\u9053\uff1a\u201c\u5343\u9524\u4e07\u51fb\u51fa\u6df1\u5c71\uff0c\u70c8\u706b\u711a\u70e7\u82e5\u7b49\u95f2\uff0c\u7c89 \u9aa8\u788e\u8eab\u6d51\u4e0d\u6015\uff0c\u8981\u7559\u6e05\u767d\u5728\u4eba\u95f4\uff01\u201d\u6b63\u662f\u4ed6\u7236\u4eb2\u751f\u524d\u501f\u548f\u77f3\u7070\u4ee5\u8a00\u5fc3\u5fd7\u7684\u8bd7\u53e5\uff0c\u8fd9\u8bd7\u7a3f\u4e0d\u77e5 \u6bd5\u64ce\u5929\u4ece\u4f55\u5904\u5f97\u6765\uff0c\u88f1\u7cca\u5728\u6b64\uff1f\u767d\u8863\u5c11\u5e74\u6cea\u73e0\u65ad\u65ad\u7eed\u7eed\uff0c\u5ffd\u5730\u54d1\u58f0\u72c2\u7b11\uff1a\u201c\u7c89\u9aa8\u788e\u8eab\u90fd\u4e0d \u6015\uff0c\u8981\u7559\u6e05\u767d\u5728\u4eba\u95f4\uff0c\u7239\u7239\u5475\uff0c\u4f60\u8fd9\u4e00\u6b7b\uff0c\u5343\u53e4\u7559\u540d\uff0c\u4f46\u5374\u53c8\u6b7b\u5f97\u591a\u4e48\u4e0d\u503c\u5475\uff01\u201d\u7b11\u5230\u540e \u6765\uff0c\u53c8\u53d8\u6210\u54ed\u58f0\uff0c\u6e10\u6e10\u54ed\u7b11\u4e0d\u5206\uff0c\u663e\u662f\u795e\u667a\u660f\u8ff7\uff0c\u5fc3\u4e2d\u4f24\u75db\u4e4b\u6781\uff01 \u3000\u3000\u6bd5\u64ce\u5929\u5374\u5e76\u4e0d\u968f\u4f17\u8dea\u62dc\uff0c\u4e5f\u4e0d\u653e\u58f0\u75db\u54ed\uff0c\u53ea\u662f\u5728\u7075\u524d\u6dfb\u4e86\u6ce8\u9999\uff0c\u53e9\u4e86\u4e2a\u5934\uff0c\u4ed6\u4e5f\u4e00\u76f4\u6ce8 \u89c6\u767d\u8863\u5c11\u5e74\uff0c\u8fd9\u65f6\u5ffd\u7136\u8a00\u9053\uff1a\u201c\u66f9\u516c\u516c\uff0c\u4e8e\u8c26\u54ea\u513f\u6765\u7684\u8fd9\u4e2a\u513f\u5b50\uff1f\u201d\u90a3\u592a\u76d1\u77a5\u4e86\u767d\u8863\u5c11\u5e74\u4e00 \u773c\uff0c\u6b32\u8bf4\u8fd8\u4f11\uff0c\u767d\u8863\u5c11\u5e74\u5ffd\u5730\u7ffb\u8eab\u8df3\u8d77\uff0c\u6012\u9053\uff1a\u201c\u4f60\u66ff\u6211\u6536\u6b93\u4e86\u7236\u4eb2\uff0c\u6211\u8fd9\u4e00\u751f\u90fd\u611f\u6fc0\u4f60\u7684 \u5927\u5fb7\u3002\u4f46\u4f60\u8bf4\u4ec0\u4e48\uff1f\u5929\u4e0b\u54ea\u6709\u5192\u8ba4\u7236\u5b50\u4e4b\u7406\uff1f\u201d\u4f17\u4eba\u4eb2\u89c1\u767d\u8863\u5c11\u5e74\u7684\u60b2\u75db\u4e4b\u60c5\uff0c\u786e\u662f\u771f\u60c5\u6d41 \u9732\uff0c\u5047\u5192\u4e0d\u6765\uff0c\u90fd\u5728\u5947\u602a\uff0c\u4f55\u4ee5\u6bd5\u64ce\u5929\u8bf4\u8bdd\u5982\u6b64\u8fdd\u80cc\u4eba\u60c5\uff1f\u4e0d\u5b89\u6170\u4e5f\u8fd8\u7f62\u4e86\uff0c\u5374\u53cd\u800c\u4f24\u4e86\u5b5d \u5b50\u7684\u5fc3\u3002 \u3000\u3000\u90a3\u592a\u76d1\u6276\u7740\u68fa\u6750\uff0c\u9762\u5bf9\u7740\u6bd5\u64ce\u5929\u548c\u767d\u8863\u5c11\u5e74\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u7684\u7239\u7239\u5c31\u662f\u4e8e\u5927 \u4eba\u3002\u201d\u767d\u8863\u5c11\u5e74\u521a\u624d\u5168\u795e\u6ce8\u89c6\u4e8e\u8c26\u7684\u9057\u4f53\uff0c\u8fd9\u65f6\u624d\u53d1\u89c9\u8001\u592a\u76d1\u5728\u65c1\uff0c\u56db\u76ee\u76f8\u4ea4\uff0c\u767d\u8863\u5c11\u5e74\u773c \u775b\u4e00\u7728\uff0c\u4f3c\u4e4e\u60f3\u8bf4\u4ec0\u4e48\u5374\u53c8\u6ca1\u8bf4\u3002\u6a0a\u82f1\u5728\u4fa7\u9762\u770b\u5f97\u6e05\u695a\uff0c\u6bd5\u64ce\u5929\u5728\u80cc\u540e\u5374\u77a7\u4e0d\u89c1\u4ed6\u7684\u795e\u60c5\uff0c \u89c1\u8001\u592a\u76d1\u5982\u6b64\u8bf4\u6cd5\uff0c\u5fc3\u4e2d\u9887\u662f\u8be7\u5f02\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5374\u8bf4\u9053\uff1a\u201c\u4e8e\u5144\uff0c\u65e2\u7136\u4e8e\u5927\u4eba\u4e43\u662f\u4ee4\u5c0a\uff0c \u90a3\u5c31\u8bf7\u6028\u5728\u4e0b\u5931\u8a00\u3002\u8bf7\u95ee\u4e8e\u5144\u51c6\u5907\u5c06\u4ee4\u5c0a\u91d1\u4f53\u5982\u4f55\u5904\u7f6e\uff1f\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u4e0d\u8fc7\u662f\u4e2a\u5341\u516d\u5c81\u7684\u5927\u5b69\u5b50\uff0c\u672a\u61c2\u4e16\u4e8b\uff0c\u52a0\u4ee5\u4f24\u75db\u672a\u5df2\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e5f\u672a\u66fe\u60f3\u5230 \u5982\u4f55\u529e\u7406\u540e\u4e8b\uff0c\u88ab\u6bd5\u64ce\u5929\u9661\u7136\u4e00\u95ee\uff0c\u4e00\u65f6\u7b54\u4e0d\u4e0a\u6765\uff0c\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u542c\u66f9\u516c\u516c\u8a00\u53ca\uff0c\u4ee4\u5c0a\u5927\u4eba \u751f\u524d\u6700\u559c\u7231\u676d\u5dde\uff0c\u4e34\u6b7b\u9057\u8a00\uff0c\u613f\u57cb\u5728\u540d\u5c71\u4e4b\u4e0b\uff0c\u4e0e\u5cb3\u575f\u4e3a\u4f34\u3002\u5982\u82e5\u4e8e\u5144\u76f8\u4fe1\u5728\u4e0b\uff0c\u5728\u4e0b\u4e00\u5b9a \u80fd\u9075\u7167\u4ee4\u5c0a\u5927\u4eba\u7684\u9057\u5fd7\uff0c\u5c06\u4ed6\u5b89\u846c\u676d\u5dde\u3002\u201d\u767d\u8863\u5c11\u5e74\u89c1\u4ed6\u66ff\u81ea\u5df1\u529e\u5f97\u5982\u6b64\u5468\u5230\uff0c\u8f6c\u8eab\u53eb\u4e86\u4e00 \u53e5\u201c\u6069\u516c\u3002\u201d\u4fbf\u6b32\u65bd\u793c\uff0c\u6bd5\u64ce\u5929\u53cc\u624b\u4e00\u6276\uff0c\u9053\uff1a\u201c\u4f60\u8be5\u591a\u8c22\u8fd9\u4f4d\u516c\u516c\u3002\u201d\u767d\u8863\u5c11\u5e74\u8eab\u5b50\u4e00 \u7f29\uff0c\u5446\u5446\u5730\u770b\u7740\u90a3\u4e2a\u592a\u76d1\uff0c\u773c\u4e2d\u5145\u6ee1\u7591\u60d1\u7684\u795e\u60c5\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u8fd9\u4f4d\u66f9\u516c\u516c\u662f\u5185\u5ead\u7684\u4f8d\u8bfb\u592a\u76d1\uff0c\u4e13\u4f34\u592a\u5b50\u8bfb\u4e66\u3002\u5f53\u4eca\u7684\u7687\u5e1d\u5728\u505a\u592a\u5b50\u4e4b\u65f6 \u4e5f\u662f\u4ed6\u4f8d\u8bfb\u7684\u3002\u4ed6\u5728\u5bab\u4e2d\u4e09\u56db\u5341\u5e74\uff0c\u4ee5\u524d\u7687\u5e1d\u6709\u4ec0\u4e48\u7269\u4e8b\u8981\u8d4f\u8d50\u5927\u81e3\uff0c\u591a\u9063\u4ed6\u524d\u5f80\uff0c\u60f3\u5fc5\u4e5f \u66fe\u5230\u8fc7\u4f60\u5bb6\uff1f\u201d\u767d\u8863\u5c11\u5e74\u542b\u7cca\u5e94\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5982\u6b64\u773c\u719f\u3002\u60f3\u6765\u662f\u89c1\u8fc7\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u201d \u3000\u3000\u6bd5\u64ce\u5929\u7eed\u9053\uff1a\u201c\u8fd9\u4f4d\u66f9\u516c\u516c\u6781\u94a6\u656c\u4f60\u7236\u4e3a\u4eba\uff0c\u4ed6\u820d\u4e86\u6027\u547d\u6073\u6c42\u7687\u5e1d\u51c6\u4ed6\u6536\u6b93\u4f60\u7236\u9057\u9ab8\uff0c \u5176\u65f6\u4ee4\u5c0a\u5927\u4eba\u7684\u9996\u7ea7\u5df2\u7ed9\u6211\u76d7\u53bb\uff0c\u7687\u5e1d\u8001\u5b50\u4e5f\u77e5\u7fa4\u60c5\u6c79\u6d8c\uff0c\u4fbf\u4e50\u5f97\u505a\u4e2a\u987a\u6c34\u4eba\u60c5\uff0c\u6279\u9053\uff1a\u59d1 \u5ff5\u4e8e\u8c26\u4e43\u4e24\u671d\u5143\u8001\uff0c\u51c6\u4e88\u6536\u6b93\u3002\u8fd9\u6837\u66f9\u516c\u516c\u624d\u5f97\u5c06\u4ed6\u7684\u5c38\u4f53\u8fd0\u51fa\u6765\uff0c\u662f\u6211\u622a\u7740\u4e86\u4ed6\uff0c\u5c06\u5c38\u9996 \u5408\u4e00\uff0c\u804a\u5c3d\u4e00\u70b9\u5fc3\u610f\u3002\u66f9\u516c\u516c\u4e5f\u6253\u7b97\u6b64\u540e\u7ec8\u8001\u6b64\u95f4\uff0c\u4e0d\u518d\u56de\u671d\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u70ed\u6cea\u76c8\u7736\uff0c\u60f3\u5230\u6bd5\u64ce\u5929\u4e3a\u4ed6\u7236\u4eb2\u5982\u6b64\u5c3d\u529b\uff0c\u800c\u81ea\u5df1\u5982\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\uff0c\u53cd\u800c\u8bef\u4f1a \u4e86\u4ed6\u7684\u597d\u610f\uff0c\u5fc3\u4e2d\u6b49\u759a\uff0c\u6bd5\u64ce\u5929\u867d\u7136\u4e0d\u80af\u53d7\u4ed6\u5927\u793c\uff0c\u4ed6\u4ea6\u4e00\u518d\u9053\u8c22\u3002\u540e\u6765\u6bd5\u64ce\u5929\u8bf7\u66f9\u592a\u76d1\u51fa \u9762\uff0c\u679c\u7136\u6d3e\u4eba\u5c06\u4e8e\u8c26\u7684\u7075\u68fa\u8fd0\u5230\u676d\u5dde\uff0c\u7b51\u57fa\u5b89\u846c\u3002\u540e\u4eba\u5f20\u82cd\u6c34\uff08\u660e\u672a\u7684\u5927\u5fe0\u81e3\uff09\u6709\u8bd7\u65e5\uff1a \u201c\u56fd\u7834\u5bb6\u4ea1\u6b32\u4f55\u4e4b\uff1f\u897f\u5b50\u6e56\u5934\u6709\u6211\u5e08\uff0c\u65e5\u6708\u53cc\u60ac\u4e8e\u6c0f\u5893\uff0c\u4e7e\u5764\u534a\u58c1\u5cb3\u5bb6\u7960\u3002\u201d\u4fbf\u662f\u5c06\u4e8e\u3001\u5cb3 \u4e8c\u4eba\u76f8\u63d0\u5e76\u8bba\u7684\uff0c\u8fd9\u662f\u95f2\u8bdd\uff0c\u8868\u8fc7\u4e0d\u63d0\u3002 \u3000\u3000\u4e14\u8bf4\u767d\u8863\u5c11\u5e74\u4e00\u518d\u5411\u6bd5\u64ce\u5929\u9053\u8c22\uff0c\u6bd5\u64ce\u5929\u5ffd\u9053\uff1a\u201c\u4e8e\u5927\u4eba\u4e00\u7247\u5fe0\u8d1e\uff0c\u81ea\u662f\u540d\u7559\u9752\u53f2\uff0c\u4f46 \u4f9d\u6bd5\u67d0\u770b\u6765\uff0c\u4ee4\u5c0a\u5374\u4e5f\u8fd8\u672a\u7b97\u5f97\u662f\u4e2a\u901a\u4eba\uff0c\u66f4\u672a\u7b97\u5f97\u662f\u4e2a\u8c6a\u6770\uff01\u201d\u767d\u8863\u5c11\u5e74\u9762\u8272\u4e00\u53d8\uff0c\u5fc3\u4e2d \u6781\u4e0d\u8212\u670d\uff0c\u6a0a\u82f1\u4ea6\u89c9\u6bd5\u64ce\u5929\u6b64\u8bdd\u5b9e\u662f\u5931\u8a00\uff0c\u62a2\u7740\u95ee\u9053\uff1a\u201c\u6bd5\u5927\u9f99\u5934\uff0c\u6b64\u8bdd\u600e\u8bf4\uff1f\u201d\u6bd5\u64ce\u5929\u54c8 \u54c8\u4e00\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u4ed6\u53ea\u662f\u5fe0\u81e3\uff0c\u82e5\u7136\u4ed6\u771f\u662f\u82f1\u96c4\u8c6a\u6770\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u843d\u5f97\u4eca\u65e5\u7684\u6789\u6b7b\u4e86\u3002\u201d \u3000\u3000\u6bd5\u64ce\u5929\u4f83\u4f83\u800c\u8c08\uff0c\u4e00\u53e3\u6c14\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u82e5\u771f\u662f\u8bfb\u901a\u4e86\u7684\u4eba\uff0c\u5c82\u4e0d\u95fb\uff1a\u5929\u4e0b\u8005\u4e43\u5929\u4e0b\u4eba\u4e4b \u5929\u4e0b\u4e5f\uff0c\u5e76\u975e\u6ce8\u5b9a\u662f\u4e00\u59d3\u4e00\u5bb6\u7684\u79c1\u4ea7\uff0c\u79e6\u59cb\u7687\u65e0\u9053\uff0c\u9879\u7fbd\u8bf4\uff1a\u5f7c\u53ef\u53d6\u800c\u4ee3\u4e4b\uff01\u8fd9\u624d\u662f\u5927\u82f1\u96c4 \u771f\u8c6a\u6770\uff01\u201d\u6a0a\u82f1\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u4eba\u53e3\u6c14\u597d\u5927\uff01\u770b\u6765\u5176\u5fd7\u4e0d\u5728\u4e8e\u505a\u4e00\u4e2a\u5927\u9f99\u5934\uff0c\u800c\u662f\u8981\u548c\u6731\u5bb6\u4e89 \u593a\u5927\u660e\u7684\u5929\u4e0b\u4e86\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u60f3\u505a\u7687\u5e1d\uff0c\u54fc\uff0c\u6c5f\u5c71\u4ee3\u6709\u82f1\u96c4\u51fa\uff0c\u5404\u82e6\u751f\u6c11\u6570\u5341\u5e74\uff01\u60f3 \u79f0\u738b\u79f0\u9738\u7684\u4eba\u4e5f\u4e0d\u89c1\u5f97\u5c31\u662f\u771f\u82f1\u96c4\u5927\u8c6a\u6770\u3002\u201d\u8fd9\u56de\u8f6e\u5230\u6bd5\u64ce\u5929\u9762\u8272\u4e00\u53d8\uff0c\u53ea\u542c\u5f97\u767d\u8863\u5c11\u5e74\u7eed \u9053\uff1a\u201c\u6709\u4eba\u5927\u6709\u673a\u4f1a\u505a\u7687\u5e1d\uff0c\u4ed6\u5374\u8584\u5929\u5b50\u800c\u4e0d\u4e3a\uff0c\u8fd9\u624d\u662f\u82f1\u96c4\u8c6a\u6770\u7684\u80f8\u895f\u3002\u201d\u6a0a\u82f1\u8131\u624b\u9053\uff1a \u201c\u4f60\u662f\u8bf4\u5f20\u5927\u4fa0\u5f20\u4e39\u67ab\uff01\u201d\u6bd5\u64ce\u5929\u52c3\u7136\u53d8\u8272\uff0c\u6b66\u632f\u4e1c\u63d2\u53e3\u9053\uff1a\u201c\u6b64\u4e00\u65f6\u5f7c\u4e00\u65f6\uff0c\u5f20\u4e39\u67ab\u81ea\u662f \u82f1\u96c4\uff0c\u4f46\u82e5\u5728\u4eca\u65e5\uff0c\u4e5f\u4e0d\u89c1\u5f97\u8fd8\u613f\u4e00\u5fc3\u6276\u6301\u660e\u5ba4\u3002\u201d\u767d\u8863\u5c11\u5e74\u4e00\u9635\u8ff7\u832b\uff0c\u6b63\u81ea\u601d\u7d22\uff0c\u5ffd\u542c\u5f97 \u6bd5\u64ce\u5929\u51b2\u53e3\u9a82\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u662f\u4ec0\u4e48\u82f1\u96c4\uff1f\u6211\u8bf4\u4ed6\u662f\u4e0d\u8096\u5b50\u5b59\uff0c\u884c\u4e8b\u4e56\u8c2c\uff0c\u6b3a\u4e16\u76d7\u540d\u7684\u5047\u4fa0 \u5ba2\uff01\u201d \u3000\u3000\u5f53\u65f6\u5f20\u4e39\u67ab\u540d\u6ee1\u5929\u4e0b\uff0c\u8c01\u4e0d\u94a6\u656c\uff0c\u6bd5\u64ce\u5929\u6b64\u8a00\u4e00\u51fa\uff0c\u6ee1\u5ea7\u5931\u8272\uff0c\u6a0a\u82f1\u6b63\u60f3\u51fa\u58f0\uff0c\u53ea\u89c1\u767d \u8863\u5c11\u5e74\u6012\u5bb9\u6ee1\u9762\uff0c\u53f1\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u6562\u9a82\u5f20\u5927\u4fa0\uff01\u201d\u500f\u5730\u5bd2\u5149\u4e00\u95ea\uff0c\u4ed6\u51fa\u5251\u5feb\u5982\u95ea \u7535\uff0c\u4e00\u6296\u624b\u5c31\u5411\u6bd5\u64ce\u5929\u6b63\u5728\u5f20\u5f00\u7684\u5634\u5df4\u523a\u53bb\uff01 \u3000\u3000\u6bd5\u64ce\u5929\u539f\u662f\u56e0\u4e3a\u89c1\u8fd9\u767d\u8863\u5c11\u5e74\u6b66\u529f\u6781\u9ad8\uff0c\u53c8\u662f\u4e8e\u8c26\u4e4b\u540e\uff0c\u56e0\u6b64\u60f3\u5c06\u4ed6\u8bf4\u52a8\uff0c\u5171\u8c0b\u5927\u4e8b\uff0c \u4e0d\u6599\u4ed6\u7a81\u7136\u4e00\u5251\u523a\u6765\uff0c\u76f8\u8ddd\u53c8\u8fd1\uff0c\u8eb2\u5df1\u4e0d\u53ca\uff01 \u3000\u3000\u53ea\u542c\u5f97\u6bd5\u64ce\u5929\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u597d\u5475\uff01\u201d\u54b3\u5730\u4e00\u58f0\uff0c\u5251\u5df2\u523a\u5165\uff0c\u6a0a\u82f1\u4e5f\u5413\u5f97\u54c7\u7136\u5927\u547c\uff01\u51e0\u4e4e \u5c31\u5728\u540c\u4e00\u77ac\u95f4\uff0c\u6b66\u632f\u4e1c\u4f38\u638c\u4e00\u62cd\uff0c\u60f3\u628a\u4ed6\u7684\u5b9d\u5251\u62cd\u5f00\uff0c\u5ffd\u89c1\u767d\u8863\u5c11\u5e74\u8eab\u5b50\u5f80\u524d\u4e00\u503e\uff0c\u6b66\u632f\u4e1c \u7ad9\u5728\u4ed6\u7684\u4fa7\u8fb9\uff0c\u8fd9\u4e00\u638c\u539f\u662f\u671d\u4ed6\u7684\u624b\u8155\u62cd\u53bb\uff0c\u6599\u4e0d\u5230\u767d\u8863\u5c11\u5e74\u8eab\u5b50\u4e00\u503e\uff0c\u65b9\u4f4d\u7acb\u53d8\uff0c\u4ed6\u7684\u8eab \u4f53\u659c\u503e\uff0c\u8fd9\u4e00\u638c\u62cd\u4e0b\uff0c\u6b63\u5f53\u4ed6\u5de6\u8fb9\u7684\u592a\u9633\u7a74\uff0c\u638c\u529b\u4e00\u53d1\uff0c\u4fbf\u662f\u81f4\u547d\u4e4b\u4f24\uff01 \u3000\u3000\u6b66\u632f\u4e1c\u3001\u767d\u8863\u5c11\u5e74\u548c\u6bd5\u64ce\u5929\u7ad9\u5728\u4e00\u6392\uff0c\u8fd9\u4e00\u4e0b\u9aa4\u7136\u4e4b\u95f4\uff0c\u4e09\u4eba\u540c\u65f6\u53d1\u96be\uff0c\u5176\u4f59\u7684\u4eba\u8ddd\u79bb \u8f83\u8fdc\uff0c\u60f3\u89e3\u6551\u4e5f\u6765\u4e0d\u53ca\uff0c\u53ea\u89c1\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u7684\u5239\u90a3\uff0c\u6bd5\u64ce\u5929\u5f20\u53e3\u4e00\u55b7\uff0c\u75be\u9000\u6570\u6b65\uff0c\u55b7\u51fa\u4e00 \u53e3\u9c9c\u8840\uff0c\u5927\u9a82\u9053\uff1a\u201c\u4f60\u6740\u7236\u4e4b\u4ec7\u4e5f\u5fd8\u8bb0\u4e86\u5417\uff1f\u4f60\u7684\u5251\u4e0d\u53bb\u5241\u5f53\u4eca\u7684\u72d7\u7687\u5e1d\u53cd\u800c\u523a\u6211\u662f\u4f55\u9053 \u7406\uff1f\u201d\u539f\u6765\u9002\u624d\u767d\u8863\u5c11\u5e74\u672c\u662f\u60f3\u60e9\u6212\u4ed6\u4e00\u4e0b\uff0c\u5e76\u672a\u4f7f\u52b2\uff0c\u4e0d\u6599\u4ed6\u53cd\u800c\u5f20\u53e3\u8fce\u4e0a\u6765\uff0c\u54ac\u7740\u5251 \u5c16\uff0c\u7259\u5e8a\u8f6f\u8089\u7adf\u88ab\u5212\u4f24\u4e86\u3002\u767d\u8863\u5c11\u5e74\u614c\u5fd9\u62bd\u5251\uff0c\u800c\u6b66\u632f\u4e1c\u90a3\u4e00\u638c\u5df2\u7136\u62cd\u4e0b\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u6b63\u5728\u5927\u9a82\uff0c\u5ffd\u7136\u53e9\u963f\u5440\u5730\u5927\u53eb\u4e00\u58f0\uff0c\u4f17\u4eba\u7684\u76ee\u5149\u90fd\u968f\u7740\u4ed6\u770b\u5230\u767d\u8863\u5c11\u5e74\u7684\u5934\u4e0a\uff01 \u6b63\u662f\uff1a \u3000\u3000\u51fa\u8a00\u4e0d\u900a\u7f18\u4f55\u4e8b\uff1f\u5251\u523a\u5589\u5499\u602a\u4e8b\u751f\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u516d\u56de\u3000\u8d25\u5bc7\u6210\u738b\u3000\u9053\u65c1\u8c08\u53f2\u4e8b\u3000\u4f24\u5fc3\u60ca\u53d8\u3000\u6e56\u4e0a\u8d77\u98ce\u6ce2 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u516d\u56de\u3000\u8d25\u5bc7\u6210\u738b\u3000\u9053\u65c1\u8c08\u53f2\u4e8b\u3000\u4f24\u5fc3\u60ca\u53d8\u3000\u6e56\u4e0a\u8d77\u98ce\u6ce2 \u3000\u3000\u53ea\u89c1\u767d\u8863\u5c11\u5e74\u7684\u5e3d\u5b50\u5df2\u8dcc\u843d\u5730\u4e0a\uff0c\u65b9\u5dfe\u4ea6\u5df2\u6563\u5f00\uff0c\u9732\u51fa\u6ee1\u5934\u79c0\u53d1\uff01\u539f\u6765\u6b66\u632f\u4e1c\u867d\u7136\u6025\u5316 \u6536\u638c\uff0c\u4f46\u638c\u98ce\u5df1\u628a\u4ed6\u7684\u5e3d\u5b50\u4e0e\u65b9\u5dfe\u9707\u5f97\u8dcc\u843d\u6563\u5f00\uff0c\u4f17\u4eba\u56e0\u4e3a\u6bd5\u64ce\u5929\u53d7\u4f24\uff0c\u4e00\u65f6\u672a\u66fe\u6ce8\u610f\uff0c\u542c \u4e86\u6bd5\u64ce\u5929\u7684\u60ca\u53eb\u4e4b\u58f0\uff0c\u968f\u7740\u4ed6\u7684\u76ee\u5149\u770b\u5230\u767d\u8863\u5c11\u5e74\u5934\u4e0a\uff0c\u8fd9\u624d\u77e5\u9053\u4ed6\u7adf\u7136\u662f\u4e2a\u5c11\u5973\uff01 \u3000\u3000\u8fd9\u4e00\u4e0b\u5f53\u771f\u662f\u53d8\u51fa\u610f\u5916\uff0c\u5927\u5bb6\u90fd\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff01\u5ffd\u542c\u5f97\u90a3\u8001\u592a\u76d1\u9053\uff1a\u201c\u627f\u73e0\uff0c\u627f\u73e0\uff01\u679c\u7136 \u662f\u4f60\uff01\u6bd5\u5be8\u4e3b\u5b50\u4f60\u6709\u6069\uff0c\u4e0d\u53ef\u52a8\u624b\uff01\u201d\u767d\u8863\u5c11\u5e74\u5446\u4e86\u4e00\u5446\uff0c\u5251\u5c16\u4e00\u6311\uff0c\u5c06\u5e3d\u5b50\u6311\u8d77\uff0c\u91cd\u65b0\u6234 \u4e0a\uff0c\u5ffd\u5730\u629a\u5251\u4e00\u63d6\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6bd5\u5be8\u4e3b\uff0c\u5927\u6069\u4e0d\u8a00\u62a5\uff0c\u65e5\u540e\u4f60\u82e5\u6709\u6240\u9700\uff0c\u6c34\u91cc\u706b\u91cc\u6211\u90fd\u542c \u4f60\u5dee\u9063\uff0c\u53ea\u662f\u4f60\u82e5\u7136\u9a82\u5f20\u5927\u4fa0\uff0c\u90a3\u5c31\u4f11\u602a\u6211\u4e0e\u4f60\u53cd\u76ee\u6210\u4ec7\uff01\u201d\u6536\u5251\u4e00\u8dc3\uff0c\u65cb\u98ce\u822c\u8dd1\u51fa\u5c4b\u5916\uff0c \u6bd5\u64ce\u5929\u5927\u53eb\u9053\uff1a\u201c\u4e8e\u5144\uff0c\u8bf7\u7559\u6b65\uff01\u201d\u4ed6\u53eb\u5f00\u4e86\u4e8e\u5144\uff0c\u4e00\u65f6\u95f4\u672a\u80fd\u8f6c\u53e3\uff0c\u53ea\u89c1\u90a3\u201c\u767d\u8863\u5c11\u5e74\u201d \u9ad8\u58f0\u957f\u5578\uff0c\u4ed6\u7684\u90a3\u5339\u767d\u9a6c\u672c\u5728\u56ed\u4e2d\uff0c\u5e94\u58f0\u800c\u6765\uff0c\u201c\u767d\u8863\u5c11\u5e74\u201d\u4e00\u8dc3\u4e0a\u9a6c\uff0c\u8fd9\u9a6c\u7aef\u7684\u662f\u795e\u9a8f\u4e4b \u6781\uff01\u88ab\u4e3b\u4eba\u5728\u80cc\u4e0a\u4e00\u62cd\uff0c\u7adf\u7136\u8df3\u8fc7\u4e08\u591a\u9ad8\u7684\u56f4\u5899\uff0c\u53ea\u542c\u5f97\u5bc6\u5bc6\u7684\u9a6c\u8e44\u58f0\u6709\u5982\u64c2\u9f13\uff0c\u970e\u5ffd\u4e4b\u95f4 \u8e44\u58f0\u6e10\u8fdc\u6e10\u9690\uff0c\u60f3\u662f\u53bb\u5f97\u8fdc\u4e86\u3002\u4f17\u4eba\u5747\u662f\u60ca\u8be7\u4e4b\u6781\uff0c\u731c\u4e0d\u900f\u5979\u4f55\u4ee5\u5982\u6b64\u4e0d\u8fd1\u60c5\u7406\uff1f \u3000\u3000\u8fd9\u767d\u8863\u5c11\u5e74\u540d\u53eb\u4e8e\u627f\u73e0\uff0c\u6b63\u662f\u4e8e\u8c26\u7684\u72ec\u751f\u7231\u5973\uff08\u66f9\u592a\u76d1\u77e5\u9053\u4e8e\u8c26\u65e0\u5b50\uff0c\u66fe\u5bf9\u6bd5\u64ce\u5929\u8a00 \u53ca\uff0c\u6240\u4ee5\u521a\u624d\u6bd5\u64ce\u5929\u6000\u7591\u5979\u7684\u8eab\u4efd\uff09\u3002\u6614\u5e74\u4e91\u857e\u5728\u4e8e\u8c26\u5bb6\u4e2d\uff0c\u89c1\u5979\u751f\u5f97\u53ef\u7231\uff0c\u751a\u662f\u559c\u6b22\uff0c\u5979 \u4e0e\u5f20\u4e39\u67ab\u7ed3\u5a5a\u4e4b\u540e\uff0c\u4fbf\u6536\u4e8e\u627f\u73e0\u4e3a\u5f92\uff0c\u5e26\u5979\u5230\u592a\u6e56\u53bb\u4f4f\u4e86\u51e0\u5e74\uff0c\u5b66\u6210\u4e86\u4e00\u8eab\u6b66\u827a\uff0c\u4e91\u857e\u548c\u5f20 \u4e39\u67ab\u4e0d\u4f46\u628a\u7384\u673a\u9038\u58eb\u6240\u521b\u7684\u5251\u6cd5\u503e\u56ca\u4f20\u6388\u7ed9\u5979\uff0c\u4e91\u857e\u8fd8\u628a\u5979\u7684\u6697\u5668\u7edd\u6280\u98de\u82b1\u6253\u7a74\u4e5f\u6559\u4e86\u5979\uff0c \u4e91\u857e\u521d\u51fa\u9053\u65f6\uff0c\u66fe\u4ed7\u7740\u8fd9\u8def\u6697\u5668\u5f97\u4e86\u4e2a\u201c\u6563\u82b1\u5973\u4fa0\u201d\u7684\u7f8e\u540d\uff0c\u5982\u4eca\u7ecf\u8fc7\u5c06\u8fd1\u5341\u5e74\u7684\u719f\u4e60\u7cbe \u7814\uff0c\u66f4\u662f\u51fa\u795e\u5165\u5316\uff0c\u4e91\u857e\u6709\u4e2a\u5fc3\u601d\uff0c\u5979\u56e0\u81ea\u5df1\u5728\u6c5f\u6e56\u4e0a\u4e0d\u8fc7\u4e24\u4e09\u5e74\u4fbf\u9041\u8ff9\u592a\u6e56\uff0c\u56e0\u6b64\u60f3\u4e8e\u627f \u73e0\u4e0d\u4f46\u627f\u7ee7\u5979\u7684\u6b66\u529f\uff0c\u4e5f\u627f\u7ee7\u5979\u201c\u6563\u82b1\u5973\u4fa0\u201d\u7684\u96c5\u53f7\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u51e0\u5e74\u6765\u5f97\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u7684\u6089\u5fc3\u4f20\u6388\uff0c\u4e0d\u4f46\u627f\u7ee7\u4e86\u4ed6\u4eec\u7684\u6b66\u529f\uff0c\u4e5f\u627f\u7ee7\u4e86\u4ed6\u4eec\u7684\u6c14 \u8d28\uff0c\u5f20\u4e39\u67ab\u592b\u5987\u5982\u4eca\u4ea6\u4e0d\u8fc7\u662f\u4e09\u5341\u5c81\u5de6\u53f3\u7684\u4e2d\u5e74\u4eba\uff0c\u4e0e\u5979\u7684\u5e74\u9f84\u8ddd\u79bb\u4e0d\u7b97\u5f88\u5927\uff0c\u6545\u6b64\u5979\u5bf9 \u5f20\u3001\u4e91\u4e8c\u4eba\uff0c\u4e0d\u4f46\u662f\u5e08\u5f92\u60c5\u4efd\uff0c\u800c\u4e14\u89c6\u540c\u7236\u6bcd\uff0c\u89c6\u540c\u597d\u53cb\uff0c\u6bd4\u8001\u7236\u8fd8\u8981\u4eb2\u8fd1\u5f97\u591a\uff0c\u5979\u662f\u4e2a\u672a \u7ecf\u4e16\u6545\u7eaf\u4efb\u6027\u60c5\u7684\u5c11\u5973\uff0c\u6240\u4ee5\u4e00\u542c\u6709\u4eba\u8fb1\u53ca\u5979\u7684\u5e08\u7236\uff0c\u5728\u90a3\u4e00\u970e\u4e4b\u95f4\uff0c\u4fbf\u7acb\u523b\u5fc3\u60c5\u6fc0\u52a8\uff0c\u7adf \u4e0d\u7ba1\u8fd9\u4eba\u662f\u4e8e\u81ea\u5df1\u6709\u6069\uff0c\u4e5f\u8981\u62d4\u5251\u800c\u8d77\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u5979\u5df2\u9a70\u51fa\u5341\u6570\u91cc\u5916\uff0c\u6fc0\u52a8\u7684\u5fc3\u60c5\u6e10\u6e10\u5e73\u9759\u4e0b\u6765\u3001\u60f3\u8d77\u81ea\u5df1\u521a\u624d\u7684\u884c\u4e8b\uff0c\u4e0d\u89c9\u4e00\u9635\u8ff7 \u832b\uff0c\u8bb7\u8bb7\u81ea\u8bed\u9053\uff1a\u201c\u6211\u505a\u5f97\u5bf9\u5462\uff0c\u8fd8\u662f\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u4e2d\u95f7\u95f7\uff0c\u7b56\u9a6c\u524d\u884c\uff0c\u60f3\u8d77\u90a3\u6bd5\u64ce\u5929\u7684\u7c97\u65f7\u8c6a\u8fc8\uff0c\u81ea\u662f\u6709\u4e00\u79cd\u82f1\u96c4\u6c14\u6168\uff0c\u4f46\u603b\u662f \u4e0d\u80fd\u53eb\u81ea\u5df1\u5fc3\u6298\uff0c\u5230\u5e95\u662f\u6709\u4ec0\u4e48\u4e0d\u987a\u773c\u4e4b\u5904\uff0c\u81ea\u5df1\u4e5f\u8bf4\u4e0d\u4e0a\u6765\u3002\u521a\u624d\u90a3\u4e00\u5251\u523a\u5f97\u5bf9\u662f\u4e0d\u5bf9\uff0c \u81ea\u5df1\u4e5f\u4e0d\u80fd\u5224\u5b9a\u3002\u7236\u4ec7\u8be5\u4e0d\u8be5\u62a5\uff0c\u5982\u4f55\u62a5\u6cd5\uff0c\u8fd9\u79cd\u79cd\u90fd\u5f15\u8d77\u4e86\u4e8e\u627f\u73e0\u601d\u60f3\u7684\u7eb7\u4e71\uff0c\u8981\u77e5\u5979\u4e0d \u8fc7\u4ec5\u4ec5\u662f\u4e2a\u521a\u6ee1\u5341\u516d\u5c81\u7684\u5973\u5b69\u5b50\uff0c\u522b\u4eba\u5728\u5979\u8fd9\u4e2a\u5e74\u9f84\uff0c\u53ef\u80fd\u8fd8\u4e0d\u89e3\u4f18\u6101\uff0c\u53ea\u77e5\u9053\u563b\u563b\u54c8\u54c8\u5730 \u8fc7\u65e5\u5b50\u5462\uff0c\u800c\u5979\u5374\u906d\u9047\u4e86\u60e8\u75db\u7684\u5de8\u53d8\uff0c\u5fc3\u7075\u4e0a\u8d1f\u4e0a\u4e86\u4e0e\u5979\u7684\u5e74\u9f84\u5927\u4e0d\u76f8\u79f0\u7684\u91cd\u62c5\u3002\u8fd9\u65f6\u5979\u53ea \u6709\u4e00\u4e2a\u613f\u671b\uff0c\u4f46\u613f\u65e9\u65e5\u8d76\u56de\u592a\u6e56\u5c71\u5e84\uff0c\u62b1\u7740\u5e08\u6bcd\u75db\u54ed\u4e00\u573a\uff0c\u7136\u540e\u518d\u5411\u5e08\u7236\u8bf7\u6559\u3002 \u3000\u3000\u90a3\u5339\u767d\u9a6c\u672c\u6765\u75be\u8dd1\u5982\u98ce\uff0c\u4e0d\u77e5\u600e\u7684\u5ffd\u7136\u6162\u4e86\u4e0b\u6765\uff0c\u4e8e\u627f\u73e0\u8f7b\u62cd\u9a6c\u80cc\uff0c\u67d4\u58f0\u53eb\u9053\uff1a\u201c\u9a6c\u513f \u5475\uff0c\u5feb\u4e9b\u8dd1\u5427\u3002\u201d\u90a3\u767d\u9a6c\u5636\u4e86\u4e24\u58f0\uff0c\u53e3\u4e2d\u5410\u51fa\u767d\u6cab\uff0c\u5b9a\u5f97\u66f4\u6162\u4e86\u3002\u4e8e\u627f\u73e0\u5927\u662f\u5947\u602a\uff0c\u5979\u4ece\u672a \u66fe\u89c1\u8fc7\u767d\u9a6c\u4f1a\u8fd9\u4e2a\u6837\u5b50\uff01\u8fd9\u5339\u767d\u9a6c\u672c\u6765\u662f\u5f20\u4e39\u67ab\u7684\u5750\u9a91\uff0c\u540d\u4e3a\u201c\u7167\u591c\u72ee\u5b50\u201d\u4e43\u662f\u4e16\u6240\u7f55\u89c1\u7684 \u5b9d\u9a6c\uff0c\u7aef\u7684\u662f\u65e5\u884c\u5343\u91cc\uff0c\u9010\u7535\u8ffd\u98ce\uff0c\u4e8e\u627f\u73e0\u5e73\u7d20\u53ea\u5acc\u5b83\u8d70\u5f97\u592a\u5feb\uff0c\u60f3\u4e0d\u5230\u5b83\u5982\u4eca\u7adf\u662f\u4e00\u6b65\u4e00 \u6b65\u5730\u6328\u7740\u8d70\uff0c\u8fde\u75c5\u9a6c\u4e5f\u4e0d\u5982\u3002\u4e8e\u627f\u73e0\u8df3\u4e0b\u9a6c\u80cc\uff0c\u53ea\u89c1\u767d\u9a6c\u5728\u5618\u5618\u5598\u6c14\uff0c\u53e3\u4e2d\u767d\u6cab\u98de\u6e85\uff0c\u4e8e\u627f \u73e0\u53c8\u4e0d\u61c2\u533b\u9a6c\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u6beb\u65e0\u529e\u6cd5\uff0c\u60f3\u8d77\u8fd9\u767d\u9a6c\u4ece\u6765\u672a\u751f\u8fc7\u75c5\uff0c\u53c8\u662f\u5fc3\u75db\uff0c\u53c8\u662f\u601c\u60dc\uff0c\u62b1 \u7740\u9a6c\u5934\uff0c\u8f7b\u8f7b\u629a\u62cd\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u518d\u8d70\u51e0\u91cc\u8def\u5427\uff0c\u5230\u4e86\u524d\u9762\u7684\u5c0f\u9547\uff0c\u6211\u7ed9\u4f60\u5403\u4e2a\u9971\u9971\u7684\uff0c\u518d \u627e\u4eba\u66ff\u4f60\u6cbb\u75c5\u3002\u201d\u90a3\u767d\u9a6c\u4f3c\u662f\u719f\u77e5\u4eba\u610f\uff0c\u5ffd\u5730\u4e00\u58f0\u957f\u5636\uff0c\u524d\u8e44\u5fae\u5c48\uff0c\u5f80\u65f6\u5b83\u4e3b\u4eba\u9a91\u5b83\u4e4b\u65f6\uff0c \u5b83\u603b\u662f\u8fd9\u4e2a\u6837\u5b50\uff0c\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u4f46\u89c1\u90a3\u5339\u9a6c\u5636\u9e23\u987e\u76fc\uff0c\u5f85\u7740\u81ea\u5df1\uff0c\u53ea\u597d\u8de8\u4e0a\u9a6c\u80cc\uff0c\u767d\u9a6c \u5636\u4e86\u4e00\u58f0\uff0c\u53c8\u653e\u5f00\u56db\u8e44\u75be\u8dd1\uff0c\u4f46\u53ea\u662f\u8fc7\u4e86\u4e00\u9645\uff0c\u53c8\u6162\u4e86\u4e0b\u6765\uff0c\u7adf\u4f3c\u4e0d\u80dc\u75b2\u52b3\uff0c\u53e3\u4e2d\u7684\u767d\u6cab\u55b7 \u5f97\u5636\u5636\u4f5c\u54cd\uff0c\u4e8e\u627f\u73e0\u6b63\u60f3\u4e0b\u9a6c\u7275\u5b83\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\u9a6c\u8e44\u75be\u54cd\uff0c\u6709\u4eba\u53eb\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\uff0c\u4f60\u7684\u9a6c\u8d70 \u4e0d\u52a8\u5566\uff0c\u54b1\u4eec\u518d\u8c08\u4e00\u8c08\u3002\u201d \u3000\u3000\u4e00\u56de\u5934\uff0c\u53ea\u89c1\u90a3\u4eba\u6d53\u7709\u5927\u773c\uff0c\u77ed\u987b\u5982\u88c1\uff0c\u53ef\u4e0d\u6b63\u662f\u6bd5\u64ce\u5929\uff0c\u4e8e\u627f\u73e0\u6b63\u6ca1\u597d\u6c14\uff0c\u8bf4\u9053\uff1a \u201c\u6709\u4ec0\u4e48\u597d\u8c08\u7684\uff1f\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u6211\u521a\u624d\u9a82\u4e86\u5f20\u4e39\u67ab\uff0c\u60f9\u4f60\u751f\u6c14\u3002\u4f60\u53ef\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u8981\u9a82\u5f20 \u4e39\u67ab\uff1f\u201d\u4e8e\u627f\u73e0\u5fc3\u4e2d\u607c\u6012\uff0c\u624b\u6309\u5251\u67c4\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u8981\u542c\u3002\u201d\uff0c\u4e4b\u540e\uff0c\u4f3c\u89c9\u592a\u8fc7\uff0c\u53c8\u9053\uff1a\u201c\u4f60 \u66ff\u6211\u6536\u6b93\u7239\u7239\uff0c\u6211\u81ea\u662f\u611f\u6fc0\u4f60\u7684\u5927\u6069\uff0c\u4f46\u6211\u65e9\u5c31\u8bf4\u8fc7\uff0c\u4e0d\u8bb8\u4f60\u518d\u63d0\u5f20\u5927\u4fa0\u7684\u540d\u5b57\uff01\u201d\u6bd5\u64ce\u5929 \u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u5012\u5947\u4e86\u3002\u5f20\u4e39\u67ab\u662f\u4f60\u7684\u4ec0\u4e48\u4eba\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d\u8981\u4f60\u7ba1\u3002\u6bd5\u5927\u9f99\u5934\uff0c\u54b1\u4eec\u5404 \u8d70\u5404\u8def\uff0c\u4f60\u7684\u6069\u60c5\uff0c\u6211\u65e5\u540e\u603b\u6709\u62a5\u7b54\u4e8e\u4f60\u3002\u201d \u3000\u3000\u6bd5\u64ce\u5929\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u542c\u6211\u5c31\u4e0d\u8bf4\u3002\u6211\u6709\u4e00\u4e2a\u6545\u4e8b\uff0c\u4f60\u542c\u4e0d\u542c\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u600e \u4e48\u4ed6\u8fd8\u6709\u95f2\u60c5\u9038\u81f4\u7ed9\u6211\u8bf4\u6545\u4e8b\uff1f\u201d\u5979\u5230\u5e95\u662f\u5c0f\u5b69\u5fc3\u60c5\uff0c\u4fbf\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6709\u4ec0\u4e48\u6545\u4e8b\uff0c\u8bf4\u51fa\u6765 \u542c\u542c\u3002\u201d \u3000\u3000\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u6709\u4e00\u4e2a\u548c\u5c1a\uff0c\u4ed6\u7684\u672c\u4e8b\u5927\u5f97\u4e0d\u5f97\u4e86\uff0c\u4e0d\u4f46\u7cbe\u901a\u6b66\u529f\uff0c\u800c\u4e14 \u719f\u77e5\u5175\u6cd5\u3002\u4ed6\u6709\u4e09\u4e2a\u5f92\u5f1f\uff0c\u4e00\u4e2a\u662f\u5c0f\u53eb\u5316\uff0c\u4e00\u4e2a\u662f\u8fd0\u79c1\u76d0\u7684\uff0c\u8fd8\u6709\u4e00\u4e2a\u65e2\u505a\u8fc7\u548c\u5c1a\uff0c\u53c8\u505a\u8fc7 \u53eb\u5316\uff0c\u540e\u6765\u5927\u5f92\u5f1f\u548c\u4e8c\u5f92\u5f1f\u90fd\u66fe\u7ecf\u79f0\u738b\u79f0\u5e1d\uff0c\u540e\u4ee3\u4e5f\u66fe\u4eab\u5bcc\u8d35\u8363\u534e\uff0c\u53ea\u6709\u6700\u5c0f\u90a3\u4e2a\u5f92\u5f1f\uff0c\u4e00 \u65e0\u6240\u6210\u3002\u4ed6\u4e3a\u4e8c\u5e08\u5144\u548c\u5927\u5e08\u5144\u5728\u957f\u6c5f\u4ea4\u6218\uff0c\u6218\u6b7b\u4e4b\u540e\uff0c\u8fde\u5c38\u9ab8\u4e5f\u635e\u4e0d\u5230\u3002\u4ed6\u7684\u540e\u4ee3\u4fbf\u6c38\u8fdc\u6d41 \u6d6a\u6c5f\u6e56\uff0c\u505a\u53eb\u5316\u505a\u548c\u5c1a\uff0c\u8fd8\u8981\u65f6\u65f6\u63d0\u5fc3\u540a\u80c6\uff0c\u9003\u907f\u7687\u5e1d\u7684\u8ffd\u7f09\u3002 \u3000\u3000\u201c\u4f46\u8fd9\u5c0f\u5f92\u5f1f\u5728\u672a\u6218\u6b7b\u4e4b\u524d\uff0c\u5374\u548c\u4ed6\u7684\u5e08\u7236\u505a\u4e86\u4e00\u4ef6\u9732\u53e4\u70c1\u4eca\u4e4b\u4e8b\uff0c\u90a3\u5c0f\u5f92\u5f1f\u65e2\u4e0d\u60f3\u79f0 \u738b\uff0c\u4e5f\u4e0d\u60f3\u79f0\u5e1d\uff0c\u4ed6\u957f\u5e74\u4f34\u7740\u5e08\u7236\u4e91\u6e38\u56db\u65b9\uff0c\u5e2e\u52a9\u5e08\u7236\u5c06\u5404\u5730\u7684\u5c71\u5ddd\u9669\u8981\u3001\u7528\u5175\u653b\u5b88\u4e4b\u5730\uff0c \u753b\u6210\u4e86\u4e00\u4efd\u519b\u7528\u7684\u5929\u4e0b\u8be6\u56fe\uff0c\u8c01\u4eba\u82e5\u5f97\u6b64\u56fe\uff0c\u4fbf\u53ef\u56fe\u738b\u9738\u4e4b\u4e1a\uff0c\u540e\u6765\u4ed6\u548c\u4e8c\u5e08\u5144\u5728\u957f\u6c5f\u6218\u6b7b \u4e4b\u540e\uff0c\u8fd9\u4efd\u5730\u56fe\u4e0d\u77e5\u4e0b\u843d\uff0c\u90a3\u4e2a\u5927\u5e08\u5144\uff0c\u4ea6\u5c31\u662f\u90a3\u4e2a\u5c0f\u53eb\u5316\uff0c\u81ea\u6b64\u7edf\u4e00\u6c5f\u5c71\u3002\u4f46\u4ecd\u4e0d\u653e\u5fc3\uff0c \u4f20\u4e0b\u9057\u8bcf\uff0c\u8981\u540e\u4ee3\u7684\u5e1d\u7687\uff0c\u8ffd\u67e5\u90a3\u4e24\u5bb6\u540e\u4eba\u548c\u90a3\u4efd\u5730\u56fe\u7684\u4e0b\u843d\u3002 \u3000\u3000\u201c\u6309\u8bf4\u8fd9\u4efd\u5730\u56fe\u5e94\u8be5\u662f\u4e24\u5bb6\u5171\u6709\uff0c\u4f3a\u51b5\u90a3\u7b2c\u4e09\u4e2a\u5f92\u5f1f\u51fa\u529b\u6700\u591a\uff0c\u66f4\u5e94\u8be5\u6709\u6743\u5904\u7f6e\u3002\u4e0d\u6599 \u4e8b\u8fc7\u767e\u5e74\uff0c\u90a3\u4efd\u5730\u56fe\u53c8\u518d\u53d1\u73b0\uff0c\u843d\u5728\u4e8c\u5f92\u5f1f\u7684\u540e\u4eba\u624b\u4e2d\uff0c\u8fd9\u4eba\u7adf\u7136\u5c06\u5730\u56fe\u732e\u4e0e\u4ec7\u4eba\uff0c\u8ba9\u4ed6\u5b50 \u5b59\u4e07\u4ee3\uff0c\u6c38\u4e3a\u7687\u5e1d\uff0c\u5931\u4e86\u5929\u4e0b\u82f1\u96c4\u4e4b\u671b\uff0c\u4f60\u8bf4\u8fd9\u4e8b\u60c5\u5e94\u4e0d\u5e94\u8be5\uff0c\u516c\u4e0d\u516c\u9053\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u8bf4\u6765\u8bf4\u53bb\uff0c\u8bf4\u7684\u8fd8\u662f\u5f20\u5927\u4fa0\u5f20\u4e39\u67ab\u3002\u90a3\u53ef\u5e76\u4e0d\u662f\u5f88\u4e45\u5f88 \u4e45\u4ee5\u524d\u7684\u4e8b\u60c5\u3002\u8001\u548c\u5c1a\u662f\u5f6d\u83b9\u4e94\uff0c\u5c0f\u53eb\u5316\u662f\u6731\u5143\u748b\uff0c\u8fd0\u79c1\u76d0\u7684\u662f\u5f20\u58eb\u8bda\uff0c\u90a3\u4e2a\u65e2\u505a\u8fc7\u548c\u5c1a\u53c8 \u505a\u8fc7\u53eb\u5316\u7684\u7b2c\u4e09\u4e2a\u5f92\u5f1f\u5927\u7ea6\u662f\u4f60\u7684\u7956\u5148\u6bd5\u51cc\u865a\u4e86\u3002\u6bd5\u5927\u9f99\u5934\uff0c\u8fd9\u4e9b\u9648\u5e74\u65e7\u5e10\u4f60\u8fd8\u63d0\u5b83\u505a\u751a \uff08\u6309\u6731\u3001\u5f20\u3001\u6bd5\u4e09\u5bb6\u4e4b\u4e8b\uff0c\u8be6\u89c1\u62d9\u8457\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\uff09 \u3000\u3000\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u5373\u7b97\u5f20\u4e39\u67ab\u540d\u6ee1\u5929\u4e0b\uff0c\u6211\u4e5f\u8bf4\u4ed6\u8fd9\u4e8b\u60c5\u505a\u5f97\u4e0d\u5408\u3002\u201d\u4e8e\u627f\u73e0\u6012\u9053\uff1a\u201c\u90a3\u65f6 \u74e6\u523a\u5165\u4fb5\uff0c\u4f60\u4e0d\u77e5\u9053\u5417\uff1f\u62b5\u5fa1\u5916\u654c\u5c82\u4e0d\u662f\u7d27\u8981\u4e8e\u81ea\u5bb6\u4e89\u738b\u4e89\u5e1d\uff1f\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u8fd9\u5730\u56fe\u4e43\u662f \u5f20\u3001\u6bd5\u4e24\u5bb6\u4e4b\u7269\uff0c\u5b9e\u5728\u8bf4\u6765\uff0c\u6211\u6bd5\u5bb6\u66f4\u5e94\u505a\u5927\u534a\u4e2a\u4e3b\u4eba\uff0c\u4ed6\u8bf4\u4e5f\u4e0d\u4e0e\u6211\u4eec\u8bf4\u4e00\u58f0\uff0c\u5c31\u62ff\u53bb\u4ea4 \u7ed9\u7687\u5e1d\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d\uff0c\u4ed6\u662f\u4ea4\u7ed9\u6211\u7684\u7236\u4eb2\u3002\u201d\u6bd5\u64ce\u5929\u76ee\u5149\u4e00\u95ea\uff0c\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u7b2c \u4e00\u4e2a\u4e0d\u5408\uff0c\u62b5\u5fa1\u5916\u654c\u56fa\u7136\u7d27\u8981\uff0c\u4f46\u603b\u4e5f\u8be5\u53d6\u5f97\u6211\u5bb6\u540c\u610f\u3002\u201d\u4e8e\u627f\u73e0\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u4e89\u4e00 \u53e3\u95f2\u6c14\u3002\u201d\u6bd5\u64ce\u5929\u4e0d\u7406\u8fd9\u8bdd\uff0c\u4ecd\u7136\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u518d\u8005\u8fd9\u5730\u56fe\u7167\u7406\u4ed6\u5e94\u7559\u4e0b\u526f\u672c\uff0c\u6216\u8005\u5728\u6253\u9000 \u74e6\u523a\u4e4b\u540e\uff0c\u5c31\u5e94\u53d6\u56de\uff0c\u603b\u4e4b\uff0c\u5f20\u4e39\u67ab\u603b\u4f1a\u7559\u6709\u4e00\u4efd\uff0c\u4f46\u6211\u7239\u7239\u4e34\u6b7b\u4e4b\u524d\uff0c\u66fe\u6d3e\u5e2e\u4e2d\u5144\u5f1f\u95ee\u4ed6 \u53d6\u56de\uff0c\u4ed6\u5374\u575a\u8bf4\u6ca1\u6709\u3002\u5982\u6b64\u4e0d\u987e\u5f53\u521d\u4e24\u5bb6\u7684\u4e49\u6c14\uff0c\u8fd9\u5c82\u4e0d\u662f\u7b2c\u4e8c\u4e2a\u4e0d\u5408\uff1f\u201d\u4e8e\u627f\u73e0\u51b7\u7b11\u9053\uff1a \u201c\u5f20\u5927\u4fa0\u53c8\u4e0d\u60f3\u79f0\u738b\u79f0\u5e1d\uff0c\u4ed6\u4e3a\u4f55\u8981\u7559\u4e0b\u526f\u672c\u6216\u5411\u6211\u7239\u7239\u53d6\u56de\uff01\u4ed6\u8bf4\u6ca1\u6709\u5c31\u662f\u6ca1\u6709\u3002\u4f60\u6562\u4e0d \u76f8\u4fe1\u4ed6\uff01\u201d \u3000\u3000\u6bd5\u64ce\u5929\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5982\u6b64\u504f\u8892\uff0c\u6211\u4e5f\u5c31\u4e0d\u5fc5\u8bf4\u4e0b\u53bb\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u6012\u9053\uff1a\u201c\u597d\uff0c\u4f60 \u518d\u8bf4\u3002\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u5c31\u7b97\u4ed6\u771f\u7684\u6ca1\u6709\u7559\u4e0b\u526f\u672c\uff0c\u5929\u4e0b\u8c01\u4e0d\u77e5\u9053\u5f20\u4e39\u67ab\u806a\u660e\u7edd\u9876\uff0c\u8fc7\u76ee\u4e0d \u5fd8\uff1f\u4ed6\u5c31\u662f\u9ed8\u5199\u4e00\u4efd\u4e5f\u53ef\u4ee5\u5199\u5f97\u51fa\u6765\u3002\u201d\u4e8e\u627f\u73e0\u542c\u4ed6\u79f0\u8d5e\u81ea\u5df1\u7684\u5e08\u7236\uff0c\u6012\u6c14\u7a0d\u655b\uff0c\u5fae\u5fae\u4e00 \u7b11\uff0c\u53ea\u542c\u5f97\u6bd5\u64ce\u5929\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u518d\u8bf4\u82e5\u4ed6\u771f\u7684\u6ca1\u7559\u4e0b\u526f\u672c\uff0c\u90a3\u5c31\u66f4\u4e3a\u4e0d\u5999\u3002\u6211\u5df2\u67e5\u660e\u8fd9\u5730\u56fe \u5e76\u4e0d\u5728\u4f60\u5bb6\u4e2d\uff0c\u90a3\u5f53\u7136\u662f\u843d\u5230\u7687\u5bab\u5927\u5185\u4e4b\u5185\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u9762\u8272\u4e00\u53d8\uff0c\u201c\u5475\u5475\u201d\u5730\u53eb\u4e86\u4e00\u58f0\uff0c \u6bd5\u64ce\u5929\u8bf4\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u5947\u602a\uff1f\u8fd9\u5fd8\u601d\u8d1f\u4e49\u7684\u7687\u5e1d\u4ec0\u4e48\u4e8b\u505a\u4e0d\u51fa\u6765\uff1f\u4ed6\u6740\u4e86\u4f60\u7684\u7239\u7239\uff0c\u6284\u4e86 \u4f60\u7684\u5bb6\uff0c\u8fd9\u5e45\u5730\u56fe\u8fd8\u6709\u653e\u8fc7\u7684\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u60f3\u7684\u53ef\u4e0d\u662f\u8fd9\u4e2a\uff0c\u5979\u542c\u4e86\u6bd5\u64ce\u5929\u7684\u8bdd\uff0c\u6599\u60f3\u6bd5\u64ce\u5929\u5b9a\u662f\u5230\u5979\u5bb6\u4e2d\u641c\u67e5\u8fc7\u90a3\u5f20\u5730 \u56fe\uff0c\u5927\u7ea6\u662f\u4ed6\u6765\u641c\u67e5\u4e4b\u65f6\uff0c\u5bb6\u4e2d\u8d22\u4ea7\u5df2\u88ab\u6ca1\u6536\u5165\u5bab\uff0c\u5730\u56fe\u5f53\u7136\u6ca1\u6709\u627e\u7740\uff0c\u7236\u4eb2\u7684\u8bd7\u7a3f\u5219\u53ef\u80fd \u662f\u6284\u5bb6\u7684\u4eba\u4e0d\u653e\u5728\u773c\u5185\uff0c\u968f\u4fbf\u629b\u5f03\uff0c\u4ee5\u81f4\u88ab\u6361\u53bb\u3002\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u6211\u672c\u4ee5\u4e3a\u4ed6\u5192\u9669\u5165\u4eac\uff0c\u95ef \u5929\u7262\uff0c\u52ab\u4eba\u5934\uff0c\u7eaf\u7136\u662f\u4e3a\u4e86\u6211\u7684\u7236\u4eb2\uff0c\u6697\u77e5\u4ed6\u53e6\u6709\u6240\u56fe\uff0c\u6562\u60c5\u90a3\u5f20\u5730\u56fe\u624d\u662f\u4ed6\u6700\u7740\u91cd\u7684\u4e1c \u897f\uff01\u201d\u4e8e\u627f\u73e0\u4e00\u7247\u6012\u706b\u8d77\u5148\u867d\u7136\u56e0\u4e3a\u6bd5\u64ce\u5929\u9a82\u5979\u5e08\u7236\uff01\u4ee4\u5979\u5927\u4e3a\u53cd\u611f\uff0c\u4f46\u5fc3\u4e2d\u4ecd\u662f\u5bf9\u4ed6\u975e\u5e38 \u611f\u6fc0\uff0c\u5982\u4eca\u542c\u4e86\u8fd9\u8bdd\uff0c\u90a3\u611f\u6fc0\u4e4b\u60c5\uff0c\u81ea\u7136\u800c\u7136\u5730\u6253\u4e86\u4e00\u4e2a\u6298\u6263\u3002\u5728\u795e\u8272\u4e0a\u4e5f\u5c31\u81ea\u7136\u5730\u8868\u9732\u4e86 \u51fa\u6765\uff0c\u6bd5\u64ce\u5929\u4e5f\u4f3c\u5bdf\u89c9\u5230\u4e86\uff0c\u53ea\u89c1\u4e8e\u627f\u73e0\u4f5c\u4e86\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u6bd5\u7237\u7684\u8bdd\u8bf4\u5b8c\u4e86\u5427\uff1f\u6211\u53ef\u8981\u8d70 \u4e86\u3002\u201d\u9762\u4e0a\u6ca1\u6709\u6012\u5bb9\uff0c\u8bdd\u4e5f\u8bf4\u5f97\u5ba2\u6c14\uff0c\u795e\u8bf7\u5374\u662f\u51b7\u6f20\u4e4b\u6781\uff0c\u6bd5\u64ce\u5929\u5e73\u7d20\u8c6a\u6c14\u5982\u4e91\uff0c\u8fd9\u65f6\u5374\u4e0d \u81ea\u7981\u5730\u5fc3\u5185\u4e00\u9178\uff0c\u597d\u751f\u5931\u671b\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u624b\u629a\u9a6c\u80cc\uff0c\u9a91\u7740\u9a6c\u521a\u8d70\u5f97\u4e24\u6b65\uff0c\u5ffd\u542c\u5f97\u6bd5\u64ce\u5929\u53eb\u9053\uff1a\u201c\u56de\u6765\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6bd5 \u5927\u9f99\u5934\uff0c\u4f60\u8fd8\u6709\u4f55\u6307\u6559\uff1f\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u4e8b\u60c5\u5fd8\u8bb0\u7684\u6ca1\u6709\uff1f\u201d\u4e8e\u627f\u73e0\u60f3\u4e86\u4e00\u60f3\uff0c \u9053\uff1a\u201c\u55ef\uff0c\u662f\u4e86\uff0c\u6211\u7236\u4eb2\u7684\u8bd7\u7a3f\uff0c\u8bf7\u4f60\u4ea4\u56de\u3002\u201d\u6bd5\u64ce\u5929\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u679c\u7136\u662f\u4e2a\u5b5d\u5973\u3002\u9664\u4e86\u90a3 \u9996\u548f\u77f3\u7070\u7684\u8bd7\u6211\u5df2\u88f1\u7cca\u4f34\u4f60\u7236\u4eb2\u7684\u7075\u5802\uff0c\u5176\u4f59\u7684\u8bd7\u7a3f\u90fd\u5728\u8fd9\u91cc\u3002\u201d\u4e8e\u627f\u73e0\u63a5\u8fc7\u8bd7\u7a3f\uff0c\u6de1\u6de1\u9053 \u8c22\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u90a3\u9996\u4e00\u8bd7\u4f60\u8bfb\u591a\u4e24\u904d\uff0c\u5f88\u6709\u597d\u5904\u3002\u201d\u6bd5\u64ce\u5929\u9762\u5bb9\u4e00\u7aef\uff0c\u76ef\u7740\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u4f60\u656c\u7231\u7236\u4eb2\uff0c\u7ee7\u627f\u5bb6\u5b66\uff0c\u81ea\u7136\u7b97\u5f97\u5c3d\u4e86\u5b5d\u9053\uff0c\u53ef\u60dc\u8fd8\u4e0d\u662f\u771f\u7684\u5b5d\u5973\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u600e \u4e48\uff1f\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u4f60\u7236\u4eb2\u51a4\u6b7b\uff0c\u4e0a\u4e0b\u540c\u6124\uff0c\u4e3a\u4ec0\u4e48\u4f60\u65e0\u52a8\u4e8e\u8877\uff1f\u201d\u4e8e\u627f\u73e0\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u662f \u4ec0\u4e48\u8bdd\uff1f\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u4f60\u7684\u7236\u4eb2\u662f\u8c01\u6740\u7684\uff1f\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u60f3\u62a5\u4ec7\uff1f\u5982\u4eca\u5317\u4e94\u7701\u7684\u7eff\u6797\u8c6a\u6770\uff0c \u7ed3\u4e3a\u540c\u76df\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u7559\u4e0b\u6765\uff0c\u4e0e\u6211\u4eec\u5171\u56fe\u5927\u4e8b\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u60f3\u6211\u4e5f\u7559\u4e0b\u6765\uff0c \u5949\u4f60\u4e3a\u5927\u9f99\u5934\uff01\u201d\u6bd5\u64ce\u5929\u76b1\u7709\u9053\uff1a\u201c\u5929\u4e0b\u767e\u59d3\u5982\u5904\u6cb8\u6c64\uff0c\u6211\u5c82\u662f\u4e3a\u81ea\u5df1\u6253\u7b97\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u53e4\u5f80\u4eca\u6765\uff0c\u51e1\u60f3\u505a\u7687\u5e1d\u7684\u4eba\u90fd\u4f1a\u8bf4\u8fd9\u53e5\u8bdd\u3002\u201d\u6bd5\u64ce\u5929\u51b7\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u8fd8\u662f\u5927\u660e\u5fe0 \u81e3\u4e8e\u8c26\u7684\u5973\u513f\uff0c\u4f46\u5374\u4e0d\u662f\u4e00\u4e2a\u5b5d\u4e49\u53cc\u5168\u6562\u4f5c\u6562\u4e3a\u7684\u5973\u4e2d\u8c6a\u6770\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u4e00\u9635\u60f6\u98a7\uff0c\u5979\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u53eb\u5979\u5728\u5373\u65f6\u4e4b\u95f4\uff0c\u51b3\u5b9a\u81ea\u5df1\u4eca\u540e\u4e00\u751f\u7684\u51fa\u5904\uff0c\u5b9e\u662f\u8d85\u51fa \u4e86\u5979\u5fc3\u7075\u7684\u8d1f\u62c5\uff0c\u53ea\u542c\u5f97\u6bd5\u64ce\u5929\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u96be\u9053\u7559\u5728\u6211\u5c71\u5be8\u4e4b\u4e2d\uff0c\u5c31\u6cd5\u8fb1\u4f60\u5343\u91d1\u5c0f\u59d0\u7684\u8eab \u4efd\uff1f\u201d\u4e8e\u627f\u73e0\u6012\u9053\uff1a\u201c\u6211\u7236\u4eb2\u4e00\u751f\u5ec9\u6d01\uff0c\u65e5\u5e38\u4eb2\u81ea\u7f1d\u8863\u8865\u5c4b\uff0c\u5929\u4e0b\u6240\u77e5\uff0c\u4f60\u5f53\u6211\u662f\u4ec0\u4e48\u4eba \u4e86\uff1f\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u90a3\u4e48\u4e00\u8a00\u7acb\u65ad\uff0c\u4f60\u613f\u4e0d\u613f\u62a5\u4ec7\uff0c\u4f60\u613f\u4e0d\u613f\u7559\u4e0b\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u62a5\u4ec7\u4e0e \u7559\u4e0b\uff0c\u8fd9\u662f\u4e24\u4ef6\u4e8b\u60c5\uff0c\u518d\u8bf4\u6211\u4e5f\u5f97\u95ee\u8fc7\u5e08\u7236\u3002\u201d\u65e0\u610f\u4e4b\u95f4\uff0c\u5979\u4e0d\u89c9\u6cc4\u6f0f\u4e86\u81ea\u5df1\u5e08\u7236\u7684\u6d88\u606f\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u6211\u65e9\u5c31\u770b\u51fa\u4e86\u4f60\u662f\u5f20\u4e39\u67ab\u7684\u5f92\u5f1f\uff0c\u602a\u4e0d\u5f97\u5bf9\u5e08\u7236\u5982\u6b64\u7ef4\u62a4\u3002\u201d \u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u65e2\u77e5\u5f20\u5927\u4fa0\u662f\u6211\u5e08\u7236\uff0c\u5c31\u4e0d\u8be5\u5728\u6211\u9762\u524d\u51fa\u8a00\u62b5\u6bc1\u3002\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u81ea \u5df1\u7684\u4ec7\u4e5f\u4e0d\u66fe\u62a5\uff0c\u4ed6\u4f1a\u66ff\u4f60\u62a5\u4ec7\u3002\u201d\u4e8e\u627f\u73e0\u67f3\u7709\u4e00\u7ad6\uff0c\u9053\uff1a\u201c\u6211\u5e08\u7236\u5728\u5927\u654c\u5f53\u524d\uff0c\u6350\u5f03\u79c1 \u4ec7\uff0c\u8fd9\u624d\u662f\u771f\u82f1\u96c4\u5927\u8c6a\u6770\u7684\u80f8\u895f\u3002\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u6b64\u4e00\u65f6\uff0c\u5f7c\u4e00\u65f6\uff0c\u5982\u4eca\u671d\u5ef7\u65e0\u9053\uff0c\u82f1\u96c4\u7eb7 \u8d77\uff0c\u4f60\u96be\u9053\u8bf4\u4ed6\u4eec\u8981\u628a\u6731\u660e\u5929\u5b50\uff0c\u53d6\u800c\u4ee3\u4e4b\uff0c\u4e3a\u7684\u5c31\u53ea\u662f\u79c1\u4ec7\uff0c\u4e0d\u7b97\u8c6a\u6770\uff1f\u201d\u4e8e\u627f\u73e0\u77aa\u4e86\u6bd5 \u64ce\u5929\u4e00\u773c\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u53ef\u4e00\u6982\u800c\u8bba\uff0c\u4f60\u662f\u4e0d\u662f\u82f1\u96c4\u8c6a\u6770\uff0c\u8fd9\u8981\u5f85\u65e5\u540e\u65b9\u77e5\uff01\u201d\u6bd5\u64ce\u5927\u7684\u8bdd\u5b9e \u662f\u501f\u522b\u4eba\u800c\u8bf4\u81ea\u5df1\uff0c\u4e8e\u627f\u73e0\u4e00\u53e3\u6c14\u8bf4\u4e86\u51fa\u6765\uff0c\u76f4\u8a00\u7b54\u590d\uff0c\u6bd5\u64ce\u5929\u4e5f\u89c9\u5c34\u5c2c\uff0c\u9762\u4e0a\u53d1\u70ed\uff0c\u53ea\u662f \u4e8e\u627f\u73e0\u53c8\u4f5c\u52bf\u6b32\u8d70\uff0c\u6bd5\u64ce\u5929\u53c8\u53eb\u9053\uff1a\u201c\u56de\u6765\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6bd5\u5927\u9f99\u5934\uff0c\u6211\u53ef\u8981\u8d81 \u65e9\u8d76\u8def\u3002\u201d \u3000\u3000\u6bd5\u64ce\u5929\u7b11\u9053\uff1a\u201c\u4f60\u8981\u8d70\u4e5f\u8d70\u4e0d\u4e86\uff0c\u4f60\u7684\u9a6c\u513f\u53ef\u4e0d\u80af\u66ff\u4f60\u8d76\u8def\uff01\u201d\u8fb9\u8bf4\u8fb9\u8d70\u8fd1\u4e8e\u627f\u73e0\u90a3\u5339 \u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\uff0c\u90a3\u5339\u9a6c\u5ffd\u5730\u6012\u5636\uff0c\u626c\u8d77\u524d\u8e44\uff0c\u4f3c\u662f\u53d1\u4e86\u813e\u6c14\uff0c\u7adf\u8981\u8e22\u6bd5\u64ce\u5929\uff0c\u6bd5\u64ce\u5929\u9000\u540e \u4e24\u6b65\uff0c\u7b11\u9053\uff1a\u201c\u867d\u5728\u75c5\u4e2d\uff0c\u4ea6\u8fd8\u8fd9\u6837\u795e\u9a8f\uff0c\u679c\u7136\u662f\u5339\u5b9d\u9a6c\uff01\u201d\u4e8e\u627f\u73e0\u672c\u5c31\u806a\u660e\uff0c\u53c8\u53d7\u4e86\u5f20\u4e39 \u67ab\u8fd9\u51e0\u5e74\u7684\u8463\u9676\uff0c\u673a\u7075\u4e4b\u6781\uff0c\u89c1\u6b64\u60c5\u72b6\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u6bd5\u5927\u9f99\u5934\uff0c\u4f60\u662f\u5317\u4e94\u7701\u7684\u7eff\u6797\u9886 \u8896\uff0c\u4f60\u53ef\u4e0d\u80fd\u6b3a\u7792\u6211\u4e00\u4e2a\u5973\u5b50\u3002\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u5339\u9a6c\u662f\u4e0d\u662f\u771f\u7684\u751f \u75c5\uff1f\u8fd8\u662f\u7ed9\u4ec0\u4e48\u4eba\u4f5c\u5f04\u4e86\uff1f\u201d \u3000\u3000\u6bd5\u64ce\u5929\u5fc3\u4e2d\u4e00\u51db\uff1a\u201c\u8fd9\u5973\u5b69\u5b50\u5bf9\u5927\u4e8b\u867d\u7136\u4e0d\u80fd\u51b3\u65ad\uff0c\u4f46\u89c1\u4e8b\u5374\u662f\u6781\u4e3a\u673a\u8b66\uff01\u201d\u539f\u6765\u4ed6\u89c1 \u4e8e\u627f\u73e0\u5982\u6b64\u7f8e\u8c8c\u5e74\u8f7b\uff0c\u6b66\u529f\u5374\u90a3\u4e48\u4e86\u5f97\uff0c\u53c8\u662f\u4e8e\u8c26\u4e4b\u5973\uff0c\u5f20\u4e39\u67ab\u4e4b\u5f92\uff0c\u5fc3\u4e2d\u5b9e\u5728\u975e\u5e38\u60f3\u628a\u5979 \u7559\u4e0b\uff0c\u6545\u6b64\u6628\u665a\u5c31\u505a\u4e86\u624b\u811a\uff0c\u628a\u4e00\u79cd\u836f\u6df7\u5728\u8349\u6599\u4e4b\u4e2d\uff0c\u7ed9\u9a6c\u5403\u4e86\uff0c\u8fd9\u79cd\u836f\u5e76\u65e0\u6bd2\u5bb3\uff0c\u4f46\u5374\u662f \u4e00\u79cd\u7f13\u6027\u7684\u9ebb\u9189\u5242\uff0c\u9a6c\u5403\u4e86\u4e4b\u540e\uff0c\u8dd1\u8d77\u8def\u6765\uff0c\u4e0d\u591a\u4e45\u4fbf\u4f1a\u75b2\u5026\uff0c\u975e\u6709\u4ed6\u7684\u7279\u5236\u89e3\u836f\uff0c\u4e0d\u80fd\u6062 \u590d\u3002\u4ed6\u4e00\u5fc3\u8981\u7559\u4e0b\u4e8e\u627f\u73e0\uff0c\u6545\u6b64\u4e0d\u60dc\u4ee5\u5927\u9f99\u5934\u7684\u8eab\u4efd\uff0c\u4f5c\u4e86\u6b64\u4e8b\uff0c\u5728\u4ed6\u4ee5\u4e3a\u8fd9\u4e43\u662f\u756a\u597d\u610f\uff0c \u4e0d\u6599\u4e8e\u627f\u73e0\u5374\u975e\u5e38\u8ba4\u771f\u5730\u6b63\u8a00\u8d28\u95ee\uff0c\u6bd5\u64ce\u5929\u7684\u8c6a\u6c14\u96c4\u98ce\uff0c\u5728\u8fd9\u6837\u4e00\u4e2a\u673a\u7075\u7684\u5973\u5b69\u5b50\u9762\u524d\uff0c\u7adf \u5982\u4e07\u4e08\u6d2a\u6ce2\uff0c\u7a81\u7136\u88ab\u77f3\u5824\u8ff3\u4f4f\uff0c\u98de\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u907f\u5f00\u4e86\u4e8e\u627f\u73e0\u7684\u76ee\u5149\uff0c\u4ece\u9a6c\u80cc\u4e0a\u89e3\u4e0b\u4e00\u4e2a\u76db\u5f97\u6ee1\u6ee1\u7684\u76ae\u56ca\uff0c\u7f13\u7f00\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u4f60 \u662f\u771f\u7684\u8981\u8d70\u4e86\uff1f\u597d\u5427\uff0c\u4f60\u62ff\u8fd9\u4e00\u76ae\u56ca\u7684\u6c34\u7ed9\u9a6c\u559d\u4e86\uff0c\u4e0d\u7528\u534a\u4e2a\u65f6\u8fb0\uff0c\u5b83\u53ef\u4ee5\u6062\u590d\u5982\u521d\u3002\u201d\u4e8e \u627f\u73e0\u5fc3\u4e2d\u51b7\u7b11\uff1a\u201c\u679c\u7136\u662f\u4ed6\u5e72\u7684\u3002\u201d\u53ea\u542c\u5f97\u6bd5\u64ce\u5929\u53c8\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\uff0c\u6211\u662f\u4e00\u5fc3\u76fc\u671b\u4f60\u7559\u4e0b \u6765\uff0c\u4f60\u4e00\u5b9a\u8981\u8d70\uff0c\u6211\u4e5f\u6ca1\u6709\u529e\u6cd5\u3002\u6211\u662f\u4e2a\u7c97\u4eba\uff0c\u4e0d\u61c2\u5f97\u7559\u5ba2\u4e4b\u9053\uff0c\u6709\u4e9b\u4e8b\u4e5f\u8bb8\u4ee4\u4f60\u751f\u6c14\uff0c\u4e8e \u59d1\u5a18\uff0c\u54b1\u4eec\u662f\u4e0d\u662f\u53ef\u4ee5\u4ea4\u4e2a\u670b\u53cb\uff1f\u201d\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u6e29\u67d4\u4e4b\u6781\uff0c\u65e2\u59d4\u5a49\u5730\u89e3\u91ca\u4e86\u4e3a\u4ec0\u4e48\u8981\u4f5c\u5f04 \u5979\u7684\u5b9d\u9a6c\uff0c\u53c8\u8868\u8fbe\u4e86\u5fc3\u4e2d\u7684\u60c5\u610f\u3002\u4e8e\u627f\u73e0\u5c1a\u4e0d\u89e3\u7537\u5973\u4e4b\u60c5\uff0c\u4f46\u89c9\u8fd9\u6837\u4e00\u4e2a\u9b41\u68a7\u5947\u4f1f\u7684\u7c97\u8c6a\u6c49 \u5b50\uff0c\u50cf\u5973\u4eba\u822c\u5730\u538b\u4f4e\u4e86\u55d3\u5b50\u8bf4\u8bdd\uff0c\u751a\u662f\u6ed1\u7a3d\u597d\u7b11\uff0c\u4f46\u89c1\u4ed6\u8bf4\u5f97\u5982\u6b64\u8bda\u6073\uff0c\u4ea6\u81ea\u5fc3\u4e2d\u611f\u52a8\uff0c\u8bf4 \u9053\uff1a\u201c\u6bd5\u5927\u9f99\u5934\uff0c\u4f60\u662f\u6211\u7684\u6069\u4eba\uff0c\u53ea\u8981\u4f60\u4e0d\u9a82\u6211\u7684\u5e08\u7236\uff0c\u6211\u81ea\u662f\u5bf9\u4f60\u611f\u6fc0\uff0c\u6211\u4e5f\u613f\u4f60\u597d\u597d\u5730 \u505a\u51fa\u4e00\u756a\u4e8b\u4e1a\u3002\u201d\u4f38\u51fa\u624b\u4e0e\u6bd5\u64ce\u5929\u4e00\u63e1\uff0c\u8868\u793a\u613f\u610f\u548c\u4ed6\u505a\u670b\u53cb\uff0c\u53ea\u89c9\u6bd5\u64ce\u5929\u7684\u624b\u6307\u5fae\u5fae\u53d1 \u6296\uff0c\u4e8e\u627f\u73e0\u751a\u662f\u5947\u602a\uff0c\u8f7b\u8f7b\u653e\u5f00\uff0c\u5c06\u76ae\u56ca\u7684\u6c34\u5012\u7ed9\u9a6c\u559d\uff0c\u53ea\u542c\u5f97\u6bd5\u6574\u5929\u53c8\u9053\uff1a\u201c\u4f60\u56de\u53bb\u89c1\u4e86 \u5e08\u7236\uff0c\u4e0d\u59a8\u5c06\u6211\u4eca\u65e5\u4e4b\u8bdd\uff0c\u544a\u8bc9\u4e8e\u4ed6\u3002\u82e5\u7136\u4ed6\u80fd\u9ed8\u5199\u51fa\u4e00\u4efd\u5730\u56fe\uff0c\u5c31\u70e6\u4f60\u66ff\u6211\u5e26\u6765\uff0c\u5176\u5b9e\u6211 \u5bf9\u4f60\u7684\u5e08\u7236\u4e5f\u5e76\u65e0\u6076\u610f\uff0c\u4f46\u5730\u56fe\u65e2\u662f\u4e24\u5bb6\u4e4b\u7269\uff0c\u4e5f\u5c31\u602a\u4e0d\u5f97\u6211\u95ee\u4ed6\u8ba8\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u597d\uff0c \u6211\u5bf9\u4ed6\u8bf4\u4fbf\u662f\u3002\u201d\u8de8\u4e0a\u9a6c\u80cc\uff0c\u90a3\u767d\u9a6c\u4f53\u529b\u6e10\u6e10\u6062\u590d\uff0c\u4e0d\u7528\u4e3b\u4eba\u50ac\u4fc3\uff0c\u7acb\u5373\u653e\u5f00\u56db\u8e44\uff0c\u8d8a\u8d70\u8d8a \u5feb\uff0c\u4e8e\u627f\u73e0\u53ea\u542c\u5f97\u6bd5\u64ce\u5929\u5728\u540e\u53eb\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u81ea\u4eec\u518d\u89c1\u5566\u3002\u201d\u767d\u9a6c\u5df2\u8dd1\u51fa\u91cc\u8bb8\u4e4b\u5730\uff0c\u56de\u5934\u4e00 \u671b\uff0c\u53ea\u89c1\u4ed6\u8fd8\u5728\u8fdc\u8fdc\u5730\u62db\u624b\u3002 \u3000\u3000\u5341\u591a\u5929\u4e4b\u540e\uff0c\u4e8e\u627f\u73e0\u5355\u4eba\u5339\u9a6c\u56de\u5230\u82cf\u5dde\u5f20\u4e39\u67ab\u5728\u82cf\u5dde\u6709\u4e00\u5904\u4ea7\u4e1a\uff0c\u90a3\u4fbf\u662f\u4ed6\u4ece\u4e5d\u5934\u72ee\u5b50 \u6bb7\u5929\u9274\u624b\u4e0a\u8d62\u6765\u7684\u5feb\u6d3b\u6797\uff0c\u672c\u662f\u5f20\u4e39\u67ab\u5148\u7956\u5f20\u58eb\u8bda\u5728\u82cf\u5dde\u79f0\u5e1d\u4e4b\u65f6\u6240\u5efa\u7684\u884c\u5bab\uff0c\u5f20\u58eb\u8bda\u5175 \u8d25\uff0c\u8d22\u4ea7\u6ca1\u6536\u5165\u5bab\uff0c\u90a3\u5ea7\u884c\u5bab\u5356\u7ed9\u4e86\u6bb7\u5bb6\u5f00\u4f5c\u8d4c\u573a\uff0c\u5230\u91cd\u5f52\u5f20\u4e39\u67ab\u4e4b\u624b\u540e\uff0c\u52a0\u610f\u5e03\u7f6e\uff0c\u7136\u540e \u6062\u590d\u4e86\u56ed\u6797\u4e4b\u80dc\uff08\u5f20\u4e39\u67ab\u8d4c\u8d62\u5feb\u6d3b\u6797\u4e4b\u4e8b\uff0c\u89c1\u62d9\u4f5c\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\u3002\uff09\u4f46\u5f20\u4e39\u67ab\u5f52\u9690\u4e4b\u540e\uff0c \u4e0d\u559c\u70ed\u95f9\uff0c\u9009\u62e9\u4e86\u592a\u987a\u5c71\u4e0a\u7684\u6d1e\u5ead\u5c71\u5e84\u4f5c\u4e3a\u4f4f\u6240\uff0c\u5feb\u6d3b\u6797\u5219\u4ea4\u7ed9\u4e91\u91cd\u548c\u6fb9\u53f0\u955c\u660e\u592b\u5987\u7ba1\u7406\u3002 \u4e8e\u627f\u73e0\u4e5f\u66fe\u5230\u8fc7\u5feb\u6d3b\u6797\u6e38\u73a9\u3002\u5982\u4eca\u5230\u4e86\u82cf\u5dde\uff0c\u5f53\u7136\u60f3\u53bb\u5148\u89c1\u4ed6\u4eec\uff0c\u4e8e\u662f\u9a91\u4e86\u767d\u9a6c\uff0c\u4e00\u8def\u6765\u5230 \u4e86\u5feb\u6d3b\u6797\uff0c\u53ea\u89c1\u56ed\u95e8\u7d27\u95ed\uff0c\u4e0a\u9762\u8d34\u6709\u4e00\u5f20\u901a\u544a\uff0c\u8fd8\u6709\u51e0\u4e2a\u95f2\u4eba\u5728\u4e0b\u9762\u4ef0\u5929\u89c2\u671b\u3002\u4e8e\u627f\u73e0\u4e0b\u9a6c \u4e00\u770b\uff0c\u53ea\u89c1\u901a\u544a\u5199\u9053\uff1a\u201c\u6b64\u56ed\u5df2\u7ecf\u672c\u4eba\u4e70\u5165\uff0c\u4fee\u7406\u65f6\u671f\u6682\u4e0d\u5f00\u653e\uff0c\u5feb\u6d3b\u6797\u4e3b\u4eba\u9f99\u5929\u4ed5\u767d\u3002\u201d \u4e8e\u627f\u73e0\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u5e08\u7236\u53c8\u4e0d\u7f3a\u94b1\u7528\uff0c\u600e\u4e48\u628a\u5feb\u6d3b\u6797\u5356\u4e86\uff1f\u8fd9\u9f99\u5929\u4ed5\u662f\u4ec0\u4e48\u4e1c \u897f\uff1f\u201d\u53ea\u542c\u5f97\u90a3\u51e0\u4e2a\u95f2\u4eba\u4e2d\u6709\u4eba\u8c08\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u5feb\u6d3b\u6797\u53c8\u8981\u6539\u56de\u8d4c\u573a\u5566\uff01\u54b1\u4eec\u5144\u5f1f\u4e5f\u6258\u8d56\u6709 \u4e2a\u751f\u8ba1\u4e86\uff0c\u9f99\u5e2e\u4e3b\u8bf7\u6211\u505a\u6253\u8377\u54e9\uff01\u201d\u770b\u6837\u5b50\u662f\u4e2a\u5730\u75de\uff0c\u4e8e\u627f\u73e0\u66f4\u662f\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u5c31\u662f\u5356\u4e5f\u5f97 \u6361\u4e2a\u4e70\u4e3b\uff0c\u600e\u7684\u5356\u7ed9\u4eba\u91cd\u5f00\u8d4c\u573a\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u95f2\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5440\uff0c\u5f00\u4e86\u8d4c\u573a\uff0c\u8fd9\u5730\u65b9\u5c31\u4e0d\u80fd\u5b89\u751f\u4e86\uff0c\u542c\u8001\u4e00\u8f88\u7684\u8bf4\uff0c\u5341 \u5e74\u4e4b\u524d\u8fd9\u91cc\u5f00\u8d4c\u573a\u7684\u65f6\u5019\uff0c\u5077\u52ab\u6bb4\u6597\uff0c\u51e0\u4e4e\u65e5\u65e5\u90fd\u6709\uff0c\u5b50\u5f1f\u4eec\u5b66\u574f\uff0c\u90a3\u662f\u66f4\u4e0d\u6d88\u8bf4\u4e86\u3002\u201d\u53e6 \u4e00\u4e2a\u9053\uff1a\u201c\u662f\u4e91\u72b6\u5143\u597d\uff0c\u4ed6\u5728\u8fd9\u91cc\u7684\u65f6\u5019\uff0c\u54b1\u4eec\u867d\u7136\u4e0d\u80fd\u968f\u610f\u8fdb\u51fa\uff0c\u4f46\u6bcf\u4e2a\u6708\u521d\u4e00\u5341\u4e94\u5374\u662f \u4efb\u4eba\u6e38\u8d4f\uff0c\u54b1\u4eec\u6258\u8d56\u6709\u4e2a\u6e05\u9759\u7684\u53bb\u5904\uff0c\u53ef\u4ee5\u770b\u82b1\u3001\u89c2\u9c7c\u3001\u8d4f\u677e\u3001\u542c\u96e8\u3002\u5c06\u6765\u7ed9\u9f99\u5e2e\u4e3b\u5728\u8fd9\u91cc \u5f00\u4e86\u8d4c\u573a\uff0c\u6015\u4e0d\u95f9\u5f97\u4e00\u7247\u4e4c\u70df\u7634\u6c14\uff0c\u54b1\u4eec\u8fd9\u4e9b\u7a77\u63aa\u5927\u60f3\u627e\u4e2a\u6d88\u95f2\u7684\u5730\u65b9\u4e5f\u4e0d\u80fd\u591f\u4e86\u3002\u201d\u770b\u6837 \u5b50\u8fd9\u4eba\u4f3c\u662f\u4e2a\u7a77\u79c0\u624d\u3002\u4e8e\u627f\u73e0\u63d2\u53e3\u95ee\u9053\uff1a\u201c\u8fd9\u56ed\u5b50\u539f\u6765\u7684\u4e3b\u4eba\u662f\u4e2a\u72b6\u5143\u5417\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u5c0f \u54e5\uff0c\u4f60\u662f\u5916\u5730\u6765\u7684\u5417\uff1f\u5927\u540d\u9f0e\u9f0e\u7684\u6b66\u72b6\u5143\u4e91\u91cd\u4f60\u4e5f\u6ca1\u542c\u8bf4\u8fc7\u5417\uff1f\u8fd9\u4f4d\u72b6\u5143\u7237\u4e0d\u4f46\u7cbe\u901a\u6b66\u827a\uff0c \u66fe\u4e3a\u5927\u5c06\uff0c\u800c\u4e14\u6587\u624d\u4e5f\u8270\u4e0d\u9519\uff0c\u4f60\u53ea\u770b\u4ed6\u8fd9\u56ed\u6797\u7684\u5e03\u7f6e\uff0c\u5c31\u77e5\u9053\u4ed6\u80f8\u4e2d\u4e0d\u4f46\u7f57\u5217\u7532\u5175\uff0c\u800c\u4e14 \u4e5f\u9690\u6709\u70df\u4e91\uff0c\u786e\u662f\u4e2a\u98ce\u96c5\u4e4b\u58eb\u5462\u3002\u201d\u8fd9\u4eba\u6447\u5934\u6446\u8111\uff0c\u8bf4\u8bdd\u9178\u6e9c\u6e9c\u7684\u4f3c\u901a\u975e\u901a\uff0c\u4e8e\u627f\u73e0\u56e0\u4e3a\u8981 \u63a2\u542c\u4e91\u91cd\u7684\u6d88\u606f\uff0c\u6240\u4ee5\u660e\u77e5\u6545\u95ee\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u54ea\u91cc\u77e5\u9053\u8fd9\u56ed\u5b50\u7684\u4e3b\u4eba \u5c31\u662f\u6211\u7684\u5e08\u7236\uff0c\u56ed\u6797\u7684\u5e03\u7f6e\uff0c\u90fd\u662f\u4ed6\u8bbe\u8ba1\u7ecf\u8425\u7684\uff1f\u201d\u90a3\u9178\u79c0\u624d\u9053\uff1a\u201c\u5c0f\u54e5\u4e3a\u4f55\u5931\u7b11\uff0c\u53ef\u5f97\u95fb \u4e4e\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u65e2\u662f\u72b6\u5143\uff0c\u81ea\u4e0d\u6101\u6ca1\u94b1\u7528\uff0c\u4e3a\u4f55\u5c06\u8fd9\u56ed\u5b50\u5356\u4e86\u800c\u4e14\u8fd8\u5356\u7ed9\u522b\u4eba\u5f00\u8d4c \u573a\u3002\u201d\u90a3\u9178\u79c0\u624d\u9053\uff1a\u201c\u5440\uff0c\u5c0f\u54e5\uff0c\u4f60\u6709\u6240\u4e0d\u77e5\uff0c\u4e91\u72b6\u5143\u4e00\u5bb6\u90fd\u642c\u8d70\u4e86\uff0c\u8fd9\u4f4d\u9f99\u5e2e\u4e3b\uff0c\u54b3\uff0c \u54b3\u2026\u2026\u201d\u90a3\u5730\u75de\u77aa\u4ed6\u4e00\u773c\uff01\u9178\u79c0\u624d\u4f3c\u4e4e\u9887\u6709\u987e\u5fcc\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u9f99\u5e2e\u4e3b\u4e45\u5df2\u60f3\u5f00\u8d4c\u573a\uff0c \u96be\u5f97\u6709\u8fd9\u7247\u597d\u5730\u65b9\uff0c\u6240\u4ee5\u5c31\u4e70\u4e0b\u6765\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u4e2d\u66f4\u662f\u7eb3\u7f55\uff0c\u4e91\u91cd\u4f55\u4ee5\u8981\u4e3e\u5bb6\u642c\u8fc1\u3002\u8fde\u5fd9 \u95ee\u9053\uff1a\u201c\u4e91\u72b6\u5143\u642c\u5230\u4e86\u4f55\u5904\uff1f\u201d\u90a3\u5730\u75de\u5927\u7b11\u9053\uff1a\u201c\u4e91\u72b6\u5143\u82e5\u7136\u80af\u8bf4\u7ed9\u4ed6\u542c\uff0c\u4ed6\u65e9\u5df2\u662f\u8fd9\u5730\u65b9 \u7684\u540d\u6d41\u4e86\uff0c\u8fd8\u4f1a\u8ddf\u6211\u4eec\u5728\u4e00\u5904\u5417\uff1f\u201d\u90a3\u9178\u79c0\u624d\u6ee1\u8138\u4e0d\u4ee5\u4e3a\u7136\u7684\u795e\u6c14\u8bf4\u9053\uff1a\u201c\u4eba\u5bb6\u4e91\u72b6\u5143\u867d\u7136 \u505a\u8fc7\u5927\u5b98\uff0c\u53ef\u6ca1\u6709\u4e00\u70b9\u67b6\u5b50\uff0c\u6211\u5c31\u548c\u4ed6\u8bf4\u8fc7\u8bdd\uff0c\u90a3\u624d\u4e0d\u662f\u4ec0\u4e48\u7a00\u7f55\u7684\u4e8b\u60c5\u3002\u201d\u53ef\u662f\u4ed6\u59cb\u7ec8\u8bf4 \u4e0d\u51fa\u4e91\u91cd\u642c\u5230\u4ec0\u4e48\u5730\u65b9\uff0c\u5730\u75de\u5c31\u5bf9\u4ed6\u5927\u5927\u5632\u7b11\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u65e0\u5fc3\u542c\u4ed6\u4eec\u4e89\u8bba\uff0c\u95f7\u95f7\u4e0d\u4e50\uff0c\u8d70\u4e86\u51fa\u6765\uff0c\u8f6c\u8fc7\u8857\u89d2\uff0c\u5ffd\u89c1\u4e24\u4e2a\u4eba\u5c3e\u968f\u81ea\u5df1\uff0c\u597d\u751f \u773c\u719f\u3002\u4e8e\u627f\u73e0\u505c\u4e0b\u6765\u4e00\u770b\uff0c\u539f\u6765\u5c31\u662f\u5728\u5f20\u98ce\u5e9c\u5bb6\u4e61\u6240\u9047\u7684\uff0c\u548c\u6a0a\u82f1\u5728\u4e00\u9053\u7684\u90a3\u4e24\u4e2a\u519b\u5b98\u3002\u90a3 \u4e24\u4e2a\u519b\u5b98\u8d70\u4e0a\u524d\u6765\u671b\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u59d3\u9646\u7684\u90a3\u4e2a\u7ba1\u5e26\uff08\u4e03\u54c1\u6b66\u5b98\u540d\u79f0\uff09\u9053\uff1a\u201c\u5582\uff0c\u8fd9\u4f4d\u5c0f\u54e5\uff0c \u4f60\u4e0d\u662f\u548c\u54b1\u4eec\u8001\u6a0a\u6253\u8fc7\u67b6\u7684\u90a3\u4f4d\u5c0f\u54e5\u5417\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u4eec\u60f3\u66ff\u670b\u53cb\u62a5\u4ec7\u5417\uff1f\u201d\u59d3 \u4e8e\u7684\u90a3\u4f4d\u7edf\u9886\u8bf4\u9053\uff1a\u201c\u4f60\u540e\u6765\u6709\u6ca1\u6709\u518d\u78b0\u89c1\u8fc7\u8001\u6a0a\uff1f\u201d\u4e8e\u627f\u73e0\u5fc3\u4e2d\u6697\u6697\u597d\u7b11\uff0c\u9053\uff1a\u201c\u78b0\u89c1\u53c8 \u600e\u6837\uff0c\u6ca1\u78b0\u89c1\u53c8\u600e\u6837\uff1f\u201d\u9646\u7ba1\u5e26\u9053\uff1a\u201c\u8001\u6a0a\u7ea6\u6211\u4eec\u5728\u592a\u6e56\u8fb9\u89c1\u9762\uff0c\u73b0\u5728\u8fc7\u4e86\u5341\u591a\u5929\u5566\uff0c\u8fd8\u6ca1 \u6709\u6765\u3002\u201d\u4e8e\u627f\u73e0\u6545\u610f\u95ee\u9053\uff1a\u201c\u4ed6\u7ea6\u4f60\u4eec\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u90a3\u4e24\u4e2a\u519b\u5b98\u5f7c\u6b64\u76f8\u671b\uff0c\u652f\u652f\u543e\u543e\u5730\u4e0d\u6562 \u76f4\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7a76\u662f\u5c0f\u5b69\u5fc3\u6027\uff0c\u867d\u7136\u8ba8\u538c\u8fd9\u4e24\u4e2a\u519b\u5b98\uff0c\u4f46\u5374\u601c\u60af\u4ed6\u4eec\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u4eec\u5728\u6b64\u7b49\u5019\uff0c \u4e0d\u89c1\u6a0a\u82f1\uff0c\u6bcf\u65e5\u90fd\u6709\u8eab\u5bb6\u6027\u547d\u4e4b\u5fe7\uff0c\u4e0d\u77e5\u9053\u591a\u7126\u6025\u5462\uff01\u201d\u59d3\u4e8e\u7684\u90a3\u4e2a\u7ba1\u5e26\u9053\uff1a\u201c\u5c0f\u54e5\uff0c\u4f60\u4f46 \u8bf4\u5728\u54ea\u91cc\u78b0\u89c1\u8fc7\u4ed6\uff0c\u6211\u4eec\u597d\u77e5\u9053\u4ed6\u7684\u786e\u5b9e\u6240\u5728\uff0c\u6765\u662f\u4e0d\u6765\uff0c\u4e5f\u514d\u5f97\u6211\u4eec\u5728\u8fd9\u91cc\u6b7b\u7b49\u3002\u201d\u4e8e\u627f \u73e0\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u6a0a\u82f1\u7ea6\u4f60\u4eec\u5728\u8fd9\u91cc\u540c\u53bb\u627e\u5f20\u4e39\u67ab\uff0c\u597d\u8ba8\u56de\u90a3\u4e09\u5341\u4e07\u4e24\u5b98\u94f6\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u90a3 \u4e24\u4e2a\u519b\u5b98\u5413\u4e86\u4e00\u8df3\uff0c\u4f46\u4ed6\u4eec\u90a3\u65e5\u89c1\u8fc7\u4e8e\u627f\u73e0\u7684\u8eab\u624b\uff0c\u65e9\u77e5\u9053\u4ed6\u4e0d\u662f\u5bfb\u5e38\u7684\u5c11\u5e74\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c \u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u4eec\u5927\u7ea6\u662f\u4e0d\u6253\u4e0d\u76f8\u8bc6\uff0c\u8001\u6a0a\u90fd\u548c\u4f60\u8bf4\u4e86\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u4eec\u89c1\u5230\u5f20\u4e39 \u67ab\u6ca1\u6709\uff1f\u201d\u90a3\u4e24\u4e2a\u519b\u5b98\u9053\uff1a\u201c\u6ca1\u6709\u8001\u6a0a\u76f8\u966a\uff0c\u6211\u4eec\u8fd9\u4e9b\u65e0\u540d\u5c0f\u5352\uff0c\u600e\u6562\u53bb\u89c1\u5f20\u5927\u4fa0\uff1f\u201d\u8fd9\u4e24 \u4e2a\u519b\u5b98\u5e73\u65e5\u5b98\u6c14\u5341\u8db3\uff0c\u5728\u771f\u6709\u672c\u9886\u7684\u4eba\u9762\u524d\uff0c\u5374\u53c8\u663e\u5f97\u5341\u5206\u840e\u7f29\u81ea\u5351\u3002\u4e8e\u627f\u73e0\u53c8\u662f\u5fae\u5fae\u4e00 \u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u5fc5\u7b49\u5566\uff0c\u90a3\u4e09\u5341\u4e07\u4e24\u5b98\u94f6\u65e9\u5df2\u6709\u4eba\u66ff\u4f60\u4eec\u7684\u4e0a\u53f8\u7f34\u8fd8\u7ed9\u5b98\u5bb6\u4e86\uff01\u4f46\u4f60\u7684 \u4e0a\u53f8\u5374\u4e5f\u4e22\u4e86\u8138\uff0c\u4f60\u4eec\u8d81\u65e9\u56de\u6e56\u5317\u5427\uff0c\u8981\u4e0d\u7136\u65b0\u5b98\u4e0a\u4efb\uff0c\u4e0d\u89c1\u4f60\u4eec\u62a5\u5230\uff0c\u4f60\u4eec\u7684\u5b98\u804c\u53ea\u6015\u4e5f \u4fdd\u4e0d\u4f4f\u3002\u201d\u90a3\u4e24\u4e2a\u519b\u5b98\u53c8\u559c\u53c8\u60ca\uff0c\u53c8\u662f\u4e0d\u6562\u76f8\u4fe1\uff0c\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4eca\u665a\u53ef\u4ee5\u5b89\u5fc3\u7761\u4e00\u89c9 \u4e86\u3002\u201d\u4e00\u7b11\u8dd1\u5f00\uff0c\u5979\u53ef\u4e0d\u77e5\uff0c\u8fd9\u65f6\u5df2\u6709\u4e24\u540d\u5927\u5185\u536b\u571f\u542c\u5230\u4e86\u5979\u7684\u8bf4\u8bdd\uff0c\u6697\u6697\u5730\u7f00\u5728\u5979\u7684\u540e \u9762\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7b2c\u4e8c\u65e5\u4e00\u65e9\u8d77\u6765\uff0c\u9a91\u4e86\u767d\u9a6c\uff0c\u8d70\u5230\u5927\u6e56\u4e4b\u8fb9\uff0c\u5e73\u65e5\u6e56\u8fb9\u6e38\u8247\u751a\u591a\uff0c\u8fd9\u65f6\u5374\u53ea\u89c1\u4e00 \u53ea\u5c0f\u8239\uff0c\u7cfb\u5728\u67f3\u9634\u5e95\u4e0b\uff0c\u4e8e\u627f\u73e0\u53c8\u662f\u6697\u6697\u7eb3\u7f55\uff0c\u5fc3\u9053\uff1a\u201c\u73b0\u5728\u6b63\u662f\u66ae\u6625\u65f6\u8282\uff0c\u6700\u597d\u6e38\u6e56\uff0c\u600e \u4e48\u6e38\u8247\u53cd\u800c\u5c11\u4e86\uff1f\u201d\u90a3\u5c0f\u8239\u7684\u68a2\u516c\uff0c\u6d53\u7709\u5927\u773c\uff0c\u4f53\u683c\u9b41\u68a7\uff0c\u4e00\u89c1\u4e8e\u627f\u73e0\uff0c\u7acb\u523b\u89e3\u5f00\u7ef3\u7f06\uff0c\u8d54 \u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u76f8\u516c\u662f\u53bb\u6e38\u6e56\u7684\u5417\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u6491\u6211\u5230\u897f\u6d1e\u5ead\u5c71\u53bb\u3002\u201d\u68a2\u516c \u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff0c\u5440\uff0c\u4f60\u8fd9\u5339\u9a6c\u771f\u597d\uff0c\u6211\u7ed9\u4f60\u7275\u8fdb\u6765\u3002\u201d \u3000\u3000\u6625\u98ce\u4f5b\u9762\uff0c\u6e56\u6c34\u6e85\u8863\uff0c\u6e56\u4e2d\u5c71\u5cf0\u9690\u7ea6\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u4fe8\u5982\u6d77\u4e0a\u795e\u5c71\uff0c\u6e56\u5149\u58a9\u6e58\uff0c\u4e07\u503e\u832b \u832b\uff0c\u6c34\u5929\u4e00\u8272\uff0c\u4e8e\u627f\u73e0\u5fc3\u4e2d\u8bb0\u6302\u7740\u5e08\u7236\u5e08\u6bcd\uff0c\u5374\u662f\u65e0\u5fc3\u89c2\u8d4f\uff0c\u5076\u4e00\u62ac\u5934\uff0c\u5ffd\u89c1\u4e24\u53ea\u5927\u8239\u5411\u7740 \u90a3\u8fb9\u5212\u6765\uff0c\u770b\u8239\u7684\u6837\u5b50\u4e0d\u662f\u666e\u901a\u6e38\u8247\uff0c\u4e24\u53ea\u5927\u8239\uff0c\u8239\u5934\u4e0a\u90fd\u7ad9\u7740\u4e00\u6761\u5927\u6c49\uff0c\u76ee\u5149\u707c\u707c\u5730\u76ef\u7740 \u4e8e\u627f\u73e0\uff0c\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u770b\u51fa\u6211\u662f\u5973\u626e\u7537\u88c5\u5417\uff1f\u600e\u4e48\u5982\u6b64\u76ef\u7740\u4eba\u5bb6\uff0c\u597d \u751f\u65e0\u793c\uff01\u201d\u6b63\u81ea\u6c14\u607c\uff0c\u5ffd\u542c\u5f97\u90a3\u68a2\u516c\u5531\u9053\uff1a\u201c\u8001\u5b50\u751f\u957f\u592a\u6e56\u8fb9\uff0c\u4e0d\u7231\u4ea4\u6e38\u53ea\u7231\u94b1\uff0c\u6628\u665a\u5e94\u916c \u795e\u8bb8\u4e86\u613f\uff0c\u54c8\u54c8\uff0c\u4eca\u671d\u679c\u7136\u78b0\u5230\u4e86\u53ea\u5927\u80a5\u7f8a\uff01\u201d\u4e8e\u627f\u73e0\u5413\u4e86\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u4f60\u5531\u4ec0\u4e48\uff1f\u201d\u90a3\u68a2 \u516c\u9053\uff1a\u201c\u76f8\u516c\uff0c\u4f60\u559c\u6b22\u5403\u677f\u5200\u9762\uff0c\u8fd8\u662f\u559c\u6b22\u5403\u9984\u9968\u9762\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ec0\u4e48\u53eb\u677f\u5200\u9762\uff0c\u4ec0\u4e48 \u53eb\u9984\u9968\u9762\uff1f\u201d\u90a3\u68a2\u516c\u500f\u5730\u4ece\u8239\u8231\u5e95\u4e0b\u53d6\u51fa\u4e00\u53e3\u677f\u5200\uff0c\u7b11\u9053\uff1a\u201c\u5403\u677f\u5200\u9762\u5c31\u662f\u4e00\u5200\u4e24\u6bb5\u3002\u201d\u4f5c \u4e86\u4e00\u4e2a\u624b\u52bf\uff0c\u865a\u65ab\u4e00\u5200\uff0c\u53c8\u9053\uff1a\u201c\u5403\u9984\u9968\u9762\u5c31\u662f\u5c06\u4f60\u7f1a\u4e86\u8d77\u6765\uff0c\u54c8\uff0c\u535c\u901a\u4e00\u58f0\uff0c\u4e22\u4e0b\u6e56 \u5fc3\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u6012\u9053\uff1a\u201c\u767d\u65e5\u7687\u5929\uff0c\u4f60\u7adf\u6562\u8c0b\u8d22\u5bb3\u547d\uff1f\u201d\u90a3\u68a2\u516c\u559d\u9053\uff1a\u201c\u5feb\u5c06\u8eab\u4e0a\u7684\u4e1c\u897f\u90fd\u653e\u5728 \u4e00\u8fb9\uff0c\u54b1\u8001\u5b50\u4e5f\u4e0d\u8981\u4f60\u7684\u6027\u547d\uff0c\u4f60\u53ef\u5f97\u4e56\u4e56\u5730\u8ddf\u7740\u6211\u8d70\u3002\u201d\u90a3\u4e24\u53ea\u5927\u8239\u8d8a\u6765\u8d8a\u8fd1\uff0c\u8239\u5934\u4e0a\u7684 \u5927\u6c49\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u8fd8\u548c\u4ed6\u591a\u8bf4\u4ec0\u4e48\uff0c\u628a\u4ed6\u4e22\u8fdb\u6e56\u5fc3\u53bb\u5148\u6df9\u4e2a\u534a\u6b7b\uff01\u541f\u54c8\uff0c\u54b1\u4eec\u62ff\u53bb\u89c1\u9633\u5927\u603b \u7ba1\uff0c\u53ef\u662f\u4e00\u529f\u3002\u201d\u90a3\u68a2\u516c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u5148\u8bf7\u4f60\u5403\u7897\u9968\u9762\u3002\u201d\u5de6\u624b\u63d0\u677f\u5200\uff0c\u53f3\u624b\u63d0\u7ef3\u7d22\uff0c\u6251\u8fdb \u8239\u8231\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u4e4b\u95f4\uff0c\u6025\u89c1\u4e8e\u627f\u73e0\u53f3\u624b\u4e00\u62ac\uff0c\u773c\u524d\u91d1\u661f\u4e00\u95ea\uff0c\u90a3\u68a2\u516c\u8fd8\u672a\u53eb\u5f97\u51fa\u58f0\uff0c\u5df2\u88ab \u4e8e\u627f\u73e0\u7684\u91d1\u82b1\u6697\u5668\u6253\u4e2d\u5589\u5499\uff0c\u535c\u901a\u5730\u8dcc\u4e0b\u6e56\u5fc3\uff0c\u81ea\u5df1\u5148\u5403\u4e86\u9984\u9968\u9762\uff01\u4e8e\u627f\u73e0\u672c\u6765\u8fd8\u4e0d\u60f3\u4e0b\u8fd9 \u8fa3\u624b\uff0c\u53ea\u56e0\u542c\u4e86\u90a3\u4e2a\u5927\u6c49\u7684\u53eb\u56b7\uff0c\u624d\u77e5\u9053\u8fd9\u4f19\u4eba\u539f\u6765\u8fd8\u4e0d\u4ec5\u662f\u8c0b\u8d22\u5bb3\u547d\u7684\u5f3a\u76d7\u800c\u5df2\uff0c\u4e00\u65f6\u706b \u8d77\uff0c\u90a3\u6735\u91d1\u82b1\u7adf\u7136\u7a7f\u4eba\u4e86\u68a2\u516c\u7684\u5589\u5499\uff0c\u89c1\u4ed6\u5728\u6e56\u5fc3\u5192\u8d77\u53c8\u6c89\u4e0b\uff0c\u5192\u8d77\u53c8\u6c89\u4e0b\uff0c\u4e0d\u8fc7\u4e00\u523b\uff0c\u8239 \u8fb9\u6e56\u6c34\u5c31\u662f\u4e00\u7247\u9c9c\u7ea2\u3002 \u3000\u3000\u90a3\u4e24\u6761\u5927\u6c49\u53eb\u9053\uff1a\u201c\u597d\u54c7\uff0c\u8fd9\u5c0f\u5b50\u8fd8\u771f\u6709\u4e24\u624b\uff01\u201d\u547d\u4ee4\u5927\u8239\u821f\u5b50\uff0c\u8d8a\u53d1\u52a0\u5feb\u6447\u8239\uff0c\u5de6\u53f3 \u4e24\u8fb9\uff0c\u5939\u7740\u4e8e\u627f\u73e0\u7684\u90a3\u53ea\u5c0f\u821f\uff0c\u4e8e\u627f\u73e0\u4e0d\u61c2\u6c34\u6027\uff0c\u4e0d\u6653\u5212\u8239\uff0c\u68a2\u516c\u4e00\u8dcc\u4e0b\u8239\u4e4b\u540e\uff0c\u8239\u5c31\u5728\u6e56 \u5fc3\u6ef4\u6e9c\u6e9c\u5730\u8f6c\uff0c\u4e8e\u627f\u73e0\u5927\u6012\uff0c\u53cc\u624b\u9f50\u626c\uff0c\u91d1\u82b1\u5de6\u53f3\u5e76\u53d1\uff0c\u5206\u6253\u90a3\u4e24\u4e2a\u8239\u5934\u5927\u6c49\uff0c\u8fd9\u65f6\u4e8e\u627f\u73e0 \u7684\u5c0f\u8239\u548c\u90a3\u4e24\u53ea\u5927\u8239\u8ddd\u79bb\u90fd\u7ea6\u6709\u5341\u4f59\u4e08\u4e4b\u9065\uff0c\u6e56\u4e2d\u98ce\u5927\uff0c\u8239\u53c8\u5728\u8f6c\uff0c\u672c\u4e0d\u4f3c\u5728\u9646\u5730\u4e4b\u4e0a\u6613\u53d6 \u51c6\u5934\uff0c\u4f46\u6bcf\u8fb9\u4e09\u6735\u91d1\u82b1\uff0c\u4ecd\u7136\u76f4\u98de\u5230\u5927\u8239\u7684\u8239\u5934\uff0c\u5206\u5c04\u90a3\u4e24\u6761\u5927\u6c49\u7684\u4e0a\u4e2d\u4e0b\u4e09\u5904\u8981\u5bb3\uff0c\u90a3\u4e24 \u6761\u5927\u6c49\u90fd\u662f\u5927\u5185\u7684\u4e00\u7ea7\u536b\u58eb\uff0c\u5de6\u8fb9\u7684\u90a3\u4e2a\u53eb\u505a\u6768\u5e72\u65a4\uff0c\u53f3\u8fb9\u7684\u90a3\u4e2a\u53eb\u91d1\u4e07\u4e24\uff0c\u6768\u5343\u65a4\u80c1\u529b\u6c89 \u96c4\uff0c\u62e9\u52a8\u4e00\u6761\u94c1\u94fe\u5c06\u4e09\u6735\u91d1\u82b1\u4e00\u9f50\u6253\u98de\uff0c\u91d1\u4e07\u4e24\u5584\u4e8e\u817e\u632a\u95ea\u5c55\u7684\u5c0f\u5de7\u4e4b\u6280\uff0c\u821e\u8d77\u4e00\u53e3\u5355\u5200\uff0c \u5de6\u8eb2\u53f3\u95ea\uff0c\u4e0a\u906e\u4e0b\u6321\uff0c\u4e5f\u628a\u4e00\u6735\u91d1\u82b1\u78d5\u98de\uff0c\u5176\u4f59\u7684\u4e24\u6735\u5219\u7ed9\u4ed6\u95ea\u8fc7\uff0c\u5c04\u5165\u4e86\u8239\u8231\u4e4b\u4e2d\uff0c\u76f4\u9677 \u5165\u8239\u8231\u7684\u677f\u58c1\uff0c\u91d1\u4e07\u4e24\u89c1\u8ddd\u79bb\u5982\u6b64\u4e4b\u8fdc\uff0c\u5355\u5200\u4e0e\u4f1a\u82b1\u76f8\u78b0\uff0c\u864e\u53e3\u5140\u81ea\u53d1\u9ebb\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\u3002 \u6768\u5343\u65a4\u529b\u5927\uff0c\u867d\u7136\u4e0d\u89c9\u600e\u6837\uff0c\u4f46\u89c1\u4ed6\u5728\u8239\u4e0a\u53d1\u653e\u6697\u5668\uff0c\u7adf\u7136\u8ba4\u7a74\u5982\u6b64\u4e4b\u51c6\uff0c\u4ea6\u662f\u5fc3\u60ca\u3002\u4e24\u6761 \u5927\u8239\u90fd\u4e0d\u6562\u8fc7\u4e8e\u8feb\u8fd1\u3002 \u3000\u3000\u4f46\u4e0d\u6d88\u7247\u523b\uff0c\u8fd9\u4e24\u4e2a\u4eba\u5c31\u770b\u51fa\u4e86\u4e8e\u627f\u73e0\u4e0d\u61c2\u4f7f\u8239\uff0c\u4e0d\u901a\u6c34\u6027\uff0c\u6768\u5343\u65a4\u54c8\u54c8\u5927\u7b11\uff0c\u559d\u9053\uff1a \u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff01\u201d\u4e00\u679a\u94c1\u80c6\u63b7\u4e86\u8fc7\u6765\uff0c\u5374\u4e0d\u662f\u6253\u4e8e\u627f\u73e0\uff0c\u800c\u662f\u6253\u5979\u7684\u5c0f\u8239\uff0c\u90a3\u94c1\u80c6\u91cd\u53ef \u6570\u65a4\uff0c\u6253\u5728\u8239\u677f\u4e0a\uff0c\u767b\u65f6\u88c2\u4e86\u4e00\u4e2a\u6d1e\uff0c\u6e56\u6c34\u6c81\u5165\uff0c\u4e8e\u627f\u73e0\u5927\u60ca\uff0c\u7b2c\u4e8c\u679a\u94c1\u80c6\u53c8\u5230\uff0c\u4e8e\u627f\u73e0\u6025 \u5fd9\u53d1\u51fa\u4e24\u6735\u91d1\u82b1\uff0c\u5de6\u53f3\u7275\u5e26\uff0c\u7528\u5185\u5bb6\u7684\u4e0a\u4e58\u529f\u592b\uff0c\u5378\u4e86\u94c1\u80c6\u7684\u6765\u52bf\uff0c\u90a3\u94c1\u80c6\u78b0\u4e0d\u7740\u8239\u677f\uff0c\u5c31 \u5728\u8239\u8fb9\u843d\u4e0b\uff0c\u6fc0\u8d77\u4e86\u4e08\u9ad8\u7684\u6d6a\u82b1\uff0c\u5c0f\u8239\u8d8a\u53d1\u9707\u8361\uff0c\u5728\u6e56\u5fc3\u4e71\u8f6c\uff0c\u4e8e\u627f\u73e0\u53ea\u89c9\u5934\u6655\u773c\u82b1\uff0c\u4f3c\u6b32 \u5455\u5410\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u5fcd\u4f4f\u4e86\uff0c\u6768\u5343\u65a4\u89c1\u72b6\u53c8\u662f\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u628a\u538b\u8239\u7684\u5927\u77f3\u5934\u62ff\u6765\uff0c\u5f85\u6211 \u5148\u628a\u8fd9\u5c0f\u5b50\u7684\u8239\u7838\u6c89\u4e86\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u65e0\u98ce\u5ffd\u8d77\u6ce2\u4e09\u4e08\uff0c\u9669\u6076\u6c5f\u6e56\u4e0d\u5fcd\u770b\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e03\u56de\u3000\u5bc2\u5bc2\u5c71\u5e84\u3000\u5e08\u95e8\u60c5\u7737\u604b\u3000\u832b\u832b\u6e56\u6c34\u3000\u4fa0\u5973\u610f\u51c4\u6006 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e03\u56de\u3000\u5bc2\u5bc2\u5c71\u5e84\u3000\u5e08\u95e8\u60c5\u7737\u604b\u3000\u832b\u832b\u6e56\u6c34\u3000\u4fa0\u5973\u610f\u51c4\u6006 \u3000\u3000\u5927\u51e1\u5728\u6e56\u6d77\u884c\u8239\uff0c\u82e5\u7136\u8239\u5927\u8d27\u5c11\uff0c\u8f7d\u91cd\u4e0d\u591f\uff0c\u9047\u4e0a\u98ce\u6d6a\uff0c\u5c31\u5bb9\u6613\u98a0\u7c38\uff0c\u751a\u6216\u8986\u821f\uff0c\u662f\u4ee5 \u8001\u4e8e\u7ecf\u9a8c\u7684\u821f\u5b50\uff0c\u5c31\u5728\u8239\u8231\u5e95\u4e0b\u5806\u4e86\u8bb8\u591a\u5927\u77f3\u7528\u4ee5\u538b\u821f\uff0c\u540d\u4e3a\u201c\u538b\u821f\u77f3\u201d\uff0c\u8fd9\u4e24\u6761\u5927\u8239\uff0c\u6bcf \u6761\u8239\u4e2d\u53ea\u6709\u4e09\u4e2a\u4eba\uff0c\u4e24\u4eba\u628a\u8235\uff0c\u53e6\u4e00\u4eba\u7ad9\u5728\u8239\u5934\u548c\u4e8e\u627f\u73e0\u52a8\u624b\uff0c\u821f\u5927\u4eba\u5c11\uff0c\u53c8\u65e0\u8d27\u7269\uff0c\u6240\u4ee5 \u6bcf\u6761\u8239\u90fd\u5806\u4e86\u4e24\u4e09\u5343\u65a4\u7684\u5927\u77f3\u5934\u3002 \u3000\u3000\u6768\u5343\u65a4\u4e00\u58f0\u547c\u559d\uff0c\u821f\u5b50\u5c06\u77f3\u5934\u90fd\u62ac\u4e86\u51fa\u6765\uff0c\u6768\u5343\u65a4\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u518d\u63a5\u8fd9 \u4e2a\uff01\u201d\u53cc\u81c2\u4e00\u632f\uff0c\u6325\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u5c06\u4e00\u5757\u91cd\u903e\u767e\u65a4\u7684\u5927\u77f3\uff0c\u547c\u7684\u4e00\u58f0\u629b\u4e86\u51fa\u53bb\uff0c\u843d\u5728\u6e56\u4e2d\uff0c \u767b\u65f6\u6fc0\u8d77\u6570\u4e08\u9ad8\u7684\u6ce2\u6d6a\uff0c\u4e8e\u627f\u73e0\u7684\u5c0f\u821f\u88ab\u6ce2\u6d6a\u4e00\u629b\uff0c\u51e0\u4e4e\u7ffb\u8f6c\uff0c\u4e8e\u627f\u6b8a\u6025\u4f7f\u201c\u5343\u65a4\u5760\u201d\u7684\u529f \u592b\uff0c\u5c06\u5168\u8eab\u6c14\u529b\u90fd\u8fd0\u5f80\u811a\u4e0a\uff0c\u7d27\u7d27\u8e0f\u7740\u8239\u5934\uff0c\u5b9a\u7740\u5c0f\u8239\uff0c\u8fd9\u79cd\u529f\u592b\u8981\u5185\u529f\u5916\u529f\u90fd\u6709\u4e86\u76f8\u5f53\u7684 \u706b\u5019\uff0c\u624d\u80fd\u5728\u6ce2\u6d9b\u9669\u6076\u4e4b\u4e2d\uff0c\u5b9a\u7740\u8239\u8eab\uff0c\u4e8e\u627f\u73e0\u867d\u7136\u5f97\u4e86\u5f20\u4e39\u67ab\u7684\u5185\u5bb6\u5fc3\u6cd5\uff0c\u7a76\u7adf\u5e74\u7eaa\u8fd8 \u8f7b\uff0c\u6c14\u529b\u4e0d\u8db3\uff0c\u5916\u529f\u914d\u4e0d\u4e0a\u5185\u529f\u3002\u5979\u867d\u7136\u4f7f\u5c3d\u5403\u5976\u7684\u6c14\u529b\uff0c\u5c0f\u821f\u6682\u65f6\u4e0d\u81f4\u7ffb\u8f6c\uff0c\u4f46\u4ea6\u5df2\u88ab\u6ce2 \u6d6a\u629b\u4e0a\u629b\u4e0b\uff0c\u4e8e\u627f\u73e0\u53ea\u611f\u5230\u4e00\u9635\u9635\u5934\u6655\uff0c\u51e0\u4e4e\u5c31\u8981\u5455\u5410\u3002\u6768\u5343\u65a4\u54c8\u54c8\u5927\u7b11\uff0c\u4e00\u58f0\u5927\u559d\uff0c\u53c8\u6367 \u8d77\u4e00\u5757\u66f4\u5927\u7684\u77f3\u5934\uff0c\u4e22\u5230\u4e8e\u627f\u73e0\u5c0f\u821f\u7684\u5de6\u4fa7\uff0c\u5c0f\u821f\u88ab\u6ce2\u6d6a\u4e00\u5377\u3001\u4e00\u629b\uff0c\u7acb\u523b\u503e\u659c\uff0c\u6d6a\u82b1\u5982 \u96e8\uff0c\u4e8e\u627f\u73e0\u8863\u88f3\u5c3d\u6e7f\uff0c\u53ea\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u6768\u5343\u65a4\u53c8\u629b\u51fa\u4e86\u7b2c\u4e09\u5757\u5927\u77f3\uff0c\u843d\u5728\u4e8e\u627f\u73e0\u5c0f\u821f \u7684\u53f3\u4fa7\uff0c\u4e24\u80a1\u6d6a\u67f1\uff0c\u5728\u6e56\u5fc3\u5377\u8d77\u4e86\u6f29\u6da1\uff0c\u5c0f\u821f\u5728\u6f29\u6da1\u4e4b\u4e2d\u6025\u8f6c\uff0c\u4e8e\u627f\u73e0\u66f4\u89c9\u5934\u6655\u773c\u82b1\uff0c \u201c\u54c7\u201d\u7684\u4e00\u58f0\u5c06\u65e9\u4e0a\u6240\u5403\u7684\u4e1c\u897f\u90fd\u5455\u4e86\u51fa\u6765\uff0c\u624b\u811a\u8f6f\u7ef5\u7ef5\u7684\uff0c\u4e00\u8eab\u6c14\u529b\u90fd\u4f7f\u4e0d\u51fa\u6765\uff0c\u5fc3\u4e2d\u53c8 \u60ca\u53c8\u6012\uff0c\u5374\u662f\u65e0\u6cd5\u62b5\u6321\uff0c\u53ea\u8d1d\u6768\u5343\u65a4\u53c8\u6367\u8d77\u4e00\u5757\u5927\u77f3\uff0c\u8fd9\u7b2c\u56db\u5757\u77f3\u5934\u629b\u51fa\uff0c\u4e8e\u627f\u73e0\u7684\u5c0f\u821f\u5fc5 \u7136\u8986\u6ca1\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u58f0\u80e1\u54e8\uff0c\u6e56\u9762\u4e0a\u7a81\u7136\u73b0\u51fa\u4e00\u6761\u5c0f\u8239\uff0c\u7bad\u4e00\u822c\u5730\u75be\u9a76\u8fc7\u6765\uff0c\u7adf\u7136\u95ef\u5165\u4e86\u4e24\u6761\u5927\u8239 \u4e0e\u5c0f\u8239\u7684\u4e2d\u95f4\uff0c\u6768\u5343\u65a4\u559d\u9053\uff1a\u201c\u4f60\u627e\u6b7b\u4e48\uff1f\u6562\u6765\u8d81\u8fd9\u8d9f\u6d51\u6c34\uff01\u201d\u90a3\u5c0f\u8239\u7406\u4e5f\u4e0d\u7406\uff0c\u8239\u4e2d\u4f38\u51fa \u4e00\u4e2a\u5934\u6765\uff0c\u7b11\u9053\uff1a\u201c\u767d\u65e5\u9752\u5929\uff0c\u5c45\u7136\u8c0b\u8d22\u5bb3\u547d\uff0c\u8fd9\u8fd8\u6210\u4ec0\u4e48\u4e16\u754c\u5475\uff01\u201d\u58f0\u97f3\u6e05\u8106\u4e4b\u6781\uff0c\u50cf\u4e2a \u5b69\u5b50\u7684\u53e3\u97f3\uff0c\u4e8e\u627f\u73e0\u660f\u660f\u4e4b\u4e0b\uff0c\u4e5f\u7981\u4e0d\u4f4f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8fd9\u58f0\u97f3\u597d\u751f\u8033\u719f\uff0c\u6025\u628a\u773c\u671b\u65f6\uff0c\u53ea\u89c1\u90a3 \u5c0f\u821f\u4e2d\u94bb\u51fa\u4e00\u4e2a\u5c0f\u53ae\uff0c\u4e00\u8eab\u9ed1\u8272\u8863\u88f3\uff0c\u5934\u4e0a\u4e5f\u62ab\u7740\u9ed1\u8272\u6597\u7bf7\uff0c\u53ea\u9732\u51fa\u4e24\u4e2a\u773c\u775b\uff0c\u4e8e\u627f\u73e0\u5934\u6655 \u773c\u82b1\uff0c\u4e00\u65f6\u4e4b\u95f4\u770b\u4e0d\u6e05\u695a\u3002\u53ea\u542c\u5f97\u6768\u5343\u65a4\u5927\u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8fd9\u4e0d\u77e5\u6b7b\u6d3b\u7684\u5c0f\u5bb6\u4f19\uff0c\u4e5f\u5403\u6211\u4e00 \u5757\u77f3\u5934\u3002\u201d\u201c\u8f70\u9686\u201d\u4e00\u58f0\u5de8\u54cd\uff0c\u7b2c\u56db\u5757\u5927\u77f3\u63b7\u4e0b\u6e56\u5fc3\uff0c\u90a3\u9ed1\u8863\u7537\u5b50\u5934\u4e0b\u811a\u4e0a\uff0c\u51b2\u5165\u78a7\u6ce2\uff0c\u5c0f \u821f\u767b\u65f6\u7ffb\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5927\u5403\u4e00\u60ca\uff0c\u5ffd\u89c9\u81ea\u5df1\u8fd9\u53ea\u5c0f\u8239\u4f3c\u4e4e\u7ed9\u4eba\u7528\u5200\u63a8\u4e86\u4e00\u628a\uff0c\u53c8\u88ab\u6c34\u6d41\u4e00\u51b2\uff0c\u500f\u5730\u5982\u7bad \u75be\u98de\uff0c\u987a\u6d41\u800c\u4e0b\uff0c\u4e0d\u4f46\u8131\u51fa\u6f29\u6da1\uff0c\u800c\u4e14\u4e00\u4e0b\u5b50\u5c31\u9a76\u51fa\u4e86\u5341\u6570\u4e08\u5916\uff0c\u8fdc\u8fdc\u5730\u79bb\u5f00\u4e86\u90a3\u4e24\u6761\u5927 \u8239\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u53c8\u60ca\u53c8\u559c\uff0c\u5c0f\u8239\u8131\u51fa\u4e86\u6f29\u6da1\uff0c\u6e56\u9762\u98ce\u5e73\u6d6a\u9759\uff0c\u4e8e\u627f\u73e0\u987f\u65f6\u51cf\u8f7b\u4e86\u6655\u6d6a\u7684\u611f\u89c9\uff0c\u5b9a \u4e86\u5fc3\u795e\uff0c\u8fd0\u4e86\u53e3\u6c14\uff0c\u6c14\u529b\u6e10\u6e10\u60ed\u590d\uff0c\u6293\u8d77\u6868\u6765\u4e71\u5212\uff0c\u5979\u867d\u7136\u4e0d\u61c2\u5212\u8239\uff0c\u4f46\u6c34\u6d41\u5e73\u9759\uff0c\u6070\u6070\u987a \u7740\u6c34\u6d41\uff0c\u5c45\u7136\u7ed9\u5979\u5212\u52a8\u5c0f\u821f\uff0c\u867d\u7136\u4e0d\u5feb\uff0c\u4f46\u4ea6\u6162\u6162\u5730\u5411\u524d\u6d41\u53bb\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u8bb0\u6302\u90a3\u4e2a\u5c0f\u7ae5\uff0c\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u90a3\u5c0f\u821f\u7ffb\u5012\u6e56\u9762\uff0c\u5c0f\u7ae5\u4e0d\u89c1\u8e2a\u8ff9\uff0c\u60f3\u5fc5\u662f\u6c89\u5230\u6c34 \u5e95\u53bb\u4e86\u3002\u4e8e\u627f\u73e0\u4e00\u9635\u96be\u8fc7\uff0c\u5fc3\u9053\uff1a\u201c\u5440\uff0c\u60f3\u4e0d\u5230\u4ed6\u8fd9\u6837\u4e00\u95ef\uff0c\u65e0\u610f\u4e2d\u6551\u4e86\u6211\uff0c\u4ed6\u5374\u767d\u4e22\u4e86\u4e00 \u6761\u6027\u547d\u3002\u201d\u5ffd\u542c\u5f97\u6768\u5343\u65a4\u54c7\u54c7\u5927\u53eb\uff0c\u90a3\u6761\u5927\u8239\u7adf\u7136\u4e5f\u50cf\u5979\u7684\u5c0f\u821f\u521a\u624d\u4e00\u6837\uff0c\u5728\u6e56\u9762\u56e2\u56e2\u6253 \u8f6c\u3002\u5927\u8239\u4e0a\u90a3\u4e24\u4e2a\u821f\u5b50\u53eb\u9053\uff1a\u201c\u6709\u4eba\u5728\u4e0b\u9762\u6363\u9b3c\uff01\u201d\u5176\u4e2d\u4e00\u4e2a\u7acb\u523b\u8df3\u4e86\u4e0b\u53bb\uff0c\u6768\u5343\u65a4\u53eb\u9053\uff1a \u201c\u91d1\u5927\u54e5\uff0c\u4f60\u53bb\u8ffd\u90a3\u4e2a\u5c0f\u5b50\uff01\u201d \u3000\u3000\u91d1\u4e07\u4e24\u6c14\u529b\u4e0d\u5982\u6768\u5343\u65a4\u4e4b\u5927\uff0c\u4e24\u8239\u76f8\u8ddd\u4e8c\u5341\u6765\u4e08\uff0c\u4ed6\u53ef\u4e0d\u80fd\u50cf\u6768\u5343\u65a4\u90a3\u822c\u5982\u6cd5\u70ae\u5236\uff0c\u7528 \u5927\u77f3\u53bb\u7838\u6c89\u4e8e\u627f\u73e0\u7684\u5c0f\u8239\uff0c\u53ef\u662f\u4ed6\u4eec\u5584\u4e8e\u4f7f\u8239\uff0c\u6bd4\u4e8e\u627f\u73e0\u987a\u7740\u6c34\u6d41\u884c\u8d70\u7684\u5c0f\u8239\u81ea\u7136\u8981\u5feb\u5f97 \u591a\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u8ddd\u79bb\u62c9\u8fd1\uff0c\u4e8e\u627f\u73e0\u4e00\u626c\u624b\u6253\u51fa\u4e94\u6735\u91d1\u82b1\uff0c\u91d1\u4e07\u4e24\u4e3e\u5200\u4e00\u4fbf\u6321\uff0c\u4e0d\u6599\u4e8e\u627f\u73e0\u751a \u662f\u806a\u660e\uff0c\u77e5\u9053\u6253\u4ed6\u4e0d\u4e2d\uff0c\u5176\u4e2d\u4e24\u6735\u91d1\u82b1\u7ed5\u7740\u6845\u6746\u4e00\u65cb\uff0c\u5c06\u98ce\u5e06\u7684\u7ef3\u5b50\u5272\u65ad\uff0c\u98ce\u5e06\u5378\u4e0b\uff0c\u5927\u8239 \u5403\u91cd\uff0c\u901f\u5ea6\u5927\u51cf\uff0c\u53e6\u5916\u4e24\u6735\u91d1\u82b1\u5206\u6253\u8239\u8fb9\u90a3\u4e24\u4e2a\u638c\u8235\u7684\u821f\u5b50\uff0c\u5de6\u8fb9\u7684\u90a3\u4e2a\u907f\u8fc7\uff0c\u53f3\u8fb9\u7684\u90a3\u4e2a \u5374\u7ed9\u91d1\u82b1\u6253\u4e2d\uff0c\u8dcc\u4e0b\u6e56\u4e2d\u3002\u8fd8\u6709\u4e00\u6735\u91d1\u82b1\u5219\u4ece\u91d1\u4e07\u4e24\u7684\u5934\u9876\u98de\u8fc7\uff0c\u53eb\u4ed6\u5fd9\u4e8e\u62db\u67b6\uff0c\u4e0d\u80fd\u6551\u63f4 \u90a3\u4e24\u4e2a\u821f\u5b50\u3002\u91d1\u4e07\u4e24\u5403\u4e86\u4e00\u60ca\uff0c\u5927\u8239\u88ab\u963b\u4e86\u4e00\u963b\uff0c\u4e8e\u627f\u73e0\u7684\u5c0f\u8239\u53c8\u79bb\u5f00\u4ed6\u4e8c\u5341\u6765\u4e08\u4e86\u3002\u91d1\u4e07 \u4e24\u62a2\u8fc7\u4e00\u6761\u6868\uff0c\u8fd8\u60f3\u5212\u8239\u518d\u8ffd\uff0c\u5ffd\u542c\u5f97\u6768\u5343\u65a4\u5728\u540e\u9762\u7684\u90a3\u6761\u8239\u4e0a\u5927\u53eb\u9053\uff1a\u201c\u91d1\u5927\u54e5\uff0c\u5feb\u5212\u56de \u6765\uff01\u201d \u3000\u3000\u56de\u5934\u671b\u5219\u3001\uff0c\u53ea\u89c1\u6e56\u5fc3\u4e00\u7247\u901a\u7ea2\uff0c\u521a\u624d\u8df3\u4e0b\u53bb\u7684\u90a3\u4e2a\u821f\u5b50\uff0c\u5c38\u8eab\u5df2\u6d6e\u4e0a\u6c34\u9762\uff0c\u6768\u5343\u65a4\u90a3 \u53ea\u8239\u6e10\u6e10\u4e0b\u6c89\uff0c\u6e56\u6c34\u5df2\u704c\u6ee1\u8239\u8231\uff0c\u539f\u6765\u90a3\u6761\u5927\u8239\uff0c\u7adf\u88ab\u9ed1\u8863\u5c0f\u7ae5\u5728\u8239\u5e95\u505a\u4e86\u624b\u811a\u3002\u5f04\u5f00\u4e86\u4e00 \u4e2a\u5927\u6d1e\uff0c\u6768\u5343\u65a4\u4e5f\u4e0d\u4fbf\u6c34\u6027\uff0c\u6545\u6b64\u547c\u5524\u91d1\u4e07\u4e24\u56de\u6765\u63f4\u6551\u3002 \u3000\u3000\u91d1\u4e07\u4e24\u53ea\u5f97\u653e\u5f00\u654c\u4eba\uff0c\u56de\u6765\u6551\u53cb\uff0c\u4e24\u8239\u76f8\u8ddd\u4e94\u516d\u5341\u4e08\uff0c\u770b\u770b\u5212\u8fd1\uff0c\u90a3\u5927\u8239\u5df2\u7ecf\u6c89\u4e0b\uff0c\u53ea \u9732\u51fa\u8239\u9876\uff0c\u6768\u5343\u65a4\u7ad9\u5728\u8239\u9876\uff0c\u6c34\u5df2\u6d78\u81f3\u811a\u8e1d\uff0c\u8239\u4e2d\u7684\u53e6\u4e00\u4e2a\u821f\u5b50\u8df3\u4e0b\u6c34\u4e2d\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u53c8\u6cdb \u8d77\u4e00\u7247\u8840\u6c34\uff0c\u60f3\u662f\u53c8\u50cf\u4ed6\u7684\u540c\u4f34\u4e00\u6837\uff0c\u88ab\u9ed1\u8863\u5c0f\u7ae5\u6740\u4e86\u3002 \u3000\u3000\u91d1\u4e07\u4e24\u53eb\u9053\uff1a\u201c\u6768\u5927\u54e5\uff0c\u4f60\u77a7\u51c6\u4e86\uff01\u201d\u629b\u51fa\u4e00\u5757\u6728\u677f\uff0c\u6768\u5343\u65a4\u7eb5\u8eab\u4e00\u8dc3\uff0c\u6070\u6070\u843d\u5728\u90a3\u5757 \u677f\u4e0a\uff0c\u53ea\u89c1\u9ed1\u8863\u5c0f\u7ae5\u5728\u6c34\u4e2d\u5192\u51fa\u5934\u6765\uff0c\u4f38\u624b\u5c31\u62a2\u90a3\u5757\u6728\u677f\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u5927\u4e2a\u5b50\uff0c\u4e0b\u6765\u73a9\u73a9 \u5427\uff01\u201d\u6768\u5343\u65a4\u547c\u7684\u4e00\u638c\u62cd\u5411\u6c34\u9762\uff0c\u8fd9\u4e00\u638c\u62fc\u4e86\u6027\u547d\uff0c\u7528\u529b\u5947\u5927\uff0c\u51fb\u5f97\u6e56\u6c34\u98de\u4e86\u8d77\u6765\u3002\u8fde\u4ed6\u7684 \u811a\u8e0f\u7684\u8fd9\u5757\u6728\u677f\uff0c\u4e5f\u88ab\u6ce2\u6d6a\u51b2\u5f00\uff0c\u7acb\u8db3\u4e0d\u7a33\uff01 \u3000\u3000\u90a3\u9ed1\u8863\u5c0f\u7ae5\uff0c\u53eb\u9053\uff1a\u201c\u54c8\uff01\u54c8\uff01\u6ca1\u6253\u7740\uff01\u201d\u5934\u9888\u4e00\u7f29\uff0c\u53c8\u6ca1\u5165\u6c34\u4e2d\uff0c\u6768\u5343\u65a4\u6b66\u529f\u786e\u662f\u9ad8 \u660e\uff0c\u5c31\u5728\u8fd9\u7edd\u9669\u4e4b\u9645\uff0c\u811a\u5c16\u8f7b\u8f7b\u4e00\u70b9\u6728\u677f\uff0c\u8dc3\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u6070\u6070\u843d\u5728\u91d1\u4e07\u4e24\u7684\u8239 \u5934\uff0c\u6c14\u5598\u5401\u5401\u5730\u9053\uff1a\u201c\u8fd9\u5c0f\u8d3c\u662f\u4e2a\u6c34\u9b3c\uff01\u91d1\u5927\u54e5\uff0c\u4f60\u4e0b\u53bb\u770b\uff01\u201d\u91d1\u4e07\u4e24\u5584\u6253\u6697\u5668\uff0c\u9887\u8df7\u6c34 \u6027\uff0c\u6025\u5fd9\u8dc3\u4e0b\u6c34\u4e2d\uff0c\u624b\u4e2d\u6263\u7740\u94c1\u7b52\u7ba9\u7bad\uff0c\u6f5c\u4f0f\u6c34\u5e95\uff0c\u53ea\u5f85\u90a3\u9ed1\u8863\u5c0f\u8463\u6e38\u8fd1\uff0c\u5c31\u6273\u5f00\u673a\u5173\uff0c\u7528 \u7ba1\u7bad\u5c04\u4ed6\u3002\u53ea\u89c1\u6c34\u4e2d\u4e00\u6761\u9ed1\u5f71\uff0c\u5c31\u50cf\u4e00\u6761\u98de\u9c7c\u500f\u5730\u4ece\u8eab\u65c1\u6570\u4e08\u4e4b\u5916\u6e38\u8fc7\uff0c\u76f4\u5954\u4e8e\u627f\u73e0\u7684\u90a3\u6761 \u5c0f\u8239\u53bb\u4e86\u3002\u91d1\u4e07\u4e24\u81ea\u95ee\u8ffd\u4ed6\u4e0d\u4e0a\uff0c\u53ea\u597d\u56de\u5230\u8239\u4e0a\u3002 \u3000\u3000\u518d\u8bf4\u4e8e\u627f\u73e0\u8131\u9669\u4e4b\u540e\uff0c\u987a\u7740\u6c34\u6d41\uff0c\u5c0f\u8239\u6162\u6162\u524d\u884c\uff0c\u5979\u56de\u5934\u671b\u89c1\u90a3\u4e24\u53ea\u5927\u8239\uff0c\u4e00\u53ea\u5df2\u6c89\uff0c \u53e6\u4e00\u53ea\u4e5f\u4e0d\u8ffd\u8d76\uff0c\u5fc3\u4e2d\u5927\u5947\uff0c\u60f3\u90a3\u5c0f\u7ae5\u6b66\u529f\uff0c\u5c31\u600e\u6837\u9ad8\u660e\uff0c\u8981\u72ec\u529b\u5f04\u6c89\u90a3\u6761\u5927\u8239\uff0c\u5374\u662f\u96be\u4ee5 \u601d\u8bae\u3002\u6b63\u81ea\u601d\u7d22\u5728\u4f55\u5904\u89c1\u8fc7\u8fd9\u4e2a\u5c0f\u7ae5\uff0c\u5ffd\u89c9\u8239\u5e95\u4f3c\u4e4e\u6709\u4ec0\u4e48\u4e1c\u897f\u9707\u52a8\uff0c\u5c0f\u821f\u5ffd\u7136\u98de\u5feb\u8d77\u6765\uff0c \u4e8e\u627f\u73e0\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u8fd9\u4e2a\u987d\u76ae\u7684\u5c0f\u5bb6\u4f19\u5feb\u4e0a\u8239\u6765\uff01\u201d\u6e56\u9762\u6c34\u6ce2\u4e0d\u5174\uff0c\u4e8e\u627f\u73e0\u8e72\u4e0b\u6765\u5728\u8239\u8fb9 \u671b\u4e0b\u6c34\u5e95\uff0c\u4eba\u5f71\u4e0d\u89c1\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u7ae5\u5c31\u7b97\u5982\u4f55\u7cbe\u901a\u6c34\u6027\uff0c\u4e5f\u8be5\u77a7\u51fa\u70b9\u8e2a\u5f71\u6765\uff01\u201d\u5947\u602a \u4e4b\u6781\uff0c\u90a3\u5c0f\u821f\u4ecd\u5728\u6025\u901f\u524d\u9a76\u3002 \u3000\u3000\u5c0f\u821f\u79bb\u5cb8\u5df2\u662f\u4e0d\u8fdc\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u4fbf\u5230\u4e86\u897f\u6d1e\u5ead\u5c71\u7684\u5c71\u811a\uff0c\u4e8e\u627f\u73e0\u5c06\u5c0f\u821f\u6cca\u5cb8\uff0c\u821f\u4e2d\u767d\u9a6c \u5ffd\u5730\u4e00\u58f0\u957f\u5636\uff0c\u521a\u624d\u6e56\u5fc3\u6fc0\u6218\u4e4b\u65f6\uff0c\u5b83\u4e00\u70b9\u4e5f\u4e0d\u5bb3\u6015\uff0c\u6ca1\u53eb\u8fc7\u4e00\u58f0\uff0c\u73b0\u5728\u5374\u7eb5\u58f0\u957f\u5636\uff0c\u4e8e\u627f \u73e0\u7b11\u9053\uff1a\u201c\u5feb\u5230\u5bb6\u5566\uff0c\u4f60\u8fd8\u53eb\u4ec0\u4e48\uff1f\u201d\u8f6c\u8eab\u7275\u9a6c\uff0c\u5ffd\u5730\u821f\u4e2d\u8dc3\u51fa\u4e00\u6761\u9ed1\u5f71\uff0c\u731b\u4e0d\u9632\u5730\u5728\u5979\u80f8 \u53e3\u4e00\u62b9\uff0c\u53c8\u5728\u5979\u9762\u4e0a\u4e00\u62b9\uff0c\u6e7f\u6f09\u6f09\u7684\u6ee1\u662f\u6ce5\u6d46\uff0c\u8fde\u773c\u775b\u4e5f\u51e0\u4e4e\u7741\u4e0d\u5f00\u6765\uff0c\u4e8e\u627f\u73e0\u4e00\u7529\u5934\u4e00\u638c \u659c\u62cd\uff0c\u90a3\u9ed1\u5f71\u5df2\u8df3\u5230\u5cb8\u4e0a\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u8fd9\u56de\u4f60\u8fd8\u4e0d\u7740\u6211\u7684\u9053\u513f\uff01\u5475\uff0c\u4f60\u8fd9\u5c0f\u5b50\uff0c\u539f\u6765\u4e0d\u662f \u5c0f\u5b50\uff0c\u662f\u4e2a\u5927\u59d1\u5a18\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u7741\u773c\u4e00\u770b\uff0c\u770b\u6e05\u695a\u4e86\uff0c\u539f\u6765\u8fd9\u9ed1\u8863\u5c0f\u7ae5\u5c31\u662f\u5f20\u98ce\u5e9c\u7684\u513f\u5b50\u5c0f\u864e\u5b50\uff01\u771f\u662f\u559c\u51fa\u671b \u5916\uff0c\u5fc3\u9053\uff1a\u201c\u5f20\u98ce\u5e9c\u4e34\u7ec8\u4e4b\u65f6\uff0c\u6258\u6a0a\u82f1\u8f6c\u6258\u6211\u7684\u5e08\u7236\u89c5\u4ed6\u7684\u8e2a\u8ff9\uff0c\u6536\u4ed6\u4e3a\u5f92\uff0c\u4eba\u6d77\u832b\u832b\uff0c\u6b63 \u4e0d\u77e5\u4f55\u65f6\u5bfb\u5230\uff01\u539f\u6765\u4ed6\u5374\u5148\u6765\u4e86\u8fd9\u91cc\uff01\u201d\u8fd9\u4e00\u559c\u4ee4\u5979\u607c\u6012\u5168\u6d88\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u864e\u5b50\u5475\uff0c\u4f60\u8fd9\u5c0f \u987d\u76ae\uff0c\u770b\u4f60\u9003\u5230\u54ea\u91cc\uff1f\u201d\u8dc3\u4e0a\u5cb8\u6765\u4fbf\u6293\uff0c\u5c0f\u864e\u5b50\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u4e0e\u5c0f\u599e\u513f\u620f\u800d\uff0c\u54c8\uff0c\u4eba\u6765 \u5566\uff01\u201d\u53d1\u8db3\u98de\u5954\uff0c\u6377\u4f3c\u733f\u7334\uff0c\u722c\u4e0a\u5c71\u5761\uff0c\u8eb2\u5165\u6811\u6797\u5b50\u53bb\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5446\u4e86\u4e00\u5446\uff0c\u8fd9\u624d\u53d1\u89c9\u81ea\u5df1\u7684\u675f\u5934\u5dfe\u5df2\u88ab\u5c0f\u864e\u5b50\u626f\u8131\uff0c\u5934\u53d1\u6563\u4e71\uff0c\u80f8\u524d\u5370\u6709\u638c\u5370\uff0c \u9762\u4e0a\u6ee1\u662f\u6ce5\u6d46\uff0c\u8863\u88f3\u90a3\u5c31\u66f4\u4e0d\u6d88\u8bf4\u4e86\u3002\u8fdc\u5904\u5ffd\u7136\u6709\u4e24\u4e2a\u4e61\u4eba\u8d70\u6765\uff0c\u4e8e\u627f\u73e0\u751a\u662f\u7231\u6d01\uff0c\u5982\u6b64\u5f62 \u72b6\uff0c\u81ea\u89c9\u4e0d\u96c5\uff0c\u6025\u5fd9\u56de\u5230\u8239\u4e2d\uff0c\u7406\u597d\u5934\u53d1\uff0c\u6d17\u51c0\u4e86\u8138\uff0c\u6362\u8fc7\u8863\u88f3\uff0c\u518d\u51fa\u6765\u65f6\uff0c\u4e0d\u4f46\u5c0f\u864e\u5b50\u65e9 \u5df2\u4e0d\u89c1\uff0c\u90a3\u4e24\u4e2a\u4e61\u4eba\u4e5f\u8d70\u8fc7\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u72ec\u81ea\u767b\u5c71\uff0c\u5fc3\u4e2d\u7591\u60d1\u4e0d\u89e3\uff0c\u60f3\u9053\uff1a\u201c\u90a3\u5c0f\u864e\u5b50\u867d\u7136\u673a\u7075\u4e4b\u6781\uff0c\u6ca1\u4eba\u5e26\u9886\uff0c\u4ed6\u5982\u4f55 \u80fd\u5bfb\u5230\u6b64\u95f4\uff1f\u4ec5\u4ec5\u76f8\u9694\u6708\u4f59\uff0c\u770b\u4ed6\u8eab\u624b\uff0c\u6b66\u529f\u7adf\u662f\u5927\u5927\u589e\u957f\uff0c\u90a3\u5b9a\u7136\u662f\u6709\u9ad8\u624b\u6307\u70b9\u7684\u4e86\u3002\u8fd9 \u4e2a\u4eba\u53c8\u662f\u8c01\uff1f\u83ab\u975e\u5c31\u662f\u6211\u7684\u5e08\u7236\uff1f\u96be\u9053\u4ed6\u65e9\u5df2\u77e5\u9053\u6d88\u606f\uff0c\u51fa\u53bb\u5bfb\u8bbf\uff0c\u5c06\u5c0f\u864e\u5b50\u6536\u4e3a\u5f92\u5f1f \u4e86\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u4e00\u8def\u601d\u7d22\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u884c\u81f3\u534a\u5c71\uff0c\u592a\u6e56\u4e2d\u7684\u897f\u6d1e\u5ead\u5c71\u662f\u4e2a\u82b1\u679c\u4e4b\u5c71\uff0c\u5c71\u4e0b\u7530\u752b\u6210 \u884c\uff0c\u5c71\u4e0a\u5c3d\u662f\u679c\u6811\uff0c\u6d53\u8584\u76f8\u63a5\uff0c\u82b1\u679c\u98d8\u9999\uff0c\u5728\u8fd9\u4e2a\u66ae\u6625\u65f6\u8282\uff0c\u6b63\u662f\u4e61\u6c11\u5fd9\u5e72\u64cd\u4f5c\u7684\u65f6\u5019\uff0c\u4f46 \u5982\u4eca\u4e00\u8def\u884c\u6765\uff0c\u65e2\u4e0d\u95fb\u91c7\u8336\u59d1\u5a18\u7684\u5c71\u6b4c\u916c\u7b54\uff0c\u4ea6\u4e0d\u89c1\u5c71\u4e0b\u63d2\u79e7\u7684\u519c\u592b\uff0c\u9664\u4e86\u9002\u624d\u90a3\u4e24\u4e2a\u8fc7\u8def \u7684\u4e61\u4eba\u4e4b\u5916\uff0c\u7a3b\u7530\u91cc\u679c\u676f\u4e2d\uff0c\u7adf\u662f\u9759\u4fcf\u4fcf\u7684\u9602\u65e0\u4eba\u5f71\uff0c\u8fd9\u79cd\u53cd\u5e38\u7684\u73b0\u8c61\uff0c\u8fde\u4e8e\u627f\u73e0\u4ea6\u611f\u6014\u5fe1 \u4e0d\u5b89\u3002\u5f53\u4e0b\u52a0\u5feb\u811a\u7a0b\uff0c\u6025\u6025\u5411\u6d1e\u5ead\u5c71\u5e84\u5954\u53bb\u3002 \u3000\u3000\u201c\u6d1e\u5ead\u5c71\u5e84\u201d\u672c\u6765\u662f\u4e91\u91cd\u7684\u5cb3\u7236\uff0c\u6fb9\u53f0\u4ef2\u5143\u7684\u4ea7\u4e1a\uff0c\u540e\u6765\u4e91\u91cd\u592b\u5987\u4f4f\u5feb\u6d3b\u6797\uff0c\u8fd9\u91cc\u4fbf\u8ba9 \u5f20\u4e39\u67ab\u4e00\u5bb6\u4eba\u5c45\u4f4f\uff0c\u5c71\u5e84\u5efa\u5728\u5c71\u8170\u7684\u4e07\u6728\u4e1b\u4e2d\uff0c\u4f9d\u7740\u5c71\u52bf\uff0c\u5efa\u4e86\u8bb8\u591a\u4ead\u53f0\u697c\u9601\uff0c\u9762\u79ef\u4e0d\u53ca\u5feb \u6d3b\u6797\u4e4b\u5927\uff0c\u4f46\u98ce\u666f\u5e7d\u83ab\uff0c\u5374\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d\u53ca\u3002\u4e8e\u627f\u73e0\u8d70\u5230\u5e84\u524d\uff0c\u6709\u5982\u6e38\u5b50\u56de\u5bb6\uff0c\u80f8\u895f\u8212\u7545\uff0c \u8f7b\u6263\u5e84\u95e8\uff0c\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u6211\u56de\u6765\u5566\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5728\u6d1e\u5ead\u5c71\u5e84\u957f\u5927\uff0c\u5979\u7684\u58f0\u97f3\uff0c\u65e0\u4eba\u4e0d\u8bc6\uff0c\u4e0d\u6599\u53eb\u4e86\u4e09\u58f0\uff0c\u65e0\u4eba\u7b54\u5e94\u3002\u4e8e\u627f\u73e0\u597d\u751f \u8be7\u5f02\uff0c\u5fc3\u9053\uff1a\u201c\u90a3\u4e9b\u5e84\u4e01\u54ea\u91cc\u53bb\u4e86\uff1f\u201d\u8f7b\u8f7b\u4e00\u63a8\uff0c\u5e84\u95e8\u5e94\u624b\u800c\u5f00\uff0c\u539f\u6765\u662f\u865a\u63a9\u7684\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u56de\u6765\u5566\uff01\u201d\u58f0\u97f3\u98d8\u8361\u5728\u7a7a\u65f7\u7684\u56ed\u5b50\u91cc\uff0c\u663e\u5f97\u66f4\u662f\u51b7\u51b7\u6e05 \u6e05\uff0c\u4e8e\u627f\u73e0\u4e0d\u7981\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u62ac\u5934\u770b\u65f6\uff0c\u4f46\u89c1\u7d2b\u85e4\u76d8\u5f84\uff0c\u7e41\u82b1\u7167\u773c\uff0c\u7eff\u9769\u5982\u8335\uff0c\u51c9\u68da\u6c34 \u77f3\uff0c\u53c2\u5dee\u63a9\u6620\uff0c\u4ecd\u662f\u5f80\u65e5\u7684\u666f\u81f4\uff0c\u4e0d\u4f3c\u65e0\u4eba\u6599\u7406\uff0c\u4e8e\u627f\u73e0\u4e00\u9897\u5fc3\u4e03\u4e0a\u516b\u843d\uff0c\u7a7f\u8fc7\u5047\u5c71\uff0c\u7ed5\u8fc7 \u56de\u5eca\uff0c\u5148\u5230\u4e91\u857e\u5e73\u65e5\u7ec3\u529f\u7684\u9759\u5ba4\uff0c\u53e9\u95e8\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u662f\u6211\u56de\u6765\u5566\uff01\u201d\u91cc\u9762\u5bc2\u65e0\u4eba\u58f0\uff0c\u4e8e\u627f \u73e0\u63a8\u95e8\u4e00\u770b\uff0c\u4f46\u89c1\u56db\u58c1\u8427\u6761\uff0c\u8fde\u5b57\u753b\u90fd\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u96be\u9053\u5e08\u7236\u4e5f\u642c\u4e86\u5bb6\uff1f\u201d\u53c8\u8dd1\u5230\u5f20\u4e39\u67ab\u7684\u4e66\u623f\uff0c\u63a8\u5f00\u4e00\u770b\uff0c\u91cc\u9762\u9664\u4e86\u5899\u58c1 \u4e0a\u6302\u7740\u5f20\u4e39\u67ab\u81ea\u753b\u7684\u201c\u957f\u6c5f\u79cb\u8272\u56fe\u201d\u4e4b\u5916\uff0c\u4ea6\u662f\u7a7a\u65e0\u6240\u6709\u3002\u753b\u4e0a\u9898\u7684\u4e00\u9996\u8bd7\u58a8\u75d5\u72b9\u65b0\uff0c\u4ee5\u524d \u672a\u89c1\uff0c\u60f3\u662f\u65b0\u6dfb\u4e0a\u53bb\u7684\uff0c\u4e8e\u627f\u73e0\u5ff5\u9053\uff1a\u201c\u8c01\u628a\u82cf\u676d\u66f2\u5b50\u8bd3\uff1f\u8377\u82b1\u5341\u91cc\u6302\u4e09\u79cb\u3002\u90a3\u77e5\u5349\u6728\u65e0\u60c5 \u97e7\uff0c\u7275\u52a8\u957f\u6c5f\u4e07\u53e4\u6101\uff01\u201d\u8fd9\u662f\u5f20\u4e39\u67ab\u5e73\u65e5\u6700\u7231\u5ff5\u7684\u8bd7\uff0c\u5e38\u5e38\u6717\u541f\u4e4b\u540e\uff0c\u5927\u7b11\u4e00\u56de\u53c8\u5927\u54ed\u4e00 \u56de\uff0c\u4e8e\u627f\u73e0\u89c1\u4e86\u5e08\u7236\u7684\u7b14\u8ff9\uff0c\u5199\u7684\u53c8\u662f\u8fd9\u4e00\u9996\u9690\u85cf\u7740\u5e08\u7236\u8eab\u4e16\u4e4b\u75db\u7684\u8bd7\uff0c\u66f4\u662f\u4e0d\u5b89\uff0c\u7a81\u7136\u4e00 \u4e2a\u5ff5\u5934\u5347\u8d77\uff1a\u201c\u83ab\u975e\u662f\u5e08\u7236\u9047\u4e86\u610f\u5916\u4e86\uff1f\u201d\u4f46\u968f\u5373\u81ea\u5df1\u5550\u4e86\u4e00\u53e3\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u7edd\u4e0d\u53ef\u80fd\u4e4b \u4e8b\uff01\u6211\u5e08\u7236\u6b66\u529f\u76d6\u4e16\uff0c\u5c82\u6709\u906d\u9047\u610f\u5916\u4e4b\u7406\uff01\u201d \u3000\u3000\u504c\u5927\u7684\u5c71\u5e84\uff0c\u4e00\u70b9\u58f0\u606f\u4ed6\u6ca1\u6709\u3002\u4e8e\u627f\u73e0\u867d\u7136\u6df1\u4fe1\u5e08\u7236\u6b66\u529f\u76d6\u4e16\uff0c\u4e0d\u81f4\u906d\u9047\u610f\u5916\uff0c\u5374\u4e5f\u6709 \u70b9\u5fc3\u614c\u3002\u5979\u7a7f\u623f\u4eba\u5ba4\uff0c\u5bfb\u5bfb\u89c5\u89c5\uff0c\u5904\u5904\u90fd\u662f\u51b7\u51b7\u6e05\u6e05\uff0c\u51c4\u51c4\u5bc2\u5bc2\uff0c\u5979\u9ad8\u58f0\u53eb\u56b7\uff0c\u7a7a\u5c4b\u91cc\u53ea\u6709 \u81ea\u5df1\u7684\u56de\u58f0\uff0c\u6700\u540e\u5979\u6765\u5230\u4e86\u5f20\u4e39\u67ab\u7684\u5367\u623f\uff0c\u95e8\u7f1d\u95f4\u9690\u9690\u4f20\u51fa\u64c5\u9999\u7684\u6c14\u5473\uff0c\u8fd9\u662f\u4e91\u857e\u5e73\u65e5\u7684\u4e60 \u60ef\uff0c\u5728\u5367\u623f\u91cc\u603b\u559c\u6b22\u71c3\u8d77\u4e00\u7089\u6a80\u9999\u3002\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u600e\u4e48\u5e08\u7236\u5e08\u5a18\u767d\u5929\u4e5f\u8eb2\u5728\u623f\u95f4\u91cc\u9762\uff1f\u201d \u5979\u5fc3\u4e2d\u6e34\u5ff5\u5e08\u7236\uff1b\u867d\u7136\u89c1\u4e86\u5e84\u4e2d\u5f02\u50cf\uff0c\u4ecd\u662f\u81ea\u5df1\u5b89\u6170\u81ea\u5df1\uff0c\u8ba4\u8d70\u5e08\u7236\u5e08\u5a18\u8fd8\u7559\u5728\u5e84\u4e2d\u3002 \u3000\u3000\u5979\u4ec1\u7acb\u95e8\u5916\uff0c\u8f7b\u6263\u95e8\u73af\uff0c\u4f4e\u58f0\u5524\u9053\uff1a\u201c\u5e08\u7236\uff0c\u662f\u6211\u56de\u6765\u5566\u3002\u201d\u623f\u4e2d\u4ecd\u662f\u65e0\u4eba\u7b54\u8bdd\uff0c\u8d34\u8033 \u4e00\u542c\uff0c\u5374\u53c8\u4f3c\u542c\u5230\u547c\u5438\u7684\u6c14\u606f\uff0c\u4e8e\u627f\u73e0\u5927\u662f\u5947\u602a\uff1a\u201c\u96be\u9053\u5e08\u7236\u4ed6\u4eec\u767d\u5929\u4e5f\u7761\u5348\u89c9\uff1f\u201d\u8e0c\u8e87\u4e00 \u9635\uff0c\u7ec8\u4e8e\u8f7b\u8f7b\u5730\u63a8\u5f00\u4e86\u623f\u95e8\uff0c\u95ea\u8eab\u5165\u5185\u3002 \u3000\u3000\u53ea\u4e00\u773c\uff0c\u5c31\u51e0\u4e4e\u628a\u4e8e\u627f\u73e0\u5413\u5f97\u8df3\u4e86\u8d77\u6765\u3002\u53ea\u89c1\u623f\u4e2d\u4e24\u5f20\u5367\u5e8a\uff0c\u4e0a\u9762\u5404\u6709\u4e00\u4eba\u76d8\u819d\u800c\u5750\uff0c \u5de6\u8fb9\u7684\u5168\u8eab\u6f06\u9ed1\uff0c\u53f3\u8fb9\u7684\u5374\u8fde\u7709\u6bdb\u90fd\u662f\u767d\u6e17\u6e17\u7684\u602a\u5f97\u6015\u4eba\uff0c\u4e00\u9ed1\u4e00\u767d\uff0c\u76f8\u6620\u6210\u8da3\uff0c\u53ea\u662f\u9664\u4e86 \u80a4\u8272\u4e0d\u540c\u4e4b\u5916\uff0c\u8eab\u6750\u76f8\u8c8c\u5374\u53c8\u751a\u4e3a\u76f8\u4f3c\uff0c\u50cf\u662f\u4e00\u6bcd\u6240\u751f\u7684\u5144\u5f1f\uff0c\u8fd9\u4e24\u4eba\u90fd\u662f\u5377\u53d1\u52fe\u9f3b\uff0c\u72ee\u53e3 \u6df1\u76ee\uff0c\u4e00\u770b\u5c31\u77e5\u662f\u5916\u56fd\u4eba\u3002\u800c\u4e14\u8fd9\u4e24\u4eba\u7684\u8eab\u4e0a\u8fd8\u6563\u53d1\u51fa\u4e00\u79cd\u8165\u8165\u7684\u6c14\u5473\uff0c\u8fde\u64c5\u9999\u7684\u6c14\u5473\u90fd\u63a9 \u76d6\u4e0d\u4e86\uff0c \u3000\u3000\u8fd9\u4e24\u4e2a\u602a\u4eba\u5bf9\u4e8e\u627f\u73e0\u7684\u8fdb\u623f\u7adf\u4f3c\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\uff0c\u5728\u5367\u69bb\u4e0a\u76d8\u819d\u8dc3\u5750\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002 \u4e24\u4eba\u90fd\u6ca1\u6709\u7a7f\u978b\u5b50\uff0c\u4e00\u53cc\u811a\u677f\uff0c\u53c8\u5927\u53c8\u9ed1\uff0c\u5728\u96ea\u767d\u7684\u5e8a\u6be1\u4e0a\u5370\u51fa\u4e86\u80ae\u810f\u7684\u9ed1\u5370\u3002\u4e8e\u627f\u73e0\u5927\u4e3a \u751f\u6c14\uff0c\u6307\u7740\u90a3\u4e24\u4e2a\u602a\u4eba\u559d\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4eec\u662f\u8c01\uff1f\u600e\u7684\u8fd9\u822c\u6ca1\u6709\u793c\u8c8c\uff1f\u201d\u90a3\u4e24\u4e2a\u602a\u4eba\u8fde\u773c\u775b\u4e5f \u4e0d\u7728\u4e00\u4e0b\uff0c\u5bf9\u4e8e\u627f\u73e0\u7684\u8bdd\u7adf\u662f\u76f8\u5e94\u4e0d\u7406\u3002\u4e8e\u627f\u73e0\u66f4\u6012\uff0c\u53c8\u559d\u9053\uff1a\u201c\u5582\uff0c\u8fd9\u662f\u6211\u5e08\u7236\u7684\u5367\u623f\uff0c \u4f60\u600e\u4e48\u53ef\u4ee5\u968f\u4fbf\u94bb\u8fdb\u6765\uff1f\u8fd8\u628a\u4ed6\u7684\u5e8a\u4e5f\u5f04\u810f\u4e86\u3002\u201d\u4e24\u4e2a\u602a\u4eba\u8fd9\u624d\u773c\u775b\u7728\u4e86\u4e00\u4e0b\uff0c\u56db\u9053\u773c\u5149\u4e00 \u9f50\u5c04\u5230\u4e8e\u627f\u73e0\u9762\u4e0a\uff0c\u4f46\u968f\u5373\u53c8\u5408\u4ec0\u4f4e\u9996\uff0c\u8fde\u770b\u4e5f\u4e0d\u7740\u5979\u4e86\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u90fd\u662f\u597d\u6d01\u4e4b\u4eba\uff0c\u623f\u95f4\u91cc\u7ea4\u5c18\u4e0d\u67d3\uff0c\u4e8e\u627f\u73e0\u77a7\u7740\u53c8\u662f\u6c14\u6124\uff0c\u53c8\u662f\u5fc3\u75bc\uff0c\u56b7 \u9053\uff1a\u201c\u4f60\u4eec\u518d\u4e0d\u7406\uff0c\u6211\u53ef\u8981\u4e0d\u5ba2\u6c14\u5566\u3002\u201d\u4f38\u51fa\u624b\u638c\uff0c\u671d\u5de6\u8fb9\u9762\u76ee\u64ce\u7f09\u7684\u90a3\u4e2a\u602a\u4eba\u4e00\u63a8\uff0c\u53ea\u89c9 \u624b\u6240\u89e6\u5904\u8f6f\u7ef5\u7ef5\uff0c\u597d\u50cf\u6253\u5728\u4e00\u5806\u68c9\u82b1\u4e0a\u4f3c\u7684\uff0c\u6beb\u65e0\u7740\u529b\u4e4b\u5904\uff0c\u4e8e\u627f\u73e0\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u602a\u4eba\u7adf\u7136 \u5177\u6709\u4e00\u8eab\u4e0a\u4e58\u7684\u5185\u529f\uff0c\u5979\u4e00\u8f6c\u8eab\uff0c\u53f3\u8fb9\u90a3\u4e2a\u602a\u4eba\u6b63\u5728\u54ea\u7259\u54a7\u5634\u5730\u51b2\u7740\u5979\u7b11\u54e9\uff01\u4e8e\u627f\u73e0\u4e00\u6012\uff0c \u547c\u5730\u4e00\u638c\u5411\u4ed6\u8170\u95f4\u7684\u8f6f\u9ebb\u7a74\u62cd\u53bb\uff0c\u5ffd\u89c9\u6709\u5982\u89e6\u7740\u4e00\u5757\u70eb\u70ed\u7684\u94c1\u677f\u4e00\u822c\uff0c\u4e8e\u627f\u73e0\u6025\u5fd9\u7f29\u624b\uff0c\u53ea \u89c1\u90a3\u4eba\u58eb\u8eab\u5fae\u5fae\u6643\u4e86\u4e00\u4e0b\uff0c\u4ecd\u5728\u602a\u7b11\u3002\u4e8e\u627f\u73e0\u5927\u6012\uff0c\u5587\u5730\u62d4\u5251\u51fa\u9798\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u4eec\u8d70\u4e0d\u8d70\uff0c \u5f20\u5927\u4fa0\u7684\u623f\u95f4\uff0c\u5c82\u5bb9\u4f60\u4eec\u80e1\u6405\uff1f\u201d\u5251\u5149\u4e00\u95ea\uff0c\u5148\u523a\u90a3\u9ed1\u9762\u602a\u4eba\u7684\u8170\u80c1\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u8fd9\u4e00\u5251\u4e43\u662f\u4e91\u857e\u6240\u8d50\u7ed9\u5979\u7684\u5b9d\u5251\uff0c\u540d\u4e3a\u201c\u9752\u51a5\u201d\u5b9d\u5251\uff0c\u4e0e\u5f20\u4e39\u67ab\u7684\u201c\u767d\u4e91\u201d\u5b9d\u5251 \u4e00\u96cc\u4e00\u96c4\uff0c\u90fd\u662f\u7384\u673a\u9038\u58eb\u82b1\u4e86\u5341\u5e74\u529f\u592b\u6240\u70bc\u6210\u7684\u5b9d\u5251\uff0c\u7aef\u7684\u524a\u94c1\u5982\u6ce5\uff0c\u5439\u6bdb\u7acb\u65ad\uff0c\u5c31\u662f\u91d1\u949f \u7f69\u94c1\u5e03\u886b\u7684\u529f\u592b\u4e5f\u62b5\u6321\u4e0d\u4f4f\uff0c\u4e8e\u627f\u73e0\u4e00\u65f6\u6012\u8d77\uff0c\u523a\u90a3\u7f09\u9762\u602a\u4eba\uff0c\u51fa\u624b\u4e4b\u540e\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u53ea\u7528 \u4e86\u4e09\u5206\u529b\u91cf\uff0c\u62e3\u4e0d\u662f\u8981\u5bb3\u4e4b\u5904\uff0c\u8f7b\u8f7b\u523a\u4e0b\uff0c\u5251\u5c16\u521a\u4e00\u89e6\u53ca\u90a3\u602a\u4eba\u7684\u8863\u88f3\uff0c\u9661\u7136\u4e00\u6ed1\uff0c\u6b6a\u8fc7\u4e00 \u8fb9\uff0c\u90a3\u602a\u4eba\u5ffd\u5730\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u6293\u75d2\u5417\uff1f\u6293\u75d2\u4e5f\u5f97\u7528\u70b9\u529b\u5440\uff01\u201d\u4e8e\u627f\u73e0\u53c8\u60ca\u53c8 \u6012\uff0c\u4e00\u6296\u5251\u67c4\uff0c\u7528\u529b\u4e00\u9001\uff0c\u53ea\u542c\u5f97\u55e4\u7684\u4e00\u58f0\uff0c\u8863\u88f3\u5212\u7834\uff0c\u4e8e\u627f\u73e0\u53c8\u662f\u4e00\u60ca\uff0c\u53cd\u800c\u6015\u5c06\u4ed6\u523a \u6b7b\uff0c\u5fd9\u4e0d\u8fed\u5730\u7f29\u624b\uff0c\u4e0d\u6599\u5251\u5c16\u53c8\u662f\u4e00\u6ed1\uff0c\u90a3\u53e3\u9752\u51a5\u5b9d\u5251\uff0c\u7adf\u4f3c\u7ed9\u4e00\u5806\u68c9\u82b1\u88f9\u4f4f\uff0c\u62d4\u4e0d\u51fa\u6765\uff0c \u4e8c\u5c3a\u516b\u5bf8\u7684\u5251\u8eab\u5df2\u6709\u4e00\u534a\u7a7f\u5165\u4ed6\u7684\u80c1\u4e0b\uff0c\u7ed9\u602a\u4eba\u631f\u7740\uff0c\u4e0d\u80fd\u8f6c\u52a8\uff0c\u602a\u4eba\u8eab\u4e0a\u50cf\u6d82\u4e86\u6cb9\u8102\u4e00 \u6837\uff0c\u5251\u5c16\u6ed1\u6765\u6ed1\u53bb\uff0c\u4e0d\u80fd\u7740\u529b\uff0c\u4f11\u8bf4\u523a\u4f24\uff0c\u8fde\u76ae\u8089\u4e5f\u6ca1\u6709\u5212\u7834\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u6da8\u7ea2\u4e86\u9762\uff0c\u7528\u529b\u62d4\u5251\uff0c\u9888\u8116\u4e0a\u5ffd\u7136\u7ed9\u4eba\u5439\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u662f\u5c0f\u864e\u5b50\u7684\u58f0\u97f3\u683c\u683c\u7b11 \u9053\uff1a\u201c\u4f60\u6b22\u559c\u627e\u4eba\u6253\u67b6\uff0c\u627e\u5230\u6211\u7684\u5e08\u7236\u90a3\u53ef\u662f\u5012\u9709\u3002\u5582\uff0c\u8981\u4e0d\u8981\u6211\u7ed9\u4f60\u5e2e\u624b\uff1f\u201d\u90a3\u602a\u4eba\u5ffd\u5730 \u808c\u8089\u4e00\u677e\uff0c\u653e\u5f00\u4e86\u4e8e\u627f\u73e0\u7684\u5251\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u679c\u7136\u4e0d\u6127\u662f\u5f20\u4e39\u67ab\u592b\u5987\u7684\u5f92\u5f1f\uff01\u771f\u597d\u529f\u592b\uff01\u5c0f \u864e\u5b50\uff0c\u4f60\u5439\u4ec0\u4e48\u5927\u6c14\uff0c\u4f60\u518d\u7ec3\u4e09\u5e74\u8fd8\u8d76\u4e0d\u4e0a\u4ed6\u5462\uff01\u4ed6\u5c06\u6765\u662f\u4f60\u7684\u5e08\u5144\uff0c\u4f60\u8d76\u5feb\u8fc7\u6765\u62dc\u89c1\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u7741\u5927\u4e86\u773c\u775b\uff0c\u6301\u5251\u5728\u624b\uff0c\u60ca\u5f02\u4e4b\u6781\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u7aef\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u90a3\u9ed1\u9762\u602a\u4eba\u7b11 \u9053\uff1a\u201c\u4f60\u5e08\u7236\u6ca1\u6709\u548c\u4f60\u8bf4\u8fc7\u4e48\uff1f\u6211\u4eec\u662f\u9ed1\u81ea\u6469\u8bc3\uff01\u201d \u3000\u3000\u8fd9\u9ed1\u767d\u6469\u8bc3\u662f\u4e00\u6bcd\u5b6a\u751f\u7684\u5144\u5f1f\uff0c\u751f\u4e8e\u5370\u5ea6\uff0c\u5374\u5728\u4e2d\u56fd\u505a\u73e0\u5b9d\u4e70\u5356\uff0c\u548c\u5f20\u4e39\u67ab\u4e43\u662f\u81f3\u4ea4\uff0c \u4e0d\u8fc7\u5f20\u4e39\u67ab\u5f52\u9690\u592a\u6e56\u4e4b\u540e\uff0c\u4ed6\u4eec\u5374\u6ca1\u6709\u6765\u8fc7\u3002 \u3000\u3000\u8fd9\u9ed1\u767d\u6469\u8bc3\u7ec3\u6709\u5370\u5ea6\u7684\u745c\u73c8\u4e4b\u672f\uff0c\u5168\u8eab\u67d4\u82e5\u65e0\u9aa8\uff0c\u5404\u90e8\u808c\u8089\u90fd\u53ef\u968f\u610f\u626d\u66f2\u5c48\u4f38\uff0c\u4e8e\u627f\u73e0 \u6700\u521d\u53ea\u7528\u4e86\u4e09\u5206\u529b\u91cf\uff0c\u90a3\u81ea\u7136\u5bb9\u6613\u7ed9\u4ed6\u4e00\u4e0b\u5378\u5f00\u5251\u52bf\u3002\u8fd9\u79cd\u529f\u592b\u548c\u4e2d\u56fd\u7684\u4e0a\u4e58\u5185\u529f\u201c\u6cbe\u8863\u5341 \u516b\u8dcc\u201d\uff0c\u6709\u5f02\u66f2\u540c\u5de5\u4e4b\u5999\uff0c\u5f53\u5e74\u5f20\u4e39\u67ab\u521d\u4f1a\u9ed1\u6469\u8bc3\u65f6\uff0c\u4e5f\u51e0\u4e4e\u5403\u8fc7\u4ed6\u7684\u4e8f\uff0c\u4f55\u51b5\u5982\u4eca\u53c8\u8fc7\u4e86 \u5341\u591a\u5e74\uff0c\u9ed1\u6469\u8bc3\u7684\u529f\u592b\u5df2\u7ec3\u81f3\u51fa\u795e\u5165\u5316\u4e4b\u5883\u3002\u4e0d\u8fc7\uff0c\u8fd9\u79cd\u529f\u592b\u4e5f\u5168\u770b\u5bf9\u65b9\u7684\u529f\u529b\uff0c\u4e0d\u53ef\u8f7b\u6613 \u5c1d\u8bd5\u3002\u82e5\u7136\u662f\u6362\u4e86\u5f20\u4e39\u67ab\uff0c\u5219\u4e0d\u8981\u8bf4\u7528\u5b9d\u5251\uff0c\u53ea\u662f\u4e00\u628a\u7af9\u5251\uff0c\u9ed1\u6469\u8bc3\u4e5f\u4e0d\u6562\u8ba9\u4ed6\u523a\u4e2d\u7684\u3002 \u3000\u3000\u9ed1\u6469\u8bc3\u8d5e\u4e8e\u627f\u73e0\u201c\u4e0d\u6127\u662f\u5f20\u4e39\u67ab\u592b\u5987\u7684\u5f92\u5f1f\u3002\u201d\u4e8e\u627f\u73e0\u9762\u4e0a\u70ed\u8fa3\u8fa3\u7684\u66f4\u89c9\u4e0d\u597d\u610f\u601d\u3002\u5176 \u5b9e\u8fd9\u53e5\u8bdd\u7edd\u975e\u5632\u8bbd\uff0c\u4ee5\u4e8e\u627f\u73e0\u7684\u5e74\u7eaa\u4e4b\u8f7b\uff0c\u4e00\u638c\u80fd\u5c06\u767d\u6469\u8bc3\u63a8\u5f97\u4e0a\u8eab\u6447\u6643\uff0c\u4e00\u5251\u80fd\u5212\u7834\u9ed1\u6469 \u8bc3\u7684\u8863\u88f3\uff0c\u8fd9\u5df2\u662f\u96be\u80fd\u53ef\u8d35\u4e4b\u6781\u7684\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u542c\u8bf4\u662f\u9ed1\u767d\u6469\u8bc3\uff0c\u5fc3\u4e2d\u6012\u6c14\u6d88\u4e86\u4e00\u534a\uff0c\u4f46\u4ecd\u662f\u602a\u4ed6\u4eec\u4e0d\u8be5\u5982\u6b64\u65e0\u793c\uff0c\u5fc3\u9053\uff1a\u201c\u4f60 \u4eec\u7eb5\u662f\u5e08\u7236\u7684\u597d\u53cb\uff0c\u4e5f\u4e0d\u8be5\u767b\u5802\u5165\u5ba4\uff0c\u7b95\u8e1e\u5728\u5367\u69bb\u4e4b\u4e0a\uff01\u201d \u3000\u3000\u9ed1\u6469\u8bc3\u54a7\u5634\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5a03\u513f\u7b80\u76f4\u4e0d\u77e5\u597d\u574f\uff0c\u8981\u4e0d\u662f\u6211\u4eec\u548c\u4f60\u5e08\u7236\u6709\u8fc7\u547d\u7684\u4ea4\u60c5\uff0c\u6211 \u4eec\u624d\u4e0d\u9ad8\u5174\u8eb2\u8fdb\u8fd9\u5a18\u513f\u7684\u623f\u95f4\u53d7\u95f7\u6c14\u5462\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u767d\u6469\u8bc3\u9053\uff1a\u201c\u4ec0\u4e48\u600e\u4e48\u4e0d \u600e\u4e48\u7684\uff1f\u201d\u6307\u7740\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u521a\u624d\u5728\u6e56\u4e0a\u548c\u72d7\u817f\u5b50\u4eec\u6253\u4e86\u4e00\u67b6\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u5c0f\u864e\u5b50\u7b11 \u9053\uff1a\u201c\u8fd8\u7ed9\u4eba\u6253\u5f97\u597d\u72fc\u72c8\u5462\uff0c\u4f60\u77a7\uff0c\u8fd9\u91cc\u8fd8\u6709\u6c61\u6ce5\u3002\u201d\u987a\u624b\u4e00\u62b9\uff0c\u5728\u4e8e\u627f\u73e0\u7684\u8896\u5b50\u4e0a\u53c8\u5370\u4e0a \u4e00\u4e2a\u638c\u5370\u3002\u4e8e\u627f\u73e0\u53cd\u624b\u4e00\u62ff\uff0c\u8f7b\u8f7b\u5728\u4ed6\u814b\u7a9d\u4e00\u634f\uff0c\u5c0f\u864e\u5b50\u7b11\u5f97\u6c14\u4e5f\u900f\u4e0d\u8fc7\u6765\uff0c\u4e8e\u627f\u73e0\u9a82\u9053\uff1a \u201c\u90fd\u662f\u4f60\u8fd9\u5c0f\u9b3c\uff0c\u518d\u987d\u76ae\uff0c\u77a7\u6211\u4e0d\u628a\u4f60\u6574\u6cbb\u4e2a\u591f\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u4f60\u7b2c\u4e00\u6b21\u89c1\u6211\u5c31\u5f04\u5f97\u6211\u6ee1 \u8eab\u6c61\u6ce5\uff0c\u4eca\u6b21\u662f\u4e00\u62a5\u8fd8\u4e00\u62a5\uff0c\u4f60\u8fd8\u602a\u6211\uff1f\u54ce\u54df\uff01\u6211\u4e0d\u548c\u4f60\u73a9\u5566\uff0c\u4f60\u8fd9\u599e\u513f\u5c31\u4e13\u4f1a\u6b3a\u8d1f\u4eba\u3002\u201d \u5c0f\u864e\u5b50\u867d\u7136\u53ea\u6709\u5341\u4e09\u5c81\uff0c\u4f46\u5374\u957f\u5f97\u6bd4\u4e8e\u627f\u73e0\u4ec5\u4ec5\u77ee\u534a\u4e2a\u5934\uff0c\u4e8e\u627f\u73e0\u80f3\u80a2\u5c0f\u864e\u5b50\u7684\u814b\u7a9d\uff0c\u987a\u624b \u4e00\u62c9\uff0c\u5c0f\u864e\u5b50\u51e0\u4e4e\u4f0f\u5012\u5979\u7684\u8eab\u4e0a\uff0c\u4e8e\u627f\u73e0\u8fd9\u624d\u4e00\u7b11\u5c06\u4ed6\u63a8\u5f00\u3002 \u3000\u3000\u53ea\u542c\u5f97\u9ed1\u6469\u8bc3\u7eed\u9053\uff1a\u201c\u72d7\u817f\u5b50\u4eec\u8fde\u4f60\u4e5f\u4e0d\u653e\u8fc7\uff0c\u53c8\u600e\u80af\u653e\u8fc7\u4f60\u7684\u5e08\u7236\uff1f\u201d\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00 \u51db\uff0c\u60f3\u8d77\u5f20\u98ce\u5e9c\u7684\u906d\u9047\uff0c\u53eb\u9053\uff1a\u201c\u6211\u7684\u5e08\u7236\u4e00\u5b9a\u662f\u6015\u7687\u5e1d\u5bb3\u4ed6\uff0c\u6240\u4ee5\u8d70\u4e86\u3002\u201d\u5979\u6700\u662f\u5d07\u62dc\u5e08 \u7236\uff0c\u4ee5\u4e3a\u5e08\u7236\u4ec0\u4e48\u90fd\u80fd\u5e94\u4ed8\uff0c\u6545\u6b64\u8fde\u8fd9\u70b9\u6d45\u663e\u7684\u9053\u7406\uff0c\u4e00\u65f6\u4e5f\u60f3\u4e0d\u8d77\u3002\u767d\u6469\u8bc3\u9053\uff1a\u201c\u4f60\u5e08\u7236 \u4e0d\u613f\u60f9\u4e8b\uff0c\u6211\u4eec\u5144\u5f1f\u5374\u504f\u504f\u8981\u66ff\u4ed6\u51fa\u4e00\u53e3\u6c14\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u7684\u5e08\u7236\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d\u9ed1\u6469 \u8bc3\u9053\uff1a\u201c\u4ed6\u53ef\u8d70\u5f97\u8fdc\u5462\u2026\u2026\u201d\u5ffd\u7136\u505c\u4e86\u8bf4\u8bdd\uff0c\u4fa7\u8033\u4e00\u542c\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff0c\u6211\u524d\u5929\u6559\u7ed9\u4f60\u7684 \u62f3\u7ecf\uff0c\u4f60\u8fd8\u8bb0\u5f97\u4e48\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u8bb0\u5f97\uff0c\u8981\u4e0d\u8981\u6211\u80cc\u8bf5\u7ed9\u4f60\u542c\u3002\u201d\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u5355\u4f1a\u80cc\u8bf5 \u6709\u4ec0\u4e48\u7528\uff0c\u8981\u7d27\u7684\u662f\u80fd\u591f\u4e34\u654c\u5e94\u7528\uff0c\u7b49\u4e0b\u6211\u5c31\u6559\u4f60\u4e00\u8bfe\uff0c\u6559\u4f60\u600e\u6837\u5728\u654c\u4f17\u6211\u5be1\u7684\u60c5\u5f62\u4e4b\u4e0b\uff0c \u8fd0\u7528\u7f57\u6c49\u795e\u62f3\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u597d\u554a\uff0c\u662f\u5230\u540e\u9762\u7684\u7ec3\u6b66\u573a\u6559\u4e48\uff1f\u201d\u9ed1\u6469\u6cb3\u9053\uff1a\u201c\u4e0d\uff0c\u5c31\u5728\u8fd9 \u91cc\uff0c\u7b49\u4e0b\u4f60\u77a7\u5f97\u4ed4\u7ec6\u4e00\u4e9b\uff01\u597d\uff0c\u73b0\u5728\uff0c\u4f60\u4eec\u5c31\u8eb2\u5230\u8863\u67dc\u4e0a\u53bb\u3002\u201d\u5f20\u4e39\u67ab\u7684\u5367\u623f\u4e2d\u6709\u4e00\u4e2a\u5927\u8863 \u67dc\uff0c\u7ea6\u6709\u4e24\u4e2a\u4eba\u9ad8\uff0c\u5c0f\u864e\u5b50\u6b63\u5728\u5947\u602a\uff0c\u600e\u4e48\u7ec3\u62f3\u8981\u5230\u8863\u67dc\u4e0a\u53bb\u7ec3\uff0c\u5ffd\u542c\u5f97\u95e8\u5916\u7eb7\u7eb7\u7684\u4eba\u58f0\u811a \u6b65\u58f0\uff0c\u4e8e\u627f\u73e0\u628a\u4ed6\u4e00\u62c9\uff0c\u8dc3\u5230\u8863\u67dc\u4e0a\uff0c\u4e24\u4eba\u6324\u5728\u4e00\u8d77\uff0c\u4e8e\u627f\u73e0\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u6709\u597d\u620f\u770b\u5566\uff0c\u4f60 \u7684\u5e08\u7236\u8981\u501f\u654c\u4eba\u505a\u9776\u5b50\uff0c\u7ec3\u62f3\u7ed9\u54b1\u4eec\u770b\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u623f\u5916\u6709\u4eba\u53eb\u9053\uff1a\u201c\u7687\u4e0a\u6709\u8bcf\uff0c\u5ba3\u5f20\u4e39\u67ab\u8dea\u63a5\uff01\u201d\u9ed1\u6469\u8bc3\u634f\u7740\u55d3\u5b50\uff0c\u5b66\u5f20\u4e39\u67ab\u7684\u53e3 \u97f3\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\u72d7\u5c41\u7687\u5e1d\uff0c\u54b1\u8001\u5b50\u504f\u4e0d\u63a5\u4ed6\u7684\u72d7\u5c41\u8bcf\u4e66\uff01\u201d\u9ed1\u6469\u8bc3\u662f\u5370\u5ea6\u4eba\uff0c\u4e2d\u56fd\u8bdd\u672c\u6765\u5c31 \u8bb2\u5f97\u4e0d\u597d\uff0c\u53e3\u97f3\u867d\u5b66\u5f97\u6709\u51e0\u5206\u76f8\u4f3c\uff0c\u4f46\u5374\u663e\u5f97\u7c97\u91cc\u7c97\u6c14\uff0c\u751f\u751f\u786c\u786c\uff0c\u66f4\u5999\u7684\u662f\uff0c\u5f20\u4e39\u67ab\u4f55\u7b49 \u65af\u6587\uff0c\u9ed1\u6469\u8bc3\u5374\u6ee1\u53e3\u7c97\u8bdd\uff0c\u4e8e\u627f\u73e0\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u7b11\uff0c\u5fc3\u4e2d\u9a82\u9053\uff1a\u201c\u771f\u662f\u72d7\u5c41\uff0c\u6211\u5e08\u7236\u4ece\u6765\u5c31\u4e0d \u8bb2\u72d7\u5c41\u3002\u201d\u623f\u5916\u7684\u4eba\u66f4\u662f\u60ca\u8be7\u4e07\u5206\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u4f60\u6562\u8fd9\u6837\u65e0\u793c\uff0c\u4e0d\u6015\u6284\u5bb6\u706d\u65cf \u5417\uff1f\u201d\u201c\u7830\u201d\u5730\u4e00\u58f0\uff0c\u8e22\u5f00\u623f\u95e8\u3002 \u3000\u3000\u95e8\u5916\u9ad8\u77ee\u80a5\u7626\uff0c\u5806\u6ee1\u4e86\u4eba\uff0c\u6768\u5343\u65a4\u3001\u91d1\u4e07\u4e24\u4e8c\u4eba\u4ea6\u5728\u5176\u5185\uff0c\u8fd9\u4e9b\u4eba\u90fd\u662f\u5949\u4e86\u7687\u5e1d\u7948\u9547\u4e4b \u547d\uff0c\u6765\u6349\u62ff\u5f20\u4e39\u67ab\u7684\uff0c\u7948\u9547\u77e5\u9053\u5f20\u4e39\u67ab\u6b66\u529f\u76d6\u4e16\uff0c\u8d77\u521d\u672c\u60f3\u6d3e\u6c34\u5e08\u6765\u5c06\u897f\u6d1e\u5ead\u5c71\u56e2\u56e2\u56f4\u7740\uff0c \u4f46\u6c34\u519b\u51fa\u52a8\uff0c\u98ce\u58f0\u5fc5\u9732\uff0c\u6df1\u6015\u5f20\u4e39\u67ab\u95fb\u98ce\u8fdc\u9041\uff0c\u6240\u4ee5\u6539\u6d3e\u4e86\u4e03\u540d\u5927\u5185\u7684\u4e00\u7b49\u536b\u58eb\u524d\u6765\uff0c\u4e0d\u6599 \u5f20\u4e39\u67ab\u4e00\u542c\u5230\u592a\u4e0a\u7687\u590d\u8f9f\u7684\u6d88\u606f\uff0c\u65e9\u5df2\u77e5\u673a\uff0c\u5148\u81ea\u8d70\u4e86\u3002\u8fd9\u4e9b\u4eba\u6765\u5230\u5927\u6e56\uff0c\u6251\u4e86\u4e2a\u7a7a\uff0c\u5fc3\u6709 \u4e0d\u7518\uff0c\u9042\u73af\u4f3a\u6e56\u8fb9\u3002\u6bcf\u65e5\u8f6e\u6d41\u6d3e\u51fa\u4e8c\u4eba\u5728\u57ce\u4e2d\u53ca\u6e56\u4e0a\u4fa6\u5bdf\uff0c\u8fd9\u65e5\u6768\u3001\u91d1\u4e8c\u4eba\uff0c\u53d1\u73b0\u4e86\u4e8e\u627f\u73e0 \u7684\u53ef\u7591\u8ff9\u8c61\uff0c\u5e03\u4e0b\u9677\u9635\uff0c\u8ffd\u5230\u6e56\u5fc3\uff0c\u4e0d\u6599\u5374\u683d\u4e86\u4e2a\u5927\u5927\u7684\u7b4b\u6597\uff0c\u4e8e\u627f\u73e0\u8131\u9669\u4e0a\u5c71\uff0c\u4ed6\u4eec\u968f\u5373 \u4e5f\u7ea0\u4f17\u8ddf\u8e2a\u5230\u3002 \u3000\u3000\u4ed6\u4eec\u8fd8\u4ee5\u4e3a\u5f20\u4e39\u67ab\u662f\u771f\u7684\u8fd8\u672a\u66fe\u8fdc\u53bb\uff0c\u8eb2\u5728\u623f\u4e2d\uff0c\u201c\u547c\u201d\u5730\u4e00\u58f0\uff0c\u8e22\u5f00\u4e86\u623f\u95e8\uff0c\u89c1\u4e86\u9ed1 \u767d\u6469\u8bc3\u7684\u602a\u76f8\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u662f\u8c01\uff1f\u201d\u9ed1\u6469\u8bc3\u9f88\u7259\u54a7\u5634\u5730\u51b2\u7740\u4ed6\u4eec\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6211\u4eec\u662f\u4e13\u5411\u72d7\u817f\u5b50\u8ffd\u9b42\u593a\u547d\u7684\u9ed1\u767d\u65e0\u5e38\u3002\u201d\u6768\u5343\u65a4\u53eb\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5c0f\u8d3c\u4e5f\u5728\u8fd9\u91cc\uff01\u201d\u624b\u6296 \u94c1\u94fe\uff0c\u7830\u7830\u4e24\u58f0\u5de8\u54cd\uff0c\u5c06\u623f\u95e8\u6253\u70c2\uff0c\u767d\u6469\u8bc3\u7b11\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u6211\u6b63\u6101\u6ca1\u6709\u9501\u9b3c\u7684\u94c1\u94fe\uff0c\u539f\u6765\u4f60 \u81ea\u5df1\u7ed9\u6211\u5e26\u6765\u4e86\uff01\u201d \u3000\u3000\u91d1\u4e07\u4e24\u9634\u607b\u607b\u5730\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u5728\u5224\u5b98\u9762\u524d\u88c5\u9b3c\u4f5c\u602a\uff0c\u5413\u5f97\u8c01\u6765\uff1f\u201d\u4ed6\u662f\u6697\u5668\u9ad8\u624b\uff0c\u4e00\u6296 \u624b\u4e00\u4f4e\u5934\uff0c\u5288\u7bad\u3001\u98de\u8757\u77f3\u3001\u94c1\u83b2\u5b50\uff0c\u4e00\u8fde\u53d1\u51fa\u5341\u51e0\u679a\u6697\u5668\uff0c\u5f20\u4e39\u67ab\u7684\u5367\u623f\u4e0d\u8fc7\u4e24\u4e08\u89c1\u65b9\uff0c\u9ed1 \u767d\u6469\u8bc3\u53c8\u662f\u76d8\u819d\u7aef\u5750\u5e8a\u4e0a\uff0c\u8fd9\u6697\u5668\u65ad\u65e0\u4e0d\u4e2d\u4e4b\u7406\uff0c\u53ea\u542c\u5f97\u9ed1\u767d\u6469\u8bc3\u540c\u58f0\u5927\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u8fd9 \u5c0f\u9b3c\u8fd8\u4f1a\u6293\u75d2\uff01\u201d\u7ba9\u7bad\u3001\u98de\u8757\u77f3\u3001\u94c1\u83b2\u5b50\u5168\u90e8\u6253\u4e2d\uff0c\u5374\u90fd\u662f\u5728\u8eab\u4e0a\u4e00\u64e6\u5373\u5760\uff0c\u7eb7\u7eb7\u843d\u5728\u5e8a \u4e0a\uff0c\u9ed1\u767d\u6469\u8bc3\u62cd\u62cd\u8863\u88f3\uff0c\u5c31\u597d\u50cf\u62cd\u6389\u7070\u5c18\u4f3c\u7684\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u518d\u6765\uff0c\u518d\u6765\uff01\u201d\u8863\u88f3\u8fde\u4e00\u4e2a\u5c0f \u5b54\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u91d1\u4e07\u4e24\u5927\u5403\u4e00\u60ca\uff0c\u6768\u5343\u65a4\u6c89\u4e0d\u4f4f\u6c14\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u4e00\u8dc3\u5165\u623f\uff0c\u94c1\u94fe\u6296\u5f97\u54d7\u5566\u5566\u4f5c\u54cd\uff0c\u8fd9\u6761 \u94c1\u94fe\u6709\u4e00\u4e08\u4e03\u5c3a\uff0c\u4e00\u6296\u5f00\u6765\uff0c\u5728\u95e8\u53e3\u53ef\u4ee5\u6253\u5230\u5185\u5899\uff0c\u94c1\u94fe\u4e00\u4e2a\u76d8\u65cb\uff0c\u547c\u5730\u4e00\u58f0\uff0c\u5411\u9ed1\u6469\u8bc3\u62e6 \u8170\u626b\u5230\uff0c\u7d22\u5c3e\u5219\u7f20\u5411\u767d\u6469\u8bc3\uff0c\u53ea\u89c1\u9ed1\u6469\u8bc3\u632f\u81c2\u4e00\u6325\uff0c\u53eb\u9053\uff1a\u201c\u5999\u5475\uff0c\u5999\u5475\uff01\u201d\u90a3\u94c1\u94fe\u9661\u5730\u98de \u4e86\u56de\u6765\uff0c\u6768\u5e72\u65a4\u6b63\u5728\u7528\u529b\uff0c\u88ab\u9ed1\u6469\u8bc3\u7684\u52b2\u529b\u4e00\u9001\u4e00\u62c9\uff0c\u8eab\u4e0d\u7531\u5df1\u5730\u987a\u7740\u94c1\u94fe\u5411\u524d\u75be\u5954\uff0c\u767d\u6469 \u8bc3\u62ff\u7740\u94c1\u94fe\u7684\u53e6\u4e00\u7aef\uff0c\u8f7b\u8f7b\u4e00\u7ed5\uff0c\u7acb\u5373\u5c06\u6768\u5e72\u65a4\u7684\u53cc\u624b\u675f\u7740\uff0c\u53cd\u7f1a\u80cc\u540e\uff0c\u7b11\u9053\uff1a\u201c\u7f1a\u7740\u4e00\u4e2a \u5c0f\u9b3c\u4e86\u3002\u201d\u90a3\u94c1\u94fe\u751a\u957f\uff0c\u7f1a\u7740\u4e86\u6768\u5343\u65a4\uff0c\u5269\u4e0b\u7684\u90a3\u5927\u534a\u622a\u8fd8\u6709\u4e00\u4e08\uff0c\u88ab\u9ed1\u6469\u8bc3\u4e00\u6325\uff0c\u957f\u86c7\u822c \u5730\u4f38\u5230\u95e8\u53e3\u6a2a\u7a7a\u4e00\u5377\uff0c\u516d\u540d\u536b\u58eb\u4e2a\u4e2a\u7eb5\u8eab\u524d\u8dc3\uff0c\u5168\u90e8\u7ed9\u94c1\u94fe\u8feb\u8fdb\u623f\u4e2d\uff0c\u9661\u89c1\u9ed1\u6469\u8bc3\u4ece\u5e8a\u4e0a\u98de \u8d77\uff0c\u51e0\u4e4e\u5c31\u5728\u540c\u4e00\u77ac\u95f4\uff0c\u536b\u58eb\u5165\u623f\uff0c\u4ed6\u5374\u843d\u5230\u95e8\u53e3\uff0c\u5f53\u95e8\u4e00\u7ad9\uff0c\u5c31\u5982\u4e00\u4e2a\u62e6\u95e8\u7684\u9ed1\u715e\u795e\uff0c\u9ad8 \u58f0\u53eb\u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff0c\u4f60\u77a7\u6e05\u695a\u4e86\uff01\u201d \u3000\u3000\u536b\u58eb\u4eec\u89c1\u6b64\u58f0\u52bf\uff0c\u4e0d\u5bd2\u800c\u6817\u3002\u4f46\u4ed7\u7740\u4eba\u591a\uff0c\u9f13\u52c7\u800c\u4e0a\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5df2\u6709\u4e00\u540d\u536b \u58eb\uff0c\u624b\u6325\u94c1\u5c3a\uff0c\u671d\u7740\u5728\u5e8a\u4e0a\u76d8\u819d\u800c\u5750\u7684\u767d\u6469\u8bc3\uff0c\u5f53\u5934\u4e00\u68d2\uff0c\u767d\u6469\u8bc3\u5927\u543c\u4e00\u58f0\uff0c\u5de6\u62f3\u4e00\u51b2\uff0c\u53f3 \u62f3\u4e00\u843d\uff0c\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u90a3\u540d\u536b\u58eb\u7684\u8155\u9aa8\u5f53\u573a\u788e\u88c2\uff0c\u4e00\u6761\u624b\u81c2\u540a\u4e86\u4e0b\u6765\uff0c\u9ed1\u6469\u8bc3\u53eb\u9053\uff1a\u201c\u8fd9 \u662f\u864e\u62f3\uff01\u201d\u767d\u6469\u8bc3\u98de\u8eab\u8dc3\u8d77\uff0c\u7b2c\u4e8c\u4e2a\u536b\u58eb\u51b2\u5230\uff0c\u88ab\u4ed6\u4e00\u62f3\u5288\u4e0b\uff0c\u6025\u6025\u659c\u95ea\uff0c\u4f46\u54ea\u91cc\u8fd8\u907f\u5f97 \u53ca\uff0c\u9ed1\u6469\u8bc3\u62f3\u5934\u5728\u4ed6\u9762\u4e0a\u4e00\u6643\uff0c\u4e00\u4e2a\u52fe\u62f3\uff0c\u6b63\u6b63\u6253\u4e2d\u9f3b\u5c16\uff0c\u9f3b\u5b50\u6253\u584c\uff0c\u8fde\u4ed6\u773c\u73e0\u4e5f\u6253\u5f97\u51f8\u4e86 \u51fa\u6765\uff0c\u9ed1\u6469\u8bc3\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u8c79\u62f3\uff0c\u5582\uff0c\u6253\u5f97\u6162\u4e00\u4e9b\uff0c\u8ba9\u5c0f\u864e\u5b50\u77a7\u6e05\u695a\u4e86\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u6211 \u77a7\u7740\u5462\uff01\u201d\u4e00\u540d\u536b\u58eb\u89c1\u52bf\u4e0d\u597d\uff0c\u7acb\u523b\u53cd\u5954\uff0c\u8fd9\u4eba\u64c5\u957f\u4e09\u5341\u516d\u8def\u8c2d\u817f\uff0c\u811a\u4e0a\u529f\u592b\uff0c\u5341\u5206\u4e86\u5f97\uff0c \u4e00\u8f6c\u8eab\u5c31\u8d77\u8fde\u73af\u98de\u811a\uff0c\u593a\u95e8\u800c\u5954\uff0c\u767d\u6469\u8bc3\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u8fd9\u662f\u4f60\u7684\u4e86\u3002\u201d\u9ed1\u6469\u8bc3\u4e94\u6307\u9760\u62e2\uff0c\u63e1 \u62f3\u5982\u9504\uff0c\u4e94\u6839\u6307\u9aa8\u5168\u90e8\u51f8\u51fa\uff0c\u53ea\u89c1\u4ed6\u8f7b\u8f7b\u4e00\u201c\u5544\u201d\uff0c\u90a3\u536b\u58eb\u5927\u53eb\u4e00\u58f0\uff0c\u819d\u76d6\u7ed9\u4ed6\u7684\u6307\u9aa8 \u201c\u5544\u201d\u5f97\u788e\u88c2\uff0c\u75db\u5165\u5fc3\u80ba\uff0c\u98de\u8d77\u7684\u5de6\u811a\u8fd8\u672a\u53ca\u843d\u4e0b\uff0c\u5931\u4e86\u91cd\u5fc3\uff0c\u7acb\u8db3\u4e0d\u7a33\uff0c\u4e00\u8de4\u8dcc\u843d\uff0c\u9ed1\u6469 \u8bc3\u5de6\u62f3\u987a\u624b\u4e00\u4e2a\u659c\u98de\u4e4b\u52bf\uff0c\u4e00\u6325\u4e00\u9001\uff0c\u201c\u547c\u201d\u5730\u4e00\u58f0\uff0c\u53c8\u628a\u90a3\u4eba\u9001\u56de\u623f\u5185\uff0c\u767d\u6469\u8bc3\u53eb\u9053\uff1a \u201c\u8fd9\u662f\u9e64\u62f3\uff01\u5582\uff0c\u4f60\u4e5f\u4e0d\u8981\u6253\u5f97\u8fd9\u6837\u5feb\u5475\uff0c\u7ed9\u54b1\u4eec\u7ec3\u9776\u5b50\u7684\u5c0f\u9b3c\u5c31\u53ea\u8fd9\u51e0\u4e2a\u5566\uff01\u201d\u5c0f\u864e\u5b50\u62cd \u638c\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u5927\u5e08\u7236\u771f\u5730\u50cf\u4e00\u53ea\u5927\u9e64\uff0c\u53ef\u60dc\u4e0d\u662f\u767d\u7684\uff0c\u8981\u662f\u4e8c\u5e08\u7236\u90a3\u5c31\u66f4\u50cf\u5566\uff01\u201d \u3000\u3000\u6768\u5343\u65a4\u529b\u5927\u5f02\u5e38\uff0c\u53cc\u624b\u867d\u88ab\u94c1\u94fe\u6240\u7f1a\uff0c\u7528\u529b\u4e00\u9707\uff0c\u6263\u7740\u7684\u4e24\u8282\u94c1\u73af\u7adf\u7136\u7ed9\u4ed6\u6323\u65ad\uff0c\u8d81\u770b \u767d\u6469\u8bc3\u8bf4\u8bdd\u7684\u5f53\u53e3\uff0c\u7528\u529b\u4e00\u62f3\uff0c\u5411\u4ed6\u80c1\u4e0b\u731b\u51fb\uff0c\u767d\u6469\u8bc3\u201c\u556a\u201d\u5730\u4e00\u4e0b\uff0c\u5de6\u624b\u63e1\u62f3\uff0c\u53f3\u638c\u4e0a\u4e00 \u64e6\uff0c\u638c\u5377\u62f3\u843d\uff0c\u53cc\u62f3\u787c\u4e2a\u6b63\u7740\uff0c\u6768\u5343\u65a4\u867d\u7136\u529b\u5927\uff0c\u5374\u54ea\u6321\u5f97\u4f4f\u767d\u6469\u8bc3\u7684\u5185\u5bb6\u771f\u529b\uff0c\u767b\u65f6\u60e8\u53eb \u4e00\u58f0\uff0c\u864e\u53e3\u6d41\u8840\uff0c\u4e94\u6839\u6307\u9aa8\u5168\u90fd\u7ed9\u767d\u6469\u8bc3\u634f\u788e\uff0c\u9ed1\u6469\u8bc3\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u9f99\u62f3\uff01\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b \u5e95\u4e1d\u6beb\u4e0d\u7f13\uff0c\u4e00\u62db\u957f\u86c7\u51fa\u6d1e\uff0c\u5148\u5410\u638c\u540e\u51fa\u62f3\uff0c\u201c\u7830\u201d\u5730\u4e00\u58f0\uff0c\u53c8\u628a\u4e00\u540d\u536b\u571f\u6253\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c \u5c0f\u864e\u5b50\u53eb\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u77e5\u9053\uff0c\u8fd9\u662f\u86c7\u62f3\uff01\u201d \u3000\u3000\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u518d\u770b\u4e00\u62db\uff0c\u8fd9\u662f\u4ec0\u4e48\u62f3\uff1f\u201d\u53cc\u62f3\u73af\u62b1\uff0c\u4e00\u4e2a\u56de\u65cb\uff0c\u5de6\u62f3\u62f3\u80cc\u671d\u5916\uff0c \u77f3\u62f3\u62f3\u80cc\u671d\u5185\uff0c\u671d\u7740\u4e00\u540d\u536b\u58eb\u7684\u80cc\u8179\u7a81\u51fb\uff0c\u53ea\u89c1\u90a3\u540d\u536b\u58eb\u4e00\u4e2a\u541e\u80f8\u5438\u8179\uff0c\u638c\u5fc3\u4e00\u7ffb\uff0c\u7528\u4e86\u4e00 \u62db\u592a\u6781\u62f3\u7684\u201c\u6247\u901a\u80cc\u201d\uff0c\u7adf\u7136\u5378\u4e86\u9ed1\u6469\u8bc3\u7684\u62f3\u52bf\uff0c\u8131\u51fa\u8eab\u6765\uff0c\u4f46\u7ed9\u9ed1\u6469\u8bc3\u7684\u52b2\u529b\u4e00\u649e\uff0c\u4e5f\u5728 \u5730\u4e0a\u4e0d\u7531\u81ea\u5df1\u5730\u6253\u4e86\u51e0\u4e2a\u5708\u5708\u3002\u5c0f\u864e\u5b50\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u9f99\u62f3\uff0c\u4f46\u6ca1\u6709\u6253\u7740\uff0c\u53ea\u6253\u5f97\u654c\u4eba\u5f2f\u8170\u66f2 \u80cc\uff0c\u8fd9\u662f\u5d1b\u5c3e\u9f99\uff01\u201d \u3000\u3000\u8fd9\u4eba\u662f\u4e03\u540d\u536b\u58eb\u7684\u9996\u9886\uff0c\u540d\u4e3a\u674e\u6db5\u771f\uff0c\u662f\u9633\u5b97\u6d77\u7684\u526f\u624b\u4e4b\u4e00\uff0c\u9ed1\u6469\u8bc3\u82e5\u7528\u5168\u529b\uff0c\u81ea\u53ef\u5c06 \u4ed6\u4e00\u4e3e\u51fb\u5012\uff0c\u4f46\u4ed6\u4e3a\u4e86\u7ed9\u5c0f\u864e\u5b50\u7ec3\u62db\uff0c\u53ea\u7528\u4e86\u4e09\u5206\u529b\u91cf\uff0c\u674e\u6db5\u771f\u662f\u592a\u6781\u9ad8\u624b\uff0c\u81ea\u7136\u77e5\u9053\uff0c\u7b2c \u4e8c\u62db\u4e0d\u6562\u518d\u63a5\uff0c\u4e00\u7a9c\u8eab\u95ea\u5230\u540c\u4f34\u7684\u80cc\u540e\u3002 \u3000\u3000\u9ed1\u6469\u8bc3\u5927\u7b11\u9053\uff1a\u201c\u4f60\u6321\u5f97\u6211\u7684\u4e00\u62f3\uff0c\u4e5f\u7b97\u662f\u96be\u5f97\u7684\u597d\u624b\u4e86\u3002\u9976\u4f60\u4e0d\u6b7b\uff0c\u4e0b\u6b21\u4e0d\u53ef\u518d\u6765\uff0c \u518d\u6765\u5c31\u4e0d\u9976\u4e86\uff01\u201d\u4e00\u4e2a\u7bad\u62f3\uff0c\u5c06\u63a9\u7740\u4ed6\u7684\u540c\u4f34\u51fb\u5f97\u98de\u8d77\uff0c\u8dcc\u843d\u5e8a\u4e0a\uff0c\u5de6\u624b\u4e00\u6293\uff0c\u5df2\u628a\u674e\u6db5\u771f \u6293\u4e86\u8d77\u6765\uff0c\u5411\u95e8\u5916\u4e00\u7529\uff0c\u53ea\u542c\u788d\u54d7\u5566\u5566\u7684\u4e00\u7247\u5c4b\u74e6\u788e\u88c2\u4e4b\u58f0\uff0c\u6562\u60c5\u662f\u7ed9\u63b7\u5230\u7b2c\u4e8c\u95f4\u623f\u7684\u5c4b\u9876 \u53bb\u4e86\uff0c\u5c0f\u864e\u5b50\u53eb\u9053\uff1a\u201c\u55ef\uff0c\u8fd9\u4e0d\u662f\u7f57\u6c49\u795e\u62f3\uff0c\u8fd9\u662f\u5927\u6454\u7891\u624b\uff01\u201d \u3000\u3000\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u54c8\uff0c\u597d\u5c0f\u5b50\uff0c\u6709\u773c\u529b\uff0c\u77a7\u7740\uff0c\u7f57\u6c49\u795e\u62f3\u6765\u4e86\uff01\u201d\u521a\u624d\u7ed9\u51fb\u5230\u5e8a\u4e0a\u7684\u90a3\u540d\u536b \u58eb\uff0c\u53cd\u624b\u4e00\u6309\uff0c\u521a\u521a\u5f39\u8d77\uff0c\u88ab\u4ed6\u4e00\u62f3\u53c8\u6253\u4e2a\u6b63\u7740\uff0c\u518d\u8dcc\u56de\u5e8a\u4e2d\u3002\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u8fd9\u6cd5\u5b50\u4e0d\u9519\uff0c \u5728\u5e8a\u4e0a\u8dcc\u4ed6\u4e0d\u6b7b\uff0c\u53ef\u4ee5\u591a\u7ec3\u51e0\u8d9f\u3002\u201d \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u8fde\u51fa\u4e03\u62f3\uff0c\u6240\u53d7\u7684\u4eba\u6216\u8f7b\u6216\u91cd\u90fd\u53d7\u4e86\u4f24\uff0c\u54ea\u91cc\u8fd8\u6709\u6597\u5fd7\uff0c\u53ef\u662f\u767d\u6469\u8bc3\u5728\u623f\u5185\uff0c \u9ed1\u6469\u8bc3\u5728\u95e8\u53e3\uff0c\u4ed6\u4eec\u60f3\u9003\u4e5f\u9003\u4e0d\u51fa\u53bb\uff0c\u53ea\u542c\u5f97\u62f3\u98ce\u864e\u864e\uff0c\u4e52\u4e52\u4e53\u4e53\u5730\u4e71\u54cd\uff0c\u9ed1\u767d\u6469\u8bc3\u628a\u90a3\u4e9b \u536b\u58eb\u4e00\u4e2a\u4e00\u4e2a\u5730\u90fd\u51fb\u5f97\u5934\u660f\u773c\u82b1\uff0c\u629b\u5230\u5e8a\u4e0a\uff0c\u4f8d\u4ed6\u8dc3\u8d77\u6765\u65f6\uff0c\u53c8\u7acb\u5373\u5c06\u5730\u51fb\u5012\uff0c\u5e8a\u4e0a\u68c9\u8925\u6e29 \u8f6f\uff0c\u591a\u8dcc\u51e0\u6b21\uff0c\u4ea6\u662f\u4e0d\u59a8\uff0c\u5c0f\u864e\u5b50\u770b\u5f97\u5f00\u5fc3\u4e4b\u6781\uff0c\u4e0d\u4f4f\u5730\u62cd\u624b\u8d5e\u5999\u3002 \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u6240\u7528\u7684\u7f57\u6c49\u795e\u62f3\uff0c\u4e43\u662f\u4e94\u79cd\u62f3\u6cd5\u7684\u603b\u79f0\u3002\u4e94\u62f3\u5c31\u662f\u201c\u9f99\u62f3\u201d\uff0c\u201c\u864e\u62f3\u201d\uff0c\u201c\u8c79 \u62f3\u201d\uff0c\u201c\u86c7\u62f3\u201d\u548c\u201c\u9e64\u62f3\u201d\u3002\u62f3\u7ecf\u4e0a\u8bf4\uff0c\u201c\u9f99\u62f3\u201d\u65e8\u5728\u201c\u7ec3\u795e\u201d\uff0c\u6ce8\u91cd\u8f7b\u9759\u53d8\u5316\uff0c\u5185\u52b2\u6700 \u957f\uff1b\u201c\u864e\u62f3\u201d\u65e8\u5728\u201c\u7ec3\u9aa8\u201d\uff0c\u6ce8\u91cd\u8d77\u843d\u6709\u52bf\uff0c\u521a\u731b\u4f24\u6b8b\uff1b\u201c\u8c79\u62f3\u201d\u65e8\u5728\u201c\u7ec3\u529b\u201d\uff0c\u6ce8\u91cd\u8df3\u640f \u51f6\u72e0\uff0c\u53d8\u5316\u7075\u6377\uff1b\u201c\u86c7\u62f3\u201d\u65e8\u5728\u201c\u7ec3\u6c14\u201d\uff0c\u6ce8\u91cd\u8212\u957f\u7075\u6d3b\uff0c\u6700\u4e3a\u673a\u5de7\uff1b\u201c\u9e64\u62f3\u201d\u65e8\u5728\u201c\u7ec3 \u7cbe\u201d\u3001\u6ce8\u91cd\u7a33\u51c6\u72e0\u51dd\uff0c\u4e00\u51fb\u5373\u4e2d\u8981\u5bb3\u3002\u8fd9\u4e94\u79cd\u62f3\u6cd5\uff0c\u672c\u6765\u6e90\u51fa\u201c\u5c11\u6797\u201d\u7684\u62f3\u6cd5\uff0c\u6e90\u6e90\u63a8\u59cb\uff0c \u53c8\u662f\u6765\u81ea\u5370\u5ea6\u7684\u8fbe\u6469\u7956\u5e08\u6240\u6388\uff0c\u9ed1\u767d\u6469\u8bc3\u662f\u5370\u5ea6\u4eba\uff0c\u5bf9\u8fbe\u6469\u5728\u5370\u5ea6\u8fd9\u6d3e\u7684\u62f3\u6cd5\uff0c\u65e9\u5df2\u719f\u4e60\uff0c \u5230\u4e86\u4e2d\u56fd\u4e4b\u540e\uff0c\u518d\u5b66\u201c\u5c11\u6797\u201d\u7684\u4e94\u62f3\uff0c\u867d\u7136\u56e0\u5728\u4e24\u56fd\u5206\u4f20\uff0c\u5df2\u6709\u53d8\u5316\uff0c\u4f46\u5230\u5e95\u6e90\u51fa\u4e00\u7956\uff0c\u6709 \u8bb8\u591a\u5171\u901a\u4e4b\u5904\uff0c\u9ed1\u767d\u6469\u8bc3\u628a\u4e2d\u5370\u4e24\u56fd\u6240\u4f20\u7684\u8fbe\u6469\u62f3\u6cd5\u878d\u4e8e\u4e00\u7089\uff0c\u7aef\u7684\u795e\u5999\u4e4b\u6781\u3002\u5f20\u98ce\u5e9c\u539f\u662f \u5c11\u6797\u9ad8\u5f92\uff0c\u5c0f\u864e\u5b50\u81ea\u5c0f\u4e5f\u7ec3\u8fc7\u7f57\u6c49\u62f3\uff0c\u6240\u4ee5\u718f\u767d\u6469\u8bc3\u6536\u4ed6\u4e3a\u5f92\u4e4b\u540e\uff0c\u5c31\u6388\u4ed6\u62f3\u7ecf\u3002\u53ea\u662f\u62f3\u7ecf \u4e0a\u7684\u9053\u7406\u5965\u5999\u975e\u5e38\uff0c\u5c0f\u864e\u5b50\u5e74\u7eaa\u592a\u5c0f\uff0c\u5c1a\u4e0d\u80fd\u7406\u89e3\uff0c\u4eca\u770b\u5230\u9ed1\u767d\u6469\u8bc3\u4e00\u62db\u4e00\u5f0f\u5730\u6f14\u5c06\u51fa\u6765\uff0c \u5c06\u654c\u4eba\u6253\u5f97\u4e0d\u4ea6\u4e50\u4e4e\uff0c\u62f3\u7ecf\u4e0a\u7684\u9053\u7406\u4e0d\u987b\u8bb2\u8ff0\uff0c\u5df2\u8c41\u7136\u81ea\u609f\u3002\u8fd9\u4e00\u4ed7\u867d\u7136\u662f\u5f3a\u5f31\u60ac\u6b8a\uff0c\u9ed1\u767d \u6469\u8bc3\u5bf9\u90a3\u4e9b\u536b\u58eb\uff0c\u6070\u5982\u732b\u513f\u620f\u9f20\uff0c\u4f46\u5c0f\u864e\u5b50\u5374\u5f97\u76ca\u751a\u5927\uff0c\u4e8e\u627f\u73e0\u4e5f\u56e0\u6b64\u589e\u957f\u4e86\u4e0d\u5c11\u4e34\u654c\u7684\u89c1 \u8bc6\u3002 \u3000\u3000\u4e24\u4eba\u6324\u5728\u8863\u67dc\u4e0a\u89c2\u6218\uff0c\u4e8e\u627f\u73e0\u770b\u5230\u4e00\u62db\u201c\u9e64\u62f3\u201d\uff0c\u628a\u654c\u4eba\u7684\u624b\u81c2\u626d\u66f2\uff0c\u53cd\u6253\u53e6\u4e00\u4e2a\u654c \u4eba\uff0c\u6b63\u81ea\u53eb\u597d\uff0c\u5c0f\u864e\u5b50\u5ffd\u9053\uff1a\u201c\u5582\uff0c\u4f60\u90a3\u65e5\u89c1\u7740\u4e86\u6211\u7684\u7239\u7239\u5417\uff1f\u201d\u8fd9\u53e5\u8bdd\u4ed6\u4e00\u89c1\u4e8e\u627f\u73e0\u4fbf\u60f3 \u95ee\u4e86\uff0c\u76f4\u5230\u73b0\u5728\u624d\u8d81\u4e2a\u7a7a\u9699\uff0c\u95ee\u4e86\u51fa\u6765\u3002\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u9178\uff0c\u60f3\u9053\uff1a\u539f\u6765\u5c0f\u864e\u5b50\u5c1a\u672a\u77e5\u4ed6\u7236\u4eb2 \u5df2\u6b7b\u3002 \u3000\u3000\u4e03\u540d\u5927\u5185\u536b\u58eb\uff0c\u6768\u5343\u65a4\u5df2\u88ab\u6253\u5f97\u534a\u6b7b\uff0c\u674e\u6db5\u771f\u88ab\u6454\u51fa\u5c4b\u5916\uff0c\u5269\u4e0b\u7684\u4e94\u4eba\uff0c\u9664\u4e86\u91d1\u4e07\u4e24\u4e4b \u5916\uff0c\u5176\u4ed6\u4e2a\u4e2a\u53d7\u4f24\u3002\u91d1\u4e07\u4e24\u7684\u672c\u9886\u5e76\u975e\u6bd4\u540c\u4f34\u9ad8\u5f3a\uff0c\u800c\u662f\u4ed6\u6700\u4e3a\u72e1\u733e\uff0c\u8eb2\u8eb2\u95ea\u95ea\uff0c\u88ab\u638c\u950b\u4e00 \u89e6\uff0c\u5c31\u8eb2\u5728\u5e8a\u4e0a\u8bc8\u6b7b\uff0c\u4ece\u4e0d\u6b63\u9762\u63a5\u62db\uff0c\u9ed1\u767d\u6469\u8bc3\u6253\u5f97\u9ad8\u5174\uff0c\u53cd\u6b63\u662f\u6709\u4eba\u53ef\u6253\uff0c\u6253\u8c01\u90fd\u662f\u4e00 \u6837\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u4e5f\u5e76\u672a\u6ce8\u610f\u53ca\u4ed6\u3002\u8fd9\u65f6\u9ed1\u6469\u8bc3\u4e00\u62db\u201c\u9e64\u62f3\u201d\u8fde\u6253\u4e24\u4e2a\u654c\u4eba\uff0c\u91d1\u4e07\u4e24\u4e5f\u88ab\u78b0 \u8dcc\u5e8a\u4e0b\uff0c\u76f4\u6eda\u5230\u8863\u67dc\u65c1\u8fb9\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u89c1\u4e8e\u627f\u73e0\u4e0e\u5c0f\u864e\u5b50\u8bb2\u8bdd\uff0c\u6b63\u81ea\u51fa\u795e\u3002\u91d1\u4e07\u4e24\u4e00\u54ac\u7259 \u6839\uff0c\u7a81\u7136\u53d1\u51fa\u4e24\u679d\u8896\u7bad\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u6b63\u5728\u8ffd\u95ee\u7239\u7239\u4e0b\u843d\uff0c\u5ffd\u89c1\u4e24\u679d\u8896\u7bad\u5c04\u5230\uff0c\u8863\u67dc\u4e4b\u4e0a\uff0c\u65e0\u6cd5\u8eb2\u907f\uff0c\u5c0f\u624b\u4e00\u4f38\uff0c\u4fbf\u5f85 \u786c\u63a5\uff0c\u53ea\u89c1\u4e8e\u627f\u73e0\u53cc\u6307\u4e00\u5f39\uff0c\u94ee\u7684\u4e00\u58f0\uff0c\u4e24\u679d\u8896\u7bad\u7ed9\u5979\u5f39\u4e2a\u6b63\u7740\uff0c\u6fc0\u98de\u5c04\u56de\uff0c\u63a5\u7740\u91d1\u5149\u4e00 \u95ea\uff0c\u4e00\u6735\u91d1\u82b1\u6697\u5668\u6253\u5165\u4e86\u91d1\u4e07\u4e24\u7684\u54bd\u5589\uff0c\u91d1\u4e07\u4e24\u60e8\u53eb\u4e00\u58f0\uff0c\u8dc3\u8d77\u4e08\u4f59\uff0c\u51e0\u4e4e\u78b0\u7740\u5c4b\u9876\uff0c\u767d\u6469 \u8bc3\u53cc\u773c\u4e00\u7741\uff0c\u602a\u58f0\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u8fd8\u6ca1\u6b7b\uff01\u201d\u4f38\u624b\u4e00\u6293\uff0c\u7acb\u7528\u5206\u7b4b\u9519\u9aa8\u7684\u624b\u6cd5\uff0c\u5c06\u4ed6\u7684\u808b\u9aa8 \u5168\u90e8\u634f\u788e\uff0c\u4e00\u628a\u6454\u51fa\u5c4b\u5916\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5f39\u8896\u7bad\uff0c\u53d1\u91d1\u82b1\uff0c\u4e24\u4e2a\u52a8\u4f5c\uff0c\u4e00\u6c14\u5475\u6210\uff0c\u5feb\u6377\u4e4b\u6781\uff0c\u5c0f\u864e\u5b50\u4e5f\u4e0d\u7981\u4f69\u670d\uff0c\u53eb\u9053\uff0c \u201c\u597d\u59d0\u59d0\uff0c\u5e08\u7236\u7684\u529f\u592b\u96be\u5b66\uff0c\u5b66\u5230\u59d0\u59d0\u7684\u529f\u592b\u6211\u4e5f\u5fc3\u8db3\u4e86\uff01\u201d\u9ed1\u767d\u6469\u8bc3\u4e00\u76f4\u4ee5\u4e3a\u4e8e\u627f\u73e0\u662f\u4e2a \u7537\u5b50\uff0c\u542c\u4e86\u5c0f\u864e\u5b50\u7684\u8bdd\uff0c\u8fd9\u624d\u77e5\u9053\u81ea\u5df1\u770b\u8d70\u4e86\u773c\uff0c\u60ca\u5947\u4e0d\u5df2\uff0c\u5fc3\u4e2d\u66f4\u662f\u4f69\u670d\u5f20\u4e39\u67ab\uff0c\u540c\u65f6\u4e5f \u8d77\u4e86\u4e89\u80dc\u4e4b\u5ff5\uff0c\u8981\u628a\u5c0f\u864e\u5b50\u8c03\u6559\u6210\u6750\uff0c\u8ba9\u4ed6\u5f52\u5165\u5f20\u4e39\u67ab\u95e8\u4e0b\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u4f24\u4e86\u654c\u4eba\uff0c\u5c0f\u864e\u5b50\u62cd\u638c\u53eb\u597d\uff0c\u4e8e\u627f\u73e0\u5374\u662f\u6beb\u65e0\u5f97\u610f\u4e4b\u8272\uff0c\u53cd\u800c\u7709\u5c16\u7d27\u8e69\uff0c\u9732\u51fa\u6101 \u5bb9\u3002\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u597d\u59d0\u59d0\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u54b1\u4eec\u521a\u624d\u8bf4\u5230\u54ea\u91cc\uff1f\u55ef\uff0c\u90a3\u65e5\u4f60\u89c1\u7740\u4e86\u6211\u7684\u7239\u7239 \u5417\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ed6\u6709\u4e24\u6837\u4e1c\u897f\uff0c\u7b49\u4e0b\u6211\u4ea4\u7ed9\u4f60\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u55ef\uff0c\u90a3\u4f60\u662f\u89c1\u7740\u4ed6\u4e86\u3002 \u4e1c\u897f\u6162\u6162\u518d\u7ed9\u6211\u4e0d\u8fdf\uff0c\u5582\uff0c\u5feb\u77a7\uff0c\u5e08\u7236\u6253\u5f97\u771f\u597d\u5475\uff01\u201d \u3000\u3000\u53ea\u89c1\u9ed1\u767d\u6469\u8bc3\u53d1\u62f3\u5982\u96e8\uff0c\u8fd0\u638c\u5982\u98ce\uff0c\u5c06\u5269\u4e0b\u7684\u90a3\u56db\u540d\u536b\u58eb\u6253\u5f97\u4e0d\u4ea6\u4e50\u4e4e\uff0c\u9ed1\u767d\u6469\u8bc3\u7684\u52b2 \u529b\u7528\u5f97\u6070\u5230\u597d\u5904\uff0c\u5c06\u654c\u4eba\u51fb\u5012\u5e8a\u4e0a\uff0c\u4fbf\u7acb\u5373\u5f39\u8d77\uff0c\u63a5\u7740\u53c8\u4e00\u62f3\u51fb\u5012\uff0c\u9ed1\u767d\u6469\u8bc3\u53eb\u9053\uff1a\u201c\u5c0f\u864e \u5b50\uff0c\u77a7\u6e05\u695a\u4e86\uff0c\u8fd9\u662f\u7f57\u6c49\u4e94\u884c\u62f3\u6574\u5957\u62f3\u6cd5\u7684\u8fd0\u7528\uff0c\u5171\u6709\u4e00\u767e\u96f6\u516b\u62db\uff0c\u6211\u73b0\u5728\u4ece\u5934\u6253\u8d77\u3002\u201d\u4e24 \u5144\u5f1f\u628a\u654c\u4eba\u5f53\u4f5c\u7ec3\u62f3\u7684\u6c99\u888b\uff0c\u8fd9\u6837\u7684\u6559\u6cd5\u7684\u786e\u522b\u5f00\u751f\u9762\uff0c\u90a3\u56db\u540d\u4e00\u7b49\u536b\u58eb\u53ef\u5c31\u82e6\u4e86\uff0c\u867d\u7136\u8dcc \u4e0d\u6b7b\uff0c\u53ef\u662f\u4e60\u8fc7\u6b66\u529f\u7684\u4eba\uff0c\u9047\u5230\u5916\u529b\u6253\u51fb\uff0c\u81ea\u7136\u4f1a\u8fd0\u52b2\u5bf9\u6297\uff0c\u5f3a\u5f31\u60ac\u6b8a\uff0c\u6240\u53d7\u7684\u82e6\u6bd4\u666e\u901a\u4eba \u66f4\u751a\uff0c\u9ed1\u767d\u6469\u8bc3\u7684\u7f57\u53cc\u4e94\u884c\u62f3\u8fd8\u672a\u7ec3\u5230\u4e00\u534a\uff0c\u8fd9\u56db\u4eba\u7684\u52b2\u529b\u5df2\u5168\u90fd\u6d88\u5931\uff0c\u6bcf\u4eba\u90fd\u662f\u6c57\u6d41\u5982 \u96e8\uff0c\u5e8a\u8925\u5c3d\u6e7f\uff0c\u5c31\u50cf\u7528\u5f3a\u529b\u69a8\u53d6\u4e00\u6837\uff0c\u770b\u770b\u5c31\u8981\u6cb9\u5c3d\u706f\u67af\uff0c\u6027\u547d\u4e0d\u4fdd\u3002\u6709\u4e24\u4e2a\u5fcd\u53d7\u4e0d\u4f4f\u7684\uff0c \u81ed\u6c57\u6d41\u5c3d\uff0c\u5c41\u6eda\u5c3f\u6d41\uff0c\u5367\u623f\u91cc\u767b\u65f6\u5f25\u6f2b\u7740\u4e00\u7247\u81ed\u6c14\uff0c\u4e8e\u627f\u73e0\u63a9\u53e3\u53eb\u9053\uff1a\u201c\u81ed\u6b7b\u5566\uff0c\u522b\u5f04\u810f\u4e86 \u6211\u5e08\u7236\u7684\u623f\u95f4\uff0c\u5feb\u6253\u53d1\u4ed6\u4eec\u53bb\u5427\uff01\u201d \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u54c8\u54c8\u5927\u7b11\uff0c\u5c06\u654c\u4eba\u4e00\u4e2a\u4e2a\u6293\u8d77\uff0c\u6454\u51fa\u95e8\u5916\uff0c\u6454\u4e00\u4e2a\uff0c\u9a82\u4e00\u58f0\uff0c\u6700\u540e\u6293\u8d77\u4e86\u6768\u5343 \u65a4\uff0c\u591a\u7528\u4e86\u4e24\u5206\u52b2\u529b\uff0c\u5c06\u4ed6\u7684\u810a\u67f1\u9aa8\u6454\u65ad\uff0c\u559d\u9053\uff1a\u201c\u56de\u53bb\u8bf4\u7ed9\u4f60\u90a3\u72d7\u7687\u5e1d\u77e5\u9053\uff0c\u82e5\u518d\u6d3e\u4eba\u6765 \u9a9a\u6270\u5f20\u5927\u4fa0\u7684\u5bb6\u56ed\uff0c\u4f60\u4eec\u5c31\u662f\u699c\u6837\u3002\u201d\u9ed1\u767d\u6469\u8bc3\u6740\u4eba\u4e0d\u7728\u773c\u775b\uff0c\u8fd8\u662f\u56e0\u4e3a\u8fd1\u51e0\u5e74\u5e74\u4e8b\u6e10\u957f\uff0c \u706b\u6c14\u6e10\u6d88\uff0c\u6240\u4ee5\u8fd9\u6b21\u51fa\u624b\uff0c\u9664\u4e86\u5c06\u6768\u5343\u65a4\u3001\u91d1\u4e07\u4e24\u6253\u5f97\u91cd\u4f24\u6b8b\u5e9f\u4e4b\u5916\uff0c\u53e6\u56db\u540d\u536b\u58eb\u4e0d\u8fc7\u4e27\u5931 \u4e86\u6b66\u529f\uff0c\u8fd8\u80fd\u50cf\u5e38\u4eba\u4e00\u6837\u8d70\u52a8\uff0c\u8fd8\u6709\u4e00\u4e2a\u674e\u6db5\u771f\uff0c\u5219\u8fde\u6b66\u529f\u4e5f\u5f97\u4fdd\u5168\uff0c\u4e03\u5927\u536b\u58eb\uff0c\u7adf\u65e0\u4e00\u4eba \u4e27\u547d\uff0c\u5bf9\u9ed1\u767d\u6469\u8bc3\u6765\u8bf4\uff0c\u8fd9\u5df2\u7ecf\u662f\u7834\u4f8b\u7684\u4ec1\u6148\u4e86\u3002 \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u5c06\u654c\u4eba\u6253\u53d1\u4e4b\u540e\uff0c\u9ed1\u767d\u6469\u8bc3\u7b11\u9053\uff1a\u201c\u5c0f\u864e\u5b50\u5475\uff0c\u4f60\u4eca\u5929\u4e0d\u591f\u8fd0\u9053\uff0c\u54b1\u4eec\u7684\u7f57\u53cc \u795e\u62f3\u8fd8\u53ea\u7ec3\u4e86\u4e00\u534a\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u4e0b\u4e00\u6b21\u4f60\u518d\u7ec3\u7ed9\u6211\u770b\u3002\u8fd9\u6b21\u7ec3\u7684\u4e00\u534a\uff0c\u5df2\u7ecf\u591f\u6211\u5b66\u597d\u51e0 \u4e2a\u6708\u5566\u3002\u201d \u3000\u3000\u563f\u767d\u6469\u8bc3\u9053\uff1a\u201c\u50bb\u5c0f\u5b50\uff0c\u4e0b\u4e00\u6b21\u54ea\u8fd8\u80fd\u6709\u8fd9\u6837\u7684\u597d\u673a\u4f1a\uff1f\u201d\u4e8e\u627f\u73e0\u53eb\u9053\uff1a\u201c\u5582\uff0c\u522b\u5c3d\u7559 \u5728\u8fd9\u623f\u4e2d\u8bf4\u8bdd\u5566\u3002\u5440\uff0c\u6211\u5e08\u7236\u82e5\u7136\u89c1\u5230\u4ed6\u7684\u7761\u623f\u7cdf\u8e4b\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u4e0d\u77e5\u591a\u751f\u6c14\u5462\uff1f\u201d \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u4e00\u51fa\u623f\uff0c\u4e8e\u627f\u73e0\u3001\u5c0f\u864e\u5b50\u8ddf\u5728\u540e\u9762\uff0c\u9ed1\u767d\u6469\u8bc3\u9053\uff1a\u201c\u4f60\u5e08\u7236\u81f3\u5c11\u8981\u4e09\u5e74\u4e4b\u540e\u624d \u80fd\u56de\u6765\uff0c\u56de\u6765\u4e4b\u540e\u4e5f\u5305\u7ba1\u4ed6\u4e0d\u4f1a\u751f\u6c14\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u4eec\u89c1\u7740\u4e86\u6211\u7684\u5e08\u7236\u4e86\uff1f\u6211\u5e08\u7236\u53ef\u6709 \u4ec0\u4e48\u8bf4\u8bdd\u4ea4\u5f85\u3002\u4ed6\u4eec\u5230\u54ea\u513f\u53bb\u4e86\uff1f\u201d\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u54c8\uff0c\u5f20\u4e39\u67ab\u771f\u662f\u6536\u5f97\u597d\u5f92\u5f1f\uff0c\u6211\u4eec\u7ed9\u4f60\u5e08 \u7236\u5356\u547d\uff0c\u4f60\u8fde\u591a\u8c22\u4e5f\u4e0d\u8bf4\u4e00\u53e5\uff0c\u5c31\u8bb0\u5f97\u95ee\u5e08\u7236\u3002\u4e8e\u627f\u73e0\u5c0f\u5634\u513f\u4e00\u5658\uff0c\u624b\u6307\u5934\u5728\u9762\u4e0a\u4e00\u5212\uff0c \u9053\uff1a\u201c\u4ec0\u4e48\u5356\u547d\uff0c\u4f60\u8fd9\u662f\u6559\u81ea\u5df1\u7684\u5f92\u5f1f\uff0c\u6211\u5e08\u7236\u53ef\u4e0d\u9886\u4f60\u7684\u60c5\u3002\u201d\u9ed1\u767d\u6469\u8bc3\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u771f \u662f\u4e0d\u77e5\u597d\u574f\uff0c\u6211\u8fd9\u662f\u7ed9\u4f60\u7684\u5e08\u7236\u6559\u5f92\u5f1f\u3002\u201d\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u6211\u4eec\u662f\u4e09\u5929\u524d\u6765\u7684\uff0c\u4f60\u5e08\u7236\u521a\u521a\u79bb \u5f00\uff0c\u4ed6\u53eb\u6211\u4eec\u4e5f\u4ece\u901f\u907f\u7978\uff0c\u6211\u4eec\u5374\u504f\u504f\u7559\u4e0b\u6765\uff0c\u8981\u66ff\u4ed6\u7ba1\u7ba1\u95f2\u4e8b\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u5927\u5e08\u7236\u8bf4 \u8c0e\u8bdd\uff0c\u4f60\u5728\u8def\u4e0a\u4e0d\u662f\u8bf4\u8981\u5411\u5f20\u5927\u4fa0\u501f\u4e00\u6837\u4e1c\u897f\u5417\uff1f\u4f60\u662f\u51d1\u5de7\u624d\u78b0\u4e0a\u8fd9\u573a\u95f2\u4e8b\u7684\u3002\u201d \u3000\u3000\uff0c\u9ed1\u767d\u6469\u8bc3\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u6709\u62dc\u5f20\u4e39\u67ab\u4e3a\u5e08\uff0c\u5c31\u5148\u5e2e\u7740\u672a\u6765\u7684\u5e08\u7236\uff0c\u771f\u53eb\u6211\u7070 \u5fc3\u3002\u5bf9\u5566\uff0c\u4f60\u5e08\u7236\u6599\u5b9a\u4f60\u4f1a\u5bfb\u6765\uff0c\u90a3\u4e1c\u897f\u53eb\u4f60\u627e\u7ed9\u6211\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u9ed1\u767d\u6469 \u8bc3\u9053\uff1a\u201c\u5f20\u5bb6\u7684\u9547\u56fd\u5b9d\u5f13\u3002\u201d\u5f20\u4e39\u67ab\u7684\u5148\u7956\u5f20\u58eb\u8bda\u5728\u82cf\u5dde\u79f0\u5e1d\uff0c\u66fe\u94f8\u6709\u4e00\u628a\u5927\u5f13\uff0c\u8db3\u6709\u4e94\u767e \u65a4\u91cd\uff0c\u8981\u51e0\u4e2a\u4eba\u624d\u62ac\u5f97\u8d77\uff0c\u5f20\u571f\u8bda\u90a3\u65f6\u4ee5\u4e3a\u81ea\u5df1\u5fc5\u5f97\u5929\u4e0b\uff0c\u94f8\u4e0b\u8fd9\u5f20\u5927\u5f13\uff0c\u51c6\u5907\u4f5c\u4e3a\u4f20\u56fd\u4e4b \u5b9d\uff0c\u610f\u601d\u662f\u8981\u7ee7\u4f4d\u7684\u513f\u5b59\u4e0d\u5fd8\u5f13\u9a6c\uff0c\u8fd9\u5f20\u5927\u5f13\u4e0d\u8fc7\u662f\u7528\u4f5c\u9547\u538b\u5929\u4e0b\u7684\u8c61\u5f81\uff0c\u5e76\u4e0d\u80fd\u5728\u9635\u524d\u5b9e \u9645\u5e94\u7528\uff0c\u5f20\u58eb\u8bda\u5175\u8d25\u4e4b\u540e\uff0c\u8fd9\u5f20\u5b9d\u5f13\u85cf\u5728\u5feb\u6d3b\u6797\u884c\u5bab\u7684\u77f3\u6d1e\u4e4b\u4e2d\uff0c\u540e\u6765\u5f20\u4e39\u67ab\u91cd\u5f97\u5feb\u6d3b\u6797\uff0c \u518d\u628a\u5b9d\u5f13\u8fd0\u56de\u5c71\u4e0a\u3002\u4e8e\u627f\u73e0\u542c\u8bf4\u9ed1\u767d\u6469\u8bc3\u8981\u501f\u8fd9\u5f20\u5927\u5f13\uff0c\u5fc3\u4e2d\u6781\u662f\u5947\u602a\uff0c\u9053\uff1a\u201c\u8fd9\u5f20\u5927\u5f13\u643a \u5e26\u6781\u4e0d\u65b9\u4fbf\uff0c\u4f60\u8981\u5b83\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u767d\u6469\u8bc3\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u599e\u513f\u522b\u7ba1\u95f2\u4e8b\u3002\u62ff\u7ed9\u6211\u4eec\uff0c\u81ea\u7136\u6709\u7528\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\uff0c\u6211 \u5c31\u4e0d\u7ed9\u4f60\u62ff\u3002\u8fd8\u6709\u4f60\u662f\u600e\u4f6f\u89c1\u7740\u6211\u7684\u5e08\u7236\u7684\uff1f\u6211\u7684\u5e08\u7236\u6709\u4ec0\u4e48\u8bf4\u8bdd\uff1f\u4f60\u4eec\u8fd8\u90fd\u6ca1\u6709\u8bf4\u5462\uff0c\u4f60 \u8bf4\u4e86\uff0c\u6211\u7ed9\u4f60\u62ff\u3002\u201d\u9ed1\u6469\u8bc3\u4e00\u770b\u5929\u8272\uff0c\u9053\uff1a\u201c\u771f\u662f\u8981\u547d\uff0c\u6536\u5973\u5f92\u5f1f\u5c31\u662f\u8fd9\u6837\u4e0d\u597d\uff0c\u4e13\u4f1a\u8981\u631f \u6492\u5a07\u3002\u597d\uff0c\u4f60\u4e00\u8fb9\u8d70\uff0c\u6211\u4e00\u8fb9\u7ed9\u4f60\u8bf4\u3002\u5582\uff0c\u8d70\u5f97\u5feb\u4e00\u70b9\u3002\u201d\u9ed1\u6469\u8bc3\u4e00\u8fb9\u8d70\u4e00\u8fb9\u8bf4\u9053\uff1a\u201c\u6211\u90fd \u4e0d\u7792\u4f60\uff0c\u6211\u672c\u6765\u8981\u627e\u4f60\u7684\u5e08\u7236\u5bf9\u4ed8\u4e24\u4e2a\u5927\u4ec7\u4eba\u3002\u504f\u504f\u4f60\u7684\u5e08\u7236\u6015\u7687\u5e1d\u627e\u4e8b\uff0c\u5168\u5bb6\u8fdc\u8d70\uff0c\u90a3\u5929 \u53ea\u662f\u5728\u6e56\u6ee8\u5306\u5306\u4e00\u9762\uff0c\u6211\u4eec\u5927\u5bb6\u628a\u4e8b\u60c5\u8bf4\u5b8c\u4e4b\u540e\uff0c\u4ed6\u6559\u6211\u4e00\u4e2a\u6cd5\u5b50\uff0c\u7528\u8fd9\u5f20\u5b9d\u5f13\u5e94\u4ed8\u5f3a\u654c\u3002 \u4ed6\u8d70\u5f97\u5b9e\u662f\u5306\u5fd9\uff0c\u6211\u4eec\u5e26\u5c0f\u864e\u5b50\u524d\u6765\uff0c\u672c\u6765\u662f\u51c6\u5907\u5f3a\u8feb\u4ed6\u6536\u5f92\u7684\uff0c\u4e5f\u8fd8\u6765\u4e0d\u53ca\u8bf4\u5462\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5927\u4e3a\u8be7\u5f02\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u5e08\u7236\u5e38\u8bf4\uff0c\u4ee5\u9ed1\u767d\u6469\u8bc3\u7684\u6b66\u529f\uff0c\u7eb5\u6a2a\u5929\u4e0b\uff0c\u5df2\u662f\u65e0\u654c\uff0c\u82e5 \u4ee5\u4e00\u654c\u4e00\uff0c\u4ed6\u548c\u9ed1\u767d\u6469\u8bc3\u4e5f\u4e0d\u8fc7\u662f\u6253\u6210\u5e73\u624b\u800c\u5df2\u3002\u77a7\u4ed6\u620f\u5f04\u4e03\u540d\u536b\u58eb\uff0c\u90a3\u662f\u4f55\u7b49\u795e\u901a\uff0c\u4ed6\u4eec \u8fd8\u987b\u60e7\u6015\u4ec0\u4e48\u5f3a\u654c\uff1f\u201d\u767d\u6469\u8bc3\u62ac\u5934\u4e00\u770b\u5929\u8272\uff0c\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u90a3\u4e24\u4e2a\u5bf9\u5934\uff0c\u5c31\u8981\u6765\u4e86\uff0c\u5feb\u7ed9\u6211 \u4eec\u62ff\u5f13\u3002\u201d\u4e8e\u627f\u73e0\u672c\u6765\u8fd8\u6709\u8bb8\u591a\u8bdd\u8981\u95ee\uff0c\u7ed9\u767d\u6469\u8bc3\u4e00\u50ac\uff0c\u4e5f\u53ea\u597d\u5fcd\u4f4f\uff0c\u5e26\u4ed6\u4eec\u5230\u540e\u5c71\u5b9d\u5e93\uff0c \u5b9d\u5e93\u85cf\u5728\u5c71\u6d1e\uff0c\u90a3\u672c\u662f\u5f20\u58eb\u8bda\u5f53\u5e74\u7684\u85cf\u5b9d\u4e4b\u5730\uff0c\u540e\u6765\u5f20\u4e39\u67ab\u5c06\u5b9d\u85cf\u90fd\u732e\u7ed9\u671d\u5ef7\uff0c\u91cc\u9762\u6240\u85cf\u7684 \u5c31\u53ea\u662f\u5148\u671d\u9057\u7559\u4e0b\u6765\u7684\u6b66\u5668\u4e0e\u4e00\u4e9b\u503c\u5f97\u7eaa\u5ff5\u7684\u4e1c\u897f\u4e86\u3002\u4e8e\u627f\u73e0\u66fe\u5165\u8fc7\u201c\u5b9d\u5e93\u201d\u591a\u6b21\uff0c\u77e5\u9053\u5f00 \u5e93\u4e4b\u6cd5\uff0c\u5728\u5ca9\u77f3\u4e0a\u5de6\u8f6c\u4e09\u8f6c\uff0c\u53f3\u8f6c\u4e09\u8f6c\uff0c\u5b9d\u5e93\u77f3\u95e8\uff0c\u4e24\u8fb9\u5206\u5f00\uff0c\u767d\u6469\u8bc3\u64e6\u71c3\u706b\u77f3\uff0c\u5165\u5185\u4e00 \u770b\uff0c\u90a3\u5f20\u5b9d\u5f13\u6446\u5728\u5f53\u4e2d\uff0c\u60f3\u662f\u56e0\u4e3a\u642c\u8fd0\u4e0d\u4fbf\u3001\u6240\u4ee5\u5f20\u4e39\u67ab\u6ca1\u6709\u5e26\u8d70\u3002\u5b9d\u5f13\u4e4b\u65c1\uff0c\u6709\u4e09\u652f\u957f \u7bad\uff0c\u5149\u8f89\u707f\u70c2\uff0c\u539f\u6765\u7adf\u662f\u9ec4\u91d1\u6253\u7684\uff0c\u9ed1\u6469\u8bc3\u8e72\u8eab\u62b1\u8d77\u5927\u5f13\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6b63\u662f\u5408\u7528\u3002\u201d\u767d\u6469 \u8bc3\u5c06\u4e09\u652f\u957f\u7bad\u4e00\u5e76\u62ff\u8d77\uff0c\u8d70\u51fa\u77f3\u6d1e\u3002 \u3000\u3000\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u6211\u672c\u60f3\u627e\u4f60\u5e08\u7236\u5e2e\u5fd9\uff0c\u4f60\u5e08\u7236\u4e0d\u5728\uff0c\u4f60\u4eec\u4e24\u4e2a\u5c0f\u5bb6\u4f19\u5e2e\u6211\u4e00\u4e0b\uff0c\u597d\u4e48\uff1f\u201d \u5c0f\u864e\u5b50\u77e5\u9053\u6709\u70ed\u95f9\u53ef\u77a7\uff0c\u5927\u58f0\u53eb\u597d\uff0c\u4e8e\u627f\u73e0\u5947\u9053\uff1a\u201c\u4f60\u4eec\u7684\u5bf9\u5934\uff0c\u6211\u4eec\u600e\u80fd\u62b5\u654c\uff1f\u201d\u9ed1\u6469\u8bc3 \u9053\uff1a\u201c\u6211\u542c\u5f20\u4e39\u67ab\u8bf4\uff0c\u5c71\u5e84\u4e0b\u9762\u6709\u4e00\u4e2a\u77f3\u9635\uff0c\u662f\u6309\u8bf8\u845b\u6b66\u4faf\u7684\u516b\u9635\u56fe\u6446\u7684\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u4e8e \u627f\u73e0\u9053\uff1a\u201c\u77e5\u9053\uff0c\u6211\u5e08\u7236\u7b2c\u4e00\u6b21\u5230\u8fd9\u6d1e\u5ead\u5c71\u65f6\uff0c\u5c31\u51e0\u4e4e\u88ab\u9677\u5165\u77f3\u9635\u4e4b\u4e2d\u3002\u201d\u767d\u6469\u8bc3\u9053\uff1a\u201c\u4f60 \u77e5\u9053\u9635\u6cd5\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u77e5\u9053\u600e\u6837\u8d70\u51fa\u751f\u95e8\uff0c\u8981\u8fd0\u7528\u53ef\u662f\u4e0d\u80fd\u3002\u201d\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u90a3\u5c31 \u884c\u5566\u3002\u6211\u53ea\u8981\u4f60\u4eec\u4e0b\u53bb\uff0c\u5c06\u6211\u4eec\u7684\u90a3\u4e24\u4e2a\u5bf9\u5934\u5f15\u5165\u9635\u4e2d\uff0c\u90a3\u4e24\u4e2a\u5bf9\u5934\u662f\u963f\u62c9\u4f2f\u4eba\uff0c\u4f60\u4e00\u89c1\u5c31 \u4f1a\u77e5\u9053\uff0c\u5feb\u53bb\uff0c\u5feb\u53bb\uff01\u201d\u5c0f\u864e\u5b50\u7acb\u523b\u98de\u8dd1\uff0c\u4e8e\u627f\u73e0\u8f6c\u773c\u5c31\u8d76\u8fc7\u4e86\u4ed6\uff0c\u9053\uff1a\u201c\u5582\uff0c\u5c0f\u864e\u5b50\uff0c\u600e \u4e48\u5f15\u6cd5\uff1f\u54b1\u4eec\u5546\u91cf\u5546\u91cf\u3002\u201d\u5c0f\u864e\u5b50\u7728\u7728\u773c\u775b\uff0c\u9053\uff1a\u201c\u8fd9\u8fd8\u4e0d\u5bb9\u6613\uff0c\u4f60\u968f\u6211\u6765\u3002\u201d\u8bf4\u5f97\u6781\u4e3a\u795e \u6c14\uff0c\u7adf\u4f3c\u80f8\u4e2d\u65e9\u6709\u6210\u7af9\u3002\u4e8e\u627f\u73e0\u6b63\u60f3\u8bf4\u8bdd\uff0c\u62ac\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u5c71\u811a\u5df2\u73b0\u51fa\u4e24\u6761\u4eba\u5f71\u3002\u6b63\u662f\uff1a \u3000\u3000\u521d\u751f\u4e4b\u728a\u4e0d\u754f\u864e\uff0c\u5c06\u95e8\u4e4b\u540e\u975e\u51e1\u7ae5\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u516b\u56de\u3000\u9a8f\u9a6c\u5636\u98ce\u3000\u6563\u82b1\u60ca\u5999\u6280\u3000\u795e\u62f3\u5374\u654c\u3000\u98de\u77e2\u5c04\u5f3a\u4ec7 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u516b\u56de\u3000\u9a8f\u9a6c\u5636\u98ce\u3000\u6563\u82b1\u60ca\u5999\u6280\u3000\u795e\u62f3\u5374\u654c\u3000\u98de\u77e2\u5c04\u5f3a\u4ec7 \u3000\u3000\u8fd9\u4e24\u4eba\u7a7f\u7684\u90fd\u662f\u9ec4\u7ee2\u957f\u888d\uff0c\u642d\u7740\u767d\u7ef8\u62ab\u80a9\uff0c\u675f\u6709\u5934\u5dfe\uff0c\u9ad8\u9f3b\u6df1\u76ee\uff0c\u4e00\u770b\u5c31\u77e5\u662f\u963f\u62c9\u4f2f \u4eba\u3002\u66f4\u5999\u7684\u662f\u4e24\u4eba\u4e0d\u4f46\u4e00\u822c\u6253\u626e\uff0c\u9762\u76ee\u4e5f\u5b8c\u5168\u4e00\u6837\uff0c\u53ea\u662f\u4e00\u4e2a\u7f3a\u4e86\u5de6\u8033\uff0c\u4e00\u4e2a\u7f3a\u4e86\u53f3\u8033\uff0c\u5c0f \u864e\u5b50\u7b11\u9053\uff1a\u201c\u5999\u5475\uff0c\u5999\u5475\uff0c\u6211\u770b\u8fd9\u4e24\u4e2a\u602a\u4eba\u4e5f\u662f\u548c\u6211\u7684\u4e24\u4f4d\u5e08\u7236\u4e00\u822c\uff0c\u4e43\u662f\u53cc\u751f\u5144\u5f1f\u3002\u4e24\u5bf9 \u53cc\u751f\u5144\u5f1f\u505a\u5927\u5bf9\u5934\uff0c\u771f\u662f\u5929\u9020\u5730\u8bbe\uff0c\u5999\u4e0d\u53ef\u8a00\u3002\u201d\u897f\u6d1e\u5ead\u5c71\u867d\u4e0d\u751a\u9ad8\uff0c\u4f46\u4ece\u5c71\u811a\u6765\u81f3\u5c71\u8170\uff0c \u4ea6\u6709\u6570\u5341\u4e08\uff0c\u800c\u4e14\u5c71\u8def\u8fc2\u56de\uff0c\u679c\u6797\u906e\u9053\uff0c\u5c11\u8bf4\u4e5f\u5f97\u8d70\u534a\u4e2a\u65f6\u8fb0\uff0c\u4e5f\u4e0d\u89c1\u8fd9\u4e24\u4eba\u4f5c\u52bf\u5954\u8dd1\uff0c\u7adf \u662f\u6643\u773c\u4e4b\u95f4\uff0c\u5c31\u5230\u4e86\u534a\u5c71\uff0c\u5c0f\u864e\u5b50\u8bdd\u521a\u8bf4\u5b8c\uff0c\u4e24\u4eba\u5df2\u5230\u4e86\u77f3\u9635\u5de6\u8fb9\u7684\u5c71\u5773\uff0c\u770b\u4ed6\u4eec\u6240\u8d70\u7684\u65b9 \u5411\uff0c\u4e0d\u5fc5\u7ecf\u8fc7\u77f3\u9635\uff0c\u4fbf\u53ef\u4e0a\u5c71\u3002\u4e8e\u627f\u73e0\u751a\u662f\u7740\u6025\uff0c\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u597d\uff0c\u6211\u5f15\u4ed6\u4eec\uff0c\u4f60\u7684\u91d1\u82b1\u6697 \u5668\u53ef\u8981\u53d1\u5f97\u5408\u65f6\u3002\u6211\u53bb\u4e5f\u3002\u201d\u8dd1\u5230\u679c\u6797\u4e2d\uff0c\u62b1\u7740\u4e00\u68f5\u6279\u628a\u6811\uff0c\u8fc5\u5373\u63c9\u5347\u6811\u9876\uff0c\u4e8e\u627f\u73e0\u4e0d\u77e5\u9053 \u5c0f\u864e\u5b50\u6253\u7684\u662f\u4ec0\u4e48\u4e3b\u610f\uff0c\u4f46\u77e5\u9053\u4ed6\u9b3c\u602a\u7cbe\u7075\uff0c\u5fc5\u6709\u53e4\u602a\u7684\u6cd5\u5b50\uff0c\u4fbf\u5728\u5c0f\u864e\u5b50\u9644\u8fd1\u6570\u4e08\u4e4b\u5730\u7406 \u4f0f\u3002 \u3000\u3000\u8f6c\u773c\u4e4b\u95f4\uff0c\u90a3\u4e24\u4e2a\u4eba\u5df2\u8d70\u5165\u679c\u6797\uff0c\u4ee5\u8fd9\u4e8c\u4eba\u7684\u6b66\u529f\uff0c\u5f53\u7136\u77e5\u9053\u6797\u4e2d\u6709\u4eba\uff0c\u4f46\u89c1\u6811\u9876\u4e0a\u662f \u4e2a\u5c0f\u5b69\u5b50\uff0c\u5374\u662f\u4e0d\u4ee5\u4e3a\u610f\uff0c\u53ea\u5f53\u662f\u60f3\u5077\u6458\u6279\u628a\u679c\u7684\u987d\u7ae5\uff0c\u4e24\u4eba\u8fb9\u8d70\u8fb9\u8c08\uff0c\u8bf4\u7684\u662f\u53fd\u54e9\u5495\u565c\u7684 \u963f\u62c9\u4f2f\u8bdd\uff0c\u4e8e\u627f\u73e0\u4e00\u53e5\u4e5f\u542c\u4e0d\u61c2\uff0c\u53ea\u89c1\u4ed6\u4eec\u521a\u521a\u8d70\u5230\u5c0f\u864e\u5b50\u90a3\u68f5\u6787\u6777\u6811\u4e0b\uff0c\u4e24\u4eba\u4f4e\u5934\u8bf4\u8bdd\uff0c \u5c0f\u864e\u5b50\u5ffd\u7136\u62c9\u5f00\u88e4\u5b50\uff0c\u6492\u4e0b\u4e00\u6ce1\u5c3f\u6765\u3002 \u3000\u3000\u4e24\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u98de\u8eab\u4e00\u8dc3\uff0c\u5de6\u53f3\u5206\u5f00\uff0c\u8138\u4e0a\u5df2\u6e85\u4e86\u51e0\u70b9\u5c3f\u73e0\uff0c\u81ed\u5473\u653b\u5fc3\uff0c\u4e24\u4eba\u52c3\u7136\u5927 \u6012\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u987d\u76ae\uff0c\u60f3\u627e\u6b7b\u4e48\uff1f\u201d\u8bf4\u7684\u7adf\u7136\u662f\u4e2d\u56fd\u8bdd\u3002\u8fd9\u4e24\u4e2a\u602a\u4eba\u4e00\u6325\u5de6\u638c\uff0c\u4e00\u6325\u53f3\u638c\uff0c \u5728\u8ddd\u79bb\u6787\u6777\u6811\u4e8c\u4e08\u5f00\u5916\uff0c\u5c31\u53d1\u51fa\u5288\u7a7a\u638c\u6765\uff01 \u3000\u3000\u53ea\u542c\u5f97\u547c\u547c\u4e24\u58f0\uff0c\u6977\u6777\u679c\u7eb7\u843d\u5982\u96e8\uff0c\u6811\u4e0a\u679d\u53f6\u7c41\u7c07\u6447\u843d\uff0c\u5c31\u5982\u522e\u5927\u98ce\u4e00\u822c\uff0c\u6811\u8eab\u4e5f\u6447\u52a8 \u4e86\u4e00\u4e0b\uff0c\u4e8e\u627f\u73e0\u89c1\u8fd9\u5a01\u52bf\uff0c\u4ea6\u662f\u60ca\u5fc3\uff0c\u7acb\u523b\u5c06\u6263\u5728\u4e24\u624b\u624b\u5fc3\u7684\u91d1\u82b1\u6697\u5668\uff0c\u4e00\u9f50\u53d1\u51fa\uff0c\u6bcf\u8fb9\u516d \u6735\uff0c\u5404\u5954\u4e00\u4e2a\u602a\u4eba\u3002 \u3000\u3000\u516d\u6735\u91d1\u82b1\uff0c\u6253\u7684\u90fd\u662f\u8981\u5bb3\u7a74\u9053\uff0c\u7aef\u7684\u975e\u540c\u5c0f\u53ef\uff0c\u90a3\u4e24\u4e2a\u602a\u4eba\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u53ea\u89c1\u4e24\u5144\u5f1f \u52a8\u4f5c\u5982\u4e00\uff0c\u4e00\u4e2a\u5411\u5de6\u8df3\u8d77\uff0c\u4e00\u4e2a\u5411\u53f3\u8df3\u8d77\uff0c\u5404\u81ea\u4f38\u51fa\u84b2\u6247\u822c\u7684\u5927\u624b\uff0c\u6253\u6a2a\u4e00\u635e\uff0c\u5404\u81ea\u66ff\u5bf9\u65b9 \u63a5\u4e86\u90a3\u516d\u6735\u6253\u7a74\u91d1\u82b1\uff0c\u4e8e\u627f\u73e0\u7684\u91d1\u82b1\u6697\u5668\uff0c\u5468\u56f4\u957f\u7740\u68f1\u89d2\uff0c\u53ef\u4ee5\u5272\u7834\u76ae\u8089\uff0c\u8fd9\u4e24\u4e2a\u602a\u4eba\u7adf\u662f \u6beb\u4e0d\u987e\u5fcc\uff0c\u4e00\u6284\u5c31\u5168\u90fd\u6284\u5165\u638c\u4e2d\uff0c\u78d4\u78d4\u602a\u7b11\uff0c\u518d\u5f20\u5f00\u624b\u65f6\uff0c\u53ea\u89c1\u91d1\u82b1\u90fd\u5df2\u88ab\u4ed6\u4eec\u634f\u5f97\u53d8\u6210\u7c89 \u5c51\uff0c\u5c31\u5982\u6d12\u4e0b\u4e86\u4e00\u84ec\u91d1\u5149\u95ea\u95ea\u7684\u91d1\u7802\uff01 \u3000\u3000\u53ea\u89c1\u5c0f\u864e\u5b50\u5728\u679d\u53f6\u679c\u5b50\u7eb7\u98de\u7684\u5f53\u4e2d\uff0c\u4e00\u4e2a\u7b4b\u6597\u51b2\u4e86\u4e0b\u6765\uff0c\u7acb\u523b\u98de\u8dd1\u3002\u539f\u6765\u8fd9\u4e24\u4e2a\u602a\u4eba\u89c1 \u5c0f\u864e\u5b50\u662f\u4e2a\u987d\u7ae5\uff0c\u867d\u7136\u607c\u6012\uff0c\u5374\u4e5f\u4e0d\u60f3\u81f4\u4ed6\u4e8e\u6b7b\uff0c\u6240\u4ee5\u5288\u7a7a\u638c\u53ea\u7528\u4e86\u4e09\u6210\u529b\u91cf\uff0c\u6253\u7b97\u5c06\u4ed6\u9707 \u843d\u5730\u4e0a\uff0c\u518d\u884c\u8d23\u9a82\u3002\u8981\u4e0d\u7136\u5c0f\u864e\u5b50\u54ea\u8fd8\u6709\u547d\u5728\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u963f\u62c9\u4f2f\u602a\u4eba\u662f\u4e00\u5bf9\u5b6a\u751f\u5144\u5f1f\uff0c\u5927\u54e5\u540d\u53eb\u963f\u8428\u739b\uff0c\u4e8c\u54e5\u540d\u53eb\u963f\u5408\u739b\uff0c\u662f\u4f0a\u6717\u738b\u5b50\u6240 \u4f9b\u517b\u7684\u4e24\u4f4d\u56fd\u5e08\uff0c\u8db3\u8ff9\u904d\u53ca\u6b27\u4e9a\uff0c\u8fd9\u6b21\u4e3a\u4e86\u4e00\u4ef6\u4f0a\u6717\u7684\u5bab\u95f1\u5947\u6848\u4e0e\u9ed1\u767d\u6469\u8bc3\u5144\u5f1f\u6709\u5173\uff0c\u5176\u4e2d \u8fd8\u7275\u6d89\u4e86\u4e00\u4ef6\u76d7\u5b9d\u6848\uff0c\u4e24\u5144\u5f1f\u8ffd\u8e2a\u9ed1\u767d\u6469\u8bc3\uff0c\u4ece\u4f0a\u6717\u8ffd\u81f3\u5370\u5ea6\uff0c\u4ece\u5370\u5ea6\u8ffd\u81f3\u4e2d\u56fd\uff0c\u9ed1\u767d\u6469\u8bc3 \u80dc\u4e0d\u4e86\u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e5f\u62ff\u4e0d\u4f4f\u9ed1\u767d\u6469\u8bc3\uff0c\u53cc\u65b9\u6b66\u529f\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u4e07\u91cc\u8ffd\u8e2a\uff0c\u5140\u662f\u5206\u4e0d\u51fa\u80dc\u8d1f\u3002 \u8fd9\u4e24\u5144\u5f1f\u4e5f\u50cf\u9ed1\u767d\u6469\u8bc3\u4e00\u6837\uff0c\u6b66\u529f\u751a\u6742\uff0c\u5b66\u517c\u6b27\u4e9a\uff0c\u4ed6\u4eec\u7684\u5288\u7a7a\u638c\u4fbf\u517c\u5177\u6709\u963f\u62c9\u4f2f\u7684\u5916\u529f\u548c \u897f\u85cf\u5bc6\u5b97\u7684\u67d4\u529f\uff0c\u638c\u529b\u521a\u67d4\u76f8\u6d4e\uff0c\u6536\u53d1\u81ea\u5982\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u4e24\u5144\u5f1f\u89c1\u5c0f\u864e\u4e8e\u662f\u4e2a\u987d\u7ae5\uff0c\u8fd9\u4e00\u5288 \u7a7a\u638c\u53ea\u7528\u4e86\u4e09\u6210\u529b\u91cf\uff0c\u6ee1\u4ee5\u4e3a\u5c0f\u864e\u5b50\u5fc5\u5b9a\u7ed9\u638c\u5200\u5288\u6655\uff0c\u54ea\u77e5\u5c0f\u864e\u5b50\u4ece\u6811\u4e0a\u4e00\u4e2a\u7b4b\u6597\u5012\u7ffb\u4e0b \u6765\uff0c\u5c45\u7136\u8fd8\u80fd\u5954\u8dd1\uff0c\u5012\u662f\u5927\u51fa\u4ed6\u4eec\u5144\u5f1f\u610f\u6599\u4e4b\u5916\u3002\u6014\u4e86\u4e00\u6014\uff0c\u53c8\u7ed9\u4e8e\u627f\u73e0\u7684\u91d1\u82b1\u6697\u5668\u963b\u4e86\u4e00 \u963b\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u5c0f\u864e\u5b50\u5df2\u5728\u4e8e\u627f\u73e0\u63a9\u62a4\u4e4b\u4e0b\uff0c\u9003\u51fa\u4e86\u4e8c\u4e09\u5341\u4e08\u4e4b\u5730\u3002 \u3000\u3000\u963f\u8428\u739b\u4e00\u58f0\u602a\u7b11\uff0c\u7528\u963f\u62c9\u4f2f\u8bed\u5bf9\u5144\u5f1f\u9053\uff1a\u201c\u54c8\uff0c\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u5c45\u7136\u6709\u8fd9\u6837\u672c\u4e8b\u7684\u5a03\u5a03\uff0c \u6211\u8981\u90a3\u4e2a\u5927\u7684\uff0c\u4f60\u8981\u90a3\u4e2a\u5c0f\u7684\u3002\u201d\u4ed6\u7684\u610f\u601d\u662f\u60f3\u6536\u4e8e\u627f\u73e0\u4e0e\u5c0f\u864e\u5b50\u4e3a\u5f92\uff0c\u963f\u5408\u739b\u5e94\u4e86\u4e00\u58f0\uff0c \u4e24\u5144\u5f1f\u5fc3\u610f\u5982\u4e00\uff0c\u811a\u5c16\u4e00\u70b9\uff0c\u500f\u5730\u63a0\u51fa\u4e86\u516d\u4e03\u4e08\uff0c\u5404\u6325\u53f3\u638c\uff0c\u53d1\u51fa\u4e86\u4e94\u6210\u638c\u529b\uff0c\u4e8e\u627f\u73e0\u6b63\u5728 \u5954\u8dd1\uff0c\u9661\u89c9\u80cc\u540e\u52b2\u98ce\u75be\u6251\uff0c\u811a\u6b65\u4e00\u6ed1\uff0c\u7a0d\u7a0d\u907f\u5f00\uff0c\u8ddd\u79bb\u867d\u8fdc\uff0c\u4e0a\u8eab\u4ecd\u4e0d\u7531\u81ea\u5df1\u5730\u6643\u4e86\u4e24\u6643\uff0c \u963f\u8428\u739b\u638c\u529b\u52a0\u5f3a\uff0c\u89c1\u4e8e\u627f\u73e0\u4ecd\u7136\u4e0d\u5012\uff0c\u66f4\u662f\u8be7\u5f02\uff0c\u811a\u5c16\u4e00\u70b9\uff0c\u53c8\u98de\u51fa\u516d\u4e03\u4e08\u5730\uff0c\u731b\u5730\u53cc\u638c\u9f50 \u53d1\uff0c\u7528\u4e86\u516b\u6210\u529b\u91cf\uff1b\u8bba\u4e8e\u627f\u73e0\u7684\u529f\u529b\uff0c\u82e5\u7136\u7ed9\u963f\u8428\u739b\u7684\u638c\u529b\u76f4\u63a5\u6253\u5230\u8eab\u4e0a\uff0c\u90a3\u81ea\u7136\u662f\u62b5\u6321\u4e0d \u4f4f\uff0c\u4f46\u5288\u7a7a\u638c\u529b\uff0c\u5373\u7b97\u7ec3\u5230\u6b62\u4e0a\u7684\u5883\u754c\uff0c\u4e5f\u548c\u5bf9\u654c\u65f6\u76f4\u63a5\u76f8\u89e6\u7684\u5b9e\u9645\u638c\u529b\u6709\u6240\u8ddd\u79bb\uff0c\u4f55\u51b5\u8fd8 \u9694\u7740\u5341\u4f59\u4e08\u5730\uff0c\u4e8e\u627f\u73e0\u542c\u98ce\u5ba1\u529b\uff0c\u81ea\u95ee\u8fd8\u652f\u6301\u5f97\u4f4f\uff0c\u4f46\u5c0f\u864e\u5b50\u5374\u62b5\u53d7\u4e0d\u4e86\uff0c\u597d\u4e2a\u4e8e\u627f\u73e0\uff0c\u4e0d \u6127\u662f\u5f20\u4e39\u67ab\u592b\u5987\u7684\u7231\u5f92\uff0c\u673a\u8b66\u4e4b\u6781\uff0c\u963f\u8428\u739b\u638c\u529b\u4e00\u53d1\uff0c\u5979\u9661\u5730\u4f7f\u4e2a\u201c\u4e8c\u9e64\u51b2\u5929\u201d\u4e4b\u52bf\uff0c\u987a\u624b \u5c06\u5c0f\u864e\u5b50\u6293\u4e86\u8d77\u6765\uff0c\u8dc3\u8d77\u4e8c\u4e08\u6765\u9ad8\uff0c\u594b\u529b\u4e00\u6325\uff0c\u53eb\u9053\uff1a\u201c\u7ad9\u7a33\u4e86\uff01\u201d\u638c\u98ce\u547c\u7684\u4e00\u58f0\uff0c\u4ece\u5979\u811a \u4e0b\u63a0\u8fc7\uff0c\u51e0\u4e4e\u5c31\u5728\u8fd9\u4e00\u77ac\u4e4b\u95f4\uff0c\u5c0f\u864e\u5b50\u5df2\u7ed9\u5979\u63b7\u5165\u77f3\u9635\u3002 \u3000\u3000\u963f\u5408\u739b\u8ddf\u8e2a\u8ffd\u5230\uff0c\u4e8e\u627f\u73e0\u524d\u811a\u5df2\u8de8\u5165\u9635\u4e2d\uff0c\u56de\u5934\u7b11\u9053\uff1a\u201c\u597d\u4e0d\u8981\u8138\uff0c\u6b3a\u8d1f\u5b69\u5b50\u3002\u201d\u963f\u8428 \u739b\u9053\uff1a\u201c\u4f60\u62dc\u6211\u4eec\u4e3a\u5e08\uff0c\u6709\u4f60\u7684\u9020\u5316\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u672c\u9886\uff0c\u8981\u6536\u6211\u4e3a\u5f92\uff1f\u201d\u963f\u8428 \u739b\u4f38\u624b\u4e00\u6293\uff0c\u4e8e\u627f\u73e0\u53cd\u624b\u4e00\u5251\uff0c\u5bd2\u5149\u75be\u8d77\uff0c\u5251\u950b\u4e00\u98a4\uff0c\u5206\u523a\u963f\u8428\u739b\u80f8\u53e3\u7684\u201c\u7487\u7391\u7a74\u201d\u548c\u80c1\u4e0b \u7684\u201c\u5173\u5143\u7a74\u201d\uff0c\u6b63\u662f\u767e\u53d8\u7384\u673a\u5251\u6cd5\u4e2d\u7684\u4e00\u4e2a\u6740\u624b\u7edd\u62db\uff0c\u66f4\u517c\u7528\u4e86\u5168\u529b\uff0c\u90a3\u81ea\u7136\u4e0d\u662f\u5979\u523a\u9ed1\u767d \u6469\u8bc3\u4e4b\u65f6\uff0c\u5fc3\u5b58\u987e\u5fcc\u6240\u6bd4\u7684\u3002 \u3000\u3000\u963f\u8428\u739b\u89c1\u5979\u51fa\u5251\u5982\u98ce\uff0c\u53d8\u5e7b\u65e0\u65b9\uff0c\u4e5f\u4e0d\u7981\u5fae\u5fae\u4e00\u60ca\uff0c\u60f3\u4e0d\u5230\u4e00\u4e2a\u4e73\u81ed\u672a\u5e72\u7684\u5c11\u5e74\uff0c\u7adf\u7136 \u6709\u8fd9\u6837\u7cbe\u5999\u7684\u5251\u6cd5\uff0c\u5012\u4e5f\u4e0d\u6562\u6020\u6162\uff0c\u770b\u5251\u5149\u6740\u5230\uff0c\u7acb\u5373\u524d\u8eab\u4e00\u503e\uff0c\u4f38\u6307\u4e00\u5f39\uff0c\u5de6\u624b\u6253\u6a2a\u4e00 \u635e\uff0c\u53ea\u542c\u5f97\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u4e8e\u627f\u73e0\u7684\u9752\u51a5\u5b9d\u5251\u7adf\u7136\u7ed9\u4ed6\u5f39\u5f97\u51e0\u4e4e\u8131\u624b\u98de\u53bb\u3002\u4ed6\u8fd9\u4e00\u5f39\u4e00\u635e\u4e43 \u662f\u963f\u62c9\u4f2f\u7684\u6454\u8de4\u65f6\u6240\u7528\u7684\u64d2\u62ff\u672f\uff0c\u4e8e\u627f\u73e0\u672c\u6765\u907f\u4e0d\u5f00\uff0c\u4f46\u5979\u673a\u7075\u4e4b\u6781\uff0c\u8fd9\u4e00\u5251\u5b9e\u662f\u4ee5\u8fdb\u4e3a \u9000\uff0c\u88ab\u4ed6\u4e00\u5f39\u4e4b\u540e\uff0c\u7acb\u523b\u501f\u529b\u53cd\u8dc3\uff0c\u5e76\u4e0d\u524d\u653b\uff0c\u53cd\u800c\u540e\u9000\uff0c\u963f\u8428\u739b\u635e\u4e86\u4e2a\u7a7a\uff0c\u8eab\u5b50\u6251\u5165\u77f3 \u9635\uff0c\u963f\u5408\u739b\u8ddf\u7740\u4e5f\u8fdb\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u77f3\u9635\u4e43\u662f\u5f6d\u548c\u5c1a\u5f53\u5e74\u63a5\u7740\u8bf8\u845b\u6b66\u5019\u7684\u9057\u6cd5\u6240\u5e03\uff0c\u5206\u6210\u4f11\u3001\u751f\u3001\u4f24\u3001\u675c\u3001\u6b7b\u3001\u666f\u3001\u60ca\u3001 \u5f00\u516b\u95e8\uff0c\u4e00\u5165\u9635\u4e2d\uff0c\u5343\u95e8\u4e07\u6237\uff0c\u82e5\u975e\u719f\u77e5\u9635\u6cd5\uff0c\u8d70\u51fa\u751f\u95e8\uff0c\u5373\u7b97\u6709\u591a\u5927\u672c\u9886\uff0c\u4e5f\u8d70\u4e0d\u51fa\uff0c\u963f \u8428\u739b\u5144\u5f1f\uff0c\u4e0d\u77e5\u6240\u4ee5\uff0c\u5728\u4e71\u77f3\u5806\u4e2d\uff0c\u7ed5\u6765\u7ed5\u53bb\uff0c\u4f46\u89c1\u4e8e\u627f\u73e0\u4e0e\u5c0f\u864e\u5b50\u5728\u9635\u4e2d\u5ffd\u9690\u5ffd\u73b0\uff0c\u4e1c\u65ab \u4e00\u5200\uff0c\u897f\u523a\u4e00\u5251\uff0c\u6251\u4e0a\u53bb\u6293\u65f6\uff0c\u5ffd\u7136\u53c8\u4e0d\u89c1\u4e86\u4ed6\u4eec\u7684\u8e2a\u8ff9\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u4ed6\u4eec\u53c8\u4ece\u659c\u523a\u6216\u8005\u80cc \u540e\u6740\u6765\uff0c\u4fe9\u5144\u5f1f\u867d\u7136\u4e0d\u60e7\u53d7\u4f24\uff0c\u4f46\u5374\u4e5f\u7ed9\u4ed6\u4eec\u5f04\u5f97\u5934\u660f\u773c\u82b1\uff0c\u8d8a\u6765\u8d8a\u6df1\u5165\u77f3\u9635\u3002 \u3000\u3000\u963f\u8428\u739b\u5fc3\u4e2d\u4e00\u51db\uff0c\u5bf9\u5144\u5f1f\u9053\uff1a\u201c\u54b1\u4eec\u627e\u7684\u662f\u9ed1\u767d\u6469\u8bc3\u8fd9\u4e24\u4e2a\u8001\u602a\u7269\uff0c\u4f55\u82e6\u4e0e\u8fd9\u4e24\u4e2a\u5c0f\u5bb6 \u4f19\u7ea0\u7f20\u3002\u201d\u5404\u51fa\u4e00\u638c\u62a4\u8eab\uff0c\u5bfb\u89c5\u9000\u8def\u3002\u5c0f\u864e\u5b50\u626e\u4e86\u4e00\u4e2a\u9b3c\u8138\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u53c8\u8bf4\u8981\u6536\u6211\u4e3a \u5f99\uff0c\u6211\u5c31\u5728\u8fd9\u91cc\uff0c\u4f60\u4eec\u600e\u6837\u53c8\u4e0d\u6562\u6765\u4e86\uff0c\u5e08\u7236\u4e5f\u6015\u5f92\u5f1f\u4e48\uff1f\u201d\u963f\u8428\u739b\u5144\u5f1f\u7ed9\u4ed6\u4e00\u6fc0\uff0c\u56de\u8eab\u53cd \u6251\uff0c\u5c0f\u864e\u5b50\u4e00\u8df3\u5c31\u8df3\u5230\u4e86\u4e8e\u627f\u73e0\u65c1\u8fb9\uff0c\u8ddf\u7740\u5979\u8f6c\u4e86\u51e0\u8f6c\uff0c\u963f\u8428\u739b\u5144\u5f1f\u8ddf\u7740\u4e71\u8f6c\uff0c\u8d8a\u9677\u8d8a\u6df1\uff0c \u7adf\u7136\u7ed9\u4ed6\u4eec\u5f15\u5165\u4e86\u6b7b\u95e8\u3002\u963f\u8428\u739b\u6e10\u89c9\u5fc3\u70e6\u610f\u8e81\uff0c\u5c0f\u864e\u5b50\u3001\u4e8e\u627f\u73e0\u4e0d\u4f4f\u5730\u53d1\u8a00\u51b7\u8bee\uff0c\u963f\u5408\u739b\u5927 \u6012\uff0c\u53cc\u624b\u4e00\u62b1\uff0c\u62b1\u7740\u4e86\u4e00\u4e2a\u51f8\u51fa\u6765\u7684\u77f3\u7b0b\uff0c\u559d\u58f0\u201c\u8d77\u201d\uff0c\u786c\u751f\u751f\u7684\u628a\u4e00\u6761\u91cd\u903e\u767e\u65a4\u7684\u77f3\u7b0b\u62d4 \u4e86\u51fa\u6765\uff0c\u5728\u77f3\u9635\u4e2d\u5de6\u5288\u77f3\u6253\uff0c\u53ea\u6253\u5f97\u6c99\u77f3\u7eb7\u98de\uff0c\u4e8e\u627f\u73e0\u5c06\u5b9d\u5251\u821e\u6210\u4e00\u5708\u94f6\u8679\uff0c\u7d27\u7d27\u5730\u62a4\u7740\u5c0f \u864e\u5b50\uff0c\u6c99\u77f3\u4e00\u89e6\u5251\u5149\uff0c\u7acb\u523b\u7ed9\u6fc0\u98de\u5f00\u53bb\uff0c\u90a3\u77f3\u9635\u867d\u662f\u4e71\u77f3\u5806\u6210\uff0c\u5e76\u975e\u5c71\u5cf0\u53ef\u6bd4\uff0c\u4f46\u6bcf\u5806\u4e71 \u77f3\uff0c\u4ea6\u662f\u9ad8\u8fbe\u6570\u4e08\uff0c\u8981\u6253\u584c\u4e00\u4e2a\u77f3\u5806\uff0c\u5927\u975e\u5bb9\u6613\uff0c\u963f\u5408\u739b\u6253\u5f97\u7b4b\u75b2\u529b\u7aed\uff0c\u4e0d\u8fc7\u6253\u584c\u4e86\u51e0\u4e2a\u77f3 \u5806\uff0c\u4ecd\u662f\u627e\u4e0d\u5230\u901a\u5230\u5916\u9762\u7684\u95e8\u6237\u3002 \u3000\u3000\u963f\u8428\u739b\u8f83\u4e3a\u6c89\u7740\uff0c\u5c06\u5144\u5f1f\u559d\u6b62\uff0c\u5b9a\u775b\u4e00\u770b\uff0c\u90a3\u4e9b\u77f3\u5806\uff0c\u6bcf\u4e2a\u9ad8\u7ea6\u5341\u4e08\uff0c\u5bfb\u5e38\u4e4b\u4eba\uff0c\u81ea\u662f \u6500\u4e0d\u4e0a\u53bb\uff0c\u4f46\u5374\u96be\u4e0d\u4f4f\u963f\u8428\u739b\u5144\u5f1f\uff0c\u963f\u8428\u739b\u53eb\u5144\u5f1f\u7ed9\u4ed6\u5728\u4e0b\u9762\u5b88\u62a4\uff0c\u9884\u9632\u4e8e\u627f\u73e0\u7684\u6697\u5668\uff0c\u4ed6 \u81ea\u5df1\u624b\u811a\u5e76\u7528\uff0c\u4ece\u4e00\u4e2a\u4e71\u77f3\u5806\u63c9\u5347\u4e0a\u53bb\uff0c\u90a3\u4e9b\u4e71\u77f3\u5c16\u524a\u5982\u5200\uff0c\u5e78\u800c\u963f\u8428\u739b\u7ec3\u5f97\u5168\u8eab\u94dc\u76ae\u94c1 \u9aa8\uff0c\u4e0d\u6015\u53d7\u4f24\uff0c\u4e0d\u8fc7\u4e00\u76cf\u8336\u65f6\u523b\uff0c\u5c31\u6500\u81f3\u4e0a\u9762\u3002\u521a\u521a\u4f38\u5934\u4e00\u770b\uff0c\u5ffd\u542c\u5f97\u5c71\u9876\u4e0a\u4f20\u6765\u54c8\u54c8\u7684\u602a \u7b11\u4e4b\u58f0\u3002 \u3000\u3000\u53ea\u89c1\u9ed1\u767d\u6469\u8bc3\u7ad9\u5728\u5c71\u9876\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u9ed1\u6469\u8bc3\u633d\u7740\u4e00\u5f20\u5927\u5f13\uff0c\u767d\u6469\u8bc3\u624b\u63e1\u957f\u7bad\uff0c\u9ed1\u767d\u6469\u8bc3 \u8eab\u6750\u672c\u5c31\u9ad8\u5927\uff0c\u8fd9\u65f6\u5f20\u5f13\u642d\u7bad\uff0c\u5e76\u7acb\u5c71\u5934\uff0c\u5a01\u98ce\u51db\u51db\uff0c\u4f0a\u5982\u5929\u795e\u3002\u963f\u8428\u739b\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u6240\u5f97 \u9ed1\u6469\u8bc3\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8fde\u6211\u7684\u5f92\u513f\u90fd\u5bf9\u4ed8\u4e0d\u4e86\uff0c\u8fd8\u901e\u4ec0\u4e48\u5f3a\uff0c\u8bc6\u8da3\u7684\u5feb\u56de\u53bb\u5427\uff01\u201d\u963f\u8428\u739b \u6012\u9053\uff1a\u201c\u88c5\u9b3c\u5f04\u602a\uff0c\u6697\u5e03\u9677\u9631\uff0c\u7b97\u4ec0\u4e48\u82f1\u96c4\u597d\u6c49\uff0c\u5927\u80c6\u7684\u54b1\u4eec\u518d\u51b3\u4e00\u6b7b\u6218\uff01\u201d\u9ed1\u6469\u8bc3\u5927\u7b11 \u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4e0d\u670d\u8f93\uff0c\u54b1\u4eec\u5c31\u518d\u8f83\u91cf\uff0c\u63a5\u7bad\uff01\u201d\u4ed6\u4eec\u4e8c\u4eba\u7528\u963f\u62c9\u4f2f\u8bed\u5bf9\u9a82\uff0c\u4e8e\u627f\u73e0\u4e0e\u5c0f\u864e \u5b50\u867d\u7136\u4e0d\u61c2\uff0c\u4f46\u542c\u5f97\u58f0\u97f3\u94ff\u9535\u9707\u8033\uff0c\u4e71\u77f3\u5806\u4e2d\u56de\u65cb\u7740\u55e1\u55e1\u4e4b\u58f0\uff0c\u4fe8\u5982\u91d1\u94c1\u4ea4\u9e23\uff0c\u6012\u6d9b\u51fb\u5cb8\uff0c \u4e5f\u81ea\u4e0d\u7981\u9a87\u7136\uff01 \u3000\u3000\u4e8e\u627f\u73e0\u4e0e\u5c0f\u864e\u5b50\u8eb2\u5728\u9635\u4e2d\u201c\u751f\u95e8\u201d\u7684\u4e00\u89d2\uff0c\u62ac\u5934\u4ef0\u671b\uff0c\u5ffd\u542c\u5f97\u201c\u545c\u201d\u7684\u4e00\u58f0\uff0c\u957f\u7bad\u7834 \u7a7a\uff0c\u52b2\u98ce\u547c\u5578\uff0c\u963f\u8428\u739b\u4e00\u4e2a\u5012\u683d\u8471\uff0c\u4ece\u4e0a\u9762\u76f4\u8dcc\u4e0b\u6765\uff0c\u6cb3\u5408\u739b\u624b\u6500\u77f3\u7b0b\uff0c\u98de\u8dc3\u63c9\u5347\uff0c\u5f20\u624b\u4e00 \u63a5\uff0c\u63a5\u4e0d\u7740\u54e5\u54e5\uff0c\u53ea\u542c\u5f97\u53c8\u662f\u201c\u545c\u201d\u7684\u4e00\u58f0\uff0c\u963f\u5408\u739b\u4e5f\u8dcc\u4e86\u4e0b\u6765\uff0c\u4e24\u5144\u5f1f\u80a9\u5934\u90fd\u662f\u4e00\u7247\u6bb7 \u7ea2\u3002\u77f3\u9635\u4e4b\u4e2d\uff0c\u91d1\u5149\u4e00\u95ea\uff0c\u4e24\u652f\u957f\u7bad\u63d2\u5728\u77f3\u4e0a\uff0c\u7bad\u5c3e\u5140\u81ea\u9707\u52a8\u4e0d\u4f11\uff0c\u94ff\u9535\u4e4b\u58f0\uff0c\u4e45\u4e45\u4e0d\u7edd\uff01 \u3000\u3000\u539f\u6765\u9ed1\u767d\u6469\u8bc3\u4e0e\u963f\u8428\u739b\u5144\u5f1f\u529f\u529b\u672c\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u82e5\u5728\u5e73\u5730\uff0c\u6253\u4e09\u65e5\u4e09\u591c\uff0c\u4e5f\u672a\u5fc5\u5206\u5f97\u80dc \u8d1f\u3002\u5982\u4eca\u9ed1\u767d\u6469\u8bc3\u4ed7\u7740\u795e\u5f13\u4e4b\u529b\uff0c\u5728\u9ad8\u5cf0\u653e\u7bad\uff0c\u529b\u9053\u4e4b\u5f3a\uff0c\u65e0\u4e0e\u4f26\u6bd4\uff0c\u963f\u8428\u739b\u5144\u5f1f\u5728\u77f3\u9635\u4e4b \u4e2d\u53c8\u8f6c\u5f97\u5934\u6655\u773c\u82b1\uff0c\u7adf\u7136\u8eb2\u95ea\u4e0d\u5f00\u3002\u4e24\u7bad\u5747\u4e2d\uff0c\u8fd8\u5e78\u9ed1\u6469\u8bc3\u624b\u4e0b\u7559\u60c5\uff0c\u5c04\u7684\u662f\u80a9\u5934\uff0c\u5e76\u975e\u8981 \u5bb3\u4e4b\u5904\uff0c\u9976\u662f\u5982\u6b64\uff0c\u963f\u8428\u739b\u5144\u5f1f\u53d7\u4e86\u795e\u7bad\u7684\u51b2\u51fb\u4e4b\u529b\uff0c\u7834\u4e86\u771f\u5143\u4e4b\u6c14\uff0c\u975e\u518d\u82e6\u7ec3\u4e00\u5e74\uff0c\u4e0d\u80fd \u6062\u590d\u539f\u6765\u7684\u529f\u529b\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u867d\u662f\u987d\u76ae\uff0c\u89c1\u5982\u6b64\u54b8\u731b\u7684\u58f0\u52bf\uff0c\u4e5f\u81ea\u5413\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u4ed6\u521d\u5b66\u5185\u529f\uff0c\u7565\u7aa5\u95e8\u5f84\uff0c\u89c1 \u963f\u8428\u739b\u5144\u5f1f\u7adf\u7136\u786c\u6321\u4e86\u8fd9\u4e24\u7bad\uff0c\u82e5\u975e\u5185\u529f\u6709\u6781\u9ad8\u7684\u9020\u8be3\uff0c\u8fd9\u4e24\u7bad\u5b9a\u7136\u7a7f\u8fc7\u80a9\u5934\uff0c\u5c04\u788e\u7b4b\u9aa8\uff0c \u5982\u4eca\u963f\u8428\u739b\u5144\u5f1f\u867d\u7ed9\u5c04\u4e2d\uff0c\u5374\u80fd\u5c06\u90a3\u6781\u521a\u52b2\u7684\u7bad\u52bf\u6d88\u89e3\u4e86\u4e00\u534a\uff0c\u9707\u843d\u5730\u4e0a\uff0c\u800c\u4e14\u90a3\u6d88\u89e3\u4e4b\u540e \u7684\u529b\u9053\uff0c\u8fd8\u80fd\u4ee4\u957f\u7bad\u63d2\u5728\u77f3\u4e0a\uff0c\u53cc\u65b9\u529f\u529b\u4e4b\u6df1\uff0c\u786e\u662f\u9a87\u4eba\u5fc3\u9b44\uff01\u5c0f\u864e\u5b50\u5bf9\u963f\u8428\u739b\u5144\u5f1f\u8877\u5fc3\u4f69 \u670d\uff0c\u975e\u4f46\u6ca1\u6709\u51fa\u8a00\u8ba5\u8bee\uff0c\u53cd\u800c\u4e0a\u524d\u53bb\u6276\u8d77\u4ed6\u4eec\u3002 \u3000\u3000\u963f\u8428\u739b\u7741\u7740\u4e00\u53cc\u602a\u773c\uff0c\u624b\u638c\u671d\u5ca9\u77f3\u4e00\u62cd\uff0c\u7a81\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5a03\u513f\u5012\u597d\u5fc3 \u773c\u3002\u201d\u5de6\u624b\u4e00\u4f38\uff0c\u628a\u5c0f\u864e\u5b50\u4e00\u628a\u63ea\u7740\uff0c\u5c06\u4ed6\u6253\u4e86\u4e2a\u8f6c\uff0c\u5de6\u638c\u5728\u4ed6\u80cc\u5fc3\u4e00\u62cd\uff0c\u4e8e\u627f\u73e0\u5927\u60ca\uff0c\u6025 \u5fd9\u62a2\u8fc7\u6765\u6551\uff0c\u53ea\u89c1\u963f\u8428\u739b\u51fa\u638c\u5feb\u6781\uff0c\u5728\u5c0f\u864e\u5b50\u80cc\u5fc3\u8fde\u62cd\u4e09\u4e0b\uff0c\u4e00\u4e0b\u5c06\u4ed6\u63a8\u5f00\uff0c\u5c0f\u864e\u5b50\u8179\u5185\u5495 \u5495\u4f5c\u58f0\uff0c\u5728\u5730\u4e0a\u8f6c\u4e86\u513f\u5708\uff0c\u7a81\u7136\u8dc3\u5165\u9635\u4e2d\u8eb2\u5230\u4e00\u5806\u4e71\u77f3\u7684\u540e\u9762\uff0c\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u5c0f\u864e\u5b50\u4f38\u51fa\u534a\u4e2a\u5934\uff0c\u8fde\u8fde\u6447\u624b\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u6765\uff0c\u6211\u8981\u62c9\u5c4e\u3002\u201d\u4e8e\u627f\u73e0\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u4f46\u89c1\u4ed6 \u9762\u8272\u5982\u5e38\uff0c\u58f0\u97f3\u4e0d\u6539\uff0c\u5374\u4e5f\u653e\u4e0b\u4e86\u5fc3\u3002\u963f\u8428\u739b\u4f3c\u7b11\u975e\u7b11\uff0c\u4e24\u53ea\u602a\u773c\u4ecd\u7136\u77aa\u7740\u4e8e\u627f\u73e0\uff0c\u628a\u4e8e\u627f \u73e0\u641e\u5f97\u83ab\u540d\u5176\u5999\uff0c\u4e0d\u77e5\u4ed6\u5f04\u7684\u662f\u751a\u7384\u865a\uff1f \u3000\u3000\u53ea\u542c\u5f97\u5c71\u5cf0\u4e0a\u9ed1\u6469\u8bc3\u53eb\u9053\uff1a\u201c\u770b\u5728\u4f60\u5e08\u5f1f\u8fd9\u4efd\u89c1\u9762\u793c\u7684\u4eba\u60c5\uff0c\u627f\u73e0\uff0c\u4f60\u9886\u4ed6\u4eec\u51fa\u53bb\u3002\u201d \u963f\u8428\u739b\u6068\u6068\u53eb\u9053\uff1a\u201c\u9ed1\u6469\u8bc3\uff0c\u6211\u53ef\u4e0d\u9886\u4f60\u8fd9\u4e2a\u60c5\uff01\u201d\u767d\u6469\u8bc3\u9053\uff1a\u201c\u4f60\u8981\u4e0e\u6211\u8f83\u91cf\uff0c\u4e5f\u5f97\u5f85\u4e00 \u5e74\u4e4b\u540e\u5566\uff01\u4f60\u77a7\u7740\uff0c\u6211\u8fd9\u91cc\u8fd8\u6709\u4e00\u652f\u672a\u5c04\uff0c\u7ed9\u4f60\u5f00\u8def\uff01\u201d\u77f3\u9635\u5e03\u5728\u5c71\u8170\u3002\u79bb\u5c71\u9876\u5c11\u8bf4\u4e5f\u6709\u767e \u6765\u4e08\u9ad8\uff0c\u4e24\u4eba\u8bf4\u8bdd\uff0c\u7adf\u5982\u9762\u5bf9\u3002\u4f46\u4e8e\u627f\u73e0\u5374\u4e5f\u542c\u51fa\uff0c\u963f\u8428\u739b\u7684\u58f0\u97f3\u77ed\u4fc3\uff0c\u663e\u662f\u5f3a\u7528\u7cbe\u795e\uff0c\u4e2d \u6c14\u4e0d\u8db3\u3002 \u3000\u3000\u8bdd\u58f0\u672a\u5b8c\uff0c\u957f\u7bad\u7834\u7a7a\u4e4b\u58f0\u53c8\u8d77\uff0c\u567c\u556a\u4e00\u58f0\u5de8\u54cd\uff0c\u7adf\u5c06\u963b\u5728\u963f\u8428\u739b\u9762\u524d\u7684\u4e00\u5757\u77f3\u5934\u5c04\u5f97\u5206 \u5f00\u4e24\u534a\uff0c\u963f\u8428\u739b\u77e5\u9053\u8fd9\u662f\u9ed1\u6469\u8bc3\u6709\u610f\u793a\u5a01\uff0c\u4e0b\u9010\u5ba2\u4ee4\uff0c\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u5a01\u98ce\uff0c\u53ea\u662f\u4f60\u8fd9 \u5a01\u98ce\u4e5f\u4e0d\u8fc7\u4ec5\u4ec5\u4e00\u5e74\u3002\u201d\u62c9\u8d77\u963f\u5408\u739b\u968f\u4e8e\u627f\u73e0\u8d70\u51fa\u77f3\u9635\uff0c\u56de\u5934\u6253\u91cf\u4e86\u4e8e\u627f\u73e0\u4e00\u4e0b\u9053\uff1a\u201c\u4f60\u4e5f \u662f\u90a3\u4e24\u4e2a\u602a\u7269\u7684\u5f1f\u5b50\u5417\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u7684\u5e08\u7236\u662f\u5f20\u5927\u4fa0\u5f20\u4e39\u67ab\u3002\u201d\u963f\u8428\u739b\u9053\uff1a\u201c\u54e6\uff0c\u5f20 \u4e39\u67ab\uff0c\u597d\uff0c\u6211\u9886\u4f60\u7684\u60c5\uff0c\u6211\u8bb0\u7740\u5566\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u8d70\u56de\u77f3\u9635\uff0c\u6361\u8d77\u90a3\u4e09\u652f\u957f\u7bad\uff0c\u7bad\u662f\u9ec4\u91d1\u6240\u94f8\uff0c\u6c89\u91cd\u975e\u5e38\uff0c\u4e8e\u627f\u73e0\u62b1\u5728\u624b\u4e2d\uff0c\u597d\u4e0d \u5403\u529b\u3002\u8d70\u5230\u751f\u95e8\uff0c\u89c1\u5c0f\u864e\u5b50\u6b63\u8d70\u51fa\u6765\uff0c\u9762\u8272\u82cd\u767d\uff0c\u597d\u50cf\u7626\u4e86\u4e00\u4e9b\uff0c\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u600e\u4e48 \u5566\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u53ea\u662f\u5927\u6cfb\u4e86\u4e00\u573a\uff0c\u53cd\u800c\u89c9\u5f97\u975e\u5e38\u8212\u670d\u3002\u201d\u539f\u6765\u963f\u8428\u739b\u6709\u4e00\u6837\u7edd \u6280\uff0c\u80fd\u7528\u63a8\u62ff\u4e4b\u6cd5\uff0c\u7ed9\u4eba\u6cbb\u75c5\uff0c\u5c0f\u864e\u5b50\u521d\u7ec3\u5185\u529f\uff0c\u8fc7\u4e8e\u6c42\u8fdb\uff0c\u80f8\u4e2d\u90c1\u79ef\uff0c\u4ed6\u81ea\u5df1\u5c1a\u672a\u77e5\u9053\uff0c \u963f\u8428\u739b\u5728\u4ed6\u80cc\u5fc3\u8fde\u62cd\u4e09\u638c\uff0c\u52a9\u4ed6\u4ee5\u6c14\u884c\u8840\uff0c\u5c06\u4f53\u4e2d\u7684\u6d4a\u6c14\u5168\u90fd\u4e0b\u964d\u6392\u6cc4\uff0c\u4ee4\u6e05\u6c14\u4e0a\u5347\uff0c\u6d41\u8f6c \u56db\u80a2\uff0c\u5bf9\u5c0f\u864e\u5b50\u5c06\u6765\u7684\u5185\u529f\u4fee\u7ec3\u5927\u6709\u88e8\u76ca\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6211\u7684\u4e24\u4f4d\u5e08\u7236\u8981\u501f\u4f60\u5e08\u7236\u7684\u9759\u5ba4\u7ec3\u529f\uff0c\u539f\u6765\u662f\u8981\u5bf9\u4ed8\u8fd9\u4e24\u4e2a\u602a \u7269\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u662f\u600e\u6837\u78b0\u5230\u8fd9\u4e24\u4f4d\u5e08\u7236\u7684\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u90a3\u5929\u665a\u4e0a\u6211\u628a\u6a0a\u82f1\u9501\u5728\u77f3 \u5ba4\uff0c\u51fa\u6765\u627e\u6211\u7684\u7239\uff0c\u884c\u5230\u6751\u5934\uff0c\u4fbf\u78b0\u89c1\u4e24\u4f4d\u5e08\u7236\uff0c\u4ed6\u4eec\u4ee5\u524d\u5230\u8fc7\u6211\u7684\u5bb6\u4e2d\uff0c\u6211\u77e5\u9053\u4ed6\u4eec\u53eb\u9ed1 \u767d\u6469\u8bc3\uff0c\u5927\u5e08\u7236\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u864e\u5b50\u5475\uff0c\u6709\u574f\u4eba\u627e\u4f60\u7239\u7684\u9ebb\u70e6\uff0c\u4f60\u4e0d\u597d\u56de\u5bb6\u53bb\u4e86\u3002\u201d\u6211\u8bf4\uff1a \u201c\u6709\u574f\u4eba\u6765\uff0c\u6211\u66f4\u8981\u56de\u53bb\u8bf4\u4e0e\u7239\u7239\u77e5\u9053\uff1a\u201c\u4e8c\u5e08\u7236\u767d\u6469\u8bc3\u9053\uff1a\u201c\u4f60\u672c\u4e8b\u8fd8\u672a\u7ec3\u6210\uff0c\u4f60\u53bb\u5e2e\u4e0d \u4e86\u4f60\u7239\uff0c\u7ed9\u4eba\u8bef\u4f24\uff0c\u90a3\u4f60\u7239\u5c31\u53cd\u800c\u7ed9\u4f60\u62d6\u7d2f\u4e86\u3002\u90a3\u4e24\u4e2a\u574f\u4eba\u4e0d\u662f\u4f60\u7239\u7684\u5bf9\u624b\uff0c\u4f60\u4e0d\u5982\u968f\u6211\u8d70 \u5427\uff0c\u6211\u5e26\u4f60\u53bb\u89c1\u5f20\u4e39\u67ab\uff0c\u4f60\u7239\u4ee5\u524d\u5bf9\u6211\u8bf4\u8fc7\uff0c\u60f3\u8ba9\u4f60\u62dc\u5728\u5f20\u4e39\u67ab\u95e8\u4e0b\uff0c\u6211\u4eec\u6b64\u6765\u5c31\u662f\u60f3\u5c06\u4f60 \u5e26\u53bb\u7684\u3002\u4f46\u4f60\u7239\u7239\u73b0\u5728\u6709\u4e8b\uff0c\u6211\u4eec\u4e5f\u6709\u6025\u4e8b\u8981\u627e\u5f20\u4e39\u67ab\uff0c\u4e0d\u80fd\u518d\u591a\u803d\u6401\uff0c\u6240\u4ee5\u6211\u4eec\u5c31\u4e0d\u53bb\u89c1 \u4f60\u7239\u5566\u3002\u6211\u4eec\u5df2\u5728\u4f60\u5bb6\u95e8\u524d\u7559\u4e0b\u4fe1\u606f\uff0c\u4ed6\u4eca\u665a\u628a\u90a3\u4e24\u4e2a\u574f\u4eba\u6253\u53d1\u4e4b\u540e\uff0c\u81ea\u7136\u4f1a\u6765\u627e\u4f60\u3002\u2019 \u55ef\uff0c\u627f\u73e0\u59d0\u59d0\uff0c\u4f60\u89c1\u7740\u4e86\u6211\u7684\u7239\uff0c\u4e3a\u4ec0\u4e48\u4ed6\u4e0d\u548c\u4f60\u4e00\u9053\u6765\uff1f\u201d\u4e8e\u627f\u73e0\u542c\u4e86\uff0c\u8fd9\u624d\u77e5\u9053\u539f\u59d4\uff0c \u5fc3\u9053\uff1a\u201c\u53ef\u60dc\u9ed1\u767d\u6469\u8bc3\u53ea\u89c1\u7740\u5148\u6765\u7684\u90a3\u4e00\u62e8\u574f\u4eba\uff0c\u4ea6\u5373\u7948\u94b0\u6d3e\u6765\u7684\u90a3\u4e24\u4e2a\u4f7f\u8005\u6218\u4e09\u5c71\u548c\u95fb\u94c1 \u58f0\uff0c\u5374\u4e0d\u77e5\u7948\u9547\u4e5f\u6d3e\u6709\u4e24\u4e2a\u4f7f\u8005\u8fd8\u5728\u540e\u5934\u3002\u8981\u4e0d\u7136\u9ed1\u767d\u6469\u8bc3\u7eb5\u6709\u5929\u5927\u7684\u4e8b\u60c5\uff0c\u4e5f\u4f1a\u7559\u4e0b\u6765\u76f8 \u52a9\u3002\u201d \u3000\u3000\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u53d7\u4e86\u4ec0\u4e48\u59d4\u5c48\uff1f\u773c\u5708\u513f\u90fd\u7ea2\u4e86\uff1f\u54e6\uff0c\u662f\u4e86\uff0c\u6211\u7239\u7239\u4e0d\u613f\u89c1\u5ba2\uff0c\u4f60\u4e00 \u5b9a\u662f\u786c\u95ef\u5165\u6211\u7684\u5bb6\u4e2d\uff0c\u88ab\u4ed6\u8d23\u9a82\u4e86\u4e00\u987f\u4e86\uff0c\u662f\u4e48\uff1f\u54ce\uff0c\u4e0d\u8981\u54ed\uff0c\u4e0d\u8981\u54ed\uff0c\u6211\u7239\u8bf4\u8fc7\u7684\uff0c\u7537\u4eba \u5927\u4e08\u592b\uff0c\u6d41\u8840\u4e0d\u6d41\u6cea\u2026\u2026\u201d\u5c0f\u864e\u5b50\u89c1\u4e8e\u627f\u73e0\u773c\u89d2\u6ef4\u4e0b\u6cea\u6765\uff0c\u83ab\u660e\u5176\u5999\uff0c\u4e8e\u662f\u5145\u4f5c\u5927\u4eba\uff0c\u51fa\u8a00 \u5b89\u6170\uff0c\u5ffd\u60f3\u8d77\u4e8e\u627f\u73e0\u4e0d\u662f\u7537\u5b50\uff0c\u7239\u7239\u8bf4\u7684\u90a3\u53e5\u8bdd\u5bf9\u5979\u5e76\u4e0d\u9002\u7528\uff0c\u6b63\u60f3\u53e6\u7528\u8bf4\u8bdd\u529d\u89e3\uff0c\u4e8e\u627f\u73e0 \u9053\uff1a\u201c\u4f60\u7239\u7239\u88ab\u5bb3\u4e86\uff01\u201d\u5c0f\u864e\u5b50\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u6211\u7239\u7239\u88ab\u5bb3\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5c31\u662f\u90a3\u4e9b\u574f \u4eba\u5c06\u4ed6\u5bb3\u6b7b\u7684\u3002\u201d\u5c0f\u864e\u5b50\u5446\u4e86\u4e00\u5446\uff0c\u5ffd\u5730\u5927\u53eb\u9053\uff1a\u201c\u4f60\u80e1\u8bf4\uff0c\u6211\u7239\u7239\u82f1\u96c4\u76d6\u4e16\uff0c\u90a3\u4e9b\u574f\u4eba\u5c82 \u80fd\u5bb3\u5f97\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5fcd\u7740\u773c\u6cea\uff0c\u62bd\u51fa\u5f20\u98ce\u5e9c\u7559\u4e0b\u7684\u90a3\u67c4\u7f05\u5200\uff0c\u53c8\u4ece\u6000\u4e2d\u638f\u51fa\u90a3\u5e45\u8840\u8863\uff0c\u9053\uff1a\u201c\u5c0f\u864e \u5b50\uff0c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u4f60\u7239\u7239\u786e\u662f\u82f1\u96c4\u76d6\u4e16\uff0c\u90a3\u4e9b\u574f\u4eba\u4e00\u4e2a\u4e2a\u90fd\u88ab\u4ed6\u6740\u6b7b\u4e86\u3002\u4ed6\u7684\u4ec7\u4ed6\u81ea\u5df1\u5df2\u7ecf \u62a5\u4e86\u3002\u201d\u5c0f\u864e\u5b50\u9762\u8272\u5530\u5730\u53d8\u5f97\u60e8\u767d\uff0c\u9053\uff1a\u201c\u6211\u7239\uff0c\u2014\u2014\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u7239\u7239\u6b7b\u4e5f\u7791\u76ee\u4e86\u3002 \u8fd9\u53e3\u5b9d\u5200\u7559\u7ed9\u4f60\u7528\u3002\u201d\u5c0f\u864e\u5b50\u4e24\u773c\u8840\u7ea2\uff0c\u5b9a\u7740\u773c\u775b\u76ef\u7740\u4e8e\u627f\u73e0\uff0c\u731b\u5730\u4e3e\u8d77\u62f3\u5934\u671d\u7740\u80f8\u53e3\u4e00 \u6376\uff0c\u8fd9\u624d\u201c\u54c7\u201d\u7684\u4e00\u58f0\u54ed\u5f97\u51fa\u6765\uff0c\u4e8e\u627f\u73e0\u62ed\u53bb\u8138\u4e0a\u7684\u6cea\u73e0\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff0c\u4f60\u7239\u8bf4 \u7684\uff0c\u7537\u4eba\u5927\u4e08\u592b\uff0c\u6d41\u8840\u4e0d\u6d41\u6cea\u3002\u201d\u5c0f\u864e\u5b50\u63a5\u8fc7\u5b9d\u5200\u8840\u8863\uff0c\u62d4\u5200\u51fa\u9798\uff0c\u5411\u7a7a\u4e2d\u4e71\u6325\u51e0\u5200\uff0c\u53eb \u9053\uff1a\u201c\u6211\u4e0d\u54ed\uff0c\u6211\u4e0d\u54ed\uff01\u201d\u54ed\u58f0\u505c\u6b62\uff0c\u6cea\u73e0\u4ecd\u662f\u7c0c\u7c0c\u843d\u4e0b\uff0c\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u55ef\uff1b\u8fd9\u624d\u662f\u597d\u5b69 \u5b50\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u6211\u8981\u7528\u8fd9\u67c4\u5200\u6740\u5c3d\u5929\u4e0b\u574f\u4eba\u3002\u597d\u59d0\u59d0\uff0c\u4f60\u5c06\u6765\u6559\u6211\u6b66\u827a\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u4f60\u6709\u8fd9\u4e2a\u5fd7\u6c14\uff0c\u8fd8\u6101\u7ec3\u4e0d\u6210\u6b66\u827a\u5417\uff1f\u4f60\u7684\u4e24\u4f4d\u5e08\u7236\u548c\u6211\u7684\u5e08\u7236\u90fd\u4f1a\u6559\u4f60\u6b66\u827a\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5bf9\u5c0f\u864e\u5b50\u67d4\u58f0\u529d\u5bfc\uff0c\u5979\u81ea\u5df1\u5fc3\u4e2d\u5374\u4e5f\u662f\u5341\u5206\u96be\u8fc7\uff0c\u60f3\u8d77\u5f20\u98ce\u5e9c\u7684\u8840\u4ec7\u4ed6\u81ea\u5df1\u751f\u524d \u5df2\u62a5\u3001\u53ef\u662f\u81ea\u5df1\u7684\u6740\u7236\u4e4b\u4ec7\uff0c\u53c8\u8be5\u5411\u4f55\u4eba\u7d22\u62a5\uff1f\u5979\u529d\u5c0f\u864e\u5b50\u522b\u54ed\uff0c\u81ea\u5df1\u7684\u773c\u6cea\u5374\u4ecd\u662f\u7981\u4e0d\u4f4f \u593a\u7736\u800c\u51fa\uff0c\u5ffd\u542c\u5f97\u9ed1\u6469\u8bc3\u53eb\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u8fd9\u4e24\u4e2a\u5c0f\u5a03\u5a03\u662f\u600e\u4e48\u641e\u7684\uff1f\u6253\u9000\u4e86\u5f3a\u654c\u8fd8\u4e0d\u9ad8\u5174\uff0c \u53cd\u800c\u5728\u8fd9\u91cc\u6d41\u6cea\uff1f\u201d\u5979\u548c\u5c0f\u864e\u5b50\u76f8\u5bf9\u6d41\u6cea\uff0c\u9ed1\u767d\u6469\u8bc3\u5230\u4e86\u8eab\u8fb9\uff0c\u4ed6\u4eec\u8fd9\u624d\u53d1\u89c9\u3002\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u5f20\u98ce\u5e9c\u4f2f\u4f2f\u6b7b\u4e86\u3002\u6211\u529d\u5c0f\u864e\u5b50\u522b\u54ed\u3002\u201d\u9ed1\u767d\u6469\u8bc3\u6014\u4e86\u4e00\u6014\uff0c\u53eb\u9053\uff1a\u201c\u5f20\u98ce\u5e9c\u600e\u4e48\u6b7b\u4e86\uff1f\u5c31 \u662f\u90a3\u5929\u51fa\u7684\u4e8b\u5417\uff1f\u201d\u4e8e\u627f\u73e0\u5c06\u542c\u81ea\u6a0a\u82f1\u7684\u5f20\u98ce\u5e9c\u60e8\u70c8\u800c\u6b7b\u7684\u60c5\u51b5\u8f6c\u8ff0\u4e86\u4e00\u904d\uff0c\u9ed1\u6469\u8bc3\u9053\uff1a \u201c\u597d\uff0c\u751f\u662f\u82f1\u96c4\uff0c\u6b7b\u662f\u597d\u6c49\u3002\u5c0f\u864e\u5b50\u4f60\u6709\u5982\u6b64\u82f1\u96c4\u7684\u7236\u4eb2\uff0c\u8fd8\u54ed\u4ec0\u4e48\uff1f\u201d\u53c8\u5bf9\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u6211\u672c\u8be5\u8ba9\u4f60\u628a\u5c0f\u864e\u5b50\u5e26\u53bb\u627e\u4f60\u7684\u5e08\u7236\uff0c\u4f46\u5c0f\u864e\u5b50\u6b66\u529f\u672a\u6210\uff0c\u4e07\u91cc\u8fdc\u884c\uff0c\u53ea\u6050\u4e8e\u4f60\u4e0d\u4fbf\uff0c\u6211 \u4eec\u8981\u8d76\u56de\u5370\u5ea6\uff0c\u5c31\u8ba9\u5c0f\u864e\u5b50\u5148\u8ddf\u6211\u4eec\u4e24\u5e74\uff0c\u7136\u540e\u518d\u9001\u7ed9\u4f60\u7684\u5e08\u7236\uff0c\u4f60\u8bf4\u53ef\u597d\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u8fd9\u66f4\u662f\u5c0f\u864e\u5b50\u7684\u9020\u5316\u4e86\u3002\u55ef\uff0c\u73b0\u5728\u4f60\u8be5\u5c06\u6211\u5e08\u7236\u7684\u6d88\u606f\u544a\u8bc9\u6211\u4e86\u3002\u201d \u3000\u3000\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u6211\u542c\u4f60\u5e08\u7236\u8bf4\uff0c\u4ed6\u4eec\u8981\u5230\u4e91\u5357\u7684\u5927\u7406\u53bb\uff0c\u4f60\u592a\u5e08\u7956\u5728\u5927\u7406\u7684\u70b9\u82cd\u5c71\u4e0a\uff0c\u4eca \u5e74\u6070\u5de7\u662f\u4ed6\u516b\u5341\u4e00\u5c81\u7684\u5927\u5bff\uff0c\u4f60\u5e08\u7236\u8d81\u6b64\u65f6\u673a\uff0c\u4e00\u6765\u907f\u7978\uff0c\u4e8c\u6765\u66ff\u4ed6\u8001\u4eba\u5bb6\u62dc\u5bff\u3002\u201d\u4e8e\u627f\u73e0 \u7684\u592a\u5e08\u7956\u5373\u662f\u7384\u673a\u9038\u571f\uff0c\u5341\u5e74\u524d\u4e0e\u5927\u5bf9\u5934\u4e0a\u5b98\u5929\u91ce\u5316\u654c\u4e3a\u53cb\uff0c\u4e00\u540c\u5f52\u9690\uff0c\u8fd9\u4e8b\u4e8e\u627f\u73e0\u4ea6\u66fe\u542c \u5e08\u7236\u8bf4\u8fc7\uff0c\u73b0\u5728\u624d\u77e5\u9053\u539f\u6765\u4ed6\u4eec\u5c31\u662f\u9690\u5c45\u5728\u70b9\u82cd\u5c71\u3002 \u3000\u3000\u9ed1\u6469\u8bc3\u53c8\u9053\uff1a\u201c\u4f60\u5e08\u7236\u66fe\u7b49\u4f60\u4e09\u65e5\uff0c\u4e0d\u89c1\u4f60\u6765\uff0c\u8fd9\u624d\u51fa\u8d70\uff0c\u4ed6\u8bf4\u6709\u4e00\u5c01\u4fe1\u7559\u5728\u4e66\u623f\u7ed9 \u4f60\u3002\u201d\u4e8e\u627f\u73e0\u56de\u6765\u4e4b\u540e\uff0c\u6b63\u56e0\u89c1\u4e0d\u7740\u5e08\u7236\u5fc3\u4e2d\u6005\u6005\uff0c\u8fd9\u65f6\u542c\u8bf4\u5e08\u7236\u66fe\u7b49\u5979\u4e09\u65e5\uff0c\u53c8\u6709\u4e66\u4fe1\u7ed9 \u5979\uff0c\u5fc3\u4e2d\u751c\u4e1d\u4e1d\u7684\uff0c\u6df1\u611f\u5e08\u95e8\u60c5\u91cd\uff0c\u6094\u6068\u81ea\u5df1\u5728\u8def\u4e0a\u591a\u803d\u6401\u4e86\u65f6\u65e5\u3002 \u3000\u3000\u767d\u6469\u8bc3\u9053\uff1a\u201c\u90a3\u4e9b\u5927\u5185\u536b\u58eb\u7ed9\u54b1\u4eec\u6253\u4e86\u4e00\u987f\uff0c\u6599\u60f3\u77ed\u671f\u95f4\u5185\u4e0d\u6562\u518d\u5230\u6d1e\u5ead\u5c71\u6765\u3002\u53ea\u662f\u6b64 \u53bb\u4e91\u5357\uff0c\u4e07\u91cc\u8fe2\u8fe2\uff0c\u4f60\u5728\u8def\u4e0a\uff0c\u53ef\u8981\u5c0f\u5fc3\u3002\u5c06\u6765\u6211\u4eec\u4e5f\u8981\u53d6\u9053\u7f05\u7538\u5230\u4e91\u5357\u6765\u89c1\u4f60\u5e08\u7236\uff0c\u4f60\u89c1 \u5230\u5e08\u7236\u5148\u66ff\u6211\u4eec\u95ee\u5019\u3002\u201d\u9ed1\u767d\u6469\u8bc3\u643a\u4e86\u5c0f\u864e\u5b50\u5148\u8d70\uff0c\u4e8e\u627f\u73e0\u518d\u5165\u4e66\u623f\uff0c\u5979\u5f80\u65e5\u7ecf\u5e38\u5728\u4e66\u6848\u524d \u4f8d\u5019\u5f20\u4e39\u67ab\u5199\u5b57\uff0c\u77e5\u9053\u5e08\u7236\u4e60\u60ef\u628a\u4fe1\u7269\u653e\u5728\u5f53\u4e2d\u7684\u62bd\u5c55\uff0c\u6253\u5f00\u4e00\u770b\uff0c\u679c\u7136\u89c1\u5230\u91cc\u9762\u6709\u4e24\u5c01 \u4fe1\uff0c\u4e00\u5c01\u4fe1\u4e0a\u5199\u7740\u5979\u7684\u540d\u5b57\uff0c\u53e6\u4e00\u5c01\u5199\u7684\u5374\u662f\u5468\u5c71\u6c11\u7684\u540d\u5b57\uff0c\u53e6\u5916\u8fd8\u6709\u4e00\u5bf9\u5c0f\u5c0f\u7ea2\u65d7\uff0c\u4e00\u9762 \u65d7\u4e0a\u7ee3\u7740\u4e00\u8f6e\u7ea2\u65e5\uff0c\u53e6\u4e00\u9762\u5219\u7ee3\u7740\u4e00\u5f2f\u7709\u6708\uff0c\u4e8e\u627f\u73e0\u5148\u628a\u7ed9\u5979\u7684\u4fe1\u6253\u5f00\u6765\u770b\uff0c\u53ea\u89c1\u9664\u4e86\u4fe1\u7b3a \u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u5f20\u56fe\u753b\uff0c\u753b\u4e2d\u4e00\u5bf9\u4e2d\u5e74\u7537\u5973\uff0c\u867d\u7136\u4e0d\u4f3c\u81ea\u5df1\u5e08\u7236\u5e08\u6bcd\u4e00\u5bf9\u58c1\u4eba\uff0c\u76f8\u8c8c\u5374\u4e5f\u4e0d \u4fd7\u3002\u4e8e\u627f\u73e0\u62bd\u51fa\u4fe1\u7b3a\u5ff5\u9053\uff1a \u3000\u3000\u201c\u627f\u73e0\u5973\u5f1f\u5982\u6664\uff0c\u60ca\u95fb\u4ee4\u5c0a\u5669\u8017\uff0c\u75db\u660e\u5ba4\u4e4b\u81ea\u6bc1\u957f\u57ce\uff0c\u4f24\u4e39\u67ab\u4e4b\u4e27\u5931\u5e08\u53cb\uff0c\u65b0\u4ead\u6d41\u6d95\uff0c \u7109\u7136\u672a\u52d2\uff0c\u629a\u81ba\u75db\u6ce3\u8005\u5c82\u5f92\u6211\u4e8c\u4eba\u54c9\u3002\u552f\u671b\u5973\u5f1f\u5ff5\u4e16\u53d8\u6b63\u6bb7\uff0c\u6cb3\u5c71\u591a\u96be\uff0c\u8282\u54c0\u4e3a\u56fd\uff0c\u7ee7\u627f\u7236 \u5fd7\uff0c\u6bcb\u8d1f\u5e73\u751f\u3002 \u3000\u3000\u201c\u592a\u4e0a\u7687\u72e0\u5fc3\u8fa3\u624b\uff0c\u6211\u6240\u6df1\u77e5\uff0c\u590d\u4f4d\u4e4b\u540e\uff0c\u5fc5\u5c06\u8bdb\u622e\u529f\u81e3\uff0c\u800c\u7ece\u9a91\u6240\u53ca\uff0c\u6b64\u95f4\u4ea6\u975e\u51c0 \u571f\u3002\u6211\u56fa\u65e0\u60e7\uff0c\u4f46\u5973\u771f\u5d1b\u8d77\u4e1c\u5317\uff0c\u502d\u5bc7\u6270\u4e71\u4e1c\u5357\uff0c\u5c1a\u5e94\u5408\u529b\u540c\u5fc3\uff0c\u5171\u5fa1\u5916\u654c\uff0c\u6211\u4ecd\u4e00\u672c\u521d \u8877\uff0c\u4e0d\u6b32\u4e0e\u671d\u5ef7\u4f5c\u5bf9\u4e5f\u3002\u56e0\u662f\u6682\u65f6\u4e3a\u907f\u7978\u4e4b\u8ba1\uff0c\u8fdc\u8d74\u6ec7\u5357\uff0c\u4ea6\u8d81\u6b64\u65f6\u673a\uff0c\u4e0e\u4f60\u592a\u5e08\u7956\u62dc\u5bff\u3002 \u6211\u77e5\u4f60\u5fc5\u5c06\u968f\u6765\uff0c\u4f46\u76ee\u524d\u53e6\u6709\u5927\u4e8b\uff0c\u987b\u4f60\u4ee3\u529e\u3002\u6240\u7559\u65e5\u6708\u53cc\u65d7\uff0c\u4f60\u5f53\u968f\u8eab\u5bc6\u85cf\uff0c\u4f5c\u4e3a\u4fe1\u7269\uff0c \u89c1\u5b57\u540e\u5373\u643a\u5e26\u540c\u51fd\u4ef6\uff0c\u5f80\u5317\u75be\u9a70\uff0c\u82e5\u9022\u753b\u4e2d\u7537\u5973\uff0c\u5373\u91d1\u5200\u5c0f\u5be8\u4e3b\u5468\u5c71\u6c11\u592b\u5987\u4e5f\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u8bfb\u5b8c\u4fe1\u540e\uff0c\u5fc3\u4e2d\u867d\u662f\u60b2\u75db\uff0c\u4f46\u5f97\u8046\u5e08\u8bad\uff0c\u5fc3\u5934\u7eb7\u4e71\u5374\u5df2\u7a0d\u7a0d\u89e3\u5f00\u3002\u968f\u5373\u7b56\u9a6c\u4e0b \u5c71\uff0c\u5979\u4e5f\u66fe\u542c\u5e08\u7236\u8c08\u8fc7\u91d1\u5200\u5be8\u4e3b\u5468\u6770\u7684\u6545\u4e8b\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5468\u6770\u5e74\u8001\uff0c\u542c\u8bf4\u5927\u5c0f\u4e8b\u52a1\uff0c\u90fd\u5df2 \u4ea4\u4e0e\u4ed6\u7684\u513f\u5b50\uff0c\u5468\u5c71\u6c11\u592b\u5987\u600e\u4e48\u6562\u5192\u9669\u5165\u5173\uff0c\u6211\u7684\u5e08\u7236\u53c8\u600e\u4e48\u77e5\u9053\uff1f\u201d\u4f46\u5979\u7d20\u77e5\u5e08\u7236\u795e\u673a\u5999 \u7b97\uff0c\u6599\u4e8b\u5982\u795e\uff0c\u867d\u7136\u4e0d\u660e\u5176\u4e2d\u539f\u6545\uff0c\u4ecd\u662f\u6309\u7167\u5e08\u7236\u5631\u6258\uff0c\u5feb\u9a6c\u75be\u9a70\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7b56\u9a6c\u4e0b\u5c71\uff0c\u6765\u5230\u6e56\u8fb9\uff0c\u4f46\u89c1\u6d69\u701a\u6ce2\u5149\uff0c\u5374\u65e0\u5e06\u5f71\uff0c\u6b63\u5728\u8e0c\u8e87\uff0c\u5ffd\u89c1\u67f3\u9634\u6df1\u5904\uff0c\u8361 \u51fa\u4e00\u53f6\u6e14\u821f\uff0c\u821f\u4e0a\u6e14\u7fc1\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\uff0c\u4f60\u8981\u5230\u65e0\u9521\u53bb\u5417\uff1f\u6211\u662f\u5c71\u8170\u6977\u6777\u6797\u5b50\u91cc\u4f4f\u7684\u859b \u8001\u4e09\u5475\uff0c\u4f60\u8fd8\u8ba4\u5f97\u6211\u5417\uff1f\u201d\u897f\u6d1e\u5ead\u5c71\u4e0a\uff0c\u901a\u5171\u4e0d\u8fc7\u6570\u767e\u4eba\u5bb6\uff0c\u4e8e\u627f\u73e0\u5728\u5c71\u4e0a\u4f4f\u4e86\u516b\u5e74\uff0c\u5bf9\u5c71 \u4e0a\u5c45\u6c11\uff0c\u867d\u7136\u672a\u5fc5\u53eb\u5f97\u51fa\u540d\u5b57\uff0c\u5927\u534a\u90fd\u80fd\u8ba4\u5f97\uff0c\u859b\u8001\u4e09\u4e00\u8bf4\uff0c\u5979\u7acb\u5373\u8bb0\u4e86\u8d77\u6765\uff0c\u6709\u70b9\u96be\u4e3a\u60c5 \u5730\u7b11\u9053\uff1a\u201c\u521a\u624d\u6211\u4e0a\u5c71\u65f6\uff0c\u4f60\u4e0d\u662f\u4e5f\u6b63\u4e0a\u5c71\u5417\uff1f\u6211\u6362\u4e86\u8fd9\u8eab\u7537\u5b69\u5b50\u7684\u8863\u88f3\uff0c\u4e8f\u4f60\u4e5f\u8ba4\u5f97\u51fa\u3002 \u4f60\u5012\u80c6\u5927\u5475\uff0c\u4ed6\u4eec\u90fd\u8eb2\u8d77\u6765\u4e86\u3002\u201d\u859b\u8001\u4e09\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5b9a\u8981\u6e21\u6c5f\uff0c\u7279\u522b\u6765\u9001\u4f60\u4e00\u7a0b\u3002\u59d1 \u5a18\uff0c\u54b1\u4eec\u4e0a\u8239\u518d\u8bf4\uff01\u201d \u3000\u3000\u859b\u8001\u4e09\u628a\u767d\u9a6c\u7275\u5230\u8239\u4e0a\uff0c\u7af9\u6746\u4e00\u6491\uff0c\u5c0f\u821f\u5982\u7bad\u79bb\u5cb8\uff0c\u4ed6\u53f9\u4e86\u53e3\u6c14\u8bf4\u9053\uff1a\u201c\u5e78\u4e8f\u4f60\u4eec\u6253\u8d25 \u4e86\u90a3\u4e9b\u5bb6\u4f19\uff0c\u8981\u4e0d\u7136\u6211\u4eec\u54ea\u6562\u51fa\u6765\u3002\u5f20\u5927\u4fa0\u771f\u662f\u597d\u4eba\uff0c\u4ed6\u4e34\u8d70\u65f6\u65e9\u5df2\u6599\u5230\u6709\u4e00\u573a\u7978\u4e8b\uff0c\u53eb\u6211 \u4eec\u8eb2\u8d77\u6765\u6682\u907f\u98ce\u5934\u7684\uff0c\u55ef\uff0c\u4ed6\u53bb\u4e86\u54ea\u91cc\uff0c\u4e0d\u77e5\u51e0\u65f6\u624d\u80fd\u56de\u6765\uff1f\u201d\u6241\u821f\u4e00\u53f6\uff0c\u4e0d\u51cf\u98ce\u5e06\uff0c\u4e8e\u627f \u73e0\u56de\u5934\u4e00\u671b\uff0c\u540e\u9762\u5c71\u5cf0\u9690\u7ea6\uff0c\u6d1e\u5ead\u5c71\u5e84\u4e5f\u671b\u4e0d\u89c1\u4e86\uff0c\u5979\u5728\u8fd9\u91cc\u4f4f\u4e86\u516b\u5e74\uff0c\u65e9\u5df2\u628a\u6d1e\u5ead\u5c71\u5e84\u5f53 \u6210\u4e86\u5979\u7684\u5bb6\uff0c\u60f3\u8d77\u81ea\u5df1\u4e5f\u4e0d\u77e5\u4f55\u65f6\u65b9\u80fd\u56de\u6765\uff0c\u4e0d\u89c9\u4e00\u9635\u5fc3\u9178\uff0c\u6f2b\u5e94\u9053\uff1a\u201c\u55ef\uff0c\u6211\u5e08\u7236\u53bb\u7684\u5730 \u65b9\u8fdc\u7740\u5462\uff0c\u4f46\u4ed6\u6700\u7231\u8fd9\u513f\uff0c\u6211\u77a7\u4ed6\u8fc7\u4e0d\u4e86\u51e0\u5e74\uff0c\u8fdf\u65e9\u603b\u8981\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u859b\u8001\u4e09\u5520\u5520\u53e8\u53e8\u5730\u548c\u5979\u9053\u8bf4\u5f20\u4e39\u67ab\u521d\u6765\u8fd9\u91cc\u4f4f\u65f6\u7684\u79cd\u79cd\u60c5\u4e8b\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u5230\u6e56\u5fc3\uff0c\u592a\u6e56 \u4e03\u5341\u4e8c\u5cf0\uff0c\u5012\u6709\u8fc7\u534a\u6570\u7684\u5c71\u5cf0\u7559\u5728\u540e\u9762\u4e86\u3002\u4e8e\u627f\u73e0\u4e0d\u4f4f\u56de\u5934\u9065\u671b\uff0c\u6d1e\u5ead\u5c71\u4e0a\uff0c\u767d\u4e91\u6df1\u5904\uff0c\u4eff \u4f5b\u8fd8\u89c1\u5979\u7684\u5e08\u7236\u767d\u8863\u7fbd\u6247\uff0c\u5f9c\u5f89\u5176\u95f4\uff0c\u9aa4\u7136\u95f4\uff0c\u5979\u8111\u6d77\u4e2d\u5ffd\u7136\u6cdb\u8d77\u6bd5\u64ce\u5929\u90a3\u7c97\u8c6a\u7684\u6837\u8c8c\uff0c\u53ea \u4e00\u51fa\u73b0\u4fbf\u7acb\u523b\u7ed9\u5979\u5e08\u7236\u7684\u5f71\u5b50\u538b\u4e0b\u53bb\uff0c\u5979\u5fc3\u4e2d\u60f3\u5230\uff1a\u201c\u82e5\u62ff\u6bd5\u64ce\u5929\u6765\u6bd4\u6211\u5e08\u7236\uff0c\u771f\u5982\u86ee\u725b\u4e4b \u6bd4\u51e4\u51f0\u3002\u201d\u5176\u5b9e\u6bd5\u64ce\u5929\u4e5f\u6ca1\u6709\u5982\u662f\u4e4b\u7cdf\uff0c\u4ed6\u6e29\u6587\u5c14\u96c5\u4e4b\u5904\uff0c\u81ea\u7136\u662f\u4e0d\u80fd\u4e0e\u5f20\u4e39\u67ab\u76f8\u63d0\u5e76\u8bba\uff0c \u4f46\u90a3\u80a1\u8c6a\u6c14\uff0c\u5374\u4e5f\u5e76\u4e0d\u89c1\u5f97\u8f93\u4e8e\u5f20\u4e39\u67ab\u3002\u897f\u65b9\u7684\u5fc3\u7406\u5b66\u5bb6\u5206\u6790\uff0c\u5973\u5b69\u5b50\u603b\u662f\u7231\u5e55\u81ea\u5df1\u6700\u4eb2\u8fd1 \u6700\u5d07\u62dc\u7684\u4eba\uff0c\u5728\u5979\u60c5\u7aa6\u521d\u5f00\u7684\u6726\u80e7\u610f\u8bc6\u4e2d\uff0c\u5979\u7b2c\u4e00\u4e2a\u60c5\u4eba\u7684\u5e7b\u5f71\uff0c\u5e38\u5e38\u5c31\u662f\u6309\u7167\u5979\u7684\u7236\u4eb2\u6216 \u8005\u5979\u7684\u5148\u751f\u7684\u5f71\u5b50\u63cf\u753b\u7684\u3002\u8fd9\u8bdd\u672a\u5fc5\u5168\u5bf9\uff0c\u4f46\u5728\u4e8e\u627f\u73e0\u5374\u6b63\u662f\u8fd9\u6837\u3002 \u3000\u3000\u5230\u4e86\u65e0\u9521\u4e0a\u5cb8\uff0c\u4e8e\u627f\u73e0\u8c22\u8fc7\u859b\u8001\u4e09\uff0c\u72ec\u81ea\u7275\u9a6c\u5317\u884c\uff0c\u7167\u591c\u72ee\u5b50\u9a6c\u811a\u7a0b\u8fc5\u75be\uff0c\u5979\u6015\u9519\u8fc7\u4e86 \u8981\u627e\u7684\u4eba\uff0c\u4e0d\u4f4f\u5730\u52d2\u7d27\u9a6c\u7586\uff0c\u4e0d\u8bb8\u5979\u8dd1\u5f97\u592a\u5feb\uff0c\u7b2c\u4e00\u5929\u8fd8\u6ca1\u4ec0\u4e48\uff0c\u7b2c\u4e8c\u5929\u5374\u53ef\u89c9\u5f97\u6709\u70b9\u5f02 \u6837\uff0c\u65f6\u4e0d\u65f6\u89c1\u6709\u4e09\u5c71\u4e94\u5cb3\u5404\u79cd\u5404\u6837\u7684\u53ef\u7591\u4eba\u7269\u5728\u9a7f\u9053\u4e0a\u5954\u9a70\uff0c\u9ec4\u660f\u65f6\u5206\uff0c\u5979\u6b63\u60f3\u653e\u9a6c\u75be\u884c\uff0c \u8d76\u5230\u524d\u9762\u7684\u4e00\u4e2a\u5c0f\u9547\u6295\u5bbf\uff0c\u5ffd\u89c1\u4e24\u9a91\u9a6c\u64e6\u8eab\u800c\u8fc7\uff0c\u4e00\u5339\u9a6c\u4e0a\u9a91\u7684\u662f\u4e2a\u6ee1\u9762\u80e1\u987b\u7684\u6c49\u5b50\uff0c\u53e6\u4e00 \u5339\u9a6c\u7684\u9a91\u5ba2\u5947\u602a\u4e4b\u6781\uff0c\u7adf\u7136\u662f\u4e2a\u4e5e\u4e10\u3002 \u3000\u3000\u90a3\u53eb\u5316\u5b50\u9e51\u8863\u767e\u7ed3\uff0c\u5374\u9a91\u7740\u4e00\u5339\u67a3\u7ea2\u5927\u9a6c\uff0c\u9a6c\u4e0a\u7ef5\u57ab\u96d5\u978d\uff0c\u5df2\u663e\u5f97\u4e0d\u4f26\u4e0d\u7c7b\uff0c\u8fd9\u65f6\u5ffd\u5730 \u56de\u5934\uff0c\u9f87\u7259\u9732\u9f7f\u5730\u51b2\u7740\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u4e8e\u76f8\u516c\u2026\u2026\u4e8e\u59d1\u5a18\uff0c\u54b1\u4eec\u7684\u5927\u9f99\u5934\u60f3\u5ff5\u4f60\u53ef\u60f3\u5ff5\u5f97\u7d27 \u5462\uff0c\u597d\u5475\uff0c\u4f60\u4e5f\u6765\u4e86\uff0c\u6211\u66ff\u5927\u9f99\u5934\u5411\u4f60\u8bf7\u5b89\u3002\u201d\u4ed6\u8eab\u5b50\u4e00\u8f6c\uff0c\u534a\u8fb9\u5c41\u80a1\u4fa7\u5750\u9a6c\u80cc\uff0c\u53cc\u624b\u6367\u7740 \u6253\u72d7\u68d2\uff0c\u5531\u4e86\u4e2a\u558f\uff0c\u5c31\u50cf\u5b98\u573a\u4e2d\u7684\u5c0f\u5b98\u89c1\u5927\u5b98\u4e4b\u65f6\uff0c\u9ad8\u6367\u540d\u523a\uff0c\u901a\u540d\u8c12\u89c1\u4e00\u822c\uff0c\u6837\u5b50\u751a\u662f\u6ed1 \u7a3d\u3002\u4e8e\u627f\u73e0\u4e00\u770b\uff0c\u539f\u6765\u8fd9\u53eb\u5316\u5b50\u6b63\u662f\u5c0f\u91d1\u9f99\u6b66\u632f\u4e1c\u5bb6\u4e2d\u89c1\u8fc7\u7684\u90a3\u4e2a\u6bd5\u613f\u7a77\u3002\u4e8e\u627f\u73e0\u53c8\u7f9e\u53c8 \u6c14\uff0c\u7389\u624b\u4e00\u626c\uff0c\u4e00\u6735\u91d1\u82b1\u7834\u7a7a\u63b7\u51fa\uff0c\u65a5\u9053\uff1a\u201c\u8c01\u8981\u4f60\u8fd9\u80ae\u810f\u5316\u5b50\u8bf7\u5b89\uff01\u201d\u91d1\u82b1\u6253\u5728\u68d2\u7684\u6b63 \u4e2d\uff0c\u53ea\u542c\u5f97\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u6253\u72d7\u68d2\u8131\u624b\u98de\u51fa\uff0c\u6bd5\u613f\u7a77\u5728\u9a6c\u80cc\u4e0a\u4e00\u8dc3\uff0c\u6253\u62d8\u68d2\u843d\u4e0b\uff0c\u6070\u6070\u7ed9\u5730 \u63a5\u7740\uff0c\u53ea\u89c1\u4ed6\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7b4b\u6597\uff0c\u5012\u7ffb\u4e0b\u6765\uff0c\u53c8\u7aef\u7aef\u6b63\u6b63\u5730\u843d\u5728\u9a6c\u80cc\u4e0a\uff0c\u6b6a\u7740\u5934\u56b7\u9053\uff1a\u201c\u81ea \u53e4\u4e91\u793c\u591a\u4eba\u4e0d\u602a\uff0c\u4f60\u67b6\u5b50\u518d\u5927\uff0c\u4e5f\u4e0d\u8be5\u4f38\u624b\u6253\u6211\u8fd9\u4e2a\u7b11\u9762\u4eba\uff0c\u5440\uff0c\u5440\uff0c\u4f60\u8fd9\u4e2a\u59d1\u5976\u5976\u771f\u96be\u4f8d \u5019\uff01\u201d\u6a2a\u68d2\u5728\u9a6c\u80cc\u4e0a\u4e00\u6572\uff0c\u90a3\u5339\u9a6c\u7acb\u523b\u6cfc\u5587\u5587\u5730\u5411\u524d\u75be\u8dd1\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5927\u6012\uff0c\u4f9d\u5979\u6027\u5b50\u672c\u60f3\u98de\u9a6c\u8ffd\u4e0a\uff0c\u518d\u6253\u4ed6\u4e24\u6735\u91d1\u82b1\uff0c\u4f46\u53c8\u6015\u4ed6\u80e1\u8bf4\u4e71\u56b7\uff0c\u63ed\u7834\u81ea\u5df1 \u7684\u5e90\u5c71\u771f\u8c8c\u3002\u8def\u4e0a\u4eba\u6765\u4eba\u5f80\uff0c\u82e5\u7ed9\u4eba\u542c\u5230\u4e00\u4e2a\u53eb\u5316\u5b50\u53eb\u81ea\u5df1\u505a\u201c\u59d1\u5976\u5976\u201d\uff0c\u8fd9\u53ef\u591a\u96be\u4e3a\u60c5\u3002 \u4e8e\u627f\u73e0\u867d\u7136\u4efb\u6027\uff0c\u5982\u6b64\u4e00\u60f3\uff0c\u5374\u662f\u6709\u6240\u987e\u5fcc\uff0c\u53cd\u800c\u52d2\u7d27\u4e86\u9a6c\uff0c\u4e0d\u6562\u4e0e\u6bd5\u613f\u7a77\u540c\u884c\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u9635\uff0c\u5c0f\u9547\u5df2\u7136\u5728\u671b\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\u9a6c\u94c3\u75be\u54cd\uff0c\u53c8\u4e00\u5339\u9a6c\u98de\u5954\u800c\u6765\uff0c\u64e6\u8eab\u800c\u8fc7\uff0c\u8fd9\u4eba \u8d76\u8def\u751a\u6025\uff0c\u4e0d\u4f4f\u5730\u6325\u52a8\u9a6c\u97ad\uff0c\u4f5c\u52bf\u8d76\u9a6c\uff0c\u51b2\u8fc7\u4e8e\u627f\u73e0\u8eab\u8fb9\u65f6\uff0c\u4e0d\u77e5\u662f\u6709\u610f\u8fd8\u662f\u65e0\u610f\uff0c\u567c\u556a\u4e00 \u97ad\u3002\u7adf\u7136\u8bef\u6253\u5230\u4e8e\u627f\u73e0\u7684\u9a6c\u4e0a\uff0c\u4e8e\u627f\u73e0\u8fd9\u5339\u7167\u591c\u72ee\u5b50\u9a6c\u751f\u6765\u672a\u66fe\u53d7\u8fc7\u4e3b\u4eba\u97ad\u6253\uff0c\u84e6\u7136\u4e2d\u4e86\u4e00 \u97ad\uff0c\u53d1\u4e86\u6027\u5b50\uff0c\u626c\u8d77\u524d\u8e44\u4fbf\u8e22\uff0c\u90a3\u4e58\u9a91\u5ba2\u662f\u4e2a\u80d6\u548c\u5c1a\uff0c\u5728\u9a6c\u80cc\u4e0a\u4e00\u4e2a\u8f6c\u8eab\uff0c\u62f3\u624b\u4e00\u62e6\u4e00\u6309\u3002 \u7adf\u7136\u628a\u7167\u591c\u72ee\u5b50\u9a6c\u62e6\u7740\uff0c\u6309\u5f97\u5b83\u5012\u9000\u51e0\u6b65\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5403\u4e86\u4e00\u60ca\uff0c\u987b\u77e5\u7167\u591c\u72ee\u5b50\u9a6c\u975e\u540c\u51e1\u9a6c\uff0c\u8fd9\u4e00\u8e22\u4e4b\u529b\u8db3\u6709\u4e94\u516d\u767e\u65a4\uff0c\u90a3\u80d6\u548c\u5c1a\u80fd\u6309 \u5f97\u5b83\u5012\u9000\uff0c\u8fd9\u4e00\u6309\u4e4b\u529b\uff0c\u6ca1\u6709\u5343\u65a4\uff0c\u4e5f\u6709\u516b\u767e\u3002\u4e8e\u627f\u73e0\u4e0d\u6687\u601d\u7d22\uff0c\u626c\u624b\u53c8\u662f\u4e00\u6735\u91d1\u82b1\uff0c\u90a3\u80d6 \u548c\u5c1a\u7684\u5750\u9a91\u5df2\u8dd1\u51fa\u5341\u4f59\u4e08\uff0c\u542c\u5f97\u540e\u9762\u6697\u5668\u5636\u98ce\u4e4b\u58f0\uff0c\u9a6c\u97ad\u4e00\u5708\uff0c\u7adf\u5c06\u91d1\u6735\u5377\u4f4f\uff0c\u626c\u97ad\u4e00\u7529\uff0c \u56de\u5934\u8d54\u793c\u9053\uff1a\u201c\u6d12\u5bb6\u8d76\u8def\u5fc3\u6025\uff0c\u8bef\u97ad\u5b9d\u9a6c\uff0c\u8bf7\u5c0f\u54e5\u591a\u591a\u6055\u7f6a\u3002\u201d\u4e8e\u627f\u73e0\u672c\u60f3\u548c\u4ed6\u5927\u6253\u4e00\u573a\uff0c \u89c1\u4ed6\u7b11\u9762\u8d54\u793c\u3002\u53c8\u60f3\u81ea\u5df1\u8eab\u6709\u8981\u4e8b\uff0c\u4e0d\u613f\u65e0\u8c13\u7f20\u6597\uff0c\u53ea\u5f97\u4f5c\u7f62\u3002 \u3000\u3000\u5230\u4e86\u9547\u4e0a\uff0c\u5929\u8272\u672a\u9ed1\uff0c\u4e8e\u627f\u73e0\u6709\u5fc3\u907f\u8fc7\u90a3\u6bd5\u613f\u7a77\uff0c\u7ecf\u8fc7\u4e00\u95f4\u5ba2\u5e97\uff0c\u89c1\u6bd5\u613f\u7a77\u90a3\u5339\u67a3\u7ea2\u5927 \u9a6c\uff0c\u62f4\u5728\u95e8\u5916\uff0c\u5979\u7acb\u523b\u6539\u4e86\u4e3b\u610f\uff0c\u60f3\u591a\u8d76\u4e00\u6bb5\u8def\u7a0b\uff0c\u54ea\u77e5\u62ac\u5934\u4e00\u770b\uff0c\u5374\u5ffd\u7136\u53d1\u73b0\u4e86\u4e00\u5b97\u7269 \u4e8b\uff0c\u4ee4\u5979\u6014\u5728\u5ba2\u5e97\u95e8\u524d\u3002 \u3000\u3000\u90a3\u5ba2\u5e97\u9752\u7816\u7eff\u74e6\uff0c\u662f\u5ea7\u4e24\u5c42\u9ad8\u516b\u89d2\u5f62\u7684\u5efa\u7b51\u7269\uff0c\u98de\u6a90\u7fd8\u89d2\uff0c\u9976\u6709\u53e4\u610f\uff0c\u697c\u4e0a\u4f4f\u5ba2\uff0c\u697c\u4e0b \u662f\u4e2a\u5927\u5802\uff0c\u6ca1\u6709\u96c5\u5ea7\uff0c\u517c\u8425\u9152\u9986\u751f\u610f\uff0c\u5ba2\u5e97\u89c4\u6a21\u76f8\u5f53\u5b8f\u4f1f\uff0c\u653e\u5728\u5927\u57ce\u5e02\u4e2d\uff0c\u4e5f\u53ef\u4ee5\u7b97\u5f97\u662f\u95f4 \u4e2d\u4e0a\u7684\u5ba2\u5e97\uff0c\u5c0f\u9547\u4e4b\u4e2d\uff0c\u5c45\u7136\u6709\u6b64\u5efa\u7b51\uff0c\u5df2\u662f\u4e00\u5947\uff0c\u4f46\u4ee4\u4e8e\u627f\u73e0\u5403\u60ca\u7684\u8fd8\u4e0d\u4ec5\u662f\u5b83\u7684\u5efa\u7b51\uff0c \u5ba2\u5e97\u7684\u6b63\u95e8\uff0c\u5de6\u53f3\u4e24\u8fb9\u5899\u4e0a\uff0c\u5404\u6709\u4e00\u5e45\u58c1\u753b\uff0c\u4e00\u8fb9\u662f\u4e00\u8f6e\u7ea2\u65e5\uff0c\u4e00\u8fb9\u662f\u4e00\u5f2f\u7709\u6708\uff0c\u8272\u6cfd\u5982 \u65b0\uff0c\u597d\u50cf\u662f\u521a\u521a\u753b\u4e0a\u53bb\u7684\u3002\u8fd9\u660e\u660e\u662f\u5468\u5c71\u6c11\u65e5\u6708\u53cc\u65d7\u7684\u6807\u8bb0\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7565\u4e00\u8e0c\u8e87\uff0c\u4fbf\u5373\u4e0b\u9a6c\uff0c\u5c06\u9a6c\u62f4\u597d\uff0c\u8e6c\u5165\u5ba2\u5e97\u7684\u5927\u5802\uff0c\u53ea\u89c1\u5e97\u5185\u5df2\u6709\u5341\u591a\u4e2a\u5ba2\u4eba\uff0c \u5206\u6210\u4e94\u516d\u5904\u5750\uff0c\u5947\u602a\u7684\u662f\uff0c\u5728\u666e\u901a\u7684\u9152\u5e97\uff0c\u6709\u8fd9\u4e48\u591a\u5ba2\u4eba\uff0c\u5fc5\u5b9a\u5608\u5608\u6742\u6742\uff0c\u751a\u6216\u731c\u62f3\u884c\u4ee4\uff0c \u5435\u95f9\u4e0d\u582a\uff1b\u800c\u8fd9\u95f4\u9152\u5e97\uff0c\u5374\u662f\u5bc2\u9759\u65e0\u54d7\uff0c\u6c14\u6c1b\u5341\u5206\u8083\u7a46\uff0c\u90a3\u4e9b\u5ba2\u4eba\uff0c\u5012\u4e0d\u50cf\u662f\u5728\u559d\u9152\uff0c\u800c\u50cf \u662f\u5230\u4ec0\u4e48\u5723\u5730\u671d\u62dc\u4f3c\u7684\u3002\u6bd5\u613f\u7a77\u548c\u90a3\u7c97\u8c6a\u6c49\u5b50\u5750\u5728\u897f\u9762\u4e34\u7a97\u7684\u4e00\u4ed8\u5ea7\u5934\uff0c\u6bd5\u613f\u7a77\u89c1\u4e8e\u627f\u73e0\u8fdb \u6765\uff0c\u54a7\u5634\u4e00\u7b11\uff0c\u4e8e\u627f\u73e0\u5fc3\u4e2d\u60f4\u60f4\uff0c\u5374\u559c\u4ed6\u4e95\u6ca1\u6709\u8bf4\u4ec0\u4e48\u523b\u8584\u7684\u8bdd\u513f\uff0c\u518d\u4e00\u770b\u90a3\u80d6\u548c\u5c1a\u4e5f\u72ec\u636e \u4e00\u684c\uff0c\u4e8e\u627f\u73e0\u770b\u4ed6\u65f6\uff0c\u4ed6\u4e5f\u6b63\u7785\u7740\u4e8e\u627f\u73e0\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u751a\u4e3a\u7eb3\u95f7\uff0c\u9009\u4e86\u4e00\u5904\u4e34\u7a97\u7684\u96c5\u5ea7\u5750\u4e0b\uff0c\u5e97\u5c0f\u4e8c\u8d70\u6765\uff0c\u4e0d\u4f4f\u5730\u6253\u91cf\u5979\uff0c\u4e8e\u627f\u73e0\u88c5\u505a \u6f2b\u4e0d\u7ecf\u610f\u5730\u5c06\u90a3\u5bf9\u65e5\u6708\u53cc\u65d7\u9732\u51fa\u3002\u5e97\u5c0f\u4e8c\u70b9\u4e86\u70b9\u5934\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5ba2\u5b98\u8981\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u4e8e\u627f\u73e0 \u8981\u4e86\u534a\u65a4\u5364\u725b\u8089\uff0c\u4e00\u65a4\u767d\u9152\uff0c\u5e97\u5c0f\u4e8c\u53c8\u7785\u4e86\u4e8e\u627f\u73e0\u4e00\u773c\uff0c\u9762\u4e0a\u9732\u51fa\u7591\u60d1\u7684\u795e\u8272\u3002\u4e8e\u627f\u73e0\u653e\u773c \u4e00\u770b\uff0c\u597d\u51e0\u5904\u684c\u4e0a\uff0c\u90fd\u6709\u4e00\u7897\u70ed\u6c14\u817e\u817e\u7684\u9cab\u9c7c\u6c64\uff0c\u4e8e\u627f\u73e0\u751a\u662f\u5947\u602a\uff0c\u600e\u4e48\u4ed6\u4eec\u4e0d\u7ea6\u800c\u540c\u5730\u90fd \u8981\u8fd9\u4e00\u5473\u83dc\u3002 \u3000\u3000\u90a3\u80d6\u548c\u5c1a\u81ea\u659f\u81ea\u996e\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u600e\u4e48\u6211\u8981\u7684\u83dc\u8fd8\u6ca1\u6765\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u5ba2\u5b98\u8981\u7684\u662f\u4ec0 \u4e48\uff1f\u201d\u80d6\u548c\u5c1a\u9053\uff1a\u201c\u6211\u4e00\u8fdb\u6765\u5c31\u5429\u5490\u8fc7\u4e86\uff0c\u6211\u8981\u7684\u662f\u7ea2\u70e7\u65f6\u5b50\u3002\u4f60\u4eec\u662f\u600e\u4e48\u641e\u7684\uff0c\u5ba2\u4eba\u8981\u4ec0 \u4e48\u83dc\u4f60\u4eec\u90fd\u5fd8\u8bb0\u4e86\uff1f\u201d\u5e97\u5c0f\u4e8c\u8d54\u7b11\u9053\uff1a\u201c\u521a\u624d\u4f3a\u5019\u4f60\u8001\u7684\u4f19\u8ba1\u8fdb\u53a8\u623f\u53bb\u4e86\uff0c\u6211\u518d\u53bb\u50ac\u4e00 \u50ac\u3002\u201d\u5ea7\u4e2d\u5ba2\u4eba\u5bf9\u90a3\u80d6\u548c\u5c1a\u6ce8\u76ee\u800c\u89c6\uff0c\u5374\u4e5f\u6ca1\u6709\u8bf4\u4ec0\u4e48\u3002\u4e0d\u4e00\u523b\uff0c\u6709\u4e00\u4eba\u79bb\u5ea7\u800c\u8d77\uff0c\u8d70\u4e0a\u697c \u68af\uff0c\u4e0a\u9762\u662f\u65c5\u5ba2\u4f4f\u5bbf\u7684\u623f\u95f4\uff0c\u4e0d\u77e5\u4ed6\u662f\u8bbf\u53cb\uff0c\u8fd8\u662f\u4ed6\u672c\u662f\u8fd9\u91cc\u7684\u4f4f\u5ba2\uff1f\u8fc7\u4e86\u7247\u523b\uff0c\u53c8\u4e0a\u53bb\u4e00 \u4e2a\u4eba\uff0c\u80d6\u548c\u5c1a\u5ffd\u7136\u65e0\u7f18\u65e0\u6545\u5730\u563b\u563b\u51b7\u7b11\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5e97\u5c0f\u4e8c\u7aef\u4e86\u4e00\u7897\u70ed\u6c14\u817e\u817e\u7684\u9cab\u9c7c\u6c64\u51fa\u6765\uff0c\u6367\u5230\u6bd5\u613f\u7a77\u7684\u684c\u5b50\u4e0a\uff0c\u80d6\u548c\u5c1a\u53cc\u773c \u4e00\u77aa\uff0c\u5ffd\u5730\u7ad9\u4e86\u8d77\u6765\u53eb\u9053\uff1a\u201c\u6211\u6bd4\u4ed6\u5148\u53eb\uff0c\u600e\u4e48\u4ed6\u7684\u5012\u5148\u6765\u4e86\uff1f\u201d\u5e97\u5c0f\u4e8c\u8d54\u7b11\u9053\uff1a\u201c\u4f60\u8001\u522b \u6025\uff0c\u5c31\u6765\u5c31\u6765\uff01\u201d\u80d6\u548c\u5c1a\u5927\u8e0f\u6b65\u8d70\u53bb\uff0c\u4e8e\u627f\u73e0\u8fd8\u4f3c\u4e3a\u4ed6\u5411\u638c\u67dc\u7684\u7406\u8bba\uff0c\u5ffd\u89c1\u4ed6\u6a2a\u8098\u4e00\u649e\uff0c\u5c06 \u5e97\u5c0f\u4e8c\u649e\u5012\u5730\u4e0a\uff0c\u56db\u811a\u671d\u5929\uff0c\u90a3\u7897\u70ed\u6c14\u817e\u817e\u7684\u9cab\u9c7c\u6c64\u6cfc\u5c06\u4e0b\u6765\uff0c\u6bd5\u613f\u7a77\u548c\u90a3\u7c97\u8c6a\u6c49\u5b50\u867d\u7136\u8eb2 \u95ea\u5f97\u5feb\uff0c\u8fd8\u662f\u7ed9\u6dcb\u5f97\u6ee1\u5934\u6ee1\u9762\u3002\u90a3\u6c49\u5b50\u5927\u6012\u559d\u9053\uff1a\u201c\u8d3c\u79c3\u9a74\uff0c\u4f60\u662f\u6545\u610f\u6d88\u9063\u8001\u5b50\u6765\u4e86\uff1f\u201d\u671d \u7740\u90a3\u80d6\u548c\u5c1a\u5288\u9762\u5c31\u662f\u4e00\u62f3\uff01 \u3000\u3000\u90a3\u80d6\u548c\u5c1a\u9053\uff1a\u201c\u6d12\u5bb6\u6b63\u5728\u624b\u75d2\uff0c\u4e0d\u6d88\u9063\u4f60\u8fd9\u86ee\u725b\u8fd8\u6d88\u9063\u8c01\uff1f\u201d\u5de6\u638c\u4e00\u4f38\uff0c\u6293\u7740\u4ed6\u7684\u62f3 \u5934\uff0c\u53f3\u624b\u4e00\u62db\u201c\u63a8\u7a97\u671b\u6708\u201d\uff0c\u6258\u7740\u90a3\u5927\u6c49\u7684\u8098\u5c16\u4e00\u63a8\u4e00\u9001\uff0c\u90a3\u5927\u6c49\u5e9e\u5927\u7684\u8eab\u8eaf\u767b\u65f6\u98de\u4e86\u8d77 \u6765\uff0c\u76f4\u5411\u67dc\u53f0\u649e\u53bb\uff0c\u638c\u67dc\u7684\u662f\u4e2a\u82b1\u767d\u80e1\u5b50\u7684\u8001\u6c49\uff0c\u6162\u817e\u817e\u5730\u9053\uff1a\u201c\u5ba2\u5b98\u4eec\u6253\u67b6\u5230\u5916\u9762\u6253\u53bb\uff0c \u5c0f\u5e97\u672c\u94b1\u77ed\u5c11\uff0c\u53ef\u8d54\u4e0d\u8d77\uff01\u201d\u90a3\u5927\u6c49\u8eab\u8eaf\u649e\u5230\uff0c\u638c\u67dc\u7684\u987a\u624b\u6293\u8d77\u4e00\u628a\u7b97\u76d8\uff0c\u5f80\u4e0a\u4e00\u67b6\uff0c\u53eb \u9053\uff1a\u201c\u6253\u574f\u5e97\u91cc\u7684\u4e1c\u897f\uff0c\u8fd9\u53ef\u4e0d\u884c\u5475\uff01\u201d\u770b\u90a3\u8001\u6c49\u6709\u6c14\u6ca1\u529b\uff0c\u8fd9\u7b97\u76d8\u4e00\u67b6\uff0c\u5374\u628a\u90a3\u5927\u6c49\u53c8\u63a8 \u56de\u53bb\u3002\u4e8e\u627f\u73e0\u5403\u4e86\u4e00\u60ca\uff0c\u770b\u8fd9\u638c\u67dc\u7684\u4e00\u63a8\u4e4b\u52bf\uff0c\u4e24\u80a1\u529b\u9053\u5bf9\u62cd\u6d88\uff0c\u4ed6\u7acb\u5373\u51ed\u7740\u672c\u8eab\u7684\u529f\u5200\uff0c \u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u5012\u7ffb\uff0c\u201c\u7830\u201d\u5730\u4e00\u811a\u5c06\u4e00\u5f20\u684c\u5b50\u8e22\u8d77\uff0c\u5411\u90a3\u80d6\u548c\u5c1a\u5f53\u5934\u5288\u4e0b\uff0c\u90a3\u80d6\u548c\u5c1a\u53cc\u81c2\u4e00 \u632f\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u597d\u597d\u5730\u6253\u4e00\u67b6\uff01\u201d\u90a3\u5f20\u684c\u4e8e\u88ab\u4ed6\u53cc\u638c\u9707\u98de\uff0c\u767b\u65f6\u88c2\u6210\u56db\u5757\uff0c\u98de\u5411\u56db \u65b9\uff0c\u6709\u4e00\u5757\u98de\u5230\u4e8e\u627f\u73e0\u7684\u5934\u4e0a\uff0c\u4e8e\u627f\u73e0\u4e00\u638c\u5c06\u5b83\u6253\u98de\uff0c\u653e\u773c\u4e00\u770b\uff0c\u5176\u4f59\u4e09\u5757\u4e5f\u90fd\u5df2\u540c\u65f6\u88ab\u4eba \u6253\u843d\u3002\u770b\u6765\u5728\u8fd9\u5e97\u4e2d\u7684\u5ba2\u4eba\uff0c\u8fde\u540c\u638c\u67dc\u7684\uff0c\u8dd1\u5802\u7684\u5728\u5185\uff0c\u4e2a\u4e2a\u90fd\u6709\u4e00\u8eab\u529f\u592b\u3002 \u3000\u3000\u5e97\u4e2d\u8bf8\u4eba\u4e2a\u4e2a\u5bf9\u90a3\u80d6\u548c\u5c1a\u6012\u76ee\u800c\u89c6\uff0c\u90a3\u80d6\u548c\u5c1a\u201c\u7830\u201d\u7684\u4e00\u62f3\uff0c\u53c8\u5c06\u90a3\u6761\u5927\u6c49\u6253\u5f97\u8dc4\u8dc4\u8e09 \u8e09\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u8138\u7684\uff0c\u5c31\u6765\u7fa4\u6bb4\uff01\u201d\u5ea7\u4e2d\u5ba2\u4eba\u90fd\u662f\u6c5f\u6e56\u4e0a\u6709\u8eab\u4efd\u7684\u4eba\u7269\uff0c\u867d\u7136\u6068\u90a3\u80d6\u548c\u5c1a \u86ee\u6a2a\u65e0\u793c\uff01\u5374\u65e0\u4e00\u4eba\u52a8\u624b\u52a9\u90a3\u58ee\u6c49\u3002 \u3000\u3000\u534e\u613f\u7a77\u563b\u563b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u53eb\u5316\u5b50\u6700\u4e0d\u8bb2\u7a76\u9762\u5b50\uff01\u201d\u6296\u8d77\u6728\u68d2\uff0c\u5f80\u90a3\u80d6\u548c\u5c1a\u8170\u80c1\u4e00\u70b9\uff0c \u80d6\u548c\u5c1a\u8eab\u8eaf\u867d\u80d6\uff0c\u8f6c\u52a8\u5374\u5f88\u7075\u4fbf\uff0c\u56de\u8eab\u4e00\u4e2a\u5288\u62f4\u638c\uff0c\u5c06\u6bd5\u613f\u7a77\u7684\u6253\u72d7\u68d2\u5e26\u8fc7\u4e00\u8fb9\uff0c\u8ddf\u7740\u4e00\u4e2a \u7bad\u62f3\uff0c\u5e73\u80f8\u6253\u5230\uff0c\u90a3\u7c97\u6c49\u5b50\u53cc\u638c\u4e00\u6321\u582a\u582a\u6321\u4f4f\uff0c\u80d6\u548c\u5c1a\u5de6\u62f3\u5316\u638c\uff0c\u62db\u6570\u5feb\u6781\uff0c\u8f7b\u8f7b\u4e00\u637a\uff0c\u638c \u98ce\u98d2\u7136\uff0c\u53c8\u7167\u7740\u6bd5\u613f\u7a77\u80f8\u819b\u5370\u4e0b\uff0c\u6bd5\u613f\u7a77\u8ba4\u51fa\u8fd9\u662f\u5c11\u6797\u62f3\u4e2d\u94c1\u7435\u7436\u638c\u7684\u529f\u592b\uff0c\u770b\u4f3c\u8f7b\u98d8\uff0c\u5176 \u5b9e\u5185\u52b2\u8574\u85cf\uff0c\u88ab\u4ed6\u201c\u5370\u201d\u4e0b\uff0c\u80f8\u9aa8\u5fc5\u7136\u6298\u65ad\uff0c\u6bd5\u613f\u7a77\u5e73\u7d20\u867d\u7136\u6ed1\u7a3d\u7a81\u68af\uff1b\u8fd9\u65f6\u5982\u4e0d\u6562\u6709\u534a\u70b9 \u5927\u610f\uff0c\u5c06\u68d2\u821e\u5f97\u98ce\u8f66\u822c\u5730\u56e2\u56e2\u75be\u8f6c\uff0c\u8fd9\u8def\u68d2\u6cd5\u662f\u6bd5\u5bb6\u4e16\u4ee3\u6240\u4e8c\u4f20\uff0c\u6709\u5708\u3001\u8f6c\u3001\u70b9\u3001\u6253\u3001\u5288\u3001 \u6302\u3001\u523a\u3001\u626b\u516b\u6cd5\uff0c\u53d8\u5316\u751a\u4e3a\u590d\u6742\u5947\u5999\uff0c\u52a0\u4e0a\u90a3\u5927\u6c49\u7684\u4e94\u884c\u62f3\u4e5f\u6253\u5f97\u751a\u4e3a\u7eaf\u719f\uff0c\u864e\u864e\u751f\u98ce\uff0c\u4ee5 \u4e8c\u654c\u4e00\uff0c\u65d7\u9f13\u76f8\u5f53\uff0c\u6253\u5f97\u684c\u5b50\u5012\u7ffb\uff0c\u677f\u51f3\u6298\u65ad\uff0c\u5ba2\u5e97\u4e2d\u987f\u65f6\u7a7a\u51fa\u4e00\u5927\u7247\u5730\u3002 \u3000\u3000\u638c\u67dc\u7684\u4e0d\u4f4f\u53eb\u56b7\uff0c\u8fd9\u4e09\u4e2a\u4eba\u6253\u5f97\u6027\u8d77\uff0c\u54ea\u91cc\u80af\u4f4f\uff0c\u6b63\u5728\u6253\u5f97\u4e0d\u4ea6\u4e50\u4e4e\uff0c\u95e8\u5916\u53c8\u8fdb\u6765\u4e86\u4e24 \u4e2a\u5ba2\u4eba\uff0c\u4e00\u8001\u4e00\u5c11\uff0c\u8001\u7684\u50cf\u662f\u4e2a\u4e61\u4e0b\u8001\u5934\uff0c\u62bd\u7740\u4e00\u6746\u65f1\u70df\u888b\uff0c\u5c11\u5e74\u4e5f\u6709\u4e09\u5341\u591a\u5c81\uff0c\u5374\u751f\u5f97\u53c8 \u77ee\u53c8\u80d6\uff0c\u50cf\u4e2a\u51ac\u74dc\u3002\u8fd9\u4e24\u4eba\u4e00\u8fdb\u6765\uff0c\u5e97\u4e2d\u7684\u5ba2\u4eba\u4eec\u76ee\u5149\u90fd\u6ce8\u5230\u4ed6\u4eec\u8eab\u4e0a\u3002 \u3000\u3000\u90a3\u8001\u5934\u5b50\u62bd\u4e86\u4e00\u53e3\u65f1\u70df\uff0c\u5c06\u70df\u6746\u4e00\u6307\uff0c\u8001\u6c14\u6a2a\u79cb\u5730\u9053\uff1a\u201c\u5e97\u4e2d\u95f9\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u638c\u67dc\u7684\u4f60 \u600e\u4e48\u4e0d\u7ba1\uff1f\u201d\u638c\u67dc\u7684\u4e0a\u524d\u8bf7\u4e86\u4e2a\u5b89\uff0c\u9053\uff1a\u201c\u90ed\u8001\u7237\u5b50\uff0c\u5b5f\u5927\u7237\uff0c\u54b1\u4eec\u5f00\u5e97\u7684\u53ef\u4e0d\u6562\u7ba1\u5ba2\u4eba \u5475\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u8d77\u5e08\u7236\u66fe\u548c\u5979\u8c08\u8fc7\u5317\u4e94\u7701\u5404\u8def\u82f1\u96c4\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u5c71\u4e1c\u7701\u7684\u72ec\u811a\u5927 \u76d7\uff0c\u540d\u5524\u90ed\u6210\u6cf0\uff0c\u6837\u5b50\u50cf\u4e2a\u8001\u5934\uff0c\u957f\u5e74\u68d2\u7740\u4e00\u6839\u65f1\u70df\u888b\uff0c\u4ed6\u70df\u7ba1\u6253\u7a74\u7684\u529f\u592b\uff0c\u5728\u7eff\u6797\u4e2d\u5374\u662f \u4e00\u628a\u4e86\u4e0d\u5f97\u7684\u597d\u624b\uff0c\u4ed6\u6709\u4e00\u4e2a\u5f92\u5f1f\u540d\u53eb\u5b5f\u957f\u751f\uff0c\u50cf\u4e2a\u77ee\u51ac\u74dc\uff0c\u90ed\u6210\u6cf0\u56e0\u6750\u65bd\u6559\uff0c\u4f20\u4e86\u4ed6\u4e00\u5957 \u5730\u5802\u62f3\uff0c\u4e5f\u662f\u540e\u8f88\u4e2d\u7684\u82f1\u6770\u3002\u60f3\u5fc5\u5c31\u662f\u8fd9\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u90ed\u6210\u6cf0\u542c\u638c\u67dc\u7684\u8bf4\u4e86\uff0c\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u8be5\u656c\u91cd\u7684\u5ba2\u4eba\u81ea\u8be5\u656c\u91cd\uff0c\u80e1\u95f9\u751f\u4e8b\u7684\u5ba2\u4eba\u4e48\uff0c \u4e5f\u8be5\u7ba1\u7ba1\u3002\u4f60\u7ba1\u5427\uff0c\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6211\u8001\u5934\u5b50\u62c5\u627f\u3002\u201d \u3000\u3000\u638c\u67dc\u7684\u7a0d\u4e00\u8e0c\u8e87\uff0c\u5954\u5165\u573a\u4e2d\uff0c\u9053\uff1a\u201c\u5ba2\u5b98\u770b\u5728\u90ed\u8001\u7237\u5b50\u7684\u4efd\u4e0a\uff0c\u505c\u624b\u4e86\u5427\uff0c\u5c0f\u7684\u5728\u8fd9\u513f \u7ed9\u4f60\u8d54\u7f6a\u4e86\u3002\u201d\u90a3\u80d6\u548c\u5c1a\u9053\uff1a\u201c\u4ec0\u4e48\u90ed\u8001\u7237\u5b50\uff1f\u4f60\u8981\u8d54\u7f6a\uff0c\u7ed9\u6211\u53e9\u4e09\u4e2a\u54cd\u5934\uff0c\u53eb\u6211\u7237\u7237\u3002\u201d \u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u5e95\u5374\u662f\u4e1d\u6beb\u4e0d\u7f13\uff0c\u201c\u7830\u201d\u201c\u7830\u201d\u4e24\u62f3\uff0c\u5de6\u62f3\u5c06\u90a3\u7c97\u8c6a\u6c49\u5b50\u6253\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u53f3\u62f3 \u5c06\u6bd5\u613f\u7a77\u7684\u6728\u68d2\u51fb\u98de\uff0c\u4e8e\u627f\u73e0\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u4e24\u62f3\u6b63\u662f\u7f57\u6c49\u795e\u62f3\u4e2d\u7684\u201c\u9f99\u62f3\u201d\u548c\u201c\u8c79\u62f3\u201d\u7684\u624b \u6cd5\uff0c\u867d\u7136\u4e0d\u53ca\u9ed1\u767d\u6469\u8bc3\u4f20\u7ed9\u5c0f\u864e\u5b50\u7684\u90a3\u6837\u795e\u5999\uff0c\u5374\u4e5f\u4e2d\u89c4\u4e2d\u77e9\uff0c\u8db3\u89c1\u529f\u529b\u3002\u770b\u6765\u8fd9\u80d6\u548c\u5c1a\u7adf \u662f\u6709\u5fc3\u53d6\u95f9\uff0c\u9002\u624d\u672a\u51fa\u5168\u529b\uff0c\u89c1\u5230\u6709\u4eba\u6765\u5e72\u9884\u65f6\uff0c\u624d\u663e\u51fa\u529f\u592b\u3002 \u3000\u3000\u90ed\u6210\u6cf0\u80e1\u5b50\u4e00\u7fd8\uff0c\u638c\u67dc\u7684\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u5e08\u7236\uff0c\u4f60\u8fd9\u6837\u95f9\u6cd5\uff0c\u5c0f\u7684\u53ea\u597d\u8bf7\u4f60\u51fa\u53bb \u5566\u3002\u201d\u4e24\u624b\u4e00\u4f38\uff0c\u642d\u5728\u80d6\u548c\u5c1a\u7684\u80a9\u4e0a\uff0c\u522b\u770b\u4ed6\u662f\u4e2a\u67af\u7626\u8001\u5934\uff0c\u8fd9\u4e00\u6293\u5374\u662f\u6b66\u6797\u7f55\u89c1\u7684\u5927\u9e70\u722a \u529b\u7684\u529f\u592b\u3002\u80d6\u548c\u5c1a\u80a9\u5934\u4e00\u6c89\uff0c\u6c14\u8fbe\u56db\u68a2\uff0c\u4e00\u4e2a\u201c\u6e14\u592b\u6652\u7f51\u201d\uff0c\u5378\u53bb\u4e86\u638c\u67dc\u7684\u5927\u529b\u9e70\u722a\u529f\uff0c\u80a9 \u5934\u5374\u662f\u706b\u8fa3\u8fa3\u5730\u75bc\u75db\uff0c\u4e24\u4eba\u90fd\u662f\u5fc3\u5934\u6697\u60ca\uff0c\u77e5\u9053\u662f\u78b0\u5230\u4e86\u52b2\u654c\u3002\u80d6\u548c\u5c1a\u53eb\u9053\uff1a\u201c\u6211\u7684\u94f6\u5b50\u53ef \u4e0d\u662f\u8165\u7684\uff0c\u4f60\u5f00\u5e97\u5b50\u51ed\u4ec0\u4e48\u4e0d\u8bb8\u6211\u5403\u4e1c\u897f\uff1f\u54fc\uff0c\u54fc\uff0c\u4f60\u8981\u64b5\u6211\u51fa\u53bb\uff0c\u6211\u5c31\u5148\u628a\u4f60\u8fd9\u5e97\u5b50\u62c6 \u4e86\u3002\u201d\u970e\u5ffd\u4e4b\u95f4\uff0c\u8fde\u51fa\u4e09\u62f3\uff0c\u90a3\u662f\u7f57\u6c49\u4e94\u884c\u795e\u62f3\u4e2d\u7684\u201c\u864e\u62f3\u201d\u201c\u86c7\u62f3\u201d\u548c\u201c\u9e64\u62f3\u201d\u7684\u8054\u5408\u8fd0 \u7528\uff0c\u4e09\u62f3\u8fde\u51fa\uff0c\u4e09\u79cd\u53d8\u5316\uff0c\u90a3\u638c\u67dc\u7684\u5927\u529b\u9e70\u722a\u529f\u53ea\u662f\u582a\u582a\u62b5\u6321\u5f97\u4f4f\u3002 \u3000\u3000\u6bd5\u613f\u7a77\u62fe\u8d77\u6728\u68d2\uff0c\u60f3\u4e0a\u6765\u52a9\u6218\uff0c\u5374\u89c1\u540c\u4f34\u5012\u5728\u5730\u4e0a\uff0c\u8fd8\u672a\u722c\u5f97\u8d77\u6765\uff0c\u4e0d\u77e5\u4ed6\u6709\u5426\u53d7\u4f24\uff0c \u65e0\u6687\u653b\u654c\uff0c\u5148\u884c\u6551\u53cb\u3002\u90ed\u6210\u6cf0\u7684\u5f92\u5f1f\u5b5f\u957f\u751f\u5df2\u5fcd\u8010\u4e0d\u4f4f\uff0c\u4e00\u4e2a\u7bad\u6b65\uff0c\u51b2\u4e0a\u524d\u6765\uff0c\u90a3\u80d6\u548c\u5c1a\u4e00 \u62f3\u6363\u51fa\uff0c\u8fd8\u672a\u51fb\u4e2d\uff0c\u4ed6\u5df2\u6251\u5012\u5730\u4e0a\uff0c\u8dcc\u4e86\u4e2a\u6eda\u5730\u846b\u82a6\u3002\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u4ed6\u5728\u7eff\u6797\u4e2d\u4e5f\u662f\u4e2a\u54cd \u5f53\u5f53\u7684\u89d2\u8272\uff0c\u600e\u4e48\u5982\u6b64\u4e0d\u6d4e\uff0c\u672a\u4e2d\u62f3\u5c31\u88ab\u62f3\u51e4\u9707\u5012\u4e86\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u6709\u5fc3\u6311\u6218\u706b\uff0c\u65e0\u610f\u4f1a\u82f1\u96c4\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e5d\u56de\u3000\u6cfc\u9152\u6597\u51f6\u987d\u3000\u591c\u5954\u8352\u91ce\u3000\u4f20\u4e66\u9080\u6297\u654c\u3000\u4e49\u8584\u4e91\u5929 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e5d\u56de\u3000\u6cfc\u9152\u6597\u51f6\u987d\u3000\u591c\u5954\u8352\u91ce\u3000\u4f20\u4e66\u9080\u6297\u654c\u3000\u4e49\u8584\u4e91\u5929 \u3000\u3000\u5374\u89c1\u90a3\u5b5f\u957f\u751f\u5728\u5730\u4e0a\u4e00\u4e2a\u201c\u61d2\u9a74\u6253\u6eda\u201d\uff0c\u6a2a\u638c\u4fbf\u524a\u90a3\u80d6\u548c\u5c1a\u7684\u5c0f\u817f\uff0c\u80d6\u548c\u5c1a\u6025\u5fd9\u7f29\u811a\uff0c \u76c2\u957f\u751f\u4ef0\u5367\u5730\u4e0a\uff0c\u53cc\u811a\u4e00\u8e22\uff0c\u53c8\u8e22\u5230\u548c\u5c1a\u7684\u819d\u76d6\uff0c\u539f\u6765\u4ed6\u5e76\u4e0d\u662f\u88ab\u548c\u5c1a\u7684\u62f3\u51e4\u6240\u9707\u5012\u4e2d\u662f\u5728 \u90a3\u91cc\u5927\u800d\u5317\u6d3e\u7684\u201c\u5730\u5802\u62f3\u201d\u3002\u53ea\u89c1\u4ed6\u5728\u5730\u4e0a\u6eda\u6765\u6eda\u53bb\uff0c\u6709\u5982\u8f97\u94f2\u4e71\u8f6c\uff0c\u5ffd\u800c\u811a\u8e22\uff0c\u5ffd\u800c\u624b \u6293\uff0c\u65f6\u800c\u4ee5\u8098\u652f\u5730\uff0c\u65f6\u800c\u4ee5\u80a9\u627f\u91cd\uff0c\u5012\u7ad6\u873b\u8712\u3002\u8eab\u5b50\u7075\u6d3b\u4e4b\u6781\uff0c\u80a9\u3001\u8098\u3001\u6307\u3001\u81c2\uff0c\u5404\u4e2a\u90e8 \u5206\uff0c\u90fd\u662f\u4e00\u6cbe\u5730\uff0c\u5373\u80fd\u501f\u529b\u817e\u8d77\uff0c\u7adf\u5982\u4e00\u4e2a\u76ae\u7403\u4e00\u822c\uff0c\u6240\u53d1\u62db\u6570\u751a\u662f\u602a\u5f02\uff0c\u5374\u65e0\u4e00\u4e0d\u662f\u653b\u5411 \u654c\u4eba\u8981\u5bb3\uff0c\u4e8e\u627f\u73e0\u867d\u7136\u542c\u5e08\u7236\u8bf4\u8fc7\u6709\u8fd9\u8def\u529f\u592b\uff0c\u5374\u672a\u66fe\u4eb2\u81ea\u89c1\u8fc7\uff0c\u8fd9\u65f6\u89c1\u90a3\u80d6\u548c\u5c1a\u88ab\u77ee\u51ac\u74dc \u903c\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u5f62\u72b6\u6ed1\u7a3d\u4e4b\u6781\uff0c\u4e0d\u7981\u5931\u58f0\u7b11\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u53cd\u811a\u8e22\u4ed6\u80cc\u810a\uff0c\u5954\u574e\u4f4d\u8e0f\u4ed6\u624b\u80cc\uff01\u201d\u201c\u8d70\u79bb\u65b9\u6311\u4ed6\u9f3b\u6881\uff01\u201d\u90ed\u6210\u6cf0 \u4e00\u77aa\u773c\u775b\uff0c\u53ea\u89c1\u4e00\u4e2a\u77ed\u5c0f\u7cbe\u608d\u7684\u53cc\u5b50\uff0c\u4e0d\u4f4f\u5730\u6307\u7740\u573a\u5fc3\uff0c\u53e3\u8bb2\u6307\u5212\uff0c\u7adf\u662f\u6307\u70b9\u90a3\u80d6\u548c\u5c1a\u7528\u9e33 \u9e2f\u8fde\u73af\u817f\u53bb\u7834\u5b5f\u957f\u751f\u7684\u5730\u5802\u62f3\uff0c\u770b\u6765\u4ed6\u5bf9\u5730\u5802\u62f3\u6781\u4e3a\u7cbe\u901a\uff0c\u7adf\u628a\u5b5f\u957f\u751f\u7684\u540e\u4e00\u7740\uff0c\u5373\u5c06\u6eda\u52a8 \u7684\u65b9\u5411\u90fd\u559d\u7834\u51fa\u6765\u3002\u5730\u5802\u62f3\u5168\u4ed7\u5728\u5730\u4e0a\u6253\u65cb\u6eda\u8f6c\uff0c\u6270\u4e71\u654c\u4eba\u5fc3\u5fd7\u4ee5\u53d6\u80dc\uff0c\u5728\u4e0d\u719f\u4e60\u5730\u5802\u62f3\u7684 \u4eba\u770b\u6765\uff0c\u4f46\u89c9\u4ed6\u4e71\u8f6c\u4e71\u6eda\uff0c\u96be\u4ee5\u9884\u6d4b\uff0c\u5176\u5b9e\u5185\u4e2d\u5b9e\u6709\u6cd5\u5ea6\uff0c\u5e76\u975e\u6742\u4e71\u65e0\u7ae0\u3002\u90a3\u80d6\u548c\u5c1a\u7684\u529f\u529b \u672c\u6765\u6bd4\u5b5f\u957f\u751f\u9ad8\u51fa\u8bb8\u591a\uff0c\u8fd9\u65f6\u4e00\u5f97\u540c\u4f34\u6307\u70b9\uff0c\u66f4\u52a0\u751f\u9f99\u6d3b\u864e\uff0c\u4e0a\u9762\u7528\u7f57\u6c49\u4e94\u884c\u62f3\uff0c\u4e0b\u9762\u7528\u9e33 \u9e2f\u8fde\u73af\u817f\uff0c\u540c\u65f6\u5bf9\u6297\u4e24\u4e2a\u5f3a\u654c\u3002\u90a3\u638c\u67dc\u7684\u65bd\u5c55\u5927\u529b\u9e70\u722a\u529f\uff0c\u5c1a\u80fd\u5e94\u4ed8\u88d5\u4f59\uff0c\u5b5f\u957f\u751f\u5374\u7ed9\u4ed6\u4e00 \u987f\u8fde\u73af\u817f\u8e22\u5f97\u5728\u5730\u4e0a\u4e1c\u95ea\u897f\u8eb2\uff0c\u72fc\u72c8\u4e4b\u6781\uff0c\u731b\u542c\u5f97\u90a3\u548c\u5c1a\u559d\u4e00\u58f0\u201c\u7740\u201d\uff01\u817e\u5730\u98de\u8d77\u811a\uff0c\u5c06\u5b5f \u957f\u751f\u8e22\u4e86\u4e00\u4e2a\u7b4b\u6597\u3002 \u3000\u3000\u90ed\u6210\u6cf0\u80e1\u5b50\u7fd8\u8d77\uff0c\u4e00\u53e3\u53e3\u5730\u55b7\u51fa\u6d53\u70df\uff0c\u663e\u89c1\u5fc3\u4e2d\u6124\u6012\u4e4b\u6781\uff0c\u53ea\u662f\u573a\u4e2d\u5df2\u662f\u4ee5\u4e8c\u6253\u4e00\uff0c\u4ee5 \u4ed6\u7684\u8eab\u4efd\uff0c\u81ea\u7136\u4e0d\u597d\u518d\u53bb\u5e2e\u573a\uff0c\u6bd5\u613f\u7a77\u8fd9\u65f6\u5df2\u5c06\u540c\u4f34\u6276\u8d77\uff0c\u90a3\u7c97\u8c62\u6c49\u5b50\u867d\u7136\u4e2d\u4e86\u80d6\u548c\u5c1a\u4e00 \u62f3\uff0c\u4ed6\u76ae\u7c97\u8089\u539a\uff0c\u5374\u65e0\u5927\u788d\uff0c\u6bd5\u613f\u7a77\u4e00\u6643\u5927\u68d2\uff0c\u76ef\u7740\u90a3\u77ed\u5c0f\u7cbe\u608d\u7684\u6c49\u5b50\u9053\uff1a\u201c\u9601\u4e0b\u65e2\u7136\u6280 \u75d2\uff0c\u6211\u8fd9\u4e2a\u5316\u5b50\u5012\u613f\u966a\u9601\u4e0b\u73a9\u73a9\u3002\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u541b\u5b50\u52a8\u53e3\u4e0d\u52a8\u624b\uff0c\u54c8\uff0c\u659c\u8d70\u9f9a\u4f4d\uff0c\u518d\u7ed9\u4ed6 \u4e00\u811a\uff0c\u7ba1\u4fdd\u4ed6\u4e0d\u80fd\u518d\u6253\u3002\u201d\u80d6\u548c\u5c1a\u4f9d\u8a00\u4e00\u811a\uff0c\u679c\u7136\u53c8\u628a\u76c2\u957f\u751f\u91cd\u91cd\u5730\u8e22\u4e86\u4e00\u811a\uff0c\u8e22\u5f97\u4ed6\u5728\u5730 \u4e0a\u8fde\u6253\u4e09\u4e2a\u5927\u7ffb\uff0c\u78b0\u5012\u4e86\u4e24\u5f20\u684c\u5b50\uff0c\u679c\u7136\u4e0d\u80fd\u518d\u6218\u4e86\u3002 \u3000\u3000\u6bd5\u613f\u7a77\u4e00\u751f\u620f\u5f04\u4eba\uff0c\u8fd9\u65f6\u53cd\u88ab\u90a3\u6c49\u5b50\u5632\u7b11\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u4fbf\u5f85\u4e0b\u573a\uff0c\u53ea\u6240\u5f97\u201c\u683c\u767b\u201d\u201c\u683c \u767b\u201d\u7684\u811a\u6b65\u58f0\uff0c\u6709\u4eba\u8d70\u4e0b\u697c\u68af\uff0c\u767b\u65f6\u5168\u573a\u8083\u9759\u65e0\u54d7\uff0c\u6bd5\u613f\u7a77\u5c06\u8981\u51fa\u7684\u811a\u6b65\u53c8\u7f29\u4e86\u56de\u6765\uff0c\u663e\u51fa \u606d\u656c\u4f8d\u5019\u7684\u6837\u5b50\u3002\u4e8e\u627f\u73e0\u5927\u4e3a\u5947\u602a\uff0c\u62ec\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u5bf9\u4e2d\u5e74\u7537\u5973\uff0c\u6b63\u5728\u4e00\u6b65\u4e00\u6b65\u5730\u8d70\u4e0b\u697c \u68af\u3002 \u3000\u3000\u8fd9\u5bf9\u4e2d\u5e74\u592b\u5987\u5e74\u7eaa\u4e0d\u8fc7\u4e09\u5341\u591a\u5c81\uff0c\u8863\u670d\u534e\u4e3d\uff0c\u4e4d\u773c\u770b\u53bb\uff0c\u4f3c\u662f\u7fc1\u5bb6\u516c\u5b50\u643a\u540c\u7737\u5c5e\u51fa\u6e38\uff0c \u4f46\u7709\u5b87\u4e4b\u95f4\uff0c\u82f1\u6c14\u52c3\u52c3\uff0c\u9152\u5e97\u4e2d\u90a3\u4e9b\u4e09\u5c71\u4e94\u5cb3\u7684\u4eba\u9a6c\uff0c\u4e00\u89c1\u4ed6\u4fe9\u8d70\u4e0b\u697c\u68af\uff0c\u7acb\u523b\u8083\u7136\u8d77\u7acb\uff0c \u9e26\u96c0\u65e0\u58f0\u3002\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u751a\u4e48\u4eba\u7adf\u6709\u8fd9\u6837\u5927\u7684\u6c14\u6d3e\uff1f\u201d\u770b\u6e05\u695a\u65f6\uff0c\u539f\u6765\u5c31\u662f\u5f20\u4e39\u67ab\u8981\u5979\u5bfb \u627e\u7684\u90a3\u5bf9\u753b\u56fe\u4e2d\u4eba\u2014\u2014\u91d1\u5200\u5c11\u5be8\u4e3b\u5468\u5c71\u6c11\u548c\u4ed6\u7684\u59bb\u5b50\u77f3\u7fe0\u51e4\u3002 \u3000\u3000\u90a3\u638c\u67dc\u7684\u4e00\u542c\u5230\u811a\u6b65\u58f0\u4fbf\u60f3\u8df3\u51fa\u5708\u5b50\uff0c\u80d6\u548c\u5c1a\u5374\u662f\u4e00\u70b9\u4e5f\u4e0d\u653e\u677e\uff0c\u4ed6\u5bf9\u5468\u56f4\u8083\u9759\u7684\u6c14 \u6c1b\uff0c\u7adf\u4f3c\u6beb\u65e0\u611f\u89c9\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u638c\u67dc\u7684\u8001\u513f\uff0c\u4f60\u8981\u8d70\u8fd9\u53ef\u4e0d\u6210\uff01\u201d\u5de6\u624b\u4e94\u6307\u4e00\u64a9\uff0c\u53f3\u62f3\u7a81 \u51fa\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u53c8\u5c06\u90a3\u638c\u67dc\u7684\u6454\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u90a3\u638c\u67dc\u7684\u5927\u529b\u9e70\u722a\u529f\u6bd4\u8d77\u80d6\u548c\u5c1a\u7684\u7f57\u6c49\u795e \u62f3\u529f\u529b\u867d\u7136\u7a0d\u900a\uff0c\u4f46\u4e5f\u4e0d\u81f3\u4e8e\u8f93\u5f97\u5982\u6b64\u4e4b\u60e8\u3002\u53ea\u56e0\u4ed6\u5fd9\u7740\u8981\u8fce\u63a5\u5468\u5c71\u6c11\uff0c\u6599\u4e0d\u5230\u80d6\u548c\u5c1a\u5c45\u7136 \u5982\u6b64\u65e0\u793c\uff0c\u6545\u6b64\u51b7\u4e0d\u9632\u4fbf\u7740\u4e86\u80d6\u548c\u5c1a\u7684\u9053\u513f\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u5168\u573a\u7686\u6012\uff0c\u6709\u51e0\u4e2a\u6469\u62f3\u64e6\u638c\uff0c\u4fbf\u60f3\u8df3\u51fa\uff0c\u90ed\u6210\u6cf0\u6025\u7ea2\u4e86\u773c\uff0c\u63d0\u8d77\u65f1\u70df\u7ba1\u4fbf\u5954\u4e0b \u573a\uff0c\u5ffd\u542c\u5f97\u5468\u5c71\u6c11\u9053\uff1a\u201c\u90ed\u8001\u7237\u5b50\uff0c\u4f60\u4e5f\u6765\u4e86\uff1f\u6709\u52b3\u524d\u8f88\uff0c\u5b9e\u4e0d\u6562\u5f53\u3002\u201d\u90ed\u6210\u6cf0\u6da8\u7ea2\u4e86\u8138\uff0c \u60f3\u8d77\u81ea\u5df1\u7684\u8eab\u4efd\u5b9e\u4e0d\u5b9c\u5728\u5468\u5c71\u6c11\u9762\u524d\uff0c\u8ddf\u8fd9\u4e2a\u80d6\u548c\u5c1a\u52a8\u6b66\u3002\u5468\u5c71\u6c11\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e8b\u8fc7 \u4e0d\u53bb\uff0c\u5750\u4e0b\u6765\u8c08\u8c08\u4e0d\u597d\u4e48\uff1f\u201d\u90a3\u80d6\u548c\u5c1a\u5927\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u4e00\u4f19\u90fd\u5e2e\u7740\u5e97\u5bb6\u6b3a\u8d1f\u4ffa\u51fa\u5bb6\u4eba\uff0c\u6d12 \u5bb6\u53ef\u662f\u4e0d\u4ff1\uff01\u201d\u5468\u5c71\u6c11\u7b11\u9053\uff1a\u201c\u600e\u89c1\u5f97\u6211\u5c31\u5e2e\u5b9a\u4e86\u5e97\u5bb6\uff0c\u4f60\u8bf4\u51fa\u7406\u6765\uff0c\u54b1\u4eec\u8bc4\u8bc4\u3002\u201d\u6709\u4e24\u4e2a \u5c11\u5e74\u4eba\u5fcd\u4e0d\u4e0b\u6c14\uff0c\u5728\u5468\u5c71\u6c11\u8bf4\u8bdd\u7684\u5f53\u513f\uff0c\u4fbf\u5954\u4e0a\u53bb\u8981\u62c9\u4e0b\u80d6\u548c\u5c1a\uff0c\u80d6\u548c\u5c1a\u4e00\u62db\u5de6\u53f3\u5f00\u5f13\uff1b\u5c06 \u4e24\u4e2a\u5c11\u5e74\u90fd\u6253\u5012\u4e86\u3002 \u3000\u3000\u90ed\u6210\u6cf0\u53eb\u9053\uff1a\u201c\u597d\u5440\uff0c\u6b3a\u8d1f\u5230\u6211\u7684\u5934\u4e0a\u8fd8\u4e0d\u6253\u7d27\uff0c\u5982\u4eca\u7adf\u7136\u6b3a\u8d1f\u5230\u91d1\u2014\u2014\u201d\u5e95\u4e0b\u7684\u8bdd\u8fd8 \u672a\u8bf4\u51fa\uff0c\u5468\u5c71\u6c11\u6446\u4e86\u6446\u624b\uff0c\u90ed\u6210\u6cf0\u731b\u9192\u8d77\u5468\u5c71\u6c11\u4e0d\u613f\u5728\u751f\u4eba\u9762\u524d\u8868\u9732\u8eab\u4efd\u3002\u4f46\u8fd9\u53e3\u6c14\u5374\u54bd\u4e0d \u4e0b\uff0c\u201c\u91d1\u5200\u5be8\u4e3b\u201d\u51e0\u5b57\u542b\u7cca\u5e26\u8fc7\uff0c\u8ddf\u7740\u5927\u559d\u9053\uff1a\u201c\u4ffa\u4e0d\u6559\u8bad\u4f60\u8fd9\u79c3\u9a74\uff0c\u4ffa\u4e0d\u59d3\u90ed\uff01\u201d\u8fd9\u65f6\u4ed6 \u6012\u706b\u653b\u5fc3\uff0c\u518d\u4e5f\u987e\u4e0d\u5f97\u5728\u5468\u5c71\u6c11\u9762\u524d\u5931\u4eea\uff0c\u4e00\u6296\u70df\u7ba1\uff0c\u5954\u5411\u80d6\u548c\u5c1a\u3002 \u3000\u3000\u80d6\u548c\u5c1a\u5927\u7b11\u9053\uff1a\u201c\u6211\u6b63\u8981\u9886\u6559\u9886\u6559\u4f60\u70df\u7ba1\u6253\u7a74\u7684\u529f\u592b\u3002\u201d\u968f\u4fbf\u7acb\u4e86\u4e00\u4e2a\u95e8\u6237\uff0c\u4f38\u62f3\u5f85 \u654c\uff0c\u5ffd\u89c1\u9762\u524d\u4eba\u5f71\u4e00\u6643\uff0c\u4e00\u4e2a\u6e05\u8106\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u51e0\u62db\u628a\u5f0f\uff0c\u914d\u5411\u90ed\u8001\u524d\u8f88\u8bf7\u6559\u4e48\uff1f\u201d \u8fd9\u4eba\u8eab\u5f62\u5feb\u6781\uff0c\u58f0\u5230\u4eba\u5230\uff0c\u500f\u5730\u4fbf\u662f\u4e00\u4e2a\u51b2\u62f3\u51fb\u5230\u9762\u95e8\uff0c\u80d6\u548c\u5c1a\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u600e\u4e48\u8fd9 \u53ae\u4e5f\u4f1a\u7f57\u6c49\u795e\u62f3\uff1f\u201d\u4f38\u638c\u4e00\u62e8\uff0c\u5de6\u638c\u7528\u7684\u662f\u94c1\u7435\u7436\u624b\uff0c\u53f3\u624b\u8fd8\u4e86\u4e00\u62db\u201c\u9e64\u62f3\u201d\uff0c\u90a3\u5c11\u5e74\u8eab\u5f62 \u5de6\u6643\uff0c\u907f\u8fc7\u4ed6\u7684\u94c1\u7435\u7436\u624b\uff0c\u5c48\u8d77\u4e94\u6307\uff0c\u731b\u7136\u4e00\u5544\uff0c\u7528\u7684\u4e5f\u662f\u201c\u9e64\u62f3\u201d\uff0c\u4f46\u62db\u6570\u5374\u662f\u602a\u5f02\u4e4b \u6781\uff0c\u4e00\u5544\u4e4b\u4e0b\uff0c\u62db\u5f0f\u672a\u53d8\uff0c\u7acb\u523b\u4fbf\u662f\u4e00\u4e2a\u8098\u9524\uff0c\u63a5\u7740\u957f\u62f3\u6a2a\u62d6\uff0c\u811a\u5e95\u4e00\u62e8\uff0c\u80d6\u548c\u5c1a\u5495\u549a\u4e00 \u58f0\uff0c\u8dcc\u5012\u5730\u4e0a\uff0c\u8fd9\u5c11\u5e74\u6b63\u662f\u4e8e\u627f\u73e0\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7684\u529f\u529b\u672c\u6765\u4e0d\u53ca\u80d6\u548c\u5c1a\u7684\u6df1\u539a\uff0c\u5374\u4f55\u4ee5\u4ec5\u4ec5\u5728\u4e09\u4e24\u4e2a\u7167\u9762\u4e4b\u95f4\uff0c\u4fbf\u80fd\u5c06\u80d6\u548c\u5c1a\u51fb \u5012\uff1f\u539f\u6765\u80d6\u548c\u5c1a\u4f7f\u7684\u662f\u6b63\u5b97\u7684\u5c11\u6797\u6d3e\u6240\u4f20\u7684\u7f57\u6c49\u795e\u62f3\uff0c\u4e8e\u627f\u73e0\u4f7f\u7684\u5374\u662f\u9ed1\u6469\u8bc3\u6240\u4f20\u7684\u3001\u63ba\u8fdb \u5370\u5ea6\u62f3\u6cd5\u3001\u7ecf\u8fc7\u53d8\u5316\u7684\u7f57\u6c49\u795e\u62f3\uff0c\u80d6\u548c\u5c1a\u4e0d\u77e5\u9053\u5176\u4e2d\u53d8\u5316\u7684\u7cbe\u5999\uff0c\u7528\u6b63\u89c4\u7684\u7f57\u6c49\u795e\u62f3\u53bb\u5bf9 \u4ed8\uff0c\u88ab\u4e8e\u627f\u73e0\u4e00\u4e2a\u5de7\u52b2\uff0c\u4fbf\u5c06\u4ed6\u7684\u62f3\u6cd5\u7834\u4e86\u3002 \u3000\u3000\u90a3\u80d6\u548c\u5c1a\u4e00\u8dcc\u5373\u8d77\uff0c\u6ee1\u9762\u61ca\u607c\u4e4b\u8272\uff0c\u5411\u4e8e\u627f\u73e0\u671b\u4e86\u4e00\u773c\uff0c\u5927\u8e0f\u6b65\u4fbf\u5411\u5916\u8d70\uff0c\u90ed\u6210\u6cf0\u559d \u9053\uff1a\u201c\u4f60\u8fd9\u6837\u4fbf\u4e00\u8d70\u4e86\u4e8b\u4e48\uff1f\u201d\u80d6\u548c\u5c1a\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u54e5\u7684\u62f3\u6280\u679c\u662f\u9ad8\u660e\uff0c\u6211\u8ba4\u8f93\u4fbf\u662f\u3002\u4f60 \u5462\uff0c\u6211\u53ef\u8fd8\u6ca1\u6709\u9886\u6559\uff0c\u4f60\u4e0d\u8bb8\u6211\u8d70\uff0c\u4e5f\u5f97\u50cf\u8fd9\u4f4d\u5c0f\u54e5\u4e00\u6837\u62ff\u51fa\u70b9\u672c\u4e8b\u6765\uff01\u201d\u90ed\u6210\u6cf0\u5927\u6012\uff0c\u70df \u6746\u4e00\u6446\uff0c\u9053\uff1a\u201c\u8001\u592b\u4e5f\u6ca1\u4ec0\u4e48\u672c\u4e8b\uff0c\u4f60\u8981\u8bd5\u5c3d\u7ba1\u6765\u8bd5\uff0c\u4f60\u82e5\u80fd\u5728\u6211\u70df\u6746\u5e95\u4e0b\u94bb\u51fa\u95e8\u53bb\uff0c\u6211\u4eca \u751f\u6c38\u4e0d\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\u3002\u201d\u5468\u5c71\u6c11\u89c1\u8fd9\u4e2a\u80d6\u548c\u5c1a\u4f3c\u662f\u5b58\u5fc3\u6311\u8845\uff0c\u4f46\u5374\u53c8\u5341\u5206\u76f4\u723d\uff0c\u8f93\u4e86\u4fbf\u670d\uff0c \u5012\u4e0d\u50cf\u662f\u4e2a\u574f\u4eba\uff0c\u5fc3\u4e2d\u5927\u662f\u7591\u60d1\uff0c\u5f53\u4e0b\u62e6\u5728\u4e24\u4eba\u4e2d\u95f4\uff0c\u7b11\u9053\uff1a\u201c\u56db\u6d77\u4e4b\u5185\u7686\u5144\u5f1f\u4e5f\uff0c\u6709\u4ec0\u4e48 \u8fc7\u4e0d\u53bb\u7684\u4e8b\u60c5\uff0c\u975e\u5f97\u62fc\u4e2a\u4f60\u6b7b\u6211\u6d3b\uff1f\u201d\u5b5f\u957f\u751f\u722c\u4e86\u8d77\u6765\uff0c\u7ad9\u5728\u4ed6\u5e08\u7236\u7684\u65c1\u8fb9\uff0c\u6c14\u547c\u547c\u5730\u6307\u7740 \u80d6\u548c\u5c1a\u9053\uff1a\u201c\u8fd9\u4e2a\u79c3\u9a74\u4e00\u8fdb\u6765\u5c31\u6363\u4e71\uff0c\u4eba\u4eba\u7686\u89c1\uff0c\u4f60\u95ee\u4ed6\u662f\u4ec0\u4e48\u9053\u7406\uff1f\u201d\u90a3\u80d6\u548c\u5c1a\u53cc\u773c\u4e00 \u7ffb\uff0c\u4e5f\u56b7\u9053\uff1a\u201c\u5927\u5bb6\u90fd\u662f\u8fdb\u6765\u559d\u9152\u5403\u996d\uff0c\u5374\u4e3a\u4f55\u8981\u539a\u6b64\u8584\u5f7c\uff0c\u4f60\u95ee\u95ee\u8fd9\u638c\u67dc\u7684\u53c8\u662f\u4ec0\u4e48\u9053 \u7406\uff1f\u201d\u4e24\u4eba\u4e03\u5634\u516b\u820c\u5730\u5435\u8d77\u6765\uff0c\u5468\u5c71\u6c11\u597d\u4e0d\u5bb9\u6613\u624d\u660e\u767d\u4e86\u539f\u59d4\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u4e3a\u4e86\u8fd9 \u4e00\u70b9\u5c0f\u4e8b\u3002\u5e97\u5bb6\uff0c\u6446\u597d\u5e2d\u4f4d\uff0c\u518d\u505a\u4e00\u4e9b\u62ff\u624b\u7684\u5c0f\u6848\u6765\uff0c\u5144\u5f1f\u4eca\u65e7\u8bf7\u5ba2\uff0c\u90ed\u8001\u7237\u5b50\uff0c\u5927\u548c\u5c1a\uff0c \u8fd9\u4f4d\u5c0f\u54e5\uff0c\u90fd\u770b\u5728\u6211\u7684\u9762\u4e0a\uff0c\u6765\u559d\u4e00\u676f\u3002\u201d\u5468\u5c71\u6c11\u662f\u4e2a\u5be8\u4e3b\u8eab\u4efd\uff0c\u8bf4\u8bdd\u81ea\u6709\u4e00\u80a1\u5a01\u4e25\uff0c\u90a3\u80d6 \u548c\u5c1a\u679c\u7136\u4e0d\u518d\u8a00\u8bed\uff0c\u4e0e\u4ed6\u540c\u6765\u7684\u90a3\u4e2a\u77ed\u5c0f\u6c49\u5b50\u5374\u6253\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u9053\uff1a\u201c\u840d\u6c34\u76f8\u9022\uff0c\u65e0\u8c13\u53e8\u6270 \u4eba\uff0c\u54b1\u4eec\u8fd8\u662f\u8d70\u5427\u3002\u201d\u5468\u5c71\u6c11\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u54e5\u7684\u8bdd\u592a\u8fc7\u4e0d\u50cf\u6c5f\u6e56\u6c49\u5b50\u7684\u8bf4\u8bdd\u4e86\uff0c\u5c82\u4e0d \u95fb\u7ea2\u82b1\u7eff\u53f6\uff0c\u90fd\u662f\u4e00\u5bb6\uff0c\u4e00\u676f\u6c34\u9152\uff0c\u4e5f\u503c\u5f97\u626d\u626d\u634f\u634f\uff0c\u50cf\u4e2a\u5a18\u513f\u4eec\u7684\u5ba2\u6c14\uff1f\u201d\u77f3\u7fe0\u51e4\u6a2a\u4ed6\u4e00 \u773c\uff0c\u9053\uff1a\u201c\u5a18\u513f\u4eec\u5c31\u90fd\u662f\u626d\u626d\u634f\u634f\u7684\u4e48\uff1f\u201d\u5468\u5c71\u6c11\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6211\u8bf4\u9519\u4e86\uff0c\u81ea\u7f5a\u4e09 \u676f\uff01\u201d\u90a3\u80d6\u548c\u5c1a\u89c1\u4ed6\u5982\u6b64\u8c6a\u723d\uff0c\u4e00\u5c41\u80a1\u4fbf\u5750\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e5f\u81ea\u7f5a\u4e09\u676f\uff01\u201d\u90a3\u77ed\u5c0f\u6c49\u5b50 \u77aa\u4ed6\u4e00\u773c\uff0c\u5468\u5c71\u6c11\u62c9\u4ed6\u9053\uff1a\u201c\u4f60\u4e5f\u6765\u540c\u559d\u4e00\u676f\uff01\u201d \u3000\u3000\u6b63\u5728\u62c9\u62c9\u626f\u626f\uff0c\u5ffd\u542c\u5f97\u95e8\u5916\u6709\u4eba\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u54b1\u4eec\u4e5f\u6765\u540c\u559d\u4e00\u676f\uff01\u201d\u53ea\u89c1\u95e8\u5916\u8d70\u8fdb\u4e24 \u4e2a\u519b\u5b98\uff0c\u90fd\u662f\u4f53\u683c\u9b41\u68a7\uff0c\u8170\u60ac\u957f\u5251\uff0c\u4e8e\u627f\u73e0\u4e00\u773c\u77a5\u53bb\uff0c\u8ba4\u5f97\u5934\u4e00\u4e2a\u6b63\u662f\u5927\u5185\u603b\u7ba1\u9633\u5b97\u6d77\uff0c\u95e8 \u5185\u8bf8\u4eba\u90fd\u53d8\u4e86\u989c\u8272\uff0c\u5468\u5c71\u6c11\u6545\u4f5c\u9547\u5b9a\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u597d\u6781\uff0c\u96be\u5f97\u4e24\u4f4d\u5927\u4eba\u5230\u6765\uff0c\u771f\u662f\u51fa\u95e8\u9022 \u7740\u8d35\u5ba2\uff0c\u8bf7\u4e5f\u8bf7\u4e0d\u5230\u5462\uff01\u201d\u5e97\u5bb6\u65e9\u5df2\u628a\u684c\u6905\u91cd\u65b0\u6446\u597d\u3002\u90a3\u4e24\u4e2a\u519b\u5b98\u8001\u5b9e\u4e0d\u5ba2\u6c14\u5c31\u5750\u7740\u5f53\u95e8\u7684 \u4e00\u684c\u3002 \u3000\u3000\u4e24\u4e2a\u519b\u5b98\u56db\u53ea\u773c\u775b\uff0c\u4e0d\u4f4f\u4ef7\u5730\u5728\u5468\u5c71\u6c11\u9762\u4e0a\u6e9c\u6765\u6e9c\u53bb\uff0c\u5468\u5c71\u6c11\u6c89\u7740\u4e86\u6c14\uff0c\u9053\uff1a\u201c\u8bf7\u6559\u4e24 \u4f4d\u5927\u4eba\u9ad8\u59d3\u5927\u540d\u3002\u201d\u9633\u5b97\u6d77\u9053\uff1a\u201c\u5144\u5f1f\u5c0f\u59d3\u9633\uff0c\u8d31\u540d\u5b97\u6d77\u3002\u8fd9\u4f4d\u662f\u5fa1\u6797\u519b\u7684\u7edf\u9886\u5a04\u6850\u82cf\u3002\u201d \u4f17\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u9633\u5b97\u6d77\u662f\u5929\u4e0b\u95fb\u540d\u7684\u56db\u5927\u5251\u5ba2\u4e4b\u4e00\uff0c\u5a04\u6850\u5b59\u5219\u662f\u524d\u4efb\u9526\u8863\u536b\u603b\u6307\u6325\u6218\u4e09\u5c71\u7684 \u5e08\u5144\uff0c\u4ed6\u4eec\u7684\u5e08\u7236\u662f\u664b\u5317\u6b66\u5b66\u5927\u5e08\u77f3\u9e3f\u535a\u3001\u4ee5\u5206\u7b4b\u9519\u9aa8\u624b\u548c\u4e94\u884c\u5251\u70b9\u7a74\u6cd5\u79f0\u4e3a\u6b66\u6797\u53cc\u7edd\uff0c\u5a04 \u6850\u82cf\u7684\u529f\u592b\u5c3d\u5f97\u5e08\u7236\u6240\u4f20\uff0c\u6bd4\u4ed6\u7684\u5e08\u5f1f\u8981\u9ad8\u660e\u5f97\u591a\u3002\u9633\u5b97\u6d77\u505a\u4e86\u519b\u5b98\u5df2\u662f\u4e00\u5947\uff0c\u5a04\u6850\u5b59\u4e5f\u53d8 \u6210\u4e86\u5fa1\u6797\u519b\u7edf\u9886\uff0c\u4f17\u4eba\u66f4\u662f\u610f\u60f3\u4e0d\u5230\u3002\u539f\u6765\u6218\u4e09\u5c71\u88ab\u5f20\u98ce\u5e9c\u6253\u6b7b\u540e\uff0c\u7687\u5e1d\u7948\u9547\u9063\u4eba\u8bf7\u77f3\u9e3f\u535a \u51fa\u5c71\uff0c\u5047\u8bf4\u6218\u4e09\u5c71\u662f\u88ab\u5f20\u4e39\u67ab\u6253\u6b7b\u7684\uff0c\u8981\u77f3\u9e3f\u535a\u4e3a\u4ed6\u7684\u5f92\u5f1f\u62a5\u4ec7\uff0c\u77f3\u9e3f\u535a\u63a8\u4ee5\u5e74\u8001\uff0c\u4f46\u4ed6\u4e00 \u65f6\u80e1\u6d82\uff0c\u4fe1\u4e86\u4f7f\u8005\u7684\u8bdd\uff0c\u4e00\u6765\u662f\u618e\u6068\u5f20\u4e39\u67ab\u4e0d\u770b\u4ed6\u7684\u60c5\u9762\uff0c\u6253\u6b7b\u6218\u4e09\u5c71\uff1b\u4e8c\u6765\u4e5f\u60f3\u672c\u6d3e\u6b66\u6280 \u626c\u540d\u4e2d\u539f\u3002\u9042\u6d3e\u4e86\u5927\u5f92\u5f1f\u8fdb\u4eac\uff0c\u7948\u9547\u679c\u7136\u91cd\u7528\uff0c\u7acb\u523b\u6388\u4e86\u4ed6\u5fa1\u6797\u519b\u7edf\u9886\u4e4b\u804c\u3002 \u3000\u3000\u9633\u5b97\u6d77\u548c\u5a04\u6850\u5b59\u5360\u7740\u4e86\u5f53\u95e8\u7684\u684c\u5b50\uff0c\u4f17\u4eba\u5fc3\u4e2d\u4e00\u51db\uff0c\u770b\u6837\u5b50\u7adf\u4f3c\u62e6\u95e8\u5835\u622a\uff0c\u4e0d\u6000\u597d\u610f\u3002 \u90a3\u77ed\u5c0f\u7cbe\u608d\u7684\u6c49\u5b50\u8d81\u7740\u4f17\u4eba\u4e0d\u7559\u610f\u4ed6\u4eec\u4e4b\u9645\uff0c\u628a\u80d6\u548c\u5c1a\u62c9\u4e86\u51fa\u6765\uff0c\u4e5f\u5750\u5728\u9760\u8fd1\u5927\u95e8\u7684\u4e00\u5f20\u684c \u5b50\u4e0a\uff0c\u4e0e\u9633\u5b97\u6d77\u3001\u5a04\u6850\u5b59\u6210\u4e3a\u4e86\u7284\u89d2\u4e4b\u52bf\u3002\u90ed\u6210\u6cf0\u563f\u563f\u51b7\u7b11\u3002\u9633\u5b97\u6d77\u7684\u773c\u5149\u5728\u4f17\u4eba\u9762\u4e0a\u626b \u8fc7\uff0c\u770b\u5230\u4e86\u4e8e\u627f\u73e0\u65f6\uff0c\u773c\u5149\u505c\u4f4f\u4e00\u4e0b\u3002\u9762\u4e0a\u5fae\u73b0\u8be7\u8272\uff0c\u4e8e\u627f\u73e0\u4e1d\u6beb\u4e0d\u4ff1\uff0c\u76f4\u7740\u773c\u775b\u76ef\u4ed6\u3002\u9633 \u5b97\u6d77\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u771f\u662f\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u529f\u592b\u3002\u5ea7\u4e2d\u90fd\u662f\u82f1\u8c6a\u3002\u4eca\u65e5\u7684\u9152\u559d\u5f97\u6700 \u75db\u5feb\u4e86\u3002\u201d\u4e0d\u5f85\u4eba\u529d\uff0c\u4ef0\u7740\u8116\u5b50\u5c31\u8fde\u559d\u4e86\u4e09\u5927\u676f\u3002 \u3000\u3000\u5468\u5c71\u6c11\u62f1\u624b\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u4eba\u516c\u4e8b\u5728\u8eab\uff0c\u5144\u5f1f\u4e0d\u6562\u529d\u9152\u3002\u559d\u4e86\u8fd9\u4e09\u676f\uff0c\u5927\u5bb6\u968f\u91cf\u5427\u3002\u8981\u9152 \u8981\u996d\uff0c\u5404\u4eba\u81ea\u4fbf\u3002\u201d\u9633\u5b97\u6d77\u9053\uff1a\u201c\u5144\u5f1f\u7684\u516c\u4e8b\uff0c\u5f97\u8001\u5144\u5e2e\u5fd9\uff0c\u60f3\u5df2\u6ca1\u6709\u95ee\u9898\uff0c\u8fd9\u4e09\u676f\u9152\u6211\u591a \u8c22\u4e86\u3002\u201d\u5468\u5c71\u6c11\u5403\u4e86\u4e00\u60ca\uff0c\u6309\u7740\u676f\u5b50\u9053\uff1a\u201c\u5927\u4eba\u6b64\u8bdd\uff0c\u662f\u4f55\u610f\u601d\uff1f\u201d\u9633\u5b97\u6d77\u9053\uff1a\u201c\u7687\u4e0a\u8bf7\u4f60 \u8001\u5144\u8fdb\u4eac\uff01\u201d\u5468\u5c71\u6c11\u867d\u7136\u6599\u5230\u9633\u5b97\u6d77\u4f1a\u77e5\u9053\u4ed6\u7684\u8eab\u4efd\uff0c\u5982\u60f3\u4e0d\u5230\u4ed6\u5bf9\u7740\u81ea\u5df1\u8fd9\u8fb9\u8fd9\u4e48\u591a\u4eba\uff0c \u7adf\u6562\u5355\u5200\u76f4\u5165\uff0c\u4e00\u6309\u9152\u676f\uff0c\u6602\u9996\u51b7\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u4e00\u4ecb\u4e66\u751f\uff0c\u8fde\u8003\u51e0\u6b21\u79c0\u624d\u90fd\u8003\u4e0d\u4e0a\uff0c\u54ea\u6709\u798f \u4efd\u89c1\u5f53\u4eca\u7687\u4e0a\uff0c\u9633\u5927\u4eba\u4e0d\u662f\u8bf4\u7b11\u8bdd\u5417\uff1f\u201d\u9633\u5b97\u6d77\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u660e\u4eba\u9762\u524d\u4e0d\u8bf4\u5047\u8bdd\uff0c\u5c11\u5be8\u4e3b\uff0c \u4f60\u6587\u6b66\u53cc\u5168\uff0c\u7687\u4e0a\u60f3\u671b\u5f97\u7d27\u54ea\u3002\u201d\u90a3\u80d6\u548c\u5c1a\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u9633\u5927\u4eba\uff0c\u8fd9\u4f4d\u5c0f\u54e5\u7684\u6b66\u827a\u4e5f\u597d\u5f97\u5f88 \u5440\uff0c\u4f60\u8981\u8bf7\u8be5\u4e00\u5e76\u8bf7\u53bb\u3002\u201d\u8fd9\u80d6\u548c\u5c1a\u662f\u4e2a\u6d51\u4eba\uff0c\u4ed6\u54ea\u91cc\u77e5\u9053\u6b64\u95f4\u5371\u673a\u4e00\u89e6\u5373\u53d1\uff0c\u8fd8\u4ee5\u4e3a\u9633\u5b97 \u6d77\u662f\u597d\u610f\u9080\u8bf7\u4ed6\u4eec\u5165\u4eac\uff0c\u4fdd\u7740\u4ed6\u4eec\u4e00\u5b98\u534a\u804c\uff0c\u4ed6\u4f69\u670d\u4e8e\u627f\u73e0\uff0c\u4e5f\u4e0d\u95ee\u81ea\u5df1\u4e0e\u9633\u5b97\u6d77\u6709\u65e0\u4ea4 \u60c5\uff0c\u4fbf\u884c\u63a8\u8350\u3002\u9633\u5b97\u6d77\u5929\u7b11\u9053\uff1a\u201c\u4e86\u7f18\u5927\u5e08\u8bf4\u7684\u662f\uff01\u6240\u6709\u8fd9\u91cc\u5728\u5ea7\u7684\u7537\u5973\u82f1\u96c4\uff0c\u6211\u90fd\u4e00\u5e76\u9080 \u8bf7\u4e86\uff01\u201d \u3000\u3000\u9633\u5b97\u6d77\u8fd9\u51e0\u53e5\u8bdd\u8f7b\u63cf\u6de1\u5199\uff0c\u773c\u4e2d\u7adf\u4f3c\u5168\u4e0d\u628a\u5404\u8def\u82f1\u96c4\u770b\u5728\u773c\u5185\u3002\u90ed\u6210\u6cf0\u7eb5\u6a2a\u7eff\u6797\uff0c\u51e0\u5341 \u5e74\u6765\u4ece\u672a\u53d7\u8fc7\u4eba\u8fd9\u822c\u8f7b\u89c6\uff0c\u9996\u5148\u6309\u637a\u4e0d\u4f4f\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u63a8\u5f00\u5ea7\u4f4d\uff0c\u53eb\u9053\uff1a\u201c\u597d\u5440\uff0c\u9633\u5927\u4eba\u8bf7 \u5ba2\uff0c\u8001\u673d\u5148\u53bb\uff01\u201d\u9633\u5b97\u6d77\u7d27\u7d27\u76ef\u7740\u5468\u5c71\u6c11\uff0c\u770b\u4e5f\u4e0d\u770b\u90ed\u6210\u6cf0\u4e00\u773c\uff0c\u4e00\u6446\u624b\u9053\uff1a\u201c\u90a3\u597d\u6781\u4e86\uff0c \u891a\u5e08\u5144\uff0c\u4f60\u63a5\u7279\u5ba2\u4eba\u3002\u201d\u90a3\u77ed\u5c0f\u7cbe\u608d\u7684\u6c49\u5b50\u5e94\u58f0\u800c\u8d77\uff0c\u4e00\u63a8\u90a3\u80d6\u548c\u5c1a\uff1a\u201c\u4e86\u7f18\u5927\u5e08\uff0c\u54b1\u4eec\u4e00 \u9f50\u63a5\u5f85\u3002\u201d\u90ed\u6210\u6cf0\u98de\u6b65\u62a2\u95e8\uff0c\u94c1\u70df\u6746\u4e00\u6307\uff0c\u5c31\u70b9\u90a3\u6c49\u5b50\u817f\u5f2f\u7684\u8f6f\u9ebb\u7a74\uff0c\u90a3\u6c49\u5b50\u6251\u5730\u53eb\u6eda\uff0c\u7a81 \u7136\u6478\u51fa\u4e00\u67c4\u5355\u5200\uff0c\u5728\u5730\u4e0a\u4e00\u5012\uff0c\u4fbf\u524a\u90ed\u6210\u6cf0\u7684\u80eb\u9aa8\u3002\u539f\u6765\u4ed6\u4e5f\u7cbe\u4e8e\u5317\u6d3e\u7684\u6eda\u5730\u5802\u5200\u6cd5\u3002 \u3000\u3000\u90ed\u6210\u6cf0\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u5b54\u592b\u5b50\u95e8\u524d\u5356\u767e\u5bb6\u59d3\uff01\u201d\u70df\u6746\u5f80\u4e0b\u4e00\u6311\uff0c\u5f53\u4f5c\u77ed\u82b1\u67aa \u7528\uff0c\u90a3\u6c49\u5b50\u4e00\u6eda\u95ea\u5f00\uff0c\u53ea\u542c\u5f97\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u90ed\u6210\u6cf0\u90a3\u652f\u70e7\u7ea2\u7684\u70df\u6597\u5df2\u5728\u4ed6\u7684\u819d\u76d6\u4e0a\u6572\u4e86\u4e00 \u4e0b\uff0c\u90a3\u6c49\u5b50\u7684\u6b66\u529f\u8fd8\u7b97\u4e0d\u5f31\uff0c\u90ed\u6210\u6cf0\u8fd9\u4e00\u8bb0\u672c\u6765\u662f\u7528\u4f5c\u6253\u7a74\u7684\uff0c\u7ed9\u4ed6\u907f\u8fc7\uff0c\u4f46\u8fd9\u4e00\u6572\u5982\u662f\u90ed \u5bb6\u72ec\u6709\u7684\u624b\u6cd5\uff0c\u5c06\u70df\u6597\u4ece\u201c\u70b9\u7a74\u6485\u201d\u53c8\u53d8\u6210\u4e86\u201c\u4e03\u661f\u9524\u201d\u4f7f\u7528\uff0c\u90a3\u6c49\u5b50\u5374\u907f\u4e0d\u8fc7\u4e86\u3002 \u3000\u3000\u80d6\u548c\u5c1a\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u53eb\u9053\uff1a\u201c\u9633\u5927\u4eba\u597d\u597d\u8bf7\u5ba2\uff0c\u4f60\u600e\u4e48\u5012\u52a8\u8d77\u7c97\u6765\u4e86\uff01\u201d\u5954\u4e0a\u53bb\u6551\uff0c \u90ed\u6210\u6cf0\u6068\u80d6\u548c\u5c1a\u9002\u624d\u6253\u4e86\u4ed6\u7684\u5f92\u5f1f\uff0c\u70df\u6746\u4e00\u6296\uff0c\u523a\u80d6\u548c\u5c1a\u8170\u80c1\uff0c\u70df\u6746\u98a4\u52a8\u4e0d\u4f11\uff0c\u5728\u523a\u622e\u4e4b \u4e2d\uff0c\u53c8\u968f\u65f6\u53ef\u53d8\u4e3a\u6253\u7a74\u7684\u624b\u6cd5\uff0c\u80d6\u548c\u5c1a\u53eb\u9053\uff1a\u201c\u597d\u5389\u5bb3\uff01\u201d\u4e00\u8f6c\u8eab\u53cc\u62f3\u9f50\u53d1\uff0c\u5de6\u9762\u7684\u62f3\u52bf\u662f \u9f99\u62f3\uff0c\u53f3\u9762\u7684\u62f3\u52bf\u662f\u864e\u62f3\uff0c\u8fd9\u5c11\u6797\u6d3e\u7684\u4e94\u884c\u7f57\u6c49\u795e\u62f3\uff0c\u5faa\u73af\u53d8\u5316\uff0c\u5999\u7528\u65e0\u7a77\uff0c\u90ed\u6210\u6cf0\u5e74\u8001\u529b \u8870\uff0c\u53ea\u80fd\u5de7\u53d6\uff0c\u4e0d\u6562\u529b\u63a5\uff0c\u60c5\u77e5\u8fd9\u70df\u6746\u523a\u4e0b\uff0c\u662f\u53ef\u6253\u4e2d\u548c\u5c1a\u7684\u7a74\u9053\uff0c\u4f46\u81ea\u5df1\u6700\u5c11\u4e5f\u5f97\u6328\u4ed6\u4e00 \u62f3\uff0c\u903c\u5f97\u64a4\u62db\u518d\u523a\uff0c\u80d6\u548c\u5c1a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u53ea\u4f1a\u5439\u725b\uff0c\u5374\u4e0d\u6562\u548c\u6211\u786c\u62fc\uff01\u201d\u8fc8\u6b65\u76f4\u4e0a\uff0c \u62f3\u51fa\u5982\u98ce\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u5c31\u4e00\u8fde\u6253\u4e86\u4e03\u516b\u62f3\uff0c\u62f3\u52bf\u51f6\u731b\u4e4b\u6781\uff0c\u6709\u5982\u94c1\u9524\u51fb\u77f3\uff0c\u5de8\u65a7\u5f00\u5c71\uff0c\u53ea\u8981 \u4e2d\u4e86\u4e00\u62f3\uff0c\u4fbf\u662f\u7b4b\u65ad\u9aa8\u6298\u4e4b\u7978\uff0c\u90ed\u6210\u6cf0\u5c55\u5f00\u5c0f\u5de7\u7684\u8eab\u6cd5\uff0c\u4e00\u6839\u70df\u6746\uff0c\u65f6\u800c\u5f53\u201c\u70b9\u7a74\u6485\u201d\uff0c\u65f6 \u800c\u5f53\u201c\u5c0f\u82b1\u67aa\u201d\uff0c\u65f6\u800c\u5f53\u201c\u4e03\u661f\u9524\u201d\uff0c\u6216\u523a\u6216\u70b9\u6216\u6572\uff0c\u4e5f\u662f\u5728\u970e\u773c\u4e4b\u95f4\uff0c\u4fbf\u53d8\u4e86\u4e03\u516b\u79cd\u624b \u6cd5\uff0c\u80d6\u548c\u5c1a\u6253\u4e0d\u7740\u4ed6\uff0c\u4ed6\u4e5f\u4e0d\u6562\u6b3a\u8eab\u8fdb\u51fb\uff0c\u770b\u6765\u4e24\u4eba\u662f\u5404\u6709\u64c5\u957f\uff0c\u534a\u65a4\u516b\u4e24\uff0c\u80d6\u548c\u5c1a\u53c8\u53eb \u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u6709\u8fd9\u51e0\u4e0b\u6563\u624b\uff0c\u6211\u5012\u662f\u770b\u9519\u4f60\u4e86\uff0c\u4f60\u786e\u4e0d\u662f\u5439\u725b\uff01\u201d\u80d6\u548c\u5c1a\u662f\u4e2a\u6d51\u4eba\uff0c\u8bf4\u8bdd \u8d63\u76f4\uff0c\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5012\u662f\u771f\u5fc3\u79f0\u8d5e\uff0c\u90ed\u6210\u6cf0\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u5c06\u70df\u6746\u821e\u5f97\u66f4\u6025\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u9633\u5b97\u6d77\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u771f\u7684\u662f\u656c\u9152\u4e0d\u5403\u8981\u5403\u7f5a\u9152\uff0c\u54c8\u54c8\uff0c\u90a3\u5c31\u6055\u6211\u4e0d\u518d\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u53ea\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u5468\u5c71\u6c11\u4eae\u51fa\u91d1\u5200\uff0c\u632f\u81c2\u4e00\u6325\uff0c\u91d1\u5200\u5728\u7a7a\u4e2d\u865a\u5288\u4e00\u5200\uff0c\u567c\u556a\u4f5c\u54cd\uff0c\u5927\u58f0 \u53eb\u9053\uff1a\u201c\u5144\u5f1f\u4eec\u62a2\u95e8\uff0c\u9633\u5927\u4eba\u8fd9\u676f\u7f5a\u9152\uff0c\u5144\u5f1f\u6765\u9886\u3002\u201d\u9633\u5b97\u6d77\u53ea\u662f\u76ef\u4f4f\u5468\u5c71\u6c11\uff0c\u5bf9\u5176\u4ed6\u4eba\u6beb \u4e0d\u7406\u4f1a\u3002\u4f46\u542c\u5f97\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u4ed6\u7684\u957f\u5251\u4ea6\u5df2\u51fa\u9798\uff0c\u5200\u5251\u76f8\u4ea4\uff0c\u5468\u5c71\u6c11\u7684\u5200\u91cd\uff0c\u9633\u5b97\u6d77\u7684\u5251 \u8f7b\uff0c\u5468\u5c71\u6c11\u7684\u91d1\u5200\u5374\u53cd\u800c\u7ed9\u4ed6\u78d5\u5f00\uff0c\u77f3\u7fe0\u51e4\u62d4\u51fa\u67f3\u53f6\u5200\uff0c\u9694\u7740\u4e00\u5f20\u684c\u5b50\uff0c\u4fbf\u662f\u4e00\u62db\u201c\u8774\u8776\u7a7f \u82b1\u201d\uff0c\u76d8\u65cb\u98de\u821e\u5730\u9694\u7740\u684c\u5b50\u65ab\u6765\uff0c\u5468\u5c71\u6c11\u4e00\u6539\u5200\u52bf\uff0c\u4ece\u659c\u62b9\u53d8\u4e3a\u76f4\u5288\uff0c\u8fd9\u4e24\u62db\u5168\u662f\u62fc\u547d\u7684\u62db \u6570\u3002 \u3000\u3000\u9633\u5b97\u6d77\u51b7\u7b11\u9053\uff1a\u201c\u8d24\u4f09\u4fea\u540c\u6765\uff0c\u90a3\u771f\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u9633\u67d0\u63a5\u9a7e\u4e86\uff01\u201d\u817e\u5730\u98de\u8d77\u4e00\u811a\uff0c\u5c06\u684c \u5b50\u8e22\u98de\uff0c\u6321\u4e86\u77f3\u7fe0\u51e4\u7684\u5200\uff0c\u5530\u5730\u4e00\u5251\uff0c\u523a\u5411\u5468\u5c71\u6c11\u5c0f\u8179\uff0c\u8fd9\u4e00\u5251\u6765\u5f97\u8f7b\u7075\u8fc5\u6377\uff0c\u5468\u5c71\u6c11\u5200\u5df2 \u5288\u51fa\uff0c\u56de\u62db\u4e0d\u53ca\uff0c\u767e\u5fd9\u4e2d\u6293\u8d77\u4e00\u4e2a\u76db\u6c64\u7684\u94dc\u9f0e\u4e00\u6321\uff0c\u6c64\u6c34\u6cfc\u4e86\u6ee1\u5730\uff0c\u94dc\u9f0e\u88ab\u9633\u5b97\u6d77\u4e00\u5251\u524a\u4e3a \u4e24\u534a\u3002\u77f3\u7fe0\u51e4\u6551\u592b\u5fc3\u5207\uff0c\u6c64\u6c34\u6e85\u6c61\u4e86\u5979\u534a\u5e45\u7f57\u88d9\uff0c\u5979\u4e1d\u6beb\u4e0d\u89c9\u5f97\uff0c\u8df3\u8fc7\u4e00\u5f20\u684c\u5b50\uff0c\u6325\u5200\u4fbf \u65ab\uff0c\u201c\u94ee\u201d\u201c\u94ee\u201d\u4e24\u58f0\uff0c\u9633\u5b97\u6d77\u4e00\u62db\u201c\u53cc\u9f99\u51fa\u6d77\u201d\uff0c\u5251\u52bf\u5de6\u53f3\u4e00\u5206\uff0c\u5c06\u4e24\u53e3\u5200\u90fd\u540c\u65f6\u67b6\u5f00 \u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u4f17\u4eba\u90fd\u5df2\u62a2\u5230\u95e8\u53e3\uff0c\u5a04\u6850\u5b59\u5173\u4e0a\u4e86\u5927\u95e8\uff0c\u5f53\u95e8\u4e00\u7ad9\u3002\u6709\u4e24\u4e2a\u5c11\u5e74\u597d\u6c49\uff0c\u5404\u821e\u4e00\u67c4\u94c1 \u9524\uff0c\u4fbf\u51b2\u4e0a\u53bb\u7838\u95e8\uff0c\u5a04\u6850\u5b59\u201c\u563f\u563f\u201d\u51b7\u7b11\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u90fd\u7ed9\u6211\u8eba\u4e0b\uff01\u201d\u4ed6\u624b\u6cd5\u5feb\u75be\u4e4b\u6781\uff0c \u4f17\u4eba\u8fde\u770b\u90fd\u672a\u770b\u5f97\u6e05\u695a\uff0c\u90a3\u4e24\u4e2a\u5c11\u5e74\u597d\u6c49\u7684\u94c1\u9524\u5df2\u662f\u8131\u624b\u98de\u51fa\uff0c\u5389\u53eb\u4e00\u58f0\uff0c\u53cc\u53cc\u5012\u5728\u5730\u4e0a\uff0c \u539f\u6765\u90fd\u88ab\u5a04\u6850\u5b59\u7528\u5206\u7b4b\u9519\u9aa8\u624b\u6cd5\u626d\u65ad\u4e86\u624b\u81c0\uff01\u4f17\u4eba\u5927\u60ca\uff0c\u62a2\u4e0a\u6765\u6551\uff0c\u5a04\u6850\u5b59\u51fa\u624b\u5982\u98ce\u2014\u2014\u5de6 \u4e00\u6293\u53f3\u4e00\u6293\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u53c8\u6709\u51e0\u4eba\u7ed9\u626d\u4f24\u7b4b\u9aa8\uff0c\u8fd9\u201c\u5206\u7b4b\u9519\u9aa8\u624b\u201d\u5728\u6df7\u6218\u4e4b\u65f6\uff0c\u6700\u89c1\u5389\u5bb3\uff0c \u4e00\u8fd1\u8eab\u4fbf\u5f97\u53d7\u4f24\u3002\u4f17\u4eba\u4e0d\u6562\u8fd1\u8eab\u8089\u640f\uff0c\u53ea\u597d\u7528\u957f\u5175\u5668\u8feb\u7740\u5a04\u6850\u5b59\uff0c\u5a04\u6850\u5b59\u4e5f\u4e0d\u8fdb\u51fb\uff0c\u53ea\u662f\u7d27 \u7d27\u5730\u628a\u5b88\u95e8\u53e3\uff0c\u5f62\u6210\u4e86\u76f8\u6301\u4e4b\u5c40\u3002 \u3000\u3000\u8fd9\u65f6\u5ba2\u5e97\u4e4b\u4e2d\uff0c\u684c\u5012\u6905\u7ffb\uff0c\u53d8\u6210\u4e86\u4e09\u5904\u7684\u6df7\u6218\u53ae\u6740\u4e4b\u5c40\uff0c\u80d6\u548c\u5c1a\u4e0e\u90ed\u6210\u6cf0\u6253\u5f97\u96be\u5206\u96be \u89e3\uff0c\u5a04\u6850\u5b59\u6321\u7740\u4e86\u62a2\u95e8\u8bf8\u4eba\uff0c\u5468\u5c71\u6c11\u592b\u5987\u529b\u654c\u9633\u5b97\u6d77\uff0c\u90a3\u77ed\u5c0f\u7cbe\u608d\u7684\u6c49\u5b50\u65e9\u5df2\u722c\u4e86\u8d77\u6765\uff0c\u88f9 \u597d\u4e86\u819d\u76d6\uff0c\u7ad9\u5728\u5a04\u6850\u5b59\u7684\u65c1\u8fb9\uff0c\u62c9\u5f00\u4e00\u5f20\u5f39\u5f13\uff0c\u50fb\u50fb\u556a\u556a\u5730\u4e71\u6253\uff0c\u4ed6\u7684\u5f39\u5b50\u8fd8\u6253\u5f97\u771f\u72e0\uff0c\u6709 \u51e0\u4e2a\u60f3\u8f6c\u8eab\u5e2e\u52a9\u5468\u5c71\u6c11\u7684\uff0c\u90fd\u7ed9\u4ed6\u7684\u5f39\u5b50\u6253\u9000\u3002 \u3000\u3000\u8fd9\u4e09\u5904\u6df7\u6218\uff0c\u5468\u5c71\u6c11\u592b\u5987\u5904\u5883\u6700\u5371\uff0c\u9633\u5b97\u6d77\u7684\u957f\u5251\u970d\u970d\u5c55\u5f00\uff0c\u5251\u52bf\u98d8\u5ffd\u4e4b\u6781\uff0c\u4f3c\u5de6\u53cd \u53f3\uff0c\u5ffd\u5b9e\u5ffd\u865a\uff0c\u6bcf\u4e00\u62db\u90fd\u4f3c\u540c\u65f6\u653b\u51fb\u4e24\u4eba\uff0c\u5468\u5c71\u6c11\u592b\u5987\u90fd\u4ec5\u80fd\u81ea\u4fdd\uff0c\u53cc\u5200\u65e0\u6cd5\u914d\u5408\uff0c\u867d\u7136\u5e76 \u80a9\u62b5\u654c\uff0c\u5374\u4f3c\u5404\u81ea\u4e3a\u6218\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u5ffd\u542c\u5f97\u201c\u548b\u5693\u201d\u4e00\u58f0\uff0c\u5468\u5c71\u6c11\u7684\u91d1\u5200\u5df2\u88ab\u9633\u5b97\u6d77\u7684\u957f\u5251\u201c\u622a\u201d\u4e86\u4e00\u4e2a\u7f3a\u53e3\uff0c\u9633\u5b97 \u6d77\u7528\u7684\u5e76\u4e0d\u662f\u5b9d\u5251\uff0c\u51ed\u7740\u5185\u5bb6\u771f\u529b\uff0c\u5c45\u7136\u80fd\u628a\u5468\u5c71\u6c11\u7684\u539a\u80cc\u91d1\u5200\u5f04\u5f97\u4f24\u635f\uff0c\u786e\u662f\u58f0\u5a01\u593a\u4eba\uff0c \u5468\u5c71\u6c11\u3001\u77f3\u7fe0\u51e4\u90fd\u4e0d\u7981\u5fc3\u4e2d\u4e00\u51db\uff0c\u9633\u5b97\u6d77\u5f97\u7406\u4e0d\u9976\u4eba\uff0c\u5530\u5530\u4e24\u5251\uff0c\u5de6\u523a\u5468\u5c71\u6c11\u7684\u201c\u9633\u767d \u7a74\u201d\uff0c\u53f3\u523a\u77f3\u7fe0\u51e4\u7684\u201c\u4e73\u7a81\u7a74\u201d\uff0c\u5251\u52bf\u8fde\u7ef5\u4e0d\u65ad\uff0c\u987f\u65f6\u628a\u4e24\u592b\u5987\u903c\u5f97\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u53ee\u53ee\u6570\u58f0\uff0c\u4e8e\u627f\u73e0\u7a81\u7136\u8d8a\u4f17\u800c\u51fa\uff0c\u626c\u624b\u4e09\u6735\u91d1\u82b1\uff0c\u5c06\u90a3\u77ed\u5c0f\u7cbe\u608d\u7684\u6c49\u5b50\u6240\u53d1\u7684\u5f39 \u5b50\u5168\u6253\u843d\uff0c\u9752\u51a5\u5b9d\u5251\u5bd2\u5149\u4e00\u95ea\uff0c\u76f4\u53d6\u9633\u5b97\u6d77\uff0c\u9633\u5b97\u6d77\u53cd\u5251\u4e00\u8fce\uff0c\u60f3\u7c98\u4f4f\u4e8e\u627f\u73e0\u7684\u5b9d\u5251\uff0c\u5468\u5c71 \u6c11\u592b\u5987\u53cc\u5200\u9f50\u4e0b\uff0c\u4e8e\u627f\u73e0\u8d81\u52bf\u4e00\u4e2a\u56de\u5251\u53cd\u524a\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u9633\u5b97\u6d77\u7684\u8863\u8896\u524a\u53bb\u534a\u622a\u3002\u4e8e \u627f\u73e0\u8fd9\u4e24\u5251\u662f\u201c\u7384\u673a\u5251\u6cd5\u201d\u4e2d\u7684\u7a81\u88ad\u5947\u62db\uff0c\u53c8\u5f97\u5468\u5c71\u6c11\u592b\u5987\u53cc\u5200\u52a9\u653b\uff0c\u7adf\u7136\u88ab\u9633\u5b97\u6d77\u95ea\u5f00\uff0c \u5fc3\u4e2d\u6697\u6697\u6212\u60e7\u3002 \u3000\u3000\u9633\u5b97\u6d77\u4e5f\u662f\u5403\u60ca\u975e\u5c0f\uff0c\u987b\u77e5\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u628a\u4ed6\u4e0e\u5f20\u4e39\u67ab\u540c\u5217\u4e3a\u56db\u5927\u5251\u5ba2\u4e4b\u4e00\uff0c\u5982\u4eca\u5f20\u4e39\u67ab \u7684\u5f92\u5f1f\u5c45\u7136\u80fd\u524a\u65ad\u4ed6\u7684\u8863\u8896\uff0c\u9633\u5b97\u6d77\u81ea\u662f\u9762\u4e0a\u65e0\u5149\u3002\u4f46\u9633\u5b97\u6d77\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u867d\u7136\u6124\u6012\uff0c\u5374\u662f\u6c89 \u7740\u4e86\u6c14\uff0c\u4e0d\u53d6\u5f3a\u653b\uff0c\u6bcf\u4e00\u62db\u62db\u6570\u90fd\u4e0d\u7528\u8001\uff0c\u4e13\u95e8\u5bfb\u6687\u62b5\u9699\uff0c\u54ea\u4e00\u65b9\u9732\u51fa\u5f31\u70b9\uff0c\u5251\u52bf\u5c31\u5411\u54ea\u4e00 \u65b9\u65bd\u5c55\u3002\u4e8e\u627f\u73e0\u7b49\u4e09\u4eba\u8054\u624b\uff0c\u867d\u662f\u7a0d\u5360\u4e0a\u98ce\uff0c\u9633\u5b97\u6d77\u5374\u4e5f\u662f\u6709\u653b\u6709\u5b88\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5c45\u4e2d\u62b5\u6321\uff0c\u5c06\u9633\u5b97\u6d77\u7684\u653b\u52bf\u4e00\u4e00\u63a5\u53bb\uff0c\u77f3\u7fe0\u51e4\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u662f\u5f20\u4e39\u67ab\u7684\u4ec0 \u4e48\u4eba\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u662f\u6211\u5e08\u7236\u3002\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u4f60\u5e08\u6bcd\u597d\u5417\uff1f\u201d\u77f3\u7fe0\u51e4\u4e0e\u4e91\u857e\u66fe\u505a\u8fc7\u201c\u5047 \u51e4\u865a\u51f0\u201d\uff08\u4e8b\u89c1\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\uff09\uff0c\u4ea4\u60c5\u6700\u597d\uff0c\u4e00\u89c1\u4e8e\u627f\u73e0\u7684\u5251\u6cd5\uff0c\u559c\u4e0d\u81ea\u80dc\uff0c\u8feb\u4e0d\u53ca\u5f85\u5c31 \u8d76\u5fd9\u6253\u542c\u3002\u4e00\u5206\u795e\u51e0\u4e4e\u4e2d\u4e86\u9633\u5b97\u6d77\u4e00\u5251\uff0c\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5979\u4eec\u90fd\u60e6\u5ff5\u4f60\u5462\u3002\u55ef\uff0c\u628a\u8fd9\u53ae \u6740\u4e86\uff0c\u6211\u518d\u5c06\u8be6\u60c5\u544a\u4f60\u3002\u201d \u3000\u3000\u9633\u5b97\u6d77\u6c89\u7740\u4e86\u6c14\uff0c\u53c8\u6597\u4e86\u5341\u4f59\u4e8c\u5341\u62db\uff0c\u5ffd\u5730\u957f\u5578\u4e00\u58f0\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u60f3\u6740\u6211\uff0c\u83ab\u505a \u68a6\u5566\uff01\u6211\u5df2\u5b89\u6392\u4e86\u4e94\u767e\u540d\u5f13\u7bad\u624b\u5c06\u4f60\u4eec\u56f4\u7740\u5566\uff0c\u8981\u547d\u7684\u5feb\u653e\u4e0b\u5175\u5200\uff0c\u4e00\u4e2a\u4e2a\u90fd\u968f\u6211\u8fdb\u5bab \u53bb\uff01\u201d\u4e8e\u627f\u73e0\u5c4f\u795e\u4e00\u542c\uff0c\u5916\u9762\u679c\u7136\u4f20\u6765\u4e86\u7eb7\u4e71\u7684\u9a6c\u8e44\u58f0\u3002 \u3000\u3000\u90a3\u80d6\u548c\u5c1a\u4e0e\u90ed\u6210\u6cf0\u6b63\u6253\u5f97\u96be\u5206\u96be\u89e3\uff0c\u5ffd\u95fb\u5f20\u4e39\u67ab\u4e4b\u540d\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u819d\u76d6\u7ed9\u90ed\u6210\u6cf0\u7684\u70df\u6597 \u6572\u4e86\u4e00\u8bb0\uff0c\u75bc\u5f97\u8df3\u4e86\u8d77\u6765\uff0c\u5374\u51b2\u7740\u9633\u5b97\u6d77\u56b7\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fd9\u6837\u5b50\u7684\u8bf7\u5ba2\u6cd5\uff1f\u201d\u9633\u5b97\u6d77\u7b11\u9053\uff1a \u201c\u4e86\u7f18\u5927\u5e08\uff0c\u4f60\u4e0d\u5fc5\u591a\u7ba1\uff0c\u4f60\u53ea\u8981\u7f20\u7740\u90a3\u7cdf\u8001\u5934\u5b50\uff0c\u628a\u7d27\u5927\u95e8\uff0c\u4fbf\u662f\u4e00\u529f\uff01\u201d \u3000\u3000\u80d6\u548c\u5c1a\u9732\u51fa\u60f6\u60d1\u4e4b\u8272\uff0c\u53ea\u542c\u5f97\u5916\u9762\u7684\u9a6c\u8e44\u58f0\uff0c\u5df2\u662f\u8d8a\u6765\u8d8a\u8fd1\u4e0a\u3002 \u3000\u3000\u5468\u5c71\u6c11\u91d1\u5200\u4e00\u6446\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u53bb\u62a2\u95e8\uff01\u201d\u4f17\u4eba\u88ab\u56f4\u5728\u5ba2\u5e97\u4e4b\u4e2d\uff0c\u82e5\u7136\u4e94\u767e\u5f13\u7bad \u624b\u5f00\u5230\uff0c\u4e71\u7bad\u5c04\u4eba\uff0c\u90a3\u540e\u679c\u53ef\u662f\u4e0d\u582a\u60f3\u8c61\u3002\u4e8e\u627f\u73e0\u60f3\u5230\u65f6\u673a\u9669\u6025\uff0c\u64a4\u5251\u4fbf\u8d70\uff0c\u54ea\u77e5\u9633\u5b97\u6d77\u7a81 \u7136\u53cd\u5b88\u4e3a\u653b\uff0c\u5530\u5530\u4e24\u5251\uff0c\u53c8\u5411\u5468\u5c71\u6c11\u4e0e\u77f3\u7fe0\u51e4\u8fde\u65bd\u6740\u624b\uff0c\u4e8e\u627f\u73e0\u903c\u5f97\u56de\u5251\u63a5\u4ed6\u653b\u52bf\uff0c\u6321\u4e86\u51e0 \u62db\uff0c\u4e8e\u627f\u73e0\u53eb\u9053\uff1a\u201c\u6211\u6765\u65ad\u540e\uff01\u201d\u5468\u5c71\u6c11\u592b\u5987\u53cc\u5200\u786c\u5f80\u5916\u51b2\u3002\u9633\u5b97\u6d77\u6b66\u529b\u867d\u9ad8\uff0c\u5728\u4e09\u4e94\u5341\u62db \u4e4b\u5185\uff0c\u5374\u4e5f\u4e0d\u80fd\u5c06\u4e8e\u627f\u73e0\u6740\u8d25\uff0c\u5468\u5c71\u6c11\u592b\u5987\u500f\u5ffd\u4e4b\u95f4\u5954\u5230\u5927\u95e8\uff0c\u90a3\u628a\u95e8\u7684\u6c49\u5b50\u53eb\u9053\uff1a\u201c\u4e86\u7f18 \u5927\u5e08\uff0c\u4ed6\u4eec\u60f3\u7fa4\u6bb4\u5475\uff0c\u4f60\u4e0d\u8981\u6015\uff0c\u6211\u7528\u5f39\u5f13\u52a9\u4f60\u3002\u201d\u90a3\u80d6\u548c\u5c1a\u88ab\u90ed\u6210\u6cf0\u70df\u6597\u6572\u4e86\u4e00\u8bb0\uff0c\u6740\u5f97 \u6027\u8d77\uff0c\u5de6\u53f3\u5f00\u5f13\uff0c\u53cc\u62f3\u9f50\u51fa\uff0c\u5468\u5c71\u6c11\u592b\u5987\u89c1\u4ed6\u62f3\u52bf\u51f6\u731b\uff0c\u53cc\u5200\u4e00\u7acb\uff0c\u5c01\u7740\u95e8\u6237\uff0c\u90ed\u6210\u6cf0\u5c06\u70df \u6746\u5f53\u4f5c\u5c0f\u82b1\u67aa\u7528\uff0c\u4e00\u62db\u201c\u795e\u9f99\u5165\u6d77\u201d\uff0c\u523a\u80d6\u548c\u5c1a\u5c0f\u8179\u7684\u4e43\u4e08\u6c14\u7a74\u3002\u90a3\u80d6\u548c\u5c1a\u7684\u672c\u4e8b\u4e0e\u90ed\u5a01\u6cf0 \u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u52a0\u4e0a\u5468\u5c71\u6c11\u592b\u5987\uff0c\u80d6\u548c\u5c1a\u81ea\u662f\u96be\u4ee5\u62b5\u654c\uff0c\u770b\u770b\u8fd9\u4e00\u62db\u975e\u4e2d\u4e0d\u53ef\uff0c\u5ffd\u542c\u5f97\u201c\u547c\u201d \u7684\u4e00\u58f0\uff0c\u4e00\u6761\u4eba\u5f71\u4ece\u8bf8\u4eba\u5934\u4e0a\u63a0\u8fc7\uff0c\u968f\u624b\u4e00\u5e26\uff0c\u5c06\u90ed\u6210\u6cf0\u7684\u70df\u7ba1\u62e8\u8fc7\u4e00\u8fb9\uff0c\u628a\u80d6\u548c\u5c1a\u4e5f\u5e26\u5f97 \u9000\u540e\u51e0\u6b65\uff0c\u9760\u7740\u5927\u95e8\uff0c\u8fd9\u4eba\u6b63\u662f\u9633\u5b97\u6d77\uff0c\u4ed6\u6015\u4f17\u4eba\u653b\u7834\u5927\u95e8\uff0c\u7adf\u7136\u4f7f\u51fa\u9669\u62db\uff0c\u4e00\u5251\u538b\u4f4f\u4e86\u4e8e \u627f\u73e0\u7684\u653b\u52bf\uff0c\u968f\u5373\u98de\u8eab\u63a0\u8d77\uff0c\u62a2\u5148\u5230\u4e86\u95e8\u8fb9\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u9633\u5b97\u6d77\u3001\u5a04\u6850\u5b59\u3001\u80d6\u548c\u5c1a\u4e0e\u90a3\u6c49\u5b50\u56db\u4eba\u5b88\u7d27\u5927\u95e8\uff0c\u9633\u5b97\u6d77\u5251\u5149\u95ea\u95ea\uff0c\u4e13\u523a\u7a74 \u9053\uff0c\u5a04\u6850\u5b59\u66f4\u662f\u5389\u5bb3\uff0c\u4e00\u8fd1\u8eab\u4fbf\u88ab\u4ed6\u7528\u201c\u5206\u7b4b\u9519\u9aa8\u624b\u201d\u626d\u4f24\uff0c\u80d6\u548c\u5c1a\u6c14\u529b\u60ca\u4eba\uff0c\u7f57\u6c49\u795e\u62f3\u4f7f \u5f00\uff0c\u4e00\u4e08\u4e4b\u5185\uff0c\u8fd1\u8eab\u4e0d\u5f97\u3002\u90a3\u77ed\u5c0f\u7cbe\u608d\u7684\u6c49\u5b50\u6b66\u529f\u8f83\u5f31\uff0c\u4f46\u4ed6\u5f39\u5b50\u6253\u5f97\u5f88\u51c6\uff0c\u820d\u8fd1\u653b\u8fdc\uff0c\u5bf9 \u62a2\u95e8\u8bf8\u4eba\uff0c\u4e5f\u662f\u4e00\u4e2a\u5a01\u80c1\u3002\u5468\u5c71\u6c11\u8fd9\u8fb9\uff0c\u53ea\u6709\u4ed6\u4e24\u592b\u5987\u3001\u4e8e\u627f\u73e0\u3001\u90ed\u6210\u6cf0\u56db\u4eba\u53ef\u4ee5\u4e0e\u9ad8\u624b\u4e00 \u6218\uff0c\u5176\u4ed6\u8bf8\u4eba\uff0c\u4e0e\u9633\u5b97\u6d77\u3001\u5a04\u6850\u5b59\u5374\u662f\u76f8\u5dee\u592a\u8fdc\uff0c\u867d\u7136\u4ed7\u7740\u4eba\u591a\uff0c\u4e5f\u4e0d\u8fc7\u662f\u4e2a\u76f8\u6301\u4e4b\u5c40\uff0c\u60f3 \u5728\u8feb\u5207\u4e4b\u95f4\u653b\u7834\u5927\u95e8\uff0c\u90a3\u5374\u662f\u96be\u6781\uff01\u95e8\u5916\u7eb7\u4e71\u7684\u9a6c\u8e44\u58f0\u8d8a\u6765\u8d8a\u8fd1\uff0c\u542c\u6765\u5df2\u5230\u95e8\u524d\uff0c\u9633\u5b97\u6d77\u54c8 \u54c8\u5927\u7b11\u9053\uff1a\u201c\u91d1\u5200\u5be8\u4e3b\uff0c\u8ba4\u547d\u4e86\u5427\uff01\u8fd9\u4e00\u676f\u7f5a\u9152\uff0c\u4f60\u975e\u559d\u4e0d\u884c\u5566\uff01\u4e86\u7f18\u5927\u5e08\uff0c\u8fd9\u4e00\u62f3\u6539\u7528\u864e \u62f3\uff0c\u5148\u628a\u4ed6\u7684\u91d1\u5200\u6253\u6389\uff01\u201d\u5468\u5c71\u6c11\u6b63\u7528\u91d1\u5200\u6a2a\u5288\uff0c\u653b\u90a3\u80d6\u548c\u5c1a\uff0c\u9633\u5b97\u6d77\u60f3\u628a\u5468\u5c71\u6c11\u751f\u64d2\uff0c\u6545 \u6b64\u51fa\u8a00\u6307\u70b9\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u201c\u8f70\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u90a3\u80d6\u548c\u5c1a\u4e0d\u6253\u5468\u5c71\u6c11\uff0c\u5374\u5ffd\u7684\u8f6c\u8fc7\u8eab\u6765\uff0c\u5c06\u5927\u95e8\u4e00\u62f3\u6253\u5f00\uff0c \u8fd9\u4e00\u4e0b\u5f53\u771f\u662f\u53d8\u51fa\u610f\u5916\uff0c\u9633\u5b97\u6d77\u5403\u4e86\u4e00\u60ca\u53eb\u9053\uff1a\u201c\u4e86\u7f18\u5927\u5e08\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u5feb\u5feb\u5835\u4f4f\u654c\u4eba\uff01\u201d \u90a3\u6c49\u5b50\u4e5f\u53eb\u9053\uff1a\u201c\u4e86\u7f18\u5927\u5e08\uff0c\u4f60\u8ddf\u6211\u8bf4\u4ec0\u4e48\u6765\u7740\uff1f\u4f60\u8fd8\u60f3\u5728\u4eac\u5e08\u7acb\u8db3\u5417\uff1f\u201d\u90a3\u80d6\u548c\u5c1a\u6c14\u547c\u547c \u5730\u5927\u53eb\u9053\uff1a\u201c\u6d12\u5bb6\u624d\u4e0d\u660e\u767d\u4f60\u4eec\u8bb2\u4e9b\u4ec0\u4e48\uff1f\u8c01\u662f\u654c\u4eba\uff1f\u6211\u53ef\u4e0d\u8ba4\u91d1\u5200\u5be8\u4e3b\u4e3a\u654c\uff01\u201d\u9633\u5b97\u6d77\u53cc \u773c\u5706\u7741\uff0c\u66f4\u4e0d\u6253\u8bdd\uff0c\u4fef\u8eab\u4e00\u5251\uff0c\u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u864e\u201d\uff0c\u957f\u5251\u4fbf\u523a\u90a3\u80d6\u548c\u5c1a\u7684\u4e39\u7530\uff0c\u4e8e\u627f\u73e0\u67aa\u4e0a \u53bb\u4e00\u5251\u67b6\u5f00\uff0c\u5468\u5c71\u6c11\u53cd\u8d77\u5200\u80cc\u4e00\u62cd\uff0c\u5c06\u90a3\u4e2a\u4e0d\u77e5\u6240\u63aa\u7684\u628a\u95e8\u6c49\u5b50\u6253\u8dcc\uff0c\u90ed\u6210\u6cf0\u63a5\u7740\u6293\u8d77\u4ed6\u4e00 \u6454\uff0c\u4f17\u4eba\u7acb\u5373\u6d8c\u51fa\u95e8\u5916\u3002 \u3000\u3000\u90a3\u77ed\u5c0f\u7cbe\u608d\u7684\u6c49\u5b50\u6b66\u529f\u786e\u662f\u4e0d\u5f31\uff0c\u88ab\u6454\u51fa\u95e8\u5916\uff0c\u8dcc\u5230\u5b98\u5175\u4e1b\u4e4b\u4e2d\uff0c\u521a\u4e00\u6cbe\u5730\uff0c\u4fbf\u4e00\u4e2a \u201c\u9ca4\u9c7c\u6253\u633a\u201d\u8df3\u4e86\u8d77\u6765\uff0c\u968f\u624b\u683c\u5f00\u4e24\u6839\u957f\u77db\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec\u778e\u4e86\u773c\u775b\u5417\uff1f\u662f\u6211\uff01\u201d\u5f13\u7bad \u8425\u7684\u7edf\u9886\u5306\u5fd9\u53eb\u9053\uff1a\u201c\u662f\u891a\u5927\u4eba\uff0c\u4f11\u5f97\u653e\u7bad\uff01\u201d\u5176\u5b9e\u9644\u8fd1\u7684\u5b98\u5175\u5df2\u8ba4\u5f97\u662f\u4ed6\uff0c\u8981\u4e0d\u7136\u6570\u5341\u6839 \u957f\u77db\u9f50\u4e0b\uff0c\u4ed6\u518d\u6709\u672c\u9886\uff0c\u4e5f\u96be\u514d\u6210\u4e3a\u8089\u9171\u3002 \u3000\u3000\u7d27\u63a5\u7740\u90a3\u4f4d\u201c\u891a\u5927\u4eba\u201d\u4e4b\u540e\uff0c\u80d6\u548c\u5c1a\u65cb\u98ce\u822c\u5730\u6253\u51fa\u6765\uff0c\u5b98\u5175\u4e2d\u6709\u4eba\u8ba4\u5f97\u4ed6\u662f\u201c\u891a\u5927\u4eba\u201d \u7684\u597d\u53cb\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u4e86\u7f18\u5927\u5e08\uff0c\u90fd\u662f\u81ea\u5df1\u4eba\u3002\u201d\u90a3\u80d6\u548c\u5c1a\u4e1d\u6beb\u4e5f\u4e0d\u7406\u4f1a\u51fa\u5730\u6012\u543c\u4e00\u58f0\uff0c\u7830 \u7684\u4e00\u62f3\u5c31\u5c06\u4e00\u4e2a\u7ba1\u5e26\u6253\u5012\uff0c\u62a2\u4e86\u4ed6\u7684\u9a6c\u843d\u8352\u800c\u9003\uff0c\u5f13\u7bad\u8425\u7ba1\u5e26\u5927\u4e3a\u5403\u60ca\uff0c\u83ab\u77e5\u6240\u63aa\uff0c\u53ea\u542c\u5f97 \u90a3\u201c\u891a\u5927\u4eba\u201d\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u8d3c\u548c\u5c1a\u5403\u91cc\u6252\u5916\uff0c\u5feb\u653e\u7bad\u5440\uff01\u201d\u5c31\u5728\u8fd9\u4e2a\u7eb7\u4e71\u4e4b\u9645\uff0c\u90ed\u6210\u6cf0\u7b49 \u4e00\u5e72\u4eba\u5df2\u662f\u7eb7\u7eb7\u5730\u5411\u5916\u51b2\u6740\uff0c\u5b98\u5175\u4eec\u4e00\u9762\u62d2\u654c\uff0c\u4e00\u9762\u653e\u7bad\uff0c\u90a3\u80d6\u548c\u5c1a\u8131\u4e0b\u8888\u88df\uff0c\u5728\u9a6c\u80cc\u4e0a\u8fce \u98ce\u98de\u821e\uff0c\u8d5b\u5982\u4e00\u9762\u94c1\u724c\uff0c\u4ed6\u7684\u9a6c\u5df2\u8dd1\u51fa\u4e00\u91cc\u5f00\u5916\uff0c\u7eb5\u6709\u4e9b\u5f3a\u5f13\u786c\u7bad\u80fd\u591f\u5c04\u5230\uff0c\u4e5f\u90fd\u7ed9\u4ed6\u7684\u8888 \u88df\u62e8\u843d\u4e86\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4f4d\u4e86\u7f18\u548c\u5c1a\u4e43\u662f\u84b2\u7530\u5c11\u6797\u5bfa\u7684\u5f1f\u5b50\uff0c\u6027\u60c5\u61a8\u76f4\uff0c\u751a\u5f97\u4ed6\u5e08\u7236\u89c9\u6167\u7985\u5e08\u559c\u7231\uff0c\u89c9\u6167 \u6b7b\u540e\uff0c\u4ed6\u56e0\u5077\u5403\u72d7\u8089\uff0c\u559d\u9152\u80e1\u95f9\uff0c\u88ab\u638c\u5bfa\u7684\u5e08\u5144\u6240\u8d23\uff0c\u4ed6\u4e0d\u60ef\u62d8\u675f\uff0c\u96be\u5b88\u6e05\u89c4\uff0c\u4e00\u65f6\u6027\u8d77\uff0c \u7adf\u7136\u5077\u51fa\u5bfa\u95e8\uff0c\u79c1\u9003\u4e0b\u5c71\uff0c\u6253\u7b97\u8fd8\u4fd7\u3002\u4ed6\u6015\u5728\u5357\u65b9\u78b0\u5230\u540c\u95e8\uff0c\u4e0d\u597d\u610f\u601d\uff0c\u7d22\u6027\u4e00\u4e0d\u505a\uff0c\u4e8c\u4e0d \u4f11\uff0c\u8d8a\u9003\u8d8a\u8fdc\uff0c\u60f3\u8dd1\u5230\u5317\u4eac\u53bb\u770b\u4eac\u5e08\u7684\u7e41\u534e\u3002\u90a3\u77ed\u5c0f\u7cbe\u608d\u7684\u6c49\u5b50\u540d\u53eb\u891a\u7384\uff0c\u662f\u7687\u5e1d\u8ddf\u524d\u7684\u4e09 \u54c1\u5e26\u5200\u4f8d\u536b\uff0c\u968f\u9633\u5b97\u6d77\u3001\u5a04\u6850\u5b59\u4e8c\u4eba\u51fa\u5916\u529e\u6848\uff0c\u4ed6\u4eec\u4fa6\u77e5\u91d1\u5200\u5c11\u5be8\u4e3b\u5468\u5c71\u6c11\u5165\u5173\uff0c\u5206\u5934\u67e5 \u63a2\u3002\u891a\u7384\u548c\u4e86\u7f18\u548c\u5c1a\u662f\u65e7\u76f8\u8bc6\uff0c\u5728\u5c71\u4e1c\u9053\u4e0a\u76f8\u9047\uff0c\u891a\u7384\u542c\u8bf4\u4ed6\u79c1\u9003\u4e0b\u5c71\uff0c\u8981\u5230\u4eac\u57ce\u53bb\u6df7\uff0c\u5927 \u4e3a\u9ad8\u5174\u3002\u4e8e\u662f\u4e00\u53e3\u62c5\u627f\u7ed9\u4ed6\u5728\u4eac\u5e08\u627e\u4e2a\u9556\u5c40\u7684\u4f4d\u7f6e\uff0c\u4f34\u4ed6\u540c\u884c\u3002\u53c8\u6307\u4f7f\u4ed6\u5728\u5ba2\u5e97\u4e2d\u80e1\u95f9\uff0c\u7528 \u610f\u4fbf\u662f\u5f15\u5468\u5c71\u6c11\u592b\u5987\u51fa\u6765\u3002 \u3000\u3000\u54ea\u77e5\u4e86\u7f18\u6b64\u4eba\u867d\u7136\u7cca\u6d82\uff0c\u5927\u662f\u5927\u975e\u5374\u4e5f\u8fd8\u80fd\u5206\u8fa8\u3002\u4ed6\u751f\u5e73\u6700\u4f69\u670d\u7684\u662f\u4e24\u4e2a\u4eba\uff0c\u4e00\u4e2a\u662f\u5f20 \u4e39\u67ab\uff0c\u4e00\u4e2a\u4fbf\u662f\u5728\u96c1\u95e8\u5173\u5916\u6297\u62d2\u74e6\u523a\u7684\u91d1\u5200\u5be8\u4e3b\u5468\u6770\uff0c\u6240\u4ee5\u4e00\u542c\u4e8e\u627f\u73e0\u662f\u5f20\u4e39\u67ab\u7684\u5f92\u5f1f\uff0c\u5fc3 \u4e2d\u5df2\u662f\u8d77\u7591\uff0c\u518d\u542c\u5468\u5c71\u6c11\u7adf\u662f\u5468\u6770\u7684\u513f\u5b50\uff0c\u800c\u9633\u5b97\u6d77\u7b49\u4eba\u7adf\u8981\u6355\u4ed6\uff0c\u5fcd\u4e0d\u4f4f\u5fc3\u4e2d\u5927\u6012\uff0c\u75db\u6068 \u891a\u7384\u9a97\u4ed6\uff0c\u4e0d\u7531\u5206\u8bf4\u4fbf\u6253\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u518d\u8bf4\u5468\u5c71\u6c11\u592b\u5987\u4e8c\u4eba\uff0c\u672c\u6765\u4e5f\u968f\u7740\u90ed\u6210\u6cf0\u7b49\u4eba\u5916\u51b2\uff0c\u4f46\u9633\u5b97\u6d77\u3001\u5a04\u6850\u82cf\u8ba4\u5b9a\u4ed6\u662f\u4e3b\u72af\uff0c \u5b81\u53ef\u653e\u8d70\u5176\u4ed6\u4eba\u7b49\uff0c\u5374\u65ad\u65ad\u4e0d\u80fd\u653e\u8fc7\u4ed6\uff0c\u8fd9\u4e00\u6765\uff0c\u90ed\u6210\u6cf0\u5e08\u5f92\u51b2\u5230\u5916\u9762\uff0c\u4ed6\u4eec\u592b\u5987\u548c\u4e8e\u627f\u73e0 \u53cd\u800c\u88ab\u622a\u5728\u91cc\u8fb9\uff0c\u53ea\u9633\u5b97\u6d77\u4e00\u4eba\uff0c\u4ed6\u4eec\u4e09\u4eba\u5df2\u662f\u96be\u4ee5\u53d6\u80dc\uff0c\u66f4\u4f55\u51b5\u8fd8\u52a0\u4e0a\u4e00\u4e2a\u5a04\u6850\u5b59\uff0c\u66f4\u4f55 \u51b5\u5916\u56f4\u8fd8\u6709\u4e94\u767e\u5f13\u7bad\u624b\uff0c\u5f53\u771f\u662f\u9669\u8c61\u73af\u751f\u3002\u4e8e\u627f\u73e0\u5927\u4e3a\u7740\u6025\uff0c\u6025\u5fd9\u64ae\u5507\u547c\u5578\uff0c\u90a3\u5339\u7167\u591c\u72ee\u5b50 \u9a6c\u542c\u5f97\u4e3b\u4eba\u547c\u558a\uff0c\u4e0d\u7ba1\u5343\u519b\u4e07\u9a6c\uff0c\u7adf\u81ea\u76f4\u51b2\u5165\u6765\u3002 \u3000\u3000\u9633\u5b97\u6d77\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u5f97\u5230\u8fd9\u5339\u5b9d\u9a6c\uff0c\u90a3\u53ef\u6bd4\u4ec0\u4e48\u90fd\u5f3a\uff01\u201d\u6025\u5fd9\u626c\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\u51c6 \u4f24\u4e86\u8fd9\u5339\u767d\u9a6c\uff0c\u8981\u6d3b\u6349\u5b83\uff01\u201d\u7167\u591c\u72ee\u5b50\u9a6c\u795e\u9a8f\u975e\u5e38\uff0c\u4e00\u58f0\u957f\u5578\uff0c\u56db\u8e44\u98de\u8d77\uff0c\u628a\u51e0\u540d\u8981\u6349\u5b83\u7684 \u5175\u58eb\u8e22\u5f97\u7ffb\u5012\u5730\u4e0a\uff0c\u5c41\u6eda\u5c3f\u6d41\uff0c\u5a04\u6850\u5b59\u558a\u4e86\u4e00\u58f0\uff0c\u53cc\u773c\u653e\u5149\uff0c\u820d\u4e86\u4e8e\u627f\u73e0\u7acb\u523b\u5954\u5411\u90a3\u5339\u767d \u9a6c\u3002 \u3000\u3000\u90a3\u5339\u9a6c\u5728\u5b98\u519b\u9635\u4e2d\u641e\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u4e0d\u51c6\u4f24\u5b83\uff0c\u800c\u8981\u6d3b\u6349\uff0c\u90a3\u5bf9\u6bd4\u6349\u4e00\u53ea\u8001\u864e\u8fd8\u8981\u96be\u529e\uff0c \u5b98\u519b\u7167\u5e94\u4e0d\u6687\uff0c\u90ed\u6210\u6cf0\u5e08\u5f92\u4e0e\u4e00\u4f17\u597d\u6c49\u90fd\u5df2\u51b2\u5230\u5916\u8fb9\uff0c\u8131\u51fa\u9669\u5883\u3002\u5b5f\u957f\u751f\u5ffd\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60 \u4fdd\u4f17\u4eba\u8131\u9669\uff0c\u6211\u56de\u53bb\u63a5\u5e94\u3002\u201d\u4ed6\u8eab\u8eaf\u77ee\u80d6\uff0c\u6251\u5728\u5730\u4e0a\u4e00\u6eda\uff0c\u5374\u662f\u5341\u5206\u7075\u6d3b\uff0c\u5b98\u519b\u4eec\u51e0\u66fe\u89c1\u8fc7 \u8fd9\u6837\u6218\u6cd5\uff1f\u4e00\u8fd1\u8eab\u5c31\u88ab\u4ed6\u7684\u6eda\u5730\u5802\u5200\u780d\u4f24\u811a\u9aa8\uff0c\u7adf\u7136\u62e6\u4ed6\u4e0d\u4f4f\u3002 \u3000\u3000\u5a04\u6850\u5b59\u8dd1\u8fd1\u767d\u9a6c\uff0c\u5fc3\u4e2d\u76f4\u4e50\uff0c\u6b63\u60f3\u7528\u201c\u5206\u7b4b\u9519\u9aa8\u624b\u201d\u626d\u4f24\u9a6c\u8db3\uff0c\u5c06\u5b83\u5236\u4f0f\uff0c\u5ffd\u89c1\u4e00\u4e2a\u8089 \u7403\u8d34\u5730\u6eda\u6765\uff0c\u5a04\u6850\u5b59\u7684\u5206\u7b4b\u9519\u9aa8\u624b\u626d\u4f24\u654c\u4eba\u7684\u56db\u80a2\u5173\u8282\uff0c\u5374\u4f38\u4e0d\u5230\u5730\u4e0a\uff0c\u5bf9\u4ed8\u5b5f\u957f\u751f\u8fd9\u6eda\u5730 \u5802\u5200\u53ef\u662f\u6beb\u65e0\u529e\u6cd5\uff0c\u6025\u5fd9\u95ea\u8eb2\uff0c\u907f\u8fc7\u4ed6\u7684\u5200\u950b\uff0c\u80eb\u9aa8\u5374\u7ed9\u4ed6\u7684\u5200\u80cc\u62cd\u4e86\u4e00\u4e0b\uff0c\u75db\u5f97\u54c7\u54c7\u5927 \u53eb\uff0c\u90a3\u5339\u767d\u9a6c\u5411\u524d\u4e00\u51b2\uff0c\u53c8\u8e22\u5012\u4e24\u540d\u519b\u58eb\uff0c\u6cfc\u5587\u5587\u5730\u76f4\u5954\u5411\u4e3b\u4eba\u3002 \u3000\u3000\u5a04\u6850\u5b59\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u4ed6\u867d\u7136\u4e0d\u61c2\u6eda\u5730\u5802\u5200\uff0c\u6b66\u529f\u5374\u8981\u6bd4\u5b5f\u957f\u751f\u9ad8\u51fa\u6570\u500d\uff0c\u4e00\u4e2a\u201c\u76d8\u9f99 \u7ed5\u6b65\u201d\uff0c\u7528\u5185\u516b\u5708\u7684\u516b\u5366\u6b65\u6cd5\u968f\u7740\u5b5f\u957f\u751f\u8f6c\u4e86\u4e24\u8f6c\uff0c\u5b5f\u957f\u751f\u780d\u4ed6\u4e0d\u7740\uff0c\u88ab\u4ed6\u817e\u5730\u98de\u8d77\u4e00\u811a\uff0c \u8e22\u51fa\u4e24\u4e08\u5f00\u5916\uff0c\u4e0d\u80fd\u52a8\u5f39\uff0c\u7acb\u65f6\u7ed9\u5b98\u5175\u6346\u7f1a\u4e86\u3002\u5a04\u6850\u5b59\u4e00\u8f6c\u8eab\u53c8\u8ffd\u767d\u9a6c\u3002 \u3000\u3000\u9633\u5b97\u6d77\u4e00\u53e3\u5251\u6321\u4f4f\u4e86\u4e8e\u627f\u73e0\u7b49\u4e09\u4eba\uff0c\u89c1\u5a04\u6850\u5b59\u8ffd\u8d76\u767d\u9a6c\uff0c\u5fc3\u4e2d\u4e03\u4e0a\u516b\u843d\uff0c\u987b\u77e5\u50cf\u201c\u7167\u591c \u72ee\u5b50\u201d\u8fd9\u6837\u795e\u9a8f\u7684\u4e00\u5339\u5b9d\u9a6c\uff0c\u5728\u6b66\u571f\u4eec\u7684\u5fc3\u76ee\u4e2d\u90a3\u53ef\u6bd4\u8fde\u57ce\u4e4b\u58c1\u8fd8\u66f4\u73cd\u8d35\uff0c\u9633\u5b97\u6d77\u751f\u6015\u5a04\u6850 \u5b59\u5f97\u53bb\uff0c\u5fc3\u4e2d\u76d8\u7b97\u9053\uff1a\u201c\u6211\u5148\u64d2\u4e86\u8fd9\u5339\u767d\u9a6c\uff0c\u518d\u6349\u5468\u5c71\u6c11\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d\u4e8e\u627f\u73e0\u4f3c\u662f\u731c\u5230\u4e86\u4ed6 \u7684\u5fc3\u610f\uff0c\u4e58\u4ed6\u5251\u52bf\u4e00\u7f13\uff0c\u5ffd\u5730\u98de\u8eab\u63a0\u8d77\uff0c\u4e00\u56de\u9996\u5c31\u662f\u4e09\u6735\u91d1\u82b1\uff0c\u9633\u5b97\u6d77\u4e3e\u5251\u62e8\u843d\uff0c\u62e8\u8db3\u4fbf \u8ffd\uff0c\u53ea\u8fd9\u6837\u5730\u7f13\u4e86\u4e00\u7f13\uff0c\u767d\u9a6c\u5df2\u8dd1\u5230\u4e8e\u627f\u73e0\u8ddf\u524d\uff0c\u4e8e\u627f\u73e0\u98de\u8eab\u4e0a\u9a6c\uff0c\u65c1\u8fb9\u4e00\u4e2a\u519b\u5b98\u633a\u77db\u4e0a \u523a\uff0c\u88ab\u5979\u4e00\u5251\u524a\u65ad\u624b\u81c2\uff0c\u987a\u624b\u593a\u8fc7\u4e86\u4e00\u6839\u957f\u77db\u3002 \u3000\u3000\u5468\u5c71\u6c11\u592b\u5987\u53cc\u5200\u6025\u5f80\u5916\u51b2\uff0c\u4e8e\u627f\u73e0\u5927\u53eb\u9053\uff1a\u201c\u5411\u8fd9\u8fb9\u6765\uff01\u201d\u62e8\u8f6c\u9a6c\u5934\uff0c\u659c\u523a\u8fce\u4e0a\u3002\u5a04\u6850 \u5b59\u8ddd\u79bb\u5f97\u8fd1\uff0c\u6025\u5fd9\u62a2\u51fa\u62e6\u622a\u3002\u9633\u5b97\u6d77\u53eb\u9053\uff1a\u201c\u5a04\u5144\uff0c\u5148\u6349\u94a6\u72af\uff01\u201d\u5468\u5c71\u6c11\u8fd9\u65f6\u5982\u731b\u864e\u51fa\u7b3c\uff0c \u91d1\u5200\u5de6\u780d\u53f3\u5288\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u8fde\u65a9\u4e86\u5341\u591a\u540d\u519b\u5352\uff0c\u770b\u770b\u5c31\u8981\u4e0e\u4e8e\u627f\u73e0\u4f1a\u5408\uff0c\u5a04\u6850\u5b59\u5fc3\u4e2d\u867d\u7136\u7231 \u715e\u90a3\u5339\u767d\u9a6c\uff0c\u53ef\u662f\u9633\u5b97\u6d77\u7684\u8bf4\u8bdd\uff0c\u4ed6\u5374\u4e5f\u4e0d\u6562\u4e0d\u542c\u3002\u9633\u5b97\u6d77\u7684\u804c\u4f4d\u867d\u7136\u548c\u4ed6\u5c5e\u4e8e\u5e73\u8f88\uff0c\u4f46\u9633 \u5b97\u6d77\u5047\u516c\u6d4e\u79c1\uff0c\u8981\u4ed6\u5c31\u8fd1\u5148\u64d2\u201c\u94a6\u72af\u201d\uff0c\u8fd9\u5374\u662f\u4e07\u4e07\u8fdd\u6297\u4e0d\u5f97\u3002 \u3000\u3000\u5a04\u6850\u5b59\u53ea\u597d\u53cd\u8eab\u4e00\u8dc3\uff0c\u53cc\u638c\u5212\u4e86\u4e00\u4e2a\u5706\u5f27\uff0c\u5de6\u51fb\u5468\u5c71\u6c11\uff0c\u53f3\u51fb\u77f3\u7fe0\u51e4\uff0c\u5468\u3001\u77f3\u4e8c\u4eba\u90fd\u7ed9 \u4ed6\u903c\u9000\u51e0\u6b65\uff0c\u5468\u5c71\u6c11\u91d1\u5200\u4e00\u62db\u201c\u987a\u624b\u63a8\u821f\u201d\uff0c\u81ea\u5de6\u5411\u53f3\u6a2a\u524a\uff0c\u8fd9\u4e00\u5200\u4e00\u9762\u5c01\u95ed\u7740\u81ea\u5df1\u80f8\u524d\u95e8 \u6237\uff0c\u4e00\u9762\u780d\u654c\u4eba\u5288\u8fdb\u6765\u7684\u53cc\u638c\uff0c\u786e\u53ef\u7b97\u5f97\u662f\u4e00\u62db\u653b\u5b88\u517c\u5907\u7684\u597d\u62db\uff0c\u54ea\u77e5\u5a04\u6850\u5b59\u7684\u201c\u5206\u7b4b\u9519\u9aa8 \u624b\u201d\u7684\u786e\u662f\u51fa\u795e\u5165\u5316\uff0c\u53d8\u5316\u83ab\u6d4b\uff0c\u4ed6\u672c\u6765\u53cc\u638c\u9f50\u51fa\u90fd\u662f\u653b\u5411\u5468\u5c71\u6c11\u7684\uff0c\u638c\u5230\u534a\u9014\uff0c\u5374\u5ffd\u5730\u5de6 \u638c\u5728\u53f3\u638c\u4e4b\u80cc\u4e00\u62cd\uff0c\u53cd\u624b\u4e00\u6325\uff0c\u659c\u51fb\u77f3\u7fe0\u51e4\u7684\u9888\u9879\uff0c\u8fd9\u4e00\u638c\u53ea\u8981\u7ed9\u4ed6\u524a\u5b9e\uff0c\u77f3\u7fe0\u51e4\u53ef\u5c31\u5f97\u53d8 \u6210\u4e2a\u201c\u6b6a\u5934\u7f8e\u4eba\u201d\uff0c\u5468\u5c71\u6c11\u6551\u59bb\u60c5\u6025\uff0c\u91d1\u5200\u4e00\u62d6\uff0c\u8f6c\u8fc7\u5200\u80cc\uff0c\u6025\u5fd9\u62cd\u51fa\uff0c\u54ea\u77e5\u5a04\u6850\u5b59\u865a\u865a\u5b9e \u5b9e\uff0c\u4ed6\u662f\u4f6f\u653b\u77f3\u7fe0\u51e4\uff0c\u5b9e\u9645\u5374\u6b63\u662f\u8981\u8bf1\u5468\u5c71\u6c11\u4e0a\u5f53\uff0c\u5468\u5c71\u6c11\u8fd9\u4e00\u53d8\u62db\uff0c\u7acb\u523b\u9732\u51fa\u7834\u7efd\uff0c\u53ea\u89c1 \u5a04\u6850\u5b59\u5de6\u624b\u4e00\u6309\uff0c\u4e94\u6307\u4e00\u5212\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5468\u5c71\u6c11\u7684\u8863\u88f3\u88c2\u6210\u51e0\u7247\uff0c\u80f8\u53e3\u9732\u51fa\u4e86\u4e94\u6839\u6307 \u5370\u3002\u5468\u5c71\u6c11\u8dc4\u8dc4\u8e09\u8e09\u5730\u5012\u9000\u6570\u6b65\uff0c\u77f3\u7fe0\u51e4\u62a2\u6551\u4e0d\u53ca\uff0c\u8138\u8272\u9752\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u95f4\u6070\u597d\u4e00\u4e2a\u7edf\u5e26\u62bc\u7740\u4e00\u5c0f\u961f\u4eba\u8fc7\u6765\uff0c\u6b63\u662f\u9152\u5e97\u4e2d\u7684\u51e0\u4e2a\u5e97\u5c0f\u4e8c\u548c\u638c\u67dc\uff0c\u90a3\u4e2a\u7edf\u5e26\u4e00 \u70b9\u4e5f\u4e0d\u77e5\u9053\u8fd9\u4e2a\u201c\u8870\u8001\u201d\u7684\u638c\u67dc\u8eab\u6000\u7edd\u6280\uff0c\u53ea\u662f\u5faa\u4f8b\u5730\u6309\u7167\u529e\u6848\u7684\u89c4\u77e9\u5c06\u9152\u5e97\u4e2d\u4eba\u90fd\u62bc\u51fa \u6765\uff0c\u51c6\u5907\u5e26\u5230\u8425\u90e8\u5ba1\u8baf\uff0c\u5bf9\u51e0\u4e2a\u7cbe\u58ee\u7684\u5e97\u5c0f\u4e8c\u8fd8\u52a0\u4e0a\u624b\u9563\uff0c\u5bf9\u90a3\u4e2a\u8001\u638c\u67dc\u5374\u56e0\u624b\u9563\u4e0d\u591f\u7528\uff0c \u8fde\u624b\u811a\u4e5f\u6ca1\u6709\u6346\u7f1a\u3002\u8fd9\u961f\u4eba\u79bb\u5f00\u5a04\u6850\u5b59\u4e0d\u8fc7\u5341\u6765\u6b65\u8fdc\uff0c\u5a04\u6850\u5b59\u6b63\u8981\u8d76\u4e0a\u5468\u5c71\u6c11\u518d\u5288\u4e00\u638c\uff0c\u9002 \u624d\u5728\u6df7\u6218\u4e4b\u4e2d\uff0c\u90a3\u8001\u638c\u67dc\u5ffd\u5730\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u8f6c\u8eab\u5c31\u6293\u7740\u4e86\u90a3\u4e2a\u7edf\u5e26\u7684\u624b\u81c2\uff0c\u65cb\u98ce\u4e00\u821e\uff0c\u500f\u7136 \u6454\u51fa\uff0c\u638c\u67dc\u7684\u4e00\u70b9\u4e0d\u9732\uff0c\u7528\u610f\u672c\u5728\u4fdd\u5b58\u8fd9\u7247\u5e97\u5b50\uff0c\u5982\u4eca\u89c1\u5468\u5c71\u6c11\u5371\u6025\uff0c\u4e00\u51fa\u624b\u5c31\u662f\u201c\u5927\u6454\u7891 \u624b\u201d\uff0c\u90a3\u80a5\u732a\u4e00\u822c\u7684\u7edf\u5e26\u88ab\u4ed6\u6454\u5f97\u547c\u547c\u5e26\u98ce\uff0c\u50cf\u4e00\u4e2a\u8089\u5c71\u822c\u5730\u5411\u5a04\u6850\u5b59\u5f53\u5934\u538b\u4e0b\u3002 \u3000\u3000\u5a04\u6850\u5b59\u8fd8\u771f\u5730\u7ed9\u4ed6\u5413\u4e86\u4e00\u8df3\uff0c\u903c\u5f97\u4e3e\u624b\u4e00\u6325\uff0c\u53c8\u628a\u90a3\u7edf\u5e26\u50cf\u8089\u7403\u822c\u5730\u63a8\u4e86\u51fa\u53bb\uff0c\u638c\u67dc\u7684 \u53eb\u9053\uff1a\u201c\u5c11\u4e3b\u4eba\u5feb\u8d70\uff01\u201d\u6ca1\u547d\u5730\u75be\u5954\u8fc7\u6765\uff0c\u7f20\u7740\u5a04\u6850\u5b59\uff0c\u5468\u5c71\u6c11\u77e5\u9053\u8fd9\u8001\u638c\u67dc\u4e0d\u662f\u5a04\u6850\u5b59\u7684 \u5bf9\u624b\uff0c\u594b\u529b\u4e3e\u8d77\u91d1\u5200\uff0c\u8fd8\u60f3\u52a9\u6218\uff0c\u53ef\u662f\u90a3\u6761\u81c2\u818a\u4e0d\u542c\u4f7f\u5524\uff0c\u91d1\u5200\u4e3e\u5230\u80f8\u53e3\uff0c\u53c8\u518d\u5782\u4e0b\uff0c\u4e8e\u627f \u73e0\u5feb\u9a6c\u63a0\u5230\uff0c\u53eb\u9053\uff1a\u201c\u5feb\u4e0a\u9a6c\uff01\u201d\u77f3\u7fe0\u98ce\u77e5\u9053\u8fd9\u662f\u4e00\u5339\u5b9d\u9a6c\uff0c\u65f6\u673a\u7a0d\u7eb5\u5373\u901d\uff0c\u4e0d\u7531\u5206\u8bf4\uff0c\u4e00 \u628a\u62b1\u8d77\u5468\u5c71\u6c11\uff0c\u98de\u8eab\u8dc3\u4e0a\u9a6c\u80cc\uff0c\u4e8e\u627f\u73e0\u5012\u9a91\u9a6c\u80cc\uff0c\u5de6\u624b\u62e9\u957f\u77db\uff0c\u53f3\u624b\u821e\u5b9d\u5251\uff0c\u8fdc\u523a\u8fd1\u524a\uff0c\u786c \u51b2\u51fa\u9635\uff0c\u9633\u5b97\u6d77\u5982\u98de\u8d76\u5230\uff0c\u5728\u767e\u6b65\u4e4b\u5185\uff0c\u4ed6\u7684\u8f7b\u529f\u771f\u4e0d\u4e9a\u4e8e\u9a8f\u9a6c\uff0c\u4e8e\u627f\u73e0\u5c45\u9ad8\u4e34\u4e0b\uff0c\u957f\u77db\u529b \u63b7\uff0c\u53ea\u542c\u5f97\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u77db\u5934\u5df2\u88ab\u9633\u5b97\u6d77\u6298\u65ad\uff0c\u4f46\u4e8e\u627f\u73e0\u5df2\u8d81\u7740\u8fd9\u4e2a\u7a7a\u6863\uff0c\u98de\u9a6c\u63a0\u51fa\u6570\u5341 \u6b65\uff0c\u9633\u5b97\u6d77\u5929\u559d\u4e00\u58f0\u201c\u7740\uff01\u201d\u5c06\u77db\u5934\u4f5c\u4e3a\u6697\u5668\u63b7\u51fa\uff0c\u4e8e\u627f\u73e0\u4e3e\u5251\u4e00\u683c\uff0c\u90a3\u65ad\u77db\u5411\u524d\u4e00\u8df3\uff0c\u63d2 \u5165\u4e86\u77f3\u7fe0\u51e4\u80a9\u5934\uff0c\u767b\u65f6\u8840\u6d41\u5982\u6ce8\uff0c\u9633\u5b97\u6d77\u53c8\u5927\u559d\u9053\uff1a\u201c\u653e\u7bad\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u6325\u821e\u957f\u77db\uff0c\u62e8\u6253\u4e71\u7bad\uff0c\u90a3\u5339\u7167\u591c\u72ee\u5b50\u9a6c\u4e00\u58f0\u957f\u5636\u58f0\uff0c\u56db\u8e44\u75be\u8d70\uff0c\u7aef\u7684\u662f\u5339\u4e45\u7ecf\u6218 \u9635\u3001\u60ef\u4e8e\u51b2\u950b\u9677\u9635\u7684\u540d\u9a79\uff0c\u9a6e\u7740\u4e09\u4eba\uff0c\u4ecd\u662f\u817e\u8dc3\u8df3\u7eb5\uff0c\u6beb\u4e0d\u8d39\u529b\uff0c\u5bf9\u7740\u98de\u8757\u822c\u7684\u7bad\u96e8\uff0c\u4e86\u65e0 \u6050\u60e7\u3002\u5468\u5c71\u6c11\u5ffd\u7136\u5636\u58f0\u8bf4\u9053\uff1a\u201c\u56de\u53bb\u6551\u90a3\u638c\u67dc\u7684\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u518d\u8fdf\u4e00\u4f1a\uff0c\u54b1\u4eec\u4e09\u4eba\u90fd\u9003 \u4e0d\u4e86\u3002\u201d\u77f3\u7fe0\u51e4\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u5148\u8131\u9669\u8981\u7d27\u3002\u201d\u5468\u5c71\u6c11\u5389\u58f0\u9053\uff1a\u201c\u4ed6\u6551\u4e86\u54b1\u4eec\uff0c\u54b1\u4eec \u5c82\u53ef\u5f03\u4ed6\uff1f\u201d\u5ffd\u542c\u5f97\u5a04\u6850\u5b59\u4e00\u58f0\u602a\u5578\uff0c\u5468\u5c71\u6c11\u5728\u9a6c\u80cc\u4e0a\u56de\u5934\u4e00\u77a5\uff0c\u53ea\u89c1\u90a3\u8001\u638c\u67dc\u5df2\u88ab\u5a04\u6850\u5b59 \u4e3e\u8d77\uff0c\u4e24\u624b\u53cd\u526a\uff0c\u60f3\u662f\u5df2\u88ab\u4ed6\u7528\u201c\u5206\u7b4b\u9519\u9aa8\u624b\u201d\u4f24\u4e86\u3002\u5a04\u6850\u5b59\u628a\u90a3\u638c\u67dc\u7684\u5728\u7a7a\u4e2d\u4e00\u821e\uff0c\u629b\u7ed9 \u4e00\u4e2a\u7259\u5c06\uff0c\u7acb\u5373\u4e5f\u53d1\u8db3\u5954\u6765\uff0c\u5468\u5c71\u6c11\u5927\u53eb\u4e00\u58f0\uff0c\u4e00\u53e3\u7600\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u6655\u5012\u9a6c\u80cc\u3002\u77f3\u7fe0\u98ce\u5927 \u60ca\uff0c\u4e00\u624b\u62b1\u7740\u4e08\u592b\uff0c\u4e00\u624b\u7528\u957f\u5200\u5288\u523a\uff0c\u5fcd\u7740\u521b\u4f24\uff0c\u6d74\u8840\u529b\u6218\uff0c\u767d\u9a6c\u51b2\u5f00\u7bad\u96e8\uff0c\u6240\u5230\u4e4b\u5904\uff0c\u5b9b \u5982\u6ce2\u5206\u6d6a\u88c2\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u5c31\u628a\u5b98\u519b\u629b\u5728\u80cc\u540e\u3002\u9633\u5b97\u6d77\u8ffd\u4e4b\u4e0d\u53ca\uff0c\u89c1\u767d\u9a6c\u5982\u6b64\u795e\u9a8f\uff0c\u5fc3\u4e2d\u8d8a\u53d1 \u7231\u60dc\uff0c\u4ed6\u633d\u8d77\u957f\u5f13\uff0c\u54ac\u4e86\u54ac\u7259\uff0c\u5c06\u7bad\u6bd4\u4e86\u53c8\u6bd4\uff0c\u7bad\u5728\u5f26\u4e0a\uff0c\u5374\u8fdf\u8fdf\u4e0d\u53d1\uff0c\u8fdf\u7591\u4e4b\u95f4\uff0c\u767d\u9a6c\u65e9 \u5df2\u53bb\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u66ae\u8272\u82cd\u832b\uff0c\u767d\u9a6c\u5954\u51fa\u6570\u91cc\u4e4b\u5730\uff0c\u9690\u9690\u542c\u5f97\u4e1c\u8fb9\u89d2\u4e0a\uff0c\u6709\u884c\u519b\u9f13\u89d2\u4e4b\u58f0\uff0c\u4e8e\u627f\u73e0\u6015\u518d\u78b0\u5230 \u5b98\u519b\uff0c\u62e8\u8f6c\u9a6c\u5934\uff0c\u5411\u897f\u75be\u8d70\uff0c\u518d\u8fc7\u7247\u523b\uff0c\u6742\u58f0\u4ff1\u5bc2\uff0c\u56db\u91ce\u7a7a\u65e0\u4e00\u4eba\uff0c\u767d\u9a6c\u8d70\u5165\u4e86\u5c71\u8c37\u7684\u7f8a\u80a0 \u5c0f\u9053\uff0c\u786e\u5b9e\u8131\u79bb\u4e86\u9669\u5883\u3002\u77f3\u7fe0\u51e4\u7cbe\u795e\u4e00\u677e\uff0c\u987f\u89c9\u5168\u8eab\u9178\u8f6f\uff0c\u6447\u6447\u6b32\u5760\uff0c\u4e8e\u627f\u73e0\u6025\u5fd9\u62b1\u7d27\u4e86 \u5979\uff0c\u53ea\u89c1\u5979\u80a9\u4e0a\u6bb7\u7ea2\u4e00\u7247\uff0c\u8840\u6d41\u672a\u6b62\uff0c\u4e8e\u627f\u73e0\u4e00\u624b\u6495\u5f00\u5979\u7684\u8863\u88f3\uff0c\u5c31\u5728\u9a6c\u80cc\u4e0a\u7ed9\u5979\u6577\u4e0a\u4e86\u91d1 \u521b\u836f\u3002 \u3000\u3000\u5468\u5c71\u6c11\u60a0\u60a0\u9192\u8f6c\uff0c\u6b63\u89c1\u7740\u4e8e\u627f\u73e0\u6495\u5f00\u4ed6\u59bb\u5b50\u7684\u8863\u88f3\uff0c\u4e00\u53ea\u624b\u4f38\u4e86\u8fc7\u6765\uff0c\u7d27\u7d27\u5730\u6402\u7740\u59bb \u5b50\uff0c\u4e0d\u89c9\u6c14\u5f80\u4e0a\u51b2\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u6014\u4e86\u4e00\u6014\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u8fd8\u672a\u66fe\u60f3\u8d77\u81ea\u5df1\u662f \u4e2a\u4e54\u88c5\u6253\u626e\u7684\u201c\u7537\u5b50\u201d\uff0c\u5ffd\u542c\u5f97\u77f3\u7fe0\u51e4\u7b11\u9053\uff1a\u201c\u5927\u54e5\u4f60\u56b7\u4ec0\u4e48\uff1f\u5979\u662f\u4e2a\u5927\u59d1\u5a18\uff01\u201d\u539f\u6765\u77f3\u7fe0 \u51e4\u6614\u5e74\u66fe\u88ab\u4e91\u857e\u4e54\u88c5\u620f\u5f04\uff0c\u95f9\u51fa\u4e86\u8bb8\u591a\u7b11\u8bdd\uff0c\u6709\u4e86\u90a3\u756a\u7ecf\u9a8c\uff0c\u6545\u6b64\u5bf9\u4e8e\u540c\u6837\u4e5f\u662f\u5973\u626e\u7537\u88c5\u7684 \u4e8e\u627f\u73e0\u65e9\u5df2\u770b\u7834\u4e86\u3002\u4e8e\u627f\u73e0\u5931\u58f0\u7b11\u4e86\u51fa\u6765\uff0c\u628a\u5305\u5934\u7684\u65b9\u5dfe\u89e3\u4e0b\uff0c\u9732\u51fa\u6ee1\u5934\u79c0\u53d1\uff0c\u9053\uff1a\u201c\u5468\u5be8 \u4e3b\uff0c\u4f60\u5403\u8fd9\u4e2a\u918b\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65e5\u843d\u897f\u5c71\uff0c\u4eba\u4f24\u9a6c\u4e4f\uff0c\u4e8e\u627f\u73e0\u5c06\u5468\u5c71\u6c11\u592b\u5987\u6276\u4e0b\u9a6c\u6765\uff0c\u7ec6\u4e00\u5ba1\u89c6\uff0c\u77f3\u7fe0\u51e4\u867d\u88ab\u77db\u5934\u6240 \u4f24\uff0c\u672a\u53ca\u7b4b\u9aa8\uff0c\u6577\u4e86\u91d1\u521b\u836f\u4e4b\u540e\uff0c\u5df2\u65e0\u5927\u788d\uff1b\u5468\u5c71\u6c11\u88ab\u5a04\u6850\u5b59\u7684\u6307\u529b\u6240\u4f24\uff0c\u5374\u662f\u751a\u4e3a\u4e25\u91cd\uff0c \u4e8e\u627f\u73e0\u7ed9\u4ed6\u670d\u4e0b\u4e24\u9897\u5b89\u795e\u9759\u6c14\u80fd\u6cbb\u5185\u4f24\u7684\u5c11\u9633\u5c0f\u8fd8\u4e39\u3002\u5468\u5c71\u6c11\u6b47\u4e86\u4e00\u4f1a\uff0c\u7cbe\u795e\u7a0d\u7a0d\u6062\u590d\uff0c\u6068 \u6068\u8bf4\u9053\uff1a\u201c\u6211\u5bf9\u74e6\u523a\u654c\u5175\uff0c\u5927\u5c0f\u6570\u767e\u6218\uff0c\u4ece\u672a\u6709\u4eca\u65e5\u4e4b\u60e8\u8d25\uff0c\u4e0d\u610f\u4eca\u65e5\u53cd\u4f24\u5728\u5b98\u519b\u4e4b\u624b\uff0c\u6b64 \u4ec7\u6211\u7acb\u8a93\u5fc5\u62a5\u3002\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u95ee\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u5e08\u7236\u5462\uff1f\u6211\u4eec\u5c31\u662f\u56e0\u4e3a\u542c\u5230\u671d\u5ef7\u5c06\u4e0d\u5229\u4e8e \u4ed6\uff0c\u7279\u5730\u6765\u63a5\u4ed6\u7684\uff0c\u4ed6\u6ca1\u6709\u4e8b\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u5e08\u7236\u65e9\u5df2\u907f\u5f00\u4e86\uff0c\u4ed6\u6709\u4e00\u5c01\u4fe1\u7ed9\u4f60\u3002\u201d \u5468\u5c71\u6c11\u770b\u8fc7\u4e86\u4fe1\uff0c\u5ffd\u5730\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u5509\uff0c\u4f60\u5e08\u7236\u7adf\u7136\u4e0d\u8bb8\u6211\u62a5\u4ec7\uff01\u201d \u3000\u3000\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u4ed6\u8bf4\uff0c\u4e1c\u5357\u6cbf\u6d77\u4e00\u5e26\uff0c\u502d\u5bc7\u6b63\u5728\u4e3a\u60a3\uff0c \u82e5\u7136\u65e0\u4eba\u5236\u6b62\uff0c\u65e5\u540e\u5fc5\u917f\u6210\u5de8\u60a3\u3002\u4ed6\u8bf4\u4ee5\u76ee\u524d\u5f62\u52bf\u800c\u8bba\uff0c\u74e6\u523a\u5df2\u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u502d\u5974\u5219\u662f\u65b0\u5f20 \u4e4b\u5bc7\uff0c\u4ed6\u529d\u6211\u5c06\u5927\u5be8\u7684\u4e00\u90e8\u5206\u5175\u529b\uff0c\u64a4\u5230\u6c5f\u5357\uff0c\u548c\u4e1c\u5357\u6cbf\u6d77\u7684\u4e49\u58eb\uff0c\u5408\u529b\u6297\u502d\uff0c\u8fd9\u4e8b\u60c5\u53ef\u4e0d \u5bb9\u6613\u5475\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e3a\u96be\u4e4b\u5904\uff1f\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u6211\u4eec\u5728\u96c1\u95e8\u5173\u5916\uff0c\u4e00\u6765\u5317\u4eba\u4e0d\u4e60 \u6c34\u6218\uff1b\u4e8c\u6765\u6211\u4eec\u591a\u5e74\u4e0e\u671d\u5ef7\u4e3a\u654c\uff0c\u5927\u961f\u5f1f\u5144\u8981\u901a\u8fc7\u5b98\u519b\u7684\u9632\u5730\uff0c\u96be\u4e8e\u767b\u5929\uff1b\u4e09\u6765\uff0c\u8fd9\uff0c\u8fd9\u5c82 \u4e0d\u662f\u53cd\u52a9\u4e86\u6731\u5bb6\u5929\u5b50\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u7ec3\u5230\u4f3c\u4f60\u6211\u8fd9\u822c\u7684\u6b66\u529f\uff0c\u662f\u4e0d\u662f\u6bd4\u7ec3\u719f\u6c34\u6027\u66f4 \u96be\uff1f\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u7ec3\u6b66\u529f\u5f53\u7136\u6bd4\u5b66\u6e38\u6c34\u66f4\u96be\u3002\u201d\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u4e0d\u5c31\u5bf9\u4e86\uff1f\u8c01\u90fd\u4e0d\u662f \u751f\u6765\u4f1a\u7684\u3002\u5317\u4eba\u5230\u4e86\u5357\u65b9\uff0c\u81ea\u7136\u4fbf\u4e60\u6c34\u6218\u3002\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u81f3\u4e8e\u8bf4\u5230\u5927\u961f\u884c\u519b\uff0c\u96be\u4e8e\u901a\u8fc7\u5b98 \u5175\u9632\u5730\uff0c\u6211\u4eec\u53ef\u4ee5\u53eb\u5f1f\u5144\u626e\u6210\u5404\u8272\u4eba\u7b49\uff0c\u5316\u6574\u4e3a\u96f6\uff0c\u90fd\u6df7\u5230\u6c5f\u5357\u6765\u5475\u3002\u201d\u5468\u5c71\u6c11\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u4f60\u4eec\u90fd\u5982\u6b64\u8bf4\uff0c\u6211\u5c82\u53ef\u4e0d\u5982\u5dfe\u5e3c\uff1f\u5176\u5b9e\u6211\u4f55\u5c1d\u4e0d\u77e5\u9053\u5f20\u4e39\u67ab\u8bf4\u7684\u662f\u6b63\u7406\uff0c\u6551\u6c11\u4e8e\u6c34\u706b\u4e4b \u4e2d\uff0c\u4e43\u662f\u6211\u8f88\u7684\u672c\u4efd\uff0c\u5c82\u53ef\u63a8\u8f9e\u3002\u6211\u5c31\u662f\u4e0d\u670d\u6c14\u6731\u5bb6\u7684\u5929\u5b50\uff0c\u6211\u4eec\u4e3a\u4ed6\u51fa\u529b\uff0c\u4ed6\u5374\u53cd\u8fc7\u6765\u8981 \u6d88\u706d\u54b1\u4eec\u3002\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u4e5f\u6ca1\u6709\u51fa\u8fc7\u6028\u8a00\uff0c\u8bba\u8d77\u6765\u4ed6\u6bd4\u6211\u4eec\u66f4\u8be5\u6028\u6068\u671d\u5ef7\u3002\u201d\u5468\u5c71 \u6c11\u9053\uff1a\u201c\u597d\uff0c\u53ea\u8981\u6211\u80fd\u56de\u5927\u5be8\uff0c\u5fc5\u5b9a\u53d1\u5175\u3002\u201d\u4ed6\u8bf4\u4e86\u8bb8\u591a\u8bdd\uff0c\u4f24\u53e3\u53c8\u9690\u9690\u4f5c\u75db\uff0c\u9762\u4e0a\u795e\u60c5\uff0c \u751a\u662f\u75db\u82e6\u3002 \u3000\u3000\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u54b1\u4eec\u53bb\u627e\u4e00\u5bb6\u4eba\u5bb6\uff0c\u6682\u5bbf\u4e00\u5bb5\u3002\u201d\u4f46\u8352\u5c71\u9759\u591c\uff0c\u4e0d\u77e5\u54ea\u91cc\u6709\u4eba\u5bb6\uff1f\u5468\u5c71\u6c11 \u592b\u5987\u53c8\u53d7\u91cd\u4f24\uff0c\u4e0d\u4fbf\u884c\u8d70\u3002\u4e8e\u627f\u73e0\u60f3\u53bb\u95ee\u9053\uff0c\u5374\u53c8\u4e0d\u6562\u79bb\u5f00\u4ed6\u4eec\uff0c\u6b63\u81ea\u8e0c\u8e87\u65e0\u8ba1\uff0c\u5ffd\u542c\u5f97\u4e00 \u58f0\u9a6c\u5636\uff0c\u4e8e\u627f\u73e0\u7684\u767d\u9a6c\u7a81\u7136\u8df3\u8dc3\u8d77\u6765\uff0c\u4e5f\u53d1\u58f0\u957f\u5636\uff0c\u4f3c\u662f\u548c\u90a3\u5339\u9a6c\u9065\u4e3a\u547c\u5e94\uff0c\u4e8e\u627f\u73e0\u5927\u4e3a\u5947 \u602a\uff0c\u53ea\u89c1\u7167\u591c\u72ee\u5b50\u9a6c\u7adf\u7136\u4e0d\u542c\u81ea\u5df1\u7684\u7ea6\u675f\uff0c\u72ec\u4e2a\u513f\u4fbf\u5954\u8fc7\u5c71\u5761\uff0c\u4e8e\u627f\u73e0\u5927\u5403\u4e00\u60ca\uff0c\u4e0d\u5047\u601d \u7d22\uff0c\u4fbf\u8ffd\u4e0a\u53bb\u3002 \u3000\u3000\u521a\u8f6c\u8fc7\u5c71\u5773\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u5927\u559d\u9053\uff1a\u201c\u597d\u5927\u80c6\u7684\u5077\u9a6c\u8d3c\uff0c\u5f20\u4e39\u67ab\u7684\u5750\u9a91\u4f60\u4e5f\u6562\u5077\uff1f\u201d\u58f0\u5230 \u4eba\u5230\uff0c\u6708\u5149\u4e4b\u4e0b\uff0c\u770b\u5f97\u5206\u660e\uff0c\u662f\u4e2a\u6d53\u7709\u5927\u773c\u7684\u548c\u5c1a\uff0c\u62ff\u7740\u7897\u4e00\u822c\u7c97\u5927\u7684\u4e00\u6839\u7985\u6756\uff0c\u89c1\u4e86\u4e8e\u627f \u73e0\uff0c\u4e0d\u7531\u5206\u8bf4\uff0c\u4fbf\u547c\u5730\u4e00\u6756\u6253\u4e0b\u6765\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u56de\u5251\u4e00\u8fce\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u90a3\u548c\u5c1a\u7684\u7985\u6756\u6cfc\u98ce\u822c\u5730\u6253\u6765\uff0c\u6709\u5982\u6cf0\u5c71\u538b\u9876\uff0c\u529b\u9053\u5f3a\u52b2\u4e4b \u6781\uff0c\u4e8e\u627f\u73e0\u7ed9\u4ed6\u903c\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u53ea\u597d\u65bd\u5c55\u8f7b\u7075\u7684\u5251\u6cd5\uff0c\u4e0e\u4ed6\u6e38\u6597\uff0c\u5fc3\u4e2d\u6697\u6697\u5403 \u60ca\uff1a\u8fd9\u548c\u5c1a\u7684\u529f\u529b\u6bd4\u8d77\u4e86\u7f18\uff0c\u90a3\u53ef\u662f\u9ad8\u5f3a\u5f97\u591a\uff01\u90a3\u548c\u5c1a\u6a2a\u626b\u76f4\u5288\uff0c\u7985\u6756\u8d77\u5904\uff0c\u6c99\u98de\u77f3\u8d70\uff0c\u89c1 \u4e8e\u627f\u73e0\u5c45\u7136\u6321\u5f97\u4e8c\u5341\u591a\u62db\uff0c\u9762\u4e0a\u4e5f\u9732\u51fa\u8be7\u5f02\u4e4b\u5bb9\uff0c\u4e8e\u627f\u73e0\u597d\u4e0d\u5bb9\u6613\u624d\u7f13\u5f97\u53e3\u6c14\uff0c\u53eb\u9053\uff1a\u201c\u5927 \u5e08\u8bf7\u542c\u6211\u8bf4\uff01\u201d\u90a3\u548c\u5c1a\u970d\u5730\u4e00\u8df3\uff0c\u7897\u53e3\u822c\u7c97\u5927\u7684\u7985\u6756\u76f4\u5f39\u8d77\u6765\uff0c\u559d\u9053\uff1a\u201c\u8bf4\u4ec0\u4e48\uff1f\u201d\u7985\u6756\u4e00 \u5f39\u4e00\u8df3\uff0c\u53ea\u542c\u5f97\u54e8\u7684\u4e00\u58f0\uff0c\u706b\u661f\u98de\u6e85\uff0c\u4e8e\u627f\u73e0\u7684\u5b9d\u5251\u5df2\u8131\u624b\u98de\u53bb\uff01\u6b63\u662f\uff1a \u3000\u3000\u5c0f\u9547\u91d1\u5200\u624d\u8131\u9669\uff0c\u8352\u5c71\u53c8\u901a\u83bd\u5934\u9640\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u5341\u56de\u3000\u5c0f\u9547\u805a\u82f1\u8c6a\u3000\u91d1\u5200\u6740\u654c\u3000\u957f\u6c5f\u9022\u79c0\u58eb\u3000\u94f6\u5251\u8bdb\u502d \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u5341\u56de\u3000\u5c0f\u9547\u805a\u82f1\u8c6a\u3000\u91d1\u5200\u6740\u654c\u3000\u957f\u6c5f\u9022\u79c0\u58eb\u3000\u94f6\u5251\u8bdb\u502d \u3000\u3000\u90a3\u548c\u5c1a\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u5f20\u4e39\u67ab\u7684\u5f1f\u5b50\u3002\u771f\u662f\u4e00\u4ee3\u80dc\u4e8e\u4e00\u4ee3\uff0c\u53eb\u6211\u4eec\u505a\u957f\u8f88\u7684 \u6127\u6b7b\u4e86\uff01\u201d\u4e8e\u627f\u73e0\u60ca\u7591\u4e0d\u5b9a\uff0c\u62fe\u8d77\u5b9d\u5251\uff0c\u53ea\u89c1\u90a3\u548c\u5c1a\u5e74\u8fd1\u516d\u65ec\uff0c\u7ea2\u5149\u6ee1\u9762\uff0c\u624b\u6a2a\u7985\u6756\uff0c\u7985\u6756 \u88ab\u81ea\u5df1\u7684\u5b9d\u5251\u622a\u4e86\u4e00\u4e2a\u7f3a\u53e3\uff0c\u5374\u6beb\u65e0\u60d5\u8272\uff0c\u54a7\u5f00\u5927\u53e3\uff0c\u5411\u81ea\u5df1\u7b11\u4e2a\u4e0d\u505c\u3002\u518d\u4e00\u770b\uff0c\u53ea\u89c1\u81ea\u5df1 \u7684\u90a3\u5339\u7167\u591c\u72ee\u5b50\u9a6c\u6b63\u5728\u79f0\u53e6\u4e00\u5339\u767d\u9a6c\u5b09\u620f\uff0c\u90a3\u5339\u767d\u9a6c\u548c\u7167\u591c\u72ee\u5b50\u9a6c\u4e00\u6a21\u4e00\u6837\uff0c\u53ea\u662f\u8eab\u4e0a\u591a\u4e86 \u8bb8\u591a\u6591\u70b9\uff0c\u7167\u591c\u72ee\u5b50\u9a6c\u5c48\u4e86\u524d\u8e44\uff0c\u534a\u8dea\u5730\u4e0a\uff0c\u6328\u7740\u90a3\u5339\u9a6c\u6469\u64e6\uff0c\u4e24\u5339\u9a6c\u90fd\u5728\u4e0d\u65ad\u5730\u5636\u53eb\uff0c\u597d \u50cf\u4e45\u522b\u7684\u4eb2\u4eba\u5728\u5f02\u5730\u76f8\u9022\u4e00\u822c\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5ffd\u542c\u5f97\u5468\u5c71\u6c11\u53eb\u9053\uff1a\u201c\u5475\uff0c\u539f\u6765\u662f\u6f6e\u97f3\u5927\u5e08\uff01\u201d\u53ea\u89c1\u77f3\u7fe0\u98ce\u6276\u7740\u5468\u5c71 \u6c11\u4e00\u6b65\u4e00\u62d0\u5730\u8d70\u6765\uff0c\u672a\u5230\u8ddf\u524d\uff0c\u4fbf\u5f20\u53e3\u5927\u53eb\u3002\u4e8e\u627f\u73e0\u8fd9\u4e00\u559c\u975e\u540c\u5c0f\u53ef\uff0c\u6025\u5fd9\u8dea\u5012\uff0c\u53e9\u4e86\u4e09\u4e2a \u54cd\u5934\u9053\uff1a\u201c\u5f92\u5b59\u4e8e\u627f\u73e0\uff0c\u53e9\u89c1\u5e08\u4f2f\u7956\u3002\u201d \u3000\u3000\u8fd9\u6f6e\u97f3\u548c\u5c1a\u5728\u7384\u673a\u9038\u58eb\u95e8\u4e0b\u6392\u884c\u7b2c\u4e8c\uff0c\u4ee5\u5929\u9b54\u6756\u6cd5\u5a01\u9707\u6c5f\u6e56\uff0c\u8bba\u8d77\u8f88\u4efd\u6b63\u662f\u5f20\u4e39\u67ab\u7684\u4e8c \u5e08\u4f2f\uff0c\u4ed6\u90a3\u5339\u767d\u9a6c\u4e43\u662f\u7167\u591c\u72ee\u5b50\u9a6c\u7684\u6bcd\u4eb2\uff0c\u6240\u4ee5\u7167\u591c\u72ee\u5b50\u9a6c\u548c\u5b83\u90a3\u822c\u4eb2\u70ed\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u5c11\u5be8\u4e3b\uff0c\u4f60\u600e\u4e48\u53d7\u4e86\u4f24\uff1f\u201d\u77f3\u7fe0\u98ce\u5c06\u524d\u4e8b\u8bf4\u4e86\u4e00\u904d\uff0c\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u539f \u6765\u4f60\u4eec\u4e5f\u662f\u627e\u5f20\u4e39\u67ab\u7684\u3002\u201d\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u8981\u627e\u4ed6\u7ed9\u6211\u62a5\u8fd9\u4e24\u5200\u4e4b\u4ec7\u5462\uff01\u201d\u6495\u5f00\u80a9\u8863\uff0c\u53ea\u89c1 \u5de6\u8fb9\u80a9\u80cc\u4ea4\u53c9\u4e24\u9053\u4f24\u53e3\uff0c\u5df2\u8d34\u4e0a\u818f\u836f\u3002\u4e8e\u627f\u73e0\u5927\u9a87\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5e08\u7236\u8bf4\u4ed6\u7684\u5916\u5bb6\u529f\u592b\u767b \u964d\u9020\u6781\uff0c\u4f24\u4e86\u4e00\u8fb9\u81c2\u818a\uff0c\u8fd8\u5c45\u7136\u8fd9\u822c\u4e86\u5f97\u3002\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u8c01\u5403\u4e86\u8001\u864e\u7684\u5fc3\uff0c\u8c79\u5b50\u7684\u80c6\uff0c\u6562 \u4e0e\u5927\u5e08\u4f5c\u5bf9\uff1f\u201d\u77f3\u7fe0\u51e4\u4e5f\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u8fd9\u4e24\u5200\u662f\u8c01\u780d\u7684\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u6068\u6068\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u5c82\u6b62 \u4e0e\u6211\u4f5c\u5bf9\uff0c\u4e1c\u5357\u6cbf\u6d77\u7684\u767e\u59d3\u88ab\u4ed6\u4eec\u6740\u622e\u5f97\u76c8\u5343\u7d2f\u4e07\uff0c\u5e78\u800c\u6211\u8fd9\u6839\u7985\u6756\u8fd8\u4e0d\u542b\u7cca\uff0c\u8981\u4e0d\u7136\u6015\u4e0d \u88ab\u4ed6\u4eec\u65a9\u4e3a\u8089\u9171\u3002\u8fd9\u4e24\u5200\u662f\u502d\u5bc7\u780d\u7684\uff01\u201d\u6f6e\u97f3\u548c\u5c1a\u8bf4\u51fa\u7ecf\u8fc7\uff0c\u539f\u6765\u4ed6\u5e73\u751f\u6700\u7231\u6253\u62b1\u4e0d\u5e73\uff0c\u542c \u8bf4\u502d\u5bc7\u5728\u4e1c\u5357\u6cbf\u6d77\u5929\u8086\u6740\u622e\uff0c\u4e49\u6124\u586b\u81ba\uff0c\u4fbf\u8dd1\u5230\u6d59\u6c5f\u53f0\u5dde\u53bb\u52a9\u90a3\u91cc\u7684\u4e49\u6c11\u9996\u9886\u4f5c\u6218\uff0c\u4f46\u5be1\u4e0d \u654c\u4f17\uff0c\u5728\u4e00\u6b21\u6218\u5f79\u4e2d\uff0c\u4ee5\u6570\u767e\u4e49\u6c11\u5bf9\u6297\u4e09\u5e72\u502d\u5bc7\uff0c\u867d\u7136\u6740\u654c\u65e0\u6570\uff0c\u4f46\u4e49\u6c11\u4ea6\u6298\u635f\u8fc7\u534a\uff0c\u6f6e\u97f3 \u548c\u5c1a\u4fdd\u62a4\u4e49\u6c11\u9996\u9886\u5b97\u7559\u3001\u90d1\u8302\u4e03\u51b2\u6740\u51fa\u6765\uff0c\u6df7\u6218\u4e4b\u4e2d\uff0c\u80a9\u80cc\u88ab\u502d\u5bc7\u780d\u4e86\u4e24\u5200\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u5e08\u7236\u5df2\u5230\u5927\u7406\u53bb\u4e86\uff01\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u4ed6\u4e00\u5b9a\u662f\u60f3\u53bb\u9080\u6211\u7684\u5927\u5e08\u5144\u51fa \u5c71\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u662f\u53bb\u7ed9\u592a\u5e08\u7956\u62dc\u5bff\u3002\u201d\u6f6e\u97f3\u548c\u5c1a\u6572\u6572\u5934\u9053\uff1a\u201c\u54c8\uff0c\u6211\u5012\u5fd8\u8bb0\u4eca\u5e74 \u662f\u5e08\u7236\u7684\u516b\u5341\u516d\u5bff\u4e86\u3002\u201d\u53c8\u7b11\u9053\uff1a\u201c\u4e39\u67ab\u8fd9\u5b69\u5b50\u8c8c\u4f3c\u5f52\u9690\uff0c\u5b9e\u5219\u4e00\u8154\u70ed\u8840\u3002\u6211\u66f4\u7231\u7ba1\u95f2\u4e8b\uff0c \u4ed6\u66fe\u6709\u4e66\u4fe1\u7ed9\u53f6\u5b97\u7559\uff0c\u53eb\u53f6\u5e2e\u4e3b\u548c\u5c71\u6c11\u5144\u53ca\u5c71\u4e1c\u5404\u5be8\u4e3b\u8054\u7edc\uff0c\u8bf7\u4f60\u4eec\u901f\u53d1\u6551\u5175\u3002\u4ed6\u6b64\u53bb\u62dc \u5bff\uff0c\u5b9a\u6709\u6240\u56fe\uff0c\u6211\u770b\u4ed6\u81f3\u8fdf\u660e\u5e74\uff0c\u5fc5\u56de\u6c5f\u5357\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5468\u5be8\u4e3b\u4f60\u7684\u4f24\u600e\u6837\u4e86\uff1f\u201d\u5468\u5c71 \u6c11\u7b11\u9053\uff1a\u201c\u670d\u4e86\u4f60\u7684\u836f\u4e38\uff0c\u597d\u4e86\u4e00\u70b9\u3002\u542c\u5f97\u6f6e\u97f3\u5927\u5e08\u6240\u8bf4\u7684\u6297\u502d\u82f1\u52c7\u4e8b\u7ee9\uff0c\u66f4\u662f\u7cbe\u795e\u4e00\u632f\uff0c \u6211\u770b\u4e0d\u59a8\u4e8b\u7684\u3002\u201d\u6f6e\u97f3\u548c\u5c1a\u731b\u9192\u9053\uff1a\u201c\u4f60\u770b\u6211\u597d\u80e1\u6d82\uff0c\u5c3d\u7ba1\u548c\u4f60\u8bf4\u8bdd\uff0c\u4f60\u4eec\u90fd\u8be5\u53bb\u6b47\u6b47 \u5566\u3002\u201d \u3000\u3000\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u53bb\u54ea\u513f\u627e\u6b47\u606f\u7684\u5730\u65b9\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u8f6c\u8fc7\u8fd9\u8fb9\u5c71\u52d2\uff0c\u6709\u4e00\u5bb6\u730e\u6237\uff0c\u662f \u81ea\u5df1\u4eba\u3002\u201d\u4e8e\u627f\u73e0\u6276\u5468\u5c71\u6c11\u592b\u5987\u4e0a\u9a6c\uff0c\u6f6e\u97f3\u548c\u5c1a\u5728\u524d\u5f15\u8def\u3002\u5468\u5c71\u6c11\u5ffd\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\uff0c\u8bf7\u4f60\u6bcf \u9694\u5341\u6b65\uff0c\u7ed9\u6211\u5728\u6811\u4e0a\u7559\u4e00\u4e2a\u8bb0\u53f7\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ec0\u4e48\u8bb0\u53f7\uff1f\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u65e5\u6708\u53cc\u65d7\u548c\u4e00 \u652f\u5927\u68d2\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u662f\u7559\u7ed9\u6bd5\u64ce\u5929\u7684\u5417\uff1f\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u8fd9\u6b21\u6765\u9664 \u4e86\u60f3\u627e\u4f60\u7684\u5e08\u7236\u4e4b\u5916\uff0c\u8fd8\u60f3\u4e0e\u4ed6\u4f1a\u76df\u3002\u4ed6\u662f\u6211\u7684\u4e49\u5f1f\u3002\u54c8\uff0c\u6f6e\u97f3\u5927\u5e08\uff0c\u6b64\u4eba\u8c6a\u6c14\u5e72\u4e91\uff0c\u786e\u662f \u540e\u8f88\u4e2d\u96be\u89c1\u7684\u82f1\u6770\u3002\u4e0e\u4f60\u6027\u60c5\u5fc5\u5b9a\u76f8\u6295\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u5934\u9707\u8361\uff0c\u6bd5\u64ce\u5929\u7c97\u8c6a\u7684\u76f8\u8c8c\u5728\u5979\u8111\u6d77\u4e2d\u6d6e\u6cdb\u51fa\u6765\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5979\u611f\u5230\u6709\u79cd\u96be\u4ee5 \u8a00\u8bf4\u7684\u538c\u70e6\uff0c\u4e0d\u60f3\u518d\u89c1\u5230\u4ed6\u3002\u6f6e\u97f3\u5927\u5e08\u5374\u662f\u5174\u81f4\u52c3\u52c3\uff0c\u5411\u5468\u5c71\u6c11\u6253\u542c\u6bd5\u64ce\u5929\u7684\u4e3a\u4eba\u548c\u6765\u5386\uff0c \u54c8\u54c8\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u6bd5\u9053\u51e1\u7684\u513f\u5b50\uff0c\u90a3\u4e48\u8bf4\u4ed6\u8be5\u53eb\u6211\u505a\u4e16\u53d4\u3002\u201d\u6bd5\u9053\u51e1\u751f\u524d\u548c\u6f6e\u97f3\u548c\u5c1a\u81f3\u4e3a \u8981\u597d\uff0c\u6f6e\u8005\u548c\u5c1a\u542c\u5f97\u6545\u4eba\u4e4b\u5b50\u505a\u4e86\u5317\u4e94\u7701\u7684\u201c\u5927\u9f99\u5934\u201d\uff0c\u5fc3\u4e2d\u81ea\u662f\u559c\u60a6\u3002 \u3000\u3000\u8f6c\u8fc7\u5c71\u5773\uff0c\u6de1\u6708\u661f\u5149\u4e4b\u4e0b\uff0c\u679c\u7136\u9690\u7ea6\u89c1\u6709\u4e00\u5bb6\u4eba\u5bb6\uff0c\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u5c11\u5be8\u4e3b\uff0c\u4f60\u7684\u4f24\u8bf4 \u91cd\u4e0d\u91cd\uff0c\u8bf4\u8f7b\u4e0d\u8f7b\uff0c\u6050\u6015\u4e5f\u5f97\u8c03\u517b\u4e2a\u628a\u6708\uff0c\u8fd9\u5bb6\u4e3b\u4eba\u9887\u901a\u533b\u7406\uff0c\u4f60\u6b63\u597d\u5728\u8fd9\u513f\u9759\u517b\u3002\u201d\u521a\u8bf4 \u8bdd\u95f4\uff0c\u5ffd\u89c1\u5c71\u4e0b\u6709\u706b\u628a\u5149\uff0c\u6709\u4e00\u9a91\u9a6c\u5954\u4e0a\u5c71\u5761\uff0c\u6f6e\u97f3\u548c\u5c1a\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u4eba\u9a91\u672f \u7cbe\u7edd\uff0c\u9a91\u7684\u9a6c\u4ea6\u975e\u51e1\u54c1\uff0c\u5468\u5144\uff0c\u4f60\u5feb\u770b\u770b\uff0c\u53ef\u662f\u6bd5\u64ce\u5929\u5417\uff1f\u201d\u4e8e\u627f\u73e0\u4e00\u773c\u77a5\u53bb\uff0c\u5c16\u58f0\u53eb\u9053\uff1a \u201c\u662f\u9633\u5b97\u6d77\uff01\u201d\u6e56\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u54ea\u4e00\u4e2a\u9633\u5b97\u6d77\uff1f\u662f\u5ddd\u897f\u5251\u5ba2\u9633\u5b97\u6d77\u5417\uff1f\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u662f\u5f53 \u4eca\u7684\u5927\u5185\u603b\u7ba1\u9633\u5b97\u6d77\uff0c\u662f\u6740\u4f24\u6211\u7684\u90a3\u4e2a\u72d7\u8d3c\u9633\u5b97\u6d77\uff01\u201d \u3000\u3000\u9633\u5b97\u6d77\u9a91\u7684\u662f\u5fa1\u82d1\u9a8f\u9a6c\uff0c\u4ec5\u6b21\u4e8e\u7167\u591c\u72ee\u5b50\uff0c\u4ed6\u4e00\u8def\u8ffd\u4e8e\u627f\u73e0\u7684\u9a6c\u8e44\u75d5\u8ff9\uff0c\u8ffd\u5230\u6b64\u5730\uff0c\u5728 \u9a6c\u80cc\u4e0a\u5c06\u706b\u628a\u4e00\u6643\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5468\u5be8\u4e3b\u539f\u6765\u8fd8\u5728\u8fd9\u513f\uff0c\u9633\u67d0\u53c8\u8981\u6765\u6349\u9a7e\u4e86\uff01\u201d\u4e8e\u627f\u73e0\u62d4\u5251 \u51fa\u9798\uff0c\u6f6e\u97f3\u548c\u5c1a\u6c89\u58f0\u9053\uff1a\u201c\u8fd9\u8d3c\u5b50\u4ea4\u7ed9\u6211\uff0c\u4f60\u4fdd\u62a4\u5468\u5be8\u4e3b\u3002\u201d\u84e6\u5730\u4e00\u58f0\u5927\u543c\uff0c\u62a1\u52a8\u7985\u6756\uff0c\u5377 \u5730\u4fbf\u626b\u9a6c\u8db3\uff0c\u90a3\u5339\u67a3\u7ea2\u5927\u9a6c\u53cc\u8e44\u4eba\u7acb\uff0c\u7eb5\u8d77\u4e00\u8dc3\uff0c\u9633\u5b97\u6d77\u5927\u6012\uff0c\u5c06\u706b\u628a\u5288\u9762\u63b7\u4e0b\uff0c\u88ab\u6756\u98ce\u4e00 \u9707\uff0c\u706b\u628a\u659c\u98de\u6570\u4e08\u4e4b\u5916\uff0c\u5929\u5a07\u6709\u5982\u706b\u86c7\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u8fd9\u4e00\u77ac\u4e4b\u95f4\uff0c\u6f6e\u97f3\u548c\u5c1a\u7b2c\u4e8c\u6756\u53c8\u5230\uff0c\u800c\u9633\u5b97\u6d77\u4e5f\u8df3\u4e86\u4e0b\u6765\uff0c\u957f\u5251\u4e00 \u62db\u201c\u6bd2\u86c7\u5410\u4fe1\u201d\uff0c\u523a\u4e86\u8fdb\u53bb\u3002\u6f6e\u97f3\u548c\u5c1a\u53eb\u9053\uff1a\u201c\u5413\uff0c\u597d\u5feb\uff01\u201d\u5012\u8f6c\u6756\u5c3e\u4e00\u6321\uff0c\u53ee\u5f53\u4e00\u58f0\uff0c\u5251 \u6756\u76f8\u4ea4\uff0c\u5404\u9000\u4e24\u6b65\uff0c\u9633\u5b97\u6d77\u53eb\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u6211\u662f\u4e13\u6253\u6076\u72d7\u7684\u964d\u9b54\u5929 \u5c0a\uff01\u4f60\u8fd9\u65e0\u803b\u5c0f\u5b50\u4e5f\u6562\u79f0\u4e3a\u5251\u5ba2\uff0c\u5403\u6d12\u5bb6\u4e09\u767e\u7985\u6756\uff01\u201d\u7985\u6756\u76f4\u4e0a\u76f4\u4e0b\u7684\u8fce\u5934\u75db\u6253\uff0c\u9633\u5b97\u6d77\u8bd5 \u51fa\u4ed6\u7684\u529b\u6c14\uff0c\u8fd8\u771f\u7684\u4e0d\u6562\u548c\u4ed6\u786c\u78b0\uff0c\u53ea\u5c55\u5f00\u5c0f\u5de7\u817e\u632a\u7684\u8eab\u6cd5\uff0c\u4e00\u53e3\u6c14\u957f\u5251\u6d3b\u4f3c\u7075\u86c7\uff0c\u5ffd\u4f38\u5ffd \u7f29\uff0c\u5bfb\u7a7a\u62b5\u9699\uff0c\u4e13\u523a\u6f6e\u97f3\u548c\u5c1a\u7684\u5173\u8282\u8981\u5bb3\uff01 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u4e45\u6218\u9633\u5b97\u6d77\u4e0d\u4e0b\uff0c\u5fc3\u4e2d\u70e6\u8e81\uff0c\u9661\u5730\u5927\u559d\u4e00\u58f0\uff0c\u7985\u6756\u62a1\u5706\uff0c\u547c\u547c\u731b\u626b\uff0c\u6709\u5982\u731b\u86c7 \u51fa\u6d1e\uff0c\u997f\u864e\u4e0b\u5c71\uff0c\u4e00\u6d3e\u62fc\u547d\u7684\u62db\u6570\uff0c\u9633\u5b97\u6d77\u4e0d\u4f4f\u540e\u9000\uff0c\u4f46\u95e8\u6237\u5c01\u5f97\u66f4\u5176\u7d27\u5bc6\uff0c\u6bcf\u4e00\u51fa\u5251\uff0c\u90fd \u662f\u7528\u6781\u5de7\u5999\u7684\u5251\u6cd5\uff0c\u5c06\u6f6e\u97f3\u548c\u5c1a\u7684\u731b\u52b2\u8f7b\u8f7b\u5316\u89e3\u3002\u4e8e\u627f\u73e0\u770b\u5f97\u6697\u76b1\u7709\u5934\uff0c\u5fc3\u9053\uff1a\u201c\u600e\u4e48\u5e08\u4f2f \u7956\u5982\u6b64\u9c81\u83bd\uff1f\u7adf\u7136\u5b8c\u5168\u770b\u4e0d\u51fa\u654c\u4eba\u4ee5\u9038\u5f85\u52b3\u7684\u6218\u6cd5\uff1f\u82e5\u662f\u6211\u7684\u5e08\u7236\uff0c\u7109\u80fd\u7ed9\u9633\u5b97\u6d77\u5360\u4e86\u4fbf \u5b9c\u3002\u201d\u539f\u6765\u5728\u7384\u673a\u9038\u58eb\u95e8\u4e0b\u5f1f\u5b50\u4e4b\u4e2d\uff0c\u6f6e\u97f3\u548c\u5c1a\u6c14\u529b\u6700\u5927\uff0c\u6b66\u529f\u5374\u662f\u6700\u5f31\uff0c\u5f20\u4e39\u67ab\u867d\u662f\u5e08 \u4f84\uff0c\u5374\u6bd4\u4ed6\u5f3a\u4e86\u4e0d\u77e5\u51e0\u500d\u3002 \u3000\u3000\u4f46\u867d\u7136\u5982\u6b64\uff0c\u6f6e\u97f3\u548c\u5c1a\u5018\u82e5\u662f\u4e0d\u6025\u4e0d\u8e81\uff0c\u4ed7\u7740\u4ed6\u767b\u5cf0\u9020\u6781\u7684\u5916\u5bb6\u529f\u592b\uff0c\u8fd8\u53ef\u4ee5\u548c\u9633\u5b97\u6d77 \u6253\u4e2a\u5e73\u624b\uff0c\u4e00\u52a8\u4e86\u6c14\uff0c\u72c2\u547c\u731b\u626b\uff0c\u4e45\u653b\u4e0d\u4e0b\uff0c\u6c14\u529b\u6e10\u6e10\u6d88\u8017\uff0c\u52a0\u4e4b\u4ed6\u4e00\u8fb9\u81c2\u818a\u53d7\u4f24\uff0c\u66f4\u662f\u96be \u4ee5\u6301\u4e45\u3001\u4e09\u5341\u62db\u8fc7\u540e\uff0c\u9633\u5b97\u6d77\u563f\u563f\u51b7\u7b11\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff0c\u4f7f\u5f97\u8fd9\u6837\u521a\u731b\u7684\u5929\u9b54\u6756\u6cd5\uff01\u539f\u6765\u662f \u5f20\u4e39\u67ab\u7684\u5e08\u4f2f\u3002\u53ef\u60dc\u4f60\u4f3c\u7b28\u725b\u4e00\u6837\uff0c\u5168\u4e0d\u61c2\u5f97\u4e0a\u4e58\u6b66\u529f\u7684\u5965\u5999\uff0c\u4e14\u5f85\u6211\u6307\u70b9\u4e8e\u4f60\u3002\u566c\uff0c\u8fd9\u4e00 \u6756\u4e0d\u5e94\u5982\u6b64\u6a2a\u626b\uff0c\u8fd9\u4e00\u6756\u7528\u7684\u52b2\u9053\u4e0d\u5bf9\uff01\u201d\u4ed6\u662f\u6709\u610f\u6fc0\u6012\u6f6e\u97f3\u548c\u5c1a\uff0c\u6f6e\u97f3\u548c\u5c1a\u679c\u7136\u4e2d\u8ba1\uff0c\u6068 \u4e0d\u5f97\u4e00\u6756\u5c06\u9633\u5b97\u6d77\u6253\u6b7b\uff0c\u9ad8\u624b\u76f8\u6597\uff0c\u54ea\u80fd\u52a8\u6c14\uff0c\u4e00\u6012\u6756\u6cd5\u4fbf\u4e71\uff0c\u9633\u5b97\u6d77\u54c8\u54c8\u5927\u7b11\uff0c\u5251\u6cd5\u4e00 \u53d8\uff0c\u7740\u7740\u53cd\u653b\uff0c\u5251\u52bf\u5982\u8679\uff0c\u6740\u5f97\u6f6e\u97f3\u548c\u5c1a\u614c\u5fd9\u56de\u6756\u62a4\u8eab\uff0c\u653b\u5b88\u4e4b\u52bf\uff0c\u987f\u7136\u4e00\u53d8\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u77a7\u4e0d\u8fc7\u773c\uff0c\u6309\u5251\u6b32\u8d77\uff0c\u4f46\u6f6e\u97f3\u548c\u5c1a\u8f88\u4efd\u6bd4\u5979\u9ad8\u51fa\u4e24\u8f88\uff0c\u81ea\u5df1\u4e0a\u53bb\u76f8\u52a9\uff0c\u5374\u53c8\u6015\u4ed6 \u6302\u4e0d\u4f4f\u9762\u5b50\uff0c\u624b\u5fc3\u6697\u6263\u4e09\u6735\u91d1\u82b1\uff0c\u60f3\u9053\uff1a\u201c\u6211\u4e0d\u5982\u7528\u6697\u5668\u52a9\u4ed6\u4e00\u81c2\u4e4b\u529b\u3002\u201d\u6b63\u5728\u8e0c\u8e87\u672a\u51b3\uff0c \u5ffd\u542c\u5f97\u767d\u9a6c\u4e00\u58f0\u957f\u5636\uff0c\u5468\u5c71\u6c11\u53eb\u9053\uff1a\u201c\u6709\u8d3c\u5b50\u6697\u7b97\uff01\u201d\u4e8e\u627f\u73e0\u4e00\u56de\u5934\uff0c\u53ea\u89c1\u90a3\u540d\u53eb\u891a\u7384\u7684\u77ed \u5c0f\u7cbe\u608d\u6c49\u5b50\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u5077\u5077\u6478\u6478\u5730\u6478\u5230\u4e00\u7094\u5ca9\u77f3\u540e\u9762\uff0c\u6b63\u66f3\u5f00\u5f39\u5f13\uff0c\u60f3\u6253\u5468\u5c71\u6c11\uff0c\u5374 \u7ed9\u767d\u9a6c\u53d1\u73b0\u4e86\u3002\u73b0\u5728\u4ed6\u6b63\u7528\u5f39\u5b50\u6253\u767d\u9a6c\u54e9\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5927\u6012\uff0c\u91d1\u82b1\u98de\u51fa\uff0c\u5148\u4e0d\u6253\u9633\u5b97\u6d77\uff0c\u4e09\u6735\u91d1\u82b1\u90fd\u5954\u5411\u891a\u7384\uff0c\u5580\u5693\u4e00\u58f0\uff0c\u7b2c\u4e00\u6735\u91d1\u82b1 \u5c06\u891a\u7384\u7684\u5f39\u5f13\u6253\u5f97\u4ece\u4e2d\u65ad\u88c2\uff0c\u5f39\u5f13\u4e24\u8fb9\u5f39\u8d77\uff0c\u891a\u7384\u864e\u53e3\u6d41\u8840\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7b2c\u4e8c\u6735\u91d1\u82b1 \u53c8\u5230\uff0c\u891a\u7384\u4e00\u7f29\u5934\uff0c\u5934\u4e0a\u4e00\u7247\u6c81\u51c9\uff0c\u91d1\u82b1\u4e94\u6735\u82b1\u74e3\u90fd\u662f\u950b\u5229\u7684\u5200\u7247\uff0c\u5c06\u4ed6\u4e00\u5927\u7247\u5934\u53d1\u524a\u53bb\uff0c \u5934\u76ae\u4e5f\u522e\u4f24\u4e86\uff0c\u891a\u7384\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u62b1\u5934\u4e00\u6eda\uff0c\u4ed6\u64c5\u957f\u5317\u6d3e\u7684\u6eda\u5730\u5802\u529f\u592b\uff0c\u8fd9\u4e00\u6eda\u8fd8\u771f\u7b97\u8eb2 \u95ea\u5f97\u5feb\uff0c\u7b2c\u4e09\u6735\u91d1\u82b1\u4ece\u79bb\u5f00\u4ed6\u540e\u8111\u4e94\u5bf8\u5de6\u53f3\u98de\u8fc7\uff0c\u5374\u6ca1\u6709\u4f24\u7740\u4ed6\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u6b63\u60f3\u518d\u6253\uff0c\u5ffd\u542c\u5f97\u9a6c\u8e44\u58f0\u6765\u5f97\u751a\u6025\uff0c\u891a\u7384\u521a\u6eda\u4e0b\u5c71\u5761\uff0c\u4e00\u4e2a\u9b41\u68a7\u5947\u4f1f\u7684\u5927\u6c49\u5df2\u7b56 \u9a6c\u5954\u4e0a\uff0c\u5ffd\u5730\u8df3\u4e0b\u9a6c\u80cc\uff0c\u671d\u7740\u891a\u7384\u5c31\u662f\u4e00\u811a\uff0c\u891a\u7384\u4e00\u95ea\u8df3\u8d77\uff0c\u4e00\u7167\u9762\u5c31\u7ed9\u90a3\u5927\u6c49\u64d2\u7740\uff0c\u5468\u5c71 \u6c11\u5927\u559c\u53eb\u9053\uff1a\u201c\u6bd5\u8d24\u5f1f\uff01\u201d\u90a3\u5927\u6c49\u53eb\u9053\uff1a\u201c\u662f\u5468\u5927\u54e5\u5417\uff1f\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u5e95\u5374\u662f\u6781\u4e3a\u72e0\u8fa3\uff0c \u627c\u7740\u891a\u7384\u7684\u54bd\u5589\u4e00\u634f\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u5c06\u4ed6\u629b\u4e0b\u5c71\u9e93\uff0c\u891a\u7384\u8fde\u53eb\u4e5f\u53eb\u4e0d\u51fa\u6765\uff0c\u4fbf\u7ed9\u4ed6\u6454\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u9633\u5b97\u6d77\u6b63\u5728\u5f97\u624b\uff0c\u62db\u62db\u8fdb\u8feb\uff0c\u5360\u5c3d\u4e0a\u98ce\uff0c\u5ffd\u89c1\u6bd5\u64ce\u5929\u6740\u5230\uff0c\u53ea\u4e00\u7167\u9762\u5c31\u5c06\u891a\u7384\u6454\u6b7b\uff0c\u4e0d \u7531\u5f97\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6bd5\u64ce\u5929\u3001\u6f6e\u97f3\u548c\u5c1a\u4e0e\u4e8e\u627f\u73e0\u4e09\u4eba\uff0c\u82e5\u7136\u5355\u6253\u72ec\u6597\uff0c\u90fd\u4e0d\u662f\u6211\u7684 \u5bf9\u624b\uff0c\u4f46\u82e5\u8054\u624b\u653b\u6211\uff0c\u90a3\u6211\u53ef\u662f\u96be\u4e8e\u62b5\u654c\u3002\u201d\u9633\u5b97\u6d77\u6b64\u4eba\u4e45\u7ecf\u9635\u4ed7\uff0c\u62b1\u5b9a\u80fd\u80dc\u5219\u6218\uff0c\u4e0d\u80fd\u80dc \u5373\u8d70\u7684\u4e3b\u610f\uff0c\u731b\u653b\u4e09\u5251\uff0c\u5c06\u6f6e\u97f3\u548c\u5c1a\u903c\u9000\u51e0\u6b65\uff0c\u9661\u5730\u98de\u8eab\u4fbf\u8d70\uff0c\u6f6e\u97f3\u548c\u5c1a\u6c14\u5f97\u5927\u547c\u5c0f\u53eb\uff0c\u5374 \u8ffd\u4ed6\u4e0d\u4e0a\u3002\u4ed6\u90a3\u5339\u9a6c\u662f\u5927\u5185\u5fa1\u9a6c\uff0c\u4e00\u4e0a\u9a6c\u80cc\uff0c\u8f6c\u77ac\u4e4b\u95f4\u4fbf\u5954\u4e0b\u5c71\u5761\uff0c\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u4e0e\u6f6e\u97f3\u548c\u5c1a\u89c1\u8fc7\uff0c\u5404\u9053\u4ef0\u6155\u4e4b\u5ff1\u3002\u5468\u5c71\u6c11\u9053\uff1a\u201c\u6bd5\u8d24\u5f1f\uff0c\u4f60\u662f\u600e\u4e48\u6765\u7684\uff1f\u201d\u6bd5\u64ce \u5929\u9053\uff1a\u201c\u6211\u542c\u8bf4\u5927\u54e5\u5357\u4e0b\uff0c\u6b22\u559c\u4e4b\u6781\u3002\u53ea\u662f\u6709\u4e9b\u7410\u4e8b\u5728\u8eab\uff0c\u4e0d\u80fd\u65e9\u6765\u8fce\u63a5\u3002\u6240\u4ee5\u5148\u6d3e\u6bd5\u613f\u7a77 \u6765\uff0c\u4ed6\u548c\u4f60\u5df2\u89c1\u8fc7\u4e86\u3002\u201d\u5468\u5c71\u6c11\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u89c1\u8fc7\u4e86\u3002\u8fd9\u6b21\u54b1\u4eec\u53ef\u6298\u635f\u4e86\u4e0d\u5c11\u4eba\u5462\u3002\u201d\u6bd5\u64ce \u5929\u9053\uff1a\u201c\u5927\u54e5\u5bbd\u5fc3\uff0c\u9664\u4e86\u6709\u9650\u51e0\u4eba\u5916\uff0c\u5176\u4f59\u7684\u4eba\u6211\u90fd\u6551\u51fa\u6765\u4e86\u3002\u201d\u5468\u5c71\u6c11\u5927\u559c\u9053\uff1a\u201c\u4f60\u662f\u600e \u4e48\u6551\u51fa\u6765\u7684\uff1f\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u6211\u548c\u5341\u4e09\u5bb6\u5be8\u4e3b\u5feb\u9a6c\u9a91\u6765\uff0c\u6b63\u597d\u78b0\u7740\u5b98\u519b\uff0c\u5927\u6740\u4e00\u987f\uff0c\u5b98\u519b\u4e4b \u4e2d\uff0c\u53ea\u6709\u90a3\u5a04\u6850\u5b59\u6700\u4e3a\u5389\u5bb3\uff0c\u5176\u4f59\u7684\u4eba\uff0c\u5374\u963b\u6321\u54b1\u4eec\u4e0d\u4f4f\u3002\u5a04\u6850\u5b59\u89c1\u52bf\u4e0d\u597d\uff0c\u7acb\u523b\u64a4\u9000\uff0c\u88ab \u4fd8\u864f\u4e86\u7684\u5f1f\u5144\uff0c\u5927\u534a\u90fd\u6551\u56de\u6765\u4e86\u3002\u6211\u542c\u8bf4\u4f60\u4eec\u5411\u8fd9\u8fb9\u9000\u8d70\uff0c\u5c31\u8ddf\u7740\u9a6c\u8ff9\u5bfb\u6765\u3002\u201d\u5468\u5c71\u6c11\u9053\uff1a \u201c\u90a3\u8001\u638c\u67dc\u5462\uff1f\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u6551\u56de\u6765\u4e86\u3002\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u90a3\u5b5f\u957f\u751f\u5462\uff1f\u4ed6\u662f\u90ed\u8001\u82f1\u96c4\u7684\u5f1f \u5b50\u3002\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u4ed6\u53d7\u4f24\u6700\u91cd\uff0c\u4e0d\u80fd\u8df3\u8dc3\uff0c\u88ab\u62bc\u5728\u56da\u8f66\u4e4b\u4e2d\uff0c\u5a04\u6850\u5b59\u4eb2\u81ea\u628a\u5b88\uff0c\u6551\u4e0d\u51fa \u6765\u3002\u201d\u5468\u5c71\u6c11\u4e00\u559c\u4e00\u5fe7\uff0c\u9eef\u7136\u4e0d\u8bed\u3002\u6bd5\u64ce\u5929\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u53ea\u8981\u54b1\u4eec\u5144\u5f1f\u8054\u76df\uff0c\u5927\u660e\u7684\u6c5f\u5c71 \u90fd\u662f\u6211\u4eec\u7684\uff0c\u4f55\u4e89\u5728\u4e00\u4e2a\u5b5f\u957f\u751f\u3002\u201d\u4e8e\u627f\u73e0\u542c\u6765\u751a\u4e0d\u987a\u8033\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u5374\u53c8\u5fcd\u4f4f\u3002\u6bd5\u64ce\u5929\u773c \u5149\u4e00\u8f6c\uff0c\u6b63\u5bf9\u7740\u5979\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u4e8e\u59d1\u5a18\uff0c\u54b1\u4eec\u53c8\u78b0\u4e0a\u4e86\uff0c\u8fd9\u53ef\u771f\u662f\u673a\u7f18\u5475\uff01\u8fd9\u56de\u4f60\u53ef \u53c2\u52a0\u54b1\u4eec\u7684\u76df\u7ea6\u4e86\u5427\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u770b\u4e86\u4e8e\u627f\u73e0\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u539f\u6765\u53c8\u662f\u4e2a\u5973\u626e\u7537\u88c5\u7684\uff0c\u771f\u50cf \u5f53\u5e74\u7684\u4e91\u857e\u3002\u4f60\u8fd9\u624b\u6697\u5668\u662f\u5979\u6559\u7684\u5427\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u8fd9\u4e00\u6253\u5c94\uff0c\u5c06\u4e8e\u627f\u73e0\u5f15\u53bb\u548c\u4ed6\u8bf4\u8bdd\uff0c\u6bd5\u64ce \u5929\u751a\u4e0d\u9ad8\u5174\uff0c\u597d\u4e0d\u5bb9\u6613\u7b49\u5230\u4e2a\u7a7a\u513f\uff0c\u53c8\u67d4\u58f0\u95ee\u9053\uff1a\u201c\u5730\u56fe\u7684\u4e8b\u51fa\u53ef\u95ee\u8fc7\u4f60\u5e08\u7236\u4e86\u5417\uff1f\u8fd9\u53ef\u662f \u6709\u5173\u5929\u4e0b\u7684\u4e00\u5f20\u5730\u56fe\u5475\uff01\u201d\u4e8e\u627f\u73e0\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e89\u5929\u4e0b\u7d27\u8981\u8fd8\u662f\u6551\u8001\u767e\u59d3\u7d27\u8981\uff1f\u201d\u6bd5\u64ce\u5929\u6014 \u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u8bdd\u751a\u4e48\u610f\u601d\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u53eb\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u627f\u73e0\u7684\u8bdd\u548c\u5979\u5e08\u7236\u7684\u8bdd\u4e00\u6837\u3002\u4e39 \u67ab\u7684\u610f\u601d\u4e5f\u662f\u8bf7\u4f60\u4eec\u5148\u53d1\u5175\u53bb\u6551\u4e1c\u5357\u6cbf\u6d77\u7684\u767e\u59d3\uff0c\u8fd1\u6765\u502d\u5bc7\u6b63\u5728\u6cbf\u6d77\u4e00\u5e26\u8086\u8650\uff0c\u4f60\u4e0d\u77e5\u9053 \u5417\uff1f\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u502d\u5bc7\u7663\u75a5\u4e4b\u60a3\u800c\u5df2\uff01\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u7663\u75a5\u82e5\u7136\u4e0d\u7406\uff0c\u4fbf\u6210\u5927\u60a3\u3002\u4f55\u51b5 \u4e5f\u672a\u5fc5\u662f\u7663\u75a5\u5462\uff01\u6bd5\u5144\uff0c\u6211\u521a\u4ece\u53f0\u5dde\u56de\u6765\uff0c\u6211\u7ed9\u4f60\u8bf4\u8bf4\u90a3\u8fb9\u502d\u5bc7\u7684\u60c5\u5f62\uff0c\u5509\uff0c\u90a3\u8fb9\u53ef\u771f\u662f\u60e8 \u5462\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u6572\u4e86\u4e00\u4e0b\u5149\u5934\u9053\uff1a\u201c\u4f60\u770b\uff0c\u6211\u597d\u7cca\u6d82\uff0c\u5468\u5be8\u4e3b\u592b\u5987\u90fd\u8981\u6b47\u606f\u3002\u54b1\u4eec\u8fd8\u662f\u56de \u5230\u90a3\u5bb6\u730e\u6237\u5bb6\u8bf4\u5427\u3002\u201d \u3000\u3000\u5230\u4e86\u90a3\u5bb6\u730e\u6237\uff0c\u4e8e\u627f\u73e0\u63a8\u8bf4\u75b2\u5026\uff0c\u5148\u53bb\u7761\u4e86\u3002\u5468\u5c71\u6c11\u5fcd\u7740\u5251\u4f24\uff0c\u5374\u662f\u7cbe\u795e\u5955\u5955\uff0c\u548c\u6bd5\u64ce \u5929\u3001\u6f6e\u97f3\u548c\u5c1a\u7b49\u5728\u5927\u5385\u8c08\u8bba\u6297\u502d\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u54ea\u7761\u5f97\u7740\uff0c\u53ea\u542c\u5f97\u6f6e\u97f3\u548c\u5c1a\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6bd5\u5be8\u4e3b\u6bd5\u8001\u5f1f\u5440\uff0c\u53ea\u8981\u4f60\u5230\u6cbf\u6d77\u4e00\u5e26\u770b \u770b\uff0c\u4f60\u975e\u5f97\u6c14\u70b8\u809a\u76ae\uff0c\u53d1\u7ad6\u76ee\u88c2\u4e0d\u53ef\u3002\u90a3\u4e9b\u502d\u5bc7\u7b80\u76f4\u4e0d\u662f\u4eba\uff0c\u6740\u4eba\u63b3\u4eba\uff0c\u90a3\u662f\u4e0d\u6d88\u8bf4\u4e86\uff0c\u4ed6 \u4eec\u8fde\u5b69\u5b50\u4e5f\u6740\uff0c\u6211\u5c31\u4eb2\u773c\u89c1\u8fc7\uff0c\u6709\u5341\u51e0\u540d\u502d\u5bc7\uff0c\u7528\u4e00\u5927\u9505\u6cb8\u6c64\u6dcb\u4e00\u5927\u7fa4\u4e09\u51e0\u5c81\u7684\u5a74\u5b69\uff0c\u5a74\u5b69 \u4eec\u547c\u53f7\u54c0\u53eb\u6323\u624e\uff0c\u90a3\u4e9b\u502d\u5bc7\u8fd8\u5728\u65c1\u8fb9\u62cd\u624b\u7b11\u4e50\u5462\u3002\u90a3\u5929\u53ef\u60dc\u6211\u5230\u8fdf\u4e00\u6b65\uff0c\u867d\u7136\u628a\u90a3\u4e9b\u502d\u5bc7\u90fd \u6253\u6740\u4e86\uff0c\u90a3\u7fa4\u5a74\u5b69\u5374\u6551\u4e0d\u56de\u6765\u4e86\u3002\u6211\u4e3a\u6b64\u597d\u51e0\u4e2a\u665a\u4e0a\u7761\u4e0d\u7740\u89c9\u3002\u4e00\u822c\u7684\u6d77\u76d7\uff0c\u5fd7\u5728\u52ab\u63a0\uff0c\u52ab \u63a0\u4e4b\u540e\uff0c\u4fbf\u547c\u5578\u800c\u53bb\uff1b\u502d\u5bc7\u5462\uff0c\u53ef\u6bd4\u4e00\u822c\u7684\u6d77\u76d7\u72e0\u6bd2\u4e0a\u767e\u500d\u5343\u500d\uff0c\u4ed6\u4eec\u52ab\u63a0\u4e4b\u540e\uff0c\u8fd8\u8981\u63b3 \u4eba\uff0c\u8fd8\u8981\u5978\u6deb\uff0c\u8fd8\u8981\u6b8b\u6740\uff0c\u6700\u540e\u8fd8\u8981\u653e\u4e00\u628a\u706b\u5c06\u6751\u5e84\u70e7\u5149\uff0c\u4f60\u8bf4\u7a0d\u6709\u70b9\u8840\u6027\u7684\u4eba\uff0c\u80fd\u770b\u5f97\u8fc7 \u53bb\u4e48\uff1f\u6bd5\u8001\u5f1f\uff0c\u4f60\u53d1\u4e0d\u53d1\u5175\uff1f\u201d \u3000\u3000\u6bd5\u64ce\u5929\u6c89\u541f\u534a\u664c\uff0c\u9053\uff1a\u201c\u53d1\u5175\u90a3\u662f\u5e94\u5f53\u7684\u3002\u8bf7\u95ee\u6f6e\u97f3\u53d4\u53d4\uff0c\u502d\u5bc7\u7684\u5389\u5bb3\u4e4b\u5904\u5728\u4ec0\u4e48\u5730 \u65b9\uff1f\u4ed6\u4eec\u4e4b\u4e2d\u4e5f\u6709\u6b66\u529f\u9ad8\u660e\u4e4b\u58eb\u4e48\uff1f\u54b1\u4eec\u53ef\u5f97\u77e5\u5df1\u77e5\u5f7c\u3002\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u502d\u5bc7\u5176\u5b9e\u4e5f\u6ca1\u6709 \u4ec0\u4e48\u5389\u5bb3\u4e4b\u5904\uff0c\u4f46\u4ed6\u4eec\u5206\u6210\u6570\u5341\u5c0f\u80a1\uff0c\u5f7c\u6b64\u547c\u5e94\uff0c\u5012\u4e0d\u50cf\u662f\u4e4c\u5408\u4e4b\u4f17\uff0c\u4ed6\u4eec\u5230\u5904\u6d41\u7a9c\uff0c\u5b98\u519b \u53c8\u53ea\u662f\u5404\u5b88\u9632\u5730\uff0c\u4e0d\u80af\u62e6\u5835\uff0c\u53ea\u9760\u4e49\u6c11\u529e\u7684\u56e2\u7ec3\uff0c\u56e2\u7ec3\u53c8\u6ca1\u6709\u7edf\u4e00\u6307\u6325\uff0c\u90a3\u5c31\u5f80\u5f80\u5403\u4e86\u502d\u5bc7 \u7684\u4e8f\u4e86\u3002\u8fd8\u6709\u4ed6\u4eec\u6240\u7528\u7684\u502d\u5200\u5374\u662f\u6bd4\u4e00\u822c\u6211\u4eec\u6240\u7528\u7684\u5200\u5251\u950b\u5229\uff0c\u5728\u77ed\u5175\u76f8\u63a5\u8089\u640f\u4e4b\u9645\uff0c\u56e2\u7ec3 \u4e5f\u5f80\u5f80\u62db\u67b6\u4e0d\u4f4f\uff0c\u6240\u4ee5\u6211\u8bf4\u975e\u5f97\u6709\u4e00\u73ed\u5b66\u8fc7\u6280\u51fb\uff0c\u50cf\u4f60\u4eec\u7684\u90e8\u4e0b\u90a3\u6837\u7cbe\u9510\u4e4b\u5e08\u53bb\u62b5\u6321\u4ed6\u4eec\u4e0d \u53ef\u3002\u8bf4\u5230\u6b66\u529f\uff0c\u542c\u8bf4\u65e5\u672c\u7684\u6b66\u58eb\u591a\u7ec3\u6709\u2018\u67d4\u9053\u2019\u548c\u2018\u5251\u9053\u2019\uff0c\u67d4\u9053\u6709\u70b9\u50cf\u6211\u4eec\u4e2d\u56fd\u7684\u592a\u6781 \u62f3\uff0c\u5251\u9053\u5373\u662f\u65e5\u672c\u7684\u5251\u6cd5\uff0c\u4ee5\u5288\u523a\u4e3a\u4e3b\uff0c\u770b\u6765\u4e5f\u4f3c\u662f\u4ece\u4e2d\u56fd\u4f20\u8fc7\u53bb\u7684\uff0c\u4f46\u548c\u4e2d\u539f\u4e60\u89c1\u7684\u5251\u6cd5 \u5374\u53c8\u4e0d\u5c3d\u76f8\u540c\u3002\u542c\u8bf4\u4ed6\u4eec\u7684\u67d4\u9053\u5251\u9053\uff0c\u90fd\u4ee5\u201c\u6bb5\u201d\u5206\u7ea7\uff0c\u6700\u9ad8\u7684\u4e5d\u6bb5\uff0c\u6211\u6253\u8d25\u8fc7\u4e24\u4e2a\u4e94\u6bb5 \u7684\uff0c\u4e5d\u6bb5\u7684\u9ad8\u624b\uff01\u5374\u672a\u66fe\u78b0\u8fc7\u3002\u201d \u3000\u3000\u6bd5\u64ce\u5929\u53c8\u662f\u4e00\u9635\u6c89\u541f\uff0c\u5468\u5c71\u6c11\u9053\uff1a\u201c\u5c31\u7b97\u502d\u5bc7\u6700\u5389\u5bb3\uff0c\u54b1\u4eec\u4e5f\u975e\u53bb\u4e0d\u53ef\u3002\u6f6e\u97f3\u5927\u5e08\uff0c\u4f60 \u7684\u9a6c\u5feb\uff0c\u6211\u5c06\u6211\u7684\u4ee4\u7bad\u4ea4\u4ed8\u4e0e\u4f60\uff0c\u53e6\u5916\u518d\u7ed9\u4f60\u4e00\u5c01\u4e66\u4fe1\uff0c\u4f60\u7ed9\u6211\u8d76\u5230\u96c1\u95e8\u5173\u5916\uff0c\u4f20\u6211\u7684\u4ee4 \u7bad\u3002\u53eb\u4ed6\u4eec\u7acb\u5373\u6df7\u8fdb\u5173\u6765\uff0c\u5230\u6d59\u6c5f\u4e49\u4e4c\u96c6\u5408\uff0c\u90a3\u65f6\u6211\u7684\u4f24\u4e5f\u5b9a\u7136\u517b\u597d\u4e86\u3002\u6211\u4eb2\u81ea\u5e26\u4ed6\u4eec\u53bb\uff0c \u96c1\u95e8\u5173\u5916\u7684\u8001\u5de2\uff0c\u7559\u4e0b\u5341\u5206\u4e00\u4e8c\u7684\u4eba\u529b\uff0c\u6211\u770b\u4e5f\u5c31\u591f\u4e86\u3002\u201d\u6f6e\u97f3\u548c\u5c1a\u5927\u58f0\u53eb\u597d\uff0c\u53c8\u9053\uff1a\u201c\u6bd5 \u8001\u5f1f\uff0c\u4f60\u5462\uff1f\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u90a3\u5f53\u7136\u8981\u8feb\u968f\u9aa1\u5c3e\u4e86\u3002\u4e0d\u8fc7\uff0c\u6709\u4e00\u4ef6\u4e8b\u54b1\u4eec\u53ef\u5f97\u5148\u5546\u91cf\u597d\u3002\u201d \u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u521a\u624d\u542c\u4f60\u6240\u8bf4\uff0c\u6cbf\u6d77\u7684\u4e49\u6c11\u548c\u5730\u65b9\u7684\u56e2\u7ec3\u90fd\u662f\u5404\u81ea \u4e3a\u6218\uff0c\u8fd9\u4e0d\u5927\u597d\u3002\u54b1\u4eec\u5f97\u63a8\u4e00\u4e2a\u9996\u9886\u3002\u6309\u8bf4\u4ee5\u5468\u5927\u54e5\u7684\u8d44\u671b\uff0c\u90a3\u662f\u6700\u9002\u5f53\u4e0d\u8fc7\u7684\u4e86\u3002\u4f46\u5f53\u5730 \u7684\u53f6\u5b97\u7559\u3001\u90d1\u8302\u4e03\u7b49\u4eba\uff0c\u4e0d\u77e5\u9053\u80af\u5fc3\u670d\u4e48\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u5927\u53eb\u9053\uff1a\u201c\u8c01\u80af\u6297\u502d\uff0c\u6211\u5c31\u670d\u8c01\u3002\u8c01 \u505a\u9996\u9886\uff0c\u8fd9\u6709\u4ec0\u4e48\u95ee\u9898\u3002\u201d\u6bd5\u64ce\u5929\u5927\u7b11\u9053\uff1a\u201c\u6253\u4ed7\u7684\u4e8b\uff0c\u6ca1\u6709\u7edf\u5e05\uff0c\u90a3\u53ef\u4e0d\u884c\u3002\u4f55\u51b5\u54b1\u4eec\u8fd8 \u4e0d\u53ea\u662f\u6253\u5e73\u502d\u5bc7\u5c31\u7b97\u4e86\u5462\u3002\u8fd9\u7edf\u5e05\u662f\u975e\u5148\u63a8\u5b9a\u4e0d\u53ef\u7684\uff0c\u5468\u5927\u54e5\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u86c7\u65e0\u5934\u800c\u4e0d\u884c\uff0c\u8fd9\u5012\u662f\u771f\u7684\u3002\u81f3\u4e8e\u8c01\u505a\u5934\u513f\uff0c\u6211\u53ef\u6ca1\u6709\u610f\u89c1\u3002\u201d\u6bd5\u64ce\u5929 \u9053\uff1a\u201c\u90a3\u81ea\u7136\u8be5\u662f\u5468\u5927\u54e5\u4e86\uff0c\u91d1\u5200\u5be8\u4e3b\u7684\u5a01\u540d\uff0c\u5929\u4e0b\u8c01\u4eba\u4e0d\u77e5\uff1f\u201d\u5468\u5c71\u6c11\u9053\uff1a\u201c\u4e0d\uff0c\u8bba\u5230\u96c4 \u624d\u5927\u7565\uff0c\u6bd5\u8d24\u5f1f\uff0c\u4f60\u80dc\u6211\u8fdc\u751a\uff0c\u800c\u4e14\u4f60\u662f\u5317\u4e94\u7701\u7684\u5927\u9f99\u5934\uff0c\u8fd9\u4e94\u7701\u7684\u7eff\u6797\u548c\u5e2e\u4f1a\u90fd\u542c\u4f60\u7684\uff0c \u4f60\u65e2\u5f97\u5730\u5229\uff0c\u53c8\u5f97\u4eba\u548c\uff0c\u82e5\u8981\u63a8\u4e3e\u6297\u502d\u7684\u9996\u9886\uff0c\u975e\u8d24\u5f1f\u4e0d\u53ef\u3002\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u5c0f\u5f1f\u6258\u5e87\uff0c\u505a \u4e86\u4e2a\u5927\u9f99\u5934\uff0c\u4f46\u5927\u54e5\u5728\u524d\uff0c\u5c0f\u5f1f\u5c82\u6562\u78e8\u8d8a\uff1f\u4f55\u51b5\uff0c\u82e5\u8bba\u5230\u5730\u5229\u4eba\u548c\uff0c\u5f53\u5730\u7684\u53f6\u5b97\u7559\u548c\u90d1\u8302\u4e03 \u7b49\u4eba\uff0c\u90a3\u53c8\u8981\u6bd4\u5c0f\u5f1f\u66f4\u5f3a\u4e86\u3002\u201d\u6f6e\u97f3\u548c\u5c1a\u5927\u7b11\u9053\uff1a\u201c\u53c8\u4e0d\u662f\u505a\u7687\u5e1d\uff0c\u4f55\u5fc5\u4f60\u63a8\u6211\u8ba9\u3002\u4f9d\u6211\u8bf4 \u5f3a\u9f99\u4e0d\u538b\u5730\u5934\u86c7\uff0c\u54c8\uff0c\u6211\u8bf4\u9519\u5566\uff0c\u6bd5\u8001\u5f1f\u662f\u540d\u6b63\u8a00\u987a\u7684\u5927\u9f99\u5934\uff0c\u600e\u80fd\u6bd4\u4f5c\u5730\u5934\u86c7\u4e86\uff0c\u6211\u7684\u610f \u601d\u662f\uff0c\u6bd5\u8001\u5f1f\u662f\u4e3b\uff0c\u5468\u5be8\u4e3b\u662f\u5ba2\uff0c\u82e5\u8981\u63a8\u4e3e\u9996\u9886\uff0c\u6bd5\u8001\u5f1f\u8be5\u5f53\u4ec1\u4e0d\u8ba9\u3002\u81f3\u4e8e\u53f6\u5b97\u7559\u548c\u90d1\u8302 \u4e03\uff0c\u4ed6\u4eec\u65e9\u5c31\u8bf4\u8fc7\uff0c\u4e0d\u8bba\u662f\u6bd5\u5927\u9f99\u5934\u6216\u5468\u5c11\u5be8\u4e3b\u5230\u6765\uff0c\u4ed6\u4eec\u90fd\u5fe0\u8bda\u62e5\u6234\u3002\u6bd5\u8001\u5f1f\uff0c\u4e0d\u5fc5\u591a\u8bf4 \u5566\uff0c\u4f60\u5c31\u662f\u8981\u505a\u7687\u5e1d\uff1b\u6211\u6f6e\u97f3\u548c\u5c1a\u4e5f\u4e00\u5200\u4fdd\u4f60\u767b\u57fa\u3002\u201d\u4e09\u4eba\u90fd\u54c8\u54c8\u5927\u7b11\uff0c\u6bd5\u64ce\u5929\u5c24\u5176\u7b11\u5f97\u54cd \u4eae\uff0c\u4e0d\u77e5\u662f\u7b11\u6f6e\u97f3\u548c\u5c1a\u7684\u6206\u76f4\uff0c\u8fd8\u662f\u4e3a\u4ed6\u7684\u4e00\u529b\u63a8\u6234\u800c\u5f97\u610f\u3002\u4e8e\u627f\u73e0\u5728\u91cc\u623f\u53ea\u542c\u5f97\u4ed6\u6545\u610f\u8c26 \u8ba9\u4e86\u4e00\u756a\uff0c\u7ec8\u4e8e\u7b54\u5141\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u77ac\u95f4\uff0c\u4e8e\u627f\u73e0\u9661\u7136\u89c9\u5f97\u5fc3\u4e2d\u4f5c\u95f7\uff0c\u6bd5\u64ce\u5929\u90a3\u7c97\u65f7\u7684\u7b11\u58f0\uff0c\u8d8a\u53d1\u60f9\u8d77\u5979\u7684\u53cd\u611f\u3002\u4e8e\u627f \u73e0\u4e0d\u7531\u5f97\u6697\u81ea\u60f3\u9053\uff1a\u201c\u539f\u6765\u6b64\u4eba\u8c8c\u4f3c\u7c97\u8c6a\uff0c\u5374\u4e5f\u751a\u6709\u673a\u5fc3\u3002\u4ed6\u660e\u660e\u8981\u505a\u5934\u513f\uff0c\u5374\u504f\u6709\u8bb8\u591a\u505a \u4f5c\uff01\u4e0d\u8fc7\u4ed6\u80af\u6d3e\u9063\u5317\u4e94\u7701\u7684\u7eff\u6797\u8c6a\u6770\u6297\u502d\uff0c\u90a3\u5012\u662f\u5927\u529f\u4e00\u4ef6\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u5916\u9762\u6f6e\u97f3\u548c\u5c1a\u53c8\u9053\uff1a\u201c\u6551\u5175\u5982\u6551\u706b\uff0c\u660e\u65e5\u6211\u4fbf\u8d76\u5f80\u96c1\u95e8\u5173\u5916\uff0c\u4f20\u5468\u5be8\u4e3b\u7684\u7eff\u6797 \u7bad\u3002\u6bd5\u8001\u5f1f\uff0c\u4f60\u660e\u65e5\u4e5f\u5148\u5e26\u968f\u4f60\u6765\u7684\u4eba\u5230\u53f0\u5dde\u53bb\u5427\u3002\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u54ea\u6709\u5982\u6b64\u7b80\u5355\uff0c\u6211\u8fd8\u8981 \u56de\u5c71\u4e1c\u672c\u5be8\uff0c\u53ec\u96c6\u5404\u5bb6\u5be8\u4e3b\uff0c\u5404\u8def\u9f99\u5934\uff0c\u518d\u8bf4\u6211\u8fd9\u5927\u9f99\u5934\u7684\u804c\u52a1\uff0c\u4e5f\u5f97\u7a0d\u4f5c\u4ea4\u4ee3\u624d\u884c\u5440\u3002\u201d \u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u4f60\u6d3e\u4eba\u56de\u53bb\u4f20\u4ee4\u4e0d\u884c\u5417\uff1f\u201d\u6bd5\u64ce\u5929\u7b11\u9053\uff1a\u201c\u6b64\u7b49\u4e8b\u60c5\uff0c\u5c82\u80fd\u9063\u4eba\u66ff\u4ee3\uff1f\u201d\u6f6e \u97f3\u548c\u5c1a\u9053\uff1a\u201c\u53f0\u5dde\u7684\u6297\u502d\u4e49\u519b\uff0c\u5f62\u52bf\u5371\u6025\uff0c\u6700\u5c11\u4e5f\u5f97\u6709\u4e2a\u5f97\u529b\u7684\u4eba\u524d\u5f80\u62a5\u8baf\uff0c\u597d\u632f\u594b\u519b\u5fc3\u624d \u662f\u3002\u201d\u5468\u5c71\u6c11\u82e6\u7b11\u9053\uff1a\u201c\u8c01\u53bb\u5462\uff1f\u201d\u77f3\u7fe0\u51e4\u9053\uff1a\u201c\u6211\u53bb\uff01\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u4f60\u8981\u5b88\u62a4\u5468\u5927 \u54e5\uff0c\u5982\u4f55\u80fd\u53bb\uff1f\u201d \u3000\u3000\u6bd5\u64ce\u5929\u8e0c\u8e87\u4e0d\u8bed\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u6e05\u8106\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u6211\u53bb\uff01\u201d\u5374\u539f\u6765\u662f\u4e8e\u627f\u73e0\u8d70\u4e86\u51fa\u6765\uff0c \u6bd5\u64ce\u5929\u62ac\u5934\u4e00\u671b\uff0c\u548c\u5979\u773c\u5149\u78b0\u4e2a\u6b63\u7740\u3002\u6bd5\u64ce\u5929\u5fc3\u4e2d\u4e00\u8361\uff0c\u60f3\u9053\uff1a\u201c\u82e5\u7559\u5f97\u5979\u5728\u6211\u8eab\u8fb9\u505a\u4e2a\u5e2e \u624b\uff0c\u90a3\u771f\u662f\u6700\u597d\u4e0d\u8fc7\uff01\u201d \u3000\u3000\u201c\u90a3\u771f\u662f\u6700\u597d\u4e0d\u8fc7\uff01\u201d\u6f6e\u97f3\u548c\u5c1a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u600e\u4e48\u6ca1\u6709\u60f3\u8d77\u4f60\u8fd9\u4e2a\u5a03\u5a03\uff1f\u54c8\u54c8\uff0c\u4f60\u80af \u53bb\u90a3\u771f\u662f\u518d\u597d\u4e0d\u8fc7\uff01\u201d\u6bd5\u64ce\u5929\u88ab\u6f6e\u97f3\u548c\u5c1a\u7684\u7b11\u58f0\u60ca\u9192\uff0c\u53ea\u89c1\u4e8e\u627f\u73e0\u51b7\u51b7\u5730\u770b\u7740\u81ea\u5df1\uff0c\u7f13\u7f13\u8bf4 \u9053\uff1a\u201c\u6bd5\u5927\u9f99\u5934\uff0c\u70e6\u4f60\u4fee\u4e66\u4e00\u5c01\uff0c\u590d\u53f0\u5dde\u6297\u502d\u7684\u4e49\u519b\u9996\u9886\uff0c\u597d\u8ba9\u4ed6\u4eec\u5f97\u77e5\uff0c\u63f4\u5175\u5c31\u8981\u5230\u6765\u3002 \u6211\u9a6c\u4e0a\u7ed9\u4f60\u9001\u5230\u53f0\u5dde\u53bb\u3002\u201d \u3000\u3000\u201c\u8fd9\u5bb6\u4e3b\u4eba\u4e0d\u662f\u4e2a\u666e\u901a\u7684\u730e\u6237\uff0c\u4ed6\u8fd8\u7cbe\u4e8e\u6b67\u9ec4\u4e4b\u672f\uff0c\u662f\u4e2a\u533b\u751f\uff0c\u5bb6\u4e2d\u5907\u6709\u7eb8\u7b14\uff0c\u542c\u4e86\u4e8e \u627f\u73e0\u7684\u8bf4\u8bdd\uff0c\u7acb\u523b\u62ff\u6765\uff0c\u653e\u5728\u6bd5\u64ce\u5929\u7684\u9762\u524d\uff0c\u6bd5\u64ce\u5929\u53cc\u773c\u4e00\u626b\uff0c\u53ea\u89c1\u4f17\u4eba\u7684\u773c\u5149\uff0c\u90fd\u5728\u671b\u7740 \u4e8e\u627f\u73e0\uff0c\u4e8e\u627f\u73e0\u7684\u795e\u8272\u5341\u5206\u5e73\u9759\uff0c\u6709\u4e00\u79cd\u4ee4\u4eba\u8083\u7136\u8d77\u656c\u7684\u5149\u8f89\uff0c\u6bd5\u64ce\u5929\u7981\u4e0d\u4f4f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9762 \u5bf9\u7740\u8fd9\u6837\u4e00\u4e2a\u5c11\u5973\uff0c\u65e2\u662f\u7231\u6155\uff0c\u53c8\u662f\u656c\u4f69\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u5b64\u8eab\u5973\u5b50\uff0c\u4e3a\u4e86\u6297\u502d\uff0c\u7adf\u6562 \u8eab\u5165\u864e\u72fc\u4e4b\u5730\uff0c\u5343\u91cc\u4f20\u4e66\uff0c\u6211\u662f\u4e2a\u81ea\u547d\u4e3a\u9876\u5929\u7acb\u5730\u7684\u82f1\u96c4\u8c6a\u6770\uff0c\u5c82\u53ef\u4e0d\u5982\u5979\u4e86\u3002\u201d\u8fd9\u4e00\u77ac \u95f4\uff0c\u5ffd\u89c9\u5f97\u81ea\u5df1\u9002\u624d\u7684\u60f3\u6cd5\uff0c\u60f3\u6c38\u8fdc\u7559\u7740\u5979\u5728\u81ea\u5df1\u8eab\u8fb9\u7684\u60f3\u6cd5\uff0c\u5341\u5206\u6e3a\u5c0f\uff0c\u4e00\u62ac\u5934\uff0c\u53c8\u78b0\u7740 \u4e8e\u627f\u73e0\u90a3\u5982\u5251\u4e00\u822c\u7684\u773c\u5149\uff0c\u597d\u50cf\u770b\u7a7f\u4e86\u4ed6\u7684\u80ba\u8151\uff0c\u6bd5\u64ce\u5929\u7981\u4e0d\u4f4f\u9762\u4e0a\u4e00\u70ed\uff0c\u614c\u5fd9\u4f4e\u4e0b\u4e86\u5934\uff0c \u907f\u5f00\u4e8e\u627f\u73e0\u7684\u773c\u5149\uff0c\u6293\u8d77\u72fc\u6beb\uff0c\u7acb\u523b\u5728\u7eb8\u4e0a\u75be\u4e66\uff0c\u4e0d\u4e00\u523b\u5c31\u628a\u4fe1\u5199\u597d\u4e86\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u6bd5\u8001\u5f1f\uff0c\u4f60\u4e5f\u7ed9\u6211\u5199\u4e24\u5c01\u4fe1\u3002\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u5199\u7ed9\u8c01\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a \u201c\u4e00\u5c01\u5199\u7ed9\u53f6\u5b97\u7559\uff0c\u5c31\u8bf4\u6211\u5230\u96c1\u95e8\u5173\u5916\u8bf7\u5175\uff0c\u53eb\u4ed6\u5b89\u5fc3\u3002\u53e6\u4e00\u5c01\u5199\u7ed9\u957f\u6c5f\u8fb9\u4e0a\u7684\u4e00\u4e2a\u821f \u5b50\u3002\u201d\u6bd5\u64ce\u5929\u8be7\u9053\uff1a\u201c\u4e00\u4e2a\u821f\u5b50\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u627f\u73e0\u4eba\u751f\u5730\u4e0d\u719f\uff0c\u4e5f\u5f97\u6709\u4eba\u5e26\u5979\u53bb\u89c1\u53f6 \u5b97\u7559\u5440\u3002\u8fd9\u4e2a\u821f\u5b50\u540d\u53eb\u5f20\u9ed1\uff0c\u4f4f\u5728\u9756\u6c5f\uff0c\u662f\u53f6\u5b97\u7559\u6d3e\u5728\u957f\u6c5f\u8fb9\u4e0a\uff0c\u4e13\u53f8\u8054\u7edc\u4e4b\u804c\u7684\u3002\u4f60\u8bf4\u8fd9 \u4e2a\u5c0f\u59d1\u5a18\u662f\u6211\u7684\u5e08\u4f84\u5f20\u4e39\u67ab\u7684\u5f92\u5f1f\uff0c\u53eb\u4ed6\u597d\u751f\u7167\u987e\u3002\u201d \u3000\u3000\u5f85\u5230\u6bd5\u64ce\u5929\u628a\u4fe1\u5199\u597d\uff0c\u6668\u66dd\u5df2\u900f\u8fdb\u7a97\u6237\uff0c\u4f17\u4eba\u4e00\u591c\u672a\u7761\uff0c\u53ea\u56e0\u80f8\u4e2d\u70ed\u8840\u6cb8\u817e\uff0c\u5374\u65e0\u4e1d\u6beb \u5026\u610f\u3002\u4e8e\u627f\u73e0\u5c06\u4fe1\u85cf\u597d\uff0c\u5411\u4f17\u4eba\u655b\u8eab\u4e00\u793c\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u6bd5\u5927\u9f99\u5934\uff0c\u591a\u8c22\u5468\u5be8\u4e3b\u548c\u5e08\u4f2f \u7956\uff0c\u6211\u5148\u8d70\u4e86\u3002\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u4f60\u5c31\u8d70\u4e86\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6551\u4eba\u5982\u6551\u706b\uff0c\u5929\u5feb\u4eae\u4e86\uff0c\u6211\u4e0d \u8d70\u5f85\u4f55\uff1f\u201d\u4f17\u4eba\u9001\u51fa\u95e8\u6765\uff0c\u4e8e\u627f\u73e0\u8de8\u4e0a\u767d\u9a6c\uff0c\u4fbf\u5728\u6668\u5149\u66e6\u5fae\u4e4b\u4e2d\uff0c\u626c\u97ad\u4e1c\u53bb\u3002\u6bd5\u64ce\u5929\u597d\u751f\u60cb \u60dc\uff0c\u4f46\u5374\u600e\u8bf4\u5f97\u51fa\u53e3\u8981\u5c06\u5979\u7559\u4f4f\uff1f \u3000\u3000\u7167\u591c\u72ee\u5b50\u9a6c\u65e5\u884c\u5343\u91cc\uff0c\u4e24\u65e5\u4e4b\u540e\uff0c\u5c31\u5230\u4e86\u957f\u6c5f\u8fb9\uff0c\u4f46\u89c1\u70df\u6ce2\u6d69\u6e3a\uff0c\u6c34\u5929\u76f8\u63a5\uff0c\u6c5f\u6d9b\u6eda \u6eda\uff0c\u4e8e\u627f\u73e0\u987f\u89c9\u80f8\u895f\u5f00\u9614\uff0c\u90ce\u58f0\u541f\u9053\uff1a\u201c\u5927\u6c5f\u4e1c\u53bb\uff0c\u6d6a\u6dd8\u5c3d\u591a\u5c11\u82f1\u96c4\u8c6a\u6770\u201d\u3002\u60f3\u8d77\u5f20\u58eb\u8bda\u5f53 \u5e74\u4e0e\u6731\u5143\u748b\u5728\u957f\u6c5f\u51b3\u6218\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u5341\u5206\u611f\u6168\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u5230\u4e86\u9756\u6c5f\uff0c\u4f9d\u7740\u4f4f\u5740\u5728\u4e1c\u95e8\u4e4b\u5916\u627e\u5230\u4e86\u90a3\u4e2a\u821f\u5b50\u5f20\u9ed1\uff0c\u5c06\u6f6e\u97f3\u548c\u5c1a\u7684\u4fe1\u4ea4\u7ed9\u4ed6\uff0c \u5f20\u9ed1\u6b22\u559c\u65e0\u9650\uff0c\u9053\uff1a\u201c\u4e8e\u76f8\u516c\uff0c\u4f60\u6765\u5f97\u6b63\u662f\u65f6\u5019\u3002\u53f0\u5dde\u6cbf\u6d77\u53c8\u6765\u4e86\u4e24\u80a1\u65b0\u7684\u502d\u5bc7\uff0c\u4e49\u519b\u5904\u5883 \u66f4\u4e3a\u5371\u6025\uff0c\u54b1\u4eec\u7684\u63f4\u519b\u867d\u7136\u672a\u5230\uff0c\u6bd5\u5927\u9f99\u5934\u90a3\u5c01\u4fe1\u7b49\u4e8e\u7ed9\u4ed6\u4eec\u5403\u4e86\u5b9a\u5fc3\u4e38\uff0c\u519b\u5fc3\u4e00\u632f\uff0c\u5c31\u4e0d \u6015\u4e86\u3002\u201d\u5f53\u65e5\u5f20\u9ed1\u5c31\u5907\u4e86\u5c0f\u821f\uff0c\u6e21\u4e8e\u627f\u73e0\u8fc7\u6c5f\uff0c\u7167\u591c\u72ee\u5b50\u9a6c\u4e0d\u4fbf\u643a\u5e26\uff0c\u7559\u5728\u5f20\u9ed1\u5bb6\u4e2d\u3002 \u3000\u3000\u5c0f\u821f\u5982\u7bad\uff0c\u987a\u7740\u6c5f\u98ce\uff0c\u75be\u884c\u800c\u4e0b\uff0c\u4e8e\u627f\u73e0\u7acb\u5728\u8239\u5934\uff0c\u9065\u671b\u6c34\u5929\u76f8\u63a5\u4e4b\u5904\uff0c\u6fc0\u8d8a\u60c5\u6000\uff0c\u96be \u4ee5\u81ea\u6291\uff0c\u6b63\u60f3\u4e0e\u5f20\u9ed1\u8c08\u8bba\u6297\u502d\u4e4b\u4e8b\uff0c\u5ffd\u542c\u5f97\u5cb8\u4e0a\u6709\u4eba\u53eb\u9053\uff1a\u201c\u821f\u5b50\uff0c\u821f\u5b50\uff01\u201d \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u5c11\u5e74\u4e66\u751f\u5728\u6c5f\u8fb9\u62db\u624b\u53eb\u5524\uff0c\u5f20\u9ed1\u8bc8\u4f5c\u4e0d\u95fb\uff0c\u53cc\u6868\u4e00\u5212\uff0c\u5c0f\u821f\u987a\u6d41\u800c\u4e0b\uff0c\u90a3\u4e66\u751f \u8d76\u4e0a\u4e24\u6b65\uff0c\u6c14\u5598\u5401\u5401\u5730\u53c8\u53eb\u9053\uff1a\u201c\u821f\u5b50\uff0c\u821f\u5b50\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u51fa\u95e8\u4e4b\u4eba\uff0c\u8be5\u4e0e\u4eba\u65b9\u4fbf\uff0c\u6491 \u56de\u53bb\u8ba9\u4ed6\u4e0a\u8239\u5427\u3002\u201d\u5f20\u9ed1\u9053\uff1a\u201c\u6c5f\u6e56\u9669\u6076\uff0c\u54b1\u4eec\u6709\u4e8b\u5728\u8eab\uff0c\u5047\u5982\u642d\u4e86\u4e00\u4e2a\u574f\u4eba\uff0c\u90a3\u5c82\u4e0d\u8bef \u4e8b\uff1f\u201d\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u4e00\u4e2a\u6587\u5f31\u4e66\u751f\uff0c\u4f55\u987b\u987e\u8651\u3002\u201d\u5f20\u9ed1\u89c1\u4ed6\u5982\u6b64\u8bf4\uff0c\u53ea\u597d\u505c\u8239\uff0c\u90a3\u4e66\u751f\u597d \u4e0d\u5bb9\u6613\u5730\u8d76\u5230\uff0c\u66f3\u8d77\u957f\u886b\uff0c\u63a5\u7740\u5f20\u9ed1\u629b\u8fc7\u6765\u7684\u6868\uff0c\u8df3\u4e0a\u8239\u5934\uff0c\u8eab\u5b50\u6447\u6447\u6643\u6643\uff0c\u4e00\u53ea\u811a\u8e0f\u4e86\u4e2a \u7a7a\uff0c\u51e0\u4e4e\u8dcc\u4e0b\u6c34\u53bb\u3002\u4e8e\u627f\u73e0\u4f38\u624b\u4e00\u62c9\uff0c\u6697\u7528\u52b2\u529b\u76f8\u8bd5\uff0c\u90a3\u4e66\u751f\u8eab\u5b50\u4e00\u503e\uff0c\u51e0\u4e4e\u8dcc\u5165\u4e8e\u627f\u73e0\u7684 \u6000\u4e2d\uff0c\u4e8e\u627f\u73e0\u6025\u5fd9\u5c06\u4ed6\u6276\u4f4f\uff0c\u90a3\u4e66\u751f\u5140\u81ea\u6643\u4e86\u51e0\u4e0b\uff0c\u624d\u7a33\u5f97\u4f4f\u8eab\u5f62\uff0c\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u4e60\u6b66\u4e4b \u4eba\uff0c\u78b0\u7740\u5916\u529b\uff0c\u5fc5\u751f\u53cd\u5e94\uff0c\u8fd9\u4e66\u751f\u770b\u6765\u975e\u4f46\u4e0d\u4f1a\u6b66\u529f\uff0c\u800c\u4e14\u5f31\u4e0d\u7981\u98ce\uff0c\u5f20\u9ed1\u786e\u662f\u8fc7\u8651\u4e86\u3002\u201d \u90a3\u4e66\u751f\u6c57\u6d41\u6ee1\u9762\uff0c\u6c14\u5598\u5401\u5401\uff0c\u638f\u51fa\u4e00\u5f20\u4e1d\u5e15\uff0c\u6162\u6761\u65af\u7406\u5730\u62ed\u6c57\uff0c\u597d\u534a\u5929\u624d\u8bf4\u5f97\u51fa\u8bdd\u9053\uff1a\u201c\u591a \u8c22\u4e86\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u8bf7\u90a3\u4e66\u751f\u5230\u8239\u8231\u5750\u8d70\u3002\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u8bf7\u95f4\u5144\u53f0\u8d35\u59d3\u5927\u540d\uff0c\u6e21\u6c5f\u4f55\u4e8b\uff1f\u201d\u90a3\u4e66\u751f \u9053\uff1a\u201c\u5c0f\u5f1f\u59d3\u94c1\uff0c\u8d31\u53f7\u955c\u5fc3\u3002\u5bb6\u7236\u6709\u75c5\uff0c\u5c0f\u5f1f\u8981\u8d76\u56de\u53f0\u5dde\u63a2\u671b\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u4e2d\u6697\u7b11\uff0c\u8fd9\u4e66\u751f \u6587\u5f31\u96c5\u9759\uff0c\u4e0e\u4ed6\u59d3\u6c0f\u53ef\u662f\u5927\u4e0d\u76f8\u79f0\u3002\u8bf4\u9053\uff1a\u201c\u90a3\u597d\u6781\u4e86\uff0c\u5c0f\u5f1f\u4e5f\u662f\u8981\u5230\u53f0\u5dde\u53bb\u7684\u3002\u201d\u4e66\u751f \u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6e21\u6c5f\u4e4b\u540e\uff0c\u54b1\u4eec\u8fd8\u662f\u540c\u8def\u3002\u8bf7\u6559\u5144\u53f0\u9ad8\u59d3\u5927\u540d\uff1f\u201d\u4e8e\u627f\u73e0\u6beb\u65e0\u987e\u8651\uff0c\u4f9d\u5b9e \u8bf4\u4e86\u3002\u8bf4\u4e86\u4e4b\u540e\uff0c\u5ffd\u5730\u5fc3\u4e2d\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u542c\u8bf4\u53f0\u5dde\u502d\u5bc7\u4e3a\u60a3\uff0c\u9053\u8def\u6050\u6015\u4e0d\u751a\u597d\u8d70\u54ea\u3002\u201d\u90a3 \u4e66\u751f\u9053\uff1a\u201c\u542c\u8bf4\u502d\u5bc7\u662f\u5728\u53f0\u5dde\u6cbf\u6d77\u4e00\u5e26\u8086\u8650\uff0c\u53f0\u5dde\u57ce\u8fd8\u5728\u5b98\u519b\u624b\u4e2d\u3002\u5371\u9669\u662f\u6709\u7684\uff0c\u53ea\u662f\u4e3a\u4eba \u5b50\u8005\uff0c\u7236\u4eb2\u6709\u75c5\u5c82\u53ef\u4e0d\u56de\u53bb\u63a2\u89c6\uff1f\u201d\u4e8e\u627f\u73e0\u89e6\u8d77\u5fc3\u4e8b\uff0c\u60f3\u8d77\u81ea\u5df1\u7684\u7236\u4eb2\uff0c\u5e7d\u5e7d\u5730\u53f9\u4e86\u4e00\u53e3 \u6c14\u3002\u4e66\u751f\u9053\uff1a\u201c\u5144\u53f0\u53f9\u6c14\u4f55\u6765\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e1c\u5357\u6cbf\u6d77\uff0c\u502d\u5bc7\u8336\u6bd2\u751f\u7075\uff0c\u671d\u5ef7\u53c8\u4e0d\u80fd\u4fdd \u6c11\uff0c\u662f\u4ee5\u6d69\u53f9\u3002\u201d\u90a3\u4e66\u751f\u9053\uff1a\u201c\u5144\u53f0\u4ec1\u8005\u4e4b\u5fc3\uff0c\u5c0f\u5f1f\u656c\u4f69\u3002\u201d\u8f6c\u8fc7\u5934\u53bb\u3002\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5144\u53f0 \u610f\u6b32\u8d4f\u89c8\u6c5f\u4e0a\u98ce\u666f\u4e48\uff1f\u201d\u53ea\u89c1\u90a3\u4e66\u751f\u4e3e\u8896\u5728\u8138\u4e0a\u4e00\u6298\uff0c\u56de\u8f6c\u5934\u6765\uff0c\u9053\uff1a\u201c\u5c0f\u5f1f\u773c\u775b\uff0c\u6709\u70b9\u6bdb \u75c5\uff0c\u88ab\u6c5f\u98ce\u4e00\u5439\uff0c\u4e0d\u89c9\u6cea\u4e0b\uff0c\u5931\u793c\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u89c1\u4ed6\u773c\u773c\u7ea2\u6da6\uff0c\u773c\u89d2\u679c\u6709\u6cea\u75d5\uff0c\u672c\u6765\u4e0d\u4ee5\u4e3a \u610f\uff0c\u53ea\u662f\u542c\u4ed6\u8bed\u97f3\u9178\u6da9\uff0c\u7adf\u4f3c\u5fcd\u7740\u6cea\u8bf4\u51fa\u6765\u7684\uff0c\u5fc3\u4e2d\u53c8\u4e0d\u7981\u9690\u9690\u8d77\u7591\u3002 \u3000\u3000\u518d\u7559\u795e\u770b\u65f6\uff0c\u90a3\u4e66\u751f\u7709\u6e05\u76ee\u79c0\uff0c\u7709\u5b87\u95f4\u5374\u9690\u6709\u4e00\u80a1\u5fe7\u90c1\u4e4b\u6c14\uff0c\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u60f3 \u5fc5\u662f\u4ed6\u8bb0\u6302\u7236\u4eb2\u7684\u75c5\uff0c\u6240\u4ee5\u5fc3\u4e2d\u95f7\u95f7\u4e0d\u4e50\u3002\u201d\u6b63\u60f3\u8bf4\u8bdd\u529d\u89e3\uff0c\u5ffd\u89c1\u4e0a\u6d41\u6765\u4e86\u4e00\u53ea\u5927\u8239\uff0c\u8239\u5934 \u523b\u6210\u5f62\uff0c\u90a3\u6761\u8239\u5176\u5927\u65e0\u6bd4\uff0c\u5171\u6709\u4e24\u5c42\uff0c\u8239\u697c\u4e0a\u4f3c\u6709\u8bb8\u591a\u4eba\uff0c\u6b63\u5728\u90a3\u91cc\u996e\u9152\u4f5c\u4e50\uff0c\u5f26\u6b4c\u7ec6\u7ec6\uff0c \u968f\u7740\u6c5f\u98ce\u9001\u5230\u8033\u4e2d\uff0c\u4e8e\u627f\u73e0\u7684\u5e08\u7236\u90fd\u662f\u535a\u5b66\u591a\u624d\u4e4b\u58eb\uff0c\u5979\u81ea\u5e7c\u53d7\u85b0\u9676\uff0c\u4ea6\u80fd\u5ba1\u97f3\u8fa8\u5f8b\uff0c\u4f46\u4ed4 \u7ec6\u542c\uff0c\u8fd9\u4e50\u58f0\u5374\u5168\u4e0d\u4f3c\u4e2d\u539f\u4e4b\u97f3\uff01 \u3000\u3000\u697c\u822c\u76f4\u9a76\u800c\u6765\uff0c\u6d8c\u8d77\u5c42\u5c42\u6ce2\u6d6a\uff0c\u770b\u5f97\u66f4\u6e05\u695a\u4e86\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u8239\u8231\u4e0a\u5bc6\u5bc6\u9ebb\u9ebb\u7ad9\u6ee1\u4e86\u4eba\uff0c \u4e2a\u4e2a\u90fd\u662f\u8eab\u8eaf\u7c97\u77ee\u7684\u6c49\u5b50\uff0c\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u54ea\u91cc\u6765\u7684\u8fd9\u4e48\u591a\u841d\u535c\u5934\uff1f\u201d\u697c\u8239\u4e0a\u7684\u6b4c\u58f0\u7c97\u65f7\u4e4b \u4e2d\u5e26\u6709\u4e00\u80a1\u60b2\u51c9\u7684\u97f5\u5473\uff0c\u4e8e\u627f\u73e0\u4fa7\u8033\u542c\u65f6\uff0c\u4e00\u4e2a\u5b57\u4e5f\u542c\u4e0d\u61c2\uff0c\u4ed6\u4eec\u5531\u7684\u662f\u4ec0\u4e48\u201c\u4f9d\u7f57\u54c8\u5c3c\u963f \u4e0e\u9640\uff0c\u55e4\u91cc\u5974\u9c81\u559d\uff01\u201d\u90a3\u5c11\u5e74\u4e66\u751f\u5ffd\u5730\u6b4c\u9053\uff1a\u201c\u82b1\u867d\u82b3\u8361\u516e\uff0c\u98d8\u96f6\u65e0\u4f9d\u3002\u8fd9\u662f\u65e5\u672c\u6a31\u82b1 \u6b4c\u3002\u201d\u5f20\u9ed1\u505c\u4e86\u5212\u6868\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u662f\u502d\u5974\u7684\u8d21\u8239\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u600e\u4e48\u4efb\u7531\u502d\u5bc7\u7684\u697c\u8239\u5728\u957f\u6c5f\u884c\u8d70\uff1f\u201d\u5f20\u9ed1\u9053\uff1a\u201c\u76f8\u516c\u6709\u6240\u4e0d \u77e5\uff0c\u502d\u5974\u72e1\u733e\u5f97\u5f88\uff0c\u4ed6\u4eec\u4e00\u9762\u5728\u6cbf\u6d77\u52ab\u63a0\uff0c\u4e00\u9762\u5047\u501f\u8fdb\u8d21\u4e3a\u540d\uff0c\u505a\u8d70\u79c1\u7684\u751f\u610f\u3002\u201d\u4e8e\u627f\u73e0 \u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u201d\u5f20\u9ed1\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5b98\u5bb6\u7684\u5e02\u8236\u53f8\u8fd8\u5c06\u4ed6\u4eec\u5949\u4e3a\u4e0a\u5bbe\u5462\u3002\u201d\u539f\u6765\u5728\u660e\u671d\u7684 \u6b63\u7edf\uff08\u82f1\u5b97\uff09\u5e74\u95f4\uff0c\u6b63\u5f53\u65e5\u672c\u7684\u201c\u6218\u56fd\u65f6\u4ee3\u201d\uff0c\u5404\u5730\u8bf8\u4faf\u62e5\u5175\u5272\u636e\uff0c\u8fd9\u4e9b\u8bf8\u4faf\u4e89\u6d3e\u8d21\u8239\u5411\u4e2d \u56fd\u8fdb\u8d21\uff0c\u56e0\u4e3a\u6839\u636e\u660e\u671d\u201c\u5e02\u8236\u53f8\u201d\uff08\u5373\u6d77\u5173\uff09\u7684\u89c4\u77e9\uff0c\u5916\u56fd\u8d21\u4f7f\u5e26\u6765\u7684\u201c\u79c1\u8d27\u201d\u53ef\u4ee5\u514d\u7a0e\uff0c \u90a3\u4e9b\u8bf8\u4faf\u4fbf\u4e58\u6b64\u5927\u505a\u8d70\u79c1\u751f\u610f\uff0c\u4ee5\u56fe\u5de8\u5229\u3002\u660e\u671d\u95ee\u8d77\u502d\u5bc7\u9a9a\u6270\u7684\u4e8b\u4ef6\uff0c\u4ed6\u4eec\u4fbf\u8bf4\u8fd9\u662f\u672c\u56fd\u7684 \u201c\u6d6a\u4eba\u201d\uff0c\u653f\u5e9c\u65e0\u6cd5\u7ba1\u8f96\uff0c\u5176\u5b9e\u8fd9\u4e9b\u201c\u6d6a\u4eba\u201d\u5341\u4e4b\u516b\u4e5d\u90fd\u662f\u5f97\u5230\u65e5\u672c\u5404\u5730\u8bf8\u5019\u7684\u652f\u6301\uff0c\u751a\u6216 \u662f\u76f4\u63a5\u9063\u6d3e\u6765\u52ab\u63a0\u8d22\u8d27\u7684\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ed6\u4eec\u5728\u4e2d\u56fd\u5730\u65b9\u711a\u70e7\u52ab\u6740\uff0c\u4e3a\u4ec0\u4e48\u4e2d\u56fd\u7684\u5b98\u5458\u8fd8\u8981\u5f85\u5982\u4e0a\u5bbe\uff1f\u201d\u5f20\u9ed1\u9053\uff1a \u201c\u8fd8\u4e0d\u662f\u4e3a\u4e86\u4e00\u4e2a\u5229\u5b57\uff1f\u4ed6\u4eec\u7684\u8eab\u4efd\u662f\u8d21\u4f7f\uff0c\u672c\u6765\u671d\u5ef7\u89c4\u5b9a\u4ed6\u4eec\u4e09\u5e74\u53ea\u80fd\u8fdb\u8d21\u4e00\u6b21\u7684\uff0c\u6bcf\u6b21 \u6765\u7684\u8d21\u4f7f\u4eba\u6570\u4e5f\u6709\u9650\u5236\uff0c\u53ef\u662f\u65e5\u672c\u5404\u5730\u7684\u8bf8\u4faf\u90fd\u4e89\u7740\u6765\u8fdb\u8d21\uff0c\u6bcf\u6761\u8d21\u8239\u90fd\u8d3f\u8d42\u5e02\u8236\u53f8\u65e9\u4e9b\u653e \u4ed6\u4eec\u5165\u6765\u3002\u201d\u4e8e\u627f\u73e0\u6447\u4e86\u6447\u5934\uff0c\u5fc3\u4e2d\u65e0\u9650\u6124\u6168\uff01 \u3000\u3000\u90a3\u6761\u65e5\u672c\u7684\u8d21\u8239\u8d8a\u6765\u8d8a\u8fd1\uff0c\u5f20\u9ed1\u9053\uff1a\u201c\u54b1\u4eec\u5feb\u907f\u5f00\u5b83\uff01\u201d\u4e8e\u627f\u73e0\u8840\u8109\u8d4d\u5f20\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0 \u4e48\u8981\u907f\u5f00\u5b83\uff0c\u6211\u8bf4\u8fce\u4e0a\u53bb\uff01\u201d\u5f20\u9ed1\u4f7f\u4e2a\u773c\u8272\uff0c\u9053\uff1a\u201c\u76f8\u516c\uff0c\u4f60\u4e0d\u662f\u8d76\u7740\u8fc7\u6c5f\u6709\u4e8b\u4e48\uff1f\u8fd9\u4e9b\u502d \u5bc7\u7684\u8d21\u8239\uff0c\u65e0\u6076\u4e0d\u4f5c\uff0c\u649e\u7740\u4e86\u5b83\uff0c\u95f9\u51fa\u4e8b\u6765\uff0c\u53ef\u4e0d\u662f\u597d\u800d\u7684\u3002\u201d\u4e8e\u627f\u73e0\u672c\u662f\u4e00\u65f6\u6124\u6068\uff0c\u88ab\u5f20 \u9ed1\u63d0\u9192\uff0c\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u5f20\u9ed1\u4f7f\u8239\u5982\u4f7f\u9a6c\uff0c\u8239\u5934\u4e00\u8f6c\uff0c\u7acb\u523b\u6389\u8fc7\u65b9\u5411\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u5212\u51fa\u5341\u4f59\u4e08\u5730\uff0c\u5ffd\u89c1\u6c5f\u4e2d\u6709\u4e00 \u6761\u9c7c\u8239\uff0c\u8239\u4e0a\u6709\u4e2a\u8001\u6e14\u592b\u548c\u4e00\u4e2a\u5973\u5b50\uff0c\u4f3c\u662f\u4ed6\u7684\u5973\u513f\uff0c\u6b63\u5728\u8d21\u8239\u9644\u8fd1\u7ecf\u8fc7\uff0c\u8239\u697c\u4e0a\u7684\u90a3\u4e9b\u65e5 \u672c\u6d6a\u4eba\u201c\u5440\u4f9d\u545c\u6696\u201d\u5730\u4e71\u53eb\u4e00\u901a\uff0c\u767e\u6868\u9f50\u5212\uff0c\u7adf\u7136\u76f4\u8ffd\u90a3\u6761\u6e14\u8239\u3002\u5c11\u5e74\u4e66\u751f\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c \u4ed6\u4eec\u8981\u6349\u8fd9\u4e2a\u6e14\u5bb6\u5973\u3002\u201d\u4e8e\u627f\u73e0\u5927\u6012\uff0c\u9053\uff1a\u201c\u5f20\u9ed1\uff0c\u5929\u5927\u4e8b\u60c5\uff0c\u54b1\u4eec\u4e5f\u8981\u78b0\u4ed6\u4e00\u78b0\uff0c\u5feb\u5212\u56de \u53bb\u3002\u201d\u53ea\u89c1\u90a3\u6761\u8d21\u8239\u582a\u582a\u8d76\u4e0a\uff0c\u8239\u4e0a\u6709\u4eba\u629b\u51fa\u4e24\u6761\u6320\u94a9\uff0c\u8981\u94a9\u90a3\u6761\u6e14\u8239\u3002\u5f20\u9ed1\u7528\u529b\u4e00\u5212\uff0c\u5c0f \u8239\u5982\u7bad\u9a76\u8fc7\uff0c\u4e8e\u627f\u73e0\u5927\u559d\u4e00\u58f0\uff0c\u62d4\u51fa\u9752\u51a5\u5b9d\u5251\uff0c\u4e00\u5251\u5c31\u628a\u90a3\u4e24\u6761\u6320\u94a9\u524a\u65ad\u3002 \u3000\u3000\u8d21\u8239\u4e0a\u7684\u65e5\u672c\u6d6a\u4eba\u7eb7\u7eb7\u9a82\u9053\uff1a\u201c\u516b\u683c\u9a6c\u9e7f\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\uff0c\u90a3\u5feb\uff0c\u4e24\u4e2a\u6d6a\u4eba\u62d4\u51fa\u502d\u5200 \u4fbf\u8df3\u8fc7\u6765\uff0c\u4e8e\u627f\u73e0\u65e9\u6709\u51c6\u5907\uff0c\u7389\u624b\u4e00\u626c\uff0c\u4e24\u6735\u91d1\u82b1\u7834\u7a7a\u98de\u51fa\uff0c\u4e00\u4e2a\u6d6a\u4eba\u7ed9\u6253\u5f97\u8dcc\u4e0b\u6c34\u53bb\uff0c\u53e6 \u4e00\u4e2a\u6d6a\u4eba\u5374\u8df3\u4e0a\u4e86\u8239\u5934\uff0c\u4e8e\u627f\u73e0\u9752\u51a5\u5b9d\u5251\u4e00\u6325\uff0c\u90a3\u6d6a\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u96ea\u4eae\u7684\u502d\u5200\u6a2a\u780d\u76f4\u5288\uff0c\u5ffd \u542c\u5f97\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u90a3\u67c4\u502d\u5200\u65ad\u4e3a\u4e24\u622a\u3002\u8fd9\u4e2a\u65e5\u672c\u6d6a\u4eba\uff0c\u662f\u56fd\u4e2d\u7684\u56db\u6bb5\u597d\u624b\uff0c\u81ea\u6043\u502d\u5200\u950b \u5229\uff0c\u54ea\u91cc\u5c06\u4e8e\u627f\u73e0\u8fd9\u6837\u4e00\u4e2a\u201c\u5c0f\u5b69\u5b50\u201d\u653e\u5728\u773c\u5185\uff1f\u4e0d\u6599\u4e09\u62db\u4e4b\u5185\uff0c\u502d\u5200\u4fbf\u88ab\u524a\u65ad\uff0c\u5446\u4e86\u4e00 \u5446\uff0c\u4e8e\u627f\u73e0\u53f1\u54a4\u4e00\u58f0\uff0c\u5530\u7684\u4e00\u58f0\uff0c\u5251\u5c16\u4ece\u524d\u5fc3\u900f\u8fc7\u540e\u5fc3\uff0c\u98de\u8d77\u4e00\u811a\uff0c\u5c06\u90a3\u502d\u5bc7\u7684\u5c38\u8eab\u8e22\u4e0b\u957f \u6c5f\uff0c\u767b\u65f6\u6c5f\u9762\u67d3\u7ea2\u4e86\u4e00\u5927\u7247\u3002 \u3000\u3000\u8d21\u8239\u4e0a\u7684\u65e5\u672c\u6b66\u58eb\u5927\u53eb\u9053\uff1a\u201c\u516b\u683c\u9a6c\u9e7f\uff0c\u4ee5\u7239\u65f6\uff01\u201d\u4e0a\u4e00\u53e5\u662f\u9a82\u4eba\u7684\u8bf4\u8bdd\uff0c\u4e0b\u4e00\u53e5\u5374\u4e0d \u7531\u5f97\u8877\u5fc3\u8d5e\u597d\uff0c\u8981\u77e5\u5728\u65e5\u672c\uff0c\u4e09\u6bb5\u56db\u6bb5\u7684\u6b66\u571f\u867d\u7136\u4e0d\u7b97\u7a00\u5947\uff0c\u4f46\u50cf\u4e8e\u627f\u73e0\u90a3\u6837\u4ea4\u624b\u5c31\u8fde\u6740\u4e24 \u4e2a\u4e0a\u6bb5\u7684\u6b66\u58eb\uff0c\u5373\u7b97\u4e5d\u6bb5\u7684\u9ad8\u624b\uff0c\u4e5f\u672a\u5fc5\u80fd\u591f\u3002 \u3000\u3000\u5f20\u9ed1\u6389\u8f6c\u8239\u5934\uff0c\u4fbf\u60f3\u9003\u8d70\uff0c\u65e9\u6709\u4e24\u540d\u65e5\u672c\u6b66\u58eb\u53c8\u8dc3\u4e86\u8fc7\u6765\uff0c\u8fd9\u4e24\u540d\u6b66\u58eb\u8eab\u6cd5\u6781\u5feb\uff0c\u4e00\u8df3 \u4e0a\u8239\u5934\uff0c\u5c0f\u8239\u767b\u65f6\u6c89\u4e86\u4e00\u622a\uff1b\u4e8e\u627f\u73e0\u89c1\u521a\u624d\u6740\u5f97\u5982\u6b64\u5bb9\u6613\uff0c\u4e0d\u4ee5\u4e3a\u610f\uff0c\u5b9d\u5251\u4e00\u4f38\uff0c\u633d\u4e86\u4e00\u4e2a \u5251\u82b1\uff0c\u7528\u4e86\u4e00\u62db\u201c\u8179\u5f0f\u5206\u91d1\u201d\uff0c\u5251\u950b\u4e00\u98a4\uff0c\u5206\u523a\u4e24\u4e2a\u65e5\u672c\u6b66\u58eb\uff0c\u4e0d\u6599\u90a3\u4e24\u4e2a\u65e5\u672c\u6b66\u58eb\u5927\u559d\u4e00 \u58f0\uff0c\u4e24\u67c4\u957f\u957f\u7684\u502d\u5200\uff0c\u4e00\u4e0a\u4e00\u4e0b\uff0c\u6a2a\u5288\u8fc7\u6765\uff0c\u653b\u52bf\u7adf\u662f\u6781\u4e3a\u51f6\u731b\uff0c\u4e8e\u627f\u73e0\u65ad\u4e0d\u80fd\u540c\u65f6\u524a\u65ad\u4e24 \u628a\u502d\u5200\uff0c\u82e5\u7136\u4ecd\u7528\u539f\u5f0f\uff0c\u52bf\u5fc5\u4e24\u8d25\u4ff1\u4f24\uff0c\u903c\u5f97\u5012\u9000\u4e24\u6b65\uff0c\u7528\u8f7b\u7075\u7684\u8eab\u6cd5\uff0c\u907f\u5f00\u4e86\u8fd9\u4e24\u5200\uff0c\u90a3 \u4e24\u4e2a\u65e5\u672c\u6b66\u58eb\u5200\u6cd5\u5982\u98ce\uff0c\u4e00\u62a2\u4e0a\u6765\uff0c\u4ea4\u53c9\u75be\u5288\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u5927\u53eb\u4e00\u58f0\uff0c\u6251\u901a\u8dcc\u4e0b\u6c34\u53bb\uff0c\u539f\u6765 \u662f\u5f20\u9ed1\u89c1\u72b6\u51f6\u9669\uff0c\u4e3e\u8d77\u94c1\u6868\uff0c\u5411\u4e00\u4e2a\u65e5\u672c\u6b66\u58eb\u5077\u88ad\uff0c\u8fd9\u4e2a\u65e5\u672c\u6b66\u571f\u662f\u516d\u6bb5\u9ad8\u624b\uff0c\u5c24\u7cbe\u4e8e\u67d4 \u672f\uff0c\u4e00\u4f4e\u5934\u8ba9\u90a3\u94c1\u6d46\u4ece\u5934\u9876\u6253\u8fc7\uff0c\u5f20\u9ed1\u6536\u52bf\u4e0d\u53ca\uff0c\u4ec6\u5012\u4ed6\u7684\u80cc\u4e0a\uff0c\u88ab\u4ed6\u4f7f\u51fa\u201c\u67d4\u9053\u201d\u4e2d\u201c\u80cc \u6295\u201d\u7684\u7edd\u62db\u6454\u4e0b\u6c5f\u5fc3\u3002 \u3000\u3000\u8fd9\u6837\u6162\u5f97\u4e00\u6162\uff0c\u4e8e\u627f\u73e0\u65e9\u5df2\u7ad9\u7a33\u811a\u8ddf\uff0c\u77ed\u5251\u56de\u73af\u53cd\u524a\uff0c\u7528\u7275\u5f15\u7c98\u8fde\u4e4b\u52b2\uff0c\u5c06\u4e24\u67c4\u502d\u5200\u7684 \u51cc\u5229\u653b\u52bf\u5316\u5f00\uff0c\u8fd9\u4e24\u4e2a\u65e5\u672c\u6b66\u58eb\u4e00\u4e2a\u662f\u4e94\u6bb5\uff0c\u4e00\u4e2a\u662f\u516d\u6bb5\uff0c\u89c1\u4e8e\u627f\u73e0\u5c0f\u5c0f\u886b\u7eaa\uff0c\u7adf\u7136\u6df1\u660e\u501f \u529b\u6253\u529b\u7684\u9053\u7406\uff0c\u4e0e\u4ed6\u4eec\u6240\u7ec3\u7684\u67d4\u9053\u4e0d\u8c0b\u800c\u5408\uff0c\u54ea\u91cc\u8fd8\u6562\u8f7b\u89c6\uff0c\u4e09\u4eba\u8fc5\u5373\u4e4b\u95f4\u62fc\u6597\u4e86\u5341\u4f59\u62db\uff0c \u4e8e\u627f\u73e0\u4e00\u6b65\u4e0d\u8ba9\uff0c\u4ee5\u7edd\u5999\u7684\u5251\u6cd5\uff0c\u5c06\u4e24\u4e2a\u65e5\u672c\u6b66\u571f\u8feb\u5728\u8239\u5934\uff0c\u4e0d\u80fd\u518d\u524d\u8fdb\u534a\u6b65\u3002\u4f46\u8fd9\u4e24\u4e2a\u6b66 \u571f\u72e1\u733e\u4e4b\u6781\uff0c\u77e5\u9053\u5979\u624b\u4e2d\u4f7f\u7684\u4e43\u662f\u5b9d\u5251\uff0c\u4e24\u67c4\u502d\u5200\u6b64\u547c\u5f7c\u5e94\uff0c\u6559\u4e8e\u627f\u73e0\u4e0d\u80fd\u4e58\u9699\u4e13\u653b\u4e00\u4eba\uff0c \u4e8e\u627f\u73e0\u60f3\u5728\u8feb\u5207\u4e4b\u95f4\uff0c\u6740\u4e24\u4e2a\u4e94\u6bb5\u516d\u6bb5\u7684\u9ad8\u624b\uff0c\u5374\u4e5f\u4e0d\u80fd\uff01\u8fd9\u65f6\u90a3\u6761\u65e5\u672c\u8d21\u8239\u53c8\u5df2\u8feb\u8fd1\uff0c\u79bb \u5f00\u4e8e\u627f\u73e0\u7684\u5c0f\u8239\uff0c\u4e0d\u8fc7\u5341\u4e08\u4e4b\u9065\u4e86\u3002\u8d21\u8239\u4e0a\u7684\u65e5\u672c\u6c34\u624b\uff0c\u53c8\u4f38\u51fa\u5341\u51e0\u652f\u6320\u94a9\uff0c\u53ea\u5f85\u4e24\u8239\u76f8 \u63a5\uff0c\u4fbf\u8981\u7acb\u5373\u5c06\u4e8e\u627f\u73e0\u7684\u5c0f\u8239\u94a9\u4f4f\uff0c\u4e8e\u627f\u73e0\u53ea\u6709\u4e00\u53cc\u624b\uff0c\u52bf\u96be\u5206\u51fa\u624b\u6765\u62b5\u5fa1\uff0c\u8fd9\u60c5\u52bf\u5f53\u771f\u662f \u5371\u9669\u4e4b\u6781\uff01 \u3000\u3000\u800c\u4e14\u4e8e\u627f\u73e0\u53c8\u4e0d\u61c2\u6c34\u6027\uff0c\u5f20\u9ed1\u5df2\u88ab\u6253\u4e0b\u6728\u53bb\uff0c\u8fd9\u6761\u5c0f\u8239\u5728\u6c5f\u5fc3\u6ef4\u6e9c\u6e9c\u5730\u4e71\u8f6c\uff0c\u4e09\u4eba\u4e52\u4e52 \u4e53\u4e53\u5728\u8239\u4e2d\u6076\u6253\uff0c\u5c0f\u821f\u5ffd\u800c\u503e\u5411\u5de6\u8fb9\uff0c\u5ffd\u800c\u503e\u5411\u53f3\u8fb9\uff0c\u9707\u8361\u4e0d\u4f11\uff0c\u8239\u7bf7\u4e5f\u7ed9\u502d\u5200\u780d\u5f97\u7a00\u70c2\uff0c \u4e8e\u627f\u73e0\u4e0d\u8010\u9707\u8361\uff0c\u6e10\u89c9\u5934\u6655\u773c\u82b1\uff0c\u90a3\u6761\u8d21\u8239\u75be\u9a76\u800c\u6765\uff0c\u8239\u4e0a\u6d6a\u4eba\u8f70\u7136\u5927\u53eb\uff0c\u500f\u5730\u4f38\u51fa\u5341\u51e0\u652f \u6320\u94a9\uff0c\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u614c\uff0c\u53f3\u9996\u8fb9\u90a3\u540d\u65e5\u672c\u6b66\u58eb\u66b4\u559d\u4e00\u58f0\uff0c\u502d\u5200\u6325\u4e86\u534a\u4e2a\u5706\u5f27\uff0c\u81ea\u5de6\u81f3\u53f3\uff0c\u8fde \u5288\u5e26\u524a\uff0c\u5de6\u9996\u90a3\u540d\u6b66\u58eb\uff0c\u865a\u5288\u4e00\u5200\uff0c\u65bd\u5c55\u67d4\u672f\uff0c\u53cd\u624b\u4e00\u631f\uff0c\u4fbf\u8981\u7528\u201c\u8d1f\u624b\u53cd\u6295\u201d\u7684\u7edd\u6280\uff0c\u5c06 \u4e8e\u627f\u73e0\u63b7\u4e0b\u6c5f\u5fc3\u3002 \u3000\u3000\u8fd9\u4e24\u540d\u6b66\u58eb\u6765\u52bf\u90fd\u6025\uff0c\u4e8e\u627f\u73e0\u7528\u4e86\u4e00\u62db\u201c\u7f51\u9a6c\u8d1f\u56fe\u201d\uff0c\u77ed\u5251\u6325\u6210\u4e00\u4e2a\u5706\u5f62\uff0c\u5f53\u80f8\u4e00\u6321\uff0c \u53f3\u9996\u90a3\u540d\u6b66\u58eb\uff0c\u5200\u950b\u5df2\u5230\uff0c\u5ffd\u5730\u5927\u53eb\u4e00\u58f0\uff0c\u624b\u81c2\u5782\u4e86\u4e0b\u6765\uff0c\u4e8e\u627f\u73e0\u89c1\u673a\u5feb\u6377\uff0c\u6025\u5fd9\u4e00\u4e2a\u76d8\u9f99 \u7ed5\u6b65\uff0c\u95ea\u8fc7\u4e00\u8fb9\uff0c\u5de6\u9996\u90a3\u540d\u6b66\u58eb\uff0c\u65bd\u5c55\u67d4\u672f\uff0c\u4e00\u631f\u4e0d\u4e2d\uff0c\u6b63\u6b32\u518d\u51fb\uff0c\u5ffd\u89c9\u80f8\u53e3\u4f3c\u7ed9\u5229\u5203\u5212\u4e86 \u4e00\u4e0b\uff0c\u75db\u5f97\u5927\u53eb\u4e00\u58f0\uff0c\u817e\u8eab\u98de\u8d77\uff0c\u4e8e\u627f\u73e0\u4e00\u5251\u523a\u53bb\uff0c\u51d1\u4e2a\u6b63\u7740\uff0c\u5728\u4ed6\u5c0f\u8179\u4e0a\u5212\u5f00\u4e86\u4e00\u6761\u88c2 \u7f1d\uff0c\u63a5\u7740\u53cd\u8eab\u4e00\u5251\uff0c\u53c8\u5c06\u53f3\u9996\u6b66\u58eb\u7684\u80f3\u818a\u65a9\u65ad\uff0c\u4e24\u540d\u6b66\u58eb\u90fd\u5fcd\u75db\u8df3\u4e0b\u6c34\u4e2d\uff0c\u4e8e\u627f\u73e0\u60ca\u7591\u4e0d \u5b9a\uff0c\u5ffd\u89c1\u5c0f\u8239\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u8239\u9996\u6389\u4e86\u4e2a\u65b9\u5411\uff0c\u53c8\u5212\u5f00\u4e86\u6570\u4e08\uff0c\u5341\u513f\u6761\u6320\u94a9\uff0c\u90fd\u6251\u4e86\u4e2a\u7a7a\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5728\u767e\u5fd9\u4e2d\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u90a3\u5c11\u5e74\u4e66\u751f\u4f3c\u7b11\u975e\u7b11\u5730\u5ffd\u5730\u907f\u5f00\u4e86\u81ea\u5df1\u7684\u773c\u5149\uff0c\u4f4e\u5934\u628a \u8235\uff0c\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u591a\u8c22\u4f60\u5566\uff01\u201d\u4e66\u751f\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4ec0\u4e48\uff1f\u5feb\u8eb2\u8fdb\u8231\u6765\uff01\u201d\u6c5f \u5fc3\u4e00\u4e2a\u5927\u6d6a\u6253\u5230\uff0c\u5c0f\u8239\u503e\u8fc7\u4e00\u8fb9\uff0c\u6d6a\u6d9b\u4e2d\u5ffd\u5730\u8df3\u8d77\u4e00\u4eba\uff0c\u53e3\u4e2d\u54ac\u7740\u4e00\u67c4\u502d\u5200\uff0c\u4e24\u624b\u5404\u63d0\u4e00\u4e2a \u5934\u9885\uff0c\u8dc3\u4e0a\u8239\u6765\uff0c\u8fd9\u4eba\u6b63\u662f\u5f20\u9ed1\uff0c\u53ea\u89c1\u4ed6\u5c06\u4e24\u9897\u5934\u503e\u5411\u502d\u8239\u63b7\u53bb\uff0c\u53d6\u4e0b\u502d\u5200\uff0c\u559d\u9053\uff1a\u201c\u8c01\u518d \u8ffd\u6765\uff0c\u8fd9\u5c31\u662f\u699c\u6837\u3002\u201d\u56de\u9996\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u786e\u662f\u771f\u4eba\u4e0d\u9732\u76f8\uff0c\u9732\u76f8\u4e0d\u771f\u4eba\uff0c\u94c1\u76f8\u516c\u7adf\u662f\u4e2a \u8eab\u6000\u7edd\u6280\u7684\u4fa0\u571f\uff01\u201d\u539f\u6765\u5f20\u9ed1\u7cbe\u901a\u6c34\u6027\uff0c\u88ab\u63b7\u4e0b\u6c34\uff0c\u5e76\u65e0\u53d7\u4f24\uff0c\u4ed6\u6f5c\u5728\u6c34\u4e2d\uff0c\u8ffd\u4e0a\u81ea\u5df1\u8fd9\u6761 \u5c0f\u8239\uff0c\u89c1\u90a3\u4e24\u540d\u53d7\u4f24\u7684\u6b66\u58eb\u6c89\u4e0b\u6c34\u4e2d\uff0c\u4ed6\u4e00\u53e3\u95f7\u6c14\u6b63\u5f85\u53d1\u6cc4\uff0c\u4fbf\u5728\u6c34\u4e2d\u5272\u4e86\u90a3\u4e24\u4eba\u7684\u5934\u9885\uff0c \u62a2\u8fc7\u4e00\u67c4\u502d\u5200\uff0c\u518d\u8df3\u4e0a\u6765\u3002\u5f53\u4ed6\u5728\u6c34\u4e2d\u5236\u4f0f\u90a3\u4e24\u4e2a\u502d\u5bc7\u65f6\uff0c\u53d1\u73b0\u4e24\u4e2a\u502d\u5bc7\u7684\u80f8\u524d\uff0c\u5404\u63d2\u7740\u4e00 \u628a\u5c0f\u5c0f\u7684\u5315\u9996\uff0c\u4ed6\u77e5\u9053\u4e8e\u627f\u73e0\u7528\u7684\u662f\u91d1\u82b1\u6697\u5668\uff0c\u8239\u4e0a\u518d\u65e0\u522b\u4eba\uff0c\u5315\u9996\u5b9a\u7136\u662f\u4e66\u751f\u7684\u6697\u5668\u65e0 \u7591\u3002 \u3000\u3000\u5f20\u9ed1\u6293\u8d77\u4e86\u6868\uff0c\u52a9\u90a3\u4e66\u751f\u5212\u8239\uff0c\u5c0f\u8239\u53bb\u5f97\u66f4\u5feb\uff0c\u4e8e\u627f\u73e0\u5438\u4e86\u53e3\u6c14\uff0c\u6b63\u81ea\u5e86\u5e78\u53ef\u4ee5\u8131\u9669\uff0c \u5ffd\u542c\u5f97\u5f20\u9ed1\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d\u4e8e\u627f\u73e0\u968f\u7740\u5f20\u9ed1\u7684\u773c\u5149\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u5c0f\u8239\u7684\u8239\u677f\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6 \u5019\uff0c\u5df2\u88c2\u5f00\u4e86\u4e24\u9053\u7834\u7f1d\uff0c\u6c5f\u6c34\u6c69\u6c69\u6d78\u5165\uff0c\u5f20\u9ed1\u4e22\u4e0b\u4e86\u6868\uff0c\u614c\u5fd9\u5c06\u6c34\u6cfc\u51fa\u3002\u90a3\u6761\u65e5\u672c\u8d21\u8239\uff0c\u626f \u8d77\u98ce\u5e06\uff0c\u8fc5\u5373\u8ffd\u4e0a\uff0c\u8239\u9996\u4e00\u6761\u7c97\u9ed1\u7684\u6b66\u58eb\u5927\u53eb\u9053\uff1a\u201c\u8fbe\u53f3\u5c71\u6469\u65f6\u513f\u9c81\u8fbe\uff01\u201d\u53cc\u624b\u9ad8\u4e3e\u4e00\u6761\u5927 \u94c1\u951a\uff0c\u9ad8\u53eb\uff1a\u201c\u54bd\u81f3\u3001\u6ce5\u3001\u5c71\u2026\u2026\u201d\u4e00\u3001\u4e8c\u3001\u4e09\uff0c\u65cb\u98ce\u4e00\u821e\uff0c\u547c\u7684\u4e00\u58f0\u629b\u51fa\uff01 \u3000\u3000\u90a3\u94c1\u951a\u91cd\u8fbe\u4e8c\u4e09\u767e\u65a4\uff0c\u88ab\u90a3\u65e5\u672c\u6b66\u58eb\u4e00\u4e3e\u629b\u51fa\uff0c\u795e\u529b\u786e\u662f\u60ca\u4eba\uff0c\u4e8e\u627f\u73e0\u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u5e74 \u5c0f\u529b\u5f31\uff0c\u8981\u63a5\u8fd9\u6837\u6c89\u91cd\u7684\u94c1\u951a\uff0c\u5374\u662f\u529b\u6240\u4e0d\u80fd\u3002\u90a3\u94c1\u951a\u631f\u7740\u4e00\u80a1\u5de8\u98ce\uff0c\u6709\u5982\u6cf0\u5c71\u538b\u9876\uff0c\u6b63\u6b63 \u5411\u7740\u5c0f\u8239\u843d\u4e0b\uff0c\u4e8e\u627f\u73e0\u8df3\u51fa\u8239\u5934\uff0c\u6b63\u6b32\u62fc\u6b7b\u62b5\u6321\uff0c\u5ffd\u7136\u88ab\u4eba\u4e00\u5e26\uff0c\u4e8e\u627f\u73e0\u672a\u53ca\u770b\u6e05\uff0c\u90a3\u94c1\u951a \u5df2\u547c\u7684\u4e00\u58f0\u63b7\u5230\uff0c\u5ffd\u89c1\u90a3\u4e66\u751f\u62a2\u4e0a\u4e00\u6b65\uff0c\u53cc\u81c2\u4e00\u633a\uff0c\u63a5\u8fc7\u90a3\u652f\u94c1\u951a\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6765\u800c\u4e0d\u5f80 \u975e\u793c\u4e5f\uff01\u201d\u94c1\u951a\u98de\u53bb\uff0c\u76f4\u5954\u8239\u697c\uff0c\u501a\u5728\u8239\u8237\u52a9\u5a01\u7684\u65e5\u672c\u6d6a\u4eba\u7eb7\u7eb7\u9003\u907f\uff0c\u90a3\u4e2a\u9ed1\u6b66\u58eb\u662f\u65e5\u672c\u7684 \u4e03\u6bb5\u9ad8\u624b\uff0c\u6025\u5fd9\u6c89\u8170\u5750\u9a6c\uff0c\u529b\u8d2f\u53cc\u81c2\uff0c\u5c06\u94c1\u951a\u63a5\u7740\uff0c\u63a5\u662f\u63a5\u7740\u4e86\uff0c\u53ef\u662f\u90a3\u4e66\u751f\u63b7\u8fc7\u6765\u7684\u529b \u9053\uff0c\u5374\u6bd4\u4ed6\u5927\u5f97\u591a\uff0c\u4ed6\u653e\u4e0b\u94c1\u951a\uff0c\u968f\u7740\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8239\u4e0a\u502d\u5bc7\u5927\u60ca\uff0c\u4ed6\u4eec\u8fd9\u6761\u8d21\u8239\u5171\u6709\u4e24\u4e2a\u4e03\u6bb5\u9ad8\u624b\uff0c\u4e24\u4e2a\u516d\u6bb5\u9ad8\u624b\uff0c\u8fd8\u6709\u516d\u4e03\u4e2a\u56db\u4e94\u6bb5\u7684 \u597d\u624b\uff0c\u800c\u4eca\u516d\u6bb5\u9ad8\u624b\u6b7b\u4e86\u4e00\u4eba\uff0c\u4e94\u6bb5\u597d\u624b\u6b7b\u4e86\u4e00\u4eba\uff0c\u4e09\u56db\u6bb5\u7684\u597d\u624b\u6b7b\u4e86\u4e24\u4eba\uff0c\u8fd9\u5168\u8239\u501a\u4e3a\u957f \u57ce\u7684\u4e03\u6bb5\u529b\u58eb\u53c8\u53d7\u4f24\u4e86\uff0c\u771f\u4e2a\u662f\u4f24\u4ea1\u60e8\u91cd\uff0c\u4e0d\u89c9\u90fd\u5bd2\u4e86\u5fc3\u3002\u6709\u4eba\u4e3b\u5f20\u4e0d\u8ffd\uff0c\u53e6\u4e00\u4e2a\u672a\u6709\u53d7\u4f24 \u7684\u4e03\u6bb5\u9ad8\u624b\uff0c\u770b\u51fa\u4e8e\u627f\u73e0\u7684\u5c0f\u8239\u5df2\u7ecf\u6f0f\u6c34\uff0c\u6392\u4f17\u559d\u9053\uff1a\u201c\u5929\u7687\u6b66\u58eb\uff0c\u5c82\u53ef\u5931\u5a01\u3002\u903c\u8fd1\u53bb\uff0c\u7528 \u4e71\u7bad\u5c04\u4ed6\uff01\u201d\u4ed6\u8bf4\u7684\u662f\u65e5\u672c\u8bdd\uff0c\u4e8e\u627f\u73e0\u542c\u4e0d\u660e\u767d\uff0c\u5f20\u9ed1\u4e0e\u90a3\u4e66\u751f\u5728\u6d77\u6ee8\u957f\u5927\uff0c\u61c2\u5f97\u65e5\u8bed\uff0c\u542c \u4e86\u53ef\u662f\u5927\u5403\u4e00\u60ca\uff0c\u5c0f\u8239\u6f0f\u6c34\uff0c\u88c2\u53e3\u6269\u5927\uff0c\u96be\u4ee5\u6301\u4e45\uff0c\u88ab\u4e71\u7bad\u6512\u5c04\uff0c\u7eb5\u7136\u62b5\u6321\u5f97\u4f4f\uff0c\u4e5f\u96be\u514d\u8986 \u821f\u706d\u9876\u4e4b\u7978\u3002 \u3000\u3000\u5f20\u9ed1\u54ac\u7259\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u4e0e\u4ed6\u62fc\u4e86\u3002\u53ea\u53ef\u60dc\u4fe1\u606f\u4f20\u4e0d\u5230\u53f6\u5927\u54e5\u8033\u4e2d\u3002\u201d\u4e66\u751f\u9053\uff1a\u201c\u54ea\u4f4d\u53f6 \u5927\u54e5\uff1f\u201d\u5f20\u9ed1\u9053\uff1a\u201c\u53f0\u5dde\u4e49\u519b\u9996\u9886\u53f6\u5b97\u7559\uff0c\u54b1\u4eec\u662f\u7ed9\u4ed6\u62a5\u4fe1\u7684\u3002\u201d\u5f20\u9ed1\u77e5\u9053\u4e86\u4e66\u751f\u662f\u81ea\u5df1 \u4eba\uff0c\u8bf4\u8bdd\u518d\u65e0\u987e\u5fcc\u3002\u4e66\u751f\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u7a81\u7136\u6325\u624b\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u5212\u8239\u9003\u547d\uff0c\u6284\u5c0f\u8def\u53bb\u53f0 \u5dde\u3002\u201d\u5728\u8170\u95f4\u4e00\u62cd\uff0c\u5ffd\u5730\u89e3\u4e0b\u4e00\u67c4\u8f6f\u94f6\u5251\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c\u4f3c\u5927\u9e64\u4e00\u822c\u98de\u4e86\u8d77\u6765\uff01\u8d21\u8239\u4e0a\u54d7\u7136\u5927 \u547c\u3002\u5343\u7bad\u9f50\u53d1\uff0c\u90a3\u4e66\u751f\u4eba\u5728\u534a\u7a7a\uff0c\u94f6\u5251\u5374\u6325\u8d77\u4e00\u5708\u94f6\u8679\uff0c\u5c06\u4e71\u7bad\u7eb7\u7eb7\u62e8\u843d\uff0c\u5c06\u8fd1\u8239\u8fb9\uff0c\u8eab\u5b50 \u4e00\u6c89\uff0c\u53ea\u89c1\u4ed6\u53cc\u811a\u4e00\u8e0f\uff0c\u5de6\u811a\u8e0f\u5728\u53f3\u811a\u811a\u80cc\u4e0a\uff0c\u4e00\u501f\u529b\u8eab\u5b50\u53c8\u5347\u9ad8\u6570\u5c3a\uff0c\u6070\u6070\u843d\u4e0b\u7b2c\u4e8c\u5c42\u7684 \u8239\u697c\uff0c\u65e5\u672c\u4eba\u54ea\u66fe\u89c1\u8fc7\u8fd9\u6837\u7684\u8f7b\u529f\u7edd\u6280\uff0c\u5341\u6709\u516b\u4e5d\u53e3\u77aa\u76ee\u5446\uff0c\u6709\u4e24\u540d\u56db\u6bb5\u6b66\u58eb\u4e0d\u77e5\u6b7b\u6d3b\uff0c\u4e58 \u4ed6\u4e00\u4e0a\u8239\u697c\uff0c\u4fbf\u6765\u5077\u88ad\uff0c\u5c1a\u672a\u6cbe\u8eab\uff0c\u90fd\u7ed9\u4ed6\u957f\u5251\u523a\u4f24\u4e86\u3002 \u3000\u3000\u90a3\u540d\u4e03\u6bb5\u6b66\u58eb\u6c14\u6781\u6012\u6781\uff0c\u4ed6\u662f\u56fd\u4e2d\u6709\u6570\u7684\u5251\u5ba2\uff0c\u662f\u4e5d\u6bb5\u5251\u5ba2\u6c5f\u53e3\u5bcc\u58eb\u6280\u7684\u5165\u5ba4\u5f1f\u5b50\uff0c\u62d4 \u51fa\u957f\u5251\uff0c\u7ad9\u4e86\u4e2a\u95e8\u6237\uff0c\u4fbf\u60f3\u6311\u6218\uff0c\u5176\u4ed6\u7684\u65e5\u672c\u6b66\u58eb\u4e5f\u5404\u62d4\u51fa\u502d\u5200\uff0c\u56f4\u5728\u56db\u8fb9\uff0c\u6392\u4e86\u4e00\u4e2a\u4ee5\u4f17 \u6b3a\u5be1\u7684\u7fa4\u6bb4\u5c40\u9762\u3002\u90a3\u4e66\u751f\u88ab\u56f4\u5728\u6838\u5fc3\uff0c\u50b2\u7136\u4e0d\u60e7\uff0c\u76ee\u5149\u5982\u7535\uff0c\u5468\u906d\u4e00\u626b\uff0c\u795e\u5a01\u51db\u51db\uff0c\u4f17\u4eba\u90fd \u66fe\u773c\u89c1\u4ed6\u5927\u529b\u63b7\u94c1\u951a\uff0c\u98de\u8eab\u62e8\u4e71\u7bad\u7684\u672c\u9886\u3002\u4e00\u65f6\u95f4\uff0c\u7adf\u6ca1\u4eba\u6562\u4e0a\u524d\u52a8\u624b\u3002\u6b63\u662f\uff1a \u3000\u3000\u4e14\u770b\u957f\u6c5f\u6ce2\u6d6a\u6d8c\uff0c\u82f1\u96c4\u6d69\u6c14\u626b\u502d\u6c1b \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u5341\u4e00\u56de\u3000\u9752\u5251\u60ca\u6d9b\u3000\u7591\u4e91\u8ff7\u4fa0\u5973\u3000\u516c\u5802\u770b\u5ba1\u3000\u6b63\u6c14\u51db\u5f3a\u7cb1 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u5341\u4e00\u56de\u3000\u9752\u5251\u60ca\u6d9b\u3000\u7591\u4e91\u8ff7\u4fa0\u5973\u3000\u516c\u5802\u770b\u5ba1\u3000\u6b63\u6c14\u51db\u5f3a\u7cb1 \u3000\u3000\u90a3\u4e66\u751f\u559d\u9053\uff1a\u201c\u53eb\u4f60\u4eec\u7684\u901a\u8bd1\u6765\u3002\u201d\u4ed6\u867d\u7136\u61c2\u5f97\u65e5\u8bed\uff0c\u5728\u502d\u5bc7\u9762\u524d\uff0c\u5982\u4e00\u53e5\u4e5f\u4e0d\u80af\u8bf4\uff0c \u90a3\u4e9b\u65e5\u672c\u6d6a\u4eba\u6709\u4e00\u534a\u4ee5\u4e0a\u61c2\u5f97\u4e2d\u56fd\u8bdd\uff0c\u7528\u4e2d\u56fd\u8bdd\u9053\uff1a\u201c\u770b\u4f60\u4e5f\u662f\u4e00\u4e2a\u82f1\u96c4\uff0c\u4f60\u6709\u4ec0\u4e48\u540e\u4e8b\u53ef \u8981\u4ea4\u4ee3\uff0c\u8bf4\u4e0e\u6211\u4eec\u542c\u4e5f\u662f\u4e00\u6837\uff0c\u4f55\u5fc5\u8981\u4ec0\u4e48\u901a\u8bd1\uff1f\u201d\u90a3\u4e66\u751f\u53cc\u773c\u4e00\u7ffb\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u6211\u4e0a\u4e86 \u8fd9\u6761\u8239\u6765\uff0c\u672c\u6765\u5c31\u4e0d\u6253\u7b97\u6d3b\u7740\u56de\u53bb\uff0c\u53ef\u4e5f\u5f97\u9080\u4f60\u4eec\u8fd9\u4e00\u5e72\u4eba\u966a\u6211\u5230\u9634\u95f4\u8d70\u8d70\u3002\u201d\u5251\u628a\u4e00\u7ffb\uff0c \u94f6\u5149\u9aa4\u8d77\uff0c\u51fa\u5176\u4e0d\u610f\u5730\u4e00\u4e3e\u5c06\u4e24\u540d\u56db\u6bb5\u6b66\u58eb\u7684\u502d\u5200\u524a\u65ad\uff0c\u90a3\u540d\u4e03\u6bb5\u6b66\u58eb\u5927\u543c\u4e00\u58f0\uff0c\u957f\u5251\u4e00\u632f \u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u53cd\u624b\u523a\u624e\uff0c\u4e03\u6bb5\u9ad8\u624b\uff0c\u529f\u529b\u679c\u662f\u4e0d\u51e1\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u201d\u4e00\u58f0\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u90a3\u4e66 \u751f\u500f\u5730\u817e\u7a7a\u98de\u8d77\uff0c\u51e0\u67c4\u502d\u5200\u4ece\u4ed6\u7684\u811a\u4e0b\u780d\u8fc7\u3002\u4ea4\u6362\u4e86\u4e00\u62db\uff0c\u5927\u5bb6\u90fd\u77e5\u9053\u5bf9\u65b9\u4e0d\u597d\u76f8\u4e0e\uff0c\u90a3\u540d \u4e03\u6bb5\u6b66\u571f\u6043\u7740\u4eba\u591a\uff0c\u65e0\u987b\u9632\u5fa1\uff0c\u8fde\u8fdb\u51e0\u624b\u62db\u6570\uff0c\u4e58\u7740\u90a3\u6266\u751f\u8eab\u5b50\u60ac\u7a7a\uff0c\u96be\u4ee5\u7528\u529b\uff0c\u633d\u4e86\u4e00\u4e2a \u5251\u82b1\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u8fde\u523a\u4e86\u4e94\u516d\u5251\uff0c\u90a3\u4e66\u751f\u5728\u534a\u7a7a\u4e2d\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u4e00\u53e3\u5251\u5982\u94f6\u86c7 \u4e71\u63a3\uff0c\u5411\u4e0b\u75be\u523a\uff0c\u4e5f\u662f\u8f6c\u77ac\u4e4b\u95f4\uff0c\u5c31\u8fde\u523a\u4e86\u4e94\u516d\u5251\uff0c\u6bcf\u4e00\u6b21\u90fd\u662f\u4e66\u751f\u7684\u5251\u5c16\u89e6\u5230\u4e03\u6bb5\u6b66\u58eb\u7684 \u5706\u5934\u5251\uff0c\u4fbf\u501f\u529b\u98de\u8d77\uff0c\u8fde\u6321\u4e86\u4e94\u516d\u5251\u90fd\u672a\u6cbe\u5730\uff0c\u771f\u5982\u82cd\u9e70\u6251\u51fb\uff0c\u873b\u8712\u70b9\u6c34\uff0c\u4ed9\u9e64\u56de\u7fd4\uff0c\u65e5\u672c \u7684\u6b66\u58eb\u4eec\uff0c\u54ea\u66fe\u89c1\u8fc7\u8fd9\u6837\u7684\u8f7b\u529f\u7edd\u6280\u914d\u4e0a\u7edd\u5999\u7684\u5251\u6cd5\uff0c\u5413\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u7adf\u6709\u4e00\u5927\u534a\u4eba\u5fd8\u4e86\u52a8 \u624b\uff0c\u53ea\u6709\u90a3\u540d\u4e03\u6bb5\u9ad8\u624b\uff0c\u5168\u795e\u8d2f\u6ce8\uff0c\u4e00\u5251\u7d27\u4f3c\u4e00\u5251\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u51ed\u4f60\u8fd9\u6837\u8eab\u5b50\u60ac\u7a7a\uff0c\u5982\u4f55 \u80fd\u591f\u6321\u5f97\u4f4f\u6211\u7684\u8fde\u73af\u653b\u51fb\uff1f\u201d\u5916\u56f4\u7684\u90a3\u4e9b\u6b66\u571f\uff0c\u60ca\u9b42\u7a0d\u5b9a\uff0c\u4e5f\u53d1\u4e00\u58f0\u558a\uff0c\u7eb7\u7eb7\u628a\u502d\u5200\u780d\u6765\uff01 \u3000\u3000\u5ffd\u542c\u5f97\u90a3\u4e66\u751f\u731b\u559d\u4e00\u58f0\uff0c\u4ed6\u76f8\u8c8c\u6e05\u79c0\uff0c\u770b\u6765\u8eab\u6750\u7626\u5f31\uff0c\u8fd9\u4e00\u559d\u5374\u5982\u6674\u5929\u8d77\u4e86\u4e2a\u9739\u96f3\uff0c\u8fde \u90a3\u4e2a\u4e03\u6bb5\u6b66\u58eb\u4e5f\u5413\u4e86\u4e00\u8df3\uff0c\u53ea\u89c9\u5f97\u8033\u9f13\u7ed9\u9707\u5f97\u55e1\u55e1\u4f5c\u54cd\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4f46\u89c1\u90a3\u4e66\u751f\u5728\u534a \u7a7a\u4e2d\u65cb\u98ce\u4e00\u8f6c\uff0c\u4e24\u540d\u4e09\u6bb5\u6b66\u58eb\u773c\u524d\u4e00\u9ed1\uff0c\u88ab\u4ed6\u626f\u7740\u548c\u670d\u7684\u7b8d\u8170\u63d0\u4e86\u8d77\u6765\uff0c\u90a3\u540d\u4e03\u6bb5\u9ad8\u624b\u6536\u624b \u4e0d\u53ca\uff0c\u5530\u5530\u4e24\u5251\uff0c\u90fd\u523a\u5230\u540c\u4f34\u8eab\u4e0a\uff0c\u5e78\u4ed6\u89c1\u673a\u5f97\u5feb\uff0c\u5251\u950b\u7a0d\u504f\uff0c\u9976\u662f\u5982\u6b64\uff0c\u90a3\u4e24\u540d\u6b66\u58eb\u7684\u811a \u7b4b\u4e5f\u5df2\u88ab\u5251\u950b\u6311\u65ad\u3002 \u3000\u3000\u90a3\u4e66\u751f\u52a8\u4f5c\u5feb\u4f3c\u7535\u5149\u77f3\u706b\uff0c\u5c06\u4e24\u540d\u6b66\u571f\u4e00\u629b\uff0c\u903c\u5f97\u90a3\u4e9b\u5305\u56f4\u7684\u6b66\u58eb\u7eb7\u7eb7\u95ea\u907f\uff0c\u4e00\u8f6c\u8eab\u53c8 \u5c06\u4e24\u540d\u502d\u5bc7\u8e22\u4e0b\u957f\u6c5f\uff0c\u5f85\u90a3\u4e03\u6bb5\u6b66\u58eb\u7741\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u4ed6\u5df2\u80cc\u501a\u7740\u8239\u697c\u7684\u94c1\u680f\u6746\uff0c\u624b\u4e2d\u957f\u5251\u5140 \u81ea\u98a0\u52a8\u4e0d\u4f11\uff0c\u55e1\u55e1\u4f5c\u54cd\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u8c01\u966a\u6211\u5230\u9634\u95f4\u8d70\u8d70\uff1f\u201d\u4e00\u526f\u62fc\u547d\u7684\u795e\u6c14\uff0c\u4ed6\u80cc \u9762\u662f\u957f\u6c5f\uff0c\u65e0\u540e\u987e\u4e4b\u5fe7\uff0c\u65e5\u672c\u7684\u8d21\u4f7f\u4e5f\u81ea\u5fc3\u614c\uff0c\u60f3\u9053\uff1a\u201c\u82e5\u7136\u5408\u4f17\u6b66\u58eb\u9f50\u4e0a\uff0c\u7eb5\u80fd\u5c06\u4ed6\u6740 \u6b7b\uff0c\u81ea\u5df1\u8fd9\u8fb9\u7684\u6b66\u58eb\uff0c\u53ea\u6050\u4e5f\u5f97\u4f24\u4ea1\u8fc7\u534a\uff01\u201d \u3000\u3000\u8239\u697c\u91cc\u8d70\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u8fd9\u4eba\u5374\u662f\u660e\u671d\u5b98\u5458\u7684\u773c\u9970\uff0c\u539f\u6765\u662f\u53f0\u5dde\u77e5\u5e9c\u6d3e\u6765\u966a\u540c\u65e5\u672c\u7684\u8d21\u4f7f \u8fdb\u4eac\u7684\uff0c\u8fd9\u5b98\u5458\u4e00\u89c1\u4e66\u751f\uff0c\u9762\u8272\u5237\u5730\u4e00\u4e0b\u53d8\u5f97\u82cd\u767d\uff0c\u4f4e\u58f0\u547c\u9053\uff1a\u201c\u94c1\u516c\u5b50\uff01\u201d \u3000\u3000\u88ab\u79f0\u505a\u201c\u94c1\u516c\u5b50\u201d\u7684\u4e66\u751f\u6309\u5251\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u90a3\u540d\u5b98\u5458\u65bd\u793c\u9053\uff1a\u201c\u53f0\u5dde\u5b88\u5907\u9ec4\u5927 \u5e86\uff0c\u6211\u548c\u5c0a\u7fc1\u76f8\u8bc6\u591a\u5e74\u3002\u201d\u90a3\u4e66\u751f\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u90a3\u66f4\u597d\u4e86\uff0c\u542c\u8bf4\u4f60\u4eec\u6b63\u8981\u627e\u6211\uff1f\u201d\u9ec4\u5b88\u5907\u6253 \u4e86\u4e2a\u5343\u9053\uff1a\u201c\u4e0d\u6562\uff01\u201d\u90a3\u4e66\u751f\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u6562\u4e0d\u6562\u7684\uff1f\u6211\u5982\u4eca\u662f\u81ea\u5df1\u6295\u6848\u6765\u4e86\u3002\u4f60\u4e0e\u502d\u5974\u7684 \u8d21\u4f7f\u8bf4\u53bb\uff0c\u6211\u81ea\u5230\u53f0\u5dde\u6295\u6848\uff0c\u53eb\u4ed6\u6d3e\u4e00\u6761\u5c0f\u8239\u9001\u6211\u53bb\u3002\u518d\u4e0d\u653e\u5fc3\uff0c\u52a0\u6d3e\u51e0\u540d\u6b66\u58eb\u4e0e\u6211\u540c\u53bb\u4e5f \u884c\u3002\u82e5\u7136\u4ed6\u4eec\u8d70\u8981\u5728\u8fd9\u91cc\u64d2\u6211\uff0c\u6740\u6211\uff0c\u90a3\u4e5f\u884c\uff0c\u6211\u4e00\u6982\u5949\u966a\uff0c\u53ea\u662f\u5200\u5251\u65e0\u60c5\uff0c\u6211\u5c31\u662f\u547d\u4e27\u957f \u6c5f\uff0c\u8fd9\u6761\u502d\u8239\u7684\u8d21\u4f7f\u4e5f\u672a\u5fc5\u80fd\u4fdd\u7740\u5934\u9885\u5230\u5317\u4eac\u8fdb\u8d21\uff01\u201d\u957f\u5251\u4e00\u6296\uff0c\u53c8\u662f\u55e1\u55e1\u4f5c\u54cd\u3002 \u3000\u3000\u90a3\u8d21\u4f7f\u7c97\u6653\u6c49\u8bed\uff0c\u542c\u4e86\u8fd9\u756a\u8bf4\u8bdd\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u5c06\u90a3\u9ec4\u5b88\u5907\u62c9\u8fc7\u4e00\u8fb9\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u539f\u6765 \u4ed6\u5c31\u662f\u90a3\u4e2a\u6740\u4eba\u8d8a\u8d27\uff0c\u80c6\u6562\u6495\u6bc1\u6211\u4eec\u592a\u9633\u65d7\u7684\u94c1\u955c\u5fc3\uff1f\u201d\u5b88\u5907\u9053\uff1a\u201c\u4ed6\u8bf4\u2014\u2014\u201d\u8d21\u4f7f\u9053\uff1a \u201c\u4ed6\u8bf4\u7684\u6211\u77e5\u9053\u5566\u3002\u4f60\u770b\u4ed6\u662f\u771f\u5fc3\u6295\u6848\u5417\uff1f\u201d\u9ec4\u5b88\u5907\u9053\uff1a\u201c\u4e2d\u56fd\u7684\u8bfb\u4e66\u4eba\u6700\u8bb2\u91cd\u5c0a\u541b\u5b5d\u4eb2\u4e4b \u9053\u3002\u6211\u770b\u4ed6\u662f\u771f\u5fc3\u6295\u6848\u7684\u3002\u201d\u90a3\u8d21\u4f7f\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0c\u6211\u4eec\u5c0a\u656c\u4ed6\u662f\u6761\u597d\u6c49\uff0c\u5c31\u8fd9\u6837\u529e \u5566\u3002\u7b49\u4e0b\u6211\u4eec\u653e\u4e00\u6761\u6a61\u76ae\u8247\uff0c\u7531\u5927\u95e8\u536b\u548c\u4f60\u62bc\u4ed6\u53bb\u3002\u73b0\u5728\u8bf7\u4ed6\u5148\u7528\u9152\u996d\u3002\u201d\u5927\u95e8\u536b\u5c31\u662f\u90a3\u4e2a \u4e03\u6bb5\u6b66\u58eb\u7684\u540d\u5b57\u3002\u9ec4\u5b88\u5907\u5c06\u8d21\u4f7f\u7684\u8bdd\u8f6c\u8ff0\u4e86\uff0c\u90a3\u4e66\u751f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u6b7b\u4ea6\u4e0d\u60e7\uff0c\u4f55\u6015\u559d\u4ed6\u7684 \u9152\uff0c\u53eb\u4ed6\u62ff\u51fa\u6765\uff0c\u966a\u7740\u6211\u559d\uff01\u201d\u7b11\u58f0\u9707\u8361\u957f\u6c5f\uff0c\u968f\u7740\u6c5f\u98ce\u76f4\u9001\u5230\u4e8e\u627f\u73e0\u7684\u8033\u4e2d\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u8fd9\u53ea\u5c0f\u821f\uff0c\u5df2\u6491\u51fa\u4e86\u4e8c\u4e09\u91cc\u7684\u6c5f\u9762\u4e4b\u9065\uff0c\u542c\u5f97\u90a3\u4e66\u751f\u7684\u7b11\u58f0\uff0c\u4e8e\u627f\u73e0\u7ad9\u5728\u8239\u5934\uff0c \u6781\u76ee\u8fdc\u773a\uff0c\u4f9d\u7a00\u89c1\u5230\u90a3\u4e66\u751f\u5728\u502d\u5bc7\u7684\u7c07\u62e5\u4e4b\u4e0b\u4e3e\u8d77\u4e00\u4e2a\u5927\u7ea2\u846b\u82a6\uff0c\u5f80\u53e3\u91cc\u5012\uff0c\u4f3c\u662f\u559d\u9152\uff0c\u4e0d \u7981\u5927\u4e3a\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u201c\u600e\u4e48\u9002\u624d\u6253\u751f\u6253\u6b7b\uff0c\u73b0\u5728\u53c8\u4e0e\u502d\u5974\u559d\u8d77\u9152\u6765\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u6050\u4e66\u751f\u4e2d \u4e86\u502d\u5974\u7684\u8be1\u8ba1\uff0c\u4f9d\u5979\u7684\u5fc3\u610f\uff0c\u8fd8\u60f3\u6491\u56de\u53bb\u770b\u3002\u5f20\u9ed1\u82e6\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u5927\u4e8b\u5728\u8eab\uff0c\u600e\u597d\u56de\u53bb\uff0c\u518d \u8bf4\u8fd9\u6761\u8239\u5c31\u5feb\u6c89\u5566\uff0c\u9003\u547d\u8fd8\u4e0d\u80fd\u591f\u5462\uff0c\u5c1a\u8bf4\u56de\u53bb\uff1f\u201d \u3000\u3000\u8239\u8231\u7684\u90a3\u6761\u88c2\u7f1d\u73b0\u5728\u5df2\u6e10\u6e10\u6269\u5927\uff0c\u6c5f\u6c34\u6c69\u6c69\u6d78\u5165\uff0c\u5f20\u9ed1\u8200\u6c34\u6cfc\u51fa\uff0c\u5165\u591a\u51fa\u5c11\u3002\u539f\u6765\u8fd9\u4e24 \u6761\u88c2\u7f1d\u662f\u9002\u624d\u6253\u6597\u4e4b\u65f6\uff0c\u90a3\u4e24\u4e2a\u65e5\u672c\u6b66\u58eb\u811a\u4e0a\u7a7f\u7740\u9489\u978b\uff0c\u6545\u610f\u7528\u529b\u8e0f\u88c2\u8239\u677f\u7684\u3002\u5728\u8fd9\u5927\u6c5f\u4e4b \u4e0a\uff0c\u8239\u5230\u4e2d\u6d41\uff0c\u5982\u4f55\u8865\u6f0f\uff01 \u3000\u3000\u4e8e\u627f\u73e0\u4e0d\u8c19\u6c34\u6027\uff0c\u7f57\u889c\u88ab\u6c34\u6d78\u6e7f\uff0c\u811a\u677f\u51b0\u51c9\uff0c\u5fc3\u5934\u4e5f\u611f\u5230\u4e00\u80a1\u51c9\u610f\u3002\u5ffd\u89c1\u4e00\u6761\u5c0f\u8239\u659c\u523a \u9a76\u6765\uff0c\u539f\u6765\u662f\u90a3\u6761\u8001\u6e14\u592b\u7684\u8239\u3002\u8001\u6e14\u592b\u5728\u8239\u5934\u4e0a\u957f\u63d6\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u76f8\u516c\u6551\u547d\u4e4b\u6069\uff0c\u8bf7\u8fc7\u6765\u53d7 \u6211\u7236\u5973\u4e00\u62dc\u3002\u201d\u8fd9\u6761\u5c0f\u8239\u6765\u5f97\u6b63\u662f\u65f6\u5019\uff0c\u5f20\u9ed1\u7acb\u523b\u548c\u4e8e\u627f\u73e0\u8fc7\u53bb\uff0c\u8be5\u8239\u4e0d\u4e45\u5c31\u5728\u6c5f\u5fc3\u6c89\u6ca1 \u4e86\u3002 \u3000\u3000\u90a3\u6e14\u5bb6\u5973\u52a0\u5f20\u9ed1\u628a\u8247\u5212\u6868\uff0c\u4e8e\u627f\u73e0\u548c\u90a3\u6e14\u7fc1\u5728\u8239\u8231\u4e2d\u53d9\u8bdd\uff0c\u539f\u6765\u90a3\u6e14\u7fc1\u662f\u53f0\u5dde\u4eba\u6c0f\uff0c\u8c08 \u8d77\u502d\u5bc7\u5728\u53f0\u5dde\u4e00\u5e26\u7684\u6a2a\u884c\u65e0\u5fcc\uff0c\u90a3\u6e14\u7fc1\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u53f0\u5dde\u4eca\u65e5\u867d\u7136\u6709\u671d\u5ef7\u7684\u77e5\u5e9c\u5927\u8859\uff0c\u502d\u5bc7 \u5374\u6210\u4e86\u592a\u4e0a\u7687\u5566\uff0c\u522b\u8bf4\u6211\u4eec\uff0c\u8fde\u5b98\u5bb6\u4e5f\u6015\u4ed6\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u502d\u5bc7\u7316\u6485\u7adf\u4e00\u81f3\u4e8e\u65af\u4e48\uff1f\u201d\u90a3\u6e14\u7fc1\u9053\uff1a\u201c\u8c01\u8bf4\u4e0d\u662f\u5462\u3002\u4e0a\u4e2a\u6708\u6709\u6761\u8d70\u79c1\u8d27 \u7684\u502d\u8239\uff0c\u9a76\u81f3\u5b81\u6d77\uff0c\u5b81\u6d77\u6709\u4e2a\u5546\u4eba\uff0c\u8d2a\u56fe\u5c0f\u5229\uff0c\u4e0a\u4e86\u4ed6\u7684\u94a9\uff0c\u5728\u6e2f\u53e3\u8bb2\u660e\u4ee5\u8d27\u6613\u8d27\uff0c\u90a3\u502d\u8239 \u7adf\u7136\u5f3a\u5356\u5f3a\u4e70\uff0c\u62ac\u9ad8\u81ea\u5df1\u7684\u7269\u4ef7\uff0c\u538b\u4f4e\u90a3\u5546\u4eba\u7684\u8d27\u4ef7\uff0c\u90a3\u5546\u4eba\u5f53\u7136\u4e0d\u5141\uff0c\u502d\u8239\u7684\u8239\u4e3b\u5c31\u5728\u6e2f \u53e3\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u5c45\u7136\u6043\u5f3a\u884c\u51f6\uff0c\u786c\u6307\u90a3\u5546\u4eba\u8fdd\u53cd\u5408\u7ea6\uff0c\u5c06\u5546\u4eba\u6253\u5f97\u6b7b\u53bb\u6d3b\u6765\uff0c\u628a\u5546\u4eba\u7684\u8d27 \u8239\u51ff\u6c89\uff0c\u8239\u4e0a\u7684\u8d37\u7269\u5168\u90e8\u52ab\u4e0a\u502d\u8239\u3002\u8fd9\u8fd8\u4e0d\u7b97\uff0c\u90a3\u5546\u4eba\u7684\u59bb\u5973\u4e5f\u5728\u8d27\u8239\u4e4b\u4e0a\uff0c\u502d\u8239\u7684\u8239\u4e3b\u8fde \u4ed6\u7684\u59bb\u5973\u90fd\u52ab\u4e86\u8fc7\u6765\uff0c\u8bf4\u662f\u8981\u62b5\u507f\u635f\u5931\uff0c\u90a3\u5546\u4eba\u8eab\u53d7\u6bd2\u6253\uff0c\u53c8\u76ee\u7779\u59bb\u5973\u88ab\u52ab\uff0c\u4e00\u53e3\u6c14\u8f6c\u4e0d\u8fc7 \u6765\uff0c\u7acb\u523b\u6295\u6c5f\u6b7b\u4e86\u3002\u8fd9\u65f6\uff0c\u5df2\u60f9\u8d77\u4e86\u516c\u6124\uff0c\u5728\u6e2f\u53e3\u56f4\u89c2\u7684\u95f2\u4eba\uff0c\u7eb7\u7eb7\u559d\u6253\uff0c\u90a3\u6761\u502d\u8239\uff0c\u96c7\u6709 \u5341\u591a\u4e2a\u4e2d\u56fd\u811a\u592b\uff0c\u8fd9\u65f6\u8239\u5230\u6e2f\u53e3\uff0c\u7406\u5e94\u7ed3\u6e05\u811a\u529b\uff0c\u90a3\u502d\u8239\u8239\u4e3b\u53c8\u6043\u5f3a\u4e0d\u7ed9\uff0c\u811a\u592b\u4e5f\u7eb7\u7eb7\u548c\u4ed6 \u7406\u8bba\uff1b\u8fd9\u6837\u4e00\u6765\uff0c\u8239\u4e0a\u7684\u811a\u592b\u548c\u5cb8\u4e0a\u62b1\u4e0d\u5e73\u7684\u95f2\u4eba\uff0c\u90fd\u56f4\u7740\u90a3\u4e2a\u502d\u8239\uff0c\u90a3\u8258\u502d\u8239\u7684\u6d6a\u4eba\u5ffd\u7684 \u62d4\u51fa\u502d\u5200\uff0c\u6307\u7740\u8239\u4e0a\u7684\u818f\u836f\u65d7\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u2018\u6709\u8fd9\u9762\u65d7\u5b50\u4fbf\u53ef\u6a2a\u884c\u4e2d\u56fd\uff0c\u4f60\u4eec\u7684\u5b98\u5e9c\u89c1\u4e86\u8fd9 \u9762\u65d7\u5b50\uff0c\u90fd\u8981\u606d\u606d\u656c\u656c\u793c\u5f85\u6211\u4eec\uff0c\u4f60\u4eec\u6562\u5728\u8fd9\u9762\u65d7\u5b50\u4e4b\u4e0b\u9f13\u566a\uff1f\u2019\u811a\u592b\u548c\u95f2\u4eba\u4e0d\u7406\u4ed6\u8fd9\u9762\u65d7 \u5b50\uff0c\u4ecd\u7136\u548c\u4ed6\u7406\u8bba\uff0c\u90a3\u502d\u8239\u4e0a\u7684\u6d6a\u4eba\u4e00\u4e0d\u505a\u4e8c\u4e0d\u4f11\uff0c\u5148\u4e0b\u624b\u4e3a\u5f3a\uff0c\u7adf\u7136\u6325\u5200\u4e71\u65a9\uff0c\u811a\u592b\u548c\u62b1 \u4e0d\u5e73\u7684\u95f2\u4eba\u624b\u65e0\u5bf8\u94c1\uff0c\u7acb\u523b\u7ed9\u6740\u4f24\u4e86\u5341\u591a\u4e2a\uff0c\u90a3\u4e9b\u6d6a\u4eba\u8fd8\u8981\u8ffd\u6740\u3002\u8fd9\u65f6\u5ffd\u7136\u5728\u5cb8\u4e0a\u56f4\u89c2\u7684\u95f2 \u4eba\u4e2d\u8d70\u51fa\u4e00\u4e2a\u5c11\u5e74\uff0c\u5927\u58f0\u559d\u9053\uff1a\u2018\u51ed\u8fd9\u9762\u65d7\u5b50\u5c31\u53ef\u4ee5\u6a2a\u884c\u65e0\u5fcc\u4e86\u4e48\uff1f\u2019\u53ea\u89c1\u4ed6\u98de\u8eab\u4e00\u8dc3\uff0c\u6377 \u4f3c\u7334\u733f\uff0c\u4e0a\u4e86\u502d\u8239\uff0c\u722c\u4e0a\u6845\u6746\uff0c\u5c06\u90a3\u9762\u818f\u836f\u65d7\u53d6\u4e0b\u6765\uff0c\u6495\u6210\u56db\u7247\uff0c\u90a3\u502d\u8239\u7684\u8239\u4e3b\u62d4\u5200\u65ab\u4ed6\uff0c \u88ab\u4ed6\u4e00\u5251\u6325\u4e3a\u4e24\u6bb5\uff0c\u63a5\u7740\u628a\u90a3\u5341\u51e0\u4e2a\u884c\u51f6\u7684\u6d6a\u4eba\uff0c\u4e2a\u4e2a\u6253\u5012\uff0c\u5c06\u90a3\u4e9b\u6d6a\u4eba\u7684\u502d\u5200\uff0c\u5168\u90e8\u6298 \u65ad\uff0c\u629b\u4e0b\u6c5f\u4e2d\uff0c\u653e\u4e86\u90a3\u5546\u4eba\u7684\u59bb\u5973\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u4fbf\u626c\u957f\u8d70\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u542c\u5f97\u7709\u98de\u8272\u821e\uff0c\u8fde\u58f0\u53eb\u9053\uff1a\u201c\u75db\u5feb\uff0c\u75db\u5feb\uff01\u8fd9\u9752\u5e74\u662f\u8c01\uff1f\u201d\u90a3\u6e14\u7fc1\u9053\uff1a\u201c\u672c\u6765\u6ca1 \u4eba\u77e5\u9053\u8fd9\u9752\u5e74\u662f\u8c01\uff0c\u4e0d\u77e5\u600e\u7684\u88ab\u4e00\u4e2a\u6c49\u5978\u6253\u542c\u5230\u4e86\uff0c\u8fd9\u9752\u5e74\u539f\u6765\u662f\u53f0\u5dde\u4e00\u4e2a\u544a\u8001\u56de\u4e61\u7684\u5fa1\u53f2 \u7684\u513f\u5b50\u3002\u8fd9\u8001\u5fa1\u53f2\u59d3\u94c1\uff0c\u540d\u53eb\u94c1\u94f1\uff0c\u5728\u53f0\u5dde\u7b97\u5f97\u662f\u540d\u95e8\u5927\u65cf\uff0c\u4e16\u4ee3\u4e3a\u5b98\uff0c\u94c1\u94f1\u505a\u5230\u5de6\u90fd\u5fa1 \u540f\uff0c\u636e\u8bf4\u662f\u4e8c\u54c1\u5927\u5b98\u4e86\u3002\u524d\u5e74\u624d\u544a\u8001\u56de\u4e61\u7684\u3002\u8fd9\u6c49\u5978\u5bc6\u62a5\u7ed9\u502d\u5974\u5728\u53f0\u5dde\u7684\u5e02\u8236\u4f7f\uff08\u7ba1\u9886\u8d38\u6613 \u7684\u5b98\uff0c\u76f8\u5f53\u4e8e\u4eca\u65e5\u9886\u4e8b\u9986\u7684\u5546\u4e1a\u53c2\u8d5e\uff09\u3002\u502d\u5974\u7684\u5e02\u8236\u4f7f\u903c\u53f0\u5dde\u77e5\u5e9c\u8981\u4eba\uff0c\u4f46\u90a3\u9752\u5e74\u5df2\u627e\u4e0d\u5230 \u4e86\u3002\u53f0\u5dde\u77e5\u5e9c\u65e0\u53ef\u5948\u4f55\uff0c\u7adf\u628a\u94c1\u8001\u5fa1\u53f2\u8f6f\u7981\u8d77\u6765\uff0c\u903c\u7740\u4ed6\u4ea4\u51fa\u513f\u5b50\u3002\u8fd9\u4ef6\u4e8b\u60c5\u8f70\u52a8\u4e86\u53f0\u5dde\uff0c \u73b0\u5728\u8fd8\u672a\u4e86\u7ed3\u5462\u3002\u4f60\u8bf4\u502d\u5bc7\u662f\u4e0d\u662f\u592a\u4e0a\u7687\uff0c\u8fde\u53f0\u5dde\u5e9c\u4e5f\u4e0d\u6562\u5bf9\u4ed6\u4eec\u6709\u534a\u70b9\u8fdd\u6297\u3002\u201d\u8bf4\u7f62\u53c8\u957f \u957f\u5730\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u8d77\u9002\u624d\u90a3\u540c\u8239\u5c11\u5e74\u81ea\u79f0\u94c1\u955c\u5fc3\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u5c31\u662f\u94c1\u94f1\u7684\u513f \u5b50\uff1f\u201d \u3000\u3000\u8001\u6e14\u7fc1\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u54ea\u4e00\u4f4d\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5c31\u662f\u9002\u624d\u5927\u6740\u502d\u5bc7\uff0c\u8df3\u4e0a\u502d\u8239\u7684\u90a3\u4e2a \u5c11\u5e74\u4e66\u751f\u3002\u201d\u8001\u6e14\u7fc1\u9053\uff1a\u201c\u679c\u7136\u597d\u4fca\u7684\u8eab\u624b\u3002\u53f0\u5dde\u7684\u77e5\u5e9c\u88ab\u502d\u5974\u5a01\u80c1\uff0c\u6b63\u8981\u62ff\u4ed6\u5f52\u6848\u5462\uff0c\u82e5 \u7136\u771f\u7684\u662f\u4ed6\uff0c\u8fd9\u56de\u72ec\u4e0a\u502d\u8239\uff0c\u5c82\u975e\u81ea\u6295\u7f57\u7f51\u3002\u201d\u4e8e\u627f\u73e0\u4e0d\u77e5\u600e\u7684\uff0c\u4e00\u8def\u95f7\u95f7\u4e0d\u4e50\uff0c\u4e3a\u90a3\u5c11\u5e74 \u4e66\u751f\u62c5\u5fc3\u3002 \u3000\u3000\u6e21\u6c5f\u4e4b\u540e\uff0c\u4e8e\u627f\u73e0\u4e0e\u90a3\u6e14\u5bb6\u7236\u5973\u5206\u624b\uff0c\u4e0e\u5f20\u9ed1\u5306\u5306\u8d76\u8def\uff0c\u6570\u65e5\u4e4b\u540e\uff0c\u6765\u5230\u53f0\u5dde\uff0c\u53f0\u5dde\u5728 \u6d59\u6c5f\u6cbf\u6d77\uff0c\u502d\u5bc7\u6b63\u5728\u53f0\u5dde\u9644\u8fd1\u4e00\u5e26\u7ea0\u7f20\u9a9a\u6270\uff0c\u53f0\u5dde\u4eba\u5fc3\u60f6\u60f6\uff0c\u5e02\u9762\u4e00\u7247\u8427\u6761\uff0c\u867d\u5728\u767d\u5929\uff0c\u5341 \u4e00\u5bb6\u5546\u5e97\uff0c\u5012\u6709\u516d\u4e03\u5bb6\u662f\u5173\u4e0a\u5e97\u95e8\u7684\u3002 \u3000\u3000\u5f20\u9ed1\u5e26\u4e8e\u627f\u73e0\u5230\u4e00\u4f4d\u540c\u4f34\u5bb6\u4e2d\u4f4f\u4e0b\uff0c\u51c6\u5907\u4e0e\u4e49\u519b\u8054\u7edc\u597d\u540e\uff0c\u4fbf\u5373\u52a8\u8eab\u3002\u8fc7\u4e86\u4e24\u5929\uff0c\u5ffd\u542c \u5f97\u5e02\u4e0a\u7eb7\u4f20\uff0c\u8bf4\u662f\u94c1\u516c\u5b50\u5df2\u81ea\u884c\u5230\u53f0\u5dde\u6295\u6848\uff0c\u4e5f\u6709\u4eba\u8bf4\u662f\u7ed9\u65e5\u672c\u7684\u6b66\u571f\u62bc\u89e3\u6765\u7684\uff0c\u4e8e\u627f\u73e0\u542c \u4e86\uff0c\u4fbf\u53eb\u5f20\u9ed1\u53bb\u6253\u542c\uff0c\u5f20\u9ed1\u5728\u53f0\u5dde\u7684\u670b\u53cb\u751a\u591a\uff0c\u8859\u5f79\u4e2d\u4e5f\u6709\u719f\u4eba\uff0c\u665a\u4e0a\u56de\u6765\u4e00\u8bf4\uff0c\u679c\u7136\u662f \u5b9e\uff0c\u542c\u8859\u5f79\u6240\u63cf\u7ed8\u7684\u5f62\u8c8c\uff0c\u786e\u662f\u821f\u4e2d\u7684\u4e66\u751f\u65e0\u7591\uff0c\u5e76\u4e14\u636e\u8859\u5f79\u6240\u62a5\u7684\u6d88\u606f\uff0c\u94c1\u955c\u5fc3\u73b0\u5728\u8fd8\u6263 \u62bc\u5728\u8859\u4e2d\uff0c\u4e09\u4e24\u65e5\u540e\u5c31\u6050\u6015\u8981\u79fb\u4ea4\u7ed9\u65e5\u672c\u4eba\u4e86\u3002\u8fd8\u542c\u8bf4\u77e5\u5e9c\u5927\u4eba\u56e0\u4e3a\u4ed6\u662f\u94c1\u5fa1\u53f2\u7684\u516c\u5b50\uff0c\u5bf9 \u4ed6\u751a\u4e3a\u4f18\u5f85\uff0c\u5e76\u4e0d\u5173\u5728\u7262\u623f\u4e2d\uff0c\u662f\u8f6f\u7981\u5728\u77e5\u5e9c\u5927\u4eba\u7684\u82b1\u5385\u5185\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u4e00\u6253\u542c\u6e05\u695a\uff0c\u5e76\u53eb\u5f20\u9ed1\u518d\u4ed4\u7ec6\u63a2\u660e\uff0c\u7ed8\u51fa\u4e86\u4e00\u4efd\u77e5\u5e9c\u8859\u95e8\u7684\u56fe\uff0c\u5f53\u665a\u8fc7\u4e86\u4e09\u66f4\uff0c \u4e8e\u627f\u73e0\u4fbf\u6362\u4e0a\u4e86\u591c\u884c\u8863\uff0c\u72ec\u81ea\u53bb\u63a2\u77e5\u5e9c\u8859\u95e8\u3002\u5f20\u9ed1\u867d\u7136\u4e0d\u5927\u8d5e\u540c\u4e8e\u627f\u73e0\u524d\u53bb\u5192\u9669\uff0c\u4f46\u60f3\u5230\u82e5 \u80fd\u5c06\u94c1\u955c\u5fc3\u6551\u51fa\uff0c\u5bf9\u4e49\u519b\u6297\u502d\uff0c\u4ea6\u662f\u5927\u6709\u5e2e\u52a9\uff0c\u56e0\u6b64\u4e5f\u5c31\u4e0d\u963b\u62e6\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u65e9\u628a\u77e5\u5e9c\u8859\u95e8\u7684\u5730\u56fe\u719f\u8bb0\u5fc3\u4e2d\uff0c\u6309\u56fe\u7d22\u9aa5\uff0c\u6beb\u4e0d\u8d39\u4e8b\u5730\u5c31\u6df7\u5165\u5185\u8859\uff0c\u6765\u5230\u82b1\u5385\uff0c \u5979\u7684\u8f7b\u529f\u867d\u7136\u8fd8\u672a\u5230\u6765\u53bb\u65e0\u8e2a\u3001\u98de\u884c\u7edd\u8ff9\u7684\u5883\u754c\uff0c\u4f46\u8981\u7792\u8fc7\u5e9c\u8859\u7684\u90a3\u4e9b\u6355\u5934\u62a4\u9662\uff0c\u5374\u662f\u7ef0\u7ef0 \u6709\u4f59\u3002 \u3000\u3000\u82b1\u5385\u5185\u706f\u706b\u672a\u706d\uff0c\u4ece\u7a97\u5916\u671b\u8fdb\u53bb\uff0c\u9690\u7ea6\u53ef\u89c1\u5230\u94c1\u955c\u5fc3\u90a3\u6e05\u79c0\u7684\u5f71\u5b50\uff0c\u4e8e\u627f\u73e0\u6b63\u5f85\u7834\u7a97\u800c \u5165\uff0c\u5ffd\u542c\u5f97\u91cc\u9762\u6709\u4eba\u54b3\u4e86\u4e00\u58f0\uff0c\u4e8e\u627f\u73e0\u6014\u4e86\u4e00\u6014\uff0c\u53ea\u89c1\u5c4b\u4e2d\u53c8\u591a\u4e86\u4e00\u4e2a\u4eba\u5f71\uff0c\u7a7f\u7684\u662f\u4e94\u54c1\u5b98 \u670d\uff0c\u60f3\u6765\u5f53\u662f\u90a3\u53f0\u5dde\u77e5\u5e9c\uff0c\u4e8e\u627f\u73e0\u4e00\u7eb5\u8eab\u8df3\u4e0a\u5c4b\u6a90\uff0c\u7528\u4e00\u4e2a\u201c\u73cd\u73e0\u5012\u5377\u5e18\u201d\u7684\u59ff\u52bf\uff0c\u8db3\u7a81\u52fe \u7740\u6a90\u89d2\uff0c\u63a2\u5934\u5185\u7aa5\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e14\u542c\u8fd9\u5b98\u513f\u548c\u4ed6\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u94c1\u955c\u5fc3\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u5e9c\u53f0\u5927\u4eba\u65e5\u591c\u8f9b\u52b3\uff0c\u4e3a\u665a\u751f\u7684\u4e8b\u60c5\u5927\u8d39\u7cbe\u795e\uff0c\u665a\u751f\u771f\u662f\u8fc7\u610f \u4e0d\u53bb\u5475\uff01\u201d\u90a3\u77e5\u5e9c\u9762\u4e0a\u4e00\u7ea2\uff0c\u5e72\u54b3\u4e24\u58f0\uff0c\u5c34\u5c2c\u8bf4\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff0c\u8fd9\u56de\u5b9e\u5728\u662f\u59d4\u5c48\u4e16\u5144 \u4e86\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u5bb6\u7236\u662f\u5426\u8fd8\u5728\u5e9c\u8859\uff0c\u53ef\u5426\u8ba9\u665a\u751f\u89c1\u4ed6\u4e00\u9762\uff1f\u201d\u77e5\u5e9c\u9053\uff1a\u201c\u5c0a\u5927\u4eba\u5df2\u91ca\u653e \u56de\u5e9c\u4e86\u3002\u4e16\u5144\u7684\u6848\u4ef6\u5c1a\u672a\u7ed3\u679c\uff0c\u6309\u671d\u5ef7\u5f8b\u4f8b\uff0c\u6682\u65f6\u8fd8\u662f\u4e0d\u89c1\u4e3a\u5b9c\u3002\u4ee5\u514d\u53cd\u7d2f\u4e86\u5c0a\u5927\u4eba\u3002\u201d\u94c1 \u955c\u5fc3\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u513f\u5b50\u7eb5\u7136\u6709\u7f6a\uff0c\u4e5f\u4e0d\u5e94\u96be\u4e3a\u4ed6\u7684\u7236\u4eb2\uff0c\u4f60\u4eec\u8fd9\u6b21\u6263\u62bc\u5bb6\u7236\uff0c\u4e0d\u77e5\u662f\u4f9d \u636e\u54ea\u4e00\u6761\u5f8b\u4f8b\uff1f\u201d \u3000\u3000\u90a3\u77e5\u5e9c\u6da8\u7ea2\u4e86\u8138\uff0c\u62e2\u8896\u4f5c\u63d6\u9053\uff1a\u201c\u4e16\u5144\u606f\u6012\uff0c\u8fd9\u6b21\u6211\u5b9e\u662f\u60c5\u975e\u5f97\u5df2\uff0c\u4e16\u5144\uff0c\u4f60\u8981\u7d27\u8c05\u6211 \u7684\u82e6\u8877\u554a\uff01\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u4f60\u662f\u671d\u5ef7\u7684\u5b98\u8fd8\u662f\u502d\u5bc7\u7684\u5b98\uff1f\u201d\u90a3\u77e5\u5e9c\u9053\uff1a\u201c\u6211\u5f53\u7136\u662f\u671d\u5ef7\u7684 \u5b98\u3002\u53ef\u662f\u94c1\u4e16\u5144\uff0c\u4f60\u4e5f\u4e0d\u662f\u4e0d\u77e5\u9053\uff0c\u53f0\u5dde\u57ce\u5916\uff0c\u4fbf\u662f\u502d\u5bc7\u7684\u4e16\u754c\uff0c\u8fd9\u57ce\u5185\u65e5\u672c\u5b98\u53c8\u50ac\u903c\u5f97 \u7d27\uff0c\u671d\u5ef7\u53c8\u6ca1\u53d1\u5175\u88ad\u502d\uff0c\u5e03\u8236\u53f8\u8fd8\u5728\u606d\u8fce\u65e5\u672c\u7684\u4f7f\u8005\uff0c\u4f60\uff0c\u4f60\uff0c\u4f60\u53eb\u6211\u600e\u751f\u53bb\u505a\uff1f\u54b3\uff0c\u6211\u7684 \u4e3a\u96be\u4e4b\u5904\uff0c\u6709\u8c01\u80fd\u591f\u660e\u767d\uff1f\u201d\u770b\u4ed6\u53ef\u601c\u7684\u6837\u5b50\uff0c\u4e8e\u627f\u73e0\u521d\u6765\u4e4b\u65f6\uff0c\u672c\u6765\u4e5f\u607c\u6068\u8fd9\u4e2a\u77e5\u5e9c\uff0c\u672c \u60f3\u628a\u4ed6\u4e00\u5200\u6740\u6389\uff0c\u4fbf\u62a2\u94c1\u955c\u5fc3\u51fa\u53bb\uff0c\u5982\u4eca\u542c\u4e86\u4ed6\u8fd9\u4e00\u756a\u8bc9\u82e6\u7684\u8bf4\u8bdd\uff0c\u867d\u7136\u4ecd\u662f\u89c9\u5f97\u4ed6\u53ef\u601c\u53ef \u9119\uff0c\u4f46\u4e00\u8154\u6012\u6c14\uff0c\u5df2\u5168\u8f6c\u79fb\u4e3a\u75db\u6068\u502d\u5bc7\u4e86\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u6124\u7136\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u90fd\u660e\u767d\u5566\uff0c\u90a3\u4f60\u51c6\u5907\u5c06\u6211\u600e\u4e2d\u5904\u7f6e\uff1f\u201d\u90a3\u77e5\u5e9c\u634b\u4e86\u4e00\u634b\u82b1\u767d \u7684\u80e1\u5b50\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u65e5\u672c\u5e02\u8236\u4f7f\u4e00\u5b9a\u8981\u5f97\u4e16\u5144\uff0c\u8bf7\u4e16\u5144\u5ff5\u5728\u53f0\u5dde\u7236\u8001\u7684\u4efd\u4e0a\uff0c\u59d4\u5c48 \u4e00\u4e9b\uff0c\u660e\u65e5\u6362\u4e2a\u5730\u65b9\u5427\u3002\u201d\u94c1\u955c\u5fc3\u51b7\u7b11\u9053\uff1a\u201c\u6211\u662f\u5927\u660e\u7684\u5b50\u6c11\uff0c\u6709\u7f6a\u4e5f\u53ea\u5e94\u7531\u4f60\u6765\u5ba1\uff0c\u4f60\u53e3 \u53e3\u58f0\u58f0\u8bf4\u671d\u5ef7\u7684\u738b\u6cd5\u5f8b\u4f8b\uff0c\u8bf7\u95ee\u671d\u5ef7\u7684\u6cd5\u5f8b\uff0c\u53ef\u4ee5\u7531\u5916\u56fd\u4eba\u6765\u5ba1\u95ee\u672c\u56fd\u7684\u4eba\u4e48\uff1f\u201d\u90a3\u6d32\u5e9c\u8fde \u5fd9\u4f5c\u63d6\u9053\uff1a\u201c\u4e16\u5144\uff0c\u8bdd\u662f\u8fd9\u4e48\u8bf4\u3002\u4f46\u4f60\u4e5f\u8981\u5ff5\u5230\u6211\u7684\u4e3a\u96be\u4e4b\u5904\uff0c\u82e5\u7136\u6211\u4e0d\u4f9d\u4ece\u4ed6\u4eec\u7684\u610f\u601d\uff0c \u4ed6\u4eec\u53eb\u57ce\u5916\u7684\u502d\u5bc7\u6253\u8fdb\u6765\uff0c\u90a3\u65f6\u5c82\u4e0d\u8fde\u7d2f\u4e86\u5168\u57ce\u767e\u59d3\uff1f\u4e16\u5144\uff0c\u4f60\u662f\u660e\u767d\u4eba\uff0c\u4f60\uff0c\u4f60\uff0c\u4f60\u8981\u4f53 \u8c05\u4e0b\u5b98\u7684\u82e6\u8877\u554a\uff01\u201d \u3000\u3000\u94c1\u955c\u5fc3\u65e0\u9650\u6fc0\u6124\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u600e\u4e48\u4e0d\u660e\u767d\uff0c\u65e0\u975e\u662f\u4f60\u81ea\u5df1\u8981\u4fdd\u5168\u5934\u4e0a\u7684\u4e4c\u7eb1\uff0c\u6240\u4ee5 \u6015\u502d\u5bc7\u6015\u6210\u8fd9\u4e2a\u6837\u5b50\uff01\u201d\u4f46\u89c1\u5730\u90a3\u526f\u53ef\u601c\u7684\u6837\u5b50\uff0c\u5374\u4e5f\u4e0d\u5fcd\u518d\u5c06\u4ed6\u8d23\u96be\u3002\u90a3\u77e5\u5e9c\u7528\u54c0\u6c42\u7684\u773c \u5149\u770b\u7740\u4ed6\uff0c\u94c1\u955c\u5fc3\u5ffd\u5730\u6602\u5934\u8bf4\u9053\uff1a\u201c\u6211\u6027\u547d\u4e0d\u8db3\u60dc\uff0c\u4f46\u7531\u4f60\u4ea4\u7ed9\u502d\u5974\uff0c\u8fd9\u671d\u5ef7\u7684\u5c0a\u4e25\uff0c\u4f60\u5c06 \u7f6e\u4e8e\u4f55\u5730\uff1f\u4f60\u4e5f\u786e\u5b9e\u4e3a\u96be\uff0c\u597d\u5427\uff0c\u90a3\u6211\u5c31\u66ff\u4f60\u60f3\u4e2a\u4e24\u5168\u4e4b\u9053\u3002\u201d\u90a3\u77e5\u5e9c\u5fd9\u9053\uff1a\u201c\u613f\u95fb\u5176 \u8be6\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u7531\u4f60\u4e3b\u5ba1\uff0c\u8ba9\u65e5\u672c\u7684\u5e02\u8236\u4f7f\u6765\u966a\u4f60\u542c\u5ba1\uff0c\u4ed6\u4eec\u65e2\u7136\u63a7\u544a\u6211\uff0c\u90a3\u4e48\u4e5f\u5f97\u4f20 \u4ed6\u4eec\u7684\u2018\u539f\u544a\u2019\u51fa\u5ead\uff0c\u5ba1\u5224\u4e4b\u65f6\uff0c\u5e94\u51c6\u53f0\u5dde\u767e\u59d3\u542c\u5ba1\uff01\u201d\u77e5\u5e9c\u9053\uff1a\u201c\u8fd9\uff0c\u8fd9\u2014\u2014\u201d\u94c1\u955c\u5fc3 \u9053\uff1a\u201c\u8fd9\u4ec0\u4e48\uff1f\u8fd9\u987e\u5168\u4e86\u671d\u5ef7\u7684\u2018\u738b\u6cd5\u2019\uff0c\u4e5f\u987e\u5168\u4e86\u65e5\u672c\u4f7f\u8005\u7684\u9762\u5b50\uff0c\u8ba9\u4f60\u5728\u65e5\u672c\u4eba\u9762\u524d\u4ea4 \u4ee3\u5f97\u8fc7\u53bb\uff0c\u8fd9\u8fd8\u4e0d\u597d\u4e48\uff1f\u4f60\u82e5\u4e0d\u4ece\uff0c\u6211\u5c31\u4e00\u8dd1\u4e86\u4e8b\uff0c\u5343\u767e\u502d\u5bc7\u5c1a\u81ea\u62e6\u6211\u4e0d\u4f4f\uff0c\u4f60\u62e6\u5f97\u4f4f\u6211 \u4e48\uff1f\u201d\u8d8a\u8bf4\u8d8a\u6c14\u6124\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u638c\u51fb\u4e0b\uff0c\u5c06\u4e00\u5f20\u6a80\u6728\u8336\u51e0\uff0c\u524a\u4e86\u4e00\u89d2\u3002 \u3000\u3000\u90a3\u77e5\u5e9c\u6df1\u77e5\u94c1\u955c\u5fc3\u672c\u9886\u975e\u51e1\uff0c\u53c8\u66fe\u542c\u5230\u4ed6\u8fde\u6740\u51e0\u4e2a\u65e5\u672c\u6b66\u58eb\u7684\u6545\u4e8b\uff0c\u89c1\u4ed6\u53d1\u6012\uff0c\u5fc3\u4e2d\u5bb3 \u6015\uff0c\u5fd9\u4f5c\u63d6\u9053\uff1a\u201c\u65e2\u7136\u4e16\u5144\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u90a3\u4e48\u6211\u660e\u65e5\u548c\u65e5\u672c\u7684\u4f7f\u8005\u8bf4\u53bb\uff0c\u8fd8\u671b\u4e16\u5144\u5343\u4e07\u4ee5\u53f0 \u5dde\u7684\u7236\u8001\u4e3a\u5ff5\u554a\uff01\u201d\u4f5c\u51fa\u4e00\u526f\u53ef\u601c\u76f8\u8e51\u624b\u8e51\u811a\u5730\u56de\u5185\u5ba4\u53bb\u3002 \u3000\u3000\u77e5\u5e9c\u4e00\u8d70\uff0c\u4e8e\u627f\u73e0\u98d8\u8eab\u8dc3\u4e0b\uff0c\u7834\u7a97\u800c\u5165\u3002\u94c1\u955c\u5fc3\u7b11\u9053\uff1a\u201c\u4f60\u6765\u4e86\u8bb8\u4e45\u4e86\uff0c\u90fd\u542c\u89c1\u4e86 \u5417\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u53ea\u9053\u662f\u4eba\u4e0d\u77e5\u9b3c\u4e0d\u89c9\uff0c\u5374\u539f\u6765\u65e9\u5df2\u88ab\u4ed6\u770b\u7834\u4e86\u3002\u201d\u5bf9 \u94c1\u955c\u5fc3\u7684\u672c\u9886\u597d\u751f\u4f69\u670d\uff0c\u53ea\u542c\u5f97\u94c1\u955c\u5fc3\u53c8\u9053\uff1a\u201c\u4f60\u65e2\u7136\u90fd\u542c\u89c1\u4e86\uff0c\u8fd8\u8fdb\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u4e8e\u627f\u73e0 \u8bf4\u9053\uff1a\u201c\u7279\u6765\u63a2\u671b\u4f60\u554a\u3002\u201d\u94c1\u955c\u5fc3\u7b11\u9053\uff1a\u201c\u90a3\u65e5\u5728\u957f\u6c5f\u4e4b\u4e0a\uff0c\u591a\u627f\u642d\u6e21\uff1b\u5982\u4eca\u5f1f\u5728\u7f27\u7ec1\u4e4b \u4e2d\uff0c\u53c8\u627f\u4e8e\u5144\u63a2\u671b\uff0c\u9ad8\u8c0a\u9686\u60c5\uff0c\u5c0f\u5f1f\u5728\u8fd9\u53a2\u8c22\u8fc7\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u6b63\u81ea\u6c14\u607c\u4ed6\u8bf4\u8bdd\u6ca1\u6709\u793c\u8c8c\uff0c\u5ffd \u89c1\u4ed6\u53c8\u9178\u6e9c\u6e9c\u5730\u4f5c\u63d6\u9053\u8c22\uff0c\u5fcd\u4e0d\u4f4f\u5657\u55e4\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u4e0d\u8be5\u8fdb\u6765\uff0c\u6211\u8bf4\u4f60\u4e5f\u4e0d\u8be5\u7559\u5728 \u8fd9\u91cc\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u7684\u7236\u4eb2\u65e2\u5df2\u91ca\u653e\u51fa\u53bb\u4e86\uff0c\u4f60\u4e3a\u4f55\u8fd8\u8981\u7559\u5728\u8fd9 \u513f\u53d7\u6c14\uff1f\u4f60\u5f53\u771f\u80fd\u591f\u5fcd\u53d7\u502d\u5974\u7684\u4f7f\u8005\u9ad8\u8e1e\u5802\u4e0a\uff0c\u770b\u4f60\u53d7\u5ba1\u4e48\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u77e5\u5e9c\u5927\u4eba\u8bf4\u7684 \u8bdd\u4f60\u8fd8\u6ca1\u6709\u542c\u660e\u767d\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ed6\u5bb3\u6015\u502d\u5bc7\uff0c\u7b80\u76f4\u5bb3\u6015\u5f97\u9b42\u9b44\u4e0d\u9f50\uff0c\u96be\u9053\u4f60\u6211\u4e16\u5bb3\u6015 \u502d\u5bc7\uff1f\u81ea\u4e3b\u9053\u5175\u6765\u5c06\u6321\uff0c\u6c34\u6765\u58eb\u63a9\uff0c\u502d\u5bc7\u82e5\u771f\u7684\u6562\u6765\u653b\u57ce\uff0c\u54b1\u4eec\u5c31\u4e0d\u80fd\u8bbe\u6cd5\u5c06\u5b83\u6253\u9000\u4e48\uff1f\u201d \u94c1\u955c\u5fc3\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u6211\u4e8c\u4eba\u5f53\u7136\u4e0d\u60e7\u502d\u5bc7\uff0c\u4f46\u53ea\u4f60\u6211\u4e8c\u4eba\u5c31\u80fd\u6253\u9000\u502d\u5bc7\u4e48\uff1f\u8bf7\u95ee\u82e5\u502d\u5bc7\u5927 \u4e3e\u653b\u57ce\uff0c\u543e\u5144\u6709\u4f55\u7834\u654c\u826f\u7b56\uff1f\u201d\u4e8e\u627f\u73e0\u53ea\u662f\u51ed\u7740\u4e00\u80a1\u5c11\u5e74\u7684\u51b2\u52a8\uff0c\u95ee\u5230\u5979\u7834\u654c\u4e4b\u7b56\uff0c\u5374\u662f\u6ca1 \u6709\u60f3\u8fc7\uff0c\u53cd\u95ee\u9053\uff1a\u201c\u96be\u9053\u4f60\u7518\u613f\u53d7\u5ba1\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7834\u654c\u4e4b\u7b56\u4e48\uff1f\u201d\u94c1\u955c\u5fc3\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u5f2f\u5f13 \u6b32\u5c04\u5357\u5c71\u864e\uff0c\u78e8\u5251\u601d\u9664\u5317\u6d77\u86df\u3002\u5c04\u864e\u9664\u86df\u8fd8\u5f85\u5f2f\u5f13\u78e8\u5251\uff0c\u4f55\u51b5\u662f\u8981\u9a71\u9010\u6bd4\u731b\u864e\u957f\u86df\u66f4\u51f6\u6b8b\u7684 \u502d\u5bc7\u3002\u201d\u4e8e\u627f\u73e0\u542c\u4ed6\u8bf4\u5f97\u597d\u4f3c\u80f8\u4e2d\u65e9\u6709\u6210\u7af9\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u4ed6\u7684\u7518\u5fc3\u53d7\u5ba1\uff0c\u4e5f\u7b49\u4e8e\u5f2f\u5f13\u78e8\u5251 \u4e00\u6837\uff0c\u662f\u5728\u505a\u51c6\u5907\u7684\u529f\u592b\u4e48\uff1f\u8fd9\u5012\u4ee4\u4eba\u83ab\u6d4b\u9ad8\u6df1\u4e86\uff01\u201d\u4f46\u89c1\u94c1\u955c\u5fc3\u773c\u5149\u4e2d\u5145\u6ee1\u81ea\u4fe1\uff0c\u53c8\u5fae\u7b11 \u9053\uff1a\u201c\u591a\u8c22\u4f60\u6765\u63a2\u671b\u6211\uff0c\u73b0\u5728\u4f60\u53ef\u4ee5\u8d70\u5566\uff0c\u5230\u6211\u53d7\u5ba1\u90a3\u5929\uff0c\u4f60\u518d\u6765\u770b\u6211\u5427\u3002\u201d\u4e8e\u627f\u73e0\u610f\u6709\u4e0d \u5feb\uff0c\u9053\uff1a\u201c\u94c1\u5144\u6709\u4f55\u5631\u6258\uff0c\u5c0f\u5f1f\u613f\u5c3d\u7ef5\u529b\u3002\u201d\u94c1\u955c\u5fc3\u6709\u70b9\u5947\u602a\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u5012\u662f\u6027\u60c5\u4e2d \u4eba\uff0c\u840d\u6c34\u76f8\u9022\uff0c\u4fbf\u628a\u6211\u5f53\u77e5\u5df1\u770b\u5f85\u3002\u201d\u773c\u5149\u7768\u53bb\uff0c\u548c\u4e8e\u627f\u73e0\u78b0\u4e2a\u6b63\u7740\uff0c\u5ffd\u89c1\u4e8e\u627f\u73e0\u8f6c\u5934\u907f \u5f00\uff0c\u8138\u4e0a\u4f3c\u6cdb\u8d77\u4e00\u7247\u7ea2\u971e\uff0c\u94c1\u955c\u5fc3\u6697\u7b11\u9053\uff1a\u201c\u771f\u662f\u5c0f\u5b69\u5b50\uff0c\u521a\u624d\u8fd8\u8bf4\u5f97\u90a3\u4e48\u6177\u6168\u6fc0\u6602\uff0c\u4f3c\u4e2a \u5927\u4eba\uff0c\u73b0\u5728\u5374\u53c8\u5bb3\u7f9e\u4e86\u3002\u201d\u94c1\u955c\u5fc3\u53ef\u6ca1\u6709\u60f3\u5230\u4e8e\u627f\u73e0\u7adf\u662f\u4e2a\u5973\u5b50\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u7565\u4e00\u6c89\u541f\uff0c\u62ac\u5934\u7b11\u9053\uff1a\u201c\u591a\u8c22\u543e\u5144\u5fc3\u610f\uff0c\u90a3\u4e48\u5c31\u8bf7\u543e\u5144\u7ed9\u5c0f\u5f1f\u5e26\u4e00\u4e2a\u53e3\u4fe1\u5427\u3002\u201d \u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5e26\u7ed9\u8c01\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u5728\u79bb\u57ce\u4e1c\u90ca\u4e03\u516b\u91cc\u7684\u5730\u65b9\uff0c\u6709\u4e00\u4e2a\u5c0f\u6751\u53eb\u505a\u767d\u6c99\u6751\uff0c \u6751\u5b50\u897f\u8fb9\uff0c\u9760\u5c71\u7684\u6240\u5728\uff0c\u6709\u4e00\u5bb6\u4eba\u5bb6\uff0c\u8fd9\u5bb6\u95e8\u524d\u6709\u4e09\u68f5\u767d\u6768\u6811\uff0c\u95e8\u9996\u6709\u4e00\u5bf9\u77f3\u72ee\u5b50\uff0c\u6700\u6613\u8fa8 \u8ba4\u3002\u4f60\u89c1\u7740\u8fd9\u5bb6\u4e3b\u4eba\uff0c\u5c31\u628a\u4f60\u4eca\u665a\u542c\u5230\u770b\u5230\u7684\u4e8b\u544a\u8bc9\u4ed6\u5427\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u5bb6\u4e3b\u4eba\u662f\u4ec0\u4e48 \u4eba\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u4f60\u89c1\u7740\u4e86\u81ea\u7136\u5c31\u77e5\u9053\u5566\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5fcd\u4e0d\u7740\u5fae\u5fae\u4e00\u7b11\uff0c\u7b11\u5f97\u9887\u4e3a\u795e \u79d8\u3002\u4e8e\u627f\u73e0\u56de\u5230\u5c45\u5904\uff0c\u5140\u662f\u60f3\u4e0d\u660e\u4ed6\u8fd9\u4e00\u7b11\u662f\u4ec0\u4e48\u610f\u601d\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\uff0c\u6d3e\u53bb\u548c\u4e49\u519b\u8054\u7edc\u7684\u4eba\uff0c\u8fd8\u6ca1\u6709\u97f3\u8baf\u56de\u62a5\uff0c\u4e8e\u627f\u73e0\u4fbf\u72ec\u81ea\u4e00\u4eba\u5230\u767d\u6c99\u6751\u53bb\u3002 \u3000\u3000\u65f6\u5e8f\u6b63\u662f\u6df1\u79cb\uff0c\u90ca\u5916\u7530\u752b\u91d1\u9ec4\uff0c\u8749\u9e23\u7a3b\u719f\uff0c\u4e00\u6d3e\u5929\u7136\u666f\u8272\uff0c\u4ee4\u4eba\u5fc3\u9189\uff0c\u53ea\u662f\u8def\u4e0a\u5374\u51b7\u51b7 \u6e05\u6e05\u7684\uff0c\u751a\u5c11\u884c\u4eba\uff0c\u4e8e\u627f\u73e0\u5fc3\u4e2d\u53f9\u9053\uff1a\u201c\u82e5\u65e0\u502d\u5bc7\u4fb5\u6270\uff0c\u8fd9\u91cc\u5012\u771f\u662f\u65e0\u6b8a\u4e16\u5916\u6843\u6e90\u3002\u201d\u767d\u6c99 \u6751\u79bb\u57ce\u4e0d\u5230\u5341\u91cc\uff0c\u4e8e\u627f\u73e0\u95ee\u660e\u9053\u8def\uff0c\u4e0d\u4e00\u523b\u4fbf\u8d70\u5230\u4e86\u3002 \u3000\u3000\u90a3\u662f\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5c71\u6751\uff0c\u6751\u4e2d\u53ea\u6709\u5341\u6570\u5bb6\u4eba\u5bb6\uff0c\u4e1c\u4e00\u5bb6\uff0c\u897f\u4e00\u5bb6\uff0c\u758f\u758f\u843d\u843d\u3002\u4e8e\u627f\u73e0\u8d70\u4e86 \u4e00\u6bb5\u76d8\u65cb\u66f2\u6298\u7684\u5c71\u8def\uff0c\u5728\u4e24\u5c71\u5408\u62b1\u7684\u5c71\u5773\u5904\uff0c\u53ea\u89c1\u4e00\u5bb6\u4eba\u5bb6\u501a\u5c71\u5efa\u7b51\uff0c\u5b64\u96f6\u96f6\u7684\u65e0\u90bb\u65e0\u820d\uff0c \u5c71\u62ab\u7740\u79cd\u6ee1\u6842\u82b1\uff0c\u5c71\u98ce\u5439\u6765\uff0c\u9999\u6c14\u88ad\u4eba\uff0c\u6709\u8bf4\u4e0d\u51fa\u7684\u8212\u670d\uff0c\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u8fd9\u5bb6\u4e3b\u4eba\u5b9a\u7136\u662f \u4e2a\u98ce\u96c5\u4e4b\u58eb\u4e86\u3002\u201d\u7a7f\u8fc7\u90a3\u4e00\u7247\u6842\u82b1\u6797\u5b50\uff0c\u679c\u7136\u89c1\u7740\u4e00\u5bf9\u77f3\u72ee\u5b50\u5728\u77f3\u9636\u4e0a\u9762\uff0c\u95e8\u524d\u4e09\u68f5\u5782\u6768\uff0c \u906e\u7740\u4e86\u7ea2\u697c\u4e00\u89d2\uff0c\u4e8e\u627f\u73e0\u7aef\u8be6\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u5fc5\u5b9a\u662f\u94c1\u955c\u5fc3\u6240\u8bf4\u7684\u90a3\u5bb6\u4eba\u5bb6 \u4e86\uff0c\u4e3a\u4ec0\u4e48\u4ed6\u4e0d\u80af\u544a\u8bc9\u6211\u5c4b\u4e2d\u7684\u4e3b\u4eba\u662f\u4ec0\u4e48\u4eba\u5462\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u6b63\u5f85\u6263\u95e8\uff0c\u5ffd\u89c9\u80cc\u540e\u5fae\u98ce\u98d2\u7136\uff0c\u6709\u4e00\u4e2a\u5a07\u6ef4\u6ef4\u7684\u58f0\u97f3\u65a5\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u9b3c\u9b3c\u795f\u795f\u5730 \u6765\u6b64\u7aa5\u63a2\uff1f\u201d\u4e8e\u627f\u73e0\u8eab\u5f62\u4e00\u95ea\uff0c\u56de\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u4e00\u4e2a\u4fcf\u4e3d\u7684\u5c0f\u59d1\u5a18\uff0c\u7a7f\u7740\u77ed\u8896\u7684\u674f\u9ec4\u886b\u5b50\uff0c \u5934\u53d1\u68b3\u6210\u4e24\u4e2a\u53c9\u89d2\uff0c\u770b\u6765\u7a1a\u6c14\u672a\u9664\uff0c\u5e74\u7eaa\u548c\u81ea\u5df1\u4e5f\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u53ef\u662f\u5374\u677f\u8d77\u9762\u5b54\uff0c\u88c5\u51fa\u4e00\u526f\u5927 \u4eba\u7684\u8154\u8c03\uff0c\u4e8e\u627f\u73e0\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\u5c4b\u4e2d\u7684\u4e3b\u4eba\u7adf\u662f\u8fd9\u6837\u7684\u4e00\u4f4d\u5c0f\u59d1\u5a18\uff0c\u53ea\u89c1\u90a3\u5c0f\u59d1\u5a18\u58f0\u5230\u4eba \u5230\uff0c\u77f3\u81c2\u4e00\u5708\uff0c\u5de6\u638c\u7a7f\u51fa\uff0c\u7528\u7684\u7adf\u662f\u4e03\u7edd\u624b\u5c0f\u64d2\u62ff\u624b\u6cd5\uff0c\u628a\u81ea\u5df1\u5f53\u6210\u4e00\u4e2a\u5c0f\u5077\u3002 \u3000\u3000\u672c\u6765\u4e8e\u627f\u73e0\u53ea\u8981\u4e00\u8bf4\u51fa\u94c1\u955c\u5fc3\u7684\u540d\u5b57\u4fbf\u53ef\u4ee5\u65e0\u4e8b\uff0c\u4f46\u5979\u4e00\u60f3\u5230\u94c1\u955c\u5fc3\u5728\u7f27\u7ec1\u4e4b\u4e2d\uff0c\u8c01\u90fd \u4e0d\u8bb0\u6302\uff0c\u53ea\u6258\u81ea\u5df1\u5e26\u4fe1\u7ed9\u8fd9\u4e2a\u5c0f\u59d1\u5a18\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u7a81\u7136\u7ae5\u5fc3\u5927\u8d77\uff0c\u8981\u8bd5\u8bd5\u8fd9\u5c0f\u59d1\u5a18\u7684\u672c\u4e8b\uff0c \u5f53\u4e0b\u53cc\u638c\u4e00\u8d77\uff0c\u4e00\u62db\u201c\u70d8\u4e91\u6258\u6708\u201d\uff0c\u5316\u89e3\u4e86\u90a3\u5c0f\u59d1\u5a18\u7684\u64d2\u62ff\u624b\u6cd5\u3002\u8fd9\u62db\u201c\u70d8\u4e91\u6258\u6708\u201d\uff0c\u662f\u5de6 \u638c\u6258\u5f00\u654c\u4eba\u7684\u8098\u5c16\uff0c\u53f3\u638c\u8ddf\u7740\u53cd\u6293\uff0c\u5de6\u638c\u662f\u865a\uff0c\u53f3\u638c\u662f\u5b9e\u3002\u90a3\u5c0f\u59d1\u5a18\u51b7\u4e0d\u9632\u88ab\u5979\u6258\u8d77\u624b\u8098\uff0c \u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u53cc\u80a9\u4e00\u6c89\uff0c\u8fc5\u5373\u8fd8\u4e86\u4e00\u62db\u201c\u4e03\u661f\u624b\u201d\uff0c\u53cd\u51fb\u4e8e\u627f\u73e0\u524d\u80f8\uff0c\u4e8e\u627f\u73e0\u53f3\u638c\u90a3\u4e00\u6293 \u7adf\u7136\u843d\u7a7a\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u6697\u6697\u4f69\u773c\u90a3\u5c0f\u59d1\u5a18\u53d8\u62db\u7684\u8fc5\u901f\uff0c\u5f53\u4e0b\u7acb\u5373\u53cc\u638c\u4e00\u5206\uff0c\u5de6\u81c2\u5982\u5f13\uff0c\u53f3\u624b \u4e94\u6307\u5982\u7bad\uff0c\u4ece\u201c\u70d8\u4e91\u6258\u6708\u201d\u4e00\u53d8\u800c\u4e3a\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\uff0c\u4e8e\u627f\u73e0\u5bf9\u4e8e\u638c\u6cd5\u867d\u975e\u6240\u957f\uff0c\u4f46\u5979\u5e08\u627f\u7684 \u201c\u767e\u53d8\u7384\u673a\u5251\u6cd5\u201d\uff0c\u6700\u8bb2\u7a76\u8eab\u624b\u7684\u5feb\u6377\uff0c\u8fd9\u4e00\u4e0b\u51fa\u624b\u5982\u98ce\uff0c\u5de6\u81c2\u62e6\u7740\u4e86\u90a3\u5c0f\u59d1\u5a18\u7684\u53cc\u638c\uff0c\u53f3 \u624b\u4e2d\u98df\u4e8c\u6307\u500f\u7684\u70b9\u5230\u4e86\u90a3\u5c0f\u59d1\u5a18\u80f8\u524d\u7684\u201c\u4e73\u7a81\u7a74\u201d\uff0c\u90a3\u5c0f\u59d1\u5a18\u674f\u9762\u98de\u7ea2\uff0c\u7a81\u7136\u4f38\u53e3\u4e00\u54ac\u3002\u4e8e \u627f\u73e0\u731b\u5730\u9192\u8d77\uff0c\u81ea\u5df1\u73b0\u5728\u662f\u7537\u5b50\u6253\u626e\uff0c\u8fd9\u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\uff0c\u5927\u662f\u65e0\u793c\u3002 \u3000\u3000\u90a3\u5c0f\u5973\u731d\u7136\u5f20\u53e3\u4e00\u54ac\uff0c\u8fd9\u4e00\u4e0b\u201d\u602a\u62db\u201d\u5927\u51fa\u4e8e\u627f\u73e0\u610f\u6599\u4e4b\u5916\uff0c\u5e78\u800c\u4e8e\u627f\u73e0\u7f29\u624b\u5f97\u5feb\uff0c\u8981 \u4e0d\u7136\u4e24\u6839\u6307\u5934\u51e0\u4e4e\u7ed9\u5979\u54ac\u65ad\u3002\u4e8e\u627f\u73e0\u5fc3\u4e2d\u597d\u7b11\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u90a3\u5c11\u5973\u638c\u6cd5\u4e00\u53d8\uff0c\u5de6\u638c\u4e00\u62cd\uff0c\u53f3 \u638c\u75be\u4e0a\uff0c\u4e00\u638c\u63a5\u7740\u4e00\u638c\uff0c\u7adf\u4f3c\u72c2\u6d9b\u9a87\u6d6a\u822c\u5730\u7ffb\u7ffb\u6eda\u6eda\u800c\u6765\uff0c\u7edd\u65e0\u534a\u70b9\u7a7a\u9699\uff0c\u4e8e\u627f\u73e0\u5403\u4e86\u4e00 \u60ca\uff0c\u4ed7\u7740\u8eab\u6cd5\u8f7b\u7075\uff0c\u817e\u632a\u95ea\u5c55\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u8eb2\u8fc7\u4e86\u5979\u7684\u4e03\u4e03\u56db\u5341\u4e5d\u638c\uff0c\u51e0\u4e4e\u7ed9\u5979\u903c\u5f97\u900f\u4e0d\u8fc7 \u6c14\u6765\uff0c\u5fc3\u4e2d\u6697\u6697\u60ca\u5947\uff1a\u8fd9\u5c11\u5973\u7684\u529f\u529b\u663e\u7136\u8f83\u81ea\u5df1\u4e3a\u6d45\uff0c\u4f46\u638c\u6cd5\u7684\u51cc\u5389\u8fc5\u901f\u5374\u8fdc\u5728\u81ea\u5df1\u4e4b\u4e0a\uff0c \u800c\u4e14\u5979\u6bcf\u6b21\u51fa\u638c\u90fd\u662f\u53cc\u638c\u76f8\u8fde\uff0c\u5f62\u6210\u4e00\u4e2a\u4e2a\u7684\u5706\u5708\uff0c\u4e0d\u4f4f\u5730\u5411\u524d\u63a8\u903c\uff0c\u5c31\u5982\u4e00\u4e2a\u6ce2\u6d6a\u63a5\u7740\u4e00 \u4e2a\u6ce2\u6d6a\uff0c\u524d\u6d6a\u672a\u901d\uff0c\u540e\u6d6a\u53c8\u6765\uff0c\u5f53\u771f\u662f\u89c1\u6240\u672a\u89c1\u3002\u4e8e\u627f\u73e0\u7684\u5e08\u7236\u5f20\u4e39\u67ab\u535a\u8bc6\u5404\u5bb6\u6b66\u5b66\uff0c\u5e73\u65e5 \u4e5f\u5e38\u4e0e\u4e8e\u627f\u73e0\u8c08\u8bba\uff0c\u4f46\u5374\u4ece\u6765\u6ca1\u6709\u8bf4\u8fc7\u8fd9\u79cd\u638c\u6cd5\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u7684\u638c\u6cd5\u4ee5\u4e03\u4e03\u56db\u5341\u4e5d\u638c\u6210\u4e00\u6bb5\u843d\uff0c\u5faa\u73af\u53cd\u590d\u8fde\u7528\uff0c\u56db\u5341\u4e5d\u638c\u4e00\u8fc7\uff0c\u7a0d\u5fae\u4e00\u904f\u3002\u4e8e \u627f\u73e0\u7acb\u523b\u7528\u201c\u5c0f\u5929\u661f\u201d\u638c\u529b\uff0c\u5c06\u5185\u5bb6\u771f\u529b\u51dd\u4e8e\u638c\u5fc3\uff0c\u8f7b\u8f7b\u4e00\u5f15\uff0c\u628a\u90a3\u5c11\u5973\u7684\u53cc\u638c\u5c01\u51fa\u5916\u95e8\uff0c \u7b11\u9053\uff1a\u201c\u597d\u638c\u6cd5\uff0c\u54b1\u4eec\u4e0d\u5fc5\u518d\u6253\u5566\u3002\u6211\u662f\u7ed9\u4f60\u5e26\u4fe1\u6765\u7684\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u7528\u529b\u4e00\u6323\uff0c\u6ca1\u6709\u6323\u8131\uff0c\u4f46\u89c9\u5bf9\u65b9\u7684\u638c\u5fc3\u4f3c\u6709\u4e00\u80a1\u7c98\u529b\uff0c\u5c06\u81ea\u5df1\u624b\u638c\u5438\u4f4f\uff0c\u7262\u4e0d\u53ef \u8131\u3002\u8981\u77e5\u5f20\u4e39\u67ab\u81ea\u5f97\u4e86\u5f6d\u548c\u5c1a\u7684\u9057\u4e66\u2014\u2014\u201c\u7384\u529f\u8981\u8bc0\u201d\u4e4b\u540e\uff0c\u7ecf\u8fc7\u4e86\u5341\u5e74\u6765\u7684\u9759\u5fc3\u53c2\u609f\uff0c\u5df2 \u7ec3\u6210\u4e86\u6700\u4e0a\u4e58\u7684\u7384\u5b97\u5185\u529f\uff0c\u4e8e\u627f\u73e0\u867d\u7136\u5e74\u5e7c\uff0c\u529f\u529b\u672a\u5230\uff0c\u4f46\u6240\u5f97\u7684\u662f\u5f20\u4e39\u67ab\u7684\u771f\u4f20\uff0c\u5df2\u662f\u975e \u540c\u5c0f\u53ef\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9887\u611f\u8be7\u5f02\uff0c\u95ee\u9053\uff1a\u201c\u5e26\u4ec0\u4e48\u4fe1\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u94c1\u955c\u5fc3\u7684\u53e3\u4fe1\u3002\u201d\u90a3\u5c0f\u5973\u9053\uff1a \u201c\u94c1\u955c\u5fc3\u6258\u4f60\u5e26\u4fe1\u7ed9\u6211\uff1f\u4f60\u5728\u4ec0\u4e48\u5730\u65b9\u89c1\u7740\u4ed6\u4e86\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5728\u77e5\u5e9c\u7684\u8859\u95e8\uff0c\u4ed6\u660e\u5929\u5c31 \u8981\u88ab\u77e5\u5e9c\u4ea4\u7ed9\u65e5\u672c\u4eba\u5462\uff01\u201d\u90a3\u5c11\u5973\u79c0\u7709\u5fae\u8e59\uff0c\u5fe7\u5f62\u4e8e\u8272\uff0c\u4e8e\u627f\u73e0\u89c1\u4e86\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5fc3\u4e2d\u5fae\u611f \u9178\u610f\u3002\u90a3\u5c11\u5973\u5ffd\u9053\uff1a\u201c\u5f53\u771f\u662f\u94c1\u955c\u5fc3\u6258\u4f60\u5e26\u4fe1\uff1f\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u59d3\u4e8e\u540d\u53eb \u627f\u73e0\u3002\u4f60\u5462\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4e8e\u627f\u73e0\uff1f\u6ca1\u542c\u4ed6\u8bf4\u8fc7\u8fd9\u4e2a\u540d\u5b57\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u4eec\u662f\u65b0\u8ba4\u8bc6 \u7684\u597d\u670b\u53cb\u3002\u201d\u90a3\u5c11\u5973\u5ffd\u5730\u4e00\u58f0\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u94c1\u955c\u5fc3\u600e\u4f1a\u6709\u4f60\u8fd9\u6837\u7684\u670b\u53cb\uff1f\u8f7b\u8584\u72c2\u5f92\uff0c\u5192\u540d\u9a97 \u5b50\uff0c\u5403\u6211\u4e00\u5251\uff01\u201d\u4e8e\u627f\u73e0\u548c\u5979\u4e00\u8fb9\u8bf4\u8bdd\uff0c\u4e0d\u514d\u5206\u5fc3\uff0c\u90a3\u5c11\u5973\u9aa4\u51fa\u4e0d\u610f\u5730\u53cc\u638c\u4e00\u6c89\uff0c\u6446\u8131\u4e86\u4e8e \u627f\u73e0\u7684\u638c\u529b\uff0c\u500f\u7136\u4e4b\u95f4\u5c31\u62d4\u51fa\u5251\u6765\uff0c\u5f53\u771f\u662f\u5feb\u5982\u95ea\u7535\uff01\u8bf4\u5230\u90a3\u4e2a\u201c\u5251\u201d\u5b57\uff0c\u5251\u5c16\u6643\u52a8\uff0c\u8eab\u5f62 \u672a\u6362\uff0c\u5df2\u63a5\u8fde\u5730\u523a\u4e86\u4e09\u5251\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u4e2d\u751f\u6c14\uff0c\u60f3\u9053\uff1a\u201c\u4f60\u5251\u6cd5\u867d\u7136\u5389\u5bb3\uff0c\u96be\u9053\u6211\u4f1a\u6015\u4f60\u4e0d\u6210\uff1f\u201d\u6b63\u60f3\u62d4\u5251\u62b5\u654c\uff0c\u5ffd \u542c\u5f97\u5c71\u80cc\u540e\u4e00\u9635\u8ffd\u9010\u558a\u53eb\u4e4b\u58f0\uff0c\u90a3\u5c11\u5973\u7a81\u7136\u6536\u5251\uff0c\u53eb\u9053\uff1a\u201c\u662f\u6210\u4e8c\u54e5\u5417\uff1f\u201d\u4e8e\u627f\u73e0\u4e0e\u5979\u4e0d\u7ea6 \u800c\u540c\u5730\u56de\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u5c71\u5773\u5df2\u8f6c\u51fa\u4e24\u4e2a\u4eba\u6765\uff0c\u4e00\u4e2a\u519b\u5b98\u633a\u7740\u957f\u5251\u6b63\u5728\u8ffd\u9010\u4e00\u4e2a\u5c11\u5e74\u6c49\u5b50\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u6c49\u5b50\u751f\u5f97\u6d53\u7709\u5927\u773c\uff0c\u7a7f\u7740\u4e00\u4ef6\u6253\u5f00\u94ae\u6263\u7684\u5f00\u80f8\u77ed\u8863\uff0c\u4e00\u5f20\u9762\u5b54\u6652\u5f97\u9ed1\u91cc\u6cdb\u7ea2\uff0c\u5b8c \u5168\u662f\u6ee8\u6d77\u6e14\u6c11\u7684\u6253\u626e\uff0c\u6837\u5b50\u6734\u5b9e\u65e0\u534e\uff0c\u529f\u592b\u5374\u9887\u6709\u6839\u5e95\uff0c\u53ea\u89c1\u4ed6\u624b\u4f7f\u4e00\u6839\u7f20\u5934\u91d1\u4e1d\u6746\u68d2\uff0c\u88ab \u90a3\u519b\u5b98\u8ffd\u5f97\u6025\u4e86\uff0c\u65f6\u4e0d\u65f6\u5730\u7a81\u7136\u56de\u5934\u5c31\u662f\u4e00\u68d2\uff0c\u90a3\u519b\u5b98\u4f7f\u7684\u662f\u4e00\u70b3\u6708\u7259\u5f2f\u5200\uff0c\u62db\u6570\u7cbe\u5947\u4e4b \u6781\uff0c\u5c11\u5e74\u6c49\u5b50\u7684\u7a81\u88ad\u6bcf\u6bcf\u88ab\u4ed6\u8f7b\u63cf\u6de1\u5199\u5730\u5316\u5f00\uff0c\u4f46\u90a3\u6c49\u5b50\u60ef\u4e8e\u884c\u8d70\u5c71\u8def\uff0c\u4ed6\u7684\u8f7b\u529f\u4e0d\u53ca\u5bf9 \u65b9\uff0c\u5c31\u7528\u7a81\u88ad\u6765\u963b\u6b62\u5bf9\u65b9\u7684\u8ffd\u51fb\uff0c\u53ea\u8981\u963b\u5f97\u4e00\u963b\uff0c\u4fbf\u7acb\u5373\u8df3\u5230\u5730\u5f62\u5d0e\u5c96\u3001\u8346\u68d8\u5c16\u77f3\u5bc6\u5e03\u4e4b \u5904\uff0c\u90a3\u519b\u5b98\u5f80\u5f80\u8981\u7ed5\u8def\u6765\u8ffd\uff0c\u56e0\u6b64\u7adf\u7ed9\u4ed6\u9003\u5230\u4e86\u77f3\u5c4b\u7684\u9762\u524d\u3002 \u3000\u3000\u8fd9\u65f6\u4e8e\u627f\u73e0\u548c\u90a3\u5c11\u5973\u5df2\u7ecf\u7f62\u6597\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u5f80\u524d\u8fce\u4e0a\uff0c\u90a3\u519b\u5b98\u89c1\u4e86\u4e8e\u627f\u73e0\uff0c\u4f3c\u4e4e\u9887\u4e3a\u5403 \u60ca\uff0c\u56b7\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u8fd9\u5c0f\u5b50\u4e5f\u5728\u8fd9\u91cc\uff0c\u4f60\u662f\u77f3\u8001\u5934\u7684\u4ec0\u4e48\u4eba\uff1f\u201d\u4e8e\u627f\u73e0\u8fd9\u65f6\u5df2\u8ba4\u51fa\u8fd9\u519b\u5b98\u4e0d \u662f\u522b\u4eba\uff0c\u6b63\u662f\u5fa1\u6797\u519b\u7684\u526f\u7edf\u9886\u4e1c\u65b9\u6d1b\uff0c\u4e8e\u627f\u73e0\u5728\u4eac\u57ce\u5077\u7236\u4eb2\u7684\u9996\u7ea7\u65f6\uff0c\u66fe\u4e0e\u4ed6\u4ea4\u8fc7\u624b\uff0c\u6df1\u77e5 \u4ed6\u7684\u5389\u5bb3\uff0c\u5979\u867d\u7136\u4e0d\u77e5\u201c\u77f3\u8001\u5934\u201d\u662f\u4ec0\u4e48\u4eba\uff0c\u6599\u60f3\u4e1c\u65b9\u6d1b\u6765\u6b64\u5fc5\u65e0\u597d\u4e8b\uff0c\u5f53\u4e0b\u7acb\u5373\u6325\u52a8\u9752\u51a5 \u5b9d\u5251\uff0c\u4fbf\u5f85\u4e0e\u90a3\u5c11\u5973\u8054\u624b\u5939\u653b\u5f3a\u654c\u3002 \u3000\u3000\u5374\u4e0d\u6599\u90a3\u5c11\u5973\u5df2\u62a2\u5feb\u4e00\u6b65\uff0c\u5530\u5530\u4e24\u5251\uff0c\u523a\u5230\u4e86\u4e1c\u65b9\u6d1b\u80f8\u524d\uff0c\u4e0e\u4e1c\u65b9\u6d1b\u5148\u4ea4\u4e0a\u4e86\u624b\uff0c\u540c\u65f6 \u5927\u58f0\u53eb\u9053\uff1a\u201c\u6210\u5e08\u54e5\uff0c\u4f60\u7ed9\u6211\u5bf9\u4ed8\u8fd9\u4e2a\u5c0f\u5b50\uff0c\u8fd9\u5c0f\u5b50\u80c6\u6562\u6765\u6b3a\u4fae\u6211\uff0c\u4ed6\u4e0d\u662f\u597d\u4eba\uff01\u201d\u53e3\u4e2d\u8bf4 \u8bdd\uff0c\u624b\u5e95\u6beb\u4e0d\u653e\u677e\uff0c\u4e00\u53e3\u9752\u94a2\u5251\u7d27\u7d27\u5730\u7f20\u4e0a\u4e86\u4e1c\u65b9\u6d1b\u7684\u6708\u7259\u5200\uff0c\u53ee\u53ee\u5f53\u5f53\u5730\u6253\u5f97\u597d\u4e0d\u6fc0\u70c8\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u6014\u4e86\u4e00\u6014\uff0c\u90a3\u5c11\u5e74\u6c49\u5b50\u975e\u5e38\u542c\u4ed6\u7684\u5e08\u59b9\u7684\u8bdd\uff0c\u7adf\u7136\u629b\u5f00\u4e86\u5f53\u524d\u7684\u5f3a\u654c\uff0c\u6746\u68d2\u4e00 \u538b\uff0c\u5c31\u5c06\u4e8e\u627f\u73e0\u7684\u9752\u51a5\u5b9d\u5251\u538b\u7740\uff0c\u4e8e\u627f\u73e0\u6012\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u8fd9\u7965\u4e0d\u8bc6\u597d\u574f\uff01\u6211\u662f\u6765\u5e2e\u4f60 \u7684\uff01\u201d\u5b9d\u5251\u4e00\u63c9\uff0c\u5316\u89e3\u4e86\u6746\u68d2\u7684\u538b\u529b\uff0c\u90a3\u5c11\u5e74\u9887\u51fa\u610f\u5916\uff0c\u4f46\u4ecd\u662f\u4e0d\u6562\u653e\u677e\uff0c\u8ffd\u4e0a\u4e24\u6b65\uff0c\u6746\u68d2 \u4e00\u6a2a\uff0c\u906e\u4f4f\u95e8\u6237\uff0c\u7741\u5927\u773c\u775b\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u90a3\u5c11\u5973\u53eb\u9053\uff1a\u201c\u6210\u5e08\u54e5\u4e0d\u8981\u542c\u8fd9\u5c0f\u5b50 \u7684\u82b1\u8a00\u5de7\u8bed\uff0c\u4ed6\u521a\u624d\u8fd8\u80c6\u6562\u5bf9\u6211\u65e0\u793c\u5462\uff0c\u4f60\u7ed9\u6211\u5148\u5c06\u4ed6\u6253\u8d70\u3002\u201d\u90a3\u5c11\u5e74\u6c49\u5b50\u4e00\u542c\u8bf4\u4e8e\u627f\u73e0\u66fe \u5bf9\u4ed6\u7684\u5e08\u59b9\u201c\u65e0\u793c\u201d\uff0c\u52c3\u7136\u5927\u6012\uff0c\u51b7\u4e0d\u9632\u53c8\u662f\u5f53\u5934\u4e00\u68d2\uff0c\u4e8e\u627f\u73e0\u5927\u4e3a\u751f\u6c14\uff0c\u65bd\u5c55\u51fa\u79fb\u5f62\u6362\u6b65 \u7684\u4e0a\u4e58\u8eab\u6cd5\uff0c\u5728\u68d2\u5e95\u4e00\u94bb\uff0c\u6ed1\u4f3c\u6e38\u9f9f\u5730\u4e00\u95ea\u95ea\u5f00\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u5530\u7684\u4e00\u58f0\uff0c\u5c06\u90a3\u5c11\u5e74\u8863\u670d\u7684\u4e24 \u9897\u94ae\u6263\u6311\u5f00\uff0c\u51b7\u6c14\u68ee\u68ee\uff0c\u76f4\u6c81\u808c\u8089\uff0c\u90a3\u5c11\u5e74\u5403\u4e86\u4e00\u60ca\uff0c\u5374\u89c1\u4e8e\u627f\u73e0\u7a81\u7136\u5730\u5c06\u5b9d\u5251\u62bd\u56de\uff0c\u51b7\u7b11 \u8bf4\u9053\uff1a\u201c\u771f\u662f\u72d7\u54ac\u5415\u6d1e\u5bbe\uff0c\u4e0d\u8bc6\u597d\u4eba\u5fc3\uff0c\u4e0d\u770b\u5728\u94c1\u955c\u5fc3\u9762\u4e0a\uff0c\u6211\u8fd9\u4e00\u5251\u5c31\u5c06\u4f60\u523a\u4e86\u4e2a\u900f\u660e\u7684 \u7a9f\u7abf\uff01\u201d\u90a3\u5c11\u5e74\u6c49\u5b50\u5fc3\u5934\u4e00\u9707\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u54ea\u4e2a\u94c1\u955c\u5fc3\uff1f\u201d\u4e8e\u627f\u73e0\u51b7\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u54ea\u4e2a\u94c1 \u955c\u5fc3\uff1f\u8fd8\u4e0d\u5c31\u662f\u73b0\u5728\u6b63\u88ab\u76d1\u7981\u5728\u77e5\u5e9c\u8859\u95e8\u7684\u90a3\u4e2a\u94c1\u955c\u5fc3\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u4e00\u9762\u6325\u5251\u62b5\u6321\u7740\u4e1c\u65b9\u6d1b\u7684\u653b\u52bf\uff0c\u4e00\u9762\u5374\u4ecd\u5728\u7559\u795e\u5730\u542c\u4ed6\u4eec\u8c08\u8bdd\uff0c\u8fd9\u65f6\u53c8\u53eb\u9053\uff1a \u201c\u4e0d\u8981\u542c\u4ed6\u80e1\u8bf4\uff0c\u94c1\u5e08\u54e5\u54ea\u6709\u8fd9\u6837\u7684\u670b\u53cb\u3002\u201d\u5ffd\u542c\u5f97\u5693\u7684\u4e00\u58f0\uff0c\u539f\u6765\u662f\u4e1c\u65b9\u6d1b\u8d81\u90a3\u5c11\u5973\u8bf4\u8bdd \u5206\u795e\u4e4b\u9645\uff0c\u731b\u65ab\u4e00\u5200\uff0c\u51e0\u4e4e\u628a\u90a3\u5c11\u5973\u624b\u4e2d\u7684\u9752\u94a2\u5251\u9707\u5f97\u8131\u624b\u98de\u53bb\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5403\u4e86\u4e00\u60ca\uff0c\u91d1\u4e1d\u6746\u68d2\u8f6c\u4e86\u4e00\u4e2a\u65b9\u5411\uff0c\u90a3\u5c11\u5973\u53c8\u95ee\u9053\uff1a\u201c\u4e0d\u5fc5\u7ba1\u6211\uff0c\u6211\u5bf9\u4ed8\u5f97\u4e86\uff0c \u4f60\u66ff\u6211\u6253\u53d1\u90a3\u4e2a\u5c0f\u5b50\u3002\u201d\u5979\u7adf\u7136\u5341\u5206\u597d\u80dc\uff0c\u4e0d\u613f\u8981\u5e08\u5144\u76f8\u52a9\u3002\u90a3\u5c11\u5e74\u7a0d\u4e00\u8e0c\u8e87\uff0c\u7ed3\u679c\u8fd8\u662f\u542c \u4e86\u5e08\u59b9\u7684\u8bdd\uff0c\u970d\u5730\u4e00\u6367\uff0c\u53c8\u5411\u4e8e\u627f\u73e0\u7684\u4e0b\u4e09\u8def\u5377\u6765\uff0c\u4e8e\u627f\u73e0\u5927\u6012\uff0c\u817e\u8eab\u4e00\u8dc3\uff0c\u4e00\u62db\u201c\u91d1\u9488\u5ea6 \u7ebf\u201d\uff0c\u60f3\u7d22\u6027\u628a\u90a3\u5c11\u5e74\u7684\u94ae\u6263\u5168\u90fd\u6311\u5f00\uff0c\u6559\u4ed6\u77e5\u96be\u800c\u9000\u3002\u90a3\u5c11\u5e74\u7684\u529f\u592b\u8fdc\u4e0d\u5982\u94c1\u955c\u5fc3\uff0c\u4ea6\u4e0d \u5982\u4ed6\u7684\u5e08\u59b9\uff0c\u4f46\u7a76\u7adf\u662f\u66fe\u5f97\u540d\u5e08\u4f20\u6388\uff0c\u521a\u624d\u5403\u4e86\u4e8e\u627f\u73e0\u7684\u4e8f\uff0c\u8fd9\u6b21\u5df2\u6709\u4e86\u9632\u5907\uff0c\u4ed6\u8f7b\u529f\u7a0d \u900a\uff0c\u81c2\u529b\u5374\u662f\u6781\u4e3a\u96c4\u6d51\uff0c\u6746\u68d2\u4e00\u4e2a\u76d8\u65cb\uff0c\u5c06\u5168\u8eab\u906e\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u4e8e\u627f\u73e0\u7684\u5ba4\u5251\u7adf\u7136\u523a\u4e0d\u8fdb \u53bb\uff0c\u90a3\u5c11\u5e74\u5c45\u7136\u8fd8\u4e58\u9699\u8fdb\u653b\uff0c\u4e8e\u627f\u73e0\u5251\u8d70\u8f7b\u7075\uff0c\u548c\u4ed6\u62c6\u4e86\u5341\u591a\u62db\uff0c\u5ffd\u5730\u7528\u4e86\u4e00\u62db\u7edd\u5999\u7684\u5251 \u6cd5\uff0c\u5c06\u4ed6\u7684\u6746\u68d2\u8feb\u4f4f\uff0c\u5b9d\u5251\u4e00\u4e2a\u56de\u73af\u53cd\u524a\uff0c\u545c\u7684\u4e00\u58f0\uff0c\u5c06\u4ed6\u7684\u6746\u68d2\u524a\u53bb\u4e86\u4e00\u622a\u3002\u4e8e\u627f\u73e0\u53eb \u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\u6211\uff0c\u4e5f\u8be5\u4fe1\u4f60\u7684\u5e08\u5144\u94c1\u955c\u5fc3\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u6c49\u5b50\u8c8c\u4f3c\u7c97\u9c81\uff0c\u4eba\u5374\u6734\u5b9e\uff0c\u4e0d\u4f3c\u4ed6\u5e08\u59b9\u90a3\u6837\u731c\u7591\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5251\u6cd5\u4e0d\u5728 \u6211\u94c1\u5e08\u5144\u4e4b\u4e0b\uff0c\u82e5\u7136\u4ed6\u771f\u662f\u6000\u6709\u574f\u610f\uff0c\u521a\u624d\u90a3\u4e24\u5251\u5c82\u80fd\u5bf9\u6211\u7559\u60c5\uff1f\u201d\u867d\u7136\u4ecd\u672a\u653e\u677e\u6212\u5907\uff0c\u624b \u4e2d\u6746\u68d2\u5374\u5df2\u6309\u7740\u4e0d\u52a8\uff0c\u7741\u7740\u4e24\u4e2a\u5927\u773c\u775b\u95ee\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f\u5e72\u4ec0\u4e48\u6765\u7684\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u662f\u7ed9 \u4f60\u7684\u5e08\u5144\u5e26\u53e3\u4fe1\u6765\u7684\u3002\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u5e26\u4ec0\u4e48\u53e3\u4fe1\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ed6\u88ab\u7981\u5728\u77e5\u5e9c\u8859\u95e8\uff0c\u660e \u65e5\u53ef\u8981\u4ea4\u7ed9\u65e5\u672c\u4eba\u4e86\u3002\u201d\u90a3\u5c11\u5e74\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4e48\u51e0\u53e5\u4e48\uff1f\u201d\u542c\u4ed6\u8bed\u6c14\uff0c\u77a7\u4ed6 \u795e\u8272\uff0c\u4f3c\u4e4e\u8fd9\u4e9b\u4e8b\u60c5\u4ed6\u65e9\u5df2\u77e5\u9053\u3002\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u95ee\u4ec0\u4e48\uff1f\u201d\u90a3\u5c11\u5e74\u7565\u4e00\u6c89\u541f\uff0c\u6602\u5934\u95ee \u9053\uff1a\u201c\u4f9d\u4f60\u6240\u8bf4\uff0c\u6211\u7684\u5e08\u5144\u662f\u88ab\u8f6f\u7981\u5728\u77e5\u5e9c\u8859\u95e8\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u6211 \u5e08\u5144\u6709\u964d\u9f99\u4f0f\u864e\u4e4b\u80fd\uff0c\u8349\u4e0a\u98de\u884c\u4e4b\u6280\uff0c\u4f55\u4ee5\u4ed6\u80af\u8ba9\u77e5\u5e9c\u4ea4\u4e0e\u502d\u5974\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u81ea \u5df1\u7684\u610f\u601d\uff0c\u4ec0\u4e48\u7528\u610f\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u4ed6\u5411\u6211\u5ff5\u8fc7\u4e24\u53e5\u8bd7\uff0c\u8bf4\u662f\u8981\u5f2f\u5f13\u6b32\u5c04\u5357\u5c71\u864e\uff0c\u62d4\u5251\u601d\u9664\u5317 \u6d77\u86df\uff0c\u542c\u6765\u597d\u6a61\u4ed6\u522b\u6709\u6253\u7b97\u5462\uff01\u201d\u90a3\u5c11\u5e74\u773c\u775b\u4e00\u4eae\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u8fd9\u4eba\u8bf4\u5f97\u4e0d\u9519\uff0c\u4ed6 \u786e\u5b9e\u662f\u66ff\u54b1\u4eec\u7684\u5e08\u5144\u5e26\u53e3\u4fe1\u6765\u7684\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u4e00\u58f0\u4e0d\u54cd\uff0c\u4e8e\u627f\u73e0\u5fc3\u4e2d\u5947\u602a\uff0c\u62ac\u5934\u671b\u65f6\uff0c\u770b\u89c1\u5979\u548c\u4e1c\u65b9\u6d1b\u6253\u5f97\u975e\u5e38\u6fc0\u70c8\uff0c\u4e00\u7247\u5200 \u5149\u5251\u5f71\uff0c\u8000\u773c\u6b32\u82b1\uff0c\u4e24\u4eba\u76f8\u6597\uff0c\u7adf\u5316\u51fa\u4e86\u5341\u6570\u6761\u4eba\u5f71\uff0c\u5374\u53c8\u5168\u4e0d\u95fb\u5175\u5203\u78b0\u51fb\u4e4b\u58f0\uff0c\u4f46\u7ad9\u5728\u79bb \u4ed6\u4eec\u5341\u6570\u4e08\u4e4b\u5904\uff0c\u4e5f\u611f\u89c9\u5230\u5bd2\u98ce\u98d2\u98d2\uff0c\u51b7\u6c14\u903c\u4eba\u3002\u4e8e\u627f\u73e0\u662f\u4e2a\u5251\u6cd5\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u53ea\u4e00\u770b\uff0c\u4fbf \u77e5\u9053\u4ed6\u4eec\u5404\u4ee5\u6700\u8fc5\u6377\u7684\u62db\u6570\u53ae\u62fc\uff0c\u4e24\u65b9\u90fd\u5728\u4e58\u57e0\u62b5\u9699\uff0c\u907f\u62db\u8fdb\u62db\uff0c\u770b\u4f3c\u6e38\u6597\uff0c\u5176\u5b9e\u5374\u51f6\u9669\u4e4b \u6781\u3002\u54ea\u4e00\u65b9\u7a0d\u6709\u4e0d\u614e\uff0c\u53ea\u6015\u5c31\u8981\u7acb\u523b\u8840\u6e85\u9ec4\u6c99\uff01 \u3000\u3000\u90a3\u5c11\u5973\u7684\u5251\u6cd5\u548c\u638c\u6cd5\u540c\u4e00\u8def\u6570\uff0c\u4e00\u62db\u672a\u5c3d\uff0c\u7b2c\u4e8c\u62db\u53c8\u5df2\u53d1\u51fa\uff0c\u8fde\u7ef5\u4e0d\u65ad\uff0c\u800c\u6bcf\u4e00\u5251\u62db\u5212 \u6210\u4e00\u5706\u5708\uff0c\u4e00\u4e2a\u5706\u5708\u63a5\u7740\u4e00\u4e2a\u5706\u5708\uff0c\u6709\u5982\u540e\u6d6a\u4e4b\u63a8\u524d\u6d6a\uff0c\u4e0e\u4efb\u4f55\u4e00\u5bb6\u5251\u6cd5\uff0c\u90fd\u7edd\u65e0\u534a\u70b9\u76f8\u7c7b \u4e4b\u5904\u3002\u4e1c\u65b9\u6d1b\u4e5f\u4f7f\u51fa\u4e86\u6781\u5176\u98d8\u5ffd\u4e0d\u5b9a\u7684\u5200\u6cd5\uff0c\u884c\u524d\u5ffd\u540e\uff0c\u884c\u5de6\u5ffd\u53f3\uff0c\u6bcf\u5288\u4e00\u5200\uff0c\u90fd\u631f\u7740\u547c\u547c \u7684\u98ce\u58f0\uff0c\u4f46\u78b0\u7740\u4e86\u5c11\u5973\u8fd9\u79cd\u60ca\u6d9b\u9a87\u6d6a\u822c\u6eda\u6eda\u800c\u4e0a\u7684\u5251\u62db\uff0c\u4e5f\u7ed9\u903c\u5f97\u56db\u8fb9\u6e38\u8d70\uff0c\u5200\u950b\u6311\u4e0d\u79bb\u5251 \u5708\u3002\u4e8e\u627f\u73e0\u770b\u5f97\u76ee\u7729\u795e\u6447\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u82e5\u7136\u8fd9\u5c11\u5973\u529f\u529b\u7a0d\u9ad8\uff0c\u4e50\u65b9\u6d1b\u7edd\u4e0d\u662f\u5979\u7684\u5bf9\u624b\uff01\u201d \u731b\u5730\u60f3\u8d77\u4e00\u4eba\uff0c\u51b2\u53e3\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u662f\u77f3\u60ca\u6d9b\u7684\u5f1f\u5b50\u4e48\uff1f\u201d\u90a3\u5c11\u5973\u8be7\u9053\uff1a\u201c\u4f60\u600e\u8ba4\u5f97\u5bb6\u5e08\uff1f\u201d \u3000\u3000\u5f53\u65f6\u5929\u4e0b\u6709\u56db\u4f4d\u8457\u540d\u7684\u5251\u5ba2\uff0c\u5357\u8fb9\u662f\u5f20\u4e39\u67ab\uff0c\u5317\u8fb9\u662f\u4e4c\u8499\u592b\uff0c\u897f\u8fb9\u662f\u9633\u5b97\u6d77\uff0c\u4e1c\u8fb9\u662f\u77f3 \u60ca\u6d9b\u3002\u56db\u5927\u5251\u5ba2\u4e4b\u4e2d\uff0c\u4ee5\u5f20\u4e39\u67ab\u7684\u5e74\u7eaa\u6700\u5c0f\uff0c\u58f0\u540d\u5374\u6700\u5927\uff0c\u77f3\u60ca\u6d9b\u7684\u5e74\u7eaa\u6700\u5927\uff0c\u77e5\u9053\u4ed6\u7684\u4eba \u53cd\u800c\u4e0d\u5f88\u591a\u3002\u56e0\u4e3a\u4ed6\u5728\u4e8c\u5341\u591a\u5e74\u4e4b\u524d\uff0c\u5c31\u66fe\u56e0\u4e3a\u76d7\u4e86\u5927\u5185\u7684\u5b9d\u5251\uff0c\u72af\u4e86\u91cd\u6848\uff0c\u9003\u4ea1\u6d77\u5916\uff0c\u4e8c \u5341\u5e74\u6765\u6c5f\u6e56\u4e0a\u4e0d\u95fb\u4ed6\u7684\u6d88\u606f\u3002\u6240\u4ee5\u540e\u4e00\u8f88\u7684\u8bb8\u591a\u90fd\u672a\u542c\u8fc7\u6c60\u7684\u540d\u5b57\u3002\u5f20\u4e39\u67ab\u4e5f\u53ea\u77e5\u9053\u4ed6\u521b\u6709 \u4e00\u5957\u201c\u60ca\u6d9b\u5251\u6cd5\u201d\uff0c\u5e74\u8f7b\u4e4b\u65f6\uff0c\u66fe\u6267\u665a\u8f88\u4e4b\u793c\u5411\u81ea\u5df1\u7684\u5e08\u7956\u7384\u673a\u9038\u58eb\u8bf7\u6559\uff0c\u7384\u673a\u9038\u58eb\u90a3\u65f6\u6b63 \u7ec3\u6210\u4e86\u767d\u4e91\u9752\u51a5\u4e24\u628a\u5b9d\u5251\uff0c\u5c31\u968f\u4fbf\u62ff\u8d77\u4e86\u4e00\u628a\u9752\u51a5\u5b9d\u5251\u548c\u4ed6\u8bd5\u62db\uff0c\u5728\u5341\u62db\u4e4b\u5185\uff0c\u5c06\u4ed6\u7684\u957f\u5251 \u524a\u65ad\u3002\u5f53\u65f6\u7384\u673a\u9038\u58eb\u4fbf\u66fe\u5927\u5927\u5730\u79f0\u8d5e\u8fc7\u77f3\u60ca\u6d9b\u7684\u5251\u6cd5\uff0c\u90a3\u65f6\u4e5f\u7ed9\u4ed6\u6307\u51fa\u4e86\u5251\u6cd5\u4e2d\u7684\u8bb8\u591a\u7834 \u7efd\u3002\u7384\u673a\u9038\u58eb\u7684\u8bdd\u7edd\u65e0\u534a\u70b9\u5ba2\u5957\uff0c\u8981\u77e5\u7384\u673a\u9038\u571f\u90a3\u65f6\u5df2\u662f\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\uff0c\u665a\u4e00\u8f88\u7684\u80fd\u591f\u548c\u4ed6 \u62c6\u5230\u5341\u62db\uff0c\u90a3\u786e\u662f\u7edd\u65e0\u4ec5\u6709\uff0c\u4f46\u77f3\u60ca\u6d9b\u5374\u751a\u611f\u7f9e\u6127\uff0c\u540c\u65f6\u53c8\u7fa1\u6155\u7384\u673a\u9038\u58eb\u6240\u7ec3\u7684\u5b9d\u5251\u3002\u867d\u7136 \u4ed6\u4e5f\u6df1\u6df1\u4f69\u670d\u7384\u673a\u5251\u6cd5\u7684\u7cbe\u5999\uff0c\u4f46\u79c1\u5fc3\u91cc\u5374\u8ba4\u4e3a\u7384\u673a\u9038\u58eb\u4e4b\u6240\u4ee5\u80fd\u5728\u5341\u62db\u4e4b\u5185\u524a\u65ad\u4ed6\u7684\u5175 \u5203\uff0c\u90a3\u8fd8\u662f\u9760\u5b9d\u5251\u4e4b\u529b\uff08\u6b8a\u4e0d\u77e5\u7384\u673a\u9038\u58eb\u53ea\u56e0\u4e3a\u6070\u597d\u6709\u8fd9\u4e24\u628a\u5b9d\u5251\u5728\u624b\u8fb9\uff0c\u6240\u4ee5\u4fbf\u987a\u624b\u62ff\u6765 \u8fc7\u62db\u3002\u82e5\u7528\u666e\u901a\u7684\u5200\u5251\uff0c\u4e5f\u540c\u6837\u53ef\u4ee5\u524a\u65ad\u77f3\u60ca\u6d9b\u7684\u5175\u5203\uff09\u3002\u56e0\u6b64\u4ed6\u540e\u6765\u624d\u52a8\u4e86\u5230\u5927\u5185\u76d7\u5251\u7684 \u5ff5\u5934\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u662f\u89c1\u4e86\u5c11\u5973\u8fd9\u5957\u72ec\u7279\u7684\u5251\u6cd5\uff0c\u4fe8\u4f3c\u60ca\u6d9b\u9a87\u6d6a\uff0c\u542c\u5f97\u4e1c\u65b9\u6d1b\u8bf4\u51fa\u201c\u77f3\u8001\u5934\u201d\u4e09\u5b57\uff0c \u8fd9\u624d\u60f3\u8d77\u6765\u7684\u3002\u679c\u7136\u4e00\u731c\u4fbf\u4e2d\uff0c\u90a3\u5c11\u5e74\u6c49\u5b50\u751a\u662f\u60ca\u8be7\uff0c\u6b63\u5728\u8ffd\u95ee\uff0c\u5ffd\u542c\u5f97\u53ee\u5f53\u4e00\u58f0\uff0c\u706b\u661f\u98de \u5904\uff0c\u4e1c\u65b9\u6d1b\u6a2a\u5200\u75be\u65ab\uff0c\u81ea\u5df1\u7684\u5e08\u59b9\u5374\u4e0d\u4f4f\u5730\u540e\u9000\u3002\u539f\u6765\u90a3\u5c11\u5973\u5251\u6cd5\u867d\u5999\uff0c\u6c14\u529b\u5374\u662f\u5927\u4e0d\u5982 \u4eba\uff0c\u4e1c\u65b9\u6d1b\u8d81\u7740\u5979\u6c14\u529b\u4e0d\u7ee7\uff0c\u56db\u5341\u4e5d\u8def\u5251\u6cd5\u544a\u4e00\u6bb5\u843d\u4e4b\u9645\uff0c\u7a81\u7136\u53cd\u6251\uff0c\u60ca\u6d9b\u5251\u6cd5\u5168\u5728\u90a3\u80a1\u51cc \u5389\u7684\u53bb\u52bf\uff0c\u5ffd\u7136\u53d7\u963b\uff0c\u5c31\u4f3c\u6ce2\u6d9b\u78b0\u5230\u4e86\u77f3\u5824\u4e00\u822c\uff0c\u51b2\u4e0d\u8fc7\u53bb\uff0c\u6d6a\u5934\u53cd\u800c\u5012\u629b\u56de\u6765\u3002\u90a3\u5c11\u5973\u7ed9 \u4e1c\u65b9\u6d1b\u8fde\u903c\u6570\u62db\uff0c\u5251\u950b\u53cd\u5f39\u56de\u6765\uff0c\u51e0\u4e4e\u4f24\u4e86\u81ea\u5df1\u3002\u90a3\u5c11\u5e74\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u4e0d\u597d\u201d\uff0c\u6b63\u5f85\u4e0a\u524d\u52a9 \u6218\uff0c\u5ffd\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u4e1c\u65b9\u6d1b\u5200\u4e0a\u7684\u6708\u7259\uff0c\u5df2\u52fe\u7834\u4e86\u5c11\u5973\u7684\u8863\u8896\u3002 \u3000\u3000\u4e1c\u65b9\u6d1b\u8fd9\u624b\u5200\u6cd5\u5f53\u771f\u662f\u4f7f\u5f97\u975e\u5e38\u72e0\u6bd2\uff0c\u5200\u4e0a\u7684\u6708\u7259\u52fe\u7740\u4e86\u5c11\u5973\u7684\u8863\u8896\uff0c\u660e\u6643\u6643\u7684\u5200\u5c16\u76f4 \u5f80\u91cc\u624e\uff0c\u5c11\u5973\u7684\u534a\u8fb9\u8eab\u5b50\u53d7\u4e86\u7275\u5236\uff0c\u624b\u81c2\u8f6c\u52a8\u4e0d\u7075\uff0c\u9752\u94a2\u5251\u4e5f\u88ab\u4e1c\u65b9\u6d1b\u7684\u5200\u67c4\u94ec\u4f4f\uff0c\u6025\u5207\u4e4b \u95f4\uff0c\u4e0d\u80fd\u64a4\u5251\u56de\u9632\uff0c\u773c\u89c1\u90a3\u5200\u5c16\u624e\u4e0b\uff0c\u4fbf\u5c06\u662f\u65ad\u8155\u6298\u81c2\u4e4b\u707e\u3002\u4e8e\u627f\u73e0\u4e00\u58f0\u957f\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u597d \u59b9\u5b50\uff0c\u4f60\u4eec\u5e08\u5144\u59b9\u53d9\u53d9\uff0c\u8ba9\u6211\u63a5\u66ff\u4f60\u5427\u3002\u201d\u957f\u7b11\u58f0\u4e2d\uff0c\u91d1\u82b1\u8131\u624b\u98de\u51fa\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u7b2c\u4e00\u6735\u91d1 \u82b1\u5c06\u4e1c\u65b9\u6d1b\u7684\u5200\u5c16\u6253\u6b6a\uff0c\u7b2c\u4e8c\u6735\u91d1\u82b1\u628a\u5c11\u5973\u7684\u8863\u62bd\u5272\u65ad\uff0c\u90a3\u5c11\u5973\u624b\u81c2\u6d3b\u52a8\uff0c\u6025\u5fd9\u53cd\u624b\u4e00\u5251\uff0c \u4e1c\u65b9\u6d1b\u8df3\u8fc7\u4e00\u8fb9\uff0c\u5374\u88ab\u4e8e\u627f\u73e0\u622a\u7740\u4e86\u53bb\u8def\uff0c\u90a3\u5c11\u5973\u56de\u5251\u518d\u524d\uff0c\u4e8e\u627f\u73e0\u5df2\u4e0e\u4e1c\u65b9\u6d1b\u4ea4\u4e0a\u4e86\u624b\u3002 \u3000\u3000\u90a3\u5c11\u5973\u5446\u4e86\u4e00\u5446\uff0c\u53ea\u89c1\u4e8e\u627f\u73e0\u5251\u52bf\u8f7b\u7075\u7fd4\u52a8\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u5df2\u4e0e\u4e1c\u65b9\u6d1b\u62c6\u4e86\u4e03\u516b\u62db\uff0c\u90a3\u5c11 \u5e74\u6c49\u5b50\u62b9\u4e86\u53e3\u989d\u51b7\u6c57\uff0c\u4e0a\u524d\u62c9\u7740\u4ed6\u7684\u5e08\u59b9\u9053\uff1a\u201c\u6211\u770b\u8fd9\u4f4d\u5c11\u5e74\u82f1\u96c4\u662f\u771f\u5fc3\u771f\u610f\u6765\u5e2e\u4f60\u7684\u3002\u201d \u5c11\u5973\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u674f\u8138\u98de\u7ea2\uff0c\u4e0d\u53d1\u8a00\u8bed\u3002\u90a3\u5c11\u5e74\u53c8\u9053\uff1a\u201c\u4ed6\u8bf4\u662f\u54b1\u4eec\u94c1\u5e08\u54e5\u7684\u597d\u53cb\uff0c\u6211\u770b \u5e76\u975e\u865a\u5047\u3002\u201d\u5c11\u5973\u6012\u6c14\u672a\u6d88\uff0c\u542b\u7cca\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u89c1\u5f97\uff1f\u201d\u90a3\u5c11\u5e74\u5c06\u5979\u62c9\u8fc7\u4e00\u8fb9\uff0c\u561f\u561f\u5495\u5495\u5730 \u4f4e\u58f0\u8bf4\u8bdd\u3002\u4e8e\u627f\u73e0\u4e00\u9762\u62b5\u6321\u4e1c\u65b9\u6d1b\u7684\u653b\u52bf\uff0c\u4e00\u9762\u51b7\u773c\u5077\u7aa5\uff0c\u5fc3\u4e2d\u6697\u6697\u597d\u7b11\u3002\u89c1\u4ed6\u4eec\u4e8c\u4eba\u4ea4\u5934 \u6388\u9888\u5730\u8c08\u7b11\uff0c\u6001\u5ea6\u751a\u4e3a\u4eb2\u70ed\uff0c\u5fc3\u4e2d\u5ffd\u5730\u4e00\u677e\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u5979\u548c\u8fd9\u4f4d\u5e08\u5144\uff0c\u4ea4\u60c5\u66f4\u597d\u3002\u90a3\u5c11 \u5973\u9002\u624d\u51fa\u8a00\u4e0d\u900a\uff0c\u5c61\u6b21\u8981\u9a71\u9010\u5979\u3002\u4e8e\u627f\u73e0\u672c\u6765\u6709\u70b9\u751f\u6c14\uff0c\u8fd9\u65f6\u5374\u4e0d\u77e5\u600e\u7684\u5ffd\u7136\u5bf9\u5979\u597d\u611f\u8d77 \u6765\uff0c\u89c9\u5f97\u5979\u7a1a\u6c14\u672a\u6d88\uff0c\u5927\u662f\u60f9\u4eba\u601c\u7231\uff08\u5176\u5b9e\u4e8e\u627f\u73e0\u4e0e\u5979\u4e00\u822c\u5e74\u7eaa\uff0c\u540c\u6837\u4e5f\u662f\u7a1a\u6c14\u672a\u6d88\uff09\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5206\u4e86\u5fc3\u795e\uff0c\u80e1\u601d\u4e71\u60f3\uff0c\u5251\u52bf\u7a0d\u677e\uff0c\u4e1c\u65b9\u6d1b\u7acb\u523b\u4e58\u673a\u53cd\u6251\uff0c\u6708\u7259\u5200\u4e00\u4f38\u4e00\u8e1e\uff0c\u4fe8\u5982 \u6bd2\u86c7\u5410\u4fe1\uff0c\u51e0\u4e4e\u523a\u5230\u4e86\u4e8e\u627f\u73e0\u7684\u54bd\u5589\u3002\u90a3\u5c11\u5e74\u6c49\u5b50\u4e00\u773c\u77a5\u89c1\uff0c\u53eb\u58f0\u4e0d\u597d\uff0c\u6746\u68d2\u4e00\u6325\uff0c\u5954\u4e0a\u51e0 \u6b65\uff0c\u5ffd\u542c\u5f97\u201c\u53ee\u5f53\u201d\u4e00\u58f0\uff0c\u706b\u661f\u98de\u6e85\uff0c\u4e1c\u65b9\u6d1b\u5200\u4e0a\u7684\u6708\u7259\uff0c\u5df2\u88ab\u4e8e\u627f\u73e0\u7684\u9752\u51a5\u5b9d\u5251\u524a\u53bb\u4e86\u4e24 \u9f7f\u3002\u539f\u6765\u4e8e\u627f\u73e0\u81ea\u51fa\u9053\u4e4b\u540e\uff0c\u7ecf\u8fc7\u4e86\u5927\u5c0f\u5341\u6570\u6b21\u7684\u53ae\u6740\uff0c\u5b9e\u6218\u7684\u7ecf\u9a8c\u589e\u957f\u4e86\u8bb8\u591a\uff0c\u800c\u4e14\u53c8\u5f97 \u9ed1\u767d\u6469\u8bc3\u8bb2\u6388\u4e94\u884c\u62f3\u7cbe\u4e49\uff0c\u6b66\u529f\u4e0a\u4e5f\u6709\u589e\u76ca\uff0c\u4e0e\u7b2c\u4e00\u6b21\u6597\u4e1c\u65b9\u6d1b\u4e4b\u65f6\uff0c\u5df2\u662f\u5927\u4e0d\u76f8\u540c\uff0c\u90a3\u4e00 \u6b21\u5979\u4e0e\u4e1c\u65b9\u6d1b\u53ea\u4e0d\u8fc7\u4ea4\u6362\u4e86\u5341\u6765\u62db\uff0c\u6253\u6210\u5e73\u624b\u3002\u8fd9\u4e00\u6b21\u4e1c\u65b9\u6d1b\u4ecd\u60f3\u6b3a\u5979\u5e74\u8f7b\u8bc6\u6d45\uff0c\u7528\u7e41\u590d\u7684 \u8fdb\u624b\u5200\u6cd5\uff0c\u8d81\u5979\u5206\u795e\u4e4b\u9645\uff0c\u6b3a\u8eab\u5288\u7948\uff0c\u54ea\u77e5\u62db\u6570\u7528\u8001\uff0c\u4e8e\u627f\u73e0\u7a81\u7136\u4f7f\u51fa\u7384\u673a\u5251\u6cd5\u4e2d\u5185\u516b\u5708\u7684 \u7cbe\u5999\u5251\u6cd5\uff0c\u4e00\u4e3e\u53cd\u51fb\uff0c\u82e5\u975e\u4e1c\u65b9\u6d1b\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u6b66\u529f\u4e5f\u786e\u6709\u9020\u8be3\uff0c\u53d8\u62db\u5f97\u5feb\uff0c\u6708\u7259\u5200\u4e5f\u51e0\u4e4e\u88ab \u5979\u524a\u65ad\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4e0d\u7981\u5927\u58f0\u53eb\u9053\uff1a\u201c\u597d\uff01\u201d\u4ed6\u7684\u5e08\u59b9\u867d\u7136\u6ca1\u6709\u559d\u5f69\uff0c\u5fc3\u4e2d\u5374\u4e5f\u6697\u6697\u4f69\u670d\u3002\u53ea\u542c\u5f97\u4e8e \u627f\u73e0\u626c\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5e08\u5144\u59b9\u90fd\u6253\u5f97\u7d2f\u5566\uff0c\u597d\u597d\u5730\u6b47\u6b47\u8c08\u8c08\u5427\u3002\u201d\u54c8\u54c8\u5730\u7b11\u4e86\u51e0\u58f0\uff0c\u90a3\u5c11\u5e74 \u6c49\u5b50\u9762\u7ea2\u8033\u70ed\uff0c\u4f46\u89c1\u4ed6\u5e08\u59b9\u77aa\u773c\u9f13\u816e\uff0c\u5374\u662f\u76ee\u4e0d\u65c1\u77ac\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u548c\u4e1c\u65b9\u6d1b\u8fd9\u65f6\u5df2\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u5927\u5bb6\u90fd\u51fa\u4e86\u5168\u529b\u53ae\u62fc\uff0c\u8d8a\u6597\u8d8a\u70c8\u3002\u4f46\u89c1\u4e8e\u627f\u73e0\u90a3 \u53e3\u5b9d\u5251\u7ffb\u817e\u98de\u821e\uff0c\u500f\u8fdb\u500f\u9000\uff0c\u65f6\u5982\u5f69\u8776\u7a7f\u82b1\uff0c\u65f6\u5982\u873b\u8712\u70b9\u6c34\uff0c\u5251\u5149\u970d\u970d\uff0c\u8d5b\u5982\u51b7\u7535\u5bd2\u971c\uff0c\u7f24 \u7eb7\u98de\u821e\uff0c\u90a3\u5c11\u5973\u4e0d\u7981\u5012\u62bd\u4e86\u4e00\u53e3\u51b7\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u53ea\u9053\u6211\u4eec\u7684\u60ca\u6d9b\u5251\u6cd5\uff0c\u5df2\u662f\u5929\u4e0a\u65e0\u53cc\uff0c\u54ea \u77e5\u4e16\u95f4\u4e0a\u8fd8\u6709\u5982\u6b64\u7cbe\u5999\u7684\u5251\u6cd5\uff01\u201d\u4e1c\u65b9\u6d1b\u7684\u6708\u7259\u5200\u6cd5\uff0c\u4ea6\u662f\u81ea\u6210\u4e00\u5bb6\uff0c\u5200\u53e3\u80cc\u548c\u5200\u4e0a\u7684\u6708 \u7259\uff0c\u90fd\u6709\u4e0d\u540c\u7684\u529f\u7528\uff0c\u6216\u5288\u6216\u65ab\uff0c\u6216\u62cd\u6216\u52fe\uff0c\u4e00\u53e3\u5200\u517c\u6709\u94a9\u5251\u4e4b\u957f\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u9634\u72e0\u6076\u6bd2\uff0c \u4ea6\u786e\u662f\u6b66\u6797\u7f55\u89c1\u7684\u5200\u6cd5\u3002\u4f46\u6bd4\u8d77\u4e8e\u627f\u73e0\u7684\u201c\u767e\u53d8\u7384\u673a\u5251\u6cd5\u201d\uff0c\u5374\u8fd8\u662f\u4e0d\u514d\u76f8\u5f62\u89c1\u7ecc\u3002\u672c\u6765\u4e1c \u65b9\u6d1b\u7684\u529f\u529b\u548c\u7ecf\u9a8c\u8981\u6bd4\u4e8e\u627f\u73e0\u7a0d\u80dc\u4e00\u7b79\uff0c\u4ed6\u539f\u53ef\u4ee5\u4ee5\u8fd9\u4e24\u6837\u957f\u5904\uff0c\u5584\u81ea\u8fd0\u7528\uff0c\u6765\u62b5\u6d88\u62db\u6570\u4e0a \u7684\u5403\u4e8f\u3002\u4f46\u4e8e\u627f\u73e0\u9664\u4e86\u62db\u6570\u7cbe\u5947\u4e4b\u5916\uff0c\u8fd8\u517c\u6709\u4e00\u67c4\u524a\u94c1\u5982\u6ce5\u7684\u5b9d\u5251\uff0c\u4e1c\u65b9\u6d1b\u7684\u6708\u7259\u5200\u4e0d\u6562\u548c \u4ed6\u786c\u78b0\uff0c\u5200\u4e0a\u7684\u6708\u7259\uff0c\u4e0d\u80fd\u8fd1\u8eab\uff0c\u529f\u7528\u51cf\u4e86\u51e0\u5206\uff0c\u8fd9\u6837\u5728\u5175\u5668\u4e0a\u53c8\u5403\u4e86\u4e8f\uff0c\u66f4\u662f\u76f8\u5f62\u89c1\u7ecc \u4e86\u3002 \u3000\u3000\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u4e8e\u627f\u73e0\u6e10\u6e10\u62a2\u5230\u4e86\u4e0a\u98ce\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u5251\u52bf\u5982\u8679\uff0c\u53d8\u5e7b\u65e0\u65b9\uff0c\u8d8a\u53d1\u51cc\u5389\u3002 \u90a3\u5c11\u5973\u770b\u5f97\u51fa\u4e86\u795e\uff0c\u5fc3\u4e2d\u7684\u6012\u6c14\uff0c\u65e9\u5df2\u5316\u4e3a\u4e4c\u6709\u3002\u90a3\u5c11\u5e74\u6c49\u5b50\u89c1\u4e8e\u627f\u73e0\u5360\u5c3d\u4e0a\u98ce\uff1b\u5fc3\u5934\u4e00 \u677e\uff0c\u5ffd\u800c\u95ee\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6\u662f\u4e0d\u662f\u771f\u7684\u56de\u6765\u4e86\uff1f\u201d\u5c11\u5973\u4e00\u5fc3\u89c2\u6218\uff0c\u6b63\u770b\u5230\u7d27\u5f20\u4e4b \u5904\uff0c\u4fe1\u53e3\u7b54\u9053\uff1a\u201c\u6765\u4e86\uff0c\u6765\u4e86\uff01\u201d\u539f\u6765\u5979\u6b63\u770b\u5230\u4e8e\u627f\u73e0\u4f7f\u51fa\u4e00\u62db\u7edd\u5999\u7684\u5251\u6cd5\uff0c\u8fd9\u4e00\u5251\u672c\u6765\u662f \u81ea\u5de6\u800c\u53f3\uff0c\u5212\u6210\u534a\u4e2a\u5706\u5f27\uff0c\u5251\u5230\u4e2d\u9014\uff0c\u5374\u5ffd\u7136\u4e00\u53d8\uff0c\u5251\u950b\u7a81\u7136\u4e00\u98a4\uff0c\u4ece\u53f3\u8fb9\u53cd\u524a\u8fc7\u6765\uff0c\u4ee5\u5c11 \u5973\u8fd9\u6837\u7684\u5168\u795e\u8d2f\u6ce8\uff0c\u7adf\u7136\u770b\u4e0d\u51fa\u4e8e\u627f\u73e0\u7684\u624b\u6cd5\u5982\u4f55\u53d8\u5316\uff0c\u662f\u4ee5\u7981\u4e0d\u4f4f\u53eb\u51fa\u58f0\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u53eb\u4e0d\u6253\u7d27\uff0c\u5374\u628a\u4e1c\u65b9\u6d1b\u5413\u4e86\u4e00\u5927\u8df3\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u5c0f\u755c\u751f\u5206\u660e\u662f\u77f3\u60ca\u6d9b\u7684\u665a \u8f88\uff0c\u5df2\u8fd9\u6837\u5389\u5bb3\uff0c\u77f3\u60ca\u6d9b\u6765\u4e86\uff0c\u90a3\u8fd8\u4e86\u5f97\uff1f\u201d\u4ed6\u672c\u6765\u662f\u5949\u7687\u547d\u6765\u641c\u6355\u77f3\u60ca\u6d9b\u7684\uff0c\u521d\u6765\u4e4b\u65f6\uff0c \u8fd8\u6043\u7740\u672c\u8eab\u6280\u4e1a\uff0c\u4ee5\u4e3a\u77f3\u60ca\u6d9b\u867d\u662f\u4e45\u5df2\u6210\u540d\uff0c\u4f46\u800c\u4eca\u5e74\u8001\u529b\u8870\uff0c\u672a\u5fc5\u662f\u81ea\u5df1\u7684\u5bf9\u624b\uff0c\u54ea\u77e5\u521d \u78b0\u5230\u5c11\u5e74\u6c49\u5b50\uff0c\u6349\u4ed6\u4e0d\u7740\uff0c\u518d\u78b0\u90a3\u4e2a\u5c11\u5973\uff0c\u5df2\u662f\u96be\u6597\uff0c\u5982\u4eca\u6218\u4e8e\u627f\u73e0\uff0c\u8981\u4fdd\u6301\u4e0d\u8d25\uff0c\u4ea6\u6050\u4e0d \u80fd\uff0c\u5fc3\u4e2d\u65e9\u662f\u6c14\u9981\uff0c\u4e00\u542c\u8bf4\u77f3\u60ca\u6d9b\u6765\u4e86\uff0c\u5403\u4e00\u5927\u60ca\uff0c\u4e8e\u627f\u73e0\u5530\u5730\u4e00\u5251\u53cd\u524a\uff0c\u201c\u548b\u201d\u7684\u4e00\u58f0\uff0c \u5c06\u4ed6\u80a9\u4e0a\u7684\u4e24\u6839\u9aa8\u5934\uff0c\u524a\u53bb\u4e86\u4e00\u5927\u622a\u3002\u4e1c\u65b9\u6d1b\u53cd\u8eab\u4e00\u8dc3\uff0c\u987e\u4e0d\u7740\u75bc\u75db\u5c31\u6025\u5fd9\u6eda\u4e0b\u5c71\u5761\u3002\u4e8e\u627f \u73e0\u6536\u5251\u4e0d\u8ffd\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u5bf9\u90a3\u5c11\u5973\u9053\uff1a\u201c\u5982\u4eca\u4f60\u8be5\u76f8\u4fe1\u6211\u4e86\u5427\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u77aa\u4e86\u77aa\u773c\uff0c\u5979\u7684\u5e08\u5144\u5df2\u62a2\u524d\u4e00\u6b65\uff0c\u65bd\u793c\u8bf4\u9053\uff1a\u201c\u591a\u627f\u76f8\u52a9\uff0c\u5c0f\u5f1f\u5728\u8fd9\u53a2\u8c22\u8fc7 \u4e86\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u54b1\u4eec\u5fd9\u7740\u548c\u8fd9\u53ae\u6253\u4e86\u534a\u5929\uff0c\u8fd8\u6ca1\u6709\u8bf7\u6559\u59d3\u540d\u5462\u3002\u201d\u90a3\u5c11\u5973\u4ecd\u4e0d\u51fa\u58f0\uff0c\u90a3 \u5c11\u5e74\u5374\u723d\u723d\u5feb\u5feb\u5730\u7b11\u9053\uff1a\u201c\u6211\u7684\u5e08\u59b9\u53eb\u77f3\u6587\u7ea8\uff0c\u6211\u53eb\u6210\u6d77\u5c71\u3002\u6211\u5e08\u59b9\u5c31\u662f\u77f3\u8001\u5251\u5ba2\u7684\u5973 \u513f\u3002\u201d\u77f3\u6587\u7ea8\u53cc\u8fab\u4e00\u7529\uff0c\u9f13\u6c14\u8bf4\u9053\uff1a\u201c\u4f60\u53c8\u4e0d\u662f\u548c\u4ed6\u5bf9\u4eb2\uff0c\u5411\u4ed6\u80cc\u5bb6\u8c31\u4f5c\u751a\uff1f\u201d\u4e8e\u627f\u73e0 \u201c\u54ad\u201d\u5730\u7b11\u4e86\u4e00\u58f0\uff0c\u77f3\u6587\u7ea8\u8a00\u8bed\u51fa\u540e\uff0c\u624d\u89c9\u5f97\u81ea\u5df1\u592a\u6ca1\u906e\u62e6\uff0c\u7f9e\u5f97\u6ee1\u9762\u901a\u7ea2\u3002 \u3000\u3000\u6210\u6d77\u5c71\u88ab\u5e08\u59b9\u8d23\u5907\uff0c\u4e0d\u6562\u56de\u5634\uff0c\u4f46\u4f4e\u4e0b\u5934\u4f4e\u58f0\u4e0b\u6c14\u5730\u8fa9\u89e3\u9053\uff1a\u201c\u522b\u4eba\u65e9\u5df2\u77e5\u9053\u54b1\u4eec\u5e08\u7236 \u7684\u540d\u5b57\uff0c\u4f55\u51b5\u53c8\u4e0d\u662f\u5916\u4eba\uff0c\u8bf4\u4e0e\u4ed6\u542c\u6709\u4f55\u59a8\u788d\uff1f\u201d\u4e8e\u627f\u73e0\u63a5\u53e3\u9053\uff1a\u201c\u6211\u53eb\u4e8e\u627f\u73e0\uff0c\u6211\u7684\u5e08\u7236 \u53eb\u5f20\u4e39\u67ab\uff0c\u8bf4\u8d77\u6765\u5f53\u771f\u4e0d\u662f\u5916\u4eba\u3002\u201d \u3000\u3000\u6210\u6d77\u5c71\u201c\u554a\u5440\u201d\u4e00\u58f0\u8df3\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u539f\u6765\u662f\u5f20\u5927\u4fa0\u7684\u5f1f\u5b50\uff0c\u602a\u4e0d\u5f97\u5982\u6b64\u672c\u4e8b\uff01\u201d\u77f3 \u6587\u7ea8\u62ac\u5934\u77a7\u4e86\u4e8e\u627f\u73e0\u4e00\u773c\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u540d\u9707\u5f53\u4e16\uff0c\u4e49\u4fa0\u65e0\u53cc\uff0c\u5374\u600e\u4e48\u6536\u4e86\u8fd9\u4e48\u4e00\u4e2a \u8f7b\u8584\u5c0f\u5b50\u4e3a\u5f92\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u5e08\u7236\u4e45\u4ef0\u5c0a\u5e08\u5927\u540d\uff0c\u65e0\u7f18\u76f8\u4f1a\uff0c\u4eca\u65e5\u6211\u81ea\u5f53\u4ee3\u8868\u6211\u5e08\u7236\u8c12\u89c1\u77f3\u8001\u5251\u5ba2\uff0c \u5c31\u8bf7\u6587\u7ea8\u59d0\u59d0\u4e3a\u6211\u5f15\u89c1\u3002\u201d\u6210\u6d77\u5c71\u5fd9\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u4e0d\u6562\u5f53\uff01\u201d\u987b\u77e5\u5f20\u4e39\u67ab\u867d\u7136\u5e74\u8f7b\uff0c\u5374\u662f \u56db\u5927\u5251\u5ba2\u4e4b\u9996\uff0c\u4e8e\u627f\u73e0\u8bf4\u5f97\u592a\u5ba2\u6c14\u4e86\uff0c\u6210\u6d77\u5c71\u662f\u4e2a\u8001\u5b9e\u4eba\uff0c\u6545\u6b64\u7acb\u5373\u66ff\u81ea\u5df1\u7684\u5e08\u7236\u8c26\u8c22\uff0c\u540c \u65f6\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u59d3\u4e8e\u7684\u6587\u8d28\u5f6c\u5f6c\uff0c\u600e\u4e48\u6211\u5e08\u59b9\u5374\u8bf4\u4ed6\u65e0\u793c\uff1f\u201d \u3000\u3000\u77f3\u6587\u7ea8\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5373\u7b97\u6211\u7236\u4eb2\u5728\u5bb6\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u89c1\u4f60\uff01\u201d\u6210\u6d77\u5c71\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u4f60\uff0c\u4f60\u600e \u53ef\u2026\u2026\u201d\u77f3\u6587\u7ea8\u77aa\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u4ec0\u4e48\uff1f\u201d\u6210\u6d77\u5c71\u672c\u60f3\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u53ef\u5982\u6b64\u5931 \u8a00\uff1f\u201d\u89c1\u4ed6\u5e08\u59b9\u4e00\u77aa\u773c\u775b\uff0c\u540e\u534a\u622a\u8bdd\u7f29\u4e86\u56de\u53bb\uff0c\u6539\u53e3\u95ee\u9053\uff1a\u201c\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6\u4e0d\u662f\u56de\u6765\u4e86\u5417\uff1f \u600e\u4e48\u53c8\u4e0d\u5728\u5bb6\u4e2d\uff1f\u201d\u77f3\u4e08\u7ea8\u9053\uff1a\u201c\u8c01\u8bf4\u4ed6\u56de\u6765\u4e86\uff1f\u201d\u6210\u6d77\u5c71\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u554a\uff01\u201d\u77f3\u6587 \u7ea8\u9053\uff1a\u201c\u4f60\u89c1\u4e86\u9b3c\u5566\uff0c\u6211\u51e0\u65f6\u8bf4\u8fc7\uff1f\u201d\u6210\u6d77\u5c71\u5927\u5947\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u6562\u60c5\u662f\u6211\u542c\u9519\u4e86\uff1f\u90a3\u4e2a\u9e70\u722a \u5b50\u4e5f\u542c\u8bf4\u662f\u4ed6\u8001\u4eba\u5bb6\u56de\u6765\u4e86\uff0c\u8fd9\u624d\u8ffd\u7740\u6211\u6765\u554a\u3002\u201d\u77f3\u6587\u7ea8\u9053\uff1a\u201c\u6211\u7236\u4eb2\u6570\u65e5\u524d\u66fe\u6258\u4eba\u634e\u4e86\u4fe1 \u6765\uff0c\u8bf4\u662f\u4e0d\u65e5\u5c31\u8981\u642d\u6d77\u8239\u56de\u6765\uff0c\u5374\u8fd8\u6ca1\u6709\u6765\u5230\u554a\u3002\u54fc\uff0c\u54fc\uff0c\u90a3\u9e70\u722a\u5b50\u8033\u53e3\u5012\u771f\u7075\uff0c\u6d3b\u8be5\u4ed6\u9001 \u4e0a\u95e8\u6765\u53d7\u8fd9\u4e00\u5251\u3002\u201d\u5ffd\u800c\u60f3\u8d77\u201c\u8fd9\u4e00\u5251\u201d\u4e43\u662f\u4e8e\u627f\u73e0\u523a\u7684\uff0c\u53c8\u4e0d\u8a00\u8bed\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u4e5f\u65e0\u7f18\u62dc\u89c1\u4e86\u3002\u201d\u77f3\u6587\u7ea8\u4e00\u9762\u5b54\u7684\u51b7\u610f\uff0c\u5e76\u4e0d\u56de\u7b54\u3002\u4e8e\u627f\u73e0 \u7ad9\u5728\u5979\u7684\u95e8\u524d\uff0c\u89c1\u5979\u5e76\u4e0d\u9080\u81ea\u5df1\u8fdb\u95e8\u53bb\u5750\uff0c\u60c5\u77e5\u5979\u662f\u6068\u81ea\u5df1\u9002\u624d\u51fa\u62db\u201c\u8f7b\u8584\u201d\uff0c\u5374\u82e6\u4e8e\u65e0\u6cd5 \u5411\u5979\u89e3\u91ca\uff0c\u8baa\u8baa\u5730\u751a\u89c9\u4e0d\u597d\u610f\u601d\uff0c\u505c\u4e86\u4e00\u505c\uff0c\u89c1\u77f3\u6587\u7ea8\u4ecd\u65e0\u8a00\u8bed\uff0c\u53ea\u5f97\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u53e3 \u4fe1\u5df2\u5e26\u5230\u4e86\uff0c\u6ca1\u4ec0\u4e48\u4e8b\uff0c\u6211\u544a\u8f9e\u5566\u3002\u201d\u6210\u6d77\u5c71\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u4eca\u65e5\u62d4\u5251\u76f8\u52a9\u3002\u54b1\u4eec\u94c1\u5e08 \u5144\u7684\u4e8b\uff0c\u6211\u4eec\u65e9\u5df2\u77e5\u9053\u5566\uff0c\u94c1\u5e08\u5144\u7279\u610f\u8ba9\u4f60\u5e26\u53e3\u4fe1\u6765\uff0c\u8ba9\u54b1\u4eec\u8ba4\u8bc6\uff0c\u53ef\u89c1\u94c1\u5e08\u5144\u786e\u662f\u4e0d\u628a\u4f60 \u5f53\u4f5c\u5916\u4eba\u3002\u94c1\u5e08\u5144\u4e4b\u4e8b\uff0c\u81ea\u7136\u9022\u51f6\u5316\u5409\uff0c\u4f60\u653e\u5fc3\u597d\u5566\uff01\u201d\u6210\u6d77\u5c71\u6b64\u8bdd\uff0c\u7279\u610f\u70b9\u660e\u94c1\u955c\u5fc3\u4e0d\u628a \u4e8e\u627f\u73e0\u201c\u5f53\u4f5c\u5916\u4eba\u201d\uff0c\u5176\u5b9e\u662f\u8bf4\u7ed9\u4ed6\u7684\u5e08\u59b9\u542c\u7684\uff0c\u4e8e\u627f\u73e0\u542c\u4e86\uff0c\u5fc3\u4e2d\u5374\u597d\u751f\u5947\u602a\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u4e0d\u7981\u60f3\u9053\uff1a\u201c\u539f\u6765\u94c1\u955c\u5fc3\u7684\u6253\u7b97\u4ed6\u4eec\u65e9\u5df2\u77e5\u9053\u4e86\uff0c\u800c\u4e14\u770b\u6765\u662f\u65e9\u5df2\u6709\u4e86\u5b89\u6392\u3002\u65e2 \u7136\u5982\u6b64\uff0c\u90a3\u4f55\u5fc5\u8fd8\u53eb\u6211\u5e26\u4ec0\u4e48\u53e3\u4fe1\uff1f\u201d\u5979\u5374\u4e0d\u77e5\uff0c\u94c1\u955c\u5fc3\u662f\u56e0\u4e3a\u89c1\u4ed6\u76db\u610f\u62f3\u62f3\uff0c\u597d\u50cf\u82e5\u4e0d\u7ed9 \u4ed6\u4e86\u4e9b\u4e8b\u60c5\u4ee3\u505a\uff0c\u4ed6\u5c31\u4e0d\u5b89\u5fc3\u4f3c\u7684\uff0c\u56e0\u6b64\u7279\u5730\u53eb\u5979\u5230\u767d\u6c99\u6751\u6765\u4f1a\u89c1\u81ea\u5df1\u7684\u5e08\u59b9\uff0c\u5374\u6599\u4e0d\u5230\u4e8e \u627f\u73e0\u80e1\u91cc\u7cca\u6d82\u548c\u4ed6\u7684\u5e08\u59b9\u7ed3\u4e0b\u9699\u6028\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u56de\u5230\u57ce\u4e2d\uff0c\u4e0e\u5f20\u9ed1\u8bf4\u4e86\u8fd9\u4e24\u65e5\u7684\u7ecf\u8fc7\u3002\u5f20\u9ed1\u4e5f\u731c\u4e0d\u900f\u94c1\u955c\u5fc3\u6253\u7684\u662f\u4ec0\u4e48\u7b97\u76d8\uff0c\u544a \u8bc9\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u53f6\u5927\u54e5\u90a3\u8fb9\u5df2\u6709\u4e86\u6d88\u606f\uff0c\u8bf4\u662f\u5927\u540e\u5929\u5c31\u4e00\u51c6\u6709\u4eba\u6765\u4e0e\u54b1\u4eec\u8054\u7edc\uff0c\u53ef\u662f\u5927\u540e\u5929 \u6070\u5de7\u662f\u53f0\u5dde\u77e5\u5e9c\u548c\u65e5\u672c\u4eba\u2018\u4f1a\u5ba1\u2019\u94c1\u955c\u5fc3\u7684\u65e5\u671f\u3002\u201d\u4e8e\u627f\u73e0\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u5f20\u9ed1 \u9053\uff1a\u201c\u5916\u9762\u51fa\u4e86\u544a\u793a\u5566\u3002\u8bb8\u591a\u4eba\u90fd\u8bf4\u8981\u53bb\u770b\u4f1a\u5ba1\u5462\u3002\u201d\u539f\u6765\u8fd9\u516c\u5f00\u4f1a\u5ba1\u4e43\u662f\u94c1\u955c\u5fc3\u529b\u4e89\u5f97\u6765 \u7684\uff0c\u65e5\u672c\u4eba\u81ea\u6043\u52bf\u529b\uff0c\u4e0d\u8651\u6709\u5b83\uff0c\u4e5f\u5c31\u7b54\u5e94\u4e0b\u6765\u4e86\u3002\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5230\u4e86\u90a3\u5929\uff0c\u4f60 \u7559\u5728\u5bb6\u4e2d\u7b49\u5f85\u53f6\u5927\u54e5\u6d3e\u6765\u7684\u4eba\uff0c\u6211\u53bb\u770b\u5ba1\u3002\u201d \u3000\u3000\u4e2d\u56fd\u7684\u77e5\u5e9c\u4f1a\u540c\u65e5\u672c\u7684\u5e02\u8236\u4f7f\u4f1a\u5ba1\u72af\u4eba\uff0c\u800c\u53c8\u51c6\u4eba\u89c2\u5ba1\uff0c\u8fd9\u4e43\u662f\u53f0\u5dde\u4ece\u6765\u6240\u65e0\u7684\u4e8b\uff0c\u7fa4 \u60c5\u6c79\u6d8c\uff0c\u90fd\u5728\u607c\u6068\u65e5\u672c\u5b98\u7684\u51f6\u6a2a\uff0c\u4e0d\u6ee1\u77e5\u5e9c\u7684\u602f\u61e6\uff0c\u8ba9\u5916\u4eba\u5e72\u9884\u53f8\u6cd5\u3002\u8fd9\u4e00\u65e5\u4e00\u5927\u65e9\u5c31\u6709\u65e0 \u6570\u4eba\u6d8c\u5230\u8859\u95e8\uff0c\u4e8e\u627f\u73e0\u4ea6\u6df7\u5728\u5176\u4e2d\u3002\u5348\u65f6\u4e00\u5230\uff0c\u53ea\u89c1\u53f0\u5dde\u7684\u77e5\u5e9c\u4f34\u7740\u4e00\u4e2a\u80a5\u80a5\u77ee\u77ee\u7684\u65e5\u672c\u5b98 \u5347\u5802\uff0c\u4f17\u4eba\u6307\u70b9\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u65e5\u672c\u7684\u5e02\u8236\u4f7f\u9ad8\u6865\u4e86\u3002\u201d\u9ad8\u6865\u5e26\u6709\u4e24\u540d\u6b66\u58eb\u968f\u4f8d\uff0c\u5176\u4e2d\u4e00\u4eba \u4e8e\u627f\u73e0\u8ba4\u5f97\u90a3\u662f\u8d21\u8239\u4e2d\u7684\u4e03\u6bb5\u5251\u5ba2\u6c5f\u53e3\uff0c\u53e6\u4e00\u4e2a\u542c\u65c1\u4eba\u6240\u8bf4\uff0c\u5374\u662f\u65e5\u672c\u9a7b\u5728\u53f0\u5dde\u7684\u6b66\u5b98\u701a \u8d8a\uff0c\u636e\u8bf4\u4e5f\u662f\u4e00\u4f4d\u516d\u6bb5\u7684\u6b66\u58eb\u3002 \u3000\u3000\u77e5\u5e9c\u5347\u5802\uff0c\u88c5\u6a21\u4f5c\u6837\u5730\u4e00\u62cd\u60ca\u5802\u6728\uff0c\u4ece\u7b7e\u7b52\u4e2d\u62bd\u51fa\u4e00\u652f\u7b7e\u4e00\u6454\uff0c\u559d\u9053\uff1a\u201c\u5c06\u72af\u4eba\u5e26 \u4e0a\uff01\u201d\u4e0d\u4e00\u523b\u5dee\u5f79\u5c06\u94c1\u955c\u5fc3\u5e26\u5230\uff0c\u53ea\u89c1\u4ed6\u6602\u7136\u76f4\u7acb\uff0c\u53cc\u76ee\u70af\u70af\uff0c\u76ef\u7740\u90a3\u4e2a\u65e5\u672c\u5b98\uff0c\u6b63\u6c14\u51db \u7136\uff0c\u6beb\u65e0\u60e7\u8272\u3002\u9ad8\u6865\u7ed9\u4ed6\u77aa\u5f97\u53cd\u800c\u6709\u4e9b\u602f\u610f\uff0c\u62cd\u6848\u559d\u9053\uff1a\u201c\u597d\u5927\u80c6\u7684\u652f\u90a3\u72af\u4eba\uff0c\u4f60\u77e5\u7f6a \u5417\uff1f\u201d\u4ed6\u8fd9\u8bdd\u662f\u7528\u65e5\u8bed\u8bf4\u7684\uff0c\u81ea\u6709\u901a\u8bd1\u8bd1\u6210\u6c49\u8bed\uff0c\u94c1\u955c\u5fc3\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\uff01\u201d\u9ad8\u6865\u9053\uff1a \u201c\u4f60\u6740\u4eba\u8d8a\u8d27\uff0c\u6253\u6b7b\u4e86\u6211\u4eec\u65e5\u672c\u7684\u8239\u4e3b\uff0c\u62a2\u4e86\u6211\u4eec\u65e5\u672c\u8239\u7684\u8d27\u7269\uff0c\u8fd8\u80c6\u6562\u626f\u4e0b\u6211\u4eec\u5927\u65e5\u672c\u7684 \u592a\u9633\u65d7\uff0c\u7f6a\u8bc1\u786e\u51ff\uff0c\u5f53\u53d7\u6781\u5211\u3002\u652f\u90a3\u7684\u77e5\u5e9c\u5b98\u513f\uff0c\u6211\u8bf4\u8fd9\u4e0d\u5fc5\u5ba1\u5566\uff0c\u5c31\u7531\u9881\u8d8a\u5927\u4f50\u76d1\u65a9\u4e86 \u5427\u3002\u201d\u540e\u9762\u534a\u6bb5\u662f\u9762\u5411\u77e5\u5e9c\u8bf4\u7684\uff0c\u4e00\u526f\u9a84\u6a2a\u4e4b\u6c14\uff0c\u5484\u5484\u903c\u4eba\uff01 \u3000\u3000\u94c1\u955c\u5fc3\u4e00\u58f0\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u7684\u8239\u957f\u5148\u6253\u6b7b\u4e86\u6211\u4eec\u7684\u4e2d\u56fd\u4eba\uff0c\u62a2\u4e86\u4ed6\u7684\u8d27\u7269\uff0c\u53e6\u5916\u8fd8 \u4f24\u4e86\u5341\u591a\u4e2a\u4eba\uff0c\u6211\u8def\u89c1\u4e0d\u5e73\uff0c\u5373\u7b97\u6253\u6b7b\u4f60\u4eec\u7684\u8239\u957f\uff0c\u4e5f\u53ea\u662f\u4e00\u547d\u8d54\u4e00\u547d\u3002\u6211\u4eec\u62a2\u56de\u6765\u7684\u662f\u4e2d \u56fd\u8239\u81ea\u5df1\u7684\u8d27\u7269\uff0c\u4f60\u4eec\u7684\u8239\u5f53\u65e5\u5c31\u6e9c\u8d70\u4e86\uff0c\u54ea\u66fe\u6709\u4ec0\u4e48\u635f\u5931\uff1f\u201d\u9ad8\u6865\u52c3\u7136\u5927\u6012\uff0c\u9762\u5411\u53f0\u5dde\u77e5 \u5e9c\u65a5\u9053\uff1a\u201c\u8d35\u77e5\u5e9c\u5c82\u53ef\u5bb9\u72af\u4eba\u5486\u54ee\u516c\u5802\uff0c\u7ed9\u6211\u62ff\u4e0b\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u57ce\u4e2d\u7a76\u662f\u8c01\u5929\u4e0b\uff1f\u5486\u54ee\u516c\u5802\u502d\u7130\u5f20\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u5341\u4e8c\u56de\u3000\u8349\u83bd\u82f1\u8c6a\u3000\u6325\u6208\u540c\u6297\u65e5\u3000\u7389\u5802\u516c\u5b50\u3000\u5212\u7b56\u6258\u7a7a\u8a00 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u5341\u4e8c\u56de\u3000\u8349\u83bd\u82f1\u8c6a\u3000\u6325\u6208\u540c\u6297\u65e5\u3000\u7389\u5802\u516c\u5b50\u3000\u5212\u7b56\u6258\u7a7a\u8a00 \u3000\u3000\u53f0\u5dde\u77e5\u5e9c\u5413\u5f97\u9762\u9752\u5507\u767d\uff0c\u6296\u6296\u7d22\u7d22\u3002\u88ab\u94c1\u955c\u5fc3\u6012\u76ee\u4e00\u77aa\uff0c\u6293\u7740\u4e00\u652f\u7af9\u7b7e\u5374\u53c8\u4e0d\u6562\u6454\u4e0b\uff0c \u53ea\u542c\u5f97\u94c1\u955c\u5fc3\u5927\u58f0\u559d\u9053\uff1a\u201c\u516c\u5802\u4e4b\u4e0a\uff0c\u8bb2\u7684\u662f\u9053\u7406\uff0c\u9053\u7406\u672a\u8bb2\u6e05\u695a\uff0c\u8c01\u6562\u80fd\u6765\u62ff\u6211\uff1f\u201d\u89c2\u5ba1 \u7684\u4e2d\u56fd\u4eba\u867d\u7136\u4e45\u5904\u502d\u5bc7\u7684\u538b\u529b\u4e4b\u4e0b\uff0c\u4e5f\u7981\u4e0d\u4f4f\u559d\u5f69\u4e3a\u94c1\u955c\u5fc3\u52a9\u5a01\u3002\u9ad8\u6865\u6c14\u5f97\u9762\u8272\u94c1\u9752\uff0c\u559d \u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\u6211\u4eec\u5927\u65e5\u672c\u7684\u8239\u4e3b\u6253\u6b7b\u4f60\u4eec\u7684\u652f\u90a3\u4eba\uff0c\u6709\u4f55\u51ed\u8bc1\uff1f\u518d\u8bf4\u4f60\u4e3a\u4ec0\u4e48\u6495\u4e0b\u6211\u4eec\u5927 \u65e5\u672c\u7684\u592a\u9633\u65d7\uff1f\u201d \u3000\u3000\u94c1\u955c\u5fc3\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u65e5\u672c\u8239\u5230\u4e2d\u56fd\u6765\uff0c\u5c31\u8be5\u5b88\u4e2d\u56fd\u7684\u6cd5\u5f8b\uff0c\u90a3\u6761\u8239\u65e2\u7136\u6740\u4eba\u62a2\u52ab\uff0c\u53c8\u5077 \u8fd0\u79c1\u8d27\uff0c\u6211\u4eec\u5c31\u53ea\u5f53\u5b83\u662f\u6d77\u76d7\u8239\u53ea\uff0c\u6599\u60f3\u4f60\u4eec\u8d35\u56fd\u4e5f\u4e0d\u4f1a\u627f\u8ba4\u8fd9\u79cd\u6d77\u76d7\u7684\u8239\u53ea\u662f\u4f60\u4eec\u653f\u5e9c \u7684\u3002\u65e2\u7136\u662f\u6d77\u76d7\u7684\u8239\u53ea\uff0c\u6302\u8d77\u65e5\u672c\u65d7\uff0c\u5176\u5b9e\u5c31\u662f\u4fae\u8fb1\u4f60\u4eec\u81ea\u5df1\u7684\u56fd\u5bb6\u3002\u6211\u66ff\u4f60\u4eec\u5c06\u6d77\u76d7\u8239\u4e0a \u7684\u592a\u9633\u65d7\u9664\u4e0b\uff0c\u5176\u5b9e\u662f\u4e3a\u4f60\u4eec\u4fdd\u5168\u4e86\u56fd\u5bb6\u7684\u4f53\u9762\u3002\u8bf4\u6765\u4f60\u8fd8\u8be5\u611f\u6fc0\u6211\uff01\u201d\u94c1\u955c\u5fc3\u7406\u76f4\u6c14\u58ee\uff0c \u4f83\u4f83\u9053\u6765\uff0c\u628a\u9ad8\u6865\u6c14\u5f97\u8fde\u8fde\u62cd\u6848\u9a82\u9053\uff1a\u201c\u5f3a\u8fa9\uff0c\u5f3a\u8fa9\uff01\u201d \u3000\u3000\u94c1\u955c\u5fc3\u4e0d\u4e88\u7406\u4f1a\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u81f3\u4e8e\u8bf4\u5230\u8bc1\u636e\u5417\uff1f\u90a3\u6709\u7684\u662f\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u53ea\u89c1\u4e00\u4e2a\u62ab \u5934\u6563\u53d1\u7684\u5973\u4eba\uff0c\u54ed\u54ed\u557c\u557c\u5730\u8d70\u4e0a\u5802\u6765\uff0c\u54ed\u9053\uff1a\u201c\u6c42\u9752\u5929\u5927\u8001\u7237\u4f5c\u4e3b\u554a\uff0c\u6211\u7684\u4e08\u592b\u7ed9\u65e5\u672c\u4eba\u6253 \u6b7b\uff0c\u6211\u4e5f\u7ed9\u6253\u4f24\uff0c\u8d27\u7269\u88ab\u62a2\uff0c\u8ffd\u56de\u6765\u7684\u8fd8\u4e0d\u5230\u4e00\u534a\u5566\uff01\u201d\u6b63\u662f\u90a3\u6761\u88ab\u62a2\u63a0\u7684\u4e2d\u56fd\u8d27\u8239\u8239\u4e3b\u7684 \u672a\u4ea1\u4eba\u3002\u7d27\u8ddf\u7740\u4e00\u7247\u54ed\u58f0\uff0c\u53ea\u89c1\u6570\u5341\u4eba\u62e5\u4e0a\u5802\u6765\uff0c\u6bcf\u4e24\u4e2a\u4eba\u62ac\u7740\u4e00\u5f20\u5e8a\u677f\uff0c\u5e8a\u677f\u4e0a\u90fd\u8eba\u7740\u4e00 \u4e2a\u53d7\u4f24\u7684\u4eba\uff0c\u6709\u7684\u65ad\u624b\uff0c\u6709\u7684\u6298\u8db3\uff0c\u6709\u7684\u4f24\u53e3\u8fd8\u5728\u6d41\u8840\uff0c\u90fd\u662f\u90a3\u65e5\u88ab\u65e5\u672c\u8239\u4e0a\u6d6a\u4eba\u6253\u4f24\u7684\u4e2d \u56fd\u4eba\u3002\u94c1\u955c\u5fc3\u53eb\u9053\uff1a\u201c\u8fd9\u4e9b\u90fd\u662f\u82e6\u4e3b\uff0c\u4f60\u8fd8\u6709\u4f55\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u9ad8\u6865\u7edd\u5bf9\u6599\u60f3\u4e0d\u5230\u8fd9\u4e9b\u201c\u652f\u90a3\u82e6\u4e3b\u201d\u5c45\u7136\u6562\u51fa\u6765\u6307\u8bc1\uff0c\u7741\u5927\u773c\u775b\uff0c\u6b63\u8981\u53d1\u4f5c\uff0c\u53ea\u542c\u5f97\u516c \u5802\u4e0a\u54ed\u58f0\u56db\u8d77\uff0c\u63a5\u7740\u4e00\u7fa4\u4e00\u7fa4\u7684\u4eba\u51fa\u6765\u63a7\u544a\uff0c\u6709\u767d\u53d1\u82cd\u82cd\u7684\u8001\u5988\u5988\u51fa\u6765\u6307\u8d23\u502d\u5bc7\u6740\u4e86\u5979\u7684\u513f \u5b50\uff0c\u6709\u6ee1\u8154\u773c\u6cea\u7684\u5c11\u5987\uff0c\u54ed\u8bc9\u502d\u5bc7\u6740\u4e86\u5979\u7684\u4e08\u592b\uff0c\u6709\u4e00\u4e2a\u8001\u7237\u7237\u66f4\u4e0d\u987e\u6027\u547d\u5730\u51b2\u5230\u516c\u6848\u524d \u9762\uff0c\u63a7\u8bc9\u502d\u5bc7\u6740\u4e86\u4ed6\u7684\u513f\u5b50\uff0c\u62a2\u4e86\u4ed6\u7684\u95fa\u5973\uff0c\u8fd8\u653e\u706b\u70e7\u4e86\u4ed6\u7684\u623f\u5c4b\u3002 \u3000\u3000\u9ad8\u6865\u6c14\u5f97\u53cc\u773c\u51f8\u51fa\uff0c\u5fc3\u4e2d\u53c8\u662f\u5341\u5206\u5bb3\u6015\uff0c\u4ed6\u54ea\u60f3\u5f97\u5230\u4ed6\u4e00\u5411\u8ba4\u4e3a\u662f\u201c\u7ef5\u7f8a\u201d\u4e00\u822c\u7684\u201c\u652f \u90a3\u4eba\u201d\uff0c\u5ffd\u7136\u4f1a\u50cf\u706b\u5c71\u4e00\u6837\u5730\u7206\u53d1\u8d77\u6765\uff0c\u63a7\u8bc9\u4ed6\u7684\u201c\u5927\u548c\u6c11\u65cf\u7684\u4f18\u79c0\u56fd\u6c11\u201d\uff1f\u9ad8\u6865\u5927\u559d\u4e00 \u58f0\uff1a\u201c\u7ed9\u6211\u6253\u53d1\u8fd9\u7fa4\u652f\u90a3\u4eba\uff01\u201d\u701a\u8d8a\u6a2a\u86ee\u5df2\u60ef\uff0c\u5e94\u58f0\u8df3\u4e0b\u516c\u5802\uff0c\u556a\u5730\u4e00\u638c\uff0c\u5c31\u5c06\u90a3\u4e2a\u8001\u5927\u7237 \u6253\u7ffb\uff0c\u8fd8\u60f3\u52a8\u624b\u518d\u6253\u4e00\u4e2a\u8001\u5988\u5988\uff0c\u53e6\u4e00\u4e2a\u4e03\u6bb5\u6b66\u58eb\u6c5f\u53e3\u5219\u62d4\u51fa\u957f\u5251\u53bb\u523a\u94c1\u955c\u5fc3\u3002 \u3000\u3000\u53ea\u89c1\u94c1\u955c\u5fc3\u8eab\u5f62\u4e00\u6643\uff0c\u6c5f\u53e3\u7684\u957f\u5251\u523a\u4e86\u4e2a\u7a7a\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u94c1\u955c\u5fc3\u4e00\u4e2a\u864e\u6b65\uff0c\u4e00\u6251 \u800c\u524d\uff0c\u53cc\u638c\u4e00\u843d\uff0c\u7acb\u523b\u6293\u7740\u701a\u8d8a\u7684\u80cc\u5fc3\uff0c\u6551\u4e86\u90a3\u8001\u5988\u5988\u7684\u4e00\u547d\u3002 \u3000\u3000\u701a\u8d8a\u7cbe\u4e8e\u67d4\u672f\uff0c\u88ab\u94c1\u955c\u5fc3\u6293\u8d77\uff0c\u5c45\u7136\u8d25\u4e2d\u53cd\u5916\uff0c\u8111\u888b\u4e00\u4ef0\uff0c\u53cc\u624b\u53cd\u7a7f\u4e0b\u6765\uff0c\u626d\u94c1\u955c\u5fc3\u81c2 \u5f2f\u5173\u8282\uff0c\u94c1\u955c\u5fc3\u8170\u8eab\u4e00\u4fef\uff0c\u5ffd\u5730\u53ea\u89c1\u4e24\u4eba\u7684\u8eab\u5f62\u7a81\u4f3c\u98ce\u8f66\u4e00\u8f6c\uff0c\u4e3b\u5ba2\u6613\u52bf\uff0c\u94c1\u955c\u5fc3\u53cd\u800c\u88ab\u701a \u8d8a\u80cc\u5230\u80cc\u4e0a\uff0c\u770b\u770b\u5c31\u8981\u88ab\u4ed6\u201c\u80cc\u6295\u201d\u7edd\u6280\uff0c\u6295\u4e0b\u77f3\u9636\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u60ca\u53eb\u4e00\u58f0\uff0c\u8d8a\u51fa\u4eba\u4e1b\uff0c\u5c31\u60f3\u6765\u6551\u3002\u53e6\u4e00\u4e2a\u4e03\u6bb5\u6b66\u58eb\u6c5f\u53e3\u89c1\u94c1\u955c\u5fc3\u88ab\u4ed6\u7684\u540c\u4f34\u5236 \u7740\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u539f\u6765\u4f60\u4e5f\u6709\u8d25\u5728\u6211\u4eec\u65e5\u672c\u6b66\u58eb\u624b\u4e2d\u4e4b\u65e5\u3002\u201d\u957f\u5251\u4e00 \u6325\uff0c\u567c\u556a\u4f5c\u54cd\uff01\u7acb\u523b\u5411\u94c1\u955c\u5fc3\u5934\u9885\u65a9\u4e0b\u3002\u4ed6\u5728\u8fd1\uff0c\u4e8e\u627f\u73e0\u5728\u8fdc\uff0c\u4e8e\u627f\u73e0\u8981\u6551\u4ed6\u4e5f\u6765\u4e0d\u53ca\u3002 \u3000\u3000\u4f17\u4eba\u60ca\u53eb\u58f0\u4e2d\uff0c\u5ffd\u89c1\u701a\u8d8a\u811a\u6b65\u8dc4\u8e09\uff0c\u5411\u524d\u4e00\u51b2\uff0c\u6070\u6070\u8fce\u7740\u4e86\u6c5f\u53e3\u7684\u957f\u5251\uff0c\u201c\u6ce2\u201d\u7684\u4e00 \u58f0\uff0c\u957f\u5251\u523a\u5165\u4e86\u701a\u8d8a\u7684\u9600\u9aa8\uff0c\u94c1\u955c\u5fc3\u54c8\u54c8\u5927\u7b11\uff0c\u4e00\u8dc3\u800c\u4e0b\uff0c\u4fe1\u624b\u6253\u4e86\u6c5f\u53e3\u4e24\u8bb0\u8033\u5149\uff0c\u559d\u9053\uff1a \u201c\u4f60\u5728\u4e2d\u56fd\u516c\u5802\u4e4b\u4e0a\uff0c\u6043\u5f3a\u884c\u51f6\uff0c\u76ee\u4e2d\u8fd8\u6709\u6211\u5929\u671d\u7687\u6cd5\u5417\uff1f\u201d\u8fd9\u4e00\u4e0b\u53d8\u51fa\u610f\u5916\uff0c\u6c5f\u53e3\u7edd\u5bf9\u6599 \u60f3\u4e0d\u5230\uff0c\u7a7a\u6709\u4e00\u8eab\u6b66\u827a\uff0c\u957f\u5251\u523a\u5165\u540c\u4f34\u7684\u8eab\u4f53\uff0c\u6025\u5fd9\u95f4\u672a\u80fd\u62d4\u51fa\uff0c\u773c\u89c1\u94c1\u955c\u5fc3\u5df4\u638c\u6253\u6765\uff0c\u7adf \u662f\u6beb\u65e0\u529e\u6cd5\u62b5\u6321\u3002 \u3000\u3000\u539f\u6765\u94c1\u955c\u5fc3\u662f\u5c06\u8ba1\u5c31\u8ba1\uff0c\u6545\u610f\u8ba9\u701a\u8d8a\u5f97\u624b\uff0c\u5c06\u4ed6\u53cd\u80cc\u8d77\u6765\uff0c\u4ed6\u5374\u7528\u64d2\u62ff\u624b\u6263\u7740\u4e86\u701a\u8d8a\u7684 \u80cc\u5fc3\u201c\u5929\u67f1\u201d\u5927\u7a74\uff0c\u201c\u5929\u67f1\u7a74\u201d\u4f4d\u5728\u810a\u690e\u7684\u795e\u7ecf\u672a\u68a2\uff0c\u611f\u89c9\u6700\u4e3a\u7075\u654f\uff0c\u88ab\u94c1\u955c\u5fc3\u7528\u529b\u4e00\u6263\uff0c \u53c8\u9ebb\u53c8\u75d2\u53c8\u75db\uff0c\u701a\u8d8a\u7684\u67d4\u672f\u975e\u4f46\u4e1d\u6beb\u65bd\u5c55\u4e0d\u51fa\uff0c\u800c\u4e14\u7ed9\u94c1\u955c\u5fc3\u5f04\u5f97\u5982\u53d1\u72c2\u766b\uff0c\u5411\u524d\u4e71\u51b2\uff0c\u8fd9 \u4e00\u51b2\u5c31\u6070\u6070\u51b2\u5230\u4e86\u6c5f\u53e3\u7684\u5251\u4e0a\u3002 \u3000\u3000\u6c5f\u53e3\u88ab\u6253\u4e86\u4e24\u8bb0\u8033\u5149\uff0c\u8fd9\u624d\u5c06\u5251\u62d4\u51fa\uff0c\u53ea\u542c\u5f97\u701a\u8d8a\u60e8\u53eb\u4e00\u58f0\uff0c\u8840\u5982\u6cc9\u6d8c\uff0c\u773c\u89c1\u4ed6\u4e0d\u6b7b\u4ea6 \u6210\u6b8b\u5e9f\uff0c\u6c5f\u53e3\u53c8\u60ca\u53c8\u6012\uff0c\u957f\u5251\u4e00\u5708\uff0c\u731b\u65bd\u6740\u624b\uff0c\u7a81\u7136\u95f4\u53c8\u4e0d\u89c1\u4e86\u94c1\u955c\u5fc3\u7684\u5f71\u5b50\uff0c\u6c5f\u53e3\u6697\u53eb\u4e00 \u58f0\u201c\u4e0d\u597d\u201d\uff0c\u8df3\u8d77\u6765\u65f6\uff0c\u624b\u8155\u5df2\u7ed9\u94c1\u955c\u5fc3\u6293\u4f4f\uff0c\u8f7b\u8f7b\u4e00\u62d7\uff0c\u767b\u65f6\u8131\u81fc\uff0c\u957f\u5251\u5f53\u7684\u4e00\u58f0\u8dcc\u843d\u5730 \u4e0a\u3002\u672c\u6765\u4ee5\u6c5f\u53e3\u4e03\u6bb5\u6b66\u58eb\u7684\u672c\u4e8b\uff0c\u94c1\u955c\u5fc3\u7eb5\u80fd\u5c06\u4ed6\u6253\u8d25\uff0c\u4e5f\u5f97\u82b1\u534a\u4e2a\u65f6\u8fb0\uff0c\u4f46\u94c1\u955c\u5fc3\u673a\u667a\u767e \u51fa\uff0c\u5148\u7528\u701a\u8d8a\u4f5c\u4e3a\u76fe\u724c\uff0c\u53eb\u4ed6\u5403\u4e86\u5927\u4e8f\uff0c\u5f85\u4ed6\u62d4\u5251\u4e4b\u65f6\uff0c\u94c1\u955c\u5fc3\u5df2\u7ed5\u5230\u4ed6\u7684\u8eab\u540e\uff0c\u8bba\u8d77\u8eab\u6cd5 \u7684\u8f7b\u7075\uff0c\u6c5f\u53e3\u7edd\u4e0d\u80fd\u4e0e\u94c1\u955c\u5fc3\u76f8\u6bd4\uff0c\u66f4\u4f55\u51b5\u88ab\u94c1\u955c\u5fc3\u4e00\u51fa\u624b\u5c31\u5236\u4e86\u5148\u673a\uff0c\u81ea\u7136\u5c31\u53ea\u6709\u6328\u6253\u7684 \u4efd\u513f\u4e86\u3002\u94c1\u955c\u5fc3\u811a\u5c16\u4e00\u6311\uff0c\u628a\u6c5f\u53e3\u7684\u957f\u5251\u6311\u8d77\uff0c\u63a5\u5230\u624b\u4e2d\uff0c\u7528\u62c7\u6307\u4e00\u9876\u5251\u8eab\uff0c\u5355\u624b\u4e00\u6296\uff0c\u548b \u568e\u4e00\u58f0\uff0c\u90a3\u67c4\u957f\u5251\u65ad\u4e3a\u4e24\u6bb5\uff0c\u6c5f\u53e3\u722c\u4e86\u8d77\u6765\uff0c\u89c1\u4ed6\u663e\u4e86\u8fd9\u624b\u529f\u592b\uff0c\u54ea\u6562\u518d\u6597\uff0c\u94c1\u955c\u5fc3\u5c06\u4e24\u622a \u65ad\u5251\u4e00\u629b\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u502d\u5974\u65e0\u793c\uff0c\u80c6\u6562\u5728\u77e5\u5e9c\u8859\u95e8\uff0c\u62ff\u5200\u5f04\u5251\uff0c\u6253\u4eba\u4f24\u4eba\uff0c\u4f17\u76ee\u5171\u89c1\uff0c\u6c42 \u77e5\u5e9c\u5927\u4eba\u5904\u7f6e\u3002\u201d\u77e5\u5e9c\u65e9\u5df2\u5413\u5f97\u54c6\u54c6\u55e6\u55e6\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u731b\u542c\u5f97\u9ad8\u6865\u62cd\u6848\u5927\u9a82\u9053\uff1a\u201c\u53cd\u4e86\uff0c\u53cd \u4e86\u3002\u201d\u7a81\u7136\u4ece\u8859\u95e8\u540e\u9762\u6d8c\u51fa\u4e00\u961f\u65e5\u672c\u5175\uff0c\u4e2a\u4e2a\u62ff\u7740\u96ea\u767d\u7684\u957f\u67c4\u502d\u5200\uff0c\u53d1\u4e00\u58f0\u558a\uff0c\u90fd\u6251\u5411\u94c1\u955c \u5fc3\u3002 \u3000\u3000\u90a3\u662f\u9ad8\u6865\u65e9\u5c31\u5e26\u6765\u4e86\u7684\u62a4\u536b\uff0c\u53ea\u56e0\u4e0d\u4fbf\u516c\u5f00\u9732\u9762\uff0c\u6545\u6b64\u7406\u4f0f\u5728\u77e5\u5e9c\u540e\u8859\uff0c\u800c\u4eca\u542c\u5f97\u5802\u4e0a \u5927\u4e71\uff0c\u88ab\u4ed6\u4eec\u6b3a\u4fae\u60ef\u4e86\u7684\u201c\u652f\u90a3\u4eba\u201d\u5c45\u7136\u6562\u95f9\u8d77\u4e8b\u6765\uff0c\u8fd9\u4e9b\u65e5\u672c\u5175\u6a2a\u884c\u5df2\u60ef\uff0c\u542c\u5f97\u9ad8\u6865\u5728\u5916 \u9762\u547c\u559d\uff0c\u54ea\u91cc\u8fd8\u4f1a\u60f3\u5230\u4ec0\u4e48\u540e\u679c\uff0c\u4e8e\u662f\u4e2a\u4e2a\u62d4\u51fa\u502d\u5200\uff0c\u4e89\u7740\u6d8c\u51fa\u3002 \u3000\u3000\u5927\u5802\u4e0a\u672c\u6765\u5c31\u6324\u6ee1\u4e86\u89c2\u5ba1\u7684\u4e2d\u56fd\u4eba\uff0c\u4e00\u76f4\u6392\u5230\u77f3\u9636\u5e95\u4e0b\uff0c\u5c11\u8bf4\u4e5f\u6709\u4e03\u516b\u767e\u4eba\uff0c\u672c\u6765\u5c31\u662f \u5df2\u6124\u60eb\u4e0d\u582a\uff0c\u8fd9\u65f6\u7a81\u89c1\u65e5\u672c\u5175\u6740\u51fa\uff0c\u66f4\u662f\u7fa4\u60c5\u6c79\u6d8c\uff0c\u6709\u8bb8\u591a\u5c11\u5e74\u4eba\u594b\u4e0d\u987e\u8eab\uff0c\u8d64\u624b\u7a7a\u62f3\u5c31\u5954 \u4e0a\u53bb\u8fce\u654c\uff0c\u502d\u5200\u950b\u5229\u5f02\u5e38\uff0c\u7a0d\u4e00\u78b0\u4e0a\u5c31\u6709\u76ae\u7834\u8840\u6d41\u4e4b\u7978\uff0c\u94c1\u955c\u5fc3\u62e6\u5728\u524d\u9762\uff0c\u547c\u547c\u53d1\u638c\uff0c\u7528\u5927 \u6454\u7891\u624b\u7684\u91cd\u624b\u6cd5\uff0c\u4e00\u8fde\u6454\u6b7b\u4e86\u4e94\u516d\u4e2a\u9ad8\u6865\u7684\u536b\u58eb\uff0c\u4f46\u90a3\u961f\u65e5\u672c\u5175\u6709\u4e09\u5341\u591a\u4eba\uff0c\u94c1\u955c\u5fc3\u4e00\u4eba\u81ea \u662f\u963b\u6321\u4e0d\u4f4f\uff0c\u6d8c\u4e0a\u53bb\u7684\u5c11\u5e74\u4eba\u4ecd\u6709\u591a\u4eba\u53d7\u4f24\uff0c\u6709\u4e00\u4e2a\u4f24\u5f97\u6700\u60e8\u7684\uff0c\u7adf\u88ab\u7948\u65ad\u4e86\u4e00\u6761\u624b\u81c2\u3002 \u3000\u3000\u5ffd\u5730\u53ea\u542c\u5f97\u94ee\u6323\u4e4b\u58f0\u8fde\u54cd\uff0c\u4e8e\u627f\u73e0\u4e00\u626c\u624b\u5c31\u662f\u4e94\u6735\u91d1\u82b1\uff0c\u9664\u4e86\u4e00\u4e2a\u65e5\u672c\u6b66\u58eb\u80fd\u591f\u907f\u5f00\u4e4b \u5916\uff0c\u5176\u4f59\u56db\u6735\u91d1\u82b1\u5168\u90fd\u547d\u4e2d\u4e86\u654c\u4eba\u7684\u8981\u7a74\uff0c\u767b\u65f6\u6709\u56db\u4e2a\u65e5\u672c\u536b\u571f\u6251\u5730\u4e0d\u8d77\u3002\u4e8e\u627f\u73e0\u968f\u8eab\u6240\u643a \u5e26\u7684\u91d1\u82b1\u6697\u5668\u6709\u9650\uff0c\u6253\u4f24\u4e86\u56db\u4e2a\u65e5\u672c\u536b\u58eb\u4e4b\u540e\uff0c\u7acb\u523b\u62d4\u51fa\u5b9d\u5251\uff0c\u6b63\u5f85\u8d8a\u4f17\u800c\u51fa\uff0c\u51e0\u4e4e\u5c31\u5728\u540c \u4e00\u65f6\u95f4\uff0c\u53ea\u89c1\u4e1c\u9762\u95e8\u9996\u62e5\u6324\u7740\u7684\u4eba\u7fa4\u53d1\u4e00\u58f0\u558a\uff0c\u4e24\u8fb9\u4e00\u5206\uff0c\u4e00\u4e2a\u7ea2\u8863\u5c11\u5973\u624b\u6325\u5229\u5251\uff0c\u6740\u4e86\u8fdb \u6765\uff0c\u540e\u9762\u8ddf\u7740\u4e00\u5927\u7fa4\u6e14\u6c11\u6253\u626e\u7684\u4eba\uff0c\u6216\u6301\u9c7c\u53c9\uff0c\u6216\u6301\u9c7c\u94a9\uff0c\u884c\u52a8\u77eb\u6377\u4e4b\u6781\uff0c\u6bcf\u4e24\u4eba\u4e00\u4e2a\u5c0f \u7ec4\uff0c\u4e00\u4eba\u7528\u9c7c\u53c9\u8feb\u4f4f\u502d\u5200\uff0c\u53e6\u4e00\u4eba\u5c31\u7528\u9c7c\u94a9\u52fe\u654c\u4eba\u7684\u53cc\u8db3\uff0c\u65e5\u672c\u4eba\u4e60\u60ef\u7eb5\u819d\u76d8\u5730\u800c\u5750\uff0c\u817f\u80a5 \u811a\u77ed\uff0c\u8df3\u8dc3\u4e0d\u7075\uff0c\u90a3\u7fa4\u6e14\u6c11\u4f3c\u662f\u4e45\u7ecf\u8bad\u7ec3\uff0c\u9c7c\u94a9\u52fe\u4e0b\uff0c\u4ece\u4e0d\u843d\u7a7a\uff0c\u7247\u523b\u65f6\u95f4\uff0c\u5c31\u628a\u90a3\u961f\u9ad8\u6865 \u7684\u536b\u58eb\u5168\u90e8\u64d2\u4e86\u3002\u5176\u4e2d\u4e00\u4e2a\u672c\u9886\u8f83\u9ad8\u7684\u6b66\u58eb\uff0c\u662f\u8fd9\u961f\u65e5\u5175\u7684\u961f\u957f\uff0c\u4e5f\u4e0d\u8fc7\u51e0\u4e2a\u7167\u9762\uff0c\u5c31\u88ab\u90a3 \u7ea2\u8863\u5c11\u5973\u524a\u65ad\u4e86\u4e00\u6761\u81c2\u818a\uff0c\u4e00\u5e76\u64d2\u4e86\u3002 \u3000\u3000\u8fd9\u7ea2\u8863\u5c11\u5973\u6b63\u662f\u4e8e\u627f\u73e0\u6628\u65e5\u6240\u89c1\u7684\u90a3\u4e2a\u77f3\u6587\u7ea8\u3002\u4e8e\u627f\u73e0\u604d\u7136\u5927\u609f\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6210\u6d77 \u5c71\u53eb\u6211\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u539f\u6765\u4ed6\u4eec\u662f\u65e9\u6709\u51c6\u5907\u7684\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e00\u4ed7\u9ad8\u6865\u5e26\u6765\u7684\u4eba\u5168\u519b\u8986\u6ca1\uff0c\u9ad8\u6865\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u6025\u6b32\u9003\u8d70\uff0c\u53cc\u811a\u5374\u4e0d\u542c\u4f7f\u5524\uff0c\u5728\u516c \u5802\u4e0a\u6296\u4e2a\u4e0d\u505c\uff0c\u88ab\u94c1\u955c\u5fc3\u62d6\u4e86\u4e0b\u6765\uff0c\u53cd\u624b\u7f1a\u4f4f\uff0c\u63a8\u5230\u77e5\u5e9c\u7684\u9762\u524d\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u502d\u5974\u8511\u89c6\u6211 \u5929\u671d\u7687\u6cd5\uff0c\u5728\u516c\u5802\u4e0a\u7eb5\u5175\u884c\u51f6\uff0c\u77e5\u5e9c\u5927\u4eba\uff0c\u4f60\u5b88\u571f\u6709\u8d23\uff0c\u4e0d\u80fd\u4e0d\u7406\u3002\u201d\u77e5\u5e9c\u4e5f\u5413\u5f97\u51e0\u4e4e\u8bf4\u4e0d \u51fa\u8bdd\u6765\uff0c\u900f\u4e86\u4e00\u5927\u53e3\u6c14\uff0c\u534a\u664c\u624d\u55eb\u5685\u8bf4\u9053\uff1a\u201c\u8fd9\uff0c\u8fd9\uff0c\u8fd9\u5982\u4f55\u662f\u597d\uff0c\u82e5\u502d\u5bc7\u56f4\u57ce\uff0c\u672c\u5e9c\u5175\u529b \u5355\u8584\uff0c\u5982\u4f55\u62b5\u6321\uff1f\u201d\u94c1\u955c\u5fc3\u7b11\u9053\uff1a\u201c\u6709\u8fd9\u4e48\u591a\u4eba\uff0c\u8fd8\u6101\u6ca1\u4eba\u62b5\u6321\uff01\u201d\u516c\u5802\u4e0a\u8fd9\u65f6\u5df2\u6324\u5f97\u6c34\u6cc4 \u4e0d\u901a\uff0c\u4f17\u53e3\u540c\u58f0\u5730\u53eb\u9053\uff1a\u201c\u6211\u4eec\u62b5\u6321\u3002\u201d\u8fd8\u6709\u4eba\u53eb\u9053\uff1a\u201c\u82e5\u7136\u77e5\u5e9c\u5927\u4eba\u60e7\u6015\u502d\u5bc7\uff0c\u90a3\u5c31\u5feb\u5feb \u9003\u547d\uff0c\u53f0\u5dde\u4e4b\u4e8b\uff0c\u6211\u4eec\u81ea\u7406\u3002\u201d\u77e5\u5e9c\u89c1\u6c11\u6c14\u5982\u6b64\uff0c\u6015\u518d\u5bf9\u65e5\u672c\u4eba\u5fcd\u8ba9\u4e4b\u65f6\u4f1a\u6fc0\u8d77\u6c11\u6124\uff0c\u53ea\u5f97 \u8bf4\u9053\uff1a\u201c\u94c1\u76f8\u516c\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u6211\u53ea\u597d\u7531\u4f60\u4f5c\u4e3b\u4e86\u3002\u201d \u3000\u3000\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u4fdd\u571f\u536b\u6c11\uff0c\u4eba\u4eba\u6709\u8d23\u3002\u5927\u4eba\u662f\u53f0\u5dde\u7684\u7236\u6bcd\u5b98\uff0c\u90a3\u66f4\u662f\u8d23\u65e0\u65c1\u8d37\u7684\u4e86\u3002\u201d\u5f53 \u4e0b\u7acb\u5373\u63a8\u51fa\u4e86\u51e0\u4f4d\u4e61\u7ec5\u548c\u5730\u65b9\u4e0a\u7684\u516c\u6b63\u4eba\u58eb\uff0c\u548c\u77e5\u5e9c\u4e00\u540c\u534f\u5546\u6297\u502d\u7684\u5927\u8ba1\uff0c\u90a3\u7fa4\u88ab\u64d2\u7684\u65e5\u672c \u4eba\uff0c\u8fde\u540c\u9ad8\u6865\u5728\u5185\uff0c\u90fd\u4e00\u5e76\u88ab\u6536\u76d1\u4e86\u3002 \u3000\u3000\u77e5\u5e9c\u672c\u8981\u5c06\u94c1\u955c\u5fc3\u7559\u4e0b\uff0c\u5171\u540c\u5546\u91cf\uff0c\u94c1\u955c\u5fc3\u8bf4\u4ed6\u8fd8\u6709\u8981\u7d27\u7684\u4e8b\u60c5\u5f85\u529e\uff0c\u60f3\u5148\u5230\u5916\u9762\u8d70\u4e00 \u8d9f\uff0c\u77e5\u5e9c\u60f3\u8d77\u4ed6\u88ab\u7f81\u56da\u591a\u65e5\uff0c\u60f3\u51fa\u53bb\u4f1a\u4f1a\u4eb2\u53cb\uff0c\u4e5f\u662f\u4eba\u60c5\u4e4b\u5e38\uff0c\u800c\u4e14\u77e5\u5e9c\u4e5f\u6709\u70b9\u5fcc\u60ee\u94c1\u955c \u5fc3\uff0c\u751f\u6015\u4ed6\u518d\u5f04\u51fa\u4ec0\u4e48\u82b1\u6837\uff0c\u6559\u81ea\u5df1\u9a91\u864e\u96be\u4e0b\uff0c\u5f53\u4e0b\u7a0d\u4e00\u6c89\u541f\uff0c\u4fbf\u51c6\u94c1\u955c\u5fc3\u5148\u884c\u544a\u9000\u3002 \u3000\u3000\u77f3\u6587\u7ea8\u7559\u4e0b\u90a3\u4e00\u961f\u6e14\u6c11\uff0c\u8ddf\u7740\u94c1\u955c\u5fc3\u6324\u51fa\u5927\u95e8\uff0c\u4f17\u4eba\u90fd\u5bf9\u4ed6\u4eec\u6b22\u547c\uff0c\u4e8e\u627f\u73e0\u4e5f\u4e0d\u81ea\u89c9\u5730 \u9001\u4ed6\u4eec\u51fa\u53bb\uff0c\u77f3\u6587\u7ea8\u8fd8\u6ca1\u6709\u7559\u610f\uff0c\u94c1\u955c\u5fc3\u5374\u77a5\u89c1\u4e86\u4ed6\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u4ed6\u4e00\u628a\u62c9\u7740\uff0c\u9053\uff1a\u201c\u54b1 \u4eec\u4e00\u540c\u8d70\u5427\u3002\u201d\u77f3\u6587\u7ea8\u671b\u4e8e\u627f\u73e0\u4e00\u773c\uff0c\u4e8e\u627f\u73e0\u5411\u5979\u70b9\u70b9\u5934\uff0c\u77f3\u6587\u7ea8\u4e5f\u51b7\u51b7\u6de1\u6de1\u5730\u5411\u5979\u70b9\u4e86\u70b9 \u5934\uff0c\u4e24\u4eba\u90fd\u6ca1\u6709\u8c08\u8bdd\u3002\u4e8e\u627f\u73e0\u4ece\u6765\u6ca1\u6709\u88ab\u4e00\u4e2a\u7537\u5b50\u7d27\u63e1\u8fc7\u624b\uff0c\u5f88\u4e0d\u81ea\u7136\uff0c\u8138\u4e0a\u6cdb\u8d77\u4e00\u7247\u7ea2 \u971e\uff0c\u597d\u5728\u4f17\u4eba\u55a7\u95f9\u4e4b\u4e2d\uff0c\u94c1\u955c\u5fc3\u4e5f\u6ca1\u6709\u7559\u610f\u5230\u5979\u7684\u5f02\u6837\u795e\u60c5\u3002 \u3000\u3000\u4e09\u4eba\u8d70\u51fa\u5e9c\u8859\uff0c\u4f46\u89c1\u9644\u8fd1\u7684\u8857\u9053\u4e0a\u62e5\u6324\u6ee1\u4e86\u4eba\uff0c\u7eb7\u7eb7\u8c08\u8bba\u4ece\u5e9c\u8859\u5185\u4f20\u51fa\u6765\u7684\u6d88\u606f\uff0c\u6709\u7684 \u4eba\u5728\u5938\u8d5e\u94c1\u955c\u5fc3\uff0c\u6709\u7684\u4eba\u5728\u5927\u9a82\u502d\u5bc7\uff0c\u94c1\u955c\u5fc3\u6015\u88ab\u4eba\u7fa4\u53d1\u73b0\uff0c\u5e26\u4e8e\u3001\u77f3\u4e8c\u4eba\u7a7f\u8fc7\u6a2a\u8857\u5c0f\u5df7\uff0c \u8d70\u4e86\u597d\u8fdc\u597d\u8fdc\uff0c\u8fd8\u9690\u9690\u95fb\u5f97\u80cc\u540e\u55a7\u95f9\u4e4b\u58f0\uff0c\u94c1\u955c\u5fc3\u7b11\u9053\uff1a\u201c\u502d\u5bc7\u8d8a\u662f\u86ee\u4e0d\u8bb2\u7406\uff0c\u8d8a\u662f\u6043\u5f3a\u901e \u51f6\uff0c\u54b1\u4eec\u7684\u6c11\u6c14\u4fbf\u8d8a\u53d1\u6fc0\u6602\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u53ef\u4f5c\u89c1\u8bc1\u3002\u201d\u4e8e\u627f\u73e0\u604d\u7136\u5927\u609f\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u7518\u613f \u53d7\u502d\u5974\u7684\u4f1a\u5ba1\uff0c\u5c31\u662f\u60f3\u6fc0\u53d1\u6c11\u6c14\u7684\uff0c\u8fd9\u9053\u7406\u6211\u524d\u65e5\u8fd8\u60f3\u4e0d\u6e05\u695a\u5462\u3002\u201d \u3000\u3000\u4f46\u8fd8\u6709\u4e00\u6837\u4e8e\u627f\u73e0\u672a\u66fe\u60f3\u5f97\u6e05\u695a\u7684\u662f\uff1a\u53f0\u5dde\u7236\u8001\u6b63\u5728\u5e9c\u8859\u540c\u5546\u6297\u502d\u5927\u8ba1\uff0c\u94c1\u955c\u5fc3\u4e3a\u4f55\u6ca1 \u6709\u53c2\u52a0\uff0c\u800c\u8981\u6025\u6025\u51fa\u5916\uff1f\u96be\u9053\u8fd8\u6709\u4ec0\u4e48\u6bd4\u6297\u502d\u66f4\u8981\u7d27\u7684\u4e8b\u60c5\uff1f\u6b63\u60f3\u95ee\u4ed6\uff0c\u94c1\u955c\u5fc3\u53c8\u5fae\u7b11\u8bf4 \u9053\uff1a\u201c\u4f60\u4eec\u8ba4\u8bc6\u4e86\u5427\uff1f\u201d\u4ed6\u8fd9\u8bdd\u662f\u9762\u5411\u77f3\u6587\u7ea8\u8bf4\u7684\u3002\u77f3\u6587\u7ea8\u8f7b\u8f7b\u5730\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60 \u4ea4\u7684\u597d\u670b\u53cb\u554a\uff01\u201d\u94c1\u955c\u5fc3\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u4e8e\u5144\u786e\u662f\u591f\u670b\u53cb\u3002\u6211\u4eec\u662f\u5728\u957f\u6c5f\u8239\u4e0a\u8ba4\u8bc6 \u7684\uff0c\u7b2c\u4e00\u6b21\u4f1a\u9762\u6211\u5c31\u66fe\u89c1\u4ed6\u594b\u4e0d\u987e\u8eab\u5730\u6551\u4e24\u4f4d\u6e14\u5bb6\u7236\u5973\u3002\u201d\u77f3\u6587\u7ea8\u9053\uff1a\u201c\u90a3\u771f\u662f\u4e00\u4f4d\u4fa0\u4e49\u4e4b \u571f\u4e86\u3002\u5c31\u2026\u2026\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u5c31\u4ec0\u4e48\uff1f\u201d\u77f3\u6587\u7ea8\u672c\u60f3\u8bf4\uff1a\u201c\u5c31\u53ef\u60dc\u884c\u4e3a\u8f7b\u8584\u3002\u201d\u4f46\u5979\u6709\u51e0\u5206 \u754f\u60e7\u8fd9\u4f4d\u5927\u5e08\u5144\uff0c\u89c1\u5927\u5e08\u5144\u5982\u6b64\u79f0\u8d5e\u4e8e\u627f\u73e0\uff0c\u8bdd\u5230\u53e3\u8fb9\u53c8\u541e\u4e86\u53bb\uff0c\u6539\u53e3\u9053\uff1a\u201c\u5c31\u662f\u592a\u5e74\u8f7b\u4e86 \u4e00\u70b9\u3002\u201d\u94c1\u955c\u5fc3\u5fcd\u4e0d\u4f4f\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u539f\u6765\u4ed6\u6709\u4e00\u4e2a\u60f3\u6cd5\uff0c\u60f3\u7ed9\u5e08\u59b9\u64ae\u5408\u59fb\u7f18\uff0c\u4ed6\u8fd8\u6ca1\u6709\u77e5 \u9053\u6210\u6d77\u5c71\u5bf9\u77f3\u6587\u7ea8\u65e9\u5df2\u840c\u4e86\u7231\u610f\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u94c1\u5144\uff0c\u4f60\u5728\u54ea\u513f\uff1f\u201d\u94c1\u955c\u5fc3\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u53bb\u54ea\u513f\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u5f53\u7136 \u662f\u56de\u5bb6\u53bb\u554a\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4e5f\u5c31\u662f\u8981\u5230\u4f60\u7684\u5bb6\u554a\uff01\u201d\u4e8e\u627f\u73e0\u89c1\u4ed6\u4e0d\u4f3c\u8bf4\u7b11\uff0c\u5fc3\u4e2d\u5947 \u9053\uff1a\u201c\u4ed6\u53c8\u8bf4\u6709\u7d27\u8981\u7684\u4e8b\u60c5\uff0c\u600e\u4e48\u5374\u53c8\u6709\u7a7a\u8ddf\u7740\u6211\u8d70\uff1f\u201d\u867d\u7136\u7eb3\u95f7\uff0c\u5fc3\u4e2d\u5374\u662f\u6b22\u559c\u3002\u4e0d\u4e00\u523b \u8d70\u5230\u4e86\u5f20\u9ed1\u5bc4\u4f4f\u7684\u5bb6\u3002\u5ffd\u89c1\u5f20\u9ed1\u548c\u4e00\u4e2a\u610f\u60f3\u4e0d\u5230\u7684\u4eba\u8fce\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u539f\u6765\u5c31\u662f\u6210\u6d77\u5c71\uff0c\u4ecd\u662f\u524d\u65e5\u90a3\u822c\u8001\u8001\u5b9e\u5b9e\u7684\u6e14\u5bb6\u88c5\u675f\uff0c\u94c1\u955c\u5fc3\u3001\u4e8e\u627f\u73e0\u548c\u6210\u6d77\u5c71\u4e00 \u89c1\uff0c\u4e09\u4eba\u90fd\u540c\u65f6\u53eb\u51fa\u58f0\u6765\uff1a\u201c\u54a6\uff0c\u539f\u6765\u662f\u4f60\uff01\u201d \u3000\u3000\u5f20\u9ed1\u9053\uff1a\u201c\u8fd9\u4f4d\u6210\u5927\u54e5\u5c31\u662f\u53f6\u7edf\u9886\u53f6\u5b97\u7559\u5927\u54e5\u6d3e\u6765\u7684\u4eba\uff0c\u7531\u4ed6\u5e26\u9886\u6211\u4eec\u5230\u53f6\u5927\u54e5\u90a3\u8fb9 \u53bb\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u8ba4\u8bc6\u53f6\u7edf\u9886\u7684\uff0c\u600e\u4e48\u8fde\u6211\u4e5f\u4e0d\u77e5\u9053\uff1f\u627e\u542c\u5e08\u59b9\u8bf4\u53f6\u7edf\u9886\u6d3e\u6709\u4eba \u6765\uff0c\u6211\u95ee\u5979\u662f\u8c01\uff0c\u5979\u4e0d\u80af\u8bf4\uff0c\u5374\u539f\u6765\u662f\u4f60\u3002\u201d\u6210\u6d77\u5c71\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u6708\u6211\u548c\u5e08\u59b9\u5c31\u5728\u53f6\u5927\u54e5\u90a3 \u8fb9\uff0c\u7941\u502d\u9756\u4e5f\u6253\u4e86\u51e0\u6b21\u4ed7\u5566\uff0c\u8fd8\u662f\u524d\u51e0\u5929\u624d\u56de\u6765\u7684\u3002\u5e08\u54e5\uff0c\u8fd9\u51e0\u4e2a\u6708\u4f60\u6e38\u5b66\u5728\u5916\uff0c\u6211\u4eec\u8fd8\u6ca1 \u6709\u673a\u4f1a\u544a\u8bc9\u4f60\u54e9\u3002\u201d\u94c1\u955c\u5fc3\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u957f\u5927\u6210\u4eba\uff0c\u61c2\u5f97\u529e\u4e8b\u5566\uff0c\u6211\u8fd8\u5f53\u4f60\u4eec\u4ecd\u7136\u4f4f\u5728\u8001 \u5bb6\uff0c\u6210\u5929\u6349\u9e1f\u5440\u9493\u9c7c\u5440\u95f9\u770b\u73a9\u54e9\u3002\u201d\u6210\u6d77\u5c71\u4e5f\u7b11\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u51e0\u5929\u662f\u5728\u8001\u5bb6\u5440\uff0c\u5e78\u597d\u4f60\u4e0d\u77e5 \u9053\u6211\u4eec\u66fe\u79bb\u5bb6\u4ed6\u53bb\uff0c\u8981\u4e0d\u7136\u4f60\u4e5f\u4e0d\u4f1a\u8bf7\u8fd9\u4f4d\u4e8e\u76f8\u516c\u5230\u767d\u6c99\u6751\u627e\u6211\u4eec\u5566\u3002\u6211\u4e5f\u6599\u60f3\u4e0d\u5230\u8fd9\u4f4d\u4e8e \u76f8\u516c\u539f\u6765\u5c31\u662f\u53f6\u7edf\u9886\u8bf7\u6765\u7684\u6551\u5175\u3002\u4eca\u65e9\u6211\u5f97\u5230\u53f6\u5927\u54e5\u9001\u6765\u7684\u4fe1\uff0c\u53eb\u6211\u5230\u8fd9\u91cc\u63a5\u4e00\u4f4d\u4ece\u8fdc\u4e1c\u8bf7 \u6765\u7684\u5927\u8c6a\u4fa0\uff0c\u6211\u8fd8\u4ee5\u4e3a\u662f\u6bd5\u64ce\u5929\u6bd5\u5927\u9f99\u5934\uff0c\u5374\u539f\u6765\u662f\u4e8e\u76f8\u516c\u3002\u8fd9\u771f\u662f\u5de7\u6781\u4e86\u3002\u524d\u5929\u82e5\u4e0d\u662f\u78b0 \u7740\u4e8e\u76f8\u516c\uff0c\u6211\u548c\u5e08\u59b9\u90fd\u51e0\u4e4e\u8981\u7ed9\u9e70\u722a\u5b50\u4f24\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u4e5f\u8ba4\u8bc6\u6bd5\u64ce\u5929\u4e48\uff1f\u201d\u6210\u6d77\u5c71 \u9053\uff1a\u201c\u6ca1\u89c1\u8fc7\u54e9\u3002\u53ef\u662f\u5317\u4e94\u7701\u5927\u9f99\u5934\u7684\u5a01\u540d\u8c01\u4e0d\u77e5\u9053\u3002\u201d\u94c1\u955c\u5fc3\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u4eba\u7684\u540d \u513f\uff0c\u6811\u7684\u5f71\u513f\uff0c\u8fd9\u4fd7\u8bed\u8bf4\u5f97\u6709\u51e0\u5206\u9053\u7406\u3002\u4f46\u4e5f\u4e0d\u89c1\u5f97\u4eba\u4eba\u90fd\u662f\u540d\u5b9e\u76f8\u7b26\uff0c\u54b1\u4eec\u4e5f\u4e0d\u5fc5\u9707\u4e8e\u522b \u4eba\u7684\u5a01\u540d\u3002\u6211\u542c\u8bf4\u6bd5\u64ce\u5929\u662f\u5317\u65b9\u4e10\u5e2e\u7684\u9996\u9886\uff0c\u4f5c\u6c5f\u6e56\u7684\u9f99\u5934\u5e2e\u4e3b\uff0c\u5927\u7ea6\u8fd8\u662f\u591f\u8d44\u683c\u7684\u3002\u201d\u6210 \u6d77\u5c71\u9ed8\u7136\u4e0d\u8bed\uff0c\u4e8e\u627f\u73e0\u867d\u7136\u5bf9\u6bd5\u64ce\u5929\u5e76\u65e0\u597d\u611f\uff0c\u5bf9\u94c1\u955c\u5fc3\u8fd9\u8bdd\uff0c\u4ea6\u611f\u5230\u4e9b\u5fae\u4e0d\u5feb\uff0c\u5fc3\u9053\uff1a \u201c\u4f60\u53c8\u6ca1\u6709\u89c1\u8fc7\u6bd5\u64ce\u5929\uff0c\u600e\u4e48\u5c31\u90fd\u77e5\u9053\u4eba\u5bb6\uff1f\u96be\u9053\u8349\u83bd\u4e4b\u4e2d\u5c31\u6ca1\u6709\u4eba\u6750\uff0c\u4e10\u5e2e\u7684\u9996\u9886\u5c31\u53ea\u914d \u5f53\u9f99\u5934\u5e2e\u4e3b\u5417\uff1f\u201d\u94c1\u955c\u5fc3\u662f\u5b98\u5bb6\u5b50\u5f1f\uff0c\u6587\u624d\u6b66\u827a\u90fd\u51fa\u8272\u5f53\u884c\uff0c\u5bf9\u4e8e\u8349\u83bd\u4eba\u7269\uff0c\u6f5c\u610f\u8bc6\u4e2d\u603b\u6709 \u4e00\u4e9b\u8f7b\u89c6\u3002\u8fd9\u548c\u4e8e\u627f\u73e0\u5374\u5fae\u6709\u4e0d\u540c\uff0c\u4e8e\u627f\u73e0\u867d\u7136\u4e5f\u662f\u9601\u8001\u7684\u72ec\u751f\u5973\u513f\uff0c\u4f46\u4e8e\u8c26\u4e3a\u4eba\uff0c\u548c\u666e\u901a \u7684\u5927\u5b98\u5b8c\u5168\u4e0d\u540c\uff0c\u505a\u5230\u9601\u8001\uff0c\u5e73\u65e5\u4e5f\u4eb2\u81ea\u64cd\u52b3\uff0c\u5e76\u65e0\u5b98\u5bb6\u4e60\u6c14\u3002\u800c\u4e8e\u627f\u73e0\u53c8\u6700\u53d7\u5e08\u7236\u5f20\u4e39\u67ab \u7684\u5f71\u54cd\uff0c\u5f20\u4e39\u67ab\u5c11\u5e74\u65f6\u5019\u95ef\u8361\u6c5f\u6e56\uff0c\u5386\u7ecf\u5fe7\u60a3\uff0c\u6240\u7ed3\u4ea4\u7684\u66f4\u591a\u7684\u662f\u8349\u83bd\u82f1\u96c4\uff0c\u6240\u4ee5\u4e8e\u627f\u73e0\u548c \u8349\u83bd\u4eba\u7269\u76f8\u5904\uff0c\u6291\u6216\u89c9\u5f97\u6c14\u8d28\u4e0d\u5927\u76f8\u8fd1\uff0c\u4f46\u5bf9\u5176\u4e2d\u7684\u82f1\u96c4\u8c6a\u6770\uff0c\u603b\u4e0d\u5931\u6389\u656c\u610f\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5bf9\u94c1\u955c\u5fc3\u8fd9\u51e0\u65e5\u7684\u884c\u4e8b\uff0c\u4f69\u670d\u4e4b\u6781\uff0c\u6240\u4ee5\u8fd9\u4e9b\u5fae\u4e0d\u5feb\uff0c\u8f6c\u77ac\u4ea6\u4e91\u6563\u70df\u6d88\u3002\u53ea\u542c\u5f97 \u94c1\u955c\u5fc3\u53c8\u95ee\u6210\u6d77\u5c71\u9053\uff1a\u201c\u4ec0\u4e48\u9e70\u722a\u5b50\uff1f\u600e\u4e48\u4ed6\u8981\u6765\u4f24\u5bb3\u4f60\u4eec\uff1f\u201d\u6210\u6d77\u5c71\u9053\uff1a\u201c\u9e70\u722a\u5b50\u542c\u8bf4\u54b1 \u4eec\u7684\u5e08\u7236\u56de\u6765\u4e86\uff0c\u4ed6\u8981\u6765\u641c\u6355\u54b1\u4eec\u7684\u5e08\u7236\u5462\u3002\u201d\u94c1\u955c\u5fc3\u5fae\u73b0\u8be7\u5f02\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u9053 \u7406\uff0c\u4ed6\u8001\u4eba\u5bb6\u72af\u4e86\u4ec0\u4e48\u6cd5\u4e86\uff1f\u201d \u3000\u3000\u6210\u6d77\u5c71\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u53ef\u4e0d\u77e5\u9053\u4e86\u3002\u201d\u94c1\u955c\u5fc3\u773c\u5149\u5411\u77f3\u6587\u7ea8\u4e00\u626b\uff0c\u77f3\u6587\u7ea8\u55eb\u5685\u8bf4\u9053\uff1a\u201c\u8fd9 \u4e2a\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u201d\u4e8e\u627f\u73e0\u5341\u5206\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u201c\u77f3\u60ca\u6d9b\u662f\u56e0\u4e3a\u76d7\u4e86\u5927\u5185\u5b9d\u5251\uff0c\u5927\u95f9\u7687\u5bab\uff0c\u8fd9\u624d \u9003\u4ea1\u6d77\u5916\u7684\u3002\u94c1\u955c\u5fc3\u662f\u4ed6\u7684\u5f97\u610f\u9ad8\u8db3\uff0c\u600e\u4e48\u4f1a\u4e0d\u77e5\u9053\uff1f\u770b\u77f3\u6587\u7ea8\u7684\u795e\u60c5\uff0c\u5979\u5206\u660e\u662f\u77e5\u9053\u7684\uff0c \u4e3a\u4f55\u5374\u53c8\u4e0d\u544a\u8bc9\u5927\u5e08\u5144\uff1f\u201d\u82e5\u662f\u5728\u4e00\u5e74\u4e4b\u524d\uff0c\u4e8e\u627f\u73e0\u5fc3\u76f4\u53e3\u5feb\uff0c\u4e00\u5b9a\u4f1a\u5c06\u6240\u77e5\u544a\u8bc9\u94c1\u955c\u5fc3\uff0c \u8fd9\u4e00\u5e74\u6765\u591a\u5c11\u7ecf\u8fc7\u4e86\u4e00\u4e9b\u78e8\u7ec3\uff0c\u7a0d\u7a0d\u61c2\u4e86\u4e00\u70b9\u4eba\u60c5\u4e16\u6545\uff0c\u8bdd\u5230\u53e3\u8fb9\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5fc3\u9053\uff1a\u201c\u77f3\u60ca \u6d9b\u7792\u7740\u8fd9\u4e2a\u5f92\u5f1f\uff0c\u5176\u4e2d\u5b9a\u6709\u9053\u7406\u3002\u77f3\u60ca\u6d9b\u76d7\u5b9d\u95f9\u7687\u5b98\u7b49\u4e8b\uff0c\u6b66\u6797\u4e2d\u77e5\u9053\u662f\u4ed6\u5e72\u7684\uff0c\u4e5f\u53ea\u6709\u6211 \u592a\u5e08\u7956\u7b49\u6709\u9650\u51e0\u4eba\uff0c\u5e08\u7236\u4fe1\u5f97\u8fc7\u6211\uff0c\u624d\u80af\u5c06\u8fd9\u4e9b\u6c5f\u6e56\u4e0a\u6210\u540d\u4eba\u7269\u7684\u9690\u79d8\u544a\u4e0e\u6211\u77e5\uff0c\u6211\u5c82\u53ef\u968f \u4fbf\u4e71\u8bf4\u3002\u201d \u3000\u3000\u6210\u6d77\u5c71\u9053\uff1a\u201c\u53f6\u5927\u54e5\u7684\u610f\u601d\uff0c\u53eb\u6211\u9001\u4ed6\u4eec\u4e8c\u4f4d\u5230\u8fbe\u4e4b\u540e\u5c31\u56de\u6765\u76f8\u52a9\u53f0\u5dde\u7684\u6c11\u56e2\u5b88\u57ce\uff0c\u5e08 \u5144\u4f60\u8bf4\u5982\u4f55\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u6664\uff0c\u4e5f\u597d\uff0c\u7b49\u6211\u5411\u77e5\u5e9c\u4fdd\u4e3e\u4f60\u4fbf\u662f\u4e86\u3002\u5e08\u59b9\uff0c\u4f60\u5462\uff1f\u201d\u77f3\u6587\u7ea8 \u9053\uff1a\u201c\u6211\u4e5f\u613f\u7559\u5728\u6b64\u52a9\u6210\u5e05\u54e5\u3002\u201d\u6210\u6d77\u5c71\u9053\uff1a\u201c\u53f6\u5927\u54e5\u5f88\u76fc\u671b\u4f60\u4e5f\u5e2e\u4ed6\u3002\u201d\u94c1\u955c\u5fc3\u7a0d\u4e00\u6c89 \u541f\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u5f85\u6211\u5148\u56de\u5bb6\u7980\u544a\u7236\u4eb2\u3002\u6211\u542c\u8bf4\u53f6\u5b97\u7559\u73b0\u6b63\u5904\u5728\u5371\u96be\u4e4b\u5883\uff0c\u6297\u502d\u5927\u4e8b\uff0c\u4eba\u4eba \u6709\u8d23\uff0c\u6211\u53bb\u662f\u5e94\u8be5\u7684\u3002\u201d\u4ed6\u8bf4\u5f97\u5f88\u5e73\u6de1\uff0c\u4f46\u4e8e\u627f\u73e0\u5374\u542c\u51fa\u4ed6\u81ea\u8d1f\u7684\u5fc3\u60c5\uff0c\u597d\u50cf\u4ed6\u4e00\u53bb\u4ec0\u4e48\u90fd \u4f1a\u597d\u8f6c\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5fc3\u4e2d\u53c8\u611f\u5230\u4e9b\u5fae\u4e0d\u5feb\uff0c\u4f46\u60f3\u5230\u94c1\u955c\u5fc3\u786e\u5b9e\u662f\u4e2a\u5927\u6709\u672c\u4e8b\u7684\u4eba\uff0c\u5fc3\u4e2d\u7684\u4e0d \u5feb\uff0c\u8fc5\u5373\u53c8\u70df\u6d88\u4e91\u6563\u4e86\u3002 \u3000\u3000\u508d\u665a\u65f6\u5206\uff0c\u94c1\u955c\u5fc3\u56de\u6765\uff0c\u795e\u60c5\u6709\u70b9\u5931\u671b\uff0c\u6210\u6d77\u5c71\u9053\uff1a\u201c\u6211\u7236\u4eb2\u4e00\u5f97\u4fdd\u91ca\u4e4b\u540e\uff0c\u5c31\u79bb\u5f00\u53f0 \u5dde\uff0c\u8fdb\u7701\u53bb\u4e86\uff01\u5440\uff0c\u6211\u5343\u91cc\u8fe2\u8fe2\u5730\u8d76\u56de\u6765\u6551\u4ed6\u8001\u4eba\u5bb6\uff0c\u5374\u89c1\u4e0d\u7740\u4ed6\u4e00\u9762\u3002\u201d\u4e8e\u627f\u73e0\u53c8\u611f\u5947 \u602a\uff0c\u5fc3\u9053\uff1a\u201c\u7236\u5b50\u9aa8\u8089\u8fde\u5fc3\uff0c\u94c1\u94f1\u600e\u4e48\u4e0d\u7b49\u4ed6\u513f\u5b50\u7684\u6848\u5b50\u7ec8\u7ed3\u5c31\u8d70\u5f00\u4e86\uff1f\u662f\u6709\u4eba\u903c\u4ed6\u5982\u6b64 \u7684\uff1f\u8fd8\u662f\u4ed6\u5bb3\u6015\u8fd9\u5371\u57ce\u4e0d\u53ef\u4e45\u5c45\uff1f\u201d\u6210\u6d77\u5c71\u9053\uff1a\u201c\u90a3\u4e48\u5927\u5e08\u5144\u660e\u5929\u540c\u6211\u4eec\u4e00\u9053\u8d70\u4e48\uff1f\u201d\u94c1\u955c \u5fc3\u4ef0\u5929\u541f\u9053\uff1a\u201c\u82f1\u96c4\u8840\u6d12\u80e1\u5c18\u91cc\uff0c\u56fd\u96be\u65b9\u6df1\u90a3\u7ba1\u5bb6\uff01\u8d70\uff0c\u5f53\u7136\u8d70\uff01\u201d \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u94c1\u955c\u5fc3\u3001\u4e8e\u627f\u73e0\u3001\u5f20\u9ed1\u3001\u6210\u6d77\u5c71\u7b49\u4eba\u79bb\u5f00\u53f0\u5dde\uff0c\u7531\u6210\u6d77\u5c71\u5e26\u8def\uff0c\u8d70\u4e86\u4e24 \u5929\uff0c\u5230\u8fbe\u4e49\u519b\u9a7b\u7ba1\u4e4b\u5730\u3002\u90a3\u662f\u6ee8\u6d77\u7684\u4e00\u5ea7\u5c71\u5934\uff0c\u8fd9\u5ea7\u5c71\u662f\u4ed9\u971e\u5cad\u7684\u652f\u8109\uff0c\u867d\u7136\u4e0d\u7b97\u5ced\u62d4\uff0c\u5374 \u4e5f\u5c71\u9ad8\u6797\u5bc6\uff0c\u4e49\u519b\u7684\u7ba1\u5730\u5c31\u5728\u5bc6\u6797\u4e4b\u4e2d\uff0c\u56db\u4eba\u8d70\u5165\u5c71\u4e2d\uff0c\u968f\u5904\u89c1\u5230\u4e49\u519b\u6216\u5728\u65a9\u67f4\uff0c\u6216\u5728\u79cd \u83dc\uff0c\u8863\u886b\u8934\u891b\uff0c\u53ef\u4ee5\u60f3\u89c1\u4ed6\u4eec\u652f\u6301\u7684\u8270\u82e6\uff0c\u4f46\u4eba\u4eba\u90fd\u662f\u563b\u563b\u54c8\u54c8\u5730\u4e00\u9762\u64cd\u4f5c\u4e00\u9762\u8c08\u7b11\uff0c\u5e76\u65e0 \u6101\u82e6\u4e4b\u5bb9\u3002\u4e8e\u627f\u73e0\u751a\u662f\u4f69\u670d\u3002\u94c1\u955c\u5fc3\u5374\u5728\u60f3\u9053\uff1a\u201c\u8fd9\u4e9b\u4e4c\u5408\u4e4b\u4f17\uff0c\u602a\u4e0d\u5f97\u96be\u4ee5\u62b5\u654c\u502d\u5bc7\uff0c\u6211 \u53ef\u5f97\u52a9\u53f6\u5b97\u7559\u7ed9\u4ed6\u597d\u597d\u6574\u987f\u4e00\u4e0b\u519b\u961f\u624d\u884c\u3002\u201d \u3000\u3000\u53f6\u5b97\u7559\u542c\u5f97\u4ed6\u4eec\u5230\u6765\uff0c\u6781\u4e3a\u9ad8\u5174\uff0c\u7acb\u523b\u8bf7\u4ed6\u4eec\u5230\u5e10\u4e2d\u76f8\u89c1\u3002\u90a3\u5e10\u7bf7\u662f\u7528\u725b\u76ae\u505a\u7684\uff0c\u7b97\u662f \u6700\u597d\u7684\u4e86\uff0c\u4f46\u4e5f\u6709\u51e0\u5904\u7834\u70c2\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u3001\u4e8e\u627f\u73e0\u7b49\u8d70\u5165\u5e10\u4e2d\uff0c\u53ea\u89c1\u51e0\u4e2a\u4eba\u4e00\u540c\u8fce\u4e86\u51fa\u6765\uff0c\u5176\u4e2d\u4e00\u4eba\u77ed\u987b\u5982\u7ff0\uff0c\u9ed1\u6f06\u53d1\u5149 \u7684\u8138\uff0c\u7a7f\u7740\u8865\u4e86\u51e0\u4e2a\u7efd\u7684\u571f\u5e03\u8863\u88f3\uff0c\u6d3b\u50cf\u4e45\u7ecf\u96e8\u6dcb\u65e5\u6652\u7684\u4e61\u4e0b\u957f\u5de5\uff0c\u4e00\u89c1\u4ed6\u4eec\u8fdb\u6765\uff0c\u7acb\u523b\u4f38 \u51fa\u4e24\u53ea\u53c8\u5927\u53c8\u9ed1\u7684\u624b\u638c\uff0c\u53eb\u9053\uff1a\u201c\u65e5\u65e5\u76fc\u671b\u4f60\u4eec\uff0c\u771f\u662f\u60f3\u6b7b\u6211\u4e86\uff0c\u8fd9\u4f4d\u662f\u94c1\u516c\u5b50\u4e48\uff1f\u201d\u53cc\u638c \u4e00\u62cd\u94c1\u955c\u5fc3\u7684\u80a9\u5934\uff0c\u5728\u4ed6\u81ea\u662f\u8868\u793a\u4eb2\u70ed\uff0c\u4e00\u62cd\u4e0b\u6765\uff0c\u94c1\u955c\u5fc3\u7684\u8863\u88f3\u767b\u65f6\u73b0\u51fa\u4e24\u4e2a\u9ed1\u638c\u5370\uff0c\u56db \u4eba\u4e4b\u4e2d\uff0c\u94c1\u955c\u5fc3\u7684\u8863\u88f3\u6700\u4e3a\u6574\u6d01\uff0c\u6599\u5b50\u4e5f\u5f88\u4e0d\u9519\uff0c\u90a3\u5927\u6c49\u4e00\u62cd\u4e4b\u4e0b\uff0c\u7acb\u523b\u53d1\u73b0\uff0c\u8d54\u7b11\u8bf4\u9053\uff1a \u201c\u54ce\u5440\uff0c\u5f04\u810f\u4e86\u8d35\u5ba2\u7684\u8863\u88f3\u4e86\u3002\u201d\u6025\u5fd9\u66ff\u94c1\u955c\u5fc3\u8f7b\u8f7b\u62c2\u62ed\uff0c\u4ed6\u60f3\u662f\u521a\u521a\u4ece\u5730\u4e0a\u56de\u6765\uff0c\u6307\u7532\u4e5f \u8fd8\u6cbe\u7740\u5c18\u571f\uff0c\u8d8a\u62c2\u8d8a\u810f\uff0c\u94c1\u955c\u5fc3\u9887\u6709\u70b9\u5c34\u5c2c\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u53f6\u7edf\u9886\u4e48\uff1f\u201d\uff0c\u201c\u7edf\u9886\u201d \u662f\u4e49\u519b\u516c\u63a8\u4ed6\u505a\u7684\uff0c\u53ef\u5e76\u4e0d\u662f\u671d\u5ef7\u7684\u547d\u5b98\u3002\u90a3\u6c49\u5b50\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u7edf\u9886\uff0c\u6211\u53eb\u53f6\u5b97\u7559\uff0c\u5f1f \u5144\u4eec\u6216\u8005\u53eb\u6211\u505a\u53f6\u8001\u9ed1\uff0c\u6216\u8005\u53eb\u6211\u53f6\u5927\u54e5\uff0c\u4f60\u4eec\u4e0d\u5fc5\u548c\u6211\u5ba2\u6c14\uff0c\u6211\u6bd4\u4f60\u4eec\u75f4\u957f\u51e0\u5c81\uff0c\u6211\u6258\u5927 \u4e00\u70b9\uff0c\u4f60\u4eec\u53eb\u6211\u505a\u53f6\u5927\u54e5\u4e5f\u5c31\u884c\u5566\u3002\u201d\u94c1\u955c\u5fc3\u6697\u9053\uff1a\u201c\u5728\u53f0\u5dde\u51e0\u4e4e\u65e5\u65e5\u542c\u5230\u53f6\u5b97\u7559\u7684\u5927\u540d\uff0c \u4eba\u4eba\u90fd\u8bf4\u4ed6\u662f\u4e86\u4e0d\u5f97\u7684\u6c49\u5b50\uff0c\u5374\u539f\u6765\u662f\u4e2a\u4e61\u4e0b\u4f6c\u7684\u6a21\u6837\u3002\u201d\u4ed6\u53ef\u4e0d\u77e5\uff0c\u53f6\u5b97\u7559\u5c82\u6b62\u662f\u201c\u4e61\u4e0b \u4f6c\u201d\uff0c\u8fd8\u662f\u4e2a\u5f53\u65f6\u793e\u4f1a\u6240\u8d31\u89c6\u7684\u5f53\u77ff\u5de5\u51fa\u8eab\u7684\u3002\u4ed6\u624b\u4e0b\u7684\u5f1f\u5144\uff0c\u6709\u8bb8\u591a\u5c31\u662f\u4ed6\u77ff\u573a\u4e0a\u7684\u4f19 \u4f34\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5c06\u6bd5\u64ce\u5929\u548c\u5468\u5c71\u6c11\u7684\u4eb2\u7b14\u4e66\u4fe1\u4ea4\u4e86\u7ed9\u4ed6\uff0c\u53f6\u5b97\u7559\u6253\u5f00\u4e00\u770b\uff0c\u9053\uff1a\u201c\u54c8\uff0c\u6709\u597d\u591a\u5b57 \u5b83\u8ba4\u5f97\u6211\uff0c\u6211\u4e0d\u8ba4\u5f97\u5b83\u3002\u4f60\u7ed9\u6211\u5ff5\u3002\u201d\u968f\u624b\u5c06\u4e66\u4fe1\u4ea4\u7ed9\u65c1\u8fb9\u4e00\u4eba\uff0c\u90a3\u4eba\u7ea6\u6478\u56db\u5341\u591a\u5c81\uff0c\u80cc\u6709 \u70b9\u4f5d\u507b\uff0c\u8863\u670d\u867d\u7136\u4e5f\u6253\u4e86\u8bb8\u591a\u8865\u4e01\uff0c\u6d17\u5f97\u8fd8\u6d01\u51c0\uff0c\u770b\u6837\u5b50\u4f3c\u4e4e\u662f\u4ed6\u7684\u5e08\u7237\uff0c\u63a5\u8fc7\u4e24\u5c01\u4fe1\u5ff5 \u4e86\uff0c\u65e0\u975e\u662f\u8868\u793a\u613f\u540c\u5fc3\u6297\u502d\uff0c\u4e0d\u65e5\u5373\u5c06\u7387\u4f17\u6765\u5230\u7b49\u8bed\uff0c\u53ea\u6709\u6bd5\u64ce\u5929\u7684\u4fe1\u5c3e\u9644\u6709\u4e24\u70b9\u8bf4\u8bdd\uff0c\u8bf4 \u7684\u662f\uff1a\u201c\u4e45\u4ef0\u543e\u5144\u5927\u540d\uff0c\u4e1c\u5357\u6cbf\u6d77\u5f97\u4ee5\u5c11\u514d\u7cdc\u70c2\uff0c\u5168\u4ed7\u543e\u5144\u4e4b\u529b\u4e5f\uff0c\u5f1f\u5fdd\u4f4d\u4e94\u7701\u9f99\u5934\uff0c\u81ea\u60ed \u5fb7\u8584\uff0c\u5f53\u5728\u543e\u5144\u5e10\u4e0b\uff0c\u542c\u5019\u9a71\u9a70\u3002\u201d\u53f6\u5b97\u7559\u542c\u4e86\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6bd5\u64ce\u5929\u5199\u4fe1\uff0c\u600e\u4e48\u4e5f\u8fd9\u6837\u6587 \u7ec9\u7ec9\u7684\uff0c\u8fd9\u4fe1\u4e00\u5b9a\u4e5f\u662f\u4ed6\u7684\u5e08\u7237\u4ee3\u7b14\u7684\uff0c\u4ed6\u662f\u4e5e\u4e10\u5934\u513f\uff0c\u6211\u662f\u77ff\u5de5\u5934\u513f\uff0c\u6b63\u597d\u642d\u6863\uff0c\u4ed6\u672c\u4e8b \u6bd4\u6211\u5927\u5f97\u591a\uff0c\u6211\u6b63\u8981\u5949\u4ed6\u505a\u5927\u54e5\uff0c\u8fd9\u4e9b\u5f1f\u5144\u90fd\u4ea4\u7ed9\u4ed6\u4f7f\u5524\uff0c\u4ed6\u5374\u548c\u6211\u5ba2\u5957\uff0c\u8fd9\u5c82\u4e0d\u592a\u7b11\u8bdd \u5417\u3002\u54c8\uff0c\u54c8\uff01\u8fd9\u5c01\u4fe1\u4e00\u5b9a\u4e0d\u662f\u6bd5\u64ce\u5929\u4eb2\u7b14\u5199\u7684\u201d\u5c82\u77e5\u8fd9\u5c01\u4fe1\u6b63\u662f\u6bd5\u64ce\u5929\u4eb2\u7b14\u5199\u7684\uff0c\u6bd5\u64ce\u5929\u8c8c \u867d\u7c97\u9c81\uff0c\u5185\u91cc\u5374\u751a\u6709\u673a\u5fc3\uff0c\u4ed6\u7956\u5148\u662f\u5f20\u58eb\u8bda\u624b\u4e0b\u7684\u5927\u5c06\uff0c\u5b50\u5b59\u8981\u505a\u5341\u5e74\u548c\u5c1a\uff0c\u5341\u5e74\u4e5e\u4e10\uff0c\u4e43 \u662f\u5bb6\u89c4\uff0c\u6240\u4ee5\u6bd5\u64ce\u5929\u5e76\u975e\u4e00\u822c\u4e5e\u4e10\uff01\u4ed6\u4e43\u662f\u7c97\u901a\u6587\u58a8\u7684\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u542c\u4e86\uff0c\u5fae\u611f\u4e0d\u5feb\u3002\u94c1\u955c\u5fc3\u662f\u65e0\u610f\u4e0e\u53f6\u5b97\u7559\u4e89\u4f4d\u7684\uff0c\u4f46\u4ed6\u542c\u5f97\u53f6\u5b97\u7559\u5bf9\u6bd5\u64ce\u5929\u5982\u6b64 \u63a8\u5d07\uff0c\u4eba\u8fd8\u672a\u5230\u5c31\u51c6\u5907\u8ba9\u4f4d\u4e86\uff0c\u663e\u89c1\u53f6\u5b97\u7559\u5bf9\u6bd5\u64ce\u5929\u66f4\u4e3a\u7740\u91cd\uff0c\u94c1\u955c\u5fc3\u5fc3\u91cc\u53ef\u6709\u70b9\u4e0d\u8212\u670d\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7684\u60f3\u6cd5\u5374\u53c8\u5b8c\u5168\u4e0d\u540c\uff0c\u4e8e\u627f\u73e0\u60f3\u9053\uff1a\u201c\u6bd5\u64ce\u5929\u5176\u5b9e\u5904\u5fc3\u79ef\u8651\uff0c\u60f3\u505a\u9996\u9886\uff0c\u5374\u504f\u504f \u60fa\u60fa\u4f5c\u6001\uff0c\u6bd4\u8d77\u53f6\u5b97\u7559\u7684\u5149\u660e\u78ca\u843d\uff0c\u54c1\u683c\u4e0a\u90a3\u662f\u6709\u6240\u4e0d\u53ca\u7684\u4e86\u3002\u201d \u3000\u3000\u4e49\u519b\u88ab\u56f0\u5c71\u4e2d\u591a\u6708\uff0c\u5168\u519b\u4e0a\u4e0b\u5403\u7684\u90fd\u662f\u7cd9\u7c73\u91ce\u83dc\uff0c\u8fd9\u665a\u4e3a\u4e86\u94c1\u955c\u5fc3\u4ed6\u4eec\u521d\u5230\uff0c\u7279\u522b\u70e4\u4e86 \u4e00\u53ea\u91ce\u732a\u5f85\u5ba2\uff0c\u7cd9\u7c73\u6742\u6709\u8bb8\u591a\u8c37\u58f3\u7802\u5b50\uff0c\u4e8e\u627f\u73e0\u672c\u6765\u5403\u5f97\u4e0d\u60ef\uff0c\u4f46\u89c1\u53f6\u5b97\u7559\u6bb7\u6bb7\u529d\u5ba2\uff0c\u5c3d\u628a \u5927\u5757\u5927\u5757\u7684\u91ce\u732a\u8089\u5939\u5728\u94c1\u955c\u5fc3\u548c\u81ea\u5df1\u7684\u7897\u91cc\uff0c\u4e8e\u627f\u73e0\u53cd\u800c\u611f\u5230\u60ed\u6127\u4e0d\u5b89\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5730\u6252\u4e86\u4e24 \u7897\u7cd9\u7c73\u996d\uff0c\u6bd4\u5e73\u65f6\u8fd8\u5403\u591a\u534a\u7897\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7b49\u56db\u4eba\u88ab\u62db\u5f85\u5728\u4e00\u4e2a\u65b0\u642d\u597d\u7684\u5e10\u7bf7\u4e2d\u4f4f\u5bbf\uff0c\u4e5f\u662f\u725b\u76ae\u5e10\u7bf7\uff0c\u65b0\u51c0\u5b8c\u6574\uff0c\u4e0d\u6015\u6f0f \u96e8\uff0c\u6bd4\u53f6\u5b97\u7559\u81ea\u5df1\u4f4f\u7684\u90a3\u5ea7\u5e10\u7bf7\u8fd8\u597d\uff0c\u4e5f\u5f88\u5bbd\u655e\uff0c\u4e8e\u627f\u73e0\u3001\u94c1\u955c\u5fc3\u3001\u5f20\u9ed1\u3001\u6210\u6d77\u5c71\u7b49\u56db\u4eba\u5404 \u5360\u4e00\u89d2\u3002 \u3000\u3000\u8fd9\u4e00\u665a\uff0c\u4e8e\u627f\u73e0\u7ffb\u6765\u8986\u53bb\u7761\u4e0d\u7740\u89c9\uff0c\u8111\u6d77\u4e2d\u63a5\u8fde\u7ffb\u51fa\u51e0\u4e2a\u4eba\u7684\u5f71\u5b50\uff0c\u5148\u662f\u5f20\u4e39\u67ab\uff0c\u518d\u662f \u94c1\u955c\u5fc3\uff0c\u7136\u540e\u662f\u6bd5\u64ce\u5929\uff0c\u6700\u540e\u662f\u53f6\u5b97\u7559\u3002\u201c\u55ef\uff0c\u94c1\u955c\u5fc3\u662f\u6709\u51e0\u5206\u50cf\u6211\u7684\u5e08\u7236\u3002\u201d\u8fd9\u5370\u8c61\u5728\u957f \u6c5f\u521d\u4f1a\u4e4b\u65f6\uff0c\u4e8e\u627f\u73e0\u5c31\u5df2\u6709\u4e86\uff0c\u5982\u4eca\u94c1\u955c\u5fc3\u7684\u5f71\u5b50\u968f\u7740\u5f20\u4e39\u67ab\u7684\u5f71\u5b50\u98d8\u8fc7\uff0c\u8fd9\u5370\u8c61\u4fbf\u66f4\u5206 \u660e\u3002\u4e8e\u627f\u73e0\u4e0d\u89c9\u4ece\u5fc3\u5e95\u7b11\u4e86\u51fa\u6765\u3002\u4f46\u8f6c\u77ac\u4e4b\u95f4\uff0c\u53e6\u4e00\u4e2a\u5ff5\u5934\u53c8\u5728\u5fc3\u4e2d\u6cdb\u8d77\uff0c\u5ffd\u89c9\u5f97\u94c1\u955c\u5fc3\u867d \u6709\u51e0\u5206\u50cf\u5f20\u4e39\u67ab\uff0c\u4f46\u5374\u6709\u66f4\u591a\u7684\u5730\u65b9\u4e0d\u4f3c\uff0c\u4ed6\u4eec\u597d\u50cf\u662f\u5e76\u4e0d\u5c5e\u4e8e\u540c\u4e00\u7c7b\u578b\u7684\u4eba\uff0c\u5206\u522b\u5728\u4ec0\u4e48 \u5730\u65b9\uff1f\u4e8e\u627f\u73e0\u4e00\u4e0b\u5b50\u53ef\u7b54\u4e0d\u51fa\u6765\uff0c\u8fd9\u4e2a\u5370\u8c61\u662f\u4eca\u665a\u624d\u6709\u7684\uff0c\u4e5f\u8d8a\u6765\u8d8a\u5206\u660e\u4e86\u3002\u4e8e\u627f\u73e0\u5ffd\u7136\u611f \u5230\u5fc3\u5934\u6709\u70b9\u6c89\u91cd\uff0c\u8ba9\u5f20\u4e39\u67ab\u4e0e\u94c1\u955c\u5fc3\u7684\u5f71\u5b50\u90fd\u4ece\u8111\u4e2d\u95ea\u8fc7\uff0c\u518d\u60f3\u8d77\u4e86\u53f6\u5b97\u7559\uff0c\u53f6\u5b97\u7559\u5728\u94c1\u955c \u5fc3\u9762\u524d\u662f\u663e\u5f97\u591a\u4e48\u7b28\u62d9\uff0c\u4f46\u4ed6\u4e5f\u6709\u51e0\u5206\u4f3c\u6211\u7684\u5e08\u7236\u3002\u8fd9\u6837\u4e00\u60f3\uff0c\u8fde\u5979\u81ea\u5df1\u4e5f\u89c9\u5f97\u5947\u602a\uff0c\u5f20\u4e39 \u67ab\u72c2\u4fa0\u6e29\u6587\uff0c\u6f47\u6d12\u8131\u4fd7\uff0c\u53f6\u5b97\u7559\u600e\u4e48\u4f3c\u4ed6\uff1f\u4f46\u53c8\u786e\u4f3c\u6709\u4e9b\u5730\u65b9\u76f8\u50cf\u3002\u54ea\u4e9b\u5730\u65b9\u76f8\u4f3c\uff0c\u4e8e\u627f\u73e0 \u4e00\u4e0b\u5b50\u4e5f\u7b54\u4e0d\u4e0a\u6765\uff0c\u987b\u5f97\u597d\u597d\u5730\u60f3\uff0c\u53f6\u5b97\u7559\u8d28\u6734\u8c6a\u723d\uff0c\u548c\u94c1\u955c\u5fc3\u5bf9\u7167\u8d77\u6765\uff0c\u66f4\u663e\u5f97\u4e00\u5de7\u4e00 \u62d9\uff0c\u4ed6\u53c8\u4e0d\u5584\u4e8e\u8a00\u8bcd\uff0c\u4f46\u4ed6\u6240\u8bf4\u7684\u8bdd\uff0c\u6bcf\u4e00\u53e5\u90fd\u4f3c\u662f\u51fa\u81ea\u80ba\u8151\uff0c\u4ee4\u4eba\u89c9\u5f97\u8bda\u6073\u53ef\u4eb2\u3002\u4e8e\u627f\u73e0 \u5ffd\u800c\u89c9\u5f97\uff0c\u5f20\u4e39\u67ab\u4e0e\u53f6\u5b97\u7559\u8868\u9762\u770b\u6765\uff0c\u867d\u4f3c\u5904\u4e8e\u4e24\u4e2a\u6781\u7aef\uff0c\u5b8c\u5168\u4e0d\u540c\u7c7b\u578b\uff0c\u4f46\u4e24\u4e2a\u4eba\u7684\u6027\u683c \u53c8\u90fd\u5404\u6709\u5176\u53ef\u7231\u4e4b\u5916\uff0c\u751a\u81f3\u6709\u5171\u901a\u7684\u5730\u65b9\u3002\u94c1\u955c\u5fc3\u6bd4\u5c06\u8d77\u6765\uff0c\u53cd\u800c\u663e\u5f97\u6709\u4e9b\u5931\u8272\u4e86\u3002\u81f3\u4e8e\u6bd5 \u64ce\u5929\u4e5f\u81ea\u6709\u5176\u8c6a\u4fa0\u53ef\u656c\u4e4b\u5904\uff0c\u4e0d\u8fc7\u6bd4\u8d77\u5176\u4f59\u4e09\u4eba\uff0c\u6bd5\u64ce\u5929\u53c8\u4f3c\u4e4e\u663e\u5f97\u66f4\u900a\u8272\u4e86\u3002\u8fd9\u4e00\u665a\uff0c\u4e8e \u627f\u73e0\u7ffb\u6765\u8986\u53bb\u5730\u5c3d\u5728\u60f3\uff0c\u6bd5\u64ce\u5929\u7684\u5f71\u5b50\u540e\u6765\u5b8c\u5168\u88ab\u94c1\u955c\u5fc3\u7684\u5f71\u5b50\u538b\u4f4f\u4e86\u3002\u5979\u60f3\u5f97\u6700\u591a\u7684\u8fd8\u662f \u94c1\u955c\u5fc3\uff0c\u8fde\u81ea\u5df1\u4e5f\u83ab\u540d\u5176\u5999\u3002\u5440\uff0c\u5979\u81ea\u5df1\u4e0d\u77e5\uff0c\u5979\u53ef\u662f\u5728\u6210\u957f\u4e2d\u7684\u5c11\u5973\u4e86\uff0c\u5f20\u4e39\u67ab\u3001\u53f6\u5b97\u7559 \u867d\u7136\u201c\u53ef\u7231\u201d\uff0c\u5374\u662f\u6bd4\u5979\u957f\u4e00\u8f88\u7684\u4eba\uff0c\u53ea\u6709\u94c1\u955c\u5fc3\u662f\u548c\u5979\u5e74\u7eaa\u76f8\u82e5\u7684\u4fca\u79c0\u5c11\u5e74\u3002 \u3000\u3000\u53ef\u662f\u4e00\u60f3\u5230\u94c1\u955c\u5fc3\u4e0e\u5f20\u3001\u53f6\u4e8c\u4eba\u7684\u4e0d\u540c\u4e4b\u5904\uff0c\u867d\u7136\u90a3\u53ea\u662f\u6a21\u6a21\u7cca\u7cca\u7684\u611f\u89c9\uff0c\u4e5f\u4ee4\u5979\u611f\u5230 \u5fc3\u5934\u6291\u90c1\u3002\u5440\uff0c\u4e00\u4e2a\u5c11\u5973\u8981\u627e\u5230\u6837\u6837\u5408\u610f\u7684\u4eba\uff0c\u90a3\u53ef\u662f\u5e76\u4e0d\u5bb9\u6613\u7684\u554a\u3002 \u3000\u3000\u8fc7\u4e86\u4e24\u65e5\uff0c\u53f0\u5dde\u6765\u4e86\u4e00\u961f\u6e14\u6c11\uff0c\u7ea6\u6709\u767e\u4eba\uff0c\u90fd\u662f\u6210\u6d77\u5c71\u4e0e\u77f3\u6587\u7ea8\u5728\u6e14\u6751\u5c45\u4f4f\u4e4b\u65f6\u8bad\u7ec3\u51fa \u6765\u7684\u3002\u6e14\u6c11\u5230\u6765\uff0c\u8bf4\u8d77\u53f0\u5dde\u57ce\u4e2d\u5df2\u6210\u7acb\u4e86\u56e2\u7ec3\uff0c\u5c31\u662f\u7f3a\u4e4f\u6307\u6325\u7684\u4eba\u624d\uff0c\u53f6\u5b97\u7559\u4fbf\u53eb\u6210\u6d77\u5c71\u56de \u53bb\uff0c\u94c1\u955c\u5fc3\u4e5f\u60f3\u56de\u53bb\uff0c\u5374\u7ed9\u53f6\u5b97\u7559\u7559\u4e0b\u4e86\uff0c\u5c31\u53eb\u4ed6\u5e26\u90a3\u961f\u6e14\u6c11\uff0c\u6574\u7f16\u4e3a\u6297\u502d\u519b\u7684\u4e00\u4e2a\u652f\u961f\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u5230\u4e86\u8425\u5730\u4e4b\u540e\uff0c\u597d\u51e0\u6b21\u8bf7\u547d\u51fa\u51fb\uff0c\u53f6\u5b97\u7559\u603b\u4e0d\u5141\u8bb8\uff0c\u94c1\u955c\u5fc3\u9887\u4e3a\u70e6\u8e81\uff0c\u79c1\u4e0b\u91cc\u5bf9 \u4e8e\u627f\u73e0\u57cb\u6028\u9053\uff1a\u201c\u4e49\u519b\u4e45\u56f0\u5c71\u4e2d\uff0c\u5403\u7684\u7a7f\u7684\uff0c\u90fd\u5f88\u56f0\u96be\uff0c\u4e0d\u6562\u51fa\u51fb\uff0c\u5c82\u975e\u81ea\u53d6\u8d25\u4ea1\uff1f\u518d\u8bf4\u54b1 \u4eec\u5230\u6b64\uff0c\u4e3a\u7684\u662f\u6253\u502d\u5bc7\uff0c\u5982\u4eca\u6765\u4e86\u534a\u4e2a\u6708\u4e86\uff0c\u8fd8\u95f7\u5728\u8fd9\u513f\uff0c\u6709\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u53f6 \u5927\u54e5\u4e0d\u5141\u51fa\u51fb\uff0c\u5fc5\u6709\u4ed6\u7684\u9053\u7406\u3002\u201d\u94c1\u955c\u5fc3\u51b7\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u9053\u7406\uff1f\u6211\u770b\u4ed6\u662f\u60e7\u6015\u502d\u5bc7\u3002\u201d\u4e8e\u627f \u73e0\u4e00\u5411\u4f69\u670d\u94c1\u955c\u5fc3\u7684\u89c1\u8bc6\uff0c\u4f46\u6b64\u6b21\u542c\u4ed6\u8a00\u8bed\u4e4b\u4e2d\u5bf9\u53f6\u5b97\u7559\u5927\u6709\u8511\u89c6\u4e4b\u610f\uff0c\u5fc3\u4e2d\u5374\u597d\u751f\u4e0d\u5feb\uff0c \u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53ea\u662f\u4f60\u6709\u8c0b\u7565\uff0c\u522b\u4eba\u5c31\u6ca1\u6709\u8c0b\u7565\u4e86\u4e48\uff1f\u5f2f\u5f13\u6b32\u5c04\u5357\u5c71\u864e\uff0c\u78e8\u5251\u601d\u9664\u5317\u6d77\u86df\u3002\u6297 \u502d\u4e0d\u662f\u5f92\u901e\u4e00\u65f6\u4e4b\u5feb\uff0c\u8fd9\u662f\u4f60\u8bf4\u8fc7\u7684\u3002\u4e5f\u8bb8\u53f6\u5927\u54e5\u73b0\u5728\u505a\u7684\u5c31\u662f\u2018\u5f2f\u5f13\u78e8\u5251\u2019\u7684\u529f\u592b\u5462\uff01\u201d \u94c1\u955c\u5fc3\u89c1\u4e8e\u627f\u73e0\u6120\u6012\uff0c\u53c8\u62ff\u81ea\u5df1\u8bf4\u8fc7\u7684\u8bdd\u66ff\u53f6\u5b97\u7559\u8fa9\u89e3\uff0c\u5f53\u4e0b\u4e0d\u518d\u8a00\u8bed\uff0c\u4f46\u5fc3\u4e2d\u5374\u662f\u4e0d\u670d\uff0c \u60f3\u9053\uff1a\u201c\u6211\u719f\u8bfb\u5175\u4e66\uff0c\u53f6\u5b97\u7559\u5c82\u80fd\u4e0e\u6211\u76f8\u6bd4\u3002\u201d \u3000\u3000\u53f6\u5b97\u7559\u867d\u7136\u6309\u5175\u4e0d\u52a8\uff0c\u4f46\u6bcf\u65e5\u90fd\u6d3e\u6709\u63a2\u5b50\u4e0b\u5c71\u6253\u63a2\u519b\u60c5\uff0c\u8fd9\u65e5\u63a2\u5b50\u56de\u6765\u62a5\u9053\uff1a\u502d\u5bc7\u5927\u4e3e \u641c\u5c71\uff0c\u5175\u5206\u4e09\u8def\uff0c\u73b0\u5728\u5df2\u5230\u4e86\u5c71\u811a\u4e86\u3002\u53f6\u5b97\u7559\u975e\u5e38\u9547\u5b9a\uff0c\u9053\uff1a\u201c\u654c\u4eba\u722c\u4e0a\u5c71\u6765\uff0c\u6700\u5c11\u4e5f\u5f97\u534a \u65e5\uff0c\u54b1\u4eec\u5148\u770b\u770b\u654c\u4eba\u6765\u52bf\uff0c\u518d\u5546\u91cf\u5982\u4f55\u5e94\u4ed8\u5427\u3002\u201d\u5e26\u94c1\u955c\u5fc3\u3001\u4e8e\u627f\u73e0\u7b49\u4e0a\u9ad8\u5cf0\u773a\u671b\u654c\u60c5\uff0c\u94c1 \u955c\u5fc3\u3001\u4e8e\u627f\u73e0\u90fd\u5177\u6709\u4e0a\u4e0a\u7684\u8f7b\u529f\uff0c\u94c1\u955c\u5fc3\u8fd8\u6545\u610f\u5356\u5f04\u672c\u9886\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5c31\u767b\u4e0a\u9ad8\u5cf0\uff0c\u53f6\u5b97\u7559 \u4e5f\u5c45\u7136\u80fd\u591f\u4ea6\u6b65\u4ea6\u8d8b\uff0c\u548c\u94c1\u3001\u4e8e\u4e8c\u4eba\u540c\u65f6\u5230\u8fbe\uff0c\u4e1d\u6beb\u4e0d\u89c1\u9762\u7ea2\u6c14\u5598\uff0c\u94c1\u955c\u5fc3\u6697\u6697\u4f69\u670d\uff0c\u628a\u8f7b \u89c6\u4ed6\u7684\u5fc3\u60c5\u53bb\u4e86\u51e0\u5206\u3002 \u3000\u3000\u53ea\u89c1\u502d\u5bc7\u4ece\u4e1c\u897f\u5317\u4e09\u9762\u767b\u5c71\uff0c\u4e1c\u5317\u4e24\u9762\uff0c\u961f\u4f0d\u5a49\u8712\u6709\u5982\u957f\u86c7\uff0c\u5c18\u571f\u853d\u5929\uff0c\u91ce\u517d\u5954\u8d70\uff0c\u897f \u9762\u4e00\u8def\uff0c\u5be5\u5be5\u843d\u843d\uff0c\u770b\u6765\u53ea\u6709\u4e09\u4e94\u767e\u4eba\uff0c\u961f\u4f0d\u4e0a\u7a7a\uff0c\u6709\u4e00\u7fa4\u98de\u9e1f\uff0c\u8d8a\u98de\u8d8a\u9ad8\uff0c\u8f6c\u77ac\u4e0d\u89c1\u3002\u770b \u4e86\u534a\u664c\uff0c\u5927\u5bb6\u56de\u5230\u5e10\u5e55\u5546\u8bae\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u7528\u5175\u4e4b\u6cd5\uff0c\u5341\u5219\u56f4\u4e4b\uff0c\u4e94\u5219\u653b\u4e4b\uff0c\u500d\u5219\u5206\u4e4b\uff0c\u654c\u5219\u80fd\u80dc\u4e4b\uff0c\u5c11\u5219\u80fd \u9003\u4e4b\uff0c\u4e0d\u82e5\u5219\u80fd\u907f\u4e4b\u3002\u8fd9\u662f\u5b59\u5b50\u5175\u6cd5\u4e2d\u653b\u8c0b\u7bc7\u6240\u8bb2\u7684\u6cd5\u5219\u3002\u610f\u601d\u662f\u8bf4\uff0c\u6709\u5341\u500d\u4f18\u52bf\u7684\u5175\u529b\u5c31 \u5305\u56f4\u654c\u4eba\uff0c\u6709\u4e94\u500d\u4f18\u52bf\u7684\u5175\u529b\u5c31\u8fdb\u653b\u654c\u4eba\uff0c\u53ea\u6709\u4e00\u500d\u4f18\u52bf\u7684\u5175\u529b\u5c31\u8981\u5206\u6563\u654c\u4eba\uff0c\u540c\u654c\u4eba\u5175\u529b \u76f8\u7b49\u5c31\u8981\u80fd\u6218\u80dc\u4ed6\uff0c\u6bd4\u654c\u4eba\u5175\u529b\u5c11\u5c31\u8981\u80fd\u9000\u5374\uff0c\u6bd4\u654c\u4eba\u519b\u961f\u5f31\u5c31\u8981\u80fd\u907f\u514d\u51b3\u6218\u3002\u5b59\u5b50\u5175\u6cd5\uff0c \u90a3\u662f\u7edd\u5bf9\u6ca1\u6709\u9519\u7684\u3002\u201d\u4e49\u519b\u7684\u5934\u76ee\u542c\u5f97\u83ab\u540d\u5176\u5999\uff0c\u5927\u5bb6\u90fd\u77a7\u7740\u94c1\u955c\u5fc3\uff0c\u4e0d\u61c2\u4ed6\u4f55\u6545\u5728\u519b\u60c5\u7d27 \u6025\u4e4b\u65f6\uff0c\u5c45\u7136\u8fd8\u6709\u95f2\u5fc3\u201c\u80cc\u4e66\u201d\uff1f \u3000\u3000\u6709\u4eba\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u5230\u5e95\u662f\u8bfb\u4e66\u4eba\uff0c\u80cc\u5f97\u8fd9\u6837\u719f\u3002\u201d\u6709\u4eba\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u8c01\u7684\u5b59\u5b50\uff0c\u6709 \u591a\u5927\u5e74\u7eaa\uff1f\u4e3a\u4ec0\u4e48\u5b59\u5b50\u8bb2\u7684\u8bdd\u5c31\u6ca1\u6709\u9519\uff1f\u90a3\u4e48\u8001\u5b50\u8bb2\u7684\u8bdd\u5c82\u4e0d\u662f\u66f4\u6ca1\u6709\u9519\u4e86\u3002\u201d\u94c1\u955c\u5fc3\u50b2\u7136 \u4e00\u7b11\uff0c\u9053\uff1a\u201c\u73b0\u5728\u502d\u5bc7\u653b\u5c71\u7684\u5175\u529b\u6bd4\u54b1\u4eec\u5927\u5f97\u591a\uff0c\u82e5\u7136\u54b1\u4eec\u4e5f\u5206\u5175\u62b5\u6321\uff0c\u90a3\u662f\u5fc5\u8d25\u65e0\u7591\u7684 \u4e86\u3002\u4f46\u502d\u5bc7\u897f\u8def\u7684\u5175\u529b\u8584\u5f31\uff0c\u54b1\u4eec\u82e5\u628a\u5175\u529b\u90fd\u96c6\u4e2d\u8d77\u6765\u5bf9\u4ed6\u7684\u897f\u8def\uff0c\u53ef\u80fd\u6bd4\u4ed6\u591a\u51fa\u4e00\u500d\uff0c\u5c31 \u53ef\u7528\u5230\u5b59\u5b50\u5175\u6cd5\u4e0a\u500d\u5219\u5206\u4e4b\u7684\u9053\u7406\u4e86\u3002\u6211\u8bf4\u54b1\u4eec\u5148\u6d88\u706d\u502d\u5bc7\u7684\u4e2d\u8def\uff0c\u7136\u540e\u6253\u4ed6\u7684\u4e1c\u8def\uff0c\u4ed6\u7684 \u4e1c\u8def\u5175\u529b\u5927\u7ea6\u548c\u54b1\u4eec\u76f8\u7b49\uff0c\u53ef\u4ee5\u7528\u5b59\u5b50\u5175\u6cd5\u4e0a\u2018\u654c\u5219\u80dc\u4e4b\u2019\u7684\u9053\u7406\u5c06\u4ed6\u6253\u8d25\u3002\u201d\u90a3\u5e08\u7237 \u201c\u54e6\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u539f\u6765\u4f60\u8bf4\u7684\u662f\u5404\u4e2a\u51fb\u7834\uff0c\u5de6\u4e00\u53e5\u5b59\u5b50\u5175\u6cd5\uff0c\u53f3\u4e00\u53e5\u5b59\u5b50\u5175\u6cd5\uff0c\u5012\u628a\u6211\u5f04 \u7cca\u6d82\u4e86\u3002\u201d \u3000\u3000\u53f6\u5b97\u7559\u9053\uff1a\u201c\u54b1\u662f\u4e00\u4e2a\u7c97\u4eba\uff0c\u4e0d\u61c2\u4ec0\u4e48\u5b59\u5b50\u5175\u6cd5\uff0c\u8001\u5b50\u5175\u6cd5\uff0c\u4f9d\u6211\u8bf4\u502d\u5bc7\u6765\u4e86\uff0c\u54b1\u4eec\u5c31 \u7ed9\u4ed6\u6253\u78e8\u78e8\u8f6c\u7740\u73a9\u513f\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ec0\u4e48\u53eb\u505a\u6253\u78e8\u78e8\uff1f\u201d\u53f6\u5b97\u7559\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u9a74\u5b50\u62c9\u78e8 \u5417\uff1f\u9a74\u5b50\u8ddf\u7740\u78e8\u8dd1\uff0c\u8f6c\u6765\u8f6c\u53bb\uff0c\u8f6c\u5f97\u5934\u660f\u773c\u82b1\uff0c\u4f60\u653e\u4e86\u5b83\u5b83\u8fd8\u662f\u6253\u8f6c\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u548c \u6253\u502d\u5bc7\u6709\u4ec0\u4e48\u5e72\u7cfb\uff1f\u201d\u53f6\u5b97\u7559\u9053\uff1a\u201c\u54c8\uff0c\u5927\u6709\u5e72\u7cfb\u3002\u54b1\u4eec\u8981\u628a\u502d\u5bc7\u53d8\u6210\u7b28\u9a74\uff0c\u5f15\u5b83\u8ddf\u7740\u54b1\u4eec \u6ee1\u5c71\u4e71\u8f6c\uff0c\u54b1\u4eec\u4e0d\u548c\u4ed6\u6253\u4ed7\uff0c\u5374\u548c\u4ed6\u515c\u5708\u5b50\u3001\u6349\u8ff7\u85cf\uff0c\u54b1\u4eec\u5730\u5f62\u6bd4\u4ed6\u719f\uff0c\u8dd1\u5c71\u8def\u6bd4\u4ed6\u5feb\uff0c\u51c6 \u80fd\u628a\u4ed6\u7d2f\u6b7b\u3002\u201d\u53f6\u5b97\u7559\u8bb2\u7684\u90fd\u662f\u4fd7\u8bdd\uff0c\u660e\u767d\u6613\u61c2\uff0c\u5927\u5c0f\u5934\u9886\u542c\u5f97\u7709\u98de\u8272\u821e\uff0c\u8f70\u7136\u53eb\u9053\uff1a\u201c\u5bf9 \u5475\uff0c\u5c31\u7167\u7edf\u9886\u8bb2\u7684\u505a\uff0c\u628a\u502d\u5bc7\u7d2f\u6b7b\u3002\u201d\u94c1\u955c\u5fc3\u51b7\u7b11\u9053\uff1a\u201c\u5386\u4ee3\u7684\u5175\u4e66\u4ece\u6765\u6ca1\u6709\u8bb2\u8fc7\u8fd9\u6837\u6253\u6cd5 \u7684\uff0c\u54b1\u4eec\u7cae\u8349\u53c8\u4e0d\u591f\uff0c\u522b\u5f04\u5f97\u81ea\u5df1\u5148\u7d2f\u6b7b\u4e86\u3002\u201d\u6709\u4eba\u53eb\u9053\uff1a\u201c\u502d\u5bc7\u8fdc\u9053\u6765\u653b\uff0c\u4ed6\u53c8\u80fd\u5e26\u591a\u5c11 \u7cae\u8349\uff1f\u54b1\u4eec\u9760\u5c71\u5403\u5c71\uff0c\u9760\u6c34\u5403\u6c34\uff0c\u53c8\u6709\u8001\u767e\u59d3\u5e2e\u54b1\u4eec\uff0c\u6015\u4ec0\u4e48\u548c\u4ed6\u78e8\uff1f\u201d\u94c1\u955c\u5fc3\u4e0d\u7406\u95f2\u4eba\u8bf4 \u8bdd\uff0c\u9762\u5bf9\u53f6\u5b97\u7559\u95ee\u9053\uff1a\u201c\u82e5\u7167\u4f60\u6240\u8bf4\u7684\u6837\u5b50\u548c\u502d\u5bc7\u6349\u8ff7\u85cf\u5f97\u82b1\u591a\u5c11\u65f6\u5019\uff1f\u201d\u53f6\u5b97\u7559\u9053\uff1a\u201c\u8fd9 \u4e2a\u6ca1\u51c6\u513f\uff0c\u5341\u5929\u4e0d\u5b9a\uff0c\u534a\u6708\u4e0d\u5b9a\uff0c\u4e00\u4e2a\u6708\u4e5f\u4e0d\u5b9a\u3002\u201d\u94c1\u955c\u5fc3\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u54b1\u4eec\u4ec0\u4e48 \u65f6\u5019\u624d\u80fd\u591f\u628a\u502d\u5bc7\u90fd\u8d76\u4e0b\u6d77\u53bb\uff1f\u4f60\u6015\u548c\u502d\u5bc7\u6253\u786c\u4ed7\uff0c\u5c3d\u662f\u907f\u4ed6\uff0c\u5916\u9762\u7684\u767e\u59d3\u53d7\u82e6\u53d7\u96be\u4f60\u5c31\u4e0d \u7ba1\u4e86\uff01\u4f60\u548c\u502d\u5bc7\u6349\u8ff7\u85cf\u53bb\u5427\uff0c\u6211\u8981\u6253\uff01\u201d\u4e49\u519b\u5934\u76ee\u5168\u90e8\u53d8\u8272\uff0c\u53f6\u5b97\u7559\u6025\u5fd9\u7528\u773c\u8272\u6b62\u4f4f\u4f17\u4eba\uff0c \u6709\u4eba\u5df2\u9a82\u51fa\u58f0\u9053\uff1a\u201c\u54b1\u4eec\u54ea\u4e00\u4e2a\u4e0d\u66fe\u51fa\u6b7b\u5165\u751f\uff0c\u548c\u502d\u5bc7\u786c\u62fc\u8fc7\u6765\uff0c\u4f60\uff0c\u4f60\u2026\u2026\u201d\u53f6\u5b97\u7559\u6025\u6b62 \u7740\u4f17\u4eba\u9053\uff1a\u201c\u94c1\u516c\u5b50\u4e5f\u662f\u4e00\u756a\u4e3a\u56fd\u4e3a\u6c11\u4e4b\u5fc3\uff0c\u54b1\u4eec\u4e0d\u8981\u5435\u95f9\u3002\u94c1\u516c\u5b50\u60f3\u628a\u502d\u5bc7\u5206\u8def\u5148\u7834\uff0c\u4e5f \u6709\u9053\u7406\u3002\u4e0d\u8fc7\u502d\u5bc7\u6ed1\u4f3c\u72d0\u72f8\uff0c\u987b\u9632\u6709\u8bc8\u554a\uff01\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u7ba1\u4ed6\u6ed1\u4f3c\u72d0\u72f8\uff0c\u72e0\u5982\u864e\u8c79\uff0c\u6211\u4e5f \u4e0d\u4ff1\u3002\u6211\u5e26\u6211\u8fd9\u961f\u4eba\u53bb\u6253\u3002\u201d \u3000\u3000\u53f6\u5b97\u7559\u82e6\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u6d3e\u4eba\u52a9\u4f60\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u4e0d\u7528\uff0c\u4f60\u81ea\u53bb\u548c\u502d\u5bc7\u6349\u8ff7\u85cf \u5427\u3002\u201d\u53f6\u5b97\u7559\u9001\u94c1\u955c\u5fc3\u51fa\u5e10\uff0c\u5ffd\u7136\u7d27\u63e1\u94c1\u955c\u5fc3\u7684\u624b\u9053\uff1a\u201c\u94c1\u516c\u5b50\uff0c\u4f60\u5b9a\u8981\u786c\u6253\uff0c\u6211\u4e5f\u4e0d\u4fbf\u62e6 \u963b\uff0c\u4f46\u4f60\u53ef\u5f97\u5c0f\u5fc3\u4e00\u4ef6\u4e8b\uff01\u201d\u8bf4\u5f97\u5341\u5206\u8bda\u6073\uff0c\u94c1\u955c\u5fc3\u4e5f\u7981\u4e0d\u4f4f\u5fc3\u5934\u4e00\u52a8\uff0c\u9759\u542c\u4ed6\u8bf4\u4ec0\u4e48\u3002\u6b63 \u662f\uff1a \u3000\u3000\u5175\u4e66\u6d3b\u8bfb\u65b9\u80fd\u7528\uff0c\u8349\u91ce\u82f1\u8c6a\u4ea6\u5c06\u624d\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u7b2c\u56db\u96c6\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u5341\u4e09\u56de\u3000\u7a7a\u8bfb\u5175\u4e66\u3000\u6218\u573a\u60ca\u4e2d\u4f0f\u3000\u51fa\u8eab\u7530\u4ea9\u3000\u8349\u846c\u6709\u5947\u624d \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u5341\u4e09\u56de\u3000\u7a7a\u8bfb\u5175\u4e66\u3000\u6218\u573a\u60ca\u4e2d\u4f0f\u3000\u51fa\u8eab\u7530\u4ea9\u3000\u8349\u846c\u6709\u5947\u624d \u3000\u3000\u53f6\u5b97\u7559\u9053\uff1a\u201c\u502d\u5bc7\u5584\u4e8e\u7528\u8bc8\uff0c\u987b\u5f97\u63d0\u9632\u4ed6\u85cf\u6709\u4f0f\u5175\u3002\u201d\u94c1\u955c\u5fc3\u6697\u9053\uff1a\u201c\u8fd9\u70b9\u519b\u4e8b\u4e0a\u7684\u5e38 \u8bc6\uff0c\u4f55\u7528\u4f60\u6765\u63d0\u9192\u3002\u800c\u4e14\u6211\u5728\u9ad8\u5cf0\u4e0a\u4e86\u671b\uff0c\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u8fd9\u8def\u502d\u5bc7\u6700\u591a\u4e0d\u8fc7\u4e94\u516d\u767e\u4eba\uff0c\u54ea \u91cc\u6709\u4ec0\u4e48\u4f0f\u5175\u3002\u201d\u6beb\u4e0d\u5728\u610f\u5730\u7b54\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u7406\u4f1a\u5f97\u3002\u201d\u53f6\u5b97\u7559\u53c8\u9053\uff1a\u201c\u884c\u519b\u5e03\u9635\u4e4b\u65f6\uff0c\u4f60 \u8fd9\u961f\u6700\u597d\u4e0d\u8981\u90fd\u6446\u5728\u4e00\u8d77\uff0c\u6253\u4ed7\u8bb2\u7a76\u7684\u662f\u80c6\u5927\u5fc3\u7ec6\uff0c\u8981\u60f3\u6cd5\u5b50\u6253\u80dc\uff0c\u4e5f\u8981\u9632\u6b62\u6253\u8d25\u4ed7\uff0c\u6211\u529d \u4f60\u5148\u6d3e\u4e00\u961f\u4f5c\u4e3a\u5148\u884c\uff0c\u8bd5\u63a2\u654c\u4eba\u7684\u5b9e\u529b\uff0c\u4f60\u81ea\u5df1\u7387\u9886\u4e2d\u519b\uff0c\u5e76\u7531\u4e8e\u76f8\u516c\u7387\u4e00\u961f\u4eba\u5728\u540e\u9762\u7b56 \u5e94\uff0c\u8fd9\u6837\u5c31\u7b97\u9047\u7740\u4f0f\u5175\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u88ab\u56f0\u3002\u201d\u94c1\u955c\u5fc3\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u867d\u7136\u4e0d\u624d\uff0c\u5175\u6cd5\u8fd8\u7a0d \u61c2\u4e00\u4e8c\uff0c\u4e0d\u52b3\u543e\u5144\u6307\u70b9\uff0c\u201d\u53f6\u5b97\u7559\u5c1a\u5f85\u8bf4\u8bdd\uff0c\u638c\u65d7\u724c\u7684\u5934\u76ee\u5df1\u8dd1\u6765\u8bf7\u4ed6\u901f\u56de\u5927\u5be8\u4e3b\u6301\uff0c\u53f6\u5b97 \u7559\u4e34\u8d70\u65f6\u8fd8\u6bb7\u6bb7\u5631\u5490\u9053\uff1a\u201c\u82e5\u679c\u4e07\u4e00\u4e2d\u4f0f\uff0c\u901f\u5411\u4e1c\u5357\u6492\u9000\u3002\u201d\u94c1\u955c\u5fc3\u6de1\u6de1\u5730\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a \u201c\u77e5\u9053\u5566\u3002\u201d \u3000\u3000\u94c1\u955c\u5fc3\u5c06\u5168\u961f\u4e8c\u767e\u591a\u4eba\u90fd\u805a\u5728\u4e00\u8d77\uff0c\u4e0b\u4ee4\u4ece\u901f\u5954\u81f3\u897f\u9762\u5c71\u5773\uff0c\u7acb\u5373\u5e94\u6218\u3002\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u53f6\u5927\u54e5\u521a\u624d\u8bf4\u7684\u2026\u2026\u201d\u94c1\u955c\u5fc3\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u61c2\u4ec0\u4e48\uff0c\u524d\u6015\u864e\uff0c\u540e\u6015\u72fc\uff0c\u8fd9\u6253\u4ec0\u4e48\u4ed7\uff1f\u6211\u65e9 \u770b\u6e05\u695a\u4e86\u6ca1\u6709\u4f0f\u5175\uff0c\u502d\u5bc7\u6700\u591a\u6709\u4e94\u767e\u4eba\u3002\u54b1\u4eec\u6709\u4e8c\u767e\u591a\u4eba\uff0c\u4e00\u4e2a\u654c\u4ed6\u4e24\u4e2a\uff0c\u5bf9\u4ed8\u5f97\u4e86\u3002\u53ef\u7b11 \u53f6\u5b97\u7559\u8fd8\u529d\u54b1\u4eec\u5206\u5f00\u4e09\u961f\uff0c\u54b1\u4eec\u4eba\u6570\u5df2\u5c11\uff0c\u518d\u5206\u6210\u4e09\u961f\uff0c\u8fd8\u80fd\u6253\u5417\uff1f\u201d\u94c1\u955c\u5fc3\u4e00\u5fc3\u60f3\u5927\u83b7\u5168 \u80dc\uff0c\u53ef\u5b8c\u5168\u6ca1\u987e\u8651\u5230\u4f1a\u6253\u8d25\u4ed7\u3002 \u3000\u3000\u5c71\u8def\u5d0e\u5c96\u96be\u8d70\uff0c\u94c1\u955c\u5fc3\u4e2a\u4ee4\u52a0\u7d27\u884c\u519b\uff0c\u8d70\u4e86\u4e24\u4e2a\u591a\u65f6\u8fb0\uff0c\u8d76\u81f3\u897f\u9762\u5c71\u5773\uff0c\u9664\u4e86\u94c1\u3001\u4e8e\u4e8c \u4eba\u4e4b\u5916\uff0c\u4eba\u4eba\u90fd\u5df2\u6709\u70b9\u6c14\u5598\uff0c\u5954\u51fa\u5c71\u53e3\uff0c\u53ea\u89c1\u4e00\u961f\u502d\u5bc7\uff0c\u4e09\u4e09\u4e94\u4e94\u5730\u641c\u7d22\u767b\u5c71\uff0c\u94c1\u955c\u5fc3\u5927\u559d \u4e00\u58f0\uff0c\u94f6\u5251\u4e00\u6325\uff0c\u5f53\u524d\u51b2\u4e0b\uff0c\u8fd9\u961f\u6e14\u6c11\u7ec4\u6210\u7684\u961f\u4f0d\uff0c\u5e73\u65e5\u6df1\u53d7\u502d\u51a0\u7684\u7978\u5bb3\uff0c\u8fd9\u6b21\u7b2c\u4e00\u6b21\u4e0e\u502d \u5bc7\u6b63\u5f0f\u6253\u4ed7\uff0c\u53c8\u89c1\u4e3b\u5c06\u5df2\u7136\u51b2\u4e0b\uff0c\u4e2a\u4e2a\u70ed\u8840\u6cb8\u817e\uff0c\u594b\u4e0d\u987e\u8eab\uff0c\u5fd8\u6389\u75b2\u52b3\uff0c\u7d27\u8ddf\u7740\u94c1\u955c\u5fc3\u4e00\u51b2 \u800c\u4e0b\uff0c\u53ea\u89c1\u94c1\u955c\u5fc3\u5251\u5149\u5230\u5904\uff0c\u5982\u6c64\u6cfc\u96ea\uff0c\u502d\u5bc7\u6216\u88ab\u4ed6\u65a9\u65ad\u624b\u8db3\uff0c\u6216\u88ab\u4ed6\u8e22\u4e0b\u5c71\u5761\uff0c\u8f6c\u77ac\u4e4b \u95f4\uff0c\u94c1\u955c\u5fc3\u4e00\u4eba\u5df2\u6740\u4e86\u5341\u51e0\u540d\u502d\u51a0\uff0c\u767b\u65f6\u6e83\u4e0d\u6210\u519b\uff0c\u88ab\u4e49\u519b\u961f\u4f0d\u4e00\u51b2\uff0c\u7eb7\u7eb7\u9003\u8d70\uff0c\u94c1\u955c\u5fc3\u54c8 \u54c8\u5927\u7b11\uff0c\u5bf9\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5982\u4f55\uff1f\u201d\u4e8e\u627f\u73e0\u6599\u4e0d\u5230\u502d\u5bc7\u7adf\u662f\u52a0\u6b64\u4e0d\u582a\u4e00\u51fb\uff0c\u6740\u5f97\u9ad8\u5174\uff0c\u4e5f\u968f\u7740 \u94c1\u955c\u5fc3\u8ffd\u6740\u502d\u5bc7\u3002\u8ffd\u5230\u534a\u5c71\uff0c\u5c71\u8305\u91ce\u8349\uff0c\u9ad8\u903e\u4eba\u5934\uff0c\u502d\u839e\u7eb7\u7eb7\u94bb\u5165\u8305\u8349\u4e2d\u63a9\u85cf\uff0c\u94c1\u955c\u5fc3\u53eb \u9053\uff1a\u201c\u9003\u8fdb\u7a9d\uff0c\u4e5f\u8981\u628a\u4ed6\u638f\u51fa\u6765\uff01\u201d\u4ed7\u5251\u5f00\u8def\uff0c\u8fd9\u961f\u4e49\u519b\uff0c\u90fd\u6ca1\u6709\u6253\u4ed7\u7684\u7ecf\u9a8c\uff0c\u6740\u5f97\u6027\u8d77\uff0c \u8854\u5c3e\u6025\u8ffd\uff0c\u4e5f\u90fd\u51b2\u5165\u4e86\u8305\u8349\u4e1b\u4e2d\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u70ae\u54cd\uff0c\u502d\u5bc7\u4f0f\u5175\u56db\u8d77\uff0c\u4ece\u8305\u8349\u4e1b\u4e2d\u7eb7\u7eb7\u6740\u4e86\u51fa \u6765\uff0c\u767b\u65f6\u628a\u94c1\u955c\u5fc3\u7684\u961f\u4f0d\u56f4\u7740\uff0c\u6709\u4e24\u4e2a\u8eab\u6750\u9ad8\u5927\u7684\u502d\u5bc7\uff0c\u62a2\u4e0a\u6765\u6325\u5200\u4fbf\u65a9\u94c1\u955c\u5fc3\uff0c\u94c1\u955c\u5fc3\u5530 \u5530\u4e24\u5251\uff0c\u7adf\u7136\u88ab\u90a3\u4e24\u4e2a\u502d\u5bc7\u4f7f\u5200\u67b6\u4f4f\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u502d\u5bc7\u4e00\u4e2a\u662f\u5728\u8d21\u8239\u4e2d\u4f1a\u8fc7\u7684\u4e03\u6bb5\u6b66\u58eb\u5927\u536b\u95e8\uff0c\u53e6\u4e00\u4e2a\u4e5f\u662f\u4e03\u6bb5\u6b66\u58eb\u677f\u7530\u8363\u7537\uff0c\u4e24 \u4eba\u90fd\u662f\u4f7f\u5200\u597d\u624b\uff0c\u94c1\u955c\u5fc3\u88ab\u4ed6\u4eec\u7f20\u7740\uff0c\u6025\u5207\u95f4\u7adf\u8131\u4e0d\u51fa\u8eab\u6765\uff0c\u8fd9\u4e00\u961f\u4f0f\u5175\u6709\u4e00\u5343\u4eba\uff0c\u90fd\u662f\u7cbe \u608d\u7684\u502d\u5bc7\uff0c\u8fde\u4e0a\u5148\u5934\u8bf1\u654c\u7684\u502d\u5bc7\uff0c\u4e0d\u4e0b\u4e00\u5343\u4e94\u767e\u4eba\u4e4b\u591a\uff0c\u6bd4\u94c1\u955c\u5fc3\u7684\u8fd9\u4e00\u961f\u4e49\u519b\uff0c\u591a\u4e0a\u4e86\u4e94 \u516d\u500d\uff0c\u5f3a\u5f31\u60ac\u6b8a\uff0c\u4f17\u5be1\u4e0d\u654c\uff0c\u4e49\u519b\u867d\u7136\u62fc\u547d\u51b2\u6740\uff0c\u5305\u56f4\u7684\u5708\u5b50\u5374\u8d8a\u6765\u8d8a\u7f29\u5c0f\u4e86\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u53c8\u6025\u53c8\u6012\uff0c\u731b\u7136\u5927\u559d\u4e00\u58f0\uff0c\u957f\u5251\u4e00\u5212\uff0c\u4e00\u62db\u201c\u77f3\u7834\u5929\u60ca\u201d\u5206\u5241\u4e8c\u4eba\uff0c\u8fd9\u4e24\u4e2a\u4e03\u6bb5 \u6b66\u58eb\u51e0\u66fe\u89c1\u8fc7\u5982\u6b64\u7cbe\u5999\u7684\u5251\u6cd5\uff0c\u677f\u7530\u8363\u7537\u8eb2\u95ea\u7a0d\u6162\uff0c\u81c2\u4e0a\u5df2\u5148\u4e2d\u4e86\u4e00\u5251\uff0c\u5927\u536b\u95e8\u6a2a\u5200\u6251\u6551\uff0c \u88ab\u94c1\u955c\u5fc3\u5251\u67c4\u4e00\u649e\uff0c\u4e5f\u9669\u9669\u8dcc\u5012\uff0c\u4f46\u8fd9\u4e24\u4e2a\u502d\u5bc7\u51f6\u608d\u4e4b\u6781\uff0c\u88ab\u6253\u4f24\u4e86\u4ecd\u7136\u4e0d\u9000\uff0c\u4ece\u65c1\u8fb9\u53c8\u6765 \u4e86\u6570\u4eba\uff0c\u628a\u94c1\u955c\u5fc3\u7d27\u7d27\u56f4\u7740\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u89c1\u52bf\u4e0d\u597d\uff0c\u523a\u4f24\u4e86\u4e24\u4e2a\u502d\u5bc7\uff0c\u98de\u5954\u6765\u6551\uff0c\u4eba\u672a\u5230\u6697\u5668\u5148\u53d1\uff0c\u4e00\u626c\u624b\u5c31\u662f\u4e94\u6735\u91d1 \u82b1\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e00\u7247\u58f0\u54cd\uff0c\u4e94\u6735\u91d1\u82b1\u660e\u660e\u90fd\u6253\u4e2d\u4e86\uff0c\u5374\u53ea\u6709\u4e24\u4eba\u5012\u4e0b\uff0c\u4e8e\u627f\u73e0\u6014\u4e86\u4e00 \u6014\uff0c\u7acb\u523b\u9192\u609f\uff0c\u8fd9\u4e9b\u502d\u5bc7\u6562\u60c5\u8eab\u4e0a\u90fd\u62ab\u6709\u8f6f\u7532\uff0c\u6240\u4ee5\u6709\u4e09\u6735\u91d1\u82b1\u6253\u5411\u7a74\u9053\u7684\uff0c\u90fd\u4e0d\u80fd\u4f24\u5bb3\u654c \u4eba\uff0c\u88ab\u6253\u5012\u7684\u90a3\u4e24\u4e2a\u502d\u5bc7\uff0c\u4e43\u662f\u56e0\u4e3a\u88ab\u91d1\u82b1\u7a7f\u8fc7\u5589\u5499\u800c\u81f4\u6b7b\u7684\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u518d\u53d1\u91d1\u82b1\uff0c\u8fd9\u56de\u5168\u662f\u7784\u51c6\u502d\u5bc7\u7684\u5589\u5499\uff0c\u4e94\u6735\u91d1\u82b1\u98de\u51fa\uff0c\u679c\u7136\u53c8\u6740\u4e86\u4e09\u4eba\uff0c\u53ea\u6709\u90a3 \u4e24\u4e2a\u4e03\u6bb5\u6b66\u58eb\u80fd\u591f\u7528\u5200\u78d5\u5f00\uff0c\u4f46\u5927\u536b\u95e8\u56e0\u4e3a\u5206\u795e\u62b5\u6321\u6697\u5668\uff0c\u53c8\u7ed9\u94c1\u955c\u5fc3\u5288\u4e86\u4e00\u5251\uff0c\u867d\u7136\u8eab\u4e0a \u62ab\u7740\u8f6f\u7532\uff0c\u90a3\u6761\u624b\u81c2\u5374\u4e5f\u7ed9\u9707\u5f97\u4e0d\u80fd\u52a8\u5f39\uff0c\u8fd9\u56de\u4e0d\u6562\u518d\u7f20\uff0c\u614c\u5fd9\u9003\u8d70\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u4e4b\u56f4\u867d\u89e3\uff0c\u4f46\u6574\u961f\u4e49\u519b\u9677\u5728\u654c\u4eba\u5305\u56f4\u4e4b\u4e2d\uff0c\u5374\u662f\u96be\u4ee5\u89e3\u56f4\u3002\u94c1\u955c\u5fc3\u3001\u4e8e\u627f\u73e0\u4e8c\u4eba \u6d74\u8840\u82e6\u6597\uff0c\u502d\u5bc7\u5c42\u5c42\u5305\u56f4\uff0c\u6740\u8fc7\u4e00\u5c42\u8fd8\u6709\u4e00\u5c42\uff0c\u4ee5\u4e8c\u4eba\u4e4b\u529b\u5982\u4f55\u80fd\u5c3d\u6740\u5343\u4f59\u502d\u5bc7\uff1f\u800c\u4e14\u4e49\u519b \u8863\u7532\u4e0d\u5168\uff0c\u88ab\u8346\u68d8\u8305\u8349\u52fe\u4f24\u7684\u4e5f\u4e3a\u6570\u4e0d\u5c11\u3002\u94c1\u955c\u5fc3\u54ac\u7259\u53eb\u9053\uff1a\u201c\u6211\u4eca\u65e5\u62fc\u6b7b\u6b64\u95f4\uff0c\u4e5f\u8981\u4e3a\u4f60 \u4eec\u7a81\u56f4\u51b2\u51fa\u3002\u201d\u957f\u5251\u6325\u821e\uff0c\u53f1\u54a4\u6740\u654c\uff0c\u502d\u5bc7\u89c1\u4ed6\u795e\u52c7\u83ab\u6321\uff0c\u7eb7\u7eb7\u8eb2\u95ea\uff0c\u4f46\u5374\u622a\u7740\u5176\u4ed6\u4e49\u519b\uff0c \u94c1\u955c\u5fc3\u53ea\u987e\u51b2\u6740\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u88ab\u502d\u5bc7\u622a\u65ad\uff0c\u5355\u8eab\u9677\u5165\u91cd\u56f4\uff0c\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e8e\u627f\u73e0\u529b\u6297\u502d\u5bc7 \u56f4\u653b\uff0c\u548c\u4e49\u519b\u4e00\u8d77\uff0c\u7ed3\u96c6\u6210\u4e00\u4e2a\u5706\u9635\uff0c\u85c9\u4ee5\u51cf\u5c11\u4f24\u4ea1\uff0c\u94c1\u955c\u5fc3\u51db\u7136\u4e00\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u6211\u53ea\u7ba1\u5411 \u524d\u51b2\u6740\uff0c\u7eb5\u7136\u6211\u6740\u5f97\u51fa\uff0c\u8fd9\u4e8c\u767e\u591a\u4e2a\u5f1f\u5144\u5c82\u975e\u8981\u5168\u4e27\u9635\u4e2d\uff1f\u201d\u6025\u5fd9\u518d\u6740\u56de\u6765\u6551\u5e94\uff0c\u4ed6\u90a3\u53e3\u5251 \u4e0d\u662f\u5b9d\u5251\uff0c\u8fde\u6740\u51e0\u5341\u540d\u502d\u5bc7\uff0c\u5251\u53e3\u4e5f\u5df2\u949d\u4e86\u3002\u94c1\u955c\u5fc3\u53f9\u4e86\u53e3\u6c14\uff0c\u6697\u9053\uff1a\u201c\u6094\u4e0d\u542c\u53f6\u5b97\u7559\u4e4b \u8a00\uff01\u201d\u8fd9\u65f6\u90a3\u5927\u536b\u95e8\u4e0e\u677f\u7530\u8363\u7537\u517b\u597d\u6c14\u529b\uff0c\u53c8\u6765\u7f20\u6597\u3002\u94c1\u955c\u5fc3\u51b2\u6740\u51e0\u6b21\u90fd\u51b2\u4e0d\u5f00\uff0c\u60f3\u56de\u5230\u4e49 \u519b\u9635\u4e2d\u4ea6\u662f\u4e3a\u96be\uff0c\u4f46\u89c1\u5305\u56f4\u5708\u5b50\u8d8a\u7f29\u8d8a\u5c0f\uff0c\u4e49\u519b\u7684\u5706\u9635\u7adf\u88ab\u51b2\u7834\u4e00\u73af\uff0c\u5e78\u6709\u4e8e\u627f\u73e0\u594b\u52c7\u6740 \u654c\uff0c\u5835\u4f4f\u7f3a\u53e3\u3002 \u3000\u3000\u4e49\u519b\u51b2\u4e0d\u51fa\u53bb\uff0c\u94c1\u955c\u5fc3\u5728\u6025\u5207\u4e4b\u95f4\u53c8\u6740\u4e0d\u56de\u6765\uff0c\u6b63\u5728\u4e07\u5206\u5371\u6025\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u6570\u58f0\u54cd\u7bad\u5212 \u8fc7\u957f\u7a7a\uff0c\u53ea\u89c1\u5c71\u5773\u5904\u7a81\u7136\u51b2\u51fa\u4e00\u961f\u4e49\u519b\uff0c\u524d\u6765\u63f4\u6551\uff0c\u88ab\u56f4\u7684\u4e49\u519b\u58eb\u6c14\u5927\u632f\uff0c\u767b\u65f6\u51b2\u7834\u4e86\u4e00\u5c42 \u5305\u56f4\u3002\u502d\u5bc7\u6709\u4e24\u95e8\u571f\u70ae\u62bc\u9635\uff0c\u80fd\u6253\u5f97\u6570\u5343\u4e08\u8fdc\uff0c\u6025\u5fd9\u5f00\u70ae\u5411\u90a3\u961f\u63f4\u519b\u4fbf\u6253\uff0c\u571f\u70ae\u7684\u6740\u4f24\u529b\u867d \u7136\u5927\uff0c\u4f46\u4e00\u70ae\u6253\u51fa\uff0c\u94c1\u7802\u5982\u96e8\uff0c\u63f4\u519b\u8eab\u4f0f\u5730\u4e0a\uff0c\u5374\u4e5f\u88ab\u963b\u4f4f\u4e86\u3002\u94c1\u955c\u5fc3\u53eb\u9053\uff1a\u201c\u4e8e\u5144\uff0c\u6211\u66ff \u4f60\u5f00\u8def\uff0c\u4f60\u6740\u90a3\u4e24\u4e2a\u70ae\u624b\u3002\u201d\u629b\u5f00\u957f\u5251\uff0c\u53cc\u624b\u4e00\u6293\uff0c\u7adf\u7136\u6293\u8d77\u4e24\u4e2a\u502d\u5bc7\uff0c\u5f53\u6210\u5175\u5668\uff0c\u6cfc\u98ce\u822c \u5730\u6253\u51fa\u53bb\uff0c\u90a3\u4e24\u4e2a\u502d\u5bc7\u88ab\u4ed6\u6293\u7740\u7a74\u9053\uff0c\u53cc\u624b\u8fd8\u80fd\u6d3b\u52a8\uff0c\u660f\u660f\u6c8c\u6c8c\uff0c\u4e0d\u8fa8\u5929\u5357\u5730\u5317\uff0c\u624b\u4e2d\u7684\u502d \u5200\u4e5f\u662f\u4e71\u821e\u4e71\u780d\uff0c\u8fd1\u8eab\u7684\u502d\u5bc7\u88ab\u6740\u4f24\u4e0d\u5c11\uff0c\u8fd9\u4e24\u4e2a\u502d\u5bc7\u81ea\u4ea6\u5f88\u5feb\u5730\u4fbf\u4e27\u751f\u5728\u81ea\u5df1\u4eba\u624b\u4e2d\uff0c\u94c1 \u955c\u5fc3\u4f9d\u8fed\u70ae\u5236\uff0c\u4e22\u5f00\u6b7b\u7684\uff0c\u53c8\u6293\u6d3b\u7684\uff0c\u628a\u502d\u5bc7\u4f5c\u4e3a\u4eba\u8d28\uff0c\u5f53\u6210\u6b66\u5668\uff0c\u51b2\u5f00\u4e00\u6761\u8840\u8def\uff0c\u4e8e\u627f\u73e0 \u98de\u8eab\u63a0\u51fa\uff0c\u4e00\u626c\u624b\u4e24\u6735\u91d1\u82b1\uff0c\u5168\u6253\u5165\u4e86\u90a3\u4e24\u4e2a\u70ae\u624b\u7684\u540e\u5934\uff0c\u4e24\u5c0a\u571f\u70ae\u767b\u65f6\u54d1\u7136\u65e0\u58f0\u3002\u4e24\u961f\u4e49 \u519b\u6c47\u5408\u4e00\u8d77\uff0c\u8fd9\u961f\u63f4\u519b\u7684\u9996\u9886\u7adf\u662f\u4e49\u519b\u7684\u526f\u7edf\u9886\u9093\u8302\u4e03\uff0c\u94c1\u955c\u5fc3\u53c8\u559c\u53c8\u6127\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u53f6 \u5927\u54e5\u5462\uff1f\u201d\u9093\u8302\u4e03\u9053\uff1a\u201c\u53f6\u5927\u54e5\u53eb\u6211\u6765\u63a5\u5e94\u4f60\uff0c\u4ed6\u7387\u961f\u5411\u4e1c\u5357\u65b9\u64a4\u9000\uff0c\u6b64\u65f6\u53ea\u6015\u4e5f\u4e0e\u502d\u5bc7\u906d \u9047\u4e86\u3002\u201d\u4e49\u519b\u603b\u5171\u4e0d\u6ee1\u5343\u4eba\uff0c\u94c1\u955c\u5fc3\u4e00\u770b\u9093\u8302\u4e03\u5e26\u6765\u7684\u7ea6\u6709\u56db\u4e94\u767e\u4eba\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u8fd9 \u600e\u4e48\u4f7f\u5f97\uff1f\u4ed6\u5206\u51fa\u4e86\u4e00\u534a\u5175\u529b\uff0c\u5982\u4f55\u80fd\u62b5\u6321\u4e24\u8def\u502d\u5bc7\uff1f\u201d\u9093\u8302\u4e03\u9053\uff1a\u201c\u53f6\u5927\u54e5\u8bf4\uff0c\u54b1\u4eec\u7684\u529b \u91cf\u80fd\u4fdd\u5168\u591a\u5c11\u4fbf\u4fdd\u5168\u591a\u5c11\uff0c\u4ed6\u719f\u6089\u5730\u5f62\uff0c\u4f60\u4eec\u5374\u662f\u521d\u6765\u4e4d\u5230\uff0c\u6240\u4ee5\u5148\u8981\u6551\u51fa\u4f60\u8fd9\u652f\u5175\u529b\uff0c\u53eb \u4f60\u4e0d\u5fc5\u6302\u8651\u4ed6\u3002\u201d\u94c1\u955c\u5fc3\u6127\u6094\u4ea4\u5e76\uff0c\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u9a6c\u4e0a\u5411\u5357\u65b9\u64a4\u9000\u3002\u201d\u63f4\u519b\u867d\u5230\uff0c\u5175\u529b\u8fd8\u662f \u6bd4\u502d\u5bc7\u5c11\u4e00\u534a\uff0c\u94c1\u955c\u5fc3\u594b\u6218\u5f00\u8def\uff0c\u9093\u8302\u4e03\u62bc\u540e\uff0c\u4e8e\u627f\u73e0\u638c\u7ba1\u4e2d\u8def\u4e49\u519b\uff0c\u4e14\u6218\u4e14\u8d70\uff0c\u6df7\u6218\u4e86\u534a \u4e2a\u65f6\u8fb0\uff0c\u8d70\u51fa\u4e86\u90a3\u7247\u8349\u5730\uff0c\u518d\u6df7\u6218\u534a\u4e2a\u65f6\u8fb0\uff0c\u521a\u521a\u8d70\u81f3\u5c71\u53e3\uff0c\u502d\u5bc7\u7d27\u8feb\u4e0d\u820d\uff0c\u94c1\u955c\u5fc3\u5927\u4e3a\u7126 \u6025\uff0c\u770b\u60c5\u5f62\u6fc0\u6218\u534a\u65e5\uff0c\u65b9\u8d70\u51fa\u6570\u91cc\uff0c\u51e0\u65f6\u624d\u80fd\u8d76\u81f3\u4e1c\u5357\u65b9\u7684\u6218\u573a\u4e0e\u53f6\u5b97\u7559\u4f1a\u5408\u3002 \u3000\u3000\u677f\u7530\u8363\u7537\u4e0e\u5927\u536b\u95e8\u8fd9\u4e24\u4e2a\u4e03\u6bb5\u6b66\u571f\u517b\u597d\u4e86\u6c14\u529b\uff0c\u7387\u519b\u5305\u6284\uff0c\u7ed5\u8fc7\u4e49\u519b\u524d\u5934\uff0c\u5c06\u94c1\u955c\u5fc3\u7684 \u5f00\u8def\u90e8\u961f\u5835\u4f4f\uff0c\u94c1\u955c\u5fc3\u5927\u6012\uff0c\u62a2\u8fc7\u4e00\u67c4\u502d\u5200\uff0c\u4e0e\u4ed6\u4eec\u53ae\u6740\uff0c\u8bba\u8d77\u6b66\u529f\uff0c\u94c1\u955c\u5fc3\u672c\u6765\u80fd\u591f\u53d6 \u80dc\uff0c\u4f46\u82e6\u6218\u4e86\u534a\u65e5\uff0c\u7eb5\u662f\u94c1\u94f8\u7684\u8eab\u5b50\u4e5f\u75b2\u5026\u4e86\uff0c\u800c\u94c1\u955c\u5fc3\u62fc\u6b7b\u6076\u6218\uff0c\u8fd8\u80fd\u582a\u582a\u6253\u4e2a\u5e73\u624b\uff0c\u53ef \u662f\u524d\u9762\u7684\u53bb\u8def\u53c8\u7ed9\u5835\u6b7b\u4e86\u3002\u4e8e\u627f\u73e0\u6025\u7387\u4e2d\u519b\u51b2\u4e0a\uff0c\u6b63\u5728\u6fc0\u6218\uff0c\u5ffd\u89c1\u524d\u9762\u5c18\u6c99\u5927\u8d77\uff0c\u4e00\u961f\u5175\u9a6c \u65cb\u98ce\u822c\u5730\u6740\u4e0a\u6765\uff0c\u5f53\u524d\u4e00\u4eba\u624b\u6325\u5927\u68d2\uff0c\u624b\u8d77\u68d2\u843d\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u5288\u7ffb\u4e86\u5341\u51e0\u4e2a\u502d\u5bc7\uff0c\u6709\u5982\u864e\u5165 \u7f8a\u7fa4\uff0c\u4e8e\u627f\u73e0\u5927\u559c\u53eb\u9053\uff1a\u201c\u6bd5\u64ce\u5929\uff01\u201d\u53ea\u89c1\u6bd5\u64ce\u5929\u671d\u7740\u5979\u70b9\u5934\u4e00\u7b11\uff0c\u624b\u5e95\u4e1d\u6beb\u4e0d\u7f13\uff0c\u4e00\u4e0b\u5b50 \u5c31\u51b2\u5230\u4e86\u94c1\u955c\u5fc3\u8fd9\u8fb9\uff0c\u624b\u8d77\u68d2\u843d\uff0c\u4e00\u68d2\u5411\u677f\u7530\u8363\u7537\u5934\u9885\u5288\u4e0b\u3002 \u3000\u3000\u677f\u7530\u8363\u7537\u4e3e\u5200\u4e00\u683c\uff0c\u7528\u7684\u662f\u65bd\u5200\u4e0a\u76d8\u523a\u624e\u624b\u6cd5\uff1b\u53ea\u8981\u4e00\u5200\u683c\u5f00\u68cd\u5c3e\uff0c\u63a5\u7740\u4fbf\u662f\u4e24\u4e0b\u4e0a\u624b \u5200\uff0c\u5728\u65e5\u672c\u6b66\u58eb\u60ef\u7528\u7684\u201c\u795e\u98ce\u5200\u6cd5\u201d\u4e2d\u7b97\u5f97\u662f\u6781\u5389\u5bb3\u7684\u62db\u6570\uff0c\u677f\u7530\u8363\u7537\u662f\u65e5\u672c\u6709\u540d\u7684\u4e03\u6bb5\u6b66 \u58eb\uff0c\u529b\u80fd\u625b\u9f0e\uff0c\u4ee5\u4e3a\u8fd9\u4e00\u5200\u4e00\u5b9a\u53ef\u4ee5\u683c\u5f00\uff0c\u54ea\u77e5\u6bd5\u64ce\u5929\u5929\u751f\u795e\u529b\uff0c\u8fd9\u4e00\u68d2\u6253\u4e0b\uff0c\u6709\u5982\u6cf0\u5c71\u538b \u9876\uff0c\u529b\u9053\u4f55\u6b62\u5343\u65a4\uff0c\u677f\u7530\u8363\u7537\u5927\u53eb\u4e00\u58f0\uff0c\u864e\u53e3\u6d41\u8840\uff0c\u502d\u5200\u8361\u8fc7\u4e00\u8fb9\uff0c\u63a5\u7740\u523a\u51fa\uff0c\u5df2\u662f\u4e0d\u6210\u7ae0 \u6cd5\uff0c\u6bd5\u64ce\u5929\u89c1\u4e00\u68d2\u5288\u4ed6\u4e0d\u5012\uff0c\u53c8\u52a0\u4e86\u51e0\u5206\u529b\u6c14\uff0c\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u518d\u63a5\u4e00\u68d2\u3002\u201d\u624b\u8d77\u68d2\u843d\uff0c\u677f \u7530\u8363\u7537\u65e0\u529b\u62db\u67b6\uff0c\u7ffb\u8eab\u4fbf\u8d70\uff0c\u5374\u7ed9\u94c1\u955c\u5fc3\u98de\u8d77\u4e00\u811a\uff0c\u8e22\u4e2d\u819d\u76d6\uff0c\u6447\u6447\u6b32\u5760\uff0c\u6bd5\u64ce\u5929\u987a\u624b\u4e00 \u68d2\uff0c\u5c06\u4ed6\u7684\u5929\u7075\u76d6\u90fd\u6253\u788e\u4e86\u3002\u90a3\u5927\u536b\u95e8\u6bd4\u8f83\u6ed1\u6e9c\uff0c\u4e00\u89c1\u540c\u4f34\u5403\u4e8f\uff0c\u7acb\u523b\u98de\u5954\u8d70\u4e86\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u8fd9\u652f\u63f4\u519b\uff0c\u4eba\u6570\u7ea6\u6709\u4e00\u5343\uff0c\u5175\u529b\u5408\u8d77\u6765\u5df2\u8d85\u8fc7\u502d\u5bc7\uff0c\u4f55\u51b5\u662f\u65b0\u5f00\u5230\u7684\u751f\u529b\u519b\uff0c\u767b \u65f6\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u628a\u502d\u5bc7\u6740\u5f97\u5927\u8d25\uff0c\u5954\u9003\u6e83\u4e0d\u6210\u519b\uff0c\u6b7b\u4f24\u72fc\u85c9\uff0c\u6bd5\u64ce\u5929\u72b9\u60f3\u6325\u519b\u75db\u88ad\uff0c\u4e8e\u627f\u73e0\u987e \u8651\u5230\u53f6\u5b97\u7559\uff0c\u529d\u4ed6\u56de\u53bb\u63a5\u5e94\u3002\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u6211\u5df2\u6d3e\u4e86\u6bd5\u613f\u7a77\u53e6\u5e26\u4e00\u5343\u4eba\u53bb\u63a5\u5e94\u5927\u5be8\u4e49\u519b\uff0c\u6599 \u53ef\u65e0\u59a8\u3002\u201d\u4e8e\u627f\u73e0\u6015\u90a3\u4e24\u8def\u502d\u5bc7\u4eba\u6570\u4f17\u591a\uff0c\u4ecd\u4e0d\u653e\u5fc3\uff0c\u6bd5\u64ce\u5929\u89c1\u5df2\u5927\u83b7\u5168\u80dc\uff0c\u6f0f\u7f51\u7684\u502d\u5bc7\u4e0d \u8fc7\u5341\u4e4b\u4e00\u4e8c\uff0c\u4e5f\u4fbf\u7b97\u4e86\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u805a\u96c6\u4ed6\u7387\u9886\u7684\u8fd9\u961f\u4e49\u519b\uff0c\u4e00\u70b9\u4e4b\u4e0b\uff0c\u4f24\u4ea1\u4e86\u4e94\u516d\u5341\u4eba\uff0c\u4e00\u573a\u5927\u6218\uff0c\u4f24\u4ea1\u4e94\u516d\u5341 \u4eba\uff0c\u5b9e\u5728\u5df2\u7b97\u5f97\u975e\u5e38\u5c11\u4e86\uff0c\u4f46\u8fd9\u961f\u4e49\u519b\u662f\u6210\u6d77\u5c71\u82e6\u5fc3\u8bad\u7ec3\u7684\u6d77\u6ee8\u6e14\u6c11\uff0c\u603b\u6570\u4e0d\u8fc7\u4e8c\u767e\u591a\u4eba\uff0c \u4e00\u6218\u5c31\u4f24\u4ea1\u4e86\u56db\u5206\u4e4b\u4e00\uff0c\u94c1\u955c\u5fc3\u5fc3\u5934\u9178\u75db\u3001\u7d27\u63e1\u7740\u4e8e\u627f\u73e0\u7684\u624b\u53f9\u9053\uff1a\u201c\u6211\u719f\u8bfb\u5175\u4e66\uff0c\u54ea\u77e5\u8fd8 \u662f\u4e0d\u80fd\u4e34\u9635\u5b9e\u7528\uff0c\u4f24\u4ea1\u4e86\u8fd9\u4e48\u591a\u7684\u5144\u5f1f\uff0c\u5440\uff0c\u6559\u6211\u6709\u4f55\u9762\u76ee\u53bb\u89c1\u53f6\u5927\u54e5\uff1f\u201d \u3000\u3000\u6bd5\u64ce\u5929\u89c1\u94c1\u955c\u5fc3\u4e0e\u4e8e\u627f\u73e0\u6001\u5ea6\u4eb2\u70ed\uff0c\u5fc3\u4e2d\u4e0d\u5feb\uff0c\u4f46\u5374\u4ecd\u80fd\u5fcd\u7740\u4e0d\u53d1\uff0c\u53cd\u800c\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u80dc\u8d25\u5175\u5bb6\u5e38\u4e8b\uff0c\u4f55\u8db3\u4ecb\u6000\uff1f\u4f60\u4ee5\u6570\u767e\u4e49\u519b\uff0c\u654c\u502d\u5bc7\u5343\u4f59\u52b2\u5352\uff0c\u4ea6\u8db3\u81ea\u8c6a\u4e86\uff01\u5144\u53f0\u8d35\u59d3\uff1f\u201d \u6bd5\u64ce\u5929\u89c1\u94c1\u955c\u5fc3\u6b66\u827a\u9ad8\u5f3a\uff0c\u4ee5\u4e3a\u4ed6\u662f\u4e49\u519b\u4e2d\u7684\u91cd\u8981\u9886\u8896\uff0c\u6709\u5fc3\u7b3c\u7edc\uff0c\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u5c0f\u5f1f\u94c1\u955c \u5fc3\uff0c\u662f\u4ece\u53f0\u5dde\u6765\u6295\u5954\u53f6\u5927\u54e5\u7684\uff0c\u6bd5\u5927\u9f99\u5934\uff0c\u4eca\u65e5\u5e78\u662f\u4f60\u53ca\u65f6\u8d76\u5230\u5c0f\u5f1f\u62dc\u8c22\u4e86\u3002\u201d\u9093\u8302\u4e03\u5728\u65c1 \u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u4fbf\u662f\u94c1\u5fa1\u53f2\u94c1\u94c2\u7684\u516c\u5b50\uff0c\u5728\u53f0\u5dde\u9f0e\u9f0e\u6709\u540d\uff0c\u6587\u6b66\u5168\u6750\uff0c\u5341\u5206\u96be\u5f97\u3002\u4f60\u4eec\u4e24\u4f4d\u4eb2 \u8fd1\u4eb2\u8fd1\u3002\u201d\u6bd5\u64ce\u5929\u542c\u4e86\u94c1\u955c\u5fc3\u7684\u8eab\u4efd\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u5374\u662f\u4e00\u4e2a\u516c\u5b50\u54e5\u513f\u3002\u201d\u659c\u773c\u4e00\u77a5\uff0c\u89c1\u4e8e \u627f\u73e0\u521a\u521a\u6454\u8131\u4ed6\u7684\u624b\uff0c\u5374\u8fd8\u662f\u508d\u5728\u4ed6\u7684\u8eab\u8fb9\uff0c\u5fc3\u4e2d\u53c8\u589e\u4e86\u51e0\u5206\u4e0d\u5feb\uff0c\u6697\u5730\u51b7\u7b11\u9053\uff1a\u201c\u4e8e\u627f\u73e0 \u4e5f\u7b97\u5f97\u662f\u4f4d\u5dfe\u5e3c\u82f1\u96c4\uff0c\u600e\u5730\u5374\u4f1a\u770b\u4e0a\u8fd9\u6837\u6ca1\u51fa\u606f\u7684\u4e66\u751f\u3002\u201d\u731b\u5730\u60f3\u8d77\u4e8e\u627f\u73e0\u6700\u5d07\u62dc\u7684\u5c31\u662f\u5979 \u7684\u5e08\u7236\u5f20\u4e39\u67ab\uff0c\u800c\u5f20\u4e39\u67ab\u4e5f\u662f\u4e00\u4e2a\u4e66\u751f\uff0c\u5148\u524d\u53ea\u662f\u4e0d\u5feb\uff0c\u8fd9\u65f6\u5374\u83ab\u540d\u5176\u5999\u5730\u6697\u4e2d\u5bf9\u94c1\u955c\u5fc3\u6709 \u4e86\u51e0\u5206\u201c\u654c\u610f\u201d\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u672c\u6765\u5bf9\u6bd5\u64ce\u5929\u8fd9\u79cd\u4eba\u7269\u9887\u4e3a\u8f7b\u89c6\uff0c\u7ecf\u8fc7\u4e86\u8fd9\u573a\u632b\u8d25\uff0c\u53cd\u800c\u628a\u5bf9\u201c\u8349\u83bd\u82f1\u96c4\u201d\u7684\u89c2 \u611f\u6539\u4e86\u8bb8\u591a\uff0c\u5bf9\u6bd5\u64ce\u5929\u5927\u9053\u4ef0\u5e55\u4e4b\u5ff1\uff0c\u6bd5\u64ce\u5929\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u662f\u4e00\u4e2a\u7c97\u4eba\uff0c\u65e2\u672a\u8bfb\u8fc7\u5175 \u4e66\uff0c\u4e5f\u4e0d\u77e5\u9053\u5175\u6d01\uff0c\u6709\u6127\u4e86\u3002\u201d\u6b64\u8bdd\u6697\u4e2d\u5b58\u7740\u8ba5\u523a\uff0c\u94c1\u955c\u5fc3\u9762\u8272\u4e00\u53d8\uff0c\u5fc3\u9053\uff1a\u201c\u8349\u91ce\u5339\u592b\uff0c \u6562\u6765\u7b11\u6211\uff1f\u4f60\u4e0d\u8fc7\u4ed7\u7740\u4e00\u652f\u65b0\u5f00\u6765\u7684\u751f\u529b\u519b\uff0c\u5076\u7136\u6253\u4e86\u4e00\u6b21\u80dc\u4ed7\uff0c\u4e5f\u4e0d\u89c1\u5f97\u6709\u4ec0\u4e48\u771f\u5b9e\u7684\u672c \u9886\u3002\u201d\u597d\u751f\u4e0d\u60a6\uff0c\u81ea\u6b64\u5bf9\u6bd5\u64ce\u5929\u4e5f\u5b58\u6210\u89c1\uff0c\u4e24\u4eba\u90fd\u6709\u5fc3\u75c5\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u4f55\u7b49\u673a\u7075\uff0c\u89c1\u4e24\u4eba\u8bdd\u4e0d\u6295\u673a\uff0c\u4fbf\u50ac\u4ed6\u4eec\u6025\u8d70\uff0c\u5954\u5230\u4e1c\u5357\u5c71\u53e3\uff0c\u53ea\u89c1\u4e00\u5f6a\u5175\u9a6c\u8d70\u4e86 \u51fa\u6765\uff0c\u53f6\u5b97\u56f9\u966a\u7740\u4e00\u4e2a\u82b1\u5b50\u5e76\u80a9\u8d70\u4e0a\uff0c\u8fd9\u82b1\u5b50\u6b63\u662f\u6bd5\u613f\u7a77\u3002\u539f\u6765\u53f6\u5b97\u7559\u719f\u8bc6\u5730\u5f62\uff0c\u5f15\u502d\u5bc7\u5230 \u4e00\u4e2a\u7edd\u8c37\uff0c\u51ed\u9669\u56fa\u5b88\uff0c\u53ea\u4ee5\u4ed6\u8fd9\u56db\u4e94\u767e\u4eba\u7684\u5175\u529b\uff0c\u5df2\u8db3\u591f\u4e0e\u5343\u4f59\u502d\u5bc7\u8c03\u65cb\uff0c\u6bd5\u613f\u7a77\u7684\u63f4\u519b\u4e00 \u5230\uff0c\u5f88\u5feb\u5c31\u6253\u4e86\u80dc\u4ed7\uff0c\u8fd9\u65f6\u4ed6\u4eec\u6b63\u5728\u6e05\u7406\u6218\u573a\u5462\u3002 \u3000\u3000\u6bd5\u613f\u7a77\u751f\u6027\u6ed1\u7a3d\uff0c\u4ed6\u5982\u4eca\u505a\u4e86\u6bd5\u64ce\u5929\u7684\u526f\u624b\uff0c\u4ecd\u662f\u7a7f\u7740\u4e00\u8eab\u8865\u4e01\u7684\u767e\u8896\u8863\uff0c\u5934\u6234\u74dc\u76ae\u5c0f \u5e3d\uff0c\u4e00\u89c1\u4e8e\u627f\u73e0\uff0c\u7acb\u523b\u8dd1\u4e86\u4e0a\u6765\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u771f\u662f\u4eba\u751f\u65e0\u5904\u4e0d\u9022\u541b\uff0c\u5f53\u65e5\u6211\u4eec\u7684\u5927\u9f99 \u5934\u7559\u4f60\uff0c\u4f60\u5acc\u6211\u4eec\u7684\u5730\u5c0f\u6c34\u6d45\uff0c\u4e0d\u80af\u7559\u4e0b\uff0c\u5982\u4eca\u54b1\u4eec\u8fd8\u662f\u8d70\u5230\u4e00\u6761\u8def\u4e0a\u6765\u4e86\u3002\u563b\uff0c\u6211\u7684\u597d\uff0c \u597d\u2014\u2014\u201d\u4e8e\u627f\u73e0\u751f\u6015\u4ed6\u8bf4\u51fa\u4ec0\u4e48\u597d\u201c\u59d1\u5976\u5976\u201d\u4e4b\u7c7b\u7684\u8bdd\u6765\uff0c\u67f3\u7709\u4e00\u7ad6\uff0c\u65a5\u9053\uff1a\u201c\u72d7\u5634\u91cc\u4e0d\u957f \u8c61\u7259\uff0c\u80e1\u8bf4\u516b\u9053\u3002\u73b0\u5728\u662f\u6297\u502d\u561b\uff0c\u53c8\u4e0d\u662f\u5e2e\u4eba\u6253\u5929\u4e0b\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u6765\uff1f\u201d\u7531\u64ce\u5929\u6015\u4e8e\u627f\u73e0\u9053 \u7834\u4ed6\u60f3\u505a\u7687\u5e1d\u7684\u7528\u5fc3\uff0c\u559d\u4f4f\u4e86\u6bd5\u613f\u7a77\uff0c\u6bd5\u613f\u7a77\u4f38\u4f38\u820c\u5934\u7b11\u9053\uff1a\u201c\u9f99\u5934\u6709\u547d\uff0c\u6211\u53ea\u597d\u8ba9\u4f60\u4e09 \u5206\uff0c\u597d\uff0c\u54b1\u4eec\u4e0d\u518d\u6597\u5634\u5566\uff0c\u6211\u7ed9\u4f60\u4f5c\u63d6\u3002\u201d\u88c5\u6a21\u4f5c\u6837\u5730\u7adf\u7136\u771f\u7684\u4f5c\u8d77\u63d6\u6765\uff0c\u628a\u4e8e\u627f\u73e0\u5f04\u5f97\u557c \u7b11\u7686\u975e\u3002\u53f6\u5b97\u7559\u4e0d\u77e5\u4ed6\u4eec\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u53ea\u9053\u4ed6\u4eec\u662f\u65e9\u5df2\u719f\u8bc6\u7684\u670b\u53cb\uff0c\u4e5f\u966a\u7740\u4ed6\u4eec\u563b\u563b\u54c8\u54c8\u5730 \u7b11\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u5c71\u5be8\u91cc\u4eba\u4eba\u9ad8\u5174\uff0c\u53f6\u5b97\u7559\u7834\u4f8b\u5bb0\u4e86\u5341\u51e0\u53e3\u5927\u732a\uff0c\u5f00\u7b75\u795d\u6377\uff0c\u94c1\u955c\u5fc3\u79c1\u4e0b\u91cc\u5411\u53f6\u5b97 \u7559\u9053\u6b49\uff0c\u53f6\u7559\u7b11\u9053\uff1a\u201c\u8fd9\u6ca1\u4ec0\u4e48\uff0c\u6211\u4e0d\u8fc7\u548c\u502d\u5bc7\u6253\u5f97\u591a\u4e86\uff0c\u6709\u4e00\u70b9\u7ecf\u9a8c\u7f62\u4e86\u3002\u6211\u8fc7\u540e\u4e00\u60f3\uff0c \u4f60\u6240\u8bb2\u7684\u90a3\u4ec0\u4e48\u5b59\u5b50\u5175\u6cd5\uff0c\u786e\u662f\u6709\u70b9\u9053\u7406\uff0c\u4f60\u4e0d\u662f\u8bf4\u5b59\u5b50\u5175\u6cd5\u8bb2\u8fc7\uff0c\u654c\u4f17\u6211\u5be1\u4e4b\u65f6\uff0c\u5c31\u8981\u907f \u514d\u51b3\u6218\u5417\uff1f\u6211\u5f53\u65f6\u60f3\u7684\u548c\u502d\u5bc7\u78e8\u7684\u6253\u6cd5\uff0c\u5176\u5b9e\u4e5f\u662f\u907f\u514d\u51b3\u6218\uff0c\u8981\u9009\u62e9\u6700\u6709\u5229\u4e8e\u6211\u4eec\u7684\u6253\u6cd5 \u554a\u3002\u4eca\u540e\u6211\u5f97\u8bf7\u4f60\u6bcf\u5929\u66ff\u6211\u8bb2\u4e00\u7ae0\u5b59\u5b50\u5175\u6cd5\uff0c\u4e0d\u77e5\u4f60\u8001\u54e5\u53ef\u613f\u610f\u6536\u6211\u8fd9\u6837\u611a\u7b28\u7684\u5b66\u751f\u4e48\uff1f\u201d \u94c1\u955c\u5fc3\u89c1\u53f6\u5b97\u7559\u6709\u529f\u4e0d\u5c45\uff0c\u6beb\u65e0\u9a84\u77dc\u4e4b\u8272\uff0c\u53cd\u800c\u8bf4\u8981\u62dc\u81ea\u5df1\u4e3a\u5e08\uff0c\u5fc3\u4e2d\u5927\u4e3a\u4f69\u670d\uff0c\u60ed\u6127\u8bf4 \u9053\uff1a\u201c\u73b0\u5728\u6211\u624d\u77e5\u9053\u53ea\u662f\u719f\u8bfb\u5175\u4e66\uff0c\u8fd8\u662f\u6ca1\u6709\u7528\u7684\u3002\u5b59\u5b50\u5175\u6cd5\u8bf4\u8fc7\u7684\u9053\u7406\uff0c\u6211\u5374\u81ea\u4f5c\u806a\u660e\uff0c \u5c06\u5b83\u7275\u5f3a\u5e94\u7528\uff0c\u602a\u4e0d\u5f97\u4f1a\u6709\u4eca\u65e5\u4e4b\u8d25\u3002\u53ea\u662f\u6211\u5c1a\u6709\u4e00\u4e8b\u672a\u660e\uff0c\u8981\u8bf7\u53f6\u5927\u54e5\u6307\u6559\u3002\u201d \u3000\u3000\u53f6\u5b97\u7559\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\uff0c\u8bf7\u94c1\u76f8\u516c\u8bf4\u6765\uff0c\u54b1\u4eec\u53c2\u8be6\u53c2\u8be6\uff0c\u201d\u4ed6\u4e0e\u94c1\u955c\u5fc3\u76f8\u5904\u591a\u65e5\uff0c\u77e5 \u9053\u4e86\u94c1\u955c\u5fc3\u7684\u4e60\u6c14\uff0c\u4e0e\u4ed6\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u4e5f\u5b66\u4f1a\u5ba2\u6c14\u3002\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u600e\u77e5\u9053\u4e2d\u8def\u7684\u502d\u5bc7 \u85cf\u6709\u4f0f\u5175\uff1f\u201d\u53f6\u5b97\u7559\u4e00\u7b11\u8d70\u51fa\u8425\u5e55\uff0c\u5916\u9762\u6740\u732a\u5bb0\u7f8a\uff0c\u4e00\u7247\u55a7\u95f9\uff0c\u6797\u5b50\u91cc\u7684\u9e1f\u90fd\u98de\u4e86\u8d77\u6765\uff0c\u6709 \u51e0\u53ea\u672a\u66fe\u98de\u8fdc\u7684\u5c1a\u5728\u7a7a\u9645\u56de\u7fd4\u3002\u53f6\u5b97\u7559\u9053\uff1a\u201c\u4eca\u65e9\u5728\u5cf0\u5934\u4e86\u671b\u4e4b\u65f6\uff0c\u53ef\u4e0d\u662f\u89c1\u770b\u90a3\u4e00\u7247\u8349\u5761 \u7684\u4e0a\u7a7a\uff0c\u6709\u8bb8\u591a\u98de\u9e1f\u5417\uff1f\u201d\u94c1\u955c\u5fc3\u604d\u7136\u5927\u609f\uff0c\u9053\uff1a\u201c\u662f\u4e86\uff0c\u8349\u4e1b\u4e2d\u82e5\u662f\u6ca1\u6709\u4f0f\u5175\uff0c\u9e1f\u513f\u4e5f\u4e0d \u4f1a\u5413\u5f97\u60ca\u98de\u4e86\u3002\u53f6\u5927\u54e5\uff0c\u4f60\u89c2\u5bdf\u5f97\u771f\u4ed4\u7ec6\u3002\u201d\u53f6\u5b97\u7559\u7b11\u9053\uff1a\u201c\u8fd9\u7b97\u5f97\u4ec0\u4e48\uff0c\u6bcf\u4e00\u4e2a\u5e84\u7a3c\u6c49\u90fd \u6709\u8fd9\u4e00\u5957\u672c\u9886\u3002\u6211\u4e0d\u8fc7\u628a\u5e84\u7a3c\u6c49\u61c2\u5f97\u7684\u4e1c\u897f\uff0c\u8fd0\u7528\u5230\u6253\u4ed7\u4e0a\u7f62\u4e86\u3002\u201d\u94c1\u955c\u5fc3\u6697\u53eb\u60ed\u6127\uff0c\u8fd9\u624d \u77e5\u9053\u4e16\u754c\u4e0a\u7684\u201c\u5b66\u95ee\u201d\u539f\u4e0d\u662f\u9650\u4e8e\u4e66\u672c\uff0c\u602a\u4e0d\u5f97\u53e4\u4eba\u6709\u4e91\uff1a\u201c\u4e16\u4e8b\u6d1e\u660e\u7686\u5b66\u95ee\uff0c\u4eba\u60c5\u901a\u8fbe\u4ea6 \u6587\u7ae0\u201d\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u5be8\u4e2d\u795d\u6377\uff0c\u53f6\u5b97\u7559\u63d0\u51fa\u8981\u8bf7\u6bd5\u64ce\u5929\u505a\u6297\u502d\u4e49\u519b\u7684\u603b\u6307\u6325\uff0c\u81ea\u5df1\u7518\u613f\u505a\u4ed6\u7684\u526f\u624b\uff0c \u8fd9\u4e8b\u60c5\u53f6\u5b97\u7559\u65e9\u5df2\u5411\u90e8\u5c5e\u758f\u901a\uff0c\u672c\u6765\u65e0\u751a\u5f02\u8bae\uff0c\u6bd5\u64ce\u5929\u5fc3\u4e2d\u5176\u5b9e\u662f\u5343\u80af\u4e07\u80af\uff0c\u8868\u9762\u4e0a\u5374\u518d\u4e09 \u8c26\u8f9e\uff0c\u53f6\u5b97\u7559\u4e00\u529b\u63a8\u8350\uff0c\u6bd5\u64ce\u5929\u770b\u770b\u201c\u620f\u201d\u5df2\u505a\u8db3\uff0c\u6b63\u60f3\u88c5\u51fa\u65e0\u53ef\u5948\u4f55\u7684\u6837\u5b50\uff0c\u5c06\u8fd9\u603b\u6307\u6325 \u7684\u804c\u4f4d\u63a5\u8fc7\u6765\uff0c\u94c1\u955c\u5fc3\u5ffd\u9053\uff1a\u201c\u8fd9\u4e8b\u4e07\u4e07\u4f7f\u4e0d\u5f97\u3002\u53f6\u5927\u54e5\u4e0e\u502d\u5bc7\u4f5c\u6218\u5df2\u4e45\uff0c\u719f\u6089\u654c\u60c5\uff0c\u53c8\u662f \u672c\u5730\u4eba\uff0c\u66f4\u6709\u8bb8\u591a\u4fbf\u5229\u4e4b\u5904\uff0c\u6b63\u6240\u8c13\u9a7e\u8f7b\u5c31\u719f\uff0c\u6362\u4e86\u522b\u4e00\u4e2a\u4eba\uff0c\u7eb5\u4f7f\u4ed6\u7684\u672c\u9886\u66f4\u5927\uff0c\u5bf9\u4ed8\u502d \u5bc7\uff0c\u7ec8\u662f\u6ca1\u6709\u53f6\u5927\u54e5\u7684\u7ecf\u9a8c\u591a\u3002\u201d\u53f6\u5b97\u7559\u9053\uff1a\u201c\u6bd5\u5927\u9f99\u5934\u5a01\u9707\u4e94\u7701\uff0c\u4e0e\u5b98\u519b\u5927\u5c0f\u767e\u6218\uff0c\u4f5c\u6218 \u7ecf\u9a8c\u8fdc\u80dc\u4e8e\u6211\uff0c\u800c\u4e14\u6297\u502d\u4e4b\u4e8b\uff0c\u4e0a\u4e0b\u540c\u5fc3\uff0c\u5927\u5bb6\u8ba1\u8bae\uff0c\u6211\u4eec\u90fd\u7b49\u4e8e\u6bd5\u5927\u9f99\u5934\u7684\u81c2\u818a\uff0c\u53c8\u4f55\u5206 \u5f7c\u6b64\u3002\u6bd5\u5927\u9f99\u5934\u672c\u9886\u6bd4\u6211\u9ad8\u660e\u767e\u500d\uff0c\u8fd8\u662f\u8bf7\u4ed6\u505a\u8fd9\u4e2a\u7edf\u9886\u7684\u597d\u3002\u201d\u94c1\u955c\u5fc3\u65e2\u6709\u5f02\u8bae\uff0c\u6bd5\u64ce\u5929 \u81ea\u4e0d\u80fd\u4e0d\u518d\u8c26\u8ba9\u4e00\u6b21\uff0c\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u662f\u554a\uff0c\u6297\u502d\u65e2\u4e0d\u5206\u5f7c\u6b64\uff0c\u90a3\u53c8\u4f55\u5fc5\u8ba9\u6765\u8ba9\u53bb\u5462\uff1f\u4f55\u51b5\u4e0e \u5b98\u519b\u4f5c\u6218\uff0c\u53c8\u4e0d\u540c\u4e8e\u5bf9\u502d\u5bc7\u4f5c\u6218\uff0c\u5982\u4eca\u6cbf\u6d77\u51e0\u7701\u8c01\u90fd\u77e5\u9053\u53f6\u5927\u54e5\u662f\u6297\u502d\u7684\u4e49\u519b\u9996\u9886\uff0c\u6362\u4e86\u4e2a \u4eba\uff0c\u5f0a\u591a\u5229\u5c11\uff0c\u53f6\u5927\u54e5\u8ba9\u4f4d\uff0c\u8db3\u89c1\u4ed6\u793c\u8d24\u4e0b\u58eb\uff0c\u6bd5\u9f99\u5934\u8c26\u8f9e\uff0c\u4e5f\u8db3\u89c1\u4ed6\u5149\u660e\u78ca\u843d\u3002\u4e24\u4f4d\u90fd\u503c \u5f97\u94a6\u4f69\u3002\u53f6\u5927\u54e5\u5e94\u8be5\u63a5\u7eb3\u6bd5\u9f99\u5934\u7684\u63a8\u6234\uff0c\u4e0d\u5fc5\u518d\u8ba9\u4e86\u3002\u201d\u94c1\u955c\u5fc3\u8bf4\u7684\u5927\u6709\u9053\u7406\uff0c\u4e49\u519b\u5934\u76ee\u672c \u6765\u5c31\u6709\u8bb8\u591a\u4eba\u4e0d\u613f\u53f6\u5b97\u7559\u8ba9\u4f4d\uff0c\u53ea\u56e0\u9274\u4e8e\u53f6\u5b97\u7559\u4e8b\u524d\u7684\u758f\u901a\uff0c\u624d\u63a8\u6234\u6bd5\u64ce\u5929\uff0c\u5982\u4eca\u542c\u94c1\u955c\u5fc3 \u8bf4\u5f97\u6709\u7406\uff0c\u53c8\u7eb7\u7eb7\u633d\u7559\u8d77\u53f6\u5b97\u7559\u6765\u3002\u6bd5\u64ce\u5929\u628a\u94c1\u955c\u5fc3\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u4f46\u89c1\u60c5\u52bf\u8f6c\u53d8\uff0c\u53f6\u5b97\u7559 \u7684\u7edf\u9886\u5df2\u6210\u8d70\u5c40\uff0c\u5ff5\u5934\u4e00\u8f6c\uff0c\u53cd\u800c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u94c1\u76f8\u516c\u5230\u5e95\u662f\u4e2a\u8bfb\u4e66\u4eba\uff0c\u89c1\u8bc6\u8fdc\u5927\uff0c\u5c06\u6211\u5fc3 \u91cc\u7684\u8bdd\u90fd\u8bf4\u51fa\u6765\u4e86\u3002\u53f6\u5927\u54e5\u4f60\u662f\u4f17\u671b\u6240\u5f52\uff0c\u4e0d\u5fc5\u518d\u63a8\u8ba9\u4e86\u3002\u800c\u4e14\u6297\u502d\u5bc7\u7ec8\u6709\u7ec8\u4e86\u4e4b\u65e5\uff0c\u54b1\u4eec \u5c06\u6765\u8fd8\u6709\u8bb8\u591a\u5927\u4e8b\u60c5\u8981\u643a\u624b\u5408\u4f5c\u5462\u3002\u201d\u4e8e\u627f\u73e0\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u6bd5\u64ce\u5929\u600e\u4e48\u628a\u5230\u53e3\u7684\u9992\u5934\u53c8 \u63a8\u6389\u4e86\uff0c\u96be\u9053\u4ed6\u7684\u91ce\u5fc3\u5df2\u6536\u655b\u4e86\u4e48\uff1f\u4f46\u542c\u4ed6\u7684\u8bf4\u8bdd\uff0c\u5176\u4e2d\u53c8\u4f3c\u542b\u6709\u6df1\u610f\u3002 \u3000\u3000\u53f6\u5b97\u7559\u5bf9\u5f85\u81ea\u5df1\u4eba\u662f\u4e00\u7247\u81f3\u8bda\uff0c\u80f8\u65e0\u57ce\u5e9c\uff0c\u542c\u6bd5\u64ce\u5929\u5982\u6b64\u8bf4\u6cd5\uff0c\u4fbf\u9053\uff1a\u201c\u6bd5\u5927\u9f99\u5934\u65e2\u7136 \u6781\u529b\u8981\u6211\u52c9\u4e3a\u5176\u96be\uff0c\u6211\u53ea\u597d\u9075\u547d\u4e86\u3002\u6bd5\u9f99\u5934\u8bf4\u5f97\u5bf9\uff0c\u54b1\u4eec\u9664\u4e86\u6297\u502d\u4e4b\u5916\uff0c\u5c06\u6765\u8fd8\u6709\u8bb8\u591a\u4e8b\u60c5 \u8981\u643a\u624b\u5408\u4f5c\u3002\u90a3\u4e48\uff0c\u6211\u770b\u5c31\u8fd9\u6837\u529e\u5427\uff0c\u76ee\u4e0b\u4ecd\u7531\u6211\u505a\u8fd9\u4e2a\u6297\u502d\u4e49\u519b\u7684\u7edf\u9886\uff0c\u4f46\u5374\u8981\u5949\u6bd5\u9f99\u5934 \u505a\u76df\u4e3b\uff0c\u6bd5\u9f99\u5934\u73b0\u5728\u5df2\u662f\u5317\u4e94\u7701\u7eff\u6797\u7684\u76df\u4e3b\uff0c\u5c06\u6765\u7531\u6211\u8d1f\u8d23\uff0c\u628a\u82cf\u3001\u6d59\u4e24\u7701\u7684\u7eff\u6797\u82f1\u96c4\u90fd\u8bf7 \u6765\u4e00\u540c\u52a0\u76df\uff0c\u5f85\u5c06\u6765\u5c06\u502d\u5bc7\u9a71\u9010\u4e0b\u6d77\uff0c\u6cbf\u6d77\u5e73\u5b89\u4e4b\u540e\uff0c\u6211\u4eec\u90fd\u542c\u6bd5\u5927\u9f99\u5934\u7684\u6307\u6325\u3002\u201d\u8fd9\u6b63\u662f \u6bd5\u64ce\u5929\u6240\u671f\u671b\u7684\u4e8b\u60c5\uff0c\u542c\u53f6\u5b97\u7559\u81ea\u5df1\u8bf4\u51fa\uff0c\u7565\u4e00\u63a8\u8f9e\uff0c\u4fbf\u5373\u6b43\u8840\u4e3a\u76df\uff0c\u94c1\u955c\u5fc3\u5bf9\u7eff\u6797\u7684\u6d3b\u52a8 \u5b8c\u5168\u4e0d\u611f\u5174\u8da3\uff0c\u867d\u7136\u5bf9\u6bd5\u64ce\u5929\u4e0d\u6ee1\uff0c\u5374\u4e5f\u672a\u66fe\u60f3\u5230\u6bd5\u64ce\u5929\u6709\u90a3\u4e48\u5927\u7684\u91ce\u5fc3\uff0c\u60f3\u5229\u7528\u53f6\u5b97\u7559\u5c06 \u6765\u66ff\u4ed6\u6253\u6c5f\u5c71\uff0c\u89c1\u4ed6\u4eec\u4e24\u4eba\u5df2\u5f97\u5230\u534f\u8bae\uff0c\u4fbf\u4e5f\u4e0d\u518d\u51fa\u8a00\u5e72\u9884\u3002 \u3000\u3000\u6b43\u8840\u4e3a\u76df\u4e4b\u540e\uff0c\u6bd5\u64ce\u5929\u5c06\u53f6\u5b97\u7559\u62c9\u8fc7\u4e00\u8fb9\uff0c\u537f\u537f\u5495\u5495\u5730\u5bc6\u8c08\uff0c\u4e8e\u627f\u73e0\u4e00\u773c\u77a5\u53bb\uff0c\u5ffd\u89c1\u53f6 \u5b97\u7559\u4e5f\u6b63\u6e9c\u8fc7\u6765\uff0c\u5bf9\u81ea\u5df1\u5fae\u7b11\uff0c\u4e8e\u627f\u73e0\u4e00\u6014\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u4e0d\u662f\u5728\u5546\u91cf\u4ec0\u4e48\u5927\u4e8b\uff0c\u5374\u5728 \u8c08\u8bba\u6211\u4e48\uff1f\u201d\u8f6c\u773c\u4e00\u7901\uff0c\u53c8\u89c1\u6bd5\u613f\u7a77\u4e5f\u5728\u53d9\u4e2d\u773c\u98d8\u5411\u81ea\u5df1\u8fd9\u65b9\uff0c\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a \u201c\u8fd9\u91cc\u51e0\u5343\u4eba\uff0c\u77e5\u9053\u6211\u662f\u5973\u5b50\u4e48\uff1f\u53ea\u6709\u6bd5\u64ce\u5929\u4e0e\u6bd5\u613f\u7a77\u4e8c\u4eba\uff0c\u5982\u679c\u4ed6\u4eec\u5c06\u6211\u7684\u79d8\u5bc6\u63ed\u9732\u51fa \u6765\uff0c\u90a3\u6211\u53ef\u4e0d\u597d\u610f\u601d\u518d\u5446\u5728\u8fd9\u513f\u4e86\u3002\u201d\u4f46\u89c1\u53f6\u5b97\u7559\u4e0e\u6bd5\u64ce\u5929\u8c08\u8bdd\u4e4b\u540e\uff0c\u7167\u5e38\u5904\u7406\u519b\u4e2d\u4e8b\u52a1\uff0c \u5bf9\u81ea\u5df1\u4ea6\u65e0\u5f02\u5bb9\uff0c\u4e8e\u627f\u73e0\u624d\u653e\u4e0b\u5fc3\u6765\u3002 \u3000\u3000\u81ea\u6210\u6d77\u5c71\u56de\u53bb\u540e\uff0c\u4e8e\u627f\u73e0\u672c\u6765\u662f\u548c\u5f20\u9ed1\u3001\u94c1\u955c\u5fc3\u4e8c\u4eba\u540c\u4f4f\u5e10\u5e55\u7684\uff0c\u8fd9\u4e00\u665a\u53f6\u5b97\u7559\u53eb\u4eba\u591a \u642d\u4e86\u4e09\u5ea7\u5e10\u5e55\uff0c\u4e00\u5ea7\u7ed9\u94c1\u955c\u5fc3\uff0c\u4e00\u5ea7\u7ed9\u6bd5\u64ce\u5929\uff0c\u4e00\u5ea7\u7ed9\u4e8e\u627f\u73e0\uff0c\u8bf4\u662f\u56e0\u4e3a\u4ed6\u4eec\u8fdc\u6765\u52a9\u6218\uff0c\u5e94 \u8be5\u8ba9\u4ed6\u4eec\u4f4f\u5f97\u8212\u670d\u4e00\u4e9b\uff0c\u94c1\u955c\u5fc3\u6700\u6b22\u559c\u5f97\u4eba\u5c0a\u91cd\uff0c\u6beb\u65e0\u7591\u5fc3\uff0c\u4e8e\u627f\u73e0\u5fc3\u601d\u7f1c\u5bc6\uff0c\u5374\u7acb\u523b\u60f3\u5230 \u90a3\u5b9a\u7136\u662f\u56e0\u4e3a\u6bd5\u64ce\u5929\u4e0d\u613f\u610f\u81ea\u5df1\u4e0e\u94c1\u955c\u5fc3\u540c\u4e00\u5e10\u5e55\uff0c\u6240\u4ee5\u53eb\u53f6\u5b97\u7559\u5982\u6b64\u5904\u7f6e\uff0c\u6562\u60c5\u4ed6\u628a\u81ea\u5df1 \u7684\u771f\u76f8\u4e5f\u5411\u53f6\u5b97\u7559\u8bf4\u4e86\u3002\u4e8e\u627f\u73e0\u5fc3\u4e2d\u6709\u70b9\u4e0d\u5feb\uff0c\u89c9\u5f97\u6bd5\u64ce\u5929\u5fc3\u5730\u4e0d\u591f\u5149\u660e\uff0c\u4f46\u80fd\u72ec\u81ea\u4f4f\u4e00\u5e10 \u5e55\uff0c\u5374\u4e5f\u6b63\u662f\u5979\u5fc3\u4e2d\u6240\u613f\uff0c\u514d\u5f97\u65e5\u5b50\u4e00\u957f\uff0c\u4f1a\u88ab\u94c1\u955c\u5fc3\u770b\u51fa\u75d5\u8ff9\uff0c\u6240\u4ee5\u4e5f\u4fbf\u9ad8\u9ad8\u5174\u5174\u5730\u8c22\u8fc7 \u53f6\u5b97\u7559\u3002 \u3000\u3000\u5927\u6377\u4e4b\u540e\uff0c\u53f6\u5b97\u7559\u91cd\u65b0\u6574\u987f\u4e49\u519b\uff0c\u5e76\u4e0e\u5404\u5730\u6c11\u5175\u8054\u7edc\uff0c\u4e8b\u52a1\u7e41\u5fd9\uff0c\u5bf9\u4e8e\u627f\u73e0\u795e\u8272\u5982\u5e38\uff0c \u6beb\u65e0\u534a\u53e5\u98ce\u8a00\u98ce\u8bed\uff0c\u4e8e\u627f\u73e0\u4e5f\u731c\u4e0d\u900f\u4ed6\u7a76\u7adf\u77e5\u4e0d\u77e5\u9053\u81ea\u5df1\u662f\u4e2a\u5973\u5b50\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u6708\uff0c\u4e49\u519b\u7ecf\u8fc7\u6574\u7f16\uff0c\u6218\u610f\u6602\u626c\uff0c\u53f6\u5b97\u7559\u4e0b\u4ee4\u5f00\u62d4\u51fa\u5c71\uff0c\u4e0e\u5404\u5730\u6c11\u5175\u8054\u5408\uff0c\u4e00\u8fde\u6253 \u4e86\u51e0\u6b21\u80dc\u4ed7\uff0c\u5c06\u502d\u5bc7\u8d76\u5230\u79bb\u6d77\u8fb9\u53ea\u6709\u5341\u591a\u91cc\u7684\u897f\u575e\uff0c\u502d\u5bc7\u5f97\u5230\u4e00\u6279\u4ece\u56fd\u5185\u6765\u7684\u6d6a\u4eba\u652f\u6301\uff0c\u4e24 \u519b\u4ecd\u5728\u76f8\u6301\uff0c\u53f6\u5b97\u7559\u5206\u5175\u5835\u4f4f\u502d\u5bc7\u7684\u53bb\u8def\uff0c\u53ea\u7559\u4e0b\u5411\u5927\u6d77\u7684\u8fd9\u4e00\u8fb9\uff0c\u514d\u5b83\u5411\u5185\u5730\u6d41\u7a9c\uff0c\u6b63\u5728 \u51c6\u5907\u51b3\u6218\uff0c\u4e00\u65e5\uff0c\u502d\u5bc7\u7684\u7edf\u5e05\u5ffd\u7136\u6d3e\u9063\u4e86\u4e24\u4e2a\u4f7f\u8005\u524d\u6765\u4e0b\u4e66\u3002\u90a3\u4e24\u4e2a\u65e5\u672c\u4f7f\u8005\u6001\u5ea6\u50b2\u6162\uff0c\u53f6 \u5b97\u7559\u63a5\u8fc7\u4e66\u4fe1\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u7740\uff1a\u201c\u8d35\u6211\u4e24\u519b\uff0c\u76f8\u6301\u4e0d\u4e0b\uff0c\u6613\u4e0d\u5c0f\u4f11\uff1f\u655d\u519b\u660e\u65e5\u4e3e\u884c\u79cb\u5b63\u6b66\u9053 \u5927\u624b\u5408\uff0c\u7a3d\u4e4b\u8d35\u56fd\u53e4\u53f2\uff0c\u5217\u56fd\u76f8\u4e89\uff0c\u4ea6\u6709\u89c2\u5175\u5c04\u5fa1\u4e4b\u8d5b\uff0c\u8d35\u519b\u5065\u513f\uff0c\u5176\u4ea6\u6709\u610f\u524d\u6765\u89d2\u9010 \u4e4e\uff1f\u201d\u201c\u5927\u624b\u5408\u201d\u662f\u65e5\u8bed\u7684\u5927\u6bd4\u8d5b\u4e4b\u610f\uff0c\u770b\u6765\u8fd9\u5c01\u4fe1\u662f\u6295\u964d\u502d\u5bc7\u7684\u65e0\u803b\u6587\u58eb\u6240\u5199\uff0c\u7528\u4e2d\u56fd\u7684 \u53f2\u5b9e\uff0c\u53c8\u7528\u65e5\u672c\u7684\u540d\u8bcd\uff0c\u4e0d\u4f26\u4e0d\u7c7b\uff0c\u94c1\u955c\u5fc3\u5c06\u4fe1\u4e2d\u7684\u610f\u601d\u89e3\u91ca\u7ed9\u53f6\u5b97\u7559\u542c\uff0c\u8bf4\u9053\uff1a\u201c\u502d\u5bc7\u8bf7 \u54b1\u4eec\u53c2\u52a0\u4ed6\u4eec\u519b\u4e2d\u7684\u6b66\u9053\u6bd4\u8d5b\uff0c\u5b9a\u65e0\u597d\u610f\u3002\u6625\u79cb\u6218\u56fd\u4e4b\u65f6\uff0c\u5217\u56fd\u76f8\u4e89\uff0c\u867d\u7136\u4e5f\u5e38\u6709\u654c\u5bf9\u7684\u4e24 \u56fd\uff0c\u5728\u6625\u79cb\u4f73\u65e5\uff0c\u53cc\u65b9\u505c\u6218\uff0c\u4f5c\u5c04\u7bad\u9a91\u9a6c\u7684\u6bd4\u8d5b\uff0c\u4f46\u90a3\u5230\u5e95\u662f\u81ea\u5df1\u4eba\u4e4b\u95f4\u7684\u7eb7\u4e89\u3002\u5982\u4eca\u54b1\u4eec \u662f\u5bf9\u5916\u4f5c\u6218\uff0c\u502d\u5bc7\u4ea6\u4e0d\u662f\u65e5\u672c\u7684\u6b63\u5f0f\u519b\u961f\u3002\u5373\u4f9d\u53e4\u793c\uff0c\u4ea6\u4e0d\u80fd\u4f5c\u4e3a\u2018\u654c\u4f53\u2019\u770b\u5f85\uff0c\u8fd9\u5c01\u4fe1\u4e71 \u5f15\u4e2d\u56fd\u7684\u53f2\u5b9e\uff0c\u4e0d\u503c\u4e00\u6652\uff0c\u4f9d\u6211\u8bf4\u4e0d\u5fc5\u7406\u5b83\uff0c\u5c06\u8fd9\u4e24\u4e2a\u4f7f\u8005\u6253\u4e94\u5341\u68cd\uff0c\u9a71\u9010\u51fa\u53bb\u4fbf\u7f62\u3002\u201d \u3000\u3000\u6bd5\u64ce\u5929\u7b11\u9053\uff1a\u201c\u4e8f\u4f60\u8fd8\u6709\u5fc3\u601d\u53bb\u8ba8\u8bba\u4ed6\u7684\u4fe1\u5199\u5f97\u5bf9\u662f\u4e0d\u5bf9\uff0c\u5e72\u8106\u626f\u788e\u4e86\u8f70\u4ed6\u51fa\u53bb\u3002\u201d\u53f6 \u5b97\u7559\u51b5\u541f\u534a\u664c\uff0c\u5ffd\u9053\uff1a\u201c\u502d\u5bc7\u8be1\u8ba1\u591a\u7aef\uff0c\u4f46\u5982\u4eca\u54b1\u4eec\u7684\u5175\u529b\u8db3\u53ef\u5e94\u4ed8\uff0c\u4e5f\u4e0d\u5fc5\u60e7\u3002\u597d\u5427\uff0c\u54b1 \u4eec\u7d22\u6027\u5c06\u8ba1\u5c31\u6c41\uff0c\u5c31\u53bb\u53c2\u52a0\u4ed6\u7684\u4ec0\u4e48\u5927\u624b\u5408\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u5927\u54e5\u6709\u4ec0\u4e48\u8ba1\u7b56\uff1f\u201d\u53f6\u5b97\u7559\u5fae \u7b11\u9053\uff1a\u201c\u4e34\u673a\u5e94\u53d8\uff0c\u4e00\u65f6\u4e5f\u96be\u4ee5\u8bf4\u5b9a\uff0c\u53ea\u662f\u54b1\u4eec\u6311\u51e0\u4e2a\u6709\u80c6\u91cf\u7684\u58ee\u58eb\u524d\u53bb\uff0c\u5373\u5728\u5343\u519b\u4e07\u9a6c\u4e2d \u4e5f\u80fd\u593a\u5173\u95ef\u51fa\u6765\u7684\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u6211\u548c\u4e8e\u627f\u73e0\u53bb\u3002\u201d\u6bd5\u64ce\u5929\u4fa7\u76ee\u77a7\u89c6\uff0c\u7b11\u9053\uff1a\u201c\u94c1\u76f8\u516c\uff0c \u8fd9\u662f\u6027\u547d\u76f8\u6251\u4e4b\u4e8b\uff0c\u53ef\u4e0d\u6bd4\u541f\u8bd7\u4f5c\u5bf9\u554a\u3002\u201d\u94c1\u955c\u5fc3\u52c3\u7136\u53d8\u8272\uff0c\u53f6\u5b97\u7559\u9053\uff1a\u201c\u94c1\u516c\u5b50\u6b66\u529f\u8d85 \u5353\uff0c\u6599\u60f3\u4e0d\u4f1a\u5931\u624b\uff0c\u4e0d\u8fc7\u591a\u53bb\u51e0\u4e2a\u4eba\u4e5f\u597d\uff0c\u6bd5\u5927\u54e5\uff0c\u4f60\u4e5f\u6709\u610f\u53bb\u8d81\u70ed\u95f9\u5417\uff1f\u6709\u4f60\u53bb\u90a3\u5c31\u66f4\u7a33 \u5f53\u4e86\u3002\u201d\u6bd5\u64ce\u5929\u6700\u521d\u672c\u6765\u662f\u6ca1\u6709\u610f\u601d\u53bb\u7684\uff0c\u540e\u6765\u542c\u5f97\u94c1\u955c\u5fc3\u8981\u9080\u4e8e\u627f\u73e0\u540c\u53bb\uff0c\u5fc3\u4e2d\u5992\u5fcc\uff0c\u4e5f \u60f3\u51fa\u58f0\u540c\u53bb\uff0c\u4f46\u5374\u53c8\u987e\u5230\u81ea\u5df1\u201c\u76df\u4e3b\u201d\u7684\u8eab\u4efd\uff0c\u4e0d\u4fbf\u5f00\u53e3\uff0c\u5374\u559c\u53f6\u5b97\u7559\u8bf7\u4ed6\u4e5f\u53bb\uff0c\u5360\u4e86\u8eab \u4efd\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u5927\u54e5\u6709\u547d\uff0c\u5c82\u6562\u4e0d\u9075\uff1f\u201d\u7acb\u5373\u4fbf\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u3001\u94c1\u955c\u5fc3\u3001\u6bd5\u64ce\u5929\u53e6\u5916\u52a0\u4e0a\u4e49\u519b\u7684\u5934\u76ee\u9093\u8302\u4e03\u3001\u90d1\u8d76\u9a74\u5171\u662f\u4e94\u4eba\uff0c\u7b2c\u4e8c\u5929\u4f9d\u7ea6\u5230 \u4e86\u502d\u5bc7\u519b\u8425\uff0c\u53ea\u89c1\u4ed6\u4eec\u5728\u6d77\u6ee8\u8f9f\u51fa\u4e00\u7247\u5e7f\u573a\uff0c\u6570\u5343\u502d\u5bc7\u56f4\u62e5\u56db\u5468\uff0c\u5e7f\u573a\u4e2d\u6709\u51e0\u5341\u4e2a\u65e5\u672c\u6b66\u58eb \u76f8\u6251\u4e3a\u620f\uff0c\u4e00\u89c1\u6bd5\u64ce\u5929\u4ed6\u4eec\u8fdb\u6765\uff0c\u7acb\u5373\u505c\u6b62\uff0c\u4e0a\u524d\u6b22\u8fce\uff0c\u4e3a\u9996\u7684\u4e00\u4e2a\u8eab\u6750\u9ad8\u5927\u7684\u6b66\u58eb\u4f38\u51fa\u624b \u6765\uff0c\u7528\u65e5\u8bed\u8bf4\u9053\uff1a\u201c\u652f\u90a3\u6b66\u58eb\u52c7\u6c14\u53ef\u559c\uff0c\u54b1\u4eec\u4eb2\u8fd1\u4eb2\u8fd1\uff01\u201d\u6bd5\u64ce\u5929\u62a2\u5728\u524d\u5934\uff0c\u4f38\u624b\u4e0e\u4ed6\u4e00 \u63e1\uff0c\u6bd5\u64ce\u5929\u7528\u7684\u662f\u91d1\u521a\u6307\u529b\uff0c\u60f3\u628a\u8fd9\u4e2a\u6b66\u571f\u7684\u624b\u9aa8\u634f\u788e\uff0c\u54ea\u77e5\u7528\u529b\u4e00\u634f\uff0c\u53ea\u89c9\u654c\u4eba\u4e94\u6307\u5982 \u94c1\uff0c\u6307\u529b\u7adf\u7136\u4e5f\u662f\u5f3a\u52b2\u975e\u5e38\uff0c\u6bd5\u64ce\u5929\u56fa\u662f\u6697\u6697\u5947\u5f02\uff0c\u90a3\u65e5\u672c\u6b66\u58eb\u66f4\u662f\u80c6\u6218\u5fc3\u60ca\uff01 \u3000\u3000\u90a3\u65e5\u672c\u6b66\u58eb\u540d\u53eb\u77f3\u4e95\u592a\u90ce\uff0c\u662f\u65b0\u4ece\u56fd\u5185\u6765\u7684\u516b\u6bb5\u6b66\u58eb\uff0c\u4e0d\u4f46\u67d4\u9053\u3001\u5200\u6cd5\u90fd\u662f\u51fa\u8272\u5f53\u884c\uff0c \u800c\u4e14\u5168\u8eab\u9aa8\u9abc\u81ea\u5e7c\u7528\u836f\u6c34\u6d78\u8fc7\uff0c\u575a\u5982\u94a2\u94c1\uff0c\u4ed6\u4e5f\u662f\u60f3\u628a\u6bd5\u64ce\u5927\u7684\u624b\u9aa8\u634f\u788e\uff0c\u54ea\u77e5\u5374\u88ab\u6bd5\u64ce\u5929 \u7684\u91d1\u94a2\u6307\u529b\u53cd\u51fb\u56de\u6765\uff0c\u6307\u9aa8\u9690\u9690\u4f5c\u75db\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u6025\u5fd9\u653e\u5f00\u4e86\u624b\uff0c\u53c8\u5f85\u548c\u4e8e\u627f\u73e0\u63e1 \u624b\uff0c\u4e8e\u627f\u73e0\u563b\u563b\u7b11\u9053\uff1a\u201c\u514d\u793c\u5566\uff01\u201d\u811a\u5c16\u4e00\u8d77\uff0c\u5c06\u4e00\u5757\u77f3\u5934\u8e22\u5f97\u7c89\u788e\uff0c\u77f3\u4e95\u66f4\u4e3a\u5403\u60ca\uff0c\u5fc3 \u9053\uff1a\u201c\u8fd9\u4e2a\u4fca\u79c0\u7684\u5c0f\u4f19\u5b50\u6562\u60c5\u6bd4\u90a3\u4e2a\u5927\u4e2a\u5b50\u66f4\u5389\u5bb3\uff1f\u201d\u4e0d\u6562\u518d\u8bd5\u3002\u5374\u54ea\u77e5\u4e8e\u627f\u73e0\u662f\u6697\u4e2d\u4f7f \u8bc8\uff0c\u5979\u7684\u6307\u529b\u5b9e\u5728\u8fd8\u4e0d\u53ca\u77f3\u4e95\uff0c\u800c\u4e14\u4e5f\u4e0d\u613f\u548c\u4ed6\u6bdb\u8338\u8338\u7684\u5927\u624b\u76f8\u649e\uff0c\u6240\u4ee5\u6697\u7779\u4f7f\u529b\u5c06\u811a\u4e0b\u7684 \u77f3\u5934\u8e0f\u4e86\u51e0\u4e0b\uff0c\u5979\u7a7f\u7684\u978b\u662f\u9576\u6709\u94c1\u7247\u7684\u201c\u5200\u9a6c\u978b\u201d\uff0c\u52a0\u4e0a\u5979\u7528\u7684\u662f\u6b63\u5b97\u7684\u5185\u529f\u52b2\u529b\uff0c\u8e0f\u4e86\u51e0 \u8e0f\uff0c\u77f3\u8d28\u5df2\u7ecf\u677e\u8f6f\uff0c\u518d\u88c5\u4f5c\u88ab\u77f3\u5934\u7eca\u811a\u7684\u6837\u5b50\uff0c\u978b\u5934\u4e00\u8e22\u5c31\u5c06\u77f3\u5757\u8e22\u788e\u4e86\u3002 \u3000\u3000\u77f3\u4e95\u9510\u6c14\u4e00\u6298\uff0c\u4e0d\u6562\u591a\u4e8b\uff0c\u5c06\u6bd5\u64ce\u5929\u7b49\u5f15\u8fdb\u5e7f\u573a\uff0c\u573a\u4e2d\u4e00\u4e2a\u6b66\u58eb\uff0c\u4e24\u8fb9\u592a\u9633\u7a74\u9f13\u8d77\uff0c\u76f8 \u8c8c\u867d\u7136\u4e11\u964b\uff0c\u773c\u5149\u5374\u70af\u70af\u6709\u795e\uff0c\u77f3\u4e95\u4ecb\u7ecd\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6b64\u6b21\u5927\u624b\u5408\u7684\u4ef2\u88c1\u957f\uff0c\u6211\u56fd\u9f0e\u9f0e\u5927\u540d \u7684\u4e5d\u6bb5\u9ad8\u624b\u957f\u8c37\u5ddd\uff01\u201d\u94c1\u955c\u5fc3\u7cbe\u795e\u4e00\u632f\uff0c\u4ed6\u4eec\u4e0e\u502d\u5bc7\u4f5c\u6218\u4ee5\u6765\uff0c\u78b0\u5230\u6700\u9ad8\u6bb5\u7684\u53ea\u662f\u4e03\u6bb5\uff0c\u60f3 \u4e0d\u5230\u5982\u4eca\u5728\u6b64\u4f1a\u78b0\u5230\u4e00\u4f4d\u4e5d\u6bb5\u9ad8\u624b\uff0c\u81ea\u7136\u7981\u4e0d\u4f4f\u5174\u594b\u8d77\u6765\u3002 \u3000\u3000\u90a3\u957f\u8c37\u5ddd\u9ad8\u50b2\u4e4b\u6781\uff0c\u4ed6\u8eab\u4e3a\u4e5d\u6bb5\u9ad8\u624b\uff0c\u5df2\u4e0d\u5fc5\u53c2\u52a0\u6bd4\u8d5b\uff0c\u800c\u662f\u4ee5\u603b\u88c1\u5224\u7684\u8eab\u4efd\uff0c\u4e3b\u6301\u6bd4 \u8d5b\uff0c\u5bf9\u94c1\u955c\u5fc3\u7b49\u4eba\u6f2b\u4e0d\u4e3a\u793c\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u4eec\u73b0\u5728\u6b63\u5728\u6bd4\u8bd5\u89d2\u529b\uff0c\u76ee\u4e0b\u573a\u4e2d\u5f97 \u80dc\u7684\u662f\u4e03\u6bb5\u6b66\u58eb\u8fd1\u536b\u4e09\u90ce\uff0c\u4f60\u4eec\u8c01\u4e0b\u53bb\u548c\u4ed6\u8bd5\u8bd5\u3002\u201d\u4ed6\u8bf4\u7684\u662f\u65e5\u8bed\uff0c\u81ea\u6709\u901a\u8bd1\u8bd1\u6210\u6c49\u8bed\u3002 \u3000\u3000\u9093\u8302\u4e03\u5bf9\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u5176\u4ed6\u7684\u6b66\u827a\u6211\u53ef\u4e0d\u61c2\uff0c\u7b28\u6c14\u529b\u8fd8\u6709\u51e0\u65a4\uff0c\u5f85\u6211\u8bd5\u8bd5\u3002\u201d\u51fa\u53bb\u548c\u8fd1 \u536b\u4e09\u90ce\u76f8\u6251\uff0c\u4e0d\u8fc7\u51e0\u4e0b\u5b50\uff0c\u5c31\u7ed9\u8fd1\u536b\u6454\u4e86\u4e00\u8de4\uff0c\u65e5\u672c\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u94c1\u955c\u5fc3\u6697\u76b1\u7709\u5934\uff0c\u5fc3\u9053\uff1a \u201c\u9093\u8302\u4e03\u662f\u4e49\u519b\u7684\u526f\u7edf\u9886\uff0c\u600e\u7684\u5982\u6b64\u4e0d\u6d4e\uff1f\u201d\u9093\u8302\u4e03\u4e00\u6454\u5012\u4fbf\u722c\u8d77\uff0c\u53c8\u662f\u4e0d\u8fc7\u51e0\u4e0b\u5b50\uff0c\u53c8\u7ed9 \u8fd1\u536b\u7528\u67d4\u9053\u624b\u6cd5\u6454\u4e86\u4e00\u8de4\uff0c\u54ea\u77e5\u9093\u8302\u4e03\u4e00\u89e6\u5730\u53c8\u8df3\u8d77\u6765\u3001\u4ecd\u7136\u62fc\u547d\u76f8\u6251\u3002\u5982\u662f\u8005\u4e03\u516b\u6b21\u4e4b \u591a\uff0c\u8fd1\u536b\u7528\u5404\u79cd\u5389\u5bb3\u7684\u624b\u6cd5\u6454\u5012\u4ed6\uff0c\u603b\u4e0d\u80fd\u53eb\u4ed6\u53d7\u4f24\uff0c\u603b\u662f\u4e00\u6454\u5012\u4f7f\u8d77\u3002\u539f\u6765\u9093\u8302\u4e03\u7ec3\u7684\u662f \u5916\u5bb6\u786c\u529f\uff0c\u53c8\u5728\u77ff\u5c71\u78e8\u7ec3\u4e86\u51e0\u5341\u5e74\uff0c\u76ae\u7c97\u8089\u539a\uff0c\u5c31\u662f\u7528\u53f3\u5934\u7838\u4ed6\uff0c\u4ed6\u4e5f\u9876\u5f97\u4f4f\uff0c\u6454\u90a3\u4e48\u5341\u4e0b \u516b\u4e0b\u53ea\u5f53\u6293\u75d2\uff0c\u4f9d\u7528\u529b\u6454\u8de4\u89c4\u77e9\uff0c\u654c\u4eba\u53ea\u8981\u8fd8\u80fd\u8df3\u8d77\uff0c\u6709\u529b\u6c14\u7ee7\u7eed\u89d2\u6597\uff0c\u90a3\u5c31\u4e0d\u80fd\u4f11\u6b62\uff0c\u8fd1 \u536b\u4e09\u90ce\u6454\u4ed6\u4e0d\u5012\uff0c\u5fc3\u4e2d\u614c\u4e86\uff0c\u9093\u8302\u4e03\u5ffd\u5730\u5927\u559d\u4e00\u58f0\uff0c\u628a\u8fd1\u536b\u4e09\u90ce\u7684\u81c2\u818a\u4e00\u6273\uff0c\u4e00\u6454\u5c31\u5c06\u4ed6\u6454 \u51fa\u51e0\u4e08\u4e4b\u5916\uff0c\u8fd1\u536b\u7684\u989d\u89d2\u78b0\u5728\u77f3\u5b50\u4e0a\uff0c\u7a7f\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u6d41\u8840\u4e0d\u6b62\uff0c\u4f11\u8bf4\u722c\u8d77\uff0c\u8fde\u52a8\u4e5f\u4e0d\u80fd\u4e00 \u52a8\u3002 \u3000\u3000\u65e5\u672c\u6b66\u58eb\u5927\u54d7\uff0c\u7acb\u5373\u6709\u4e00\u4e2a\u4eba\u8df3\u4e86\u51fa\u6765\uff0c\u62d4\u51fa\u502d\u5200\uff0c\u5728\u7a7a\u4e2d\u865a\u5288\u4e24\u5200\uff0c\u547c\u547c\u4f5c\u54cd\uff0c\u9ad8\u58f0 \u53eb\u9053\uff1a\u201c\u8fd8\u662f\u767d\u5200\u5b50\u8fdb\u7ea2\u5200\u5b50\u51fa\u5730\u6bd4\u5200\u6765\u5f97\u723d\u5feb\u3002\u201d\u4e8e\u627f\u73e0\u563b\u563b\u4e00\u7b11\uff0c\u6b65\u51fa\u573a\u4e2d\uff0c\u5374\u4e0d\u62d4 \u5251\uff0c\u800c\u89e3\u4e0b\u4e86\u4e00\u6761\u675f\u8170\u7684\u7ef8\u5e26\u3002 \u3000\u3000\u573a\u4e2d\u7684\u65e5\u672c\u6b66\u571f\u83ab\u540d\u5947\u5999\uff0c\u90a3\u4f7f\u5927\u5200\u7684\u540d\u53eb\u5c0f\u662d\uff0c\u4e5f\u662f\u4e2a\u4e03\u6bb5\u597d\u624b\uff0c\u89c1\u4e8e\u627f\u73e0\u6325\u821e\u7ef8 \u5e26\uff0c\u72b6\u5982\u513f\u620f\uff0c\u602a\u800c\u95ee\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u5343\u4ec0\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u662f\u8bf4\u8981\u6bd4\u6b66\u5417\uff1f\u201d\u5c0f\u662d \u9053\uff1a\u201c\u65e2\u662f\u6bd4\u6b66\uff0c\u4e3a\u4f55\u4e0d\u62d4\u51fa\u5251\u6765\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u4eec\u4e2d\u56fd\u7684\u89c4\u77e9\uff0c\u6bd4\u6b66\u8981\u770b\u5bf9\u624b\uff0c\u6240\u7528 \u7684\u5175\u5668\u4e5f\u5c31\u56e0\u4eba\u800c\u65bd\uff0c\u5bf9\u4ed8\u4f60\u561b\uff0c\u4e0d\u503c\u5f97\u6211\u62d4\u51fa\u5b9d\u5251\u2026\u2026\u201d\u7ef8\u5e26\u4e00\u6325\uff0c\u77eb\u5982\u6e38\u9f99\uff0c\u4e00\u7b11\u63a5 \u9053\uff0c\u201c\u8fd9\u4fbf\u662f\u6211\u7684\u5175\u5668\u3002\u201d\u4ed6\u4eec\u4e4b\u95f4\u7684\u5bf9\u8bdd\uff0c\u5f53\u7136\u662f\u901a\u8fc7\u901a\u8bd1\u8bf4\u7684\uff0c\u901a\u8bd1\u5df2\u628a\u4e8e\u627f\u73e0\u8f7b\u8511\u7684 \u8bed\u6c14\u51cf\u4e86\u51e0\u5206\uff0c\u4f46\u5c0f\u662d\u8fd8\u662f\u53ef\u4ee5\u770b\u5f97\u51fa\u4e8e\u627f\u73e0\u5bf9\u4ed6\u8f7b\u8511\u7684\u795e\u8272\uff0c\u6c14\u5f97\u54c7\u54c7\u53eb\u9053\uff1a\u201c\u4f60\u8981\u7528\u8fd9 \u6839\u5e26\u5b50\u6765\u5bf9\u4ed8\u6211\u7684\u5b9d\u5200\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u8fd8\u8981\u8ba9\u4f60\u5148\u5288\u4e09\u5200\uff01\u201d\u901a\u8bd1\u628a\u8fd9\u8bdd\u4f20\u4e86\u8fc7 \u53bb\uff0c\u5c0f\u662d\u52c3\u7136\u5927\u6012\uff0c\u970d\u5730\u6325\u5200\u4fbf\u780d\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4fbf\u7528\u5e26\u5b50\u6321\u5427\uff01\u201d \u3000\u3000\u8fd9\u4e00\u5200\u5288\u5f97\u53c8\u72e0\u53c8\u75be\uff0c\u65c1\u89c2\u7684\u65e5\u672c\u6b66\u58eb\u5927\u58f0\u559d\u5f69\uff0c\u4e8e\u627f\u73e0\u6545\u610f\u5356\u4e2a\u7834\u7efd\uff0c\u8ba9\u4ed6\u7684\u5200\u5288\u5230 \u80f8\u524d\uff0c\u7ea4\u8170\u4e00\u6298\uff0c\u4fbf\u907f\u4e86\u5f00\u53bb\uff0c\u59ff\u52bf\u6709\u5982\u98ce\u4e2d\u6446\u67f3\uff0c\u7f8e\u5999\u975e\u5e38\uff0c\u94c1\u955c\u5fc3\u770b\u5f97\u51fa\u795e\uff0c\u559d\u4e86\u58f0 \u5f69\uff0c\u5ffd\u5730\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4e00\u95ea\u4e00\u907f\uff0c\u521a\u5065\u4e4b\u4e2d\u663e\u51fa\u554a\u5a1c\uff0c\u54a6\uff0c\u4e8e\u5144\u5f1f\u7684\u8eab\u6cd5\u600e\u4e48\u4f3c\u4e2a \u5973\u5b50\uff1f\u201d\u5e73\u65f6\u65e0\u5fc3\u76f8\u5411\uff0c\u5e76\u4e0d\u89c9\u5f97\u4ec0\u4e48\uff0c\u8fd9\u65f6\u5374\u5ffd\u5730\u89e6\u4e86\u8d77\u6765\uff0c\u8054\u60f3\u8d77\u8bf8\u591a\u75d5\u8ff9\uff1a\u4e8e\u627f\u73e0\u5728 \u4eba\u524d\u4ece\u6765\u4e0d\u80af\u8131\u4e0b\u5916\u8863\uff0c\u6c90\u6d74\u4e4b\u65f6\u4e00\u5b9a\u8981\u5c06\u81ea\u5df1\u548c\u5f20\u9ed1\u8bf7\u51fa\u5e10\u5916\u7b49\u7b49\uff0c\u4ee5\u524d\u53ea\u9053\u662f\u5979\u7684\u4e60 \u60ef\uff0c\u800c\u4eca\u4e00\u60f3\uff0c\u4e0d\u89c9\u5446\u4e86\u3002\u5ffd\u89c1\u6bd5\u64ce\u5929\u72e0\u72e0\u5730\u5411\u4ed6\u77aa\u4e86\u4e00\u4e0b\u773c\u775b\uff0c\u94c1\u955c\u5fc3\u609a\u7136\u4e00\u60ca\uff0c\u53ea\u542c\u5f97 \u573a\u4e2d\u4e00\u7247\u6684\u95f9\uff0c\u539f\u6765\u4e8e\u627f\u73e0\u53c8\u7528\u7f8e\u5999\u7684\u8eab\u6cd5\u907f\u8fc7\u4e86\u5c0f\u662d\u7684\u7b2c\u4e8c\u5200\u3002 \u3000\u3000\u5c0f\u662d\u7b2c\u4e09\u5200\u8fde\u73af\u65ab\u81f3\uff0c\u8fd9\u4e00\u5200\u7528\u7684\u662f\u795e\u98ce\u5200\u6cd5\uff0c\u5200\u5149\u95ea\u95ea\uff0c\u628a\u4e8e\u627f\u73e0\u7684\u524d\u540e\u5de6\u53f3\u5168\u90fd\u5c01 \u4f4f\uff0c\u4e0d\u8bba\u5979\u600e\u6837\u95ea\u907f\u4e5f\u95ea\u4e0d\u5f00\uff0c\u6ee1\u4ee5\u4e3a\u8fd9\u4e00\u5200\u8d70\u80fd\u780d\u4e2d\uff0c\u4e8e\u627f\u73e0\u5ffd\u5730\u7528\u4e2a\u201c\u4e00\u9e64\u51b2\u5929\u201d\u4e4b \u52bf\uff0c\u8eab\u5b50\u7a81\u62d4\u8d77\u6570\u5c3a\uff0c\u5c0f\u662d\u90a3\u4e00\u5200\u521a\u597d\u4ece\u5979\u7684\u978b\u5e95\u524a\u8fc7\uff0c\u65e5\u672c\u4eba\u54ea\u66fe\u89c1\u8fc7\u8fd9\u7b49\u8f7b\u529f\u5999\u6280\uff0c\u8fde \u5728\u573a\u8fb9\u66ff\u5c0f\u662d\u52a9\u5a01\u7684\u90a3\u7fa4\u6b66\u58eb\u4e5f\u60c5\u4e0d\u81ea\u7981\u5730\u559d\u8d77\u5f69\u6765\u3002\u5c0f\u662d\u624b\u8db3\u65e0\u63aa\uff0c\u502d\u5200\u5c1a\u672a\u6536\u56de\uff0c\u53ea\u89c1 \u4e8e\u627f\u73e0\u5df2\u843d\u5728\u4e00\u4e08\u4e4b\u5916\uff0c\u7b11\u76c8\u76c8\u5730\u5c06\u7ef8\u5e26\u4e00\u6325\uff0c\u9053\uff1a\u201c\u4e09\u5200\u5df2\u8fc7\uff0c\u8f6e\u5230\u4f60\u63a5\u6211\u7684\u4e86\uff01\u201d \u3000\u3000\u5c0f\u662d\u4e00\u5200\u5288\u53bb\uff0c\u5200\u98ce\u9707\u8361\uff0c\u7ef8\u5e26\u8f7b\u98d8\uff0c\u5ffd\u5730\u5982\u957f\u8679\u75be\u5377\uff0c\u8f6c\u4e86\u4e2a\u5f2f\uff0c\u671d\u4ed6\u624b\u8155\u5377\u5230\uff0c\u5c0f \u662d\u614c\u5fd9\u4f38\u624b\u53bb\u6293\uff0c\u4ed6\u773c\u660e\u624b\u5feb\uff0c\u8fd9\u4e00\u6293\u8fd8\u771f\u7b97\u8fc5\u75be\uff0c\u4f46\u4e8e\u627f\u73e0\u7684\u7ef8\u5e26\u7f29\u5f97\u66f4\u5feb\uff0c\u5c0f\u662d\u6293\u4e86\u4e2a \u7a7a\uff0c\u7ef8\u5e26\u53c8\u4ece\u4fa7\u9762\u88ad\u6765\uff0c\u7ef8\u5e26\u662f\u6781\u67d4\u8f6f\u4e4b\u7269\uff0c\u56de\u7fd4\u98d8\u821e\uff0c\u94a2\u5200\u867d\u5229\uff0c\u5374\u4f11\u60f3\u5c06\u5b83\u780d\u65ad\u3002\u5c0f\u662d \u7d2f\u51fa\u4e86\u4e00\u8eab\u5927\u6c57\uff0c\u4f46\u89c1\u7ef8\u5e26\u98d8\u98d8\uff0c\u5ffd\u4f38\u5ffd\u7f29\uff0c\u5728\u65c1\u89c2\u8005\u770b\u6765\uff0c\u90a3\u662f\u7f8e\u5999\u4e4b\u6781\uff0c\u597d\u770b\u715e\u4eba\uff1b\u4f46 \u5728\u5c0f\u662d\u770b\u6765\uff0c\u5374\u65e0\u6b8a\u6bd2\u86c7\u5410\u4fe1\uff0c\u9632\u4e0d\u80dc\u9632\u3002\u4e0d\u6d88\u534a\u523b\uff0c\u5c0f\u662d\u5df2\u662f\u5934\u6655\u76ee\u7729\uff0c\u5ffd\u542c\u5f97\u4e8e\u627f\u73e0\u683c \u683c\u4e00\u7b11\uff0c\u559d\u58f0\u201c\u7740\uff01\u201d\u7ef8\u5e26\u5ffd\u5730\u628a\u5200\u67c4\u7f20\u7740\uff0c\u53ea\u4e00\u5377\u5c31\u5377\u4e86\u53bb\uff0c\u4e8e\u627f\u73e0\u5c06\u7ef8\u5e26\u4e00\u629b\uff0c\u502d\u5200\u55d6 \u5730\u5411\u7a7a\u4e2d\u98de\u51fa\uff0c\u94f6\u5149\u6620\u65e5\uff0c\u502d\u5200\u7ed9\u5979\u629b\u9ad8\u5f97\u53ea\u89c1\u4e00\u7247\u5200\u5f71\u3002 \u3000\u3000\u502d\u5200\u98de\u5f97\u9ad8\uff0c\u8dcc\u5f97\u5feb\uff0c\u970e\u773c\u95f4\u5200\u5728\u7a7a\u4e2d\u6253\u4e86\u4e2a\u8f6c\uff0c\u5200\u950b\u5411\u4e0b\uff0c\u631f\u7740\u4e00\u9053\u5149\uff0c\u5b9b\u5982\u767d\u8679\u75be \u5c04\uff0c\u987b\u77e5\u7269\u4f53\u5728\u7a7a\u4e2d\u843d\u4e0b\uff0c\u4f4d\u7f6e\u8d8a\u9ad8\uff0c\u4e0b\u964d\u6108\u901f\uff0c\u529b\u91cf\u5c31\u6108\u5927\uff0c\u4e00\u9897\u77f3\u5934\uff0c\u4e5f\u5f80\u5f80\u53ef\u7f6e\u4eba\u6b7b \u547d\uff0c\u4f55\u51b5\u662f\u4e00\u67c4\u91cd\u8fbe\u5341\u4f59\u65a4\u3001\u950b\u5229\u5f02\u5e38\u7684\u502d\u5200\uff0c\u6563\u5e03\u5728\u573a\u8fb9\u7684\u4eba\u90fd\u7eb7\u7eb7\u8d70\u907f\uff0c\u53ea\u89c1\u90a3\u53e3\u5200\u6d41 \u661f\u95ea\u7535\u822c\u5730\u5411\u5c0f\u662d\u98de\u53bb\uff0c\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u6253\u6697\u5668\u7684\u4e0a\u4e58\u624b\u6cd5\u5440\uff01\u201d\u539f\u6765\u4e91\u857e\u7684\u91d1\u82b1\u6697 \u5668\uff0c\u6709\u4e00\u4e2a\u72ec\u7279\u7684\u6253\u6cd5\uff0c\u80fd\u628a\u91d1\u82b1\u98de\u51fa\uff0c\u843d\u4e0b\u4e4b\u65f6\u518d\u4f24\u4eba\uff0c\u800c\u4eca\u4e8e\u627f\u73e0\u501f\u654c\u4eba\u7684\u5200\u53d8\u4f5c\u654c\u4eba \u7684\u6697\u5668\uff0c\u8fd9\u624b\u6cd5\u5c31\u6b63\u662f\u4e91\u857e\u6240\u6388\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u58f0\u602a\u7b11\uff0c\u4e00\u4e2a\u65e5\u672c\u6b66\u58eb\uff0c\u98de\u6b65\u62a2\u51fa\uff0c\u629b\u51fa\u4e00\u6839\u957f\u7d22\uff0c\u7d22\u4e0a\u6253\u6709\u4e00\u4e2a\u6d3b\u7ed3\uff0c\u90a3\u6839 \u957f\u7d22\u88ab\u4ed6\u6296\u5f97\u7b14\u76f4\uff0c\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u8fd1\u7740\u502d\u5200\uff0c\u6070\u6070\u5957\u7740\u5200\u67c4\uff0c\u90a3\u65e5\u672c\u6b66\u571f\u4e00\u62c9\uff0c\u7acb\u523b\u5c06\u502d \u5200\u6536\u5230\u624b\u4e2d\uff0c\u8fd9\u4e00\u4e0b\u629b\u7d22\u5957\u5200\uff0c\u6240\u53d6\u7684\u51c6\u5934\uff0c\u6240\u7528\u7684\u52b2\u529b\uff0c\u65e0\u4e00\u4e0d\u6070\u5230\u597d\u5904\uff0c\u4e0e\u4e8e\u627f\u73e0\u9002\u624d \u7528\u7ef8\u5e26\u5377\u5200\u7684\u624b\u6cd5\uff0c\u6709\u5f02\u66f2\u540c\u5de5\u4e4b\u5999\uff0c\u94c1\u955c\u5fc3\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u6599\u502d\u5bc7\u519b\u4e2d\uff0c\u7adf\u4e5f\u6709\u8fd9\u822c\u597d\u624b\uff0c \u573a\u4e2d\u7684\u65e5\u672c\u6b66\u571f\uff0c\u8f70\u96f7\u822c\u5730\u559d\u8d77\u5f69\u6765\u3002\u94c1\u955c\u5fc3\u61c2\u5f97\u65e5\u672c\u8bdd\uff0c\u542c\u4ed6\u4eec\u6b22\u547c\u559d\u5f69\uff0c\u9ad8\u53eb\u90a3\u65e5\u672c\u6b66 \u58eb\u7684\u540d\u5b57\uff0c\u77e5\u9053\u8fd9\u6b66\u58eb\u540d\u53eb\u82a5\u5ddd\u9f99\u6728\uff0c\u7adf\u7136\u662f\u4e00\u4f4d\u516b\u6bb5\u9ad8\u624b\u3002\u539f\u6765\u8fd9\u6b21\u502d\u5bc7\u4ece\u56fd\u5185\u618e\u63f4\uff0c\u5176 \u4e2d\u6709\u4e00\u4e2a\u4e5d\u6bb5\u9ad8\u624b\uff0c\u4e24\u4e2a\u516b\u6bb5\u9ad8\u624b\uff0c\u4e5d\u6bb5\u9ad8\u624b\u957f\u8c37\u5ddd\uff0c\u4f8b\u4e0d\u4e0b\u573a\u6bd4\u8d5b\uff0c\u5176\u4e2d\u4e24\u4e2a\u516b\u6bb5\u6b66\u571f\uff0c \u4e00\u4e2a\u662f\u521a\u624d\u63a5\u5f85\u4f17\u4eba\u7684\u77f3\u4e95\u5927\u90ce\uff0c\u4e00\u4e2a\u4fbf\u662f\u73b0\u5728\u5411\u4e8e\u627f\u73e0\u6311\u6218\u7684\u82a5\u5ddd\u9f99\u6728\uff0c\u8fd9\u4e24\u4e2a\u6b66\u58eb\u539f\u672c \u662f\u5b89\u6392\u4e86\u6765\u5bf9\u4ed8\u4e2d\u56fd\u6700\u5389\u5bb3\u7684\u9009\u624b\uff0c\u51c6\u5907\u6700\u540e\u624d\u51fa\u573a\u4f5c\u51b3\u8d5b\u7684\uff0c\u54ea\u77e5\u4e2d\u56fd\u9009\u624b\u8fde\u80dc\u4e86\u4e24\u573a\uff0c \u4e8e\u627f\u73e0\u7684\u8f7b\u529f\u5999\u6280\u66f4\u4ee4\u5f97\u502d\u5bc7\u6151\u670d\uff0c\u662f\u4ee5\u82a5\u5ddd\u9f99\u6728\u53ea\u5f97\u63d0\u524d\u51fa\u573a\u3002 \u3000\u3000\u53ea\u89c1\u82a5\u5ddd\u9f99\u6728\u53f3\u624b\u63a5\u5200\uff0c\u5de6\u81c2\u8f6c\u4e86\u51e0\u4e2a\u5708\u5b50\uff0c\u90a3\u6839\u957f\u8fbe\u4e09\u4e08\u6709\u591a\u7684\u7ef3\u7d22\u7ed5\u5728\u81c2\u4e0a\uff0c\u5389\u58f0 \u559d\u9053\uff1a\u201c\u54b1\u4eec\u4e5f\u6765\u6bd4\u6bd4\uff0c\u4f60\u7231\u7528\u4ec0\u4e48\u5175\u5668\u4ffa\u90fd\u4e00\u51c6\u5949\u966a\uff01\u201d\u901a\u8bd1\u6b63\u5728\u4f20\u8bdd\uff0c\u4e8e\u627f\u73e0\u8fd8\u672a\u56de \u7b54\uff0c\u4ed6\u5ffd\u7684\u8eab\u5f62\u4e00\u6251\uff0c\u5de6\u81c2\u4e00\u632f\uff0c\u957f\u7d22\u7a81\u7136\u98de\u51fa\uff0c\u4e00\u5377\u5377\u7740\u4e86\u4e8e\u627f\u73e0\u7684\u7ef8\u5e26\uff0c\u4e8e\u627f\u73e0\u6599\u4e0d\u5230 \u4ed6\u4f1a\u7a81\u7136\u5077\u88ad\uff0c\u53ea\u89c9\u4e00\u80a1\u731b\u52b2\u5c06\u81ea\u5df1\u5f80\u524d\u62d6\uff0c\u7d22\u957f\u5e26\u77ed\uff0c\u5148\u5403\u4e86\u4e8f\uff0c\u7adf\u7ed9\u4ed6\u62d6\u52a8\u4e24\u6b65\u3002 \u3000\u3000\u82a5\u5ddd\u9f99\u6728\u683c\u683c\u602a\u7b11\uff0c\u5de6\u81c2\u53c8\u8f6c\u4e86\u51e0\u4e2a\u5708\u5b50\uff0c\u5c06\u7ef3\u7d22\u6536\u77ed\uff0c\u540c\u65f6\u502d\u5200\u4e00\u626c\uff0c\u75be\u5730\u5288\u51fa\uff0c\u4ed6 \u8fd9\u4e24\u62db\uff0c\u540c\u65f6\u5e94\u7528\uff0c\u5389\u5bb3\u975e\u5e38\uff1b\u5c31\u5728\u8fd9\u4e00\u65f6\u95f4\uff0c\u5ffd\u542c\u5f97\u201c\u535c\u201d\u7684\u4e00\u54cd\uff0c\u58f0\u5982\u88c2\u5e1b\uff0c\u90a3\u6839\u957f\u7d22 \u5c1a\u672a\u6536\u56de\uff0c\u5374\u5df2\u5f53\u4e2d\u65ad\u4e86\uff0c\u4e8e\u627f\u73e0\u8eab\u5f62\u5982\u7bad\uff0c\u500f\u5730\u4fbf\u5230\u9762\u524d\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00 \u53e3\u5bd2\u5149\u95ea\u95ea\u7684\u77ed\u5251\uff0c\u82a5\u5ddd\u9f99\u6728\u6025\u5fd9\u65cb\u8eab\u659c\u5288\uff0c\u201c\u53ee\u5f53\u201d\u4e00\u58f0\uff0c\u90a3\u67c4\u502d\u5200\u53c8\u5df2\u65ad\u4e3a\u4e24\u622a\u3002\u539f\u6765 \u8bba\u6c14\u529b\u4e8e\u627f\u73e0\u867d\u6bd4\u4e0d\u4e0a\u82a5\u5ddd\uff0c\u4f46\u5185\u529b\u7684\u8fd0\u7528\u4e4b\u5de7\uff0c\u5374\u8fdc\u4e3a\u9ad8\u660e\uff0c\u5979\u8d81\u82a5\u5ddd\u5c06\u7ef3\u7d22\u7ef7\u7d27\uff0c\u7ef8\u5e26 \u4e00\u7ed5\u4e00\u62c9\uff0c\u767b\u65f6\u7ef3\u7d22\u4e0e\u7ef8\u5e26\u90fd\u65ad\uff0c\u4f46\u7d22\u7c97\u5e26\u7ec6\uff0c\u65c1\u89c2\u7684\u4eba\uff0c\u4fbf\u53ea\u770b\u5230\u9f50\u5ddd\u9f99\u6728\u5403\u4e8f\u3002 \u3000\u3000\u82a5\u5ddd\u9f99\u6728\u5df2\u8bd5\u51fa\u4e8e\u627f\u73e0\u6c14\u529b\u6bd4\u4ed6\u5f31\uff0c\u4e0b\u77e5\u600e\u7684\uff0c\u7a81\u7136\u7ed9\u5979\u7528\u5de7\u52b2\u5f04\u65ad\u7ef3\u7d22\uff0c\u63aa\u624b\u4e0d\u53ca\uff0c \u7ed9\u4e8e\u627f\u73e0\u524a\u65ad\u502d\u5200\uff0c\u60ca\u60f6\u4e0d\u5df2\uff0c\u5782\u5934\u4e27\u6c14\u5730\u7ad9\u5728\u4e00\u8fb9\u3002\u4e8e\u627f\u73e0\u65ad\u7d22\u3001\u62d4\u5251\u3001\u524a\u5200\uff0c\u4e09\u4e2a\u52a8\u4f5c \u4e00\u6c14\u5475\u6210\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u5fc3\u4e2d\u5f97\u610f\u4e4b\u6781\uff0c\u5ffd\u542c\u5f97\u94c1\u955c\u5fc3\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3\uff01\u201d\u9661\u7136\u95f4\u53ea\u89c1\u5200\u5149\u4e00 \u95ea\uff0c\u82a5\u5ddd\u9f99\u6728\u5df2\u6362\u4e86\u4e00\u628a\u502d\u5200\uff0c\u7a81\u7136\u5077\u88ad\uff0c\u5200\u950b\u5df2\u5241\u5230\u5979\u7684\u9762\u95e8\uff01 \u3000\u3000\u4ee5\u4e8e\u627f\u73e0\u7684\u672c\u9886\uff0c\u82e5\u7136\u7a0d\u52a0\u7559\u610f\uff0c\u82a5\u5ddd\u9f99\u6728\u7109\u80fd\u5077\u88ad\uff0c\u53ea\u662f\u4e8e\u627f\u73e0\u7684\u4e34\u9635\u7ecf\u9a8c\u5230\u5e95\u8fd8 \u6d45\uff0c\u800c\u4e14\u80dc\u6765\u5bb9\u6613\uff0c\u4e0d\u628a\u65e5\u672c\u6b66\u58eb\u653e\u5728\u5fc3\u4e0a\u3002\u82a5\u5ddd\u9f99\u6728\u7a76\u7adf\u662f\u65e5\u672c\u516b\u6bb5\u9ad8\u624b\uff0c\u65e5\u672c\u5206\u6bb5\uff0c\u975e \u5e38\u4e25\u683c\uff0c\u90a3\u662f\u7ecf\u8fc7\u5168\u56fd\u6b66\u58eb\u7684\u6bd4\u8bd5\u9009\u51fa\u6765\u7684\uff0c\u80fd\u591f\u4e0a\u6bb5\uff0c\u5df2\u975e\u5e78\u81f4\uff0c\u4e03\u524d\u9ad8\u6bb5\uff08\u4e03\u6bb5\u4ee5\u4e0a\u79f0 \u201c\u9ad8\u6bb5\u201d\uff09\uff0c\u66f4\u662f\u5be5\u5be5\u53ef\u6570\u3002\u5f53\u65f6\u65e5\u672c\u4e00\u56fd\uff0c\u53ea\u6709\u4e09\u540d\u4e5d\u6bb5\u9ad8\u624b\uff0c\u516b\u540d\u516b\u6bb5\u9ad8\u624b\uff0c\u82a5\u5ddd\u9f99\u6728 \u80fd\u5217\u540d\u5728\u5341\u4e00\u540d\u3002\u9ad8\u6bb5\u6b66\u58eb\u4e4b\u5185\uff0c\u6b66\u529f\u81ea\u6709\u8fc7\u4eba\u4e4b\u5904\uff0c\u4ed6\u88ab\u4e8e\u627f\u73e0\u65ad\u7d22\u524a\u5200\u4e4b\u540e\uff0c\u770b\u51fa\u4e86\u654c \u6211\u7684\u4f18\u52a3\uff0c\u77e5\u9053\u4e8e\u627f\u73e0\u662f\u80dc\u5728\u8eab\u6cd5\u8f7b\u7075\uff0c\u800c\u4e14\u6709\u4e00\u628a\u6bd4\u767e\u70bc\u502d\u5200\u8fd8\u8981\u950b\u5229\u7684\u5b9d\u5200\uff0c\u7acb\u5373\u5728\u5fc3 \u4e2d\u76d8\u7b97\u53d6\u80dc\u4e4b\u6cd5\u3002 \u3000\u3000\u504f\u5de7\u4e8e\u627f\u73e0\u8f7b\u654c\u8fc7\u751a\uff0c\u6ca1\u6709\u7559\u610f\uff0c\u53c8\u4ee5\u4e3a\u4ed6\u7684\u502d\u5200\u5df2\u65ad\uff0c\u522b\u65e0\u5175\u5668\uff0c\u66f4\u4e0d\u9632\u5907\u3002\u5979\u4e00\u65f6 \u6ca1\u6709\u7701\u8d77\uff0c\u82a5\u5ddd\u9f99\u6728\u88ab\u5979\u524a\u65ad\u7684\u90a3\u628a\u502d\u5200\uff0c\u4e43\u662f\u4ed6\u63a5\u4e86\u5c0f\u662d\u7684\uff0c\u4ed6\u8eab\u4e0a\u8fd8\u53e6\u85cf\u6709\u4e00\u628a\u81ea\u5df1\u7528 \u60ef\u7684\u502d\u5200\u3002\u8fde\u4ed6\u90a3\u526f\u5782\u5934\u4e27\u6c14\u7684\u6837\u5b50\uff0c\u4e5f\u662f\u88c5\u51fa\u6765\uff0c\u4ee5\u4fbf\u677e\u61c8\u654c\u4eba\u7684\u3002 \u3000\u3000\u82a5\u5ddd\u9f99\u6728\u8fd9\u4e00\u5200\u51fa\u5176\u4e0d\u610f\uff0c\u5f53\u771f\u662f\u72e0\u6bd2\u975e\u5e38\uff0c\u4e8e\u627f\u73e0\u5728\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u970d\u5730\u4e00\u4e2a\u201c\u5f69\u51e4 \u56de\u5934\u201d\uff0c\u4f7f\u51fa\u201c\u94c1\u677f\u6865\u201d\u7684\u529f\u592b\uff0c\u4e0a\u534a\u8eab\u540e\u4ef0\uff0c\u67f3\u8170\u5f2f\u6298\uff0c\u79c0\u53d1\u51e0\u4e4e\u89e6\u53ca\u5730\u9762\uff0c\u82a5\u5ddd\u9f99\u6728\u547c \u7684\u4e00\u5200\u4ece\u5979\u9762\u95e8\u524a\u8fc7\uff0c\u5374\u6ca1\u6709\u4f24\u7740\u5979\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c31\u5728\u6570\u5343\u502d\u5bc7\u8f70\u96f7\u822c\u7684\u559d\u5f69\u58f0\u4e2d\uff0c\u53ea\u89c1\u4e8e\u627f\u73e0\u811a\u8ddf\u4e00\u65cb\uff0c\u4e58\u7740\u654c\u4eba\u62db \u6570\u7528\u8001\uff0c\u9752\u51a5\u5251\u5728\u5730\u4e0a\u4e00\u78b0\uff0c\u7acb\u523b\u53cd\u5f39\uff0c\u4eba\u672a\u8dc3\u8d77\uff0c\u5251\u950b\u5df2\u524a\u81f3\u654c\u4eba\u7684\u624b\u8155\uff0c\u8fd9\u4e00\u62db\u602a\u5f02\u4e4b \u6781\uff0c\u662f\u5f20\u4e39\u67ab\u4ece\u201c\u767e\u53d8\u7384\u673a\u201d\u5251\u6cd5\u4e2d\uff0c\u53c2\u609f\u53d8\u5316\u51fa\u6765\u7684\uff0c\u4e13\u80fd\u8d25\u4e2d\u6c42\u80dc\uff0c\u53ea\u542c\u5f97\u5530\u7684\u4e00\u58f0\uff0c \u4e8e\u627f\u73e0\u4e00\u5251\u628a\u82a5\u6728\u7684\u56f4\u8170\u76ae\u5e26\u524a\u65ad\uff0c\u6bd5\u64ce\u5929\u5927\u58f0\u53eb\u597d\uff0c\u66ff\u82a5\u5ddd\u9f99\u6728\u52a9\u5a01\u7684\u502d\u5bc7\u767b\u65f6\u54d1\u53e3\u65e0 \u58f0\u3002 \u3000\u3000\u5c31\u5728\u6bd5\u64ce\u5929\u5927\u58f0\u53eb\u597d\u7684\u540c\u65f6\uff0c\u5ffd\u542c\u5f97\u94c1\u955c\u5fc3\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5440\uff0c\u4e0d\u5999\uff01\u201d\u6bd5\u64ce\u5929\u60ca\u9053\uff1a \u201c\u600e\u4e48\u4e0d\u5999\uff1f\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u53ea\u89c1\u82a5\u5ddd\u9f99\u6728\u5de6\u638c\u4e00\u62cd\uff0c\u500f\u5730\u4e00\u62ff\uff0c\u786c\u6293\u4e8e\u627f\u73e0\u7684\u624b\u8155\uff0c\u53f3\u624b\u7684 \u502d\u5200\u53c8\u6b3a\u8eab\u76f4\u8fdb\uff0c\u5288\u4e8e\u627f\u73e0\u7684\u4e0a\u81c2\u3002\u4e8e\u627f\u73e0\u53e6\u53e3\u4e00\u5251\u672c\u6765\u662f\u60f3\u628a\u502d\u5200\u524a\u65ad\u7684\uff0c\u54ea\u77e5\u82a5\u5ddd\u7684\u5200 \u6cd5\u5feb\u6781\uff0c\u4e8e\u627f\u73e0\u4f7f\u51fa\u602a\u62db\uff0c\u867d\u7136\u524a\u65ad\u4e86\u4ed6\u7684\u8170\u5e26\uff0c\u5374\u6ca1\u6709\u78b0\u7740\u4ed6\u7684\u5200\uff0c\u8868\u9762\u662f\u5360\u4e86\u4fbf\u5b9c\uff0c\u5b9e \u9645\u5374\u7ed9\u82a5\u5ddd\u62a2\u4e86\u653b\u52bf\u3002\u82a5\u5ddd\u7684\u201c\u795e\u98ce\u5200\u6cd5\u201d\u5728\u65e5\u672c\u56fd\u4e2d\uff0c\u5750\u7b2c\u4e09\u628a\u4ea4\u6905\uff0c\u4ec5\u6b21\u4e8e\u4e24\u4e2a\u4f7f\u5200\u7684 \u4e5d\u6bb5\u9ad8\u624b\uff0c\u795e\u98ce\u5200\u6cd5\u4ee5\u5feb\u901f\u89c1\u957f\uff0c\u7b2c\u4e00\u6d41\u7684\u9ad8\u624b\u5982\u82a5\u5ddd\u9f99\u6728\u8005\u53ef\u4ee5\u5728\u4e00\u79d2\u949f\u7684\u65f6\u95f4\u8fde\u5288\u4e03 \u5200\uff0c\u7aef\u7684\u662f\u51cc\u5389\u65e0\u5339\uff0c\u4e00\u62a2\u4e86\u5148\u624b\uff0c\u653b\u52bf\u7ef5\u7ef5\u4e0d\u7edd\uff01\u6b63\u662f\uff1a \u3000\u3000\u5251\u5f71\u5200\u5149\u98de\u821e\u5904\uff0c\u4e14\u770b\u5973\u4fa0\u632b\u502d\u950b\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e00\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u5341\u56db\u56de\u3000\u7ed5\u6811\u7a7f\u82b1\u3000\u4e66\u751f\u7591\u7389\u5973\u3000\u5174\u6ce2\u4f5c\u6d6a\u3000\u56fd\u624b\u6597\u9f99\u5934 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u5341\u56db\u56de\u3000\u7ed5\u6811\u7a7f\u82b1\u3000\u4e66\u751f\u7591\u7389\u5973\u3000\u5174\u6ce2\u4f5c\u6d6a\u3000\u56fd\u624b\u6597\u9f99\u5934 \u3000\u3000\u4e8e\u627f\u73e0\u4e00\u65f6\u6349\u6478\u4e0d\u900f\u8fd9\u79cd\u5200\u6cd5\uff0c\u53ea\u60f3\u7528\u5251\u53bb\u524a\u65ad\u4ed6\u7684\u502d\u5200\uff0c\u53ea\u662f\u82a5\u5ddd\u9f99\u6728\u90a3\u201c\u795e\u98ce\u5200 \u6cd5\u201d\u4f7f\u5f00\uff0c\u6709\u5982\u8fc5\u96f7\u63a3\u7535\uff0c\u5feb\u5f97\u96be\u4ee5\u5f62\u5bb9\uff0c\u6bcf\u4e00\u5200\u90fd\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u4e8e\u627f\u73e0\u4e34\u9635\u7ecf\u9a8c\u5c1a \u6d45\uff0c\u88ab\u4ed6\u5360\u4e86\u5148\u624b\uff0c\u53ea\u80fd\u5934\u6025\u533b\u5934\uff0c\u811a\u6025\u533b\u811a\uff0c\u968f\u7740\u654c\u4eba\u5200\u950b\u6240\u6307\uff0c\u8fd0\u5251\u5316\u89e3\u654c\u62db\uff0c\u8fd9\u6837\u4e00 \u6765\uff0c\u5168\u5c45\u88ab\u52a8\uff0c\u66f4\u96be\u524a\u65ad\u502d\u5200\u3002 \u3000\u3000\u82a5\u5ddd\u9f99\u6728\u8fde\u5288\u4e86\u6570\u5341\u5200\uff0c\u88ab\u4e8e\u627f\u73e0\u4e00\u4e00\u5316\u89e3\uff0c\u4e5f\u81ea\u6697\u6697\u60ca\u5fc3\uff0c\u795e\u98ce\u5200\u6cd5\u4f7f\u5f97\u66f4\u52a0\u51cc\u5389\uff0c \u4f46\u89c1\u56db\u9762\u516b\u65b9\u90fd\u662f\u5200\u5149\u5251\u5f71\uff0c\u82a5\u5ddd\u9f99\u6728\u66f4\u4e0d\u65f6\u4ee5\u65e5\u672c\u7684\u201c\u65e0\u5200\u672f\u201d\uff0c\u6765\u786c\u62a2\u4e8e\u627f\u73e0\u7684\u5b9d\u5251\uff0c \u65e5\u672c\u7684\u201c\u65e0\u5200\u672f\u201d\u548c\u4e2d\u56fd\u7684\u201c\u7a7a\u624b\u5165\u767d\u5203\u201d\uff0c\u540c\u5c5e\u4e00\u7c7b\u529f\u592b\uff0c\u8bb2\u7a76\u7684\u4e5f\u662f\u4e00\u4e2a\u201c\u5feb\u201d\u5b57\uff0c\u4e0d \u8fc7\u624b\u6cd5\u5374\u5404\u6709\u5de7\u5999\u4e0d\u540c\uff0c\u4e8e\u627f\u73e0\u4ed7\u7740\u8eab\u6cd5\u8f7b\u7075\uff0c\u4efb\u4ed6\u4f7f\u5c3d\u8bf8\u822c\u624b\u6cd5\uff0c\u9752\u51a5\u5b9d\u5251\u6454\u6d12\u81ea\u5982\uff0c\u4e0d \u8fc7\u5728\u654c\u4eba\u5200\u638c\u9f50\u653b\u4e4b\u4e0b\uff0c\u6dfb\u4e86\u4e00\u5c42\u987e\u5fcc\uff0c\u5b9d\u5251\u7684\u5a01\u529b\u66f4\u51cf\u4e86\u51e0\u5206\u3002 \u3000\u3000\u8fd9\u65f6\u82a5\u5ddd\u9f99\u6728\u5b8c\u5168\u5360\u4e86\u4e0a\u98ce\uff0c\u770b\u4e0a\u53bb\u4e8e\u627f\u73e0\u7adf\u53ea\u6709\u62db\u67b6\u7684\u4efd\u513f\uff0c\u573a\u4e2d\u7684\u65e5\u672c\u6b66\u58eb\u90fd\u5927\u58f0 \u559d\u5f69\uff0c\u4e3a\u540c\u4f34\u52a9\u5a01\uff0c\u8fde\u94c1\u955c\u5fc3\u548c\u6bd5\u64ce\u5929\u4e5f\u6697\u6697\u4e3a\u4e8e\u627f\u73e0\u62c5\u5fc3\uff0c\u500f\u5ffd\u95f4\uff0c\u5ffd\u89c1\u4e8e\u627f\u73e0\u8eab\u6cd5\u4e00 \u53d8\uff0c\u5728\u5200\u5149\u5251\u5f71\u4e2d\u7a7f\u6765\u63d2\u53bb\uff0c\u5de6\u624b\u5e76\u8d77\u4e2d\u98df\u4e8c\u6307\uff0c\u5ffd\u4f38\u5ffd\u7f29\uff0c\u4e0d\u4f4f\u5730\u5bfb\u7a7a\u62b5\u9699\uff0c\u6b3a\u8fd1\u8eab\u53bb\u70b9 \u654c\u4eba\u7684\u7a74\u9053\u3002\u82a5\u5ddd\u9f99\u6728\u7684\u51f6\u7130\u767b\u65f6\u88ab\u538b\u4e86\u4e0b\u6765\uff0c\u94c1\u3001\u6bd5\u4e8c\u4eba\u8fd9\u624d\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u539f\u6765\u4e8e\u627f\u73e0\u7ecf\u9a8c\u867d\u6d45\uff0c\u5230\u5e95\u662f\u53d7\u8fc7\u5f20\u4e39\u67ab\u85b0\u9676\u7684\u4e00\u4e2a\u673a\u7075\u4e4b\u6781\u7684\u59d1\u5a18\u3002\u5979\u770b\u51fa\u4e86\u654c\u4eba\u7684 \u957f\u5904\u5168\u5728\u5200\u6cd5\u7684\u4e00\u4e2a\u201c\u5feb\u201d\u5b57\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u51fa\u5251\u867d\u7136\u6ca1\u6709\u4ed6\u5feb\uff0c\u4f46\u8eab\u6cd5\u7684\u7075\u5feb\u5374\u8fdc\u80dc\u4e8e \u4ed6\uff0c\u4f55\u4e0d\u4ee5\u5df2\u4e4b\u957f\u5236\u654c\u4e4b\u77ed\u3002\u201d\u5fc3\u610f\u4e00\u51b3\uff0c\u7acb\u5373\u4fbf\u7528\u4e91\u857e\u6240\u6388\u7684\u201c\u7a7f\u82b1\u7ed5\u6811\u201d\u8eab\u6cd5\u4e0e\u82a5\u5ddd\u6e38 \u6597\uff0c\u540c\u65f6\u7528\u70b9\u7a74\u7684\u529f\u592b\uff0c\u53bb\u5236\u4ed6\u7684\u201c\u65e0\u5200\u672f\u201d\u3002 \u3000\u3000\u8fd9\u201c\u7a7f\u82b1\u7ed5\u6811\u201d\u7684\u8eab\u6cd5\u4e43\u662f\u7b2c\u4e00\u7b49\u7684\u79fb\u4f4d\u6362\u4f4d\u7684\u529f\u592b\uff0c\u6709\u5982\u821e\u8e48\uff0c\u8513\u5999\u4e4b\u6781\u3002\u94c1\u955c\u5fc3\u770b \u5f97\u5446\u4e86\uff0c\u4f4e\u58f0\u541f\u9053\uff1a\u201c\u9713\u88f3\u5999\u821e\u5dee\u582a\u62df\uff0c\u98de\u71d5\u8f7b\u76c8\u4e0d\u53ca\u4f0a\u3002\u201d\u6bd5\u64ce\u5929\u4e00\u76b1\u7709\u5934\uff0c\u72e0\u72e0\u5730\u6a2a\u4e86 \u4ed6\u4e00\u773c\u3002 \u3000\u3000\u4f46\u82a5\u5ddd\u9f99\u6728\u4ea6\u751a\u4e3a\u72e1\u733e\uff0c\u5e76\u4e0d\u8ddf\u7740\u4e8e\u627f\u73e0\u8f6c\u52a8\uff0c\u4ee5\u5feb\u901f\u7684\u5200\u6cd5\u4ee5\u5b88\u4e3a\u653b\uff0c\u53c8\u8fc7\u4e86\u6570\u5341 \u62db\uff0c\u4e8e\u627f\u73e0\u8eab\u5f62\u7a0d\u6162\uff0c\u82a5\u5ddd\u9f99\u6728\u5fc3\u9053\uff1a\u201c\u4f60\u8f6c\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c\u6c14\u529b\u81ea\u662f\u96be\u4ee5\u652f\u6301\u3002\u201d\u89d1\u51c6\u673a \u4f1a\uff0c\u731b\u5288\u4e00\u5200\uff0c\u4e8e\u627f\u73e0\u8eab\u5b50\u5411\u524d\u4e00\u4ec6\uff0c\u4f3c\u6b32\u503e\u5012\uff0c\u573a\u4e2d\u65e5\u4eba\u8f70\u96f7\u822c\u5730\u559d\u5f69\uff0c\u5374\u4e0d\u6599\u5c31\u5728\u8fd9\u4e00 \u970e\u90a3\uff0c\u53ea\u542c\u5f97\u201c\u55b7\u201d\u7684\u4e00\u58f0\uff0c\u82a5\u5ddd\u9f99\u6728\u5e9e\u5927\u7684\u8eab\u8eaf\u5df2\u8dcc\u4e86\u4e09\u4e08\u5f00\u5916\uff0c\u502d\u5200\u4e5f\u5230\u4e86\u4e8e\u627f\u73e0\u624b \u4e2d\uff0c\u88ab\u5979\u6298\u4e3a\u4e24\u6bb5\u3002\u539f\u6765\u8fd9\u662f\u4e8e\u627f\u73e0\u7684\u8bf1\u62db\uff0c\u8bf1\u4ed6\u77ed\u5175\u76f8\u6388\uff0c\u7a81\u7136\u70b9\u4e2d\u4e86\u4ed6\u624b\u8155\u7684\u5173\u5143\u7a74\uff0c \u6b64\u7a74\u88ab\u70b9\uff0c\u5168\u8eab\u9ebb\u75f9\uff0c\u54ea\u91cc\u8fd8\u80fd\u6321\u5f97\u4f4f\u4e8e\u627f\u73e0\u7684\u4e00\u51fb\uff1f \u3000\u3000\u82a5\u5ddd\u9f99\u6728\u8f93\u5f97\u201c\u83ab\u540d\u5176\u5999\u201d\uff0c\u65e5\u672c\u6b66\u58eb\u54d7\u7136\u5927\u95f9\uff0c\u7acb\u5373\u53c8\u63a8\u51fa\u4e00\u4e2a\u4eba\u6765\u6311\u6218\uff0c\u8fd9\u4eba\u6b63\u662f \u4e0e\u6bd5\u64ce\u5929\u6697\u4e2d\u8f83\u8fc7\u6307\u529b\u7684\u516b\u6bb5\u6b66\u58eb\u77f3\u4e95\u592a\u90ce\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u77e5\u9053\u77f3\u4e95\u592a\u90ce\u7684\u6b66\u529f\u6bd4\u82a5\u5ddd\u9f99\u6728\u66f4\u9ad8\uff0c\u672c\u60f3\u51fa\u53bb\u548c\u4ed6\u5bf9\u6297\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u77f3\u4e95\u592a \u90ce\u4e0d\u8fc7\u662f\u516b\u6bb5\u6b66\u571f\uff0c\u4ed6\u4eec\u8fd9\u8fb9\u8fd8\u6709\u4e00\u4e2a\u957f\u8c37\u5ddd\u662f\u4e5d\u6bb5\u3002\u81ea\u5df1\u662f\u201c\u5927\u9f99\u5934\u201d\u7684\u8eab\u4efd\uff0c\u5e94\u8be5\u4e0e\u4ed6 \u4eec\u7684\u4e5d\u6bb5\u65d7\u9f13\u76f8\u5f53\uff0c\u4ed6\u5374\u4e0d\u77e5\u9053\u65e5\u672c\u6b66\u58eb\u9053\u7684\u89c4\u77e9\uff0c\u5728\u6ca1\u6709\u540c\u7ea7\u7684\u6b66\u58eb\u7ade\u8d5b\u4e2d\uff0c\u4e5d\u6bb5\u662f\u4f8b\u4e0d \u4e0b\u573a\u7684\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u6b63\u5728\u8e0c\u8e87\u672a\u51b3\uff0c\u53ea\u89c1\u94c1\u955c\u5fc3\u5df2\u8d70\u51fa\u573a\u6765\uff0c\u6bd5\u64ce\u5929\u4e00\u559c\u4e00\u5fe7\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u77f3\u4e95 \u592a\u90ce\u7684\u6c14\u529b\u4e0e\u6211\u5dee\u4e0d\u591a\uff0c\u94c1\u955c\u5fc3\u600e\u80fd\u662f\u4ed6\u7684\u5bf9\u624b\uff1f\u201d\u968f\u5373\u53c8\u60f3\u9053\uff1a\u201c\u6211\u65b9\u5df2\u8fde\u80dc\u4e86\u4e09\u573a\uff0c\u4fbf \u8d25\u4e00\u573a\u4e5f\u65e0\u5173\u7d27\u8981\uff0c\u4e14\u7531\u5f97\u8fd9\u4e66\u5446\u5b50\u88ab\u632b\u4e00\u632b\u9a84\u6c14\u3002\u201d \u3000\u3000\u573a\u4e2d\u65b9\u4e95\u592a\u90ce\u4e0e\u94c1\u955c\u5fc3\u5df2\u4ea4\u4e0a\u4e86\u624b\uff0c\u77f3\u4e95\u592a\u90ce\u62f3\u6c89\u529b\u91cd\uff1a\u6bcf\u4e00\u62f3\u6253\u51fa\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u62f3\u98ce \u6240\u53ca\uff0c\u7802\u98de\u77f3\u8d70\uff0c\u5a01\u52bf\u786e\u662f\u60ca\u4eba\uff0c\u94c1\u955c\u5fc3\u65bd\u7528\u817e\u632a\u95ea\u5c55\u7684\u5c0f\u5de7\u624b\u6cd5\uff0c\u4e0e\u4ed6\u5468\u65cb\u4e86\u5341\u591a\u56de\u5408\uff0c \u6478\u719f\u4e86\u4ed6\u7684\u62f3\u8def\u4e4b\u540e\uff0c\u638c\u6cd5\u4e00\u53d8\uff0c\u5de6\u638c\u4e00\u62cd\uff0c\u77f3\u62ff\u75be\u4e03\uff0c\u53cc\u638c\u76f8\u8fde\uff0c\u5f62\u6210\u4e00\u4e2a\u5706\u5708\uff0c\u6070\u4f3c\u72c2 \u6d9b\u9a87\u6d6a\u5730\u7ffb\u7ffb\u6eda\u6eda\u800c\u6765\uff0c\u573a\u4e2d\u7684\u65e5\u672c\u6b66\u571f\u770b\u5f97\u76ee\u77aa\u53e3\u5446\u3002\u8981\u77e5\u795e\u98ce\u5200\u6cd5\u662f\u65e5\u672c\u6b66\u58eb\u5949\u4e3a\u81f3\u9ad8 \u65e0\u4e0a\u7684\u5200\u6cd5\uff0c\u65e5\u672c\u6b66\u571f\u4ee5\u4e3a\u4e16\u4e0a\u6ca1\u6709\u6bd4\u201c\u795e\u51e4\u5200\u6cd5\u201d\u8981\u5feb\u7684\u4e86\uff0c\u54ea\u77e5\u94c1\u955c\u5fc3\u7684\u60ca\u6d9b\u638c\u6cd5\u6709\u5982 \u8fc5\u96f7\u95ea\u7535\uff0c\u51fa\u624b\u6bd4\u521a\u624d\u82a5\u5ddd\u9f99\u6728\u7684\u795e\u98ce\u5200\u6cd5\u66f4\u5feb\uff0c\u4ed6\u4eec\u7109\u5f97\u4e0d\u60ca\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5ffd\u542c\u5f97\u94c1\u955c\u5fc3\u559d\u4e00\u58f0\u201c\u7740\u201d\uff0c\u556a\u7684\u4e00\u638c\u6253\u4e2d\u4e86\u77f3\u4e95\u592a\u90ce\u7684\u80cc\u5fc3\uff0c\u77f3\u4e95 \u592a\u90ce\u8eab\u5f62\u5fae\u6643\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u5ffd\u5730\u8f6c\u8eab\u4e00\u62f3\u6253\u5230\uff0c\u94c1\u955c\u5fc3\u8fd9\u4e00\u638c\u6253\u4e0b\uff0c\u5982\u89e6\u94c1\u77f3\uff0c\u638c\u5fc3\u9690\u9690\u4f5c \u75db\uff0c\u51b7\u4e0d\u9632\u4ed6\u4e00\u62f3\u6253\u5230\uff0c\u907f\u65e0\u53ef\u907f\uff0c\u53ea\u5f97\u4e00\u4fa7\u8eab\uff0c\u5de6\u65f6\u4e00\u62ac\uff0c\u6d88\u89e3\u4e86\u4ed6\u51e0\u5206\u52b2\u529b\uff0c\u7528\u80a9\u5934\u786c \u63a5\u4e86\u4ed6\u4e00\u62f3\uff0c\u77f3\u4e95\u592a\u90ce\u90a3\u4e00\u62f3\u6709\u4e03\u516b\u767e\u65a4\u6c14\u529b\uff0c\u5fc3\u4ee5\u4e3a\u94c1\u955c\u5fc3\u5fc5\u5c06\u9aa8\u65ad\u808b\u6298\uff0c\u54ea\u77e5\u4e00\u62f3\u6253 \u4e2d\uff0c\u94c1\u955c\u5fc3\u7684\u80a9\u5934\u7adf\u4f3c\u6d82\u4e86\u6cb9\u8102\u4e00\u6837\uff0c\u6ed1\u4e0d\u6e9c\u624b\uff0c\u62f3\u5934\u4e00\u64e6\u5373\u8fc7\uff0c\u94c1\u955c\u5fc3\u4e5f\u4e0d\u8fc7\u5fae\u5fae\u5730\u6643\u4e86 \u4e00\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u4e24\u4eba\u90fd\u662f\u6697\u6697\u5fc3\u60ca\uff0c\u94e3\u955c\u5fc3\u77e5\u9053\u5bf9\u65b9\u7684\u786c\u529f\uff0c\u5df2\u7ec3\u5230\u6781\u9ad8\u7684\u5883\u754c\uff0c\u867d\u7136\u4e0d\u77e5\u9053 \u4ed6\u7ec3\u7684\u662f\u4ec0\u4e48\u529f\u592b\uff0c\u4f46\u770b\u6765\u5374\u4e0e\u4e2d\u56fd\u5916\u5bb6\u62f3\u4e2d\u9876\u5389\u5bb3\u7684\u91d1\u949f\u7f69\u3001\u94c1\u5e03\u6749\u5dee\u4e0d\u591a\u3002\u77f3\u4e95\u592a\u90ce\u4e5f \u662f\u5fc3\u4e2d\u6697\u6697\u5600\u5495\uff0c\u60f3\u9053\uff1a\u201c\u4e45\u95fb\u4e2d\u56fd\u6b66\u58eb\u6709\u4e00\u79cd\u5185\u529f\uff0c\u5584\u80fd\u6d88\u89e3\u5bf9\u65b9\u7684\u62f3\u529b\uff0c\u83ab\u975e\u8fd9\u6587\u5f31\u6e05 \u79c0\u7684\u6b66\u58eb\uff0c\u5c31\u7ec3\u6709\u8fd9\u79cd\u795e\u5947\u5965\u5999\u7684\u5185\u529f\uff1f\u201d\u4f46\u4ed6\u81ea\u6043\u5168\u8eab\u575a\u5982\u6728\u77f3\uff0c\u5374\u4e5f\u5e76\u4e0d\u754f\u60e7\u3002\u8f6c\u773c \u95f4\uff0c\u4e24\u4eba\u53c8\u4ea4\u624b\u4e86\u51e0\u5341\u56de\u5408\uff0c\u94c1\u955c\u5fc3\u8fde\u7528\u91cd\u624b\u6253\u4e2d\u4e86\u4ed6\u6570\u638c\uff0c\u6253\u5f97\u4ed6\u66b4\u8df3\u5982\u96f7\uff0c\u9aa8\u9abc\u9690\u9690\u4f5c \u75db\u3001\u4f46\u5374\u603b\u4e0d\u80fd\u5c06\u4ed6\u6253\u5012\u3002\u8fd9\u5176\u95f4\uff0c\u77f3\u4e95\u592a\u90ce\u4e5f\u6253\u4e86\u94c1\u955c\u5fc3\u4e24\u62f3\uff0c\u4ea6\u662f\u88ab\u94c1\u955c\u5fc3\u7528\u5de7\u5999\u7684\u624b \u6cd5\uff0c\u4e0a\u4e58\u7684\u5185\u529f\u6d88\u89e3\u4e86\uff0c\u4ed6\u7684\u52b2\u9053\uff0c\u4e24\u4eba\u7adf\u662f\u8c01\u4e5f\u4f24\u4e0d\u4e86\u8c01\u3002\u6218\u5230\u5206\u9645\uff0c\u94c1\u955c\u5fc3\u865a\u6643\u4e00\u638c\uff0c \u5ffd\u5730\u7528\u65e5\u8bed\u53eb\u9053\uff1a\u201c\u4e14\u4f4f\uff01\u201d \u3000\u3000\u77f3\u4e95\u592a\u90ce\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u54b1\u4eec\u6253\u4e86\u534a\u5929\uff0c\u4f60\u4f24\u4e0d\u4e86\u6211\uff0c\u6211\u4e5f\u4f24\u4e0d\u4e86\u4f60\uff0c\u662f \u4e48\uff1f\u201d\u77f3\u4e95\u592a\u90ce\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u90a3\u4e48\u518d\u6253\u4e0b\u53bb\u4e5f\u6ca1\u6709\u4ec0\u4e48\u610f\u601d\u3002\u201d\u77f3\u4e95\u592a\u90ce \u9053\uff1a\u201c\u4f60\u60f3\u5c31\u6b64\u4f5c\u7b97\u4e86\u4e48\uff1f\u4e0d\u884c\uff0c\u4e0d\u884c\uff0c\u4f60\u4eec\u5df2\u80dc\u4e86\u4e09\u573a\uff0c\u8fd9\u4e00\u573a\u975e\u5206\u51fa\u80dc\u8d1f\u4e0d\u53ef\u3002\u201d\u94c1\u955c \u5fc3\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u6253\u6cd5\uff0c\u518d\u6253\u534a\u5929\u4e5f\u5206\u4e0d\u51fa\u80dc\u8d1f\u3002\u201d\u77f3\u4e95\u592a\u90ce\u9053\uff1a\u201c\u90a3\u4f60\u8bf4\u600e\u5730\uff1f\u201d\u94c1 \u955c\u5fc3\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u6253\u4e09\u62f3\uff0c\u6211\u4e5f\u7ed9\u4f60\u6253\u4e09\u62f3\u3002\u4f60\u6253\u6211\u65f6\uff0c\u6211\u4e00\u4e0d\u8eb2\u95ea\uff0c\u4e8c\u4e0d\u8fd8\u624b\uff1b\u6211\u6253\u4f60 \u65f6\uff0c\u4f60\u4e5f\u8981\u4e00\u6837\u3002\u201d\u77f3\u4e95\u592a\u90ce\u9053\uff1a\u201c\u82e5\u7136\u8fd8\u662f\u5f7c\u6b64\u65e0\u4f24\u5462\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u8fd9\u65b9\u6cd5\u662f\u6211\u63d0\u51fa \u6765\u7684\uff0c\u82e5\u7136\u8fd8\u662f\u5f7c\u6b64\u65e0\u4f24\uff0c\u90a3\u4fbf\u7b97\u4f5c\u6211\u8f93\u597d\u4e86\u3002\u201d\u77f3\u4e95\u592a\u90ce\u5927\u559c\uff0c\u4ed6\u88ab\u94c1\u955c\u5fc3\u7528\u91cd\u624b\u6cd5\u6253\u4e86 \u5341\u51e0\u4e0b\uff0c\u5468\u8eab\u9aa8\u80f3\u90fd\u5df2\u9690\u9690\u4f5c\u75db\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u518d\u6253\u4e0b\u53bb\uff0c\u53ea\u6709\u5403\u4e8f\u3002\u96be\u5f97\u5929\u4e0b\u7adf\u6709\u5982\u6b64\u7b28 \u86cb\u3002\u201d\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u8c01\u4eba\u5148\u6253\uff1f\u201d\u94c1\u955c\u5fc3\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u56fd\u4e43\u662f\u793c\u8ba9\u4e4b\u90a6\uff0c\u81ea\u7136\u8ba9\u4f60\u5148 \u6253\u3002\u201d\u4ee5\u811a\u8ddf\u4e3a\u8f74\uff0c\u63a5\u8fde\u5212\u4e86\u4e24\u4e2a\u5708\u5708\uff0c\u9053\uff1a\u201c\u8c01\u8981\u662f\u88ab\u6253\u51fa\u8fd9\u4e2a\u5708\u5708\uff0c\u4e5f\u7b97\u8f93\u4e86\u3002\u201d \u3000\u3000\u77f3\u4e95\u592a\u90ce\u5927\u559c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u627f\u8ba9\u4e86\uff01\u201d\u4e3e\u8d77\u7897\u53e3\u822c\u7c97\u5927\u7684\u62f3\u5934\uff0c\u201c\u7830\u201d\u7684\u4e00\u62f3\u5c31\u7167 \u94c1\u955c\u5fc3\u7684\u5934\u9762\u6253\u53bb\uff0c\u5fc3\u60f3\uff1a\u201c\u4efb\u4f60\u5185\u529f\u7ec3\u5f97\u591a\u597d\uff0c\u603b\u4e0d\u4f1a\u7ec3\u6210\u94c1\u5934\u3002\u201d\u54ea\u77e5\u94c1\u955c\u5fc3\u970d\u5730\u4e00\u4e2a \u51e4\u70b9\u5934\uff0c\u77f3\u4e95\u592a\u90ce\u8fd9\u4e00\u62f3\u5bf9\u51c6\u4e86\u4ed6\u7684\u5929\u7075\u76d6\uff0c\u94c1\u955c\u5fc3\u4e00\u4f4e\u5934\uff0c\u8fd9\u4e00\u62f3\u6070\u597d\u4ece\u4ed6\u7684\u5934\u76ae\u64e6\u8fc7\uff0c \u77f3\u4e95\u592a\u90ce\u6536\u52bf\u4e0d\u4f4f\uff0c\u51e0\u4e4e\u4ec6\u5012\u3002\u94c1\u955c\u5fc3\u7684\u811a\u6b65\u4e1d\u6beb\u6ca1\u6709\u79fb\u52a8\uff0c\u8eab\u5b50\u76f4\u633a\u633a\u5730\u7ad9\u5728\u5708\u5b50\u5f53\u4e2d\uff0c \u90a3\u81ea\u7136\u4e0d\u80fd\u7b97\u4ed6\u95ea\u907f\u3002\u94c1\u955c\u5fc3\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u4e24\u62f3\uff0c\u770b\u51c6\u4e86\u518d\u6253\u5427\u3002\u201d\u77f3\u4e95\u592a\u90ce\u60f3\u9053\uff1a\u201c\u662f \u4e86\uff0c\u6211\u6253\u7684\u76ee\u6807\u5e94\u8be5\u653e\u5927\u4e00\u4e9b\uff0c\u90a3\u4ed6\u5c31\u4e0d\u80fd\u53d6\u5de7\u4e86\u3002\u201d\u5927\u559d\u4e00\u58f0\uff0c\u7b2c\u4e8c\u62f3\u671d\u94c1\u955c\u5fc3\u7684\u5fc3\u53e3\u6253 \u53bb\uff0c\u5708\u5b50\u72ed\u7a84\uff0c\u5c31\u7b97\u4ed6\u4fa7\u8eab\u6216\u5f2f\u8170\u4e5f\u8981\u4e2d\u62f3\uff0c\u94c1\u955c\u5fc3\u6709\u610f\u5356\u5f04\uff0c\u63d0\u4e86\u4e00\u53e3\u5185\u5bb6\u771f\u6c14\uff0c\u628a\u80f8\u812f \u4e00\u633a\uff0c\u201c\u55b7\u201d\u7684\u4e00\u58f0\uff0c\u77f3\u4e95\u592a\u90ce\u7684\u62f3\u5934\u6709\u5982\u649e\u5230\u4e86\u4e00\u5757\u94c1\u677f\uff0c\u62f3\u5934\u7ed9\u5f39\u4e86\u51fa\u6765\uff0c\u5403\u4e86\u4e00\u60ca\uff0c \u5fc3\u9053\uff1a\u201c\u770b\u4e0d\u51fa\u8fd9\u4e2a\u6587\u5f31\u4e66\u751f\uff0c\u7adf\u7136\u4e5f\u7ec3\u5f97\u4e00\u8eab\u94dc\u76ae\u94c1\u9aa8\uff0c\u4f3c\u6211\u4e00\u822c\u3002\u201d\u5176\u5b9e\u94c1\u955c\u5fc3\u6240\u7ec3\u7684 \u529f\u592b\u548c\u4ed6\u5b8c\u5168\u4e0d\u540c\u8def\u5b50\uff0c\u4ed6\u662f\u628a\u5168\u8eab\u7684\u5185\u5bb6\u6c14\u529b\u90fd\u8fd0\u6765\u4fdd\u62a4\u5fc3\u53e3\uff0c\u8981\u662f\u77f3\u4e95\u592a\u90ce\u4e34\u65f6\u53d8\u5366\uff0c \u6253\u4ed6\u522b\u5904\u8981\u5bb3\uff0c\u4ed6\u5c31\u4e07\u4e07\u4e0d\u80fd\u62b5\u6321\u3002\u53ef\u662f\u77f3\u4e95\u5927\u90ce\u600e\u80fd\u77e5\u9053\uff1f \u3000\u3000\u94c1\u955c\u5fc3\u7b11\u9053\uff1a\u201c\u53ea\u6709\u6700\u540e\u4e00\u62f3\u4e86\uff0c\u6253\u5427\uff1f\u201d\u77f3\u4e95\u5927\u90ce\u624b\u81c2\u4e00\u6325\uff0c\u8fd0\u8db3\u6c14\u529b\uff0c\u7a81\u7136\u8e72\u4e0b\u9a6c \u6b65\uff0c\u7b2c\u4e09\u62f3\u7167\u94c1\u955c\u5fc3\u7684\u5c0f\u8179\u6253\u53bb\uff0c\u5fc3\u60f3\u5c0f\u8179\u7684\u808c\u8089\u6d6e\u8f6f\uff0c\u603b\u4e0d\u80fd\u7ec3\u6210\u94c1\u677f\u4e00\u822c\uff0c\u54ea\u77e5\u4e00\u62f3\u6253 \u4e0b\uff0c\u597d\u50cf\u6253\u8fdb\u4e86\u68c9\u82b1\u5806\u91cc\u4e00\u6837\uff0c\u8f6f\u7ef5\u7ef5\u7684\u6beb\u65e0\u53ef\u4ee5\u7740\u529b\u4e4b\u5904\uff0c\u62f3\u5934\u4e5f\u88ab\u5438\u7740\u4e86\uff0c\u94c1\u955c\u5fc3\u809a\u76ae \u4e00\u633a\uff0c\u5c06\u77f3\u4e95\u5927\u90ce\u5f39\u51fa\u6570\u5c3a\uff0c\u4e3e\u8d77\u62f3\u5934\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u73b0\u5728\u8f6e\u5230\u6211\u4e86\uff01\u201d\u77f3\u4e95\u592a\u90ce\u76ee\u77aa\u53e3 \u5446\uff0c\u60ca\u7591\u4e0d\u6b62\uff0c\u60f3\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u662f\u4f1a\u5996\u6cd5\u7684\u4e48\uff1f\u201d\u4efb\u4ed6\u5982\u4f55\u9a81\u52c7\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u6050\u60e7\u3002 \u3000\u3000\u4f46\u89c1\u94c1\u955c\u5fc3\u5251\u7709\u4e00\u7ad6\uff0c\u4e24\u9053\u773c\u5149\u5982\u5bd2\u51b0\uff0c\u5982\u5229\u5251\uff0c\u53ea\u662f\u5f80\u654c\u4eba\u8eab\u4e0a\u626b\u5c04\uff0c\u4ed6\u62f3\u5934\u9ad8\u9ad8\u4e3e \u8d77\uff0c\u5374\u8fdf\u8fdf\u4e0d\u5411\u4e0b\u6253\uff0c\u77f3\u4e95\u592a\u90ce\u5c31\u50cf\u4e00\u4e2a\u5c06\u88ab\u884c\u5211\u7684\u72af\u4eba\u4e00\u6837\uff0c\u6700\u521d\u672c\u662f\u9f13\u8d77\u52c7\u6c14\uff0c\u4f5c\u51fa\u4e00 \u526f\u51db\u7136\u65e0\u6240\u754f\u60e7\u7684\u6837\u5b50\u3002\u8fd9\u65f6\u5728\u94c1\u955c\u5fc3\u7684\u62f3\u5934\u5a01\u80c1\u4e4b\u4e0b\uff0c\u5c31\u50cf\u72af\u4eba\u88ab\u63a8\u5230\u94e1\u5200\u5200\u53e3\uff0c\u89c1\u7740\u5200 \u5149\u95ea\u95ea\uff0c\u800c\u94e1\u5200\u53c8\u5c06\u4e0b\u672a\u4e0b\u4e4b\u65f6\uff0c\u5fc3\u60c5\u4e0d\u7531\u5f97\u5927\u4e3a\u7d27\u5f20\uff0c\u51c4\u7f29\u8d77\u6765\u3002 \u3000\u3000\u77f3\u4e95\u592a\u90ce\u5fc3\u4e2d\u6050\u60e7\uff0c\u80a9\u818a\u4e0d\u81ea\u89c9\u5730\u8038\u4e86\u4e00\u8038\uff0c\u4f46\u4ed6\u5230\u5e95\u662f\u516b\u6bb5\u6b66\u58eb\uff0c\u5fc3\u4e2d\u6050\u60e7\u9762\u4e0a\u7edd\u4e0d \u8868\u9732\u51fa\u6765\uff0c\u786c\u7740\u5934\u76ae\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u652f\u90a3\u574f\u86cb\uff0c\u4f60\u6253\u8fd8\u662f\u4e0d\u6253\uff1f\u201d\u94c1\u955c\u5fc3\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6765\u5566\uff0c\u6765\u5566\uff01\u201d\u62f3\u5934\u4e00\u6643\uff0c\u500f\u5730\u6253\u4e0b\uff0c\u672a\u66fe\u89e6\u53ca\u77f3\u4e95\u592a\u90ce\u7684\u8eab\u4f53\uff0c\u5374\u53c8\u500f\u5730\u6536\u56de\uff0c\u8fd9\u4e00\u77ac \u95f4\uff0c\u4f46\u89c1\u77f3\u4e95\u5927\u90ce\u9888\u8116\u53e3\u7f29\uff0c\u7565\u7565\u4fa7\u8eab\uff0c\u7528\u5de6\u80a9\u6a2a\u626b\u4e0a\u6765\uff0c\u94c1\u955c\u5fc3\u5ffd\u5730\u6536\u624b\uff0c\u4ed6\u5374\u51e0\u4e4e\u6536\u52bf \u4e0d\u4f4f\uff0c\u77f3\u811a\u5411\u524d\u79fb\u4e86\u4e00\u6b65\uff0c\u5927\u58f0\u9a82\u9053\uff1a\u201c\u516b\u683c\u9a6c\u9e7f\uff01\u201d\u9a82\u58f0\u521a\u521a\u51fa\u53e3\uff0c\u94c1\u955c\u5fc3\u201c\u7830\u201d\u7684\u4e00\u638c \u626b\u53bb\uff0c\u5728\u4ed6\u53f3\u5c51\u7684\u7435\u7436\u9aa8\u4e0a\u7334\u72e0\u5730\u5288\u4e86\u4e00\u8bb0\uff0c\u77f3\u4e95\u592a\u90ce\u8eab\u5b50\u5931\u4e86\u5e73\u8861\uff0c\u767b\u65f6\u53c8\u5411\u540e\u9000\u4e86\u4e24 \u6b65\uff0c\u51e0\u4e4e\u7ed9\u94c1\u955c\u5fc3\u8fd9\u80a1\u731b\u5200\u63a8\u51fa\u5708\u5b50\uff0c\u5e78\u800c\u4ed6\u6536\u52bf\u5f97\u5feb\uff0c\u811a\u6b65\u521a\u521a\u81a8\u5728\u5708\u5b50\u7684\u8fb9\u7f18\u3002\u6025\u5fd9\u5411 \u5708\u5b50\u4e2d\u5fc3\u7ad9\u5b9a\uff0c\u5413\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u539f\u6765\u94c1\u955c\u5fc3\u6b64\u4eba\uff0c\u6709\u65f6\u867d\u7136\u8bfb\u4e66\u4e0d\u5316\uff0c\u4f46\u4e00\u4efd\u5c0f\u806a\u660e\u5374\u662f\u6709\u7684\u3002\u4ed6\u521a\u624d\u7684\u505a\u4f5c\uff0c\u6b63\u662f\u8bd5 \u63a2\u77f3\u4e95\u5927\u5373\u8eab\u4e0a\u7684\u5f31\u70b9\u6240\u5728\uff0c\u770b\u4e86\u77f3\u4e95\u592a\u90ce\u7684\u795e\u60c5\uff0c\u7acb\u523b\u77e5\u9053\u4ed6\u540e\u9888\u9888\u7a9d\u51f9\u4e0b\u7684\u6570\u5bf8\u4e4b\u5730\uff0c \u4fbf\u662f\u6700\u6015\u653b\u51fb\u7684\u5730\u65b9\uff0c\u90a3\u90e8\u4f4d\u6b63\u662f\u201c\u5929\u67f1\u7a74\u201d\u7684\u6240\u5728\uff0c\u94c1\u955c\u5fc3\u5fc3\u4e2d\u5927\u559c\uff0c\u9002\u624d\u6076\u6597\u4e4b\u65f6\uff0c\u4ed6 \u5df2\u7ecf\u5c61\u6b21\u60f3\u70b9\u77f3\u4e95\u592a\u90ce\u7684\u7a74\u9053\uff0c\u53ea\u56e0\u77f3\u4e95\u5927\u90ce\u4e00\u8eab\u786c\u529f\uff0c\u8eab\u5982\u94c1\u77f3\uff0c\u70b9\u7a74\u8bb2\u7a76\u8f7b\u5feb\uff0c\u96be\u4ee5\u8fd0 \u7528\u771f\u529b\uff0c\u63aa\u529b\u4e0d\u900f\uff0c\u867d\u7136\u70b9\u4e2d\u7a74\u9053\uff0c\u4e5f\u6ca1\u6709\u7528\u5904\uff0c\u6240\u4ee5\u4e0d\u6562\u5c1d\u8bd5\u3002\u800c\u4eca\u770b\u51fa\u4e86\u5730\u7684\u5f31\u70b9\uff0c\u6bd4 \u8d5b\u7684\u89c4\u77e9\uff0c\u53c8\u4e0d\u80fd\u95ea\u8eb2\u8fd8\u624b\uff0c\u8fd9\u60c5\u51b5\u4e0e\u53cc\u65b9\u4ea4\u624b\u7684\u6b63\u5f0f\u6bd4\u6597\u5927\u4e0d\u76f8\u540c\uff0c\u70b9\u7a74\u81ea\u53ef\u5168\u529b\u65bd\u4e3a\u3002 \u4f46\u4ed6\u80cc\u5411\u5916\u8fb9\uff0c\u5982\u4f55\u80fd\u591f\u6253\u5230\u4ed6\u7684\u80cc\u540e\u3002 \u3000\u3000\u4e5d\u6bb5\u6b66\u571f\u957f\u8c37\u5ddd\u5ffd\u5730\u559d\u9053\uff1a\u201c\u652f\u90a3\u574f\u86cb\u5c31\u8981\u4f7f\u8bc8\uff0c\u4f60\u7559\u5fc3\u80cc\u540e\u3002\u7ad9\u7a33\u4e86\u786c\u633a\uff0c\u4e0d\u53ef\u4fa7 \u8eab\u3002\u201d\u94c1\u955c\u5fc3\u61c2\u5f97\u65e5\u8bed\uff0c\u5fc3\u5934\u4e00\u51db\uff0c\u5c45\u7136\u7ed9\u957f\u8c37\u5ddd\u770b\u51fa\u4e86\u4ed6\u7684\u5fc3\u610f\uff0c\u4f46\u8fd9\u8bdd\u4e5f\u63d0\u9192\u4e86\u4ed6\u3002\u8bf4 \u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u94c1\u955c\u5fc3\u624b\u638c\u6325\u4e86\u534a\u4e2a\u5706\u5f27\uff0c\u500f\u5730\u5f80\u4ed6\u80f8\u53e3\u7684\u201c\u7487\u7391\u7a74\u201d\u4e00\u6309\uff0c\u77f3\u4e95\u592a\u90ce \u521a\u624d\u88ab\u4ed6\u5288\u4e2d\u7435\u7436\u9aa8\uff0c\u75bc\u75db\u672a\u5df2\uff0c\u8fd9\u4e2a\u90e8\u4f4d\u62b5\u6297\u7684\u529b\u9053\u6700\u5f31\uff0c\u94c1\u955c\u5fc3\u8fd9\u4e00\u6309\u6b63\u662f\u201c\u60ca\u6d9b\u638c \u6cd5\u201d\u4e2d\u6700\u7cbe\u5999\u7684\u62db\u6570\uff0c\u542b\u6709\u5de6\u65cb\u53f3\u8f6c\u7684\u4e24\u80a1\u529b\u9053\uff0c\u77f3\u4e95\u592a\u90ce\u867d\u7136\u5f97\u957f\u8c37\u5ddd\u63d0\u9192\uff0c\u8eab\u5b50\u4e5f\u4e0d\u7981 \u65cb\u8f6c\u8d77\u6765\uff0c\u8f6c\u4e86\u4e00\u4e2a\u4e00\u767e\u516b\u5341\u5ea6\uff0c\u80cc\u810a\u6070\u597d\u6b63\u5bf9\u654c\u624b\u3002\u94c1\u955c\u5fc3\u5927\u559d\u4e00\u58f0\uff0c\u500f\u5730\u5316\u638c\u4e3a\u6307\uff0c\u5f80 \u4ed6\u7684\u201c\u5929\u67f1\u7a74\u201d\u4e00\u6233\uff0c\u8fd9\u4e00\u4e0b\u529b\u900f\u6307\u5c16\uff0c\u5c31\u662f\u6709\u91d1\u949f\u7f69\u3001\u94c1\u5e03\u886b\u7684\u529f\u592b\u4e5f\u88ab\u7834\u4e86\uff0c\u4f55\u51b5\u77f3\u4e95 \u592a\u90ce\u53ea\u662f\u7528\u836f\u6c34\u6d78\u7ec3\u51fa\u6765\u7684\u786c\u529f\uff0c\u66f4\u4f55\u51b5\u90a3\u4e2a\u90e8\u4f4d\u6b63\u662f\u4ed6\u6700\u8106\u5f31\u7684\u6240\u5728\uff01\u201d \u3000\u3000\u53ea\u6240\u5f97\u77f3\u4e95\u592a\u90ce\u4e00\u58f0\u5389\u53eb\uff0c\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u767b\u65f6\u8dcc\u5012\uff0c\u65e5\u672c\u6b66\u571f\u5927\u60ca\uff0c\u6025\u5fd9\u4e0a\u524d\u62a2\u6551\uff0c \u4f46\u89c1\u77f3\u4e95\u592a\u90ce\u9762\u5982\u91d1\u7eb8\uff0c\u4e0a\u6c14\u4e0d\u63a5\u4e0b\u6c14\uff0c\u7adf\u7136\u662f\u53d7\u4e86\u6781\u91cd\u7684\u5185\u4f24\uff0c\u7eb5\u7136\u80fd\u591f\u6551\u6d3b\uff0c\u8fd9\u8eab\u786c\u529f \u4e5f\u8981\u5e9f\u4e86\u3002 \u3000\u3000\u4e24\u4e2a\u516b\u6bb5\u6b66\u58eb\u63a5\u8fde\u60e8\u8d25\uff0c\u5168\u573a\u9707\u52a8\uff0c\u5f80\u524d\u62a2\u6551\u7684\u65e5\u672c\u6b66\u571f\u53c8\u60ca\u53c8\u6012\uff0c\u6469\u62f3\u64e6\u638c\uff0c\u7eb7\u7eb7\u62e5 \u4e0a\uff0c\u94c1\u955c\u5fc3\u8d1f\u624b\u80cc\u540e\uff0c\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u65e5\u672c\u56fd\u7684\u6b66\u58eb\u9053\u7cbe\u795e\u4e48\uff1f\u201d\u5ffd\u542c\u5f97\u957f\u8c37\u5ddd\u5389\u58f0 \u559d\u9053\uff1a\u201c\u90fd\u7ed9\u6211\u9000\u4e0b\u3002\u201d\u767b\u65f6\u5168\u573a\u8083\u9759\uff0c\u53ea\u89c1\u957f\u8c37\u5ddd\u9762\u8272\u5982\u94c5\uff0c\u4e00\u6b65\u4e00\u6b65\u5730\u8e0f\u51fa\u573a\u6765\u3002\u94c1\u955c \u5fc3\u6b63\u5f85\u53d1\u8bdd\uff1b\u53ea\u89c1\u957f\u8c37\u5ddd\u5411\u4ed6\u4e00\u6307\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u9000\u4e0b\uff0c\u4f60\u77e5\u9053\u6211\u662f\u4f55\u4eba\uff1f\u6211\u5802\u5802\u4e5d\u6bb5\u9ad8\u624b\uff0c \u5c82\u80fd\u4e58\u4f60\u4e4b\u4e4f\uff01\u5484\uff0c\u4f60\u4eec\u7684\u961f\u957f\u662f\u8c01\uff1f\u201d \u3000\u3000\u6bd5\u64ce\u5929\u5e94\u58f0\u800c\u51fa\uff0c\u5176\u5b9e\u4ed6\u4eec\u6765\u65f6\uff0c\u4e5f\u5e76\u6ca1\u6709\u5b9a\u597d\u8c01\u662f\u201c\u961f\u957f\u201d\uff0c\u4e0d\u8fc7\u5728\u6bd5\u64ce\u5929\u7684\u5fc3\u76ee \u4e2d\uff0c\u65e9\u5df2\u662f\u4ee5\u201c\u9996\u9886\u201d\u81ea\u5c45\u3002\u4e00\u542c\u5f97\u901a\u8bd1\u4f20\u8bdd\uff0c\u7acb\u523b\u5927\u6b65\u51fa\u573a\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u6211\u6211\u4e48\uff1f\u597d \u6781\uff0c\u597d\u6781\uff0c\u6211\u6b63\u8981\u9886\u6559\u4f60\u4eec\u4e5d\u6bb5\u6b66\u58eb\u7684\u624b\u6bb5\u3002\u201d \u3000\u3000\u957f\u8c37\u5ddd\u7fd8\u8d77\u5927\u62c7\u6307\u9053\uff1a\u201c\u4f60\u662f\u4e2d\u56fd\u597d\u6c49\u7684\u5927\u9996\u9886\u4e48\uff1f\u201d\u502d\u5bc7\u519b\u4e2d\uff0c\u81ea\u6709\u901a\u756a\u5356\u56fd\u7684\u5978 \u7ec6\uff0c\u4e49\u519b\u4e2d\u91cd\u8981\u4eba\u7269\u7684\u5e95\u7ec6\u6253\u63a2\u5f97\u4e00\u6e05\u4e8c\u695a\uff0c\u6bd5\u64ce\u5929\u662f\u5317\u4e94\u7701\u7684\u201c\u5927\u9f99\u5934\u201d\uff0c\u65e9\u5df2\u6709\u5978\u7ec6\u62a5 \u544a\u7ed9\u957f\u8c37\u5ddd\uff0c\u4e0d\u8fc7\u957f\u8c37\u5ddd\u4e0d\u61c2\u5f97\u4e2d\u56fd\u7684\u201c\u5927\u9f99\u5934\u201d\u662f\u4ec0\u4e48\uff0c\u7528\u65e5\u8bed\u8bf4\u51fa\u6765\u65f6\uff0c\u5c31\u53d8\u6210\u4e86\u5927\u9996 \u9886\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u5f97\u610f\u4e4b\u6781\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u77e5\u9053\u6211\u7684\u76db\u540d\uff01\u201d\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e2d\u56fd\u7684\u597d\u6c49\u6570\u4e0d\u80dc \u6570\uff0c\u80dc\u8fc7\u6211\u7684\u4e5f\u4e0d\u77e5\u591a\u5c11\u3002\u4f55\u987b\u5927\u9996\u9886\u4e0e\u4f60\u8f83\u91cf\u3002\u201d\u957f\u8c37\u5ddd\u55d4\u76ee\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u5927\u9996\u9886 \u4e48\uff1f\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u8c2c\u627f\u4ed6\u4eec\u63a8\u9009\uff0c\u6211\u53ef\u4e0d\u6562\u4ee5\u5927\u9996\u9886\u81ea\u5c45\u3002\u201d\u957f\u8c37\u5ddd\u9053\uff1a\u201c\u4f60\u4eec\u652f \u90a3\u4eba\u603b\u4e0d\u723d\u5feb\uff0c\u65e2\u7136\u662f\u4e86\uff0c\u53c8\u4f55\u5fc5\u8c26\u865a\u3002\u597d\uff0c\u6211\u4eca\u65e5\u4ee5\u5927\u65e5\u672c\u4e5d\u6bb5\u6b66\u58eb\u7684\u8eab\u4efd\uff0c\u5411\u4f60\u4eec\u652f\u90a3 \u7684\u5927\u9996\u9886\u6311\u6218\uff01\u201d\u8981\u77e5\u65e5\u672c\u7684\u4e5d\u6bb5\u6b66\u58eb\uff0c\u9664\u975e\u6709\u540c\u4e00\u7ea7\u7684\u9ad8\u624b\u5728\u573a\uff0c\u5426\u5219\u5012\u4e0d\u4e0b\u573a\u3002\u957f\u8c37\u5ddd \u4ee5\u4e5d\u6bb5\u6b66\u571f\u53c8\u517c\u603b\u88c1\u5224\u7684\u8eab\u4efd\uff0c\u672c\u6765\u662f\u4e0d\u51c6\u5907\u51fa\u573a\u7684\uff0c\u4f46\u89c1\u4e24\u4e2a\u516b\u6bb5\u6b66\u58eb\u60e8\u8d25\uff0c\u81ea\u5df1\u8981\u4e0d\u51fa \u573a\uff0c\u65e5\u672c\u6b66\u58eb\u9053\u7684\u9762\u5b50\u5168\u88ab\u4e22\u5149\u3002\u8feb\u4e0d\u5f97\u5df2\uff0c\u51fa\u6765\u6311\u6218\uff0c\u53c8\u6545\u610f\u70b9\u660e\u6bd5\u64ce\u5929\u7684\u8eab\u4efd\uff0c\u542b\u6df7\u5730 \u628a\u6bd5\u64ce\u5929\u62ac\u9ad8\u4e3a\u201c\u4e2d\u56fd\u597d\u6c49\u7684\u5927\u9996\u9886\u201d\uff0c\u90a3\u662f\u8bf4\u7ed9\u672c\u56fd\u4eba\u542c\u7684\uff0c\u8868\u660e\u81ea\u5df1\u5e76\u4e0d\u662f\u4e3a\u4e86\u4e00\u4e2a\u666e \u901a\u4eba\u7269\u800c\u7834\u4f8b\u4e0b\u573a\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u5f53\u771f\u662f\u5168\u573a\u8038\u52a8\uff0c\u5728\u573a\u7684\u5343\u591a\u65e5\u672c\u4eba\uff0c\u4e2a\u4e2a\u90fd\u662f\u53c8\u5174\u594b\u53c8\u62c5\u5fc3\uff0c\u4ed6\u4eec\u672c\u6765\u5bf9\u4e5d\u6bb5 \u6b66\u58eb\u5949\u82e5\u795e\u660e\uff0c\u8ba4\u4e3a\u4ed6\u4eec\u6218\u65e0\u4e0d\u80dc\uff0c\u4f46\u4eca\u65e5\u63a5\u8fde\u770b\u4e86\u51e0\u573a\u4e2d\u56fd\u6b66\u58eb\u7684\u5947\u5999\u6b66\u529f\uff0c\u8fd9\u4fe1\u5fc3\u5374\u53c8 \u4e0d\u514d\u591a\u5c11\u6709\u70b9\u52a8\u6447\uff0c\u751f\u6015\u957f\u8c37\u5ddd\u4e5f\u5403\u8d25\u4ed7\u3002\u4eba\u4eba\u90fd\u7741\u5927\u773c\u775b\uff0c\u770b\u8fd9\u6709\u5173\u65e5\u672c\u6b66\u58eb\u9053\u8363\u8fb1\u7684\u4e00 \u573a\u51b3\u6218\u3002 \u3000\u3000\u5168\u573a\u9e26\u96c0\u65e0\u58f0\uff0c\u8fde\u4e00\u6839\u9488\u8dcc\u5728\u5730\u4e0b\u90fd\u542c\u5f97\u89c1\u54cd\u3002\u573a\u4e2d\u7684\u957f\u8c37\u5ddd\u4e0e\u6bd5\u64ce\u5929\u4e8c\u4eba\u77aa\u76ee\u5bf9\u89c6\uff0c \u72b6\u82e5\u6597\u9e21\u3002\u53cc\u65b9\u90fd\u5728\u51dd\u795e\u5f85\u654c\uff0c\u4e0d\u6562\u62a2\u5148\u53d1\u62db\u3002 \u3000\u3000\u8fd9\u8fb9\u53a2\uff0c\u4e8e\u627f\u73e0\u4e0e\u94c1\u955c\u5fc3\u4e16\u6697\u6697\u634f\u4e86\u4e00\u628a\u51b7\u6c57\uff0c\u4ed6\u4eec\u4e8c\u4eba\u5404\u81ea\u80dc\u4e86\u4e00\u4e2a\u516b\u6bb5\u6b66\u58eb\uff0c\u80dc\u6765 \u6b8a\u4e0d\u5bb9\u6613\uff0c\u516b\u6bb5\u5982\u6b64\uff0c\u4e5d\u6bb5\u53ef\u77e5\uff0c\u6bd5\u64ce\u5929\u65e2\u5df2\u81ea\u8ba4\u662f\u4ed6\u4eec\u7684\u9886\u8896\uff0c\u82e5\u7136\u8fd9\u573a\u8f93\u4e86\uff0c\u5219\u4ee5\u524d\u8fde \u80dc\u7684\u51e0\u573a\uff0c\u4e5f\u5c06\u9eef\u7136\u5931\u8272\u3002 \u3000\u3000\u6570\u5343\u5bf9\u773c\u775b\u5168\u795e\u51dd\u89c6\u6597\u573a\uff0c\u5ffd\u542c\u5f97\u573a\u4e2d\u4e8c\u4eba\u540c\u65f6\u5927\u559d\u4e00\u58f0\uff0c\u98de\u8eab\u731b\u6251\uff01\u6bd5\u64ce\u5929\u4e00\u51fa\u624b\u4fbf \u662f\u5927\u6454\u7891\u624b\uff0c\u4ed6\u5929\u751f\u795e\u529b\uff0c\u5927\u6454\u7891\u624b\u53c8\u662f\u6700\u521a\u52b2\u7684\u638c\u6cd5\uff0c\u624b\u811a\u8d77\u5904\uff0c\u5168\u5e26\u52b2\u98ce\uff0c\u767b\u65f6\u5377\u5f97\u7802 \u98de\u77f3\u8d70\uff0c\u5728\u65c1\u8fb9\u9a7b\u8db3\u800c\u89c2\u7684\u65e5\u672c\u6b66\u58eb\u7eb7\u7eb7\u9000\u540e\uff0c\u8fd9\u5a01\u52bf\u6bd4\u9002\u624d\u7684\u77f3\u4e95\u592a\u90ce\u66f4\u8981\u60ca\u4eba\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u89c1\u6bd5\u64ce\u5929\u638c\u529b\u5982\u6b64\u96c4\u52b2\uff0c\u4e5f\u4e0d\u7981\u6697\u4e2d\u559d\u5f69\uff0c\u770b\u770b\u90a3\u4e00\u638c\u5df2\u6253\u5230\u957f\u8c37\u5ddd\u7684\u8eab\u4e0a\uff0c\u5ffd \u89c1\u957f\u8c37\u5ddd\u624b\u638c\u4e00\u5207\uff0c\u4e24\u4eba\u8eab\u5f62\u500f\u5730\u5206\u5f00\uff0c\u5404\u81ea\u8dc4\u8dc4\u8e09\u8e09\u5730\u5012\u9000\u4e09\u6b65\uff0c\u4e24\u4eba\u7684\u52a8\u4f5c\u90fd\u662f\u5feb\u5230\u6781 \u70b9\uff0c\u56f4\u5728\u573a\u8fb9\u7684\u65e5\u672c\u6b66\u58eb\uff0c\u4f46\u89c1\u4ed6\u4eec\u4e00\u5408\u5373\u5206\uff0c\u7a0d\u6cbe\u5373\u9000\uff0c\u90fd\u4e0d\u77e5\u9053\u5176\u4e2d\u5965\u5999\uff0c\u94c1\u955c\u5fc3\u5374\u662f \u5927\u5403\u4e00\u60ca\uff0c\u957f\u8c37\u5ddd\u7684\u51fa\u624b\uff0c\u7528\u7684\u7adf\u662f\u4e0a\u4e58\u7684\u501f\u529b\u6253\u529b\u529f\u592b\uff01 \u3000\u3000\u539f\u6765\u957f\u8c37\u5ddd\u7684\u201c\u67d4\u9053\u201d\u529f\u592b\uff0c\u5728\u65e5\u672c\u624b\u5c48\u4e00\u6307\u3002\u201c\u67d4\u9053\u201d\u672c\u6765\u662f\u4ece\u4e2d\u56fd\u4f20\u53bb\u7684\u592a\u6781\u62f3\u53d8 \u5316\u51fa\u6765\u7684\uff0c\u6240\u7528\u7684\u6b66\u529f\u539f\u7406\u548c\u592a\u6781\u62f3\u4e00\u6837\uff0c\u7ec3\u5230\u6700\u9ad8\u7684\u5883\u754c\u4e4b\u65f6\uff0c\u90fd\u80fd\u501f\u529b\u6253\u529b\uff0c\u6709\u201c\u56db\u4e24 \u62e8\u5343\u65a4\u201d\u4e4b\u5999\uff0c\u957f\u8c37\u5ddd\u5f53\u7136\u8fd8\u6ca1\u6709\u7ec3\u5230\u8fd9\u7b49\u5883\u754c\uff0c\u4f46\u4e0e\u4e2d\u56fd\u4e00\u6d41\u7684\u592a\u6781\u9ad8\u624b\u4ea6\u5df2\u76f8\u53bb\u65e0\u591a\uff0c \u6bd5\u64ce\u5929\u90a3\u4e00\u638c\u626b\u51fa\uff0c\u529b\u903e\u5343\u65a4\uff0c\u4e00\u80a1\u731b\u52b2\uff0c\u7a81\u7136\u7ed9\u4ed6\u5378\u5f00\uff0c\u91cd\u5fc3\u767b\u65f6\u5931\u4e86\u5e73\u8861\uff0c\u672c\u6765\u975e\u8dcc\u5012 \u4e0d\u53ef\uff0c\u5e78\u4e8f\u6bd5\u64ce\u5929\u4e5f\u662f\u5185\u5916\u53cc\u4fee\uff0c\u89c1\u673a\u5f97\u65e9\uff0c\u5c31\u5728\u90a3\u4e00\u77ac\u4e4b\u95f4\uff0c\u5f3a\u628a\u5927\u6454\u7891\u624b\u7684\u731b\u52b2\uff0c\u7a81\u7136 \u715e\u4f4f\uff0c\u5de6\u638c\u540c\u65f6\u53cd\u5288\uff0c\u5c06\u957f\u8c37\u5ddd\u7684\u773c\u795e\u4e00\u5f15\uff0c\u53c8\u500f\u5730\u53d8\u638c\u4e3a\u6307\uff0c\u53cd\u624b\u70b9\u957f\u8c37\u5ddd\u989d\u4e0a\u7684\u201c\u767d\u864e \u7a74\u201d\uff0c\u957f\u8c37\u5ddd\u77e5\u9053\u4e2d\u56fd\u70b9\u7a74\u6cd5\u7684\u5389\u5bb3\uff0c\u903c\u5f97\u9000\u540e\u4e09\u6b65\uff0c\u800c\u6bd5\u64ce\u5929\u4e5f\u56e0\u7a81\u7136\u715e\u4f4f\uff0c\u7acb\u8db3\u4e0d\u7a33\uff0c \u7ed9\u81ea\u5df1\u7684\u90a3\u80a1\u731b\u52b2\u7684\u53cd\u529b\u63a8\u5f97\u540e\u9000\u4e09\u6b65\uff0c\u8fd9\u624d\u91cd\u65b0\u7ef4\u6301\u4e86\u8eab\u4f53\u7684\u91cd\u5fc3\u3002 \u3000\u3000\u4e24\u4eba\u4ea4\u6362\u4e86\u4e00\u62db\uff0c\u53cc\u65b9\u90fd\u6ca1\u6709\u53d6\u80dc\u7684\u628a\u63e1\uff0c\u6bd5\u64ce\u5929\u4f7f\u51fa\u5bb6\u4f20\u7684\u964d\u9f99\u638c\u6cd5\uff0c\u5de6\u638c\u7528\u7684\u662f\u9633 \u521a\u4e4b\u529b\uff0c\u53f3\u638c\u5219\u7528\u9634\u67d4\u4e4b\u529b\uff0c\u521a\u67d4\u4e92\u6d4e\uff0c\u628a\u654c\u4eba\u62d2\u5728\u79bb\u8eab\u516b\u5c3a\u4e4b\u5916\uff0c\u957f\u8c37\u5ddd\u7684\u201c\u67d4\u9053\u201d\u4e00\u5b9a \u8981\u89e6\u53ca\u654c\u4eba\u7684\u8eab\u4f53\u624d\u80fd\u65bd\u5c55\uff0c\u6bd5\u64ce\u5929\u5de6\u653b\u53f3\u5b88\uff0c\u603b\u4e0d\u8ba9\u4ed6\u6b3a\u8fd1\u8eab\u8fb9\u3002\u4f46\u5982\u6b64\u4e00\u6765\uff0c\u6bd5\u64ce\u5929\u4e5f \u65e0\u6cd5\u6253\u4e2d\u957f\u8c37\u5ddd\uff0c\u4e24\u4eba\u6e38\u6597\u4e86\u6570\u5341\u56de\u5408\uff0c\u5140\u662f\u6210\u4e86\u4e2a\u4e24\u5e73\u4e4b\u5c40\u3002\u573a\u4e2d\u7684\u502d\u5bc7\u770b\u5f97\u6697\u6697\u7eb3\u7f55\uff0c \u4ed6\u4eec\u56fd\u4e2d\uff0c\u4ee5\u4e5d\u6bb5\u4e3a\u6700\u9ad8\u7684\u8363\u8a89\uff0c\u5bfb\u5e38\u7684\u6b66\u4e0a\u6839\u672c\u6ca1\u6709\u8d44\u683c\u53c2\u89c2\u4e5d\u6bb5\u7684\u89d2\u6597\uff0c\u66f4\u4e0d\u8981\u8bf4\u8fd9\u4e9b \u51fa\u56fd\u4f5c\u6d77\u76d7\u751f\u6daf\u7684\u6d6a\u4eba\u502d\u5bc7\u4e86\u3002\u6240\u4ee5\u4ed6\u4eec\u4e5f\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\u4e5d\u6bb5\u9ad8\u624b\u7684\u6280\u827a\uff0c\u60f3\u4e0d\u5230\u4e5d\u6bb5\u9ad8\u624b \u51fa\u573a\uff0c\u7adf\u7136\u662f\u573a\u95f7\u6218\uff0c\u5728\u4ed6\u4eec\u770b\u6765\uff0c\u8fd8\u8fdc\u4e0d\u53ca\u521a\u624d\u90a3\u51e0\u573a\u7684\u7cbe\u5f69\u523a\u6fc0\u3002\u53ef\u662f\u5728\u94c1\u955c\u5fc3\u4e0e\u4e8e\u627f \u73e0\u770b\u6765\uff0c\u90a3\u5374\u662f\u4e00\u573a\u6781\u51f6\u9669\u7684\u640f\u6597\uff0c\u770b\u6765\u867d\u662f\u95f7\u6218\uff0c\u5b9e\u5219\u53cc\u65b9\u90fd\u5728\u5bfb\u7a7a\u89c5\u9699\uff0c\u54ea\u4e00\u65b9\u7a0d\u6709\u4e0d \u614e\uff0c\u5c31\u7acb\u523b\u6709\u6027\u547d\u4e4b\u5fe7\u3002 \u3000\u3000\u4e45\u6218\u4e0d\u51b3\uff0c\u6bd5\u64ce\u5929\u6e10\u6e10\u7126\u8e81\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e8e\u627f\u73e0\u548c\u94c1\u955c\u5fc3\u4ed6\u4eec\u90e8\u80dc\u5f97\u5149\u5f69\u6f02\u4eae\uff0c\u6211\u82e5 \u8f93\u7ed9\u8fd9\u4e2a\u502d\u5bc7\uff0c\u6709\u4f55\u9762\u76ee\u505a\u4ed6\u4eec\u7684\u5927\u9f99\u5934\uff1f\u201d\u60f3\u8d77\u4e8e\u627f\u73e0\u548c\u94c1\u955c\u5fc3\u90a3\u4e24\u573a\u90fd\u662f\u4ee5\u70b9\u7a74\u6cd5\u53d6 \u80dc\uff0c\u4e5f\u60f3\u4f9d\u6837\u753b\u846b\u82a6\u5c06\u957f\u8c37\u5ddd\u70b9\u5012\uff0c\u53ef\u662f\u957f\u8c37\u5ddd\u7684\u201c\u67d4\u9053\u201d\u529f\u592b\u9ad8\u660e\u4e4b\u6781\uff0c\u53ea\u8981\u88ab\u4ed6\u4e00\u89e6\u7740 \u8eab\u4f53\u5c31\u80fd\u7ed9\u4ed6\u501f\u529b\u6253\u529b\uff0c\u624b\u6307\u600e\u70b9\u5f97\u5230\u4ed6\u7684\u8eab\u4e0a\uff1f \u3000\u3000\u573a\u8fb9\u94c1\u955c\u5fc3\u4e0e\u4e8e\u627f\u73e0\u5e76\u80a9\u89c2\u6218\uff0c\u770b\u5230\u6b64\u9645\uff0c\u94c1\u955c\u5fc3\u624d\u677e\u4e86\u53e3\u6c14\uff0c\u5bf9\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6bd5\u5927\u54e5 \u539f\u6765\u4ea6\u662f\u7c97\u4e2d\u6709\u7ec6\uff0c\u7528\u8fd9\u6837\u7684\u6218\u6cd5\uff0c\u7eb5\u4e0d\u80fd\u80dc\uff0c\u4ea6\u53ef\u4fdd\u6301\u4e0d\u8d25\u3002\u53ea\u8981\u8fd9\u4e00\u573a\u6253\u6210\u5e73\u624b\uff0c\u54b1\u4eec \u4eca\u5929\u5c31\u80dc\u5b9a\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8bba\u771f\u5b9e\u7684\u529f\u592b\u53ea\u6015\u662f\u90a3\u957f\u8c37\u5ddd\u8fd8\u80dc\u4e00\u7b79\uff0c\u597d\u5728\u6bd5\u5927\u54e5 \u7684\u964d\u9f99\u638c\u6cd5\u5389\u5bb3\uff0c\u5185\u529b\u4e5f\u6bd4\u654c\u4eba\u5f3a\u5f97\u591a\uff0c\u8fd9\u6837\u7f20\u6597\u4e0b\u53bb\uff0c\u5e76\u4e0d\u5403\u4e8f\u3002\u6015\u5c31\u6015\u4ed6\u8d2a\u529f\u8e81\u8fdb\u3002\u201d \u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u6bd5\u64ce\u5929\u638c\u6cd5\u4e00\u53d8\uff0c\u6709\u5982\u957f\u6c5f\u5927\u6cb3\u6eda\u6eda\u800c\u4e0a\uff0c\u4e00\u6d3e\u7c97\u65f7\u4e4b\u6c14\uff0c\u624b\u811a\u8d77\u5904\uff0c\u5168\u5e26 \u52b2\u98ce\uff0c\u957f\u8c37\u5ddd\u7ed9\u4ed6\u903c\u5f97\u6b65\u6b65\u540e\u9000\uff0c\u573a\u4e2d\u7684\u65e5\u672c\u6b66\u58eb\uff0c\u5168\u90e8\u76f8\u987e\u5931\u8272\uff01 \u3000\u3000\u94c1\u955c\u5fc3\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u7cdf\u5566\uff01\u201d\u53ea\u89c1\u6bd5\u64ce\u5929\u731b\u5730\u4e00\u4e2a\u864e\u8df3\uff0c\u5de6\u638c\u4e00\u7a7f\uff0c\u62e8\u5f00\u957f\u8c37\u5ddd\u7684\u624b \u81c2\uff0c\u500f\u5730\u5e76\u6307\u5982\u671d\uff0c\u5411\u4ed6\u80c1\u4e0b\u7684\u201c\u4e2d\u5b6a\u7a74\u201d\u72e0\u72e0\u4e00\u6233\uff0c\u8fd9\u4e00\u4e0b\u5192\u9669\u72af\u96be\uff0c\u624b\u6cd5\u5e72\u51c0\u5229\u843d\uff0c\u786e \u662f\u6781\u5176\u9ad8\u660e\u7684\u70b9\u7a74\u62db\u6570\uff0c\u4e8e\u627f\u73e0\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u7cdf\u5566\uff1f\u201d\u5fc3\u5ff5\u672a\u8f6c\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6 \u5feb\uff0c\u53ea\u89c1\u957f\u8c37\u5ddd\u4e00\u4e2a\u53cd\u8eab\u5408\u62b1\uff0c\u53cc\u624b\u5df2\u6273\u7740\u4e86\u6bd5\u64ce\u5929\u7684\u81c2\u818a\uff0c\u53cd\u526a\u80cc\u540e\uff0c\u8fd9\u4e00\u7740\u4e43\u662f\u67d4\u9000\u4e2d \u6781\u4e3a\u5389\u5bb3\u7684\u4e00\u7740\u624b\u6cd5\uff0c\u79f0\u4e3a\u201c\u53cd\u624b\u81ea\u6295\u201d\u7684\u7edd\u6280\uff0c\u6bd5\u64ce\u5929\u53cc\u81c2\u53d7\u5236\uff0c\u957f\u8c37\u5ddd\u53ea\u8981\u501f\u4ed6\u672c\u8eab\u7684 \u6323\u624e\u4e4b\u529b\uff0c\u5c31\u53ef\u5c06\u4ed6\u4e3e\u8d77\u6454\u4e2a\u5927\u7b4b\u6597\u3002\u65e5\u672c\u6b66\u58eb\u4eec\u8f70\u96f7\u822c\u5730\u559d\u5f69\uff0c\u5c31\u7b49\u7740\u77a7\u957f\u8c37\u5ddd\u600e\u6837\u6454\u6bd5 \u64ce\u5929\u3002 \u3000\u3000\u5374\u4e0d\u6599\u573a\u4e2d\u4e24\u4eba\u5ffd\u7136\u90fd\u4f3c\u77f3\u50cf\u4e00\u822c\uff0c\u50f5\u7acb\u4e0d\u52a8\uff0c\u957f\u8c37\u5ddd\u4ecd\u7136\u6273\u7740\u534e\u64ce\u5929\u7684\u81c2\u818a\uff0c\u53ef\u662f\u5374 \u5e76\u6ca1\u6709\u5c06\u5730\u4e3e\u8d77\u6765\uff0c\u6bd5\u64ce\u5929\u53cc\u811a\u7262\u7262\u9489\u7740\u5730\u9762\uff0c\u6709\u5982\u6253\u6869\u4e00\u822c\uff0c\u8eab\u5b50\u7eb9\u98ce\u4e0d\u52a8\uff0c\u4e24\u4eba\u56db\u773c\uff0c \u76f8\u5bf9\u6012\u89c6\uff0c\u4ee4\u4eba\u5fc3\u60b8\uff0c\u5f62\u72b6\u5374\u53c8\u900f\u7740\u6709\u70b9\u6ed1\u7a3d\u3002 \u3000\u3000\u539f\u6765\u6bd5\u64ce\u5929\u662f\u60f3\u4ee5\u5feb\u6253\u5feb\u7684\u638c\u6cd5\uff0c\u51fa\u5176\u4e0d\u610f\u5730\u7a81\u7136\u53bb\u70b9\u654c\u4eba\u7684\u7a74\u9053\uff0c\u4ed6\u4e5f\u77e5\u9053\u957f\u8c37\u5ddd\u67d4 \u9053\u529f\u6df1\uff0c\u4f46\u4e0d\u5192\u9669\u5219\u65e0\u6cd5\u53d6\u80dc\uff0c\u6545\u6b64\u62fc\u7740\u88ab\u4ed6\u6454\u5012\uff0c\u4e5f\u8981\u4e00\u8bd5\uff0c\u4ed6\u70b9\u7684\u201c\u4e2d\u5b6a\u7a74\u201d\u662f\u4eba\u8eab\u7684 \u4e5d\u5927\u9ebb\u7a74\u4e4b\u4e00\uff0c\u4ee5\u4e3a\u957f\u8c37\u5ddd\u82e5\u7ed9\u4ed6\u70b9\u4e2d\uff0c\u5168\u8eab\u4fbf\u7acb\u65f6\u9ebb\u8f6f\u65e0\u529b\uff0c\u90a3\u65f6\u81ea\u5df1\u7eb5\u7136\u7ed9\u4ed6\u6454\u5012\uff0c\u4e5f \u4e0d\u4f1a\u53d7\u4f24\u3002\u54ea\u77e5\u957f\u8c37\u5ddd\u7684\u67d4\u9053\u5df2\u7ec3\u5230\u4e5d\u6bb5\u5883\u754c\uff0c\u808c\u8089\u53ef\u4ee5\u6269\u5f20\u6536\u7f29\uff0c\u968f\u5fc3\u63a7\u5236\u3002\u6bd5\u64ce\u5929\u4e00\u6307 \u6233\u4e0b\uff0c\u5ffd\u89c9\u654c\u4eba\u7684\u8179\u808c\u7a81\u7136\u5185\u9677\uff0c\u70b9\u7a74\u7684\u6307\u529b\u7ade\u88ab\u6d88\u4e8e\u65e0\u5f62\uff0c\u7acb\u77e5\u4e0d\u5999\uff0c\u8fd9\u65f6\u4e07\u4e07\u4e0d\u80fd\u7ed9\u4ed6 \u4e3e\u8d77\u6454\u5012\u4e86\u3002\u6bd5\u64ce\u5929\u662f\u8eab\u7ecf\u767e\u6218\u7684\u597d\u6c49\uff0c\u4e34\u8d25\u4e0d\u4e71\uff0c\u4e00\u88ab\u654c\u4eba\u5236\u4f4f\uff0c\u7acb\u523b\u65bd\u7528\u201c\u5343\u65a4\u5760\u201d\u7684 \u91cd\u8eab\u6cd5\uff0c\u5c06\u8eab\u5f62\u5b9a\u4f4f\uff01 \u3000\u3000\u501f\u529b\u6253\u529b\u7684\u8981\u8bc0\u662f\u5584\u4e8e\u5229\u7528\u654c\u4eba\u5411\u81ea\u5df1\u653b\u51fb\u7684\u529b\u9053\uff0c\u53cd\u8fc7\u6765\u6253\u51fb\u654c\u4eba\uff0c\u653b\u51fb\u4e4b\u529b\u8d8a\u5927\u5219 \u53cd\u51fb\u4e4b\u529b\u8d8a\u5927\uff0c\u73b0\u5728\u6bd5\u64ce\u5929\u5168\u8eab\u7684\u6c14\u529b\u90fd\u7528\u6765\u9632\u536b\u81ea\u5df1\uff0c\u53cc\u811a\u9489\u7262\u5730\u9762\uff0c\u6709\u5982\u94dc\u6d47\u94c1\u94f8\u4e00 \u822c\uff0c\u4f46\u9664\u4e86\u53cc\u811a\u4e4b\u5916\uff0c\u5176\u4ed6\u90e8\u4efd\u5374\u5e76\u65e0\u534a\u70b9\u653b\u51fb\u654c\u4eba\u7684\u529b\u9053\uff0c\u957f\u8c37\u5ddd\u6273\u7740\u4ed6\u7684\u81c2\u818a\uff0c\u53ea\u89c9\u8f6f \u7ef5\u7ef5\u7684\u65e0\u534a\u70b9\u529b\u9053\u53ef\u201c\u501f\u201d\u3002\u65e2\u7136\u65e0\u529b\u53ef\u501f\uff0c\u82e6\u8981\u5c06\u654c\u4eba\u4e3e\u8d77\uff0c\u90a3\u5c31\u5f97\u672c\u8eab\u7684\u6c14\u529b\u6bd4\u654c\u4eba\u5927 \u5f97\u591a\u624d\u884c\uff0c\u957f\u8c37\u5ddd\u5374\u53c8\u600e\u6bd4\u5f97\u4e0a\u6bd5\u64ce\u5929\u7684\u795e\u529b\uff1f \u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u53cc\u65b9\u90fd\u53ea\u597d\u50f5\u6301\u4e0b\u53bb\uff0c\u4e0d\u6562\u653e\u677e\uff0c\u6bd5\u64ce\u5929\u56fa\u7136\u4e0d\u6562\u6323\u624e\uff0c\u6015\u4e00\u6323\u624e\u4fbf\u88ab\u654c\u4eba \u501f\u529b\u53cd\u51fb\uff0c\u957f\u8c37\u5ddd\u4e5f\u4e0d\u6562\u653e\u5f00\u4ed6\u7684\u81c2\u818a\uff0c\u53e6\u5916\u653b\u51fb\u4ed6\u7684\u8981\u5bb3\uff0c\u56e0\u4e3a\u8fd9\u65f6\u4e24\u4eba\u9762\u9762\u76f8\u5bf9\uff0c\u8ddd\u79bb \u6781\u8fd1\uff0c\u82e5\u4e00\u6362\u624b\uff0c\u6bd5\u64ce\u5929\u7684\u6c14\u529b\u6bd4\u4ed6\u5927\u5f97\u591a\uff0c\u7acb\u523b\u5c31\u53ef\u5229\u7528\u4ed6\u6362\u624b\u7684\u7a7a\u9699\u81f4\u4ed6\u6b7b\u547d\u3002 \u3000\u3000\u56f4\u89c2\u7684\u5343\u591a\u502d\u5bc7\u90fd\u770b\u5f97\u5446\u4e86\uff0c\u8d77\u5148\u662f\u5927\u5bb6\u5c4f\u606f\u800c\u89c6\uff0c\u6e10\u6e10\u4fbf\u6709\u4eba\u9f13\u566a\u8d77\u6765\uff0c\u94c1\u955c\u5fc3\u9891\u9891 \u6413\u624b\uff0c\u5927\u4e3a\u7126\u6025\uff0c\u4e8e\u627f\u73e0\u77e5\u9053\u4ed6\u4eec\u4e24\u4eba\u4e00\u5411\u4e0d\u548c\uff0c\u89c1\u94c1\u955c\u5fc3\u5982\u6b64\u7740\u6025\u7684\u795e\u60c5\uff0c\u786e\u6709\u540c\u4ec7\u654c\u5ffe \u4e4b\u5fc3\uff0c\u6beb\u65e0\u5e78\u707e\u4e50\u7978\u4e4b\u8c61\uff0c\u5bf9\u94c1\u955c\u5fc3\u7684\u597d\u611f\u7a0d\u7a0d\u589e\u4e86\u51e0\u5206\u3002 \u3000\u3000\u502d\u5bc7\u9f13\u566a\u4e4b\u58f0\u6e10\u5927\uff0c\u4e8e\u627f\u73e0\u542c\u4e0d\u61c2\u65e5\u672c\u8bdd\uff0c\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u5608\u4e9b\u4ec0\u4e48\uff1f\u201d\u94c1\u5883\u5fc3\u9053\uff1a\u201c\u4ed6 \u4eec\u4e0d\u5fff\u6c14\u88ab\u6211\u4eec\u6253\u8f93\u3002\u8bf4\u6211\u4eec\u8fde\u80dc\u51e0\u573a\uff0c\u7528\u7684\u90fd\u662f\u90aa\u672f\u3002\u4ed6\u4eec\u8fd8\u4ee5\u4e3a\u4ed6\u4eec\u7684\u4e5d\u6bb5\u4e5f\u662f\u88ab\u6bd5\u64ce \u5929\u7528\u90aa\u672f\u5b9a\u4f4f\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u502d\u5bc7\u4e0d\u61c2\u5f97\u4e2d\u56fd\u6b66\u529f\u7684\u5965\u5999\uff0c\u96be\u9053\u4ed6\u4eec\u4e03\u6bb5\u516b\u6bb5 \u7684\u6b66\u58eb\u4e5f\u8fd9\u6837\u611a\u5473\u65e0\u77e5\uff1f\u201d\u94c1\u955c\u5fc3\u5fc3\u4e2d\u4e00\u51db\uff0c\u9053\uff1a\u201c\u770b\u6765\u8fd9\u573a\u5927\u624b\u5408\u7684\u4e3b\u6301\u4eba\uff0c\u662f\u6545\u610f\u5229\u7528 \u502d\u5bc7\u7684\u65e0\u77e5\uff0c\u597d\u5411\u6211\u4eec\u53d1\u52a8\u653b\u51fb\u3002\u201d\u987b\u77e5\u82e5\u771f\u7684\u7167\u65e5\u672c\u201c\u6b66\u58eb\u9053\u201d\u6240\u6807\u699c\u7684\u201c\u7cbe\u795e\u201d\uff0c\u8f93\u4e86 \u4fbf\u5f97\u8ba4\u8f93\uff0c\u5982\u4eca\u502d\u5bc7\u786c\u8bf4\u4e2d\u56fd\u6b66\u672f\u4e0d\u662f\u7528\u771f\u5b9e\u529f\u592b\u53d6\u80dc\uff0c\u90a3\u4fbf\u6709\u85c9\u53e3\u7fa4\u6bb4\u4e86\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u6599\u5f97\u4e0d\u9519\uff0c\u6ca1\u591a\u4e45\u679c\u7136\u6709\u597d\u4e9b\u502d\u5bc7\u5486\u54ee\u9f13\u566a\u5411\u4ed6\u4eec\u8d70\u6765\uff0c\u9002\u624d\u88ab\u4e8e\u627f\u73e0\u6253\u5012\u7684\u90a3 \u4e2a\u516b\u6bb5\u6b66\u58eb\u82a5\u5ddd\u9f99\u6728\uff0c\u7ecf\u8fc7\u6309\u6469\u4e4b\u540e\uff0c\u6d3b\u52a8\u4e86\u88ab\u70b9\u7a74\u6cd5\u6240\u9ebb\u75f9\u7684\u5173\u8282\uff0c\u7adf\u7136\u4e5f\u5e26\u5934\u51b2\u6765\u3002\u94c1 \u955c\u5fc3\u5927\u559d\u9053\uff1a\u201c\u4f60\u4eec\u65e5\u672c\u6b66\u58eb\u9053\u7684\u7cbe\u795e\u539f\u6765\u5c31\u662f\u8fd9\u6837\u5b50\u5417\uff1f\u201d\u82a5\u5ddd\u9f99\u6728\u5012\u5e95\u662f\u516b\u6bb5\u9ad8\u624b\uff0c\u88ab \u94c1\u955c\u5fc3\u4e00\u559d\uff0c\u5fc3\u4e2d\u6e10\u6127\uff0c\u8e0c\u8e87\u4e0d\u524d\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u8fdc\u5904\u4f20\u6765\u95f7\u96f7\u4f3c\u7684\u70ae\u58f0\uff0c\u6709\u4e00\u4e2a\u519b\u5b98\u6a21\u6837\u7684\u502d\u5bc7\u5ffd\u5730\u632f\u81c2\u5927\u547c\u9053\uff1a\u201c\u652f\u90a3\u4eba\u4e0d\u8bb2 \u4fe1\u4e49\uff0c\u4e00\u9762\u6d3e\u4eba\u6765\u548c\u54b1\u4eec\u5408\u624b\uff0c\u4e00\u9762\u5374\u53c8\u5077\u88ad\u54b1\u4eec\u7684\u8425\u5730\uff0c\u54b1\u4eec\u8981\u628a\u652f\u90a3\u574f\u86cb\u5168\u90fd\u6740\u51c0\u3002\u201d \u51b2\u4e0a\u6765\u7684\u502d\u5bc7\u4e5f\u7eb7\u7eb7\u53eb\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u652f\u90a3\u574f\u86cb\u7528\u90aa\u672f\u6253\u4f24\u54b1\u4eec\u5149\u8363\u7684\u6b66\u58eb\uff0c\u5148\u628a\u4ed6\u4eec\u6740 \u4e86\u3002\u201d\u970e\u65f6\u95f4\u5200\u67aa\u5e76\u4e3e\uff0c\u9f50\u51b2\u8fc7\u6765\uff0c\u94c1\u955c\u5fc3\u4e00\u6325\u624b\u5c06\u4e24\u6839\u957f\u67aa\u9707\u5f97\u98de\u4e0a\u534a\u5929\uff0c\u55d6\u5730\u62d4\u51fa\u4f69 \u5251\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8981\u89c1\u8bc6\u771f\u5b9e\u7684\u529f\u592b\u662f\u4e0d\u662f\uff1f\u201d\u6a2a\u5251\u4e00\u524a\uff0c\u767b\u65f6\u4e00\u7247\u65ad\u91d1\u788e\u7389\u4e4b\u58f0\uff0c\u516d\u4e03\u67c4 \u502d\u5200\u88ab\u4ed6\u5728\u4e3e\u624b\u4e4b\u95f4\uff0c\u5168\u90fd\u524a\u4e3a\u4e24\u6bb5\u3002\u53ef\u662f\u502d\u5bc7\u5982\u6f6e\uff0c\u94c1\u955c\u5fc3\u3001\u4e8e\u627f\u73e0\u7eb5\u6709\u5929\u5927\u7684\u672c\u9886\u4e5f\u62b5 \u6321\u4e0d\u4f4f\u3002 \u3000\u3000\u77ac\u606f\u4e4b\u95f4\uff0c\u5728\u6700\u5148\u5934\u7684\u94c1\u955c\u5fc3\u5df2\u88ab\u502d\u5bc7\u56e2\u56e2\u56f4\u4f4f\u3002\u6709\u51e0\u4e2a\u65e5\u672c\u6b66\u58eb\u6b63\u51b2\u51fa\u573a\u5fc3\uff0c\u770b\u60c5\u5f62 \u662f\u53bb\u5bf9\u4ed8\u6bd5\u64ce\u5929\u3002\u4e8e\u627f\u73e0\u5fc3\u5934\u4e00\u9707\uff1a\u94c1\u955c\u5fc3\u5f62\u52bf\u867d\u9669\uff0c\u5374\u8fdc\u4e0d\u5982\u6bd5\u64ce\u5929\u4e4b\u751a\uff0c\u6bd5\u64ce\u5929\u6b63\u5728\u5168 \u529b\u5e94\u4ed8\u957f\u8c37\u5ddd\uff0c\u5bf9\u5916\u6765\u7684\u88ad\u51fb\u90a3\u662f\u6beb\u65e0\u529e\u6cd5\u62b5\u6297\u7684\u4e86\uff0c\u4fbf\u662f\u4e00\u4e2a\u5c0f\u5b69\u5b50\u5728\u65c1\u8fb9\u5288\u4ed6\u4e00\u5200\uff1a\u4e5f \u53ef\u81f4\u4ed6\u6b7b\u547d\uff0c\u4f55\u51b5\u662f\u51f6\u72e0\u5f02\u5e38\u7684\u65e5\u672c\u6b66\u58eb\uff1f\u4e8e\u627f\u73e0\u4e0d\u53ca\u7ec6\u60f3\uff0c\u7acb\u523b\u4fbf\u98de\u8eab\u63a0\u8d77\uff0c\u201c\u547c\u201d\u7684\u4e00 \u58f0\uff0c\u4ece\u4e00\u5927\u7fa4\u502d\u5bc7\u7684\u5934\u4e0a\u201c\u98de\u201d\u8fc7\uff0c\u502d\u5bc7\u201c\u54d7\u201d\u7136\u5927\u547c\uff0c\u57cb\u4f0f\u5728\u573a\u4e2d\u7684\u5f13\u7bad\u624b\u767b\u65f6\u5343\u5f29\u9f50 \u53d1\u3002\u4e8e\u627f\u73e0\u4e09\u4f0f\u4e09\u8d77\uff0c\u51b2\u5230\u4e86\u79bb\u5f00\u573a\u5fc3\u6570\u4e08\u4e4b\u5730\uff0c\u88ab\u5bc6\u96c6\u5982\u8757\u7684\u7fbd\u7bad\u963b\u4f4f\uff0c\u518d\u4e5f\u4e0d\u80fd\u7eb5\u8d77\u524d \u8fdb\uff0c\u56e0\u4e3a\u82e5\u4eba\u5728\u534a\u7a7a\uff0c\u5168\u8eab\u90fd\u662f\u76ee\u6807\uff0c\u65e2\u4e0d\u80fd\u8d8b\u907f\uff0c\u53c8\u4e0d\u80fd\u62b5\u6321\uff0c\u6015\u4e0d\u88ab\u4e71\u7bad\u5c04\u5f97\u53d8\u6210\u523a \u732c\uff1f\u53ea\u597d\u6325\u5251\u62e8\u7bad\uff0c\u5e78\u4e8f\u5979\u7684\u9752\u51a5\u5251\u662f\u4e00\u628a\u524a\u94c1\u5982\u6ce5\u7684\u5b9d\u5251\uff0c\u7565\u4e00\u62e9\u52a8\uff0c\u4fbf\u5982\u62a4\u8eab\u7684\u4e00\u9053\u94f6 \u8679\uff0c\u5c04\u6765\u7684\u7fbd\u7bad\uff0c\u53ea\u8981\u7565\u6cbe\u7740\u5b9d\u5251\u7684\u950b\u8292\uff0c\u4fbf\u7eb7\u7eb7\u6298\u65ad\uff0c\u4e8e\u627f\u73e0\u6551\u4eba\u5fc3\u5207\uff0c\u72b9\u60f3\u62fc\u786c\u51b2\u5f00\u7bad \u96e8\uff0c\u5ffd\u89c1\u82a5\u5ddd\u9f99\u6728\u5df2\u662f\u6325\u5200\u8ffd\u4e0a\uff0c\u5927\u547c\u5c0f\u53eb\uff0c\u66b4\u6012\u5982\u96f7\uff0c\u4e8e\u627f\u73e0\u867d\u7136\u542c\u4e0d\u61c2\u65e5\u672c\u8bdd\uff0c\u4f46\u770b\u82a5 \u5ddd\u9f99\u6728\u8fd9\u6124\u6012\u7684\u795e\u6c14\uff0c\u6599\u77e5\u4ed6\u5fc5\u662f\u4e0d\u5fff\u8d25\u5728\u81ea\u5df1\u70b9\u7a74\u6cd5\u4e0b\uff0c\u56e0\u6b64\u53c8\u8d76\u6765\u62fc\u547d\uff0c\u82a5\u5ddd\u9f99\u6728\u7684\u795e \u98ce\u5200\u6cd5\u8fc5\u901f\u60ca\u4eba\uff0c\u4e00\u4ea4\u4e0a\u624b\uff0c\u975e\u4e09\u4e94\u5341\u62db\u4e0d\u80fd\u6446\u8131\uff0c\u5954\u5411\u6bd5\u64ce\u5929\u7684\u90a3\u51e0\u4e2a\u502d\u5bc7\uff0c\u53c8\u5df2\u5230\u4e86\u6bd5 \u64ce\u5929\u7684\u80cc\u540e\uff0c\u502d\u5200\u95ea\u95ea\uff0c\u518d\u8fc8\u4e00\u6b65\uff0c\u5200\u950b\u4fbf\u53ef\u89e6\u53ca\u6bd5\u64ce\u5929\u7684\u5934\u9885\uff0c\u4e8e\u627f\u73e0\u5927\u4e3a\u7740\u6025\uff0c\u987e\u4e0d\u53ca \u53d6\u597d\u51c6\u5934\uff0c\u4e00\u626c\u624b\u4fbf\u662f\u4e09\u6735\u91d1\u82b1\u98de\u4e86\u51fa\u53bb\uff0c\u51e0\u4e4e\u5c31\u5728\u540c\u4e00\u65f6\u95f4\uff0c\u80cc\u540e\u91d1\u5200\u5288\u98ce\u4e4b\u58f0\u5df2\u5230\uff0c\u4e8e \u627f\u73e0\u53cd\u624b\u4e00\u5251\uff0c\u521a\u597d\u8361\u5f00\u82a5\u5ddd\u9f99\u6728\u7684\u502d\u5200\u3002 \u3000\u3000\u957f\u8c37\u5ddd\u6b63\u5728\u4e0e\u6bd5\u64ce\u5929\u5168\u529b\u76f8\u6301\uff0c\u89c1\u540c\u4f34\u8d76\u6765\uff0c\u4f3c\u6b32\u52a9\u5df1\u6697\u88ad\u654c\u4eba\uff0c\u4ed6\u5012\u5e95\u662f\u4e5d\u6bb5\u6b66\u58eb\u7684 \u8eab\u4efd\uff0c\u55d4\u76ee\u559d\u9053\uff1a\u201c\u90fd\u7ed9\u6211\u9000\u4e0b\u3002\u201d\u7a81\u89c1\u91d1\u5149\u95ea\u95ea\uff0c\u5954\u6765\u7684\u502d\u5bc7\uff0c\u4e09\u4eba\u4e2d\u5df2\u6709\u4e24\u4eba\u5012\u4e0b\uff0c\u8fd8 \u6709\u4e00\u6735\u91d1\u82b1\u5411\u81ea\u5df1\u98de\u6765\uff0c\u957f\u8c37\u5ddd\u4e0d\u7531\u5f97\u653e\u677e\u4e86\u624b\uff0c\u5927\u8896\u4e00\u6325\uff0c\u5c06\u90a3\u6735\u91d1\u82b1\u62c2\u4e86\u5f00\u53bb\u3002\u6bd5\u64ce\u5929 \u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u8981\u4eba\u76f8\u52a9\uff0c\u6211\u4e5f\u4e0d\u8981\u4eba\u76f8\u52a9\uff0c\u518d\u6597\u4e00\u573a\u3002\u201d\u672c\u6765\u6bd5\u64ce\u5929\u53ef\u4ee5\u8d81\u4ed6\u677e\u624b\u7684\u90a3 \u4e00\u5239\u90a3\uff0c\u4e58\u52bf\u53cd\u51fb\uff0c\u5236\u654c\u6b7b\u547d\uff0c\u4f46\u4ed6\u4e5f\u8981\u987e\u4f4f\u201c\u5927\u9f99\u5934\u201d\u7684\u8eab\u4efd\uff0c\u5fc3\u60f3\u5728\u4e8e\u627f\u73e0\u6697\u5668\u76f8\u52a9\u4e4b \u4e0b\uff0c\u80dc\u4e4b\u4e0d\u6b66\uff0c\u6545\u6b64\u7518\u5fc3\u653e\u5f03\u4e86\u8fd9\u6700\u96be\u5f97\u7684\u673a\u4f1a\u3002 \u3000\u3000\u957f\u8c37\u5ddd\u7c97\u901a\u6c49\u8bed\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u679c\u7136\u662f\u6761\u597d\u6c49\u5b50\u3002\u201d\u5728\u8170\u4e0a\u4e00\u62cd\uff0c\u5ffd\u5730\u624b\u4e2d\u591a\u4e86\u4e00\u628a\u5bd2 \u5149\u95ea\u95ea\u7684\u5b9d\u5200\uff0c\u539f\u6765\u4ed6\u7684\u502d\u5200\u66fe\u7ecf\u767e\u70bc\uff0c\u4ece\u767e\u70bc\u94a2\u53d8\u6210\u4e86\u201c\u7ed5\u6307\u67d4\u201d\uff0c\u53ef\u4ee5\u56f4\u5728\u8170\u95f4\u5f53\u6210\u8170 \u5e26\u3002\u6bd5\u64ce\u5929\u4f7f\u7684\u662f\u201c\u964d\u9f99\u68d2\u201d\uff0c\u56e0\u4e3a\u4eca\u65e5\u8bf4\u597d\u662f\u6765\u6bd4\u7528\u529b\u4e0e\u5200\u5251\u7684\uff0c\u4ed6\u81ea\u5df1\u6258\u5927\uff0c\u7adf\u6ca1\u6709\u5c06 \u5175\u5668\u5e26\u6765\uff0c\u88ab\u957f\u8c37\u5ddd\u8fde\u5288\u4e24\u5200\uff0c\u8fde\u8fde\u540e\u9000\u3002\u957f\u8c37\u5ddd\u54c8\u54c8\u5927\u7b11\uff0c\u5ffd\u5730\u51fa\u624b\uff0c\u62a2\u8fc7\u540c\u4f34\u7684\u4e00\u53e3\u502d \u5200\uff0c\u63b7\u7ed9\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u63a5\u597d\u4e86\uff0c\u54b1\u4eec\u518d\u6bd4\u6bd4\u5200\u6cd5\uff01\u201d\u8c8c\u4f3c\u516c\u5e73\uff0c\u5b9e\u5219\u662f\u957f\u8c37\u5ddd\u5360\u5c3d\u4fbf\u5b9c\u3002\u6bd5 \u64ce\u5929\u65e2\u4e0d\u7cbe\u4e8e\u5200\u6cd5\uff0c\u502d\u5200\u53c8\u4e0d\u662f\u5408\u9002\u7684\u5175\u5668\uff0c\u6709\u7b49\u4e8e\u65e0\uff0c\u4ecd\u7136\u7ed9\u957f\u8c37\u5ddd\u6740\u5f97\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u3001\u4e8e\u627f\u73e0\u3001\u94c1\u955c\u5fc3\u4e09\u4eba\u7ade\u88ab\u622a\u6210\u4e09\u5904\uff0c\u4e0d\u80fd\u547c\u5e94\uff0c\u8fd9\u5f62\u52bf\u5b9e\u662f\u51f6\u9669\u4e4b\u6781\u3002\u5e78\u5728\u957f \u8c37\u5ddd\u4ee5\u4e5d\u6bb5\u56fd\u624b\u7684\u8eab\u4efd\uff0c\u575a\u6301\u5355\u6253\u72ec\u6597\uff0c\u6bd5\u64ce\u5929\u867d\u7136\u88ab\u4ed6\u6740\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5c1a\u4e0d\u81f3 \u6709\u6027\u547d\u4e4b\u5fe7\uff1b\u4e8e\u627f\u73e0\u6301\u6709\u5b9d\u5251\uff0c\u53ea\u5b88\u4e0d\u653b\uff0c\u4ea6\u8fd8\u53ef\u4ee5\u52c9\u5f3a\u81ea\u4fdd\uff1b\u94c1\u955c\u5fc3\u5374\u88ab\u4e94\u516d\u4e2a\u65e5\u672c\u6b66\u58eb \u56f4\u653b\uff0c\u9669\u8c61\u73af\u751f\u3002\u5e78\u800c\u9093\u8302\u4e03\u3001\u90d1\u8d76\u9a74\u4e8c\u4eba\u4e0e\u4ed6\u76f8\u8ddd\u751a\u8fd1\uff0c\u62fc\u547d\u51b2\u6740\uff0c\u5c45\u7136\u7ed9\u4ed6\u4eec\u6c47\u96c6\u4e00 \u8d77\uff0c\u4e09\u4eba\u54c1\u5b57\u5f62\u7ad9\u7acb\uff0c\u4e92\u4e3a\u5b88\u62a4\uff0c\u9093\u8302\u4e03\u7528\u7684\u662f\u4e00\u6839\u8f6f\u97ad\uff0c\u4ed6\u6c14\u529b\u51b5\u96c4\uff0c\u65bd\u5c55\u8d77\u6765\uff0c\u4e08\u8bb8\u4e4b \u5185\uff0c\u4f46\u89c1\u97ad\u5f62\u7ffb\u98de\uff0c\u5f53\u8005\u62ab\u7cdc\uff1b\u90d1\u8d76\u9a74\u4f7f\u7684\u4e24\u67c4\u6d41\u661f\u94fe\u5b50\u9524\uff0c\u53ef\u4ee5\u5f53\u4f5c\u6d3b\u52a8\u7684\u6697\u5668\u4f7f\u7528\uff0c\u629b \u51fa\u53bb\u4e13\u6253\u654c\u4eba\u7684\u5934\u9885\uff0c\u6709\u51e0\u4e2a\u51f6\u608d\u8fdb\u653b\u7684\u502d\u5bc7\uff0c\u88ab\u4ed6\u4e00\u9524\u4e00\u4e2a\uff0c\u6253\u7834\u4e86\u5929\u7075\u76d6\uff0c\u7acb\u523b\u8840\u6e85\u9ec4 \u6c99\uff0c\u94c1\u955c\u5fc3\u7684\u4e00\u53e3\u5251\u5c31\u4e2d\u7b56\u5e94\uff0c\u66f4\u4e3a\u5389\u5bb3\uff0c\u4ed6\u5c55\u5f00\u60ca\u6d9b\u5251\u6cd5\uff0c\u987e\u4e0d\u53ca\u6740\u4f24\u654c\u4eba\uff0c\u53ea\u662f\u4e13\u524a\u654c \u4eba\u7684\u624b\u6307\uff0c\u624b\u6307\u88ab\u524a\uff0c\u5175\u5668\u81ea\u662f\u65e0\u6cd5\u628a\u6301\uff0c\u4f46\u89c1\u5251\u5149\u6240\u81f3\uff0c\u502d\u5200\u7eb7\u7eb7\u5760\u5730\u3002\u4e09\u4eba\u4e09\u79cd\u5175\u5668\uff0c \u5404\u5c55\u6240\u957f\uff0c\u502d\u5bc7\u867d\u591a\uff0c\u5374\u4e0d\u6562\u8fd1\u8eab\uff0c\u53ef\u662f\u5916\u9762\u5c42\u5c42\u5305\u56f4\uff0c\u4e09\u4eba\u88ab\u56f4\u5728\u6838\u5fc3\uff0c\u4e5f\u662f\u51b2\u4e0d\u51fa\u53bb\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u6740\u4e00\u4e2a\u591f\u672c\uff0c\u6740\u4e24\u4e2a\u6709\u8d5a\uff0c\u591a\u6740\u51e0\u4e2a\u654c\u4eba\uff01\u201d\u6325\u821e\u957f\u5251\uff0c\u594b\u4e0d\u987e\u8eab \u5730\u610f\u6b32\u6740\u5f00\u4e00\u6761\u8840\u8def\u3002\u9093\u8302\u4e03\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u53f6\u5927\u54e5\u65e9\u6709\u5b89\u6392\uff0c\u94c1\u76f8\u516c\u4e0d\u5fc5\u5fc3\u6025\u3002\u54b1\u4eec\u4e09\u4eba\u4f11 \u8981\u81ea\u5df1\u4e71\u4e86\u9635\u52bf\u3002\u201d\u94c1\u955c\u5fc3\u60f3\u8d77\u53f6\u5b97\u7559\u884c\u4e8b\u8c28\u614e\uff0c\u5fc5\u7136\u4f1a\u6599\u5230\u4eca\u65e5\u4e4b\u4e8b\uff0c\u65ad\u4e0d\u81f3\u4e8e\u8ba9\u4ed6\u4eec\u9677 \u5165\u7edd\u5883\uff0c\u5fc3\u4e2d\u4e00\u5bbd\uff0c\u7cbe\u795e\u500d\u589e\uff0c\u6709\u4e24\u4e2a\u65e5\u672c\u516d\u6bb5\u6b66\u58eb\uff0c\u4e58\u7740\u94c1\u955c\u5fc3\u8eab\u5f62\u5411\u524d\u79fb\u52a8\u4e4b\u9645\uff0c\u5ffd\u5730 \u4ece\u4fa7\u8fb9\u5411\u9093\u8302\u4e03\u5077\u88ad\uff0c\u5374\u4e0d\u6599\u94c1\u955c\u5fc3\u500f\u7136\u4e4b\u95f4\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u53cd\u624b\u4e00\u6325\uff0c\u5530\u5530\u4e24\u5251\uff0c\u53c8\u628a\u90a3\u4e24 \u4e2a\u516d\u6bb5\u6b66\u58eb\u7684\u624b\u6307\u524a\u6389\uff0c\u5835\u4f4f\u4e86\u7f3a\u53e3\u3002\u53ea\u542c\u5f97\u55e4\u55e4\u4e24\u58f0\uff0c\u4e00\u9053\u84dd\u8272\u7684\u706b\u7130\u5347\u4e0a\u5929\u7a7a\uff0c\u539f\u6765\u662f \u9093\u8302\u4e03\u8d81\u6b64\u65f6\u673a\uff0c\u5c04\u51fa\u4e86\u6c42\u6551\u8baf\u53f7\u7684\u86c7\u7130\u7bad\u3002 \u3000\u3000\u86c7\u7130\u7bad\u5c04\u51fa\uff0c\u502d\u5bc7\u66f4\u662f\u7fa4\u60c5\u6c79\u6d8c\uff0c\u767b\u65f6\u8c03\u96c6\u4e86\u4e00\u961f\u85e4\u724c\u519b\uff0c\u52a0\u7d27\u5305\u56f4\u7684\u538b\u529b\uff0c\u6570\u5341\u9762\u85e4 \u813e\u5f62\u6210\u4e86\u4e00\u9762\u5c4f\u98ce\uff0c\u4e00\u6b65\u4e00\u6b65\u5730\u5411\u524d\u63a8\u8fdb\uff0c\u7f29\u5c0f\u5305\u56f4\u5708\u5b50\uff0c\u94c1\u955c\u5fc3\u867d\u7136\u5927\u5c55\u795e\u5a01\uff0c\u6740\u7ffb\u51e0 \u4eba\uff0c\u53ef\u662f\u4ed6\u4eec\u6709\u85e4\u724c\u62a4\u8eab\uff0c\u96be\u4ee5\u524a\u65ad\u4ed6\u4eec\u7684\u624b\u6307\uff0c\u7a76\u4e0d\u5982\u9002\u624d\u7684\u987a\u624b\uff0c\u85e4\u813e\u519b\u524d\u4ec6\u540e\u7ee7\uff0c\u5377 \u5730\u538b\u6765\uff0c\u4e09\u4eba\u6e10\u6e10\u88ab\u6324\u4f5c\u4e00\u56e2\uff0c\u7eb5\u7136\u672c\u9886\u518d\u9ad8\uff0c\u5728\u767e\u6570\u5341\u9762\u85e4\u813e\u6324\u8feb\u4e4b\u4e0b\uff0c\u7adf\u53ea\u6709\u5f85\u6bd9\u7684\u4efd \u513f\u3002 \u3000\u3000\u6b63\u5728\u6781\u5ea6\u7d27\u5f20\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u6740\u58f0\u9707\u5929\uff0c\u502d\u5bc7\u9635\u5f62\u5927\u4e71\uff0c\u6709\u4e00\u5f6a\u4eba\u9a6c\u6740\u4e86\u8fdb\u6765\uff0c\u94c1\u955c\u5fc3\u5927 \u559c\u72c2\u547c\uff1a\u201c\u63f4\u519b\u6765\u5566\uff01\u201d\u5f20\u773c\u4e00\u77a7\uff0c\u53ea\u89c1\u51b2\u8fdb\u6765\u53ea\u6709\u4e00\u5c0f\u961f\u4eba\uff0c\u770b\u6765\u4e0d\u6ee1\u767e\u4eba\uff0c\u800c\u4e14\u90fd\u662f\u6e14 \u6c11\u6253\u626e\uff0c\u5e76\u975e\u4e49\u519b\uff0c\u94c1\u955c\u5fc3\u5927\u4e3a\u5931\u671b\uff0c\u5ffd\u89c1\u4e00\u4e2a\u957f\u987b\u8001\u8005\uff0c\u4ece\u6e14\u6c11\u961f\u4e2d\u51b2\u51fa\uff0c\u8fce\u7740\u502d\u5bc7\uff0c\u4e00 \u624b\u4e00\u4e2a\uff0c\u4fbf\u50cf\u6454\u7a3b\u8349\u4eba\u4f3c\u7684\uff0c\u4e00\u6293\u7740\u4fbf\u7529\uff0c\u500f\u5ffd\u4e4b\u95f4\uff0c\u6454\u6b7b\u6454\u4f24\u4e86\u51e0\u5341\u540d\u502d\u5bc7\uff0c\u8fd9\u5927\u6454\u7891\u624b \u7684\u529f\u5929\u6bd4\u94c1\u955c\u5fc3\u9ad8\u660e\u4e0d\u77e5\u51e0\u500d\uff0c\u94c1\u955c\u5fc3\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff1a\u8fd9\u4f4d\u7a81\u5982\u5176\u6765\u7684\u8001\u8005\uff0c\u539f \u6765\u7adf\u7136\u662f\u4ed6\u7684\u5e08\u7236\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u4e0e\u4ed6\u7684\u5e08\u7236\u4f1a\u5c11\u79bb\u591a\uff0c\u8fd9\u51e0\u5e74\u6765\u77f3\u60ca\u6d9b\u9003\u4ea1\u6d77\u5916\uff0c\u94c1\u955c\u5fc3\u66f4\u662f\u4e0d\u77e5\u9053\u4ed6\u7684\u8e2a\u8ff9\uff0c \u7a81\u7136\u89c1\u4ed6\u5728\u6b64\u51fa\u73b0\uff0c\u53c8\u60ca\u53c8\u559c\u3002\u90a3\u4e00\u961f\u4eba\u4eba\u6570\u867d\u7136\u4e0d\u591a\uff0c\u4e2a\u4e2a\u52c7\u731b\u975e\u5e38\uff0c\u4ee5\u4e00\u5f53\u5341\uff0c\u8f6c\u77ac\u4e4b \u95f4\uff0c\u5c31\u4ece\u5916\u9762\u653b\u4e86\u8fdb\u6765\uff0c\u4e0e\u502d\u5bc7\u7684\u85e4\u724c\u519b\u6df7\u6218\u3002\u77f3\u60ca\u6d9b\u626c\u624b\u4e0e\u5f92\u5f1f\u6253\u4e86\u4e00\u4e2a\u62db\u547c\uff0c\u8eab\u5f62\u5374\u5e76 \u4e0d\u505c\u4e0b\uff0c\u76f4\u5411\u573a\u4e2d\u7684\u6bd5\u64ce\u5927\u4e0e\u957f\u8c37\u5ddd\u6251\u53bb\u3002 \u3000\u3000\u957f\u8c37\u5ddd\u6b63\u4f7f\u5230\u795e\u98ce\u5200\u6cd5\u4e2d\u7684\u4e00\u62db\u7edd\u5999\u6740\u624b\uff0c\u5200\u950b\u5411\u5916\u75be\u5c55\uff0c\u500f\u5730\u4e00\u5377\uff0c\u6bd5\u64ce\u5929\u7528\u4e86\u4e00\u62db \u201c\u6a2a\u5956\u91d1\u6881\u201d\uff0c\u5200\u80cc\u53cd\u78d5\uff0c\u60f3\u4ed7\u7740\u8155\u52b2\u5927\u8fc7\u5bf9\u65b9\uff0c\u628a\u957f\u8c37\u5ddd\u7684\u5b9d\u5200\u78d5\u98de\uff0c\u54ea\u77e5\u957f\u8c37\u5ddd\u8fd9\u4e00\u624b \u795e\u98ce\u5200\u6cd5\u521a\u4e2d\u6709\u67d4\uff0c\u201c\u67d4\u9053\u201d\u4e2d\u501f\u529b\u6253\u529b\u7684\u529f\u592b\uff0c\u7adf\u7136\u7ed9\u4ed6\u8fd0\u7528\u5230\u5176\u5feb\u65e0\u6bd4\u7684\u795e\u98ce\u5200\u6cd5\u4e0a\uff0c \u6bd5\u64ce\u5929\u90a3\u4e00\u62db\u521a\u521a\u4f7f\u51fa\uff0c\u5ffd\u89c9\u4e00\u80a1\u6025\u901f\u7684\u65cb\u8f6c\u4e4b\u529b\u7d27\u7d27\u5730\u626f\u7740\u81ea\u5df1\u8fd9\u53e3\u502d\u5200\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u6025 \u5fd9\u5f80\u5916\u593a\u5200\uff0c\u54ea\u77e5\u4e0d\u7528\u529b\u4e5f\u8fd8\u7f62\u4e86\uff0c\u4e00\u7528\u529b\u90a3\u80a1\u53cd\u65cb\u4e4b\u529b\u5c31\u66f4\u6025\u66f4\u5f3a\uff0c\u6bd5\u64ce\u5929\u864e\u53e3\u6b32\u88c2\uff0c\u624b \u6307\u677e\u4e86\u677e\uff0c\u53ea\u542c\u5f97\u5f53\u7684\u4e00\u58f0\uff0c\u624b\u4e2d\u7684\u502d\u5200\u5df2\u7ed9\u654c\u4eba\u5377\u8d70\uff0c\u957f\u8c37\u5ddd\u632f\u5200\u4e00\u7529\uff0c\u628a\u90a3\u53e3\u502d\u5200\u524a\u4e3a \u4e24\u6bb5\uff0c\u968f\u624b\u6296\u8d77\u4e00\u4e2a\u5200\u82b1\uff0c\u5411\u6bd5\u64ce\u5929\u5206\u5fc3\u4fbf\u523a\uff0c\u795e\u98ce\u5200\u6cd5\u7684\u5389\u5bb3\u4e4b\u5904\u5c31\u5728\u4e8e\u51e0\u4e2a\u6740\u7740\u63a5\u8fde\u800c \u6765\uff0c\u957f\u8c37\u5ddd\u7684\u5377\u5200\u3001\u524a\u5200\u3001\u523a\u8155\u3001\u63d2\u80f8\uff0c\u811a\u6b65\u672a\u6362\uff0c\u56db\u5f0f\u6781\u5389\u5bb3\u7684\u6740\u624b\u5200\u6cd5\u5df2\u662f\u4e00\u6c14\u5475\u6210\uff0c \u6bd5\u64ce\u5929\u624b\u4e2d\u6ca1\u6709\u5408\u9002\u7684\u5175\u5668\uff0c\u8f93\u5f97\u6781\u4e3a\u4e0d\u5fff\uff0c\u4e00\u53e3\u95f7\u6c14\u8fd8\u672a\u8f6c\u5f97\u8fc7\u6765\uff0c\u957f\u8c37\u5ddd\u660e\u6643\u6643\u7684\u5200\u950b \u5df2\u523a\u5230\u4e86\u4ed6\u7684\u80f8\u53e3\uff0c\u800c\u4e14\u5200\u950b\u4e24\u8fb9\u6446\u52a8\uff0c\u5c06\u4ed6\u5de6\u53f3\u8d8b\u95ea\u7684\u53bb\u8def\u4e5f\u5168\u90fd\u5c01\u7740\uff0c\u957f\u8c37\u5ddd\u8fd9\u4e00\u5200\u4f7f \u5f97\u72e0\u6bd2\u4e4b\u6781\uff0c\u7adf\u4e0d\u8bb8\u6bd5\u64ce\u5929\u6709\u534a\u70b9\u9003\u751f\u7684\u673a\u4f1a\uff01 \u3000\u3000\u54ea\u77e5\u957f\u8c37\u5ddd\u7a7a\u6253\u4e86\u4e00\u4e2a\u5982\u610f\u7b97\u76d8\uff0c\u4ed6\u7684\u5200\u5feb\uff0c\u6709\u4eba\u6bd4\u4ed6\u66f4\u5feb\uff0c\u5c31\u5728\u6bd5\u64ce\u5929\u5e72\u94a7\u4e00\u53d1\u4e4b \u9645\uff0c\u5ffd\u7136\u88ab\u4e00\u80a1\u5927\u529b\u4e00\u649e\uff0c\u6bd5\u64ce\u5929\u5e9e\u5927\u7684\u8eab\u8eaf\u7a81\u7136\u7ed9\u4eba\u629b\u4e86\u8d77\u6765\uff0c\u8eab\u4e0d\u7531\u5df1\u5730\u5728\u7a7a\u4e2d\u8fde\u7ffb\u4e86 \u4e24\u4e2a\u7b4b\u6597\uff0c\u987a\u7740\u8fd9\u80a1\u5de7\u52bf\uff0c\u7adf\u7136\u8f7b\u8f7b\u5de7\u5de7\u4ed6\u843d\u5728\u5730\u4e0a\uff0c\u6beb\u53d1\u65e0\u4f24\u3002\u5f20\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u767d\u987b \u98d8\u62c2\u7684\u8001\u5934\u6b63\u5728\u5411\u7740\u957f\u8c37\u5ddd\u659c\u7768\u51b7\u7b11\u3002 \u3000\u3000\u957f\u8c37\u5ddd\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u5140\u4f60\u8fd9\u8001\u5934\u5b50\u51b7\u7b11\u4ec0\u4e48\uff1f\u201d\u77f3\u60ca\u6d9b\u61c2\u5f97\u65e5\u8bed\uff0c\u5374\u7528\u4e2d\u56fd\u8bdd\u7b54\u9053\uff1a \u201c\u7b11\u4f60\u8fd9\u5c9b\u56fd\u867e\u5937\uff0c\u5b66\u4e2d\u56fd\u7684\u4e1c\u897f\uff0c\u61c2\u5f97\u51e0\u624b\u5200\u6cd5\uff0c\u5c31\u5984\u81ea\u5c0a\u5927\uff01\u201d\u957f\u8c37\u5ddd\u5bf9\u4e2d\u56fd\u8bdd\u80fd\u542c\u4e0d \u80fd\u8bf4\uff0c\u65e5\u672c\u7684\u6b66\u529f\u672c\u6765\u6e90\u51fa\u4e2d\u56fd\uff0c\u4f46\u7ecf\u8fc7\u4e86\u65e5\u672c\u6b66\u58eb\u7684\u53c2\u609f\u53d8\u5316\u4e4b\u540e\uff0c\u521b\u7acb\u4e86\u201c\u67d4\u9053\u201d\u548c \u201c\u795e\u98ce\u5200\u6cd5\u201d\u8fd9\u4e24\u79cd\u6781\u5389\u5bb3\u7684\u6b66\u529f\uff0c\u5df2\u4e0d\u80af\u4ee5\u5b66\u751f\u81ea\u5c45\uff0c\u5984\u81ea\u4ee5\u4e3a\u5929\u4e0b\u65e0\u6545\u4e86\u3002\u957f\u8c37\u5ddd\u8eab\u4e3a \u4e5d\u6bb5\u6b66\u58eb\uff0c\u54ea\u66fe\u53d7\u8fc7\u4eba\u5982\u6b64\u8f7b\u89c6\uff1f\u5b9d\u5200\u4e00\u6325\uff0c\u556a\u556a\u4f5c\u54cd\uff0c\u559d\u9053\uff1a\u201c\u4f60\u62d4\u51fa\u5200\u6765\u3002\u54b1\u5a29\ue317\u5212\u6bd4 \u5212\uff0c\u201d\u82e5\u4e0d\u662f\u4ed6\u89c1\u5230\u77f3\u60ca\u6d9b\u521a\u624d\u6551\u4eba\u7684\u90a3\u4e00\u624b\u529f\u592b\uff0c\u53c8\u88ab\u77f3\u60ca\u6d9b\u7528\u8bf4\u8bdd\u4e00\u6fc0\uff0c\u4ed6\u8fd8\u771f\u4e0d\u5c51 \u201c\u81ea\u8d2c\u201d\u8eab\u4efd\uff0c\u8981\u548c\u77f3\u60ca\u6d9b\u6597\u5200\u6bd4\u5251\u3002\u77f3\u60ca\u6d9b\u8170\u60ac\u957f\u5251\uff0c\u542c\u4e86\u957f\u8c37\u5ddd\u7684\u8bdd\uff0c\u5374\u7768\u4ed6\u4e00\u773c\uff0c\u51b7 \u7b11\u9053\uff1a\u201c\u51ed\u4f60\u8fd9\u4e00\u70b9\u70b9\u5fae\u672a\u4e4b\u6280\uff0c\u5c31\u6562\u53eb\u6211\u62a1\u5200\u62d4\u5251\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u53e3\u6c14\u7b80\u76f4\u628a\u65e5\u672c\u7684\u4e5d\u6bb5\u6b66\u58eb\u89c6\u540c\u65e0\u7269\uff0c\u957f\u8c37\u5ddd\u6012\u4e0d\u53ef\u6291\uff0c\u53cd\u800c\u7eb5\u58f0\u5927\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c \u6211\u751f\u5e73\u4e0d\u65a9\u65e0\u540d\u4e4b\u5352\uff0c\u6211\u672c\u4e0d\u60f3\u6740\u4f60\uff0c\u90a3\u662f\u4f60\u81ea\u5df1\u51d1\u5230\u5200\u53e3\u4e0a\u6765\u4e86\uff01\u201d\u5b9d\u5200\u5212\u4e86\u4e00\u4e2a\u534a\u5706\uff0c \u500f\u5730\u524a\u51fa\uff0c\u77f3\u60ca\u6d9b\u8eab\u5f62\u7565\u4fa7\uff0c\u8ba9\u8fc7\u5200\u950b\uff0c\u4f38\u51fa\u53cc\u6307\u5728\u5200\u80cc\u4e00\u63a8\uff0c\u90a3\u53e3\u5200\u672c\u5982\u6bd2\u86c7\u5410\u4fe1\uff0c\u53c8\u72e0 \u53c8\u75be\uff0c\u88ab\u4ed6\u53cc\u6307\u4e00\u63a8\uff0c\u7a81\u5982\u6bd2\u86c7\u7684\u4e03\u5bf8\u88ab\u4eba\u8e0f\u4f4f\uff0c\u500f\u5730\u53cd\u7a9c\u56de\u53bb\uff0c\u957f\u8c37\u5ddd\u5927\u60ca\uff0c\u624b\u8155\u4e00\u6c89\uff0c \u5c06\u8fd9\u80a1\u529b\u5316\u89e3\u4e86\uff0c\u5200\u950b\u4e00\u5f39\uff0c\u8f6c\u4e86\u4e2a\u5f2f\uff0c\u53c8\u5411\u77f3\u60ca\u6d9b\u7684\u5c0f\u8179\u523a\u4e0b\uff0c\u54ea\u77e5\u5200\u950b\u4e00\u51fa\uff0c\u4f46\u89c9\u5fae\u98ce \u98d2\u7136\uff0c\u773c\u524d\u7684\u654c\u4eba\u5df2\u5931\u4e86\u6240\u5728\uff0c\u957f\u8c37\u5ddd\u5230\u5e95\u662f\u8eab\u7ecf\u767e\u6218\u7684\u4e5d\u6bb5\u6b66\u58eb\uff0c\u5fc3\u77e5\u4e0d\u5999\uff0c\u5b9d\u5200\u7acb\u523b\u53cd \u5377\u56de\u6765\uff0c\u53d8\u4f5c\u4e86\u4e00\u5708\u62a4\u8eab\u7684\u5200\u73af\uff0c\u77f3\u60ca\u6d9b\u6b63\u5728\u65bd\u5c55\u5c0f\u64d2\u62ff\u624b\u52fe\u4ed6\u7684\u624b\u8155\uff0c\u89c1\u4ed6\u53d8\u62db\u5f97\u5feb\uff0c\u5e94 \u4ed8\u5f97\u5b9c\uff0c\u7b11\u9053\uff1a\u201c\u80fd\u6321\u4e09\u62db\uff0c\u9976\u4f60\u4e0d\u6b7b\uff01\u201d\u7f29\u624b\u907f\u5f00\u5200\u950b\uff0c\u957f\u8c37\u5ddd\u82e5\u7136\u53ea\u5b88\u4e0d\u653b\uff0c\u672c\u6765\u8fd8\u53ef \u6321\u5341\u4f59\u4e8c\u5341\u62db\uff0c\u4f46\u89c1\u5bf9\u65b9\u662f\u4e2a\u8001\u5934\uff0c\u4e14\u53c8\u7a7a\u624b\uff0c\u81ea\u5df1\u8fd8\u8fde\u8d70\u4e0b\u98ce\uff0c\u53ea\u89c9\u989c\u9762\u65e0\u5b58\uff0c\u5fc3\u5934\u706b \u8d77\uff0c\u4e0d\u53ca\u601d\u7d22\uff0c\u5b9d\u5200\u53c8\u518d\u6325\u51fa\uff0c\u77f3\u60ca\u6d9b\u54c8\u54c8\u4e00\u7b11\uff0c\u957f\u8896\u4e00\u62c2\uff0c\u5f15\u5f00\u957f\u8c37\u5ddd\u7684\u773c\u795e\uff0c\u5de6\u624b\u9a73\u6307 \u4e00\u5f39\uff0c\u54d0\u5f53\u4e00\u58f0\uff0c\u90a3\u53e3\u5200\u7acb\u523b\u53cd\u5f39\u8d77\u6765\uff0c\u51e0\u4e4e\u523a\u7740\u4e86\u81ea\u5df1\u7684\u989d\u89d2\uff0c\u957f\u8c37\u5ddd\u5fd9\u6c89\u80a9\u5378\u52bf\uff0c\u8bf4\u65f6 \u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u77f3\u60ca\u6d9b\u957f\u8896\u62c2\u5904\uff0c\u864e\u53e3\u8fa3\u8fa3\u4f5c\u75bc\uff0c\u518d\u594b\u529b\u6240\u51fa\u4e4b\u65f6\uff0c\u8fde\u5200\u67c4\u4e5f\u7ed9\u77f3\u60ca\u6d9b\u5377 \u4f4f\u4e86\u3002\u77f3\u60ca\u6d9b\u559d\u9053\uff1a\u201c\u8fd8\u4e0d\u653e\u624b\u4e48\uff1f\u201d\u8f7b\u8f7b\u4e00\u5377\u4e00\u62c9\uff0c\u957f\u8c37\u5ddd\u7684\u8eab\u5b50\u7981\u4e0d\u4f4f\u5411\u524d\u51b2\u51fa\uff0c\u5fc3\u4e2d \u5927\u60ca\uff0c\u660e\u77e5\u654c\u4eba\u4f7f\u7684\u4e5f\u662f\u501f\u529b\u6253\u529b\u7684\u529f\u592b\uff0c\u4f46\u4f7f\u5f97\u6bd4\u81ea\u5df1\u5374\u597d\u5f97\u591a\uff0c\u7adf\u662f\u65e0\u6cd5\u5378\u89e3\uff0c\u53ea\u5f97\u677e \u624b\u629b\u5200\uff0c\u7ffb\u8eab\u540e\u8dc3\u3002\u603b\u5171\u4e0d\u8fc7\u4e94\u62db\uff0c\u4e00\u4e2a\u9f0e\u9f0e\u5927\u540d\u7684\u4e5d\u6bb5\u6b66\u571f\uff0c\u7adf\u88ab\u77f3\u60ca\u6d9b\u5f04\u5f97\u629b\u5200\u800c\u9003\u3002 \u3000\u3000\u77f3\u60ca\u6d9b\u9053\uff1a\u201c\u597d\u4e00\u628a\u5b9d\u5200\uff0c\u6b63\u597d\u7ed9\u6211\u7684\u5f92\u5f1f\u3002\u5582\uff0c\u9976\u4f60\u4e0d\u6b7b\uff0c\u5200\u9798\u62ff\u6765\u3002\u201c\u957f\u8c37\u5ddd\u6b63\u5728 \u5954\u8dd1\uff0c\u5ffd\u5730\u80a9\u5934\u88ab\u4eba\u4e00\u62cd\uff0c\u6025\u5fd9\u8f6c\u8eab\u6325\u62f3\uff0c\u53ea\u89c1\u77f3\u60ca\u6d9b\u5df2\u5728\u79bb\u8eab\u4e08\u8bb8\u4e4b\u5916\uff0c\u81ea\u5df1\u56f4\u5728\u8170\u95f4\u7684 \u86c7\u76ae\u8f6f\u5200\u9798\u4e5f\u7ed9\u4ed6\u89e3\u53bb\u4e86\u3002 \u3000\u3000\u957f\u8c37\u5ddd\u9644\u8fd1\u672c\u6709\u8bb8\u591a\u65e5\u672c\u6b66\u58eb\u56f4\u7740\u89c2\u6218\uff0c\u53ea\u56e0\u957f\u8c37\u5ddd\u662f\u4e5d\u6bb5\u8eab\u4efd\uff0c\u6545\u6b64\u4e0d\u6562\u4e0a\u524d\u52a9\u62f3\uff0c \u8fd9\u65f6\u89c1\u957f\u8c37\u5ddd\u8d25\u5f97\u5982\u6b64\u4e4b\u60e8\uff0c\u9f50\u90fd\u5403\u60ca\uff0c\u7eb7\u7eb7\u62e5\u4e0a\u3002\u957f\u8c37\u5ddd\u7a81\u7136\u4ece\u540c\u4f34\u624b\u4e2d\u62a2\u8fc7\u4e00\u67c4\u502d\u5200\uff0c \u5927\u58f0\u53eb\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff01\u201d\u6a2a\u5200\u5728\u809a\u76ae\u4e0a\u5207\u4e86\u4e00\u4e2a\u4ea4\u53c9\u5341\u5b57\uff0c\u767b\u65f6\u8840\u5982\u6cc9\u6d8c\uff0c\u5012\u5730\u8eab\u4ea1\uff01 \u539f\u6765\u8fd9\u662f\u65e5\u672c\u6b66\u58eb\u9053\u7684\u98ce\u6c14\uff0c\u8eab\u53d7\u5947\u8fb1\uff0c\u65e0\u529b\u62a5\u590d\uff0c\u4fbf\u5f97\u5207\u8179\u81ea\u6740\uff0c\u957f\u8c37\u5ddd\u8eab\u4e3a\u4e5d\u6bb5\u9ad8\u624b\uff0c \u88ab\u4eba\u7a7a\u624b\u7f34\u68b0\uff0c\u77f3\u60ca\u6d9b\u867d\u7136\u9976\u4ed6\u4e0d\u6b7b\uff0c\u4ed6\u4e5f\u4e0d\u80fd\u4e0d\u6b7b\u4e86\u3002 \u3000\u3000\u573a\u4e2d\u502d\u5bc7\u53c8\u6068\u53c8\u6012\uff0c\u516b\u6bb5\u6b66\u58eb\u82a5\u5ddd\u9f99\u6728\u820d\u4e86\u4e8e\u627f\u73e0\uff0c\u6307\u6325\u502d\u5bc7\uff0c\u5c06\u77f3\u60ca\u6d9b\u56e2\u56e2\u56f4\u4f4f\uff0c\u77f3 \u60ca\u6d9b\u7b11\u9053\uff1a\u201c\u73b0\u5728\u6211\u53ef\u4ee5\u8bd5\u8bd5\u81ea\u5df1\u7684\u8fd9\u628a\u5b9d\u5251\u4e86\u3002\u201d\u62d4\u51fa\u5b9d\u5251\uff0c\u4e00\u62db\u98ce\u5377\u56db\u65b9\uff0c\u6b64\u65f6\u542c\u5f97\u53ee \u53ee\u5f53\u5f53\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u6cbe\u7740\u5b9d\u5251\u7684\u502d\u5200\u90fd\u7ed9\u524a\u65ad\u4e86\uff01 \u3000\u3000\u77f3\u60ca\u6d9b\u8fd9\u53e3\u5251\u662f\u5077\u81ea\u5927\u5185\u7684\u5b9d\u5251\uff0c\u5251\u8d28\u4e0d\u5728\u7384\u673a\u9038\u58eb\u6240\u70bc\u7684\u9752\u51a5\u3001\u767d\u4e91\u4e24\u628a\u5b9d\u5251\u4e4b\u4e0b\uff0c \u8fd9\u65f6\u62d4\u51fa\u5b9d\u5251\uff0c\u5982\u864e\u6dfb\u7ffc\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u5c31\u4e0e\u4e8e\u627f\u73e0\u6c47\u5230\u4e00\u8d77\uff0c\u4e24\u67c4\u5b9d\u5251\uff0c\u5de6\u53f3\u9f50\u53d1\uff0c\u6740\u5f97\u502d \u5bc7\u4f24\u4ea1\u904d\u5730\uff0c\u6bd5\u64ce\u5929\u7528\u91cd\u624b\u6cd5\u3001\u638c\u6bd9\u4e86\u8bb8\u591a\u502d\u5bc7\uff0c\u4f46\u502d\u5bc7\u987d\u5f3a\u4e4b\u6781\uff0c\u7adf\u4f3c\u62fc\u4e86\u6027\u547d\uff0c\u8981\u4e3a\u4e5d \u6bb5\u56fd\u624b\u62a5\u4ec7\uff0c\u524d\u4ec6\u540e\u7ee7\uff0c\u4ecd\u7136\u8702\u62e5\u800c\u6765\u3002 \u3000\u3000\u77f3\u60ca\u6d9b\u5e26\u6765\u7684\u90a3\u961f\u6e14\u6c11\uff0c\u9a81\u52c7\u4e4b\u6781\uff0c\u4ed6\u4eec\u4e00\u624b\u6301\u94a9\uff0c\u4e00\u624b\u63d0\u5200\uff0c\u85e4\u724c\u519b\u7684\u85e4\u724c\uff0c\u62a4\u4e0a\u76d8 \u6613\uff0c\u62a4\u4e0b\u76d8\u96be\uff0c\u88ab\u4ed6\u4eec\u4e00\u94a9\u4e00\u4e2a\uff0c\u94a9\u7ffb\u4e86\u4fbf\u662f\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u6740\u4f24\u4e86\u4e00\u5927\u534a\uff0c\u5176 \u4f59\u7684\u4e5f\u90fd\u6e83\u8d25\u800c\u9003\uff0c\u94c1\u955c\u5fc3\u8131\u51fa\u91cd\u56f4\uff0c\u53cd\u8fc7\u6765\u63a5\u5e94\u77f3\u60ca\u6d9b\u3002\u5ffd\u542c\u5f97\u5916\u9762\u9686\u9686\u70ae\u54cd\uff0c\u53c8\u662f\u4e00\u5f6a \u519b\u9a6c\u6740\u4e86\u8fdb\u6765\uff0c\u9886\u519b\u7684\u662f\u53f6\u5b97\u7559\u7684\u53e6\u4e00\u4f4d\u526f\u624b\u626c\u5b97\u6b66\uff0c\u5728\u9a6c\u4e0a\u626c\u5200\u5927\u547c\u9053\uff1a\u201c\u5916\u9762\u7684\u502d\u5bc7\u5df2 \u5168\u519b\u8986\u706d\u4e86\uff0c\u53ea\u5269\u4e0b\u8fd9\u91cc\u7684\u4e00\u5c0f\u80a1\uff0c\u54b1\u4eec\u628a\u4ed6\u8d76\u4e0b\u6d77\u53bb\uff01\u201d \u3000\u3000\u539f\u6765\u502d\u5bc7\u7684\u5934\u9886\u72e1\u733e\u975e\u5e38\uff0c\u5b9a\u4e0b\u8be1\u8ba1\uff0c\u4ed6\u524d\u4e24\u65e5\u6388\u5230\u6d88\u606f\uff0c\u77e5\u9053\u4eca\u65e5\u5c06\u6709\u4e00\u6279\u63f4\u519b\u4ece\u56fd \u5185\u5f00\u6765\uff0c\u4e0d\u8fc7\u4eba\u6570\u4e0d\u591a\uff0c\u53ea\u6709\u5343\u4eba\u5de6\u53f3\uff0c\u4ecd\u7136\u4e0d\u8db3\u89e3\u56f4\u3002\u56e0\u6b64\u4fbf\u5b9a\u4e0b\u4eca\u65e5\u4f5c\u6b66\u58eb\u7684\u7ade\u8d5b\u5927 \u4f1a\uff0c\u9080\u8bf7\u4e86\u4e49\u519b\u6d3e\u9009\u624b\u6765\u53c2\u52a0\uff0c\u60f3\u4e58\u7740\u4e49\u519b\u4e0d\u9632\uff0c\u7a81\u65bd\u5077\u88ad\uff0c\u5206\u5175\u4e09\u8def\uff0c\u4e00\u8def\u662f\u65b0\u5f00\u6765\u7684\u502d \u5bc7\uff0c\u4ece\u53f0\u5dde\u897f\u8fb9\u4e09\u54d1\u6e7e\u767b\u9646\uff0c\u629a\u4e49\u519b\u4e4b\u80cc\uff1b\u4e00\u8def\u4ee5\u539f\u6765\u7684\u502d\u5bc7\u4f5c\u4e3a\u4e3b\u529b\uff0c\u653b\u51fb\u6b63\u9762\u9632\u7ebf\uff1b\u53e6 \u5916\u4e00\u8def\u5219\u662f\u7559\u5728\u6d77\u6ee8\u5e7f\u573a\u5047\u4f5c\u53c2\u89c2\u7ade\u8d5b\u7684\u5343\u591a\u502d\u5bc7\uff0c\u8fd9\u4e00\u8def\u502d\u5bc7\u51c6\u5907\u5c06\u4e49\u519b\u6d3e\u6765\u7684\u9009\u624b\u6740\u6b7b \u4e86\u540e\uff0c\u5728\u5348\u724c\u65f6\u5206\uff0c\u4fbf\u51b2\u51fa\u53bb\uff0c\u7ed5\u8fc7\u4e34\u6d77\u7684\u5c0f\u5c71\uff0c\u653b\u51fb\u4e49\u519b\u7684\u603b\u90e8\uff0c\u7531\u4e5d\u6bb5\u6b66\u58eb\u957f\u8c37\u5ddd\u7387 \u9886\u3002\u4e09\u8def\u502d\u5bc7\u7ea6\u597d\u5348\u65f6\u52a8\u624b\uff0c\u6240\u4ee5\u5f53\u94c1\u955c\u5fc3\u4ed6\u4eec\u6e05\u6668\u6765\u5230\u4e4b\u65f6\uff0c\u4ed6\u4eec\u5c1a\u672a\u53d1\u52a8\uff0c\u63a5\u8fde\u6bd4\u4e86\u51e0 \u573a\u3002\u5728\u957f\u8c37\u5ddd\u7684\u5fc3\u76ee\u4e2d\uff0c\u4ee5\u4e3a\u81ea\u5df1\u8fd9\u65b9\u62e5\u6709\u4e24\u4e2a\u516b\u6bb5\u6b66\u58eb\uff0c\u5176\u4ed6\u516d\u6bb5\u4e03\u6bb5\u7684\u6b66\u571f\u4e5f\u6709\u5341\u591a \u4eba\uff0c\u4e0d\u5fc5\u81ea\u5df1\u51fa\u624b\uff0c\u5df2\u53ef\u7a33\u64cd\u80dc\u5238\uff0c\u5b9e\u4e0d\u5fc5\u7528\u56f4\u653b\u7684\u529e\u6cd5\uff0c\u54ea\u77e5\u4e00\u8d25\u6d82\u5730\uff0c\u8fde\u957f\u8c37\u5ddd\u81ea\u5df1\u4e5f \u8981\u5207\u8179\u81ea\u6740\uff0c\u8fd8\u672a\u80fd\u51b2\u51fa\u53bb\u914d\u5408\u5176\u4ed6\u4e24\u8def\uff0c\u5df2\u7ecf\u53cd\u88ab\u654c\u4eba\u5305\u56f4\u4e86\u3002 \u3000\u3000\u502d\u5bc7\u7684\u8ba1\u5212\u672c\u6765\u5468\u5bc6\uff0c\u53f6\u5b97\u7559\u6599\u654c\u5982\u795e\uff0c\u65e9\u5df2\u9632\u5230\u4ed6\u7684\u5077\u88ad\uff0c\u6070\u597d\u77f3\u60ca\u6d9b\u53c8\u521a\u4ece\u6d77\u5916\u56de \u6765\uff0c\u4ed6\u5e26\u7684\u4e00\u767e\u591a\u4eba\u90fd\u662f\u4e1c\u6d77\u5404\u5c9b\u7684\u4e49\u519b\uff0c\u542c\u5f97\u502d\u5bc7\u4fb5\u6270\u6cbf\u6d77\u5bb6\u4e61\uff0c\u81ea\u613f\u56de\u6765\u6297\u502d\u7684\u3002\u4ed6\u4eec \u5728\u6d77\u4e0a\u89c1\u5230\u502d\u5bc7\u589e\u63f4\u7684\u8239\u53ea\uff0c\u56de\u6765\u7acb\u523b\u62a5\u4e0e\u53f6\u5b97\u7559\u77e5\u9053\uff0c\u53f6\u5b97\u7559\u4fbf\u8bf7\u77f3\u60ca\u6d9b\u5148\u53bb\u63f4\u6551\u94c1\u955c\u5fc3 \u7b49\u4eba\uff0c\u81ea\u5df1\u53e6\u6d3e\u5927\u519b\u5230\u4e09\u6781\u6e7e\u53bb\u8fce\u51fb\u767b\u9646\u7684\u502d\u5bc7\uff0c\u53ef\u601c\u90a3\u4e00\u6279\u502d\u5bc7\uff0c\u521a\u4e00\u4e0a\u5cb8\uff0c\u4fbf\u9677\u5165\u4e49\u519b \u7684\u7f57\u7f51\uff0c\u5168\u90e8\u88ab\u6b7c\u706d\u4e86\u3002\u8fd9\u65f6\u53ea\u5269\u4e0b\u4e86\u6d77\u6ee8\u5e7f\u573a\u7684\u8fd9\u4e00\u8def\u502d\u5bc7\uff0c\u54ea\u7981\u5f97\u8d77\u5185\u5916\u5939\u653b\uff0c\u7eb7\u7eb7\u9003 \u547d\uff0c\u5343\u4f59\u502d\u5bc7\uff0c\u5341\u6298\u516b\u4e5d\uff0c\u53ea\u5269\u767e\u591a\u4eba\u62a2\u5230\u8239\u53ea\u4e0b\u6d77\u9003\u547d\u3002\u6b63\u662f\uff1a \u3000\u3000\u4e49\u58eb\u6325\u6208\u540c\u6297\u654c\uff0c\u502d\u6c1b\u7ec8\u89c1\u5316\u51b0\u6d88\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e94\u56de\u3000\u62cd\u5cb8\u60ca\u6d9b\u3000\u82b3\u5fc3\u968f\u901d\u6c34",content:(0,t.jsx)(t.Fragment,{children:"\u51b2\u6ce2\u6d77\u71d5\u3000\u58ee\u5fd7\u6b32\u51cc\u4e91 \u3000\u3000\u502d\u5bc7\u4f24\u4ea1\u516b\u4e5d\uff0c\u4f59\u4f17\u4e5f\u5c3d\u90fd\u88ab\u8d76\u4e0b\u6d77\u53bb\u3002\u4e8e\u627f\u73e0\u75db\u5feb\u4e4b\u6781\uff0c\u62ff\u51fa\u4e00\u65b9\u4e1d\u7ee2\uff0c\u62b9\u53bb\u9752\u51a5\u5251 \u4e0a\u7684\u8840\u6e0d\u3002\u5b9d\u5251\u786e\u662f\u4e0d\u540c\uff0c\u6740\u4e86\u8bb8\u591a\u502d\u5bc7\uff0c\u5251\u5203\u4e0a\u53ea\u6709\u51e0\u4e1d\u6de1\u6de1\u7684\u8840\u75d5\uff0c\u8f7b\u8f7b\u4e00\u62ed\uff0c\u5149\u8292\u8000 \u773c\u3002\u77f3\u60ca\u6d9b\u76ee\u4e0d\u8f6c\u775b\u5730\u6ce8\u89c6\u7740\u4e8e\u627f\u73e0\u8fd9\u628a\u5b9d\u5251\uff0c\u4e8e\u627f\u73e0\u6b63\u5728\u628a\u5b9d\u5251\u63d2\u5165\u9798\u4e2d\uff0c\u77f3\u60ca\u6d9b\u5ffd\u5730\u4e00 \u4f38\u624b\u5c06\u4e8e\u627f\u73e0\u7684\u5b9d\u5251\u593a\u4e86\u8fc7\u6765\uff0c\u8fd9\u4e00\u4e0b\u62c2\u51fa\u4e0d\u610f\uff0c\u4e2d\u627f\u73e0\u5403\u4e86\u4e00\u60ca\uff0c\u56b7\u9053\uff1a\u201c\u77f3\u8001\u524d\u8f88\uff0c\u4f55 \u6545\u620f\u5f04\uff1f\u201d\u53ea\u89c1\u77f3\u60ca\u6d9b\u5c06\u9752\u51a5\u5b9d\u5251\u8fce\u7740\u4ed6\u539f\u6765\u90a3\u628a\u5b9d\u5251\u4e00\u524a\uff0c\u4e24\u5251\u76f8\u4ea4\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u706b\u661f\u98de \u6e85\uff0c\u4e24\u53e3\u5251\u7adf\u90fd\u662f\u5404\u65e0\u4f24\u635f\uff0c\u4e8e\u627f\u73e0\u731b\u5730\u7701\u9053\uff1a\u201c\u662f\u4e86\uff0c\u4ed6\u4ee5\u524d\u66fe\u8d25\u5728\u6211\u592a\u5e08\u7956\u7684\u9752\u51a5\u5251 \u4e0b\uff0c\u56e0\u6b64\u4ed6\u624d\u53bb\u5077\u5927\u5185\u7684\u5b9d\u5251\uff0c\u73b0\u5728\u60f3\u662f\u8bd5\u8bd5\u8fd9\u4e24\u53e3\u5b9d\u5251\u54ea\u53e3\u66f4\u597d\u3002\u201d \u3000\u3000\u77f3\u60ca\u6d9b\u54c8\u54c8\u5927\u7b11\uff0c\u628a\u9752\u51a5\u5b9d\u5251\u8fd8\u7ed9\u4e8e\u627f\u73e0\uff0c\u95ee\u9053\uff1a\u201c\u7384\u673a\u9038\u58eb\u662f\u4f60\u4f55\u4eba\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u662f\u6211\u592a\u5e08\u7956\u3002\u201d\u77f3\u60ca\u6d9b\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u7684\u5e08\u7236\u662f\u5f20\u4e39\u67ab\u4e86\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6b63\u662f\u3002\u5bb6\u5e08\u66fe\u5c61 \u6b21\u63d0\u8d77\u524d\u8f88\u5927\u540d\uff0c\u4f69\u670d\u4e4b\u6781\u3002\u665a\u8f88\u66ff\u5bb6\u5e08\u95ee\u5019\u3002\u201d\u77f3\u60ca\u6d9b\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5f92\u5f1f\u5982\u6b64\uff0c\u5e08\u7236\u53ef \u77e5\u3002\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u5c06\u6211\u4e0e\u5f20\u4e39\u67ab\u5e76\u5217\uff0c\u540c\u79f0\u56db\u5927\u5251\u5ba2\uff0c\u8001\u673d\u80fd\u4e0d\u60ed\u6127\uff1f\u201d\u8ddf\u7740\u53c8\u7b11\u9053\uff1a\u201c\u957f \u6c5f\u540e\u6d6a\u63a8\u524d\u6d6a\uff0c\u4e16\u4e0a\u65b0\u4eba\u6362\u65e7\u4eba\u3002\u89c1\u4e86\u4f60\u4eec\u8fd9\u4e00\u8f88\u5c11\u5e74\u82f1\u4fa0\uff0c\u8001\u673d\u4e00\u9762\u60ed\u6127\uff0c\u4e00\u9762\u5374\u4e5f\u662f\u9ad8 \u5174\u5f97\u5f88\u554a\uff01\u201d\u5176\u5b9e\u77f3\u60ca\u6d9b\u7684\u8f88\u4efd\u6bd4\u5f20\u4e39\u67ab\u8981\u9ad8\u51fa\u4e00\u8f88\uff0c\u4ed6\u5bf9\u5f20\u4e39\u67ab\u7684\u5e08\u7236\u4e00\u8f88\u5982\u6f6e\u97f3\u548c\u5c1a\u3001 \u8463\u5cb3\u7b49\u4eba\u8fd8\u4e0d\u5927\u653e\u5728\u773c\u4e2d\uff0c\u66f4\u4e0d\u8981\u5f20\u4e39\u67ab\u4e86\u3002\u6c5f\u6e56\u4e0a\u5c06\u4ed6\u4e0e\u5f20\u4e39\u67ab\u5e76\u5217\uff0c\u4ed6\u4ee5\u524d\u8fd8\u662f\u4e0d\u5927\u670d \u6c14\u7684\uff0c\u73b0\u5728\u89c1\u4e86\u4e8e\u627f\u73e0\u7684\u5251\u6cd5\uff0c\u4e0d\u7531\u5f97\u5927\u4e3a\u4f69\u670d\uff0c\u77e5\u9053\u5f20\u4e39\u67ab\u7684\u672c\u9886\u5b9e\u5728\u8981\u6bd4\u81ea\u5df1\u9ad8\u5f97\u591a\uff0c \u518d\u627e\u7384\u673a\u9038\u58eb\u6bd4\u5251\u7684\u5ff5\u5934\uff0c\u90a3\u662f\u60f3\u4e5f\u4e0d\u6562\u60f3\u4e86\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u4ece\u540e\u9762\u8d76\u6765\uff0c\u77f3\u60ca\u6d9b\u6551\u4e86\u4ed6\u7684\u6027\u547d\uff0c\u4ed6\u8fd8\u672a\u5411\u77f3\u60ca\u6d9b\u9053\u8c22\u3002\u77f3\u60ca\u6d9b\u7b11\u9053\uff1a\u201c\u8fd9\u7b97 \u4ec0\u4e48\uff0c\u4f55\u52b3\u8a00\u8c22\uff1f\u8fd9\u4f4d\u597d\u6c49\u662f\u2014\u2014\u201d\u9093\u8302\u4e03\u5728\u65c1\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u5317\u4e94\u7701\u7684\u6bd5\u5927\u9f99\u5934\u3002\u201d\u77f3\u60ca \u6d9b\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u662f\u6bd5\u64ce\u5929\u6bd5\u5927\u9f99\u5934\u3002\u8001\u673d\u8fd9\u4e24\u5e74\u6765\u867d\u5728\u6d77\u5916\uff0c\u4e5f\u66fe\u542c\u5230\u6bd5\u5927\u9f99\u5934\u7684\u540d\u5b57\uff0c \u5f53\u771f\u662f\u540d\u4e0d\u865a\u4f20\u3002\u6211\u95e8\u4e0b\u7684\u5f1f\u5b50\uff0c\u770b\u6765\u53ea\u6709\u94c1\u955c\u5fc3\u53ef\u4ee5\u8ddf\u4f60\u6bd4\u4e00\u6bd4\uff0c\u5176\u4ed6\u4e24\u4e2a\u53ef\u5c31\u5dee\u5f97\u8fdc \u4e86\u3002\u55ef\uff0c\u4f60\u89c1\u8fc7\u6211\u7684\u5f92\u5f1f\u6ca1\u6709\uff1f\u201d\u6bd5\u64ce\u5929\u542c\u5f97\u81ea\u5df1\u540d\u4f20\u6d77\u5916\uff0c\u672c\u6765\u751a\u662f\u9ad8\u5174\uff0c\u4f46\u4e00\u542c\u77f3\u60ca\u6d9b \u5c06\u4ed6\u4e0e\u94c1\u955c\u5fc3\u76f8\u6bd4\uff0c\u628a\u81ea\u5df1\u5f53\u4f5c\u4ed6\u7684\u5f92\u5f1f\u4e00\u8f88\u770b\u5f85\uff0c\u5fc3\u4e2d\u53c8\u5927\u662f\u4e0d\u60a6\uff0c\u795e\u8272\u663e\u5f97\u9887\u4e3a\u5c34\u5c2c\u3002 \u6070\u597d\u94c1\u955c\u5fc3\u4e5f\u8d76\u4e86\u4e0a\u6765\uff0c\u95ee\u5019\u5e08\u7236\uff0c\u77f3\u60ca\u6d9b\u9053\uff1a\u201c\u5570\uff0c\u4ed6\u5c31\u662f\u4e86\u3002\u4f60\u4eec\u4e24\u4eba\u8ba4\u8bc6\u4e86\u4e48\uff1f\u201d\u6bd5 \u64ce\u5929\u5f3a\u7b11\u9053\uff1a\u201c\u4ee4\u5f92\u5e74\u5c11\u82f1\u96c4\uff0c\u8fd9\u6b21\u6297\u502d\uff0c\u5f97\u4ed6\u76f8\u52a9\u4e0d\u5c11\u3002\u201d\u77f3\u60ca\u6d9b\u5f88\u662f\u6b22\u559c\uff0c\u62c9\u7740\u6bd5\u64ce\u5929 \u8bdd\u957f\u8bdd\u77ed\uff0c\u8fde\u94c1\u955c\u5fc3\u4e5f\u63d2\u4e0d\u8fdb\u8bdd\u53bb\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u94c1\u955c\u5fc3\u4e0e\u4e8e\u627f\u73e0\u5df2\u8d70\u5728\u4f17\u4eba\u524d\u9762\u3002\u6bd5\u64ce \u5929\u89c1\u4ed6\u4eec\u4e8c\u4eba\u54bd\u54bd\u7ec6\u8bed\uff0c\u6709\u8bf4\u6709\u7b11\uff0c\u5fc3\u5934\u66f4\u4e0d\u8212\u670d\uff0c\u5f88\u60f3\u8d76\u4e0a\u524d\u53bb\uff0c\u9694\u5f00\u4e8c\u4eba\uff0c\u53ef\u662f\u77f3\u60ca\u6d9b \u4e0d\u505c\u53e3\u5730\u548c\u4ed6\u8bf4\u8bdd\uff0c\u4ed6\u53ea\u597d\u77a7\u7740\u4e8c\u4eba\u5e72\u7740\u6025\uff0c\u800c\u4e14\u8fd8\u4e0d\u80fd\u4e0d\u88c5\u51fa\u606d\u606d\u656c\u656c\u7684\u6837\u5b50\u6577\u884d\u77f3\u60ca \u6d9b\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u5bf9\u4e8e\u627f\u73e0\u7684\u8eab\u4efd\u672c\u6765\u5c31\u6709\u4e86\u51e0\u5206\u8d77\u7591\uff0c\u521a\u521a\u53c8\u89c1\u5230\u4e8e\u627f\u73e0\u7528\u4e1d\u5e15\u62ed\u5251\uff0c\u7537\u5b50\u8eab \u4e0a\uff0c\u54ea\u4f1a\u85cf\u6709\u8fd9\u7b49\u7269\u4e8b\uff1f\u7591\u5fc3\u4e0d\u7981\u53c8\u589e\u4e86\u51e0\u5206\u3002\u4ed6\u4eec\u6cbf\u7740\u6d77\u6ee8\u8d70\u56de\u8425\u5730\uff0c\u6d6a\u6d9b\u62cd\u5cb8\uff0c\u6d77\u4e2d\u7684 \u502d\u8239\u53ea\u89c1\u5230\u51e0\u70b9\u5c0f\u5c0f\u7684\u9ed1\u70b9\u4e86\u3002\u4e8e\u627f\u73e0\u8c6a\u5174\u904d\u98de\uff0c\u548c\u94c1\u955c\u5fc3\u8c08\u8bb2\u4eca\u65e5\u7684\u6bd4\u6b66\uff0c\u94c1\u955c\u5fc3\u82e5\u4e0d\u7ecf \u610f\u5730\u8bf4\u9053\uff1a\u201c\u4e8e\u76f8\u516c\uff0c\u4f60\u4eca\u65e5\u548c\u90a3\u4e2a\u516b\u6bb5\u6b66\u58eb\u6bd4\u6b66\u90a3\u573a\uff0c\u8f7b\u8eab\u7684\u672c\u9886\u771f\u662f\u4fca\u6781\u4e86\uff0c\u90a3\u662f\u4ec0\u4e48 \u8eab\u6cd5\u5440\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u90a3\u662f\u6211\u5e08\u6bcd\u4f20\u6388\u7684\uff0c\u540d\u53eb\u7a7f\u82b1\u7ed5\u6811\u7684\u8eab\u6cd5\u3002\u5440\uff0c\u4f60\u4e0d\u77e5\u9053\uff0c\u6211\u4eec\u592a \u6e56\u5c71\u5e84\u7684\u98ce\u666f\u591a\u7f8e\uff0c\u6211\u5e08\u6bcd\u53c8\u6700\u7231\u82b1\uff0c\u5e84\u524d\u79cd\u4e86\u65e0\u6570\u82b1\u6811\uff0c\u6843\u82b1\u3001\u674e\u82b1\u3001\u6885\u82b1\u3001\u73ab\u7470\u82b1\uff0c\u4ec0 \u4e48\u90fd\u6709\u3002\u6625\u5929\u6765\u7684\u65f6\u5019\uff0c\u767e\u82b1\u5f00\u653e\uff0c\u66f4\u662f\u707f\u82e5\u4e91\u971e\u3002\u6211\u548c\u5e08\u6bcd\u5c31\u5728\u82b1\u6811\u4e1b\u4e2d\u7ec3\u8fd9\u79cd\u7a7f\u82b1\u7ed5\u6811 \u7684\u8f7b\u8eab\u529f\u592b\uff0c\u5934\u4e24\u5e74\u6211\u975e\u4f46\u8ffd\u4e0d\u4f4f\u5e08\u6bcd\uff0c\u8fd8\u65f6\u5e38\u88ab\u6811\u679d\u6216\u523a\u52fe\u7740\u8863\u88f3\uff0c\u7ec3\u4e86\u4e09\u56db\u5e74\uff0c\u8fd9\u624d\u80fd \u591f\u7a7f\u7ed5\u81ea\u5982\uff0c\u7ec3\u5230\u7b2c\u4e94\u5e74\uff0c\u624d\u6293\u5f97\u7740\u6211\u5e08\u6bcd\u7684\u88d9\u3002\u201d\u94c1\u955c\u5fc3\u7b11\u9053\uff1a\u201c\u4f60\u5e08\u6bcd\u5bf9\u4f60\u8fd9\u6837\u597d\uff0c\u771f \u4ee4\u4eba\u7fa1\u6155\u3002\u6211\u770b\u5979\u5bf9\u4f60\u662f\u6709\u5982\u5bf9\u5f85\u4eb2\u751f\u513f\u5973\u4e00\u822c\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u4e00\u770b\uff0c\u89c1\u94c1\u955c\u5fc3\u4f3c\u7b11\u975e\u7b11\uff0c\u9762\u8272\u6709\u5f02\uff0c\u8fd9\u795e\u6001\u6709\u51e0\u5206\u4f3c\u4ed6\u7684\u5e08\u7236\u5f20\u4e39\u67ab\uff0c\u4e0d\u89c9\u5fc3 \u4e2d\u4e00\u52a8\uff0c\u53c8\u4e0d\u7981\u5fc3\u4e2d\u4e00\u60e7\uff0c\u731b\u7136\u60f3\u8d77\u81ea\u5df1\u65e0\u610f\u4e4b\u4e2d\u8bf4\u6e9c\u4e86\u5634\uff0c\u7537\u5f92\u5f1f\u54ea\u6709\u548c\u5e08\u6bcd\u8fd9\u6837\u4e0d\u62d8\u75d5 \u8ff9\u7684\uff1f\u9762\u4e0a\u4e00\u7ea2\uff0c\u53ea\u542c\u5f97\u94c1\u955c\u5fc3\u53c8\u7b11\u9053\uff1a\u201c\u7a7f\u82b1\u7ed5\u6811\uff0c\u8fd9\u540d\u79f0\u771f\u7f8e\u3002\u6211\u770b\u4f60\u620f\u5f04\u90a3\u4e2a\u6b66\u58eb \u65f6\uff0c\u5c31\u771f\u50cf\u7a7f\u82b1\u7684\u5f69\u8776\u4e00\u822c\uff0c\u90a3\u7b80\u76f4\u4e0d\u662f\u6bd4\u6b66\uff0c\u800c\u662f\u770b\u4f60\u4f5c\u5929\u5973\u6563\u82b1\u7684\u821e\u8e48\uff01\u771f\u662f\u7f8e\u6781\u5566\u3002 \u7f8e\u6781\u5566\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u518d\u80e1\u6367\u778e\u8d5e\uff0c\u6211\u4e0d\u548c\u4f60\u8bf4\u5566\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u8bf4\u5f97\u4e0d\u5bf9\u4e48\uff1f\u8d5e\u5f97 \u4e0d\u591f\u7f8e\u5999\uff0c\u4e5f\u7528\u4e0d\u7740\u751f\u6c14\u5440\u3002\u8bf4\u771f\u7684\uff0c\u6211\u8fd8\u771f\u60f3\u8bf7\u4f60\u6559\u6211\u5462\u3002\u201d\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u4f60\u6bd4\u6211\u5e74\u7eaa \u957f\uff0c\u672c\u9886\u9ad8\uff0c\u89c1\u8bc6\u591a\uff0c\u6211\u8981\u6e05\u4f60\u6307\u6559\uff0c\u90a3\u624d\u662f\u771f\u7684\uff0c\u4f60\u600e\u4e48\u4e0e\u6211\u5ba2\u5957\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u6b66\u6797 \u4e4b\u4e2d\uff0c\u5f7c\u6b64\u7422\u78e8\uff0c\u90a3\u662f\u5e94\u8be5\u7684\uff0c\u4f60\u4f1a\u7684\u6559\u6211\uff0c\u6211\u4f1a\u7684\u6559\u4f60\uff0c\u597d\u5f97\u5f88\u5440\u3002\u4e8e\u76f8\u516c\uff0c\u4eca\u665a\u6211\u5230\u4f60 \u7684\u5e10\u5e55\uff0c\u54b1\u4eec\u62b5\u8db3\u800c\u7720\uff0c\u62fc\u7740\u4e00\u591c\u4e0d\u7761\uff0c\u4e92\u76f8\u8c08\u8bba\u6b66\u529f\uff0c\u597d\u4e48\uff1f\u53e4\u4eba\u4e91\uff1a\u542c\u541b\u4e00\u5915\u8bdd\uff0c\u80dc\u8bfb \u5341\u5e74\u4e66\u3002\u8bfb\u4e66\u5982\u662f\uff0c\u60f3\u6765\u5bf9\u6b66\u5b66\u7684\u94bb\u7814\uff0c\u4ea6\u662f\u5dee\u4e0d\u591a\u7684\u3002\u5927\u5bb6\u8c08\u4e00\u8c08\u5bf9\u6b66\u5b66\u7684\u5fc3\u5f97\uff0c\u80dc\u8fc7\u72ec \u5b66\u65e0\u53ca\uff0c\u90a3\u662f\u4e0d\u6d88\u8bf4\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u7ea2\u900f\u8116\u5b50\uff0c\u4e0d\u7b49\u94c1\u955c\u5fc3\u8bf4\u8bdd\uff0c\u7740\u6025\u8bf4\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u8c01\u548c\u4f60\u540c\u4e00\u5e10\u5e55\uff1f\u4f60\u8fdb\u6765 \u6211\u5c31\u62ff\u5251\u523a\u4f60\uff01\u201d\u94c1\u955c\u5fc3\u6545\u4f5c\u60ca\u8be7\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8d24\u5f1f\u4f55\u6545\u751f\u5982\u6b64\u5927\u6c14\uff1f\u54b1\u4eec\u521d\u6765\u4e4b \u65f6\uff0c\u4e0d\u662f\u4e5f\u540c\u8fc7\u5e10\u5e55\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u4e00\u60f3\uff0c\u81ea\u5df1\u8bf4\u8bdd\u592a\u6025\uff0c\u4e0d\u89c9\u53c8\u9732\u4e86\u75d5\u8ff9\uff0c\u5b9a\u4e00\u5b9a\u795e\uff0c\u5e73\u9759 \u8bf4\u9053\uff1a\u201c\u6211\u6700\u4e0d\u6b22\u559c\u4e0e\u4eba\u540c\u4f4f\uff0c\u521d\u6765\u4e4b\u65f6\uff0c\u5c71\u5be8\u4e2d\u4e00\u5207\u56e0\u964b\u5c31\u7b80\uff0c\u90a3\u662f\u6ca1\u6709\u529e\u6cd5\u3002\u201d\u5979\u60f3\u88c5 \u51fa\u9547\u9759\u7684\u795e\u60c5\u6765\u52a0\u4ee5\u89e3\u91ca\uff0c\u5374\u4e0d\u6599\u5fc3\u4e2d\u865a\u602f\uff0c\u81ea\u7136\u6d41\u9732\u51fa\u6765\uff0c\u5c3d\u7ba1\u5979\u8bf4\u8bdd\u4ece\u5bb9\uff0c\u5374\u63a9\u4e0d\u4f4f\u5c34 \u5c2c\u7684\u795e\u8272\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u54c8\u54c8\u4e00\u7b11\uff0c\u4ed6\u672c\u6765\u4e0d\u662f\u8f7b\u8584\u4e4b\u5f92\uff0c\u6545\u610f\u8bf4\u8981\u4e0e\u4e8e\u627f\u73e0\u62b5\u8db3\u591c\u8c08\uff0c\u90a3\u662f\u8bd5\u63a2\u5979\u7684\u3002 \u4e00\u89c1\u5979\u5982\u6b64\u7740\u6025\u7684\u795e\u60c5\uff0c\u77e5\u9053\u4e86\u5979\u662f\u4e2a\u5973\u5b50\uff0c\u7edd\u5bf9\u65e0\u7591\u3002\u4e0d\u5fcd\u518d\u903c\u5979\u7740\u7a98\uff0c\u4e8e\u662f\u7b11\u9053\uff1a\u201c\u8d24 \u5f1f\u65e2\u7136\u5acc\u627e\u8fd9\u4e2a\u81ed\u7537\u5b50\uff0c\u90a3\u4e48\u4e3a\u5144\u7684\u81ea\u7136\u4e0d\u65b9\u4fbf\u5230\u4f60\u7684\u5e10\u5e55\u53bb\u4e86\u3002\u8fc7\u4e24\u5929\u54b1\u4eec\u518d\u6765\u8fd9\u91cc\uff0c\u502d \u5bc7\u7ed9\u54b1\u4eec\u5f00\u8f9f\u4e86\u8fd9\u4e00\u5ea7\u5927\u6b66\u573a\uff0c\u6b63\u597d\u5728\u8fd9\u91cc\u7531\u4f60\u6307\u70b9\u3002\u201d\u4e8e\u627f\u73e0\u542c\u4ed6\u8bdd\u4e2d\u6709\u8bdd\uff0c\u77e5\u9053\u5e90\u5c71\u771f \u76f8\u7ed9\u4ed6\u7aa5\u7834\uff0c\u7f9e\u5f97\u65e0\u5730\u81ea\u5bb9\uff0c\u5e78\u800c\u94c1\u955c\u5fc3\u8bf4\u81f3\u6b64\u5373\u6b62\uff0c\u77e5\u9053\u5979\u662f\u5973\u5b50\u4e4b\u540e\uff0c\u795e\u8272\u53cd\u800c\u6bd4\u524d\u5e84 \u91cd\u4e86\u3002 \u3000\u3000\u79bb\u5f00\u6d77\u6d5a\uff0c\u8d70\u8fdb\u5c71\u533a\uff0c\u5404\u961f\u4e49\u519b\u90fd\u5df2\u83b7\u80dc\u5f52\u6765\uff0c\u94c1\u955c\u5fc3\u5ffd\u7136\u77a7\u89c1\u5e08\u5f1f\u548c\u5e08\u59b9\u4e5f\u5728\u90a3\u91cc\uff0c \u6025\u5fd9\u8d70\u8fc7\u53bb\u95ee\uff0c\u539f\u6765\u6210\u6d77\u5c71\u4e0e\u77f3\u6587\u7ea8\u534f\u52a9\u53f0\u5dde\u64cd\u7ec3\u5b88\u57ce\uff0c\u8fd9\u4e24\u4e2a\u6708\u4e2d\uff0c\u7686\u6253\u9000\u4e86\u502d\u5bc7\u7684\u51e0\u6b21 \u5077\u88ad\uff0c\u6700\u8fd1\u56e0\u4e3a\u53f6\u5b97\u7559\u7684\u4e49\u519b\u52bf\u76db\uff0c\u5404\u8def\u502d\u5bc7\u8c03\u53bb\u589e\u63f4\uff0c\u53f0\u5dde\u7684\u5b89\u5168\u5df2\u7ecf\u53ef\u4ee5\u65e0\u8651\u4e86\uff0c\u56e0\u6b64 \u4ed6\u4eec\u5e26\u4e86\u6570\u767e\u540d\u76ee\u613f\u6297\u502d\u7684\u4e49\u6c11\u524d\u6765\u52a9\u6218\u3002\u6070\u597d\u77f3\u60ca\u6d9b\u4e5f\u5728\u8fd9\u4e2a\u65f6\u5019\u5f52\u6765\uff0c\u7236\u5973\u5e08\u5f92\uff0c\u76f8\u89c1 \u81ea\u662f\u4e00\u573a\u6b22\u559c\u3002 \u3000\u3000\u77f3\u6587\u7ea8\u4f3c\u4e4e\u8fd8\u8bb0\u7740\u627f\u73e0\u5411\u5979\u620f\u5f04\u7684\u65e7\u6068\uff0c\u89c1\u4e86\u9762\u51b7\u51b7\u6de1\u6de1\u7684\uff0c\u4e0d\u7406\u4e0d\u776c\uff0c\u4e8e\u627f\u73e0\u5fc3\u4e2d\u6697 \u6697\u597d\u7b11\uff0c\u4e58\u673a\u6487\u5f00\u4e86\u94c1\u955c\u5fc3\uff0c\u8d70\u8fc7\u4e00\u8fb9\uff0c\u6bd5\u64ce\u5929\u60f3\u627e\u5979\u8bf4\u8bdd\uff0c\u5979\u5374\u94bb\u5165\u4e86\u4eba\u4e1b\u4e4b\u4e2d\uff0c\u5ffd\u89c1\u4eba \u4e1b\u4e2d\u6709\u4e00\u4e2a\u4f3c\u662f\u4ece\u53f0\u5dde\u6765\u7684\u56e2\u7ec3\u4eba\uff0c\u76ee\u4e0d\u8f6c\u775b\u5730\u671b\u7740\u94c1\u955c\u5fc3\uff0c\u5728\u4eba\u4e1b\u5c0f\u6324\u8fc7\u53bb\uff0c\u8fd8\u4f3c\u4e4e\u6084\u6084 \u5730\u5411\u94c1\u955c\u5fc3\u6253\u773c\u8272\uff0c\u627f\u73e0\u6709\u70b9\u5947\u602a\uff0c\u4f46\u5979\u4e3a\u4e86\u907f\u5f00\u6bd5\u64ce\u5929\u4e0e\u94c1\u955c\u5fc3\u7684\u7ea0\u7f20\uff0c\u81ea\u5df1\u4e5f\u94bb\u5165\u4eba\u4e1b \u4e4b\u4e2d\uff0c\u90a3\u4e2a\u4eba\u8f6c\u773c\u4e4b\u95f4\u4e5f\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u662f\u591c\u4e49\u519b\u8425\u5730\uff0c\u70ed\u95f9\u975e\u5e38\uff0c\u9644\u8fd1\u5c45\u6c11\uff0c\u5f97\u77e5\u5927\u6377\u7684\u6d88\u606f\uff0c\u7eb7\u7eb7\u6740\u732a\u5bb0\u725b\uff0c\u62c5\u7c73\u6311\u9152\uff0c\u524d \u6765\u7292\u519b\u3002\u53f6\u5b97\u7559\u8bf7\u77f3\u60ca\u6d9b\u3001\u6bd5\u64ce\u5929\u3001\u94c1\u955c\u5fc3\u3001\u4e8e\u627f\u73e0\u7b49\u56db\u4eba\u5750\u5728\u4e0a\u5ea7\uff0c\u81ea\u5df1\u5750\u5728\u4e0b\u624b\u76f8\u966a\uff0c \u5c06\u8fd9\u6b21\u5927\u6377\u7684\u529f\u52b3\u5927\u90fd\u5f52\u56db\u4eba\u3002\u94c1\u955c\u5fc3\u548c\u4e8e\u627f\u73e0\u90fd\u89c9\u4e0d\u5b89\u3002\u6bd5\u64ce\u5929\u5374\u4e0d\u4f4f\u5730\u548c\u53f6\u5b97\u7559\u8c08\u4eca\u540e \u7684\u8ba1\u5212\uff0c\u559d\u4e86\u51e0\u676f\uff0c\u6bd5\u64ce\u5929\u4f3c\u4e4e\u6709\u4e86\u9189\u610f\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u53f6\u5927\u54e5\u4f60\u8fd9\u6b21\u6307\u6325\u82e5\u8d70\uff0c\u786e\u662f\u4e00\u4e2a \u4e86\u4e0d\u8d77\u7684\u5c06\u624d\u3002\u9a71\u9010\u502d\u5974\uff0c\u4e0d\u8fc7\u662f\u725b\u5200\u5c0f\u8bd5\u800c\u5df2\u3002\u5c06\u6765\u6f84\u6e05\u56db\u6d77\uff0c\u5efa\u5927\u529f\u521b\u5927\u4e1a\uff0c\u4e5f\u8fd8\u6709\u5f85 \u543e\u5144\u5462\uff01\u201d\u4ed6\u867d\u7136\u6ca1\u6709\u660e\u8bf4\uff0c\u4f46\u542c\u8fd9\u6837\u7684\u53e3\u6c14\uff0c\u7adf\u662f\u60f3\u529d\u53f6\u5b97\u7559\u548c\u4ed6\u540c\u8c0b\u5927\u4e8b\u3002\u94c1\u955c\u5fc3\u6781\u4e3a \u4e0d\u60a6\uff0c\u4f46\u89c1\u6bd5\u64ce\u5929\u5df2\u6709\u4e86\u9152\u610f\uff0c\u53c8\u662f\u795d\u6377\u7684\u6b22\u5bb4\uff0c\u4e0d\u4fbf\u548c\u4ed6\u5435\u7ffb\uff0c\u7d22\u6027\u81ea\u996e\u95f7\u9152\uff0c\u4ed6\u6b63\u597d\u5750 \u5728\u4e8e\u627f\u73e0\u7684\u4fa7\u8fb9\uff0c\u4e0d\u4f4f\u5730\u7528\u773c\u89d2\u8198\u4e8e\u627f\u73e0\uff0c\u9189\u4e2d\u770b\u7f8e\u4eba\u8d8a\u770b\u8d8a\u7f8e\uff0c\u94c1\u955c\u5fc3\u4e5f\u4e0d\u7981\u6e10\u6e10\u9732\u51fa\u4e00 \u4e9b\u72c2\u6001\uff0c\u4e8e\u627f\u73e0\u7ed9\u4ed6\u77a7\u5f97\u5fc3\u4e2d\u70e6\u8e81\uff0c\u4e0d\u5f85\u5e2d\u6563\uff0c\u4fbf\u5411\u53f6\u5b97\u7559\u544a\u7f6a\uff0c\u63a8\u8bf4\u4e0d\u80dc\u9152\u529b\uff0c\u56de\u53bb\u7761 \u4e86\u3002 \u3000\u3000\u4f46\u4e8e\u627f\u73e0\u54ea\u91cc\u7761\u5f97\u7740\u89c9\uff0c\u6574\u665a\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u60f3\u8d77\u94c1\u955c\u5fc3\u65e5\u95f4\u7684\u8bf4\u8bdd\uff0c\u7f9e\u6127\u4e0e\u6c14\u6124\u7684\u5fc3\u60c5\u4ea4 \u7ec7\u4e0d\u6e05\uff0c\u53c8\u9632\u94c1\u955c\u5fc3\u4f1a\u95ef\u8fdb\u6765\uff0c\u8fde\u5916\u8863\u4e5f\u4e0d\u6562\u8131\uff0c\u6795\u7740\u5b9d\u5251\uff0c\u5750\u5728\u5e8a\u4e0a\uff0c\u80e1\u601d\u4e71\u60f3\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u3001\u94c1\u955c\u5fc3\u3001\u6bd5\u64ce\u5929\u7684\u5f71\u5b50\u53c8\u4e00\u6b21\u5730\u4ece\u5979\u8111\u6d77\u4e2d\u98d8\u8fc7\uff0c\u81ea\u4ece\u6765\u5230\u4e49\u519b\u519b\u4e2d\u4e4b\u540e\uff0c\u5979 \u548c\u94c1\u3001\u6bd5\u4e8c\u4eba\u671d\u5915\u76f8\u89c1\uff0c\u5df2\u662f\u4e0d\u6b62\u4e00\u6b21\u5c06\u4ed6\u4eec\u4e8c\u4eba\u4e0e\u81ea\u5df1\u7684\u5e08\u7236\u6bd4\u8f83\uff0c\u53c8\u5c06\u4ed6\u4eec\u4e00\u6bd4\u8f83\uff0c\u8d8a \u6765\u8d8a\u6709\u8fd9\u6837\u7684\u611f\u89c9\uff1a\u5982\u679c\u628a\u5f20\u4e39\u67ab\u6bd4\u4f5c\u78a7\u6d77\u6f84\u6ce2\uff0c\u5219\u94c1\u955c\u5fc3\u4e0d\u8fc7\u662f\u4e00\u6e56\u6b7b\u6c34\uff0c\u7eb5\u8bb8\u6e56\u5149\u6f88 \u6e58\uff0c\u4e5f\u80fd\u4ee4\u4eba\u5fc3\u65f7\u795e\u6021\uff0c\u4f46\u600e\u80fd\u6bd4\u5f97\u5927\u6d77\u4ee4\u4eba\u80f8\u895f\u5e7f\u9614\uff1f\u800c\u6bd5\u64ce\u5929\u5462\uff1f\u90a3\u662f\u4ece\u9ad8\u5c71\u4e0a\u51b2\u4e0b\u6765 \u7684\u7011\u5e03\uff0c\u6709\u4e00\u80a1\u5f00\u5c71\u88c2\u77f3\u7684\u6c14\u6982\uff0c\u8fd9\u80a1\u7011\u5e03\u4e5f\u8bb8\u80fd\u51b2\u5230\u5927\u6d77\uff0c\u4e5f\u8bb8\u53ea\u6d41\u5165\u6e56\u4e2d\uff0c\u5c31\u53d8\u4f5c\u4e86\u6ca1 \u6709\u6e90\u5934\u7684\u6b7b\u6c34\uff0c\u6709\u4eba\u4e5f\u8bb8\u4f1a\u6b22\u559c\u7011\u5e03\uff0c\u4f46\u5374\u4e0d\u662f\u5979\u3002\u4e0d\u8fc7\u6bd5\u64ce\u5929\u56fa\u7136\u4ee4\u5979\u8ba8\u538c\uff0c\u94c1\u955c\u5fc3\u4e5f\u6ca1 \u6709\u8ba8\u5f97\u5979\u7684\u6b22\u5fc3\u3002\u6b64\u9645\uff0c\u5979\u60f3\u8d77\u4e86\u65e5\u95f4\u4e4b\u4e8b\uff0c\u7ed9\u94c1\u955c\u5fc3\u7aa5\u7834\u4e86\u5979\u7684\u5e90\u5c71\u771f\u76f8\uff0c\u5fc3\u4e2d\u65e2\u662f\u7126\u8e81 \u4e0d\u5b89\uff0c\u53c8\u662f\u60f6\u60d1\u5931\u671b\uff0c\u8fd9\u79cd\u79cd\u4e0d\u540c\u7684\u60c5\u7eea\uff0c\u7ea0\u7ed3\u4e0d\u6e05\uff0c\u6298\u78e8\u7740\u4e00\u4e2a\u5341\u516d\u5c81\u5c11\u5973\u7684\u82b3\u5fc3\u3002\u8fde\u5979 \u81ea\u5df1\u4e5f\u4e0d\u660e\u767d\u81ea\u5df1\u4e3a\u4f55\u6709\u90a3\u4e9b\u60c5\u7eea\uff1f\u4f8b\u5982\u94c1\u955c\u5fc3\u4e0e\u5979\u4f55\u5173\uff1f\u4e3a\u4f55\u5979\u6bcf\u5f53\u5728\u770b\u4e0d\u987a\u773c\uff0c\u542c\u4e0d\u987a \u8033\u4e4b\u65f6\uff0c\u5c31\u89c9\u5f97\u5fc3\u4e2d\u5931\u671b\uff1f \u3000\u3000\u591c\u5df2\u4e09\u66f4\uff0c\u55a7\u54d7\u6e10\u5bc2\u3002\u5979\u7ffb\u4e86\u4e2a\u8eab\uff0c\u542c\u5f97\u8fdc\u5904\u6d77\u98ce\u547c\u5578\uff0c\u60ca\u6d9b\u62cd\u5cb8\u4e4b\u58f0\uff0c\u7adf\u4f3c\u4ed6\u7684\u5e08\u7236 \u5728\u5411\u5979\u62db\u5524\u3002\u5979\u5728\u8fd9\u4e16\u754c\u4e0a\u9664\u4e86\u5e08\u7236\u4e4b\u5916\uff0c\u5c31\u518d\u4e5f\u6ca1\u6709\u4eb2\u4eba\u4e86\u3002\u60f3\u8d77\u4e86\u5e08\u7236\uff0c\u5fc3\u4e2d\u81ea\u7136\u6709\u4e00 \u79cd\u751c\u871c\u7684\u611f\u89c9\u3002\u5ffd\u5730\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u502d\u5bc7\u4eca\u65e5\u5403\u4e86\u8fd9\u4e2a\u5927\u8d25\u4ed7\uff0c\u51e0\u4e4e\u662f\u5168\u519b\u8986\u706d\uff0c\u5404\u5730\u867d\u7136\u8fd8 \u6709\u5c0f\u80a1\u7684\u96f6\u661f\u502d\u5bc7\uff0c\u5df2\u662f\u4e0d\u8db3\u4e3a\u60a3\uff0c\u4f55\u51b5\u8fd8\u6709\u5468\u5c71\u6c11\u7684\u63f4\u519b\u5c31\u5c06\u6765\u5230\uff0c\u66f4\u53ef\u5b89\u6795\u65e0\u5fe7\u3002\u6211\u8fd8 \u7559\u5728\u8fd9\u671b\u4f5c\u4ec0\u4e48\uff1f\u6211\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u8ddf\u6211\u7684\u5e08\u7236\uff1f\u201d\u4f46\u60f3\u8d77\u82e5\u7136\u660e\u65e5\u6b63\u5f0f\u5411\u53f6\u5b97\u7559\u544a\u8f9e\uff0c\u5219\u4e0d\u4f46 \u53f6\u5b97\u7559\u5fc5\u5b9a\u633d\u7559\uff0c\u94c1\u955c\u5fc3\u4e0e\u6bd5\u64ce\u5929\u4e8c\u4eba\u53ea\u6015\u4e5f\u4f1a\u5411\u5979\u7ea0\u7f20\u3002 \u3000\u3000\u5979\u60f3\u4e86\u53c8\u60f3\uff0c\u5ffd\u5730\u62ab\u8863\u5750\u8d77\uff0c\u62fe\u597d\u884c\u56ca\uff0c\u7559\u4e0b\u4e86\u4e00\u5c01\u5411\u53f6\u5b97\u7559\u544a\u522b\u7684\u4e66\u4fe1\uff0c\u6084\u4fcf\u8d70\u51fa\u5e10 \u5e55\uff0c\u8fd9\u665a\u662f\u4e0a\u5f26\u6708\u591c\uff0c\u6708\u8272\u5e76\u4e0d\u660e\u4eae\uff0c\u94c1\u955c\u5fc3\u7684\u5e10\u5e55\u548c\u5979\u7684\u9760\u8fd1\uff0c\u76f8\u8ddd\u4e0d\u8fc7\u534a\u91cc\u4e4b\u5730\uff0c\u5e10\u5e55 \u4e2d\u9690\u9690\u900f\u51fa\u706f\u5149\u3002\u201c\u539f\u6765\u94c1\u955c\u5fc3\u8fd8\u6ca1\u6709\u7761\u5462\uff01\u201d\u5979\u5fc3\u4e2d\u5ffd\u7136\u8d77\u4e86\u4e00\u80a1\u5947\u5f02\u7684\u611f\u60c5\uff0c\u60f3\u4ece\u4ed6\u7684 \u5e10\u5e55\u65c1\u8fb9\u8d70\u8fc7\uff0c\u5728\u4ed6\u7684\u5e10\u5e55\u65c1\u8fb9\u7559\u4e0b\u81ea\u5df1\u6700\u540e\u7684\u8db3\u5370\u3002\u94c1\u955c\u5fc3\u7ec8\u7a76\u662f\u5979\u7684\u4e00\u573a\u670b\u53cb\uff0c\u4e0d\u80fd\u8bf4 \u5b8c\u5168\u6ca1\u6709\u4e0d\u820d\u4e4b\u60c5\u3002\u4f46\u5979\u53c8\u6015\u7ed9\u4ed6\u53d1\u89c9\uff0c\u4e8e\u662f\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u501f\u7269\u969c\u5f62\uff0c\u60f3\u4ece\u4ed6\u7684\u5e10\u5e55\u65c1\u8fb9 \u634e\u6084\u6e9c\u8fc7\uff0c\u987a\u4fbf\u770b\u4e00\u770b\u4ed6\u7684\u5f71\u5b50\u3002\u8fd9\u662f\u591a\u4e48\u5947\u602a\u7684\u800c\u53c8\u77db\u76fe\u7684\u611f\u60c5\u5475\uff01\u7136\u800c\u5341\u516d\u5c81\u5c11\u5973\u7684\u5fc3 \u60c5\uff0c\u672c\u6765\u5c31\u662f\u8fd9\u6837\u5947\u602a\u800c\u53c8\u77db\u76fe\u7684\u554a\uff01 \u3000\u3000\u5ffd\u542c\u5f97\u5e10\u5e55\u65c1\u8fb9\u7684\u704c\u6728\u6797\u4e2d\uff0c\u4f3c\u6709\u4eba\u4f4e\u58f0\u79c1\u8bed\uff0c\u5176\u4e2d\u4e00\u4e2a\u58f0\u97f3\u6e05\u6e05\u695a\u695a\u662f\u94c1\u955c\u5fc3\u7684\u3002\u4e8e \u627f\u73e0\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u6837\u665a\u4ed6\u8fd8\u6ca1\u7761\u3002\u5374\u5728\u8fd9\u91cc\u9b3c\u9b3c\u795f\u795f\u5730\u548c\u4eba\u79c1\u8bed\u4f5c\u4ec0\u4e48\uff1f\u201d\u4e8e\u627f\u73e0 \u98de\u8eab\u8df3\u4e0a\u4e00\u68f5\u5927\u6811\uff0c\u5979\u8f7b\u529f\u6bd4\u8f83\u955c\u5fc3\u9ad8\u5f97\u591a\uff0c\u843d\u5728\u6811\u4e0a\uff0c\u8fde\u6811\u679d\u4e5f\u4e0d\u6447\u52a8\u4e00\u4e0b\uff0c\u5b9a\u775b\u4e00\u770b\uff0c \u548c\u94c1\u955c\u5fc3\u8bf4\u8bdd\u7684\u4eba\u539f\u6765\u5c31\u662f\u65e5\u95f4\u90a3\u4e2a\u5077\u5077\u76ef\u7740\u94c1\u955c\u5fc3\u7684\u90a3\u4e2a\u53f0\u5dde\u56e2\u7ec3\u3002 \u3000\u3000\u53ea\u542c\u5f97\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u738b\u5b89\uff0c\u4f60\u4e0d\u5728\u676d\u57ce\u4f8d\u5019\u8001\u7237\uff0c\u5374\u6765\u8fd9\u91cc\u4f5c\u4ec0\u4e48\uff1f\u4e49\u519b\u53c8\u4e0d\u5dee\u5728\u4f60\u4e00 \u4e2a\u4eba\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4e2a\u56e2\u7ec3\u4e43\u662f\u4ed6\u7684\u5bb6\u4eba\u3002\u53ea\u662f\u94c1\u955c\u5fc3\u8fd9\u53e5\u8bdd\u53ef\u5927\u4e0d\u5bf9\u3002\u201d\u738b\u5b89 \u9053\uff1a\u201c\u662f\u8001\u5927\u4eba\u5dee\u9063\u6211\u6765\u7684\uff0c\u8981\u6211\u7ed9\u4f60\u5e26\u4e2a\u53e3\u4fe1\uff0c\u767d\u5929\u4eba\u591a\uff0c\u6211\u4e0d\u65b9\u4fbf\u8bf4\u3002\u201d \u3000\u3000\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u8001\u7237\u5dee\u9063\u4f60\u7684\uff1f\u4ec0\u4e48\u53e3\u4fe1\uff1f\u201d\u8bed\u6c14\u4e4b\u95f4\uff0c\u9887\u4e3a\u60ca\u8be7\u3002\u738b\u5b89\u9053\uff1a\u201c\u8001\u5927\u4eba\u8bf4 \u4e49\u519b\u4e4b\u4e2d\u9f99\u86c7\u6df7\u6742\uff0c\u542c\u8bf4\u5404\u7701\u7684\u7eff\u6797\u5927\u76d7\u4e5f\u85c9\u6297\u502d\u4e4b\u540d\uff0c\u805a\u96c6\u4e86\u6765\u3002\u53eb\u4f60\u4e0d\u8981\u548c\u8fd9\u4e9b\u4eba\u518d\u6df7 \u5728\u4e00\u8d77\u4e86\u201d\uff01\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u5b98\u5175\u4e0d\u6562\u6297\u502d\uff0c\u7eff\u6797\u8c6a\u6770\u80af\u6295\u6548\u4e49\u519b\uff0c\u5171\u540c\u6297\u502d\uff0c\u90a3\u4e5f\u662f\u597d \u7684\u3002\u201d\u738b\u5b89\u9053\uff1a\u201c\u8bdd\u662f\u8fd9\u6837\u8bf4\uff0c\u4f46\u7763\u5baa\u5927\u5458\u53ef\u4e0d\u662f\u5982\u6b64\u60f3\u3002\u8001\u5927\u4eba\u8bf4\uff0c\u54b1\u5bb6\u4e16\u4ee3\u4e3a\u5b98\uff0c\u72af\u4e0d \u7740\u548c\u76d7\u532a\u4eec\u6df7\u5728\u4e00\u8d77\uff0c\u82e5\u7136\u4ed6\u4eec\u5c06\u6765\u72af\u4e0a\u4f5c\u4e71\uff0c\u7275\u8fde\u5728\u5185\uff0c\u8fd9\u53ef\u4e0d\u662f\u5f53\u800d\u7684\uff01\u53eb\u4f60\u60f3\u6e05\u695a \u4e86\uff01\u201d\u94c1\u955c\u5fc3\u9ed8\u7136\u4e0d\u8bed\uff0c\u4e49\u519b\u7684\u9996\u9886\u53f6\u5b97\u7559\u7b49\u4eba\uff0c\u6b63\u76f4\u65e0\u79c1\uff0c\u4ed6\u662f\u4f69\u670d\u7684\uff0c\u4f46\u4e5f\u603b\u662f\u89c9\u5f97\u81ea \u5df1\u548c\u4ed6\u4eec\u5230\u5e95\u4e0d\u662f\u540c\u4e00\u8def\u4eba\uff0c\u81f3\u4e8e\u6bd5\u64ce\u5929\u7b49\u4eba\uff0c\u90a3\u662f\u66f4\u4e0d\u6d88\u8bf4\u4e86\u3002\u94c1\u955c\u5fc3\u9661\u7136\u60f3\u8d77\u4e86\u6bd5\u64ce\u5929 \u4eca\u665a\u7684\u9152\u540e\u72c2\u8a00\uff0c\u60f3\u9053\uff1a\u201c\u53ea\u6015\u8fd9\u53ae\u8fd8\u4e0d\u6b62\u662f\u50cf\u666e\u901a\u7684\u76d7\u532a\u4f5c\u4e71\uff0c\u800c\u662f\u60f3\u62a2\u593a\u5927\u660e\u5929\u5b50\u7684\u6c5f \u5c71\u5462\u3002\u6211\u7239\u7239\u6240\u8651\uff0c\u679c\u662f\u89c1\u8bc6\u6df1\u8fdc\u3002\u201d\u738b\u5b89\u53c8\u9053\uff1a\u201c\u8001\u5927\u4eba\u53eb\u4f60\u9a6c\u4e0a\u56de\u53bb\uff0c\u53cd\u6b63\u73b0\u5728\u502d\u5bc7\u5df2 \u6d88\uff0c\u4f9d\u8001\u5974\u4e4b\u89c1\uff0c\u5c31\u5b66\u516c\u5b50\u9002\u624d\u6240\u8bf4\uff0c\u4e49\u519b\u4e5f\u4e0d\u5dee\u5728\u4f60\u4e00\u4e2a\u4eba\uff0c\u516c\u5b50\u8fd8\u662f\u56de\u53bb\u5427\uff0c\u514d\u5f97\u8001\u5927 \u4eba\u6302\u5fc3\u3002\u201d \u3000\u3000\u94c1\u955c\u5fc3\u4ecd\u662f\u9ed8\u7136\u4e0d\u8bed\uff0c\u8e0c\u8e87\u83ab\u51b3\u3002\u4ed6\u4e0d\u662f\u4e0d\u80af\u79bb\u5f00\u4e49\u519b\uff0c\u5374\u662f\u60f3\u8d77\u4e86\u4e8e\u627f\u73e0\uff0c\u820d\u4e0d\u5f97\u79bb \u5f00\u4e8e\u627f\u73e0\u3002\u738b\u5b89\u50ac\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u65e9\u70b9\u62ff\u5b9a\u4e3b\u610f\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u5f85\u6211\u518d\u60f3\u4e00\u60f3\u3002\u8001\u7237\u5728\u676d \u5dde\u53ef\u597d\uff1f\u201d\u738b\u5b89\u9053\uff1a\u201c\u4ed6\u4f4f\u5728\u629a\u53f0\u7684\u8859\u95e8\uff0c\u8fd9\u4f4d\u629a\u53f0\u53eb\u536b\u6625\u5ef7\uff0c\u4f60\u8bb0\u5f97\u4e48\uff1f\u201d\u94c1\u955c\u5fc3\u70b9\u5934 \u9053\uff1a\u201c\u8bb0\u5f97\uff0c\u4ed6\u662f\u8001\u7237\u7684\u5b66\u751f\u3002\u8001\u7237\u7684\u5b66\u751f\uff0c\u5b98\u505a\u5f97\u6700\u5927\u7684\u5c31\u662f\u4ed6\u4e86\u3002\u201d\u738b\u5b89\u9053\uff1a\u201c\u4e0d\u9519\u3002 \u96be\u5f97\u4ed6\u8fd8\u5ff5\u8d77\u5e08\u751f\u4e4b\u8c0a\uff0c\u4e00\u542c\u8bf4\u8001\u5927\u4eba\u8fc1\u5c45\u676d\u57ce\uff0c\u5c31\u7acb\u523b\u8fce\u63a5\u6211\u4eec\u5230\u629a\u53f0\u8859\u95e8\u53bb\u4f4f\uff0c\u62db\u547c\u5f97 \u5f88\u5468\u5230\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u90a3\u6211\u5c31\u653e\u5fc3\u4e86\u3002\u738b\u5b89\uff0c\u4f60\u5148\u56de\u53bb\u5427\uff0c\u6211\u5c31\u662f\u8d70\u4e5f\u8981\u8fdf\u4e24\u5929\u3002\u201d \u3000\u3000\u5de5\u5b89\u9053\uff1a\u201c\u8fdf\u4e24\u5929\u4e5f\u597d\uff0c\u8001\u5927\u4eba\u8fd8\u6709\u4e00\u6869\u4e8b\u60c5\uff0c\u53eb\u4f60\u659f\u914c\u7740\u529e\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b \u60c5\uff1f\u201d\u738b\u5b89\u9053\uff1a\u201c\u8001\u5927\u4eba\u63a5\u5230\u7687\u4e0a\u5bc6\u65e8\uff0c\u8981\u4ed6\u7763\u4ee4\u4f60\u534f\u52a9\u5fa1\u6797\u519b\u7684\u7edf\u9886\u6349\u62ff\u4e00\u4e2a\u94a6\u72af\u3002\u201d\u94c1 \u955c\u5fc3\u9053\uff1a\u201c\u8fd9\u53ef\u5947\u4e86\uff0c\u6349\u62ff\u94a6\u72af\u4e0e\u6211\u4f55\u4e8e\uff0c\u6211\u53c8\u4e0d\u662f\u671d\u5ef7\u7684\u547d\u5b98\uff0c\u7687\u4e0a\u5723\u660e\uff0c\u54ea\u6709\u8fd9\u6837\u7cca\u6d82 \u4e4b\u7406\uff0c\u83ab\u975e\u4f60\u542c\u9519\u4e86\u3002\u201d\u738b\u5b89\u9053\uff1a\u201c\u8fd9\u4e2a\u94a6\u72af\u4e0d\u662f\u65c1\u4eba\uff0c\u662f\u4f60\u7684\u77f3\u8001\u5e08\uff01\u201d\u94c1\u955c\u5fc3\u8fd9\u4e00\u60ca\u975e \u540c\u5c0f\u53ef\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u77f3\u8001\u5e08\u662f\u94a6\u72af\uff1f\u201d\u738b\u5b89\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5723\u65e8\u662f\u5fa1\u6797\u519b\u7edf\u9886\u5a04\u6850\u5b59\u5e26\u6765 \u7684\uff0c\u7531\u536b\u629a\u4eb2\u81ea\u4ea4\u7ed9\u8001\u5927\u4eba\u3002\u636e\u8bf4\u77f3\u8001\u5e08\u4e09\u5341\u5e74\u524d\u66fe\u5927\u95f9\u7687\u5bab\uff0c\u5077\u53bb\u4e86\u5927\u5185\u5b9d\u5251\u3002\u73b0\u5728\u624d\u8bbf \u67e5\u5230\u4ed6\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u8fd9\u6d88\u606f\u50cf\u4e00\u4e2a\u6674\u5929\u7684\u9739\u96f3\uff0c\u628a\u94c1\u955c\u5fc3\u60ca\u5f97\u5446\u4e86\uff01\u8fd9\u5239\u90a3\u95f4\uff0c\u4e0e\u5e08\u7236\u9047\u5408\u7684\u7ecf\u8fc7\uff0c\u53c8\u4f3c\u95ea \u7535\u822c\u4e00\u5e55\u5e55\u4ece\u8111\u4e2d\u95ea\u8fc7\u3002 \u3000\u3000\u90a3\u662f\u5341\u5e74\u524d\u7684\u4e00\u4e2a\u79cb\u5929\uff0c\u94c1\u955c\u5fc3\u8fd8\u662f\u4e00\u4e2a\u5341\u4e8c\u5c81\u7684\u5b69\u5b50\uff0c\u4ed6\u7684\u7236\u4eb2\u5728\u4eac\u4f9b\u804c\u505a\u5fa1\u53f2\u5927 \u592b\uff0c\u5bb6\u4e2d\u6ca1\u4eba\u7ba1\u4ed6\uff0c\u4ed6\u8bfb\u4e66\u4e4b\u5916\uff0c\u5c31\u559c\u6b22\u8ddf\u62a4\u9662\u7684\u6b66\u5e08\u62c8\u5200\u5f04\u68d2\u3002\u4ed6\u5bb6\u5728\u53f0\u5dde\u57ce\u5916\uff0c\u6709\u4e00\u4e2a \u522b\u5885\uff0c\u6bcf\u5e74\u590f\u5929\uff0c\u4ed6\u90fd\u548c\u5802\u5144\u5f1f\u5230\u522b\u5885\u907f\u6691\uff0c\u5230\u4e86\u79cb\u51c9\u65f6\u5206\u624d\u56de\u4eac\u57ce\u3002\u5728\u522b\u5885\u4e2d\u7ec3\u6b66\uff0c\u90a3\u66f4 \u662f\u65e0\u62d8\u65e0\u675f\u3002\u8fd9\u4e00\u5e74\u65b0\u8bf7\u5230\u4e24\u4f4d\u672c\u9886\u9ad8\u5f3a\u7684\u6b66\u5e08\uff0c\u4e00\u4e2a\u5584\u4f7f\u4e8c\u90ce\u68d2\uff0c\u4e00\u4e2a\u81ea\u79f0\u61c2\u5f97\u94c1\u7802\u638c\uff0c \u94c1\u955c\u5fc3\u66fe\u89c1\u5230\u4ed6\u4e00\u638c\u5c06\u4e00\u5757\u9752\u7816\u62cd\u5f97\u788e\u88c2\uff0c\u4f69\u670d\u5f97\u4e86\u4e0d\u5f97\u3002\u4ed6\u4eec\u5728\u6d77\u6ee8\u522b\u5885\uff0c\u6574\u5929\u6325\u62f3\u821e \u68d2\uff0c\u7b80\u76f4\u4e50\u800c\u5fd8\u8fd4\uff0c\u5c06\u8fd1\u4e2d\u79cb\uff0c\u8fd8\u672a\u820d\u5f97\u56de\u57ce\u4e2d\u8001\u5bb6\u3002 \u3000\u3000\u8fd9\u4e00\u5929\u665a\u4e0a\uff0c\u5ffd\u7136\u6709\u4e00\u4f19\u5f3a\u76d7\uff0c\u660e\u706b\u6267\u4ed7\u5730\u8fdb\u6765\u62a2\u52ab\uff0c\u62a4\u9662\u7684\u6b66\u5e08\u4e00\u4e2a\u4e2a\u90fd\u88ab\u6253\u5012\uff0c\u5f3a \u76d7\u8fd8\u8981\u7ed1\u67b6\u4ed6\uff0c\u90a3\u4e24\u4e2a\u672c\u9886\u6700\u9ad8\u7684\u6b66\u5e08\u5374\u85cf\u533f\u4e0d\u89c1\uff0c\u5f62\u52bf\u5341\u5206\u5371\u6025\uff0c\u5e78\u559c\u94c1\u955c\u5fc3\u8eab\u5f62\u6e9c\u6ed1\uff0c \u4eba\u53c8\u77ee\u5c0f\uff0c\u5728\u522b\u5885\u4e2d\u6e9c\u6765\u6e9c\u53bb\uff0c\u5f3a\u76d7\u4eec\u597d\u534a\u5929\u8fd8\u6ca1\u627e\u5230\u4ed6\u3002\u6709\u4e00\u4e2a\u5f3a\u76d7\u5934\u5b50\u6025\u4e86\uff0c\u66f3\u5f00\u5f39\u5f13 \u5c31\u60f3\u6253\u4ed6\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u4e00\u4e2a\u957f\u987b\u98d8\u62c2\u7684\u8001\u6e14\u592b\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u7a81\u7136\u51fa\u73b0\u5728\u5047\u5c71\u524d\u9762\uff0c\u53ea\u662f\u4e00\u4e2a \u7167\u9762\uff0c\u5c31\u5c06\u90a3\u5f3a\u76d7\u5934\u5b50\u7684\u5f39\u5f13\u5939\u624b\u62a2\u53bb\uff0c\u5f3a\u76d7\u4eec\u5927\u6012\uff0c\u62a1\u5200\u52a8\u65a7\u7eb7\u7eb7\u4e0a\u524d\u5288\u65ab\uff0c\u8fd9\u8001\u6e14\u592b\u62d4 \u51fa\u4e00\u53e3\u5b9d\u5251\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u65ad\u91d1\u788e\u7389\u4e4b\u58f0\uff0c\u5f3a\u76d7\u4eec\u7684\u5175\u5668\u51e0\u4e4e\u5168\u7ed9\u4ed6\u524a\u65ad\uff0c\u8fd9\u8001\u6e14\u592b\u559d\u9053\uff1a \u201c\u6211\u5b9d\u5251\u4e0d\u6740\u65e0\u540d\u5c0f\u5352\uff0c\u5feb\u7ed9\u6211\u6eda\uff01\u201d\u970e\u65f6\u95f4\uff0c\u5f3a\u76d7\u4eec\u9003\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u90a3\u4e9b\u62a4\u9662\u7684\u6b66\u5e08\u8fd8\u54fc \u54fc\u537f\u537f\u5728\u5730\u4e0a\u722c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u8001\u6e14\u592b\u54c8\u54c8\u5927\u7b11\uff0c\u62c9\u7740\u94c1\u955c\u5fc3\u7684\u624b\u7aef\u8be6\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u5ffd\u800c\u53c8\u53f9\u6c14\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u8ba8\u60dc\uff01 \u5929\u751f\u7684\u4e00\u526f\u5b66\u6b66\u8d44\u8d28\uff0c\u5374\u8bef\u5728\u5eb8\u5e08\u4e4b\u624b\u3002\u201d\u8fd9\u65f6\u90a3\u4e24\u4e2a\u672c\u9886\u6700\u5f3a\u7684\u6b66\u5e08\u624d\u4ece\u91cc\u9762\u94bb\u51fa\u6765\uff0c\u6c89 \u7740\u9762\u9053\uff1a\u201c\u8001\u5e08\u7236\u8d23\u5907\u5f97\u5f53\uff0c\u6211\u4eec\u90fd\u662f\u6765\u6df7\u996d\u5403\u7684\u3002\u6211\u4eec\u672c\u5f53\u7acb\u5373\u8ba9\u9053\uff0c\u4f46\u6211\u4eec\u4e0d\u77e5\u81ea\u8c05\uff0c \u8fd8\u60f3\u8bf7\u8001\u5e08\u7236\u518d\u7ed9\u6211\u4eec\u5f00\u5f00\u773c\u754c\u3002\u201d\u7a81\u7136\u5de6\u53f3\u5939\u51fb\uff0c\u4e00\u4e2a\u7528\u6728\u68d2\u5288\u8001\u6e14\u592b\u7684\u5934\u9885\uff0c\u4e00\u4e2a\u7528\u94c1 \u7802\u638c\u5288\u8001\u6e14\u592b\u7684\u80cc\u5fc3\uff0c\u4f46\u89c1\u8001\u6e14\u592b\u632f\u81c2\u4e00\u6325\uff0c\u6728\u68d2\u201c\u5580\u5587\u201d\u4e00\u58f0\uff0c\u65ad\u4e3a\u4e24\u6bb5\uff0c\u524d\u9762\u7684\u90a3\u4e2a\u6b66 \u5e08\u7acb\u5373\u4ec6\u5012\u5730\u4e0a\uff1b\u4f46\u540e\u9762\u90a3\u4e2a\u6b66\u5e08\u7684\u4e00\u638c\uff0c\u5374\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u6253\u4e2d\u4e86\u8001\u6e14\u592b\u7684\u80cc\u5fc3\u3002\u94c1\u955c\u5fc3\u867d\u662f \u4e2a\u5c0f\u5b69\u5b50\uff0c\u4f46\u5df2\u77e5\u9053\u8fa8\u522b\u5584\u6076\uff0c\u4e00\u89c1\u8fd9\u4e24\u4e2a\u6b66\u5e08\u884c\u4e3a\u5982\u6b64\u5351\u52a3\uff0c\u5927\u662f\u751f\u6c14\uff0c\u5954\u4e0a\u524d\u9762\u65a5\u9a82\u90a3 \u4e2a\u81ea\u5938\u61c2\u5f97\u201c\u94c1\u7802\u638c\u201d\u7684\u6b66\u5e08\uff0c\u52ab\u89c1\u90a3\u6b66\u5e08\u6367\u7740\u624b\u8155\u5594\u5594\u547c\u75db\u3002\u8001\u6e14\u592b\u7b11\u9053\uff1a\u201c\u5c0f\u54e5\u513f\u4e0d\u5fc5 \u518d\u8d23\u9a82\u4ed6\u4e86\uff0c\u4ed6\u5df1\u591f\u53d7\u5566\uff01\u201d\u90a3\u6b66\u5e08\u7684\u81c2\u818a\u80bf\u5f97\u5982\u540c\u540a\u6876\uff0c\u624b\u638c\u7fd8\u8d77\uff0c\u4e94\u6307\u50f5\u786c\uff0c\u518d\u4e5f\u4e0d\u80fd \u5f2f\u66f2\uff0c\u540e\u6765\u94c1\u955c\u5fc3\u624d\u77e5\u9053\uff0c\u8fd9\u4e2a\u6b66\u5e08\u4e0d\u4f46\u4e00\u6761\u81c2\u818a\u518d\u4e5f\u4e0d\u80fd\u4f7f\u7528\uff0c\u5168\u8eab\u7684\u6b66\u529f\u4e5f\u88ab\u5e9f\u4e86\u3002 \u3000\u3000\u7ecf\u6b64\u4e00\u6765\uff0c\u62a4\u9662\u7684\u6b66\u5e08\u5168\u90fd\u8d70\u4e86\u3002\u94c1\u955c\u5fc3\u4fbf\u8981\u62dc\u8fd9\u4e2a\u8001\u6e14\u592b\u4e3a\u5e08\uff0c\u4f46\u8001\u6e14\u592b\u5374\u8981\u4ed6\u5148\u7b54 \u5e94\u4e00\u4e2a\u6761\u4ef6\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u6b63\u5728\u5174\u5934\u4e0a\uff0c\u4e0d\u8981\u8bf4\u4e00\u4e2a\u6761\u4ef6\uff0c\u5341\u4e2a\u6761\u4ef6\u4e5f\u80af\u7b54\u5e94\u3002\u5374\u539f\u6765\u90a3\u4e2a\u6761\u4ef6\u662f\u4e0d\u8bb8\u4ed6\u5c06 \u62dc\u5e08\u4e4b\u4e8b\u8bf4\u4e0e\u65c1\u4eba\u77e5\u9053\uff0c\u5373\u81f3\u4eb2\u5982\u7236\u6bcd\u5144\u5f1f\u4e5f\u4e0d\u8bb8\u544a\u8bc9\uff0c\u540c\u65f6\u4ed6\u7edd\u4e0d\u5230\u94c1\u955c\u5fc3\u5bb6\u4e2d\u4f20\u6280\u3002\u94c1 \u955c\u5fc3\u95ee\u4ed6\u662f\u4e0d\u662f\u8981\u8ddf\u4ed6\u5230\u522b\u7684\u5730\u65b9\u53bb\u5b66\uff0c\u90a3\u8001\u6e14\u5927\u6447\u6447\u5934\u7b11\u9053\uff1a\u201c\u6211\u600e\u6562\u5e26\u4f60\u8fd9\u4e2a\u5b98\u5bb6\u5b50\u5f1f \u5230\u522b\u7684\u5730\u65b9\u53bb\uff0c\u4e0d\u6015\u843d\u4e86\u4e2a\u62d0\u5e26\u7684\u7f6a\u540d\u4e48\uff1f\u201d\u94c1\u955c\u5fc3\u95ee\u4ed6\u600e\u4e48\u4f20\u6280\uff0c\u4ed6\u8bf4\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u8fc7 \u51e0\u5929\u4fbf\u8981\u56de\u53f0\u57ce\u8001\u5bb6\uff0c\u6211\u5148\u6559\u4f60\u4e00\u5957\u624e\u6839\u57fa\u7684\u5410\u7eb3\u529f\u592b\uff0c\u8fd9\u4e00\u5e74\u4e2d\u4f60\u4f9d\u6cd5\u7ec3\u529f\uff0c\u660e\u5e74\u4f60\u518d\u5230 \u6b64\u5904\u907f\u6691\uff0c\u6211\u81ea\u7136\u4f1a\u518d\u6765\u89c1\u4f60\u3002\u201d\u94c1\u955c\u5fc3\u56de\u5bb6\u540e\uff0c\u679c\u7136\u53ea\u628a\u9047\u76d7\u4e4b\u4e8b\u544a\u77e5\u5bb6\u4eba\uff0c\u5374\u5c06\u62dc\u5e08\u4e4b \u4e8b\u7792\u8fc7\u3002 \u3000\u3000\u7b2c\u4e8c\u5e74\u94c1\u955c\u5fc3\u5e26\u4e86\u5fc3\u8179\u7684\u5bb6\u4eba\u518d\u6765\u907f\u6691\uff0c\u90a3\u8001\u6e14\u592b\u679c\u7136\u4f9d\u7ea6\u800c\u6765\uff0c\u4f46\u5374\u4e0d\u5728\u522b\u5885\u4e2d\u6559\u4ed6 \u6b66\u529f\uff0c\u539f\u6765\u8fd9\u8001\u4e00\u6e14\u592b\u6709\u4e00\u95f4\u5c0f\u5c4b\u5728\u6d77\u8fb9\uff0c\u4ed6\u53eb\u94c1\u955c\u5fc3\u6bcf\u5929\u5047\u4f5c\u6e38\u73a9\u3001\u5230\u4ed6\u7684\u5c4b\u5b50\u6765\uff0c\u8fd9\u8001 \u6e14\u592b\u8fd8\u6709\u4e00\u4e2a\u5973\u513f\uff0c\u53ea\u6709\u516b\u5c81\uff0c\u8001\u6e14\u592b\u5c31\u53eb\u4ed6\u548c\u5973\u513f\u4e00\u540c\u4e60\u6b66\u3002\u8fd9\u65f6\uff0c\u94c1\u955c\u5fc3\u624d\u77e5\u9053\u8fd9\u4e2a\u8001 \u6e14\u592b\u7684\u540d\u5b57\u53eb\u505a\u77f3\u60ca\u6d9b\uff0c\u4ed6\u7684\u5973\u513f\u53eb\u77f3\u6587\u7ea8\u3002\u5982\u6b64\u8fd9\u822c\uff0c\u94c1\u955c\u5fc3\u6bcf\u5e74\u8ddf\u77f3\u60ca\u6d9b\u5b66\u4e09\u4e2a\u6708\u7684\u6b66 \u529f\uff0c\u5176\u4f59\u7684\u65f6\u95f4\uff0c\u4fbf\u5728\u5bb6\u4e2d\u6697\u81ea\u7ec3\u4e60\u3002\u77f3\u60ca\u6d9b\u6709\u65f6\u5728\u665a\u4e0a\u4e5f\u4f1a\u6765\u5230\u522b\u5885\u770b\u4ed6\uff0c\u4f46\u53f0\u57ce\u7684\u8001 \u5bb6\uff0c\u5374\u4e00\u6b21\u4e5f\u6ca1\u6765\u8fc7\u3002 \u3000\u3000\u5982\u662f\u8005\u8fc7\u4e86\u4e03\u5e74\uff0c\u5728\u8fd9\u4e03\u5e74\u4e2d\uff0c\u53d1\u751f\u4e86\u4e0d\u5c11\u53d8\u5316\uff0c\u77f3\u60ca\u6d9b\u53c8\u6536\u4e86\u4e00\u4e2a\u6e14\u5bb6\u5b50\u5f1f\u6210\u6d77\u5c71\u505a \u5f92\u5f1f\uff0c\u94c1\u955c\u5fc3\u7684\u7236\u4eb2\u5378\u5b98\u56de\u5bb6\uff0c\u94c1\u955c\u5fc3\u4e5f\u5728\u53bf\u91cc\u8003\u4e2d\u4e86\u79c0\u624d\uff0c\u4ff1\u4ed6\u6bcf\u5e74\u4ecd\u662f\u7167\u4f8b\u5230\u6d77\u57ce\u907f \u6691\uff0c\u6bcf\u5929\u4ecd\u6697\u4e2d\u7ec3\u4e60\u6b66\u529f\uff0c\u4ed6\u5bb6\u4e2d\u53c8\u8bf7\u6765\u4e86\u65b0\u7684\u62a4\u9662\u6b66\u5e08\uff0c\u4ed6\u4e5f\u5076\u5c14\u8ddf\u62a4\u9662\u6b66\u5e08\u5b66\u5b66\u82b1\u62f3\u7ee3 \u817f\uff0c\u8c01\u4e5f\u4e0d\u77e5\u4ed6\u8eab\u6000\u7edd\u6280\u3002 \u3000\u3000\u5230\u4e86\u7b2c\u4e03\u5e74\u7684\u6625\u5929\uff0c\u502d\u5bc7\u5f00\u59cb\u4fb5\u6270\u6cbf\u6d77\u4e00\u5e26\uff0c\u6709\u4e00\u6b21\u4ed6\u8def\u89c1\u4e0d\u5e73\uff0c\u62d4\u5200\u76f8\u52a9\uff0c\u628a\u4e00\u5c0f\u80a1 \u502d\u5bc7\u6253\u9000\uff0c\u6551\u51fa\u4e86\u4e00\u5bb6\u4e61\u6c11\uff0c\u8fd9\u4e00\u6765\u4ed6\u4f1a\u6b66\u7684\u540d\u5934\u7acb\u523b\u4f20\u64ad\u51fa\u53bb\uff0c\u4ed6\u7684\u7236\u4eb2\u94c1\u94f1\u4e5f\u77e5\u9053\u4e86\uff0c \u4e00\u5929\u665a\u4e0a\u4fbf\u5524\u4e86\u4ed6\u6765\u95ee\u8bdd\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u81ea\u5c0f\u656c\u670d\u4ed6\u7684\u7236\u4eb2\uff0c\u5728\u7236\u4eb2\u76d8\u8bdd\u4e4b\u4e0b\uff0c\u5fd8\u6389\u4e86\u5bf9\u5e08\u7236\u7684\u8a93\u8a00\uff0c\u5c06\u6697\u4e2d\u6295\u5e08\u5b66\u6280\u7684 \u4e8b\u60c5\u548c\u76d8\u6258\u51fa\uff0c\u4ed6\u7236\u4eb2\u53c8\u60ca\u53c8\u559c\uff0c\u559c\u7684\u662f\u513f\u5b50\u5b66\u6210\u4e86\u6587\u6b66\u5168\u624d\uff0c\u60ca\u7684\u662f\u6015\u513f\u5b50\u4ea4\u7ed3\u8fd9\u5e2e\u6c5f\u6e56 \u5f02\u4eba\uff0c\u4f1a\u60f9\u51fa\u7978\u4e8b\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u5e74\u7684\u590f\u5929\uff0c\u94c1\u955c\u5fc3\u5230\u522b\u5885\u53bb\uff0c\u77f3\u60ca\u6d9b\u5374\u4e0d\u6765\u4e86\uff0c\u94c1\u955c\u5fc3\u95ee\u5e08\u59b9\u77f3\u6587\u7ea8\uff0c\u77f3\u6587\u7ea8\u8bf4\u4ed6 \u7684\u7236\u4eb2\u884c\u8e2a\u65e0\u5b9a\uff0c\u4ec0\u4e48\u65f6\u5019\u56de\u6765\uff0c\u5979\u4e5f\u4e0d\u77e5\u3002\u94c1\u955c\u5fc3\u5728\u522b\u5885\u7b49\u4e86\u4e00\u4e2a\u590f\u5929\uff0c\u90fd\u6ca1\u6709\u5f97\u5230\u5e08\u7236 \u7684\u97f3\u8baf\uff0c\u4e00\u76f4\u5230\u4e86\u4eca\u5929\uff0c\u624d\u5728\u4e49\u519b\u4e2d\u51fa\u5176\u610f\u5916\u5730\u91cd\u9022\u3002 \u3000\u3000\u5f80\u4e8b\u4e00\u5e55\u5e55\u4ece\u8111\u6d77\u4e2d\u95ea\u8fc7\uff0c\u5e08\u7236\u8be1\u79d8\u7684\u884c\u5f84\uff0c\u4ee5\u524d\u65e0\u6cd5\u7406\u89e3\u7684\u884c\u5f84\uff0c\u73b0\u5728\u624d\u771f\u76f8\u5927\u767d\uff0c \u539f\u6765\u5e08\u7236\u7adf\u7136\u662f\u5927\u5185\u6240\u8981\u7f09\u62ff\u7684\u5fa1\u72af\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u542c\u4e86\u738b\u5b89\u7684\u8bdd\uff0c\u767b\u65f6\u5446\u82e5\u6728\u9e21\uff0c\u9976\u4ed6\u81ea\u8d1f\u806a\u660e\uff0c\u8fd9\u65f6\u5374\u60f3\u4e0d\u51fa\u534a\u70b9\u529e\u6cd5\u3002\u6b66\u6797\u4e4b \u4e2d\uff0c\u53db\u5e08\u4e43\u662f\u4e00\u79cd\u65e0\u53ef\u9976\u6055\u7684\u5927\u7f6a\uff0c\u4f55\u51b5\u5c06\u5e08\u7236\u6349\u62ff\uff1f\u4f46\u7687\u547d\u66f4\u662f\u4e0d\u53ef\u8fdd\u6297\uff01 \u3000\u3000\u6de1\u6de1\u7684\u6708\u5149\uff0c\u900f\u8fc7\u7e41\u679d\u5bc6\u53f6\uff0c\u4e8e\u627f\u73e0\u4f0f\u8eab\u6811\u9876\uff0c\u53ea\u89c1\u94c1\u955c\u5fc3\u7684\u5f71\u5b50\u5728\u5730\u4e0a\u4e1c\u98d8\u897f\u6643\uff0c\u663e \u89c1\u662f\u7ed5\u6811\u5f77\u5fa8\uff0c\u5fc3\u60c5\u70e6\u8e81\u4e4b\u6781\u3002\u5ffd\u542c\u5f97\u738b\u5b89\u5e72\u54b3\u4e00\u58f0\uff0c\u90d1\u91cd\u95ee\u9053\uff1a\u201c\u516c\u5b50\u5e7c\u8bfb\u8bd7\u4e66\uff0c\u4eba\u4f26\u7684 \u5c0a\u5351\u4e4b\u5e8f\uff0c\u90a3\u81ea\u7136\u662f\u77e5\u9053\u7684\u4e86\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u5929\u5730\u541b\u4eb2\u5e08\uff0c\u8fd9\u662f\u4e09\u5c3a\u7ae5\u5b50\u90fd\u77e5\u9053\u7684\uff0c\u4f60\u95ee \u8fd9\u4e2a\u5e72\u4ec0\u4e48\uff1f\u201d\u738b\u5b89\u9053\uff1a\u201c\u7167\u8fd9\u6837\u8bf4\u6765\uff0c\u9664\u4e86\u5929\u5730\u4e4b\u5916\uff0c\u5c31\u662f\u541b\u4e0a\u6700\u5c0a\uff0c\u5176\u6b21\u662f\u7236\u5b50\u4e4b\u4eb2\uff0c \u6700\u540e\u624d\u662f\u5e08\u751f\u4e4b\u8c0a\u4e86\u3002\u201d\u94c1\u955c\u5fc3\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u6559\u6211\u505a\u4e2a\u53db\u5e08\u7684\u4e0d\u4e49\u4e4b\u4eba \u4e48\uff1f\u201d\u8272\u5389\u5185\u832c\uff0c\u8bdd\u58f0\u8bf4\u5230\u540e\u6765\uff0c\u5df2\u662f\u5fae\u5fae\u98a4\u6296\uff0c\u5fc3\u60c5\u60f6\u6050\u4e0d\u5b89\u53c8\u6709\u4e00\u4e9b\u5947\u602a\uff0c\u60f3\u4e0d\u5230\u738b\u5b89 \u4e5f\u4f1a\u4ee5\u201c\u5fae\u8a00\u5927\u4e49\u201d\u76f8\u8d23\uff0c\u4ed6\u4e0d\u77e5\u9053\uff0c\u8fd9\u756a\u8bdd\u5176\u5b9e\u662f\u4ed6\u7684\u7236\u4eb2\u6559\u738b\u5b89\u8bf4\u7684\u3002 \u3000\u3000\u738b\u5b89\u9053\uff1a\u201c\u5974\u4ec6\u600e\u6562\u6559\u516c\u5b50\u505a\u4e2a\u4e0d\u4e49\u4e4b\u4eba\uff0c\u4f46\u5974\u4ec6\u66f4\u4e0d\u613f\u610f\u516c\u5b50\u505a\u4e2a\u4e0d\u5fe0\u4e0d\u5b5d\u4e4b\u4eba\uff01\u201d \u94c1\u955c\u5fc3\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u6211\u82e5\u4e0d\u9075\u5723\u65e8\uff0c\u6211\u7236\u4eb2\u4f1a\u6709\u5371\u9669\u4e48\uff1f\u201d\u738b\u5b89\u9053\uff1a\u201c\u53ea\u6015\u91cd\u5219\u6709\u6284 \u5bb6\u4e4b\u7978\uff0c\u8f7b\u4ea6\u6709\u7262\u72f1\u4e4b\u707e\u3002\u201d\u94c1\u955c\u5fc3\u9762\u8272\u60e8\u65e5\uff0c\u5728\u6708\u8272\u4e4b\u4e0b\uff0c\u795e\u6c14\u663e\u5f97\u5341\u5206\u96be\u770b\uff0c\u5b8c\u5168\u5931\u4e86 \u4e3b\u610f\uff0c\u50cf\u4e2a\u968f\u98ce\u98d8\u8361\u7684\u5e7d\u7075\u3002\u53ea\u542c\u5f97\u738b\u5b89\u53c8\u9053\uff1a\u201c\u8001\u5927\u4eba\u73b0\u5728\u5176\u5b9e\u662f\u5df2\u88ab\u8f6f\u7981\u629a\u8859\uff0c\u5409\u51f6\u7978 \u798f\u5c31\u5168\u5728\u516c\u5b50\u624b\u4e0a\u4e86\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u4f60\u4e2a\u662f\u8bf4\u629a\u53f0\u4e43\u662f\u6211\u7239\u7239\u6700\u5f97\u610f\u7684\u5b66\u751f\uff1f\u201d\u738b\u5b89\u9053\uff1a \u201c\u5974\u4ec6\u8ddf\u968f\u8001\u5927\u4eba\u4e09\u5341\u5e74\uff0c\u5b98\u573a\u7684\u4e8b\u513f\uff0c\u5974\u4ec6\u8fd8\u7565\u77e5\u4e00\u4e8c\uff0c\u78b0\u5230\u5229\u5bb3\u7684\u5173\u5934\u4e0a\u5934\uff0c\u5b98\u505a\u5f97\u8d8a \u5927\u5c31\u8d8a\u4e0d\u4f1a\u987e\u5ff5\u60c5\u8c0a\u3002\u60f3\u7687\u4e0a\u6df1\u5c45\u4e5d\u91cd\uff0c\u4ed6\u600e\u4f1a\u77e5\u9053\u77f3\u60ca\u6d9b\u662f\u516c\u5b50\u7684\u5e08\u7236\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a \u201c\u662f\u554a\uff0c\u90a3\u4e48\u8fd9\u5723\u65e8\u83ab\u975e\u6709\u5047\uff1f\u201d\u738b\u5b89\u9053\uff1a\u201c\u5723\u65e8\u600e\u4f1a\u6709\u5047\uff1f\u516c\u5b50\u4e0d\u61c2\u5b98\u573a\u4e4b\u4e8b\u3002\u5a04\u6850\u5b59\u4ee5 \u5fa1\u6797\u519b\u7edf\u9886\u7684\u8eab\u4efd\u51fa\u6765\u6355\u6349\u94a6\u72af\uff0c\u4ed6\u8eab\u4e0a\u81ea\u7136\u5e26\u6709\u7687\u4e0a\u6240\u8e22\u7684\u76d6\u6709\u5fa1\u5370\u7684\u7a7a\u767d\u6298\u5b50\uff0c\u586b\u4e0a\u53bb \u90a3\u5c31\u662f\u5723\u65e8\u4e86\u3002\u542c\u8bf4\u77f3\u8001\u5e08\u7684\u6765\u5386\u548c\u4e0b\u843d\u662f\u5a04\u6850\u5b59\u63a2\u51fa\u6765\u7684\uff0c\u5a04\u6850\u5b59\u4e00\u4e2a\u4eba\u4e0d\u6562\u6765\u6355\u62ff\u77f3\u8001 \u5e08\uff0c\u56e0\u6b64\u7528\u5723\u65e8\u8d23\u6210\u536b\u629a\u53f0\u548c\u8001\u5927\u4eba\u66ff\u4ed6\u51fa\u529b\uff0c\u82e5\u7136\u516c\u5b50\u4e0d\u80af\u52a9\u4ed6\uff0c\u4e0d\u4f46\u662f\u8001\u5927\u4eba\u7acb\u6709\u707e \u7978\uff0c\u8fde\u536b\u629a\u53f0\u4e5f\u8131\u4e0d\u4e86\u5e72\u7cfb\u7684\u3002\u90a3\u5a04\u6850\u5b59\u53ea\u6015\u4e5f\u4f1a\u5230\u8fd9\u513f\u6765\u5462\u3002\u201d\u94c1\u955c\u5fc3\u5583\u5583\u8bf4\u9053\uff1a\u201c\u6211\u82e5 \u5356\u5e08\u6c42\u8363\uff0c\u5b9a\u53d7\u5929\u4e0b\u82f1\u96c4\u553e\u9a82\uff01\u201d\u738b\u5b89\u9053\uff1a\u201c\u8001\u5927\u4eba\u82e5\u6709\u4e0d\u6d4b\uff0c\u516c\u5b50\u4e0d\u5b5d\u4e4b\u7f6a\uff0c\u503e\u957f\u6c5f\u4e4b\u6c34 \u53ea\u6015\u4e5f\u6d17\u4e0d\u6e05\uff01\u201d\u94c1\u955c\u5fc3\u9762\u5b54\u94c1\u9752\uff0c\u6325\u624b\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u8bf4\u5566\uff0c\u4f60\u4e14\u56de\u53bb\uff0c\u6b64\u4e8b\u5f85\u6211\u4e09\u601d\u800c \u884c\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5728\u6811\u4e0a\u4e5f\u542c\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u60f3\u9053\uff1a\u201c\u597d\u5440\uff0c\u94c1\u955c\u5fc3\u5230\u5e95\u662f\u4e49\u4fa0\u4e4b\u58eb\uff0c\u6216\u662f\u4e2a\u5351\u9119\u5c0f \u4eba\uff0c\u4e5f\u5168\u770b\u4ed6\u4eca\u665a\u7684\u884c\u4e8b\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u6700\u5c0a\u656c\u5e08\u7236\uff0c\u4e0d\u7ba1\u5982\u4f55\uff0c\u5356\u5e08\u6c42\u8363\uff0c\u5728\u5979\u770b\u6765\uff0c\u90a3\u662f \u7edd\u5bf9\u4e0d\u53ef\u9976\u6055\u4e4b\u4e8b\uff0c\u4f55\u51b5\u77f3\u60ca\u6d9b\u53c8\u662f\u4e0e\u5979\u5e08\u7236\u9f50\u540d\u7684\u4fa0\u4e49\u4e4b\u58eb\uff01 \u3000\u3000\u6811\u4e0a\u7684\u4e8e\u627f\u73e0\u3001\u6811\u4e0b\u7684\u94c1\u955c\u5fc3\u4e24\u4eba\u90fd\u662f\u5404\u6709\u5fc3\u601d\uff0c\u8fd9\u65f6\u5df2\u662f\u6708\u8fc7\u4e2d\u5929\uff0c\u5728\u4e07\u7c41\u4ff1\u5bc2\u4e4b\u4e2d \u51fa\u542c\u5f97\u6709\u4eba\u957f\u5578\uff0c\u6717\u58f0\u541f\u9053\uff1a\u201c\u4e0d\u8d1f\u9752\u950b\u4e09\u5c3a\u5251\uff0c\u8001\u6765\u809d\u80c6\u66f4\u5982\u971c\uff01\u201d\u4e00\u4eba\u5f39\u5251\u800c\u6b4c\uff0c\u6e10\u884c \u6e10\u8fd1\uff0c\u7adf\u662f\u94c1\u955c\u5fc3\u7684\u5e08\u7236\u77f3\u60ca\u6d9b\uff01 \u3000\u3000\u94c1\u955c\u5fc3\u5fc3\u5934\u549a\u549a\u6253\u9f13\uff0c\u8fce\u4e0a\u53bb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u8fd8\u6ca1\u7761\u4e48\uff1f\u201d\u77f3\u60ca\u6d9b\u5f39\u5251\u7b11\u9053\uff1a\u201c\u4eca\u65e5\u4e00 \u6218\uff0c\u5927\u5feb\u5e73\u751f\uff01\u6211\u9ad8\u5174\u5f97\u7761\u4e0d\u7740\uff0c\u54b1\u4eec\u5e08\u5f92\u4e5f\u6709\u4e09\u5e74\u6ca1\u89c1\u5566\uff0c\u4eca\u5929\u767d\u5929\u6ca1\u7a7a\u548c\u4f60\u8bf4\u8bdd\uff0c\u7279\u6765 \u770b\u4f60\uff0c\u539f\u6765\u4f60\u4e5f\u6ca1\u7761\uff0c\u55ef\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u795e\u8272\u53ef\u4e0d\u5927\u597d\uff0c\u662f\u4e0d\u662f\u767d\u5929\u82e6\u6597\u4e00\u5929\uff0c\u592a\u8fc7\u7d2f\u5566\uff1f\u201d \u94c1\u955c\u5fc3\u5f20\u60f6\u5931\u63aa\uff0c\u9053\uff1a\u201c\u662f\uff0c\u662f\u6709\u70b9\u7d2f\uff0c\u4e0d\u7d27\u8981\u3002\u5e08\u7236\uff0c\u4f60\u8fd9\u53e3\u5251\u53ef\u771f\u662f\u628a\u5b9d\u5251\u554a\uff01\u201d \u3000\u3000\u77f3\u60ca\u6d9b\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u559c\u6b22\u8fd9\u628a\u5251\uff1f\u4f60\u7684\u5251\u672f\u5927\u6709\u8fdb\u5883\uff0c\u6587\u7ea8\u548c\u6d77\u5c71\u7684\u8d44\u8d28\u53ef\u5dee\u5f97 \u591a\uff0c\u54c8\uff0c\u60f3\u4e0d\u5230\u6211\u77f3\u5bb6\u7684\u5251\u6cd5\uff0c\u5012\u8ba9\u5916\u59d3\u4e4b\u4eba\u5f97\u4e86\u771f\u4f20\uff01\u201d\u987f\u4e86\u4e00\u987f\u53c8\u9053\uff1a\u201c\u8fd9\u4e24\u5e74\u6765\uff0c\u6211 \u53c8\u609f\u4e86\u8bb8\u591a\u5947\u5999\u7684\u53d8\u62db\uff0c\u660e\u513f\u6709\u7a7a\uff0c\u4e00\u80a1\u8111\u513f\u90fd\u4f20\u6388\u7ed9\u4f60\uff0c\u8ba9\u4f60\u7ee7\u627f\u6211\u7684\u8863\u94b5\u3002\u201d\u77f3\u60ca\u6d9b\u4e09 \u4e2a\u5f92\u5f1f\uff0c\u8fde\u5973\u513f\u5728\u5185\uff0c\u4ed6\u6700\u6b22\u559c\u7684\u5374\u662f\u94c1\u955c\u5fc3\uff0c\u8fc7\u53bb\u4ed6\u56e0\u4e3a\u94c1\u955c\u5fc3\u662f\u5b98\u5bb6\u5b50\u5f1f\uff0c\u8eab\u4e16\u548c\u5fc3\u4e8b \u4e00\u76f4\u4e0d\u6562\u5411\u4ed6\u900f\u9732\uff0c\u800c\u4eca\u89c1\u4ed6\u53c2\u52a0\u4e86\u6297\u502d\u7684\u4e49\u519b\uff0c\u8fde\u53f6\u5b97\u7559\u4e5f\u8d5e\u8d4f\u4ed6\uff0c\u81ea\u89c9\u8001\u773c\u660f\u82b1\uff0c\u6536\u4e86 \u4e2a\u597d\u5f92\u5f1f\uff0c\u4ed6\u7684\u9632\u5907\u4e4b\u5fc3\u5c3d\u90fd\u6d88\u6563\uff0c\u7b80\u76f4\u662f\u5c06\u4ed6\u5f53\u4f5c\u513f\u5b50\u770b\u5f85\u4e86\u3002\u4ed6\u4eca\u665a\u6b64\u6765\uff0c\u5c31\u662f\u51c6\u5907\u5c06 \u81ea\u5df1\u6700\u5fc3\u7231\u7684\u5192\u4e86\u6027\u547d\u5371\u9669\u5f97\u6765\u7684\u5b9d\u5251\u4f20\u6388\u7ed9\u4ed6\uff0c\u5e76\u7acb\u4ed6\u4e3a\u638c\u95e8\u5f1f\u5b50\u7684\u3002 \u3000\u3000\u82e5\u7136\u662f\u5728\u5f80\u65e5\uff0c\u94c1\u955c\u5fc3\u542c\u5f97\u5e08\u7236\u8981\u628a\u65b0\u5947\u7684\u5251\u6cd5\u4e00\u822c\u8111\u513f\u90fd\u4f20\u6388\u7ed9\u4ed6\uff0c\u5fc5\u5b9a\u5927\u559c\u62dc\u8c22\uff0c \u800c\u4eca\u542c\u6765\uff0c\u5374\u5982\u8292\u523a\u5728\u80cc\uff0c\u66f4\u4e3a\u60ed\u6127\u4e0d\u5b89\u3002\u77f3\u60ca\u6d9b\u89c1\u4ed6\u4e00\u526f\u5931\u9b42\u843d\u9b44\u7684\u6837\u5b50\uff0c\u5927\u4e3a\u60ca\u8be7\uff0c\u67d4 \u58f0\u95ee\u9053\uff1a\u201c\u4f60\u4e0d\u8212\u670d\u4e48\uff1f\u201d \u3000\u3000\u94c1\u955c\u5fc3\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u8fd9\u53e3\u5251\u662f\u4ece\u54ea\u513f\u6765\u7684\uff1f\u201d\u77f3\u60ca\u6d9b\u5fc3\u4e2d\u4e00\u51db\uff0c\u9053\uff1a\u201c\u4f60\u95ee\u8fd9 \u4e2a\u5e72\u4ec0\u4e48\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u6ca1\uff0c\u6ca1\u4ec0\u4e48\uff01\u201d\u77f3\u60ca\u6d9b\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u8c01\u6559\u4f60\u95ee\u7684\uff1f\u201d\u94c1\u955c\u5fc3 \u9053\uff1a\u201c\u6ca1\uff0c\u6ca1\u4eba\u6559\u6211\uff0c\u662f\u6211\u81ea\u5df1\u95ee\u7684\u3002\u201d\u77f3\u60ca\u6d9b\u76ef\u4e86\u94c1\u955c\u5fc3\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u62dc\u5e08\u4e4b\u65f6\uff0c\u66fe\u8bf4 \u8fc7\u4ec0\u4e48\u4e8b\u90fd\u542c\u5e08\u7236\u7684\u8bdd\uff0c\u8fd8\u8bb0\u5f97\u4e48\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u8bb0\u5f97\u3002\u201d\u77f3\u60ca\u6d9b\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u4f55\u4ee5\u8981 \u7792\u9a97\u5e08\u7236\uff1f\u4e3a\u4ec0\u4e48\u4f60\u8981\u95ee\u6211\u8fd9\u628a\u5b9d\u5251\uff1f\u201d \u3000\u3000\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6055\u5f1f\u5b50\u6597\u80c6\uff0c\u4f60\u8fd9\u53e3\u5b9d\u5251\u662f\u4e0d\u662f\u4ece\u7687\u5bab\u5927\u5185\u5077\u6765\u7684\uff1f\u201d\u77f3\u60ca\u6d9b\u9053\uff1a \u201c\u4e0d\u9519\uff01\u662f\u5077\u6765\u7684\uff01\u5982\u6b64\u795e\u7269\u5229\u5668\u85cf\u5728\u5bab\u4e2d\u4e43\u662f\u66b4\u6b84\u5929\u7269\uff0c\u6211\u62ff\u6765\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d\u94c1\u955c\u5fc3\u4e0d \u6562\u4f5c\u58f0\uff0c\u77f3\u60ca\u6d9b\u53cc\u6307\u4e00\u5f39\uff0c\u5b9d\u5251\u4e4b\u58f0\u6709\u5982\u9f99\u541f\u864e\u5578\uff0c\u77f3\u60ca\u6d9b\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u4e3a\u8fd9\u53e3\u5251\u6211\u4ea1\u547d\u56db \u65b9\uff0c\u4ece\u65e0\u540e\u6094\uff01\u201d\u58f0\u97f3\u4e00\u8f6c\uff0c\u53c8\u76ef\u7740\u94c1\u955c\u5fc3\u95ee\u9053\uff1a\u201c\u5feb\u8bf4\uff0c\u662f\u8c01\u6559\u4f60\u95ee\u7684\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a \u201c\u662f\u5fa1\u6797\u519b\u7edf\u9886\u5a04\u6850\u5b59\u6559\u6211\u95ee\u7684\u3002\u201d\u77f3\u60ca\u6d9b\u9053\uff1a\u201c\u4ed6\u5728\u54ea\u513f\uff1f\u53eb\u4ed6\u524d\u6765\u95ee\u6211\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a \u201c\u662f\u4ed6\u903c\u5bb6\u7236\uff0c\u8981\u5bb6\u7236\u903c\u6211\u62ff\u4f60\u3002\u201d\u77f3\u60ca\u6d9b\u51b7\u7b11\u9053\uff1a\u201c\u62ff\u6211\uff1f\u201d\u5ffd\u5730\u9192\u609f\uff0c\u9053\uff1a\u201c\u662f\u4e86\uff0c\u4f60 \u82e5\u4e0d\u80af\u52a9\u4ed6\u62ff\u6211\uff0c\u4ed6\u5c31\u8981\u5bf9\u4f60\u7236\u4eb2\u4e0d\u5229\uff0c\u662f\u8fd9\u6837\u4e48\uff01\u201d\u94c1\u955c\u5fc3\u54ed\u51fa\u58f0\u6765\uff0c\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u7236 \u4eb2\u73b0\u5728\u5df2\u88ab\u8f6f\u7981\u5728\u5de1\u629a\u8859\u95e8\u4e86\u3002\u201d\u77f3\u60ca\u6d9b\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u5e08\u5f92\u4e00\u573a\uff0c\u4f60\u8bf4\u5b9e\u8bdd\uff0c\u4f60\u5fc3\u4e2d\u6253\u7b97 \u5982\u4f55\uff1f\u662f\u4e0d\u662f\u60f3\u62ff\u6211\u7684\u9888\u8840\u53bb\u67d3\u7ea2\u4f60\u7236\u4eb2\u9876\u4e0a\u7684\u4e4c\u7eb1\uff1f\u201d \u3000\u3000\u94c1\u955c\u5fc3\u54ed\u9053\uff1a\u201c\u5f1f\u5b50\u4e0d\u6562\uff01\u201d\u77f3\u60ca\u6d9b\u9053\uff1a\u201c\u7537\u5b50\u6c49\u6d41\u8840\u4e0d\u6d41\u6cea\uff0c\u6211\u77f3\u60ca\u6d9b\u65e2\u6562\u5927\u95f9\u7687 \u5bab\uff0c\u5929\u584c\u4e0b\u6765\u6211\u4e5f\u4e0d\u6015\uff0c\u54ed\u4e9b\u4ec0\u4e48\uff1f\u4ec0\u4e48\u6562\u4e0d\u6562\u7684\uff1f\u4f60\u5feb\u8bf4\uff0c\u4f60\u5230\u5e95\u662f\u6253\u4ec0\u4e48\u4e3b\u610f\uff1f\u201d\u94c1\u955c \u5fc3\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u7684\u6b66\u529f\u73b0\u5728\u5df2\u7ec3\u81f3\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u4e0e\u4f60\u53ef\u4ee5\u5e76\u80a9\u76f8\u6bd4\u7684\u5f53\u4e16\u6ca1\u6709\u51e0\u4eba\uff0c\u4f60 \u5df2\u65e0\u987b\u4e00\u628a\u5b9d\u5251\uff0c\u5e08\u7236\uff0c\u4f60\u4f55\u82e6\u4e3a\u4e86\u4e00\u628a\u5b9d\u5251\u62c5\u4e86\u4e2a\u53db\u9006\u7684\u7f6a\u540d\uff01\u201d\u58f0\u6cea\u4ff1\u4e0b\u5730\u529d\u8bf4\uff0c\u77f3\u60ca \u6d9b\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u6211\u4e0d\u662f\u5916\u4eba\uff0c\u4e0d\u5fc5\u591a\u4e0b\u8bf4\u8bcd\uff0c\u4f9d\u4f60\u8bf4\uff0c\u6211\u8be5\u5982\u4f55\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u5e08\u7236\u4e0d \u5982\u5c06\u8fd9\u628a\u5b9d\u5251\u7ed9\u6211\uff0c\u8ba9\u6211\u4ea4\u56de\u5927\u5185\uff0c\u8bf7\u6c42\u7687\u4e0a\u9500\u4e86\u8fd9\u573a\u516c\u6848\uff0c\u5c82\u4e0d\u662f\u4e24\u5168\u5176\u7f8e\uff1f\u201d \u3000\u3000\u77f3\u60ca\u6d9b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\u4e3b\u610f\uff01\u201d\u8fd9\u5239\u90a3\u95f4\uff0c\u4ed6\u4f24\u5fc3\u5230\u4e86\u6781\u70b9\u3002\u4ed6\u672c\u6765\u5c31\u51c6\u5907\u5c06\u8fd9\u628a \u5251\u9001\u7ed9\u94c1\u955c\u5fc3\uff0c\u5374\u60f3\u4e0d\u5230\u7531\u94c1\u955c\u5fc3\u5148\u8bf4\u51fa\u6765\uff0c\u66f4\u60f3\u4e0d\u5230\u7684\u662f\u94c1\u955c\u5fc3\u628a\u4ed6\u7684\u884c\u4e3a\u5f53\u4f5c\u201c\u53db \u9006\u201d\uff0c\u7adf\u6562\u8981\u6c42\u4ed6\u7f34\u5251\u6c42\u5168\uff0c\u8fd9\u5b9e\u662f\u72af\u4e86\u6b66\u6797\u7684\u5927\u5fcc\uff0c\u4ed6\u672c\u6765\u6253\u7b97\u53bb\u6551\u94c1\u94f1\u7684\uff0c\u7136\u540e\u5e26\u94c1\u955c \u5fc3\u7236\u5b50\u4e00\u540c\u8fdc\u8d70\u9ad8\u98de\uff0c\u5374\u60f3\u4e0d\u5230\u94c1\u955c\u5fc3\u66ff\u4ed6\u51fa\u4e86\u8fd9\u4e2a\u4e3b\u610f\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u6014\u6014\u5730\u671b\u7740\u5e08\u7236\uff0c\u5e08\u7236\u597d\u4f3c\u7a81\u7136\u95f4\u6362\u4e86\u4e2a\u4eba\uff0c\u9762\u4e0a\u4e00\u6d3e\u6f20\u7136\u7684\u795e\u8272\uff0c\u597d\u50cf\u4e0d\u8ba4\u8bc6 \u81ea\u5df1\u4f3c\u7684\uff0c\u94c1\u955c\u5fc3\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u7236\u2026\u2026\u201d\u77f3\u60ca\u6d9b\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4f60\u7684\u5e08\u7236\uff01\u201d\u58f0\u97f3\u5e73 \u9759\uff0c\u5185\u4e2d\u5374\u542b\u6709\u65e0\u9650\u7684\u6124\u6fc0\uff0c\u94c1\u955c\u5fc3\u60ca\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u2014\u2014\u201d\u77f3\u60ca\u6d9b\u9053\uff1a\u201c\u7f57\u55e6\u4ec0\u4e48\uff1f\u5b9d\u5251 \u62ff\u53bb\uff01\u201d\u5012\u6301\u5251\u67c4\uff0c\u5c06\u5b9d\u5251\u9001\u5230\u4e86\u94c1\u955c\u5fc3\u7684\u9762\u524d\uff0c\u4e00\u654c\u7cbe\u5149\uff0c\u8000\u4eba\u773c\u76ee\uff0c\u94c1\u955c\u5fc3\u832b\u7136\u65e0\u63aa\uff0c \u4e0d\u6562\u4f38\u624b\u53bb\u63a5\uff0c\u77f3\u60ca\u6d9b\u9053\uff1a\u201c\u62ff\u53bb\u5440\uff0c\u8ba9\u4f60\u505a\u4e2a\u5fe0\u5b5d\u4e24\u5168\u7684\u4eba\uff0c\u600e\u4e48\u8fd8\u4e0d\u62ff\u53bb\uff1f\u201d\u94c1\u955c\u5fc3\u54c6 \u54c6\u55e6\u55e6\u4e3e\u8d77\u4e86\u4e00\u53ea\u624b\uff0c\u77f3\u60ca\u6d9b\u9053\uff1a\u201c\u5b9d\u5251\u7ed9\u4f60\uff0c\u6211\u6559\u4f60\u7684\u6b66\u529f\u4f60\u4e5f\u8fd8\u56de\u7ed9\u6211\uff01\u201d\u8981\u77e5\u5929\u4e0b\u6ca1 \u6709\u5e08\u7236\u5411\u5f92\u5f1f\u201c\u7f34\u68b0\u201d\u4e4b\u7406\uff0c\u94c1\u955c\u5fc3\u8fd9\u624d\u77e5\u9053\uff0c\u77f3\u60ca\u6d9b\u8bf4\u4ece\u6b64\u4e0d\u518d\u662f\u4ed6\u7684\u5e08\u7236\uff0c\u539f\u6765\u662f\u8fd9\u4e2a \u610f\u601d\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u6cea\u6d41\u6ee1\u9762\uff0c\u545c\u54bd\u8bf4\u9053\uff1a\u201c\u5f92\u5f1f\u4e0d\u8096\uff0c\u5e05\u7236\u8d23\u7f5a\uff0c\u7f6a\u6709\u5e94\u5f97\uff0c\u4f46\u6c42\u5e08\u7236\u4e0d\u8981\u5c06\u5f1f\u5b50 \u9010\u51fa\u95e8\u5899\uff01\u201d\u77f3\u60ca\u6d9b\u9762\u5b54\u94c1\u9752\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6211\u54ea\u6709\u798f\u6c14\u6536\u8fd9\u6837\u597d\u7684\u5f92\u5f1f\uff1f\u6211\u6559\u4f60\u7684 \u90a3\u4e00\u70b9\u5fae\u672a\u4e4b\u6280\uff0c\u8c05\u4f60\u4e5f\u4e0d\u5728\u4e4e\uff0c\u6211\u5c06\u4f60\u7684\u6b66\u529f\u6536\u56de\uff0c\u4ece\u4eca\u540e\u54b1\u4eec\u5404\u8d70\u5404\u7684\uff0c\u8fd9\u628a\u5251\u4f60\u62ff\u53bb \u732e\u7ed9\u7687\u4e0a\uff0c\u7b97\u662f\u6211\u6700\u540e\u9001\u7ed9\u4f60\u7684\u4e1c\u897f\u3002\u6211\u5e73\u751f\u8bf4\u4e00\u4e0d\u4e8c\uff0c\u8fd9\u628a\u5251\u4e3a\u4f55\u8fd8\u4e0d\u62ff\u53bb\uff1f\u201d\u94c1\u955c\u5fc3\u6b64 \u65f6\u5fc3\u4e2d\u60b2\u82e6\u4e4b\u6781\uff0c\u82e5\u662f\u4e0d\u63a5\u6b64\u5251\uff0c\u5b5d\u9053\u96be\u4ee5\u4fdd\u5168\uff0c\u7eb5\u4e0d\u6284\u5bb6\uff0c\u7236\u4eb2\u4e5f\u8981\u53d7\u7f81\u7f06\u4e4b\u8fb1\uff1b\u82e5\u63a5\u6b64 \u5251\uff0c\u5219\u5e08\u5f92\u4e4b\u4e49\u65ad\u7edd\uff0c\u81ea\u5df1\u7684\u4e00\u8eab\u6b66\u529f\u4e5f\u5c06\u5316\u4e3a\u4e4c\u6709\u3002\u8111\u4e2d\u4e0d\u89c9\u53c8\u6d6e\u8d77\u90a3\u4e2a\u88ab\u5e08\u7236\u5e9f\u4e86\u6b66\u529f \u7684\u62a4\u9662\u6b66\u5e08\u7684\u60e8\u72b6\uff0c\u4e0d\u7981\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\u3002\u53f3\u60ca\u6d9b\u559d\u9053\uff1a\u201c\u4eba\u8d35\u5f53\u673a\u7acb\u65ad\uff0c\u4f60\u600e\u7684\u8fd9\u6837\u7f20\u5939\u4e0d \u6e05\uff1f\u5b9d\u5251\u62ff\u53bb\uff0c\u6b66\u529f\u8fd8\u6765\uff0c\u6211\u6709\u534a\u70b9\u4e8f\u5f85\u4f60\u4e48\uff1f\u201d\u53f3\u624b\u6301\u5251\u5728\u94c1\u955c\u5fc3\u7684\u9762\u524d\u6643\u52a8\uff0c\u5de6\u638c\u626c \u8d77\uff0c\u53ea\u5f85\u94c1\u955c\u5fc3\u63a5\u5251\uff0c\u4ed6\u5c31\u8981\u4e00\u638c\u62cd\u4e0b\uff0c\u628a\u94c1\u955c\u5fc3\u53d8\u6210\u5e9f\u4eba\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5728\u6811\u4e0a\u542c\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u5c3d\u7ba1\u5979\u5bf9\u94c1\u955c\u5fc3\u5e76\u4e0d\u540c\u60c5\u3002\u4f46\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u613f\u89c1\u4ed6\u7684\u6b66\u529f \u5316\u4e3a\u4e4c\u6709\uff0c\u6de1\u6de1\u7684\u6708\u5149\u900f\u8fc7\u7e41\u679d\u8302\u53f6\uff0c\u4e8e\u627f\u73e0\u9690\u7ea6\u770b\u89c1\u77f3\u60ca\u6d9b\u7684\u624b\u6b63\u7f13\u7f13\u5411\u94c1\u955c\u5fc3\u9876\u5934\u62cd \u4e0b\uff0c\u4e8e\u627f\u73e0\u5413\u5f97\u51e0\u4e4e\u53eb\u51fa\u58f0\u6765\uff0c\u8fd9\u5239\u90a3\u95f4\u5979\u547c\u5438\u90fd\u505c\u6b62\u4e86\uff0c\u53ea\u89c9\u4e00\u9635\u6655\u7729\uff0c\u4e0d\u81ea\u89c9\u5730\u628a\u773c\u775b \u95ed\u4e86\u8d77\u6765\u3002\u5ffd\u542c\u5f97\u77f3\u60ca\u6d9b\u4e00\u58f0\u957f\u53f9\uff0c\u4e8e\u627f\u73e0\u7684\u5fc3\u731b\u5730\u4e00\u8df3\uff0c\u968f\u5373\u542c\u5f97\u545b\u5f53\u4e00\u58f0\uff0c\u90a3\u662f\u5b9d\u5251\u8dcc \u843d\u5730\u4e0a\u7684\u58f0\u97f3\uff0c\u4e8e\u627f\u73e0\u7741\u773c\u770b\u65f6\uff0c\u77f3\u60ca\u6d9b\u7684\u5f71\u5b50\u5df2\u7ecf\u4e0d\u89c1\uff0c\u94c1\u955c\u5fc3\u4e00\u526f\u5931\u9b42\u843d\u9b44\u7684\u6837\u5b50\uff0c\u5446 \u82e5\u6728\u9e21\u822c\u7ad9\u5728\u6811\u4e0b\uff0c\u90a3\u628a\u5b9d\u5251\u5c31\u63d2\u5728\u4ed6\u7684\u811a\u8fb9\uff0c\u4e8e\u627f\u73e0\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u9192\u609f\uff0c\u77f3\u60ca\u6d9b\u987e\u5ff5\u5e08 \u5f92\u4e4b\u60c5\uff0c\u6bd5\u7adf\u4e0b\u4e0d\u4e86\u624b\uff0c\u60f3\u8d77\u4ed6\u63b7\u5251\u4e4b\u65f6\u7684\u4e00\u58f0\u957f\u53f9\uff0c\u5fc3\u4e2d\u6b63\u4e0d\u77e5\u5145\u6ee1\u4f55\u7b49\u7edd\u671b\u4e0e\u51c4\u82e6\u7684\u5fc3 \u60c5\uff1f \u3000\u3000\u6797\u5b50\u91cc\u4e00\u7247\u9759\u5bc2\uff0c\u826f\u4e45\u826f\u4e45\uff01\u624d\u89c1\u94c1\u955c\u5fc3\u5f2f\u8170\u62fe\u8d77\u90a3\u628a\u5b9d\u5251\uff0c\u4e8e\u627f\u73e0\u8fd9\u65f6\u5fc3\u60c5\u4e5f\u662f\u590d\u6742 \u4e4b\u6781\uff0c\u5bf9\u94c1\u955c\u5fc3\u4f3c\u662f\u6709\u70b9\u618e\u6076\uff0c\u53c8\u4f3c\u6709\u70b9\u601c\u60af\uff0c\u5bf9\u4ed6\u4f3c\u662f\u76f8\u5f53\u719f\u6089\uff0c\u5374\u53c8\u90a3\u6837\u964c\u751f\u3002 \u3000\u3000\u5ffd\u89c1\u6797\u5b50\u5916\u8fb9\u4eba\u5f71\u4e00\u95ea\uff0c\u94c1\u955c\u5fc3\u62ac\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u8001\u5bb6\u4eba\u738b\u5b89\u966a\u7740\u4e00\u4e2a\u56db\u5341\u591a\u5c81\u7684\u5927\u6c49\u8d70 \u6765\uff0c\u8fd9\u4eba\u7a7f\u7684\u4e5f\u662f\u53f0\u5dde\u56e2\u7ec3\u7684\u670d\u9970\uff0c\u8138\u4e0a\u5806\u7740\u72e1\u733e\u7684\u7b11\u5bb9\uff0c\u76ef\u7740\u81ea\u5df1\u624b\u4e0a\u7684\u5b9d\u5251\uff0c\u94c1\u955c\u5fc3\u8ba4 \u4e0d\u5f97\u8fd9\u4eba\uff0c\u4e8e\u627f\u73e0\u53ef\u662f\u5927\u5403\u4e00\u60ca\uff0c\u6b64\u4eba\u975e\u4ed6\uff0c\u6b63\u662f\u66fe\u548c\u5979\u4ea4\u8fc7\u624b\u7684\u5fa1\u6797\u519b\u7edf\u9886\u5a04\u6850\u5b59\uff01 \u3000\u3000\u5a04\u6850\u5b59\u7b11\u563b\u563b\u5730\u8d70\u5230\u94c1\u955c\u5fc3\u8ddf\u524d\uff0c\u4f38\u624b\u5728\u4ed6\u80a9\u5934\u4e00\u62cd\uff0c\u9053\uff1a\u201c\u94c1\u516c\u5b50\uff0c\u5f97\u624b\u4e86\u4e48\uff1f\u600e\u4e48 \u8ba9\u90a3\u8001\u8d3c\u8dd1\u4e86\uff1f\u201d\u94c1\u955c\u5fc3\u7741\u773c\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u738b\u5b89\u9053\uff1a\u201c\u6211\u89c1\u77f3\u8001\u5e08\u521a\u624d\u8d70\u6765\uff0c\u6015\u516c\u5b50 \u9047\u9669\uff0c\u6240\u4ee5\u8bf7\u5a04\u5927\u4eba\u524d\u6765\uff0c\u5a04\u5927\u4eba\u672c\u6765\u662f\u548c\u6211\u4e00\u9053\u4ece\u53f0\u5dde\u6765\u7684\uff0c\u6055\u8001\u5974\u672a\u66fe\u7980\u544a\u3002\u77f3\u8001\u5e08\u548c \u516c\u5b50\u95f9\u7ffb\u4e86\u4e48\uff1f\u6ca1\u6709\u52a8\u624b\u5427\uff1f\u201d\u94c1\u955c\u5fc3\u5927\u5403\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u662f\u5a04\u6850\u5b59\uff1f\u201d\u5a04\u6850\u5b59\u7b11\u9053\uff1a\u201c\u6b63 \u662f\u533a\u533a\u3002\u201d\u94c1\u955c\u5fc3\u624b\u81c2\u4e00\u632f\uff0c\u957f\u5251\u8131\u624b\u98de\u51fa\uff0c\u9053\uff1a\u201c\u5b9d\u5251\u62ff\u53bb\uff0c\u4ece\u4eca\u540e\u4f11\u6765\u89c1\u6211\uff01\u201d\u5a04\u6850\u5b59 \u8f7b\u8f7b\u4e00\u95ea\uff0c\u6293\u7740\u5251\u67c4\uff0c\u968f\u624b\u4e00\u6325\uff0c\u5494\u5693\u4e00\u58f0\uff0c\u628a\u4e00\u682a\u6811\u679d\u524a\u65ad\uff0c\u5567\u5567\u8d5e\u9053\uff1a\u201c\u679c\u7136\u662f\u5927\u5185\u5b9d \u5251\uff01\u54c8\uff0c\u94c1\u516c\u5b50\uff0c\u4f60\u8fd9\u4ef6\u529f\u52b3\u53ef\u4e0d\u5c0f\u554a\uff01\u201d\u94c1\u955c\u5fc3\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u5b9d\u5251\u5230\u624b\uff0c\u8fd8\u4e0d\u5feb\u8d70\uff1f\u201d\u5a04 \u6850\u5b59\u7b11\u9053\uff1a\u201c\u5b9d\u5251\u662f\u6709\u4e86\uff0c\u94a6\u72af\u53ef\u8fd8\u6ca1\u6709\u5c31\u64d2\uff0c\u94c1\u516c\u5b50\uff0c\u4f60\u4e3a\u4eba\u4e3a\u5230\u5e95\uff0c\u9001\u4f5b\u4e0a\u897f\u5929\uff01\u201d\u94c1 \u955c\u5fc3\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u5a04\u6850\u5b59\u9053\uff1a\u201c\u5927\u4e49\u706d\u4eb2\uff0c\u4f55\u51b5\u53ea\u662f\u5e08\u5f92\uff0c\u77f3\u8001\u8d3c\u5931\u4e86\u5b9d\u5251\uff0c\u51ed\u4f60\u6211\u4e8c\u4eba \u4e4b\u529b\uff0c\u5927\u7ea6\u53ef\u4ee5\u5bf9\u4ed8\u4ed6\u4e86\uff0c\u54c8\u54c8\uff01\u201d\u7b11\u58f0\u672a\u5df2\uff0c\u5ffd\u89c1\u94c1\u955c\u5fc3\u53cc\u773c\u6012\u51f8\uff0c\u77b3\u4ec1\u4e2d\u4f3c\u8981\u55b7\u51fa\u706b \u6765\uff0c\u5a04\u6850\u5b59\u5fc3\u5934\u4e00\u9707\uff0c\u5374\u5ffd\u5730\u597d\u7b11\u9053\uff1a\u201c\u5c0a\u5927\u4eba\u5728\u5de1\u629a\u8859\u95e8\u65e5\u5915\u76fc\u671b\u516c\u5b50\uff0c\u6709\u4ec0\u4e48\u4e8b\u60c5\u4ee4\u516c \u5b50\u5982\u6b64\u751f\u6c14\uff0c\u6c14\u574f\u4e86\u8eab\u5b50\uff0c\u8001\u5927\u4eba\u4e5f\u5fc3\u75bc\u554a\uff01\u201d\u94c1\u955c\u5fc3\u731b\u5730\u60f3\u8d77\u7236\u4eb2\u8fd8\u5728\u4ed6\u4eec\u624b\u4e2d\uff0c\u5fc3\u5934\u4e00 \u6c89\uff0c\u84c4\u52b2\u5f85\u53d1\u7684\u4e00\u638c\u7adf\u7136\u53d1\u4e0d\u51fa\u53bb\u3002\u5a04\u6850\u5b59\u53c8\u563b\u563b\u7b11\u9053\uff1a\u201c\u94c1\u516c\u5b50\u662f\u806a\u660e\u4eba\uff0c\u82e5\u7136\u518d\u7acb\u4e00\u4ef6 \u5927\u529f\uff0c\u4eca\u540e\u4e00\u751f\u7684\u529f\u540d\u5229\u7984\uff0c\u90a3\u662f\u4e0d\u7528\u6101\u4e86\u3002\u201d \u3000\u3000\u5a04\u6850\u5b59\u6b63\u62df\u5a01\u80c1\u5229\u8bf1\uff0c\u518d\u4e0b\u8bf4\u8f9e\uff0c\u5ffd\u89c1\u94c1\u955c\u5fc3\u9762\u8272\u5927\u53d8\uff0c\u7a81\u7136\u6376\u80f8\u5927\u53eb\u9053\uff1a\u201c\u5929\u5566\uff0c\u6211 \u505a\u4e86\u4ec0\u4e48\u9519\u4e8b\uff0c\u7ed9\u4eba\u5f53\u4f5c\u65e0\u803b\u5c0f\u4eba\uff01\u201d\u5a04\u6850\u5b59\u5413\u4e86\u4e00\u8df3\uff0c\u94c1\u955c\u5fc3\u559d\u9053\uff0c\u201c\u6211\u82e5\u8981\u6c42\u53d6\u529f\u540d\u5229 \u7984\uff0c\u6211\u4f55\u4e0d\u81ea\u5df1\u62ff\u4e86\u8fd9\u67c4\u5b9d\u5251\uff0c\u5165\u4eac\u9762\u5723\uff0c\u4f60\u518d\u6562\u80e1\u8a00\u4e71\u8bed\uff0c\u6211\u5c31\u62fc\u4e2a\u8eab\u6b7b\u540d\u706d\uff0c\u505a\u4e2a\u4e0d\u5fe0 \u4e0d\u5b5d\u4e4b\u4eba\uff01\u201d\u5a04\u6850\u5b59\u9053\uff1a\u201c\u5582\uff0c\u6709\u8bdd\u597d\u8bf4\uff0c\u4f60\u5927\u53eb\u5927\u56b7\u505a\u4ec0\u4e48\uff1f\u201d\u94c1\u955c\u5fc3\u80f8\u4e2d\u90a3\u6068\u96be\u582a\uff0c\u5728 \u5a04\u606b\u5b59\u4e00\u903c\u518d\u903c\u4e4b\u4e0b\uff0c\u5ffd\u5982\u706b\u5c71\u7206\u53d1\uff0c\u773c\u6cea\u7c0c\u7c0c\u800c\u4e0b\uff0c\u5bf9\u5a04\u6850\u5b59\u7684\u8bdd\u6beb\u4e0d\u7406\u4f1a\uff0c\u53c8\u5927\u58f0\u53eb \u9053\uff1a\u201c\u77f3\u8001\u5e08\u554a\u77f3\u8001\u5e08\uff0c\u4ec0\u4e48\u65f6\u5019\uff0c\u6211\u518d\u80fd\u89c1\u4f60\u8868\u660e\u5fc3\u8ff9\uff1f\u201d\u5a04\u6850\u5b59\u9762\u8272\u94c1\u9752\uff0c\u6068\u4e0d\u5f97\u4e00\u624b \u627c\u7740\u94c1\u955c\u5fc3\u7684\u5589\u5499\uff0c\u4f46\u4ed6\u4e5f\u77e5\u9053\u94c1\u955c\u5fc3\u6b66\u827a\u975e\u51e1\uff0c\u81ea\u5df1\u7eb5\u80fd\u80dc\u4ed6\uff0c\u4ea6\u975e\u4e09\u4e94\u5341\u62db\u4e0d\u53ef\uff0c\u800c\u4e14 \u4e49\u519b\u4e2d\u9ad8\u624b\u5982\u4e91\uff0c\u4e00\u52a8\u624b\u60ca\u52a8\u4f17\u4eba\uff0c\u53ea\u6015\u81ea\u5df1\u96be\u4ee5\u8d70\u8131\uff0c\u597d\u5728\u5b9d\u5251\u5df2\u7ecf\u5230\u624b\uff0c\u867d\u7136\u672a\u83b7\u94a6 \u72af\uff0c\u4e5f\u53ef\u4ee5\u4ea4\u5dee\u4e86\u3002 \u3000\u3000\u738b\u5b89\u4ece\u672a\u89c1\u8fc7\u5c11\u7237\u5982\u6b64\u96be\u8fc7\uff0c\u5fc3\u4e2d\u751a\u662f\u4e0d\u5b89\uff0c\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u548c\u6211\u4e00\u540c\u56de\u53bb\u89c1\u8001 \u5927\u4eba\u5427\uff0c\u65e9\u65e9\u79bb\u5f00\u8fd9\u662f\u975e\u4e4b\u5730\u3002\u201d\u94c1\u955c\u5fc3\u5927\u543c\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4e5f\u7ed9\u6211\u6eda\uff0c\u4ece\u4eca\u540e\u4f11\u518d\u89c1 \u6211\uff01\u201d\u5ffd\u5730\u6376\u80f8\u75db\u54ed\u8d77\u6765\uff0c\u738b\u5b89\u624b\u8db3\u65e0\u63aa\uff0c\u5a04\u6850\u5b59\u5fd9\u9053\uff1a\u201c\u4f60\u5bb6\u516c\u5b50\u5df2\u7ecf\u75af\u5566\uff0c\u54b1\u4eec\u5feb \u8d70\u3002\u201d\u4ed6\u4e00\u6015\u94c1\u955c\u5fc3\u60ca\u52a8\u4f17\u4eba\uff0c\u4e8c\u6015\u738b\u5b89\u88ab\u4e49\u519b\u64d2\u83b7\uff0c\u95ee\u51fa\u771f\u76f8\uff0c\u6025\u5fd9\u62c9\u4e86\u738b\u5b89\u98de\u9003\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u54ed\u4e86\u4e00\u9635\uff0c\u6e10\u6e10\u6c14\u8870\u529b\u7aed\uff0c\u8fd9\u4e00\u573a\u5185\u5fc3\u7684\u4ea4\u6218\uff0c\u6bd4\u8d77\u4ed6\u5bf9\u516b\u6bb5\u9ad8\u624b\uff0c\u8fd8\u66f4\u4f24\u795e\uff0c \u7559\u4e0b\u7684\u521b\u75d5\uff0c\u90a3\u662f\u6bd5\u751f\u96be\u4ee5\u78e8\u706d\u7684\u4e86\u3002\u4e8e\u627f\u73e0\u5728\u6811\u4e0a\u4e5f\u89c9\u4e00\u7247\u4f24\u5fc3\uff0c\u4f46\u89c1\u4ed6\u9893\u7136\u5750\u5728\u5730\u4e0a\uff0c \u597d\u50cf\u4e00\u5c0a\u5931\u4e86\u77e5\u89c9\u7684\u77f3\u50cf\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u6697\u6697\u53f9\u4e86\u53e3\u6c14\uff0c\u4e0d\u77e5\u9053\u662f\u601c\u60af\u3001\u662f\u60cb\u60dc\u3001\u8fd8\u662f\u9119\u5937\u4e86\u3002\u6797\u5b50\u5916\u4f20\u6765\u5608\u6742\u7684\u4eba\u58f0\u548c \u811a\u6b65\u58f0\uff0c\u60f3\u662f\u542c\u5230\u4e86\u94c1\u955c\u5fc3\u9002\u624d\u7684\u53eb\u56b7\uff0c\u5306\u5306\u4ece\u5c71\u5be8\u91cc\u8d76\u6765\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u731b\u5730\u60f3\u8d77\u81ea\u5df1\u8981\u79bb\u5f00\u6b64\u5730\uff0c\u671d\u7740\u5730\u4e0b\u7684\u94c1\u955c\u5fc3\u518d\u77a5\u4e86\u4e00\u773c\uff0c\u811a\u5c16\u4e00\u70b9\u6811\u679d\uff0c\u7acb\u5982 \u79bb\u5f26\u7684\u7bad\uff0c\u55d6\u5730\u4e00\u4e0b\u7a9c\u51fa\u6811\u6797\uff0c\u94c1\u955c\u5fc3\u8fd9\u624d\u53d1\u89c9\u6811\u4e0a\u4f0f\u6709\u4eba\uff0c\u6781\u76ee\u770b\u65f6\uff0c\u4f9d\u7a00\u8ba4\u5f97\u4e8e\u627f\u73e0\u7684 \u80cc\u5f71\uff0c\u4e0d\u89c9\u5446\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7a9c\u51fa\u6811\u6797\uff0c\u8dd1\u4e0b\u5c71\u5c97\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u4f46\u89c1\u661f\u6708\u897f\u6c89\uff0c\u66d9\u5149\u672a\u9732\uff0c\u4f46\u5927\u6d77\u78a7\u6ce2\u4e4b\u4e0a\uff0c \u5df2\u6709\u4e09\u4e24\u53ea\u7edd\u65e9\u79bb\u5de2\u7684\u6d77\u9e25\u5728\u63a0\u6c34\u98de\u7fd4\u3002\u4e71\u77f3\u7a7f\u7a7a\uff0c\u60ca\u6d9b\u62cd\u5cb8\uff0c\u4e8e\u627f\u73e0\u7684\u5fc3\u60c5\u4e5f\u968f\u7740\u6ce2\u6d9b\u8d77 \u4f0f\uff0c\u60f3\u8d77\u521d\u6765\u4e4b\u65f6\uff0c\u5174\u9ad8\u91c7\u70c8\uff0c\u800c\u4eca\u72ec\u81ea\u79bb\u53bb\uff0c\u9eef\u7136\u795e\u4f24\u3002\u5979\u56de\u5934\u4e00\u671b\uff0c\u6d77\u98ce\u547c\u5578\uff0c\u9690\u7ea6\u4f3c \u95fb\u94c1\u955c\u5fc3\u5411\u5979\u547c\u5524\u3002\u5979\u4e0d\u77e5\u9053\u77f3\u60ca\u6d9b\u629b\u5f00\u94c1\u955c\u5fc3\u4e4b\u65f6\u5fc3\u60c5\u5982\u4f55\uff1f\u4f46\u60f3\u6765\u81ea\u5df1\u7684\u96be\u53d7\u4e5f\u4e0d\u5728\u4ed6 \u4e4b\u4e0b\u3002\u4ee5\u524d\u5979\u60f3\u8d77\u94c1\u955c\u5fc3\u65f6\uff0c\u867d\u7136\u6709\u8bb8\u591a\u4ee4\u5979\u4e0d\u80fd\u6ee1\u610f\uff0c\u4f46\u5fc3\u4e2d\u603b\u6709\u4e00\u4e1d\u751c\u871c\u7684\u611f\u89c9\uff0c\u800c\u4eca \u60f3\u8d77\u6765\u65f6\uff0c\u5374\u4f3c\u559d\u4e86\u4e00\u676f\u53d8\u4e86\u5473\u7684\u8461\u8404\u9152\uff0c\u611f\u89c9\u5f97\u6ee1\u4e0d\u662f\u5473\u513f\u3002\u5979\u968f\u624b\u6361\u8d77\u4e00\u5757\u77f3\u5934\u5411\u6d77\u4e2d \u629b\u53bb\uff0c\u597d\u50cf\u8981\u629b\u6389\u81ea\u5df1\u7684\u56de\u5fc6\uff0c\u6ce2\u6d9b\u4e00\u5377\uff0c\u77f3\u5757\u7acb\u5373\u65e0\u5f71\u65e0\u8e2a\uff0c\u5979\u7684\u5fc3\u60c5\u4e5f\u50cf\u968f\u7740\u6d77\u6d9b\u4e1c \u901d\u3002\u6b63\u662f\uff1a \u3000\u3000\u6eda\u6eda\u6d6a\u6d9b\u4e1c\u901d\u6c34\uff0c\u53ef\u601c\u6d88\u5c3d\u5973\u513f\u60c5\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff0c\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u5341\u516d\u56de\u3000\u6d77\u89d2\u98ce\u4e91\u3000\u82f1\u96c4\u593a\u5b9d\u5251\u3000\u82d7\u533a\u602a\u4e8b\u3000\u7a1a\u5b50\u4f5c\u65b0\u90ce \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u5341\u516d\u56de\u3000\u6d77\u89d2\u98ce\u4e91\u3000\u82f1\u96c4\u593a\u5b9d\u5251\u3000\u82d7\u533a\u602a\u4e8b\u3000\u7a1a\u5b50\u4f5c\u65b0\u90ce \u3000\u3000\u5929\u5c06\u7834\u6653\uff0c\u5927\u6d77\u6f6e\u751f\uff0c\u6d77\u9762\u6d8c\u8d77\u5343\u6761\u767d\u7ec3\uff0c\u9690\u9690\u95fb\u5f97\u8f70\u8f70\u9686\u9686\u4e4b\u58f0\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u6d6a\u5934\u6253 \u5230\uff0c\u51b2\u51fb\u6d77\u5824\uff0c\u5377\u8d77\u5343\u5806\u767d\u6d6a\uff0c\u6d6a\u82b1\u5982\u96e8\uff0c\u6709\u5982\u98de\u73e0\u6e85\u7389\uff0c\u6e7f\u988a\u6cbe\u8863\uff0c\u6709\u51e0\u70b9\u6e85\u5230\u4e8e\u627f\u73e0\u9762 \u4e0a\uff0c\u51b7\u6c81\u6c81\u7684\u4ee4\u4eba\u7cbe\u795e\u4e00\u723d\uff0c\u4e8e\u627f\u73e0\u4e0d\u89c9\u6717\u58f0\u5ff5\u9053\uff1a\u201c\u4e71\u77f3\u7a7f\u7a7a\uff0c\u60ca\u6d9b\u62cd\u5cb8\uff0c\u5377\u8d77\u5e72\u5806\u96ea\u3002 \u6c5f\u5c71\u5982\u753b\uff0c\u4e00\u65f6\u591a\u5c11\u8c6a\u6770\uff01\u201d\u8fd9\u662f\u82cf\u4e1c\u5761\u201c\u5927\u6c5f\u4e1c\u53bb\u201d\u7684\u540d\u53e5\uff0c\u4e8e\u627f\u73e0\u5fc3\u4e2d\u7b11\u9053\uff1a\u201c\u5927\u6c5f\u600e \u5982\u5927\u6d77\uff0c\u82cf\u4e1c\u5761\u8fd8\u6ca1\u6709\u6211\u7684\u773c\u798f\uff01\u201d\u4f46\u541f\u5230\u201c\u6d6a\u6dd8\u5c3d\u5343\u53e4\u98ce\u6d41\u4eba\u7269\uff0c\u6c5f\u5c71\u5982\u753b\uff0c\u4e00\u65f6\u591a\u5c11\u8c6a \u6770\uff01\u201d\u7b49\u53e5\uff0c\u5fc3\u4e2d\u7684\u611f\u6168\uff0c\u4e0d\u6b8a\u82cf\u8001\u5f53\u5e74\u3002\u75f4\u75f4\u60f3\u9053\uff1a\u201c\u6bd4\u5982\u5728\u4e49\u519b\u4e4b\u4e2d\uff0c\u591a\u5c11\u82f1\u8c6a\u4e4b\u58eb\uff0c \u4f46\u53c8\u6709\u8c01\u50cf\u516c\u747e\u5f53\u5e74\u7684\u98ce\u6d41\u4eba\u7269\uff1f\u914d\u5f97\u4e0a\u5927\u597d\u6c5f\u5c71\u7684\u771f\u82f1\u96c4\u3001\u5927\u8c6a\u6770\uff1f\u201d\u53ea\u89c9\u81ea\u79bb\u5f00\u5e08\u95e8\u4e4b \u540e\uff0c\u5c31\u6ca1\u6709\u9047\u8fc7\u4e00\u4e2a\u503c\u5f97\u81ea\u5df1\u503e\u5fc3\u7684\u4eba\u7269\u3002\u94c1\u955c\u5fc3\u3001\u6bd5\u64ce\u5929\u7b49\u4eba\u7684\u5f71\u5b50\uff0c\u4e00\u4e00\u968f\u7740\u6ce2\u6d9b\u6d88 \u901d\uff0c\u53f6\u5b97\u7559\u867d\u7136\u503c\u5f97\u4f69\u670d\uff0c\u4f46\u90a3\u5374\u4e0d\u662f\u5c11\u5973\u5fc3\u76ee\u4e2d\u7684\u201c\u82f1\u96c4\u201d\u3002\u60f3\u5230\u6b64\u65f6\uff0c\u4e0d\u7981\u6697\u6697\u7fa1\u6155\u81ea \u5df2\u7684\u5e08\u6bcd\uff0c\u771f\u6709\u798f\u6c14\u3002 \u3000\u3000\u4e1c\u8fb9\u6e10\u6e10\u9732\u51fa\u9c7c\u809a\u767d\u8272\uff0c\u6d77\u6d6a\u5954\u817e\u547c\u5578\uff0c\u6108\u6765\u6108\u6025\uff0c\u6d6a\u5934\u5377\u5f97\u66f4\u9ad8\uff0c\u66d9\u8272\u6ce2\u5149\u76f8\u6620\uff0c\u4f46 \u89c1\u5929\u8fde\u6c34\u3001\u6c34\u8fde\u5929\uff0c\u767d\u832b\u832b\u4e00\u7247\uff0c\u6d69\u85cf\u65e0\u6daf\u3002\u4e8e\u627f\u73e0\u76ee\u7729\u795e\u8ff7\uff0c\u9707\u60ca\u4e8e\u5927\u6d77\u7684\u96c4\u5947\u58ee\u9614\uff0c\u53ea \u89c1\u6ce2\u7ffb\u6d6a\u6d8c\u4e4b\u4e2d\uff0c\u90a3\u7fa4\u6d77\u9e25\u8fd8\u662f\u4e00\u6837\u7684\u63a0\u6c34\u620f\u6ce2\uff0c\u56de\u7fd4\u5982\u610f\uff0c\u4e8e\u627f\u73e0\u80f8\u895f\u4e00\u723d\uff0c\u90c1\u95f7\u987f\u6d88\uff0c \u81ea\u987e\u81ea\u5730\u7b11\u9053\uff1a\u201c\u6d77\u9e25\u5c1a\u81ea\u80fd\u591f\u51b2\u6ce2\u51b2\u6d6a\uff0c\u5c55\u7ffc\u51cc\u4e91\uff0c\u6211\u96be\u9053\u5c31\u4e0d\u80fd\u50cf\u5b83\uff1f\u201d\u5ffd\u7136\u6709\u4e86\u4e00\u79cd \u8f7b\u5feb\u4e4b\u611f\uff0c\u75be\u5411\u524d\u884c\u3002 \u3000\u3000\u66d9\u5149\u663e\u73b0\uff0c\u4e0d\u4f46\u5927\u6d77\u6cdb\u8d77\u6e05\u5149\uff0c\u6d77\u8fb9\u5c71\u5730\uff0c\u4e5f\u50cf\u7a81\u7136\u95f4\u88ab\u65e0\u5f62\u7684\u5de8\u624b\uff0c\u63ed\u53bb\u4e86\u4e00\u5c42\u8584\u96fe \u8f7b\u7ee1\uff0c\u8f6e\u5ed3\u4e00\u8c41\u3002\u4e8e\u627f\u73e0\u6b63\u81ea\u9189\u5fc3\u89c2\u8d4f\u8fd9\u6d77\u6ee8\u7684\u6e05\u6668\u666f\u8272\uff0c\u5ffd\u542c\u5f97\u4e00\u9635\u6025\u4fc3\u7684\u811a\u6b65\u58f0\uff0c\u98de\u5954 \u800c\u6765\uff0c\u4e8e\u627f\u73e0\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u662f\u53f6\u5927\u54e5\u6d3e\u4eba\u6765\u8ffd\u6211\u56de\u53bb\uff1f\u201d\u4f46\u542c\u90a3\u811a\u6b65\u4e4b\u58f0\uff0c\u5374\u4e0d \u662f\u4ece\u540e\u9762\u6765\u7684\uff0c\u5fc3\u4e2d\u4e00\u5bbd\uff0c\u5374\u53c8\u6697\u6697\u8d77\u7591\uff1a\u201c\u600e\u4e48\u8fd9\u6837\u65e9\u5c31\u6709\u4eba\u8d76\u8def\uff1f\u201d\u811a\u6b65\u6e10\u6765\u6e10\u8fd1\uff0c\u53ea \u542c\u5f97\u4e00\u4e2a\u4eba\u6c14\u547c\u547c\u5730\u53eb\u9053\uff1a\u201c\u8eb2\u5728\u6697\u4e2d\u5077\u88ad\uff0c\u7b97\u54ea\u95e8\u5b50\u7684\u597d\u6c49\uff1f\u6709\u80c6\u7684\u6562\u5728\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\uff0c \u51fa\u6765\u6bd4\u5212\u6bd4\u5212\u4e48\uff1f\u201d\u58f0\u97f3\u597d\u719f\uff0c\u542c\u6e05\u695a\u4e86\u7adf\u662f\u5fa1\u6797\u519b\u7edf\u9886\u5a04\u6850\u5b59\u7684\u58f0\u97f3\uff01\u4e8e\u627f\u73e0\u60ca\u5947\u4e4b\u6781\uff01 \u4ee5\u5a04\u4f97\u5b59\u7684\u672c\u9886\uff0c\u8fd8\u8fdc\u5728\u5979\u4e0e\u94c1\u955c\u5fc3\u3001\u6bd5\u64ce\u5929\u8bf8\u4eba\u4e4b\u4e0a\uff0c\u6709\u8c01\u6562\u5728\u6697\u4e2d\u5411\u4ed6\u5077\u88ad\uff1f \u3000\u3000\u970e\u773c\u4e4b\u95f4\uff0c\u4eba\u5f71\u5df2\u5728\u8def\u8fb9\u8f6c\u89d2\u4e4b\u5904\u73b0\u51fa\uff0c\u4e0d\u662f\u5a04\u6850\u5b59\u662f\u8c01\uff1f\u4e8e\u627f\u73e0\u6025\u5fd9\u89c5\u5730\u8eb2\u85cf\uff0c\u6070\u597d \u8def\u8fb9\u5c71\u811a\u6709\u4e24\u5757\u76f8\u8fde\u7684\u5927\u77f3\uff0c\u4e2d\u95f4\u7f1d\u9699\uff0c\u521a\u53ef\u5bb9\u8eab\uff0c\u4e8e\u627f\u73e0\u94bb\u4e86\u8fdb\u53bb\uff0c\u5a04\u6850\u5b59\u4ea6\u5df2\u6765\u5230\uff0c\u53ea \u89c1\u4ed6\u62ab\u5934\u6563\u53d1\uff0c\u9762\u4e0a\u9752\u4e00\u5757\uff0c\u9ed1\u4e00\u5757\uff0c\u8863\u670d\u4e0a\u4e5f\u6cbe\u6ee1\u6c61\u6ce5\uff0c\u6837\u5b50\u7adf\u662f\u5341\u5206\u72fc\u72c8\u3002\u4e8e\u627f\u73e0\u66f4\u662f \u60ca\u5947\u4e0d\u5df2\uff01\u5fc3\u9053\uff1a\u7eb5\u4f7f\u662f\u77f3\u60ca\u6d9b\u77f3\u8001\u524d\u8f88\uff0c\u4e5f\u672a\u5fc5\u80fd\u628a\u5a04\u6850\u5b59\u5f04\u6210\u8fd9\u4e2a\u6a21\u6837\uff01\u4f55\u51b5\u77f3\u60ca\u6d9b\u5fc3 \u7070\u610f\u51b7\uff0c\u4e5f\u6ca1\u6709\u8fd9\u6837\u7684\u95f2\u5fc3\uff01\u4e8e\u627f\u73e0\u81ea\u77e5\u4e0d\u662f\u5a04\u6850\u5b59\u7684\u5bf9\u624b\uff0c\u8eb2\u5728\u5927\u77f3\u7f1d\u4e2d\uff0c\u8fde\u547c\u5438\u4e5f\u4e0d\u6562 \u5927\u58f0\uff0c\u751f\u6015\u7ed9\u4ed6\u53d1\u89c9\u3002 \u3000\u3000\u4f60\u9053\u5a04\u6850\u5b59\u4f55\u4ee5\u72fc\u72c8\u5982\u65af\uff1f\u539f\u6765\u4ed6\u53d6\u5f97\u5927\u5185\u5b9d\u5251\u4e4b\u540e\uff0c\u542c\u5f97\u5c71\u5be8\u6d3e\u51fa\u4eba\u6765\u5bfb\u89c5\u94c1\u955c\u5fc3\u7684 \u58f0\u97f3\uff0c\u6025\u6025\u629b\u4e0b\u738b\u5b89\uff0c\u8fde\u591c\u98de\u9003\uff0c\u4ed6\u6015\u5728\u6d77\u6ee8\u8def\u4e0a\u4f1a\u649e\u5230\u54e8\u5175\uff0c\u867d\u7136\u4e0d\u60e7\uff0c\u52a8\u8d77\u624b\u6765\uff0c\u603b\u60f9 \u9ebb\u70e6\uff0c\u4e8e\u662f\u4e13\u62e3\u9760\u8fd1\u5c71\u8fb9\u7684\u5c0f\u8def\u884c\u8d70\uff0c\u90a3\u6761\u5c0f\u8def\u8981\u901a\u8fc7\u4e00\u7247\u5c71\u5c97\uff0c\u5a04\u6850\u5b59\u94bb\u5165\u6797\u5b50\uff0c\u4f30\u91cf\u79bb \u5f00\u4e49\u519b\u8425\u5730\u5df2\u6709\u5341\u91cc\u4e4b\u9065\u3002\u4e8e\u662f\u653e\u677e\u811a\u6b65\uff0c\u62bd\u51fa\u5b9d\u5251\u4e00\u770b\uff0c\u4f46\u89c1\u4e00\u7f15\u5bd2\u5149\uff0c\u8131\u5323\u98de\u51fa\uff0c\u5728\u9ed1 \u6c89\u6c89\u7684\u6811\u6797\u4e2d\uff0c\u5b9b\u5982\u7167\u8def\u7684\u591c\u660e\u73e0\uff0c\u79bb\u8eab\u4e94\u6b65\u4e4b\u5185\uff0c\u53ef\u4ee5\u770b\u5f97\u76f8\u5f53\u6e05\u6670\uff0c\u5a04\u6850\u5b59\u5927\u559c\u8d5e\u9053\uff1a \u201c\u5927\u5185\u5b9d\u5251\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff01\u602a\u4e0d\u5f97\u77f3\u60ca\u6d9b\u8fd9\u8001\u513f\u4e3a\u5b83\u5927\u95f9\u7687\u5bab\uff01\u201d\u60f3\u5230\u5c06\u5b9d\u5251\u7f34\u5448\u7687\u4e0a\uff0c \u5b9a\u6709\u91cd\u8d4f\uff0c\u5fc3\u4e2d\u72c2\u559c\uff0c\u54a7\u5f00\u5634\u5df4\u7b11\u4e2a\u4e0d\u505c\uff0c\u53c8\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u5e78\u4e8f\u9633\u5b97\u6d77\u6ca1\u6709\u540c\u6765\uff0c\u82e5\u7136\u7ed9 \u4ed6\u6709\u8fd9\u628a\u5b9d\u5251\uff0c\u6211\u770b\u4ed6\u8fde\u5927\u5185\u603b\u7ba1\u8fd9\u4e2a\u5b98\u804c\u4e5f\u4e0d\u7a00\u7f55\uff0c\u51c6\u4f1a\u631f\u5e26\u4e86\u8fd9\u628a\u5b9d\u5251\u79c1\u9003\u3002\u563f\uff0c\u53ef\u60dc \u6211\u5f53\u5e74\u6ca1\u6709\u5b66\u5251\uff0c\u8981\u4e0d\u7136\u6211\u4e5f\u820d\u4e0d\u5f97\u7f34\u56de\u5927\u5185\u3002\u201d\u4ed6\u867d\u7136\u4e0d\u64c5\u957f\u5251\u6cd5\uff0c\u4f46\u4e00\u4e9b\u666e\u901a\u7684\u62db\u5f0f\u8fd8 \u662f\u4f1a\u7684\uff0c\u5b9d\u5251\u5728\u624b\uff0c\u7981\u4e0d\u4f4f\u4e71\u821e\u4e00\u901a\uff0c\u5ffd\u542c\u5f97\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u4e0d\u77e5\u4ece\u4f55\u5904\u63b7\u6765\u4e00\u7c92\u77f3\u5b50\uff0c\u6070 \u6070\u78b0\u7740\u5251\u5c16\uff0c\u9707\u5f97\u55e1\u55e1\u4f5c\u54cd\uff0c\u5a04\u6850\u5b59\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u54ea\u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff0c\u8bf7\u51fa\u6765\u4e00\u89c1\u3002\u201d\u6797\u5b50 \u91cc\u5bc2\u7136\u65e0\u58f0\uff0c\u5a04\u6850\u5b59\u821e\u5251\u62a4\u8eab\uff0c\u56db\u9762\u63a2\u671b\uff0c\u5ffd\u542c\u5f97\u4e1c\u8fb9\u9690\u6709\u7b11\u58f0\uff0c\u5a04\u6850\u5b59\u98de\u6251\u8fc7\u53bb\uff0c\u626c\u58f0\u53eb \u9053\uff1a\u201c\u5a04\u6850\u5b59\u5728\u6b64\u5019\u6559\uff01\u201d\u4ed6\u4eae\u51fa\u201c\u4e07\u513f\u201d\uff08\u540d\u5934\uff09\uff0c\u4ee5\u4e3a\u4e0d\u8bba\u9ed1\u9053\u767d\u9053\uff0c\u603b\u5f97\u5356\u4ed6\u7684\u5e10\uff0c \u54ea\u6599\u8bdd\u72b9\u672a\u4e86\uff0c\u53c8\u662f\u4e00\u7c92\u77f3\u5b50\u98de\u6765\uff0c\u8fd9\u4e00\u6b21\u52b2\u9053\u6bd4\u524d\u66f4\u5927\uff0c\u78b0\u5f97\u5b9d\u5251\u53cd\u5f39\u8d77\u6765\uff0c\u8fde\u864e\u53e3\u4e5f\u6709 \u70b9\u53d1\u9ebb\uff01 \u3000\u3000\u5a04\u6850\u5b59\u5927\u6012\uff0c\u98de\u8eab\u6251\u53bb\uff0c\u90a3\u7b11\u58f0\u5ffd\u5730\u53c8\u8f6c\u5230\u897f\u8fb9\uff0c\u5a04\u6850\u5b59\u7834\u53e3\u9a82\u9053\uff1a\u201c\u9b3c\u9b3c\u795f\u795f\uff0c\u518d\u4e0d \u51fa\u6765\uff0c\u6211\u53ef\u8981\u9a82\u5566\uff01\u201d\u5ffd\u5730\u4e00\u80a1\u6c61\u6ce5\u7684\u81ed\u5473\u653b\u5165\u54bd\u5589\uff0c\u4e00\u56e2\u6e7f\u6f09\u6f09\u7684\u4e1c\u897f\uff0c\u585e\u5165\u4e86\u53e3\u4e2d\uff0c\u5a04 \u6850\u5b59\u54c7\u7684\u4e00\u58f0\u5455\u5410\u51fa\u6765\uff0c\u53ef\u4e0d\u662f\u6c61\u6ce5\u662f\u4ec0\u4e48\uff1f\u8fd8\u60f3\u518d\u9a82\uff0c\u7b2c\u4e8c\u56e2\u6c61\u6ce5\u53c8\u5230\uff0c\u6253\u5f97\u4ed6\u9762\u4e0a\u706b\u8fa3 \u8fa3\u7684\uff0c\u7b11\u58f0\u53c8\u8f6c\u5230\u5357\u8fb9\u4e86\u3002 \u3000\u3000\u8bd5\u60f3\u5a04\u4f97\u5b59\u662f\u4f55\u7b49\u6b66\u529f\uff0c\u5bfb\u5e38\u6697\u5668\uff0c\u968f\u53d1\u968f\u63a5\uff0c\u6c38\u65e0\u5931\u624b\uff0c\u7adf\u7136\u7ed9\u4eba\u63a5\u8fde\u6253\u4e2d\u4e24\u6b21\uff0c\u5fc3 \u4e2d\u4e0d\u7981\u7531\u6012\u751f\u60e7\uff0c\u60f3\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u662f\u9b3c\u9b45\u4e0d\u6210\uff1f\u201d\u4e0d\u6562\u518d\u9a82\uff0c\u53ea\u6c42\u8d70\u51fa\u8fd9\u7247\u6797\u5b50\uff0c\u54ea\u77e5\u624d\u8d70 \u5f97\u51e0\u6b65\uff0c\u731b\u542c\u5f97\u4e00\u4e2a\u4f4e\u6c89\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u56de\u53bb\uff01\u201d\u547c\u7684\u4e00\u58f0\uff0c\u53c8\u662f\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\uff0c\u52b2\u9053\u6bd4\u524d \u51e0\u6b21\u66f4\u5927\uff0c\u5a04\u6850\u5b59\u903c\u5f97\u5411\u540e\u5012\u7eb5\u907f\u5f00\uff0c\u524d\u51e0\u6b21\u662f\u5c0f\u77f3\u5b50\u548c\u6e7f\u6ce5\u56e2\uff0c\u8fd9\u6b21\u5374\u662f\u9e45\u5375\u822c\u7684\u77f3\u5757\uff0c \u4ee5\u90a3\u4eba\u7684\u52b2\u529b\uff0c\u7ed9\u6253\u4e2d\u4e86\uff0c\u9aa8\u5934\u4e5f\u4f1a\u788e\u88c2\u3002 \u3000\u3000\u5c31\u8fd9\u6837\u7684\uff0c\u5a04\u6850\u5b59\u88ab\u8fd9\u4e2a\u4e0d\u9732\u9762\u7684\u602a\u4eba\u8d76\u5f97\u76f4\u5f80\u56de\u5934\u5c31\u8d70\uff0c\u65f6\u4e0d\u65f6\u8fd8\u98de\u6765\u51e0\u56e2\u6e7f\u6ce5\uff0c\u65e0 \u58f0\u65e0\u606f\u5730\u6253\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u628a\u4ed6\u7684\u8863\u670d\u4e0a\u9762\u5f04\u5f97\u6ce5\u6c34\u6dcb\u6f13\uff0c\u5929\u8272\u672a\u4eae\uff0c\u5a04\u6850\u5b59\u7a7a\u81ea\u6c14\u788d\u4e03\u7a8d\u751f \u70df\uff0c\u4e0d\u6562\u53d1\u6076\uff0c\u539f\u6765\u4ed6\u7ec3\u7684\u5206\u7b4b\u9519\u9aa8\u624b\u867d\u7136\u72ec\u6b65\u6b66\u6797\uff0c\u8fd9\u79cd\u529f\u592b\uff0c\u5374\u53ea\u80fd\u8fd1\u8eab\u8089\u640f\uff0c\u800c\u4e14\u4ed6 \u4e0d\u662f\u6253\u6697\u5668\u7684\u9ad8\u624b\uff0c\u6ca1\u7ec3\u6709\u201c\u591c\u773c\u201d\uff08\u4e00\u6d41\u6697\u5668\u9ad8\u624b\uff0c\u5728\u9ed1\u591c\u4e4b\u4e2d\u4e5f\u767e\u53d1\u767e\u4e2d\uff09\uff0c\u5728\u9ed1\u591c\u91cc \u66f4\u662f\u5403\u4e8f\u3002 \u3000\u3000\u597d\u5bb9\u6613\u6328\u5230\u5929\u4eae\uff0c\u5a04\u6850\u5b59\u6253\u5f97\u660f\u5934\u660f\u8111\uff0c\u6d77\u98ce\u5439\u6765\uff0c\u7cbe\u795e\u4e00\u723d\uff0c\u628a\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u5df2\u56de\u5230 \u6d77\u6ee8\u8def\u4e0a\uff0c\u5fc3\u4e2d\u6697\u6697\u5403\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u518d\u8fc7\u6570\u91cc\u4e4b\u9065\uff0c\u5c31\u53ef\u671b\u5230\u4e49\u519b\u8425\u5730\uff0c\u5e78\u5728\u800c\u4eca\u5929\u8272\u5df2 \u4eae\uff0c\u8981\u4e0d\u7136\u88ab\u4ed6\u8d76\u5230\u8425\u524d\uff0c\u90a3\u53ef\u4e0d\u662f\u60f9\u4eba\u7b11\u8bdd\uff01\u201d\u5927\u8272\u4e00\u4eae\uff0c\u4ed6\u80c6\u6c14\u987f\u58ee\uff0c\u56db\u9762\u4e00\u671b\uff0c\u6668\u66dd \u521d\u73b0\uff0c\u8def\u4e0a\u8fd8\u6ca1\u6709\u884c\u4eba\uff0c\u90a3\u4e2a\u602a\u4eba\uff0c\u4e5f\u59cb\u7ec8\u6ca1\u6709\u9732\u9762\u3002 \u3000\u3000\u5a04\u6850\u5b59\u9a82\u4e86\u4e00\u901a\uff0c\u5401\u4e86\u53e3\u6c14\uff0c\u6905\u7740\u8def\u8fb9\u7684\u5ca9\u77f3\u4f11\u606f\uff0c\u4ed6\u8dd1\u4e86\u534a\u591c\uff0c\u8179\u4e2d\u5df1\u662f\u6709\u4e9b\u9965\u997f\uff0c \u4e8e\u662f\u628a\u5251\u63d2\u5728\u5730\u4e0a\uff0c\u638f\u51fa\u5e72\u7cae\u6765\u5403\uff0c\u4ed6\u5374\u6ca1\u6709\u7ec6\u5fc3\u5bdf\u89c6\uff0c\u90a3\u5757\u5ca9\u77f3\u5176\u5b9e\u662f\u4e24\u77f3\u76f8\u8fde\uff0c\u5728\u4fa7\u9762 \u6709\u4e00\u9053\u7a84\u7f1d\uff0c\u7f1d\u9699\u4e2d\u85cf\u6709\u4e00\u4e2a\u5c11\u5973\u3002 \u3000\u3000\u5374\u8bf4\u4e8e\u627f\u73e0\u85cf\u5728\u77f3\u7f1d\u4e4b\u4e2d\uff0c\u5ffd\u542c\u5f97\u5a04\u6850\u5b59\u7684\u5598\u606f\u4e4b\u58f0\uff0c\u8fd9\u4e00\u5413\u975e\u540c\u5c0f\u53ef\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u672a \u89c1\u52a8\u9759\uff0c\u60f3\u662f\u4ed6\u672a\u53d1\u73b0\u77f3\u5934\u4fa7\u9762\u6709\u7f1d\uff0c\u7565\u7565\u5bbd\u5fc3\uff0c\u4ecd\u662f\u4e0d\u6562\u5927\u58f0\u547c\u5438\uff0c\u5ffd\u7136\u773c\u775b\u4e00\u4eae\uff0c\u4ece\u77f3 \u7f1d\u4e2d\u671b\u51fa\uff0c\u4f46\u89c1\u90a3\u628a\u5b9d\u5251\u63d2\u5728\u5730\u4e0a\uff0c\u4f38\u624b\u53ef\u53ca\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u6211\u4f55\u4e0d\u628a\u5b9d\u5251\u5077\u4e86\uff0c\u5c06\u4ed6\u4e00\u5251\u523a\u5012\uff01\u201d\u610f\u52a8\u624b\u52a8\uff0c\u500f\u5730\u6293\u7740 \u5251\u67c4\uff0c\u54ea\u77e5\u521a\u521a\u62d4\u8d77\uff0c\u5a04\u6850\u5b59\u5df2\u662f\u542c\u5230\u58f0\u606f\uff0c\u4fa7\u8eab\u4e00\u6293\uff0c\u4e8e\u627f\u73e0\u7684\u624b\u8155\u4e0a\u597d\u4f3c\u7a81\u7136\u52a0\u4e86\u4e00\u5c42 \u94c1\u7b8d\uff0c\u5a04\u6850\u5b59\u4e00\u770b\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\uff01\u201d\u7528\u529b\u4e00\u62d6\uff0c\u4e8e\u627f\u73e0\u4e0d\u5f85\u4ed6\u529b\u9053\u7528\u8db3\uff0c\u500f\u7136\u8d81 \u52bf\u8df3\u51fa\u3002 \u3000\u3000\u597d\u4e2a\u4e8e\u627f\u73e0\uff0c\u771f\u4e0d\u6127\u662f\u4e45\u7ecf\u5f20\u4e39\u67ab\u85b0\u9676\u7684\u540d\u5bb6\u5f1f\u5b50\uff0c\u4e34\u5371\u4e0d\u4e71\uff0c\u5e94\u62db\u673a\u8b66\u4e4b\u6781\uff0c\u5c31\u5728\u8d81 \u52bf\u8df3\u51fa\u7684\u4e00\u77ac\u4e4b\u95f4\uff0c\u9752\u51a5\u5b9d\u5251\u5df2\u662f\u8131\u9798\u800c\u51fa\uff0c\u5979\u53f3\u624b\u624b\u8155\u88ab\u5a04\u6850\u5b59\u6293\u7740\uff0c\u8eab\u5f62\u672c\u5df1\u5411\u524d\u503e \u4fef\uff0c\u91cd\u5fc3\u4e0d\u7a33\uff0c\u4f46\u5de6\u624b\u5b9d\u5251\u8fd9\u4e48\u4e00\u523a\uff0c\u5374\u6b63\u597d\u52a0\u5f3a\u4e86\u5411\u524d\u51b2\u523a\u4e4b\u529b\uff0c\u53ea\u89c1\u9752\u5149\u4e00\u95ea\uff0c\u5251\u5c16\u51e0 \u4e4e\u89e6\u53ca\u4e86\u5a04\u6850\u5b59\u7684\u54bd\u5589\uff0c\u5a04\u6850\u5b59\u5c06\u4e8e\u627f\u73e0\u7684\u624b\u8155\u4e00\u626d\uff0c\u4e8e\u627f\u73e0\u53f3\u624b\u4ecd\u7136\u63e1\u7d27\u90a3\u628a\u5927\u5185\u5b9d\u5251\uff0c \u88ab\u4ed6\u4e00\u626d\uff0c\u5251\u8eab\u7ffb\u4e86\u4e0a\u6765\uff0c\u54e8\u7684\u4e00\u58f0\uff0c\u4e0e\u9752\u51a5\u5b9d\u5251\u78b0\u4e2a\u6b63\u7740\uff0c\u4e8e\u627f\u73e0\u624b\u88ab\u5a04\u6850\u5b59\u5236\u4f4f\uff0c\u867d\u7136 \u4e24\u624b\u90fd\u63e1\u6709\u5b9d\u5251\uff0c\u5374\u975e\u4f46\u523a\u4e0d\u7740\u654c\u4eba\uff0c\u53cd\u800c\u5de6\u53f3\u624b\u7684\u5b9d\u5251\u4ea4\u8d77\u950b\u6765\u3002 \u3000\u3000\u4e0d\u8fc7\u8fd9\u6837\u4e00\u6765\uff0c\u4e8e\u627f\u73e0\u7684\u90a3\u6761\u53f3\u624b\uff0c\u5012\u53ef\u4ee5\u4fdd\u5168\uff0c\u672c\u6765\u5a04\u6850\u5b59\u53ef\u4ee5\u62d7\u6298\u5979\u7684\u624b\u8155\uff0c\u4f46\u4e3a \u4e86\u8981\u501f\u5979\u53f3\u624b\u7684\u5b9d\u5251\uff0c\u6321\u7740\u5de6\u624b\u7684\u5b9d\u5251\uff0c\u53ea\u80fd\u7528\u9634\u67d4\u4e4b\u529b\u79fb\u8f6c\u64cd\u7eb5\u5979\u7684\u624b\u8155\u3002\u4e8e\u627f\u73e0\u6446\u8131\u4e0d \u5f00\uff0c\u53c8\u6c14\u53c8\u6124\uff0c\u731b\u5730\u94f6\u7259\u4e00\u54ac\uff0c\u5de6\u624b\u7684\u9752\u51a5\u5251\u7528\u529b\u4e00\u5f39\uff0c\u7adf\u7136\u5411\u7740\u81ea\u5df1\u53f3\u624b\u7684\u624b\u8155\u6a2a\u5207\u4e0b \u53bb\uff0c\u5a04\u6850\u5b59\u7684\u624b\u6307\u82e5\u4e0d\u677e\u5f00\uff0c\u5c31\u8fde\u4ed6\u7684\u624b\u6307\u4e5f\u4e00\u9f50\u65a9\u65ad\uff01 \u3000\u3000\u8fd9\u4e00\u62db\u5927\u6709\u201c\u6bd2\u86c7\u7f20\u81c2\uff0c\u58ee\u58eb\u65ad\u8155\u201d\u4e4b\u6168\uff0c\u5a04\u6850\u5b59\u5927\u5403\u4e00\u60ca\uff0c\u624b\u6307\u6025\u677e\uff0c\u4e8e\u627f\u73e0\u65e9\u6599\u5230 \u4ed6\u4f1a\u5982\u6b64\uff0c\u54c8\u54c8\u4e00\u7b11\u3002\u4e24\u53e3\u5b9d\u5251\u90fd\u65cb\u98ce\u822c\u5730\u6740\u4e86\u8fc7\u6765\uff0c\u5a04\u6850\u5b59\u9a82\u9053\uff1a\u201c\u597d\u72e1\u733e\u7684\u5c0f\u5b50\uff01\u201d\u4f7f \u51fa\u5206\u7b4b\u9519\u9aa8\u7684\u64d2\u62ff\u624b\u6cd5\uff0c\u7adf\u7136\u5728\u4e24\u53e3\u5b9d\u5251\u7eb5\u6a2a\u4ea4\u51fb\u4e4b\u4e0b\uff0c\u65bd\u5c55\u7a7a\u624b\u5165\u767d\u5203\u7684\u529f\u592b\uff0c\u53ef\u662f\u4e8e\u627f \u73e0\u7684\u7384\u673a\u5251\u6cd5\uff0c\u53d8\u5316\u65e0\u65b9\uff0c\u5a04\u6850\u5b59\u7684\u6b66\u529f\u867d\u7136\u6bd4\u5979\u9ad8\u51fa\u8bb8\u591a\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u4e5f\u4e0d\u80fd\u5c06\u5979\u5236 \u4f0f\u3002\u970e\u65f6\u95f4\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u4e24\u4eba\u90fd\u611f\u7126\u8e81\u4e0d\u5b89\uff0c\u5a04\u6850\u5b59\u987e\u5fcc\u90a3\u795e\u51fa\u9b3c\u6ca1\u7684\u602a\u4eba\uff0c\u4e8e\u627f\u73e0\u4e5f\u6015 \u6bd5\u64ce\u5929\u8ffd\u6765\uff0c\u5bf9\u5979\u7ea0\u7f20\u3002\u767e\u53d8\u7384\u673a\u5251\u6cd5\uff0c\u4ee5\u4e24\u4eba\u5408\u4f7f\uff0c\u5a01\u529b\u6700\u5927\uff0c\u4e8e\u627f\u73e0\u4e00\u4eba\u4f7f\u5de6\u53f3\u53cc\u5251\uff0c \u53f3\u624b\u5251\u62db\u6709\u5982\u6d41\u6c34\u884c\u4e91\uff0c\u5de6\u624b\u5251\u62db\u5374\u8fd8\u4e0d\u80fd\u968f\u5fc3\u5982\u610f\uff0c\u5a04\u6850\u5b59\u7aa5\u7834\u5f31\u70b9\uff0c\u5ffd\u5730\u6b3a\u8eab\u4e00\u62cd\uff0c\u5de6 \u638c\u5f15\u5f00\u4e8e\u627f\u73e0\u7684\u9752\u51a5\u5b9d\u5251\uff0c\u53f3\u638c\u7a81\u7136\u5316\u6293\u4e3a\u62ff\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6492\u5251\uff01\u201d\u4e2d\u98df\u4e8c\u6307\u5728\u5979\u624b\u8155\u4e0a \u4e00\u6572\uff0c\u53cd\u624b\u4e00\u593a\uff0c\u90a3\u4e00\u5927\u5185\u5b9d\u5251\u5230\u4e86\u4ed6\u7684\u624b\u4e2d\u3002\u5a04\u6850\u5b59\u54c8\u54c8\u5927\u7b11\uff0c\u4e3e\u5251\u4e00\u6321\uff0c\u5de6\u638c\u53c8\u7528\u5206\u7b4b \u9519\u9aa8\u7684\u624b\u6cd5\u6765\u626d\u4e8e\u627f\u73e0\u7684\u81c2\u818a\uff0c\u53c8\u559d\u9053\uff1a\u201c\u6492\u5251\uff01\u201d\u7adf\u60f3\u628a\u9752\u51a5\u5b9d\u5251\u4e5f\u593a\u8fc7\u6765\uff01\u54ea\u6599\u4e8e\u627f\u73e0 \u4e00\u5251\u5728\u624b\uff0c\u5374\u6bd4\u524d\u66f4\u4e3a\u7075\u6d3b\uff0c\u5de6\u624b\u865a\u634f\u5251\u8bc0\uff0c\u4e00\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u5251\u5149\u4e00\u7ede\uff0c\u7acb\u5373\u5206\u5fc3\u4fbf \u523a\uff0c\u5a04\u6850\u5b59\u5251\u6cd5\u672c\u975e\u6240\u957f\uff0c\u53cc\u5251\u4e00\u4ea4\uff0c\u7adf\u88ab\u4e8e\u627f\u73e0\u7684\u5251\u76f4\u538b\u4e0b\u53bb\uff0c\u5251\u5c16\u582a\u582a\u523a\u5230\u4ed6\u7684\u80f8\u524d\uff0c \u5a04\u6850\u5b59\u987e\u4e0d\u5f97\u593a\u5251\u4f24\u654c\uff0c\u903c\u5f97\u64a4\u638c\u56de\u9632\u3002\u4e8e\u627f\u73e0\u5f97\u7406\u4e0d\u9976\u4eba\uff0c\u5530\u5530\u5530\u4e00\u8fde\u4e09\u5251\uff0c\u628a\u5a04\u6850\u5b59\u903c \u5f97\u8fde\u9000\u6570\u6b65\uff0c\u5a04\u6850\u65b0\u5927\u6012\u559d\u9053\uff1a\u201c\u6211\u82e5\u593a\u4e0d\u4e86\u4f60\u7684\u5251\uff0c\u6211\u5a04\u5b57\u5012\u5199\uff01\u201d\u6b63\u62df\u63d2\u56de\u5b9d\u5251\uff0c\u4ecd\u7528 \u5206\u7b4b\u9519\u9aa8\u624b\u6cd5\u80dc\u4ed6\uff0c\u5ffd\u542c\u5f97\u4e00\u4eba\u53eb\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff01\u201d\u4e00\u9897\u77f3\u5b50\u7a81\u7136\u98de\u6765\uff0c\u53ee\u5f53\u4e00\u58f0\uff0c\u628a\u4e24\u53e3 \u5b9d\u5251\u90fd\u8361\u5f00\u6765\u3002 \u3000\u3000\u5a04\u6850\u5b59\u6025\u5fd9\u8df3\u51fa\u5708\u5b50\uff0c\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u79bb\u8eab\u5341\u6b65\u4e4b\u5916\uff0c\u6709\u4e00\u5bf9\u4e2d\u5e74\u7537\u5973\uff0c\u8d1f\u624b\u65c1\u89c2\uff0c\u610f \u6001\u95f2\u9002\uff0c\u4f3c\u4e4e\u5df2\u5728\u65c1\u8fb9\u770b\u4e86\u591a\u65f6\uff0c\u7537\u7684\u8863\u9970\u7279\u522b\uff0c\u4f3c\u662f\u56de\u7586\u88c5\u675f\uff0c\u5973\u7684\u80cc\u63d2\u53cc\u94a9\uff0c\u7a7f\u7684\u88d9\u5b50 \u4e5f\u975e\u6c49\u4eba\u6253\u626e\uff0c\u5a04\u6850\u5b59\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u51ed\u4ed6\u8fd9\u4e00\u8eab\u6b66\u529f\uff0c\u654c\u4eba\u6765\u5230\u8eab\u524d\uff0c\u7adf\u7136\u6beb\u65e0\u77e5\u89c9\uff01\u4e0d \u95ee\u53ef\u77e5\uff0c\u8fd9\u7537\u7684\u5b9a\u662f\u6628\u665a\u5728\u6797\u4e2d\u63b7\u77f3\u7684\u602a\u4eba\u4e86\uff01 \u3000\u3000\u5ffd\u89c1\u90a3\u7537\u7684\u6307\u7740\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5582\uff0c\u4f60\u8fd9\u4e2a\u5973\u5a03\u513f\u662f\u5f20\u4e39\u67ab\u7684\u5f92\u5f1f\u5417\uff1f\u201d\u4e8e\u627f\u73e0\u6014\u4e86\u4e00 \u6014\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u4e00\u773c\u5c31\u770b\u51fa\u6211\u662f\u5973\u626e\u7537\u88c5\u3002\u53c8\u77e5\u9053\u6211\u7684\u5e08\u95e8\u5b97\u6d3e\uff1f\u201d\u731b\u5730\u4e00\u9192\uff0c\u53eb\u9053\uff1a \u201c\u4f60\u662f\u4e4c\u4f2f\u4f2f\uff0c\u4e4c\u8499\u592b\u4f2f\u4f2f\uff01\u201d\u539f\u6765\u4e4c\u8499\u592b\u662f\u4e0a\u5b98\u5929\u91ce\u7684\u8863\u94b5\u4f20\u4eba\uff0c\u6bd4\u4ed6\u7684\u5927\u5e08\u5144\u6fb9\u53f0\u706d\u660e \u6b66\u529f\u8fd8\u5f3a\uff0c\u90a3\u5973\u7684\u5219\u662f\u4ed6\u7684\u5e08\u59b9\u91d1\u94a9\u4ed9\u5b50\u6797\u4ed9\u97f5\uff0c\u4e0a\u5b98\u5929\u91ce\u4e0e\u5f20\u4e39\u67ab\u7684\u5e08\u7956\u7384\u673a\u9038\u58eb\u9f50\u540d\uff0c \u813e\u6c14\u6781\u602a\uff0c\u4e0d\u8bb8\u95e8\u4e0b\u5f1f\u5b50\u7ed3\u5a5a\uff0c\u540e\u6765\u5168\u9760\u5f20\u4e39\u67ab\u4e4b\u52a9\uff0c\u53c8\u8bb2\u670d\u4e86\u4e0a\u5b98\u5929\u91ce\uff0c\u4e4c\u8499\u592b\u4e0e\u4ed6\u7684\u5e08 \u59b9\u624d\u5f97\u4ee5\u7ed3\u6210\u592b\u5987\uff0c\u662f\u6545\u4e4c\u8499\u592b\u4e0e\u5f20\u4e39\u67ab\u4e0d\u8bba\u73ed\u8f88\uff0c\u7ed3\u4e3a\u597d\u53cb\uff0c\u5144\u5f1f\u76f8\u79f0\u3002\uff08\u8be6\u89c1\u300a\u840d\u8e2a\u4fa0 \u5f71\u5f55\u300b\uff09\u3002\u4e8e\u627f\u73e0\u65e5\u5e38\u4e5f\u542c\u5e08\u7236\u8c08\u8fc7\uff0c\u6240\u4ee5\u4e00\u89c1\u4e86\u4ed6\u4eec\u4e8c\u4eba\u7684\u5175\u5668\u548c\u88c5\u675f\uff0c\u4fbf\u731c\u5230\u4ed6\u4eec\u662f \u8c01\u3002 \u3000\u3000\u4e4c\u8499\u592b\u3001\u5f20\u4e39\u67ab\u3001\u77f3\u60ca\u6d9b\u3001\u9633\u5b97\u6d77\u540c\u79f0\u5929\u4e0b\u56db\u5927\u5251\u5ba2\uff0c\u5a04\u6850\u5b59\u542c\u4e86\uff0c\u5fc3\u4e2d\u81ea\u662f\u5927\u60ca\uff0c\u4f46 \u81ea\u6043\u5206\u7b4b\u9519\u9aa8\u7684\u529f\u592b\u5929\u4e0b\u65e0\u654c\uff0c\u5728\u767d\u65e5\u6674\u5929\u4e4b\u4e0b\uff0c\u5374\u4e5f\u5e76\u4e0d\u600e\u6837\u754f\u60e7\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e4c\u8499 \u592b\uff0c\u60f3\u4f60\u4e5f\u662f\u4e2a\u6210\u540d\u4eba\u7269\uff0c\u600e\u4e48\u5374\u4e13\u5728\u9ed1\u6697\u4e4b\u4e2d\u4e0d\u6562\u629b\u5934\u9732\u9762\uff0c\u6211\u5a04\u67d0\u4eca\u65e5\u7b97\u662f\u6559\u4e86\uff01\u201d \u3000\u3000\u4e4c\u8499\u592b\u51b7\u51b7\u5730\u770b\u4e86\u5a04\u6850\u5b59\u4e00\u773c\uff0c\u5374\u95ee\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u77e5\u9053\u8fd9\u53ae\u662f\u4ec0\u4e48\u4eba\uff1f\u6765\u8fd9\u91cc\u505a\u4ec0 \u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ed6\u662f\u5fa1\u6797\u519b\u7edf\u9886\uff0c\u5949\u4e86\u7687\u547d\u6765\u6349\u62ff\u77f3\u60ca\u6d9b\u77f3\u8001\u524d\u8f88\u7684\uff0c\u8fd8\u60f3\u62a2\u6211\u7684\u5b9d \u5251\uff0c\u54fc\uff0c\u54fc\uff01\u662f\u4e2a\u5927\u574f\u86cb\u3002\u201d\u4e4c\u8499\u592b\u51b7\u7b11\u4e00\u58f0\uff0c\u8f6c\u5411\u5a04\u6850\u5b59\u9053\uff1a\u201c\u6628\u665a\u6211\u4e0d\u77e5\u9053\u4f60\u7684\u6765\u5386\uff0c \u6240\u4ee5\u624b\u4e0b\u7559\u60c5\uff0c\u54fc\uff0c\u4f60\u5374\u53cd\u800c\u9a82\u6211\uff1f\u4f60\u4e00\u5fc3\u8981\u62a2\u522b\u4eba\u7684\u5b9d\u5251\uff0c\u6211\u4e5f\u60f3\u8981\u4f60\u8fd9\u628a\u5b9d\u5251\uff0c\u6765\uff0c \u6765\uff0c\u54b1\u5a29\ue317\u5212\u6bd4\u5212\uff01\u201d\u5ca9\u77f3\u65c1\u8fb9\u6709\u4e00\u4e1b\u91ce\u7af9\uff0c\u4e4c\u8499\u592b\u8bf4\u5230\u201c\u6bd4\u5212\u201d\u4e8c\u5b57\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u968f\u624b\u6298 \u4e0b\u4e00\u682a\u5ae9\u7af9\uff0c\u624b\u638c\u524a\u4e86\u51e0\u524a\uff0c\u53d8\u6210\u4e86\u4e00\u67c4\u4e0d\u5230\u4e09\u5c3a\u957f\u7684\u7af9\u5251\uff0c\u865a\u5288\u4e00\u4e0b\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u7528 \u4f60\u7684\u5b9d\u5251\uff0c\u82e5\u80fd\u6253\u8d25\u6211\u7684\u7af9\u5251\uff0c\u6211\u7acb\u523b\u91cd\u56de\u6f20\u5317\uff0c\u4ece\u6b64\u4e0d\u5230\u6c5f\u5357\uff01\u201d \u3000\u3000\u5a04\u6850\u5b59\u6c14\u5f80\u4e0a\u51b2\uff0c\u9053\uff1a\u201c\u82e5\u662f\u4f60\u7684\u7af9\u5251\u7ed9\u6211\u524a\u65ad\u4e86\u53c8\u5982\u4f55\uff1f\u201d\u4e4c\u8499\u592b\u9053\uff1a\u201c\u90a3\u81ea\u7136\u7b97\u6211 \u8f93\u4e86\u3002\u201d\u5a04\u6850\u5b59\u5fc3\u9053\uff1a\u201c\u6211\u867d\u7136\u5251\u5b66\u4e0d\u7cbe\uff0c\u4f46\u8fd9\u67c4\u5251\u524a\u94c1\u5982\u6ce5\uff0c\u5439\u6bdb\u7acb\u65ad\uff0c\u5c82\u6709\u524a\u4e0d\u65ad\u4f60\u7684 \u7af9\u5251\u4e4b\u7406\uff1f\u201c\u51b2\u53e3\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e5f\u662f\u4e2a\u6210\u540d\u4eba\u7269\uff0c\u54b1\u4eec\u4e00\u8a00\u4e3a\u5b9a\uff0c\u4f60\u82e5\u7528\u7af9\u5251\u6253\u8d25\u6211\uff0c\u8fd9 \u67c4\u5927\u5185\u5b9d\u5251\u53cc\u624b\u5949\u9001\uff01\u201d \u3000\u3000\u8bf4\u5230\u4e00\u4e2a\u201c\u9001\u201d\u5b57\uff0c\u5251\u8bc0\u4e00\u9886\uff0c\u500f\u5730\u4e00\u62db\u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\uff0c\u62e6\u8170\u75be\u65a9\uff0c\u4e4c\u8499\u592b\u7b11\u9053\uff1a \u201c\u5413\uff0c\u597d\u5feb\uff01\u201d\u4e00\u95ea\u8eab\u5c31\u5230\u4e86\u5a04\u6850\u5b59\u80cc\u540e\uff0c\u7af9\u5251\u523a\u4ed6\u8111\u540e\u7684\u201c\u98ce\u5e9c\u7a74\u201d\uff0c\u5a04\u6850\u5b59\u7684\u5251\u62db\u7528 \u8001\uff0c\u6025\u5207\u4e4b\u95f4\u64a4\u4e0d\u56de\u6765\uff0c\u6697\u53eb\u4e00\u58f0\u4e0d\u5999\uff0c\u614c\u5fd9\u53cd\u624b\u4e00\u6293\uff0c\u8fd9\u4e00\u62db\u4f7f\u51fa\u7684\u5374\u662f\u201c\u5206\u7b4b\u9519\u9aa8\u624b\u201d \u7684\u529f\u592b\uff0c\u4e4c\u8499\u592b\u5927\u7b11\u9053\uff1a\u201c\u8bf4\u662f\u6bd4\u5251\uff0c\u72d7\u722a\u5b50\u4e5f\u4f38\u51fa\u6765\u5566\uff01\u201d\u5a04\u6850\u5b59\u8138\u4e0a\u4e00\u70ed\uff0c\u867d\u7136\u4e8b\u5148\u5e76 \u672a\u8bf4\u660e\u4e0d\u8bb8\u7528\u638c\uff0c\u4f46\u4ee5\u5f7c\u6b64\u6210\u540d\u4eba\u7269\u7684\u8eab\u4efd\uff0c\u8fd9\u4e00\u4e0b\u603b\u662f\u5931\u4e86\u989c\u9762\uff01 \u3000\u3000\u5a04\u6850\u5b59\u60f3\u4ed7\u5b9d\u5251\u4e4b\u5229\uff0c\u8fde\u65bd\u653b\u51fb\uff0c\u5374\u4e0d\u6599\u4e4c\u8499\u592b\u8eab\u6cd5\u602a\u6781\uff0c\u95ea\u5c55\u817e\u632a\uff0c\u65e0\u4e0d\u6070\u5230\u597d\u5904\uff0c \u5a04\u6850\u5b59\u53cd\u800c\u6709\u51e0\u6b21\u9669\u9669\u7ed9\u4ed6\u7684\u7af9\u5251\u523a\u4e2d\u7a74\u9053\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u5982\u6b64\u4e0b\u53bb\uff0c\u603b\u6709\u758f\u5931\u4e4b \u5904\uff0c\u522b\u8981\u4e0a\u4ed6\u7684\u5f53\u3002\u201d\u5251\u6cd5\u4e00\u53d8\uff0c\u821e\u6210\u4e86\u4e00\u5708\u94f6\u8679\uff0c\u4ed6\u5251\u5b66\u867d\u7136\u4e0d\u7cbe\uff0c\u4f46\u9632\u8eab\u7684\u5251\u6cd5\uff0c\u53ea\u5b88 \u4e0d\u653b\uff0c\u5374\u906e\u62e6\u5f97\u751a\u4e3a\u4e25\u5bc6\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u770b\u4f60\u5982\u4f55\u653b\u5f97\u8fdb\u6765\u3002\u53ea\u8981\u7ed9\u6211\u5b9d\u5251\u7684\u950b\u8292\u6cbe\u4e0a\uff0c\u4f60 \u7684\u7af9\u5251\u5c31\u8981\u88ab\u524a\u4e3a\u4e24\u6bb5\u3002\u201d\u54ea\u77e5\u4ed6\u5251\u52bf\u65b9\u81ea\u6025\u653b\u6539\u4e3a\u56fa\u5b88\uff0c\u8f6c\u6362\u4e4b\u95f4\uff0c\u5251\u52bf\u7a0d\u6162\uff0c\u4e4c\u8499\u592b\u7af9 \u5251\u4e00\u4f38\uff0c\u642d\u4e0a\u4e86\u4ed6\u7684\u957f\u5251\u3002 \u3000\u3000\u5a04\u6850\u5b59\u6025\u5fd9\u8f6c\u52a8\u5251\u8eab\uff0c\u60f3\u524a\u65ad\u4ed6\u7684\u7af9\u5251\uff0c\u90a3\u67c4\u7af9\u5251\u7adf\u4f3c\u4e4e\u7c98\u5728\u4ed6\u7684\u5251\u4e0a\uff0c\u8f7b\u98d8\u98d8\u5730\u5168\u4e0d \u53d7\u529b\uff0c\u5a04\u6850\u5b59\u63a5\u8fde\u53d8\u4e86\u5341\u51e0\u79cd\u62db\u5f0f\uff0c\u603b\u662f\u6446\u8131\u4e0d\u5f00\u3002\u8fd9\u5c24\u4e0d\u5df2\uff0c\u7c98\u5728\u5251\u4e0a\u7684\u7af9\u5251\uff0c\u521d\u65f6\u672c\u5982 \u4e00\u5f20\u8584\u7eb8\uff0c\u7c7b\u6850\u5b59\u7684\u5251\u8eab\u5e76\u4e0d\u53d7\u529b\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u90a3\u7af9\u5251\u5374\u5ffd\u800c\u53d8\u5f97\u6c89\u91cd\u8d77\u6765\uff0c\u518d\u8fc7\u4e00\u4f1a\uff0c\u5a04 \u6850\u5b59\u7684\u5251\u5c16\u7adf\u4f3c\u633d\u4e86\u5343\u65a4\u91cd\u7269\u4e00\u6837\uff0c\u6e10\u6e10\u8fde\u62db\u5f0f\u4e5f\u65bd\u5c55\u4e0d\u5f00\uff01\u5a04\u6850\u5b59\u5927\u5927\u5403\u60ca\uff0c\u60f3\u4e0d\u5230\u654c\u4eba \u7684\u5185\u529b\u8fd0\u7528\u5f97\u5982\u6b64\u795e\u5999\uff01\u4e4c\u8499\u592b\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4f55\u8bdd\u8bf4\uff1f\u201d\u5a04\u6850\u5b59\u54ac\u7259\u8bf4\u9053\uff1a\u201c\u5b9d\u5251\u7ed9\u4f60\uff01\u201d \u731b\u5730\u5f80\u524d\u4e00\u9001\uff0c\u5b9d\u5251\u8131\u624b\u5411\u4e4c\u8499\u592b\u5fc3\u623f\u63d2\u53bb\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u4e0d\u7981\u60ca\u547c\uff0c\u53ea\u89c1\u4e4c\u8499\u592b\u7af9\u5251\u8f7b\u8f7b\u4e00\u5f15\uff0c\u4e00\u7728\u773c\uff0c\u90a3\u67c4\u5927\u5185\u5b9d\u5251\u4fbf\u5230\u4e86\u4e4c\u8499\u592b\u624b \u4e0a\uff0c\u8fde\u4e8e\u627f\u73e0\u4e5f\u770b\u4e0d\u6e05\u695a\u4ed6\u7528\u7684\u662f\u4ec0\u4e48\u624b\u6cd5\u3002\u5fc3\u4e2d\u5927\u4e3a\u53f9\u670d\uff1a\u4e16\u4e0a\u7adf\u6709\u8fd9\u6837\u5947\u5999\u7684\u6b66\u529f\u3002\u60f3 \u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4ed6\u80fd\u4e0e\u5e08\u7236\u9f50\u540d\u4e86\u3002\u201d\u5176\u5b9e\u9e1f\u8499\u672a\u7684\u8f88\u4efd\u6bd4\u5979\u7684\u5e08\u7236\u5f20\u4e39\u67ab\u8fd8\u9ad8\u4e00\u8f88\uff0c\u4e4c\u8499\u592b \u8fd9\u624b\u7af9\u5251\u514b\u654c\u7684\u529f\u592b\uff0c\u4e43\u662f\u4ece\u4ed6\u5e08\u7236\u7684\u597d\u53cb\uff0c\u5f53\u4eca\u8f88\u4efd\u6700\u9ad8\u7684\u5973\u4fa0\u8427\u97f5\u5170\u90a3\u91cc\u5b66\u6765\u7684\uff0c\u8427\u97f5 \u5170\u5f53\u5e74\u66fe\u7528\u4e00\u679d\u7af9\u5251\u548c\u8c22\u5929\u534e\u3001\u53f6\u76c8\u76c8\u7684\u53cc\u5251\u5408\u58c1\u6253\u6210\u5e73\u624b\uff08\u4e8b\u8be6\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\uff09\uff0c\u90a3\u66f4 \u662f\u5929\u4e0b\u7f55\u89c1\u7684\u6b66\u529f\u4e86\u3002 \u3000\u3000\u4e4c\u8499\u592b\u63a5\u5251\u5728\u624b\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u5a04\u6850\u5b59\u9762\u8272\u4e00\u6c89\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7528\u7af9\u5251\u593a\u5251\uff0c\u4f55\u8db3\u4e3a \u5947\uff1f\u770b\u6211\u7a7a\u624b\u593a\u4f60\u7684\u5b9d\u5251\uff01\u201d\u53cc\u638c\u4e00\u9519\uff0c\u6dec\u7136\u53d1\u62db\u3002\u4e8e\u627f\u73e0\u558a\u9053\uff1a\u201c\u5478\uff0c\u4e0d\u8981\u8138\uff01\u201d\u4e4c\u8499\u592b \u7b11\u9053\uff1a\u201c\u4e0d\u8ba9\u4ed6\u65bd\u5c55\u4ed6\u90a3\u70b9\u770b\u5bb6\u672c\u9886\uff0c\u4ed6\u8f93\u4e86\u4e5f\u4e0d\u7518\u5fc3\u3002\u597d\uff0c\u4e14\u89c1\u8bc6\u89c1\u8bc6\u4f60\u9e70\u722a\u95e8\u72ec\u6b65\u5929\u4e0b \u7684\u5206\u7b4b\u9519\u9aa8\u624b\u7684\u529f\u592b\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5a04\u6850\u5b59\u5df2\u662f\u72c2\u98ce\u66b4\u96e8\u822c\u5730\u63a5\u8fde\u653b\u4e86\u4e03\u516b\u62db\uff0c\u4e4c\u8499\u592b\u9053\uff1a \u201c\u54b1\u4eec\u5728\u638c\u6cd5\u4e0a\u518d\u6bd4\u5212\u6bd4\u5212\uff01\u201d\u5c06\u957f\u5251\u8854\u5728\u53e3\u4e2d\uff0c\u51dd\u795e\u63a5\u62db\uff0c\u628a\u7c7b\u6850\u5b59\u7684\u653b\u52bf\u4e00\u5316\u89e3\uff0c\u4e4c\u8499 \u592b\u662f\u5929\u4e0b\u77e5\u540d\u7684\u5251\u5ba2\uff0c\u4ed6\u5982\u4eca\u820d\u957f\u53d6\u77ed\uff0c\u90a3\u81ea\u7136\u662f\u660e\u8ba9\u5a04\u6850\u5b59\u4e86\u3002 \u3000\u3000\u5a04\u6850\u5b59\u4e00\u58f0\u4e0d\u54cd\uff0c\u53cc\u81c2\u7b95\u5f20\uff0c\u624b\u811a\u8d77\u5904\uff0c\u5168\u5e26\u52b2\u98ce\uff0c\u679c\u7136\u597d\u4e00\u6d3e\u7c97\u72b7\u51cc\u5389\u4e4b\u52bf\uff0c\u4e4c\u8499\u592b \u56db\u9762\u6e38\u8d70\uff0c\u4e0d\u8ba9\u4ed6\u8fd1\u8eab\u8089\u640f\uff0c\u4e00\u653b\u4e00\u5b88\uff0c\u8f6c\u773c\u95f4\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u5a04\u6850\u5b59\u5fc3\u4e2d\u70e6\u8e81\uff0c\u5927\u58f0\u559d \u9053\uff1a\u201c\u4e4c\u8499\u592b\u4f60\u4e0d\u6562\u63a5\u6211\u7684\u638c\uff0c\u8fd9\u6837\u6597\u6cd5\uff0c\u6597\u5230\u4f55\u65f6\uff1f\u201d\u4e4c\u8499\u592b\u7b11\u9053\uff1a\u201c\u6211\u8ba9\u4f60\u591a\u73a9\u4e00\u4f1a\uff0c \u4f60\u8fd8\u4e0d\u9886\u6211\u7684\u60c5\uff0c\u6211\u82e5\u8981\u6253\u5012\u4f60\uff0c\u4f55\u987b\u7528\u4e00\u638c\u4e4b\u529b\uff01\u201d\u4ed6\u53e3\u4e2d\u8854\u5251\uff0c\u58f0\u97f3\u4ece\u7259\u7f1d\u4e2d\u900f\u51fa\u6765\uff0c \u663e\u5f97\u8be1\u79d8\u4e4b\u6781\uff0c\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e0d\u77e5\u662f\u6709\u610f\u8fd8\u662f\u65e0\u610f\uff0c\u80f8\u524d\u7adf\u662f\u9732\u51fa\u7834\u7efd\uff0c\u53cc\u638c\u90fd\u4fa7\u5411\u4e00\u8fb9\uff0c\u5a04 \u6850\u5b59\u5927\u559d\u4e00\u58f0\uff1a\u201c\u7740\uff0c\u5de6\u638c\u4e00\u6258\uff0c\u53f3\u638c\u7a7f\u51fa\uff0c\u75be\u6293\u4e4c\u8499\u592b\u80c1\u4e0b\u7684\u90a3\u4e09\u6761\u8f6f\u9aa8\uff0c\u8fd9\u4e00\u62db\u6b63\u662f\u5206 \u7b4b\u9519\u9aa8\u624b\u4e2d\u6700\u5389\u5bb3\u7684\u4e00\u62db\u6740\u624b\uff0c\u82e5\u7136\u88ab\u4ed6\u7528\u4e0a\uff0c\u4e4c\u8499\u592b\u5fc5\u5c06\u7b4b\u65ad\u9aa8\u6298\uff0c\u7eb5\u6709\u591a\u597d\u529f\u592b\uff0c\u4e5f\u662f \u7ec8\u8eab\u6b8b\u5e9f\u7684\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u6b63\u81ea\u4e0d\u660e\u4e4c\u8499\u592b\u4f55\u4ee5\u5982\u6b64\u758f\u5931\u5927\u610f\uff0c\u5ffd\u89c1\u5a04\u6850\u5b59\u90a3\u4e00\u6293\uff0c\u6307\u5c16\u582a \u582a\u6cbe\u7740\u4e4c\u8499\u592b\u7684\u8863\u88f3\uff0c\u4e4c\u8499\u592b\u7a81\u7136\u53cd\u6307\u4e00\u5f39\uff0c\u59ff\u52bf\u7f8e\u5999\u4e4b\u6781\uff0c\u5a04\u4f97\u5b59\u4e00\u58f0\u60e8\u53eb\uff0c\u5012\u7eb5\u51fa\u6570\u4e08 \u4e4b\u5916\uff0c\u4e4c\u8499\u592b\u7b11\u9053\uff1a\u201c\u4f60\u5c45\u7136\u633a\u5f97\u4f4f\u6211\u7684\u4e00\u6307\u5f39\u529f\uff0c\u4e5f\u7b97\u96be\u5f97\uff0c\u9976\u4f60\u4e0d\u6b7b\uff0c\u56de\u53bb\u597d\u597d\u517b\u606f\u4e03 \u65e5\u5427\uff01\u5b9d\u5251\u62dc\u9886\u4e86\u3002\u201d\u5de6\u624b\u4e00\u4e3e\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u628a\u5251\u9798\uff0c\u539f\u6765\u4ed6\u77f3\u624b\u4f7f\u51fa\u4e00\u6307\u5f39\u529f\uff0c\u5de6\u624b\u4e5f \u5728\u540c\u4e00\u77ac\u95f4\uff0c\u6293\u5230\u4e86\u5a04\u6850\u5b59\u8170\u95f4\u60ac\u7740\u7684\u5251\u9798\uff0c\u4e24\u62db\u6700\u4e0a\u4e58\u7684\u6b66\u529f\u540c\u65f6\u4f7f\u51fa\uff0c\u5982\u6b64\u529f\u592b\uff0c\u5a04\u6850 \u5b59\u671b\u5c18\u83ab\u53ca\uff0c\u54ea\u91cc\u8fd8\u6562\u518d\u6597\u3002\u4e4c\u8499\u592b\u53d6\u4e0b\u5b9d\u5251\uff0c\u63d2\u5165\u5251\u9798\uff0c\u5a04\u6850\u5b59\u5df2\u9003\u5f97\u6ca1\u4e86\u5f71\u513f\u3002\u4e8e\u627f\u73e0 \u559c\u4e0d\u81ea\u80dc\uff0c\u8dd1\u4e0a\u524d\u53bb\u8fce\u63a5\u4e4c\u8499\u592b\u3002 \u3000\u3000\u201c\u91d1\u94a9\u4ed9\u5b50\u201d\u6797\u4ed9\u97f5\u62c9\u7740\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u957f\u5f97\u771f\u50cf\u5f53\u5e74\u7684\u4e91\u857e\uff0c\u4f60\u5e08\u6bcd\u5f53\u5e74\u4e5f\u662f\u5973\u626e\u7537 \u88c5\uff0c\u95ef\u8361\u6c5f\u6e56\uff0c\u548c\u4f60\u4e00\u6a21\u4e00\u6837\uff0c\u8499\u592b\uff0c\u4f60\u77a7\u4e39\u67ab\u6536\u7684\u5f92\u5f1f\u591a\u597d\uff0c\u54b1\u4eec\u53ef\u6ca1\u6709\u8fd9\u6837\u7684\u798f\u6c14\uff0c\u597d \u50cf\u5929\u4e0b\u867d\u5927\uff0c\u597d\u5f92\u5f1f\u90fd\u7ed9\u522b\u4eba\u62a2\u5149\u5566\u3002\u201d\u4e8e\u627f\u73e0\u5140\u662f\u60f3\u4e0d\u660e\u767d\uff0c\u4f55\u4ee5\u4ed6\u4eec\u4e00\u89c1\uff0c\u5c31\u77e5\u9053\u81ea\u5df1 \u662f\u5973\u626e\u7537\u88c5\u3002\u95ee\u9053\uff1a\u201c\u4e4c\u4f2f\u4f2f\u3001\u4f2f\u6bcd\uff0c\u6211\u7684\u5e08\u7236\u5e08\u6bcd\u5230\u82cd\u5c71\u53bb\uff0c\u4f60\u4eec\u89c1\u7740\u4e86\u6ca1\u6709\uff1f\u201d\u4e4c\u8499\u592b \u7b11\u9053\uff1a\u201c\u8981\u4e0d\u662f\u89c1\u4e86\u4f60\u7684\u5e08\u7236\uff0c\u6211\u8fd8\u4e0d\u4f1a\u5230\u6c5f\u5357\u6765\u5462\u3002\u55ef\uff0c\u4f60\u5e08\u6bcd\u542c\u8bf4\u4f60\u52a0\u5165\u4e86\u4e49\u519b\uff0c\u53c8\u662f \u6b22\u559c\uff0c\u53c8\u662f\u62c5\u5fe7\uff0c\u6015\u4f60\u5931\u4e8b\u3002\u54c8\uff0c\u60f3\u4e0d\u5230\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5df2\u5c3d\u5f97\u5e08\u95e8\u5fc3\u6cd5\uff0c\u8fde\u5a04\u6850\u5b59\u8fd9\u8001\u8d3c\u4e5f \u96be\u5948\u4f60\u4f55\uff0c\u771f\u662f\u53ef\u559c\u53ef\u8d3a\u3002\u201d\u6797\u4ed9\u97f5\u4e5f\u7b11\u9053\uff1a\u201c\u6211\u56de\u53bb\u548c\u4f60\u7684\u5e08\u7236\u5e08\u6bcd\u4e00\u8bf4\uff0c\u4fdd\u7ba1\u4e50\u574f\u4ed6 \u4eec\uff0c\u4f60\u5e08\u6bcd\u4e5f\u4e0d\u7528\u6302\u5fc3\u5566\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u817c\u8146\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u8981\u8d76\u5230\u4e91\u5357\u82cd\u5c71\u7ed9\u592a\u5e08\u7956\u62dc\u5bff\u3002\u201d\u4e4c\u8499\u592b\u9053\uff1a\u201c\u6211\u6b63\u60f3\u53bb \u627e\u77f3\u60ca\u6d9b\uff0c\u542c\u8bf4\u4ed6\u5728\u4e49\u519b\u4e4b\u4e2d\uff0c\u662f\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u4e4c\u8499\u592b\u7b11\u9053\uff1a\u201c\u6211\u548c\u4ed6\u7d20\u672a \u8c0b\u9762\uff0c\u8fd9\u56de\u5f20\u4e39\u67ab\u53eb\u6211\u5230\u6c5f\u5357\u627e\u4ed6\uff0c\u78b0\u5de7\u6211\u800c\u4eca\u7ed9\u4ed6\u8ffd\u56de\u5b9d\u5251\uff0c\u6b63\u597d\u4f5c\u4e2a\u89c1\u9762\u4e4b\u793c\u3002\u5c31\u70e6\u4f60 \u7ed9\u6211\u5f15\u89c1\u5982\u4f55\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u77f3\u8001\u524d\u8f88\u53ea\u6015\u5982\u4eca\u5df2\u4e0d\u5728\u8fd9\u513f\uff0c\u6211\u731c\u4ed6\u662f\u56de\u5230\u53f0\u5dde\u8001\u5bb6\u53bb \u5566\u3002\u201d\u4e4c\u8499\u592b\u9053\uff1a\u201c\u4ed6\u7684\u5b9d\u5251\u600e\u4e48\u4f1a\u5230\u4e86\u5a04\u6850\u5b59\u7684\u624b\u4e2d\uff1f\u201d\u4e8e\u627f\u73e0\u5c06\u6628\u665a\u4e4b\u4e8b\u7ea6\u7565\u8bf4\u4e86\u4e00 \u904d\uff0c\u4e4c\u8499\u592b\u7b11\u9053\uff1a\u201c\u6211\u9053\u4ee5\u5a04\u6850\u5b59\u90a3\u70b9\u5fae\u672a\u4e4b\u6280\uff0c\u4ed6\u600e\u6562\u53bb\u62a2\u77f3\u60ca\u6d9b\u7684\u5b9d\u5251\uff0c\u539f\u6765\u5982\u6b64\u3002\u6628 \u665a\u6211\u5c31\u662f\u56e0\u4e3a\u4e0d\u660e\u4ed6\u7684\u6765\u5386\uff0c\u60f3\u628a\u4ed6\u8d76\u56de\u5230\u4e49\u519b\u7684\u8425\u5730\uff0c\u67e5\u95ee\u4e2a\u660e\u767d\uff0c\u518d\u4f5c\u5904\u7f6e\u3002\u5e78\u4e8f\u9047\u89c1 \u4e86\u4f60\uff0c\u77f3\u60ca\u6d9b\u65e2\u4e0d\u5728\u6b64\uff0c\u6211\u4e5f\u8d39\u4e8b\u5728\u6b64\u803d\u6401\u5566\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u82e6\u6709\u6240\u601d\uff0c\u5ffd\u9053\uff1a\u201c\u4e4c\u4f2f\u4f2f\uff0c\u4f60\u7ed9\u4ed6\u9001\u56de\u8fd9\u628a\u5b9d\u5251\uff0c\u4ed6\u5b9a\u7136\u4e0d\u53d7\uff0c\u5f92\u60f9\u4ed6\u7684\u4f24 \u5fc3\u3002\u201d\u4e4c\u8499\u592b\u9053\uff1a\u201c\u90a3\u600e\u4e48\u529e\uff1f\u8fd9\u6837\u7684\u795e\u7269\u5229\u5668\u603b\u8be5\u6709\u4e2a\u4e3b\u513f\uff0c\u6211\u5374\u4e0d\u80fd\u8981\u4ed6\u7684\u3002\u201d\u4e8e\u627f\u73e0 \u9053\uff1a\u201c\u4f60\u7ed9\u6211\u5427\uff0c\u6211\u7ed9\u4f60\u4ea4\u7ed9\u59a5\u5f53\u7684\u4eba\u3002\u201d\u4e4c\u8499\u592b\u9053\uff1a\u201c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u4f46\u542c\u4f60\u6240\u8bf4\uff0c\u4ed6\u7684 \u90a3\u4e2a\u4ec0\u4e48\u59d3\u94c1\u7684\u5f92\u5f1f\u4e5f\u4e0d\u914d\u6709\u8fd9\u4e00\u628a\u5b9d\u5251\u3002\u201d\u4e8e\u627f\u73e0\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4ea4\u7ed9\u4ed6\u3002\u201d \u3000\u3000\u4e4c\u8499\u592b\u5c06\u5b9d\u5251\u4ea4\u7ed9\u4e8e\u627f\u73e0\uff0c\u5bf9\u6797\u4ed9\u97f5\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u8d81\u65e9\u8d70\u5427\uff0c\u5148\u627e\u77f3\u60ca\u6d9b\uff0c\u518d\u5bfb\u9633\u5b97 \u6d77\uff0c\u628a\u4e8b\u60c5\u529e\u597d\uff0c\u514d\u5f97\u8bef\u4e86\u56de\u53bb\u7ed9\u7384\u673a\u524d\u8f88\u62dc\u5bff\u4e4b\u671f\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u8981 \u53bb\u627e\u9633\u5b97\u6d77\u3002\u201d\u4e4c\u8499\u592b\u9053\uff1a\u201c\u662f\u5440\uff0c\u77f3\u3001\u9633\u4e8c\u4eba\u548c\u6211\u4eec\u540c\u79f0\u56db\u5927\u5251\u5ba2\uff0c\u5728\u6211\u6765\u8bf4\uff0c\u90a3\u662f\u6b66\u6797 \u670b\u53cb\u7ed9\u6211\u9762\u4e0a\u8d34\u91d1\uff0c\u4f46\u4ed6\u4eec\u53ef\u662f\u540d\u5b9e\u76f8\u526f\uff0c\u6211\u4e5f\u8be5\u89c1\u89c1\u4ed6\u4eec\u5440\u3002\u201d\u4e8e\u627f\u73e0\u5c0f\u5634\u4e00\u6487\uff0c\u9053\uff1a \u201c\u9633\u5b97\u6d77\u624d\u4e0d\u914d\u548c\u4f60\u4eec\u5e76\u79f0\u56db\u5927\u5251\u5ba2\u3002\u201d\u4e4c\u8499\u592b\u9053\uff1a\u201c\u662f\u4e48\uff1f\u4f60\u548c\u4ed6\u4ea4\u8fc7\u624b\u4e86\uff1f\u201d\u4e8e\u627f\u73e0 \u9053\uff1a\u201c\u636e\u6211\u770b\u6765\uff0c\u4ed6\u7684\u6b66\u529f\u4e0e\u5a04\u6850\u5b59\u4e0d\u8fc7\u5728\u4f2f\u4ef2\u4e4b\u95f4\u3002\u201d\u4e4c\u8499\u592b\u9762\u8272\u6c89\u91cd\uff0c\u9053\uff1a\u201c\u5982\u6b64\u8bf4 \u6765\uff0c\u8fd9\u4e8b\u60c5\u5012\u68d8\u624b\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u5947\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u8bf4\uff1f\u201d\u4e4c\u8499\u592b\u9053\uff1a\u201c\u4ed6\u7684\u6b66\u529f\u5df2\u7ecf\u5982\u6b64\uff0c\u4ed6 \u80cc\u540e\u7684\u4eba\u7269\u5389\u5bb3\u53ef\u77e5\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u96be\u9053\u8fd8\u6709\u4ec0\u4e48\u4eba\u80fd\u5f3a\u8fc7\u4e0a\u5b98\u524d\u8f88\u4e0d\u6210\uff1f\u201d\u4e4c\u8499\u592b\u7b11\u9053\uff1a\u201c\u5929\u5916\u6709\u5929\uff0c\u4eba\u5916 \u6709\u4eba\uff0c\u8fd9\u8bdd\u4e5f\u5f88\u96be\u8bf4\u3002\u9633\u5b97\u6d77\u662f\u8d64\u57ce\u6d3e\u7b2c\u4e8c\u4ee3\u4e2d\u6709\u5934\u9762\u7684\u4eba\u7269\uff0c\u4ed6\u6562\u80e1\u4f5c\u975e\u4e3a\uff0c\u81ea\u7136\u662f\u6709\u6240 \u6043\u7684\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u8d77\u5e08\u7236\u66fe\u548c\u5979\u63d0\u8fc7\u8d64\u57ce\u6d3e\u7684\u521b\u6d3e\u7956\u5e08\u8d64\u57ce\u5b50\uff0c\u66fe\u8bf4\u8d64\u57ce\u5b50\u4e5f \u662f\u4e00\u4e2a\u6b66\u6797\u602a\u6770\uff0c\u66fe\u5148\u540e\u4e09\u6b21\u62dc\u8bbf\u8fc7\u81ea\u5df1\u7684\u592a\u5e08\u7956\u7384\u673a\u9038\u58eb\uff0c\u6bcf\u4e00\u6b21\u7384\u673a\u9038\u58eb\u90fd\u8bf7\u4ed6\u5230\u9759\u5ba4 \u4e4b\u4e2d\uff0c\u7b2c\u4e00\u6b21\u7559\u4e86\u4e00\u65e5\uff0c\u7b2c\u4e8c\u6b21\u4e24\u65e5\uff0c\u7b2c\u4e09\u6b21\u4e09\u65e5\uff0c\u5f53\u65f6\u7384\u673a\u9038\u58eb\u95e8\u4e0b\uff0c\u8fd8\u53ea\u6709\u8463\u5cb3\u4e00\u4eba\uff0c \u5949\u547d\u5b88\u5728\u95e8\u5916\uff0c\u4e0d\u51c6\u65c1\u4eba\u8fdb\u6765\u5e72\u6270\uff0c\u8fde\u8463\u5cb3\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec\u4e8c\u4eba\u5728\u91cc\u9762\u505a\u4ec0\u4e48\uff0c\u82e5\u8bf4\u662f\u8f83\u91cf\u6b66 \u529f\uff0c\u5374\u53c8\u4e1d\u6beb\u4e0d\u95fb\u52a8\u62f3\u811a\u7684\u58f0\u606f\uff0c\u53ea\u662f\u6bcf\u4e00\u6b21\u8d64\u57ce\u5b50\u8d70\u65f6\uff0c\u90fd\u9732\u51fa\u5782\u5934\u4e27\u6c14\u7684\u6837\u5b50\uff0c\u8fc7\u4e86\u4e09 \u6b21\u4e4b\u540e\uff0c\u5c31\u518d\u4e5f\u4e0d\u6765\u4e86\u3002\u6700\u540e\u90a3\u6b21\uff0c\u4e24\u4eba\u5446\u5728\u9759\u5ba4\u4e4b\u4e2d\u4e09\u65e5\uff0c\u5927\u5bb6\u90fd\u662f\u6ef4\u6c34\u4e0d\u8fdb\uff0c\u53ea\u662f\u8fd9\u4e00 \u4efd\u529f\u592b\uff0c\u5c31\u8db3\u4ee5\u60ca\u4e16\u9a87\u4fd7\u3002\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u83ab\u975e\u4e4c\u4f2f\u4f2f\u6240\u8bf4\u7684\uff0c\u9633\u5b97\u6d77\u80cc\u540e\u7684\u5389\u5bb3\u4eba\u7269\uff0c\u5c31 \u662f\u8d64\u57ce\u5b50\u4e0d\u6210\uff1f\u201d\u4f46\u89c1\u4e4c\u8499\u592b\u884c\u8272\u5306\u5306\uff0c\u81ea\u5df1\u53c8\u5fc3\u4e2d\u6709\u4e8b\uff0c\u4e0d\u4fbf\u518d\u8be6\u7ec6\u67e5\u95ee\u3002 \u3000\u3000\u4e4c\u8499\u592b\u592b\u5987\u8d70\u540e\uff0c\u4e8e\u627f\u73e0\u6367\u8d77\u90a3\u628a\u5927\u5185\u5b9d\u5251\uff0c\u5251\u67c4\u4e0a\u643a\u6709\u201c\u7d2b\u8679\u201d\u4e8c\u5b57\uff0c\u5323\u4e2d\u9690\u9690\u9732\u51fa \u6de1\u6de1\u7684\u7d2b\u8272\u5149\u8292\uff0c\u4e8e\u627f\u73e0\u60f3\u8d77\u6628\u665a\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u4e0d\u80dc\u6168\u53f9\u3002\u8fd9\u65f6\u5929\u8272\u5df2\u662f\u5927\u767d\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u4e00 \u8f6e\u65ed\u65e5\u597d\u50cf\u4ece\u6d77\u4e2d\u5347\u8d77\uff0c\u6d77\u9762\u4e0a\u91d1\u971e\u4e07\u9053\uff0c\u4e3d\u5f69\u971e\u8f89\uff0c\u8000\u773c\u751f\u8f89\u3002\u4e49\u519b\u7684\u8425\u5730\u5df2\u54cd\u8d77\u6668\u64cd\u7684 \u53f7\u89d2\uff0c\u4e8e\u627f\u73e0\u6025\u5fd9\u8d76\u8def\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\u9a6c\u8e44\u75be\u54cd\uff0c\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u4e00\u53cc\u9752\u5e74\u7537\u5973\uff0c\u98de\u9a6c\u8d76\u6765\uff0c \u7537\u7684\u662f\u6210\u6d77\u5c71\uff0c\u5973\u7684\u662f\u77f3\u6587\u7ea8\u3002\u4e8e\u627f\u73e0\u89c1\u4e0d\u662f\u53f6\u5b97\u7559\u548c\u6bd5\u64ce\u5929\uff0c\u5fc3\u4e2d\u4e00\u677e\uff0c\u8f6c\u8eab\u8fce\u63a5\u4ed6\u4eec\u3002 \u3000\u3000\u53ea\u542c\u5f97\u77f3\u6587\u7ea8\u56b7\u9053\uff1a\u201c\u6211\u8bf4\u8fd9\u5c0f\u5b50\u4e0d\u662f\u597d\u4eba\uff0c\u5e08\u54e5\uff0c\u4f60\u8fd8\u4e0d\u4fe1\uff1f\u568e\uff0c\u4f60\u4e3a\u4ec0\u4e48\u79c1\u81ea\u9003 \u8d70\uff1f\u201d\u8bf4\u5230\u540e\u9762\u8fd9\u53e5\u8bdd\u65f6\uff0c\u4e8e\u627f\u73e0\u5df2\u8d70\u5230\u4e86\u5979\u7684\u9762\u524d\uff0c\u5979\u8fd9\u53e5\u8bdd\u662f\u5411\u4e8e\u627f\u73e0\u559d\u95ee\u7684\uff0c\u4e8e\u627f\u73e0 \u68d2\u8d77\u5b9d\u5251\uff0c\u51c4\u7136\u4e00\u7b11\uff0c\u4e07\u8bed\u5343\u8a00\uff0c\u6b63\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\uff0c\u5ffd\u89c1\u77f3\u6587\u7ea8\u4f3c\u4e4e\u6014\u4e86\u4e00\u6014\uff0c\u5446\u5446\u7684\u770b\u7740 \u81ea\u5df1\uff0c\u7a81\u7136\u56b7\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u662f\u4e00\u4e2a\u5973\u7684\uff1f\u4e8e\u627f\u73e0\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u81ea\u89c9\u5730\u968f\u7740\u5979\u7684\u76ee\u5149\u6240\u6ce8\uff0c \u4e00\u63a0\u4e91\u53d1\uff0c\u5374\u539f\u6765\u81ea\u5df1\u7684\u5934\u5dfe\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u88c2\u4e86\u4e00\u89d2\uff0c\u79c0\u53d1\u9732\u4e86\u51fa\u6765\uff0c\u4e0d\u77e5\u662f\u7ed9\u5a04\u6850\u5b59\u6293 \u88c2\u7684\uff0c\u8fd8\u662f\u5728\u77f3\u7f1d\u4e2d\u8dc3\u51fa\u4e4b\u65f6\u7ed9\u52fe\u7834\u7684\uff0c\u4e8e\u627f\u73e0\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u4e4c\u8499\u592b\u4e3a\u4ec0\u4e48\u4e00\u773c\u5c31\u770b\u7834\u5979 \u5973\u626e\u7537\u88c5\uff0c\u800c\u77f3\u6587\u7ea8\u4e5f\u662f\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u4ee5\u524d\u602a\u4ed6\u8f7b\u8584\u662f\u602a\u9519\u4eba\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u8fd9\u628a\u5251\u4f60\u62ff\u53bb\uff01\u201d\u77f3\u6587\u7ea8\u60ca\u8be7\u4e4b\u6781\uff0c\u987e\u4e0d\u5f97\u9053\u95ee\u4e8e\u627f\u73e0 \u662f\u7537\u662f\u5973\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u6211\u7239\u7239\u7684\u5251\u600e\u4e48\u5230\u4e86\u4f60\u7684\u624b\u4e2d\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u95ee\uff0c\u8fd9\u628a\u5251 \u4f60\u53ea\u7ba1\u6536\u4e0b\uff0c\u5f53\u4f5c\u662f\u6211\u8f6c\u9001\u4f60\u7684\u597d\u4e86\u3002\u4f60\u7239\u7239\u73b0\u5728\u4f24\u5fc3\u4e4b\u6781\uff0c\u6b63\u8981\u4f60\u5728\u8eab\u65c1\u6170\u89e3\u3002\u4f60\u5feb\u56de\u5bb6 \u53bb\u770b\u4ed6\u5427\uff0c\u6211\u4e5f\u8981\u8d70\u4e86\u3002\u6587\u7ea8\u59b9\u59b9\uff0c\u4f60\u8981\u597d\u597d\u4f8d\u5949\u4ed6\u8001\u4eba\u5bb6\uff0c\u529d\u4ed6\u5f00\u6000\u554a\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u8bda\u631a\u975e\u5e38\uff0c\u771f\u60c5\u6bd5\u9732\uff0c\u6709\u5982\u81ea\u5df1\u4e5f\u662f\u77f3\u60ca\u6d9b\u7684\u5973\u513f\u4e00\u6837\uff0c\u77f3\u6587\u7ea8\u8038 \u7136\u52a8\u5bb9\uff0c\u5bf9\u4e8e\u627f\u73e0\u518d\u65e0\u534a\u70b9\u6000\u7591\u3002\u5979\u601d\u5ff5\u8001\u7236\uff0c\u5fc3\u4e2d\u5982\u711a\uff0c\u63a5\u8fc7\u5b9d\u5251\uff0c\u9053\u58f0\uff1a\u201c\u591a\u8c22\uff01\u201d\u6025 \u6025\u5fd9\u5fd9\u4e0e\u6210\u6d77\u5c71\u7b56\u9a6c\u98de\u9a70\uff0c\u5e76\u8f94\u800c\u53bb\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u76ee\u9001\u9a6c\u8e44\u626c\u5c18\uff0c\u4eba\u5f71\u6d88\u901d\uff0c\u5e7d\u5e7d\u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5c0f\u59d0\u5012\u6709\u773c\u5149\uff0c\u6210\u6d77\u5c71\u7684 \u8d28\u6734\u5b9e\u80dc\u8fc7\u4ed6\u7684\u5e08\u5144\uff01\u201d\u6210\u6d77\u5c71\u7684\u6837\u5b50\u770b\u6765\u7b28\u5934\u7b28\u8111\uff0c\u4e0e\u94c1\u955c\u5fc3\u7684\u6f47\u6d12\u806a\u660e\u76f8\u6bd4\uff0c\u4e0d\u557b\u5929\u6e0a \u4e4b\u522b\uff0c\u4e8e\u627f\u73e0\u4ee5\u524d\u66fe\u5bf9\u77f3\u6587\u7ea8\u4e4b\u9009\u62e9\u6210\u6d77\u5c71\u5927\u60d1\u4e0d\u89e3\uff0c\u5982\u4eca\u60f3\u6765\uff0c\u4e0d\u7981\u9eef\u7136\u81ea\u4f24\u3002\u4f46\u89c9\u8fc7\u53bb \u4e0e\u94c1\u955c\u5fc3\u76f8\u5904\u7684\u51e0\u4e2a\u6708\u6709\u5982\u4e00\u573a\u68a6\u5883\u3002 \u3000\u3000\u731b\u4e00\u62ac\u5934\uff0c\u53ea\u89c1\u7ea2\u65e5\u4e1c\u5347\uff0c\u6d77\u6ce2\u5982\u955c\uff0c\u662f\u4e00\u4e2a\u5927\u597d\u7684\u6674\u5929\uff0c\u5927\u6d77\u6781\u76ee\u65e0\u8fb9\uff0c\u6d77\u4e0a\u7684\u5929 \u7a7a\uff0c\u4e5f\u663e\u5f97\u7279\u522b\u851a\u84dd\uff0c\u4ee4\u4eba\u5fc3\u80f8\u5f00\u9614\u660e\u51c0\uff0c\u84dd\u5929\u767d\u4e91\u4e4b\u4e0a\uff0c\u6d77\u71d5\u98de\u7fd4\uff0c\u4e8e\u627f\u73e0\u6296\u843d\u8eab\u4e0a\u7684\u6ce5 \u5c18\uff0c\u731b\u7136\u95f4\u5fc3\u60c5\u8f7b\u5feb\uff0c\u4f3c\u51b2\u6ce2\u7a7f\u4e91\u7684\u6d77\u71d5\uff0c\u5934\u4e5f\u4e0d\u56de\u5730\u5411\u524d\u8d70\u4e86\u3002 \u3000\u3000\u6570\u65e5\u4e4b\u540e\uff0c\u5979\u6e21\u8fc7\u957f\u6c5f\uff0c\u8239\u5230\u4e2d\u6d41\uff0c\u4ecd\u4e0d\u81ea\u7981\u5730\u60f3\u8d77\u4e0e\u94c1\u955c\u5fc3\u521d\u4f1a\u7684\u60c5\u666f\uff0c\u4f46\u8fd9\u4e9b\u524d\u4e0a \u5f80\u4e8b\uff0c\u4e5f\u53ea\u662f\u4e00\u95ea\u5373\u8fc7\uff0c\u597d\u50cf\u968f\u7740\u5927\u6c5f\u4e1c\u901d\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7684\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\u5f53\u65e5\u56e0\u4e3a\u6e21\u6c5f\u4e0d\u4fbf\uff0c\u5bc4\u517b\u5728\u957f\u6c5f\u5cb8\u8fb9\u7684\u5f20\u9ed1\u5bb6\u4e2d\u3002\u4e8e\u627f\u73e0\u6e21\u6c5f \u4e4b\u540e\uff0c\u7b2c\u4e00\u4ef6\u4e8b\u5c31\u662f\u5230\u5f20\u9ed1\u5bb6\u4e2d\u53bb\u53d6\u56de\u81ea\u5df1\u7684\u5b9d\u9a6c\uff0c\u5f20\u9ed1\u7684\u5bb6\u4eba\u5bf9\u8fd9\u5339\u9a6c\u7167\u6599\u5f97\u975e\u5e38\u5468\u5230\uff0c \u517b\u606f\u51e0\u6708\uff0c\u6bd4\u524d\u66f4\u52a0\u795e\u9a8f\u4e86\uff0c\u89c1\u7740\u4e3b\u4eba\uff0c\u6b22\u5636\u4e0d\u5df2\uff0c\u4e8e\u627f\u73e0\u53c8\u4e0d\u7981\u6697\u751f\u611f\u6168\uff0c\u60f3\u8d77\u81ea\u5df1\uff0c\u81ea \u79bb\u5f00\u5e08\u95e8\u4e4b\u540e\uff0c\u867d\u7136\u8ba4\u8bc6\u4e86\u4e0d\u5c11\u4eba\uff0c\u4f46\u6700\u8981\u597d\u7684\u670b\u53cb\uff0c\u8fd8\u662f\u8fd9\u5339\u767d\u9a6c\u3002 \u3000\u3000\u5f20\u9ed1\u7684\u5bb6\u4eba\u7eb7\u7eb7\u63a2\u95ee\u6297\u502d\u7684\u6d88\u606f\uff0c\u542c\u5f97\u4e8e\u627f\u73e0\u8bf4\u502d\u5bc7\u5df2\u88ab\u9a71\u9010\u4e0b\u6d77\uff0c\u5f20\u9ed1\u4e0d\u65e5\u4e5f\u5c06\u56de \u6765\uff0c\u6b22\u58f0\u96f7\u52a8\uff0c\u7eb7\u7eb7\u5938\u8d5e\u6297\u502d\u7684\u82f1\u96c4\uff0c\u5bf9\u4e8e\u627f\u73e0\u66f4\u662f\u8d5e\u626c\u5907\u81f3\u3002\u4e8e\u627f\u73e0\u53c8\u662f\u60ed\u6127\uff0c\u53c8\u662f\u5174 \u594b\uff0c\u60f3\u8d77\u8fd9\u51e0\u4e2a\u6708\u706b\u70ed\u7684\u751f\u6d3b\uff0c\u60f3\u8d77\u90a3\u4e9b\u6fc0\u52a8\u5fc3\u5f26\uff0c\u6c38\u4e0d\u80fd\u5fd8\u7684\u6218\u6597\uff0c\u867d\u7136\u8fd9\u4e00\u6b21\u5728\u5979\u5fc3\u4e0a \u7559\u4e0b\u7684\u521b\u75d5\u4e5f\u6c38\u4e0d\u80fd\u78e8\u706d\uff0c\u4f46\u5979\u5374\u7edd\u4e0d\u540e\u6094\u6b64\u884c\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5728\u5f20\u9ed1\u5bb6\u4f4f\u4e86\u4e00\u5929\uff0c\u7b2c\u4e8c\u65e5\u4fbf\u7b56\u9a6c\u897f\u884c\uff0c\u79bb\u5f00\u4e86\u6c5f\u5357\u7684\u5c71\u660e\u6c34\u79c0\u4e4b\u4e61\uff0c\u7ecf\u8fc7\u4e86\u4e00 \u4e2a\u591a\u6708\u7684\u65c5\u7a0b\uff0c\u8fdb\u5165\u4e86\u897f\u5357\u7684\u4e18\u9675\u5c71\u533a\uff0c\u98ce\u666f\u8fe5\u7136\u4e0d\u540c\uff0c\u82e5\u628a\u6c5f\u5357\u6bd4\u505a\u660e\u5a9a\u52a8\u4eba\u7684\u5c11\u5973\uff0c\u5219 \u897f\u5357\u5e94\u662f\u8d28\u6734\u8c6a\u65f7\u7684\u7537\u513f\u3002\u4e8e\u627f\u73e0\u5fc3\u4e2d\u5ffd\u7136\u6709\u4e00\u4e2a\u5947\u602a\u7684\u8054\u60f3\uff1a\u94c1\u955c\u5fc3\u4f3c\u662f\u6c5f\u5357\u56ed\u6797\u4e2d\u7684\u7261 \u4e39\uff0c\u800c\u53f6\u5b97\u7559\u7b49\u4e49\u519b\u7684\u9996\u9886\u5219\u4f3c\u4e91\u8d35\u9ad8\u539f\u4e0a\u7684\u677e\u6749\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u53d6\u9053\u8d35\u5dde\u524d\u5f80\u4e91\u5357\uff0c\u5230\u4e86\u8d35\u5dde\uff0c\u5c71\u5cad\u66f4\u591a\uff0c\u5230\u5904\u90fd\u662f\u7ef5\u4e98\u5cfb\u5ced\u7684\u5cf0\u5ce6\uff0c\u5230\u5904\u90fd\u662f \u8471\u90c1\u8302\u5bc6\u7684\u677e\u6797\uff0c\u5c71\u5cad\u4e0a\u968f\u5904\u53ef\u95fb\u82d7\u65cf\u5987\u5973\u7684\u5c71\u6b4c\uff0c\u5065\u7855\u7684\u82d7\u65cf\u59d1\u5a18\u50cf\u7537\u4eba\u4e00\u6837\u5728\u5c71\u95f4\u64cd \u4f5c\uff0c\u4e0e\u6c5f\u5357\u8db3\u4e0d\u51fa\u95e8\u7684\u95fa\u79c0\uff0c\u5927\u4e0d\u76f8\u7c7b\u3002\u4e8e\u627f\u73e0\u5e74\u6765\u5973\u626e\u91cc\u88c5\uff0c\u603b\u6709\u62d8\u675f\u4e4b\u611f\uff0c\u5230\u4e86\u8d35\u5dde\u4e4b \u540e\uff0c\u89c1\u7537\u5973\u90fd\u662f\u4e00\u6837\u64cd\u4f5c\uff0c\u4fbf\u7d22\u6027\u56de\u590d\u4e86\u5973\u513f\u8eab\u4efd\uff0c\u6536\u8d77\u4e86\u7537\u5b50\u7684\u8863\u88c5\u3002 \u3000\u3000\u82d7\u4eba\u6700\u4e3a\u559c\u5ba2\uff0c\u5c71\u8def\u8fb9\u7684\u51c9\u4ead\u5e38\u5e38\u653e\u7740\u4ece\u5c71\u4e0b\u6311\u6765\u7684\u6cc9\u6c34\uff0c\u8fd8\u653e\u7740\u8349\u978b\uff0c\u8ba9\u8fc7\u8def\u7684\u65c5\u4eba \u53e3\u6e34\u4e86\u53ef\u996e\u6e05\u51c9\u7684\u6cc9\u6c34\uff0c\u978b\u7834\u4e86\u53ef\u6362\u5408\u9002\u7684\u8349\u978b\u3002\u7eb5\u662f\u6700\u7a77\u7684\u4eba\u5bb6\uff0c\u6709\u964c\u751f\u7684\u65c5\u4eba\u6295\u5bbf\uff0c\u4ed6 \u4eec\u4e5f\u5949\u5982\u8d35\u5bbe\uff0c\u6089\u5fc3\u7167\u6599\uff0c\u5bb6\u4e2d\u6ca1\u6709\u5403\u7684\u4e5f\u4f1a\u5230\u5916\u9762\u5f20\u7f57\uff0c\u52a1\u5fc5\u4ee4\u5230\u5ba2\u4eba\u79f0\u5fc3\u6ee1\u610f\u4e3a\u6b62\uff0c\u6240 \u4ee5\u4e8e\u627f\u73e0\u4ee5\u4e00\u4e2a\u5b64\u8eab\u5c11\u5973\uff0c\u901a\u8fc7\u5c71\u5ce6\u91cd\u53e0\u7684\u82d7\u533a\uff0c\u5374\u4e5f\u6ca1\u6709\u611f\u5230\u4ec0\u4e48\u4e0d\u4fbf\u3002 \u3000\u3000\u5728\u82d7\u533a\u8d70\u4e86\u534a\u6708\uff0c\u5230\u4e86\u8d35\u5dde\u897f\u90e8\u7684\u91ce\u9a6c\u5ddd\uff0c\u5927\u7ea6\u8fd8\u6709\u516d\u4e03\u65e5\u8def\u7a0b\uff0c\u5c31\u53ef\u4ee5\u7a7f\u8fc7\u82d7\u533a\uff0c\u8fdb \u5165\u4e91\u5357\u8fb9\u5883\u4e86\u3002\u8fd9\u4e00\u665a\u4e8e\u627f\u73e0\u5728\u5c71\u8fb9\u4e00\u5bb6\u82d7\u5bb6\u6295\u5bbf\uff0c\u8fd9\u4e00\u5bb6\u82d7\u5bb6\u672c\u6709\u6bcd\u5b50\u4e8c\u4eba\uff0c\u513f\u5b50\u5230\u571f\u53f8 \u5bb6\u6267\u5f79\u53bb\u4e86\u3002\u5bb6\u4e2d\u53ea\u5269\u4e0b\u8001\u5927\u5a18\u4e00\u4eba\uff0c\u5bf9\u4e8e\u627f\u73e0\u6bb7\u52e4\u62db\u5f85\uff0c\u4e3a\u5979\u6740\u4e86\u5bb6\u4e2d\u4ec5\u6709\u7684\u4e00\u53ea\u8001\u6bcd \u9e21\uff0c\u4e8e\u627f\u73e0\u8fc7\u610f\u4e0d\u53bb\u3002\u5e2e\u5979\u6dd8\u7c73\u716e\u996d\u3002 \u3000\u3000\u9ed4\u897f\u6c49\u82d7\u6742\u5904\uff0c\u82d7\u4eba\u591a\u61c2\u5f97\u6c49\u8bed\uff0c\u8fd9\u4f4d\u8001\u5927\u5a18\u8bf4\u5f97\u867d\u7136\u4e0d\u5927\u6d41\u7545\uff0c\u5f7c\u6b64\u5374\u4e5f\u80fd\u591f\u4ea4\u8c08\u3002 \u5403\u8fc7\u665a\u996d\u4e4b\u540e\uff0c\u4e24\u4eba\u5750\u5728\u95e8\u5916\u7684\u5927\u6811\u4e0b\u95f2\u8bdd\u5bb6\u5e38\uff0c\u8fd9\u4f4d\u8001\u5927\u5a18\u975e\u5e38\u6b22\u559c\u4e8e\u627f\u73e0\uff0c\u62c9\u7740\u5979\u7684\u624b \u4e0d\u4f4f\u5730\u8d5e\u53f9\uff1a\u201c\u6211\u4e5f\u66fe\u89c1\u8fc7\u8bb8\u591a\u6c49\u4eba\u59d1\u5a18\uff0c\u53ea\u6709\u4f60\u6bd4\u6211\u4eec\u82d7\u65cf\u6700\u7f8e\u7684\u59d1\u5a18\u8fd8\u7f8e\uff0c\u8fd9\u53cc\u624b\u600e\u4e48 \u957f\u5f97\u8fd9\u6837\u767d\u53c8\u8fd9\u6837\u5ae9\uff0c\u5c31\u50cf\u9f13\u513f\u8bcd\u91cc\u9762\u6240\u6b4c\u5531\u7684\u516c\u4e3b\u4e00\u822c\u3002\u201d\u4e8e\u627f\u73e0\u88ab\u5979\u4e00\u8d5e\uff0c\u53cd\u800c\u89c9\u5f97\u6709 \u4e9b\u60ed\u6127\uff0c\u7b11\u9053\uff1a\u201c\u6211\u54ea\u513f\u6bd4\u5f97\u4e0a\u4f60\u4eec\u82d7\u65cf\u7684\u59d1\u5a18\uff0c\u4f60\u4eec\u7684\u59d1\u5a18\u90a3\u53cc\u624b\u624d\u771f\u662f\u80fd\u5e72\u5462\uff0c\u53c8\u4f1a\u505a \u996d\uff0c\u53c8\u4f1a\u79cd\u5730\uff0c\u8fd8\u4f1a\u7ee3\u82b1\uff0c\u6211\u624d\u771f\u662f\u7fa1\u6155\u5f97\u4e0d\u5f97\u4e86\u3002\u201d\u8001\u5927\u5a18\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u7b11\u8bdd\u6211 \u4eec\u547d\u82e6\uff0c\u771f\u662f\u96be\u5f97\u3002\u201d\u62c9\u7740\u4e8e\u627f\u73e0\u7684\u624b\u95ee\u9053\uff1a\u201c\u4f60\u4eca\u5e74\u51e0\u5c81\u5566\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5341\u516d\u5c81 \u5566\u3002\u201d\u8001\u5927\u5a18\u9053\uff1a\u201c\u6709\u5a46\u5bb6\u6ca1\u6709\uff1f\u201d\u4e8e\u627f\u73e0\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d\u8001\u5927\u5a18\u9053\uff1a\u201c\u6211\u4eec\u8fd9 \u91cc\u7684\u59d1\u5a18\u4e03\u516b\u5c81\uff0c\u5f88\u5c11\u6ca1\u6709\u5a46\u5bb6\u7684\uff0c\u5c24\u5176\u50cf\u4f60\u8fd9\u6837\u957f\u5f97\u7f8e\u4e3d\u7684\u59d1\u5a18\uff0c\u6c42\u4eb2\u7684\u65e9\u5c31\u6324\u7834\u95e8 \u5566\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u4e48\u5c0f\u7684\u5e74\u7eaa\u5c31\u7ed3\u5a5a\uff1f\u201d\u5176\u5b9e\u5728\u90a3\u4e2a\u65f6\u5019\uff0c\u6c49\u4eba\u4e5f\u662f\u76db\u884c\u65e9\u5a5a\uff0c\u5341\u516d\u4e03 \u5c81\u505a\u65b0\u5ac1\u5a18\u662f\u5f88\u666e\u901a\u7684\u4e8b\uff0c\u4e0d\u8fc7\u4e8e\u627f\u73e0\u4e00\u5fc3\u5b66\u6587\u7ec3\u6b66\uff0c\u6ca1\u6709\u7559\u610f\u5230\u8fd9\u4e0a\u5934\u7f62\u4e86\u3002 \u3000\u3000\u8c08\u7b11\u95f4\u5ffd\u542c\u5f97\u5c71\u5761\u90a3\u8fb9\u98d8\u6765\u4e00\u9635\u9635\u7684\u4e50\u58f0\uff0c\u975e\u5e38\u597d\u542c\uff0c\u4e50\u58f0\u4e2d\u6742\u6709\u82d7\u65cf\u59d1\u5a18\u7684\u6b4c\u58f0\uff0c\u4e8e \u627f\u73e0\u867d\u7136\u542c\u4e0d\u61c2\u6b4c\u8bcd\uff0c\u4f46\u4e5f\u611f\u5230\u6b4c\u58f0\u4e2d\u7684\u6b22\u6109\u60c5\u8c03\uff0c\u8001\u5927\u5a18\u7b11\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u770b\u8fc7\u54b1\u4eec\u82d7\u65cf\u7684 \u5a5a\u793c\u5427\uff1f\u201d\u4e8e\u627f\u73e0\u8fd8\u672a\u8131\u5c11\u5973\u5fc3\u60c5\uff0c\u559c\u6b22\u65b0\u5947\u70ed\u95f9\uff0c\u4e00\u542c\u8bf4\u6709\u4eba\u7ed3\u5a5a\uff0c\u975e\u5e38\u9ad8\u5174\uff0c\u7acb\u523b\u592e\u6c42 \u90a3\u8001\u5927\u5a18\u5e26\u5979\u53bb\u770b\u3002 \u3000\u3000\u8001\u5927\u5a18\u5e26\u4e8e\u627f\u73e0\u8f6c\u8fc7\u5c71\u5761\uff0c\u53ea\u89c1\u524d\u9762\u4e00\u4e2a\u5927\u8349\u576a\uff0c\u8349\u576a\u4e2d\u6709\u51e0\u68f5\u82b1\u6811\uff0c\u5c0f\u4f19\u5b50\u548c\u59d1\u5a18\u4eec \u90fd\u7ed5\u7740\u82b1\u6811\u8df3\u821e\uff0c\u6709\u7684\u5f39\u594f\u53e4\u74e2\u7434\uff0c\u7434\u5982\u74e2\u5f62\uff0c\u4e50\u58f0\u67d4\u548c\uff1b\u6709\u7684\u5439\u7740\u957f\u957f\u7684\u82a6\u7b19\uff0c\u8fd9\u662f\u7528\u516d \u6839\u7af9\u5b50\u505a\u6210\u7684\u4e50\u5668\uff0c\u5439\u51fa\u6765\u7684\u58f0\u97f3\u96c4\u6d51\u7c97\u65f7\uff0c\u70ed\u60c5\u6d0b\u6ea2\uff0c\u4e8e\u627f\u73e0\u542c\u5f97\u5165\u8ff7\uff0c\u5ffd\u7136\u6709\u4e24\u4e2a\u82d7\u65cf \u9752\u5e74\u8d70\u5230\u5979\u7684\u9762\u524d\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u4e00\u6115\uff0c\u53ea\u89c1\u90a3\u4e24\u4e2a\u82d7\u65cf\u9752\u5e74\u5f2f\u4e0b\u4e86\u8170\uff0c\u9762\u4e0a\u5806\u7740\u7b11\u5bb9\uff0c\u5f20\u5f00\u4e24\u6761\u81c2\u818a\uff0c\u4e24\u4e2a\u4eba\u4f60 \u6324\u6211\u6211\u6324\u4f60\u5730\u6025\u7740\u8981\u6324\u5230\u4e8e\u627f\u73e0\u9762\u524d\u3002\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u90a3\u8001\u5927\u5a18\u8fde\u5fd9\u8bf4 \u4e86\u51e0\u53e5\u82d7\u8bed\uff0c\u4e24\u4e2a\u9752\u5e74\u663e\u51fa\u6781\u5176\u5931\u671b\u7684\u6837\u5b50\uff0c\u600f\u600f\u4e0d\u4e50\u5730\u8d70\u4e86\u3002 \u3000\u3000\u90a3\u8001\u5927\u5a18\u968f\u624b\u6458\u4e0b\u4e24\u6735\u767d\u82b1\uff0c\u7ed9\u4e8e\u627f\u73e0\u7c2a\u5728\u9b13\u8fb9\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u8c01\u53eb\u4f60\u957f\u5f97\u8fd9\u4e48\u6f02\u4eae\uff0c \u5c0f\u4f19\u5b50\u4eec\u90fd\u6025\u7740\u8bf7\u4f60\u8df3\u82b1\u5566\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ec0\u4e48\u53eb\u505a\u8df3\u82b1\uff1f\u201d\u8001\u5927\u5a18\u9053\uff1a\u201c\u558f\uff0c\u8fd9\u4e0d\u5c31\u662f \u8df3\u82b1\uff1f\u201d\u573a\u4e2d\u7684\u5c0f\u4f19\u5b50\u5404\u6301\u82a6\u7b19\uff0c\u8fb9\u5439\u8fb9\u7ed5\u6811\u800c\u884c\uff0c\u53e4\u74e2\u7434\u7684\u4e50\u97f3\u4e5f\u5f39\u5f97\u66f4\u5176\u60a6\u8033\uff0c\u5c11\u5973\u4eec \u8fb9\u5531\u8fb9\u8df3\uff0c\u4e0d\u4e45\u5c31\u5404\u81ea\u914d\u6210\u4e86\u5bf9\u513f\uff0c\u7ed5\u7740\u573a\u4e2d\u82b1\u6811\uff0c\u7fe9\u7fe9\u8d77\u821e\u3002\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u771f\u597d\u770b\uff0c\u53ef \u60dc\u6211\u65e2\u4e0d\u4f1a\u5531\u6b4c\uff0c\u53c8\u4e0d\u4f1a\u8df3\u821e\u3002\u201d\u8001\u5927\u5a18\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4eec\u6c49\u4eba\u7684\u59d1\u5a18\u591a\u5bb3\u7f9e\uff0c\u6240\u4ee5\u6211\u7ed9 \u4f60\u7c2a\u4e0a\u4e24\u6735\u767d\u82b1\u5566\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u7c2a\u4e0a\u767d\u82b1\uff0c\u522b\u4eba\u5c31\u4e0d\u4f1a\u6765\u9080\u8bf7\u4e86\uff0c\u662f\u4e48\uff1f\u201d\u8001\u5927\u5a18\u9053\uff1a \u201c\u4e0d\u9519\u3002\u90a3\u662f\u8868\u793a\u4f60\u5df2\u6709\u4e86\u5fc3\u4e0a\u4eba\uff0c\u4f46\u5fc3\u4e0a\u4eba\u4e0d\u5728\u8fd9\u513f\uff0c\u4f60\u53ea\u662f\u6765\u770b\u70ed\u95f9\u7684\u7f62\u4e86\u3002\u4f60\u4e0d\u8981\u602a \u6211\uff0c\u4e0d\u8fd9\u6837\uff0c\u4efb\u4f60\u600e\u6837\u63a8\u8f9e\uff0c\u5c0f\u4f19\u5b50\u4eec\u90fd\u4e0d\u653e\u8fc7\u4f60\u7684\u3002\u558f\uff0c\u8bf4\u771f\u7684\uff0c\u4f60\u6709\u4e86\u5fc3\u4e0a\u4eba\u6ca1\u6709\uff1f\u201d \u4e8e\u627f\u73e0\u674f\u8138\u6cdb\u7ea2\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5ffd\u7136\u89c9\u5f97\u4e00\u9635\u6006\u51c9\uff0c\u4f46\u8349\u576a\u4e0a\u6b4c\u821e\u6b63\u6b22\uff0c\u82a6\u7b19\u5439\u6563\u4e86\u5979\u6de1\u6de1\u7684 \u54c0\u6101\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u5979\u53c8\u8f6c\u4e3a\u6b22\u4e50\u4e86\u3002 \u3000\u3000\u6708\u4eae\u6e10\u6e10\u5347\u9ad8\uff0c\u5230\u8349\u576a\u6765\u5531\u6b4c\u8df3\u821e\u7684\u5c0f\u4f19\u5b50\u548c\u59d1\u5a18\u4eec\u66f4\u591a\u4e86\uff0c\u65f6\u4e0d\u65f6\u6709\u4e00\u5bf9\u5bf9\u7684\u9752\u5e74\u7537 \u5973\u643a\u624b\u8d70\u5165\u6797\u4e2d\uff0c\u4ed6\u4eec\u7684\u4f4d\u7f6e\u8fc5\u5373\u88ab\u540e\u6765\u7684\u8865\u4e0a\u3002\u8001\u5927\u5a18\u7b11\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u91cc\u7684\u98ce\u4fd7\uff0c\u6709\u4e00\u65f6 \u7ed3\u5a5a\uff0c\u5c31\u53ef\u4ee5\u64ae\u5408\u597d\u591a\u5bf9\u59fb\u7f18\u3002\u201d\u4e8e\u627f\u73e0\u7f9e\u4e0d\u53ef\u6291\uff0c\u6025\u5fd9\u8f6c\u63a9\u8bdd\u9898\u9053\uff1a\u201c\u65b0\u5a18\u5b50\u5462\uff1f\u8fd8\u6ca1\u6709 \u51fa\u6765\u4e48\uff1f\u201d \u3000\u3000\u8001\u5927\u5a18\u9053\uff1a\u201c\u5feb\u5566\uff01\u201d\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u5ffd\u89c1\u4e24\u4e2a\u7a7f\u7740\u5f69\u8863\u7684\u58ee\u6c49\uff0c\u7275\u7740\u4e00\u5934\u725b\u51fa\u6765\uff0c\u7ed5\u573a \u884c\u4e86\u4e00\u531d\uff0c\u8349\u576a\u4e0a\u6b22\u58f0\u96f7\u52a8\uff0c\u4eba\u4eec\u7eb7\u7eb7\u4e0a\u53bb\u5e2e\u5fd9\uff0c\u628a\u725b\u7684\u56db\u811a\u6346\u597d\uff0c\u6709\u4e00\u4e2a\u5deb\u5e08\u6a21\u6837\u7684\u4eba\u8d70 \u51fa\u6765\uff0c\u7528\u65a7\u5934\u5728\u725b\u7684\u8111\u888b\u4e0a\u51fb\u4e86\u4e09\u4e0b\uff0c\u90a3\u5934\u725b\u660f\u5012\u5730\u4e0a\uff0c\u573a\u4e2d\u7684\u5c0f\u4f19\u5b50\u4eec\u7acb\u523b\u52a8\u624b\u5f00\u819b\u5265 \u76ae\uff0c\u751f\u706b\u70e4\u8089\uff0c\u539f\u6765\u8fd9\u662f\u82d7\u65cf\u7684\u5a5a\u5bb4\uff0c\u79f0\u4e3a\u201c\u6253\u725b\u201d\u3002\u8001\u5927\u5a18\u9053\uff1a\u201c\u6253\u725b\u4e4b\u540e\uff0c\u65b0\u90ce\u65b0\u5a18\u5c31 \u8981\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u662f\u8c01\u5bb6\u7ed3\u5a5a\uff0c\u573a\u9762\u771f\u70ed\u95f9\uff01\u201d\u8001\u5927\u5a18\u7b11\u9053\uff1a\u201c\u82e5\u662f\u7a77\u4eba\u5bb6\uff0c\u54ea\u820d\u5f97\u7528\u8fd9\u6761 \u80a5\u725b\uff1f\u8fd9\u662f\u6211\u4eec\u571f\u53f8\u5973\u513f\u7684\u5a5a\u793c\uff01\u201d\u5979\u7559\u5230\u73b0\u5728\u624d\u8bf4\uff0c\u6b32\u4ee4\u4e8e\u627f\u73e0\u610f\u5916\u6b22\u559c\uff0c\u4e8e\u627f\u73e0\u679c\u7136\u751a \u611f\u5174\u8da3\uff0c\u76ee\u4e0d\u8f6c\u775b\u5730\u6ce8\u89c6\u573a\u5fc3\uff0c\u7b49\u5019\u65b0\u4eba\u51fa\u73b0\u3002 \u3000\u3000\u5ffd\u5730\u91cc\u573a\u4e2d\u7684\u6b4c\u821e\u90fd\u9759\u6b62\u4e0b\u6765\uff0c\u53ea\u89c1\u516b\u5bf9\u7ae5\u7537\u7ae5\u5973\uff0c\u7c07\u62e5\u7740\u4e00\u5bf9\u65b0\u4eba\u9c7c\u8d2f\u8d70\u6765\uff0c\u65b0\u5a18\u6491 \u7740\u4e00\u628a\u5f69\u8272\u9c9c\u660e\u7684\u7eb8\u4f1e\uff0c\u65b0\u90ce\u80f8\u7ed3\u6709\u5927\u7ea2\u7ef8\u82b1\uff0c\u906e\u8fc7\u4e86\u534a\u8fb9\u8138\u5b54\uff0c\u4e00\u5230\u8349\u576a\uff0c\u573a\u4e0a\u7684\u9752\u5e74\u7537 \u5973\u7acb\u523b\u62cd\u638c\u6b22\u547c\uff0c\u65b0\u5a18\u5b50\u628a\u7eb8\u4f1e\u4ea4\u7ed9\u4f34\u5a18\uff0c\u6709\u4eba\u628a\u65b0\u90ce\u7684\u7ef8\u82b1\u89e3\u4e0b\uff0c\u62ab\u5230\u65b0\u5a18\u8eab\u4e0a\u3002\u8fd9\u4e00\u77ac \u95f4\uff0c\u4e8e\u627f\u73e0\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u8fd9\u5c0f\u65b0\u90ce\u7adf\u7136\u662f\u5c0f\u864e\u5b50\uff01 \u3000\u3000\u4e0d\u89c1\u4e00\u5e74\uff0c\u5c0f\u864e\u5b50\u5df2\u957f\u5f97\u9ad8\u591a\u4e86\uff0c\u4f46\u6bd4\u8d77\u65b0\u5a18\uff0c\u5374\u8fd8\u77ee\u534a\u4e2a\u5934\u3002\u4e16\u754c\u4e0a\u51fa\u4eba\u610f\u8868\u7684\u4e8b\u60c5 \u5f88\u591a\uff0c\u4f46\u773c\u524d\u4e4b\u4e8b\uff0c\u5374\u662f\u7edd\u5bf9\u96be\u4ee5\u60f3\u8c61\u2014\u2014\u5c0f\u864e\u5b50\u7adf\u7136\u4f1a\u5230\u82d7\u65cf\u4f5c\u65b0\u90ce\uff01\u8981\u4e0d\u662f\u8349\u576a\u4e0a\u6709\u8fd9 \u4e48\u591a\u72c2\u6b22\u5e86\u795d\u7684\u4eba\u7fa4\uff0c\u4e8e\u627f\u73e0\u8fd8\u4ee5\u4e3a\u662f\u987d\u76ae\u7684\u5c0f\u864e\u5b50\u5728\u73a9\u201c\u5a36\u65b0\u5a18\u201d\u7684\u628a\u620f\uff0c\u4f46\u6446\u5728\u773c\u524d\u7684 \u60c5\u666f\uff0c\u8fd9\u53ef\u4e0d\u662f\u5c0f\u5b69\u5b50\u7684\u6e38\u620f\uff0c\u800c\u662f\u5b9e\u5b9e\u5728\u5728\u7684\u5a5a\u793c\u5440\uff01\u201c\u5c0f\u864e\u5b50\u4e0d\u662f\u8ddf\u968f\u9ed1\u767d\u6469\u8bc3\u5230\u5929\u7afa \u53bb\u4e48\uff1f\u600e\u7684\u4f1a\u5355\u8eab\u4e00\u4eba\u6765\u5230\u8fd9\u513f\uff1f\u201d\u201c\u9ed1\u767d\u6469\u8bc3\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u571f\u53f8\u7684\u5973\u513f\u600e\u4f1a\u5ac1\u4ed6\uff1f\u201d\u4e00\u8fde \u4e32\u96be\u4ee5\u89e3\u7b54\u7684\u7591\u95ee\uff0c\u505a\u68a6\u4e5f\u60f3\u8c61\u4e0d\u5230\u7684\u4e8b\u60c5\uff0c\u628a\u4e8e\u627f\u73e0\u7684\u8111\u888b\u90fd\u5f04\u5f97\u660f\u7729\u4e86\u3002 \u3000\u3000\u90a3\u4f4d\u82d7\u65cf\u7684\u8001\u5927\u5a18\u7b11\u9053\uff1a\u201c\u600e\u4e48\u5566\uff0c\u5f88\u4ee4\u4f60\u60ca\u5947\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u5c0f\u65b0\u90ce\u662f\u4f60\u4eec\u7684\u6c49\u4eba \u5462\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u5c0f\u5b69\u5b50\u662f\u600e\u4e48\u6765\u7684\uff1f\u571f\u53f8\u4e3a\u4ec0\u4e48\u628a\u5973\u513f\u8bb8\u914d\u7ed9\u4ed6\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d\u8001\u5927 \u5a18\u6447\u6447\u5934\u7b11\u9053\uff1a\u201c\u571f\u53f8\u5bb6\u91cc\u7684\u4e8b\u60c5\uff0c\u54b1\u4eec\u600e\u4e48\u6562\u53bb\u6253\u542c\uff1f\u5728\u6211\u4eec\u7684\u4e0a\u4e00\u8f88\uff0c\u82d7\u4eba\u6c49\u4eba\u7ed3\u4eb2\u5bb6 \u7684\u4e0d\u591a\uff0c\u8fd1\u5e74\u6765\u8fd9\u5374\u5e76\u4e0d\u662f\u4ec0\u4e48\u7a00\u5947\u7684\u4e8b\u4e86\u3002\u201d\u5176\u5b9e\u4e8e\u627f\u73e0\u60ca\u8be7\u7684\u5e76\u4e0d\u662f\u56e0\u4e3a\u65b0\u90ce\u662f\u6c49\u4eba\uff0c \u800c\u662f\u56e0\u4e3a\u65b0\u90ce\u662f\u5c0f\u864e\u5b50\uff01 \u3000\u3000\u8001\u5927\u5a18\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u65b0\u90ce\u662f\u5c0f\u5b69\u5b50\uff0c\u4f60\u4eec\u6c49\u4eba\u6ca1\u6709\u53d6\u7ae5\u517b\u5ab3\u2018\u62b1\u90ce\u2019\u7684\u4e8b\u60c5\u5417\uff1f\u201d\u4ee5 \u524d\u6709\u4e9b\u4eba\u5bb6\uff0c\u5b69\u5b50\u53ea\u6709\u4e24\u4e09\u5c81\uff0c\u7236\u6bcd\u5c31\u7ed9\u4ed6\u201c\u5a36\u5ab3\u5987\u201d\uff0c\u5ab3\u5987\u6bd4\u4ed6\u5927\u5341\u51e0\u5c81\uff0c\u90fd\u4e0d\u7a00\u5947\uff0c\u5ab3 \u5987\u5a36\u4e86\u56de\u6765\u5c31\u50cf\u6bcd\u4eb2\u4e00\u6837\u7167\u6599\u5c0f\u4e08\u592b\uff0c\u8fd9\u79cd\u98ce\u4fd7\u5728\u82d7\u6c49\u90fd\u662f\u6709\u7684\u3002\u8001\u5927\u5a18\u53c8\u9053\uff1a\u201c\u54b1\u4eec\u571f\u53f8 \u7684\u5973\u513f\u4eca\u5e74\u5341\u516d\u5c81\uff0c\u542c\u7ed9\u4e24\u4eba\u5408\u516b\u5b57\u7684\u5deb\u5e08\u900f\u9732\uff0c\u8fd9\u5c0f\u65b0\u90ce\u662f\u5341\u56db\u5c81\uff0c\u5e74\u7eaa\u76f8\u5dee\u8fd8\u4e0d\u7b97 \u5927\u3002\u201d \u3000\u3000\u8349\u576a\u4e0a\u7684\u5c0f\u4f19\u5b50\u4eec\u628a\u90a3\u6761\u80a5\u725b\u70e4\u4e86\uff0c\u6495\u4e0b\u4e00\u5757\u5757\u7684\u725b\u8089\u559d\u9152\uff0c\u72c2\u996e\u547c\u5578\uff0c\u8001\u5927\u5a18\u9053\uff1a \u201c\u54b1\u4eec\u82d7\u65cf\u7684\u5a5a\u5bb4\u662f\u4e0d\u5fc5\u4eba\u9080\u8bf7\u7684\uff0c\u4f60\u4e5f\u53bb\u5403\u70b9\u70e4\u725b\u8089\u5427\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u4e0d\u997f\u3002\u201d\u8001\u5927 \u5a18\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u5403\u725b\u8089\uff0c\u53c8\u4e0d\u559d\u9152\uff0c\u90a3\u5c31\u662f\u4e0d\u7ed9\u4e3b\u4eba\u9762\u5b50\u4e86\u3002\u597d\u5427\uff0c\u4f60\u4e0d\u597d\u610f\u601d\u8ddf\u90a3\u4e9b\u5c0f\u5b69 \u5b50\u6324\uff0c\u6211\u7ed9\u4f60\u62ff\u6765\u3002\u201d\u4e8e\u627f\u73e0\u4efb\u5f97\u90a3\u8001\u5927\u5a18\u4f5c\u4e3b\uff0c\u5979\u53ea\u662f\u5168\u795e\u8d2f\u6ce8\u5728\u5c0f\u864e\u5b50\u8eab\u4e0a\uff0c\u53ea\u89c1\u5c0f\u864e \u5b50\u76ee\u5149\u5446\u6ede\uff0c\u4e00\u70b9\u4e5f\u4e0d\u50cf\u4ee5\u524d\u90a3\u6d3b\u6cfc\u987d\u76ae\u7684\u6a21\u6837\uff0c\u4ed6\u5446\u5446\u5730\u7ad9\u5728\u573a\u4e2d\uff0c\u5c31\u50cf\u4e00\u5c0a\u4efb\u4eba\u6446\u5e03\u7684 \u6728\u5076\uff0c\u4e8e\u627f\u73e0\u5927\u662f\u8d77\u7591\u3002\u5ffd\u542c\u5f97\u4e00\u4e2a\u82d7\u65cf\u7684\u5c0f\u4f19\u5b50\u7528\u6c49\u8bed\u5531\u9053\uff1a\u201c\u5929\u4e0a\u7684\u6708\u4eae\u4f34\u5f69\u971e\uff0c\u5730\u4e0b \u7684\u51e4\u51f0\u600e\u80fd\u914d\u4e4c\u9e26\uff1f\u54c8\u54c8\uff0c\u6f02\u4eae\u7684\u5927\u59d1\u5a18\u4e3a\u4ec0\u4e48\u914d\u4e11\u5a03\u5a03\uff1f\u201d\u573a\u4e2d\u7537\u5973\u8f70\u7136\u5927\u7b11\uff0c\u90a3\u5c0f\u4f19\u5b50 \u8fb9\u5531\u8fb9\u8dd1\u51fa\u6765\uff0c\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u54fc\uff0c\u8bf4\u5c0f\u864e\u5b50\u662f\u4e11\u5a03\u5a03\uff1f\u5c0f\u864e\u5b50\u53ef\u6bd4\u4f60\u4fca\u5f97\u591a\uff01\u201d\u90a3\u5c0f\u4f19\u5b50 \u559d\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u9189\u6001\u53ef\u63ac\uff0c\u8dd1\u5230\u5c0f\u864e\u5b50\u8ddf\u524d\uff0c\u4f38\u624b\u638c\u62e8\u4ed6\u4e0b\u5df4\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u5a03\u5a03\uff0c\u8ba9\u6211\u770b\u4f60 \u7684\u4e73\u7259\u957f\u9f50\u6ca1\u6709\uff1f\u201d\u5c0f\u864e\u5b50\u95f7\u58f0\u4e0d\u54cd\uff0c\u5ffd\u7136\u201c\u556a\u201d\u7684\u4e00\u638c\uff0c\u628a\u90a3\u5c0f\u4f19\u5b50\u6253\u5f97\u8dcc\u51fa\u4e00\u4e08\u5f00\u5916\uff0c \u7259\u4e5f\u6389\u4e86\u4e24\u9897\uff01 \u3000\u3000\u8349\u576a\u4e0a\u53c2\u52a0\u5a5a\u5bb4\u7684\u4eba\u7fa4\u54d7\u7136\u7b11\u53eb\u3002\u6709\u4eba\u5531\u9053\uff1a\u201c\u8fd9\u662f\u9e92\u9e9f\u9f99\u51e4\u914d\uff0c\u4e0d\u662f\u51e4\u51f0\u914d\u4e4c\u9e26\u3002\u201d \u4e8e\u627f\u73e0\u4ece\u4ed6\u4eec\u7684\u773c\u5149\u91cc\u770b\u5f97\u51fa\u6765\uff1a\u9002\u624d\u4ed6\u4eec\u5bf9\u5c0f\u864e\u5b50\u5927\u534a\u5b58\u6709\u5632\u5f04\u7684\u795e\u6c14\uff0c\u800c\u4eca\u5374\u90fd\u662f\u60ca\u5947 \u4f69\u670d\u7684\u773c\u5149\u4e86\u3002\u90a3\u4f4d\u82d7\u65cf\u8001\u5927\u5a18\u53d6\u4e86\u725b\u8089\u56de\u6765\uff0c\u5c06\u4e00\u4e2a\u88c5\u9152\u7684\u7af9\u7b52\u548c\u4e00\u5757\u70e4\u725b\u8089\u9012\u7ed9\u4e8e\u627f\u73e0 \u5403\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u5c0f\u4f19\u5b50\u82e5\u975e\u559d\u9189\u4e86\u9152\uff0c\u4e5f\u4e0d\u6562\u8fd9\u6837\u80e1\u95f9\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u5c0f\u4f19\u5b50\u662f\u4ec0\u4e48 \u4eba\uff1f\u201d\u8001\u5927\u5a18\u9053\uff1a\u201c\u8fd9\u5c0f\u4f19\u4e8e\u662f\u571f\u53f8\u5c5e\u4e0b\u7684\u4e00\u4e2a\u5934\u4eba\u7684\u513f\u5b50\uff0c\u4ed6\u81ea\u5c0f\u6697\u604b\u571f\u53f8\u7684\u5973\u513f\uff0c\u524d\u5e74 \u8fd8\u548c\u571f\u53f8\u7684\u5973\u513f\u8df3\u8fc7\u4e00\u6b21\u82b1\uff0c\u571f\u53f8\u7684\u5973\u513f\u4e5f\u50cf\u751a\u6b22\u559c\u4ed6\uff0c\u5374\u4e0d\u6599\u571f\u53f8\u5ffd\u7136\u5c06\u5973\u513f\u914d\u4e86\u8fd9\u4e2a\u6765 \u5386\u4e0d\u660e\u7684\u6c49\u4eba\uff0c\u60f3\u662f\u4ed6\u5fc3\u4e2d\u4e0d\u6124\uff0c\u6545\u6b64\u501f\u9152\u884c\u51f6\u3002\u563f\uff0c\u8fd9\u4e2a\u6c49\u4eba\u5c0f\u5a03\u5a03\u8fd8\u771f\u6709\u672c\u4e8b\uff0c\u4f60\u4e0d\u77e5 \u9053\uff0c\u521a\u624d\u90a3\u5c0f\u4f19\u5b50\u662f\u6211\u4eec\u82d7\u65cf\u4e2d\u51fa\u540d\u7684\u52c7\u58eb\u5462\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u4e2d\u7591\u60d1\u66f4\u751a\uff0c\u5c0f\u864e\u5b50\u53ea\u6709\u5341\u56db\u5c81\uff0c\u4ed6\u6839\u672c\u8fd8\u672a\u61c2\u5f97\u7ed3\u5a5a\u662f\u4ec0\u4e48\u4e00\u56de\u4e8b\u513f\u3002\u4f46\u82e5 \u8bf4\u4ed6\u662f\u5168\u7136\u4e0d\u613f\u5427\uff0c\u4ee5\u4ed6\u8fd9\u8eab\u6b66\u827a\uff0c\u8c01\u53c8\u80fd\u5f3a\u8feb\u4ed6\uff1f\u4ed6\u600e\u4f1a\u4e0e\u65b0\u5a18\u4e00\u540c\u8d70\u6765\uff0c\u53c8\u4e3a\u4ec0\u4e48\u8981\u628a \u90a3\u5c0f\u4f19\u5b50\u6253\u8dd1\uff1f \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u5c06\u4e00\u652f\u957f\u957f\u7684\u725b\u89d2\u545c\u545c\u5730\u5439\u4e86\u51e0\u4e0b\uff0c\u4e00\u961f\u4e50\u624b\u53c8\u5439\u8d77\u82a6\u7b19\uff0c\u5f39\u8d77\u65b9\u74e2\u7434\uff0c\u8001 \u5927\u5a18\u9053\uff1a\u201c\u884c\u5a5a\u793c\u5566\uff01\u201d\u53ea\u89c1\u4e00\u4e2a\u82d7\u65cf\u957f\u8001\u7aef\u51fa\u4e24\u4e2a\u725b\u89d2\u676f\uff0c\u676f\u4e2d\u76db\u6ee1\u7f8e\u9152\uff0c\u6709\u4eba\u5c06\u725b\u8840\u6ef4 \u5230\u676f\u4e2d\uff0c\u957f\u8001\u5531\u9053\uff1a\u201c\u559d\u7f62\u4ea4\u676f\u9152\uff0c\u6069\u7231\u5230\u767d\u5934\uff01\u201d\u5c06\u4e24\u676f\u8840\u9152\u5206\u9012\u7ed9\u65b0\u4eba\u3002\u65b0\u5a18\u542b\u7f9e\u7b54 \u7b54\uff0c\u63a5\u8fc7\u9152\u676f\uff0c\u5c0f\u864e\u5b50\u5374\u5ffd\u7136\u4f38\u6307\u4e00\u5f39\uff0c\u9053\uff1a\u201c\u6211\u7238\u7238\u5429\u5490\u8fc7\u7684\uff0c\u6211\u8fd8\u672a\u957f\u5927\uff0c\u4e0d\u8bb8\u559d \u9152\uff01\u201d\u9152\u676f\u88ab\u5f39\uff0c\u767b\u65f6\u98de\u4e0a\u534a\u7a7a\uff0c\u8840\u9152\u503e\u6d12\uff0c\u6dcb\u4e86\u957f\u8001\u6ee1\u5934\uff01\u4e8e\u627f\u73e0\u4e0d\u7981\u5931\u7b11\uff01\u5c0f\u864e\u5b50\u7adf\u7136 \u8fd8\u8bb0\u5f97\u4ed6\u7236\u4eb2\u751f\u524d\u7684\u6559\u8bad\uff0c\u90a3\u6837\u5b50\u4f60\u8bf4\u4ed6\u662f\u50bb\u53c8\u4e0d\u50cf\u50bb\uff0c\u8bf4\u4ed6\u4e0d\u50bb\u4ed6\u5374\u5728\u5a5a\u793c\u5f53\u4e2d\u95f9\u51fa\u5b69\u5b50 \u7684\u813e\u6c14\uff01 \u3000\u3000\u957f\u8001\u5927\u60ca\u5931\u8272\uff0c\u4ea4\u676f\u9152\u88ab\u6cfc\uff0c\u8fd9\u4e43\u662f\u5927\u4e0d\u5409\u4e4b\u5146\uff0c\u4e8e\u627f\u73e0\u6697\u6697\u597d\u7b11\uff0c\u548c\u573a\u4e2d\u7684\u9752\u5e74\u7537\u5973 \u4e00\u6837\uff0c\u90fd\u7741\u5927\u4e86\u773c\u775b\uff0c\u770b\u4ed6\u600e\u4e48\u529e\uff1f\u5ffd\u542c\u5f97\u4e00\u4e2a\u4f4e\u6c89\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u518d\u659f\u4e00\u676f\u7ed9\u4ed6\uff01\u201d\u65c1\u8fb9 \u8d70\u51fa\u4e00\u4eba\uff0c\u8c8c\u4f3c\u6c49\u4eba\uff0c\u7a7f\u7684\u5374\u662f\u82d7\u65cf\u670d\u9970\uff0c\u7ea6\u83ab\u6709\u56db\u5341\u591a\u5c81\u7684\u6837\u5b50\uff0c\u76f8\u8c8c\u5a01\u4e25\uff0c\u4ee4\u4eba\u671b\u800c\u751f \u754f\uff0c\u53ea\u89c1\u4ed6\u5c06\u4e00\u4e2a\u76db\u6ee1\u4e86\u9152\u7684\u725b\u89d2\u676f\u9012\u5230\u5c0f\u864e\u5b50\u9762\u524d\uff0c\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u4e0d\u559d\u9152\u561b\uff01\u201d\u84e6 \u7136\u4f38\u51fa\u53cc\u6307\uff0c\u53c8\u5411\u9152\u676f\u4e00\u5f39\uff0c\u90a3\u4eba\u6c89\u58f0\u559d\u9053\uff1a\u201c\u4e0d\u8981\u80e1\u95f9\uff01\u201d\u624b\u638c\u4e00\u6258\uff0c\u90a3\u9152\u676f\u5230\u4e86\u5c0f\u864e\u5b50 \u624b\u4e2d\uff0c\u5ffd\u7136\u5411\u5c0f\u864e\u5b50\u53e3\u4e2d\u5012\u4e0b\uff0c\u5c0f\u864e\u5b50\u8fd8\u672a\u5408\u5634\uff0c\u5403\u5f97\u4ed6\u55b7\u4e86\u51fa\u6765\uff0c\u4f46\u603b\u7b97\u662f\u559d\u4e86\u8fd9\u676f\u201c\u4ea4 \u676f\u9152\u201d\u4e86\u3002\u65c1\u4eba\u770b\u4e0d\u6e05\u695a\uff0c\u8fd8\u4ee5\u4e3a\u662f\u5c0f\u864e\u5b50\u81ea\u5df1\u5012\u5165\u53e3\u4e2d\u7684\u3002\u4e8e\u627f\u73e0\u53ef\u662f\u5927\u5403\u4e00\u60ca\uff0c\u90a3\u4eba\u7528 \u7684\u7adf\u662f\u6700\u4e0a\u4e58\u7684\u201c\u501f\u5200\u6740\u4eba\u201d\u7684\u624b\u6cd5\uff0c\u6bd4\u501f\u529b\u6253\u4eba\u7684\u529f\u592b\u8fd8\u8981\u9ad8\u660e\uff0c\u7adf\u7136\u501f\u5c0f\u864e\u5b50\u7684\u624b\u903c\u4ed6 \u81ea\u5df1\u559d\u9152\uff0c\u771f\u662f\u532a\u5937\u6240\u601d\u3002 \u3000\u3000\u573a\u4e2d\u9752\u5e74\u7537\u5973\u6b22\u547c\u8df3\u53eb\uff0c\u4f34\u5a18\u5c06\u7eb8\u4f1e\u6c40\u5f00\uff0c\u906e\u7740\u8fd9\u5bf9\u65b0\u4eba\uff0c\u5c0f\u864e\u5b50\u4f3c\u7ed9\u4eba\u63a8\u7740\u4e00\u822c\uff0c\u966a \u7740\u65b0\u5a18\u7f13\u7f13\u8d70\u51fa\u8349\u576a\u3002\u8001\u5927\u5a18\u9053\uff1a\u201c\u5a5a\u793c\u544a\u6210\u5566\uff0c\u7b49\u4e0b\u5b50\u5c31\u662f\u5230\u571f\u53f8\u5e9c\u4e2d\u53bb\u95f9\u65b0\u623f\u5566\uff01\u201d\u6b63 \u662f\uff1a \u3000\u3000\u5c11\u5c0f\u672a\u77e5\u4eba\u4e16\u4e8b\uff0c\u8fd9\u822c\u5a5a\u793c\u592a\u79bb\u5947\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u5341\u4e03\u56de\u3000\u65b9\u5821\u5947\u60c5\u3000\u9b54\u5934\u5f00\u591c\u5bb4\u3000\u6df1\u5bb5\u5f02\u4e8b\u3000\u516c\u4e3b\u5230\u82d7\u7586 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u5341\u4e03\u56de\u3000\u65b9\u5821\u5947\u60c5\u3000\u9b54\u5934\u5f00\u591c\u5bb4\u3000\u6df1\u5bb5\u5f02\u4e8b\u3000\u516c\u4e3b\u5230\u82d7\u7586 \u3000\u3000\u4e8e\u627f\u73e0\u4e0d\u77e5\u4e0d\u89c9\u5730\u6324\u5728\u5c0f\u4f19\u5b50\u4e2d\u95f4\uff0c\u8ddf\u5728\u65b0\u90ce\u65b0\u5a18\u540e\u9762\uff0c\u8d70\u51fa\u8349\u576a\uff0c\u8001\u5927\u5a18\u7b11\u9053\uff1a\u201c\u600e \u4e48\uff0c\u4f60\u4e5f\u60f3\u53bb\u95f9\u65b0\u623f\u4e48\uff1f\u6211\u8001\u5927\u5a18\u5934\u53d1\u90fd\u767d\u4e86\uff0c\u53ef\u4e0d\u65b9\u4fbf\u968f\u7740\u4f60\u4eec\u5c0f\u4f19\u5b50\u80e1\u95f9\u5566\u3002\u201d\u4e8e\u627f\u73e0 \u5fc3\u4e2d\u4e00\u52a8\uff0c\u8d81\u52bf\u8bf4\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u8fd9\u5a5a\u793c\u771f\u6709\u610f\u601d\uff0c\u96be\u5f97\u770b\u5230\u4e00\u6b21\uff0c\u6211\u8ddf\u4ed6\u4eec\u53bb\u770b\u95f9\u65b0\u623f\uff0c\u8001 \u5927\u5a18\u4f60\u7d2f\u5566\uff0c\u4f60\u5148\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u82d7\u65cf\u7684\u95f9\u65b0\u623f\u6bd4\u6c49\u4eba\u7684\u82b1\u6837\u8fd8\u591a\uff0c\u8981\u65b0\u90ce\u548c\u65b0\u5a18\u5171\u56bc\u4e00\u7c92\u69df\u6994\u5566\uff0c\u8981\u65b0\u90ce\u66ff\u65b0\u5a18\u9664\u4e0b\u5934 \u7eb1\u5566\uff0c\u8981\u65b0\u5a18\u5531\u6b4c\u8c22\u5ba2\u5566\u7b49\u7b49\u3002\u4e8e\u627f\u73e0\u6324\u5728\u4eba\u4e1b\u4e2d\u7559\u795e\u770b\u5c0f\u864e\u5b50\u7684\u52a8\u4f5c\uff0c\u4f46\u89c1\u4ed6\u76ee\u5149\u5446\u6ede\uff0c \u663e\u51fa\u4e00\u526f\u9b42\u4e0d\u5b88\u820d\u7684\u6a21\u6837\uff0c\u4efb\u7531\u65c1\u4eba\u6446\u5e03\uff0c\u95f9\u4e86\u597d\u4e00\u4f1a\uff0c\u9002\u624d\u90a3\u4e2a\u903c\u5c0f\u864e\u5b50\u559d\u9152\u7684\u7537\u5b50\u8bf4 \u9053\uff1a\u201c\u591f\u5566\uff0c\u65b0\u90ce\u9762\u5ae9\uff0c\u518d\u95f9\u4ed6\u5c31\u8981\u54ed\u5566\u3002\u201d\u4f17\u4eba\u54d7\u7b11\u58f0\u4e2d\uff0c\u4f34\u5a18\u53d6\u51fa\u4e00\u67c4\u6247\u5b50\uff0c\u9012\u7ed9\u5c0f\u864e \u5b50\uff0c\u53eb\u4ed6\u5728\u65b0\u5a18\u9999\u80a9\u4e0a\u6253\u4e09\u4e0b\uff0c\u5c0f\u864e\u5b50\u5bd2\u7740\u8138\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u5979\u5bf9\u6211\u5f88\u597d\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u6253 \u5979\uff1f\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u54c4\u5802\u5927\u7b11\uff0c\u4f34\u5a18\u5728\u5c0f\u864e\u5b50\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u793c\u8282\uff0c\u4f60\u5c31\u968f\u610f\u5730\u8f7b\u8f7b\u6253\u4e09\u4e0b \u5427\u3002\u201d\u4f34\u5a18\u7684\u8bf4\u8bdd\u58f0\u4f4e\u5f97\u597d\u4f3c\u868a\u53eb\uff0c\u5c0f\u864e\u5b50\u4f3c\u4e4e\u8fd8\u6ca1\u542c\u6e05\u695a\uff0c\u65c1\u8fb9\u8033\u6735\u7075\u654f\u7684\u5c0f\u4f19\u5b50\u5374\u542c\u5230 \u4e86\uff0c\u5927\u53eb\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u4e0d\u6210\uff01\u8981\u91cd\u91cd\u5730\u6253\u4e09\u4e0b\uff0c\u8981\u4e0d\uff0c\u5c31\u662f\u6015\u8001\u5a46\u3002\u201d\u4f17\u4eba\u90fd\u5927\u7b11\uff0c\u5c0f\u864e\u5b50 \u773c\u775b\u4e00\u7728\uff0c\u9732\u51fa\u4e00\u70b9\u60f6\u60d1\u7684\u795e\u6c14\uff0c\u4f3c\u4e4e\u4ed6\u4e5f\u61c2\u5f97\u4e86\u201c\u6015\u8001\u5a46\u201d\u662f\u4ef6\u201c\u5dee\u803b\u201d\u7684\u4e8b\u60c5\uff0c\u62ff\u8d77\u6247 \u5b50\uff0c\u535c\u3001\u535c\u3001\u535c\u7684\u5728\u65b0\u5a18\u80a9\u4e0a\u6572\u4e86\u4e09\u4e0b\u3002\u6bcf\u6253\u4e00\u4e0b\uff0c\u65b0\u5a18\u5a07\u8eaf\u4e00\u98a4\uff0c\u6253\u5230\u6700\u540e\u4e00\u4e0b\uff0c\u65b0\u5a18\u53cc \u80a9\u4e00\u98a4\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u773c\u89d2\u5659\u7740\u6cea\u73e0\uff0c\u9762\u8272\u90fd\u5168\u53d8\u4e86\uff0c\u95f9\u65b0\u623f\u7684\u5c0f\u4f19\u5b50\u4eec\u563b\u54c8\u5927\u7b11\uff0c\u9ad8\u58f0\u53eb \u597d\uff0c\u4e8e\u627f\u73e0\u53ef\u662f\u770b\u5f97\u9a87\u7136\uff0c\u5fc3\u4e2d\u60ca\u7591\u4e0d\u5df2\uff01\u8981\u77e5\u5c0f\u864e\u5b50\u867d\u7136\u5e74\u5c0f\uff0c\u4f46\u6240\u7ec3\u7684\u5185\u5bb6\u771f\u529b\uff0c\u5373\u7b97 \u86ee\u725b\u4e00\u822c\u7684\u58ee\u6c49\uff0c\u4e5f\u7981\u4e0d\u4f4f\u4ed6\u8f7b\u8f7b\u4e00\u51fb\uff0c\u4ed6\u8fd9\u4e09\u4e0b\u6247\u5b50\uff0c\u4e0d\u77e5\u662f\u7cca\u6d82\u8fd8\u662f\u53d7\u6fc0\uff0c\u7528\u7684\u7adf\u662f\u5185 \u5bb6\u91cd\u624b\u6cd5\uff0c\u800c\u8fd9\u65b0\u5a18\u5c45\u7136\u80fd\u5fcd\u7740\u75bc\u75db\uff0c\u54fc\u4e5f\u4e0d\u54fc\u4e00\u58f0\uff01 \u3000\u3000\u7b11\u58f0\u5ffd\u7136\u4f4f\u6b62\uff0c\u53ea\u89c1\u65b0\u5a18\u80a9\u4e0a\u7684\u8863\u88f3\uff0c\u5df2\u88ab\u6253\u5f97\u7247\u7247\u788e\u88c2\uff0c\u9732\u51fa\u4e86\u96ea\u4e00\u822c\u7684\u767d\u8089\uff0c\u5c0f\u4f19 \u5b50\u4eec\u624d\u77e5\u9053\u5c0f\u864e\u5b50\u7684\u624b\u52b2\u4e4b\u5927\uff0c\u4e0d\u6562\u518d\u95f9\uff0c\u6709\u4eba\u8200\u4e86\u4e24\u74e2\u6c34\uff0c\u4e00\u74e2\u6cfc\u5230\u65b0\u5a18\u8eab\u4e0a\uff0c\u4e00\u74e2\u6cfc\u5230 \u5c0f\u864e\u5b50\u8eab\u4e0a\uff0c\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u550f\uff0c\u4f60\u6562\u6cfc\u6211\uff1f\u201d\u6247\u5b50\u4e00\u5f20\u4e00\u62e8\uff0c\u628a\u6cfc\u5411\u4ed6\u8eab\u4e0a\u7684\u51b7\u6c34\u90fd\u53cd\u6cfc\u56de \u53bb\uff0c\u6dcb\u5f97\u90a3\u4e9b\u95f9\u65b0\u623f\u7684\u5c0f\u4f19\u5b50\u6ee1\u5934\u6ee1\u9762\uff0c\u4f17\u4eba\u5927\u60ca\u5931\u8272\uff0c\u539f\u6765\u8fd9\u4e5f\u662f\u82d7\u65cf\u5a5a\u793c\u7684\u4e00\u4e2a\u793c\u8282\uff0c \u6cfc\u6c34\u662f\u8868\u793a\u5e86\u8d3a\u7684\u610f\u601d\uff0c\u6cfc\u5f97\u8d8a\u6e7f\u5c31\u8d8a\u597d\u5146\u5934\uff0c\u90a3\u6c49\u5b50\u6025\u5fd9\u62c9\u7740\u5c0f\u864e\u5b50\u7684\u81c2\u818a\uff0c\u5728\u4ed6\u8033\u8fb9\u8bf4 \u4e86\u51e0\u53e5\uff0c\u518d\u4e00\u74e2\u6c34\u7167\u5934\u6cfc\u4e0b\u53bb\uff0c\u53ef\u662f\u7b2c\u4e8c\u6b21\u624d\u80fd\u6cfc\u5230\u5c0f\u864e\u5b50\u8eab\u4e0a\uff0c\u8fd9\u5df2\u662f\u5927\u5927\u7684\u4e0d\u5409\u4e4b\u5146\uff0c \u7167\u82d7\u65cf\u7684\u8ff7\u4fe1\uff0c\u8fd9\u5bf9\u65b0\u4eba\uff0c\u5c06\u6765\u4e0d\u662f\u7537\u7684\u518d\u5a5a\u5c31\u662f\u5973\u7684\u518d\u5ac1\u4e86\u3002\u95f9\u65b0\u623f\u5c31\u8fd9\u6837\u7684\u8349\u8349\u6536\u573a\uff0c \u4e0d\u6b22\u800c\u6563\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5374\u6084\u6084\u5730\u8eb2\u5728\u9662\u5b50\u91cc\u7684\u5047\u5c71\u6697\u89d2\uff0c\u5f85\u5f97\u4f17\u4eba\u6563\u5c3d\uff0c\u5979\u5374\u5077\u5077\u5730\u53bb\u770b\u5c0f\u864e\u5b50\u6d1e\u623f\uff0c \u4f0f\u5728\u5c4b\u6a90\u4e0a\uff0c\u77a7\u5165\u623f\u4e2d\uff0c\u53ea\u89c1\u5c0f\u864e\u5b50\u548c\u65b0\u5a18\u6beb\u65e0\u8868\u60c5\u5730\u5750\u5728\u65b0\u5e8a\u4e0a\u3002 \u3000\u3000\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u624d\u542c\u5f97\u65b0\u5a18\u602f\u751f\u751f\u5730\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u8bf4\u559c\u6b22\u6211\uff0c\u539f\u6765\u90a3\u662f\u5047\u7684\u3002\u201d\u5c0f\u864e\u5b50 \u9053\uff1a\u201c\u8c01\u8bf4\u5047\u7684\uff1f\u6211\u5bf9\u5c0f\u9f99\u90fd\u6ca1\u6709\u5bf9\u4f60\u90a3\u4e48\u597d\u3002\u201d\u65b0\u5a18\u9053\uff1a\u201c\u5c0f\u9f99\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a \u201c\u5c0f\u9f99\u662f\u6211\u90bb\u5bb6\u4e8c\u4f2f\u7684\u513f\u5b50\uff0c\u4ece\u5c0f\u54b1\u4eec\u5c31\u5728\u4e00\u8d77\u73a9\u800d\uff0c\u4ed6\u5440\uff0c\u5c31\u662f\u80c6\u5c0f\u4e00\u4e9b\uff0c\u4e09\u6708\u5929\u65f6\uff0c\u8fd8 \u4e0d\u6562\u4e0b\u6c60\u5858\u6349\u9c7c\uff01\u201d\u4e8e\u627f\u73e0\u60f3\u8d77\u521d\u89c1\u5c0f\u864e\u5b50\u4e4b\u65f6\u7684\u60c5\u666f\uff0c\u4ed6\u6b63\u5728\u6c60\u5858\u91cc\u620f\u5f04\u4e00\u4e2a\u987d\u7ae5\uff0c\u6562\u60c5 \u90a3\u987d\u7ae5\u4fbf\u662f\u5c0f\u9f99\uff0c\u5fc3\u4e2d\u6697\u6697\u597d\u7b11\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u62fc\u547d\u5fcd\u7740\u7b11\uff0c\u65b0\u5a18\u5374\u5df2\u7b11\u51fa\u58f0\u6765\uff0c\u9053\uff1a\u201c\u5c0f\u9f99\u600e\u597d\u4e0e\u6211\u6765\u6bd4\uff0c\u6211\u662f\u4f60\u7684\u59bb\u5b50\u3002\u201d \u5c0f\u864e\u5b50\u9053\uff1a\u201c\u4ec0\u4e48\u53eb\u505a\u59bb\u5b50\uff1f\u201d\u65b0\u5a18\u9053\uff1a\u201c\u59bb\u5b50\u5c31\u662f\u4f60\u81f3\u4eb2\u81f3\u8fd1\u7684\u4eba\u3002\u201d\u5c0f\u864e\u5b50\u201c\u54e6\u201d\u4e86\u4e00 \u58f0\uff0c\u770b\u60c5\u5f62\u4ed6\u6b63\u5728\u7591\u60d1\uff0c\u5e76\u4e0d\u80af\u627f\u8ba4\u8fd9\u4e2a\u5c0f\u59d1\u5a18\u662f\u4ed6\u7684\u4eb2\u4eba\uff0c\u53ef\u53c8\u4e0d\u597d\u610f\u601d\u8bf4\u51fa\u6765\u3002\u65b0\u5a18\u6120 \u9053\uff1a\u201c\u4f60\u5230\u5e95\u8ba4\u4e0d\u8ba4\u6211\u505a\u59bb\u5b50\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u600e\u4e48\u4f60\u8001\u662f\u95ee\u6211\u8fd9\u4e2a\uff1f\u201d\u65b0\u5a18\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0 \u4e48\u4e0d\u548c\u6211\u996e\u4ea4\u676f\u9152\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u6211\u5e74\u7eaa\u5c0f\uff0c\u4e0d\u559d\u9152\u3002\u201d\u65b0\u5a18\u6c14\u607c\u4e4b\u6781\uff0c\u5624\u5624\u557c\u6ce3\uff0c\u864e\u5b50 \u597d\u50cf\u6709\u70b9\u7740\u614c\uff0c\u53eb\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u6b3a\u8d1f\u4f60\uff0c\u4f60\u54ed\u4ec0\u4e48\uff1f\u201d\u65b0\u5a18\u9053\uff1a\u201c\u8fd8\u8bf4\u4e0d\u6b3a\u8d1f\u6211\uff1f\u4f60\u4e3a\u4f55\u91cd \u91cd\u5730\u6253\u4e86\u6211\u4e09\u4e0b\uff0c\u73b0\u5728\u8fd8\u75db\uff01\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u4ed6\u4eec\u8bf4\u4e0d\u6253\u5c31\u662f\u6015\u8001\u5a46\uff0c\u5475\uff0c\u539f\u6765\u4f60\u662f\u4e3a\u4e86\u8fd9 \u4e2a\u607c\u6211\uff0c\u90a3\u4e48\u6211\u4e5f\u7ed9\u4f60\u6253\u56de\u4e09\u4e0b\u597d\u4e0d\u597d\uff1f\u4f60\u82e5\u8fd8\u4e0d\u591f\uff0c\u6211\u53ef\u4ee5\u8ba9\u4f60\u4e00\u8fde\u6253\u516d\u4e0b\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u5c0f\u864e\u5b50\u773c\u775b\u7728\u5440\u7728\u7684\uff0c\u6e10\u6e10\u53c8\u9732\u51fa\u4e86\u4e00\u4e1d\u4e8e\u627f\u73e0\u6240\u719f\u6089\u7684\u4ed6\u4ee5\u524d\u7684\u90a3\u79cd\u987d\u76ae \u795e\u6001\u4e86\u3002\u4e8e\u627f\u73e0\u6697\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u95f4\u54ea\u6709\u505a\u4e86\u65b0\u90ce\u8fd8\u8bf4\u8fd9\u6837\u7684\u5b69\u5b50\u6c14\u8bdd\u3002\u201d\u5fc3\u4e2d\u5ffd\u5730\u8d77\u7591\uff0c\u60f3 \u9053\uff1a\u201c\u5c0f\u864e\u5b50\u6d3b\u6cfc\u673a\u7075\uff0c\u513f\u7ae5\u4e2d\u7f55\u6709\u5176\u5339\u3002\u600e\u7684\u5730\u4eca\u665a\u4e00\u526f\u75f4\u5446\u7684\u795e\u6c14\uff1f\u5b8c\u5168\u50cf\u4e2a\u4e0d\u61c2\u4e8b\u7684 \u6751\u7ae5\uff1f\u4f9d\u4ed6\u7684\u6027\u513f\uff0c\u4ed6\u53c8\u600e\u80af\u4efb\u4eba\u6446\u5e03\uff1f\u83ab\u975e\u662f\u8ff7\u4e86\u672c\u6027\u4e0d\u6210\uff1f\u201d\u5979\u8bb0\u8d77\u5f20\u4e39\u67ab\u66fe\u7ecf\u8bf4\u8fc7\uff0c \u4e00\u4e2a\u4eba\u5927\u559c\u5927\u5fe7\u53ef\u4ee5\u8ff7\u5931\u672c\u6027\uff0c\u4f46\u5c0f\u864e\u5b50\u8fd8\u672a\u6210\u4eba\uff0c\u8bba\u7406\u8fd8\u672a\u5f88\u61c2\u5f97\u4eba\u4e16\u7684\u54c0\u4e50\uff0c\u8fd9\u53c8\u8be5\u5982 \u4f55\u89e3\u91ca\uff1f \u3000\u3000\u53ea\u542c\u5f97\u90a3\u65b0\u5a18\u8bf4\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u771f\uff1f\u4f60\u6b22\u559c\u6253\u73b0\u5728\u5c31\u6253\uff01\u201d\u65b0\u5a18\u62ff \u8fc7\u90a3\u628a\u6247\u5b50\uff0c\u5c0f\u864e\u5b50\u5c06\u65b0\u8863\u8131\u4e0b\uff0c\u8892\u9732\u4e0a\u8eab\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u8131\u4e86\u8863\u670d\u8ba9\u4f60\u6253\u4e2a\u75db\u5feb\uff0c\u4f60\u603b \u8be5\u9ad8\u5174\u5566\uff01\u201d\u65b0\u5a18\u5012\u63d0\u6247\u67c4\uff0c\u679c\u7136\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u5411\u5c0f\u864e\u5b50\u80f8\u77a0\u76f4\u6233\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5947\u9053\uff1a\u201c\u600e\u4e48\u65b0\u5a18\u5b50\u4e5f\u662f\u8fd9\u4e48\u7684\u5c0f\u5b69\u5b50\u6c14\u3002\u201d\u731b\u5730\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u89c1\u90a3\u628a\u6247\u5b50\u4e00\u6296 \u4e00\u6233\uff0c\u7528\u7684\u7adf\u662f\u70b9\u7a74\u624b\u6cd5\uff0c\u6247\u67c4\u6307\u5411\u5c0f\u864e\u5b50\u7684\u7487\u7391\u7a74\uff0c\u4e8e\u627f\u73e0\u638c\u5fc3\u6697\u6263\u4e00\u6735\u91d1\u82b1\uff0c\u53ea\u5f85\u65b0\u5a18 \u5c06\u5c0f\u864e\u5b50\u70b9\u6655\uff0c\u5979\u5c31\u7acb\u523b\u8981\u8fdb\u53bb\u6551\u4eba\u3002\u53ea\u89c1\u5c0f\u864e\u5b50\u5438\u4e86\u53e3\u6c14\uff0c\u65b0\u5a18\u5b50\u5728\u4ed6\u80f8\u819b\u8fde\u6233\u4e09\u4e0b\uff0c\u4ed6 \u7684\u808c\u80a4\u4e0a\u597d\u50cf\u6d82\u4e86\u6cb9\u4e00\u822c\uff0c\u6247\u67c4\u4e00\u6cbe\u7740\u4ed6\u7684\u8eab\u4f53\uff0c\u5c31\u7acb\u523b\u6ed1\u5f00\uff0c\u65b0\u5a18\u5b50\u867d\u7136\u7528\u7684\u662f\u91cd\u624b\u6cd5\u70b9 \u7a74\uff0c\u5c0f\u864e\u5b50\u53ea\u5f53\u5979\u662f\u6293\u75d2\uff0c \u3000\u3000\u4e8e\u627f\u73e0\u770b\u5f97\u53c8\u60ca\u53c8\u559c\uff0c\u60f3\u4e0d\u5230\u4e00\u5e74\u4e0d\u89c1\uff0c\u5c0f\u864e\u5b50\u7684\u529f\u592b\u7adf\u662f\u7cbe\u8fdb\u5982\u65af\uff01\u672c\u6765\u5185\u529f\u7ec3\u5230\u6700 \u4e0a\u4e58\u7684\u5883\u754c\uff0c\u53ef\u4ee5\u81ea\u95ed\u7a74\u9053\uff0c\u4e0d\u4ff1\u70b9\u7a74\uff0c\u4f46\u90a3\u5373\u4f7f\u662f\u5929\u8d44\u6781\u597d\u7684\u4eba\uff0c\u4e5f\u975e\u5341\u5e74\u4ee5\u4e0a\u7684\u529f\u529b\u4e0d \u884c\u3002\u4f46\u5370\u5ea6\u7684\u745c\u73c8\u529f\u592b\uff0c\u5374\u53e6\u6709\u4e00\u79cd\u95ed\u6c14\u548c\u7ec3\u808c\u8089\u7684\u65b9\u6cd5\uff0c\u540c\u6837\u53ef\u4ee5\u4e0d\u60e7\u70b9\u7a74\uff0c\u6b66\u529f\u6709\u6839\u57fa \u7684\u4eba\uff0c\u7ec3\u4e0a\u4e24\u4e09\u5e74\u4fbf\u884c\uff0c\u73b0\u5728\u5c0f\u864e\u5b50\u53ea\u8ddf\u4e86\u9ed1\u767d\u6469\u8bc3\u4e00\u5e74\uff0c\u5c45\u7136\u4efb\u4eba\u7528\u91cd\u624b\u6cd5\u70b9\u7a74\uff0c\u8fdb\u5883\u4e4b \u901f\uff0c\u90a3\u662f\u975e\u5e38\u7f55\u89c1\u7684\u4e86\u3002\u8fd9\u79cd\u529f\u592b\u4e0e\u4e2d\u56fd\u4e0a\u4e58\u7684\u5185\u529f\u4e4b\u7406\u76f8\u901a\uff0c\u4e0d\u8fc7\u6240\u8d70\u7684\u8def\u5b50\u5374\u5168\u7136\u4e24 \u6837\uff0c\u745c\u73c8\u5728\u67d0\u4e9b\u65b9\u9762\uff08\u5982\u95ed\u6c14\u7ec3\u7b4b\uff09\u89c1\u6548\u8f83\u901f\uff0c\u800c\u4e2d\u56fd\u6b63\u5b97\u7684\u7384\u95e8\u5185\u529f\uff0c\u8bb2\u7a76\u7684\u662f\u64e6\u771f\u517b \u5143\uff0c\u6839\u57fa\u5374\u662f\u8f83\u4e3a\u6df1\u539a\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u770b\u5f97\u51fa\u795e\uff0c\u53ea\u542c\u5f97\u5c0f\u864e\u5b50\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u56de\u4e86\u6211\u4e09\u4e0b\uff0c\u6c14\u6d88\u4e86\u5427\uff1f\u201d\u65b0\u5a18\u9053\uff1a\u201c\u4e0d \u6210\uff0c\u4f60\u4eca\u665a\u6253\u6211\u4e4b\u65f6\uff0c\u6211\u75db\u5f97\u6cea\u6c34\u90fd\u6d41\u4e86\u51fa\u6765\uff0c\u4f60\u5374\u8fde\u7709\u5934\u4e5f\u4e0d\u76b1\u4e00\u4e0b\uff0c\u53ef\u77e5\u662f\u4e00\u70b9\u75bc\u75db\u90fd \u6ca1\u6709\u7684\u4e86\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u5440\uff0c\u90a3\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u6211\u662f\u5e08\u7236\u6559\u7684\uff0c\u4f60\u600e\u4e48\u6253\u6211\uff0c\u6211\u90fd\u4e0d\u4f1a\u75bc \u75db\uff0c\u522b\u4eba\u5b66\u4e0d\u6765\u7684\uff01\u201d\u65b0\u5a18\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u5b66\u4f1a\uff0c\u522b\u4eba\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u5b66\u4f1a\uff1f\u201d\u5c0f\u864e\u4e8e\u7741\u5927\u773c\u775b\uff0c \u4f3c\u5e73\u89c9\u5f97\u5979\u8bf4\u7684\u9887\u6709\u9053\u7406\u3002\u65b0\u5a18\u9053\uff1a\u201c\u5582\uff0c\u4f60\u8fd9\u4e2a\u529f\u592b\u6559\u6211\u6210\u4e0d\u6210\uff1f\u201d\u5c0f\u864e\u5b50\u5446\u4e86\u4e00\u5446\uff0c\u773c \u775b\u91cc\u9732\u51fa\u60f6\u60d1\u7684\u795e\u6c14\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u6210\u3002\u201d\u65b0\u5a18\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u6210\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u8fd9\uff0c \u8fd9\u662f\u4e0d\u80fd\u6559\u522b\u4eba\u7684\u3002\u201d\u65b0\u5a18\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u522b\u4eba\u4f60\u53ef\u4ee5\u4e0d\u6559\uff0c\u6211\u662f\u4f60\u7684\u59bb\u5b50\uff0c\u592b\u59bb\u5982\u540c\u4e00\u4f53\uff0c \u4f60\u600e\u4e48\u53ef\u4ee5\u4e0d\u6559\uff1f\u201d\u864e\u5b50\u54ed\u4e27\u7740\u8138\u9053\uff1a\u201c\u59bb\u5b50\u5c31\u6709\u8fd9\u4e48\u5389\u5bb3\u5417\uff1f\u201d\u65b0\u5a18\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u59bb \u5b50\u8981\u4ec0\u4e48\u4e08\u592b\u5c31\u8981\u7ed9\u5979\uff01\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u54ce\u54df\uff0c\u90a3\u6211\u8fd9\u4e00\u751f\u90fd\u4e0d\u8981\u59bb\u5b50\uff01\u201d\u65b0\u5a18\u6012\u9053\uff1a\u201c\u4f60 \u6211\u5df2\u7136\u6210\u5a5a\uff0c\u4f60\u60f3\u7529\u6389\u53ef\u4e0d\u6210\uff01\u201d\u5c0f\u864e\u5b50\u8d8a\u53d1\u60ca\u6050\uff0c\u5446\u5446\u5730\u60f3\u4e86\u4e00\u9635\uff0c\u5ffd\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u6211\u628a \u8fd9\u529f\u592b\u6559\u7ed9\u4f60\uff0c\u4f60\u4e0d\u505a\u6211\u7684\u59bb\u5b50\u6210\u4e0d\u6210\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u89c1\u5c0f\u864e\u5b50\u5982\u6b64\u50bb\u6c14\uff0c\u5fc3\u60f3\u65b0\u5a18\u5fc5\u7136\u8981\u53d1\u6012\u7684\u4e86\u3002\u54ea\u77e5\u65b0\u5a18\u6258\u816e\u4e00\u60f3\uff0c\u5c45\u7136\u8bf4\u9053\uff1a \u201c\u5440\uff0c\u4f60\u65e2\u7136\u4e0d\u613f\u505a\u6211\u4e08\u592b\uff0c\u90a3\u4e5f\u52c9\u5f97\u3002\u4f60\u628a\u8fd9\u529f\u592b\u6559\u6211\uff0c\u6211\u4e0d\u505a\u4f60\u7684\u59bb\u5b50\u7f62\u3002\u8fd9\u529f\u592b\u8981\u7ec3 \u591a\u4e45\u624d\u6210\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u8fdf\u5219\u4e09\u5e74\uff0c\u5feb\u5219\u4e00\u5e74\u3002\u4e0d\u8fc7\u5b66\u4e86\u8fd0\u529f\u7684\uff0c\u5c31\u53ef\u4ee5\u81ea\u5df1\u7ec3\u4e86\u3002\u201d\u65b0 \u5a18\u9053\uff1a\u201c\u5b66\u4f1a\u7684\u65b9\u6cd5\u8981\u591a\u4e45\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u5341\u5929\u561b\u5dee\u4e0d\u591a\u3002\u201d\u65b0\u5a18\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5341\u5929\u4e4b\u5185 \u6559\u4f1a\u4e86\u6211\uff0c\u6211\u5341\u5929\u4e4b\u540e\u653e\u4f60\u8d70\uff01\u201d\u5c0f\u864e\u5b50\u559c\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d\u65b0\u5a18\u9053\uff1a\u201c\u6211\u4eec\u82d7\u5bb6\u7684\u8bdd\u8bf4\u4e00\u4e0d \u4e8c\uff01\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u597d\u561b\uff0c\u9a6c\u4e0a\u5c31\u6559\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u7591\u4e91\u5927\u8d77\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u65b0\u5a18\u5b50\u770b\u6765\u5e76\u4e0d\u662f\u771f\u5fc3\u60f3\u5ac1\u5c0f\u864e\u5b50\u3002\u5979\u5e74\u7eaa\u867d\u7136\u6bd4\u6211 \u8fd8\u5c0f\uff0c\u5374\u4f3c\u751a\u6709\u8ba1\u8c0b\uff0c\u53ef\u80fd\u662f\u5927\u4eba\u6559\u5979\u7684\u3002\u5509\u5440\uff0c\u4e0d\u597d\uff0c\u83ab\u975e\u8fd9\u662f\u4e2a\u5708\u5957\uff0c\u8981\u9a97\u864e\u5b50\u7684\u6b66 \u529f\u3002\u201d\u8981\u77e5\u5404\u6d3e\u7684\u6b66\u529f\u5fc3\u6cd5\uff0c\u90e1\u662f\u672c\u95e8\u4e4b\u79d8\uff0c\u7edd\u5bf9\u4e0d\u80fd\u4f20\u7ed9\u5916\u4eba\u7684\uff0c\u9664\u975e\u5f97\u5230\u4e1a\u5e08\u7684\u5141\u8bb8\u3002 \u4e8e\u627f\u73e0\u89c1\u5c0f\u864e\u5b50\u5c31\u8981\u4f20\u6388\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u5ffd\u7136\u4ece\u5c4b\u6a90\u4e0a\u4e00\u8dc3\u800c\u4e0b\uff0c\u8df3\u5165\u65b0\u623f\uff01 \u3000\u3000\u90a3\u5c0f\u65b0\u5a18\u7a81\u7136\u89c1\u5c4b\u4e0a\u8df3\u4e0b\u4e00\u4e2a\u4eba\u6765\uff0c\u8fd9\u4e00\u5413\u975e\u540c\u5c0f\u53ef\uff0c\u5f20\u5f00\u4e86\u5634\u5df4\uff0c\u5374\u53eb\u4e0d\u51fa\u58f0\u6765\u3002\u5c0f \u864e\u5b50\u4e00\u6d3e\u832b\u7136\u7684\u795e\u8272\uff0c\u5b9a\u7740\u773c\u775b\u76ef\u7740\u4e8e\u627f\u73e0\uff0c\u663e\u5f97\u975e\u5e38\u60f6\u611f\uff0c\u4e8e\u627f\u73e0\u4e0d\u7406\u90a3\u4e2a\u65b0\u5a18\uff0c\u51b2\u7740\u5c0f \u864e\u5b50\u56b7\u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff0c\u4f60\u8ba4\u5f97\u6211\u4e48\uff1f\u201d\u5c0f\u864e\u5b50\u9000\u540e\u4e24\u6b65\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u662f\u8c01\uff1f\u54b1 \u4eec\u5728\u54ea\u513f\u89c1\u8fc7\uff1f\u201d\u90a3\u526f\u8bf4\u8bdd\u7684\u795e\u6c14\uff0c\u5c31\u50cf\u68a6\u6e38\u60a3\u8005\u4e00\u6837\uff0c\u4e5f\u8bb8\u4ed6\u6b63\u5728\u82e6\u82e6\u5730\u601d\u7d22\uff0c\u5728\u54ea\u513f\u89c1 \u8fc7\u4e8e\u627f\u73e0\uff1f \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u4e2d\u60b2\u75db\uff0c\u770b\u8fd9\u60c5\u5f62\u5c0f\u864e\u5b50\u5b9a\u662f\u5403\u4e86\u8ff7\u836f\u65e0\u7591\uff0c\u53ef\u601c\u4e00\u4e2a\u673a\u4f36\u7684\u5b69\u5b50\uff0c\u7adf\u88ab\u6298\u78e8 \u6210\u8fd9\u4e2a\u6a21\u6837\uff0c\u4e8e\u627f\u73e0\u4e00\u4f38\u624b\uff0c\u6293\u7740\u4e86\u5c0f\u864e\u5b50\u7684\u80a9\u818a\uff0c\u53eb\u9053\uff1a\u201c\u6211\u662f\u4f60\u7684\u627f\u73e0\u59d0\u59d0\uff0c\u4f60\u4e0d\u8bb0\u5f97 \u4e86\u4e48\uff1f\u201d\u5c0f\u864e\u5b50\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u627f\u73e0\uff1f\u201d\u4f3c\u4e4e\u5374\u4ecd\u7136\u4e0d\u6562\u8ba4\u5979\u3002\u4e8e\u627f\u73e0\u5ffd\u5730\u60f3\u8d77\u5f20\u4e39\u67ab\u6240\u6388\u7684 \u201c\u7384\u529f\u79d8\u8bc0\u201d\u4e2d\uff0c\u6709\u4e00\u4e2a\u65b9\u6cd5\u80fd\u533b\u5931\u5fc3\u75af\u7684\uff0c\u4e8e\u662f\u7a81\u7136\u4f38\u51fa\u6307\u7532\uff0c\u5728\u4ed6\u7684\u4eba\u4e2d\u6390\u4e86\u4e00\u4e0b\uff0c\u5c0f \u864e\u5b50\u201c\u54d7\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u8d77\u6765\uff0c\u4e8e\u627f\u73e0\u62a2\u8fc7\u65b0\u5e8a\u4e0a\u7684\u90a3\u628a\u6247\uff0c\u5f20\u5f00\u4e00\u62e8\uff0c\u9053\uff1a\u201c\u8ba4\u5f97\u6211\u5417\uff1f\u201d \u5c0f\u864e\u5b50\u53cc\u773c\u4e00\u5f20\uff0c\u9053\uff1a\u201c\u55ef\uff0c\u8fd9\u624b\u6cd5\u662f\u4f60\u6559\u7ed9\u6211\u7684\uff01\u627f\u73e0\u59d0\u59d0\uff01\u201d\u4e8e\u627f\u73e0\u53bb\u5e74\u6625\u5929\uff0c\u521d\u89c1\u5c0f \u864e\u5b50\u4e4b\u65f6\uff0c\u66fe\u7528\u6247\u5b50\u53cd\u62e8\u5c0f\u864e\u5b50\u6cfc\u5979\u7684\u6c61\u6c34\uff0c\u5c0f\u864e\u5b50\u4eca\u665a\u4ee5\u6247\u62e8\u9152\u7684\u624b\u6cd5\uff0c\u6b63\u662f\u4e8e\u627f\u73e0\u6240 \u6388\uff0c\u4e8e\u627f\u73e0\u7528\u8fd9\u65b9\u6cd5\uff0c\u679c\u7136\u53eb\u5c0f\u864e\u5b50\u8bb0\u8d77\u6765\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5927\u559c\uff0c\u9053\uff1a\u201c\u8bb0\u5f97\u4fbf\u597d\uff0c\u5feb\u8ddf\u6211\u8d70\u3002\u201d\u5c0f\u864e\u5b50\u5ffd\u7136\u73b0\u51fa\u60ca\u60f6\u4e4b\u8272\uff0c\u7529\u8131\u4e86\u4e8e\u627f\u73e0 \u7684\u624b\uff0c\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u4e0d\u8d70\uff0c\u4f60\u4e5f\u8981\u505a\u6211\u7684\u59bb\u5b50\u5417\uff1f\u201d\u539f\u6765\u5c0f\u864e\u5b50\u786e\u662f\u5403\u4e86\u8ff7\u836f\uff0c\u4e8e\u627f\u73e0\u7528\u533b \u5931\u5fc3\u75af\u7684\u65b9\u6cd5\u533b\u4ed6\uff0c\u5e76\u4e0d\u5bf9\u8def\uff0c\u5c0f\u864e\u5b50\u867d\u7136\u8bb0\u8d77\u4e86\u6709\u4e00\u4e2a\u201c\u627f\u73e0\u59d0\u59d0\u201d\uff0c\u4f46\u4eba\u5374\u5e76\u672a\u6e05\u9192\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u505a\u4f60\u7684\u59bb\u5b50\uff0c\u6211\u662f\u8981\u6551\u4f60\u51fa\u53bb\uff0c\u4f60\u6015\u4ec0\u4e48\uff1f\u201d\u4e00\u628a \u62d6\u7740\u5c0f\u864e\u5b50\u4fbf\u5f80\u5916\u8dd1\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u539f\u6765\u662f\u90a3\u65b0\u5a18\u62bd\u51fa\u4e86\u4e00\u67c4\u5229\u5203\uff0c\u6076\u72e0\u72e0\u5730 \u5411\u4e8e\u627f\u73e0\u624b\u81c2\u4fbf\u65a9\uff0c\u53e3\u4e2d\u9a82\u9053\uff1a\u201c\u4e0d\u8981\u8138\u7684\u5973\u4eba\uff0c\u4e3a\u4ec0\u4e48\u62a2\u6211\u7684\u4e08\u592b\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u54ea\u91cc\u628a\u5979\u653e\u5728\u5fc3\u4e0a\uff0c\u53cd\u624b\u4e00\u6293\uff0c\u7acb\u523b\u628a\u5979\u7684\u90a3\u67c4\u5229\u5203\u593a\u53bb\uff0c\u63b7\u51fa\u5c4b\u5916\uff0c\u6c14\u5979\u4e0d \u8fc7\uff0c\u56de\u5934\u9a82\u9053\uff1a\u201c\u5478\uff0c\u4f60\u624d\u4e0d\u8981\u8138\uff0c\u4f60\u54ea\u91cc\u662f\u8bda\u5fc3\u5ac1\u4ed6\uff1f\u4f60\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u600e\u4e48\u8fd9\u6837\u5978\u6ed1\uff0c\u8981\u9a97 \u4ed6\u7684\u6b66\u529f\uff1f\u201d\u90a3\u5c0f\u65b0\u5a18\u5ffd\u5730\u54c7\u54c7\u5927\u54ed\uff0c\u5728\u5730\u4e0a\u4e00\u6eda\uff0c\u53cc\u811a\u7a81\u7136\u4e71\u8e22\u4e8e\u627f\u73e0\uff0c\u5c45\u7136\u662f\u83b2\u82b1\u817f\u7684 \u529f\u592b\uff0c\u5c0f\u864e\u5b50\u6b63\u81ea\u53eb\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u4e5f\u8bf4\u8fc7\u4e0d\u505a\u6211\u7684\u59bb\u5b50\u7684\uff01\u201d\u5ffd\u89c1\u65b0\u5a18\u4e71\u54ed\u4e71\u8e22\uff0c\u4e00\u65f6\u95f4 \u53c8\u6ca1\u4e86\u4e3b\u610f\uff0c\u4e8e\u627f\u73e0\u53cd\u638c\u4e00\u626b\uff0c\u5c0f\u864e\u5b50\u867d\u7136\u795e\u667a\u4e0d\u6e05\uff0c\u5374\u8fd8\u77e5\u9053\u8fd9\u662f\u4e00\u8bb0\u6740\u624b\uff0c\u6025\u5fd9\u62c9\u7740\u4e8e \u627f\u73e0\u7684\u81c2\u818a\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u4f24\u5979\uff0c\u5979\u662f\u597d\u4eba\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ec0\u4e48\u597d\u4eba\uff1f\u201d\u626c\u624b\u53c8\u8981\u6253\u4e0b\uff0c\u5c0f \u864e\u5b50\u5fd9\u9053\uff1a\u201c\u4e0d\u8981\u6253\u5979\uff0c\u6211\u8ddf\u4f60\u8d70\u4fbf\u7f62\uff01\u201d\u4e8e\u627f\u73e0\u6b63\u662f\u8981\u4ed6\u8bf4\u8fd9\u53e5\u8bdd\uff0c\u653e\u8fc7\u65b0\u5a18\uff0c\u62d6\u4e86\u5c0f\u864e \u5b50\u7acb\u523b\u7a9c\u51fa\u95e8\u5916\u3002 \u3000\u3000\u521a\u8dd1\u5230\u5916\u9762\u7684\u9662\u5b50\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u9634\u607b\u607b\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u597d\u5927\u80c6\u7684\u5996\u5973\uff0c\u5c45\u7136\u6562\u5230\u8fd9\u513f\u62a2 \u65b0\u90ce\u6765\u4e86\uff01\u201d\u4f46\u89c1\u4e00\u4e2a\u4eba\u62e6\u7740\u53bb\u8def\u3002 \u3000\u3000\u6b63\u662f\u5728\u5a5a\u793c\u4e2d\u5f3a\u8feb\u5c0f\u864e\u5b50\u559d\u4ea4\u676f\u9152\u7684\u90a3\u4e2a\u6c49\u4eba\uff0c\u4ed6\u7a7f\u7684\u5374\u662f\u82d7\u5bb6\u670d\u9970\uff0c\u4e24\u8fb9\u81c2\u818a\u5404\u6709\u4e94 \u4e2a\u94f6\u73af\uff0c\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u4ee5\u624b\u4f5c\u52bf\uff0c\u6447\u52a8\u94f6\u73af\uff0c\u53ee\u5f53\u4f5c\u54cd\uff0c\u66f4\u663e\u5f97\u8be1\u5f02\u975e\u5e38\uff01 \u3000\u3000\u4e8e\u627f\u73e0\u61d2\u5f97\u6253\u8bdd\uff0c\u7389\u624b\u4e00\u626c\uff0c\u9884\u5148\u6263\u5728\u638c\u5fc3\u7684\u4e09\u6735\u91d1\u82b1\u7acb\u5373\u7834\u7a7a\u98de\u51fa\uff0c\u5206\u6253\u90a3\u602a\u4eba\u7684\u7709 \u5174\u3001\u9633\u767d\u3001\u8840\u6d77\u4e0a\u4e2d\u4e0b\u4e09\u5904\u5927\u7a74\uff0c\u90a3\u602a\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u624b\u81c2\u4e00\u6325\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u7528\u7684\u662f\u4ec0\u4e48\u624b\u6cd5\uff0c \u53ea\u542c\u5f97\u545c\u545c\u602a\u5578\uff0c\u5de6\u81c2\u7684\u4e00\u4e2a\u94f6\u73af\u5ffd\u7136\u8131\u81c2\u98d8\u51fa\uff0c\u5929\u4e0b\u7684\u6697\u5668\uff0c\u5341\u4e5d\u90fd\u662f\u76f4\u7ebf\u98de\u884c\uff0c\u8fd9\u602a\u4eba \u6240\u53d1\u7684\u94f6\u73af\uff0c\u5374\u662f\u4e0a\u4e0b\u76d8\u65cb\uff0c\u8d70\u4e00\u4e2a\u534a\u5f27\u5f62\u7684\u8def\u5b50\uff0c\u6765\u52bf\u8fdc\u4e0d\u5982\u4e8e\u627f\u73e0\u91d1\u82b1\u7684\u8fc5\u731b\uff0c\u8f6c\u773c\u4e4b \u95f4\uff0c\u5374\u628a\u4e8e\u627f\u73e0\u6240\u53d1\u7684\u4e09\u6735\u91d1\u82b1\u90fd\u5377\u5165\u73af\u4e2d\uff0c\u671b\u5947\u5999\u7684\u662f\u90a3\u94f6\u73af\u80fd\u53d1\u80fd\u6536\uff0c\u4e8e\u627f\u73e0\u6b63\u62df\u62d4\u5251 \u62b5\u5fa1\uff0c\u90a3\u94f6\u73af\u53c8\u5df2\u56de\u5230\u4e86\u602a\u4eba\u624b\u4e2d\uff0c\u602a\u4eba\u53d6\u51fa\u91d1\u82b1\uff0c\u5fae\u5fae\u9732\u51fa\u8be7\u5f02\u4e4b\u8272\uff01 \u3000\u3000\u4e8e\u627f\u73e0\u4e5f\u662f\u5403\u60ca\u4e0d\u5c0f\uff0c\u770b\u90a3\u602a\u4eba\u6240\u53d1\u7684\u94f6\u73af\u6697\u5668\uff0c\u4e0d\u6b62\u662f\u624b\u6cd5\u5947\u5999\uff0c\u800c\u4e14\u7eaf\u51ed\u5185\u529b\u64cd\u7eb5 \u63a7\u5236\uff0c\u8fd9\u4e00\u4efd\u529f\u592b\uff0c\u4e5f\u8db3\u4ee5\u9707\u4e16\u9a87\u4fd7\uff0c\u4e8e\u627f\u73e0\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff0c\u4f60\u60f3\u51fa\u53bb\uff0c\u54b1\u4eec\u53ef\u5f97\u5e76 \u80a9\u95ef\u554a\uff01\u201d\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5c0f\u864e\u5b50\u8fd9\u4e00\u5e74\u6765\u529f\u529b\u5927\u8ff8\uff0c\u6709\u4ed6\u76f8\u52a9\uff0c\u5bf9\u4ed8\u8fd9\u4e2a\u602a\u4eba\uff0c\u8c05\u4e0d\u81f3\u4e8e\u5403 \u4e8f\uff01\u201d \u3000\u3000\u5374\u4e0d\u6599\u5c0f\u864e\u5b50\u5e76\u65e0\u56de\u7b54\uff0c\u4e8e\u627f\u73e0\u56de\u7738\u4e00\u77a5\uff0c\u4f46\u89c1\u4ed6\u4e00\u7247\u832b\u7136\u7684\u795e\u8272\uff0c\u7adf\u662f\u5446\u5446\u5730\u89c2\u6218\uff0c \u6beb\u65e0\u534a\u70b9\u51fa\u624b\u7684\u6a21\u6837\uff0c\u4e8e\u627f\u73e0\u5927\u6025\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u5ffd\u542c\u5f97\u90a3\u602a\u4eba\u53c8\u662f\u4e00\u58f0 \u72de\u7b11\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u62a2\u65b0\u90ce\u4e5f\u5f97\u8981\u4eba\u5fc3\u7518\u60c5\u613f\u624d\u884c\u5475\uff01\u5478\uff0c\u8fd9\u6837\u62d6\u62d6\u62c9\u62c9\u7684\uff0c\u8fde\u4e00\u70b9\u7f9e\u803b\u90fd \u6ca1\u6709\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u5927\u6012\u65a5\u9053\uff1a\u201c\u4f60\u4eec\u624d\u662f\u786c\u62a2\u65b0\u90ce\uff0c\u5478\uff0c\u9a97\u5c0f\u5b69\u5b50\uff0c\u4e0d\u8981\u8138\uff01\u201d\u90a3\u602a\u4eba\u51b7\u7b11 \u9053\uff1a\u201c\u4f60\u8981\u62c9\u7537\u4eba\u8fd9\u91cc\u6709\u7684\u662f\uff0c\u4ed6\u4e0d\u613f\u8e09\u4f60\u8d70\uff0c\u4f60\u8fd8\u5728\u8fd9\u513f\u7ea0\u7f20\u4ec0\u4e48\uff1f\u770b\u5728\u4f60\u8fd9\u4e09\u6735\u91d1\u82b1\u7684 \u9762\u4e0a\uff0c\u6211\u4e0d\u4f24\u4f60\uff0c\u4f60\u7ed9\u6211\u6eda\uff01\u56de\u53bb\u544a\u8bc9\u4f60\u5e08\u6bcd\u77e5\u9053\uff0c\u5c31\u8bf4\u662f\u8d64\u57ce\u95e8\u4e0b\u7684\u8499\u5143\u5b50\u5c06\u8fd9\u4e09\u6735\u91d1\u82b1 \u7559\u4e0b\u4e86\u3002\u5979\u8981\u53d6\u56de\u91d1\u82b1\uff0c\u53ef\u5230\u4e4c\u8499\u5c71\u6765\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u51e0\u66fe\u53d7\u8fc7\u5982\u6b64\u4fae\u8fb1\uff0c\u6c14\u5f97\u7389\u989c\u53d8\u8272\uff0c\u55d6\u5730\u4e00\u58f0\u62d4\u51fa\u9752\u51a5\u5b9d\u5251\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u864e \u5b50\u5feb\u8e09\u6211\u8d70\uff01\u201d\u5411\u524d\u4fbf\u95ef\uff0c\u8499\u5143\u5b50\u559d\u9053\uff1a\u201c\u5c0f\u864e\u5b50\u7559\u4e0b\u3002\u4f60\u7ed9\u6211\u6eda\uff01\u201d\u957f\u81c2\u4e00\u6325\uff0c\u4e24\u4e2a\u94f6\u73af \u76d8\u65cb\u98de\u81f3\uff0c\u7adf\u662f\u8981\u903c\u4e8e\u627f\u73e0\u9003\u8d70\uff0c\u4e8e\u627f\u73e0\u5927\u6012\uff0c\u811a\u5c16\u4e00\u70b9\uff0c\u8eab\u5f62\u75be\u8d77\uff0c\u4e0d\u5f85\u90a3\u4e24\u4e2a\u94f6\u73af\u98de \u5230\uff0c\u5530\u5530\u4e24\u5251\uff0c\u8fce\u7740\u94f6\u73af\u4fbf\u65a9\uff0c\u4e8e\u627f\u73e0\u7684\u8f7b\u529f\u5251\u6cd5\u9664\u4e86\u7a0d\u6b20\u706b\u5019\u4e4b\u5916\uff0c\u5728\u6c5f\u6e56\u4e0a\u5df2\u7f55\u6709\u5339 \u654c\uff0c\u90a3\u602a\u4eba\u8fd8\u771f\u6599\u4e0d\u5230\u5979\u6765\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c\u94f6\u73af\u672a\u53ca\u6536\u56de\uff0c\u5df2\u88ab\u5979\u90a3\u628a\u524a\u94c1\u5982\u6ce5\u7684\u5b9d\u5251\u524a\u4e3a\u56db \u7247\uff01 \u3000\u3000\u4e8e\u627f\u73e0\u5251\u8d70\u8fde\u73af\uff0c\u8eab\u5f62\u4e00\u79fb\uff0c\u9752\u51a5\u5251\u7684\u950b\u8292\u5df2\u5728\u8499\u5143\u5b50\u7684\u773c\u524d\u75be\u95ea\uff0c\u8499\u5143\u5b50\u559d\u58f0\uff1a\u201c\u597d \u4e00\u628a\u5b9d\u5251\uff01\u201d\u6325\u8896\u4e00\u62c2\uff0c\u7a81\u7136\u6a2a\u638c\u5207\u8155\uff0c\u64d2\u62ff\u624b\u6cd5\u65e2\u5feb\u4e14\u72e0\uff0c\u7adf\u4e0d\u4e9a\u4e8e\u5a04\u6850\u5b59\uff0c\u4e8e\u627f\u73e0\u7684\u5251 \u62db\u7528\u8001\uff0c\u6025\u5207\u4e4b\u95f4\u7adf\u662f\u62bd\u4e0d\u56de\u6765\uff01\u773c\u770b\u6301\u5251\u7684\u624b\u8155\u5c31\u8981\u88ab\u90a3\u602a\u4eba\u4e00\u638c\u5207\u65ad\uff01 \u3000\u3000\u5c0f\u864e\u5b50\u201c\u54ce\u5440\u201d\u53eb\u4e86\u4e00\u58f0\uff0c\u5ffd\u89c1\u4e8e\u627f\u73e0\u5de6\u624b\u6240\u634f\u7684\u5251\u8bc0\u7a81\u7136\u4e00\u6536\uff0c\u4e94\u6307\u9760\u62e2\uff0c\u4e2d\u3001\u98df\u4e8c \u6307\u5fae\u5c48\uff0c\u6307\u9aa8\u5982\u68f1\uff0c\u8f7b\u8f7b\u4e00\u201c\u5544\u201d\uff0c\u8499\u5143\u5b50\u8fd8\u771f\u6599\u4e0d\u5230\u4e8e\u627f\u73e0\u6709\u6b64\u602a\u62db\uff0c\u6025\u5fd9\u540e\u9000\uff0c\u90a3\u62db\u64d2 \u62ff\u624b\u81ea\u662f\u4e0d\u89e3\u800c\u89e3\u3002\u5c0f\u864e\u5b50\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u9e64\u62f3\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d\u5251\u5c16\u4e00\u6307\uff0c\u5de6 \u62f3\u4e00\u4e2a\u52fe\u62f3\u5728\u5251\u5e95\u7a7f\u51fa\uff0c\u5c0f\u864e\u5b50\u53c8\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u8c79\u62f3\uff01\u201d\u5f53\u65e5\u9ed1\u767d\u6469\u8bc3\u5728\u592a\u6e56\u5c71\u5e84\u6559\u5c0f \u864e\u5b50\u7ec3\u201c\u7f57\u6c49\u4e94\u884c\u795e\u62f3\u201d\uff0c\u628a\u5927\u5185\u7684\u4e03\u540d\u536b\u58eb\u5f53\u4f5c\u201c\u6d3b\u9776\u5b50\u201d\uff0c\u6253\u5f97\u4ed6\u4eec\u843d\u82b1\u6d41\u6c34\uff0c\u5176\u65f6\u4e8e \u627f\u73e0\u4e0e\u5c0f\u864e\u5b50\u540c\u5728\u65c1\u8fb9\u89c2\u6218\uff0c\u5fc3\u9886\u795e\u4f1a\uff0c\u90fd\u5b66\u4f1a\u4e86\u8fd9\u79cd\u4e0a\u4e58\u62f3\u6cd5\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u867d\u662f\u53d7\u4eba\u4f5c\u5f04\uff0c\u4e2d\u4e86\u8ff7\u836f\uff0c\u4f46\u7075\u6027\u5c1a\u672a\u5b8c\u5168\u6d88\u5931\uff0c\u5ffd\u89c1\u4e8e\u627f\u73e0\u4f7f\u51fa\u8fd9\u79cd\u62f3\u6cd5\uff0c\u5e08 \u7236\u5f53\u65e5\u6388\u62f3\u7684\u60c5\u666f\uff0c\u4f9d\u7a00\u8bb0\u5f97\uff0c\u82e6\u82e6\u601d\u7d22\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u8fd8\u672a\u80fd\u60f3\u5f97\u8d77\u6765\uff0c\u5ffd\u89c1\u8499\u5143\u5b50\u53cc\u81c2 \u7b95\u5f20\uff0c\u62f3\u6253\u811a\u8e22\uff0c\u72e0\u72e0\u6251\u51fb\uff0c\u4e8e\u627f\u73e0\u53c8\u7ed9\u903c\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u5c0f\u864e\u5b50\u547c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u4f7f\u9f99 \u62f3\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u5fd8\u8bb0\u5566\uff01\u201d\u5176\u5b9e\u5e76\u4e0d\u662f\u5979\u5fd8\u8bb0\uff0c\u800c\u662f\u56e0\u4e3a\u7f57\u6c49\u795e\u62f3\u7684\u4e94\u79cd\u62f3\u6cd5\u4e2d\uff0c\u9f99 \u62f3\u6700\u4e3a\u7528\u529b\uff0c\u4e8e\u627f\u73e0\u5230\u5e95\u662f\u4e2a\u5c11\u5973\uff0c\u6c14\u529b\u8fdc\u8fdc\u4e0d\u5982\u5bf9\u65b9\uff0c\u6240\u4ee5\u867d\u7136\u77e5\u9053\u8fd9\u4e00\u62db\u6700\u597d\u7528\u9f99\u62f3\u5316 \u89e3\uff0c\u5374\u4e0d\u6562\u4e0e\u5bf9\u65b9\u786c\u78b0\u3002\u8499\u5143\u5b50\u770b\u51fa\u5979\u7684\u5f31\u70b9\uff0c\u5728\u64d2\u62ff\u624b\u4e2d\u6742\u4ee5\u521a\u731b\u65e0\u6bd4\u7684\u6df7\u5143\u771f\u529b\uff0c\u5e78\u800c \u4e8e\u627f\u73e0\u7684\u5251\u6cd5\u8f7b\u7075\u5947\u5999\uff0c\u9752\u51a5\u5b9d\u5251\u53c8\u4e13\u7834\u91d1\u949f\u7f69\u94c1\u5e03\u886b\u8fd9\u7c7b\u786c\u529f\uff0c\u7f57\u6c49\u4e94\u884c\u62f3\u4e2d\u7684\u9e64\u62f3\u3001\u8c79 \u62f3\u3001\u86c7\u62f3\u4e0d\u987b\u7528\u751a\u6c14\u529b\uff0c\u6b63\u5408\u4e8e\u627f\u73e0\u4f7f\uff0c\u4e8e\u627f\u73e0\u53f3\u624b\u4f7f\u767e\u53d8\u7384\u673a\u5251\u6cd5\uff0c\u5de6\u624b\u4f7f\u7f57\u6c49\u795e\u62f3\uff0c\u867d \u7136\u5904\u5728\u4e0b\u98ce\uff0c\u5374\u4e5f\u5c1a\u80fd\u62b5\u654c\u3002\u90a3\u5c0f\u65b0\u5a18\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u5df1\u8d70\u5230\u65c1\u8fb9\u89c2\u6218\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u5c0f\u864e \u5b50\uff0c\u4f60\u8bf4\u8bdd\u7b97\u4e0d\u7b97\u6570\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff0c\u8fd8\u4e0d\u5feb\u8d70\uff1f\u5979\u53c8\u8981\u7f20\u4f60\u505a\u4e08\u592b\u5566\u3002\u201d\u8bf4\u8bdd\u5206 \u5fc3\uff0c\u9669\u4e9b\u88ab\u8499\u5143\u5b50\u4e00\u6293\u6293\u4e2d\uff0c\u5c0f\u864e\u5b50\u51db\u7136\u4e00\u60ca\uff0c\u5927\u53eb\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u7528\u864e\u62f3\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u54ce\u54df\uff0c\u864e\u62f3\u6211\u4e5f\u5fd8\u8bb0\u5566\uff01\u201d\u8499\u5143\u5b50\u53cd\u638c\u4e00\u626b\uff0c\u4e8e\u627f\u73e0\u8e09\u8e09\u8dc4\u8dc4\u5012\u9000\u4e09\u6b65\uff0c\u7adf\u4e0d\u77e5\u5979\u662f\u5426\u53d7 \u4f24\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u5ffd\u7136\u8dc3\u8d77\uff0c\u201c\u7830\u201d\u7684\u4e00\u62f3\u6253\u4e2d\u8499\u5143\u5b50\u7684\u80a9\u818a\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u4e0d\u5c31\u662f\u864e\u62f3\uff1f\u201d\u8499\u5143\u5b50 \u6599\u4e0d\u5230\u5c0f\u864e\u5b50\u4f1a\u7a81\u7136\u52a9\u9635\uff0c\u51b7\u4e0d\u53ca\u9632\uff0c\u7ed9\u4ed6\u6253\u5f97\u9887\u4e3a\u75bc\u75db\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff0c\u4f60\u9020\u53cd \u5566\uff1f\u201d\u4e8e\u627f\u73e0\u53eb\u9053\uff1a\u201c\u5bf9\uff0c\u518d\u7528\u9f99\u62f3\uff01\u201d\u8eab\u5f62\u4e00\u8d77\uff0c\u75be\u5730\u70b9\u4e86\u90a3\u5c0f\u65b0\u5a18\u7684\u54d1\u7a74\uff0c\u53eb\u9053\uff1a\u201c\u5c0f \u864e\u5b50\uff0c\u6211\u4e0e\u4f60\u5408\u529b\u5c06\u8fd9\u5927\u4e2a\u5b50\u6253\u5012\uff0c\u5979\u5c31\u4e0d\u4f1a\u505a\u4f60\u7684\u59bb\u5b50\u5566\u3002\u201d\u90a3\u5c0f\u65b0\u5a18\u672c\u60f3\u62ff\u8bdd\u95ee\u4f4f\u5c0f\u864e \u5b50\uff0c\u8981\u903c\u4ed6\u4f20\u6388\u529f\u592b\uff0c\u5c82\u77e5\u88ab\u4e8e\u627f\u73e0\u70b9\u4e86\u54d1\u7a74\uff0c\u786c\u8bf4\u5979\u8981\u7f20\u5c0f\u864e\u5b50\u505a\u4e08\u592b\uff0c\u5c0f\u864e\u5b50\u679c\u7136\u6050 \u60e7\uff0c\u540c\u65f6\u5bf9\u4e8e\u627f\u73e0\u53c8\u6709\u4e86\u51e0\u5206\u4eb2\u70ed\u4e4b\u60c5\uff0c\u8499\u5143\u5b50\u6050\u5413\u4e5f\u6ca1\u6709\u7528\uff0c\u53ea\u89c1\u4ed6\u53c8\u662f\u201c\u7830\u201d\u7684\u4e00\u62f3\u6253 \u51fa\uff0c\u53eb\u9053\uff1a\u201c\u77a7\uff0c\u8fd9\u4e0d\u662f\u9f99\u62f3\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u4e50\u5f97\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u662f\u9f99\u62f3\uff01\u201d\u9752\u51a5\u5251\u633d\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u4e00\u62db\u201c\u5012\u5377\u94f6 \u6cb3\u201d\uff0c\u4ece\u4e0a\u523a\u4e0b\uff0c\u4ee5\u8499\u5143\u5b50\u7684\u6b66\u529f\uff0c\u5c0f\u864e\u5b50\u81ea\u662f\u548c\u4ed6\u5dee\u5f97\u5f88\u8fdc\uff0c\u4e8e\u627f\u73e0\u8fd9\u4e00\u5251\u867d\u7136\u5389\u5bb3\uff0c\u4ed6 \u8981\u8eb2\u907f\uff0c\u4ea6\u975e\u96be\u4e8b\u3002\u4f46\u800c\u4eca\u662f\u62f3\u5251\u4e00\u9f50\u653b\u5230\uff0c\u4ed6\u8eb2\u5f97\u5f00\u62f3\uff0c\u5c31\u907f\u4e0d\u5f00\u5251\uff0c\u907f\u5f97\u5f00\u5251\uff0c\u5c31\u5b9a\u8981 \u4e2d\u62f3\uff0c\u6743\u8861\u5229\u5bb3\uff0c\u81ea\u662f\u4e0d\u613f\u88ab\u4e8e\u627f\u73e0\u7684\u5b9d\u5251\u7a7f\u80f8\u5241\u8179\uff0c\u800c\u5b81\u613f\u6328\u5c0f\u864e\u5b50\u7684\u62f3\u5934\u3002\u53ea\u542c\u5f97 \u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u8499\u5143\u5b50\u7684\u8170\u80ef\u53c8\u4e2d\u4e86\u4e00\u62f3\uff0c\u767b\u65f6\u8eab\u5f62\u6643\u4e86\u51e0\u6643\uff0c\u597d\u5bb9\u6613\u624d\u7528\u64d2\u62ff\u6cd5\u5316\u89e3\u5f00\u4e8e \u627f\u73e0\u7684\u5251\u62db\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u867d\u7136\u53ea\u662f\u5341\u56db\u5c81\u7684\u5927\u5b69\u5b50\uff0c\u4f46\u4ed6\u4ece\u5468\u5c81\u7684\u65f6\u5019\u8d77\uff0c\u521a\u521a\u5b66\u8d70\u8def\uff0c\u4ed6\u7684\u7236\u4eb2\u5f20\u98ce\u5e9c \u5c31\u7528\u836f\u6c34\u66ff\u4ed6\u6d78\u70bc\u7b4b\u9aa8\uff0c\u4e00\u61c2\u4eba\u4e8b\uff0c\u5c31\u7763\u7740\u4ed6\u78e8\u7ec3\u6b66\u529f\uff0c\u6545\u6b64\u4ed6\u4e60\u6280\u7684\u5e74\u9f84\uff0c\u5e76\u4e0d\u5728\u4e8e\u627f\u73e0 \u4e4b\u4e0b\u3002\u52a0\u4ee5\u5f20\u98ce\u5e9c\u8fd9\u4e00\u95e8\u7684\u6b66\u529f\uff0c\u4e43\u662f\u5148\u7ec3\u5916\u529f\uff0c\u540e\u7ec3\u5185\u529f\uff0c\u4ee5\u5916\u529f\u4e3a\u57fa\u7840\u7684\u5185\u5916\u53cc\u4fee\u4e4b \u5b66\uff0c\u6240\u4ee5\u82e5\u8bba\u6b66\u827a\uff0c\u90a3\u662f\u4e8e\u627f\u73e0\u6bd4\u5c0f\u864e\u5b50\u5f3a\uff0c\u82e5\u8bba\u6c14\u529b\uff0c\u5c0f\u864e\u5b50\u53cd\u800c\u6bd4\u4e8e\u627f\u73e0\u5927\u5f97\u591a\u3002\u8fd9\u4e00 \u62f3\u6253\u4e0b\uff0c\u8db3\u6709\u4e09\u56db\u767e\u65a4\u529b\u6c14\uff0c\u8499\u5143\u5b50\u867d\u7136\u4e0d\u81f3\u88ab\u4ed6\u51fb\u5012\uff0c\u4e5f\u51e0\u4e4e\u75db\u5f97\u54fc\u51fa\u58f0\u6765\uff01 \u3000\u3000\u4e8e\u627f\u73e0\u5927\u58f0\u559d\u5f69\uff0c\u624b\u5e95\u4e1d\u6beb\u4e0d\u7f13\uff0c\u5530\u3001\u5530\u3001\u5530\uff0c\u53c8\u662f\u8fde\u73af\u4e09\u5251\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u5c0f\u864e\u5b50\uff0c \u6211\u548c\u4f60\u6bd4\u4e00\u6bd4\uff0c\u770b\u662f\u4f60\u7684\u7f57\u6c49\u795e\u62f3\u5389\u5bb3\uff0c\u8fd8\u662f\u6211\u7684\u7384\u673a\u5251\u6cd5\u5389\u5bb3\u3002\u201d\u5c0f\u5b69\u5b50\u5341\u4e5d\u597d\u80dc\uff0c\u5c0f\u864e \u5b50\u4e00\u8fde\u51fb\u4e2d\u8499\u5143\u5b50\u4e24\u62f3\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5f53\u7136\u662f\u6211\u7684\u62f3\u5934\u5389\u5bb3\uff0c\u4f60\u770b\u8fd9\u5927\u4e2a\u5b50\u8fde\u95ea\u4e5f\u95ea\u4e0d\u5f00\uff01 \u770b\uff0c\u6211\u518d\u7528\u8c79\u62f3\u6253\u4ed6\u9f3b\u7cb1\uff01\u201d\u4e00\u4f38\u8170\uff0c\u5de6\u638c\u6a2a\u62e8\uff0c\u53f3\u62f3\u500f\u5730\u7a7f\u51fa\uff0c\u4e8e\u627f\u73e0\u7684\u5251\u52bf\u5982\u94f6\u8679\u6a2a \u63a0\uff0c\u5c01\u7740\u4e86\u8499\u5143\u5b50\u7684\u9000\u8def\uff0c\u8499\u5143\u5b50\u903c\u5f97\u5411\u524d\u4e00\u8dc3\uff0c\u53ea\u542c\u5f97\u53c8\u662f\u201c\u7830\u201d\u7684\u4e00\u62f3\uff0c\u679c\u7136\u7ed9\u5c0f\u864e\u5b50 \u6b63\u6b63\u51fb\u4e2d\u9f3b\u6881\uff0c\u5c31\u597d\u50cf\u8499\u5143\u5b50\u7279\u610f\u51d1\u4e0a\u53bb\u6328\u5c0f\u864e\u5b50\u63cd\u4e00\u6837\u3002\u5c0f\u864e\u5b50\u53ef\u4e50\u574f\u4e86\uff0c\u53c8\u53eb\u9053\uff1a \u201c\u77a7\uff0c\u4f60\u770b\u6211\u518d\u7528\u9e64\u62f3\uff01\u201d\u9f3b\u6881\u8106\u5f31\uff0c\u4e00\u62f3\u51fb\u4e2d\uff0c\u9c9c\u8840\u76f4\u6d41\uff0c\u8499\u5143\u5b50\u5fc3\u4e2d\u6697\u6697\u5600\u5495\uff0c\u60f3\u9053\uff1a \u201c\u8fd9\u4e00\u62f3\u53ef\u4e0d\u80fd\u7ed9\u4ed6\u51fb\u4e2d\u9762\u95e8\u4e86\u3002\u201d\u53cd\u624b\u4e00\u638c\u89e3\u5f00\u4e8e\u627f\u73e0\u7684\u653b\u52bf\uff0c\u63d0\u817f\u4e0a\u8e22\uff0c\u60f3\u8e22\u5f00\u5c0f\u864e\u5b50 \u7684\u62f3\u5934\uff0c\u54ea\u77e5\u4e94\u884c\u795e\u62f3\u5999\u7528\u65e0\u7a77\uff0c\u9e64\u62f3\u8bb2\u7a76\u7684\u662f\u8f7b\u7075\u8fc5\u6377\uff0c\u8499\u5143\u5b50\u7684\u5f39\u817f\u867d\u5feb\uff0c\u5c0f\u864e\u5b50\u7684\u62f3 \u5934\u66f4\u5feb\uff0c\u53ea\u542c\u5f97\u201c\u7830\u2019\u7684\u4e00\u62f3\uff0c\u6b63\u6b63\u51fb\u4e2d\u4e86\u8499\u5143\u5b50\u7684\u819d\u76d6\uff0c\u8499\u5143\u5b50\u767b\u65f6\u5f2f\u4e86\u534a\u622a\uff0c\u5c0f\u864e\u5b50\u53eb \u9053\uff1a\u201c\u5440\uff0c\u4f60\u8981\u5411\u6211\u8dea\u5730\u6c42\u9976\u4e48\uff1f\u6211\u53ef\u4e0d\u597d\u610f\u601d\u518d\u6253\u4f60\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4eec\u8fd9\u4e00\u573a\u6fc0\u6597\uff0c\u65e9\u60ca\u52a8\u4e86\u571f\u53f8\u5821\u5185\u7684\u4eba\uff0c\u6709\u4e9b\u95f9\u5b8c\u65b0\u623f\u8fd8\u7559\u5728\u5916\u9762\u8df3\u82b1\u7684\u4eba\u4e16\u8dd1\u8fdb \u6765\uff0c\u4e8e\u627f\u73e0\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u4f60\u4e0d\u5c06\u8fd9\u5927\u4e2a\u5b50\u6253\u5012\uff0c\u54b1\u4eec\u53ef\u8d70\u4e0d\u8131\u5566\uff01\u201d\u9752\u51a5\u5b9d\u5251\u4e00\u8d77\uff0c\u75be\u5241 \u8499\u5143\u5b50\u54bd\u5589\uff0c\u903c\u8499\u5143\u5b50\u9732\u51fa\u80cc\u5fc3\u8981\u5bb3\uff0c\u7adf\u65e0\u9632\u5fa1\uff0c\u5c0f\u864e\u5b50\u53eb\u9053\uff1a\u201c\u597d\uff0c\u6211\u518d\u6765\u4e00\u8bb0\u9f99\u62f3\uff01\u201d \u7528\u529b\u5288\u4e86\u4e00\u62f3\uff0c\u8499\u5143\u5b50\u4e00\u8fde\u6328\u4e86\u51e0\u62f3\uff0c\u6c14\u529b\u5927\u51cf\uff0c\u8fd9\u4e00\u62f3\u518d\u4e5f\u7981\u53d7\u4e0d\u8d77\uff0c\u4e00\u62f3\u6253\u4e0b\uff0c\u7acb\u65f6\u5927 \u53eb\u4e00\u58f0\uff0c\u4ec6\u5012\u5730\u4e0a\uff0c\u722c\u4e0d\u8d77\u6765\uff01 \u3000\u3000\u4e8e\u627f\u73e0\u7eb5\u5165\u4eba\u4e1b\uff0c\u4f38\u638c\u8212\u6307\uff0c\u6709\u5982\u5f69\u8776\u7a7f\u82b1\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5c06\u62e5\u8fdb\u6765\u7684\u4eba\uff0c\u90fd\u70b9\u4e86\u7a74\u9053\uff0c \u975e\u8fc7\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff0c\u4e0d\u80fd\u81ea\u89e3\uff0c\u4e8e\u662f\u4e00\u62d6\u7740\u5c0f\u864e\u5b50\u7684\u81c2\u818a\uff0c\u4e00\u6e9c\u70df\u5730\u8dd1\u51fa\u571f\u53f8\u5e9c\u95e8\u3002 \u3000\u3000\u6708\u4eae\u5df2\u8fc7\u4e2d\u5929\uff0c\u8df3\u82b1\u7684\u5c0f\u4f19\u5b50\u4eec\u4e5f\u5168\u90e8\u6563\u4e86\uff0c\u5e7d\u4f1a\u7684\u7537\u5973\u4e5f\u85cf\u5230\u4e86\u5bc6\u6797\u6df1\u5904\uff0c\u770b\u4e0d\u5230\u8e2a \u8ff9\u4e86\uff0c\u5c71\u4e2d\u4e00\u7247\u5bc2\u9759\u3002\u4e8e\u627f\u73e0\u4e0e\u5c0f\u864e\u5b50\u7ecf\u8fc7\u9002\u624d\u4e3e\u884c\u5a5a\u793c\u7684\u90a3\u7247\u8349\u576a\uff0c\u8349\u576a\u4e0a\u4f59\u706b\u672a\u706d\uff0c\u82b1 \u73af\u4e22\u5f97\u904d\u5730\u90fd\u662f\uff0c\u4e8e\u627f\u73e0\u4e00\u770b\uff0c\u5c0f\u864e\u5b50\u8eab\u4e0a\u7a7f\u7684\u8fd8\u662f\u65b0\u90ce\u670d\u9970\uff0c\u4e0d\u7981\u54d1\u7136\u5931\u7b11\uff0c\u53c8\u89c9\u4e00\u7247\u832b \u7136\uff0c\u4eca\u591c\u7684\u5947\u9047\uff0c\u771f\u4f3c\u4e00\u573a\u68a6\u5883\u3002\u5c0f\u864e\u5b50\u5374\u8fd8\u4f3c\u5728\u68a6\u5883\u4e2d\u672a\u9192\u8fc7\u6765\uff0c\u4e00\u5bf9\u773c\u73e0\u6ef4\u6e9c\u6e9c\u5730\u8f6c\u6765 \u8f6c\u53bb\uff0c\u5c3d\u77a7\u7740\u4e8e\u627f\u73e0\uff0c\u534a\u664c\u95ee\u9053\uff1a\u201c\u4f60\u8981\u5e26\u6211\u5230\u54ea\u513f\u53bb\uff1f\u201d\u4e00\u526f\u832b\u7136\u65e0\u6240\u9002\u4ece\u7684\u795e\u6c14\uff0c\u4e8e\u627f \u73e0\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u60f3\u53bb\u54ea\u513f\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff1a\u201c\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u662f\u600e\u4e48\u5230\u8fd9\u513f\u6765 \u7684\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u600e\u4f1a\u4e0d\u77e5\u9053\uff1f\u96be\u9053\u4f60\u662f\u4ece\u5929\u4e0a\u6389\u4e0b\u6765\u7684\u5417\uff1f\u4f60 \u60f3\u4e00\u60f3\uff1a\u4f60\u90a3\u4e2a\u5c0f\u65b0\u5a18\u662f\u51e0\u65f6\u51fa\u73b0\u5728\u4f60\u7684\u8eab\u8fb9\u7684\uff1f\u96be\u9053\u5979\u662f\u4ece\u5730\u4e0b\u94bb\u51fa\u6765\u7684\u5417\uff1f\u201d\u8bf4\u7740\u5657\u55e4 \u4e00\u7b11\uff0c\u5c0f\u864e\u5b50\u4f4e\u5934\u9ed8\u60f3\uff0c\u773c\u5149\u751a\u662f\u60f6\u60d1\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u771f\u5947\u602a\uff0c\u5979\u771f\u50cf\u662f\u4ece\u5730\u4e0b\u94bb\u51fa\u6765\u7684\u3002 \u6211\u597d\u4f3c\u662f\u4e00\u89c9\u9192\u6765\uff0c\u5c31\u89c1\u5979\u5728\u8eab\u8fb9\u670d\u4f8d\u6211\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5947\u602a\u4e4b\u6781\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u7684\u5e08\u7236\u5462\uff1f\u201d\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u9ed1\u767d\u6469\u8bc3\u76f8\u8c8c\u602a\u5f02\uff0c\u5c0f\u864e\u5b50 \u603b\u4e0d\u5e94\u5fd8\u8bb0\u5427\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4ec0\u4e48\u5e08\u7236\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u7684\u6b66\u529f\u662f\u5929\u751f\u7684\u5417\uff1f\u8c01 \u4eba\u6559\u4f60\u7684\u6b66\u529f\uff0c\u4f60\u8bb0\u4e0d\u8bb0\u5f97\uff1f\u201d\u5c0f\u864e\u5b50\u60f3\u5f97\u5934\u660f\u8111\u6da8\uff0c\u9053\uff1a\u201c\u597d\u50cf\u6709\u8bb8\u591a\u4eba\u6559\u8fc7\uff0c\u54c8\uff0c\u5bf9 \u5566\uff0c\u4f60\u4e5f\u6559\u8fc7\uff01\u6211\u7528\u6247\u5b50\u62e8\u9152\u7684\u529f\u592b\u5c31\u662f\u4f60\u6559\u7684\uff0c\u4f60\u662f\u6211\u7684\u5e08\u7236\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u557c\u7b11\u7686\u975e\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u4e0d\u77e5\u5403\u4e86\u4ec0\u4e48\u8ff7\u836f\uff0c\u8fde\u5e08\u7236\u90fd\u5fd8\u8bb0\u4e86\uff1f\u4f46\u770b\u8fd9\u60c5\u5f62\uff0c\u4ed6\u53c8 \u4f3c\u4e4e\u4e0d\u662f\u5b8c\u5168\u8ff7\u4e86\u7075\u6027\uff0c\u4f8b\u5982\u4ed6\u89c1\u4e86\u6211\u4e4b\u540e\uff0c\u5374\u4e5f\u8fd8\u80fd\u8bb0\u5f97\u8d77\u6765\u3002\u201d \u3000\u3000\u5c0f\u864e\u5b50\u95ee\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u5e08\u7236\uff0c\u54b1\u4eec\u73b0\u5728\u53bb\u54ea\u513f\uff1f\u201d\u4e8e\u627f\u73e0\u4e5f\u4e0d\u77e5\u9053\u8981\u53bb\u54ea\u513f\uff0c\u53ea\u662f\u7b11 \u9053\uff1a\u201c\u6211\u4e0d\u662f\u4f60\u7684\u5e08\u7236\uff0c\u6211\u662f\u4f60\u7684\u59d0\u59d0\uff0c\u4f60\u7684\u5e08\u7236\u662f\u4e00\u9ed1\u4e00\u767d\u7684\u4e24\u4e2a\u5370\u5ea6\u4eba\u3002\u201d\u5c0f\u864e\u5b50\u773c\u73e0 \u4e00\u8f6c\uff0c\u82e5\u6709\u6240\u601d\uff0c\u5ffd\u9053\uff1a\u201c\u6211\u6015\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6015\u4ec0\u4e48\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u6015\u4f60\uff01\u201d\u4e8e\u627f\u73e0 \u7b11\u9053\uff1a\u201c\u5e72\u561b\u6015\u6211\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u5979\u8bf4\u8fc7\u7684\uff0c\u9664\u4e86\u5979\u4e4b\u5916\uff0c\u5c31\u6ca1\u6709\u597d\u4eba\u3002\u4f60\u4eca\u665a\u5c06\u5979\u4e5f\u6253 \u4f24\u4e86\uff0c\u6211\u6015\u3002\u201d\u4e8e\u627f\u73e0\u77e5\u9053\u4ed6\u53e3\u4e2d\u6240\u8bf4\u7684\u201c\u5979\u201d\u662f\u6307\u90a3\u5c0f\u65b0\u5a18\uff0c\u7b11\u95ee\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u4fe1\u5979\u7684\u8bdd \u5417\uff1f\u201d\u5c0f\u864e\u5b50\u6ca1\u6709\u56de\u7b54\uff0c\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u90a3\u4e48\u5979\u8981\u505a\u4f60\u7684\u59bb\u5b50\uff0c\u4f60\u4e0d\u6015\u5417\uff1f\u201d\u5c0f\u864e\u5b50\u8eab\u8eaf\u4e00\u9707 \u9053\uff1a\u201c\u662f\u5440\uff0c\u770b\u6765\u6bcf\u4e00\u4e2a\u4eba\u90fd\u53ef\u6015\u3002\u201d\u770b\u4ed6\u7684\u795e\u6c14\uff0c\u7adf\u4f3c\u662f\u6709\u4e9b\u754f\u7f29\uff0c\u4e0d\u6562\u8ddf\u81ea\u5df1\u8d70\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u4e2d\u6697\u601d\uff1a\u201c\u600e\u6837\u624d\u80fd\u4ee4\u4ed6\u76f8\u4fe1\u81ea\u5df1\uff1f\u201d\u5ffd\u7136\u5728\u4ed6\u8170\u95f4\u4e00\u89e6\uff0c\u9053\uff1a\u201c\u4f60\u7238\u7238\u9057\u7ed9 \u4f60\u7684\u7f05\u5200\u8fd8\u5728\u4e48\uff1f\u201d\u5c0f\u864e\u5b50\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5728\uff01\u201d\u90a3\u7f05\u5200\u4ece\u767e\u70bc\u94a2\u70bc\u6210\u7ed5\u6307\u67d4\uff0c\u5c0f\u864e\u5b50\u7f20 \u5728\u8170\u95f4\u5f53\u4f5c\u8170\u5e26\uff0c\u8fde\u4ed6\u7684\u201c\u65b0\u5a18\u201d\u4e5f\u6ca1\u6709\u53d1\u73b0\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u89e3\u4e0b\u90a3\u53e3\u7f05\u5200\uff0c\u5728\u7a7a\u4e2d\u865a\u5288\u4e24\u5200\uff0c\u9053\uff1a\u201c\u8fd9\u4e0d\u5c31\u662f\uff01\u201d\u4e00\u65f6\u5174\u8d77\uff0c\u5c31\u5728\u8349\u576a\u4e0a\u4f7f \u51fa\u4e00\u8def\u4e94\u864e\u65ad\u95e8\u5200\u6cd5\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u6211\u8fd8\u6ca1\u5fd8\u8bb0\u5462\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u7684\u8bb0\u6027\u771f \u597d\uff0c\u518d\u60f3\u60f3\u770b\uff0c\u8fd9\u8def\u5200\u6cd5\u662f\u8c01\u6559\u7ed9\u4f60\u7684\uff1f\u201d\u5c0f\u864e\u5b50\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u5f53\u7136\u662f\u6211\u7684\u7239\u7239\uff0c\u6211\u7239\u7239\u662f \u4e00\u4e2a\u5927\u82f1\u96c4\uff0c\u5927\u597d\u6c49\uff01\u201d\u4e8e\u627f\u73e0\u5ffd\u9053\uff1a\u201c\u4f60\u7239\u7239\u7684\u90a3\u7247\u8840\u8863\u5462\uff1f\u201d\u5c0f\u864e\u5b50\u53c8\u5446\u4e86\u4e00\u5446\uff0c\u8bb7\u8bb7 \u8bf4\u9053\uff1a\u201c\u8840\u8863\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u662f\u5440\uff0c\u8840\u8863\uff01\u8fd9\u6837\u7684\u4e8b\uff0c\u4f60\u600e\u80fd\u5fd8\u5f97\u4e86\uff1f\u201d \u3000\u3000\u8981\u77e5\u4eba\u4e3a\u4e07\u7269\u4e4b\u7075\uff0c\u4e0d\u8bba\u4ec0\u4e48\u5389\u5bb3\u7684\u8ff7\u836f\uff0c\u53ef\u4ee5\u6559\u4ed6\u5fd8\u4e00\u5207\u4e8b\u60c5\uff0c\u4f46\u603b\u4e0d\u80fd\u6559\u4ed6\u5fd8\u4e86\u7236 \u5b50\u7684\u5929\u6027\u3002\u4f55\u51b5\u6b63\u50cf\u4e8e\u627f\u73e0\u5d07\u62dc\u5979\u5e08\u7236\u5f20\u4e39\u67ab\u4e00\u6837\uff0c\u5c0f\u864e\u5b50\u6700\u5d07\u62dc\u7684\u662f\u4ed6\u7684\u7236\u4eb2\uff0c\u8fd9\u4e00\u4e0b\u6e10 \u6e10\u5524\u8d77\u4e86\u4ed6\u6a21\u7cca\u7684\u8bb0\u5fc6\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u6211\u7238\u7238\u4e3a\u4ec0\u4e48\u7559\u7ed9\u6211\u8fd9\u7247\u8840\u8863\uff1f\u4ed6\u662f\u53d7\u4e86 \u4ec0\u4e48\u51a4\u5c48\u6b7b\u7684\uff1f\u201d\u4e8e\u627f\u73e0\u731b\u7136\u95ee\u9053\uff1a\u201c\u4f60\u7238\u7238\u662f\u4e0d\u662f\u597d\u4eba\uff1f\u201d\u5c0f\u864e\u5b50\u6012\u9053\uff1a\u201c\u90a3\u8fd8\u7528\u8bf4\uff01\u201d \u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u628a\u7f05\u5200\u548c\u8fd9\u7247\u8840\u8863\u662f\u8c01\u4ea4\u7ed9\u4f60\u7684\uff1f\u201d\u5c0f\u864e\u5b50\u7741\u5927\u773c\u775b\u4e86\uff0c\u7a81\u7136\u53eb\u9053\uff1a\u201c\u662f \u4f60\uff01\u5440\uff0c\u627f\u73e0\u59d0\u59d0\uff0c\u6211\u76f8\u4fe1\u4f60\u4e86\uff0c\u4f60\u662f\u597d\u4eba\uff01\u544a\u8bc9\u6211\uff0c\u6211\u7238\u7238\u4e3a\u4ec0\u4e48\u8981\u5c06\u8840\u8863\u7559\u7ed9\u6211\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u76f8\u4fe1\u6211\u90a3\u4fbf\u597d\u4e86\uff0c\u4f60\u7236\u4eb2\u7684\u4e8b\u60c5\u4ee5\u540e\u6211\u518d\u544a\u8bc9\u4f60\u3002\u4f60\u5feb\u60f3\u60f3\uff0c\u4f60\u662f \u600e\u4e48\u5230\u8fd9\u513f\u6765\u7684\uff1f\u4f60\u90a3\u4e24\u4f4d\u5e08\u7236\u53c8\u5230\u54ea\u513f\u53bb\u4e86\uff1f\u201d\u4e8e\u627f\u73e0\u6015\u4ed6\u518d\u53d7\u523a\u6fc0\uff0c\u6545\u6b64\u4e0d\u613f\u5728\u4ed6\u795e\u667a \u5c1a\u672a\u5b8c\u5168\u6e05\u9192\u7684\u65f6\u5019\uff0c\u91cd\u628a\u65e7\u4e8b\u63d0\u8d77\u3002 \u3000\u3000\u53ef\u662f\u5c0f\u864e\u5b50\u4ecd\u7136\u60f3\u4e0d\u8d77\u6765\u3002\u4e8e\u627f\u73e0\u6ca1\u6709\u529e\u6cd5\uff0c\u5ffd\u5730\u60f3\u9053\uff1a\u201c\u6211\u65e9\u542c\u8bf4\u82d7\u533a\u4e2d\u6709\u8bb8\u591a\u53e4\u602a \u7684\u836f\u8349\uff0c\u4e0d\u5982\u6211\u5e26\u4ed6\u53bb\u95ee\u95ee\u90a3\u4e2a\u8001\u5927\u5a18\u3002\u201d\u8fd9\u65f6\u5c0f\u864e\u5b50\u5df2\u662f\u5b8c\u5168\u4fe1\u670d\u4e86\u4e8e\u627f\u73e0\uff0c\u5bf9\u5979\u7684\u8bf4\u8bdd \u767e\u4f9d\u767e\u987a\uff0c\u670d\u670d\u5e16\u5e16\u5730\u8ddf\u5979\u5230\u4e86\u90a3\u82d7\u65cf\u8001\u5927\u5a18\u7684\u8305\u820d\u3002 \u3000\u3000\u90a3\u8001\u5927\u5a18\u521a\u521a\u719f\u7761\uff0c\u5ffd\u88ab\u4e8e\u627f\u73e0\u60ca\u9192\uff0c\u8d77\u8eab\u8bf4\u9053\uff1a\u201c\u95f9\u65b0\u623f\u95f9\u5b8c\u4e86\u5417\uff1f\u6211\u8fd8\u4ee5\u4e3a\u4f60\u8981\u5230 \u5929\u4eae\u624d\u56de\u5462\uff01\u201d\u71c3\u8d77\u677e\u679d\u4e00\u770b\uff0c\u4e0d\u89c9\u5927\u5403\u4e00\u60ca\uff0c\u597d\u534a\u664c\u624d\u8bf4\u51fa\u8bdd\u6765\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u4e0d\u662f\u65b0\u90ce \u5417\uff1f\u5440\uff0c\u597d\u5927\u80c6\u7684\u95fa\u5973\uff0c\u4f60\u600e\u4e48\u628a\u571f\u53f8\u7684\u65b0\u90ce\u4e5f\u62c9\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684\u5f1f\u5f1f\uff0c\u4ed6\u4e0d\u77e5\u662f\u5403\u4e86\u4ec0\u4e48\u8ff7\u836f\uff0c\u7cca\u91cc\u7cca\u6d82\u7684\u628a\u4ec0\u4e48\u90fd\u5fd8\u8bb0\u4e86\u3002\u4ed6 \u5e76\u4e0d\u60c5\u613f\u505a\u571f\u53f8\u5bb6\u7684\u65b0\u90ce\uff01\u201d\u8001\u5927\u5a18\u5f20\u53e3\u7ed3\u5df4\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u4e8b\uff1f\u201d\u5c06\u706b\u628a\u5728\u5c0f\u864e\u5b50\u8138\u4e0a \u4ed4\u7ec6\u7167\u4e86\u4e00\u7167\uff0c\u5ffd\u5730\u60ca\u60f6\u5931\u8272\uff0c\u5c06\u4e8e\u627f\u73e0\u62c9\u8fc7\u4e00\u8fb9\uff0c\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u4ed6\u4e0d\u4f46\u662f\u5403\u4e86\u8ff7\u836f\uff0c\u800c\u4e14 \u8fd8\u4e2d\u4e86\u86ca\uff0c\u4e00\u5e74\u4e4b\u540e\uff0c\u82e5\u4e0d\u8ba8\u5f97\u653e\u86ca\u4e4b\u4eba\u7684\u89e3\u836f\uff0c\u5fc5\u6b7b\u65e0\u7591\u3002\u6562\u60c5\u662f\u571f\u53f8\u7684\u5973\u513f\u6015\u4f60\u5f1f\u5f1f\u53d8 \u5fc3\uff0c\u6240\u4ee5\u653e\u4e86\u86ca\u3002\u8ff7\u836f\u5df2\u96be\u89e3\u6551\uff0c\u86ca\u836f\u66f4\u662f\u975e\u4eb2\u81ea\u653e\u86ca\u7684\u4eba\u89e3\u6551\u4e0d\u6210\u3002\u201d\u4e8e\u627f\u73e0\u5403\u60ca\u975e\u5c0f\uff0c \u4f46\u542c\u90a3\u8001\u5927\u5a18\u53e3\u6c14\uff0c\u597d\u50cf\u8ff7\u836f\u5e76\u975e\u7edd\u5bf9\u65e0\u89e3\uff0c\u5fc3\u4e2d\u53cd\u800c\u7a0d\u5bbd\uff0c\u4fbf\u6c42\u90a3\u8001\u5927\u5a18\u89e3\u8fd9\u79cd\u8ff7\u9b42\u836f\uff0c \u8001\u5927\u5a18\u6c89\u541f\u534a\u664c\uff0c\u5306\u5306\u51fa\u95e8\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u91c7\u4e86\u4e00\u675f\u8349\u836f\u56de\u6765\uff0c\u7acb\u523b\u714e\u8336\u7ed9\u5c0f\u864e\u5b50\u559d\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u559d\u4e86\u4e00\u53e3\uff0c\u76b1\u7709\u8bf4\u9053\uff1a\u201c\u597d\u82e6\u3002\u201d\u4e8e\u627f\u73e0\u6e29\u67d4\u5730\u770b\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u82f1\u96c4\u597d\u6c49\uff0c\u5929 \u4e0d\u6015\uff0c\u5730\u4e0d\u6015\uff0c\u8fd8\u80fd\u6015\u82e6\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u5bf9\uff01\u201d\u4e00\u4ef0\u8116\u5b50\uff0c\u628a\u82e6\u8336\u5495\u565c\u5495\u565c\u5730\u559d\u5f97\u5e72\u5e72\u51c0 \u51c0\uff0c\u5ffd\u9053\uff1a\u201c\u5440\uff0c\u6211\u60f3\u6253\u778c\u7761\u3002\u201d\u8001\u5927\u5a18\u8f7b\u8f7b\u62cd\u4e86\u4ed6\u4e24\u4e0b\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u5c31\u7761\u4e00\u4f1a\u513f\u3002\u201d \u3000\u3000\u5c0f\u864e\u5b50\u76d8\u819d\u4e00\u5750\uff0c\u95ed\u76ee\u5047\u7761\uff0c\u770b\u90a3\u59ff\u52bf\uff0c\u6b63\u662f\u6253\u5750\u8fd0\u529f\u7684\u59ff\u52bf\u3002\u4e8e\u627f\u73e0\u53d6\u51fa\u4e00\u952d\u94f6\u5b50\uff0c \u9053\uff1a\u201c\u8001\u5927\u5a18\u591a\u8c22\u4f60\u5566\uff01\u201d\u90a3\u82d7\u65cf\u7684\u8001\u5927\u5a18\u602b\u7136\u4e0d\u60a6\uff0c\u4e0d\u63a5\u94f6\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u89c1\u4f60\u5fc3\u597d\uff0c \u624d\u5e2e\u4f60\u7684\u5fd9\uff0c\u96be\u9053\u662f\u8d2a\u56fe\u4f60\u7684\u94f6\u5b50\u6765\u4e86\uff1f\u201d\u4e8e\u627f\u73e0\u8fde\u5fd9\u9053\u6b49\uff0c\u8001\u5927\u5a18\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u8fd9 \u89e3\u836f\u4e5f\u4e0d\u77e5\u6210\u4e0d\u6210\u5462\uff1f\u201d\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u51db\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u8001\u5927\u5a18\u9053\uff1a\u201c\u6211\u91c7\u7684\u8fd9\u79cd\u8349\u836f\u867d \u7136\u80fd\u89e3\u4e00\u822c\u8ff7\u836f\uff0c\u4f60\u5f1f\u5f1f\u5403\u7684\u5374\u4f3c\u662f\u6211\u4eec\u82d7\u533a\u4e2d\u4e5f\u5f88\u96be\u5bfb\u83b7\u7684\u2018\u5fd8\u5fe7\u8349\u2019\uff0c\u66f4\u52a0\u4e2d\u4e86\u86ca\uff0c\u53ea \u6015\u5403\u4e86\u6211\u7684\u89e3\u836f\u4e4b\u540e\uff0c\u4e16\u672a\u80fd\u5b8c\u5168\u6e05\u9192\u3002\u4e0d\u8fc7\u5728\u4ed6\u5403\u4e86\u8ff7\u836f\u4e4b\u540e\u7684\u79cd\u79cd\u4e8b\u60c5\uff0c\u5374\u4e00\u5b9a\u80fd\u6e05\u695a \u5730\u8bb0\u8d77\u6765\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5ffd\u89c1\u5c0f\u864e\u5b50\u4f38\u4e86\u4e00\u4e2a\u61d2\u8170\uff0c\u5f20\u773c\u53eb\u9053\uff1a\u201c\u597d\u8212\u670d\uff01\u6211\u8bb0\u8d77\u6765\u5566\uff0c\u6211\u7684\u4e24\u4f4d\u5e08 \u7236\u5728\u4e00\u4e2a\u53e4\u602a\u7684\u5c4b\u5b50\u91cc\u548c\u4eba\u6253\u67b6\u3002\u201d\u4e8e\u627f\u73e0\u5927\u559c\uff0c\u6025\u5fd9\u8c22\u8fc7\u90a3\u4f4d\u8001\u5927\u5a18\uff0c\u8001\u5927\u5a18\u9053\uff1a\u201c\u4e0d \u9519\uff0c\u4f60\u4eec\u5e94\u8be5\u8d76\u5feb\u9003\u8d70\u3002\u5929\u4e00\u4eae\uff0c\u90a3\u5c31\u4e0d\u5bb9\u6613\u9003\u5566\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u4e0e\u5c0f\u864e\u5b50\u8dd1\u5230\u5916\u9762\uff0c\u8d76\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u7684\u4e24\u4f4d\u5e08\u7236\u548c\u4ec0\u4e48\u4eba\u6253\u67b6\uff1f\u4f60\u548c\u4ed6\u4eec\u53c8\u662f\u600e \u6837\u5206\u624b\u7684\uff1f\u201d\u5c0f\u8651\u5b50\u9053\uff1a\u201c\u6211\u548c\u4e24\u4f4d\u5e08\u7236\u597d\u50cf\u662f\u4ece\u5f88\u8fdc\u5f88\u8fdc\u7684\u5730\u65b9\u6765\uff0c\u6709\u4e00\u5929\uff0c\u4e0d\u77e5\u600e\u7684\u5ffd \u7136\u95ef\u8fdb\u4e00\u4e2a\u53e4\u5821\uff0c\u53e4\u5821\u91cc\u6b63\u5728\u6446\u8bbe\u7b75\u5e2d\uff0c\u91cc\u9762\u7684\u4eba\u76f8\u8c8c\u90fd\u662f\u5947\u5947\u602a\u602a\u7684\uff0c\u6709\u4e00\u4e2a\u5934\u9876\u5149\u79c3 \u79c3\uff0c\u76ae\u80a4\u4e8e\u762a\uff0c\u6d3b\u50cf\u50f5\u5c38\u6a21\u6837\u7684\u602a\u4eba\uff0c\u66f4\u662f\u53ef\u6015\u3002\u4e0d\u8fc7\u4ed6\u4eec\u5bf9\u6211\u7684\u4e24\u4f4d\u5e08\u7236\u5374\u50cf\u5f88\u606d\u656c\uff0c\u8bf7 \u4ed6\u4eec\u559d\u9152\uff0c\u4e0d\u77e5\u600e\u7684\u5374\u5ffd\u7136\u6253\u8d77\u67b6\u6765\u5566\uff0c\u6211\u5e2e\u4e24\u4f4d\u5e08\u7236\u6253\u90a3\u4e2a\u602a\u4eba\uff0c\u88ab\u4ed6\u6293\u4e86\u4e00\u4e0b\uff0c\u767b\u65f6\u4e0d \u7701\u4eba\u4e8b\uff0c\u4e00\u89c9\u9192\u6765\uff0c\u5374\u7761\u5728\u571f\u53f8\u7684\u5bb6\u91cc\uff0c\u5979\u7ed9\u4e00\u7897\u70ed\u8336\u6211\u559d\uff0c\u559d\u4e86\u4fbf\u89c9\u7cca\u91cc\u6e56\u6d82\uff0c\u4e0d\u8fc7\u5979\u5bf9 \u6211\u5374\u771f\u597d\uff0c\u5929\u5929\u8863\u4e0d\u89e3\u5e26\u5730\u670d\u4f8d\u6211\uff0c\u6211\u75c5\u597d\u4e4b\u540e\uff0c\u5979\u53c8\u5929\u5929\u7f20\u6211\uff0c\u8bf4\u8981\u505a\u6211\u7684\u59bb\u5b50\u3002\u65e9\u77e5\u59bb \u5b50\u8fd9\u6837\u4e0d\u597d\u60f9\uff0c\u6211\u4e5f\u4e0d\u6562\u7b54\u5e94\u5566\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5657\u55e4\u4e00\u7b11\uff0c\u542c\u5c0f\u864e\u5b50\u8bf4\u8bdd\uff0c\u8bb8\u591a\u4e8b\u60c5\u4ed6\u5df2\u7136\u80fd\u591f\u8bb0\u5fc6\uff0c\u5c24\u5176\u662f\u5230\u4e86\u571f\u53f8\u5bb6\u4e2d\u4e4b \u540e\uff0c\u66f4\u8bb0\u5f97\u660e\u767d\u3002\u4e0d\u8fc7\u795e\u667a\u8fd8\u672a\u5b8c\u5168\u6e05\u695a\u3002\u4e8e\u627f\u73e0\u60f3\u9053\uff1a\u201c\u4e2d\u86ca\u4e4b\u4e8b\uff0c\u8981\u4e00\u5e74\u4e4b\u540e\u624d\u53d1\u4f5c\uff0c \u5c3d\u6709\u65f6\u95f4\u903c\u90a3\u5996\u5973\u62ff\u51fa\u89e3\u836f\uff0c\u5012\u662f\u9ed1\u767d\u6469\u8bc3\u7684\u4e0b\u843d\u5e94\u8be5\u5148\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\u3002\u201d\u4fbf\u95ee\u5c0f\u864e\u5b50\u9053\uff1a \u201c\u90a3\u53e4\u5821\u5750\u843d\u4f55\u65b9\uff0c\u4f60\u8fd8\u8bb0\u5f97\u5417\uff1f\u201d \u3000\u3000\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u6211\u8bd5\u53bb\u627e\u627e\u770b\uff0c\u597d\u50cf\u5c31\u5728\u5bf9\u9762\u7684\u90a3\u4e2a\u5c71\u4e2d\u3002\u201d\u8fd9\u56de\u662f\u4ed6\u5e26\u7740\u4e8e\u627f\u73e0\u8d70\uff0c\u5c71 \u8def\u8fc2\u56de\u66f2\u6298\uff0c\u4e8f\u4ed6\u5c45\u7136\u8bb0\u5f97\u65b9\u5411\uff0c\u8d70\u4e86\u597d\u4e00\u4f1a\uff0c\u7a7f\u8fdb\u4e00\u4e2a\u5e7d\u6697\u7684\u5ce1\u8c37\uff0c\u6708\u5149\u88ab\u5ca9\u77f3\u6321\u4f4f\uff0c\u53ea \u6709\u4e00\u70b9\u70b9\u6f0f\u4e0b\u6765\uff0c\u4ec5\u80fd\u8fa8\u51fa\u6a21\u7cca\u7684\u666f\u7269\uff0c\u5c71\u4e0a\u8001\u9e26\u591c\u557c\uff0c\u5e7d\u8c37\u4e2d\u65f6\u4e0d\u65f6\u522e\u6765\u4e00\u9635\u9635\u7684\u5bd2\u98ce\uff0c \u4ee4\u4eba\u6bdb\u9aa8\u609a\u7136\uff0c\u4e8e\u627f\u73e0\u4e5f\u4e0d\u89c9\u6709\u4e9b\u5fc3\u602f\u3002\u8d70\u4e86\u597d\u4e45\uff0c\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u5230\u5566\uff0c\u4f60\u77a7\uff0c\u5c31\u662f\u8fd9\u4e2a\u53e4 \u5821\uff01\u201d \u3000\u3000\u90a3\u53e4\u5821\u5f0f\u6837\u5947\u7279\uff0c\u56db\u5468\u5efa\u6709\u57ce\u5899\uff0c\u5de6\u53f3\u4e24\u4fa7\uff0c\u5374\u6709\u4e00\u4e2a\u5706\u5854\u5f62\u7684\u5efa\u7b51\uff0c\u57ce\u5899\u4e0b\u9762\u5f00\u6709\u4e00 \u9053\u7a84\u95e8\uff0c\u4ec5\u53ef\u5bb9\u4e00\u4eba\u901a\u8fc7\uff0c\u91cc\u9762\u900f\u51fa\u706f\u706b\uff0c\u95e8\u6237\u6253\u5f00\uff0c\u5185\u95f4\u8c08\u7b11\u4e4b\u58f0\uff0c\u9690\u9690\u53ef\u95fb\uff0c\u8fd9\u65f6\u5df2\u662f \u56db\u66f4\u65f6\u5206\uff0c\u5821\u5185\u5374\u8fd8\u6709\u706f\u706b\u4eba\u58f0\uff0c\u6ee1\u900f\u7740\u602a\u5f02\u4e4b\u8c61\uff0c\u4e8e\u627f\u73e0\u7565\u4e00\u8e0c\u8e87\uff0c\u4fbf\u633d\u7740\u5c0f\u864e\u5b50\u7684\u624b\u786c \u95ef\u8fdb\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u5927\u5385\u4e0a\u6446\u7740\u4e00\u4e2a\u957f\u684c\uff0c\u684c\u4e0a\u5806\u6ee1\u9152\u5e2d\uff0c\u5374\u53ea\u662f\u4e3b\u4f4d\u4e0a\u5750\u7740\u4e00\u4e2a\u4eba\uff0c\u5ba2\u4f4d\u7a7a\u7a7a\u5982\u4e5f\uff0c \u8fd9\u4eba\u5934\u9876\u5149\u79c3\u79c3\u7684\uff0c\u76ae\u80a4\u5e72\u762a\uff0c\u679c\u7136\u50cf\u4e2a\u50f5\u5c38\uff0c\u9152\u5e2d\u4e24\u8fb9\u7684\u957f\u5eca\u4e0a\uff0c\u5374\u5404\u6709\u4e00\u961f\u7537\u5973\u6392\u7acb\u4f3a \u5019\uff0c\u597d\u50cf\u5728\u7b49\u5019\u751a\u4e48\u5c0a\u8d35\u7684\u5ba2\u4eba\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u53eb\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4e2a\u4eba\uff01\u201d\u90a3\u50f5\u5c38\u6a21\u6837\u7684\u602a\u4eba\uff0c\u9aa4\u7136\u89c1\u5c0f\u864e\u5b50\u51fa\u73b0\uff0c\u201c\u54a6\u201d\u4e86\u4e00 \u58f0\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4e0d\u5728\u571f\u53f8\u5bb6\u91cc\u4f5c\u65b0\u90ce\uff0c\u6765\u8fd9\u91cc\u4f5c\u751a\u4e48\uff1f\u201d\u5c0f\u864e\u5b50\u5927\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u8981\u59bb\u5b50\uff0c\u6211 \u8981\u5e08\u7236\uff01\u201d\u90a3\u602a\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u5e08\u7236\uff1f\u201d\u5c0f\u864e\u5b50\u56b7\u9053\uff1a\u201c\u6211\u600e\u4e48\u6ca1\u6709\u4ec0\u4e48\u5e08\u7236\uff1f\u6211 \u4e0d\u6b62\u4e00\u4e2a\u5e08\u7236\uff0c\u9ed1\u5e08\u7236\u548c\u767d\u5e08\u7236\u90a3\u5929\u4e0d\u662f\u5728\u4f60\u8fd9\u91cc\u6253\u67b6\u5417\uff1f\u5feb\u8fd8\u6211\u7684\u5e08\u7236\uff01\u201d\u90a3\u602a\u4eba\u9762\u8272\u8d8a \u53d1\u96be\u770b\uff0c\u5411\u65c1\u8fb9\u4e00\u4e2a\u5f1f\u5b50\u8bf4\u9053\uff1a\u201c\u662f\u8c01\u628a\u89e3\u836f\u7ed9\u4ed6\u5403\u4e86\uff0c\u5feb\u7ed9\u6211\u5c06\u4ed6\u62ff\u4e0b\uff01\u201d\u90a3\u540d\u5f1f\u5b50\u521a\u8e0f \u51fa\u811a\u6b65\uff0c\u88ab\u4e8e\u627f\u73e0\u53d1\u51fa\u4e00\u6735\u91d1\u82b1\uff0c\u6253\u4e2d\u7a74\u9053\uff0c\u53cc\u81c2\u4f38\u51fa\uff0c\u4f5c\u52bf\u64d2\u62ff\uff0c\u5374\u52a8\u4e5f\u4e0d\u80fd\u4e00\u52a8\u3002\u90a3\u602a \u4eba\u78d4\u78d4\u602a\u7b11\u9053\uff1a\u201c\u539f\u6765\u6709\u5f20\u4e39\u67ab\u5728\u80cc\u540e\u7ed9\u4f60\u6491\u8170\uff0c\u602a\u4e0d\u5f97\u6562\u5230\u8fd9\u513f\u6765\u8ba8\u4eba\uff01\u201d\u4ef0\u5929\u5927\u7b11\u4e09 \u58f0\uff0c\u53eb\u9053\uff1a\u201c\u5f20\u5927\u4fa0\u76d6\u4e16\u82f1\u540d\uff0c\u600e\u7684\u5374\u8fd9\u6837\u85cf\u5934\u9732\u5c3e\uff1f\u6d3e\u4e24\u4e2a\u5c0f\u5b69\u5b50\u6765\u6270\u4e71\uff0c\u81ea\u5df1\u5374\u8eb2\u5728\u4e00 \u8fb9\uff0c\u4e0d\u6015\u4f20\u51fa\u53bb\u7ed9\u522b\u4eba\u7b11\u8bdd\u5417\uff1f\u76f8\u8bf7\u4e0d\u5982\u5076\u9047\uff0c\u8bf7\u8fdb\u6765\u540c\u559d\u4e09\u676f\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u89c1\u90a3\u602a\u4eba\u88c5\u8154\u4f5c\u52bf\uff0c\u5f2f\u8170\u5f20\u624b\uff0c\u4f5c\u8bf7\u5ba2\u8fdb\u6765\u7684\u795e\u6c14\u3002\u4e0d\u89c9\u5657\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u89c1 \u9b3c\u4e48\uff1f\u6211\u5e08\u7236\u73b0\u5728\u5927\u7406\u82cd\u5c71\uff0c\u4f60\u8981\u8bf7\u4ed6\u8d74\u5bb4\uff0c\u5feb\u5199\u8bf7\u5e16\u8ba9\u6211\u66ff\u4f60\u5e26\u53bb\uff01\u201d\u90a3\u602a\u4eba\u7edd\u5bf9\u6599\u4e0d\u5230 \u4e8e\u627f\u73e0\u6709\u8fd9\u6837\u7684\u80c6\u5b50\uff0c\u4ee5\u4e3a\u5b9a\u662f\u5f20\u4e39\u67ab\u548c\u5979\u540c\u6765\uff0c\u8fd8\u4ee5\u4e3a\u5c0f\u864e\u5b50\u4e5f\u662f\u5f20\u4e39\u67ab\u89e3\u6551\u7684\uff0c\u5fc3\u6709\u5fcc \u60ee\uff0c\u6545\u6b64\u4e0d\u62df\u5bf9\u4ed6\u4eec\u52a8\u624b\uff0c\u800c\u4eca\u4e00\u542c\uff0c\u5f20\u4e39\u67ab\u8fd8\u5728\u5927\u7406\u82cd\u5c71\uff0c\u9762\u8272\u4e00\u6c89\uff0c\u5bf9\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u4f60 \u542c\u4e0d\u542c\u6211\u7684\u8bdd\uff1f\u201d\u4e24\u9053\u773c\u5149\u5728\u5c0f\u864e\u5b50\u7684\u9762\u4e0a\u4e00\u626b\uff0c\u53c8\u5411\u4e8e\u627f\u73e0\u72e0\u72e0\u5730\u77aa\u4e86\u4e00\u773c\uff0c\u5c0f\u864e\u5b50\u548c\u4e8e \u627f\u73e0\u90fd\u4e0d\u81ea\u7981\u5730\u673a\u4f36\u4f36\u5730\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff0c\u4e8e\u627f\u73e0\u4f46\u89c9\u76ee\u5149\u4e2d\u4f3c\u6709\u4e00\u80a1\u9b54\u529b\uff0c\u4ee4\u4eba\u5fc3\u795e\u604d\u60da\uff0c \u4e0d\u5bd2\u800c\u6817\uff0c\u4e8e\u627f\u73e0\u6025\u5fd9\u9547\u6444\u5fc3\u795e\uff0c\u6084\u6084\u5bf9\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u5feb\u8fd0\u7384\u529f\uff0c\u4e0d\u8981\u770b\u4ed6\uff01\u201d \u3000\u3000\u5c0f\u864e\u5b50\u5446\u4e86\u4e00\u5446\uff0c\u4f3c\u662f\u53d7\u4e86\u90a3\u602a\u4eba\u7684\u50ac\u7720\uff0c\u5374\u53c8\u5ffd\u7136\u60ca\u9192\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8c01\u542c\u4f60\u7684\u8bdd\uff1f \u6211\u53ea\u542c\u5e08\u7236\u7684\u8bdd\u3002\u6211\u7684\u4e24\u4f4d\u5e08\u7236\u5462\uff1f\u201d\u90a3\u602a\u4eba\u9053\uff1a\u201c\u4f60\u7684\u4e24\u4f4d\u5e08\u7236\u4e0d\u662f\u6211\u7684\u5bf9\u624b\uff0c\u7ed9\u6211\u6253\u8dd1 \u5566\uff01\u201d\u5c0f\u864e\u5b50\u53eb\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u6211\u4e24\u4f4d\u5e08\u7236\u76d6\u4e16\u82f1\u96c4\uff0c\u4f60\u591f\u4ed6\u6253\uff1f\u201d\u90a3\u602a\u4eba\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u4fe1 \u6211\u5c31\u5e26\u4f60\u770b\u4ed6\u4eec\u53bb\uff01\u201d\u77aa\u7740\u773c\u775b\uff0c\u4e00\u6b65\u4e00\u6b65\u5411\u5c0f\u864e\u5b50\u884c\u6765\uff0c\u9762\u4e0a\u5374\u9732\u51fa\u6781\u5176\u8be1\u5f02\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u6697\u53eb\u4e0d\u5999\uff0c\u4e00\u626c\u624b\u6253\u51fa\u4e09\u6735\u91d1\u82b1\uff0c\u90a3\u602a\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u7c73\u7c92\u4e4b\u73e0\uff0c\u4e5f\u653e\u5149\u534e\uff01\u201d\u8212 \u638c\u4e00\u6325\uff0c\u4e94\u6307\u75be\u5f39\uff0c\u53ea\u542c\u5f97\u94ee\u94ee\u6570\u58f0\uff0c\u4e09\u6735\u91d1\u82b1\u90fd\u7ed9\u4ed6\u5f39\u5f97\u5411\u4fa7\u65b9\u659c\u98de\uff0c\u5d4c\u5165\u6bbf\u4e0a\u6881\u67f1\u4e4b \u4e2d\uff0c\u5217\u6210\u4e86\u4e00\u4e2a\u54c1\u5b57\u5f62\uff0c\u6309\u7167\u8fd9\u4e2a\u65b9\u4f4d\uff0c\u82e5\u7136\u662f\u6253\u5728\u4eba\u8eab\u4e4b\u4e0a\uff0c\u90a3\u5c31\u662f\u5de6\u4e73\u7a81\u7a74\u3001\u53f3\u4e73\u7a81\u7a74 \u548c\u8110\u95e8\u7a74\u4e86\u3002\u4e09\u6735\u91d1\u82b1\u5206\u6253\u4e09\u5904\u7a74\u9053\uff0c\u7adf\u7136\u88ab\u4ed6\u6325\u624b\u4e4b\u95f4\uff0c\u5168\u6570\u5f39\u5f00\uff0c\u800c\u4e14\u65b9\u5411\u4e0d\u53d8\uff0c\u8fd9\u624b \u529f\u592b\uff0c\u786e\u8db3\u4ee5\u60ca\u4e16\u9a87\u4fd7\u3002\u4e8e\u627f\u73e0\u4e5f\u4e0d\u7981\u53d8\u4e86\u989c\u8272\u3002\u8981\u77e5\u4e8e\u627f\u73e0\u7684\u91d1\u82b1\uff0c\u56db\u8fb9\u950b\u5229\uff0c\u4ece\u65e0\u4eba\u6562 \u7528\u8089\u638c\u6765\u63a5\uff0c\u8fd9\u602a\u4eba\u5374\u53ea\u7528\u624b\u6307\u8f7b\u5f39\uff0c\u4fbf\u80fd\u5c06\u91d1\u82b1\u5f39\u98de\uff0c\u542c\u90a3\u94ee\u94ee\u4e4b\u58f0\uff0c\u7adf\u4f3c\u78b0\u5230\u91d1\u5c5e\u4e00 \u822c\uff0c\u597d\u50cf\u4ed6\u7684\u624b\u6307\u7adf\u4e0d\u662f\u8840\u8089\u505a\u7684\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u53eb\u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff0c\u5feb\u7528\u9f99\u62f3\uff01\u201d\u5979\u7684\u9752\u51a5\u5251\u4e5f\u7acb\u5373\u51fa\u9798\uff0c\u5c0f\u864e\u5b50\u5728\u524d\uff0c\u201c\u84ec\u201d\u7684 \u4e00\u62f3\uff0c\u5148\u51fb\u4e2d\u4e86\u90a3\u4e2a\u602a\u4eba\u3002\u5176\u58f0\u6709\u5982\u8d25\u9769\uff0c\u5c0f\u864e\u5b50\u5e74\u7eaa\u867d\u5c0f\uff0c\u8fd9\u4e00\u62f3\u5c11\u8bf4\u4e5f\u6709\u4e09\u56db\u767e\u65a4\u6c14 \u529b\uff0c\u90a3\u602a\u4eba\u7adf\u662f\u8fde\u8eab\u8eaf\u4e5f\u4e0d\u6643\u52a8\u4e00\u4e0b\uff0c\u6325\u8896\u4e00\u62c2\uff0c\u53c8\u5c06\u4e8e\u627f\u73e0\u7684\u5b9d\u5251\u8361\u5f00\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5b9d \u5251\u867d\u5229\uff0c\u80fd\u5948\u6211\u4f55\uff1f\u201d\u4fa7\u76ee\u659c\u89c6\uff0c\u5374\u76ef\u7740\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u6562\u4e0d\u542c\u6211\u7684\u8bdd\uff01\u201d\u5c0f\u864e\u5b50\u53c8\u662f \u673a\u4f36\u4f36\u5730\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff0c\u4e8e\u627f\u73e0\u6325\u5251\u6025\u4e0a\uff0c\u5530\uff0c\u5530\uff0c\u5530\uff0c\u60ca\u96f7\u8fc5\u7535\u822c\u5730\u75be\u4f7f\u8fde\u73af\u4e09\u5251\uff0c\u90a3\u602a \u4eba\u50b2\u6162\u4e4b\u6781\uff0c\u8fc7\u4e8e\u5927\u610f\uff0c\u4ecd\u7136\u65bd\u5c55\u98de\u8896\u7684\u529f\u592b\uff0c\u60f3\u7528\u5185\u529b\u8361\u5f00\u4e8e\u627f\u73e0\u7684\u5b9d\u5251\uff0c\u54ea\u77e5\u767e\u53d8\u7384\u673a \u5251\u6cd5\u7aef\u7684\u662f\u53d8\u5316\u83ab\u6d4b\uff0c\u8981\u4e0d\u7136\u600e\u80fd\u79f0\u5f97\u4e0a\u5929\u4e0b\u7b2c\u4e00\u7cbe\u5999\u7684\u5251\u6cd5\uff0c\u4e8e\u627f\u73e0\u4e24\u5251\u865a\u524a\uff0c\u6700\u540e\u4e00 \u5251\uff0c\u7a81\u7136\u8f6c\u6362\u65b9\u4f4d\uff0c\u53ea\u542c\u4e00\u58f0\u88c2\u5e1b\uff0c\u90a3\u602a\u4eba\u7684\u957f\u8896\u5df2\u88ab\u524a\u53bb\u4e86\u534a\u622a\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u6697\u53eb\u53ef\u60dc\uff0c\u8fd9\u4e00\u5251\u5979\u539f\u662f\u60f3\u524a\u65ad\u90a3\u602a\u4eba\u7684\u624b\u8155\u7684\u3002\u867d\u7136\u5982\u6b64\uff0c\u90a3\u602a\u4eba\u7684\u50b2\u6c14\u4ea6\u5df2 \u6d88\u4e86\u51e0\u5206\uff0c\u4e00\u8f6c\u8eab\uff0c\u907f\u5f00\u4e86\u4e8e\u627f\u73e0\u7684\u4e00\u5251\uff0c\u5c0f\u864e\u5b50\u53c8\u662f\u84ec\u7684\u4e00\u62f3\uff0c\u6253\u4e2d\u4e86\u4ed6\u7684\u5c0f\u8179\uff0c\u5ffd\u89c9\u4ed6 \u7684\u5c0f\u8179\u5374\u6709\u4e00\u80a1\u5438\u529b\uff0c\u62f3\u5934\u62d4\u4e0d\u51fa\u6765\uff0c\u5c0f\u864e\u5b50\u6da8\u7ea2\u4e86\u9762\uff0c\u521a\u53eb\u5f97\u4e00\u58f0\uff1a\u201c\u59d0\u59d0\u201d\uff0c\u9661\u5730\u4f3c\u817e \u4e91\u9a7e\u96fe\u822c\u5730\u7ed9\u90a3\u602a\u4eba\u629b\u8d77\uff0c\u4e8e\u627f\u73e0\u5927\u60ca\uff0c\u4e00\u62db\u201c\u5929\u6cb3\u5012\u6302\u201d\uff0c\u53cd\u624b\u524a\u4ed6\u81c2\u818a\uff0c\u90a3\u602a\u4eba\u53f3\u8fb9\u957f \u8896\u4e00\u5377\uff0c\u628a\u5b9d\u5251\u4e00\u88f9\uff0c\u4e8e\u627f\u73e0\u5251\u950b\u4e00\u98a4\uff0c\u53c8\u628a\u4ed6\u7684\u957f\u8896\u5272\u65ad\uff0c\u5fc3\u5ff5\u65b9\u52a8\uff0c\u60f3\u8d81\u52bf\u523a\u4ed6\u80f8\u77a0\uff0c \u5374\u5ffd\u5730\u95fb\u5230\u4e00\u80a1\u5f02\u9999\uff0c\u4ece\u4ed6\u7684\u8896\u7ba1\u4e2d\u98de\u51fa\u6765\uff0c\u4e8e\u627f\u73e0\u6025\u5fd9\u95ed\u6c14\u62b5\u5fa1\uff0c\u5251\u5c16\u5c1a\u672a\u523a\u51fa\uff0c\u5374\u88ab\u90a3 \u602a\u4eba\u70b9\u4e2d\u4e86\u7a74\u9053\u3002\u90a3\u602a\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u5012\u60f3\u5bb9\u4f60\u628a\u5251\u6cd5\u4f7f\u5168\uff0c\u770b\u770b\u7384\u673a\u5251\u6cd5\u6709\u4f55\u7b49\u7cbe\u5999\uff0c \u53ea\u53ef\u60dc\u6211\u8981\u6b3e\u5f85\u8d35\u5bbe\uff0c\u96be\u4ee5\u5949\u966a\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u4e0e\u5c0f\u864e\u5b50\u90fd\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u88ab\u90a3\u602a\u4eba\u6742\u7f6e\u5728\u5eca\u4e0b\u7684\u5f1f\u5b50\u884c\u5217\u4e2d\uff0c\u4e8e\u627f\u73e0\u4e0d\u80fd\u52a8\u5f39\uff0c \u5fc3\u5934\u5374\u8fd8\u6e05\u9192\uff0c\u597d\u5947\u4e4b\u5ff5\uff0c\u6cb9\u7136\u800c\u751f\uff0c\u4e0d\u77e5\u8fd9\u9b54\u5934\u7684\u5bbe\u5ba2\uff0c\u53c8\u662f\u4f55\u7b49\u6837\u7684\u602a\u4eba\uff1f\u53ea\u89c1\u90a3\u602a\u4eba \u6362\u8fc7\u8863\u88f3\uff0c\u547d\u4ee4\u594f\u4e50\u3002\u4e50\u58f0\u4e00\u505c\uff0c\u4e24\u4e2a\u4eba\u8d70\u4e86\u8fdb\u6765\uff0c\u4e8e\u627f\u73e0\u5ffd\u89c9\u773c\u775b\u4e00\u4eae\uff0c\u4f46\u89c1\u6765\u7684\u4e43\u662f\u4e00 \u7537\u4e00\u5973\uff0c\u90a3\u5973\u7684\u7adf\u7136\u662f\u91d1\u53d1\u7684\u897f\u57df\u7f8e\u4eba\uff0c\u53ea\u89c1\u5979\u957f\u88d9\u66f3\u5730\uff0c\u4eea\u6001\u4e07\u5343\uff0c\u81ea\u6709\u4e00\u79cd\u96cd\u5bb9\u534e\u8d35\u7684 \u6c14\u5ea6\u3002\u5982\u6b64\u8d35\u5987\u51fa\u73b0\u5728\u5982\u6b64\u602a\u5f02\u7684\u5730\u65b9\uff0c\u771f\u662f\u4ee4\u4eba\u96be\u4ee5\u60f3\u8c61\u3002 \u3000\u3000\u90a3\u7537\u5b50\u8eab\u957f\u8c8c\u79c0\uff0c\u6709\u5982\u7389\u6811\u4e34\u98ce\uff0c\u4e00\u773c\u77a5\u53bb\uff0c\u5374\u4e0d\u77e5\u4ed6\u662f\u80e1\u4eba\u8fd8\u662f\u6c49\u4eba\uff1f\u4ed6\u7a7f\u7684\u4e43\u662f\u80e1 \u670d\uff0c\u9ad8\u9ad8\u7684\u9f3b\u5b50\uff0c\u53cc\u773c\u71a0\u71a0\u6709\u5149\uff0c\u4f46\u5374\u662f\u9ec4\u8272\u7684\u76ae\u80a4\uff0c\u9ed1\u8272\u7684\u5934\u53d1\uff0c\u9762\u8c8c\u4e5f\u4f3c\u6c49\u4eba\uff0c\u8fd9\u65f6\u7537 \u5973\u7275\u624b\u540c\u51fa\uff0c\u6001\u5ea6\u751a\u662f\u4eb2\u70ed\uff0c\u5c0f\u864e\u5b50\u770b\u5f97\u51fa\u795e\uff0c\u4e8e\u627f\u73e0\u5374\u5728\u5fc3\u60f3\uff1a\u4ed6\u4eec\u662f\u4e0d\u662f\u4e00\u5bf9\u592b\u5987\u5462\uff1f \u3000\u3000\u5ffd\u542c\u5f97\u90a3\u7537\u5b50\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u738b\u7237\u4f60\u7684\u62db\u5f85\uff0c\u6211\u4eec\u5728\u8d35\u5821\u5df2\u6253\u6270\u591a\u65e5\uff0c\u5b9e\u5728\u4e0d\u4fbf\u4e45\u7559\uff0c\u4eca \u65e5\u544a\u8f9e\u4e86\u3002\u201d\u8bf4\u7684\u4e43\u662f\u6c49\u8bed\uff0c\u4e0d\u8fc7\u6709\u4e9b\u751f\u786c\uff0c\u597d\u6a61\u662f\u8fdc\u79bb\u4e86\u5bb6\u4e61\u7684\u5f52\u5ba2\uff0c\u4e61\u97f3\u672a\u6539\uff0c\u4f46\u5df2\u4e0d \u80fd\u8bf4\u5f97\u6d41\u7545\u81ea\u5982\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u6697\u6697\u5600\u5495\uff1a\u201c\u8fd9\u50f5\u5c38\u822c\u7684\u602a\u4eba\u662f\u54ea\u95e8\u5b50\u7684\u738b\u7237\uff1f\u201d\u5fc3\u4e2d\u7591\u4e91\u5927\u8d77\u3002\u987b\u77e5\u4e8e\u627f\u73e0\u4e43 \u662f\u9601\u8001\u4e8e\u8c26\u7684\u5973\u513f\uff0c\u5bf9\u660e\u671d\u7684\u4f53\u5236\u5927\u81f4\u77e5\u9053\uff0c\u660e\u671d\u81ea\u592a\u7956\u6731\u5143\u748b\u5f00\u56fd\u4e4b\u540e\uff0c\u867d\u7136\u5206\u5c01\u5404\u738b\u5b50 \u5230\u5404\u5730\u4e3a\u738b\uff0c\u4f46\u5e76\u672a\u542c\u8bf4\u6709\u7687\u5b50\u5c01\u5230\u8d35\u5dde\u6765\u7684\uff0c\u800c\u4e14\u5373\u7b97\u662f\u738b\u7237\uff0c\u4ed6\u7684\u201c\u738b\u5e9c\u201d\u4e5f\u4e0d\u4f1a\u8bbe\u5728 \u8fd9\u6837\u7684\u8352\u5c71\u5e7d\u8c37\u4e4b\u4e2d\uff0c\u90a3\u5206\u660e\u662f\u5192\u5145\u7684\u4e86\u3002 \u3000\u3000\u90a3\u50f5\u5c38\u822c\u7684\u602a\u4eba\u5bf9\u4ed6\u4eec\u6267\u793c\u751a\u606d\uff0c\u9762\u4e0a\u5806\u6ee1\u7b11\u5bb9\uff0c\u8eac\u8eab\u8bf4\u9053\uff1a\u201c\u5c0f\u738b\u5f97\u8499\u516c\u4e3b\u548c\u9a78\u9a6c\u5149 \u4e34\uff0c\u771f\u662f\u4e09\u751f\u6709\u5e78\u3002\u9a78\u9a6c\u65e2\u6267\u610f\u8981\u8d70\uff0c\u5c0f\u738b\u4e5f\u4e0d\u4fbf\u4e45\u7559\u3002\u4f46\u6b64\u53bb\u4e2d\u56fd\u4eac\u90fd\uff0c\u5c71\u957f\u6c34\u8fdc\uff0c\u8def\u9014 \u4e0d\u9756\uff0c\u5fc5\u987b\u6709\u80fd\u4eba\u62a4\u9001\uff0c\u624d\u5f97\u5b89\u5fc3\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u66f4\u662f\u60ca\u5947\uff0c\u5fc3\u9053\uff1a\u201c\u679c\u7136\u662f\u4e00\u5bf9\u592b\u5987\uff0c\u4e0d\u77e5\u662f\u54ea\u4e00\u56fd\u7684\u516c\u4e3b\u3002\u65e2\u7136\u8d35\u4e3a\u516c\u4e3b\uff0c\u4f55 \u4ee5\u6ca1\u6709\u968f\u4ece\uff0c\u4e2d\u56fd\u867d\u53f7\u79f0\u4e0a\u56fd\uff0c\u4f46\u56fd\u52bf\u8870\u5fae\uff0c\u5f88\u4e45\u4ee5\u6765\uff0c\u5df2\u6ca1\u6709\u8fdc\u65b9\u56fd\u5bb6\u7684\u4f7f\u8005\u6765\u671d\u8d21\uff0c\u66f4 \u4f55\u51b5\u516c\u4e3b\u4eb2\u4e34\uff0c\u800c\u4e14\u5373\u7b97\u662f\u4ed6\u4eec\u4ee3\u8868\u672c\u56fd\uff0c\u8981\u5230\u5317\u4eac\u671d\u8d21\uff0c\u4e5f\u4e0d\u987b\u53d6\u9053\u8d35\u5dde\uff0c\u8981\u4e0d\u987b\u7a7f\u8fc7\u8fd9 \u6837\u7684\u7a77\u5c71\u5cfb\u5cad\uff0c\u4e8b\u60c5\u602a\u8bde\u4e0d\u7edd\uff0c\u7591\u56e2\u767e\u51fa\uff0c\u83ab\u975e\u53c8\u662f\u5047\u5192\u7684\u4e0d\u6210\uff1f\u4f46\u770b\u8fd9\u4e24\u4eba\u795e\u6c14\uff0c\u5747\u662f\u96cd \u5bb9\u534e\u8d35\uff0c\u81ea\u6709\u4e00\u79cd\u5c0a\u4e25\uff0c\u5374\u53c8\u4e0d\u4f3c\u5047\u5192\u3002\u201d\u4e8e\u627f\u73e0\u767e\u601d\u83ab\u89e3\uff0c\u6697\u6697\u7eb3\u7f55\u3002 \u3000\u3000\u90a3\u88ab\u79f0\u505a\u9a78\u9a6c\u7684\u7537\u58eb\u7a0d\u7a0d\u73b0\u51fa\u8e0c\u8e87\u7684\u795e\u8272\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u672c\u6765\u6709\u4e24\u4f4d\u5f02\u4eba\u76f8\u9001\uff0c\u4e2d \u9014\u5931\u6563\uff0c\u4e45\u5019\u4e0d\u6765\uff0c\u6211\u4eec\u53ea\u597d\u5148\u8d70\u4e86\u3002\u201d\u90a3\u602a\u4eba\u9053\uff1a\u201c\u8fd9\u6837\u4e0d\u6210\uff0c\u4e0d\u5982\u6211\u6d3e\u4eba\u62a4\u9001\u516c\u4e3b\u548c\u9a78 \u9a6c\u5427\uff0c\u8bf7\u9a78\u9a6c\u5c06\u56fd\u4e66\u548c\u793c\u7269\u4ea4\u6258\u7ed9\u4ed6\uff0c\u6b64\u4eba\u662f\u6709\u540d\u7684\u52c7\u58eb\uff0c\u6b66\u529f\u9ad8\u5f3a\uff0c\u5fe0\u5b9e\u65e0\u6bd4\uff0c\u9a78\u9a6c\u53ef\u4ee5 \u653e\u5fc3\u3002\u201d \u3000\u3000\u90a3\u9a78\u9a6c\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u5fc5\u5566\uff0c\u793c\u7269\u6211\u5df2\u4ed8\u6258\u7ed9\u90a3\u4e24\u4f4d\u5f02\u4eba\uff0c\u6211\u4eec\u7a7a\u8eab\u4e0a\u8def\uff0c\u6ca1\u6709\u4ec0\u4e48\u987e \u8651\uff0c\u8def\u4e0a\u7eb5\u6709\u4e9b\u6bdb\u532a\uff0c\u6211\u5927\u7ea6\u4e5f\u8fd8\u5bf9\u4ed8\u5f97\u4e86\u3002\u201d\u90a3\u602a\u4eba\u53c8\u8d54\u7b11\u8bf4\u9053\uff1a\u201c\u9a78\u9a6c\u7237\u6587\u6b66\u517c\u8d44\uff0c\u5c0f \u738b\u4f69\u670d\u5f97\u5f88\u3002\u4f46\u516c\u4e3b\u5230\u5e95\u662f\u91d1\u679d\u7389\u53f6\uff0c\u5373\u7b97\u662f\u4ec5\u53d7\u60ca\u6050\uff0c\u90a3\u4e5f\u5f88\u4e0d\u503c\u554a\u3002\u5662\uff0c\u9a78\u9a6c\u4f60\u8bf4\u7684\u90a3 \u4e24\u4f4d\u5f02\u4eba\u662f\u4e0d\u662f\u4e00\u9ed1\u4e00\u767d\u7684\u5370\u5ea6\u73e0\u5b9d\u5546\u4eba\uff0c\u540d\u53eb\u9ed1\u767d\u6469\u8bc3\u7684\u5b6a\u751f\u5144\u5f1f\uff1f\u201d\u90a3\u9a78\u9a6c\u5947\u9053\uff1a\u201c\u8d35 \u738b\u600e\u4e48\u77e5\u9053\uff1f\u201d\u90a3\u602a\u4eba\u9053\uff1a\u201c\u4ed6\u4eec\u6d3e\u4e00\u4e2a\u5c0f\u5f92\u5f1f\u5230\u8fd9\u513f\u8bf4\u7684\uff0c\u6211\u8fd8\u4e0d\u6562\u76f8\u4fe1\uff0c\u539f\u6765\u771f\u662f\u4ed6 \u4eec\u3002\u201d\u90a3\u9a78\u9a6c\u559c\u9053\uff1a\u201c\u9ed1\u767d\u6469\u8bc3\u7684\u5c0f\u5f92\u5f1f\u5728\u54ea\u513f\uff1f\u201d\u90a3\u602a\u4eba\u9053\uff1a\u201c\u5728\u8fd9\u513f\uff01\u201d\u7acb\u5373\u8d70\u5230\u4f17\u4eba \u4e2d\u5c06\u5c0f\u864e\u5b50\u62c9\u51fa\uff0c\u4e8e\u627f\u73e0\u51b7\u773c\u65c1\u89c2\uff0c\u77e5\u4ed6\u5df2\u7528\u6781\u4fd0\u843d\u7684\u624b\u6cd5\u89e3\u4e86\u5c0f\u864e\u5b50\u7684\u7a74\u9053\uff0c\u4f46\u5374\u8fd8\u662f\u6697 \u6263\u7740\u5c0f\u864e\u5b50\u7684\u8109\u95e8\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff0c\u4e56\u4e56\u5730\u8ddf\u7740\u90a3\u602a\u4eba\u8d70\uff0c\u4e8e\u627f\u73e0\u597d\u751f\u6000\u7591\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5c0f\u864e\u5b50\u7d22 \u6027\u5014\u5f3a\uff0c\u867d\u7136\u8109\u95e8\u53d7\u5236\uff0c\u4e5f\u4e0d\u5e94\u5982\u6b64\u670d\u5e16\uff1f\u201d\u4ed4\u7ec6\u4e00\u770b\uff0c\u4f46\u89c1\u90a3\u602a\u4eba\u51b7\u68ee\u68ee\u7684\u76ee\u5149\uff0c\u7d27\u7d27\u5730 \u76ef\u7740\u5c0f\u864e\u5b50\uff0c\u5c0f\u864e\u5b50\u7adf\u7136\u663e\u51fa\u7cbe\u795e\u604d\u60da\u7684\u6a21\u6837\uff0c\u4e8e\u627f\u73e0\u5927\u4e3a\u7740\u6025\uff0c\u5374\u53eb\u4e0d\u51fa\u58f0\u6765\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u602a\u4eba\u95ee\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u4f60\u548c\u4f60\u7684\u9ed1\u767d\u5e08\u7236\u4e00\u8def\u540c\u6765\u7684\u662f\u4e48\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u4e0d \u9519\u3002\u201d\u90a3\u602a\u4eba\u9053\uff1a\u201c\u4f60\u5230\u8fd9\u513f\u6765\u627e\u5e08\u7236\uff0c\u662f\u4e48\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u90a3\u602a\u4eba\u9053\uff1a\u201c\u4f60\u7b49 \u5230\u5e08\u7236\u4e4b\u540e\uff0c\u8fd8\u8981\u548c\u4ed6\u4eec\u540c\u8d70\u7684\uff0c\u662f\u4e48\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u662f\u5440\uff0c\u4e00\u70b9\u4e0d\u9519\uff01\u201d\u90a3\u9a78\u9a6c\u5ffd\u9053\uff1a \u201c\u5c0f\u864e\u5b50\uff0c\u4f60\u8fd8\u8ba4\u5f97\u6211\u4eec\u5417\uff1f\u201d\u5c0f\u864e\u5b50\u5446\u5446\u5730\u671b\u7740\u4ed6\u4eec\uff0c\u4f3c\u662f\u4f9d\u7a00\u8ba4\u5f97\uff0c\u4e00\u65f6\u95f4\u8bb0\u4e0d\u8d77\u6765\u3002 \u90a3\u602a\u4eba\u5fae\u7b11\u9053\uff1a\u201c\u5c0f\u5b69\u5b50\u8bb0\u6027\u5dee\uff0c\u9a78\u9a6c\u7237\u6ca1\u548c\u4ed6\u89c1\u8fc7\u51e0\u6b21\u9762\u5427\uff1f\u201d\u90a3\u9a78\u9a6c\u9053\uff1a\u201c\u55ef\uff0c\u5728\u5929\u7afa \u5580\u6797\u90a6\u7684\u65f6\u5019\u89c1\u8fc7\u4e00\u9762\uff0c\u90a3\u65f6\u4ed6\u597d\u50cf\u8fd8\u673a\u4f36\u5f97\u591a\uff01\u201d\u90a3\u602a\u4eba\u9053\uff1a\u201c\u4ed6\u6765\u8fd9\u513f\uff0c\u6c34\u571f\u4e0d\u670d\uff0c\u75c5 \u4e86\u51e0\u5929\uff0c\u521a\u521a\u624d\u597d\uff01\u201d\u62cd\u62cd\u624b\u9053\uff1a\u201c\u8bf7\u8499\u5143\u5b50\u6765\uff01\u201d\u4e00\u4e2a\u7a7f\u7740\u82d7\u88c5\u7684\u7537\u5b50\u4ece\u5185\u95f4\u8d70\u51fa\uff0c\u6b63\u662f \u5728\u571f\u53f8\u5e9c\u4e2d\u6446\u5e03\u5c0f\u864e\u5b50\u7684\u90a3\u4e2a\u4eba\u3002 \u3000\u3000\u90a3\u602a\u4eba\u53c8\u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff0c\u4f60\u8fd8\u8bb0\u5f97\u8fd9\u4e2a\u4eba\u5417\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u8bb0\u5f97\uff0c\u6628\u665a\u4ed6\u8fd8\u548c\u6211\u5728\u4e00 \u8d77\u3002\u201d\u90a3\u602a\u4eba\u5bf9\u9a78\u9a6c\u9053\uff1a\u201c\u8fd9\u4f4d\u8499\u5143\u5b50\u548c\u9ed1\u767d\u6469\u8bc3\u662f\u597d\u670b\u53cb\uff0c\u9ed1\u767d\u6469\u8bc3\u8fd9\u51e0\u5929\u5c31\u4f1a\u5230\u6765\uff0c\u9a78 \u9a6c\u82e5\u662f\u6025\u7740\u8d77\u7a0b\uff0c\u6211\u53eb\u8499\u5143\u5b50\u62a4\u9001\u4f60\u4eec\uff0c\u8ba9\u9ed1\u767d\u6469\u8bc3\u968f\u540e\u8d76\u4e0a\u597d\u4e86\u3002\u201d\u90a3\u9a78\u9a6c\u89c1\u4e86\u5c0f\u864e\u5b50\u4e4b \u540e\uff0c\u5bf9\u90a3\u602a\u4eba\u7684\u8bdd\uff0c\u4f3c\u662f\u4fe1\u4e86\u51e0\u5206\uff0c\u70b9\u4e86\u70b9\u5934\u3002\u90a3\u602a\u4eba\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6211\u7ed9\u516c\u4e3b\u548c\u9a78\u9a6c\u996f \u884c\u3002\u201d\u5728\u767d\u7389\u9152\u676f\u4e2d\u5012\u4e86\u4e00\u676f\u78a7\u7eff\u8272\u7684\u9152\uff0c\u5148\u9012\u7ed9\u9a78\u9a6c\uff0c\u8fd9\u9152\u6b63\u662f\u82d7\u533a\u4e2d\u72ec\u6709\u7684\u8ff7\u9b42\u9152\u3002 \u3000\u3000\u9a78\u9a6c\u63a5\u8fc7\u9152\u676f\uff0c\u521a\u521a\u78b0\u5230\u5507\u8fb9\uff0c\u5ffd\u89c1\u773c\u524d\u91d1\u5149\u4e00\u95ea\uff0c\u545b\u5577\u4e00\u58f0\uff0c\u767d\u7389\u676f\u88c2\u6210\u56db\u7247\uff0c\u8131\u624b \u98de\u53bb\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u6e05\u8106\u7684\u58f0\u97f3\u53eb\uff1a\u201c\u9152\u4e2d\u6709\u6bd2\uff0c\u8fd9\u53ae\u4e0d\u662f\u597d\u4eba\uff01\u201d \u3000\u3000\u5374\u539f\u6765\u5728\u8fd9\u4e00\u4f1a\u5b50\u529f\u592b\uff0c\u4e8e\u627f\u73e0\u5df2\u8fd0\u7528\u5185\u529f\uff0c\u81ea\u884c\u51b2\u5173\u89e3\u7a74\uff0c\u90a3\u602a\u4eba\u6599\u4e0d\u5230\u5979\u5e74\u7eaa\u8f7b \u8f7b\uff0c\u7adf\u6709\u8fd9\u6837\u4e0a\u4e58\u7684\u5185\u529f\uff0c\u51b7\u4e0d\u53ca\u9632\uff0c\u963b\u6b62\u5df2\u6765\u4e0d\u53ca\u3002\u4e8e\u627f\u73e0\u8fd0\u5251\u5982\u98ce\uff0c\u5411\u90a3\u602a\u4eba\u75be\u653b\u731b \u5241\uff0c\u90a3\u602a\u4eba\u8863\u8896\u4e00\u6296\uff0c\u4e00\u7f15\u5f02\u9999\uff0c\u76f4\u51b2\u4e8e\u627f\u73e0\u9f3b\u89c2\uff0c\u4e8e\u627f\u73e0\u5c4f\u606f\u5fc3\u795e\uff0c\u53cd\u624b\u4e00\u5251\u524a\u51fa\uff0c\u8f6c\u5934 \u6362\u6c14\uff0c\u5ffd\u542c\u5f97\u90a3\u602a\u4eba\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6492\u5251\uff01\u201d\u4e8e\u627f\u73e0\u53ea\u89c9\u5251\u5c16\u597d\u4f3c\u6709\u5343\u65a4\u538b\u529b\uff0c\u539f\u6765\u90a3\u50f5\u5c38\u822c \u7684\u602a\u4eba\u8d81\u7740\u8fd9\u4e2a\u7a7a\u9699\u968f\u624b\u5728\u684c\u4e0a\u62ff\u8d77\u4e00\u53cc\u7389\u7b77\uff0c\u631f\u7740\u4e86\u4e8e\u627f\u73e0\u7684\u5251\u5c16\uff0c\u90a3\u602a\u4eba\u7684\u529f\u529b\u6bd4\u5341\u627f \u73e0\u9ad8\u51fa\u4f55\u6b62\u4e00\u500d\uff0c\u4e8e\u627f\u73e0\u867d\u6709\u7edd\u597d\u7684\u5251\u6cd5\uff0c\u6beb\u65e0\u529e\u6cd5\u65bd\u5c55\u3002 \u3000\u3000\u5c0f\u864e\u4e8e\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u627f\u73e0\u59d0\u59d0\uff0c\u4e0d\u8981\u7740\u614c\uff0c\u6211\u6765\u52a9\u4f60\uff01\u201d\u201c\u7830\u201d\u7684\u4e00\u62f3\u6253\u51fa\uff0c\u9f99\u62f3\u7684\u62db \u5f0f\u521a\u4f7f\u5230\u4e00\u534a\uff0c\u80f3\u818a\u7a81\u7136\u7ed9\u8499\u5143\u5b50\u4e00\u53cd\u626d\uff0c\u8499\u5143\u5b50\u4eca\u665a\u88ab\u5c0f\u864e\u5b50\u8fde\u6253\u51e0\u62f3\uff0c\u5fc3\u5934\u6c14\u6068\u672a\u6d88\uff0c \u8fd9\u4e00\u4e0b\u64d2\u62ff\u624b\u626d\u5f97\u751a\u4e3a\u5389\u5bb3\uff0c\u5c0f\u864e\u5b50\u75db\u5f7b\u9aa8\u9ad3\uff0c\u4e5f\u4e8f\u4ed6\u633a\u5f97\u4f4f\uff0c\u5c45\u7136\u672a\u53eb\u51fa\u58f0\u3002\u90a3\u9a78\u9a6c\u7709\u5934 \u4e00\u76b1\uff0c\u6b63\u60f3\u53d1\u8bdd\uff0c\u5ffd\u542c\u5f97\u95e8\u5916\u4e00\u58f0\u602a\u7b11\uff0c\u6709\u4eba\u559d\u9053\uff1a\u201c\u8c01\u6562\u6b3a\u8d1f\u6211\u7684\u5f92\u513f\u3002\u201d \u3000\u3000\u8f70\u9686\u4e00\u58f0\uff0c\u5927\u95e8\u5012\u584c\uff0c\u6709\u5982\u8fc5\u96f7\u66b4\u51fb\uff0c\u72c2\u98ce\u9aa4\u8d77\uff0c\u5927\u5385\u4e0a\u70db\u5149\u6447\u66f3\uff0c\u4eba\u4eba\u53d8\u8272\uff0c\u53ea\u89c1\u9ed1 \u767d\u6469\u8bc3\u5df2\u51b2\u4e86\u8fdb\u6765\u3002\u8fd9\u4e24\u5144\u5f1f\u5f62\u8c8c\u76f8\u540c\uff0c\u5fc3\u5ff5\u5982\u4e00\uff0c\u8fde\u8bf4\u8bdd\u7684\u58f0\u97f3\u4e5f\u4e00\u6a21\u4e00\u6837\uff0c\u4e24\u4eba\u540c\u65f6\u6012 \u559d\uff0c\u8bf4\u8bdd\u7684\u5feb\u6162\u8bed\u53e5\u5747\u662f\u4e0d\u7ea6\u800c\u540c\uff0c\u5c31\u4f3c\u662f\u51fa\u4e8e\u4e00\u4eba\u4e4b\u53e3\uff0c\u6709\u5982\u91d1\u94c1\u4ea4\u9e23\uff0c\u76f4\u9707\u5f97\u4eba\u8033\u9f13\u55e1 \u55e1\u4f5c\u54cd\uff0c\u8499\u5143\u5b50\u6025\u5fd9\u653e\u624b\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u542c\u5f97\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u8499\u5143\u5b50\u5df1\u88ab\u9ed1\u767d\u6469\u8bc3\u6253 \u5f97\u98de\u4e86\u8d77\u6765\uff0c\u7ed9\u629b\u5230\u5385\u4e2d\u5fc3\u7684\u957f\u684c\u4e0a\uff0c\u90a3\u684c\u4e0a\u6446\u6ee1\u98df\u7269\uff0c\u88ab\u8499\u5143\u5b50\u7684\u8eab\u8eaf\u4e00\u6c0f\uff0c\u684c\u817f\u767b\u65f6\u65ad \u4e86\uff0c\u684c\u4e0a\u7684\u7897\u7897\u789f\u789f\u66f4\u662f\u7834\u788e\u65e0\u9057\uff0c\u54d7\u5566\u5566\u4e00\u7247\u523a\u8033\u7684\u5608\u97f3\u6742\u54cd\uff0c\u8fd9\u5a01\u52bf\u7684\u786e\u9a87\u4eba\u4e4b\u6781\u3002 \u3000\u3000\u9ed1\u6469\u8bc3\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u9f99\u62f3\u8981\u8fd9\u6837\u6253\u624d\u591f\u52b2\u9053\u3002\u5c0f\u864e\u5b50\uff0c\u77a7\u6e05\u695a\u4e86\uff0c\u6211\u518d\u6559\u4f60\u7ec3 \u62f3\uff01\u201d\u8863\u8896\u4e00\u62c2\uff0c\u53c8\u662f\u547c\u7684\u4e00\u62f3\u6253\u51fa\uff0c\u4ed6\u8ddd\u79bb\u90a3\u50f5\u5c38\u822c\u7684\u602a\u4eba\u5c1a\u6709\u6570\u4e08\uff0c\u62f3\u98ce\u4e00\u8d77\uff0c\u62f3\u5934\u5df2 \u500f\u5730\u6253\u5230\u4e86\u90a3\u602a\u4eba\u7684\u9762\u95e8\uff0c\u4e8e\u627f\u73e0\u53ea\u89c9\u5251\u4e0a\u4e00\u8f7b\uff0c\u539f\u6765\u5c31\u5728\u6b64\u65f6\uff0c\u602a\u4eba\u631f\u7740\u4e8e\u627f\u73e0\u5b9d\u5251\u7684\u90a3 \u53cc\u7b77\u5b50\uff0c\u65e9\u5df2\u88ab\u9ed1\u6469\u8bc3\u7684\u8863\u8896\u62c2\u65ad\u3002\u9ed1\u6469\u8bc3\u62f3\u8896\u4e24\u7528\uff0c\u62db\u6570\u7684\u5965\u5999\u5df2\u662f\u532a\u5937\u6240\u601d\uff0c\u800c\u8863\u8896\u8fd9 \u6837\u67d4\u8f6f\u4e4b\u97e7\uff0c\u7adf\u88ab\u4ed6\u8fd0\u7528\u5f97\u6709\u5982\u5200\u5251\uff0c\u90a3\u53cc\u7b77\u5b50\u88ab\u201c\u524a\u201d\u5f97\u6574\u6574\u9f50\u9f50\uff0c\u4ece\u4e2d\u5206\u4e3a\u56db\u6bb5\uff0c\u5185\u529f \u4e4b\u5f3a\uff0c\u66f4\u662f\u5230\u4e86\u96be\u4ee5\u7f6e\u4fe1\u7684\u5730\u6b65\uff01 \u3000\u3000\u90a3\u602a\u4eba\u907f\u5df2\u4e0d\u53ca\uff0c\u968f\u624b\u6293\u8d77\u4e24\u4e2a\u5c0f\u5f92\u5f1f\u4e00\u6863\uff0c\u8fd9\u4e24\u4e2a\u5c0f\u5f92\u5f1f\u80fd\u6709\u51e0\u5e74\u706b\u5019\uff0c\u6bd4\u8d77\u8499\u5143\u5b50 \u6765\u66f4\u662f\u5927\u5927\u4e0d\u5982\uff0c\u5e78\u800c\u9ed1\u6469\u8bc3\u4e34\u65f6\u6536\u52bf\uff0c\u53ea\u7528\u4e86\u4e09\u6210\u529b\u91cf\uff0c\u9976\u662f\u5982\u6b64\uff0c\u8fd9\u4e24\u4e2a\u5c0f\u5f92\u5f1f\u4ea6\u5df2\u7981 \u53d7\u4e0d\u8d77\uff0c\u88ab\u9ed1\u6469\u8bc3\u4e00\u62f3\u6253\u98de\uff0c\u4e00\u4e2a\u65ad\u4e86\u808b\u9aa8\uff0c\u4e00\u4e2a\u6298\u4e86\u624b\u81c2\uff0c\u90fd\u5012\u5728\u5730\u4e0a\u54fc\u54fc\u561f\u561f\u5730\u722c\u4e0d\u8d77 \u6765\u3002\u5eca\u4e0b\u7684\u4f17\u5f1f\u5b50\u5927\u4e3a\u5bd2\u5fc3\uff0c\u7eb7\u7eb7\u8d70\u907f\uff0c\u751f\u6015\u88ab\u5e08\u7236\u6293\u8d77\u6765\u5f53\u4f5c\u76fe\u724c\u3002 \u3000\u3000\u90a3\u602a\u4eba\u5fd9\u53eb\u9053\uff1a\u201c\u9ed1\u767d\u6469\u8bc3\uff0c\u6709\u8bdd\u597d\u8bf4\u3002\u201d\u767d\u6469\u8bc3\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u597d\u8bf4\uff1f\u6211\u8fd9\u62f3\u5934\u8fd8\u672a\u53d1 \u5e02\u5462\uff01\u5582\uff0c\u5c0f\u864e\u5b50\uff1b\u4f60\u7684\u7f57\u6c49\u795e\u62f3\u5fd8\u4e86\u6ca1\u6709\uff1f\u201d\u5c0f\u864e\u5b50\u54ed\u4e27\u7740\u8138\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u8fd9\u6761\u81c2\u818a \u4e0d\u80fd\u7528\u529b\u5566\uff01\u201d\u767d\u6469\u8bc3\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u600e\u4e48\u4e0d\u80fd\u7528\u529b\uff1f\u201d\u6293\u7740\u4ed6\u90a3\u6761\u88ab\u626d\u4f24\u7684\u81c2\u818a\u4e00\u6309\uff0c\u8f7b\u8f7b \u4e00\u62c9\uff0c\u5c0f\u864e\u5b50\u767b\u65f6\u75db\u695a\u82e5\u5931\uff0c\u767d\u6469\u8bc3\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4eba\u626d\u4f24\u4f60\u7684\u81c2\u818a\uff0c\u4f60\u53bb\u6253\u4ed6\u5341\u62f3\u3002\u201d\u8499\u5143 \u5b50\u521a\u722c\u8d77\u6765\uff0c\u88ab\u5c0f\u864e\u5b50\u8fce\u9762\u4e00\u62f3\uff0c\u53c8\u6253\u5f97\u76ae\u5f00\u8089\u88c2\u3001\u8dc4\u8dc4\u8e09\u8e09\u5730\u76f4\u9000\u4e86\u5341\u6765\u6b65\uff0c\u51e0\u4e4e\u53c8\u518d\u4ec6 \u5012\u3002 \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u54c8\u54c8\u5927\u7b11\uff0c\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u8fd9\u8001\u9b54\u5934\u4e5f\u5403\u6211\u4e00\u62f3\u3002\u201d\u4e24\u5144\u5f1f\u540c\u65f6\u98de\u8d77\uff0c\u53cc\u62f3 \u9f50\u51fa\uff0c\u90a3\u602a\u4eba\u6293\u8d77\u4e00\u4e2a\u4e91\u77f3\u8336\u51e0\u4e00\u6321\uff0c\u4e91\u77f3\u4e5f\u7ed9\u6253\u5f97\u788e\u88c2\u7eb7\u98de\uff0c\u90a3\u9a78\u9a6c\u5ffd\u9053\uff1a\u201c\u4e24\u4f4d\u5e08\u7236\u4f11 \u5f97\u83bd\u649e\uff01\u201d\u9ed1\u767d\u6469\u8bc3\u77aa\u773c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u8bf7\u6211\u4eec\u62a4\u9001\uff0c\u5374\u600e\u7684\u4e0d\u8bb8\u6211\u4eec\u6253\u4eba\uff1f\u201d\u90a3\u9a78\u9a6c \u9053\uff1a\u201c\u4ed6\u662f\u85e9\u738b\u3002\u201d\u9ed1\u6469\u8bc3\u5927\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u85e9\u738b\uff1f\u4ed6\u662f\u4e4c\u8499\u5c71\u7684\u5996\u4eba\u76d8\u5929\u7f57\uff0c\u5728\u8fd9\u91cc\u5f04\u9b3c\u4f5c \u602a\uff01\u201d\u4e24\u5144\u5f1f\u9053\u4e0a\u53bb\u518d\u6253\uff0c\u76d8\u5929\u7f57\u53eb\u9053\uff1a\u201c\u9ed1\u767d\u6469\u8bc3\uff0c\u6211\u597d\u610f\u4e0e\u4f60\u5546\u91cf\uff0c\u4f60\u5f53\u6211\u6015\u4f60\u4e0d \u6210\uff01\u201d\u5728\u8170\u95f4\u4e00\u62cd\uff0c\u624b\u4e2d\u5ffd\u5730\u591a\u4e86\u4e00\u4ef6\u5947\u5f62\u602a\u72b6\u7684\u5175\u5668\u3002 \u3000\u3000\u8fd9\u5175\u5668\u4f3c\u662f\u4e00\u6761\u8f6f\u97ad\uff0c\u4f46\u97ad\u7684\u5468\u56f4\uff0c\u5374\u6ee1\u662f\u952f\u9f7f\u72b6\u7684\u5c16\u523a\uff0c\u540d\u79f0\u5c31\u53eb\u505a\u201c\u952f\u9f7f\u97ad\u201d\uff0c\u8fd9 \u79cd\u97ad\u6cd5\uff0c\u53ea\u6709\u4e4c\u8499\u5c71\u7684\u8d64\u9707\u9053\u5165\u95e8\u4e0b\u80fd\u4f7f\uff0c\u4e0d\u4f46\u53ef\u4ee5\u5377\u8d70\u654c\u4eba\u5175\u5203\uff0c\u66f4\u5389\u5bb3\u7684\u662f\u8fd9\u79cd\u201c\u952f\u9f7f \u97ad\u201d\u4e13\u7834\u6c14\u529f\uff0c\u53ea\u8981\u8eab\u4f53\u4e00\u88ab\u6cbe\u4e0a\uff0c\u7acb\u523b\u76ae\u5f00\u8089\u88c2\uff0c\u591a\u597d\u7684\u5185\u529f\u4e5f\u96be\u62b5\u6321\u3002 \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u7eb5\u58f0\u5927\u7b11\uff1a\u201c\u4e4c\u8499\u5c71\u7684\u770b\u5bb6\u672c\u9886\u4e5f\u62ff\u51fa\u6765\u5566\uff01\u4f60\u6709\u795e\u97ad\uff0c\u54b1\u4eec\u4e5f\u6709\u5b9d\u6756\uff0c\u5012\u8981 \u770b\u770b\u662f\u4f60\u7684\u795e\u97ad\u5389\u5bb3\uff0c\u8fd8\u662f\u54b1\u4eec\u7684\u5b9d\u6756\u9ad8\u5f3a\uff1f\u201d\u9ed1\u6469\u8bc3\u62bd\u51fa\u7eff\u7389\u6756\uff0c\u767d\u6469\u8bc3\u62bd\u51fa\u767d\u7389\u6756\uff0c\u7eff \u5149\u767d\u5149\uff0c\u4ea4\u53c9\u98de\u821e\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u53ee\u5f53\u4e4b\u58f0\uff0c\u4fe8\u82e5\u7e41\u5f26\u6025\u7ba1\uff0c\u6709\u5982\u7435\u7436\u5723\u624b\uff0c\u7528\u98de\u5feb\u7684\u8f6e\u6307\u594f \u4e50\u4e00\u822c\uff01\u76d8\u65e0\u7f57\u5012\u62bd\u4e00\u53e3\u51b7\u6c14\uff0c\u62bd\u97ad\u4e00\u770b\uff0c\u53ea\u89c1\u97ad\u4e0a\u7684\u952f\u9f7f\u5168\u90fd\u5012\u5377\uff0c\u539f\u6765\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c \u4ed6\u4eec\u5df2\u8fc7\u4e86\u5341\u4f59\u4e8c\u5341\u62db\uff0c\u9ed1\u767d\u6469\u8bc3\u8fd9\u4e24\u67c4\u5b9d\u6756\u662f\u81f3\u575a\u81f3\u786c\u4e4b\u7269\uff0c\u5f53\u5e74\u5f20\u4e39\u67ab\u7528\u9752\u51a5\u5b9d\u5251\u4e0e\u4ed6 \u4eec\u4ea4\u624b\uff0c\u4e5f\u4e0d\u80fd\u5c06\u8fd9\u4e24\u67c4\u5b9d\u6756\u635f\u4f24\uff0c\u4f55\u51b5\u662f\u952f\u9f7f\u97ad\uff1f\u53cd\u800c\u662f\u952f\u9f7f\u88ab\u5b9d\u6756\u78e8\u949d\u4e86\uff01 \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u53cc\u6756\u4e00\u5408\uff0c\u4e00\u6b65\u4e00\u6b65\u5730\u5411\u4e2d\u5fc3\u5408\u56f4\uff0c\u76d8\u5929\u7f57\u8fd9\u6761\u952f\u9f7f\u97ad\u957f\u8fbe\u4e00\u4e08\u4e94\u5c3a\uff0c\u821e\u52a8\u8d77 \u6765\u4e8c\u4e09\u4e08\u5185\uff0c\u65e0\u4eba\u6562\u8fd1\uff0c\u4e0d\u6599\u800c\u4eca\u649e\u7740\u4e86\u514b\u661f\uff0c\u4e0d\u4f46\u6b66\u529f\u53ca\u4e0d\u4e0a\u5bf9\u65b9\uff0c\u8fde\u5175\u5668\u4e5f\u4e0d\u53ca\u5bf9\u65b9\uff0c \u773c\u770b\u5708\u5b50\u8d8a\u7f29\u8d8a\u5c0f\uff0c\u518d\u8fc7\u7247\u65f6\uff0c\u76d8\u5929\u7f57\u5b9a\u7136\u8981\u4f24\u5728\u9ed1\u767d\u6469\u8bc3\u6756\u4e0b\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e8e\u627f\u73e0\u53eb\u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff0c\u4f60\u600e\u4e48\u53c8\u4e0d\u6253\u5566\uff1f\u201d\u767d\u6469\u8bc3\u56de\u5934\u4e00\u77a5\uff0c\u53ea\u89c1\u5c0f\u864e\u5b50\u773c\u5149 \u5446\u6ede\uff0c\u7ad9\u5728\u8499\u5143\u5b50\u9762\u524d\uff0c\u62f3\u5934\u6162\u6162\u5782\u4e0b\uff0c\u8499\u5143\u5b50\u53cc\u773c\u5706\u7741\uff0c\u76ee\u4e0d\u8f6c\u775b\u5730\u76ef\u7740\u5c0f\u864e\u5b50\uff0c\u6c89\u58f0\u53eb \u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff0c\u4f60\u5f97\u542c\u6211\u7684\u8bdd\uff01\u201d \u3000\u3000\u767d\u6469\u8bc3\u5927\u543c\u4e00\u58f0\uff0c\u500f\u5730\u8dc3\u51fa\u5708\u5b50\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u6211\u6559\u4f60\u7684\u7f57\u6c49\u795e\u62f3\uff0c\u4f60 \u90fd\u5fd8\u4e86\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5c0f\u864e\u5b50\u5403\u4e86\u4ed6\u4eec\u7684\u8ff7\u836f\u5566\uff01\u201d\u767d\u6469\u8bc3\u53eb\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u201d\u628a\u5c0f\u864e \u5b50\u4e00\u628a\u626f\u8fc7\uff0c\u5728\u4ed6\u8111\u95e8\u3001\u80cc\u5fc3\u3001\u5de6\u80c1\uff0c\u8fde\u62cd\u4e09\u638c\uff0c\u53eb\u9053\uff1a\u201c\u5feb\u53bb\u6253\u4ed6\uff0c\u4ed6\u662f\u574f\u4eba\uff01\u201d\u767d\u6469\u8bc3 \u8fd9\u4e09\u638c\u662f\u745c\u73c8\u672f\u4e2d\u4e00\u79cd\u6781\u5947\u5999\u7684\u529f\u592b\uff0c\u795e\u7ecf\u9519\u4e71\u3001\u7075\u6027\u8ff7\u5931\u7684\u4eba\u88ab\u4ed6\u4e00\u62cd\u5373\u9178\uff0c\u5c0f\u864e\u5b50\u773c\u795e \u9aa4\u957f\uff0c\u970e\u65f6\u95f4\u597d\u50cf\u6362\u4e86\u4e2a\u4eba\uff0c\u5fd8\u8bb0\u7684\u4e8b\u5168\u90fd\u8bb0\u4e86\u8d77\u6765\uff0c\u8499\u5143\u5b50\u5bf9\u4ed6\u7684\u6298\u78e8\uff0c\u5c06\u4ed6\u6446\u5e03\u7b49\u7b49\u4e8b \u60c5\uff0c\u90fd\u5386\u5386\u5982\u5728\u76ee\u524d\uff0c\u5c0f\u864e\u5b50\u53f1\u54a4\u4e00\u58f0\uff0c\u4e0d\u987b\u4e8e\u627f\u73e0\u518d\u53eb\uff0c\u679c\u7136\u4fbf\u50cf\u4e00\u5934\u5c0f\u8001\u864e\u4f3c\u7684\u6251\u4e0a \u53bb\uff0c\u4e00\u53e3\u6c14\u8fde\u4f7f\u51fa\u9f99\u3001\u864e\u3001\u8c79\u3001\u86c7\u3001\u9e64\u4e94\u79cd\u795e\u62f3\uff0c\u8499\u5143\u5b50\u9002\u624d\u4e2d\u4e86\u9ed1\u767d\u6469\u8bc3\u4e00\u62f3\uff0c\u529f\u529b\u5df2\u6d88 \u4e86\u4e00\u534a\uff0c\u5982\u4f55\u4ee5\u53d7\u5f97\u8d77\uff0c\u88ab\u5c0f\u864e\u5b50\u6253\u5f97\u76ae\u5f00\u8089\u88c2\uff0c\u7b4b\u65ad\u9aa8\u6298\uff0c\u4ec6\u5728\u5730\u4e0a\uff0c\u4e0a\u6c14\u4e0d\u63a5\u4e0b\u6c14\uff0c\u518d \u4e5f\u722c\u4e0d\u8d77\u6765\u4e86\uff01 \u3000\u3000\u9ed1\u767d\u8bc3\u5927\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u8ba9\u4f60\u628a\u5e2e\u624b\u5524\u51fa\u6765\u518d\u6253\uff01\u201d\u53cc\u6756\u652f\u5730\u4fa7\u76ee\u659c\u7768\uff0c\u53ea\u89c1\u602a\u5578\u58f0 \u4e2d\uff0c\u5927\u5385\u4e0a\u53c8\u7a81\u7136\u6d8c\u51fa\u4e24\u4e2a\u602a\u4eba\uff01\u6b63\u662f\uff1a \u3000\u3000\u53cc\u53cc\u5f02\u56fd\u5947\u4eba\u5230\uff0c\u864e\u6597\u9f99\u4e89\u53c8\u4e00\u573a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff0c\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u5341\u516b\u56de\u3000\u624b\u53d1\u91d1\u7403\u3000\u901a\u7384\u53c2\u5999\u7406\u3000\u53e3\u541e\u706b\u5251\u3000\u70ab\u6280\u9a87\u95f2\u4eba \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u5341\u516b\u56de\u3000\u624b\u53d1\u91d1\u7403\u3000\u901a\u7384\u53c2\u5999\u7406\u3000\u53e3\u541e\u706b\u5251\u3000\u70ab\u6280\u9a87\u95f2\u4eba \u3000\u3000\u8fd9\u4e24\u4eba\u7a7f\u7684\u90fd\u662f\u4e00\u5f0f\u9ec4\u7ee2\u957f\u888d\uff0c\u9876\u675f\u5e1b\u9526\uff0c\u9ad8\u9f3b\u6df1\u76ee\uff0c\u66f4\u5999\u7684\u662f\uff0c\u4e0d\u4f46\u6253\u626e\u76f8\u540c\uff0c\u9762\u8c8c \u4e5f\u5b8c\u5168\u4e00\u6837\uff0c\u53ea\u662f\u4e00\u4e2a\u7f3a\u4e86\u5de6\u8033\uff0c\u4e00\u4e2a\u7f3a\u4e86\u53f3\u8033\u3002\u53e4\u5821\u4e2d\u7684\u4e00\u4f17\u4eba\u7b49\uff0c\u5148\u524d\u89c1\u4e86\u9ed1\u767d\u6469\u8bc3\u8fd9 \u5bf9\u5b6a\u751f\u5144\u5f1f\uff0c\u5df2\u81ea\u89c9\u5f97\u5947\u7279\uff0c\u54ea\u77e5\u5929\u4e0b\u4e4b\u5927\uff0c\u7adf\u662f\u65e0\u5947\u4e0d\u6709\uff0c\u4e0d\u5230\u4e00\u76cf\u8336\u7684\u529f\u592b\uff0c\u7adf\u7136\u53c8\u6765 \u4e86\u4e00\u65f6\u5b6a\u751f\u7684\u602a\u4eba\uff0c\u4f17\u4eba\u90fd\u770b\u5f97\u5446\u4e86\u3002 \u3000\u3000\u8fd9\u5bf9\u5b6a\u751f\u7684\u602a\u4eba\uff0c\u6b63\u662f\u53bb\u5e74\u5728\u592a\u6e56\u6d1e\u5ead\u5c71\u4e0b\uff0c\u88ab\u9ed1\u767d\u6469\u8bc3\u795e\u7bad\u6240\u4f24\u7684\u90a3\u5bf9\u602a\u4eba\u2014\u2014\u963f\u8428 \u739b\u548c\u963f\u5408\u739b\u3002\u9ed1\u767d\u6469\u8bc3\u89c1\u662f\u4ed6\u4eec\uff0c\u4e5f\u4e0d\u7981\u4e00\u6014\uff0c\u968f\u5373\u54c8\u54c8\u5927\u7b11\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u8d24\u6606\u4ef2\u679c\u662f\u4fe1 \u4eba\uff0c\u4e00\u5e74\u4e4b\u671f\u8fd8\u5dee\u4e09\u65e5\u672a\u6ee1\u5462\uff01\u201d \u3000\u3000\u963f\u8428\u739b\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u62b1\u62f3\u8fd8\u793c\uff0c\u5374\u5e76\u4e0d\u56de\u7b54\u9ed1\u767d\u6469\u8bc3\u7684\u8bdd\uff0c\u8f6c\u8eab\u5411\u90a3\u897f\u57df\u7f8e\u4eba\u5f2f\u8170\u884c \u793c\uff0c\u53fd\u54e9\u5495\u565c\u4ed6\u8bb2\u4e86\u4e00\u5927\u6bb5\u8bdd\uff0c\u9664\u4e86\u9ed1\u767d\u6469\u8bc3\u7a0d\u80fd\u542c\u61c2\u4e4b\u5916\uff0c\u5176\u4f59\u7b49\u4eba\u90fd\u662f\u83ab\u540d\u5176\u5999\u3002\u53ea\u89c1 \u90a3\u897f\u57df\u7f8e\u4eba\u67f3\u7709\u5fae\u8e59\uff0c\u773c\u89d2\u6709\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u6e10\u6e10\u9762\u8272\u4e0d\u5bf9\uff0c\u8d8a\u6765\u8d8a\u663e\u51fa\u602b\u7136\u4e0d\u60a6\u7684\u795e\u6c14\uff0c\u963f \u8428\u739b\u6001\u5ea6\u4ea6\u662f\u8d8a\u6765\u8d8a\u606d\u656c\uff0c\u4f46\u4ecd\u662f\u5520\u5520\u53e8\u53e8\u5730\u8bf4\u4e2a\u4e0d\u4f11\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5927\u4e3a\u8be7\u5f02\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ee5\u963f\u8428\u739b\u5144\u5f1f\u7684\u672c\u9886\uff0c\u5bf9\u8fd9\u897f\u57df\u7f8e\u4eba\u6267\u793c\u5982\u6b64\u4e4b\u606d\uff0c\u770b \u6765\u5979\u771f\u662f\u516c\u4e3b\u7684\u8eab\u4efd\u4e86\uff0c\u4f46\u4f55\u4ee5\u5374\u53c8\u548c\u9ed1\u767d\u6469\u8bc3\u53ca\u8fd9\u53e4\u5821\u602a\u4eba\u7275\u8fde\u4e0a\u5173\u7cfb\u5462\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u731c\u5f97\u4e0d\u9519\uff0c\u8fd9\u897f\u57df\u7f8e\u4eba\u679c\u7136\u662f\u6ce2\u65af\u56fd\u7684\u516c\u4e3b\uff0c\u5979\u7684\u4e08\u592b\u5c31\u662f\u90a3\u4e2a\u8eab\u957f\u7389\u7acb\u7684\u52c7 \u58eb\u3002\u8fd9\u7537\u5b50\u9762\u8c8c\u6709\u4e00\u534a\u4f3c\u6c49\u4eba\uff0c\u5374\u53c8\u6709\u4e00\u534a\u4f3c\u963f\u62c9\u4f2f\u4eba\uff0c\u5176\u5b9e\u5374\u662f\u5927\u7406\u767d\u65cf\u4eba\uff0c\u540d\u53eb\u6bb5\u6f84 \u82cd\u3002\u5927\u7406\u6bb5\u5bb6\uff0c\u9996\u5c48\u4e00\u6307\uff0c\u5728\u5b8b\u4ee3\u4ee5\u524d\uff0c\u4e16\u4ee3\u4e3a\u738b\uff0c\u5143\u706d\u5927\u7406\u56fd\uff0c\u6539\u5c01\u5f53\u65f6\u6bb5\u5bb6\u7684\u540e\u4ee3\u6bb5\u529f \u4e3a\u201c\u5e73\u7ae0\u201d\uff08\u5bb0\u76f8\uff09\uff0c\u8fd9\u4f4d\u6bb5\u529f\u66fe\u5728\u5927\u7406\u7559\u4e0b\u8bb8\u591a\u653f\u7ee9\uff0c\u6bd4\u8d77\u4ed6\u7684\u524d\u4ee3\u90a3\u4e9b\u5927\u7406\u56fd\u738b\u5efa\u6811\u66f4 \u591a\uff0c\u81f3\u4eca\u6ec7\u4eba\u5c1a\u79f0\u9053\u4e0d\u8870\u3002\u6bb5\u6f84\u82cd\u662f\u6bb5\u529f\u7684\u4e03\u4e16\u5b59\u3002\u5f53\u5e74\u8499\u53e4\u5175\u5a01\uff0c\u9707\u6151\u4e16\u754c\uff0c\u66fe\u6a2a\u626b\u6b27 \u4e9a\uff0c\u8fdc\u8fbe\u975e\u6d32\uff0c\u5efa\u7acb\u4e86\u4e3e\u4e16\u65e0\u5339\u7684\u56db\u5927\u6c57\u56fd\u3002\u6bb5\u529f\u6709\u4e2a\u513f\u5b50\u66fe\u9886\u5e08\u65c5\u968f\u8499\u5360\u519b\u897f\u5f81\uff0c\u8499\u53e4\u7684 \u5927\u5e1d\u56fd\u74e6\u89e3\u4e4b\u540e\uff0c\u4ed6\u4eec\u4ecd\u7559\u5728\u6ce2\u65af\uff08\u5373\u4eca\u4e4b\u4f0a\u6717\uff09\uff0c\u51e0\u4ee3\u76f8\u4f20\uff0c\u5747\u5a36\u6ce2\u65af\u5987\u4eba\u3002\u6bb5\u5bb6\u4ece\u6bb5\u529f \u90a3\u4ee3\u8d77\uff0c\u4e2a\u4e2a\u7cbe\u4e8e\u5251\u672f\uff0c\u6bb5\u6f84\u82cd\u5c24\u4e3a\u7279\u51fa\uff0c\u5e74\u672a\u5f31\u51a0\uff0c\u5df2\u7ecf\u996e\u8a89\u6ce2\u65af\uff0c\u88ab\u79f0\u4e3a\u6ce2\u65af\u5251\u672f\u7b2c\u4e00 \u9ad8\u624b\uff0c\u6ce2\u65af\u56fd\u738b\u8058\u8bf7\u4ed6\u4e3a\u5bab\u5ef7\u7684\u5251\u672f\u6559\u5e08\uff0c\u4e0d\u610f\u6ce2\u65af\u516c\u4e3b\uff0c\u7adf\u7136\u5bf9\u4ed6\u5782\u9752\uff0c\u4e24\u4eba\u6697\u604b\u6570\u5e74\uff0c \u56fd\u738b\u4e5f\u542c\u5230\u4e00\u4e9b\u98ce\u58f0\u4e86\uff0c\u4e3a\u4e86\u4fdd\u6301\u7687\u5ba4\u7684\u5c0a\u4e25\uff0c\u516c\u4e3b\u7edd\u65e0\u4e0b\u5ac1\u4e00\u4e2a\u5bab\u5ef7\u7684\u5251\u672f\u6559\u5e08\u4e4b\u7406\uff0c\u56fd \u738b\u662f\u516c\u4e3b\u7684\u957f\u5144\uff0c\u542c\u5230\u98ce\u58f0\u4e4b\u540e\uff0c\u4fbf\u50ac\u59b9\u59b9\u51fa\u5ac1\u3002\u516c\u4e3b\u628a\u5fc3\u4e00\u6a2a\uff0c\u7ade\u968f\u6bb5\u6f84\u82cd\u51fa\u8d70\uff0c\u968f\u8eab\u5e26 \u4e86\u8bb8\u591a\u6ce2\u65af\u5bab\u4e2d\u7684\u5b9d\u7269\u3002 \u3000\u3000\u963f\u8428\u739b\u5144\u5f1f\u662f\u6ce2\u65af\u7684\u56fd\u5e08\uff0c\u56fd\u738b\u95fb\u8baf\u5927\u6012\uff0c\u7acb\u547d\u4ed6\u4eec\u4e07\u91cc\u8ffd\u8e2a\uff0c\u52a1\u5fc5\u8981\u5c06\u516c\u4e3b\u548c\u6bb5\u6f84\u82cd \u7f09\u56de\u3002\u6bb5\u6f84\u82cd\u81ea\u77e5\u4e0d\u662f\u963f\u8428\u739b\u5144\u5f1f\u7684\u5bf9\u624b\uff0c\u9003\u5230\u5370\u5ea6\uff0c\u5f97\u4eba\u4ecb\u7ecd\uff0c\u6c42\u52a9\u4e8e\u9ed1\u767d\u6469\u8bc3\uff0c\u9ed1\u767d\u6469 \u8bc3\u672c\u6765\u662f\u5927\u73e0\u5b9d\u5546\uff0c\u884c\u8e2a\u904d\u5370\u5ea6\u3001\u4e2d\u56fd\u3001\u6ce2\u65af\u7b49\u56fd\uff08\u4ed6\u4eec\u7684\u59bb\u5b50\u5c31\u662f\u6ce2\u65af\u4eba\u58eb\uff09\u3002\u516c\u4e3b\u4ee5\u91cd \u5b9d\u4e3a\u916c\uff0c\u9ed1\u767d\u6469\u8bc3\u5bf9\u4ed6\u4eec\u751a\u662f\u540c\u60c5\uff0c\u4fbf\u7b54\u5e94\u62a4\u9001\u4ed6\u4eec\u5230\u4e2d\u56fd\u6765\u3002 \u3000\u3000\u4f46\u963f\u8428\u739b\u5144\u5f1f\u5df2\u7acb\u5373\u8ffd\u8e2a\u800c\u81f3\uff0c\u5728\u5370\u5ea6\u5580\u6797\u90a6\u7684\u9996\u5e9c\u4fbf\u4e0e\u9ed1\u767d\u6469\u8bc3\u5927\u6597\u4e00\u573a\uff0c\u4e0d\u5206\u80dc \u8d1f\uff0c\u9ed1\u767d\u6469\u8bc3\u6765\u4e0d\u53ca\u643a\u5e26\u6ce2\u65af\u516c\u4e3b\uff0c\u53ea\u597d\u8ba9\u5979\u548c\u6bb5\u6f84\u82cd\u9690\u853d\u5728\u81ea\u5df1\u5370\u5ea6\u670b\u53cb\u7684\u5bb6\u4e2d\u3002\u963f\u8428\u739b \u5144\u5f1f\u5374\u53ea\u987e\u5411\u9ed1\u767d\u6469\u8bc3\u8981\u4eba\uff0c\u4e00\u8def\u8ffd\u8d76\uff0c\u7ea0\u7f20\u4e0d\u4f11\uff0c\u76f4\u8ffd\u5230\u4e2d\u56fd\uff0c\u9ed1\u767d\u6469\u8bc3\u5341\u5206\u70e6\u607c\uff0c\u672c\u6765 \u60f3\u8bf7\u5f20\u4e39\u67ab\u51fa\u6765\uff0c\u628a\u4ed6\u4eec\u6253\u53d1\uff0c\u5c82\u77e5\u5230\u4e86\u592a\u6e56\u6d1e\u5ead\u5c71\uff0c\u5f20\u4e39\u67ab\u53c8\u5df2\u5f03\u5bb6\u51fa\u8d70\uff0c\u907f\u96be\u6ec7\u4e2d\uff0c\u5e78 \u4e8f\u9047\u5230\u4e86\u4e8e\u627f\u73e0\uff0c\u501f\u5230\u4e86\u5f20\u58eb\u8bda\u5f53\u5e74\u7684\u9547\u56fd\u5b9d\u5f13\uff0c\u8fde\u53d1\u4e09\u7bad\uff0c\u624d\u628a\u963f\u8428\u739b\u5144\u5f1f\u5c04\u4f24\uff0c\u628a\u4ed6\u4eec \u9010\u8d70\u3002 \u3000\u3000\u4e8e\u662f\u9ed1\u767d\u6469\u8bc3\u91cd\u56de\u5370\u5ea6\uff0c\u518d\u62a4\u9001\u6bb5\u6f84\u82cd\u4e0e\u516c\u4e3b\u6765\u534e\u3002\u516c\u4e3b\u540c\u610f\u6765\u534e\uff0c\u5374\u53e6\u6709\u4e00\u756a\u5fc3\u4e8b\uff0c \u539f\u6765\u8499\u53e4\u81ea\u4e5f\u5148\u5174\u8d77\u4e4b\u540e\uff0c\u53c8\u518d\u5f3a\u76db\u8d77\u6765\uff0c\u4e5f\u5148\u4f50\u8131\u8131\u4e0d\u82b1\u5efa\u56fd\u74e6\u523a\uff0c\u5728\u571f\u6728\u5821\u4e4b\u540e\uff0c\u51e0\u4e4e \u518d\u706d\u4e2d\u56fd\uff0c\u5176\u540e\u4e5f\u5148\u867d\u88ab\u8499\u53e4\u53e6\u4e00\u4e2a\u5927\u90e8\u843d\u7684\u914b\u957f\u963f\u523a\u51fb\u706d\uff0c\u4f46\u8131\u8131\u4e0d\u82b1\u7684\u513f\u5b50\u4e4c\u67ef\u514b\u56fe\u53c8 \u518d\u7ee7\u8d77\uff0c\u660e\u671d\u79f0\u4e4b\u4e3a\u201c\u5c0f\u738b\u5b50\u201d\uff0c\u56fd\u52bf\u66f4\u76db\uff0c\u5175\u529b\u76f4\u5230\u4e2d\u4e9a\u7ec6\u4e9a\uff0c\u51e0\u4e0e\u6ce2\u65af\u5e1d\u56fd\u63a5\u58e4\u3002\u6ce2\u65af \u5f53\u5e74\u66fe\u53d7\u8499\u53e4\u94c1\u8e44\u8df7\u56f0\uff0c\u63d0\u8d77\u201c\u9ec4\u7978\u201d\uff0c\u4eba\u4eba\u8272\u53d8\u3002\u6ce2\u65af\u516c\u4e3b\u867d\u7136\u5f03\u56fd\u9003\u4ea1\uff0c\u4f46\u5bf9\u7956\u56fd\u7684\u5fe7 \u60a3\uff0c\u90a3\u5374\u662f\u65e0\u65f6\u4e0d\u6302\u5728\u5fc3\u5934\u3002\u56e0\u6b64\u5979\u60f3\u4ee5\u6ce2\u65af\u516c\u4e3b\u7684\u8eab\u4efd\uff0c\u5230\u5317\u4eac\u8c12\u89c1\u4e2d\u56fd\u7684\u7687\u5e1d\uff0c\u6c42\u4ed6\u4e0e \u6ce2\u65af\u8054\u76df\uff0c\u5171\u9632\u9791\u977c\uff08\u201c\u5c0f\u738b\u5b50\u201d\u7edf\u9886\u74e6\u523a\u65e7\u90e8\uff0c\u79f0\u53f7\u631e\u978d\u53ef\u6c57\uff09\u3002\u6ce2\u65af\u516c\u4e3b\u4e00\u7247\u5929\u771f\u70ed \u60c5\uff0c\u54ea\u77e5\u660e\u671d\u7684\u73b0\u52bf\uff0c\u6bb5\u6f84\u82cd\u6570\u4ee3\u4ee5\u6765\uff0c\u7f81\u56f9\u5f02\u56fd\uff0c\u4e5f\u4e0d\u77e5\u4e2d\u56fd\u56fd\u60c5\uff0c\u8fd8\u771f\u60f3\u56de\u56fd\u4e4b\u540e\uff0c\u521b \u7acb\u4e00\u756a\u4e8b\u4e1a\uff0c\u4e3a\u4e2d\u56fd\u4e3a\u6ce2\u65af\u6548\u52b3\u3002 \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u5341\u4f59\u5e74\u524d\uff0c\u66fe\u5728\u5317\u4eac\u6210\u4eb2\u738b\u738b\u5e9c\u76d7\u8fc7\u73e0\u5b9d\uff0c\u53c8\u56e0\u4e0e\u5f20\u4e39\u67ab\u6765\u5f80\uff0c\u5f97\u7f6a\u4e86\u5f53\u65f6\u7684 \u7687\u5e1d\u7948\u9547\uff0c\u901a\u7f09\u5728\u6848\uff0c\u800c\u4eca\u7948\u9547\u590d\u767b\u7687\u4f4d\uff0c\u9ed1\u767d\u6469\u8bc3\u867d\u7136\u662f\u5929\u4e0d\u6015\u5730\u4e0d\u6015\u7684\u4eba\u7269\uff0c\u4f46\u5374\u4e0d\u80fd \u4e0d\u6015\u7275\u7d2f\u4e86\u6ce2\u65af\u516c\u4e3b\uff0c\u6545\u6b64\u4e00\u8def\u4e0a\u4e0d\u6562\u540c\u884c\uff0c\u53ea\u662f\u6697\u4e2d\u4fdd\u62a4\uff0c\u4ed6\u4eec\u672c\u60f3\u5148\u5230\u4e91\u5357\u5927\u7406\uff0c\u4e00\u8005 \u662f\u60f3\u627e\u5f20\u4e39\u67ab\u5546\u91cf\uff0c\u4e8c\u8005\u662f\u6bb5\u6f84\u82cd\u4ea6\u5f97\u507f\u8fd8\u4e61\u4e4b\u613f\uff0c\u54ea\u77e5\u5728\u4e91\u8d35\u9ad8\u539f\u7684\u4e1b\u5c71\u5cfb\u5cad\u4e2d\uff0c\u5ffd\u7136\u8d70 \u6563\uff0c\u9ed1\u767d\u6469\u8bc3\u51e0\u7ecf\u63a2\u542c\uff0c\u624d\u63a2\u51fa\u6bb5\u6f84\u82cd\u662f\u88ab\u76d8\u5929\u7f57\u8bbe\u4e0b\u9677\u9631\u5f15\u6765\u6b64\u5730\u3002 \u3000\u3000\u76d8\u5929\u7f57\u662f\u4e4c\u8499\u5c71\u8d64\u971e\u9053\u4eba\u7684\u5927\u5f1f\u5b50\uff0c\u8d64\u971e\u9053\u4eba\u4e09\u4e2a\u5f1f\u5b50\uff0c\u76d8\u5929\u7f57\u5c45\u9996\uff0c\u6b66\u529f\u4ea6\u662f\u6700\u5f3a\uff0c \u4f46\u56e0\u8db3\u8ff9\u4e0d\u51fa\u8d35\u5dde\uff0c\u957f\u5e74\u5f85\u5949\u5e08\u7236\uff0c\u540d\u5b57\u53cd\u800c\u4e0d\u4e3a\u5916\u4eba\u6240\u77e5\u3002\u9633\u5b97\u6d77\u6392\u884c\u7b2c\u4e09\uff0c\u6700\u5f97\u5e08\u7236\u559c \u7231\uff0c\u4f20\u4e86\u4e00\u624b\u8d64\u57ce\u5251\u6cd5\uff0c\u5728\u897f\u5357\u4e00\u5e26\uff0c\u626c\u540d\u95ef\u4e07\uff0c\u56e0\u6b64\u5f97\u4e0e\u5f20\u4e39\u67ab\u7b49\u4eba\u5e76\u79f0\u5929\u4e0b\u56db\u5927\u5251\u5ba2\uff0c \u90a3\u4e2a\u8499\u5143\u5b50\u6392\u884c\u7b2c\u4e8c\uff0c\u6b66\u529f\u5374\u662f\u6700\u5f31\u3002 \u3000\u3000\u963f\u8428\u739b\u5144\u5f1f\u53d7\u4e86\u7bad\u4f24\u4e4b\u540e\uff0c\u5c31\u53bb\u62dc\u8bbf\u76d8\u5929\u7f57\uff0c\u8bf7\u4ed6\u62d4\u5200\u76f8\u52a9\uff0c\u622a\u51fb\u9ed1\u767d\u6469\u8bc3\uff0c\u8ba2\u4e0b\u534f \u5b9a\uff0c\u963f\u8428\u739b\u5144\u5f1f\u53ea\u8981\u622a\u56de\u6ce2\u65af\u516c\u4e3b\uff0c\u81f3\u4e8e\u4ed6\u4eec\u968f\u8eab\u643a\u5e26\u7684\u73e0\u5b9d\uff0c\u5219\u5c3d\u5f52\u76d8\u5929\u7f57\u6240\u6709\uff0c\u76d8\u5929\u7f57 \u81ea\u662f\u6026\u7136\u5fc3\u52a8\uff0c\u4f46\u4ed6\u4eec\u4ea6\u4e45\u95fb\u9ed1\u767d\u6469\u8bc3\u7684\u5a01\u540d\uff0c\u4e0d\u6562\u9020\u6b21\uff0c\u5546\u91cf\u518d\u4e09\uff0c\u624d\u60f3\u51fa\u4e00\u4e2a\u529e\u6cd5\u3002 \u3000\u3000\u8d64\u57ce\u6d3e\u7684\u5f1f\u5b50\u5728\u4e91\u8d35\u9ad8\u539f\u6709\u6781\u5927\u7684\u52bf\u529b\uff0c\u7531\u76d8\u5929\u7f57\u5bc6\u4ee4\u5f92\u4f17\uff0c\u4e2d\u9014\u622a\u52ab\u6bb5\u6f84\u82cd\u548c\u6ce2\u65af\u516c \u4e3b\uff0c\u800c\u4ed6\u5374\u8d81\u9ed1\u767d\u6469\u8bc3\u672a\u8d76\u5230\u4e4b\u524d\uff0c\u5148\u7387\u4eba\u6765\u89e3\u6551\uff0c\u53c8\u5047\u5192\u662f\u660e\u5ba4\u7684\u85e9\u738b\uff0c\u5c06\u516c\u4e3b\u8fce\u56de\u4ed6\u7684 \u53e4\u5821\uff0c\u6bb7\u52e4\u62db\u5f85\u3002\u9ed1\u767d\u6469\u8bc3\u7b2c\u4e00\u6b21\u8d76\u6765\uff0c\u56e0\u4e3a\u5f53\u65f6\u8fd8\u67e5\u4e0d\u5230\u5b9e\u636e\uff0c\u5927\u95f9\u4e00\u573a\u4e4b\u540e\uff0c\u975e\u4f46\u65e0\u7ed3 \u679c\u800c\u9000\uff0c\u53cd\u800c\u53c8\u5931\u843d\u4e86\u5c0f\u864e\u5b50\u3002 \u3000\u3000\u963f\u8428\u739b\u5144\u5f1f\u4e0e\u76d8\u5929\u7f57\u7b49\u4eba\u6700\u5fcc\u60ee\u7684\u662f\u9ed1\u767d\u6469\u8bc3\u4ece\u745c\u73c8\u672f\u6240\u4fee\u70bc\u6765\u7684\u5947\u5999\u5185\u529f\uff0c\u64d2\u83b7\u4e86\u5c0f \u864e\u5b50\u540e\uff0c\u5982\u83b7\u81f3\u5b9d\uff0c\u60f3\u4ece\u5c0f\u864e\u5b50\u53e3\u4e2d\uff0c\u5957\u53d6\u9ed1\u767d\u6469\u8bc3\u7684\u5185\u529f\u5fc3\u6cd5\uff0c\u54ea\u77e5\u5c0f\u864e\u5b50\u867d\u7136\u5e74\u5c0f\uff0c\u5374 \u662f\u673a\u7075\u4e4b\u6781\uff0c\u7edd\u4e0d\u53d7\u9a97\uff0c\u56e0\u6b64\u76d8\u5929\u7f57\u548c\u8499\u5143\u5b50\u9042\u5b9a\u4e0b\u6bd2\u8ba1\uff0c\u4ee4\u5c0f\u864e\u5b50\u5403\u4e0a\u8ff7\u836f\uff0c\u5f53\u5730\u571f\u53f8\u7684 \u5973\u513f\u662f\u8499\u5143\u5b50\u7684\u5f92\u5f1f\uff0c\u8499\u5143\u5b50\u53c8\u53eb\u571f\u53f8\u62db\u8d58\u5c0f\u864e\u5b50\u4e3a\u5a7f\uff0c\u8fd9\u5c31\u662f\u4e8e\u627f\u73e0\u5728\u6d1e\u623f\u5185\u6240\u7aa5\u89c1\u7684\u79d8 \u5bc6\u4e86\u3002 \u3000\u3000\u4e14\u8bf4\u9ed1\u767d\u6469\u8bc3\u89c1\u963f\u8428\u739b\u5144\u5f1f\u5520\u53e8\u4e0d\u6b64\uff0c\u767d\u6469\u8bc3\u9996\u5148\u5fcd\u8010\u4e0d\u4f4f\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\u4e0d\u613f\u56de \u53bb\uff0c\u4f60\u8fd8\u5728\u8fd9\u91cc\u7f57\u5506\u4ec0\u4e48\uff1f\u5c31\u6b64\u56de\u8f6c\u6ce2\u65af\uff0c\u8fd8\u53ef\u4ee5\u4fdd\u5f97\u4f4f\u4f60\u56fd\u5e08\u7684\u8363\u534e\uff0c\u54fc\uff0c\u54fc\uff0c\u8981\u662f\u518d\u4e0d \u77e5\u8da3\uff0c\u54b1\u4eec\u5144\u5f1f\u53ef\u4e0d\u50cf\u4e0a\u6b21\u7684\u5ba2\u6c14\u4e86\u3002\u4e0a\u6b21\u4f60\u4eec\u53ea\u635f\u4e86\u4e00\u5e74\u529f\u529b\uff0c\u8bbe\u82e5\u591a\u635f\u51e0\u5e74\uff0c\u8bd5\u95ee\u4f60\u4eec \u7684\u56fd\u5e08\u5730\u4f4d\u5982\u4f55\u8fd8\u7ad9\u5f97\u4f4f\u811a\uff1f\u201d \u3000\u3000\u963f\u8428\u739b\u5144\u5f1f\u4e0a\u6b21\u53d7\u4e86\u7bad\u4f24\uff0c\u5f15\u4e3a\u5947\u803b\u5927\u8fb1\uff0c\u542c\u767d\u6469\u8bc3\u7684\u8bf4\u6cd5\uff0c\u7adf\u7136\u56e0\u4ed6\u4eec\u635f\u4e86\u4e00\u5e74\u529f \u529b\uff0c\u800c\u5c0f\u89d1\u4e86\u4ed6\u4eec\uff0c\u90fd\u7981\u4e0d\u4f4f\u52c3\u7136\u5927\u6012\uff0c\u53ea\u542c\u5f97\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u4e24\u5144\u5f1f\u540c\u65f6\u62d4\u51fa\u4e86\u4e00\u67c4\u6708\u7259 \u5f2f\u5200\u3002\u963f\u62c9\u4f2f\u5200\u6781\u662f\u6709\u540d\uff0c\u4e0d\u5728\u7f05\u5200\u502d\u5200\u4e4b\u4e0b\uff0c\u8fd9\u4e24\u67c4\u5200\u66f4\u662f\u5343\u9524\u767e\u70bc\u7684\u5b9d\u5200\uff0c\u4e00\u51fa\u5200\u9798\uff0c \u4fbf\u89c9\u51b7\u6c14\u68ee\u68ee\uff0c\u5200\u5149\u8000\u773c\u3002 \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u53cc\u6756\u4e00\u4f38\uff0c\u4f46\u542c\u5f97\u53ee\u5f53\u4e4b\u58f0\uff0c\u9707\u5f97\u4eba\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u53ea\u5728\u90a3\u4e00\u77ac\u4e4b\u95f4\uff0c\u4e24\u65b9\u5df2 \u786c\u62fc\u4e86\u5341\u4f59\u62db\uff0c\u9ed1\u6469\u8bc3\u5927\u53eb\u9053\uff1a\u201c\u597d\u4e00\u628a\u5b9d\u5200\uff0c\u7eff\u7389\u6756\u970d\u5730\u4e00\u626b\uff0c\u963f\u8428\u739b\u5df2\u5728\u5b9d\u6756\u4e0a\u8fde\u5288\u4e86 \u4e09\u5200\uff0c\u4e8e\u627f\u73e0\u66fe\u89c1\u8fc7\u65e5\u672c\u516b\u6bb5\u6b66\u58eb\u7684\u201c\u795e\u98ce\u5200\u6cd5\u201d\uff0c\u4f46\u89c9\u963f\u8428\u739b\u51fa\u624b\u4e4b\u5feb\uff0c\u66f4\u5728\u201c\u795e\u98ce\u5200 \u6cd5\u201d\u4e4b\u4e0a\u3002\u4e8e\u627f\u73e0\u8fd8\u672a\u77a7\u5f97\u6e05\u695a\uff0c\u963f\u8428\u739b\u5df2\u662f\u6012\u543c\u4e00\u58f0\uff0c\u95ea\u8eab\u75be\u9000\uff0c\u963f\u5408\u739b\u8ddf\u7740\u4e00\u5200\uff0c\u5374\u88ab \u767d\u6469\u8bc3\u7684\u767d\u7389\u6756\u8361\u5f00\uff0c\u4e24\u5144\u5f1f\u4e00\u9000\u590d\u8fdb\uff0c\u51fa\u624b\u8d8a\u6765\u8d8a\u5feb\uff0c\u4e0e\u5148\u524d\u5927\u4e0d\u76f8\u540c\uff0c\u53ea\u89c1\u5200\u5149\u98de\u821e\uff0c \u7830\u82e5\u7535\u5149\u75be\u95ea\uff0c\u5374\u4e0d\u95fb\u5175\u5668\u78b0\u51fb\u4e4b\u58f0\u3002 \u3000\u3000\u539f\u6765\u963f\u8428\u739b\u9002\u624d\u8fde\u5288\u4e09\u5200\uff0c\u53cc\u65b9\u5185\u529b\u649e\u51fb\uff0c\u9ed1\u6469\u8bc3\u7684\u7eff\u7389\u6756\u6beb\u65e0\u4f24\u635f\uff0c\u963f\u8428\u739b\u5200\u4e0a\u7684\u6708 \u7259\u5374\u90fd\u5df2\u6298\u65ad\u4e86\u3002\u963f\u8428\u739b\u5144\u5f1f\u4e0e\u9ed1\u767d\u6469\u8bc3\u76f8\u6597\u8fc7\u4f55\u6b62\u767e\u6b21\uff0c\u4ee5\u524d\u53cc\u65b9\u529f\u529b\u6089\u654c\uff0c\u6bcf\u6b21\u4ea4\u624b\uff0c \u90fd\u662f\u4e0d\u5206\u80dc\u8d1f\uff0c\u53cc\u65b9\u7684\u5175\u5668\u90fd\u6ca1\u6709\u4f24\u635f\uff0c\u800c\u4eca\u963f\u8428\u739b\u5144\u5f1f\u7684\u5143\u6c14\u5c1a\u672a\u5b8c\u5168\u6062\u590d\uff0c\u52a8\u529b\u7a0d\u900a\uff0c \u4fbf\u7acb\u89c1\u5403\u4e8f\u3002\u56e0\u6b64\u4e24\u5144\u5f1f\u518d\u4e0d\u6562\u4ee5\u5175\u5668\u786c\u62fc\uff0c\u53ea\u80fd\u4ed7\u7740\u8fc5\u75be\u7684\u5200\u6cd5\u548c\u9ed1\u767d\u6469\u8bc3\u6e38\u6597\u3002 \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u80dc\u5238\u5728\u64cd\uff0c\u5374\u662f\u597d\u6574\u4ee5\u6687\uff0c\u5e76\u4e0d\u548c\u654c\u4eba\u62a2\u653b\uff0c\u4e24\u5144\u5f1f\u7684\u5b9d\u6756\u5de6\u53f3\u76f8\u8fde\uff0c\u654c\u4eba\u7684 \u5200\u6cd5\u8d8a\u5feb\uff0c\u4ed6\u4eec\u7684\u6756\u6cd5\u5374\u8d8a\u6162\u3002\u597d\u50cf\u7b51\u8d77\u4e86\u4e00\u9053\u957f\u63d0\uff0c\u4efb\u5b83\u6ce2\u6d9b\u51b2\u51fb\uff0c\u5140\u7136\u4e0d\u4e3a\u6240\u52a8\u3002 \u3000\u3000\u4f17\u4eba\u54ea\u66fe\u89c1\u8fc7\u5982\u6b64\u6fc0\u6218\uff1f\u5c4f\u795e\u606f\u6c14\uff0c\u4f46\u89c1\u4e24\u9053\u767d\u5149\uff0c\u4fe8\u82e5\u7389\u9f99\u5929\u5a07\uff0c\u4e0e\u9ed1\u767d\u6469\u8bc3\u5b9d\u6756\u7684 \u7eff\u8272\u5149\u534e\u4e92\u76f8\u7ea0\u7ed3\uff0c\u6e10\u6e10\u7eff\u5149\u5927\u6da8\uff0c\u90a3\u4e24\u67c4\u6708\u7259\u5f2f\u5200\u6240\u53d1\u51fa\u7684\u767d\u5149\u6e10\u6e10\u51cf\u5f31\uff0c\u7ec8\u4e8e\u538b\u5230\u4e86\u963f \u8428\u739b\u5144\u5f1f\u7684\u5934\u9876\uff0c\u5c0f\u864e\u5b50\u5927\u559c\u53eb\u9053\uff1a\u201c\u6211\u7684\u5e08\u7236\u6253\u8d62\u5566\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u963f\u8428\u739b\u5144\u5f1f\u540c \u58f0\u66b4\u559d\uff0c\u8fde\u4e8e\u627f\u73e0\u4e5f\u770b\u4e0d\u6e05\u4ed6\u4eec\u7528\u7684\u662f\u4ec0\u4e48\u8eab\u6cd5\uff0c\u500f\u7136\u4e4b\u95f4\u5df2\u8131\u51fa\u7eff\u5149\u7684\u5305\u56f4\uff0c\u5012\u7eb5\u51fa\u4e00\u4e08 \u5f00\u5916\u3002\u963f\u8428\u739b\u53eb\u9053\uff1a\u201c\u4eca\u65e5\u8a93\u5fc5\u8981\u62a5\u8fd9\u4e00\u7bad\u4e4b\u4ec7\uff01\u201d\u624b\u638c\u4e00\u626c\uff0c\u4f17\u4eba\u773c\u775b\u4e00\u4eae\uff0c\u4f46\u89c1\u4e2a\u5706 \u7403\uff0c\u91d1\u5149\u95ea\u95ea\uff0c\u5e26\u7740\u545c\u545c\u7684\u602a\u5578\u58f0\uff0c\u5411\u7740\u9ed1\u6469\u8bc3\u75be\u88ad\uff0c\u9ed1\u6469\u67ef\u5927\u7b11\u9053\uff1a\u201c\u597d\u9614\u6c14\u7684\u6697\u5668\uff0c \u5582\uff0c\u4f60\u6709\u591a\u5c11\uff0c\u6211\u90fd\u6536\u8d2d\uff0c\u4f60\u8981\u591a\u5c11\u4ef7\u94b1\uff1f\u201d\u9ed1\u767d\u6469\u8bc3\u662f\u73e0\u5b9d\u5546\u4eba\u8eab\u4efd\uff0c\u8bf4\u8bdd\u4e0d\u79bb\u672c\u8272\u3002\u963f \u8428\u739b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53ea\u6015\u4f60\u6536\u4e70\u4e0d\u8d77\uff0c\u4e00\u626c\u624b\u53c8\u662f\u4e09\u9897\u91d1\u7403\uff0c\u90a3\u4e00\u8fb9\u963f\u5408\u739b\u4e5f\u662f\u7528\u8fd9\u79cd\u91d1\u7403\u6697 \u5668\u53bb\u5bf9\u4ed8\u767d\u6469\u8bc3\u3002\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u7a00\u5947\uff0c\u4e0d\u8fc7\u4e0e\u6211\u7684\u91d1\u82b1\u4e00\u6837\uff0c\u80fd\u591f\u6253\u7a74\u800c\u5df2\uff0c\u600e \u4f24\u5f97\u4e86\u9ed1\u767d\u6469\u8bc3\uff1f\u201d \u3000\u3000\u4f46\u89c1\u524d\u5934\u90a3\u4e00\u7ec4\u4e09\u9897\u91d1\u7403\uff0c\u88ab\u9ed1\u767d\u6469\u8bc3\u7684\u5b9d\u6756\u4e00\u8361\uff0c\u5411\u7740\u963f\u8428\u739b\u5144\u5f1f\u6fc0\u5c04\u98de\u56de\uff0c\u5374\u88ab\u4ed6 \u4eec\u7eed\u53d1\u7684\u91d1\u7403\u4e00\u649e\uff0c\u53c8\u98de\u8fc7\u53bb\uff0c\u5982\u662f\u6570\u6b21\uff0c\u963f\u8428\u739b\u5144\u5f1f\u8fde\u53d1\u4e09\u5341\u516d\u4e2a\u91d1\u7403\uff0c\u4e92\u76f8\u78b0\u649e\uff0c\u5927\u5385 \u4e0a\u91d1\u5149\u95ea\u95ea\uff0c\u6ee1\u662f\u91d1\u7403\u98de\u821e\uff0c\u6bcf\u4e2a\u91d1\u7403\u90fd\u662f\u9542\u7a7a\u4e86\u7684\uff0c\u8fce\u98ce\u53d1\u58f0\uff0c\u597d\u50cf\u4ec0\u4e48\u602a\u517d\u7684\u602a\u5578\uff0c\u60e8 \u5389\u4e4b\u6781\uff0c\u4f17\u4eba\u90fd\u89c9\u5fc3\u9b44\u52a8\u8361\uff0c\u7eb7\u7eb7\u6495\u88c2\u8863\u895f\uff0c\u5835\u585e\u8033\u6735\uff0c\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u79cd\u6697\u5668\u8fd8\u6709 \u52fe\u9b42\u6444\u9b44\u7684\u529f\u7528\uff0c\u4f46\u5185\u529f\u6709\u706b\u5019\u7684\u4eba\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u5c31\u88ab\u6270\u4e71\u5fc3\u795e\u3002\u201d\u518d\u4e00\u770b\u65f6\uff0c\u53ea\u89c1\u90a3\u4e09\u5341\u516d \u9897\u91d1\u73e0\uff0c\u98de\u53bb\u98de\u56de\uff0c\u7adf\u65e0\u4e00\u9897\u8dcc\u843d\u5730\u4e0a\uff0c\u6709\u65f6\u662f\u4e92\u76f8\u78b0\u51fb\uff0c\u6709\u65f6\u662f\u963f\u8428\u739b\u5144\u5f1f\u7528\u5f2f\u5200\u53bb\u62e8\u90a3 \u91d1\u7403\uff0c\u770b\u4e0d\u591a\u4e45\uff0c\u5df1\u770b\u51fa\u6bcf\u9897\u91d1\u7403\u90fd\u662f\u8ba4\u5b9a\u5bf9\u65b9\u4e00\u4e2a\u7a74\u9053\u88ad\u51fb\uff0c\u4e09\u5341\u516d\u9897\u91d1\u7403\uff0c\u7adf\u662f\u5206\u6253\u4eba \u8eab\u7684\u4e09\u5341\u516d\u9053\u5927\u7a74\uff0c\u4e8e\u627f\u73e0\u5341\u5206\u5947\u602a\uff0c\u518d\u770b\u4e9b\u65f6\uff0c\u624d\u770b\u51fa\u5176\u4e2d\u9053\u7406\uff0c\u539f\u6765\u6709\u4e9b\u91d1\u7403\u88ab\u9ed1\u767d\u6469 \u8bc3\u7684\u6756\u529b\u9707\u6b6a\uff0c\u963f\u8428\u739b\u5144\u5f1f\u5c31\u7acb\u523b\u7528\u5f2f\u5200\u5c06\u5b83\u62d4\u6b63\uff0c\u6240\u4ee5\u6bcf\u4e00\u9897\u91d1\u7403\u98de\u5411\u9ed1\u767d\u6469\u8bc3\u4e4b\u65f6\uff0c\u90fd \u662f\u5bf9\u51c6\u4ed6\u4eec\u7684\u7a74\u9053\u3002\u4e8e\u627f\u73e0\u8fd9\u624d\u5927\u60ca\u5931\u8272\uff0c\u60f3\u4e0d\u5230\u4e16\u95f4\u7adf\u6709\u5982\u65af\u795e\u5999\u7684\u624b\u6cd5\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u770b\uff01\u201d\u53ea\u89c1\u4e8e\u627f\u73e0\u770b\u5f97\u5982\u9189\u5982\u75f4\uff0c\u597d\u4f3c\u8fde\u4ed6\u7684\u53eb\u58f0\u90fd\u542c\u4e0d\u89c1\uff0c\u5c0f \u864e\u5b50\u8fde\u53eb\u4e09\u58f0\uff0c\u4e8e\u627f\u73e0\u624d\u7b54\u5e94\u9053\uff1a\u201c\u522b\u5435\uff0c\u522b\u5435\uff0c\u6211\u770b\u7740\u5462\uff01\u201d\u539f\u6765\u5728\u201c\u7384\u529f\u8981\u8bc0\u201d\u4e4b\u4e2d\u4e5f \u8f7d\u6709\u8bb2\u53d1\u6697\u5668\u7684\u4e0a\u4e58\u529f\u592b\uff0c\u5c06\u5fc3\u3001\u610f\u3001\u773c\u3001\u624b\u3001\u6b65\u4e94\u6cd5\u8bb2\u5f97\u5341\u5206\u8be6\u7ec6\uff0c\u4f46\u4e8e\u627f\u73e0\u672a\u66fe\u89c1\u4eba\u7528 \u8fc7\uff0c\u867d\u660e\u771f\u7406\uff0c\u5374\u8fd8\u8c08\u4e0d\u4e0a\u771f\u6b63\u9886\u609f\uff0c\u800c\u4eca\u4e00\u89c1\uff0c\u5fc3\u7a8d\u5927\u5f00\uff0c\u6b63\u5728\u9ed8\u60f3\u5982\u4f55\u5c06\u8fd9\u79cd\u624b\u6cd5\u8fd0\u7528 \u5230\u81ea\u5df1\u7684\u91d1\u82b1\u6697\u5668\u4e0a\uff0c\u91d1\u82b1\u82b1\u74e3\u950b\u5229\uff0c\u82e5\u7136\u4ed6\u80fd\u7ec3\u5230\u963f\u8428\u739b\u5144\u5f1f\u8fd9\u79cd\u529f\u592b\uff0c\u9664\u4e86\u6253\u7a74\uff0c\u8fd8\u53ef \u4f24\u4eba\uff0c\u90a3\u5b9a\u7136\u662f\u6bd4\u963f\u8428\u739b\u5144\u5f1f\u7684\u91d1\u7403\u66f4\u5389\u5bb3\u4e86\u3002\u4e8e\u627f\u73e0\u6b63\u5728\u7528\u795e\uff0c\u88ab\u5c0f\u864e\u4e8e\u6253\u65ad\u751a\u4e0d\u9ad8\u5174\uff0c \u5ffd\u89c1\u5c0f\u864e\u5b50\u5c0f\u5634\u4e00\u95f9\uff0c\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u7a00\u5947\uff0c\u6211\u5e08\u7236\u7684\u624b\u6cd5\u624d\u5947\u5999\u5462\uff0c\u4f60\u770b\uff0c\u4f60\u770b\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5b9a\u795e\u6ce8\u89c6\uff0c\u53ea\u89c1\u9ed1\u767d\u6469\u8bc3\u6756\u6cd5\u4e00\u53d8\uff0c\u9ed1\u6469\u8bc3\u53f3\u624b\u6267\u7eff\u7389\u6756\uff0c\u767d\u6469\u8bc3\u5de6\u624b\u6267\u767d\u7389 \u6756\uff0c\u4e24\u67c4\u5b9d\u6756\u90fd\u5728\u5934\u9876\u62a1\u5706\uff0c\u7eff\u5149\u767d\u5149\uff0c\u9996\u5c3e\u76f8\u8854\uff0c\u5f62\u6210\u4e86\u4e00\u9053\u4e24\u8272\u7684\u5149\u8f6e\uff0c\u4f46\u542c\u5f97\u53ee\u5f53\u4e4b \u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u963f\u8428\u739b\u5144\u5f1f\u6240\u53d1\u7684\u91d1\u7403\uff0c\u4e00\u8fdb\u5165\u5149\u8f6e\u4e4b\u4e2d\uff0c\u4fbf\u4f3c\u6ce5\u725b\u5165\u6d77\uff0c\u518d\u4e5f\u98de\u4e0d\u51fa\u6765\uff0c \u4e0d\u591a\u4e00\u4f1a\uff0c\u9ed1\u767d\u6469\u8bc3\u7684\u4e24\u67c4\u5b9d\u6756\u4e0a\uff0c\u90fd\u6302\u6ee1\u4e86\u4e00\u957f\u4e32\u7684\u91d1\u7403\uff0c\u5149\u8f6e\u4e2d\u91d1\u661f\u95ea\u95ea\uff0c\u5947\u8273\u65e0\u6bd4\uff01 \u539f\u6765\u521a\u624d\u963f\u8428\u739b\u5144\u5f1f\u6240\u53d1\u7684\u91d1\u7403\uff0c\u80fd\u591f\u98de\u53bb\u98de\u56de\uff0c\u56fa\u7136\u662f\u7531\u4e8e\u4ed6\u4eec\u5947\u5de7\u72ec\u7279\u7684\u6697\u5668\u624b\u6cd5\uff0c\u540c \u65f6\u4e5f\u662f\u7531\u4e8e\u9ed1\u767d\u6469\u8bc3\u5b9d\u6756\u7684\u53cd\u9707\u4e4b\u529b\uff0c\u800c\u4eca\u9ed1\u767d\u6469\u8bc3\u53cc\u6756\u5408\u56f4\uff0c\u5c31\u5982\u5f20\u8d77\u4e86\u4e00\u5f20\u5de8\u7f51\uff0c\u672c\u6765 \u53ef\u4ee5\u5c06\u91d1\u7403\u90fd\u62e6\u5728\u201c\u7f51\u201d\u5916\uff0c\u4f46\u9ed1\u767d\u6469\u8bc3\u5374\u6545\u610f\u653e\u5b83\u8fdb\u6765\uff0c\u9ed1\u767d\u6469\u8bc3\u7684\u5185\u529b\u8f83\u963f\u8428\u739b\u5144\u5f1f\u7a0d \u80dc\u4e00\u7b79\uff0c\u90a3\u4e9b\u91d1\u7403\u53c8\u90fd\u662f\u9542\u7a7a\u4e86\u7684\uff0c\u88ab\u9ed1\u767d\u6469\u8bc3\u5b9d\u6756\u6240\u53d1\u7684\u529b\u91cf\u4e00\u6324\uff0c\u90fd\u98de\u5230\u4e86\u6756\u4e0a\uff0c\u8ba9\u9ed1 \u767d\u6469\u8bc3\u81ea\u7136\u800c\u7136\u5730\u5c06\u91d1\u7403\u8d2f\u4e32\u8d77\u6765\uff01 \u3000\u3000\u4e8e\u627f\u73e0\u770b\u5f97\u5fc3\u795e\u5982\u9189\uff0c\u963f\u8428\u739b\u5144\u5f1f\u6240\u53d1\u7684\u6697\u5668\uff0c\u5df2\u662f\u5947\u5999\u60ca\u4eba\uff0c\u800c\u9ed1\u767d\u6469\u8bc3\u7834\u6697\u5668\u7684\u624b \u6cd5\uff0c\u66f4\u662f\u96be\u4ee5\u601d\u8bae\u3002\u4e8e\u627f\u73e0\u60f3\u9053\uff1a\u201c\u7eb5\u7136\u662f\u6e14\u7fc1\u6492\u7f51\uff0c\u4e5f\u6709\u6f0f\u7f51\u4e4b\u9c7c\uff0c\u8fd9\u624b\u6cd5\u662f\u600e\u4e48\u7ec3\u7684\uff1f \u4f55\u51b5\u4ed6\u4eec\u53c8\u8981\u8ba9\u4e00\u4e9b\u91d1\u7403\u8fdb\u6765\uff1f\u80fd\u4ee4\u654c\u4eba\u7684\u6697\u5668\u6709\u53bb\u65e0\u56de\uff0c\u4e00\u65e0\u6f0f\u7f51\uff0c\u8fd9\u771f\u662f\u96be\u4e0a\u52a0\u96be\u7684 \u4e86\u3002\u201d\u5ffd\u800c\u8054\u60f3\u5230\u81ea\u5df1\u5e08\u7236\u5e08\u6bcd\u53cc\u5251\u5408\u58c1\u7684\u5a01\u529b\uff0c\u60f3\u9053\uff1a\u201c\u6211\u5e08\u7236\u5e38\u8bf4\u53cc\u5251\u5408\u5815\u7684\u5947\u5999\u5a01 \u529b\uff0c\u5929\u4e0b\u65e0\u53cc\u3002\u90a3\u5b9a\u7136\u662f\u6bd4\u9ed1\u767d\u6469\u8bc3\u7684\u53cc\u6756\u5408\u56f4\u66f4\u4e3a\u5389\u5bb3\u4e86\u3002\u53ef\u60dc\u8fd9\u79cd\u5251\u6cd5\u5fc5\u987b\u4e24\u4eba\u540c\u4f7f\uff0c \u82e5\u662f\u4e00\u4eba\u53ef\u4ee5\u540c\u65f6\u4f7f\u51fa\u5e08\u7236\u5e08\u6bcd\u7684\u4e24\u79cd\u5251\u6cd5\uff0c\u518d\u5c06\u9ed1\u767d\u6469\u8bc3\u53cc\u6756\u5408\u56f4\u7684\u5999\u6cd5\u63ba\u5165\u5176\u4e2d\uff0c\u90a3\u4e48 \u5929\u4e0b\u4efb\u4f55\u5389\u5bb3\u7684\u6697\u5668\u4e5f\u90fd\u80fd\u7834\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u5728\u5f20\u4e39\u67ab\u95e8\u4e0b\u5341\u5e74\uff0c\u65e9\u5df2\u5c06\u90a3\u672c\u201c\u7384\u529f\u8981\u8bc0\u201d\u8bfb \u5f97\u70c2\u719f\uff0c\u770b\u4e86\u9ed1\u767d\u6469\u8bc3\u8fd9\u4e00\u573a\u6fc0\u6218\uff0c\u53ea\u89c9\u4e66\u4e0a\u8bb8\u591a\u7cbe\u5fae\u5965\u5999\u4e4b\u5904\uff0c\u5c24\u5176\u662f\u5bf9\u6697\u5668\u7684\u8fd0\u7528\u4e0e\u7834 \u89e3\u8fd9\u4e00\u7ae0\uff0c\u5e73\u65f6\u96be\u4ee5\u9886\u609f\u7684\uff0c\u73b0\u5728\u90fd\u4e00\u4e00\u8fce\u5203\u800c\u89e3\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u9ed1\u767d\u6469\u8bc3\u7eb5\u58f0\u5927\u7b11\uff0c\u9ed1\u6469\u8bc3\u53eb\u9053\uff1a\u201c\u8fd9\u573a\u4e70\u5356\u54b1\u4eec\u662f\u8d5a\u5b9a\u5566\uff01\u54c8\u54c8\uff01\u5929\u4e0b\u7adf\u7136\u6709 \u4e0d\u82b1\u4e00\u6587\u672c\u94b1\uff0c\u5c31\u8d5a\u5230\u4e86\u8fd9\u4e48\u591a\u9ec4\u91d1\uff0c\u8fd9\u6837\u7684\u4e70\u5356\u4e00\u751f\u4e2d\u4e5f\u96be\u9047\u89c1\u4e00\u6b21\uff01\u4f60\u4eec\u8fd8\u6709\u591a\u5c11\u91d1 \u7403\uff1f\u6709\u591a\u5c11\u6211\u5c31\u8981\u6536\u591a\u5c11\uff01\u201d\u963f\u8428\u739b\u5144\u5f1f\u7684\u4e09\u5341\u516d\u9897\u91d1\u7403\uff0c\u5df2\u53ea\u5269\u4e0b\u516d\u9897\uff0c\u8fd9\u65f6\u90fd\u6536\u56de\u624b \u4e2d\uff0c\u4e0d\u6562\u518d\u53d1\uff0c\u63d0\u7740\u6708\u7259\u5f2f\u5200\u6123\u5728\u5f53\u573a\u3002 \u3000\u3000\u76d8\u5929\u7f57\u77a7\u89c1\u52bf\u4e0d\u5bf9\uff0c\u5ffd\u5730\u91cc\u4e00\u58f0\u957f\u5578\uff0c\u8eab\u5f62\u75be\u5411\u5c0f\u864e\u5b50\u6251\u6765\uff0c\u963f\u8428\u739b\u5144\u5f1f\u4e5f\u540c\u65f6\u51fa\u624b\uff0c \u53cc\u5200\u76d8\u65cb\uff0c\u518d\u6251\u9ed1\u767d\u6469\u8bc3\uff0c\u76d8\u5929\u7f57\u6765\u5f97\u5feb\u6781\uff0c\u952f\u9f7f\u97ad\u626c\u7a7a\u4e00\u6325\uff0c\u7acb\u523b\u5377\u5230\u5c0f\u864e\u5b50\u8eab\u4e0a\uff0c\u4e8e\u627f \u73e0\u5c31\u5728\u5c0f\u864e\u5b50\u8eab\u8fb9\uff0c\u9976\u662f\u5979\u62d4\u5251\u5f97\u5feb\uff0c\u7acb\u523b\u6321\u5f00\uff0c\u4f46\u90a3\u97ad\u68a2\u7684\u952f\u9f7f\uff0c\u5df2\u628a\u5c0f\u864e\u5b50\u7684\u8863\u895f\u6495\u53bb \u4e86\u4e00\u5927\u7247\uff01 \u3000\u3000\u76d8\u5929\u7f57\u957f\u97ad\u4e00\u632f\uff0c\u4e00\u62db\u201c\u6bd2\u9f99\u51fa\u6d77\u201d\uff0c\u97ad\u68a2\u98a4\u60a0\u60a0\u5730\u6307\u5230\u4e86\u4e8e\u627f\u73e0\u80f8\u524d\uff0c\u4e8e\u627f\u73e0\u7528\u4e86\u4e00 \u62db\u201c\u7389\u5973\u6295\u68ad\u201d\uff0c\u97ad\u5251\u4e00\u4ea4\uff0c\u706b\u661f\u98de\u8d77\uff0c\u90a3\u6761\u952f\u9f7f\u97ad\u970d\u5730\u5377\u5230\uff0c\u53d8\u4e3a\u201c\u8001\u6811\u76d8\u6839\u201d\uff0c\u8fd9\u6761\u952f \u9f7f\u97ad\u653e\u5c3d\uff0c\u957f\u8fbe\u4e00\u4e08\u6709\u4f59\uff0c\u5c06\u4e8e\u627f\u73e0\u524d\u540e\u5de6\u53f3\u7684\u9053\u8def\u5168\u90e8\u5c01\u7740\uff0c\u97ad\u4e0a\u7684\u952f\u9f7f\uff0c\u770b\u770b\u5c31\u8981\u52fe\u4e0a \u4e8e\u627f\u73e0\u7684\u8863\u88f3\uff0c\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u53ea\u89c1\u4e00\u6761\u4eba\u5f71\u51cc\u7a7a\u98de\u8d77\uff0c\u500f\u5730\u9752\u5149\u56db\u6cfb\uff0c\u53ee\u53ee\u4e4b\u58f0\uff0c\u5b9b\u5982 \u7e41\u5f26\u6025\u7ba1\uff0c\u60a6\u8033\u975e\u5e38\uff0c\u76d8\u5929\u7f57\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u597d\u4fca\u7684\u5251\u6cd5\uff0c\u518d\u63a5\u4e00\u97ad\uff01\u201d\u539f\u6765\u5c31\u5728\u8fd9\u4e09\u62db\u4e4b \u5185\uff0c\u4e8e\u627f\u73e0\u7684\u5b9d\u5251\u5df2\u628a\u76d8\u5929\u7f57\u97ad\u4e0a\u7684\u952f\u9f7f\uff0c\u5168\u90e8\u524a\u65ad\u3002 \u3000\u3000\u8fd9\u4e09\u62db\u8fc5\u5982\u7535\u5149\u77f3\u706b\uff0c\u4e8e\u627f\u73e0\u867d\u7136\u4e00\u7834\u89e3\uff0c\u5df2\u662f\u4f7f\u5c3d\u5403\u5976\u4e4b\u529b\uff0c\u8981\u77e5\u76d8\u5929\u7f57\u7684\u6b66\u529f\u5b9e \u529b\uff0c\u6bd4\u4e4b\u9633\u5b97\u6d77\u6700\u5c11\u8981\u9ad8\u51fa\u4e00\u500d\uff0c\u4e8e\u627f\u73e0\u66f4\u662f\u8fdc\u975e\u5176\u654c\uff0c\u5979\u4e4b\u6240\u4ee5\u80fd\u591f\u524a\u65ad\u76d8\u5929\u7f57\u97ad\u4e0a\u7684\u952f \u9f7f\uff0c\u56fa\u7136\u662f\u4ed7\u7740\u7cbe\u5999\u7edd\u4f26\u7684\u201c\u767e\u53d8\u7384\u673a\u5251\u6cd5\u201d\uff0c\u53e6\u4e00\u534a\u5374\u662f\u5360\u4e86\u9752\u51a5\u5b9d\u5251\u7684\u4fbf\u5b9c\u3002 \u3000\u3000\u547c\u7684\u4e00\u58f0\uff0c\u76d8\u5929\u7f57\u7684\u7b2c\u56db\u97ad\u53c8\u5230\uff0c\u8fd9\u4e00\u97ad\u52bf\u6c89\u529b\u731b\uff0c\u957f\u97ad\u5728\u7a7a\u4e2d\u821e\u6210\u4e00\u4e2a\u5706\u5708\uff0c\u4e8e\u627f\u73e0 \u6321\u4e86\u4e09\u62db\uff0c\u864e\u53e3\u75bc\u75db\uff0c\u66f4\u517c\u4eba\u672a\u7740\u5730\uff0c\u6c14\u529b\u66f4\u96be\u4f7f\u7528\uff0c\u82e5\u7136\u786c\u63a5\uff0c\u53ea\u6050\u9752\u51a5\u5b9d\u5251\u4e5f\u8981\u7ed9\u9707\u5f97 \u8131\u624b\u98de\u53bb\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u521a\u521a\u4ece\u5730\u4e0a\u8dc3\u8d77\uff0c\u89c1\u4e86\u4e8e\u627f\u73e0\u9669\u72b6\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4e0d \u8981\u7740\u614c\u3001\u6211\u6765\u5e2e\u4f60\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5440\uff0c\u4f60\u600e\u4e48\u6210\uff1f\u201d\u5fc3\u5ff5\u65b9\u52a8\uff0c\u5c0f\u864e\u5b50\u5df2\u662f\u4e00\u62f3\u624d\u51fa\uff0c\u53ea \u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u51fb\uff0c\u5c0f\u864e\u5b50\u8eab\u5f62\u5f39\u8d77\uff0c\u76d8\u5929\u7f57\u7684\u97ad\u68a2\u5374\u4e5f\u7a0d\u7a0d\u6b6a\u8fc7\u4e00\u8fb9\uff0c\u4e8e\u627f\u73e0\u8d81\u52bf\u4e00\u62db \u201c\u4e58\u98ce\u8e51\u865a\u201d\uff0c\u633d\u4e2a\u5251\u82b1\u62a4\u7740\u524d\u80f8\uff0c\u98d8\u7136\u7740\u5730\u3002\u76d8\u5929\u7f57\u53cd\u624b\u4e00\u97ad\u3002\u97ad\u5934\u6307\u7740\u4e8e\u627f\u73e0\u7684\u201c\u9886\u9965 \u7a74\u201d\uff0c\u97ad\u68a2\u5374\u626b\u5411\u5c0f\u864e\u5b50\u7684\u65b9\u5411\uff0c\u5c0f\u864e\u5b50\u7684\u8f7b\u529f\u8fdc\u4e0d\u5982\u4e8e\u627f\u73e0\uff0c\u60ac\u5728\u534a\u7a7a\uff0c\u66f4\u96be\u5e94\u654c\uff0c\u82e5\u7136 \u843d\u4e0b\uff0c\u90a3\u5c82\u4e0d\u662f\u9001\u4e0a\u53bb\u6328\u8fd9\u4e00\u97ad\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5927\u4e3a\u7740\u6025\uff0c\u5ffd\u89c1\u7eff\u5149\u4e00\u95ea\uff0c\u76d8\u5929\u7f57\u7684\u952f\u9f7f\u97ad\u8361\u8fc7\u4e00\u8fb9\uff0c\u9ed1\u6469\u8bc3\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0f\u864e \u5b50\uff0c\u6210\u5440\uff01\u4f60\u8fd9\u4e00\u62db\u9f99\u62f3\u53ef\u4ee5\u51fa\u5e08\u4e86\u3002\u201d\u5c0f\u864e\u5b50\u88ab\u654c\u4eba\u53cd\u529b\u9707\u98de\uff0c\u5fc3\u4e2d\u6b63\u81ea\u60ed\u6127\uff0c\u8fd8\u4ee5\u4e3a\u5e08 \u7236\u53d6\u7b11\u81ea\u5df1\uff0c\u5c82\u77e5\u4ed6\u90a3\u4e00\u62f3\u6253\u5f97\u76d8\u5929\u7f57\u97ad\u68a2\u7a0d\u6b6a\uff0c\u5df2\u662f\u5927\u4e0d\u5bb9\u6613\uff0c\u9ed1\u6469\u8bc3\u4e43\u662f\u8bda\u5fc3\u5938\u5956\u4ed6\u7684 \u5f92\u5f1f\u3002 \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u53cc\u6756\u4e00\u5408\uff0c\u5c06\u76d8\u5929\u7f57\u4e0e\u963f\u8428\u739b\u5144\u5f1f\u90fd\u5708\u5728\u5f53\u4e2d\uff0c\u963f\u8428\u739b\u5144\u5f1f\u591a\u4e86\u4e00\u4e2a\u5e2e\u624b\uff0c\u582a \u582a\u4e0e\u9ed1\u767d\u6469\u8bc3\u6253\u6210\u5e73\u624b\u3002\u76d8\u5929\u7f57\u4e00\u58f0\u660e\u5578\uff0c\u4e24\u5eca\u5f1f\u5b50\u90fd\u62d4\u51fa\u5175\u5668\uff0c\u5c31\u60f3\u6765\u4e2a\u201c\u4ee5\u591a\u4e3a\u80dc\u201d\uff0c \u9ed1\u6469\u8bc3\u53eb\u9053\uff1a\u201c\u627f\u73e0\uff0c\u4f60\u4fdd\u62a4\u516c\u4e3b\u5148\u95ef\u51fa\u53bb\uff01\u201d\u6bb5\u6f84\u82cd\u9053\uff1a\u201c\u54b1\u4eec\u540c\u8d70\u4e86\u5427\u3002\u201d\u9ed1\u6469\u8bc3\u53eb \u9053\uff1a\u201c\u4e0d\u6210\uff0c\u6211\u975e\u628a\u8fd9\u53ae\u75db\u6253\u4e09\u62f3\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5821\u4e2d\u8bf8\u4eba\u7eb7\u7eb7\u6d8c\u4e0a\uff0c\u4e8e\u627f\u73e0\u63d0\u5251\u7acb\u5728\u6ce2\u65af\u516c\u4e3b\u8eab\u8fb9\uff0c\u53ea\u89c1\u5979\u795e\u8272\u81ea\u82e5\uff0c\u90a3\u80a1\u96cd\u5bb9\u534e\u8d35\u7684 \u6c14\u5ea6\u4e1d\u6beb\u4e0d\u6539\u3002 \u3000\u3000\u8fd9\u4f4d\u6ce2\u65af\u516c\u4e3b\u66fe\u8ddf\u6bb5\u6f84\u82cd\u5b66\u8fc7\u51e0\u5e74\u5251\u672f\uff0c\u5728\u5200\u5149\u5251\u5f71\u4e4b\u4e2d\u5e76\u65e0\u4ff1\u8272\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u7528\u6ce2\u65af \u8bdd\u5bf9\u6bb5\u6f84\u82cd\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u987e\u6211\uff0c\u4f60\u597d\u610f\u601d\u8ba9\u4e00\u4e2a\u5c0f\u5b69\u5b50\u72ec\u81ea\u7ed9\u4f60\u95ef\u9053\u5417\uff1f\u201d\u5c0f\u864e\u5b50\u65e9\u5df2\u62d4\u51fa \u7f05\u5200\uff0c\u5de6\u624b\u7528\u5bb6\u4f20\u7684\u4e94\u864e\u65ad\u95e8\u5200\u6cd5\uff0c\u53f3\u624b\u65bd\u5c55\u9ed1\u767d\u6469\u8bc3\u6240\u6388\u7684\u7f57\u6c49\u795e\u62f3\uff0c\u5c45\u7136\u52c7\u4e0d\u53ef\u5f53\uff0c\u6740 \u5f97\u53e4\u5821\u8bf8\u4eba\u4e0d\u6562\u8fd1\u8eab\uff0c\u4f46\u4ed6\u5230\u5e95\u4eba\u5c0f\u529b\u5f31\uff0c\u4e0d\u80fd\u6301\u4e45\uff0c\u76d8\u5929\u7f57\u6709\u51e0\u4e2a\u5f1f\u5b50\u6362\u4e86\u957f\u67aa\u5927\u621f\u4e4b\u7c7b \u7684\u957f\u5175\u5668\u6765\uff0c\u5c06\u4ed6\u622a\u7740\uff0c\u5c0f\u864e\u5b50\u5927\u6c57\u6dcb\u6f13\u3002\u662f\u52c7\u6218\u4e0d\u9000\u3002\u4e8e\u627f\u73e0\u9ad8\u5174\u4e4b\u6781\uff0c\u5fc3\u9053\uff1a\u201c\u5440\uff0c\u771f \u4e0d\u6127\u662f\u5f20\u98ce\u5e9c\u7684\u513f\u5b50\uff01\u201d \u3000\u3000\u6bb5\u6f84\u82cd\u5e94\u4e86\u4e00\u58f0\uff0c\u62d4\u5251\u51fa\u624b\uff0c\u53ea\u542c\u5f97\u4e00\u7247\u201c\u54ce\u54df\u201d\u4e4b\u58f0\uff0c\u7acb\u523b\u4fbf\u6709\u51e0\u4eba\u5012\u5730\uff0c\u76d8\u5929\u7f57\u6012 \u559d\u9053\uff1a\u201c\u6211\u597d\u5fc3\u62db\u5f85\u4f60\uff0c\u4f60\u600e\u4e48\u53cd\u4f24\u6211\u7684\u968f\u4ece\uff1f\u201d\u6bb5\u6f84\u82cd\u9053\uff1a\u201c\u591a\u8c22\u85e9\u738b\uff0c\u65e2\u662f\u597d\u5fc3\uff0c\u4e3a\u4f55 \u4e0d\u5c06\u968f\u4ece\u9063\u6563\uff1f\u963b\u6211\u4f55\u4e3a\uff1f\u62db\u5f85\u4e4b\u60c5\uff0c\u5f85\u6211\u5230\u4e86\u5317\u4eac\uff0c\u594f\u660e\u4f60\u4eec\u7684\u7687\u4e0a\u4fbf\u662f\u3002\u201d\u4ed6\u7684\u6c49\u8bed\u672c \u6765\u6709\u4e9b\u751f\u786c\uff0c\u4f3c\u5632\u4f3c\u8bbd\uff0c\u542c\u6765\u66f4\u89c9\u523a\u8033\uff0c\u76d8\u5929\u7f57\u6012\u4e0d\u53ef\u904f\uff0c\u4f46\u88ab\u9ed1\u767d\u6469\u8bc3\u4e24\u67c4\u5b9d\u6756\u56f4\u4f4f\uff0c\u54ea \u8131\u5f97\u51fa\u8eab\u53bb\u7167\u5e94\u5f1f\u5b50\uff1f \u3000\u3000\u6bb5\u6f84\u82cd\u5728\u6ce2\u65af\u56fd\u4e2d\u6709\u7b2c\u4e00\u5251\u5e08\u4e4b\u53f7\uff0c\u5b66\u517c\u4e2d\u897f\u4e4b\u957f\uff0c\u51fa\u624b\u679c\u7136\u4e0d\u540c\u51e1\u54cd\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u53c8 \u6709\u51e0\u4eba\u5012\u5730\uff0c\u4e8e\u627f\u73e0\u7ec6\u770b\u4ed6\u7684\u5251\u6cd5\uff0c\u53ea\u89c1\u4ed6\u51fa\u624b\u4fbf\u523a\uff0c\u5f88\u5c11\u7528\u6a2a\u524a\u3001\u659c\u5288\u7684\u5251\u5f0f\uff0c\u4e0e\u4e2d\u58eb\u5251 \u6cd5\u751a\u662f\u4e0d\u540c\uff0c\u5251\u5f0f\u53ea\u662f\u4e00\u5473\u523a\u6233\uff0c\u770b\u4f3c\u5355\u7eaf\uff0c\u5374\u662f\u6781\u4e3a\u5389\u5bb3\uff0c\u56e0\u4ed6\u4e0d\u7528\u6a2a\u524a\u659c\u5288\u7684\u5927\u5708\u5251 \u5f0f\uff0c\u6240\u4ee5\u51fa\u624b\u6781\u5feb\uff0c\u5251\u70b9\u5bc6\u96c6\u5982\u96e8\uff0c\u800c\u6240\u5241\u4e4b\u5904\uff0c\u53c8\u90fd\u662f\u5173\u8282\u7a74\u9053\u8981\u5bb3\uff0c\u8fd9\u5374\u53c8\u4e0e\u4e2d\u56fd\u7528\u5251 \u523a\u7a74\u4e4b\u6cd5\u76f8\u4f3c\u4e86\u3002\u4e8e\u627f\u73e0\u770b\u5f97\u51fa\u795e\uff0c\u5fc3\u9053\uff1a\u201c\u6b64\u4eba\u5251\u6cd5\u867d\u7136\u4e0d\u53ca\u6211\u5e08\u7236\u767e\u53d8\u7384\u673a\u5251\u6cd5\u7684\u795e \u5999\uff0c\u4f46\u4e5f\u6709\u5176\u72ec\u7279\u7684\u5730\u65b9\u3002\u53ef\u89c1\u6b66\u5b66\u4e4b\u9053\uff0c\u786e\u662f\u65e0\u7a77\u65e0\u5c3d\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6697\u5668\u7684\u545c\u545c\u602a\u5578\u4e4b\u58f0\uff0c\u539f\u6765\u662f\u76d8\u5927\u7f57\u7684\u5e08\u5f1f\u8499\u5143\u5b50\u53d1\u51fa\u5957\u5728\u81c2\u4e0a\u7684\u94f6\u73af\uff0c\u4ed6\u521a\u624d \u88ab\u9ed1\u6469\u8bc3\u4e00\u62f3\u6253\u5012\uff0c\u65ad\u4e86\u808b\u9aa8\uff0c\u76f4\u5230\u73b0\u5728\u624d\u6323\u624e\u7740\u722c\u8d77\u6765\uff0c\u4ed6\u867d\u7136\u4e0d\u80fd\u8d70\u5251\uff0c\u53d1\u6697\u5668\u7684\u529f\u592b \u8fd8\u5728\uff0c\u8fd9\u4e00\u4e0b\u53cc\u81c2\u4e00\u6296\uff0c\u516d\u73af\u9f50\u6253\uff0c\u5373\u7b97\u662f\u5584\u907f\u6697\u5668\u7684\u4eba\u4ea6\u4e0d\u5bb9\u6613\u62db\u67b6\u3002 \u3000\u3000\u6bb5\u6f84\u82cd\u5251\u5c16\u75be\u70b9\uff0c\u5374\u4e0d\u6599\u4e00\u78b0\u94f6\u73af\uff0c\u7acb\u523b\u659c\u98de\uff0c\u542c\u90a3\u602a\u5578\u4e4b\u58f0\uff0c\u7adf\u662f\u4ece\u5934\u9876\u98de\u8fc7\uff0c\u76f4\u53d6 \u6ce2\u65af\u516c\u4e3b\uff0c\u6bb5\u6f84\u82cd\u5927\u5403\u4e00\u60ca\uff0c\u56de\u8eab\u6551\u65f6\uff0c\u53e6\u5916\u4e09\u4e2a\u94f6\u73af\u5df2\u5411\u4ed6\u54bd\u5589\u524d\u5fc3\u540e\u5fc3\u4e09\u5904\u8981\u5bb3\u98de\u5230\u3002 \u6bb5\u6f84\u82cd\u65b9\u81ea\u53eb\u5f97\u4e00\u58f0\uff1a\u201c\u82e6\u4e5f\uff01\u201d\u9aa4\u89c1\u91d1\u5149\u8fde\u95ea\uff0c\u516d\u679a\u94f6\u73af\u5c3d\u884c\u843d\u5730\uff0c\u539f\u6765\u662f\u4e8e\u627f\u73e0\u5b66\u4e86\u963f \u8428\u739b\u5144\u5f1f\u7684\u6697\u5668\u624b\u6cd5\uff0c\u98de\u51fa\u91d1\u82b1\uff0c\u4e00\u4e3e\u4fbf\u5c06\u94f6\u73af\u6253\u843d\u4e86\u3002\u4e8e\u627f\u73e0\u6253\u5f97\u5174\u8d77\uff0c\u7d22\u6027\u628a\u91d1\u82b1\u90fd\u53d1 \u51fa\u6765\uff0c\u5979\u56ca\u4e2d\u6709\u4e03\u5341\u4e8c\u6735\u91d1\u82b1\uff0c\u5821\u4e2d\u56f4\u653b\u7684\u4e0d\u8fc7\u56db\u4e94\u5341\u4eba\uff0c\u9664\u4e86\u88ab\u5c0f\u864e\u5b50\u3001\u6bb5\u6f84\u82cd\u51fb\u5012\u4e4b \u5916\uff0c\u4e0d\u5230\u4e09\u5341\u4eba\uff0c\u5979\u7684\u91d1\u82b1\u672a\u53d1\u5230\u4e00\u534a\uff0c\u5df2\u662f\u5c06\u8bf8\u4eba\u5c3d\u6570\u51fb\u5012\uff01 \u3000\u3000\u4e8e\u627f\u73e0\u7ed5\u573a\u75be\u8d70\uff0c\u5c06\u91d1\u82b1\u4e00\u6536\u56de\u3002\u573a\u4e2d\u9ed1\u767d\u6469\u8bc3\u6b63\u4e0e\u963f\u8428\u739b\u5144\u5f1f\u9ad8\u547c\u9163\u6597\uff0c\u7eff\u5149\u3001\u767d \u5149\u3001\u91d1\u5149\u7ea0\u7ed3\u6210\u4e00\u7247\u5149\u5e55\u3002 \u3000\u3000\u770b\u8fd9\u60c5\u5f62\u3002\u4e0d\u77e5\u8981\u6253\u5230\u51e0\u65f6\u3002\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u9ed1\u767d\u4e24\u4f4d\u524d\u8f88\uff0c\u8d70\u5427\uff01\u201d\u9ed1\u767d\u6469\u8bc3\u54c8\u54c8\u5927\u7b11 \u9053\uff1a\u201c\u68cb\u9022\u5bf9\u624b\uff0c\u4e00\u751f\u4e2d\u4e5f\u96be\u9047\u4e00\u6b21\uff0c\u8fd9\u573a\u67b6\u4f60\u53ef\u5f97\u8ba9\u6211\u75db\u75db\u5feb\u5feb\u5927\u6253\u4e00\u573a\u3002\u201d\u8bf4\u8bdd\u58f0\u4e2d\uff0c \u53cc\u6756\u4e00\u5408\uff0c\u54e8\u7684\u4e00\u58f0\uff0c\u628a\u963f\u8428\u739b\u7684\u6708\u7259\u5f2f\u5200\u9707\u4e0a\u534a\u7a7a\uff0c\u963f\u8428\u739b\u624b\u6cd5\u5feb\u6781\uff0c\u767d\u6469\u8bc3\u7b2c\u4e8c\u62db\u672a \u5230\uff0c\u4ed6\u53c8\u5df2\u5c06\u5200\u6309\u5728\u624b\u4e2d\uff0c\u4e0e\u5144\u5f1f\u5e76\u80a9\u4e00\u7ad9\uff0c\u53cc\u5200\u5de6\u65cb\u53f3\u8f6c\uff0c\u6e38\u6597\u4e4b\u4e2d\uff0c\u4e5f\u4e0d\u65f6\u53cd\u51fb\uff0c\u76d8\u5929 \u7f57\u529f\u529b\u867d\u7136\u7a0d\u5f31\uff0c\u4f46\u5728\u963f\u8428\u739b\u5144\u5f1f\u53cc\u5200\u63a9\u62a4\u4e4b\u4e0b\uff0c\u90a3\u6761\u952f\u9f7f\u97ad\u4e5f\u662f\u75be\u8fdb\u75be\u9000\uff0c\u77eb\u82e5\u6e38\u9f99\uff0c\u4f46 \u89c1\u5404\u8272\u5149\u534e\uff0c\u4e92\u76f8\u7ea0\u7ed3\uff0c\u5ffd\u805a\u5ffd\u6563\uff0c\u8fde\u4e8e\u627f\u73e0\u4e5f\u51e0\u4e4e\u5206\u8fa8\u4e0d\u51fa\u5176\u4e2d\u62db\u6570\u3002\u4e8e\u627f\u73e0\u771f\u820d\u4e0d\u5f97\u4e0d \u770b\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u8fd9\u4e94\u5927\u9ad8\u624b\u62fc\u6597\uff0c\u81ea\u5df1\u4fbf\u662f\u8981\u63d2\u624b\u4e5f\u63d2\u4e0d\u8fdb\u53bb\u3002\u5929\u8272\u5df2\u5c06\u62c2\u6653\uff0c\u82e5\u7136\u4e0a\u53f8 \u6d3e\u4eba\u9053\u6765\uff0c\u81ea\u5df1\u867d\u7136\u4e0d\u6015\uff0c\u4f34\u7740\u6ce2\u65af\u516c\u4e3b\uff0c\u7ec8\u4e8e\u9ebb\u70e6\uff0c\u4fbf\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6211\u4eec\u5728\u5357\u9762\u7684\u5c71\u8c37 \u7b49\u4f60\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u62d6\u7740\u6ce2\u65af\u516c\u4e3b\u8d70\u51fa\u57ce\u5821\uff0c\u53ea\u89c1\u6bb5\u6f84\u82cd\u5df2\u9a91\u5728\u4e00\u5339\u67a3\u7ea2\u8272\u7684\u9a6c\u80cc\u4e0a\uff0c\u53e6\u5916\u8fd8\u6709\u4e00\u5339 \u540c\u6837\u8272\u6cfd\u7684\u9a6c\uff0c\u6bb5\u6f84\u82cd\u9053\uff1a\u201c\u6211\u548c\u5c0f\u864e\u5b50\u4e58\u8fd9\u5339\u9a6c\uff0c\u4f60\u4fdd\u62a4\u516c\u4e3b\u5750\u90a3\u5339\u5427\uff0c\u8fd9\u4e24\u5339\u9a6c\u90fd\u662f\u6ce2 \u65af\u540d\u9a6c\uff0c\u5728\u5c71\u8def\u5954\u9a70\uff0c\u5982\u5c65\u5e73\u5730\uff0c\u4e0d\u4e00\u5230\u4fbf\u5230\u4e86\u5357\u9762\u7684\u5c71\u8c37\uff0c\u6bb5\u6f84\u82cd\u8df3\u4e0b\u9a6c\u80cc\uff0c\u7b11\u7740\u5bf9\u5c0f\u864e \u5b50\u9053\uff1a\u201c\u8fd9\u4e24\u5339\u9a6c\u5982\u4f55\uff1f\u4f60\u82e5\u6b22\u559c\uff0c\u5c06\u6765\u6211\u9001\u7ed9\u4f60\u4eec\u3002\u201d\u4e8e\u627f\u73e0\u5fae\u5fae\u4e00\u7b11\uff0c\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u8fd9 \u4e24\u5339\u9a6c\u786e\u662f\u4e0d\u9519\uff0c\u4f46\u82e5\u8981\u6bd4\u8d77\u6211\u59d0\u59d0\u7684\u90a3\u5339\u5b9d\u9a6c\uff0c\u90a3\u8fd8\u76f8\u5dee\u592a\u8fdc\u3002\u201d\u6bb5\u6f84\u82cd\u610f\u6b8a\u4e0d\u4fe1\uff0c\u9053\uff1a \u201c\u662f\u4e48\uff1f\u201d\u5ffd\u542c\u5f97\u4e8e\u627f\u73e0\u64ae\u5507\u4e00\u5578\uff0c\u6e05\u8d8a\u4e4b\u6781\uff0c\u58f0\u9707\u6797\u8c37\uff0c\u6bb5\u6f84\u82cd\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u5bb6 \u8001\u8f88\uff0c\u5386\u4ee3\u76f8\u4f20\uff0c\u8bf4\u662f\u4e2d\u56fd\u6b66\u529f\u5982\u4f55\u5982\u4f55\u795e\u5999\uff0c\u679c\u7136\u4e0d\u662f\u8a00\u8fc7\u5176\u5b9e\uff0c\u8fde\u8fd9\u4f4d\u5c0f\u59d1\u5a18\u4e5f\u6709\u8fd9\u6837 \u597d\u7684\u5185\u529f\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u9a6c\u58f0\u957f\u5636\uff0c\u4f46\u89c1\u66d9\u5149\u4e4b\u4e2d\uff0c\u4e00\u5339\u767d\u9a6c\u98de\u5954\u800c\u6765\uff0c\u75be\u5982\u63a3\u7535\uff0c\u500f\u5730\u8df3\u8fc7\u4e00\u9053\u4e24\u4e08\u6765 \u5bbd\u7684\u5c71\u6d1e\uff0c\u6765\u5230\u9762\u524d\uff0c\u539f\u6765\u662f\u90a3\u5339\u7167\u591c\u72ee\u5b50\u9a6c\uff0c\u542c\u5f97\u4e3b\u4eba\u547c\u5524\uff0c\u7acb\u5373\u8d76\u5230\u3002\u6bb5\u6f84\u82cd\u53f9\u9053\uff1a \u201c\u6b27\u6d32\u4eba\u90fd\u8bf4\u6ce2\u65af\u591a\u5b9d\uff0c\u6211\u8bf4\u54b1\u4eec\u4e2d\u56fd\uff0c\u624d\u662f\u7269\u534e\u5929\u5b9d\uff0c\u4eba\u6770\u5730\u7075\uff0c\u8fde\u9a6c\u513f\u4e5f\u8fd9\u6837\u795e\u9a8f\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u76c8\u76c8\u4e00\u7b11\uff0c\u5c06\u6ce2\u65af\u516c\u4e3b\u6276\u4e0b\u9a6c\u80cc\u3002\u6ce2\u65af\u516c\u4e3b\u63e1\u7740\u4e8e\u627f\u73e0\u7684\u624b\u9053\uff1a\u201c\u8c22\u8c22\uff01\u201d\u5979\u8ddf \u6bb5\u6f84\u82cd\u5b66\u4f1a\u51e0\u53e5\u6c49\u8bed\uff0c\u8fd9\u4e24\u4e2a\u5b57\u8bf4\u5f97\u5f88\u751f\u786c\uff0c\u4f46\u5374\u975e\u5e38\u52a8\u542c\u3002\u5979\u548c\u4e8e\u627f\u73e0\u4e00\u89c1\u6295\u7f18\uff0c\u5c31\u7528\u5979 \u6240\u6653\u5f97\u7684\u51e0\u53e5\u6c49\u8bed\uff0c\u4e00\u9762\u6bd4\u7740\u624b\u52bf\uff0c\u548c\u4e8e\u627f\u73e0\u8c08\u8bdd\uff0c\u4e8e\u627f\u73e0\u95ee\u5979\u4e3a\u4f55\u5230\u4e2d\u56fd\u6765\uff0c\u5979\u8bf4\u4e0d\u6e05 \u695a\uff0c\u4e0d\u65f6\u53eb\u6bb5\u6f84\u82cd\u63d2\u8fdb\u6765\u89e3\u91ca\u3002\u6ce2\u65af\u5973\u5b50\u7684\u4e60\u6c14\uff0c\u4ee5\u6709\u60c5\u90ce\u631a\u7231\u4e3a\u9a84\u50b2\uff0c\u6d25\u6d25\u4e50\u9053\uff0c\u6beb\u65e0\u754f \u4ff1\u3002\u4e8e\u627f\u73e0\u597d\u4e0d\u5bb9\u6613\u542c\u61c2\u4e86\u4ed6\u4eec\u7684\u8bdd\uff0c\u89c1\u4ed6\u4eec\u76f8\u504e\u76f8\u4f9d\uff0c\u4f5c\u51fa\u5404\u79cd\u624b\u52bf\u6765\u6bd4\u55bb\u89e3\u8bf4\uff0c\u521d\u65f6\u8fd8 \u89c9\u5f97\u597d\u7b11\uff0c\u6e10\u89c9\u5fc3\u9189\u795e\u9a70\uff0c\u9661\u7136\u60f3\u8d77\u81ea\u5df1\u7684\u906d\u9047\uff0c\u5fc3\u4e2d\u5ffd\u751f\u51fa\u65e0\u9650\u611f\u6168\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u6beb\u65e0\u5174\u8da3\uff0c\u8df3\u6765\u8df3\u53bb\uff0c\u8dd1\u5230\u5c71\u8c37\u9065\u671b\uff0c\u53eb\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u6211\u7684\u4e24\u4f4d\u5e08\u7236\u6765\u5566\uff01\u4f60 \u77a7\uff0c\u4ed6\u4eec\u4e50\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u4e00\u5b9a\u662f\u6253\u67b6\u6253\u8d62\u4e86\u3002\u201d \u3000\u3000\u53ea\u89c1\u9ed1\u767d\u6469\u8bc3\u7b56\u9a6c\u5954\u6765\uff0c\u8fdc\u8fdc\u5730\u5c31\u626c\u97ad\u5927\u7b11\uff0c\u4e8e\u627f\u73e0\u4e0e\u5c0f\u864e\u5b50\u62a2\u4e0a\u53bb\u8fce\u63a5\uff0c\u9ed1\u767d\u6469\u8bc3\u8df3 \u4e0b\u9a6c\u80cc\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u573a\u6253\u5f97\u771f\u75db\u5feb\uff01\u6ca1\u6253\u8fd9\u6837\u5bf9\u624b\u7684\u67b6\uff0c\u5df2\u6709\u5341\u591a\u5e74\u5566\uff01\u201d\u5c0f\u864e\u5b50\u7709 \u98de\u8272\u821e\uff0c\u9053\uff1a\u201c\u8bf4\u6765\u542c\u542c\u3002\u201d\u9ed1\u6469\u8bc3\u9762\u5411\u4e8e\u627f\u73e0\u8bf4\u9053\uff1a\u201c\u5341\u591a\u5e74\u524d\uff0c\u6211\u4e24\u5144\u5f1f\u66fe\u4e0e\u4f60\u5e08\u7236\u5e08 \u6bcd\u5927\u6253\u4e00\u573a\uff0c\u5f53\u65f6\u662f\u6211\u4eec\u8f93\u4e86\uff0c\u8f93\u5f97\u5fc3\u670d\u53e3\u670d\uff1b\u4eca\u65e5\u8fd9\u4e00\u573a\u5927\u6253\uff0c\u53ef\u662f\u6211\u4eec\u8d62\u4e86\uff0c\u963f\u8428\u739b\u5144 \u5f1f\u4e5f\u8f93\u5f97\u5fc3\u670d\u53e3\u670d\uff01\u201d\u767d\u6469\u8bc3\u9053\uff1a\u201c\u8fd9\u4e24\u5144\u5f1f\u5012\u662f\u503c\u5f97\u4ea4\u4e00\u4ea4\u7684\u670b\u53cb\uff0c\u53ef\u60dc\u4ed6\u4eec\u6ca1\u6709\u4f60\u5e08\u7236 \u7684\u5ea6\u91cf\uff0c\u4e00\u8f93\u4e4b\u540e\uff0c\u7acb\u523b\u53d1\u8a93\u56de\u8f6c\u6ce2\u65af\uff0c\u518d\u4e5f\u4e0d\u7406\u95f2\u4e8b\u5566\uff01\u201d\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u6700\u75db\u5feb\u7684\u662f\u76d8\u5929 \u7f57\u90a3\u53ae\u5403\u4e86\u6211\u7684\u4e00\u6756\uff0c\u628a\u4ed6\u7684\u80eb\u9aa8\u4e5f\u6253\u65ad\u4e86\uff0c\u5c0f\u864e\u5b50\uff0c\u4e5f\u53ef\u4ee5\u51fa\u53e3\u6076\u6c14\u5566\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u542c\u8bf4\u76d8\u5929\u7f57\u548c\u9633\u5b97\u6d77\u90fd\u662f\u8d64\u971e\u9053\u4eba\u7684\u95e8\u4e0b\u3002\u201d\u9ed1\u767d\u6469\u8bc3\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u8d64\u971e\u9053\u4eba\u53c8\u600e\u4e48 \u6837\uff1f\u96be\u9053\u6211\u548c\u4f60\u7684\u5e08\u7236\u8fd8\u80fd\u5bb3\u6015\u4ed6\u4eec\uff01\u54c8\uff0c\u5c0f\u864e\u5b50\uff0c\u4f60\u600e\u4e48\u4e0d\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u6211\u6709\u70b9\u5934\u6655\u3002\u201d\u9ed1\u6469\u8bc3\u4e00\u624b\u6293\u7740\u4ed6\u7684\u8109\u95e8\uff0c\u542c\u4ed6\u8109\u606f\uff0c\u9053\uff1a\u201c\u4e0d\u5bf9\uff01\u201d\u4e8e \u627f\u73e0\u9053\uff1a\u201c\u4ed6\u5403\u4e86\u522b\u4eba\u7684\u8ff7\u9b42\u836f\uff0c\u540e\u6765\u53c8\u7ed9\u571f\u53f8\u7684\u5973\u513f\u653e\u4e86\u86ca\u3002\u201d\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u8ff7\u9b42\u836f\u5df2\u7ecf \u89e3\u5566\uff0c\u653e\u86ca\u5374\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u542c\u8bf4\u8fd9\u662f\u82d7\u4eba\u5c06\u5404\u79cd\u6bd2\u86ca\u9972\u517b\u5728\u4e00\u4e2a\u76c6\u5b50\u91cc\uff0c\u8ba9 \u5b83\u4eec\u4e92\u76f8\u541e\u98df\uff0c\u6700\u540e\u53ea\u5269\u4e0b\u4e00\u79cd\u6bd2\u86ca\uff0c\u5c31\u5c06\u8fd9\u6bd2\u86ca\u7814\u4e3a\u7c89\u672a\uff0c\u70bc\u6210\u6bd2\u836f\uff0c\u653e\u5728\u8336\u6c34\u6216\u83dc\u996d\u4e4b \u4e2d\uff0c\u7ed9\u4eba\u670d\u4e0b\uff0c\u5230\u4e00\u5b9a\u671f\u9650\uff0c\u6216\u662f\u767e\u65e5\uff0c\u6216\u662f\u4e00\u5e74\uff0c\u4fbf\u8981\u53d1\u4f5c\u3002\u975e\u5f97\u653e\u86ca\u4e4b\u4eba\u7684\u89e3\u836f\u4e0d \u53ef\u3002\u201d\u767d\u6469\u8bc3\u6012\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u54b1\u4eec\u4fbf\u56de\u53bb\u5c06\u90a3\u571f\u53f8\u7684\u5bb6\u6363\u4e2a\u7a00\u70c2\uff0c\u903c\u90a3\u5996\u5973\u62ff\u51fa\u89e3\u836f \u6765\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u4e0d\uff0c\u5979\u4e0d\u662f\u5996\u5973\uff0c\u6211\u90a3\u5929\u665a\u4e0a\u7ed9\u76d8\u5929\u7f57\u548c\u8499\u5143\u5b50\u6253\u4f24\uff0c\u75c5\u4e86\u534a\u4e2a\u6708\uff0c\u8fd8 \u5168\u662f\u9760\u5979\u7167\u6599\u5462\u3002\u201d\u4e8e\u627f\u73e0\u522e\u8138\u7f9e\u4ed6\u9053\uff1a\u201c\u5c0f\u864e\u5b50\u633a\u6709\u826f\u5fc3\uff0c\u75bc\u7740\u4ed6\u7684\u5ab3\u5987\u513f\u5462\u3002\u201d\u5c0f\u864e\u5b50 \u53eb\u9053\uff1a\u201c\u8c01\u8bf4\u5979\u662f\u6211\u7684\u5ab3\u5987\u513f\uff1f\u54b1\u4eec\u4e0d\u662f\u65e9\u5c31\u8bf4\u5f00\u4e86\u5417\uff1f\u201d\u9ed1\u6469\u8bc3\u5947\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de \u4e8b\uff1f\u201d\u4e8e\u627f\u73e0\u5c06\u5c0f\u864e\u5b50\u88ab\u9a97\u4f5c\u65b0\u90ce\u7684\u4e8b\u60c5\u8bf4\u4e86\uff0c\u8bf4\u5230\u4ed6\u6d1e\u623f\u4e4b\u591c\u7684\u5c34\u5c2c\u60c5\u72b6\uff0c\u9ed1\u767d\u6469\u8bc3\u542c \u4e86\uff0c\u4e0d\u7981\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u9ed1\u6469\u8bc3\u5ffd\u5730\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u82e5\u4f9d\u6211\u4ee5\u524d\u7684\u813e\u6c14\uff0c\u6211\u4e5f\u51c6\u4f1a\u5c06\u90a3\u571f\u53f8\u7684\u5bb6\u6363\u4e2a\u70ef\u70c2\uff0c\u4f46\u81ea\u4ece \u4e0e\u4f60\u7684\u5e08\u7236\u4ea4\u4e86\u670b\u53cb\uff0c\u6211\u8fd9\u9c81\u83bd\u7684\u813e\u6c14\u5df2\u6539\u4e86\u8bb8\u591a\u3002\u542c\u4f60\u6240\u8bf4\u770b\u6765\uff0c\u90a3\u571f\u53f8\u7684\u5973\u513f\uff0c\u5176\u5b9e\u4e5f \u662f\u7ed9\u76d8\u5929\u7f57\u5229\u7528\u7684\u5080\u5121\uff0c\u54b1\u4eec\u4f55\u82e6\u4e0e\u5979\u4e3a\u96be\uff1f\u6211\u5c31\u4e0d\u4fe1\u5929\u4e0b\u6709\u4e0d\u80fd\u89e3\u7684\u6bd2\u836f\u3002\u201d\u9ed1\u767d\u6469\u8bc3\u8db3 \u8ff9\u8e0f\u904d\u5370\u5ea6\u3001\u6ce2\u65af\u3001\u4e2d\u56fd\u7b49\u4e1c\u65b9\u53e4\u56fd\uff0c\u4e1c\u65b9\u5404\u56fd\u7684\u6c11\u95f4\u504f\u65b9\u6700\u591a\uff0c\u9ed1\u6469\u8bc3\u5c24\u5176\u5230\u5904\u7559\u5fc3\uff0c\u4ec0 \u4e48\u7a00\u5947\u53e4\u602a\u7684\u75c5\u75c7\uff0c\u4ed6\u90e8\u61c2\u5f97\u4e00\u4e9b\u3002\u5f53\u4e0b\u53eb\u5c0f\u864e\u5b50\u76d8\u819d\u9759\u5750\uff0c\u518d\u66ff\u4ed6\u8bca\u89c6\uff0c\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9 \u6bd2\u836f\u679c\u7136\u5389\u5bb3\uff0c\u4f46\u5374\u96be\u4e0d\u5012\u4e60\u8fc7\u745c\u73c8\u529f\u592b\u7684\u4eba\u3002\u627f\u73e0\uff0c\u4f60\u548c\u516c\u4e3b\u3001\u9a78\u9a6c\u5148\u8d70\u4e00\u7a0b\uff0c\u5f85\u6211\u4eec\u7ed9 \u5c0f\u864e\u5b50\u6d88\u86ca\u3002\u201d\u4e8e\u627f\u73e0\u7b49\u4f9d\u8a00\u8d70\u4e86\uff0c\u9ed1\u767d\u6469\u8bc3\u7acb\u523b\u7ed9\u5c0f\u864e\u5b50\u63a8\u6469\u3002 \u3000\u3000\u4f46\u89c9\u4e00\u80a1\u70ed\u529b\u4ece\u9ed1\u767d\u6469\u8bc3\u638c\u5fc3\u4f20\u5165\u4f53\u5185\uff0c\u5c0f\u864e\u5b50\u70ed\u5f97\u96be\u53d7\uff0c\u547c\u5438\u6025\u901f\u3002\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u6f5c \u5fc3\u5185\u865a\uff0c\u7531\u865a\u751f\u660e\u3002\u201d\u8fd9\u662f\u745c\u73c8\u672f\u4e2d\u8c03\u606f\u5410\u7eb3\u7684\u4e24\u53e5\u53e3\u8bc0\uff0c\u5c0f\u864e\u5b50\u4f9d\u7740\u6240\u6559\uff0c\u5c4f\u795e\u9759\u6c14\uff0c\u597d \u50cf\u65e5\u5e38\u505a\u529f\u8bfe\u4e00\u6837\uff0c\u5c06\u547c\u5438\u653e\u6162\uff0c\u521d\u65f6\u5341\u5206\u96be\u53d7\uff0c\u6e10\u6e10\u4fbf\u89c9\u4f53\u5185\u771f\u6c14\u5145\u6c9b\uff0c\u6c14\u673a\u6d3b\u6cfc\uff0c\u8fc7\u4e86 \u4e00\u4f1a\uff0c\u4f3c\u89c9\u809a\u4e2d\u6709\u7269\u8815\u8815\u800c\u52a8\uff0c\u8179\u5982\u96f7\u9e23\uff0c\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u6210\u5566\uff01\u201d\u8ba9\u5c0f\u864e\u5b50\u5230\u50fb\u9759\u5904\u5927\u6cfb\u4e00 \u573a\uff0c\u7136\u540e\u518d\u7ed9\u4ed6\u670d\u98df\u57f9\u5143\u56fa\u672c\u7684\u8865\u836f\uff0c\u5982\u662f\u4e00\u8fde\u4e09\u65e5\uff0c\u9ed1\u767d\u6469\u8bc3\u76f8\u52a9\u5c0f\u864e\u5b50\u8fd0\u529f\u81ea\u7597\uff0c\u4e0d\u4f46 \u86ca\u6bd2\u5c3d\u89e3\uff0c\u5c0f\u864e\u5b50\u5728\u5185\u529f\u4e0a\u4e5f\u5f97\u76ca\u4e0d\u6d45\u3002 \u3000\u3000\u8d70\u51fa\u82d7\u65cf\u5c71\u533a\uff0c\u9ed1\u767d\u6469\u8bc3\u91cd\u7533\u524d\u8bae\uff0c\u4e3b\u5f20\u5148\u5230\u82cd\u5c71\uff0c\u5bfb\u89c5\u5f20\u4e39\u67ab\u592b\u5987\u3002\u82cd\u5c71\u811a\u4e0b\u7684\u5927\u7406 \u57ce\uff0c\u4e43\u662f\u6bb5\u6f84\u82cd\u7684\u6545\u4e61\uff0c\u6bb5\u6f84\u82cd\u81ea\u8868\u8d5e\u540c\u3002\u800c\u4e14\u8fd9\u6570\u65e5\u6765\uff0c\u4ed6\u4ece\u4e8e\u627f\u73e0\u4e0e\u9ed1\u767d\u6469\u8bc3\u53e3\u4e2d\uff0c\u5f97 \u77e5\u5f20\u4e39\u67ab\u7684\u4e3a\u4eba\uff0c\u77e5\u9053\u5f20\u4e39\u67ab\u4e5f\u66fe\u7f81\u7559\u5f02\u56fd\uff0c\u5386\u5c3d\u96be\u8f9b\uff0c\u624d\u5f97\u91cd\u5f52\u6545\u56fd\uff0c\u8fd9\u8eab\u4e16\u7adf\u662f\u4e0e\u81ea\u5df1 \u76f8\u540c\uff0c\u66f4\u6068\u4e0d\u5f97\u65e9\u65e5\u76f8\u89c1\u3002 \u3000\u3000\u9ed1\u6469\u8bc3\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e00\u884c\u4eba\u8eab\u4efd\u4e0d\u540c\uff0c\u76f8\u8c8c\u7279\u522b\uff0c\u800c\u4e14\u81ea\u5df1\u53c8\u662f\u94a6\u72af\uff0c\u201d\u8bda\u6050\u5728\u4e00\u5904\u884c \u8d70\uff0c\u5bb9\u6613\u60f9\u4eba\u6ce8\u76ee\uff0c\u4fbf\u63d0\u8bae\u5206\u6279\u884c\u8d70\u3002\u4e8e\u627f\u73e0\u4e0e\u5c0f\u864e\u5b50\u505a\u7b2c\u4e00\u6279\uff0c\u6bb5\u6f84\u82cd\u4e0e\u6ce2\u65af\u516c\u4e3b\u5c45\u4e2d\uff0c \u9ed1\u767d\u6469\u8bc3\u62bc\u540e\uff0c\u8fd9\u6837\u5b89\u6392\uff0c\u4e5f\u662f\u4fdd\u62a4\u6ce2\u65af\u516c\u4e3b\u7684\u5b89\u6392\u3002\u82e5\u7136\u524d\u9762\u53d1\u73b0\u654c\u4eba\uff0c\u5219\u6709\u4e8e\u627f\u73e0\u4e0e\u5c0f \u864e\u5b50\u62a5\u8b66\uff1b\u82e5\u7136\u540e\u6709\u8ffd\u5175\uff0c\u9ed1\u767d\u6469\u8bc3\u5c3d\u53ef\u62b5\u6321\u5f97\u4f4f\u3002 \u3000\u3000\u9ed1\u6469\u8bc3\u53d6\u51fa\u51e0\u679d\u54cd\u7bad\uff0c\u4ea4\u7ed9\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u82e5\u662f\u767d\u5929\u9047\u89c1\u654c\u4eba\uff0c\u53ef\u4ee5\u5c04\u767d\u8272\u7bad\u6746\u8fd9\u4e00\u79cd\uff1b \u82e5\u662f\u665a\u4e0a\u9047\u89c1\u654c\u4eba\uff0c\u53ef\u4ee5\u5c04\u9ed1\u8272\u7bad\u6746\u8fd9\u4e00\u79cd\u3002\u8fd9\u79cd\u54cd\u7bad\uff0c\u4e0d\u4f46\u6570\u91cc\u4e4b\u5185\u53ef\u95fb\uff0c\u800c\u4e14\u8fd8\u53ef\u53d1\u51fa \u4e00\u6e9c\u84dd\u706b\uff0c\u5728\u591c\u95f4\u6700\u6613\u8fa8\u8ba4\u3002\u201d\u6bb5\u6f84\u82cd\u89c1\u4ed6\u8bbe\u60f3\u5f97\u5982\u6b64\u5468\u5230\uff0c\u5927\u662f\u653e\u5fc3\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u4e0e\u5c0f\u864e\u5b50\u540c\u4e58\u767d\u9a6c\uff0c\u8de8\u8fc7\u4e91\u8d35\u9ad8\u539f\uff0c\u8fdb\u5165\u4e91\u5357\uff0c\u4e00\u8def\u4e0a\u5e78\u559c\u65e0\u4e8b\uff0c\u54cd\u7bad\u59cb\u7ec8\u6ca1\u6709 \u653e\u8fc7\u3002\u5c0f\u864e\u5b50\u6bd4\u4e8e\u627f\u73e0\u5c0f\u4e09\u5c81\uff0c\u8eab\u4f53\u8301\u58ee\uff0c\u4ec5\u6bd4\u4e8e\u627f\u73e0\u77ee\u534a\u4e2a\u5934\uff0c\u4e00\u8def\u4e0a\u59d0\u5f1f\u79f0\u547c\uff0c\u5f7c\u6b64\u8c08 \u8bba\u6b66\u529f\uff0c\u5012\u662f\u6beb\u4e0d\u5bc2\u5bde\u3002 \u3000\u3000\u6570\u65e5\u4e4b\u540e\uff0c\u5c06\u8fd1\u6606\u660e\uff0c\u5b98\u9053\u5766\u8361\uff0c\u66f4\u4e0d\u7528\u62c5\u5fc3\u3002\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u4e3a\u4e86\u6015\u8ddd\u79bb\u8fc7\u8fdc\uff0c \u8fd9\u51e0\u65e5\u603b\u4e0d\u6562\u653e\u4efb\u767d\u9a6c\u5954\u9a70\uff0c\u767d\u9a6c\u4e5f\u4e00\u5b9a\u95f7\u6781\u5566\uff01\u201d\u4e00\u65f6\u5174\u8d77\uff0c\u653e\u677e\u7ef3\u7f30\uff0c\u7167\u591c\u72ee\u5b50\u9a6c\u653e\u5f00 \u56db\u8e44\uff0c\u4e24\u65c1\u7684\u6811\u6728\u623f\u5c4b\u4e5f\u50cf\u4f1a\u79fb\u52a8\u4e00\u822c\uff0c\u7eb7\u7eb7\u540e\u9000\uff0c\u5c0f\u864e\u5b50\u62b1\u7740\u4e8e\u627f\u73e0\u7684\u7ea4\u8170\uff0c\u53eb\u9053\uff1a\u201c\u723d \u5feb\uff0c\u723d\u5feb\uff01\u54b1\u4eec\u90fd\u53d8\u6210\u4f1a\u817e\u4e91\u9a7e\u96fe\u7684\u795e\u4ed9\u4e86\uff01\u201d\u4e8e\u627f\u73e0\u4e00\u7b11\u52d2\u9a6c\uff0c\u6606\u660e\u57ce\u5899\u5df2\u7ecf\u5728\u671b\u3002 \u3000\u3000\u6606\u660e\u53f7\u79f0\u56db\u5b63\u5982\u6625\uff0c\u65f6\u8282\u5df2\u662f\u4ef2\u79cb\uff0c\u90ca\u5916\u4ecd\u662f\u7e41\u82b1\u4f3c\u9526\uff0c\u8fdb\u5f97\u57ce\u6765\uff0c\u4f46\u89c1\u5e02\u8857\u6574\u6d01\uff0c\u5904 \u5904\u82b1\u6728\u6276\u758f\uff0c\u897f\u5c71\u9038\u6eba\uff0c\u597d\u50cf\u4e00\u4e2a\u4fa7\u5367\u7684\u7f8e\u4eba\uff0c\u4fef\u77b0\u5168\u57ce\uff0c\u897f\u5c71\u811a\u4e0b\uff0c\u6ec7\u6c60\u6e2f\u8fce\u4ea4\u9519\uff0c\u6ce2\u5149 \u6d69\u8361\uff0c\u4fe8\u82e5\u6c5f\u5357\u6c34\u4e61\u3002\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u771f\u597d\uff0c\u54b1\u4eec\u53ef\u4ee5\u591a\u73a9\u4e24\u5929\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ed6\u4eec \u6700\u5c11\u8981\u540e\u5929\u624d\u80fd\u8d76\u5230\uff0c\u591f\u4f60\u73a9\u7684\u5566\u3002\u201d\u4e24\u4eba\u7ed5\u57ce\u4e00\u531d\uff0c\u5148\u9971\u89c8\u4e86\u4e00\u904d\u6606\u660e\u7684\u666f\u8272\uff0c\u7136\u540e\u624d\u5230 \u5e02\u4e2d\u5fc3\u627e\u4e86\u4e00\u95f4\u5ba2\u5e97\uff0c\u5728\u5916\u9762\u7559\u4e0b\u6807\u8bb0\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u4e8e\u627f\u73e0\u6253\u542c\u4e86\u6606\u660e\u7684\u540d\u80dc\u53e4\u8ff9\uff0c\u5bf9\u5c0f\u864e\u5b50\u7b11\u9053\uff1a\u201c\u5c0f\u987d\u7ae5\uff0c\u4eca\u5929\u653e\u4f60\u4e00\u5929 \u5047\uff0c\u4e0a\u5348\u54b1\u4eec\u53bb\u6e38\u5927\u89c2\u56ed\uff0c\u4e0b\u5348\u53bb\u901b\u897f\u5c71\u3002\u5e26\u4f60\u53bb\u73a9\uff0c\u4f60\u53ef\u4e0d\u8bb8\u80e1\u95f9\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u6211\u8fd8 \u6ca1\u6709\u5411\u5f20\u5927\u4fa0\u62dc\u5e08\uff0c\u4f60\u5c31\u6446\u8d77\u5e08\u59d0\u7684\u67b6\u5b50\u6765\u4e86\uff01\u6211\u504f\u8981\u80e1\u95f9\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u8981\u80e1\u95f9\uff0c\u6211 \u5c31\u4e0d\u5e26\u4f60\u53bb\uff0c\u7384\u529f\u8981\u8bc0\uff0c\u4e5f\u4e0d\u4f20\u6388\u7ed9\u4f60\u3002\u201d\u5c0f\u864e\u5b50\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u62ff\u7384\u529f\u8981\u8bc0\u6765\u5a01\u5413\u6211\uff0c\u6211 \u53ea\u597d\u542c\u4f60\u7684\u8bdd\u5566\u3002\u201d\u5f20\u98ce\u5e9c\u9057\u8a00\u8981\u5f20\u4e39\u67ab\u6536\u5c0f\u864e\u5b50\u4e3a\u5f92\uff0c\u9ed1\u767d\u6469\u8bc3\u8fd9\u6b21\u62a4\u9001\u6ce2\u65af\u516c\u4e3b\u524d\u5f80\u5927 \u7406\uff0c\u53e6\u4e00\u4e2a\u76ee\u7684\u4fbf\u662f\u8981\u5c06\u5c0f\u864e\u5b50\u8f6c\u5230\u5f20\u4e39\u67ab\u95e8\u4e0b\uff0c\u8fd9\u4e8b\u60c5\u4e8e\u627f\u73e0\u4e0e\u5c0f\u864e\u5b50\u90fd\u5df2\u77e5\u9053\uff0c\u5c0f\u864e\u5b50 \u4e5f\u65e9\u5df2\u5c06\u4e8e\u627f\u73e0\u5f53\u4f5c\u5e08\u59d0\u770b\u5f85\u4e86\u3002 \u3000\u3000\u56ed\u4e2d\u7a7a\u5730\u4e0a\u4e00\u8001\u4e00\u5c11\uff0c\u4f3c\u662f\u7236\u5973\uff0c\u8001\u8005\u5934\u7f20\u767d\u5e03\uff0c\u5973\u7684\u7a7f\u7740\u767e\u6298\u88d9\uff0c\u770b\u6765\u4e43\u662f\u5f5d\u65cf\u7684\u6253 \u626e\u3002\u90a3\u5c11\u5973\u62bd\u51fa\u4e00\u628a\u957f\u5251\uff0c\u8868\u6f14\u541e\u5251\u7684\u529f\u592b\uff0c\u957f\u5251\u4f38\u5165\u53e3\u5185\uff0c\u76f4\u6ca1\u81f3\u67c4\uff0c\u7136\u540e\u518d\u62bd\u51fa\u6765\uff0c\u5728 \u7a7a\u4e2d\u4e00\u6325\uff0c\u5530\u7684\u4e00\u58f0\uff0c\u523a\u5165\u4e00\u68f5\u67f3\u6811\uff0c\u6ca1\u5165\u51e0\u5bf8\uff0c\u8868\u660e\u8fd9\u628a\u5251\u5e76\u4e0d\u662f\u628a\u8f6f\u5251\uff0c\u65c1\u89c2\u7684\u51e0\u4e2a\u5c0f \u4f19\u5b50\u5927\u58f0\u559d\u5f69\u3002\u90a3\u8001\u8005\u7aef\u8d77\u94dc\u76d8\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u66f4\u7cbe\u5f69\u7684\u628a\u620f\uff0c\u770b\u5b98\u8bf7\u5148\u6253\u8d4f\u51e0\u4e2a\u94f6\u5b50\u3002\u201d\u4f46 \u89c2\u4f17\u4e0d\u591a\uff0c\u8001\u8005\u7ed5\u573a\u4e00\u5468\uff0c\u6536\u96c6\u8d77\u6765\u8fd8\u4e0d\u591f\u4e00\u4e24\u94f6\u5b50\uff0c\u8001\u8005\u5c06\u94dc\u76d8\u9012\u5230\u4e8e\u627f\u73e0\u7684\u9762\u524d\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u4f38\u624b\u638f\u94b1\uff0c\u5ffd\u5730\u7c89\u8138\u901a\u7ea2\uff0c\u539f\u6765\u5979\u5fd8\u8bb0\u5e26\u94f6\u5b50\uff0c\u888b\u4e2d\u53ea\u6709\u5341\u591a\u6587\u94dc\u94b1\uff0c\u600e\u597d\u610f\u601d \u62ff\u51fa\u6765\u3002\u90a3\u8001\u8005\u9053\uff1a\u201c\u8bf7\u5c0f\u59d0\u9ad8\u62ac\u8d35\u624b\uff0c\u968f\u4fbf\u8d4f\u8d50\u51e0\u4e2a\u3002\u201d\u4e8e\u627f\u73e0\u8d8a\u53d1\u5c34\u5c2c\uff0c\u5fc3\u4e00\u6025\uff0c\u62d4\u51fa \u5934\u4e0a\u7684\u7389\u9497\uff0c\u4e22\u5230\u94dc\u76d8\u4e2d\u9053\uff1a\u201c\u8fd9\u4e2a\u7ed9\u4f60\u3002\u201d\u5ffd\u5730\u60f3\u8d77\u8fd9\u662f\u6bcd\u4eb2\u7684\u9057\u7269\uff0c\u600e\u80fd\u968f\u4fbf\u7ed9\u4eba\uff1f\u90a3 \u8001\u8005\u5df2\u62ff\u8d77\u4e86\u7389\u9497\uff0c\u9762\u4e0a\u9732\u51fa\u8be7\u5f02\u7684\u795e\u8272\uff0c\u4ed6\u4e00\u751f\u5728\u6c5f\u6e56\u5356\u827a\u53ef\u8fd8\u672a\u6709\u8fc7\u4eba\u5c06\u9970\u7269\u9001\u7ed9\u4ed6 \u7684\uff0c\u4f55\u51b5\u8fd9\u7389\u9497\u662f\u4e00\u7247\u901a\u4f53\u6676\u83b9\u7684\u78a7\u7389\u96d5\u6210\uff0c\u867d\u975e\u7a00\u4e16\u4e4b\u73cd\uff0c\u5c11\u8bf4\u4e5f\u503c\u6570\u767e\u4e24\u94f6\u5b50\u3002\u65c1\u8fb9\u6709 \u4e00\u4e2a\u8f7b\u8584\u7684\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u59d1\u5a18\u597d\u9614\u7ef0\uff0c\u600e\u4e48\u5c06\u8058\u793c\u4e5f\u62ff\u51fa\u6765\u5566\uff01\u201d\u4e8e\u627f\u73e0\u6b63\u6ca1\u597d\u6c14\uff0c \u6458\u4e0b\u4e00\u7247\u67f3\u53f6\uff0c\u8f7b\u8f7b\u4e00\u5f39\uff0c\u5979\u867d\u7136\u8fd8\u6ca1\u7ec3\u5230\u201c\u6458\u53f6\u98de\u82b1\uff0c\u4f24\u4eba\u7acb\u6b7b\u201d\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u4f46\u8fd9\u4e00\u5f39 \u52b2\u9053\u4e5f\u662f\u4e0d\u5c0f\uff0c\u90a3\u7247\u67f3\u53f6\u5728\u8f7b\u8584\u5c11\u5e74\u7684\u624b\u8155\u4e0a\u201c\u524a\u201d\u8fc7\uff0c\u5c11\u5e74\u7684\u624b\u8155\u4e0a\u767b\u65f6\u8d77\u4e86\u4e00\u9053\u7ea2\u5370\uff0c \u597d\u50cf\u88ab\u94c1\u7ebf\u201c\u524a\u201d\u4e86\u4e00\u4e0b\u4f3c\u7684\uff0c\u201c\u54ce\uff0c\u54df\uff0c\u54df\uff01\u201d\u5730\u53eb\u8d77\u75db\u6765\u3002\u4e8e\u627f\u73e0\u7684\u624b\u6cd5\u8f7b\u5de7\u4e4b\u6781\uff0c\u90a3 \u8f7b\u8584\u7684\u5c11\u5e74\u53d7\u4e86\u521b\u4f24\uff0c\u8fd8\u4e0d\u77e5\u9053\u662f\u4e8e\u627f\u73e0\u5f04\u7684\u628a\u620f\uff0c\u8fde\u58f0\u547c\u602a\uff0c\u5413\u5f97\u4e0d\u6562\u518d\u5728\u56ed\u5185\u505c\u7559\u3002 \u3000\u3000\u90a3\u5356\u827a\u8001\u4eba\u62ff\u8d77\u7389\u9497\uff0c\u770b\u4e86\u4e00\u773c\uff0c\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u4e2a\u91ce\u4e2b\u5934\u53ef\u4e0d\u914d\u6234\u8fd9\u4e2a\u7389\u9497\uff0c\u5979\u5e74 \u7eaa\u53c8\u5c0f\uff0c\u8981\u4e0d\u7136\u6211\u5012\u53ef\u4ee5\u7ed9\u5979\u505a\u5ac1\u5986\u3002\u5c0f\u59d0\uff0c\u4f60\u7684\u597d\u5fc3\u6211\u611f\u6fc0\u4e0d\u5c3d\uff0c\u8fd9\u6837\u7684\u539a\u793c\u6211\u53ef\u4e0d\u6562\u8981 \u5440\uff01\u4f60\u5c31\u968f\u4fbf\u8d4f\u8d50\u51e0\u6587\u94b1\u5427\u3002\u201d\u7b11\u563b\u563b\u5730\u5c06\u7389\u9497\u9012\u56de\u7ed9\u4e8e\u627f\u73e0\uff0c\u4e8e\u627f\u73e0\u7ea2\u900f\u8116\u5b50\uff0c\u63a5\u8fc7\u7389 \u9497\uff0c\u5c06\u888b\u4e2d\u6240\u6709\u7684\u94dc\u94b1\uff0c\u90fd\u6296\u4e86\u51fa\u6765\uff0c\u6254\u8fdb\u94dc\u76d8\uff0c\u65c1\u89c2\u4eba\u7b49\uff0c\u53c8\u662f\u4e00\u9635\u54c4\u7b11\u3002 \u3000\u3000\u5356\u827a\u7684\u573a\u5b50\u65c1\u8fb9\uff0c\u6709\u4e00\u4e2a\u5356\u4e91\u5357\u7c73\u7ebf\u7684\u62c5\u5b50\uff0c\u7089\u706b\u70e7\u5f97\u6b63\u65fa\uff0c\u548c\u8fd9\u5bf9\u5356\u827a\u7236\u5973\uff0c\u4f3c\u4e4e\u662f \u719f\u637b\u7684\u670b\u53cb\u3002\u5728\u8001\u8005\u5411\u4eba\u8ba8\u94b1\u7684\u65f6\u5019\uff0c\u4ed6\u7684\u5973\u513f\u5df2\u5c06\u90a3\u67c4\u957f\u5251\u653e\u5230\u7089\u706b\u4e2d\u70e7\u5f97\u901a\u7ea2\uff0c\u8fd9\u65f6\u62d4 \u4e86\u51fa\u6765\uff0c\u4ea4\u7ed9\u4ed6\u7684\u7236\u4eb2\uff0c\u90a3\u8001\u8001\u63d0\u8d77\u5251\u67c4\u4e00\u6325\uff0c\u5251\u5c16\u4e0a\u5c1a\u6709\u706b\u661f\u98de\u6e85\uff0c\u65c1\u89c2\u8005\u7eb7\u7eb7\u907f\u5f00\uff0c\u90a3 \u8001\u8005\u7b11\u9053\uff1a\u201c\u77a7\uff0c\u7cbe\u5f69\u7684\u628a\u620f\u6765\u4e86\u3002\u201d\u5c06\u90a3\u67c4\u70e7\u5f97\u901a\u7ea2\u7684\u957f\u5251\u9001\u5165\u53e3\u4e2d\uff0c\u4f17\u4eba\u54d7\u7136\u60ca\u547c\uff0c\u53ea \u89c1\u90a3\u8001\u8005\u5c06\u957f\u5251\u6162\u6162\u9001\u5165\uff0c\u76f4\u6ca1\u81f3\u67c4\uff0c\u5ffd\u7136\u5f20\u53e3\u4e00\u5410\uff0c\u90a3\u67c4\u5251\u8df3\u4e86\u51fa\u6765\u3002\u8001\u8005\u628a\u5251\u63d2\u5165\u7c73\u7ebf \u62c5\u5b50\u65c1\u8fb9\u7684\u4e00\u6876\u6c34\u4e2d\uff0c\u70eb\u5f97\u55e4\u55e4\u4f5c\u54cd\uff0c\u6c34\u4e2d\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u65c1\u89c2\u8005\u90fd\u770b\u5f97\u5446\u4e86\uff0c\u6ca1\u6709\u4eba \u518d\u53bb\u6ce8\u610f\u4e8e\u627f\u73e0\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u4e5f\u662f\u5927\u4e3a\u9707\u60ca\uff0c\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u662f\u4ec0\u4e48\u529f\u592b\uff1f\u201d\u5c0f\u864e\u5b50\u5ffd\u7136\u5728\u5979\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u8fd9\u662f \u5047\u7684\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u600e\u4e48\u662f\u5047\u7684\uff1f\u201d\u5c0f\u864e\u5b50\u5c06\u4e8e\u627f\u73e0\u62c9\u8fc7\u4e00\u8fb9\uff0c\u6084\u6084\u8bf4\u9053\uff1a\u201c\u8fd9\u628a\u620f\u6211\u5728 \u5370\u5ea6\u89c1\u5f97\u591a\uff0c\u5047\u867d\u7136\u662f\u5047\uff0c\u4e0d\u8fc7\u541e\u5251\u7684\u4eba\u6700\u5c11\u4e5f\u5f97\u7ec3\u8fc7\u5341\u5e74\u516b\u5e74\uff0c\u4ed6\u4eec\u7ec3\u5230\u53ef\u4ee5\u541e\u4efb\u4f55\u5229 \u5668\uff0c\u5728\u5589\u9053\u91cc\u4e0d\u4f1a\u8f6c\u52a8\uff0c\u90a3\u4e48\u5c31\u4e0d\u4f1a\u53d7\u4f24\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f46\u90a3\u628a\u5251\u662f\u70e7\u7ea2\u7684\u5462\u3002\u201d\u5c0f\u864e \u5b50\u9053\uff1a\u201c\u8fd9\u8001\u4eba\u9884\u5148\u541e\u4e0b\u4e00\u628a\u5251\u9798\uff0c\u90a3\u628a\u5251\u5176\u5b9e\u662f\u63d2\u5728\u5251\u9798\u4e4b\u4e2d\uff0c\u70e7\u4e0d\u7740\u76ae\u8089\u7684\u3002\u201d\u8fd9\u4e2a\u89e3 \u91ca\u6d88\u91ca\u4e86\u4e8e\u627f\u73e0\u7684\u60ca\u5947\uff0c\u4f46\u5979\u5fc3\u4e2d\u8fd8\u662f\u7591\u56e2\u83ab\u91ca\u3002 \u3000\u3000\u8fd9\u541e\u5251\u529f\u592b\u65e2\u7136\u53ea\u6d41\u884c\u5370\u5ea6\uff0c\u90a3\u4e48\u8fd9\u4e24\u4e2a\u5f5d\u4eba\u5374\u4ece\u54ea\u91cc\u5b66\u6765\uff1f\u5728\u90a3\u4e2a\u65f6\u5019\uff0c\u4e2d\u5370\u4ea4\u901a\u5c1a \u672a\u53d1\u8fbe\uff0c\u4e91\u5357\u548c\u5370\u5ea6\uff0c\u867d\u7136\u53ea\u9694\u4e00\u4e2a\u7f05\u7538\uff0c\u4f46\u51fa\u56fd\u7684\u4eba\u8fd8\u662f\u6781\u5c11\uff0c\u800c\u4e14\u5f5d\u4eba\u4e60\u4fd7\uff0c\u6bd4\u6c49\u4eba\u66f4 \u4e3a\u5b89\u571f\u91cd\u8fc1\uff0c\u8fd9\u4e24\u4e2a\u5f5d\u4eba\u4e3a\u4f55\u80af\u79bb\u4e61\u80cc\u4e95\uff0c\u4e07\u91cc\u897f\u884c\uff0c\u53ea\u4e3a\u6c42\u53d6\u5370\u5ea6\u800d\u628a\u620f\u7684\u529f\u592b\uff1f\u518d\u8bf4\u8fd9 \u541e\u5251\u7684\u529f\u592b\u867d\u7136\u662f\u5047\uff0c\u4f46\u770b\u8fd9\u8001\u8005\u7684\u773c\u795e\u548c\u4ed6\u521a\u624d\u6325\u5251\u7684\u59ff\u52bf\uff0c\u5374\u53c8\u4f3c\u662f\u6709\u6b66\u529f\u5e95\u5b50\u7684\u4eba\u3002 \u66f4\u6709\u4e00\u6837\u53ef\u7591\u4e4b\u5904\uff0c\u82e5\u7136\u4ed6\u4eec\u53ea\u662f\u9760\u5356\u6280\u4e3a\u751f\u7684\u827a\u4eba\uff0c\u4f55\u4ee5\u521a\u624d\u5374\u53c8\u4e0d\u80af\u8981\u5979\u7684\u7389\u9497\uff1f \u3000\u3000\u4e0d\u8bf4\u4e8e\u627f\u73e0\u5fc3\u4e2d\u7684\u7591\u60d1\uff0c\u4e14\u8bf4\u8fd9\u8001\u8005\u9732\u4e86\u8fd9\u624b\u541e\u706b\u5251\u7684\u529f\u592b\uff0c\u867d\u7136\u83b7\u5f97\u5168\u573a\u559d\u5f69\uff0c\u4f46\u89c2 \u4f17\u8fd8\u662f\u4e0d\u89c1\u62e5\u6324\uff0c\u94dc\u76d8\u91cc\u53ea\u6709\u767e\u591a\u6587\u94dc\u94b1\u548c\u51e0\u94b1\u788e\u94f6\u5b50\uff0c\u90a3\u8001\u8005\u597d\u751f\u5931\u671b\uff0c\u5fae\u201c\u566b\u201d\u4e00\u58f0\uff0c \u65c1\u8fb9\u6709\u4e00\u4e2a\u597d\u5fc3\u7684\u770b\u5ba2\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u521d\u5230\u6606\u660e\u7684\u5427\uff1f\u600e\u4e48\u4e0d\u77e5\u9053\u4eca\u5929\u662f\u57ce\u968d\u5e99\u843d\u6210\u7684\u5927\u65e5 \u5b50\uff1f\u5168\u6606\u660e\u7684\u4eba\u90fd\u53bb\u77a7\u70ed\u95f9\u5566\uff0c\u4f60\u5feb\u5230\u57ce\u968d\u5e99\u53bb\u6446\u5f00\u6863\u53e3\u5427\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5927\u4e3a\u5947\u602a\uff0c\u57ce\u968d\u5e99\u4e43\u662f\u6700\u5e38\u89c1\u7684\u5e99\u5b87\uff0c\u5728\u4e2d\u56fd\u7684\u795e\u8bdd\u4f20\u8bf4\u4e2d\uff0c\u57ce\u968d\u4e5f\u5e76\u4e0d\u662f\u4ec0\u4e48 \u201c\u5927\u795e\u201d\uff0c\u600e\u4e48\u542c\u4ed6\u8bf4\u6765\uff0c\u7adf\u662f\u503e\u52a8\u5168\u57ce\u7684\u5927\u4e8b\uff1f\u96be\u9053\u6606\u660e\u7684\u57ce\u968d\u4e0e\u522b\u5730\u7684\u57ce\u968d\u6709\u4ec0\u4e48\u4e0d \u540c\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u56ed\u5b50\u5916\u8fb9\u4eba\u58f0\u9f0e\u6cb8\uff0c\u9523\u58f0\u9f13\u58f0\u4e0e\u71ce\u4eae\u7684\u9501\u5450\u58f0\uff0c\u6c47\u6210\u516b\u97f3\u5408\u594f\uff0c\u770b\u628a\u620f\u7684\u4eba\u53eb \u9053\uff1a\u201c\u54c8\uff0c\u57ce\u968d\u51fa\u5de1\u5566\uff0c\u54b1\u4eec\u5feb\u770b\u70ed\u95f9\u53bb\u3002\u201d\u90a3\u800d\u628a\u620f\u7684\u7236\u5973\u4fe9\uff0c\u90a3\u5356\u7c73\u7ebf\u7684\u5c0f\u8d29\uff0c\u90fd\u6536\u62fe \u8d77\u5bb6\u79c1\u62c5\u5b50\uff0c\u968f\u7740\u4eba\u7fa4\u5230\u5916\u9762\u770b\u70ed\u95f9\u4e86\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u54b1\u4eec\u4e5f\u53bb\u3002\u201d\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u7684\u57ce\u968d\u90fd\u662f\u5927\u540c\u5c0f\u5f02\uff0c\u53cd\u6b63\u4e0d\u8fc7 \u662f\u4e00\u5c0a\u6728\u5076\uff0c\u6709\u4ec0\u4e48\u597d\u770b\uff1f\u62ac\u57ce\u968d\u7684\u50cf\u51fa\u5de1\uff0c\u4f60\u5728\u4e61\u4e0b\u8fd8\u672a\u770b\u8fc7\u5417\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u54b1\u4eec\u4e0d \u770b\u795e\u50cf\uff0c\u53bb\u770b\u770b\u70ed\u95f9\u7684\u4eba\u4e5f\u597d\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5c0f\u5b69\u5b50\u5c31\u662f\u8d2a\u770b\u70ed\u95f9\uff01\u201d\u5176\u5b9e\u5979\u4e5f\u60f3\u53bb\u770b\uff0c \u4e0d\u8fc7\u5fc3\u6709\u6240\u7591\uff0c\u4e0d\u613f\u8ddf\u90a3\u5356\u827a\u7684\u7236\u5973\u548c\u8fd9\u4e9b\u770b\u5ba2\u4e00\u9053\uff0c\u56e0\u6b64\u6545\u610f\u5ef6\u6401\u4e00\u4e0b\uff0c\u8fd9\u624d\u548c\u5c0f\u864e\u5b50\u8d70 \u51fa\u5927\u89c2\u56ed\u3002 \u3000\u3000\u8857\u4e0a\u770b\u70ed\u95f9\u7684\u4eba\u62e5\u6324\u4e0d\u582a\uff0c\u4e8e\u627f\u73e0\u548c\u5c0f\u864e\u5b50\u597d\u4e0d\u5bb9\u6613\u624d\u6324\u5230\u524d\u9762\uff0c\u8fd9\u4e00\u770b\uff0c\u51e0\u4e4e\u4ee4\u4e8e\u627f \u73e0\u53eb\u51fa\u58f0\u6765\uff01 \u3000\u3000\u53ea\u89c1\u8fd9\u57ce\u968d\u5929\u5ead\u5b9d\u6ee1\uff0c\u9762\u5982\u6ee1\u6708\uff0c\u87d2\u888d\u7389\u5e26\uff0c\u624b\u6367\u671d\u7b0f\u3002\u53cc\u773c\u5982\u751f\uff0c\u5a01\u4e25\u4e4b\u6781\u800c\u53c8\u6148\u7965 \u4e4b\u6781\uff01\u8fd9\u6b63\u662f\u5979\u7236\u4eb2\u4e8e\u8c26\u7684\u96d5\u50cf\uff01\uff08\u6309\u6606\u660e\u7684\u57ce\u968d\u5e99\u5efa\u7b51\u6781\u4e3a\u5b8f\u4f1f\uff0c\u73b0\u5728\u5c1a\u5b58\uff0c\u4e0d\u8fc7\u5df2\u6539\u4f5c \u522b\u7528\u3002\u5e99\u4e2d\u5199\u660e\u662f\uff1a\u6606\u660e\u57ce\u968d\u4e8e\u8083\u5ffd\u516c\u5b50\u8c26\u795e\u4f4d\u3002\uff09 \u3000\u3000\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u4e0d\u8212\u670d\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6ca1\u6709\u5440\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u54ed \u4e86\uff1f\u201d\u4e8e\u627f\u73e0\u6025\u5fd9\u62ed\u6389\u773c\u89d2\u7684\u6cea\u73e0\uff0c\u9053\uff1a\u201c\u6211\u6b22\u559c\u5f97\u6d41\u6cea\u5566\uff01\u201d\u5c0f\u864e\u5b50\u5927\u4e3a\u5947\u602a\uff0c\u7b11\u9053\uff1a \u201c\u4f60\u8fd8\u8bf4\u6211\u5462\uff1f\u539f\u6765\u4f60\u6bd4\u6211\u8fd8\u7231\u770b\u70ed\u95f9\u3002\u201d\u597d\u534a\u664c\u4e0d\u89c1\u4e8e\u627f\u73e0\u7b54\u8154\uff0c\u4f46\u89c1\u5979\u53ea\u662f\u5446\u5446\u5730\u770b\u7740 \u90a3\u4e2a\u795e\u50cf\u3002\u6b63\u662f\uff1a \u3000\u3000\u5343\u79cb\u81ea\u6709\u516c\u8bc4\u5728\uff0c\u5fe0\u81e3\u6b7b\u540e\u5408\u4e3a\u795e\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u5341\u4e5d\u56de\u3000\u795e\u5e99\u60ca\u5fc3\u3000\u5fe0\u81e3\u7231\u9999\u706b\u3000\u9f99\u95e8\u7eb5\u76ee\u3000\u7389\u5973\u52a8\u60c5\u6000 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u5341\u4e5d\u56de\u3000\u795e\u5e99\u60ca\u5fc3\u3000\u5fe0\u81e3\u7231\u9999\u706b\u3000\u9f99\u95e8\u7eb5\u76ee\u3000\u7389\u5973\u52a8\u60c5\u6000 \u3000\u3000\u4e8e\u627f\u73e0\u5b9a\u4e00\u5b9a\u795e\uff0c\u5411\u4e00\u4e2a\u8ddf\u968f\u795e\u50cf\u6e38\u884c\u7684\u4eba\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u4f4d\u57ce\u968d\u8001\u7237\u662f\u8c01\uff1f\u201d\u90a3\u4eba\u9f13 \u8d77\u773c\u775b\u8bf4\u9053\uff1a\u201c\u57ce\u968d\u5c31\u662f\u57ce\u968d\uff0c\u5f53\u7136\u662f\u795e\u3002\u4f60\u8fd9\u4f4d\u59d1\u5a18\u95ee\u5f97\u597d\u602a\u3002\u201c\u4e8e\u627f\u73e0\u6014\u4e86\u4e00\u6014\uff0c\u5fc3 \u9053\uff1a\u201c\u4ed6\u662f\u4e0d\u77e5\u9053\u8fd9\u795e\u50cf\u5c31\u662f\u6211\u7684\u7236\u4eb2\u5462\uff1f\u8fd8\u662f\u4e0d\u65b9\u4fbf\u5bf9\u6211\u8bf4\uff1f\u201d\u53c8\u95ee\u9053\uff1a\u201c\u57ce\u968d\u5e99\u662f\u8c01\u8d77 \u7684\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u6350\u94b1\u7684\u7ec5\u5546\u591a\u7740\u5462\uff0c\u6211\u4e5f\u8bf4\u4e0d\u6e05\u695a\uff0c\u4f60\u95ee\u8fd9\u4e2a\u5e72\u4ec0\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9532\u800c\u4e0d \u820d\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u8fd9\u795e\u50cf\u662f\u8c01\u96d5\u523b\u7684\uff1f\u201d\u90a3\u4eba\u6120\u9053\uff1a\u201c\u4f60\u95ee\u7ba1\u6728\u5de5\u7684\u5934\u5b50\u53bb\u3002\u6211\u53ef\u6ca1\u5de5\u592b\u548c\u4f60 \u8bf4\u5e9f\u8bdd\u3002\u201d\u6025\u6025\u5fd9\u5fd9\u8d76\u4e0a\u524d\u5934\uff0c\u62ac\u7740\u57ce\u968d\u50cf\u7684\u884c\u5217\u5df2\u53bb\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u4e0d\u662f\u4e2d\u6691\u5427\uff1f\u201d\u6478\u6478\u4e8e\u627f\u73e0\u7684\u989d\u5934\uff0c\u4f46\u89c9\u4e00\u7247\u6c81\u51c9\uff0c\u4e8e\u627f\u73e0\u7529\u5f00 \u4ed6\u7684\u624b\u9053\uff1a\u201c\u522b\u80e1\u95f9\u3002\u201d\u5c0f\u864e\u5b50\u5fc3\u9053\uff1a\u201c\u4f60\u624d\u662f\u80e1\u95f9\u5462\uff0c\u54ea\u6709\u8fd9\u6837\u95ee\u4eba\u5bb6\u7684\u3002\u201d\u4f46\u89c1\u4e8e\u627f\u73e0 \u4e00\u526f\u4e27\u9b42\u843d\u9b44\u7684\u6837\u5b50\uff0c\u5c0f\u864e\u5b50\u751a\u662f\u62c5\u5fe7\u3002 \u3000\u3000\u4ed6\u54ea\u77e5\u4e8e\u627f\u73e0\u5fc3\u5934\u7684\u7d0a\u4e71\uff0c\u987b\u77e5\u4e8e\u627f\u73e0\u7684\u7236\u4eb2\u4e8e\u8c26\u662f\u4ee5\u53db\u9006\u4e4b\u7f6a\u88ab\u6284\u5bb6\u5904\u65a9\u7684\uff0c\u867d\u7136\u5929 \u4e0b\u4e4b\u4eba\uff0c\u95fb\u8baf\u60b2\u6124\uff0c\u4f46\u5728\u7687\u5e1d\u6deb\u5a01\u4e4b\u4e0b\uff0c\u8c01\u6562\u5410\u534a\u53e5\u4e0d\u5e73\u4e4b\u8bed\uff1f\u60f3\u4e0d\u5230\u6606\u660e\u7adf\u7136\u628a\u4e8e\u8c26\u5949\u4e3a \u57ce\u968d\u3002\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u6606\u660e\u867d\u7136\u50fb\u5904\u5357\u7586\uff0c\u4f46\u4ecd\u662f\u671d\u5ef7\u7ba1\u6cbb\uff0c\u82e5\u88ab\u671d\u5ef7\u5b98\u540f\u770b\u51fa\u8fd9\u662f\u6211\u7236\u4eb2 \u7684\u795e\u50cf\uff0c\u53d1\u8d77\u9020\u50cf\u5efa\u5e99\u7684\u4eba\u5b9a\u96be\u9003\u6284\u5bb6\u706d\u65cf\u4e4b\u7978\uff0c\u8c01\u4eba\u6709\u8fd9\u822c\u5927\u80c6\u3002\u201d\u800c\u4e14\u4e5f\u60f3\u4e0d\u5230\u6606\u660e\u57ce \u4e2d\uff0c\u6709\u4ec0\u4e48\u7236\u4eb2\u7684\u4eb2\u53cb\u3002\u5fc3\u4e2d\u66f4\u662f\u5947\u602a\uff0c\u6697\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u7236\u4eb2\u7adf\u7136\u4f1a\u5230\u8fd9\u8fbd\u8fdc\u7684\u8fb9\u57ce\u6765\u4f5c\u57ce \u968d\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u8eab\u4e0d\u7531\u5df1\u5730\u8ddf\u968f\u7740\u770b\u70ed\u95f9\u7684\u4eba\u8d70\u5230\u57ce\u968d\u5e99\u53bb\uff0c\u57ce\u968d\u672c\u6765\u4e0d\u662f\u201c\u5c0a\u795e\u201d\uff0c\u5929\u4e0b\u5404\u5730 \u7684\u57ce\u968d\u5e99\u90fd\u53ea\u662f\u804a\u5177\u89c4\u6a21\u800c\u5df2\uff0c\u8fd9\u5ea7\u57ce\u968d\u5e99\u5374\u5927\u5f97\u51fa\u5947\uff0c\u8fdb\u4e86\u4e09\u91cd\uff0c\u624d\u5230\u5927\u6bbf\uff0c\u4f46\u89c1\u98de\u6a90\u7fd8 \u89d2\uff0c\u91d1\u78a7\u8f89\u714c\uff0c\u5927\u7406\u77f3\u7684\u6a90\u9636\u4e5f\u6709\u6570\u5341\u7ea7\u4e4b\u591a\uff0c\u4e8e\u627f\u73e0\u4e0e\u5c0f\u864e\u5b50\u6324\u5230\u524d\u9762\uff0c\u4f46\u89c1\u5927\u6bbf\u91cc\u9999\u70df \u6f66\u7ed5\uff0c\u6324\u6ee1\u4e86\u4eba\uff0c\u5ffd\u95fb\u5f97\u516b\u97f3\u9f50\u594f\uff0c\u770b\u70ed\u95f9\u7684\u4eba\u7eb7\u7eb7\u8ba9\u8def\uff0c\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u77a7\uff0c\u5c0f\u516c\u7239\u6765 \u4e86\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5fd9\u5411\u65c1\u8fb9\u4e00\u4f4d\u8001\u8005\u8bf7\u95ee\u9053\uff1a\u201c\u54ea\u4f4d\u5c0f\u516c\u7239\uff1f\u201d\u90a3\u8001\u8005\u7b11\u9053\uff1a\u201c\u6606\u660e\u57ce\u91cc\u80fd\u6709\u51e0\u4f4d \u56fd\u516c\uff1f\u201d\u4e8e\u627f\u73e0\u5927\u5403\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u662f\u6c90\u56fd\u516c\uff1f\u201d\u90a3\u8001\u8005\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u57ce\u968d\u5e99\u4fbf\u662f\u6c90 \u5c0f\u516c\u7239\u5021\u4fee\u7684\u3002\u201d\u53ea\u89c1\u90a3\u4e58\u84dd\u5462\u5927\u8f7f\u505c\u5728\u53f0\u9636\u4e0b\u9762\uff0c\u8f7f\u4e2d\u8d70\u51fa\u4e00\u4e2a\u8d35\u4ecb\u516c\u5b50\uff0c\u5507\u7ea2\u9f7f\u767d\uff0c\u770b \u6765\u4e0d\u8fc7\u5341\u4e03\u516b\u5c81\uff0c\u8138\u4e0a\u8fd8\u5e26\u6709\u4e9b\u7a1a\u6c14\u3002\u4ed6\u4e00\u8fdb\u6765\uff0c\u5e99\u4e2d\u8083\u9759\u65e0\u54d7\uff0c\u8d5e\u793c\u7684\u9053\uff1a\u201c\u9e23\u949f\u51fb\u9f13\uff0c \u8bf7\u5c0a\u795e\u5347\u4f4d\u3002\u201d\u539f\u6765\u8fd9\u4f4d\u5c0f\u516c\u7239\u662f\u6765\u4e3b\u6301\u57ce\u968d\u5e99\u7684\u843d\u6210\u5927\u5178\u7684\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5982\u5728\u68a6\u4e2d\uff0c\u60f6\u60d1\u4e0d\u5df2\uff0c\u539f\u6765\u6c90\u5bb6\u4e16\u88ad\u9ed4\u56fd\u516c\uff0c\u9547\u5b88\u4e91\u6d77\uff0c\u5728\u6731\u5143\u748b\u7684\u624b\u4e0b\u5927\u5c06\u4e4b \u4e2d\uff0c\u7b97\u5f97\u662f\u6700\u6709\u798f\u6c14\u7684\u4e00\u4f4d\u3002\u6c90\u5bb6\u59cb\u7956\u6c90\u82f1\uff0c\u8fd8\u662f\u592a\u7956\u6731\u5143\u748b\u7684\u517b\u5b50\uff0c\u5e73\u5b9a\u4e86\u4e91\u5357\u7684\u201c\u7cb1\u738b \u4e4b\u4e71\u201d\u540e\uff0c\u53d7\u5c01\u4e3a\u201c\u9ed4\u56fd\u516c\u201d\uff08\u89c1\u300a\u660e\u53f2\u300b\u4e00\u4e8c\u516d\uff0c\u5217\u4f20\u56db\u3002\uff09\uff0c\u6c90\u5bb6\u7684\u5b50\u5b59\uff0c\u6709\u597d\u51e0\u4f4d\u90fd \u662f\u9a78\u9a6c\uff0c\u5bcc\u8d35\u8363\u534e\uff0c\u5728\u529f\u81e3\u4e4b\u4e2d\uff0c\u6570\u4e0d\u51fa\u7b2c\u4e8c\u4f4d\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7684\u7236\u4eb2\u662f\u660e\u671d\u5927\u81e3\uff0c\u4e8e\u627f\u73e0\u5f53\u7136\u719f\u6089\u672c\u671d\u53f2\u4e8b\u3002\u8981\u77e5\u660e\u592a\u7956\u672a\u5143\u748b\u52be\u8584\u5be1\u6069\uff0c\u5f97 \u4e86\u5929\u4e0b\u4e4b\u540e\uff0c\u5927\u6740\u529f\u81e3\uff0c\u624b\u6bb5\u6bd2\u8fa3\uff0c\u5b9e\u4e0d\u5728\u6c49\u9ad8\u7956\u5218\u90a6\u4e4b\u4e0b\u3002\u4ed6\u624b\u4e0b\u7684\u5927\u81e3\uff0c\u519b\u529f\u6bd4\u6c90\u82f1\u5927 \u7684\u6709\u7684\u662f\uff0c\u4f8b\u5982\u5f90\u8fbe\u3001\u5e38\u9047\u6625\u3001\u84dd\u7389\u90fd\u662f\uff0c\u4f46\u6216\u8005\u672c\u8eab\u4e0d\u5f97\u5584\u7ec8\uff0c\u6216\u8005\u5b50\u5b59\u906d\u53d7\u8bdb\u622e\u3002\u5982\u84dd \u7389\u4ee5\u201c\u53db\u9006\u201d\u7f6a\u8bdb\u4e09\u65cf\uff0c\u5e38\u9047\u6625\u7684\u513f\u5b50\u4e5f\u88ab\u7275\u8fde\u5165\u84dd\u7389\u6848\u5185\u800c\u88ab\u8d50\u6b7b\uff1b\u5f90\u8fbe\u662f\u660e\u671d\u5f00\u56fd\u7684\u7b2c \u4e00\u529f\u81e3\uff0c\u53d7\u5c01\u4e3a\u4e2d\u5c71\u738b\uff0c\u8d50\u6709\u514d\u6b7b\u7684\u94c1\u5238\u4e39\u4e66\uff0c\u4f46\u540e\u6765\u71d5\u738b\u4ee5\u53d4\u593a\u4f84\u4f4d\uff08\u660e\u6210\u7956\uff09\uff0c\u5f90\u8fbe\u7684 \u513f\u5b50\u5f90\u8f89\u7956\u4ecd\u4e0d\u514d\u88ab\u524a\u7235\u5e7d\u6b7b\uff08\u89c1\u300a\u660e\u53f2\u300b\u4e00\u4e8c\u4e94\uff0c\u5217\u4f20\u5341\uff09\u3002\u53ea\u6709\u6c90\u82f1\u4e00\u5bb6\uff0c\u8fdc\u9547\u4e91\u5357\uff0c \u4e16\u4ee3\u4e3a\u201c\u516c\u201d\uff08\u7235\u4f4d\uff09\uff0c\u53ef\u7b97\u5f02\u6570\u3002 \u3000\u3000\u56e0\u6b64\u4e8e\u627f\u73e0\u542c\u8bf4\u8fd9\u57ce\u968d\u5e99\u662f\u6c90\u5e9c\u7684\u201c\u5c0f\u516c\u7239\u201d\u5021\u4fee\u7684\uff0c\u4e0d\u80dc\u60f6\u60d1\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u82e5\u662f\u522b \u4eba\u4e5f\u8fd8\u7f62\u4e86\uff0c\u6c90\u5bb6\u5c61\u4ee3\u90fd\u5f97\u671d\u5ef7\u6069\u5ba0\uff0c\u4f55\u4ee5\u4ed6\u5374\u4e0d\u6015\u7275\u8fde\uff0c\u7ed9\u6211\u7684\u7236\u4eb2\u7acb\u50cf\u9020\u5e99\uff0c\u867d\u8bf4\u662f\u5047 \u6258\u57ce\u968d\uff0c\u4f46\u5982\u6b64\u662d\u5f70\uff0c\u5c82\u80fd\u7792\u5c3d\u6240\u6709\u4e4b\u4eba\u3002\u800c\u4e14\u4e5f\u672a\u542c\u8bf4\u6211\u7236\u4eb2\u548c\u6c90\u5bb6\u6709\u4ec0\u4e48\u4ea4\u60c5\uff0c\u8fd9\u4e8b\u672a \u514d\u592a\u5947\u602a\u4e86\u3002\u201d \u3000\u3000\u53ea\u89c1\u90a3\u5c0f\u516c\u7239\u606d\u606d\u656c\u656c\u5730\u4e0a\u4e86\u4e09\u67f1\u9999\uff0c\u4e0b\u9762\u7684\u7ec5\u5546\u4f9d\u6b21\u8fdb\u9999\u884c\u793c\uff0c\u53ea\u662f\u9664\u4e86\u90a3\u201c\u5c0f\u516c \u7239\u201d\u4e4b\u5916\uff0c\u5374\u5e76\u65e0\u4e00\u4e2a\u5b98\u5458\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5ffd\u5730\u6392\u4f17\u800c\u51fa\uff0c\u5728\u5e99\u795d\u624b\u91cc\u4e5f\u63a5\u8fc7\u4e09\u67f1\u9999\uff0c\u70ed\u6cea\u76c8\u7736\uff0c\u8dea\u5728\u795e\u524d\uff0c\u4f4e\u5934\u9ed8\u7977\uff1a \u201c\u7239\u7239\u5475\uff0c\u4f60\u88ab\u5949\u656c\u4e3a\u795e\uff0c\u6c38\u53d7\u4e07\u6c11\u819c\u62dc\uff0c\u6b7b\u4e5f\u4e0d\u673d\u4e86\uff01\u201d \u3000\u3000\u90a3\u5c0f\u516c\u7239\u751a\u662f\u8be7\u5f02\uff0c\u62db\u624b\u53eb\u5979\u95ee\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u59d4\u5c48\uff0c\u8981\u7980\u544a\u57ce\u968d\uff1f\u201d\u4e8e\u627f\u73e0\u62ed\u6389\u773c\u89d2 \u7684\u6cea\u73e0\uff0c\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u89c1\u4f60\u4eec\u5982\u6b64\u5c0a\u656c\u57ce\u968d\uff0c\u4e00\u65f6\u611f\u89e6\uff0c\u7981\u4e0d\u4f4f\u6d41\u6cea\u4e86\u3002\u201d\u5c0f\u516c\u7239\u8d8a\u53d1 \u5947\u602a\uff0c\u6b63\u60f3\u518d\u95ee\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u53c8\u662f\u9e23\u9523\u5f00\u9053\u4e4b\u58f0\uff0c\u6709\u4eba\u62a5\u9053\uff1a\u201c\u738b\u526f\u5c06\u519b\u5230\u3002\u201d \u3000\u3000\u5c0f\u516c\u7239\u76b1\u7709\u9053\uff1a\u201c\u4ed6\u4e5f\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u8d70\u51fa\u53bb\u8fce\u63a5\uff0c\u4e8e\u627f\u73e0\u4e58\u673a\u9000\u4e0b\uff0c\u5076\u7136\u4e00\u77a5\uff0c\u5ffd\u89c1\u90a3 \u4e24\u4e2a\u5356\u827a\u7684\u7236\u5973\u4e5f\u6324\u5728\u4e00\u4e2a\u89d2\u843d\u91cc\uff0c\u6b63\u5728\u5077\u5077\u5730\u671b\u7740\u81ea\u5df1\u3002\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u5f85\u9ed1\u767d\u6469\u8bc3\u4e00\u5230\uff0c\u53ef\u5f97\u7acb\u523b\u79bb\u5f00\u8fd9\u513f\u3002\u201d\u5979\u4e5f\u81ea\u77e5\u9732\u4e86\u75d5 \u8ff9\uff0c\u4f46\u773c\u89c1\u81ea\u5df1\u7236\u4eb2\u7684\u795e\u50cf\uff0c\u5374\u53c8\u5982\u4f55\u80fd\u591f\u65e0\u52a8\u4e8e\u8877\uff1f \u3000\u3000\u9523\u58f0\u4e00\u6b62\uff0c\u53ea\u89c1\u4e00\u4e2a\u8d35\u5b98\u8d70\u8fdb\u5e99\u6765\uff0c\u5c0f\u516c\u7239\u9053\uff1a\u201c\u738b\u5c06\u519b\uff0c\u4f60\u4e5f\u6765\u8fdb\u9999\u5417\uff1f\u201d\u90a3\u8d35\u5b98 \u9053\uff1a\u201c\u5c0f\u516c\u7239\uff0c\u4f60\u8fd9\u573a\u529f\u5fb7\u9053\u5f97\u597d\u5440\u3002\u201d\u5411\u57ce\u968d\u50cf\u6253\u91cf\u4e86\u597d\u4e00\u4f1a\uff0c\u7b11\u9053\uff1a\u201c\u597d\u624b\u827a\uff0c\u523b\u5f97\u6829 \u6829\u5982\u751f\u3002\u4e3a\u4ec0\u4e48\u548c\u6211\u5728\u522b\u5904\u6240\u89c1\u7684\u57ce\u968d\u50cf\u4e0d\u540c\uff1f\u201d\u5c0f\u516c\u7239\u9053\uff1a\u201c\u4e00\u4e2a\u5730\u65b9\u6709\u4e00\u4e2a\u5730\u65b9\u7684\u57ce \u968d\uff0c\u8fd9\u6709\u4ec0\u4e48\u5947\u602a\uff1f\u201d\u90a3\u7389\u5c06\u519b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0f\u516c\u7239\u6b64\u8a00\uff0c\u771f\u662f\u4ee4\u6211\u5927\u5f00\u8305\u585e\uff0c\u539f\u6765\u57ce\u968d\u50cf \u4e5f\u662f\u56e0\u5730\u4e0d\u540c\u7684\u3002\u54c8\u54c8\uff0c\u8fd9\u5efa\u5e99\u9020\u50cf\uff0c\u662f\u6c90\u516c\u7239\u7684\u4e3b\u610f\u8fd8\u662f\u5c0f\u516c\u7239\u7684\u4e3b\u610f\uff1f\u201d\u5c0f\u516c\u7239\u6de1\u6de1\u8bf4 \u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u4e3b\u610f\uff0c\u6709\u4ec0\u4e48\u4e0d\u5bf9\u4e48\uff1f\u201d \u3000\u3000\u90a3\u738b\u5c06\u519b\u6ee1\u8138\u5978\u7b11\uff0c\u9053\uff1a\u201c\u597d\u6781\u4e86\uff0c\u5728\u86ee\u5937\u4e4b\u533a\uff0c\u539f\u4e0d\u59a8\u4ee5\u795e\u9053\u8bbe\u6559\uff0c\u8fd9\u662f\u5723\u4eba\u4e5f\u8bf4\u8fc7 \u7684\u3002\u201d\u65c1\u8fb9\u7684\u571f\u8457\u7ec5\u5546\uff0c\u542c\u90a3\u5c06\u519b\u8bf4\u4e91\u5357\u662f\u201c\u86ee\u5937\u4e4b\u533a\u201d\uff0c\u4e2a\u4e2a\u6012\u76ee\u800c\u89c6\u3002\u90a3\u4f4d\u738b\u5c06\u519b\u4f3c\u4e4e \u4e5f\u5bdf\u89c9\u5230\u81ea\u5df1\u7684\u5931\u8a00\uff0c\u6025\u5fd9\u5806\u6ee1\u7b11\u5bb9\uff0c\u8865\u4e0a\u4e00\u53e5\u9053\uff1a\u201c\u5144\u5f1f\u7684\u610f\u601d\uff0c\u54b3\uff0c\u54b3\uff0c\u5144\u5f1f\u7684\u610f\u601d\uff0c \u662f\u8bf4\u5c0f\u516c\u7239\u7684\u4f5c\u4e3a\uff0c\u9887\u5408\u5723\u8d24\u4e4b\u9053\u3002\u201d\u8fd9\u53e5\u8bdd\u53ef\u6367\u5f97\u6781\u4e3a\u7275\u5f3a\u3002\u90a3\u5c0f\u516c\u7239\u7b11\u9053\uff1a\u201c\u662f\u5417\uff1f \u597d\uff0c\u597d\uff01\u90a3\u4e48\u4f60\u4e5f\u8be5\u5411\u8fd9\u57ce\u968d\u53e9\u4e09\u4e2a\u5934\uff01\u201d\u90a3\u4e2a\u5c06\u519b\u540d\u53eb\u738b\u9547\u5357\uff0c\u8eab\u53d7\u5e73\u5357\u526f\u5c06\u519b\u4e4b\u804c\u3002\u4e91 \u5357\u7684\u519b\u653f\u5927\u6743\u4e00\u5411\u64cd\u4e8e\u6c90\u5bb6\u624b\u4e2d\uff0c\u201c\u5e73\u5357\u5c06\u519b\u201d\u4e5f\u662f\u89c4\u4efb\u7684\u201c\u9ed4\u56fd\u516c\u201d\u6c90\u742e\u81ea\u517c\uff0c\u8fd9\u4f4d\u526f\u5c06 \u519b\u867d\u662f\u671d\u5ef7\u6d3e\u6765\u7684\uff0c\u5176\u5b9e\u5f62\u540c\u201c\u4f34\u98df\u201d\uff0c\u6beb\u65e0\u5b9e\u6743\uff0c\u88ab\u5c0f\u516c\u7239\u6c90\u78f7\u5f3a\u4ed6\u5411\u57ce\u968d\u50cf\u53e9\u5934\uff0c\u5fc3\u91cc \u867d\u7136\u662f\u4e07\u5206\u7684\u4e0d\u613f\u610f\uff0c\u5374\u4e0d\u6562\u4e0d\u4f9d\uff0c\u679c\u7136\u8dea\u5012\u5730\u4e0a\uff0c\u4e56\u4e56\u5730\u53e9\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u7ad9\u8d77\u6765\u65f6\uff0c\u6ee1\u9762 \u5c34\u5c2c\u4e4b\u8272\uff0c\u4e8e\u627f\u73e0\u77a7\u5728\u773c\u91cc\uff0c\u5fc3\u4e2d\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u738b\u5c06\u519b\u4e00\u5b9a\u662f\u66fe\u7ecf\u89c1\u8fc7\u6211\u7684\u7236\u4eb2\uff0c\u54c8\u54c8\uff0c\u53eb \u4e00\u4e2a\u671d\u5ef7\u547d\u5b98\uff0c\u5411\u2018\u53db\u9006\u2019\u53e9\u5934\uff0c\u8fd9\u4f4d\u5c0f\u516c\u7239\u7684\u6076\u4f5c\u5267\u53ef\u771f\u4ee4\u4eba\u75db\u5feb\uff01\u201d \u3000\u3000\u90a3\u4f4d\u738b\u5c06\u519b\u642d\u8baa\u4e86\u51e0\u53e5\uff0c\u60bb\u60bb\u800c\u9000\u3002\u770b\u4ed6\u8d70\u51fa\u5e99\u95e8\uff0c\u91cc\u9762\u7684\u7ec5\u5546\u4eec\u7a83\u7a83\u5077\u7b11\u3002\u5c0f\u516c\u7239\u6c90 \u78f7\u62ac\u8d77\u773c\u775b\uff0c\u5728\u4eba\u4e1b\u91cc\u5bfb\u89c5\u4e8e\u627f\u73e0\uff0c\u5ffd\u542c\u5f97\u95e8\u5916\u53c8\u662f\u8083\u9759\u65e0\u54d7\uff0c\u8fdb\u9999\u53c2\u795e\u7684\u4eba\u4eec\u81ea\u52a8\u8ba9\u5f00\uff0c \u53ea\u89c1\u4e24\u4e2a\u4e2b\u9b13\u966a\u7740\u4e00\u4e2a\u5c0f\u59d0\u8d70\u4e0a\u53f0\u9636\uff0c\u6c90\u78f7\u6025\u5fd9\u8fce\u4e0a\u53bb\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u4e5f\u6765\u4e86\u3002\u201d\u8fd9\u4f4d\u5c0f\u59d0 \u6b63\u662f\u9ed4\u56fd\u516c\u6c90\u742e\u7684\u5973\u513f\u6c90\u71d5\u3002\u770b\u5979\u957f\u7709\u5165\u9b13\uff0c\u554a\u5a1c\u77eb\u67d4\uff0c\u5374\u662f\u6b65\u5c65\u5b89\u8be6\uff0c\u6c14\u5ea6\u9ad8\u534e\uff0c\u81ea\u6709\u5927 \u5bb6\u98ce\u8303\uff0c\u53ea\u89c1\u5979\u5148\u5411\u57ce\u968d\u50cf\u88e3\u5728\u65bd\u793c\uff0c\u7136\u540e\u5bf9\u6c90\u78f7\u8bf4\u9053\uff1a\u201c\u5f1f\u5f1f\uff0c\u4f60\u8ddf\u6211\u56de\u53bb\u5427\uff0c\u7239\u7239\u5728\u627e \u4f60\u5462\u3002\u201d\u6c90\u78f7\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u7239\u7239\u6709\u4ec0\u4e48\u8bf4\uff1f\u201d\u6c90\u71d5\u4f3c\u4e4e\u4e0d\u65b9\u4fbf\u5728\u6b64\u591a\u8bf4\uff0c\u5fae\u5fae\u7b11\u9053\uff1a \u201c\u90fd\u6709\u6211\u5462\uff0c\u4f60\u56de\u53bb\u5427\u3002\u201d\u5c06\u6c90\u78f7\u62c9\u51fa\u5e99\u95e8\uff0c\u4e8e\u627f\u73e0\u5728\u4eba\u4e1b\u91cc\u4e3e\u773c\u5077\u7aa5\uff0c\u4f46\u89c1\u5979\u7709\u5b87\u4e4b\u95f4\uff0c \u9690\u6709\u5fe7\u8272\u3002 \u3000\u3000\u6c90\u78f7\u3001\u6c90\u71d5\u4e00\u8d70\uff0c\u5e99\u91cc\u4e71\u5608\u5608\u7684\uff0c\u5916\u9762\u7684\u4eba\u4e5f\u4e89\u7740\u8fdb\u6765\u53c2\u795e\uff0c\u4e8e\u627f\u73e0\u4e0e\u53f6\u3001\u864e\u5b50\u4e58\u673a\u9000 \u8d70\uff0c\u4e8e\u627f\u73e0\u6697\u4e2d\u5077\u770b\uff0c\u90a3\u5356\u827a\u7684\u4e24\u7236\u5973\u8fd8\u7559\u5728\u5e99\u4e2d\uff0c\u4f3c\u4e4e\u5e76\u6ca1\u6709\u53d1\u73b0\u5979\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5982\u5728\u68a6\u4e2d\uff0c\u5bf9\u773c\u524d\u4e4b\u4e8b\uff0c\u5b9e\u662f\u767e\u601d\u83ab\u89e3\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u770b\u8fd9\u60c5\u5f62\uff0c\u542c\u90a3\u5c11\u5973\u7684\u8bed \u6c14\uff0c\u8fd9\u5efa\u5e99\u9020\u50cf\u4e4b\u4e8b\uff0c\u6c90\u56fd\u516c\u60f3\u6765\u4e8b\u5148\u672a\u77e5\u3002\u4f46\u8fd9\u5c0f\u516c\u7239\u5982\u6b64\u5e74\u8f7b\uff0c\u4ed6\u672a\u66fe\u89c1\u8fc7\u6211\u7684\u7239\u7239\uff0c \u53c8\u600e\u77e5\u9053\u6211\u7239\u7239\u7684\u76f8\u8c8c\u3002\u201d \u3000\u3000\u5c0f\u864e\u5b50\u6ee1\u6000\u7eb3\u95f7\uff0c\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u5f53\u771f\u4e0d\u662f\u4e2d\u6691\u5417\uff1f\u201d\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u4f60\u600e\u4e48\u80e1\u4e71\u5492 \u6211\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u6211\u770b\u4f60\u6709\u70b9\u5931\u5e38\uff0c\u521a\u624d\u597d\u7aef\u7aef\u7684\u600e\u4e48\u5728\u5e99\u91cc\u54ed\u8d77\u6765\u4e86\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u4f60\u770b\u4ed6\u4eec\u90a3\u6837\u5c0a\u656c\u57ce\u968d\uff0c\u6240\u4ee5\u53eb\u6211\u4e5f\u611f\u52a8\u4e86\u3002\u201d\u62bf\u5634\u4e00\u7b11\uff0c\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u4e00\u5b9a\u6709\u4ec0 \u4e48\u5fc3\u4e8b\uff0c\u7792\u7740\u4e0d\u544a\u8bc9\u6211\u3002\u201d\u4e8e\u627f\u73e0\u76b1\u7709\u9053\uff1a\u201c\u522b\u518d\u5728\u8fd9\u91cc\u80e1\u7f20\u5566\uff0c\u5c0f\u5b69\u5b50\u77e5\u9053\u4ec0\u4e48\u5927\u4eba\u5fc3 \u4e8b\uff1f\u8d76\u5feb\u56de\u53bb\u5403\u4e2d\u996d\u6b63\u7ecf\u3002\u201d \u3000\u3000\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff01\u4f60\u7b54\u5e94\u8fc7\u6211\uff0c\u4e0b\u5348\u53bb\u901b\u897f\u5c71\u7684\u3002\u541b\u5b50\u4e00\u8a00\u2026\u2026\u201d\u4e8e\u627f\u73e0\u7ed9\u4ed6\u901e\u5f97 \u7b11\u8d77\u6765\uff0c\u63a5\u7740\u4ed6\u7684\u53e3\u5934\u7985\u9053\uff1a\u201c\u5feb\u9a6c\u4e00\u97ad\uff01\u201d\u5c0f\u864e\u5b50\u7b11\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u8bf4\u8bdd\u7b97\u6570\uff0c\u4f60\u5feb\u5e26\u6211 \u53bb\u901b\u897f\u5c71\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u5c31\u4e0d\u997f\uff1f\u201d\u5c0f\u864e\u5b50\u563b\u563b\u7b11\u9053\uff1a\u201c\u6211\u888b\u91cc\u8fd8\u6709\u51e0\u5341\u6587\u94dc\u94b1\u5462\u3002\u201d \u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u7ed9\u90a3\u5356\u827a\u7684\u8001\u5934\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u6211\u662f\u8bda\u5fc3\u7559\u7ed9\u4f60\u5403\u5348\u996d\u7684\u5440\u3002\u6211 \u77a7\u4f60\u90a3\u4e2a\u6837\u513f\u5c31\u77e5\u9053\u4f60\u5fd8\u8bb0\u5e26\u94f6\u5b50\u4e86\u3002\u201d\u7b11\u563b\u563b\u5730\u62c9\u4e8e\u627f\u73e0\u5230\u4e00\u4e2a\u5c0f\u5e97\u5b50\u91cc\u5403\u4e86\u4e24\u7897\u7c73\u7ebf\uff0c \u888b\u91cc\u5c31\u53ea\u5269\u4e0b\u4e09\u679a\u94dc\u94b1\u4e86\u3002 \u3000\u3000\u8d70\u51fa\u57ce\u6765\uff0c\u5929\u65b9\u8fc7\u5348\uff0c\u4e07\u91cc\u65e0\u4e91\uff0c\u662f\u4e00\u4e2a\u5927\u597d\u7684\u6674\u5929\u3002\u4e8e\u627f\u73e0\u80f8\u6000\u8212\u7545\uff0c\u628a\u5fc3\u4e8b\u629b\u8fc7\u4e00 \u8fb9\uff0c\u5c3d\u60c5\u89c2\u8d4f\u5c71\u666f\u3002\u6606\u660e\u897f\u5c71\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u8d8a\u4e0a\u5c71\u52bf\u8d8a\u5947\u8d8a\u9669\uff0c\u4e00\u5230\u9f99\u95e8\uff0c\u66f4\u662f\u4ee4\u4eba\u60ca \u5fc3\u9a87\u76ee\uff0c\u90a3\u201c\u9f99\u95e8\u201d\u7adf\u662f\u4ece\u5c71\u5cf0\u4e0a\u51ff\u51fa\u6765\u7684\uff0c\u4ece\u4e0b\u671b\u4e0a\uff0c\u5ced\u58c1\u5343\u4e08\u3002\u4e0a\u9762\u7684\u5e99\u5b87\uff0c\u7adf\u4f3c\u51cc\u7a7a \u800c\u5efa\uff0c\u4e0b\u9762\u662f\u82cd\u832b\u65e0\u9645\u7684\u6ec7\u6c60\uff0c\u62fe\u7ea7\u800c\u4e0a\uff0c\u5c71\u98ce\u98de\u8863\uff0c\u5982\u767b\u4ed9\u5883\u3002\u4e8e\u627f\u73e0\u8d5e\u4e00\u526f\u5bf9\u8054\u9053\uff1a \u201c\u4ef0\u7b11\u5b9b\u79bb\u65e0\u5c3a\u4e94\uff0c\u51ed\u4e34\u6070\u5728\u6c34\u4e2d\u592e\u3002\u201d\u4e0b\u671b\u6ec7\u6c60\uff0c\u60a0\u7136\u795e\u5f80\u3002 \u3000\u3000\u9f99\u95e8\u7684\u6cbf\u5d16\u90fd\u51ff\u6210\u77f3\u5eca\uff0c\u8fc2\u56de\u66f2\u6298\uff0c\u6709\u7684\u5730\u65b9\uff0c\u4ec5\u5bb9\u4e00\u4eba\u4fa7\u8eab\u7a7f\u8fc7\uff0c\u5c0f\u864e\u5b50\u7b11\u9053\uff1a\u201c\u8fd9 \u5730\u65b9\u6700\u597d\u6349\u8ff7\u85cf\u3002\u201d\u4e8e\u627f\u73e0\u4e0d\u7981\u5931\u7b11\uff0c\u9053\uff1a\u201c\u5e26\u4f60\u6765\u901b\u897f\u5c71\uff0c\u4f60\u5374\u60f3\u6349\u8ff7\u85cf\uff0c\u5c82\u4e0d\u8f9c\u8d1f\u4e86\u8fd9 \u5929\u7136\u7f8e\u666f\u3002\u201d \u3000\u3000\u767b\u4e0a\u9f99\u95e8\uff0c\u53ea\u89c1\u4e00\u5e45\u58c1\u753b\uff0c\u753b\u4e2d\u4e00\u6761\u9ca4\u9c7c\uff0c\u51cc\u7a7a\u98de\u8dc3\uff0c\u4e0b\u534a\u8eab\u662f\u9c7c\u8eab\uff0c\u4e0a\u534a\u8eab\u5374\u662f\u9f99 \u76f8\uff0c\u4f20\u8bf4\u4e2d\u7684\u201c\u9ca4\u9c7c\u8dc3\u9f99\u95e8\u201d\uff0c\u4fbf\u662f\u8fd9\u4e2a\u6240\u5728\uff0c\u636e\u8bf4\u201c\u9f99\u95e8\u201d\u592a\u9ad8\u4e86\uff0c\u6240\u4ee5\u6ec7\u6c60\u4e2d\u7684\u9ca4\u9c7c\uff0c \u82e5\u80fd\u8dc3\u8fc7\u9f99\u95e8\uff0c\u4fbf\u53ef\u5316\u9f99\u5347\u5929\u3002\u5c0f\u864e\u5b50\u7b11\u9053\uff1a\u201c\u6211\u770b\uff0c\u5c31\u662f\u5929\u4e0b\u7684\u7b2c\u4e00\u7b49\u8f7b\u529f\uff0c\u4e5f\u96be\u4ee5\u8dc3\u8fc7 \u9f99\u95e8\uff01\u201d\u4e8e\u627f\u73e0\u53c8\u4e0d\u7981\u54d1\u7136\u5931\u7b11\uff0c\u4f46\u5374\u4e5f\u4f69\u670d\u4ed6\u5bf9\u6b66\u529f\u7684\u4e13\u5fc3\u6ce8\u610f\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u9ed1\u767d\u6469 \u8bc3\u8bf4\u4ed6\u662f\u4e2a\u6709\u6839\u57fa\u7684\u5b69\u5b50\uff0c\u5bf9\u6b66\u827a\u7b80\u76f4\u662f\u5165\u4e86\u8ff7\u3002\u201d \u3000\u3000\u9f99\u95e8\u4e0a\u8fd8\u6709\u4e2a\u9b41\u661f\u7684\u77f3\u96d5\u50cf\uff0c\u90a3\u662f\u7528\u6574\u5757\u77f3\u5934\u523b\u51fa\u6765\u7684\uff0c\u53ea\u6709\u624b\u91cc\u7684\u7b14\u5374\u662f\u6728\u7684\u3002\u4e8e\u627f \u73e0\u770b\u90a3\u9898\u8bb0\uff0c\u539f\u6765\u8fd9\u5728\u5ced\u58c1\u4e0a\u51ff\u51fa\u6765\u7684\u9f99\u95e8\uff0c\u7adf\u6709\u4e00\u4e2a\u54c0\u8273\u7edd\u4f26\u7684\u6545\u4e8b\u3002\u636e\u8bf4\u6709\u4f4d\u5c11\u5e74\uff0c\u56e0 \u4e3a\u5931\u6389\u4e86\u4ed6\u7684\u610f\u4e2d\u4eba\uff0c\u5fc3\u65e0\u5bc4\u6258\uff0c\u4fbf\u72ec\u81ea\u8dd1\u5230\u897f\u5c71\u4e0a\u53bb\u523b\u9f99\u95e8\uff0c\u662f\u60f3\u7559\u4e0b\u4e00\u4e2a\u80dc\u8ff9\uff0c\u7eaa\u5ff5\u4ed6 \u7684\u60c5\u4eba\u3002\u523b\u5230\u6700\u540e\u7684\u9b41\u661f\u50cf\u65f6\uff0c\u6ca1\u6709\u77f3\u5934\u9002\u5408\u523b\u9b41\u661f\u7684\u7b14\uff0c\u8fd9\u5c11\u5e74\u4e00\u751f\u81f4\u529b\u7684\u5de5\u4f5c\uff0c\u5c31\u5dee\u8fd9 \u4e00\u70b9\u70b9\u4e0d\u80fd\u5b8c\u6210\uff0c\u4f24\u5fc3\u5230\u4e86\u6781\u70b9\uff0c\u7adf\u4ece\u9f99\u95e8\u8dc3\u4e0b\uff0c\u4e27\u8eab\u6ec7\u6c60\u3002\u4e8e\u627f\u73e0\u8bfb\u4e86\u9898\u8bb0\uff0c\u53ea\u611f\u5230\u5fc3\u5934 \u4e00\u9635\u8ff7\u60d8\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u7684\u4f5c\u4e3a\u53c8\u6bd4\u9003\u7985\u7684\u5883\u754c\u66f4\u9ad8\u4e86\uff01\u5440\uff0c\u53ef\u60dc\u5728\u8fd9\u4e16\u4e0a\uff0c\u5b9e\u662f\u96be\u9022\u5177 \u6709\u8fd9\u6837\u771f\u60c5\u631a\u7231\u7684\u5c11\u5e74\uff01\u201d\u94c1\u955c\u5fc3\u7684\u5f71\u5b50\u7a81\u7136\u53c8\u4ece\u5979\u5fc3\u4e2d\u98d8\u8fc7\uff0c\u5979\u4fef\u77b0\u6ec7\u6c60\uff0c\u4f46\u89c1\u6ec7\u6c60\u4e0a\u7684 \u70b9\u70b9\u6d6e\u840d\uff0c\u5ffd\u5730\u88ab\u98ce\u5439\u6563\uff0c\u6c34\u5982\u65e0\u6570\u82b1\u74e3\uff0c\u4e5f\u5404\u81ea\u98d8\u96f6\uff0c\u5fc3\u4e2d\u66f4\u589e\u51c4\u695a\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u5ffd\u7136\u6084\u7136\u8bf4\u9053\uff1a\u201c\u542c\uff0c\u4e0b\u9762\u597d\u50cf\u6709\u4eba\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u81ea\u5c0f\u8ddf\u968f\u4e91\u857e\u7ec3\u91d1\u82b1\u6697\u4e8c\u5668\uff0c\u8033\u529b\u6781\u597d\uff0c\u53c8\u5b66\u8fc7\u201c\u4f0f\u5730\u542c\u58f0\u201d\u7684\u529f\u592b\uff0c\u5f53\u4e0b\u628a\u8033 \u8d34\u5728\u77f3\u58c1\u4e0a\u4e00\u542c\uff0c\u9f99\u95e8\u7684\u77f3\u5eca\u662f\u4ece\u5ced\u58c1\u4e0a\u51ff\u51fa\u6765\u7684\uff0c\u8fc2\u56de\u66f2\u6298\uff0c\u6570\u6b65\u4e4b\u5916\uff0c\u5f7c\u6b64\u4e0d\u89c1\uff0c\u4f46\u90a3 \u58f0\u97f3\u4ece\u77f3\u58c1\u4e0a\u4f20\u8fc7\u6765\uff0c\u867d\u7136\u7ec6\u5982\u868a\u53eb\uff0c\u5374\u662f\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e00\u4e2a\u4f4e\u6c89\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u738b\u5c06\u519b\u90d1\u91cd\u4ed8\u6258\uff0c\u8fd9\u5c01\u4fe1\u5173\u7cfb\u91cd\u8981\uff0c\u4f60\u4e00\u5b9a\u8981\u9001\u5230\u4eac \u4e2d\u3002\u201d\u53e6\u4e00\u4e2a\u58f0\u97f3\u9053\uff1a\u201c\u4ea4\u7ed9\u8c01\uff1f\u201d\u5148\u5934\u7684\u58f0\u97f3\u9053\uff1a\u201c\u7ed9\u5927\u5185\u603b\u7ba1\u9633\u5b97\u6d77\u3002\u82e5\u7136\u9633\u5b97\u6d77\u51fa\u5dee \u53bb\u4e86\uff0c\u5c31\u4ea4\u7ed9\u5fa1\u6797\u519b\u603b\u6307\u6325\u5a04\u6850\u5b59\u3002\u82e5\u7136\u4e24\u4eba\u90fd\u51fa\u5dee\u53bb\u4e86\uff0c\u5c31\u76f4\u63a5\u4ea4\u7ed9\u5bab\u5185\u7684\u738b\u516c\u516c\u3002\u201d\u90a3 \u4eba\u55ef\u4e86\u4e00\u58f0\uff0c\u8fc7\u4e86\u534a\u664c\u95ee\u9053\uff1a\u201c\u82e5\u662f\u9014\u4e2d\u78b0\u5230\u6c90\u516c\u7239\u7684\u4eba\u5462\uff1f\u201d\u5148\u5934\u7684\u58f0\u97f3\u7b54\u9053\uff1a\u201c\u80fd\u654c\u5219 \u654c\uff0c\u4e0d\u80fd\u654c\u5219\u8dd1\uff0c\u8dd1\u4e0d\u4e86\u5c31\u628a\u4e66\u4fe1\u56bc\u788e\u541e\u4e0b\uff0c\u603b\u4e4b\u4e0d\u80fd\u8ba9\u6b64\u4fe1\u843d\u5728\u4efb\u4f55\u4eba\u7684\u624b\u4e2d\u3002\u201d\u90a3\u4eba \u9053\uff1a\u201c\u54ce\u5440\uff0c\u8fd9\u53ef\u662f\u5356\u547d\u7684\u4e8b\u513f\uff0c\u6211\u53ef\u4e0d\u53ef\u4ee5\u56de\u5bb6\u4e00\u8f6c\uff0c\u544a\u522b\u59bb\u5b50\u3002\u201d\u5148\u5934\u90a3\u58f0\u97f3\u9053\uff1a\u201c\u5f20 \u8001\u5927\uff0c\u5e72\u54b1\u4eec\u8fd9\u4e00\u884c\u7684\u8fd8\u6015\u6b7b\u4e48\uff1f\u4f60\u4eca\u665a\u53ef\u5c31\u5f97\u7acb\u523b\u52a8\u8eab\uff0c\u5ac2\u5b50\u6709\u6211\u7167\u6599\uff0c\u4f60\u4e0d\u5fc5\u62c5\u5fc3\u3002\u201d \u8bf4\u5230\u6b64\u5904\uff0c\u4e24\u4eba\u518d\u65e0\u8a00\u8bed\uff0c\u53ea\u542c\u5f97\u811a\u6b65\u58f0\u4ece\u91cc\u9762\u8d70\u51fa\u6765\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u738b\u5c06\u519b\u5b9a\u662f\u4eca\u65e5\u5230\u57ce\u968d\u5e99\u7684\u90a3\u4e2a\u5b98\u513f\uff0c\u53ea\u8fd9\u4e48\u4e00\u4f1a\u513f\u5de5 \u592b\uff0c\u4ed6\u5c31\u628a\u5bc6\u4fe1\u5199\u597d\u4e86\uff01\u542c\u8fd9\u8bed\u6c14\uff0c\u770b\u6765\u8fd9\u5c01\u4fe1\u5b9a\u662f\u5bf9\u6c90\u516c\u7239\u6709\u6240\u4e0d\u5229\u3002\u201d\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4e3b\u610f \u5df2\u51b3\uff0c\u8ddf\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u73a9\u5f97\u591f\u4e86\uff0c\u54b1\u4eec\u8be5\u56de\u53bb\u5566\uff01\u201d \u3000\u3000\u77f3\u5eca\u91cc\u90a3\u4e24\u4e2a\u5bb6\u4f19\u5ffd\u7136\u542c\u5f97\u6709\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u4e8e\u627f\u73e0\u548c\u5c0f\u864e\u5b50\u8d70\u8fdb\u77f3\u5eca\uff0c\u4e24 \u4eba\u4e00\u671b\uff0c\u89c1\u662f\u4e00\u4e2a\u5c11\u5973\u548c\u4e00\u4e2a\u5b69\u5b50\uff0c\u53ea\u5f53\u4ed6\u4eec\u662f\u6765\u6e38\u5c71\u7684\u59d0\u5f1f\u4e24\u4eba\uff0c\u653e\u4e0b\u4e86\u5fc3\uff0c\u90a3\u4e2a\u5f20\u8001\u5927 \u662f\u4e2a\u597d\u8272\u4e4b\u4eba\uff0c\u89c1\u4e8e\u627f\u73e0\u4e3d\u8d28\u5929\u751f\uff0c\u6545\u610f\u8fc8\u524d\u4e24\u6b65\uff0c\u5835\u7740\u77f3\u5eca\u7684\u72ed\u7a84\u7684\u901a\u9053\uff0c\u563b\u563b\u7b11\u9053\uff1a \u201c\u5c0f\u59d1\u5a18\uff0c\u8fd9\u58c1\u771f\u4e0d\u597d\u8d70\uff0c\u8981\u4e0d\u8981\u3002\u6211\u6276\u4f60\u4e00\u628a\u3002\u201d \u3000\u3000\u5c0f\u864e\u5b50\u4e00\u4e2a\u7bad\u6b65\u8df3\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u8ba9\u5f00\uff01\u201d\u80a9\u5934\u4e00\u649e\uff0c\u5de6\u62f3\u4ece\u8098\u5e95\u7a7f\u8fc7\uff0c\u5c31\u60f3\u6765\u4ed6\u4e00\u62db \u201c\u9f99\u62f3\u201d\uff0c\u4e8e\u627f\u73e0\u6025\u5fd9\u5c06\u5c0f\u864e\u5b50\u4e00\u626f\u626f\u5f00\uff0c\u90a3\u4eba\u88ab\u5c0f\u864e\u5b50\u4e00\u78b0\uff0c\u7565\u4e00\u4fa7\u8eab\uff0c\u6b63\u60f3\u65bd\u5c55\u64d2\u62ff\u624b \u7684\u529f\u592b\uff0c\u5c06\u5c0f\u864e\u5b50\u6454\u5230\u77f3\u58c1\u4e0a\uff0c\u5ffd\u89c9\u4e00\u9635\u9999\u98ce\uff0c\u4e8e\u627f\u73e0\u5df2\u662f\u548c\u4ed6\u6328\u80a9\u64e6\u8fc7\uff0c\u90a3\u4eba\u5fc3\u9b44\u4e00\u8361\uff0c \u4f38\u624b\u53bb\u62c9\uff0c\u5374\u6ca1\u6709\u62c9\u7740\uff0c\u4ed6\u7684\u540c\u4f34\u6025\u5fd9\u6b62\u7740\u4ed6\u9053\uff1a\u201c\u5f20\u8001\u5927\uff0c\u522b\u80e1\u95f9\u5566\u3002\u201d\u5f20\u8001\u5927\u88ab\u4ed6\u7684\u540c \u4f34\u559d\u7740\uff0c\u60bb\u60bb\u9a82\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u8fd9\u4e2a\u5c0f\u86ee\u725b\uff0c\u8981\u4e0d\u662f\u78b0\u4e0a\u4eca\u5929\u6709\u4e8b\uff0c\u5b9a\u8981\u6367\u4f60\u4e00\u987f\uff01\u201d\u5c0f\u864e\u5b50 \u56de\u5934\u8fd8\u9a82\u9053\uff1a\u201c\u597d\u5440\uff0c\u5c0f\u7237\u6b63\u60f3\u6253\u67b6\uff01\u201d\u4e8e\u627f\u73e0\u5fd9\u628a\u5c0f\u864e\u5b50\u62c9\u5f00\uff0c\u8d54\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u5f1f\u5f1f\u662f \u6709\u70b9\u725b\u6c14\uff0c\u8bf7\u4f60\u4eec\u4e24\u4f4d\u5927\u4eba\u4e0d\u8981\u89c1\u602a\u5b69\u5b50\u3002\u201d\u90a3\u4e2a\u5f20\u8001\u5927\u542c\u5f97\u975e\u5e38\u8212\u670d\uff0c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u8fd9 \u4e2a\u5c0f\u599e\u513f\u5f88\u597d\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\u3002\u201d\u4e8e\u627f\u73e0\u53ea\u5f53\u4e0d\u542c\u89c1\uff0c\u5728\u4ed6\u8bf4\u8bdd\u7684\u5f53\u513f\uff0c\u5df2\u62c9\u7740\u5c0f\u864e\u5b50\u8d70\u51fa \u77f3\u5eca\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u751a\u662f\u4e0d\u5e73\uff0c\u5411\u4e8e\u627f\u73e0\u53d1\u4f5c\u9053\uff1a\u201c\u90a3\u4e2a\u5bb6\u4f19\u80c6\u6562\u6b3a\u8d1f\u4f60\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u6211\u6253\u4ed6\u4e00 \u987f\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8981\u6253\u4ed6\u6211\u4e0d\u4f1a\u6253\u5417\uff1f\u5feb\u8d70\uff01\u201d\u5c0f\u864e\u5b50\u6ee1\u809a\u95f7\u6c14\uff0c\u4f46\u89c1\u4e8e\u627f\u73e0\u58f0\u8272\u4ff1\u5389\uff0c\u5374 \u662f\u4e0d\u6562\u8fdd\u62d7\uff0c\u53ea\u5f97\u63d0\u8d77\u811a\u6b65\uff0c\u8ddf\u7740\u4e8e\u627f\u73e0\u5feb\u8dd1\u3002 \u3000\u3000\u8fd8\u672a\u8dd1\u81f3\u201c\u4e09\u6e05\u9601\u201d\uff0c\u53ea\u89c1\u90a3\u4e24\u4e2a\u5bb6\u4f19\u5df2\u6c14\u547c\u547c\u5730\u8ffd\u4e86\u4e0a\u6765\uff0c\u7834\u53e3\u9a82\u9053\uff1a\u201c\u4e24\u4e2a\u5c0f\u8d3c\uff0c \u7ed9\u6211\u7ad9\u7740\uff01\u201d\u539f\u6765\u4e8e\u627f\u73e0\u9002\u624d\u5728\u4e0e\u90a3\u4e2a\u5f20\u8001\u5927\u6328\u80a9\u64a9\u8fc7\u7684\u5239\u90a3\uff0c\u5df2\u65bd\u5c55\u4e86\u7a7a\u7a7a\u5999\u624b\uff0c\u5c06\u90a3\u5c01 \u5bc6\u4fe1\u5077\u5230\u624b\u4e2d\u3002\u8fd9\u624b\u529f\u592b\uff0c\u6b63\u662f\u5f20\u4e39\u67ab\u6240\u4f20\u7684\u7edd\u6280\u4e4b\u4e00\u3002\u5f53\u5e74\u5f20\u4e39\u67ab\u521d\u9047\u4e91\u857e\u4e4b\u65f6\uff0c\u5c31\u66fe\u65bd \u5c55\u8fc7\u8fd9\u4e00\u624b\u7edd\u6280\uff0c\u5c06\u5979\u7684\u94f6\u5b50\u5077\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u548c\u4e91\u857e\u5f00\u4e86\u4e2a\u5927\u5927\u7684\u73a9\u7b11\u3002\u5f20\u4e39\u67ab\u8bf4\u8fd9\u4e0d\u662f\u6b63 \u6d3e\u7684\u6b66\u529f\uff0c\u672c\u6765\u4e0d\u60f3\u4f20\u7ed9\u4e8e\u627f\u73e0\u7684\uff0c\u4f46\u4e8e\u627f\u73e0\u542c\u4e86\u5e08\u7236\u5f53\u5e74\u620f\u800d\u5e08\u6bcd\u7684\u6545\u4e8b\uff0c\u7f20\u7740\u8981\u5b66\uff0c\u60f3 \u4e0d\u5230\u5374\u5728\u4eca\u65e5\u6d3e\u4e86\u7528\u573a\u3002 \u3000\u3000\u90a3\u5f20\u8001\u5927\u4e5f\u7b97\u673a\u7075\uff0c\u4e8e\u627f\u73e0\u4e00\u8d70\uff0c\u4ed6\u731b\u5730\u60f3\u8d77\uff1a\u201c\u4e00\u4e2a\u5c0f\u5b69\u5b50\u4e3a\u4ec0\u4e48\u4f1a\u649e\u5f97\u6211\u80a9\u5934\u4f5c \u75db\uff1f\u201d\u4e00\u6478\u6000\u4e2d\uff0c\u53d1\u73b0\u5931\u4e86\u4fe1\u4ef6\uff0c\u8fd9\u4e00\u6025\u975e\u540c\u5c0f\u53ef\uff0c\u5fd9\u4e0e\u540c\u4f34\u8ffd\u8d76\uff0c\u53ea\u89c1\u4e8e\u627f\u73e0\u4e0e\u5c0f\u864e\u5b50\u4e0d \u8d70\u6b63\u8def\uff0c\u5df2\u7ed5\u8fc7\u4e09\u6e05\u9601\u5411\u540e\u9762\u5954\u4e0a\u5c71\u53bb\uff0c\u5f20\u8001\u5927\u5012\u62bd\u4e86\u4e00\u53e3\u51b7\u6c14\uff0c\u770b\u4e8e\u627f\u73e0\u8fd9\u8eab\u8f7b\u529f\uff0c\u7adf\u662f \u5728\u81ea\u5df1\u4e4b\u4e0a\u3002 \u3000\u3000\u8fd9\u5f20\u8001\u5927\u672c\u662f\u4eac\u4e2d\u7684\u4e00\u4e2a\u5f85\u536b\uff0c\u540d\u53eb\u5f20\u5927\u6d2a\uff0c\u88ab\u6d3e\u5728\u6606\u660e\uff0c\u5bdf\u4f3a\u6c90\u56fd\u516c\u7684\uff0c\u4e3a\u6015\u8d77\u7591\uff0c \u6240\u4ee5\u5c06\u5bb6\u5c0f\u4e5f\u5e26\u4e86\u6765\uff0c\u88c5\u4f5c\u4e00\u5bb6\u666e\u901a\u7684\u6c11\u5c45\u3002\u4ed6\u7684\u540c\u4f34\u540d\u53eb\u738b\u91d1\u6807\uff0c\u5374\u662f\u5f81\u5357\u526f\u5c06\u519b\u738b\u9547\u5357 \u624b\u4e0b\u7684\u4e00\u4e2a\u4eb2\u4fe1\uff0c\u539f\u6765\u4e5f\u662f\u4eac\u4e2d\u7684\u4f8d\u536b\uff0c\u8ddf\u738b\u9547\u5357\u6765\u8d1f\u76d1\u89c6\u6c90\u742e\u4e4b\u4efb\u3002\u6c90\u5bb6\u867d\u7136\u5386\u4ee3\u6548\u5fe0\uff0c \u6781\u5f97\u5386\u671d\u7687\u5e1d\u4fe1\u4efb\uff0c\u4f46\u7687\u5e1d\u5fc5\u987b\u6d3e\u4eba\u76d1\u89c6\u5404\u7701\u7684\u5c01\u7586\u5927\u540f\uff0c\u4e43\u662f\u660e\u671d\u884c\u4e4b\u5df2\u4e45\u7684\u5236\u5ea6\uff0c\u5e76\u975e \u4e91\u5357\u4e00\u7701\u4e3a\u7136\u3002\u738b\u9547\u5357\u5230\u6606\u660e\u4f5c\u6c90\u742e\u7684\u526f\u5c06\uff0c\u5df2\u6709\u5341\u591a\u5e74\uff0c\u4ece\u672a\u53d1\u73b0\u8fc7\u534a\u70b9\u53ef\u7591\u4e4b\u8ff9\uff0c\u5f20\u5927 \u6d2a\u4e0e\u738b\u91d1\u6807\u6b63\u6101\u6ca1\u6709\u5efa\u529f\u7684\u673a\u4f1a\uff0c\u4f1a\u8001\u6b7b\u4e91\u5357\uff0c\u60f3\u4e0d\u5230\u5374\u51fa\u4e86\u4e00\u6869\u5c0f\u516c\u7239\u4e3a\u4e8e\u8c26\u9020\u50cf\uff0c\u5949\u4e3a \u57ce\u968d\u7684\u4e8b\u60c5\uff0c\u6b63\u597d\u501f\u4e8b\u751f\u975e\uff0c\u9080\u529f\u56fe\u8d4f\u3002\u6240\u4ee5\u738b\u9547\u5357\u7acb\u523b\u5199\u597d\u594f\u6298\uff0c\u53eb\u738b\u91d1\u6807\u5077\u5077\u4ea4\u7ed9\u5f20\u5927 \u6d2a\uff0c\u54ea\u6599\u4e8b\u6709\u51d1\u5de7\uff0c\u5374\u504f\u504f\u78b0\u5230\u4e86\u4e8e\u627f\u73e0\uff0c\u5bc6\u4ef6\u7adf\u7136\u7ed9\u4e8e\u627f\u73e0\u5077\u53bb\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u90a3\u201c\u767b\u840d\u6e21\u6c34\u201d\u7684\u8f7b\u529f\u7edd\u6280\uff0c\u867d\u7136\u4ee4\u4ed6\u4eec\u5927\u5403\u4e00\u60ca\uff0c\u4f46\u4ed6\u4eec\u54ea\u80af\u5c31\u6b64\u5e72\u4f11\uff0c\u4ecd\u7136 \u62fc\u547d\u8ffd\u8d76\u3002\u5c0f\u864e\u5b50\u7684\u5185\u529f\u6839\u5e95\u751a\u597d\uff0c\u8f7b\u529f\u5374\u975e\u6240\u957f\uff0c\u8dd1\u4e86\u4e00\u4f1a\uff0c\u8ddd\u79bb\u6e10\u6e10\u7f29\u77ed\uff0c\u4e8e\u627f\u73e0\u4e0d\u5f97 \u4e0d\u653e\u6162\u811a\u6b65\u7b49\u4ed6\uff0c\u5f20\u5927\u6d2a\u628a\u5c0f\u864e\u5b50\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u8ffd\u5230\u4e09\u4e08\u5de6\u53f3\uff0c\u4e00\u6298\u624b\u4fbf\u53d1\u51fa\u4e24\u652f\u74e6\u9762\u900f \u98ce\u9556\uff0c\u4ed6\u5728\u8fd9\u6697\u5668\u4e0a\u4e0b\u8fc7\u5341\u5e74\u5de5\u592b\uff0c\u767e\u53d1\u767e\u4e2d\uff0c\u54ea\u77e5\u5c0f\u864e\u5b50\u6e9c\u6ed1\u975e\u5e38\uff0c\u542c\u98ce\u8fa8\u5668\uff0c\u8eab\u8eaf\u4e00 \u77ee\uff0c\u94bb\u4eba\u8305\u8349\u4e1b\u4e2d\uff0c\u950b\u950b\u4e24\u58f0\uff0c\u4e24\u652f\u9556\u90fd\u6253\u5728\u77f3\u4e0a\uff0c\u5c0f\u864e\u5b50\u54c8\u54c8\u5927\u7b11\uff0c\u94bb\u51fa\u6765\u9053\uff1a\u201c\u6ca1\u6253 \u7740\uff01\u201d\u56de\u5934\u8fd8\u626e\u4e86\u4e00\u4e2a\u9b3c\u8138\u3002\u4f46\u7ecf\u8fc7\u8fd9\u6837\u4e00\u4f1a\u513f\u95ea\u8eb2\u7684\u5de5\u592b\uff0c\u5f20\u5927\u6d2a\u5df2\u8ffd\u5230\u4ed6\u80cc\u540e\u4e00\u4e08\u4e4b \u5730\uff0c\u731b\u5730\u7eb5\u8eab\u98de\u8d77\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u8d3c\u8fd8\u60f3\u8d70\u5417\uff1f\u201d\u4e00\u62db\u201c\u82cd\u9e70\u6251\u5154\u201d\uff0c\u7adf\u662f\u6cb3\u5317\u5cb3\u5bb6\u201c\u4e94\u64d2 \u638c\u201d\u7684\u529f\u592b\u3002\u4e8e\u627f\u73e0\u8ddd\u79bb\u5c0f\u864e\u5b50\u5728\u5341\u4e08\u5f00\u5916\uff0c\u56de\u8eab\u6765\u6551\uff0c\u5df2\u662f\u4e0d\u53ca\u3002 \u3000\u3000\u5f20\u5927\u6d2a\u51fa\u5c71\u4ee5\u6765\uff0c\u66fe\u7528\u8fd9\u201c\u4e94\u64d2\u638c\u201d\u6cd5\u4f24\u8fc7\u4e0d\u5c11\u597d\u624b\uff0c\u6ee1\u4ee5\u4e3a\u5c0f\u864e\u5b50\u5b9a\u7136\u96be\u9003\u638c\u4e0b\uff0c\u5982 \u5ffd\u542c\u5f97\u5c0f\u864e\u5b50\u563b\u563b\u7b11\u9053\uff1a\u201c\u4f60\u5c3d\u7f20\u7740\u5c0f\u7237\u4e5e\u8ba8\uff0c\u6ca1\u8bdd\u8bf4\uff0c\u5c0f\u7237\u53ea\u597d\u628a\u8eab\u4e0a\u8fd9\u51e0\u4e2a\u94dc\u94b1\u90fd\u65bd\u820d \u7ed9\u4f60\u5566\uff01\u201d\u9661\u7136\u95f4\u94ee\u94ee\u6570\u58f0\uff0c\u5c0f\u864e\u5b50\u628a\u8eab\u4e0a\u4ec5\u5269\u7684\u4e09\u679a\u94dc\u94b1\uff0c\u7528\u8f6e\u6307\u624b\u6cd5\u4e00\u4e0b\u5f39\u51fa\uff0c\u5f53\u4f5c \u201c\u91d1\u94b1\u9556\u201d\u4f7f\u7528\uff0c\u5206\u6253\u5f20\u5927\u6d2a\u5934\u4e0a\u7684\u201c\u592a\u9633\u7a74\u201d\uff0c\u80f8\u819b\u7684\u201c\u63b0\u5de9\u7a74\u201d\uff0c\u548c\u811a\u8ddf\u7684\u201c\u6d8c\u6cc9 \u7a74\u201d\u3002\u201c\u592a\u9633\u7a74\u201d\u548c\u201c\u7487\u7391\u7a74\u201d\u90fd\u662f\u81f4\u547d\u7684\u7a74\u9053\uff0c\u4e5f\u4e8f\u5f97\u5f20\u5927\u6d2a\u6b66\u529f\u4e0d\u5f31\uff0c\u4eba\u5728\u7a7a\u4e2d\uff0c\u5c45\u7136 \u80fd\u628a\u201c\u4e94\u64d2\u638c\u201d\u6cd5\u786c\u4f7f\u5f00\u6765\uff0c\u63a5\u4e86\u5c0f\u864e\u5b50\u6253\u6765\u5954\u5411\u4ed6\u4e0a\u58e4\u4e2d\u76d8\u7684\u4e24\u679a\u94dc\u94b1\uff0c\u4f46\u4e3a\u4e86\u5168\u529b\u9632\u62a4 \u201c\u592a\u9633\u7a74\u201d\u548c\u201c\u7487\u7391\u7a74\u201d\u811a\u8ddf\u7684\u201c\u6d8c\u6cc9\u7a74\u201d\u5374\u7ed9\u94dc\u94b1\u6253\u4e2a\u6b63\u7740\uff0c\u7acb\u523b\u8dcc\u5012\u5c18\u57c3\uff0c\u773c\u6cea\u76f4\u6d41\uff0c \u5c0f\u864e\u5b50\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u6211\u4e0d\u6740\u4f60\uff0c\u4f60\u54ed\u4ec0\u4e48\uff1f\u725b\u9ad8\u9a6c\u5927\uff0c\u6cea\u6c6a\u6c6a\u7684\uff0c\u4f60\u7f9e\u4e0d\u7f9e\uff1f\u201d\u6d8c\u6cc9\u7a74\u88ab\u6253 \u4e2d\u5fc5\u7136\u6d41\u6cea\uff0c\u5c0f\u864e\u5b50\u5c82\u6709\u4e0d\u77e5\uff1f\u4ed6\u4e43\u662f\u6545\u610f\u5411\u654c\u4eba\u6316\u82e6\u3002 \u3000\u3000\u738b\u91d1\u6807\u4e00\u58f0\u5927\u543c\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u98de\u63a0\u4e08\u8bb8\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u671d\u6211\u6765\u5427\u3002\u201d\u9661\u5730\u62d4\u51fa\u4e00\u652f \u5224\u5b98\u7b14\uff0c\u5411\u5c0f\u864e\u5b50\u8eab\u4e0a\u7684\u5927\u7a74\u75be\u70b9\uff0c\u4ed6\u662f\u6cb3\u5317\u7684\u6253\u7a74\u540d\u5bb6\uff0c\u53c8\u5584\u63a5\u6697\u5668\uff0c\u7acb\u5fc3\u8981\u70b9\u5012\u5c0f\u864e\u5b50 \u7ed9\u540c\u4f34\u6cc4\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u6211\u8eab\u4e0a\u4e0d\u540d\u4e00\u6587\uff0c\u4f60\u600e\u4e48\u8fd8\u5411\u6211\u4e5e\u8ba8\uff01\u59d0\u59d0\uff0c\u4f60\u7ed9\u6211\u6253\u53d1\u4ed6\uff01\u201d\u8fd9 \u4e00\u77ac\u95f4\uff0c\u5c0f\u864e\u5b50\u5df2\u63a5\u8fde\u904f\u4e86\u51e0\u6b21\u9669\u62db\uff0c\u738b\u91d1\u6807\u7684\u5224\u5b98\u7b14\uff0c\u75be\u53d1\u5982\u98ce\uff0c\u628a\u5c0f\u864e\u5b50\u903c\u5f97\u56e2\u56e2\u4e71 \u8f6c\uff0c\u773c\u89c1\u4ed6\u7b14\u5c16\u4e00\u8d77\uff0c\u76f4\u6307\u5230\u4e86\u5c0f\u864e\u5b50\u7684\u524d\u5fc3\uff0c\u5ffd\u542c\u5f97\u4e8e\u627f\u73e0\u6e05\u8106\u7684\u7b11\u58f0\u53eb\u9053\uff1a\u201c\u597d\uff0c\u6211\u7ed9 \u4f60\u8d4f\u4ed6\u91d1\u5b50\uff01\u201d\u738b\u91d1\u6807\u53ea\u89c1\u773c\u524d\u91d1\u5149\u75be\u95ea\uff0c\u6025\u628a\u5224\u5b98\u7b14\u62db\u67b6\uff0c\u4f46\u542c\u5f97\u94ee\u94ee\u4e24\u58f0\uff0c\u4e8e\u627f\u73e0\u7684\u4e24 \u6735\u91d1\u82b1\u7ed9\u4ed6\u7684\u5224\u5b98\u7b14\u78b0\u98de\uff0c\u738b\u91d1\u6807\u6b63\u60f3\u8bf4\u4e24\u53e5\u4fcf\u76ae\u8bdd\uff0c\u5ffd\u5730\u90a3\u4e24\u6735\u91d1\u82b1\u5728\u7a7a\u4e2d\u4e00\u8f6c\uff0c\u659c\u98de\u5c04 \u4e0b\uff0c\u6765\u52bf\u66f4\u6025\uff0c\u738b\u91d1\u6807\u5584\u6321\u6697\u5668\uff0c\u5374\u8fd8\u672a\u89c1\u8fc7\u8fd9\u79cd\u6253\u6cd5\uff0c\u731d\u4e0d\u53ca\u9632\uff0c\u4e24\u6735\u91d1\u82b1\u90fd\u6253\u4e2d\u4e86\u4ed6\u7684 \u7a74\u9053\uff0c\u767b\u65f6\u6655\u5012\u3002\u5c0f\u864e\u5b50\u7b11\u9053\uff1a\u201c\u4ed6\u54ea\u503c\u5f97\u4f60\u8d4f\u4ed6\u91d1\u5b50\u3002\u201d\u5c06\u91d1\u82b1\u53d6\u56de\uff0c\u53c8\u5411\u5f20\u5927\u6d2a\u7684\u8f6f\u9ebb \u7a74\u91cd\u91cd\u5730\u8e22\u4e86\u4e00\u811a\uff0c\u8fd9\u624d\u80af\u8ddf\u4e8e\u627f\u73e0\u4e0b\u5c71\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u8bd5\u7528\u963f\u8428\u739b\u5144\u5f1f\u53d1\u91d1\u7403\u7684\u624b\u6cd5\uff0c\u679c\u7136\u4e00\u4e3e\u594f\u6548\uff0c\u751a\u662f\u9ad8\u5174\u3002\u56de\u5230\u65c5\u820d\uff0c\u5173\u4e0a\u623f \u95e8\uff0c\u62c6\u5f00\u90a3\u5c01\u5bc6\u4fe1\uff0c\u5374\u662f\u4e00\u5fe7\u3002\u539f\u6765\u90a3\u5c01\u594f\u6298\u679c\u7136\u662f\u5bc6\u62a5\u6c90\u5c0f\u516c\u7239\u7ed9\u4e8e\u8c26\u5efa\u5e99\u9020\u50cf\u4e4b\u4e8b\uff0c\u594f \u6298\u8fd8\u62df\u597d\u6761\u9648\uff0c\u53eb\u7687\u4e0a\u5ba3\u53ec\u6c90\u5c0f\u516c\u7239\u5165\u4eac\uff0c\u5c06\u4ed6\u5e9f\u4e3a\u5eb6\u4eba\uff0c\u53e6\u9009\u6c90\u5bb6\u7684\u5b50\u4f84\uff0c\u7acb\u4e3a\u56fd\u516c\u3002\u53e6 \u5916\u6709\u51e0\u4e2a\u6761\u9648\uff0c\u662f\u524a\u6c90\u56fd\u516c\u6743\u529b\u7684\u529e\u6cd5\u3002\u4e8e\u627f\u73e0\u56e0\u4e3a\u6c90\u78f7\u7ed9\u5979\u7236\u4eb2\u9020\u50cf\uff0c\u5bf9\u4e4b\u9887\u6709\u597d\u611f\uff0c\u62ff \u4e86\u8fd9\u5c01\u4fe1\uff0c\u4e00\u65f6\u60f3\u4e0d\u51fa\u5999\u7f6e\u4e4b\u6cd5\u3002 \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u8fd8\u6ca1\u6709\u6765\u5230\uff0c\u4e8e\u627f\u73e0\u65e0\u4eba\u5546\u91cf\uff0c\u95f7\u4e60\u4e0d\u4e50\uff0c\u5403\u8fc7\u665a\u996d\uff0c\u4fbf\u8eba\u5728\u623f\u4e2d\uff0c\u5c0f\u864e\u5b50\u542c \u8bf4\u4e91\u5357\u7684\u201c\u82b1\u706f\u620f\u201d\u597d\u770b\uff0c\u9080\u5979\u53bb\u770b\uff0c\u5979\u4e5f\u63d0\u4e0d\u8d77\u5174\u8da3\u3002\u9ec4\u660f\u4e4b\u540e\u597d\u4e00\u4f1a\u5b50\uff0c\u5927\u7ea6\u662f\u76f8\u8fd1\u4e8c \u66f4\u7684\u65f6\u5206\uff0c\u65c5\u820d\u4e3b\u4eba\u5ffd\u7136\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u5916\u9762\u6709\u4e00\u4e2a\u4eba\u8981\u6765\u6c42\u89c1\u4e8e\u59d1\u5a18\uff0c\u95ee\u4e8e\u59d1\u5a18\u89c1\u662f\u4e0d \u89c1\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u662f\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d\u8001\u638c\u67dc\u9053\uff1a\u201c\u662f\u4e00\u4e2a\u6f02\u4eae\u7684\u76f8\u516c\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u5c31\u53ea\u4e00\u4e2a\u4eba\u5417\uff1f\u201d\u8001\u638c\u67dc\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u53ea\u4ed6\u4e00\u4e2a\u4eba\u3002\u201d\u4e8e\u627f\u73e0\u5927\u4e3a\u8be7\u5f02\uff0c\u521d\u65f6\u5979\u8fd8\u4ee5\u4e3a \u662f\u9ed1\u767d\u6469\u8bc3\u5bfb\u6765\uff0c\u540e\u6765\u53c8\u4ee5\u4e3a\u662f\u6bb5\u6f84\u82cd\uff0c\u4f46\u6bb5\u6f84\u82cd\u65ad\u65e0\u4e00\u4eba\u524d\u6765\u4e4b\u7406\uff0c\u6c89\u541f\u534a\u664c\uff0c\u60f3\u9053\uff1a \u201c\u8fd9\u4e2a\u5730\u65b9\u600e\u4e48\u4f1a\u6709\u4eba\u8ba4\u5f97\u6211\uff1f\u201d\u638c\u67dc\u7684\u9053\uff1a\u201c\u90a3\u4f4d\u76f8\u516c\u770b\u6765\u4eba\u5f88\u6b63\u6d3e\uff0c\u4e8e\u59d1\u5a18\u89c1\u662f\u4e0d \u89c1\uff1f\u201d\u4e91\u5357\u7684\u7537\u5973\u5927\u9632\u867d\u7136\u6ca1\u6709\u4e2d\u539f\u4e25\u8c28\uff0c\u4f46\u4e00\u4e2a\u5c11\u5e74\u91cc\u5b50\u591c\u95f4\u5230\u65c5\u820d\u53bb\u62dc\u4f1a\u4e00\u4e2a\u5355\u8eab\u5973 \u5ba2\uff0c\u4e8b\u60c5\u5374\u4e5f\u5e76\u975e\u5bfb\u5e38\uff0c\u90a3\u8001\u638c\u67dc\u53d7\u4e86\u6765\u4eba\u7684\u539a\u793c\uff0c\u7ed9\u5979\u5c3d\u8bf4\u597d\u8bdd\uff0c\u4e8e\u627f\u73e0\u6c89\u541f\u534a\u664c\u9053\uff1a \u201c\u597d\u5427\uff0c\u90a3\u5c31\u8bf7\u8fd9\u4f4d\u76f8\u516c\u8fdb\u6765\u3002\u201d \u3000\u3000\u638c\u67dc\u7684\u4e00\u8d70\uff0c\u5c0f\u864e\u5b50\u4fbf\u7b11\u563b\u563b\u5730\u7f9e\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e00\u4e2a\u6f02\u4eae\u7684\u76f8\u516c\uff01\u563b\u563b\uff0c\u539f\u6765\u59d0\u59d0\u7684\u610f \u4e2d\u4eba\u5728\u8fd9\u513f\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u770b\u6211\u4e0d\u6495\u7834\u4f60\u7684\u5634\u3002\u201d\u9762\u8272\u4e00\u7aef\uff0c\u9053\uff1a\u201c\u6b64\u4eba\u6df1\u591c \u6c42\u89c1\uff0c\u5fc5\u6709\u673a\u5bc6\u4e4b\u4e8b\uff0c\u4f60\u8eb2\u56de\u623f\u53bb\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u563b\uff0c\u4f60\u5acc\u6211\u5728\u65c1\uff0c\u4e0d\u597d\u610f\u601d\u4e48\uff1f\u201d\u4e8e\u627f \u73e0\u53cc\u773c\u4e00\u7741\uff0c\u88c5\u4f5c\u53d1\u6012\u7684\u795e\u6c14\uff0c\u5c0f\u864e\u5b50\u4f38\u4f38\u820c\u5934\uff0c\u8e51\u624b\u8e51\u811a\u5730\u8d70\u56de\u81ea\u5df1\u7684\u623f\u4e2d\u3002\u4ed6\u7684\u623f\u95f4\u5c31 \u5728\u4e8e\u627f\u73e0\u7684\u9694\u90bb\uff0c\u5c0f\u864e\u5b50\u6dd8\u6c14\u5f97\u5f88\uff0c\u8de8\u5728\u5899\u4e0a\uff0c\u51c6\u5907\u5077\u5077\u542c\u4ed6\u4eec\u7684\u8bf4\u8bdd\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u6ee1\u8179\u7591\u56e2\uff0c\u6ca1\u6709\u6ce8\u610f\u5c0f\u864e\u5b50\u7684\u52a8\u9759\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u53ea\u542c\u5f97\u638c\u67dc\u7684\u5728\u5916\u9762\u8bf4\u9053\uff1a\u201c\u5ba2 \u4eba\u6765\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u6253\u5f00\u623f\u95e8\uff0c\u4f46\u89c1\u4e00\u4e2a\u62ab\u7740\u767d\u72d0\u88d8\u62ab\u80a9\u7684\u534e\u8d35\u5c11\u5e74\uff0c\u7f13\u7f13\u8d70\u4eba\uff0c\u4e8e\u627f\u73e0\u6014\u4e86 \u4e00\u6014\uff1a\u8fd9\u4e2a\u4eba\u7adf\u4f3c\u5728\u4ec0\u4e48\u5730\u65b9\u89c1\u8fc7\u4f3c\u7684\u3002\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8bf7\u95ee\u76f8\u516c\u9ad8\u59d3\u5927\u540d\uff0c\u591c\u95f4\u5230\u6b64\uff0c\u6709\u4f55 \u89c1\u6559\uff1f\u201d\u90a3\u5c11\u5e74\u6253\u91cf\u4e86\u623f\u95f4\u4e00\u773c\uff0c\u542c\u5f97\u90a3\u8001\u638c\u67dc\u7684\u811a\u6b65\u58f0\u5df2\u7ecf\u8fdc\u53bb\u3001\u5ffd\u7136\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u623f\u95e8 \u5173\u4e0a\uff0c\u800c\u4e14\u95e9\u4e0a\u4e86\u95e8\u95e9\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u52c3\u7136\u8272\u53d8\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u90a3\u5c11\u5e74\u201c\u5657\u55e4\u201d\u4e00\u7b11\u58f0\u751a\u662f\u67d4\u5a9a\uff0c\u4e8e\u627f\u73e0\u5fc3\u5ff5 \u4e00\u52a8\uff0c\u53ea\u89c1\u90a3\u5c11\u5e74\u9664\u4e0b\u5934\u4e0a\u7684\u65b9\u5dfe\uff0c\u9732\u51fa\u4e00\u5934\u79c0\u53d1\uff0c\u4e8e\u627f\u73e0\u4ed4\u7ec6\u4e00\u770b\uff0c\u8fd9\u624d\u8ba4\u51fa\u539f\u6765\u662f\u65e5\u95f4 \u966a\u7740\u6c90\u5c0f\u59d0\u5230\u57ce\u968d\u5e99\u8fdb\u9999\u7684\u4e00\u4e2a\u4e2b\u73af\u3002\u4e8e\u627f\u73e0\u5fc3\u4e2d\u6697\u7b11\uff1a\u81ea\u5df1\u4e24\u5e74\u6765\u90fd\u662f\u5973\u626e\u7537\u88c5\uff0c\u7adf\u7136\u770b \u4e0d\u51fa\u5979\u7684\u7834\u7efd\u3002 \u3000\u3000\u90a3\u4e2b\u73af\u9053\uff1a\u201c\u4e8e\u5c0f\u59d0\uff0c\u8bf7\u6055\u5192\u6627\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u7684\u59d3\u540d\uff1f\u4f4f\u5728\u6b64\u95f4\u7684\u90a3 \u4e2b\u73af\u4e0d\u7b54\u8fd9\u8bdd\uff0c\u9053\uff1a\u201c\u6211\u5bb6\u5c0f\u59d0\u6709\u8bf7\u3002\u4e8e\u59d1\u5a18\u89c1\u5230\u5c0f\u59d0\uff0c\u4e00\u5207\u5c31\u660e\u767d\u3002\u201d\u4e8e\u627f\u73e0\u66f4\u662f\u7591\u60d1\uff0c \u90a3\u4e2b\u73af\u9053\uff1a\u201c\u8bf7\u4e8e\u59d1\u5a18\u9a6c\u4e0a\u52a8\u8eab\uff0c\u5c0f\u59d0\u6709\u6781\u5927\u7684\u7591\u96be\u4e4b\u4e8b\uff0c\u8981\u5411\u4e8e\u59d1\u5a18\u8ba8\u6559\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u5934 \u4e00\u9707\uff0c\u60f3\u9053\uff1a\u201c\u83ab\u975e\u662f\u4e0e\u4eca\u65e5\u4e4b\u4e8b\u6709\u5173\uff1f\u201d\u7ee7\u800c\u60f3\u9053\uff1a\u201c\u6211\u6b63\u6101\u6ca1\u6cd5\u5904\u7f6e\u90a3\u5c01\u5bc6\u4fe1\uff0c\u4ea4\u7ed9\u6c90 \u5c0f\u59d0\u5c82\u4e0d\u662f\u6b63\u597d\u3002\u201d\u90a3\u4e2b\u73af\u53c8\u50ac\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\uff0c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u4e09\u66f4\u4e4b\u540e\uff0c\u5728\u8857\u4e0a\u884c\u8d70\uff0c\u5c31\u60f9\u4eba \u8d77\u7591\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u77a7\u5979\u7709\u5b87\u4e4b\u95f4\uff0c\u9690\u6709\u4f18\u8272\uff0c\u6ea2\u6025\u4e4b\u60c5\uff0c\u6ea2\u4e8e\u8a00\u8868\uff0c\u4fbf\u9053\uff1a\u201c\u597d\uff0c\u6211\u8fd8\u6709\u70b9 \u4e8b\u60c5\u8981\u4ea4\u4ee3\u4e00\u4e0b\u3002\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u53ea\u89c1\u5899\u5934\u8df3\u4e0b\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5413\u4e86\u4e00\u8df3\uff0c\u53ea\u542c\u5f97\u5c0f\u864e\u5b50\u7b11\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u6211\u5728\u8fd9\u513f\u5462\u3002\u201d\u4e8e\u627f\u73e0\u5411\u90a3\u4e2b\u73af\u8d54\u7b11\u8bf4 \u9053\uff1a\u201c\u6211\u7684\u5f1f\u5f1f\u6dd8\u6c14\u5f97\u5f88\uff0c\u4f60\u53d7\u60ca\u4e86\u5427\uff1f\u201d\u90a3\u4e2b\u73af\u9053\uff1a\u201c\u6ca1\uff0c\u6ca1\u4ec0\u4e48\uff0c\u566b\uff0c\u4f60\u7684\u5f1f\u5f1f\u771f\u597d\u672c \u9886\uff0c\u6211\u5bb6\u7684\u6b66\u5e08\u4e5f\u53ca\u4e0d\u4e0a\u4ed6\u7684\u8eab\u624b\u3002\u201d\u5979\u53e3\u8bf4\u4e0d\u60ca\uff0c\u5fc3\u5934\u5982\u5728\u535c\u535c\u76f4\u8df3\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u7684\u9ed1\u767d\u5e08\u7236\u660e\u65e5\u5b9a\u53ef\u8d76\u5230\uff0c\u82e5\u7136\u6211\u672a\u56de\u6765\uff0c\u4f60\u5c31\u544a\u8bc9\u4ed6\u4eec\uff0c\u8bf4\u662f\u6211\u5230\u6c90 \u516c\u7239\u7684\u5e9c\u4e0a\u53bb\u4e86\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u77e5\u9053\u5566\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u672a\u56de\u6765\uff0c\u4f60\u4e00\u4e2a\u4eba\u4e0d\u53ef\u5230\u5916\u9762 \u8d70\u52a8\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u4f60\u5f53\u6211\u662f\u5c0f\u5b69\u5b50\u4e48\uff1f\u8fd9\u4e5f\u7528\u5f97\u7740\u5429\u5490\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u90a3\u5339\u7167\u591c\u72ee\u5b50 \u9a6c\uff0c\u4f60\u8981\u597d\u751f\u7167\u6599\uff0c\u4e0d\u53ef\u8ba9\u4eba\u5077\u8d70\u4e86\u3002\u201d\u5c0f\u864e\u5b50\u7b11\u9053\uff1a\u201c\u8fd9\u9a6c\u662f\u4f60\u7684\u547d\u6839\uff0c\u6211\u4e5f\u5b9d\u8d1d\u7740\u5b83 \u5462\uff0c\u8c01\u6562\u5077\uff0c\u6211\u5c31\u548c\u4ed6\u62fc\u547d\u3002\u201d\u4e8e\u627f\u73e0\u4e00\u7b11\u9053\uff1a\u201c\u80fd\u5077\u8d70\u8fd9\u9a6c\u7684\u4eba\uff0c\u53ea\u6015\u4f60\u672a\u5fc5\u662f\u4ed6\u5bf9 \u624b\u3002\u201d\u5c0f\u864e\u5b50\u6485\u7740\u5c0f\u5634\u9053\uff1a\u201c\u90a3\u4f60\u4f55\u5fc5\u5631\u5490\u6211\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u5339\u9a6c\u548c\u4f60\u5df2\u7136\u719f\u8bc6\uff0c\u751f\u4eba \u5b83\u4e0d\u670d\uff0c\u4f60\u9a91\u5b83\u5b83\u4e0d\u4f1a\u53cd\u6297\uff0c\u82e5\u6709\u4eba\u6765\u5077\uff0c\u4f60\u6253\u4e0d\u8fc7\uff0c\u5c31\u8d76\u5feb\u9a91\u7740\u5b83\u8dd1\u3002\u201d\u5c0f\u864e\u5b50\u6ee1\u4e0d\u9ad8 \u5174\uff0c\u9053\uff1a\u201c\u597d\u5566\uff0c\u597d\u5566\uff0c\u4f60\u8d70\u5427\uff01\u5c11\u4e00\u6839\u9a6c\u6bdb\uff0c\u4f60\u56de\u6765\u95ee\u6211\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u548c\u90a3\u4e2b\u73af\u8d70\u51fa\u65c5\u820d\uff0c\u6606\u660e\u662f\u4e2a\u5c71\u57ce\uff0c\u4e8c\u66f4\u8fc7\u540e\uff0c\u8857\u4e0a\u5df2\u5c11\u884c\u4eba\uff0c\u90a3\u4e2b\u73af\u5e26\u5979\u8d70\u51fa \u4e86\u5c0f\u4e1c\u95e8\uff0c\u63a5\u8fd1\u90ca\u5916\uff0c\u66f4\u662f\u5bc2\u9759\uff0c\u8fd9\u665a\u662f\u516b\u6708\u521d\u4e09\uff0c\u6de1\u6de1\u7684\u4e00\u5f2f\u5a25\u7709\u6708\u5728\u6d6e\u4e91\u4e2d\u65f6\u9690\u65f6\u73b0\uff0c \u591c\u8272\u6726\u80e7\uff0c\u758f\u6768\u5728\u591c\u98ce\u4e2d\u547c\u5578\uff0c\u9887\u6709\u8427\u745f\u4e4b\u611f\u3002\u4e8e\u627f\u73e0\u4f46\u89c9\u65e5\u6765\u4e00\u8fde\u4e32\u7684\u5947\u9047\uff0c\u5fc3\u4e2d\u5fd0\u5fd1\u4e0d \u5b89\u3002 \u3000\u3000\u4e24\u4eba\u521a\u521a\u8d70\u8fdb\u57ce\u95e8\uff0c\u5ffd\u542c\u5f97\u547c\u7684\u4e00\u58f0\uff0c\u57ce\u5899\u4e0a\u4eba\u5f71\u4e00\u95ea\uff0c\u4e8e\u627f\u73e0\u542c\u98ce\u8fa8\u5668\uff0c\u77e5\u662f\u6709\u4eba\u6697 \u88ad\uff0c\u6025\u5fd9\u65bd\u5c55\u201c\u4e00\u9e64\u51b2\u5929\u201d\u4e4b\u6280\uff0c\u51cc\u7a7a\u8dc3\u8d77\uff0c\u624b\u4e2d\u7684\u91d1\u82b1\u5c1a\u672a\u6253\u51fa\uff0c\u53ea\u89c1\u90a3\u4e2b\u73af\u7684\u8eab\u5b50\u4e5f\u51cc \u7a7a\u98de\u8d77\uff0c\u4e8e\u627f\u73e0\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u6025\u5fd9\u5c06\u9ed1\u6469\u8bc3\u7ed9\u5979\u7684\u90a3\u652f\u86c7\u7130\u7bad\u53d1\u51fa\uff0c\u5c16\u9510\u7684\u54cd\u7bad\u58f0\u4e2d\uff0c \u98de\u8d77\u4e00\u6e9c\u84dd\u706b\uff0c\u53ea\u89c1\u4e00\u4e2a\u8499\u7740\u5934\u9762\u7684\u9ed1\u8863\u6c49\u5b50\uff0c\u629b\u51fa\u4e00\u6839\u7ef3\u7d22\uff0c\u7d22\u4e0a\u7684\u5957\u73af\u5c06\u90a3\u4e2b\u73af\u5957\u7740\uff0c \u5f85\u4e8e\u627f\u73e0\u53d1\u73b0\u4e4b\u65f6\uff0c\u90a3\u4e2b\u73af\u5df2\u7ed9\u4ed6\u626f\u4e0a\u57ce\u5899\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u4e00\u6296\u624b\u53d1\u51fa\u4e24\u6735\u91d1\u82b1\uff0c\u57ce\u5899\u6709\u4e09\u4e08\u6765\u9ad8\uff0c\u91d1\u82b1\u5c04\u5230\uff0c\u90a3\u4eba\u5df2\u8df3\u4e0b\u57ce\u5899\uff0c\u5411\u90ca\u5916\u9003 \u8d70\u3002\u8fd9\u4e00\u4e0b\uff0c\u53d8\u751f\u610f\u5916\uff0c\u4e8e\u627f\u73e0\u5927\u4e3a\u60f6\u6025\uff0c\u8d76\u5fd9\u62d4\u51fa\u9752\u51a5\u5984\u5251\uff0c\u4e00\u8dc3\u4e08\u8bb8\uff0c\u5b9d\u5251\u5728\u57ce\u5899\u4e0a\u4e00 \u63d2\uff0c\u624b\u638c\u4e00\u6309\u57ce\u5899\uff0c\u62d4\u51fa\u5b9d\u5251\uff0c\u4e00\u7ffb\u8eab\u4e5f\u8dc3\u4e0a\u57ce\u5934\uff0c\u53ea\u89c1\u90a3\u8499\u9762\u4eba\u5df2\u5728\u6570\u5341\u4e08\u5916\uff0c\u6708\u8272\u6726 \u80e7\uff0c\u4f9d\u7a00\u8ba4\u5f97\u51fa\u6a21\u7cca\u7684\u80cc\u5f71\u3002\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u51db\uff1a\u8fd9\u4eba\u7684\u8eab\u6cd5\u597d\u5feb\uff01\u6025\u5fd9\u8df3\u4e0b\u57ce\u5899\u8ffd\u8d76\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7684\u8f7b\u529f\uff0c\u5728\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u5df2\u662f\u5c11\u4eba\u80fd\u4e0e\u6bd4\u62df\uff0c\u4f46\u8ffd\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u8fd8\u662f\u843d\u5728\u90a3\u4eba\u80cc\u540e \u5341\u4f59\u4e08\u4e4b\u591a\uff0c\u4e8e\u627f\u73e0\u4e5f\u66fe\u63a5\u8fde\u53d1\u8fc7\u4e09\u6735\u91d1\u82b1\uff0c\u4f46\u7ec8\u56e0\u8ddd\u79bb\u8fc7\u8fdc\uff0c\u6253\u4e0d\u7740\u654c\u4eba\uff0c\u4e8e\u627f\u73e0\u4e0d\u613f\u6d6a \u8d39\u6697\u5668\uff0c\u53ea\u597d\u7d27\u7d27\u8ffd\u8e2a\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u53ea\u89c1\u90a3\u4eba\u8d70\u4eba\u4e00\u4e2a\u5c71\u5773\uff0c\u4e8e\u627f\u73e0\u8ffd\u5165\u5c71\u8c37\uff0c\u5df2\u5931\u4e86\u90a3\u4eba \u7684\u5f71\u5b50\uff0c\u4f46\u89c1\u4e00\u95f4\u5927\u5c4b\uff0c\u4e0d\u4f3c\u5c71\u9ca2\u4eba\u5bb6\uff0c\u5c4b\u4e2d\u900f\u51fa\u706f\u706b\u3002 \u3000\u3000\u5c71\u8c37\u5185\u518d\u65e0\u7b2c\u4e8c\u5bb6\u4eba\u5bb6\uff0c\u8fd9\u8499\u9762\u4eba\u5f53\u7136\u662f\u8eb2\u8fdb\u5c4b\u5185\u53bb\u4e86\u3002\u4e8e\u627f\u73e0\u4e0d\u6687\u601d\u7d22\uff0c\u8ffd\u5230\u90a3\u95f4\u5927 \u5c4b\u95e8\u524d\uff0c\u89c1\u90a3\u4e24\u6247\u5927\u95e8\uff0c\u4f3c\u662f\u865a\u63a9\u3002\u4e8e\u627f\u73e0\u7528\u529b\u4e00\u63a8\uff0c\u90a3\u4e24\u6247\u53c8\u539a\u53c8\u786c\u7684\u7ea2\u6728\u5927\u95e8\uff0c\u7adf\u7136\u5e94 \u624b\u800c\u5f00\u3002\u4e8e\u627f\u73e0\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u9053\uff1a\u4ed6\u6545\u610f\u4e0d\u5173\u5927\u95e8\uff0c\u96be\u9053\u662f\u8bf1\u654c\u4e4b\u8ba1\u4e48\uff1f\u4f46\u6551\u4eba\u8981\u7d27\uff0c\u800c\u4e14 \u5979\u827a\u9ad8\u660e\u5927\uff0c\u4e5f\u987e\u8651\u4e0d\u4e86\u8fd9\u8bb8\u591a\uff0c\u7565\u4e00\u8fdf\u7591\uff0c\u4fbf\u62d4\u8db3\u8de8\u95e8\u8fdb\u4eba\u3002 \u3000\u3000\u8d70\u4e86\u5341\u6570\u6b65\uff0c\u90a3\u4e24\u6247\u5927\u95e8\u5ffd\u7136\u201c\u7830\u201d\u7684\u4e00\u58f0\u5173\u5408\uff0c\u4e8e\u627f\u73e0\u56de\u5934\u4e00\u671b\uff0c\u5374\u53c8\u4e0d\u89c1\u6709\u4eba\u3002\u4e8e \u627f\u73e0\u6012\u9053\uff1a\u201c\u7b97\u4f60\u662f\u9f99\u6f6d\u864e\u7a74\uff0c\u6211\u4e5f\u5f97\u95ef\u4f60\u4e00\u95ef\uff01\u201d\u91cc\u9762\u9690\u9690\u4f20\u51fa\u7b11\u58f0\uff0c\u4e8e\u627f\u73e0\u5faa\u7b11\u58f0\u8ffd \u53bb\uff0c\u51e0\u91cd\u95e8\u6237\uff0c\u90fd\u662f\u865a\u63a9\uff0c\u5e94\u624b\u4fbf\u5f00\uff0c\u53ea\u6709\u4e00\u6240\u5385\u5802\u5185\uff0c\u4e00\u4e2a\u519b\u5b98\u9ad8\u8e1e\u4e0a\u5ea7\uff0c\u90a3\u4e2b\u73af\u7ad9\u5728\u4ed6 \u7684\u9762\u524d\uff0c\u8eab\u4e0a\u7684\u7ef3\u7d22\u5c1a\u672a\u89e3\u8131\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u4e00\u770b\uff0c\u6012\u6c14\u4e0a\u51b2\uff0c\u9a82\u9053\uff1a\u201c\u54fc\uff0c\u539f\u6765\u662f\u4f60\uff01\u8eab\u4e3a\u5927\u5185\u603b\u7ba1\uff0c\u534a\u591c\u63b3\u4eba\uff0c\u8be5\u5f53\u4f55 \u7f6a\u3002\u201d\u8fd9\u519b\u5b98\u6b63\u662f\u9633\u5b97\u6d77\u3002 \u3000\u3000\u9633\u5b97\u6d77\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e8e\u5c0f\u59d0\uff0c\u4f60\u5728\u9752\u5929\u767d\u65e5\uff0c\u51fa\u624b\u4f24\u4eba\uff0c\u53c8\u5f53\u4f55\u7f6a\uff1f\u201d\u6562\u60c5\u4ed6\u5df2\u77e5\u9053\u4e8e \u627f\u73e0\u767d\u5929\u4e4b\u4e8b\u3002\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5979\u662f\u8c01\uff1f\u9633\u5b97\u6d77\u7b11\u9053\uff1a\u201c\u522b\u4eba\u754f\u60e7\u6c90\u56fd\u516c\uff0c\u6211\u9633\u5b97\u6d77\u4f55 \u987b\u754f\u60e7\uff1f\u201d\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u62cd\u6848\u559d\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\uff0c\u5feb\u628a\u4e66\u4fe1\u4ea4\u51fa\u6765\uff1f\u201d\u90a3\u4e2b\u73af\u9053\uff1a\u201c\u4ec0\u4e48\u4e66 \u4fe1\uff1f\u201d\u9633\u5b97\u6d77\u9053\uff1a\u201c\u738b\u5c06\u519b\u7684\u5bc6\u4fe1\uff1f\u201d\u90a3\u5c0f\u4e2b\u73af\u9053\uff1a\u201c\u54ea\u4e2a\u738b\u5c06\u519b\uff1f\u201d\u9633\u5b97\u6d77\u9053\uff1a\u201c\u4f60\u88c5\u4ec0 \u4e48\u50bb\uff1f\u4f60\u5bb6\u5c0f\u59d0\u5dee\u9063\u4f60\u534a\u591c\u4e09\u66f4\u53bb\u627e\u4e8e\u59d1\u5a18\uff0c\u4e3a\u7684\u4ec0\u4e48\uff1f\u4f60\u4e0d\u4ea4\u51fa\u6765\uff0c\u6211\u53ea\u597d\u65e0\u793c\u4e86\uff0c\u77a7\uff0c \u6211\u6562\u4e0d\u6562\u641c\u4f60\uff01\u201d\u4f38\u624b\u4fbf\u6495\u90a3\u4e2b\u73af\u7684\u8863\u670d\uff0c\u90a3\u4e2b\u73af\u53eb\u9053\uff1a\u201c\u4f60\u6562\u6b3a\u4fae\u516c\u7239\u5e9c\u5185\u7684\u4eba\uff01\u201d\u9633\u5b97 \u6d77\u51b7\u7b11\u4e00\u58f0\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\u628a\u90a3\u4e2b\u73af\u7684\u5916\u8863\u6495\u4e3a\u4e24\u7247\uff0c\u9732\u51fa\u91cc\u9762\u5973\u88c5\u7684\u7ea2\u7f0e\u7d27\u8eab\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5927\u6012\u559d\u9053\uff1a\u201c\u4fe1\u4ef6\u5728\u6211\u8eab\u4e0a\uff0c\u4f60\u6b3a\u4fae\u4e00\u4e2a\u4e2b\u73af\uff0c\u4e0d\u8981\u8138\u4e48\uff1f\u201d\u9633\u5b97\u6d77\u6b63\u662f\u8981\u5979\u8bf4 \u51fa\u8fd9\u8bdd\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4f55\u4e0d\u65e9\u8bf4\uff1f\u5c06\u4fe1\u4ef6\u4ea4\u7ed9\u6211\uff0c\u4e07\u4e8b\u5e72\u4f11\uff0c\u8981\u4e0d\uff0c\u4f60\u4e5f\u4f11\u60f3\u51fa\u53bb\u3002\u201d\u4e8e \u627f\u73e0\u9053\uff1a\u201c\u6709\u672c\u4e8b\u4f60\u5c31\u6765\u53d6\uff01\u201d\u9752\u51a5\u5b9d\u5251\u500f\u5730\u8fdb\u62db\uff0c\u9633\u5b97\u6d77\u5728\u6905\u4e0a\u4e00\u8dc3\u800c\u8d77\uff0c\u65bd\u5c55\u5c0f\u64d2\u62ff\u624b \u7684\u529f\u592b\uff0c\u4fbf\u6765\u62a2\u4e8e\u627f\u73e0\u7684\u5b9d\u5251\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u62c6\u4e86\u51e0\u62db\uff0c\u9633\u5b97\u6d77\u9053\uff1a\u201c\u5c11\u5e74\u4eba\u679c\u7136\u8fdb\u6b65\u5f97\u5feb\uff0c \u54fc\uff0c\u54fc\uff0c\u6015\u8981\u548c\u6211\u5bf9\u624b\uff0c\u90a3\u8fd8\u5dee\u5f97\u8fdc\u4e86\u5462\uff01\u201d\u4e00\u62db\u201c\u98de\u9f99\u5728\u5929\u201d\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u4e8e\u627f\u73e0\u9000\u4e86\u4e24 \u6b65\uff0c\u9633\u5b97\u6d77\u4ea6\u5df2\u8d81\u52bf\u62d4\u51fa\u957f\u5251\uff01\uff0c\uff09 \u3000\u3000\u4e8e\u627f\u73e0\u8eab\u843d\u864e\u53e3\uff0c\u8c41\u51fa\u6027\u547d\uff0c\u628a\u767e\u53d8\u7384\u673a\u5251\u6cd5\u4f7f\u5f97\u51cc\u5389\u65e0\u524d\uff0c\u6fc0\u6597\u4e2d\u53c8\u5c06\u90a3\u4e2b\u73af\u8eab\u4e0a\u7684 \u7ef3\u7d22\u524a\u65ad\uff0c\u90a3\u4e2b\u73af\u5413\u5f97\u8f6f\u4e86\uff0c\u7ef3\u7d22\u867d\u89e3\uff0c\u5374\u4e0d\u4f1a\u8d70\u8def\uff0c\u4e8e\u627f\u73e0\u6025\u9053\uff1a\u201c\u4f60\u5feb\u8dd1\uff0c\u4e0d\u5fc5\u987e \u6211\u3002\u201d\u9633\u5b97\u6d77\u5927\u7b11\u9053\uff1a\u201c\u5230\u4e86\u8fd9\u91cc\uff0c\u8fd8\u60f3\u9003\u8d70\uff0c\u4f60\u505a\u68a6\u4e48\uff1f\u201d\u8f6c\u773c\u95f4\u53ea\u89c1\u95e8\u53e3\u7ad9\u6ee1\u4e86\u4eba\uff0c\u88ab \u5c0f\u864e\u5b50\u7528\u94dc\u94b1\u6253\u4f24\u7684\u90a3\u4e2a\u5f20\u5927\u6d2a\u4e5f\u5728\u5176\u5185\uff0c\u8fd9\u4e9b\u4eba\u90fd\u77e5\u9053\u9633\u5b97\u6d77\u7d20\u6765\u5355\u6253\u72ec\u6597\uff0c\u53ea\u6709\u5f20\u5927\u6d2a \u4e0d\u77e5\u5c31\u91cc\uff0c\u8df3\u8fdb\u53bb\u60f3\u62a5\u4eca\u65e5\u4e4b\u4ec7\uff0c\u4e8e\u627f\u73e0\u56de\u8eab\u4e00\u5251\uff0c\u5de6\u624b\u4e00\u5f39\uff0c\u91d1\u82b1\u4ece\u5251\u5e95\u98de\u51fa\uff0c\u5728\u5f20\u5927\u6d2a \u7684\u989d\u89d2\u4e0a\u7a7f\u4e86\u4e00\u4e2a\u900f\u660e\u7684\u7a9f\u7abf\u3002 \u3000\u3000\u9633\u5b97\u6d77\u559d\u9053\uff1a\u201c\u62ac\u4ed6\u51fa\u53bb\uff0c\u4f60\u4eec\u5835\u7740\u5916\u8fb9\uff0c\u63d0\u9632\u6709\u4ec0\u4e48\u53ef\u7591\u7684\u4eba\u6f5c\u5165\u3002\u8fd9\u5c4b\u5b50\u91cc\u8c01\u90fd\u4e0d \u8bb8\u8de8\u8fdb\u534a\u6b65\u3002\u201d\u4e8e\u627f\u73e0\u9002\u624d\u90a3\u51e0\u4e0b\u5b50\u52a8\u4f5c\u867d\u5feb\uff0c\u9633\u5b97\u6d77\u82e5\u80af\u51fa\u624b\u62e6\u963b\uff0c\u4e8e\u627f\u73e0\u7109\u80fd\u4ece\u5bb9\u53d1\u51fa \u91d1\u82b1\uff1f\u770b\u6765\u4ed6\u662f\u6709\u610f\u8ba9\u5f20\u5927\u6d2a\u53d7\u4f24\u7684\u4e86\u3002 \u3000\u3000\u9633\u5b97\u6d77\u81ea\u6043\u6b66\u827a\u9ad8\u5f3a\uff0c\u6ee1\u5fc3\u4ee5\u4e3a\u767e\u62db\u4e4b\u5185\uff0c\u5b9a\u80fd\u5c06\u4e8e\u627f\u73e0\u5236\u4f0f\uff0c\u5374\u4e0d\u6599\u4e8e\u627f\u73e0\u4e58\u4ed6\u5206\u795e \u8bf4\u8bdd\u7684\u5f53\u53e3\uff0c\u5ffd\u5730\u65bd\u5c55\u51fa\u201c\u7a7f\u82b1\u7ed5\u6811\u201d\u7684\u8eab\u6cd5\uff0c\u56db\u9762\u6e38\u8d70\uff0c\u9633\u5b97\u6d77\u633a\u5251\u6765\u8ffd\uff0c\u597d\u51e0\u6b21\u5251\u5c16\u5df2 \u582a\u582a\u523a\u5230\u5979\u7684\u80cc\u5fc3\uff0c\u90fd\u88ab\u5979\u6e9c\u8d70\u907f\u5f00\uff0c\u5c4b\u5916\u56f4\u89c2\u7684\u4eba\u4e71\u62cd\u9a6c\u5c41\uff0c\u9633\u5b97\u6d77\u6bcf\u51fa\u4e00\u624b\u5251\u62db\uff0c\u4ed6\u4eec \u5c31\u5567\u5567\u8d5e\u8d4f\u9053\uff1a\u201c\u9633\u603b\u7ba1\u597d\u5251\u6cd5\uff01\u201d\u5c82\u77e5\u9633\u5b97\u6d77\u51fa\u624b\u5982\u98ce\uff0c\u8fde\u523a\u4e86\u6570\u5341\u767e\u5251\uff0c\u5982\u8fd8\u672a\u80fd\u4f24\u5f97 \u4e8e\u627f\u73e0\u6beb\u53d1\uff0c\u4e0d\u4f46\u9633\u5b97\u6d77\u81ea\u89c9\u9762\u4e0a\u65e0\u5149\uff0c\u65c1\u89c2\u559d\u5f69\u7684\u4eba\u6e10\u6e10\u4e5f\u53eb\u4e0d\u54cd\u4e86\u3002 \u3000\u3000\u9633\u5b97\u6d77\u52c3\u7136\u5927\u6012\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u7684\u5f92\u5f1f\u8fde\u4e00\u62db\u4e5f\u4e0d\u6562\u63a5\u4e48\uff1f\u201d\u5176\u5b9e\uff0c\u4e8e\u627f\u73e0\u7684 \u201c\u7a7f\u82b1\u7ed5\u6811\u201d\u8eab\u6cd5\uff0c\u53ea\u80fd\u5e94\u4ed8\u4e00\u65f6\uff0c\u4e45\u7f20\u4e0b\u53bb\uff0c\u5b9a\u56e0\u6c14\u529b\u4e0d\u7ee7\u800c\u9732\u51fa\u7834\u7efd\u3002\u9633\u5b97\u6d77\u7684\u6b66\u529f\u548c \u6c14\u529b\u90fd\u8f83\u5979\u5f3a\uff0c\u53ea\u8981\u6c89\u5f97\u4f4f\u6c14\uff0c\u7ec8\u80fd\u53d6\u80dc\u3002\u4e0d\u8fc7\u9633\u5b97\u6d77\u81ea\u6301\u8eab\u4efd\uff0c\u603b\u89c9\u5f97\u5728\u767e\u62db\u4e4b\u5916\uff0c\u7eb5\u7136 \u80fd\u591f\u5c06\u5979\u64d2\u83b7\uff0c\u4ea6\u662f\u80dc\u4e4b\u4e0d\u6b66\u3002\u6545\u6b64\u6025\u7740\u8981\u6fc0\u5979\u8fd8\u624b\u3001\u63a5\u62db\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u679c\u4f3c\u88ab\u4ed6\u6fc0\u6012\uff0c\u5ffd\u5730\u56de\u7738\uff0c\u4e00\u58f0\u51b7\u7b11\uff0c\u559d\u9053\uff1a\u201c\u63a5\u62db\uff01\u201d\u9661\u7136\u95f4\u5251\u5149\u4e00\u95ea\uff0c\u950b\u950b \u4e24\u58f0\uff0c\u91d1\u82b1\u4ece\u5251\u5e95\u98de\u51fa\uff0c\u9633\u5b97\u6d77\u9632\u4e0d\u53ca\u9632\uff0c\u53ea\u5f97\u9000\u540e\u51e0\u6b65\uff0c\u4e3e\u5251\u4e00\u683c\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7b2c \u4e09\u7b2c\u56db\u6735\u91d1\u82b1\u53c8\u76f8\u7ee7\u5c04\u5230\uff0c\u9633\u5b97\u6d77\u638c\u5288\u5251\u6321\uff0c\u5c06\u91d1\u82b1\u4e00\u9707\u98de\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u7c73\u7c92\u4e4b\u73e0\uff0c\u4e5f\u653e \u5149\u534e\uff01\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e94\u3001\u516d\u3001\u4e03\u3001\u516b\u6735\u91d1\u82b1\u8fde\u7fe9\u98de\u81f3\uff0c\u9633\u5b97\u6d77\u5356\u5f04\u672c\u9886\uff0c\u7eb5\u8eab\u4e00\u8dc3\uff0c\u4e00\u62db \u201c\u795e\u9f99\u5165\u6d77\u201d\uff0c\u957f\u5251\u4e00\u4e2a\u76d8\u65cb\uff0c\u4f46\u542c\u5f97\u4e00\u9635\u94b5\u94ae\u4e4b\u58f0\uff0c\u56db\u6735\u91d1\u82b1\u90fd\u7ed9\u8361\u5f00\uff0c\u9633\u5b97\u6d77\u5f97\u610f\u4e4b \u6781\uff0c\u53d1\u58f0\u72c2\u7b11\uff0c\u5374\u4e0d\u6599\u5148\u524d\u7ed9\u4ed6\u683c\u5f00\u7684\u90a3\u51e0\u6735\u91d1\u82b1\uff0c\u5728\u7a7a\u4e2d\u659c\u98de\u6025\u5c04\uff0c\u5ffd\u5730\u53c8\u6389\u8f6c\u5934\u6765\uff0c\u5bf9 \u51c6\u4ed6\u7684\u7a74\u9053\u5c04\u4e0b\uff0c\u9633\u5b97\u6d77\u4e00\u6014\uff0c\u521a\u521a\u9707\u98de\u7684\u90a3\u56db\u6735\u91d1\u82b1\u4e5f\u4e00\u9f50\u6389\u5934\u98de\u56de\uff0c\u5168\u5954\u5411\u4ed6\u7684\u5927\u7a74\u3002 \u3000\u3000\u9633\u5b97\u6d77\u8fd9\u624d\u770b\u51fa\uff0c\u90a3\u6ee1\u7a7a\u98de\u821e\u7684\u91d1\u82b1\uff0c\u8d70\u7684\u90fd\u662f\u5f27\u7ebf\uff0c\u867d\u7136\u7ed9\u4ed6\u9707\u98de\uff0c\u5374\u662f\u4e1d\u6beb\u4e0d\u4e71\uff0c \u7adf\u4f3c\u90fd\u6709\u8f68\u8ff9\u53ef\u5faa\u3002\u9633\u5b97\u6d77\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5c0f\u4e2b\u5934\u7684\u624b\u6cd5\u597d\u53e4\u602a\uff01\u201d\u8f6c\u77ac\u95f4\u4e8e\u627f\u73e0\u5df2 \u662f\u4e00\u8fde\u53d1\u51fa\u5341\u516b\u6735\u91d1\u82b1\uff0c\u5728\u7a7a\u4e2d\u7ec7\u6210\u91d1\u5149\u95ea\u95ea\u7684\u5927\u7f51\uff0c\u5c06\u9633\u5b97\u6d77\u7684\u8eab\u5f62\u7b3c\u7f69\u5728\u5149\u7f51\u4e4b\u4e0b\uff0c\u9633 \u5b97\u6d77\u591a\u597d\u6b66\u529f\uff0c\u8fd9\u65f6\u4e5f\u4e0d\u7981\u6709\u70b9\u624b\u5fd9\u811a\u4e71\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u6240\u7528\u7684\u624b\u6cd5\uff0c\u6b63\u662f\u5979\u4ece\u963f\u8428\u739b\u5144\u5f1f\u7684\u91d1\u7403\u624b\u6cd5\u4e2d\u6240\u53c2\u609f\u51fa\u6765\u7684\uff0c\u53ef\u60dc\u65f6\u65e5\u65e0\u591a\uff0c \u672a\u81fb\u5316\u5883\uff0c\u8981\u4e0d\u7136\u5c31\u51ed\u8fd9\u4e00\u624b\u6697\u5668\u7684\u529f\u592b\uff0c\u4fbf\u53ef\u5236\u9633\u5b97\u6d77\u7684\u6b7b\u547d\u3002\u8fd9\u65f6\u9633\u5b97\u6d77\u867d\u7136\u6709\u4e9b\u5fd9 \u4e71\uff0c\u4f46\u91d1\u82b1\u5374\u4f24\u4e0d\u4e86\u4ed6\uff0c\u53ea\u89c1\u4ed6\u628a\u4e00\u67c4\u957f\u5251\u821e\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u91d1\u82b1\u4e00\u6cbe\u7740\u4ed6\u7684\u5251\u5c16\uff0c\u7acb\u523b\u4fbf\u7ed9 \u8361\u5f00\uff0c\u94ee\u94ee\u4e4b\u58f0\uff0c\u795f\u97f3\u5bc6\u54cd\uff0c\u4e0d\u7edd\u4e8e\u8033\uff01\u5374\u65e0\u4e00\u6735\u80fd\u900f\u8fc7\u4ed6\u7684\u5251\u5708\uff01 \u3000\u3000\u9633\u5b97\u6d77\u6012\u6781\u6c14\u6781\uff0c\u628a\u624b\u4e00\u6325\uff0c\u53ea\u542c\u5f97\u8f70\u9686\u9686\u51e0\u58f0\u5927\u54cd\uff0c\u90a3\u5ea7\u5ba2\u5385\u5de6\u53f3\u897f\u8fb9\u7684\u56db\u6247\u5927\u95e8\u5168 \u90fd\u5173\u95ed\uff0c\u4e8e\u627f\u73e0\u65e9\u5df2\u7edd\u4e86\u9003\u8d70\u4e4b\u5ff5\uff0c\u4ed7\u7740\u4e00\u53e3\u5b9d\u5251\uff0c\u5341\u516b\u6735\u91d1\u82b1\uff0c\u548c\u9633\u5b97\u6d77\u786c\u62fc\uff0c\u4f46\u89c1\u6ee1\u5c4b \u5b50\u91cc\u91d1\u5149\u95ea\u70c1\uff0c\u6709\u5982\u6d41\u661f\u63a0\u7a7a\uff1b\u5251\u6c14\u7eb5\u6a2a\uff0c\u4f0a\u82e5\u94f6\u8679\u4ea4\u9519\u3002\u5c4b\u5185\u7684\u706f\u706b\u867d\u7136\u5168\u90fd\u7184\u706d\uff0c\u4f46\u5728 \u91d1\u82b1\u5b9d\u5251\u7684\u5149\u8292\u95ea\u8000\u4e4b\u4e0b\uff0c\u5bf9\u65b9\u7684\u8eab\u5f62\u79fb\u52a8\uff0c\u90fd\u770b\u5f97\u6e05\u6e05\u695a\u3002 \u3000\u3000\u9633\u5b97\u6d77\u4e00\u58f0\u5927\u543c\uff0c\u632f\u5251\u75be\u62e9\uff0c\u5de6\u624b\u53c8\u4f7f\u51fa\u5288\u7a7a\u638c\u7684\u529f\u592b\uff0c\u7adf\u7136\u5728\u91d1\u82b1\u4ea4\u7ec7\u7684\u7f51\u4e2d\uff0c\u786c\u51b2 \u800c\u51fa\uff0c\u4e8e\u627f\u73e0\u5403\u4e86\u4e00\u60ca\uff0c\u5374\u4e5f\u4e0d\u60e7\uff0c\u9752\u51a5\u5251\u76d8\u7a7a\u4e00\u8f6c\uff0c\u62a2\u7740\u5360\u4e86\u4e0a\u9996\uff0c\u548c\u4ed6\u62a2\u653b\u3002\u9633\u5b97\u6d77\u7684 \u6b66\u529f\u867d\u7136\u8f83\u4e8e\u627f\u73e0\u9ad8\u51fa\u4e0d\u6b62\u4e00\u7b79\uff0c\u4f46\u8fd9\u65f6\u4ed6\u65e2\u8981\u9632\u5907\u90a3\u6ee1\u7a7a\u98de\u821e\u7684\u91d1\u82b1\uff0c\u53c8\u5f97\u63d0\u9632\u81ea\u5df1\u624b\u4e2d \u7684\u957f\u5251\u4f1a\u7ed9\u4e8e\u627f\u73e0\u7684\u5b9d\u5251\u524a\u65ad\uff0c\u6709\u6b64\u4e24\u91cd\u987e\u5fcc\uff0c\u7adf\u7136\u8fd8\u7ed9\u4e8e\u627f\u73e0\u7a0d\u5360\u4e0a\u98ce\u3002\u8fd9\u4e00\u6218\u53cc\u65b9\u90fd\u4f7f \u51fa\u5e73\u751f\u7edd\u6280\uff0c\u9633\u5b97\u6d77\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\uff0c\u4ed6\u672c\u6765\u5c1a\u6709\u5176\u4ed6\u529e\u6cd5\u53ef\u4ee4\u4e8e\u627f\u73e0\u675f\u624b\u5c31\u64d2\uff0c\u4f46\u81ea\u5df1\u8bf4\u8bdd \u5728\u5148\uff0c\u82e5\u7136\u8fde\u4e00\u4e2a\u201c\u9ec4\u6bdb\u4e2b\u5934\u201d\u90fd\u65e0\u6cd5\u964d\u670d\uff0c\u9762\u5b50\u4f55\u5728\uff1f\u56e0\u6b64\u53ea\u597d\u4e0e\u4e8e\u627f\u73e0\u82e6\u6597\uff0c\u53ea\u542c\u5f97\u5916 \u9762\u6668\u9e21\u4e09\u5531\uff0c\u7a97\u5b54\u6e10\u6e10\u900f\u5165\u5fae\u5f31\u7684\u5149\u7ebf\uff0c\u4ed6\u4eec\u5927\u7ea6\u662f\u5728\u56db\u66f4\u4e4b\u65f6\u52a8\u624b\uff0c\u8fd9\u65f6\u4e0d\u77e5\u4e0d\u89c9\u5df2\u8fc7\u4e86 \u4e00\u4e2a\u66f4\u6b21\uff0c\u53cc\u65b9\u90e8\u5df2\u611f\u5230\u7b4b\u75b2\u529b\u5026\uff0c\u4ecd\u662f\u5206\u4e0d\u51fa\u9ad8\u4e0b\uff0c\u82e6\u6218\u4e0d\u4f11\uff01\u4f0f\u5728\u5916\u9762\u4ece\u7a97\u773c\u5077\u7aa5\u7684 \u4eba\uff0c\u90fd\u5728\u6697\u6697\u62c5\u5fc3\uff0c\u5374\u53c8\u4e0d\u6562\u53eb\u9633\u5b97\u6d77\u7f62\u624b\u3002 \u3000\u3000\u9633\u5b97\u6d77\u4e5f\u60f3\u4e0d\u51fa\u5982\u4f55\u4e86\u7ed3\uff0c\u53c8\u8fc7\u7247\u523b\uff0c\u4e8e\u627f\u73e0\u6c14\u5598\u7684\u58f0\u606f\u53ef\u95fb\uff0c\u9633\u5b97\u6d77\u7684\u5934\u4e0a\u4e5f\u5192\u51fa\u817e \u817e\u767d\u6c14\uff0c\u4ed6\u7684\u5185\u529b\u867d\u8f83\u4e8e\u627f\u73e0\u8fdc\u4e3a\u6df1\u539a\uff0c\u4f46\u4e8e\u627f\u73e0\u7684\u91d1\u82b1\u6697\u5668\u8fc7\u4e8e\u5389\u5bb3\uff0c\u53ea\u8981\u6709\u534a\u70b9\u758f\u795e\uff0c \u5c31\u4f1a\u88ab\u6253\u4e2d\u7a74\u9053\uff0c\u9633\u5b97\u6d77\u4e24\u9762\u7167\u987e\uff0c\u6bd4\u4e8e\u627f\u73e0\u81ea\u662f\u5403\u529b\u5f97\u591a\u3002\u518d\u8fc7\u7247\u523b\uff0c\u7a97\u5b54\u4e2d\u900f\u5165\u6765\u7684\u5149 \u7ebf\u66f4\u4e3a\u660e\u4eae\uff0c\u60f3\u6765\u5916\u9762\u5df2\u662f\u5929\u5149\u5927\u767d\u4e86\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u5916\u9762\u6709\u4eba\u53eb\u9053\uff1a\u201c\u9633\u5927\u4eba\uff0c\u738b\u5c06\u519b\u6709\u8bf7\uff01\u201d\u9633\u5b97\u6d77\u6b63\u5df4\u671b\u6709\u6b64\u4e00\u5524\uff0c\u5e94\u4e86\u4e00\u58f0\uff0c \u632f\u5251\u4e00\u5c01\uff0c\u5c06\u4e8e\u627f\u73e0\u903c\u9000\u4e24\u6b65\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\uff0c\u8ba9\u4f60\u591a\u6d3b\u51e0\u4e2a\u65f6\u8fb0\uff0c\u5f85\u6211\u56de\u6765\u518d\u6162\u6162 \u5730\u6536\u62fe\u4f60\u3002\u201d\u4e8e\u627f\u73e0\u51b7\u7b11\u9053\uff1a\u201c\u5927\u603b\u7ba1\u60f3\u9003\u8d70\u4e86\u4e48\uff1f\u201d\u9633\u5b97\u6d77\u987e\u4e0d\u5f97\u548c\u5979\u6597\u53e3\uff0c\u7a81\u7136\u632f\u81c2\u4e00 \u51b2\uff0c\u5e73\u5730\u62d4\u8d77\uff0c\u53ea\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u5c4b\u9876\u5f00\u4e86\u4e00\u4e2a\u5929\u7a97\uff0c\u9633\u5b97\u6d77\u7bad\u4e00\u822c\u5730\u51b2\u4e86\u51fa\u53bb\uff0c\u4e8e\u627f \u73e0\u6b63\u60f3\u968f\u7740\u51fa\u53bb\uff0c\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u5c4b\u5b50\u91cc\u7a81\u7136\u5929\u6447\u5730\u52a8\uff0c\u90a3\u4e2b\u73af\u672c\u662f\u8eb2\u5728\u4e00\u4e2a\u201c\u6b7b\u89d2\u201d\uff0c \u85c9\u7740\u5927\u7406\u77f3\u684c\u906e\u853d\uff0c\u4e0d\u6562\u52a8\u5f39\uff0c\u8fd9\u65f6\u6025\u5f97\u51b2\u4e86\u51fa\u6765\uff0c\u6025\u58f0\u5524\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\uff0c\u4e8e\u59d1\u5a18\uff01\u4f60\u5728\u54ea \u513f\uff1f\u201d\u4e8e\u627f\u73e0\u5fc3\u5934\u4e00\u51db\uff1a\u6211\u600e\u4e48\u5fd8\u8bb0\u4e86\u5979\uff1f\u67d4\u58f0\u7b54\u9053\uff1a\u201c\u522b\u6015\uff0c\u522b\u6015\uff01\u6211\u5728\u8fd9\u91cc\u5462\uff01\u201d\u56de\u8eab \u5c06\u5979\u6293\u7740\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4e0a\u9762\u5929\u7a97\u5df2\u95ed\uff0c\u540c\u65f6\uff0c\u5c4b\u4e2d\u7a81\u7136\u88c2\u5f00\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u4e8e\u627f\u73e0\u62b1\u7740 \u90a3\u4e2a\u4e2b\u73af\uff0c\u4f7f\u4e0d\u51fa\u529b\u6765\uff0c\u8ddf\u7740\u5979\u4e00\u540c\u5760\u4e0b\uff0c\u4e0b\u9762\u7adf\u662f\u4e2a\u9ed1\u9edd\u9edd\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\u7684\u5730\u7262\uff0c\u4e8e\u627f\u73e0 \u6c14\u5f97\u5927\u9a82\uff0c\u60f3\u4e0d\u5230\u9633\u5b97\u6d77\u7684\u8eab\u4efd\uff0c\u7adf\u7136\u4f1a\u4f7f\u51fa\u8fd9\u79cd\u4e0b\u6d41\u624b\u6bb5\u3002\u6b63\u662f\uff1a \u3000\u3000\u586b\u6c60\u4e5f\u81ea\u98ce\u6ce2\u9669\uff0c\u864e\u7a74\u9f99\u6f6d\u53c8\u4e00\u906d\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u56de\u3000\u7262\u5e95\u6551\u4eba\u3000\u795e\u901a\u6765\u5f02\u58eb\u3000\u7b75\u524d\u9a82\u654c\u3000\u6b63\u6c14\u5c5e\u5ce8\u5d4b \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u56de\u3000\u7262\u5e95\u6551\u4eba\u3000\u795e\u901a\u6765\u5f02\u58eb\u3000\u7b75\u524d\u9a82\u654c\u3000\u6b63\u6c14\u5c5e\u5ce8\u5d4b \u3000\u3000\u4e8e\u627f\u73e0\u6b63\u5728\u7834\u53e3\u5927\u9a82\uff0c\u95fb\u5f97\u6c34\u58f0\u6dd9\u6dd9\uff0c\u904d\u4f53\u751f\u5bd2\uff0c\u4e0a\u9762\u6709\u4eba\u58f0\u8bf4\u9053\uff1a\u201c\u9633\u603b\u7ba1\u6709\u8bdd\u5429 \u5490\uff0c\u53eb\u4f60\u5c06\u5b9d\u5251\u4e0e\u4e66\u4fe1\u629b\u4e0a\u6765\uff0c\u5426\u5219\u4f11\u602a\u6211\u4eec\u4e0d\u7559\u60c5\u9762\uff0c\u5148\u628a\u4f60\u6df9\u4e2a\u534a\u6b7b\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u597d\uff0c\u4f60\u628a\u5730\u7262\u6253\u5f00\uff01\u201d\u5f85\u4e0a\u9762\u9732\u51fa\u5929\u5149\uff0c\u4e8e\u627f\u73e0\u7acb\u523b\u65bd\u5c55\u201c\u4e00\u9e64\u51b2\u5929\u201d\u4e4b\u6280\uff0c\u540c\u65f6\u55d6\u3001 \u55d6\u3001\u55d6\u5730\u53d1\u51fa\u4e09\u6735\u91d1\u82b1\uff0c\u90a3\u5730\u7262\u6df1\u8fbe\u5341\u4f59\u4e08\uff0c\u4e8e\u627f\u73e0\u4e0d\u77e5\u6df1\u6d45\uff0c\u7eb5\u8d77\u4e08\u4f59\uff0c\u624b\u521a\u78b0\u7740\u77f3\u58c1\uff0c \u53ea\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u5730\u7262\u7684\u94c1\u76d6\u53c8\u518d\u5173\u95ed\uff0c\u4e0a\u9762\u7684\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u57ce\u968d\u5e99\u91cc\u5f04\u9b3c\uff0c\u5b54\u592b\u95e8 \u524d\u5356\u6587\uff0c\u54c8\u54c8\uff0c\u5012\u6559\u54b1\u4eec\u53d1\u4e86\u6a2a\u8d22\uff01\u54fc\uff0c\u5c0f\u4e2b\u5934\uff0c\u4f60\u4e0d\u8001\u5b9e\uff0c\u90a3\u53ea\u6709\u542f\u8ba8\u82e6\u5403\uff01\u201d\u6c34\u58f0\u6e10\u6765 \u6e10\u5927\uff0c\u6e10\u6e10\u6df9\u81f3\u819d\u76d6\uff0c\u4e8e\u627f\u73e0\u6c14\u5f97\u534a\u6b7b\uff0c\u90a3\u5c0f\u4e2b\u73af\u76f4\u51bb\u5f97\u7259\u5173\u6253\u98a4\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u89e3\u4e0b\u4e00\u4ef6\u8863\u88f3\uff0c\u5c06\u5979\u6402\u7740\uff0c\u9053\uff1a\u201c\u4f60\u5bb3\u6015\u5417\uff1f\u201d\u90a3\u4e2b\u73af\u7728\u7728\u773c\u775b\uff0c\u8bf4\u9053\uff1a\u201c\u672c\u6765 \u5bb3\u6015\uff0c\u548c\u4f60\u5728\u4e00\u8d77\uff0c\u5c31\u4e0d\u5bb3\u6015\u5566\u3002\u201d\u4e8e\u627f\u73e0\u5fae\u7b11\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u90a3\u4e2b\u73af\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u662f\u6211 \u671d\u7b2c\u4e00\u4e2a\u5927\u5fe0\u81e3\u7684\u5973\u513f\u3002\u6211\u60f3\u4ee4\u5c0a\u5927\u4eba\u5f53\u5e74\u4e3a\u4e86\u633d\u6551\u56fd\u5bb6\uff0c\u7518\u53d7\u706d\u95e8\u4e4b\u7978\uff0c\u5018\u4e14\u4e0d\u60e7\uff0c\u54b1\u4eec \u6328\u70b9\u997f\uff0c\u53d7\u70b9\u51b7\uff0c\u53c8\u7b97\u5f97\u4ec0\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u5927\u4e3a\u611f\u52a8\uff0c\u5fc3\u9053\uff1a\u201c\u53e4\u8bed\u4e91\uff1a\u6b7b\u6709\u91cd\u4e8e\u6cf0\u5c71\uff0c\u771f\u662f \u4e0d\u9519\uff0c\u6211\u7236\u4eb2\u867d\u7136\u542b\u51a4\u5c48\u6b7b\uff0c\u4f46\u4ee4\u5f97\u5929\u4e0b\u5987\u5b7a\u4e5f\u95fb\u98ce\u800c\u8d77\uff0c\u8fd9\u6b7b\u4e5f\u503c\u5f97\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e2b\u73af\u62ac\u8d77\u773c\u775b\uff0c\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\uff0c\u6211\u5f97\u89c1\u4f60\uff0c\u8fd9\u4e00\u751f\u603b\u7b97\u6ca1\u6709\u767d\u8fc7\u4e86\u3002\u6211\u5bb6\u5c0f\u59d0\u5bf9\u4f60\u4ef0 \u6155\u5f97\u5f88\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u5bf9\u4f60\u5bb6\u7684\u5c11\u7237\u5c0f\u59d0\u4e5f\u611f\u6fc0\u5f97\u5f88\u3002\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u90a3\u4e2b\u73af\u9053\uff1a \u201c\u6211\u53eb\u675c\u4f59\u5a25\uff0c\u662f\u5927\u7406\u7684\u767d\u65cf\u4eba\uff0c\u4ece\u5c0f\u5c31\u670d\u4f8d\u6c90\u5c0f\u59d0\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u4eec\u600e\u4e48\u77e5\u9053\u6211 \u7684\u6765\u5386\uff1f\u201d\u675c\u91d1\u5a25\u9053\uff1a\u201c\u662f\u5c0f\u59d0\u544a\u8bc9\u6211\u7684\u3002\u5979\u8fd8\u77e5\u9053\u662f\u4f60\u6253\u4f24\u4e86\u5f20\u5927\u6d2a\u548c\u738b\u91d1\u9556\u5462\u3002\u201d\u4e8e\u627f \u73e0\u8be7\u9053\uff1a\u201c\u5979\u600e\u4e48\u77e5\u9053\uff1f\u201d\u675c\u91d1\u5a25\u9053\uff1a\u201c\u6628\u65e5\u5728\u897f\u5c71\u5de1\u903b\u7684\u5175\u4e01\uff0c\u5c06\u4ed6\u4eec\u4e24\u4e2a\u4eba\u62ac\u56de\u6765\uff0c\u6070 \u597d\u6c90\u516c\u7239\u4e0d\u5728\uff0c\u5927\u5bb6\u90fd\u51fa\u6765\u770b\u70ed\u95f9\uff0c\u6c90\u5c0f\u59d0\u8ba4\u5f97\u90a3\u738b\u91d1\u9556\u662f\u738b\u5c06\u519b\u8425\u91cc\u671d\u3002\u95ee\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u53d7 \u4f24\uff0c\u4ed6\u4eec\u4e0d\u80af\u8bf4\u3002\u540e\u6765\u738b\u5c06\u519b\u5c31\u6d3e\u4eba\u5c06\u4ed6\u4eec\u9886\u8d70\u4e86\u3002\u6c90\u5c0f\u59d0\u5306\u5306\u51fa\u53bb\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\u56de\u6765\uff0c\u5c31 \u8981\u6211\u5230\u65c5\u820d\u627e\u4f60\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ed6\u4eec\u65e2\u6ca1\u6709\u8bf4\uff0c\u4f60\u5bb6\u5c0f\u59d0\u53c8\u600e\u77e5\u9053\u662f\u6211\u6253\u4f24\u7684\uff1f\u201d\u675c\u91d1\u5a25 \u9053\uff1a\u201c\u5979\u8ba4\u5f97\u4f60\u7684\u91d1\u82b1\u6697\u5668\uff0c\u5979\u8bf4\u5929\u4e0b\u80fd\u53d1\u8fd9\u79cd\u6697\u5668\u7684\u53ea\u6709\u4e24\u4eba\uff0c\u4e0d\u662f\u5f20\u5927\u4fa0\u7684\u592b\u4eba\u5c31\u662f\u4f60 \u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u7591\u4e91\u5927\u8d77\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6c90\u5c0f\u59d0\u5170\u95fa\u5f31\u8d28\uff0c\u516c\u5e9c\u5343\u91d1\uff0c\u600e\u7684\u8fd9\u6837\u719f\u6089\u6b66\u6797\u4e4b\u4e8b\uff0c \u518d\u8bf4\uff0c\u5979\u53c8\u600e\u4e48\u77e5\u9053\u6211\u5728\u90a3\u95f4\u65c5\u820d\u5c45\u4f4f\uff1f\u201d\u6068\u4e0d\u5f97\u5373\u523b\u98de\u51fa\u53bb\u627e\u7740\u6c90\u5c0f\u59d0\u5c06\u8fd9\u4e2a\u95f7\u846b\u82a6\u6253 \u7834\uff0c\u4f46\u5728\u8fd9\u6df1\u4e0d\u53ef\u6d4b\u7684\u6c34\u7262\u4e2d\uff0c\u5929\u5927\u7684\u6b66\u529f\uff0c\u4ea6\u662f\u63d2\u7ffc\u96be\u98de\u3002\u597d\u5728\u6c34\u6df9\u8fc7\u819d\u76d6\u4e4b\u540e\uff0c\u5c31\u4e0d\u518d \u4e0a\u6da8\u4e86\u3002\u90a3\u4e2b\u73af\u53c8\u51b7\u53c8\u997f\uff0c\u8fde\u8bf4\u8bdd\u7684\u6c14\u529b\u4e5f\u6ca1\u6709\u4e86\u3002\u4e8e\u627f\u73e0\u4e00\u76f4\u5c06\u5979\u62b1\u7740\uff0c\u4e0d\u8ba9\u5979\u53d7\u6c34\u6d78\uff0c \u6e10\u6e10\u4e8e\u627f\u73e0\u4e5f\u89c9\u9965\u997f\u96be\u582a\uff0c\u6c14\u529b\u6e10\u611f\u4e0d\u652f\uff0c\u5ffd\u5730\u4e0a\u9762\u4eae\u5149\u4e00\u95ea\uff0c\u6709\u4e00\u5305\u4e1c\u897f\u201c\u535c\u201d\u5730\u8dcc\u843d\u4e0b \u6765\uff0c\u4e8e\u627f\u73e0\u6025\u5fd9\u63a5\u7740\uff0c\u4e0a\u9762\u94c1\u76d6\u5173\u95ed\uff0c\u6c34\u7262\u4e2d\u53c8\u662f\u6f06\u9ed1\u4e00\u7247\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u53ea\u89c9\u624b\u5fc3\u6e29\u6da6\uff0c\u539f\u6765\u4e0a\u9762\u629b\u4e0b\u7684\u7adf\u662f\u4e00\u5927\u5305\u8377\u53f6\u996d\uff0c\u996d\u7684\u9999\u5473\u548c\u8377\u53f6\u7684\u6e05\u9999\u6df7 \u548c\uff0c\u900f\u4eba\u9f3b\u89c2\uff0c\u5341\u5206\u8bf1\u4eba\u3002\u90a3\u4e2b\u73af\u7cbe\u795e\u4e00\u632f\uff0c\u62ac\u8d77\u5934\u9053\uff1a\u201c\u597d\u9999\uff0c\u597d\u9999\uff01\u201d\u4e8e\u627f\u73e0\u5fc3\u5934\u4e00 \u52a8\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u662f\u606b\u5413\u8bf4\u8981\u997f\u6b7b\u6211\u5417\uff1f\u600e\u4e48\u53c8\u628a\u98df\u7269\u629b\u4e0b\u6765\u4e86\uff1f\u83ab\u975e\u8fd9\u8377\u53f6\u996d\u4e2d\u4e0b\u4e86\u6bd2 \u836f\uff1f\u201d\u5ffd\u6240\u5f97\u4e00\u4e2a\u58f0\u97f3\u5728\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u522b\u6015\uff0c\u522b\u6015\uff0c\u4f60\u653e\u5fc3\u98df\u597d\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u5413\u4e86\u4e00\u8df3\uff0c\u53ea \u89c9\u5f97\u8fd9\u58f0\u97f3\u4f3c\u66fe\u719f\u8bc6\uff0c\u4f46\u900f\u8fc7\u77f3\u58c1\uff0c\u539f\u97f3\u5df1\u53d8\uff0c\u600e\u6837\u4e5f\u5206\u8fa8\u4e0d\u51fa\u3002 \u3000\u3000\u5185\u529f\u6709\u4e86\u706b\u5019\u7684\u4eba\uff0c\u80fd\u591f\u9f13\u6c14\u884c\u8fdc\uff0c\u58f0\u97f3\u6bd4\u5e38\u4eba\u4f20\u5f97\u8fdc\u500d\uff0c\u8fd9\u4e5f\u4e0d\u8db3\u4e3a\u5947\u3002\u4f46\u8fd9\u5730\u7262\u5bc6 \u4e0d\u901a\u98ce\uff0c\u58f0\u97f3\u7adf\u7136\u80fd\u900f\u58c1\u7a7f\u5165\uff0c\u8fd9\u4efd\u529f\u592b\uff0c\u5374\u662f\u975e\u540c\u5c0f\u53ef\uff01\u4e8e\u627f\u73e0\u60f3\u9053\uff1a\u201c\u6b64\u4eba\u7adf\u7136\u5177\u6709\u4f20 \u97f3\u5165\u5bc6\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u82e5\u8981\u64d2\u6211\uff0c\u90a3\u662f\u6613\u5982\u53cd\u638c\uff0c\u4f55\u987b\u4e0b\u6bd2\u9a97\u6211\uff1f\u201d\u90a3\u4e2b\u73af\u998b\u6d8e\u6b32\u6ef4\uff0c\u547b\u541f\u8bf4 \u9053\uff1a\u201c\u997f\u6b7b\u6211\u5566\uff0c\u997f\u6b7b\u6211\u5566\u3002\u4f60\u62ff\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\u8377\u53f6\u996d\u3002\u201d\u5c06\u8377\u53f6\u89e3\u5f00\uff0c\u62d4\u4e0b\u4e00\u652f\u94f6\u7c2a\u63d2\u5165\u996d\u4e2d\u4e00\u8bd5\uff0c\u94f6\u7c2a \u6beb\u4e0d\u53d8\u8272\uff0c\u4e8e\u627f\u73e0\u653e\u5fc3\u9012\u7ed9\u90a3\u4e2a\u4e2b\u73af\uff0c\u90a3\u4e2b\u73af\u4e5f\u65e0\u6687\u95ee\u5979\u8fd9\u996d\u662f\u600e\u4e48\u6765\u7684\uff1f\u7528\u94f6\u7c2a\u628a\u996d\u5206\u6210 \u4e24\u534a\uff0c\u800c\u4eba\u90fd\u5403\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u4f46\u89c9\u8fd9\u4e00\u5305\u6781\u5176\u5bfb\u5e38\u7684\u8377\u53f6\u996d\uff0c\u80dc\u4f3c\u4efb\u4f55\u6d77\u5473\u5c71\u73cd\u3002 \u3000\u3000\u63a5\u7740\u53c8\u6709\u4e00\u4ef6\u5947\u602a\u7684\u4e8b\u60c5\u53d1\u751f\uff0c\u6c34\u7262\u4e2d\u7684\u6c34\u672c\u6765\u5df2\u6d78\u81f3\u8170\u90e8\uff0c\u5c31\u5728\u5979\u4eec\u98df\u996d\u7684\u65f6\u95f4\uff0c\u6c34 \u7adf\u7136\u6e10\u6e10\u6d88\u9000\uff0c\u8fc7\u4e86\u5927\u7ea6\u534a\u4e2a\u65f6\u8fb0\uff0c\u9732\u51fa\u7262\u5e95\u7684\u77f3\u5757\uff0c\u6c34\u5df2\u5b8c\u5168\u9000\u53bb\u4e86\u3002\u4e8e\u627f\u73e0\u53c8\u60ca\u53c8\u559c\uff0c \u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u7528\u610f\uff1f\u9001\u996d\u7684\u90a3\u4eba\u7a76\u7adf\u662f\u53cb\u662f\u654c\uff1f\u201d \u3000\u3000\u90a3\u4e2b\u73af\u75b2\u5026\u4e4b\u6781\uff0c\u9760\u5728\u4e8e\u627f\u73e0\u7684\u8eab\u4e0a\u6c89\u6c89\u7761\u53bb\u3002\u4e8e\u627f\u73e0\u4e0d\u53bb\u60ca\u52a8\u5979\uff0c\u72ec\u81ea\u5446\u5446\u5730\u60f3\uff0c\u4e5f \u4e0d\u77e5\u9053\u8fc7\u4e86\u591a\u4e45\uff0c\u5ffd\u542c\u5f97\u4e0a\u9762\u4e52\u4e52\u4e53\u4e53\u7684\u597d\u50cf\u662f\u5175\u5668\u78b0\u51fb\u7684\u58f0\u97f3\uff0c\u58f0\u97f3\u900f\u5165\u5730\u7262\uff0c\u6709\u5982\u6674\u5929 \u6253\u8d77\u7684\u95f7\u96f7\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u8bf8\u58f0\u4ff1\u5bc2\uff0c\u5ffd\u7136\u9732\u51fa\u5929\u5149\uff0c\u53ea\u89c1\u5730\u7262\u4e0a\u7684\u94c1\u76d6\u5df2\u7ecf\u5f00\u542f\uff0c\u4e8e\u627f\u73e0\u4e00 \u8dc3\u800c\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u91d1\u5a25\u59d0\u59d0\uff0c\u54b1\u4eec\u6709\u6551\u5566\u3002\u201d \u3000\u3000\u90a3\u4e2b\u73af\u63c9\u63c9\u773c\u775b\uff0c\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u6402\u7740\u6211\uff0c\u4e0d\u8981\u5bb3\u6015\uff0c\u6211\u5e26\u4f60\u4e0a \u53bb\u3002\u201d\u4e00\u624b\u62b1\u7740\u4e2b\u73af\uff0c\u4e00\u624b\u62d4\u51fa\u5b9d\u5251\uff0c\u4e00\u8dc3\u4e08\u8bb8\uff0c\u5c06\u5251\u63d2\u5165\u77f3\u58c1\uff0c\u5982\u662f\u8005\u4e03\u516b\u6b21\uff0c\u7a7f\u51fa\u7262 \u6d1e\uff0c\u7741\u773c\u4e00\u770b\uff0c\u4e24\u4eba\u90fd\u5413\u5f97\u5446\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u5c4b\u5b50\u91cc\u5341\u51e0\u6761\u5927\u6c49\uff0c\u4e2a\u4e2a\u90fd\u4f3c\u5802\u4e86\u5deb\u672f\u4f3c\u7684\uff0c\u6709\u7684\u4f38\u5251\u4f5c\u523a\u51fb\u4e4b\u72b6\uff0c\u6709\u7684\u5f2f\u5f13\u4f5c\u6b32 \u5c04\u4e4b\u72b6\uff0c\u6709\u7684\u63d0\u5200\u4f5c\u5288\u6240\u4e4b\u72b6\uff0c\u8bf8\u822c\u602a\u50cf\uff0c\u4e0d\u4e00\u800c\u662f\uff0c\u6700\u4ee4\u4eba\u5bb3\u6015\u7684\u8fd8\u662f\u4ed6\u4eec\u8138\u4e0a\u7684\u795e\u6c14\uff0c \u773c\u775b\u5706\u9f13\u9f13\u5730\u7728\u4e5f\u4e0d\u7728\u4e00\u4e0b\uff0c\u60ca\u4ff1\u3001\u75db\u82e6\u7684\u795e\u60c5\u4ee4\u4eba\u4e0d\u5bd2\u800c\u6817\u3002\u4e8e\u627f\u73e0\u4e00\u770b\uff0c\u4fbf\u77e5\u9053\u4ed6\u4eec\u662f \u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u4f46\u770b\u8fd9\u60c5\u5f62\uff0c\u7adf\u7136\u662f\u5728\u4e00\u7167\u9762\u4e4b\u95f4\uff0c\u5c31\u88ab\u5b8c\u5168\u5236\u4f0f\uff0c\u521a\u624d\u90a3\u5175\u5668\u78b0\u51fb\u4e4b\u58f0\uff0c\u53ef \u4ee5\u6599\u60f3\u5f97\u5230\uff0c\u90a3\u662f\u4ed6\u4eec\u4e00\u7a9d\u8702\u5730\u62e5\u4e0a\uff0c\u4e92\u76f8\u78b0\u649e\u7684\u3002\u4e8e\u627f\u73e0\u8bd5\u7740\u7ed9\u4ed6\u4eec\u89e3\u7a74\uff0c\u4f7f\u4e86\u51e0\u79cd\u624b \u6cd5\uff0c\u6beb\u65e0\u6548\u679c\u3002 \u3000\u3000\u8fd9\u5341\u51e0\u4e2a\u4eba\uff0c\u5176\u4e2d\u7eb5\u7136\u6ca1\u6709\u597d\u624b\uff0c\u4f46\u5728\u4e00\u7167\u9762\u4e4b\u95f4\uff0c\u5c31\u88ab\u4eba\u5b8c\u5168\u70b9\u4e86\u7a74\u9053\uff0c\u6765\u4eba\u7684\u6b66\u529f \u4e4b\u9ad8\uff0c\u7b80\u76f4\u96be\u4ee5\u60f3\u8c61\uff01\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u96be\u9053\u662f\u9ed1\u767d\u6469\u8bc3\u542c\u5230\u6211\u7684\u54cd\u7bad\uff0c\u8d76\u6765\u7684\u4e48\uff1f\u201d\u8d70\u51fa\u5c4b \u5b50\u5916\u4e00\u770b\uff0c\u4f46\u89c1\u65e5\u5f71\u897f\u659c\uff0c\u665a\u971e\u9690\u73b0\uff0c\u56db\u5468\u56f4\u9759\u6084\u6084\u7684\u6ca1\u4e00\u4e2a\u4eba\uff0c\u82e5\u662f\u9ed1\u767d\u6469\u8bc3\uff0c\u65ad\u65e0\u4e0d\u7559 \u4e0b\u534a\u53e5\u8bdd\u4fbf\u8d70\u7684\u9053\u7406\u3002\u66f4\u6709\u4e00\u6869\u5947\u602a\u7684\u662f\uff1a\u770b\u90a3\u70b9\u7a74\u7684\u8ff9\u8c61\uff0c\u5e76\u4e0d\u4f3c\u4ec0\u4e48\u5947\u7279\u7684\u624b\u6cd5\uff0c\u548c\u9ed1 \u767d\u6469\u8bc3\u90a3\u4e00\u6d3e\u5927\u4e0d\u76f8\u540c\uff0c\u4f46\u4ee5\u4e8e\u627f\u73e0\u7684\u672c\u4e8b\uff0c\u7adf\u7136\u65e0\u6cd5\u89e3\u7a74\uff0c\u770b\u6765\u90a3\u4eba\u7684\u5185\u529f\u5df2\u5230\u4e86\u6df1\u4e0d\u53ef \u6d4b\u7684\u5730\u6b65\uff0c\u5373\u7b97\u662f\u7528\u6781\u5bfb\u5e38\u7684\u624b\u6cd5\u70b9\u7a74\uff0c\u82e5\u975e\u5185\u529f\u7684\u6839\u5e95\u53ef\u4ee5\u6bd4\u5f97\u4e0a\u5730\u7684\u4eba\uff0c\u4fbf\u65e0\u6cd5\u51b2\u5173\u89e3 \u7a74\uff0c\u53ea\u6709\u7b49\u4ed6\u90a3\u4e00\u70b9\u6240\u51dd\u805a\u7684\u5185\u529b\u81ea\u884c\u6d88\u6563\u4e86\u3002 \u3000\u3000\u90a3\u4e2b\u73af\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\uff0c\u8fd9\u91cc\u602a\u9a87\u4eba\u7684\u3002\u5feb\u8d70\u4e86\u5427\uff01\u6211\u5bb6\u5c0f\u59d0\u89c1\u54b1\u4eec\u4e00\u591c\u6ca1\u56de\uff0c\u4e0d\u77e5\u591a\u7740 \u6025\u5462\u3002\u201d\u4e8e\u627f\u73e0\u970d\u7136\u4e00\u60ca\uff0c\u5728\u6c34\u7262\u91cc\u539f\u6765\u5df2\u5ea6\u8fc7\u4e00\u4e2a\u767d\u5929\uff0c\u5fc3\u4e2d\u867d\u662f\u7591\u56e2\u83ab\u91ca\uff0c\u5374\u662f\u6ca1\u6709\u65f6 \u95f4\u7b49\u90a3\u4e9b\u4eba\u9192\u6765\u518d\u95ee\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u4e0e\u90a3\u4e2b\u73af\u5de1\u89c6\u4e00\u904d\uff0c\u4f46\u89c1\u5904\u5904\u95e8\u6237\u5927\u5f00\uff0c\u6240\u6709\u7684\u4eba\u90fd\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u50f5\u7acb\u5982\u6b7b\uff0c\u795e \u6c14\u9a87\u4eba\uff0c\u5c31\u50cf\u5c4b\u5b50\u91cc\u7684\u90a3\u4e9b\u4eba\u4e00\u6837\uff0c\u9a6c\u53a9\u4e2d\u8fd8\u6709\u51e0\u5339\u9a6c\uff0c\u4e8e\u627f\u73e0\u4e0e\u90a3\u4e2b\u73af\u5907\u9009\u4e86\u4e00\u5339\u9a6c\uff0c\u7acb \u523b\u98de\u5954\u5165\u57ce\u3002 \u3000\u3000\u6c90\u5bb6\u7684\u201c\u9ed4\u56fd\u516c\u201d\u5927\u5e9c\u5728\u6606\u660e\u7684\u5c0f\u4e1c\u95e8\u5916\uff0c\u5230\u5f97\u516c\u5e9c\uff0c\u5df2\u662f\u638c\u706f\u65f6\u5206\uff0c\u90a3\u4e2b\u73af\u5e26\u4e8e\u627f\u73e0 \u4ece\u540e\u95e8\u6e9c\u5165\uff0c\u770b\u95e8\u7684\u8ba4\u5f97\u5979\uff0c\u53ea\u9053\u4e8e\u627f\u73e0\u662f\u5979\u7684\u59d0\u59b9\uff0c\u5e76\u65e0\u62e6\u963b\u3002\u8fd9\u4e2b\u73af\u5e26\u9886\u4e8e\u627f\u73e0\u7a7f\u5802\u5165 \u5ba4\uff0c\u5230\u4e86\u4e00\u95f4\u7cbe\u81f4\u7684\u623f\u5b50\u5916\u8fb9\uff0c\u505c\u4e86\u4e0b\u6765\uff0c\u6572\u95e8\u53eb\u9053\uff1a\u201c\u6c90\u5c0f\u59d0\uff0c\u4e8e\u59d1\u5a18\u6765\u5566\uff01\u201d\u91cc\u9762\u6beb\u65e0 \u58f0\u606f\uff0c\u90a3\u4e2b\u73af\u9053\uff1a\u201c\u54a6\uff0c\u5c0f\u59d0\u5230\u54ea\u513f\u53bb\u4e86\uff1f\u201d\u8fc7\u4e86\u597d\u4e45\uff0c\u624d\u6709\u4e00\u4e2a\u4e2b\u73af\u51fa\u6765\u5f00\u95e8\uff0c\u4e00\u89c1\u9762\u4fbf \u9053\uff1a\u201c\u91d1\u5a25\u59d0\uff0c\u4f60\u600e\u4e48\u8fd9\u4e2a\u65f6\u5019\u624d\u56de\u6765\uff1f\u201d\u8fd9\u4e2a\u4e2b\u73af\u540d\u53eb\u94f6\u6842\uff0c\u548c\u91d1\u5a25\u90fd\u662f\u6c90\u71d5\u7684\u8d34\u8eab\u4e2b \u5934\u3002 \u3000\u3000\u91d1\u5a25\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\uff0c\u5c0f\u59d0\u5462\uff1f\u201d\u94f6\u6842\u9053\uff1a\u201c\u5c0f\u59d0\u8d70\u5566\u3002\u201d\u91d1\u5a25\u9053\uff1a\u201c\u53bb\u54ea\u513f\uff1f\u201d\u94f6\u6842 \u9053\uff1a\u201c\u9ec4\u660f\u65f6\u5019\u8d70\u51fa\u56ed\u5b50\u7684\uff0c\u5979\u795e\u8272\u5306\u5306\uff0c\u6211\u4e0d\u6562\u95ee\u3002\u201d\u8fb9\u8bf4\u8fb9\u8ba9\u4e8e\u627f\u73e0\u8fdb\u623f\u6765\u5750\uff0c\u4e8e\u627f\u73e0 \u5fc3\u6025\u5982\u711a\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u5ffd\u89c1\u5899\u4e0a\u6302\u7740\u4e00\u5f20\u6761\u5e45\uff0c\u5199\u7684\u662f\u8f9b\u5f03\u75be\u7684\u4e00\u9996\u8bcd\uff1a \u3000\u3000\u201c\u9189\u91cc\u6311\u706f\u770b\u5251\uff0c\u68a6\u56de\u5439\u89d2\u8fde\u8425\u3002\u516b\u767e\u91cc\u5206\u9ebe\u4e0b\u7099\uff0c\u4e94\u5341\u5f26\u7ffb\u585e\u5916\u58f0\uff0c\u6c99\u573a\u79cb\u70b9\u5175\uff0c\u9a6c \u4f5c\u7684\u5362\u98de\u5feb\uff0c\u5f13\u5982\u9739\u96f7\u5f26\u60ca\u3002\u4e86\u5374\u541b\u738b\u5929\u4e0b\u4e8b\uff0c\u8d62\u5f97\u751f\u524d\u8eab\u540e\u540d\uff0c\u53ef\u601c\u767d\u53d1\u751f\u3002\u201d\u8fd9\u9996\u8bcd\u58ee \u6c14\u8c6a\u60c5\uff0c\u662f\u8f9b\u5f03\u75be\u7684\u5f97\u610f\u4f73\u4f5c\uff0c\u4f20\u8bf5\u5343\u53e4\uff0c\u95fa\u9601\u4e4b\u4e2d\u6302\u8fd9\u6837\u7684\u4e00\u9996\u8bcd\uff0c\u867d\u7136\u4e0d\u5f88\u8c03\u548c\uff0c\u4ea6\u4e0d \u7b97\u5947\u602a\uff0c\u4f46\u8fd9\u9996\u8bcd\u7684\u7b14\u8ff9\uff0c\u94c1\u4e66\u94f6\u94a9\uff0c\u9f99\u98de\u51e4\u821e\uff0c\u5374\u662f\u5f20\u4e39\u67ab\u7684\u624b\u8ff9\uff01\u4e8e\u627f\u73e0\u5fc3\u4e2d\u5927\u5947\uff0c\u60f3 \u9053\uff1a\u201c\u54a6\uff0c\u5979\u600e\u4e48\u6c42\u5f97\u6211\u5e08\u7236\u7684\u6cd5\u4e66\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u90a3\u94f6\u6842\u8bf4\u9053\uff1a\u201c\u516c\u7239\u4eca\u665a\u5bb4\u5ba2\uff0c\u542c\u8bf4\u4eac\u4e2d\u6765\u4e86\u4e00\u4e2a\u4ec0\u4e48\u603b\u7ba1\u7684\u5927\u5b98\u5462\u3002\u516c\u7239\u9002\u624d \u8fd8\u5429\u5490\u5c0f\u59d0\uff0c\u8981\u5c0f\u59d0\u770b\u7ba1\u5c11\u7237\uff0c\u7b49\u5e2d\u6563\u4e4b\u540e\uff0c\u8fd8\u6709\u8bdd\u8bf4\u7684\uff0c\u5c82\u77e5\u5c0f\u59d0\u4e0d\u58f0\u4e0d\u54cd\u5730\u5c31\u8d70\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u5934\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u4ec0\u4e48\u603b\u7ba1\uff0c\u83ab\u975e\u662f\u9633\u5b97\u6d77\uff1f\u201d\u95ee\u9053\uff1a\u201c\u600e\u4e48\u53eb\u6c90\u5c0f\u59d0\u770b\u7ba1\u5c0f \u516c\u7239\uff1f\u201d\u94f6\u6842\u8fdf\u7591\u4e00\u4e0b\uff0c\u91d1\u5a25\u9053\uff1a\u201c\u8fd9\u4f4d\u4e8e\u59d1\u5a18\u662f\u5c0f\u59d0\u8bf7\u6765\u7684\uff0c\u4f46\u8bf4\u65e0\u59a8\u3002\u201d\u94f6\u6842\u9053\uff1a\u201c\u516c \u7239\u4e0d\u77e5\u600e\u7684\uff0c\u6628\u65e5\u5927\u53d1\u813e\u6c14\uff0c\u5c06\u5c11\u7237\u9501\u5728\u5185\u623f\uff0c\u8fd9\u4e8b\u60c5\u5916\u9762\u6ca1\u4eba\u77e5\u9053\uff0c\u5f53\u7136\u4e5f\u6ca1\u6709\u6b66\u571f\u770b \u5b88\uff0c\u6240\u4ee5\u53eb\u5c0f\u59d0\u770b\u7ba1\u3002\u201d\u4e8e\u627f\u73e0\u4e00\u542c\uff0c\u6599\u60f3\u5b9a\u662f\u56e0\u4e3a\u6c90\u78f7\u66ff\u81ea\u5df1\u7236\u4eb2\u5efa\u5e99\u9020\u50cf\u4e4b\u4e8b\uff0c\u7ed9\u6c90\u56fd \u516c\u77e5\u9053\u4e86\uff0c\u6240\u4ee5\u5c06\u4ed6\u5e7d\u7981\u5185\u5802\uff0c\u8fd9\u4e8b\u60c5\u5f53\u7136\u4e0d\u597d\u660e\u8bf4\u3002 \u3000\u3000\u5916\u9762\u6709\u8f66\u9a6c\u4e4b\u58f0\uff0c\u94f6\u6842\u9053\uff1a\u201c\u5ba2\u4eba\u6765\u5566\u3002\u201d\u4e8e\u627f\u73e0\u5ffd\u9053\uff1a\u201c\u5728\u54ea\u513f\u5bb4\u5ba2\uff1f\u201d\u94f6\u6842\u9053\uff1a \u201c\u5728\u56ed\u5b50\u897f\u8fb9\u7684\u85d5\u9999\u683c\u5185\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u5e26\u6211\u53bb\u770b\u770b\u3002\u201d\u94f6\u6842\u5413\u4e86\u4e00\u8df3\uff0c\u91d1\u5a25\u7b11\u9053\uff1a \u201c\u6211\u5e26\u4f60\u53bb\uff0c\u54b1\u4eec\u85cf\u5728\u6c60\u5858\u8fb9\u7684\u5047\u5c71\u77f3\u540e\uff0c\u53ef\u4ee5\u770b\u5f97\u6e05\u6e05\u695a\u695a\u3002\u82e5\u7ed9\u4eba\u53d1\u73b0\u4e86\uff0c\u54b1\u4eec\u5c31\u5f53\u5728 \u90a3\u91cc\u6349\u8ff7\u85cf\u73a9\u513f\uff0c\u6599\u516c\u7239\u4e0d\u4f1a\u89c1\u602a\u3002\u201d \u3000\u3000\u91d1\u5a25\u62db\u5f85\u4e8e\u627f\u73e0\u80e1\u4e71\u5403\u8fc7\u4e00\u4e9b\u4e1c\u897f\uff0c\u6362\u8fc7\u6c34\u6e0d\u7684\u8863\u88f3\uff0c\u4fbf\u5e26\u5979\u6084\u6084\u5730\u85cf\u5230\u5047\u5c71\u77f3\u540e\uff0c\u4f46 \u89c1\u6c34\u69ad\u5185\u5b98\u706f\u9ad8\u6302\uff0c\u7167\u8000\u5f97\u5982\u540c\u767d\u663c\uff0c\u7b75\u5e2d\u4f3c\u662f\u521a\u521a\u6446\u5f00\uff0c\u5e2d\u4e0a\u8bf8\u4eba\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5750\u5728\u4e0a \u4f4d\u7684\u662f\u4e00\u4e2a\u9762\u767d\u65e0\u987b\u5dcd\u5ce8\u51a0\u9ad8\u670d\u7684\u5927\u5b98\uff0c\u7b2c\u4e8c\u4f4d\u679c\u7136\u4fbf\u662f\u9633\u5b97\u6d77\uff0c\u7b2c\u4e09\u4f4d\u662f\u4e2a\u6b66\u5b98\uff0c\u4e8e\u627f\u73e0 \u8ba4\u5f97\u662f\u524d\u65e5\u5230\u8fc7\u57ce\u968d\u5e99\u7684\u90a3\u4e2a\u738b\u5c06\u519b\uff0c\u4e3b\u5ba2\u659c\u5bf9\u9762\u7684\u90a3\u4e00\u4f4d\u5374\u662f\u4e2a\u9053\u58eb\uff0c\u6c90\u56fd\u516c\u5750\u5728\u90a3\u9053\u58eb \u4fa7\u9762\u7684\u4e3b\u4f4d\u4e0a\uff0c\u4e09\u7efa\u957f\u987b\uff0c\u751a\u662f\u5a01\u4e25\u3002 \u3000\u3000\u91d1\u5a25\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u4e8b\u60c5\u53ef\u771f\u5947\u602a\uff0c\u6c90\u516c\u7239\u600e\u4e48\u5c06\u9053\u58eb\u4e5f\u8bf7\u6765\u4e86\u3002\u201d\u51fa\u89c1\u9996\u5e2d\u7684\u90a3 \u4e2a\u5927\u5b98\u53e3\u5507\u5f00\u9609\uff0c\u4f3c\u662f\u8bf4\u8bdd\uff0c\u675c\u91d1\u5a25\u542c\u4e0d\u6e05\u695a\uff0c\u4e8e\u627f\u73e0\u7ec3\u8fc7\u201c\u542c\u98ce\u8fa8\u5668\u201d\u7684\u529f\u592b\uff0c\u628a\u8033\u6735\u8d34 \u5728\u50cf\u5c71\u77f3\u4e0a\uff0c\u5374\u662f\u4e00\u65e0\u9057\u6f0f\uff0c\u53ea\u542c\u5f97\u90a3\u9762\u767d\u65e0\u987b\u7684\u5927\u5b98\u8bf4\u9053\uff1a\u201c\u95fb\u8bf4\u5927\u7406\u5e9c\u7684\u767d\u65cf\u5a03\u5b50\u8981\u9020 \u53cd\uff0c\u7531\u6bb5\u5bb6\u5e26\u5934\uff0c\u5c06\u671d\u5ef7\u6240\u6d3e\u7684\u5b98\u5458\u90fd\u9a71\u9010\u4e86\uff0c\u6709\u8fd9\u56de\u4e8b\u4e48\uff1f\u201d\u8bf4\u8bdd\u9634\u58f0\u7ec6\u6c14\uff0c\u7adf\u4f3c\u5973\u4eba\u8154 \u8c03\u3002\u6c90\u56fd\u516c\u9053\uff1a\u201c\u6709\u8fd9\u4e48\u56de\u4e8b\u3002\u4e0d\u8fc7\u4ed6\u4eec\u6240\u53d1\u7684\u6a84\u6587\uff0c\u5374\u8bf4\u4e0d\u662f\u9020\u53cd\uff0c\u5e76\u4e0d\u60f3\u8981\u6c49\u4eba\u7684\u5730 \u65b9\u3002\u5927\u7ea6\u662f\u60f3\u81ea\u7acb\u4e3a\u738b\u3002\u201d\u90a3\u5927\u5b98\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u81ea\u7acb\u4e3a\u738b\uff0c\u8fd9\u8fd8\u4e0d\u662f\u9020\u53cd\u5417\uff1f\u671d\u5ef7\u5bf9 \u6bb5\u5bb6\u4e0d\u8584\uff0c\u5f53\u5e74\u4ee4\u7956\u9ed8\u5b81\u738b\u706d\u4e86\u5927\u7406\u56fd\u540e\uff0c\u4e16\u4e16\u4ee3\u4ee3\u5bf9\u6bb5\u5bb6\u4e3a\u5927\u7406\u5e9c\u7684\u77e5\u5e73\u7ae0\u4e8b\uff0c\u4ed6\u600e\u4e48\u8fd8 \u4e0d\u77e5\u8db3\uff1f\u201d\u6c90\u56fd\u516c\u9053\uff1a\u201c\u662f\u5440\uff0c\u8fd9\u4e8b\u60c5\u6211\u5df2\u79e6\u7980\u7687\u4e0a\uff0c\u5218\u516c\u516c\u6070\u597d\u5230\u6765\uff0c\u90a3\u597d\u6781\u4e86\uff0c\u5218\u516c\u516c \u63a5\u8fd1\u5929\u989c\uff0c\u53c8\u662f\u4e91\u5357\u6851\u6893\uff0c\u6211\u6b63\u60f3\u95ee\u5218\u516c\u516c\u7684\u4e3b\u610f\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u662f\u4e2a\u592a\u76d1\u3002\u201d \u660e\u592a\u7956\u521d\u5efa\u56fd\u65f6\uff0c\u4e0d\u8bb8\u592a\u6ee5\u8fc7\u95ee\u56fd\u4e8b\uff0c\u4f20\u4e86\u51e0\u4ee3\u4e4b\u540e\uff0c\u8fd9\u7981\u4f8b\u677e\u5f1b\uff0c\u7687\u5e1d\u5e38\u5e38\u6d3e\u592a\u76d1\u505a\u94a6\u5dee \u5927\u81e3\uff0c\u5de1\u9605\u5404\u7701\uff0c\u50cf\u660e\u6210\u7956\u6240\u6d3e\u7684\u90a3\u4e2a\u592a\u76d1\u90d1\u548c\u4e03\u4e0b\u897f\u6d0b\uff0c\u58f0\u5a01\u663e\u8d6b\uff0c\u538b\u5012\u671d\u81e3\uff0c\u4fbf\u662f\u4e00 \u4f8b\u3002\u660e\u671d\u7684\u592a\u76d1\u5f88\u591a\u662f\u4e91\u5357\u4eba\uff08\u90d1\u548c\u4e5f\u662f\uff09\uff0c\u5176\u4e2d\u6709\u624d\u80fd\u7684\u56fa\u6709\uff0c\u7978\u56fd\u6b83\u6c11\u7684\u4e5f\u4e0d\u5c11\u3002\u8fd9\u4e2a \u5218\u516c\u516c\u542c\u4ed6\u7684\u53e3\u97f3\uff0c\u4e5f\u662f\u4e91\u5357\u4eba\u3002\u6c90\u56fd\u516c\u5411\u4ed6\u8bf7\u6559\uff0c\u4ed6\u5927\u4e3a\u6b22\u60a6\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u516c\u7239\u4e0b\u90e8\u3002 \u6211\u5c82\u6562\u4e0d\u5c3d\u6240\u8a00\uff0c\u4f9d\u6211\u6240\u8bf4\uff0c\u6c90\u516c\u7239\u65e9\u5c31\u8be5\u6d3e\u5175\u8fdb\u88ad\uff01\u6211\u8fd9\u6b21\u51fa\u4eac\u4e4b\u65f6\uff0c\u7687\u4e0a\u4e5f\u66fe\u53eb\u6211\u8f6c\u544a \u516c\u7239\uff0c\u63d0\u9632\u86ee\u4eba\u4f5c\u53cd\uff0c\u65e2\u7136\u6709\u4e86\u53cd\u8ff9\uff0c\u90a3\u5c31\u53ea\u6709\u628a\u4ed6\u4eec\u6740\u7edd\uff01\u201d \u3000\u3000\u6c90\u742e\u7565\u4e00\u6c89\u541f\uff0c\u62c8\u987b\u8bf4\u9053\uff1a\u201c\u5927\u52a8\u5e72\u6208\uff0c\u5c82\u4e0d\u4ee4\u751f\u7075\u6d82\u70ad\uff1f\u201d\u90a3\u5218\u516c\u516c\u5fc3\u4e2d\u4e0d\u60a6\uff0c\u4f46\u4e91 \u5357\u7701\u8fb9\u7586\u7701\u5206\uff0c\u4e2d\u67a2\u7ba1\u8f96\u4e0d\u5230\uff0c\u6c90\u5bb6\u4e16\u4ee3\u638c\u6743\uff0c\u5373\u7b97\u7687\u5e1d\u4e5f\u8981\u7ed9\u4ed6\u51e0\u5206\u9762\u5b50\uff0c\u5218\u516c\u516c\u8d54\u7b11\u8bf4 \u9053\uff1a\u201c\u6c90\u516c\u7239\u4ec1\u4e49\u4e3a\u6000\uff0c\u4e0d\u6127\u4e3a\u6c11\u7236\u6bcd\u3002\u4f46\u6cbb\u4e71\u4e16\u987b\u7528\u91cd\u5211\uff0c\u82e5\u7136\u4e0d\u52a8\u5e72\u6208\uff0c\u7109\u80fd\u6538\u5e73\u53db \u4e71\uff1f\u6211\u5012\u8981\u5411\u516c\u7239\u8bf7\u6559\u3002\u201d\u6c90\u742e\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u65e5\u5185\u6709\u4e24\u4f4d\u8fdc\u5ba2\u8981\u5230\u6606\u660e\uff0c\u4ece\u4ed6\u4eec\u8eab \u4e0a\uff0c\u6211\u60f3\u597d\u4e00\u6761\u6000\u67d4\u4e4b\u7b56\uff0c\u4e0d\u77e5\u80fd\u4e0d\u80fd\u884c\uff1f\u6211\u8fd8\u672a\u53ca\u7980\u79e6\u7687\u4e0a\uff0c\u5148\u8bf4\u4e0e\u5218\u516c\u516c\u542c\u542c\u3002\u201d\u90a3\u592a \u76d1\u653e\u4e0b\u9152\u676f\uff0c\u9053\uff1a\u201c\u6c90\u516c\u7239\u8bf7\u8bf4\u3002\u201d\u9633\u5b97\u6d77\u63d2\u53e3\u95ee\u9053\uff1a\u201c\u662f\u4e24\u4f4d\u4ec0\u4e48\u8d35\u5bbe\uff1f\u201d\u5fc3\u4e2d\u751a\u662f\u6000 \u7591\uff0c\u60f3\u9053\uff1a\u201c\u542c\u6c90\u56fd\u516c\u7684\u53e3\u6c14\uff0c\u5b9a\u7136\u662f\u4e24\u4f4d\u975e\u5e38\u4eba\u7269\uff0c\u5982\u4f55\u6211\u7684\u624b\u4e0b\u4eba\u4e8b\u5148\u90a3\u4e0d\u77e5\u9053\u4e00\u70b9\u6d88 \u606f\u3002\u201d \u3000\u3000\u6c90\u742e\u9053\uff1a\u201c\u662f\u6ce2\u65af\u56fd\u7684\u516c\u4e3b\u548c\u9a78\u9a6c\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u9616\u5ea7\u60ca\u8be7\uff0c\u9633\u5b97\u6d77\u9053\uff1a\u201c\u6ce2\u65af\u516c\u4e3b\u548c \u5927\u7406\u7684\u53db\u4e71\u6709\u4f55\u5173\u8fde\uff1f\u201d\u6c90\u742e\u9053\uff1a\u201c\u8fd9\u4f4d\u6ce2\u65af\u516c\u4e3b\u7684\u9a78\u9a6c\uff0c\u59d3\u6bb5\u540d\u6f84\u82cd\uff0c\u6211\u5df2\u67e5\u63a2\u6e05\u695a\u4e86\u4ed6 \u6b63\u662f\u5f53\u5e74\u6bb5\u5e73\u7ae0\u6bb5\u529f\u7684\u5b50\u5b59\uff0c\u4ed6\u7684\u7956\u5148\u66fe\u4ece\u5143\u519b\u897f\u5f81\uff0c\u6d41\u843d\u6ce2\u65af\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u4ed6\u7adf\u56e0\u7f18\u65f6 \u4f1a\uff0c\u8d35\u4e3a\u9a78\u9a6c\u3002\u60f3\u662f\u601d\u5ff5\u5bb6\u90a6\uff0c\u6000\u4e61\u60c5\u5207\uff0c\u4e0d\u8f9e\u4e07\u91cc\u5954\u6ce2\uff0c\u91cd\u5f52\u6545\u91cc\uff0c\u8fd9\u5012\u662f\u672c\u671d\u7684\u4e00\u5927\u4f73 \u8bdd\u5475\uff1f\u201d\u90a3\u5218\u516c\u516c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5f02\u90a6\u516c\u4e3b\u6765\u671d\uff0c\u8db3\u89c1\u5723\u5fb7\u8fdc\u64ad\uff0c\u4f46\u8bf7\u95ee\u516c\u7239\uff0c\u600e\u7684\u4ece\u4ed6\u4eec\u8eab \u4e0a\uff0c\u60f3\u5230\u6000\u67d4\u4e4b\u7b56\uff1f\u201d\u6c90\u742e\u9053\uff1a\u201c\u4ed6\u662f\u6bb5\u529f\u7684\u5b50\u5b59\uff0c\u7b97\u8d77\u6765\u4e0e\u73b0\u5728\u5927\u7406\u7684\u77e5\u5e73\u7ae0\u4e8b\u6bb5\u6f84\u5e73\u4e43 \u662f\u5144\u5f1f\u4e4b\u8f88\uff0c\u6211\u610f\u5373\u8bf7\u7687\u4e0a\u6b63\u5f0f\u5c01\u4ed6\u4e3a\u5927\u7406\u7684\u5e73\u7ae0\u3002\u201d\u5218\u516c\u516c\u9053\uff1a\u201c\u8fd9\u6837\u5c31\u80fd\u9632\u6b62\u5f97\u4e86\u5927\u7406 \u7684\u53db\u4e71\u4e48\uff1f\u201d\u6c90\u742e\u9053\uff1a\u201c\u671d\u5ef7\u5bf9\u4ed6\u4f5c\u5927\u7406\u5e73\u7ae0\uff0c\u8fd9\u53ea\u662f\u4e00\u4e2a\u865a\u8854\uff0c\u5b9e\u9645\u5374\u8981\u4ed6\u5c45\u8e0f\u6606\u660e\uff0c\u53eb \u9065\u9886\u5927\u7406\u7684\u5e73\u7ae0\u4e8b\u3002\u5927\u7406\u7684\u767e\u5b98\uff0c\u91cd\u8981\u7684\u804c\u4f4d\uff0c\u5f53\u7136\u8fd8\u662f\u671d\u5ef7\u6240\u6d3e\u3002\u672c\u671d\u653f\u5236\uff0c\u4eac\u5b98\u4e5f\u53ef\u4ee5 \u9065\u9886\u8fb9\u519b\uff0c\u628a\u6bb5\u6f84\u82cd\u7f81\u7559\u5728\u6606\u660e\uff0c\u53eb\u4ed6\u9065\u9886\u5927\u7406\u7684\u5e73\u7ae0\u4e4b\u4e8b\uff0c\u60f3\u6765\u4e5f\u662f\u884c\u5f97\u901a\u7684\uff0c\u201d\u5218\u516c\u516c \u9053\uff1a\u201c\u884c\u662f\u884c\u5f97\u901a\uff0c\u4f46\u516c\u7239\u600e\u80fd\u4fdd\u5f97\u5927\u7406\u7684\u6bb5\u5bb6\u4ece\u6b64\u4fbf\u6d88\u5f25\u7978\u5fc3\uff1f\u201d\u6c90\u742e\u9053\uff1a \u3000\u3000\u201c\u6bb5\u5bb6\u5728\u5b8b\u4ee3\u4e4b\u65f6\uff0c\u5728\u5927\u7406\u81ea\u5efa\u56fd\u53f7\uff0c\u81ea\u7acb\u4e3a\u738b\uff1b\u81f3\u5143\u4ee3\u4e4b\u65f6\uff0c\u5927\u7406\u56fd\u706d\uff0c\u6bb5\u5bb6\u4ecd\u7136\u4e16 \u88ad\u5e73\u7ae0\u4e8b\u5de5\u5230\u4e86\u672c\u671d\uff0c\u53ea\u7ed9\u4ed6\u4eec\u4e16\u88ad\u201c\u77e5\u5e73\u7ae0\u4e8b\u201d\uff0c\u5b98\u8854\u804c\u6743\uff0c\u4e00\u524a\u518d\u524a\uff0c\u53ef\u80fd\u56e0\u6b64\u800c\u62db\u81f4 \u6028\u6124\u3002\u54b1\u4eec\u5982\u4eca\u7ed9\u6bb5\u6f84\u82cd\u5b9e\u6388\u5e73\u7ae0\uff0c\u7b97\u7ed9\u4e86\u4ed6\u4eec\u6bb5\u5bb6\u7684\u9762\u5b50\u3002\u4ed6\u4eec\u8317\u7136\u8fd8\u8981\u53db\u4e71\uff0c\u90a3\u4e48\u54b1\u4eec \u7684\u8ba8\u4f10\u4e5f\u5c31\u5e08\u51fa\u6709\u540d\u3002\u800c\u4e14\u6bb5\u6f84\u82cd\u4ee5\u9a78\u9a6c\u4e4b\u5c0a\u6765\u5f52\uff0c\u54b1\u4eec\u7ed9\u4ed6\u865a\u8854\uff0c\u7ba1\u8f96\u5927\u7406\uff0c\u6b63\u662f\u540d\u6b63\u8a00 \u987a\u3002\u8d81\u6b64\u4e5f\u6b63\u597d\u524a\u6bb5\u6f84\u5e73\u7684\u6743\u67c4\uff0c\u8fd9\u5c82\u4e0d\u662f\u5206\u800c\u6cbb\u4e4b\uff0c\u4e00\u4e3e\u4e24\u5f97\u4e4b\u7b56\uff1f\u201d\u5176\u5b9e\u5927\u7406\u4eba\u8981\u9a71\u9010 \u660e\u671d\u5b98\u540f\uff0c\u6b63\u662f\u56e0\u4e3a\u4e0d\u582a\u82db\u653f\u4e4b\u6405\uff0c\u4e0d\u7518\u660e\u671d\u628a\u4ed6\u4eec\u5f53\u4f5c\u88ab\u5f81\u670d\u7684\u86ee\u4eba\u6765\u7edf\u6cbb\uff0c\u5012\u5e76\u975e\u6bb5\u5bb6 \u4e3a\u4e86\u81ea\u5df1\u4e00\u5bb6\u7684\u8363\u534e\u5bcc\u8d35\u7684\u3002\u4e0d\u8fc7\u5f53\u65f6\u9ad8\u5b98\u663e\u7235\uff0c\u5927\u90fd\u53ea\u770b\u5230\u4e2a\u4eba\uff0c\u770b\u4e0d\u5230\u8001\u767e\u59d3\uff0c\u6240\u4ee5\u4fbf \u628a\u5927\u7406\u7684\u201c\u4e71\u4e8b\u201d\u770b\u6210\u662f\u4e2a\u4eba\u7684\u6743\u4f4d\u4e4b\u5c0a\u3002\u50cf\u6c90\u70f7\u7684\u4e0d\u80af\u7528\u5175\uff0c\u5df2\u7ecf\u7b97\u662f\u8f83\u597d\u7684\u4e86\u3002\u4e0d\u8fc7\u6c90 \u742e\u4e5f\u6709\u79c1\u5fc3\uff0c\u4ed6\u4e4b\u6240\u4ee5\u60f3\u628a\u6bb5\u6f84\u82cd\u7f81\u7559\u6606\u660e\uff0c\u5b9e\u662f\u60f3\u4fbf\u4e8e\u81ea\u5df1\u7684\u64cd\u7eb5\u3002 \u3000\u3000\u90a3\u5218\u516c\u516c\u542c\u4e86\u6c90\u742e\u4e4b\u7b56\uff0c\u6c89\u541f\u4e0d\u8bed\uff0c\u5ffd\u89c1\u4e00\u4e2a\u95e8\u56ca\uff0c\u5306\u5306\u5fd9\u5fd9\u5730\u8dd1\u5230\u6c34\u68da\u6765\u3002 \u3000\u3000\u6c90\u742e\u8ba4\u5f97\u5979\u662f\u4e0a\u623f\u670d\u4f8d\u592b\u4eba\u7684\u4e00\u4e2a\u4e2b\u73af\uff0c\u559d\u9053\uff1a\u201c\u597d\u6ca1\u89c4\u77e9\uff0c\u6211\u4e0d\u53eb\u4f60\uff0c\u4f60\u51fa\u6765\u505a\u4ec0 \u4e48\uff1f\u201d\u90a3\u4e2b\u73af\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u5c0f\u59d0\u2014\u2014\u201d\u6c90\u742e\u6012\u9053\uff1a\u201c\u5c0f\u59d0\u4ec0\u4e48\uff1f\u201d\u90a3\u4e2b\u73af\u8bf4\u9053\uff1a\u201c\u5c0f\u59d0\u5979\u8d70 \u6389\u5566\u3002\u201d\u539f\u6765\u6c90\u592b\u4eba\u5230\u4e86\u638c\u706f\u65f6\u5206\uff0c\u8fd8\u4e0d\u89c1\u7231\u5973\uff0c\u5fc3\u4e2d\u614c\u4e71\uff0c\u6545\u6b64\u9063\u4e2b\u73af\u524d\u6765\u7980\u62a5\u3002\u6c90\u592b\u4eba \u5e74\u8001\u591a\u75c5\uff0c\u957f\u5e74\u793c\u4f5b\uff0c\u4e0d\u95ee\u5916\u4e8b\uff0c\u4e0e\u4e08\u592b\u4e5f\u7ecf\u5e38\u662f\u6570\u65e5\u4e00\u89c1\u3002\u5979\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u4e08\u592b\u4ee4\u665a\u5bb4\u8bf7 \u671d\u4e2d\u8d35\u5bbe\u3002 \u3000\u3000\u6c90\u742e\u9762\u8272\u4e00\u53d8\uff0c\u5389\u58f0\u65a5\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u5927\u60ca\u5c0f\u602a\uff01\u5c0f\u59d0\u662f\u6211\u53eb\u5979\u5230\u6768\u5bb6\u53bb\u63a5\u5979\u7684\u59e8\u6bcd \u7684\uff0c\u8bb8\u662f\u59e8\u6bcd\u5c06\u5979\u7559\u4e0b\u4e86\uff0c\u8981\u4f60\u7740\u6025\u505a\u4ec0\u4e48\uff01\u201d\u987b\u77e5\u5728\u90a3\u65f6\u5019\uff0c\u4ed5\u5ba6\u4e4b\u5bb6\uff0c\u6700\u8bb2\u672d\u6559\uff0c\u5343\u91d1 \u5c0f\u59d0\uff0c\u8db3\u4e0d\u51fa\u6237\uff0c\u5076\u4e00\u51fa\u95e8\uff0c\u4e5f\u662f\u4e58\u8f66\u5750\u8f7f\uff0c\u5728\u4e2b\u73af\u5a62\u4ec6\u7c07\u62e5\u4e4b\u4e0b\uff0c\u95f2\u4eba\u8f7b\u6613\u4e0d\u80fd\u4e00\u89c1\u3002\u6c90 \u742e\u7684\u5973\u513f\uff0c\u8eab\u4efd\u4ec5\u7565\u6b21\u4e8e\u201c\u90e1\u4e3b\u201d\uff08\u4eb2\u738b\u3001\u85e9\u738b\u4e4b\u5973\u79f0\u90e1\u4e3b\uff09\uff0c\u6bd4\u4ed5\u5ba6\u4e4b\u5bb6\u7684\u201c\u5343\u91d1\u5c0f\u59d0\u201d \u5c0a\u8d35\u4f55\u6b62\u5341\u500d\uff1f\u800c\u4eca\u8fd9\u4e2b\u73af\u5728\u94a6\u5dee\u5927\u81e3\u3001\u5185\u5e9c\u603b\u7ba1\u4e4b\u524d\uff0c\u7adf\u7136\u76f4\u8bf4\u4ed6\u7684\u5973\u513f\u201c\u8d70\u6389\u201d\uff0c\u4e0d\u7ba1 \u662f\u5426\u4e8b\u5b9e\uff0c\u90fd\u662f\u5927\u5931\u9762\u5b50\u3002\u6545\u6b64\u6c90\u742e\u52c3\u7136\u5927\u6012\uff0c\u6025\u5fd9\u5389\u58f0\u65a5\u8d23\u4e2b\u73af\uff0c\u610f\u56fe\u63a9\u9970\u3002 \u3000\u3000\u90a3\u4e2b\u73af\u624b\u8db3\u65e0\u9519\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5c0f\u59d0\u82e5\u662f\u53bb\u63a5\u5979\u7684\u59e8\u6bcd\uff0c\u592b\u4eba\u7109\u6709\u4e0d\u77e5\u4e4b\u7406\u3002\u201d\u88ab\u6c90\u742e \u65a5\u8d23\uff0c\u6781\u611f\u51a4\u5c48\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u592b\u4eba\uff0c\u592b\u4eba\u2014\u2014\u201d\u6c90\u742e\u6325\u624b\u65a5\u9053\uff1a\u201c\u56de\u53bb\u7ed9\u592b\u4eba\u7096\u71d5\u7a9d\uff0c\u7410 \u788e\u5c0f\u4e8b\uff0c\u4e0d\u8bb8\u6765\u9ebb\u70e6\u6211\u3002\u5feb\u7ed9\u6211\u6eda\uff01\u201d\u90a3\u4e2b\u73af\u4e0d\u6562\u518d\u8bf4\uff0c\u5fcd\u7740\u773c\u6cea\uff0c\u8d70\u51fa\u6c34\u69ad\uff0c\u526f\u5c06\u519b\u738b\u9547 \u5357\u770b\u5728\u773c\u91cc\uff0c\u60f3\u8d77\u6628\u65e5\u6c90\u71d5\u4e5f\u66fe\u5230\u57ce\u968d\u5e99\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5927\u8d77\u601d\u7591\u3002 \u3000\u3000\u6c90\u742e\u4ea6\u662f\u60f6\u60d1\u4e0d\u5b89\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5973\u513f\u77e5\u4e66\u8bc6\u793c\uff0c\u6c89\u9759\u7aef\u5e84\uff0c\u4f55\u4ee5\u4e0d\u7980\u544a\u7236\u6bcd\uff0c\u79c1\u51fa\u516c \u5e9c\uff0c\u81f3\u4eca\u672a\u56de\uff1f\u201d\u7a81\u7136\u8054\u60f3\u5230\u6c90\u78f7\u7684\u80e1\u4f5c\u975e\u4e3a\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u4e00\u51c9\uff0c\u795e\u8272\u4e4b\u95f4\uff0c\u4e5f\u63a9\u9970\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u90a3\u5218\u516c\u516c\u6025\u5fd9\u5c06\u8bdd\u9898\u91cd\u65b0\u63d0\u8d77\uff0c\u51b2\u6de1\u8fd9\u4e0d\u6109\u5feb\u7684\u6c14\u6c1b\u3002\u95ee\u9053\uff1a\u201c\u516c\u7239\u521a\u624d\u6240\u8bf4\u7684\u6000\u67d4\u4e4b \u7b56\uff0c\u597d\u867d\u662f\u597d\uff0c\u4f46\u8ba8\u4ee3\u4e4b\u4e8b\uff0c\u4e5f\u5f97\u65e9\u6709\u51c6\u5907\uff0c\u65b9\u662f\u4e24\u5168\u4e4b\u7b56\uff0c\u4e0d\u77e5\u516c\u7239\u610f\u4e0b\u5982\u4f55\uff1f\u201d\u6c90\u742e \u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\u3002\u201d\u9633\u5b97\u6d77\u9053\uff1a\u201c\u90a3\u4f4d\u6bb5\u6f84\u82cd\u548c\u6ce2\u65af\u516c\u4e3b\uff0c\u4f55\u65f6\u65b9\u5230\u6606\u660e\uff1f\u600e\u5730\u53eb\u4ed6\u77e5\u9053\u516c \u7239\u7684\u597d\u610f\uff1f\u201d\u6c90\u742e\u7b11\u9053\uff1a\u201c\u6211\u65e9\u5df2\u6d3e\u4eba\u53bb\u8fce\u63a5\u4ed6\u4eec\u4e86\u3002\u201d\u56de\u987e\u5de6\u53f3\u9053\uff1a\u201c\u770b\u65b9\u7edf\u9886\u56de\u6765\u4e86\u6ca1 \u6709\uff1f\u201d\u8ddf\u968f\u7684\u4e0a\u524d\u7980\u9053\uff1a\u201c\u65b9\u7edf\u9886\u56de\u6765\u5df2\u6709\u4e00\u4e2a\u65f6\u8fb0\u4e86\uff0c\u4ed6\u8bf4\u4e0d\u65b9\u4fbf\u6765\u89c1\u56fd\u516c\u3002\u201d \u3000\u3000\u6c90\u742e\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u90fd\u662f\u81ea\u5df1\u4eba\uff0c\u6709\u4f55\u4e0d\u4fbf\uff1f\u9633\u603b\u7ba1\u5728\u6b64\uff0c\u6b63\u597d\u6307\u70b9\u4ed6 \u4eec\uff0c\u5feb\u53eb\u4ed6\u548c\u624b\u4e0b\u4eba\u90fd\u6765\u62dc\u89c1\u3002\u201d\u9633\u5b97\u6d77\u9053\uff1a\u201c\u65b9\u7edf\u9886\u662f\u4e0d\u662f\u6ec7\u5357\u8457\u540d\u7684\u52c7\u58eb\u65b9\u5730\u521a\uff0c\u95fb\u8bf4 \u4ed6\u66fe\u8d64\u624b\u7a7a\u62f3\uff0c\u6253\u670d\u4e3d\u6c5f\u7684\u5341\u516b\u5cd2\u5cd2\u4e3b\uff0c\u5728\u4e0b\u4ef0\u5e55\u5f97\u5f88\uff0c\u6307\u70b9\u90a3\u662f\u592a\u4e0d\u6562\u5f53\u3002\u201d\u6c90\u742e\u542c\u5f97\u9633 \u5b97\u6d77\u4e5f\u79f0\u8d5e\u4ed6\u7684\u6b66\u571f\u7edf\u9886\uff0c\u5fc3\u4e2d\u5927\u60a6\uff0c\u8fde\u58f0\u5730\u53eb\u624b\u4e0b\u53bb\u50ac\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u65b9\u5730\u521a\u5e26\u9886\u56db\u4e2a\u6b66\u4e0a\u6765\u5230\uff0c\u4e00\u8fdb\u5c0f\u69ad\uff0c\u4f17\u4eba\u90fd\u662f\u5927\u5403\u4e00\u60ca\uff01 \u3000\u3000\u53ea\u89c1\u90a3\u56db\u4e2a\u6b66\u4e0a\u9762\u9752\u5507\u80bf\uff0c\u5305\u5934\u624e\u81c2\uff0c\u4e00\u4e2a\u4e2a\u5782\u5934\u4e27\u6c14\uff0c\u597d\u50cf\u6597\u8d25\u4e86\u7684\u516c\u9e21\uff01\u65b9\u5730\u521a\u6bd4 \u8f83\u597d\u4e9b\uff0c\u80a9\u5934\u4e0a\u4e5f\u662f\u8840\u8ff9\u6591\u6591\uff0c\u672a\u66fe\u62b9\u51c0\u3002\u6c90\u742e\u6c14\u5f97\u77aa\u76ee\u7ed3\u820c\uff0c\u597d\u534a\u664c\u624d\u8bf4\u51fa\u58f0\u6765\uff0c\u559d\u9053\uff1a \u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u65b9\u5730\u521a\u9053\uff1a\u201c\u6211\u4eec\u5949\u547d\u9080\u8bf7\u6ce2\u65af\u516c\u4e3b\u548c\u9a78\u9a6c\u5165\u57ce\uff0c\u4e0d\u6599\u4ed6\u4eec\u975e\u4f46\u4e0d\u9886\u516c\u7239\u7684\u60c5\uff0c\u53cd\u800c\u53eb \u4eba\u5c06\u6211\u4eec\u6253\u4e86\uff01\u201d\u6c90\u742e\u9053\uff1a\u201c\u6bb5\u6f84\u82cd\u54ea\u6765\u7684\u519b\u9a6c\uff1f\u201d\u8981\u77e5\u65b9\u5730\u521a\u662f\u6ec7\u5357\u7b2c\u4e00\u52c7\u58eb\uff0c\u4ed6\u624b\u4e0b\u7684 \u56db\u4e2a\u6b66\u58eb\uff0c\u4e5f\u90fd\u8db3\u4ee5\u529b\u654c\u767e\u592b\uff0c\u6545\u6b64\u6c90\u742e\u6709\u6b64\u4e00\u95ee\u3002\u65b9\u5730\u521a\u5782\u5934\u8bf4\u9053\uff1a\u201c\u5c31\u53ea\u4e24\u4eba\uff01\u201d\u6c90\u743c \u4e00\u6c14\u975e\u540c\u5c0f\u53ef\uff0c\u559d\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u5c31\u53ea\u4e24\u4eba\uff1f\u4f60\u4eec\u662f\u996d\u6876\u5417\uff1f\u201d\u9633\u5b97\u6d77\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u662f\u600e\u4e48\u6837 \u7684\u4e24\u4e2a\u4eba\uff1f\u201d\u65b9\u5730\u521a\u9053\uff1a\u201c\u662f\u4e00\u9ed1\u4e00\u767d\u7684\u4e24\u4e2a\u5370\u5ea6\u4eba\u3002\u201d \u3000\u3000\u9633\u5b97\u6d77\u7b11\u9053\uff1a\u201c\u516c\u7239\u8fd9\u5c31\u4e0d\u80fd\u602a\u4ed6\u4eec\u4e86\u3002\u8fd9\u4e24\u4e2a\u4eba\u540d\u53eb\u9ed1\u767d\u6469\u8bc3\uff0c\u662f\u51fa\u540d\u7684\u76d7\u5b9d\u8d3c\uff0c\u5341 \u5e74\u524d\u5728\u4eac\u5e08\u4e5f\u66fe\u505a\u4e0b\u6848\u5b50\uff0c\u5f53\u65f6\u7684\u5927\u5185\u603b\u7ba1\u5eb7\u8d85\u6d77\u4e5f\u66fe\u8d25\u7ed9\u4ed6\u4eec\u3002\u82e5\u662f\u4ed6\u4eec\uff0c\u6211\u4e5f\u6ca1\u6709\u628a\u63e1 \u51c6\u80dc\u3002\u563f\uff0c\u563f\uff0c\u65b9\u7edf\u9886\u53ea\u53d7\u4e86\u4e00\u70b9\u8f7b\u4f24\uff0c\u786e\u662f\u540d\u4e0d\u865a\u4f20\uff01\u7406\u5b9c\u8d50\u8d4f\uff01\u201d\u4eb2\u81ea\u659f\u4e86\u4e00\u676f\u9152\u7ed9\u65b9 \u5730\u521a\uff0c\u6c90\u742e\u89c1\u9633\u5b97\u6d77\u5c06\u654c\u4eba\u8bf4\u5f97\u5982\u6b64\u5389\u5bb3\uff0c\u867d\u7136\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u4e2d\u6012\u6c14\u5982\u5df2\u6d88\u6563\uff0c\u6b63\u60f3\u8be2\u95ee\uff0c \u90a3\u5218\u516c\u516c\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u6ca1\u6709\u8bf4\u6e05\u695a\u5417\uff1f\u6bb5\u6f84\u82cd\u83ab\u975e\u4e0d\u4fe1\u4f60\u4eec\u662f\u6c90\u56fd\u516c\u6d3e\u6765\u7684\u4eba\uff1f\u201d\u65b9\u5730 \u521a\u6ee1\u809a\u76ae\u95f7\u6c14\uff0c\u6068\u6068\u8bf4\u9053\uff1a\u201c\u6211\u5c06\u516c\u7239\u4eb2\u7b14\u7684\u51fd\u4ef6\u4ea4\u4e0e\u4ed6\u4eec\uff0c\u4fe1\u5c01\u4e0a\u76d6\u6709\u6c90\u56fd\u516c\u7684\u7ae0\u8bb0\uff0c \u54fc\uff0c\u54fc\uff0c\u4ed6\u4eec\u8fde\u770b\u4e5f\u4e0d\u770b\uff0c\u5c31\u6495\u4e2a\u7a00\u70c2\uff0c\u8981\u4e0d\u7136\u6211\u4eec\u4e5f\u4e0d\u4f1a\u4e0e\u4ed6\u4eec\u52a8\u624b\u3002\u201d\u539f\u6765\u6bb5\u6f84\u82cd\u5728\u8d35 \u5dde\u4e0a\u8fc7\u5047\u85e9\u738b\u7684\u4e00\u6b21\u5f53\uff0c\u53ea\u9063\u8fd9\u6b21\u4e5f\u662f\u5047\u7684\uff0c\u6240\u4ee5\u53eb\u9ed1\u767d\u6469\u8bc3\u7edd\u4e0d\u7559\u60c5\u3002 \u3000\u3000\u5218\u516c\u516c\u51b7\u7b11\u9053\uff1a\u201c\u5982\u4f55\uff1f\u4ed6\u4e00\u89c1\u9762\u4fbf\u6253\uff0c\u5bf9\u516c\u7239\u7b80\u76f4\u662f\u4e0d\u7559\u4f59\u5730\uff0c\u8bf7\u95ee\u516c\u7239\uff0c\u600e\u6837\u6000 \u67d4\uff1f\u201d\u6c90\u742e\u6012\u9053\uff1a\u201c\u6bb5\u6f84\u82cd\u8fd9\u6837\u4e0d\u8bc6\u62ac\u4e3e\uff0c\u563f\uff0c\u90a3\u662f\u6ca1\u5f97\u8bf4\u7684\u4e86\u3002\u6211\u5175\u7834\u5927\u7406\u4e4b\u65e5\uff0c\u5b9a\u8981\u5c06 \u4ed6\u63b3\u6765\u6cbb\u7f6a\u3002\u201d\u5218\u516c\u516c\u7b11\u9053\uff1a\u201c\u8fd9\u624d\u662f\u5475\uff0c\u548c\u86ee\u5b50\u4eec\u8bb2\u4ec0\u4e48\u9053\u7406\uff1f\u65b9\u7edf\u9886\uff0c\u4f60\u4eec\u56e0\u516c\u53d7\u4f24\uff0c \u90fd\u5750\u4e0b\u6765\u559d\u9152\u3002\u201d\u5218\u516c\u516c\u548c\u9633\u5b97\u6d77\u4e00\u80a1\u52b2\u5730\u529d\u6170\u65b9\u5730\u521a\uff0c\u5b9e\u662f\u60f3\u5c06\u4ed6\u62c9\u62e2\u8fc7\u6765\uff0c\u6536\u4e3a\u5df1\u7528\uff0c \u6c90\u742e\u4eba\u6781\u7cbe\u660e\uff0c\u770b\u5728\u773c\u5185\uff0c\u7acb\u77e5\u5176\u610f\uff0c\u5fc3\u4e2d\u751a\u662f\u4e0d\u5feb\u3002 \u3000\u3000\u559d\u4e86\u4e24\u676f\uff0c\u6c90\u742e\u8bf4\u9053\uff1a\u201c\u9ed1\u767d\u6469\u8bc3\u65e2\u7136\u5982\u6b64\u5389\u5bb3\uff0c\u9633\u5927\u603b\u7ba1\u53c8\u4e0d\u80fd\u4e45\u5728\u6606\u660e\uff0c\u4f55\u4eba\u80fd \u5236\uff1f\u201d\u9633\u5b97\u6d77\u7b11\u9053\uff1a\u201c\u9ed1\u767d\u6469\u8bc3\u867d\u7136\u5389\u5bb3\uff0c\u53ea\u8981\u6211\u7684\u5e08\u53d4\u51fa\u624b\uff0c\u5b9a\u7136\u624b\u5230\u64d2\u6765\u3002\u201d\u4e0a\u5ea7\u7684\u90a3 \u4e2a\u64c5\u58eb\u8fd9\u65f6\u624d\u5f00\u58f0\u8bf4\u9053\uff1a\u201c\u5b97\u6d77\uff0c\u4f60\u4e5f\u4e0d\u53ef\u592a\u8fc7\u8f7b\u654c\uff0c\u82e5\u662f\u4f60\u7684\u5e08\u7236\u51fa\u624b\uff0c\u9ed1\u767d\u6469\u8bc3\u81ea\u662f\u4e0d \u582a\u4e00\u51fb\u3002\u6211\u5417\uff0c\u5927\u7ea6\u8fd8\u5f97\u548c\u4ed6\u4eec\u6253\u4e00\u4e24\u767e\u62db\uff0c\u624d\u80fd\u5c06\u4ed6\u4eec\u964d\u670d\u3002\u201d\u6c90\u742e\u559c\u9053\uff1a\u201c\u90a3\u5c31\u5168\u4ed7\u9057 \u957f\u51fa\u529b\u4e86\u3002\u201d\u65b9\u5730\u521a\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6d2a\u5ca9\u9053\u957f\u4e48\uff1f\u5931\u656c\uff0c\u5931\u656c\uff01\u201d\u6025\u5fd9\u66ff\u4ed6\u659f\u9152\u3002\u8d64\u971e\u9053\u4eba\u53ea \u6709\u4e00\u4e2a\u5e08\u5f1f\uff0c\u5c31\u662f\u8fd9\u4e2a\u6d2a\u5ca9\u9053\u4eba\u3002\u8d64\u971e\u9053\u4eba\u540d\u5934\u592a\u54cd\uff0c\u4ed6\u7684\u5e08\u5f1f\u81ea\u662f\u8fdc\u8fdc\u4e0d\u53ca\uff0c\u4f46\u6b66\u6797\u4e2d\u4eba \u5374\u6ca1\u6709\u4e0d\u77e5\u9053\u7684\u3002 \u3000\u3000\u6d2a\u5ca9\u9053\u4eba\u5927\u6a21\u5927\u6837\u5730\u559d\u4e86\u65b9\u5730\u521a\u7684\u656c\u9152\uff0c\u8bf4\u9053\uff1a\u201c\u5b97\u6d77\u8fd9\u6b21\u9080\u6211\u5230\u4e91\u5357\u6765\uff0c\u672c\u6765\u5c31\u662f\u51c6 \u5907\u5bf9\u4ed8\u4e00\u4e2a\u6bd4\u9ed1\u767d\u6469\u8bc3\u66f4\u5389\u5bb3\u7684\u5f3a\u654c\u3002\u201d\u6c90\u742e\u5947\u9053\uff1a\u201c\u8c01\uff1f\u201d\u6d2a\u5ca9\u9053\u4eba\u9053\uff1a\u201c\u662f\u5f20\u4e39\u67ab\u3002\u542c \u8bf4\u4ed6\u6f5c\u5165\u4e91\u5357\uff0c\u73b0\u5728\u5df2\u5230\u5927\u7406\u53bb\u4e86\uff0c\u516c\u7239\u4e0d\u77e5\u9053\u4e48\uff1f\u201d\u6c90\u742e\u5403\u4e86\u4e00\u60ca\uff0c\u5f20\u4e39\u67ab\u5f53\u5e74\u8f85\u4f50\u4e8e \u8c26\uff0c\u6253\u8d25\u4e5f\u5148\uff0c\u53c8\u4e0e\u4e91\u91cd\u6df1\u5165\u74e6\u523a\uff0c\u8fce\u63a5\u5f53\u4eca\u7684\u7687\u4e0a\u56de\u671d\uff0c\u58f0\u9707\u5929\u4e0b\u3002\u6c90\u742e\u867d\u7136\u50fb\u5904\u4e91\u5357\uff0c \u4ea6\u6709\u77e5\u95fb\u3002\u95ee\u9053\uff1a\u201c\u9053\u957f\u548c\u5f20\u4e39\u67ab\u6709\u751a\u4ec7\u6028\uff1f\u201d\u9633\u5b97\u6d77\u7b11\u9053\uff0c\u201c\u5f20\u4e39\u67ab\u662f\u4e8e\u8c26\u7684\u515a\u7fbd\uff0c\u516c\u7239 \u8fd8\u4e0d\u77e5\u4e48\uff1f\u90a3\u662f\u7687\u4e0a\u6240\u8981\u7f09\u62ff\u7684\u94a6\u72af\u3002\u4e0d\u8fc7\u6b64\u4eba\u4ea4\u6e38\u5e7f\u9614\uff0c\u6d88\u606f\u7075\u901a\uff0c\u7f09\u62ff\u4e4b\u4e8b\uff0c\u7edd\u4e0d\u53ef\u4ee5 \u5f20\u626c\u51fa\u53bb\u3002\u201d\u6c90\u742e\u5fc3\u9053\uff1a\u201c\u4e8e\u8c26\u8d64\u5fc3\u4e3a\u56fd\uff0c\u60e8\u906d\u6740\u622e\uff0c\u4e0d\u8bf4\u522b\u4eba\uff0c\u8fde\u6211\u4e5f\u4e0d\u670d\u6c14\u3002\u7687\u4e0a\u518d\u8981 \u6740\u5f20\u4e39\u67ab\uff0c\u90a3\u5c82\u4e0d\u6700\u6069\u5c06\u4ec7\u62a5\u4e48\uff1f\u201d\u4ed6\u60f3\u662f\u5982\u6b64\u60f3\uff0c\u795e\u8272\u4e0a\u5374\u4e0d\u6562\u9732\u51fa\u4e1d\u6beb\uff0c\u8bf4\u9053\uff1a\u201c\u5475\uff0c \u539f\u6765\u9633\u603b\u7ba1\u662f\u8bf7\u5e08\u53d4\u51fa\u5c71\uff0c\u7f09\u62ff\u53db\u9006\uff0c\u8fd9\u7b49\u4e3a\u7687\u4e0a\u51fa\u529b\uff0c\u53ef\u4f69\uff0c\u53ef\u4f69\uff01\u201d\u6d2a\u5ca9\u9053\u4eba\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u5f20\u4e39\u67ab\u7eb5\u6a2a\u4e2d\u539f\uff0c\u83b7\u5f97\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\u7684\u540d\u5934\uff0c\u82e5\u4e0d\u662f\u6211\uff0c\u5927\u7ea6\u4e5f\u65e0\u4eba\u6562\u6349\u4ed6\u4e86\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u4f0f\u5728\u5047\u5c71\u77f3\u540e\uff0c\u542c\u5f97\u4ed6\u4eec\u5927\u5439\u6cd5\u87ba\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u725b\u9f3b\u5b50\u9053\u58eb\u82e5\u78b0 \u5230\u6211\u7684\u5e08\u7236\uff0c\u4e0d\u5c06\u4ed6\u7684\u9f3b\u5b50\u524a\u4e0b\u624d\u602a\u3002\u201d\u5979\u6700\u656c\u7231\u5e08\u7236\uff0c\u542c\u5f97\u6d2a\u5ca9\u8fd9\u4eba\u8bcb\u6bc1\u5979\u7684\u5e08\u7236\uff0c\u51e0\u4e4e \u5fcd\u8010\u4e0d\u4f4f\uff0c\u60f3\u51fa\u53bb\u5c06\u4ed6\u523a\u4e00\u4e2a\u7a9f\u7abf\u3002\u201d \u3000\u3000\u6c90\u742e\u597d\u5947\u95ee\u9053\uff1a\u201c\u90a3\u5f20\u4e39\u67ab\u662f\u600e\u4e48\u6a21\u6837\uff1f\u9633\u603b\u7ba1\u53ef\u89c1\u8fc7\u4e48\uff1f\u201d\u9633\u5b97\u6d77\u7b11\u9053\uff1a\u201c\u89c1\u662f\u6ca1\u89c1 \u8fc7\u3002\u6211\u8eab\u8fb9\u5e26\u6709\u4ed6\u7684\u56fe\u50cf\u591a\u5e45\uff0c\u73b0\u5728\u9001\u4e00\u5e45\u7ed9\u4f60\uff0c\u8bf7\u516c\u7239\u8ba9\u624b\u4e0b\u4eba\u7559\u610f\u3002\u83ab\u53eb\u4ed6\u6f5c\u5165\u6606 \u660e\u3002\u201d\u6c90\u742e\u5c06\u753b\u56fe\u4e00\u5c55\uff0c\u6538\u7136\u95f4\u795e\u8272\u5927\u53d8\uff0c\u9633\u5b97\u6d77\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u6c90\u742e\u559d\u4e86\u4e00\u5927\u676f\u9152\uff0c\u5fae\u7b11 \u8bf4\u9053\uff1a\u201c\u6211\u53ea\u9053\u5f20\u4e39\u67ab\u662f\u4e2a\u4e09\u5934\u516d\u81c2\u7684\u51f6\u795e\u6076\u715e\uff0c\u539f\u6765\u5374\u50cf\u4e2a\u98ce\u6d41\u6f47\u6d12\u7684\u4e66\u751f\uff01\u201d\u9633\u5b97\u6d77 \u9053\uff1a\u201c\u662f\u5475\uff0c\u602a\u4e0d\u5f97\u516c\u7239\u60ca\u8be7\u4e86\u3002\u201d \u3000\u3000\u559d\u4e86\u4e24\u676f\uff0c\u90a3\u5218\u516c\u516c\u5ffd\u9053\uff1a\u201c\u542c\u8bf4\u5c0f\u516c\u7239\u806a\u660e\u82f1\u4fca\uff0c\u6587\u6b66\u5168\u6750\uff0c\u4f55\u4e0d\u8bf7\u51fa\u6765\u4e00\u89c1\uff1f\u201d\u6c90 \u742e\u9053\uff1a\u201c\u5c0f\u513f\u987d\u52a3\u6210\u6027\uff0c\u600e\u6562\u5f53\u516c\u516c\u7f8e\u8a89\uff1f\u6211\u6b63\u8981\u4ed6\u95ed\u6237\u8bfb\u4e66\uff0c\u4e0d\u6562\u53eb\u4ed6\u70e6\u62e2\u8d35\u5ba2\u3002\u201d\u9633\u5b97 \u6d77\u9053\uff1a\u201c\u516c\u7239\u592a\u8c26\u865a\u4e86\u3002\u81ea\u53e4\u6709\u4e91\u77e5\u5b50\u83ab\u82e5\u7236\uff0c\u5c0f\u516c\u7239\u7684\u806a\u660e\u624d\u667a\uff0c\u5c3d\u4eba\u7686\u77e5\uff0c\u90a3\u90fd\u662f\u516c\u7239 \u6559\u8bf2\u7684\u529f\u52b3\u5475\uff01\u201d\u6c90\u742e\u5fc3\u5185\u6697\u60ca\uff0c\u6b63\u5728\u7422\u78e8\u9633\u5b97\u6d77\u7684\u8bf4\u8bdd\uff0c\u90a3\u5218\u516c\u516c\u53c8\u9053\uff1a\u201c\u55ef\uff0c\u542c\u8bf4\u6c90\u5c0f \u516c\u7239\u524d\u65e5\u4e3b\u6301\u57ce\u968d\u5e99\u7684\u843d\u6210\u5927\u5178\uff0c\u8f70\u52a8\u5168\u57ce\uff0c\u54b3\uff0c\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u4fbf\u80fd\u505a\u4e8b\uff0c\u4ed6\u65e5\u65e0\u53ef\u9650\u91cf\u3002\u656c \u8bf7\u5c0f\u516c\u7239\u51fa\u6765\u4e00\u89c1\u3002\u201d\u6c90\u742e\u7565\u4e00\u6c89\u541f\uff0c\u5429\u5490\u4e0b\u53bb\u9053\uff1a\u201c\u8bf7\u5c0f\u516c\u7239\u51fa\u6765\uff01\u201d\u4ed6\u5fc3\u4e2d\u5df2\u6253\u5b9a\u4e3b \u610f\uff0c\u60c5\u77e5\u5218\u516c\u516c\u4ed6\u4eec\u5df2\u7ecf\u77e5\u9053\u4e86\u6c90\u78f7\u7ed9\u4e8e\u8c26\u5efa\u5e99\u9020\u50cf\u4e4b\u4e8b\uff0c\u4ed6\u4eec\u65e2\u4e0d\u8bf4\u7834\uff0c\u81ea\u5df1\u4e5f\u5f53\u4e0d\u77e5\uff0c \u7b49\u4e0b\u5c06\u6c90\u78f7\u53eb\u51fa\u6765\uff0c\u5f53\u7740\u4ed6\u4eec\u7684\u8138\uff0c\u8d23\u9a82\u4e00\u987f\uff0c\u8981\u4ed6\u5c06\u5e99\u50cf\u6bc1\u53bb\uff0c\u7b97\u662f\u5fc3\u7167\u4e0d\u5ba3\uff0c\u4ea4\u4ee3\u6b64 \u4e8b\uff0c\u4e5f\u4fbf\u7f62\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u89c1\u90a3\u624b\u4e0b\u4eba\u795e\u8272\u5f20\u7687\uff0c\u5355\u8eab\u4e00\u4eba\uff0c\u5306\u5306\u8dd1\u56de\uff0c\u6c90\u742e\u95ee\u9053\uff1a\u201c\u5c0f\u516c\u7239\u4e3a\u4f55\u4e0d \u4e0e\u4f60\u4e00\u9053\u540c\u6765\uff1f\u662f\u5728\u6362\u8863\u670d\u4e48\uff1f\u201d\u90a3\u624b\u4e0b\u4eba\u55eb\u55eb\u5685\u5685\uff0c\u597d\u534a\u664c\u8bf4\u9053\uff1a\u201c\u5c0f\uff0c\u5c0f\uff0c\u5c0f\u516c\u7239\uff0c \u4ed6\uff0c\u4ed6\uff0c\u4ed6\u8dd1\u4e86\uff01\u201d \u3000\u3000\u6c90\u742e\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u53ea\u6709\u4e00\u5b50\u4e00\u5973\uff0c\u7231\u5982\u73cd\u5b9d\uff0c\u73b0\u5728\u5168\u90fd\u8dd1\u4e86\uff0c\u4e0d\u89c9\u5fc3\u5934\u75db\u5982\u5200 \u5272\u3002\u5218\u516c\u516c\u6545\u4f5c\u60ca\u8be7\uff0c\u53eb\u9053\uff1a\u201c\u600e\u4e48\u5c0f\u516c\u7239\u8dd1\u4e86\uff0c\u4ed6\u53c8\u6ca1\u505a\u9519\u4e8b\uff0c\u4e3a\u4f55\u9003\u8dd1\uff1f\u5440\uff0c\u60f3\u662f\u516c\u7239 \u7ba1\u5f97\u8fc7\u4e25\u4e86\uff01\u201d\u6c90\u742e\u5b9a\u4e00\u5b9a\u795e\uff0c\u51b7\u6c57\u76f4\u6d41\uff0c\u6025\u5fd9\u987a\u7740\u4ed6\u7684\u53e3\u6c14\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u65e9\u8bf4\u5c0f\u513f\u987d \u76ae\u6210\u6027\uff0c\u679c\u7136\u4ed6\u53c8\u95f9\u51fa\u4e8b\u4e86\u3002\u771f\u662f\u7ed9\u6211\u4e22\u8138\uff01\u201d\u9633\u5b97\u6d77\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5fc3\u4e2d\u601d\u91cf\uff0c\u82e5\u7136\u6c90\u56fd \u516c\u4f46\u76f4\u8bf4\u660e\u6c90\u78f7\u5efa\u5e99\u9020\u50cf\u4e4b\u7f09\uff0c\u5e94\u8be5\u5982\u4f55\u63aa\u8f9e\u3002\u6c90\u742e\u6012\u6c14\u51b2\u51b2\u5730\u8bf4\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u4e0d\u6b22\u559c\u8bfb \u4e66\uff0c\u4e00\u5b9a\u53c8\u662f\u6e9c\u51fa\u53bb\u770b\u82b1\u706f\u620f\u4e86\uff01\u201d \u3000\u3000\u5218\u516c\u516c\u9053\uff1a\u201c\u5c0f\u5b69\u5b50\u8d2a\u73a9\u4e5f\u662f\u6709\u7684\uff0c\u5bf9\u6c90\u742e\u7684\u4e3a\u513f\u5b50\u63a9\u9970\uff0c\u5927\u4e3a\u4e0d\u5feb\u3002\u6c90\u742e\u5ffd\u9053\uff1a\u201c\u5c0f \u513f\u987d\u52a3\u5143\u77e5\uff0c\u50cf\u521a\u624d\u6240\u8bf4\u7684\u5efa\u57ce\u968d\u5e99\u4e4b\u4e8b\uff0c\u5c31\u662f\u5927\u5927\u7684\u4e0d\u5bf9\u3002\u8fd9\u7b49\u662f\u611a\u592b\u611a\u5987\u7684\u6240\u4e3a\uff0c\u57ce \u968d\uff0c\u5351\u4e0d\u8db3\u9053\u7684\u5c0f\u795e\uff0c\u4ed6\u53bb\u8fdb\u9999\u53e9\u5934\u3002\u771f\u662f\u6210\u4f55\u4f53\u7edf\uff01\u201d\u9633\u5b8c\u6d77\u9053\uff1a\u201c\u542c\u8bf4\u8fd9\u57ce\u968d\u7684\u795e\u50cf\u4e5f \u4e0e\u522b\u5904\u4e0d\u540c\uff01\u201d\u6c90\u742e\u9053\uff1a\u201c\u8c01\u77e5\u9053\u4ed6\u53bb\u54ea\u91cc\u5f04\u6765\u7684\u90aa\u795e\u6728\u5076\uff1f\u5440\uff0c\u771f\u662f\u4e22\u5c3d\u6211\u7684\u8138\u76ae\uff0c\u660e\u5929 \u6211\u5c31\u9a6c\u4e0a\u6d3e\u4eba\u5c06\u795e\u5e99\u62c6\u6bc1\uff0c\u5c06\u5076\u50cf\u711a\u5316\uff0c\u518d\u6293\u4ed6\u56de\u6765\uff0c\u75db\u6253\u4e09\u767e\u5927\u677f\uff01\u201d \u3000\u3000\u5218\u516c\u516c\u8fd9\u65f6\u8138\u4e0a\u9732\u51fa\u4e00\u4e1d\u7b11\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u516c\u7239\u4e00\u65f6\u542c\u4eba\u5506\u6446\uff0c\u7ed9\u90aa\u795e\u5efa\u5e99\u9020\u50cf\uff0c\u8fd9\u4e5f \u4e0d\u8db3\u6df1\u602a\u3002\u6211\u6073\u6c42\u516c\u7239\u5c06\u5c0f\u516c\u7239\u7684\u8d23\u7f5a\u514d\u4e86\u3002\u5012\u662f\u90a3\u4e2a\u90aa\u795e\u6728\u5076\uff0c\u975e\u5f97\u75db\u6253\u4e09\u767e\u5927\u677f\uff0c\u7136\u540e \u518d\u711a\u5316\u4e0d\u53ef\uff01\u514d\u5f97\u90a3\u4e9b\u611a\u592b\u611a\u5987\u53d7\u60d1\uff01\u201d\u9633\u5b97\u6d77\u7b49\u540c\u58f0\u8bf4\u9053\uff1a\u201c\u5bf9\uff01\u90aa\u795e\u5076\u50cf\uff0c\u5e94\u8be5\u6253\u4e2a\u7a00 \u70c2\uff0c\u7acb\u523b\u711a\u5316\uff01\u201d \u3000\u3000\u8bdd\u58f0\u672a\u505c\uff0c\u5ffd\u89c1\u4e00\u4e2a\u5c11\u5973\u8d70\u5230\u7b75\u524d\uff0c\u5979\u8eab\u6cd5\u5feb\u6781\uff0c\u4f17\u4eba\u5728\u4e71\u54c4\u54c4\u4e4b\u9645\uff0c\u7adf\u4e0d\u77e5\u5979\u662f\u600e\u4e48 \u6765\u7684\u3002\u6c90\u742e\u8fd8\u4ee5\u4e3a\u5979\u662f\u4e2b\u73af\uff0c\u4e00\u770b\u4e4b\u4e0b\uff0c\u53ea\u89c1\u5979\u7a7f\u7740\u5973\u513f\u60ef\u7a7f\u7684\u4e00\u4ef6\u8863\u88f3\uff0c\u6bd4\u5973\u513f\u5927\u7ea6\u8981\u5c0f \u4e00\u4e24\u5c81\u7684\u5e74\u7eaa\uff0c\u5929\u59ff\u56fd\u8272\uff0c\u6bd4\u5973\u513f\u8fd8\u7f8e\u5f97\u591a\uff01\u6700\u5947\u602a\u7684\u662f\u5979\u795e\u6c14\u4e4b\u95f4\uff0c\u81ea\u6709\u4e00\u80a1\u5c0a\u4e25\uff0c\u7709\u5c16 \u5fae\u8e69\uff0c\u76c8\u76c8\u79cb\u6c34\u4e4b\u4e2d\uff0c\u9690\u85cf\u7740\u4e00\u80a1\u6028\u6124\u4e4b\u6c14\uff0c\u4ee4\u4eba\u609a\u7136\u751f\u60e7\uff0c\u5979\u53cc\u773c\u4e00\u626b\u5168\u573a\uff0c\u7adf\u4f3c\u5168\u4e0d\u628a \u8fd9\u4e9b\u4eba\u770b\u5728\u773c\u5185\u3002\u9633\u5b97\u6d77\u5927\u60ca\u5931\u8272\uff0c\u8fd9\u6b63\u662f\u4ed6\u5e7d\u7981\u5728\u6c34\u7262\u91cc\u7684\u4e8e\u627f\u73e0\uff01\u53ef\u662f\u5979\u5728\u6b64\u65f6\u6b64\u9645\u51fa \u73b0\uff0c\u9633\u5b97\u6d77\u5374\u4e5f\u4e0d\u6562\u5192\u7136\u52a8\u624b\uff01 \u3000\u3000\u970e\u65f6\u95f4\u6c34\u69ad\u91cc\u9759\u5f97\u8fde\u4e00\u6839\u9488\u8dcc\u5728\u5730\u4e0b\u4e5f\u542c\u5f97\u89c1\u54cd\u3002\u6c90\u742e\u60f6\u7136\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u4e8e\u627f\u73e0 \u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u7239\u7239\u53d7\u4e07\u6c11\u7231\u6234\uff0c\u656c\u7acb\u4e3a\u795e\u3002\u4f60\u4eec\u662f\u4e9b\u4ec0\u4e48\u4e1c\u897f\uff1b\u6562\u5c06\u6211\u7239\u7239\u7684\u795e\u50cf\u711a \u5316\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u4e3e\u5ea7\u9a9a\u7136\uff0c\u6c90\u742e\u8df3\u8d77\u6765\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u4e0d \u8bb8\u4f60\u4eec\u5c06\u6211\u7239\u7239\u7684\u795e\u50cf\u6363\u6bc1\uff01\u201d\u6c90\u742e\u9053\uff1a\u201c\u4f60\u7239\u7239\u662f\u8c01\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u7239\u7239\u662f\u5185\u9601\u5927\u5b66 \u58eb\u517c\u5175\u90e8\u5c1a\u4e66\u4e8e\u8c26\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u6c90\u742e\u9762\u8272\u5982\u6b7b\u3002\u867d\u7136\u57ce\u968d\u5e99\u50cf\uff0c\u5ea7\u4e2d\u4eba\u90fd\u77e5\u9053\u4e43\u662f\u4e8e\u8c26\uff0c \u4f46\u4e00\u8bf4\u7834\u4e86\uff0c\u5374\u662f\u4e0d\u53ef\u6536\u62fe\uff01\u9633\u5b97\u6d77\u559d\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u5feb\u628a\u8fd9\u5996\u5973\u62ff\u4e0b\u3002\u201d\u6c90\u742e\u4e5f\u559d\u9053\uff1a \u201c\u4f60\u771f\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\uff0c\u5982\u4f55\u6562\u5192\u79f0\u662f\u53db\u9006\u4e4b\u5973\uff01\u6211\u513f\u5b50\u5c82\u6709\u4e3a\u4f60\u7236\u4eb2\u9020\u50cf\u4e4b\u7406\uff0c\u80e1\u8bf4\u516b\u9053\uff0c \u5feb\u6eda\u51fa\u53bb\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u4e00\u8a00\u60ca\u7834\u80c6\uff0c\u6b63\u6c14\u5c5e\u5a25\u7709\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u6c34\u69ad\u5251\u5149\u5bd2\u3000\u6768\u679d\u632b\u654c\u3000\u77f3\u6797\u5947\u666f\u89c2\u3000\u9a8f\u9a6c\u8ffd\u98ce \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u6c34\u69ad\u5251\u5149\u5bd2\u3000\u6768\u679d\u632b\u654c\u3000\u77f3\u6797\u5947\u666f\u89c2\u3000\u9a8f\u9a6c\u8ffd\u98ce \u3000\u3000\u6c90\u56fd\u516c\u751f\u6015\u5979\u771f\u662f\u4e8e\u8c26\u4e4b\u5973\uff0c\u4e00\u62ff\u4e0b\u4e86\uff0c\u95ee\u51fa\u53e3\u4f9b\uff0c\u53ea\u6015\u81ea\u5df1\u7684\u513f\u5b50\u4e5f\u53d7\u5e72\u8fde\uff0c\u6240\u4ee5\u53e3 \u53e3\u58f0\u58f0\u6307\u5979\u5192\u8ba4\uff0c\u6068\u4e0d\u5f97\u65e9\u65e9\u5c06\u5979\u9001\u8d70\uff0c\u6545\u6b64\u53eb\u5979\u201c\u5feb\u6eda\u201d\uff0c\u8fd9\u5b9e\u662f\u7ed9\u5979\u6307\u660e\u4e00\u6761\u201c\u751f \u8def\u201d\uff0c\u597d\u8ba9\u5979\u81ea\u5df1\u201c\u843d\u53f0\u201d\uff1b\u9633\u5b97\u6d77\u660e\u77e5\u5979\u662f\u4e8e\u8c26\u4e4b\u5973\uff0c\u4f46\u788d\u4e8e\u6c90\u56fd\u516c\u7684\u9762\u5b50\uff0c\u5374\u4e5f\u4e0d\u6562\u5373 \u65f6\u52a8\u7c97\uff0c\u987a\u7740\u6c90\u56fd\u516c\u7684\u53e3\u6c14\u9a82\u5979\u5192\u8ba4\uff0c\u54ea\u77e5\u4e8e\u627f\u73e0\u7edd\u4e0d\u9886\u4f1a\u8fd9\u4e2a\u60c5\uff0c\u53ea\u89c1\u5979\u67f3\u7709\u4e00\u7ad6\uff0c\u6717\u58f0 \u8bf4\u9053\uff1a\u201c\u6211\u7239\u7239\u6276\u6301\u660e\u5ba4\uff0c\u72ec\u633d\u72c2\u6f9c\uff0c\u8d64\u80c6\u5fe0\u5fc3\uff0c\u5929\u4eba\u540c\u4ef0\u3002\u6211\u6709\u8fd9\u6837\u7684\u7239\u7239\uff0c\u6b63\u662f\u6781\u8db3\u5938 \u8000\u7684\u4e8b\u60c5\uff01\u4f55\u7528\u7f9e\u60ed\uff1f\u4f55\u987b\u6015\u8ba4\uff1f\u53ea\u6709\u4f60\u4eec\uff0c\u4e0d\u7406\u82cd\u751f\u75be\u82e6\uff0c\u4f46\u77e5\u9014\u541b\u4e4b\u6076\uff0c\u90a3\u624d\u771f\u662f\u6127\u5bf9 \u6211\u7684\u7239\u7239\uff01\u201d\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u6b63\u6c14\u51db\u7136\uff0c\u6c90\u742e\u5fc3\u5e95\u91cc\u5176\u5b9e\u751a\u662f\u4ef0\u6155\u4e8e\u8c26\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u505a\u58f0\u4e0d \u5f97\u3002\u9633\u5b97\u6d77\u8bf8\u4eba\uff0c\u52c3\u7136\u53d8\u8272\u3002\u4e8e\u627f\u73e0\u50b2\u7136\u4e0d\u60e7\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u5176\u5b9e\u5728\u5ea7\u8bf8\u4eba\uff0c\u8c01 \u4e0d\u77e5\u9053\u57ce\u968d\u5e99\u4e2d\u7684\u795e\u50cf\u4e43\u662f\u6211\u7684\u7239\u7239\uff1f\u4f60\u770b\u6b64\u4fe1\uff01\u201d\u5c06\u738b\u9547\u5357\u594f\u7980\u7687\u5e1d\u7684\u5bc6\u4fe1\uff0c\u500f\u5730\u638f\u4e86\u51fa \u6765\uff0c\u9012\u7ed9\u6c90\u742e\u3002 \u3000\u3000\u738b\u9547\u5357\u9762\u65e0\u4eba\u8272\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u4eba\u5f71\u4e00\u95ea\uff0c\u5495\u549a\u4e00\u58f0\uff0c\u738b\u9547\u5357\u521a\u521a\u7ad9\u8d77\uff0c\u4fbf\u7ed9 \u4e8e\u627f\u73e0\u6454\u5012\u5728\u5730\u4e0a\u3002\u4e8e\u627f\u73e0\u201c\u55d6\u201d\u7684\u4e00\u58f0\uff0c\u62d4\u51fa\u9752\u51a5\u5b9d\u5251\uff0c\u7ad9\u5728\u6c90\u56fd\u516c\u7684\u8eab\u8fb9\uff0c\u51b7\u7b11\u65a5\u9053\uff1a \u201c\u4f60\u4eec\u6562\u4e0d\u8ba9\u6c90\u56fd\u516c\u770b\u8fd9\u4fe1\u4e48\uff1f\u201d \u3000\u3000\u6d2a\u5ca9\u9053\u4eba\u4e0e\u9633\u5b97\u6d77\u7684\u6b66\u529f\u5747\u8db3\u4ee5\u5236\u6b62\u4e8e\u627f\u73e0\uff0c\u4f46\u88ab\u4e8e\u627f\u73e0\u5148\u7528\u8bf4\u8bdd\u8feb\u4f4f\uff0c\u7adf\u662f\u4e0d\u6562\u52a8 \u624b\uff01\u970e\u65f6\u95f4\uff0c\u6c14\u6c1b\u7d27\u5f20\u4e4b\u6781\uff0c\u7b75\u5e2d\u524d\u5251\u62d4\u5f29\u5f20\uff0c\u5927\u5bb6\u90fd\u5728\u5077\u5077\u5730\u77a7\u7740\u6c90\u56fd\u516c\u7684\u9762\u8272\u3002 \u3000\u3000\u6c90\u56fd\u516c\u628a\u4fe1\u770b\u5b8c\uff0c\u5fc3\u4e2d\u53c8\u60ca\u53c8\u6012\uff0c\u60ca\u8005\u662f\u7687\u5e1d\u7adf\u7136\u5bf9\u81ea\u5df1\u4e0d\u653e\u5fc3\uff0c\u539f\u6765\u8fd9\u4e2a\u738b\u526f\u5c06\u519b\u7adf \u662f\u7687\u5e1d\u6d3e\u6765\uff0c\u6697\u4e2d\u76d1\u89c6\u81ea\u5df1\u7684\uff01\u6012\u8005\u662f\u738b\u9547\u5357\u7adf\u60f3\u6697\u4e2d\u9677\u5bb3\uff0c\u60f3\u524a\u6389\u4ed6\u6c90\u5bb6\u5728\u4e91\u5357\u7684\u6743\u67c4\uff01 \u4f46\u4ed6\u7a76\u7adf\u662f\u8001\u4e8e\u5b98\u573a\uff0c\u9971\u7ecf\u4e16\u6545\u7684\u4eba\u7269\uff0c\u770b\u4e86\u4e4b\u540e\uff0c\u795e\u8272\u4e0d\u53d8\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u738b\u526f\u5c06\u519b\uff0c\u4f60 \u770b\u6b64\u4fe1\uff0c\u5c45\u7136\u6709\u4eba\u6562\u5192\u4f60\u7684\u7b14\u8ff9\uff0c\u4fe1\u4e2d\u6240\u8bf4\uff0c\u8352\u5510\u4e4b\u6781\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u738b\u9547\u5357\u3001\u9633\u5b97\u6d77\u7b49\u4e3a\u4e4b\u5927\u559c\uff0c\u77e5\u9053\u6c90\u56fd\u516c\u6709\u6240\u987e\u5fcc\uff0c\u4e0d\u6562\u7834\u9762\u51b3\u88c2\u3002\u738b\u9547\u5357 \u8fd9\u65f6\u65e9\u5df2\u722c\u4e86\u8d77\u6765\uff0c\u80f8\u812f\u4e00\u633a\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u8499\u516c\u7239\u63a8\u5fc3\u7f6e\u8179\uff0c\u4e0d\u4fe1\u6f9c\u8a00\uff0c\u5c0f\u5c06\u611f\u6069\u6234\u5fb7\u3002 \u8fd9\u4fe1\u4e0d\u5fc5\u770b\u4e86\uff0c\u6495\u6bc1\u4fbf\u662f\u3002\u53ea\u662f\u8fd9\u5c0f\u5996\u5973\u80c6\u6562\u5192\u5c0f\u5c06\u7684\u7b14\u8ff9\uff0c\u5174\u6ce2\u4f5c\u6d6a\uff0c\u80cc\u540e\u5fc5\u5b9a\u6709\u4eba\uff0c\u8fd8 \u8bf7\u516c\u7239\u8ffd\u7a76\uff01\u201d\u738b\u9547\u5357\u8bf4\u8fd9\u756a\u8bdd\u7684\u610f\u601d\uff0c\u8a00\u5916\u4e4b\u610f\uff0c\u4e5f\u662f\u4e3a\u6c90\u56fd\u516c\u63a9\u9970\uff0c\u5c06\u4e8e\u627f\u73e0\u9a82\u4f5c\u201c\u5996 \u5973\u201d\uff0c\u5927\u5bb6\u90fd\u4e0d\u6562\u6307\u660e\u5979\u662f\u4e8e\u8c26\u7684\u5973\u513f\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u6012\u6c14\u4e0a\u51b2\uff0c\u51b7\u7136\u50b2\u7b11\uff0c\u7d27\u63e1\u5251\u67c4\uff0c\u53ea\u542c\u5f97\u6c90\u56fd\u516c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u8981\u8ffd \u7a76\uff01\u201d\u9633\u5b97\u6d77\u7b49\u5019\u591a\u65f6\uff0c\u5c31\u662f\u8981\u6c90\u56fd\u516c\u8bf4\u51fa\u6b64\u8bdd\uff0c\u7acb\u523b\u4e00\u8dc3\u800c\u524d\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u5c0f\u5996\u5973\u5feb\u4ece \u5b9e\u62db\u6765\uff0c\u662f\u8c01\u4eba\u6307\u4f7f\u4f60\u7684\uff01\u201d\u6402\u5934\u4e00\u6293\uff0c\u7528\u64d2\u62ff\u624b\u7684\u7edd\u62db\uff0c\u7a81\u65bd\u731b\u88ad\uff0c\u4e8e\u627f\u73e0\u65e9\u5df2\u8c41\u51fa\u6027 \u547d\uff0c\u9633\u5b97\u6d77\u8eab\u5f62\u4e00\u52a8\uff0c\u5979\u7684\u5b9d\u5251\u5df2\u62a2\u5148\u51fa\u62db\uff0c\u53ea\u89c1\u5bd2\u5149\u75be\u95ea\uff0c\u7535\u5c04\u5954\u53bb\u3002\u4e09\u6735\u91d1\u82b1\u4ea6\u540c\u65f6\u51fa \u624b\uff01 \u3000\u3000\u5ffd\u89c1\u6d2a\u5ca9\u9053\u4eba\u8eab\u5f62\u9aa4\u8d77\uff0c\u62e6\u5728\u9633\u5b97\u6d77\u7684\u9762\u524d\uff0c\u5927\u8896\u4e00\u62c2\uff0c\u91d1\u5149\u4e00\u95ea\u5373\u706d\uff0c\u4e8e\u627f\u73e0\u6240\u53d1\u7684 \u4e09\u6735\u91d1\u82b1\uff0c\u5168\u90e8\u88ab\u4ed6\u5377\u5165\u8896\u4e2d\uff0c\u65e0\u58f0\u65e0\u606f\u3002\u6d2a\u5ca9\u9063\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff01\u201d\u968f\u624b\u6293\u8d77\u4e00\u53ea \u8c61\u7259\u7b77\u5b50\uff0c\u5c06\u4e8e\u627f\u73e0\u7684\u5b9d\u5251\u4e00\u62e8\uff0c\u53ea\u542c\u5f97\u201c\u4fa7\u201d\u7684\u4e00\u58f0\uff0c\u5b9d\u5251\u63d2\u5230\u64c5\u6728\u684c\u4e0a\uff0c\u6df1\u5165\u6570\u5bf8\uff0c\u4e8e \u627f\u73e0\u7d27\u63e1\u5251\u67c4\uff0c\u7528\u529b\u4e00\u62d4\uff0c\u6d2a\u5ca9\u9053\u4eba\u7684\u8c61\u7259\u7b77\u538b\u5728\u5979\u7684\u5251\u4e0a\uff0c\u4e5f\u4e0d\u89c1\u600e\u4e48\u7528\u529b\uff0c\u4e8e\u627f\u73e0\u7adf\u662f \u62d4\u4e0d\u51fa\u6765\uff01\u6d2a\u5ca9\u9053\u4eba\u6709\u610f\u5728\u6c90\u56fd\u516c\u9762\u524d\u663e\u9732\u60ca\u4eba\u7684\u6b66\u529f\uff0c\u6697\u7528\u5185\u5bb6\u771f\u529b\uff0c\u5c06\u4e8e\u627f\u73e0\u7684\u5b9d\u5251\u538b \u4f4f\uff0c\u5374\u5e76\u4e0d\u5373\u52a8\u624b\u4f24\u5979\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0f\u5996\u5973\uff0c\u53eb\u4f60\u5f00\u5f00\u773c\u754c\uff0c\u4f60\u670d\u4e86\u5417\uff0c\u5feb\u5feb\u8bf4\u51fa\uff0c\u4f60\u80cc \u540e\u7a76\u6709\u4f55\u4eba\uff1f\u201d \u3000\u3000\u5ffd\u542c\u5f97\u6c34\u69ad\u5916\u9762\u4e5f\u6709\u4eba\u7eb5\u58f0\u957f\u7b11\uff0c\u58f0\u5982\u9f99\u541f\u864e\u5578\uff0c\u9707\u5f97\u4eba\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u6d2a\u5ca9\u9053\u4eba\u5fc3\u4e2d \u4e00\u51db\uff0c\u53ea\u89c1\u4e00\u4e2a\u4e66\u751f\u5df2\u8d70\u4e86\u8fdb\u6765\uff0c\u6717\u58f0\u541f\u9053\uff1a\u201c\u5343\u9524\u4e07\u51fb\u51fa\u6df1\u5c71\uff0c\u70c8\u706b\u711a\u70e7\u82e5\u7b49\u95f2\uff0c\u711a\u9aa8\u788e \u8eab\u90fd\u4e0d\u6015\uff0c\u8981\u7559\u6e05\u767d\u5728\u4eba\u95f4\uff01\u201d\u8fd9\u662f\u4e8e\u8c26\u6700\u51fa\u540d\u7684\u4e00\u9996\u8bd7\uff0c\u4f20\u8bf5\u5168\u56fd\uff0c\u7ecf\u8fd9\u4e66\u751f\u4e00\u5531\uff0c\u66f4\u663e \u5f97\u58f0\u60c5\u6c89\u70c8\u6c14\u7eb5\u6a2a\uff01\u542c\u5230\u8033\u4e2d\uff0c\u4ee4\u4eba\u4f9d\u7136\u81ea\u60ed\uff0c\u51db\u7136\u751f\u60e7\uff01 \u3000\u3000\u6d2a\u5ca9\u9053\u4eba\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u90a3\u4e66\u751f\u7b11\u9053\uff1a\u201c\u6211\u5c31\u662f\u4f60\u6240\u8981\u8ffd\u7a76\u7684\u80cc\u540e\u4e4b\u4eba\uff01\u201d\u6d2a\u5ca9\u9053 \u4eba\u7684\u7b77\u5b50\u4e0d\u7531\u5f97\u4e00\u677e\uff0c\u4e8e\u627f\u73e0\u62d4\u5251\u800c\u8d77\uff0c\u6b22\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff01\u201d\u8fd9\u4e66\u751f\u7adf\u7136\u662f\u56db\u6d77\u95fb\u540d\uff0c\u88ab \u6b66\u6797\u516c\u8ba4\u4e3a\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\u7684\u5f20\u4e39\u67ab\uff01 \u3000\u3000\u8fd9\u4e00\u4e0b\u5f53\u771f\u662f\u53d8\u51fa\u610f\u5916\uff0c\u987f\u65f6\u95f4\u6c34\u69ad\u4e2d\u9759\u5f97\u8fde\u4e00\u6839\u9488\u8dcc\u5728\u5730\u4e0b\u90fd\u542c\u5f97\u89c1\u54cd\uff01\u6c90\u56fd\u516c\u9762\u8272 \u5927\u53d8\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u5f20\u5148\u751f\u5230\u6765\uff0c\u6709\u4f55\u6307\u6559\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u8981\u8d23\u9a82\u516c\u5b50\uff0c\u6211\u770b\u4ed6\u7ed9 \u4e8e\u8c26\u5efa\u5e99\u9020\u50cf\uff0c\u505a\u5f97\u5f88\u5bf9\u554a\uff0c\u90a3\u662f\u6211\u53eb\u4ed6\u505a\u7684\uff0c\u6240\u4ee5\u7279\u6765\u4e3a\u4ed6\u5411\u516c\u7239\u6c42\u60c5\uff0c\u516c\u7239\u82e5\u8981\u8d23\u5907\uff0c \u8d23\u5907\u6211\u597d\u5566\u3002\u201d \u3000\u3000\u6c90\u56fd\u516c\u5f3a\u7b11\u9053\uff1a\u201c\u5f20\u5148\u751f\u8bf4\u7b11\u4e86\uff01\u201d\u6025\u5fd9\u9762\u5411\u5218\u516c\u516c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5f20\u5148\u751f\u66fe\u4efb\u8fc7\u5c0f\u513f\u897f \u5e2d\uff0c\u867d\u7136\u4e3a\u65f6\u4e0d\u8fc7\u4e00\u6708\uff0c\u4f46\u4ed6\u7684\u535a\u5b66\u624d\u60c5\uff0c\u6211\u662f\u65e0\u9650\u94a6\u4f69\u7684\u3002\u5f20\u5148\u751f\u540d\u58eb\u98ce\u6d41\uff0c\u559c\u6b22\u8bf4\u7b11\uff0c \u8fd8\u671b\u5218\u516c\u516c\u5305\u6db5\u3002\u201d\u4e8e\u627f\u73e0\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u602a\u4e0d\u5f97\u6c90\u5c0f\u59d0\u7684\u95fa\u4e2d\u6302\u6709\u81ea\u5df1\u5e08\u7236\u7684\u624b\u4e66\uff0c\u539f\u6765 \u5e08\u7236\u7adf\u7136\u505a\u8d77\u6c90\u516c\u5b50\u7684\u5148\u751f\uff0c\u60f3\u8d77\u5e08\u7236\u505a\u4e8b\u7684\u51fa\u4eba\u610f\u8868\uff0c\u5fc3\u4e2d\u6697\u6697\u597d\u7b11\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5728\u8def\u8fc7\u6606\u660e\u4e4b\u65f6\uff0c\u5076\u7136\u89c1\u5230\u6c90\u78f7\uff0c\u89c9\u5f97\u4ed6\u662f\u4e00\u4e2a\u53ef\u9020\u4e4b\u624d\uff0c\u8c08\u8bdd\u6295\u673a\uff0c\u4fbf\u6536\u4e86\u4ed6 \u505a\u8bb0\u540d\u5f1f\u5b50\u3002\u5f20\u4e39\u67ab\u5176\u65f6\u5df2\u77e5\u9053\u5927\u7406\u767d\u65cf\u4e0e\u671d\u5ef7\u4e4b\u95f4\u7684\u7ea0\u7eb7\uff0c\u56e0\u6b64\u4ed6\u6536\u6c90\u78f7\u4e3a\u8bb0\u540d\u5f1f\u5b50\uff0c\u5176 \u4e2d\u8fd8\u53e6\u6709\u4e00\u756a\u6df1\u610f\uff0c\u6c90\u56fd\u516c\u54ea\u77e5\u9053\u4ed6\u662f\u5929\u4e0b\u95fb\u540d\u7684\u5f20\u4e39\u67ab\u5927\u4fa0\uff0c\u4f46\u89c9\u4ed6\u535a\u96c5\u878d\u901a\uff0c\u786e\u5b9e\u5bf9\u4ed6 \u94a6\u4f69\u3002\u5f20\u4e39\u67ab\u5728\u516c\u5e9c\u4e2d\u53ea\u7559\u4e86\u4e00\u4e2a\u6708\uff0c\u4fbf\u5306\u5306\u8d70\u4e86\u3002\u5f53\u65f6\u6c90\u56fd\u516c\u8fd8\u975e\u5e38\u60cb\u60dc\u5462\u3002 \u3000\u3000\u800c\u4eca\u6c90\u56fd\u516c\u89c1\u4e86\u9633\u5b97\u6d77\u7ed9\u4ed6\u770b\u7684\u753b\u50cf\uff0c\u8fd9\u624d\u77e5\u9053\u662f\u5f20\u4e39\u67ab\uff0c\u8fd9\u4e00\u60ca\u7aef\u7684\u975e\u540c\u5c0f\u53ef\uff01\u970e\u65f6 \u95f4\u8f6c\u4e86\u597d\u51e0\u904d\u5ff5\u5934\uff0c\u521d\u65f6\u60f3\u88c5\u4f5c\u4e0d\u8ba4\u8bc6\u5f20\u4e39\u67ab\uff0c\u4f46\u53c8\u6015\u5f20\u4e39\u67ab\u88ab\u9633\u5b97\u6d77\u6240\u64d2\uff0c\u4f9b\u51fa\u548c\u4ed6\u7684\u513f \u5b50\u7684\u5173\u7cfb\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u53ea\u597d\u66ff\u5f20\u4e39\u67ab\u63a9\u9970\u3002\u4f46\u671b\u5f20\u4e39\u67ab\u4e0d\u8981\u81ea\u5df1\u8bf4\u51fa\u540d\u5b57\u3002\u9633\u5b97\u6d77\u8fd9\u4e9b\u4eba\u8981 \u7ed9\u81ea\u5df1\u9762\u5b50\uff0c\u6599\u4ed6\u4eec\u4e0d\u6562\u516c\u7136\u53eb\u7834\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u5f2f\u6307\u4e00\u5f39\uff0c\u4fa7\u76ee\u8155\u89c6\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5218\u516c\u516c\uff0c\u522b\u6765\u65e0\u6059\u554a\u3002\u6606\u660e\u56db\u5b63\u5982\u6625\uff0c\u5728\u6b64 \u8d4f\u82b1\u996e\u9152\uff0c\u6bd4\u8d77\u80e1\u7586\u96ea\u5730\uff0c\u90a3\u771f\u662f\u5929\u6e0a\u4e4b\u522b\u4e86\u3002\u201d\u539f\u6765\u8fd9\u4e2a\u59d3\u5218\u7684\u592a\u76d1\uff0c\u5c31\u662f\u5728\u571f\u6728\u5821\u4e4b\u5f79 \u65f6\uff0c\u4e0e\u7687\u5e1d\u7948\u9547\u540c\u65f6\u88ab\u4e5f\u5148\u4fd8\u864f\u8fc7\u53bb\u7684\uff0c\u56e0\u4ed6\u66fe\u4e0e\u7687\u5e1d\u540c\u53d7\u707e\u96be\uff0c\u6545\u6b64\u5982\u4eca\u624d\u88ab\u91cd\u7528\u3002\u90a3\u5218 \u516c\u516c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u5f20\u5148\u751f\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u7687\u4e0a\u5584\u5fd8\uff0c\u60f3\u4e0d\u5230\u5218\u516c\u516c\u4e5f\u4e00\u6837 \u5584\u5fd8\uff0c\u5218\u516c\u516c\u56de\u5230\u4eac\u4e2d\uff0c\u8bf7\u95ee\u95ee\u7687\u4e0a\uff0c\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\u6211\u5728\u74e6\u523a\u548c\u4ed6\u8bf4\u8fc7\u7684\u8bdd\uff0c\u90a3\u4ef6\u72d0\u76ae\u88d8\u5b50\u3002 \u60f3\u6765\u7687\u4e0a\u4e5f\u65e9\u5df2\u629b\u6389\u4e86\u3002\u201d\u5f53\u5e74\u7948\u9547\u88ab\u56da\uff0c\u5f20\u4e39\u67ab\u53bb\u63a2\u671b\u4ed6\uff0c\u66fe\u9001\u4e00\u4ef6\u767d\u72d0\u5916\u5957\u7ed9\u4ed6\u5fa1\u5bd2\uff0c \u8fd9\u4e2a\u5218\u592a\u76d1\u6b63\u662f\u5f53\u573a\u76ee\u51fb\u4e4b\u4eba\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u505a\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u6c90\u56fd\u516c\u9053\uff1a\u201c\u5f20\u5148\u751f\u559d\u9189\u5566\uff01\u201d\u5f20\u4e39\u67ab\u7aef\u8d77\u5927\u676f\uff0c\u4e00\u996e\u800c\u5c3d\uff0c\u4ef0\u5929\u5927\u7b11\u9053\uff1a\u201c\u79bb\u733f\u5c48\u5b50 \u5e7d\u5170\u6028\uff0c\u5c82\u662f\uff1a\u4e3e\u4e16\u6c89\u8ff7\u6211\u72ec\u9192\uff1f\u54c8\u54c8\uff0c\u53ea\u6015\u9189\u7684\u4e0d\u662f\u6211\uff0c\u800c\u662f\u5f53\u4eca\u7687\u4e0a\uff0c\u548c\u4f60\u4eec\u8fd9\u4e00\u73ed \u4eba\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u4e3e\u5ea7\u5931\u8272\uff01\u5f20\u4e39\u67ab\u6beb\u4e0d\u7406\u4f1a\uff0c\u4f83\u4f83\u8bf4\u9053\uff1a\u201c\u53ea\u6015\u7687\u4e0a\u6263\u5218\u516c\u516c\u90fd\u5fd8\u8bb0\u4e86\uff01 \u65e7\u4e8b\u672c\u6765\u4e0d\u8be5\u91cd\u63d0\uff0c\u4f46\u8fd9\u4ef6\u65e7\u4e8b\uff0c\u63d0\u4e00\u63d0\u5374\u6709\u6781\u5927\u597d\u5904\uff01\u60f3\u5f53\u5e74\u4e8e\u9601\u8001\u6d3e\u4e91\u72b6\u5143\u548c\u6211\u606d\u8fce\u7687 \u4e0a\u56de\u56fd\uff0c\u7687\u4e0a\u66fe\u4fe1\u8a93\u65e6\u65e6\uff0c\u8bf4\u662f\u82e5\u80fd\u91cd\u767b\u5927\u5b9d\uff0c\u5fc5\u5f53\u505a\u4e2a\u5c27\u821c\u4e4b\u541b\u3002\u60f3\u4e0d\u5230\u7687\u4e0a\u590d\u4f4d\uff0c\u4e0d\u5230 \u5341\u5929\uff0c\u5c31\u628a\u4e8e\u9601\u8001\u6740\u4e86\uff0c\u8fd9\u6837\u7684\u81ea\u6bc1\u957f\u57ce\uff0c\u5c82\u80fd\u4fdd\u6ca1\u6709\u7b2c\u4e8c\u6b21\u571f\u6728\u5821\u4e4b\u5f79\uff01\u5c82\u4e0d\u4ee4\u5929\u4e0b\u7684\u5fe0 \u81e3\u4e49\u58eb\u5bd2\u5fc3\uff01\u54c8\u54c8\uff0c\u6c90\u56fd\u516c\uff0c\u6211\u53ef\u4e0d\u662f\u8bf4\u7b11\uff01\u5c0f\u516c\u7239\u66ff\u4e8e\u9601\u8001\u5efa\u5e99\u9020\u50cf\u4e4b\u4e8b\uff0c\u867d\u7136\u4e0d\u662f\u6211\u4ee3 \u4ed6\u7b79\u5212\uff0c\u4f46\u4ed6\u786e\u662f\u542c\u6211\u8bf4\u8fc7\u4e8e\u9601\u8001\u7684\u5fe0\u70c8\u4e8b\u8ff9\uff0c\u624d\u8d77\u4e86\u5fc3\u610f\u7684\u3002\u8bf7\u4f60\u4eec\u626a\u5fc3\u81ea\u95ee\uff0c\u50cf\u4e8e\u9601\u8001 \u8fd9\u6837\u7684\u5fe0\u5fc3\u8d64\u80c6\uff0c\u91cd\u9020\u4e7e\u5764\u671d\u5927\u5fd7\u81e3\uff0c\u6b7b\u540e\u96be\u9053\u4e0d\u914d\u4e3a\u795e\uff1f\u4f60\u4eec\u82e5\u6562\u6bc1\u4ed6\u7684\u5e99\uff0c\u711a\u4ed6\u7684\u50cf\uff0c \u53ea\u6015\u5929\u5730\u4e0d\u5bb9\uff0c\u4eba\u795e\u5171\u6124\uff01 \u3000\u3000\u8fd9\u756a\u8bdd\u4e49\u6b63\u8f9e\u4e25\uff0c\u6c90\u742e\u7981\u4e0d\u4f4f\u624b\u98a4\u811a\u9707\uff0c\u60ca\u60f6\u4e4b\u6781\uff0c\u52a0\u53c8\u5174\u594b\u4e4b\u6781\uff01\u8d3e\u77e5\u7687\u5e1d\u51a4\u6740\u4e8e\u8c26 \u4e4b\u4e8b\uff0c\u7a0d\u5fae\u6b63\u6d3e\u7684\u5927\u81e3\uff0c\u90fd\u662f\u5fc3\u5fc3\u4e0d\u6124\uff0c\u53ea\u662f\u8fd9\u80a1\u51a4\u90c1\u4e4b\u6c14\uff0c\u5728\u4e13\u5236\u7687\u6743\u4e4b\u4e0b\uff0c\u5374\u4e0d\u6562\u6709\u534a \u70b9\u53d1\u51fa\u6765\u3002\u800c\u4eca\u7ecf\u5f20\u4e39\u67ab\u75db\u5feb\u6dcb\u6f13\u5730\u4e00\u8bf4\uff0c\u8bf4\u5230\u4e86\u6c90\u742e\u7684\u5fc3\u91cc\uff0c\u65e0\u5f02\u66ff\u4ed6\u5410\u51fa\u4e86\u4e00\u53e3\u90c1\u6c14\uff0c \u4ed6\u4e0d\u77e5\u662f\u88ab\u5f20\u4e39\u67ab\u5413\u4f4f\u8fd8\u662f\u6709\u610f\u8ba9\u4ed6\u5c3d\u60c5\u503e\u5410\uff0c\u7adf\u7136\u6ca1\u6709\u5236\u6b62\u4ed6\u7684\u53d1\u8a00\u3002 \u3000\u3000\u597d\u534a\u664c\u5218\u516c\u516c\u624d\u5b9a\u4e86\u5fc3\u795e\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u5996\u8a00\u60d1\u4f17\uff01\u201d\u6c90\u56fd\u516c\u5fd9\u53eb\u9053\uff1a\u201c\u5feb\u6276\u5f20\u5148\u751f\u51fa \u53bb\uff0c\u7ed9\u4ed6\u8bf7\u533b\u751f\u770b\uff01\u201d\u5f20\u4e39\u67ab\u51b7\u7b11\u9053\uff1a\u201c\u5996\u8a00\u60d1\u4f17\uff0c\u54fc\uff0c\u4eca\u65e5\u4f60\u4eec\u82e5\u4e0d\u5bb9\u6211\u628a\u8bdd\u8bf4\u6e05\uff0c\u8c01\u6562 \u78b0\u6211\u4e00\u4e0b\uff0c\u5c31\u4f11\u602a\u6211\u4e0d\u7559\u60c5\u9762\uff01\u201d\u6d2a\u5ca9\u9053\u4eba\u55d4\u76ee\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u600e\u6562\u5982\u6b64\u653e\u8086\uff01\u201d \u5f20\u4e39\u67ab\u5927\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u7687\u4e0a\u4e5f\u4e0d\u6562\u5982\u6b64\u95ee\u6211\uff0c\u4f60\u80c6\u6562\u653e\u8086\uff01\u6211\u5f20\u4e39\u67ab\u5750\u4e0d\u6539\u540d\uff0c \u884c\u4e0d\u6539\u59d3\uff0c\u4f60\u5f85\u600e\u5730\uff1f\u201d\u6c90\u56fd\u516c\u4e00\u542c\u4ed6\u81ea\u62a5\u59d3\u540d\uff0c\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u5fc3\u4e2d\u6697\u53eb\u201c\u7cdf\u4e86\u3002\u7cdf \u4e86\uff01\u201d\u4e00\u65f6\u95f4\u6ca1\u4e86\u4e3b\u610f\uff0c\u5ffd\u542c\u5f97\u9633\u5b97\u6d77\u54c8\u54c8\u5927\u7b11\u8d77\u6765\uff01 \u3000\u3000\u6c90\u56fd\u516c\u4e00\u6014\uff0c\u9053\uff1a\u201c\u9633\u603b\u7ba1\u4f55\u4e8b\u597d\u7b11\uff1f\u201d\u9633\u5b97\u6d77\u9053\uff1a\u201c\u5929\u65f6\u4e0d\u6b63\uff0c\u8fd9\u4f4d\u5f20\u5148\u751f\u5927\u7ea6\u662f\u60a3 \u4e86\u5931\u5fc3\u75af\u4e86\u3002\u60f3\u90a3\u5f20\u4e39\u67ab\u4e0e\u5c0f\u5f1f\u5e76\u79f0\u5929\u4e0b\u56db\u5927\u5251\u5ba2\uff0c\u6b66\u529f\u4f55\u7b49\u4e86\u5f97\uff1f\u8fd9\u4f4d\u5f20\u5148\u751f\u5206\u660e\u662f\u4e00\u4f4d \u6587\u5f31\u4e66\u751f\uff0c\u54c8\u54c8\uff0c\u4ed6\u7adf\u6562\u5192\u5f20\u4e39\u67ab\u7684\u540d\u5934\uff0c\u6b64\u4e8b\u5c82\u4e0d\u5927\u4e3a\u53ef\u7b11\uff01\u201d\u9633\u5b97\u6d77\u660e\u660e\u77e5\u9053\u662f\u5f20\u4e39 \u67ab\uff0c\u4f46\u5374\u53e3\u53e3\u58f0\u58f0\u8bf4\u4ed6\u5047\u5192\uff0c\u76ee\u7684\u5c31\u662f\u66ff\u6c90\u56fd\u516c\u63a9\u9970\u3002\u6b63\u4e0e\u521a\u624d\u6307\u65a5\u4e8e\u627f\u73e0\u5192\u540d\u7684\u7528\u610f\u76f8 \u540c\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u53cc\u670d\u4e00\u7ffb\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u5c31\u662f\u9633\u5b97\u6d77\u5417\uff1f\u201d\u6c90\u56fd\u516c\u5fd9\u9053\uff1a\u201c\u8fd9\u4f4d\u6b63\u662f\u5927\u5185\u603b\u7ba1 \u9633\u5927\u4eba\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u4ec0\u4e48\u603b\u7ba1\u4e0d\u603b\u7ba1\uff1f\u9633\u5b97\u6d77\uff0c\u6211\u6765\u95ee\u4f60\uff0c\u662f\u8c01\u5c01\u4f60\u505a\u5251\u5ba2 \u7684\uff1f\u201d\u9633\u5b97\u6d77\u9053\uff1a\u201c\u55ef\uff0c\u90a3\u662f\u6c5f\u6e56\u670b\u53cb\u5728\u5c0f\u5f1f\u9762\u4e0a\u8d34\u91d1\u3002\u5f20\u5148\u751f\uff0c\u8bdd\u8bf4\u53ea\u8be5\u5f20\u4e39\u67ab\u624d\u80fd\u95ee \u6211\u3002\u201d\u5f20\u4e39\u67ab\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u8981\u95ee\u4f60\uff0c\u4f60\u6709\u4ec0\u4e48\u672c\u9886\uff0c\u51ed\u4f60\u4e5f\u914d\u4e0e\u6211\u5e76\u79f0\u56db\u5927\u5251 \u5ba2\uff1f\u54c8\uff0c\u54c8\uff01\u6211\u770b\u4f60\u624d\u662f\u5047\u5192\u5251\u5ba2\u4e4b\u540d\uff01\u201d\u9633\u5b97\u6d77\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u5192\u8ba4\u662f\u5f20\u4e39\u67ab\uff1f\u597d\uff0c\u4f60\u65e2\u7136 \u81ea\u4ece\u662f\u5f20\u4e39\u67ab\uff0c\u603b\u5f97\u9732\u51fa\u4e00\u4e24\u624b\u5251\u672f\u3002\u201d\u6d2a\u5b95\u9053\u4eba\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u82e5\u8d62\u5f97\u6211\u624b\u4e2d\u7684\u957f \u5251\uff0c\u6211\u5c31\u8ba4\u4f60\u662f\u5f20\u4e39\u67ab\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u522b\u5fd9\uff0c\u522b\u5fd9\uff0c\u6211\u5f97\u5148\u6559\u8bad\u6559\u8bad\u8fd9\u5192\u540d\u5251\u5ba2\u7684\u65e0\u803b\u4e4b\u5f92\uff01\u9633\u5b97\u6d77\uff0c\u4f60\u82e5\u80fd \u5728\u6211\u624b\u5185\u63a5\u4e0a\u5341\u62db\uff0c\u6211\u5c31\u7531\u5f97\u4f60\u540d\u5217\u56db\u5927\u5251\u5ba2\u3002\u201d\u9633\u5b97\u6d77\u6043\u7740\u6709\u5e08\u53d4\u5728\u5ea7\uff0c\u6545\u6b64\u6562\u516c\u7136\u53eb \u9635\uff0c\u4ed6\u672c\u610f\u662f\u4e00\u4e0a\u573a\u5c31\u8bf7\u5e08\u53d4\u51fa\u624b\uff0c\u4e0d\u6599\u5374\u7ed9\u5f20\u4e39\u67ab\u7528\u8bf4\u8bdd\u6324\u5f97\u4e0b\u4e0d\u4e86\u53f0\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u6050 \u60e7\u3002\u4f46\u968f\u5373\u60f3\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u7eb5\u7136\u5389\u5bb3\uff0c\u6211\u5c82\u4e0d\u80fd\u63a5\u4ed6\u5341\u62db\uff1f\u201d\u786c\u7740\u5934\u76ae\u7b54\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u8bf7 \u5f20\u5148\u751f\u4eae\u5251\uff01\u5f20\u5148\u751f\u662f\u56fd\u516c\u7684\u897f\u5e2d\uff0c\u5144\u5f1f\u53c8\u7d2f\u6765\u656c\u91cd\u8bfb\u4e66\u7684\u4eba\uff0c\u5f20\u5148\u751f\u65e2\u7136\u6709\u6b64\u96c5\u5174\uff0c\u5c0f\u5f1f \u7406\u5f53\u5949\u966a\uff0c\u54b1\u4eec\u5f7c\u6b64\u70b9\u5230\u4e3a\u6b62\uff0c\u514d\u5f97\u53eb\u516c\u7239\u4e0d\u5b89\u5fc3\u3002\u201d\u6b64\u8bdd\u542c\u6765\uff0c\u4f3c\u4e4e\u662f\u9633\u5b97\u6d77\u6697\u793a\u6709\u610f\u8ba9 \u4ed6\uff0c\u4ecd\u7136\u628a\u4ed6\u5f53\u4f5c\u6559\u4e66\u5148\u751f\u770b\u5f85\uff0c\u5176\u5b9e\u5374\u662f\u5411\u5f20\u4e39\u67ab\u5957\u4ea4\u60c5\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u559d\u9053\uff1a\u201c\u5e9f\u8bdd\u591a\u8bf4\u4ec0\u4e48\uff1f\u4eae\u5251\uff01\u201d\u9633\u5b97\u6d77\u62d4\u5251\u8df3\u51fa\u573a\u5fc3\uff0c\u4e8e\u627f\u73e0\u62d4\u51fa\u9752\u51a5\u5b9d\u5251 \u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u7684\u5251\u3002\u201d\u5f20\u4e39\u67ab\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5bf9\u4ed8\u8fd9\u9897\uff0c\u4f55\u987b\u7528\u5251\uff1f\u201d\u5cb8\u4e0a\u5782\u67f3\uff0c\u8986\u76d6\u8377 \u5858\uff0c\u6709\u51e0\u679d\u76f4\u4f38\u5230\u6c34\u6a84\u5916\u8fb9\uff0c\u5f20\u4e39\u67ab\u968f\u624b\u6298\u4e0b\u4e00\u679d\u67f3\u679d\uff0c\u7f13\u7f13\u8d70\u51fa\uff0c\u9053\uff1a\u201c\u9633\u5927\u603b\u7ba1\uff0c\u8fd9\u662f \u4f60\u6210\u540d\u7684\u597d\u673a\u4f1a\u4e86\u3002\u4f60\u53ea\u8981\u5728\u6211\u7684\u67f3\u679d\u4e4b\u4e0b\uff0c\u80fd\u63a5\u5341\u62db\uff0c\u4f60\u8fd9\u56db\u5927\u5251\u5ba2\u4e4b\u4e00\u7684\u5ea7\u4f4d\uff0c\u5c31\u7b97\u7a33 \u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\u5408\u5ea7\u7686\u60ca\uff0c\u5c24\u5176\u662f\u56fd\u516c\u5e9c\u4e2d\u7684\u90a3\u51e0\u4e2a\u6b66\u58eb\u90fd\u7741\u5927\u4e86\u773c\u775b\uff0c\u89c9\u5f97\u5f20\u4e39\u67ab\u672a\u514d\u592a\u8fc7\u72c2 \u5984\u3002\u6c90\u56fd\u516c\u89c1\u9633\u5b97\u6d77\u6ee1\u9762\u6740\u6c14\uff0c\u624b\u4e2d\u957f\u5251\u6296\u52a8\uff0c\u55e1\u55e1\u4f5c\u58f0\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u8fd9\u5c82\u4e0d\u662f\u81ea \u5df1\u9001\u6b7b\u4e48\uff1f\u201d\u5fc3\u4e2d\u7231\u60dc\u5f20\u4e39\u67ab\u7684\u624d\u5b66\uff0c\u5927\u662f\u4e0d\u5fcd\u3002\u4f46\u968f\u5373\u60f3\u5230\uff0c\u9633\u5b97\u6d77\u4e0d\u80af\u53eb\u7834\uff0c\u90a3\u5df2\u7ecf\u662f \u7ed9\u4e86\u81ea\u5df1\u9762\u5b50\uff0c\u5f20\u4e39\u67ab\u4e0d\u6b7b\u56fa\u597d\uff0c\u6b7b\u4e86\u5bf9\u81ea\u5df1\u4e5f\u6ca1\u6709\u4ec0\u4e48\uff0c\u4e00\u573a\u4e0e\u53db\u9006\u6709\u5173\u8fde\u7684\u4e8b\u60c5\uff0c\u5012\u53ef \u4ee5\u5b8c\u5168\u906e\u76d6\u3002\u56e0\u6b64\u6c90\u56fd\u516c\u8e0c\u8e87\u518d\u4e09\uff0c\u7ec8\u4e8e\u6ca1\u6709\u51fa\u58f0\u62e6\u963b\u3002 \u3000\u3000\u8fd9\u65f6\u5f20\u4e39\u67ab\u5df2\u4e0e\u9633\u5b97\u6d77\u9762\u9762\u76f8\u5bf9\uff0c\u5f20\u4e39\u67ab\u8f7b\u4e3e\u67f3\u679d\uff0c\u62c2\u4e00\u62c2\u8eab\u4e0a\u7684\u98ce\u5c18\uff0c\u7b11\u9053\uff1a\u201c\u627f \u73e0\uff0c\u4f60\u7ed9\u6211\u6570\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u9633\u5b97\u6d77\u81f3\u4e0d\u6d4e\u4e5f\u662f\u4e2a\u5927\u5185\u603b\u7ba1\uff0c\u56db\u5927\u5251\u5ba2\u7684\u79f0\u53f7\uff0c\u4e5f\u4eab\u4e86\u5341\u591a\u5e74\uff0c\u5982\u4eca\u7adf\u53d7\u5f20\u4e39\u67ab\u8fd9\u6837 \u5730\u8511\u89c6\uff0c\u8fd9\u4e00\u6c14\u975e\u540c\u5c0f\u53ef\uff0c\u5bf9\u5f20\u4e39\u67ab\u7684\u754f\u60e7\u987f\u65f6\u5316\u4e3a\u6012\u706b\uff01\u5373\u7b97\u5f20\u4e39\u67ab\u624b\u4e2d\u4f7f\u7684\u662f\u9752\u51a5\u5b9d \u5251\uff0c\u4ed6\u4e5f\u8981\u8c41\u51fa\u6027\u547d\u4e00\u62fc\uff0c\u4f55\u51b5\u5f20\u4e39\u67ab\u624b\u4e2d\u63e1\u7684\u53ea\u662f\u4e00\u6839\u4e00\u6298\u5373\u65ad\u7684\u67f3\u679d\uff01 \u3000\u3000\u53ea\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u9633\u5b97\u6d77\u4e00\u62db\u201c\u6392\u4e91\u9a76\u7535\u201d\uff0c\u9707\u5f97\u55e1\u55e1\u4f5c\u54cd\uff0c\u8fd9\u4e00\u5251\u4ed6\u4f7f\u5c3d\u5185\u5bb6\u771f\u529b\uff0c\u7aef \u7684\u662f\u52bf\u631f\u98ce\u96f7\uff0c\u8fc5\u731b\u65e0\u4f26\u3002\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u865a\u6709\u5176\u8868\uff0c\u5931\u4e4b\u51dd\u7ec3\u3002\u201d\u811a\u6b65\u4e0d\u79fb\uff0c\u9633\u5b97\u6d77\u90a3\u4e00 \u5251\u5374\u63b7\u4e86\u4e2a\u7a7a\uff0c\u5f20\u4e39\u67ab\u67f3\u679d\u4e00\u4e3e\uff0c\u53ea\u542c\u5f97\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u6839\u67d4\u679d\u7adf\u7136\u6296\u5f97\u7b14\u76f4\uff0c\u5c45\u7136\u5e26\u7740 \u5b9d\u5251\u51fa\u9798\u7684\u5578\u58f0\uff0c\u67f3\u679d\u4e00\u6643\uff0c\u5df2\u70b9\u5230\u9633\u5b97\u6d77\u7684\u9762\u95e8\u3002\u9633\u5b97\u6d77\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u624d\u77e5\u9053\u5f20\u4e39\u67ab\u7684\u786e \u540d\u4e0d\u865a\u4f20\uff0c\u5185\u529f\u7684\u7cbe\u7eaf\uff0c\u786e\u662f\u5230\u4e86\u901a\u7384\u4e4b\u5883\u3002\u9038\u67f3\u679d\u4e00\u523a\uff0c\u52b2\u9000\u4e0d\u4e9a\u5229\u5251\uff0c\u82e5\u7ed9\u4ed6\u5241\u4e2d\uff0c\u9762 \u76ae\u52bf\u5fc5\u622e\u7a7f\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u76c8\u76c8\u7b11\u9053\uff1a\u201c\u7b2c\u4e00\u62db\uff01\u201d\u9633\u5b97\u6d77\u4e00\u62db\u201c\u6a2a\u6d41\u5728\u63d6\u201d\uff0c\u957f\u5251\u4e00\u67b6\uff0c\u4ee5\u653b\u4e3a\u5b88\uff0c\u597d\u4e0d \u5bb9\u6613\u624d\u5c06\u5f20\u4e39\u67ab\u7684\u653b\u52bf\u5316\u5f00\uff0c\u5f20\u4e39\u67ab\u67f3\u679d\u4e00\u62c2\uff0c\u4f3c\u5de6\u4f3c\u53f3\uff0c\u865a\u5b9e\u4e0d\u5b9a\uff0c\u6765\u52bf\u53d8\u5e7b\u65e0\u65b9\uff0c\u9633\u5b97 \u6d77\u8fde\u7528\u51e0\u79cd\u8eab\u6cd5\uff0c\u521a\u521a\u6446\u8131\uff0c\u5f20\u4e39\u67ab\u7b2c\u4e09\u62db\u53c8\u5230\uff0c\u9633\u5b97\u6d77\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u4f46\u4ed6\u5230\u5e95\u662f\u4e00\u6d41\u9ad8 \u624b\uff0c\u4e34\u5371\u4e0d\u4e71\uff0c\u767e\u5fd9\u4e2d\u4f7f\u51fa\u5e05\u95e8\u7edd\u6280\u7684\u6551\u547d\u795e\u62db\uff0c\u53cd\u624b\u4e00\u524a\uff0c\u957f\u5251\u4e00\u4e2a\u76d8\u65cb\uff0c\u5b88\u4e2d\u6709\u653b\uff0c\u5c45 \u7136\u628a\u5f20\u4e39\u67ab\u8fde\u6388\u4e24\u62db\u7684\u653b\u52bf\u4e00\u9f50\u6d88\u89e3\uff0c\u800c\u4e14\u8fd8\u523a\u4e86\u4e00\u5251\uff0c\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u67f3\u679d\u4fa7\u5730\u5728\u4ed6\u5251 \u80cc\u4e00\u51fb\uff0c\u9633\u5b97\u6d77\u9707\u5f97\u864e\u53e3\u9ebb\u75db\uff0c\u957f\u5251\u8361\u5f00\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u98ce\u7b11\u9053\uff1a\u201c\u8fd9\u4e24\u4e0b\u5b50\u7684\u5251\u6cd5\u5c1a\u53ef\u4e00 \u89c2\uff0c\u4f46\u5c01\u95ed\u867d\u4e25\uff0c\u7834\u7efd\u8fd8\u662f\u6709\u7684\uff0c\u8fd9\u8fd8\u7b97\u4e0d\u5f97\u4e0a\u4e58\u7684\u5251\u6cd5\uff0c\u4f60\u518d\u770b\u6211\u8fd9\u8fde\u63a5\u7684\u4e09\u62db\uff01\u201d\u8fd9\u65f6 \u4e8e\u627f\u73e0\u5df2\u6570\u5230\u7b2c\u4e94\u62db\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u63a5\u8fde\u4e09\u62db\uff0c\u7b2c\u4e00\u62db\u5206\u82b1\u62c2\u67f3\uff0c\u8fde\u523a\u4f60\u5de6\u53f3\u4e24\u80a9\u4e95\u7a74\uff0c\u7b2c\u4e8c\u62db \u51af\u5937\u51fb\u9f13\uff0c\u622e\u4f60\u7684\u54bd\u5589\u8981\u5bb3\uff0c\u7b2c\u4e09\u62db\u767d\u8679\u8d2f\u65e5\uff0c\u76f4\u523a\u4f60\u7684\u80f8\u819b\uff01\u201d\u5f20\u4e39\u67ab\u8fb9\u8bf4\u8fb9\u505a\uff0c\u76f4\u4f3c\u8001 \u5e08\u6559\u5b66\u751f\u4e00\u6837\u3002\u9633\u5b97\u6d77\u5e78\u5f97\u6709\u4ed6\u7684\u6307\u70b9\u3002\u4f7f\u5c3d\u5e73\u751f\u6240\u5b66\uff0c\u7b2c\u4e00\u62db\u201c\u7528\u865a\u5f0f\u5206\u91d1\u201d\u7684\u9634\u67d4\u5251\u672f \u5378\u5f00\u5f20\u4e39\u67ab\u7684\u653b\u52bf\uff0c\u7b2c\u4e8c\u62db\u7528\u201c\u94c1\u95e8\u95e9\u201d\u62e6\u6321\u80f8\u524d\uff0c\u7b2c\u4e09\u62db\u60f3\u5c3d\u65b9\u6cd5\u5374\u65e0\u53ef\u62b5\u6321\uff0c\u53ea\u597d\u7528\u4e00 \u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\uff0c\u4ee5\u6700\u521a\u731b\u7684\u5251\u52bf\u53cd\u51fb\uff0c\u5e0c\u671b\u51ed\u7740\u624b\u4e2d\u5229\u5251\u524a\u65ad\u4ed6\u7684\u67f3\u679d\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211 \u4ee5\u8fd9\u6837\u51f6\u731b\u7684\u53cd\u51fb\u4e4b\u52bf\uff0c\u62fc\u7740\u4e0e\u4f60\u4e24\u8d25\u4ff1\u4f24\uff0c\u6599\u4f60\u4e5f\u4e0d\u6562\u653e\u8086\u62a2\u653b\u3002\u201d\u4f9d\u5251\u5b66\u7684\u9053\u7406\uff0c\u4ed6\u8fd9 \u4e09\u62db\u8fd8\u771f\u7b97\u5f97\u662f\u89e3\u62c6\u5f97\u5b9c\uff0c\u4e2d\u89c4\u4e2d\u77e9\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u4e00\u53e3\u6c14\u6570\u9053\uff1a\u201c\u7b2c\u516d\u62db\uff0c\u7b2c\u4e03\u62db\uff0c\u7b2c\u516b\u62db\uff01\u201d\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5440\uff0c\u53ef\u60dc\uff0c\u53ef\u60dc\u6211\u5e08 \u7236\u82e5\u4e0d\u5c06\u62db\u6570\u8bf4\u7834\uff0c\u8fd9\u4e09\u62db\u4ed6\u7109\u80fd\u62b5\u6321\u4e86\uff1f\u73b0\u5728\u53ea\u6709\u4e24\u62db\u4e86\uff0c\u9633\u5b97\u6d77\u62fc\u4e86\u6027\u547d\uff0c\u5168\u529b\u53cd\u51fb\uff0c \u5341\u62db\u4e4b\u5185\uff0c\u53ea\u6015\u672a\u5fc5\u80fd\u5c06\u4ed6\u6253\u8d25\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u4e2a\u4eba\u5f71\u4ece\u7a97\u53e3\u98de \u51fa\uff0c\u90a3\u6c34\u69ad\u56db\u9762\u4e34\u6c34\uff0c\u7a97\u4e0a\u90fd\u9576\u7740\u73bb\u7483\uff0c\u8fd9\u4e00\u4e0b\u76f4\u649e\u5f97\u788e\u7247\u7eb7\u98de\uff0c\u4eba\u4eba\u8d70\u907f\uff01 \u3000\u3000\u968f\u5373\u542c\u5f97\u6251\u901a\u4e00\u58f0\uff0c\u6d6a\u82b1\u56db\u6e85\uff0c\u9633\u5b97\u6d77\u90a3\u5e9e\u5927\u7684\u8eab\u8eaf\uff0c\u5df2\u8dcc\u4e0b\u8377\u5858\uff01\u539f\u6765\u9633\u5b97\u6d77\u4f7f\u5230\u6700 \u540e\u90a3\u4e00\u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\uff0c\u7528\u5c3d\u5168\u529b\uff0c\u5ffd\u89c9\u654c\u4eba\u653b\u6765\u7684\u52b2\u9053\u513f\u5b8c\u5168\u6d88\u5931\uff0c\u957f\u5251\u88ab\u5f20\u4e39\u67ab\u7684\u67f3\u679d \u8f7b\u8f7b\u4e00\u5e26\uff0c\u8fd9\u4e00\u4e0b\u6b63\u662f\u5185\u5bb6\u7684\u201c\u56db\u4e24\u62e8\u5343\u65a4\u201d\u7684\u7edd\u6280\uff0c\u9ad8\u624b\u6bd4\u62fc\uff0c\u6700\u5fcc\u7684\u5c31\u662f\u201c\u65e0\u7684\u653e \u5931\u201d\uff0c\u653b\u52bf\u7a81\u7136\u65e0\u7740\uff0c\u9633\u5b97\u6d77\u8fd9\u4e00\u4e0b\u731b\u51b2\u4e4b\u52bf\uff0c\u88ab\u5f20\u4e39\u67ab\u8d81\u52bf\u4e00\u7275\uff0c\u7b49\u5982\u5927\u77f3\u6eda\u4e0b\u659c\u5761\uff0c\u66f4 \u6709\u4eba\u5728\u540e\u9762\u63a8\u4e86\u4e00\u628a\uff0c\u54ea\u91cc\u8fd8\u80fd\u715e\u4f4f\uff0c\u56e0\u800c\u8eab\u8eaf\u98de\u4e86\u8d77\u6765\uff0c\u76f4\u8dcc\u4e0b\u8377\u5858\u624d\u6b62\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u80fd\u653e\u80fd\u6536\u65b9\u8fd1\u9053\uff0c\u6b66\u529f\u5904\u4e16\u4e00\u822c\u540c\uff0c\u627f\u73e0\uff0c\u8fd9\u662f\u7b2c\u51e0\u62db\u4e86\uff1f\u201d\u4e8e\u627f\u73e0\u5401 \u4e86\u53e3\u6c14\uff0c\u53eb\u9053\uff1a\u201c\u7b2c\u4e5d\u62db\uff01\u201d\u5f20\u4e39\u67ab\u4e34\u7a97\u53eb\u9053\uff1a\u201c\u9633\u5b97\u6d77\u542c\u7740\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u4e0d\u51c6\u4f60\u518d\u7528\u56db\u5927 \u5251\u5ba2\u7684\u540d\u5934\uff01\u201d \u3000\u3000\u6d2a\u5ca9\u9053\u4eba\u9762\u4f3c\u5bd2\u6c34\uff0c\u8df3\u51fa\u6765\u9053\uff1a\u201c\u5f85\u6211\u6765\u9886\u6559\u4f60\u7684\u7384\u673a\u5251\u6cd5\uff01\u201d\u4f38\u51fa\u4e00\u53cc\u8c61\u7259\u7b77\u5b50\uff0c\u5f80 \u5f20\u4e39\u67ab\u7684\u67f3\u679d\u4e0a\u4e00\u631f\uff0c\u6d2a\u5ca9\u9053\u4eba\u662f\u8d64\u57ce\u5b50\u7684\u5e08\u5f1f\uff0c\u5e74\u6653\u867d\u7136\u6bd4\u7384\u673a\u9038\u58eb\u5c0f\u4e86\u4e8c\u5341\u5e74\uff0c\u8bba\u8d77\u8f88 \u4efd\uff0c\u5374\u662f\u548c\u7384\u673a\u9038\u58eb\u4e00\u8f88\uff0c\u6bd4\u5f20\u4e39\u67ab\u9ad8\u51fa\u4e24\u8f88\uff0c\u5f20\u4e39\u67ab\u53ea\u4f7f\u67f3\u679d\uff0c\u4ed6\u7109\u80fd\u7528\u5251\uff0c\u8fd9\u7b77\u5b50\u4e00 \u631f\uff0c\u6b63\u662f\u4ed6\u60f3\u4e0e\u5f20\u4e39\u67ab\u8d4c\u6597\u5185\u529b\u8f93\u8d62\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u5c0f\u7684\u4e0d\u884c\uff0c\u8001\u7684\u4e5f\u6765\u4e86\u4e48\uff1f\u201d\u8eab\u5f62\u7565\u4e00\u6643\uff0c\u67f3\u679d\u500f\u5730\u79fb\u5f00\uff0c\u6d2a\u5ca9\u9053\u4eba\u8fd8 \u9053\u662f\u4ed6\u907f\u6218\uff0c\u4e00\u53cc\u7b77\u5b50\u8fd0\u8db3\u5185\u52b2\uff0c\u518d\u631f\u8fc7\u53bb\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u7684\u67f3\u679d\u4e00\u5377\uff0c\u559d \u9053\uff1a\u201c\u6362\u8fc7\u5251\u6765\uff01\u201d\u8fde\u4e8e\u627f\u73e0\u4e5f\u770b\u4e0d\u6e05\u695a\u5979\u5e08\u7236\u7528\u4ec0\u4e48\u624b\u6cd5\uff0c\u6d2a\u5b95\u9053\u4eba\u90a3\u53cc\u7b77\u5b50\u53c8\u5df2\u8131\u624b\u98de \u53bb\uff0c\u5c04\u51fa\u7a97\u53e3\uff0c\u8dcc\u4e0b\u6e56\u5fc3\u3002 \u3000\u3000\u4ee5\u5f20\u4e39\u67ab\u7684\u5185\u529f\u800c\u8bba\uff0c\u5176\u5b9e\u548c\u6d2a\u5ca9\u9053\u4eba\u4e43\u662f\u4f2f\u4ef2\u4e4b\u95f4\u3002\u4f46\u4ed6\u4fee\u4e60\u7684\u662f\u6b63\u5b97\u5fc3\u6cd5\uff0c\u5374\u6bd4\u6d2a \u5ca9\u9053\u4eba\u8f83\u4e3a\u7cbe\u7eaf\uff0c\u66f4\u517c\u4ed6\u548c\u9633\u5b97\u6d77\u4ea4\u624b\u5728\u524d\uff0c\u77e5\u9053\u4e86\u8d64\u57ce\u9053\u4eba\u8fd9\u4e00\u6d3e\u6b66\u529f\u7684\u8def\u5b50\u53c8\u6545\u610f\u9a84 \u654c\uff0c\u8d81\u7740\u6d2a\u5b95\u9053\u4eba\u72c2\u653b\u731b\u88ad\u4e4b\u9645\uff0c\u8f7b\u8f7b\u4e00\u4e2a\u4ee5\u9038\u5f85\u52b3\uff0c\u7acb\u523b\u594f\u529f\u3002 \u3000\u3000\u9633\u5b97\u6d77\u8fd9\u65f6\u5df1\u722c\u4e86\u4e0a\u6765\uff0c\u6e7f\u6dcb\u6dcb\u5730\u8d70\u5230\u5e08\u53d4\u8ddf\u524d\uff0c\u624b\u6367\u957f\u5251\uff0c\u9012\u7ed9\u6d2a\u5ca9\u9053\u4eba\u9053\uff1a\u201c\u5e08 \u53d4\uff0c\u8bf7\u7528\u5251\uff01\u201d\u9633\u5b97\u6d77\u8dcc\u4e0b\u8377\u5858\uff0c\u957f\u5251\u5c45\u7136\u8fd8\u672a\u66fe\u8131\u624b\uff0c\u4e5f\u7b97\u96be\u5f97\u4e86\u3002\u6d2a\u5ca9\u9053\u4eba\u8f88\u4efd\u592a\u9ad8\uff0c \u8fd1\u5e74\u4ea6\u5df2\u4e0d\u7528\u5251\u4e0e\u4eba\u5bf9\u654c\u3002\u8fd9\u65f6\u4ed6\u7b77\u5b50\u8131\u624b\uff0c\u5c34\u5c2c\u4e4b\u6781\uff0c\u9633\u5b97\u6d77\u53c8\u9053\uff1a\u201c\u8bf7\u5e08\u53d4\u7528\u5251\uff01\u201d\u6d2a \u5ca9\u9053\u4eba\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u7ec8\u4e8e\u628a\u957f\u5251\u62ff\u8d77\uff0c\u5f20\u4e39\u67ab\u4fa7\u76ee\u659c\u7768\uff0c\u67f3\u679d\u8f7b\u62c2\u8863\u88f3\uff0c\u610f\u6001\u60a0\u95f2\u4e4b\u6781\uff01 \u6d2a\u5ca9\u9053\u4eba\u9762\u4e0a\u706b\u8fa3\u8fa3\u7684\uff0c\u53eb\u9053\uff1a\u201c\u5f20\u4e39\u67ab\uff0c\u4f60\u4e5f\u6362\u8fc7\u5251\uff01\u201d\u81ea\u5f20\u4e39\u67ab\u6765\u5230\u6c34\u69ad\uff0c\u8fd9\u662f\u4ed6\u4eec\u7b2c \u4e00\u6b21\u76f4\u547c\u5176\u540d\u3002\u6c90\u56fd\u516c\u542c\u4e86\uff0c\u9762\u8272\u5927\u53d8\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u73b0\u5728\u4e0d\u8bf4\u6211\u662f\u5047\u5192\u4e86\u5427\uff1f\u627f\u73e0\uff0c\u4f60\u7ed9\u6211\u518d\u6298\u4e00\u6280\u67f3\u679d\u6765\u3002\u201d\u5f20\u4e39 \u67ab\u53cc\u624b\u5404\u6267\u67f3\u679d\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u6d2a\u5ca9\u9053\u4eba\uff0c\u4f60\u662f\u8d64\u57ce\u5b50\u7684\u5e08\u5f1f\uff0c\u6211\u82e5\u53ea\u7528\u4e00\u6839\u67f3\u679d\uff0c\u5927\u662f\u4e0d \u656c\u3002\u73b0\u5728\u6211\u7528\u4e24\u6839\u67f3\u679d\uff0c\u5bf9\u4f60\u4e00\u67c4\u957f\u5251\uff0c\u54b1\u4eec\u5f7c\u6b64\u90fd\u4e0d\u5403\u4e8f\uff01\u201d\u5176\u5b9e\u4e24\u6839\u67f3\u679d\uff0c\u5982\u4f55\u80fd\u62b5\u5f97 \u4f4f\u4e00\u67c4\u957f\u5251\uff1f\u5f20\u4e39\u67ab\u8fd9\u8bdd\uff0c\u56fa\u662f\u81ea\u9ad8\u8eab\u4efd\uff0c\u4f46\u4ea6\u662f\u7ed9\u6d2a\u5ca9\u9053\u4eba\u5706\u4e86\u9762\u5b50\u3002 \u3000\u3000\u6d2a\u5ca9\u9053\u4eba\u95f7\u58f0\u4e0d\u54cd\uff0c\u6597\u9e21\u4e5f\u4f3c\u5730\u7d27\u7d27\u76ef\u4f4f\u5f20\u4e39\u67ab\uff0c\u731b\u5730\u559d\u9053\uff1a\u201c\u5c0f\u8f88\u65e0\u793c\uff0c\u770b\u62db\uff01\u201d\u5587 \u7684\u4e00\u5251\uff0c\u5206\u5fc3\u76f4\u523a\uff0c\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u8fd9\u4e00\u62db\u7528\u5f97\u4e0d\u9519\uff0c\u6bd4\u4f60\u7684\u5e08\u4f84\u9ad8\u660e\u4e00\u500d\uff01\u201d\u4f3c \u8d5e\u4f3c\u8bbd\u3002\u67f3\u679d\u4e00\u8d77\uff0c\u5de6\u53f3\u4ea4\u53c9\uff0c\u6d2a\u5ca9\u9053\u4eba\u5fc3\u4e2d\u4e00\u51db\uff0c\u5f20\u4e39\u67ab\u8fd9\u4e00\u62db\u770b\u4f3c\u8f7b\u63cf\u6de1\u5199\uff0c\u4f46\u5374\u5999\u5230 \u6beb\u5dc5\uff0c\u6d2a\u5ca9\u9053\u4eba\u82e5\u524a\u4ed6\u5de6\u624b\u7684\u67f3\u679d\uff0c\u81ea\u5df1\u53f3\u65b9\u5c31\u8981\u9732\u51fa\u7a7a\u95e8\uff0c\u534a\u8fb9\u8eab\u5b50\u7684\u5341\u516b\u5904\u5927\u7a74\u4fbf\u5168\u5728 \u4ed6\u53f3\u624b\u67f3\u679d\u7684\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u82e5\u524a\u4ed6\u53f3\u624b\u7684\u67f3\u679d\uff0c\u5de6\u65b9\u7684\u7a7a\u95e8\uff0c\u4ea6\u662f\u540c\u6837\u53d7\u5230\u5a01\u80c1\uff0c\u6d2a\u5ca9\u9053\u4eba\u8feb \u5f97\u9000\u5251\u81ea\u4fdd\uff0c\u4ed6\u7684\u5251\u672f\u5f97\u8fc7\u5e08\u5144\u7684\u82e6\u5fc3\u6559\u6388\uff0c\u8fd9\u4e00\u4e0b\u7531\u653b\u6539\u5b88\uff0c\u53d8\u62db\u5947\u5feb\uff0c\u786e\u662f\u4e00\u7b49\u4e00\u7684\u9ad8 \u624b\u529f\u592b\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88\u6570\u5341\u5e74\u5bd2\u6691\u4e4b\u529f\uff0c\u5728\u6b64\u4e00\u62db\u6089\u89c1\u3002\u4f60\u5df2\u5f97\u4e86\u4e0a\u4e58\u6b66\u529f\u7684\u79d8\u5965\uff0c\u53ef \u60dc\u4ec5\u662f\u767b\u5802\uff0c\u672a\u66fe\u5165\u5ba4\u3002\u56de\u53bb\u518d\u4e0e\u5e08\u5144\u5207\u78cb\uff0c\u53ef\u80fd\u81ea\u521b\u5251\u6d3e\u3002\u6211\u5bf9\u4f60\u6709\u539a\u671b\u7109\uff01\u201d\u8fd9\u756a\u8bdd\u66f4 \u662f\u4f3c\u8d5e\u4f3c\u8bbd\uff0c\u76f4\u4f3c\u587e\u5e08\u6279\u5b66\u751f\u7684\u6587\u5377\u3002\u6d2a\u5ca9\u9053\u4eba\u7ed9\u5f20\u4e39\u67ab\u6c14\u5f97\u51e0\u4e4e\u70b8\u4e86\u5fc3\u80ba\uff0c\u4f46\u9ad8\u624b\u6bd4\u6597\uff0c \u54ea\u53ef\u52a8\u6012\uff0c\u6d2a\u5ca9\u9053\u4eba\u6ee1\u809a\u76ae\u7684\u6012\u706b\uff0c\u53ea\u597d\u81ea\u5df1\u6291\u4f4f\uff0c\u51dd\u795e\u5bf9\u4ed8\u5f20\u4e39\u67ab\u4e24\u6839\u67f3\u679d\uff01 \u3000\u3000\u7247\u523b\u4e4b\u95f4\u5df2\u6597\u4e86\u5341\u62db\uff0c\u4f46\u89c1\u5f20\u4e39\u67ab\u7684\u4e24\u6839\u67f3\u679d\u7eb5\u6a2a\u98de\u821e\uff0c\u77eb\u82e5\u6e38\u9f99\uff0c\u6bcf\u62db\u6bcf\u5f0f\uff0c\u90fd\u662f\u51fa \u4eba\u610f\u5916\uff0c\u914d\u5408\u5f97\u5999\u5230\u6beb\u5dc5\uff01\u6d2a\u5ca9\u9053\u4eba\u867d\u7136\u6709\u4e00\u67c4\u957f\u5251\uff0c\u7adf\u662f\u88ab\u5f20\u4e39\u67ab\u7684\u4e24\u6839\u67f3\u679d\u7275\u5236\u5f97\u53ea\u6709 \u62db\u67b6\u4e4b\u529f\uff0c\u6e10\u6e10\u8fde\u62db\u6570\u4e5f\u9012\u4e0d\u51fa\u53bb\u3002\u672c\u6765\u6d2a\u5ca9\u8fe2\u4eba\u90a3\u67c4\u957f\u5251\u4f7f\u5f00\uff0c\u4e00\u4e08\u4e94\u516d\u7684\u5468\u56f4\u90fd\u5728\u4ed6\u5251 \u5149\u7684\u5708\u5b50\u4e4b\u5185\uff0c\u8d8a\u6597\u5708\u5b50\u8d8a\u5c0f\uff0c\u5230\u4e86\u5341\u62db\u4e4b\u5916\uff0c\u5708\u5b50\u5df2\u7f29\u5230\u4e03\u5c3a\u4ee5\u5185\uff0c\u6d2a\u5ca9\u9053\u4eba\u5251\u6c14\u6d88\u81e7\uff0c \u9eef\u7136\u65e0\u5149\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u770b\u5f97\u5fc3\u795e\u4ff1\u9189\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u53cc\u5251\u5408\u58c1\u7684\u6700\u4e0a\u4e58\u5251\u6cd5\uff0c\u4e00\u4eba\u4e5f\u53ef\u4f7f\u7528\u3002\u201d\u7384\u673a\u9038 \u58eb\u6240\u521b\u7684\u53cc\u5251\u5408\u58c1\u7684\u5251\u6cd5\uff0c\u53d8\u5316\u7cbe\u5fae\uff0c\u65e0\u4e0e\u4f26\u6bd4\uff0c\u4f46\u6b63\u56e0\u4e3a\u8fd9\u5957\u5251\u6cd5\u592a\u8fc7\u590d\u6742\u6df1\u5965\uff0c\u5206\u5fc3\u4fbf \u5b66\u4e0d\u597d\uff0c\u6240\u4ee5\u7384\u673a\u9038\u58eb\u5f53\u5e74\uff0c\u6559\u81ea\u5df1\u95e8\u4e0b\u4e24\u4e2a\u6700\u5f97\u610f\u7684\u5f1f\u5b50\uff0c\u8c22\u5929\u534e\u548c\u53f6\u76c8\u76c8\uff0c\u6bcf\u4eba\u53ea\u6559\u534a \u5957\uff0c\u8c22\u5927\u534e\u4f20\u5f20\u4e39\u67ab\uff0c\u53f6\u76c8\u76c8\u4f20\u4e91\u857e\uff0c\u56db\u5e08\u5f92\u7684\u53cc\u5251\u5408\u58c1\uff0c\u5929\u4e0b\u65e0\u654c\uff01\u5f20\u4e39\u67ab\u806a\u660e\u7edd\u9876\uff0c\u53c8 \u5f97\u4e86\u7384\u529f\u8981\u8bc0\uff0c\u548c\u4e91\u857e\u5a5a\u540e\uff0c\u6f5c\u5fc3\u82e6\u7814\uff0c\u94bb\u609f\u51fa\u4e00\u4eba\u4fbf\u53ef\u4f7f\u53cc\u5251\u5408\u58c1\u7684\u7edd\u5b66\uff0c\u53ea\u4ee5\u5251\u6cd5\u800c \u8a00\uff0c\u5df2\u80dc\u8fc7\u4ed6\u5e08\u7956\u5f53\u5e74\uff0c\u4e5f\u6b63\u56e0\u6b64\uff0c\u5f20\u4e39\u67ab\u624d\u6562\u4ee5\u4e24\u6839\u67f3\u679d\uff0c\u62b5\u654c\u6bd4\u81ea\u5df1\u9ad8\u51fa\u4e24\u8f88\u7684\u6d2a\u5ca9\u9053 \u4eba\u7684\u957f\u5251\u3002 \u3000\u3000\u518d\u6597\u4e86\u5341\u62db\uff0c\u6d2a\u5ca9\u9053\u4eba\u7684\u957f\u5251\u4ec5\u80fd\u5c01\u95ed\u95e8\u6237\uff0c\u6c14\u5598\u4e4b\u58f0\uff0c\u5408\u5ea7\u7686\u95fb\u3002\u9633\u5b97\u6d77\u4ece\u4e00\u4e2a\u6b66\u58eb \u624b\u4e2d\u62a2\u8fc7\u4e00\u67c4\u9752\u521a\u5251\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u53db\u9006\u8eab\u4efd\u5df2\u660e\uff0c\u4e0d\u64d2\u4f55\u65f6\uff1f\u201d\u968f\u7740\u559d\u58f0\uff0c\u6709\u5341\u591a\u4eba\u8fdb\u5165 \u6c34\u69ad\uff0c\u6709\u7684\u6b66\u58eb\u88c5\u675f\uff0c\u6709\u7684\u9053\u58eb\u6253\u626e\uff0c\u539f\u6765\u90fd\u662f\u6d2a\u5ca9\u9053\u4eba\u5e26\u6765\u7684\u8d64\u57ce\u6d3e\u95e8\u4e0b\u5f1f\u5b50\uff0c\u4ed6\u4eec\u6ca1\u8d44 \u683c\u548c\u6c90\u56fd\u516c\u540c\u5ea7\uff0c\u6545\u6b64\u9002\u624d\u88ab\u62db\u5f85\u5728\u5916\u9762\uff0c\u7531\u56fd\u516c\u5e9c\u7684\u6b66\u58eb\u966a\u4ed6\u4eec\u5bb4\u996e\u3002\u5982\u4eca\u90fd\u88ab\u9633\u5b97\u6d77\u53ec \u5524\u4e86\u6765\u3002\u8fd9\u6c34\u69ad\u5730\u65b9\u5bbd\u5e7f\uff0c\u4f46\u591a\u4e86\u5341\u591a\u4e2a\u4eba\uff0c\u4ea6\u5df2\u628a\u901a\u5f80\u5cb8\u8fb9\u7684\u8def\u56f4\u5f97\u6c34\u6cc4\u4e0d\u901a\u3002 \u3000\u3000\u6c90\u56fd\u516c\u5927\u4e3a\u4e0d\u60a6\uff0c\u4f46\u5904\u6b64\u60c5\u5f62\uff0c\u53ea\u597d\u7531\u81ea\u5df1\u7684\u51e0\u4e2a\u6b66\u58eb\u62a4\u7740\uff0c\u501a\u58c1\u89c2\u6218\u3002 \u3000\u3000\u4f46\u89c1\u9633\u5b97\u6d77\u628a\u624b\u4e00\u6325\uff0c\u8fd9\u5341\u591a\u4e2a\u4eba\u7acb\u5373\u62a2\u8fdb\u6c34\u69ad\uff0c\u5404\u81ea\u7ad9\u597d\u65b9\u4f4d\uff0c\u6392\u6210\u4e86\u4e00\u4e2a\u94c1\u6876\u822c\u7684 \u5251\u9635\u3002\u6d2a\u5ca9\u9053\u4eba\u8df3\u51fa\u5708\u5b50\u3002\u7ad9\u5728\u5251\u9635\u7684\u4e2d\u5fc3\uff0c\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u4e3e\u8d77\u67f3\u679d\uff0c\u8f7b\u8f7b\u62c2\u62ed\u81ea\u5df1 \u8eab\u4e0a\u7684\u5c18\u58eb\uff0c\u7b11\u9053\uff1a\u201c\u4e45\u95fb\u8d64\u57ce\u6d3e\u7684\u5251\u9635\u9887\u6709\u5999\u5904\uff0c\u4eca\u65e5\u5f97\u89c2\uff0c\u4f55\u5e78\u5982\u4e4b\uff01\u201d \u3000\u3000\u9633\u5b97\u6d77\u4e0e\u4e00\u4f17\u540c\u95e8\u6446\u597d\u5251\u9635\uff0c\u5168\u795e\u51dd\u6ce8\uff0c\u53ea\u7b49\u5e08\u53d4\u4e0b\u4ee4\u53d1\u52a8\u3002\u5bf9\u5f20\u4e39\u67ab\u7684\u51b7\u5632\u70ed\u8bbd\uff0c\u7adf \u4e0d\u6562\u7b54\u534a\u53e5\u8bdd\u3002\u5f20\u4e39\u67ab\u8f6c\u9762\u5411\u7740\u4e8e\u627f\u73e0\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u6218\u603b\u5f97\u534a\u4e2a\u65f6\u8fb0\uff0c\u4f60\u5728\u8fd9\u91cc\uff0c\u5df2\u65e0\u4e8b \u60c5\uff0c\u4f60\u5148\u8d70\u5427\u3002\u82e5\u89c1\u9ed1\u767d\u6469\u8bc3\uff0c\u66ff\u6211\u95ee\u597d\u3002\u4f60\u4e0d\u5fc5\u7b49\u6211\u53bb\u627e\u4f60\u4e86\uff0c\u4f60\u4eec\u53ef\u5148\u5230\u5927\u7406\uff0c\u6211\u6700\u591a \u8fdf\u4f60\u4eec\u4e00\u65e5\u4fbf\u8d76\u56de\u6765\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u8f7b\u677e\u4e4b\u6781\uff0c\u770b\u6765\u8fd9\u5251\u9635\u867d\u662f\u58f0\u5a01\u5413\u4eba\uff0c\u5374\u5168\u4e0d\u5728\u4ed6\u7684\u773c\u5185\u3002\u4e8e\u627f\u73e0 \u5b9e\u5728\u820d\u4e0d\u5f97\u79bb\u5f00\u5979\u7684\u5e08\u7236\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5e08\u7236\u5429\u5490\uff0c\u5fc5\u6709\u9053\u7406\uff0c\u800c\u4e14\u81ea\u5df1\u65e2\u5df2\u77e5\u9053\u9ed1\u767d\u6469\u8bc3 \u5230\u4e86\uff0c\u4e5f\u8be5\u56de\u53bb\u627e\u4ed6\u4eec\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u90a3\u672a\u5f1f\u5b50\u5148\u8d70\u4e86\u3002\u201d\u62d4\u5251\u51fa\u9798\uff0c\u4fbf\u5f80\u5916\u95ef\uff0c\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u6536\u8d77\u5251\u6765\uff0c\u4e0d \u8981\u5413\u4e71\u4e86\u4ed6\u4eec\u7684\u9635\u52bf\u3002\u201d\u4e8e\u627f\u73e0\u6014\u4e86\u4e00\u6014\uff0c\u773c\u89c1\u8fd9\u5251\u9635\u957f\u5251\u5982\u6797\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u6211\u8d70\u51fa\u53bb\uff0c \u4ed6\u4eec\u4fbf\u4e0d\u963b\u6321\uff1f\u6211\u8d64\u624b\u7a7a\u62f3\uff0c\u600e\u80fd\u62b5\u6556\u5341\u51e0\u679d\u5229\u5251\uff1f\u201d\u4f46\u5979\u7d20\u6765\u4fe1\u670d\u5e08\u7236\uff0c\u5e08\u7236\u65e2\u5982\u6b64\u8bf4\uff0c \u5979\u4fbf\u65e0\u6240\u754f\u60e7\uff0c\u7acb\u5373\u628a\u9752\u51a5\u5b9d\u5251\u63d2\u5165\u9798\u4e2d\uff0c\u7f13\u7f13\u5730\u8d70\u51fa\u6c34\u69ad\u3002\u679c\u7136\u90a3\u4e9b\u8d64\u57ce\u7684\u95e8\u4e0b\u5f1f\u5b50\uff0c\u65e0 \u4e00\u4eba\u58eb\u6765\u62e6\u963b\u3002\u4f46\u89c1\u4ed6\u4eec\u90fd\u4f3c\u77f3\u50cf\u4e00\u6837\uff0c\u7ad9\u5728\u539f\u5904\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u770b\u60c5\u5f62\uff0c\u5c31\u662f\u6709\u4eba\u6253\u4ed6\u4eec\u4e00 \u8bb0\u8033\u5149\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u6562\u79fb\u52a8\u811a\u6b65\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5251\u9635\u6700\u8bb2\u7a76\u65b9\u4f4d\u7684\u914d\u5408\uff0c\u5f20\u4e39\u67ab\u77e5\u9053\u9633\u5b97\u6d77\u6446\u8fd9\u4e2a\u5251\u9635\uff0c\u6b63\u662f\u4ee5\u5168\u529b\u6765\u5bf9\u4ed8\u81ea \u5df1\uff0c\u6599\u60f3\u4ed6\u4eec\u4e00\u5b9a\u4e0d\u80af\u4e3a\u4e8e\u627f\u73e0\u800c\u4e71\u4e86\u9635\u811a\uff0c\u6545\u6b64\u624d\u6562\u653e\u5fc3\u53eb\u4e8e\u627f\u73e0\u7a7a\u8eab\u8d70\u51fa\u3002\u4ed6\u53eb\u4e8e\u627f\u73e0 \u5148\u8d70\uff0c\u6b63\u662f\u4e3a\u4e86\u4fdd\u62a4\u5979\u3002\u56e0\u4e3a\u5251\u9635\u82e5\u7136\u53d1\u52a8\uff0c\u81ea\u5df1\u65e0\u59a8\uff0c\u4e8e\u627f\u73e0\u53ea\u6050\u96be\u4ee5\u8131\u8eab\uff0c\u81ea\u5df1\u4e5f\u4e0d\u80fd \u5168\u795e\u5e94\u4ed8\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u521a\u8d70\u4e0a\u5cb8\uff0c\u4fbf\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u7684\u5251\u51fb\u4e4b\u58f0\uff0c\u56de\u5934\u4e00\u770b\uff0c\u4f46\u89c1\u6c34\u69ad\u5185\u6ee1\u662f\u5251\u5149\u4eba\u5f71\uff0c \u4e8e\u627f\u73e0\u975e\u5e38\u60f3\u56de\u53bb\u89c2\u6218\uff0c\u4f46\u7ec8\u4e8e\u8fd8\u662f\u542c\u5e08\u7236\u7684\u8bdd\u8d70\u4e86\u3002 \u3000\u3000\u665a\u98ce\u8f7b\u62c2\uff0c\u4e8e\u627f\u73e0\u53ea\u89c9\u7cbe\u795e\u723d\u5feb\uff0c\u5fc3\u4e2d\u751c\u7f8e\uff0c\u8fd9\u4e24\u65e5\u6765\u5979\u867d\u7136\u5403\u8fc7\u8bb8\u591a\u82e6\u5934\uff0c\u4f46\u5374\u51fa\u4e4e \u610f\u5916\u5730\u78b0\u5230\u5e08\u7236\uff0c\u8fd9\u65f6\u5979\u624d\u5ffd\u7136\u60f3\u8d77\uff0c\u6562\u60c5\u5c31\u662f\u5e08\u7236\u5c06\u5979\u6551\u51fa\u6c34\u7262\uff0c\u8d8a\u60f3\u8d8a\u5bf9\uff0c\u9664\u4e86\u5e08\u7236\uff0c \u522b\u4eba\u54ea\u80fd\u6709\u8fd9\u4efd\u672c\u4e8b\uff1f\u5979\u771f\u60f3\u56de\u53bb\u95ee\u95ee\u5e08\u7236\uff0c\u4f46\u8fd9\u65f6\u5979\u5df2\u8d70\u5165\u57ce\u4e2d\uff0c\u5c06\u8fd1\u5ba2\u5e97\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u5c0f\u864e\u5b50\u4e0d\u77e5\u591a\u8bb0\u6302\u6211\u5462\u3002\u9ed1\u767d\u6469\u8bc3\u4e5f\u4e0d\u77e5\u6765\u4e86\u6ca1\u6709\uff1f\u201d\u56de\u5230\u5ba2\u5e97\uff0c\u53ea\u89c1 \u5916\u9762\u5899\u58c1\uff0c\u81ea\u5df1\u6240\u7559\u7684\u6807\u8bb0\u4ecd\u5728\u3002\u4e8e\u627f\u73e0\u5174\u51b2\u51b2\u5730\u8d70\u5165\u623f\u4e2d\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u864e\u5b50\uff01\u5c0f\u864e\u5b50\uff01\u54b1 \u4eec\u7684\u5e08\u7236\u6765\u5566\u3002\u201d\u623f\u95f4\u5185\u65e0\u4eba\u56de\u7b54\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5927\u4e3a\u4e0d\u60a6\uff0c\u5fc3\u9053\uff1a\u201c\u5c0f\u864e\u5b50\u600e\u7684\u8fd9\u6837\u4f1a\u73a9\uff0c\u5b88\u5019\u4e24\u5929\u4e5f\u65e0\u8010\u6027\uff0c\u771f\u5f97\u597d\u597d\u5730\u6559\u8bad \u4ed6\u4e00\u987f\u3002\u201d\u5979\u8fd8\u4ee5\u4e3a\u662f\u5c0f\u864e\u5b50\u4e00\u4eba\u5077\u5077\u51fa\u57ce\u53bb\u73a9\u3002\u8c01\u5f00\u5c0f\u864e\u5b50\u7684\u623f\u95e8\u4e00\u770b\uff0c\u4f46\u89c1\u8863\u88ab\u51cc\u4e71\uff0c \u4f3c\u4e4e\u662f\u5c0f\u864e\u5b50\u4ece\u7761\u68a6\u4e2d\u88ab\u4eba\u60ca\u9192\uff0c\u4fbf\u7a81\u7136\u8dd1\u4e86\u3002\u4e8e\u627f\u73e0\u5403\u4e86\u4e00\u60ca\uff0c\u5fd9\u53eb\u5e97\u5c0f\u4e8c\u6765\u95ee\u3002 \u3000\u3000\u53ea\u89c1\u5e97\u5c0f\u4e8c\u6218\u6218\u5162\u5162\u5730\u8d70\u5230\u8ddf\u524d\uff0c\u55eb\u55eb\u5685\u5685\u5730\u8bf4\u9053\uff1a\u201c\u5c0f\u5e97\u53ea\u7ba1\u5ba2\u4eba\u98df\u4f4f\uff0c\u77e2\u4e86\u4e1c\u897f\uff0c \u53ef\u4e0d\u5173\u5c0f\u5e97\u7684\u4e8b\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u5931\u4e86\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u6606\u660e\u57ce\u4e2d\u4e45\u65e0\u76d7\u6848 \u53d1\u751f\uff0c\u8fd9\u6b21\u504f\u504f\u5728\u5c0f\u5e97\u53d1\u751f\u76d7\u6848\uff0c\u771f\u662f\u610f\u5916\u3002\u5c0f\u59d0\u8981\u4e0d\u8981\u8bf7\u6211\u4eec\u638c\u67dc\u7684\u966a\u4f60\u53bb\u62a5\u6848\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u7126\u6025\u4e4b\u6781\uff0c\u5fd9\u9053\uff1a\u201c\u95f2\u8bdd\u522b\u591a\u8bb2\u4e86\uff0c\u5feb\u8bf4\u5f3a\u76d7\u5077\u4e86\u6211\u4eec\u7684\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u5e97\u5c0f\u4e8c \u9053\uff1a\u201c\u5f3a\u76d7\u8c15\u4e86\u4f60\u90a3\u5339\u767d\u9a6c\uff01\u201d\u4e8e\u627f\u73e0\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u53eb\u9053\uff1a\u201c\u5f3a\u76d7\u5077\u4e86\u6211\u7684\u5b9d\u9a6c\uff1f\u201d\u5e97 \u5c0f\u4e8c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u7684\u5f1f\u5f1f\u8ffd\u8d3c\u53bb\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u65cb\u98ce\u4e00\u6837\u5730\u6025\u5fd9\u5954\u5230\u9a6c\u53a9\u53bb\u770b\uff0c\u4f46\u89c1\u9a6c\u53a9\u5916\u8e44\u5370\u4ecd\u7559\uff0c\u6392\u6210\u4e24\u884c\uff0c\u9a6c\u53a9\u4e2d\u81ea\u5df1\u90a3 \u5339\u7167\u591c\u72ee\u5b50\u9a6c\u679c\u7136\u4e0d\u89c1\u4e86\uff01\u4e8e\u627f\u73e0\u5954\u51fa\u6570\u91cc\uff0c\u89c1\u8e44\u5370\u9690\u6ca1\u5728\u6548\u5916\u7684\u7530\u91ce\u4e4b\u95f4\uff0c\u8fd9\u624d\u56de\u53bb\u3002\u5e97 \u5c0f\u4e8c\u6b63\u5b88\u5019\u5728\u9a6c\u53a9\u65c1\u8fb9\uff0c\u89c1\u4e8e\u627f\u73e0\u5982\u6b64\u7740\u6025\uff0c\u53c8\u53e3\u53e3\u58f0\u58f0\u8bf4\u662f\u201c\u5b9d\u9a6c\u201d\uff0c\u5fc3\u4e2d\u751a\u662f\u6050\u60e7\uff0c\u751f \u6015\u4e8e\u627f\u73e0\u8981\u4ed6\u4eec\u5e97\u5bb6\u8d54\u507f\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7a0d\u8d70\u5fc3\u795e\uff0c\u95ee\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u6837\u7684\u76d7\u9a6c\u8d3c\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u6628\u665a\u5927\u7ea6\u662f\u56db\u66f4\u65f6\u5206\uff0c \u6211\u4eec\u542c\u5f97\u5c0f\u7237\u5927\u558a\uff0c\u8d76\u51fa\u6765\u65f6\uff0c\u8d3c\u4eba\u5df2\u628a\u9a6c\u5077\u8d70\u4e86\u3002\u5c0f\u7237\u8dd1\u5f97\u771f\u5feb\uff0c\u4ed6\u8863\u670d\u8fd8\u672a\u653e\u5f97\u6574\u9f50\uff0c \u4fbf\u53bb\u8ffd\u90a3\u4e2a\u5077\u9a6c\u8d3c\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u5c31\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9759\u4e86\u4e0b\u6765\uff0c\u7ec6\u5fc3\u4e00\u60f3\uff0c\u5927\u4e3a\u8be7\u5f02\u3002\u5fc3\u9053\uff1a\u201c\u6211\u8fd9\u5339\u7167\u591c\u72ee\u5b50\u9a6c\u53ea\u542c\u5e08\u7236\u5e08\u6bcd\u548c\u6211 \u4e09\u4e2a\u4eba\u7684\u547d\u4ee4\uff0c\u65c1\u4eba\u4f11\u60f3\u9a91\u5f97\u4e86\u5b83\u3002\u7b49\u95f2\u7684\u76d7\u9a6c\u8d3c\u53ea\u6015\u672a\u66fe\u8d70\u8fd1\uff0c\u5c31\u8981\u7ed9\u5b83\u8e22\u7ffb\uff0c\u96be\u9053\u8fd9\u5077 \u9a6c\u8d3c\u7adf\u662f\u4e00\u4e2a\u6b66\u529f\u6781\u5f3a\u7684\u9ad8\u624b\uff1f\u5440\uff0c\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\uff0c\u5373\u7b97\u4ed6\u6b66\u529f\u6781\u5f3a\uff0c\u8db3\u4ee5\u5236\u4f0f\u9f99\u9a79\uff0c\u4f46\u6211\u8fd9 \u5339\u7167\u591c\u72ee\u5b50\u9a6c\u5fc5\u7136\u6323\u624e\uff0c\u600e\u7684\u8e44\u5370\u5374\u53c8\u5e76\u65e0\u51cc\u4e71\u7684\u8ff9\u8c61\u3002\u96be\u9053\u662f\u5e08\u6bcd\u6765\u5c06\u5b83\u7275\u8d70\uff1f\u5e08\u6bcd\u7d20\u6027 \u7aef\u5e84\uff0c\u5979\u7edd\u4e0d\u4f1a\u548c\u6211\u5f00\u8fd9\u4e2a\u73a9\u7b11\uff01\u5e08\u7236\u73b0\u5728\u6c90\u56fd\u516c\u5e9c\u4e2d\uff0c\u66f4\u4e0d\u4f1a\u662f\u4ed6\u4e86\u3002\u5929\u4e0b\u5c1a\u6709\u4f55\u4eba\uff0c\u80fd \u591f\u76d7\u8d70\u6211\u7684\u5b9d\u9a6c\uff1f\u800c\u53c8\u4eca\u5b83\u4e56\u4e56\u987a\u4ece\uff1f\u201d\u60f3\u75db\u8111\u888b\uff0c\u5140\u662f\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u3002 \u3000\u3000\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\uff0c\u4f60\u8981\u4e0d\u8981\u62a5\u6848\uff1f\u201d\u4e8e\u627f\u73e0\u6120\u9053\uff1a\u201c\u8fd8\u62a5\u4ec0\u4e48\u6848\uff1f\u5440\uff0c\u5931\u4e86\u8fd9\u5339\u9a6c \u53eb\u6211\u5982\u4f55\u8d76\u8d3c\u4eba\uff1f\u201d\u5e97\u5c0f\u4e8c\u5ffd\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\uff0c\u4f60\u5931\u4e86\u5750\u9a91\uff0c\u4e0d\u5fc5\u5fc3\u7126\uff0c\u6709\u4e00\u4f4d\u5ba2\u4eba\u7559\u4e0b\u4e86\u4e00 \u5339\u9a6c\u7ed9\u4f60\u3002\u201d\u4e8e\u627f\u73e0\u5927\u5947\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u5ba2\u4eba\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u662f\u4e24\u4e2a\u5916\u56fd\u4eba\uff0c\u4e00\u7537\u4e00\u5973\uff0c\u8863 \u670d\u534e\u4e3d\uff0c\u7537\u7684\u80fd\u8bb2\u54b1\u4eec\u7684\u4e91\u5357\u8bdd\uff0c\u4ed6\u4eec\u8d70\u4e86\u4e0d\u4e45\uff0c\u4ed6\u8bf4\u4ed6\u8ba4\u8bc6\u4f60\uff0c\u542c\u8bf4\u5931\u4e86\u767d\u9a6c\uff0c\u5c31\u5c06\u4e00\u5339 \u5750\u9a91\u7559\u4e0b\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u539f\u6765\u662f\u6bb5\u6f84\u82cd\u548c\u6ce2\u65af\u516c\u4e3b\u6765\u8fc7\u4e86\u3002\u201d\u5fd9\u9053\uff1a\u201c\u4ed6\u4eec\u5462\uff1f\u8fd8\u6709 \u4ec0\u4e48\u4eba\u548c\u4ed6\u4eec\u540c\u6765\uff1f\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u5c31\u662f\u4ed6\u4eec\u4e24\u4e2a\u4eba\uff0c\u770b\u4ed6\u4eec\u884c\u53bb\u5306\u5306\uff0c\u4f3c\u662f\u6709\u4ec0\u4e48\u6025\u4e8b\u3002 \u4e00\u542c\u59d1\u5a18\u4e0d\u5728\u8fd9\u513f\uff0c\u7559\u4e0b\u5750\u9a91\u4fbf\u8d70\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u6bb5\u6f84\u82cd\u548c\u6ce2\u65af\u516c\u4e3b\u9014\u4e2d\u53d7\u5230\u56fd\u516c\u5e9c\u6b66\u58eb\u62e6\u622a\uff0c\u65e0\u602a\u4ed6\u4eec\u4e0d\u6562\u5728\u6606\u660e\u57ce\u4e2d \u4e45\u7559\u4e86\u3002\u201d\u6bb5\u6f84\u82cd\u7559\u4e0b\u7684\u8fd9\u5339\u9a6c\uff0c\u4e43\u662f\u963f\u62c9\u4f2f\u540d\u9a6c\uff0c\u867d\u7136\u8fd8\u4e0d\u53ca\u201c\u7167\u591c\u72ee\u5b50\u201d\uff0c\u4f46\u4ea6\u662f\u96be\u5f97 \u7684\u826f\u9a79\u3002\u5e97\u5c0f\u4e8c\u5c06\u90a3\u5339\u9a6c\u7275\u4e86\u51fa\u6765\uff0c\u4e8e\u627f\u73e0\u4e00\u8dc3\u4e0a\u9a6c\uff0c\u95ee\u9053\uff1a\u201c\u8d3c\u4eba\u5411\u54ea\u4e2a\u65b9\u5411\u8d70\uff1f\u201d\u5e97\u5c0f \u4e8c\u9053\uff1a\u201c\u5357\u65b9\uff01\u201d\u4e8e\u627f\u73e0\u4e00\u8a00\u4e0d\u53d1\uff0c\u7acb\u523b\u50ac\u9a6c\u98de\u5954\uff0c\u5728\u66ae\u972d\u82cd\u832b\u4e2d\uff0c\u51fa\u57ce\u5357\u53bb\uff0c\u5e97\u5c0f\u4e8c\u60ca\u6115 \u4e0d\u5df2\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u59d1\u5a18\u597d\u5947\u602a\uff01\u201d\u4e8e\u627f\u73e0\u4e0d\u5411\u5e97\u5bb6\u7d22\u507f\uff0c\u5e97\u5c0f\u4e8c\u62b9\u4e86\u4e00\u989d\u51b7\u6c57\uff0c\u5fc3\u4e2d\u5982\u540c\u653e \u4e0b\u4e00\u5757\u5927\u77f3\u3002\u5176\u5b9e\u5f53\u76d7\u6848\u53d1\u751f\u4e4b\u65f6\uff0c\u4ed6\u5df2\u88ab\u5413\u5f97\u660f\u5934\u660f\u8111\uff0c\u90a3\u65b9\u5411\u4e43\u662f\u4e71\u6307\u7684\u3002 \u3000\u3000\u4e8e\u4e8c\u627f\u73e0\u5fc3\u7231\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50\u201d\u9a6c\u6709\u5982\u6027\u547d\uff0c\u867d\u7136\u660e\u77e5\u8ffd\u8d76\u4e0d\u4e0a\uff0c\u4ecd\u7136\u5b58\u7740\u4e07\u4e00\u7684\u5ff5 \u5934\uff0c\u5e0c\u671b\u81ea\u5df1\u90a3\u5339\u5b9d\u9a6c\uff0c\u4e0d\u542c\u8d3c\u4eba\u9a71\u7b56\uff0c\u4f1a\u5728\u9014\u4e2d\u76f8\u9047\u3002\u4e8e\u627f\u73e0\u8fd9\u4e00\u7b56\u9a6c\u75be\u9a70\uff0c\u76f4\u5230\u5929\u8272\u5168 \u9ed1\uff0c\u624d\u5230\u5bb6\u519c\u5bb6\u6295\u5bbf\uff0c\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u53c8\u7235\u7b56\u9a6c\u8ffd\u8e2a\uff0c\u53e3\u8def\u4e4b\u4e0a\uff0c\u4e5f\u78b0\u5230\u56db\u4e94\u4e2a\u9a91\u5ba2\uff0c\u6709\u7684\u662f \u7c97\u8c6a\u5927\u6c49\uff0c\u6709\u7684\u662f\u4e0a\u4e86\u5e74\u7eaa\u7684\u8001\u5934\uff0c\u6709\u7684\u50cf\u662f\u8dd1\u6c5f\u6e56\u7684\u5973\u5b50\uff0c\u6bcf\u4e2a\u90fd\u597d\u50cf\u5f62\u8ff9\u53ef\u7591\uff0c\u4f46\u4ed6\u4eec \u9a91\u7684\u90fd\u4e0d\u662f\u201c\u7167\u591c\u72ee\u5b50\u201d\u9a6c\uff0c\u4e8e\u627f\u73e0\u6709\u4e8b\u5728\u8eab\uff0c\u65e0\u5fc3\u7406\u4f1a\u3002 \u3000\u3000\u6b63\u5728\u7b56\u9a6c\u75be\u9a70\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\u8e44\u58f0\u5f97\u5f97\uff0c\u4e00\u9a91\u9a6c\u5982\u98de\u8d76\u4e0a\uff0c\u4e8e\u627f\u73e0\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u9a91\u5728\u9a6c \u80cc\u4e0a\u7684\u4e43\u662f\u4e00\u4e2a\u6d53\u7709\u5927\u773c\u7684\u5c11\u5e74\uff0c\u7a7f\u7740\u4e00\u4ef6\u7c97\u5e03\u8863\uff0c\u50cf\u4e2a\u8d28\u6734\u7684\u5e84\u7a3c\u6c49\uff0c\u8fd9\u5c11\u5e74\u89c1\u4e8e\u627f\u73e0\u56de \u5934\uff0c\u53e4\u94dc\u8272\u7684\u8138\u4e0a\u73b0\u51fa\u4e00\u5708\u7ea2\u8272\uff0c\u6e58\u6e58\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u662f\u4e00\u4e2a\u4eba\u8d76\u8def\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u600e\u4e48\uff1f\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u6211\u4e5f\u662f\u4e00\u4e2a\u8d76\u8def\uff0c\u6b64\u53bb\u6ec7\u5357\uff0c\u8def\u9014\u4e0d\u9756\u3002\u54b1\u4eec\u4e0d\u5982\u540c\u8d70\uff0c\u5f7c\u6b64\u6709\u4e2a \u7167\u987e\uff0c\u4f60\u770b\u5982\u4f55\uff1f\u201d\u4e8e\u627f\u73e0\u6ee1\u809a\u76ae\u4e0d\u597d\u6c14\uff0c\u8981\u4e0d\u662f\u89c1\u8fd9\u5c11\u5e74\u6837\u5b50\u8001\u5b9e\uff0c\u4e0d\u4f3c\u5b58\u7740\u574f\u5fc3\uff0c\u5979\u771f \u60f3\u62bd\u4ed6\u4e00\u5df1\u9a6c\u97ad\uff0c\u5f53\u4e0b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u7d20\u6765\u4e0d\u559c\u6b22\u4e0e\u4eba\u540c\u6b65\uff0c\u591a\u8c22\u4e86\u3002\u201d\u9a6c\u97ad\u5728\u7a7a\u4e2d\u731b\u62bd\uff0c \u567c\u556a\u4f5c\u54cd\uff0c\u80ef\u4e0b\u7684\u963f\u62c9\u4f2f\u9ec4\u8198\u9a6c\u653e\u5f00\u56db\u8e44\uff0c\u4e0d\u4e45\u5c31\u628a\u90a3\u5c11\u5e74\u6487\u5f97\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u6697\u6697\u597d\u7b11\uff0c\u731b\u5730\u60f3\u9053\uff1a\u201c\u8fd9\u4e61\u4e0b\u5c11\u5e74\u770b\u6765\u8eab\u4e0a\u5e76\u65e0\u503c\u94b1\u7684\u4e1c\u897f\uff0c\u5373\u7b97\u8def\u9014\u4e0d\u9756\uff0c \u4ed6\u53c8\u4f55\u60e7\uff1f\u83ab\u975e\u4ed6\u8c8c\u4f3c\u8001\u5b9e\uff0c\u5374\u662f\u574f\u4eba\u4e48\uff1f\u60f3\u4e86\u4e00\u4f1a\uff0c\u201c\u5478\u201d\u4e86\u4e00\u53e3\u51b7\u7b11\u9053\uff1a\u201c\u5373\u7b97\u662f\u574f \u4eba\uff0c\u4ed6\u4e0d\u60f9\u6211\uff0c\u6211\u53c8\u4f55\u5fc5\u7406\u4ed6\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u4f9d\u7740\u5357\u65b9\u7684\u6307\u5411\uff0c\u89c1\u8def\u6717\u884c\uff0c\u76f4\u81f3\u9ec4\u660f\u65f6\u5206\uff0c\u4ecd\u7136\u6ca1\u6709\u89c1\u770b\u81ea\u5df1\u90a3\u5339\u767d\u9a6c\uff0c\u5fc3\u5934 \u51b7\u4e86\u534a\u622a\uff0c\u8fd9\u624d\u9192\u609f\u81ea\u5df1\u7684\u60f3\u6cd5\u592a\u5e7c\u7a1a\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u6837\u8ffd\u8e2a\uff0c\u4e0d\u662f\u529e\u6cd5\uff0c\u4e0d\u5982\u5230\u5927\u7406\u53bb\u7b49\u5019 \u5e08\u7236\u3002\u201d\u62ac\u5934\u524d\u671b\uff0c\u53ea\u89c1\u65e0\u6570\u77f3\u5cf0\uff0c\u5c42\u5c42\u7f57\u5217\uff0c\u6709\u7684\u5b64\u5cf0\u5ced\u7acb\uff0c\u6709\u7684\u5982\u969c\u5c4f\u8fde\uff0c\u5c31\u50cf\u5730\u9762\u4e0a \u7a81\u7136\u6d8c\u8d77\u65e0\u6570\u7389\u5f87\uff0c\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u524d\u4eba\u548f\u6842\u6797\u7684\u98ce\u666f\uff0c\u6709\u8bd7\u4e91\uff1a\u6c34\u4f3c\u9752\u7f57\u5e26\uff1b\u5c71\u5982\u78a7\u7389 \u5f87\u3002\u600e\u7684\u8fd9\u5947\u666f\u4e91\u5357\u4e5f\u6709\u3002\u201d\u4e8e\u627f\u73e0\u535a\u89c8\u7fa4\u4e66\uff0c\u5730\u7406\u56fe\u7c4d\u4e4b\u9898\uff0c\u4e5f\u66fe\u6d89\u730e\uff0c\u7ec6\u7ec6\u4e00\u60f3\uff0c\u731b\u5730 \u60f3\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u5c31\u662f\u524d\u4eba\u79f0\u4e3a\u2018\u5929\u5f00\u5f02\u5883\u2019\u7684\u77f3\u6797\uff1f\u201d\u8fd9\u624d\u8bb0\u8d77\u77f3\u6797\u7684\u786e\u662f\u5728\u4e91\u5357\u7701\u6f5e\u5357\u53bf \u7684\uff0c\u4e0e\u5927\u7406\u5df2\u662f\u80cc\u9053\u800c\u9a70\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7eb5\u9a6c\u8d70\u8fd1\u77f3\u6797\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5934\u9876\u4e00\u5757\u60ac\u7a7a\u7684\u5927\u77f3\u4e0a\u679c\u7136\u9898\u6709\u201c\u5929\u5f00\u5f02\u5883\u201d\u56db \u4e2a\u6731\u7b14\u7ea2\u624b\uff0c\u65c1\u8fb9\u8fd8\u6709\u201c\u5927\u9020\u5947\u89c2\u201d\u201c\u5927\u6c14\u78c5\u7934\u201d\u201c\u9b3c\u65a7\u795e\u5de5\u201d\u7b49\u7b49\u8d5e\u53f9\u7684\u9898\u53e5\uff0c\u671b\u5165 \u201c\u6797\u201d\u4e2d\uff0c\u4f46\u89c1\u4e07\u6237\u5343\u95e8\uff0c\u9634\u68ee\u53ef\u6015\uff0c\u4e8e\u627f\u73e0\u60f3\u9053\uff1a\u201c\u53e4\u4eba\u6e38\u8bb0\u4e2d\u8bf4\uff1a\u77f3\u6797\u5200\u6237\u5343\u95e8\u95ed\uff0c\u4e0d \u4e9a\u6b66\u4faf\u516b\u9635\u56fe\u3002\u82e5\u975e\u6709\u5f53\u5730\u58eb\u4eba\u5411\u5bfb\uff0c\u5207\u4e0d\u53ef\u5b64\u8eab\u64c5\u4eba\u3002\u770b\u6765\u4e0d\u662f\u5938\u5927\u4e4b\u8f9e\u3002\u201d\u53c8\u60f3\u9053\uff1a \u201c\u96be\u5f97\u5230\u6b64\uff0c\u4e0d\u6e38\u4e00\u8d9f\uff0c\u5c82\u975e\u9057\u61be\u3002\u53cd\u6b63\u4e0d\u5dee\u5728\u8fd9\u4e00\u5929\uff0c\u660e\u65e5\u518d\u95ee\u8def\u53bb\u5927\u7406\uff0c\u4e5f\u8fd8\u672a\u8fdf\u3002\u201d \u3000\u3000\u5f53\u4e0b\u627e\u4e00\u5bb6\u519c\u5bb6\u6295\u5bbf\uff0c\u8fd9\u91cc\u662f\u5f5d\u65cf\u5730\u533a\uff0c\u58eb\u4eba\u7279\u522b\u597d\u5ba2\uff0c\u5bf9\u4e8e\u627f\u73e0\u6bb7\u52e4\u62db\u5f85\uff0c\u6367\u51fa\u4ed6\u4eec \u5f85\u5ba2\u7684\u4e0a\u54c1\u571f\u4ea7\u201c\u4e73\u6247\u201d\uff0c\u90a3\u662f\u7528\u7f8a\u4e73\u6216\u725b\u4e73\u505a\u6210\u7684\u4e00\u79cd\u98df\u54c1\uff0c\u6709\u4e00\u80a1\u81ca\u5473\uff0c\u4e8e\u627f\u73e0\u751a\u662f\u4e0d \u60ef\uff0c\u4f46\u8fd8\u662f\u5403\u4e86\u51e0\u5757\u3002\u5403\u4e86\u665a\u996d\uff0c\u4e8e\u627f\u73e0\u95ee\u90a3\u4e3b\u4eba\u8bc6\u4e0d\u8bc6\u77f3\u6797\u4e2d\u7684\u9053\u8def\uff1f\u4e3b\u4eba\u9053\uff1a\u201c\u8bc6\u662f\u8bc6 \u5f97\uff0c\u4e0d\u8fc7\u73b0\u5728\u4e0d\u597d\u53bb\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u4e3b\u4eba\u9053\uff1a\u201c\u542c\u8bf4\u6797\u4e2d\u6709\u76d7\u532a\u7684\u5de2\u7a74\uff0c\u524d \u5e74\u6709\u4eba\u5e26\u4e24\u4e2a\u6c49\u4eba\u5165\u5185\u6e38\u89c8\uff0c\u4ece\u6b64\u4e0d\u89c1\u3002\u6211\u4eec\u90fd\u4e0d\u6562\u53bb\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u6012\u9053\uff1a\u201c\u7adf\u6709\u6b64\u4e8b\uff1f\u5929 \u4e0b\u5947\u666f\uff0c\u5c82\u5bb9\u532a\u5f92\u5360\u636e\uff0c\u4f60\u5e26\u6211\u53bb\uff0c\u6211\u66ff\u4f60\u4eec\u5730\u65b9\u9664\u6b64\u4e00\u5bb3\u3002\u201d\u90a3\u4e3b\u4eba\u53cc\u624b\u8fde\u6447\uff0c\u9053\uff1a\u201c\u4f7f \u4e0d\u5f97\uff0c\u4f7f\u4e0d\u5f97\uff0c\u4f11\u8bf4\u59d1\u5a18\u53ea\u662f\u4e00\u4eba\uff0c\u5c31\u662f\u5343\u519b\u4e07\u9a6c\uff0c\u4ed6\u4eec\u5728\u91cc\u9762\u5148\u5360\u4e86\u5730\u5229\uff0c\u4e5f\u662f\u6709\u53bb\u65e0 \u56de\u3002\u201d\u4e8e\u627f\u73e0\u89c1\u8fd9\u4e3b\u4eba\u5982\u6b64\u5bb3\u6015\uff0c\u4e0d\u613f\u5f3a\u62b1\u6240\u96be\uff0c\u5fc3\u4e2d\u95f7\u95f7\u4e0d\u4e50\u3002 \u3000\u3000\u5403\u4e86\u665a\u996d\uff0c\u65b0\u6708\u521d\u4e0a\uff0c\u4e8e\u627f\u73e0\u72ec\u51fa\u6751\u8fb9\u6f2b\u6b65\uff0c\u4e3b\u4eba\u5bb6\u8981\u966a\u5979\uff0c\u4e8e\u627f\u73e0\u63a8\u8f9e\u4e86\uff0c\u58eb\u4eba\u4e0d\u5584 \u8bf4\u8f9e\uff0c\u89c1\u4e8e\u627f\u73e0\u575a\u6267\u8981\u72ec\u81ea\u51fa\u5916\u8d70\u8d70\uff0c\u53ea\u597d\u7531\u5979\u3002\u4e3b\u4eba\u5bb6\u53c8\u5343\u53ee\u4e07\u5631\u53eb\u4e8e\u627f\u73e0\u53ea\u53ef\u5728\u6751\u8fb9\u6563 \u6b65\uff0c\u4e0d\u53ef\u8d70\u5f97\u592a\u8fdc\uff0c\u4e8e\u627f\u73e0\u4e5f\u70b9\u5934\u5e94\u5141\u4e86\u3002 \u3000\u3000\u6751\u5916\u6709\u4e00\u4e2a\u5c0f\u6e56\uff0c\u6e56\u8fb9\u4e5f\u6709\u5e73\u5730\u6d8c\u8d77\u7684\u77f3\u5cf0\uff0c\u5012\u5f71\u5165\u6e56\uff0c\u5947\u4e3d\u65e0\u501a\uff0c\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u524d \u4eba\u6e38\u8bb0\u4e2d\u8bf4\uff0c\u77f3\u6797\u4e2d\u4e5f\u6709\u6e56\uff0c\u540d\u4e3a\u5251\u5730\uff0c\u60f3\u6765\u90a3\u91cc\u7684\u98ce\u666f\u5f53\u66f4\u80dc\u6b64\u3002\u201d\u4e0d\u89c9\u5fc3\u6d9f\u6447\u6447\uff0c\u4e0d\u77e5 \u4e0d\u89c9\u79fb\u6b65\u8d70\u5411\u77f3\u6797\u3002 \u3000\u3000\u5ffd\u89c1\u6709\u4e24\u6761\u9ed1\u5f71\u4ece\u4fa7\u63a0\u8fc7\uff0c\u8ddd\u79bb\u4e8e\u627f\u73e0\u7ea6\u6709\u5341\u4f59\u4e08\u4e4b\u9065\uff0c\u4e8e\u627f\u73e0\u662f\u7ec3\u8fc7\u6697\u5668\u7684\uff0c\u773c\u529b\u7279 \u522b\u9510\u5229\uff0c\u5728\u6708\u8272\u9eef\u6de1\u4e4b\u4e0b\uff0c\u4ecd\u7136\u542c\u5f97\u51fa\u4ed6\u4eec\u7684\u53bb\u8def\u3002\u77f3\u6797\u5916\u9762\u6709\u4e2a\u5927\u8349\u576a\uff0c\u5927\u8349\u576a\u4e2d\u4e5f\u6709\u51e0 \u77f3\u5cf0\uff0c\u4e0a\u9762\u5982\u6811\u4ea4\u6cb3\uff0c\u5982\u4f1e\u5982\u76d6\uff0c\u90a3\u4e24\u6761\u4eba\u5f71\u5c31\u94bb\u8fdb\u4e0b\u9762\u6d1e\u4e2d\uff0c\u4e0d\u4e45\u53c8\u89c1\u4e24\u6761\u4eba\u5f71\u5165\u5185\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u4e9b\u4eba\u5c31\u662f\u515a\u532a\u3002\u201d\u90a3\u51e0\u4e2a\u77f3\u5cf0\u5e76\u4e0d\u9ad8\uff0c\u5c0f\u5de7\u73b2\u73d1\uff0c\u5b9b\u5982\u76c6\u666f\uff0c\u4e8e \u627f\u73e0\u827a\u9ad8\u80c6\u5927\uff0c\u8dd1\u4e86\u51fa\u6765\uff0c\u65bd\u5c55\u4e0a\u4e58\u8f7b\u8eab\u529f\u592b\uff0c\u6084\u65e0\u58f0\u606f\u5730\u98de\u8eab\u63a0\u4e0a\u77f3\u5cf0\uff0c\u4ece\u77f3\u9699\u7f1d\u4e2d\u5f20\u76ee \u671b\u4e0b\uff0c\u4f46\u89c1\u9ed1\u5f71\u7ef0\u7ef0\uff0c\u53ea\u5206\u522b\u5f97\u4e00\u4eba\u4f3c\u662f\u4e2a\u5973\u5b50\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e00\u4e2a\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8463\u8001\u5927\uff0c\u4f60\u53ef\u770b\u6e05\u695a\u4e86\uff0c\u70b9\u5b50\u5c31\u53ea\u662f\u5b64\u8eab\u4e00\u4eba\uff1f\u201d\u201c\u70b9\u5b50\u201d\u4e43 \u662f\u9ed1\u9053\u4e2d\u7684\u201c\u9ed1\u8bdd\u201d\uff0c\u6307\u76d7\u515a\u6240\u8981\u52a8\u624b\u5bf9\u4ed8\u7684\u4eba\u5ba2\uff0c\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u679c\u7136\u662f\u76d7\u515a\u5728\u8fd9\u91cc\u5546\u91cf \u8c0b\u8d22\u5bb3\u547d\u4e4b\u4e3e\u3002\u6211\u65e2\u5728\u6b64\uff0c\u5c82\u53ef\u4e0d\u7ba1\uff1f\u201d\u90a3\u88ab\u53eb\u505a\u8463\u8001\u5927\u7684\u4eba\u8bf4\u9053\uff1a\u201c\u5343\u771f\u4e07\u786e\uff0c\u5c31\u662f\u70b9\u5b50 \u4e00\u4eba\u3002\u201d\u518d\u542c\u4e0b\u53bb\uff0c\u53ef\u4ee4\u4e8e\u627f\u73e0\u5927\u5403\u4e00\u60ca\u3002\u6b63\u662f\uff1a \u3000\u3000\u4ed9\u5883\u90a3\u5bb9\u72d0\u9f20\u5360\uff0c\u4e4d\u95fb\u9ed1\u8bdd\u6700\u60ca\u5fc3\u3002 \u3000\u3000\u6b32\u77e5\u540e\u52df\u5982\u4f55\uff0c\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u5f39\u6307\u795e\u901a\u3000\u5c11\u5e74\u663e\u8eab\u624b\u3000\u98de\u82b1\u7edd\u6280\u3000\u5973\u4fa0\u670d\u5f3a\u4eba \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u5f39\u6307\u795e\u901a\u3000\u5c11\u5e74\u663e\u8eab\u624b\u3000\u98de\u82b1\u7edd\u6280\u3000\u5973\u4fa0\u670d\u5f3a\u4eba \u3000\u3000\u53ea\u542c\u5f97\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u70b9\u5b50\u6562\u5355\u8eab\u4e00\u4eba\uff0c\u72ec\u884c\u4e07\u671b\uff0c\u5012\u4e0d\u53ef\u5927\u610f\u4e86\u3002\u201d\u8fd9\u53e5\u8bdd \u5e76\u4e0d\u51fa\u5947\uff0c\u51fa\u5947\u7684\u662f\u8fd9\u58f0\u97f3\u597d\u751f\u719f\u6089\uff0c\u4e8e\u627f\u73e0\u4ed4\u7ec6\u4e00\u60f3\uff0c\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u539f\u6765\u8bf4\u8fd9\u8bdd\u7684\u4eba\u662f \u66fe\u5230\u8fc7\u592a\u6e56\u5c71\u5e84\u7684\u4e03\u4e2a\u5927\u5185\u536b\u58eb\u4e4b\u4e00\uff0c\u540d\u5b57\u53eb\u505a\u674e\u6db5\u771f\u3002\u5f53\u65e5\u90a3\u4e03\u4e2a\u536b\u58eb\u88ab\u9ed1\u767d\u6469\u8bc3\u6253\u6b7b\u6253 \u4f24\u4e86\u516d\u4eba\uff0c\u53ea\u6709\u8fd9\u4e2a\u674e\u6db5\u771f\u56e0\u4e3a\u80fd\u591f\u6321\u5f97\u9ed1\u767d\u6469\u8bc3\u4e24\u62f3\uff0c\u6545\u6b64\u9ed1\u767d\u6469\u8bc3\u6709\u610f\u653e\u4ed6\u9003\u8d70\uff0c\u4e8e\u627f \u73e0\u60f3\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u662f\u532a\u515a\uff0c\u5374\u539f\u6765\u662f\u5b98\u5bb6\u7684\u4eba\uff0c\u8fd9\u5012\u5947\u4e86\uff0c\u4ed6\u4eec\u8981\u5bf9\u4ed8\u8c01\u5462\uff1f\u201d \u3000\u3000\u518d\u542c\u4e0b\u53bb\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u5c11\u5987\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8001\u7237\u5b50\u653e\u5fc3\uff0c\u54b1\u4eec\u4e0d\u548c\u4ed6\u660e\u5200\u660e\u67aa\u5730\u52a8\u624b\uff0c \u81ea\u6709\u5de7\u8ba1\u5c06\u4ed6\u5f15\u5165\u77f3\u6797\uff0c\u54c8\u54c8\uff0c\u4ed6\u5355\u8eab\u4e00\u4eba\uff0c\u4efb\u4ed6\u6709\u5929\u5927\u795e\u901a\uff0c\u4e5f\u662f\u63d2\u7fc5\u96be\u98de\u3002\u201d\u674e\u6db5\u771f \u9053\uff1a\u201c\u4ed6\u51c6\u4f1a\u88ab\u4f60\u5f15\u5165\u77f3\u6797\u4e48\uff1f\u201d\u90a3\u5c11\u5987\u9053\uff1a\u201c\u53ea\u6d88\u7565\u65bd\u5c0f\u8ba1\uff0c\u4ed6\u6ca1\u6709\u4e0d\u4e0a\u94a9\u4e4b\u7406\u3002\u201d\u4e8e\u627f \u73e0\u5c4f\u606f\u547c\u5438\uff0c\u60f3\u542c\u90a3\u5c11\u5987\u8bf4\u7684\u662f\u4ec0\u4e48\u8be1\u8ba1\uff0c\u5374\u4e0d\u6599\u8fd9\u4e9b\u4eba\u5012\u662f\u673a\u7075\u5f97\u5f88\uff0c\u8bf4\u5230\u8fd9\u91cc\uff0c\u58f0\u97f3\u987f \u65f6\u5c0f\u4e86\u3002\u4ed6\u4eec\u5012\u4e0d\u662f\u6599\u5f97\u4e0a\u9762\u6709\u4eba\uff0c\u53ea\u662f\u6bcf\u9022\u8bf4\u5230\u673a\u5bc6\u4e4b\u4e8b\uff0c\u4fbf\u7528\u8033\u8bed\uff0c\u5728\u4ed6\u4eec\u5df2\u6210\u4e60\u60ef \u4e86\u3002\u4e8e\u627f\u73e0\u51dd\u795e\u9759\u542c\uff0c\u4e5f\u542c\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u542c\u5f97\u674e\u6db5\u771f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u679c\u7136\u5999\u8ba1\uff0c\u53ea\u662f\u59d4\u5c48\u4f60\u4e86\u201d\u3002\u987f\u4e86\u4e00\u987f\u53c8\u9053\uff1a \u201c\u6536\u62fe\u4e86\u8fd9\u4e2a\u70b9\u5b50\uff0c\u54b1\u4eec\u518d\u5bf9\u4ed8\u90a3\u5c0f\u4e2b\u5934\u3002\u201d\u90a3\u5c11\u5987\u95ee\u9053\uff1a\u201c\u8fd9\u5c0f\u4e2b\u5934\u4e5f\u662f\u4e2a\u786c\u70b9\u5b50\u4e48\uff1f\u201d \u674e\u6db5\u771f\u9053\uff1a\u201c\u542c\u9633\u603b\u7ba1\u8bf4\uff0c\u8fd9\u4e2b\u5934\u7684\u5251\u6cd5\u5df2\u5f97\u4ed6\u5e08\u7236\u771f\u4f20\uff0c\u4e00\u624b\u91d1\u82b1\u6697\u5668\uff0c\u66f4\u662f\u975e\u540c\u5c0f\u53ef\uff0c \u5176\u5b9e\u4e0d\u5fc5\u4ed6\u8bf4\uff0c\u662f\u5f20\u4e39\u67ab\u7684\u5f92\u5f1f\uff0c\u9519\u4e5f\u9519\u4e0d\u5230\u54ea\u513f\uff0c\u5f53\u7136\u662f\u4e2a\u6709\u672c\u9886\u7684\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00 \u51db\uff1a\u4ed6\u4eec\u8bf4\u7684\u53ef\u4e0d\u6b63\u662f\u81ea\u5df1\uff1f\u771f\u60f3\u7acb\u523b\u53d1\u51fa\u91d1\u82b1\uff0c\u5c06\u4ed6\u4eec\u6253\u4e2a\u534a\u6b7b\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u6697\u4e2d\u5077 \u88ad\uff0c\u6709\u6b20\u5149\u660e\uff0c\u800c\u4e14\u597d\u5947\u5ff5\u8d77\uff0c\u60f3\u770b\u770b\u4ed6\u4eec\u6240\u8981\u5bf9\u4ed8\u7684\u662f\u4ec0\u4e48\u4eba\uff0c\u56e0\u6b64\u54ac\u4e00\u54ac\u7259\uff0c\u53c8\u5fcd\u7740 \u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5987\u53c8\u95ee\u9053\uff1a\u201c\u90a3\u5c0f\u4e2b\u5934\u548c\u70b9\u5b50\u662f\u540c\u4e00\u6761\u8def\uff0c\u82e5\u7136\u4e24\u4e2a\u540c\u65f6\u9047\u4e0a\uff0c\u54b1\u4eec\u5148\u5bf9\u4ed8\u8c01\uff1f\u201d \u674e\u6db5\u771f\u9053\uff1a\u201c\u8fd9\u8fd8\u7528\u95ee\u4e48\uff1f\u5f53\u7136\u662f\u4f9d\u8ba1\u884c\u4e8b\uff0c\u5148\u5bf9\u4ed8\u90a3\u4e2a\u70b9\u5b50\u3002\u5207\u4e0d\u53ef\u53eb\u4ed6\u4eec\u6c47\u5408\u5728\u4e00\u8d77\u3002 \u597d\u5566\uff0c\u54b1\u4eec\u53ef\u4ee5\u5230\u77f3\u6797\u91cc\u5148\u5e03\u7f72\u4e00\u756a\u4e86\u3002\u201d\u542c\u5230\u8fd9\u91cc\uff0c\u4e8e\u627f\u73e0\u98d8\u8eab\u4fbf\u8d70\u3002\u85cf\u8eab\u6e56\u7554\uff0c\u679c\u7136\u89c1 \u4e00\u4e2a\u9ed1\u5f71\u4eba\u8d70\u5165\u77f3\u6797\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u4e0b\u81ea\u601d\uff1a\u201c\u674e\u6db5\u771f\u7684\u672c\u9886\u751a\u9ad8\uff0c\u8fd9\u4e48\u591a\u4eba\uff0c\u5374\u4e0d\u6562\u548c\u4eba\u5bb6\u660e\u5200\u660e\u67aa\u5730\u52a8\u624b\uff0c\u8fd9 \u2018\u70b9\u5b50\u2019\u662f\u4ec0\u4e48\u6837\u7684\u4eba\u7269\uff1f\u201d\u53c8\u60f3\u5230\u81ea\u5df1\u662f\u201c\u53db\u9006\u201d\u4e4b\u5973\uff0c\u9633\u5b97\u6db5\u6b32\u5f97\u800c\u7518\u5fc3\uff0c\u4f46\u542c\u8fd9\u5e72\u4eba \u7684\u53e3\u6c14\uff0c\u4ed6\u4eec\u6240\u8981\u5bf9\u4ed8\u7684\u654c\u4eba\uff0c\u6562\u60c5\u6bd4\u81ea\u5df1\u66f4\u4e3a\u91cd\u8981\u3002\u597d\u5947\u5fc3\u8d8a\u53d1\u6d53\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u5929\u8fd8\u672a\u4eae\uff0c\u4e8e\u627f\u73e0\u63a8\u8bf4\u8981\u8d76\u8def\u3002\u4fbf\u5411\u4e3b\u4eba\u544a\u8f9e\uff0c\u5374\u6084\u6084\u85cf\u5728\u77f3\u6797\u5916\u9762\u8349\u576a\u4e0a\u4e24\u5757 \u602a\u77f3\u7f1d\u4e2d\uff0c\u60f3\u770b\u770b\u4ed6\u4eec\u65bd\u5c55\u7684\u662f\u4ec0\u4e48\u8be1\u8ba1\uff1f\u76f4\u7b49\u5230\u65e5\u4e0a\u4e09\u7aff\uff0c\u53ea\u6709\u597d\u51e0\u4e2a\u884c\u4eba\u7ecf\u8fc7\u77f3\u6797\uff0c\u6797 \u4e2d\u603b\u65e0\u534a\u70b9\u58f0\u606f\u3002\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u96be\u9053\u90a3\u4eba\u4eca\u65e5\u4e0d\u6765\u4e86\uff1f\u201d\u5ffd\u542c\u5f97\u4e00\u9635\u9a6c\u8e44\u4e4b\u58f0\uff0c\u8fdc\u8fdc\u4f20 \u6765\uff0c\u4e0d\u4e45\u5373\u5230\u3002 \u3000\u3000\u62ac\u5934\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u6628\u65e5\u76f8\u9022\u7684\u90a3\u4e2a\u5c11\u5e74\uff0c\u90a3\u5c11\u5e74\u8d70\u5230\u77f3\u6797\u524d\u9762\u7684\u8349\u576a\uff0c\u4f3c\u4e4e\u662f\u88ab\u8fd9\u5929 \u7136\u7684\u5947\u666f\u6240\u5438\u5f15\uff0c\u8df3\u4e0b\u9a6c\u80cc\uff0c\u4ef0\u5934\u8d1f\u624b\uff0c\u9a7b\u8db3\u89c2\u8d4f\u3002\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u770b\u4ed6\u4e00\u526f\u6123\u5934\u6123\u8111\u7684\u6837 \u5b50\uff0c\u5374\u4e5f\u61c2\u5f97\u6b23\u8d4f\u98ce\u666f\u3002\u201d\u5ffd\u542c\u5f97\uff1a\u4e00\u4e2a\u5973\u4e8e\u7684\u58f0\u97f3\u5c16\u53eb\uff0c\u90a3\u5c11\u5e74\u4e00\u773c\u626b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u76f8\u8c8c \u72f0\u72de\u7684\u6076\u6c49\uff0c\u62b1\u7740\u4e00\u4e2a\u5c11\u5987\uff0c\u72c2\u5954\u5165\u6797\uff0c\u90a3\u5c11\u5987\u624b\u821e\u8db3\u8e48\u5730\u6323\u624e\uff0c\u5927\u53eb\u5927\u56b7\uff0c\u558a\u9053\uff1a\u201c\u62a2\u4eba \u5566\uff0c\u6551\u547d\u5440\uff0c\u62a2\u4eba\u5566\uff0c\u6551\u547d\u5440\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u4e00\u58f0\u5927\u559d\uff0c\u98de\u6b65\u67aa\u53bb\u3002\u8fd9\u4e00\u5207\u60c5\u5f62\u81ea\u7136\u4e5f\u5165\u4e86\u4e8e\u627f\u73e0\u773c\u5e18\uff0c\u4e8e\u627f\u73e0\u5446\u4e86\u4e00\u5446\uff0c\u9aa4 \u7136\u9192\u609f\uff1a\u90a3\u4e00\u4f19\u4eba\u6240\u8981\u5bf9\u4ed8\u7684\u201c\u70b9\u5b50\u201d\uff0c\u6562\u60c5\u7adf\u662f\u8fd9\u4e2a\u6123\u5934\u6123\u8111\u7684\u5c11\u5e74\uff01\u4e8e\u627f\u73e0\u6025\u5fd9\u53eb\u9053\uff1a \u201c\u522b\u8ffd\uff0c\u522b\u8ffd\uff01\u8fd9\u662f\u8be1\u8ba1\uff01\u201d\u90a3\u5c11\u5e74\u8eab\u6cd5\u4f55\u7b49\u5feb\u6377\uff0c\u4e0d\u5f85\u4e8e\u627f\u73e0\u8bdd\u558a\u51fa\u53e3\uff0c\u4ed6\u5df2\u4ece\u4e24\u5cf0\u4ea4\u6cb3 \u7684\u5165\u53e3\uff0c\u5954\u5165\u77f3\u6797\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u4fa0\u4e49\u5fc3\u80a0\uff0c\u65e0\u6687\u601d\u7d22\uff0c\u62d4\u51fa\u5b9d\u5251\uff0c\u8ddf\u7740\u4e5f\u95ef\u8fdb\u53bb\u4e86\u77f3\u6797\uff0c\u4f46\u542c\u5f97\u91cc\u9762\u4e00\u7247\u91d1\u94c1\u4ea4 \u9e23\u4e4b\u58f0\uff0c\u4e8e\u627f\u73e0\u4ed7\u7740\u8033\u529b\u806a\u654f\uff0c\u7ed5\u4e86\u4e24\u4e2a\u5f2f\u8def\uff0c\u53ea\u89c1\u9762\u524d\u6709\u4e00\u4e2a\u4e08\u4f59\u65b9\u5706\u7684\u77f3\u576a\uff0c\u51e0\u6761\u6c49\u5b50 \u6b63\u5728\u56f4\u7740\u90a3\u4e2a\u5c11\u5e74\u53ae\u6740\uff0c\u5176\u4e2d\u4e00\u4e2a\u8001\u5934\uff0c\u6b63\u662f\u90a3\u4e2a\u674e\u6db5\u771f\u3002\u9002\u624d\u72c2\u53eb\u201c\u62a2\u4eba\u201d\u7684\u90a3\u4e2a\u5c11\u5987\uff0c \u501a\u58c1\u65c1\u89c2\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u6211\u7684\u8ba1\u7b56\u5982\u4f55\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u5c11\u5e74\u7684\u4e00\u638c\uff0c\u628a\u4e00\u4e2a\u654c\u4eba\u6454\u51fa\uff0c\u649e\u5230\u5ca9\u77f3\u4e0a\uff0c\u987f\u65f6\u5934\u7834\u8840\u6d41\uff0c \u4e8e\u627f\u73e0\u53c8\u60ca\u53c8\u559c\uff0c\u60f3\u4e0d\u5230\u8fd9\u5c11\u5e74\u7adf\u4f1a\u91d1\u521a\u638c\u5927\u6454\u780c\u624b\u7684\u529f\u592b\u3002\u674e\u6db5\u771f\u201c\u54fc\u201d\u7684\u4e00\u58f0\uff0c\u53cc\u638c\u4e00 \u7275\u4e00\u5f15\uff0c\u7528\u7684\u662f\u592a\u6781\u62f3\u7684\u62db\u5f0f\u201c\u5982\u5c01\u4f3c\u95ed\u201d\uff0c\u5c06\u90a3\u5c11\u5e74\u7684\u91d1\u521a\u638c\u529b\u8f7b\u8f7b\u5316\u89e3\uff0c\u4f46\u90a3\u5c11\u5e74\u7684\u638c \u52bf\u5f3a\u52b2\u4e4b\u6781\uff0c\u53cc\u638c\u8fde\u73af\u75be\u626b\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u674e\u6db5\u771f\u4ed7\u7740\u6570\u5341\u5e74\u7cbe\u7eaf\u7684\u529f\u529b\uff0c\u4ea6\u4e0d\u8fc7\u4ec5\u80fd\u5c06\u4ed6\u6253 \u5411\u81ea\u5df1\u8eab\u4e0a\u7684\u638c\u529b\u5378\u5f00\u800c\u5df2\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u53c8\u662f\u4e00\u4e2a\u53d7\u4f24\u5012\u4ed6\u3002 \u3000\u3000\u90a3\u5c11\u5987\u4e00\u9762\u66ff\u53d7\u4f24\u7684\u540c\u515a\u5305\u624e\u4f24\u53e3\uff0c\u4e00\u9762\u53eb\u9053\uff1a\u201c\u8001\u7237\u5b50\u4e0d\u5fc5\u786c\u62fc\uff0c\u5148\u53eb\u4ed6\u5c1d\u5c1d\u6211\u7684\u5b50 \u6bcd\u8fde\u73af\u8774\u8776\u9556\u3002\u201d\u4e00\u626c\u624b\u6697\u5668\u6ee1\u7a7a\u6492\u51fa\uff0c\u4e8e\u627f\u73e0\u5927\u6012\uff0c\u970e\u5730\u4ece\u77f3\u9a8f\u4e2d\u98de\u8eab\u800c\u51fa\uff0c\u559d\u9053\uff1a\u201c\u4e0d \u8981\u8138\u7684\u4e0b\u6d41\u884c\u5f84\uff01\u201d\u4e00\u626c\u624b\uff0c\u4e5f\u6492\u51fa\u6ee1\u7a7a\u91d1\u82b1\uff0c\u628a\u90a3\u5c11\u5987\u7684\u8774\u8776\u9556\u626b\u6570\u6253\u843d\uff0c\u731b\u7136\u95f4\u53ea\u542c\u5f97 \u94ee\u94ee\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u53ea\u89c1\u90a3\u4e9b\u8774\u8776\u9556\u7eb7\u7eb7\u788e\u88c2\uff0c\u5ffd\u7136\u5c04\u51fa\u4e86\u65e0\u6570\u94f6\u9489\uff0c\u539f\u6765\u8fd9\u5c11\u5987\u7684\u6697\u5668 \u540d\u4e3a\u201c\u5b50\u6bcd\u8fde\u73af\u8774\u8776\u9556\u201d\uff0c\u4e00\u9047\u5916\u529b\u9707\u8361\uff0c\u7acb\u523b\u5206\u88c2\uff0c\u6bcf\u4e00\u4e2a\u201c\u6bcd\u4f53\u201d\u4e4b\u5185\uff0c\u90fd\u6709\u51e0\u679a\u6bd2 \u9488\uff0c\u6697\u5668\u4e4b\u4e2d\uff0c\u53c8\u6709\u6697\u5668\uff0c\u7aef\u7684\u662f\u72e0\u6bd2\u975e\u5e38\uff0c\u9632\u4e0d\u80dc\u9632\uff0c\u4e0d\u8bba\u7528\u624b\u6765\u63a5\uff0c\u6216\u7528\u5175\u5668\u78b0\u78d5\uff0c\u90fd \u4f1a\u7740\u4e86\u9053\u513f\u3002\u5e78\u4e8f\u5728\u534a\u7a7a\u4e2d\u4fbf\u88ab\u4e8e\u627f\u73e0\u7528\u91d1\u82b1\u6253\u788e\uff0c\u8981\u4e0d\u7136\u5f85\u5230\u8fd1\u8eab\uff0c\u90a3\u4e00\u5343\u6570\u767e\u6bd2\u9489\uff0c\u53ea \u8981\u6709\u4e00\u679a\u5c04\u5230\u8eab\u4e0a\uff0c\u4fbf\u662f\u6027\u547d\u4e4b\u5fe7\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u9aa4\u89c1\u6bd2\u9488\u98de\u51fa\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u628a\u5b9d\u5251\u821e\u6210\u4e00\u5708\u94f6\u8679\uff0c\u53ea\u542c\u5f97\u90a3\u5c11\u5e74\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3 \u4e86\uff01\u201d\u547c\u7684\u4e00\u638c\uff0c\u90a3\u6ee1\u7a7a\u98de\u9488\u88ab\u638c\u98ce\u4e00\u9707\uff0c\u90fd\u5c04\u5230\u5bf9\u9762\u7684\u77f3\u58c1\u4e0a\uff0c\u77f3\u576a\u4e0a\u4f17\u4eba\u7eb7\u7eb7\u5c04\u95ea\u3002 \u3000\u3000\u5ffd\u5730\u91cc\u90a3\u674e\u6db5\u771f\u4e00\u58f0\u547c\u5578\uff0c\u53eb\u9053\uff1a\u201c\u626f\u547c\uff01\u201d\u4e94\u4e2a\u4eba\u5206\u5411\u56db\u65b9\u9003\u8d70\uff0c\u77f3\u6797\u4e2d\u5343\u95e8\u4e07\u6237\uff0c \u9053\u8def\u7eb7\u6b67\uff0c\u4e8e\u627f\u73e0\u4e0e\u90a3\u5c11\u5e74\u8ba4\u5b9a\u674e\u6db5\u771f\u7684\u80cc\u5f71\u8ffd\u9010\uff0c\u7ed5\u4e86\u51e0\u7ed5\uff0c\u674e\u6db5\u771f\u94bb\u5165\u4e00\u6761\u6781\u72ed\u7a84\u7684\u901a \u8def\uff0c\u628a\u773c\u671b\u53bb\uff0c\u4f46\u89c1\u8fc2\u56de\u66f2\u6298\uff0c\u9634\u9634\u68ee\u68ee\uff0c\u602a\u77f3\u602a\u5ca9\uff0c\u5982\u5251\u5982\u621f\uff0c\u906e\u7740\u5929\u5149\uff0c\u4ee4\u4eba\u4e0d\u5bd2\u800c \u6817\u3002\u4e8e\u627f\u73e0\u987f\u8db3\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u542c\u6211\u7684\u8bdd\uff1f\u660e\u77e5\u5c71\u6709\u864e\uff0c\u4f60\u5374\u504f\u5411\u864e\u4e2d\u884c\u3002\u4f60\u6ca1\u542c\u89c1\u6211\u56b7 \u662f\u8be1\u8ba1\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u5c34\u5c2c\u7b11\u9053\uff1a\u201c\u542c\u662f\u542c\u89c1\u7684\u3002\u55ef\uff0c\u5f53\u65f6\u6551\u4eba\u5fc3\u5207\uff0c\u90a3\u5987\u4eba\u558a\u5f97\u51c4\u51c4\u60e8\u60e8\uff0c\u6211\uff0c \u6211\u2026\u2026\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u4e0d\u4fe1\u6211\u7684\u8bdd\u3002\u6562\u60c5\u5f53\u65f6\u4f60\u8fd8\u6000\u7591\u6211\u662f\u6076\u5f92\u7684\u515a\u7fbd\u5427\uff1f\u201d\u90a3\u5c11 \u5e74\u7684\u9762\u8272\u6da8\u7ea2\uff0c\u6e58\u6e58\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\u3002\u201d\u4e8e\u627f\u73e0\u89c1\u4ed6\u8fd9\u526f\u6837\u5b50\uff0c\u53c8\u597d\u6c14\uff0c\u53c8\u597d\u7b11\uff0c\u8f6c\u5ff5 \u4e00\u60f3\uff0c\u81ea\u5df1\u672c\u6765\u4e0e\u4ed6\u4e0d\u76f8\u8ba4\u8bc6\uff0c\u4eab\u51fa\u5076\u7136\uff0c\u4ed6\u773c\u89c1\u90a3\u5c11\u5987\u88ab\u6076\u5f92\u5f3a\u62a2\uff0c\u4e5f\u96be\u602a\u4ed6\u4e0d\u6562\u4fe1\u81ea\u5df1 \u7684\u8bdd\uff0c\u5bf9\u4ed6\u7684\u4fa0\u4e49\u5fc3\u80a0\uff0c\u5012\u8d77\u4e86\u51e0\u5206\u656c\u610f\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fdb\u6765\u5bb9\u6613\uff0c\u51fa\u53bb\u5c31\u96be\u4e86\u3002\u201d\u4e0e\u90a3\u5c11\u5e74\u540c\u8def\u51fa\u6765\uff0c\u6cbf\u8def\u7559\u4e0b\u6807\u5fd7\uff0c\u8d70\u4e86\u534a \u5929\uff0c\u53c8\u56de\u5230\u539f\u6765\u7684\u4f4d\u7f6e\uff0c\u4e8e\u627f\u73e0\u4e5f\u8d70\u5f97\u6709\u70b9\u7d2f\u4e86\uff0c\u501a\u5728\u5ca9\u5de5\u77f3\u4e0a\u5598\u6c14\uff0c\u90a3\u5c11\u5e74\u4e00\u8def\u4e0a\u4e0d\u53d1\u4e00 \u8a00\uff0c\u8fd9\u65f6\u624d\u62ff\u51fa\u5e72\u7cae\uff0c\u9012\u7ed9\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u997f\u4e86\u5427\uff1f\u5403\u4e00\u70b9\u513f\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u5e26 \u6709\u591a\u5c11\u5e72\u7cae\uff1f\u4eca\u5929\u5bf9\u4ed8\u8fc7\u53bb\uff0c\u660e\u5929\u5462\uff1f\u660e\u5929\u5bf9\u4ed8\u8fc7\u53bb\uff0c\u540e\u5929\u5462\uff1f\u8d70\u4e0d\u51fa\u77f3\u6797\u770b\u600e\u529e\uff1f\u201d\u5979\u8d70 \u4e0d\u51fa\u77f3\u6797\u3001\u6ee1\u809a\u76ae\u95f7\u6c14\uff0c\u8bf4\u8bdd\u4e4b\u540e\uff0c\u60f3\u8d77\u73b0\u5728\u8be5\u540c\u821f\u5171\u6d4e\uff0c\u5b9e\u4e0d\u8be5\u602a\u8d23\u90a3\u4e2a\u5c11\u5e74\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5374\u5df2\u7ed9\u5979\u8bf4\u5f97\u8baa\u8baa\u7684\u602a\u4e0d\u597d\u610f\u601d\uff0c\u671b\u4e86\u4e8e\u627f\u73e0\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u6211\u8fde\u7d2f\u59d1\u5a18\u4e86\u3002 \u59d1\u5a18\u65e2\u7136\u77e5\u9053\u8fd9\u91cc\u6613\u8fdb\u96be\u51fa\uff0c\u4f55\u4ee5\u53c8\u8981\u8fdb\u6765\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u5c82\u80fd\u89c1\u4f60\u9047\u9669\u4e0d\u6551\uff1f\u201d\u90a3\u5c11 \u5e74\u9053\uff1a\u201c\u4fa0\u58eb\u5fc3\u80a0\uff0c\u53ef\u656c\u53ef\u656c\uff01\u201d\u5411\u4e8e\u627f\u73e0\u4f5c\u4e86\u4e00\u63d6\uff0c\u4e8e\u627f\u73e0\u5657\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u81ea\u5df1 \u79f0\u8d5e\u81ea\u5df1\u3002\u201d \u3000\u3000\u6b47\u4e86\u4e00\u4f1a\uff0c\u4e8e\u627f\u73e0\u95f7\u6c14\u6d88\u4e86\uff0c\u9053\uff1a\u201c\u65e2\u7136\u6765\u5230\u8fd9\u513f\uff0c\u6b63\u597d\u8d81\u6b64\u673a\u4f1a\u770b\u770b\u77f3\u6797\u5947\u666f\u3002\u201d\u628a \u5fc3\u4e8b\u6682\u629b\u8111\u540e\uff0c\u4ed7\u5251\u800c\u884c\uff0c\u4e13\u62e3\u6ca1\u8d70\u8fc7\u7684\u8def\u8d70\uff0c\u90a3\u5c11\u5e74\u4ea6\u6b65\u4ea6\u8d8b\uff0c\u968f\u5728\u540e\u9762\u3002\u4f46\u89c1\u5947\u5ca9\u602a \u77f3\uff0c\u89e6\u76ee\u7686\u662f\uff0c\u6709\u7684\u5730\u65b9\uff0c\u72ed\u7a84\u5f97\u4ec5\u53ef\u5bb9\u8eab\uff0c\u6709\u7684\u5730\u65b9\u5374\u53c8\u7a7a\u9614\u5f97\u53ef\u4f5c\u7ec3\u6b66\u573a\u3002\u8d70\u5230\u4e00\u5904\uff0c \u4e24\u5cf0\u76f8\u63a5\u7684\u7a84\u8def\uff0c\u5ffd\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u652f\u6697\u7bad\u5c04\u4e0b\uff0c\u4e8e\u627f\u73e0\u968f\u624b\u7528\u5251\u62e8\u843d\uff0c\u8fc7\u4e0d\u591a\u4e45\uff0c \u53c8\u662f\u4e00\u679a\u94b1\u954a\u98de\u6765\uff0c\u4e8e\u627f\u73e0\u5927\u6012\uff0c\u89d1\u51c6\u77f3\u5cf0\u4e0a\u9762\u4eba\u5f71\u4e00\u95ea\uff0c\u7acb\u523b\u4e00\u6735\u91d1\u82b1\u5c04\u53bb\uff0c\u53ea\u542c\u5f97\u201c\u54ce \u54df\u201d\uff0c\u4e00\u58f0\uff0c\u90a3\u653e\u6697\u5668\u7684\u4eba\u4f3c\u4e4e\u53d7\u4f24\u4e0d\u8f7b\uff0c\u4e0a\u9762\u6709\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u7684\u91d1\u82b1\u5389\u5bb3\uff0c\u4f55\u5fc5\u60f9 \u5979\uff0c\u8ba9\u5979\u997f\u4e86\u51e0\u5929\uff0c\u54b1\u4eec\u518d\u53bb\u6536\u62fe\u5979\u3002\u201d\u4e8e\u627f\u73e0\u6c14\u4ed6\u4e0d\u8fc7\uff0c\u53c8\u53d1\u51fa\u4e86\u4e24\u6735\u91d1\u82b1\uff0c\u8fd9\u56de\u5374\u53d1\u4e86 \u4e2a\u7a7a\uff0c\u4e24\u6735\u91d1\u82b1\u78b0\u5230\u77f3\u58c1\u4e0a\uff0c\u8dcc\u4e0b\u6765\u3002 \u3000\u3000\u98ce\u666f\u867d\u4f73\uff0c\u654c\u4eba\u7aa5\u4f3a\uff0c\u4e8e\u627f\u73e0\u5174\u81f4\u5927\u51cf\u3002\u90a3\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u4f60\u4f46\u653e\u5fc3\u89c2\u8d4f\uff0c\u518d\u6709\u9f20\u8f88 \u9a9a\u6270\uff0c\u6211\u7ed9\u4f60\u6253\u53d1\u4ed6\u3002\u201d\u6ca1\u591a\u4e45\uff0c\u5728\u4e00\u5904\u5c96\u58c1\u80cc\u540e\uff0c\u53c8\u89c1\u6709\u4eba\u5f71\u4e00\u95ea\uff0c\u90a3\u5c11\u5e74\u4e0d\u5f85\u5979\u53d1\u6697 \u5668\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u4fbf\u662f\u4e00\u5757\u77f3\u5b50\u98de\u53bb\uff0c\u53ea\u542c\u5f97\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u90a3\u4eba\u62b1\u5934\u98de\u7a9c\uff0c\u4e8e\u627f\u73e0\u8d5e\uff1a\u201c\u597d \u4e00\u4e2a\u5f39\u6307\u795e\u901a\u7684\u529f\u592b\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u4e2d\u7591\u9053\uff1a\u201c\u5f53\u4eca\u4e4b\u4e16\uff0c\u91d1\u94a2\u624b\u548c\u5f39\u6307\u795e\u901a\u7684\u529f\u592b\uff0c\u8981\u7b97\u6211\u7684\u592a\u5e08\u4f2f\u8463\u5cb3\u6700\u4e3a\u9ad8 \u5f3a\uff0c\u4ed6\u8fdc\u5904\u6f20\u5916\uff0c\u542c\u5e08\u7236\u8bf4\uff0c\u53ea\u662f\u5341\u5e74\u4e4b\u524d\uff0c\u4ed6\u5230\u8fc7\u4e00\u6b21\u4e2d\u539f\uff0c\u8fd9\u5c11\u5e74\u6c5f\u5357\u53e3\u97f3\uff0c\u5374\u600e\u7684\u5b66 \u4f1a\u4e86\u4ed6\u7684\u4e24\u95e8\u7edd\u6280\uff1f\u83ab\u975e\u662f\u6211\u89c1\u95fb\u6d45\u964b\uff0c\u6b66\u6797\u4e2d\u8fd8\u53e6\u6709\u4f1a\u8fd9\u4e24\u95e8\u7edd\u6280\u7684\u9ad8\u624b\u4e48\uff1f\u201d\u6b63\u60f3\u95ee\u90a3 \u5c11\u5e74\uff0c\u5ffd\u89c1\u773c\u524d\u8c41\u7136\u5f00\u6717\uff0c\u53ea\u89c1\u8244\u58c1\u4e0b\u9762\u4e00\u4e2a\u5c0f\u6e56\uff0c\u6e56\u8fb9\u91ce\u82b1\u6742\u5f00\uff0c\u5e7d\u9999\u6251\u9f3b\uff0c\u5ced\u58c1\u4e0a\u9898\u6709 \u201c\u5251\u5cf0\u201d\u4e24\u4e2a\u5927\u5b57\uff0c\u8fd9\u4e2a\u5c0f\u6e56\u60f3\u5fc5\u5c31\u662f\u201c\u5251\u6c60\u201d\u4e86\u3002\u5251\u5cf0\u3002\u4e0a\u900f\u4e0b\u5929\u5149\uff0c\u4ee4\u6e56\u5149\u66f4\u589e\u6f88\u6f4d\uff0c \u91ce\u82b1\u6811\u6781\uff0c\u4ece\u77f3\u58c1\u4e0a\u6a2a\u4f38\u5165\u74f6\u6e56\u4e2d\u82b1\u6811\u7684\u5012\u5f71\u548c\u77f3\u5cf0\u7684\u5012\u5f71\uff0c\u6784\u6210\u4e86\u7edd\u7f8e\u7684\u753b\u56fe\uff0c\u4e8e\u627f\u73e0\u5fc3 \u65f7\u795e\u6021\uff0c\u5929\u5927\u7684\u6101\u70e6\u90fd\u5f52\u4e8e\u4e4c\u6709\uff0c\u5fae\u7b11\u541f\u9053\uff1a\u201c\u758f\u5f71\u6a2a\u659c\u6c34\u6df1\u6d45\uff0c\u6697\u9999\u6d6e\u52a8\u6708\u9ec4\u660f\uff0c\u82e5\u975e\u77f3 \u6797\u4e2d\u6709\u532a\u5f92\u76d8\u8e1e\uff0c\u5728\u6b64\u6c60\u7554\uff0c\u7ed3\u5e90\u8bfb\u4e66\uff0c\u4e0e\u6e56\u5149\u5c71\u8272\uff0c\u5171\u4f34\u6668\u660f\uff0c\u5012\u662f\u4eba\u751f\u81f3\u4e50\u3002\u201d\u90a3\u5c11\u5e74 \u5ffd\u9053\uff1a\u201c\u6797\u548c\u9756\u5b64\u5c71\u548f\u6885\u7684\u8fd9\u4e24\u53e5\u8bd7\uff0c\u79fb\u5230\u8fd9\u91cc\u6765\u7528\uff0c\u679c\u7136\u8d34\u5207\u4e0d\u8fc7\u3002\u4f46\u5929\u4e0b\u7eb7\u6270\uff0c\u54b1\u4eec\u53c8 \u600e\u5fcd\u81ea\u5f97\u5176\u4e50\uff1f\u201d\u4e8e\u627f\u73e0\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u770b\u8fd9\u5c11\u5e74\u4e00\u526f\u4e61\u4e0b\u7684\u795e\u6c14\uff0c\u4ed6\u5374\u4e5f\u61c2\u5f97\u6797\u548c\u9756 \u7684\u8bd7\u3002\u201d\u5bf9\u90a3\u5c11\u5e74\u6e10\u6709\u4e00\u4e9b\u597d\u611f\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7ad9\u5728\u6e56\u8fb9\uff0c\u51fa\u4e86\u4e00\u4f1a\u795e\uff0c\u5fc3\u9053\uff1a\u201c\u82e5\u662f\u5e08\u7236\u5728\u8fd9\u513f\uff0c\u5b9a\u6709\u4f73\u53e5\u541f\u548f\u3002\u201d\u5ffd\u7136\u53c8\u60f3 \u8d77\u94c1\u955c\u5fc3\u6765\uff0c\u94c1\u955c\u5fc3\u4f3c\u4e4e\u4e5f\u914d\u5f97\u4e0a\u8fd9\u6e56\u5149\u5c71\u8272\uff0c\u5446\u5446\u5730\u51fa\u4e86\u4e00\u4f1a\u795e\uff0c\u5ffd\u7136\u8f6c\u5934\u95ee\u9053\uff1a\u201c\u4f60\u53eb \u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u5979\u548c\u8fd9\u5c11\u5e74\u5728\u77f3\u6797\u4e2d\u5927\u534a\u5929\uff0c\u8fd9\u65f6\u65b9\u60f3\u8d77\u4e86\u95ee\u4ed6\u7684\u540d\u5b57\u3002\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u6211\u59d3 \u53f6\uff0c\u540d\u53eb\u6210\u6797\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u662f\u6c5f\u5357\u4eba\u5417\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u6e10\u897f\u77f3\u95e8\u4eba\u3002\u201d \u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e07\u91cc\u8fe2\u8fe2\uff0c\u4f60\u8dd1\u5230\u4e91\u5357\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u6210\u6797\u8fdf\u7591\u4e86\u4e00\u4f1a\uff0c\u77a7\u4e86\u77a7\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u60f3\u5230\u5927\u7406\u53bb\u5bfb\u627e\u4e00\u4e2a\u4eba\u3002\u201d\u4e8e\u627f\u9053\uff1a\u201c\u5927\u7406\u53ef \u4e0d\u662f\u8d70\u8fd9\u6761\u8def\u5475\u3002\u201d\u53f6\u6210\u6797\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u59d1\u5a18\u6709\u8fd9\u4e48\u597d\u6b66\u827a\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u54a6\uff0c\u6211\u95ee\u4f60\u4e3a\u4ec0\u4e48\u8d70\u8fd9\u6761\u8def\uff1f\u8fd9\u4e0e\u6211\u7684\u6b66\u827a\u597d\u574f\u53c8\u6709\u4ec0\u4e48\u76f8\u4e8e\uff1f\u201d\u53f6\u6210\u6797\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u6211 \u89c1\u59d1\u5a18\u5355\u8eab\u4e00\u4eba\uff0c\u8def\u4e0a\u53c8\u6709\u6b79\u5f92\u8e2a\u8ff9\uff0c\u6211\uff0c\u6211\u2026\u2026\u201d\u4e8e\u627f\u73e0\u5927\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u4e0d\u653e\u5fc3\u6211\uff0c \u60f3\u5728\u6697\u4e2d\u4fdd\u62a4\u6211\u5462\u3002\u602a\u4e0d\u5f97\u4f60\u6628\u65e5\u60f3\u9080\u6211\u540c\u884c\u4e86\u3002\u201d\u53f6\u6210\u676f\u9053\uff1a\u201c\u542c\u59d1\u5a18\u7684\u53e3\u97f3\uff0c\u4e5f\u662f\u6c5f\u5357 \u4eba\uff0c\u8bf7\u95ee\u59d1\u5a18\u4f55\u4ee5\u4e5f\u5230\u4e91\u5357\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u662f\u8981\u5230\u5927\u7406\u3002\u4f60\u522b\u5fd9\u95ee\u6211\uff0c\u6211\u5148\u95ee\u4f60\uff0c\u4f60\u8981\u5230\u5927\u7406\u627e\u8c01\uff1f\u201d\u53f6\u6210\u6797 \u9053\uff1a\u201c\u59d1\u5a18\u662f\u540c\u9053\u4e2d\u4eba\uff0c\u4e0d\u6015\u89c1\u544a\u3002\u6211\u60f3\u5bfb\u8bbf\u7684\u662f\u5f53\u4eca\u5929\u4e0b\u7684\u7b2c\u4e00\u4f4d\u5251\u5ba2\u5f20\u4e39\u67ab\uff01\u201d\u4e8e\u627f\u73e0 \u8df3\u8d77\u6765\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u4f60\u627e\u7684\u4eba\u5c31\u662f\u6211\u7684\u5e08\u7236\u2026\u2026\u201d\u53f6\u6210\u6797\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u5f20\u4e39\u67ab\u662f\u4f60\u7684\u5e08 \u7236\uff1f\u201d\u7a81\u7136\u5411\u4e8e\u627f\u73e0\u4f5c\u4e86\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u662f\u6211\u7684\u5e08\u59d0\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u5e08\u7236\u662f \u8c01\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u6211\u5e08\u7236\u662f\u53f2\u5b9a\u5c71\u3002\u201d\u53f2\u5b9a\u5c71\u662f\u8463\u5cb3\u7684\u5f1f\u5b50\uff0c\u4e8e\u627f\u73e0\u53ef\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\uff0c\u8fd9 \u624d\u60f3\u8d77\u4e86\u662f\u6709\u8fd9\u4e48\u4e00\u4e2a\u5e08\u4f2f\uff0c\u6d6a\u8ff9\u5927\u6c5f\u5357\u5317\uff0c\u884c\u533b\u6551\u4eba\u3002\u5ffd\u5730\u5657\u55e4\u7b11\u9053\uff1a\u201c\u4f60\u4eca\u5e74\u51e0\u5c81 \u4e86\uff1f\u201d \u3000\u3000\u53f6\u6210\u6797\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u865a\u5ea6\u4e8c\u5341\u4e8c\u4e2a\u6625\u79cb\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u6211\u4eca\u5e74\u521a\u6ee1\u5341\u4e03\u5c81\u3002 \u4f60\u600e\u4e48\u53eb\u6211\u505a\u5e08\u59d0\uff1f\u201d\u6210\u6797\u6734\u8bb7\u8c26\u606d\uff0c\u5bf9\u5e73\u8f88\u4e4b\u4eba\uff0c\u4e60\u60ef\u4e86\u79f0\u547c\u522b\u4eba\u505a\u5144\u59d0\u4ee5\u793a\u656c\u610f\uff0c\u542c\u4e86 \u6b64\u8bdd\uff0c\u4e0d\u7981\u54d1\u7136\u5931\u7b11\uff0c\u6539\u53e3\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u5e08\u59b9\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u627e\u6211\u7684\u5e08\u7236\uff1f\u201d\u6210\u6797\u9053\uff1a\u201c\u662f\u53d4\u53d4\u5dee\u9063\u6211\u6765\u7684\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u4f60\u53d4\u53d4\u662f\u8c01\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u6211\u53d4\u53d4\u540d\u53eb\u53f6\u5b97\u7559\u3002\u201d\u4e8e\u627f\u73e0\u5931\u58f0\u53eb\u9053\uff1a\u201c\u539f\u6765\u662f\u53f6\u5927 \u54e5\uff01\u201d\u5979\u5728\u4e49\u519b\u4e4b\u65f6\uff0c\u519b\u4e2d\u4e0a\u4e0b\u90fd\u79f0\u547c\u53f6\u5b97\u7559\u505a\u201c\u53f6\u5927\u54e5\u201d\uff0c\u5979\u53eb\u60ef\u4e86\u53e3\uff0c\u4e00\u65f6\u8f6c\u4e0d\u8fc7\u6765\uff0c \u5ffd\u5730\u60f3\u8d77\u81ea\u5df1\u4e0e\u6b64\u4eba\u5e08\u5144\u59b9\u6392\u884c\uff0c\u600e\u4e48\u53eb\u522b\u4eba\u7684\u53d4\u53d4\u505a\u201c\u5927\u54e5\u201d\uff1f\u751a\u89c9\u4e0d\u597d\u610f\u601d\u3002 \u3000\u3000\u53f6\u6210\u6797\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4eba\u4eec\u90fd\u53eb\u6211\u7684\u53d4\u53d4\u505a\u2018\u5927\u54e5\u2019\u3002\u54a6\uff0c\u4f60\u662f\u4e0d\u662f\u4e8e\u59d1\u5a18\uff1f\u201d\u4e8e\u627f\u73e0 \u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u6211\u53d4\u53d4\u544a\u8bc9\u6211\u7684\uff0c\u8bf4\u4f60\u66fe\u5e2e\u8fc7\u4ed6\u4e0d\u5c11\u5fd9\uff0c\u79f0\u8bb8\u4f60\u662f\u5f53\u4eca\u5973 \u6770\u3002\u201d\u4e8e\u627f\u73e0\u60f3\u5230\u5f53\u65f6\u5973\u626e\u7537\u88c5\uff0c\u88ab\u53f6\u5b97\u7559\u8bc6\u7834\u884c\u85cf\uff0c\u4ed6\u4e00\u76f4\u6ca1\u6709\u8bf4\u7834\uff0c\u5374\u539f\u6765\u5077\u5077\u5730\u5411\u4f84 \u513f\u8bf4\u4e86\uff0c\u4e0d\u7981\u674f\u8138\u98de\u971e\uff0c\u7ea2\u900f\u8033\u80cc\u3002\u5c34\u5c2c\u4e00\u7b11\uff0c\u63a9\u9970\u7a98\u6001\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\u6211\u5728\u4e49\u519b\u4e4b\u65f6\uff0c\u5374 \u4e0d\u89c1\u4f60\uff1f\u201d \u3000\u3000\u53f6\u6210\u6797\u9053\uff1a\u201c\u6211\u542c\u5230\u53d4\u53d4\u7ea0\u96c6\u4e49\u519b\uff0c\u6297\u51fb\u502d\u5bc7\u7684\u6d88\u606f\uff0c\u624d\u8f9e\u522b\u5e08\u7236\u8d76\u5f80\uff0c\u8d76\u5230\u4e4b\u65f6\uff0c\u4f60 \u4eec\u65e9\u5df2\u628a\u502d\u5bc7\u9a71\u9010\u4e0b\u6d77\u4e86\u3002\u771f\u662f\u60ed\u6127\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u53d4\u53d4\u6709\u4ec0\u4e48\u7d27\u8981\u7684\u4e8b\u60c5\uff0c\u8981\u4f60\u4e07\u91cc \u8fe2\u8fe2\uff0c\u8d76\u5230\u5927\u7406\u53bb\u5bfb\u89c5\u6211\u7684\u5e08\u7236\u3002\u201d \u3000\u3000\u53f6\u6210\u6797\u9053\uff1a\u201c\u4e49\u519b\u9a71\u9010\u502d\u5bc7\u4e0b\u6d77\u4e4b\u540e\uff0c\u6211\u53d4\u53d4\u5949\u6bd5\u64ce\u5929\u505a\u5929\u4e0b\u5341\u516b\u7701\u7684\u5927\u9f99\u5934\u3002\u201d\u4e8e\u627f \u73e0\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u505a\u5317\u4e94\u7701\u7684\u5927\u9f99\u5934\u8fd8\u8d5a\u4e0d\u591f\uff0c\u5c45\u7136\u53c8\u8981\u81ea\u5c01\u505a\u5929\u4e0b\u5341\u516b\u7701\u7684\u5927\u9f99\u5934 \u4e86\uff1f\u201d\u53f6\u6210\u6797\u5446\u4e86\u4e00\u5446\uff0c\u7565\u6709\u8be7\u5f02\u4e4b\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u6bd5\u5927\u9f99\u5934\u96c4\u624d\u5927\u7565\uff0c\u8c6a\u6c14\u8ffd\u4eba\uff0c\u8fd9\u5927\u9f99\u5934 \u4e4b\u4f4d\uff0c\u662f\u6211\u53d4\u53d4\u7518\u5fc3\u8ba9\u4e0e\u4ed6\u7684\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u4e0d\u8c08\u6bd5\u64ce\u5929\uff0c\u4f60\u518d\u8bf4\u4f60\u7684\u53d4\u53d4\u3002\u201d \u53f6\u6210\u6797\u9053\uff1a\u201c\u6bd5\u5927\u9f99\u5934\u8981\u7ea0\u96c6\u5929\u4e0b\u4e49\u5e08\uff0c\u63ed\u7aff\u8d77\u4e8b\uff0c\u63a8\u7ffb\u660e\u5ba4\uff0c\u53e6\u5efa\u7687\u671d\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u6211\u65e9\u77e5\u4ed6\u60f3\u79f0\u7687\u79f0\u5e1d\uff0c\u568e\uff0c\u600e\u4e48\u8fd8\u662f\u8c08\u4ed6\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u4e0d\u8c08\u6bd5\u64ce\u5929\uff0c\u53ef\u5c31\u6ca1\u6cd5\u8bf4\u5f97\u6e05 \u695a\u3002\u201d\u4e0d\u660e\u4e8e\u627f\u73e0\u4f55\u4ee5\u5982\u6b64\u618e\u6076\u6bd5\u64ce\u5929\uff1f\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u73b0\u4e0b\u4e49\u519b \u5f15\u5f13\u5f85\u53d1\uff0c\u4e3e\u4e8b\u5728\u5373\u3001\u6bd5\u64ce\u5929\u8bf4\u4f60\u5e08\u7236\u6709\u4e00\u5e45\u5730\u56fe\uff0c\u5f97\u6b64\u5730\u56fe\uff0c\u7528\u519b\u884c\u5175\uff0c\u5f53\u6709\u5927\u52a9\uff0c\u4ed6\u77e5 \u9053\u6211\u662f\u5f20\u5927\u4fa0\u7684\u5e08\u4f84\uff0c\u6545\u6b64\u53eb\u6211\u53d4\u53d4\u5dee\u9063\u6211\u6765\u5411\u4f60\u5e08\u7236\u8ba8\u8fd9\u5e45\u5730\u56fe\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u4e8b\u60c5 \u4ed6\u5df2\u5411\u6211\u8bf4\u8fc7\u4e00\u6b21\uff0c\u6211\u4e0d\u7b54\u5e94\uff0c\u4ed6\u73b0\u5728\u53c8\u60f3\u5230\u501f\u7528\u4f60\u53d4\u53d4\u7684\u9762\u5b50\u4e86\u3002\u201d\u53f6\u6210\u6797\u5f80\u4e0b\u7eed\u9053\uff1a \u201c\u5730\u56fe\u5012\u5728\u5176\u6b21\uff0c\u63a8\u7ffb\u671d\u5ef7\uff0c\u5179\u4e8b\u4f53\u5927\uff0c\u6211\u53d4\u53d4\u6700\u987a\u670d\u5f20\u5927\u4fa0\uff0c\u4e5f\u60f3\u95ee\u95ee\u5f20\u5927\u4fa0\u6b64\u4e8b\u662f\u5426\u53ef \u884c\u3002\u56fa\u6b64\u5dee\u9063\u6211\u6765\u5411\u5f20\u5927\u4fa0\u95ee\u8ba1\uff0c\u5f20\u5927\u4fa0\u82e5\u8bf4\u53ef\u884c\uff0c\u518d\u7d22\u5730\u56fe\uff0c\u4e0d\u8fc7\uff0c\u770b\u76ee\u524d\u4e4b\u52bf\uff0c\u201d\u5c31\u7b97 \u6211\u53d4\u53d4\u5c1a\u6709\u72b9\u7591\u4e3e\u5175\u4e4b\u4e8b\uff0c\u6bd5\u5927\u9f99\u5934\u4e5f\u662f\u52bf\u6240\u5fc5\u884c\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u601d\u6f6e\u7eb7\u4e71\uff0c\u5bf9\u6b64\u7b49\u5927\u4e8b\uff0c\u5979\u4e5f\u5b9e\u662f\u60f3\u4e0d\u6e05\u695a\uff0c\u53ea\u662f\u5bf9\u6bd5\u64ce\u5929\u6b64\u4eba\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u603b \u662f\u611f\u5230\u4e0d\u5feb\u3002\u8fc7\u4e86\u597d\u4e45\uff0c\u5979\u5ffd\u7136\u62ac\u8d77\u5934\u6765\uff0c\u8f7b\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6709\u4e00\u4f4d\u94c1\uff0c\u94c1\u516c\u5b50\u5417\uff1f\u201d \u3000\u3000\u53f6\u6210\u6797\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u53f0\u5dde\u5fa1\u53f2\u94c1\u94bb\u7684\u513f\u5b50\u94c1\u955c\u5fc3\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u53f6\u6210\u6797 \u9053\uff1a\u201c\u6211\u5230\u53f0\u5dde\u4e4b\u65f6\uff0c\u4ed6\u8fd8\u5728\u8fd9\u513f\u3002\u89c1\u8fc7\u51e0\u9762\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u55ef\uff0c\u4ed6\u73b0\u5728\u5df2\u7ecf\u79bb\u5f00\u4e86\u90a3\u513f \u5417\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u4e0a\u4e2a\u6708\u521d\u79bb\u5f00\u7684\uff0c\u4ed6\u597d\u50cf\u548c\u6bd5\u5927\u9f99\u5934\u4e0d\u5927\u5408\u5f97\u62e2\u6765\u3002\u201d\u4e8e\u627f\u73e0\u9ed8\u7136\u4e0d \u8bed\uff0c\u53f6\u6210\u6797\u9053\uff1a\u201c\u8fd9\u4f4d\u94c1\u516c\u5b50\u5012\u662f\u6709\u70b9\u5947\u602a\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u6026\u7136\u5fc3\u8df3\uff0c\u9053\uff1a\u201c\u600e\u4e48\u5947\u602a\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u5728\u6297\u502d\u4e4b\u65f6\uff0c\u5f88\u51fa\u8fc7\u4e00\u628a \u529b\uff0c\u6211\u53d4\u53d4\u8fd8\u5f88\u770b\u91cd\u4ed6\u5462\u3002\u6211\u53d4\u53d4\u8bf4\u4ed6\u6587\u624d\u6b66\u7565\uff0c\u90fd\u5f88\u51fa\u8272\u5f53\u884c\uff0c\u8981\u7559\u4ed6\u4e0b\u6765\u6559\u4ec0\u4e48\u5b59\u5b50\u5175 \u6cd5\uff0c\u5c82\u77e5\u4ed6\u5728\u6297\u502d\u8fc7\u540e\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u751a\u662f\u9893\u5510\uff0c\u7ecf\u5e38\u662f\u72ec\u4e2a\u513f\u559d\u9152\uff0c\u53c8\u4e0d\u559c\u6b22\u4e0e\u4eba\u6765\u5f80\uff0c\u8c01 \u4e5f\u4e0d\u77e5\u9053\u4ed6\u6709\u4ec0\u4e48\u5fc3\u4e8b\u3002\u4e0a\u4e2a\u6708\u521d\uff0c\u6bd5\u64ce\u65e0\u505a\u4e86\u5341\u516b\u7701\u7684\u5927\u9f99\u5934\uff0c\u5021\u8bae\u4e3e\u5175\uff0c\u63a8\u7ffb\u660e\u5ba4\uff0c\u4ed6 \u5c31\u6084\u7136\u8d70\u4e86\u3002\u6bd5\u5927\u9f99\u5934\u72e0\u72e0\u5730\u9a82\u4e86\u4ed6\u4e00\u987f\uff0c\u8bf4\u4ed6\u662f\u5b98\u5bb6\u5b50\u5f1f\uff0c\u548c\u6211\u4eec\u5408\u4e0d\u6765\u3002\u6211\u53d4\u53d4\u5982\u662f\u60cb \u60dc\u5f97\u5f88\uff0c\u59d1\u5a18\uff0c\u4f60\u548c\u4ed6\u5f88\u719f\u6089\u4e48\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u770b\u7740\u6e56\u5149\u6f4b\u6edf\uff0c\u53c8\u4e00\u6b21\u5730\u60f3\u8d77\u4e86\u957f\u6c5f\u7684\u9a87\u6d6a\u60ca\u6d9b\uff0c\u60f3\u8d77\u4e86\u521d\u4f1a\u94c1\u955c\u5fc3\u7684\u60c5\u666f\uff0c\u60f3 \u8d77\u4e86\u677e\u6797\u4e2d\u77f3\u60ca\u6d9b\u548c\u94c1\u955c\u5fc3\u90a3\u4e00\u6155\u60b2\u5267\uff0c\u5fc3\u5934\u4e00\u7247\u6005\u60d8\uff0c\u4e45\u4e45\u59cb\u56de\u7b54\u53f6\u6210\u5c31\u7684\u8bdd\u9053\uff1a\u201c\u55ef\uff0c \u4e5f\u5e76\u4e0d\u600e\u4e48\u719f\u6089\uff0c\u968f\u4fbf\u95ee\u95ee\uff0c\u54b1\u4eec\u4e0d\u63d0\u4ed6\u4e86\u3002\u201d \u3000\u3000\u53f6\u6210\u6797\u4e5f\u662f\u4e00\u7247\u832b\u7136\uff0c\u5fc3\u9053\uff1a\u201c\u600e\u4e48\u4e00\u63d0\u8d77\u8fd9\u4e2a\u94c1\u516c\u5b50\u5979\u5c31\u90c1\u90c1\u5be1\u6b22\uff1f\u201d\u4e0d\u81ea\u7981\u5730\u8d77\u4e86 \u4e00\u79cd\u5f02\u6837\u7684\u611f\u89c9\uff0c\u968f\u5373\u60f3\u9053\uff1a\u201c\u6211\u7406\u522b\u4eba\u7684\u95f2\u4e8b\u505a\u4ec0\u4e48\uff1f\u201d\u4e00\u62ac\u5934\uff0c\u4f46\u89c1\u77f3\u9699\u95f4\u900f\u5165\u6765\u7684\u65e5 \u5f71\u6e10\u6e10\u9eef\u6de1\uff0c\u6e56\u5149\u53cd\u7167\u51fa\u665a\u971e\u7684\u4e3d\u5f69\u971e\u8f89\uff0c\u53f6\u6210\u6797\u9053\uff1a\u201c\u8d81\u7740\u5929\u8fd8\u672a\u9ed1\uff0c\u54b1\u4eec\u518d\u5230\u5404\u5904\u8d70 \u8d70\uff0c\u627e\u4e00\u4e2a\u597d\u7684\u6b47\u5bbf\u5730\u65b9\u3002\u6e56\u8fb9\u98ce\u666f\u867d\u4f73\uff0c\u5730\u65b9\u7a7a\u65f7\uff0c\u82e5\u654c\u4eba\u5077\u88ad\uff0c\u53ef\u4e0d\u6613\u9632\u5907\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9ed8\u9ed8\u65e0\u8a00\u5730\u968f\u7740\u53f6\u6210\u6797\u4ece\u6570\u5cf0\u5408\u62f1\u7684\u95e8\u6237\u8d70\u51fa\uff0c\u4e24\u4eba\u4fe1\u6b65\u6240\u4e4b\uff0c\u7a7f\u63d2\u5728\u5947\u5cf0\u5f02\u77f3 \u4e4b\u95f4\uff0c\u524d\u4eba\u8bf4\u77f3\u6797\u4e43\u201c\u5929\u5f00\u5f02\u5883\u201d\uff0c\u679c\u662f\u540d\u4e0d\u865a\u4f20\uff0c\u4f46\u89c1\u77f3\u5cf0\u5904\u5904\u76f8\u8fde\uff0c\u6784\u6210\u4e86\u5404\u79cd\u5404\u6837\u7684 \u56fe\u7b3c\u51e0\u5e73\u662f\u79fb\u6b65\u6362\u666f\uff0c\u4f73\u5999\u7eb7\u5448\uff0c\u4e8e\u627f\u73e0\u6101\u7709\u7a0d\u5c55\uff0c\u4f46\u4ecd\u662f\u63d0\u4e0d\u8d77\u5174\u81f4\u548c\u53f6\u6210\u6797\u8bf4\u8bdd\u3002\u8d70\u5230 \u4e00\u5904\uff0c\u6709\u4e00\u9053\u5c0f\u6eaa\u4ece\u4e71\u77f3\u632b\u4e2d\u6d41\u8fc7\u3002\u6c34\u58f0\u6e7f\u6e7f\uff0c\u6e05\u6f88\u89c1\u5e95\uff0c\u4e8e\u627f\u73e0\u559d\u4e86\u4e00\u53e3\u51c9\u6c34\uff0c\u53f6\u6210\u6797 \u9053\uff1a\u201c\u54c8\uff0c\u8fd8\u6709\u9c7c\u5462\uff0c\u5f85\u6211\u53bb\u6349\u5b83\u4e24\u6761\u3002\u201d\u5ffd\u89c1\u4e0a\u6e38\u6eaa\u6c34\uff0c\u6709\u4e00\u4e2a\u5c11\u5973\u7684\u5f71\u5b50\u5728\u6c34\u4e2d\u6643\u52a8\uff0c \u4e00\u62ac\u5934\u53c8\u4e0d\u89c1\u4e86\u3002\u53f6\u6210\u6797\u62fe\u8d77\u4e86\u4e00\u628a\u77f3\u5b50\uff0c\u4e00\u626c\u624b\u7528\u201c\u6ee1\u5929\u82b1\u96e8\u201d\u7684\u624b\u6cd5\u53d1\u4e86\u51fa\u53bb\uff0c\u77f3\u5b50\u7a7f \u4eba\u4e86\u77f3\u7b0b\u4e1b\u4e2d\uff0c\u53ea\u542c\u5f97\u4e00\u58f0\u60ca\u53eb\uff0c\u4e00\u4e2a\u5c11\u5973\u4ece\u4e71\u77f3\u4e4b\u95f4\u9732\u51fa\u8eab\u6765\uff0c\u53f6\u6210\u6797\u5de6\u624b\u4e00\u626c\uff0c\u4e00\u5757\u77f3 \u5b50\u98de\u53bb\uff0c\u5ffd\u542c\u5f97\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u4e8e\u627f\u73e0\u53d1\u51fa\u91d1\u82b1\u5c06\u4ed6\u7684\u77f3\u51fb\u843d\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u52a8\u624b\u3002\u201d\u58f0\u53d1 \u4eba\u5230\uff0c\u201c\u55d6\u201d\u5730\u98de\u63a0\u81f3\u90a3\u5c11\u5973\u8ddf\u524d\uff0c\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\uff0c\u4f60\u7239\u7239\u5462\uff1f\u201d\u90a3\u5c11\u5973\u5f5d\u65cf\u6253\u626e\uff0c\u60ca \u9b42\u65b9\u5b9a\uff0c\u671b\u7740\u4e8e\u627f\u73e0\uff0c\u8f7b\u8f7b\u7528\u6c49\u8bed\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u8fd8\u8ba4\u5f97\u6211\uff1f\u201d \u3000\u3000\u8fd9\u5f5d\u65cf\u59d1\u5a18\u5c31\u662f\u90a3\u65e5\u5728\u5927\u89c4\u697c\u4e0b\u770b\u5230\u7684\u90a3\u4e2a\u8868\u6f14\u541e\u5251\u7684\u5c11\u5973\uff0c\u53ea\u89c1\u5979\u56db\u9762\u5f20\u671b\uff0c\u5ffd\u5730\u4f4e \u58f0\u8bf4\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\uff0c\u6211\u5148\u5e26\u4f60\u4eec\u8d70\u51fa\u77f3\u6797\u518d\u8bf4\u5427\u3002\u201d\u4e8e\u627f\u73e0\u60ca\u559c\u4ea4\u96c6\uff0c\u9053\uff1a\u201c\u4f60\u8bc6\u5f97\u77f3\u6797 \u7684\u9053\u8def\uff1f\u201d\u90a3\u5c11\u5973\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u662f\u5728\u8fd9\u513f\u957f\u5927\u7684\uff0c\u95ed\u7740\u773c\u775b\u4e5f\u53ef\u4ee5\u8d70\u51fa\u6797\u5b50\u3002\u201d\u53f6\u6210\u6797\u8d70 \u4e86\u4e0a\u6765\uff0c\u5411\u90a3\u5c11\u5973\u4f5c\u4e86\u4e00\u63d6\uff0c\u8d54\u7f6a\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u59d1\u5a18\u662f\u8fd9\u91cc\u7684\u8d3c\u515a\u5462\u3002\u201d\u90a3\u5c11\u5973\u7b11\u9053\uff1a \u201c\u8c01\u8bf4\u4e0d\u662f\u5462\uff1f\u201d\u53f6\u6210\u6797\u5403\u4e86\u4e00\u60ca\uff0c\u90a3\u5c11\u5973\u9053\uff1a\u201c\u8981\u4e0d\u662f\u6211\u8ba4\u5f97\u4e8e\u59d1\u5a18\uff0c\u6211\u624d\u771f\u4e0d\u613f\u610f\u5192\u8fd9 \u6837\u5927\u7684\u5371\u9669\u3002\u201d\u4e8e\u627f\u73e0\u751a\u662f\u8be7\u5f02\uff0c\u53ea\u89c1\u90a3\u5c11\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u6307\u7740\u5979\u5934\u4e0a\u7684\u7389\u7c2a\uff0c\u4e8e\u627f\u73e0\u731b\u7136\u9192 \u609f\uff0c\u90a3\u65e5\u81ea\u5df1\u66fe\u8981\u628a\u7389\u7c2a\u9001\u7ed9\u5979\uff0c\u90a3\u8001\u5934\u5b50\u4e0d\u80af\u63a5\u53d7\uff0c\u4f46\u7389\u7c2a\u5df2\u7ecf\u5979\u8fc7\u76ee\uff0c\u7389\u7c2a\u4e0a\u523b\u6709\u4e8e\u5e9c \u7684\u8bb0\u53f7\uff0c\u5979\u7531\u6b64\u800c\u731c\u5230\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u8fd9\u4e5f\u4e0d\u8db3\u4e3a\u5947\u3002 \u3000\u3000\u53f6\u6210\u6797\u5ffd\u9053\uff1a\u201c\u65e2\u7136\u59d1\u5a18\u719f\u8bc6\u6797\u4e2d\u9053\u8def\uff0c\u90a3\u4e48\u6211\u4eec\u5012\u4e0d\u5fd9\u7740\u8d70\u51fa\u6797\u5b50\u4e86\u3002\u201d\u8fd9\u56de\u8f6e\u5230\u90a3 \u5f5d\u65cf\u59d1\u5a18\u9762\u89c4\u8be7\u5f02\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u8d76\u5feb\u51fa\u53bb\uff0c\u5728\u8fd9\u91cc\u5750\u4ee5\u5f85\u6bd9\u4e48\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u5c31\u70e6 \u59d1\u5a18\u5e26\u5f15\uff0c\u5f85\u6211\u4eec\u628a\u8d3c\u515a\u9010\u51fa\u77f3\u6797\u3002\u514d\u5f97\u8fd9\u540d\u5c71\u80dc\u666f\uff0c\u4e4c\u70df\u7634\u6c14\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u8fd9\u5c11\u5973 \u81ea\u8ba4\u8d3c\u515a\uff0c\u770b\u795e\u6c14\u53c8\u4e0d\u4f3c\u8bf4\u7b11\uff0c\u53f6\u6210\u6797\u600e\u7684\u5bf9\u5979\u8bf4\u8fd9\u4e2a\u8bdd\uff1f\u201d \u3000\u3000\u90a3\u5f5d\u65cf\u5c11\u5973\u671b\u4e86\u53f6\u6210\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u5c31\u4e24\u4e2a\u4eba\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a \u201c\u8d3c\u4eba\u8bf4\u591a\u4e0d\u591a\uff0c\u4e5f\u6709\u4e00\u4e8c\u767e\u4eba\uff0c\u8fd8\u6765\u4e86\u4e9b\u4ec0\u4e48\u4eac\u57ce\u7684\u4f8d\u536b\uff0c\u4f60\u4eec\u4e24\u4eba\u6210\u5417\uff1f\u201d\u4e8e\u627f\u73e0\u4e00\u542c \u5c11\u5973\u8fd9\u4e2a\u8bed\u6c14\uff0c\u559c\u9053\uff1a\u201c\u6211\u65e9\u77e5\u9053\u59d1\u5a18\u4e0d\u662f\u574f\u4eba\uff0c\u4f46\u6c42\u59d1\u5a18\u5e26\u8def\uff0c\u4ee5\u540e\u7684\u4e8b\uff0c\u59d1\u5a18\u4f60\u4e0d\u5fc5 \u7ba1\u3002\u201d \u3000\u3000\u90a3\u5f5d\u65cf\u5c11\u5973\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\uff0c\u5f20\u5927\u4fa0\u53ea\u6015\u8981\u7ba1\u3002\u201d\u4e8e\u627f\u73e0\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u54ea\u4f4d\u5f20\u5927 \u4fa0\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u5929\u4e0b\u9664\u4e86\u4f60\u7684\u5e08\u7236\uff0c\u8fd8\u6709\u54ea\u4f4d\u914d\u79f0\u5f20\u5927\u4fa0\uff1f\u201d\u4e8e\u627f\u73e0\u5982\u5760\u4e94\u91cc\u96fe\u4e2d\uff0c \u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u5fc3\u9053\uff1a\u201c\u6211\u5e08\u7236\u672c\u9886\u518d\u5927\uff0c\u4ed6\u53c8\u5c82\u6709\u5148\u77e5\u4e4b\u660e\uff1f\u96be\u9053\u4ed6\u9884\u5148\u77e5\u9053\u6211 \u4eec\u4f1a\u9677\u8eab\u6b64\u5730\uff1f\u201d\u90a3\u5c11\u5973\u4f3c\u662f\u731c\u5230\u4e86\u4e8e\u627f\u73e0\u7684\u5fc3\u610f\uff0c\u7b11\u9053\uff1a\u201c\u5f20\u5927\u4fa0\u5dee\u9063\u6211\u7236\u5973\u5230\u8fd9\u513f\u6765\uff0c \u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u9047\u5230\u4e8e\u59d1\u5a18\uff0c\u771f\u662f\u51d1\u5de7\u6781\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u5fd9\u9053\uff1a\u201c\u597d\u59d0\u59d0\uff0c\u4f60\u5feb\u7ed9\u6211\u8bf4\u8fd9\u662f\u600e\u4e48 \u56de\u4e8b\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u8fd9\u91cc\u8d3c\u515a\u6709\u4e00\u5927\u534a\u662f\u5f5d\u4eba\uff0c\u526f\u9996\u9886\u4e5f\u662f\u5f5d\u4eba\uff0c\u540d\u53eb\u6717\u82f1\u3002\u5927\u5934\u76ee\u5374\u662f\u4ee5\u524d \u6ec7\u897f\u9053\u4e0a\u4e00\u4e2a\u540d\u5524\u675c\u5e4c\u7684\u72ec\u811a\u5927\u76d7\uff0c\u4ed6\u770b\u4e2d\u4e86\u77f3\u6797\u7684\u5f62\u52bf\uff0c\u5c31\u9080\u6717\u82f1\u5408\u4f19\uff0c\u5360\u636e\u77f3\u6797\u505a\u5de2 \u7a74\u3002\u6717\u82f1\u5728\u5f5d\u65cf\u4e2d\u7b97\u5f97\u662f\u4e2a\u8c6a\u6770\uff0c\u53ea\u56e0\u5b98\u5e9c\u82db\u6350\u91cd\u7a0e\uff0c\u773c\u89c1\u65cf\u4eba\u88ab\u538b\u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\u3002\u56e0\u6b64\u7ade \u7ed9\u675c\u5e4c\u8bf4\u52a8\uff0c\u7ea0\u96c6\u4e86\u4e00\u4e8c\u767e\u65e0\u4ee5\u4e3a\u751f\u7684\u5f5d\u65cf\u5c11\u5e74\uff0c\u8ddf\u675c\u5e4c\u5408\u4f19\u3002\u6b63\u56e0\u4e3a\u6717\u82f1\u505a\u4e86\u526f\u9996\u9886\uff0c\u6240 \u4ee5\u4ed6\u4eec\u4ece\u4e0d\u6253\u52ab\u9644\u8fd1\u7684\u5f5d\u4eba\u3002\u201d\u4e8e\u627f\u73e0\u70b9\u4e86\u70b9\u5934\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u9644\u8fd1\u7684\u519c\u4eba\u5e76\u65e0\u60ca\u6270\u3002\u5c55 \u505c\u4e3b\u4eba\u4e0d\u80af\u5e26\u8def\uff0c\u6562\u60c5\u4e5f\u662f\u522b\u6709\u539f\u56e0\uff1f\u201d\u90a3\u5c11\u5973\u7ee7\u7eed\u9053\uff1a\u201c\u675c\u5e4c\u4e5f\u7ea0\u96c6\u4e86\u4e00\u4e9b\u515a\u7fbd\u6765\uff0c\u4ed6\u4eec \u4eba\u5c11\uff0c\u4f46\u672c\u9886\u5374\u6bd4\u90ce\u82f1\u5927\uff0c\u675c\u5e4c\u5927\u6743\u72ec\u63fd\uff0c\u8fd1\u5e74\u4e0d\u4f46\u52ab\u593a\u8d22\u5b9d\uff0c\u8fd8\u6740\u5bb3\u5ba2\u5546\uff0c\u5f04\u5f97\u5f5d\u4eba\u4e5f\u4e0d \u6562\u63a5\u8fd1\u4ed6\u4eec\uff0c\u77f3\u6797\u4e5f\u6210\u4e86\u7981\u5730\uff0c\u6717\u82f1\u6781\u4e3a\u4e0d\u6ee1\uff0c\u4f46\u5374\u65e0\u53ef\u5982\u4f55\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u60f3\u4e0d\u5230\u4e00\u4e2a\u5c0f\u5c0f\u7684\u532a\u5e2e\uff0c\u5185\u60c5\u4e5f\u8fd9\u822c\u590d\u6742\u3002\u53ea\u542c\u5f97\u90a3\u5f5d\u65cf\u59d1\u5a18\u5f80\u4e0b\u7eed\u9053\uff1a\u201c\u6211\u4eec \u7236\u5973\u672c\u6765\u662f\u77f3\u6797\u9644\u8fd1\u7684\u4eba\uff0c\u540e\u6765\u642c\u5230\u5927\u7406\u53bb\u7684\u3002\u4f4f\u5728\u82cd\u5c71\u811a\u4e0b\uff0c\u542c\u8bf4\u82cd\u5c71\u4e0a\u6709\u51e0\u4f4d\u9690\u58eb\u4fee \u884c\uff0c\u9644\u8fd1\u7684\u5c45\u6c11\u628a\u4ed6\u4eec\u5f53\u4f5c\u6d3b\u795e\u4ed9\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u8fd9\u5fc5\u5b9a\u662f\u6211\u7684\u5e08\u7956\u7384\u673a\u9038\u58eb\u548c\u4e0a\u5b98\u5929 \u91ce\u4ee5\u53ca\u8427\u8001\u592a\u5a46\u8fd9\u4e09\u4e2a\u4eba\u4e86\u3002\u201d\u95ee\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u4ed6\u4eec\u5417\uff1f\u201d\u90a3\u5f5d\u65cf\u59d1\u5a18\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u4eec\u4f4f\u5728 \u82cd\u5c71\u7edd\u9876\u7684\u4e91\u5f04\u5cf0\uff0c\u7ec8\u5e74\u4e91\u96fe\u7b3c\u7f69\uff0c\u7b49\u95f2\u4eba\u54ea\u80fd\u4e0a\u53bb\uff1f\u5c31\u662f\u4e0a\u53bb\u4e86\uff0c\u90a3\u51e0\u4f4d\u2018\u8001\u795e\u4ed9\u2019\u4e5f\u4e0d \u80af\u89c1\u5916\u4eba\u3002\u4e0d\u8fc7\u6709\u4e00\u4f4d\u59d3\u4e4c\u7684\u5927\u7237\uff0c\u636e\u8bf4\u662f\u5176\u4e2d\u4e00\u4f4d\u8001\u795e\u4ed9\u7684\u5f1f\u5b50\uff0c\u4ed6\u5012\u65f6\u5e38\u4e0b\u5c71\u91c7\u8d2d\u6742 \u7269\uff0c\u5e76\u4e14\u884c\u533b\u6551\u4eba\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u4f4d\u4e4c\u5927\u7237\u662f\u4e0d\u662f\u53eb\u505a\u4e4c\u8499\u592b\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4e0d\u9519\uff0c \u4e4c\u5927\u7237\u7684\u540d\u8bb3\uff0c\u8fd8\u662f\u524d\u5e74\u6211\u4eec\u624d\u77e5\u9053\u7684\u3002\u6211\u4eec\u5728\u82cd\u5c71\u811a\u4e0b\u79cd\u6709\u83dc\u56ed\uff0c\u4e4c\u5927\u7237\u6bcf\u6b21\u4e0b\u5c71\u90fd\u5411\u6211 \u4eec\u4e70\u83dc\uff0c\u540e\u6765\u719f\u4e86\uff0c\u4e5f\u5e38\u5728\u6211\u4eec\u8fd9\u91cc\u6b47\u811a\u3002\u6211\u7239\u7239\u77e5\u9053\u4ed6\u662f\u4e2a\u5927\u6709\u672c\u9886\u7684\u4eba\uff0c\u4fbf\u6c42\u4ed6\u6536\u6211\u505a \u5f1f\u5b50\u3002\u53ef\u60dc\u4e4c\u5927\u7237\u4e0d\u7b54\u5141\uff0c\u8bf4\u662f\u4ed6\u5e08\u7236\u5c1a\u5728\uff0c\u4ed6\u4e0d\u80af\u6ee5\u6536\u95e8\u5f92\u3002\u4e0d\u8fc7\u95f2\u5e38\u4e5f\u4f20\u6388\u6211\u4eec\u7236\u5973\u51e0 \u8def\u9632\u8eab\u7684\u62f3\u811a\uff0c\u53ea\u662f\u4e0d\u5141\u4ee5\u5e08\u5f92\u76f8\u79f0\u3002\u90a3\u541e\u5251\u7684\u529f\u592b\uff0c\u5c31\u662f\u4e4c\u5927\u7237\u4e00\u65f6\u9ad8\u5174\uff0c\u6559\u7ed9\u6211\u4eec \u7684\u3002\u201d\u4e4c\u8499\u592b\u662f\u4e0a\u5b98\u5929\u91ce\u7684\u7b2c\u4e8c\u4e2a\u5f1f\u5b50\uff0c\u5728\u5e08\u95e8\u7684\u65e5\u5b50\u6700\u957f\uff0c\u6bd4\u5927\u5f1f\u5b50\u6fb9\u53f0\u706d\u660e\u6240\u5f97\u7684\u4f20\u6388 \u66f4\u591a\uff0c\u4e0d\u8fc7\u90a3\u541e\u5251\u7684\u529f\u592b\uff0c\u5e76\u975e\u4e0a\u5b98\u5929\u91ce\u6240\u6388\uff0c\u4e4c\u8499\u592b\u4e0e\u9ed1\u767d\u6469\u8bc3\u4ea4\u60c5\u751a\u597d\uff0c\u90a3\u541e\u5251\u7684\u529f\u592b \u4e43\u662f\u4e4c\u8499\u592b\u89c1\u7740\u597d\u800d\u5374\u5411\u9ed1\u767d\u6469\u8bc3\u5b66\u6765\u7684\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u4eec\u65e2\u7136\u5728\u82cd\u5c71\u811a\u4e0b\u5b89\u5c45\u4e50\u4e1a\uff0c\u600e\u7684\u53c8\u56de\u5230\u8fd9\u77f3\u6797\u6765\uff1f\u201d\u90a3\u59d1\u5a18\u9053\uff1a\u201c\u5c31 \u662f\u5949\u4f60\u5e08\u7236\u7684\u5dee\u9063\u5440\u3002\u4eca\u5e74\u6625\u5929\uff0c\u5f20\u5927\u4fa0\u5230\u4e86\u82cd\u5c71\uff0c\u548c\u6211\u4eec\u4e5f\u5f88\u719f\u6089\u3002\u5f20\u5927\u4fa0\u559c\u6b22\u5230\u5904\u8d70 \u52a8\uff0c\u6bb5\u738b\u7237\u4e5f\u65f6\u5e38\u8bf7\u4ed6\u8fdb\u5bab\u3002\u201d\u6bb5\u5bb6\u5728\u5143\u671d\u4ee5\u524d\uff0c\u5728\u5927\u7406\u4e16\u4ee3\u4e3a\u738b\uff0c\u867d\u7136\u73b0\u5728\u53ea\u88ab\u671d\u5ef7\u5c01\u4e3a \u201c\u77e5\u5e73\u7ae0\u4e8b\u201d\uff0c\u8001\u767e\u59d3\u53eb\u60ef\u4e86\uff0c\u4ecd\u79f0\u4ed6\u4eec\u4e3a\u201c\u738b\u7237\u201d\u3002\u90a3\u5c11\u5973\u7eed\u9053\uff1a\u201c\u6700\u8fd1\u6bb5\u738b\u7237\u60f3\u81ea\u7acb\u4e3a \u738b\uff0c\u4e91\u5357\u5404\u65cf\u90fd\u62e5\u62a4\u4ed6\uff0c\u597d\u4e0e\u5b98\u5e9c\u5bf9\u6297\u3002\u4fbf\u60f3\u8d77\u4e86\u77f3\u6797\u5f5d\u65cf\u7684\u8c6a\u6770\u6717\u82f1\uff0c\u6253\u542c\u4e4b\u4e0b\uff0c\u77e5\u9053\u4ed6 \u5728\u77f3\u6797\u4e3a\u5bc7\uff0c\u6781\u89c9\u53ef\u60dc\uff0c\u5f20\u5927\u4fa0\u732e\u8ba1\uff0c\u5c06\u4ed6\u4eec\u62db\u5230\u5927\u7406\u6765\u3002\u56e0\u4e3a\u6211\u4eec\u7236\u5973\u672c\u662f\u77f3\u6797\u7684\u5f5d\u4eba\uff0c \u5f20\u5927\u4fa0\u4fbf\u4fdd\u6211\u4eec\u6765\u529e\u8fd9\u4ef6\u5dee\u4e8b\u3002\u5f20\u5927\u4fa0\u53eb\u6211\u4eec\u5148\u5230\u6606\u660e\u548c\u5c0f\u516c\u7239\u63a5\u5934\uff0c\u63a2\u542c\u6d88\u606f\uff0c\u7136\u540e\u518d\u5230 \u77f3\u6797\u3002\u201d\u4e8e\u627f\u73e0\u604d\u7136\u5927\u609f\uff0c\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6c90\u5c0f\u59d0\u77e5\u9053\u6211\u4eec\u7684\u4f4f\u5740\uff0c\u60f3\u5fc5\u662f\u90a3\u65e5\u88ab\u4f60\u4eec\u770b\u7834\u884c \u85cf\uff0c\u544a\u8bc9\u6c90\u5c0f\u59d0\u7684\u3002\u201d\u90a3\u5f5d\u65cf\u5c11\u5973\u5fae\u7b11\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8bf7\u6055\u6211\u4eec\u6697\u5730\u8ddf\u8e2a\u4e4b\u7f6a\u3002\u201d \u3000\u3000\u90a3\u5f5d\u65cf\u5c11\u5973\u6b47\u4e86\u4e00\u6b47\uff0c\u5f80\u4e0b\u7eed\u9053\uff1a\u201c\u6211\u6709\u4e00\u4e2a\u8868\u54e5\uff0c\u5c31\u662f\u6717\u82f1\u624b\u4e0b\u7684\u4e00\u4e2a\u5c0f\u5934\u76ee\uff0c\u6211\u4eec \u5230\u8fd9\u91cc\u6765\u5df2\u6709\u4e24\u4e09\u5929\u4e86\uff0c\u8fd8\u6ca1\u6709\u673a\u4f1a\u5f97\u89c1\u6717\u82f1\u9762\u8c08\u3002\u6211\u7684\u8868\u54e5\u8bf4\uff0c\u6717\u82f1\u88ab\u675c\u5e4c\u631f\u6301\uff0c\u53ea\u6015\u4e0d \u80fd\u4f5c\u4e3b\u3002\u524d\u65e5\u6765\u4e86\u51e0\u4e2a\u4eac\u57ce\u4f8d\u536b\uff0c\u5176\u4e2d\u4e00\u4eba\u540d\u53eb\u97e9\u5c55\u7684\u548c\u675c\u5e4c\u4ee5\u524d\u662f\u516b\u62dc\u4e4b\u4ea4\uff0c\u6b63\u5728\u6e38\u8bf4\u793e \u5e4c\u505a\u4ed6\u4eec\u5728\u4e91\u5357\u7684\u8033\u76ee\uff0c\u6211\u7684\u8868\u54e5\u66f4\u4e0d\u6562\u5411\u6717\u82f1\u8bf4\u4e86\u3002\u8fd9\u6b21\u5b9a\u8ba1\u8bf1\u4f60\u4eec\u8fdb\u77f3\u6797\u7684\u4fbf\u662f\u97e9\u5c55\u592b \u5987\u548c\u675c\u5e4c\u7684\u5408\u8c0b\u3002\u542c\u8bf4\u8fd9\u6b21\u6765\u7684\u51e0\u4f4d\u4f8d\u536b\uff0c\u90fd\u662f\u9ad8\u624b\uff0c\u4e3a\u7740\u7684\u90a3\u4e2a\u674e\u6db5\u771f\u66f4\u662f\u5389\u5bb3\u3002\u201d\u4e8e\u627f \u73e0\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u4e0d\u8fc7\u5982\u6b64\uff01\u201d\u7a81\u7136\u60f3\u8d77\u4e00\u4e8b\uff0c\u5374\u76b1\u4e86\u7709\u5934\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u597d\u6c49\u4e0d\u654c\u4eba\u591a\uff0c\u4e8e\u59d1\u5a18\u72af\u4e0d\u7740\u4ee5\u5343\u91d1\u4e4b\u4f53\uff0c\u5192\u6b64\u5de8\u9669\u3002\u201d\u5979\u53ea\u9053\u662f\u4e8e\u627f\u73e0 \u5fc3\u751f\u602f\u610f\uff0c\u5374\u53c8\u56e0\u5148\u524d\u7684\u8bdd\u8bf4\u5f97\u592a\u6ee1\uff0c\u4e0d\u4fbf\u8f6c\u53e3\uff0c\u6545\u6b64\u76b1\u7709\u3002\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u90a3\u513f\u4e2a\u4f8d\u536b\u4e5f \u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u51ed\u6211\u548c\u53f6\u5927\u54e5\u8fd8\u5bf9\u4ed8\u5f97\u4e86\u3002\u53ea\u662f\u52a8\u8d77\u624b\u6765\uff0c\u53ea\u6015\u4f1a\u8bef\u4f24\u4e86\u4f60\u4eec\u7684\u65cf\u4eba\u3002\u201d\u90a3\u5f5d \u65cf\u5c11\u5973\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\u65e2\u6709\u628a\u63e1\uff0c\u90a3\u4e48\u6211\u7684\u5dee\u4e8b\u5c31\u8bf7\u4f60\u4ee3\u52b3\u4e86\u3002\u201d\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00 \u9762\u5c0f\u65d7\uff0c\u65d7\u4e0a\u7ee3\u6709\u4e24\u5934\u72ee\u5b50\uff0c\u9012\u7ed9\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u662f\u6bb5\u738b\u7237\u7684\u738b\u65d7\uff0c\u4e91\u5357\u5404\u65cf\uff0c\u65e0\u4e0d\u8ba4\u5f97\uff0c \u4e8e\u59d1\u5a18\u82e5\u80fd\u5c06\u90a3\u51e0\u4e2a\u5f85\u536b\u548c\u675c\u5e4c\u4e00\u9f50\u6253\u8d25\uff0c\u51ed\u738b\u65d7\u4f5c\u4fe1\u7269\uff0c\u62db\u964d\u6717\u82f1\uff0c\u90a3\u5c31\u6613\u529e\u5f97\u591a\u4e86\u3002\u201d \u8fd9\u6b63\u662f\u4e00\u4e3e\u4e24\u5f97\u4e4b\u8ba1\uff0c\u4e8e\u627f\u73e0\u5927\u559c\uff0c\u63a5\u8fc7\u738b\u65d7\uff0c\u9053\uff1a\u201c\u597d\uff0c\u8bf7\u4f60\u7acb\u5373\u5e26\u8def\u3002\u201d \u3000\u3000\u532a\u515a\u7684\u5de2\u7a74\u5728\u77f3\u6797\u5185\u7684\u5927\u91d1\u5cad\u4e0a\uff0c\u6797\u5185\u7684\u77f3\u5cf0\u90fd\u4e0d\u5f88\u9ad8\uff0c\u53ea\u6709\u8fd9\u5927\u91d1\u5cad\u9ad8\u8fbe\u767e\u4e08\uff0c\u5c71\u52bf \u4ea6\u6700\u4e3a\u5d0e\u5c96\uff0c\u5c71\u5cad\u5468\u56f4\uff0c\u8bf8\u5cf0\u62f1\u7ed5\uff0c\u4fe8\u82e5\u8ff7\u5bab\u3002\u90a3\u5f5d\u65cf\u59d1\u5a18\u5e26\u9886\u4e8e\u627f\u73e0\u3001\u53f6\u6210\u6797\u4e8c\u4eba\uff0c\u4e0a\u9ad8 \u4e0b\u4f4e\uff0c\u7a7f\u8fc7\u5947\u5ca9\u524a\u58c1\u8fc2\u56de\u66f2\u6298\u7684\u901a\u9053\uff0c\u4ece\u5982\u5251\u5982\u88c1\u7684\u77f3\u5cf0\u4e2d\u7a7f\u63d2\u800c\u8fc7\uff0c\u8d8a\u4e0a\u8d8a\u9ad8\uff0c\u90a3\u4e9b\u77f3 \u5cf0\uff0c\u5cf0\u5cf0\u76f8\u8fde\uff0c\u5728\u8bb8\u591a\u77f3\u5cfb\u4e4b\u95f4\uff0c\u4e2d\u6a2a\u602a\u77f3\uff0c\u72b6\u5982\u5929\u6865\uff0c\u82e5\u975e\u4e8e\u3001\u53f6\u4e8c\u4eba\u90fd\u662f\u8f7b\u529f\u7edd\u9876\uff0c\u5728 \u77f3\u5cf0\u4e4b\u4e0a\u884c\u8d70\uff0c\u6015\u4e0d\u4e24\u817f\u9178\u8f6f\uff0c\u5bf8\u6b65\u96be\u79fb\uff1f\u6b64\u65f6\u5df2\u662f\u65e5\u843d\u9ec4\u660f\uff0c\u5728\u77f3\u5cf0\u9ad8\u5904\u4e00\u671b\uff0c\u4f46\u89c1\u4e07\u97f5 \u671d\u5929\uff0c\u5343\u5ca9\u7ade\u79c0\uff0c\u5728\u5915\u9633\u6b8b\u7167\u4e0b\u66f4\u663e\u5f97\u9759\u7a46\u5e84\u4e25\uff0c\u604d\u4f3c\u795e\u4ed9\u5883\u754c\u3002\u4e8e\u627f\u73e0\u53f9\u9053\uff1a\u201c\u5982\u6b64\u6d1e\u5929 \u798f\u5730\uff0c\u54ea\u5bb9\u5c11\u6570\u532a\u5f92\u76d8\u636e\uff0c\u5373\u7b97\u4e0d\u662f\u66ff\u6bb5\u738b\u7237\u529e\u4e8b\uff0c\u6211\u4eec\u4e5f\u8be5\u628a\u8fd9\u4e9b\u532a\u5f92\u9a71\u9010\u51fa\u53bb\u3002\u201d \u3000\u3000\u8fd9\u5f5d\u65cf\u59d1\u5a18\u81ea\u5c0f\u5728\u77f3\u6797\u5185\u73a9\u800d\uff0c\u9053\u8def\u6781\u719f\uff0c\u5e26\u9886\u4ed6\u4eec\u4ece\u79d8\u9053\u8fdb\u5165\u5927\u91d1\u5cad\u5185\uff0c\u7adf\u65e0\u4eba\u77e5 \u6653\uff0c\u5230\u4e86\u5cad\u811a\uff0c\u5929\u8272\u5df2\u9ed1\u3002\u4f46\u89c1\u5c71\u5761\u95f4\u9ed1\u5f71\u5e62\u5e62\uff0c\u5cad\u4e0a\u5927\u5be8\u7684\u706b\u5149\u9690\u7ea6\u53ef\u89c1\u3002\u90a3\u5f5d\u65cf\u59d1\u5a18\u6015 \u78b0\u89c1\u5de1\u5c71\u7684\u4eba\uff0c\u5bf9\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ece\u8fd9\u91cc\u76f4\u4e0a\uff0c\u7ecf\u8fc7\u4e09\u5ea7\u77f3\u5cf0\uff0c\u4fbf\u662f\u5927\u5be8\u3002\u4e8e\u59d1\u5a18\uff0c\u795d\u4f60\u9a6c\u5230 \u6210\u529f\uff0c\u5f85\u4f60\u7834\u5be8\u4e4b\u540e\uff0c\u54b1\u4eec\u518d\u89c1\u3002\u201d\u6084\u6084\u6e9c\u5f00\uff0c\u4ece\u7b2c\u4e8c\u6761\u8def\u6df7\u5165\u540e\u5be8\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5750\u4e0b\u6765\u7a0d\u4e3a\u6b47\u606f\uff0c\u5e76\u4e0e\u53f6\u6210\u6797\u5546\u8bae\uff0c\u4f9d\u4e8e\u627f\u73e0\u4e4b\u610f\uff0c\u4fbf\u8981\u76f4\u95ef\u5165\u5be8\u4e2d\uff0c\u6740\u4ed6\u4e2a\u843d \u82b1\u6d41\u6c34\u3002\u53f6\u6210\u6797\u7b11\u9053\uff1a\u201c\u5be8\u4e2d\u867d\u65e0\u4e00\u6d41\u9ad8\u624b\uff0c\u4f46\u54b1\u4eec\u4eba\u5c11\uff0c\u4ed6\u4eec\u4eba\u591a\uff0c\u5012\u4e5f\u4e0d\u53ef\u4e0d\u9632\u3002\u4e0d\u5982 \u6211\u4e0e\u4f60\u5206\u4e3a\u4e24\u8def\uff0c\u4f60\u5728\u524d\u5be8\u5f15\u4f4f\u90a3\u4e9b\u4f8d\u536b\uff0c\u6211\u653e\u706b\u70e7\u4ed6\u7684\u540e\u5be8\uff0c\u8ba9\u4ed6\u4e0d\u77e5\u9053\u6211\u4eec\u7684\u865a\u5b9e\uff0c\u4e5f \u7edd\u4e86\u6717\u82f1\u76d8\u636e\u4e4b\u5fc3\uff0c\u4fbf\u4e8e\u62db\u964d\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u770b\u4ed6\u4e0d\u51fa\uff0c\u8bf4\u6765\u7adf\u662f\u6df1\u5408\u5175\u6cd5\uff0c\u4f3c\u4e4e\u6bd4\u94c1 \u955c\u5fc3\u7684\u5938\u5938\u5176\u8c08\u8981\u5b9e\u9645\u5f97\u591a\u3002\u201d \u3000\u3000\u8ba1\u8bae\u65e2\u5b9a\u4e24\u4eba\u5206\u8def\u4e0a\u5c71\uff0c\u4e8e\u627f\u73e0\u5c55\u5f00\u8f7b\u529f\uff0c\u7aef\u7684\u662f\u6377\u5982\u98de\u9e1f\uff0c\u63a0\u8fc7\u7b2c\u4e00\u5ea7\u77f3\u5cf0\uff0c\u54e8\u5175\u7ade \u65e0\u77e5\u89c9\uff0c\u4e8e\u627f\u73e0\u6709\u4e9b\u8f7b\u654c\uff0c\u63a5\u7740\u4e0a\u7b2c\u4e8c\u5ea7\u77f3\u5cf0\uff0c\u4ece\u54e8\u5c97\u6570\u4e08\u4e4b\u5730\u63a0\u8fc7\uff0c\u5ffd\u542c\u5f97\u201c\u55d6\u201d\u7684\u4e00 \u58f0\uff0c\u5229\u7bad\u7a7f\u7a7a\uff0c\u75be\u7684\u5c04\u5230\uff0c\u542c\u98ce\u8fa8\u5668\uff0c\u529b\u9053\u9887\u4e3a\u5f3a\u52b2\uff0c\u4e8e\u627f\u73e0\u6025\u5fd9\u95ea\u5f00\uff0c\u90a3\u4eba\u521a\u521a\u51fa\u58f0\uff0c\u4fbf \u88ab\u4e8e\u627f\u73e0\u4e00\u6735\u91d1\u82b1\u5c01\u95ed\u4e86\u7a74\u9053\uff0c\u56de\u5934\u770b\u90a3\u5229\u7bad\uff0c\u7adf\u5c04\u5165\u4e86\u4e00\u5757\u5927\u77f3\uff0c\u867d\u975e\u4e00\u6d41\u9ad8\u624b\uff0c\u4ea6\u8db3\u60ca \u4eba\uff0c\u4e8e\u627f\u73e0\u5012\u4e0d\u6562\u592a\u5927\u610f\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5c06\u90a3\u54e8\u5175\u7684\u53f7\u8863\u5265\u4e0b\uff0c\u62ab\u5728\u8eab\u4e0a\uff0c\u63a5\u7740\u6500\u767b\u7b2c\u4e09\u5ea7\u77f3\u5cf0\uff0c\u591c\u8272\u82cd\u832b\uff0c\u53ea\u89c1\u4e24\u6761\u4eba \u5f71\u7a9c\u4e86\u8fc7\u6765\uff0c\u626c\u58f0\u95ee\u9053\uff1a\u201c\u5468\u5927\u54e5\uff0c\u4f60\u600e\u4e48\u4e0d\u5728\u4e0b\u9762\u628a\u5b88\uff1f\u201d\u4ee5\u4e8e\u627f\u73e0\u7684\u8f7b\u529f\u672c\u9886\uff0c\u4e5f\u88ab\u6765 \u4eba\u542c\u51fa\u58f0\u606f\uff0c\u53ef\u89c1\u4ea6\u975e\u5eb8\u624b\u3002\u8fd9\u56de\u4e8e\u627f\u73e0\u65e9\u6709\u51c6\u5907\uff0c\u98de\u8eab\u4e00\u63a0\uff0c\u91d1\u82b1\u7acb\u523b\u51fa\u624b\uff0c\u90a3\u4e24\u4eba\u521a\u521a \u53d1\u89c9\u4e0d\u662f\u201c\u5468\u5927\u54e5\u201d\uff0c\u5df2\u88ab\u91d1\u82b1\u6253\u4e2d\u7a74\u9053\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002\u539f\u6765\u5728\u7b2c\u4e8c\u7b2c\u4e09\u9675\u77f3\u5cf0\u628a\u5b88\u7684\u4eba\uff0c\u90fd \u662f\u675c\u5e4c\u7684\u5f97\u529b\u52a9\u624b\uff0c\u672c\u9886\u81ea\u6bd4\u4e00\u822c\u5c0f\u5934\u76ee\u9ad8\u5f3a\u5f97\u591a\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u86c7\u884c\u5154\u8d70\uff0c\u6084\u6084\u6478\u8fd1\u5927\u5be8\uff0c\u5979\u8eab\u4e0a\u62ab\u7740\u7682\u8863\uff0c\u591c\u8272\u6726\u80e7\u4e2d\uff0c\u503c\u591c\u7684\u5934\u76ee\u7edd\u5bf9\u6599\u4e0d \u5230\u654c\u4eba\u80fd\u6df1\u5165\u77f3\u6797\uff0c\u5e76\u8d8a\u8fc7\u4e09\u5ea7\u77f3\u5cf0\uff0c\u867d\u6709\u4e8c\u4eba\u542c\u51fa\u58f0\u606f\uff0c\u4e5f\u4ee5\u4e3a\u5979\u662f\u540c\u4f34\u3002\u4e8e\u627f\u73e0\u6478\u8fd1\u5927 \u5be8\u3001\u53ea\u542c\u5f97\u91cc\u9762\u731c\u62f3\u547c\u5578\uff0c\u95f9\u6210\u4e00\u7247\uff0c\u4e8e\u627f\u73e0\u5fc3\u4e2d\u51b7\u7b11\uff1a\u6562\u60c5\u4ed6\u4eec\u662f\u5f00\u201c\u5e86\u529f\u5bb4\u201d\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u731c\u5f97\u4e0d\u5dee\uff0c\u4ed6\u4eec\u679c\u7136\u662f\u5f00\u5e86\u529f\u5bb4\uff0c\u53ea\u542c\u5f97\u674e\u6db5\u771f\u90a3\u82cd\u8001\u7684\u58f0\u97f3\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u97e9 \u5ac2\uff0c\u8fd9\u56de\u8bbe\u8ba1\u64d2\u654c\uff0c\u4f60\u7684\u529f\u52b3\u6700\u5927\u3002\u97e9\u4e8c\u54e5\uff0c\u4f60\u53d7\u4e86\u70b9\u4f24\uff0c\u4e5f\u503c\u5f97\u4e86\u3002\u201d\u63a5\u7740\u4e00\u4e2a\u5987\u4eba\u5996\u91cc \u5996\u6c14\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8001\u7237\u5b50\u8fc7\u5956\u5566\uff0c\u6211\u53ef\u4e0d\u6562\u8d2a\u529f\u3002\u8bf4\u5b9e\u8bdd\uff0c\u8fd9\u56de\u7684\u529f\u52b3\uff0c\u5e94\u6570\u675c\u5be8\u4e3b\u6700 \u5927\uff0c\u8981\u4e0d\u662f\u4ed6\u501f\u77f3\u6797\u7ed9\u6211\u4eec\uff0c\u8fd9\u4e24\u4e2a\u70b9\u5b50\u53ef\u4e0d\u5bb9\u6613\u5bf9\u4ed8\u3002\u201d\u674e\u6db5\u771f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5927\u5bb6\u90fd\u6709\u529f \u52b3\uff0c\u5927\u5bb6\u90fd\u6709\u529f\u52b3\uff01\u9633\u603b\u7ba1\u5df2\u5230\u6606\u660e\u6765\u4e86\u3002\u54b1\u4eec\u53ef\u4ee5\u5c06\u70b9\u5b50\u89e3\u53bb\u6606\u660e\uff0c\u7701\u53bb\u591a\u5c11\u9ebb\u70e6\uff0c\u8fd8\u53ef \u4ee5\u5c31\u8fd1\u8bf7\u529f\u9886\u8d4f\uff0c\u675c\u5be8\u4e3b\uff0c\u4f60\u82e5\u662f\u6b22\u559c\u7684\u8bdd\uff0c\u5c31\u8bf7\u9633\u603b\u7ba1\u5bf9\u6c90\u56fd\u516c\u8bf4\u8bf4\uff0c\u518d\u8bf7\u51c6\u671d\u5ef7\u5c01\u4f60\u505a \u8fd9\u91cc\u7684\u571f\u738b\uff0c\u54c8\u54c8\uff0c\u90a3\u65f6\u4f60\u5c31\u540d\u6b63\u8a00\u987a\uff0c\u4e0d\u5fc5\u518d\u5c40\u4fc3\u5728\u8fd9\u77f3\u6797\u91cc\u9762\u505a\u5c71\u5927\u738b\u5566\uff01\u201d\u675c\u5e4c\u7c97\u58f0 \u7c97\u6c14\u5730\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u671b\u4ec0\u4e48\u5c01\u8d4f\u3002\u5582\uff0c\u90a3\u59d3\u4e8e\u7684\u5c0f\u59d1\u5a18\u8d4f\u7ed9\u6211\u884c\u4e0d\u884c\uff1f\u201d\u674e\u6db5\u771f\u5927\u7b11\u9053\uff1a \u201c\u4f60\u77e5\u9053\u5979\u662f\u4f55\u4eba\uff1f\u5979\u662f\u4e8e\u8c26\u7684\u5973\u513f\uff0c\u4e5f\u662f\u7687\u4e0a\u6240\u8981\u7684\u53db\u9006\u4e4b\u5973\uff0c\u4f60\u600e\u80fd\u8981\u5979\uff1f\u201d\u675c\u5e4c\u5931\u58f0 \u53eb\u9053\uff1a\u201c\u4e8e\u9601\u8001\u4e8e\u8c26\u4e4b\u5973\uff1f\u554a\uff0c\u8be5\u6b7b\uff0c\u8be5\u6b7b\uff0c\u65e9\u77e5\u662f\u5979\uff0c\u6211\u5c82\u6562\u52a8\u8fd9\u4e2a\u5ff5\u5934\uff1f\u201d\u539f\u6765\u4e8e\u8c26\u5fe0 \u809d\u4e49\u80c6\uff0c\u5929\u4e0b\u540c\u94a6\uff0c\u5373\u4f7f\u662f\u675c\u5e4c\u8fd9\u6837\u7684\u6076\u4eba\uff0c\u5fc3\u5e95\u91cc\u4e5f\u662f\u4f69\u670d\u7684\u3002 \u3000\u3000\u674e\u6db5\u771f\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4e8e\u8c26\u7684\u540d\u5b57\u628a\u4f60\u5413\u7740\u4e86\uff1f\u672c\u671d\u6cd5\u4f8b\uff0c\u7f6a\u4eba\u4e4b\u5973\uff0c\u6ca1\u4e3a\u5b98\u5974\u3002\u53ea\u53ef\u60dc \u90a3\u5c0f\u59d1\u5a18\u957f\u5f97\u592a\u7f8e\uff0c\u53ea\u6015\u7687\u4e0a\u89c1\u4e86\u4f1a\u81ea\u5df1\u8981\uff0c\u8981\u4e0d\u7136\u4f60\u82b1\u4e00\u7b14\u5927\u94b1\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u5c06\u5979\u5728\u5185\u5e9c\u91cc \u8d4e\u51fa\u6765\u3002\u201d\u8bf4\u7f62\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u7b11\u58f0\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u201c\u5587\u201d\u7684\u4e00\u58f0\uff0c\u5e10\u7bf7\u500f\u5730\u88c2\u5f00\uff0c\u91d1\u5149\u4e00\u95ea\uff0c \u90a3\u201c\u97e9\u4e8c\u5ac2\u201d\u4e00\u58f0\u5389\u53eb\uff0c\u9996\u5148\u4ec6\u5012\u5730\u4e0a\uff0c\u674e\u6db5\u771f\u5374\u624b\u660e\u773c\u5feb\uff0c\u62d4\u51fa\u8170\u5200\uff0c\u56de\u8eab\u4e00\u6321\uff0c\u5c06\u4e00\u6735 \u91d1\u82b1\u683c\u5f00\uff0c\u53ea\u89c1\u4e8e\u627f\u73e0\u67f3\u7709\u5012\u7ad6\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u98de\u8eab\u6740\u4eba\u3002 \u3000\u3000\u675c\u5e4c\u60ca\u53eb\u4e00\u58f0\uff0c\u5413\u5f97\u5446\u4e86\uff0c\u4e8e\u627f\u73e0\u4e00\u58f0\u53f1\u54a4\uff0c\u4e00\u626c\u624b\u53c8\u662f\u4e09\u6735\u91d1\u82b1\uff0c\u90a3\u97e9\u4e8c\u54e5\u9996\u5f53\u5176 \u51b2\uff0c\u88ab\u4e00\u6735\u91d1\u82b1\u7a7f\u8fc7\u5589\u5499\uff0c\u767b\u65f6\u6bd9\u547d\u3002\u675c\u5e4c\u521a\u521a\u6325\u52a8\u9f50\u7709\u68cd\uff0c\u6b63\u60f3\u4e0a\u524d\u52a9\u6218\uff0c\u4e5f\u88ab\u4e24\u6735\u91d1\u82b1 \u6253\u4e2d\uff0c\u4e8e\u627f\u73e0\u5ff5\u4ed6\u5c0a\u656c\u81ea\u5df1\u7684\u7236\u4eb2\uff0c\u8fd9\u4e24\u679a\u91d1\u82b1\uff0c\u6253\u4e2d\u7a74\u9053\uff0c\u53ea\u628a\u4ed6\u7684\u6b66\u529f\u5e9f\u4e86\uff0c\u5374\u5e76\u4e0d\u4f24 \u4ed6\u6027\u547d\u3002 \u3000\u3000\u674e\u6db5\u771f\u770b\u6e05\u695a\u53ea\u662f\u4e8e\u627f\u73e0\u4e00\u4eba\uff0c\u53c8\u662f\u54c8\u54c8\u5927\u7b11\uff0c\u4e8e\u627f\u73e0\u559d\u9053\uff1a\u201c\u9ed1\u767d\u6469\u8bc3\u653e\u4f60\u9003\u751f\uff0c\u8981 \u4f60\u6d17\u5fc3\u9769\u9762\uff0c\u60f3\u4e0d\u5230\u4f60\u8fd8\u662f\u7518\u4e3a\u9e70\u72ac\uff0c\u6b8b\u5bb3\u5fe0\u826f\uff0c\u597d\uff0c\u4eca\u65e5\u53ef\u4e0d\u80fd\u8f7b\u9976\u4f60\u4e86\uff01\u201d\u674e\u6db5\u771f\u7528\u592a \u6781\u5200\u62db\u5f0f\uff0c\u4ee5\u67d4\u5145\u521a\uff0c\u4e00\u8fde\u5316\u89e3\u4e86\u4e8e\u627f\u73e0\u7684\u4e09\u5251\u731b\u653b\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u9976\u6211\uff1f\u6211\u53ef\u8981\u9976\u4f60 \u5462\uff01\u5e76\u80a9\u5b50\u9f50\u4e0a\uff0c\u8fd9\u662f\u53db\u9006\u4e4b\u5973\uff0c\u53ea\u51c6\u6d3b\u64d2\uff0c\u4e0d\u8bb8\u6bd9\u547d\uff01\u201d\u674e\u6db5\u771f\u5e26\u6765\u56db\u4e2a\u4f8d\u536b\uff0c\u9664\u4e86\u97e9\u5c55 \u4e00\u4eba\u88ab\u6253\u6b7b\u4e4b\u5916\uff0c\u8fd8\u6709\u4e09\u4eba\uff0c\u90fd\u662f\u9ad8\u624b\uff0c\u4e00\u62e5\u800c\u4e0a\uff0c\u767b\u65f6\u628a\u4e8e\u627f\u73e0\u56f4\u5728\u6838\u5fc3\uff01 \u3000\u3000\u4e8e\u627f\u73e0\u4e00\u58f0\u51b7\u7b11\uff0c\u9752\u51a5\u5251\u500f\u5730\u5c55\u5f00\uff0c\u4f46\u89c1\u51b7\u7535\u7cbe\u8292\uff0c\u7f24\u7eb7\u98de\u821e\uff0c\u767e\u53d8\u7384\u673a\u5251\u6cd5\uff0c\u7cbe\u5999\u7edd \u4f26\uff0c\u53ea\u6740\u5f97\u90a3\u51e0\u4e2a\u536b\u58eb\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\uff0c\u5e78\u800c\u6709\u674e\u6db5\u771f\u8fd8\u63a5\u5f97\u4f4f\u4e8e\u627f\u73e0\u7684\u5251 \u62db\uff0c\u8981\u4e0d\u7136\u90a3\u51e0\u4e2a\u536b\u58eb\u7684\u5175\u5203\u65e9\u88ab\u524a\u65ad\u3002 \u3000\u3000\u674e\u6db5\u771f\u5728\u592a\u6781\u62f3\u5200\u4e24\u95e8\uff0c\u4e0b\u8fc7\u51e0\u5341\u5e74\u82e6\u529f\uff0c\u5200\u638c\u517c\u4f7f\uff0c\u582a\u582a\u62b5\u6321\u5f97\u4f4f\u3002\u4e8e\u627f\u73e0\u6068\u4ed6\u53e3\u820c \u8f7b\u8584\uff0c\u62db\u62db\u51cc\u5389\uff0c\u5251\u52bf\u5982\u8679\uff0c\u674e\u6db5\u771f\u90a3\u4e09\u4e2a\u52a9\u624b\uff0c\u53ea\u6c42\u81ea\u4fdd\uff0c\u653b\u52bf\u51e0\u4e4e\u5168\u6307\u5411\u674e\u6db5\u771f\u8eab\u4e0a\uff0c \u674e\u6db5\u771f\u6321\u4e86\u4e8c\u4e09\u5341\u62db\uff0c\u6e10\u89c9\u5e94\u4ed8\u8270\u96be\uff0c\u62db\u6570\u5168\u88ab\u5c01\u4f4f\uff0c\u653b\u4e0d\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u573a\u5927\u6253\uff0c\u65e9\u628a\u5168\u5be8\u60ca\u52a8\u3002\u675c\u5e4c\u5728\u5730\u4e0b\u722c\u4e86\u8d77\u6765\uff0c\u5636\u58f0\u53eb\u9053\uff1a\u201c\u6717\u5be8\u4e3b\uff0c\u5feb\u53eb\u5f13\u7bad\u624b \u6765\uff01\u201d\u4e8e\u627f\u73e0\u56de\u8eab\u4e00\u5251\uff0c\u628a\u674e\u6db5\u771f\u903c\u9000\u4e09\u6b65\uff0c\u626c\u624b\u53c8\u662f\u4e09\u6735\u91d1\u82b1\uff0c\u90a3\u4e09\u540d\u536b\u58eb\uff0c\u9664\u4e86\u4e00\u4e2a\u672c \u9886\u8f83\u9ad8\u7684\u80fd\u591f\u907f\u5f00\u4e4b\u5916\uff0c\u5176\u4ed6\u4e24\u4eba\uff0c\u4e00\u4e2a\u88ab\u6253\u778e\u773c\u775b\uff0c\u4e00\u4e2a\u4e5f\u50cf\u97e9\u5c55\u4e00\u6837\uff0c\u88ab\u91d1\u82b1\u7a7f\u5589\u800c \u8fc7\uff0c\u767b\u65f6\u6bd9\u547d\u3002\u4e8e\u627f\u73e0\u5251\u950b\u6307\u7740\u675c\u5e4c\u559d\u9053\uff1a\u201c\u9976\u4f60\u6027\u547d\uff0c\u8fd8\u4e0d\u9886\u60c5\uff1f\u518d\u6562\u591a\u8bdd\uff0c\u8fd9\u4e24\u4e2a\u4eba\u5c31 \u662f\u4f60\u7684\u699c\u6837\u3002\u201d \u3000\u3000\u5927\u5be8\u91cc\u4eba\u58f0\u9f0e\u6cb8\uff0c\u4e8e\u627f\u73e0\u8fd0\u5251\u5982\u98ce\uff0c\u7d27\u7d27\u903c\u7740\u674e\u6db5\u771f\uff0c\u4e0d\u8bb8\u4ed6\u9003\u8d70\uff0c\u62bd\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u4e00 \u4e2a\u5f5d\u65cf\u6253\u626e\u7684\u866c\u9aef\u6c49\u5b50\uff0c\u53cc\u76ee\u70af\u70af\uff0c\u5835\u7740\u5be8\u95e8\uff0c\u540e\u9762\u5df2\u96c6\u5408\u4e86\u51e0\u5341\u540d\u5f13\u7bad\u624b\uff0c\u60f3\u6765\u8fd9\u4eba\u4fbf\u662f \u6717\u82f1\u4e86\u3002\u4e8e\u627f\u73e0\u53d6\u51fa\u90a3\u9762\u7ee3\u7740\u4e24\u5934\u72ee\u5b50\u7684\u738b\u65d7\uff0c\u8fce\u98ce\u4e00\u5c55\uff0c\u53eb\u9053\uff1a\u201c\u6717\u5be8\u4e3b\uff0c\u4f60\u662f\u5f5d\u65cf\u82f1 \u8c6a\uff0c\u4f55\u5fc5\u4e3a\u864e\u4f5c\u6005\uff0c\u6bb5\u738b\u7237\u8bf7\u4f60\u5230\u5927\u7406\u53bb\u5171\u56fe\u5927\u4e8b\uff0c\u671b\u4f60\u4e09\u601d\u3002\u201d\u4e00\u626c\u624b\u90a3\u9762\u738b\u65d7\u5f84\u5411\u6717\u82f1 \u98de\u53bb\uff0c\u6717\u82f1\u63a5\u5230\u624b\u4e2d\uff0c\u767b\u65f6\u5446\u4e86\u3002 \u3000\u3000\u674e\u6db5\u771f\u558a\u9053\uff1a\u201c\u6717\u5be8\u4e3b\uff0c\u4f60\u8981\u8363\u534e\u5bcc\u8d35\u3002\u6211\u8bf7\u7687\u4e0a\u5c01\u4f60\u505a\u77f3\u6797\u7684\u571f\u53f8\u3002\u5feb\u5408\u529b\u628a\u8fd9\u5973\u8d3c \u64d2\u4e86\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u9a74\u9a6c\u5636\u9e23\uff0c\u811a\u6b65\u5608\u6742\uff0c\u540e\u5be8\u706b\u5149\u5927\u8d77\uff0c\u6717\u82f1\u54ea\u77e5\u9053\u53ea\u662f\u53f6\u6210\u6797\u4e00 \u4eba\u6240\u505a\u7684\u4e8b\uff0c\u53ea\u9053\u5927\u5be8\u5df2\u88ab\u653b\u7834\uff0c\u9677\u5165\u5305\u56f4\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u5730\u559d\u9053\uff1a\u201c\u8c01\u5e0c\u7f55\u4f60\u671d\u5ef7\u7684\u5c01 \u8d60\uff01\u201d\u4e00\u6454\u624b\u53eb\u5f13\u7bad\u624b\u9000\u5f00\uff0c\u7adf\u7136\u62d4\u51fa\u5200\u6765\uff0c\u52a9\u4e8e\u627f\u73e0\u6740\u674e\u6db5\u771f\u3002 \u3000\u3000\u674e\u6db5\u771f\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4f46\u4ed6\u8001\u5978\u5de8\u6ed1\uff0c\u867d\u5371\u4e0d\u4e71\uff0c\u5ffd\u5730\u5fc3\u751f\u8be1\u8ba1\uff0c\u970d\u5730\u4e00\u4e2a\u95ea\u8eab\uff0c\u5de6 \u81c2\u4e00\u4f38\uff0c\u65bd\u5c55\u5927\u64d2\u62ff\u624b\u6cd5\uff0c\u5c06\u6717\u82f1\u626d\u4f4f\uff0c\u4e8e\u627f\u73e0\u6b63\u81ea\u4e00\u5251\u523a\u6765\uff0c\u674e\u6db5\u771f\u628a\u6717\u82f1\u4e00\u63a8\uff0c\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u62fc\u4e2a\u540c\u5f52\u4e8e\u5c3d\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5251\u950b\u4e00\u98a4\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u4ece\u674e\u6db5\u771f\u8033\u8fb9\u524a\u8fc7\uff0c\u5979\u6295\u9f20\u5fcc\u5668\uff0c\u8fd9\u4e00\u62db\u7adf\u662f\u4e34\u5d16\u52d2 \u9a6c\uff0c\u4e0d\u6562\u9aa4\u4e0b\u6740\u624b\u3002\u674e\u6db5\u771f\u54c8\u54c8\u5927\u7b11\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u5927\u543c\uff0c\u9707\u8033\u5982\u96f7\uff0c\u5e10\u5e55\u500f\u5730\u5377\u5f00\uff0c\u4e00\u6761\u6c49 \u5b50\u65cb\u98ce\u822c\u6251\u5165\uff0c\u674e\u6db5\u771f\u8fd8\u672a\u770b\u6e05\u695a\uff0c\u7acb\u89c9\u5947\u75db\u5f7b\u9aa8\uff0c\u539f\u6765\u5728\u8fd9\u4e00\u7167\u9762\u4e4b\u95f4\uff0c\u5df2\u7ed9\u6765\u4eba\u7528\u64d2\u62ff \u624b\u626d\u5f2f\u4e86\u53f3\u624b\u81c2\u818a\u3002\u8fd9\u4eba\u4e0d\u95ee\u53ef\u77e5\uff0c\u5f53\u7136\u662f\u53f6\u6210\u6797\u4e86\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u4ee5\u5176\u4eba\u4e4b\u9053\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\uff0c\u53f6\u6210\u6797\u7ec3\u6709\u5927\u529b\u91d1\u94a2\u624b\u7684\u529f\u592b\uff0c\u4e94\u6307\u5df2\u4e00\u7d27\uff0c\u7565\u4e00\u7528 \u529b\uff0c\u674e\u6db5\u771f\u5df2\u662f\u7981\u53d7\u4e0d\u4f4f\uff0c\u624b\u4e0a\u7684\u94a2\u5200\u7fd8\u4e86\u8d77\u6765\uff0c\u53cd\u65ab\u81ea\u5df1\u7684\u989d\u89d2\uff0c\u674e\u6db5\u771f\u903c\u5f97\u653e\u5f00\u6293\u7740\u6717 \u82f1\u7684\u5de6\u624b\uff0c\u62fc\u529b\u6297\u62d2\uff0c\u6717\u82f1\u8eab\u5b50\u4e00\u677e\uff0c\u52c3\u7136\u5927\u6012\uff0c\u53cd\u624b\u4e00\u5200\uff0c\u201c\u5494\u5693\u2019\u4e00\u58f0\uff0c\u5c06\u674e\u6db5\u771f\u65a9\u4e3a \u4e24\u6bb5\u3002 \u3000\u3000\u628a\u773c\u770b\u65f6\uff0c\u675c\u5e4c\u65e9\u5df2\u5728\u6df7\u4e71\u4e4b\u4e2d\u9003\u8d70\uff0c\u5269\u4e0b\u7684\u90a3\u540d\u536b\u58eb\u4e5f\u88ab\u4e8e\u627f\u73e0\u6740\u4e86\u3002\u8fd9\u4e00\u5f79\uff0c\u675c\u5e4c \u7684\u515a\u7fbd\u4ee5\u53ca\u674e\u6db5\u771f\u5e26\u6765\u7684\u4eba\uff0c\u6216\u6b7b\u6216\u9003\uff0c\u5927\u5be8\u5185\u5269\u4e0b\u6765\u7684\u5168\u662f\u6717\u82f1\u7684\u4eba\u3002\u4e00\u4e9b\u4eba\u5f85\u53bb\u6551\u706b\uff0c \u6717\u82f1\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u70e7\u4e86\u5e72\u51c0\uff0c\u54b1\u4eec\u6446\u8131\u4e86\u8fd9\u4e9b\u72d7\u5b50\uff0c\u90fd\u5230\u5927\u7406\u6295\u6bb5\u738b\u7237\u53bb\u3002\u201d\u6709\u4eba\u5e94\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u518d\u4e5f\u4e0d\u5e72\u8fd9\u4e2a\u8425\u751f\uff0c\u4e5f\u7701\u5f97\u88ab\u4e61\u4eb2\u8d23\u9a82\u3002\u201d\u8fd9\u4e2a\u4eba\u6b63\u662f\u90a3\u5f5d\u65cf\u5c11\u5973\u7684\u8868\u54e5\uff0c\u90a3 \u5f5d\u65cf\u5c11\u5973\u65e9\u5df2\u56de\u5230\u5be8\u4e2d\uff0c\u8fd9\u65f6\u6b63\u62b1\u7740\u4e8e\u627f\u73e0\u6b22\u559c\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u5f53\u665a\uff0c\u6717\u82f1\u8fd9\u4e00\u4f19\u4eba\u4fbf\u64a4\u51fa\u77f3\u6797\uff0c\u9644\u8fd1\u7684\u6751\u5b50\u542c\u5230\u8fd9\u4e2a\u6d88\u606f\uff0c\u4e61\u6c11\u90fd\u8d76\u4e86\u6765\uff0c\u6717\u82f1\u4eb2\u81ea \u5ba3\u5e03\u6539\u90aa\u5f52\u6b63\u4e4b\u4e8b\uff0c\u4e61\u6c11\u6b22\u58f0\u96f7\u52a8\uff0c\u767b\u65f6\u5728\u77f3\u6797\u524d\u9762\u7684\u5927\u8349\u576a\u6740\u732a\u5bb0\u7f8a\uff0c\u6b4c\u821e\u72c2\u6b22\u3002\u6717\u82f1\u7684 \u624b\u4e0b\u5168\u662f\u5f5d\u4eba\uff0c\u51e0\u4e4e\u6709\u4e00\u5927\u534a\u5728\u9644\u8fd1\u7684\u4e61\u6751\u91cc\u8fd8\u6709\u5bb6\u4eba\u4eb2\u621a\uff0c\u6717\u82f1\u5f53\u5373\u51b3\u5b9a\uff0c\u653e\u5047\u4e09\u5929\uff0c\u8ba9 \u624b\u8db3\u5144\u5f1f\u4e0e\u5bb6\u4eba\u56e2\u805a\uff0c\u4e09\u5929\u4e4b\u540e\uff0c\u518d\u53bb\u5927\u7406\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u4e0e\u53f6\u6210\u6797\u53ef\u662f\u6025\u4e0d\u53ca\u5f85\uff0c\u53c2\u52a0\u4e86\u5f5d\u65cf\u7684\u72c2\u6b22\u821e\u4f1a\u4e4b\u540e\uff0c\u7acb\u5373\u5411\u6717\u82f1\u9053\u522b\uff0c\u8d77\u7a0b\u4e0a \u8def\u3002\u62e8\u8f6c\u9a6c\u5934\uff0c\u6539\u8fc7\u65b9\u5411\uff0c\u524d\u5f80\u5927\u7406\u3002 \u3000\u3000\u4ece\u77f3\u6797\u524d\u5f80\u5927\u7406\uff0c\u4e00\u5343\u591a\u91cc\u8def\u7a0b\uff0c\u5168\u662f\u5c71\u5730\u9ad8\u539f\uff0c\u5341\u5206\u96be\u8d70\uff0c\u8d70\u4e86\u56db\u4e94\u5929\uff0c\u8fd8\u662f\u5728\u4e1b\u5c71 \u5cfb\u5cad\u4e4b\u4e2d\uff0c\u53f6\u6210\u6797\u6734\u5b9e\u5be1\u8a00\uff0c\u5bf9\u4e8e\u627f\u73e0\u5374\u662f\u7167\u6599\u5f97\u5f88\u5468\u5230\uff0c\u4e8e\u627f\u73e0\u4f46\u89c9\u8fd9\u4e2a\u65c5\u4f34\uff0c\u867d\u7136\u5e76\u4e0d \u8ba8\u4eba\u559c\u6b22\uff0c\u4f46\u5374\u4e5f\u4e0d\u60f9\u4eba\u8ba8\u538c\u3002\u4e91\u5357\u7684\u82b1\u6728\u4e4b\u591a\uff0c\u51a0\u4e8e\u5168\u56fd\uff0c\u6c14\u5019\u53c8\u7279\u522b\u597d\uff0c\u53f6\u6210\u6797\u867d\u7136\u6734 \u5b9e\u5be1\u8a00\uff0c\u4e00\u8def\u4e0a\u9e1f\u8bed\u82b1\u9999\uff0c\u5c71\u5947\u6c34\u79c0\uff0c\u4e8e\u627f\u73e0\u5012\u4e5f\u4e0d\u89c9\u5f97\u5bc2\u5bde\uff0c\u6709\u4e00\u79cd\u6811\u53eb\u505a\u201c\u5927\u9752\u6811\u201d\uff0c \u5f53\u5730\u58eb\u4eba\u53eb\u505a\u201c\u98ce\u6c34\u6811\u201d\uff0c\u6cbf\u8def\u7686\u53ef\u89c1\u5230\u3002\u8fd9\u662f\u5728\u5317\u65b9\u89c1\u4e0d\u5230\u7684\u4e00\u79cd\u4e54\u6728\uff0c\u6811\u53f6\u6781\u4e3a\u8302\u76db\uff0c \u8471\u830f\u8038\u7acb\uff0c\u6d53\u836b\u853d\u5730\uff0c\u56db\u5b63\u5e38\u9752\uff0c\u6811\u6839\u50cf\u9f99\u722a\uff0c\u7262\u56fa\u5730\u76d8\u7ed3\u5728\u5730\u4e0a\uff0c\u5c31\u4f3c\u9752\u6625\u548c\u751f\u547d\u7684\u8c61 \u5f81\uff0c\u4efb\u8c01\u89c1\u4e86\uff0c\u90fd\u4f1a\u6b22\u559c\u8d5e\u53f9\u3002\u4e8e\u627f\u73e0\u5ffd\u8d77\u9057\u601d\uff0c\u4ee5\u524d\u5979\u66fe\u628a\u94c1\u955c\u5fc3\u6bd4\u4f5c\u6c5f\u5357\u56ed\u6797\u91cc\u7684\u73ab\u7470 \u82b1\uff0c\u628a\u53f6\u5b97\u7559\u6bd4\u4f5c\u4e91\u8d35\u9ad8\u539f\u4e0a\u7684\u677e\u6749\uff0c\u73b0\u5728\u53c8\u89c9\u5f97\u53f6\u6210\u6797\u6709\u4e9b\u50cf\u5927\u9752\u6811\uff0c\u9759\u7a46\u5e84\u4e25\u5374\u53c8\u5145\u6ee1 \u751f\u547d\u529b\u7684\u5927\u9752\u6811\u3002\u4f46\u5979\u5012\u5e95\u662f\u613f\u610f\u5728\u5927\u9752\u6811\u4e0b\u906e\u836b\u5462\uff1f\u8fd8\u662f\u613f\u610f\u5728\u73ab\u7470\u4e1b\u4e2d\u541f\u548f\u5462\uff1f\u90a3\u5c31\u8fde \u5979\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u4e86\u3002 \u3000\u3000\u8fdb\u5165\u5927\u7406\u5dde\u754c\uff0c\u5c71\u5cad\u5ced\u5cfb\uff0c\u5c71\u8def\u8d8a\u89c1\u5d0e\u5c96\uff0c\u8fd9\u4e00\u65e5\u4e8e\u3001\u53f6\u4e8c\u4eba\u7ffb\u8fc7\u4e00\u4e2a\u6781\u5176\u9669\u9661\u7684\u5c71 \u5761\uff0c\u540d\u53eb\u201c\u7ea2\u5d16\u5761\u201d\uff0c\u5728\u5c71\u4e0b\u4e4b\u65f6\uff0c\u4e8e\u627f\u73e0\u66fe\u5411\u5c71\u6c11\u6253\u63a2\u8def\u7a0b\uff0c\u77e5\u9053\u8fc7\u4e86\u7ea2\u5d16\u5761\u4e4b\u540e\uff0c\u518d\u8d70 \u4e24\u5929\uff0c\u4fbf\u53ef\u4ee5\u5230\u5927\u7406\u4e86\u3002\u4e8e\u627f\u73e0\u4e00\u60f3\u5230\u5373\u5c06\u53ef\u4ee5\u89c1\u5230\u5e08\u7236\uff0c\u7cbe\u795e\u7115\u53d1\uff0c\u5fd8\u4e86\u75b2\u52b3\uff0c\u62a2\u5148\u767b \u5c71\uff0c\u54ea\u77e5\u5c71\u5761\u9669\u9661\u66f2\u6298\uff0c\u6781\u4e4b\u96be\u8d70\uff0c\u4eba\u7eb5\u4e0d\u75b2\uff0c\u9a6c\u4e5f\u7d2f\u4e86\uff0c\u4e8e\u627f\u73e0\u548c\u53f6\u6210\u6797\u53ea\u597d\u7275\u7740\u9a6c\u8d70\uff0c \u4e8e\u627f\u73e0\u53f9\u9053\uff1a\u201c\u4e00\u8def\u4e0a\u4eba\u8bf4\uff0c\u5929\u5b50\u5e99\u5761\u6700\u9ad8\uff0c\u7ea2\u5d16\u5761\u6700\u9669\uff0c\u679c\u662f\u540d\u4e0d\u865a\u4f20\u3002\u201d\u53f6\u6210\u6797\u7b11\u9053\uff1a \u201c\u4e00\u8def\u4e0a\u4eba\u4eec\u4e5f\u8bf4\uff0c\u5927\u7406\u98ce\u666f\u6700\u4f73\uff0c\u7ecf\u8fc7\u9669\u963b\u7684\u8def\u7a0b\uff0c\u624d\u66f4\u663e\u5f97\u90a3\u662f\u6843\u6e90\u798f\u5730\u3002\u6211\u770b\u8fd9\u662f\u5929 \u516c\u6709\u610f\u7684\u5b89\u6392\uff0c\u5148\u6709\u8270\u96be\uff0c\u540e\u6709\u5b89\u4e50\uff0c\u4e16\u4e8b\u5982\u6b64\uff0c\u884c\u8def\u4ea6\u7136\u3002\u201d\u53f6\u6210\u6797\u77e5\u9053\u4e8e\u627f\u73e0\u6b22\u559c\u540d\u80dc \u98ce\u666f\uff0c\u8fd9\u8bf4\u8bdd\u81ea\u7136\u662f\u7ed9\u5979\u201c\u6253\u6c14\u201d\u7684\uff0c\u4e8e\u627f\u73e0\u5374\u662f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u53ea\u89c9\u4ed6\u7684\u8bf4\u8bdd\u867d\u4f3c\u8bf4\u7b11\uff0c\u5374\u4e5f \u81ea\u6709\u51e0\u5206\u54f2\u7406\u3002 \u3000\u3000\u597d\u4e0d\u5bb9\u6613\u722c\u4e0a\u4e86\u7ea2\u5d16\u5761\uff0c\u4e24\u5339\u9a6c\u90fd\u7d2f\u5f97\u55b7\u6c14\u5636\u5598\uff0c\u4e8e\u627f\u73e0\u548c\u53f6\u6210\u6797\u5750\u4e0b\u6765\u6b47\u606f\uff0c\u4f46\u89c1\u5c71 \u5761\u4e4b\u4e0b\u662f\u4e00\u4e2a\u5c71\u95f4\u575d\u5b50\uff0c\u5730\u52bf\u5e73\u5766\uff0c\u5e84\u56ed\u9690\u7ea6\u53ef\u89c1\u3002\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u4f60\u7684\u8bdd\u4e0d\u9519\uff0c\u8fc7\u4e86\u9ad8 \u5c71\uff0c\u4fbf\u662f\u5e73\u5730\u3002\u201d\u84e6\u7136\u60f3\u8d77\u81ea\u5df1\u4ece\u957f\u6c5f\u4e4b\u6ee8\u6765\u5230\u4e91\u8d35\u9ad8\u539f\uff0c\u5730\u65b9\u8fe5\u5f02\uff0c\u65c5\u4f34\u4e5f\u5927\u4e0d\u76f8\u540c\uff0c\u4e0d \u89c9\u500f\u7136\u795e\u5f80\uff0c\u94c1\u955c\u5fc3\u7684\u5f71\u5b50\u53c8\u5728\u8111\u6d77\u4e2d\u6447\u6643\uff0c\u56de\u5934\u4e00\u77a5\uff0c\u4f46\u89c1\u53f6\u6210\u6797\u4e5f\u6b63\u5728\u770b\u7740\u5979\uff0c\u4e8e\u627f\u73e0 \u5ffd\u7136\u9762\u4e0a\u53d1\u70e7\uff0c\u4f46\u89c9\u53f6\u6210\u6797\u597d\u4f3c\u770b\u7834\u4e86\u5979\u7684\u5fc3\u4e8b\u3002\u5176\u5b9e\u81ea\u4ece\u90a3\u5929\u5728\u77f3\u6797\u4e4b\u540e\uff0c\u4e8e\u627f\u73e0\u5728\u53f6\u6210 \u6797\u9762\u524d\u7edd\u53e3\u4e0d\u63d0\u94c1\u955c\u5fc3\uff0c\u53f6\u6210\u6797\u53c8\u54ea\u91cc\u731c\u60f3\u5f97\u5230\u4e8e\u627f\u73e0\u6b64\u523b\u5fc3\u4e2d\u5728\u60f3\u94c1\u955c\u5fc3\uff1f \u3000\u3000\u4e8e\u627f\u73e0\u4f4e\u5934\u9ed8\u60f3\uff0c\u8d8a\u60f3\u8d8a\u4e71\uff0c\u5ffd\u542c\u5f97\u4e0b\u9762\u575d\u5b50\u4f20\u6765\u4e00\u58f0\u9a8f\u9a6c\u7684\u5636\u9e23\uff0c\u970e\u90a3\u95f4\uff0c\u4e8e\u627f\u73e0\u597d \u4f3c\u68a6\u4e2d\u9aa4\u7136\u60ca\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u7167\u591c\u72ee\u5b50\uff0c\u7167\u591c\u72ee\u5b50\uff01\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211 \u5931\u53bb\u7684\u5b9d\u9a6c\uff0c\u6211\u5931\u53bb\u7684\u5b9d\u9a6c\uff01\u4f60\u5728\u8fd9\u513f\u7167\u6599\u7272\u53e3\uff0c\u6211\u53bb\u770b\u770b\uff01\u201d\u4e0d\u5f85\u53f6\u6210\u6797\u518d\u95ee\uff0c\u7acb\u523b\u98de\u5954 \u4e0b\u5c71\uff0c\u628a\u53f6\u6210\u6797\u5f04\u5f97\u83ab\u660e\u5176\u5999\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u8dd1\u5230\u534a\u5c71\uff0c\u53ea\u89c1\u575d\u5b50\u4e0a\u6709\u4e00\u95f4\u7ea2\u7816\u7eff\u74e6\u7684\u5927\u5c4b\uff0c\u5916\u9762\u5927\u8349\u576a\u4e0a\u6709\u8bb8\u591a\u5e84\u4e01\uff0c\u8349\u576a \u4e0a\u5e76\u65e0\u7272\u53e3\uff0c\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u6211\u7edd\u5bf9\u4e0d\u4f1a\u542c\u9519\uff0c\u90a3\u662f\u6211\u5b9d\u9a6c\u7684\u5636\u9e23\u3002\u5440\uff0c\u9a6c\u513f\u5440\u9a6c\u513f\uff0c\u4f60\u4e00 \u5b9a\u662f\u7ed9\u6076\u4eba\u5173\u4e86\u8d77\u6765\uff0c\u77e5\u9053\u6211\u6765\uff0c\u5411\u6211\u6c42\u6551\u4e86\u3002\u201d\u6b63\u5f85\u4e0d\u987e\u4e00\u5207\uff0c\u51b2\u4e0b\u53bb\u641c\u5e84\uff0c\u5ffd\u89c1\u4e0b\u9762\u6709 \u4e00\u4e2a\u767d\u8863\u5c11\u5e74\uff0c\u5411\u7740\u8349\u576a\u90a3\u7fa4\u4eba\u5982\u98de\u75be\u8dd1\uff0c\u4e8e\u627f\u73e0\u9aa4\u7136\u95f4\u53c8\u4f3c\u5815\u5165\u68a6\u4e2d\uff0c\u5446\u82e5\u6728\u9e21\uff0c\u8fd9\u4e2a\u767d \u8863\u5c11\u5e74\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u5979\u521a\u521a\u5ff5\u53ca\u7684\u94c1\u955c\u5fc3\uff01 \u3000\u3000\u8fd9\u4e00\u77ac\u95f4\uff0c\u4e8e\u627f\u73e0\u5fc3\u9b42\u8ff7\u4e71\uff0c\u60f3\u51b2\u4e0b\u53bb\uff0c\u4f46\u4e24\u6761\u817f\u8f6f\u8f6f\u5730\u63d0\u4e0d\u8d77\u52b2\u6765\uff0c\u5012\u5e95\u662f\u559c\u6b22\u8fc7 \u751a\uff0c\u8fd8\u662f\u4ecd\u60f3\u4f3c\u5728\u53f0\u5dde\u4e4b\u65f6\u90a3\u6837\u5c06\u4ed6\u907f\u5f00\uff1f\u5979\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u7684\u5fc3\u60c5\uff0c\u5ffd\u5730\u60f3\u9053\uff1a\u201c\u4e14\u770b \u770b\u4ed6\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u5440\uff0c\u94c1\u955c\u5fc3\u4e5f\u4f1a\u5230\u8fd9\u513f\u6765\uff0c\u8fd9\u771f\u662f\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u7684\u4e8b\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u662f\u7231\u662f\u618e\u96be\u81ea\u8bc6\uff0c\u5973\u513f\u5fc3\u4e8b\u6ca1\u4eba\u77e5\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u5f80\u4e8b\u5982\u70df\u3000\u7f61\u98ce\u5439\u5df1\u6563\u3000\u524d\u5c18\u82e5\u68a6\u3000\u6b7b\u6c34\u53c8\u91cd\u6ce2 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u5f80\u4e8b\u5982\u70df\u3000\u7f61\u98ce\u5439\u5df1\u6563\u3000\u524d\u5c18\u82e5\u68a6\u3000\u6b7b\u6c34\u53c8\u91cd\u6ce2 \u3000\u3000\u4e8e\u627f\u73e0\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u94c1\u955c\u5fc3\u4f1a\u6765\u5230\u8fd9\u513f\u3002\u94c1\u955c\u5fc3\u5374\u662f\u6709\u610f\u8ffd\u8e2a\u4e8e\u627f\u73e0\u7684\u3002\u51ed\u4ed6\u7684\u806a\u660e\uff0c \u4ed6\u65e9\u6599\u5230\u4e8e\u627f\u73e0\u79bb\u5f00\u4e49\u519b\u4e4b\u540e\uff0c\u8d70\u662f\u5230\u5927\u7406\u6765\u627e\u5979\u7684\u5e08\u7236\u3002\u4ed6\u4e00\u8def\u517c\u7a0b\u8ffd\u6e21\uff0c\u78b0\u5de7\u4e8e\u627f\u73e0\u5728 \u8d35\u5dde\u82d7\u533a\u548c\u6606\u660e\u803d\u6401\u4e86\u4e00\u4e9b\u65f6\u5019\uff0c\u4e2d\u95f4\u53c8\u8d70\u4e86\u77f3\u6797\u8fd9\u4e00\u6bb5\u6b6a\u8def\uff0c\u6545\u6b64\u94c1\u955c\u5fc3\u53cd\u800c\u8d76\u5728\u5979\u7684\u524d \u5934\uff0c\u4f46\u662f\u94c1\u955c\u5fc3\u4e5f\u662f\u505a\u68a6\u90fd\u60f3\u4e0d\u5230\uff0c\u4e8e\u627f\u73e0\u6b64\u523b\u5c31\u5728\u8fd9\u5c71\u5761\u4e0a\uff0c\u79bb\u5f00\u81ea\u5df1\u53ea\u6709\u534a\u91cc\u4e4b\u9065\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u8eb2\u5728\u4e00\u5757\u5ca9\u77f3\u4e4b\u540e\uff0c\u5fc3\u5934\u597d\u50cf\u6709\u4e00\u5934\u5c0f\u9e7f\uff0c\u8df3\u8dc3\u4e0d\u4f11\uff0c\u773c\u775b\u5374\u6ce8\u5b9a\u94c1\u955c\u5fc3\uff0c\u53ea\u89c1 \u94c1\u955c\u5fc3\u51b2\u5165\u8349\u576a\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u8c37\u8001\u5339\u592b\uff0c\u5feb\u51fa\u6765\u89c1\u6211\u3002\u201d\u90a3\u4e9b\u5e84\u4e01\u7eb7\u7eb7\u9669\u559d\uff0c\u94c1\u955c\u5fc3\u5c31\u4f3c \u4e00\u5934\u53d1\u4e86\u72c2\u7684\u72ee\u5b50\uff0c\u8c01\u6765\u62e6\u963b\uff0c\u5c31\u628a\u8c01\u63a8\u7ffb\u5730\u4e0a\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u6b63\u81ea\u60ca\u8be7\uff0c\u53ea\u89c1\u5e84\u95e8\u5ffd\u542f\uff0c\u4e00\u4e2a\u866c\u9aef\u5927\u6c49\u624b\u63d0\u4e00\u67c4\u539a\u65ab\u5c71\u5200\uff0c\u5927\u8e0f\u6b65\u8d70\u4e86\u51fa\u6765\uff0c \u626c\u58f0\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u4e24\u6b21\u4e09\u756a\u5230\u6211\u5e84\u524d\u95f9\u4e8b\uff0c\u5230\u5e95\u610f\u6b32\u5982\u4f55\uff1f\u201d\u94c1\u955c\u5fc3\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u610f \u6b32\u5982\u4f55\uff1f\u6211\u624d\u8981\u95ee\u4f60\u610f\u6b32\u5982\u4f55\uff1f\u4f60\u4e3a\u4ec0\u4e00\u4e48\u4e0d\u8ba9\u6211\u4e0e\u4e8e\u76f8\u516c\u76f8\u89c1\uff1f\u201d\u90a3\u8c37\u5e84\u4e3b\u9053\uff1a\u201c\u8fd9\u91cc\u662f \u8c37\u5bb6\u5e84\uff0c\u54ea\u6709\u4ec0\u4e48\u4e8e\u76f8\u516c\uff1f\u201d\u94c1\u955c\u5fc3\u559d\u9053\uff1a\u201c\u6ca1\u6709\u4e8e\u76f8\u516c\uff1f\u600e\u7684\u6709\u4e8e\u76f8\u516c\u6240\u9a91\u7684\u5b9d\u9a6c\uff1f\u201d\u58f0 \u97f3\u5ffd\u7136\u653e\u4f4e\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4e8e\u76f8\u516c\u4e0d\u613f\u89c1\u6211\uff0c\u8fd8\u662f\u4f60\u4e0d\u8ba9\u4ed6\u89c1\u6211\uff0c\u4f60\u603b\u5f97\u628a\u8bdd\u8bf4\u4e2a\u5206\u660e\u3002\u201d\u90a3 \u8c37\u5e84\u4e3b\u559d\u9053\uff1a\u201c\u80e1\u8a00\u4e71\u8bed\uff0c\u4f60\u518d\u80e1\u95f9\uff0c\u4eca\u65e5\u6211\u53ef\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u600e\u4e48\u6837\u6211\u4e5f\u8981\u89c1 \u4e8e\u76f8\u516c\u4e00\u9762\uff0c\u4e0d\uff0c\u4e0d\uff0c\u4ed6\u4e0d\u4f1a\u4e0d\u89c1\u6211\u7684\uff01\u201d\u65c1\u8fb9\u4e00\u4e2a\u5c11\u5e74\u53eb\u9053\uff1a\u201c\u7239\uff0c\u548c\u8fd9\u75af\u5c0f\u5b50\u591a\u8bf4\u4ec0 \u4e48\uff0c\u4e00\u5200\u5c06\u4ed6\u6253\u53d1\u4e86\u5427\u3002\u51e0\u6b21\u4e09\u756a\u5728\u6b64\u80e1\u95f9\uff0c\u4f20\u5c06\u51fa\u53bb\uff0c\u5c82\u4e0d\u8fb1\u4e86\u6211\u8c37\u5bb6\u5e84\u7684\u5a01\u540d\uff1f\u201d\u8fd9\u5c11 \u5e74\u4e43\u662f\u5c0f\u5e84\u4e3b\uff0c\u539f\u6765\u94c1\u955c\u5fc3\u5df2\u5728\u5e84\u4e0a\u95f9\u8fc7\u4e09\u5929\u4e86\u3002\u90a3\u8c37\u5e84\u4e3b\u4e5f\u66fe\u4e0e\u94c1\u955c\u5fc3\u4ea4\u8fc7\u4e24\u6b21\u624b\uff0c\u5fc3\u4e2d \u60f3\u9053\uff1a\u201c\u82e5\u662f\u4e00\u5200\u6253\u53d1\u5f97\u4e86\uff0c\u90a3\u5012\u6613\u529e\u4e86\u201d \u3000\u3000\u94c1\u955c\u5fc3\u53c8\u9053\uff1a\u201c\u4f60\u8bf4\u4e0d\u662f\u4e8e\u76f8\u516c\uff0c\u90a3\u4f60\u603b\u5f97\u8bf7\u8fd9\u5339\u7167\u591c\u72ee\u5b50\u9a6c\u7684\u4e3b\u4eba\u51fa\u6765\u4e0e\u6211\u4e00\u89c1\uff01\u201d \u8bf4\u8bdd\u7684\u58f0\u6c14\u7b80\u76f4\u8fd1\u4e8e\u6073\u6c42\u4e86\uff0c\u8c37\u5e84\u4e3b\u5927\u6012\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u7167\u591c\u72ee\u5b50\u9a6c\uff0c\u4ec0\u4e48\u4e3b\u4eba\uff1f\u8c37\u5bb6\u5e84\u662f\u6211 \u7684\uff0c\u8fd9\u5468\u56f4\u5341\u91cc\u7684\u7530\u5730\u3001\u623f\u5c4b\u3001\u7272\u755c\u90fd\u662f\u6211\u7684\uff0c\u6211\u5c31\u662f\u4e3b\u4eba\uff01\u6562\u60c5\u4f60\u662f\u770b\u4e2d\u4e86\u6211\u7684\u8fd9\u5339\u5b9d \u9a6c\uff0c\u54fc\uff0c\u54fc\uff01\u4f60\u8fd9\u5c0f\u8d3c\u64e6\u4eae\u8ddf\u775b\uff0c\u6211\u8c37\u4e2d\u8c6a\u53ef\u662f\u597d\u6b3a\u8d1f\u7684\uff01\u201d\u94c1\u955c\u5fc3\u5927\u559d\u9053\uff1a\u201c\u4e0d\u8ba9\u6211\u89c1 \u4eba\uff0c\u6562\u60c5\u4f60\u662f\u8c0b\u8d22\u5bb3\u547d\uff0c\u6740\u4e86\u4e8e\u76f8\u516c\uff0c\u62a2\u4e86\u4ed6\u7684\u9a6c\uff1f\u201d\u8c37\u4e2d\u8c6a\u5927\u6012\u559d\u8fd9\uff1a\u201c\u75af\u5c0f\u5b50\uff0c\u80e1\u8bf4\u516b \u9053\uff0c\u770b\u5200\uff01\u201d\u53ea\u542c\u5f97\u53ee\u5f53\u4e00\u58f0\uff0c\u706b\u82b1\u56db\u6e85\uff0c\u94c1\u955c\u5fc3\u4e0e\u8c37\u4e2d\u8c6a\u5df2\u4ea4\u4e0a\u4e86\u624b\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u542c\u4e86\u534a\u5929\uff0c\u9010\u6e10\u660e\u767d\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5fc5\u7136\u662f\u94c1\u955c\u5fc3\u53d1\u73b0\u7167\u591c\u72ee\u5b50\u9a6c\u5728\u8fd9\u8c37\u5bb6\u5e84\uff0c \u6545\u6b64\u4ee5\u4e3a\u6211\u5728\u8fd9\u513f\u4e86\uff0c\u4ed6\u4e0d\u77e5\u9053\u6211\u5df2\u6362\u4e86\u5973\u88c5\uff0c\u602a\u4e0d\u5f97\u4ed6\u8fd8\u662f\u53e3\u53e3\u58f0\u58f0\u53eb\u6211\u505a\u4e8e\u76f8\u516c\u3002\u5440\uff0c \u94c1\u955c\u5fc3\u5440\u94c1\u955c\u5fc3\uff0c\u4f60\u539f\u6765\u7adf\u662f\u5982\u6b64\u8bb0\u6302\u6211\u4e48\uff1f\u201d \u3000\u3000\u5200\u6765\u5251\u5f80\uff0c\u91d1\u94c1\u4ea4\u9e23\uff0c\u52a0\u4e0a\u5e84\u4e01\u547c\u559d\u7684\u58f0\u97f3\uff0c\u95f9\u5f97\u9707\u5929\u4ef7\u54cd\u3002\u4e8e\u627f\u73e0\u5bf9\u8fd9\u4e9b\u58f0\u97f3\uff0c\u597d\u4f3c \u5168\u6ca1\u6709\u542c\u8fdb\u8033\u4e2d\uff0c\u53ea\u662f\u75f4\u75f4\u5730\u60f3\uff1a\u201c\u94c1\u955c\u5fc3\u8fd9\u6837\u6e34\u671b\u89c1\u6211\uff0c\u6211\u5374\u5c3d\u662f\u60f3\u8eb2\u907f\u4ed6\uff01\u201d\u5ffd\u5730\u611f\u5230 \u6709\u4e9b\u5bf9\u4e0d\u4f4f\u94c1\u955c\u5fc3\uff0c\u51e0\u4e4e\u5c31\u60f3\u51b2\u4e0b\u53bb\u4e0e\u94c1\u955c\u5fc3\u76f8\u89c1\uff0c\u5ffd\u800c\u53c8\u60f3\u8d77\u3002\u4ee5\u524d\u4e0e\u4ed6\u76f8\u5904\u7684\u65e5\u5b50\uff0c\u53ea \u6015\u89c1\u4e86\u53cd\u8981\u5e73\u6dfb\u8bb8\u591a\u9ebb\u70e6\u3002\u84e6\u7136\u95f4\u542c\u5f97\u94c1\u955c\u5fc3\u5927\u53eb\u4e00\u58f0\uff0c\u4e8e\u627f\u73e0\u970d\u7136\u4e00\u60ca\uff0c\u6025\u5fd9\u63a2\u5934\u4e00\u770b\uff0c \u53ea\u89c1\u94c1\u955c\u5fc3\u80a9\u5934\u9c9c\u8840\u6ef4\u4e0b\uff0c\u539f\u6765\u5df2\u662f\u7ed9\u90a3\u8c37\u5e84\u4e3b\u7684\u5200\u950b\u5212\u4e86\u4e00\u9053\u4f24\u53e3\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u8fd9\u65f6\u518d\u4e5f\u65e0\u6687\u601d\u7d22\uff0c\u638c\u5fc3\u6263\u4e86\u4e09\u6735\u91d1\u82b1\uff0c\u4fbf\u5f85\u51fa\u53bb\u52a9\u6218\uff0c\u53ea\u542c\u5f97\u53c8\u662f\u4e00\u58f0\u5389\u53eb\uff0c \u539f\u6765\u662f\u8c37\u4e2d\u8c6a\u7684\u81c2\u818a\u4e5f\u7ed9\u94c1\u955c\u5fc3\u523a\u4e86\u4e00\u5251\uff0c\u94c1\u955c\u5fc3\u5927\u7b11\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff01\u770b\u5251\uff01\u201d\u55e4 \u7684\u4e00\u58f0\uff0c\u8fd9\u4e00\u5251\u8c37\u4e2d\u8c6a\u582a\u582a\u907f\u5f00\uff0c\u4f46\u8863\u5e26\u5374\u7ed9\u524a\u65ad\u3002\u94c1\u955c\u5fc3\u53d7\u4f24\u4e4b\u540e\uff0c\u66f4\u662f\u51f6\u731b\u96be\u6321\uff0c\u5251\u52bf \u5982\u8679\uff0c\u6740\u5f97\u8c37\u4e2d\u8c6a\u8fde\u8fde\u5012\u9000\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u4e2d\u7a0d\u5b9a\uff0c\u4ecd\u7136\u8eb2\u5728\u5ca9\u77f3\u540e\u9762\uff0c\u5fc3\u9053\uff1a\u201c\u94c1\u955c\u5fc3\u5bf9\u4ed8\u5f97\u4e86\u8fd9\u4e2a\u5e84\u4e3b\uff0c\u6211\u4e14\u518d\u770b\u4e00 \u4f1a\u513f\u3002\u201d\u90a3\u8c37\u4e2d\u8c6a\u867d\u8d25\u4e0d\u4e71\uff0c\u4e00\u67c4\u539a\u80cc\u65ab\u5c71\u5200\u821e\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u5c45\u7136\u906e\u62e6\u5f97\u98ce\u96e8\u4e0d\u900f\u3002\u4e8e\u627f\u73e0 \u5fc3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5728\u8fd9\u4e2a\u5730\u65b9\uff0c\u5c45\u7136\u6709\u5982\u6b64\u4eba\u7269\uff1f\u6211\u7684\u7167\u591c\u72ee\u5b50\u9a6c\u600e\u4e48\u4f1a\u5230\u4ed6\u7684\u5e84\u4e0a\u6765\u5462\uff1f\u4ed6 \u6b66\u529f\u867d\u9ad8\uff0c\u5374\u4e5f\u672a\u5fc5\u76d7\u5f97\u4e86\u6211\u7684\u5b9d\u9a6c\u3002\u201d \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u8c37\u4e2d\u8c6a\u53c8\u4e2d\u4e00\u5251\u3002\u94c1\u955c\u5fc3\u7684\u60ca\u6d9b\u5251\u6cd5\uff0c\u53d8\u5e7b\u65e0\u65b9\uff0c\u62db\u62db\u51cc\u5389\uff0c\u8c37\u4e2d\u8c6a\u867d\u7136\u81c2 \u529b\u6c89\u96c4\uff0c\u5200\u6cd5\u4e5f\u906e\u62e6\u5f97\u6781\u4e3a\u4e25\u5bc6\uff0c\u4f46\u6bd4\u5c06\u8d77\u6765\uff0c\u5230\u5e95\u662f\u76f8\u5f62\u89c1\u7ecc\u3002\u90a3\u5c0f\u5e84\u4e3b\u89c1\u7236\u4eb2\u72ec\u529b\u96be \u652f\uff0c\u5728\u5e84\u4e01\u624b\u91cc\u62a2\u8fc7\u4e00\u6761\u957f\u77db\uff0c\u51b2\u6765\u52a9\u6218\u3002\u8c37\u4e2d\u8c6a\u6025\u5fd9\u559d\u9053\uff1a\u201c\u4fca\u513f\uff0c\u9000\u4e0b\uff01\u201d\u90a3\u5c0f\u5e84\u4e3b\u86c7 \u77db\u6025\u523a\uff0c\u54ea\u91cc\u6536\u52bf\u5f97\u4f4f\uff1f\u53ea\u89c1\u957f\u77db\u7684\u94a2\u5c16\uff0c\u5c31\u8981\u89e6\u5230\u94c1\u955c\u5fc3\u7684\u540e\u5fc3\uff0c\u5ffd\u542c\u5f97\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c \u94c1\u955c\u5fc3\u53cd\u624b\u4e00\u5251\uff0c\u5c06\u957f\u77db\u524a\u4e3a\u4e24\u622a\uff0c\u4e00\u4e2a\u98de\u8eab\u8e6c\u811a\uff0c\u90a3\u5c0f\u5e84\u4e3b\u201c\u5427\u54d2\u201d\u4e00\u58f0\uff0c\u8dcc\u5012\u4e86\u4e00\u4e08\u5f00 \u5916\uff01 \u3000\u3000\u8c37\u4e2d\u8c6a\u4e0d\u77e5\u513f\u5b50\u6709\u5426\u53d7\u4f24\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u65ab\u5c71\u5200\u547c\u547c\u98ce\u54cd\uff0c\u75af\u72c2\u53cd\u6251\uff0c\u4f46\u9ad8\u624b\u6bd4\u62fc\uff0c\u54ea\u5bb9 \u5206\u5fc3\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u7834\u7efd\u9732\u5f97\u66f4\u591a\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u5de6\u81c2\u53c8\u4e2d\u4e86\u4e00\u5251\uff0c\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u94c1\u955c\u5fc3\u5927\u559d \u4e00\u58f0\uff0c\u957f\u5251\u4e00\u5c55\uff0c\u53cd\u624b\u4e00\u6572\uff0c\u201c\u5f53\u5577\u201d\u4e00\u58f0\uff0c\u8c37\u4e2d\u8c6a\u90a3\u67c4\u539a\u80cc\u65ab\u5c71\u5200\u8131\u624b\u98de\u51fa\uff0c\u94c1\u955c\u5fc3\u957f\u5251 \u4e00\u6307\uff0c\u5251\u5c16\u5bf9\u51c6\u4e86\u8c37\u4e2d\u8c6a\u7684\u54bd\u5589\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8ba9\u4e0d\u8ba9\u4e8e\u76f8\u516c\u89c1\u6211\uff1f\u201d \u3000\u3000\u8c37\u4e2d\u8c6a\u4e00\u58f0\u957f\u53f9\uff0c\u95ee\u9053\uff1a\u201c\u4fca\u513f\uff0c\u4f60\u53d7\u4f24\u4e86\u4e48\uff1f\u201d\u90a3\u5c0f\u5e84\u4e3b\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d\u8c37\u4e2d\u8c6a\u9053\uff1a \u201c\u597d\uff0c\u8fd9\u662f\u6211\u4e8c\u5341\u5e74\u6765\u7684\u7b2c\u4e00\u6b21\u8d25\u4ed7\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u53f0\u5dde\u94c1\u955c\u5fc3\uff01\u201d\u8c37\u4e2d \u8c6a\u9053\uff1a\u201c\u597d\uff0c\u4fca\u513f\uff0c\u628a\u90a3\u5339\u9a6c\u7684\u4e24\u4f4d\u4e3b\u4eba\u8bf7\u51fa\u6765\u89c1\u94c1\u76f8\u516c\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4e24\u4f4d\u4e3b \u4eba\uff1f\u201d\u8c37\u4e2d\u8c6a\u4e0d\u7b54\u8fd9\u8bdd\uff0c\u6495\u4e0b\u8863\u895f\uff0c\u5305\u624e\u4e86\u4e09\u5904\u4f24\u53e3\uff0c\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u5429\u5490\u513f\u5b50\u9053\uff1a\u201c\u628a\u90a3\u5339 \u9a6c\u4e5f\u7275\u51fa\u6765\u3002\u201d \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u53ea\u89c1\u90a3\u5c0f\u5e84\u4e3b\u5e26\u4e86\u4e00\u5bf9\u9752\u5e74\u7537\u5973\u8d70\u51fa\u6765\uff0c\u770b\u6765\u90fd\u4e0d\u5230\u4e8c\u5341\u5c81\u7684\u5e74\u7eaa\uff0c\u8863\u670d\u534e \u4e3d\uff0c\u7adf\u4f3c\u5bcc\u5bb6\u5b50\u5f1f\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u5446\u4e86\u4e00\u5446\uff0c\u53eb\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u4eec\u662f\u8c01\uff1f\u201d\u90a3\u5bf9\u5c11\u5e74\u7537\u5973\u4e5f\u662f\u83ab\u660e\u5176\u5999\uff0c\u9053\uff1a \u201c\u4f60\uff0c\u4f60\u662f\u8c01\uff1f\u4e3a\u4ec0\u4e48\u5b9a\u8981\u89c1\u6211\uff1f\u201d \u3000\u3000\u4e0d\u8bf4\u94c1\u955c\u5fc3\u60ca\u8be7\uff0c\u4e8e\u627f\u73e0\u66f4\u662f\u60ca\u5947\u4e0d\u5df2\uff01\u5979\u4e00\u5fc3\u7b49\u5019\uff0c\u60f3\u7740\u8fd9\u4e2a\u5077\u9a6c\u8d3c\u662f\u8c01\uff0c\u8c01\u77e5\u5374\u662f \u6c90\u56fd\u516c\u7684\u4e00\u5bf9\u513f\u5973\uff0c\u6c90\u78f7\u548c\u6c90\u71d5\uff0c\u4ed6\u4eec\u7adf\u820d\u5f03\u4e86\u56fd\u516c\u5e9c\u4e2d\u7684\u9526\u8863\u7389\u98df\uff0c\u9003\u5230\u4e86\u8fd9\u513f\u6765\uff01 \u3000\u3000\u539f\u6765\u90a3\u65e5\u6c90\u78f7\u77e5\u9053\u5927\u5185\u603b\u7ba1\u9633\u5b97\u6d77\u5230\u5e9c\uff0c\u81ea\u77e5\u95ef\u4e0b\u4e86\u7978\uff0c\u548c\u6c90\u71d5\u5546\u91cf\uff0c\u4e24\u4eba\u90fd\u538c\u5026\u4e86\u56fd \u516c\u5e9c\u4e2d\u7262\u7b3c\u822c\u7684\u751f\u6d3b\uff0c\u61a7\u61ac\u7740\u5916\u9762\u7684\u4e16\u754c\uff0c\u5546\u91cf\u4e4b\u540e\uff0c\u7adf\u4e00\u9f50\u9003\u8d70\uff0c\u60f3\u5230\u5927\u7406\u53bb\u627e\u5f20\u4e39\u67ab\uff0c \u5f20\u4e39\u67ab\u5728\u56fd\u516c\u5e9c\u6559\u4e66\u4e4b\u65f6\uff0c\u66fe\u5bf9\u4ed6\u4eec\u8bf4\u8d77\u81ea\u5df1\u6709\u4e00\u5339\u5b9d\u9a6c\uff0c\u540d\u4e3a\u201c\u7167\u591c\u72ee\u5b50\u201d\uff0c\u795e\u9a8f\u65e0\u6bd4\uff0c \u73b0\u5728\u7ed9\u4e86\u5f92\u5f1f\u5b50\u627f\u73e0\u4f5c\u4e3a\u5750\u9a91\uff0c\u8fd9\u5339\u9a6c\u4e0d\u4f46\u795e\u9a8f\uff0c\u800c\u4e14\u6781\u6709\u7075\u6027\uff0c\u9664\u4e86\u4e3b\u4eba\uff0c\u8c01\u90fd\u4e0d\u80fd\u9a91 \u5b83\uff0c\u5b83\u53c8\u6700\u8ba4\u5f97\u4e3b\u4eba\u65e5\u5e38\u4f69\u6234\u7684\u4e1c\u897f\uff0c\u82e5\u662f\u6301\u6709\u5b83\u8ba4\u5f97\u7684\u4e3b\u4eba\u4e4b\u7269\uff0c\u5b83\u4e5f\u4f1a\u542c\u8bdd\u3002\u5f20\u4e39\u67ab\u662f \u65e0\u5fc3\u63d0\u8d77\uff0c\u6c90\u71d5\u5374\u542c\u5728\u5fc3\u91cc\uff0c\u5728\u5f20\u4e39\u67ab\u79bb\u5e9c\u4e4b\u65f6\uff0c\u6c90\u71d5\u6c42\u4ed6\u9001\u4e00\u628a\u65e5\u5e38\u60ef\u7528\u7684\u63cf\u91d1\u6247\u5b50\u4f5c\u4e3a \u7eaa\u5ff5\uff0c\u5f20\u4e39\u67ab\u4e0d\u4ee5\u4e3a\u610f\u3001\u968f\u624b\u5c31\u9001\u4e86\u7ed9\u5979\u3002 \u3000\u3000\u90a3\u4e00\u65e5\u6c90\u71d5\u65e2\u51b3\u5b9a\u4e86\u9003\u5230\u5927\u7406\u53bb\u627e\u5f20\u4e39\u67ab\uff0c\u672c\u6765\u8fd8\u672a\u6253\u7b97\u76d7\u9a6c\uff0c\u5979\u548c\u5f1f\u5f1f\u9003\u51fa\u56fd\u516c\u5e9c \u540e\uff0c\u5148\u5230\u65c5\u820d\u53bb\u627e\u4e8e\u627f\u73e0\uff0c\u5c82\u77e5\u4e8e\u627f\u73e0\u90a3\u65f6\u6b63\u4e0e\u5979\u7684\u4e2b\u9b13\u90fd\u88ab\u9633\u5b97\u6d77\u56f0\u5728\u6c34\u7262\uff0c\u6c90\u71d5\u59d0\u5f1f\u4e0d \u89c1\u4e8e\u627f\u73e0\u5374\u89c1\u4e86\u90a3\u5339\u7167\u591c\u72ee\u5b50\u9a6c\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u5230\u9a91\u4e86\u8fd9\u5339\u9a6c\u9003\uff0c\u90a3\u5f53\u771f\u662f\u6700\u597d\u4e0d\u8fc7\uff0c\u4e8e\u662f \u624b\u6301\u5f20\u4e39\u67ab\u7684\u6247\u5b50\uff0c\u5c06\u767d\u9a6c\u9a6f\u670d\uff0c\u9a91\u4e86\u4fbf\u8d70\u3002 \u3000\u3000\u4e24\u59d0\u5f1f\u5408\u9a91\u5b9d\u9a6c\uff0c\u4e0d\u6d88\u4e09\u65e5\uff0c\u4fbf\u8fc7\u4e86\u7ea2\u5d16\u5761\uff0c\u5176\u65f6\u5929\u8272\u5df2\u9ed1\uff0c\u4e24\u4eba\u5230\u8c37\u5bb6\u5e84\u6295\u5bbf\uff0c\u5374\u4e0d \u77e5\u8c37\u5e84\u4e3b\u8c37\u4e2d\u8c6a\u4e43\u662f\u6ec7\u897f\u4e00\u9738\uff0c\u89c1\u4e86\u5b9d\u9a6c\uff0c\u5fc3\u5b58\u6518\u593a\uff0c\u613f\u4ee5\u9ec4\u91d1\u767e\u4e24\uff0c\u6362\u8fd9\u5339\u7167\u591c\u72ee\u5b50\uff0c\u6c90 \u71d5\u59d0\u5f1f\uff0c\u5f53\u7136\u4e0d\u80af\u3002\u8fd9\u4e2a\u8c37\u4e2d\u8c6a\u4e43\u662f\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u9274\u8c8c\u8fa8\u8272\uff0c\u731c\u60f3\u6c90\u71d5\u59d0\u5f1f\u4e43\u662f\u521d\u51fa\u9053 \u7684\u96cf\u513f\uff0c\u7528\u8bf4\u8bdd\u4e00\u69a8\uff0c\u767e\u822c\u76d8\u95ee\uff0c\u679c\u7136\u7ed9\u4ed6\u76d8\u95ee\u51fa\u4e86\u6b64\u9a6c\u5e76\u975e\u6c90\u71d5\u59d0\u5f1f\u4e4b\u7269\u3002\u8fd9\u4e00\u6765\u8c37\u4e2d\u8c6a \u66f4\u662f\u4e0d\u80af\u653e\u8fc7\uff0c\u518d\u76d8\u95ee\u6c90\u71d5\u59d0\u5f1f\u7684\u6765\u5386\uff0c\u6c90\u71d5\u59d0\u5f1f\u751f\u6015\u88ab\u9001\u56de\u56fd\u516c\u5e9c\u53bb\uff0c\u8fd9\u56de\u5374\u4e0d\u80af\u9732\u51fa\u534a \u70b9\u53e3\u98ce\u3002\u8c37\u4e2d\u8c6a\u8d77\u521d\u4ee5\u4e3a\u4ed6\u4eec\u662f\u521d\u51fa\u9053\u7684\u5c0f\u8d3c\uff0c\u540e\u6765\u89c1\u4ed6\u4eec\u4e3e\u6b62\u96cd\u5bb9\u9ad8\u8d35\uff0c\u5fc3\u4e2d\u731c\u7591\u4e0d\u5b9a\uff0c \u5012\u4e0d\u6562\u5c06\u4ed6\u4eec\u4e3a\u96be\u3002\u53ea\u662f\u5c06\u4ed6\u4eec\u8f6f\u7981\u8d77\u6765\uff0c\u4e00\u9762\u6d3e\u4eba\u5230\u6606\u660e\u6253\u542c\u3002\u90a3\u5339\u7167\u591c\u72ee\u5b50\u9a6c\u4e0d\u80af\u542c\u8c37 \u4e2d\u8c6a\u4f7f\u5524\uff0c\u597d\u51e0\u6b21\u8c37\u4e2d\u8c6a\u60f3\u9a91\u5b83\uff0c\u51e0\u4e4e\u88ab\u5b83\u9003\u8131\uff0c\u6709\u4e00\u6b21\u5728\u5e84\u524d\u8bd5\u9a91\uff0c\u6070\u597d\u94c1\u955c\u5fc3\u8def\u8fc7\u770b \u89c1\uff0c\u56e0\u800c\u60f9\u51fa\u4e86\u4eca\u65e5\u4e4b\u4e8b\u3002 \u3000\u3000\u4e14\u8bf4\u6c90\u78f7\u3001\u6c90\u71d5\u548c\u94c1\u955c\u5fc3\u89c1\u4e86\u9762\uff0c\u53cc\u65b9\u90fd\u4e0d\u8ba4\u8bc6\uff0c\u5927\u4e3a\u8be7\u5f02\uff0c\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u4f60\u4eec\u662f\u8c01\uff1f \u54ea\u513f\u5077\u6765\u7684\u8fd9\u5339\u767d\u9a6c\uff1f\u201d\u6c90\u71d5\u4e00\u51db\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u4e5f\u77e5\u9053\u6211\u662f\u5077\u7684\uff1f\u201d\u6c90\u78f7\u5374\u53d1\u4e86\u516c\u5b50\u813e \u6c14\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u5339\u9a6c\u4e0d\u662f\u6211\u7684\uff0c\u96be\u9053\u662f\u9601\u4e0b\u7684\u5417\uff1f\u8c01\u80fd\u9a91\u5b83\uff0c\u4fbf\u662f\u4e3b\u4eba\uff0c\u4f60\u4eec\u90fd\u60f3\u8981\u8fd9 \u5339\u9a6c\uff0c\u4f60\u4eec\u5c31\u4e00\u4e2a\u4e2a\u8bd5\u53bb\u9a91\u5b83\uff0c\u770b\u5b83\u7a76\u7adf\u670d\u8c01\uff1f\u201d \u3000\u3000\u94c1\u955c\u5fc3\u6014\u4e86\u4e00\u6014\uff0c\u4ed6\u4e0e\u4e8e\u627f\u73e0\u76f8\u5904\u8fc7\u591a\u65f6\uff0c\u81ea\u662f\u77e5\u9053\u8fd9\u5339\u5b9d\u9a6c\u7684\u7075\u5f02\u4e4b\u5904\uff0c\u5fc3\u9053\uff1a\u201c\u5bf9 \u554a\uff0c\u4ed6\u4eec\u600e\u4e48\u80fd\u9a91\u5f97\u4e86\u8fd9\u5339\u7167\u591c\u72ee\u5b50\uff1f\u201d \u3000\u3000\u6b63\u5f85\u76d8\u95ee\uff0c\u5ffd\u89c1\u4e24\u9a91\u5feb\u9a6c\u98de\u6765\uff0c\u8c37\u7532\u8c6a\u4e00\u58f0\u6b22\u547c\uff0c\u4e8e\u627f\u73e0\u5728\u5ca9\u77f3\u540e\u63a2\u5934\u4e00\u770b\uff0c\u6765\u7684\u7adf\u7136 \u662f\u9633\u5b97\u6d77\u548c\u76d8\u5929\u7f57\uff0c\u9633\u5b97\u6d77\u5728\u672a\u5165\u4eac\u5e08\u4f9b\u804c\u4e4b\u524d\uff0c\u79f0\u9738\u897f\u5357\uff0c\u4e0e\u5e08\u5144\u76d8\u5929\u7f57\u5230\u8fc7\u51e0\u6b21\u5927\u7406\uff0c \u4ed6\u4eec\u548c\u8c37\u4e2d\u8c6a\u90fd\u662f\u65e7\u76f8\u8bc6\u3002 \u3000\u3000\u9633\u5b97\u6d77\u53eb\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u5f97\u4e86\u4e00\u5339\u5b9d\u9a6c\u2026\u2026\u54a6\uff0c\u6c90\u5c0f\u516c\u7239\uff0c\u4f60\uff0c\u4f60\u5728\u8fd9\u513f\uff01\u201d\u8c37\u4e2d\u8c6a\u8dc3\u540e \u51e0\u6b65\uff0c\u8131\u51fa\u4e86\u94c1\u955c\u5fc3\u7684\u5a01\u80c1\uff0c\u6b63\u8dd1\u4e0a\u53bb\u8fce\u63a5\u9633\u5b97\u6d77\uff0c\u5ffd\u95fb\u6b64\u8bed\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f \u4ed6\u662f\u6c90\u5c0f\u516c\u7239\uff1f\u8fd9\u5339\u9a6c\u6b63\u662f\u4ed6\u9a91\u6765\u7684\uff01\u201d\u9633\u5b97\u6d77\u9053\uff1a\u201c\u6c90\u5c0f\u59d0\uff0c\u6c90\u516c\u5b50\uff0c\u4f60\u4eec\u79c1\u81ea\u9003\u8dd1\uff0c\u4e0d \u6015\u6025\u574f\u4e86\u516c\u7239\u4e48\uff1f\u201d\u53cc\u773c\u4e00\u626b\uff0c\u53c8\u53d1\u73b0\u4e86\u94c1\u955c\u5fc3\uff0c\u66f4\u662f\u60ca\u5947\uff0c\u53eb\u9053\uff1a\u201c\u94c1\u516c\u5b50\uff0c\u600e\u4e48\u4f60\u4e5f\u5230 \u4e86\u8fd9\u513f\uff1f\u201d\u8c37\u4e2d\u8c6a\u9053\uff1a\u201c\u6b64\u4eba\u4e09\u756a\u51e0\u6b21\u5230\u6211\u5e84\u4e0a\u80e1\u95f9\uff0c\u8981\u8ba8\u4ec0\u4e48\u4e8e\u76f8\u516c\uff0c\u53c8\u8981\u8ba8\u8fd9\u5339\u767d\u9a6c\uff0c \u600e\u4e48\uff1f\u4ed6\u662f\u4e0d\u662f\u4f60\u7684\u670b\u53cb\uff1f\u201d\u5fc3\u4e2d\u81ea\u5fd6\uff0c\u8981\u662f\u9633\u5b97\u6d77\u7684\u670b\u53cb\uff0c\u8fd9\u4ec7\u53ef\u96be\u62a5\u4e86\u3002 \u3000\u3000\u9633\u5b97\u6d77\u4ef0\u5929\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u94c1\u516c\u5b50\uff0c\u4f60\u4f55\u82e6\u5728\u6c5f\u6e56\u4e0a\u548c\u4e00\u4e9b\u53db\u515a\u80e1\u6df7\uff1f\u5c0a\u5927\u4eba\u6b63\u5728\u676d\u5dde \u629a\u8859\uff0c\u76fc\u4f60\u5f52\u53bb\u3002\u201d\u8f6c\u5934\u5bf9\u8c37\u4e2d\u8c6a\u9053\uff1a\u201c\u8c37\u5e84\u4e3b\u70e6\u4f60\u6d3e\u4eba\u5907\u9a6c\uff0c\u9001\u6c90\u5c0f\u516c\u7239\u548c\u5c0f\u59d0\u56de\u53bb\u3002\u8fd9 \u5339\u9a6c\u662f\u65e0\u4e3b\u4e4b\u9a6c\uff0c\u6211\u4e0d\u4e0e\u5927\u54e5\u5ba2\u6c14\u4e86\u3002\u201d\u9633\u5b97\u6d77\u773c\u89c1\u5fc3\u8c0b\uff0c\u8981\u593a\u201c\u7167\u591c\u72ee\u5b50\u201d\uff0c\u8c37\u4e2d\u8c6a\u6012\u6c14 \u4e0a\u51b2\uff0c\u5ffd\u800c\u4e00\u60f3\uff0c\u8fd9\u5339\u9a6c\u53cd\u6b63\u81ea\u5df1\u964d\u4f0f\u4e0d\u4e86\uff0c\u4e50\u5f97\u505a\u4e2a\u4eba\u60c5\uff0c\u9762\u8272\u4e00\u6362\uff0c\u4e0d\u6012\u53cd\u7b11\uff0c\u9053\uff1a \u201c\u5b9d\u5251\u8d60\u58ee\u58eb\uff0c\u540d\u9a6c\u8d60\u82f1\u96c4\u3002\u9633\u603b\u7ba1\u6b63\u597d\u914d\u8fd9\u5339\u795e\u9a79\u3002\u201d \u3000\u3000\u94c1\u955c\u5fc3\u5ffd\u5730\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9633\u5b97\u6d77\uff0c\u4f60\u4e5f\u60f3\u8981\u9a91\u8fd9\u5339\u9a6c\uff1f\u201d\u9633\u5b97\u6d77\u6b6a\u7740\u773c\u775b\u7b11\u9053\uff1a \u201c\u94c1\u516c\u5b50\uff0c\u6211\u4e0d\u5c06\u4f60\u4e0e\u53f6\u5b97\u7559\u80e1\u6df7\u4e4b\u4e8b\u62a5\u544a\u671d\u5ef7\uff0c\u603b\u7b97\u591f\u670b\u53cb\u4e86\u5427\uff1f\u9a6c\u53c8\u4e0d\u662f\u4f60\u7684\uff0c\u8fd9\u4efd\u4ea4 \u60c5\u4f60\u8fd8\u4e0d\u5356\uff1f\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u53ea\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u94c1\u955c\u5fc3\u5df2\u662f\u5530\u5730\u4e00\u5251\u523a\u5230\uff01 \u3000\u3000\u539f\u6765\u94c1\u955c\u5fc3\u81ea\u88ab\u5e08\u7236\u9010\u51fa\u95e8\u5899\u4e4b\u540e\uff0c\u81ea\u601d\u81ea\u60f3\uff0c\u8981\u4e0d\u662f\u81ea\u5df1\u5f53\u65f6\u88ab\u9633\u5b97\u6d77\u5a01\u80c1\uff0c\u529d\u5e08\u7236 \u5c06\u5b9d\u5251\u4ea4\u56de\u671d\u5ef7\uff0c\u4ea6\u4e0d\u81f3\u5982\u65af\u3002\u4ed6\u4e0d\u77e5\u81ea\u8d23\uff0c\u5374\u628a\u4e00\u8154\u6012\u6c14\u90fd\u53d1\u6cc4\u5230\u9633\u5b97\u6d77\u8eab\u4e0a\uff0c\u8fd9\u65f6\u501f\u6b64 \u9a6c\u4e3a\u7531\uff0c\u7acb\u523b\u4fbf\u4e0e\u9633\u5b97\u6d77\u52a8\u624b\u3002 \u3000\u3000\u9633\u5b97\u6d77\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u94c1\u516c\u5b50\uff0c\u4f60\u8fd9\u662f\u72d7\u54ac\u5415\u6d1e\u5bbe\uff0c\u4e0d\u8bc6\u597d\u4eba\u5fc3\u3002\u563f\uff0c\u563f\uff0c\u5200\u5251\u65e0\u60c5\uff0c\u4f60 \u5c0f\u5fc3\u4e86\uff01\u201d\u4ed6\u672c\u6765\u4e0d\u5c06\u94c1\u955c\u5fc3\u653e\u5728\u773c\u5185\uff0c\u54ea\u77e5\u94c1\u955c\u5fc3\u7adf\u662f\u5f62\u540c\u62fc\u547d\uff0c\u4e00\u5251\u7d27\u4f3c\u4e00\u5251\uff0c\u53ea\u542c\u5f97 \u5530\u7684\u4e00\u58f0\uff0c\u9633\u5b97\u6d77\u7684\u624b\u8155\u9669\u88ab\u523a\u4e2d\uff0c\u8896\u7ba1\u5148\u88ab\u524a\u4e86\u4e00\u622a\u3002\u9633\u5b97\u6d77\u52c3\u7136\u5927\u6012\uff0c\u60f3\u9053\uff1a\u201c\u4e0d\u7ed9\u4f60 \u70b9\u989c\u8272\uff0c\u4f60\u4e5f\u4e0d\u77e5\u9053\u6211\u7684\u5389\u5bb3\u3002\u6c90\u516c\u7239\u7684\u513f\u5b50\u6211\u4e0d\u6562\u4f24\uff0c\u4f60\u4e00\u4e2a\u9000\u804c\u5fa1\u53f2\u7684\u513f\u5b50\uff0c\u7ed9\u4f60\u6302\u70b9 \u82b1\u4e5f\u6ca1\u4ec0\u4e48\u5927\u4e0d\u4e86\u3002\u201d\u957f\u5251\u4e00\u5c55\uff0c\u5168\u529b\u5468\u65cb\uff0c\u53cc\u65b9\u90fd\u662f\u4e00\u6d41\u7684\u5251\u672f\uff0c\u4f46\u89c1\u5251\u5149\u98d8\u5ffd\uff01\u5251\u6c14\u7eb5 \u6a2a\uff0c\u6bd4\u8d77\u9002\u624d\u4e0e\u8c37\u4e2d\u8c6a\u4e4b\u6597\uff0c\u4f55\u6b62\u6fc0\u70c8\u5341\u500d\uff01 \u3000\u3000\u5ffd\u542c\u5f97\u9633\u5b97\u6d77\u7eb5\u58f0\u957f\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u94c1\u516c\u5b50\uff0c\u4f60\u8fd8\u8981\u518d\u6253\u5417\uff1f\u201d\u9aa4\u7136\u95f4\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u4e4b \u58f0\uff0c\u9707\u4eba\u8033\u819c\uff0c\u53ea\u89c1\u53cc\u5251\u76f8\u4ea4\uff0c\u706b\u661f\u56db\u6e85\uff0c\u4e24\u4eba\u8eab\u5f62\u500f\u5730\u5206\u5f00\uff0c\u94c1\u955c\u5fc3\u8e0f\u7740\u4e94\u884c\u516b\u5366\u65b9\u4f4d\uff0c \u8fde\u8fde\u540e\u9000\u3002\u539f\u6765\u4ed6\u7684\u9752\u94a2\u5251\u5df2\u7ed9\u9633\u5b97\u6d77\u524a\u53bb\u4e86\u4e00\u7247\u5251\u5c16\uff0c\u6210\u4e86\u949d\u5934\u5251\u4e86\u3002\u8981\u77e5\u9633\u5b97\u6d77\u540d\u5217\u5929 \u4e0b\u56db\u5927\u5251\u5ba2\u4e4b\u4e2d\uff0c\u867d\u8bf4\u6bd4\u8d77\u5f20\u4e39\u67ab\u3001\u4e4c\u8499\u592b\u7b49\u4eba\u90a3\u662f\u5927\u5927\u4e0d\u5982\uff0c\u4f46\u5728\u5251\u672f\u4e0a\u4e5f\u786e\u6709\u4e0d\u51e1\u7684\u9020 \u8be3\uff0c\u800c\u4e14\u5185\u529b\u6df1\u539a\uff0c\u8f83\u4e4b\u94c1\u955c\u5fc3\u81ea\u662f\u9ad8\u51fa\u4e00\u7b79\u3002\u4e8e\u627f\u73e0\u4ed7\u5b9d\u5251\uff0c\u51ed\u6697\u5668\uff0c\u5728\u6606\u660e\u4e4b\u65f6\uff0c\u4e5f\u4e0d \u8fc7\u4ec5\u4ec5\u548c\u4ed6\u6253\u4e2a\u5e73\u624b\uff0c\u94c1\u955c\u5fc3\u5728\u4e09\u5341\u62db\u4e4b\u524d\u8fd8\u53ef\u4ee5\u4e0e\u4ed6\u52c9\u5f3a\u5468\u65cb\uff0c\u4e09\u5341\u62db\u4e4b\u540e\uff0c\u5374\u5c31\u88ab\u8feb\u5904 \u4e0b\u98ce\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u9633\u5b97\u6d77\u524a\u6389\u4e86\u94c1\u955c\u5fc3\u7684\u5251\u5c16\uff0c\u66f4\u662f\u5168\u529b\u8fdb\u903c\u3002\u94c1\u955c\u5fc3\u6012\u706b\u4e2d\u70e7\uff0c\u8c41\u4e86\u6027\u547d\uff0c\u867d\u8d25 \u4e0d\u9981\uff0c\u5c55\u5f00\u4e86\u60ca\u6d9b\u5251\u6cd5\u4e2d\u7684\u7cbe\u5999\u62db\u672f\uff0c\u7f29\u5c0f\u5708\u5b50\uff0c\u5c45\u7136\u4ecd\u662f\u6709\u5b88\u6709\u653b\uff0c\u4e0d\u8fc7\u5df2\u662f\u5b88\u591a\u653b\u5c11 \u4e86\u3002\u4e8e\u627f\u73e0\u89e6\u76ee\u60ca\u5fc3\uff0c\u624b\u629a\u5251\u67c4\uff0c\u6b63\u5f85\u8dc3\u51fa\uff0c\u5ffd\u542c\u5f97\u6025\u4fc3\u7684\u811a\u6b65\u58f0\u5954\u6765\uff0c\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u53f6 \u6210\u6797\u5df2\u5230\u4e86\u80cc\u540e\uff0c\u8138\u4e0a\u73b0\u51fa\u8be7\u5f02\u4e4b\u5bb9\uff0c\u6307\u7740\u573a\u5fc3\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u90a3\u4e0d\u662f\u94c1\u955c\u5fc3\u5417\uff1f\u201d \u3000\u3000\u539f\u6765\u53f6\u6210\u6797\u5728\u4e0a\u9762\u7b49\u5f97\u4e0d\u8010\u70e6\uff0c\u53c8\u542c\u5f97\u53ae\u6740\u4e4b\u58f0\uff0c\u6545\u6b64\u8dd1\u4e0b\u6765\u770b\uff0c\u9661\u7136\u53d1\u73b0\u4e86\u94c1\u955c\u5fc3\u5728 \u573a\u4e2d\u53ae\u6740\uff0c\u5df2\u662f\u4e00\u5947\uff1b\u800c\u4eca\u77a7\u7740\u4e8e\u627f\u73e0\u4e00\u526f\u5931\u9b42\u4e27\u9b44\u7684\u6837\u5b50\uff0c\u66f4\u662f\u5927\u51fa\u610f\u5916\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a \u201c\u5979\u548c\u94c1\u955c\u5fc3\u4e43\u662f\u76f8\u4ea4\u5df2\u4e45\u7684\u670b\u53cb\uff0c\u65e5\u524d\u8fd8\u6bb7\u6bb7\u5411\u6211\u6253\u63a2\u4ed6\u7684\u6d88\u606f\u3002\u600e\u4e48\u73b0\u5728\u5374\u8896\u624b\u65c1 \u89c2\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u597d\u50cf\u4ece\u68a6\u4e2d\u88ab\u4eba\u60ca\u9192\uff0c\u629a\u5251\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u94c1\u955c\u5fc3\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u548c\u4ed6\u53ae\u6740 \u7684\u90a3\u4eba\u662f\u8c01\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5927\u5185\u603b\u7ba1\u9633\u5b97\u6d77\u3002\u201d\u53f6\u6210\u6797\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u5feb\u53bb \u52a9\u4ed6\u4e00\u81c2\u4e4b\u529b\u3002\u201d\u4e0d\u5f85\u4e8e\u627f\u73e0\u7b54\u8bdd\uff0c\u7acb\u523b\u98de\u6b65\u8d76\u4e0b\u5c71\u5761\u3002\u539f\u6765\u53f6\u6210\u6797\u4e3a\u4eba\u4ed4\u7ec6\uff0c\u5728\u672a\u77e5\u9053\u94c1 \u955c\u5fc3\u56e0\u4f55\u53ae\u6740\u4e4b\u524d\uff0c\u4e0d\u6562\u9c81\u83bd\u51fa\u624b\uff0c\u800c\u4eca\u4e00\u542c\u8bf4\u5bf9\u624b\u662f\u671d\u5ef7\u7684\u5927\u5185\u603b\u7ba1\uff0c\u90a3\u81ea\u662f\u4e0d\u5fc5\u518d\u95ee\u60c5 \u7531\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e2a\u65f6\u95f4\uff0c\u53ea\u542c\u5f97\u9633\u5b97\u6d77\u53c8\u662f\u54c8\u54c8\u5927\u7b11\uff1a\u201c\u5530\u201d\u5730\u4e00\u5251\u3002\u524a\u53bb\u4e86\u94c1\u955c\u5fc3\u5934\u4e0a\u7684\u65b9 \u5dfe\uff0c\u7eb5\u58f0\u53eb\u9053\uff1a\u201c\u94c1\u516c\u5b50\u4f60\u518d\u4e0d\u629b\u4e0b\u957f\u5251\u3002\u9633\u67d0\u53ef\u8981\u5f97\u7f6a\u5566\uff01\u201d\u53ea\u89c1\u4ed6\u5251\u8d70\u8fde\u73af\uff0c\u5c31\u8981\u75db\u4e0b \u6740\u624b\uff0c\u4e8e\u627f\u73e0\u4e00\u58f0\u5927\u53eb\uff0c\u8eab\u5f62\u75be\u8d77\uff0c\u5b9b\u5982\u5927\u9e1f\u817e\u7a7a\uff0c\u98de\u8eab\u63a0\u4e0b\uff0c\u540e\u53d1\u5148\u81f3\uff0c\u6bd4\u53f6\u6210\u6797\u8fd8\u5feb\u4e86 \u4e00\u6b65\uff0c\u5148\u5230\u8349\u576a\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u9aa4\u7136\u95f4\u542c\u5230\u4e8e\u627f\u73e0\u7684\u53eb\u5524\uff0c\u5fc3\u5934\u5927\u9707\uff0c\u767e\u5fd9\u4e4b\u4e2d\uff0c\u62bd\u773c\u4e00\u671b\uff0c\u4f46\u89c1\u4e00\u4e2a\u5982\u82b1\u4f3c\u7389 \u7684\u5c11\u5973\uff0c\u5df2\u662f\u5411\u7740\u81ea\u5df1\u5954\u6765\uff0c\u94c1\u955c\u5fc3\u8fd8\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\u4e8e\u627f\u73e0\u7684\u5973\u513f\u672c\u76f8\uff0c\u4e0d\u89c9\u5446\u4e86\uff0c\u9633\u5b97\u6d77 \u4e00\u523d\u523a\u6765\uff0c\u4ed6\u7adf\u7136\u5fd8\u4e86\u62db\u67b6\uff0c\u53ea\u542c\u5f97\u53c8\u662f\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u5de6\u8fb9\u80a9\u818a\uff0c\u5df2\u7ed9\u9633\u5b97\u6d77\u5212\u4e86\u4e00\u9053\u957f \u957f\u7684\u4f24\u53e3\uff01\u94c1\u955c\u5fc3\u597d\u4f3c\u6beb\u4e0d\u77e5\u9053\u75db\u695a\uff0c\u4f46\u89c1\u4ed6\u8eab\u8eaf\u6447\u6643\uff0c\u62fc\u547d\u4e00\u51b2\uff0c\u51b2\u5f00\u4e86\u9633\u5b97\u6d77\u5251\u5149\u7684\u7b3c \u7f69\uff0c\u8fce\u7740\u4e8e\u627f\u73e0\u5954\u53bb\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u627f\u73e0\uff0c\u627f\u73e0\uff01\u201d \u3000\u3000\u8fd9\u970e\u90a3\u95f4\uff0c\u4e8e\u627f\u73e0\u4f46\u89c9\u8f9b\u9178\u3001\u75db\u695a\u3001\u5173\u6000\u3001\u611f\u6fc0\u3001\u2026\u2026\u8bf8\u822c\u60c5\u611f\uff0c\u4e00\u9f50\u6d8c\u4e0a\u5fc3\u5934\u3002\u9aa4\u7136 \u95f4\u53c8\u542c\u5f97\u90a3\u5339\u7167\u591c\u72ee\u5b50\u9a6c\u7684\u5636\u9e23\u4e4b\u58f0\uff0c\u539f\u6765\u90a3\u5339\u767d\u9a6c\u4e00\u89c1\u4e3b\u4eba\u6765\u5230\uff0c\u7acb\u523b\u8dd1\u6765\u3002\u8c37\u4e2d\u8c6a\u56e0\u4e3a \u8fd9\u5339\u767d\u9a6c\u4e0d\u80af\u9a6f\u670d\uff0c\u5728\u5b83\u56db\u6761\u817f\u4e0a\uff0c\u90fd\u7f20\u4e0a\u7c97\u957f\u7684\u94c1\u94fe\uff0c\u53eb\u56db\u4e2a\u529b\u6c14\u5927\u7684\u5e84\u4e01\u7275\u7740\u94c1\u94fe\uff0c\u9632 \u5b83\u9003\u8d70\u3002\u54ea\u77e5\u5b83\u4ecd\u662f\u53d1\u529b\u5954\u8dd1\uff0c\u56db\u4e2a\u5e84\u4e01\uff0c\u90fd\u7ed9\u5b83\u62d6\u5012\u5730\u4e0a\uff0c\u4f46\u767d\u9a6c\u56db\u8db3\u4e5f\u88ab\u94c1\u94fe\u78e8\u635f\uff0c\u4e00 \u70b9\u70b9\u9c9c\u8840\u6ef4\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5bf9\u8fd9\u5339\u767d\u9a6c\uff0c\u7231\u5f97\u5982\u540c\u6027\u547d\uff0c\u600e\u5fcd\u89c1\u5b83\u53d7\u5982\u6b64\u6298\u78e8\uff1f\u4f46\u5979\u66f4\u4e0d\u5fcd\u5728\u8fd9\u4e2a\u65f6\u5019\uff0c\u5148 \u79bb\u5f00\u94c1\u955c\u5fc3\u53bb\u6551\u767d\u9a6c\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u9633\u5b97\u6d77\u5df2\u662f\u201c\u5530\u201d\u5730\u4e00\u5251\u63b7\u6765\uff0c\u4e8e\u627f\u73e0\u6b63\u5f85\u5c55\u5251\u62db \u67b6\uff0c\u5ffd\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u53f6\u6210\u6797\u4ece\u65c1\u653b\u4e0a\uff0c\u4e00\u7167\u9762\u5c31\u662f\u201c\u8fde\u73af\u53cc\u649e\u638c\u201d\u7684\u5927\u529b\u91d1\u94a2\u624b\u529f \u592b\uff0c\u9633\u5b97\u6d77\u51db\u7136\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u597d\u529f\u592b\uff01\u201d\u957f\u5251\u4e00\u7f29\uff0c\u8f6c\u4e86\u534a\u4e2a\u5f27\u5f62\uff0c\u5316\u89e3\u4e86\u53f6\u6210\u6797\u7684\u653b \u52bf\uff0c\u5251\u950b\u4e00\u98a4\uff0c\u4e00\u62db\u201c\u5954\u96f7\u95ea\u7535\u201d\uff0c\u5206\u523a\u53f6\u6210\u6797\u4e0e\u94c1\u955c\u5fc3\u4e24\u4eba\uff0c\u8fd9\u4e00\u62db\u653b\u5b88\u76f8\u9022\uff0c\u4e00\u6c14\u5475 \u6210\uff0c\u786e\u662f\u4e00\u6d41\u5251\u5ba2\u7684\u624b\u6cd5\u3002 \u3000\u3000\u53f6\u6210\u6797\u52a0\u4e5f\u50b2\u7136\u4e0d\u60e7\uff0c\u811a\u6b65\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u8fde\u63a5\u4e86\u9633\u5b97\u6d77\u4e09\u62db\uff0c\u4e8e\u627f\u73e0\u4e00\u770b\uff0c\u77e5\u9053\u5408\u53f6\u6210\u6797 \u4e0e\u94c1\u955c\u5fc3\u4e8c\u4eba\u4e4b\u529b\uff0c\u5c3d\u53ef\u62b5\u6321\u5f97\u4f4f\uff0c\u5bf9\u94c1\u955c\u5fc3\u77a5\u4e86\u4e00\u773c\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u597d\u751f\u62b5\u654c\uff0c\u6211\u6551\u4e86 \u767d\u9a6c\u5c31\u6765\uff01\u201d\u98de\u8eab\u4e00\u63a0\uff0c\u90a3\u5339\u767d\u9a6c\u4ea6\u5df2\u6765\u5230\u8ddf\u524d\uff0c\u4e8e\u627f\u73e0\u5b9d\u5251\u8fde\u6325\uff0c\u5c06\u56db\u6761\u94c1\u94fe\u5168\u90fd\u65a9\u65ad\uff0c \u90a3\u56db\u4e2a\u5728\u4e01\u5df2\u7ed9\u767d\u9a6c\u62d6\u5f97\u534a\u6b7b\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u9633\u5b97\u6d77\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u90fd\u662f\u94a6\u72af\uff0c\u4e0d\u53ef\u653e\u4ed6\u4eec\u9003\u4e86\u3002\u201d\u4ed6\u4e0e\u53f6\u6210\u6797\u62c6\u4e86\u5341\u4f59 \u62db\uff0c\u5df2\u662f\u8bc6\u7834\u4e86\u4ed6\u7684\u8eab\u4efd\uff0c\u5fc3\u4e2d\u771f\u662f\u53c8\u60ca\u53c8\u559c\uff0c\u559c\u8005\u662f\u674e\u6db5\u771f\u4e07\u91cc\u8ffd\u8e2a\u7684\u4eba\uff0c\u5374\u7ed9\u81ea\u5df1\u65e0\u610f \u4e4b\u4e2d\u5728\u8fd9\u91cc\u649e\u5230\uff08\u4ed6\u8fd8\u4e0d\u77e5\u674e\u6db5\u771f\u5df2\u5728\u77f3\u6797\u8eab\u6b7b\uff09\uff1b\u60ca\u8005\u662f\u53f6\u6210\u6797\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5c45\u7136\u6709\u8fd9\u6837\u786c \u7684\u529f\u592b\uff0c\u7adf\u4e0d\u5728\u94c1\u955c\u5fc3\u4e4b\u4e0b\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u624b\u629a\u9a6c\u80cc\uff0c\u6b63\u5f85\u56de\u8eab\u8fce\u6218\uff0c\u5ffd\u542c\u5f97\u4e00\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e8e\u5c0f\u59d0\uff0c\u4f60\u7684\u5e08\u7236\u5462\uff1f\u54c8\uff0c \u8fd9\u56de\u53ef\u6ca1\u6709\u4eba\u6551\u4f60\u4e86\uff01\u201d\u58f0\u53d1\u4eba\u5230\uff0c\u4e00\u6761\u4eba\u5f71\u6296\u8d77\u957f\u97ad\uff0c\u500f\u5730\u51cc\u7a7a\u626b\u4e0b\uff0c\u4e8e\u627f\u73e0\u559d\u9053\uff1a\u201c\u4f11 \u5f97\u4f24\u4e86\u6211\u7684\u5b9d\u9a6c\uff01\u201d\u9752\u51a5\u5251\u626c\u7a7a\u75be\u5c55\uff0c\u53ee\u5f53\u4e00\u58f0\uff0c\u4e8e\u627f\u73e0\u4f46\u89c9\u4e00\u80a1\u5927\u529b\u626b\u6765\uff0c\u4e0d\u7531\u81ea\u5df1\u5730\u5012 \u9000\u4e09\u6b65\uff0c\u539f\u6765\u6765\u7684\u4eba\u6b63\u662f\u9633\u5b97\u6d77\u7684\u5e08\u5144\u76d8\u5929\u7f57\uff0c\u4ed6\u662f\u8d64\u971e\u9053\u4eba\u7684\u9996\u5f92\uff0c\u529f\u529b\u8fdc\u5728\u9633\u5b97\u6d77\u4e4b \u4e0a\u3002 \u3000\u3000\u90a3\u5339\u767d\u9a6c\u89c1\u4e3b\u4eba\u5371\u9669\uff0c\u626c\u8e44\u4fbf\u8e22\uff0c\u76d8\u5929\u7f57\u559d\u9053\uff1a\u201c\u755c\u751f\uff0c\u4f60\u627e\u6b7b\u4e48\uff1f\u201d\u5de6\u624b\u4e00\u6309\u9a6c\u5934\uff0c \u90a3\u5339\u767d\u9a6c\u524d\u8e44\u534a\u5c48\uff0c\u5374\u5e76\u672a\u88ab\u4ed6\u6309\u5012\uff0c\u4e8e\u627f\u73e0\u4e00\u62db\u201c\u5999\u89e3\u8fde\u73af\u201d\uff0c\u5b9d\u5251\u4e00\u65cb\uff0c\u6b3a\u8eab\u76f4\u4e0a\uff0c\u76d8 \u5929\u7f57\u653e\u5f00\u4e86\u9a6c\uff0c\u6325\u97ad\u8fce\u6218\uff0c\u4e8e\u627f\u73e0\u6120\u5507\u957f\u5578\uff0c\u53eb\u9053\uff1a\u201c\u9a6c\u513f\uff0c\u4f60\u8dd1\u5230\u5c71\u5761\u7b49\u6211\u3002\u201d\u90a3\u5339\u9a6c\u751a \u901a\u4eba\u6027\uff0c\u679c\u7136\u4e00\u6323\u8131\u4fbf\u8dd1\uff0c\u53ea\u89c1\u573a\u4e2d\u53c8\u98de\u6b65\u5954\u51fa\u4e24\u4eba\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u662f\u6c90\u78f7\u548c\u6c90\u71d5\uff0c\u4ed6\u4eec\u60f3\u8d81\u6df7\u6218\u4e4b\u9645\uff0c\u9a91\u4e0a\u767d\u9a6c\u9003\u8d70\uff0c\u76d8\u5929\u7f57\u5982\u4f55\u80af\u653e\u8d70\u4ed6\u4eec\uff0c \u53ea\u89c1\u4ed6\u8eab\u5f62\u4e00\u6643\uff0c\u4eba\u8fd8\u672a\u5230\uff0c\u957f\u97ad\u5df2\u53d1\uff0c\u547c\u5730\u4e00\u5377\uff0c\u4e00\u68f5\u5927\u6811\u7adf\u7ed9\u957f\u97ad\u8dcb\u4e86\u8d77\u6765\uff0c\u6070\u597d\u62e6\u7740 \u4e86\u6c90\u78f7\u3001\u6c90\u71d5\u7684\u53bb\u8def\uff0c\u76d8\u5929\u7f57\u563f\u563f\u7b11\u9053\uff1a\u201c\u5c0f\u516c\u7239\u4f11\u8981\u4e71\u8dd1\uff0c\u7b49\u4e0b\u5b50\u54b1\u4eec\u540c\u56de\u6606\u660e\u3002\u201d\u8c37\u5c0f \u5e84\u4e3b\u7387\u9886\u5bb6\u4e01\u5c06\u6c90\u78f7\u3001\u6c90\u71d5\u56f4\u7740\uff0c\u8fd9\u65f6\u4ed6\u4eec\u5df2\u77e5\u9053\u4e86\u6c90\u78f7\u3001\u6c90\u71d5\u7684\u8eab\u4efd\uff0c\u4e0d\u6562\u52a8\u7c97\uff0c\u606d\u606d\u656c \u656c\u5730\u8bf4\u9053\uff1a\u201c\u8bf7\u5c0f\u516c\u7239\u548c\u5c0f\u59d0\u56de\u5e84\u3002\u201d\u6c90\u78f7\u9053\uff1a\u201c\u6211\u504f\u8981\u5728\u8fd9\u91cc\u770b\u70ed\u95f9\u3002\u201d\u8c37\u5c0f\u5e84\u4e3b\u4f46\u8981\u4ed6 \u4e0d\u518d\u9003\u8d70\uff0c\u4e8e\u613f\u5df2\u8db3\uff0c\u4e0d\u6562\u591a\u8bf4\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u8d76\u4e0a\u6765\u60f3\u63a5\u5e94\u6c90\u78f7\u3001\u6c90\u71d5\uff0c\u5374\u662f\u8fdf\u4e86\u4e00\u6b65\u3002\u4e8e\u627f\u73e0\u5927\u6012\uff0c\u626c\u624b\u4fbf\u662f\u4e94\u6735\u91d1\u82b1\uff0c\u76d8 \u5929\u7f57\u957f\u97ad\u98de\u821e\uff0c\u6c34\u6cfc\u4e0d\u8fdb\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u53ee\u53ee\u4e4b\u58f0\uff0c\u6709\u5982\u7e41\u5f26\u6025\u594f\uff0c\u4e94\u6735\u91d1\u82b1\u90fd\u88ab\u62e8\u843d\uff0c\u4e8e\u627f \u73e0\u4e0d\u53ca\u518d\u53d1\uff0c\u957f\u97ad\u5df2\u970d\u5730\u5377\u6765\uff0c\u4e8e\u627f\u73e0\u60f3\u7528\u5b9d\u5251\u524a\u5b83\uff0c\u76d8\u5929\u7f57\u7684\u957f\u97ad\u4f7f\u5f97\u7075\u6d3b\u975e\u5e38\uff0c\u82d1\u5982\u6570 \u5341\u6761\u957f\u86c7\u4ece\u56db\u65b9\u516b\u9762\u98de\u6765\uff0c\u4e8e\u627f\u73e0\u5b9d\u5251\u867d\u5229\uff0c\u65ad\u4e0d\u80fd\u4e00\u4e3e\u5c06\u5b83\u524a\u4e3a\u6570\u6bb5\uff0c\u7eb5\u7136\u80fd\u524a\u53bb\u4e00\u622a\uff0c \u8fd8\u662f\u4f1a\u88ab\u4ed6\u7684\u957f\u97ad\u5708\u4f4f\u3002\u4e8e\u627f\u73e0\u65e0\u6cd5\uff0c\u53ea\u597d\u4e0d\u6c42\u6709\u529f\uff0c\u5148\u6c42\u65e0\u8fc7\uff0c\u4ed7\u7740\u7cbe\u5999\u7684\u5251\u6cd5\u81ea\u4fdd\uff0c\u514d \u5f97\u9732\u51fa\u7a7a\u95e8\u3002 \u3000\u3000\u90a3\u5339\u767d\u9a6c\u542c\u8bdd\u5f97\u5f88\uff0c\u8dd1\u5230\u5c71\u4e0b\uff0c\u4fbf\u505c\u4e86\u4e0b\u6765\uff0c\u7fd8\u9996\u626c\u8e44\uff0c\u6709\u5982\u4eba\u7acb\uff0c\u7ade\u4f3c\u5173\u5fc3\u4e3b\u4eba\u7684\u5b89 \u5371\uff0c\u5728\u90a3\u91cc\u51fa\u795e\u89c2\u6218\u3002\u8c37\u4e2d\u8c6a\u5fc3\u4e2d\u75d2\u75d2\u7684\uff0c\u771f\u60f3\u8d76\u53bb\uff0c\u6349\u8fd9\u5339\u767d\u9a6c\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u6349\u5230\u4e86\u4e5f \u662f\u9633\u5b97\u6d77\u7684\uff0c\u4f55\u82e6\u7ed9\u4ed6\u5356\u529b\uff0c\u628a\u773c\u4e00\u77a7\uff0c\u53ea\u89c1\u9633\u5b97\u6d77\u7ed9\u53f6\u6210\u6797\u4e0e\u94c1\u955c\u5fc3\u4e8c\u4eba\u8054\u624b\u7a81\u653b\uff0c\u6e10\u5904 \u4e0b\u98ce\uff0c\u9633\u5b97\u6d77\u53eb\u9053\uff1a\u201c\u8c37\u5e84\u4e3b\uff0c\u8fd9\u662f\u4f60\u7ed9\u671d\u5ef7\u7acb\u529f\u7684\u673a\u4f1a\u4e86\u3002\u201d\u8c37\u4e2d\u8c6a\u5bb6\u8d22\u4e07\u8d2f\uff0c\u5012\u4e0d\u5728\u4e4e \u4e00\u5b98\u534a\u804c\uff0c\u4f46\u4ed6\u88ab\u94c1\u955c\u5fc3\u523a\u4e86\u4e09\u5251\uff0c\u5728\u4e00\u4f17\u5bb6\u4e01\u4e4b\u524d\uff0c\u8981\u5411\u94c1\u955c\u5fc3\u670d\u8f93\u6c42\u9976\uff0c\u8fd9\u53e3\u6c14\u5374\u662f\u96be \u6d88\u3002\u4e00\u542c\u9633\u5b97\u6d77\u6c42\u63f4\uff0c\u4e5f\u4e50\u5f97\u5356\u4e2a\u4eba\u60c5\uff0c\u62fe\u8d77\u4e86\u539a\u80cc\u65ab\u5c71\u5200\u4e0a\u524d\u52a9\u6218\uff0c\u5200\u5200\u5288\u5411\u94c1\u955c\u5fc3\u7684\u8981 \u5bb3\u3002\u8c37\u4e2d\u8c6a\u672c\u9886\u867d\u7136\u8f83\u5dee\uff0c\u4f46\u52a0\u4e0a\u9633\u5b97\u6d77\u8fd9\u6837\u4e00\u4e2a\u5f3a\u624b\uff0c\u548c\u53f6\u3001\u94c1\u4e24\u4eba\u521a\u597d\u65d7\u9f13\u76f8\u5f53\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u72ec\u6218\u76d8\u5929\u7f57\u5374\u662f\u5403\u529b\u975e\u5e38\uff0c\u5e78\u800c\u5979\u7684\u7384\u673a\u5251\u6cd5\uff0c\u4e43\u662f\u5929\u4e0b\u7b2c\u4e00\u7b49\u7684\u5251\u6cd5\uff0c\u4f7f\u5c06\u5f00 \u6765\uff0c\u7aef\u7684\u53d8\u5e7b\u65e0\u65b9\uff0c\u5947\u8be1\u767e\u51fa\uff0c\u5c24\u5176\u53ea\u5b88\u4e0d\u653b\uff0c\u66f4\u96be\u627e\u51fa\u7834\u7efd\u3002\u76d8\u5929\u7f57\u5728\u8feb\u5207\u4e4b\u95f4\uff0c\u5374\u4e5f\u5948 \u4f55\u4e0d\u4e86\u5979\u3002\u6c90\u78f7\u3001\u6c90\u71d5\u5168\u795e\u89c2\u6218\uff0c\u5374\u662f\u5404\u6709\u6240\u6ce8\u3002\u770b\u5230\u7d27\u5f20\u4e4b\u5904\uff0c\u6c90\u78f7\u559d\u5f69\u53eb\u9053\uff1a\u201c\u59d0\u59d0\u5feb \u77a7\uff0c\u59d1\u5a18\u8fd9\u4e00\u5251\u7aef\u7684\u7f8e\u5999\u7edd\u4f26\uff0c\u5440\uff0c\u53ef\u60dc\uff0c\u53ef\u60dc\uff0c\u54ce\u54df\uff0c\u8fd9\u4e00\u97ad\u597d\u9669\u54ea\uff01\u597d\uff0c\u597d\uff0c\u5e78\u4e8f\u907f\u8fc7 \u4e86\uff01\u201d\u6c90\u71d5\u7684\u76ee\u5149\u5374\u8ddf\u7740\u94c1\u955c\u5fc3\uff0c\u5979\u5f1f\u5f1f\u5927\u547c\u5c0f\u53eb\uff0c\u5979\u53ea\u662f\u542b\u7cca\u7b54\u5e94\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u5440\uff0c\u8fd9 \u4e00\u5251\u624d\u53eb\u5999\u5462\uff0c\u4f60\u770b\u4e00\u62db\u2018\u9e70\u51fb\u957f\u7a7a\u2019\u5c31\u628a\u5927\u5200\u7684\u2018\u4e09\u73af\u5957\u6708\u2019\u7834\u89e3\u4e86\uff01\u201d\u6c90\u78f7\u9053\uff1a\u201c\u4ec0 \u4e48\uff1f\u8fd9\u4e00\u62db\u5e08\u7236\u7ed9\u54b1\u4eec\u8bb2\u89e3\u8fc7\uff0c\u54ea\u91cc\u662f\u2018\u9e70\u51fb\u957f\u7a7a\u2019\uff0c\u8fd9\u4e0d\u5206\u660e\u662f\u7389\u5973\u6295\u68ad\u5417\uff1f\u201d\u4ed6\u4e0d\u77e5\u9053 \u59d0\u59d0\u8bf4\u7684\u662f\u94c1\u955c\u5fc3\uff0c\u800c\u4ed6\u8bf4\u7684\u662f\u4e8e\u627f\u73e0\uff0c\u81ea\u7136\u662f\u725b\u5934\u4e0d\u5bf9\u9a6c\u5634\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u529b\u6218\u5f3a\u654c\uff0c\u8eab\u6cd5\u4ecd\u662f\u6f47\u6d12\u4e4b\u6781\uff0c\u6c90\u71d5\u770b\u5f97\u51fa\u795e\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u53ea\u9053\u4ee5\u5f20\u5927\u4fa0\u7684 \u5251\u6cd5\u5982\u795e\uff0c\u4e16\u4e0a\u65e0\u4eba\u80fd\u53ca\uff0c\u5374\u539f\u6765\u8fd8\u6709\u4eba\u6bd4\u5f97\u4e0a\u4ed6\u3002\u201d\u5176\u5b9e\u4ee5\u94c1\u955c\u5fc3\u4e0e\u5f20\u4e39\u67ab\u76f8\u8f83\uff0c\u90a3\u81ea\u662f \u76f8\u5dee\u751a\u8fdc\uff0c\u4e0d\u8fc7\u4e24\u4eba\u90fd\u662f\u4e66\u751f\u672c\u8272\uff0c\u94c1\u955c\u5fc3\u53c8\u6b63\u662f\u4e8c\u5341\u521a\u521a\u51fa\u5934\u7684\u7f8e\u5c11\u5e74\uff0c\u5728\u6c90\u71d5\u773c\u4e2d\uff0c\u66f4 \u662f\u64a9\u4eba\u6ce8\u76ee\u3002 \u3000\u3000\u4e24\u59d0\u5f1f\u5404\u81ea\u5168\u795e\u89c2\u6218\uff0c\u5fc3\u6709\u6240\u601d\uff0c\u770b\u5230\u7d27\u5f20\u60ca\u9669\u4e4b\u5904\uff0c\u90fd\u4e0d\u7981\u5931\u58f0\u53eb\u5524\u3002\u5ffd\u542c\u5f97\u8fdc\u5904\u9a6c \u5636\uff0c\u90a3\u5339\u7167\u591c\u72ee\u5b50\u9a6c\u4e5f\u7a81\u7136\u5636\u9e23\u8d77\u6765\uff0c\u4e8e\u627f\u73e0\u6014\u4e86\u4e00\u6014\uff0c\u53ea\u89c1\u90a3\u5339\u767d\u9a6c\u8dc3\u4e0b\u5c71\u5761\uff0c\u8df3\u4e0a\u5b98 \u9053\uff0c\u5982\u98de\u5954\u8dd1\uff0c\u5fc3\u4e2d\u5927\u5947\uff0c\u4e00\u4e2a\u758f\u795e\uff0c\u51e0\u4e4e\u7ed9\u76d8\u5929\u7f57\u7684\u957f\u97ad\u626b\u4e2d\u3002 \u3000\u3000\u767d\u9a6c\u53bb\u5f97\u5feb\uff0c\u6765\u5f97\u66f4\u5feb\uff0c\u9661\u7136\u95f4\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u5927\u559d\uff0c\u58f0\u5982\u9739\u96f3\uff0c\u53ea\u89c1\u4e00\u4e2a\u78a7\u773c\u9ec4\u987b\u7684\u5927 \u6c49\uff0c\u8c8c\u4f3c\u80e1\u4eba\uff0c\u8eab\u62ab\u9501\u5b50\u9ec4\u91d1\u7532\uff0c\u624b\u63d0\u53cc\u9f99\u62a4\u624b\u94a9\uff0c\u9a91\u5728\u767d\u9a6c\u80cc\u4e0a\uff0c\u5982\u98de\u5954\u81f3\uff01\u4e8e\u627f\u73e0\u5927\u559c \u53eb\u9053\uff1a\u201c\u6fb9\u53f0\u4f2f\u4f2f\uff01\u201d\u539f\u6765\u6765\u7684\u4eba\u6b63\u662f\u5f20\u4e39\u67ab\u7684\u5bb6\u5c06\u6fb9\u53f0\u706d\u660e\uff0c\u4ea6\u662f\u5b98\u5929\u91ce\u7684\u5927\u5f1f\u5b50\uff0c\u8bba\u8d77 \u8f88\u4efd\uff0c\u6bd4\u5f20\u4e39\u67ab\u8fd8\u9ad8\u51fa\u4e00\u8f88\u3002\u4ed6\u672c\u662f\u6c49\u4eba\uff0c\u53ea\u56e0\u4e16\u4ee3\u5c45\u4f4f\u8499\u65b9\uff0c\u6545\u6b64\u8c8c\u4f3c\u80e1\u4eba\u3002\u6fb9\u53f0\u706d\u660e\u6b64 \u65f6\u5df2\u662f\u671b\u516d\u4e4b\u5e74\uff0c\u4ecd\u7136\u77eb\u5065\u65e0\u6bd4\uff0c\u53ea\u542c\u4ed6\u4e00\u58f0\u5927\u559d\uff0c\u7eb5\u9a6c\u72c2\u5954\uff0c\u5f53\u8005\u8f9f\u6613\uff0c\u76d8\u5929\u7f57\u90a3\u4e00\u97ad\u521a \u8981\u6253\u4e0b\uff0c\u7ed9\u4ed6\u4e00\u559d\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u6025\u5fd9\u56de\u97ad\u62db\u67b6\uff0c\u53ea\u89c1\u4e24\u9053\u91d1\u9ec4\u8272\u7684\u94a9\u5149\uff0c\u500f\u7136\u5288\u4e0b\uff0c\u76d8\u5929\u7f57 \u7684\u957f\u97ad\u4e00\u4e0b\u5b50\u4fbf\u88ab\u9501\u4f4f\uff0c\u6fb9\u53f0\u706d\u660e\u5927\u559d\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u80c6\u6562\u6b3a\u4fae\u6211\u7684\u4f84\u5973\u3002\u201d\u76d8\u5929\u7f57\u7528\u529b \u4e00\u593a\uff0c\u597d\u4e0d\u5bb9\u6613\u593a\u4e86\u51fa\u6765\uff0c\u957f\u97ad\u5df2\u65ad\u4e86\u4e00\u622a\u3002\u4e8e\u627f\u73e0\u53eb\u9053\uff1a\u201c\u8fd9\u53ae\u662f\u8d64\u971e\u9053\u4eba\u7684\u95e8\u5f1f\uff0c\u5c61\u6b21 \u6b3a\u8d1f\u4e8e\u6211\uff0c\u6fb9\u53f0\u4f2f\u4f2f\uff0c\u4f60\u7ed9\u6211\u5728\u4ed6\u8eab\u4e0a\u7559\u4e00\u4e2a\u8bb0\u53f7\uff01\u201d\u6fb9\u53f0\u706d\u660e\u559d\u4e00\u58f0\u201c\u597d\uff01\u201d\u53cc\u94a9\u970d\u970d\uff0c \u8fde\u73af\u75be\u8fdb\uff0c\u526a\u3001\u624e\u3001\u62bd\u3001\u64a4\uff0c\u6070\u5982\u9a87\u7535\u60ca\u9706\uff0c\u4e24\u9053\u91d1\u86c7\uff0c\u8d34\u7740\u76d8\u5929\u7f57\u7684\u8eab\u5b50\u98de\u821e\uff0c\u8f6c\u77ac\u4e4b \u95f4\uff0c\u53ea\u89c1\u6fb9\u53f0\u706d\u660e\u53cc\u94a9\u4e00\u5408\uff0c\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u76d8\u5929\u7f57\u7684\u952f\u9f7f\u97ad\u53c8\u88ab\u526a\u65ad\u4e00\u622a\uff0c\u4e08\u8bb8\u957f\u7684\u957f \u97ad\uff0c\u6298\u4e0b\u56db\u5c3a\u4e0d\u5230\uff0c\u76d8\u5929\u7f57\u9b42\u98de\u9b44\u6563\uff0c\u64a4\u97ad\u4fbf\u9003\u3002\u6fb9\u53f0\u706d\u660e\u559d\u9053\uff1a\u201c\u770b\u4f60\u7684\u5e08\u7236\u548c\u7384\u673a\u524d\u8f88 \u7684\u4ea4\u60c5\u9762\u5b50\uff0c\u9976\u4f60\u4e0d\u6b7b\uff0c\u8bb0\u4e0b\u6765\u4e86\uff01\u201d\u94a9\u5149\u4e00\u95ea\uff0c\u76d8\u5927\u7f57\u54ea\u8eb2\u5f97\u5f00\uff0c\u8033\u6735\u7adf\u7ed9\u786c\u751f\u751f\u7684\u6495 \u4e0b\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u8f6c\u8eab\u60f3\u52a9\u94c1\u955c\u5fc3\uff0c\u90a3\u9633\u5b97\u6d77\u5374\u662f\u6e9c\u6ed1\u5f97\u5f88\uff0c\u4e00\u89c1\u6fb9\u53f0\u706d\u660e\u8fdb\u573a\uff0c\u52bf\u5934\u4e0d\u5bf9\uff0c\u5148\u81ea \u9003\u8d70\u4e86\u3002\u94c1\u955c\u5fc3\u6b63\u5728\u8ffd\u8d76\uff0c\u89c1\u4e8e\u627f\u73e0\u8fc7\u6765\uff0c\u500f\u5730\u505c\u4e86\u811a\u6b65\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\uff0c\u4f60\u597d\uff01\u201d \u4e8e\u627f\u73e0\u6de1\u6de1\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u5230\u4e91\u5357\u505a\u4ec0\u4e48\uff1f\u201d\u94c1\u955c\u5fc3\u51c9\u4e86\u534a\u622a\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u4e07\u91cc\u8ffd \u8e2a\uff0c\u96be\u9053\u4f60\u4e0d\u77e5\u9053\u6211\u7684\u5fc3\u601d\uff1f\u201d\u4f46\u5f53\u7740\u4f17\u4eba\uff0c\u5982\u53c8\u600e\u597d\u5411\u4e8e\u627f\u73e0\u503e\u8bc9\u81ea\u5df1\u7684\u5fc3\u610f\uff1f\u5c34\u5c2c\u4e00 \u7b11\uff0c\u9053\uff1a\u201c\u542c\u8bf4\u5f20\u5927\u4fa0\u5728\u5927\u7406\u2026\u2026\u201d\u53f6\u6210\u6797\u77a7\u770b\u4e24\u4eba\u795e\u60c5\u5947\u602a\uff0c\u63d2\u53e3\u8bf4\u9053\uff1a\u201c\u5bf9\u554a\uff0c\u60f3\u6765\u94c1 \u5144\u4e5f\u662f\u53bb\u627e\u5f20\u5927\u4fa0\u7684\u4e86\uff0c\u54b1\u4eec\u6b63\u597d\u540c\u8def\u3002\u201d\u5e78\u800c\u6709\u53f6\u6210\u6797\u7684\u8a00\u8bed\u89e3\u56f4\uff0c\u94c1\u955c\u5fc3\u795e\u8272\u624d\u60ed\u590d\u6b63 \u5e38\u3002\u4f46\u89c1\u4e8e\u627f\u73e0\u6001\u5ea6\u51b7\u6de1\uff0c\u597d\u4f3c\u6545\u610f\u4e0d\u776c\u81ea\u5df1\uff0c\u53cd\u800c\u53bb\u9017\u53f6\u6210\u6797\u8bf4\u8bdd\uff0c\u5fc3\u4e2d\u53c8\u662f\u65e0\u9650\u8f9b\u9178\u3002 \u3000\u3000\u6b64\u65f6\u6fb9\u53f0\u706d\u660e\u65e9\u5df2\u628a\u8c37\u5bb6\u5e84\u7684\u5e84\u4e01\u9a71\u6563\uff0c\u5c06\u6c90\u78f7\u3001\u6c90\u71d5\u6551\u51fa\u6765\u3002\u539f\u6765\u90a3\u65e5\u5f20\u4e39\u67ab\u5927\u95f9\u56fd \u516c\u5e9c\u4e4b\u540e\uff0c\u6253\u63a2\u51fa\u6c90\u71d5\u59d0\u5f1f\u9003\u8d70\u7684\u4e8b\uff0c\u6599\u5230\u4ed6\u4eec\u5fc5\u662f\u524d\u6765\u5927\u7406\u8981\u8ddf\u968f\u81ea\u5df1\uff0c\u53ef\u662f\u7b49\u4e86\u591a\u65e5\u8fd8 \u672a\u89c1\u4ed6\u4eec\u6765\u5230\u3002\u5f20\u4e39\u67ab\u751f\u6015\u4ed6\u4eec\u5728\u8def\u4e0a\u51fa\u4e8b\uff0c\u6545\u6b64\u53eb\u6fb9\u53f0\u706d\u660e\u524d\u6765\uff0c\u6cbf\u9014\u6253\u63a2\u4ed6\u4eec\u7684\u6d88\u606f\u3002 \u6fb9\u53f0\u706d\u660e\u662f\u5f20\u4e39\u67ab\u7684\u5bb6\u5c06\uff0c\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50\u9a6c\u201d\u81ea\u7136\u542c\u4ed6\u4f7f\u5524\u3002 \u3000\u3000\u8c37\u4e2d\u8c6a\u7236\u5b50\u4e00\u89c1\u52bf\u5934\u4e0d\u5bf9\uff0c\u4fbf\u5df2\u9003\u5165\u5e84\u4e2d\uff0c\u5728\u5e84\u524d\u6492\u4e0b\u94c1\u9ece\uff0c\u5173\u95ed\u5e84\u95e8\uff0c\u51c6\u5907\u8fce\u654c\u3002\u6fb9 \u53f0\u706d\u660e\u5411\u6c90\u71d5\u59d0\u5f1f\u95ee\u660e\u539f\u59d4\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6b64\u4eba\u662f\u6ec7\u897f\u4e00\u9738\uff0c\u8fd9\u6b21\u5c45\u7136\u80af\u4ee5\u9ec4\u91d1\u767e\u4e24\u6362\u53d6\u4f60 \u7684\u5b9d\u9a6c\uff0c\u867d\u8bf4\u662f\u6709\u773c\u65e0\u73e0\uff0c\u4f46\u8fd8\u4e0d\u7b97\u662f\u7a77\u51f6\u6781\u6076\uff0c\u54b1\u4eec\u5c31\u9976\u4e86\u4ed6\u5427\u3002\u201d\u4e8e\u627f\u73e0\u5f97\u56de\u5b9d\u9a6c\uff0c\u559c \u51fa\u671b\u5916\uff0c\u4e00\u5fc3\u60f3\u5feb\u4e0a\u82cd\u5c71\u62dc\u89c1\u5e08\u7236\u548c\u592a\u5e08\u7956\uff0c\u4ea6\u4e0d\u613f\u518d\u5728\u8c37\u5bb6\u5e84\u803d\u6401\u3002 \u3000\u3000\u6c90\u78f7\u3001\u6c90\u71d5\u8131\u56f0\u4e4b\u540e\uff0c\u6025\u5fd9\u8dd1\u6765\u4e0e\u4e8e\u627f\u73e0\u3001\u94c1\u955c\u5fc3\u76f8\u89c1\u3002\u4e8e\u627f\u73e0\u6b63\u60f3\u6446\u8131\u94c1\u955c\u5fc3\u7684\u7ea0 \u7f20\uff0c\u8fce\u4e0a\u524d\u9053\uff1a\u201c\u5c0f\u516c\u7239\uff0c\u591a\u8c22\u4f60\u554a\uff01\u201d\u6c90\u78f7\u53d7\u5ba0\u82e5\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u6765\u6551\u6211\uff0c\u6211\u624d\u8981\u591a\u8c22\u4f60 \u5462\uff01\u4f60\u591a\u8c22\u6211\u4ec0\u4e48\uff1f\u201d\u4e00\u5f20\u5b69\u5b50\u6c14\u7684\u8138\u4e0a\uff0c\u9732\u51fa\u53c8\u6b22\u559c\u53c8\u60f6\u6050\u7684\u60c5\u7eea\uff0c\u4e8e\u627f\u73e0\u5657\u55e4\u4e00\u7b11\uff0c \u9053\uff1a\u201c\u4f60\u66ff\u6211\u7239\u7239\u5efa\u5e99\u9020\u50cf\uff0c\u6211\u600e\u4e48\u4e0d\u8981\u591a\u8c22\u4f60\u3002\u201d\u6c90\u78f7\u9053\uff1a\u201c\u4ee4\u5c0a\u8d64\u80c6\u5fe0\u5fc3\uff0c\u4e3a\u56fd\u51a4\u6b7b\uff0c \u5929\u4e0b\u540c\u94a6\u3002\u5efa\u5e99\u9020\u50cf\u8fd8\u4e0d\u8db3\u8868\u793a\u6211\u5fc3\u4e2d\u7684\u656c\u6155\u4e8e\u4e07\u4e00\uff0c\u4e8e\u5c0f\u59d0\u4f60\u628a\u5b83\u5f53\u4f5c\u4e00\u6869\u4e8b\u60c5\u63d0\u8d77\u6765\uff0c \u4ee4\u6211\u8d8a\u53d1\u60ed\u6127\u5566\u3002\u201d\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u4f60\u7684\u52c7\u6c14\u603b\u662f\u503c\u5f97\u4f69\u670d\u3002\u4f60\u7ed9\u56fd\u516c\u7239\u8d23\u7f5a\u7684 \u4e8b\u60c5\uff0c\u6211\u90fd\u77e5\u9053\u5566\uff01\u201d\u6c90\u78f7\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u8bf4\u5b9e\u5728\u8bdd\uff0c\u6211\u4e5f\u6709\u70b9\u80c6\u602f\uff0c\u5168\u4e8f\u6211\u7684\u59d0 \u59d0\uff0c\u8981\u4e0d\u662f\u5979\u66ff\u6211\u58ee\u80c6\uff0c\u8fd9\u6b21\u6211\u4e5f\u4e0d\u6562\u9003\u51fa\u6765\u3002\u54c8\uff0c\u4f60\u4e0d\u77e5\u9053\u6211\u59d0\u59d0\u6700\u4f1a\u505a\u4f5c\uff0c\u54c4\u5f97\u6211\u7239\u76f8 \u4fe1\u5979\uff0c\u4ee5\u4e3a\u5979\u4e0d\u4f1a\u95f9\u4e8b\uff0c\u5e73\u65f6\u603b\u662f\u8981\u5979\u7ba1\u6559\u6211\u3002\u54c8\uff0c\u5176\u5b9e\u5979\u7684\u80c6\u5b50\u6bd4\u6211\u8fd8\u8981\u5927\uff0c\u4e0d\u8fc7\u5979\u603b\u662f \u8eb2\u5728\u80cc\u540e\uff0c\u63a8\u6211\u51fa\u5934\u5c31\u662f\u4e86\u3002\u201d\u6c90\u78f7\u8d77\u521d\u60f3\u5b66\u5927\u4eba\u7684\u53e3\u543b\u548c\u4e8e\u627f\u73e0\u8bf4\u5ba2\u6c14\u8bdd\uff0c\u8bf4\u5440\u8bf4\u7684\uff0c\u7ec8 \u4e8e\u8fd8\u662f\u9732\u51fa\u5b69\u5b50\u6c14\u6765\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5fcd\u4f4f\u4e86\u7b11\uff0c\u4e0e\u6c90\u71d5\u62db\u547c\uff0c\u9053\uff1a\u201c\u90a3\u65e5\u59d0\u59d0\u6d3e\u9063\u91d1\u5a25\u53ec\u6211\uff0c\u53ef\u60dc\u6211\u6765\u5f97\u8fdf\u4e86\u3002\u201d\u6c90 \u71d5\u9053\uff1a\u201c\u90a3\u65e5\u4e4b\u4e8b\uff0c\u5192\u6627\u4e4b\u6781\uff0c\u59d0\u59d0\u52ff\u602a\u3002\u6211\u4e00\u5fc3\u60f3\u89c1\u59d0\u59d0\uff0c\u8c01\u77e5\u4e34\u65f6\u51fa\u4e86\u5c94\u5b50\uff0c\u597d\u5728\u5982\u4eca \u8fd8\u662f\u89c1\u7740\u4e86\u3002\u201d\u5979\u53e3\u8bf4\u4e00\u5fc3\u60f3\u89c1\u4e8e\u627f\u73e0\uff0c\u773c\u775b\u5374\u6697\u6697\u5730\u6e9c\u7740\u94c1\u955c\u5fc3\u3002\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u94c1 \u516c\u5b50\uff0c\u90fd\u5fa1\u53f2\u94c1\u94f1\u7684\u5c11\u7237\u3002\u201d\u94c1\u955c\u5fc3\u7709\u5934\u4e00\u76b1\uff0c\u53ea\u542c\u5f97\u6c90\u71d5\u8bf4\u9053\uff1a\u201c\u554a\uff0c\u90a3\u662f\u5f53\u5e74\u53c2\u52ab\u8fc7\u5978 \u5ba6\u738b\u632f\u7684\u94c1\u5fa1\u53f2\u4e86\uff0c\u6211\u7239\u7239\u4e5f\u66fe\u63d0\u8fc7\u94c1\u5927\u4eba\u7684\u3002\u4e45\u4ef0\u4e86\u3002\u201d\u94c1\u955c\u5fc3\u542c\u5979\u5938\u8d5e\u81ea\u5df1\u7684\u7236\u4eb2\uff0c\u5fc3 \u4e2d\u6b22\u559c\uff0c\u53ea\u6c82\u5f97\u6c90\u71d5\u53c8\u9053\uff1a\u201c\u591a\u8c22\u94c1\u516c\u5b50\u548c\u4e8e\u5c0f\u59d0\u8fd9\u6b21\u51fa\u529b\u76f8\u6551\uff0c\u54ce\u54df\uff0c\u94c1\u516c\u5b50\u8fd8\u53d7\u4e86\u4f24 \u5462\u3002\u201d\u5ffd\u5730\u60f3\u8d77\u8fd8\u6709\u4e00\u4f4d\u53f6\u6210\u6797\uff0c\u5fd8\u8bb0\u62db\u547c\uff0c\u987a\u53e3\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u8fd9\u4f4d\u5927\u54e5\uff0c\u4e5f\u4e00\u5e76\u591a\u8c22 \u4e86\u3002\u201d\u53f6\u6210\u6797\u6beb\u65e0\u82a5\u8482\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u8d70\u5f00\u4e00\u8fb9\uff0c\u81ea\u53bb\u548c\u6fb9\u53f0\u706d\u660e\u8bf4\u8bdd\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u542c\u5f97\u6c90\u71d5\u53e3\u53e3\u58f0\u58f0\u8c22\u4ed6\u76f8\u6551\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u4f60\u88ab\u5173\u5728\u8c37\u5bb6\u5e84\uff0c\u8fd9 \u8bdd\u4ece\u54ea\u91cc\u8bf4\u8d77\uff1f\u201d\u4f46\u6709\u4eba\u76f8\u8c22\uff0c\u5fc3\u4e2d\u603b\u662f\u9ad8\u5174\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4e00\u70b9\u8f7b\u4f24\uff0c\u4e0d\u7b97\u4ec0\u4e48\u3002\u201d\u6c90\u71d5 \u201c\u54a6\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8fd8\u8bf4\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u4f60\u77a7\u8840\u8fd8\u6ca1\u6709\u6b62\u5462\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u6211\u6709\u91d1\u521b\u836f\uff0c\u518d \u6577\u4e00\u70b9\u4fbf\u6ca1\u4e8b\u4e86\u3002\u6c90\u5c0f\u59d0\uff0c\u8fd9\u786e\u5b9e\u4e0d\u7b97\u4ec0\u4e48\uff0c\u4f60\u4e0d\u77e5\u9053\uff0c\u6211\u4ee5\u524d\u5728\u53f0\u5dde\u6cbf\u6d77\u6297\u502d\u4e4b\u65f6\uff0c\u51e0\u4e4e \u5929\u5929\u6d41\u8840\uff0c\u90a3\u624d\u771f\u662f\u60e8\u70c8\u5462\u3002\u6709\u4e00\u6b21\u6211\u548c\u51e0\u4e2a\u65e5\u672c\u4e03\u6bb5\u516b\u6bb5\u7684\u6b66\u58eb\u62fc\u529b\u3002\u6211\u7684\u80f3\u818a\u51e0\u4e4e\u7ed9\u4ed6 \u4eec\u5288\u65ad\uff0c\u5e78\u4e8f\u6211\u8eb2\u95ea\u5f97\u5feb\uff0c\u7ec8\u4e8e\u8fd8\u662f\u628a\u4ed6\u4eec\u6253\u8d25\u4e86\u3002\u201d\u6c90\u71d5\u9732\u51fa\u65e0\u9650\u94a6\u4f69\u7684\u795e\u60c5\uff0c\u9053\uff1a\u201c\u662f \u4e48\uff1f\u94c1\u516c\u5b50\u771f\u662f\u5e74\u5c11\u82f1\u96c4\u3002\u55ef\uff0c\u4ec0\u4e48\u53eb\u505a\u4e03\u6bb5\u516b\u6bb5\uff1f\u522b\u52a8\uff0c\u522b\u52a8\uff0c\u6211\u66ff\u4f60\u5305\u624e\u3002\u201d\u4e00\u9762\u8bf4 \u8bdd\uff0c\u4e00\u9762\u9676\u51fa\u4e1d\u7ee2\uff0c\u66ff\u94c1\u955c\u5fc3\u5305\u624e\u4f24\u53e3\uff0c\u94c1\u955c\u5fc3\u7531\u4e8e\u627f\u73e0\u51b7\u6de1\uff0c\u4e00\u53e3\u6c14\u6b63\u81ea\u96be\u54bd\uff0c\u8fd9\u65f6\u5fc3\u4e2d \u751c\u4e1d\u4e1d\u7684\uff0c\u60f3\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u4e0d\u7406\u6211\uff0c\u6709\u4eba\u5374\u4e89\u7740\u7406\u6211\u5462\u3002\u5979\u8fd8\u662f\u516c\u4faf\u7684\u5343\u91d1\u5c0f\u59d0\uff0c\u4e5f\u4e0d\u89c1\u6709 \u4f60\u8fd9\u4e48\u5927\u7684\u67b6\u5b50\u3002\u201d\u4ed6\u672c\u6765\u662f\u60f3\u63a8\u8f9e\u7684\uff0c\u7ec8\u4e8e\u8fd8\u662f\u8ba9\u6c90\u71d5\u7ed9\u4ed6\u5305\u624e\u4e86\u3002\u4ed6\u4e5f\u6709\u610f\u6c14\u6c14\u4e8e\u627f \u73e0\uff0c\u6c90\u71d5\u95ee\u4e00\u53e5\uff0c\u4ed6\u7b54\u5341\u53e5\uff0c\u7ed9\u5979\u8bf4\u5f53\u65f6\u6297\u502d\u7684\u6545\u4e8b\uff0c\u628a\u81ea\u5df1\u63cf\u7ed8\u5f97\u597d\u50cf\u662f\u6297\u502d\u4e49\u519b\u4e2d\u9996\u5c48 \u4e00\u6307\u7684\u82f1\u96c4\u3002 \u3000\u3000\u54ea\u77e5\u4e8e\u627f\u73e0\u4e00\u70b9\u4e5f\u4e0d\u751f\u6c14\uff0c\u53ea\u662f\u94c1\u955c\u5fc3\u7684\u8bed\u8a00\u548c\u6001\u5ea6\uff0c\u5374\u628a\u5979\u5f15\u5165\u66f4\u6df1\u6c89\u5730\u601d\u7d22\u4e2d\uff0c\u5979 \u597d\u50cf\u66f4\u6df1\u523b\u5730\u770b\u5230\u4e86\u94c1\u955c\u5fc3\u7075\u9b42\u7684\u6df1\u5904\u3002\u5979\u5ffd\u7136\u60f3\u8d77\u4e86\u53f6\u5b97\u7559\uff0c\u53f6\u5b97\u7559\u662f\u6297\u502d\u7684\u67f1\u77f3\uff0c\u8c01\u7684 \u529f\u52b3\u90fd\u6ca1\u4ed6\u5927\uff0c\u4f46\u53f6\u5b97\u7559\u5c31\u4ece\u6765\u6ca1\u6709\u534a\u53e5\u8bdd\u5938\u8000\u8fc7\u81ea\u5df1\u3002\u5979\u7684\u773c\u5149\u53c8\u843d\u5728\u53f6\u6210\u6797\u8eab\u4e0a\uff0c\u53f6\u6210 \u6797\u4e5f\u5e72\u4e86\u4e0d\u5c11\u5927\u4e8b\uff0c\u4e5f\u66fe\u5e2e\u4ed6\u53d4\u53d4\u505a\u8fc7\u5584\u540e\u7684\u5de5\u4f5c\uff0c\u4e00\u8def\u540c\u884c\uff0c\u4e5f\u672a\u66fe\u542c\u4ed6\u534a\u53e5\u8c08\u8fc7\u81ea\u5df1\u3002 \u90a3\u66fe\u7ecf\u5728\u5979\u8111\u6d77\u4e2d\u6d6e\u6c89\u8fc7\u7684\u8054\u60f3\uff0c\u73b0\u5728\u662f\u66f4\u52a0\u9c9c\u660e\u4e86\uff1a\u201c\u55ef\uff0c\u4e00\u4e2a\u662f\u6c5f\u5357\u56ed\u6797\u4e2d\u7684\u73ab\u7470\u82b1\uff1b \u4e00\u4e2a\u662f\u4e91\u8d35\u9ad8\u539f\u4e0a\u7684\u5927\u9752\u6811\u3002\u73ab\u7470\u82b1\u53ea\u5411\u5bcc\u8d35\u4e2d\u4eba\u5c3d\u91cf\u5c55\u793a\u767d\u5df1\uff1b\u5927\u9752\u6811\u5374\u6c38\u8fdc\u662f\u9ed8\u9ed8\u65e0\u8a00 \u5730\u836b\u5e87\u7740\u6765\u5f80\u7684\u65c5\u4eba\u3002\u201d\u8fd9\u662f\u4e24\u79cd\u622a\u7136\u4e0d\u540c\u7684\u98ce\u683c\uff0c\u4e8e\u627f\u73e0\u73b0\u5728\u662f\u770b\u5f97\u6e05\u695a\u4e86\u3002\u5979\u6709\u4e00\u70b9\u70b9 \u618e\u538c\uff0c\u7136\u800c\u66f4\u611f\u5230\u8f9b\u9178\uff0c\u5c31\u597d\u50cf\u5ffd\u7136\u53d1\u73b0\u81ea\u5df1\u5fc3\u7231\u7684\u73cd\u73e0\u9879\u94fe\u4e43\u662f\u5047\u7684\u4e00\u6837\u3002\u7136\u800c\u5979\u8fd8\u662f\u5fcd \u4e0d\u4f4f\u5411\u94c1\u955c\u5fc3\u518d\u770b\u4e86\u4e00\u773c\uff0c\u4ed6\u4eca\u65e5\u7684\u53d7\u4f24\u8fd8\u662f\u4e3a\u4e86\u81ea\u5df1\u5475\uff01\u7136\u800c\u4e5f\u4e0d\u8fc7\u662f\u4ec5\u4ec5\u4e00\u773c\uff0c\u5f53\u5979\u7684 \u773c\u5149\u548c\u94c1\u955c\u5fc3\u76f8\u63a5\uff0c\u5979\u611f\u5230\u94c1\u955c\u5fc3\u6d0b\u6d0b\u81ea\u5f97\u597d\u50cf\u8981\u95ee\u5979\u70ab\u8000\u7684\u5fc3\u60c5\uff0c\u5979\u53c8\u628a\u773c\u5149\u79fb\u5f00\u4e86\u3002 \u3000\u3000\u6c90\u78f7\u9053\uff1a\u201c\u59d0\u59d0\u4f60\u60f3\u4ec0\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6ca1\u60f3\u4ec0\u4e48\u3002\u6211\u53ea\u60f3\u5feb\u70b9\u5230\u5927\u7406\u53bb\u89c1\u5e08\u7236\u3002\u201d \u6c90\u78f7\u9053\uff1a\u201c\u662f\u5475\uff0c\u6211\u4e5f\u60f3\u5feb\u70b9\u89c1\u5230\u4ed6\u8001\u4eba\u5bb6\u5462\u3002\u201d\u6fb9\u53f0\u706d\u660e\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u5c31\u5feb\u70b9\u8d70\u5440\uff01\u201d\u4e8e \u627f\u73e0\u4ecd\u628a\u7167\u591c\u72ee\u5b50\u9a6c\u8ba9\u7ed9\u6c90\u71d5\u59d0\u5f1f\u9a91\uff0c\u6c90\u71d5\u4e0d\u80af\uff0c\u8bf4\u662f\u8f76\u955c\u5fc3\u53d7\u4f24\uff0c\u4e00\u5b9a\u8981\u8ba9\u94c1\u955c\u5fc3\u9a91\u9a6c\uff0c \u7ec8\u4e8e\u662f\u94c1\u955c\u5fc3\u9a91\u4e86\u90a3\u5339\u6ce2\u65af\u7684\u9ec4\u7f25\u9a6c\uff0c\u6c90\u71d5\u9a91\u201c\u7167\u591c\u72ee\u5b50\u201d\uff0c\u6c90\u78f7\u5374\u81ea\u613f\u6b65\u884c\uff0c\u966a\u4e8e\u627f\u73e0\u3002 \u3000\u3000\u4ece\u7ea2\u5d16\u5761\u5230\u5927\u7406\uff0c\u4e0d\u5230\u4e09\u767e\u91cc\u8def\uff0c\u82e5\u4ee5\u7167\u591c\u72ee\u5b50\u9a6c\u7684\u811a\u529b\uff0c\u4e0d\u9700\u534a\u65e5\u4fbf\u53ef\u8d70\u5230\u3002\u4f46\u56e0\u6709 \u4eba\u4e58\u5750\u5e73\u5e38\u7684\u9a6c\u5339\uff0c\u6709\u4eba\u6b65\u884c\uff0c\u5c24\u5176\u6c90\u5c0f\u516c\u7239\u4e0d\u60ef\u884c\u8d70\u5c71\u8def\uff0c\u5374\u5b9a\u8981\u966a\u4e8e\u627f\u73e0\u6b65\u884c\uff0c\u6545\u6b64\u5728 \u9014\u4e2d\u53c8\u6b47\u5bbf\u4e00\u5bb5\u3002\u8fd9\u4e00\u665a\u4e8e\u627f\u73e0\u867d\u662f\u65c5\u9014\u52b3\u987f\uff0c\u4ecd\u7136\u7ffb\u6765\u8986\u53bb\u7761\u4e0d\u7740\u89c9\uff0c\u94c1\u955c\u5fc3\u548c\u53f6\u6210\u6797\u7684 \u5f71\u5b50\u4ea4\u66ff\u7684\u5728\u5979\u8111\u6d77\u4e2d\u6d6e\u73b0\uff0c\u5979\u60f3\u5f97\u5f88\u591a\u5f88\u591a\uff0c\u5979\u5728\u6c5f\u6e56\u4e0a\u7ecf\u5386\u4e86\u4e00\u4e24\u5e74\uff0c\u601d\u60f3\u5df2\u662f\u6e10\u6e10\u6210 \u719f\uff0c\u8fdc\u975e\u5bfb\u5e38\u7684\u521a\u6ee1\u5341\u516d\u5c81\u7684\u5c11\u5973\u53ef\u6bd4\u4e86\u3002\u6c90\u78f7\u90a3\u5e26\u7740\u7a1a\u6c14\u7684\u9762\u5b54\uff0c\u4e5f\u5076\u5c14\u5728\u94c1\u3001\u53f6\u4e8c\u4eba\u7684 \u5f71\u5b50\u4e2d\u95f4\u7a7f\u63d2\u8fdb\u6765\uff0c\u5979\u6709\u8fd9\u6837\u7684\u611f\u89c9\uff0c\u6c90\u78f7\u867d\u8bf4\u5e74\u7eaa\u548c\u5979\u76f8\u82e5\uff0c\u5728\u5979\u7684\u773c\u4e2d\uff0c\u5374\u548c\u5c0f\u864e\u5b50\u5dee \u4e0d\u591a\uff0c\u60f3\u8d77\u4ed6\u5e7c\u7a1a\u7684\u795e\u60c5\uff0c\u4e8e\u627f\u73e0\u4e0d\u7981\u6697\u6697\u53d1\u7b11\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\u8d77\u6765\uff0c\u8d70\u8fc7\u4e86\u4e00\u6bb5\u5c71\u8def\uff0c\u4e2d\u5348\u65f6\u5206\uff0c\u8f6c\u51fa\u5c71\u5773\uff0c\u4fbf\u671b\u89c1\u4e00\u5ea7\u58a8\u84dd\u8272\u7684\u50cf\u662f\u4ece \u5730\u5e95\u7a81\u7136\u6d8c\u51fa\u7684\u9ad8\u5c71\u5dcd\u7136\u8038\u7acb\u9762\u524d\uff0c\u5f00\u59cb\u53ea\u89c1\u5c71\u5cf0\uff0c\u6e10\u6e10\u8d70\u5230\u5c71\u811a\uff0c\u770b\u5230\u5c71\u811a\u7684\u65f6\u5019\uff0c\u5728\u5c71 \u7684\u4e1c\u9762\u4e5f\u770b\u5230\u4e86\u88ab\u9633\u5149\u7167\u5f97\u8000\u773c\u7684\u6e56\u6c34\uff0c\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u4e0b\u53bb\u4fbf\u662f\u4e0b\u5173\uff0c\u63a5\u7740\u4fbf\u5230\u5927\u7406\u4e86\u3002 \u4f60\u770b\u8fd9\u4fbf\u662f\u6709\u540d\u7684\u82cd\u5c71\u548c\u6d31\u6d77\u4e86\u3002\u627f\u73e0\uff0c\u4eca\u665a\uff0c\u4f60\u53ef\u4ee5\u89c1\u5230\u5e08\u7236\u5566\u3002\u201d \u3000\u3000\u4f17\u4eba\u52a0\u5feb\u811a\u6b65\uff0c\u5230\u4e86\u4e0b\u5173\uff0c\u82cd\u5c71\u548c\u6d31\u6d77\u7684\u9762\u76ee\uff0c\u5b8c\u5168\u8c41\u9732\uff0c\u201c\u4e0b\u5173\u201d\u5750\u843d\u5728\u82cd\u5c71\u548c\u6d31\u6d77 \u7684\u5357\u8fb9\uff0c\u4f9d\u508d\u7740\u82cd\u5c71\u5341\u4e5d\u5cf0\u5357\u7aef\u6700\u672b\u4e00\u5cf0\u7684\u659c\u9633\u5cf0\uff0c\u9762\u4e34\u6d31\u6d77\u7684\u4e00\u7aef\uff0c\u4ece\u6d31\u6d77\u6cfb\u51fa\u6765\u7684\u6c34\uff0c \u5c31\u7ed5\u8fc7\u8fd9\u5ea7\u5c0f\u57ce\uff0c\u7a7f\u8fc7\u4e00\u4e2a\u5c71\u53e3\uff0c\u6d41\u5165\u6f3e\u5824\u6cb3\uff0c\u5230\u4e86\u4e0b\u5173\uff0c\u5927\u98ce\u9661\u8d77\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u6d31\u6d77\u4e00\u671b \u65e0\u9645\u7684\u851a\u84dd\u6d77\u6c34\uff0c\u6380\u8d77\u4e86\u5954\u817e\u7684\u6ce2\u6d9b\uff0c\u6d6a\u82b1\u5377\u7740\u70df\u96fe\uff0c\u968f\u7740\u98de\u821e\uff0c\u8fd9\u60c5\u666f\u4ee4\u4e8e\u627f\u73e0\u60f3\u8d77\u4e86\u5728 \u53f0\u5dde\u7684\u6d77\u8fb9\u770b\u843d\u65e5\uff0c\u5ffd\u7136\u64a9\u8d77\u4e86\u9635\u9635\u60c5\u601d\u3002\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u4e0b\u5173\u98ce\uff0c\u4e0a\u5173\u82b1\uff0c\u82cd\u5c71\u96ea\uff0c\u6d31\u6d77 \u6708\u3002\u8fd9\u662f\u5927\u7406\u8457\u540d\u7684\u98ce\u82b1\u96ea\u6708\u56db\u666f\uff0c\u4f60\u4eec\u82e5\u662f\u6015\u98ce\uff0c\u53ef\u4ee5\u5230\u6c11\u5c45\u6682\u907f\u3002\u4e0b\u5173\u7684\u98ce\u5f88\u5947\u602a\uff0c\u98ce \u4ece\u5c4b\u9876\u63a0\u8fc7\uff0c\u5c31\u662f\u6253\u5f00\u7a97\u5b50\uff0c\u5b83\u4e5f\u5439\u4e0d\u8fdb\u5c4b\u4e2d\u3002\u201d\u4e8e\u627f\u73e0\u6025\u7740\u8981\u89c1\u5e08\u7236\uff0c\u7b11\u9053\uff1a\u201c\u98ce\u4e2d\u770b\u6d77 \u666f\uff0c\u522b\u6709\u97f5\u5473\uff0c\u6211\u4eec\u8fd8\u662f\u8d70\u5427\u3002\u201d\u6c90\u78f7\u8d5e\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u771f\u662f\u96c5\u4eba\u3002\u201d\u8fd9\u65f6\u5df2\u662f\u51c9\u79cb\u4e5d\u6708\u7684 \u5b63\u8282\uff0c\u4f46\u4e2d\u5348\u65f6\u5206\uff0c\u5929\u6c14\u8fd8\u662f\u6696\u6d0b\u6d0b\u7684\u5982\u540c\u521d\u590f\uff0c\u8857\u5934\u5c1a\u6709\u547c\u5524\u5356\u96ea\u7684\u5c0f\u8d29\uff0c\u6c90\u71d5\u62bf\u5634\u4e00 \u7b11\uff0c\u5bf9\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u6b64\u5730\u98ce\u7269\uff0c\u6bd4\u8d77\u6c5f\u5357\u5982\u4f55\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u5404\u6709\u5404\u7684\u597d\u5904\uff0c\u6211\u770b\u60ef\u4e86\u6c5f \u5357\u7684\u666f\u8272\uff0c\u53cd\u800c\u66f4\u559c\u6b22\u8fd9\u513f\u3002\u201d\u6c90\u71d5\u9053\uff1a\u201c\u6211\u5c0f\u65f6\u5019\u5ff5\u8fc7\u4e00\u9996\u300a\u5356\u96ea\u8bcd\u300b\uff0c\u662f\u4e00\u4e2a\u5927\u7406\u7684\u548c \u5c1a\u5199\u7684\uff0c\u8bd7\u9053\uff1a\u2018\u53cc\u9f99\u5173\u91cc\u767e\u82b1\u9999\uff0c\u94f6\u6d77\u900f\u9038\u70b9\u82cd\u5c71\uff0c\u516d\u6708\u8857\u5934\u53eb\u5356\u96ea\uff0c\u884c\u4eba\u9519\u8ba4\u662f\u743c \u6d46\u3002\u2019\u8fd9\u9996\u8bd7\u4e0b\u6709\u6ce8\u8bf4\uff1a\u2018\u5927\u7406\u82cd\u5c71\u96ea\u516d\u6708\u4e0d\u5316\uff0c\u5e02\u4e0a\u5356\u4e4b\uff0c\u72b9\u5434\u4e0b\u4e4b\u5356\u51b0\u4e5f\u3002\u2019\u90a3\u4e48\u4f60\u4eec \u90a3\u8fb9\u4e5f\u6709\u5356\u51b0\u7684\u4e86\uff0c\u60c5\u666f\u6bd4\u8fd9\u91cc\u5982\u4f55\uff1f\u201d\u94c1\u955c\u5fc3\u7b11\u9053\uff1a\u201c\u82cf\u676d\u5e02\u51b5\u5c18\u56a3\uff0c\u6ca1\u6709\u8fd9\u91cc\u8d28\u6734\u6e05\u96c5 \u7684\u60c5\u8c03\u3002\u201d\u6c90\u71d5\u597d\u50cf\u6478\u719f\u4e86\u94c1\u955c\u5fc3\u7684\u6027\u683c\uff0c\u4e00\u8def\u4e0a\u548c\u4ed6\u8c08\u8bd7\u8bba\u6587\uff0c\u94c1\u955c\u5fc3\u4e5f\u89c9\u5f97\u8fd9\u4e2a\u4faf\u95e8\u5c0f \u59d0\uff0c\u5c45\u7136\u4e0d\u4fd7\uff0c\u867d\u7136\u4e0d\u80fd\u5728\u4ed6\u5fc3\u4e2d\u66ff\u4ee3\u4e8e\u627f\u73e0\uff0c\u8c08\u5f97\u5012\u4e5f\u6295\u673a\u3002 \u3000\u3000\u8fc7\u4e86\u4e0b\u5173\uff0c\u98ce\u5e73\u6d6a\u9759\uff0c\u4e00\u671b\u6d31\u6d77\u53c8\u662f\u4e00\u756a\u666f\u8272\uff0c\u4f46\u89c1\u6e56\u5149\u4f3c\u955c\uff08\u4e91\u5357\u4eba\u60ef\u628a\u5927\u6e56\u79f0\u4e3a \u201c\u6d77\u201d\uff0c\u6d31\u6d77\u5b9e\u662f\u5185\u9646\u7684\u5927\u6e56\uff09\uff0c\u6e56\u9762\u4e0a\u5e06\u5f71\u70b9\u70b9\uff0c\u4ee4\u4eba\u89c9\u5f97\u5b81\u9759\u5e7d\u7f8e\uff0c\u6e56\u5cb8\u904d\u690d\u5782\u6768\uff0c\u7ec6 \u5ae9\u7684\u679d\u6761\uff0c\u98d8\u6d6e\u6c34\u9762\uff0c\u6c34\u9e1f\u5580\u9e23\uff0c\u6d77\u9e25\u98de\u7fd4\uff0c\u666f\u7269\u5982\u8bd7\u4f3c\u753b\u3002\u6c90\u78f7\u53c8\u7b11\u9053\uff1a\u201c\u4e8e\u5c0f\u59d0\u8bf4\u5728\u98ce \u4e2d\u770b\u6d77\u522b\u9976\u97f5\u5473\uff0c\u6211\u770b\u78a7\u6c34\u65e0\u6ce2\uff0c\u66f4\u662f\u53e6\u6709\u4f73\u8da3\u3002\u6709\u4e00\u9996\u8bd7\u5199\u6d31\u6d77\u65e0\u6ce2\u7684\u60c5\u666f\u9053\uff1a\u2018\u5ba0\u96c1\u63a5 \u789f\u83f1\u8367\u5149\uff0c\u7fe1\u7fe0\u6447\u6ce2\u5170\u82d5\u9999\u3002\u53e4\u82bd\u53cc\u6797\u5e26\u70df\u90ed\uff0c\u5e73\u6e56\u5341\u91cc\u901a\u6625\u822a\u3002\u8fdc\u68a6\u4f3c\u66fe\u7ecf\u6b64\u5730\uff0c\u6e38\u5b50\u604d \u7591\u5f52\u6545\u4e61\u3002\u6d31\u6d77\u6cdb\u821f\u770b\u660e\u6708\uff0c\u6d6e\u840d\u6897\u6cdb\u60b2\u82cd\u832b\u3002\u6bd4\u5bf9\u773c\u524d\u7684\u666f\u8272\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\u5999\u7edd\u3002\u54b1\u4eec\u518d \u9009\u4e2a\u660e\u6708\u4e4b\u591c\uff0c\u5728\u6d31\u6d77\u6cdb\u821f\uff0c\u90a3\u5c31\u66f4\u6709\u610f\u601d\u4e86\u3002\u201d\u94c1\u955c\u5fc3\u7b11\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u6e38\u5b50\u604d\u7591\u5f52\u6545\u4e61\uff0c \u5230\u4e86\u8fd9\u513f\uff0c\u6211\u771f\u4e0d\u60f3\u8d70\u4e86\uff01\u201d \u3000\u3000\u53f6\u6210\u6797\u4e00\u8def\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u6b64\u65f6\u5ffd\u7136\u6709\u4e00\u79cd\u5947\u5f02\u7684\u611f\u89c9\uff0c\u4f46\u89c9\u4e8e\u627f\u73e0\u7684\u5fc3\u610f\u597d\u50cf\u548c\u4ed6\u76f8\u901a\uff0c \u4ed6\u4e5f\u662f\u559c\u6b22\u72c2\u98ce\u4e0b\u7684\u6ce2\u6d9b\u58ee\u9614\u7684\u666f\u8272\u7684\uff0c\u4e0d\u7981\u60f3\u9053\uff1a\u201c\u98ce\u5e73\u6d6a\u9759\uff0c\u666f\u8272\u867d\u7f8e\uff0c\u7a76\u5c5e\u5e73\u51e1\uff0c\u90a3 \u662f\u9002\u5b9c\u4e8e\u94c1\u955c\u5fc3\u548c\u6c90\u71d5\u8fd9\u7c7b\u7684\u516c\u5b50\u5c0f\u59d0\u6b23\u8d4f\u7684\u3002\u201d\u4e8e\u627f\u73e0\u7684\u7236\u4eb2\u4e8e\u8c26\u66fe\u4e3a\u9601\u8001\uff0c\u4e43\u662f\u4e00\u54c1\u5927 \u81e3\uff0c\u8bba\u201c\u95e8\u7b2c\u201d\u5e76\u4e0d\u5728\u6c90\u71d5\u4e4b\u4e0b\uff0c\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u53f6\u6210\u6797\u5374\u603b\u662f\u89c9\u5f97\u4e8e\u627f\u73e0\u597d\u50cf\u662f\u5c5e\u4e8e\u81ea\u5df1\u8fd9 \u4e00\u8def\u4eba\uff0c\u548c\u6c90\u71d5\u5c0f\u59d0\u5e76\u65e0\u76f8\u540c\u4e4b\u5904\u3002\u5176\u5b9e\u53f6\u6210\u6797\u548c\u4e8e\u627f\u73e0\u4ea6\u8fd8\u662f\u76f8\u77e5\u672a\u6df1\uff0c\u4ed6\u5bf9\u4e8e\u627f\u73e0\u7684\u4f30 \u8ba1\u4e5f\u8fd8\u662f\u201c\u504f\u9ad8\u201d\u4e86\u3002\u4e8e\u627f\u73e0\u7684\u786e\u548c\u6c90\u71d5\u3001\u94c1\u955c\u5fc3\u6709\u6240\u4e0d\u540c\uff0c\u4f46\u5374\u4e5f\u4e0d\u80fd\u8bf4\u4fbf\u5b8c\u5168\u4e24\u6837\u4e86\u3002 \u8981\u4e0d\u7136\u5979\u5bf9\u94c1\u955c\u5fc3\u7684\u60c5\u611f\uff0c\u4e5f\u4e0d\u81f4\u90a3\u6837\u96be\u4ee5\u5272\u5f00\u3002 \u3000\u3000\u4ece\u4e0b\u5173\u5230\u5927\u7406\uff0c\u4e0d\u8fc7\u4e00\u4e2a\u65f6\u8fb0\uff0c\u6fb9\u53f0\u706d\u660e\u4e0d\u8fdb\u5e02\u533a\uff0c\u5f84\u81ea\u5e26\u4ed6\u4eec\u4ece\u559c\u5dde\u9547\u7a7f\u8fc7\uff0c\u6a2a\u6e21\u6d31 \u6d77\uff0c\u4e00\u884c\u516d\u4eba\uff0c\u8fde\u4eba\u5e26\u9a6c\uff0c\u5206\u4e58\u4e24\u53ea\u6e14\u821f\uff0c\u6fb9\u53f0\u706d\u660e\u3001\u4e8e\u627f\u73e0\u3001\u53f6\u6210\u6797\u4e00\u8fb9\uff0c\u6c90\u71d5\u59d0\u5f1f\u548c\u94c1 \u955c\u5fc3\u4e00\u8fb9\uff0c\u6c90\u78f7\u672c\u6765\u662f\u60f3\u8ddf\u4e8e\u627f\u73e0\u7684\uff0c\u4f46\u6fb9\u53f0\u706d\u660e\u5374\u62c9\u7740\u53f6\u6210\u6797\u5148\u4e0b\u4e86\u8239\uff0c\u6c90\u78f7\u4e0d\u597d\u610f\u601d\u6324 \u8fdb\u53bb\uff0c\u53ea\u597d\u8ddf\u59d0\u59d0\u4e86\u3002 \u3000\u3000\u6d31\u6d77\u7684\u6c34\u6e90\u662f\u6765\u81ea\u82cd\u5c71\u7684\u878d\u96ea\uff0c\u6240\u4ee5\u7279\u522b\u6676\u83b9\uff0c\u6e56\u4e0a\u6e14\u821f\u5f88\u591a\uff0c\u9e6d\u9e33\u6f2b\u5929\u7fe9\u98de\uff0c\u65f6\u65f6\u4fef \u51b2\u4e0b\u6765\uff0c\u518d\u98de\u4e0a\u6765\u65f6\uff0c\u53e3\u4e2d\u5df2\u8854\u7740\u4e00\u5c3e\u5c3e\u7684\u9c7c\uff0c\u4f46\u542c\u5f97\u9694\u821f\u7684\u94c1\u955c\u5fc3\u7b11\u9053\uff1a\u201c\u8fd9\u666f\u8272\u53c8\u4f3c\u6c5f \u5357\u7684\u6c34\u4e61\u4e86\u3002\u201d\u63a5\u7740\u53c8\u662f\u6c90\u71d5\u541f\u8bd7\uff0c\u53f6\u6210\u6797\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u5012\u662f\u98ce\u96c5\u5f97\u5f88\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u60c5\u71ce \u4e71\uff0c\u56de\u7738\u5411\u53f6\u6210\u6797\u4e00\u7b11\uff0c\u5982\u4f3c\u5fc3\u795e\u53e6\u6709\u6240\u5c5e\u5730\u62cd\u6253\u7740\u6e56\u9762\u7684\u67d4\u6ce2\u3002 \u3000\u3000\u6e21\u8fc7\u6d31\u6d77\u5df2\u5230\u82cd\u5c71\u811a\u4e0b\uff0c\u53ea\u89c1\u5c71\u9876\u79ef\u96ea\u8986\u8d23\uff0c\u5728\u79ef\u96ea\u4e2d\u9732\u51fa\u4e00\u70b9\u70b9\u82cd\u7fe0\u7684\u5c71\u8272\uff0c\u4e8e\u627f\u73e0 \u9053\uff1a\u201c\u602a\u4e0d\u5f97\u82cd\u5c71\u53c8\u540d\u70b9\u82cd\u5c71\u3002\u8fd9\u540d\u5b57\u771f\u662f\u771f\u6781\u4e86\u3002\u201d\u5728\u5c71\u9876\u671b\u4e0a\u53bb\uff0c\u53c8\u89c1\u5c42\u5c42\u767d\u4e91\u7b3c\u7f69\uff0c \u597d\u50cf\u4e00\u6761\u767d\u7389\u5b9d\u5e26\uff0c\u56f4\u7ed5\u4e86\u82cd\u5c71\u5341\u4e5d\u5cf0\u3002\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u6b64\u5730\u4eba\u79f0\u8fd9\u666f\u81f4\u4e3a\u4e94\u5e26\u9501\u82cd\u5c71\u3002\u6211 \u6ca1\u6709\u4f60\u4eec\u8fd9\u4e48\u96c5\uff0c\u73b0\u5728\u6025\u7740\u56de\u53bb\uff0c\u53ef\u65e0\u5fc3\u5728\u5c71\u811a\u4ed4\u7ec6\u6b23\u8d4f\u4e86\u3002\u201d\u8bf4\u7f62\uff0c\u7a81\u7136\u653e\u51fa\u4e00\u679d\u54cd\u7bad\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5c71\u4e0a\u8dd1\u4e0b\u51e0\u4e2a\u4eba\u6765\u8fce\u63a5\uff0c\u4e43\u662f\u9ed1\u767d\u6469\u8bc3\u548c\u5c0f\u864e\u5b50\u3002\u5c0f\u864e\u5b50\u9ad8\u5174\u6781\u4e86\uff0c\u8e66\u8e66\u8df3 \u8df3\u5730\u6bd4\u9ed1\u767d\u6469\u8bc3\u8dd1\u5f97\u8fd8\u5feb\uff0c\u4e00\u6e9c\u70df\u5730\u51b2\u5230\u4e8e\u627f\u73e0\u9762\u524d\uff0c\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u5c0f\u9b3c\u5934\uff0c\u90a3\u5929\u6025\u6b7b\u6211 \u4e86\uff0c\u539f\u6765\u4f60\u5df2\u5148\u5230\u4e86\u8fd9\u513f\u3002\u201d\u5c0f\u864e\u5b50\u563b\u563b\u4e00\u7b11\uff0c\u5ffd\u7136\u56de\u8fc7\u5934\u6765\uff0c\u7167\u7740\u6c90\u78f7\u7684\u80a9\u5934\u5c31\u662f\u4e00\u6376\uff0c \u4e8e\u627f\u73e0\u5fd9\u559d\u9053\uff1a\u201c\u5c0f\u864e\u5b50\u4e0d\u5f97\u65e0\u793c\uff0c\u4ed6\u662f\u6c90\u5c0f\u516c\u7239\u3002\u201d\u5c0f\u864e\u5b50\u7b11\u9053\uff1a\u201c\u6211\u65e9\u77e5\u9053\u5566\uff0c\u8981\u4e0d\u7136 \u6211\u8fd9\u4e00\u6376\u8fd8\u4e0d\u628a\u4ed6\u6376\u6241\uff01\u201d\u62c9\u7740\u6c90\u78f7\u7684\u624b\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u90a3\u5929\u4f60\u600e\u7684\u4e0d\u8bb2\u660e\u662f\u6211\u5e08\u7236\u672a\u5165 \u95e8\u7684\u5f1f\u5b50\uff0c\u4f60\u8bb2\u660e\u767d\u4e86\uff0c\u6211\u7109\u80fd\u4e0d\u8ba9\u4f60\u9a91\u90a3\u5339\u767d\u9a6c\uff0c\u54ce\u54df\uff0c\u4f60\u600e\u7684\u6241\u7740\u5634\u513f\u4e0d\u8bf4\u8bdd\uff0c\u6211\u6253\u75db \u4e86\u4f60\u5417\uff1f\u597d\uff0c\u597d\uff0c\u522b\u607c\uff0c\u522b\u607c\uff0c\u6211\u7ed9\u4f60\u8d54\u793c\uff0c\u6211\u5e26\u4f60\u53bb\u6349\u5f13\u9c7c\u3002\u201d\u6c90\u78f7\u81ea\u51fa\u751f\u4ee5\u6765\uff0c\u56fd\u516c\u5e9c \u91cc\u7684\u4e0a\u4e0b\u4eba\u7b49\uff0c\u90fd\u6367\u51e4\u51f0\u4f3c\u7684\u5475\u62a4\u4ed6\u3001\u5949\u627f\u4ed6\uff0c\u4ed6\u9664\u4e86\u548c\u59d0\u59d0\u73a9\u800d\u4e4b\u5916\uff0c\u5c31\u627e\u4e0d\u5230\u4e00\u4e2a\u670b \u53cb\uff0c\u5982\u4eca\u5c0f\u864e\u5b50\u5c06\u4ed6\u5f53\u4f5c\u76f8\u7b49\u8eab\u4efd\u7684\u670b\u53cb\u770b\u5f85\uff0c\u53cd\u800c\u4ee4\u4ed6\u611f\u5230\u4eb2\u70ed\u6295\u7f18\uff0c\u4ed6\u867d\u7136\u820d\u4e0d\u5f97\u79bb\u5f00 \u4e8e\u627f\u73e0\uff0c\u7ec8\u4e8e\u8fd8\u662f\u7ed9\u5c0f\u864e\u5b50\u62c9\u4e86\u53bb\uff0c\u6458\u91ce\u82b1\u3001\u770b\u5f13\u9c7c\u4e86\u3002\u4e24\u4e2a\u8bf4\u5c0f\u4e0d\u5c0f\u8bf4\u5927\u4e0d\u5927\u7684\u5b69\u5b50\u8fd8\u73a9 \u5f97\u633a\u6709\u610f\u601d\u7684\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u3001\u53f6\u6210\u6797\u7b49\u5411\u9ed1\u767d\u6469\u8bc3\u53d9\u793c\u76f8\u89c1\uff0c\u8bf4\u8d77\u6765\u624d\u77e5\u9053\u4ed6\u4eec\u5230\u4e86\u5927\u7406\u5df2\u6709\u4e03\u516b\u5929\u4e86\uff0c\u6bb5 \u6f84\u82cd\u548c\u6ce2\u65af\u516c\u4e3b\u4f4f\u5728\u6bb5\u738b\u7237\u7684\u738b\u5e9c\u91cc\uff0c\u4ed6\u4eec\u5219\u4f4f\u5728\u82d5\u5c71\u4e0a\u548c\u5f20\u4e39\u67ab\u4f5c\u4f34\uff0c\u5c0f\u864e\u5b50\u4e5f\u6b63\u5f0f\u5411\u5f20 \u4e39\u67ab\u55d1\u5934\u62dc\u5e08\uff0c\u7b97\u4f5c\u4ed6\u95e8\u4e0b\u7684\u7b2c\u4e8c\u4e2a\u5f1f\u5b50\u4e86\u3002\u6fb9\u53f0\u706d\u660e\u5c06\u9a6c\u5339\u7559\u5728\u5c71\u4e0b\u90a3\u4e2a\u79cd\u83dc\u7684\u5f5d\u4eba\u5bb6 \u4e2d\uff0c\u4e00\u884c\u4eba\u7b49\u4fbf\u968f\u7740\u9ed1\u767d\u6469\u8bc3\u4e0a\u5c71\u3002 \u3000\u3000\u82cd\u5c71\u5341\u4e5d\u5cf0\u5341\u516b\u6da7\u662f\u5927\u7406\u6700\u8457\u540d\u7684\u98ce\u666f\uff0c\u5341\u516b\u6761\u6eaa\u6d41\uff0c\u72b9\u5982\u4eba\u4f53\u7684\u8109\u80f3\u4e00\u6837\uff0c\u7a7f\u63d2\u5728\u7fa4 \u5cf0\u4e4b\u95f4\uff0c\u901a\u5230\u6d31\u6d77\uff0c\u6bcf\u5ea7\u5c71\u5cf0\u4e2d\u95f4\u90fd\u6d41\u7740\u6eaa\u6c34\uff0c\u56f4\u7ed5\u7740\u4e3b\u5cf0\u7684\u4e09\u5858\u6eaa\u66f4\u662f\u6676\u51b0\u6d01\u83b9\uff0c\u4e8e\u627f\u73e0 \u7b49\u4e00\u8def\u4e0a\u5c71\uff0c\u4f46\u89c1\u592a\u9633\u7167\u8fc7\u5c71\u5cf0\u7684\u80cc\u5f71\u6298\u5c04\u5728\u6c34\u9762\u4e0a\uff0c\u78a7\u6ce2\u5fae\u6f3e\uff0c\u5f62\u6210\u4e94\u5f69\u8679\u9713\u822c\u56de\u65cb\u7740\u7684 \u5c42\u5c42\u5708\u73af\uff0c\u8f89\u6620\u7740\u6df1\u7d2b\u3001\u5929\u84dd\u3001\u78a7\u7eff\u3001\u6a59\u9ec4\u3001\u9c9c\u7ea2\u7b49\u7b49\u8272\u5149\uff1b\u5404\u79cd\u5404\u5f0f\u7f8e\u5999\u60a6\u773c\u7684\u77f3\u5375\uff0c\u5d4c \u5728\u6c34\u5e95\uff0c\u5982\u73cd\u73e0\uff0c\u5982\u7fe1\u7fe0\uff0c\u5982\u5b9d\u77f3\uff0c\u5806\u6210\u4e86\u6c34\u5e95\u7684\u5b9d\u85cf\u3002\u82cd\u5c71\u9876\u4e0a\u7684\u79ef\u96ea\u867d\u7136\u662f\u7ec8\u5e74\u4e0d\u5316\uff0c \u5c71\u5761\u7684\u6c14\u5019\u5374\u6696\u6d0b\u6d0b\u6070\u4f3c\u6c5f\u5357\u7684\u66ae\u6625\uff0c\u957f\u6ee1\u5982\u8335\u7684\u7eff\u8349\u548c\u4e07\u7d2b\u5343\u7ea2\u7684\u82b1\u6735\u3002\u94c1\u955c\u5fc3\u6717\u58f0\u541f \u9053\uff1a\u201c\u4f46\u5f97\u540d\u82b1\u957f\u4f5c\u4f34\uff0c\u6b64\u8eab\u7ec8\u8001\u5728\u82cd\u5c71\u3002\u201d\u6c90\u71d5\u5fae\u5fae\u4e00\u7b11\uff0c\u795e\u91c7\u98de\u626c\uff0c\u5728\u5979\u5fc3\u4e2d\uff0c\u4ee5\u4e3a\u94c1 \u955c\u5fc3\u6240\u8bf4\u7684\u201c\u540d\u82b1\u201d\u5fc5\u662f\u6307\u5979\u7684\u4e86\uff0c\u5076\u7136\u4e00\u77a5\uff0c\u4f46\u89c1\u4e8e\u627f\u73e0\u53cc\u7709\u7d27\u8e59\uff0c\u76ee\u6ce8\u91ce\u82b1\uff0c\u82e5\u6709\u6240 \u601d\uff0c\u53c8\u7981\u4e0d\u4f4f\u5fc3\u5934\u4e00\u52a8\u3002 \u3000\u3000\u4e0a\u5230\u5c71\u9876\uff0c\u53ea\u89c1\u51e0\u95f4\u77f3\u5c4b\uff0c\u5f0f\u6837\u53e4\u96c5\uff0c\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u4f60\u592a\u5e08\u7956\u548c\u4e0a\u5b98\u5929\u91ce\u3001\u8427\u8001\u5927\u5a18 \u4e24\u4f4d\u8001\u524d\u8f88\u4f4f\u5728\u540e\u5c71\u77f3\u5ba4\uff0c\u54b1\u4eec\u5148\u5230\u540e\u62c9\u5c71\u77f3\u5c4b\u89c1\u89c1\u4f60\u7684\u5e08\u7236\u5427\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u662f\u7406\u6240 \u5f53\u7136\u3002\u201d\u63a8\u95e8\u800c\u5165\uff0c\u53ea\u89c1\u4e91\u91cd\u592b\u5987\u65e9\u5728\u5c4b\u4e2d\u7b49\u5019\uff0c\u5374\u4e0d\u89c1\u5f20\u4e39\u67ab\u3002 \u3000\u3000\u4e91\u91cd\u9053\uff1a\u201c\u4f60\u5e08\u7236\u5230\u738b\u5e9c\u8bae\u4e8b\u53bb\u4e86\uff0c\u8fd9\u51e0\u65e5\u519b\u60c5\u7d27\u5f20\uff0c\u542c\u8bf4\u6c90\u56fd\u516c\u5df2\u5728\u6606\u660e\u53d1\u5175\u4e86 \u5462\u3002\u201d\u6c90\u78f7\u3001\u6c90\u71d5\u201c\u554a\u201d\u4e86\u4e00\u58f0\uff0c\u5fc3\u4e2d\u9887\u611f\u4e0d\u5b89\u3002\u4e8e\u627f\u73e0\u6b63\u60f3\u8bf7\u95ee\u5e08\u6bcd\uff0c\u5ffd\u542c\u5f97\u5c4f\u98ce\u80cc\u540e\u5a74 \u513f\u7684\u54ed\u58f0\uff0c\u4e91\u857e\u62b1\u4e86\u4e00\u4e2a\u5b69\u5b50\u51fa\u6765\uff0c\u539f\u6765\u4e91\u857e\u5230\u5927\u7406\u4e4b\u540e\uff0c\u4e0d\u4e45\u5373\u517b\u4e86\u4e00\u4e2a\u5973\u513f\uff0c\u5982\u4eca\u5df2\u6709 \u534a\u5c81\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u8fde\u5fd9\u4e0a\u524d\u62dc\u89c1\uff0c\u5e76\u5411\u5e08\u7236\u9053\u559c\uff0c\u4e91\u857e\u4e00\u628a\u62c9\u7740\u627f\u73e0\uff0c\u8f7b\u629a\u5979\u7684\u79c0\u53d1\uff0c\u7231\u601c\u5907\u81f3\uff0c \u8bf4\u9053\uff1a\u201c\u627f\u73e0\uff0c\u8fd9\u4e00\u5e74\u6765\u4e8f\u4e86\u4f60\u4e86\uff0c\u8ba9\u4f60\u4e00\u4e2a\u4eba\u6d41\u6d6a\u6c5f\u6e56\uff0c\u6211\u4eec\u771f\u653e\u5fc3\u4e0d\u4e0b\u5462\uff0c\u597d\u5728\u4f60\u73b0\u5728 \u5e73\u5b89\u6765\u5230\u4e86\u3002\u55ef\uff0c\u4f60\u957f\u5f97\u548c\u6211\u4e00\u822c\u9ad8\u5566\uff01\u201d\u4e8e\u627f\u73e0\u60f3\u8d77\u8fd9\u4e00\u5e74\u591a\u7684\u906d\u9047\uff0c\u5982\u4eca\u624d\u597d\u4f3c\u56de\u5230\u5bb6 \u4e2d\uff0c\u5fc3\u4e2d\u65e0\u9650\u611f\u6168\uff0c\u508d\u7740\u4e91\u857e\u5750\u4e0b\uff0c\u8bb8\u591a\u8bdd\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\uff0c\u90a3\u5973\u5a74\u751f\u5f97\u7389\u96ea\u53ef\u7231\uff0c\u4e0e\u4e8e\u627f\u73e0 \u5012\u5f88\u6295\u7f18\uff0c\u4e8e\u627f\u73e0\u8f7b\u8f7b\u9017\u5f04\uff0c\u5f04\u5f97\u5979\u7834\u6d95\u4e3a\u7b11\uff0c\u4e8e\u627f\u73e0\u62a2\u7740\u62b1\u5979\uff0c\u7231\u4e0d\u91ca\u624b\u3002 \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u542c\u5f97\u5c71\u4e0b\u53c8\u662f\u4e00\u652f\u54cd\u7bad\uff0c\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u5f85\u6211\u770b\u662f\u8c01\u6765\u4e86\uff1f\u201d\u8fc7\u4e86\u4e00\u4f1a\uff0c \u53ea\u542c\u5f97\u4e00\u9635\u54cd\u4eae\u7684\u7b11\u58f0\uff0c\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u662f\u5e08\u7236\u56de\u6765\u5566\uff01\u201d\u8d76\u4e0a\u53bb\u5f00\u95e8\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u966a\u7740\u4e24\u4e2a \u4eba\u8d70\u8fdb\u6765\u3002\u6b63\u662f\u4e4c\u8499\u592b\u592b\u5987\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u90fd\u6765\u5566\uff0c\u6765\u5f97\u6b63\u662f\u5408\u65f6\uff0c\u6bb5\u738b\u7237\u542c\u8bf4\u4f60\u4eec\u6765\u4e86\uff0c\u4e5f\u5f88\u9ad8\u5174\uff0c\u660e \u65e5\u8bf7\u4f60\u4eec\u5230\u738b\u5e9c\u53bb\u73a9\u3002\u201d\u4f17\u4eba\u4e0a\u524d\u62dc\u89c1\uff0c\u5f20\u4e39\u67ab\u542c\u8bf4\u53f6\u6210\u6797\u662f\u53f6\u5b97\u7559\u7684\u4f84\u513f\uff0c\u7b11\u9053\uff1a\u201c\u53f6\u5144 \u60f3\u5fc5\u662f\u5949\u4ee4\u53d4\u4e4b\u547d\u6765\u7684\u4e86\u3002\u201d\u53f6\u6210\u6797\u606d\u606d\u656c\u656c\u8bf4\u9053\uff1a\u201c\u6b63\u662f\u3002\u6709\u7591\u96be\u4e4b\u4e8b\uff0c\u8981\u5411\u5f20\u5927\u4fa0\u8ba8 \u6559\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d\u53f6\u6210\u6797\u5c06\u6765\u610f\u8bf4\u4e86\uff0c\u5f20\u4e39\u67ab\u5e76\u4e0d\u5373\u7b54\uff0c\u5411\u94c1\u955c\u5fc3\u7b11\u9053\uff1a\u201c\u6211\u4e0e \u4ee4\u5e08\u795e\u4ea4\u5df2\u4e45\uff0c\u4ed6\u53ef\u597d\u5417\uff1f\u201d\u94c1\u955c\u5fc3\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u597d\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5bf9\u6c5f\u5357\u7684\u4e49\u519b\u4e4b \u4e8b\uff0c\u6211\u4e0d\u719f\u6089\uff0c\u4f60\u4e24\u4eba\u90fd\u662f\u4ece\u90a3\u8fb9\u6765\u7684\uff0c\u4f9d\u4f60\u4eec\u4e4b\u89c1\u5982\u4f55\uff1f\u201d \u3000\u3000\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u53ea\u6015\u4e0d\u6613\u6210\u4e8b\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f55\u6545\u4e0d\u6613\u6210\u4e8b\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u7528\u5175\u4e4b \u9053\uff0c\u4e09\u4ef6\u4e8b\u6700\u4e3a\u7d27\u8981\uff0c\u90a3\u5c31\u662f\u5929\u65f6\u3001\u5730\u5229\u548c\u4eba\u548c\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u73b0 \u5728\u8d77\u5175\uff0c\u4f3c\u975e\u5176\u65f6\u3002\u56fd\u5bb6\u591a\u96be\uff0c\u7ecf\u571f\u6728\u5821\u4e00\u5f79\u4e4b\u540e\uff0c\u4e2d\u534e\u5143\u6c14\u5927\u4f24\uff0c\u73b0\u5728\u521a\u521a\u5f97\u51e0\u5e74\u4f11\u606f\u751f \u517b\uff0c\u53ea\u6015\u4eba\u5fc3\u538c\u4e71\u3002\u81ea\u53e4\u4ee5\u6765\uff0c\u5e1d\u738b\u5d1b\u8d77\uff0c\u591a\u662f\u4ee5\u897f\u5317\u800c\u5236\u4e2d\u539f\uff0c\u7f55\u89c1\u6709\u5728\u6cbf\u6d77\u8d77\u5175\uff0c\u53ef\u4ee5 \u6210\u5927\u4e8b\u7684\u3002\u800c\u4e14\uff0c\u4e0d\u662f\u6211\u6562\u5c0f\u770b\u522b\u4eba\u2014\u2014\u4f3c\u6bd5\u64ce\u5929\u8fd9\u7b49\u8349\u83bd\u82f1\u96c4\uff0c\u4e5f\u4e0d\u662f\u5f00\u56fd\u4e4b\u541b\u7684\u6750\u6599\u3002 \u6240\u4ee5\uff0c\u4f9d\u6211\u6240\u89c1\uff0c\u5929\u65f6\u5730\u5229\u4eba\u548c\uff0c\u4e09\u8005\u90fd\u4e0d\u9002\u5b9c\u3002\u201d\u94c1\u955c\u5fc3\u5f97\u5f20\u4e39\u67ab\u4e0b\u98ce\u6709\u610f\u5356\u5f04\uff0c\u63a5\u7740\u4f83 \u4f83\u800c\u8c08\u7528\u5175\u4e4b\u6cd5\uff0c\u65ad\u5b9a\u4e49\u519b\u82e5\u7136\u8d77\u4e8b\uff0c\u5fc5\u8d25\u65e0\u7591\u3002\u4e8e\u627f\u73e0\u542c\u6765\uff0c\u53ea\u89c9\u6709\u4e9b\u9053\u7406\uff0c\u6709\u4e9b\u65e0\u7406\uff0c \u53ea\u662f\u53eb\u5979\u8bf4\u5979\u5374\u8bf4\u4e0d\u51fa\u6240\u4ee5\u7136\u6765\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u5bf9\u53f6\u6210\u6797\u9053\uff1a\u201c\u4f9d\u4f60\u4e4b\u89c1\uff0c\u53c8\u662f\u5982\u4f55\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u6210\u8d25\u6211\u4e0d\u6562 \u8bf4\u3002\u4f46\u4f9d\u6211\u4e4b\u89c1\uff0c\u4e8b\u60c5\u53ea\u95ee\u8be5\u4e0d\u8be5\u505a\uff0c\u6210\u8d25\u5012\u5728\u5176\u6b21\u3002\u201d \u3000\u3000\u94c1\u955c\u5fc3\u5728\u65c1\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u82e5\u7136\u4e0d\u8ba1\u6210\u8d25\uff0c\u90a3\u53c8\u4f55\u5fc5\u8d77\u5175\u81ea\u60f9\u7978\u6b83\u3002\u201d\u5f20\u4e39\u67ab\u5fc3\u9053\uff1a\u201c\u82e5 \u662f\u4eba\u4eba\u90fd\u50cf\u4f60\u8fd9\u4e48\u806a\u660e\uff0c\u4e00\u5b9a\u8981\u6709\u5fc5\u80dc\u7684\u628a\u63e1\u624d\u80af\u53bb\u505a\uff0c\u90a3\u4e48\u5f53\u521d\u6211\u7684\u7956\u5148\u548c\u6731\u5143\u6f33\u4e5f\u5c31\u4e0d \u5fc5\u8d77\u5175\u6297\u5143\u4e86\u3002\u201d\u4f46\u4ed6\u4e0d\u613f\u5373\u65f6\u53d1\u8868\u610f\u89c1\uff0c\u4ecd\u7136\u671b\u7740\u53f6\u6210\u6797\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u518d\u8bf4\u4e0b\u53bb\u3002\u201d \u3000\u3000\u53f6\u6210\u6797\u60f3\u4e86\u4e00\u9635\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u4e0b\u74e6\u523a\u590d\u5174\uff0c\u502d\u6c1b\u867d\u7136\u6682\u6b62\uff0c\u9690\u5fe7\u4ecd\u5728\u3002\u671d\u5ef7\u4e0d\u6562\u62b5\u5fa1\u5916 \u654c\uff0c\u5374\u4e13\u5411\u5185\u7528\u5175\uff0c\u5927\u5931\u6c11\u671b\u3002\u6211\u770b\u767e\u59d3\u4e0d\u662f\u6015\u4e71\uff0c\u800c\u662f\u6015\u671d\u5ef7\u82df\u5b89\uff0c\u5c06\u6765\u66f4\u4f1a\u60f9\u8d77\u4ea1\u56fd\u7684 \u5927\u4e71\u3002\u81f3\u4e8e\u8bf4\u5230\u5730\u5229\uff0c\u5f53\u5e74\u660e\u592a\u7956\u4e5f\u662f\u4ece\u6c5f\u5357\u4e3e\u5175\u9a71\u9010\u52cb\u864f\u7684\uff0c\u5e76\u4e0d\u4e00\u5b9a\u8981\u501a\u4ed7\u897f\u5317\u624d\u80fd\u7edf \u4e00\u4e2d\u539f\u3002\u518d\u8bf4\u5230\u9886\u8896\u7684\u4eba\u624d\uff0c\u53ea\u8981\u4e49\u65d7\u4e00\u4e3e\uff0c\u8001\u767e\u59d3\u81ea\u4f1a\u9009\u62e9\u3002\u201d \u3000\u3000\u94c1\u955c\u5fc3\u9762\u8272\u6da8\u7ea2\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u7136\uff0c\u4e0d\u7136\uff01\u201d\u5f15\u7ecf\u636e\u5178\uff0c\u4ee5\u53e4\u8bc1\u4eca\uff0c\u4ece\u5175\u6cd5\u53f2\u4e8b\u79cd\u79cd \u65b9\u9762\uff0c\u9a73\u65a5\u53f6\u6210\u6797\u7684\u610f\u89c1\u3002\u5f20\u4e39\u67ab\u9ed8\u9ed8\u9759\u542c\u4e24\u4eba\u7684\u8fa9\u8bba\uff0c\u4e0d\u53d1\u4e00\u8a00\uff0c\u6fb9\u53f0\u706d\u660e\u5374\u542c\u5f97\u4e0d\u8010\u70e6 \u4e86\uff0c\u9053\uff1a\u201c\u56fd\u5bb6\u5927\u4e8b\uff0c\u4ee5\u540e\u518d\u8c08\u5982\u4f55\uff1f\u6211\u770b\u600e\u6837\u5e94\u4ed8\u7fa4\u9b54\u653b\u5c71\uff0c\u90a3\u624d\u662f\u5f53\u52a1\u4e4b\u6025\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u7fa4\u9b54\u653b\u5c71\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u7684\u4e4c\u4f2f\u4f2f\u5e26\u6709\u6d88\u606f\u6765\u3002\u201d\u4e4c \u8499\u592b\u9053\uff1a\u201c\u6211\u5728\u6c5f\u5357\u904d\u627e\u77f3\u60ca\u6d9b\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u4ed6\u4e00\u56de\u6765\uff0c\u5c31\u5931\u4e86\u8e2a\u8ff9\u3002\u6211\u5c31\u6298\u56de\u6765\u8ffd\u8e2a\u9633\u5b97 \u6d77\u3002\u542c\u8bf4\u4ed6\u5df2\u8bf4\u52a8\u5e08\u7236\u8d64\u57ce\u5b50\u51fa\u5934\uff0c\u9080\u8bf7\u4e86\u51e0\u4e2a\u4e45\u4f0f\u9690\u5c45\u7684\u9b54\u5934\uff0c\u8981\u501f\u5411\u7384\u673a\u524d\u8f88\u62dc\u5bff\u4e3a \u540d\uff0c\u5230\u82cd\u5c71\u6843\u8845\u3002\u201d\u6fb9\u53f0\u706d\u660e\u9053\uff1a\u201c\u662f\u4e9b\u4ec0\u4e48\u9b54\u5934\uff1f\u201d\u4e4c\u8499\u592b\u9053\uff1a\u201c\u6709\u54c0\u7262\u5c71\u7684\u9e20\u76d8\u5a46\u516c\u5b59 \u65e0\u57a2\uff0c\u6709\u6606\u4ed1\u5c71\u661f\u5bbf\u6d77\u7684\u6458\u661f\u4e0a\u4eba\uff0c\u8fd8\u6709\u4e1c\u6d77\u660e\u971e\u5c9b\u7684\u5c60\u9f99\u5c0a\u8005\u548c\u7518\u8083\u79ef\u77f3\u5c71\u7684\u4e91\u9633\u771f\u541b\u3002 \u8fd9\u51e0\u4e2a\u4eba\u82e5\u7136\u662f\u54b1\u4eec\u7684\u5e08\u957f\u51fa\u624b\uff0c\u60f3\u6765\u8fd8\u53ef\u5bf9\u4ed8\uff0c\u5c31\u53ea\u6015\u4ed6\u4eec\u4e09\u4f4d\u8001\u524d\u8f88\u4e0d\u80af\u51fa\u624b\uff0c\u54b1\u4eec\u5012 \u4e0d\u53ef\u8f7b\u654c\u5462\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4e09\u4f4d\u8001\u524d\u8f88\u6b63\u5728\u5750\u5173\u7ec3\u529f\uff0c\u8981\u5230\u6211\u5e08\u7956\u516b\u5341\u5927\u5bff\u4e4b\u65e5\uff0c\u624d\u529f\u884c\u5b8c\u6ee1\uff0c\u9009 \u5b9a\u90a3\u65e5\u5f00\u5173\u3002\u201d\u4e4c\u8499\u592b\u5947\u9053\uff1a\u201c\u4ed6\u4eec\u8fd8\u8981\u7ec3\u4ec0\u4e48\u529f\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6b66\u5b66\u4e4b\u9053\uff0c\u7edd\u65e0\u6b62\u5883\uff0c \u4ed6\u4eec\u5750\u5173\u7ec3\u529f\uff0c\u60f3\u662f\u8981\u96c6\u4e09\u5bb6\u4e4b\u957f\u4e3a\u6b66\u5b66\u521b\u4e00\u65b0\u5883\u3002\u53ea\u4e0d\u77e5\u8d64\u971e\u9053\u4eba\uff08\u8d64\u57ce\u5b50\u7684\u6cd5\u53f7\uff09\u548c\u90a3 \u51e0\u4e2a\u9b54\u5934\u4ec0\u4e48\u65f6\u5019\u6765\uff1f\u201d\u4e4c\u8499\u592b\u9053\uff1a\u201c\u4ed6\u4eec\u4ee5\u62dc\u5bff\u4e3a\u540d\uff0c\u60f3\u5f53\u5728\u7384\u673a\u9038\u58eb\u5bff\u8fb0\u7684\u6b63\u65e5\u6765 \u4e86\u3002\u201d\u5c0f\u864e\u5b50\u62cd\u624b\u9053\uff1a\u201c\u597d\u554a\uff0c\u90a3\u65e5\u54b1\u4eec\u53ef\u4ee5\u770b\u5230\u592a\u5e08\u7956\u5927\u663e\u795e\u901a\uff0c\u9a71\u9010\u7fa4\u9b54\u4e86\uff0c\u8fd9\u773c\u798f\u771f \u4e0d\u6d45\u5566\u3002\u201d\u4e8e\u627f\u73e0\u5fae\u7b11\u9053\uff1a\u201c\u592a\u5e08\u7956\u662f\u5f53\u4eca\u6b66\u6797\u81f3\u5c0a\uff0c\u4ed6\u8f7b\u6613\u4e0d\u80af\u51fa\u624b\u7684\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a \u201c\u592a\u5e08\u4f2f\u8463\u5cb3\u8fdc\u5728\u85cf\u8fb9\uff0c\u53ea\u6015\u4e0d\u80fd\u6765\u62dc\u5bff\u4e86\u3002\u4e8c\u5e08\u4f2f\u6f6e\u97f3\u548c\u5c1a\u8fdc\u5230\u96c1\u95e8\u5173\u5916\u8bbf\u91d1\u5200\u5be8\u4e3b\uff0c\u53ea \u6015\u6765\u4e0d\u53ca\u8d76\u56de\u3002\u4f46\u6211\u7684\u5e08\u7236\u548c\u5e08\u5a18\uff08\u5373\u8c22\u5929\u534e\u548c\u53f6\u76c8\u76c8\uff09\u5230\u65f6\u4e00\u5b9a\u4f1a\u4ece\u5c0f\u5bd2\u5c71\u8d76\u81f3\uff0c\u6709\u4ed6\u4eec \u4e8c\u4eba\u7684\u53cc\u5251\u5408\u58c1\uff0c\u654c\u4eba\u867d\u5f3a\uff0c\u8c05\u4ea6\u65e0\u8db3\u4e3a\u60a3\u3002\u201d \u3000\u3000\u5c0f\u4e00\u8f88\u7684\u5927\u90fd\u5e74\u8f7b\u597d\u4e8b\uff0c\u542c\u8bf4\u51e0\u65e5\u4e4b\u540e\uff0c\u5c06\u6709\u597d\u620f\u53ef\u770b\uff0c\u5747\u662f\u5927\u4e3a\u5174\u594b\u3002\u6b63\u662f\uff1a \u3000\u3000\u521d\u751f\u4e4b\u728a\u4e0d\u754f\u864e\uff0c\u8840\u96e8\u8165\u98ce\u53c8\u4e00\u573a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u56db\u56de\u3000\u738b\u5e9c\u903c\u624d\u534e\u3000\u8054\u9898\u4f73\u53e5\u3000\u9b54\u5934\u65bd\u6bd2\u624b\u3000\u5171\u95ef\u540d\u5c71 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u56db\u56de\u3000\u738b\u5e9c\u903c\u624d\u534e\u3000\u8054\u9898\u4f73\u53e5\u3000\u9b54\u5934\u65bd\u6bd2\u624b\u3000\u5171\u95ef\u540d\u5c71 \u3000\u3000\u5f20\u4e39\u67ab\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u65c5\u9014\u52b3\u7d2f\uff0c\u65e9\u70b9\u6b47\u606f\u53bb\u5427\uff0c\u660e\u65e5\u4e00\u65e9\uff0c\u8fd8\u8981\u53bb\u89c1\u6bb5\u738b\u7237\u5462\u3002\u201d \u94c1\u955c\u5fc3\u77aa\u4e86\u53f6\u6210\u6797\u4e00\u773c\uff0c\u597d\u4f3c\u521a\u624d\u7684\u8fa9\u8bba\uff0c\u610f\u72b9\u672a\u5c3d\uff0c\u56de\u5934\u4e00\u77a5\uff0c\u4f46\u89c1\u4e8e\u627f\u73e0\u4ecd\u662f\u4e00\u526f\u51b7\u6de1 \u7684\u795e\u60c5\uff0c\u4f3c\u4e4e\u5e76\u4e0d\u6b23\u8d4f\u4ed6\u7684\u201c\u8fa9\u624d\u201d\uff0c\u94c1\u955c\u5fc3\u6ee1\u6000\u4e0d\u4e50\uff0c\u4e5f\u53ea\u597d\u968f\u7740\u4f17\u4eba\u544a\u9000\uff0c\u5404\u81ea\u6b47\u606f\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u5f20\u4e39\u67ab\u5e26\u9886\u6c90\u78f7\u3001\u6c90\u71d5\u3001\u94c1\u955c\u5fc3\u3001\u53f6\u6210\u6797\u548c\u4e8e\u627f\u73e0\u4e94\u4eba\uff0c\u4e00\u540c\u53bb\u62dc\u8bbf\u6bb5\u738b \u7237\u3002\u738b\u5e9c\u5728\u5927\u7406\u57ce\u90ca\uff0c\u90bb\u8fd1\u86c7\u9aa8\u5854\uff0c\u6cbf\u9014\u90fd\u662f\u5927\u7406\u8457\u540d\u7684\u98ce\u666f\u540d\u80dc\uff0c\u5148\u662f\u7ecf\u8fc7\u8774\u8776\u6cc9\uff0c\u6cc9\u8fb9 \u4e00\u682a\u8001\u6811\uff0c\u679d\u6860\u4ea4\u7ed3\uff0c\u4e0b\u4e34\u6e05\u78a7\u7684\u6c60\u5858\uff0c\u666f\u9762\u6e05\u7edd\u3002\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u73b0\u5728\u5df2\u662f\u79cb\u5929\uff0c\u6ca1 \u6709\u8774\u8776\uff0c\u8981\u662f\u4f60\u4eec\u5728\u6625\u590f\u4e4b\u4ea4\u6765\u5230\uff0c\u53ef\u4ee5\u770b\u5230\u8774\u8776\u5230\u5904\u98de\u6765\uff0c\u90fd\u96c6\u7ed3\u5728\u8fd9\u68f5\u5927\u6811\u4e0a\uff0c\u5c24\u5176\u662f \u5728\u56db\u6708\u5341\u516d\u90a3\u5929\uff0c\u8774\u8776\u66f4\u662f\u4e00\u4e32\u4e32\u5730\u6302\u5728\u6811\u4e0a\uff0c\u76f4\u5230\u6c34\u9762\uff0c\u9996\u5c3e\u76f8\u8854\uff0c\u7262\u7ed3\u4e0d\u6563\uff0c\u4efb\u7531\u6e38\u4eba \u56f4\u89c2\uff0c\u90a3\u624d\u771f\u662f\u4eba\u95f4\u7f55\u89c1\u7684\u5947\u666f\u5462\uff01\u201d\u4f17\u4eba\u60a0\u7136\u795e\u5f80\uff0c\u6c90\u71d5\u53f9\u9053\uff1a\u201c\u53ea\u6015\u4eba\u4e8b\u805a\u6563\u65e0\u5e38\uff0c\u5230 \u4e86\u660e\u6625\uff0c\u54b1\u4eec\u8fd9\u4e9b\u4eba\u53c8\u4e0d\u77e5\u6563\u5230\u54ea\u91cc\u53bb\u4e86\uff01\u201d\u6709\u610f\u65e0\u610f\u5730\u77a5\u4e86\u94c1\u955c\u5fc3\u4e00\u773c\uff0c\u94c1\u955c\u5fc3\u6026\u7136\u5fc3 \u8df3\uff0c\u4f4e\u4e0b\u4e86\u5934\uff0c\u53ea\u5f53\u542c\u4e0d\u61c2\u5979\u8bf4\u8bdd\u4e2d\u7684\u542b\u610f\u3002 \u3000\u3000\u518d\u8d70\u4e00\u4f1a\uff0c\u7ecf\u8fc7\u4e09\u5854\u5bfa\u3002\u4e09\u5854\u5bfa\u76f8\u4f20\u662f\u5510\u671d\u7684\u5927\u5c06\u5c09\u8fdf\u656c\u5fb7\u6240\u5efa\uff0c\u6709\u4e00\u6837\u5947\u5999\u4e4b\u5904\uff0c\u6bcf \u5f53\u659c\u9633\u897f\u4e0b\u7684\u65f6\u5019\uff0c\u5854\u5f71\u843d\u5728\u5341\u4e94\u91cc\u5916\u7684\u4e00\u4e2a\u6c34\u6f6d\u4e2d\uff0c\u79f0\u4e3a\u201c\u4e09\u5854\u5012\u5f71\u201d\u3002\u8fc7\u4e86\u4e09\u5854\u5bfa\uff0c\u6ca1 \u591a\u4e45\u4fbf\u671b\u89c1\u201c\u86c7\u9aa8\u4e00\u5854\u201d\uff0c\u8fd9\u86c7\u9aa8\u5854\u4e5f\u6709\u4e00\u4e2a\u4f20\u5947\u7684\u6545\u4e8b\uff1b\u636e\u8bf4\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u6d31\u6d77\u6709\u4e00\u6761 \u5927\u87d2\uff0c\u65f6\u5e38\u5174\u98ce\u4f5c\u6d6a\uff0c\u6df9\u6ca1\u519c\u7530\uff0c\u4e3a\u5bb3\u4eba\u755c\uff0c\u540e\u6765\u6709\u4e00\u4e2a\u52c7\u58eb\u540d\u53eb\u6bb5\u8d64\u57ce\u7684\uff0c\u5e26\u4e86\u4e03\u628a\u94a2 \u5200\uff0c\u8df3\u8fdb\u6d31\u6d77\uff0c\u6545\u610f\u8ba9\u87d2\u86c7\u541e\u5165\u8179\u4e2d\uff0c\u5728\u91cc\u9762\u5c06\u87d2\u86c7\u523a\u6b7b\uff0c\u53ef\u662f\u4ed6\u81ea\u5df1\u4e5f\u95f7\u6b7b\u5728\u87d2\u86c7\u809a\u4e2d\uff0c \u8001\u767e\u7272\u4e3a\u4e86\u6c38\u4e16\u7eaa\u5ff5\u8fd9\u6740\u87d2\u7684\u82f1\u96c4\uff0c\u5c06\u87d2\u86c7\u7684\u9aa8\u5934\u70e7\u6210\u7070\u70ec\uff0c\u4fee\u76d6\u4e86\u8fd9\u4e00\u5ea7\u86c7\u9aa8\u5854\u3002\u76f8\u4f20\u8fd9 \u4f4d\u6bb5\u8d64\u57ce\u4fbf\u662f\u6bb5\u5bb6\u7684\u59cb\u7956\uff0c\u5927\u7406\u767e\u59d3\u611f\u4ed6\u7684\u6069\u5fb7\uff0c\u4fbf\u62e5\u7acb\u4ed6\u7684\u513f\u5b59\u4e16\u4ee3\u4e3a\u738b\u3002\u5982\u4eca\u6bb5\u738b\u5e9c\u8d77 \u5728\u86c7\u9aa8\u5854\u7684\u65c1\u8fb9\uff0c\u60f3\u6765\u4e5f\u662f\u7eaa\u5ff5\u8fd9\u4f4d\u4f20\u8bf4\u4e2d\u7684\u5148\u7956\u4e4b\u6545\u3002 \u3000\u3000\u6bb5\u5bb6\u5230\u4e86\u660e\u4ee3\u867d\u7136\u53ea\u88ab\u5c01\u4e3a\u201c\u77e5\u5e73\u7ae0\u4e8b\u201d\uff0c\u4f46\u4e00\u822c\u4eba\u4e60\u60ef\u4e0a\u4ecd\u662f\u79f0\u4ed6\u4eec\u4e3a\u201c\u738b\u7237\u201d\uff0c\u73b0 \u4efb\u7684\u77e5\u5e73\u7ae0\u4e8b\u6bb5\u6f84\u5e73\u7b97\u8d77\u6392\u884c\u6b63\u662f\u6bb5\u6f84\u82cd\u7684\u5802\u5144\u3002\u6bb5\u6f84\u5e73\u3001\u6bb5\u6f84\u82cd\u548c\u6ce2\u65af\u516c\u4e3b\u542c\u8bf4\u4ed6\u4eec\u5230 \u6765\uff0c\u65e9\u5728\u82b1\u56ed\u4e2d\u8bbe\u5bb4\u76f8\u5019\uff0c\u6bb5\u6f84\u82cd\u4e0e\u6ce2\u65af\u516c\u4e3b\u548c\u4e8e\u627f\u73e0\u4e92\u53d9\u522b\u540e\u4e4b\u60c5\uff0c\u500d\u89c9\u4eb2\u70ed\u3002 \u3000\u3000\u6bb5\u6f84\u5e73\u5219\u5bf9\u6c90\u71d5\u59d0\u5f1f\uff0c\u62db\u547c\u5907\u81f3\uff0c\u6c90\u78f7\u597d\u751f\u8fc7\u610f\u4e0d\u53bb\uff0c\u5e2d\u95f4\u8c08\u8d77\u6c90\u742e\u5c06\u8981\u53d1\u5175\u6765\u653b\u5927\u7406 \u7684\u4e8b\uff0c\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u5c0f\u516c\u7239\uff0c\u6bb5\u738b\u7237\u5bf9\u4f60\u8fd9\u4e48\u597d\uff0c\u4f60\u7684\u7239\u7239\u8fd8\u8981\u6d3e\u5175\u6253\u4ed6\uff1f\u201d\u6c90\u78f7\u6da8\u7ea2\u4e00 \u8138\uff0c\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u529d\u963b\u7239\u7239\uff0c\u8bf7\u4ed6\u4e0d\u8981\u628a\u5175\u9a6c\u95ef\u8fdb\u57ce\u6765\u3002\u201d\u8bdd\u662f\u8fd9\u6837\u8bf4\uff0c\u5176\u5b9e\u4ed6\u81ea\u5df1\u4ea6\u65e0\u628a \u63e1\u3002\u6bb5\u6f84\u5e73\u4e0e\u5f20\u4e39\u67ab\u76f8\u89c6\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u591a\u8c22\u5c0f\u516c\u7239\u4e86\uff01\u201d\u7b75\u5e2d\u5c06\u6563\uff0c\u4e00\u4f4d\u5c11\u5973\u7b11\u76c8\u76c8\u5730\u8d70\u8fdb \u82b1\u56ed\u3002 \u3000\u3000\u6bb5\u6f84\u5e73\u62db\u624b\u7b11\u9053\uff1a\u201c\u73e0\u513f\uff0c\u5feb\u6765\u89c1\u8fc7\u5ba2\u4eba\u3002\u201d\u539f\u6765\u662f\u4ed6\u7684\u5973\u513f\u3002\u6bb5\u73e0\u513f\u521a\u6ee1\u5341\u516d\u5c81\uff0c\u4e0e \u6c90\u78f7\u5e74\u7eaa\u76f8\u82e5\uff0c\u806a\u660e\u6d3b\u6cfc\uff0c\u60f9\u4eba\u601c\u7231\u3002\u6c90\u71d5\u62c9\u7740\u5979\u7684\u624b\u7b11\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u6f02\u4eae\u7684\u59d1\u5a18\uff0c\u771f\u50cf\u5f39 \u8bcd\u91cc\u9762\u5531\u7684\u516c\u4e3b\u3002\u201d\u6bb5\u73e0\u513f\u9053\uff1a\u201c\u59d0\u59d0\u624d\u662f\u5929\u4ed9\u5316\u4eba\u5462\u3002\u542c\u8bf4\u6c90\u516c\u7239\u8981\u6d3e\u5175\u6765\u6253\u6211\u4eec\uff0c\u5c06\u6765 \u56fd\u5bb6\u4ea1\u4e86\uff0c\u53ea\u6015\u6211\u7ed9\u59d0\u59d0\u505a\u5974\u5a62\uff0c\u59d0\u59d0\u4e5f\u4e0d\u8981\u5462\u3002\u201d\u6c90\u71d5\u9053\uff1a\u201c\u59b9\u59b9\u8bf4\u8fd9\u6837\u7684\u8bdd\uff0c\u81ca\u6b7b\u6211 \u4e86\u3002\u5176\u5b9e\u6211\u7239\u7239\u5e76\u4e0d\u60f3\u4e0e\u4f60\u4eec\u4e3a\u654c\uff0c\u90a3\u662f\u671d\u5ef7\u7684\u610f\u601d\u3002\u201d\u6bb5\u6f84\u5e73\u7b11\u9053\uff1a\u201c\u522b\u8bf4\u8fd9\u6740\u98ce\u666f\u7684\u4e8b \u4e86\uff0c\u5c0f\u516c\u7239\u548c\u6c90\u5c0f\u59d0\u8fdc\u8fdc\u5730\u4ece\u6606\u660e\u8dd1\u5230\u54b1\u4eec\u8fd9\u513f\u6765\uff0c\u90fd\u662f\u81ea\u5df1\u4eba\u5462\u3002\u73e0\u513f\uff0c\u4f60\u5531\u4e00\u6bb5\u66f2\u540c\u7ed9 \u59d0\u59d0\u542c\u3002\u201d \u3000\u3000\u6bb5\u73e0\u513f\u8f7b\u6572\u6a80\u677f\uff0c\u5531\u5927\u7406\u7684\u56db\u5b63\u6b4c\uff0c\u7b2c\u4e8c\u6bb5\u662f\u5531\u590f\u5b63\u7684\uff1b\u8bcd\u9053\uff1a\u201c\u4e94\u6708\u6ec7\u5357\u70df\u666f\u522b\uff0c\u6e05 \u51c9\u56fd\u91cc\u65e0\u70e6\u70ed\u3002\u53cc\u9e64\u6865\u8fb9\u4eba\u5356\u96ea\uff0c\u51b0\u7897\u55d3\uff0c\u8c03\u6885\u70b9\u5bc6\u548c\u742e\u5c51\u3002\u201d\u6c90\u78f7\u7b11\u9053\uff1a\u201c\u6211\u4eec\u6765\u7684\u65f6\u5019 \u8fd8\u89c1\u8857\u5934\u6709\u4eba\u5356\u96ea\u5462\u3002\u201d\u6bb5\u73e0\u513f\u5fae\u7efd\u6a31\u5507\uff0c\u8f7b\u8f7b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u7406\u548c\u4f60\u4eec\u7684\u6606\u660e\u4e00\u6837\uff0c\u56db\u5b63 \u6ca1\u6709\u5f88\u5927\u7684\u5dee\u522b\u3002\u201d\u518d\u5531\u4e86\u4e24\u6bb5\uff0c\u603b\u62ec\u5927\u7406\u56db\u5b63\u7684\u98ce\u5149\uff0c\u5a49\u8f6c\u800c\u6b4c\uff0c\u5531\u9053\uff1a\u201c\u96ea\u6708\u98ce\u82b1\u6b4c\u5927 \u7406\uff0c\u82cd\u5c71\u6d31\u6d77\u98ce\u5149\u7f8e\u3002\u4e09\u5854\u659c\u9633\u6ce2\u5f71\u91cc\uff0c\u5c71\u6cb3\u4e3d\uff0c\u9ece\u6c11\u4ff1\u613f\u5f81\u5c18\u606f\u3002\u201d\u5f20\u4e39\u67ab\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u597d\u4e00\u4e2a\uff1a\u9ece\u6c11\u4f46\u613f\u5f81\u5c18\u606f\u3002\u201d\u6c90\u78f7\u542c\u5f97\u5fc3\u9189\u795e\u6021\uff0c\u4f46\u89c9\u82e5\u628a\u5927\u7406\u4f5c\u6218\u573a\uff0c\u90a3\u771f\u662f\u83ab\u5927\u7684\u7f6a \u8fc7\u3002 \u3000\u3000\u5e2d\u6563\u4e4b\u540e\uff0c\u6bb5\u6f84\u5e73\u5e26\u4f17\u4eba\u6e38\u73a9\u738b\u5e9c\uff0c\u6bb5\u5bb6\u738b\u5e9c\u7ecf\u8fc7\u51e0\u767e\u5e74\u7684\u7ecf\u8425\uff0c\u7aef\u7684\u662f\u6c34\u6728\u6e05\u534e\uff0c\u9ad8 \u4e3d\u5e7d\u96c5\uff0c\u517c\u800c\u6709\u4e4b\uff0c\u8d70\u5230\u82b1\u56ed\u7684\u4e2d\u592e\uff0c\u6709\u4e00\u4e2a\u5c0f\u6e56\uff0c\u5468\u56f4\u767d\u77f3\u680f\u6746\uff0c\u6709\u56db\u9053\u5927\u7406\u77f3\u7684\u957f\u6865\u4ea4 \u53c9\u7a7f\u8fc7\uff0c\u666f\u8272\u7f8e\u6781\uff0c\u6e56\u7684\u4e1c\u9762\u5c3d\u5934\uff0c\u6709\u4e00\u5757\u5927\u77f3\u5140\u7acb\uff0c\u72b6\u5982\u5de8\u72ee\uff01\u77f3\u4e0a\u8fd8\u5efa\u6709\u4ead\u53f0\u697c\u9601\uff0c\u6c90 \u78f7\u5567\u5567\u79f0\u8d4f\uff0c\u6bb5\u73e0\u513f\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u521a\u5230\u5927\u7406\uff0c\u5927\u7ea6\u8fd8\u6ca1\u6709\u6e38\u8fc7\u89c2\u97f3\u5eb5\uff0c\u90a3\u89c2\u97f3\u5eb5\u6574\u4f53\u5efa\u5728\u4e00 \u5757\u5927\u77f3\u4e4b\u4e0a\uff0c\u90a3\u624d\u771f\u662f\u5927\u5462\u3002\u8fd9\u5757\u77f3\u5934\u548c\u5b83\u6bd4\u8d77\u6765\uff0c\u4e0d\u557b\u5c0f\u5deb\u4e4b\u89c1\u5927\u5deb\u3002\u201d\u6c90\u71d5\u9053\u3002\u201c\u89c2\u97f3 \u5eb5\u662f\u4e0d\u662f\u53c8\u540d\u5927\u77f3\u5eb5\uff1f\u201d\u6bb5\u73e0\u513f\u9053\uff1a\u201c\u662f\u5440\u3002\u59d0\u59d0\u4f60\u5230\u8fc7\u4e86\u3002\u201d\u6c90\u71d5\u9053\uff1a\u201c\u6211\u662f\u5728\u6ec7\u5357\u98ce\u7269 \u5fd7\u4e0a\u8bfb\u5230\u7684\uff0c\u542c\u8bf4\u5b83\u8fd8\u6709\u4e00\u6bb5\u6545\u4e8b\u3002\u636e\u8bf4\u53e4\u65f6\u5019\u6709\u4e00\u6279\u5f3a\u76d7\uff0c\u8981\u6765\u6d17\u52ab\u5927\u7406\uff0c\u89c2\u4e16\u97f3\u83e9\u8428\u5316 \u6210\u4e86\u4e00\u4e2a\u8001\u5987\uff0c\u80cc\u7740\u90a3\u5757\u5927\u77f3\uff0c\u5f3a\u76d7\u89c1\u4e86\u975e\u5e38\u60ca\u8be7\uff0c\u89c2\u97f3\u8bf4\u9053\uff1a\u201c\u6211\u5e74\u7eaa\u8001\u4e86\uff0c\u53ea\u80fd\u80cc\u8fd9\u5757 \u5c0f\u77f3\u5934\uff0c\u57ce\u91cc\u7684\u5e74\u8f7b\u5c0f\u4f19\u5b50\uff0c\u7ecf\u5e38\u80cc\u7684\u77f3\u5934\uff0c\u6bd4\u8fd9\u4e2a\u5927\u5341\u500d\u8fd8\u4e0d\u6b62\u2019\u5f3a\u76d7\u542c\u4e86\u5bb3\u6015\uff0c\u4e0d\u6562\u8fdb \u57ce\uff0c\u4fbf\u9003\u8dd1\u4e86\u3002\u8fd9\u4e2a\u6545\u4e8b\u53eb\u505a\u2018\u80cc\u77f3\u963b\u5175\u2019\u662f\u4e48\uff1f\u201d\u6c90\u71d5\u5728\u94c1\u955c\u5fc3\u9762\u524d\uff0c\u6700\u6b22\u559c\u5356\u5f04\u5979\u7684\u535a \u5b66\uff0c\u6bb5\u73e0\u513f\u70b9\u5934\u9053\uff1a\u201c\u6b63\u662f\u3002\u59d0\u59d0\u535a\u95fb\u5f3a\u8bb0\uff0c\u4ee4\u4eba\u4f69\u670d\u3002\u636e\u8bf4\u540e\u6765\u8001\u767e\u59d3\u4e3a\u4e86\u611f\u6069\uff0c\u4fbf\u5728\u8fd9 \u5757\u5927\u77f3\u4e0a\u5efa\u8d77\u4e86\u4e00\u5ea7\u89c2\u97f3\u5eb5\u6765\u3002\u201d\u8fd9\u6545\u4e8b\u5176\u5b9e\u4e8e\u627f\u73e0\u4e5f\u662f\u77e5\u9053\u7684\uff0c\u5979\u5374\u9759\u6084\u6084\u5730\u7ad9\u5728\u4e00\u8fb9\uff0c \u542c\u6c90\u71d5\u4e00\u4e2a\u4eba\u8bf4\uff0c\u5fc3\u4e2d\u5c3d\u5728\u60f3\u9053\uff1a\u201c\u89c2\u97f3\u53ef\u4ee5\u80cc\u77f3\u963b\u5175\uff0c\u54b1\u4eec\u6ca1\u6709\u89c2\u97f3\u7684\u2018\u795e\u529b\u2019\uff0c\u53ef\u4e0d\u77e5 \u9053\u80fd\u4e0d\u80fd\u963b\u6b62\u8fd9\u6b21\u5200\u5175\uff1f\u201d \u3000\u3000\u94c1\u955c\u5fc3\u4e00\u76f4\u5728\u6697\u4e2d\u7559\u5fc3\u4e8e\u627f\u73e0\u7684\u795e\u8272\uff0c\u53ea\u9053\u5979\u5fc3\u4e2d\u4e0d\u5feb\uff0c\u7b11\u9053\uff1a\u201c\u77a7\u8fd9\u98ce\u666f\u591a\u7f8e\uff0c\u4f60\u4eec \u4e0d\u662f\u8d4f\u98ce\u666f\u800c\u662f\u8c08\u98ce\u666f\u4e86\u3002\u201d\u6c90\u71d5\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u662f\u5728\u8d4f\u7740\u98ce\u666f\u5417\uff1f\u542c\u4f60\u8bf4\u7684\uff0c\u597d\u50cf\u53ea \u6709\u4f60\u662f\u5929\u4e0b\u7b2c\u4e00\u4e2a\u96c5\u4eba\u4e86\u3002\u201d\u508d\u7740\u94c1\u955c\u5fc3\u8d70\u8fc7\u957f\u6865\uff0c\u53ea\u89c1\u90a3\u5757\u5927\u77f3\u4e0a\u6b63\u4e2d\u5efa\u6709\u4e00\u5ea7\u5c0f\u5c0f\u7684\u51c9 \u4eab\uff0c\u5012\u4e5f\u5341\u5206\u96c5\u81f4\uff0c\u82b1\u56ed\u4e2d\u5404\u5904\u98ce\u666f\u90fd\u6709\u9898\u548f\uff0c\u552f\u72ec\u8fd9\u5ea7\u51c9\u4eab\uff0c\u4e24\u8fb9\u7684\u5927\u7406\u77f3\u4e0a\u8fd8\u7559\u6709\u8054\u8bed \u7684\u5730\u4f4d\uff0c\u4e00\u7247\u7a7a\u767d\u3002 \u3000\u3000\u6bb5\u6f84\u5e73\u9053\uff1a\u201c\u7d2f\u4ef0\u5f20\u5927\u4fa0\u6587\u6b66\u5168\u624d\uff0c\u8bf7\u5f20\u5927\u4fa0\u5728\u6b64\u7559\u4e0b\u4e00\u70b9\u7b14\u58a8\u5982\u4f55\uff1f\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a \u201c\u7559\u7ed9\u5c0f\u8f88\u4eec\u51fa\u51fa\u51e4\u5934\u5427\uff0c\u4f60\u4eec\u8c01\u66ff\u6bb5\u738b\u7237\u5728\u6b64\u9898\u4e0a\u4e00\u8054\u3002\u201d\u6c90\u71d5\u8dc3\u8dc3\u6b32\u8bd5\uff0c\u4e00\u65f6\u5374\u60f3\u4e0d\u51fa \u5408\u9002\u7684\u8054\u8bed\uff0c\u671b\u4e86\u94c1\u955c\u5fc3\u4e00\u773c\uff0c\u94c1\u955c\u5fc3\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u5012\u6709\u4e86\uff0c\u53ea\u662f\u5728\u5f20\u5927\u4fa0\u4e4b\u524d\uff0c\u53ef\u4e0d\u6562 \u732e\u4e11\u3002\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u94c1\u516c\u5b50\u5bb6\u5b66\u6e0a\u6e90\uff0c\u5b9a\u7136\u662f\u597d\u7684\u4e86\u3002\u201d\u6c90\u71d5\u4e5f\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u8bf4\u4f60\u51fa \u98ce\u5934\u4fbf\u662f\uff0c\u5feb\u5199\u51fa\u6765\u3002\u201d\u94c1\u955c\u5fc3\u5f97\u5f20\u4e39\u67ab\u4e00\u8d5e\uff0c\u751a\u4e3a\u5f97\u610f\uff0c\u7d22\u4e86\u7eb8\u7b14\uff0c\u4e00\u6325\u800c\u5c31\uff0c\u8054\u9053\uff1a \u3000\u3000\u201c\u4f9d\u7136\u660e\u5a9a\u5c71\u5ddd\uff0c\u4e00\u77f3\u5343\u79cb\u6491\u534a\u58c1\uff1b \u3000\u3000\u4f3c\u6b64\u5a46\u5a46\u98ce\u6708\uff0c\u56db\u6865\u4e24\u5cb8\u843d\u53cc\u8679\u3002\u201d \u3000\u3000\u6c90\u71d5\u9996\u5148\u62cd\u624b\u8d5e\u9053\uff1a\u201c\u5207\u666f\u5207\u9898\uff0c\u547d\u610f\u6df1\u8fdc\uff0c\u786e\u662f\u4f73\u4f5c\u3002\u201d\u6bb5\u6f84\u5e73\u56e0\u4ed6\u8054\u9996\u7684\u90a3\u53e5\u201c\u4e00 \u77f3\u5343\u79cb\u6491\u534a\u58c1\u201d\uff0c\u542b\u6709\u53cc\u5173\u4e4b\u610f\uff0c\u501f\u5927\u77f3\u800c\u55bb\u6bb5\u5bb6\uff0c\u4ea6\u662f\u6781\u4e3a\u6b22\u559c\uff0c\u8fde\u8fde\u8d5e\u7f8e\uff0c\u7acb\u523b\u4ee4\u4eba\u5c06 \u94c1\u955c\u5fc3\u6240\u9898\u7684\u8054\u8bed\u4ea4\u7ed9\u77f3\u5320\u523b\u4e0a\u3002\u5f20\u4e39\u67ab\u4e5f\u70b9\u5934\u8d5e\u597d\uff0c\u5fc3\u4e2d\u5374\u5728\u60f3\u9053\uff1a\u201c\u6b64\u4eba\u786e\u662f\u6709\u70b9\u624d \u534e\uff0c\u53ea\u53ef\u60dc\u4e0a\u8054\u8bed\u6c14\u751a\u8c6a\uff0c\u4e0b\u8054\u8f6f\u5f31\uff0c\u5374\u662f\u914d\u4e0d\u4e0a\u4e86\u3002\u770b\u5176\u6587\u800c\u89c2\u5176\u4eba\uff0c\u53ea\u6015\u4ed6\u6709\u5934\u65e0\u5c3e\uff0c \u6b20\u7f3a\u6bc5\u529b\u3002\u201d \u3000\u3000\u4f17\u4eba\u5728\u77f3\u4e0b\u5c0f\u61a9\uff0c\u6bb5\u6f84\u5e73\u610f\u5174\u751a\u8c6a\uff0c\u53c8\u53eb\u6b66\u58eb\u6765\u6454\u89d2\u52a9\u5174\uff0c\u6c90\u71d5\u770b\u5f97\u9ad8\u5174\uff0c\u7b11\u9053\uff1a\u201c\u8fd9 \u4e2a\u73a9\u610f\u5012\u597d\u73a9\uff0c\u94c1\u516c\u5b50\u4f60\u4f55\u4e0d\u4e0b\u53bb\u8bd5\u8bd5\uff01\u201d\u6bb5\u6f84\u5e73\u9053\uff1a\u201c\u539f\u6765\u94c1\u516c\u5b50\u4e5f\u662f\u6587\u6b66\u53cc\u624d\uff0c\u563f\uff0c\u4f60 \u4eec\u5f97\u6b64\u673a\u4f1a\uff0c\u5feb\u6765\u5411\u94c1\u516c\u5b50\u9886\u6559\u3002\u201d\u90a3\u4e24\u4e2a\u6b66\u58eb\u89c1\u94c1\u955c\u5fc3\u4e00\u8868\u65af\u6587\uff0c\u4e0d\u6562\u51fa\u5c3d\u5168\u529b\uff0c\u53ea\u6015\u6454 \u574f\u4e86\u8d35\u5bbe\u3002\u54ea\u77e5\u94c1\u955c\u5fc3\u5f97\u6bb5\u738b\u7237\u4e00\u8d5e\uff0c\u8d8a\u53d1\u6709\u610f\u5356\u5f04\uff0c\u90a3\u4e24\u4e2a\u6b66\u58eb\u672c\u6765\u5c31\u4e0d\u662f\u94c1\u955c\u5fc3\u7684\u5bf9 \u624b\uff0c\u88ab\u4ed6\u4e00\u6454\uff0c\u4e24\u4e2a\u6b66\u58eb\u90fd\u8dcc\u5f97\u56db\u811a\u671d\u5929\uff0c\u989d\u4e0a\u78b0\u8d77\u4e86\u597d\u5927\u4e00\u4e2a\u7624\uff01\u4e8e\u627f\u73e0\u4e0d\u89c9\u7709\u5934\u4e00\u76b1\uff0c \u770b\u6c90\u71d5\u65f6\uff0c\u6c90\u71d5\u4e5f\u4f3c\u6709\u70b9\u5c34\u5c2c\uff0c\u6bb5\u738b\u7237\u8eab\u4e3a\u4e3b\u4eba\uff0c\u53ea\u6709\u62cd\u638c\u8d5e\u597d\uff0c\u94c1\u955c\u5fc3\u521d\u65f6\u4e5f\u6599\u4e0d\u5230\u6454\u5f97 \u90a3\u4e48\u91cd\uff0c\u4f46\u5f97\u4e3b\u4eba\u4e00\u8d5e\uff0c\u795e\u8272\u4e5f\u5c31\u6062\u590d\u81ea\u5982\u3002 \u3000\u3000\u6e38\u5b8c\u738b\u5e9c\uff0c\u5df2\u8fd1\u9ec4\u660f\uff0c\u4f17\u4eba\u544a\u8f9e\u56de\u53bb\u3002\u5728\u8def\u4e0a\u4e8e\u627f\u73e0\u8ba9\u94c1\u955c\u5fc3\u548c\u6c90\u71d5\u5148\u8d70\uff0c\u6545\u610f\u548c\u53f6\u6210 \u6797\u8d70\u5728\u540e\u9762\uff0c\u6084\u6084\u8bf4\u9053\uff1a\u201c\u8bf4\u5230\u6454\u89d2\uff0c\u4f60\u7684\u5927\u529b\u91d1\u521a\u624b\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u82e5\u7136\u4eca\u65e5\u662f\u4f60\u51fa\u624b\uff0c\u90a3 \u4e24\u4e2a\u6b66\u58eb\u66f4\u8981\u5403\u82e6\u5934\u4e86\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u53c8\u4e0d\u662f\u548c\u654c\u4eba\u62fc\u547d\uff0c\u9002\u53ef\u800c\u6b62\u4e0d\u597d\u5417\u3002\u4e0d\u8fc7\u94c1\u516c\u5b50 \u6454\u89d2\u7684\u624b\u6cd5\u5229\u843d\u5e72\u51c0\uff0c\u4e5f\u7684\u786e\u4ee4\u4eba\u4f69\u670d\u3002\u201d\u4e8e\u627f\u73e0\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u4eca\u5929\u4e3a\u4f55\u603b\u4e0d\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u53f6\u6210\u6797\u9053\uff1a\u201c\u6211\u770b\u90a3\u738b\u5e9c\u5efa\u7b51\u7684\u5f62\u52bf\uff0c\u4e09\u9762\u4f9d\u5c71\uff0c\u4e00\u9762\u508d\u6c34\uff0c\u5b88\u5fa1\u575a\u56fa\uff0c\u4f46\u82e5\u4ece\u6c34\u9053\u4e0a \u8f7b\u821f\u5947\u88ad\uff0c\u4e00\u4e0a\u5cb8\u4fbf\u53ef\u5230\u5185\u56f4\u9632\u5730\uff0c\u5c71\u4e0a\u7684\u9632\u519b\u56de\u5e08\u4e0d\u53ca\uff0c\u5374\u662f\u5371\u9669\u3002\u52a0\u4ee5\u738b\u5e9c\u5b64\u60ac\u57ce\u5916\uff0c \u4e0e\u57ce\u5185\u7684\u5b88\u519b\u4e5f\u7f3a\u4e4f\u7167\u5e94\uff0c\u5047\u82e5\u6211\u662f\u654c\u519b\u7684\u4e3b\u5e05\uff0c\u6211\u5fc5\u8d70\u7528\u5947\u5175\u5148\u653b\u5360\u738b\u5e9c\u3002\u4ee5\u5236\u5927\u7406\u3002\u201d \u4e8e\u627f\u73e0\u9053\uff1a\u201c\u539f\u6765\u4f60\u6574\u5929\u4e0d\u8bf4\u8bdd\uff0c\u5374\u662f\u5728\u60f3\u7740\u7528\u5175\u4e4b\u9053\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u4e0d\u8fc7\u654c\u4eba\u82e5\u6d3e\u5947\u5175 \u5077\u88ad\uff0c\u53ea\u80fd\u7528\u5c11\u91cf\u7684\u5175\u529b\uff0c\u624d\u80fd\u5077\u6e21\u6c5f\u9632\uff0c\u6211\u65b9\u82e5\u65e9\u6709\u9632\u5907\uff0c\u53ea\u6d88\u4ee5\u6570\u767e\u8bad\u7ec3\u6709\u7d20\u7684\u6c34\u5e08\uff0c \u5728\u6d31\u6d77\u4e0a\u6e38\u8bbe\u9632\uff0c\u4fbf\u53ef\u4ee5\u8bf1\u654c\u6df1\u5165\uff0c\u4e00\u7f51\u6210\u64d2\u3002\u201d\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4f60\u5728\u738b\u5e9c\u5185\u5c3d\u662f\u770b \u90a3\u58c1\u4e0a\u753b\u7684\u519b\u4e8b\u5730\u56fe\u3002\u201d \u3000\u3000\u4e24\u4eba\u7684\u8c08\u8bdd\u5ffd\u88ab\u6c90\u71d5\u67d4\u5a9a\u7684\u7b11\u58f0\u6240\u6253\u65ad\uff0c\u4e8e\u627f\u73e0\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u6c90\u71d5\u548c\u94c1\u955c\u5fc3\u5e76\u80a9\u800c \u8d70\uff0c\u72b6\u751a\u4eb2\u70ed\uff0c\u4e8e\u627f\u73e0\u9762\u4e0a\u4e00\u70ed\uff0c\u773c\u5149\u5c1a\u672a\u53ca\u6536\u56de\uff0c\u94c1\u955c\u5fc3\u5ffd\u7136\u56de\u5934\u4e00\u77a5\uff0c\u56db\u76ee\u76f8\u4ea4\uff0c\u4e24\u4eba \u90fd\u7f00\u7f13\u5730\u4f4e\u4e0b\u5934\u53bb\uff0c\u8fd9\u4e00\u5239\u90a3\uff0c\u4e8e\u627f\u73e0\u5fc3\u5934\u9707\u8361\uff0c\u4f46\u89c9\u94c1\u955c\u5fc3\u7684\u773c\u5149\u4e2d\u542b\u6709\u65e0\u9650\u5e7d\u6028\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u4e8e\u627f\u73e0\u53c8\u662f\u8f97\u8f6c\u601d\u91cf\uff0c\u76f4\u5230\u5348\u591c\u4e4b\u540e\uff0c\u624d\u9616\u4e0a\u7761\u773c\u3002\u53f6\u6210\u6797\u6734\u8bb7\u6c89\u6bc5\u7684\u5f71\u5b50\u548c\u94c1 \u955c\u5fc3\u90a3\u6f47\u6d12\u800c\u53c8\u5e26\u7740\u5e7d\u6028\u7684\u795e\u60c5\u4ecd\u662f\u4e0d\u65ad\u5730\u5728\u5979\u5fc3\u5934\u6d6e\u73b0\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\u8d77\u6765\uff0c\u4e8e\u627f\u73e0\u5728\u5979\u5e08\u7236\u7684\u7a97\u5916\u5f98\u5f8a\uff0c\u5374\u4e0d\u6562\u53e9\u95e8\u6c42\u89c1\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5f20\u4e39\u67ab\u5f00 \u95e8\u51fa\u6765\uff0c\u89c1\u7740\u4e8e\u627f\u73e0\uff0c\u5fae\u5fae\u4e00\u8be7\uff0c\u7b11\u9053\uff1a\u201c\u627f\u73e0\uff0c\u4f60\u53ef\u662f\u6709\u4ec0\u4e48\u5fc3\u4e8b\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6ca1 \u6709\u4ec0\u4e48\uff0c\u5f92\u513f\u6765\u5411\u5e08\u7236\u8bf7\u5b89\u3002\u201d\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u4e0e\u4e8e\u627f\u73e0\u8d70\u51fa\u9662\u5b50\uff0c\u51ed\u680f\u773a\u671b\u82cd\u5c71\u6d31\u6d77\u7684 \u6c34\u8272\u5c71\u5149\uff0c\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u55ef\uff0c\u65e5\u5b50\u8fc7\u5f97\u771f\u5feb\uff0c\u4f60\u5df2\u6ee1\u4e86\u5341\u516d\u5c81\u4e86\uff0c\u662f\u5417\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fc7 \u4e86\u5341\u516d\u5c81\u7684\u751f\u65e5\u53c8\u4e09\u4e2a\u6708\u5566\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u6765\u592a\u6e56\u5c71\u5e84\u7684\u65f6\u5019\uff0c\u8fd8\u53ea\u6709\u4e03\u5c81\uff0c\u90a3\u65f6\u8fd8\u540a \u7740\u9f3b\u6d95\u5462\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5341\u5e74\u6765\u591a\u8c22\u5e08\u7236\u7684\u6559\u8bf2\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u770b\u4f60\u957f\u5927\u6210\u4eba\uff0c\u6211 \u4e5f\u653e\u5fc3\u4e86\uff0c\u4e0d\u8fc7\u2026\u2026\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u4e03\u5c81\u4e4b\u65f6\uff0c\u5f53\u65f6\u4e0d\u4f1a\u60f3\u4ec0 \u4e48\u5fc3\u4e8b\uff0c\u73b0\u5728\u662f\u5341\u4e03\u5c81\u7684\u5927\u59d1\u5a18\u5566\uff0c\u6211\u5374\u4e0d\u80fd\u4e0d\u4e3a\u4f60\u5728\u5176\u4ed6\u7684\u4e8b\u60c5\u4e0a\u62c5\u5fc3\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9ed8\u7136\u4e0d\u8bed\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5ffd\u7136\u62ac\u5934\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u666f\u8272\u548c\u592a\u6e56\u5c71\u5e84\u5404\u6709\u80dc\u573a\u3002\u201d\u5f20 \u4e39\u67ab\u9053\uff1a\u201c\u662f\u5440\uff0c\u6d31\u6d77\u53ef\u6bd4\u592a\u6e56\uff0c\u5230\u6625\u5929\u7684\u65f6\u5019\uff0c\u6ee1\u5c71\u662f\u82b1\uff0c\u90a3\u666f\u8272\u5c31\u66f4\u50cf\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u5ffd \u9053\uff1a\u201c\u82cd\u5c71\u4e0a\u4e5f\u6709\u73ab\u7470\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6211\u5012\u6ca1\u6709\u7559\u610f\u3002\u4e0d\u8fc7\u5927\u7406\u56db\u5b63\u5982\u6625\uff0c \u5c31\u7b97\u6ca1\u6709\u653f\u7470\u82b1\uff0c\u82e5\u662f\u4ece\u6c5f\u5357\u79fb\u690d\u8fc7\u6765\uff0c\u6211\u60f3\u4e5f\u53ef\u4ee5\u6210\u957f\u7684\u3002\u201d\u4e8e\u627f\u73e0\u5ffd\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u559c \u6b22\u6c5f\u5357\u56ed\u6797\u91cc\u7684\u73ab\u7470\u82b1\uff0c\u8fd8\u662f\u559c\u6b22\u8fd9\u91cc\u7684\u5927\u9752\u6811\uff1f\u201d\u5f20\u4e39\u67ab\u53c8\u662f\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u7136\u597d\u4f3c\u4ece\u773c\u5149 \u4e2d\u731c\u5230\u4e86\u4e8e\u627f\u73e0\u5fc3\u5934\u7684\u79d8\u5bc6\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4e24\u6837\u6211\u90fd\u559c\u6b22\u3002\u73ab\u7470\u82b1\u4ee4\u4eba\u8d4f\u5fc3\u60a6\u76ee\uff0c\u5927\u9752\u6811\u53ef \u4ee5\u4f9b\u4eba\u4e58\u51c9\u906e\u836b\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d\uff0c\u5047\u5982\u53ea\u8bb8\u4f60\u9009\u62e9\u4e00\u6837\u5462\uff1f\u201d\u51dd\u7738\u671b\u7740\u5e08\u7236\uff0c\u90a3\u60c5\u5f62\u5c31 \u50cf\u5b69\u5b50\u9047\u5230\u96be\u9898\uff0c\u8981\u8bf7\u5927\u4eba\u7ed9\u4ed6\u4e00\u4e2a\u51b3\u5b9a\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u60f3\u4e86\u4e00\u4f1a\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u8981\u770b\u5404\u4e2a\u4eba\u7684\u6027\u60c5\u4e86\u3002\u6bd4\u5982\u8bf4\uff0c\u82e5\u662f\u6c90\u71d5\uff0c\u6211\u60f3\u5979\u4f1a\u66f4 \u559c\u6b22\u73ab\u7470\u82b1\u3002\u201d\u4e8e\u627f\u73e0\u70b9\u4e86\u70b9\u5934\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u82e5\u8bf4\u5230\u5bf9\u4eba\u7c7b\u7684\u7528\u5904\uff0c\u90a3\u81ea\u662f \u5927\u9752\u6811\u6709\u7528\u5f97\u591a\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u53c8\u70b9\u4e86\u70b9\u5934\u3002\u5f20\u4e39\u67ab\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u518d\u8fc7\u4e24\u5e74\uff0c\u518d\u60f3\u60f3\u8fd9 \u4e9b\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d\u4e8e\u627f\u73e0\u9762\u4e0a\u4e00\u7ea2\uff0c\u5f20\u4e39\u67ab\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u53bb\u548c\u5e08\u6bcd\u8c08\u8c08\u3002\u5979\u60f3\u8003\u8003\u4f60\u7684\u6697 \u5668\u529f\u592b\u5462\u3002\u6211\u8fd8\u8981\u5230\u738b\u5e9c\u53bb\u6253\u4e00\u8f6c\uff0c\u540e\u5929\u662f\u4f60\u592a\u5e08\u7956\u5f00\u5173\u7684\u65e5\u5b50\uff0c\u4f60\u7ec3\u70b9\u529f\u592b\u7ed9\u4ed6\u8001\u4eba\u5bb6 \u770b\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u8d70\u540e\uff0c\u4e8e\u627f\u73e0\u5480\u56bc\u4ed6\u7684\u8bf4\u8bdd\uff0c\u5fc3\u5934\u4ecd\u662f\u4e00\u7247\u70e6\u95f7\uff0c\u60f3\u8fdb\u53bb\u627e\u5e08\u6bcd\uff0c\u542c\u5f97\u91cc\u9762\u5b69\u5b50 \u7684\u54ed\u58f0\uff0c\u4e91\u857e\u4f3c\u4e4e\u6b63\u5728\u7ed9\u5b69\u5b50\u5582\u5976\uff0c\u4e8e\u627f\u73e0\u4e0d\u60f3\u5728\u8fd9\u4e2a\u65f6\u5019\u53bb\u6253\u6270\u5979\u3002\u6b63\u81ea\u6005\u7136\uff0c\u5ffd\u89c1\u5c0f\u864e \u5b50\u8e66\u8e66\u8df3\u8df3\u5730\u8d70\u8fdb\u6765\u3002\u5c0f\u864e\u5b50\u4e00\u628a\u5c06\u5979\u62c9\u770b\uff0c\u56b7\u9053\uff1a\u201c\u627e\u4e86\u4f60\u8bb8\u4e45\uff0c\u539f\u6765\u4f60\u5728\u8fd9\u513f\uff0c\u5feb\u6765\uff0c \u5feb\u6765\uff0c\u54b1\u4eec\u53bb\u6349\u5f13\u9c7c\u53bb\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u8fd8\u6709\u6c90\u5c0f\u516c\u7239\u5462\uff0c\u4ed6\u53eb\u6211\u6765\u9080\u4f60\u53bb\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u65e0\u53ef\u65e0\u4e0d\u53ef\u5730\u966a\u5c0f\u864e\u5b50\u53bb\u6349\u5f13\u9c7c\uff0c\u6c90\u78f7\u89c1\u5979\uff0c\u5927\u4e3a\u6b22\u559c\uff0c\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\uff0c\u4f60 \u597d\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u5478\uff0c\u5979\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u8981\u4f60\u95ee\u5019\u3002\u201d\u6c90\u78f7\u6da8\u7ea2\u4e86\u8138\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u793c\u8282\u3002\u5c0f \u864e\u5b50\uff0c\u4f60\u771f\u50cf\u4e00\u4e2a\u91ce\u5b69\u5b50\u3002\u201d\u5c0f\u864e\u5b50\u53cc\u80a9\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u662f\u91ce\u5b69\u5b50\uff0c\u4f60\u662f\u5927\u5c11\u7237\uff0c\u4f60\u4e0d \u800d\u548c\u6211\u4eec\u73a9\uff01\u201d\u6c90\u78f7\u5fd9\u6c42\u9976\u9053\uff1a\u201c\u7b97\u6211\u8bf4\u9519\u4e86\u8bdd\uff0c\u5c0f\u864e\u5b50\u5440\uff0c\u6211\u6015\u4e86\u4f60\u4e86\uff01\u201d\u4e8e\u627f\u73e0\u77a7\u770b\u8fd9 \u4e24\u4e2a\u8bf4\u5c0f\u4e0d\u5c0f\u8bf4\u5927\u4e0d\u5927\u7684\u5b69\u5b50\uff0c\u563b\u563b\u54c8\u54c8\u5730\u95f9\u7740\u73a9\uff0c\u5fc3\u5934\u7684\u70e6\u95f7\u5012\u662f\u89e3\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u90a3\u5f13\u9c7c\u662f\u6d31\u6d77\u7684\u7279\u4ea7\uff0c\u4e5f\u662f\u4e16\u754c\u4e0a\u72ec\u4e00\u65e0\u4e8c\u7684\u6709\u7740\u602a\u813e\u6c14\u7684\u9c7c\uff0c\u522b\u79cd\u9c7c\u90fd\u662f\u987a\u6d41\u800c\u6e38\uff0c \u53ea\u6709\u5f13\u9c7c\u662f\u9006\u6c34\u4e0a\u6e38\uff0c\u6c38\u4e0d\u56de\u5934\uff01\u5b83\u4ece\u6d31\u6d77\u9006\u6e38\uff0c\u6cbf\u7740\u82cd\u5c71\u5341\u516b\u5cf0\u7684\u6eaa\u6d41\uff0c\u5e38\u5e38\u6e38\u4e0a\u82cd\u5c71\u7684 \u5c71\u9876\uff0c\u6e38\u4e0d\u4e0a\u53bb\u65f6\uff0c\u5c31\u5f13\u7740\u8170\u5c04\u5411\u524d\u9762\uff0c\u600e\u4e48\u4e5f\u4e0d\u9000\u540e\uff0c\u6240\u4ee5\u53eb\u505a\u5f13\u9c7c\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u6298\u4e86\u4e00\u6280\u67d4\u679d\uff0c\u7ed3\u6210\u4e00\u4e2a\u5708\u5708\uff0c\u5f04\u5f97\u597d\u4f3c\u4e00\u6761\u8f6f\u97ad\u4e00\u6837\uff0c\u4e00\u89c1\u5f13\u9c7c\u5c04\u51fa\u6c34\u9762\uff0c\u968f \u624b\u4e00\u5708\uff0c\u4fbf\u4e22\u8fdb\u9c7c\u7bd3\uff0c\u624b\u6cd5\u8fc5\u75be\uff0c\u767e\u4e0d\u5931\u4e00\uff0c\u4e0d\u591a\u4e00\u4f1a\uff0c\u7bd3\u4e2d\u5df2\u5806\u4e86\u534a\u7bd3\u5f13\u9c7c\uff0c\u6c90\u78f7\u770b\u5f97\u9891 \u9891\u53eb\u597d\uff0c\u5c0f\u864e\u5b50\u6b63\u81ea\u6349\u7740\u9ad8\u5174\uff0c\u5ffd\u89c1\u6eaa\u4e2d\u4eba\u5f71\u4e00\u6643\uff0c\u6a2a\u523a\u91cc\u4e00\u6761\u67f3\u679d\u62c2\u6765\uff0c\u5c06\u5c0f\u864e\u5b50\u7684\u201c\u8f6f \u97ad\u201d\u62c2\u843d\u6eaa\u4e2d\uff0c\u63a5\u7740\u201c\u535c\u901a\u201d\u4e00\u58f0\uff0c\u6c34\u82b1\u56db\u6e85\uff0c\u90a3\u534a\u7bd3\u5f13\u9c7c\uff0c\u4e5f\u5168\u7ed9\u6765\u4eba\u503e\u5012\u6eaa\u4e2d\uff0c\u8f6c\u77ac\u4e4b \u95f4\uff0c\u90fd\u5411\u4e0a\u6d41\u6e38\u53bb\u4e86\uff0c\u8fd9\u4eba\u662f\u53f6\u6210\u6797\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u6012\u9053\uff1a\u201c\u53f6\u5927\u54e5\uff0c\u4f60\u505a\u4ec0\u4e48\uff1f\u201d\u53f6\u6210\u6797\u7b11\u9053\uff1a\u201c\u5f13\u9c7c\u4e0d\u754f\u8270\u96be\uff0c\u767e\u6298\u4e0d\u6320\uff0c\u529b\u4e89 \u4e0a\u6e38\uff0c\u5b9e\u662f\u503c\u5f97\u656c\u4ef0\uff0c\u4f60\u5374\u5c06\u5b83\u6349\u6765\u5173\u5728\u9c7c\u7bd3\u91cc\uff0c\u53eb\u6211\u77a7\u89c1\u4e86\uff0c\u5c82\u80fd\u4e0d\u4e3a\u5b83\u6253\u62b1\u4e0d\u5e73\uff1f\u201d\u5c0f \u864e\u5b50\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u597d\uff0c\u7b97\u4f60\u6709\u7406\uff01\u201d\u62cd\u62cd\u624b\u7ad9\u4e86\u8d77\u6765\uff0c\u8fde\u9c7c\u7bd3\u4e5f\u4e0d\u8981\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u548c\u6c90\u78f7\u90fd\u7b11\u4e86\u8d77\u6765\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u6781\u5176\u523a\u8033\u7684\u7b11\u58f0\uff0c\u5c06\u4ed6\u4eec\u7684\u58f0\u97f3\u90fd\u538b\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u516d\u4e03\u4e2a\u5947\u5f62\u602a\u72b6\u7684\u4eba\u7269\uff0c\u7a81\u7136\u5728\u5c71\u5761\u4e0a\u51fa\u73b0\uff0c\u5176\u4e2d\u4e00\u4e2a\uff0c\u53d1\u7ea2\u5982\u706b\uff0c\u53cc\u817f\u633a\u76f4\uff0c\u8e66 \u5730\u4e00\u8df3\uff0c\u5c31\u662f\u4e03\u516b\u5c3a\u9ad8\uff0c\u4e24\u4e09\u4e08\u8fdc\uff0c\u77aa\u7740\u4e24\u53ea\u94dc\u94c3\u5927\u7684\u773c\u775b\uff0c\u5411\u7740\u4e8e\u627f\u73e0\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u4e00 \u4e2a\u6f02\u4eae\u7684\u5c0f\u59d1\u5a18\uff01\u201d\u51ed\u7740\u4e8e\u627f\u73e0\u548c\u53f6\u6210\u6797\u7ec3\u8fc7\u6697\u5668\u7684\u8033\u529b\uff0c\u4ed6\u4eec\u90a3\u4e48\u591a\u4eba\uff0c\u7adf\u7136\u5230\u4e86\u8ddf\u524d\uff0c \u624d\u7ed9\u53d1\u73b0\uff0c\u53f6\u6210\u6797\u4e0d\u7531\u5f97\u6697\u6697\u5403\u60ca\uff0c\u770b\u6765\u8fd9\u4e00\u4f19\u4eba\u4e2a\u4e2a\u90fd\u662f\u9876\u513f\u5c16\u513f\u7684\u6b66\u6797\u9ad8\u624b\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u7ea2\u53d1\u602a\u4eba\uff0c\u731b\u5730\u4e00\u8df3\uff0c\u5c31\u8df3\u5230\u4e86\u4e8e\u627f\u73e0\u9762\u524d\uff0c\u4f38\u5f00\u84b2\u6247\u822c\u5927\u7684\u602a\u624b \u5f80\u4e0b\u4e00\u6293\uff0c\u4e8e\u627f\u73e0\u51b7\u4e0d\u53ca\u9632\uff0c\u51e0\u4e4e\u7ed9\u4ed6\u6293\u7740\uff0c\u5c0f\u864e\u5b50\u5927\u559d\u4e00\u58f0\uff0c\u201c\u7830\u201d\u7684\u4e00\u62f3\u6253\u5c06\u51fa\u53bb\uff0c\u8fd9 \u4e00\u62f3\u4e43\u662f\u201c\u9f99\u62f3\u201d\uff0c\u5c0f\u864e\u5b50\u867d\u7136\u5e74\u5c11\uff0c\u8fd9\u4e00\u62f3\u5c11\u8bf4\u4e5f\u6709\u516d\u4e03\u767e\u65a4\u6c14\u529b\u3001\u4f46\u542c\u5f97\u201c\u7bf7\u201d\u7684\u4e00\u58f0 \u5de8\u54cd\uff0c\u8dcc\u5012\u7684\u4e0d\u662f\u602a\u4eba\uff0c\u5374\u662f\u5c0f\u864e\u5b50\uff0c\u4ed6\u88ab\u90a3\u602a\u4eba\u5f39\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u6536\u52bf\u4e0d\u4f4f\uff0c\u76f4\u8dcc\u5230\u6c34\u91cc\u53bb \u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u4f19\u4eba\u6b63\u662f\u8d64\u971e\u9053\u4eba\u4ece\u56db\u9762\u516b\u65b9\u9080\u8bf7\u6765\u7684\u6709\u540d\u9b54\u5934\uff0c\u5176\u4e2d\u7684\u516d\u9633\u771f\u541b\u6700\u4e3a\u597d\u8272\uff0c\u4e00\u89c1 \u4e8e\u627f\u73e0\u8fd9\u4e48\u7f8e\u8c8c\uff0c\u7adf\u7136\u4e0d\u95ee\u6765\u5386\uff0c\u4f38\u624b\u4fbf\u62a2\u3002 \u3000\u3000\u5c31\u5728\u5c0f\u864e\u5b50\u88ab\u516d\u9633\u771f\u541b\u9707\u5012\u7684\u540c\u65f6\uff0c\u4e8e\u627f\u73e0\u65e9\u5df2\u62d4\u51fa\u9752\u51a5\u5b9d\u5251\uff0c\u516d\u9633\u771f\u541b\u6beb\u4e0d\u5728\u610f\uff0c\u54a7 \u5f00\u5634\u5df4\uff0c\u957f\u81c2\u4e00\u5377\uff0c\u4ecd\u7136\u8086\u65e0\u5fcc\u60ee\u5730\u6293\u6765\uff0c\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\uff0c\u4f46\u89c1\u5bd2\u5149\u4e00\u95ea\uff0c\u4e8e\u627f\u73e0\u4f7f\u51fa\u2018\u7a7f \u82b1\u7ed5\u6811\u2019\u7684\u8eab\u6cd5\uff0c\u5728\u4ed6\u8eab\u8fb9\u500f\u5730\u7ed5\u8fc7\uff0c\u9752\u51a5\u5251\u4e00\u62db\u201c\u7389\u5973\u6295\u68ad\u201d\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u516d\u9633\u771f \u541b\u7684\u9053\u888d\u524a\u53bb\u4e86\u597d\u4e00\u5927\u7247\uff0c\u4f46\u5251\u950b\u89e6\u53ca\u4ed6\u7684\u8863\u8896\uff0c\u7adf\u7136\u4e5f\u7ed9\u53cd\u5f39\u5f00\u6765\uff0c\u516d\u9633\u771f\u541b\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u597d\u4e00\u628a\u5b9d\u5251\uff0c\u7f8e\u4eba\u5b9d\u5251\uff0c\u4e24\u8005\u4ff1\u5f97\uff0c\u5c82\u4e0d\u5feb\u54c9\uff1f\u201d\u4e00\u722a\u6293\u4e0b\uff0c\u62ff\u7740\u4e86\u4e8e\u627f\u73e0\u80a9\u5934\u7684\u7435\u7436 \u9aa8\uff0c\u8fd9\u7435\u7436\u9aa8\u4e43\u662f\u4eba\u8eab\u4e0a\u6700\u8106\u5f31\u7684\u8f6f\u9aa8\uff0c\u7eb5\u662f\u6b66\u529f\u9ad8\u5f3a\u4e4b\u58eb\uff0c\u5f7c\u4eba\u6293\u4f4f\u4e86\u7435\u7436\u9aa8\uff0c\u4ea6\u662f\u4f11\u60f3 \u52a8\u5f39\u3002 \u3000\u3000\u516d\u9633\u771f\u541b\u6b63\u81ea\u6d0b\u6d0b\u5f97\u610f\uff0c\u521a\u8981\u628a\u4e8e\u627f\u73e0\u6273\u8f6c\u8fc7\u6765\uff0c\u53ea\u542c\u5f97\u53c8\u662f\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u516d\u9633\u771f\u541b \u80cc\u5fc3\u4e00\u9635\u5267\u75db\uff0c\u7adf\u7136\u4e0d\u7531\u81ea\u5df1\u5730\u5411\u524d\u51b2\u4e86\u4e24\u6b65\uff0c\u4e8e\u627f\u73e0\u8d81\u52bf\u5012\u8f6c\u5251\u67c4\uff0c\u75be\u70b9\u4ed6\u4e73\u4e0b\u7684\u201c\u5fd7\u5802 \u7a74\u201d\uff0c\u5251\u67c4\u649e\u5230\u4ed6\u7684\u80f8\u819b\uff0c\u53d1\u51fa\u6728\u77f3\u822c\u7684\u58f0\u54cd\uff0c\u516d\u9633\u771f\u541b\u7adf\u4e0d\u8dcc\u5012\uff0c\u6447\u6447\u6643\u6643\u5730\u53eb\u9053\uff1a\u201c\u597d \u554a\uff0c\u539f\u6765\u4f60\u8fd8\u61c2\u6b66\u529f\uff0c\u8fd9\u66f4\u597d\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u4e0d\u5bb9\u4ed6\u7a0d\u606f\uff0c\u5530\u5530\u5530\uff0c\u4fbf\u662f\u4e00\u8fde\u4e09\u5251\uff0c\u62ac\u5934\u4e00 \u770b\uff0c\u53ea\u89c1\u53f6\u6210\u6797\u5728\u5730\u4e0a\u4e0d\u505c\u7684\u6253\u7740\u8f6c\u513f\uff0c\u62f3\u5934\u80bf\u5f97\u6d77\u7897\u822c\u5927\u3002\u539f\u6765\u521a\u624d\u51fb\u5728\u516d\u9633\u771f\u541b\u80cc\u5fc3\u90a3 \u4e00\u62f3\u6b63\u662f\u53f6\u6210\u6797\u6253\u7684\uff0c\u53f6\u6210\u6797\u7684\u5927\u529b\u91d1\u521a\u624b\u6709\u5f00\u7891\u88c2\u77f3\u4e4b\u529f\uff0c\u4e0d\u6599\u8fd9\u4e00\u62f3\u7adf\u7136\u53ea\u80fd\u628a\u516d\u9633\u771f \u541b\u6253\u5f97\u51b2\u51fa\u4e24\u4e08\uff0c\u800c\u53f6\u6210\u6797\u7684\u62f3\u5934\u53cd\u800c\u6253\u80bf\uff0c\u8fd8\u7ed9\u4ed6\u7684\u53cd\u529b\u9707\u5f97\u7a33\u4e0d\u4f4f\u8eab\u5f62\u3002 \u3000\u3000\u516d\u9633\u771f\u541b\u4e5f\u6599\u4e0d\u5230\u8fd9\u51e0\u4e2a\u5c11\u5e74\u7537\u5973\u6709\u8fd9\u4e48\u5f3a\u7684\u6b66\u529f\uff0c\u4ed6\u63a5\u8fde\u51fa\u624b\uff0c\u8fd8\u662f\u6293\u4e0d\u7740\u4e8e\u627f\u73e0\uff0c \u53cd\u800c\u6328\u4e86\u53f6\u6210\u6797\u4e00\u62f3\uff0c\u5fc3\u4e2d\u751a\u662f\u607c\u6012\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7075\u5de7\u4e4b\u6781\uff0c\u77e5\u9053\u81ea\u5df1\u7684\u6b66\u529f\u6bd4\u4e4b\u654c\u4eba\u5dee\u5f97\u592a\u8fdc\uff0c\u4fbf\u53ea\u7528\u7a7f\u82b1\u7ed5\u6811\u7684\u8eab\u6cd5\uff0c\u4ed7\u7740\u9752 \u51a5\u5b9d\u5251\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u516d\u9633\u771f\u541b\u867d\u7136\u7ec3\u6709\u91d1\u949f\u7f69\u3001\u94c1\u5e03\u886b\u7684\u529f\u592b\uff0c\u5bf9\u8fd9\u628a\u524a\u94c1\u5982\u6ce5 \u7684\u5b9d\u521b\u4e5f\u4e0d\u80fd\u4e0d\u6709\u6240\u987e\u5fcc\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u5df2\u88ab\u4e8e\u627f\u73e0\u63a5\u4e86\u4ed6\u5341\u4f59\u62db\uff0c\u5140\u662f\u6293\u4e0d\u7740\u5979\u7684\u8863\u89d2\u3002\u4e8e \u627f\u73e0\u53eb\u9053\uff1a\u201c\u53f6\u5e08\u5144\uff0c\u5feb\u56de\u53bb\u8bf7\u5e08\u7236\u3002\u201d \u3000\u3000\u53f6\u6210\u6797\u8fd9\u65f6\u5df2\u6d88\u89e3\u4e86\u90a3\u80a1\u53cd\u9707\u4e4b\u529b\uff0c\u4f46\u89c1\u4e8e\u627f\u73e0\u5f62\u52bf\u5371\u6025\uff0c\u4e0d\u8dd1\u56de\u53bb\uff0c\u53cd\u8fce\u4e0a\u6765\uff0c\u4ed6\u53f3 \u624b\u7ea2\u80bf\u80c0\u75db\uff0c\u5de6\u624b\u4ecd\u7136\u53ef\u7528\uff0c\u6325\u52a8\u5de6\u62f3\uff0c\u53c8\u5411\u516d\u9633\u771f\u541b\u731b\u51fb\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u53eb\u9053\uff1a\u201c\u4e0d\u53ef\u8089\u640f\uff0c\u4ed6\u6709\u6c14\u529f\uff01\u201d\u5251\u5149\u4e00\u95ea\uff0c\u9694\u5f00\u4e86\u516d\u9633\u771f\u541b\u548c\u53f6\u6210\u6797\uff0c\u53f6\u6210\u6797 \u5de6\u81c2\u4e00\u5c55\uff0c\u62b1\u4f4f\u4e86\u4e00\u68f5\u5c0f\u6811\uff0c\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u628a\u90a3\u5c0f\u6811\u8fde\u6839\u62d4\u8d77\uff0c\u5411\u516d\u9633\u771f\u541b\u62e6\u8170\u75be\u626b\uff0c\u7aef \u7684\u662f\u52c7\u731b\u7edd\u4f26\uff0c\u594b\u4e0d\u987e\u8eab\u3002 \u3000\u3000\u516d\u9633\u771f\u541b\u7ec3\u7684\u662f\u6df7\u5143\u4e00\u6c14\u529f\uff0c\u9002\u624d\u4e3a\u4e86\u4e0d\u613f\u4f24\u53ca\u4e8e\u756a\u73e0\uff0c\u820d\u800c\u4e0d\u7528\uff0c\u53ea\u7528\u672c\u8eab\u771f\u529b\uff0c\u4e0e \u4e4b\u5468\u65cb\uff0c\u8fd9\u65f6\u4e45\u6218\u4e0d\u4e0b\uff0c\u6df1\u611f\u5931\u4e86\u9762\u5b50\uff0c\u6740\u673a\u9661\u8d77\uff0c\u60f3\u9053\uff1a\u201c\u6211\u5148\u6bd9\u4e86\u8fd9\u4e2a\u5c0f\u5b50\uff0c\u518d\u5c06\u8fd9\u4e2a \u4e2b\u5934\u5413\u6655\uff01\u201d\u9163\u6597\u4e4b\u4e2d\uff0c\u5ffd\u7136\u4f38\u8170\u6df1\u6df1\u5438\u6c14\uff0c\u5ffd\u542c\u5f97\u8d64\u971e\u9053\u4eba\u5927\u58f0\u559d\u9053\uff1a\u201c\u771f\u4e3b\u624b\u4e0b\u7559\u60c5\uff0c \u8fd9\u4e2b\u5934\u662f\u5f20\u4e39\u67ab\u7684\u5f92\u5f1f\uff01\u201d \u3000\u3000\u8d64\u971e\u9053\u4eba\u672c\u6765\u4e0d\u8ba4\u5f97\u4e8e\u627f\u73e0\uff0c\u4ed6\u5e26\u6765\u7684\u5927\u5f92\u5f1f\u76d8\u5929\u7f57\u5374\u8ba4\u5f97\u3002\u76d8\u5929\u7f57\u66fe\u5403\u8fc7\u4e8e\u627f\u73e0\u7684 \u4e8f\uff0c\u6068\u4e0d\u5f97\u516d\u9633\u771f\u541b\u5c06\u5979\u64d2\u53bb\uff0c\u56e0\u6b64\u4e00\u76f4\u4e0d\u80af\u8bf4\u660e\uff1b\u53cd\u800c\u662f\u8d64\u971e\u9053\u4eba\u77a7\u7740\u4e8e\u627f\u73e0\u7684\u5251\u6cd5\u597d\u5f97 \u51fa\u5947\u3002\u8d77\u4e86\u7591\u5fc3\uff0c\u5411\u76d8\u5929\u7f57\u95ee\u53ca\uff0c\u5e08\u5c0a\u95ee\u53ca\uff0c\u76d8\u5929\u7f57\u81ea\u662f\u4e0d\u80fd\u4e0d\u8bf4\u3002\u8d64\u971e\u9053\u4eba\u5e76\u4e0d\u662f\u6015\u5f20\u4e39 \u67ab\uff0c\u5374\u4e0d\u662f\u5bf9\u4e8e\u627f\u73e0\u6709\u6240\u7231\u60dc\uff0c\u4f46\u4ed6\u5230\u5e95\u662f\u4e00\u6d3e\u5b97\u5e08\uff0c\u884c\u4e8b\u5f97\u4f9d\u6b66\u6797\u89c4\u77e9\uff0c\u672a\u89c1\u4e3b\u4eba\uff0c\u672a\u66fe \u4ea4\u624b\uff0c\u5c31\u4fae\u8fb1\u4eba\u5bb6\u7684\u5973\u5f92\u5f1f\uff0c\u8fd9\u4e8b\u8bf4\u51fa\u53bb\u603b\u662f\u4e0d\u592a\u5149\u5f69\u3002\u56e0\u6b64\u8d64\u971e\u9053\u4eba\u8fde\u5fd9\u559d\u6b62\u516d\u9633\u771f\u541b\u3002 \u3000\u3000\u516d\u9633\u771f\u541b\u609a\u7136\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u662f\u5f20\u4e39\u67ab\u7684\u5f92\u5f1f\uff0c\u5012\u4e0d\u53ef\u80e1\u6765\u4e86\u3002\u201d\u4f46\u4ed6\u7684\u6df7\u5143\u4e00\u6c14 \u529f\u5df2\u7ecf\u4f7f\u74dc\u6025\u5207\u4e4b\u95f4\uff0c\u4e0d\u80fd\u5168\u90e8\u64a4\u56de\uff0c\u53ea\u542c\u5f97\u201c\u548b\u5566\u5566\u201d\u51e0\u58f0\u731b\u9707\uff0c\u53f6\u6210\u6797\u90a3\u68f5\u5c0f\u6811\u6298\u4e3a\u51e0 \u6bb5\uff0c\u5e78\u800c\u516d\u9633\u771f\u541b\u672a\u5c3d\u5168\u529b\uff0c\u53f6\u6210\u6797\u4e5f\u6709\u5927\u529b\u91d1\u521a\u638c\u62a4\u8eab\uff0c\u4e00\u89c1\u4e0d\u597d\uff0c\u7acb\u523b\u629b\u6811\u64a4\u638c\uff0c\u56de\u62a4 \u80f8\u524d\uff0c\u5728\u5730\u4e0a\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u7ffb\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u6ca1\u6709\u53d7\u7740\u5185\u4f24\u3002 \u3000\u3000\u5728\u53f6\u6210\u6797\u624b\u4e2d\u7684\u6811\u6728\u88ab\u9707\u65ad\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u4e8e\u627f\u73e0\u4e3a\u4e86\u62a2\u6551\u53f6\u6210\u6797\uff0c\u4ea6\u5df2\u8c41\u4e86\u6027\u547d\uff0c\u8fd0\u5251 \u5982\u98ce\uff0c\u6b3a\u8eab\u75be\u523a\uff0c\u671f\u800c\u5230\u5e95\u8fdf\u4e86\u4e00\u6b65\uff0c\u53f6\u6210\u6797\u5df2\u88ab\u629b\u5f00\uff0c\u516d\u9633\u771f\u541b\u7684\u6df7\u5143\u4e00\u6c14\u529f\u4ea6\u5df2\u6536\u56de\uff0c \u957f\u8896\u4e00\u62c2\uff0c\u5377\u7740\u4e86\u4e8e\u627f\u73e0\u7684\u5b9d\u5251\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u7b49\u4e0b\u6211\u518d\u5411\u4f60\u7684\u5e08\u7236\u8ba8\u4eba\uff0c\u6211\u8981\u5f20\u4e39\u67ab\u8ba9\u4f60 \u505a\u6211\u7684\u5f92\u7ed9\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u6c14\u5f97\u9762\u8272\u9752\u767d\uff0c\u7528\u52b2\u518d\u523a\uff0c\u4f46\u90a3\u628a\u5251\u88ab\u516d\u9633\u771f\u541b\u7684\u957f\u8896\u88f9\u4f4f\uff0c\u5374\u662f\u62d4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u53f6\u6210\u6797\u5fcd\u7740\u75bc\u75db\uff0c\u4ece\u5730\u4e0a\u8dc3\u8d77\uff0c\u60f3\u53eb\u6c90\u78f7\u56de\u53bb\u62a5\u8baf\uff0c\u4e3e\u5934\u4e00\u770b\uff0c\u5374\u4e0d\u89c1\u6c90\u78f7\u7684\u5f71\u5b50\u3002\u4ed6 \u4e0d\u77e5\u9053\u516d\u9633\u771f\u541b\u5bf9\u4e8e\u627f\u73e0\u53ea\u662f\u5fc3\u5b58\u620f\u5f04\uff0c\u89c1\u4e8e\u627f\u73e0\u7684\u5b9d\u5251\u88ab\u516d\u9633\u771f\u541b\u7684\u957f\u8896\u5377\u4f4f\uff0c\u5fc3\u4e5f\u5927 \u6025\uff0c\u54ac\u7d27\u7259\u6839\uff0c\u6323\u624e\u7740\u53c8\u51b2\u4e0a\u53bb\u3002 \u3000\u3000\u516d\u9633\u771f\u541b\u51b7\u7b11\u9053\uff1a\u201c\u81ed\u5c0f\u5b50\uff0c\u60f3\u627e\u6b7b\u4e48\uff1f\u201d\u4e00\u53ea\u8896\u5b50\u5377\u7740\u4e8e\u627f\u73e0\u7684\u5b9d\u5251\uff0c\u53e6\u4e00\u53ea\u8896\u5b50\u50fb \u556a\u4e00\u6325\uff0c\u671d\u7740\u53f6\u6210\u6797\u8fce\u9762\u62cd\u6253\uff0c\u53f6\u6210\u6797\u53ea\u6709\u4e00\u638c\u53ef\u7528\uff0c\u62a4\u7740\u80f8\u819b\u5c31\u62a4\u4e0d\u4e86\u9762\u95e8\uff0c\u516d\u9633\u771f\u541b\u8fd9 \u4e00\u62cd\u52bf\u9053\u51cc\u5389\uff0c\u770b\u6765\u5b9e\u662f\u96be\u4ee5\u8eb2\u5f00\u3002 \u3000\u3000\u52b2\u98ce\u62c2\u9762\uff0c\u53f6\u6210\u6797\u7a81\u611f\u6655\u7729\uff0c\u6b63\u60f3\u62fc\u6b7b\u8fdb\u62db\uff0c\u9661\u7136\u4e4b\u95f4\u5ffd\u89c1\u7eff\u5149\u4e00\u95ea\uff0c\u4e00\u80a1\u6f5c\u529b\u5c06\u53f6\u6210 \u6797\u63a8\u51fa\u6570\u6b65\uff0c\u4eba\u672a\u7ad9\u8d70\uff0c\u4f46\u542c\u5f97\u4e8e\u627f\u73e0\u4e00\u58f0\u6b22\u547c\uff0c\u51e0\u6761\u4eba\u5f71\u98de\u817e\u800c\u8d77\uff0c\u968f\u5373\u542c\u5f97\u516d\u9633\u771f\u541b\u5927 \u53eb\u4e00\u58f0\uff0c\u8dcc\u4e0b\u5730\u6765\uff0c\u4e24\u9053\u7eff\u5149\u767d\u5149\u8854\u5c3e\u6025\u8feb\u800c\u4e0b\uff0c\u53f6\u6210\u6797\u8fd9\u65f6\u624d\u770b\u5f97\u6e05\u695a\uff0c\u539f\u6765\u9ed1\u767d\u6469\u8bc3\u5230 \u4e86\uff01 \u3000\u3000\u539f\u6765\u9ed1\u767d\u6469\u8bc3\u662f\u88ab\u5c0f\u864e\u5b50\u53eb\u6765\u7684\uff0c\u5c0f\u864e\u5b50\u7cbe\u901a\u6c34\u6027\uff0c\u8dcc\u4e0b\u6eaa\u6d41\u4e4b\u540e\uff0c\u6f5c\u6c34\u9006\u6e38\uff0c\u8c01\u4e5f\u6ca1 \u6709\u6ce8\u610f\u4ed6\u3002\u6b63\u597d\u9ed1\u767d\u6469\u8bc3\u60f3\u4e0b\u5c71\u8bbf\u6bb5\u6f84\u82cd\uff0c\u5728\u5c71\u5761\u4e0a\u4fbf\u78b0\u5230\u5c0f\u864e\u5b50\uff0c\u542c\u5f97\u6709\u4eba\u6562\u6b3a\u4fae\u4e8e\u627f \u73e0\uff0c\u7acb\u523b\u5982\u98de\u8d76\u5230\uff0c\u4e00\u7167\u9762\u4fbf\u65bd\u6740\u624b\uff0c\u516d\u9633\u771f\u541b\u63d0\u4e0d\u53ca\u9632\uff0c\u5148\u5403\u4e86\u4e00\u5927\u4e8f\u3002 \u3000\u3000\u516d\u9633\u771f\u541b\u6b66\u529f\u4e5f\u786e\u662f\u60ca\u4eba\uff0c\u88ab\u9ed1\u767d\u6469\u8bc3\u53cc\u6756\u9707\u98de\uff0c\u5c45\u7136\u4e00\u8dcc\u4e0b\u5730\u4fbf\u7acb\u5373\u7a33\u4f4f\u8eab\u5f62\uff0c\u767d\u6469 \u8bc3\u4e00\u6756\u5288\u4e0b\uff0c\u516d\u9633\u771f\u541b\u5927\u543c\u4e00\u58f0\uff0c\u8fd9\u65f6\u4ed6\u7684\u6df7\u5143\u4e00\u6c14\u529f\u5df2\u7136\u4f7f\u51fa\uff0c\u53cc\u638c\u5f00\u63a8\uff0c\u52bf\u5982\u6392\u5c71\u5012\u6d77 \u4e00\u767d\u6469\u8bc3\u7684\u5b9d\u6756\u7adf\u88ab\u4ed6\u8361\u5f00\u5c3a\u8bb8\uff0c\u516d\u9633\u771f\u541b\u8fd0\u8db3\u771f\u6c14\uff0c\u7b2c\u4e8c\u638c\u8fd8\u672a\u62cd\u51fa\uff0c\u9661\u542c\u5f97\u9ed1\u6469\u8bc3\u4e00\u58f0 \u5927\u559d\uff1a\u201c\u4f55\u65b9\u5996\u4eba\u6562\u5230\u82cd\u5c71\u653e\u8086\uff01\u201d\u8fd9\u4e00\u559d\u76f4\u5982\u6674\u5929\u9aa4\u8d77\u9739\u96f3\uff0c\u9707\u5f97\u4eba\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u516d\u9633 \u771f\u541b\u5927\u5403\u4e00\u60ca\uff0c\u4f46\u4ed6\u51f6\u608d\u6210\u6027\uff0c\u660e\u77e5\u6765\u4eba\u5185\u529f\u6df1\u539a\uff0c\u90a3\u4e00\u638c\u66f4\u662f\u62fc\u4e86\u5168\u529b\u51fb\u51fa\u53bb\u3002 \u3000\u3000\u4f46\u89c1\u767d\u6469\u8bc3\u8eab\u5f62\u4e00\u6643\uff0c\u767d\u5149\u7a0d\u7a0d\u4e00\u504f\uff0c\u9ed1\u6469\u6cb3\u7684\u7eff\u7389\u6756\u540e\u53d1\u5148\u81f3\uff0c\u7eff\u5149\u767d\u5149\u53cc\u6756\u4e00\u5408\uff0c \u767b\u65f6\u5f62\u6210\u4e86\u4e00\u9053\u4e24\u8272\u7684\u5149\u8f6e\uff0c\u516d\u9633\u771f\u541b\u7684\u6df7\u5143\u4e00\u6c14\u529f\u867d\u82e5\u72c2\u6f6e\u6012\u5377\uff0c\u5374\u88ab\u90a3\u5149\u8f6e\u6321\u4f4f\uff0c\u4fbf\u4f3c \u78b0\u5230\u4e86\u94c1\u58c1\u94dc\u5899\uff01 \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u53cc\u6756\u5408\u56f4\uff0c\u4e00\u62db\u731b\u8fc7\u4e00\u62db\uff0c\u516d\u9633\u771f\u541b\u4ed7\u7740\u6df7\u5143\u4e00\u6c14\u529f\u5de6\u51b2\u53f3\u7a81\uff0c\u5374\u603b\u662f\u51b2\u4e0d\u51fa \u90a3\u4e24\u8272\u5149\u5708\uff0c\u4f17\u9b54\u5934\u4e2d\u53ea\u6709\u54c0\u7262\u5c71\u7684\u9e20\u76d8\u5a46\u516c\u5b59\u65e0\u57a2\u4e0e\u516d\u9633\u771f\u541b\u6700\u597d\uff0c\u9f99\u5934\u62d0\u6756\u4e00\u987f\uff0c\u4fbf\u60f3 \u8d70\u51fa\uff0c\u8d64\u971e\u9053\u4eba\u9053\uff1a\u201c\u5148\u522b\u6df7\u6218\uff0c\u4e14\u5f85\u6211\u5148\u53bb\u628a\u8bdd\u8bf4\u660e\u3002\u201d\u7f13\u7f13\u8d70\u51fa\uff0c\u7fbd\u6247\u4e00\u6325\uff0c\u9053\uff1a\u201c\u8fd9 \u4e24\u4f4d\u53ef\u662f\u9ed1\u767d\u6469\u8bc3\u4e48\uff1f\u201d\u9ed1\u767d\u6469\u8bc3\u53cc\u6756\u5f00\u4e0b\uff0c\u6b63\u81ea\u65bd\u5c55\u6740\u624b\uff0c\u88ab\u90a3\u7fbd\u6247\u4e00\u62e8\uff0c\u4e24\u8272\u5149\u8f6e\u7adf\u81ea \u7f29\u77ed\u4e86\u51e0\u5bf8\uff0c\u516d\u9633\u771f\u541b\u677e\u4e86\u53e3\u6c14\uff0c\u731b\u5730\u8dc3\u8d77\uff0c\u4e00\u62db\u201c\u9e4f\u6377\u4e5d\u9704\u201d\uff0c\u51cc\u7a7a\u51fb\u4e0b\uff0c\u9ed1\u767d\u6469\u8bc3\u5927\u6012 \u559d\u9053\uff1a\u201c\u4eca\u65e5\u82e5\u653e\u4f60\u8fd9\u5996\u4eba\u751f\u51fa\u6b64\u5c71\uff0c\u6c5f\u6e56\u9053\u4e0a\u5c31\u62b9\u4e86\u6211\u9ed1\u767d\u6469\u8bc3\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u8d64\u971e\u9053\u4eba\u7fbd\u6247\u8f7b\u6447\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u51e0\u4f4d\u9053\u5144\u771f\u4e0d\u7ed9\u8d2b\u9053\u4e00\u70b9\u9762\u5b50\u4e48\uff1f\u201d\u9ed1\u81ea\u6469\u8bc3\u53cc\u6756\u4e00 \u5708\uff0c\u5148\u6321\u4f4f\u4e86\u516d\u9633\u771f\u541b\uff0c\u6b63\u60f3\u56de\u7b54\uff0c\u5ffd\u542c\u5f97\u5c71\u5934\u4e0a\u4e00\u58f0\u6e05\u5578\uff0c\u4e00\u4e2a\u6781\u5176\u6e05\u8106\u7684\u58f0\u97f3\u7f13\u7f13\u8bf4 \u9053\uff1a\u201c\u539f\u6765\u662f\u8d64\u971e\u9053\u957f\u524d\u6765\uff0c\u6709\u5931\u8fdc\u8fce\u4e86\u3002\u9ed1\u767d\u4e8c\u5144\u6682\u4e14\u56de\u6765\uff0c\u6709\u8bdd\u597d\u8bf4\u3002\u201d\u8bf4\u8bdd\u7684\u4eba\u6b63\u662f \u5f20\u4e39\u67ab\u3002\u4e8e\u627f\u73e0\u5fc3\u4e2d\u5927\u559c\uff0c\u5979\u6b63\u5fe7\u8651\u5e08\u7236\u5230\u6bb5\u738b\u5e9c\u53bb\uff0c\u4e0d\u53ca\u56de\u6765\u5e94\u654c\uff0c\u5374\u6599\u4e0d\u5230\u5e08\u7236\u65e9\u5df2\u8d76 \u56de\u6765\u4e86\u3002 \u3000\u3000\u8d64\u971e\u9053\u4eba\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u679c\u662f\u540d\u4e0d\u865a\u4f20\uff0c\u542c\u4ed6\u8fd9\u4f20\u97f3\u5165\u5bc6\u7684\u529f\u592b\u51fa\u529b\u7adf\u4f3c\u4e0d \u900a\u4e8e\u6211\u3002\u201d\u60f3\u8d77\u5f20\u4e39\u67ab\u4e0d\u8fc7\u662f\u7384\u673a\u9038\u571f\u7684\u7b2c\u4e09\u4ee3\u5f1f\u5b50\uff0c\u4e0d\u7981\u6709\u4e9b\u5fc3\u865a\uff0c\u6025\u5fd9\u62c9\u7740\u4e86\u516d\u9633\u771f \u541b\uff0c\u9053\uff1a\u201c\u662f\u554a\uff0c\u54b1\u4eec\u4e14\u53bb\u89c1\u4e86\u4e3b\u4eba\u518d\u8bf4\u3002\u201d\u516d\u9633\u771f\u541b\u6ee1\u8179\u6012\u6c14\uff0c\u4e0d\u5f97\u4e0d\u4f9d\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u8f7b\u8f7b\u6276\u7740\u53f6\u6210\u6797\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u4f24\u5f97\u91cd\u4e48\uff1f\u201d\u53f6\u6210\u6797\u6367\u7740\u90a3\u6761\u88ab\u9707\u4f24\u7684\u81c2 \u818a\uff0c\u5fcd\u7740\u75db\u7b11\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u53ea\u662f\u4e00\u5916\u4f24\u3002\u201d\u4e8e\u627f\u73e0\u8fc7\u610f\u4e0d\u53bb\uff0c\u6276\u7740\u4ed6\u8d70\uff0c\u53f6\u6210\u6797\u4e0d\u4fbf\u62d2 \u7edd\uff0c\u8138\u5b54\u6da8\u7ea2\u5f97\u6bd4\u624b\u81c2\u8fd8\u8981\u5389\u5bb3\u3002 \u3000\u3000\u4f17\u4eba\u56de\u5230\u5c71\u4e0a\uff0c\u6c90\u78f7\u4e0d\u77e5\u4ece\u54ea\u513f\u94bb\u4e86\u51fa\u6765\uff0c\u89c1\u53f6\u6210\u6797\u81c2\u818a\u80bf\u5f97\u6c34\u6876\u822c\u7c97\u5927\uff0c\u60ca\u5f97\u5446\u4e86\uff0c \u53f6\u6210\u6797\u8f7b\u8f7b\u62cd\u4ed6\u7684\u80a9\u818a\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u5f1f\u6ca1\u5413\u7740\u4e48\uff1f\u201d\u6c90\u78f7\u597d\u751f\u60ed\u6127\uff0c\u9053\uff1a\u201c\u5440\uff0c\u53ef\u60dc \u6211\u4e0d\u4f1a\u6b66\u529f\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u90a3\u4f60\u8ddf\u6211\u5b66\u561b\uff01\u201d\u6c90\u78f7\u672c\u6765\u60f3\u548c\u4e8e\u627f\u73e0\u8bf4\u8bdd\uff0c\u60f3\u8d77\u521a\u624d\u88ab\u654c\u4eba \u5413\u5f97\u9003\u8dd1\uff0c\u5ffd\u89c9\u96be\u4ee5\u4e3a\u60c5\uff0c\u8baa\u8baa\u5730\u548c\u5c0f\u864e\u5b50\u5148\u8dd1\u4e0a\u5c71\u3002 \u3000\u3000\u5c71\u4e0a\u5f20\u4e39\u67ab\u3001\u4e4c\u8499\u592b\u3001\u4e91\u91cd\u4e09\u5bf9\u592b\u5f52\u548c\u6fb9\u53f0\u706d\u660e\u3001\u94c1\u955c\u5fc3\u7b49\u4eba\u5e76\u5217\u4e00\u8d77\uff0c\u5f20\u4e39\u67ab\u89c1\u53f6\u6210 \u6797\u53d7\u4f24\uff0c\u53d6\u51fa\u79d8\u5236\u7684\u91d1\u521b\u836f\u53eb\u4ed6\u53bb\u6577\u3002\u8d64\u971e\u9053\u4eba\u7387\u9886\u4e00\u4f17\u9b54\u5934\uff0c\u5728\u9ed1\u767d\u6469\u8bc3\u7684\u80cc\u540e\uff0c\u8fd9\u65f6\u4ea6 \u5df2\u5230\u4e86\u5c71\u4e0a\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5fae\u7b11\u95ee\u9053\uff1a\u201c\u8d64\u971e\u9053\u957f\u6b64\u6765\uff0c\u6709\u4f55\u8d50\u6559\uff1f\u201d\u8d64\u971e\u9053\u4eba\u9053\uff1a\u201c\u7279\u6765\u7ed9\u7384\u673a\u9038\u58eb\u62dc \u5bff\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u655d\u5e08\u7956\u7684\u5bff\u8fb0\u4e43\u662f\u540e\u5929\u3002\u201d\u8d64\u971e\u9053\u4eba\u9053\uff1a\u201c\u5148\u5230\u4e3a\u656c\u3002\u60f3\u7384\u673a\u9038\u58eb\u4e0d\u4f1a \u95ed\u95e8\u4e0d\u7eb3\uff0c\u70e6\u4f60\u5411\u4ee4\u5e08\u7956\u901a\u62a5\u4e00\u58f0\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u655d\u5e08\u7956\u4e0e\u4e0a\u5b98\u8001\u524d\u8f88\u95ed\u95e8\u5750\u5173\uff0c\u8981\u5230\u540e \u65e5\u5bff\u8fb0\uff0c\u624d\u5f00\u5173\u89c1\u5ba2\u3002\u201d\u8d64\u971e\u9053\u4eba\u9762\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u771f\u7684\u4e48\uff1f\u201d\u9ed1\u767d\u6469\u8bc3\u6012\u9053\uff1a\u201c\u4ed6\u4eec\u6015\u4f60 \u4ec0\u4e48\uff1f\u96be\u9053\u4f60\u4ee5\u4e3a\u7384\u673a\u8001\u524d\u8f88\u662f\u4e0d\u6562\u89c1\u4f60\uff0c\u6545\u4f5c\u9041\u8bcd\u4e48\uff1f\u201d\u8d64\u971e\u9053\u4eba\u7565\u4e00\u6c89\u541f\uff0c\u5316\u6012\u4e3a\u559c\uff0c \u7b11\u9053\uff1a\u201c\u90a3\u5c31\u771f\u4e2a\u4e0d\u5de7\u4e86\u3002\u4e0d\u8fc7\u4e0e\u7384\u673a\u9038\u571f\u4efd\u5c5e\u6545\u4eba\uff0c\u4eca\u65e5\u65e2\u7136\u5230\u6765\uff0c\u8bf4\u4e0d\u5f97\u53ea\u597d\u53e8\u6270\u5b9d \u6bbf\uff0c\u7b49\u5019\u8001\u53cb\u5f00\u5173\u4e86\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u655d\u5e08\u7956\u95ed\u95e8\u5750\u5173\uff0c\u4e8b\u5148\u66fe\u6709\u5429\u5490\uff0c\u4e0d\u8bb8\u522b\u4eba\u5608\u6270\u3002\u8bf7\u6055\u6211\u4e0d\u6562\u62db\u5f85 \u8bf8\u4f4d\u3002\u201d\u8d64\u971e\u9053\u4eba\u52c3\u7136\u53d8\u8272\uff0c\u9053\uff1a\u201c\u6211\u4e0e\u7384\u673a\u9038\u571f\u8ba2\u4ea4\u4e4b\u65f6\uff0c\u4f60\u8fd8\u6ca1\u6709\u51fa\u4e16\u5462\uff01\u201d\u6fb9\u53f0\u706d\u660e \u51b7\u7b11\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u5f20\u4e39\u67ab\u5c31\u66f4\u4e0d\u5fc5\u5356\u4f60\u7684\u5e10\u4e86\u3002\u4f60\u8981\u8bb2\u4ea4\u60c5\uff0c\u5f85\u540e\u65e5\u548c\u7384\u673a\u524d\u8f88\u8bb2\u53bb\uff0c\u6c5f\u6e56 \u4e0a\u5404\u8bb2\u5404\u7684\u4ea4\u60c5\uff0c\u4f60\u4e0d\u61c2\u4e48\uff1f\u201d \u3000\u3000\u8d64\u971e\u9053\u4eba\u6012\u9053\uff1a\u201c\u81ea\u4eec\u8fdc\u6765\u975e\u6613\uff0c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u5f53\u771f\u662f\u60f3\u95ed\u95e8\u4e0d\u7eb3\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8bf8 \u4f4d\u65e2\u662f\u4e3a\u62dc\u5bff\u800c\u6765\uff0c\u540e\u65e5\u4e0a\u5c71\uff0c\u5f85\u6211\u7980\u660e\u5e08\u7956\uff0c\u81ea\u5f53\u6b3e\u5f85\uff0c\u4eca\u65e5\u53ea\u597d\u5931\u656c\u4e86\u3002\u201d\u9e20\u76d8\u5a46\u94c1\u62d0 \u91cd\u91cd\u4e00\u987f\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u597d\u5927\u7684\u67b6\u5b50\uff01\u201d\u8d64\u971e\u9053\u4eba\u7fbd\u6247\u4e00\u6447\uff0c\u5ffd\u5730\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec \u53ef\u77e5\uff0c\u6211\u4eca\u65e5\u6b64\u6765\uff0c\u9664\u4e86\u5411\u7384\u673a\u9038\u58eb\u62dc\u5bff\u4e4b\u5916\uff0c\u8fd8\u6709\u522b\u4e8b\u4e48\uff1f\u201d\u767d\u6469\u8bc3\u9053\uff1a\u201c\u6211\u4eec\u53c8\u4e0d\u662f\u4f60 \u809a\u91cc\u7684\u86d4\u866b\uff0c\u8c01\u77e5\u9053\u4f60\u6253\u7684\u4ec0\u4e48\u5fc3\u601d\uff1f\u201d \u3000\u3000\u8d64\u971e\u9053\u4eba\u6c14\u5f97\u56de\u8272\u94c1\u9752\uff0c\u7fbd\u6247\u4e00\u6447\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u4e0e\u95f2\u4eba\u6253\u8bdd\u3002\u5f20\u4e39\u67ab\uff0c\u6211\u6765\u95ee\u4f60\uff0c\u4f60\u7684 \u5e08\u7236\u5e08\u4f2f\u4e5f\u6ca1\u6765\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5bb6\u5e08\u53ea\u6015\u4e5f\u8981\u5230\u540e\u65e5\u624d\u80fd\u8d76\u5230\u3002\u201d\u8d64\u971e\u9053\u4eba\u51b7\u7b11\u9053\uff1a \u201c\u90a3\u5c31\u771f\u662f\u51b7\u4e86\u6211\u4eec\u8fd9\u4e00\u73ed\u9053\u53cb\u6155\u540d\u6765\u8bbf\u4e4b\u5fc3\u4e86\u3002\u201d\u4e0e\u8d64\u971e\u9053\u4eba\u540c\u6765\u7684\u6606\u4ed1\u5c71\u5949\u5bbf\u6d77\u6458\u661f\u4e0a \u4eba\u4ef0\u5929\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u7b11\u9053\uff1a\u201c\u53ea\u6015\u662f\u865a\u6709\u5176\u540d\uff0c\u6709\u610f\u6302\u514d\u6218\u724c\u662f\u5b9e\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u7709\u6bdb\u4e00\u626c\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u8d64\u971e\u9053\u4eba\u9053\uff1a\u201c\u4e09\u5341\u5e74\u524d\uff0c\u6211\u4e0e\u7384\u673a\u9038\u58eb\u5207\u78cb\u6b66\u529f\uff0c \u9886\u76ca\u4e0d\u6d45\u3002\u95fb\u8bf4\u4ed6\u8fd9\u4e9b\u5e74\u6765\uff0c\u6b66\u529f\u8d8a\u53d1\u7cbe\u7eaf\u4e86\u3002\u8fd9\u51e0\u4f4d\u9053\u4eba\u90fd\u662f\u6d77\u5185\u9ad8\u4eba\uff0c\u53ea\u662f\u672a\u6709\u673a\u7f18\u5f97 \u4e0e\u4eca\u5e08\u7236\u8bf7\u6559\u3002\u6240\u4ee5\u8fd9\u6b21\u4e0e\u6211\u540c\u5230\u5b9d\u5c71\uff0c\u4e00\u6765\u662f\u8d3a\u4ed6\u516b\u5341\u5927\u5bff\uff0c\u4e8c\u6765\u4e5f\u662f\u60f3\u85c9\u6b64\u673a\u7f18\u89c1\u8bc6\u89c1 \u8bc6\u540d\u626c\u6d77\u5185\u7684\u7384\u673a\u9038\u58eb\u7684\u7edd\u6280\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u51b7\u7b11\u7eed\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u60f3\u5230\u7384\u673a\u9038\u58eb\u516b\u5341\u9ad8\u9f84\uff0c \u975e\u590d\u5f53\u5e74\u4e4b\u52c7\uff0c\u4f46\u4ed6\u95e8\u4e0b\u56db\u5927\u5f1f\u5b50\uff0c\u6bcf\u4eba\u90fd\u5f97\u4ed6\u4f20\u6388\u4e00\u9879\u7edd\u6280\uff0c\u5440\uff0c\u53ef\u60dc\u90fd\u4e0d\u5728\u6b64\uff0c\u8fd9\u5c82\u4e0d \u53eb\u6211\u4eec\u7a7a\u8dd1\u4e00\u8d9f\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u89c1\u8bc6\u7384\u673a\u9038\u58eb\u95e8\u4e0b\u7684\u6280\u827a\uff0c\u90a3\u53ef\u5bb9\u6613\uff0c \u7b2c\u4e09\u4ee3\u5f1f\u5b50\u8fd8\u6709\u51e0\u4eba\u5728\u6b64\uff0c\u7edd\u4e0d\u4f1a\u53eb\u4f60\u4eec\u5931\u671b\u800c\u56de\u3002\u201d\u4e4c\u8499\u592b\u4e5f\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4e0a\u5b98\u5929\u91ce\u7b2c\u4e8c \u4ee3\u5f1f\u5b50\u4e5f\u6709\u6570\u4eba\u5728\u6b64\uff0c\u8bf8\u4f4d\u60f3\u5207\u78cb\u6b66\u529f\uff0c\u54b1\u4eec\u4e5f\u4e00\u51c6\u5949\u966a\uff01\u201d\u9ed1\u767d\u6469\u8bc3\u5927\u53eb\u9053\uff1a\u201c\u54b1\u5144\u5f1f\u4e8c \u4eba\uff0c\u4e0d\u5c5e\u4efb\u4f55\u4e00\u6d3e\uff0c\u5c31\u662f\u770b\u4e0d\u60ef\u4f60\u4eec\u8fd9\u73ed\u5996\u90aa\uff0c\u5582\uff0c\u5f20\u4e39\u67ab\uff0c\u8fd9\u4e00\u67b6\u6211\u4e5f\u662f\u8981\u6253\u5b9a\u7684\u4e86\u3002\u201d \u3000\u3000\u8d64\u971e\u9053\u4eba\u9053\uff1a\u201c\u4e24\u4f4d\u4e5f\u80af\u6367\u573a\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\uff0c\u4e5f\u7701\u5f97\u6211\u4eec\u843d\u4e2a\u4ee5\u5927\u6b3a\u5c0f\u7684\u7f6a\u540d\u3002\u201d\u5176 \u5b9e\u8d64\u971e\u9053\u4eba\u542c\u8bf4\u7384\u673a\u9038\u58eb\u5750\u5173\uff0c\u56db\u5927\u5f1f\u5b50\u4ea6\u90fd\u4e0d\u5728\uff0c\u6b63\u662f\u5fc3\u4e2d\u6697\u559c\uff0c\u672c\u6765\u82e5\u662f\u4ed6\u60f3\u771f\u5fc3\u627e\u7384 \u673a\u9038\u58eb\u8f83\u6280\u7684\u8bdd\uff0c\u4e5f\u4e0d\u8fdf\u5728\u8fd9\u4e24\u5927\uff0c\u4ed6\u6b63\u662f\u60f3\u8d81\u6b64\u673a\u4f1a\uff0c\u5148\u5728\u82cd\u5c71\u5927\u95f9\u4e00\u573a\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u90a3\u5c31\u8bf7\u9053\u957f\u5212\u51fa\u9053\u6765\uff0c\u8981\u5982\u4f55\u5207\u78cb\uff0c\u54b1\u4eec\u4e00\u5b9a\u9886\u6559\u3002\u201d\u8d64\u971e\u9053\u4eba\u9000 \u4e0b\u53bb\u548c\u51e0\u4e2a\u9b54\u5934\u7a83\u7a83\u79c1\u8bae\uff0c\u5f20\u4e39\u67ab\u51b7\u773c\u65c1\u89c2\uff0c\u4f46\u89c1\u76d8\u5929\u7f57\u5728\u4ed6\u5e08\u7236\u65c1\u8fb9\u6307\u624b\u5212\u811a\uff0c\u9762\u8272\u4e00 \u53d8\uff0c\u5ffd\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d\u9ed1\u767d\u6469\u8bc3\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u9633\u5b97\u6d77\u662f\u4ed6\u6700\u5f97\u610f\u7684\u5f1f\u5b50\uff0c\u5374 \u4e0d\u540c\u6765\uff1b\u4ed6\u4eec\u65e2\u7136\u8bf4\u662f\u62dc\u5bff\uff0c\u5374\u6545\u610f\u63d0\u524d\u6765\u5230\uff0c\u6446\u660e\u662f\u60f3\u653b\u6211\u4eec\u4e2a\u63aa\u624b\u4e0d\u53ca\u3002\u53ea\u6015\u5176\u4e2d\u6709 \u8bc8\uff01\u201d\u9ed1\u6469\u8bc3\u9053\uff1a\u201c\u6211\u8fd8\u662f\u4e0d\u61c2\uff0c\u4f60\u5feb\u7ed9\u6211\u4eec\u5256\u5f00\uff0c\u4ed6\u4eec\u95f7\u846b\u82a6\u91cc\u5356\u7684\u662f\u4ec0\u4e48\u836f\uff1f\u201d \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u731c\u9633\u5b97\u6d77\u662f\u8bf7\u4ed6\u5e08\u7236\u51fa\u5934\uff0c\u7f20\u7740\u54b1\u4eec\uff0c\u4ed6\u5374\u53bb\u5077\u88ad\u738b\u5e9c\u3002\u8fd9\u51e0\u4e2a\u9b54\u5934\u867d \u7136\u5389\u5bb3\uff0c\u6211\u731c\u4ed6\u4eec\u4e5f\u754f\u5fcc\u4e0a\u5b98\u524d\u8f88\u548c\u6211\u7684\u5e08\u7956\uff0c\u6562\u60c5\u4ed6\u4eec\u4e5f\u6253\u63a2\u5230\u6211\u5e08\u7956\u5750\u5173\uff0c\u8fd9\u624d\u653e\u5fc3\u6765 \u6b64\u6311\u8845\u3002\u54fc\uff0c\u4ed6\u4eec\u7684\u7b97\u76d8\u53ef\u662f\u6253\u5f97\u518d\u597d\u4e0d\u8fc7\uff0c\u5728\u4ed6\u4eec\u7684\u5fc3\u91cc\u8fd9\u91cc\u53ea\u662f\u51e0\u4e2a\u5c0f\u8f88\u4e3b\u6301\uff0c\u738b\u5e9c\u90a3 \u8fb9\u4e5f\u65e0\u4eba\u62b5\u5fa1\uff0c\u5c82\u4e0d\u662f\u53ef\u4ee5\u5927\u83b7\u5168\u80dc\u3002\u201d\u9ed1\u767d\u6469\u8bc3\u9053\uff1a\u201c\u6211\u4e0d\u6015\u8fd9\u73ed\u5996\u90aa\uff0c\u5c31\u62c5\u5fc3\u738b\u5e9c\u88ab \u88ad\u3002\u201d\u4ed6\u4eec\u672c\u60f3\u5373\u523b\u8d76\u53bb\u738b\u5e9c\uff0c\u5374\u53c8\u820d\u4e0d\u5f97\u9519\u8fc7\u8fd9\u573a\u5927\u6218\uff0c\u751a\u662f\u8e0c\u8e87\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u9633\u5b97\u6d77\u6b66\u529f\u6b8a\u4e0d\u8db3\u9053\uff0c\u53ea\u662f\u4ed6\u82e5\u53bb\u5077\u88ad\u738b\u5e9c\uff0c\u5b9a\u662f\u4ece\u6c34\u8def\u8fdb\u5175\u3002\u8fd9\u4e0d\u662f\u4e00 \u4e24\u4e2a\u4eba\u8f83\u91cf\u6b66\u529f\u7684\u4e8b\uff0c\u987b\u5f97\u6709\u4e00\u4e2a\u61c2\u5f97\u5175\u6cd5\uff0c\u61c2\u5f97\u6c34\u6218\u7684\u4eba\uff0c\u8d76\u53bb\u6307\u6325\u3002\u738b\u5e9c\u90a3\u8fb9\uff0c\u6c34\u5e08\u5df2 \u6709\u9632\u5907\uff0c\u5c31\u662f\u7f3a\u5c11\u4e00\u4e2a\u6307\u6325\u4e4b\u4eba\u3002\u201d\u773c\u775b\u5411\u53f6\u6210\u6797\u779f\u4e86\u4e00\u773c\uff0c\u539f\u6765\u53f6\u6210\u6797\u6628\u665a\u4e4b\u6765\u4e4b\u540e\uff0c\u5df2 \u5c06\u738b\u5e9c\u9632\u5fa1\u758f\u5ffd\u4e4b\u5904\uff0c\u5bf9\u5f20\u4e39\u67ab\u8a00\u53ca\uff0c\u5f20\u4e39\u67ab\u4e5f\u6709\u540c\u611f\uff0c\u4eca\u671d\u5306\u5306\u8d76\u53bb\u738b\u5e9c\uff0c\u5c31\u662f\u63d0\u9192\u6bb5\u738b \u7237\u7684\u3002\u4e0d\u60f3\u4ed6\u4eec\u6765\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c\u5f20\u4e39\u67ab\u521a\u521a\u56de\u6765\uff0c\u4ed6\u4eec\u4e5f\u8ddf\u7740\u5230\u4e86\u3002 \u3000\u3000\u5728\u5f20\u4e39\u67ab\u7684\u610f\u601d\uff0c\u672c\u6765\u662f\u60f3\u53eb\u53f6\u6210\u6797\u53bb\u6700\u4e3a\u9002\u5408\uff0c\u4f46\u89c1\u4ed6\u624b\u81c2\u53d7\u4f24\uff0c\u7ea2\u80bf\u672a\u9000\uff0c\u773c\u5149\u4e00 \u8f6c\uff0c\u53c8\u5411\u94c1\u955c\u5fc3\u671b\u53bb\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u8fd9\u65f6\u6b63\u5728\u5411\u4e8e\u627f\u73e0\u5927\u732e\u6bb7\u52e4\uff0c\u53ea\u542c\u4ed6\u8bf4\u9053\uff1a\u201c\u54ce\u5440\uff0c\u4e8e\u59d1\u5a18\uff0c\u4f60\u53d7\u4e86\u4f24\u5566\uff0c\u80a9\u5934 \u4e5f\u7ed9\u6293\u7834\u5566\uff0c\u8ba9\u6211\u7ed9\u4f60\u6577\u4e0a\u836f\u818f\u3002\u201d\u5176\u5b9e\u4e8e\u627f\u73e0\u9002\u624d\u7ed9\u516d\u9633\u771f\u541b\u4e00\u6293\uff0c\u4ec5\u662f\u7ed9\u6293\u88c2\u4e00\u7247\u8863\u88f3 \u800c\u5df2\uff0c\u8fde\u76ae\u8089\u90fd\u6ca1\u6709\u4f24\u53ca\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u6b63\u5728\u7528\u5fc3\u542c\u5e08\u7236\u7684\u8bdd\uff0c\u94c1\u955c\u5fc3\u5728\u5979\u8033\u8fb9\u5520\u5520\u53e8\u53e8\uff0c\u5979\u7adf\u6ca1\u6709\u5168\u542c\u8fdb\u53bb\u3002\u8be7\u7136\u53eb \u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u8bf4\u96be\u53d7\u4e86\u4f24\uff1f\u5440\uff0c\u5e08\u7236\uff0c\u4f60\u6015\u738b\u5e9c\u88ab\u88ad\uff0c\u53f6\u5927\u54e5\u6628\u5929\u4e5f\u6709\u8bf4\u4e86\uff0c\u4f60\u4eec\u4e24\u4eba\u6b63 \u662f\u82f1\u96c4\u6240\u89c1\uff0c\u5f7c\u6b64\u76f8\u540c\uff01\u201d\u53f6\u6210\u6797\u8df3\u8d77\u6765\u9053\uff1a\u201c\u6211\u5728\u6c34\u4e61\u957f\u5927\uff0c\u7a0d\u61c2\u821f\u65c5\u4e4b\u4e8b\uff0c\u5f85\u6211\u53bb\uff01\u201d \u5f20\u4e39\u67ab\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u7684\u4f24\u4e0d\u788d\u4e8b\u4e48\uff1f\u201d\u53f6\u6210\u6797\u6454\u52a8\u4e00\u6761\u81c2\u818a\uff0c\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u6761\u53ef\u4ee5\u7528\u5462\uff0c \u6bd4\u6b66\u6216\u8005\u4e0d\u80fd\uff0c\u9a76\u821f\u8fd8\u6765\u5f97\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u597d\uff0c\u6fb9\u53f0\u706d\u660e\uff0c\u4f60\u62a4\u9001\u6210\u6797\u5230\u738b\u5e9c\u53bb\uff01\u201d\u4e8e\u627f \u73e0\u9001\u4ed6\u8d70\u4e86\u51e0\u6b65\uff0c\u9053\uff1a\u201c\u53f6\u5927\u54e5\uff0c\u4f60\u597d\u597d\u4fdd\u91cd\u4e86\u3002\u201d \u3000\u3000\u94c1\u955c\u5fc3\u4e00\u7247\u832b\u7136\uff0c\u60f3\u4e0d\u5230\u81ea\u5df1\u4e00\u7247\u597d\u5fc3\uff0c\u4e8e\u627f\u73e0\u7adf\u7136\u8fde\u4ed6\u7684\u8bdd\u4e5f\u6ca1\u6709\u542c\u6e05\u695a\u3002\u5bf9\u53f6\u6210\u6797 \u66f4\u662f\u4e0d\u5fff\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u61c2\u4ec0\u4e48\u5175\u6cd5\uff0c\u6562\u53bb\u6307\u6325\uff1f\u201d\u82e5\u975e\u788d\u7740\u5f20\u4e39\u67ab\u7684\u9762\u5b50\uff0c\u4ed6\u51e0\u4e4e \u5c31\u8981\u51b7\u7b11\u51fa\u6765\u3002 \u3000\u3000\u53ea\u89c1\u8d64\u971e\u9053\u4eba\u90a3\u8fb9\u4f3c\u662f\u5546\u8bae\u5df2\u5b9a\uff0c\u4e00\u5b57\u6392\u5f00\uff0c\u8d64\u971e\u9053\u4eba\u5f53\u4e2d\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u6bcf\u4eba\u5e72\u5e72\u8106\u8106 \u5404\u6bd4\u4e00\u573a\uff0c\u4e0d\u8fc7\u53ef\u5f97\u8bf4\u8bdd\u5728\u5148\uff0c\u8fd9\u51e0\u4f4d\u9053\u5144\u90fd\u7ec3\u6709\u72ec\u95e8\u7edd\u6280\uff0c\u82e5\u6709\u5931\u624b\uff0c\u6253\u6b7b\u6216\u6253\u4f24\uff0c\u5404\u5b89 \u5929\u547d\u3002\u6211\u65e2\u5c5e\u4f60\u5e08\u7956\u65e7\u4ea4\uff0c\u53ea\u597d\u7b49\u5f85\u4ed6\u4eec\u548c\u4f60\u7684\u5e08\u7956\u6216\u5e08\u4f2f\u6bd4\u8bd5\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5 \u5ba2\u6c14\uff0c\u8001\u524d\u8f88\u82e5\u80af\u6307\u6559\uff0c\u90a3\u6b63\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u4e0d\u8fc7\uff0c\u6211\u4eec\u6709\u4e24\u4f4d\u670b\u53cb\uff0c\u6709\u4e8b\u53ef\u8981\u5148\u4e0b\u5c71\u53bb\u3002\u201d \u6fb9\u53f0\u706d\u660e\u4f34\u7740\u53f6\u6210\u6797\u5927\u6b65\u8d70\uff0c\u4f17\u9b54\u5934\u4ff1\u662f\u4e00\u6014\uff0c\u6012\u76ee\u76f8\u5411\uff0c\u6b63\u662f\uff1a \u3000\u3000\u95ef\u7834\u5929\u7f57\u5730\u7f51\u9635\uff0c\u864e\u7a74\u9f99\u6f6d\u8d70\u4e00\u906d\u3002 \u3000\u3000\u6b32\u77e5\u6fb9\u53f0\u706d\u660e\u4e0e\u53f6\u6210\u6797\u80fd\u5426\u901a\u8fc7\uff0c\u82cd\u5c71\u6bd4\u6b66\u7ed3\u679c\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u8f83\u6280\u82cd\u5c71\u3000\u9ad8\u5cf0\u817e\u5251\u6c14\u3000\u6cdb\u821f\u6d31\u6d77\u3000\u6708\u591c\u52a8\u60c5\u6000 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u8f83\u6280\u82cd\u5c71\u3000\u9ad8\u5cf0\u817e\u5251\u6c14\u3000\u6cdb\u821f\u6d31\u6d77\u3000\u6708\u591c\u52a8\u60c5\u6000 \u3000\u3000\u6fb9\u53f0\u706d\u660e\u82e5\u65e0\u5176\u4e8b\uff0c\u643a\u7740\u53f6\u6210\u6797\u4ece\u4eba\u4e1b\u4e2d\u95ef\u8fc7\uff0c\u5ffd\u542c\u5f97\u6458\u661f\u4e0a\u4eba\u201c\u54fc\u201d\u7684\u4e00\u58f0\uff0c\u559d\u9053\uff1a \u201c\u670b\u53cb\u6162\u8d70\uff01\u201d\u5ffd\u53d1\u4e00\u638c\uff0c\u6025\u5982\u95ea\u7535\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u53ea\u89c1\u6fb9\u53f0\u706d\u660e\u62b1\u7740\u53f6\u6210\u6797 \u51cc\u7a7a\u98de\u8d77\u3002\u6458\u661f\u4e0a\u4eba\u5728\u6606\u4ed1\u5c71\u661f\u5bbf\u6d77\u6f5c\u4fee\u591a\u5e74\uff0c\u6240\u7ec3\u7684\u201c\u6458\u661f\u624b\u201d\u72e0\u6bd2\u975e\u5e38\uff0c\u8fd9\u4e00\u638c\u6765\u5f97\u65e0 \u58f0\u65e0\u606f\uff0c\u7adf\u7136\u88ab\u6fb9\u53f0\u706d\u660e\u63a5\u8fc7\uff0c\u4f46\u89c9\u706b\u8fa3\u8fa3\u822c\u4e00\u9635\u75bc\u75db\uff0c\u624b\u8155\u88ab\u6fb9\u53f0\u706d\u660e\u94c1\u6307\u6240\u62c2\u4e4b\u5904\uff0c\u8d77 \u4e86\u4e00\u6761\u7ea2\u5370\uff0c\u6709\u5982\u706b\u70d9\u4e00\u822c\uff0c\u5fc3\u5934\u4e00\u9707\uff0c\u7b2c\u4e8c\u638c\u53c8\u53d1\u51fa\u53bb\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u9ed1\u767d\u6469\u8bc3\u5df2\u662f \u53cc\u53cc\u62a2\u51fa\uff0c\u53cc\u6756\u4e00\u6a2a\uff0c\u62e6\u7740\u4e86\u6458\u661f\u4e0a\u4eba\u7684\u53bb\u8def\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u60f3\u6253\u67b6\u4e48\uff1f\u6709\u4eba\u5949\u966a\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u53eb\u9053\uff1a\u201c\u8bf7\u95ee\u8d64\u971e\u9053\u957f\uff0c\u8fd9\u662f\u4ec0\u4e48\u89c4\u77e9\uff1f\u201d\u8d64\u971e\u9053\u4eba\u7fbd\u6247\u4e00\u6325\uff0c\u9053\uff1a\u201c\u7531\u4ed6\u53bb \u5427\uff01\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5c60\u9f99\u5c0a\u8005\u65e9\u5df2\u4e00\u628a\u98de\u5200\u63b7\u53bb\uff0c\u5c04\u5230\u6fb9\u53f0\u706d\u660e\u7684\u80cc\u5fc3\uff0c\u8d64\u971e\u9053\u4eba\u7709\u5934\u4e00\u76b1\uff0c \u4f46\u542c\u5f97\u201c\u55d6\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u628a\u98de\u5200\uff0c\u5ffd\u5730\u5c04\u56de\uff0c\u539f\u6765\u662f\u5f20\u4e39\u67ab\u4f7f\u51fa\u201c\u6458\u53f6\u98de\u82b1\u201d\u7684\u5185\u529f\u7edd\u6280\uff0c \u5f39\u51fa\u4e00\u9897\u5c0f\u5c0f\u7684\u77f3\u5b50\uff0c\u786c\u751f\u751f\u5730\u5c06\u5c60\u9f99\u5c0a\u8005\u7684\u98de\u5200\u78b0\u4e86\u56de\u6765\u3002 \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u5927\u6012\uff0c\u53cc\u6756\u75be\u8d77\uff0c\u5de6\u6253\u6458\u661f\u4e0a\u4eba\uff0c\u53f3\u6253\u5c60\u9f99\u5c0a\u8005\uff0c\u5f20\u4e39\u67ab\u559d\u9053\uff1a\u201c\u4f60\u4eec\u662f\u60f3\u7422 \u78e8\u6b66\u529f\uff0c\u8fd8\u662f\u60f3\u7fa4\u6bb4\u4e71\u6253\uff1f\u201d\u8d64\u971e\u9053\u4eba\u4ea6\u5df2\u6599\u5230\u6fb9\u53f0\u706d\u660e\u662f\u53bb\u63f4\u52a9\u738b\u5e9c\uff0c\u4f46\u4ed6\u662f\u4e00\u6d3e\u5b97\u5e08\uff0c \u88ab\u5f20\u4e39\u67ab\u7528\u8bf4\u8bdd\u95ee\u4f4f\uff0c\u53c8\u89c1\u6fb9\u53f0\u706d\u660e\u5df2\u5954\u4e0b\u5c71\u5761\uff0c\u53ea\u5f97\u505a\u597d\u505a\u574f\uff0c\u5c06\u4f17\u4eba\u529d\u5f00\uff0c\u7fbd\u6247\u4e00\u6447\uff0c \u628a\u9ed1\u767d\u6469\u8bc3\u3001\u6458\u661f\u4e0a\u4eba\u3001\u5c60\u9f99\u5c0a\u8005\u9694\u5f00\u4e24\u8fb9\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u5bb6\u522b\u95f9\uff0c\u6309\u6b66\u6797\u89c4\u77e9\u5404\u6bd4\u8bd5\u4e00 \u573a\u3002\u201d\u8fd9\u8bf4\u8bdd\u628a\u9ed1\u767d\u6469\u8bc3\u9a82\u5728\u91cc\u5934\uff0c\u9ed1\u767d\u6469\u8bc3\u6012\u9053\uff1a\u201c\u597d\u4e2a\u4e0d\u5206\u9752\u7ea2\u7682\u767d\uff0c\u662f\u8c01\u80e1\u95f9\u6765\u4e86\uff1f \u597d\uff0c\u54b1\u5144\u5f1f\u4fe9\u5c31\u5148\u8bf7\u6559\u4f60\u8d64\u57ce\u6d3e\u5927\u5b97\u5e08\u7684\u7edd\u6280\uff01\u201d \u3000\u3000\u516d\u9633\u771f\u541b\u53cc\u638c\u4e00\u9519\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5272\u9e21\u7109\u7528\u725b\u5200\uff1f\u8fd8\u662f\u54b1\u4eec\u628a\u521a\u624d\u90a3\u4e00\u573a\u672a\u6253\u5b8c\u7684\u67b6\u5206 \u4e2a\u80dc\u8d1f\u5427\uff01\u201d\u9ed1\u767d\u6469\u8bc3\u53cc\u6756\u4e00\u987f\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u90a3\u6b63\u662f\u6c42\u4e4b\u4e0d\u5f97\uff01\u201d\u9e20\u76d8\u5a46\u516c\u5b59\u65e0\u57a2 \u5728\u65c1\u8fb9\u9634\u607b\u607b\u5730\u8bf4\u9053\uff1a\u201c\u516d\u9633\u771f\u541b\u4ee5\u4e00\u654c\u4e8c\uff0c\u4e0d\u607c\u81ea\u635f\u540d\u5934\u5417\uff1f\u201d\u9ed1\u767d\u6469\u8bc3\u6012\u9053\uff1a\u201c\u4f60\u4e00\u4eba \u6765\u662f\u54b1\u5144\u5f1f\u63a5\uff0c\u5341\u4eba\u6765\u4e5f\u662f\u54b1\u5144\u5f1f\u63a5\uff01\u201d\u9e20\u76d8\u5a46\u8fd9\u756a\u8bdd\u5176\u5b9e\u662f\u6697\u5e2e\u516d\u9633\u771f\u541b\uff0c\u516d\u9633\u771f\u541b\u706b\u7206 \u7684\u6027\u5b50\u4e00\u65f6\u95f4\u5374\u542c\u4e0d\u51fa\u6765\uff0c\u76db\u6c14\u51cc\u4eba\u5730\u559d\u9053\uff1a\u201c\u6211\u5c31\u51ed\u8fd9\u53cc\u8089\u638c\u8981\u4f1a\u4f1a\u9ed1\u767d\u6469\u8bc3\u53cc\u6756\u5408\u58c1\u7684 \u897f\u57df\u5947\u529f\uff01\u201d\u9e20\u76d8\u5a46\u7b11\u9053\uff1a\u201c\u516d\u9633\u771f\u541b\u4f60\u662f\u4e00\u6d3e\u5b97\u5e08\uff0c\u867d\u7136\u4ee5\u4e00\u654c\u4e8c\uff0c\u4ea6\u662f\u80dc\u4e4b\u4e0d\u6b66\u3002\u8fd8\u662f \u5f85\u6211\u8001\u5a46\u5b50\u66ff\u4f60\u5148\u6253\u8fd9\u4e00\u67b6\u5427\uff01\u201d\u5176\u5b9e\u6709\u8bb8\u591a\u79cd\u6b66\u529f\u662f\u5fc5\u987b\u4e24\u4eba\u5408\u4f7f\u7684\uff0c\u7b97\u4e0d\u5f97\u4ee5\u4e8c\u654c\u4e00\uff0c \u9e20\u76d8\u5a46\u548c\u516d\u9633\u771f\u541b\u4ea4\u597d\uff0c\u660e\u77e5\u4ed6\u4e0d\u662f\u9ed1\u767d\u9b54\u8bc3\u7684\u5bf9\u624b\uff0c\u6545\u6b64\u62a2\u7740\u51fa\u5934\uff0c\u8981\u66ff\u4ed6\u6321\u8fd9\u4e00\u573a\u3002\u5728 \u8fd9\u4e9b\u9b54\u5934\u4e4b\u4e2d\uff0c\u9e20\u76d8\u5a46\u7684\u6b66\u529f\u4ec5\u6b21\u4e8e\u8d64\u971e\u9053\u4eba\uff0c\u81ea\u4fe1\u5bf9\u9ed1\u767d\u6469\u8bc3\u53ef\u64cd\u80dc\u5238\u3002 \u3000\u3000\u4f46\u516d\u9633\u771f\u541b\u4e5f\u662f\u72c2\u5984\u81ea\u8d1f\u4e4b\u6781\u7684\u4eba\uff0c\u7adf\u7136\u4e0d\u80af\u9000\u8ba9\uff0c\u6b63\u81ea\u50f5\u6301\u4e0d\u4e0b\uff0c\u5ffd\u542c\u5f97\u4e00\u4eba\u6717\u58f0\u8bf4 \u9053\uff1a\u201c\u9ed1\u767d\u4e8c\u5144\u548c\u516c\u5b59\u5148\u8f88\u8bf7\u62bc\u540e\u4e00\u573a\uff0c\u5f85\u6211\u5148\u89c1\u8bc6\u516d\u9633\u771f\u541b\u7684\u6df7\u5143\u4e00\u6c14\u529f\uff01\u201d \u3000\u3000\u8fd9\u4eba\u662f\u4e4c\u8499\u592b\uff0c\u5728\u56db\u5927\u5251\u5ba2\u4e4b\u4e2d\u7684\u540d\u6b21\u4ec5\u6b21\u4e8e\u5f20\u4e39\u67ab\uff0c\u8bba\u8f88\u4efd\u5374\u6bd4\u5f20\u4e39\u67ab\u8fd8\u9ad8\u51fa\u4e00\u8f88\uff0c \u9ed1\u767d\u6469\u8bc3\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u4e00\u573a\u8ba9\u4f60\uff0c\u4f46\u6211\u4eec\u5df2\u6709\u8bdd\u5728\u5148\uff0c\u7edd\u4e0d\u8ba9\u8fd9\u4eba\u751f\u51fa\u6b64\u5c71\uff0c\u4f60\u4e0b\u624b\u53ef\u4e0d\u8bb8 \u7559\u60c5\u3002\u201d\u4e4c\u8499\u592b\u7b11\u9053\uff1a\u201c\u77e5\u9053\u5566\uff0c\u4e0d\u52b3\u4e8c\u5144\u5429\u5490\uff0c\u6211\u81ea\u5f53\u5c3d\u529b\u800c\u4e3a\u3002\u201d \u3000\u3000\u516d\u9633\u771f\u541b\u52c3\u7136\u5927\u6012\uff0c\u4f46\u52b2\u654c\u5f53\u524d\uff0c\u5374\u4e5f\u4e0d\u6562\u66b4\u8e81\u51fa\u624b\uff0c\u53ea\u89c1\u4ed6\u5934\u53d1\u6839\u6839\u5012\u7ad6\uff0c\u7ed5\u7740\u4e4c\u8499 \u592b\u659c\u8d70\u4e09\u6b65\uff0c\u76f4\u8d70\u4e09\u6b65\uff0c\u6c89\u8170\u84c4\u52bf\uff0c\u5c31\u50cf\u4e00\u53ea\u62e9\u4eba\u800c\u55bb\u7684\u731b\u72ee\u3002\u4e4c\u8499\u592b\u8138\u4e0a\u4e5f\u73b0\u51fa\u7d27\u5f20\u7684\u795e \u8272\uff0c\u811a\u8e0f\u4e5d\u5bab\u516b\u5366\u65b9\u4f4d\uff0c\u516d\u9633\u771f\u541b\u8ff8\u4e09\u6b65\uff0c\u4ed6\u9000\u4e09\u6b65\uff0c\u516d\u9633\u771f\u541b\u9000\u4e09\u6b65\uff0c\u4ed6\u53c8\u8e0f\u8fdb\u4e09\u6b65\u3002\u4e24 \u4eba\u76d8\u65cb\u8fdb\u9000\uff0c\u534a\u4e2a\u65f6\u8fb0\u8fd8\u672a\u4ea4\u624b\u3002\u5728\u573a\u7684\u90fd\u662f\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u77e5\u9053\u4ed6\u4eec\u4e8c\u4eba\u6b63\u5728\u8fd0\u6c14\u84c4\u52b2\uff0c \u5bfb\u6687\u62b5\u9699\uff0c\u4e00\u51fa\u624b\u5c31\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u5f3a\u5b58\u5f31\u4ea1\uff01 \u3000\u3000\u4e8e\u627f\u73e0\u770b\u5f97\u6709\u70b9\u53d1\u95f7\uff0c\u9065\u671b\u5c71\u4e0b\uff0c\u6fb9\u53f0\u706d\u660e\u548c\u53f6\u6210\u6797\u7684\u80cc\u5f71\u5c1a\u4f9d\u7a00\u89c1\u5230\u4e24\u4e2a\u767d\u70b9\uff0c\u4e8e\u627f \u73e0\u5fc3\u9053\uff1a\u201c\u54a6\uff0c\u4ed6\u4eec\u600e\u4e48\u8d70\u5f97\u5982\u6b64\u6162\u6cd5\uff1f\u201d\u5fc3\u4e2d\u6302\u5ff5\u738b\u5e9c\u5b89\u5371\uff0c\u6068\u4e0d\u5f97\u50ac\u4ed6\u4eec\u5feb\u8d70\uff0c\u4f46\u53c8\u60f3 \u5411\u53f6\u6210\u6797\u7684\u80cc\u5f71\u591a\u770b\u4e24\u773c\uff0c\u5979\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\u5fc3\u60c5\uff0c\u4f46\u89c9\u53f6\u6210\u6797\u8fd9\u6b21\u820d\u4e86\u6027\u547d\uff0c\u76f8\u52a9 \u81ea\u5df1\uff0c\u800c\u4eca\u53c8\u5e26\u7740\u91cd\u4f24\uff0c\u6551\u63f4\u738b\u5e9c\uff0c\u6b64\u4e00\u53bb\u5409\u51f6\u96be\u535c\uff0c\u201c\u5440\uff0c\u4f46\u613f\u4ed6\u80fd\u5e73\u5b89\u56de\u6765\uff01\u201d\u4e8e\u627f\u73e0 \u5fc3\u9053\u3002\u5979\u53ef\u4e0d\u77e5\u9053\u6fb9\u53f0\u706d\u660e\u9002\u624d\u4e0e\u6458\u661f\u4e0a\u4eba\u5bf9\u4e86\u4e00\u638c\uff0c\u5f7c\u6b64\u90fd\u53d7\u4e86\u4e00\u70b9\u4f24\uff0c\u800c\u53f6\u6210\u6797\u4ea6\u88ab\u6ce2 \u53ca\uff0c\u6545\u6b64\u4e0d\u80fd\u65bd\u5c55\u8f7b\u529f\u3002\u4ed6\u4eec\u8981\u8d76\u5230\u738b\u5e9c\u7684\u5fc3\u60c5\uff0c\u5176\u5b9e\u6bd4\u4e8e\u627f\u73e0\u66f4\u6025\u3002 \u3000\u3000\u518d\u770b\u4e86\u4e00\u4f1a\uff0c\u6fb9\u53f0\u706d\u660e\u548c\u53f6\u6210\u6797\u7684\u5f71\u5b50\u6162\u6162\u6d88\u5931\u3002\u4e8e\u627f\u73e0\u5446\u5446\u5730\u51fa\u4e86\u4e00\u4f1a\u795e\uff0c\u5076\u7136\u4e00 \u77a5\uff0c\u4f46\u89c1\u94c1\u955c\u5fc3\u7684\u773c\u5149\u4e5f\u6b63\u5bf9\u7740\u81ea\u5df1\uff0c\u5145\u6ee1\u67d4\u60c5\u800c\u53c8\u5145\u6ee1\u5e7d\u6028\u7684\u773c\u5149\uff01\u4e8e\u627f\u73e0\u5fc3\u5934\u731b\u5730\u4e00 \u98a4\uff0c\u970e\u7136\u4e4b\u95f4\uff0c\u53f6\u6210\u6797\u7684\u5f71\u5b50\u548c\u94c1\u955c\u5fc3\u7684\u5f71\u5b50\u4ea4\u4e92\u5728\u5fc3\u5934\u7ffb\u817e\uff0c\u7ec8\u4e8e\u94c1\u955c\u5fc3\u7684\u5f71\u5b50\u5c06\u53f6\u6210\u6797 \u7684\u5f71\u5b50\u538b\u4e0b\u53bb\u3002\u5979\u5fcd\u4e0d\u4f4f\u62ac\u8d77\u5934\u6765\u770b\u94c1\u955c\u5fc3\u4e00\u773c\uff0c\u5ffd\u89c1\u94c1\u955c\u5fc3\u7684\u9762\u4e0a\u4e5f\u73b0\u51fa\u4e86\u7d27\u5f20\u7684\u795e\u8272\uff0c \u773c\u5149\u5df2\u79fb\u5411\u573a\u5fc3\uff0c\u4e8e\u627f\u73e0\u6025\u5fd9\u770b\u65f6\uff0c\u539f\u6765\u573a\u4e2d\u7684\u4e4c\u8499\u592b\u4e0e\u516d\u9633\u771f\u541b\u4e8c\u4eba\u5df2\u5230\u4e86\u62fc\u6b7b\u4e00\u6597\u7684\u65f6 \u5019\u3002 \u3000\u3000\u4f46\u89c1\u516d\u9633\u771f\u541b\u7ed5\u7740\u4e4c\u8499\u592b\u76f4\u6253\u5708\u5708\uff0c\u8d8a\u8d70\u8d8a\u6025\uff0c\u731b\u5730\u559d\u9053\uff1a\u201c\u4e0d\u662f\u4f60\uff0c\u4fbf\u662f\u6211\uff01\u201d\u8fd9\u65f6 \u4ed6\u5df2\u8fd0\u8db3\u771f\u529b\uff0c\u6df7\u5143\u4e00\u6c14\u529f\u731b\u5730\u4f7f\u51fa\uff0c\u4f46\u542c\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u6c99\u98de\u77f3\u8d70\uff0c\u4e4c\u8499\u592b\u8eab\u8eaf\u4e00\u6643\uff0c\u500f\u5730 \u4f38\u51fa\u4e00\u6307\uff0c\u53ea\u542c\u5f97\u201c\u5618\u201d\u7684\u4e00\u58f0\uff0c\u6781\u5176\u5c16\u9510\u523a\u8033\u7684\u58f0\u80b2\uff0c\u597d\u50cf\u4e00\u4e2a\u5927\u76ae\u7403\u7a81\u7136\u88ab\u5229\u9488\u6233\u7834\u4e00 \u6837\uff0c\u516d\u9633\u771f\u541b\u8e09\u8e09\u8dc4\u8dc4\u5730\u5012\u9000\u51e0\u6b65\uff0c\u9762\u8272\u60e8\u767d\uff0c\u604d\u5982\u6597\u8d25\u4e86\u7684\u516c\u9e21\uff0c\u539f\u6765\u4e4c\u8499\u592b\u4f7f\u7684\u662f\u6700\u4e0a \u4e58\u7684\u5185\u529f\u201c\u4e00\u6307\u63b8\u201d\u7684\u529f\u592b\uff0c\u521a\u597d\u662f\u6df7\u5143\u4e00\u6c14\u529f\u7684\u514b\u661f\u3002\u8981\u4e0d\u662f\u516d\u9633\u771f\u541b\u7684\u62a4\u8eab\u6c14\u529f\u5df2\u6709\u4e86 \u4e5d\u6210\u706b\u5019\uff0c\u8fd9\u4e00\u6307\u5c31\u80fd\u53eb\u4ed6\u5fc3\u810f\u9707\u88c2\uff0c\u6c14\u7edd\u800c\u4ea1\uff01 \u3000\u3000\u9ad8\u624b\u6bd4\u6597\uff0c\u80dc\u8d1f\u5224\u4e8e\u4e00\u62db\u3002\u6309\u8bf4\u516d\u9633\u771f\u541b\u7684\u201c\u6df7\u5143\u4e00\u6c14\u529f\u201d\u5df2\u88ab\u4e4c\u8499\u592b\u7684\u201c\u4e00\u6307\u7985\u201d\u6240 \u7834\uff0c\u5c31\u8be5\u8ba4\u8f93\u624d\u662f\u3002\u4f46\u516d\u9633\u771f\u541b\u81ea\u8d1f\u4e4b\u6781\uff0c\u5c82\u80af\u5728\u4f19\u4f34\u9762\u524d\u5931\u8fd9\u4e2a\u9762\u5b50\u3002\u53ea\u89c1\u4ed6\u6362\u5730\u4e00\u4e2a \u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u6837\u5947\u5f62\u602a\u72b6\u7684\u5175\u5668\uff0c\u90a3\u662f\u4e00\u6761\u901a\u7ea2\u5982\u8840\u7684\u957f\u97ad\uff0c\u4e5f\u4e0d\u77e5\u662f\u4ec0\u4e48 \u4e1c\u897f\u505a\u7684\uff0c\u97ad\u4e0a\u6302\u7740\u4e24\u4e2a\u767d\u91d1\u6240\u94f8\u7684\u9ab7\u9ac5\u5934\uff0c\u9aa4\u773c\u671b\u53bb\uff0c\u5c31\u50cf\u771f\u7684\u767d\u9aa8\u9ab7\u9ac5\u4e00\u6837\uff0c\u886c\u7740\u90a3\u6761 \u8272\u6cfd\u6bb7\u7ea2\u7684\u957f\u97ad\uff0c\u66f4\u663e\u5f97\u72f0\u72de\u8be1\u5f02\uff01 \u3000\u3000\u53ea\u542c\u5f97\u516d\u9633\u771f\u541b\u559d\u9053\uff1a\u201c\u4e4c\u8499\u592b\uff0c\u4f60\u53f7\u79f0\u5317\u65b9\u5251\u5ba2\uff0c\u6211\u5012\u8981\u770b\u4f60\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\u7684\u80fd \u4e3a\uff01\u201d\u4e0d\u5f85\u7b54\u8bdd\uff0c\u201c\u5530\u201d\u5730\u5c31\u662f\u4e00\u97ad\uff0c\u90a3\u4e24\u53ea\u9ab7\u9ac5\u968f\u7740\u97ad\u98ce\u7ffb\u817e\u98de\u821e\uff0c\u5634\u5df4\u5ffd\u5730\u88c2\u5f00\uff0c\u9732\u51fa \u4e00\u6392\u767d\u767d\u7684\u7259\u9f7f\uff0c\u4e5f\u5411\u7740\u4e4c\u8499\u592b\u54ac\u6765\uff01 \u3000\u3000\u4e4c\u8499\u592b\u4e00\u58f0\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4f7f\u7528\u8fd9\u7b49\u90aa\u95e8\u5175\u5668\uff0c\u5c31\u5413\u5f97\u4e86\u4eba\u4e48\uff1f\u201d\u516d\u9633\u771f\u541b\u6765\u5f97\u5feb\uff0c\u4ed6 \u6bd4\u516d\u9633\u771f\u541b\u8fd8\u5feb\uff0c\u9752\u94a2\u5251\u500f\u5730\u51fa\u9798\uff0c\u4f46\u542c\u5f97\u94ee\u7684\u4e00\u58f0\uff0c\u4e24\u53ea\u9ab7\u9ac5\u5934\u53cd\u6251\u56de\u53bb\uff0c\u5251\u5149\u97ad\u5f71\uff0c\u767b \u65f6\u5377\u4f5c\u4e00\u56e2\u3002 \u3000\u3000\u516d\u9633\u771f\u541b\u624b\u8155\u4e00\u7ffb\uff0c\u90a3\u6761\u9ab7\u9ac5\u97ad\u500f\u5730\u53c8\u98de\u4e86\u8d77\u6765\uff0c\u4f7f\u51fa\u4e86\u201c\u8fde\u73af\u4e09\u97ad\u201d\uff0c\u201c\u56de\u98ce\u72c2\u67f3\u201d \u7684\u7edd\u6280\uff0c\u98ce\u58f0\u547c\u54cd\uff0c\u5377\u8d77\u4e86\u4e00\u56e2\u97ad\u5f71\uff0c\u4e4c\u8499\u592b\u53cc\u6307\u4e00\u5f39\uff0c\u628a\u6251\u8fd1\u8eab\u7684\u4e24\u53ea\u9ab7\u9ac5\u5934\u5f39\u5f00\uff0c\u5251\u5203 \u4e00\u538b\u97ad\u68a2\uff0c\u5251\u950b\u6cbf\u7740\u957f\u97ad\u4fbf\u524a\u516d\u9633\u771f\u541b\u7684\u624b\u6307\uff0c\u516d\u9633\u771f\u541b\u201c\u547c\u54e7\u201d\u4e00\u58f0\uff0c\u5de6\u638c\u4e00\u5288\uff0c\u594b\u529b\u6321 \u4e86\u4e00\u4e0b\u4ed6\u7684\u4e00\u6307\u7985\u529f\uff0c\u957f\u97ad\u4e00\u64a4\uff0c\u5530\u3001\u5530\u3001\u5530\uff0c\u53c8\u662f\u4e00\u8fde\u4e09\u97ad\uff0c\u4e24\u4e2a\u4eba\u4f7f\u7684\u7adf\u7136\u5168\u90fd\u662f\u8fdb\u653b \u7684\u62db\u6570\u3002 \u3000\u3000\u516d\u9633\u771f\u541b\u8fd9\u6761\u9ab7\u9ac5\u97ad\uff0c\u4e13\u7834\u654c\u4eba\u6c14\u529f\uff0c\u64c5\u957f\u6253\u7a74\uff0c\u90a3\u4e24\u53ea\u9ab7\u9ac5\u66f4\u662f\u4e00\u79cd\u9634\u6bd2\u7684\u6b66\u5668\uff0c\u5999 \u7528\u751a\u591a\uff0c\u62db\u6570\u602a\u5f02\u3002\u4f46\u4e4c\u8499\u592b\u53f7\u79f0\u5317\u65b9\u5251\u5ba2\uff0c\u5c82\u662f\u6d6a\u5f97\u865a\u540d\uff0c\u53ea\u89c1\u4ed6\u5251\u5f0f\u5c55\u5f00\uff0c\u6709\u5982\u957f\u6c5f\u5927 \u6d6a\uff0c\u6eda\u6eda\u800c\u4e0a\uff0c\u5947\u62db\u5999\u7740\uff0c\u4ea6\u662f\u5c42\u51fa\u4e0d\u7a77\uff0c\u5f20\u4e39\u67ab\u770b\u4e86\uff0c\u4e5f\u9891\u9891\u70b9\u5934\uff0c\u5fc3\u9053\uff1a\u201c\u4e4c\u8499\u5939\u4e0d\u5080 \u662f\u4e0a\u5b98\u524d\u8f88\u7684\u8863\u94b5\u4f20\u4eba\uff0c\u6b66\u529f\u6bd4\u4ed6\u7684\u5e08\u5144\u6fb9\u53f0\u706d\u660e\u679c\u7136\u8fd8\u9ad8\u51fa\u8bb8\u591a\u3002\u201d\u4e24\u4eba\u8d8a\u6597\u8d8a\u6025\uff0c\u5ffd\u89c1 \u516d\u9633\u771f\u541b\u957f\u97ad\u4e00\u5377\uff0c\u4f3c\u5de6\u53cd\u53f3\uff0c\u97ad\u68a2\u5377\u5230\u4e86\u4e4c\u8499\u592b\u7684\u8db3\u8ddf\uff0c\u97ad\u4e0a\u7684\u4e24\u53ea\u9ab7\u9ac5\u5374\u98de\u4e86\u8d77\u6765\uff0c\u4e00 \u4e2a\u556e\u4e4c\u8499\u592b\u7684\u5de6\u80a9\uff0c\u4e00\u4e2a\u556e\u4ed6\u7684\u77f3\u80a9\uff0c\u8fd9\u4e00\u4e0b\u4e00\u62db\u4e09\u7528\uff0c\u7aef\u7684\u9634\u72e0\u4e4b\u6781\uff0c\u4e8e\u627f\u73e0\u770b\u5f97\u51e0\u4e4e\u8981 \u53eb\u51fa\u58f0\u6765\u3002\u8bf4\u65f6\u8fdf\u90a3\u65f6\u5feb\uff0c\u4f17\u4eba\u4f46\u89c9\u773c\u775b\u4e00\u82b1\uff0c\u4e4c\u8499\u592b\u5df2\u662f\u8eab\u79fb\u6b65\u6362\uff0c\u4e00\u4e2a\u201c\u71d5\u5b50\u94bb\u4e91\u201d\uff0c \u5530\u5730\u8df3\u8d77\u4e00\u4e08\u6765\u9ad8\uff0c\u5de6\u5251\u53f3\u6307\uff0c\u51cc\u7a7a\u4e0b\u51fb\uff0c\u201c\u7830\u201d\u201c\u7830\u201d\u4e24\u58f0\u54cd\u8fc7\uff0c\u90a3\u4e24\u53ea\u9ab7\u9ac5\u5934\u9aa4\u7136\u88c2 \u5f00\uff0c\u5ffd\u7136\u55b7\u51fa\u4e00\u6e9c\u6697\u8d64\u8272\u7684\u706b\u5149\uff01 \u3000\u3000\u539f\u6765\u516d\u9633\u771f\u541b\u8fd9\u6761\u97ad\u540d\u4e3a\u201c\u9ab7\u9ac5\u70c8\u706b\u97ad\u201d\uff0c\u90a3\u4e24\u53ea\u9ab7\u9ac5\u9664\u4e86\u5584\u4e8e\u556e\u4eba\u54ac\u65ad\u654c\u4eba\u7b4b\u8109\u4e4b \u5916\uff0c\u5185\u4e2d\u8fd8\u85cf\u6709\u706b\u5668\uff0c\u80fd\u55b7\u78f7\u706b\u3002\u516d\u9633\u771f\u541b\u9002\u624d\u4e4b\u6562\u5411\u9ed1\u767d\u6469\u8bc3\u518d\u5ea6\u6311\u6218\uff0c\u5c31\u662f\u6043\u6709\u6b64\u97ad\uff01 \u3000\u3000\u8fd9\u4e00\u4e0b\u5f53\u771f\u662f\u53d8\u51fa\u610f\u5916\uff0c\u4f46\u542c\u5f97\u54cd\u58f0\u4e00\u8fc7\uff0c\u4e4c\u8499\u592b\u5168\u8eab\u5df2\u5728\u706b\u5149\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u5934\u53d1\u8863\u88f3\u90e8 \u5df2\u70e7\u7740\uff01 \u3000\u3000\u8fd9\u4e00\u4e0b\u53d8\u51fa\u610f\u5916\uff0c\u60ca\u9669\u7edd\u4f26\uff0c\u4e24\u8fb9\u90fd\u6709\u51e0\u6761\u4eba\u5f71\u7eb5\u8d77\uff0c\u60f3\u628a\u81ea\u5df1\u8fd9\u8fb9\u7684\u4eba\u6551\u56de\uff0c\u6551\u5175\u6765 \u5f97\u5feb\uff0c\u573a\u4e2d\u52a8\u624b\u66f4\u5feb\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u4e4b\u95f4\uff0c\u53ea\u542c\u5f97\u53c8\u662f\u201c\u7830\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u4e4c\u8499\u592b\u4e00\u638c\u5c06\u90a3\u4e24 \u53ea\u9ab7\u9ac5\u5934\u6253\u6210\u7c89\u788e\uff0c\u638c\u4e2d\u5939\u7740\u4e00\u6307\u7985\u7684\u529f\u592b\uff0c\u90a3\u8fb9\u53a2\u5c60\u9f99\u5c0a\u8005\u521a\u521a\u8d76\u5230\uff0c\u4fbf\u542c\u5f97\u516d\u9633\u771f\u541b\u4e00 \u58f0\u5389\u53eb\uff0c\u539f\u6765\u4ed6\u5df2\u88ab\u4e4c\u8499\u592b\u7684\u4e00\u6307\u7985\u529f\u7834\u53bb\u4e86\u6df7\u5143\u4e00\u6c14\u529f\uff0c\u767b\u65f6\u4e03\u7a8d\u6d41\u8840\uff0c\u75db\u5f97\u4ed6\u5728\u5730\u4e0a\u6253 \u6eda\uff0c\u8f97\u8f6c\u547b\u541f\u3002 \u3000\u3000\u9e20\u76d8\u5a46\u5927\u6012\uff0c\u547c\u7684\u4e00\u62d0\uff0c\u5377\u5730\u626b\u6765\uff0c\u9ed1\u767d\u6469\u8bc3\u53cc\u6756\u4e00\u67b6\uff0c\u559d\u9053\uff1a\u201c\u60f3\u7fa4\u6bb4\u5417\uff1f\u201d\u659c\u523a\u91cc \u5c60\u9f99\u5c0a\u8005\u4e00\u5200\u5288\u51fa\uff0c\u5374\u88ab\u4e91\u91cd\u6321\u4f4f\u3002\u5f20\u4e39\u67ab\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8d64\u971e\u9053\u957f\uff0c\u4f60\u6709\u8a00\u5728\u5148\uff0c\u8bf4\u662f\u82e5\u6709 \u6b7b\u4f24\uff0c\u5404\u5b89\u5929\u547d\uff0c\u8fd9\u8bf4\u8bdd\u4e0d\u7b97\u6570\u4e48\uff1f\u201d\u8d64\u971e\u9053\u4eba\u9053\uff1a\u201c\u516c\u5b59\u9053\u53cb\u4e14\u9000\u3002\u201d\u9e20\u76d8\u5a46\u53ea\u6302\u5ff5\u516d\u9633 \u771f\u541b\uff0c\u62d0\u6756\u91cd\u91cd\u4e00\u987f\uff0c\u9053\uff1a\u201c\u4e0b\u4e00\u573a\u6211\u6302\u4e86\u53f7\u4e86\u3002\u201d\u9ed1\u767d\u6469\u8bc3\u7b11\u9053\uff1a\u201c\u54b1\u5144\u5f1f\u4fe9\u4e00\u51c6\u5949 \u966a\u3002\u201d\u9e20\u76d8\u5a46\u9000\u4e0b\u53bb\u770b\u90a3\u516d\u9633\u771f\u541b\u65f6\uff0c\u4f46\u89c1\u4ed6\u53e3\u9f3b\u6d41\u8840\uff0c\u8109\u606f\u5982\u4e1d\uff0c\u4e94\u810f\u516d\u8151\u90fd\u53d7\u4e86\u9707\u4f24\uff0c \u663e\u89c1\u6d3b\u4e0d\u6210\u4e86\u3002 \u3000\u3000\u573a\u4e2d\u5269\u4e0b\u4e86\u4e91\u91cd\u3001\u5c60\u9f99\u5c0a\u8005\uff0c\u4e24\u4eba\u66f4\u4e0d\u6253\u8bdd\uff0c\u7acb\u5373\u52a8\u624b\uff0c\u5c60\u9f99\u5c0a\u8005\u90a3\u53e3\u5200\u5f0f\u6837\u53e4\u602a\uff0c\u5200 \u5934\u4e0a\u5f00\u53c9\uff0c\u8fd0\u52a8\u4e4b\u9645\uff0c\u95ea\u51fa\u6697\u8d64\u8272\u7684\u5149\u534e\uff0c\u4e91\u91cd\u89c1\u591a\u8bc6\u5e7f\uff0c\u6599\u5230\u8fd9\u53e3\u5200\u591a\u534a\u662f\u7528\u6bd2\u836f\u6dec\u8fc7 \u7684\uff0c\u52a0\u500d\u5c0f\u5fc3\uff0c\u4f7f\u51fa\u4e00\u8def\u7f57\u6c49\u795e\u5200\uff0c\u5c06\u5468\u8eab\u9632\u62a4\u5f97\u98ce\u96e8\u4e0d\u900f\u3002 \u3000\u3000\u8fd9\u8def\u201c\u7f57\u6c49\u795e\u5200\u201d\u4e43\u662f\u7384\u673a\u9038\u58eb\u72ec\u521b\u7684\u4e00\u8def\u5200\u6cd5\uff0c\u6a21\u62df\u4e94\u767e\u7f57\u6c49\u7684\u59ff\u52bf\uff0c\u5316\u5230\u5200\u6cd5\u4e0a \u6765\uff0c\u62db\u6570\u7684\u53d8\u5316\u7e41\u590d\uff0c\u53ef\u79f0\u6b66\u5b66\u4e00\u7edd\uff0c\u7384\u673a\u9038\u58eb\u65e9\u5e74\uff0c\u5c31\u66fe\u4ed7\u8fd9\u8def\u5200\u6cd5\u51fa\u9707\u4e2d\u539f\uff0c\u8463\u5cb3\u662f\u4ed6 \u7684\u5927\u5f1f\u5b50\uff0c\u6240\u4ee5\u5f97\u4e86\u8fd9\u8def\u5200\u6cd5\u7684\u771f\u4f20\u3002\u800c\u4eca\u4e91\u91cd\u7ecf\u8fc7\u5341\u591a\u5e74\u7684\u82e6\u7ec3\uff0c\u4e0d\u900a\u5e08\u7956\u5f53\u5e74\uff0c\u5c60\u9f99\u5c0a \u8005\u5360\u4e86\u5175\u5668\u7684\u4fbf\u5b9c\uff0c\u4e5f\u4e0d\u8fc7\u582a\u582a\u5730\u6253\u4e2a\u5e73\u624b\u3002 \u3000\u3000\u4f46\u89c1\u5200\u5149\u8d77\u5904\uff0c\u970d\u970d\u98ce\u751f\uff0c\u5149\u534e\u95ea\u70c1\uff0c\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u5df2\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u731b\u7136\u95f4\uff0c\u5ffd\u542c \u5f97\u5c60\u9f99\u5c0a\u8005\u5927\u559d\u4e00\u58f0\uff0c\u5149\u534e\u5ffd\u76db\uff0c\u4e00\u62db\u201c\u6bd2\u9f99\u51fa\u6d77\u201d\u8eab\u968f\u5200\u8fdb\uff0c\u90a3\u53e3\u5c60\u9f99\u5200\u7adf\u7136\u9707\u6563\u4e86\u4e91\u91cd \u7684\u62a4\u8eab\u5200\u5149\uff0c\u6b3a\u8eab\u76f4\u8fdb\uff0c\u8fde\u9ed1\u767d\u6469\u8bc3\u4e5f\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u5fcd\u4e0d\u4f4f\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6 \u5feb\uff0c\u4f46\u89c1\u4e91\u91cd\u5728\u5c60\u9f99\u5200\u79bb\u9762\u95e8\u8fd8\u4e0d\u5230\u4e94\u5bf8\u4e4b\u9645\uff0c\u7a81\u7136\u95f4\u4e00\u4e2a\u62e7\u8eab\uff0c\u4e00\u7ffb\u5200\u628a\uff0c\u53cd\u624b\u4e00\u5200\uff0c\u7acb \u523b\u6539\u5b88\u4e3a\u653b\uff0c\u5f84\u622a\u5c60\u9f99\u5c0a\u8005\u7684\u624b\u8155\uff0c\u8fd9\u4e00\u4e0b\u53d8\u62db\u795e\u901f\u4e4b\u6781\uff0c\u5c60\u9f99\u5c0a\u8005\u6025\u5fd9\u56de\u5200\u9632\u62a4\uff0c\u4f46\u542c\u5f97 \u545c\u54e8\u4e24\u58f0\uff0c\u4e91\u91cd\u6a2a\u5200\u75be\u626b\uff0c\u4ece\u201c\u6625\u4e91\u4e4d\u5c55\u201d\u53d8\u4e3a\u201c\u98ce\u51f0\u5c55\u7fc5\u201d\uff0c\u5df2\u662f\u5c06\u5c60\u9f99\u5c0a\u8005\u7684\u653b\u52bf\uff0c\u8f7b \u8f7b\u5316\u89e3\u4e86\u3002 \u3000\u3000\u767d\u6469\u8bc3\u53eb\u9053\uff1a\u201c\u5999\u554a\uff0c\u5999\u554a\uff01\u201d\u8bdd\u521a\u51fa\u53e3\uff0c\u4f46\u89c1\u5f20\u4e39\u67ab\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u7b2c\u4e8c\u5200\u65ab\u5f97 \u4e0d\u5999\uff01\u201d\u539f\u6765\u7f57\u6c49\u795e\u5200\u8fd9\u4e24\u5200\u4e43\u662f\u653b\u5b88\u517c\u5907\u7684\u5200\u6cd5\uff0c\u7b2c\u4e00\u5200\u4e3b\u653b\uff0c\u7b2c\u4e8c\u5200\u4e3b\u5b88\uff0c\u4e91\u91cd\u6025\u4e8e\u6c42 \u80dc\uff0c\u628a\u4e24\u5200\u90fd\u6539\u4e3a\u653b\u62db\uff0c\u51cc\u5389\u662f\u51cc\u5389\u6781\u4e86\uff0c\u5374\u4e0d\u514d\u9732\u51fa\u4e00\u4e1d\u7834\u7efd\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u8bdd\u58f0\u672a\u4e86\uff0c\u4f46\u89c1\u5c60\u9f99\u5c0a\u8005\u8eab\u5f62\u75be\u8d77\uff0c\u5c60\u9f99\u5200\u51fa\u624b\u5982\u7535\uff0c\u4ee5\u201c\u602a\u9e1f\u7ffb\u4e91\u201d\u4e4b\u5f0f\uff0c\u76d8 \u65cb\u626b\u4e0b\uff0c\u4e91\u91cd\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u7a81\u7136\u64a4\u624b\u6254\u5200\uff0c\u8fd9\u4e00\u62db\u662f\u201c\u7f57\u6c49\u795e\u5200\u201d\u4e2d\u7684\u6551\u6025\u7edd\u62db\uff0c\u63b7\u5200 \u4e4b\u65f6\uff0c\u4f7f\u4e86\u5de7\u52b2\uff0c\u4f24\u4e86\u654c\u4eba\u4ecd\u53ef\u98de\u56de\u3002\u53ea\u89c1\u5200\u5149\u7535\u5c04\uff0c\u76f4\u53d6\u5c60\u9f99\u5c0a\u8005\u7684\u54bd\u5589\uff0c\u5c60\u9f99\u5c0a\u8005\u5927\u53eb \u4e00\u58f0\uff0c\u5728\u534a\u7a7a\u4e2d\u8eab\u5f62\u4e00\u8f6c\uff0c\u54bd\u5589\u8981\u5bb3\u662f\u907f\u5f00\u4e86\uff0c\u4f46\u80a9\u5934\u7684\u76ae\u8089\uff0c\u5374\u88ab\u4e91\u91cd\u7684\u98de\u5200\u524a\u53bb\u4e86\u4e00\u5927 \u7247\uff0c\u767d\u6469\u8bc3\u677e\u4e86\u53e3\u6c14\uff0c \u3000\u3000\u53ca \u3000\u3000\xb7\uff5e\u53ea\u89c1\u8fd9\u573a\u4e91\u91cd\u5df2\u80dc\uff0c\u6b63\u5f85\u81ea\u5df1\u51fa\u573a\u3002\u54ea\u77e5\u8fd9\u5c60\u9f99\u5c0a\u8005\u7adf\u662f\u51f6\u608d\u4e4b\u6781\uff0c\u4e1d\u6beb\u4e0d\u987e\u53d7 \u4f24\uff0c\u5ffd\u5730\u5728\u7a7a\u4e2d\u75be\u6251\u800c\u4e0b\uff0c\u5c60\u9f99\u5200\u6697\u8d64\u8272\u7684\u5149\u534e\u5212\u5230\u4e86\u4e91\u91cd\u7684\u9762\u95e8\uff01 \u3000\u3000\u8bf4\u65f6\u8fdf\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u4e91\u91cd\u201c\u547c\u201d\u7684\u4e00\u638c\uff0c\u6a2a\u7a7a\u6253\u51fa\uff0c\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u5c60\u9f99\u5c0a\u8005\u7684\u4e00\u6761\u81c2 \u818a\u5df2\u662f\u65ad\u4e86\uff0c\u4e91\u91cd\u7684\u624b\u8155\uff0c\u4e5f\u7ed9\u5c60\u9f99\u5200\u5212\u5f00\u4e86\u4e00\u6761\u4e09\u5bf8\u591a\u957f\u7684\u4f24\u53e3\uff0c\u5c60\u9f99\u5c0a\u8005\u4e00\u58f0\u72de\u7b11\uff0c\u628a \u65ad\u81c2\u677e\u4e0b\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u7d2f\u5f97\u54b1\u5bb6\u6b8b\u5e9f\uff0c\u4f60\u8fd9\u6761\u5c0f\u547d\u4e5f\u4fdd\u4e0d\u5168\uff01\u201d\u4f17\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u4f46\u89c1\u4e91\u91cd\u8e09 \u8e09\u8dc4\u8dc4\u5730\u5954\u4e86\u56de\u6765\uff0c\u624b\u8155\u4e0a\u6d8c\u51fa\u4e00\u70b9\u70b9\u7600\u9ed1\u8272\u7684\u8840\u73e0\uff0c\u539f\u6765\u5c60\u9f99\u5c0a\u8005\u7ed9\u4e91\u91cd\u7684\u5927\u529b\u91d1\u521a\u638c\u626b \u65ad\u4e86\u624b\u81c2\uff0c\u4f46\u4e91\u91cd\u5374\u4e2d\u4e86\u4ed6\u7684\u6bd2\u5200\uff0c\u8fd9\u6bd2\u5200\u662f\u7528\u4e1c\u6d77\u660e\u971e\u5c9b\u7684\u7280\u89d2\u9e1f\u7caa\u548c\u6bd2\u86c7\u53e3\u6d8e\u6dec\u70bc\u7684\uff0c \u9664\u4e86\u5c60\u9f99\u5c0a\u8005\u672c\u4eba\u6240\u914d\u7684\u89e3\u836f\u4e4b\u5916\uff0c\u65e0\u836f\u53ef\u6cbb\u3002 \u3000\u3000\u4e91\u91cd\u7684\u59bb\u5b50\u6fb9\u53f0\u955c\u660e\u5c06\u4e08\u592b\u6276\u4e86\u56de\u6765\uff0c\u6495\u5f00\u4ed6\u7684\u8863\u88f3\uff0c\u4f46\u89c1\u4e00\u6761\u9ed1\u7ebf\u6162\u6162\u4e0a\u5347\uff0c\u5f20\u4e39\u67ab \u6025\u9053\uff1a\u201c\u4f60\u5feb\u6276\u4ed6\u5230\u9759\u5ba4\u91cc\u53bb\uff0c\u52a9\u4ed6\u8fd0\u529f\uff0c\u628a\u6bd2\u6c14\u963b\u6b62\u3002\u201d\u6fb9\u53f0\u955c\u660e\u4ea6\u662f\u884c\u5bb6\uff0c\u77e5\u9053\u8fd9\u6761\u9ed1 \u7ebf\u82e5\u5347\u5230\u5fc3\u623f\uff0c\u90a3\u5c31\u7eb5\u6709\u7075\u4e39\u5999\u836f\uff0c\u4e5f\u96be\u633d\u6551\uff0c\u6025\u6025\u4f9d\u8a00\u5c06\u4e91\u91cd\u6276\u56de\u9759\u5ba4\u3002 \u3000\u3000\u8d64\u971e\u9053\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u573a\u5f7c\u6b64\u90fd\u53d7\u91cd\u4f24\uff0c\u7b97\u626f\u5e73\u4e86\u3002\u4e0b\u4e00\u573a\u5462\u3002\u201d\u9ed1\u767d\u6469\u8bc3\u53cc\u53cc\u7eb5 \u51fa\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u4e00\u573a\u54b1\u5144\u5f1f\u4fe9\u65e9\u6302\u4e86\u53f7\u4e86\uff01\u201d \u3000\u3000\u9e20\u76d8\u5a46\u51b7\u7b11\u4e00\u58f0\uff0c\u66f3\u7740\u9e20\u5934\u62d0\u6756\uff0c\u7f13\u7f13\u8d70\u51fa\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4e45\u95fb\u9ed1\u767d\u6469\u8bc3\u8fd9\u4e24\u6839 \u5b9d\u6756\u4e43\u662f\u7a00\u4e16\u5947\u73cd\u3002\u4ef7\u503c\u8fde\u57ce\uff0c\u4ffa\u8001\u5a46\u5b50\u5012\u8981\u548c\u4f60\u4eec\u8d4c\u4e0a\u4e24\u6ce8\u3002\u201d\u9ed1\u767d\u6469\u8bc3\u9053\uff1a\u201c\u8d4c\u4ec0 \u4e48\uff1f\u201d\u9e20\u76d8\u5a46\u9053\uff1a\u201c\u4e00\u8d4c\u6027\u547d\uff0c\u4e8c\u8d4c\u5f69\u5934\uff0c\u5f69\u5934\u5c31\u662f\u624b\u4e2d\u7684\u5175\u5668\uff0c\u6211\u770b\u4e0a\u4e86\u4f60\u4eec\u8fd9\u4e24\u6839\u5b9d\u6756 \u5462\uff01\u201d \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u51b7\u7b11\u9053\uff1a\u201c\u6709\u672c\u4e8b\u7684\u5c3d\u7ba1\u62ff\u53bb\uff0c\u6211\u53ef\u4e0d\u5e0c\u7f55\u8fd9\u6839\u62d0\u6756\u3002\u201d\u9e20\u76d8\u5a46\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u6211\u8fd9\u6839\u62d0\u6756\u867d\u7136\u4e0d\u8d77\u773c\uff0c\u5374\u4e5f\u662f\u4ef6\u597d\u5b9d\u8d1d\u54e9\u3002\u8fd9\u4e2a\u8d4c\u8d5b\uff0c\u4e00\u7edd\u4e0d\u662f\u5360\u4f60\u7684\u4fbf\u5b9c\uff0c\u4e0d\u4fe1\u4f60\u5403 \u4e00\u62d0\u5c31\u77e5\u9053\u4e86\uff01\u201d\u9ed1\u767d\u6469\u8bc3\u53cc\u6756\u4e00\u5708\uff0c\u9e20\u76d8\u5a46\u8bdd\u672a\u8bf4\u5b8c\uff0c\u500f\u5730\u4e00\u62d0\u6253\u51fa\uff0c\u4f46\u53ea\u542c\u5f97\u4e00\u9635\u91d1\u9e23 \u7389\u632f\u4e4b\u58f0\uff0c\u55e1\u55e1\u4e0d\u7edd\uff0c\u7eff\u5149\u767d\u5149\uff0c\u500f\u5730\u6563\u5f00\uff0c\u9ed1\u767d\u6469\u8bc3\u548c\u9e20\u76d8\u5a46\u5404\u81ea\u9707\u9000\u4e09\u6b65\uff0c\u4e09\u4eba\u4e2d\u767d\u6469 \u8bc3\u529f\u529b\u7a0d\u5dee\uff0c\u4e24\u818a\u90fd\u7ed9\u9707\u5f97\u9178\u9ebb\uff0c\u8fd9\u624d\u77e5\u9053\u54c0\u7262\u5c71\u9e20\u76d8\u5a46\u516c\u5b59\u65e0\u57a2\u7684\u5929\u751f\u795e\u529b\u786e\u662f\u540d\u4e0d\u865a \u4f20\uff01 \u3000\u3000\u9e20\u76d8\u5a46\u4e5f\u662f\u5fc3\u5934\u4e00\u9707\uff0c\u4ece\u6765\u6ca1\u6709\u4eba\u6562\u786c\u78b0\u786c\u5730\u63a5\u5979\u4e00\u62d0\uff0c\u8fd9\u6b21\u9ed1\u767d\u6469\u8bc3\u4e0d\u4f46\u786c\u78b0\u786c\u63a5\uff0c \u800c\u4e14\u5c06\u5979\u9707\u9000\u4e09\u6b65\uff0c\u8981\u4e0d\u662f\u5979\u53ca\u65e9\u4f7f\u51fa\u5343\u65a4\u5815\u7684\u91cd\u8eab\u6cd5\uff0c\u9669\u4e9b\u5c31\u8981\u5f53\u573a\u683d\u5012\uff01 \u3000\u3000\u53cc\u65b9\u4e00\u9000\u5373\u4e0a\uff0c\u53ea\u89c1\u9ed1\u6469\u8bc3\u5b9d\u6756\u5de6\u6307\uff0c\u659c\u62cd\u8109\u95e8\uff1b\u767d\u6469\u8bc3\u5b9d\u6756\u53f3\u6307\uff0c\u6b63\u6233\u8840\u6d77\uff0c\u7eff\u5149\u767d \u5149\uff0c\u6709\u5982\u5954\u96f7\u95ea\u7535\uff0c\u500f\u5730\u5408\u56f4\uff0c\u628a\u90a3\u51e0\u4e2a\u7d20\u8d1f\u76db\u540d\u7684\u5927\u9b54\u5934\u4e5f\u5413\u5f97\u5fc3\u60ca\u80c6\u6218\uff0c\u731b\u542c\u5f97\u9e20\u76d8\u5a46 \u5927\u543c\u4e00\u58f0\uff0c\u9e20\u5934\u62d0\u6756\u5f80\u4e0b\u4e00\u6c89\uff0c\u4e00\u62db\u201c\u5e73\u6c99\u848b\u96c1\u201d\u5148\u5378\u5f00\u4e86\u767d\u6469\u8bc3\u7684\u653b\u52bf\uff0c\u63a5\u7740\u987a\u52bf\u4e00\u62cd\uff0c \u5f80\u4e0a\u53cd\u5c55\uff0c\u500f\u5730\u559d\u58f0\u201c\u7740\uff01\u201d\u62d0\u6756\u4e00\u8f6c\uff0c\u9e20\u5934\u7684\u957f\u5634\uff0c\u5544\u5230\u4e86\u9ed1\u6469\u8bc3\u7684\u9762\u95e8\u3002 \u3000\u3000\u9e20\u76d8\u5a46\u8fd9\u51e0\u62db\u7528\u5f97\u7cbe\u5999\u7edd\u4f26\uff0c\u9669\u72e0\u4e4b\u6781\uff0c\u6ee1\u4ee5\u4e3a\u9ed1\u6469\u8bc3\u96be\u9003\u62d0\u4e0b\uff0c\u54ea\u77e5\u9ed1\u6469\u8bc3\u529f\u529b\u6bd4\u767d \u6469\u8bc3\u6df1\u539a\u5f97\u591a\uff0c\u5979\u8fd9\u4e00\u4e0b\u6740\u624b\uff0c\u82e5\u662f\u7528\u6765\u5bf9\u4ed8\u767d\u6469\u8bc3\uff0c\u6216\u8bb8\u80fd\u591f\u594f\u6548\uff0c\u62ff\u6765\u5bf9\u4ed8\u9ed1\u6469\u8bc3\uff0c\u5979 \u5feb\uff0c\u9ed1\u6469\u8bc3\u4e5f\u5feb\uff0c\u4f46\u542c\u788d\u201c\u55d6\u201d\u7684\u4e00\u58f0\uff0c\u9ed1\u6469\u8bc3\u7684\u7eff\u7389\u6756\u5df2\u5c01\u4e86\u4e0a\u53bb\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4e0d\u89c1 \u5f97\uff01\u201d\u7eff\u5149\u4e00\u5708\uff0c\u8f6c\u773c\u4e4b\u95f4\u53c8\u4e0e\u767d\u5149\u5408\u56f4\uff0c\u5c06\u9e20\u76d8\u5a46\u5708\u5728\u4e24\u8272\u5149\u8f6e\u91cc\u9762\u3002 \u3000\u3000\u8fd9\u51e0\u4e0b\u5b50\u5154\u8d77\u9e64\u843d\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u5f62\u52bf\u63a5\u8fde\u53d8\u6362\uff0c\u628a\u65c1\u89c2\u4eba\u7b49\u770b\u5f97\u773c\u82b1\u64a9\u4e71\uff0c\u4f46\u542c\u5f97\u9e20\u76d8 \u5a46\u8fde\u58f0\u6012\u543c\uff0c\u9e20\u5934\u62d0\u6756\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u4f46\u9ed1\u767d\u6469\u8bc3\u7684\u53cc\u6756\u5408\u56f4\uff0c\u4e0d\u6c42\u5e78\u80dc\uff0c\u5c01\u95ed\u5f97 \u8c28\u4e25\u4e4b\u6781\uff0c\u76f4\u6253\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u5140\u662f\u4e0d\u5206\u80dc\u8d25\uff0c\u53cc\u65b9\u90fd\u89c9\u5f97\u5bf9\u65b9\u7684\u538b\u529b\u6709\u5982\u6cf0\u5c71\u538b\u9876\uff0c\u53ea\u597d\u62fc \u4e86\u5168\u529b\u6297\u62d2\uff0c\u534a\u70b9\u4e16\u4e0d\u6562\u653e\u677e\u3002 \u3000\u3000\u4f17\u9b54\u5934\u770b\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u4e2a\u4e2a\u5012\u5438\u51c9\u6c14\uff0c\u731b\u7136\u95f4\u53ea\u542c\u5f97\u4e00\u9635\u4f59\u58f0\u7389\u632f\uff0c\u500f\u5730\u58f0\u97f3\u9759\u5bc2\uff0c\u4f46 \u89c1\u9e20\u76d8\u5a46\u53cc\u624b\u7d27\u63e1\u9e20\u6756\u7684\u4e2d\u95f4\uff0c\u5de6\u7aef\u62b5\u4f4f\u9ed1\u6469\u8bc3\u7684\u7eff\u7389\u6756\uff0c\u53f3\u7aef\u62b5\u4f4f\u767d\u6469\u8bc3\u7684\u767d\u7389\u6756\uff0c\u4e09\u4e2a \u4eba\u6210\u4e86\u4e2a\u54c1\u5b57\u5f62\uff0c\u7262\u7262\u9489\u7740\u5730\u9762\uff0c\u5c31\u50cf\u4e09\u5c0a\u5851\u50cf\u4e00\u6837\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002\u4e0d\u8fc7\u4e00\u76cf\u8336\u7684\u65f6\u523b\uff0c\u4e09\u4e2a \u4eba\u7684\u5934\u9884\uff0c\u90fd\u5192\u51fa\u70ed\u817e\u817e\u767d\u6c14\u6765\u3002 \u3000\u3000\u8d64\u971e\u9053\u4eba\u548c\u5f20\u4e39\u67ab\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u8981\u77e5\u8fd9\u7b49\u4ee5\u5185\u529b\u76f8\u62fc\uff0c\u6700\u8017\u7cbe\u795e\uff0c\u4e0d\u662f\u4e24\u8d25\u4ff1\u4f24\uff0c\u4fbf\u662f \u5f3a\u5b58\u5f31\u4ea1\uff0c\u7edd\u65e0\u4fa5\u5e78\uff01\u9e20\u76d8\u5a46\u7684\u6b66\u529f\u5728\u4e00\u4f17\u9b54\u5934\u4e4b\u4e2d\uff0c\u4ec5\u6b21\u4e8e\u8d64\u971e\u8fe2\u4eba\u3002\u8d64\u971e\u9053\u4eba\u8fd9\u6b21\u9080\u5979 \u4e0a\u5c71\uff0c\u539f\u662f\u51c6\u5907\u4e07\u4e00\u4e0a\u5b98\u5929\u91ce\u51fa\u624b\uff0c\u8981\u5979\u6765\u5bf9\u4ed8\u4e0a\u5b98\u5929\u91ce\u7684\uff0c\u89c1\u6b64\u60c5\u5f62\uff0c\u6697\u53eb\u4e0d\u5999\uff0c\u751f\u6015\u9e20 \u76d8\u5a46\u88ab\u9ed1\u767d\u6469\u8bc3\u7d2f\u5012\uff0c\u81ea\u5df1\u5148\u6298\u4e86\u4e2a\u6700\u5f97\u529b\u7684\u5e2e\u624b\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u7422\u78e8\u6b66\u529f\uff0c\u539f\u662f\u4e0d\u5fc5\u62fc\u4e2a\u751f\u6b7b\uff0c\u8fd9\u4e00\u573a\u7b97\u548c\u4e86\u5427\u3002\u201d\u4ed6 \u4f55\u5c1d\u770b\u4e0d\u51fa\u6765\uff0c\u82e5\u662f\u4e45\u6301\u4e0b\u53bb\uff0c\u9ed1\u767d\u6469\u8bc3\u7ec8\u53ef\u5360\u5230\u4e0a\u98ce\uff0c\u4f46\u5373\u7b97\u628a\u9e20\u76d8\u5a46\u7d2f\u6b7b\uff0c\u9ed1\u767d\u6469\u8bc3\u6700 \u5c11\u4e5f\u5f97\u5927\u75c5\u4e00\u573a\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u56e0\u6b64\u800c\u81f4\u6b8b\u5e9f\uff0c\u662f\u4ee5\u51fa\u8a00\u529d\u89e3\u3002 \u3000\u3000\u8d64\u971e\u9053\u4eba\u5df4\u4e0d\u5f97\u5f20\u4e39\u67ab\u8bf4\u8fd9\u53e5\u8bdd\uff0c\u6025\u5fd9\u63a5\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u554a\uff01\u201d\u624b\u6301\u7fbd\u6247\uff0c\u4eb2\u81ea\u51fa\u573a\u3002 \u3000\u3000\u4f46\u89c1\u4ed6\u6b65\u5c65\u5b89\u7965\uff0c\u5c31\u50cf\u5e73\u5e38\u8d70\u8def\u4e00\u822c\uff0c\u6643\u773c\u4e4b\u95f4\uff0c\u5c31\u5230\u4e86\u9e20\u76d8\u5a46\u8eab\u8fb9\uff0c\u7565\u4e00\u8e0c\u8e87\uff0c\u7fbd\u6247 \u4fbf\u5f80\u5f53\u4e2d\u4e00\u9694\uff0c\u4f46\u9e20\u76d8\u5a46\u4e0e\u9ed1\u767d\u6469\u8bc3\u4e09\u5927\u9ad8\u624b\u7684\u5185\u529b\u62e7\u6210\u4e00\u80a1\uff0c\u7aef\u7684\u91cd\u5982\u5c71\u5cb3\uff0c\u8d64\u971e\u9053\u4eba\u6643 \u4e86\u4e24\u6643\uff0c\u573a\u4e2d\u76f8\u6301\u7684\u4e09\u4e2a\u4eba\u4ecd\u662f\u539f\u5730\u4e0d\u52a8\u3002\u8d64\u971e\u9053\u4eba\u9762\u5b54\u6da8\u5f97\u901a\u7ea2\uff0c\u5438\u4e86\u53e3\u6c14\uff0c\u6b63\u60f3\u518d\u62fc\u635f \u8017\u771f\u529b\uff0c\u5c06\u4e09\u4eba\u5206\u9694\u5f00\u6765\uff0c\u5ffd\u542c\u5f97\u201c\u55d6\u201d\u7684\u4e00\u58f0\uff0c\u5f20\u4e39\u67ab\u4e00\u5251\u98de\u6765\uff0c\u5f80\u5f53\u4e2d\u4e00\u63d2\uff0c\u5fae\u7b11\u8bf4 \u9053\uff1a\u201c\u6211\u6765\u52a9\u9053\u957f\u4e00\u81c2\u4e4b\u529b\uff01\u201d\u8d64\u971e\u9053\u4eba\u7fbd\u6247\u4e00\u6325\uff0c\u5c06\u9e20\u76d8\u5a46\u7684\u62d0\u6756\u6258\u8d77\uff0c\u5f20\u4e39\u67ab\u7684\u957f\u5251\u4e00 \u5f15\uff0c\u4e5f\u5c06\u9ed1\u767d\u6469\u8bc3\u7684\u53cc\u6756\u5206\u5f00\u3002\u8981\u4e0d\u662f\u8d64\u971e\u9053\u4eba\u4e0e\u5f20\u4e39\u67ab\u5408\u529b\u65bd\u4e3a\uff0c\u53ea\u6015\u4e16\u4e0a\u65e0\u4eba\u80fd\u591f\u4ee5\u4e00 \u4e2a\u4eba\u7684\u529b\u91cf\u9694\u5f00\u8fd9\u4e09\u5927\u9ad8\u624b\uff01 \u3000\u3000\u9e20\u76d8\u5a46\u4e0e\u9ed1\u767d\u6469\u8bc3\u6012\u76ee\u800c\u89c6\uff0c\u4f46\u4e09\u4e2a\u4eba\u90fd\u5df1\u7d2f\u5f97\u6c14\u5598\u5401\u5401\uff0c\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u53ea\u597d\u5404\u81ea\u9000\u4e0b\u3002 \u5f20\u4e39\u67ab\u5fae\u7b11\u9053\uff1a\u201c\u96be\u5f97\u8d64\u971e\u9053\u957f\u9a7e\u5230\uff0c\u665a\u8f88\u73b0\u5728\u5c31\u8bf7\u6307\u6559\u4e00\u573a\u3002\u201d \u3000\u3000\u8d64\u971e\u9053\u4eba\u6602\u9996\u5411\u5929\uff0c\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8d2b\u9053\u6614\u65f6\u66fe\u4e09\u6b21\u5411\u4ee4\u5e08\u7956\u9886\u6559\uff0c\u53ef\u60dc \u8fd9\u6b21\u65e0\u7f18\u5f97\u4ed6\u6307\u6559\uff0c\u4e5f\u7f62\uff0c\u542c\u8bf4\u4f60\u4eec\u592b\u5987\u5df2\u5f97\u4e86\u7384\u673a\u9038\u58eb\u53cc\u5251\u5408\u58c1\u7684\u771f\u4f20\uff0c\u597d\uff0c\u5c31\u8bf7\u8d24\u4f09\u4fea \u8054\u624b\u540c\u4e0a\uff0c\u8ba9\u8d2b\u9053\u5f00\u5f00\u773c\u754c\u3002\u201d\u8d64\u971e\u9053\u4eba\u5176\u5b9e\u4e5f\u77e5\u9053\u5f20\u4e39\u67ab\u7684\u5251\u6cd5\u529f\u529b\u90fd\u6781\u6df1\u539a\uff0c\u751a\u81f3\u6bd4\u4ed6 \u7684\u5e08\u53d4\u5e08\u4f2f\u8fd8\u5f3a\uff0c\u4e0d\u8fc7\u8bba\u8d77\u73ed\u8f88\uff0c\u4ed6\u5230\u5e95\u6bd4\u5f20\u4e39\u67ab\u9ad8\u51fa\u4e24\u8f88\uff0c\u82e5\u7136\u4ee5\u4e00\u654c\u4e00\uff0c\u53ea\u6050\u5728\u4f17\u9b54\u5934 \u773c\u4e2d\u5931\u4e86\u8eab\u4efd\uff0c\u662f\u4ee5\u53e3\u51fa\u6b64\u8a00\u3002 \u3000\u3000\u4e91\u857e\u62b1\u7740\u5b69\u5b50\uff0c\u501a\u5728\u95e8\u524d\uff0c\u542c\u4e86\u8d64\u971e\u9053\u4eba\u7684\u8bdd\uff0c\u67f3\u7709\u4e00\u7ad6\uff0c\u9053\uff1a\u201c\u627f\u73e0\uff0c\u4f60\u7ed9\u6211\u62b1\u62b1\u5c0f \u5e08\u59b9\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4e91\u59b9\uff0c\u4f60\u4e0d\u7528\u6765\u3002\u201d\u4e8e\u627f\u73e0\u77e5\u9053\u5e08\u6bcd\u4ea7\u540e\uff0c\u529f\u529b\u5c1a\u672a\u6062\u590d\uff0c\u8bf4\u9053\uff1a \u201c\u5e08\u6bcd\uff0c\u6211\u66ff\u4f60\u53bb\u4e00\u8d9f\uff0c\u6211\u82e5\u662f\u4e0d\u6210\uff0c\u4f60\u518d\u66ff\u6211\u3002\u201d\u94c1\u955c\u5fc3\u9a87\u9053\uff1a\u201c\u4f60\u53bb\uff01\u201d\u8981\u77e5\u8d64\u971e\u9053\u4eba \u7684\u6b66\u529f\uff0c\u4e45\u5df2\u58f0\u9707\u6b66\u6797\uff0c\u51e0\u4e4e\u4e0e\u7384\u673a\u9038\u58eb\u3001\u4e0a\u5b98\u5929\u91ce\u9f0e\u8db3\u800c\u7acb\uff0c\u4e8e\u627f\u73e0\u8981\u60f3\u4e0e\u4ed6\u76f8\u6297\uff0c\u4efb\u8c01 \u542c\u4e86\uff0c\u90fd\u4f1a\u8ba4\u4e3a\u662f\u87b3\u81c2\u5f53\u8f66\uff0c\u94c1\u955c\u5fc3\u5173\u6000\u5fc3\u5207\uff0c\u66f4\u662f\u60ca\u9a87\u4e4b\u6781\uff0c\u6025\u5fd9\u62e6\u963b\u3002\u6c90\u71d5\u5728\u65c1\u8fb9\u770b \u4e86\uff0c\u53ea\u89c9\u9178\u6e9c\u6e9c\u5730\u6ee1\u4e0d\u662f\u5473\u513f\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u627f\u73e0\u4f60\u4e5f\u4e0d\u7528\u6765\uff0c\u628a\u4f60\u7684\u5251\u7ed9\u6211\u3002\u201d\u4e8e\u627f\u73e0\u7565\u4e00\u8e0c\u8e87\uff0c\u89e3 \u4e0b\u9752\u51a5\u5b9d\u5251\u5f80\u524d\u4e00\u629b\uff0c\u5f20\u4e39\u67ab\u4e00\u628a\u63a5\u7740\uff0c\u968f\u5373\u53c8\u4eae\u51fa\u767d\u4e91\u5b9d\u5251\uff0c\u53cc\u5251\u4e00\u6643\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u655d \u6d3e\u7684\u53cc\u5251\u5408\u58c1\u4e4b\u672f\uff0c\u539f\u4e0d\u5fc5\u4e24\u4e2a\u4eba\u4f7f\uff0c\u8fd9\u5c31\u8bf7\u524d\u8f88\u6307\u6559\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u8fd9\u51e0\u5e74\u6765\u6b66\u529f\u5df2\u5230\u5927\u6210\u5883\u754c\uff0c\u4e0e\u4eba\u5bf9\u654c\uff0c\u4ece\u4e0d\u7528\u5251\uff0c\u800c\u4eca\u4eae\u51fa\u4e24\u628a\u5b9d\u5251\uff0c\u5b9e\u5df2\u662f \u5bf9\u8d64\u971e\u9053\u4eba\u5927\u5927\u5c0a\u91cd\u3002\u8d64\u971e\u9053\u4eba\u5374\u4ecd\u662f\u81ea\u6043\u8eab\u4efd\uff0c\u7fbd\u6247\u4e00\u6325\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5c31\u8fdb\u62db \u5427\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u655d\u5e08\u7956\u4ea6\u66fe\u79f0\u8a89\u8fc7\u9053\u957f\u7684\u5251\u6cd5\uff0c\u8bf7\u9053\u957f\u4eae\u5251\uff0c\u4e5f\u597d\u8ba9\u5c0f\u8f88\u89c1\u8bc6\u89c1\u8bc6\u3002\u201d \u8d64\u971e\u9053\u4eba\u9053\uff1a\u201c\u662f\u4e48\uff1f\u4ee4\u5e08\u7956\u8fd9\u6837\u8bf4\u8fc7\u4e48\u4e86\u5440\uff0c\u53ef\u60dc\u4ed6\u73b0\u5728\u95ed\u5173\u4e0d\u51fa\uff0c\u53eb\u6211\u4e0e\u8c01\u4eba\u6bd4\u5251\uff1f\u5f20 \u4e39\u67ab\u4f60\u4e0d\u5fc5\u7f57\u55e6\uff0c\u8fdb\u62db\u5427\uff01\u201d\u7fbd\u6247\u8f7b\u6447\uff0c\u795e\u6c14\u72c2\u50b2\u4e4b\u6781\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5fc3\u4e2d\u6709\u6c14\uff0c\u4e0d\u6012\u53cd\u7b11\uff0c\u53cc\u5251\u626c\u7a7a\u4e00\u95ea\uff1b\u50fb\u556a\u4f5c\u54cd\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u5c31 \u8bf7\u6055\u5c0f\u8f88\u5931\u793c\u4e86\uff01\u201d\u500f\u5730\u9752\u5149\u4e00\u8d77\uff0c\u9752\u51a5\u5b9d\u5251\u76f4\u5954\u8d64\u971e\u9053\u4eba\u7684\u201c\u98ce\u5e9c\u7a74\u201d\uff0c\u8d64\u971e\u9053\u4eba\u5916\u8c8c\u72c2 \u50b2\uff0c\u5b9e\u662f\u5bf9\u5f20\u4e39\u67ab\u4e00\u70b9\u4e5f\u4e0d\u6562\u5c0f\u89d1\uff0c\u89c1\u5f20\u4e39\u67ab\u5251\u628a\u4e00\u52a8\uff0c\u7fbd\u6247\u7acb\u523b\u4e00\u5f20\uff0c\u4ed6\u8fd9\u628a\u7fbd\u6247\u9aa8\u662f\u7528 \u767e\u70bc\u5408\u91d1\u6240\u6253\uff0c\u5341\u51e0\u679d\u6247\u9aa8\uff0c\u9664\u4e86\u7fbd\u6bdb\u88c5\u9970\uff0c\u90fd\u662f\u6781\u4e3a\u950b\u5229\u7684\u900f\u9aa8\u9488\uff0c\u5b9e\u9645\u4e0a\u4e5f\u662f\u4e00\u4ef6\u7f55\u89c1 \u7684\u5916\u95e8\u5175\u5668\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u7fbd\u6247\u4e00\u6325\uff0c\u9752\u5149\u95ea\u70c1\uff0c\u8d64\u971e\u9053\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u53cc\u5251\u5408\u58c1\uff0c\u4e0d\uff0c \u4e0d\uff0c\u54ce\u5440\uff01\u201d\u4ed6\u672c\u60f3\u8bf4\uff1a\u201c\u53cc\u5251\u5408\u58c1\uff0c\u4e0d\u8fc7\u5982\u65af\uff01\u201d\u54ea\u77e5\u521a\u8bf4\u5f97\u534a\u53e5\uff0c\u90a3\u9752\u5149\u5df2\u500f\u5730\u63a0\u8fc7\u4ed6 \u7684\u5934\u9876\uff0c\u53cd\u5708\u56de\u6765\uff0c\u51e0\u4e4e\u5c31\u5728\u540c\u4e00\u77ac\u95f4\uff0c\u5f20\u4e39\u67ab\u5de6\u624b\u7684\u767d\u4e91\u5ba4\u5251\uff0c\u53c8\u5df2\u7535\u5c04\u800c\u51fa\uff0c\u53cc\u5251\u4e00 \u5708\uff0c\u628a\u4ed6\u4e0a\u534a\u8eab\u7684\u5341\u516b\u5904\u5927\u7a74\uff0c\u5168\u90e8\u7b3c\u7f69\u5728\u53cc\u5251\u7684\u5a01\u529b\u4e4b\u4e0b\uff0c\u8d64\u971e\u9053\u4eba\u7fbd\u6247\u5de6\u53f3\u76d8\u65cb\uff0c\u7384\u529f \u9ed8\u8fd0\uff0c\u5f20\u4e39\u67ab\u4e00\u5251\u7d27\u4f3c\u4e00\u5251\uff0c\u7adf\u662f\u6beb\u4e0d\u653e\u677e\uff0c\u8d64\u971e\u9053\u4eba\u6325\u6247\u62d2\u654c\uff0c\u53c8\u4e00\u8fde\u53d1\u4e86\u4e09\u638c\u5288\u7a7a\u638c\uff0c \u4e24\u4eba\u529f\u529b\u539f\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u5f20\u4e39\u67ab\u4e00\u5360\u4e86\u5148\u624b\uff0c\u5288\u7a7a\u638c\u4e5f\u9707\u4ed6\u4e0d\u9000\uff0c\u5f20\u4e39\u67ab\u53cc\u5251\u8fde\u73af\u75be\u523a\uff0c\u8d8a \u8feb\u8d8a\u7d27\uff0c\u5ffd\u5730\u51b7\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88\u8fd8\u662f\u4e0d\u80af\u62d4\u5251\u8d50\u6559\u4e48\uff1f\u201d\u9752\u51a5\u5251\u5de6\u523a\u201c\u5546\u4e18\u7a74\u201d\uff0c\u767d\u4e91\u5251\u53f3 \u523a\u201c\u7075\u67a2\u7a74\u201d\u8d64\u971e\u9053\u4eba\u7fbd\u6247\u4e00\u6251\uff0c\u5de6\u638c\u6a2a\u5288\uff0c\u6b63\u81ea\u51dd\u795e\u89e3\u62c6\uff0c\u500f\u7136\u95f4\u5f20\u4e39\u67ab\u7684\u53cc\u5251\u500f\u5730\u6613 \u4f4d\uff0c\u9752\u5149\u767d\u5149\u4ea4\u53c9\u75be\u63a0\uff0c\u7ade\u4ece\u8d64\u971e\u9053\u4eba\u610f\u6599\u4e0d\u5230\u7684\u65b9\u4f4d\u75be\u5c04\u8fc7\u6765\uff0c\u8d64\u971e\u9053\u4eba\u6025\u610f\u65bd\u5c55\u201c\u79fb\u5f62 \u6613\u4f4d\u201d\u7684\u8eab\u6cd5\uff0c\u7fbd\u6247\u65b9\u6447\uff0c\u4f46\u89c9\u5251\u6c14\u9634\u68ee\uff0c\u89e6\u4f53\u751f\u51c9\uff0c\u5f20\u4e39\u67ab\u7684\u53cc\u5251\u5df2\u5728\u4ed6\u7684\u5934\u9876\u524a\u8fc7\uff0c\u5251 \u950b\u51e0\u4e4e\u89e6\u53ca\u4e86\u5934\u76ae\u3002\u8d64\u971e\u9053\u4eba\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u4e0d\u81ea\u89c9\u5730\u628a\u4f69\u5251\u51fa\u9798\uff0c\u5de6\u6247\u53f3 \u5251\uff0c\u594b\u529b\u4e00\u6321\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u5316\u89e3\u4e86\u5f20\u4e39\u67ab\u7684\u53cc\u5251\u653b\u52bf\uff0c\u5413\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\u3002\u767d\u6469\u8bc3\u8fd9\u65f6\u5598\u606f\u5df2 \u5b9a\uff0c\u5728\u65c1\u8fb9\u770b\u5f97\u62cd\u638c\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u725b\u9f3b\u5b50\u81ed\u9053\u571f\uff0c\u6446\u4ec0\u4e48\u67b6\u5b50\uff1f\u54c8\uff0c\u54c8\uff0c\u54c8\uff01\u4f60\u8fd9\u662f\u656c\u9152 \u4e0d\u54c8\u5403\u7f5a\u9152\uff0c\u77a7\uff0c\u8fd8\u4e0d\u662f\u4e56\u4e56\u5730\u8981\u4eae\u5251\uff1f\u201d\u8d64\u971e\u9053\u4eba\u9762\u8272\u4e00\u9635\u9752\u4e00\u9635\u7ea2\uff0c\u4f46\u5f20\u4e39\u67ab\u7684\u653b\u52bf\u4ecd \u7d27\uff0c\u4ed6\u53ef\u4e0d\u6562\u548c\u767d\u6469\u8bc3\u6597\u5634\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u867d\u7136\u62a2\u4e86\u5148\u624b\uff0c\u5374\u662f\u534a\u70b9\u4e5f\u4e0d\u6562\u5927\u610f\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u8001\u9053\u5c45\u7136\u53ea\u51ed\u7740\u4e00\u628a\u7fbd \u6247\uff0c\u8fde\u6321\u6211\u5341\u4e09\u624b\u5947\u95e8\u5251\u6cd5\uff0c\u602a\u4e0d\u5f97\u5e08\u7956\u4e5f\u63a8\u8bb8\u4ed6\u662f\u4e00\u6d41\u9ad8\u624b\u3002\u201d\u8d64\u971e\u9053\u4eba\u6709\u5251\u5728\u624b\uff0c\u5f62\u52bf \u5927\u5927\u4e0d\u540c\uff0c\u4f46\u89c1\u4ed6\u90a3\u53e3\u5251\u9ed1\u6f06\u6f06\u7684\u751a\u4e0d\u8d77\u773c\uff0c\u4f46\u5374\u662f\u5343\u9524\u767e\u70bc\u7684\u9563\u94c1\u7cbe\u534e\uff0c\u5f20\u4e39\u67ab\u7684\u5b9d\u5251\uff0c \u867d\u7136\u80fd\u4ee4\u4ed6\u8fd9\u53e3\u5251\u635f\u4f24\uff0c\u5374\u4e0d\u80fd\u5c06\u4ed6\u524a\u65ad\uff0c\u53ea\u89c1\u4ed6\u7684\u5251\u4e0d\u4f4f\u5730\u6253\u7740\u5706\u5708\uff0c\u597d\u50cf\u4e00\u5708\u5708\u7684\u6ce2\u6d6a \u4f3c\u7684\uff0c\u8981\u628a\u5f20\u4e39\u67ab\u7684\u53cc\u5251\u5377\u8d70\u3002\u539f\u6765\u8d64\u971e\u9053\u4eba\u7684\u5185\u529f\u6df1\u6e5b\uff0c\u4ed6\u7684\u5251\u6cd5\u81ea\u6210\u4e00\u5bb6\uff0c\u4e0d\u5728\u4e4e\u5251\u7684 \u950b\u5229\uff0c\u6240\u4ee5\u5f88\u5c11\u7528\u524a\u523a\u7684\u624b\u6cd5\uff0c\u53cd\u800c\u6709\u9524\u68d2\u7684\u786c\u78b0\u624b\u6cd5\uff0c\u6bcf\u4e00\u63a5\u89e6\uff0c\u90fd\u662f\u5185\u529b\u76f8\u53cd\u3002\u6bd4\u4e00\u822c \u7684\u6bd4\u5251\uff0c\u90a3\u60ca\u9669\u4e4b\u5904\uff0c\u80dc\u8fc7\u4e07\u5206\u3002 \u3000\u3000\u6fc0\u6218\u95f4\u53ea\u89c1\u5f20\u4e39\u67ab\u7684\u5251\u6cd5\u9aa4\u5730\u5c55\u5f00\uff0c\u6025\u5982\u63a3\u7535\uff0c\u5251\u82b1\u9519\u8584\uff0c\u5b9b\u5982\u6d12\u4e0b\u4e86\u6ee1\u5929\u7e41\u661f\uff0c\u5c06\u8d64 \u971e\u9053\u4eba\u8361\u8d77\u7684\u4e00\u5708\u5708\u201c\u5251\u75d5\u201d\u90fd\u53cd\u903c\u56de\u53bb\u3002\u8d64\u971e\u9053\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u8fd0\u8db3\u771f\u529b\uff0c\u94c1\u5251\u6025\u538b\uff0c\u7fbd\u6247 \u7ffb\u98de\uff0c\u4f46\u542c\u7740\u98d2\u98d2\u8fde\u58f0\uff0c\u5251\u5149\u95ea\u95ea\uff0c\u4e00\u65f6\u95f4\u4f3c\u4e4e\u662f\u8d64\u971e\u9053\u4eba\u5360\u4e86\u4e0a\u98ce\uff0c\u770b\u770b\u5c31\u8981\u5c06\u5f20\u4e39\u67ab\u7684 \u653b\u52bf\u538b\u4e86\u4e0b\u53bb\uff0c\u53ea\u4e00\u8f6c\u773c\u95f4\uff0c\u5f20\u4e39\u67ab\u7684\u5251\u5149\u53c8\u628a\u4ed6\u5305\u56f4\u8d77\u6765\uff0c\u5982\u662f\u8005\u4e24\u6b21\u4e09\u756a\uff0c\u4e92\u76f8\u96c4\u957f\uff0c \u5230\u5f97\u540e\u6765\uff0c\u4f46\u89c1\u5251\u5149\u970d\u970d\uff0c\u5251\u6c14\u7eb5\u6a2a\uff0c\u76d8\u65cb\u8fdb\u9000\uff0c\u8d77\u843d\u53d8\u5316\uff0c\u4e0d\u53ef\u540d\u72b6\uff0c\u4e0d\u53ef\u6349\u6478\uff0c\u5373\u7b97\u662f \u9ed1\u767d\u6469\u8bc3\u548c\u4e4c\u8499\u592b\u592b\u5987\u7b49\u4e00\u7b49\u7684\u6b66\u5b66\u5927\u5e08\uff0c\u4e5f\u5206\u8fa8\u4e0d\u51fa\u8c01\u5f3a\u8c01\u5f31\u3002 \u3000\u3000\u8d64\u971e\u9053\u4eba\u80dc\u5728\u529f\u529b\u7a0d\u9ad8\uff0c\u5f20\u4e39\u67ab\u5219\u80dc\u5728\u5251\u6cd5\u7a0d\u5999\uff0c\u6fc0\u6218\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u5140\u81ea\u4e0d\u5206\u80dc\u8d1f\u3002\u9e20 \u76d8\u5a46\u8fd9\u65f6\u6c14\u529b\u65e9\u5df2\u6062\u590d\uff0c\u94c1\u62d0\u91cd\u91cd\u4e00\u987f\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u4ee5\u4e00\u4e2a\u5c0f\u8f88\uff0c\u7adf\u6562\u95ed\u95e8\u4e0d\u7eb3\uff0c \u8fd9\u5c82\u662f\u5f85\u5ba2\u4e4b\u9063\uff1f\u54b1\u4eec\u6253\u8fdb\u53bb\u5411\u7384\u673a\u8001\u5934\u513f\u95ee\u4e2a\u660e\u767d\u3002\u201d\u5c60\u9f99\u5c0a\u8005\u63a5\u58f0\u53eb\u9053\uff1a\u201c\u5bf9\u554a\uff0c\u8d64\u971e \u9053\u53cb\uff0c\u54b1\u4eec\u672c\u6765\u662f\u8bf4\u597d\u627e\u7384\u673a\u9038\u58eb\u7684\uff0c\u4f60\u4f55\u82e6\u4e0e\u5c0f\u8f88\u7ea0\u7f20\uff1f\u8981\u77e5\u8d64\u971e\u9053\u4eba\u662f\u4e00\u6d3e\u5b97\u5e08\u7684\u8eab \u4efd\uff0c\u4e0e\u5f20\u4e39\u67ab\u6218\u4e2a\u5e73\u624b\uff0c\u5df2\u662f\u9762\u4e0a\u65e0\u5149\uff0c\u82e5\u6709\u95ea\u5931\uff0c\u90a3\u66f4\u662f\u76db\u540d\u5c3d\u6298\u3002\u6545\u6b64\u4e00\u4f17\u9b54\u5934\uff0c\u5927\u547c \u5c0f\u53eb\uff0c\u8981\u6253\u8fdb\u53bb\uff0c\u4e00\u6765\u662f\u4e3a\u89e3\u8d64\u971e\u9053\u4eba\u4e4b\u7a98\uff1b\u4e8c\u6765\u662f\u60f3\u6043\u591a\u4e3a\u80dc\uff0c\u4e0d\u5206\u7682\u767d\uff0c\u5148\u95f9\u4ed6\u4e2a\u4e0d\u4ea6 \u4e50\u4e4e\u3002 \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8bf4\u7684\u4e0d\u7b97\u8bdd\u4e48\uff1f\u7384\u673a\u524d\u8f88\u95ed\u5173\u9759\u5750\uff0c\u5148\u5934\u5df2\u8bf4\u5f97\u4e00\u6e05\u4e8c \u695a\uff0c\u4f60\u4eec\u64c5\u95ef\u4ed6\u9759\u4fee\u4e4b\u5730\uff0c\u60f3\u6210\u5fc3\u6b3a\u4fae\u4eba\u4e48\uff1f\u201d\u9e20\u76d8\u5a46\u4ef0\u5929\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u6210\u5fc3\u6b3a \u8d1f\u4f60\uff01\u201d\u94c1\u62d0\u4e00\u626b\uff0c\u4e0e\u9ed1\u767d\u6469\u8bc3\u7684\u53cc\u6756\u6597\u5728\u4e00\u8d77\uff0c\u5c60\u9f99\u5c0a\u8005\u5931\u4e86\u81c2\uff0c\u4ecd\u7136\u594b\u52c7\u5411\u524d\uff0c\u4e00\u5200\u5288 \u53bb\uff0c\u5374\u88ab\u91d1\u94a7\u4ed9\u5b50\u6797\u4ed9\u97f5\u6321\u7740\uff0c\u4e4c\u8499\u592b\u62a2\u4e0a\u53bb\u536b\u62a4\u7231\u59bb\uff0c\u8054\u6307\u4e00\u70b9\uff0c\u5ffd\u89c9\u638c\u98ce\u63a0\u9762\uff0c\u4e4c\u8499\u592b \u7684\u4e00\u6307\u7985\u529f\u7adf\u7136\u53d7\u5230\u6781\u5927\u53cd\u51fb\uff0c\u53cc\u65b9\u6328\u4e86\u4e00\u62db\uff0c\u5f7c\u6b64\u52bf\u5747\u529b\u654c\uff0c\u7741\u773c\u770b\u65f6\uff0c\u539f\u6765\u5374\u662f\u6606\u4ed1\u5c71 \u661f\u5bbf\u6d77\u7684\u6458\u661f\u4e0a\u4eba\uff0c\u4ed6\u7684\u6458\u661f\u624b\u4ea6\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u4e4c\u8499\u592b\u7684\u4e00\u6307\u7985\u529f\u53ea\u80fd\u5c06\u4ed6\u6321 \u4f4f\uff0c\u5374\u514b\u4e0d\u4f4f\u4ed6\u3002 \u3000\u3000\u8f6c\u773c\u4e4b\u95f4\uff0c\u53cc\u65b9\u5df2\u6210\u6df7\u6218\u4e4b\u5c40\uff0c\u4f17\u9b54\u5934\u4e00\u62e5\u800c\u4e0a\uff0c\u9ed1\u767d\u6469\u8bc3\u4e0e\u4e4c\u8499\u5927\u592b\u7ad9\u8fb9\u6218\u8fb9\u9000\uff0c\u5835 \u622a\u4e0d\u4f4f\uff0c\u5f20\u4e39\u67ab\u60f3\u8d77\u5e08\u7956\u95ed\u5173\u672a\u51fa\uff0c\u4e91\u91cd\u9759\u5ba4\u7597\u4f24\uff0c\u90fd\u4e07\u4e07\u4e0d\u80fd\u7ed9\u4eba\u6253\u6405\uff0c\u5fc3\u4e2d\u7126\u6025\uff0c\u6b63\u60f3 \u4e0e\u8d64\u971e\u9053\u4eba\u6253\u8bdd\uff0c\u8d64\u971e\u9053\u4eba\u5ffd\u5730\u4e00\u58f0\u957f\u5578\uff0c\u94c1\u5251\u4e00\u6d51\uff0c\u7fbd\u6247\u4e00\u683c\uff0c\u4ee5\u8fdb\u4e3a\u9000\uff0c\u51b2\u51fa\u4e86\u5f20\u4e39\u65ad \u7684\u5251\u5708\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u4f60\u4e0d\u7ed9\u6211\u5f15\u89c1\uff0c\u6211\u81ea\u884c\u53bb\u62dc\u8bbf\u4f60\u7684\u5e08\u7956\u4fbf\u662f\uff0c\u5c11\u966a\u5566\uff01\u201d\u5f20\u4e39 \u67ab\u6599\u4e0d\u5230\u8d64\u971e\u9053\u4eba\u4e5f\u800d\u65e0\u8d56\uff0c\u51b7\u7b11\u559d\u9053\uff1a\u201c\u7384\u673a\u5251\u6cd5\u4f60\u5df2\u89c1\u8bbd\u8fc7\u4e86\uff0c\u8fd8\u627e\u6211\u7684\u5e08\u7956\u505a\u4ec0 \u4e48\uff1f\u201d\u8fd9\u610f\u601d\u662f\u8bf4\uff1a\u201c\u4f60\u8fde\u6211\u4e5f\u6253\u4e0d\u8fc7\uff0c\u600e\u914d\u53bb\u627e\u6211\u7684\u5e08\u7956\uff01\u201d\u8d64\u971e\u9053\u4eba\u9762\u4e0a\u4e00\u7ea2\uff0c\u53cd\u624b\u4e00 \u5251\uff0c\u6321\u5f00\u5f20\u4e39\u67ab\u7684\u5251\u62db\uff0c\u4ecd\u7136\u662f\u5f80\u524d\u786c\u95ef\u3002\u5f20\u4e39\u67ab\u4e0e\u8d64\u971e\u9053\u4eba\u4e5f\u662f\u534a\u65a4\u516b\u4e24\uff0c\u5f7c\u6b64\u90fd\u4e0d\u80fd\u53d6 \u80dc\uff0c\u8c01\u4e5f\u62e6\u4e0d\u4e86\u8c01\u3002\u5f20\u4e39\u67ab\u8fd9\u8fb9\u5c11\u4e86\u6fb9\u53f0\u706d\u660e\u548c\u4e91\u91cd\u592b\u5987\u4e09\u628a\u597d\u624b\uff09\u4f17\u5be1\u4e0d\u6556\uff0c\u5728\u4eba\u529b\u4e0a\u5148 \u5403\u4e86\u4e8f\u3002 \u3000\u3000\u8d64\u971e\u9053\u4eba\u7387\u9886\u4f17\u9b54\u5934\u5f3a\u653b\u731b\u6253\uff1b\u8f6c\u773c\u4e4b\u95f4\uff0c\u653b\u5230\u5927\u95e8\uff0c\u4e91\u857e\u5c06\u5a74\u5b69\u4ea4\u7ed9\u6c90\u71d5\uff0c\u9053\uff1a\u201c\u4f60 \u59d0\u5f1f\u4e8c\u4eba\u8fdb\u53bb\u6682\u907f\u4e00\u65f6\u3002\u201d\u6c90\u71d5\u62b1\u7740\u5a74\u5b69\u770b\u4e86\u94c1\u955c\u5fc3\u4e00\u773c\uff0c\u94c1\u955c\u5fc3\u6b63\u81ea\u508d\u7740\u4e8e\u627f\u73e0\uff0c\u67d4\u58f0\u8bf4 \u9053\uff1a\u201c\u73e0\u59b9\uff0c\u4f60\u53d7\u4e86\u4f24\uff0c\u654c\u4eba\u52bf\u76db\uff0c\u4f60\u4e0d\u5b9c\u518d\u6218\uff0c\u4e5f\u907f\u4e00\u907f\u5427\u3002\u201d\u4e8e\u627f\u73e0\u597d\u4f3c\u6ca1\u6709\u542c\u5230\u4ed6\u7684 \u8bf4\u8bdd\uff0c\u5168\u795e\u8d2f\u6ce8\uff0c\u7ad9\u5230\u5979\u5e08\u6bcd\u8eab\u65c1\u3002\u53ea\u89c1\u4e91\u857e\u4e00\u58f0\u5a07\u53f1\uff0c\u5f39\u6307\u4e4b\u95f4\uff0c\u53d1\u51fa\u4e09\u6735\u91d1\u82b1\uff0c\u5c60\u9f99\u5c0a \u8005\u6b63\u81ea\u4e00\u5200\u5288\u5411\u6797\u4ed9\u97f5\u7684\u80a9\u5934\uff0c\u201c\u55d6\u201d\u7684\u4e00\u58f0\uff0c\u88ab\u91d1\u82b1\u78b0\u4e2a\u6b63\u7740\uff0c\u5c60\u9f99\u5200\u53cd\u5f39\u98de\u8d77\uff1b\u6797\u4ed9\u97f5 \u987a\u624b\u4e00\u52fe\uff0c\u5728\u5c60\u9f99\u5c0a\u8005\u7684\u72ec\u81c2\u4e0a\u5212\u4e86\u4e00\u9053\u957f\u957f\u7684\u53e3\u5b50\u3002\u4e91\u857e\u7684\u7b2c\u4e00\u6735\u91d1\u82b1\u5c04\u5411\u6458\u661f\u4e0a\u4eba\uff0c\u6458 \u661f\u4e0a\u4eba\u624b\u638c\u4e00\u7ffb\uff0c\u610f\u6b32\u5356\u5f04\u6458\u661f\u624b\u63a5\u6697\u5668\u7684\u529f\u592b\uff0c\u5ffd\u542c\u5f97\u90a3\u6697\u5668\u5636\u98ce\u4e4b\u58f0\uff0c\u5fc3\u5934\u4e00\u9707\uff0c\u6025\u5c06 \u9634\u638c\u6539\u4e3a\u9633\u638c\uff0c\u638c\u5fc3\u5f80\u5916\u4e00\u7ffb\uff0c\u7528\u5288\u7a7a\u638c\u7684\u5c0f\u5929\u661f\u638c\u529b\u5f3a\u628a\u91d1\u82b1\u6253\u843d\uff0c\u53ea\u89c1\u90a3\u91d1\u82b1\u88ab\u638c\u98ce\u4e00 \u6f88\uff0c\u545c\u545c\u602a\u5578\uff0c\u9661\u7684\u6539\u5411\u659c\u98de\uff0c\u76d8\u5929\u7f57\u51b7\u4e0d\u53ca\u9632\uff0c\u7ed9\u91d1\u82b1\u5c04\u4e2d\u7a74\u9053\uff0c\u767b\u65f6\u683d\u5012\u3002\u6458\u661f\u4e0a\u4eba\u5413 \u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u6697\u81ea\u5e86\u5e78\u9002\u624d\u6ca1\u6709\u7528\u624b\u53bb\u63a5\u3002\u4e91\u857e\u7b2c\u4e09\u6735\u91d1\u82b1\u6253\u8d64\u971e\u9053\u4eba\uff0c\u8d64\u971e\u9053\u4eba\u5374\u662f\u4e0d\u614c \u4e0d\u5fd9\uff0c\u5f85\u5f97\u90a3\u91d1\u82b1\u98de\u5230\u9762\u524d\uff0c\u8fd0\u8db3\u771f\u529b\uff0c\u4e3e\u5251\u4e00\u5288\uff0c\u5c06\u91d1\u82b1\u5288\u4e3a\u4e24\u8fb9\uff0c\u9976\u662f\u5982\u6b64\uff0c\u94c1\u5251\u4e0a\u4e5f \u6fc0\u8d77\u70b9\u70b9\u706b\u661f\uff0c\u4ee4\u8d64\u971e\u9053\u4eba\u4e5f\u7981\u4e0d\u4f4f\u5fc3\u5934\u5fae\u602f\u3002\u4e8e\u627f\u73e0\u770b\u5e08\u7236\u53d1\u7684\u4e09\u6735\u91d1\u82b1\uff0c\u770b\u4f3c\u540c\u65f6\u53d1 \u51fa\uff0c\u5176\u5b9e\u5374\u5206\u4e3a\u4e09\u79cd\u4e0d\u540c\u624b\u6cd5\uff0c\u5206\u5411\u4e09\u5904\u4e0d\u540c\u7684\u65b9\u5411\uff0c\u88ad\u51fb\u4e09\u4e2a\u5f3a\u654c\uff0c\u529f\u529b\u4e4b\u6df1\uff0c\u624b\u6cd5\u4e4b \u5999\uff0c\u76f4\u628a\u4e8e\u627f\u73e0\u770b\u5f97\u5446\u4e86\u3002 \u3000\u3000\u4e91\u857e\u8fde\u53d1\u4e09\u6735\u91d1\u82b1\uff0c\u867d\u7136\u53ea\u6253\u4f24\u4e86\u76d8\u5929\u7f57\u4e00\u4eba\uff0c\u654c\u4eba\u7684\u653b\u52bf\u5374\u56e0\u6b64\u7a0d\u7a0d\u53d7\u632b\u3002\u4e8e\u627f\u73e0\u6280 \u75d2\u96be\u71ac\uff0c\u7d27\u63a5\u7740\u4e5f\u53d1\u51fa\u4e00\u628a\u91d1\u82b1\uff0c\u5979\u7528\u7684\u662f\u81ea\u5df1\u5728\u770b\u4e86\u963f\u8428\u739b\u5144\u5f1f\u4e0e\u9ed1\u767d\u6469\u8bc3\u4e4b\u6218\u540e\u6240\u89c9\u609f \u51fa\u6765\u7684\u624b\u6cd5\uff0c\u5341\u4e8c\u6735\u91d1\u82b1\u6ee1\u5929\u98de\u821e\uff0c\u6709\u7684\u659c\u98de\uff0c\u6709\u7684\u76f4\u5c04\uff0c\u770b\u4f3c\u6742\u4e71\u65e0\u7ae0\uff0c\u4f46\u6bcf\u4e00\u6735\u91d1\u82b1\uff0c \u90fd\u662f\u6253\u5411\u654c\u4eba\u7684\u7a74\u9053\uff0c\u53ef\u60dc\u5979\u7684\u529f\u529b\u5c1a\u6d45\uff0c\u5341\u4e8c\u6735\u91d1\u82b1\uff0c\u6709\u4e00\u534a\u88ab\u8d64\u971e\u9053\u4eba\u7528\u7fbd\u6247\u6251\u843d\uff0c\u6709 \u4e00\u534a\u88ab\u9e20\u76d8\u5a46\u7528\u94c1\u62d0\u9707\u5f97\u7c89\u788e\uff0c\u4f46\u867d\u7136\u4f24\u4e0d\u4e86\u654c\u4eba\uff0c\u5374\u4e5f\u628a\u654c\u4eba\u7684\u9635\u5f62\u6253\u4e71\u3002\u4e91\u857e\u53c8\u60ca\u53c8 \u559c\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u7684\u6697\u5668\u529f\u592b\uff0c\u4e0d\u7528\u6211\u518d\u8f83\u8003\u4f60\u4e86\uff01\u201d \u3000\u3000\u4f46\u91d1\u82b1\u6697\u5668\uff0c\u53ea\u80fd\u963b\u654c\u4e8e\u4e00\u65f6\uff0c\u8d64\u971e\u9053\u4eba\u4e0e\u9e20\u76d8\u5a46\u6325\u6247\u821e\u62d0\uff0c\u63a9\u62a4\u7740\u4f17\u9b54\u5934\u518d\u5411\u524d\u5f3a \u653b\uff0c\u77ac\u5373\u51b2\u5230\u4e91\u857e\u8ddf\u524d\uff0c\u6c90\u71d5\u62b1\u4e86\u5a74\u5b69\u548c\u6c90\u78f7\u5148\u9000\u5165\u5c4b\u5185\uff0c\u94c1\u955c\u5fc3\u770b\u4e86\u4e8e\u627f\u73e0\u4e00\u773c\uff0c\u6b63\u60f3\u8bf4 \u8bdd\uff0c\u4e8e\u627f\u73e0\u7389\u624b\u4e00\u4f38\uff0c\u5ffd\u5c06\u94c1\u955c\u5fc3\u7684\u9752\u94a2\u5251\u62a2\u4e86\u8fc7\u6765\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4fdd\u62a4\u6c90\u5c0f\u59d0\u53bb\u5427\u3002\u4f60 \u7684\u5251\u6682\u501f\u4e00\u7528\u3002\u201d\u94c1\u955c\u5fc3\u6014\u4e86\u4e00\u6014\uff0c\u9e20\u76d8\u5a46\u5df2\u662f\u4e00\u62d0\u626b\u6765\uff0c\u6756\u98ce\u547c\u547c\uff0c\u5c06\u94c1\u955c\u5fc3\u903c\u5f97\u5012\u9000\u51e0 \u6b65\uff0c\u731b\u7136\u95f4\u53c8\u542c\u5f97\u4e00\u9635\u65ad\u91d1\u4eae\u7389\u4e4b\u58f0\uff0c\u539f\u6765\u662f\u8d64\u971e\u9053\u4eba\u7684\u94c1\u5251\u5288\u5230\uff0c\u5f20\u4e39\u67ab\u98de\u8eab\u63a0\u8d77\uff0c\u53cc\u5251 \u6025\u62e6\uff0c\u4e8e\u627f\u73e0\u633d\u8d77\u4e86\u4e00\u6735\u5251\u82b1\uff0c\u4e5f\u6b63\u5728\u5411\u8d64\u971e\u9053\u4eba\u80f8\u53e3\u75be\u523a\uff0c\u94c1\u955c\u5fc3\u5927\u60ca\u5931\u8272\uff0c\u4f46\u542c\u5f97\u4e00\u9635 \u5251\u51fb\u4e4b\u58f0\u75be\u8fc7\uff0c\u8d64\u971e\u9053\u4eba\u8dc4\u8dc4\u8e09\u8e09\u5411\u540e\u5012\u9000\u51e0\u6b65\u3002 \u3000\u3000\u539f\u6765\u7384\u673a\u9038\u58eb\u6240\u521b\u7684\u53cc\u5251\u5408\u74a7\u4e4b\u672f\uff0c\u795e\u5999\u975e\u5e38\uff0c\u4e8e\u627f\u73e0\u8fd9\u5251\u5f53\u4e2d\u75be\u523a\uff0c\u521a\u597d\u4e0e\u5f20\u4e39\u67ab\u7684 \u5251\u62db\u914d\u5408\uff0c\u8bba\u529f\u529b\uff0c\u8d64\u971e\u9053\u4eba\u539f\u53ef\u4ee5\u9707\u65ad\u4e8e\u627f\u73e0\u7684\u9752\u94a2\u5251\uff0c\u4f46\u82e6\u7136\u5982\u6b64\uff0c\u8d64\u971e\u9053\u4eba\u7684\u4e24\u80c1\u5c31 \u5f97\u7ed9\u5f20\u4e39\u67ab\u523a\u4e2a\u900f\u660e\u7a9f\u7abf\uff0c\u82e5\u7136\u4e13\u95e8\u5bf9\u4ed8\u5f20\u4e39\u67ab\uff0c\u80f8\u53e3\u7684\u7487\u7391\u7a74\u53c8\u5728\u4e8e\u627f\u73e0\u7684\u5251\u5c16\u5a01\u80c1\u4e4b \u4e0b\uff0c\u8fd9\u7487\u7391\u7a74\u662f\u4eba\u8eab\u7684\u6b7b\u7a74\u4e4b\u4e00\uff0c\u4efb\u8d64\u971e\u9053\u4eba\u6b66\u529f\u591a\u5f3a\uff0c\u4e5f\u4e0d\u80fd\u7f6e\u4e4b\u4e0d\u7406\u3002\u662f\u4ee5\u8d64\u971e\u9053\u4eba\u6025 \u5fd9\u8eb2\u907f\u3002 \u3000\u3000\u4e91\u857e\u5fae\u5fae\u4e00\u7b11\uff0c\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u53cc\u6307\u8fde\u5f39\uff0c\u94ee\u94ee\u6570\u58f0\uff0c\u91d1\u82b1\u518d\u53d1\u3002\u8fd9\u4e00\u6765\uff0c\u4f17\u9b54\u5934 \u4e2d\u6b66\u529f\u6700\u5f3a\u7684\u8d64\u971e\u9053\u4eba\u88ab\u5f20\u4e39\u67ab\u5e08\u5f99\u963b\u4f4f\uff0c\u9e20\u76d8\u5a46\u3001\u6458\u661f\u4e0a\u4eba\u7b49\u867d\u7136\u594b\u52c7\u4e89\u5148\uff0c\u5374\u53c8\u5728\u4e91\u857e \u7684\u91d1\u82b1\u5a01\u80c1\u4e4b\u4e0b\uff0c\u653b\u52bf\u987f\u7136\u53d7\u632b\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u53cc\u5251\u4e00\u4e2a\u76d8\u65cb\uff0c\u5c06\u8d64\u971e\u9053\u4eba\u518d\u903c\u9000\u4e09\u6b65\uff0c\u53c8\u7ed9\u6797\u4ed9\u97f5\u89e3\u4e86\u9e20\u76d8\u5a46\u7684\u4e00\u8bb0\u5389\u5bb3\u6740 \u624b\uff0c\u6717\u58f0\u541f\u9053\uff1a\u201c\u5fcd\u89c1\u540d\u5c71\u817e\u6740\u6c14\uff0c\u4e14\u770b\u5b9d\u5251\u9000\u7fa4\u9b54\uff0c\u8d64\u971e\u9053\u957f\uff0c\u4f60\u518d\u4e0d\u77e5\u8fdb\u9000\uff0c\u6211\u53ef\u8981\u4e0d \u5ba2\u6c14\u4e86\uff01\u201d\u8d64\u971e\u9053\u4eba\u9a91\u864e\u96be\u4e0b\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u597d\uff0c\u770b\u4f60\u80fd\u4e0d\u80fd\u6321\u6211\u5165\u5185\uff01\u201d\u7fbd\u6247\u4e00\u6447\uff0c\u4f17\u9b54 \u5934\u7ed3\u6210\u4e86\u4e00\u5b57\u957f\u86c7\u9635\uff0c\u7531\u9e20\u76d8\u5a46\u94c1\u62d0\u5f00\u8def\uff0c\u5c60\u9f99\u5c0a\u8005\u4e0e\u6458\u661f\u4e0a\u4eba\u5de6\u53f3\u5939\u653b\uff0c\u8d64\u971e\u9053\u4eba\u81ea\u5df1\u5f53 \u4e2d\u7b56\u5e94\uff0c\u96c6\u4f17\u9b54\u5934\u4e4b\u529b\uff0c\u5f3a\u51b2\u731b\u653b\uff0c\u5c45\u7136\u7ed9\u4ed6\u4eec\u51b2\u51fa\u4e86\u4e24\u4e09\u4e08\u8def\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u563f\u563f\u51b7\u7b11\uff0c\u957f\u5251\u4e00\u6307\uff0c\u6b63\u60f3\u53d8\u9635\u53cd\u51fb\u3002\u5ffd\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u5b9b\u82e5\u9f99\u541f\uff0c\u4e91\u857e\u559c\u9053\uff1a \u201c\u5e08\u7236\u5230\u5566\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u4e00\u7537\u4e00\u5973\uff0c\u5982\u98de\u75be\u81f3\uff0c\u8eab\u6cd5\u5feb\u6377\uff0c\u7f8e\u5999\u65e0\u4f26\uff0c\u6b63\u662f\u5f20\u4e39\u67ab\u548c \u4e91\u857e\u7684\u5e08\u7236\uff1a\u8c22\u5929\u534e\u548c\u53f6\u76c8\u76c8\u3002 \u3000\u3000\u8c22\u5929\u534e\u957f\u5251\u51fa\u9798\uff0c\u8fce\u98ce\u4e00\u6643\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u6562\u5230\u82cd\u5c71\u80e1\u95f9\uff0c\u90fd\u7ed9\u6211\u6eda\u4e0b\u53bb\u3002\u201d\u8fd9 \u4e00\u559d\u58f0\u97f3\u5e76\u4e0d\u5f88\u5927\uff0c\u5374\u9707\u5f97\u4f17\u9b54\u5934\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\u3002\u5c60\u9f99\u5c0a\u8005\u548c\u6458\u661f\u4e0a\u4eba\u4e0d\u77e5\u9053\u8c22\u5929\u534e\u548c\u53f6\u76c8 \u76c8\u662f\u8c01\uff0c\u5140\u81ea\u4e0d\u77e5\u5389\u5bb3\uff0c\u51b7\u7b11\u559d\u9053\uff1a\u201c\u597d\u5927\u7684\u67b6\u5b50\uff0c\u4f60\u6709\u4ec0\u4e48\u672c\u4e8b\u6562\u53eb\u6211\u7b49\u4e0b\u5c71\uff1f\u201d\u4e00\u4e2a\u4e00 \u5200\uff0c\u4e00\u4e2a\u4e00\u638c\uff0c\u5939\u653b\u8c22\u5929\u534e\u3002\u8c22\u5929\u534e\u7684\u59bb\u5b50\u53f6\u76c8\u76c8\u5916\u53f7\u201c\u98de\u5929\u9f99\u5973\u201d\uff0c\u8f7b\u529f\u4f73\u5999\uff0c\u6b66\u6797\u65e0 \u53cc\uff0c\u89c1\u8fd9\u4e24\u4eba\u5939\u653b\u4e08\u592b\uff0c\u968f\u624b\u4e00\u5251\uff0c\u540e\u53d1\u5148\u81f3\uff0c\u5c60\u9f99\u5c0a\u8005\u6b63\u81ea\u51dd\u795e\u6ce8\u89c6\u8c22\u5929\u534e\u7684\u5251\u67c4\uff0c\u4e0d\u6599 \u53f6\u76c8\u76c8\u7684\u5251\u62db\u6765\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c\u7f29\u624b\u4e0d\u53ca\uff0c\u88ab\u53f6\u76c8\u76c8\u4e00\u5251\u523a\u4e2d\u624b\u8155\uff0c\u5c60\u9f99\u5200\u8131\u624b\u98de\u53bb\uff0c\u6458\u661f\u4e0a \u4eba\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u8c22\u5929\u534e\u7684\u5251\u62db\u53c8\u5230\uff0c\u53cc\u5251\u4e00\u5408\uff0c\u5251\u5149\u66b4\u957f\uff0c \u6458\u661f\u4e0a\u4eba\u548c\u5c60\u9f99\u5c0a\u8005\u4f46\u89c9\u5934\u9876\u4e00\u7247\u6c81\u51c9\uff0c\u614c\u5fd9\u540e\u8dc3\uff0c\u4f38\u624b\u4e00\u6478\uff0c\u5934\u53d1\u7adf\u5df2\u88ab\u524a\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u9e20\u76d8\u5a46\u6025\u5fd9\u6765\u6551\uff0c\u53cd\u624b\u4e00\u62d0\uff0c\u53ea\u89c1\u53c8\u6709\u4e00\u4e2a\u80d6\u548c\u5c1a\u98de\u5954\u800c\u81f3\uff0c\u731b\u5730\u5f20\u5f00\u5589\u5499\uff0c\u9739\u96f3\u4e00\u58f0 \u5927\u559d\uff0c\u201c\u5403\u6d12\u5bb6\u4e00\u6756\uff01\u201d\u8fd9\u4eba\u6b63\u662f\u7384\u673a\u9038\u58eb\u7684\u7b2c\u4e8c\u4e2a\u5f92\u5f1f\u6f6e\u97f3\u548c\u5c1a\uff0c\u4ed6\u7684\u5916\u5bb6\u529f\u592b\u767b\u5cf0\u9020 \u6781\uff0c\u8fd9\u4e00\u6756\u6709\u5343\u65a4\u4e4b\u529b\uff0c\u4e0e\u9e20\u76d8\u5a46\u6b63\u597d\u529f\u529b\u6089\u654c\uff0c\u53cc\u6756\u4e00\u4ea4\uff0c\u6709\u5982\u5927\u9524\u51fb\u949f\uff0c\u201c\u8f70\u201d\u7684\u4e00\u58f0 \u5de8\u54cd\uff0c\u4e24\u6839\u6756\u90fd\u5f53\u4e2d\u65ad\u4e86\uff0c\u6f6e\u97f3\u548c\u5c1a\u6298\u4e86\u5f39\u6756\u8fd8\u5e76\u4e0d\u600e\u4e48\uff0c\u9e20\u76d8\u5a46\u8fd9\u6839\u62d0\u6756\uff0c\u5374\u662f\u54c0\u7262\u5c71\u7684 \u9f99\u8840\u6811\u6240\u5236\uff0c\u6781\u4e3a\u96be\u5f97\uff0c\u88ab\u4ed6\u4e00\u6756\u6253\u65ad\uff0c\u5fc3\u75db\u5982\u523a\u3002 \u3000\u3000\u9e20\u76d8\u5a46\u76f8\u8c8c\u5947\u7279\uff0c\u8c22\u5929\u534e\u867d\u7591\u4e0d\u8ba4\u8bc6\u5979\uff0c\u5374\u542c\u8fc7\u6b66\u6797\u524d\u8f88\u9053\u8d77\uff0c\u89c1\u5979\u8fd0\u795e\u529b\u9707\u6f6e\u97f3\u548c\u5c1a \u7684\u94c1\u62d0\uff0c\u5251\u7709\u4e00\u7ad6\uff0c\u6717\u58f0\u9053\uff1a\u201c\u516c\u5b59\u524d\u8f88\uff0c\u4f60\u4e0d\u5728\u54c0\u7262\u5c71\u4e2d\u9759\u4fee\uff0c\u5374\u4e0e\u8fd9\u73ed\u5996\u4eba\u5230\u82cd\u5c71\u80e1 \u95f9\uff0c\u610f\u6b32\u4f55\u4e3a\uff1f\u201d\u9e20\u76d8\u5a46\u6b63\u81ea\u6ee1\u8154\u6012\u6c14\uff0c\u5389\u58f0\u53eb\u9053\uff1a\u201c\u4eca\u65e5\u6211\u4e0e\u4f60\u4eec\u8fd9\u73ed\u5c0f\u8f88\u62fc\u4e86\uff01\u201d\u4e3e\u8d77 \u534a\u622a\u62d0\u6756\uff0c\u4e00\u62db\u201c\u6392\u4e91\u9a76\u7535\u201d\uff0c\u6756\u5934\u90a3\u5c16\u957f\u7684\u9e20\u5634\uff0c\u95ea\u7f29\u4e0d\u5b9a\uff0c\u5206\u88ad\u8c22\u5929\u534e\u4e0e\u53f6\u76c8\u76c8\u4e8c\u4eba\uff0c \u8fd9\u4e00\u62db\u662f\u9e20\u76d8\u5a46\u62fc\u4e86\u6b7b\u547d\u7684\u8fa3\u8fd8\uff0c\u62db\u6570\u602a\u5f02\uff0c\u52bf\u4f3c\u96f7\u9706\uff0c\u6f6e\u97f3\u548c\u5c1a\u4e5f\u4e0d\u89c9\u5413\u4e86\u4e00\u8df3\u3002\u8bf4\u65f6 \u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u542c\u5f97\u8c22\u5929\u534e\u51b7\u7b11\u9053\uff1a\u201c\u5ff5\u4f60\u5e74\u8fc8\u7cca\u6d82\uff0c\u653e\u4f60\u56de\u53bb\u5427\u3002\u201d\u8bdd\u8bed\u65b9\u51fa\uff0c\u53cc\u5251\u4e00 \u5408\uff0c\u5251\u5149\u66b4\u957f\uff0c\u5530\u5530\u4e24\u58f0\uff0c\u9e20\u76d8\u5a46\u7684\u5de6\u53f3\u811a\u8e1d\uff0c\u4e00\u8fb9\u4e2d\u4e86\u4e00\u5251\uff0c\u90a3\u534a\u622a\u94c1\u62d0\u817e\u7a7a\u98de\u8d77\uff0c\u62d0\u4e0a \u7684\u9e20\u5634\u4e5f\u88ab\u524a\u5e73\uff0c\u9e20\u76d8\u5a46\u88ab\u5251\u98ce\u4e00\u8361\uff0c\u5012\u7eb5\u51fa\u6570\u4e08\u4e4b\u5916\uff0c\u843d\u4e0b\u4e4b\u65f6\uff0c\u5df2\u5728\u5c71\u5761\uff0c\u542d\u4e5f\u4e0d\u6562\u518d \u542d\u4e00\u58f0\uff0c\u4e00\u8df7\u4e00\u62d0\u5730\u8d70\u4e86\u3002\u8fd9\u8fd8\u662f\u8c22\u5929\u534e\u624b\u4e0b\u7559\u60c5\uff0c\u8981\u4e0d\u7136\u9e20\u76d8\u5a46\u6298\u4e86\u62d0\u6756\uff0c\u7109\u80fd\u5728\u53cc\u5251\u5408 \u58c1\u4e4b\u4e0b\uff0c\u9003\u51fa\u6027\u547d\u3002 \u3000\u3000\u4f17\u9b54\u5934\u4e2a\u4e2a\u53d7\u4f24\uff0c\u7eb7\u7eb7\u9003\u8d70\uff0c\u5269\u4e0b\u7684\u5c31\u53ea\u6709\u8d64\u971e\u9053\u4eba\u4e00\u4eba\uff0c\u4ed6\u4ee5\u4e00\u6d3e\u5b97\u5e08\u7684\u8eab\u4efd\uff0c\u4ece\u672a \u66fe\u8bd5\u8fc7\u4f3c\u4eca\u65e5\u7684\u63ba\u8d25\uff0c\u4e0b\u4e0d\u4e86\u53f0\uff0c\u4e5f\u62fc\u4e86\u6027\u547d\uff0c\u5c31\u5728\u9e20\u76d8\u5a46\u843d\u8352\u800c\u9003\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u7fbd\u6247\u4e00 \u6325\uff0c\u94c1\u5251\u6a2a\u6321\uff0c\u5de6\u523a\u53f6\u76c8\u76c8\uff0c\u53f3\u6251\u8c22\u5929\u534e\uff0c\u8c22\u3001\u53f6\u4e24\u4eba\u51fa\u5251\u53c2\u5dee\uff0c\u5c1a\u672a\u5408\u58c1\uff0c\u88ab\u4ed6\u7684\u7fbd\u6247\u9694 \u5f00\uff0c\u94c1\u5251\u4e00\u5708\uff0c\u4e09\u4eba\u90fd\u6643\u4e86\u4e00\u6643\u3002\u8c22\u5929\u534e\u5fc3\u5934\u4e00\u9707\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u201d\u8d64\u971e\u9053\u4eba\u52bf\u4f3c\u75af \u864e\uff0c\u6247\u5251\u8fde\u6325\uff0c\u75be\u6251\u6570\u62db\uff0c\u5f20\u4e39\u67ab\u5728\u65c1\u8bf4\u9053\uff1a\u201c\u7980\u5e08\u5c0a\uff0c\u8fd9\u4f4d\u662f\u8d64\u971e\u9053\u957f\uff01\u201d\u8c22\u5929\u534e\u201c\u54e6\u201d \u4e86\u4e00\u58f0\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u90a3\u8d64\u971e\u9053\u4eba\u6740\u5f97\u5931\u4e86\u7406\u6027\uff0c\u653b\u52bf\u6709\u5982\u957f\u6c5f\u5927\u6cb3\uff0c\u6eda\u6eda\u800c\u4e0a\uff0c\u8c22\u5927\u534e\u7709\u5934 \u4e00\u76b1\uff0c\u9053\uff1a\u201c\u6b64\u4eba\u4e0d\u77e5\u597d\u6b79\uff0c\u76c8\u59b9\uff0c\u4e0d\u5fc5\u548c\u4ed6\u5ba2\u6c14\u3002\u201d\u53cc\u5251\u4e00\u5408\uff0c\u8d64\u971e\u9053\u4eba\u7684\u94c1\u5251\u8361\u5f00\uff0c\u8d64 \u971e\u9053\u4eba\u594b\u529b\u63a5\u4e86\u51e0\u62db\uff0c\u5ffd\u542c\u5f97\u201c\u55d6\u201d\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u8c22\u5929\u534e\u4e00\u5251\u683c\u5f00\u8d64\u971e\u9053\u4eba\u7684\u94c1\u6247\uff0c \u4f59\u52bf\u672a\u8870\uff0c\u5251\u950b\u987a\u624b\u62b9\u53bb\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u524a\u8fc7\u8d64\u971e\u9053\u4eba\u7684\u5934\u9876\uff0c\u5c06\u4ed6\u7684\u9053\u51a0\u5288\u4e3a\u4e24\u534a\uff0c\u8d64 \u971e\u9053\u4eba\u53cd\u624b\u4e00\u6247\uff0c\u53f6\u76c8\u76c8\u7684\u5251\u62db\u53c8\u5230\uff0c\u53ea\u542c\u5f97\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u7fbd\u6247\u7684\u4e24\u652f\u94a2\u9aa8\u53c8\u6298\u65ad\u4e86\u3002\u8d64 \u971e\u9053\u4eba\u5934\u7b4b\u66b4\u6da8\uff0c\u811a\u8e0f\u4e94\u884c\u516b\u5366\u65b9\u4f4d\uff0c\u82e6\u82e6\u7f20\u6597\u3002\u4f46\u53cc\u5251\u5408\u58c1\u7684\u5a01\u52bf\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u8d64\u971e\u9053\u4eba \u521a\u624d\u4e0e\u5f20\u4e39\u67ab\u5355\u6253\u72ec\u6597\uff0c\u5df2\u611f\u5e94\u4ed8\u8270\u96be\uff0c\u600e\u6321\u5f97\u4e86\u8c22\u5929\u534e\u548c\u53f6\u76c8\u76c8\u7684\u53cc\u5251\u8054\u653b\u3002 \u3000\u3000\u53ef\u662f\u8d64\u971e\u9053\u4eba\u51ed\u7740\u51e0\u5341\u5e74\u529f\u529b\uff0c\u53c8\u5f53\u72c2\u6012\u4e4b\u9645\uff0c\u6240\u4f7f\u7684\u7adf\u662f\u633a\u7740\u4e24\u8d25\u4ff1\u4f24\u7684\u5251\u6cd5\uff0c\u8c22\u3001 \u53f6\u4e8c\u4eba\u7b97\u6709\u5fc3\u9976\u4ed6\uff0c\u4e5f\u4e0d\u80fd\u7f13\u624b\u3002\u8fd9\u5f62\u52bf\u4e2a\u4e2a\u90fd\u770b\u5f97\u51fa\u6765\uff0c\u6700\u591a\u518d\u8fc7\u4e8c\u4e09\u5341\u62db\uff0c\u8d64\u971e\u9053\u4eba\u5fc5 \u7136\u6298\u5728\u53cc\u5251\u4e4b\u4e0b\uff0c\u4e0d\u6b7b\u4ea6\u4f24\uff0c\u53ef\u662f\u8c01\u4e5f\u6ca1\u6709\u8fd9\u4e2a\u80fd\u8010\u4e0a\u524d\u5206\u89e3\u3002\u5f20\u4e39\u67ab\u66f4\u662f\u7126\u6025\uff0c\u5fc3\u4e2d\u6012 \u9053\uff1a\u201c\u8d64\u971e\u9053\u4eba\u539f\u4e5f\u8be5\u53d7\u70b9\u6559\u8bad\uff0c\u53ef\u662f\u82e5\u91cd\u4f24\u4e86\u4ed6\uff0c\u4e24\u6d3e\u7684\u51a4\u4ec7\u53ef\u89e3\u4e0d\u5f00\u4e86\u3002\u201d\u4ed6\u7d20\u6765\u8db3\u667a \u591a\u8c0b\uff0c\u800c\u5bf9\u8fd9\u4e2a\u5c34\u5c2c\u4e4b\u5c40\uff0c\u4e00\u65f6\u95f4\u5374\u4e5f\u60f3\u4e0d\u51fa\u597d\u7684\u4e3b\u610f\u3002 \u3000\u3000\u53ea\u89c1\u573a\u4e2d\u8d8a\u6597\u8d8a\u70c8\uff0c\u8d64\u971e\u9053\u4eba\u5df2\u5728\u53cc\u5251\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u5140\u662f\u987d\u6297\u4e0d\u4f11\uff0c\u6bcf\u62db\u6bcf\u5f0f\uff0c\u90fd\u662f\u8c41\u51fa \u4e86\u6027\u547d\u62fc\u7740\u4e24\u8d25\u4ff1\u4f24\u7684\u6740\u624b\uff0c\u5f20\u4e39\u67ab\u8e0c\u8e87\u4e0d\u51b3\uff0c\u4ed6\u60f3\u51fa\u624b\u62c6\u5f00\uff0c\u4e00\u6765\u6015\u81ea\u5df1\u7684\u529f\u529b\u4e0d\u591f\uff0c\u5f04 \u5f97\u56db\u4e2a\u4eba\u90fd\u53d7\u4f24\uff1b\u4e8c\u6765\u4e5f\u6015\u72af\u4e86\u5e08\u7236\u7684\u5c0a\u4e25\uff0c\u4f46\u770b\u6b64\u5f62\u52bf\uff0c\u5e08\u7236\u4e5f\u7edd\u4e0d\u80fd\u7a0d\u8ba9\uff0c\u4e00\u8ba9\u4e5f\u5f97\u53d7 \u4f24\u3002 \u3000\u3000\u731b\u542c\u5f97\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u8d64\u971e\u9053\u4eba\u7684\u6247\u9aa8\u53c8\u65ad\u4e86\u4e24\u652f\uff0c\u8d64\u971e\u9053\u4eba\u94c1\u5251\u76d8\u65cb\uff0c\u7a81\u7136\u4e00\u62db\u201c\u540e \u7fcc\u5c04\u65e5\u201d\uff0c\u75be\u5241\u51fa\u53bb\uff0c\u8fd9\u4e00\u62db\u4e43\u662f\u4ed6\u6700\u540e\u7684\u6740\u624b\uff0c\u6b7b\u751f\u4e00\u63b7\uff0c\u8c22\u3001\u53f6\u4e24\u4eba\u88ab\u4ed6\u903c\u5f97\u9a91\u4e0a\u864e \u80cc\uff0c\u53cc\u5251\u4e00\u5408\uff0c\u5c06\u4ed6\u7684\u94c1\u5251\u5708\u5728\u5f53\u4e2d\uff0c\u76f4\u538b\u4e0b\u53bb\uff0c\u5f20\u4e39\u67ab\u201c\u5509\u5440\u201d\u4e00\u58f0\u53eb\u4e86\u8d77\u6765\uff0c\u773c\u89c1\u8d64\u971e \u9053\u4eba\u7684\u6027\u547d\u4fbf\u8981\u4e27\u751f\u503e\u523b\u4e4b\u95f4\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8d64\u971e\u9053\u53cb\uff0c\u591a\u8c22\u4f60\u6709\u5fc3\u63a2 \u671b\uff0c\u4e0d\u5fc5\u4e0e\u5c0f\u8f88\u4e89\u4e86\uff0c\u8001\u673d\u8c22\u7edd\u5c18\u7f18\uff0c\u5f97\u89c1\u6545\u4eba\u4e00\u9762\uff0c\u8bf8\u4e8b\u4ff1\u4e86\u3002\u795d\u8d35\u6d3e\u5174\u9686\uff0c\u66f4\u795d\u9053\u53cb\u52c9 \u529b\u4fee\u4e3a\uff0c\u5f97\u6210\u6b63\u679c\uff0c\u9053\u53cb\u8bf7\u56de\u5c71\u53bb\u5427\uff0c\u8001\u673d\u6055\u4e0d\u8fdc\u9001\u4e86\u3002\u201d\u968f\u7740\u8bdd\u58f0\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u8c22\u5929 \u534e\u3001\u53f6\u76c8\u76c8\u548c\u8d64\u971e\u9053\u4eba\u7684\u4e09\u67c4\u957f\u5251\u90fd\u8131\u624b\u98de\u51fa\u3002 \u3000\u3000\u4f17\u4eba\u4e0d\u7ea6\u800c\u540c\u5730\u5411\u7740\u53d1\u8bdd\u4e4b\u5904\u770b\u53bb\uff0c\u4f46\u89c1\u540e\u5c71\u7684\u90a3\u5ea7\u77f3\u5ba4\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u5df2\u6253\u5f00\u5927\u95e8\uff0c \u5728\u95e8\u524d\u7684\u8349\u5730\u4e0a\uff0c\u7384\u673a\u9038\u58eb\u76d8\u819d\u5750\u5728\u5f53\u4e2d\uff0c\u4e0a\u5b98\u5929\u91ce\u548c\u8427\u97f5\u5170\u4e00\u5de6\u4e00\u53f3\uff0c\u795e\u6001\u5e84\u4e25\uff0c\u4e25\u4f3c\u4e09 \u5c0a\u5f97\u9053\u7684\u83e9\u8428\u3002\u4f17\u4eba\u604d\u7136\u5927\u609f\uff0c\u521a\u624d\u90a3\u9897\u77f3\u5b50\u5b9a\u662f\u7384\u673a\u9038\u58eb\u53d1\u51fa\u6765\u7684\uff0c\u4e16\u4e0a\u9664\u4ed6\u4e4b\u5916\uff0c\u65e0\u4eba \u6709\u6b64\u529f\u529b\u3002 \u3000\u3000\u8d64\u971e\u9053\u4eba\u9762\u8272\u60e8\u767d\uff0c\u60f3\u8d77\u81ea\u5df1\u82e6\u7ec3\u4e86\u51e0\u5341\u5e74\uff0c\u4ecd\u662f\u672a\u8db3\u6321\u7384\u673a\u9038\u58eb\u7684\u4e00\u51fb\uff0c\u5f53\u4e0b\u62fe\u8d77\u94c1 \u5251\uff0c\u7a3d\u9996\u4e00\u62dc\uff0c\u9053\uff1a\u201c\u8c22\u5c45\u58eb\u6307\u70b9\u3002\u201d\u4ece\u6b64\u56de\u8f6c\u4e4c\u8499\u5c71\u4e2d\uff0c\u518d\u4e5f\u4e0d\u6562\u591a\u4e8b\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7b49\u89c1\u4e00\u573a\u6d69\u52a8\uff0c\u6d88\u5f2d\u4e8e\u65e0\u5f62\uff0c\u7956\u5e08\u53c8\u63d0\u65e9\u5f00\u5173\uff0c\u4ff1\u90fd\u5927\u559c\u3002\u8c22\u5929\u534e\u7387\u4f17\u4eba\u4e0a\u524d\u53e9 \u89c1\uff0c\u4e8e\u627f\u73e0\u6392\u5728\u6700\u540e\uff0c\u4e5f\u62dc\u89c1\u592a\u5e08\u7956\uff0c\u7384\u673a\u9038\u571f\u5fae\u7b11\u9053\uff1a\u201c\u4eca\u65e5\u5f97\u89c1\u56db\u4e16\u540c\u805a\uff0c\u4eba\u751f\u81f3\u6b64\uff0c \u5c1a\u6709\u4f55\u61be\uff1f\u201d\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u5929\u534e\u3001\u76c8\u76c8\u3001\u4e39\u67ab\uff0c\u4f60\u4eec\u7684\u6b66\u529f\u90fd\u5df2\u5927\u6210\uff0c\u6211\u5fc3\u5934\u518d\u4e5f\u6ca1 \u6709\u4ec0\u4e48\u6302\u8651\u4e86\uff0c\u53ea\u662f\u6b66\u5b66\u4e4b\u9053\uff0c\u6709\u5982\u5927\u6d77\uff0c\u4f60\u4eec\u8fd8\u662f\u4e0d\u80fd\u81ea\u6ee1\u554a\uff01\u201d\u8c22\u5929\u534e\u3001\u53f6\u76c8\u76d7\u548c\u5f20\u4e39 \u67ab\u5782\u624b\u8bf4\u9053\uff1a\u201c\u8c22\u5e08\u7236\u5e08\u7956\u7684\u8bad\u8bf2\u3002\u201d\u7384\u673a\u9038\u58eb\u5fbd\u5fbd\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u6211\u7b49\u4e09\u4eba\uff0c\u81ea\u60ed\u6570\u5341 \u5e74\uff0c\u82df\u6d3b\u4eba\u95f4\uff0c\u4e8e\u56fd\u4e8e\u6c11\uff0c\u90fd\u672a\u66fe\u505a\u8fc7\u4ec0\u4e48\u6709\u76ca\u4e4b\u4e8b\uff0c\u6240\u5e78\u8005\u5c1a\u7559\u4e00\u70b9\u5fae\u672a\u4e4b\u6280\uff0c\u671b\u4f60\u4eec\u5584 \u81ea\u8fd0\u7528\u6211\u4eec\u6240\u521b\u7684\u6b66\u529f\uff0c\u597d\u597d\u505a\u4e00\u756a\u4e8b\u4e1a\u3002\u201d\u4e0a\u5b98\u5929\u91ce\u4e5f\u5524\u4e4c\u8499\u592b\u7b49\u5f1f\u5b50\u4e0a\u524d\uff0c\u52c9\u52b1\u4e86\u51e0 \u53e5\u3002\u7384\u673a\u9038\u58eb\u6717\u58f0\u541f\u9053\uff1a\u201c\u6e38\u620f\u4eba\u95f4\u51e0\u5341\u5e74\uff0c\u8292\u978b\u7834\u5e3d\u81ea\u968f\u7f18\uff1b\u201d\u4e0a\u5b98\u5929\u91ce\u63a5\u9053\uff1a\u201c\u5fc3\u9b54\u53bb \u5c3d\u65e0\u7275\u6302\uff0c\u201d\u8083\u97f5\u5170\u63a5\u9053\uff1a\u201c\u5251\u8c31\u62f3\u7ecf\u540e\u4e16\u4f20\uff01\u201d\u541f\u7f62\u8bd7\u53e5\uff0c\u4e09\u4eba\u5bc2\u7136\u4e0d\u52a8\uff0c\u539f\u6765\u90fd\u662f\u5750\u5316 \u4e86\u3002 \u3000\u3000\u9ed1\u767d\u6469\u8bc3\u7a3d\u9996\u8bf4\u9053\uff1a\u201c\u4e09\u4f4d\u524d\u8f88\u798f\u5bff\u5168\u5f52\uff0c\u53ef\u559c\u53ef\u8d3a\u3002\u201d\u8c22\u5929\u534e\u7b49\u5411\u9057\u4f53\u884c\u4e86\u5927\u793c\uff0c\u8fdb \u5165\u77f3\u5ba4\uff0c\u53ea\u89c1\u56db\u58c1\u90fd\u753b\u6ee1\u4e86\u6b66\u529f\u56fe\u89e3\uff0c\u7cbe\u5fae\u5965\u5999\uff0c\u96be\u4ee5\u8a00\u5ba3\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u770b\u5f97\u5982\u9189\u5982\u75f4\uff0c\u4f46\u89c9\u5e08\u7956\u6240\u7559\u4e0b\u7684\u6b66\u529f\u56fe\u89e3\uff0c\u6709\u4e0d\u5c11\u5730\u65b9\u4e0e\u81ea\u5df1\u6240\u4e60\u7684\u201c\u7384\u529f\u8981 \u8bc0\u201d\u6697\u6697\u76f8\u901a\uff0c\u4e0d\u8fc7\u201c\u7384\u529f\u8981\u51b3\u201d\u8bb2\u7684\u53ea\u662f\u63d0\u7eb2\u5951\u9886\u7684\u8981\u7406\uff0c\u8fd9\u56fe\u89e3\u8fd8\u8981\u5b9e\u7528\u5f97\u591a\u3002\u5f20\u4e39\u67ab \u609f\u6027\u6781\u9ad8\uff0c\u770b\u4e86\u4e00\u904d\uff0c\u5ffd\u5730\u5bf9\u4e91\u857e\u7b11\u9053\uff1a\u201c\u6709\u4e86\u5e08\u7956\u6240\u7559\u4e0b\u7684\u8fd9\u4e2a\u6b66\u529f\u56fe\u89e3\uff0c\u54b1\u4eec\u4f55\u987b\u53bb\u6c42 \u4ec0\u4e48\u7075\u4e39\u5999\u836f\uff1f\u201d\u4e91\u857e\u4e0d\u660e\u5176\u610f\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u77a7\u8fd9\u5750\u529f \u516b\u5f0f\uff0c\u4f9d\u4f60\u54e5\u54e5\u7684\u529f\u529b\uff0c\u7528\u56fe\u4fee\u4e60\uff0c\u6211\u770b\u4e0d\u7528\u4e09\u5929\uff0c\u5c31\u53ef\u4ee5\u628a\u6240\u53d7\u7684\u6bd2\u6c14\u5c3d\u6cc4\u4f53\u5916\u3002\u201d\u4e91\u857e \u8fd9\u624d\u77e5\u9053\u4e08\u592b\u4ece\u56fe\u89e3\u4e2d\u609f\u51fa\u66ff\u4e91\u91cd\u6cbb\u4f24\u4e4b\u6cd5\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u7b49\u4e0b\u5c31\u5c06\u54e5\u54e5\u79fb\u5230\u8fd9\u91cc\uff0c\u8ba9 \u4ed6\u9759\u9759\u7597\u517b\u51e0\u5929\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u4ed4\u7ec6\u5c06\u56fe\u89e3\u770b\u4e86\u4e00\u904d\uff0c\u6700\u540e\u4e00\u6bb5\u4e09\u5341\u516d\u4e2a\u56fe\u5f0f\u4e43\u662f\u5251\u8c31\uff0c\u5c06\u767e\u53d8\u7384\u673a\u5251\u6cd5\u53c8\u589e\u6dfb \u4e86\u8bb8\u591a\u53d8\u5316\uff0c\u590d\u6742\u4e4b\u6781\uff0c\u53ea\u662f\u4f3c\u4e4e\u6709\u51e0\u4e2a\u5f0f\u5b50\u672a\u66fe\u5b8c\u5168\uff0c\u6700\u540e\u7684\u90a3\u6bb5\u77f3\u5899\uff0c\u7559\u4e0b\u4e00\u7247\u7a7a\u767d\uff0c \u5f20\u4e39\u67ab\u6c89\u541f\u534a\u664c\uff0c\u604d\u7136\u609f\u9053\uff1a\u201c\u662f\u4e86\uff0c\u5b9a\u662f\u5e08\u7956\u56e0\u4e3a\u8d64\u971e\u9053\u4eba\u5230\u6765\uff0c\u63d0\u65e9\u5f00\u5173\uff0c\u6240\u4ee5\u4e0d\u53ca\u8865 \u4e0a\u4e86\uff0c\u82e5\u7136\u5c06\u8fd9\u5957\u5251\u6cd5\u8865\u8db3\uff0c\u66f4\u53ef\u4ee5\u65e0\u654c\u4e8e\u5929\u4e0b\u4e86\u3002\u201d \u3000\u3000\u7384\u673a\u9038\u58eb\u5bf9\u4e8e\u8eab\u540e\u4e4b\u4e8b\u65e9\u6709\u5b89\u6392\uff0c\u5893\u5730\u4ea6\u5df2\u9009\u597d\uff0c\u5f53\u4e0b\u7531\u8c22\u5929\u534e\u4e0e\u4e4c\u8499\u592b\u7387\u9886\u4e24\u4ee3\u5f1f \u5b50\uff0c\u7ed9\u5e08\u5c0a\u6536\u6b93\uff0c\u62e9\u65e5\u5b89\u846c\u3002\u7384\u673a\u9038\u58eb\u3001\u4e0a\u5b98\u5929\u91ce\u548c\u8427\u97f5\u5170\u4e09\u4eba\u90fd\u662f\u5bff\u767b\u516b\u5341\uff0c\u7559\u4e0b\u6b66\u529f\uff0c \u5b89\u7136\u5750\u5316\uff0c\u5b9e\u662f\u6b66\u6797\u4e2d\u767e\u5e74\u96be\u9047\u7684\u4f73\u8bdd\uff0c\u6545\u6b64\u4f17\u5f1f\u5b50\u867d\u7136\u5bf9\u5e08\u5c0a\u7684\u53bb\u4e16\u6df1\u81f4\u60bc\u5ff5\uff0c\u4f46\u5982\u6ca1\u6709 \u4e00\u822c\u4e27\u5bb6\u7684\u90a3\u79cd\u60b2\u4f24\u6c14\u6c1b\u3002 \u3000\u3000\u5c71\u98ce\u5439\u9001\uff0c\u6d31\u6d77\u4e0b\u9762\u9690\u9690\u4f20\u6765\u53ae\u6740\u4e4b\u58f0\uff0c\u5f20\u4e39\u67ab\u5f85\u5e08\u7956\u5165\u68fa\uff0c\u4eb2\u89c6\u542b\u6b93\u4e4b\u540e\uff0c\u6302\u5ff5\u738b\u5e9c \u7684\u5b89\u5371\uff0c\u5dee\u9063\u9ed1\u767d\u6469\u8bc3\u4e0b\u5c71\u63a2\u542c\u3002\u8fd9\u65f6\u4e8e\u627f\u73e0\u4e5f\u6b63\u4e3a\u7740\u53f6\u6210\u6797\u7684\u5b89\u5371\u5fe7\u8651\u3002\u5f20\u4e39\u67ab\u548c\u8c22\u5929 \u534e\u3001\u4e4c\u8499\u592b\u7b49\u4eba\u5728\u91cc\u9762\u5546\u91cf\u4e27\u4e8b\u4eea\u793c\uff0c\u5979\u662f\u5c0f\u8f88\uff0c\u63d2\u4e0d\u8fdb\u53e3\uff0c\u72ec\u81ea\u4e00\u4eba\uff0c\u8d70\u5230\u6eaa\u8fb9\uff0c\u542c\u6d31\u6d77 \u4e0b\u9762\u4f20\u6765\u7684\u53ae\u6740\u58f0\uff0c\u60f3\u8d77\u53f6\u6210\u6797\u6276\u4f24\u8d74\u63f4\uff0c\u5fc3\u4e2d\u65e2\u662f\u5174\u594b\uff0c\u53c8\u662f\u6302\u8651\u3002 \u3000\u3000\u8fd9\u65f6\u65f6\u65b9\u8fc7\u5348\uff0c\u592a\u9633\u7167\u8fc7\u5c71\u5cf0\u7684\u80cc\u666f\u6298\u5c04\u5728\u6c34\u9762\u4e0a\uff0c\u78a7\u6ce2\u5fae\u6f3e\uff0c\u5f62\u6210\u4e94\u5f69\u8679\u9713\u56de\u65cb\u7684\u5c42 \u5c42\u5708\u73af\uff0c\u4e8e\u627f\u73e0\u501a\u7740\u6eaa\u8fb9\u7684\u5927\u9752\u6811\uff0c\u4e34\u6d41\u7167\u5f71\uff0c\u4f46\u89c9\u601d\u6f6e\u8d77\u4f0f\uff0c\u4e0d\u80fd\u81ea\u4f11\uff0c\u6e05\u6eaa\u4e2d\u4e00\u5ffd\u513f\u5e7b \u51fa\u94c1\u955c\u5fc3\u7684\u5f71\u5b50\uff0c\u4e00\u5ffd\u513f\u53c8\u5e7b\u51fa\u53f6\u6210\u6797\u7684\u5f71\u5b50\uff0c\u5c31\u50cf\u78a7\u5f7c\u4e0a\u7684\u8679\u9713\u5708\u73af\u4e00\u6837\uff0c\u53d8\u5e7b\u65e0\u5b9a\u3002\u7ecf \u8fc7\u8fd9\u4e24\u65e5\u6765\u7684\u89c2\u5bdf\uff0c\u5c24\u5176\u662f\u7ecf\u8fc7\u82cd\u5c71\u8fd9\u4e00\u6218\u4e4b\u540e\uff0c\u4e8e\u627f\u73e0\u5bf9\u4e24\u4e2a\u4eba\u7684\u6027\u683c\u662f\u770b\u5f97\u66f4\u6e05\u695a\u4e86\uff0c \u7136\u800c\u5979\u5c11\u5973\u7684\u5fc3\u60c5\uff0c\u5374\u8fd8\u4e0d\u80fd\u4f3c\u6e05\u6eaa\u4e00\u6837\u7684\u6f84\u660e\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u6b63\u81ea\u51fa\u795e\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\u4e00\u58f0\u54b3\u55fd\uff0c\u56de\u5934\u4e00\u770b\uff0c\u6765\u7684\u6b63\u662f\u94c1\u955c\u5fc3\u3002\u4e8e\u627f\u73e0\u98de\u7ea2\u4e86 \u8138\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u53bb\u966a\u6c90\u5c0f\u59d0\uff0c\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d\u94c1\u955c\u5fc3\u53f9\u4e86\u53e3\u6c14\uff0c\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u6211\u7684\u5fc3\u4e8b\uff0c \u8981\u5230\u51e0\u65f6\u4f60\u624d\u660e\u767d\uff1f\u5979\u4e0d\u50cf\u4f60\u6709\u4e00\u8eab\u6b66\u529f\uff0c\u5728\u6076\u6218\u4e4b\u65f6\uff0c\u6211\u5949\u4f60\u5e08\u6bcd\u4e4b\u547d\uff0c\u5c82\u80fd\u4e0d\u7167\u6599 \u5979\uff1f\u201d\u4e8e\u627f\u73e0\u6c14\u9053\uff1a\u201c\u6211\u662f\u53eb\u4f60\u4e0d\u7406\u5979\u4e48\uff1f\u4f60\u628a\u6211\u5f53\u6210\u4ec0\u4e48\u4eba\u4e86\uff1f\u201d\u53f6\u7f13\u7f13\u5730\u56de\u8f6c\u4e86\u5934\uff0c\u5fc3 \u4e2d\u65e0\u9650\u9178\u695a\u3002\u4f46\u89c9\u94c1\u955c\u5fc3\u5e73\u65e5\u867d\u7136\u5584\u4e8e\u4f3a\u5019\u81ea\u5df1\u7684\u5fc3\u610f\uff0c\u7a76\u5176\u5b9e\u9645\uff0c\u5374\u53c8\u4f3c\u4e00\u70b9\u4e5f\u4e0d\u7406\u89e3\u81ea \u5df1\u7684\u4e3a\u4eba\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u65e9\u77e5\u4eca\u65e5\uff0c\u4f55\u5fc5\u5f53\u521d\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4eca\u65e5\u5982\u4f55\uff1f\u5f53 \u521d\u600e\u6837\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u60f3\u5f53\u65e5\u5728\u53f0\u5dde\u4e4b\u65f6\uff0c\u4f60\u6211\u540c\u4f4f\u4e00\u4e2a\u5e10\u5e55\uff0c\u60c5\u5982\u624b\u8db3\uff0c\u55ef\uff0c\u4f60\u53ef\u8fd8\u8bb0 \u5f97\uff0c\u54b1\u4eec\u66fe\u7ea6\u597d\u4e92\u76f8\u7422\u78e8\u6b66\u529f\u5462\u3002\u5982\u4eca\u4f60\u773c\u754c\u5bbd\u4e86\uff0c\u5230\u4e86\u8fd9\u513f\uff0c\u6b63\u773c\u513f\u4e5f\u4e0d\u77a7\u4eba\u5bb6\u4e86\u3002\u201d\u4e8e \u627f\u73e0\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u53ea\u542c\u5f97\u94c1\u955c\u5fc3\u53c8\u9053\uff1a\u201c\u5373\u7b97\u4f60\u4e0d\u5ff5\u5f53\u521d\u624b\u8db3\u4e4b\u60c5\uff0c\u4e5f\u5f53\u5ff5\u6211\u8fd9\u6b21\u4e07\u91cc\u8ffd\u8e2a\u4e4b \u82e6\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u8d77\u4ed6\u4e3a\u4e86\u8ffd\u5bfb\u81ea\u5df1\uff0c\u5728\u8c37\u5bb6\u5e84\u524d\u7d22\u4eba\u3001\u89c5\u9a6c\uff0c\u5982\u75af\u4f3c\u50bb\u7684\u60c5\u666f\uff0c \u4e0d\u77e5\u4e0d\u89c9\u56de\u8fc7\u5934\u6765\uff0c\u94c1\u955c\u5fc3\u5fc3\u4e2d\u5927\u559c\uff0c\u5374\u4ecd\u7136\u88c5\u51fa\u4e00\u526f\u53ef\u601c\u7684\u795e\u6c14\uff0c\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u6211 \u4e3a\u4f60\u5728\u8c37\u5bb6\u5e84\u6240\u53d7\u7684\u5200\u4f24\uff0c\u73b0\u5728\u4f24\u53e3\u8fd8\u672a\u5408\u62e2\u5462\uff01\u201d\u8fb9\u8bf4\u8fb9\u634b\u8863\u8896\uff0c\u5ffd\u60f3\u8d77\u4f24\u53e3\u5176\u5b9e\u65e9\u5df2\u7ed3 \u75a4\uff0c\u624b\u6307\u6162\u6162\u5730\u5377\u7740\u8863\u8896\uff0c\u5077\u77a7\u4e8e\u627f\u73e0\u7684\u795e\u8272\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u539f\u60f3\u7528\u8bf4\u8bdd\u6253\u52a8\u4e8e\u627f\u73e0\u7684\u5fc3\u5f26\uff0c\u5374\u4e0d\u6599\u56e0\u6b64\u5f15\u8d77\u4e86\u4e8e\u627f\u73e0\u7684\u8054\u60f3\uff0c\u60f3\u8d77\u53f6\u6210\u6797\u4eca \u65e5\u6240\u53d7\u4e4b\u4f24\uff0c\u6bd4\u8d77\u94c1\u955c\u5fc3\u6765\uff0c\u4e0d\u77e5\u91cd\u4e86\u591a\u5c11\uff0c\u53ef\u662f\u53f6\u6210\u6797\u5374\u4ece\u672a\u8bf4\u8fc7\u4e00\u53e5\u79f0\u529f\u9053\u52b3\u7684\u8bf4\u8bdd\u3002 \u94c1\u955c\u5fc3\u89c1\u4e8e\u627f\u73e0\u9762\u8272\u6c89\u6697\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u542c\u6d31\u6d77\u4e0b\u8fb9\u7684\u53ae\u6740\u4e4b\u58f0\u5df2\u9759\u6b62\u4e86\uff0c\u4e0d\u77e5\u53f6\u5927\u54e5\u631f\u4f24\u8840\u6218\uff0c\u7ed3\u679c\u5982\u4f55\uff1f\u201d \u94c1\u955c\u5fc3\u51c9\u4e86\u534a\u622a\uff0c\u60f3\u4e0d\u5230\u4e8e\u627f\u73e0\u770b\u4e5f\u4e0d\u7740\u4ed6\u7684\u201c\u4f24\u75d5\u201d\uff0c\u5374\u60f3\u8d77\u53f6\u6210\u6797\u6765\uff0c\u597d\u534a\u664c\u624d\u642d\u8baa\u7b11 \u9053\uff1a\u201c\u672c\u6765\u8be5\u6211\u53bb\u7684\uff0c\u6211\u4e0d\u613f\u4e0e\u53f6\u5144\u5f1f\u4e89\u529f\uff0c\u6545\u6b64\u8ba9\u4ed6\u53bb\u4e86\u3002\u54ce\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u8fd8\u662f\u6211\u53bb\u7684 \u597d\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u597d\u50cf\u95fb\u5230\u4e86\u4e00\u80a1\u9709\u5473\u4f3c\u7684\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6210\u6797\u6b64\u53bb\uff0c\u5c82\u662f\u4e3a\u4e86\u4e89 \u529f\uff1f\u201d\u53e3\u4e2d\u5374\u4e0d\u8bf4\u51fa\u6765\u3002\u94c1\u955c\u5fc3\u89c1\u4e8e\u627f\u73e0\u9762\u8272\u8d8a\u6765\u8d8a\u4e0d\u5bf9\uff0c\u7eb3\u7f55\u4e4b\u6781\uff0c\u4e00\u65f6\u95f4\u7adf\u60f3\u4e0d\u51fa\u8bf4\u4ec0 \u4e48\u8bdd\u624d\u597d\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u524d\u5c71\u6b65\u5c65\u58f0\u55a7\uff0c\u6fb9\u53f0\u706d\u660e\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u9633\u5b97\u6d77\u8fd9\u6b21\u5168\u519b\u8986\u6ca1\uff0c\u5168\u4e8f\u4e86\u53f6\u6210\u6797\u5144 \u5f1f\uff0c\u8d76\u53bb\u6b63\u662f\u65f6\u5019\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u529f\u52b3\uff0c\u9633\u5b97\u6d77\u52c7\u731b\u4e4b\u6781\uff0c\u82e5\u4e0d\u662f\u6fb9\u53f0\u5c06\u519b\uff0c\u8c01 \u80fd\u5c06\u4ed6\u6740\u8d25\u3002\u201d\u6fb9\u53f0\u706d\u660e\u7b11\u9053\uff1a\u201c\u6253\u4ed7\u6211\u6253\u5f97\u591a\uff0c\u6c34\u4e0a\u6253\u4ed7\u53ef\u8fd8\u662f\u7b2c\u4e00\u6b21\uff0c\u73b0\u5728\u8fd8\u89c9\u6655\u8239 \u5462\u3002\u6211\u90a3\u4e00\u5200\u4e00\u67aa\u7684\u529f\u52b3\u7b97\u788d\u4ec0\u4e48\uff0c\u53f6\u5144\u5f1f\uff0c\u4f60\u6307\u6325\u6c34\u5e08\u7684\u672c\u9886\uff0c\u6211\u53ef\u662f\u771f\u7684\u4f69\u670d\u5462\u3002\u201d\u9ed1 \u767d\u6469\u8bc3\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u8c26\u8ba9\u4e86\uff0c\u5927\u5bb6\u90fd\u6709\u529f\u52b3\u3002\u54a6\uff0c\u4e8e\u627f\u73e0\u5462\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u8d70\u4e86\u8fc7\u6765\uff0c\u94c1\u955c\u5fc3\u6ca1\u7cbe\u6253\u91c7\u5730\u8ddf\u5728\u540e\u9762\uff0c\u5fc3\u4e2d\u6781\u4e0d\u670d\u6c14\uff0c\u60f3\u9053\uff1a\u201c\u82e5\u662f\u6211\u53bb\uff0c\u8fd9 \u4e00\u4ed7\u6253\u5f97\u66f4\u6f02\u4eae\u3002\u201d\u6094\u6068\u4e0d\u5df2\uff0c\u53ea\u597d\u88c5\u51fa\u7b11\u5bb9\uff0c\u52a0\u6025\u811a\u6b65\uff0c\u62a2\u4e0a\u524d\uff0c\u4f38\u624b\u5411\u53f6\u6210\u6797\u9053\u8d3a\u3002 \u3000\u3000\u53ea\u89c1\u53f6\u6210\u6797\u8863\u88f3\u7834\u88c2\uff0c\u53f3\u81c2\u4e0a\u4e24\u9053\u957f\u957f\u7684\u4f24\u53e3\uff0c\u8840\u6d41\u672a\u6b62\uff0c\u4e8e\u627f\u73e0\u60ca\u9053\uff1a\u201c\u4f60\u600e\u4e48 \u5566\uff1f\u201d\u53f6\u6210\u6797\u5fae\u7b11\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u7ed9\u9633\u5b97\u6d77\u624e\u4e86\u4e24\u4e0b\uff0c\u8fd9\u5012\u7701\u5f97\u6211\u653e\u8840\u4e86\uff0c\u4f60\u77a7\uff0c\u80bf\u5df2\u9000\u4e86 \u5462\u3002\u201d\u773c\u5149\u4ece\u4e8e\u627f\u73e0\u9762\u4e0a\u4e00\u63a0\u800c\u8fc7\uff0c\u53c8\u5411\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u9633\u5b97\u6d77\u7684\u5077\u88ad\u867d\u7136\u5931\u8d25\uff0c\u4e8b\u60c5\u53ef\u6ca1\u4e86 \u7ed3\uff0c\u6bb5\u738b\u7237\u6b63\u60f3\u542c\u4f60\u7684\u4e3b\u610f\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u6c90\u56fd\u516c\u4eb2\u7387\u5927\u519b\uff0c\u5df2\u5728\u79bb\u57ce\u4e09\u5341\u91cc\u5916\u624e\u4e0b\u8425\u5be8\u3002\u54b1 \u4eec\u521a\u521a\u6253\u9000\u9633\u5b97\u6d77\uff0c\u4fbf\u63a5\u5230\u6c90\u56fd\u516c\u9001\u6765\u7684\u6218\u4e66\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6218\u4e66\u4e0a\u600e\u4e48\u8bf4\uff1f\u201d\u53f6\u6210\u6797 \u9053\uff1a\u201c\u6218\u4e66\u5386\u6570\u6bb5\u738b\u7237\u7684\u4e09\u6761\u5927\u7f6a\uff0c\u7b2c\u4e00\u6761\u662f\u8bf4\u56fd\u5bb6\u7235\u4f4d\u4e43\u671d\u5ef7\u6240\u5c01\uff0c\u6bb5\u738b\u7237\u4e0d\u8be5\u81ea\u7acb\u4e3a \u738b\u3002\u7b2c\u4e8c\u6761\u662f\u8d23\u5907\u6bb5\u738b\u7237\u4e0d\u8be5\u64c5\u81ea\u9a71\u9010\u671d\u5ef7\u547d\u5b98\u3002\u7b2c\u4e09\u6761\u6700\u5999\uff0c\u8d23\u5907\u6bb5\u738b\u7237\u4e0d\u8be5\u6d3e\u4eba\u5077\u5165\u6606 \u660e\uff0c\u62d0\u8d70\u4ed6\u7684\u513f\u5973\u3002\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u5982\u6b64\u770b\u6765\uff0c\u8fd9\u4e00\u4f4d\u6c90\u56fd\u516c\u4e5f\u4e0d\u662f\u8bda\u5fc3\u8981\u6253\u3002\u201d\u53f6\u6210\u6797 \u9053\uff1a\u201c\u613f\u95fb\u9ad8\u89c1\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6218\u4e66\u4e0a\u53e3\u6c14\u867d\u7136\u4e25\u5389\uff0c\u5176\u5b9e\u5927\u6709\u8f6c\u5706\u4e4b\u5730\u3002\u6bd4\u5982\u8bf4\u81ea\u7acb\u4e3a \u738b\u4e4b\u4e8b\uff0c\u82e5\u7136\u5f97\u671d\u5ef7\u8ffd\u8ba4\uff0c\u518d\u4e0b\u4e00\u9053\u5fa1\u65e8\u5c01\u8d4f\uff0c\u4e8b\u60c5\u4e5f\u5c31\u4e86\u7ed3\u4e86\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u671d\u5ef7\u80af \u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u53ea\u8981\u6c90\u56fd\u516c\u4e0d\u613f\u52a8\u5175\uff0c\u96be\u9053\u671d\u5ef7\u8fd8\u4f1a\u4e07\u91cc\u8fe2\u8fe2\uff0c\u6d3e\u5175\u5230\u5927\u7406\u6253\u4ed7\u4e48\uff1f\u6240 \u4ee5\u8fd9\u4e8b\u60c5\u5168\u770b\u6c90\u56fd\u516c\u7684\u594f\u6298\u5982\u4f55\u8bf4\u6cd5\u4e86\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u4f46\u6bb5\u738b\u7237\u7684\u771f\u610f\u4e5f\u4e0d\u662f\u60f3\u81ea\u7acb\u4e3a\u738b \u800c\u5df2\uff0c\u4ed6\u662f\u60f3\u767d\u65cf\u7684\u8001\u767e\u59d3\u4e0d\u53d7\u660e\u671d\u66b4\u653f\u4e4b\u82e6\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u53ea\u8981\u53cc\u65b9\u505c\u6218\uff0c\u5730\u65b9\u653f\u4e8b\uff0c \u81ea\u53ef\u5546\u91cf\u3002\u201d\u987f\u4e86\u4e00\u987f\u53c8\u9053\uff1a\u201c\u6211\u770b\u6c90\u56fd\u516c\u76ee\u524d\u6700\u6025\u7684\u5c31\u662f\u4ed6\u7684\u513f\u5973\uff0c\u78f7\u513f\uff0c\u71d5\u513f\uff0c\u4f60\u4eec\u613f \u56de\u53bb\u5417\uff1f\u201d \u3000\u3000\u6c90\u78f7\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u613f\u8ddf\u968f\u5e08\u7236\u3002\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4f60\u5c31\u4e0d\u5ff5\u5927\u7406\u5dde\u7684\u767e\u59d3\u4e48\uff1f\u201d \u6c90\u71d5\u9053\uff1a\u201c\u542c\u5e08\u7236\u5429\u5490\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u4eec\u4fee\u4e66\u4e00\u5c01\uff0c\u66ff\u6bb5\u738b\u7237\u6c42\u548c\u3002\u201d\u6c90\u71d5\u9053\uff1a\u201c\u600e\u4e48 \u5199\u6cd5\uff1f\u201d\u5f20\u4e39\u67ab\u53e3\u6388\u4e86\u4e66\u4fe1\u7684\u5185\u5bb9\uff0c\u5927\u610f\u662f\u8981\u6c90\u56fd\u516c\u7b54\u5141\u6bb5\u738b\u7237\u7684\u82e5\u5e72\u6761\u4ef6\uff0c\u7136\u540e\u6c90\u78f7\u3001\u6c90 \u71d5\u4fbf\u53ef\u653e\u56de\u3002\u53eb\u6c90\u71d5\u7528\u81ea\u5df1\u7684\u53e3\u6c14\uff0c\u52a8\u4ee5\u771f\u60c5\uff0c\u518d\u6653\u4ee5\u5927\u4e49\uff0c\u514d\u767e\u59d3\u53d7\u5200\u5175\u4e4b\u52ab\u3002 \u3000\u3000\u6c90\u71d5\u624d\u601d\u654f\u6377\uff0c\u7acb\u5373\u4e00\u6325\u800c\u5c31\u3002\u5374\u6c89\u541f\u8bf4\u9053\uff1a\u201c\u8fd8\u5f97\u4e00\u4e2a\u80fd\u8a00\u5584\u8fa9\u4e0b\u4e66\u7684\u4eba\u3002\u201d\u94c1\u955c\u5fc3 \u907f\u5f00\u4e86\u6c90\u71d5\u7684\u773c\u5149\uff0c\u5374\u542c\u5f97\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u5f97\u6709\u52b3\u955c\u5fc3\u4e00\u884c\u4e86\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u6211\u4e0d\u884c \u54ea\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u80fd\u8a00\u5584\u8fa9\uff0c\u4f60\u662f\u51fa\u8272\u5f53\u884c\uff0c\u8fd9\u5dee\u4e8b\u4f60\u4f55\u5fc5\u8fd8\u8981\u63a8\u8f9e\u3002\u201d\u6c90\u71d5\u8fd9\u624d\u7b11\u9053\uff1a \u201c\u662f\u5440\uff0c\u94c1\u516c\u5b50\u53bb\u8fd9\u6700\u597d\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u94a6\u955c\u5fc3\u672c\u6709\u5fc3\u75c5\uff0c\u4f46\u542c\u5f97\u4e8e\u627f\u73e0\u4e5f\u8fd9\u6837\u8bf4\u4e86\uff0c\u4f17\u4eba\u53c8\u4e00\u81f4\u201c\u6367\u201d\u4ed6\uff0c\u5fc3\u4e2d\u5f97\u610f\uff0c\u628a\u521a\u624d \u7684\u4e0d\u5feb\u4e4b\u611f\uff0c\u6d88\u9664\u4e86\u4e00\u5927\u534a\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u6211\u5c31\u52c9\u4e3a\u5176\u96be\uff0c\u8bd5\u4e00\u8bd5\u770b\u3002\u201d\u5f53\u4e0b\u53d6\u4e86\u6c90\u78f7\u3001\u6c90 \u71d5\u7f72\u540d\u7684\u4e66\u4fe1\uff0c\u7acb\u523b\u4e0b\u5c71\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u5348\u95f4\uff0c\u4f17\u4eba\u90fd\u5728\u738b\u5e9c\u4e2d\u9759\u5019\u6d88\u606f\uff0c\u53ea\u89c1\u94c1\u955c\u5fc3\u56de\u6765\uff0c\u6625\u98ce\u6ee1\u9762\uff0c\u4e00\u95ee\u4e4b\u4e0b\uff0c\u6c90\u56fd \u516c\u679c\u7136\u613f\u610f\u8c08\u548c\uff0c\u8981\u6c42\u6bb5\u738b\u7237\u6b63\u5f0f\u6d3e\u9063\u4f7f\u8005\u53bb\u8c08\uff0c\u5e76\u5148\u8981\u9001\u6c90\u78f7\u3001\u6c90\u71d5\u5230\u4ed6\u7684\u519b\u4e2d\u3002\u6bb5\u738b\u7237 \u4e5f\u5f88\u8d4f\u8bc6\u94c1\u955c\u5fc3\uff0c\u4fbf\u59d4\u6258\u4ed6\u505a\u8c08\u548c\u7684\u4f7f\u8005\uff0c\u6c90\u71d5\u4fcf\u6084\u5c06\u94c1\u955c\u5fc3\u62c9\u8fc7\u4e00\u8fb9\uff0c\u95ee\u4ed6\u89c1\u5230\u81ea\u5df1\u7236\u4eb2 \u7684\u60c5\u5f62\u3002\u539f\u6765\u6c90\u56fd\u516c\u4e5f\u77e5\u9053\u94c1\u955c\u5fc3\u7684\u7236\u4eb2\u662f\u4e00\u4e2a\u6b63\u76f4\u7684\u5fa1\u53f2\uff0c\u5728\u5e2d\u95f4\u8bd5\u94c1\u955c\u5fc3\u7684\u624d\u5b66\uff0c\u5bf9\u4ed6 \u5938\u5956\u5907\u81f3\uff0c\u602a\u4e0d\u5f97\u94c1\u955c\u5fc3\u8fd9\u6837\u5f97\u610f\u3002\u6c90\u71d5\u82b3\u5fc3\u6697\u559c\uff0c\u6c90\u78f7\u5374\u662f\u6101\u7709\u82e6\u8138\u5730\u820d\u4e0d\u5f97\u79bb\u5f00\u5f20\u4e39 \u67ab\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u65e0\u4e0d\u6563\u4e4b\u7b75\u5e2d\uff0c\u4f55\u51b5\u54b1\u4eec\u53c8\u4e0d\u662f\u4ee5\u540e\u6c38\u4e0d\u518d\u89c1\u4e86\uff0c\u78f7\u513f\uff0c\u4f60\u4f55\u5fc5\u60b2 \u4f24\uff1f\u4f60\u4eec\u59d0\u5f1f\u672c\u6765\u4e0d\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u6211\u8fd9\u51e0\u65e5\u6559\u7ed9\u4f60\u4eec\u7684\u529f\u592b\uff0c\u4f60\u4eec\u56de\u53bb\u597d\u597d\u7ec3\u4e60\uff0c\u4e5f\u5c3d\u591f\u7528 \u4e86\u3002\u201d\u6c90\u78f7\u54ed\u4e27\u7740\u8138\u9053\uff1a\u201c\u5e08\u7236\u8bdd\u8bf4\u7684\u662f\uff0c\u53ea\u662f\u5728\u8fd9\u513f\u81ea\u7531\u81ea\u5728\uff0c\u591a\u4e48\u597d\u73a9\uff0c\u56de\u53bb\u4e4b\u540e\uff0c\u5173 \u5728\u5e9c\u4e2d\uff0c\u90a3\u53ef\u591f\u95f7\u6c14\u7684\u5566\u3002\u201d\u4e4c\u8499\u592b\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u8d2a\u8fd9\u513f\u597d\u73a9\uff0c\u4e0d\u613f\u56de\u53bb\u3002\u597d \u5427\uff0c\u8fd9\u6b21\u6218\u7978\u6d88\u9664\uff0c\u54b1\u4eec\u6b63\u8be5\u5e86\u8d3a\u4e00\u573a\uff0c\u4eca\u665a\u5c31\u5230\u6d31\u6d77\u6cdb\u821f\u53bb\u3002\u4e00\u6765\u8ba9\u4f60\u73a9\u4e2a\u75db\u5feb\uff0c\u4e8c\u6765\u7ed9 \u4f60\u4eec\u9001\u884c\u3002\u4e39\u67ab\uff0c\u4f60\u5927\u7ea6\u4e0d\u65e5\u4e5f\u8981\u79bb\u5f00\u82cd\u5c71\u4e86\u5427\uff1f\u201d\u5f20\u4e39\u67ab\u70b9\u4e86\u70b9\u5934\uff0c\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u52a8\uff0c\u53ea \u89c1\u53f6\u6210\u6797\u9762\u9732\u559c\u8272\u3002\u94c1\u955c\u5fc3\u5374\u6709\u70b9\u5c34\u5c2c\u7684\u795e\u60c5\u3002 \u3000\u3000\u201c\u6d31\u6d77\u6708\u201d\u662f\u5927\u7406\u6700\u8457\u540d\u98ce\u666f\uff0c\u8fd9\u4e00\u665a\u4ed6\u4eec\u5206\u4e58\u4e24\u53ea\u753b\u80aa\uff0c\u5728\u6d31\u6d77\u8d4f\u6708\uff0c\u4e4c\u8499\u592b\u592b\u5987\uff0c \u8c22\u5929\u534e\u592b\u5987\uff0c\u9ed1\u767d\u6469\u8bc3\uff0c\u6bb5\u6f84\u82cd\u548c\u6ce2\u65af\u516c\u4e3b\u7b49\u5728\u4e00\u6761\u8239\uff0c\u5f20\u4e39\u67ab\u592b\u5987\uff0c\u6f6e\u97f3\u548c\u5c1a\u548c\u94c1\u955c\u5fc3\uff0c \u4e8e\u627f\u73e0\uff0c\u53f6\u6210\u6797\uff0c\u6c90\u71d5\u59d0\u5f1f\u7b49\u51e0\u4e2a\u5c0f\u8f88\u5728\u53e6\u4e00\u6761\u8239\u3002\u78a7\u6ce2\u4f3c\u955c\uff0c\u6708\u534e\u5982\u7ec3\uff0c\u4e00\u671b\u65e0\u9645\u7684\u6d31\u6d77 \u4e0a\u6d6e\u6ca1\u7740\u5e06\u5f71\u70b9\u70b9\uff0c\u6e14\u706b\u661f\u5149\uff0c\u4e92\u76f8\u8f89\u6620\uff0c\u8bf4\u4e0d\u51fa\u7684\u5b81\u9759\u5e7d\u7f8e\uff0c\u771f\u6559\u4eba\u60f3\u8c61\u4e0d\u5230\uff0c\u524d\u4e24\u5929\u8fd9 \u91cc\u66fe\u5377\u8d77\u8fc7\u8840\u6d6a\u8165\u98ce\u3002\u6c90\u71d5\u508d\u7740\u94c1\u955c\u5fc3\uff0c\u6307\u70b9\u6e56\u4e0a\u7684\u98ce\u666f\uff0c\u4e8e\u627f\u73e0\u5ffd\u7136\u611f\u5230\u4e00\u9635\u8ff7\u832b\uff0c\u5fc3\u5934 \u597d\u4f3c\u6709\u4e00\u79cd\u9884\u5146\uff0c\u597d\u4f3c\u94c1\u955c\u5fc3\u660e\u65e5\u9001\u6c90\u71d5\u59d0\u5f1f\u56de\u53bb\u4e4b\u540e\uff0c\u5c31\u8981\u548c\u81ea\u5df1\u8fdc\u8fdc\u5730\u79bb\u5f00\uff0c\u4e0d\u77e5\u600e \u7684\uff0c\u5ffd\u4f3c\u6709\u4e86\u51e0\u5206\u4f24\u611f\u4e4b\u610f\u3002\u94c1\u955c\u5fc3\u5374\u662f\u610f\u6001\u751a\u8c6a\uff0c\u53ea\u542c\u4ed6\u6263\u5f26\u6b4c\u9053\uff1a\u201c\u6d1e\u5ead\u9752\u8349\u4f34\u4e2d\u79cb\uff0c \u66f4\u65e0\u4e00\u70b9\u98ce\u8272\u3002\u7389\u9274\u743c\u7530\u4e09\u4e07\u9877\uff0c\u7740\u6211\u6241\u821f\u4e00\u53f6\u3002\u7d2f\u6708\u5206\u8f89\uff0c\u660e\u6cb3\u5171\u6620\uff0c\u8868\u91cc\u4ff1\u6f84\u6f88\u3002\u60a0\u7136 \u5fc3\u4f1a\uff0c\u5999\u5904\u96be\u4e0e\u541b\u8bf4\uff01\u201d\u6c90\u71d5\u4e0d\u5f85\u6b4c\u5b8c\uff0c\u4fbf\u62cd\u638c\u8d5e\u9053\uff1a\u201c\u5f20\u4e8e\u6e56\u8fd9\u9752\u6d1e\u5ead\u79cb\u6708\uff0c\u771f\u662f\u5343\u53e4\u7edd \u5531\uff01\u53ef\u60dc\u4ed6\u4e0d\u66fe\u5230\u6d31\u6d77\u6cdb\u821f\u3002\u201d\u5f20\u4e39\u67ab\u52fe\u8d77\u6587\u601d\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u592a\u6e56\u4e0e\u6d31\u6d77\uff0c\u72b9\u5982\u897f\u5b50\u738b \u5ae1\uff0c\u5404\u6709\u5176\u7f8e\uff0c\u54b1\u4eec\u4e24\u5904\u7684\u6708\u8272\u90fd\u66fe\u8d4f\u8fc7\uff0c\u6bd4\u8d77\u524d\u4eba\u662f\u6709\u798f\u5f97\u591a\u4e86\u3002\u201d\u6b47\u4e86\u4e00\u4f1a\uff0c\u94c1\u955c\u5fc3\u7eed \u6b4c\u4e0b\u534a\u9615\u9053\uff1a\u201c\u5e94\u5ff5\u5cad\u8868\u7ecf\u5e74\uff0c\u5b64\u5149\u81ea\u7167\uff0c\u809d\u80c6\u7686\u51b0\u96ea\u3002\u77ed\u53d1\u8427\u9a9a\u895f\u8896\u51b7\uff0c\u7a33\u6cdb\u6ca7\u6eaa\u7a7a\u9614\uff0c \u5c3d\u6c72\u897f\u6c5f\uff0c\u7ec6\u659f\u5317\u6597\uff0c\u4e07\u8c61\u4e3a\u5bbe\u5ba2\uff0c\u6263\u5f26\u72ec\u5578\uff0c\u4e0d\u77e5\u4eca\u5915\u4f55\u5915\uff1f\u201d\u6c90\u71d5\u51fb\u638c\u7b11\u9053\uff1a\u201c\u5c3d\u6c72\u897f \u6c5f\uff0c\u7ec6\u659f\u5317\u6597\uff0c\u4e07\u8c61\u4e3a\u5bbe\u5ba2\uff01\u771f\u662f\u5927\u624b\u7b14\uff0c\u5927\u6c14\u9b44\uff0c\u5f20\u4e8e\u6e56\u66fe\u4e2d\u72b6\u5143\uff0c\u81ea\u6709\u6c14\u6982\uff0c\u82e5\u662f\u843d\u9b44 \u6587\u58eb\uff0c\u90a3\u662f\u4e07\u4e07\u5199\u4e0d\u51fa\u6765\uff01\u201d\u8a00\u4e2d\u85cf\u6709\u6df1\u610f\uff0c\u90a3\u662f\u529d\u94c1\u955c\u5fc3\u5728\u5979\u7236\u4eb2\u4e4b\u4e0b\uff0c\u6c42\u53d6\u529f\u540d\u3002\u4e8e\u627f \u73e0\u7709\u5934\u4e00\u76b1\uff0c\u5374\u4e0d\u8bf4\u4ec0\u4e48\u3002\u4f46\u89c1\u94c1\u955c\u5fc3\u6ee1\u6ee1\u5730\u94a6\u4e86\u4e00\u676f\u9152\uff0c\u773c\u5149\u4e00\u77a5\uff0c\u6b63\u5411\u81ea\u5df1\u8fd9\u9762\u5c04\u6765\uff0c \u4e8e\u627f\u73e0\u4f4e\u5934\u73a9\u6c34\uff0c\u4f46\u542c\u5f97\u94c1\u955c\u5fc3\u8bf4\u9053\uff1a\u201c\u6d31\u6d77\u6708\u8272\u867d\u7f8e\uff0c\u4f46\u6211\u5374\u66f4\u6000\u5ff5\u957f\u6c5f\uff0c\u53ea\u53ef\u60dc\u5343\u5e74\u4ee5 \u6765\uff0c\u591a\u5c11\u82f1\u96c4\uff0c\u5c3d\u662f\u628a\u957f\u6c5f\u4f5c\u6218\u573a\uff0c\u5f04\u5f97\u6ce2\u6d9b\u6c79\u6d8c\uff0c\u4ee4\u51e0\u8bb8\u9ad8\u4eba\u96c5\u58eb\uff0c\u8f9c\u8d1f\u4e86\u7f8e\u666f\u826f\u8fb0\u3002\u201d \u6709\u610f\u65e0\u610f\uff0c\u773c\u6ce2\u53c8\u5728\u4e8e\u627f\u73e0\u7684\u8138\u4e0a\u63a0\u8fc7\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u8f7b\u8f7b\u62c2\u5f00\u98d8\u5230\u8eab\u4e0a\u7684\u6d6a\u82b1\uff0c\u6d31\u6d77\u7684\u6708\u591c\u7f8e\u6781\u4e86\uff0c\u6726\u80e7\u7684\u6708\u8272\u5c31\u50cf\u4e00\u5c42\u8584\u96fe\u8f7b\u7eb1\uff0c \u7f69\u5728\u6c34\u9762\u4e0a\uff0c\u6d6a\u82b1\u98de\u821e\uff0c\u6c34\u6c14\u8499\u8499\uff0c\u604d\u4f3c\u6de1\u70df\u7b3c\u78a7\uff0c\u5982\u6b64\u6708\uff0c\u5982\u6b64\u591c\uff0c\u672c\u6765\u5bb9\u6613\u60f9\u4eba\u5f15\u8d77\u7f8e \u5999\u7684\u9050\u601d\uff0c\u53ef\u662f\u542c\u4e86\u94c1\u955c\u5fc3\u7684\u8bdd\uff0c\u5374\u597d\u50cf\u4e0d\u548c\u8c10\u7684\u4e50\u58f0\uff0c\u53cd\u800c\u7834\u574f\u4e86\u8fd9\u5e7d\u7f8e\u7684\u6c14\u6c1b\u3002\u53ef\u601c\u94c1 \u955c\u5fc3\u63d0\u8d77\u957f\u6c5f\uff0c\u539f\u662f\u60f3\u52fe\u8d77\u4e8e\u627f\u73e0\u7684\u56de\u5fc6\uff0c\u5374\u4e0d\u6599\u8fd9\u751c\u7f8e\u7684\u56de\u5fc6\uff0c\u4e5f\u6e10\u6e10\u5728\u4e8e\u627f\u73e0\u5fc3\u4e2d\u53d8\u8d28 \u4e86\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u628a\u773c\u5077\u89d1\uff0c\u4e8e\u627f\u73e0\u4e00\u76f4\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5374\u5ffd\u542c\u5f97\u53f6\u6210\u6797\u63d2\u53e3\u8bf4\u9053\uff1a\u201c\u8c01\u4e0d\u613f\u610f\u6709\u826f\u8fb0 \u7f8e\u666f\uff0c\u8d4f\u5fc3\u4e50\u4e8b\u3002\u53ef\u662f\u957f\u6c5f\u5357\u5317\u7684\u8001\u767e\u59d3\uff0c\u9965\u65e0\u4ee5\u4e3a\u98df\uff0c\u5bd2\u65e0\u4ee5\u4e3a\u8863\uff0c\u53ea\u6015\u6ca1\u6709\u80fd\u4f3c\u94c1\u516c\u5b50 \u90a3\u6837\u7684\u9ad8\u4eba\u96c5\u81f4\u5462\uff01\u201d\u94c1\u955c\u5fc3\u88ab\u4ed6\u5632\u8bbd\uff0c\u6781\u4e0d\u8212\u670d\uff0c\u6c90\u71d5\u9053\uff1a\u201c\u5982\u6b64\u6e56\u5c71\u5982\u6b64\u591c\uff0c\u53ea\u5b9c\u628a\u9152 \u8bf4\u98ce\u82b1\u3002\u201d\u4e0e\u94c1\u955c\u5fc3\u76f8\u89c6\u4e00\u7b11\uff0c\u7709\u8bed\u76c8\u76c8\uff0c\u597d\u50cf\u662f\u8bf4\uff0c\u4f60\u4f55\u82e6\u4e0e\u201c\u4fd7\u4eba\u201d\u8ba1\u8f83\u3002\u94c1\u955c\u5fc3\u597d\u50cf \u88ab\u6170\u6597\u6170\u8fc7\u4e00\u6837\uff0c\u6709\u8bf4\u4e0d\u51fa\u7684\u8212\u670d\u3002\u672c\u6765\u60f3\u201c\u56de\u656c\u201d\u53f6\u6210\u6797\u51e0\u53e5\u7684\uff0c\u542c\u4e86\u6c90\u71d5\u7684\u6697\u529d\uff0c\u4e5f\u4e0d \u5c51\u8bf4\u4e86\u3002 \u3000\u3000\u53f6\u6210\u6797\u4e0d\u7406\u4f1a\u522b\u4eba\u7684\u9762\u8272\uff0c\u8bf4\u5f00\u4e86\u5934\uff0c\u53c8\u5f80\u4e0b\u7eed\u9053\uff1a\u201c\u53e4\u5f80\u4eca\u6765\uff0c\u56fa\u7136\u6709\u4e0d\u5c11\u6b8b\u6c11\u4ee5\u901e \u7684\u67ad\u96c4\uff0c\u4f46\u4e5f\u4e0d\u89c1\u5f97\u5c31\u6ca1\u6709\u771f\u5fc3\u771f\u610f\u62ef\u6c11\u4e8e\u6c34\u706b\u7684\u8c6a\u6770\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u8bdd\u8bf4\u6b63\u662f\uff0c\u4e16\u4e8b \u539f\u4e0d\u53ef\u4e00\u6982\u800c\u8bba\uff0c\u50cf\u4f60\u7684\u53d4\u53d4\uff0c\u6211\u770b\u4ed6\u5c31\u6ca1\u5b58\u6709\u4ec0\u4e48\u79c1\u5fc3\u3002\u201d\u94c1\u955c\u5fc3\u5bf9\u53f6\u5b97\u7559\u9887\u6709\u656c\u610f\uff0c\u542c \u4e8e\u627f\u73e0\u5c06\u4ed6\u4e3e\u4f8b\uff0c\u9ed8\u4e0d\u4f5c\u58f0\u3002\u53f6\u6210\u6797\u9053\uff1a\u201c\u5f20\u5927\u4fa0\uff0c\u957f\u6c5f\u4e0a\u7a7a\uff0c\u81ea\u4e0b\u6b63\u662f\u6218\u4e91\u6fc0\u6f2b\uff0c\u6b64\u5730\u7684 \u4e8b\u60c5\uff0c\u65e2\u7136\u544a\u4e86\u4e00\u4e2a\u6bb5\u843d\uff0c\u6211\u53d4\u53d4\u8fd8\u5728\u5019\u5f20\u5927\u4fa0\u7684\u56de\u97f3\u3002\u201d\u5f20\u4e39\u67ab\u60f3\u4e86\u4e00\u4e0b\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u6211\u4f1a\u56de\u5230\u6c5f\u5357\u53bb\u7684\uff0c\u4e0d\u8fc7\u987b\u5f97\u5f85\u94c1\u516c\u5b50\u89c1\u4e86\u6c90\u56fd\u516c\u4e4b\u540e\uff0c\u8fd9\u91cc\u7684\u4e8b\u60c5\u5b89\u6392\u5f97\u59a5\u5e16\u4e86\uff0c\u6211\u624d \u80fd\u534f\u52a9\u3002\u201d\u6c90\u71d5\u9053\uff1a\u201c\u94c1\u516c\u5b50\uff0c\u4f60\u5462\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u6211\u7eb5\u56de\u6c5f\u5357\uff0c\u4e5f\u7edd\u4e0d\u4e0e\u6bd5\u64ce\u5929\u4e4b\u6d41\u4e3a \u4f0d\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u5bf9\u53f6\u5927\u54e5\u5c31\u6ca1\u6709\u4e00\u70b9\u60c5\u4efd\u5417\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u53f6\u5b97\u7559\u5927\u54e5\u5bbd\u539a\u5f85\u4eba\uff0c \u6211\u7d20\u6765\u4f69\u670d\uff0c\u53ea\u662f\u4ed6\u592a\u8fc7\u5bbd\u539a\u4e86\uff0c\u53ea\u6015\u8981\u53d7\u6bd5\u64ce\u5929\u4e4b\u6d41\u7684\u611a\u5f04\u3002\u6211\u94c1\u67d0\u4eba\u5c82\u80fd\u53d7\u8349\u83bd\u72c2\u592b\u7684 \u53f7\u4ee4\u3002\u201d\u6c90\u71d5\u9053\uff1a\u201c\u662f\u554a\uff0c\u90a3\u4e48\uff0c\u90a3\u4e48\uff0c\u4f60\u2026\u2026\u201d\u60f3\u529d\u4ed6\u7559\u5728\u4e91\u5357\uff0c\u5ffd\u89c9\u53f6\u6210\u6797\u3001\u4e8e\u627f\u73e0\u7b49 \u4eba\u7684\u773c\u5149\u90fd\u96c6\u5728\u5979\u7684\u8eab\u4e0a\uff0c\u5979\u62bf\u5634\u4e00\u7b11\uff0c\u628a\u8bf4\u5230\u5507\u8fb9\u7684\u8bf4\u8bdd\u53c8\u541e\u56de\u53bb\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5bf9\u6bd5\u64ce\u5929\u6b8a\u65e0\u597d\u611f\uff0c\u4f46\u542c\u5f97\u94c1\u955c\u5fc3\u5982\u6b64\u8bf4\u6cd5\uff0c\u597d\u50cf\u548c\u8349\u83bd\u4e4b\u4eba\u4e3a\u4f0d\uff0c\u5c31\u5931\u6389\u4e86\u516c \u5b50\u8eab\u4efd\u4f3c\u7684\uff0c\u5fc3\u4e2d\u611f\u5230\u6781\u4e0d\u81ea\u7136\u3002\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u6211\u770b\u6bd5\u64ce\u5929\u5f88\u4e0d\u9519\u561b\uff0c\u4f60\u4eec\u600e\u7684\u603b\u4f3c\u5bf9\u4ed6 \u4e0d\u6ee1\u3002\u6211\u77e5\u9053\u4ed6\u5df2\u8bf7\u5468\u5c71\u6c11\u592b\u5987\u8fdb\u5173\u6765\u4e86\u3002\u55ef\uff0c\u4e91\u857e\uff0c\u77f3\u7fe0\u98ce\u5f88\u60f3\u89c1\u4f60\u5462\u3002\u201d\u4e91\u857e\u60f3\u8d77\u4ee5\u524d \u5973\u626e\u7537\u88c5\u548c\u77f3\u7fe0\u51e4\u4f5c\u5047\u51e4\u865a\u51f0\u7684\u4e8b\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u6211\u4e5f\u53ea\u597d\u968f\u4e39\u67ab\u8d70\u4e00\u8d9f\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u5fae \u7b11\u9053\uff1a\u201c\u597d\u5566\uff0c\u4e8b\u60c5\u5c31\u8fd9\u6837\u51b3\u5b9a\u3002\u4e0d\u8c08\u8fd9\u4e9b\u5927\u4e8b\u4e86\uff0c\u6c90\u59d1\u5a18\u8981\u4e0d\u9ad8\u5174\u5566\u3002\u201d\u6c90\u71d5\u9053\uff1a\u201c\u5e08\u7236 \u8bf4\u7b11\u4e86\uff0c\u4e0d\u8fc7\u5982\u6b64\u826f\u8fb0\u4f73\u666f\uff0c\u7684\u786e\u8fd8\u662f\u5c3d\u60c5\u8d4f\u73a9\u6e56\u5c71\u4e3a\u5999\u3002\u201d\u94c1\u955c\u5fc3\u89c1\u5979\u6709\u70b9\u5c34\u5c2c\uff0c\u966a\u5979\u8bf4 \u7b11\u4e86\u4e00\u4f1a\uff0c\u6c90\u71d5\u53c8\u6109\u5feb\u5982\u521d\u4e86\u3002\u4e8e\u627f\u73e0\u5374\u662f\u60c5\u6000\u52a8\u8361\uff0c\u4e0d\u80fd\u81ea\u4f11\u3002\u6708\u4eae\u900f\u8fc7\u4e91\u5c42\uff0c\u6708\u5f71\u6ce2 \u5149\uff0c\u7aef\u7684\u662f\u7389\u5b87\u65e0\u5c18\uff0c\u5c71\u6cb3\u660e\u51c0\uff0c\u6709\u51e0\u53ea\u6d77\u9e25\uff0c\u4e0d\u77e5\u662f\u8d2a\u604b\u6708\u534e\uff0c\u8fd8\u662f\u5c06\u6708\u5149\u8bef\u4f5c\u6668\u66e6\uff0c\u5140 \u81ea\u5728\u6d31\u6d77\u4e0a\u7a7a\u98de\u7fd4\u3002\u4e8e\u627f\u73e0\u5ffd\u5730\u60f3\u8d77\u5979\u79bb\u5f00\u53f0\u5dde\u7684\u90a3\u4e00\u4e2a\u65e9\u6668\uff0c\u66fe\u4e0b\u4e86\u51b3\u5fc3\u8981\u6254\u6389\u81ea\u5df1\u7684\u8bb0 \u5fc6\u7684\uff0c\u8981\u50cf\u51b2\u6ce2\u9010\u6d6a\u7684\u6d77\u9e25\u4e00\u6837\uff0c\u5c55\u7ffc\u51cc\u4e91\u3002\u90a3\u60c5\u666f\u4e0e\u4eca\u665a\u591a\u5c11\u76f8\u540c\uff0c\u5fc3\u60c5\u66f4\u5b8c\u5168\u4e00\u6837\u3002\u53ef \u662f\u5979\u8fd8\u662f\u629b\u4e0d\u5f00\u8fc7\u53bb\u7684\u8bb0\u5fc6\uff0c\u4e00\u4e2a\u5341\u516d\u5c81\u7684\u5c11\u5973\uff0c\u6b63\u50cf\u542b\u82de\u5f85\u653e\u7684\u82b1\uff0c\u4f60\u4e0d\u80fd\u671f\u671b\u5979\u5c31\u50cf\u5927 \u9752\u6811\u4e00\u6837\uff0c\u624e\u6839\u6df1\u5165\u6ce5\u571f\uff0c\u80fd\u72ec\u81ea\u62b5\u6321\u65e0\u60c5\u7684\u98ce\u96e8\u554a\u3002\u5440\uff0c\u7231\u60c5\u7684\u77db\u76fe\u4e0e\u82e6\u607c\uff0c\u8fd8\u5728\u6298\u78e8\u4e00 \u4e2a\u5341\u516d\u5c81\u5c11\u5973\u7684\u5fc3\u3002\u8fd9\u4e00\u665a\u4e8e\u627f\u73e0\u53c8\u662f\u5f7b\u591c\u65e0\u7720\uff0c\u94c1\u955c\u5fc3\u548c\u53f6\u6210\u6797\u7684\u5f71\u5b50\u53c8\u662f\u4ea4\u66ff\u5730\u5728\u5979\u8111 \u6d77\u4e2d\u6d6e\u73b0\u3002\u4e0d\u8fc7\u6709\u4e00\u70b9\u4e0d\u540c\u7684\u662f\uff1a\u5728\u4ee5\u5f80\uff0c\u5f53\u4e8e\u627f\u73e0\u60f3\u8d77\u8fd9\u4e24\u4e2a\u4eba\u7684\u65f6\u5019\uff0c\u4e0d\u7ba1\u5979\u600e\u6837\u4f69\u670d \u53f6\u6210\u6797\uff0c\u5230\u4e86\u6700\u540e\uff0c\u5374\u603b\u662f\u94c1\u955c\u5fc3\u7684\u5f71\u5b50\u5360\u636e\u4e86\u5979\u7684\u5fc3\u5934\uff1b\u4f46\u4eca\u665a\uff0c\u5f53\u7b2c\u4e00\u7ebf\u6668\u66e6\u900f\u5165\u7a97\u6237 \u7684\u65f6\u5019\uff0c\u53f6\u6210\u6797\u7684\u5f71\u5b50\u5374\u538b\u5012\u4e86\u94c1\u955c\u5fc3\uff0c\u4e8e\u627f\u73e0\u5728\u671d\u9633\u7684\u6e29\u6696\u4e2d\u4e5f\u7761\u7740\u4e86\u3002\u5f85\u5230\u4e8e\u627f\u73e0\u9192\u6765 \u7684\u65f6\u5019\uff0c\u94c1\u955c\u5fc3\u5df2\u7ecf\u9001\u6c90\u71d5\u59d0\u5f1f\u51fa\u57ce\u53bb\u4e86\u3002\u5c0f\u864e\u5b50\u544a\u8bc9\u4e8e\u627f\u73e0\uff0c\u8bf4\u662f\u94c1\u955c\u5fc3\u66fe\u6765\u5411\u5979\u8f9e\u884c\uff0c \u89c1\u5979\u5c1a\u5728\u68a6\u4e2d\uff0c\u53ea\u597d\u600f\u600f\u800c\u53bb\u3002\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u771f\u5947\u602a\uff0c\u53c8\u4e0d\u662f\u751f\u79bb\u6b7b\u522b\uff0c\u6211\u8fd8\u77a7\u89c1\u4ed6 \u5728\u5077\u5077\u5730\u62ed\u6cea\u5462\u3002\u201d\u4e8e\u627f\u73e0\u4e00\u9635\u5fc3\u9178\uff0c\u5fc3\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u662f\u60f3\u4e0e\u6211\u4f5c\u6700\u540e\u4e00\u6b21\u7684\u8bdd\u522b\uff01\u201d\u4e5f\u8bb8 \u4ee5\u540e\u8fd8\u4f1a\u89c1\u9762\u5427\uff1f\u4e5f\u8bb8\u8fd9\u5e76\u975e\u6700\u540e\u7684\u201c\u8bdd\u522b\u201d\u5427\uff1f\u4f46\u5728\u611f\u60c5\u4e0a\u4e8e\u627f\u73e0\u5374\u7684\u786e\u662f\u611f\u5230\u201c\u6c38\u8fdc\u5206 \u522b\u201d\u7684\u6ecb\u5473\uff0c\u6b63\u56e0\u5982\u6b64\uff0c\u5979\u6ca1\u6709\u8ba9\u94c1\u955c\u5fc3\u5f97\u5230\u201c\u8bdd\u522b\u201d\u7684\u673a\u4f1a\uff0c\u611f\u60c5\u4e0a\u603b\u597d\u50cf\u8fd8\u8d1f\u7740\u503a\u3002 \u3000\u3000\u8fc7\u4e86\u4e24\u65e5\uff0c\u94c1\u955c\u5fc3\u8fd8\u6ca1\u6709\u56de\u6765\uff0c\u5e26\u56de\u6765\u7684\u6d88\u606f\u8bf4\uff1a\u6c90\u56fd\u516c\u5927\u81f4\u540c\u610f\u6bb5\u6f84\u82cd\u7684\u6761\u4ef6\uff0c\u4f46\u8fd8 \u8981\u594f\u7980\u7687\u4e0a\u5b9a\u593a\uff0c\u540c\u65f6\u4e3a\u4e86\u6613\u4e8e\u4f20\u8fbe\u8d77\u89c1\uff0c\u6c90\u56fd\u516c\u63d0\u8bae\u7531\u6ce2\u65af\u9a78\u9a6c\u6bb5\u6f84\u82cd\u594f\u8bf7\u671d\u5ef7\u5c01\u8d60\uff0c\u56e0 \u4e3a\u5f02\u56fd\u516c\u4e3b\u548c\u9a78\u9a6c\u6765\u5f52\uff0c\u7b97\u5f97\u662f\u201c\u5723\u671d\u4f73\u8bdd\u201d\uff0c\u5c01\u6bb5\u5bb6\u4e3a\u738b\uff0c\u4e5f\u6709\u4e2a\u597d\u7684\u85c9\u53e3\uff0c\u8fd9\u79cd\u79cd\u8ba1 \u5212\uff0c\u8fd8\u5f97\u7b49\u5230\u6c90\u56fd\u516c\u56de\u6606\u660e\u4e4b\u540e\u518d\u8be6\u8c08\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u5f20\u4e39\u67ab\u5c06\u4e8e\u627f\u73e0\u5524\u5230\u8ddf\u524d\uff0c\u53ea\u89c1\u53f6\u6210\u6797\u548c\u6f6e\u97f3\u548c\u5c1a\u5df2\u6574\u88c5\u5f85\u53d1\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u73e0 \u513f\uff0c\u6211\u76ee\u524d\u8fd8\u4e0d\u80fd\u8d70\uff0c\u53ef\u80fd\u7b49\u94c1\u955c\u5fc3\u56de\u6765\u4e4b\u540e\u624d\u52a8\u8eab\u3002\u4f60\u613f\u610f\u7b49\u6211\u4eec\u4e00\u540c\u8d70\u5462\uff0c\u8fd8\u662f\u73b0\u5728\u5c31 \u8d70\uff1f\u201d\u4e8e\u627f\u73e0\u672c\u60f3\u8bf4\uff1a\u201c\u6211\u8ddf\u968f\u5e08\u7236\u3002\u201d\u4f46\u542c\u5f97\u5e08\u7236\u63d0\u8d77\u94c1\u955c\u5fc3\uff0c\u8e0c\u8e87\u4e86\u4e00\u9635\uff0c\u62ac\u5934\u8bf4\u9053\uff1a \u201c\u542c\u5e08\u7236\u5429\u5490\u3002\u201d\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u73b0\u5728\u8d70\u5427\u3002\u6211\u5df2\u7ed8\u597d\u4e86\u4e00\u4efd\u6c5f\u5357\u7684\u5730\u56fe\uff0c \u4f60\u5e26\u7ed9\u53f6\u5b97\u7559\uff0c\u53eb\u4ed6\u4e0d\u8981\u8d2a\u529f\uff0c\u6682\u65f6\u5b88\u7740\u6c5f\u5357\u7684\u5730\u76d8\u4fbf\u597d\u3002\u201d\u4e8e\u627f\u73e0\u63a5\u8fc7\u5730\u56fe\uff0c\u773c\u524d\u5ffd\u7136\u73b0 \u51fa\u6676\u83b9\u7684\u6cea\u73e0\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4f60\u4eec\u8d70\u5427\u3002\u55ef\uff0c\u8fd9\u91cc\u6709\u4e00\u5305\u5927\u9752\u6811\u7684\u79cd\u5b50\uff0c\u6210\u6797\uff0c\u4f60\u5e26\u5230\u6c5f\u5357 \u53bb\uff0c\u770b\u5b83\u5728\u957f\u6c5f\u4e24\u5cb8\u80fd\u4e0d\u80fd\u751f\u957f\uff1f\u201d\u53f6\u6210\u6797\u6014\u4e86\u4e00\u6014\uff0c\u832b\u7136\u63a5\u8fc7\u79cd\u5b50\u3002\u5f20\u4e39\u67ab\u7b11\u4e86\u4e00\u7b11\uff0c\u4f46 \u89c1\u4e8e\u627f\u73e0\u5df2\u62ed\u4e86\u6cea\u75d5\uff0c\u968f\u7740\u4ed6\u4eec\u8d70\u4e86\u3002\u6b63\u662f\uff1a \u3000\u3000\u957f\u6c5f\u7eb5\u6709\u98ce\u6ce2\u6076\uff0c\u5927\u6811\u76d8\u6839\u53ef\u62a4\u82b1\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u516d\u56de\u3000\u8e0f\u96ea\u795e\u9a79\u3000\u65c5\u9014\u4f20\u8b66\u62a5\u3000\u51cc\u4e91\u4e00\u51e4\u3000\u534a\u9053\u52ab\u9556\u94f6 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u516d\u56de\u3000\u8e0f\u96ea\u795e\u9a79\u3000\u65c5\u9014\u4f20\u8b66\u62a5\u3000\u51cc\u4e91\u4e00\u51e4\u3000\u534a\u9053\u52ab\u9556\u94f6 \u3000\u3000\u4e00\u4e2a\u591a\u6708\u4e4b\u540e\uff0c\u6f6e\u97f3\u548c\u5c1a\u3001\u53f6\u6210\u6797\u548c\u4e8e\u627f\u73e0\u4e09\u4eba\uff0c\u5df2\u7a7f\u8fc7\u4e86\u4e91\u8d35\u9ad8\u539f\uff0c\u53d6\u9053\u6e56\u5357\uff0c\u8fdb\u5165 \u4e86\u6c5f\u897f\u5c71\u533a\uff0c\u53f6\u5b97\u7559\u7684\u5175\u529b\u5360\u636e\u7740\u6d59\u6c5f\u3001\u6c5f\u82cf\u3001\u798f\u5efa\u4e09\u7701\u7684\u6cbf\u6d77\u5730\u5e26\uff0c\u53ea\u8981\u8fc7\u4e86\u6c5f\u897f\uff0c\u8fdb\u5165 \u6d59\u6c5f\uff0c\u90a3\u4fbf\u662f\u53f6\u5b97\u7559\u7684\u52bf\u529b\u8303\u56f4\u4e86\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7231\u62a4\u5f92\u5f1f\uff0c\u4ecd\u7136\u628a\u90a3\u5339\u7167\u591c\u72ee\u5b50\u9a6c\u8ba9\u7ed9\u4e8e\u627f\u73e0\u4e58\u5750\uff0c\u6f6e\u97f3\u548c\u5c1a\u7684\u5750\u9a91\u4e5f\u662f\u4e00\u5339\u5b9d \u9a6c\uff0c\u53ea\u6709\u53f6\u6210\u6797\u7684\u9a6c\u5339\u8f83\u5dee\uff0c\u4f46\u4e5f\u662f\u6bb5\u738b\u7237\u6240\u9001\u7684\u5927\u7406\u540d\u9a6c\uff0c\u8d63\u5357\u867d\u7136\u662f\u5c71\u533a\uff0c\u4f46\u6bd4\u8d77\u4e91\u8d35 \u9ad8\u539f\uff0c\u5df2\u7b97\u5f97\u662f\u5766\u9014\u4e86\uff0c\u4ee5\u90a3\u4e09\u5339\u9a6c\u7684\u811a\u7a0b\u6765\u770b\uff0c\u5927\u7ea6\u4e0d\u9700\u5341\u65e5\u4fbf\u53ef\u56de\u5230\u6d59\u6c5f\uff0c\u7ecf\u8fc7\u4e2a\u591a\u6708 \u8270\u82e6\u7684\u65c5\u7a0b\uff0c\u8fd9\u65f6\u624d\u677e\u4e86\u53e3\u6c14\uff0c\u4e09\u4e2a\u4eba\u7684\u5fc3\u60c5\u90fd\u8212\u7545\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u4e2a\u591a\u6708\uff0c\u4e8e\u627f\u73e0\u4e0e\u53f6\u6210\u6797\u867d\u662f\u671d\u5915\u76f8\u5bf9\uff0c\u4f46\u53f6\u6210\u6797\u6c89\u9ed8\u5be1\u8a00\uff0c\u53c8\u6709\u6f6e\u97f3\u548c\u5c1a\u8fd9\u4e48\u4e00 \u4e2a\u957f\u8f88\u540c\u5728\u4e00\u8d77\uff0c\u9664\u4e86\u6709\u65f6\u8c08\u8bba\u4e00\u4e9b\u6b66\u6797\u6545\u4e8b\u4e4b\u5916\uff0c\u4e8e\u3001\u53f6\u4e8c\u4eba\u6781\u5c11\u79c1\u4e0b\u4ea4\u8c08\uff0c\u4e8e\u627f\u73e0\u7684\u5fc3 \u4e8b\u66f4\u6ca1\u6709\u5728\u53f6\u6210\u6797\u8ddf\u524d\u900f\u9732\u8fc7\u534a\u70b9\u3002\u53f6\u6210\u6797\u867d\u7136\u6709\u65f6\u4ece\u4e8e\u627f\u73e0\u7d27\u9501\u7684\u53cc\u7709\uff0c\u731c\u5230\u5979\u5fc3\u4e2d\u6709\u6240 \u82e6\u607c\uff0c\u53ef\u662f\u4e8e\u627f\u73e0\u4e0d\u8bf4\uff0c\u53f6\u6210\u6797\u4ed6\u4ece\u4e0d\u6562\u95ee\u3002\u4e0d\u77e5\u600e\u7684\uff0c\u79bb\u5f00\u4e86\u94c1\u955c\u5fc3\u4e4b\u540e\uff0c\u4e8e\u627f\u73e0\u53cd\u800c\u6709 \u65f6\u6302\u5ff5\u8d77\u4ed6\uff0c\u5c24\u5176\u6bcf\u4e0e\u53f6\u6210\u6797\u548c\u5979\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u94c1\u955c\u5fc3\u7684\u5f71\u5b50\u66f4\u4f1a\u7a81\u7136\u5730\u4ece\u8111\u6d77\u4e2d\u6d6e\u8d77\u3002 \u3000\u3000\u5230\u4e86\u6c5f\u897f\uff0c\u6cbf\u9014\u6240\u6709\u7684\u90fd\u662f\u9003\u96be\u7684\u4eba\u4eec\uff0c\u539f\u6765\u5b98\u519b\u51c6\u5907\u5357\u5317\u5939\u653b\uff0c\u6709\u4e00\u652f\u5927\u519b\u6b63\u4ece\u6e56\u5317 \u5357\u4e0b\u6c5f\u897f\uff0c\u6240\u4ee5\u63a5\u8fd1\u6218\u533a\u7684\u6c5f\u897f\u4e1c\u5317\u90e8\u7684\u8001\u767e\u59d3\u7eb7\u7eb7\u907f\u96be\uff0c\u5341\u5ba4\u4e5d\u7a7a\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u4ed6\u4eec\u8fc7\u4e86\u6c38\u4e30\uff0c\u4e3a\u7740\u8d76\u8def\uff0c\u9519\u8fc7\u5bbf\u5934\uff0c\u508d\u665a\u65f6\u5206\uff0c\u5230\u4e86\u4e00\u4e2a\u8352\u6751\uff0c\u4f46\u89c1\u5bb6\u5bb6\u95ed \u6237\uff0c\u8352\u65e0\u4eba\u70df\uff0c\u4e09\u4eba\u5728\u4e00\u4e2a\u53e4\u5e99\u4e2d\u6b47\u811a\uff0c\u65f6\u8282\u5df2\u5165\u521d\u51ac\uff0c\u5c71\u533a\u5bd2\u98ce\u51db\u51bd\uff0c\u6240\u5e26\u7684\u5e72\u7cae\u6070\u5de7\u53c8 \u5403\u5b8c\u4e86\uff0c\u8def\u4e0a\u65e0\u5904\u6dfb\u8d2d\uff0c\u4e09\u4eba\u90fd\u611f\u89c9\u5230\u6709\u70b9\u9965\u51b7\u3002 \u3000\u3000\u53f6\u6210\u6797\u60f3\u53bb\u649e\u649e\u8fd0\u6c14\uff0c\u770b\u6751\u4e2d\u6709\u54ea\u4e00\u5bb6\u8fd8\u672a\u9003\u8d70\u7684\uff0c\u6c42\u5bbf\u4e00\u5bb5\uff0c\u6216\u8005\u4e70\u4e9b\u98df\u7269\u3002\u6f6e\u97f3\u548c \u5c1a\u7b11\u9053\uff1a\u201c\u6284\u5316\u662f\u548c\u5c1a\u7684\u4e8b\u60c5\uff0c\u5f85\u6211\u53bb\u5427\u3002\u201d\u4e0d\u7531\u5206\u8bf4\uff0c\u62ab\u8d77\u8888\u88df\uff0c\u5306\u5306\u51fa\u95e8\u3002 \u3000\u3000\u53f6\u6210\u6797\u62fe\u4e86\u4e00\u4e9b\u67af\u679d\uff0c\u5728\u5e99\u4e2d\u751f\u8d77\u706b\u6765\uff0c\u4f46\u89c1\u4e8e\u627f\u73e0\u53cc\u988a\u6655\u7ea2\uff0c\u4e0d\u77e5\u662f\u88ab\u706b\u5149\u6620\u7ea2\u7684\uff0c \u8fd8\u662f\u5979\u5fc3\u4e2d\u6b63\u5728\u60f3\u7740\u4ec0\u4e48\u4e8b\u60c5\u3002\u53f6\u6210\u6797\u5446\u4e86\u4e00\u5446\uff0c\u51d1\u8fd1\u67f4\u706b\uff0c\u9053\uff1a\u201c\u5929\u5bd2\u5730\u51bb\uff0c\u8fde\u65e5\u6765\u4f60\u8f9b \u82e6\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u7b97\u5f97\u4ec0\u4e48\uff1f\u6211\u53c8\u4e0d\u662f\u672a\u51fa\u8fc7\u95e8\u7684\u5a07\u751f\u60ef\u517b\u7684\u5c0f\u59d0\u3002\u201d\u5ffd\u800c\u60f3\u8d77\u6606 \u660e\uff0c\u6606\u660e\u56db\u5b63\u5982\u6625\uff0c\u94c1\u955c\u5fc3\u8fd9\u65f6\u4e5f\u8bb8\u6b63\u5728\u56fd\u516c\u5e9c\u91cc\u548c\u6c90\u71d5\u996e\u9152\u8d4f\u6885\u3002\u548c\u8fd9\u91cc\u7684\u60c5\u666f\u90a3\u662f\u5927\u4e0d \u76f8\u540c\u4e86\u3002 \u3000\u3000\u53f6\u6210\u6797\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u8fd9\u6837\u5b50\uff0c\u5f88\u5feb\u5c31\u4f1a\u6253\u8d77\u5927\u4ed7\u6765\u3002\u5f20\u5927\u4fa0\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u624d\u6765\uff0c \u6211\u7684\u53d4\u53d4\u4e00\u5b9a\u7126\u6025\u6781\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u4e5f\u76fc\u671b\u5e08\u7236\u5feb\u6765\uff0c\u5728\u4ed6\u7684\u8eab\u8fb9\uff0c\u4eba\u4e5f\u4f3c\u591a \u4e86\u51e0\u5206\u4e3b\u610f\u4f3c\u7684\u3002\u201d\u53f6\u6210\u6797\u62ac\u8d77\u5934\u6765\uff0c\u53ea\u89c1\u5979\u9762\u4e0a\u6709\u4e00\u6d3e\u5f77\u5fa8\u7684\u795e\u8272\uff0c\u597d\u50cf\u8ff7\u9014\u7684\u5b69\u5b50\u4e00 \u6837\u3002 \u3000\u3000\u53f6\u6210\u6797\u4e0d\u89c9\u53c8\u6014\u4e86\u4e00\u6014\uff0c\u63e3\u6d4b\u4e8e\u627f\u73e0\u8bf4\u8fd9\u53e5\u8bdd\u7684\u610f\u601d\u3002\u4e8e\u627f\u73e0\u770b\u4e86\u53f6\u6210\u6797\u4e00\u773c\uff0c\u7f13\u7f13\u5730 \u4f4e\u4e0b\u5934\u53bb\uff0c\u5fc3\u4e2d\u7740\u6709\u6240\u601d\uff0c\u53ea\u987e\u70d8\u706b\u3002\u53f6\u6210\u6797\u642d\u8baa\u8bf4\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u4f46\u613f\u94c1\u955c\u5fc3\u4e5f\u80fd\u591f\u548c\u5f20 \u5927\u4fa0\u4e00\u540c\u56de\u6765\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u55ef\uff0c\u94c1\u955c\u5fc3\uff0c\u4ed6\uff0c\u4ed6\u6050\u6015\u4e0d\u4f1a\u6765\u4e86\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u6211\u53d4\u53d4 \u4e00\u5411\u656c\u91cd\u4ed6\uff0c\u8bf4\u4ed6\u6587\u6b66\u5168\u624d\uff0c\u66f4\u517c\u719f\u8bfb\u5175\u4e66\uff0c\u7cbe\u901a\u97ec\u7565\uff0c\u4e49\u519b\u4e2d\u5c31\u7f3a\u5c11\u8fd9\u6837\u7684\u4eba\u6750\u3002\u5c31\u6015\u4ed6 \u4e0d\u80af\u7ea1\u5c0a\u964d\u8d35\uff0c\u5c48\u8eab\u8349\u83bd\u4e4b\u4e2d\u3002\u201d\u4e8e\u627f\u73e0\u542c\u53f6\u6210\u6797\u4e0d\u4f4f\u5730\u79f0\u8d5e\u94c1\u955c\u5fc3\uff0c\u7981\u4e0d\u4f4f\u60f3\u8d77\u94c1\u955c\u5fc3\u66fe \u5728\u5979\u9762\u524d\u8ba5\u8bee\u8fc7\u53f6\u6210\u6797\u7c97\u9119\u65e0\u6587\u7684\u8bf4\u8bdd\uff0c\u5176\u5b9e\u53f6\u6210\u6797\u7684\u6587\u624d\u867d\u7136\u8fdc\u4e0d\u5982\u94c1\u955c\u5fc3\uff0c\u5374\u4e5f\u4e0d\u81f3\u4e8e \u50cf\u4ed6\u6240\u8bf4\u4e4b\u751a\u3002\u8fd9\u970e\u90a3\u95f4\uff0c\u4e8e\u627f\u73e0\u5ffd\u7136\u6709\u4e00\u4e2a\u5947\u5f02\u7684\u611f\u89c9\uff0c\u53f6\u6210\u6797\u867d\u7136\u662f\u4e00\u4e2a\u77ff\u5de5\u7684\u513f\u5b50\uff0c \u4f46\u597d\u50cf\u6bd4\u51fa\u8eab\u5728\u201c\u4e66\u9999\u95e8\u7b2c\u201d\u7684\u94c1\u955c\u5fc3\u8fd8\u201c\u9ad8\u8d35\u201d\u5f97\u591a\u3002 \u3000\u3000\u5929\u8272\u6c89\u9ed1\uff0c\u6709\u51e0\u53ea\u591c\u67ad\u4f4e\u9e23\u98de\u8fc7\u5e99\u53bb\uff0c\u6f6e\u97f3\u548c\u5c1a\u5df2\u53bb\u4e86\u8bb8\u4e45\uff0c\u8fd8\u672a\u56de\u6765\u3002\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u54a6\uff0c\u600e\u4e48\u8fd8\u672a\u56de\u6765\uff1f\u83ab\u975e\u4ed6\u8001\u4eba\u5bb6\u53c8\u95ef\u51fa\u7978\u6765\u4e86\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u5e08\u4f2f\u7956\u6b66\u529f\u8d85\u5353\uff0c\u5728\u8fd9 \u8352\u6751\u4e2d\u8fd8\u80fd\u5931\u4e8b\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u8001\u4eba\u5bb6\u6709\u70b9\u83bd\u95ef\uff0c\u53c8\u559c\u6b22\u7ba1\u95f2\u4e8b\uff0c\u5012\u4e0d\u6015\u4ed6\u88ab\u4ec0\u4e48\u7ea2 \u5dfe\u5973\u8d3c\u6349\u53bb\uff0c\u800c\u662f\u6015\u4ed6\u88ab\u4ec0\u4e48\u95f2\u4e8b\u7eca\u4f4f\u4e86\u3002\u201d\u539f\u6765\u5728\u8def\u4e0a\u4ed6\u4eec\u66fe\u542c\u4eba\u8bf4\uff0c\u5939\u5728\u5b98\u519b\u533a\u57df\u548c\u4e49 \u519b\u533a\u57df\u7684\u4e2d\u95f4\u5730\u5e26\uff0c\u6709\u4e00\u4e2a\u7ea2\u5dfe\u5973\u8d3c\u5360\u5c71\u4e3a\u738b\uff0c\u5341\u5206\u5389\u5bb3\uff0c\u6545\u6b64\u4e8e\u627f\u73e0\u62ff\u6b64\u8bf4\u7b11\u3002 \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u6f6e\u97f3\u548c\u5c1a\u54c8\u54c8\u5927\u7b11\uff0c\u63a8\u5f00\u5e99\u95e8\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u5c0f\u5a03\u5a03\u5728\u80cc\u540e \u8bae\u8bba\u6211\u4ec0\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d\u6562\u3002\u201d\u62ac\u8d77\u5934\u6765\uff0c\u53ea\u89c1\u6f6e\u97f3\u548c\u5c1a\u6276\u7740\u4e00\u4e2a\u9e51\u8863\u767e\u7ed3\u7684\u53eb\u5316 \u5b50\uff0c\u8dcc\u8dcc\u649e\u649e\u5730\u8d70\u8fdb\u6765\u3002\u8fd9\u4e8b\u60c5\u5927\u51fa\u4e8e\u627f\u73e0\u610f\u5916\uff0c\u8fd9\u53eb\u5316\u4e8e\u539f\u6765\u7adf\u662f\u6bd5\u64ce\u5927\u7684\u5f1f\u5f1f\u6bd5\u613f\u7a77\u3002 \u3000\u3000\u6bd5\u613f\u7a77\u8863\u895f\u67d3\u6709\u8840\u8ff9\uff0c\u9762\u4e0a\u900f\u7740\u9ed1\u6c14\uff0c\u4f3c\u4e4e\u53d7\u4f24\u4e0d\u6d45\uff0c\u4f46\u4ecd\u662f\u90a3\u526f\u6ed1\u7a3d\u7684\u6a21\u7965\uff0c\u53ea\u89c1\u4ed6 \u5c48\u4e86\u534a\u819d\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u53eb\u5316\u5b50\u7684\u817f\u7ed9\u4eba\u5bb6\u6253\u8ddb\u5566\uff0c\u6ca1\u6cd5\u7ed9\u4f60\u59d1\u5976\u5976\u4e0b\u8dea\u8bf7\u5b89\u5566\uff01\u201d\u4e8e\u627f\u73e0 \u95ee\u9053\uff1a\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u4f46\u89c1\u6f6e\u97f3\u548c\u5c1a\u628a\u6bd5\u613f\u7a77\u653e\u5012\u5730\u4e0a\uff0c\u53cc\u6307\u4e00\u5939\uff0c\u5728\u4ed6\u817f\u5f2f\u5904\u8d77\u51fa\u4e86\u4e00\u679a \u4e94\u5bf8\u6765\u957f\u7684\u94a2\u9489\uff0c\u53eb\u9053\uff1a\u201c\u662f\u5440\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u4f60\u600e\u4e48\u4e2d\u4e86\u91d1\u9488\u5723\u624b\u97e9\u8001\u9556\u5934\u7684\u6bd2\u9488\uff1f\u201d \u6bd5\u613f\u7a77\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\uff0c\u4f60\u8d76\u5feb\u7ed9\u6211\u5c06\u90a3\u8001\u5bb6\u4f19\u6c40\u53d1\u4e86\u5427\uff01\u201d\u8bdd\u58f0\u5636\u5636\uff0c\u663e\u7136\u662f\u5fcd\u7740\u75db\u695a\uff0c \u90a3\u6545\u4f5c\u6ed1\u7a3d\u7684\u7b11\u5bb9\u66f4\u4ee4\u4eba\u611f\u5230\u96be\u53d7\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u97e9\u8001\u9556\u5934\u662f\u4e00\u4e2a\u6b63\u6d3e\u7684\u9556\u884c\u4eba\uff0c\u55ef\uff0c\u4f60\u4eec\u600e\u4e48\u548c\u4ed6\u8fc7\u4e0d\u53bb\uff1f \u4e24\u8fb9\u90fd\u662f\u670b\u53cb\uff0c\u8fd9\u4e8b\u60c5\u6211\u4e5f\u4e0d\u77e5\u600e\u4e48\u5566\uff1f\u54ce\u5440\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u4f46\u89c1\u6bd5\u613f\u7a77\u773c\u775b\u7ffb\u767d\uff0c\u624b\u6307\u5916 \u9762\uff0c\u53e3\u8bf4\u51fa\u4e24\u4e2a\u201c\u6025\u201d\u5b57\uff0c\u6f6e\u97f3\u548c\u5c1a\u6025\u5fd9\u66ff\u4ed6\u5212\u5f00\u4f24\u53e3\uff0c\u6324\u51fa\u9ed1\u8840\uff0c\u4e00\u9762\u53eb\u9053\uff1a\u201c\u627f\u73e0\uff0c\u4f60 \u7ed9\u6211\u8dd1\u4e00\u8d9f\uff0c\u770b\u4ed6\u4eec\u95f9\u7684\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5c31\u5728\u524d\u9762\u90a3\u4e2a\u5c71\u53e3\uff0c\u6709\u4e00\u7fa4\u4eba\u6253\u67b6\uff0c\u4f60\u7ed9\u6211\u62ff\u5de6\u610f\uff0c\u8be5 \u529d\u67b6\u7684\u5c31\u529d\uff0c\u4e0d\u8ba9\u529d\u7684\u5c31\u6492\u624b\u4e0d\u7ba1\uff0c\u54c8\u54c8\uff0c\u4f60\u4eec\u522b\u4ee5\u4e3a\u6211\u662f\u7231\u7ba1\u95f2\u4e8b\u7684\u4eba\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u5e08\u4f2f\u7956\u653e\u5fc3\uff0c\u6211\u4e0d\u7ed9\u4f60\u60f9\u4e8b\u4fbf\u662f\u3002\u53f6\u5927\u54e5\uff0c\u4f60\u505a\u4e8b\u628a\u7a33\uff0c\u966a\u6211\u8d70\u4e00\u8d9f \u5427\u3002\u201d\u4e24\u4eba\u5954\u5230\u6751\u5934\uff0c\u53ea\u89c1\u524d\u9762\u5c71\u5773\u4e4b\u95f4\uff0c\u679c\u7136\u6709\u4e00\u5806\u4eba\u53ae\u6740\u3002 \u3000\u3000\u53f6\u6210\u6797\u653e\u7f13\u811a\u6b65\uff0c\u9053\uff1a\u201c\u8fd9\u4e8b\u60c5\u53ef\u6709\u70b9\u53e4\u602a\uff0c\u54b1\u4eec\u5148\u77a7\u77a7\u518d\u8bf4\u3002\u201d\u4f46\u89c1\u9556\u884c\u7684\u9a91\u9a6c\u90fd\u5012 \u5728\u5730\u4e0a\uff0c\u53eb\u58f0\u51c4\u5389\uff0c\u4e00\u4e2a\u4e2a\u6a1f\u6c34\u7bb1\u7b3c\u5806\u5f97\u50cf\u5c0f\u5c71\u4f3c\u7684\uff0c\u9556\u884c\u4eba\u56f4\u5728\u56db\u5468\uff0c\u7bb1\u9876\u6709\u4e00\u4e2a\u8001\u9556\u5934 \u76d8\u819d\u800c\u5750\uff0c\u62ff\u7740\u65f1\u70df\u7ba1\uff0c\u4e00\u53e3\u4e00\u53e3\u5730\u55b7\u7740\u6d53\u70df\u3002\u52ab\u9556\u7684\u4e43\u662f\u4e00\u7fa4\u4e5e\u4e10\uff0c\u4e2a\u4e2a\u9a91\u7740\u5065\u9a6c\uff0c\u5411\u9556 \u884c\u7684\u4eba\u51b2\u51fb\uff0c\u9556\u884c\u7684\u4eba\u770b\u770b\u5b88\u4e0d\u4f4f\u4e86\uff0c\u90a3\u8001\u9556\u5934\u628a\u624b\u4e00\u626c\uff0c\u55e4\u55e4\u4e4b\u58f0\u7834\u7a7a\u800c\u51fa\uff0c\u7fa4\u4e10\u62e8\u8f6c\u9a6c \u5934\u4fbf\u8dd1\uff0c\u8fc7\u4e86\u4e00\u4f1a\u53c8\u653b\u4e0a\u6765\u3002\u770b\u60c5\u5f62\u662f\u9887\u4e3a\u5fcc\u60ee\u90a3\u8001\u9556\u5934\u7684\u91d1\u9488\u6697\u5668\uff0c\u60f3\u5f15\u90a3\u8001\u9556\u5934\u628a\u6697\u5668 \u53d1\u5b8c\u4e86\uff0c\u518d\u5927\u4e3e\u52ab\u9556\u3002 \u3000\u3000\u90a3\u8001\u9556\u5934\u559d\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4e10\u5e2e\u7684\u5417\uff1f\u201d\u4e3a\u9996\u7684\u4e00\u4e2a\u58ee\u4e10\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u7136\u77e5\u9053\uff0c\u8fd9\u4e2a\u4ea4\u60c5 \u4f60\u600e\u4e48\u8fd8\u4e0d\u80af\u5356\u5462\uff1f\u5c06\u89e3\u836f\u4ea4\u51fa\uff0c\u9556\u94f6\u7559\u4e0b\uff0c\u54c8\u54c8\uff0c\u54b1\u4eec\u7edd\u4e0d\u4f1a\u628a\u4f60\u96be\u4e3a\u3002\u201d\u90a3\u8001\u9556\u5934\u559d \u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u60f3\u4e10\u5e2e\u7684\u6bd5\u5e2e\u4e3b\u73b0\u5728\u5df2\u662f\u5929\u4e0b\u5341\u516b\u7701\u7684\u5927\u9f99\u5934\uff0c\u4ed6\u5c82\u4f1a\u52ab\u5c0f\u8001\u513f\u533a\u533a\u8fd9\u4e00\u652f \u9556\uff1f\u4f60\u4eec\u5206\u660e\u662f\u5192\u540d\u7684\u3002\u90a3\u4e2a\u662f\u5934\u9886\uff1f\u201d\u524d\u5934\u8bf4\u8bdd\u7684\u90a3\u4e2a\u9053\uff1a\u201c\u4f60\u8981\u4e0d\u4fe1\uff0c\u8fd9\u4e5f\u8bbe\u6cd5\u3002\u628a\u9556 \u94f6\u7559\u4e0b\u4e86\uff0c\u6211\u518d\u548c\u4f60\u8bf4\u3002\u201d\u90a3\u8001\u9556\u5934\u6012\u9053\uff1a\u201c\u97e9\u5bb6\u9556\u5c40\u5c82\u6709\u62f1\u624b\u5949\u9001\u9556\u94f6\u4e4b\u7406\uff0c\u54fc\uff0c\u54fc\uff0c\u9ed1 \u9053\u4e0a\u52ab\u9556\uff0c\u4e8b\u4ea6\u5e38\u6709\uff0c\u5374\u4ece\u6ca1\u6709\u50cf\u4f60\u4eec\u8fd9\u4f19\u7684\u4e0b\u6d41\u884c\u5f84\u3002\u6697\u4e2d\u4e0b\u6bd2\uff0c\u628a\u7272\u53e3\u5bb3\u4e86\uff0c\u5982\u6b64\u884c \u4e3a\uff0c\u4e0d\u6015\u4ee4\u6c5f\u6e56\u4e0a\u9f7f\u51b7\u4e48\uff1f\u5c45\u7136\u6562\u5192\u5145\u662f\u4e10\u5e2e\u7684\uff1f\u4eca\u65e5\u6211\u975e\u628a\u4f60\u63ea\u53bb\u89c1\u6bd5\u64ce\u5929\u4e0d\u53ef\uff0c\u770b\u6211\u80af \u9976\u4f60\uff0c\u6bd5\u64ce\u5929\u4e5f\u4e0d\u80af\u9976\u4f60\uff01\u201d\u90a3\u5934\u76ee\u54c8\u54c8\u5927\u7b11\u53eb\u9053\uff1a\u201c\u6211\u7b49\u7740\u4f60\u8001\u63ea\u5462\uff01\u201d\u653e\u9a6c\u76f4\u51b2\uff0c\u90a3\u8001 \u9556\u5934\u4e00\u626c\u624b\uff0c\u4ed6\u62e8\u8f6c\u9a6c\u5934\u53c8\u8dd1\uff0c\u91d1\u9488\u4e0d\u80fd\u53ca\u8fdc\uff0c\u8fd9\u4f19\u4e5e\u4e10\u9a91\u672f\u7cbe\u7edd\uff0c\u91d1\u9488\u81ea\u662f\u8ffd\u4ed6\u4eec\u4e0d\u4e0a\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u771f\u5947\u4e86\uff0c\u6bd5\u64ce\u5929\u4e3a\u4ec0\u4e48\u8981\u52ab\u97e9\u5bb6\u9556\u5c40\u7684\u9556\uff1f\u542c\u97e9\u8001\u9556\u5934\u9a82\u4ed6\u7684\u8bdd\uff0c \u6211\u4e5f\u66ff\u4ed6\u96be\u8fc7\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u771f\u662f\u6bd5\u64ce\u5929\u6d3e\u6765\u7684\u4eba\u3002\u201d\u770b\u6765\u4ed6\u4e5f\u4e0d\u5927\u76f8\u4fe1\u3002\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u7edd\u5bf9\u4e0d\u4f1a\u5192\u5145\uff0c\u6bd5\u613f\u7a77\u662f\u6bd5\u64ce\u5929\u6700\u4eb2\u4fe1\u7684\u4eba\uff0c\u8fd9\u4e2a\u5927\u5934\u76ee\u59d3\u767d\uff0c\u6211\u4e5f\u8ba4\u5f97\u3002\u800c\u4e14\u5f04\u5012\u4eba\u5bb6 \u7684\u7272\u53e3\uff0c\u8fd9\u4e5f\u6b63\u662f\u6bd5\u64ce\u5927\u7684\u62ff\u624b\u672c\u9886\u3002\u6211\u4ee5\u524d\u4e5f\u5403\u8fc7\u4ed6\u7684\u4e8f\uff0c\u4ed6\u60f3\u628a\u6211\u7559\u4e0b\uff0c\u628a\u6211\u7684\u7167\u591c\u72ee \u5b50\u9a6c\u4e5f\u5f04\u5f97\u51e0\u4e4e\u4e0d\u80fd\u884c\u8d70\u5462\uff01\u201d\u53f6\u6210\u6797\u6447\u4e86\u6447\u5934\uff0c\u7528\u8fd9\u79cd\u624b\u6bb5\u52ab\u9556\uff0c\u786e\u5b9e\u6709\u6b20\u5149\u660e\u78ca\u843d\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u97e9\u8001\u9556\u5934\u4e48\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u672a\u66fe\u89c1\u8fc7\u3002\u53ea\u542c\u53d4\u53d4\u8bf4\uff0c\u8fd9\u4eba\u7b97\u5f97\u662f \u9556\u884c\u4e2d\u7b2c\u4e00\u4e2a\u4eba\u7269\uff0c\u4e0d\u6b62\u662f\u7531\u4e8e\u4ed6\u6b66\u827a\u9ad8\u5f3a\uff0c\u800c\u662f\u4ed6\u6700\u91cd\u4e49\u6c14\u3002\u4ed6\u6709\u4e09\u4e0d\u4fdd\uff0c\u6765\u5386\u4e0d\u660e\u7684\u4e0d \u4fdd\uff0c\u8d3c\u8d43\u4e0d\u4fdd\uff0c\u8d2a\u5b98\u4e0d\u4fdd\u3002\u4f46\u53ea\u8981\u4ed6\u7b54\u5e94\u4fdd\u4e86\uff0c\u90a3\u5c31\u4e07\u5143\u4e00\u5931\u3002\u9ed1\u3001\u767d\u4e24\u9053\u7684\u670b\u53cb\u90fd\u5356\u4ed6\u7684 \u4ea4\u60c5\u3002\u4e0d\u77e5\u9053\u6bd5\u64ce\u5929\u4f55\u6545\u8981\u4e0e\u4ed6\u4e3a\u96be\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u5f88\u5c11\u81ea\u5df1\u8d70\u9556\uff0c\u8fd9\u56de\u4eb2\u81ea\u51fa \u9a6c\uff0c\u770b\u6765\u6240\u4fdd\u7684\u9556\u975e\u6bd4\u5bfb\u5e38\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u5c31\u7b97\u4ed6\u4fdd\u7684\u662f\u591a\u5927\u94f6\u5b50\uff0c\u6bd5\u64ce\u5929\u73b0\u5728\u662f\u4e49\u519b\u7edf \u5e05\uff0c\u6309\u7406\u4e5f\u4e0d\u8be5\u53bb\u52ab\u4ed6\u9556\u94f6\u3002\u201d\u8fd9\u4e8b\u60c5\u771f\u662f\u53e4\u602a\u4e4b\u6781\uff01\u4e8e\u3001\u53f6\u4e8c\u4eba\u867d\u7136\u806a\u660e\u900f\u9876\uff0c\u4e5f\u662f\u767e\u601d \u4e0d\u5f97\u5176\u89e3\uff01 \u3000\u3000\u4e24\u4eba\u6b63\u5728\u7a83\u7a83\u79c1\u8bae\uff0c\u5ffd\u542c\u5f97\u4e10\u5e2e\u7684\u4eba\u7eb7\u7eb7\u53eb\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u8fd9\u8001\u513f\u7684\u6697\u5668\u53d1\u5b8c\u5566\u3002\u201d\u201c\u5e76 \u80a9\u5b50\u653b\u4e0a\u53bb\u554a\uff01\u201d\u201c\u7ed9\u4ed6\u7559\u4e00\u70b9\u60c5\u9762\uff0c\u4e0d\u8981\u62d4\u4ed6\u7684\u9556\u65d7\u3002\u201d\u7fa4\u4e10\u89c1\u8001\u9556\u5934\u53cc\u624b\u8fde\u626c\uff0c\u5374\u5e76\u65e0 \u4e00\u679a\u98de\u9488\u53d1\u51fa\uff0c\u4f30\u91cf\u4ed6\u7684\u6697\u5668\u4e5f\u8be5\u53d1\u5149\u4e86\uff0c\u5fc3\u4e2d\u5c11\u4e86\u987e\u5fcc\uff0c\u4f46\u4ecd\u821e\u52a8\u5175\u5668\uff0c\u62a4\u7740\u9762\u95e8\u80f8\u53e3\u7b49 \u5904\u8981\u5bb3\uff0c\u7b56\u9a6c\u76f4\u51b2\u5165\u9556\u884c\u9635\u4e2d\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u90a3\u8001\u9556\u5934\u820c\u7efd\u6625\u96f7\uff0c\u9661\u7684\u4e00\u58f0\u559d\u9053\uff1a\u201c\u8d3c\u5316\u5b50\uff0c\u7ed9\u6211\u7559\u4e0b\u4e86\uff01\u201d\u55e4\u55e4\u55e4\u51e0\u58f0\u75be \u54cd\uff0c\u5de6\u53f3\u4e24\u9762\u7684\u58ee\u4e10\u8dcc\u4e0b\u9a6c\u80cc\uff0c\u5f53\u4e2d\u59d3\u767d\u90a3\u4e2a\u4e10\u5e2e\u5927\u5934\u76ee\u53cd\u624b\u4e00\u97ad\uff0c\u7acb\u5373\u62e8\u8f6c\u9a6c\u5934\uff0c\u8bf4\u65f6 \u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u97e9\u8001\u9556\u5934\u8eab\u5f62\u75be\u8d77\uff0c\u5728\u7bb1\u9876\u4e0a\u98de\u8eab\u6251\u4e0b\uff0c\u624b\u4e2d\u4f7f\u4e00\u6746\u9ed1\u6f06\u53d1\u4eae\u7684\u5175\u5668\uff0c\u4e00 \u62db\u201c\u674e\u5e7f\u5c04\u77f3\u201d\uff0c\u70b9\u5230\u654c\u624b\u80f8\u819b\u7684\u201c\u7487\u7391\u7a74\u201d\uff0c\u8fd9\u5927\u5934\u76ee\u540d\u53eb\u767d\u5b5f\u5ddd\uff0c\u4e43\u662f\u4e10\u5e2e\u4e2d\u7684\u4e00\u6d41\u597d \u624b\uff0c\u6b66\u529f\u4e0d\u5728\u6bd5\u613f\u7a77\u4e4b\u4e0b\uff0c\u5728\u9a6c\u80cc\u4e0a\u4e00\u4e2a\u201c\u956b\u91cc\u85cf\u8eab\u201d\uff0c\u521a\u521a\u95ea\u5f00\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u5988\u5df4\u5b50 \u7684\uff0c\u4f60\u8fd9\u8001\u8d3c\uff01\u201d\u9a82\u58f0\u672a\u4e86\uff0c\u53ea\u89c1\u51e0\u70b9\u706b\u661f\u6e85\u8d77\uff0c\u767d\u5b5f\u5ddd\u4e00\u4e2a\u7b4b\u6597\uff0c\u7ffb\u4e0b\u9a6c\u80cc\uff0c\u539f\u6765\u97e9\u8001\u9556 \u5934\u9664\u4e86\u5584\u4f7f\u6885\u82b1\u900f\u9aa8\u9488\u4e4b\u5916\uff0c\u8fd8\u7cbe\u5e72\u6253\u7a74\uff0c\u4ed6\u7684\u6253\u7a74\u5175\u5668\u4fbf\u662f\u968f\u8eab\u643a\u5e26\u7684\u65f1\u70df\u6746\uff0c\u767d\u5b5f\u5ddd\u907f \u5f00\u4e86\u4ed6\u7684\u70b9\u7a74\uff0c\u5374\u7ed9\u90a3\u6eda\u70ed\u7684\u70df\u9505\u70eb\u7126\u4e86\u4e00\u7247\u76ae\u8089\u3002 \u3000\u3000\u767d\u5b5f\u5ddd\u9003\u5f97\u5feb\uff0c\u97e9\u8001\u9556\u5934\u8ffd\u5f97\u66f4\u5feb\uff0c\u767d\u5b5f\u5ddd\u521a\u521a\u7ffb\u4e0b\u9a6c\u80cc\uff0c\u4ed6\u7684\u70df\u6746\u53c8\u70b9\u5230\u4e86\u540e\u5fc3\uff0c\u767d \u5b5f\u5ddd\u5530\u5730\u53cd\u624b\u4e00\u97ad\u5374\u626b\u4e86\u4e2a\u7a7a\uff0c\u4f46\u89c1\u97e9\u8001\u9556\u5934\u4e00\u53e3\u6d53\u70df\uff0c\u8fce\u9762\u55b7\u5230\uff0c\u767d\u5b5f\u5ddd\u5934\u6655\u76ee\u7729\uff0c\u97ad\u6cd5 \u5927\u4e71\uff0c\u97e9\u8001\u9556\u5934\u90a3\u6839\u70df\u6746\u6709\u5982\u7075\u86c7\u56db\u94bb\uff0c\u65f6\u800c\u4f5c\u70b9\u7a74\u6485\u7528\uff0c\u65f6\u800c\u4f5c\u4e94\u884c\u5251\u4f7f\uff0c\u6740\u5f97\u767d\u5b5f\u5ddd\u53ea \u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u8be5\u529d\u67b6\u4e86\u5427\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u4e14\u518d\u770b\u4e00\u4f1a\u513f\u3002\u201d\u4e10\u5e2e\u7684\u4eba\u60f3\u51b2\u4e0a\u53bb \u6551\uff0c\u4f46\u767d\u5b5f\u5ddd\u88ab\u56f0\uff0c\u4e24\u4e2a\u6b66\u529f\u4ec5\u6b21\u4e8e\u767d\u5b5f\u5ddd\u7684\u53c8\u4e2d\u4e86\u6bd2\u9488\uff0c\u5b9e\u529b\u5927\u51cf\uff0c\u9556\u884c\u7684\u4eba\uff0c\u4e00\u81f4\u594b \u8d77\uff0c\u7528\u5f13\u7bad\u5c04\u7740\u9635\u811a\uff0c\u773c\u770b\u4e10\u5e2e\u7684\u8d25\u5c40\u5df2\u662f\u65e0\u53ef\u633d\u56de\u3002 \u3000\u3000\u6df7\u6218\u4e2d\u53ea\u542c\u5f97\u97e9\u8001\u9556\u5934\u54c8\u54c8\u5927\u7b11\uff0c\u767d\u5b5f\u5ddd\u624b\u5fd9\u811a\u4e71\uff0c\u4e00\u97ad\u626b\u53bb\uff0c\u97e9\u8001\u9556\u5934\u4e0d\u95ea\u4e0d\u683c\uff0c\u53cd \u5c06\u70df\u6746\u51d1\u4e0a\u524d\u53bb\uff0c\u957f\u97ad\u7f20\u5728\u70df\u6746\u4e0a\uff0c\u88ab\u97e9\u8001\u9556\u5934\u987a\u52bf\u53cd\u5377\uff0c\u8d8a\u5377\u8d8a\u77ed\uff0c\u731b\u5730\u559d\u9053\uff1a\u201c\u5012 \u4e0b\uff01\u201d\u767d\u5b5f\u5ddd\u4e00\u4e2a\u8e09\u8dc4\uff0c\u8eab\u5b50\u503e\u659c\uff0c\u4f46\u5374\u8fd8\u5e76\u672a\u5e94\u58f0\u5012\u4e0b\u3002 \u3000\u3000\u9556\u884c\u4e2d\u6709\u4eba\u770b\u51fa\u4e0d\u5bf9\uff0c\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u53ae\u6562\u60c5\u771f\u662f\u4e10\u5e2e\u4e2d\u7684\uff1f\u201d\u97e9\u8001\u9556\u5934\u51b7\u7b11\u9053\uff1a\u201c\u7ba1 \u4ed6\u662f\u8c01\uff1f\u6349\u4ed6\u53bb\u9001\u7ed9\u6bd5\u5927\u9f99\u5934\u770b\u770b\uff0c\u82e5\u7136\u771f\u662f\u4e10\u5e2e\u4e2d\u7684\uff0c\u4e0d\u5fc5\u54b1\u4eec\u60e9\u7f5a\uff0c\u6bd5\u64ce\u5929\u4fbf\u8981\u5e9f\u4e86\u4ed6 \u7684\u53cc\u817f\uff01\u201d\u76f4\u5230\u73b0\u5728\uff0c\u4ed6\u8fd8\u4e0d\u4fe1\u8fd9\u4f19\u4eba\u662f\u6bd5\u64ce\u5929\u5dee\u9063\u6765\u7684\u3002\u53f6\u6210\u6797\u4e0e\u4e8e\u627f\u73e0\u8eb2\u5728\u4e00\u5757\u5927\u77f3\u540e \u9762\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u4f38\u4e86\u4f38\u820c\u5934\uff0c\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u82e5\u53bb\u529d\u67b6\uff0c\u8be5\u600e\u4e48\u8bf4\uff0c\u96be\u9053\u597d\u8bf4\u4ed6\u4eec\u771f\u662f\u6bd5\u64ce\u5929 \u5dee\u9063\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u97e9\u8001\u9556\u5934\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u5e95\u5374\u4e1d\u6beb\u4e5f\u4e0d\u653e\u677e\uff0c\u4ed6\u7684\u5185\u529b\u672c\u6765\u5c31\u6bd4\u767d\u5b5f\u5ddd\u9ad8\u51fa\u8bb8\u591a\uff0c\u53ea\u89c1\u4ed6 \u70df\u6597\u4e00\u632f\uff0c\u767d\u5b5f\u5ddd\u957f\u97ad\u7acb\u5373\u65ad\u4e3a\u51e0\u6bb5\u3002 \u3000\u3000\u773c\u89c1\u97e9\u8001\u9556\u5934\u8fd9\u70df\u888b\u4e00\u78d5\uff0c\u767d\u5b5f\u5ddd\u975e\u683d\u5012\u4e0d\u53ef\uff0c\u5c31\u5728\u8fd9\u970e\u90a3\u4e4b\u95f4\uff0c\u5ffd\u89c1\u9556\u884c\u4e2d\u4eba\u5982\u6f6e\u6c34 \u822c\u500f\u8fdb\u500f\u9000\uff0c\u4e00\u6761\u4eba\u5f71\u75be\u903e\u98d8\u98ce\u5730\u51b2\u4e86\u8fdb\u6765\uff0c\u97e9\u8001\u9556\u5934\u70df\u6746\u4e00\u6325\uff0c\u5c06\u767d\u5b5f\u5ddd\u9707\u9000\u6570\u6b65\uff0c\u5b9a\u775b \u4e00\u770b\uff0c\u53ea\u89c1\u6765\u7684\u4e43\u662f\u4e00\u4e2a\u8eab\u7a7f\u674f\u9ec4\u8272\u9053\u888d\u7684\u9053\u58eb\uff0c\u624b\u6301\u62c2\u5c18\uff0c\u906e\u5728\u767d\u5b5f\u5ddd\u7684\u9762\u524d\u3002 \u3000\u3000\u97e9\u8001\u9556\u5934\u6253\u4e86\u4e2a\u7a81\uff0c\u624b\u629a\u70df\u676f\uff0c\u6717\u58f0\u95ee\u9053\uff1a\u201c\u6765\u8005\u53ef\u662f\u5c71\u4e1c\u4e0a\u6e05\u89c2\u7684\u7384\u745b\u9053\u957f\u4e48\uff1f\u201d\u7384 \u745b\u9053\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e45\u95fb\u97e9\u8001\u9556\u5934\u5927\u540d\uff0c\u4eca\u65e5\u5e78\u4f1a\u3002\u201d\u97e9\u8001\u9556\u5934\u9053\uff1a\u201c\u6562\u95ee\u9053\u957f\u5927\u9a7e\u5357\u6765\uff0c \u6709\u4f55\u6307\u6559\uff1f\u201d\u7384\u745b\u9053\u4eba\u9053\uff1a\u201c\u8d2b\u9053\u6765\u5411\u5c45\u58eb\u5316\u7f18\uff0c\u8fd9\u8d9f\u9556\u8bf7\u4f60\u65bd\u820d\u4e86\u5427\u3002\u201d\u8138\u4e0a\u51b7\u6c14\u68ee\u68ee\uff0c \u4e1d\u6beb\u4e0d\u4f3c\u8bf4\u7b11\u3002 \u3000\u3000\u97e9\u8001\u9556\u5934\u70df\u6746\u5fae\u98a4\uff0c\u5f3a\u6291\u6012\u706b\u95ee\u9053\uff1a\u201c\u9053\u957f\u4e16\u5916\u9ad8\u4eba\uff0c\u8981\u8fd9\u94b1\u7269\u4f55\u7528\uff1f\u201d\u7384\u745b\u9053\u4eba\u6de1\u6de1 \u8bf4\u9053\uff1a\u201c\u5929\u4e0b\u82cd\u751f\uff0c\u55f7\u55f7\u5f85\u54fa\u8005\u751a\u591a\uff0c\u8d2b\u9053\u5316\u7f18\uff0c\u81ea\u6709\u7528\u5904\u3002\u201d\u97e9\u8001\u9556\u5934\u4ef0\u5929\u4e00\u7b11\uff0c\u54c8\u54c8\u8bf4 \u9053\uff1a\u201c\u51b2\u7740\u9053\u957f\u7684\u9762\u5b50\uff0c\u8fd9\u4e2a\u5584\u7f18\u672c\u6765\u975e\u7ed3\u4e0d\u53ef\u3002\u65e0\u5948\u6211\u97e9\u632f\u7fbd\u4fdd\u9556\u6570\u5341\u5e74\uff0c\u8fd8\u662f\u4e24\u8896\u6e05 \u98ce\uff0c\u8fd9\u4e2a\u9556\u6211\u53ef\u8d54\u4e0d\u8d77\u3002\u82e5\u8bf4\u6211\u4e5f\u968f\u9053\u957f\u4e00\u8d70\u4e86\u4e4b\u5427\uff1f\u6211\u97e9\u67d0\u4e00\u751f\u4ece\u672a\u5931\u4fe1\u96c7\u4e3b\uff0c\u9053\u957f\uff0c\u4f60 \u8fd9\u4e0d\u662f\u5f3a\u4eba\u6240\u96be\u4e48\uff1f\u201d \u3000\u3000\u7384\u745b\u9053\u4eba\u4ecd\u662f\u9762\u8272\u6728\u7136\uff0c\u6beb\u65e0\u8868\u60c5\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8bf4\u6765\u8bf4\u53bb\uff0c\u8001\u9556\u5934\u8fd8\u662f\u5584\u8d22\u96be\u820d\u7684 \u4e86\uff1f\u201d\u97e9\u632f\u7fbd\u70df\u6746\u4e00\u6446\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u9053\u957f\u82e5\u7136\u8d70\u8981\u4f38\u624b\uff0c\u90a3\u4e48\u5c31\u8bf7\u5148\u62d4\u4e86\u5c0f\u5f1f\u7684\u9556\u65d7\u3002\u201d \u8bdd\u8bf4\u81f3\u6b64\uff0c\u5df2\u662f\u6beb\u65e0\u8f6c\u5708\u4e4b\u5730\uff0c\u53ea\u89c1\u7384\u745b\u9053\u4eba\u9762\u8272\u4e00\u6c89\uff0c\u62c2\u5c18\u75be\u8d77\uff0c\u4e00\u51fa\u624b\u4fbf\u662f\u4e0a\u4e58\u7684\u62c2\u7a74 \u529f\u592b\uff0c\u5de6\u6307\u201c\u4e2d\u660e\u201d\uff0c\u77f3\u6307\u201c\u767e\u6c47\u201d\uff0c\u97e9\u8001\u9556\u5934\u70df\u6746\u6296\u5f00\uff0c\u8fc5\u5373\u8eab\u79fb\u6b65\u6362\uff0c\u907f\u62db\u8fdb\u62db\u3002\u4e24\u4eba \u90fd\u662f\u6253\u7a74\u62c2\u7a74\u7684\u5927\u540d\u5bb6\uff0c\u767b\u65f6\u6740\u5f97\u4e2a\u96be\u5206\u96be\u89e3\u3002 \u3000\u3000\u7384\u745b\u9053\u4eba\u8fd9\u6837\u7684\u7a81\u5982\u5176\u6765\uff0c\u4e0d\u4f46\u4ee4\u9556\u884c\u4e2d\u4eba\u6115\u7136\u5931\u60ca\uff0c\u4e8e\u3001\u53f6\u4e8c\u4eba\u66f4\u662f\u5927\u611f\u610f\u5916\u3002\u987b\u77e5 \u7384\u745b\u9053\u4eba\u4e3a\u4eba\u803f\u4ecb\uff0c\u5728\u5317\u4e94\u7701\u7b97\u5f97\u662f\u4e2a\u54cd\u5f53\u5f53\u7684\u4eba\u7269\uff0c\u4f9d\u5e38\u7406\u800c\u8a00\uff0c\u4ed6\u7edd\u65e0\u52ab\u9556\u4e4b\u7406\u3002\u53f6\u6210 \u6797\u95ee\u9053\uff1a\u201c\u542c\u8bf4\u6bd5\u64ce\u5929\u593a\u5317\u4e94\u7701\u5927\u9f99\u5934\u7684\u5bf9\u5019\uff0c\u7384\u745b\u9053\u4eba\u4e5f\u66fe\u52a9\u4ed6\u4e00\u81c2\u4e4b\u529b\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u4ed6\u4eec\u4e8c\u4eba\u662f\u6709\u4ea4\u60c5\u3002\u770b\u6765\u4ed6\u8fd9\u6b21\u4e5f\u662f\u53d7\u4e86\u6bd5\u64ce\u5929\u7684\u8bf7\u6258\u3002\u4f46\u4ee5\u81f3\u745b\u9053\u957f\u7684\u4e3a\u4eba\uff0c\u82e5 \u975e\u4ed6\u8ba4\u4e3a\u7406\u6240\u8be5\u5f53\u7684\u4e8b\u60c5\uff0c\u4ed6\u7edd\u4e0d\u4f1a\u4f38\u624b\u591a\u7ba1\u3002\u8fd9\u4e8b\u60c5\u8d8a\u6765\u8d8a\u5947\u602a\u4e86\uff01\u83ab\u975e\u97e9\u8001\u9556\u5934\u8fd9\u8d9f\u9556 \u771f\u662f\u6709\u4ec0\u4e48\u95ee\u9898\uff1f\u201d \u3000\u3000\u573a\u4e2d\u4e24\u4eba\u8d8a\u6597\u8d8a\u70c8\uff0c\u97e9\u632f\u7fbd\u7684\u94c1\u70df\u6746\u662f\u77ed\u5175\u5668\uff0c\u523a\u7a74\u4e5f\u53ea\u80fd\u523a\u4e00\u5904\u5730\u65b9\uff0c\u7384\u745b\u9053\u4eba\u7684\u62c2 \u5c18\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u5c18\u5c3e\u6563\u5f00\uff0c\u5343\u4e1d\u4e07\u7f15\uff0c\u5c06\u97e9\u632f\u7fbd\u7684\u5168\u8eab\u7a74\u9053\u90e8\u7b3c\u7f69\u5728\u4e00\u67c4\u62c2\u5c18\u4e4b \u4e0b\uff0c\u8981\u4e0d\u662f\u97e9\u632f\u7fbd\u7684\u6b66\u529f\u7cbe\u7eaf\u4e4b\u6781\uff0c\u65e9\u5df2\u843d\u8d25\u3002\u9976\u662f\u5982\u6b64\uff0c\u4ed6\u5728\u5175\u5668\u4e0a\u5403\u4e86\u4e8f\uff0c\u7ec8\u662f\u5c48\u5904\u4e0b \u98ce\u3002\u9556\u884c\u4e2d\u4eba\u90fd\u634f\u4e00\u628a\u51b7\u6c57\uff0c\u53ea\u6015\u51e0\u5341\u5e74\u6765\u4ece\u672a\u5931\u8fc7\u624b\u7684\u97e9\u8001\u9556\u5934\u8fd9\u6b21\u96be\u579b\u5a01\u540d\uff0c\u9661\u7136\u95f4\u5ffd \u542c\u5f97\u556a\u7684\u4e00\u58f0\uff0c\u7384\u745b\u9053\u4eba\u5012\u8f6c\u62c2\u5c18\uff0c\u683c\u5f00\u4e86\u97e9\u632f\u7fbd\u7684\u70df\u6746\uff0c\u5c18\u5c3e\u6839\u6839\u7ad6\u8d77\uff0c\u6709\u5982\u5343\u767e\u94a2\u9488\uff0c \u5411\u97e9\u8001\u9556\u5934\u9762\u95e8\u75be\u523a\uff01 \u3000\u3000\u53f6\u6210\u6797\u53eb\u9053\uff1a\u201c\u4e0d\u597d\u3002\u201d\u6b63\u5f85\u8dc3\u51fa\uff0c\u9661\u7136\u95f4\uff0c\u5ffd\u89c1\u51e0\u70b9\u91d1\u661f\u75be\u95ea\uff0c\u82e6\u745b\u9053\u4eba\u5012\u63d0\u62c2\u5c18\uff0c \u8eab\u5f62\u51ed\u7a7a\u62d4\u8d77\uff0c\u4e00\u4e2a\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\uff0c\u5012\u7eb5\u51fa\u4e00\u4e08\u5f00\u5916\u3002\u4e8e\u627f\u73e0\u8d5e\u9053\uff1a\u201c\u6253\u5f97\u597d\uff0c\u907f\u5f97\u4e5f \u5999\u3002\u201d\u539f\u6765\u97e9\u8001\u9556\u5934\u5728\u5f39\u6307\u4e4b\u95f4\uff0c\u53d1\u51fa\u4e09\u53e3\u91d1\u9488\u3002\u4ed6\u53f7\u79f0\u201c\u91d1\u9488\u5723\u624b\u201d\uff0c\u786e\u662f\u795e\u6280\u975e\u51e1\uff0c\u4fd7 \u8bed\u8bf4\uff1a\u5fc3\u65e0\u4e8c\u7528\u201d\uff0c\u4ed6\u7adf\u7136\u5728\u62b5\u6321\u654c\u4eba\u6076\u62db\u4e4b\u9645\uff0c\u80fd\u62bd\u7a7a\u53d1\u51fa\u91d1\u9488\uff0c\u8981\u4e0d\u662f\u7384\u745b\u9053\u4eba\u8f7b\u529f\u8d85 \u5353\uff0c\u5e94\u53d8\u5947\u901f\uff0c\u51e0\u4e4e\u906d\u4e86\u6bd2\u624b\u3002 \u3000\u3000\u4f46\u89c1\u97e9\u8001\u9556\u5934\u5e76\u4e0d\u8ddf\u8e2a\u53cd\u6251\uff0c\u5374\u597d\u6574\u4ee5\u6687\u5730\u88c5\u4e86\u4e00\u53e3\u65f1\u70df\uff0c\u9556\u884c\u7684\u4eba\u83ab\u540d\u5176\u5999\uff0c\u5fc3\u4e2d\u90fd \u9053\uff1a\u201c\u8fd9\u5c82\u4e0d\u662f\u9519\u8fc7\u826f\u673a\uff1f\u201d\u54ea\u77e5\u7384\u745b\u9053\u4eba\u7684\u62c2\u5c18\u62db\u6570\u795e\u5999\u65e0\u6bd4\uff0c\u770b\u4f3c\u8d25\u8d70\uff0c\u5b9e\u662f\u85cf\u6709\u6781\u5389 \u5bb3\u7684\u540e\u7740\uff0c\u97e9\u8001\u9556\u5934\u53ef\u4e0d\u4e0a\u8fd9\u4e2a\u5f53\uff0c\u4ed6\u8d81\u8fd9\u4e2a\u673a\u4f1a\uff0c\u7f13\u4e00\u53e3\u6c14\uff0c\u5fc3\u4e2d\u65e9\u5df2\u76d8\u7b97\u597d\u5236\u654c\u4e4b\u65b9\u3002 \u3000\u3000\u97e9\u8001\u9556\u5934\u5438\u4e86\u4e00\u53e3\u65f1\u70df\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u7384\u745b\u9053\u957f\uff0c\u53ef\u4ee5\u9976\u4e86\u5c0f\u8001\u513f\u5427\uff1f\u201d\u7384\u745b\u9053\u4eba\u62c2\u5c18 \u4e00\u6446\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u51e0\u6839\u91d1\u9488\uff0c\u6d4e\u4e0d\u4e86\u4e8b\uff0c\u8d2b\u9053\u8fd8\u5f97\u5411\u5c45\u58eb\u5316\u7f18\uff01\u201d\u4e00\u62db\u201c\u4e91\u9ebe\u4e09\u821e\u201d\uff0c\u62c2 \u5c18\u6a2a\u626b\uff0c\u97e9\u8001\u9556\u5934\u53eb\u9053\uff1a\u201c\u9053\u957f\uff0c\u5929\u4e0b\u95f4\u4e5f\u5c11\u89c1\u4f60\u8fd9\u6837\u5316\u7f18\uff0c\u53ef\u53eb\u5c0f\u8001\u513f\u8bbe\u6cd5\u5b50\u5566\u3002\u201d\u8bed\u672a \u8bf4\u5b8c\uff0c\u9aa4\u7136\u4e00\u53e3\u6d53\u70df\u55b7\u51fa\uff0c\u97e9\u632f\u7fbd\u7684\u900f\u9aa8\u91d1\u9488\uff0c\u70df\u6746\u523a\u7a74\u548c\u55b7\u70df\u6270\u654c\uff0c\u4e43\u662f\u4ed6\u7684\u4e09\u79cd\u7edd\u6280\uff0c \u5c24\u5176\u55b7\u54bd\u6270\u654c\u66f4\u662f\u532a\u5937\u6240\u6069\uff0c\u80fd\u5728\u5f20\u53e3\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u5c06\u70df\u6c14\u7559\u5728\u53e3\u4e2d\uff0c\u5f85\u5230\u654c\u4eba\u4e0d\u5907\u4e4b\u65f6\uff0c\u8fd9 \u624d\u7a81\u7136\u55b7\u51fa\u3002 \u3000\u3000\u9ad8\u624b\u6bd4\u6597\uff0c\u6700\u5fcc\u654c\u4eba\u5728\u6697\uff0c\u81ea\u5df1\u5728\u660e\u3002\u7384\u745b\u9053\u4eba\u62c2\u5c18\u4e00\u626b\uff0c\u5ffd\u7136\u70df\u96fe\u8ff7\u6f2b\uff0c\u9976\u4ed6\u6280\u9ad8\u80c6 \u5927\uff0c\u4ea6\u81ea\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u5fd9\u5012\u8f6c\u62c2\u5750\uff0c\u6539\u653b\u4e3a\u5b88\uff0c\u4e00\u62db\u201c\u516b\u65b9\u98ce\u96e8\u201d\uff0c\u5c06\u4e0a\u4e2d\u4e0b\u4e09\u8def\u5168\u90fd\u62a4 \u7740\u3002\u97e9\u8001\u9556\u5934\u4e5f\u4e0d\u7981\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u4e00\u53e3\u6d53\u70df\uff0c\u53c8\u968f\u7740\u201c\u597d\u201d\u5b57\u55b7\u51fa\uff0c\u7384\u745b\u9053\u4eba\u9a82\u9053\uff1a \u201c\u8fd9\u7b97\u4ec0\u4e48\u6b63\u7ecf\u6bd4\u6b66\uff1f\u201d\u97e9\u8001\u9556\u5934\u7b11\u9053\uff1a\u201c\u8d35\u5ba2\u5149\u987e\uff0c\u5c0f\u8001\u5934\u8be5\u656c\u70df\u4e3e\u8336\uff0c\u5ba2\u9014\u65e0\u8336\uff0c\u53ea\u597d \u5411\u4f60\u656c\u70df\u4e86\u3002\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u5e95\u5374\u6beb\u4e0d\u653e\u677e\uff0c\u4e00\u53e3\u70df\u6746\u6a2a\u6311\u76f4\u523a\uff0c\u65f6\u800c\u4f5c\u70b9\u7a74\u6746\u4f7f\uff0c\u65f6\u800c\u4f5c \u5c0f\u82b1\u67aa\u7528\uff0c\u5904\u5904\u4e0d\u79bb\u7384\u745b\u9053\u4eba\u7684\u4e09\u5341\u516d\u9053\u5927\u7a74\u3002 \u3000\u3000\u53ef\u662f\u7384\u745b\u9053\u4eba\u5b88\u5f97\u5f88\u7a33\uff0c\u4ed6\u5728\u62c2\u5c18\u4e0a\u4e0b\u4e86\u51e0\u5341\u5e74\u7684\u529f\u592b\uff0c\u8fd0\u7528\u5f97\u7eaf\u719f\u4e4b\u6781\uff0c\u867d\u7136\u88ab\u70df\u96fe \u6240\u6270\uff0c\u53ea\u80fd\u89c1\u7740\u654c\u4eba\u6a21\u7cca\u7684\u8eab\u5f71\uff0c\u4ecd\u7136\u89c1\u62db\u62c6\u62db\uff0c\u6beb\u65e0\u7834\u7efd\u3002\u97e9\u8001\u9556\u5934\u90a3\u4e00\u888b\u65f1\u70df\u62bd\u5b8c\u4e4b \u540e\uff0c\u4ecd\u7136\u6253\u4e0d\u5012\u7384\u745b\u9053\u4eba\u3002\u4ed6\u7684\u900f\u9aa8\u91d1\u9489\u53c8\u53ea\u5269\u4e0b\u51e0\u6839\uff0c\u4e0d\u6562\u8f7b\u6613\u53d1\u51fa\u3002\u8fd9\u4e00\u6765\uff0c\u8868\u9762\u4e0a\u4ed6 \u4f3c\u5360\u4e86\u4e0a\u98ce\uff0c\u5b9e\u9645\u5374\u662f\u5371\u673a\u6697\u4f0f\u3002 \u3000\u3000\u8fd9\u65f6\u4e10\u5e2e\u548c\u9556\u884c\u4e5f\u5728\u6df7\u6218\u4e4b\u4e2d\uff0c\u767d\u5b5f\u5ddd\u957f\u97ad\u6298\u65ad\uff0c\u62a2\u8fc7\u4e00\u53e3\u5355\u5200\uff0c\u4e00\u9a6c\u5f53\u5148\uff0c\u780d\u5012\u4e86\u9556 \u884c\u4e24\u4e2a\u5f97\u529b\u7684\u4f19\u8ba1\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u97e9\u8001\u9556\u5934\uff0c\u9556\u65d7\u7559\u4e0b\uff0c\u54b1\u4eec\u7eff\u6c34\u5343\u5c71\uff0c\u76f8\u89c1\u6709\u671f\u3002\u201d\u6307\u6325 \u7fa4\u4e10\u3001\u5c06\u5927\u5927\u5c0f\u5c0f\u7684\u7bb1\u7b3c\u90fd\u642c\u4e0a\u4e86\u9aa1\u8f66\u3002\u4e10\u5e2e\u4eba\u591a\u52bf\u76db\uff0c\u9556\u884c\u7684\u4eba\u88ab\u767d\u5b5f\u5ddd\u56f0\u5728\u4e00\u89d2\uff0c\u65e0\u6cd5 \u963b\u62e6\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u54b1\u4eec\u8be5\u51fa\u53bb\u4e86\u5427\uff1f\u201d\u53f6\u6210\u6797\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u51fa\u53bb\u662f\u52a9\u7384\u745b\u9053\u4eba\u52ab\u9556\u5462\uff1f\u8fd8\u662f \u52a9\u97e9\u8001\u9556\u5934\u4fdd\u9556\u5462\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u54b1\u4eec\u529d\u67b6\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u7384\u745b\u9053\u957f\u4ed6\u4eec\u975e\u52ab\u9556\u4e0d\u53ef\uff0c \u8fd9\u573a\u67b6\u600e\u4e48\u529d\u5f97\u4e0b\u6765\uff1f\u201d\u4e8e\u627f\u73e0\u4e00\u60f3\uff0c\u4eca\u665a\u4e4b\u4e8b\uff0c\u53e4\u602a\u5f97\u51fa\u4e4e\u5e38\u7406\u4e4b\u5916\uff0c\u97e9\u8001\u9556\u5934\u4fdd\u7684\u662f\u4ec0 \u4e48\u9556\uff0c\u6bd5\u64ce\u5929\u53c8\u4e3a\u4f55\u8981\u52ab\u9556\uff0c\u6765\u9f99\u53bb\u8109\uff0c\u81ea\u5df1\u5168\u4e0d\u6e05\u695a\uff0c\u8fd9\u573a\u67b6\u7684\u786e\u4e0d\u77e5\u4ece\u4f55\u529d\u8d77\uff1f\u4e8e\u627f\u73e0 \u95ee\u9053\uff1a\u201c\u4f9d\u4f60\u4e4b\u89c1\u5982\u4f55\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u770b\u6765\u4ed6\u4eec\u53ea\u662f\u5fd7\u5728\u52ab\u9556\uff0c\u4e0d\u5728\u4f24\u4eba\uff0c\u54b1\u4eec\u5c31\u7531\u5f97\u7384 \u745b\u9053\u957f\u5c06\u9556\u52ab\u53bb\uff0c\u7136\u540e\u518d\u622a\u4f4f\u4ed6\u7ec6\u95ee\u6839\u7531\u3002\u597d\u5728\u6bd5\u5927\u54e5\u90fd\u662f\u81ea\u5df1\u4eba\uff0c\u662f\u975e\u66f2\u76f4\uff0c\u6709\u7406\u53ef \u8bf4\u3002\u201d\u4e8e\u627f\u73e0\u4e00\u60f3\u4e0d\u9519\uff0c\u4fbf\u4e0d\u4f5c\u58f0\u3002 \u3000\u3000\u773c\u89c1\u4e10\u5e2e\u7684\u4eba\u5c06\u7bb1\u7b3c\u90fd\u642c\u5230\u9aa1\u8f66\u4e0a\uff0c\u53f6\u6210\u6797\u5ffd\u9053\uff1a\u201c\u4f60\u542c\uff0c\u8fd9\u662f\u4ec0\u4e48\u58f0\u97f3\uff1f\u201d\u591c\u98ce\u4e2d\u9690 \u9690\u4f20\u6765\u6e05\u8d8a\u7684\u89d2\u58f0\uff0c\u4e0d\u591a\u4e00\u4f1a\uff0c\u9556\u884c\u548c\u4e10\u5e2e\u7684\u4eba\u4e5f\u5168\u90fd\u542c\u5230\u4e86\uff0c\u4e2a\u4e2a\u5fc3\u4e2d\u7591\u60d1\uff0c\u4fa7\u8033\u7ec6\u542c\u3002 \u9661\u7136\u95f4\uff0c\u53f7\u89d2\u58f0\u4e2d\u5939\u7740\u4e00\u58f0\u6e05\u5578\uff0c\u4f17\u4eba\u773c\u775b\u4e00\u4eae\uff0c\u4f46\u89c1\u4e00\u961f\u620e\u88c5\u5c11\u5973\uff0c\u6392\u5f97\u6574\u6574\u9f50\u9f50\uff0c\u4ece\u5c71 \u5773\u8f6c\u89d2\u5904\u8d70\u51fa\u6765\uff0c\u6700\u524d\u9762\u7684\u56db\u4e2a\u5c11\u5973\uff0c\u63d0\u7740\u78a7\u7eb1\u706f\u7b3c\uff0c\u62e5\u7740\u4e00\u4f4d\u675f\u7740\u7ea2\u5dfe\u7684\u5c11\u5973\uff0c\u7b11\u58f0\u4e2d\u7ea2 \u5dfe\u98d8\u52a8\uff0c\u7aef\u7684\u662f\u201c\u77eb\u5065\u5a40\u5a1c\u4e24\u6709\u4e4b\u201d\uff0c\u4e24\u8fb9\u6df7\u6218\u7684\u4eba\u90fd\u4e0d\u81ea\u7981\u5730\u9759\u6b62\u4e0b\u6765\uff0c\u770b\u90a3\u4e2a\u7ea2\u5dfe\u5c11 \u5973\u3002\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u770b\u8fd9\u6c14\u6d3e\uff0c\u83ab\u975e\u5979\u5c31\u662f\u8def\u4eba\u4e89\u8bf4\u7684\u7ea2\u5dfe\u5973\u8d3c\uff1f\u201d \u3000\u3000\u4f46\u89c1\u90a3\u7ea2\u5dfe\u5c11\u5973\u7389\u624b\u4e00\u62db\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u652f\u9556\u7ed9\u6211\u7559\u4e0b\u3002\u201d\u7384\u745b\u9053\u4eba\u6012\u9053\uff1a\u201c\u4ec0 \u4e48\uff1f\u201d\u90a3\u5c11\u5973\u76c8\u76c8\u4e00\u7b11\uff0c\u5ffd\u5730\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u6ca1\u957f\u8033\u6735\u5417\uff1f\u8fd9\u652f\u9556\u7ed9\u6211\u7559\u4e0b\uff01\u201d\u7384\u745b\u9053\u4eba\u62c2 \u5c18\u4e00\u4e3e\uff0c\u9053\uff1a\u201c\u51ed\u4ec0\u4e48\u8981\u7ed9\u4f60\u8fd9\u652f\u9556\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u8981\u52a8\u624b\u5417\uff1f\u5c31\u51ed\u8fd9\u4e2a\u8981\u4f60\u7684 \u9556\u3002\u201d\u500f\u7136\u4e4b\u95f4\uff0c\u5bd2\u5149\u75be\u95ea\uff0c\u8fd9\u5c11\u5973\u62d4\u5251\u8fdb\u62db\uff0c\u5feb\u5f97\u65e0\u4ee5\u5f62\u5bb9\uff0c\u4f46\u542c\u5f97\u201c\u55d6\u201d\u7684\u4e00\u58f0\uff0c\u7384\u745b \u9053\u4eba\u7684\u94c1\u62c2\u5c18\u5df2\u88ab\u65ab\u4e86\u4e00\u9053\u7f3a\u53e3\uff0c\u8fd9\u5c18\u6746\u662f\u7cbe\u94c1\u6240\u94f8\uff0c\u770b\u6765\u90a3\u5c11\u5973\u7684\u957f\u5251\u7eb5\u975e\u5b9d\u5251\uff0c\u4ea6\u662f\u950b \u5229\u975e\u5e38\u3002 \u3000\u3000\u7384\u745b\u9053\u4eba\u4f55\u7b49\u6b66\u529f\uff0c\u7adf\u7136\u51b7\u4e0d\u9632\u5730\u5148\u7ed9\u5979\u6765\u4e86\u4e2a\u201c\u4e0b\u9a6c\u5a01\u201d\uff0c\u5fc3\u4e2d\u53c8\u60ca\u53c8\u6012\uff0c\u8bf4\u65f6\u8fdf\uff0c \u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u8fd9\u7ea2\u5dfe\u5c11\u5973\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u62db\u62db\u51cc\u5389\uff0c\u4f3c\u8fd9\u822c\u4e00\u89c1\u9762\u4fbf\u62d4\u5251\u52a8\u624b\uff0c\u4e1d\u6beb\u4e0d\u6309\u6c5f\u6e56 \u89c4\u77e9\uff0c\u5728\u9ed1\u9053\u4e0a\u4e5f\u5c11\u89c1\u7f55\u95fb\u3002 \u3000\u3000\u7384\u745b\u9053\u4eba\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\u8fd9\u5c11\u5973\u7684\u5251\u6cd5\u7adf\u7136\u5982\u6b64\u5389\u5bb3\uff0c\u6296\u8d77\u7cbe\u795e\uff0c\u5c55\u5f00\u516b\u516b\u516d\u5341\u56db\u624b\u8fde\u73af \u4f5b\u7a74\u7684\u529f\u592b\uff0c\u8fd8\u672a\u4f7f\u5230\u4e00\u534a\uff0c\u90a3\u5c11\u5973\u5ffd\u5730\u53c8\u662f\u4e00\u58f0\u6e05\u5578\uff0c\u4e00\u62db\u201c\u661f\u6c49\u98de\u6413\u201d\uff0c\u5251\u5149\u5982\u7ec3\uff0c\u4e0a \u523a\u54bd\u5589\uff0c\u517c\u6307\u53cc\u76ee\uff0c\u7384\u745b\u9053\u4eba\u6325\u5c18\u6a2a\u626b\uff0c\u6697\u4e2d\u4e5f\u85cf\u4e86\u6740\u624b\uff0c\u90a3\u5c11\u5973\u7684\u5251\u6cd5\u5947\u8be1\u4e4b\u6781\uff0c\u770b\u770b\u523a \u5230\uff0c\u5ffd\u5730\u4e2d\u9014\u4e00\u53d8\uff0c\u500f\u7136\u4e00\u4e2a\u76d8\u65cb\uff0c\u5e73\u524a\u8fc7\u6765\uff0c\u5c06\u7384\u745b\u9053\u4eba\u7684\u4e0a\u534a\u8eab\u90fd\u7b3c\u7f69\u5728\u5251\u5149\u4e4b\u4e0b\uff0c\u7384 \u745b\u9053\u4eba\u4ea6\u975e\u5f31\u8005\uff0c\u62c2\u5c18\u4e00\u95ea\u98d8\u5f00\uff0c\u6361\u7740\u5979\u5168\u529b\u8fdb\u653b\uff0c\u4e2d\u8def\u7a7a\u865a\u4e4b\u9645\uff0c\u5c18\u5c3e\u56db\u6563\uff0c\u8fde\u62c2\u5979\u80f8\u53e3 \u7684\u201c\u7389\u8861\u201d\u201c\u5173\u5143\u201d\u201c\u5929\u5173\u201d\u201c\u7487\u7391\u201d\u201c\u7476\u5149\u201d\u201c\u4e2d\u5e9c\u201d\u516d\u5904\u5927\u7a74\uff0c\u8fd9\u662f\u7384\u745b\u9053\u4eba\u8d25\u4e2d\u6c42\u80dc \u7684\u6740\u624b\u7edd\u62db\uff0c\u4e24\u4eba\u90fd\u662f\u8fd1\u8eab\u76f8\u640f\uff0c\u773c\u89c1\u4e00\u62db\u4e4b\u95f4\uff0c\u4fbf\u8981\u5f3a\u5f31\u7acb\u5224\u3002 \u3000\u3000\u7384\u745b\u9053\u4eba\u7684\u4f5b\u5c18\u6b63\u5728\u6cbe\u7740\u5c11\u5973\u8863\u88f3\uff0c\u52b2\u529b\u8fd8\u672a\u8fd0\u5230\u4e4b\u9645\uff0c\u90a3\u7ea2\u5dfe\u5c11\u5973\u5ffd\u7136\u5f20\u53e3\u4e00\u5439\uff0c\u7b11 \u9053\uff1a\u201c\u81ed\u9053\u58eb\u7684\u6b66\u529f\u8fd8\u4e0d\u9519\u554a\uff0c\u7531\u4f60\u53bb\u5427\uff01\u201d\u4f46\u89c1\u5c18\u5c3e\u6839\u6839\u98d8\u8d77\uff0c\u968f\u7740\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u7384\u745b \u5934\u4e0a\u7684\u9053\u51a0\u7ade\u88ab\u90a3\u5c11\u5973\u4e00\u5251\u524a\u4e3a\u4e24\u534a\u3002 \u3000\u3000\u7384\u745b\u9053\u4eba\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u81ea\u4ece\u6210\u540d\u4ee5\u6765\uff0c\u53ea\u8d25\u8fc7\u4e00\u56de\uff0c\u90a3\u662f\u7384\u745b\u9053\u4eba\u52a9\u6bd5\u64ce\u5929\u62a2\u5317 \u4e94\u7701\u5927\u9f99\u5934\u65f6\uff0c\u8d25\u5728\u9633\u5b97\u6d77\u624b\u4e0b\u3002\u9633\u5b97\u6d77\u540d\u5217\u5929\u4e0b\u56db\u5927\u5251\u5ba2\uff0c\u7384\u745b\u9053\u4eba\u529b\u6218\u800c\u8d25\uff0c\u72b9\u6709\u53ef \u8bf4\u3002\u60f3\u4e0d\u5230\u8fd9\u5c11\u5973\u5728\u4e09\u5341\u62db\u4e4b\u5185\uff0c\u4fbf\u524a\u4e86\u4ed6\u7684\u9053\u51a0\uff0c\u8d25\u5f97\u6bd4\u90a3\u4e00\u56de\u66f4\u60e8\uff01 \u3000\u3000\u97e9\u8001\u9556\u5934\u559c\u6c14\u6d0b\u6d0b\uff0c\u6025\u5fd9\u4e0a\u524d\u65bd\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u6765\u8005\u53ef\u662f\u8299\u84c9\u5c71\u7684\u51cc\u4e91\u51e4\u5973\u4fa0\u4e48\uff1f\u8001\u673d\u662f \u4eac\u90fd\u632f\u8fdc\u9556\u5c40\u7684\u9556\u5934\u97e9\u632f\u7fbd\uff0c\u8def\u8fc7\u8d35\u5730\uff0c\u672a\u66fe\u5230\u5b9d\u5be8\u62dc\u5c71\uff0c\u591a\u591a\u5931\u793c\u4e86\u3002\u201d\u5176\u5b9e\u4e0d\u662f\u97e9\u8001\u9556 \u5934\u5fd8\u8bb0\u62dc\u5c71\uff0c\u8fd9\u7ea2\u5dfe\u5973\u76d7\u51cc\u4e91\u51e4\u51fa\u9053\u672a\u6ee1\u4e00\u5e74\uff0c\u540d\u6c14\u672a\u54cd\uff0c\u97e9\u632f\u7fbd\u5df2\u62dc\u4f1a\u4e86\u6c5f\u5357\u7684\u4e03\u4e2a\u5927\u76d7 \u5934\u4e86\uff0c\u5374\u5e76\u672a\u5c06\u5979\u5217\u5185\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u51e4\u773c\u4e00\u626b\uff0c\u76b1\u7709\u8bf4\u9053\uff1a\u201c\u8001\u4eba\u5bb6\uff0c\u4f60\u7f57\u54e9\u7f57\u55e6\uff0c\u8bf4\u8fd9\u4e00\u756a\u8bdd\u505a\u4ec0\u4e48\uff1f\u6211\u53ef\u5e76\u6ca1\u6709 \u8bf7\u6559\u4f60\u7684\u6765\u5386\u554a\uff01\u201d\u97e9\u8001\u5934\u6014\u4e86\u4e00\u6014\uff0c\u966a\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u652f\u9556\u662f\u6211\u4fdd\u7684\u3002\u671b\u59d1\u5a18\u9ad8\u62ac\u8d35\u624b\uff0c\u6211 \u5fc5\u6309\u6c5f\u6e56\u9053\u4e0a\u7684\u89c4\u77e9\uff0c\u9001\u4e00\u4efd\u539a\u793c\u4e0e\u59d1\u5a18\u6dfb\u5986\u3002\u201d\u97e9\u632f\u7fbd\u540d\u6ee1\u5929\u4e0b\uff0c\u9ed1\u767d\u4e24\u9053\u5168\u6709\u4ea4\u60c5\uff0c\u4ee5 \u4e3a\u8fd9\u51cc\u4e91\u51e4\u4e43\u662f\u521d\u51fa\u9053\u7684\u5973\u76d7\uff0c\u7528\u610f\u4e0d\u8fc7\u5728\u626c\u540d\u7acb\u5fd7\uff0c\u81ea\u5df1\u4e0b\u6c14\u76f8\u6c42\uff0c\u5979\u4e0d\u53ef\u80fd\u4e0d\u77e5\u9053\u81ea\u5df1 \u7684\u540d\u5b57\uff0c\u6309\u5e38\u7406\u800c\u8a00\uff0c\u5979\u5b9e\u5728\u72af\u4e0d\u7740\u7ed3\u4e00\u4e2a\u5f3a\u4ec7\uff0c\u66f4\u4e0e\u5929\u4e0b\u9556\u884c\u4f5c\u5bf9\uff08\u97e9\u632f\u7fbd\u662f\u9556\u884c\u9886 \u8896\uff09\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u51cc\u4e91\u51e4\u7adf\u662f\u4e1d\u6beb\u4e0d\u7559\u60c5\u9762\uff0c\u542c\u4e86\u97e9\u8001\u9556\u5934\u7684\u8bf4\u8bdd\uff0c\u4e00\u5f20\u4fcf\u8138\u4e0a\u4ecd\u662f\u51b7\u68ee\u68ee\u7684\u6beb\u65e0 \u8868\u60c5\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8fd9\u79cd\u94f6\u5b50\u53d6\u4e4b\u4f55\u4f24\uff0c\u627e\u7ba1\u8fd9\u662f\u8c01\u4fdd\u7684\u9556\uff1f\u201d\u767d\u5b5f\u5ddd\u7709\u6bdb\u4e00\u626c\uff0c\u8df3\u4e0a\u524d\u53eb \u9053\uff1a\u201c\u4e0d\u9519\u554a\uff0c\u8fd9\u79cd\u94f6\u5b50\u53d6\u4e4b\u4f55\u4f24\uff0c\u54b1\u4eec\u662f\u9053\u4e0a\u540c\u6e90\uff0c\u6309\u89c4\u77e9\u5e73\u5206\u4e86\u5427\uff01\u201d\u51cc\u4e91\u51e4\uff1a\u201c\u8fd9\u652f \u9556\u662f\u4f60\u4eec\u5148\u4e0b\u624b\u52ab\u7684\uff1f\u201d\u767d\u5b5f\u5ddd\u9053\uff1a\u201c\u662f\u554a\uff0c\u54b1\u4eec\u662f\u5949\u6bd5\u5e2e\u4e3b\u4e4b\u547d\u6765\u7684\uff0c\u8fd9\uff01\u8fd9\u2026\u2026\u201d\u6b63\u60f3 \u8bf4\u8fd9\u652f\u9556\u7684\u6765\u5386\uff0c\u51cc\u4e91\u51e4\u597d\u50cf\u542c\u5f97\u6781\u4e0d\u8010\u70e6\uff0c\u4e00\u6325\u624b\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u4eec\u5728\u6211\u7684\u5730\u754c\u7adf\u7136\u4f38\u624b\u52ab \u9556\uff0c\u672c\u6765\u7ed9\u4f60\u4eec\u6bcf\u4eba\u90fd\u7559\u4e0b\u4e00\u70b9\u8bb0\u53f7\uff0c\u770b\u5728\u8fd9\u9053\u58eb\u6b66\u529f\u4e0d\u4fd7\uff0c\u8ba9\u4f60\u4eec\u597d\u597d\u8d70\u5f00\uff0c\u4f60\u4eec\u8fd8\u4e0d\u5feb \u6eda\u3002\u201d \u3000\u3000\u767d\u5b5f\u5ddd\u5927\u6012\uff0c\u6325\u821e\u5355\u5200\uff0c\u50fb\u556a\u4f5c\u54cd\uff0c\u9053\uff1a\u201c\u597d\u5440\uff0c\u7ed9\u4f60\u9762\u5b50\uff0c\u4f60\u656c\u9152\u4e0d\u5403\u5403\u7f5a\u9152\uff0c\u597d \u5427\uff0c\u4f60\u5c31\u4ece\u6211\u4eec\u624b\u4e2d\u518d\u52ab\u53bb\u5427\u3002\u201d\u7384\u745b\u9053\u4eba\u62c2\u5c18\u4e00\u63a2\uff0c\u9053\uff1a\u201c\u97e9\u632f\u7fbd\uff0c\u4f60\u600e\u4e48\u8bf4\uff1f\u201d\u51cc\u4e91\u51e4 \u7684\u5973\u5175\u628a\u88c5\u597d\u7684\u9556\u94f6\u7684\u9aa1\u8f66\u5c31\u8981\u9a71\u8d70\uff0c\u4e10\u5e2e\u548c\u9556\u884c\u7684\u4eba\u90fd\u4e0a\u524d\u62e6\u622a\uff0c\u90a3\u4e9b\u5973\u5175\u4e2a\u4e2a\u6b66\u827a\u9ad8 \u5f3a\uff0c\u54ea\u91cc\u62e6\u963b\u5f97\u4f4f\uff1f\u97e9\u8001\u9556\u5934\u54ac\u4e00\u54ac\u7259\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u540c\u821f\u5171\u6d4e\uff0c\u5148\u628a\u8fd9\u5973\u5f3a\u76d7\u6253\u9000\u4e86 \u518d\u8bf4\uff01\u201d\u500f\u5730\u70df\u6746\u4e00\u63a2\uff0c\u4e00\u53e3\u6d53\u70df\u75be\u55b7\u51fa\u53bb\uff0c\u4e0e\u7384\u745b\u9053\u4eba\u3001\u767d\u5b5f\u5ddd\u5408\u6218\u51cc\u4e91\u51e4\uff01\u6b63\u662f\uff1a \u3000\u3000\u5f02\u519b\u7a81\u8d77\u7ea2\u5dfe\u5973\uff0c\u4e00\u51e4\u51cc\u4e91\u5c55\u7fc5\u98de\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u5b9d\u5251\u91d1\u82b1\u3000\u53cc\u82f1\u65bd\u7edd\u6280\u3000\u4ec1\u5fc3\u4fa0\u9aa8\u3000\u4e00\u8bfa\u6551\u9556\u5e08 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u5b9d\u5251\u91d1\u82b1\u3000\u53cc\u82f1\u65bd\u7edd\u6280\u3000\u4ec1\u5fc3\u4fa0\u9aa8\u3000\u4e00\u8bfa\u6551\u9556\u5e08 \u3000\u3000\u51cc\u4e91\u51e4\u7eb5\u58f0\u957f\u7b11\uff0c\u4f46\u89c1\u5979\u8eab\u5f62\u5fae\u52a8\uff0c\u9752\u94a2\u5251\u500f\u5730\u5148\u5411\u97e9\u632f\u7fbd\u523a\u6765\u3002\u97e9\u8001\u9556\u5934\u5de6\u624b\u4e00\u62ac\uff0c \u7acb\u638c\u62a4\u80f8\uff0c\u5374\u5c06\u53f3\u624b\u7684\u94c1\u70df\u6746\u5f53\u4f5c\u5c0f\u82b1\u67aa\u4f7f\u7528\uff0c\u970d\u5730\u4e00\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u4f7f\u51fa\u653b\u5b88\u517c\u5907\u7684 \u201c\u4e2d\u5e73\u67aa\u201d\u62db\u6570\uff0c\u865a\u70b9\u54bd\u5589\uff0c\u5b9e\u523a\u80c1\u4e0b\u7684\u201c\u6a80\u4e2d\u7a74\u201d\uff0c\u5374\u4e0d\u6599\u51cc\u4e91\u51e4\u8eab\u6cd5\u4e4b\u5feb\uff0c\u65e0\u4ee5\u5f62\u5bb9\uff0c \u97e9\u8001\u9556\u5934\u7684\u70df\u6746\u521a\u521a\u9012\u51fa\uff0c\u5979\u5df2\u62a2\u5148\u534a\u6b65\uff0c\u5251\u5c16\u522e\u4e86\u97e9\u8001\u9556\u5934\u7684\u624b\u8155\uff0c\u6362\u662f\u4ed6\u4eba\uff0c\u8fd9\u4e00\u62db\u94c1 \u70df\u6746\u975e\u6492\u624b\u9001\u51fa\u4e0d\u53ef\uff0c\u97e9\u8001\u9556\u5934\u4e45\u7ecf\u5927\u654c\uff0c\u6025\u5fd9\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u547c\u7684\u4e00\u53e3\u6d53\u70df\u55b7\u51fa\uff0c\u540c\u65f6\u5de6 \u624b\u4e00\u626c\uff0c\u91d1\u661f\u8fde\u95ea\uff0c\u5c06\u6700\u540e\u7684\u4e03\u6839\u900f\u9aa8\u91d1\u9488\uff0c\u4e00\u80a1\u8111\u513f\u90fd\u53d1\u4e86\u51fa\u53bb\u3002\u4f46\u542c\u5f97\u957f\u7b11\u58f0\u4e2d\uff0c\u51cc\u4e91 \u51e4\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u4e00\u6761\u4eba\u5f71\uff0c\u51cc\u7a7a\u98de\u8d77\uff0c\u5979\u4e00\u51fb\u4e0d\u4e2d\uff0c\u65e9\u5df2\u7fe9\u7136\u63a0\u51fa\uff0c\u4e03\u679a\u91d1\u9488\uff0c\u90fd\u4ece \u811a\u4e0b\u98de\u8fc7\u3002 \u3000\u3000\u8f6c\u773c\u4e4b\u95f4\uff0c\u53c8\u5230\u4e86\u7384\u745b\u9053\u4eba\u7684\u8eab\u8fb9\uff0c\u4f46\u89c1\u5979\u8eab\u5f62\u672a\u843d\uff0c\u5df2\u5728\u7a7a\u4e2d\u4f7f\u4e2a\u201c\u6c14\u71d5\u6b83\u5f7c\u201d\u4e4b \u52bf\uff0c\u7fe9\u5982\u98de\u9e1f\u822c\u76f4\u51b2\u7384\u745b\u9053\u4eba\u800c\u6765\uff0c\u7384\u745b\u9053\u4eba\u8bc6\u5f97\u5389\u5bb3\uff0c\u94c1\u62c2\u5c18\u6296\u5f97\u7b14\u76f4\uff0c\u4ed6\u5df2\u8bd5\u8fc7\u62c2\u7a74\u65e0 \u6548\uff0c\u8fd9\u65f6\u6539\u7528\u201c\u7384\u95e8\u62c2\u5c18\u516b\u6cd5\u201d\uff0c\u4f7f\u4e86\u4e00\u4e2a\u201c\u5377\u201d\u5b57\u8bc0\uff0c\u51cc\u4e91\u51e4\u7684\u5251\u5c16\u7adf\u7ed9\u5c18\u5c3e\u5fae\u5fae\u7f20\u4f4f\uff0c \u767d\u5b5f\u5ddd\u773c\u660e\u624b\u5feb\uff0c\u770b\u5f97\u6709\u4fbf\u5b9c\u53ef\u6361\uff0c\u4e00\u4e2a\u7bad\u6b65\u4fbf\u8df3\u4e86\u4e0a\u6765\uff0c\u5530\u7684\u4e00\u5200\u5288\u4e0b\uff0c\u8fd9\u4e00\u5200\u52bf\u6377\u529b \u6c89\uff0c\u7aef\u7684\u662f\u51f6\u9669\u4e4b\u6781\u3002 \u3000\u3000\u5200\u5149\u5251\u5f71\u4e2d\uff0c\u4eec\u542c\u5f97\u51cc\u4e91\u51e4\u4e00\u58f0\u51b7\u7b11\uff0c\u767d\u5b5f\u5ddd\u4e00\u5200\u5288\u4e0b\uff0c\u5374\u5ffd\u7136\u89c1\u4e86\u5979\u7684\u8eab\u5f71\uff0c\u7384\u745b\u9053 \u4eba\u53eb\u58f0\u4e0d\u597d\uff0c\u94c1\u62c2\u5c18\u8131\u624b\u98de\u51fa\uff0c\u767d\u5b5f\u5ddd\u542c\u5f97\u7384\u745b\u9053\u4eba\u7684\u53eb\u558a\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u4ed3\u5352\u4e4b\u95f4\uff0c\u4e0d\u77e5\u5982 \u4f55\u5e94\u4ed8\uff0c\u8fd8\u672a\u53ca\u8f6c\u8eab\uff0c\u4f46\u89c9\u80cc\u540e\u5fae\u98ce\u98d2\u7136\uff0c\u80a9\u5934\u4e00\u9635\u5267\u75db\uff0c\u8fd9\u65f6\u624d\u89c1\u51cc\u4e91\u51e4\u7684\u8eab\u5f71\u8d34\u8eab\u63a0 \u8fc7\u3002\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u7684\u970e\u90a3\u4e4b\u95f4\uff0c\u5979\u4e00\u4e3e\u9707\u9000\u4e86\u7384\u745b\u9053\u4eba\uff0c\u53c8\u628a\u767d\u5b5f\u5ddd\u523a\u4e86\u4e00\u5251\uff0c\u8fd8\u662f\u5979\u624b \u4e0b\u7559\u60c5\uff0c\u8fd9\u4e00\u5251\u4ece\u4ed6\u7435\u7436\u9aa8\u65c1\u8fb9\u4e09\u5bf8\u523a\u8fc7\u3002 \u3000\u3000\u7384\u745b\u9053\u4eba\u6b66\u529f\u6700\u5f3a\uff0c\u4e00\u98de\u8eab\u62a2\u8fc7\u62c2\u5c18\uff0c\u7acb\u523b\u4e0e\u97e9\u8001\u9556\u5934\u8fde\u6210\u7284\u89d2\u4e4b\u52bf\uff0c\u4e92\u76f8\u63a9\u62a4\u3002\u90a3\u767d \u5b5f\u5ddd\u4e2d\u4e86\u4e00\u5251\uff0c\u5374\u662f\u5fc3\u60ca\u80c6\u6218\u3002\u672c\u6765\u4ee5\u4ed6\u4eec\u4e09\u4eba\u4e4b\u529b\uff0c\u5408\u6218\u51cc\u4e91\u51e4\uff0c\u7eb5\u4e0d\u80fd\u80dc\uff0c\u4ea6\u4e0d\u81f3\u8d25\uff0c \u65e0\u5948\u51cc\u4e91\u51e4\u673a\u8b66\u4e4b\u6781\uff0c\u5979\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6cd5\uff0c\u5148\u7834\u4e86\u6700\u5f31\u7684\u4e00\u73af\uff0c\u8fd9\u4e00\u6765\uff0c\u4fbf\u5c06\u5408\u56f4\u4e4b \u52bf\u6253\u5f00\u4e86\u4e00\u4e2a\u7f3a\u53e3\uff0c\u53d7\u4e86\u4f24\u7684\u767d\u5b5f\u5ddd\uff0c\u53cd\u800c\u6210\u4e3a\u4e24\u4eba\u7684\u8d1f\u62c5\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u4e0e\u53f6\u6210\u6797\u5728\u5ca9\u77f3\u540e\u51dd\u795e\u6ce8\u89c6\uff0c\u4f46\u89c1\u51cc\u4e91\u51e4\u5728\u4e09\u4eba\u56f4\u653b\u4e4b\u4e0b\uff0c\u500f\u8fdb\u500f\u9000\uff0c\u5ffd\u5b88\u5ffd \u653b\uff0c\u8eab\u5f62\u5c55\u5f00\uff0c\u771f\u5982\u884c\u4e91\u6d41\u6c34\uff0c\u6f47\u6d12\u81ea\u5982\u3002\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u8fd9\u662f\u54ea\u4e00\u5bb6\u7684\u5251\u672f\uff0c\u7cbe\u5999\u5982\u65af\uff0c \u770b\u6765\u7ade\u4e0d\u5728\u5e08\u7956\u6240\u521b\u7684\u767e\u53d8\u7384\u673a\u5251\u6cd5\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u5f53\u5577\u4e00\u58f0\uff0c\u767d\u5b5f\u5ddd\u7684\u5355\u5200\u88ab\u524a\u4e3a\u4e24\u6bb5\uff0c\u51cc\u4e91\u51e4\u4e00\u4e2a\u8e6c\u811a\u5c06\u5730\u8e22\u7ffb\uff0c\u7384\u745b\u9053\u4eba\u548c\u97e9 \u8001\u9556\u5934\u6025\u9000\uff0c\u6df7\u6218\u4e2d\uff0c\u9556\u884c\u548c\u4e10\u5e2e\u4e4b\u4f17\uff0c\u90fd\u88ab\u5973\u5175\u8d76\u5f97\u56db\u6563\u5954\u9003\uff0c\u51cc\u4e91\u51e4\u4ea6\u662f\u7d27\u8feb\u4e0d\u820d\uff0c\u7384 \u745b\u9053\u4eba\u548c\u97e9\u8001\u9556\u5934\u521a\u8dd1\u4e86\u51e0\u6b65\uff0c\u731b\u542c\u5f97\u91d1\u5200\u5288\u98ce\u4e4b\u58f0\u5230\u4e86\u80cc\u540e\uff0c\u51cc\u4e91\u51e4\u7684\u5251\u6cd5\u5947\u8be1\u7edd\u4f26\uff0c\u4f3c \u5de6\u4f3c\u53f3\uff0c\u4e00\u62db\u540c\u65f6\u653b\u51fb\u4e24\u4eba\uff0c\u97e9\u8001\u9556\u5934\u548c\u7384\u745b\u9053\u4eba\u90fd\u611f\u5230\u5979\u5251\u5c16\u7684\u950b\u8292\uff01 \u3000\u3000\u9aa4\u7136\u95f4\uff0c\u5ffd\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u7384\u745b\u9053\u4eba\u53cd\u624b\u4e00\u4f5b\uff0c\u521a\u521a\u56de\u8fc7\u5934\u6765\uff0c\u4f9d\u7a00\u4f3c\u89c1\u7ec6\u5982\u6e38\u4e1d \u7684\u91d1\u5149\u4e00\u95ea\uff0c\u53ea\u9053\u97e9\u8001\u9556\u5934\u53d1\u51fa\u91d1\u9488\u62d2\u654c\uff0c\u4f46\u89c1\u51cc\u4e91\u51e4\u5df2\u5728\u79bb\u8eab\u5341\u4e08\u4e4b\u5916\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u770b \u5728\u8fd9\u4e24\u679a\u6697\u5668\u7684\u4efd\u4e0a\uff0c\u653e\u4f60\u4eec\u8d70\u5427\uff01\u201d\u7384\u745b\u9053\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u97e9\u8001\u5934\u513f\u90a3\u4e00\u624b\u91d1\u9488\uff0c \u6709\u4ec0\u4e48\u4e86\u4e0d\u5f97\uff0c\u503c\u5f97\u8fd9\u5973\u9b54\u5934\u5982\u6b64\u770b\u91cd\uff1f\u201d \u3000\u3000\u51cc\u4e91\u51e4\u6765\u5f97\u5feb\uff0c\u53bb\u5f97\u4e5f\u5feb\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u90a3\u961f\u5973\u5175\u5df2\u628a\u9556\u8f66\u9a71\u5165\u5bc6\u6797\uff0c\u97e9\u8001\u9556\u5934\u5140\u81ea\u6c14\u5401 \u5401\u5730\u5f80\u524d\u5954\u8dd1\uff0c\u7384\u745b\u9053\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u8ffd\u4e0a\u53bb\u9053\uff1a\u201c\u5973\u8d3c\u5df2\u8d70\u5f97\u8fdc\u5566\uff0c\u4f60\u8fd8\u614c\u4ec0\u4e48\uff1f\u201d\u97e9\u8001\u9556 \u5934\u6012\u9053\uff1a\u201c\u90fd\u662f\u4f60\u4eec\uff0c\u5bb3\u5f97\u6211\u8fd9\u9556\u5c40\u7684\u62db\u724c\u7ed9\u4eba\u5bb6\u6298\u4e86\uff01\u201d\u70df\u6746\u4e00\u62a1\uff0c\u6296\u8d77\u4e2a\u7897\u5927\u7684\u67aa\u82b1\uff0c \u671d\u7384\u745b\u9053\u4eba\u7684\u201c\u98ce\u5e9c\u7a74\u201d\u4fbf\u523a\uff0c\u989d\u4e0a\u7ea2\u7b4b\u66b4\u9732\uff0c\u4e00\u8138\u62fc\u547d\u7684\u795e\u6c14\u3002\u7384\u745b\u9053\u4eba\u4e3e\u62c2\u5c18\u6321\u5f00\uff0c\u51b7 \u51b7\u7b11\u9053\uff1a\u201c\u53c8\u4e0d\u662f\u6211\u62a2\u4f60\u7684\uff01\u201d\u97e9\u8001\u9556\u5934\u9a82\u9053\uff1a\u201c\u90fd\u662f\u4f60\u4eec\u60f9\u51fa\u6765\u7684\uff01\u201d\u70df\u6746\u75be\u4e0b\u5982\u96e8\uff0c\u4ed6 \u8d70\u9556\u56db\u5341\u5e74\uff0c\u8fd9\u8fd8\u662f\u7b2c\u4e00\u6b21\u5931\u624b\uff0c\u602a\u4e0d\u5f97\u4ed6\u6c14\u5f97\u51e0\u4e4e\u75af\u4e86\uff01 \u3000\u3000\u7384\u745b\u9053\u4eba\u53ea\u9053\u521a\u624d\u7684\u6697\u5668\u662f\u4ed6\u53d1\u7684\uff0c\u5fc3\u611f\u4ed6\u76f8\u6551\u4e4b\u6069\uff0c\u800c\u9556\u94f6\u53c8\u5df2\u88ab\u51cc\u4e91\u51e4\u52ab\u53bb\uff0c\u5b9e\u5df2 \u65e0\u5fc3\u4e0e\u97e9\u8001\u9556\u5934\u53ae\u62fc\uff0c\u53ea\u662f\u628a\u62c2\u5c18\u5c55\u5f00\uff0c\u62a4\u7740\u5168\u8eab\u7a74\u9053\uff0c\u4f46\u5b88\u4e0d\u653b\u3002\u97e9\u8001\u9556\u5934\u5374\u8d8a\u6253\u8d8a\u6025\uff0c \u62db\u62db\u51cc\u5389\uff0c\u7384\u745b\u9053\u4eba\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u5934\u513f\u597d\u6ca1\u6765\u7531\uff0c\u6211\u95ee\u4f60\uff0c\u4f60\u662f\u60f3\u8981\u56de\u8fd9\u9556\u94f6\u4e0d\u662f\uff1f\u201d\u97e9 \u8001\u9556\u5934\u7709\u5934\u4e00\u626c\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\uff01\u201d\u7384\u745b\u9053\u4eba\u9053\uff1a\u201c\u8fd9\u652f\u9556\u662f\u90a3\u5973\u8d3c\u52ab\u7684\u4e0d\u662f\uff1f\u201d\u97e9\u8001\u9556\u5934 \u9053\uff1a\u201c\u4e0d\u662f\u4f60\u4eec\u80e1\u7f20\uff0c\u6211\u65e9\u5df2\u8fc7\u4e86\u8299\u84c9\u5c71\u5566\uff01\u201d\u7384\u745b\u9053\u4eba\u9053\uff1a\u201c\u65e7\u5e10\u6162\u4e9b\u518d\u7b97\uff0c\u54b1\u4eec\u8bf4\u76ee\u524d \u7684\u3002\u201d\u97e9\u8001\u9556\u5934\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u7384\u745b\u9053\u4eba\u9053\uff1a\u201c\u4f60\u60f3\u8981\u56de\u9556\u94f6\uff0c\u6211\u4e5f\u60f3\u8981\u8fd9\u652f\u9556\u3002\u5728\u8fd9\u4e00\u70b9 \u4e0a\uff0c\u54b1\u4eec\u53ef\u662f\u5fd7\u540c\u9053\u5408\uff0c\u7406\u8be5\u540c\u821f\u5171\u6d4e\u624d\u662f\u3002\u201d\u97e9\u8001\u9556\u5934\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u54b1\u4eec\u540c\u6765\u60f3\u6cd5\uff0c\u5411\u90a3 \u5973\u8d43\u8ffd\u56de\u9556\u94f6\u4e48\uff1f\u201d\u7384\u745b\u9053\u4eba\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u97e9\u8001\u9556\u5934\u60f3\u4e86\u4e00\u60f3\uff0c\u5ffd\u7136\u6012\u6c14\u51b2\u51b2\u9053\uff1a\u201c\u6211\u624d \u4e0d\u4e0e\u4f60\u4eec\u8fd9\u5e72\u5351\u52a3\u5c0f\u4eba\u540c\u8c0b\u5408\u4f19\uff01\u201d \u3000\u3000\u7384\u745b\u9053\u4eba\u5927\u6012\uff0c\u53eb\u9053\uff1a\u201c\u6211\u600e\u4e48\u5351\u52a3\u4e86\uff1f\u201d\u4f46\u89c1\u767d\u5b5f\u5ddd\u4e58\u4e86\u4e00\u5339\u9a6c\uff0c\u5728\u9a6c\u80cc\u4e0a\u6447\u6447\u6b32\u5760 \u5730\u5954\u6765\uff0c\u97e9\u8001\u9556\u5934\u6012\u6c14\u5927\u8d77\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u4eec\u5c06\u6211\u7684\u9aa1\u9a6c\u90fd\u4e0b\u836f\u8ff7\u5012\uff0c\u8fd9\u884c\u5f84\u8fd8\u4e0d\u5351\u52a3\u4e48\uff1f\u201d \u7a81\u7136\u820d\u4e86\u7384\u745b\u9053\u4eba\uff0c\u7eb5\u8eab\u4e00\u8dc3\uff0c\u94c1\u70df\u6746\u5411\u767d\u5b5f\u5ddd\u7684\u9a6c\u5934\u78d5\u4e0b\u3002 \u3000\u3000\u70df\u6746\u672a\u843d\uff0c\u90a3\u5339\u9a6c\u4e00\u58f0\u957f\u5636\uff0c\u767d\u5b5f\u5ddd\u6eda\u7ffb\u5730\u4e0b\uff0c\u7384\u745b\u9053\u4eba\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8bf4\u54b1\u4eec\u5351 \u52a3\uff0c\u4f60\u6253\u4e00\u4e2a\u53d7\u4f24\u7684\u4eba\uff0c\u8fd9\u7b97\u5f97\u82f1\u96c4\u5417\uff1f\u201d\u62c2\u5c18\u6a2a\u626b\uff0c\u4e00\u8fde\u51e0\u8bb0\u75be\u653b\uff0c\u97e9\u8001\u9556\u5934\u8fd9\u624d\u9192\u8d77\u767d \u5b5f\u5ddd\u662f\u4e0e\u81ea\u5df2\u8054\u624b\u62d2\u654c\u4e4b\u65f6\uff0c\u53d7\u4e86\u90a3\u7ea2\u5dfe\u5973\u8d3c\u7684\u4e24\u5904\u5251\u4f24\uff0c\u5fc3\u4e2d\u9887\u611f\u6b49\u759a\uff0c\u4f46\u7384\u745b\u9053\u4eba\u7684\u62c2 \u5c18\u6765\u5f97\u751a\u6025\uff0c\u903c\u5f97\u4ed6\u4e0d\u597d\u548c\u89e3\uff0c\u53ea\u6709\u594b\u529b\u62db\u67b6\u3002\u6b63\u5728\u6253\u5f97\u4e0d\u53ef\u5f00\u652f\u65f6\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u6e05\u8106\u7684\u58f0 \u97f3\u53eb\u9053\uff1a\u201c\u4e24\u4f4d\u524d\u8f88\u606f\u4e89\uff0c\u655d\u5e08\u4f2f\u7956\u6f6e\u97f3\u548c\u5c1a\u8bf7\u4e24\u4f4d\u76f8\u89c1\u3002\u201d \u3000\u3000\u7384\u745b\u9053\u4eba\u548c\u97e9\u8001\u9556\u5934\u6536\u4e86\u5175\u5668\uff0c\u970d\u5730\u8df3\u51fa\u5708\u5b50\uff0c\u53ea\u89c1\u9762\u524d\u7ad9\u7740\u4e00\u4e2a\u5bb9\u8c8c\u6f02\u4eae\u7684\u5c11\u5973\u548c\u4e00 \u4e2a\u6d53\u7709\u5927\u773c\u7684\u5c11\u5e74\uff0c\u8fd9\u81ea\u7136\u662f\u4e8e\u627f\u73e0\u548c\u53f6\u6210\u6797\u4e86\u3002 \u3000\u3000\u7384\u745b\u9053\u4eba\u5e2e\u6bd5\u64ce\u5929\u62a2\u5317\u4e94\u7701\u7684\u5927\u9f99\u5934\u4e4b\u65f6\uff0c\u5728\u6b66\u5bb6\u5e84\u4e0a\u89c1\u8fc7\u4e8e\u627f\u73e0\uff0c\u77e5\u9053\u5979\u662f\u5f20\u4e39\u67ab\u7684 \u5f92\u5f1f\uff0c\u6025\u5fd9\u4e3e\u5175\u4e3a\u793c\u3002\u97e9\u8001\u9556\u5934\u867d\u672a\u89c1\u8fc7\uff0c\u89c1\u5979\u5982\u6b64\u8bf4\u6cd5\uff0c\u604d\u7136\u5927\u609f\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u7fa4\u53eb\u5316\u5b50 \u6700\u521d\u4e0e\u6211\u52a8\u624b\u4e4b\u65f6\uff0c\u6709\u4e00\u5339\u767d\u9a6c\u5728\u6797\u5916\u9a70\u8fc7\uff0c\u5176\u5feb\u5982\u98ce\uff0c\u6211\u6b63\u8ffd\u90a3\u4e2a\u53eb\u5316\u5934\u5b50\uff0c\u9a6c\u80cc\u4e0a\u7684\u4eba \u4e00\u628a\u5c06\u4ed6\u62a2\u53bb\uff0c\u90a3\u9a6c\u8dd1\u5f97\u592a\u5feb\uff0c\u9ed1\u591c\u4e2d\u6211\u770b\u4e0d\u6e05\u695a\uff0c\u83ab\u975e\u8fd9\u4eba\u5c31\u662f\u6f6e\u97f3\u548c\u5c1a\u4e48\uff1f\u201d\u4e8e\u627f\u73e0 \u9053\uff1a\u201c\u6b63\u662f\u4ed6\u8001\u4eba\u5bb6\u3002\u201d\u97e9\u8001\u9556\u5934\u53cc\u773c\u4e00\u7ffb\uff0c\u53eb\u9053\uff1a\u201c\u8001\u670b\u53cb\u7adf\u7136\u662f\u8fd9\u6837\u5e2e\u5fd9\u6211\u5417\uff1f\u597d\u5440\uff0c \u6211\u975e\u5411\u8fd9\u83bd\u548c\u5c1a\u8ba8\u4e2a\u516c\u9053\u4e0d\u53ef\uff01\u201d\u7384\u745b\u4e5f\u53eb\u9053\uff1a\u201c\u6f6e\u97f3\u5927\u5e08\u539f\u6765\u4eca\u665a\u4e5f\u6765\u8fc7\u4e86\uff1f\u4ed6\u773c\u89c1\u6bd5\u613f \u7a77\u53d7\u4f24\uff0c\u600e\u4e48\u4e0d\u52a9\u4ed6\u4e00\u81c2\u4e4b\u529b\uff1f\u65e9\u5c06\u9556\u94f6\u52ab\u8d70\uff0c\u4e5f\u7701\u5f97\u8fd9\u8bb8\u591a\u662f\u975e\uff01\u201d\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u6b63\u56e0 \u4ed6\u8001\u4eba\u5bb6\u548c\u4e24\u4f4d\u524d\u8f88\u90fd\u662f\u8001\u670b\u53cb\u4e86\uff0c\u6240\u4ee5\u624d\u5dee\u9063\u6211\u6765\u8bf7\u4e24\u4f4d\u606f\u4e89\u3002\u201d \u3000\u3000\u97e9\u8001\u9556\u5934\u548c\u7384\u745b\u9053\u4eba\u90fd\u662f\u6ee1\u809a\u5b50\u95f7\u6c14\uff0c\u51b7\u51b7\u5730\u54fc\u4e86\u4e00\u58f0\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u968f\u7740\u4e8e\u627f\u73e0\u4fbf\u8d70\u3002 \u8d70\u5230\u90a3\u5ea7\u7834\u5e99\uff0c\u89c1\u6f6e\u97f3\u548c\u5c1a\u6b63\u5728\u66ff\u6bd5\u613f\u7a77\u7597\u4f24\uff0c\u6f6e\u97f3\u548c\u5c1a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u97e9\u8001\u54e5\uff0c\u4f60\u6765\u5f97\u6b63 \u597d\uff0c\u5c06\u89e3\u836f\u62ff\u51fa\u6765\uff0c\u7701\u5f97\u6211\u8d39\u529b\u66ff\u8fd9\u5316\u5b50\u6cbb\u4f24\u5566\uff01\u201d\u97e9\u632f\u7fbd\u4e00\u53e3\u6c14\u51b2\u4e86\u4e0a\u6765\uff0c\u53eb\u9053\uff1a\u201c\u6f6e \u97f3\uff0c\u4f60\u5e2e\u7684\u662f\u8c01\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u6211\u8c01\u4e5f\u4e0d\u5e2e\uff0c\u8fd9\u5973\u5a03\u521a\u624d\u8fd8\u8bf4\u6211\u7231\u7406\u95f2\u4e8b\u54e9\uff01\u4e24\u8fb9\u90fd \u662f\u670b\u53cb\uff0c\u6211\u82e5\u4e00\u5e2e\uff0c\u8fd9\u4e8b\u60c5\u5c82\u4e0d\u662f\u66f4\u95f9\u5927\u4e86\u3002\u201d\u97e9\u8001\u9556\u5934\u6c14\u547c\u547c\u5730\u56b7\u9053\uff1a\u201c\u4f60\u8bf4\u4e0d\u5e2e\uff0c\u600e\u4e48 \u903c\u6211\u62ff\u51fa\u89e3\u836f\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u8001\u5144\u8a00\u91cd\u4e86\uff0c\u6211\u662f\u8bf7\u4f60\u62ff\u51fa\u89e3\u836f\uff0c\u8bf7\u4f60\u4e0d\u7740\u50e7\u9762\u770b\u4f5b \u9762\u3002\u201d\u97e9\u8001\u9556\u5934\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6f6e\u97f3\u548c\u5c1a\uff0c\u4f60\u662f\u62ff\u4f60\u8001\u5927\u54e5\u7684\u9762\u5b50\u6765\u538b\u6253\u5c0f\u5f1f\u5f1f \u4e86\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u6211\u6ca1\u6709\u90a3\u4e48\u5927\u7684\u9762\u5b50\uff0c\u6211\u662f\u8bf7\u4f60\u770b\u5728\u5929\u4e0b\u5341\u516b\u7701\u5927\u9f99\u5934\u6bd5\u64ce\u5929\u7684\u9762 \u4e0a\uff01\u201d\u97e9\u8001\u9556\u5934\u5446\u4e86\u4e00\u5446\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u6bd5\u64ce\u5929\uff0c\u6bd5\u5927\u9f99\u5934\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u4e0d \u9519\u3002\u8fd9\u9762\u5b50\u4f60\u503c\u5f97\u5356\u5427\uff1f\u201d\u97e9\u8001\u9556\u5934\u5927\u53eb\u9053\uff1a\u201c\u4f9d\u4f60\u8bf4\u6765\uff0c\u8fd9\u7fa4\u6076\u53eb\u5316\u7adf\u7136\u662f\u6bd5\u64ce\u5929\u5dee\u9063\u6765 \u7684\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff01\u201d\u97e9\u8001\u9556\u5934\u4e24\u773c\u7ffb\u767d\uff0c\u6c14\u5401\u5401\u5730\u762b\u5728\u5730\u4e0a\uff0c\u9053\uff1a\u201c\u51ed\u4ed6\u7684 \u8eab\u4efd\uff0c\u8981\u52ab\u6211\u533a\u533a\u8fd9\u4e00\u652f\u9556\uff1f\u8fd8\u4efb\u51ed\u624b\u4e0b\u4f7f\u51fa\u90a3\u7b49\u6076\u6bd2\u8be1\u8ba1\uff1f\u201d\u7384\u745b\u9053\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u52ab \u4f60\u52ab\u8c01\uff1f\u8c01\u53eb\u4f60\u4fdd\u8fd9\u652f\u9556\uff1f\u201d\u97e9\u8001\u9556\u5934\u8df3\u8d77\u6765\u9053\uff1a\u201c\u600e\u4e48\uff1f\u6211\u5f00\u9556\u884c\u7684\u4e0d\u4fdd\u9556\uff0c\u559d\u897f\u5317 \u98ce\uff1f\u201d\u6bd5\u613f\u7a77\u75bc\u75db\u7a0d\u6b62\uff0c\u53c8\u7b11\u563b\u563b\u5730\u9053\uff1a\u201c\u4f60\u97e9\u8001\u9556\u5934\u8fd8\u6015\u6ca1\u5403\u7684\u5417\uff1f\u6211\u4eec\u5012\u662f\u8981\u53e6\u4e00\u4e9b\u4eba \u997f\u997f\u809a\u5b50\uff01\u201d\u97e9\u8001\u9556\u5934\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u8bf7\u95ee\u8fd9\u652f\u9556\u662f\u8c01\u4ea4\u4f60\u4fdd\u7684\uff1f\u201d\u97e9\u8001 \u9556\u5934\u9053\uff1a\u201c\u4f60\u96be\u9053\u4e0d\u77e5\u6211\u751f\u5e73\u6709\u4e09\u4e0d\u4fdd\uff0c\u82e5\u7136\u8fd9\u652f\u9556\u6765\u5386\u4e0d\u660e\uff0c\u6211\u5c82\u6709\u4fdd\u5b83\u4e4b\u7406\uff1f\u201d\u7384\u745b\u9053 \u4eba\u9053\uff1a\u201c\u4f60\u4e09\u4e0d\u4fdd\u4e5f\u597d\uff0c\u4e09\u5341\u4e0d\u4fdd\u4e5f\u597d\uff0c\u8fd9\u6211\u7ba1\u4e0d\u7740\uff0c\u6211\u53ea\u95ee\u4f60\uff0c\u8fd9\u652f\u9556\u662f\u8c01\u7684\uff1f\u201d \u3000\u3000\u97e9\u8001\u9556\u5934\u6012\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u8fd9\u7b97\u662f\u5ba1\u95ee\u6211\u4e86\uff1f\u201d\u7384\u745b\u9053\uff1a\u201c\u4e0d\u6562\u3002\u8bf4\u4e0d\u8bf4\u5728\u4f60\uff0c\u8fd9\u652f\u9556 \u6211\u4eec\u662f\u8981\u5b9a\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u5657\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u652f\u9556\u6b63\u5728\u4eba\u5bb6\u624b\u91cc\uff0c\u4e24\u4f4d\u524d\u8f88\u4f55\u5fc5\u4f60\u4e89\u6211 \u593a\uff1f\u201d\u8fd9\u8bdd\u4e24\u8fb9\u90fd\u523a\u4e86\u4e00\u4e0b\uff0c\u53ef\u662f\u7531\u5979\u5e26\u7b11\u8bf4\u6765\uff0c\u4f17\u4eba\u90fd\u4e0d\u7981\u54d1\u7136\u5931\u7b11\uff0c\u8fd9\u5251\u62d4\u9a7d\u5f20\u7684\u6c14\u6c1b \u767b\u65f6\u7f13\u548c\u4e86\u4e0d\u5c11\u3002\u53f6\u6210\u6797\u9053\uff1a\u201c\u9556\u884c\u9ed1\u9053\uff0c\u5404\u6709\u89c4\u77e9\u3002\u97e9\u8001\u9556\u5934\u4e0d\u80af\u8bf4\u4e5f\u5c31\u7f62\u4e86\u3002\u201d\u767d\u5b5f\u5ddd \u88f9\u597d\u5251\u4f24\uff0c\u55d4\u76ee\u8bf4\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u4f60\u662f\u8c01\u4eba\uff1f\u6211\u4eec\u53ef\u5e76\u6ca1\u6709\u8bf7\u4f60\u51fa\u4e3b\u610f\u3002\u201d\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u767d\u8001 \u5f1f\u4f11\u5f97\u65e0\u793c\uff0c\u8fd9\u4f4d\u662f\u53f6\u7edf\u9886\u7684\u4f84\u5b50\u3002\u201d\u767d\u5b5f\u5ddd\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u4ecd\u7136\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u5c31 \u66f4\u65e0\u80f3\u818a\u5411\u5916\u5f2f\u4e4b\u7406\uff01\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u8fd9\u652f\u9556\u662f\u4e49\u519b\u8981\u7684\u5417\uff1f\u201d\u767d\u5b5f\u5ddd\u9053\uff1a\u201c\u96be\u9053\u662f\u6211\u6709\u8fd9 \u6837\u5927\u7684\u80c3\u53e3\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u6211\u53d4\u53d4\u2014\u2014\u4ed6\u77e5\u9053\u8fd9\u4e8b\u5417\uff1f\u201d\u767d\u5b5f\u5ddd\u9053\uff1a\u201c\u8fd9\uff0c\u8fd9\u2026\u2026\u201d\u539f\u6765 \u8fd9\u4e8b\u662f\u4ed6\u4eec\u79c9\u627f\u6bd5\u64ce\u5929\u7684\u65e8\u610f\u800c\u884c\uff0c\u5e76\u672a\u6709\u5411\u53f6\u5b97\u7559\u7980\u544a\u3002\u97e9\u8001\u9556\u5934\u51b7\u7b11\u9053\uff1a\u201c\u82e5\u662f\u53f6\u5b97\u7559 \u8981\u7684\uff0c\u6216\u8bb8\u6211\u8fd8\u5356\u8fd9\u4e2a\u9762\u5b50\u3002\u54c8\uff0c\u539f\u6765\u4f60\u4eec\u662f\u5047\u501f\u4e49\u519b\u4e4b\u540d\uff01\u201d\u767d\u5b5f\u5ddd\u6012\u9053\uff1a\u201c\u6bd5\u5927\u9f99\u5934\u505a \u4e0d\u5f97\u4e3b\u4e48\uff1f\u5c31\u662f\uff0c\u5c31\u662f\u2026\u2026\u201d\u4ed6\u672c\u6765\u60f3\u8bf4\uff1a\u201c\u5c31\u662f\u53f6\u5b97\u7559\u4e5f\u5f97\u542c\u6bd5\u5927\u9f99\u5934\u7684\u53f7\u4ee4\u3002\u201d\u4f46\u8fd9\u8bdd \u5230\u5e95\u4e0d\u65b9\u4fbf\u8bf4\u51fa\u6765\uff0c\u4e8e\u662f\u6539\u53e3\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u53f6\u7edf\u9886\u5728\u6b64\uff0c\u8fd9\u652f\u9556\u4ed6\u4e5f\u4e00\u5b9a\u662f\u8bf4\u975e\u52ab\u4e0d\u53ef\u3002\u201d \u6f6e\u97f3\u548c\u5c1a\u662f\u4e2a\u76f4\u80a0\u7684\u4eba\uff0c\u51b2\u53e3\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u4e0d\u662f\u4ed6\uff0c\u600e\u4e48\u4ee3\u4ed6\u8bf4\u8bdd\uff1f\u201d\u6362\u662f\u522b\u4eba\uff0c\u8fd9\u8bdd\u975e\u5f15 \u8d77\u5927\u4e89\u4e0d\u53ef\u3002\u6f6e\u97f3\u548c\u5c1a\u8f88\u4efd\u65e2\u9ad8\uff0c\u53c8\u662f\u6551\u6bd5\u613f\u7a77\u7684\u6069\u4eba\uff0c\u4e10\u5e2e\u6240\u6765\uff0c\u867d\u7136\u751a\u4e0d\u8212\u670d\uff0c\u5374\u65e0\u4e00 \u4eba\u53cd\u9a73\u3002\u53f6\u6210\u6797\u5fae\u7b11\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\u8bf4\u5f97\u597d\uff0c\u8fd9\u652f\u9556\u53cd\u6b63\u5728\u4eba\u5bb6\u624b\u91cc\uff0c\u5927\u5bb6\u81ea\u5df1\u4eba\u4f55\u5fc5\u5148\u4f60\u4e89 \u6211\u593a\u3002\u8fc7\u4e24\u5929\u6211\u5c31\u80fd\u89c1\u770b\u53d4\u53d4\u548c\u6bd5\u5927\u9f99\u5934\uff0c\u6211\u518d\u8bf7\u4ed6\u4eec\u8d70\u593a\u3002\u8c05\u90a3\u51cc\u4e91\u51e4\u5728\u51e0\u5929\u4e4b\u5185\u672a\u5fc5\u82b1 \u5f97\u5b8c\uff0c\u8fd9\u652f\u9556\u8fd8\u4e0d\u662f\u7b49\u4e8e\u5bc4\u5b58\u5728\u5979\u90a3\u91cc\u4e00\u6837\u5417\uff1f\u201d\u8fd9\u8bf4\u8bdd\u9762\u9762\u90fd\u7167\u987e\u5230\uff0c\u97e9\u8001\u9556\u5934\u6012\u6c14\u7a0d \u5e73\uff0c\u70b9\u5934\u8a00\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31\u542c\u4f60\u53d4\u53d4\u4e00\u53e5\u8bdd\uff01\u201d\u6bd5\u613f\u7a77\u7709\u5934\u4e00\u76b1\uff0c\u767d\u5b5f\u5ddd\u9762\u8272\u5927\u53d8\uff0c\u53eb\u8d77 \u6765\u9053\uff1a\u201c\u8fd9\uff0c\u8fd9\u4e8b\u53ef\u4e0d\u80fd\u8fdf\u529e\uff01\u201d \u3000\u3000\u97e9\u8001\u9556\u5934\u9053\uff1a\u201c\u600e\u4e48\uff0c\u6709\u672c\u4e8b\u4f60\u53bb\u628a\u8fd9\u9556\u62ff\u56de\uff0c\u6211\u97e9\u67d0\u53cc\u624b\u5949\u9001\u3002\u201d\u773c\u770b\u7eb7\u4e89\u53c8\u8d77\uff0c\u5ffd \u542c\u5f97\u6709\u6572\u95e8\u4e4b\u58f0\uff0c\u4e8e\u627f\u73e0\u671b\u4e86\u773c\uff0c\u7b11\u9053\uff1a\u201c\u4eba\u5bb6\u53ef\u5148\u6765\u5566\u3002\u201d\u53f6\u6210\u6797\u6253\u5f00\u5e99\u95e8\uff0c\u53ea\u89c1\u4e24\u4e2a\u5c11 \u5973\uff0c\u674f\u9ec4\u886b\u513f\uff0c\u767d\u7ee3\u675f\u8170\uff0c\u4e00\u4eba\u6367\u7740\u62dc\u5323\uff0c\u4e00\u4eba\u63d0\u7740\u706f\u7b3c\u7f13\u7f13\u8d70\u5165\uff0c\u5374\u539f\u6765\u5c31\u662f\u7ea2\u5dfe\u5973\u8d3c\u51cc \u4e91\u51e4\u4e24\u4e2a\u8d34\u8eab\u4e2b\u9b1f\u3002\u6367\u62dc\u5323\u7684\u90a3\u4e2a\u5411\u4f17\u4eba\u626b\u4e86\u4e00\u773c\uff0c\u773c\u5149\u505c\u5728\u4e8e\u627f\u73e0\u8eab\u4e0a\uff0c\u884c\u4e0a\u524d\u6765\uff0c\u5c06\u62dc \u5323\u5448\u4e0a\uff0c\u4e8e\u627f\u73e0\u5947\u9053\uff1a\u201c\u4f60\u5bb6\u5be8\u4e3b\u53eb\u4f60\u6765\u8bf7\u8c01\u554a\uff1f\u201d\u90a3\u4e2b\u9b1f\u9053\uff1a\u201c\u8bf7\u5973\u4fa0\u628a\u62dc\u5323\u6253\u5f00\u3002\u201d\u4e8e \u627f\u73e0\u7565\u4e00\u8e0c\u8e87\uff0c\u970d\u5730\u628a\u62dc\u5323\u6253\u5f00\uff0c\u53ea\u89c1\u5185\u91cc\u4e09\u6735\u91d1\u82b1\uff0c\u6574\u6574\u9f50\u9f50\u5730\u5d4c\u5728\u62dc\u5e16\u4e4b\u4e0a\uff0c\u90a3\u4e2b\u9b1f \u9053\uff1a\u201c\u6211\u5bb6\u5be8\u4e3b\u8bf7\u8fd9\u4e09\u6735\u91d1\u82b1\u7684\u4e3b\u4eba\uff01\u201d\u4e8e\u627f\u73e0\u5fae\u5fbd\u4e00\u7b11\uff0c\u6361\u8d77\u4e09\u6735\u91d1\u82b1\uff0c\u8bf4\u9053\uff1a\u201c\u96d5\u866b\u5c0f \u6280\uff0c\u8d3b\u7b11\u4f60\u5bb6\u5be8\u4e3b\u4e86\u3002\u201d\u90a3\u4e2b\u73af\u9053\uff1a\u201c\u59d1\u5a18\u7684\u91d1\u82b1\u5999\u6280\uff0c\u6211\u5bb6\u5be8\u4e3b\u4f69\u670d\u5f97\u5f88\u3002\u5979\u8bf4\uff0c\u770b\u5728\u8fd9 \u4e09\u6735\u91d1\u82b1\u7684\u9762\u4e0a\uff0c\u8bf7\u59d1\u5a18\u7684\u670b\u53cb\u4eec\u4e5f\u4e00\u540c\u4e0a\u5c71\u3002\u201d\u7384\u745b\u9053\u4eba\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u521a\u624d\u5728\u6df7\u6218 \u4e2d\u53d1\u6697\u5668\u76f8\u6551\u7684\u662f\u4e8e\u627f\u73e0\uff0c\u4e00\u76f4\u8fd8\u4ee5\u4e3a\u662f\u97e9\u8001\u9556\u5934\u7684\u900f\u9aa8\u9488\u5462\u3002\u6bd5\u613f\u7a77\u563b\u563b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1 \u5a18\uff0c\u8fd9\u56de\u54b1\u4eec\u5168\u6cbe\u4e86\u4f60\u7684\u5149\u4e86\u3002\u767d\u8d24\u5f1f\uff0c\u4f60\u6276\u6211\u4e0a\u5c71\u53bb\u3002\u201d\u767d\u5b5f\u5ddd\u9053\uff1a\u201c\u4f60\u6b47\u6b47\u5427\u3002\u201d\u6bd5\u613f \u7a77\u9053\uff1a\u201c\u6709\u59d1\u5976\u5976\u51fa\u5934\uff0c\u8fd9\u652f\u9556\u4eca\u65e5\u975e\u8ba8\u56de\u4e0d\u53ef\u3002\u201d\u4e8e\u627f\u73e0\u5477\u4e86\u4e00\u53e3\uff0c\u9053\uff1a\u201c\u8c01\u548c\u4f60\u7a77\u5f00 \u5fc3\u3002\u201d\u6bd5\u613f\u7a77\u7b11\u9053\uff1a\u201c\u8ba8\u56de\u8fd9\u652f\u9556\u94f6\uff0c\u4f60\u5bb6\u5927\u7237\u53ef\u5c31\u9614\u5566\u3002\u597d\u5c0f\u59d0\uff0c\u6211\u4e0d\u6562\u5f97\u7f6a\u4f60\u5566\uff0c\u770b\u5728 \u4f60\u5e08\u7236\u548c\u54b1\u4eec\u4e10\u5e2e\u8001\u5e2e\u4e3b\u7684\u4ea4\u60c5\u4e0a\uff0c\u8fd9\u652f\u9556\u94f6\u4f60\u662f\u975e\u5f97\u8ba8\u56de\u4e0d\u53ef\u3002\u201d\u6491\u7740\u5899\u58c1\uff0c\u5411\u4e8e\u627f\u73e0\u5c48 \u4e86\u534a\u819d\uff0c\u90a3\u6001\u5ea6\u7adf\u662f\u5341\u5206\u8ba4\u771f\uff0c\u628a\u4e8e\u627f\u73e0\u5f04\u5f97\u6c14\u4e5f\u4e0d\u662f\uff0c\u607c\u4e5f\u4e0d\u662f\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6bd5\u64ce\u5929\u867d \u7136\u8dcb\u6248\uff0c\u4f46\u4e5f\u8fd8\u4e0d\u662f\u80e1\u4f5c\u975e\u4e3a\u7684\u4eba\uff0c\u7384\u745b\u9053\u4eba\u66f4\u662f\u6b63\u6d3e\u7684\u6b66\u6797\u4eba\u7269\uff0c\u4ed6\u4eec\u90fd\u8fd9\u822c\u7740\u6025\uff0c\u96be\u9053 \u8fd9\u652f\u9556\u94f6\u771f\u662f\u6709\u4ec0\u4e48\u91cd\u5927\u7684\u5173\u7cfb\uff1f\u201d \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u97e9\u5927\u54e5\uff0c\u4f60\u7684\u89e3\u836f\u8be5\u62ff\u51fa\u6765\u4e86\u5427\uff1f\u6211\u5728\u8fd9\u7834\u5e99\u66ff\u4f60\u4eec\u770b\u5bb6\uff0c\u4e5f\u7ed9\u8fd9\u51e0\u4f4d \u53d7\u4f24\u7684\u5316\u5b50\u5927\u7237\u8c03\u7406\u8c03\u7406\u3002\u201d\u97e9\u8001\u9556\u5934\u4e00\u60f3\uff0c\u4e10\u5e2e\u52ab\u9556\uff0c\u867d\u7136\u53ef\u6076\uff0c\u4f46\u8981\u4ed6\u4eec\u7684\u547d\u5374\u4e5f\u592a \u8fc7\uff0c\u5148\u524d\u4e0d\u77e5\u9053\u4ed6\u4eec\u7684\u6765\u5386\uff0c\u73b0\u5728\u65e2\u77e5\u4ed6\u4eec\u786e\u662f\u4e10\u5e2e\u4e2d\u4eba\uff0c\u90a3\u4fbf\u65e0\u8bba\u5219\u53ef\uff0c\u603b\u5f97\u7559\u6709\u4f59\u5730\uff0c \u542c\u6f6e\u97f3\u548c\u5c1a\u4e00\u8bf4\uff0c\u4fbf\u987a\u6c34\u63a8\u821f\u5730\u5c06\u89e3\u836f\u62ff\u4e86\u51fa\u6765\uff0c\u5e76\u4ea4\u4e86\u4e00\u4efd\u7ed9\u6bd5\u613f\u7a77\u3002\u6bd5\u613f\u7a77\u7b11\u9053\uff1a\u201c\u4f60 \u9001\u6211\u89e3\u836f\uff0c\u6211\u9886\u4f60\u7684\u60c5\u4efd\u3002\u53ef\u8fd9\u652f\u9556\u6211\u8fd8\u662f\u975e\u8981\u4e0d\u53ef\u3002\u201d\u97e9\u8001\u9556\u5934\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u884c\u5440\uff0c \u90a3\u5c31\u518d\u770b\u4f60\u7684\u672c\u4e8b\u5427\u3002\u201d \u3000\u3000\u9664\u4e86\u6f6e\u97f3\u548c\u5c1a\u4e4b\u5916\uff0c\u4e00\u884c\u4eba\u90fd\u968f\u90a3\u4e2b\u9b1f\u4e0a\u5c71\uff0c\u4e0a\u5f97\u5c71\u6765\uff0c\u5df2\u662f\u5929\u8272\u5fae\u660e\uff0c\u665a\u971e\u9690\u89c4\u3002\u8299 \u84c9\u5c71\u4e43\u662f\u4ed9\u971e\u5cad\u7684\u4e00\u4e2a\u652f\u8109\uff0c\u5c71\u52bf\u5e76\u4e0d\u600e\u4e48\u9669\u5cfb\uff0c\u53ef\u662f\u7ecf\u8fc7\u51cc\u4e91\u51e4\u7684\u5e03\u7f6e\uff0c\u51b2\u8981\u4e4b\u5904\uff0c\u7889\u5821 \u68ee\u4e25\u3002\u6805\u57ce\u56f4\u7ed5\uff0c\u770b\u6765\u7adf\u4e0d\u4e9a\u4e8e\u91d1\u57ce\u6c64\u6c60\u3002\u53f6\u6210\u6797\u4e5f\u4e0d\u7981\u6697\u6697\u4f69\u670d\uff0c\u5fc3\u4e2d\u53f9\u9053\uff1a\u201c\u8349\u91ce\u4e4b \u4e2d\uff0c\u4e0d\u77e5\u57cb\u6ca1\u591a\u5c11\u4eba\u624d\uff1f\u5c31\u662f\u8fd9\u7ea2\u5dfe\u5973\u8d3c\uff0c\u4fbf\u4e0d\u8f93\u4e8e\u624b\u63e1\u5175\u7b26\u7684\u5927\u5c06\u3002\u201d \u3000\u3000\u90a3\u4e2b\u9b1f\u8ba9\u4f17\u4eba\u7a0d\u5019\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u53ea\u542c\u5f97\u91cc\u9762\u4e09\u901a\u9f13\u54cd\uff0c\u5be8\u95e8\u5927\u5f00\uff0c\u53f6\u6210\u6797\u6025\u5fd9\u5c06\u4e8e\u627f\u73e0 \u63a8\u5230\u524d\u9762\uff0c\u539f\u6765\u8fd9\u662f\u7eff\u6797\u4e2d\u8fce\u63a5\u8d35\u5bbe\u4e4b\u793c\uff0c\u5979\u8bf7\u7684\u4e3b\u5ba2\u662f\u4e8e\u627f\u73e0\uff0c\u5c3d\u7ba1\u4e8e\u627f\u73e0\u8f88\u4efd\u6700\u4f4e\uff0c\u4f17 \u4eba\u5374\u662f\u4e0d\u80fd\u50ed\u8d8a\u3002 \u3000\u3000\u53ea\u89c1\u5be8\u4e2d\u4e24\u961f\u5973\u5175\u6392\u5217\uff0c\u51cc\u4e91\u51e4\u620e\u88c5\u4f69\u5251\uff0c\u51fa\u5be8\u76f8\u8fce\u3002\u4e8e\u627f\u73e0\u843d\u843d\u5927\u65b9\uff0c\u4ee5\u793c\u76f8\u89c1\u3002\u9053 \u4e86\u59d3\u540d\uff0c\u51cc\u4e91\u51e4\u5ffd\u7136\u95f4\u95ee\u9053\uff1a\u201c\u4e8e\u5c0f\u59d0\u4e0e\u5f20\u4e39\u67ab\u5927\u4fa0\u600e\u6837\u79f0\u547c\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u90a3\u662f\u5bb6 \u5e08\u3002\u201d\u51cc\u4e91\u51e4\u7b11\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4e8e\u5c0f\u59d0\u7528\u91d1\u82b1\u6697\u5668\u3002\u201d\u53c8\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u4eba\u79f0\u6563\u82b1\u5973\u4fa0\u7684\u60f3\u5fc5\u5c31 \u662f\u59d0\u59d0\u4e86\uff1f\u201d\u95ee\u8fd9\u8bdd\u65f6\uff0c\u773c\u5149\u4e2d\u6709\u4e00\u79cd\u5f02\u6837\u7684\u8868\u60c5\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u662f\u6c5f\u6e56\u4e0a\u7684\u524d\u8f88\u5956\u6396\u540e\u8fdb\uff0c\u5c0f\u59b9\u5c82\u6562\u5f53\u5973\u4fa0\u3002\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u4eba\u7684\u540d \u513f\uff0c\u6811\u7684\u5f71\u513f\uff0c\u90a3\u662f\u7edd\u5bf9\u5047\u4e0d\u4e86\u7684\u3002\u5973\u4fa0\u51fa\u4e8e\u5fe0\u5b5d\u4e4b\u5bb6\uff0c\u4e49\u4fa0\u4e4b\u95e8\uff0c\u5c0f\u59b9\u4ef0\u6155\u5f97\u7d27\uff0c\u8bf7\u53d7\u4e00 \u62dc\uff01\u201d\u51cc\u4e91\u51e4\u662f\u4e00\u5be8\u4e4b\u4e3b\uff0c\u7b80\u9080\u4e8e\u627f\u73e0\u4e0a\u5c71\uff0c\u6309\u7eff\u6797\u7684\u89c4\u77e9\u6765\u8bf4\uff0c\u5728\u51cc\u4e91\u51e4\u8fd9\u8fb9\u662f\u8bf7\u5ba2\uff0c\u5728 \u4e8e\u627f\u73e0\u8fd9\u8fb9\u5219\u662f\u62dc\u5c71\uff0c\u6700\u591a\u662f\u4ee5\u5f1f\u8f88\u4e4b\u793c\u76f8\u53d9\uff0c\u65ad\u65e0\u4e3b\u4eba\u62dc\u5ba2\u4e4b\u793c\u3002\u51cc\u4e91\u51e4\u8fd9\u4e00\u4e3e\u52a8\uff0c\u5b9e\u662f \u5927\u51fa\u5bfb\u5e38\uff0c\u540c\u6765\u8bf8\u4eba\uff0c\u65e0\u4e0d\u60ca\u8bb6\uff01\u4e8e\u627f\u73e0\u6025\u5fd9\u907f\u5f00\uff0c\u51cc\u4e91\u51e4\u5374\u5df2\u62e6\u5728\u9762\u524d\uff0c\u76c8\u76c8\u4e0b\u62dc\uff0c\u4e24\u8fb9 \u6324\u7740\u5973\u5175\uff0c\u907f\u65e0\u53ef\u907f\uff0c\u53ea\u597d\u4e00\u9762\u62e6\u7740\u51cc\u4e91\u51e4\uff0c\u4e00\u9762\u5c48\u4e0b\u534a\u819d\u8fd8\u793c\u3002\u54ea\u77e5\u51cc\u4e91\u51e4\u4e0b\u62dc\u4e4b\u65f6\uff0c\u731d \u7136\u95f4\u53cc\u81c2\u4e00\u62ac\uff0c\u5c06\u4e8e\u627f\u73e0\u6276\u8d77\uff0c\u4e8e\u627f\u73e0\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u5979\u662f\u8d81\u52bf\u8f83\u91cf\u6211\u4e48\uff1f\u201d\u5ff5\u5934 \u65b9\u52a8\uff0c\u8fd8\u672a\u53ca\u8fd0\u52b2\u76f8\u6297\uff0c\u51cc\u4e91\u51e4\u53cc\u81c2\u4e00\u5782\uff0c\u5374\u5df2\u6df1\u6df1\u5730\u4f5c\u4e86\u4e00\u62dc\u3002\u5ffd\u5730\u773c\u5708\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a \u201c\u6211\u751f\u5e73\u6700\u656c\u6155\u7684\u662f\u4e8e\u5927\u4eba\u548c\u5f20\u5927\u4fa0\uff0c\u4e8e\u5927\u4eba\u5f53\u5e74\u542b\u51a4\u4e0b\u72f1\uff0c\u6211\u672a\u5f97\u5c3d\u534a\u70b9\u5fc3\u529b\uff0c\u8fd9\u4e00\u62dc\u662f \u62dc\u4ee4\u5c0a\u7684\uff0c\u8bf7\u59d0\u59d0\u66ff\u5c0a\u5927\u4eba\u53d7\u793c\uff01\u201d\u4e8e\u627f\u73e0\u6697\u53eb\u201c\u60ed\u6127\u201d\u3002\u539f\u6765\u8fd9\u6709\u5973\u9b54\u5934\u4e4b\u79f0\u7684\u7ea2\u5dfe\u5973\u8d3c \u7adf\u7136\u662f\u8840\u6027\u82f1\u96c4\uff0c\u89c1\u5979\u5982\u6b64\u656c\u91cd\u81ea\u5df1\u7684\u7236\u4eb2\uff0c\u8fd9\u4e00\u62dc\u5012\u4e0d\u597d\u63a8\u8f9e\u4e86\u3002\u5f53\u4e0b\u542b\u6cea\u8fd8\u793c\uff0c\u6293\u7d27\u51cc \u4e91\u51e4\u7684\u624b\uff0c\u5c31\u50cf\u4e00\u5bf9\u5206\u522b\u4e86\u591a\u5e74\u7684\u59d0\u59b9\u89c1\u9762\u4e00\u822c\u3002\u97e9\u8001\u9556\u5934\u548c\u7384\u745b\u9053\u4eba\u5fc3\u4e2d\u6697\u559c\uff0c\u5747\u662f\u60f3 \u9053\uff1a\u201c\u96be\u5f97\u8fd9\u5973\u8d3c\u5bf9\u4e8e\u627f\u73e0\u7d20\u773c\u6709\u52a0\uff0c\u770b\u6765\u8ba8\u9556\u6709\u671b\u4e86\u3002\u201d \u3000\u3000\u51cc\u4e91\u51e4\u8bf7\u4e8e\u627f\u73e0\u5750\u5728\u4e0a\u9996\uff0c\u542b\u7b11\u95ee\u9053\uff1a\u201c\u5c0f\u59b9\u8fd9\u6b21\u8bf7\u59d0\u59d0\u4e0a\u5c71\uff0c\u4e00\u6765\u662f\u4e3a\u4e86\u5fc3\u4e2d\u4ef0\u6155\uff0c \u85c9\u6b64\u8bc6\u8346\uff0c\u4e8c\u6765\u662f\u60f3\u8bf7\u95ee\u59d0\u59d0\u53d1\u90a3\u4e09\u6735\u91d1\u82b1\u7684\u7528\u610f\u3002\u201d\u4e8e\u627f\u73e0\u89c1\u5979\u610f\u6c14\u76f8\u6295\uff0c\u4e0d\u518d\u63a9\u9970\uff0c\u5355 \u5200\u76f4\u5165\u5730\u8bf4\u9053\uff1a\u201c\u660e\u4eba\u9762\u524d\u4e0d\u8bf4\u5047\u8bdd\uff0c\u90a3\u662f\u4e3a\u4e86\u8fd9\u4e00\u652f\u9556\u3002\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u55ef\uff0c\u8fd9\u4e00\u652f \u9556\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u662f\u5440\uff0c\u8fd9\u4e00\u652f\u9556\u662f\u97e9\u8001\u9556\u5934\u4fdd\u7684\u3002\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u8fd9\u6211\u65e9\u5c31\u77e5\u9053\uff0c\u5c31\u56e0 \u4ed6\u4fdd\u4e86\u8fd9\u4e00\u652f\u9556\uff0c\u6211\u662f\u975e\u52ab\u4e0d\u53ef\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u652f\u9556\u7275\u8fde\u53ef\u5927\u7740\u5462\u3002\u6bd5\u64ce\u5929\u4e5f\u60f3\u52ab\u8fd9\u652f \u9556\u3002\u55ef\uff0c\u6211\u662f\u4e0d\u660e\u767d\u4f60\u4eec\u4e3a\u4ec0\u4e48\u90fd\u8981\u52ab\u8fd9\u652f\u9556\uff1f\u4f46\u60f3\u6765\u5fc5\u6709\u590d\u6742\u7684\u5185\u60c5\uff0c\u4e0d\u59a8\u5927\u5bb6\u8bf4\u4e2a\u660e \u767d\uff01\u201d\u51cc\u4e91\u51e4\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u81ea\u5c01\u5341\u516b\u7701\u5927\u9f99\u5934\u6bd5\u64ce\u5929\u4e5f\u8981\u52ab\u8fd9\u652f\u9556\uff1f\u8fd9\u7fa4\u5316\u5b50\u548c\u8fd9\u725b\u9f3b\u5b50 \u5c31\u662f\u4ed6\u5dee\u9063\u6765\u7684\uff1f\u54fc\uff0c\u7adf\u7136\u7528\u90a3\u79cd\u4e0b\u6d41\u6697\u7b97\u7684\u624b\u6bb5\u52ab\u9556\uff1f\u8981\u4e0d\u662f\u4f60\u8bf4\uff0c\u6211\u7edd\u4e0d\u76f8\u4fe1\u3002\u201d\u4e8e\u627f \u73e0\u8138\u4e0a\u70ed\u8fa3\u8fa3\u7684\uff0c\u4e0d\u7531\u5f97\u66ff\u6bd5\u64ce\u5929\u96be\u8fc7\uff0c\u60f3\u8d77\u6bd5\u64ce\u5929\u7684\u505a\u4e8b\u6bcf\u591a\u4e0d\u62e9\u624b\u6bb5\uff0c\u786e\u662f\u6709\u635f\u5a01\u671b\uff0c \u5f04\u5f97\u81ea\u5df1\u4e5f\u65e0\u8f9e\u7f6e\u7b54\u3002\u6bd5\u613f\u7a77\u7a81\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u7b11\u563b\u563b\u5730\u9053\uff1a\u201c\u8bf7\u95ee\u5be8\u4e3b\uff0c\u522b\u4eba\u628a\u5200\u6401\u5728\u4f60\u7684 \u8116\u5b50\u4e0a\uff0c\u4f60\u662f\u4e0d\u662f\u8981\u8bf7\u522b\u4eba\u5148\u653e\u4e0b\u5200\u5b50\uff0c\u518d\u5149\u660e\u78ca\u843d\u5730\u8f83\u91cf\uff1f\u8fd8\u662f\u5c3d\u5feb\u5c06\u4ed6\u51fb\u5012\uff0c\u4ee5\u514d\u9664\u5371 \u9669\uff1f\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u97e9\u8001\u9556\u5934\u6c14\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u4e5f\u8df3\u8d77\u6765\u9053\uff1a\u201c\u662f\u554a\uff0c \u4f60\u8bf4\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u6211\u4fdd\u6211\u7684\u9556\uff0c\u5bf9\u4f60\u6709\u4f55\u635f\u4f24\uff1f\u201d\u6bd5\u613f\u7a77\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u9556\u8fd0\u5230\u6e56\u5317\uff0c \u90a3\u5c31\u66ff\u671d\u5ef7\u78e8\u5229\u5341\u4e07\u5f20\u5200\u5b50\uff0c\u6765\u5bf9\u4ed8\u6211\u4eec\u6c5f\u5357\u7684\u4e49\u519b\uff01\u201d\u97e9\u8001\u9556\u5934\u6012\u53eb\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u4f60 \u77e5\u9053\u6211\u4fdd\u7684\u662f\u4ec0\u4e48\u9556\uff1f\u201d\u51cc\u4e91\u51e4\u6e9c\u4e86\u97e9\u8001\u9556\u5934\u4e00\u773c\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u6b64\u523b\u5c31\u8981\u770b\u4f60\u4fdd\u7684\u662f\u4ec0\u4e48 \u9556\uff1f\u201d \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u5973\u5175\u628a\u6628\u65e5\u6253\u52ab\u7684\u90a3\u4e9b\u5927\u5927\u5c0f\u5c0f\u7684\u7bb1\u6760\uff0c\u90fd\u5806\u5230\u5385\u5b50\u4e0a\uff0c\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u97e9\u9556 \u5934\uff0c\u4f60\u8bf4\uff0c\u4f60\u4fdd\u7684\u662f\u4ec0\u4e48\u9556\uff1f\u201d\u97e9\u8001\u9556\u5934\u56b7\u9053\uff1a\u201c\u8fd9\u662f\u5317\u4eac\u4e50\u5bb6\u6258\u6211\u4fdd\u7684\u8d35\u91cd\u836f\u6750\uff0c\u8fd0\u5230\u6e56 \u5317\uff0c\u4ea6\u662f\u6d4e\u4e16\u6551\u4eba\uff0c\u6709\u4ec0\u4e48\u9519\u4e86\uff1f\u201d\u5317\u4eac\u4e50\u5bb6\u4e43\u5168\u56fd\u836f\u5546\u7684\u9996\u8111\uff0c\u5bcc\u7532\u4eac\u534e\uff0c\u6bcf\u5e74\u90fd\u8981\u8bf7\u4e00 \u6b21\u4fdd\u9556\uff0c\u5c06\u836f\u6750\u8fd0\u9500\u6c5f\u5357\uff0c\u662f\u9556\u884c\u6700\u597d\u7684\u4e3b\u987e\uff0c\u8fd9\u4e00\u6b21\u4fdd\u7684\u7279\u522b\u8d35\u91cd\uff0c\u6240\u4ee5\u624d\u8bf7\u5230\u97e9\u8001\u9556 \u5934\u3002\u4e8e\u627f\u73e0\u8be7\u5f02\u4e4b\u6781\uff0c\u5fc3\u9053\uff1a\u201c\u82e5\u662f\u4e50\u5bb6\u4fdd\u7684\u836f\u6750\uff0c\u90a3\u5c31\u66f4\u4e0d\u8be5\u52ab\u4e86\uff01\u201d \u3000\u3000\u6bd5\u613f\u7a77\u51b7\u7b11\u9053\uff1a\u201c\u6d4e\u4e16\u6551\u4eba\uff0c\u6211\u8bf4\u5374\u662f\u4e71\u4e16\u5bb3\u4eba\uff01\u201d\u97e9\u8001\u9556\u5934\u559d\u9053\uff1a\u201c\u4f60\u72d7\u53e3\u91cc\u4e0d\u957f\u8c61 \u7259\uff01\u201d\u51cc\u4e91\u51e4\u628a\u624b\u4e00\u62e9\uff0c\u53eb\u9053\uff1a\u201c\u4e0e\u6211\u628a\u8fd9\u4e9b\u7bb1\u7b3c\u90fd\u5288\u5f00\u6765\u770b\uff01\u201d\u97e9\u8001\u9556\u5934\u6c14\u5f97\u624b\u98a4\u811a\u9707\uff0c \u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u5c82\u4e0d\u662f\u5c06\u836f\u6750\u7cdf\u8e0f\u4e86\u4e48\uff1f\u201d\u4e50\u5bb6\u4ea4\u4ed6\u4fdd\u8fd9\u652f\u9556\u65f6\uff0c\u66fe\u8bf4\u660e\u5927\u90e8\u5206\u7684\u8d35\u91cd\u836f\u6750\u5fc5 \u987b\u5bc6\u5c01\uff0c\u514d\u5f97\u8d70\u4e86\u6c14\u5473\uff0c\u53ef\u662f\u8fd9\u65f6\u97e9\u8001\u9556\u5934\u4e3a\u4e86\u9a8c\u660e\u771f\u76f8\uff0c\u52bf\u4e0d\u80fd\u4e0a\u524d\u963b\u62e6\u3002\u970e\u65f6\u95f4\uff0c\u90a3\u4e9b \u5927\u5927\u5c0f\u5c0f\u7684\u7bb1\u7b3c\u90fd\u88ab\u5288\u5f00\uff0c\u4f46\u89c1\u836f\u6750\u62e8\u5f00\uff0c\u91cc\u9762\u9732\u51fa\u7684\u90fd\u662f\u9ec4\u6f84\u6f84\u7684\u91d1\u5b50\u3002\u6bd5\u613f\u7a77\u51b7\u7b11\u9053\uff1a \u201c\u5982\u4f55\uff1f\u8fd9\u662f\u5b98\u5bb6\u7684\u519b\u664c\uff0c\u603b\u503c\u4e03\u5341\u4e07\u4e24\u767d\u94f6\u7684\u91d1\u5b50\uff0c\u90a3\u662f\u4e5d\u95e8\u63d0\u7763\u5949\u4e86\u7687\u547d\uff0c\u5f3a\u8feb\u4e50\u5bb6\u51fa \u9762\uff0c\u5047\u8bf4\u662f\u836f\u6750\u6258\u4f60\u4ee3\u8fd0\u7684\u3002\u6e56\u5317\u5341\u4e07\u5b98\u519b\uff0c\u65ad\u9977\u7f3a\u7cae\uff0c\u82e5\u65e0\u63a5\u6d4e\uff0c\u4e0d\u6218\u81ea\u6e83\uff0c\u4f60\u7ed9\u4ed6\u4eec\u4fdd \u8fd9\u652f\u9556\u5230\u6e56\u5317\u53bb\uff0c\u90a3\u5c82\u4e0d\u662f\u7ed9\u5b98\u519b\u9001\u4e0a\u4e86\u7eed\u547d\u6c64\uff0c\u8ba9\u4ed6\u4eec\u78e8\u5229\u5341\u4e07\u5f20\u5200\u5b50\u6765\u5bf9\u4ed8\u6211\u4eec\u5417\uff1f\u201d \u3000\u3000\u97e9\u8001\u9556\u5934\u624b\u811a\u51b0\u51b7\uff0c\u60f3\u4e0d\u5230\u81ea\u5df1\u4e00\u751f\u4e0d\u4fdd\u5b98\u94f6\uff0c\u8fd9\u6b21\u5374\u4e0a\u4e86\u5b98\u5bb6\u7684\u5708\u5957\u3002\u90a3\u4e50\u5bb6\u662f\u8457\u540d \u7684\u6bb7\u5b9e\u5546\u4eba\uff0c\u600e\u6599\u4ed6\u5374\u5728\u5b98\u5bb6\u7684\u5a01\u8feb\u4e4b\u4e0b\uff0c\u53eb\u81ea\u5df1\u4e5f\u4e00\u540c\u4e0a\u5f53\u3002\u767d\u5b5f\u5ddd\u5927\u53eb\u9053\uff1a\u201c\u97e9\u8001\u5934 \u513f\uff0c\u770b\u6e05\u695a\u6ca1\u6709\uff1f\u8fd9\u652f\u9556\u662f\u4e0d\u662f\u8be5\u7531\u6211\u4eec\u622a\u4e0b\u4e86\uff1f\u201d\u4f46\u542c\u5f97\u201c\u5495\u549a\u201d\u4e00\u58f0\uff0c\u97e9\u8001\u9556\u5934\u4e00\u53e3\u6c14 \u900f\u4e0d\u8fc7\u6765\uff0c\u6655\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u5c06\u8fd9\u8001\u513f\u6276\u8fdb\u53bb\uff0c\u7528\u51b7\u6c34\u5c06\u4ed6\u55b7\u9192\uff01\u201d\u4e8e\u627f\u73e0\u53f9\u4e86\u53e3\u6c14\uff0c\u60f3\u4e0d\u5230\u81ea\u5df1\u7236\u4eb2 \u4e00\u751f\u5fe0\u5fc3\u803f\u803f\u6276\u52a9\u7684\u671d\u5ef7\uff0c\u884c\u4e8b\u7adf\u662f\u5982\u540c\u5bb5\u5c0f\uff0c\u9a97\u4e86\u5546\u4eba\uff0c\u9a97\u4e86\u9556\u884c\uff0c\u6c5f\u5357\u836f\u6750\uff0c\u4eca\u5e74\u4e5f\u5c06 \u56e0\u4e4b\u7f3a\u8d27\u3002\u6bd5\u613f\u7a77\u5f97\u610f\u6d0b\u6d0b\u5730\u8bf4\u9053\uff1a\u201c\u5e78\u4e8f\u54b1\u4eec\u7684\u5927\u9f99\u5934\u8033\u76ee\u7075\u806a\uff0c\u5b98\u5bb6\u4ee5\u4e3a\u54b1\u4eec\u4e0d\u52ab\u9556 \u884c\uff0c\u53ef\u4ee5\u6df7\u8fc7\uff0c\u54c8\u54c8\uff0c\u5230\u5e95\u8fd8\u662f\u7ed9\u54b1\u4eec\u622a\u4f4f\u3002\u201d\u51cc\u4e91\u51e4\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u652f\u9556\u53ef\u8fd8\u4e0d\u662f\u5728\u4f60\u7684\u624b \u4e2d\u5462\uff01\u201d \u3000\u3000\u6bd5\u613f\u7a77\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u6765\u5386\u65e2\u660e\uff0c\u4f60\u8fd8\u8981\u52ab\u8fd9\u652f\u9556\u5417\uff1f\u201d\u51cc\u4e91\u51e4\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u6bd5\u64ce\u5929\u52ab \u5f97\uff0c\u6211\u5c31\u4e0d\u80fd\u52ab\u5f97\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u770b\u5728\u53f6\u5b97\u7559\u65e2\u7387\u7684\u4e49\u519b\u4efd\u4e0a\uff0c\u59d0\u59d0\u4f60\u5c31\u5f97\u9ad8\u62ac\u8d35\u624b\u4e86 \u5427\uff01\u201d\u53f6\u5b97\u7559\u5728\u6c5f\u6e56\u4e0a\u58f0\u671b\u6781\u9ad8\uff0c\u6bd5\u64ce\u5929\u867d\u7136\u81ea\u5c01\u4e3a\u5341\u516b\u7701\u5927\u9f99\u5934\uff0c\u4f46\u4ecd\u8981\u4ef0\u4ed7\u4e8e\u4ed6\uff0c \u201c\u5c01\u201d\u4ed6\u505a\u4e49\u519b\u7684\u5927\u7edf\u9886\uff0c\u51cc\u4e91\u51e4\u542c\u5f97\u4e8e\u627f\u73e0\u62ac\u51fa\u4e86\u53f6\u5b97\u7559\u7684\u540d\u53f7\uff0c\u8038\u7136\u52a8\u5bb9\uff0c\u5fae\u5fae\u7b11\u9053\uff1a \u201c\u8fd9\u5e2e\u6076\u4e10\u548c\u81ed\u9053\u58eb\u6211\u624d\u61d2\u7ba1\uff0c\u53f6\u5927\u54e5\u548c\u4f60\u7684\u5e10\u6211\u5356\u4e86\u5427\u3002\u201d\u4e8e\u627f\u73e0\u5927\u559c\u9053\uff1a\u201c\u591a\u8c22\u59d0 \u59d0\uff01\u201d\u51cc\u4e91\u51e4\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u53f6\u5b97\u7559\u4e0d\u5728\u8fd9\u513f\uff0c\u90a3\u4e48\u7b97\u662f\u4f60\u4fdd\u9556\u4e86\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5c31\u7b97\u6211 \u5427\u3002\u201d\u4f46\u89c1\u7384\u745b\u9053\u4eba\u548c\u6bd5\u613f\u7a77\u4e00\u9f50\u8272\u53d8\uff0c\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u5c31\u8981\u8bf7\u59d0\u59d0\u6307\u6559\u4e86\u3002\u6211\u4e5f\u6b63 \u60f3\u89c1\u8bc6\u5f20\u5927\u4fa0\u6240\u4f20\u7684\u5251\u6cd5\u5462\uff01\u201d\u4e8e\u627f\u73e0\u8fd9\u624d\u77e5\u9053\u51cc\u4e91\u51e4\u95ee\u5979\u7684\u7528\u610f\uff0c\u539f\u6765\u51cc\u4e91\u51e4\u8fd8\u662f\u8981\u56fa\u6267 \u7740\u7eff\u6797\u9053\u4e2d\u8ba8\u9556\u8fd8\u9556\u7684\u89c4\u77e9\uff0c\u8981\u548c\u5979\u6bd4\u8bd5\u4e00\u573a\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u53ea\u597d\u544a\u4e86\u4e2a\u7f6a\uff0c\u4eae\u51fa\u5251\u6765\uff0c\u4e24\u4eba\u62b1\u5251\u800c\u7acb\uff0c\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u59d0\u59d0\u8fdc\u6765\u662f\u5ba2\uff0c\u4e3b\u4e0d\u6f5c \u5ba2\uff0c\u8bf7\u5148\u5427\u3002\u201d\u4e8e\u627f\u73e0\u5b9d\u5251\u4e00\u6301\uff0c\u9053\uff1a\u201c\u732e\u62d9\u4e86\uff01\u201d\u4e8e\u627f\u73e0\u548c\u51cc\u4e91\u51e4\u60fa\u60fa\u76f8\u60dc\uff0c\u8fd9\u4e00\u5251\u53ea\u662f \u4e2a\u201c\u8d77\u52bf\u201d\u7684\u62db\u5f0f\uff0c\u54ea\u77e5\u51cc\u4e91\u51e4\u7684\u5251\u62db\u5374\u662f\u8001\u8fa3\u975e\u5e38\uff0c\u4f46\u89c1\u5979\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u65b9\u4f4d\u7acb\u53d8\uff0c\u60ca \u9e3f\u63a0\u71d5\u822c\u5730\u7ed5\u5230\u4e8e\u627f\u73e0\u80cc\u540e\uff0c\u5530\u7684\u4e00\u5251\uff0c\u5c31\u671d\u4e8e\u627f\u73e0\u540e\u5fc3\u63b7\u6765\uff0c\u4e8e\u627f\u73e0\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a \u201c\u539f\u6765\u5979\u771f\u4e2a\u8f83\u91cf\uff01\u201d\u6025\u7528\u201c\u7384\u673a\u5251\u6cd5\u201d\u4e2d\u7684\u201c\u5927\u96c1\u5357\u5f52\u201d\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u89e3\u4e86\u51cc\u4e91\u51e4\u7684\u5251 \u52bf\uff0c\u63a5\u7740\u5bd2\u5149\u4e00\u95ea\uff0c\u4e00\u62db\u201c\u7389\u5973\u6295\u68ad\u201d\uff0c\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u523a\u51cc\u4e91\u51e4\u80a9\u540e\u7684\u201c\u98ce\u5e9c\u7a74\u201d\uff0c\u51cc\u4e91\u51e4\u8d5e \u4e86\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u4e00\u5251\u63b7\u7a7a\uff0c\u5251\u62db\u500f\u53d8\uff0c\u8eab\u968f\u5251\u8f6c\uff0c\u4fe8\u5982\u201c\u9e70\u96bc\u7a7f\u6797\u201d\uff0c\u731b\u5730\u4e00\u4e2a\u201c\u82cf\u79e6\u80cc \u5251\u201d\uff0c\u811a\u6b65\u8fd8\u672a\u65cb\u8f6c\u8fc7\u6765\uff0c\u5251\u950b\u5df2\u5148\u523a\u5230\u3002\u4e8e\u627f\u73e0\u4e00\u89c1\u6709\u673a\u53ef\u4e58\uff0c\u7acb\u523b\u4f7f\u4e86\u4e00\u62db\u201c\u4e3e\u706b\u64a9 \u5929\u201d\uff0c\u5b9d\u5251\u6a2a\u5c01\u4e0a\u53bb\uff0c\u5ffd\u5730\u60f3\u9053\uff1a\u201c\u6211\u7684\u5251\u4e43\u662f\u5b9d\u5251\uff0c\u524a\u65ad\u4e86\u5979\u7684\u5175\u5203\u53ef\u4e0d\u597d\u770b\u3002\u201d\u5fc3\u5ff5\u65b9 \u52a8\uff0c\u4f46\u89c9\u52b2\u98ce\u6251\u9762\uff0c\u5bd2\u6c14\u6c81\u808c\uff0c\u4e8e\u627f\u73e0\u6025\u5fd9\u95ea\u907f\uff0c\u53ea\u89c9\u51cc\u4e91\u98ce\u4e00\u5251\u4ece\u5979\u9b13\u8fb9\u524a\u8fc7\uff0c\u4e8e\u627f\u73e0\u811a \u5c16\u70b9\u5730\uff0c\u63a0\u51fa\u4e09\u4e24\u4e08\u5916\uff0c\u51cc\u4e91\u51e4\u5982\u5f71\u968f\u5f62\uff0c\u8ddf\u8e2a\u76f4\u4e0a\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u59d0\u59d0\u4e0d\u7528\u5ba2\u6c14\u3002\u201d\u53e3\u4e2d \u8bf4\u8bdd\uff0c\u624b\u5e95\u5374\u662f\u4e1d\u6beb\u4e0d\u6162\uff0c\u4e00\u8fde\u51e0\u62db\u201c\u767d\u733f\u8fdb\u679c\u201d\uff0c\u201c\u4ed9\u4eba\u6307\u8def\u201d\uff0c\u201c\u5927\u9e4f\u5c55\u7fc5\u201d\uff0c\u66b4\u98ce\u9aa4 \u96e8\u822c\u5730\u88ad\u6765\uff01 \u3000\u3000\u4e8e\u627f\u73e0\u903c\u5f97\u6253\u70b9\u7cbe\u795e\uff0c\u594b\u529b\u62c6\u62db\uff0c\u597d\u4e0d\u5bb9\u6613\u5230\u4e8c\u5341\u62db\u4e4b\u5916\uff0c\u624d\u89e3\u4e86\u51cc\u4e91\u51e4\u7684\u5148\u624b\u3002\u4f46\u89c9 \u51cc\u4e91\u51e4\u7684\u5251\u6cd5\u5947\u8be1\u4e4b\u6781\uff0c\u865a\u865a\u5b9e\u5b9e\uff0c\u96be\u4ee5\u6349\u6478\uff0c\u81ea\u5df1\u624b\u4e2d\u7a7a\u6709\u4e00\u67c4\u5b9d\u5251\uff0c\u4ea6\u53ea\u80fd\u582a\u582a\u6253\u4e2a\u5e73 \u624b\u3002\u5176\u5b9e\u8bba\u8d77\u5251\u6cd5\uff0c\u4e24\u4eba\u4e43\u662f\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u4e0d\u8fc7\u51cc\u4e91\u51e4\u80dc\u5728\u7ecf\u9a8c\uff0c\u6240\u4ee5\u7528\u7684\u867d\u7136\u53ea\u662f\u4e00\u67c4\u666e \u901a\u7684\u9752\u94a2\u5251\uff0c\u5374\u53cd\u800c\u5360\u4e86\u516d\u6210\u653b\u52bf\u3002 \u3000\u3000\u53cc\u65b9\u53c8\u62c6\u4e86\u4e09\u56db\u5341\u62db\uff0c\u51cc\u4e91\u51e4\u5251\u6cd5\u5ffd\u7136\u4e00\u53d8\uff0c\u4f46\u89c1\u5979\u67d4\u5982\u67f3\u7d6e\uff0c\u5feb\u82e5\u60ca\u9e3f\uff0c\u62db\u62db\u90fd\u85cf\u7740 \u65e0\u7a77\u53d8\u5316\uff01 \u3000\u3000\u4e8e\u627f\u73e0\u6697\u6697\u7eb3\u7f55\uff0c\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u4ecd\u770b\u4e0d\u51fa\u5979\u662f\u4f55\u5bb6\u4f55\u6d3e\uff0c\u5251\u6cd5\u5947\u8be1\u5982\u65af\uff0c\u8981\u4e0d\u662f\u4e8e\u627f \u73e0\u8fd9\u4e24\u5e74\u6765\uff0c\u6b66\u529f\u7ecf\u9a8c\u90fd\u5927\u6709\u957f\u8fdb\uff0c\u5f53\u7136\u4e0d\u6613\u62b5\u654c\u3002\u5e78\u800c\u4e8e\u627f\u73e0\u66fe\u8ddf\u5f20\u4e39\u67ab\u4e60\u8fc7\u201c\u7384\u529f\u8981 \u8bc0\u201d\uff0c\u867d\u7136\u65f6\u65e5\u5c1a\u6d45\uff0c\u529f\u529b\u672a\u6df1\uff0c\u4f46\u90a3\u201c\u7384\u529f\u8981\u51b3\u201d\uff0c\u4e0d\u4f46\u662f\u4fee\u4e60\u6b63\u5b97\u5185\u529f\u7684\u4eba\u95e8\u9014\u5f84\uff0c\u800c \u4e14\u662f\u5404\u79cd\u4e0a\u4e58\u6b66\u672f\u7684\u603b\u7eb2\uff0c\u6597\u4e86\u8bb8\u591a\uff0c\u4e8e\u627f\u73e0\u5bf9\u51cc\u4e91\u51e4\u7684\u5251\u6cd5\uff0c\u6e10\u6e10\u6478\u5230\u4e86\u4e00\u70b9\u95e8\u8def\uff0c\u4f46\u89c9 \u5979\u867d\u7136\u5947\u8be1\u767e\u51fa\uff0c\u4ecd\u6709\u8ff9\u8c61\u53ef\u5bfb\uff0c\u4f3c\u4e4e\u662f\u4ee5\u6b66\u5f53\u3001\u5c11\u6797\u3001\u5d69\u9633\u4e09\u6d3e\u5251\u6cd5\u4e3a\u57fa\u7840\uff0c\u800c\u52a0\u4ee5\u65b9\u5411 \u7684\u53d8\u5316\uff0c\u7f13\u75be\u7684\u4e0d\u540c\u3002\u5982\u6b64\u4e00\u6765\uff0c\u4e8e\u627f\u73e0\u5e94\u4ed8\u867d\u7136\u4e0d\u81f4\u4f3c\u5148\u524d\u5403\u529b\uff0c\u4f46\u4ea6\u4e0d\u8fc7\u582a\u582a\u6253\u4e2a\u5e73 \u624b\u3002 \u3000\u3000\u7384\u745b\u9053\u4eba\u548c\u6bd5\u613f\u7a77\u7b49\u4eba\u4e00\u5fc3\u76fc\u671b\u4e8e\u627f\u73e0\u5f97\u80dc\uff0c\u8fd9\u65f6\u90fd\u662f\u5341\u5206\u7126\u6025\uff0c\u5fc3\u4e2d\u4ff1\u5728\u60f3\u9053\uff1a\u201c\u8fd9 \u6837\u6253\u6cd5\uff0c\u4e0d\u77e5\u4f55\u5bf9\u65b9\u4e86\uff1f\u201d\u9661\u7136\u95f4\uff0c\u4f46\u89c1\u51cc\u4e91\u51e4\u4e00\u5251\u6a2a\u6311\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u5251\u5149\u4eba\u5f71\uff0c\u75be\u8f6c\u5982 \u98ce\uff0c\u773c\u82b1\u64a9\u4e71\uff0c\u7a81\u89c1\u4e8e\u627f\u73e0\u8eab\u5f62\u98de\u8d77\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u628a\u51cc\u4e91\u51e4\u7684\u9752\u94a2\u5251\u524a\u4e3a\u4e24\u6bb5\uff0c\u6bd5\u613f\u7a77 \u5927\u559c\uff0c\u8fd8\u672a\u53eb\u51fa\u58f0\u6765\uff0c\u4f46\u89c1\u9752\u5149\u4e00\u95ea\uff0c\u592d\u77eb\u5982\u9f99\uff0c\u659c\u98de\u76f4\u4e0a\uff0c\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u63d2\u5165\u5927\u7cb1\uff0c\u4e8e \u627f\u73e0\u7684\u5b9d\u5251\u4e5f\u88ab\u51cc\u4e91\u98ce\u9707\u5f97\u8131\u624b\u98de\u51fa\u3002 \u3000\u3000\u539f\u6765\u4e8e\u627f\u73e0\u4e5f\u662f\u7126\u6025\u975e\u5e38\uff0c\u6240\u4ee5\u7a81\u7528\u9669\u62db\uff0c\u8ba9\u51cc\u4e91\u51e4\u7684\u957f\u5251\u6b3a\u5230\u8eab\u524d\uff0c\u4ed7\u7740\u9752\u51a5\u5b9d\u5251\u4e4b \u5229\uff0c\u4e00\u4e3e\u5c06\u5b83\u524a\u65ad\uff1b\u53ef\u662f\u5979\u5728\u5185\u5708\u53d1\u5251\uff0c\u52b2\u529b\u5c31\u8fdc\u4e0d\u53ca\u5bf9\u65b9\uff1b\u56e0\u4e4b\u867d\u7136\u65ad\u4e86\u5bf9\u65b9\u7684\u5251\uff0c\u53ef\u662f \u81ea\u5df1\u7684\u5b9d\u5251\uff0c\u4e5f\u88ab\u5bf9\u65b9\u9707\u98de\u3002 \u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u53ea\u80fd\u7b97\u662f\u6253\u4e2a\u5e73\u624b\u3002\u51cc\u4e91\u51e4\u5fae\u7b11\u9053\uff1a\u201c\u59d0\u59d0\u7684\u5251\u6cd5\uff0c\u6211\u9886\u6559\u8fc7\u4e86\uff0c\u679c\u662f\u4e0d \u51e1\uff0c\u6211\u5f97\u9647\u671b\u8700\uff0c\u8fd8\u60f3\u518d\u9886\u6559\u59d0\u59d0\u7684\u6697\u5668\u3002\u201d \u3000\u3000\u68cb\u9022\u5bf9\u624b\uff0c\u4e8e\u627f\u73e0\u4e5f\u7ed9\u5979\u64a9\u8d77\u4e86\u597d\u80dc\u4e4b\u5fc3\uff0c\u4f46\u89c9\u81ea\u5df1\u4ed7\u7740\u5b9d\u5251\u4e4b\u529b\uff0c\u7565\u5360\u4e0a\u98ce\uff0c\u6b8a\u4e0d\u5149 \u91c7\uff0c\u5982\u4eca\u5979\u8981\u8f83\u91cf\u6697\u5668\uff0c\u6b63\u5408\u5fc3\u610f\u3002\u4fbf\u9053\uff1a\u201c\u59d0\u59d0\u80af\u6307\u6559\uff0c\u90a3\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u4fbf\u8bf7\u59d0\u59d0\u5212\u51fa\u9053 \u6765\u3002\u201d \u3000\u3000\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u54b1\u4eec\u5148\u6765\u4e2a\u6587\u6bd4\uff0c\u7136\u540e\u518d\u6765\u6b66\u6bd4\u3002\u201d\u8f83\u91cf\u6697\u5668\uff0c\u4e5f\u6709\u6587\u6bd4\u6b66\u6bd4\uff0c\u4e8e\u627f\u73e0\u53ef \u8fd8\u6ca1\u6709\u542c\u8fc7\u3002\u51cc\u4e91\u51e4\u7eed\u9053\uff1a\u201c\u59d0\u59d0\u8fdc\u6765\u662f\u5ba2\uff0c\u6211\u8ba9\u4f60\u5148\u6253\u4e09\u679a\u6697\u5668\uff0c\u82e5\u7136\u6211\u4fa5\u5e78\u907f\u8fc7\uff0c\u90a3\u4e48 \u5c31\u8bf7\u59d0\u59d0\u4e5f\u63a5\u6211\u4e09\u679a\u3002\u8fd9\u662f\u6587\u6bd4\u3002\u5404\u6253\u4e09\u679a\uff0c\u82e5\u7136\u4e24\u65e0\u4f24\u635f\uff0c\u90a3\u4e48\u54b1\u4eec\u518d\u6765\u6b66\u6bd4\uff0c\u5404\u7528\u6697\u5668 \u653b\u654c\uff0c\u76f4\u81f3\u89c1\u4e86\u5f3a\u5f31\u65b9\u4f11\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u8fd9\u6837\uff0c\u6211\u4e0d\u662f\u5360\u4e86\u59d0\u59d0\u7684\u4fbf\u5b9c\u5417\uff1f\u201d\u7384\u745b\u9053\uff1a\u201c\u606d\u656c\u4e0d\u5982\u4ece\u547d\uff0c\u4e8e\u59d1 \u5a18\uff0c\u4f60\u4e0d\u5fc5\u63a8\u8f9e\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u6599\u51cc\u4e91\u51e4\u4e5f\u4e0d\u80af\u8ba9\u5979\u5148\u63a5\u6697\u5668\uff0c\u53ea\u5f97\u53d6\u4e86\u4e09\u6735\u91d1\u82b1\u5728\u624b\uff0c\u65bd\u4e86 \u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u8bf7\u6055\u5c0f\u59b9\u50ed\u8d8a\u4e86\u3002\u201d\u53ea\u542c\u5f97\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u4e8e\u627f\u73e0\u53cc\u6307\u4e00\u5f39\uff0c\u4e00\u6735\u91d1 \u82b1\uff0c\u7535\u5c04\u800c\u51fa\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u51cc\u4e91\u51e4\u4e00\u4e2a\u8f6c\u8eab\uff0c\u90a3\u6735\u91d1\u82b1\u8d34\u7740\u9b13\u4e91\u98de\u8fc7\uff0c\u5c31\u5728\u8fd9\u4e00\u8f6c\u8eab \u4e4b\u95f4\uff0c\u51cc\u4e91\u51e4\u5df2\u628a\u5934\u4e0a\u7684\u7ea2\u5dfe\u89e3\u4e0b\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7b2c\u4e8c\u6735\u91d1\u82b1\u76f8\u7ee7\u98de\u51fa\uff0c\u4f46\u89c1\u51cc\u4e91\u51e4\u7ea2\u5dfe\u4e00\u626c\uff0c\u91d1\u5149\u4e00\u95ea\u5373\u706d\uff0c\u7adf\u4f3c\u6ce5\u725b\u5165\u6d77\uff0c\u65e0 \u58f0\u65e0\u8ff9\u3002\u4e8e\u627f\u73e0\u5403\u4e86\u4e00\u60ca\uff0c\u7b2c\u4e09\u6735\u91d1\u82b1\u53c8\u98de\u4e86\u51fa\u53bb\uff0c\u8fd9\u4e00\u6735\u91d1\u82b1\u6253\u5f97\u52b2\u9053\u5341\u8db3\uff0c\u76f4\u53d6\u51cc\u4e91\u51e4 \u5de6\u8155\u7684\u201c\u66f2\u6c60\u7a74\u201d\u3002\u51cc\u4e91\u51e4\u8d5e\u9053\uff1a\u201c\u6563\u82b1\u5973\u4fa0\uff0c\u540d\u4e0d\u865a\u4f20\uff01\u201d\u7a81\u7136\u4e00\u4e2a\u8f6c\u8eab\uff0c\u7ea2\u5dfe\u75be\u5c55\uff0c\u8863 \u8882\u98ce\u98d8\uff0c\u59ff\u6001\u7f8e\u5999\u4e4b\u6781\uff0c\u4f46\u542c\u5f97\u94ee\u94ee\u4e24\u58f0\uff0c\u51cc\u4e91\u51e4\u5c06\u9002\u624d\u5377\u53bb\u7684\u91d1\u82b1\uff0c\u501f\u7ea2\u5dfe\u4e00\u6325\u4e4b\u529b\u6fc0\u5c04 \u51fa\u6765\uff0c\u628a\u4e8e\u627f\u73e0\u7684\u7b2c\u4e09\u6735\u91d1\u82b1\u53c8\u6253\u843d\u4e86\uff01 \u3000\u3000\u4e8e\u627f\u73e0\u7684\u91d1\u82b1\u6697\u5668\uff0c\u6bcf\u7247\u82b1\u74e3\u90fd\u662f\u950b\u5229\u5f02\u5e38\u7684\u5200\u7247\uff0c\u51cc\u4e91\u51e4\u7adf\u7136\u80fd\u7528\u4e00\u6761\u7ea2\u5dfe\u5c06\u5b83\u5377 \u53bb\uff0c\u8fd9\u79cd\u4e0a\u4e58\u7684\u5185\u5bb6\u5378\u529b\u529f\u592b\u5df2\u662f\u975e\u540c\u5c0f\u53ef\uff1b\u5979\u8fd8\u80fd\u653b\u80fd\u653e\uff0c\u4ee5\u91d1\u82b1\u8fd8\u51fb\u91d1\u82b1\uff0c\u8fd9\u4e00\u624b\u7edd \u6280\uff0c\u4ee4\u7384\u745b\u9053\u4eba\u8fd9\u4e00\u64de\u6b66\u6797\u9ad8\u624b\uff0c\u4e5f\u770b\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u4e8e\u627f\u73e0\u662f\u6697\u5668\u7684\u884c\u5bb6\uff0c\u6df1\u6089\u5176\u4e2d\u7684\u8270 \u96be\uff0c\u66f4\u662f\u6697\u6697\u4f69\u670d\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u597d\u6574\u4ee5\u6687\u5730\u5c06\u7ea2\u5dfe\u624e\u597d\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u627f\u8ba9\u4e86\u3002\u201d\u5ffd\u5730\u7693\u8155\u4e00\u62ac\uff0c\u4e00\u679a\u6697\u5668\u6084\u65e0 \u58f0\u606f\u98de\u4e86\u51fa\u6765\uff0c\u4e8e\u627f\u73e0\u6709\u610f\u5356\u5f04\u529f\u592b\uff0c\u53ea\u5f53\u6ca1\u6709\u77a7\u89c1\uff0c\u76f4\u5230\u90a3\u6697\u5668\u98de\u5230\u8eab\u524d\uff0c\u4e00\u6298\u8170\u8eaf\uff0c\u4fbf \u95ea\u4e86\u5f00\u53bb\u3002\u4e8e\u627f\u73e0\u7ec3\u8fc7\u7a7f\u82b1\u7ed5\u6811\u7684\u8eab\u6cd5\uff0c\u8eb2\u95ea\u6697\u5668\uff0c\u4ece\u5bb9\u4e4b\u6781\uff0c\u59ff\u6001\u7f8e\u5999\uff0c\u4e5f\u4e0d\u5728\u51cc\u4e91\u51e4\u4e4b \u4e0b\uff0c\u5c71\u5be8\u5973\u5175\u90fd\u8f70\u7136\u559d\u5f69\u3002\u5374\u4e0d\u6599\u51cc\u4e91\u51e4\u90a3\u679a\u6697\u5668\u53e4\u602a\u4e4b\u6781\uff0c\u5728\u559d\u5f69\u58f0\u4e2d\uff0c\u5ffd\u7136\u201c\u55e4\u201d\u7684\u4e00 \u58f0\u7adf\u5728\u7a7a\u4e2d\u8f6c\u6298\u56de\u745a\uff0c\u6389\u8f6c\u4e86\u5934\uff0c\u53c8\u5411\u4e8e\u627f\u73e0\u95ea\u907f\u7684\u5730\u65b9\u5c04\u6765\uff0c\u4e8e\u627f\u73e0\u8fd9\u624d\u77a7\u6e05\u695a\u4e43\u662f\u4e00\u679a \u5185\u4e2d\u85cf\u6709\u673a\u5173\u7684\u8774\u8776\u9556\u3002\u4e8e\u627f\u73e0\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u8eab\u5f62\u5c55\u5f00\uff0c\u4fe8\u5982\u71d5\u5b50\u63a0\u6ce2\uff0c\u8712\u9756\u70b9\u6c34\uff0c \u8774\u8776\u9556\u8fde\u6362\u4e86\u4e09\u6b21\u65b9\u5411\uff0c\u4ecd\u662f\u8ffd\u5979\u4e0d\u4e0a\uff0c\u7ec8\u4e8e\u843d\u5230\u5730\u4e0a\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u8d5e\u9053\uff1a\u201c\u8eb2\u907f\u6697\u5668\u7684\u8eab\u6cd5\uff0c\u8981\u7b97\u59d0\u59d0\u72ec\u6b65\u6b66\u6797\u4e86\u3002\u201d\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u7b2c\u4e8c\u679a\u8774\u8776 \u9556\u53c8\u7834\u7a7a\u6253\u51fa\uff0c\u4e8e\u627f\u73e0\u626d\u8eab\u95ea\u8fc7\uff0c\u5f85\u90a3\u8774\u8776\u9556\u7684\u52b2\u9053\u6d88\u4e86\u4e00\u534a\u4e4b\u65f6\uff0c\u731b\u7684\u56de\u5934\u7528\u624b\u4e00\u5f39\uff0c\u90a3 \u679a\u8774\u8776\u9556\u521a\u521a\u8ffd\u5230\u8eab\u540e\uff0c\u88ab\u5979\u4e00\u5f39\uff0c\u731d\u7136\u53cd\u5c04\uff0c\u6070\u6070\u4e0e\u51cc\u4e91\u51e4\u6240\u53d1\u7684\u7b2c\u4e09\u679a\u8774\u8776\u9556\u78b0\u4e2a\u6b63 \u7740\uff0c\u53cc\u53cc\u8dcc\u843d\u5730\u4e0a\u3002\u8fd9\u4e00\u4e0b\u7528\u7684\u5374\u662f\u4e4c\u8499\u592b\u7684\u201c\u4e00\u6307\u7985\u201d\u624b\u6cd5\uff0c\u4e8e\u627f\u73e0\u867d\u7136\u5b66\u5f97\u53ea\u4e09\u6210\u529f \u592b\uff0c\u4f46\u7528\u6765\u5bf9\u4ed8\u51cc\u4e91\u51e4\u7684\u6697\u5668\uff0c\u5df2\u662f\u7ef0\u6709\u4f59\u4e86\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u6587\u6bd4\u4e0d\u5206\u9ad8\u4e0b\uff0c\u54b1\u4eec\u53ef\u8981\u518d\u6765\u6b66\u6bd4\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u56de\u8be5\u8bf7\u59d0\u59d0 \u5148\u884c\u6307\u6559\u4e86\u3002\u201d\u51cc\u4e91\u51e4\u98de\u8eab\u4e00\u63a0\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u731b\u5730\u6296\u624b\u6253\u53bb\uff0c\u4e00\u4e0b\u5b50\u4fbf\u662f\u5341\u4e8c\u679a\u8774\u8776\u9556\u8fde\u7fe9 \u98de\u51fa\uff0c\u6709\u5982\u6d41\u661f\u4e71\u821e\uff0c\u60ca\u96f9\u9aa4\u843d\uff0c\u4e8e\u627f\u73e0\u65bd\u5c55\u4ece\u963f\u8428\u739b\u5144\u5f1f\u90a3\u91cc\u5b66\u6765\u7684\u624b\u6cd5\uff0c\u624b\u6307\u75be\u5f39\uff0c\u4f46 \u542c\u5f97\u201c\u94ee\u94ee\u201d\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u4e5f\u628a\u5341\u4e8c\u6735\u91d1\u82b1\u98de\u4e86\u51fa\u53bb\u3002\u51cc\u4e91\u51e4\u7684\u8774\u8776\u9556\u5185\u6709\u673a\u5173\uff0c\u53ef\u4ee5 \u5728\u7a7a\u4e2d\u4efb\u610f\u8f6c\u6298\u56de\u7fd4\uff1b\u4f46\u4e8e\u627f\u73e0\u7684\u91d1\u82b1\u4e92\u76f8\u78b0\u649e\uff0c\u5c45\u7136\u4e5f\u4ece\u4e0d\u540c\u7684\u65b9\u5411\u6fc0\u5c04\uff0c\u5c06\u51cc\u4e91\u51e4\u7684\u8774 \u8776\u9556\u649e\u5f97\u9635\u5f62\u5927\u4e71\uff0c\u51cc\u4e91\u51e4\u4e5f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u9661\u7136\u95f4\uff0c\u4f46\u89c1\u91d1\u5149\u4e00\u95ea\uff0c\u5df2\u5230\u9762\u524d\uff0c\u51cc\u4e91\u51e4\u6025 \u5fd9\u95ea\u907f\uff0c\u4f46\u542c\u5f97\u201c\u5636\u201d\u7684\u4e00\u58f0\uff1b\u534a\u6761\u7ea2\u5dfe\u5df2\u5728\u7a7a\u4e2d\u98d8\u821e\uff01 \u3000\u3000\u4f17\u4eba\u773c\u82b1\u64a9\u4e71\uff0c\u8fd9\u65f6\u521a\u521a\u5b9a\u4e0b\u795e\u6765\uff0c\u4f46\u89c1\u51cc\u4e91\u51e4\u970d\u5730\u8df3\u51fa\u5708\u5b50\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u6563\u82b1\u5999 \u6280\uff0c\u4e16\u4e0a\u65e0\u53cc\uff01\u5c0f\u59b9\u8fd9\u56de\u662f\u771f\u7684\u8f93\u5f97\u5fc3\u670d\u53e3\u670d\u4e86\uff01\u201d\u539f\u6765\u4e8e\u627f\u73e0\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6cd5\uff0c\u4e00 \u6735\u91d1\u82b1\u524a\u65ad\u4e86\u5979\u7684\u7ea2\u5dfe\u3002 \u3000\u3000\u6bd5\u613f\u7a77\u4e0e\u767d\u5b5f\u5ddd\u559c\u5f97\u8df3\u4e86\u8d77\u6765\uff1b\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u4f60\u4eec\u5fd9\u4ec0\u4e48\uff1f\u201d\u6307\u6325\u5973\u5175\uff0c\u5c06\u7bb1\u7b3c\u91cd\u65b0\u88c5 \u597d\uff0c\u7b11\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\uff0c\u6309\u7167\u54b1\u4eec\u7eff\u6797\u9053\u4e0a\u7684\u89c4\u77e9\uff0c\u8fd9\u652f\u9556\u73b0\u5728\u4ea4\u7ed9\u4f60\u4e86\u3002\u201d\u6bd5\u613f\u7a77\u4e0a\u524d\u5531\u4e86 \u4e2a\u558f\uff0c\u9053\uff1a\u201c\u59d1\u5976\u5976\uff0c\u591a\u8c22\u4f60\u5566\uff01\u201d\u767d\u5b5f\u5ddd\u4e5f\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\uff0c\u4f60\u672a\u5230\u519b\u4e2d\uff0c\u5c31\u5148\u7ed9\u54b1\u4eec\u7acb\u4e86 \u4e00\u4ef6\u5947\u529f\uff0c\u771f\u662f\u53ef\u559c\u53ef\u8d3a\u554a\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u773c\u73e0\u4e00\u6e9c\uff0c\u9053\uff1a\u201c\u53f6\u5927\u54e5\uff0c\u4f60\u8fc7\u6765\u3002\u201d\u53f6\u6210\u6797\u5e94\u58f0\u800c\u51fa\uff0c\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u652f\u9556\u6211 \u4ed8\u7ed9\u4f60\uff0c\u4f60\u4ea4\u7ed9\u4f60\u53d4\u53d4\u4e5f\u597d\uff0c\u4ea4\u7ed9\u6bd5\u64ce\u5929\u4e5f\u597d\uff0c\u6211\u7ba1\u4e0d\u7740\uff01\u201d\u6bd5\u613f\u7a77\u4e0e\u767d\u5b5f\u5ddd\u6ee1\u5fc3\u4ee5\u4e3a\u4e8e\u627f \u73e0\u662f\u66ff\u4ed6\u4eec\u593a\u9556\uff0c\u5374\u4e0d\u6599\u4e8e\u627f\u73e0\u4ed8\u6258\u7ed9\u53f6\u6210\u6797\uff0c\u8fd9\u4e0d\u4f46\u662f\u5f53\u7740\u4f17\u4eba\u626b\u4e86\u4ed6\u4eec\u7684\u9762\u5b50\uff0c\u800c\u4e14\u662f \u626b\u4e86\u6bd5\u64ce\u5929\u6bd5\u5927\u9f99\u5934\u7684\u9762\u5b50\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u8fd9\u652f\u9556\u53cd\u6b63\u5230\u4e86\u81ea\u5df1\u4eba\u7684\u624b\u4e2d\uff0c\u5fc3\u91cc\u5934\u867d\u7136\u4e0d \u5feb\uff0c\u5374\u4e5f\u4e0d\u6562\u591a\u8bf4\u8bdd\u3002 \u3000\u3000\u7eb7\u6270\u4e2d\u90a3\u7a7f\u7740\u674f\u9ec4\u886b\u5b50\u7684\u4e2b\u9b1f\u51fa\u6765\u7980\u9053\uff1a\u201c\u90a3\u8001\u5934\u513f\u9192\u8fc7\u6765\u5566\uff0c\u6376\u7740\u80f8\u76f4\u53f9\u6c14\uff01\u201d\u51cc\u4e91 \u51e4\u7b11\u9053\uff1a\u201c\u5931\u4e86\u4e03\u5341\u4e07\u4e24\u94f6\u5b50\uff0c\u602a\u4e0d\u5f97\u4ed6\u8981\u5fc3\u75bc\u4e86\u3002\u7ed9\u4ed6\u51e0\u4e24\u76d8\u7f20\uff0c\u9001\u4ed6\u4e0b\u5c71\u53bb\u5427\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u89c1\u97e9\u8001\u9556\u5934\u8dc4\u8dc4\u8e09\u7405\u5730\u5954\u4e86\u51fa\u6765\uff0c\u5636\u58f0\u53eb\u9053\uff1a\u201c\u602a\u6211\u6709\u773c\u65e0\u73e0\uff0c\u8d70\u4e86\u56db\u5341 \u5e74\u9556\uff0c\u5230\u5934\u6765\u8fd8\u7ffb\u4e86\u8fd9\u4e48\u4e00\u4e2a\u7b4b\u6597\u3002\u7384\u745b\u9053\u5144\uff0c\u4f60\u809d\u80c6\u7167\u4eba\uff0c\u97e9\u67d0\u5728\u5317\u4eac\u7684\u5bb6\u5c0f\uff0c\u6258\u4f60\u7167\u987e \u4e86\uff01\u201d\u7a81\u7136\u7eb5\u8eab\u4e00\u8df3\uff0c\u5411\u7740\u5be8\u4e2d\u7684\u5927\u67f1\u4e00\u5934\u649e\u53bb\uff01\u539f\u6765\u7167\u4fdd\u9556\u7684\u89c4\u77e9\uff0c\u5931\u4e86\u9556\u82e5\u8ba8\u4e0d\u56de\u6765\uff0c \u9556\u884c\u5c31\u975e\u8d1f\u8d23\u8d54\u507f\u4e0d\u53ef\u3002\u97e9\u8001\u9556\u5934\u867d\u7136\u4fdd\u4e86\u51e0\u5341\u5e74\u9556\uff0c\u8584\u6709\u79ef\u84c4\uff0c\u4f46\u54ea\u91cc\u8d54\u5f97\u8d77\u4e03\u5341\u4e07\u4e24\u9556 \u94f6\uff1f\u82e5\u8bf4\u4e00\u8d70\u4e86\u4e4b\uff0c\u4f46\u4e00\u6765\u7275\u7d2f\u5bb6\u5c0f\uff1b\u4e8c\u6765\u97e9\u8001\u9556\u5934\u4ee5\u51e0\u5341\u5e74\u7684\u4fe1\u7528\uff0c\u4ea6\u4e0d\u613f\u5982\u6b64\u505a\u6cd5\u3002\u97e9 \u8001\u9556\u5934\u60f3\u6765\u60f3\u53bb\uff0c\u65e0\u6cd5\u53ef\u65bd\uff0c\u4e00\u53e3\u6c14\u8f6c\u4e0d\u8fc7\u6765\uff0c\u56e0\u6b64\u81ea\u5bfb\u77ed\u89c1\u3002 \u3000\u3000\u97e9\u8001\u9556\u5934\u6b63\u5728\u7edd\u5883\uff0c\u672c\u6765\u8c01\u90fd\u53ef\u4ee5\u60f3\u8c61\u5f97\u5230\u3002\u4f46\u4f17\u4eba\u6b63\u5728\u6b22\u559c\u4e0a\u5934\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u60f3\u5230 \u4ed6\u3002\u8fd9\u4e00\u4e0b\u7aef\u7684\u662f\u5927\u51fa\u610f\u5916\uff0c\u7384\u745b\u9053\u4eba\u4e00\u58f0\u60ca\u547c\uff0c\u62a2\u4e0a\u53bb\u5df2\u6765\u4e0d\u53ca\uff0c\u53ea\u89c1\u97e9\u8001\u9556\u5934\u53bb\u52bf\u5982 \u7bad\uff0c\u770b\u770b\u5c31\u8981\u649e\u5230\u67f1\u4e0a\uff01 \u3000\u3000\u5ffd\u542c\u5f97\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u5be8\u4e2d\u7684\u5927\u67f1\u5ffd\u7136\u4ece\u4e2d\u65ad\u4e86\uff0c\u97e9\u8001\u9556\u5934\u4ece\u7f3a\u53e3\u5904\u98de\u8fc7\uff0c\u7ed9\u4e00\u4e2a\u4eba\u62e6 \u8170\u62b1\u4f4f\uff0c\u8fd9\u4e2a\u4eba\u6b63\u662f\u53f6\u6210\u6797\u3002\u539f\u6765\u662f\u4ed6\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u65bd\u5c55\u5927\u529b\u91d1\u521a\u624b\u7684\u529f\u592b\uff0c\u628a\u5927\u67f1\u6253 \u65ad\uff0c\u6551\u4e86\u97e9\u8001\u9556\u5934\u4e00\u547d\u3002 \u3000\u3000\u53f6\u6210\u6797\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u97e9\u8001\u9556\u5934\u653e\u4e0b\uff0c\u5bf9\u51cc\u4e91\u51e4\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u4e8b\u975e\u5f97\u5df2\uff0c\u635f\u4e86\u8d35\u5be8\u5927\u7cb1\uff0c \u8bf7\u6055\u7f6a\u4e86\u3002\u201d\u97e9\u8001\u9556\u5934\u53eb\u9053\uff1a\u201c\u4f60\u6551\u6211\u4f5c\u4ec0\u4e48\uff1f\u201d\u53f6\u6210\u6797\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u652f\u9556\u4ecd\u8bf7\u4f60\u5e26\u5230\u6e56 \u5317\u53bb\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u77f3\u7834\u5929\u60ca\uff0c\u4f17\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u9759\u5f97\u8fde\u4e00\u6839\u9488\u4e00\u8dcc\u5728\u5730\u4e0b\u90fd\u542c\u5f97\u89c1\u54cd\uff0c\u4f46\u60ca\u6115\u7a0d \u8fc7\uff0c\u970e\u65f6\u95f4\u53c8\u5608\u58f0\u56db\u8d77\u3002\u97e9\u8001\u9556\u5934\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\uff0c\u8fd9\u2026\u2026\u8fd9\u6211\u600e\u4e48\u6562\u8bf4\uff1f\u201d\u767d\u5b5f\u5ddd\u56b7\u9053\uff1a \u201c\u4f60\uff0c\u4f60\u51ed\u4ec0\u4e48\u64c5\u81ea\u4e3b\u5f20\uff1f\u5c06\u94f6\u5b50\u9001\u5230\u5b98\u519b\u624b\u4e2d\uff0c\u8fd9\u5c82\u4e0d\u662f\u52a9\u654c\u4eba\u6765\u6253\u81ea\u5df1\uff1f\u201d\u6bd5\u613f\u7a77\u4e0d\u4f4f \u4ef7\u5730\u563b\u563b\u51b7\u7b11\uff0c\u8138\u4e0a\u5374\u65e0\u4e1d\u6beb\u7684\u6ed1\u7a3d\u4f38\u60c5\uff0c\u7b11\u5f97\u5927\u5931\u5e38\u6001\uff0c\u731b\u5730\u62cd\u6848\u9a82\u9053\uff1a\u201c\u53f6\u54e5\u513f\uff0c\u4f60\u505a \u5f97\u4e5f\u592a\u8fc7\u4efd\u5566\uff0c\u5c06\u5927\u4f19\u513f\u7684\u6027\u547d\u6765\u9001\u4eba\u60c5\u5417\uff1f\u201d \u3000\u3000\u53f6\u6210\u6797\u795e\u8272\u81ea\u82e5\uff0c\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u4f17\u4eba\u56b7\u56b7\u9a82\u9a82\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u81ea\u7136\u9759\u4e86\u4e0b\u6765\uff0c\u65e0\u6570\u9053\u76ee\u5149\u90fd \u76ef\u7740\u4ed6\uff0c\u53ea\u89c1\u4ed6\u7f13\u7f13\u8d70\u51fa\u573a\u5fc3\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u4e03\u5341\u4e07\u4e24\u94f6\u5b50\uff0c\u54b1\u4eec\u5c06\u5b83\u622a\u4e86\u3002\u6e56\u5317\u7684\u5341\u4e07 \u5b98\u519b\uff0c\u7f3a\u7cae\u7f3a\u9977\uff0c\u52bf\u5c06\u4e0d\u6545\u800c\u6e83\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u767d\u5b5f\u5ddd\u9053\uff1a\u201c\u5b98\u519b\u4e0d\u6218\u800c\u6e83\uff0c\u5bf9\u6211\u4eec\u5c82\u4e0d\u662f \u597d\u5f97\u5f88\u4e48\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u4e0d\u9519\u3002\u53ef\u662f\u5341\u4e07\u5f20\u809a\u5b50\uff0c\u4e5f\u5f97\u5403\u996d\u7684\u662f\u4e0d\u662f\uff1f\u201d\u6bd5\u613f\u7a77\u51b7\u7b11\u9053\uff1a \u201c\u54c8\uff0c\u53f6\u54e5\u513f\uff0c\u4f60\u5fc3\u80a0\u771f\u597d\uff0c\u53ef\u601c\u8d77\u5b98\u519b\u6765\u5566\uff01\u201d\u53f6\u6210\u6797\u5927\u8896\u4e00\u6325\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u662f\u53ef\u601c \u6e56\u5317\u7684\u8001\u767e\u59d3\uff01\u5341\u4e07\u6e83\u519b\uff0c\u5728\u8fd9\u5929\u8352\u5730\u51bb\u7684\u65e5\u5b50\u91cc\uff0c\u4ed6\u4eec\u4e0d\u62a2\u8001\u767e\u59d3\uff0c\u5403\u4ec0\u4e48\uff1f\u7a7f\u4ec0\u4e48\uff1f\u6709 \u94b1\u7684\u4eba\u5bb6\u91cd\u95e8\u6df1\u6237\u8fd8\u53ef\u4ee5\u9632\u8303\u6e83\u519b\uff0c\u7a77\u4eba\u5bb6\u53ef\u5c31\u8981\u5927\u5927\u5730\u5012\u5a9a\uff0c\u4f60\u4eec\u4e5f\u4e0d\u60f3\u60f3\uff0c\u8fd9\u4e00\u573a\u5927\u5175 \u707e\u8981\u5bb3\u4e86\u591a\u5c11\u767e\u59d3\uff01\u201d \u3000\u3000\u7384\u745b\u9053\u4eba\u548c\u6bd5\u613f\u7a77\u9762\u8272\u60e8\u767d\uff0c\u597d\u50cf\u6cc4\u4e86\u6c14\u7684\u76ae\u7403\uff0c\u4f5c\u58f0\u4e0d\u5f97\u3002\u767d\u5b5f\u5ddd\u76f4\u77aa\u773c\u775b\uff0c\u8fd8\u60f3\u53eb \u56b7\u3002\u53f6\u6210\u6797\u8138\u5b54\u4e00\u677f\uff0c\u65a9\u9489\u622a\u94c1\u5730\u9053\uff1a\u201c\u8fd9\u652f\u9556\u662f\u4e8e\u59d1\u5a18\u8ba8\u56de\u6765\u7684\uff0c\u73b0\u5728\u4ea4\u6258\u7ed9\u6211\uff0c\u6211\u6709\u5168 \u6743\u5904\u7406\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u8c01\u4e5f\u4e0d\u8bb8\u591a\u8bdd\uff0c\u97e9 \u8001\u9556\u5934\uff0c\u8fd9\u652f\u9556\u4f60\u5e26\u5230\u6e56\u5317\u53bb\uff0c\u5c3d\u7ba1\u4ea4\u7ed9\u5b98\u519b\uff0c\u5929\u5927\u7684\u62c5\u5b50\uff0c\u7531\u6211\u6765\u6311\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u4e00\u9897\u5fc3\u535c\u535c\u5730\u8df3\u4e2a\u4e0d\u4f11\uff0c\u60f3\u4e0d\u5230\u53f6\u6210\u6797\u8fd9\u6837\u4e00\u4e2a\u8d28\u6734\u5be1\u8a00\u7684\u4eba\u8fd9\u65f6\u5374\u6d3b\u4f3c\u4e00\u4e2a\u6307 \u6325\u82e5\u5b9a\u7684\u5927\u5c06\uff0c\u81ea\u6709\u4e00\u80a1\u51db\u7136\u4e0d\u53ef\u4fb5\u72af\u7684\u795e\u60c5\u3002\u53ea\u89c1\u4ed6\u53cc\u76ee\u4e00\u626b\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u662f\u4e3a\u6c11 \u8bf7\u547d\u7684\u4ec1\u4e49\u4e4b\u5e08\uff0c\u600e\u80fd\u8ba9\u8001\u767e\u59d3\u5148\u53d7\u707e\u6b83\uff1f\u4ec1\u4e49\u4e4b\u5e08\uff0c\u65e0\u654c\u5929\u4e0b\uff0c\u53c8\u4f55\u60e7\u4ed6\u5341\u4e07\u5b98\u519b\uff0c\u767e\u4e07 \u5b98\u519b\uff1f\u54b1\u4eec\u505a\u7684\u597d\u4e8b\uff0c\u603b\u4f1a\u6709\u4eba\u77e5\u9053\u3002\u8fd9\u5341\u4e07\u5b98\u519b\uff0c\u5403\u9971\u4e86\u809a\u5b50\uff0c\u4e5f\u672a\u5fc5\u5c31\u80af\u4e3a\u671d\u5ef7\u5356\u547d\uff1f \u4f60\u4eec\u6015\u5341\u4e07\u5b98\u519b\uff0c\u6211\u6765\u505a\u524d\u950b\uff0c\u6211\u6709\u6cd5\u5b50\u8981\u4ed6\u4eec\u6295\u964d\uff0c\u4e0d\u6295\u964d\u5c31\u628a\u4ed6\u4eec\u51fb\u8d25\uff01\u6709\u4ec0\u4e48\u53ef\u60e7 \u7684\uff1f\u6253\u4ed7\u8981\u4f5c\u957f\u8fdc\u6253\u7b97\uff0c\u8fd9\u4ec1\u4e49\u4e24\u5b57\uff0c\u5c31\u503c\u5f97\u5341\u4e07\u96c4\u5e08\uff01\u201d \u3000\u3000\u51cc\u4e91\u51e4\u7eb5\u58f0\u957f\u7b11\uff0c\u7fd8\u8d77\u5927\u62c7\u6307\u9053\uff1a\u201c\u58ee\u54c9\uff01\u8fd9\u624d\u662f\u5927\u82f1\u96c4\u5927\u8c6a\u6770\u7684\u6c14\u9b44\uff01\u5973\u5175\u4eec\u5c06\u9aa1\u8f66 \u62a4\u9001\u4e0b\u5c71\uff0c\u4ea4\u56de\u9556\u884c\uff01\u6765\uff0c\u6765\uff0c\u6765\uff01\u53f6\u5927\u54e5\uff0c\u6211\u656c\u4f60\u4e09\u676f\uff01\u201d\u767b\u65f6\u63d0\u58f6\u628a\u76cf\uff0c\u659f\u4e86\u6ee1\u6ee1\u7684\u4e09 \u5927\u676f\u9152\uff0c\u5148\u81ea\u4ef0\u7740\u8116\u5b50\u559d\u4e86\u3002\u53f6\u6210\u6797\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u6211\u8d54\u4f60\u7684\u5927\u6881\uff0c\u8fd9\u4e09\u676f\u9152\u6211\u4e5f\u53ea\u597d \u559d\u5566\uff01\u201d\u5927\u5be8\u4e2d\u4e00\u7247\u9759\u5bc2\uff0c\u4f46\u542c\u5f97\u53f6\u6210\u6797\u548c\u51cc\u4e91\u51e4\u8c6a\u8fc8\u7684\u7b11\u58f0\uff01\u6b63\u662f\uff1a \u3000\u3000\u77f3\u7834\u5929\u60ca\u8fd8\u5de8\u6b3e\uff0c\u4ec1\u5fc3\u4fa0\u9aa8\u4e24\u76f8\u77e5\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff0c\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u516b\u56de\u3000\u96ea\u591c\u6b65\u6885\u6797\u3000\u76f8\u601c\u76f8\u60dc\u3000\u51b0\u5fc3\u7275\u585e\u5916\u3000\u540c\u68a6\u540c\u60b2 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u516b\u56de\u3000\u96ea\u591c\u6b65\u6885\u6797\u3000\u76f8\u601c\u76f8\u60dc\u3000\u51b0\u5fc3\u7275\u585e\u5916\u3000\u540c\u68a6\u540c\u60b2 \u3000\u3000\u4e8e\u627f\u73e0\u9ed8\u9ed8\u7ad9\u5728\u4e00\u65c1\uff0c\u4f46\u89c1\u51cc\u4e91\u51e4\u7ea2\u5dfe\u98d8\u52a8\uff0c\u795e\u91c7\u98de\u626c\uff0c\u7aef\u7684\u4f3c\u51cc\u4e91\u5f69\u51e4\uff0c\u50b2\u89c6\u7a7a\u6f20\u3002 \u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5ffd\u7136\u8d77\u4e86\u4e00\u79cd\u5f02\u6837\u7684\u611f\u89c9\uff0c\u4f46\u89c9\u4ed6\u4eec\u4e24\u4eba\u5e76\u80a9\u800c\u7acb\uff0c\u5c31\u4f3c\u53e4\u753b\u4e2d\u7684\u674e\u9756\u4e0e \u7ea2\u62c2\u4e00\u822c\uff0c\u82f1\u96c4\u513f\u5973\uff0c\u8c6a\u4fa0\u98ce\u534e\uff0c\u914d\u5408\u5f97\u81ea\u7136\u4e4b\u6781\uff0c\u5982\u6b64\u4e00\u60f3\uff0c\u4e0d\u89c9\u75f4\u4e86\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\uff0c\u4f60\u4e5f\u6765\u996e\u4e09\u676f\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5c0f\u59b9\u91cf\u6d45\uff0c\u4e0d\u6562\u5949 \u966a\u3002\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u9152\u9022\u77e5\u5df1\u4f55\u8f9e\u9189\uff01\u4e8e\u59d1\u5a18\uff0c\u8fd9\u4e00\u676f\u4f60\u662f\u975e\u996e\u4e0d\u53ef\uff01\u201d\u4e8e\u627f\u73e0\u5480\u56bc\u201c\u9152\u9022 \u77e5\u5df1\u201d\u8fd9\u51e0\u4e2a\u5b57\uff0c\u5fc3\u4e2d\u6005\u7136\uff0c\u63a5\u8fc7\u51cc\u4e91\u51e4\u624b\u4e2d\u7684\u9152\u676f\uff0c\u4e00\u996e\u800c\u5c3d\u3002\u51cc\u4e91\u51e4\u7b11\u9053\uff1a\u201c\u8fd9\u624d\u591f\u75db \u5feb\u3002\u201d\u6b63\u60f3\u518d\u529d\uff0c\u53f6\u6210\u6797\u5377\u7740\u820c\u5934\u8bf4\u9053\uff1a\u201c\u6211\u624d\u662f\u771f\u4e2a\u4e0d\u884c\uff0c\u9189\u4e86\uff0c\u9189\u4e86\uff01\u201d\u53f6\u6210\u6797\u786e\u662f\u4e0d \u5584\u996e\u9152\uff0c\u5728\u51cc\u4e91\u51e4\u8c6a\u6c14\u51cc\u903c\u4e4b\u4e0b\uff0c\u5e72\u4e86\u4e09\u676f\uff0c\u4f46\u89c9\u811a\u6b65\u865a\u6d6e\uff0c\u6447\u6447\u6b32\u5760\u3002\u51cc\u4e91\u51e4\u89c1\u4ed6\u795e\u6001\u975e \u5047\uff0c\u7eb5\u58f0\u957f\u7b11\uff0c\u5c06\u7389\u676f\u63b7\u5730\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4eca\u665a\u518d\u996e\uff0c\u674f\u513f\uff0c\u4f60\u6536\u62fe\u53a2\u623f\uff0c\u8bf7\u53f6\u5927\u54e5\u5b89\u6b47\u53bb\u3002 \u4e8e\u59d1\u5a18\uff0c\u6211\u966a\u4f60\u5230\u5c71\u91cc\u8d70\u8d70\uff01\u201d \u3000\u3000\u767d\u5b5f\u5ddd\u7b49\u89c1\u51cc\u4e91\u51e4\u5e76\u4e0d\u7406\u776c\u4ed6\u4eec\uff0c\u751a\u662f\u5c34\u5c2c\uff0c\u5f53\u5373\u62f1\u624b\u544a\u8f9e\uff0c\u51cc\u4e91\u98ce\u7b11\u9053\uff1a\u201c\u5fd9\u4ec0\u4e48\uff1f \u5c71\u4e0b\u4e00\u7247\u8352\u6751\uff0c\u542c\u8bf4\u4f60\u4eec\u4e10\u5e2e\u6709\u8bb8\u591a\u4eba\u53d7\u4e86\u4f24\uff0c\u597d\uff0c\u4f60\u4eec\u6d3e\u4e00\u4e2a\u4eba\u53bb\uff0c\u5c06\u4ed6\u4eec\u90fd\u8bf7\u4e0a\u6765\u5427\u3002 \u6211\u8fd9\u4e2a\u5c71\u5be8\u867d\u5c0f\uff0c\u603b\u5f3a\u4f3c\u8352\u6751\u91ce\u5e97\u5427\uff01\u201d\u6bd5\u613f\u7a77\u4e0e\u767d\u5b5f\u5ddd\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4e2a\u5973\u9b54\u5934\u4f55 \u653e\u524d\u5014\u540e\u606d\uff1f\u201d\u53ea\u542c\u5f97\u51cc\u4e91\u51e4\u53c8\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e49\u519b\u4e2d\u786e\u662f\u5927\u6709\u4eba\u7269\uff0c\u6211\u4ee5\u524d\u5374\u662f\u5c0f\u770b\u5929 \u4e0b\u58eb\u4e86\u3002\u6709\u4f84\u5982\u6b64\uff0c\u60f3\u6765\u53f6\u5b97\u7559\u66f4\u662f\u540d\u4e0b\u65e0\u865a\u58eb\uff0c\u6211\u5c06\u6765\u4e5f\u8981\u53bb\u62dc\u8d1d\u62dc\u89c1\uff01\u201d\u7384\u745b\u9053\u4eba\u548c\u6bd5 \u613f\u7a77\u5927\u559c\uff0c\u5f97\u51cc\u4e91\u51e4\u5408\u4f19\uff0c\u6c5f\u897f\u4e00\u8def\u53ef\u4ee5\u5927\u632f\u58f0\u5a01\uff0c\u8fd9\u771f\u662f\u6c42\u4e4b\u4e0d\u5f97\u7684\u4e8b\uff0c\u9002\u624d\u7684\u82a5\u8482\u81ea\u662f \u4e00\u7b14\u52fe\u9500\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u631f\u7740\u51e0\u5206\u9189\u610f\uff0c\u4e0e\u4e8e\u627f\u73e0\u643a\u624b\u540c\u884c\uff0c\u7eb5\u89c8\u5c71\u5be8\u5f62\u52bf\uff0c\u53e3\u8bb2\u6307\u5212\uff0c\u8bba\u6b66\u8c08\u5175\uff0c\u4e8e\u627f \u73e0\u867d\u975e\u6240\u957f\uff0c\u4f46\u4ea6\u7565\u89e3\u5175\u4e8b\uff0c\u542c\u6765\u786e\u662f\u6bd4\u94c1\u955c\u5fc3\u5b9e\u9645\u5f97\u591a\u3002\u867d\u89c9\u51cc\u4e91\u98ce\u9152\u540e\u72c2\u6c14\u903c\u4eba\uff0c\u5fc3\u4e2d \u5bf9\u5979\u5374\u662f\u5341\u5206\u559c\u53d7\u3002 \u3000\u3000\u8fd9\u65f6\u6b63\u662f\u51ac\u6b8b\u814a\u6708\uff0c\u5c71\u9876\u58eb\u79ef\u96ea\u7691\u7691\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u5c31\u50cf\u94f6\u5149\u6cfb\u5730\u4e00\u822c\uff0c\u8f6c\u8fc7\u4e00\u4e2a\u5c71\u5773\uff0c \u5ffd\u89c1\u96ea\u91cc\u7ea2\u767d\u6885\u82b1\u76db\u5f00\uff0c\u5e7d\u9999\u6251\u9f3b\u3002\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u6211\u542c\u8bf4\u9093\u5c09\u5c71\u4e0a\u7684\u6885\u82b1\u6709\u9999\u96ea\u6d77\u4e4b\u79f0\uff0c\u53ef \u60dc\u6211\u6ca1\u6709\u5230\u8fc7\u3002\u8fd9\u91cc\u7684\u6885\u6797\uff0c\u8fd8\u662f\u6211\u6765\u4e86\u4e4b\u540e\uff0c\u624d\u53eb\u5979\u4eec\u5728\u5404\u5904\u5c71\u8c37\u79fb\u6765\u683d\u7684\u3002\u201d\u4e8e\u627f\u73e0 \u9053\uff1a\u201c\u539f\u6765\u59d0\u59d0\u5982\u6b64\u98ce\u96c5\u3002\u201d\u51cc\u4e91\u98ce\u5927\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u98ce\u96c5\uff1f\u6211\u79cd\u8fd9\u4e9b\u6885\u82b1\uff0c\u4e0d\u8fc7\u662f\u60f3\u7a0d\u89e3\u5c71 \u91ce\u7c97\u9119\u4e4b\u6c14\u7f62\u4e86\u3002\u59d0\u59d0\uff0c\u4f60\u51b0\u5fc3\u4fa0\u9aa8\uff0c\u50fb\u9759\u5e7d\u96c5\uff0c\u90a3\u624d\u771f\u4f3c\u6885\u82b1\u6e05\u82ac\u5462\u3002\u201d\u4e8e\u627f\u73e0\u82e6\u7b11\u9053\uff1a \u201c\u6211\u4f46\u613f\u80fd\u591a\u6240\u5386\u7ec3\uff0c\u53ef\u4ee5\u50cf\u6885\u82b1\u8010\u5bd2\uff0c\u53ef\u662f\u89c1\u4e86\u59d0\u59d0\uff0c\u624d\u77e5\u9053\u81ea\u5df1\u8fd8\u5dee\u5f97\u592a\u8fdc\u5462\uff01\u201d\u51cc\u4e91 \u51e4\u5ffd\u9053\uff1a\u201c\u82e5\u8bf4\u8010\u5bd2\uff0c\u5728\u5929\u5c71\u4e0a\u90a3\u624d\u771f\u662f\u5bd2\u51b7\uff0c\u8fd9\u91cc\u7684\u51ac\u5929\u7b80\u76f4\u4e0d\u50cf\u51ac\u5929\u3002\u201d\u4e8e\u627f\u73e0\u542c\u4e86\uff0c \u5fc3\u4e2d\u4e00\u52a8\uff0c\u84e6\u7136\u60f3\u8d77\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u8bb0\u5f97\u5e08\u7236\u5f20\u4e39\u67ab\u6709\u4e00\u65e5\u548c\u5979\u8c08\u8bba\u6d77\u5185\u5404\u5251\u6d3e\u540d\u5bb6\uff0c\u66fe\u8bf4\u8d77\u5929\u5c71\u4e4b\u4e0a\u6709\u4e00\u4e2a\u9690\u58eb\uff0c\u540d\u53eb\u970d \u884c\u4ef2\uff0c\u66fe\u53d1\u4e0b\u5b8f\u613f\uff0c\u8981\u641c\u96c6\u5929\u4e0b\u5251\u8c31\uff0c\u81ea\u521b\u4e00\u5bb6\u3002\u4ed6\u4e2d\u5e74\u9690\u5c45\uff0c\u7edd\u8ff9\u4e2d\u539f\uff0c\u5929\u5c71\u50fb\u5904\u56de\u7586\uff0c \u4eba\u8ff9\u7f55\u5230\uff0c\u77e5\u8005\u7edd\u5c11\u3002\u53ea\u6709\u7384\u673a\u9038\u58eb\u5728\u4ed6\u9690\u5c45\u4e4b\u524d\uff0c\u66fe\u548c\u4ed6\u89c1\u8fc7\u4e00\u9762\u3002\u7384\u673a\u9038\u58eb\u5f88\u4f69\u670d\u4ed6\u7684 \u6bc5\u529b\u8654\u5fc3\uff0c\u4f46\u4e5f\u89c9\u5f97\u4ed6\u53d1\u613f\u592a\u5b8f\uff0c\u8c08\u4f55\u5bb9\u6613\u3002\u5206\u522b\u4e4b\u540e\uff0c\u97f3\u4fe1\u9694\u7edd\uff0c\u7384\u673a\u9038\u58eb\u4e5f\u4e0d\u77e5\u9053\u4ed6\u751f \u6b7b\u5982\u4f55\uff0c\u81f3\u4e8e\u4ed6\u7a76\u7adf\u641c\u96c6\u591a\u5c11\u5251\u8c31\uff0c\u6b66\u529f\u6df1\u6d45\uff0c\u90a3\u66f4\u662f\u65e0\u4eba\u77e5\u9053\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u542c\u51cc\u4e91\u51e4\u63d0\u8d77\u5929\u5c71\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8131\u53e3\u95ee\u9053\uff1a\u201c\u59d0\u59d0\u5230\u8fc7\u5929\u5c71\u5417\uff1f\u201d\u51cc\u4e91\u51e4\u9053\uff1a \u201c\u6211\u662f\u5728\u5929\u5c71\u957f\u5927\u7684\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8bf7\u95ee\u970d\u884c\u4ef2\u970d\u8001\u524d\u8f88\u548c\u59d0\u59d0\u662f\u600e\u4e48\u4e2a\u79f0\u547c\uff1f\u201d\u51cc\u4e91\u98ce \u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684\u8205\u8205\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u59d0\u59d0\u5251\u6cd5\u5982\u6b64\u795e\u5999\uff0c\u60f3\u6765\u662f\u970d\u8001\u524d\u8f88\u4eb2\u6388\u7684 \u4e86\u3002\u55ef\uff0c\u6211\u542c\u8bf4\u4ed6\u8001\u4eba\u5bb6\u8981\u641c\u96c6\u5929\u4e0b\u5251\u8c31\uff0c\u81ea\u521b\u4e00\u5bb6\uff0c\u8fd9\u53ef\u771f\u662f\u4e86\u4e0d\u8d77\u554a\uff01\u201d \u3000\u3000\u970e\u7136\u95f4\uff0c\u5ffd\u89c1\u51cc\u4e91\u51e4\u9762\u4e0a\u63a0\u8fc7\u4e00\u4e1d\u9634\u5f71\uff0c\u5c31\u50cf\u6674\u7a7a\u6cab\u4e0a\u4e86\u6de1\u6de1\u7684\u8f7b\u4e91\uff0c\u4e8e\u627f\u73e0\u4e8e\u65e0\u610f\u4e4b \u4e2d\u542c\u5230\u4e86\u970d\u884c\u4ef2\u7684\u6d88\u606f\u548c\u51cc\u4e91\u51e4\u7684\u6765\u5386\uff0c\u751a\u662f\u9ad8\u5174\uff0c\u4e00\u65f6\u6ca1\u6709\u5bdf\u89c9\uff0c\u8fde\u73e0\u70ae\u5730\u8ffd\u95ee\u9053\uff1a\u201c\u5929 \u5c71\u4e0a\u5f88\u597d\u73a9\u4e48\uff1f\u970d\u8001\u524d\u8f88\u8fd8\u5728\u90a3\u513f\u4e48\uff1f\u201d\u51cc\u4e91\u51e4\u4ef0\u671b\u5c71\u9876\u79ef\u96ea\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u8205\u8205\u65e9\u5df2\u6b7b \u4e86\u3002\u5929\u5c71\u7684\u60c5\u666f\uff0c\u65e5\u5b50\u9694\u5927\u4e45\uff0c\u6211\u8bb0\u4e0d\u8d77\u6765\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u5f81\u4e86\u4e00\u6014\uff0c\u8fd9\u624d\u53d1\u89c9\u51cc\u4e91\u98ce\u9762\u8272\u7684 \u53d8\u5316\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u63d0\u8d77\u5929\u5c71\uff0c\u5979\u597d\u50cf\u6709\u4ec0\u4e48\u4f24\u5fc3\u4e4b\u4e8b\u4f3c\u7684\uff1f\u201d\u4e8e\u627f\u73e0\u672c\u6765\u8fd8\u6709\u8bb8\u591a \u7591\u95ee\uff0c\u4f8b\u5982\u5979\u662f\u600e\u6837\u79bb\u5f00\u5929\u5c71\uff0c\u5230\u8fd9\u513f\u6765\u505a\u5973\u5be8\u4e3b\u7684\uff1f\u89c1\u5979\u795e\u60c5\u6de1\u6f20\uff0c\u4e5f\u4e0d\u597d\u518d\u95ee\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u7f13\u7f13\u7a7f\u8fc7\u6885\u6797\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u51cc\u4e91\u51e4\u5ffd\u9053\uff1a\u201c\u4f60\u90a3\u4f4d\u53f6\u5927\u54e5\u771f\u6709\u610f\u601d\u3002\u201d\u4e8e\u627f\u73e0\u9762\u4e0a \u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u662f\u51e0\u4e2a\u6708\u524d\u624d\u8ba4\u8bc6\u4ed6\u7684\uff0c\u53d9\u8d77\u6765\u624d\u77e5\u9053\u662f\u540c\u4e00\u5e08\u95e8\u3002\u201d\u51cc\u4e91\u51e4\u7b11\u4e86\u4e00\u7b11\uff0c \u9053\uff1a\u201c\u4ed6\u5bf9\u4f60\u5173\u5fc3\u4e4b\u6781\uff0c\u4f60\u548c\u6211\u6bd4\u5251\u4e4b\u65f6\uff0c\u6211\u4ece\u4ed6\u7684\u773c\u8272\u91cc\u770b\u5f97\u51fa\u6765\u3002\u201d\u4e8e\u627f\u73e0\u7f9e\u5f97\u4f4e\u4e0b\u4e86 \u5934\uff0c\u9053\uff1a\u201c\u59d0\u59d0\u53d6\u7b11\u4e86\u3002\u201d\u51cc\u4e91\u51e4\u5fae\u559f\u8bf4\u9053\uff1a\u201c\u6709\u4eba\u5173\u5fc3\uff0c\u90a3\u4fbf\u662f\u6700\u5927\u7684\u798f\u4efd\u3002\u55ef\uff0c\u4f60\u7684\u53f6 \u5927\u54e5\u771f\u50cf\u6211\u4e00\u4e2a\u719f\u8bc6\u7684\u4eba\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u5f26\u98a4\u6296\uff0c\u8f7b\u8f7b\u95ee\u9053\uff1a\u201c\u662f\u4e48\uff0c\u90a3\u662f\u8c01\uff1f\u201d\u51cc\u4e91\u51e4\u5ffd\u5730 \u53c8\u7eb5\u58f0\u957f\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u6709\u70b9\u9189\u4e86\uff0c\u65f6\u5019\u4e0d\u65e9\uff0c\u54b1\u4eec\u8be5\u56de\u53bb\u4e86\u3002\u54ce\uff0c\u4e00\u4e2a\u4eba\u5e38\u5e38\u4e3a\u5f80\u4e8b\u56f0 \u6270\uff0c\u90a3\u662f\u4f55\u82e6\uff1f\u201d\u4e8e\u627f\u73e0\u6709\u5982\u7ed9\u4eba\u5728\u5fc3\u5f26\u4e0a\u62e8\u4e86\u4e00\u4e0b\uff0c\u5ffd\u7136\u60f3\u8d77\u4e86\u94c1\u955c\u5fc3\u6765\uff0c\u767b\u65f6\u610f\u5174\u8427 \u7d22\uff0c\u4e5f\u5c31\u4e0d\u518d\u8c08\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u665a\uff0c\u51cc\u4e91\u51e4\u9080\u4e8e\u627f\u73e0\u8054\u5e8a\u591c\u8bdd\uff0c\u53ef\u662f\u665a\u9910\u4e4b\u65f6\uff0c\u51cc\u4e91\u51e4\u5927\u676f\u5927\u676f\u5730\u559d\u9152\uff0c\u5012\u5728\u5e8a \u4e0a\uff0c\u4e0d\u4e00\u4f1a\u5c31\u7761\u7740\u4e86\uff0c\u4e8e\u627f\u73e0\u5374\u7ffb\u6765\u8986\u53bb\u7761\u4e0d\u7740\u89c9\u3002\u6726\u80e7\u95f4\uff0c\u597d\u50cf\u81ea\u5df1\u53c8\u5230\u4e86\u6d31\u6d77\u4e4b\u6ee8\uff0c\u4e00 \u68f5\u5927\u9752\u6811\u6811\u53f6\u7e41\u8302\uff0c\u6d53\u836b\u853d\u5730\uff0c\u4e8e\u627f\u73e0\u6b63\u60f3\u8dd1\u5230\u6811\u4e0b\uff0c\u5ffd\u7136\u5e73\u5730\u4e0a\u53c8\u5192\u8d77\u4e00\u68f5\u5927\u9752\u6811\u6765\uff0c\u773c \u775b\u4e00\u82b1\uff0c\u4f46\u89c1\u4e24\u68f5\u5927\u9752\u6811\u4e0b\u7684\u7e41\u679d\u5bc6\u53f6\u4e4b\u4e2d\uff0c\u85cf\u7740\u4e00\u5bf9\u5c11\u5e74\u7537\u5973\u3002 \u3000\u3000\u5de6\u8fb9\u90a3\u68f5\u5927\u9752\u6811\u4e0b\u7ad9\u7684\u662f\u53f6\u6210\u6797\uff0c\u53f3\u8fb9\u90a3\u68f5\u5927\u9752\u6811\u4e0b\u7ad9\u7684\u662f\u51cc\u4e91\u51e4\u3002\u4e8e\u627f\u73e0\u6251\u8fc7\u53bb\u53eb \u9053\uff1a\u201c\u53f6\u5927\u54e5\uff01\u201d\u5929\u7a7a\u9690\u9690\u54cd\u8fc7\u96f7\u58f0\uff0c\u53f6\u6210\u6797\u5ffd\u7136\u4e0d\u89c1\u4e86\uff0c\u53ea\u6709\u90a3\u68f5\u5927\u9752\u6811\u5728\u6447\u52a8\u3002\u4e8e\u627f\u73e0 \u53eb\u9053\uff1a\u201c\u51cc\u59d0\u59d0\u3002\u201d\u51cc\u4e91\u51e4\u7b11\u9762\u76f8\u8fce\uff0c\u4e8e\u627f\u73e0\u5954\u5230\u5979\u7684\u8ddf\u524d\uff0c\u6b63\u60f3\u95ee\u9053\uff1a\u201c\u53f6\u5927\u54e5\u5462\uff1f\u201d\u9661 \u7136\u95f4\u5ffd\u89c1\u51cc\u4e91\u51e4\u67f3\u7709\u5012\u7ad6\uff0c\u5530\u5730\u4e00\u5251\u523a\u6765\uff01\u4e8e\u627f\u73e0\u5927\u53eb\u9053\uff1a\u201c\u51cc\u59d0\u59d0\uff0c\u662f\u6211\uff01\u662f\u6211\uff01\u201d\u5251\u5149 \u95ea\u95ea\uff0c\u8fce\u9762\u523a\u5230\uff0c\u4e8e\u627f\u73e0\u8fde\u8fde\u540e\u9000\uff0c\u201c\u535c\u901a\u201d\u4e00\u58f0\uff0c\u8dcc\u5165\u6d31\u6d77\u4e4b\u4e2d\uff0c\u53ea\u542c\u5f97\u8033\u8fb9\u6709\u4eba\u67d4\u58f0\u5524 \u9053\uff1a\u201c\u522b\u6015\uff0c\u522b\u6015\uff0c\u6211\u5728\u8fd9\u91cc\u5462\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u7741\u773c\u4e00\u770b\uff0c\u4f46\u89c1\u51dd\u4e91\u51e4\u7ad9\u5728\u9762\u524d\uff0c\u81ea\u5df1\u5374\u8dcc\u843d\u5e8a\u4e0b\uff0c\u518d\u4e00\u770b\u65f6\uff0c\u53ea\u89c1\u51cc\u4e91\u98ce\u7a7f\u7740 \u4e00\u5957\u591c\u884c\u8863\u670d\uff0c\u624b\u4e2d\u6b63\u62ff\u7740\u4e00\u628a\u660e\u6643\u6643\u7684\u957f\u5251\u3002\u4e8e\u627f\u73e0\u5927\u5403\u4e00\u60ca\uff0c\u7b80\u76f4\u4e0d\u77e5\u662f\u771f\u662f\u68a6\uff1f \u3000\u3000\u53ea\u542c\u5f97\u51cc\u4e91\u51e4\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5916\u9762\u4f3c\u662f\u6709\u591c\u884c\u4eba\u6765\u4e86\uff0c\u4f60\u522b\u60ca\u614c\uff0c\u6211\u53bb\u77a7\u77a7\u5c31\u6765\uff01\u201d\u7a97\u95e8 \u65e9\u5df2\u6253\u5f00\uff0c\u51cc\u4e91\u51e4\u4f3c\u4e4e\u6025\u4e0d\u53ca\u5f85\uff0c\u8bf4\u4e86\u8fd9\u4e24\u53e5\u8bdd\uff0c\u500f\u5730\u5c31\u7a7f\u7a97\u98de\u51fa\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5b9a\u4e86\u5b9a\u795e\uff0c\u8fd9\u624d\u77e5\u9053\u786e\u662f\u505a\u4e86\u4e00\u4e2a\u6076\u68a6\u3002\u7ad6\u8033\u7ec6\u542c\uff0c\u5916\u9762\u679c\u6709\u5fae\u788e\u7684\u811a\u6b65\u58f0\uff0c\u800c \u4e14\u4e0d\u6b62\u4e00\u4eba\uff0c\u4e8e\u627f\u73e0\u4e00\u542c\uff0c\u4fbf\u77e5\u8fd9\u4e9b\u4eba\u8f7b\u529f\u751a\u9ad8\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u5c82\u53ef\u8ba9\u51cc\u59d0\u59d0\u4e00\u4eba\u5192 \u9669\u3002\u201d\u62ab\u8d77\u8863\u670d\uff0c\u63d0\u8d77\u9752\u51a5\u5251\uff0c\u7acb\u5373\u4e5f\u8ffd\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u4e00\u53e3\u6c14\u8ffd\u5230\u524d\u5c71\uff0c\u8fd9\u624d\u89c1\u5230\u51cc\u4e91\u51e4\u7684\u80cc\u5f71\uff0c\u518d\u8ffd\u51fa\u534a\u91cc\u4e4b\u9065\uff0c\u524d\u9762\u96ea\u5730\u4e0a\u7684\u51e0\u6761 \u4eba\u5f71\u5df2\u9690\u7ea6\u53ef\u89c1\uff0c\u679c\u7136\u662f\u6765\u4e86\u8f7b\u529f\u8d85\u5353\u7684\u591c\u884c\u4eba\uff01\u4e8e\u627f\u73e0\u7591\u60d1\u4e4b\u6781\uff0c\u731c\u4e0d\u900f\u8fd9\u51e0\u4e2a\u591c\u884c\u4eba\u662f \u4ec0\u4e48\u8def\u9053\uff0c\u82e5\u8bf4\u662f\u597d\u610f\uff0c\u4e3a\u4f55\u4e0d\u6b63\u6b63\u5f53\u5f53\u5730\u62dc\u5c71\u6c42\u89c1\uff1f\u82e5\u8bf4\u662f\u574f\u610f\uff0c\u5374\u4e3a\u4f55\u4e00\u6765\u53c8\u8dd1\uff0c\u5e76\u4e0d \u7528\u51cc\u4e91\u51e4\u52a8\u624b\u8fc7\u62db\uff1f \u3000\u3000\u5c31\u5728\u8fd9\u4e2a\u65f6\u5019\uff0c\u5ffd\u89c1\u90a3\u51e0\u6761\u4eba\u5f71\uff0c\u4e00\u9f50\u505c\u6b65\uff0c\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u4f60\u4eec\u662f\u8c01\uff1f\u201d\u4e00\u4e2a\u7626\u957f\u6c49\u5b50 \u5e94\u9053\uff1a\u201c\u6211\u4eec\u662f\u970d\u5929\u90fd\u7684\u81f3\u4ea4\u53cb\u597d\uff0c\u54ce\u5440\uff0c\u51cc\u59d1\u5a18\uff0c\u4f60\u4e0d\u8ba4\u5f97\u6211\u4e86\u5417\uff1f\u6211\u662f\u706b\u9e92\u9e9f\u90dd\u4e91\u53f0\uff0c \u4e94\u5e74\u524d\u54b1\u4eec\u4e0d\u662f\u5728\u5929\u5c71\u5357\u9762\u77d7\u5cf0\u89c1\u8fc7\u4e00\u9762\u5417\uff1f\u8fd9\u51e0\u4f4d\u662f\u6211\u7684\u62dc\u628a\u5144\u5f1f\u3002\u201d \u3000\u3000\u4e94\u5e74\u524d\u51cc\u4e91\u51e4\u8fd8\u662f\u4e00\u4e2a\u5341\u4e94\u5c81\u7684\u5c0f\u59d1\u5a18\uff0c\u4f9d\u7a00\u8bb0\u5f97\u970d\u5929\u90fd\u7684\u670b\u53cb\u4e2d\u4f3c\u4e4e\u662f\u6709\u8fd9\u4e00\u4e2a\u4eba\uff0c \u6025\u58f0\u95ee\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u4eec\u4e3a\u4f55\u8fd9\u6837\u9b3c\u9b3c\u795f\u795f\u591c\u95f4\u5077\u6765\uff1f\u201d \u3000\u3000\u90dd\u4e91\u53f0\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u60f3\u60ca\u52a8\u4f60\u5be8\u4e2d\u4f17\u4eba\u3002\u5413\uff0c\u90a3\u662f\u8c01\u4eba\uff1f\u201d\u51cc\u4e91\u51e4\u56de\u5934\u4e00\u671b\uff0c\u9053\uff1a\u201c\u90a3 \u662f\u6211\u7684\u59d0\u59b9\uff0c\u6709\u8bdd\u4f46\u8bf4\u65e0\u59a8\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u542c\u5230\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u653e\u5bbd\u4e86\u5fc3\uff0c\u6697\u9053\uff1a\u201c\u539f\u6765\u662f\u51cc\u59d0\u59d0\u76f8\u8bc6\u7684\u3002\u201d\u4e0d\u4fbf\u4e0a\u524d\u542c\u4ed6\u4eec \u8c08\u8bdd\uff0c\u6b63\u60f3\u8d70\u5f00\uff0c\u5ffd\u542c\u5f97\u51cc\u4e91\u51e4\u56b7\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u662f\u970d\u5929\u90fd\u53eb\u4f60\u4eec\u6765\u7684\uff1f\u4ed6\u5728\u54ea\u513f\uff1f\u4ed6\u5728\u54ea \u513f\uff1f\u201d\u8a00\u8bed\u4e2d\u5145\u6ee1\u6fc0\u60c5\uff0c\u4f3c\u4e4e\u662f\u671f\u5f85\u7740\u4e00\u4e2a\u6e34\u671b\u591a\u5e74\u7684\u97f3\u8baf\u3002 \u3000\u3000\u90a3\u81ea\u79f0\u706b\u9e92\u9e9f\u90dd\u4e91\u53f0\u7684\u7626\u957f\u6c49\u5b50\u8bf4\u9053\uff1a\u201c\u970d\u5929\u90fd\u73b0\u5728\u9655\u4e2d\u67d0\u5730\uff0c\u8bf7\u51cc\u59d1\u5a18\u524d\u53bb\u76f8\u4f1a\u3002\u201d \u51cc\u4e91\u51e4\u9053\uff1a\u201c\u5929\u90fd\u65e2\u77e5\u6211\u5728\u6b64\u5c71\uff0c\u4e3a\u4f55\u4ed6\u4e0d\u4eb2\u6765\uff1f\u662f\u75c5\u4e86\u4e48\uff1f\u662f\u53d7\u4f24\u4e86\u4e48\uff1f\u201d\u90dd\u4e91\u53f0\u9053\uff1a \u201c\u5343\u91cc\u8fe2\u8fe2\uff0c\u4ed6\u4e0d\u65b9\u4fbf\u6765\uff0c\u59d1\u5a18\u4f60\u53bb\u4e86\u5c31\u77e5\u9053\u4e86\u3002\u201d\u51cc\u4e91\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u5343\u91cc\u8fe2\u8fe2\uff0c\u6211\u4e5f\u4e0d\u5bb9 \u6613\u53bb\u5440\u3002\u53eb\u6211\u6254\u4e0b\u8fd9\u5c71\u5be8\uff1b\u4e5f\u5f97\u6709\u4e9b\u65e5\u5b50\u5b89\u6392\u5440\u3002\u627f\u73e0\u77e5\u51cc\u4e91\u51e4\u8fd9\u4e24\u5e74\u6765\u4e0e\u5b98\u519b\u4f5c\u5bf9\uff0c\u65e9\u5df2 \u88ab\u5217\u4e3a\u6c5f\u6d0b\u5927\u76d7\uff0c\u5355\u8eab\u5317\u4e0a\uff0c\u786e\u662f\u5371\u9669\u4e4b\u6781\uff0c\u800c\u4e14\u5979\u4e5f\u820d\u4e0d\u4e0b\u4e24\u5e74\u6765\u540c\u7518\u5171\u82e6\uff0c\u4eb2\u5982\u59d0\u59b9\u7684 \u55bd\u5175\u3002 \u3000\u3000\u90dd\u4e91\u53f0\u9053\uff1a\u201c\u8fd9\u53ef\u4e3a\u96be\u4e86\u3002\u5929\u90fd\u95ee\u4f60\uff0c\u53ef\u8bb0\u5f97\u65e7\u65f6\u4e4b\u7ea6\u4e48\uff1f\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u90dd \u4e91\u53f0\u9053\uff1a\u201c\u73b0\u4e0b\u4e16\u4e71\u5175\u8352\uff0c\u6b63\u76f4\u9690\u5c45\u7ec3\u5251\uff0c\u5929\u90fd\u95ee\u4f60\uff0c\u90a3\u4e9b\u5251\u8c31\uff0c\u4f60\u8fd8\u6536\u85cf\u597d\u5462\uff1f\u201d\u51cc\u4e91\u98ce \u773c\u775b\u4e00\u7ea2\u9053\uff1a\u201c\u8fd9\u8bdd\u662f\u5929\u90fd\u8bf4\u7684\u4e48\uff1f\u201d\u90dd\u4e91\u53f0\u9053\uff1a\u201c\u4ed6\u6709\u4eb2\u7b14\u4e66\u4fe1\u5728\u6b64\uff0c\u4f60\u81ea\u5df1\u770b\u53bb\u3002\u201d \u3000\u3000\u51cc\u4e91\u51e4\u559c\u6ea2\u7709\u68a2\uff0c\u6708\u5149\u4e0b\u66f4\u589e\u59a9\u5a9a\u3002\u4e8e\u627f\u73e0\u5df2\u731c\u6599\u51e0\u5206\uff0c\u5fc3\u4e2d\u6697\u7b11\uff1a\u201c\u8fd9\u8c6a\u6c14\u903c\u4eba\u7684\u5dfe \u5e3c\u82f1\u96c4\uff0c\u5f97\u5230\u4e86\u5fc3\u4e0a\u4eba\u7684\u4e66\u4fe1\uff0c\u5374\u7f9e\u602f\u5f97\u4f3c\u65b0\u5a18\u5b50\u4e00\u822c\uff01\u201d\u53ea\u89c1\u51cc\u4e91\u51e4\u624b\u6307\u5fae\u5fae\u98a4\u6296\uff0c\u5c55\u5f00 \u4fe1\u7b3a\uff0c\u770b\u4e86\u4e00\u773c\uff0c\u5ffd\u5730\u8f7b\u58f0\u5ff5\u9053\uff1a\u201c\u51e4\u59b9\u5982\u6664\uff0c\u51e4\u59b9\u5982\u6664\u2026\u2026\u6664\uff01\u201d\u4e8e\u627f\u73e0\u51e0\u4e4e\u7b11\u51fa\u58f0\u6765\uff0c \u7b11\u5979\u9690\u85cf\u4e0d\u4f4f\u5fc3\u4e2d\u7684\u60c5\u611f\uff0c\u7adf\u628a\u60c5\u4eba\u7684\u547c\u5524\uff0c\u7ffb\u6765\u8986\u53bb\u7684\u5ff5\u51fa\u6765\u3002 \u3000\u3000\u5ffd\u89c1\u51cc\u4e91\u51e4\u9762\u8272\u4e00\u6c89\uff0c\u968f\u5373\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u539f\u6765\u5929\u90fd\u4e5f\u6599\u5230\u6211\u4e0d\u80fd\u7acb\u5373\u52a8\u8eab\uff0c\u6240\u4ee5\u8bf7\u4f60\u4eec \u8fd9\u51e0\u4f4d\u6b66\u529f\u9ad8\u5f3a\u4e4b\u58eb\u4ee3\u4e3a\u62a4\u9001\u5251\u8c31\u3002\u54c8\uff0c\u96be\u4e3a\u4ed6\u60f3\u5f97\u771f\u5468\u5230\u5440\uff01\u201d\u90dd\u4e91\u53f0\u9053\uff1a\u201c\u6211\u4eec\u867d\u8bf4\u6280 \u4e1a\u5e73\u5eb8\uff0c\u4f46\u53d7\u4e86\u5929\u90fd\u5144\u7684\u91cd\u6258\uff0c\u81ea\u5f53\u820d\u4e86\u6027\u547d\uff0c\u4e5f\u8981\u5c06\u5251\u8c31\u9001\u5230\u5929\u90fd\u5144\u624b\u4e2d\u3002\u201d \u3000\u3000\u51cc\u4e91\u51e4\u773c\u6ce2\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u597d\u4e00\u73ed\u591f\u4e49\u6c14\u7684\u670b\u53cb\uff0c\u90a3\u51e0\u672c\u5251\u8c31\u672c\u6765\u662f\u970d\u5bb6\u4e4b\u7269\uff0c\u5929\u90fd\u6765 \u8981\uff0c\u6211\u6ca1\u6709\u4e0d\u7ed9\u4e4b\u7406\uff0c\u6709\u4f60\u4eec\u62a4\u9001\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u4e91\u53f0\uff0c\u4f60\u8fc7\u6765\u3002\u201d\u90dd\u4e91\u53f0\u6014\u4e86\u4e00\u6014\uff0c \u9053\uff1a\u201c\u90a3\u51e0\u672c\u5251\u8c31\uff0c\u51cc\u59d1\u5a18\u968f\u8eab\u643a\u5e26\u7740\u4e48\uff1f\u201d\u51cc\u4e91\u51e4\u201c\u6664\u201d\u4e86\u4e00\u58f0\uff0c\u4f38\u624b\u5165\u6000\u3002\u90dd\u4e91\u53f0\u8d70\u4e0a \u4e24\u6b65\uff0c\u51cc\u4e91\u51e4\u5ffd\u5730\u4e00\u58f0\u957f\u7b11\uff0c\u5c31\u5728\u8fd9\u970e\u90a3\u4e4b\u95f4\uff0c\u62d4\u5251\u51fa\u9798\uff0c\u5530\u5730\u4e00\u5251\u5411\u90dd\u4e91\u53f0\u523a\u53bb\u3002\u540c\u65f6\u5de6 \u624b\u4e00\u626c\uff0c\u4e09\u652f\u8774\u8776\u9556\u7535\u5c04\u800c\u51fa\uff0c\u539f\u6765\u5979\u638f\u7684\u4e0d\u662f\u5251\u8c31\u800c\u662f\u6697\u5668\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u90dd\u4e91\u53f0\u7684\u80a9\u5934\u5df2\u7740\u4e86\u4e00\u5251\uff0c\u8fd8\u5e78\u4ed6\u95ea\u5f97\u5feb\uff0c\u8981\u4e0d\u7136\u7435\u7436\u9aa8\u4e5f\u7ed9\u6d1e \u7a7f\u3002\u90dd\u4e91\u53f0\u5927\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u662f\u4e00\u756a\u597d\u610f\uff0c\u4f60\u600e\u4e48\u51fa\u6b64\u6bd2\u624b\uff1f\u201d\u51cc\u4e91\u51e4\u8ffd\u8e2a\u6025\u4e0a\uff0c\u5530\u5530\u4e24\u5251\uff0c \u8fde\u73af\u75be\u523a\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u597d\u4e00\u756a\u597d\u610f\uff0c\u54fc\uff0c\u54fc\uff0c\u4f60\u8fd8\u5f53\u6211\u662f\u516d\u5e74\u524d\u4e0d\u61c2\u4e8b\u7684\u5c0f\u59d1\u5a18\uff1f\u5feb\u8bf4\uff0c\u4f60 \u4eec\u5230\u5e95\u628a\u970d\u5929\u90fd\u600e\u4e48\u6837\u4e86\uff1f\u4f60\u4eec\u5077\u5b66\u4ed6\u7684\u7b14\u8ff9\uff0c\u600e\u7792\u5f97\u8fc7\u6211\u7684\u773c\u775b\uff1f\u201d \u3000\u3000\u90dd\u4e91\u53f0\u8fde\u95ea\u4e09\u5251\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u77a7\u6e05\u695a\u4e9b\uff0c\u8fd9\u660e\u660e\u662f\u970d\u5929\u90fd\u7684\u4eb2\u7b14\u4e66\u4fe1\uff0c\u600e\u4e48\u8bf4\u662f\u5047 \u7684\uff1f\u201d\u51cc\u4e91\u51e4\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u8bf4\u771f\u8bdd\uff0c\u6211\u5c31\u628a\u4f60\u7684\u62db\u5b50\u5e9f\u4e86\uff01\u201d\u4e00\u6296\u624b\uff0c\u53c8\u662f\u56db\u679a\u8774\u8776\u50cf \u8fde\u7fe9\u98de\u51fa\u3002 \u3000\u3000\u53ea\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e00\u7247\u788e\u91d1\u65ad\u7389\u4e4b\u58f0\uff0c\u4e0e\u90dd\u4e91\u53f0\u540c\u6765\u7684\u4e00\u4e2a\u7ef4\u4eba\uff0c\u821e\u8d77\u4e00\u67c4\u94dc\u9524\uff0c\u5c06\u51cc\u4e91 \u51e4\u51e0\u679a\u8774\u8776\u9556\u9707\u5f97\u7c89\u788e\u3002\u90dd\u4e91\u53f0\u62d4\u51fa\u4e00\u5bf9\u5224\u5b98\u7b14\uff0c\u5de6\u7b14\u4e00\u62ac\uff0c\u67b6\u5f00\u4e86\u51cc\u4e91\u51e4\u7684\u9752\u94a2\u5251\uff0c\u53f3\u7b14 \u4e00\u6307\uff0c\u75be\u70b9\u5979\u80f8\u524d\u7684\u201c\u4e73\u7a81\u7a74\u201d\uff0c\u6012\u58f0\u9a82\u9053\uff1a\u201c\u54b1\u4eec\u662f\u770b\u5728\u5929\u90fd\u5144\u7684\u4efd\u4e0a\uff0c\u8c01\u8fd8\u6015\u4f60\u4e0d\u6210\uff1f \u54fc\uff0c\u8fd9\u6cfc\u5a46\u5a18\u4e0d\u8bb2\u7406\uff0c\u54b1\u4eec\u5148\u628a\u5979\u5e9f\u4e86\uff01\u201d \u3000\u3000\u4e0e\u90dd\u4e91\u53f0\u540c\u6765\u7684\u5171\u6709\u4e09\u4eba\uff0c\u5176\u4e2d\u4e24\u4e2a\u7ef4\u4eba\uff0c\u4e00\u4e2a\u624b\u821e\u94dc\u9524\uff0c\u4e00\u4e2a\u4f7f\u6708\u7259\u5f2f\u5200\uff0c\u81c2\u529b\u6c89\u96c4 \u4e4b\u6781\uff0c\u53e6\u4e00\u6c49\u4eba\u4f7f\u7684\u662f\u4e00\u6761\u94a2\u97ad\uff0c\u957f\u8fbe\u4e00\u4e08\uff0c\u97ad\u98ce\u970d\u970d\uff0c\u4e13\u5411\u51cc\u4e91\u51e4\u7684\u4e0b\u4e09\u8def\u626b\u6765\uff0c\u4e5f\u662f\u4e00 \u4e2a\u52b2\u654c\u3002\u4f46\u6700\u5389\u5bb3\u7684\u8fd8\u662f\u90a3\u4e2a\u90dd\u4e91\u53f0\uff0c\u4ed6\u867d\u53d7\u4e86\u5251\u4f24\uff0c\u4e00\u5bf9\u5224\u5b98\u7b14\u4ecd\u662f\u5201\u94bb\u7075\u6d3b\u975e\u5e38\uff0c\u62db\u62db \u6307\u5411\u51cc\u4e91\u51e4\u7684\u8981\u5bb3\u7a74\u9053\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u7eb5\u58f0\u957f\u7b11\uff0c\u5728\u56db\u4eba\u56f4\u653b\u4e4b\u4e0b\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u90a3\u4e24\u4e2a\u7ef4\u4eba\u6043\u7740\u5175\u5668\u91cd\u6c14\u529b \u5927\uff0c\u60f3\u7838\u65ad\u51cc\u4e91\u51e4\u7684\u9752\u94a2\u5251\uff0c\u5c82\u77e5\u8fde\u5979\u7684\u8863\u88f3\u4e5f\u6cbe\u4e0d\u7740\uff0c\u4f46\u89c1\u5251\u5149\u95ea\u95ea\uff0c\u5c31\u5728\u9762\u95e8\u4e0a\u6643\u6765\u6643 \u53bb\uff0c\u53f1\u54a4\u58f0\u4e2d\uff0c\u51cc\u4e91\u51e4\u624b\u8155\u4e00\u7ffb\uff0c\u4fa7\u7684\u4e00\u58f0\uff0c\u90a3\u821e\u7740\u94dc\u9524\u7684\u7ef4\u4eba\u5148\u4e2d\u4e86\u4e00\u5251\u3002\u90dd\u4e91\u53f0\u53eb\u9053\uff1a \u201c\u4e0d\u5fc5\u786c\u62fc\uff0c\u5c06\u5979\u56f4\u7740\u3002\u201d\u5224\u5b98\u7b14\u4e00\u5206\uff0c\u5de6\u7b14\u70b9\u7a74\uff0c\u53f3\u7b14\u62db\u67b6\uff0c\u5c06\u51cc\u4e91\u51e4\u7684\u62db\u6570\uff0c\u63a5\u4e86\u5341\u4e03 \u516b\uff0c\u90a3\u4f7f\u94a2\u97ad\u7684\u6c49\u5b50\uff0c\u5728\u4e00\u4e08\u4e4b\u5916\u53d1\u62db\uff0c\u6559\u51cc\u4e91\u51e4\u4e0d\u80fd\u6b3a\u8eab\u53ae\u62fc\uff0c\u97ad\u957f\u5251\u77ed\uff0c\u51cc\u4e91\u51e4\u88ab\u90dd\u4e91 \u53f0\u7eca\u4f4f\uff0c\u8fd8\u771f\u65e0\u5948\u4ed6\u4f55\u3002\u90a3\u4e24\u4e2a\u7ef4\u4eba\u9000\u5230\u5916\u56f4\uff0c\u6708\u7259\u5200\u4e0e\u94dc\u9524\u4ecd\u7136\u821e\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u62e6\u4f4f\u4e86\u51cc \u4e91\u51e4\u7684\u9000\u8def\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u53eb\u9053\uff1a\u201c\u51cc\u59d0\u59d0\uff0c\u4f60\u8981\u628a\u8fd9\u7626\u6c49\u5b50\u7684\u62db\u5b50\u5e9f\u4e86\uff0c\u662f\u4e48\uff1f\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u4e8e\u627f\u73e0\u9053\uff1a\u201c\u597d\uff0c\u4e0d\u5fc5\u59d0\u59d0\u52a8\u624b\uff0c\u6211\u5148\u6253\u778e\u4ed6\u5de6\u8fb9\u7684\u773c\u775b\uff01\u201d\u90dd\u4e91\u53f0\u65e9\u5df2\u9632\u5907\u4e8e\u627f\u73e0\u4f1a\u6765\u52a9 \u6218\uff0c\u4f46\u89c1\u5979\u6bd4\u51cc\u4e91\u51e4\u66f4\u5e74\u8f7b\uff0c\u5374\u4e5f\u5e76\u4e0d\u600e\u6837\u5728\u610f\uff0c\u542c\u5979\u53e3\u51fa\u5927\u8a00\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\uff0c\u4f60 \u5bb6\u5927\u7237\u662f\u4e13\u6253\u6697\u5668\u7684\u884c\u5bb6\uff0c\u770b\u662f\u8c01\u628a\u8c01\u7684\u62db\u5b50\u5e9f\u4e86\uff1f\u201d\u5224\u5b98\u7b14\u4e00\u62ac\uff0c\u62a4\u7740\u9762\u95e8\uff0c\u4e00\u679d\u7529\u624b\u7bad \u5728\u8896\u4e2d\u53d1\u51fa\u3002 \u3000\u3000\u4f46\u89c1\u91d1\u5149\u4e00\u95ea\uff0c\u7535\u5c04\u800c\u6765\uff0c\u90dd\u4e91\u53f0\u5224\u5b98\u7b14\u5f80\u4e0a\u4e00\u7838\uff0c\u5c82\u77e5\u4e8e\u627f\u73e0\u7684\u91d1\u82b1\u6697\u5668\uff0c\u795e\u5999\u975e \u5e38\uff0c\u5979\u7528\u4e86\u53cd\u65cb\u4e4b\u529b\uff0c\u521a\u521a\u78b0\u7740\u7b14\u5c16\uff0c\u5ffd\u5730\u4e00\u4e2a\u62d0\u5f2f\uff0c\u90dd\u4e91\u53f0\u8fd9\u624d\u77e5\u9053\u4e0d\u5999\uff0c\u6b63\u60f3\u64a4\u56de\u53f3\u624b \u7684\u5224\u5bab\u7b14\u62db\u67b6\uff0c\u9000\u6b65\u62bd\u8eab\uff0c\u51cc\u4e91\u51e4\u8eab\u624b\u4f55\u7b49\u5feb\u6377\uff0c\u4e00\u62db\u201c\u79e6\u5cad\u4e91\u6a2a\u201d\uff0c\u628a\u4ed6\u7684\u5224\u5b98\u7b14\u5c01\u7740\uff0c \u4f46\u542c\u5f97\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u679a\u91d1\u82b1\u5df2\u628a\u90dd\u4e91\u53f0\u5de6\u773c\u7684\u773c\u73e0\u6253\u51fa\u3002 \u3000\u3000\u90dd\u4e91\u53f0\u5927\u53eb\u4e00\u58f0\uff0c\u53cc\u7b14\u8131\u624b\u63b7\u51fa\uff0c\u51cc\u4e91\u51e4\u98de\u8eab\u4e00\u8dc3\uff0c\u4f46\u89c1\u5730\u5df2\u548c\u8eab\u4e00\u6eda\uff0c\u6eda\u4e0b\u5c71\u5761\uff0c\u4e8e \u627f\u73e0\u53eb\u9053\uff1a\u201c\u8fd8\u4f60\u4e00\u652f\u7bad\uff01\u201d\u5c06\u521a\u521a\u63a5\u5230\u624b\u4e2d\u7684\u7529\u624b\u7bad\u53cd\u63b7\u51fa\u6765\uff0c\u5761\u9661\u5c71\u9ad8\uff0c\u90dd\u4e91\u53f0\u6eda\u5f97\u5feb \u6781\uff0c\u90a3\u652f\u7bad\u79bb\u4ed6\u4e09\u5c3a\uff0c\u6ca1\u6709\u5c04\u4e2d\u3002\u90a3\u4e24\u4e2a\u7ef4\u4eba\u4f9d\u6837\u753b\u846b\u82a6\uff0c\u4e5f\u628a\u5175\u5668\u98de\u51fa\uff0c\u62b1\u7740\u5934\u6eda\u4e0b\u53bb \u4e86\u3002 \u3000\u3000\u90a3\u4f7f\u94a2\u97ad\u7684\u6c49\u5b50\u4e5f\u60f3\u9003\u8d70\uff0c\u5374\u88ab\u4e8e\u627f\u73e0\u62e6\u4f4f\uff0c\u8fd9\u6c49\u5b50\u540d\u53eb\u80e1\u5b8f\uff0c\u662f\u585e\u5916\u7684\u9a6c\u8d3c\uff0c\u9a81\u52c7\u975e \u5e38\uff0c\u89c1\u4e8e\u627f\u73e0\u5e74\u5c0f\uff0c\u6043\u7740\u97ad\u957f\u5251\u77ed\uff0c\u5728\u79bb\u8eab\u4e00\u4e08\u5f00\u5916\uff0c\u731b\u7684\u53d1\u62db\uff0c\u8fde\u73af\u4e09\u97ad\uff0c\u75be\u626b\u800c\u4e0b\uff0c \u5530\uff0c\u5530\uff0c\u5530\uff0c\u98ce\u58f0\u547c\u54cd\uff0c\u5377\u8d77\u4e00\u56e2\u97ad\u5f71\uff0c\u6ee1\u4ee5\u4e3a\u4e8e\u627f\u73e0\u7eb5\u4e0d\u53d7\u4f24\uff0c\u4e5f\u5f97\u8ba9\u8def\uff0c\u54ea\u77e5\u4e8e\u627f\u73e0\u7684 \u201c\u7a7f\u82b1\u7ed5\u6811\u201d\u8eab\u6cd5\uff0c\u8f7b\u7075\u4e4b\u6781\uff0c\u82f1\u5999\u975e\u5e38\uff0c\u5728\u80e1\u5b8f\u7684\u957f\u97ad\u75be\u626b\u4e4b\u4e0b\uff0c\u7adf\u662f\u67f3\u8170\u7f13\u6446\uff0c\u83b2\u6b65\u8f7b \u79fb\uff0c\u82e5\u65e0\u5176\u4e8b\u5730\u7f13\u7f13\u884c\u6765\uff0c\u8fde\u8863\u89d2\u4e5f\u6ca1\u6709\u8ba9\u97ad\u68a2\u6cbe\u7740\uff0c\u80e1\u5b8f\u5927\u5403\u4e00\u60ca\uff0c\u8981\u5f85\u64a4\u97ad\u540e\u9000\uff0c\u4ea6\u5df2 \u6536\u52bf\u4e0d\u53ca\uff0c\u500f\u7136\u95f4\uff0c\u4f46\u89c1\u9752\u5149\u4e00\u7ed5\uff0c\u5580\u5693\u4e24\u58f0\uff0c\u90a3\u6761\u94dc\u97ad\u5df2\u88ab\u4e8e\u627f\u73e0\u7684\u9752\u51a5\u5b9d\u5251\u524a\u4e3a\u4e09\u6bb5\u3002 \u4e8e\u627f\u73e0\u968f\u624b\u4e00\u62db\u201c\u767d\u86c7\u5410\u4fe1\u201d\uff0c\u5251\u5c16\u62b5\u4f4f\u4e86\u80e1\u5b8f\u7684\u54bd\u5589\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u7b11\u9053\uff1a\u201c\u59d0\u59d0\u6536\u5251\uff0c\u7559\u4e00\u4e2a\u6d3b\u53e3\uff0c\u5f85\u6211\u95ee\u4ed6\u3002\u201d\u4e00\u8dc3\u800c\u524d\uff0c\u70b9\u4e86\u80e1\u5b8f\u7684\u9ebb\u7a74\uff0c\u5389 \u58f0\u559d\u9053\uff1a\u201c\u970d\u5929\u90fd\u7684\u4e66\u4fe1\uff0c\u662f\u4f60\u4eec\u5047\u5192\u7684\u4e0d\u662f\uff1f\u201d\u80e1\u5b8f\u9053\uff1a\u201c\u8fd9\u4e0d\u5173\u6211\u7684\u4eab\uff0c\u662f\u90dd\u5927\u54e5\u5e72 \u7684\u3002\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u6479\u4eff\u5230\u4ed6\u7684\u7b14\u8ff9\uff1f\u201d\u80e1\u8870\u9053\uff1a\u201c\u90dd\u5927\u54e5\u4ece\u51c9\u5dde\u5e9c\u8bf1\u4e86\u4e00\u4e2a\u9000\u804c \u7684\u8001\u5e08\u7237\u6765\uff0c\u8d39\u4e86\u4e00\u4e2a\u6708\u7684\u529f\u592b\u5b66\u7684\u3002\u201d \u3000\u3000\u51cc\u4e91\u51e4\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5012\u662f\u7528\u5fc3\u826f\u82e6\uff01\u970d\u5929\u90fd\u5462\uff1f\u4ed6\u5230\u5e95\u5728\u4ec0\u4e48\u5730 \u65b9\uff1f\u4f60\u4eec\u600e\u80fd\u5077\u5230\u4e86\u4ed6\u7684\u7b14\u8ff9\uff1f\u201d\u80e1\u5b8f\u8fdf\u8fdf\u7591\u7591\uff0c\u8bb7\u8bb7\u4e0d\u8bed\u3002\u51cc\u4e91\u51e4\u559d\u9053\uff1a\u201c\u4e0d\u8bf4\u5b9e\u8bdd\uff0c\u6211 \u5c31\u5148\u628a\u4f60\u7684\u62db\u5b50\u5e9f\u4e86\uff01\u201d\u80e1\u5b8f\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u970d\u5929\u90fd\uff0c\u970d\u5929\u90fd\u4ed6\u65e9\u5df2\u6b7b\u4e86\uff01\u201d\u51cc\u4e91\u51e4\u9762\u8272\u60e8 \u767d\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u600e\u4e48\u6b7b\u7684\uff1f\u201d\u80e1\u5b8f\u9053\uff1a\u201c\u662f\u90dd\u4e91\u53f0\u5c06\u4ed6\u6740\u6b7b\u7684\uff01\u201d\u51cc\u4e91\u51e4\u5ffd\u5730\u8fde\u58f0\u51b7\u7b11\uff0c \u8bf4\u9053\uff1a\u201c\u51ed\u90dd\u4e91\u53f0\u90a3\u70b9\u529f\u592b\uff0c\u80fd\u628a\u970d\u5929\u90fd\u6740\u4e86\uff1f\u54fc\uff0c\u4f60\u80e1\u8bf4\u516b\u9053\uff0c\u610f\u6b32\u4f55\u4e3a\uff1f\u201d\u53cc\u6307\u4e00\u63a2\uff0c \u4f5c\u52bf\u5c31\u8981\u6316\u80e1\u5b8f\u7684\u773c\u73e0\u3002 \u3000\u3000\u80e1\u5b8f\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u5be8\u4e3b\u4e14\u6162\uff0c\u5f85\u6211\u9053\u6765\u3002\u201d\u51cc\u4e91\u51e4\u77aa\u773c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u82e5\u6709\u534a\u5b57\u865a\u8a00\uff0c \u8fde\u4f60\u7684\u820c\u5934\u4e5f\u5272\u4e86\uff01\u201d\u80e1\u5b8f\u9053\uff1a\u201c\u970d\u5929\u90fd\u5728\u534e\u5c71\u811a\u4e0b\uff0c\u9047\u5230\u4e86\u5927\u6f20\u795e\u72fc\u54c8\u6728\u56fe\uff0c\u54c8\u6728\u56fe\u60f3\u62a2 \u4ed6\u7684\u5251\u8c31\uff0c\u4e24\u4eba\u5927\u6253\u4e00\u573a\uff0c\u5f7c\u6b64\u90fd\u53d7\u4e86\u4f24\uff0c\u90dd\u4e91\u53f0\u8d81\u4e86\u73b0\u6210\uff0c\u5728\u4e24\u4eba\u90fd\u53d7\u4f24\u4e4b\u9645\uff0c\u8d76\u8d70\u4e86\u5927 \u6f20\u795e\u72fc\uff0c\u5411\u970d\u5929\u90fd\u7d22\u53d6\u5251\u8c31\uff0c\u4f5c\u4e3a\u916c\u62a5\uff0c\u613f\u66ff\u4ed6\u6cbb\u4f24\uff0c\u970d\u5929\u90fd\u4e0d\u5141\uff0c\u53c8\u6253\u8d77\u6765\uff0c\u8d66\u5927\u54e5\u4e00\u4e2a \u5931\u624b\uff0c\u70b9\u4e2d\u4e86\u4ed6\u7684\u91cd\u7a74\uff0c\u89e3\u6551\u4e0d\u53ca\uff0c\u540e\u6094\u4ea6\u5df2\u8fdf\u4e86\uff01\u201d \u3000\u3000\u5927\u6f20\u795e\u72fc\u662f\u585e\u5916\u6709\u540d\u9b54\u5934\uff0c\u80e1\u5b8f\u8fd9\u756a\u8bdd\u5012\u662f\u8bf4\u5f97\u5165\u60c5\u5165\u7406\uff0c\u51cc\u4e91\u51e4\u8d8a\u542c\u8d8a\u614c\uff0c\u84e6\u7136\u95f4\u82b1 \u5bb9\u5931\u8272\uff0c\u201c\u54c7\u201d\u5730\u4e00\u53e3\u9c9c\u8840\u5410\u4e86\u51fa\u6765\uff0c\u4e8e\u627f\u73e0\u6025\u5fd9\u5954\u8fc7\u53bb\u5c06\u5979\u6276\u7740\uff0c\u8bf4\u9053\uff1a\u201c\u51cc\u59d0\u59d0\uff0c\u4f60\u5148 \u522b\u6025\uff0c\u5f85\u54b1\u4eec\u518d\u4ed4\u7ec6\u7684\u95ee\u4ed6\u3002\u201d\u5ffd\u542c\u5f97\u5495\u549a\u4e00\u58f0\uff0c\u5374\u539f\u6765\u662f\u80e1\u5b8f\u8d81\u6b64\u65f6\u673a\uff0c\u81ea\u5df1\u8fd0\u6c14\u51b2\u5173\u89e3 \u7a74\uff0c\u4e5f\u548c\u8863\u6eda\u4e0b\u5c71\u5761\u53bb\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u54ea\u8fd8\u6709\u5fc3\u60c5\u8ffd\u654c\uff0c\u53ea\u89c1\u51cc\u4e91\u51e4\u6cea\u75d5\u6ee1\u9762\uff0c\u5ffd\u5730\u5927\u58f0\u53eb\u9053\uff1a\u201c\u970d\u5929\u90fd\u6b7b\u4e86\uff1f\u6211\u4e0d \u4fe1\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u8bf4\u9053\uff1a\u201c\u6211\u867d\u7136\u4e0d\u77e5\u9053\u970d\u5927\u54e5\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u4f46\u60f3\u6765\u603b\u662f\u4e2a\u667a\u52c7\u53cc\u5168\u7684\u82f1\u96c4\u597d\u6c49\uff0c \u8981\u4e0d\u7136\u4e5f\u914d\u4e0d\u4e0a\u59d0\u59d0\uff0c\u600e\u80fd\u5982\u6b64\u8f7b\u6613\u5730\u4fbf\u7ed9\u4eba\u5bb3\u4e86\u3002\u6211\u770b\u662f\u8fd9\u4e2a\u7626\u6c49\u6545\u610f\u8bd3\u4f60\uff0c\u4eca\u4f60\u5206\u5fc3\uff0c \u4ed6\u597d\u4e58\u673a\u9003\u8d70\uff01\u201d \u3000\u3000\u51cc\u4e91\u51e4\u773c\u775b\u4e00\u5f20\uff0c\u773c\u5149\u4e2d\u71c3\u8d77\u4e86\u4e00\u7ebf\u5e0c\u671b\uff0c\u5ffd\u5730\u53c8\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u90a3\u5b57\u8ff9\u5b66\u5f97\u771f\u50cf\uff0c\u5440\uff0c \u82e5\u4e0d\u662f\u4ed6\u4eec\u83b7\u5f97\u4e86\u4ed6\u624b\u6284\u7684\u5251\u8c31\uff0c\u53c8\u600e\u6837\u6479\u4eff\u5f97\u6765\uff1f\u201d\u51cc\u4e91\u51e4\u672c\u6765\u7cbe\u660e\u4e4b\u6781\uff0c\u8fd9\u65f6\u5374\u662f\u65b9\u5bf8 \u5927\u4e71\uff0c\u4e00\u4f1a\u513f\u5f80\u597d\u7684\u65b9\u9762\u731c\u60f3\uff0c\u4e00\u4f1a\u513f\u5f80\u574f\u7684\u65b9\u9762\u731c\u60f3\uff0c\u5982\u75f4\u4f3c\u50bb\uff0c\u597d\u534a\u5929\u6728\u7136\u4e0d\u8bed\u3002\u4e8e\u627f \u73e0\u6025\u4e86\uff0c\u6b63\u60f3\u518d\u529d\uff0c\u51cc\u4e91\u51e4\u5ffd\u7136\u4e00\u624b\u6293\u8d77\u4e86\u5730\u4e0a\u90a3\u5c01\u5047\u5192\u7684\u4e66\u4fe1\uff0c\u9053\uff1a\u201c\u5440\uff0c\u5047\u5192\u5f97\u8fd9\u4f6f \u50cf\uff0c\u771f\u4f3c\u89c1\u5230\u4e86\u4ed6\u4e00\u822c\u3002\u201d\u604b\u604b\u4e0d\u820d\u5730\u518d\u4e00\u6b21\u8bfb\u8fd9\u5c01\u4fe1\uff0c\u5ffd\u5730\u60f3\u8d77\u8fd9\u662f\u5351\u9119\u5c0f\u4eba\u7684\u5047\u5192\uff0c\u53c8 \u72e0\u72e0\u5730\u628a\u5b83\u6495\u788e\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u81ea\u5df1\u66fe\u53d7\u8fc7\u60c5\u7684\u78e8\u6298\uff0c\u6df1\u6df1\u4f53\u4f1a\u5230\u51cc\u4e91\u51e4\u7684\u5fc3\u60c5\uff0c\u8fd9\u65f6\u53cd\u89c9\u4e07\u8bed\u5343\u8a00\uff0c\u4e0d\u77e5\u4ece\u4f55 \u8bf4\u8d77\u3002\u53ea\u542c\u5f97\u51cc\u4e91\u51e4\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u4ed6\u771f\u7684\u6b7b\u4e86\u3002\u6b7b\u4e86\uff0c\u6211\u4e0d\u4fe1\uff0c\u6211\u4e0d\u4fe1\u5440\u2026\u2026\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u662f\u5440\uff0c\u672c\u6765\u4f60\u5c31\u4e0d\u8be5\u76f8\u4fe1\uff01\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u5440\uff0c\u6211\u5fc3\u5982\u4e71\u9ebb\uff0c\u8fd9\u8111\u888b\u4e5f\u4e0d \u542c\u4f7f\u5524\uff0c\u6211\u90fd\u8bf4\u7ed9\u4f60\u542c\uff0c\u597d\u59d0\u59d0\uff0c\u4f60\u7ed9\u6211\u7aef\u8be6\u7aef\u8be6\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u77e5\u9053\u6b64\u65f6\u6b64\u9645\uff0c\u53ea\u6709\u8ba9\u5979\u5c3d\u60c5\u503e\u5410\uff0c\u65b9\u80fd\u7a0d\u89e3\u54c0\u6101\uff0c\u96be\u5f97\u5979\u628a\u81ea\u5df1\u5f53\u4f5c\u4eb2\u59d0\u59b9\u770b \u5f85\uff0c\u4e8e\u662f\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u8bf4\u3002\u201d\u51cc\u4e91\u51e4\u62ac\u8d77\u5934\u6765\uff0c\u4ef0\u671b\u5c71\u5cad\u7684\u79ef\u96ea\uff0c\u597d\u50cf\u8fd9\u58c1\u4fbf\u662f\u5929 \u5c71\uff0c\u800c\u90a3\u96ea\u5149\u96ea\u6d77\u4e4b\u4e2d\uff0c\u6709\u7740\u970d\u5929\u90fd\u7684\u5f71\u5b50\u3002 \u3000\u3000\u53ea\u542c\u5979\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u51cc\u970d\u4e24\u5bb6\uff0c\u4e16\u4ee3\u4ea4\u597d\uff0c\u672c\u6765\u7956\u7c4d\u6c5f\u5357\uff0c\u6bd4\u90bb\u800c\u5c45\u3002\u5927\u7ea6\u5728\u767e\u5e74 \u4e4b\u524d\uff0c\u90a3\u65f6\u6b63\u662f\u5143\u672b\u660e\u521d\u7684\u65f6\u5019\uff0c\u7fa4\u96c4\u5e76\u8d77\uff0c\u4e89\u57ce\u4e89\u91ce\uff0c\u4e2d\u539f\u5927\u4e71\uff0c\u6c11\u4e0d\u804a\u751f\u3002\u51cc\u970d\u4e24\u5bb6\u7ed3 \u4f34\uff0c\u8fdc\u907f\u5175\u7978\uff0c\u76f4\u5230\u56de\u7586\uff0c\u4e24\u5bb6\u4e16\u4ee3\u901a\u5a5a\uff0c\u5230\u4e86\u7236\u4eb2\u548c\u8205\u8205\u8fd9\u4e00\u4ee3\uff0c\u6211\u7236\u4eb2\u53ea\u6709\u6211\u4e00\u4e2a\u5973 \u513f\uff0c\u970d\u884c\u4ef2\u8205\u8205\u4e5f\u53ea\u6709\u5929\u90fd\u8fd9\u4e2a\u513f\u5b50\uff0c\u6211\u7236\u4eb2\u65e9\u6b7b\uff0c\u6240\u4ee5\u6211\u81ea\u5e7c\u4fbf\u5728\u8205\u8205\u5bb6\u4e2d\u5c45\u4f4f\uff0c\u7531\u8205\u8205 \u629a\u517b\u6210\u4eba\u3002 \u3000\u3000\u201c\u6211\u4eec\u4e24\u5bb6\u672c\u6765\u662f\u6b66\u5b66\u4e16\u5bb6\uff0c\u970d\u884c\u4ef2\u8205\u8205\u517c\u4e24\u5bb6\u4e4b\u957f\uff0c\u6b66\u529f\u9020\u8be3\uff0c\u5c24\u5176\u8fdc\u80dc\u524d\u4eba\u3002\u4ed6\u5e74 \u8f7b\u4e4b\u65f6\uff0c\u5fc3\u96c4\u4e07\u4e08\uff0c\u4e5f\u66fe\u8fdc\u6e38\u4e2d\u539f\uff0c\u5931\u5fd7\u641c\u96c6\u5404\u5bb6\u5251\u8c31\uff0c\u72ec\u521b\u4e00\u6d3e\u3002\u540e\u6765\u89c1\u4e2d\u539f\u4ecd\u662f\u6218\u7978\u9891 \u7e41\uff0c\u4fbf\u53c8\u56de\u5230\u5929\u5c71\u9690\u5c45\uff0c\u53c8\u641c\u96c6\u585e\u5916\u7684\u5404\u6d3e\u5251\u8c31\uff0c\u60f3\u4ee5\u6bd5\u751f\u4e4b\u529b\uff0c\u5f00\u521b\u5929\u5c71\u5251\u6d3e\u3002 \u3000\u3000\u201c\u641c\u96c6\u5251\u8c31\uff0c\u90a3\u8fd8\u6bd4\u8f83\u5bb9\u6613\uff0c\u60f3\u5c06\u5404\u5bb6\u5404\u6d3e\u878d\u4f1a\u8d2f\u901a\uff0c\u81ea\u521b\u65b0\u6d3e\uff0c\u90a3\u5374\u662f\u8d39\u4e86\u4e00\u751f\u5fc3 \u8840\uff0c\u4e5f\u672a\u5fc5\u505a\u5f97\u5230\u7684\uff0c\u6211\u8205\u8205\u7a77\u5e74\u7d2f\u6708\uff0c\u82e6\u82e6\u94bb\u7a76\uff0c\u8fde\u5934\u53d1\u4e5f\u60f3\u5f97\u6591\u767d\u4e86\uff0c\u867d\u7136\u5c0f\u6709\u6210\u5c31\uff0c \u5374\u603b\u4e0d\u80fd\u6ee1\u610f\u3002\u4ed6\u7528\u5fc3\u8fc7\u5ea6\uff0c\u672a\u6ee1\u4e94\u5341\u4e4b\u5e74\uff0c\u7adf\u7136\u58ee\u5fd7\u672a\u916c\uff0c\u4fbf\u5148\u5f52\u9ec4\u571f\uff0c\u4e34\u6b7b\u524d\u6bb7\u6bb7\u5631\u5490 \u5929\u90fd\uff0c\u8981\u4ed6\u7ee7\u627f\u9057\u5fd7\uff0c\u4f20\u4e4b\u5b50\u5b59\uff0c\u4e00\u4ee3\u4e0d\u884c\uff0c\u4fbf\u4e24\u4ee3\u4e09\u4ee3\uff0c\u4e5f\u603b\u5f97\u628a\u878d\u4f1a\u5929\u4e0b\u5404\u5bb6\u5404\u6d3e\u5251\u672f \u7684\u5929\u5c71\u5251\u6d3e\u521b\u7acb\u8d77\u6765\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u542c\u4e86\u8fd9\u4e2a\u6545\u4e8b\uff0c\u751a\u662f\u611f\u52a8\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5979\u8205\u8205\u8fd9\u756a\u8654\u5fc3\u6bc5\u529b\uff0c\u771f\u53ef\u4ee5\u4e0e\u611a\u516c\u79fb \u5c71\u76f8\u6bd4\u3002\u5440\uff0c\u82e5\u662f\u970d\u884c\u4ef2\u5c1a\u5728\u4eba\u95f4\uff0c\u6211\u4e00\u5b9a\u8bf7\u5e08\u7236\u6210\u5168\u4ed6\u7684\u5fd7\u613f\u3002\u201d \u3000\u3000\u51cc\u4e91\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u5f80\u4e0b\u7eed\u9053\uff1a\u201c\u6211\u8205\u8205\u6b7b\u7684\u65f6\u5019\uff0c\u6211\u624d\u5341\u4e8c\u5c81\u3002\u5929\u90fd\u6bd4\u6211\u5e74\u957f\u56db\u5c81\uff0c\u6240 \u4ee5\u6211\u7684\u6b66\u529f\u6839\u57fa\u662f\u8205\u8205\u624e\u7684\uff0c\u5251\u672f\u5374\u662f\u8ddf\u5929\u90fd\u5b66\u7684\u3002\u6211\u4eec\u90fd\u6ca1\u4e86\u7236\u6bcd\uff0c\u4e24\u4e2a\u5927\u5b69\u5b50\u5728\u5929\u5c71\u76f8 \u4f9d\u4e3a\u547d\uff0c\u771f\u6bd4\u4eb2\u5144\u59b9\u8fd8\u8981\u4eb2\u3002 \u3000\u3000\u201c\u5929\u90fd\u6837\u6837\u90fd\u597d\uff0c\u8d28\u6734\u8bda\u631a\u5c31\u50cf\u4f60\u7684\u53f6\u5927\u54e5\u4e00\u6837\u3002\u4e0d\u8fc7\u9aa8\u5b50\u91cc\u5374\u4e5f\u6709\u70b9\u5fc3\u9ad8\u6c14\u50b2\uff0c\u4e0d\u613f \u5728\u5929\u5c71\u57cb\u6ca1\u4e00\u751f\uff0c\u8205\u8205\u4e00\u751f\u641c\u96c6\u4e86\u5341\u4e8c\u5bb6\u7684\u5251\u8c31\uff0c\u5929\u4e0b\u91cd\u8981\u7684\u5251\u6d3e\uff0c\u636e\u8205\u8205\u8bf4\u5171\u6709\u4e09\u5341\u516d \u6d3e\uff0c\u5373\u662f\u8bf4\u4ed6\u6240\u641c\u96c6\u7684\u5251\u8c31\uff0c\u4ec5\u4ec5\u53ea\u662f\u4e09\u5206\u4e4b\u4e00\u3002\u5929\u90fd\u4e00\u76f4\u60f3\u5230\u4e2d\u539f\u6e38\u5b66\uff0c\u5b8c\u6210\u4ed6\u7236\u4eb2\u7684\u5fd7 \u613f\uff0c\u53ea\u662f\u56e0\u4e3a\u987e\u5ff5\u5230\u6211\u5e74\u7eaa\u592a\u5c0f\uff0c\u8fdf\u8fdf\u6ca1\u6709\u6210\u884c\u3002 \u3000\u3000\u201c\u6643\u773c\u8fc7\u4e86\u56db\u5e74\uff0c\u74e6\u523a\u7684\u5c0f\u738b\u5b50\u5e26\u5175\u4fb5\u5165\u56de\u7586\uff0c\u5929\u5c71\u5357\u5317\u52a8\u8361\u4e0d\u5b81\uff0c\u5929\u90fd\u6709\u4e00\u65e5\u5bf9\u6211 \u8bf4\uff0c\u54b1\u4eec\u672c\u6765\u662f\u4e2d\u539f\u4eba\u6c0f\uff0c\u5148\u7956\u4e3a\u907f\u5175\u9003\u5230\u5929\u5c71\uff0c\u73b0\u5728\u56de\u7586\u4e5f\u662f\u5175\u8352\u9a6c\u4e71\uff0c\u54b1\u4eec\u53ea\u597d\u518d\u9003\u56de \u53bb\u5566\u3002\u54ce\uff0c\u82e5\u662f\u65e9\u77e5\u6709\u751f\u79bb\u6b7b\u522b\u4e4b\u7978\uff0c\u8fd8\u662f\u5728\u5929\u5c71\u9690\u5c45\u4e00\u4e16\u7684\u597d\u3002 \u3000\u3000\u201c\u4e0d\u8fc7\u90a3\u65f6\u5019\uff0c\u5176\u5b9e\u6211\u4e5f\u5f88\u61a7\u61ac\u4e2d\u539f\u7684\u7e41\u534e\uff0c\u6211\u7236\u4eb2\u7ed9\u6211\u8d77\u7684\u540d\u5b57\u4fbf\u53eb\u505a\u51cc\u6155\u534e\uff0c\u90a3\u662f \u8981\u6211\u6bcb\u5fd8\u6545\u56fd\uff0c\u604b\u6155\u4e2d\u534e\u7684\u610f\u601d\uff0c\u8d81\u8fd9\u4e2a\u673a\u4f1a\u56de\u5230\u4e2d\u534e\u6545\u571f\uff0c\u6211\u81ea\u7136\u662f\u6beb\u65e0\u5f02\u8a00\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u201c\u554a\u201d\u4e86\u4e00\u58f0\uff0c\u51cc\u4e91\u51e4\u51c4\u7136\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4f60\u77e5\u9053\u6211\u4f55\u4ee5\u4e00\u770b\u90a3\u5c01\u4fe1\uff0c\u5c31\u77e5\u9053\u5b83\u662f \u5047\u7684\u4e86\u5462\uff1f\u4e91\u51e4\u8fd9\u4e2a\u540d\u5b57\uff0c\u662f\u6211\u9003\u5230\u4e2d\u539f\u4e4b\u540e\uff0c\u81ea\u5df1\u8d77\u7684\uff0c\u5929\u90fd\u6839\u672c\u4e0d\u77e5\u9053\u6211\u6709\u8fd9\u4e2a\u540d\u5b57\uff0c \u4ed6\u4e00\u76f4\u5524\u6211\u505a\u534e\u59b9\u534e\u59b9\u7684\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u4eec\u540c\u8def\u800c\u6765\uff0c\u600e\u4e48\u53c8\u4f1a\u4e2d\u9014\u5206\u6563\u4e86\u5462\uff1f\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u4f60\u4eec\u5728\u4e2d\u539f\u957f\u5927 \u7684\u4eba\uff0c\u600e\u77e5\u9053\u5728\u6c99\u6f20\u8d76\u8def\u7684\u82e6\u51b5\u3002\u90a3\u4e9b\u5927\u6c99\u6f20\u51e0\u65e0\u8fb9\u9645\uff0c\u5e38\u5e38\u8d70\u4e86\u5341\u5929\u534a\u6708\uff0c\u672a\u5230\u8def\u5934\u3002\u6211 \u4eec\u4fbf\u662f\u5728\u6492\u54c8\u62c9\u5927\u6c99\u6f20\u5206\u6563\u7684\u3002\u90a3\u4e00\u65e5\u6211\u4eec\u6240\u5e26\u7684\u6c34\u5feb\u559d\u5b8c\u4e86\uff0c\u5929\u90fd\u5230\u51e0\u91cc\u5916\u4e00\u4e2a\u5c0f\u5c71\u8fb9\u53bb \u627e\u6c34\u6e90\uff0c\u5176\u65f6\u5929\u6c14\u6674\u6717\uff0c\u5c0f\u5c71\u8ddd\u79bb\u53c8\u8fd1\uff0c\u6211\u75b2\u5026\u6781\u4e86\uff0c\u5c31\u8ba9\u4ed6\u72ec\u884c\u3002\u54ea\u77e5\u4ed6\u4e00\u8d70\u4e4b\u540e\uff0c\u6c99\u6f20 \u84e6\u8d77\u72c2\u98ce\uff0c\u9ec4\u6c99\u6ee1\u5929\uff0c\u5341\u6b65\u4e4b\u5185\uff0c\u4e0d\u89c1\u4eba\u5f71\uff0c\u6211\u9a87\u6015\u6781\u4e86\uff0c\u5728\u72c2\u98ce\u9ec4\u6c99\u4e4b\u4e2d\u5954\u8dd1\uff0c\u60f3\u53bb\u627e \u4ed6\uff0c\u54ea\u77e5\u65b9\u5411\u8d70\u9519\uff0c\u8d8a\u8dd1\u8d8a\u8fdc\u3002\u6211\u88ab\u72c2\u98ce\u5439\u5012\uff0c\u9192\u8f6c\u6765\u65f6\uff0c\u4f46\u89c1\u6c99\u6f20\u53d8\u578b\uff0c\u8fdc\u8fdc\u8fd1\u8fd1\uff0c\u9ec4\u6c99 \u5806\u79ef\u6210\u5341\u51e0\u4e2a\u571f\u5806\uff0c\u81f3\u4e8e\u90a3\u5ea7\u5c0f\u5c71\uff0c\u5374\u8fde\u5f71\u5b50\u4e5f\u4e0d\u89c1\u4e86\u3002\u5e78\u559c\u540e\u6765\u6211\u78b0\u5230\u4e00\u4e2a\u9a86\u9a7c\u5546\u961f\uff0c\u8ddf \u4ed6\u4eec\u8d70\u51fa\u4e86\u6c99\u6f20\u3002\u53ef\u662f\u53c8\u78b0\u5230\u4e86\u74e6\u523a\u548c\u54c8\u8428\u514b\u65cf\u7684\u4e24\u519b\u4ea4\u6218\uff0c\u4e00\u8def\u6d41\u79bb\uff0c\u66f4\u662f\u65e0\u6cd5\u6253\u542c\u5929\u90fd \u7684\u4e0b\u843d\u4e86\u3002\u6211\u60f3\u5929\u90fd\u65e2\u8bf4\u8981\u6e38\u5b66\u4e2d\u539f\uff0c\u6211\u4fbf\u5230\u4e2d\u539f\u6253\u63a2\uff0c\u54ea\u77e5\u8fd9\u51e0\u5e74\u6765\uff0c\u8fd8\u662f\u4eca\u5929\u624d\u542c\u5230\u4ed6 \u7684\u97f3\u8baf\uff0c\u8fd9\u97f3\u8baf\u8fd8\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff1f\u731c\u4e0d\u900f\u4ed6\u662f\u6b7b\u662f\u751f\uff1f\u201d \u3000\u3000\u79ef\u6c34\u6d6e\u5149\uff0c\u5bd2\u6885\u5410\u8273\uff0c\u6708\u5149\u82b1\u5f71\u4e4b\u4e0b\uff0c\u51cc\u4e91\u51e4\u503e\u5410\u8877\u60c5\uff0c\u628a\u4e8e\u627f\u73e0\u542c\u5f97\u75f4\u4e86\u3002\u5fc3\u4e2d\u60f3 \u9053\uff1a\u201c\u65e5\u95f4\u770b\u5979\uff0c\u662f\u4f55\u7b49\u8c6a\u6c14\u903c\u4eba\uff0c\u5374\u539f\u6765\u5979\u4e00\u65b9\u9762\u662f\u4fa0\u9aa8\u5982\u94a2\uff0c\u4e00\u65b9\u9762\u53c8\u662f\u67d4\u60c5\u4f3c\u6c34\u3002\u201d \u53c8\u60f3\u9053\uff1a\u201c\u5979\u6709\u970d\u5929\u90fd\u8fd9\u6837\u7684\u98ce\u5c18\u4fa0\u4fa3\uff0c\u53ef\u4ee5\u6258\u523b\u9aa8\u76f8\u601d\uff0c\u7eb5\u4f7f\u6709\u751a\u4e0d\u5e78\uff0c\u4e5f\u4e0d\u6789\u6b64\u4e00 \u751f\u3002\u201d\u60f3\u8d77\u81ea\u5df1\u7684\u906d\u9047\uff0c\u4e0d\u7981\u9eef\u7136\u795e\u4f24\uff0c\u5bf9\u51cc\u4e91\u51e4\u65e2\u662f\u601c\u60dc\uff0c\u53c8\u662f\u7fa1\u6155\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u7eed\u9053\uff1a\u201c\u970d\u5929\u90fd\u4e0e\u6211\u4ece\u56de\u7586\u51fa\u8d70\u4e4b\u65f6\uff0c\u4ed6\u5c06\u8205\u8205\u6240\u9057\u4e0b\u7684\u5341\u4e8c\u672c\u5251\u8c31\uff0c\u90fd\u4ea4\u7ed9\u6211 \u4fdd\u7ba1\u3002\u4ed6\u66fe\u548c\u6211\u5f00\u73a9\u7b11\u4ed6\u8bf4\u8fc7\uff0c\u5047\u82e5\u6709\u4e00\u5929\u54b1\u4eec\u4e0d\u5e78\u79bb\u6563\uff0c\u8fd9\u5341\u4e8c\u672c\u5251\u8c31\u6211\u5df2\u719f\u8bb0\u80f8\u4e2d\uff0c\u4f60 \u51ed\u5251\u8c31\u81ea\u5df1\u4fee\u7ec3\uff0c\u4e5f\u53ef\u4ee5\u7ee7\u627f\u8205\u8205\u7684\u9057\u5fd7\u3002\u54ce\uff0c\u60f3\u4e0d\u5230\u5f80\u65e5\u620f\u8a00\uff0c\u7adf\u6210\u4e8b\u5b9e\u3002\u800c\u8fd9\u4e5f\u662f\u6211\u770b \u51fa\u90a3\u5c01\u4fe1\u5047\u5192\u7684\u53c8\u4e00\u4e2a\u539f\u56e0\uff0c\u8bd5\u60f3\u4ed6\u65e2\u719f\u8bb0\u80f8\u4e2d\uff0c\u4f55\u987b\u5411\u6211\u7d22\u8c31\u3002 \u3000\u3000\u201c\u6211\u5230\u4e86\u4e2d\u539f\uff0c\u4e5f\u66fe\u60f3\u8fc7\u904d\u8bbf\u6b66\u6797\u540d\u5bb6\uff0c\u52e4\u4fee\u7ec3\u5251\uff0c\u4e0d\u6599\u4e2d\u539f\u4e5f\u662f\u4e00\u6837\u7684\u5175\u8352\u9a6c\u4e71\uff0c\u8001 \u767e\u59d3\u6bd4\u56de\u7586\u8fd8\u82e6\uff0c\u6211\u4e00\u4e2a\u4eba\u95ef\u6765\u95ef\u53bb\uff0c\u4eba\u4e5f\u53d8\u5f97\u7c97\u91ce\u4e86\uff0c\u6211\u805a\u4e86\u4e00\u4e9b\u6d41\u79bb\u5931\u6240\u7684\u82e6\u547d\u5973\u513f\uff0c \u6e10\u6e10\u89c9\u5f97\u8fd9\u4e0d\u662f\u529e\u6cd5\uff0c\u7d22\u6027\u81ea\u5df1\u5f00\u5c71\u7acb\u5be8\uff0c\u505a\u8d77\u5973\u5be8\u4e3b\u6765\u3002\u6211\u60f3\u82e5\u662f\u5929\u90fd\u77e5\u9053\uff0c\u4ed6\u4e5f\u4f1a\u540c\u610f \u6211\u7684\u3002\u5440\uff0c\u53ef\u60dc\u6211\u4eca\u751f\u53ea\u6015\u89c1\u4e0d\u7740\u4ed6\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u59d0\u59d0\u4fa0\u9aa8\u805a\u80a0\uff0c\u5c31\u56e0\u4f60\u8fd9\u7247\u5584\u5fc3\uff0c\u8001\u5929\u7237\u4e5f\u5fc5\u5b9a\u4fdd\u4f51\u4f60\u4eec\u89c1\u9762\u3002\u201d\u51cc\u4e91\u51e4 \u82e6\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4f46\u613f\u5982\u6b64\u3002\u53ea\u662f\u90a3\u4e9b\u4eba\u600e\u77e5\u9053\u5251\u8c31\u5728\u6211\u624b\u4e2d\uff0c\u600e\u80fd\u5077\u5230\u5929\u90fd\u624b\u6284\u7684\u5251\u8c31\uff0c\u90a3 \u662f\u8205\u8205\u4ece\u5341\u4e8c\u672c\u5251\u8c31\u4e2d\u64b7\u5176\u7cbe\u534e\u53eb\u5929\u90fd\u6284\u4e0b\u6765\u7684\u3002\u4ece\u8fd9\u4e24\u4ef6\u4e8b\u770b\u6765\uff0c\u5929\u90fd\u4e5f\u6781\u53ef\u80fd\u906d\u9047\u4e86\u4ec0 \u4e48\u4e0d\u5e78\uff0c\u5403\u4e86\u4ed6\u4eec\u7684\u5927\u4e8f\u3002\u201d\u8bf4\u7740\u8bf4\u7740\uff0c\u773c\u6cea\u4e0d\u7981\u53c8\u6ef4\u4e0b\u6765\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u867d\u8bf4\u65b9\u5bf8\u5df2\u4e71\uff0c\u4f46\u8bb2\u7406\u8bba\u4e8b\uff0c\u8fd8\u662f\u6bd4\u4e8e\u627f\u73e0\u8001\u7ec3\u5f97\u591a\u3002\u4e8e\u627f\u73e0\u7adf\u60f3\u4e0d\u51fa\u7528\u4ec0\u4e48\u8bdd \u6765\u66ff\u5979\u5f00\u89e3\uff0c\u597d\u534a\u664c\u8bf4\u9053\uff1a\u201c\u5fe7\u80fd\u4f24\u4eba\u3002\u76ee\u524d\u6b63\u6709\u4e00\u756a\u4e8b\u4e1a\u8981\u5f85\u59d0\u59d0\u53bb\u505a\uff0c\u59d0\u59d0\u8fd8\u5e94\u81ea\u5df1\u4fdd \u91cd\u3002\u201d\u51cc\u4e91\u51e4\u51c4\u7136\u4e00\u7b11\uff0c\u5ffd\u5730\u6062\u590d\u4e86\u65e5\u95f4\u7684\u795e\u91c7\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u8fd9\u6211\u7406\u4f1a\u5f97\u5230\uff0c\u59d0\u59d0\uff0c\u4f60\u771f \u662f\u6211\u7684\u77e5\u5df1\uff0c\u6211\u6ca1\u6709\u5144\u5f1f\u59d0\u59b9\uff0c\u6211\u628a\u5929\u90fd\u5f53\u505a\u5144\u5f1f\uff0c\u4eca\u540e\u6211\u4e5f\u8981\u628a\u4f60\u5f53\u4f5c\u59d0\u59b9\u4e86\u3002\u201d\u4e8e\u627f\u73e0 \u9053\uff1a\u201c\u8fd9\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u201d\u53d9\u8d77\u5e74\u9f7f\uff0c\u51cc\u4e91\u51e4\u6bd4\u4e8e\u627f\u73e0\u5e74\u957f\u4e24\u5c81\uff0c\u5f53\u4e0b\u6387\u571f\u4e3a\u9999\uff0c\u7ed3\u62dc\u4e3a\u91d1\u5170 \u59d0\u59b9\u3002\u4e8e\u627f\u73e0\u5524\u4e86\u4e00\u58f0\u201c\u59d0\u59d0\u201d\uff0c\u51cc\u4e91\u51e4\u5524\u4e86\u4e00\u58f0\u201c\u59b9\u59b9\u201d\uff0c\u4e24\u4eba\u773c\u89d2\u90fd\u6c81\u51fa\u6676\u83b9\u7684\u6cea\u73e0\u3002 \u3000\u3000\u5ffd\u89c1\u6885\u679d\u98ce\u52a8\uff0c\u4e24\u4eba\u5b9a\u775b\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u53f6\u6210\u6797\u8d70\u4e86\u8fc7\u6765\uff0c\u8fdc\u8fdc\u8bf4\u9053\uff1a\u201c\u5be8\u4e2d\u5973\u5175\u4e0d\u89c1 \u4f60\u4eec\uff0c\u5979\u4eec\u53c8\u4f3c\u542c\u5f97\u6709\u591c\u884c\u4eba\u7684\u8e2a\u8ff9\uff0c\u5608\u4e86\u8d77\u6765\uff0c\u6ca1\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d\u51cc\u4e91\u51e4\u64e6\u62ed\u4e86\u6cea\u75d5\uff0c\u4e00\u7b11 \u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u4e8b\uff0c\u5982\u6b64\u826f\u591c\uff0c\u6211\u548c\u4e8e\u59d1\u5a18\u51fa\u6765\u6563\u5fc3\u3002\u65e2\u7136\u5979\u4eec\u62c5\u5fc3\uff0c\u6211\u8fd9\u5c31\u56de\u53bb\u5427\uff0c\u96be\u5f97 \u8fd9\u6885\u6797\u6708\u8272\uff0c\u4f60\u65e2\u7136\u8d77\u6765\u4e86\uff0c\u5c31\u966a\u4e8e\u59d1\u5a18\u591a\u73a9\u4e00\u4f1a\u5427\u3002\u201d\u4e8e\u627f\u73e0\u8ffd\u4e0a\u4e24\u6b65\uff0c\u51cc\u4e91\u51e4\u5df2\u7fe9\u7136\u8d70 \u51fa\u6885\u6797\u3002\u4e8e\u627f\u73e0\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u505c\u4e86\u4e0b\u6765\uff0c\u5fc3\u4e2d\u5927\u662f\u611f\u52a8\u3002 \u3000\u3000\u53f6\u6210\u6797\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u771f\u662f\u96c5\u5174\u4e0d\u6d45\u3002\u201d\u4e8e\u627f\u73e0\u5fc3\u4e2d\u9178\u695a\uff0c\u9ed8\u9ed8\u65e0\u8a00\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u51cc\u59d0 \u59d0\u8eab\u7ecf\u767e\u53d8\uff0c\u5c45\u7136\u80fd\u6291\u4f4f\u5fc3\u5934\u60e8\u75db\uff0c\u5374\u4e3a\u6211\u4eec\u8bbe\u60f3\u3002\u54ce\uff0c\u4f60\u8fd9\u756a\u597d\u610f\uff0c\u53ea\u6015\u6211\u8981\u5c06\u5b83\u8f9c\u8d1f \u4e86\u3002\u201d \u3000\u3000\u53f6\u6210\u6797\u7f13\u6b65\u8d70\u8fd1\uff0c\u4f46\u89c1\u4e8e\u627f\u73e0\u4f4e\u5782\u626e\u9888\uff0c\u773c\u89d2\u513f\u4e5f\u4e0d\u5411\u81ea\u5df1\u6d41\u6cea\uff0c\u4e0d\u7981\u9762\u4e0a\u4e00\u7ea2\uff0c\u53c8\u9000 \u4e86\u4e24\u6b65\uff0c\u8baa\u8baa\u95ee\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\uff0c\u4f60\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u8f7b\u8f7b\u62c2\u5f00\u5934\u4e0a\u7684\u6885\u679d\uff0c\u5ffd\u5730\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u53f6\u5927\u54e5\uff0c\u4f60\u770b\u5be8\u4e3b\u8fd9\u4eba\u600e\u6837\uff1f\u201d\u53f6\u6210\u6797 \u6115\u4e86\u4e00\u6115\uff0c\u968f\u5373\u7b11\u9053\uff1a\u201c\u51cc\u5be8\u4e3b\u80f8\u85cf\u7532\u5175\uff0c\u895f\u6000\u723d\u6717\uff0c\u81ea\u662f\u4eba\u4e2d\u8c6a\u6770\uff0c\u5973\u4e2d\u4e08\u592b\uff01\u201d\u4e8e\u627f\u73e0 \u5fc3\u4e2d\u4e00\u52a8\uff0c\u624b\u6307\u4e00\u98a4\uff0c\u5c06\u6273\u7740\u7684\u6885\u679d\u653e\u5f00\uff0c\u6885\u82b1\u7c0c\u7c0c\u843d\u4e0b\uff0c\u6cbe\u6ee1\u4e86\u5979\u7684\u4e91\u9b13\u8863\u88f3\u3002 \u3000\u3000\u53f6\u6210\u6797\u95ee\u9053\uff1a\u201c\u51cc\u5be8\u4e3b\u548c\u4f60\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u566b\uff0c\u53f6\u5927\u54e5\u6211\u60f3\u95ee\u4f60 \u4e00\u53e5\u8bdd\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u53e4\u4eba\u8bf4\uff0c\u4e24\u60c5\u76f8\u60a6\uff0c\u575a\u5982\u91d1\u77f3\u3002\u8fd9\u8bdd\u662f\u771f\u7684 \u4e48\uff1f\u201d\u53f6\u6210\u6797\u9762\u7ea2\u5fc3\u8df3\uff0c\u8baa\u8baa\u8bf4\u9053\uff1a\u201c\u53e4\u4e66\u6240\u8f7d\uff0c\u50cf\u795d\u82f1\u53f0\u6b7b\u540e\u5316\u8776\uff0c\u5b5f\u59dc\u5973\u54ed\u5012\u957f\u57ce\uff0c\u5982 \u6b64\u81f3\u60c5\uff0c\u76f4\u53ef\u611f\u52a8\u5929\u5730\uff0c\u575a\u5982\u91d1\u77f3\uff0c\u90a3\u8fd8\u4e0d\u80fd\u6bd4\u62df\u5462\u3002\u4f60\u8bfb\u4e66\u6bd4\u6211\u591a\uff0c\u77e5\u9053\u7684\u4f8b\u5b50\u81ea\u7136\u6bd4\u6211 \u66f4\u591a\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u53e4\u4eba\u5982\u6b64\uff0c\u4eca\u4eba\u5982\u4f55\uff1f\u201d\u53f6\u6210\u6797\u7b11\u9053\uff1a\u201c\u60c5\u4e4b\u4e3a\u7269\uff0c\u53ea\u6015\u662f\u53e4\u4eca\u4e00 \u4f8b\u7684\u3002\u5f53\u7136\u53e4\u4eba\u4e2d\u6709\u771f\u60c5\u8584\u60c5\uff0c\u4eca\u4eba\u4e5f\u81ea\u662f\u6709\u771f\u60c5\u8584\u60c5\u7684\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u7136\u5219\u90a3\u662f\u56e0\u4eba\u800c \u522b\uff0c\u4e0d\u53ef\u4e00\u6982\u800c\u8bba\u4e86\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\uff0c\u81ea\u662f\u5f7c\u6b64\u76f8\u6295\uff0c\u65b9\u53ef\u4e24\u60c5\u76f8\u60a6\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u7565\u4e00\u51dd\u601d\uff0c\u5ffd\u5730\u53c8\u95ee\u9053\uff1a\u201c\u8bbe\u82e5\u662f\u4e00\u5bf9\u77e5\u5df1\uff0c\u56e0\u4e3a\u5076\u7136\u7684\u53d8\u6545\uff0c\u4eba\u5404\u4e00\u65b9\uff0c\u6d88\u606f \u8fdc\u9694\uff0c\u751a\u81f3\u4f55\u65f6\u76f8\u89c1\uff0c\u4ea6\u81ea\u65e0\u671f\uff0c\u4ed6\u4eec\u8be5\u4e0d\u8be5\u81f3\u6b7b\u4e0d\u53d8\u3002\u201d\u53f6\u6210\u6797\u6026\u7136\u5fc3\u8df3\uff0c\u4ed6\u54ea\u77e5\u4e8e\u627f\u73e0 \u95ee\u7684\u662f\u51cc\u4e91\u51e4\u7684\u4e8b\u60c5\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u539f\u6765\u94c1\u955c\u5fc3\u7adf\u4ee4\u5979\u5982\u6b64\u503e\u5fc3\uff0c\u5e78\u559c\u6211\u4e0d\u66fe\u5192\u6627\uff01\u201d\u6de1\u6de1 \u7b54\u9053\uff1a\u201c\u90a3\u4e0d\u662f\u8be5\u4e0d\u8be5\u7684\u95ee\u9898\uff0c\u90a3\u53ea\u662f\u60c5\u6df1\u60c5\u6d45\u7684\u95ee\u9898\u3002\u4f9d\u6211\u770b\u6765\uff0c\u65e2\u7136\u662f\u5f7c\u6b64\u4ee5\u77e5\u5df1\u76f8 \u8bb8\uff0c\u4ed6\u4eec\u5c31\u5fc5\u7136\u4f1a\u76f8\u5b88\u4e0d\u79fb\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u53c8\u95ee\u9053\uff1a\u201c\u8bbe\u82e5\u6709\u4e00\u65b9\u771f\u4e2a\u6b7b\u4e86\u5462\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u54ea\u6709\u8fd9\u6837\u8f7b\u6613\u4fbf\u6b7b\u4e86\u7684\u3002\u4f60 \u8bf4\u7684\u662f\u8c01\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u662f\u8ba8\u8bba\u3002\u53f6\u5927\u54e5\uff0c\u53e4\u793c\u8bf4\u5973\u5b50\u8be5\u4ece\u4e00\u800c\u7ec8\uff0c\u82e5\u662f\u672a\u66fe\u5a5a\u914d\uff0c\u76f8 \u7231\u7684\u4eba\u5148\u6b7b\u4e86\uff0c\u4e5f\u8be5\u4ece\u4e00\u800c\u7ec8\u4e48\uff1f\u201d\u53f6\u6210\u6797\u89c1\u5979\u95ee\u5f97\u8ba4\u771f\uff0c\u4e5f\u8ba4\u771f\u7b54\u9053\uff1a\u201c\u90a3\u81ea\u7136\u4e5f\u662f\u56e0\u4eba \u800c\u522b\u3002\u613f\u5b88\u4fbf\u5b88\uff0c\u4e0d\u613f\u5b88\u7684\u4fbf\u4e0d\u5b88\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f9d\u4f60\u4e4b\u89c1\uff0c\u662f\u5b88\u7684\u597d\uff1f\u8fd8\u662f\u4e0d\u5b88\u7684\u597d \u5462\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u8bbe\u82e5\u6211\u662f\u90a3\u4e2a\u6b7b\u4e86\u7684\u4eba\uff0c\u6211\u6b7b\u540e\u82e5\u6709\u77e5\u9053\uff0c\u5fc5\u613f\u6211\u5fc3\u7231\u7684\u4eba\u6211\u5230\u6bd4\u6211\u66f4 \u9002\u5f53\u7684\u4eba\uff0c\u514d\u5f97\u5979\u5b64\u82e6\u4f36\u6c40\uff0c\u51c4\u51c9\u8fc7\u4e16\u3002\u54a6\uff0c\u4f60\u4eca\u665a\u600e\u4e48\u95ee\u5f97\u8fd9\u6837\u5947\u602a\uff1f\u201d\u4e8e\u627f\u73e0\u62bf\u5634\u4e00 \u7b11\uff0c\u9053\uff1a\u201c\u591a\u8c22\u4f60\u901a\u60c5\u8fbe\u7406\u4e4b\u8a00\uff0c\u4ee4\u6211\u987f\u5f00\u8305\u585e\u3002\u662f\u554a\uff0c\u662f\u4e0d\u8be5\u8ba9\u5979\u90c1\u90c1\u5be1\u6b22\uff0c\u51c4\u51c9\u8fc7 \u4e16\uff01\u201d \u3000\u3000\u53f6\u6210\u6797\u8be7\u5f02\u4e4b\u6781\uff0c\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u5230\u5e95\u8bf4\u7684\u662f\u8c01\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u662f\u6211\u4e00\u4f4d\u77e5\u5fc3\u7684\u59d0 \u59b9\uff0c\u65e5\u540e\u4f60\u5c31\u77e5\u9053\u3002\u201d\u53f6\u6210\u6797\u4e0d\u559c\u7406\u4eba\u95f2\u4e8b\uff0c\u867d\u662f\u89c9\u5f97\u5947\u602a\uff0c\u542c\u8fc7\u4e5f\u5c31\u7b97\u4e86\u3002\u773c\u5149\u4e00\u77a5\uff0c\u4f46 \u89c1\u4e8e\u627f\u73e0\u9065\u671b\u8fdc\u65b9\uff0c\u5446\u5446\u51fa\u795e\uff0c\u4f3c\u662f\u6709\u51e0\u5206\u60b2\u4f24\uff0c\u53c8\u4f3c\u6709\u51e0\u5206\u559c\u60a6\uff0c\u826f\u4e45\uff0c\u826f\u4e45\uff0c\u59cb\u53f9\u53e3\u6c14 \u9053\uff1a\u201c\u8fd9\u91cc\u597d\u51b7\uff0c\u597d\u51b7\uff01\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u662f\u554a\uff0c\u8fd9\u91cc\u54ea\u6bd4\u5f97\u4e0a\u6606\u660e\u56db\u5b63\u5982\u6625\u3002\u201d\u4e8e\u627f\u73e0\u5ffd \u9053\uff1a\u201c\u4f60\u77a7\uff0c\u94c1\u3001\u94c1\u955c\u5fc3\u4ed6\u4f1a\u4e0d\u4f1a\u6765\uff1f\u201d\u8fd9\u8bdd\u539f\u662f\u53f6\u6210\u6797\u95ee\u8fc7\u5979\u7684\uff0c\u53f6\u6210\u6797\u8fd9\u65f6\u542c\u5979\u62ff\u6765\u53cd \u95ee\u81ea\u5df1\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u4e00\u9178\uff0c\u7b54\u9053\uff1a\u201c\u94c1\u516c\u5b50\u7684\u4e3a\u4eba\uff0c\u4f60\u6bd4\u6211\u66f4\u4e3a\u660e\u767d\u3002\u5440\uff0c\u8fd9\u91cc\u662f\u51b7\uff0c\u54b1\u4eec\u8be5 \u56de\u53bb\u5566\uff01\u201d\u4ed6\u54ea\u91cc\u77e5\u9053\u4e8e\u627f\u73e0\u53e6\u6709\u6240\u601d\uff0c\u53ea\u5f53\u5979\u5ff5\u5ff5\u4e0d\u5fd8\u94c1\u955c\u5fc3\uff1b\u4e8e\u627f\u73e0\u4f55\u7b49\u806a\u660e\uff0c\u542c\u4ed6\u8a00 \u8bed\u795e\u60c5\uff0c\u4e5f\u81ea\u77e5\u9053\u4ed6\u6709\u8fd9\u4e2a\u8bef\u4f1a\uff0c\u4f46\u8fd9\u65f6\u5979\u5374\u4e0d\u613f\u8fa9\u89e3\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\uff0c\u6f6e\u97f3\u548c\u5c1a\u5f97\u4e86\u97e9\u8001\u9556\u5934\u7684\u89e3\u836f\u4e4b\u540e\uff0c\u628a\u4e10\u5e2e\u53d7\u4f24\u7684\u4f17\u4eba\u6cbb\u597d\uff0c\u5bfb\u4e0a\u5c71\u6765\u3002\u51cc\u4e91 \u51e4\u4e0e\u5404\u5973\u5175\u5934\u76ee\u5546\u8bae\u5df2\u5b9a\uff0c\u62d4\u5be8\u540c\u884c\uff0c\u4e00\u9f50\u53bb\u6295\u4e49\u519b\u7684\u9996\u9886\u53f6\u5b97\u7559\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u7684\u4f24\u5fc3\u4e4b\u4e8b\uff0c\u9664\u4e86\u4e8e\u627f\u73e0\u4e4b\u5916\uff0c\u522b\u65e0\u4e00\u4eba\u77e5\u9053\uff0c\u800c\u51cc\u4e91\u51e4\u4e5f\u771f\u80fd\u514b\u5236\u81ea\u5df1\uff0c\u5e76\u4e0d \u5728\u4eba\u524d\u8868\u9732\u51fa\u6765\u3002\u4e00\u8def\u4e4b\u4e0a\uff0c\u4e8e\u627f\u73e0\u65f6\u65f6\u6545\u610f\u8ba9\u5979\u4e0e\u53f6\u6210\u6797\u540c\u884c\uff0c\u51cc\u3001\u53f6\u4e24\u4eba\u90fd\u662f\u6027\u60c5\u723d\u6717 \u7684\u4eba\uff0c\u6839\u672c\u5c31\u60f3\u4e0d\u5230\u4e8e\u627f\u73e0\u522b\u6709\u7528\u5fc3\uff0c\u5747\u662f\u8a00\u7b11\u81ea\u5982\uff0c\u80f8\u4e2d\u6beb\u65e0\u82a5\u8482\u3002\u4ed6\u4eec\u6307\u70b9\u5c71\u5ddd\uff0c\u8c08\u8bba \u5175\u6cd5\uff0c\u5012\u4e5f\u751a\u4e3a\u6295\u5408\u3002\u4e8e\u627f\u73e0\u6bcf\u5f53\u4ed6\u4eec\u5728\u4e00\u8d77\u65f6\uff0c\u5c31\u4f1a\u4e0d\u671f\u7136\u5730\u60f3\u8d77\u68a6\u4e2d\u7684\u60c5\u5883\uff0c\u4f46\u89c9\u53f6\u6210 \u6797\u548c\u51cc\u4e91\u51e4\u90fd\u662f\u50cf\u5927\u9752\u6811\u4e00\u6837\u7684\u4eba\uff0c\u8fd9\u6837\u4e00\u60f3\uff0c\u5fc3\u4e2d\u4fbf\u6d6e\u8d77\u559c\u60a6\uff0c\u4f46\u8fd9\u559c\u60a6\u5374\u53c8\u63a9\u76d6\u4e0d\u4f4f\u5185 \u5fc3\u6df1\u5904\u7684\u51c4\u51c9\u3002\u53ef\u601c\u4e8e\u627f\u73e0\u8fd9\u6837\u66f2\u6298\u7684\u513f\u5973\u5fc3\u4e8b\uff0c\u4e0d\u8981\u8bf4\u53f6\u6210\u6797\uff0c\u8fde\u51cc\u4e91\u51e4\u4e5f\u672a\u66fe\u7406\u89e3\u3002 \u3000\u3000\u534a\u6708\u4e4b\u540e\uff0c\u4ed6\u4eec\u6765\u5230\u6d59\u6c5f\u67d0\u5904\u7684\u4e49\u519b\u57fa\u5730\uff0c\u4e8e\u627f\u73e0\u56de\u9996\u524d\u5c18\uff0c\u4e0d\u80dc\u6005\u7136\u3002\u53f6\u6210\u6797\u7b11\u9053\uff1a \u201c\u4e0a\u6b21\u4f60\u5728\u53f0\u5dde\u4e4b\u65f6\uff0c\u4e49\u519b\u4e2d\u53ea\u6709\u4f60\u4e00\u4e2a\u5dfe\u5e3c\u82f1\u96c4\uff0c\u800c\u4eca\u6709\u4e86\u51cc\u5be8\u4e3b\u4e00\u5927\u5e2e\u4eba\uff0c\u4f60\u53ef\u4e0d\u5fc5\u8981 \u5973\u626e\u7537\u88c5\u4e86\u3002\u201d\u6b63\u8bf4\u7b11\u95f4\uff0c\u5ffd\u89c1\u6709\u4e00\u5f6a\u519b\u9a6c\u8fce\u9762\u800c\u6765\uff0c\u4e3a\u9996\u7684\u4e24\u4e2a\u7edf\u9886\u4e00\u7537\u4e00\u5973\uff0c\u6b63\u662f\u6210\u6d77 \u5c71\u548c\u77f3\u6587\u7ea8\u3002\u53f6\u6210\u6797\u5947\u9053\uff1a\u201c\u54a6\uff0c\u600e\u4e48\u4ed6\u4eec\u5c31\u63a5\u5230\u4e86\u4fe1\u606f\uff0c\u77e5\u9053\u54b1\u4eec\u4eca\u65e5\u6765\u5230\u5462\uff1f\u201d\u4ed6\u8fd8\u4ee5 \u4e3a\u662f\u6bd5\u64ce\u5929\u6d3e\u6765\u8fce\u63a5\u7684\u3002 \u3000\u3000\u77f3\u6587\u7ea8\u4e00\u773c\u5c31\u8ba4\u51fa\u4e86\u4e8e\u627f\u73e0\uff0c\u7eb5\u9a6c\u4e0a\u524d\uff0c\u6267\u624b\u76f8\u53d9\uff0c\u7b11\u9053\uff1a\u201c\u627f\u73e0\u59d0\u59d0\uff0c\u4f60\u56de\u590d\u672c\u6765\u9762 \u76ee\uff0c\u8d8a\u53d1\u663e\u5f97\u4fcf\u4e86\u3002\u53ef\u6709\u89c1\u7740\u6211\u7684\u94c1\u5927\u54e5\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\u3002\u4ed6\u73b0\u5728\u6606\u660e\u6c90\u56fd\u516c \u90a3\u91cc\u4eab\u798f\u5462\uff0c\u4f60\u4e0d\u5fc5\u6302\u5fc3\u3002\u4ee4\u5c0a\u5927\u4eba\u5462\uff1f\u201d\u77f3\u6587\u7ea8\u9053\uff1a\u201c\u6211\u7239\u7239\u81ea\u90a3\u665a\u95f9\u4e8b\u4e4b\u540e\uff0c\u4e00\u76f4\u6ca1\u6709 \u56de\u6765\u3002\u201d\u4e8e\u627f\u73e0\u9eef\u7136\u65e0\u8bed\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u89c1\u6210\u6d77\u5c71\u6b63\u5728\u6307\u624b\u5212\u811a\u5730\u548c\u53f6\u6210\u6797\u8bf4\u8bdd\uff0c\u8138\u4e0a\u4f3c\u6709\u6124 \u6124\u4e0d\u5e73\u7684\u795e\u8272\uff0c\u518d\u770b\u77f3\u6587\u7ea8\u65f6\uff0c\u89c1\u5979\u7709\u5b87\u4e4b\u95f4\uff0c\u4e5f\u4f3c\u6709\u9690\u5fe7\u3002\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u52a8\uff0c\u95ee\u77f3\u6587\u7ea8 \u9053\uff1a\u201c\u53f6\u7edf\u9886\u597d\u4e48\uff1f\u4f60\u4eec\u662f\u4e0d\u662f\u4ed6\u6d3e\u6765\u63a5\u5e94\u6211\u4eec\u7684\uff1f\u201d\u77f3\u6587\u7ea8\u9053\uff1a\u201c\u6211\u4eec\u662f\u88ab\u6bd5\u5927\u9f99\u5934\u6d3e\u9063 \u53bb\u6253\u4ed7\u7684\uff0c\u54fc\uff0c\u54fc\uff0c\u4e0d\u662f\u770b\u5728\u53f6\u7edf\u9886\u4efd\u4e0a\uff0c\u6211\u4eec\u624d\u4e0d\u670d\u4ed6\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u4f46\u89c1\u67d0\u96c4\u56fe\u9738\u4e1a\uff0c\u5374\u6559\u519b\u65c5\u8d77\u98ce\u6ce2\u3002\u6b32\u77e5\u540e\u4eab\u5982\u4f55\uff0c\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u9690\u60a3\u6f5c\u57cb\u3000\u91ce\u5fc3\u56fe\u9738\u4e3b\u3000\u4f0f\u5175\u7a81\u53d1\u3000\u6d6e\u6d77\u8d70\u82f1\u8c6a \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u9690\u60a3\u6f5c\u57cb\u3000\u91ce\u5fc3\u56fe\u9738\u4e3b\u3000\u4f0f\u5175\u7a81\u53d1\u3000\u6d6e\u6d77\u8d70\u82f1\u8c6a \u3000\u3000\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u4e49\u5e08\u65e2\u8d77\uff0c\u6253\u4ed7\u600e\u4e48\u80fd\u514d\uff1f\u201d\u77f3\u6587\u7ea8\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u662f\u6015\u6253\u4ed7\uff0c\u53ea\u662f\u6253\u4ed7 \u7684\u5730\u65b9\u9009\u5f97\u4e0d\u5bf9\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u6210\u6d77\u5c71\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u652f\u5b50\u5f1f\u5175\u672c\u6765\u90fd\u662f\u6ee8\u6d77\u7684\u6e14 \u6c11\uff0c\u4ece\u53f0\u5dde\u8c03\u5230\u6e29\u5dde\u5b88\u5907\uff0c\u4e00\u5e74\u591a\u6765\uff0c\u5728\u6c34\u4e0a\u4e0e\u5b98\u519b\u4ea4\u950b\uff0c\u6218\u65e0\u4e0d\u80dc\uff0c\u5982\u4eca\u5ffd\u7136\u8981\u8c03\u5230\u5c71\u5730 \u53bb\u4f5c\u6218\uff0c\u800c\u4e14\u662f\u5b64\u519b\u6df1\u5165\uff0c\u5949\u547d\u53bb\u653b\u7565\u6c5f\u897f\u7684\u4e0a\u9976\uff0c\u8fd9\u5c82\u4e0d\u662f\u7528\u975e\u5176\u6750\uff0c\u800c\u4e14\u72af\u4e86\u5175\u6cd5\u7684\u5927 \u5fcc\u5417\uff1f\u201d\u77f3\u6587\u7ea8\u9053\uff1a\u201c\u800c\u4e14\u54b1\u4eec\u8fd9\u652f\u6e14\u6c11\u5b50\u5f1f\u519b\u8c03\u79bb\u672c\u571f\u4e4b\u540e\uff0c\u6e29\u5dde\u95e8\u6237\u6d1e\u5f00\uff0c\u5b98\u519b\u82e5\u4ece\u6d77 \u4e0a\u6765\u653b\uff0c\u5b9e\u662f\u5371\u9669\u3002\u201d\u53f6\u6210\u6797\u76b1\u4e86\u7709\u5934\uff0c\u9053\uff1a\u201c\u6bd5\u64ce\u5929\u9887\u901a\u5175\u6cd5\uff0c\u4ed6\u6614\u5e74\u5728\u5c71\u4e1c\u4e3a\u76d7\uff0c\u548c\u5b98 \u519b\u5927\u6218\u5c0f\u6218\uff0c\u4e5f\u4e0d\u4e0b\u767e\u6b21\u4e4b\u591a\uff0c\u600e\u7684\u5982\u6b64\u8c03\u5ea6\uff1f\u4f60\u4eec\u8ddf\u6211\u53d4\u53d4\u8bf4\u4e86\u6ca1\u6709\u4e86\u3002\u201d\u6210\u6d77\u5c71\u9053\uff1a \u201c\u8bf4\u5566\u3002\u53ef\u662f\u6bd5\u64ce\u5929\u4e0b\u4e86\u5c06\u4ee4\uff0c\u4e0d\u80af\u6536\u53e3\uff0c\u53f6\u7edf\u9886\u548c\u4ed6\u4e89\u6267\u4e86\u4e24\u56de\uff0c\u7ec8\u4e8e\u8fd8\u662f\u529d\u6211\u4eec\u987a\u4ece\u4ed6 \u7684\u610f\u601d\uff0c\u514d\u5f97\u4f24\u4e86\u548c\u6c14\u3002\u53f6\u5927\u54e5\uff0c\u4f60\u8fd9\u6b21\u56de\u6765\uff0c\u62dc\u6258\u4f60\u518d\u53bb\u8bf4\u8bf4\uff0c\u5144\u5f1f\u4eec\u5b9e\u662f\u4e0d\u60f3\u79bb\u5f00\u6545 \u4e61\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u597d\uff0c\u6211\u8fd9\u5c31\u53bb\u89c1\u6bd5\u64ce\u5929\u53bb\u3002\u4e0d\u8fc7\u5c06\u4ee4\u65e2\u4e0b\uff0c\u519b\u4e2d\u6700\u8bb2\u7a76\u7684\u662f\u7eaa\u5f8b\u4e25\u660e\uff0c \u4f60\u4eec\u8fd8\u662f\u4f9d\u65e7\u884c\u519b\u3002\u82e5\u7136\u6211\u529d\u5f97\u6bd5\u64ce\u5929\u6536\u56de\u5c06\u4ee4\uff0c\u90a3\u65f6\u518d\u7528\u5feb\u9a6c\u5c06\u4f60\u4eec\u8bf7\u56de\u4fbf\u662f\u4e86\u3002\u201d \u3000\u3000\u53f6\u6210\u6797\u548c\u4e8e\u627f\u73e0\u7b49\u4e00\u884c\u4eba\u56de\u5230\u5927\u8425\uff0c\u6bd5\u64ce\u5929\u548c\u53f6\u5b97\u7559\u6b63\u5728\u5927\u8425\u8bae\u4e8b\uff0c\u542c\u5f97\u6d88\u606f\uff0c\u8fce\u4e86\u51fa \u6765\u3002\u4e00\u89c1\u53f6\u6210\u6797\u4fbf\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u8f9b\u82e6\u4f60\u5566\uff01\u5e10\u4e2d\u6b47\u6b47\u53bb\u3002\u54c8\uff0c\u4e8e\u59d1\u5a18\uff0c\u4f60\u4e5f\u56de\u6765 \u5566\uff0c\u6211\u6b63\u60f3\u5efa\u7acb\u4e00\u961f\u5973\u519b\uff0c\u4f60\u56de\u6765\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u4e86\u3002\u201d\u773c\u5149\u4e00\u77a5\uff0c\u767d\u5b5f\u5ddd\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6c5f\u897f \u8299\u84c9\u5c71\u7684\u51cc\u4e91\u51e4\u51cc\u5be8\u4e3b\u3002\u201d\u6bd5\u64ce\u5929\u62f1\u624b\u9053\uff1a\u201c\u4e45\u4ef0\u4e86\uff01\u201d\u51cc\u4e91\u51e4\u7eb5\u58f0\u957f\u7b11\uff0c\u4ef0\u5934\u8bf4\u9053\uff1a\u201c\u6211 \u4e5f\u4e45\u4ef0\u5566\uff0c\u4f60\u6d3e\u4eba\u52ab\u97e9\u632f\u7fbd\u7684\u9556\uff0c\u6240\u7528\u7684\u624b\u6bb5\u4e4b\u5999\uff0c\u53ef\u771f\u4eca\u6211\u60f3\u4e0d\u5230\u662f\u53f7\u79f0\u5929\u4e0b\u5341\u516b\u7701\u5927\u9f99 \u5934\u5e72\u7684\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u4e00\u884c\u4eba\u5df2\u8fdb\u5165\u5927\u8425\u5e05\u5e10\uff0c\u53f6\u5b97\u7559\u95fb\u58f0\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u8c01\u52ab\u97e9\u632f\u7fbd\u7684\u9556\uff1f\u201d\u6bd5 \u64ce\u5929\u9762\u8272\u4e00\u53d8\uff0c\u968f\u5373\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u662f\u6211\u6d3e\u4eba\u52ab\u7684\uff0c\u8fd9\u652f\u9556\u662f\u6e56\u5317\u5b98\u519b\u7684\u519b\u9977\uff0c\u55ef\uff0c\u613f\u7a77\uff0c\u8fd9 \u652f\u9556\u52ab\u6765\u4e86\u6ca1\u6709\uff1f\u201d\u53f6\u6210\u6797\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6bd5\u5927\u9f99\u5934\uff0c\u5c0f\u5f1f\u7279\u6765\u8bf7\u7f6a\uff01\u201d\u6bd5\u64ce\u5929\u53cc\u773c\u4e00\u7ffb\uff0c \u9053\uff1a\u201c\u8bf7\u4ec0\u4e48\u7f6a\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u662f\u5c0f\u5f1f\u5c06\u8fd9\u652f\u9556\u653e\u4e86\u3002\u60f3\u90a3\u5341\u4e07\u5b98\u519b\uff0c\u82e5\u65e0\u7cae\u9977\uff0c\u5fc5\u7136\u4e3a \u7978\u767e\u59d3\uff0c\u54b1\u4eec\u65e2\u53f7\u79f0\u4e49\u519b\uff0c\u5c82\u53ef\u4e0d\u62e9\u624b\u6bb5\u3002\u201d\u6bd5\u64ce\u5929\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5012\u662f\u4ec1\u4e49\u4e3a\u6000\uff01\u201d\u53f6\u5b97\u7559 \u9053\uff1a\u201c\u6210\u6797\u8bf4\u5f97\u4e5f\u6709\u9053\u7406\uff0c\u54b1\u4eec\u90fd\u662f\u8001\u767e\u59d3\u51fa\u8eab\uff0c\u4e3a\u8001\u767e\u59d3\u6253\u4ed7\uff0c\u662f\u8be5\u5148\u987e\u5ff5\u767e\u59d3\u3002\u542c\u8bf4\u90a3 \u97e9\u8001\u9556\u5934\uff0c\u4e5f\u662f\u4e00\u4f4d\u8840\u6027\u6c49\u5b50\uff0c\u7d2f\u4ed6\u8d54\u4e86\u8eab\u5bb6\u6027\u547d\uff0c\u6211\u4e5f\u4e8e\u5fc3\u4e0d\u5fcd\u3002\u201d\u6bd5\u64ce\u5929\u9762\u8272\u4e00\u6c89\uff0c\u968f \u5373\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u4f60\u82f1\u96c4\u5e74\u5c11\uff0c\u773c\u5149\u8fdc\u5927\uff0c\u4ffa\u597d\u751f\u4f69\u670d\u3002\u52ab\u9556\u4e4b\u4e8b\uff0c\u6211\u601d\u8651\u4e0d\u5468\uff0c\u65e2 \u7136\u653e\u4e86\uff0c\u90a3\u5c31\u7b97\u5566\u3002\u4f60\u8fd9\u6b21\u524d\u5f80\u5927\u7406\uff0c\u89c1\u4e86\u5f20\u4e39\u67ab\u6ca1\u6709\uff1f\u4ed6\u6709\u4ec0\u4e48\u8bf4\u8bdd\uff0c\u90a3\u5730\u56fe\u5462\uff0c\u53ef\u53d6\u6765 \u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u53f6\u6210\u6797\u9053\uff1a\u201c\u5f20\u5927\u4fa0\u95ee\u5019\u53d4\u53d4\uff0c\u5730\u56fe\u5df2\u7ecf\u5e26\u6765\u4e86\u3002\u201d\u6bd5\u64ce\u5929\u542c\u5f97\u5f20\u4e39\u67ab\u53ea\u5411\u53f6\u5b97\u7559\u81f4 \u610f\uff0c\u5fc3\u4e2d\u5df2\u6709\u51e0\u5206\u4e0d\u5feb\uff0c\u4e00\u89c1\u53f6\u6210\u6797\u53d6\u51fa\u5730\u56fe\uff0c\u614c\u5fd9\u4f38\u624b\u53bb\u63a5\uff0c\u5ffd\u542c\u5f97\u4e8e\u627f\u73e0\u53eb\u9053\uff1a\u201c\u6211\u5e08 \u7236\u8fd9\u5e45\u5730\u56fe\u662f\u4ea4\u7ed9\u53f6\u7edf\u9886\u7684\u3002\u201d\u53f6\u6210\u6797\u6014\u4e86\u4e00\u6014\uff0c\u8f6c\u8fc7\u8138\u6765\uff0c\u53cc\u624b\u6367\u7ed9\u53f6\u5b97\u7559\u3002\u6bd5\u64ce\u5929\u6c14\u5f97 \u8138\u76ae\u7d2b\u6da8\uff0c\u4fbf\u60f3\u53d1\u4f5c\uff0c\u53f6\u5b97\u7559\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6bd5\u8001\u5f1f\uff0c\u4f60\u6536\u7740\u5427\u3002\u201d\u8f6c\u624b\u5c31\u4ea4\u7ed9\u4e86\u6bd5\u64ce \u5929\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u6253\u5f00\u4e00\u770b\uff0c\u9053\uff1a\u201c\u600e\u4e48\u53ea\u662f\u6c5f\u5357\u4e94\u7701\u7684\u5730\u56fe\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u5f20\u5927\u4fa0\u7684\u610f\u601d\uff0c\u53eb \u6211\u4eec\u4e0d\u5fc5\u6025\u4e8e\u8fdb\u53d6\uff0c\u80fd\u591f\u5148\u4fdd\u4f4f\u6c5f\u5357\u7684\u5730\u76d8\uff0c\u4e0e\u8001\u767e\u59d3\u4f11\u517b\u751f\u606f\uff0c\u90a3\u4fbf\u7acb\u4e0b\u4e86\u4e0d\u8d25\u4e4b\u57fa\u3002\u201d \u6bd5\u64ce\u5929\u9762\u8272\u4e00\u6c89\uff0c\u521a\u6b32\u53d1\u8bdd\uff0c\u53ea\u542c\u5f97\u53f6\u6210\u6797\u53c8\u9053\uff1a\u201c\u6211\u9002\u624d\u5728\u5927\u8425\u5916\u78b0\u5230\u4e86\u6210\u6d77\u5c71\uff0c\u542c\u8bf4\u6bd5 \u5927\u9f99\u5934\u8c03\u4ed6\u53bb\u6253\u4e0a\u9976\uff1f\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u600e\u4e48\u4e86\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u6210\u6d77\u5c71\u8fd9\u652f\u5b50\u5f1f\u5175\u4e60\u4e8e\u6c34 \u6218\uff0c\u8c03\u5230\u5c71\u5730\uff0c\u6050\u4e0d\u9002\u5b9c\u3002\u518d\u8005\u7167\u5f20\u5927\u4fa0\u7684\u770b\u6cd5\uff0c\u5de9\u56fa\u6c5f\u5357\u4e43\u662f\u4e0a\u7b56\uff0c\u5206\u5175\u63a0\u5730\uff0c\u53ea\u6015\u53cd\u4e3a \u5b98\u519b\u6240\u4e58\u3002\u201d \u3000\u3000\u6bd5\u64ce\u5929\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5f20\u5927\u4fa0\uff0c\u5f20\u5927\u4fa0\uff01\u8fd9\u5927\u9f99\u5934\u7684\u4f4d\u5b50\u53ef\u4e0d\u662f\u5f20\u4e39\u67ab\u5728 \u5750\uff01\u201d\u4e8e\u627f\u73e0\u6012\u9053\uff1a\u201c\u6bd5\u64ce\u5929\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u6bd5\u64ce\u5929\u6a2a\u4e86\u4e8e\u627f\u73e0\u4e00\u773c\uff0c\u773c\u5149\u4e00\u8f6c\uff0c\u76ef\u7740\u53f6\u6210 \u6797\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u6709\u90a3\u4e48\u591a\u7684\u610f\u89c1\uff0c\u4f55\u4ee5\u4ed6\u81ea\u5df1\u4e0d\u6765\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u5f20\u5927\u4fa0\u4ed6\u62a4\u9001\u6ce2\u65af\u516c\u4e3b \u8fdb\u4eac\u53bb\u4e86\u3002\u201d\u6bd5\u64ce\u5929\u51b7\u7b11\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u5728\u5341\u5e74\u4e4b\u524d\uff0c\u4ece\u74e6\u523a\u5c06\u7687\u5e1d\u8001\u513f\u8fce\u63a5\u56de\u671d\uff0c\u5982\u4eca\u53c8\u5165 \u4eac\u9762\u5723\uff0c\u54c8\uff0c\u529f\u540d\u5bcc\u8d35\uff0c\u53ef\u5c11\u4e0d\u4e86\u4ed6\u7684\u4efd\u513f\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u52c3\u7136\u5927\u6012\uff0c\u6309\u5251\u65a5\u9053\uff1a\u201c\u6211\u5e08\u7236\u82e5\u60f3\u529f\u540d\u5bcc\u8d35\uff0c\u8fd9\u5927\u660e\u6c5f\u5c71\u65e9\u59d3\u4e86\u5f20\uff0c\u54ea\u8f6e\u5230\u4f60 \u59d3\u6bd5\u7684\u67d3\u6307\u3002\u201d\u53f6\u5b97\u7559\u6025\u5fd9\u529d\u9053\uff1a\u201c\u5f20\u5927\u4fa0\u5929\u4e0b\u540c\u94a6\uff0c\u81ea\u7136\u4e0d\u662f\u8d2a\u56fe\u5bcc\u8d35\u4e4b\u4eba\u3002\u4e8e\u59d1\u5a18\uff0c\u4f60 \u7684\u706b\u6c14\u4e5f\u5927\u4e86\u4e00\u70b9\u3002\u201d\u6bd5\u64ce\u5929\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u6211\u5c82\u80fd\u4e0e\u5979\u8ba1\u8f83\uff1f\u201d\u4e8e\u627f\u73e0\u6c14 \u70b8\u5fc3\u80ba\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u6bd5\u64ce\u5929\u5bf9\u81ea\u5df1\u66fe\u6709\u846c\u7236\u4e4b\u6069\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u770b\u5728\u8fd9\u4e2a\u60c5\u4efd\uff0c\u6211\u8fd8\u662f\u6743 \u4e14\u4e0d\u4e0e\u8fd9\u53ae\u8ba1\u8f83\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u6bd5\u64ce\u5929\u7eed\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u81ea\u662f\u4e00\u4e2a\u4eba\u624d\uff0c\u4f46\u4ed6\u8fdc\u5728\u6ec7\u5357\uff0c\u600e\u77e5\u8fd9\u91cc\u519b\u4e2d\u4e4b\u4e8b\uff1f\u671d\u5ef7 \u5b98\u519b\uff0c\u767e\u500d\u4e8e\u6211\uff0c\u82e5\u975e\u653b\u57ce\u63a0\u5730\uff0c\u5148\u6253\u4ed6\u51e0\u4e2a\u80dc\u4ed7\uff0c\u600e\u80fd\u632f\u594b\u6c11\u5fc3\uff1f\u600e\u80fd\u4ee4\u5929\u4e0b\u54cd\u5e94\uff1f\u6211\u6d3e \u6210\u6d77\u5c71\u53bb\u6253\u4e0a\u9976\uff0c\u5c31\u662f\u60f3\u4ee5\u653b\u4e3a\u5b88\uff0c\u7275\u5236\u5f3a\u654c\u3002\u4e3a\u5c06\u4e4b\u9053\uff0c\u5e94\u5f53\u65e2\u4e60\u6c34\u6218\uff0c\u4ea6\u4e60\u9646\u6218\uff0c\u4e0d\u61c2 \u5c31\u5b66\uff0c\u600e\u53ef\u4ee5\u53ea\u5728\u6d77\u4e0a\u79f0\u96c4\u3002\u201d \u3000\u3000\u53f6\u6210\u6797\u672c\u60f3\u9a73\u4ed6\uff0c\u4f46\u89c1\u4ed6\u4f3c\u662f\u52a8\u4e86\u771f\u6c14\uff0c\u6682\u4e14\u5fcd\u4f4f\u3002\u53f6\u5b97\u7559\u5fae\u7b11\u9053\uff1a\u201c\u51b3\u8c0b\u5b9a\u7b56\uff0c\u54b1\u662f \u4e00\u4e2a\u8001\u7c97\uff0c\u8bf4\u4e0d\u4e0a\u6765\u3002\u53ef\u662f\u542c\u4e86\u5f20\u5927\u4fa0\u548c\u6bd5\u8001\u5f1f\u6240\u8bf4\uff0c\u4e24\u8fb9\u90fd\u6709\u70b9\u9053\u7406\u3002\u8fc7\u51e0\u5929\u54b1\u4eec\u8bf7\u5168\u519b \u5c06\u58eb\uff0c\u5404\u6292\u5df1\u89c1\uff0c\u4fd7\u8bed\u9053\uff1a\u2018\u4e00\u4eba\u8ba1\u77ed\uff0c\u4e24\u4eba\u8ba1\u957f\u2019\uff0c\u603b\u4e4b\u5927\u5bb6\u5546\u91cf\u4e00\u4e2a\u597d\u529e\u6cd5\u6765\u3002\u201d\u53f6\u5b97 \u7559\u8fd9\u4e00\u8c03\u505c\uff0c\u7ed9\u6bd5\u64ce\u5929\u633d\u56de\u4e86\u9762\u5b50\uff0c\u4f46\u8c03\u56de\u6210\u6d77\u5c71\u4e4b\u4e8b\uff0c\u4e5f\u53ea\u597d\u4f5c\u4e86\u7f62\u8bba\u3002\u8fd9\u4e00\u665a\u7684\u63a5\u98ce \u9152\uff0c\u5927\u5bb6\u90fd\u5403\u5f97\u6781\u4e0d\u75db\u5feb\uff01 \u3000\u3000\u8fc7\u4e86\u51e0\u5929\uff0c\u6bd5\u64ce\u5929\u53c8\u8c03\u4e86\u4e24\u652f\u519b\u961f\u51fa\u5916\u4f5c\u6218\uff0c\u8fd9\u4e24\u652f\u519b\u961f\u90fd\u662f\u8ddf\u968f\u53f6\u5b97\u7559\u591a\u5e74\u7684\u90e8\u5c5e\uff0c \u51cc\u4e91\u51e4\u6709\u4e00\u65e5\u5bf9\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u4e8b\u60c5\u6709\u70b9\u5947\u602a\uff0c\u600e\u4e48\u603b\u662f\u628a\u53f6\u7edf\u9886\u7684\u4eba\u8c03\u8d70\uff1f\u201d\u4e8e\u627f\u73e0\u5fc3\u5934 \u4e5f\u8499\u4e86\u4e00\u5c42\u9634\u5f71\uff0c\u4f46\u5fc3\u60f3\u6bd5\u64ce\u5929\u6216\u8005\u662f\u597d\u5927\u559c\u529f\uff0c\u519b\u4e2d\u4e5f\u4e0d\u5e94\u5206\u5f00\u5f7c\u6b64\uff0c\u867d\u7136\u611f\u5230\u6709\u70b9\u5947 \u602a\uff0c\u5374\u4e5f\u4e0d\u4fbf\u591a\u7591\u3002 \u3000\u3000\u5e78\u559c\u90a3\u51e0\u652f\u519b\u961f\u90fd\u6253\u5f97\u5f88\u51fa\u8272\uff0c\u5b98\u519b\u88ab\u6297\u62d2\u5728\u4ed9\u971e\u5cad\u5916\uff0c\u6c5f\u6d59\u4e24\u7701\u548c\u798f\u5efa\u5317\u90e8\u88ab\u4e49\u519b\u5360 \u9886\u7684\u5730\u65b9\uff0c\u4e00\u7247\u592a\u5e73\u666f\u8c61\uff0c\u6bd5\u64ce\u5929\u4e09\u65e5\u4e94\u65e5\u6446\u9152\u5e86\u8d3a\uff0c\u5404\u5730\u524d\u6765\u6295\u6548\u7684\u7eff\u6797\uff0c\u5bf9\u4ed6\u66f4\u662f\u4e00\u7247 \u9882\u8d5e\u4e4b\u58f0\uff0c\u5012\u628a\u4ed6\u5f04\u5f97\u6709\u70b9\u98d8\u98d8\u7136\u4e86\u3002 \u3000\u3000\u8f6c\u773c\u6625\u6696\u82b1\u5f00\uff0c\u6625\u98ce\u89e3\u51bb\u3002\u6e56\u5317\u90a3\u5341\u4e07\u5b98\u519b\u6709\u4e86\u7cae\u9977\uff0c\u679c\u7136\u517c\u7a0b\u4e1c\u4e0b\uff0c\u524d\u950b\u5230\u4e86\u5c6f\u6eaa\u3002 \u6bd5\u64ce\u5929\u4ee5\u53f6\u6210\u6797\u6709\u8a00\u5728\u5148\uff0c\u4fbf\u8c03\u53f6\u6210\u6797\u7edf\u519b\u4e00\u4e07\uff0c\u524d\u5f80\u62b5\u6321\u3002\u8fd9\u4e00\u4e07\u4eba\u53c8\u662f\u53f6\u5b97\u7559\u7684\u90e8\u961f\uff0c \u81f3\u6b64\u53f6\u5b97\u7559\u591a\u5e74\u5fc3\u8840\u8bad\u7ec3\u7684\u7cbe\u5175\uff0c\u51e0\u4e4e\u5df2\u88ab\u62bd\u8c03\u4e00\u7a7a\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u662f\u53f6\u6210\u6797\u5927\u519b\u51fa\u53d1\u4e4b\u65e5\uff0c\u6bd5\u64ce\u5929\u548c\u4e8e\u627f\u73e0\u3001\u51cc\u4e91\u51e4\u90fd\u524d\u5f80\u9001\u884c\uff0c\u9001\u51fa\u4e94\u671b\u4e4b\u5916\uff0c \u53f6\u6210\u6797\u8bf7\u6bd5\u64ce\u5929\u56de\u9a6c\uff0c\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u6211\u9759\u5f85\u8d24\u5f1f\u597d\u97f3\uff0c\u8fd9\u6b21\u654c\u4f17\u6211\u5be1\uff0c\u5168\u4ed7\u543e\u5f1f\u65bd\u5c55\u5c06\u624d \u4e86\u3002\u5f85\u5404\u8def\u4e49\u519b\u9f50\u96c6\u540e\uff0c\u6211\u5b9a\u5f53\u518d\u7ed9\u8d24\u5f1f\u589e\u5175\u52a9\u6218\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u8fd9\u91cc\u57fa\u4e1a\u91cd\u5730\uff0c\u9632\u5907\u76f8 \u5f53\u575a\u56fa\u3002\u7ed9\u6211\u589e\u5175\uff0c\u5012\u53ef\u4e0d\u5fc5\u3002\u53ea\u662f\u654c\u4f17\u6211\u5be1\uff0c\u6211\u8fd9\u6b21\u524d\u5f80\uff0c\u4e0d\u62df\u4e0e\u5b98\u519b\u5373\u884c\u51b3\u6218\uff0c\u51c6\u5907\u5360 \u7740\u5730\u5f62\uff0c\u5148\u56fe\u56fa\u5b88\uff0c\u6d88\u5176\u9510\u6c14\uff0c\u51fb\u5176\u66ae\u5f52\uff0c\u5b98\u519b\u867d\u4f17\uff0c\u6597\u5fd7\u4e0d\u5f3a\uff0c\u5047\u4ee5\u65f6\u65e5\uff0c\u53ef\u4ee5\u74e6\u89e3\u3002\u201d \u6bd5\u64ce\u5929\u62cd\u624b\u8d5e\u9053\uff1a\u201c\u8d24\u5f1f\u9ad8\u89c1\uff01\u8fd9\u4e00\u4ed7\u4e00\u5b9a\u6253\u80dc\u4e86\uff01\u4ed6\u65e5\u6210\u529f\uff0c\u6211\u5b9a\u5f53\u5c01\u8d24\u5f1f\u505a\u4e00\u5b57\u5e76\u80a9 \u738b\uff01\u201d\u53f6\u6210\u6797\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u5c82\u662f\u56fe\u4ec0\u4e48\u5c01\u738b\u5c01\u7235\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u6bd5\u64ce\u5929\u5c31\u622a\u4f4f\u8bf4 \u9053\uff1a\u201c\u5bf9\uff0c\u54b1\u4eec\u662f\u4e3a\u6551\u6c11\u4e8e\u6c34\u706b\u4e4b\u4e2d\u3002\u201d\u8fd9\u8bdd\u82e5\u8ba9\u53f6\u6210\u6797\u8bf4\u6765\uff0c\u90a3\u662f\u81ea\u7136\u4e0d\u8fc7\uff0c\u5728\u6bd5\u64ce\u5929\u53e3 \u4e2d\u9053\u51fa\uff0c\u51cc\u4e91\u51e4\u548c\u4e8e\u627f\u73e0\u90fd\u89c9\u5f97\u6709\u70b9\u523a\u8033\uff0c\u8a00\u4e0d\u7531\u8877\u3002 \u3000\u3000\u53f6\u6210\u6797\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u6bd5\u5927\u54e5\u8bf7\u56de\uff0c\u5c0f\u5f1f\u4e0d\u987b\u6dfb\u5175\uff0c\u53ea\u6709\u4e00\u4e8b\u8bf7\u6258\u3002\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u8bf7 \u8bf4\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u8fd9\u4e00\u6218\u53ea\u6015\u4e0d\u662f\u77ed\u671f\u6240\u80fd\u7ed3\u675f\uff0c\u519b\u7cae\u63a5\u6d4e\uff0c\u52a1\u8bf7\u4f9d\u65f6\u3002\u201d\u6bd5\u64ce\u5929\u5927\u7b11 \u9053\uff1a\u201c\u6b64\u4e8b\u4f55\u52b3\u5631\u5490\uff0c\u4e09\u519b\u672a\u52a8\uff0c\u7cae\u8349\u5148\u884c\u3002\u60f3\u8d24\u5f1f\u5bf9\u5b98\u519b\u7684\u7cae\u9977\u5c1a\u4e14\u653e\u884c\uff0c\u96be\u9053\u6211\u8fd8\u4f1a\u6263 \u4f4f\u4f60\u7684\u7cae\u8349\u4e0d\u53d1\u4e0d\u6210\u3002\u201d\u5f53\u4e0b\u4e0e\u53f6\u6210\u6797\u626c\u97ad\u9053\u522b\u3002\u4e8e\u627f\u73e0\u5fc3\u5ff5\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u51cc\u59d0\u59d0\uff0c\u6211\u4e0e\u4f60 \u518d\u9001\u4e00\u7a0b\u5427\u3002\u201d\u51cc\u4e91\u51e4\u4e0e\u4e8e\u627f\u73e0\u5e76\u9a6c\u8d70\u4e86\u4e00\u9635\uff0c\u5ffd\u9053\uff1a\u201c\u5440\uff0c\u6211\u8fd8\u6709\u70b9\u4e8b\u60c5\uff0c\u4f60\u518d\u9001\u4e00 \u7a0b\u3002\u201d\u4e8e\u627f\u73e0\u9762\u4e0a\u4e00\u7ea2\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u4ecd\u7136\u7b56\u9a6c\u9001\u884c\u3002 \u3000\u3000\u76f4\u9001\u51fa\u5341\u91cc\u4e4b\u5916\uff0c\u53f6\u6210\u6797\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\u8bf7\u56de\u5427\u3002\u201d\u4e8e\u627f\u73e0\u89c1\u4ed6\u795e\u60c5\u6de1\u6f20\uff0c\u5fc3\u5185\u5fae\u9178\uff0c\u4f46 \u53c8\u89c9\u5f97\u8fd9\u6b63\u662f\u81ea\u5df1\u6240\u76fc\u671b\u7684\u4e8b\uff0c\u53ea\u53ef\u60dc\u51cc\u4e91\u51e4\u4e0d\u5728\u8fd9\u513f\uff0c\u53f6\u6210\u6797\u4e5f\u4f3c\u4e0d\u89e3\u81ea\u5df1\u7684\u5fc3\u610f\u3002\u53f6\u6210 \u6797\u9a7b\u9a6c\u8bf4\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\u6709\u4f55\u8bdd\u8bf4\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u53f6\u5927\u54e5\u4f60\u6b64\u53bb\u53ef\u8981\u5f53\u5fc3\u3002\u201d \u3000\u3000\u53f6\u6210\u6797\u9053\uff1a\u201c\u591a\u8c22\u4f60\u5173\u5fc3\u4e86\u3002\u6211\u4f1a\u6599\u5230\uff0c\u6bcb\u52b3\u4f60\u6302\u5ff5\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff0c \u6211\u53ea\u6015\u2026\u2026\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u6015\u4ec0\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u770b\u6bd5\u64ce\u5929\u8fd9\u4eba\u5982\u4f55\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a \u201c\u600e\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6bd5\u64ce\u5929\u8fd9\u4eba\u91ce\u5fc3\u6781\u5927\u3002\u4e00\u5c71\u51c6\u85cf\u4e8c\u864e\uff0c\u6211\u53ea\u6015\u4ed6\u5992\u5fcc\u4f60\u4eec\u53d4\u4f84\u3002\u201d \u53f6\u6210\u6797\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u81f3\u4e8e\u5427\uff0c\u6211\u53c8\u4e0d\u4e0e\u4ed6\u4e89\u4f4d\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd8\u662f\u5c0f\u5fc3\u4e3a\u5999\u3002\u63d0\u9632\u4ed6\u5f04\u4ec0 \u4e48\u8be1\u8ba1\u3002\u6bd4\u5982\u7cae\u8349\u4e4b\u4e8b\u2026\u2026\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u6211\u4e5f\u7b79\u5212\u597d\u4e86\u3002\u82e5\u7136\u4ed6\u4e0d\u8fd0\u6765\uff0c\u6211\u5c31\u5728\u5f53\u5730\u81ea \u7b79\uff0c\u60f3\u54b1\u4eec\u82e5\u662f\u4e00\u5fc3\u4e3a\u7740\u767e\u59d3\uff0c\u767e\u59d3\u65ad\u4e0d\u4f1a\u53eb\u54b1\u4eec\u997f\u7740\u809a\u5b50\u6253\u4ed7\u3002\u54b1\u4eec\u90fd\u662f\u4e00\u5bb6\u4eba\uff0c\u6211\u5012\u529d \u4f60\u4e0d\u8981\u592a\u591a\u7591\uff0c\u5c24\u5176\u4e0d\u53ef\u9732\u4e8e\u795e\u8272\uff0c\u514d\u5f97\u4e0e\u4ed6\u4f24\u4e86\u548c\u6c14\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u4e2d\u6697\u53f9\uff0c\u60f3\u9053\uff1a\u201c\u4e16\u95f4\u53ea\u6015\u4e0d\u5c3d\u662f\u50cf\u4f60\u4eec\u53d4\u4f84\u8fd9\u822c\u7684\u597d\u4eba\u3002\u201d\u65e0\u53ef\u5948\u4f55\uff01\u4ea6\u4e0d \u518d\u8bf4\uff0c\u53ea\u597d\u4e0e\u53f6\u6210\u6797\u9053\u522b\u3002\u62e8\u9a6c\u56de\u5934\uff0c\u795e\u601d\u56f0\u5026\uff0c\u8d70\u4e86\u4e00\u9635\uff0c\u5ffd\u542c\u5f97\u9a6c\u94c3\u58f0\u54cd\uff0c\u539f\u6765\u662f\u6bd5\u64ce \u5929\u8fce\u9762\u800c\u6765\u3002\u4e8e\u627f\u73e0\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u6bd5\u5927\u9f99\u5934\uff0c\u53f6\u6210\u6797\u5df2\u53bb\u5f97\u8fdc\u4e86\uff0c\u4f60\u6709\u4ec0\u4e48\u8981\u4e8b\uff0c\u6211\u7684\u9a6c \u5feb\uff0c\u66ff\u4f60\u8ffd\u4ed6\u56de\u6765\uff01\u201d \u3000\u3000\u6bd5\u64ce\u5929\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8ffd\u4ed6\uff0c\u6211\u662f\u63a5\u4f60\uff01\u201d\u4e8e\u627f\u73e0\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4e0d\u6562\u6709\u52b3\u9f99\u5934 \u5927\u9a7e\uff01\u201d\u6bd5\u64ce\u5929\u7b11\u9053\uff1a\u201c\u4f60\u548c\u53f6\u6210\u6797\u53cb\u60c5\u5012\u5f88\u597d\u554a\uff0c\u8fd9\u56de\u9001\u522b\uff0c\u4f60\u597d\u50cf\u6bd4\u4e0a\u6b21\u542c\u5f97\u94c1\u955c\u5fc3\u8d70 \u4e86\uff0c\u8fd8\u66f4\u4f24\u5fc3\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u674f\u9762\u98de\u971e\uff0c\u67f3\u7709\u5012\u7ad6\uff0c\u6012\u9053\uff1a\u201c\u6bd5\u5927\u9f99\u5934\u653e\u5c0a\u91cd\u4e9b\uff0c\u6211\u662f\u7ed9\u4f60\u6d88\u9063\u7684\u4e48\uff1f\u201d\u6bd5\u64ce \u5929\u8d76\u5fd9\u62d4\u9a6c\u9000\u4e86\u4e00\u7bad\u4e4b\u5730\uff0c\u8d54\u7b11\u8bf4\u9053\uff1a\u201c\u5c82\u6562\uff0c\u5c82\u6562\uff0c\u6211\u662f\u4e3a\u59d1\u5a18\u8bbe\u60f3\uff01\u201d\u4e8e\u627f\u73e0\u51b7\u7b11\u9053\uff1a \u201c\u5927\u9f99\u5934\u5982\u6b64\u597d\u5fc3\uff0c\u66ff\u6211\u8bbe\u60f3\u4ec0\u4e48\uff1f\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u6211\u82e5\u5bf9\u59d1\u5a18\u6beb\u65e0\u5fc3\u610f\uff0c\u5f53\u5e74\u4e5f\u4e0d\u81f3\u4e8e\u5192 \u4e86\u5927\u9669\uff0c\u5077\u8fdb\u4eac\u57ce\uff0c\u6536\u6b93\u5c0a\u5927\u4eba\u7684\u9ab8\u9aa8\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u51b7\u7740\u9762\u543c\u9053\uff1a\u201c\u4f60\u6536\u6b93\u5148\u7236\u7684\u5927\u6069\u5927 \u5fb7\uff0c\u6211\u4e0d\u4f1a\u5fd8\u8bb0\u7684\u3002\u4e0d\u5fc5\u52b3\u4f60\u4e09\u756a\u4e24\u6b21\u5730\u63d0\u8d77\uff0c\u6211\u5b9a\u7136\u5f90\u56fe\u540e\u62a5\u4fbf\u662f\u3002\u201d\u6bd5\u64ce\u5929\u7ed9\u5979\u62a2\u767d\uff0c \u751a\u662f\u5c34\u5c2c\uff0c\u53f9\u4e86\u4e00\u58f0\uff0c\u63a9\u9970\u7b11\u9053\uff1a\u201c\u6211\u6bd5\u67d0\u5c82\u662f\u65bd\u6069\u671b\u62a5\u4e4b\u4eba\uff0c\u53ea\u662f\u8868\u767d\u4e00\u756a\u5fc3\u610f\u7f62\u5566\u3002\u201d \u4e8e\u627f\u73e0\u9053\uff1a\u201c\u597d\uff0c\u6211\u660e\u767d\u5566\u3002\u5927\u9f99\u5934\uff0c\u4f60\u8bf7\u4fbf\u3002\u201d\u6bd5\u64ce\u5929\u62e6\u7740\u9a6c\u5934\uff0c\u9053\uff1a\u201c\u6211\u66ff\u59d1\u5a18\u8bbe\u60f3\uff0c \u6211\u4e0d\u53ea\u662f\u66ff\u4f60\u6536\u6b93\u7236\u4eb2\u9057\u9aa8\u4fbf\u7b97\uff0c\u6211\u8fd8\u8981\u4e3a\u4f60\u62a5\u5374\u5927\u4ec7\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ec0\u4e48\u5927\u4ec7\uff01\u201d\u6bd5\u64ce \u5929\u9053\uff1a\u201c\u4f60\u7684\u7236\u4eb2\u662f\u7687\u5e1d\u6740\u7684\uff0c\u6211\u8d77\u5175\u63a8\u5012\u9f99\u5ef7\uff0c\u706d\u5374\u5927\u660e\uff0c\u4e0d\u662f\u4e3a\u4f60\u62a5\u4ec7\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u51b7 \u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u63a8\u5012\u9f99\u5ef7\uff0c\u4f60\u505a\u7687\u5e1d\uff0c\u5c82\u6b62\u53ea\u662f\u4e3a\u6211\u62a5\u4ec7\uff1f\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4fbf\u597d\uff0c\u4e3a \u4f60\u8bbe\u60f3\uff0c\u90a3\u53f6\u6210\u6797\u5c06\u6765\u6700\u591a\u53ea\u80fd\u505a\u4e2a\u5f00\u56fd\u529f\u81e3\uff0c\u5c82\u4f3c\u6211\u6709\u4e5d\u4e94\u4e4b\u5c0a\u4e4b\u671b\u3002\u4f60\u4f55\u5fc5\u5bf9\u4ed6\u5982\u6b64\u597d \u6cd5\uff1f\u201d \u3000\u3000\u56fe\u7a77\u5315\u89c1\uff0c\u539f\u6765\u6bd5\u64ce\u5929\u7adf\u662f\u60f3\u7528\u8363\u534e\u5bcc\u8d35\u8bf1\u5979\uff01\u8fd9\u6bd4\u542c\u5230\u94c1\u955c\u5fc3\u7684\u5938\u5938\u5176\u8c08\u66f4\u4f1a\u4ee4\u5979\u6076 \u5fc3\u767e\u500d\uff01\u201c\u4e0d\u8981\u8138\u201d\u4e09\u5b57\u51e0\u4e4e\u9a82\u4e86\u51fa\u6765\uff0c\u6781\u529b\u5fcd\u4f4f\uff0c\u9a6c\u97ad\u4e00\u5530\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8bf7\u672a\u6765\u5929\u5b50\u8ba9 \u8def\uff0c\u8981\u4e0d\u7136\u6211\u8981\u95ef\u9a7e\u5566\uff01\u201d\u6bd5\u64ce\u5929\u9762\u8272\u6da8\u7ea2\uff0c\u843d\u4e0d\u4e0b\u53f0\uff0c\u6b63\u5728\u7ea0\u7f20\uff0c\u5ffd\u542c\u5f97\u51cc\u4e91\u51e4\u7eb5\u58f0\u957f \u7b11\uff0c\u98de\u9a6c\u800c\u6765\uff0c\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u5927\u9f99\u5934\uff0c\u4f60\u8fd8\u5728\u8fd9\u513f\u3002\u201d \u3000\u3000\u534e\u64ce\u5929\u62e8\u5f00\u9a6c\u5934\uff0c\u5c34\u5c2c\u7b11\u9053\uff1a\u201c\u6211\u89c1\u4e8e\u59d1\u5a18\u8bb8\u4e45\u672a\u56de\uff0c\u53ea\u9053\u53f6\u6210\u6797\u5c1a\u6709\u4ec0\u4e48\u4e8b\u60c5\u672a\u66fe\u4ea4 \u4ee3\uff0c\u662f\u4ee5\u524d\u6765\u63a2\u671b\uff0c\u51cc\u5be8\u4e3b\uff0c\u4f60\u4e5f\u6765\u4e86\uff1f\u201d\u51cc\u4e91\u51e4\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u5f53\u4f60\u4eec\u6709\u4ec0\u4e48\u4e8b\u5546\u91cf\uff0c\u51e0\u4e4e \u5413\u5f97\u6211\u4e0d\u6562\u524d\u6765\u6253\u6405\u5462\u3002\u201d\u4e8e\u627f\u73e0\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u7684\u786e\u662f\u5728\u8c08\u8bba\u5927\u4e8b\u3002\u6bd5\u5927\u9f99\u5934\u6b63\u5728\u6253\u7b97\u767b\u57fa \u4e4b\u540e\u5927\u5c01\u529f\u81e3\u5462\uff01\u201d\u51cc\u4e91\u51e4\u7eb5\u58f0\u5927\u7b11\uff0c\u5728\u9a6c\u80cc\u4e0a\u629a\u5251\u65bd\u793c\uff0c\u5531\u4e86\u4e2a\u558f\uff0c\u9053\uff1a\u201c\u5c0f\u5973\u53c2\u89c1\u9f99 \u9a7e\uff0c\u8bf7\u738b\u4e0a\u8d4f\u8d50\u3002\u201d\u51cc\u4e91\u51e4\u8c6a\u8fc8\u4e0d\u7f81\uff0c\u6bd5\u64ce\u5929\u4e5f\u60e7\u5979\u4e09\u5206\uff0c\u88ab\u5979\u8c03\u4f83\uff0c\u557c\u7b11\u7686\u975e\uff0c\u6025\u5fd9\u8fd8\u793c \u8bf4\u9053\uff1a\u201c\u51cc\u5be8\u4e3b\u53d6\u7b11\u4e86\u3002\u201d\u642d\u8baa\u51e0\u53e5\uff0c\u5148\u81ea\u8d70\u4e86\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u54c8\u54c8\u5927\u7b11\uff0c\u56de\u5230\u5e10\u4e2d\uff0c\u4e8e\u627f\u73e0\u5c06\u9002\u624d\u4e4b\u4e8b\u90fd\u4e0e\u51cc\u4e91\u51e4\u8bf4\u4e86\u3002\u51cc\u4e91\u51e4\u7b11\u5bb9\u5c3d\u655b\uff0c \u9053\uff1a\u201c\u4f60\u6253\u7b97\u5982\u4f55\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u771f\u6599\u4e0d\u5230\u6bd5\u64ce\u5929\u662f\u8fd9\u6837\u7684\u4eba\uff0c\u6211\u6253\u7b97\u8d70\u4e86\u3002\u201d\u51cc\u4e91\u51e4 \u9053\uff1a\u201c\u552f\u5176\u5982\u6b64\u54b1\u4eec\u66f4\u4e0d\u80fd\u8d70\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u54b1\u4eec\u4e00\u8d70\uff0c\u53f6\u7edf\u9886\u5b64 \u7acb\u65e0\u63f4\uff0c\u53ea\u6015\u4f1a\u6709\u610f\u5916\u4e4b\u4e8b\u3002\u201d\u4e8e\u627f\u73e0\u867d\u7136\u65e9\u5df2\u770b\u51fa\u6bd5\u64ce\u5929\u6697\u4e2d\u4e0e\u53f6\u5b97\u7559\u4e89\u6743\uff0c\u4f46\u5c1a\u672a\u60f3\u5230 \u6709\u4f55\u5371\u9669\uff0c\u542c\u5f97\u51cc\u4e91\u51e4\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5fc3\u4e2d\u4e0d\u5bd2\u800c\u6817\uff0c\u7acb\u5373\u6253\u6d88\u4e86\u51fa\u8d70\u4e4b\u610f\u3002 \u3000\u3000\u5149\u9634\u8fc5\u901f\uff0c\u52ff\u5306\u53c8\u8fc7\u4e86\u4e00\u4e2a\u591a\u6708\uff0c\u8fd9\u4e00\u4e2a\u591a\u6708\u4e2d\uff0c\u6bd5\u64ce\u5929\u4e0d\u6562\u518d\u5411\u4e8e\u627f\u73e0\u64a9\u62e8\uff0c\u5012\u4e5f\u76f8 \u5b89\u65e0\u4e8b\uff0c\u53ea\u662f\u524d\u65b9\u519b\u60c5\u65e5\u7d27\uff0c\u9664\u4e86\u53f6\u6210\u6797\u4e00\u8def\u4e0e\u5b98\u519b\u5728\u5c6f\u6eaa\u76f8\u6301\u4e4b\u5916\uff0c\u5176\u4f59\u5404\u8def\uff0c\u90fd\u6709\u8d25 \u8c61\uff0c\u5c24\u5176\u662f\u6210\u6d77\u5c71\u8fd9\u652f\u6e14\u6c11\u5144\u5f1f\u5175\uff0c\u56e0\u4e3a\u4e0d\u60ef\u5728\u5c71\u5730\u4f5c\u6218\uff0c\u8d25\u5f97\u66f4\u60e8\uff0c\u6253\u4e86\u4e24\u573a\u786c\u4ed7\uff0c\u4f24\u4ea1 \u51e0\u8fd1\u4e00\u534a\u3002\u4e8e\u627f\u73e0\u548c\u51cc\u4e91\u51e4\u90fd\u662f\u751a\u4e3a\u62c5\u5fe7\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u4e8e\u627f\u73e0\u548c\u51cc\u4e91\u51e4\u6b63\u5728\u5e10\u4e2d\u8c08\u8bba\uff0c\u5ffd\u542c\u5f97\u5e10\u5916\u6684\u54d7\uff0c\u51cc\u4e91\u51e4\u5524\u4e00\u4e2a\u5973\u5175\u51fa\u5e10\u6253\u542c\uff0c \u8fc7\u4e86\u4e00\u76cf\u8336\u65f6\u523b\uff0c\u90a3\u5973\u5175\u56de\u6765\u62a5\u9053\uff1a\u201c\u5de6\u8425\u7684\u519b\u58eb\u4eec\u5728\u9a82\u6bd5\u5927\u9f99\u5934\u3002\u201d \u3000\u3000\u5de6\u8425\u7684\u7edf\u9886\u662f\u53f6\u5b97\u7559\u7684\u526f\u624b\u9093\u8302\u4e03\uff0c\u53f6\u5b97\u7559\u624b\u4e0b\u7684\u519b\u961f\uff0c\u53ea\u6709\u8fd9\u4e00\u652f\u672a\u66fe\u8c03\u8d70\u3002\u4e8e\u627f\u73e0 \u8bf4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u9a82\u6bd5\u5927\u9f99\u5934\uff1f\u201d\u90a3\u5973\u5175\u9053\uff1a\u201c\u9a82\u6bd5\u5927\u9f99\u5934\u4e0d\u80af\u7ed9\u53f6\u6210\u6797\u62e8\u9001\u519b\u7cae\uff01\u201d\u4e8e\u627f\u73e0 \u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u4e8b\u3002\u201d\u90a3\u5973\u5175\u9053\uff1a\u201c\u542c\u8bf4\u53f6\u6210\u6797\u5df2\u6d3e\u4e86\u4e09\u62e8\u4eba\u56de\u6765\u50ac\u9001\u7cae\u8349\uff0c\u6bd5 \u5927\u9f99\u5934\u603b\u662f\u63a8\u4e09\u963b\u56db\u3002\u9093\u7edf\u9886\u660e\u660e\u77e5\u9053\u57ce\u4e2d\u5c1a\u6709\u4e07\u62c5\u519b\u7cae\uff0c\u8dd1\u53bb\u95ee\u4ed6\u3002\u4ed6\u8bf4\uff0c\u5927\u8425\u8981\u7559\u4e0b\u4e94 \u5343\u62c5\uff0c\u8fd8\u6709\u4e94\u5343\u62c5\u8981\u62e8\u7ed9\u6e29\u5dde\u7684\u9a7b\u519b\u3002\u5176\u5b9e\u6e29\u5dde\u7f3a\u7cae\uff0c\u5e76\u4e0d\u4e25\u91cd\uff0c\u6743\u8861\u8f7b\u91cd\uff0c\u5e94\u5f53\u8fd0\u5230\u524d\u65b9 \u624d\u662f\u3002\u53ef\u662f\u6bd5\u5927\u9f99\u5934\u575a\u4e0d\u80af\u653e\uff0c\u9093\u526f\u7edf\u9886\u56de\u6765\u5927\u54ed\u4e00\u573a\uff01\u201d \u3000\u3000\u51cc\u4e91\u51e4\u51b7\u7b11\u9053\uff1a\u201c\u679c\u7136\u7ed9\u6211\u4e0d\u5e78\u6599\u4e2d\u3002\u201d\u4e8e\u627f\u73e0\u6012\u6c14\u51b2\u51b2\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u627e\u6bd5\u64ce\u5929\u8bf4\u8bdd \u53bb\u3002\u201d\u51cc\u4e91\u51e4\u6c89\u601d\u6709\u9877\uff0c\u5524\u5973\u5175\u5934\u76ee\u6765\u5429\u5490\u4e86\u51e0\u53e5\uff0c\u7acb\u5373\u6b66\u88c5\u4f69\u5251\u548c\u4e8e\u627f\u73e0\u9a70\u5230\u5927\u8425\u3002 \u3000\u3000\u53ea\u89c1\u5927\u8425\u6212\u5907\u68ee\u4e25\uff0c\u8fe5\u5f02\u5f80\u65e5\u3002\u51cc\u3001\u4e8e\u4e8c\u4eba\u5230\u4e86\u8425\u5916\u534a\u91cc\u4e4b\u5730\uff0c\u4fbf\u7ed9\u62e6\u4f4f\uff0c\u4e2d\u519b\u8bf4\u9053\uff1a \u201c\u6bd5\u5927\u9f99\u5934\u6b63\u5728\u4e0e\u53f6\u7edf\u9886\u5546\u8bae\u519b\u60c5\uff0c\u672a\u5f97\u4f20\u5524\uff0c\u4efb\u4f55\u4eba\u4e0d\u5f97\u64c5\u8fdb\uff01\u201d\u51cc\u4e91\u51e4\u67f3\u7709\u5012\u7ad6\uff0c\u6012\u58f0 \u65a5\u9053\uff1a\u201c\u6211\u4eec\u6709\u91cd\u5927\u7684\u519b\u60c5\u8981\u4e0e\u4ed6\u5546\u8bae\uff0c\u8c01\u6562\u963b\u62e6\uff01\u201d \u3000\u3000\u90a3\u5b88\u95e8\u7684\u4e2d\u519b\u88ab\u51cc\u4e91\u51e4\u4e00\u559d\uff0c\u5012\u9000\u51e0\u6b65\u3002\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u4eec\u8fdb\u53bb\u89c1\u6bd5\u64ce\u5929\uff0c\u8981\u602a\u8ba9\u4ed6\u602a \u6211\uff0c\u4e0e\u4f60\u65e0\u5e72\uff01\u201d\u90a3\u4e9b\u536b\u58eb\u4eec\u77e5\u9053\u6bd5\u64ce\u5929\u5e73\u65e5\u5bf9\u4e8e\u627f\u73e0\u53e6\u773c\u76f8\u770b\uff0c\u679c\u7136\u4e0d\u6562\u62e6\u963b\uff0c\u51cc\u4e91\u51e4\u4e0e \u4e8e\u627f\u73e0\u7acb\u523b\u8df3\u4e0b\u9a6c\u80cc\uff0c\u76f4\u95ef\u5927\u8425\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5e10\u4e2d\u4e71\u5608\u5608\u5730\u95f9\u6210\u4e00\u7247\uff0c\u84e6\u7136\u95f4\u542c\u5f97\u9093\u8302\u4e03\u9739\u96f3\u4e00\u58f0\u5927\u559d\uff1a\u201c\u6bd5\u64ce\u5929\u4f60\u610f\u6b32\u4f55 \u4e3a\uff1f\u201d\u4e8e\u627f\u73e0\u6697\u53eb\u4e00\u58f0\u201c\u4e0d\u597d\uff01\u201d\u63ed\u5e10\u51b2\u4eba\uff0c\u53ea\u89c1\u6bd5\u64ce\u5929\u4e0e\u767d\u5b5f\u5ddd\u3001\u6bd5\u613f\u7a77\u7b49\u603b\u6709\u5341\u4f59\u4eba\u4e4b \u591a\uff0c\u6392\u6210\u4e86\u4e00\u4e2a\u534a\u5f27\u5f62\uff0c\u56f4\u7740\u4e86\u53f6\u5b97\u7559\uff0c\u53f6\u5b97\u7559\u5e76\u65e0\u536b\u58eb\uff0c\u53ea\u5e26\u6765\u4e86\u526f\u624b\u9093\u8302\u4e03\u4e00\u4eba\u3002 \u3000\u3000\u4f46\u89c1\u6bd5\u64ce\u5929\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u53f6\u7edf\u9886\u8fde\u5e74\u52b3\u82e6\uff0c\u800c\u4eca\u5e74\u4e8b\u5df2\u9ad8\uff0c\u6211\u5b9e\u5728\u4e0d\u5fcd\u8ba9\u4ed6\u591a\u6240\u64cd\u52b3\uff0c \u7279\u5730\u7ed9\u4ed6\u5b89\u6392\u4e86\u4e00\u6240\u5e7d\u9759\u7684\u5c45\u5904\uff0c\u8bf7\u4ed6\u517b\u8001\uff0c\u5c82\u6709\u574f\u5fc3\uff1f\u201d\u9093\u8302\u4e03\u5927\u6012\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5927\u9f99\u5934 \u7684\u4f4d\u5b50\u8fd8\u662f\u53f6\u5927\u54e5\u8ba9\u7ed9\u4f60\u7684\uff0c\u4f60\u800c\u4eca\u5374\u8981\u593a\u4ed6\u7684\u5175\u6743\uff0c\u8fd8\u60f3\u5e7d\u7981\u4ed6\uff0c\u54fc\uff0c\u54fc\uff01\u5929\u4e0b\u4e8b\u603b\u5f97\u6709 \u4e00\u4e2a\u9053\u7406\uff01\u53f6\u5927\u54e5\u521a\u6ee1\u4e94\u5341\u4e4b\u5e74\uff0c\u8bf7\u4ed6\u517b\u8001\uff0c\u8fd9\u662f\u7b11\u8bdd\uff01\u201d \u3000\u3000\u53f6\u5b97\u7559\u54c8\u54c8\u5927\u7b11\uff1a\u201c\u6bd5\u8d24\u5f1f\u96c4\u624d\u5927\u7565\uff0c\u80dc\u6211\u767e\u500d\uff0c\u6211\u505a\u8fd9\u4e2a\u7edf\u9886\u672c\u6765\u5c31\u89c9\u5f97\u6709\u70b9\u6c57\u989c\u3002 \u6bd5\u8d24\u5f1f\u80fd\u8005\u591a\u52b3\uff0c\u613f\u610f\u7ed9\u6211\u517c\u6311\u8fd9\u526f\u91cd\u62c5\uff0c\u771f\u662f\u6700\u597d\u4e0d\u8fc7\uff0c\u8001\u9093\uff0c\u4f60\u4e3a\u8fd9\u4e2a\u4e89\u8bba\uff0c\u522b\u4eba\u4e0d \u77e5\uff0c\u5012\u4ee5\u4e3a\u662f\u6211\u548c\u6bd5\u8d24\u5f1f\u4e89\u6743\u4e86\uff0c\u5c82\u4e0d\u6559\u4eba\u7b11\u8bdd\u4e48\uff1f\u201d \u3000\u3000\u9093\u8302\u4e03\u53eb\u9053\uff1a\u201c\u53f6\u5927\u54e5\uff0c\u4f60\uff0c\u4f60\u2026\u2026\u4f60\u5fcd\u5fc3\u8ba9\u591a\u5e74\u57fa\u4e1a\u90fd\u7ed9\u4ed6\u4e00\u624b\u6bc1\u4e86\u4e48\uff1f\u4f60\uff0c\u4f60\u2026\u2026 \u4f60\u4e0d\u987e\u5ff5\u81ea\u5df1\uff0c\u8fde\u5f1f\u5144\u4eec\u4e5f\u4e0d\u987e\u5ff5\u4e48\uff1f\u201d\u58f0\u6cea\u4ff1\u4e0b\uff0c\u53f6\u5b97\u7559\u6b63\u60f3\u8bf4\u8bdd\uff0c\u5ffd\u542c\u5e10\u5916\u53f7\u89d2\u55a7\u5929\uff0c \u53f6\u5b97\u7559\u9053\uff1a\u201c\u6bd5\u5927\u9f99\u5934\uff0c\u8fd9\u662f\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6bd5\u64ce\u5929\u9762\u8272\u5c34\u5c2c\uff0c\u6a2a\u4e86\u5fc3\u80a0\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u5de6\u8425\u7684\u5175\u58eb\u4e0d\u80af\u542c\u547d\u6539\u7f16\uff0c\u662f\u6211\u8981\u4ed6\u4eec\u7f34 \u68b0\uff01\u201d\u53f6\u5b97\u7559\u53cc\u76ee\u4e00\u5f20\uff0c\u559d\u9053\uff1a\u201c\u6bd5\u64ce\u5929\uff0c\u8fd9\u4f60\u5c31\u4e0d\u5bf9\u4e86\uff01\u4f60\u8981\u6211\u4ea4\u51fa\u5175\u6743\uff0c\u8fd9\u4e2a\u5bb9\u6613\uff0c\u5374 \u4e3a\u4f55\u540c\u5ba4\u64cd\u6208\uff1f\u201d\u6bd5\u64ce\u5929\u8baa\u8baa\u8bf4\u9053\uff1a\u201c\u53ea\u6015\u5de6\u8425\u5144\u5f1f\uff0c\u4e0d\u662f\u548c\u53f6\u7edf\u9886\u4e00\u6837\u5fc3\u80a0\uff0c\u4e0d\u5982\u2026\u2026\u201d \u60f3\u8bf4\uff1a\u201c\u4e0d\u5982\u8bf7\u4f60\u529d\u8c15\u4ed6\u4eec\u5f52\u987a\u4e8e\u6211\u3002\u201d\u8fd9\u8bdd\u5374\u4e0d\u597d\u51fa\u53e3\u3002\u9093\u8302\u4e03\u5927\u559d\u9053\uff1a\u201c\u597d\uff0c\u4eca\u65e5\u7b97\u8ba4 \u5f97\u4f60\u4e86\uff0c\u4f60\u8fd9\u72fc\u5fc3\u72d7\u80ba\u7684\u8d4b\u5b50\uff01\u201d \u3000\u3000\u6bd5\u64ce\u5929\u52c3\u7136\u53d8\u8272\uff0c\u559d\u9053\uff1a\u201c\u628a\u8fd9\u72af\u4e0a\u4f5c\u4e71\u7684\u8d3c\u5b50\u62ff\u4e0b\uff01\u201d\u53f6\u5b97\u7559\u632f\u81c2\u559d\u9053\uff1a\u201c\u4e0d\u53ef\u52a8 \u624b\uff01\u201d\u8425\u4e2d\u867d\u7136\u5c3d\u662f\u6bd5\u64ce\u5929\u7684\u4eb2\u4fe1\uff0c\u4f46\u53f6\u5b97\u7559\u7684\u5a01\u671b\u6df1\u5f97\u4eba\u5fc3\uff0c\u4f17\u4eba\u88ab\u4ed6\u4e00\u559d\uff0c\u7adf\u7136\u9762\u9762\u76f8 \u89d1\uff0c\u6bd5\u64ce\u5929\u8d8a\u53d1\u5927\u6012\uff0c\u5411\u767d\u5b5f\u5ddd\u4e00\u629b\u773c\u8272\uff0c\u9053\uff1a\u201c\u8981\u4f60\u4eec\u4f55\u7528\uff1f\u201d\u767d\u5b5f\u5ddd\u597d\u7b11\u9053\uff1a\u201c\u53f6\u5927\u54e5 \u522b\u52a8\u809d\u706b\uff0c\u8eab\u4f53\u4fdd\u91cd\u8981\u7d27\uff0c\u5230\u6e29\u5dde\u9759\u517b\u53bb\u5427\u3002\u201d\u8df3\u4e0a\u53bb\u5c31\u60f3\u628a\u53f6\u5b97\u7559\u67b6\u8d70\uff0c\u5ffd\u542c\u5f97\u201c\u94ee\u201d\u7684 \u4e00\u58f0\uff0c\u51cc\u4e91\u51e4\u9661\u624b\u53d1\u51fa\u4e00\u679a\u8774\u8776\u9556\uff0c\u628a\u767d\u5b5f\u5ddd\u7684\u989d\u89d2\u6253\u7a7f\uff0c\u767b\u65f6\u8840\u6d41\u5982\u6ce8\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u5e10\u4e2d\u5927\u4e71\uff0c\u6bd5\u64ce\u5929\u7684\u515a\u7fbd\u6495\u7834\u4e86\u9762\u5b50\uff0c\u4fbf\u6709\u51e0\u4e2a\u4eba\u58eb\u6765\u8981\u902e\u6355\u53f6\u5b97\u7559\uff0c\u51cc\u4e91\u51e4\u53eb \u9041\uff1a\u201c\u627f\u73e0\uff0c\u4f60\u622a\u7740\u8fd9\u4e2a\u53cd\u8d3c\uff0c\u6211\u4fdd\u62a4\u53f6\u7edf\u9886\u95ef\u51fa\u5927\u8425\uff01\u201d \u3000\u3000\u6bd5\u64ce\u5929\u53eb\u9053\uff1a\u201c\u627f\u73e0\uff0c\u4f60\u600e\u4e48\u4e0e\u6211\u4f5c\u5bf9\uff1f\u201d\u4e8e\u627f\u73e0\u65a5\u9053\uff1a\u201c\u4f60\u53c8\u600e\u4e48\u4e0e\u53f6\u4f2f\u4f2f\u4f5c\u5bf9\uff1f\u201d \u6bd5\u64ce\u5929\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u5feb\u5c31\u5fd8\u4e86\u846c\u7236\u4e4b\u6069\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u5feb\u5c31\u5fd8\u4e86\u53f6\u4f2f\u4f2f\u6276\u690d\u4e4b\u6069 \u4e48\uff1f\u201d\u9488\u950b\u76f8\u5bf9\uff0c\u534a\u53e5\u4e0d\u8ba9\uff0c\u6bd5\u64ce\u5929\u6709\u70b9\u6c14\u9981\uff0c\u9000\u4e86\u4e24\u6b65\uff0c\u4e8e\u627f\u73e0\u6309\u5251\u65a5\u9053\uff1a\u201c\u4f60\u653e\u4e0d\u653e\u53f6 \u7edf\u9886\u51fa\u8425\uff1f\u201d\u6bd5\u64ce\u5929\u7ea2\u4e86\u53cc\u773c\uff0c\u63d0\u8d77\u72fc\u7259\u5927\u68d2\uff0c\u559d\u9053\uff1a\u201c\u628a\u8fd9\u4e24\u4e2a\u4e0d\u77e5\u597d\u574f\u7684\u5973\u5a03\u513f\u4e5f\u90fd\u64d2 \u4e86\uff01\u201d\u4e8e\u627f\u73e0\u4e00\u58f0\u51b7\u7b11\uff0c\u5530\u5730\u5c31\u662f\u8fce\u9762\u4e00\u5251\uff01 \u3000\u3000\u6bd5\u64ce\u5929\u4e3e\u6367\u4e00\u8fce\uff0c\u4e8e\u627f\u73e0\u77e5\u4ed6\u529b\u5927\uff0c\u5251\u950b\u4e00\u98a4\uff0c\u56de\u5251\u53cd\u524a\uff0c\u8fd9\u4e00\u5251\u53d8\u62db\u5feb\u6781\uff0c\u51cc\u5389\u975e \u5e38\uff0c\u6bd5\u64ce\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u4e00\u5e74\u4e0d\u89c1\uff0c\u5979\u7684\u5251\u6cd5\u7adf\u7136\u7cbe\u8fdb\u5982\u65af\uff01\u201d\u72fc\u7259\u68d2\u821e\u8d77\u4e00\u4e2a\u201c\u96ea \u82b1\u76d6\u9876\u201d\uff0c\u62a4\u7740\u8eab\u8eaf\uff0c\u4e8e\u627f\u73e0\u62a2\u4e86\u653b\u52bf\uff0c\u5530\u3001\u5530\u3001\u5530\u4e00\u8fde\u51e0\u5251\uff0c\u5c06\u6bd5\u64ce\u5929\u903c\u5f97\u6b65\u6b65\u9000\u540e\uff0c\u4f46 \u6bd5\u64ce\u5929\u6b66\u529f\u8d85\u5353\uff0c\u5185\u529b\u8fdc\u80dc\u4e8e\u4e8e\u627f\u73e0\uff0c\u4e8e\u627f\u73e0\u867d\u7136\u5728\u5251\u6cd5\u4e0a\u7a0d\u5360\u4fbf\u5b9c\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u5374\u662f\u80dc\u4ed6 \u4e0d\u5f97\u3002 \u3000\u3000\u5e10\u4e2d\u4e00\u7247\u6df7\u6218\uff0c\u51cc\u4e91\u51e4\u4e00\u53e3\u5251\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u523a\u4f24\u4e86\u4e09\u540d\u536b\u58eb\uff0c\u4f46\u6bd5 \u64ce\u5929\u7684\u4eb2\u4fe1\u4e4b\u4e2d\uff0c\u6709\u597d\u51e0\u4e2a\u662f\u7eff\u6797\u9ad8\u624b\uff0c\u8702\u62e5\u800c\u4e0a\uff0c\u7ec8\u4e8e\u5c06\u53f6\u5b97\u7559\u4e0e\u51cc\u4e91\u51e4\u903c\u5230\u4e00\u9685\u3002\u6fc0\u6218 \u4e2d\u5ffd\u542c\u5f97\u9093\u8302\u4e03\u4e00\u58f0\u60e8\u547c\uff0c\u5636\u58f0\u53eb\u9053\uff1a\u201c\u53f6\u5927\u54e5\uff0c\u6211\u5148\u53bb\u4e86\uff01\u4f60\u53ef\u5343\u4e07\u4e0d\u8981\u653e\u5f03\u8fd9\u4e2a\u57fa\u4e1a \u5440\uff01\u201d\u539f\u6765\u4ed6\u7740\u4e86\u767d\u5b5f\u5ddd\u4e00\u97ad\uff0c\u5012\u4e0b\u6765\u65f6\uff0c\u53c8\u88ab\u4e24\u5200\u9f50\u80a9\u5288\u4e0b\uff0c\u7adf\u81ea\u6b7b\u4e86\uff01 \u3000\u3000\u591a\u5e74\u6218\u53cb\uff0c\u4e00\u65e6\u4f24\u4ea1\uff0c\u53f6\u5b97\u7559\u809d\u80c6\u4ff1\u88c2\uff0c\u9739\u96f7\u4e00\u58f0\u5927\u559d\uff0c\u62a2\u8fc7\u4e86\u4e00\u53e3\u539a\u80cc\u65ab\u5c71\u5200\uff0c\u594b\u8d77 \u4e00\u5200\uff0c\u5c06\u90a3\u780d\u6b7b\u9093\u8302\u4e03\u7684\u536b\u58eb\u5288\u4e3a\u4e24\u6bb5\uff0c\u559d\u9053\uff1a\u201c\u6bd5\u64ce\u5929\uff0c\u4f60\u542c\u6211\u4e00\u8a00\uff01\u201d\u6bd5\u64ce\u5929\u67b6\u5f00\u4e86\u4e8e \u627f\u73e0\u7684\u5251\u62db\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u4e8b\u5df2\u5982\u6b64\uff0c\u65e0\u8bdd\u53ef\u8c08\uff01\u201d\u72fc\u7259\u68d2\u4e00\u6307\uff0c\u5c06\u90a3\u4e9b\u88ab\u53f6\u5b97\u7559\u5a01\u98ce\u6151\u4f4f \u7684\u4eb2\u4fe1\u53c8\u8feb\u4e0a\u53bb\u3002 \u3000\u3000\u53f6\u5b97\u7559\u8fd9\u65f6\u7aef\u7684\u662f\u52a8\u4e86\u771f\u6012\uff0c\u4e0e\u51cc\u4e91\u51e4\u80cc\u5411\u800c\u7acb\uff0c\u5927\u5200\u970d\u970d\uff0c\u594b\u6218\u95ef\u8425\uff0c\u4f46\u5e10\u4e2d\u9ad8\u624b\u56db \u5e03\uff0c\u54ea\u95ef\u5f97\u51fa\uff0c\u53f6\u5b97\u7559\u867d\u7136\u53c8\u5288\u4e86\u4e24\u4eba\uff0c\u80a9\u5934\u5374\u4e2d\u4e86\u4e00\u5251\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u672c\u6b32\u64d2\u8d3c\u64d2\u738b\uff0c\u8fd9\u65f6\u5374\u53cd\u88ab\u6bd5\u64ce\u5929\u7eca\u4f4f\uff0c\u5c06\u5979\u4e0e\u53f6\u5b97\u7559\u3001\u51cc\u4e91\u51e4\u9694\u4e3a\u4e24\u5904\uff0c\u89c1\u53f6 \u5b97\u7559\u5904\u5883\u8d8a\u6765\u8d8a\u9669\uff0c\u5fc3\u4e2d\u6025\u6012\u4e4b\u6781\uff0c\u731b\u5730\u4e00\u5251\u523a\u6bd4\uff0c\u56de\u65cb\u53cd\u524a\uff0c\u5251\u5149\u98d8\u95ea\uff0c\u5b9b\u5982\u9ed1\u591c\u7e41\u661f\uff0c \u5343\u70b9\u4e07\u70b9\u76f4\u6d12\u4e0b\u6765\uff0c\u7adf\u7136\u662f\u4e0d\u987e\u81ea\u8eab\u7684\u62fc\u547d\u62db\u6570\u3002\u6bd5\u64ce\u5929\u8fd9\u65f6\u5df2\u662f\u5341\u516b\u7701\u5927\u9f99\u5934\u4e4b\u5c0a\uff0c\u54ea\u6562 \u548c\u5979\u62fc\u547d\u3002\u6025\u6025\u907f\u5f00\uff0c\u4e8e\u627f\u73e0\u7f13\u51fa\u624b\u6765\uff0c\u638f\u51fa\u4e00\u628a\u91d1\u82b1\uff0c\u626c\u58f0\u559d\u9053\uff1a\u201c\u6321\u6211\u8005\u6b7b\uff0c\u8ba9\u6211\u8005 \u751f\uff01\u201d\u53cc\u6307\u75be\u5f39\uff0c\u4e00\u6735\u6735\u91d1\u82b1\u7834\u7a7a\u98de\u51fa\uff0c\u8f6c\u773c\u95f4\u4f24\u4e86\u6570\u4eba\uff0c\u95ef\u5f00\u4e86\u4e00\u6761\u8840\u8def\u3002\u6bd5\u64ce\u5929\u6025\u5fd9\u62e6 \u622a\uff0c\u4e8e\u627f\u73e0\u626c\u624b\u53c8\u662f\u4e09\u6735\u91d1\u82b1\uff0c\u6bd5\u64ce\u5929\u5927\u68d2\u4e00\u8361\uff0c\u5c06\u4e09\u6735\u91d1\u82b1\u5168\u90e8\u78d5\u98de\uff0c\u4f46\u89c1\u8fd9\u5a01\u52bf\uff0c\u5374\u4e5f \u4e0d\u6562\u903c\u8fd1\uff0c\u767d\u5b5f\u5ddd\u63a5\u8fc7\u6765\u63a9\u62a4\uff0c\u521a\u521a\u7eb5\u8d77\uff0c\u5c31\u88ab\u4e8e\u627f\u73e0\u4e00\u6735\u91d1\u82b1\u6253\u4e2d\u4e86\u811a\u5e95\u7684\u201c\u6d8c\u6cc9\u7a74\u201d\uff0c \u767b\u65f6\u8dcc\u4e86\u4e0b\u6765\uff0c\u4e09\u4eba\u4f1a\u5408\uff0c\u53f6\u5b97\u7559\u594b\u8d77\u795e\u529b\uff0c\u4e00\u5200\u5288\u65ad\u5927\u67f1\uff0c\u5e10\u5e55\u7f69\u4e86\u4e0b\u6765\uff0c\u5c06\u6bd5\u64ce\u5929\u7b49\u4e00 \u5e72\u4eba\u90fd\u7f69\u5728\u91cc\u9762\u3002\u7acb\u523b\u95ef\u51fa\u8425\u95e8\u3002 \u3000\u3000\u8425\u5916\u5343\u519b\u4e07\u9a6c\uff0c\u65e9\u5df2\u5217\u6210\u9635\u52bf\uff0c\u91cd\u91cd\u56f4\u56f0\u3002\u53f6\u5b97\u7559\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4e3a\u6211\u4e00\u4eba\uff0c\u4f55\u987b\u5982 \u6b64\uff1f\u201d\u53cc\u76ee\u4e00\u5f20\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f17\u4f4d\u5144\u5f1f\u542c\u7740\uff0c\u800c\u4eca\u5b98\u519b\u538b\u5883\uff0c\u54b1\u4eec\u56db\u9762\u53d7\u654c\uff0c\u7edd\u4e0d\u80fd\u81ea\u76f8 \u6b8b\u6740\uff0c\u5984\u52a8\u5e72\u6208\uff0c\u6211\u5fb7\u8584\u624d\u758f\uff0c\u4e0d\u80fd\u6276\u52a9\u4f60\u4eec\u7684\u6bd5\u5927\u9f99\u5934\uff0c\u5171\u6210\u5927\u4e8b\uff0c\u5b9e\u6df1\u6709\u6127\uff0c\u5982\u4eca\u5148\u544a \u9000\u4e86\uff0c\u6258\u4f60\u4eec\u5584\u81ea\u4e3a\u4e4b\uff0c\u8425\u4e2d\u6ca1\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4f60\u4eec\u90fd\u6563\u53bb\u5427\uff01\u201d\u5927\u8425\u5916\u7684\u519b\u961f\u5f53\u7136\u90fd\u662f\u6bd5\u64ce \u5929\u7684\u4eba\uff0c\u4eba\u4eba\u90fd\u77e5\u9053\u6bd5\u64ce\u5929\u8981\u5c06\u53f6\u5b97\u7559\u7684\u52bf\u529b\u6d88\u706d\uff0c\u9884\u6599\u5fc5\u6709\u4e00\u573a\u706b\u62fc\uff0c\u5ffd\u542c\u53f6\u5b97\u7559\u53e3\u51fa\u6b64 \u8a00\uff0c\u4e0d\u4f46\u6653\u4ee5\u5927\u4e49\uff0c\u800c\u4e14\u8fd8\u4e3a\u4ed6\u4eec\u7684\u5927\u9f99\u5934\u63a9\u9970\uff0c\u5341\u4eba\u4e2d\u5012\u6709\u4e5d\u4eba\u53d7\u4e86\u611f\u52a8\uff0c\u8f70\u7136\u5927\u547c\u7eb7\u7eb7 \u56db\u6563\uff0c\u4e8e\u627f\u73e0\u6120\u5507\u4e00\u5578\uff0c\u90a3\u5339\u7167\u591c\u72ee\u5b50\u9a6c\u98de\u5954\u800c\u6765\uff0c\u4e8e\u627f\u73e0\u53eb\u9053\uff1a\u201c\u53f6\u4f2f\u4f2f\uff0c\u5feb\u4e0a\u9a6c\uff0c\u54b1\u4eec \u9003\u5230\u5c6f\u6eaa\u53bb\u548c\u6210\u6797\u4f1a\u5408\u3002\u201d\u53f6\u5b97\u7559\u9762\u8272\u4e00\u6c89\u9053\uff1a\u201c\u4f60\u4eec\u5230\u5c6f\u6eaa\u53bb\u544a\u77e5\u6210\u6797\uff0c\u53eb\u4ed6\u4e00\u5fc3\u62b5\u5fa1\u5b98 \u519b\uff0c\u5343\u4e07\u4e0d\u53ef\u4e0e\u6bd5\u64ce\u5929\u706b\u62fc\u3002\u201d\u4e8e\u627f\u73e0\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d\u53f6\u5b97\u7559\u9053\uff1a\u201c\u6211\u53bb\u5de6\u8425\uff01\u201d\u51cc \u4e91\u51e4\u521a\u521a\u9053\u51fa\u201c\u4e0d\u53ef\u201d\u4e24\u5b57\uff0c\u6bd5\u64ce\u5929\u8fd9\u4e00\u5e72\u4eba\u5df2\u63ed\u5f00\u5e10\u5e55\uff0c\u62a2\u4e86\u5feb\u9a6c\uff0c\u8ffd\u4e86\u51fa\u6765\u3002\u90a3\u7167\u591c\u72ee \u5b50\u9a6c\u4e0d\u5f85\u4e3b\u4eba\u5429\u5490\uff0c\u7acb\u523b\u626c\u8e44\u75be\u8dd1\uff0c\u4e8e\u627f\u73e0\u548c\u51cc\u4e91\u51e4\u6025\u5fd9\u4e5f\u62a2\u4e86\u4e24\u5339\u9a6c\uff0c\u7d27\u968f\u7740\u53f6\u5b97\u7559\u95ef \u8425\u3002 \u3000\u3000\u53ea\u89c1\u53f6\u5b97\u7559\u7684\u9a6c\u8e44\u5230\u5904\uff0c\u4f17\u519b\u58eb\u7eb7\u7eb7\u8ba9\u8def\uff0c\u4e07\u9a6c\u5343\u519b\uff0c\u7adf\u65e0\u4eba\u53d1\u51fa\u4e00\u77e2\u3002\u6bd5\u64ce\u5929\u5927\u6012\uff0c \u7387\u4e86\u51e0\u767e\u4eb2\u5175\uff0c\u4eb2\u81ea\u6765\u8ffd\uff0c\u4e8e\u627f\u73e0\u53cd\u624b\u4e00\u626c\uff0c\u53d1\u51fa\u4e24\u679a\u91d1\u82b1\uff0c\u5c04\u4f24\u4e86\u6bd5\u64ce\u5929\u7684\u5750\u9a91\uff0c\u5f85\u4ed6\u518d \u6362\u8fc7\u9a6c\u65f6\uff0c\u4e8e\u627f\u73e0\u548c\u51cc\u4e91\u51e4\u4e5f\u90fd\u95ef\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u53ea\u8fd9\u4e00\u5239\u90a3\u5de5\u592b\uff0c\u90a3\u7167\u591c\u72ee\u5b50\u9a6c\u5df2\u8dd1\u51fa\u6570\u91cc\u4e4b\u5730\u3002\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u6bd5\u64ce\u5929\u8c03\u4e86\u91cd\u5175\u8f6c\u653b\u5de6 \u8425\uff0c\u53f6\u7edf\u9886\u6b64\u53bb\u65e0\u5f02\u81ea\u6295\u7f57\u7f51\uff01\u201d\u4e24\u4eba\u6325\u97ad\u8d76\u9a6c\uff0c\u7aed\u529b\u5954\u9a70\uff0c\u5de6\u8425\u79bb\u5f00\u5927\u8425\u4e0d\u8fc7\u516d\u4e03\u91cc\u4e4b \u5730\uff0c\u8d76\u5230\u4e4b\u65f6\uff0c\u53ea\u89c1\u53f6\u5b97\u7559\u5355\u9a91\u5339\u9a6c\uff0c\u5df2\u9677\u5165\u5305\u56f4\u4e4b\u4e2d\uff0c\u5de6\u8425\u7684\u519b\u58eb\u4e43\u662f\u9093\u8302\u4e03\u7684\u90e8\u961f\uff0c\u672c \u6765\u5c31\u4e0d\u80af\u7f34\u68b0\uff0c\u8fd9\u65f6\u89c1\u53f6\u5b97\u7559\u6765\u5230\uff0c\u90fd\u7a7a\u8425\u800c\u51fa\uff0c\u773c\u89c1\u5c31\u662f\u4e00\u573a\u6df7\u6218\u3002 \u3000\u3000\u4f46\u89c1\u5f97\u53f6\u5b97\u7559\u5728\u5343\u519b\u4e07\u9a6c\u4e4b\u4e2d\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u6ca1\u6709\u4e8b\u513f\uff0c\u5de6\u8425\u5f1f\u5144\u90fd\u56de\u8425\u53bb\u3002\u6211\u81ea\u613f\u89e3\u7532 \u5f52\u7530\uff0c\u4f60\u4eec\u4ee5\u540e\u597d\u597d\u542c\u5927\u9f99\u5934\u7684\u53f7\u4ee4\uff0c\u8fd9\u4e2a\u65f6\u5019\uff0c\u7edd\u4e0d\u5bb9\u81ea\u5bb6\u4eba\u706b\u62fc\uff01\u201d\u767b\u65f6\u51e0\u5343\u519b\u58eb\u90fd\u54ed \u4e86\u8d77\u6765\uff0c\u56f4\u7740\u5de6\u8425\u7684\u5175\u5c06\u9762\u9762\u76f8\u89d1\uff0c\u4e0d\u6562\u52a8\u624b\u3002\u53f6\u5b97\u7559\u4e00\u63d0\u9a6c\u7586\uff0c\u907f\u5f00\u4e86\u8ffd\u4e0a\u6765\u8981\u633d\u7559\u4ed6\u7684 \u4eba\uff0c\u75be\u9a70\u53bb\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u548c\u51cc\u4e91\u51e4\u62cd\u9a6c\u9053\u8d76\uff0c\u4f17\u519b\u58eb\u53d1\u4e00\u58f0\u558a\uff0c\u6709\u90a3\u4e9b\u8ddf\u968f\u53f6\u5b97\u7559\u591a\u5e74\uff0c\u820d\u4e0d\u5f97\u4ed6\u8d70\u7684\uff0c \u4e5f\u8ddf\u7740\u8ffd\u6765\uff0c\u518d\u540e\u9762\u5c31\u662f\u6bd5\u64ce\u5929\u7684\u9a6c\u961f\uff0c\u4f46\u4ed6\u6765\u8fdf\u4e86\u4e00\u6b65\uff0c\u90a3\u4e9b\u5949\u4ed6\u547d\u4ee4\u5305\u56f4\u5de6\u8425\u7684\u519b\u961f\uff0c \u90fd\u5df2\u56db\u6563\u5f00\u6765\uff0c\u6545\u610f\u5792\u585e\u9053\u8def\uff0c\u4e71\u6210\u4e00\u7247\uff0c\u6bd5\u64ce\u5929\u4e0d\u5f97\u4e0d\u4e0b\u9a6c\u9547\u538b\uff0c\u91cd\u6574\u961f\u5f62\uff0c\u773c\u89c1\u53f6\u5b97\u7559 \u7684\u767d\u9a6c\u7edd\u5c18\u800c\u53bb\uff0c\u6beb\u65e0\u529e\u6cd5\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u548c\u51cc\u4e91\u51e4\u7684\u9a6c\u8dd1\u5f97\u6700\u5feb\uff0c\u867d\u7136\u8ffd\u4e0d\u4e0a\u7167\u591c\u72ee\u5b50\u9a6c\uff0c\u4f46\u5df2\u628a\u4f17\u519b\u4e0a\u629b\u5728\u540e\u5934\uff0c\u8ffd\u4e86 \u4e00\u7a0b\uff0c\u4e0d\u89c9\u5230\u4e86\u6d77\u8fb9\uff0c\u8fdc\u8fdc\u770b\u89c1\u4e00\u6761\u5c0f\u8239\u5212\u5230\u5cb8\u8fb9\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u53eb\u9053\uff1a\u201c\u53f6\u4f2f\u4f2f\uff0c\u53f6\u4f2f\u4f2f\uff01\u201d\u53ea\u89c1\u53f6\u5b97\u7559\u4e0b\u9a6c\u4e0a\u8239\uff0c\u5f85\u5979\u4eec\u8d76\u5230\u5cb8\u8fb9\uff0c\u90a3\u8239\u5df2\u5230 \u4e86\u6d77\u4e2d\u5fc3\u4e86\u3002 \u3000\u3000\u53f6\u5b97\u7559\u7acb\u5728\u8239\u5934\uff0c\u5411\u4e8e\u3001\u51cc\u4e24\u4eba\u626c\u624b\u9053\uff1a\u201c\u4f60\u4eec\u9a91\u8fd9\u767d\u9a6c\uff0c\u4e0a\u5c6f\u6eaa\u53bb\u5427\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u53f6\u4f2f\u4f2f\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u56de\u6765\uff01\u201d\u53f6\u5b97\u7559\u9053\uff1a\u201c\u6211\u65e9\u6599\u5230\u6709\u4eca\u65e5\u4e4b\u4e8b\uff0c\u6211\u82e5\u4e0d\u8d70\uff0c\u4e8b\u60c5\u66f4\u8981\u5f04 \u7cdf\uff01\u6743\u8861\u5229\u5bb3\uff0c\u8ba9\u6bd5\u64ce\u5929\u72ec\u63fd\u5927\u6743\uff0c\u603b\u80dc\u4e8e\u81ea\u76f8\u6b8b\u6740\uff0c\u4e24\u8d25\u4ff1\u4f24\uff01\u6bd5\u64ce\u5929\u8fd9\u4eba\u5fc3\u672f\u4e0d\u597d\uff0c\u5374 \u4e5f\u8fd8\u662f\u4e2a\u4eba\u624d\uff01\u4f60\u4eec\u613f\u6276\u52a9\u4ed6\u5c31\u7559\u4e0b\uff0c\u82e5\u4e0d\u80fd\u5171\u4e8b\uff0c\u4e5f\u4e0d\u53ef\u5411\u4ed6\u5bfb\u4ec7\uff01\u201d\u8fd9\u4e00\u6bb5\u8bdd\u8bf4\u5b8c\uff0c\u5c0f \u8239\u4e5f\u53ea\u5269\u4e0b\u4e00\u7247\u5e06\u5f71\u4e86\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u62ed\u4e86\u4e00\u4e0b\u773c\u6cea\uff0c\u9053\uff1a\u201c\u8fd9\u8fd8\u662f\u6211\u79bb\u5f00\u4e86\u5929\u90fd\u4e4b\u540e\u7b2c\u4e00\u6b21\u6d41\u6cea\uff01\u50cf\u53f6\u7edf\u9886\u624d\u662f\u771f\u6b63 \u7684\u5927\u8c6a\u6770\uff0c\u5927\u82f1\u96c4\uff01\u201d\u4e8e\u627f\u73e0\u53f9\u4e86\u53e3\u6c14\u9eef\u7136\u65e0\u8bed\uff0c\u51cc\u4e91\u51e4\u5ffd\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u4f60\u5e2e\u6211\u5e72\u4e00\u4ef6\u4e8b \u60c5\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u627e\u6bd5\u64ce\u5929\u53bb\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u4e5f\u6068\u4e0d\u5f97\u523a\u4ed6\u4e00 \u4e2a\u900f\u660e\u7a9f\u7abf\uff0c\u53ef\u662f\u53f6\u4f2f\u4f2f\u7684\u5429\u5490\u2026\u2026\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8981\u4f60\u6740\u4ed6\uff0c\u6211\u662f\u8981\u4f60\u62a2\u4ed6\u7684\u5175 \u7b26\u3002\u201d\u4e8e\u627f\u73e0\u60f3\u4e86\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u4e3a\u53f6\u6210\u6797\u3002\u201d\u51cc\u4e91\u51e4\u7b11\u9053\uff1a\u201c\u662f\u5440\uff0c\u96be\u9053\u4f60\u5c31\u4e0d\u6302\u5ff5\u4ed6 \u5417\uff1f\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u540e\u9762\u7684\u519b\u9a6c\u5df2\u5c06\u8ffd\u4e0a\uff0c\u51cc\u4e91\u51e4\u62c9\u4e8e\u627f\u73e0\u4e0a\u4e86\u767d\u9a6c\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u65f6\u5019\uff0c\u4ed6 \u7684\u5973\u5175\u5df2\u5728\u6e29\u5dde\u9053\u4e0a\uff0c\u51c6\u5907\u622a\u7cae\u3002\u4f60\u548c\u6211\u4eca\u665a\u62a2\u5230\u5175\u7b26\uff0c\u54b1\u4eec\u4e5f\u6765\u6f14\u4e00\u51fa\u4fe1\u9675\u541b\u6551\u8d75\u7684\u597d \u620f\u3002\u201d \u3000\u3000\u53f6\u5b97\u7559\u4e00\u8d70\uff0c\u5de6\u8425\u7684\u519b\u58eb\u867d\u7136\u4e0d\u80af\u7f34\u68b0\uff0c\u5374\u4e5f\u6ca1\u6709\u53cd\u6297\uff0c\u6bd5\u64ce\u5929\u5927\u529f\u544a\u6210\uff0c\u5f97\u610f\u4e4b\u6781\u3002 \u552f\u4e00\u7f8e\u4e2d\u4e0d\u8db3\u7684\u662f\uff1a\u51cc\u4e91\u51e4\u90a3\u961f\u5973\u5175\uff0c\u65e9\u5df2\u5f00\u8d70\u3002\u60f3\u6765\u4e8e\u627f\u73e0\u4e5f\u662f\u8ddf\u51cc\u4e91\u51e4\u9003\u8d70\u7684\u4e86\u3002\u60f3\u8d77 \u4e8e\u627f\u73e0\u7684\u5bb9\u8c8c\u6b66\u529f\uff0c\u90fd\u662f\u5973\u4e2d\u7f55\u89c1\uff0c\u5c31\u4e0d\u662f\u80af\u5f52\u987a\u81ea\u5df1\uff0c\u6bcf\u4e00\u5ff5\u53ca\uff0c\u60d8\u7136\u82e5\u5931\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u6bd5\u6500\u5929\u548c\u4eb2\u4fe1\u5c5e\u4e0b\u72c2\u6b22\u201c\u795d\u6377\u201d\uff0c\u56de\u5230\u5e10\u4e2d\uff0c\u65e9\u5df2\u6709\u4e86\u51e0\u5206\u9152\u610f\uff0c\u6b63\u5f85\u5b89\u6b47\uff0c\u536b \u58eb\u5ffd\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\u6c42\u89c1\u5927\u9f99\u5934\u3002\u201d\u6bd5\u64ce\u5929\u6115\u4e00\u6115\uff0c\u9053\uff1a\u201c\u5979\u8fd8\u6562\u6765\u89c1\u6211\uff1f\u201d\u60f3\u4e86\u4e00 \u4e0b\uff0c\u5429\u5490\u90a3\u536b\u58eb\u9053\uff1a\u201c\u53eb\u5979\u628a\u4f69\u5251\u89e3\u4e0b\uff0c\u7a7a\u624b\u8fdb\u6765\u3002\u201d\u90a3\u4eb2\u4fe1\u7684\u536b\u571f\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\u8fd9 \u6b21\u524d\u6765\u6c42\u89c1\uff0c\u77a7\u5979\u795e\u60c5\u5012\u662f\u633a\u548c\u6c14\u7684\u3002\u90a3\u628a\u5b9d\u5251\u4e5f\u6ca1\u6709\u5e26\u5728\u8eab\u65c1\u3002\u6240\u4ee5\u5c0f\u7684\u624d\u6562\u64c5\u81ea\u4f5c\u4e3b\uff0c \u8ba9\u5979\u8fdb\u5165\u5927\u8425\u3002\u201d\u6bd5\u64ce\u5929\u7709\u5f00\u773c\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u5979\u4e5f\u8fd8\u61c2\u5f97\u89c4\u77e9\uff0c\u597d\uff0c\u90a3\u5c31\u4f20\u5979\u8fdb\u6765\u3002\u201d \u3000\u3000\u6bd5\u64ce\u5929\u672c\u6765\u5bf9\u4e8e\u627f\u73e0\u5c31\u65e2\u662f\u6e34\u5ff5\uff0c\u53c8\u662f\u60e7\u6015\uff0c\u8fd9\u65f6\u60f3\u9053\uff1a\u201c\u53f6\u5b97\u7559\u5df2\u53bb\uff0c\u83ab\u975e\u5979\u56e0\u6b64\u800c \u56de\u5fc3\u8f6c\u610f\u4e86\u4e48\uff1f\u201d\u867d\u7136\u8fd8\u6709\u4e9b\u5c11\u987e\u5fcc\uff0c\u4f46\u60f3\u4e8e\u627f\u73e0\u7684\u6b66\u529f\u7eb5\u597d\uff0c\u81ea\u5df1\u5c3d\u53ef\u62b5\u6321\u5f97\u4f4f\uff0c\u800c\u4e14\u5979 \u53c8\u6ca1\u6709\u5e26\u5b9d\u5251\uff0c\u66f4\u653e\u4e86\u5fc3\u3002 \u3000\u3000\u5f85\u4e86\u7247\u523b\uff0c\u53ea\u89c1\u4e8e\u627f\u73e0\u7f13\u7f13\u8d70\u8fdb\u5e10\u5e55\uff0c\u4f3c\u5632\u4f3c\u8bbd\u5730\u8bf4\u9053\uff1a\u201c\u5c0f\u5973\u5b50\u4e8e\u627f\u73e0\u53c2\u89c1\u5927\u9f99 \u5934\uff01\u201d\u6bd5\u64ce\u5929\u7b11\u9053\uff1a\u201c\u597d\u5728\u4eca\u65e5\u6ca1\u6709\u88ab\u4f60\u523a\u4e2a\u900f\u660e\u7a9f\u7abf\uff0c\u600e\u4e48\uff0c\u6211\u4ee5\u4e3a\u4f60\u968f\u53f6\u5b97\u7559\u8d70\u4e86 \u5462\u3002\u201d\u4e8e\u627f\u73e0\u51b7\u7b11\u9053\uff1a\u201c\u53f6\u7edf\u9886\u81ea\u613f\u606f\u4e8b\u5b81\u4eba\uff0c\u8ba9\u4f4d\u4e8e\u4f60\uff0c\u4f60\u5982\u4eca\u603b\u8be5\u79f0\u5fc3\u5982\u610f\u4e86\u5427\uff1f\u201d\u6bd5 \u64ce\u5929\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u542c\u6765\u4f60\u597d\u50cf\u5bf9\u6211\u5341\u5206\u4e0d\u6ee1\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e8b\u65e2\u5982\u65af\uff0c\u4f60\u8fd8\u4f55\u7528\u7ba1\u522b\u4eba\u6ee1\u4e0d\u6ee1\u610f\uff1f\u4f60\u8fd9\u5927\u9f99\u5934\u7684\u4f4d\u5b50\u603b\u662f\u5750\u5b9a\u7684\u4e86\u3002 \u54fc\uff0c\u54fc\uff0c\u8981\u4e0d\u662f\u53f6\u7edf\u9886\u518d\u4e09\u529d\u6211\u4ee5\u5927\u5c40\u4e3a\u91cd\uff0c\u4e0d\u8bb8\u4e92\u76f8\u6b8b\u6740\uff0c\u6211\u771f\u6068\u4e0d\u5f97\u523a\u4f60\u4e2a\u900f\u660e\u7a9f \u7abf\uff01\u201d\u6bd5\u64ce\u5929\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5982\u4eca\u5c40\u52bf\u5df2\u5b9a\uff0c\u8bc6\u65f6\u52a1\u8005\u4e3a\u4fca\u6770\uff0c\u4f60\u662f\u5dfe\u5e3c\u82f1\u96c4\uff0c\u5973\u4e2d\u8c6a \u6770\uff0c\u8fd9\u8bdd\uff0c\u8fd9\u8bdd\u672c\u6765\u5c31\u4e0d\u5fc5\u6211\u518d\u8bf4\u4e86\u3002\u4f60\u4eca\u665a\u524d\u6765\u89c1\u6211\uff0c\u6253\u7b97\u5982\u4f55\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u4eca\u540e \u53c8\u6253\u7b97\u5982\u4f55\uff1f\u201d\u6bd5\u64ce\u5929\u5f97\u610f\u4e4b\u6781\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6325\u519b\u5317\u4e0a\uff0c\u53f7\u4eca\u5929\u4e0b\uff0c\u5bb0\u5272\u6cb3\u5c71\uff01\u627f\u73e0\uff0c\u4f60 \u7559\u4e0b\u6765\u5427\uff0c\u7ed9\u6211\u6574\u987f\u5973\u519b\uff0c\u6211\u7edd\u4e0d\u8ba1\u8f83\u65e7\u6068\u3002\u201d\u4e8e\u627f\u73e0\u51b7\u7b11\u9053\uff1a\u201c\u5373\u7b97\u4f60\u4ed6\u65e5\u767b\u57fa\u5f00\u56fd\uff0c\u53ea \u6015\u4e5f\u672a\u5fc5\u80fd\u4ee4\u6211\u79f0\u81e3\u3002\u201d\u5ffd\u5730\u58f0\u8c03\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u53ea\u8981\u4f60\u8981\u60f3\u5f97\u5929\u4e0b\uff0c\u6211\u5012\u53ef\u4ee5\u9001\u4f60\u4e00\u6837\u4e1c \u897f\uff0c\u8ba9\u4f60\u5b8c\u6210\u5fc3\u613f\u3002\u201d\u6bd5\u64ce\u5929\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5f6d\u548c\u5c1a\u6240\u7559\u4e0b\u7684\u90a3\u5e45\u5730\u56fe\uff0c\u4f60 \u5f97\u7684\u90a3\u4efd\uff0c\u4ec5\u662f\u6c5f\u5357\u90e8\u4efd\uff0c\u6211\u8eab\u8fb9\u5e26\u6765\u7684\u4e43\u662f\u5168\u56fe\uff01\u201d \u3000\u3000\u6bd5\u64ce\u5929\u8fd9\u4e00\u559c\u771f\u662f\u51fa\u4e4e\u610f\u5916\uff0c\u4ed6\u76fc\u671b\u5f97\u8fd9\u5730\u56fe\uff0c\u5df2\u4e0d\u77e5\u591a\u5c11\u5e74\u6708\uff0c\u4e0d\u610f\u4e8e\u627f\u73e0\u7adf\u80af\u9001 \u4ed6\uff0c\u4ed6\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u53ea\u542c\u5f97\u4e8e\u627f\u73e0\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u82e5\u975e\u53f6\u7edf\u9886\u7ed9\u4f60\u903c\u8d70\uff0c\u4e49\u519b\u820d \u4f60\u4e4b\u5916\uff0c\u65e0\u4eba\u80fd\u591f\u7edf\u5e26\uff0c\u8fd9\u5730\u56fe\u4e5f\u7edd\u4e0d\u4f1a\u843d\u5728\u4f60\u7684\u624b\u4e2d\u3002\u201d\u6bd5\u64ce\u5929\u4e0e\u4e8e\u627f\u73e0\u76f8\u5904\u591a\u65f6\uff0c\u65e9\u5df2 \u77e5\u9053\u4e8e\u627f\u73e0\u521a\u67d4\u517c\u5907\u7684\u6027\u683c\uff0c\u8981\u662f\u4e8e\u627f\u73e0\u5411\u4ed6\u732e\u5a9a\uff0c\u4ed6\u7edd\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u800c\u4eca\u4e8e\u627f\u73e0\u4e00\u9762\u9a82\u4ed6\uff0c \u4e00\u9762\u5374\u53c8\u4ee5\u5927\u5c40\u4e3a\u91cd\uff0c\u8bf4\u8981\u9001\u4ed6\u5730\u56fe\uff0c\u4ed6\u5fc3\u4e2d\u66f4\u65e0\u534a\u70b9\u6000\u7591\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u5e10\u4e0a\u4f3c\u6709\u8f7b\u5fae\u7684\u58f0\u97f3\uff0c\u82e5\u975e\u843d\u5728\u6bd5\u64ce\u5929\u3001\u4e8e\u627f\u73e0\u8fd9\u6837\u884c\u5bb6\u7684\u8033\u91cc\uff0c\u7aef\u7684\u542c\u4e0d\u51fa \u6765\uff0c\u6bd5\u64ce\u5929\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u4e8e\u627f\u73e0\u5df2\u53d6\u51fa\u5730\u56fe\uff0c\u6162\u6162\u5c55\u5f00\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u9762\u6709\u70b9\u8bb2\u7a76\uff0c \u4f60\u5feb\u8fc7\u6765\u770b\uff0c\u6211\u4e0d\u8010\u70e6\u4e45\u7559\u3002\u201d\u6bd5\u64ce\u5929\u9152\u610f\u9189\u4e86\u51e0\u5206\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u5e10\u5916\u6709\u6bd5\u613f\u7a77\u7b49\u9ad8\u624b\u5de1 \u56de\uff0c\u5927\u8425\u5916\u66f4\u6709\u4e09\u91cd\u5b88\u536b\uff0c\u6599\u53ef\u65e0\u8651\u3002\u89c1\u4e8e\u627f\u73e0\u5c55\u5f00\u5730\u56fe\uff0c\u6025\u5fd9\u51d1\u8fc7\u53bb\u770b\uff0c\u4e8e\u627f\u73e0\u500f\u5730\u4e00\u4e0b \u5c55\u5f00\uff0c\u5730\u56fe\u4e2d\u7adf\u5305\u7740\u4e00\u67c4\u7cbe\u5149\u8000\u773c\u7684\u5315\u9996\uff0c\u539f\u6765\u4e8e\u627f\u73e0\u662f\u5e08\u6cd5\u8346\u8f72\u523a\u79e6\u7687\u7684\u6545\u4e8b\uff0c\u7ed8\u4e86\u4e00\u5e45 \u5047\u56fe\uff0c\u56fe\u7a77\u5315\u89c1\uff0c\u5c31\u8981\u7528\u8fd9\u5315\u9996\u80c1\u8feb\u6bd5\u64ce\u5929\u4ea4\u51fa\u5175\u7b26\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4f46\u89c1\u5315\u9996\u4e00\u95ea\uff0c\u5200\u950b\u5df2\u903c\u8fd1\u6bd5\u64ce\u5929\u7684\u54bd\u5589\uff0c\u6bd5\u64ce\u5929\u5ffd\u5730\u4e00\u53e3\u5411\u4e8e\u627f\u73e0 \u624b\u80cc\u54ac\u4e0b\uff0c\u4e24\u4eba\u52a8\u4f5c\u5747\u5feb\uff0c\u4e8e\u627f\u73e0\u7f29\u624b\u659c\u5200\uff0c\u90a3\u5200\u950b\u521a\u521a\u523a\u51fa\uff0c\u6bd5\u64ce\u5929\u4e00\u4e2a\u201c\u65a9\u9f99\u638c\u201d\u5288 \u4e0b\uff0c\u5f53\u5577\u4e00\u58f0\uff0c\u4e8e\u627f\u73e0\u5315\u9996\u843d\u5730\uff0c\u53ea\u542c\u5f97\u6bd5\u64ce\u5929\u563f\u563f\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u70b9\u4f0e\u4fe9\uff0c\u987b\u7792\u4e0d\u8fc7 \u54b1\u5bb6\u3002\u201d \u3000\u3000\u7b11\u58f0\u672a\u7edd\uff0c\u9661\u7136\u95f4\u53ea\u542c\u5f97\u4e00\u58f0\u88c2\u5e1b\uff0c\u5e10\u5e55\u4e0a\u7a7f\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u6bd5\u64ce\u5929\u672a\u53ca\u56de\u5934\uff0c\u80cc\u5fc3\u5df2\u7ed9 \u4e00\u67c4\u957f\u5251\u62b5\u4f4f\uff0c\u6765\u7684\u4e43\u662f\u51cc\u4e91\u51e4\uff0c\u5979\u8d81\u7740\u4e8e\u627f\u73e0\u4e0e\u6bd5\u64ce\u5929\u7ea0\u7f20\u4e4b\u9645\uff0c\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u7adf\u7136\u795e \u4e0d\u77e5\u9b3c\u4e0d\u89c9\u5730\u5077\u8fdb\u4e86\u6bd5\u64ce\u5929\u7684\u5e05\u5e10\u3002\u5979\u62ff\u7684\u4e43\u662f\u4e8e\u627f\u73e0\u90a3\u628a\u9752\u51a5\u5b9d\u5251\uff0c\u5251\u6c14\u68ee\u68ee\uff0c\u62b5\u7740\u540e \u5fc3\uff0c\u5bd2\u6c14\u76f4\u900f\u4e0a\u6bd5\u64ce\u5929\u7684\u5fc3\u5934\uff01\u6b63\u662f\uff1a \u3000\u3000\u6b32\u4ed7\u9f99\u6cc9\u4e09\u5c3a\u5251\uff0c\u76d7\u7b26\u622a\u664c\u6551\u82f1\u8c6a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff0c\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e09\u5341\u56de\u3000\u864e\u5e10\u76d7\u7b26\u3000\u519b\u4e2d\u4f24\u60e8\u53d8\u3000\u5f81\u978d\u89e3\u5251\u3000\u9053\u4e0a\u8d60\u5609\u8a00 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e09\u5341\u56de\u3000\u864e\u5e10\u76d7\u7b26\u3000\u519b\u4e2d\u4f24\u60e8\u53d8\u3000\u5f81\u978d\u89e3\u5251\u3000\u9053\u4e0a\u8d60\u5609\u8a00 \u3000\u3000\u6bd5\u64ce\u5929\u5728\u51cc\u4e91\u51e4\u5b9d\u5251\u5a01\u80c1\u4e4b\u4e0b\uff0c\u5b9b\u5982\u6597\u8d25\u4e86\u7684\u516c\u9e21\uff0c\u5782\u9996\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u4ec0\u4e48\uff1f\u201d\u51cc\u4e91\u51e4 \u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u628a\u4f60\u7684\u5175\u7b26\u62ff\u51fa\u6765\uff01\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u597d\uff0c\u5175\u7b26\u5728\u6211\u6000\u4e2d\uff0c\u5f85\u6211\u7ed9\u4f60\u4fbf\u662f\uff01\u201d\u8bf4 \u8bdd\u4e4b\u95f4\uff0c\u4f38\u624b\u4f5c\u638f\u6478\u4e4b\u72b6\uff0c\u5ffd\u7136\u6a2a\u8098\u4e00\u649e\uff0c\u4e00\u8bb0\u201c\u8131\u62b1\u89e3\u7532\u201d\uff0c\u53cd\u624b\u64d2\u62ff\uff0c\u4ed6\u6599\u5f97\u51cc\u4e91\u51e4\u4e0d \u6562\u6740\u4ed6\uff0c\u8fd9\u4e00\u62db\u5192\u9669\u65bd\u4e3a\uff0c\u7adf\u4f7f\u51fa\u4e86\u6781\u72e0\u8fa3\u7684\u5c0f\u5929\u661f\u64d2\u62ff\u624b\u6cd5\u3002 \u3000\u3000\u4f46\u542c\u5f97\u201c\u5f53\u5577\u201d\u4e00\u58f0\uff0c\u51cc\u4e91\u51e4\u624b\u4e2d\u7684\u5b9d\u5251\uff0c\u7ed9\u4ed6\u5288\u843d\uff0c\u6bd5\u64ce\u5929\u6b63\u60f3\u5f20\u53e3\u5927\u547c\uff0c\u5374\u60f3\u4e0d\u5230 \u4e8e\u627f\u73e0\u51fa\u624b\u66f4\u5feb\uff0c\u5c31\u5728\u4ed6\u5288\u843d\u51cc\u4e91\u51e4\u5b9d\u5251\u8fd9\u4e00\u95f4\uff0c\u62cd\u7684\u4e00\u638c\u6253\u51fa\uff0c\u4e8b\u60c5\u7d27\u8feb\uff0c\u65e0\u6687\u8003\u8651\uff0c\u8fd9 \u4e00\u638c\u7adf\u662f\u5168\u529b\u65bd\u4e3a\uff0c\u4f7f\u51fa\u4e86\u201c\u7384\u529f\u8981\u8bc0\u201d\u4e2d\u62cd\u7a74\u7684\u72ec\u95e8\u529f\u592b\uff0c\u4e00\u638c\u62cd\u4e0b\uff0c\u5c01\u95ed\u4e86\u6bd5\u64ce\u5929\u7684\u4e03 \u9053\u5927\u7a74\uff0c\u5373\u7b97\u4ed6\u6b66\u529f\u518d\u9ad8\u5341\u500d\uff0c\u4ea6\u5df2\u65e0\u529b\u52a8\u5f39\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u53ae\u771f\u662f\u597d\u72e1\u51f6\u72e0\uff01\u201d\u556a\u7684\u4e00\u638c\u6253\u4e86\u4ed6\u4e00\u8bb0\u8033\u5149\uff0c\u6bd5\u64ce\u5929\u53cc\u775b\u55b7\u706b\uff0c \u5fc3\u5934\u6012\u6781\uff0c\u5374\u662f\u558a\u4e0d\u51fa\u6765\u3002\u51cc\u4e91\u51e4\u641c\u4ed6\u8eab\u4e0a\uff0c\u4e0d\u89c1\u5175\u7b26\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u627f\u73e0\u59b9\u59b9\uff0c\u8fd9\u5175\u7b26\u5b9a \u5728\u5e10\u4e2d\uff0c\u6211\u7ed9\u4f60\u628a\u98ce\uff0c\u4f60\u8d76\u5feb\u641c\u5bfb\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u628a\u6bd5\u64ce\u5929\u7684\u673a\u5bc6\u6863\u6848\uff0c\u7ffb\u4e86\u6ee1\u5730\uff0c\u53ea\u662f\u4e0d\u89c1\u5175\u7b26\uff0c\u5fc3\u4e2d\u7126\u6025\u4e4b\u6781\u3002\u5ffd\u542c\u5f97\u5e10\u5916\u4eba \u58f0\u5608\u6742\uff0c\u6709\u4e00\u4e2a\u6781\u719f\u8bc6\u7684\u58f0\u97f3\u5927\u53eb\u9053\uff1a\u201c\u6bd5\u64ce\u5929\u4f60\u6446\u4ec0\u4e48\u67b6\u5b50\uff0c\u6562\u4e0d\u89c1\u6211\uff0c\u4e8e\u59d1\u5a18\uff0c\u662f\u6211\u6765 \u4e86\uff0c\u4f60\u5feb\u51fa\u6765\u5440\uff01\u201d \u3000\u3000\u8fd9\u4eba\u7adf\u7136\u662f\u94c1\u955c\u5fc3\uff01\u60f3\u4e0d\u5230\u4ed6\u4e5f\u5728\u8fd9\u6df1\u591c\uff0c\u95ef\u8425\u6c42\u89c1\uff0c\u4e8e\u627f\u73e0\u987f\u65f6\u5446\u4e86\uff0c\u51cc\u4e91\u51e4\u5fd9\u9053\uff1a \u201c\u5feb\u641c\uff0c\u5feb\u641c\uff01\u201d\u4e8e\u627f\u73e0\u5fc3\u5934\u4e00\u9192\uff0c\u5ffd\u7136\u60f3\u8d77\u6bd5\u64ce\u5929\u4e3a\u4eba\u8c8c\u4f3c\u7c97\u8c6a\uff0c\u5b9e\u751a\u7cbe\u7ec6\uff0c\u8fd9\u5175\u7b26\u5e94\u85cf \u5728\u8eab\u4e0a\u3002\u5982\u4eca\u65e2\u4e0d\u5728\u8eab\u4e0a\uff0c\u4e5f\u5b9a\u5f53\u5728\u79bb\u8eab\u6700\u8fd1\u7684\u5730\u65b9\uff0c\u60f3\u8d77\u8fdb\u5e10\u4e4b\u65f6\uff0c\u4ed6\u5df2\u5378\u4e0b\u5916\u8863\uff0c\u5373\u5c06 \u6b47\u606f\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4f38\u624b\u5230\u5e8a\u4e0a\u7684\u6795\u5934\u5e95\u4e0b\u4e00\u6478\uff0c\u7ffb\u8d77\u4e86\u4e00\u4ef6\u5916\u8863\uff0c\u679c\u7136\u5e95\u4e0b\u538b\u7740\u4e00\u5757\u91d1\u724c\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5927\u559c\u53eb\u9053\uff1a\u201c\u5175\u7b26\u5230\u624b\u5566\uff01\u201d\u53ea\u542c\u5f97\u5e10\u5916\u567c\u556a\u4e24\u58f0\uff0c\u94c1\u955c\u5fc3\u5927\u559d\u9053\uff1a\u201c\u6bd5\u64ce\u5929\uff0c \u4f60\u518d\u4e0d\u653e\u4e8e\u59d1\u5a18\u51fa\u6765\uff0c\u6211\u53ef\u8981\u52a8\u624b\u5566\uff01\u201d\u4f3c\u662f\u6709\u4e24\u4e2a\u4eba\u5df2\u7ed9\u4ed6\u63a8\u5012\u5730\u4e0a\u3002 \u3000\u3000\u4f46\u89c1\u5e10\u5e55\u4e00\u63ed\uff0c\u6bd5\u64ce\u5929\u7684\u4f8d\u536b\u961f\u957f\u95ef\u4e86\u8fdb\u6765\uff0c\u519b\u961f\u7eaa\u5f8b\u68ee\u4e25\uff0c\u672c\u6765\u4e0d\u5f97\u6bd5\u64ce\u5929\u7684\u5429\u5490\uff0c \u8c01\u4e5f\u4e0d\u6562\u8fdb\u5165\u5e05\u5e10\uff0c\u4f46\u8fd9\u4e2a\u5f85\u536b\u961f\u957f\u540d\u53eb\u987e\u5b5f\u7ae0\uff0c\u5f53\u5e74\u662f\u548c\u6bd5\u64ce\u5929\u540c\u65f6\u79f0\u96c4\u9f50\u9c81\u7684\u5927\u76d7\uff0c\u7d20 \u5f97\u6bd5\u64ce\u5929\u4fe1\u4efb\uff0c\u4e3a\u4eba\u4e5f\u5de5\u5fc3\u8ba1\uff0c\u89c1\u94c1\u955c\u5fc3\u5728\u5916\u9762\u5927\u56b7\u5927\u95f9\uff0c\u6bd5\u64ce\u5929\u7adf\u7136\u6beb\u65e0\u58f0\u606f\uff0c\u5fc3\u77e5\u5b9a\u6709 \u8e4a\u8df7\uff0c\u6043\u7740\u548c\u6bd5\u64ce\u5929\u79f0\u5144\u9053\u5f1f\u5df2\u60ef\uff0c\u8fdb\u6765\u7980\u9053\uff1a\u201c\u94c1\u955c\u5fc3\u5b9a\u8981\u6c42\u89c1\uff0c\u8bf7\uff0c\u8bf7\u5927\u9f99\u5934\u2026\u2026\u201d \u201c\u5b9a\u593a\u201d\u4e24\u5b57\u5c1a\u672a\u51fa\u53e3\uff0c\u5df2\u662f\u77a5\u89c1\u6bd5\u64ce\u5929\u7ed9\u5236\u4f4f\u7a74\u9053\u7684\u90a3\u526f\u602a\u72b6\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u51cc\u4e91\u51e4 \u90a3\u4e00\u5251\u4e5f\u5df2\u5288\u9762\u65a9\u5230\u3002 \u3000\u3000\u987e\u5b5f\u7ae0\u6b66\u529f\u751a\u9ad8\uff0c\u8fd9\u4e00\u5251\u7adf\u7136\u7ed9\u4ed6\u907f\u8fc7\uff0c\u968f\u624b\u4e00\u62db\u201c\u5206\u6d2a\u65ad\u6d41\u201d\uff0c\u547c\u547c\u4e24\u58f0\uff0c\u5de6\u51fb\u51cc\u4e91 \u51e4\uff0c\u53f3\u51fb\u4e8e\u627f\u73e0\u3002\u51cc\u3001\u4e8e\u4e24\u4eba\u5c82\u80af\u4e0e\u4ed6\u5728\u5e10\u4e2d\u6df7\u6218\uff0c\u51cc\u4e91\u51e4\u4e00\u5251\u6311\u5f00\u5e10\u5e55\uff0c\u4e8e\u627f\u73e0\u7acb\u523b\u4e00\u628a \u91d1\u82b1\u6d12\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u90a3\u4e9b\u4eba\u89c1\u8bc6\u8fc7\u91d1\u82b1\u7684\u5389\u5bb3\uff0c\u91d1\u5149\u95ea\u5904\uff0c\u7eb7\u7eb7\u8eb2\u907f\uff0c\u4e8e\u627f\u73e0\u548c\u51cc\u4e91\u51e4\u95ef\u4e86\u51fa\u6765\uff0c\u62ac\u5934\u4e00 \u671b\uff0c\u4f46\u89c1\u94c1\u955c\u5fc3\u5df2\u88ab\u51e0\u4e2a\u9ad8\u624b\u56f4\u4f4f\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u62fc\u547d\u51b2\u523a\u3002\u4e8e\u627f\u73e0\u7981\u4e0d\u4f4f\u5fc3\u5185\u4e00\u9178\uff0c\u60f3\u9053\uff1a \u201c\u6211\u53ea\u5f53\u4ed6\u5728\u6c90\u56fd\u516c\u5e9c\u5185\u8d2a\u604b\u7e41\u534e\uff0c\u5374\u539f\u6765\u4ed6\u8fd8\u60e6\u8bb0\u7740\u6211\uff01\u201d\u8fd9\u4e2a\u65f6\u5019\uff0c\u5979\u54ea\u91cc\u8fd8\u4f1a\u60f3\u53ca\u94c1 \u955c\u5fc3\u60f9\u4eba\u8ba8\u538c\u7684\u5730\u65b9\uff0c\u6025\u5fd9\u633a\u5251\u6251\u4e0a\uff0c\u7ed9\u4ed6\u89e3\u56f4\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u5f97\u89c1\u4e8e\u627f\u73e0\uff0c\u5982\u83b7\u81f3\u5b9d\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\uff0c\u6211\u53c8\u6765\u4e86\uff0c\u54b1\u4eec\u5feb\u8df3\u51fa\u8fd9\u4e2a\u662f\u975e \u4e4b\u5730\u5427\uff0c\u522b\u7406\u90a3\u4e2a\u6bd5\u64ce\u5929\u4e86\uff01\u201d\u4e00\u65f6\u72c2\u559c\uff0c\u5251\u62db\u9732\u51fa\u7a7a\u9699\uff0c\u80a9\u5934\u7740\u4e86\u6bd5\u613f\u7a77\u4e00\u68d2\uff0c\u8bf4\u65f6\u8fdf\uff0c \u90a3\u65f6\u5feb\uff0c\u987e\u76c2\u7ae0\u5df2\u6251\u4e86\u51fa\u6765\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5927\u9f99\u5934\u53d7\u4e86\u6697\u7b97\uff0c\u8fd9\u4e09\u4e2a\u4eba\u4e00\u4e2a\u4e5f\u4e0d\u80fd\u653e\u8d70\uff01\u201d \u5530\u7684\u4e00\u97ad\uff0c\u626b\u5230\u4e8e\u627f\u73e0\u80cc\u540e\uff0c\u987e\u5b5f\u7ae0\u6b66\u529f\u8d85\u5353\uff0c\u8fd9\u4e00\u97ad\u903c\u5f97\u4e8e\u627f\u73e0\u56de\u8eab\u62db\u67b6\uff0c\u54ea\u77e5\u987e\u5b5f\u7ae0\u4e43 \u662f\u58f0\u4e1c\u51fb\u897f\uff0c\u4ed6\u7684\u864e\u5c3e\u957f\u8fbe\u4e00\u4e08\uff0c\u8f7b\u8f7b\u4e00\u6296\uff0c\u97ad\u68a2\u500f\u5730\u8f6c\u4e86\u4e00\u4e2a\u65b9\u5411\uff0c\u94c1\u955c\u5fc3\u6b63\u5411\u7740\u4e8e\u627f\u73e0 \u7684\u65b9\u5411\u98de\u8eab\u7eb5\u8d77\uff0c\u88ab\u957f\u97ad\u4e00\u5377\uff0c\u201c\u535c\u901a\u201d\u4e00\u58f0\uff0c\u8dcc\u843d\u5730\u4e0b\uff0c\u7acb\u65f6\u6709\u4eba\u8fc7\u6765\uff0c\u5c06\u4ed6\u64d2\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5927\u6012\uff0c\u5530\u5530\u4e24\u5251\uff0c\u6b3a\u8eab\u76f4\u8fdb\uff0c\u5728\u987e\u5b5f\u7ae0\u957f\u97ad\u98de\u821e\u4e4b\u4e0b\uff0c\u5c55\u5f00\u4e86\u4e00\u6d3e\u51cc\u538b\u7684\u8fdb\u624b\u62db \u6570\uff0c\u987e\u5b5f\u7ae0\u6b63\u8981\u5c06\u5979\u7f20\u4f4f\uff0c\u89c1\u5979\u62fc\u547d\uff0c\u6070\u5408\u5fc3\u610f\uff0c\u957f\u97ad\u6325\u52a8\uff0c\u6025\u5fd9\u62a2\u4e0a\uff0c\u5148\u5c01\u4f4f\u4e86\u4e8e\u627f\u73e0\u7684 \u9000\u8def\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u53eb\u9053\uff1a\u201c\u73e0\u59b9\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u8fd8\u4e0d\u5feb\u8d70\uff01\u201d\u4e8e\u627f\u73e0\u9661\u7136\u9192\u8d77\u4e86\u81ea\u5df1\u5df2\u593a\u4e86\u5175\u7b26\uff0c\u518d \u4e0d\u9003\u8d70\uff0c\u5c31\u8981\u8bef\u4e86\u5927\u4e8b\uff0c\u53ef\u662f\u94c1\u955c\u5fc3\u4e3a\u5979\u800c\u6765\uff0c\u5979\u600e\u5fcd\u820d\u4e86\u94c1\u955c\u5fc3\u72ec\u53bb\u3002 \u3000\u3000\u51cc\u4e91\u98ce\u89c1\u52bf\u5371\u6025\uff0c\u7ffb\u8eab\u6740\u4eba\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u7acb\u523b\u628a\u4e00\u540d\u536b\u58eb\u7684\u780d\u5c71\u5200\u524a\u65ad\uff0c\u987e \u5b5f\u7ae0\u89c1\u5979\u6765\u5f97\u52bf\u731b\uff0c\u6325\u97ad\u4e00\u63a5\uff0c\u53ea\u89c1\u5251\u5149\u95ea\u5904\uff0c\u90a3\u6761\u864e\u5c3e\u97ad\u53c8\u65ad\u4e86\u4e00\u622a\uff0c\u539f\u6765\u51cc\u4e91\u51e4\u624b\u4e2d\u62ff \u7684\u4e43\u662f\u4e8e\u627f\u73e0\u7684\u9752\u51a5\u5b9d\u5251\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u7684\u5251\u6cd5\u867d\u4e0d\u82e5\u4e8e\u627f\u73e0\u7cbe\u5999\uff0c\u4f46\u5947\u8be1\u72e0\u8fa3\uff0c\u5374\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d\u53ca\uff0c\u66f4\u517c\u5b9d\u5251\u5728\u624b\uff0c\u5982 \u864e\u6dfb\u7ffc\uff0c\u5530\uff0c\u5530\uff0c\u5530\uff0c\u8fde\u8fdb\u51e0\u5251\uff0c\u9664\u4e86\u987e\u5b5f\u7ae0\u7b49\u51e0\u540d\u9ad8\u624b\u8eb2\u95ea\u5f97\u5f00\u4e4b\u5916\uff0c\u5916\u56f4\u653b\u5979\u4eec\u7684\u4e09\u540d \u536b\u58eb\uff0c\u90fd\u88ab\u51cc\u4e91\u51e4\u7684\u5251\u5c16\u523a\u4e2d\u4e86\u7a74\u9053\uff0c\u6eda\u5728\u5730\u4e0a\u722c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u4e0e\u4e8e\u627f\u73e0\u6740\u51fa\u91cd\u56f4\uff0c\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u63a5\u8fde\u8df3\u8fc7\u4e09\u91cd\u5e10\u5e55\uff0c\u4e8e\u627f\u73e0\u56de\u5934\u4e00\u671b\uff0c\u94c1\u955c \u5fc3\u5df2\u88ab\u7f1a\u5165\u6bd5\u64ce\u5929\u7684\u5e10\u4e2d\uff0c\u4e0d\u89c9\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u4e24\u4eba\u9003\u51fa\u5927\u8425\uff0c\u8de8\u4e0a\u5b9d\u9a6c\uff0c\u4e0d\u6d88\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5c31\u5df2\u8dd1\u51fa\u4e94\u5341\u91cc\u5916\uff0c\u79bb\u5f00\u4e86\u6bd5\u64ce\u5929\u9a7b\u519b\u7684\u8303 \u56f4\u4e86\u3002\u51cc\u4e91\u51e4\u677e\u4e86\u53e3\u6c14\uff0c\u5728\u9a6c\u80cc\u4e0a\u56de\u5934\u4e00\u671b\uff0c\u4f46\u89c1\u4e8e\u627f\u73e0\u8138\u4e0a\u6709\u51e0\u70b9\u6cea\u75d5\uff0c\u51cc\u4e91\u51e4\u5fc3\u4e2d\u4e00 \u52a8\uff0c\u9053\uff1a\u201c\u8d24\u59b9\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u90a3\u4e2a\u5c11\u5e74\u662f\u4ec0\u4e48 \u4eba\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u662f\u94c1\u955c\u5fc3\u3002\u201d\u51cc\u4e91\u51e4\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u5fa1\u53f2\u94c1\u94f1\u7684\u513f\u5b50\uff0c\u6211\u4e5f\u542c\u8bf4\u8fc7\u4ed6\u7684 \u540d\u5b57\uff0c\u8bf4\u4ed6\u4e00\u8868\u4eba\u624d\uff0c\u679c\u7136\u4e0d\u9519\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9762\u4e0a\u4e00\u7ea2\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u53ef\u60dc\u94c1\u955c\u5fc3\u7a7a\u81ea\u751f\u4e86\u4e00\u526f\u5927\u597d\u76ae\u56ca\uff0c\u54ea\u53ca\u5f97\u4e0a\u53f6\u5b97\u7559\u53d4 \u4f84\u7684\u82f1\u96c4\u6c14\u6982\uff01\u201d\u51cc\u4e91\u51e4\u77a7\u5979\u795e\u8272\uff0c\u89c1\u5979\u4e45\u4e45\u4e0d\u8bed\uff0c\u5fc3\u4e2d\u5927\u7591\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u8d24\u59b9\uff0c\u4f60\u53ef\u662f \u6709\u4ec0\u4e48\u5fc3\u4e8b\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u5ffd\u5730\u638f\u51fa\u5175\u7b26\uff0c\u8bf4\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u622a\u4e86\u7cae\u8349\uff0c\u9001\u5230\u5c6f\u6eaa\u7ed9\u53f6\u6210\u6797 \u5427\uff0c\u6211\u4e0d\u53bb\u4e86\u3002\u201d\u51cc\u4e91\u51e4\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u53bb\u89c1\u53f6\u6210\u6797\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u55ef\uff0c\u6709\u4e86\u8fd9\u9053\u5175\u7b26\uff0c\u8fd0 \u7cae\u5b98\u4e0d\u6562\u8fdd\u62d7\u4f60\u7684\u547d\u4ee4\uff0c\u4f60\u5750\u6211\u8fd9\u5339\u7167\u591c\u72ee\u5b50\u9a6c\uff0c\u5148\u5728\u6e29\u5dde\u9053\u4e0a\u622a\u7cae\uff0c\u518d\u4e1c\u4e0b\u5c6f\u6eaa\uff0c\u5373\u7b97\u6bd5 \u64ce\u5929\u6d3e\u4eba\u62e6\u963b\uff0c\u4ea6\u662f\u8ffd\u4f60\u4e0d\u53ca\uff0c\u6211\u53bb\u4e5f\u5e2e\u4e0d\u4e86\u4f60\u7684\u5fd9\u3002\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u53ef\u662f\u8981\u56de\u8f6c\u5927 \u8425\uff0c\u6551\u90a3\u94c1\u955c\u5fc3\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u4e3a\u6211\u800c\u6765\uff0c\u6211\u5c82\u53ef\u8ba9\u4ed6\u843d\u5728\u6bd5\u64ce\u5929\u624b\u4e0a\uff1f\u6211\u81ea\u6709 \u4e07\u5168\u4e4b\u7b56\u6551\u4ed6\uff0c\u59d0\u59d0\u4f46\u8bf7\u653e\u5fc3\u3002\u201d \u3000\u3000\u672c\u6765\u5728\u8fd9\u6837\u60c5\u5f62\u4e4b\u4e0b\uff0c\u4e8e\u627f\u73e0\u8981\u53bb\u6551\u94c1\u955c\u5fc3\u90a3\u4e5f\u662f\u51fa\u4e8e\u60c5\u7406\u4e4b\u5e38\uff0c\u4f46\u4e8e\u627f\u73e0\u90a3\u8138\u4e0a\u7684\u6cea \u75d5\uff0c\u90a3\u5931\u795e\u7684\u773c\u8272\uff0c\u8fde\u7740\u90a3\u4e0d\u81ea\u7981\u800c\u6d41\u9732\u51fa\u6765\u7684\u5f77\u7bc1\uff0c\u5df2\u662f\u77a7\u5728\u51cc\u4e91\u51e4\u773c\u5185\uff0c\u51cc\u4e91\u51e4\u4e5f\u4e0d\u7981 \u60f6\u60d1\u4e0d\u5b89\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u53ea\u9053\u5979\u548c\u53f6\u6210\u6797\u662f\u4e00\u5bf9\u98ce\u5c18\u4fa0\u4fa3\uff0c\u5f7c\u6b64\u6709\u60c5\uff0c\u96be\u9053\u7adf\u662f\u6211\u4ee5\u524d\u770b \u9519\u4e86\uff0c\u96be\u9053\u5979\u7684\u5fc3\u4e0a\u4eba\u7adf\u7136\u4e0d\u662f\u53f6\u6210\u6797\u800c\u662f\u94c1\u955c\u5fc3\uff1f\u201d\u4f46\u89c9\u4e8e\u627f\u73e0\u820d\u5f03\u4e86\u53f6\u6210\u6797\u8fd9\u6837\u7684\u4eba\uff0c \u6b8a\u4e3a\u53ef\u60dc\uff0c\u8bd5\u63a2\u95ee\u9053\uff1a\u201c\u53f6\u6210\u6797\u5b64\u5b88\u5728\u5916\uff0c\u5904\u5883\u8270\u5371\uff0c\u8d24\u59b9\uff0c\u4f60\u5c31\u4e0d\u6302\u5ff5\u4ed6\u4e48\uff1f\u201d\u4e8e\u627f\u73e0 \u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u52bf\u96be\u517c\u987e\uff0c\u53ea\u6709\u5206\u5f00\u6765\u505a\uff0c\u4f60\u4e0a\u5c6f\u6eaa\uff0c\u6211\u56de\u5927\u8425\uff0c\u5404\u5c3d\u4e00\u4efd\u5fc3\u529b\u3002\u53f6\u6210\u6797 \u6709\u59d0\u59d0\u76f8\u52a9\uff0c\u6211\u653e\u5fc3\u5f97\u5f88\uff01\u201d\u773c\u5708\u4e00\u7ea2\uff0c\u5ffd\u5730\u7ffb\u8eab\u4e0b\u9a6c\u8fd4\u56de\u539f\u8def\u53bb\u4e86\u3002\u82e5\u5728\u5e73\u65f6\uff0c\u51cc\u4e91\u51e4\u662f \u8981\u8ffd\u4e0a\u5979\u548c\u5979\u7ec6\u8c08\u5fc3\u66f2\uff0c\u53ef\u662f\u60c5\u51b5\u5982\u65af\uff0c\u6551\u4eba\u5982\u540c\u6551\u706b\uff0c\u53c8\u54ea\u5bb9\u5f97\u5979\u59d0\u59b9\u7ec6\u7ec6\u8c08\u5fc3\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u54ea\u91cc\u77e5\u9053\uff0c\u4e8e\u627f\u73e0\u6b64\u9645\u6b63\u662f\u5fc3\u4e2d\u5982\u7ede\uff01\u5979\u8ba9\u51cc\u4e91\u51e4\u72ec\u81ea\u53bb\u52a9\u53f6\u6210\u6797\uff0c\u5b9e\u662f\u542b\u6709\u8fd9 \u6837\u7684\u5fc3\u610f\uff1a\u8981\u628a\u53f6\u6210\u6797\u8ba9\u7ed9\u5979\uff01\u867d\u7136\u8fd9\u5fc3\u610f\u65e9\u5728\u8299\u84c9\u5c71\u4e4b\u65f6\uff0c\u4e8e\u627f\u73e0\u542c\u5f97\u970d\u5929\u90fd\u6b7b\u8baf\u90a3\u665a\u5c31 \u5df2\u6709\u4e86\uff0c\u53ef\u662f\u5982\u4eca\u624d\u662f\u5979\u5728\u5fc3\u4e2d\u4f5c\u4e86\u6700\u575a\u51b3\u7684\u5272\u820d\uff0c\u8981\u628a\u5979\u5bf9\u53f6\u6210\u6797\u6b63\u5728\u840c\u8d77\u7684\u7231\u82d7\u62d4\u6389\uff0c \u8fd9\u5bf9\u4e8e\u4e00\u4e2a\u5341\u516b\u5c81\u7684\u5c11\u5973\uff0c\u771f\u662f\u6700\u6b8b\u5fcd\u7684\u727a\u7272\uff0c\u4e5f\u9700\u8981\u6700\u575a\u5f3a\u7684\u52c7\u6c14\uff01\u5509\uff0c\u53ef\u601c\u5979\u5c0f\u5c0f\u5e74 \u7eaa\uff0c\u5c31\u63a5\u8fde\u906d\u53d7\u4e86\u4e24\u6b21\u7231\u60c5\u4e0a\u7684\u78e8\u6298\uff0c\u800c\u8fd9\u4e00\u6b21\u7684\u78e8\u6298\uff0c\u6bd4\u8d77\u4e0a\u4e00\u6b21\u6765\uff0c\u66f4\u751a\u51e0\u5343\u4e07\u500d\uff01\u56e0 \u4e3a\u5979\u5df2\u7ecf\u4ece\u5fc3\u5e95\u91cc\u7231\u4e0a\u4e86\u53f6\u6210\u6797\uff0c\u800c\u94c1\u955c\u5fc3\u5374\u8fd8\u6ca1\u6709\u95ef\u8fdb\u5979\u7684\u5fc3\u6249\uff0c\u4ec5\u4ec5\u662f\u60c5\u6d77\u6ce2\u6d9b\u4e2d\u7684\u4e00 \u7247\u6d6e\u5149\u63a0\u5f71\u3002\u53f6\u6210\u6797\u50cf\u5927\u9752\u6811\u4e00\u6837\u624e\u4e0b\u4e86\u6839\uff0c\u800c\u94c1\u955c\u5fc3\u5219\u4e0d\u8fc7\u50cf\u82b1\u76c6\u4e2d\u76db\u5f00\u7684\u73ab\u7470\uff0c\u7231\u60c5\u7684 \u6839\u82d7\u5e76\u4e0d\u662f\u79cd\u690d\u5728\u6df1\u539a\u7684\u571f\u58e4\u4e0a\uff01 \u3000\u3000\u518d\u8bf4\u6bd5\u64ce\u5929\u88ab\u4e8e\u627f\u73e0\u7528\u91cd\u624b\u6cd5\u5c01\u95ed\u4e86\u4e03\u9053\u5927\u7a74\uff0c\u4ed7\u7740\u7cbe\u7eaf\u7684\u529f\u592b\uff0c\u7ecf\u8fc7\u6574\u6574\u4e00\u665a\uff0c\u867d\u7136 \u80fd\u901a\u4e86\u4e09\u5904\u7a74\u9053\uff0c\u4e5f\u80fd\u591f\u52a8\u5f39\u548c\u5f00\u53e3\u8bf4\u8bdd\u4e86\uff0c\u53ef\u662f\u90a3\u7487\u7391\u3001\u4e2d\u5e9c\u3001\u5929\u9600\u3001\u5730\u85cf\u56db\u5904\u5927\u7a74\u8fd8\u6ca1 \u6709\u89e3\u5f00\uff0c\u800c\u4e14\u4e8e\u627f\u73e0\u7684\u95ed\u7a74\u6cd5\u4e43\u662f\u201c\u7384\u529f\u8981\u8bc0\u201d\u4e2d\u6781\u79d8\u5965\u7684\u95ed\u7a74\u65b9\u6cd5\uff0c\u82e5\u4e0d\u662f\u4f1a\u5bb6\u6765\u89e3\uff0c\u7eb5 \u80fd\u5f3a\u884c\u8fd0\u6c14\u51b2\u5173\uff0c\u4e5f\u8981\u843d\u4e2a\u534a\u8eab\u6b8b\u5e9f\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u81ea\u7136\u77e5\u9053\u8fd9\u4e2a\u9053\u7406\uff0c\u60f3\u8d77\u5c06\u6765\u7eb5\u7136\u80fd\u591f\u79f0\u738b\u79f0\u5e1d\uff0c\u8fd9\u6b8b\u5e9f\u7684\u7f3a\u9677\u4ea6\u662f\u65e0\u53ef\u8865\u507f\uff0c \u5fc3\u4e2d\u4e0d\u89c9\u51c9\u4e86\u534a\u622a\u3002\u4ed6\u7684\u624b\u4e0b\u5c1a\u672a\u77e5\u9053\u6bd5\u64ce\u5929\u6709\u96be\u4ee5\u89e3\u6551\u7684\u9690\u5fe7\uff0c\u5929\u660e\u4e4b\u540e\uff0c\u7eb7\u7eb7\u8fdb\u5e10\u95ee \u5019\uff0c\u5e76\u6c47\u62a5\u519b\u60c5\uff0c\u4f46\u89c9\u6bd5\u64ce\u5929\u813e\u6c14\u66b4\u8e81\uff0c\u5927\u5f02\u5bfb\u5e38\uff0c\u4f17\u4eba\u5747\u662f\u60f4\u60f4\u4e0d\u5b89\u3002 \u3000\u3000\u987e\u5b5f\u7ae0\u548c\u6bd5\u613f\u7a77\u7b49\u4e00\u5e72\u4eba\u867d\u77e5\u9053\u6bd5\u64ce\u5929\u5403\u4e86\u4e8e\u627f\u73e0\u7684\u5927\u4e8f\uff0c\u89c1\u4ed6\u5df2\u884c\u52a8\u5982\u5e38\uff0c\u4e5f\u4e0d\u6562\u518d \u95ee\uff0c\u6050\u6709\u4f24\u4ed6\u7684\u9762\u5b50\u3002\u4f17\u5c06\u5b98\u5728\u5e10\u4e2d\u5546\u8bae\u519b\u60c5\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u987e\u5b5f\u7ae0\u6e10\u6e10\u77a7\u51fa\u4e0d\u59a5\uff0c\u6b63\u60f3\u51fa\u8a00 \u63d0\u9192\uff0c\u53eb\u4f17\u5c06\u5b98\u9000\u4e0b\uff0c\u5ffd\u89c1\u5b88\u8425\u95e8\u7684\u4e2d\u519b\uff0c\u9762\u8272\u5f20\u7687\uff0c\u5306\u5306\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u90a3\uff0c\u90a3\u4f4d\u4e8e\u59d1\u5a18\u53c8 \u8fdb\u6765\u4e86\uff01\u201d \u3000\u3000\u987e\u5b5f\u7ae0\u5403\u4e86\u4e00\u60ca\uff0c\u5077\u773c\u770b\u6bd5\u64ce\u5929\u65f6\uff0c\u53ea\u89c1\u4ed6\u9762\u8272\u9634\u6697\uff0c\u597d\u50cf\u5c31\u8981\u5927\u53d1\u96f7\u9706\uff0c\u5374\u5ffd\u5730\u9762\u8272 \u4e00\u8f6c\uff0c\u538b\u4f4e\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5524\u5979\u8fdb\u6765\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5728\u51e0\u5341\u53cc\u60ca\u8bb6\u654c\u89c6\u7684\u773c\u5149\u6ce8\u89c6\u4e0b\uff0c\u7f13\u7f13\u8d70\u8fdb\uff0c\u53ea\u542c\u5f97\u6bd5\u64ce\u5929\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a \u201c\u4e8e\u59d1\u5a18\uff0c\u4f60\u597d\u5927\u7684\u80c6\u5b50\uff01\u201d\u4e8e\u627f\u73e0\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6709\u6240\u6c42\u4e8e\u6211\uff0c\u6211\u4f55\u987b\u60e7\u4f60\uff01\u201d\u6bd5\u64ce\u5929\u54c8\u54c8 \u7b11\u9053\uff1a\u201c\u53ea\u6015\u4f60\u4e5f\u6709\u6240\u6c42\u4e8e\u6211\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u5c31\u6253\u5f00\u5929\u7a97\u8bf4\u4eae\u8bdd\uff0c\u505a\u4e00\u6b21\u516c\u5e73\u7684 \u4e70\u5356\u3002\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u94c1\u955c\u5fc3\u5462\uff1f\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u4f60\u662f\u4e3a\u8fd9 \u5c0f\u5b50\u800c\u6765\u3002\u201d\u4ed6\u867d\u7136\u65e9\u5df2\u6599\u5230\u4e8e\u627f\u73e0\u7684\u6765\u610f\uff0c\u4f46\u7531\u4e8e\u627f\u73e0\u4eb2\u53e3\u8bf4\u51fa\u94c1\u955c\u5fc3\u7684\u540d\u5b57\uff0c\u6bd5\u64ce\u5929\u5fc3 \u91cc\u4ecd\u662f\u9178\u6e9c\u6e9c\u7684\u602a\u4e0d\u662f\u5473\u513f\u3002\u4e8e\u627f\u73e0\u9762\u8272\u4e00\u677f\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u4e3a\u94c1\u955c\u5fc3\u800c\u6765\uff0c\u4f46\u4e5f\u662f\u4e3a \u4f60\u6bd5\u5927\u9f99\u5934\u800c\u6765\u554a\uff01\u201d\u6bd5\u64ce\u5929\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u53ea\u6709\u94c1\u955c\u5fc3\u80fd\u7ed9\u4f60\u89e3\u7a74\uff0c \u4f60\u4e0d\u653e\u4ed6\uff0c\u4f60\u5c31\u51c6\u5907\u505a\u4e00\u4e2a\u7ec8\u8eab\u6b8b\u5e9f\u7684\u8349\u5934\u7687\u5e1d\u597d\u4e86\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u5c06\u5b98\u604d\u7136\u5927\u609f\uff0c\u6bd5\u64ce\u5929\u9762\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u7ea2\uff0c\u5bf9\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u597d\uff0c\u4f60\u53bb\u628a\u94c1 \u955c\u5fc3\u653e\u51fa\u6765\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d\uff0c\u8ba9\u6211\u5148\u53bb\u89c1\u4ed6\u3002\u201d\u6bd5\u64ce\u5929\u4e00\u60f3\uff0c\u7acb\u5373\u660e\u767d\u4e86\u4e8e\u627f\u73e0\u7684\u7528 \u610f\uff0c\u90a3\u662f\u4e3a\u89e3\u7a74\u4e4b\u9645\uff0c\u4e0d\u514d\u808c\u80a4\u76f8\u63a5\uff0c\u4e8e\u627f\u73e0\u6562\u60c5\u662f\u8fde\u624b\u6307\u4e5f\u4e0d\u613f\u6cbe\u7740\u4ed6\uff0c\u6240\u4ee5\u8981\u5148\u4f20\u94c1\u955c \u5fc3\u7684\u89e3\u7a74\u4e4b\u6cd5\uff0c\u5047\u624b\u94c1\u955c\u5fc3\u800c\u4e3a\u3002\u6bd5\u64ce\u5929\u5992\u6068\u4ea4\u8ff8\uff0c\u5374\u662f\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u5429\u5490\u6bd5\u613f\u7a77\u5e26\u4e8e\u627f \u73e0\u53bb\u89c1\u94c1\u955c\u5fc3\u3002 \u3000\u3000\u6bd5\u613f\u7a77\u5c06\u4e8e\u627f\u73e0\u5e26\u5230\u5e10\u540e\u7684\u4e00\u95f4\u6728\u5c4b\u524d\u9762\uff0c\u626e\u4e86\u4e00\u4e2a\u9b3c\u8138\u7b11\u9053\uff1a\u201c\u59d1\u5976\u5976\uff0c\u4f60\u4f55\u4ee5\u8001\u662f \u548c\u6211\u4eec\u7684\u5927\u9f99\u5934\u4f5c\u5bf9\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u53c8\u4f55\u82e6\u8001\u662f\u8ddf\u7740\u4f60\u4eec\u7684\u5927\u9f99\u5934\u4e0e\u53f6\u5927\u54e5\u4f5c\u5bf9\uff1f\u201d\u6bd5 \u613f\u7a77\u5fc3\u5934\u4e00\u9707\uff0c\u5185\u6127\u4e8e\u5fc3\uff0c\u518d\u4e5f\u7b11\u4e0d\u51fa\u6765\uff0c\u5c34\u5c2c\u4e4b\u6781\uff0c\u53ea\u597d\u53c8\u626e\u4e86\u4e00\u4e2a\u9b3c\u8138\u9053\uff1a\u201c\u59d1\u5976\u5976\uff0c \u7b97\u6211\u6015\u4e86\u4f60\u5566\u3002\u8fd9\u662f\u89e3\u5f00\u94c1\u955c\u5fc3\u9563\u94d0\u7684\u9501\u5319\uff0c\u4f60\u8fdb\u53bb\u5427\u3002\u201d \u3000\u3000\u94c1\u955c\u5fc3\u88ab\u56da\u5728\u6728\u5c4b\u5185\uff0c\u6b63\u5927\u53d1\u813e\u6c14\uff0c\u542c\u5f97\u4eba\u58f0\uff0c\u4fbf\u5927\u9a82\u9053\uff1a\u201c\u6bd5\u64ce\u5929\uff0c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c \u4ffa\u94c1\u955c\u5fc3\u662f\u9876\u5929\u7acb\u5730\u7684\u7537\u5b50\uff0c\u5c82\u4f1a\u5f52\u987a\u4e8e\u4f60\uff0c\u4f60\u7ed9\u6211\u6eda\u51fa\u53bb\uff01\u201d\u4e8e\u627f\u73e0\u4e00\u811a\u8de8\u5165\u623f\uff0c\u67d4\u58f0\u8bf4 \u9053\uff1a\u201c\u955c\u5fc3\uff0c\u662f\u6211\uff01\u201d \u3000\u3000\u94c1\u955c\u5fc3\u773c\u775b\u4e00\u4eae\uff0c\u8bb8\u4e45\u8bb8\u4e45\u4ee5\u6765\uff0c\u4ed6\u6ca1\u6709\u542c\u8fc7\u4e8e\u627f\u73e0\u8fd9\u6837\u6e29\u67d4\u7684\u547c\u5524\uff0c\u611f\u89c9\u5fc3\u4e2d\u751c\u4e1d\u4e1d \u7684\uff0c\u4ec0\u4e48\u6bd5\u64ce\u5929\uff0c\u53f6\u6210\u6797\u7b49\u6240\u7ed9\u4e88\u4ed6\u7684\u56f0\u607c\uff0c\u5728\u8fd9\u4e00\u58f0\u547c\u5524\u4e2d\uff0c\u5168\u90e8\u5316\u4e3a\u4e4c\u6709\uff0c\u62ac\u8d77\u5934\u6765\uff0c \u7785\u7740\u4e8e\u627f\u73e0\u53ea\u662f\u75f4\u7b11\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7ed9\u4ed6\u89e3\u5f00\u9563\u94d0\uff0c\u94c1\u955c\u5fc3\u5438\u4e86\u4e00\u53e3\u957f\u6c14\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u68a6\u4e48\uff1f\u6bd5\u64ce\u5929\u600e\u4e48\u8bb8 \u4f60\u8fdb\u6765\u89c1\u6211\uff1f\u201d\u5fc3\u4e2d\u84e6\u4e00\u5bd2\uff0c\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u96be\u9053\u662f\u4f60\u5f52\u987a\u4e86\u4ed6\u4e48\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5550\u4e86\u4e00\u53e3\u9053\uff1a\u201c\u4f60\u77a7\u6211\u662f\u6ca1\u6709\u9aa8\u6c14\u7684\u5973\u6d41\u4e4b\u8f88\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u8fd9\u53e5\u8bdd\u5176\u5b9e\u662f\u607c\u94c1\u955c \u5fc3\u4e0e\u5979\u76f8\u5904\u8bb8\u4e45\uff0c\u8fd8\u4e0d\u61c2\u5f97\u5979\u7684\u4e3a\u4eba\uff0c\u5728\u94c1\u955c\u5fc3\u542c\u6765\uff0c\u5374\u4ee5\u4e3a\u4e8e\u627f\u73e0\u53ea\u770b\u5f97\u8d77\u4ed6\uff0c\u5fc3\u4e2d\u60f3 \u9053\uff1a\u201c\u662f\u554a\uff0c\u51ed\u6bd5\u64ce\u5929\u90a3\u526f\u6837\u5b50\uff0c\u600e\u914d\u4e0e\u6211\u76f8\u6bd4\uff0c\u5979\u5c82\u80fd\u5f52\u987a\u4e8e\u4ed6\uff1f\u201d\u5982\u6b64\u4e00\u60f3\uff0c\u5fc3\u82b1\u6012 \u653e\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u662f\u600e\u6837\u8fdb\u6765\u7684\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8ba9\u4f60\u53bb\u6551\u6bd5\u64ce\u5929\u3002\u201d\u94c1\u955c\u5fc3\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u8981\u6211\u53bb\u6551\u5730\uff1f\u201d\u4e8e\u627f\u73e0 \u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u8981\u4f60\u53bb\u6551\u4ed6\u3002\u201d\u5c06\u6bd5\u64ce\u5929\u88ab\u5979\u5c01\u95ed\u4e86\u7a74\u9053\uff0c\u4ee5\u53ca\u5979\u51c6\u5907\u4f20\u6388\u94c1\u955c\u5fc3\u7684\u89e3\u7a74 \u4e4b\u6cd5\uff0c\u7531\u94c1\u955c\u5fc3\u4e3a\u6bd5\u64ce\u5929\u89e3\u7a74\uff0c\u4f5c\u4e3a\u4ea4\u6362\u91ca\u653e\u7684\u6761\u4ef6\u8bf4\u4e86\u3002\u94c1\u955c\u5fc3\u5401\u4e86\u53e3\u6c14\u3002\u7b11\u9053\uff1a\u201c\u539f\u6765 \u5982\u6b64\uff0c\u6211\u5230\u8fd9\u91cc\u6765\uff0c\u6b63\u662f\u4e3a\u4e86\u4e0e\u4f60\u8d70\u51fa\u8fd9\u662f\u975e\u4e4b\u5730\uff0c\u5982\u4eca\u53ef\u9042\u4e86\u5fc3\u613f\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u53c8\u662f\u600e\u4e48\u6765\u7684\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u6211\u662f\u591a\u4e48\u60e6\u8bb0\u4f60\uff0c\u662f\u6211\u5411\u6c90\u56fd \u516c\u8ba8\u4e86\u4e2a\u5dee\u4f7f\uff0c\u8d70\u51fa\u6606\u660e\uff0c\u5c31\u4e00\u76f4\u4e0a\u8fd9\u513f\u6765\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u76ef\u7740\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u5dee\u4f7f\uff1f\u201d\u94c1\u955c\u5fc3 \u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u66ff\u6c90\u56fd\u516c\u62dc\u8868\u4e0a\u4eac\uff0c\u594f\u660e\u5927\u7406\u4e4b\u4e8b\u3002\u201d\u5176\u5b9e\u6c90\u56fd\u516c\u65e9\u5df2\u53e6\u6d3e\u4eb2\u4fe1\u62dc\u8868\u4e0a\u4eac\uff0c\u4ed6 \u6d3e\u9063\u94c1\u955c\u5fc3\u4e0a\u4eac\uff0c\u5176\u5b9e\u662f\u4e3a\u4e86\u5973\u513f\u3002\u8981\u77e5\u94c1\u955c\u5fc3\u867d\u7136\u6587\u6b66\u5168\u624d\uff0c\u5374\u4e0d\u5c51\u5e94\u8003\u79d1\u4e3e\uff0c\u6240\u4ee5\u8fd8\u6ca1 \u6709\u529f\u540d\u3002\u6c90\u56fd\u516c\u4e00\u6765\u662f\u4e3a\u4e86\u987a\u5973\u513f\u7684\u5fc3\u610f\uff0c\u4e8c\u6765\u662f\u4ed6\u81ea\u5df1\u4e5f\u770b\u4e0a\u4e86\u94c1\u955c\u5fc3\uff0c\u5fc3\u5185\u65e9\u5df2\u628a\u94c1\u955c \u5fc3\u5217\u4e3a\u96c0\u5c4f\u4e4b\u9009\uff0c\u56e0\u6b64\u85c9\u4e2a\u6765\u7531\uff0c\u8bf7\u94c1\u955c\u5fc3\u4ee3\u8868\u4ed6\u4e0a\u4eac\u9762\u5723\u3002\u6c90\u56fd\u516c\u662f\u8fb9\u7586\u91cd\u81e3\uff0c\u5f02\u59d3\u5c01 \u738b\uff0c\u6599\u7687\u5e1d\u4e5f\u8981\u7ed9\u4ed6\u51e0\u5206\u9762\u5b50\uff0c\u4ed6\u518d\u5728\u594f\u6298\u4e2d\u5c06\u94c1\u955c\u5fc3\u91cd\u91cd\u4fdd\u4e3e\uff0c\u90a3\u4e48\u94c1\u955c\u5fc3\u5b9a\u53ef\u5e73\u6b65\u9752 \u4e91\uff0c\u94c1\u955c\u5fc3\u4e5f\u9690\u7ea6\u77e5\u9053\u6c90\u56fd\u516c\u7684\u7528\u610f\uff0c\u53ef\u662f\u4e00\u6765\u4ed6\u4e0d\u6562\u79c1\u62c6\u594f\u6298\uff0c\u4e8c\u6765\u4ed6\u7eb5\u7136\u6599\u5230\u51e0\u5206\uff0c\u4e5f \u4e0d\u6562\u5728\u4e8e\u627f\u73e0\u9762\u524d\u660e\u8bf4\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u7684\u5e08\u7236\u5462\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u5f20\u5927\u4fa0\u592b\u5987\u4e5f\u4e3a\u4e86\u62a4\u9001\u6ce2\u65af\u516c\u4e3b\u4e4b\u4e8b\uff0c\u4e0a\u4eac \u53bb\u4e86\u3002\u4ed6\u6bd4\u6211\u65e9\u8d70\u5341\u5929\uff0c\u8fd9\u65f6\u53ea\u6015\u5feb\u5230\u4eac\u90fd\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u53c8\u95ee\u5230\uff1a\u201c\u4f60\u5df2\u77e5\u9053\u4e86\u6bd5\u64ce\u5929\u6392\u6324 \u53f6\u5b97\u7559\u7684\u4e8b\u4e48\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u5c31\u662f\u56e0\u4e3a\u77e5\u9053\u6b64\u4e8b\uff0c\u624d\u5230\u5927\u8425\u627e\u4f60\u5440\u3002\u6211\u65e9\u5c31\u770b\u51fa\u4e86\u6bd5\u64ce\u5929 \u4e0d\u662f\u4e2a\u597d\u4e1c\u897f\uff0c\u53f6\u5b97\u7559\u867d\u7136\u7a0d\u4e3a\u61c2\u5f97\u6253\u4ed7\uff0c\u4e00\u4e2a\u571f\u5934\u571f\u8111\u7684\u77ff\u5de5\u51fa\u8eab\u7684\u4eba\uff0c\u5374\u4e0d\u662f\u6bd5\u64ce\u5929\u7684 \u5bf9\u624b\uff0c\u5403\u4e86\u4e8f\u4e5f\u662f\u6d3b\u8be5\u3002\u5c31\u53ef\u60dc\u4f60\u504f\u548c\u8fd9\u4e9b\u4eba\u6df7\u5728\u4e00\u8d77\uff0c\u6559\u6211\u5982\u4f55\u4e0d\u6025\uff0c\u6240\u4ee5\u6211\u5c31\u662f\u62fc\u4e86\u6027 \u547d\uff0c\u4e5f\u8981\u8bbe\u6cd5\u4ee4\u4f60\u8fdc\u79bb\u6b64\u5730\u3002\u201d\u4e8e\u627f\u73e0\u4e00\u76b1\u7709\u5934\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u662f\u4e48\uff1f\u201d \u3000\u3000\u94c1\u955c\u5fc3\u6025\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fd8\u4e0d\u77e5\u9053\u6211\u7684\u5fc3\uff1f\u201d\u4e8e\u627f\u73e0\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u597d\u50cf\u666e\u5929 \u4e4b\u4e0b\uff0c\u5c31\u53ea\u6709\u4f60\u662f\u82f1\u96c4\u8c6a\u6770\uff0c\u6211\u4e0d\u8fc7\u662f\u4e2a\u5e73\u5e73\u5e38\u5e38\u7684\u5973\u5b50\uff0c\u600e\u61c2\u5f97\u4f60\u60f3\u4e9b\u4ec0\u4e48\uff1f\u201d\u94c1\u955c\u5fc3\u53eb \u9053\uff1a\u201c\u54a6\uff0c\u6211\u4ec0\u4e48\u90fd\u4e3a\u4e86\u4f60\uff0c\u4f60\u5bf9\u6211\u51b7\u6f20\u4e5f\u8fd8\u7f62\u4e86\uff0c\u600e\u4e48\u4e00\u89c1\u9762\u5c31\u8ba5\u523a\u8d77\u6211\u6765\uff1f\u6211\u8bf4\uff0c\u4ee5\u4f60 \u7684\u7389\u9aa8\u51b0\u5fc3\uff0c\u548c\u8fd9\u822c\u7c97\u4eba\u6df7\u5728\u4e00\u8d77\uff0c\u5c82\u4e0d\u662f\u6c61\u8fb1\u4e86\u4f60\uff1f\u54b1\u4eec\u51fa\u53bb\u4e4b\u540e\uff0c\u5728\u676d\u5dde\u6216\u8005\u5728\u6606\u660e\u7b51 \u51e0\u95f4\u7cbe\u820d\uff0c\u6216\u8005\u8bfb\u4e66\uff0c\u6216\u8005\u7ec3\u5251\uff0c\u4f3c\u6b64\u6e05\u798f\uff0c\u60f3\u795e\u4ed9\u4e5f\u5f53\u7fa1\u6155\u6211\u4eec\uff01\u201d\u4e8e\u627f\u73e0\u7aef\u8d77\u9762\u8272\uff0c\u6b63 \u5bb9\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u914d\u505a\u795e\u4ed9\uff0c\u4e5f\u4e0d\u60f3\u505a\u795e\u4ed9\u3002\u6211\u5012\u662f\u60f3\u529d\u4f60\u6682\u65f6\u4e0d\u5fc5\u4e0a\u4eac\uff0c\u6211\u5e08\u7236\u5df2\u8fdb\u4eac\u53bb \u4e86\uff0c\u5927\u7406\u7684\u4e8b\u60c5\uff0c\u4f60\u8fd8\u6101\u7687\u5e1d\u8001\u513f\u4e0d\u77e5\u9053\u5417\uff1f\u201d\u94c1\u955c\u5fc3\u559c\u9053\uff1a\u201c\u4f46\u5f97\u54b1\u4eec\u957f\u805a\uff0c\u4e0d\u8fdb\u4eac\u5c31\u4e0d \u8fdb\u4eac\uff01\u201d\u4e8e\u627f\u73e0\u6120\u9053\uff1a\u201c\u4f60\u600e\u4e48\u603b\u662f\u7f20\u5939\u4e0d\u6e05\uff0c\u6211\u529d\u4f60\u6682\u65f6\u4e0d\u5fc5\u8fdb\u4eac\uff0c\u662f\u60f3\u4f60\u4e0a\u5c6f\u6eaa\u4e00 \u8d9f\u3002\u201d\u94c1\u955c\u5fc3\u8be7\u9053\uff1a\u201c\u4e0a\u5c6f\u6eaa\u5e72\u5417\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u53f6\u6210\u6797\u5728\u90a3\u513f\u72ec\u6297\u5341\u4e07\u5b98\u519b\uff0c\u6b63\u8981\u6709\u4eba \u76f8\u52a9\u3002\u201d\u94c1\u955c\u5fc3\u5927\u4e3a\u5931\u671b\uff0c\u53eb\u9053\uff1a\u201c\u53f6\u6210\u6797\u8fd9\u5c0f\u5b50\u5c31\u503c\u5f97\u4f60\u8fd9\u6837\u6302\u5fc3\uff0c\u4ec0\u4e48\u53f6\u6210\u6797\uff0c\u4ec0\u4e48\u6bd5 \u64ce\u5929\uff0c\u54ea\u4e00\u4e2a\u662f\u80fd\u9020\u5c31\u5927\u4e8b\u4e4b\u4eba\uff1f\u503c\u5f97\u6211\u53bb\u76f8\u52a9\u4ed6\uff1f\u5bf9\u8fd9\u4e9b\u8349\u83bd\u82f1\u96c4\uff0c\u6211\u538c\u70e6\u900f\u4e86\uff0c\u627f\u73e0\uff0c \u4f60\u600e\u4e48\u4e5f\u50cf\u8d8a\u6765\u8d8a\u53d8\u4e86\uff1f\u201d \u3000\u3000\u94c1\u955c\u5fc3\u56fa\u7136\u5931\u671b\uff0c\u4ed6\u5374\u4e0d\u77e5\u4e8e\u627f\u73e0\u66f4\u662f\u5931\u671b\u5230\u4e86\u6781\u70b9\uff0c\u94c1\u955c\u5fc3\u62b1\u6028\u5979\u53d8\u4e86\uff0c\u5979\u66f4\u75db\u60dc\u94c1 \u955c\u5fc3\u4e00\u70b9\u4e5f\u6ca1\u6709\u53d8\uff0c\u603b\u662f\u4e3a\u81ea\u5df1\u6253\u7b97\uff0c\u603b\u662f\u770b\u4e0d\u8d77\u522b\u4eba\uff01\u5979\u672c\u6765\u4e0b\u4e86\u6781\u5927\u51b3\u5fc3\uff0c\u8981\u628a\u53f6\u6210\u6797 \u820d\u5f03\uff0c\u8981\u628a\u53f6\u6210\u6797\u8ba9\u7ed9\u51cc\u4e91\u51e4\uff0c\u53ef\u662f\u6b64\u65f6\u6b64\u9645\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u53f6\u6210\u6797\u90a3\u6734\u5b9e\u65e0\u534e\u7684\u5f62\u8c8c\uff0c\u5374\u7a81 \u7136\u6d8c\u73b0\u5fc3\u5934\uff0c\u867d\u7136\u53ea\u662f\u5e7b\u5f71\uff0c\u8fd9\u5e7b\u5f71\u5374\u906e\u853d\u4e86\u7ad9\u5728\u5979\u9762\u524d\u7684\uff0c\u4f38\u624b\u53ef\u89e6\u7684\u94c1\u955c\u5fc3\u8fd9\u4e2a\u771f\u5b9e\u7684 \u4eba\uff01 \u3000\u3000\u53ea\u542c\u5f97\u4e8e\u627f\u73e0\u5e7d\u5e7d\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u4eba\u5404\u6709\u5fd7\uff0c\u6211\u4e0d\u4f1a\u52c9\u5f3a\u4f60\u7684\uff0c\u54b1\u4eec\u4e0d\u5fc5\u8c08 \u5566\u3002\u201d\u94c1\u955c\u5fc3\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u53eb\u9053\uff1a\u201c\u627f\u73e0\uff0c\u627f\u73e0\uff0c\u4f60\uff0c\u4f60\u542c\u6211\u8bf4\u3002\u201d\u4e8e\u627f\u73e0\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4e0d \u5fc5\u8bf4\u5566\u3002\u4f60\u60f3\u5feb\u4e9b\u51fa\u53bb\uff0c\u90a3\u5c31\u8d76\u5feb\u5b66\u89e3\u7a74\u4e4b\u6cd5\uff0c\u6bd5\u64ce\u5929\u6050\u6015\u4e5f\u7b49\u5f97\u4e0d\u8010\u70e6\u4e86\uff01\u201d \u3000\u3000\u94c1\u955c\u5fc3\u63a5\u89e6\u5230\u4e8e\u627f\u73e0\u7684\u773c\u5149\uff0c\u4f46\u89c9\u5979\u6709\u4e00\u80a1\u51db\u7136\u4e0d\u53ef\u4fb5\u72af\u7684\u795e\u60c5\uff0c\u4e0d\u6562\u518d\u8bf4\u3002\u201c\u7384\u529f\u8981 \u8bc0\u201d\u4e2d\u7684\u95ed\u7a74\u4e4b\u6cd5\u867d\u6781\u79d8\u5965\u3001\u5bf9\u4e8e\u5185\u529f\u6709\u4e86\u6839\u5e95\u7684\u4eba\uff0c\u89e3\u7a74\u4e4b\u6cd5\uff0c\u5e76\u4e0d\u96be\u5b66\u3002\u800c\u4e14\u94c1\u955c\u5fc3\u53c8 \u662f\u4e2a\u6709\u5c0f\u806a\u660e\u7684\u4eba\uff0c\u4e0d\u8fc7\u4e00\u987f\u996d\u7684\u65f6\u95f4\uff0c\u4ed6\u5c31\u5b66\u4f1a\u4e86\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u679c\u7136\u662f\u7b49\u5f97\u751a\u4e0d\u8010\u70e6\uff0c\u4e00\u89c1\u4ed6\u4eec\u51fa\u6765\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u5374\u6545\u4f5c\u77dc\u6301\uff0c\u677f\u7740\u9762\u5b54\u8bf4\u9053\uff1a \u201c\u94c1\u955c\u5fc3\uff0c\u6211\u770b\u5728\u4e8e\u59d1\u5a18\u7684\u9762\u4e0a\uff0c\u4eca\u65e5\u653e\u4f60\u56de\u53bb\uff0c\u4f60\u82e5\u7136\u79c1\u4e0b\u5f04\u4ec0\u4e48\u624b\u811a\uff0c\u54fc\uff0c\u54fc\uff0c\u90a3\u53ef\u6028 \u4e0d\u5f97\u54b1\u5bb6\uff01\u201d\u94c1\u955c\u5fc3\u4ef0\u5929\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6015\u6211\u7ed9\u4f60\u89e3\u7a74\u4e4b\u65f6\u4f5c\u5f04\u4f60\uff1f\u6211\u4e5f\u6015\u4f60\u8bf4\u8bdd\u4e0d\u7b97\u6570 \u5462\u3002\u4f60\u662f\u4ec0\u4e48\u4e86\u4e0d\u5f97\u7684\u4eba\uff1f\u503c\u5f97\u6211\u9677\u5bb3\u4f60\uff1f\u6211\u5c82\u662f\u50cf\u4f60\u4e00\u6837\u7684\u5351\u9119\u5c0f\u4eba\uff1f\u597d\uff0c\u54b1\u4eec\u5c31\u5728\u4f17\u4eba \u9762\u524d\u8bf4\u6e05\u695a\u4e86\uff0c\u6211\u7ed9\u4f60\u89e3\u7a74\uff0c\u4f60\u8ba9\u6211\u51fa\u8425\uff0c\u8c01\u82e5\u5931\u8a00\uff0c\u5c31\u662f\u72d7\u86cb\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u6bd5\u64ce\u5929\u5927\u662f \u5c34\u5c2c\u3002 \u3000\u3000\u867d\u7136\u987e\u5b5f\u7ae0\u7b49\u4e00\u5e72\u4eba\u81ea\u4e8e\u627f\u73e0\u6765\u540e\uff0c\u90fd\u5df2\u77e5\u9053\u6bd5\u64ce\u5929\u5b9a\u662f\u7a74\u9053\u53d7\u5236\uff0c\u6240\u4ee5\u624d\u80af\u91ca\u653e\u4e8e\u3001 \u94c1\u4e8c\u4eba\u3002\u4f46\u7531\u94c1\u955c\u5fc3\u660e\u767d\u9053\u51fa\uff0c\u603b\u662f\u4f24\u4e86\u6bd5\u64ce\u5929\u7684\u9762\u5b50\u3002 \u3000\u3000\u4f46\u89c1\u6bd5\u64ce\u5929\u9762\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u7ea2\uff0c\u5fc3\u91cc\u663e\u662f\u6124\u6012\u4e4b\u6781\uff0c\u5374\u53c8\u65e0\u53ef\u5948\u4f55\u3002\u94c1\u955c\u5fc3\u504f\u4e0d\u653e\u8fc7\uff0c \u903c\u7740\u4ed6\u53c8\u95ee\u4e00\u53e5\u9053\uff1a\u201c\u5982\u4f55\uff1f\u201d\u6bd5\u64ce\u5929\u54ac\u4e00\u54ac\u7259\uff0c\u9053\uff1a\u201c\u597d\uff0c\u5c31\u4f9d\u4f60\u6240\u8bf4\u3002\u201d\u94c1\u955c\u5fc3\u7709\u98de\u8272 \u821e\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u542c\u7740\u4e86\uff0c\u6211\u7ed9\u4f60\u4eec\u7684\u5927\u9f99\u5934\u89e3\u7a74\uff0c\u7b49\u4e0b\u6211\u51fa\u53bb\uff0c\u8c01\u90fd\u4e0d\u5f97\u62e6\u963b\u3002\u6bd5 \u64ce\u5929\u662f\u8fd9\u6837\u5417\uff1f\u201d\u6bd5\u64ce\u5929\u70b9\u70b9\u5934\u9053\uff1a\u201c\u662f\u8fd9\u6837\uff01\u201d\u94c1\u955c\u5fc3\u54c8\u54c8\u5927\u7b11\uff0c\u4ed6\u6599\u60f3\u6bd5\u64ce\u5929\u867d\u7136\u5fc3\u672f \u4e0d\u6b63\uff0c\u4f46\u6bd5\u64ce\u5929\u662f\u4e2a\u5927\u9f99\u5934\uff0c\u5f53\u7740\u90e8\u4e0b\u7b54\u5141\u7684\u4e8b\uff0c\u4e0d\u6562\u63a8\u7ffb\uff0c\u4e8e\u662f\u653e\u5fc3\u7ed9\u6bd5\u64ce\u5929\u89e3\u7a74\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u548c\u6bd5\u64ce\u5929\u7684\u5185\u5916\u529f\u90fd\u81ea\u4e0d\u5f31\uff0c\u94c1\u955c\u5fc3\u8fd0\u52b2\u4e8e\u5916\uff0c\u6bd5\u64ce\u5929\u884c\u6c14\u4e8e\u5185\uff0c\u4e24\u80a1\u5185\u529b\uff0c\u51b2 \u51fb\u5173\u5143\u8981\u7a74\uff0c\u8fc7\u4e86\u4e00\u67f1\u9999\u7684\u65f6\u523b\uff0c\u6bd5\u64ce\u5929\u6e10\u89c9\u6c14\u673a\u901a\u7545\uff0c\u7487\u7391\u3001\u4e2d\u5e9c\u3001\u5929\u9619\u4e09\u5904\u88ab\u5c01\u95ed\u7684\u5927 \u7a74\uff0c\u5df2\u7ecf\u89e3\u5f00\uff0c\u53ea\u6709\u5730\u85cf\u4e00\u7a74\uff0c\u8fd8\u672a\u66fe\u6253\u901a\uff0c\u5ffd\u542c\u5f97\u5e10\u5916\u53c8\u662f\u55a7\u54d7\u53f1\u54a4\u4e4b\u58f0\u3002 \u3000\u3000\u4f46\u89c1\u6bd5\u613f\u7a77\u614c\u614c\u5f20\u5f20\u5730\u8fdb\u6765\u7980\u9053\uff1a\u201c\u6f6e\u97f3\u5927\u5e08\u4e0d\u5206\u7682\u767d\uff0c\u89c1\u4eba\u4fbf\u6253\uff0c\u5c31\u8981\u95ef\u8fdb\u5e10\u4e2d\u3002\u201d \u6bd5\u64ce\u5929\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u5b5f\u7ae0\uff0c\u4f60\u53bb\u6682\u963b\u4e00\u4e0b\u3002\u201d\u94c1\u955c\u5fc3\u8fd0\u52b2\u4e8e\u638c\uff0c\u731b\u52b2\u4e00\u62cd\uff0c\u6bd5\u64ce\u5929\u201c\u54ce \u54df\u201d\u4e00\u58f0\uff0c\u5012\u5728\u4ed6\u4e0a\uff0c\u4f17\u6b66\u58eb\u5927\u60ca\uff0c\u4fbf\u5f85\u4e0a\u524d\uff0c\u94c1\u955c\u5fc3\u5927\u7b11\u9053\uff1a\u201c\u884c\u5566\uff0c\u56db\u5904\u5927\u7a74\u90fd\u5df2\u89e3 \u5f00\uff0c\u6bd5\u64ce\u5929\u4f60\u8bf4\u8bdd\u7b97\u4e0d\u7b97\u6570\uff1f\u201d\u6bd5\u64ce\u5929\u6c89\u58f0\u559d\u9053\uff1a\u201c\u8ba9\u4ed6\u4eec\u8d70\uff0c\u613f\u7a77\u3001\u7ae0\u9022\uff0c\u4f60\u4eec\u90fd\u51fa\u53bb\u5e2e \u5b5f\u7ae0\u62e6\u963b\u90a3\u4e2a\u75af\u548c\u5c1a\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u7684\u5e08\u4f2f\u7956\u5e08\u662f\u4f60\u4eec\u62e6\u963b\u5f97\u6765\uff1f\u5f85\u6211\u518d\u7ed9\u4f60\u5356\u4e2a\u4eba\u60c5\uff0c\u529d\u4ed6\u8d70\u5427\u3002\u201d\u76c8\u76c8 \u4e00\u7b11\uff0c\u79fb\u6b65\u51fa\u8425\uff0c\u94c1\u955c\u5fc3\u6025\u5fd9\u4ea6\u6b65\u4ea6\u8d8b\uff0c\u8ddf\u5728\u80cc\u540e\uff0c\u51fa\u4e86\u5927\u8425\uff0c\u4f46\u89c1\u6f6e\u97f3\u548c\u5c1a\u559d\u9e23\u53f1\u54a4\uff0c\u4e00 \u7985\u6756\u821e\u5f97\u6cfc\u98ce\u4e5f\u4f3c\uff0c\u5c06\u4f17\u6b66\u58eb\u6253\u5f97\u8dcc\u8dcc\u649e\u649e\uff0c\u6709\u4e24\u5339\u9a6c\u5728\u4ed6\u7684\u80cc\u540e\u3002\u5176\u4e2d\u4e00\u5339\uff0c\u6b63\u662f\u4e8e\u627f\u73e0 \u7684\u7167\u591c\u72ee\u5b50\u9a6c\u3002 \u3000\u3000\u987e\u5b5f\u7ae0\u548c\u7ae0\u9022\u53cc\u53cc\u8d76\u4e0a\uff0c\u90a3\u7ae0\u9022\u662f\u6bd5\u64ce\u5929\u7684\u4eb2\u519b\u7edf\u9886\uff0c\u624b\u821e\u4e24\u67c4\u5f00\u5c71\u5927\u65a7\uff0c\u6709\u4e07\u592b\u4e0d\u5f53 \u4e4b\u52c7\uff0c\u6043\u7740\u5927\u5200\uff0c\u98de\u6b65\u62a2\u4e0a\uff0c\u53cc\u65a7\u9f50\u5288\u3002\u54ea\u77e5\u6f6e\u97f3\u548c\u5c1a\u7684\u5916\u5bb6\u529f\u592b\u767b\u5cf0\u9020\u6781\uff0c\u5728\u70b9\u82cd\u5c71\u6bd4\u6b66 \u4e4b\u65f6\uff0c\u4ee5\u9e20\u76d8\u5a46\u7684\u795e\u5200\uff0c\u5c1a\u4e14\u5948\u4f55\u4ed6\u4e0d\u5f97\uff0c\u4f55\u51b5\u7ae0\u9022\uff1f\u6f6e\u97f3\u548c\u5c1a\u6b63\u81ea\u6740\u5f97\u4f50\u8d77\uff0c\u89c1\u53cc\u65a7\u5288 \u5230\uff0c\u5927\u7b11\u9053\uff1a\u201c\u6765\u5f97\u597d\u554a\uff01\u201d\u7985\u6756\u4e00\u6325\uff0c\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u9707\u8033\u6b32\u804b\uff0c\u53ea\u89c1\u7ae0\u9022\u7684\u4e24\u67c4\u5927\u65a7\uff0c \u90fd\u5df2\u8131\u624b\u98de\u53bb\uff0c\u7ae0\u9022\u864e\u53e3\u7834\u88c2\uff0c\u6447\u6447\u6b32\u5012\uff0c\u987e\u5b5f\u7ae0\u5530\u7684\u4e00\u97ad\u626b\u53bb\uff0c\u6f6e\u97f3\u548c\u5c1a\u8fde\u626b\u4e09\u6756\uff0c\u90fd\u7ed9 \u987e\u5b5f\u7ae0\u907f\u5f00\uff0c\u6f6e\u97f3\u548c\u5c1a\u5927\u6012\uff0c\u4e00\u8dc3\u800c\u524d\uff0c\u624b\u8155\u5374\u53cd\u800c\u7ed9\u4ed6\u7684\u957f\u97ad\u7f20\u4f4f\uff0c\u6f6e\u97f3\u548c\u5c1a\u731b\u5730\u4e00\u58f0\u5927 \u559d\uff0c\u8fd0\u52b2\u4e00\u6323\uff0c\u90a3\u6761\u957f\u97ad\u767b\u65f6\u65ad\u4e3a\u51e0\u6bb5\uff0c\u6f6e\u97f3\u548c\u5c1a\u5927\u53eb\u9053\uff1a\u201c\u4f60\u4e5f\u7b97\u5f97\u662f\u4e00\u6761\u597d\u6c49\uff0c\u6211\u4e0d\u6740 \u4f60\uff0c\u5feb\u4e0e\u6211\u53bb\u53eb\u6bd5\u64ce\u5929\u51fa\u6765\u6253\u8bdd\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u7f13\u6b65\u4e0a\u524d\uff0c\u88e3\u887d\u4e00\u798f\uff0c\u9053\uff1a\u201c\u5e08\u4f2f\u7956\uff0c\u4f60\u8001\u597d\u554a\uff01\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u4f60 \u4eec\u90fd\u5728\u8fd9\u513f\uff01\u597d\uff0c\u6211\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u4e0d\u597d\u7684\u662f\u6bd5\u64ce\u5929\uff01\u5484\uff0c\u4f60\u8fd9\u53ae\u4e3a\u4f55\u8fd8\u4e0d\u53bb\u53eb\u6bd5\u64ce\u5929\u89c1 \u6211\uff1f\u201d\u540e\u9762\u8fd9\u8bdd\u662f\u5bf9\u987e\u5b5f\u7ae0\u8bf4\u7684\u3002\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u8981\u89c1\u6bd5\u64ce\u5929\u505a\u4ec0\u4e48\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a \u9053\uff1a\u201c\u6211\u4e00\u5411\u628a\u4ed6\u5f53\u5927\u82f1\u96c4\u5927\u8c6a\u6770\uff0c\u4eca\u65e5\u6211\u4ece\u6e29\u5dde\u56de\u6765\uff0c\u4e00\u8def\u4e0a\u78b0\u5230\u4e86\u51cc\u59d1\u5a18\uff0c\u624d\u77e5\u9053\u4ed6\u5e72 \u4e0b\u8fd9\u7b49\u4f24\u5929\u5bb3\u7406\u4e4b\u4e8b\u3002\u5484\uff0c\u903c\u8d70\u53f6\u5b97\u7559\uff0c\u6740\u6b7b\u9093\u8302\u4e03\uff0c\u8fd9\u4e24\u6869\u4e8b\u679c\u662f\u771f\u7684\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u4e00\u70b9\u4e0d\u5047\u3002\u201d\u6f6e\u97f3\u548c\u5c1a\u53eb\u9053\uff1a\u201c\u597d\uff0c\u51ed\u8fd9\u4e24\u6869\u4e8b\uff0c\u6211\u5c31\u8981\u5411\u4ed6\u95ee\u7f6a\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u82e5\u662f \u4ed6\u4e0d\u670d\u5462\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u6211\u4e00\u6756\u628a\u4ed6\u6253\u6740\uff01\u201d\u4e8e\u627f\u73e0\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u4e00\u6756\u628a\u4ed6\u6253\u6740\uff0c\u5012\u662f \u5bb9\u6613\uff0c\u8fd9\u6b8b\u5c40\u8c01\u6765\u6536\u62fe\uff1f\u4f60\u8001\u4eba\u5bb6\u6765\u505a\u5927\u9f99\u5934\u5417\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u55d4\u76ee\u8bf4\u9053\uff1a\u201c\u6211\u5e0c\u7f55\u4ec0\u4e48\u5927\u9f99 \u5934\uff1f\u6211\u4e5f\u505a\u4e0d\u6765\uff01\u201d\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u662f\u5440\uff0c\u8d70\u4e86\u53f6\u5927\u54e5\uff0c\u519b\u5fc3\u5df2\u662f\u4e0d\u7a33\uff0c\u5c31\u518d\u4e09\u529d\u6211\u4eec\u987e\u5168 \u5927\u5c40\uff0c\u4e0d\u53ef\u4e92\u76f8\u6b8b\u6740\u3002\u4f60\u548c\u51cc\u59d0\u59d0\u8def\u4e0a\u76f8\u9022\uff0c\u5306\u5306\u4e00\u9762\uff0c\u5927\u7ea6\u51cc\u59d0\u59d0\u8fd8\u672a\u66fe\u5c06\u53f6\u7edf\u9886\u7684\u5fc3\u610f \u5bf9\u4f60\u8be6\u544a\u5427\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u5446\u4e86\u534a\u54cd\uff0c\u9053\uff1a\u201c\u4f60\u7684\u8bdd\u4e5f\u6709\u9053\u7406\u3002\u201d\u4e8e\u627f\u73e0\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u5e08\u4f2f \u7956\uff0c\u591a\u8c22\u4f60\u8001\u4eba\u5bb6\u7ed9\u6211\u5e26\u56de\u4e86\u8fd9\u5339\u5b9d\u9a6c\uff0c\u54b1\u4eec\u4e0a\u9a6c\u8d70\u5427\uff01\u201d\u987e\u5b5f\u7ae0\u7b49\u6b63\u82e6\u4e8e\u65e0\u6cd5\u5bf9\u4ed8\uff0c\u5ffd\u89c1 \u6f6e\u97f3\u548c\u5c1a\u88ab\u4e8e\u627f\u73e0\u4e09\u8a00\u4e24\u8bed\u4fbf\u529d\u8d70\u4e86\uff0c\u81ea\u662f\u559c\u51fa\u671b\u5916\uff0c\u4f46\u7ec6\u542c\u4e8e\u627f\u73e0\u4e4b\u8a00\uff0c\u5374\u53c8\u6697\u6697\u4e3a\u81ea\u5df1 \u6240\u62e5\u6234\u7684\u5927\u9f99\u5934\u611f\u5230\u60ed\u6127\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u867d\u88ab\u529d\u670d\uff0c\u90c1\u95f7\u96be\u6d88\uff0c\u4e00\u58f0\u4e0d\u54cd\u5730\u62e8\u8f6c\u9a6c\u5934\u4fbf\u8d70\u3002\u94c1\u955c\u5fc3\u62a2\u4e86\u4e00\u5339\u5feb\u9a6c\uff0c\u76f4\u8ffd \u51fa\u5341\u6570\u91cc\u5916\uff0c\u624d\u89c1\u524d\u9762\u90a3\u4e24\u5339\u767d\u9a6c\u7f13\u4e86\u4e0b\u6765\u7b49\u4ed6\uff0c\u94c1\u955c\u5fc3\u8ffd\u4e0a\u524d\u53bb\uff0c\u53ea\u542c\u5f97\u4e8e\u627f\u73e0\u95ee\u9053\uff1a \u201c\u5e08\u4f2f\u7956\u4f60\u4e0a\u54ea\u513f\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u6c14\u547c\u547c\u5730\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u53cd\u6b63\u6211\u4e0d\u4f1a\u7559\u5728\u8fd9\u513f\u4e86\u3002\u201d\u94c1\u955c\u5fc3 \u9053\uff1a\u201c\u662f\u5440\uff0c\u7ba1\u4ed6\u4eec\u4e89\u6743\u593a\u5229\uff0c\u95f9\u5f97\u8986\u5730\u7ffb\u5929\uff0c\u54b1\u4eec\u624d\u4e0d\u5c51\u6cbe\u60f9\u4ed6\u4eec\uff0c\u8fdc\u8d70\u9ad8\u98de\uff0c\u843d\u5f97\u4e00\u4e2a \u5e72\u51c0\u3002\u201d\u4e8e\u627f\u73e0\u4fa7\u76ee\u659c\u7768\uff0c\u5fc3\u4e2d\u751a\u4e0d\u8212\u670d\uff0c\u5979\u672c\u60f3\u529d\u6f6e\u97f3\u548c\u5c1a\u4e0a\u5c6f\u6eaa\u53bb\u52a9\u53f6\u6210\u6797\uff0c\u89c1\u4ed6\u6c14\u6124 \u672a\u6d88\uff0c\u94c1\u955c\u5fc3\u53c8\u5728\u65c1\u8fb9\u51b7\u8a00\u51b7\u8bed\uff0c\u53ea\u5f97\u6682\u4e14\u628a\u8bdd\u5fcd\u4f4f\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u9a6c\u5636\u4eba\u95f9\uff0c\u4e00\u5f6a\u519b\u9a6c\u4ece\u5c71\u5773\u5904\u51fa\u6765\uff0c\u6f6e\u97f3\u548c\u5c1a\u6012\u9053\uff1a\u201c\u597d\uff0c\u6211\u653e\u8fc7\u4e86\u6bd5\u64ce\u5929\uff0c\u4ed6 \u8fd8\u6562\u6d3e\u4eba\u6765\u8ffd\u6211\uff01\u201d\u6a2a\u8d77\u7985\u6756\uff0c\u7741\u773c\u4e00\u77a7\uff0c\u5374\u662f\u6210\u6d77\u5c71\u548c\u77f3\u6587\u7ea8\u4e24\u4eba\uff0c\u5e26\u7740\u5341\u6570\u9a91\u4eba\u9a6c\uff0c\u8863 \u7532\u4e0d\u5168\uff0c\u5f62\u5bb9\u6194\u60b4\uff0c\u7adf\u662f\u6e83\u8d25\u5f52\u6765\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u4eec\u600e\u4e48\u843d\u6210\u4e86\u8fd9\u4e2a\u6837\u5b50\uff1f\u201d\u6210\u6d77\u5c71\u4e0a\u524d\u89c1\u8fc7\u5e08\u5144\uff0c\u5782\u624b\u7b54\u9053\uff1a \u201c\u5c0f\u8f88\u65e0\u80fd\uff0c\u60ed\u6127\u5df2\u6781\uff0c\u6211\u4eec\u8fd9\u652f\u6e14\u6c11\u5b50\u5f1f\u519b\u7ed9\u5b98\u519b\u6253\u8d25\u4e86\uff0c\u4e24\u5343\u519b\u9a6c\uff0c\u624d\u9003\u51fa\u4e86\u5341\u516d \u9a91\u3002\u201d\u77f3\u4e49\u7ea8\u6c14\u6124\u6124\u5730\u9053\uff1a\u201c\u82e5\u662f\u5728\u6c34\u4e0a\u4f5c\u6218\uff0c\u54b1\u4eec\u4e00\u547d\u5f53\u5341\uff0c\u504f\u504f\u7ed9\u6bd5\u64ce\u5929\u8c03\u5230\u5c71\u5730\u53bb\uff0c \u5f1f\u5144\u4eec\u8fde\u9a6c\u4e5f\u4e0d\u4f1a\u9a91\uff0c\u5149\u51ed\u7740\u4e00\u80a1\u9510\u6c14\u6253\u4e0d\u4e86\u4ed7\uff01\u201d \u3000\u3000\u6210\u6d77\u5c71\u9053\uff1a\u201c\u5144\u5f1f\u4eec\u5012\u662f\u5c3d\u4e86\u529b\u4e86\uff0c\u51ed\u7740\u4e00\u822c\u9510\u6c14\uff0c\u5728\u5c71\u5730\u82e6\u6218\uff0c\u4e5f\u652f\u6491\u4e86\u51e0\u4e2a\u6708\uff0c\u53ef \u662f\u4f24\u4ea1\u751a\u91cd\uff0c\u4e00\u65e0\u63f4\u519b\uff0c\u4e8c\u65e0\u7cae\u8349\uff0c\u5e78\u514d\u5168\u519b\u8986\u6ca1\uff0c\u5df1\u7b97\u662f\u597d\u7684\u4e86\u3002\u53ea\u662f\u6211\u5c06\u4e24\u5343\u591a\u6e14\u6c11\u5b50 \u5f1f\u5e26\u4e86\u51fa\u6765\uff0c\u53ea\u5269\u5341\u516d\u9a91\u56de\u53bb\uff0c\u53eb\u6211\u6709\u4f55\u9762\u76ee\u89c1\u6545\u4e61\u7236\u8001\u3002\u201d \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u54fc\uff0c\u53c8\u662f\u8fd9\u4e2a\u6bd5\u64ce\u5929\u5e72\u7684\u597d\u4e8b\uff01\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u5e78\u4e8f\u4f60\u9047\u89c1\u6211\u4eec\uff0c\u4f60\u4eec \u4e0d\u56de\u53bb\u4e5f\u7f62\u4e86\uff01\u6bd5\u64ce\u5929\u5df2\u628a\u53f6\u5b97\u7559\u903c\u8d70\uff0c\u4ed6\u628a\u4f60\u4eec\u5f53\u4f5c\u662f\u53f6\u5b97\u7559\u7684\u4eba\uff0c\u4f60\u4eec\u518d\u53bb\u89c1\u4ed6\uff0c\u8fd9\u5c31 \u662f\u81ea\u6295\u7f57\u7f51\u3002\u201d \u3000\u3000\u6210\u6d77\u5c71\u5446\u4e86\u534a\u664c\uff0c\u4f5c\u58f0\u4e0d\u5f97\u3002\u77f3\u6587\u7ea8\u9053\uff1a\u201c\u54ce\uff0c\u53ef\u60dc\u6211\u7239\u7239\u4e0d\u5728\u8fd9\u513f\u3002\u5e08\u5144\uff0c\u4f60\u53bb\u4e86\u54ea \u91cc\uff0c\u8fd9\u4e48\u4e45\u4e0d\u89c1\u4f60\uff0c\u4f60\u53ef\u77e5\u9053\u6211\u7239\u7239\u7684\u6d88\u606f\u5417\uff1f\u201d\u94c1\u955c\u5fc3\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u6211\u4e0a\u5927\u7406\u62dc\u8bbf\u4e86 \u5f20\u5927\u4fa0\u4e00\u8d9f\uff0c\u4e5f\u662f\u524d\u51e0\u5929\u624d\u56de\u6765\u7684\uff0c\u672a\u66fe\u89c1\u8fc7\u5e08\u7236\u3002\u201d \u3000\u3000\u94c1\u955c\u5fc3\u773c\u5149\u4e00\u77a5\uff0c\u89c1\u6210\u6d77\u5c71\u8170\u60ac\u5b9d\u5251\uff0c\u8be7\u9053\uff1a\u201c\u600e\u4e48\u5e08\u7236\u8fd9\u628a\u5b9d\u5251\u5728\u4f60\u8fd9\u513f\u3002\u201d\u77f3\u6587\u7ea8 \u9053\uff1a\u201c\u662f\u4e8e\u59d1\u5a18\u7ed9\u6211\u7684\uff0c\u6211\u4e0d\u89c1\u4e86\u7239\u7239\uff0c\u5c31\u628a\u5b83\u4ea4\u7ed9\u6210\u5e08\u54e5\u7528\uff0c\u90a3\u665a\u5230\u5e95\u95f9\u7684\u662f\u4ec0\u4e48\u4e8b\u60c5\uff1f \u6211\u7239\u7239\u5ffd\u7136\u4e0d\u89c1\uff0c\u8fd9\u628a\u5b9d\u5251\u53c8\u5230\u4e86\u4e8e\u59d1\u5a18\u624b\u91cc\uff0c\u8fd9\u7591\u56e2\u4e00\u76f4\u672a\u89e3\u3002\u4e8e\u59d1\u5a18\uff0c\u4f60\u73b0\u5728\u53ef\u4ee5\u8bf4\u4e86 \u5427\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u628a\u5251\u662f\u4e4c\u8499\u592b\u4ece\u5fa1\u6797\u519b\u7edf\u9886\u5a04\u6850\u5b59\u624b\u4e2d\u593a\u6765\u7684\uff0c\u4e4c\u4f2f\u4f2f\u53eb\u6211\u5c06\u8fd9\u628a\u5251 \u8fd8\u7ed9\u4f60\u7684\u7239\u7239\uff0c\u53ef\u60dc\u4ed6\u5df2\u7ecf\u8d70\u4e86\u3002\u5440\uff0c\u53ea\u6015\u5c31\u662f\u9001\u8fd8\u7ed9\u4ed6\uff0c\u4ed6\u4e5f\u4e0d\u80af\u8981\u8fd9\u628a\u5b9d\u5251\u4e86\u3002\u201d\u77f3\u6587 \u7ea8\u66f4\u662f\u7591\u5fc3\uff0c\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u843d\u5230\u5a04\u6850\u5b59\u624b\u4e2d\uff0c\u4e3a\u4ec0\u4e48\u6211\u7239\u7239\u53c8\u4e0d\u80af\u8981\u8fd9\u628a\u5b9d\u5251\uff1f\u201d\u4e8e\u627f\u73e0 \u9053\uff1a\u201c\u4f60\u95ee\u4f60\u4eec\u7684\u5927\u5e08\u5144\u3002\u201d \u3000\u3000\u8fd9\u628a\u5b9d\u5251\u5b9e\u662f\u94c1\u955c\u5fc3\u5728\u53f0\u5dde\u90a3\u4e00\u665a\uff0c\u88ab\u5a04\u6850\u5b59\u4ee5\u7236\u4eb2\u7684\u6027\u547d\u4f5c\u5a01\u80c1\uff0c\u4ece\u5e08\u7236\u624b\u4e2d\u8ba8\u6765\uff0c \u9001\u4e86\u7ed9\u5a04\u6850\u5b59\u7684\uff0c\u4e3a\u4e86\u6b64\u4e8b\uff0c\u77f3\u60ca\u6d9b\u4f24\u5fc3\u4e4b\u6781\uff0c\u4ece\u6b64\u4e0d\u8ba4\u94c1\u955c\u5fc3\u4e3a\u5f92\u3002\u8fd9\u4e00\u5e74\u591a\u6765\uff0c\u94c1\u955c\u5fc3 \u6bcf\u4e00\u5ff5\u53ca\uff0c\u6094\u6127\u65e0\u5df2\u3002\u800c\u4eca\u88ab\u4e8e\u627f\u73e0\u5f53\u7740\u5e08\u5f1f\u5e08\u59b9\u7684\u9762\u63d0\u8d77\uff0c\u4e0d\u89c9\u9762\u7ea2\u8fc7\u8033\uff0c\u5bf9\u4e8e\u627f\u73e0\u4e5f\u662f \u5927\u4e3a\u4e0d\u8c05\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u4e3a\u4f60\u523b\u9aa8\u76f8\u601d\uff0c\u51e0\u756a\u820d\u547d\uff0c\u4f60\u5bf9\u6211\u90a3\u80a1\u51b7\u6de1\u4e5f\u8fd8\u7f62\u4e86\uff0c\u800c\u4eca\u53c8\u5f53 \u7740\u5e08\u5f1f\u5e08\u59b9\uff0c\u4ee4\u6211\u96be\u582a\u3002\u201d\u8981\u4e0d\u662f\u4ed6\u76fc\u671b\u4e8e\u627f\u73e0\u56de\u5fc3\u8f6c\u610f\uff0c\u51e0\u4e4e\u5c31\u8981\u53d1\u4f5c\u3002 \u3000\u3000\u77f3\u6587\u7ea8\u4eba\u751a\u673a\u4f36\uff0c\u89c1\u5e08\u5144\u7684\u795e\u8272\u4e0d\u5bf9\uff0c\u77e5\u5b9a\u5b9a\u6709\u9690\u60c5\uff0c\u4ed6\u4eec\u4e00\u5411\u656c\u754f\u5e08\u5144\uff0c\u4e0d\u6562\u591a\u95ee\u3002 \u94c1\u955c\u5fc3\u601d\u6f6e\u8d77\u4f0f\uff0c\u8f6c\u4e86\u65e0\u6570\u5ff5\u5934\uff0c\u5ffd\u9053\uff1a\u201c\u6210\u5e08\u5f1f\uff0c\u4f60\u628a\u8fd9\u628a\u5b9d\u5251\u7ed9\u6211\uff0c\u6211\u89c1\u4e86\u5e08\u7236\u518d\u4ea4\u7ed9 \u4ed6\u3002\u201d\u4e8e\u627f\u73e0\u6b63\u6b32\u51fa\u8a00\u62e6\u963b\uff0c\u6210\u6d77\u5c71\u5df2\u9053\uff1a\u201c\u6211\u5e74\u8f7b\u5fb7\u8584\uff0c\u6b66\u529f\u4f4e\u5fae\uff0c\u4f69\u8fd9\u628a\u5251\u65e5\u591c\u62c5\u5fc3\uff0c \u4ea4\u7ed9\u5e08\u5144\u4fdd\u7ba1\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8fd9\u662f\u77f3\u5bb6\u4e4b\u7269\uff0c\u6587\u7ea8\uff0c\u4f60\u4eec\u5728\u519b\u65c5\u4e4b\u4e2d\uff0c\u7559 \u7740\u4e00\u628a\u5b9d\u5251\u9632\u8eab\u4e5f\u597d\u3002\u201d\u94c1\u955c\u5fc3\u6124\u7136\u4e8e\u8272\uff0c\u77f3\u6587\u7ea8\u8e0c\u8e87\u534a\u664c\uff0c\u4ecd\u662f\u8bf4\u9053\uff1a\u201c\u8c22\u8c22\u59d0\u59d0\u5173\u5fc3\uff0c \u6211\u7239\u7239\u65e9\u5df2\u8bf4\u8fc7\uff0c\u94c1\u5e08\u5144\u867d\u662f\u5916\u59d3\uff0c\u806a\u660e\u624d\u667a\u8fdc\u975e\u6211\u6240\u53ef\u53ca\uff0c\u5c06\u6765\u8fd9\u628a\u5ba4\u5251\u8981\u4f20\u7ed9\u5e08\u5144\uff0c\u53eb \u6211\u4e0d\u53ef\u591a\u5fc3\u3002\u8fd9\u8bdd\uff0c\u7239\u7239\u4e5f\u8bb8\u672a\u66fe\u5bf9\u94c1\u5e08\u5144\u8bf4\u8fc7\uff0c\u6211\u5374\u65e9\u5df2\u77e5\u9053\u3002\u8fd9\u628a\u5251\u4ea4\u7ed9\u5e08\u5144\uff0c\u6b63\u662f\u6211 \u7239\u7239\u7684\u672c\u610f\u3002\u94c1\u5e08\u5144\uff0c\u4f60\u63a5\u4e86\u5427\uff01\u201d \u3000\u3000\u94c1\u955c\u5fc3\u6599\u4e0d\u5230\u5e08\u5f1f\u5e08\u59b9\u7adf\u662f\u5bf9\u4ed6\u5982\u6b64\u656c\u7231\uff0c\u60f3\u8d77\u5e08\u7236\u7684\u6069\u4e49\uff0c\u5185\u6127\u4e8e\u5fc3\uff0c\u773c\u6cea\u51e0\u4e4e\u8981\u6ef4 \u4e86\u51fa\u6765\uff0c\u53cd\u800c\u4e0d\u597d\u610f\u601d\u53bb\u63a5\u90a3\u628a\u5b9d\u5251\u3002\u77f3\u6587\u7ea8\u5012\u6301\u5251\u67c4\uff0c\u76f4\u9012\u5230\u4e86\u94c1\u955c\u5fc3\u624b\u4e2d\uff0c\u4e8e\u627f\u73e0\u51b7\u51b7 \u7b11\u9053\uff1a\u201c\u77f3\u8001\u82f1\u96c4\u4ed7\u7740\u8fd9\u628a\u5b9d\u5251\u66fe\u5e72\u4e86\u591a\u5c11\u4fa0\u4e49\u4e4b\u4e8b\uff0c\u94c1\u516c\u5b50\uff0c\u4f60\u53ef\u4e0d\u8981\u5e78\u8d1f\u4e86\u8fd9\u628a\u5b9d\u5251 \u554a\uff01\u201d\u94c1\u955c\u5fc3\u9762\u4e0a\u4e00\u7ea2\uff0c\u4f46\u968f\u5373\u60f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u82f1\u96c4\u5b9d\u5251\u76f8\u5f97\u76ca\u5f70\uff0c\u6211\u6709\u4e86\u8fd9\u628a\u5b9d\u5251\uff0c\u6b66\u6797 \u4e2d\u4eba\u66f4\u8981\u5bf9\u6211\u522e\u76ee\u76f8\u770b\u4e86\uff0c\u82e5\u80fd\u4ed7\u7740\u8fd9\u628a\u5b9d\u5251\uff0c\u505a\u51fa\u4e00\u756a\u5927\u4e8b\uff0c\u5c06\u6765\u89c1\u4e86\u5e08\u7236\uff0c\u4e5f\u597d\u8bf4 \u8bdd\u3002\u201d\u5982\u6b64\u4e00\u60f3\uff0c\u4fbf\u5766\u7136\u5730\u5c06\u8fd9\u628a\u5b9d\u5251\u63a5\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6587\u7ea8\u3001\u6d77\u5c71\uff0c\u4f60\u4eec\u6253\u7b97\u5982\u4f55\uff1f\u201d\u77f3\u6587\u7ea8\u9053\uff1a\u201c\u8fd9\u91cc\u53d8\u51fa\u610f\u5916\uff0c\u6211\u4e5f\u4e0d\u77e5\u8be5 \u5f53\u5982\u4f55\u4e86\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u6211\u8981\u8fdb\u4eac\u4e00\u8d9f\uff0c\u8def\u8fc7\u676d\u5dde\u8001\u5bb6\u3002\u8fd9\u91cc\u4e0d\u4e45\u5fc5\u5c06\u5927\u4e71\uff0c\u6bd5\u64ce\u5929\u4e5f\u5b9a \u7136\u8986\u8d25\u65e0\u7591\uff0c\u6211\u770b\u4f60\u4eec\u5927\u53ef\u4e0d\u5fc5\u518d\u6cbe\u8fd9\u8d9f\u6d51\u6c34\u4e86\uff0c\u4e0d\u5982\u5230\u6211\u5bb6\u4e2d\u6682\u907f\u4e00\u65f6\uff0c\u5f85\u6e05\u5e73\u4e4b\u540e\uff0c\u518d \u53bb\u8bbf\u5bfb\u5e08\u7236\u5427\u3002\u201d\u6210\u6d77\u5c71\u5251\u7709\u4e00\u626c\uff0c\u5927\u6709\u4e0d\u4ee5\u4e3a\u7136\u4e4b\u610f\uff0c\u94c1\u955c\u5fc3\u6b63\u60f3\u53d1\u8bdd\uff0c\u4e8e\u627f\u73e0\u62a2\u7740\u8bf4 \u9053\uff1a\u201c\u6bd5\u64ce\u5929\u786e\u662f\u96be\u4e8e\u76f8\u5904\uff0c\u4f46\u53f6\u6210\u6797\u8fd8\u5728\u5c6f\u6eaa\uff0c\u72ec\u6297\u5341\u4e07\u5b98\u519b\uff0c\u4e0d\u5982\u4f60\u4eec\u4e0a\u5c6f\u6eaa\u4e5f\u597d\u3002\u201d \u6210\u6d77\u5c71\u9053\uff1a\u201c\u6211\u4e0e\u53f6\u5927\u54e5\u867d\u7136\u76f8\u4ea4\u4e0d\u6df1\uff0c\u5374\u4e5f\u77e5\u9053\u4ed6\u662f\u5fe0\u809d\u4e49\u80c6\u7684\u6c49\u5b50\uff0c\u65e2\u7136\u4ed6\u6b63\u8981\u4eba\u76f8 \u52a9\uff0c\u6211\u81ea\u8be5\u5230\u5c6f\u6eaa\u52a9\u4ed6\u4e00\u81c2\u4e4b\u529b\u3002\u7ea8\u59b9\uff0c\u4f60\u5462\uff1f\u201d\u77f3\u6587\u7ea8\u6beb\u4e0d\u8e0c\u8e87\u5730\u9053\uff1a\u201c\u4f60\u53bb\u54ea\u513f\uff0c\u6211\u81ea \u7136\u968f\u7740\u4f60\u53bb\u3002\u201d\u94c1\u955c\u5fc3\u867d\u7136\u6697\u602a\u4e8e\u627f\u73e0\u591a\u4e8b\uff0c\u89c1\u4ed6\u4eec\u53bb\u610f\u575a\u51b3\uff0c\u5374\u4e5f\u4e0d\u4fbf\u963b\u62e6\u3002 \u3000\u3000\u5f53\u4e0b\u6210\u3001\u77f3\u4e24\u4eba\u4e0e\u5e08\u5144\u522b\u8fc7\uff0c\u5e26\u4e86\u90a3\u5341\u4e03\u9a91\u4eba\u9a6c\uff0c\u62e8\u8f6c\u9a6c\u5934\uff0c\u6295\u5411\u5c6f\u6eaa\u8def\u4e0a\u53bb\u4e86\u3002\u6f6e\u97f3 \u548c\u5c1a\u9053\uff1a\u201c\u627f\u73e0\uff0c\u4f60\u5462\uff1f\u201d\u4e8e\u627f\u73e0\u60f3\u4e86\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u6211\u5e08\u7236\u5e08\u6bcd\u5df2\u4e0a\u4eac\u90fd\uff0c\u6211\u60f3\u53bb\u89c1\u89c1\u4ed6 \u4eec\u3002\u201d\u94c1\u955c\u5fc3\u5927\u559c\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u6b63\u597d\u540c\u8def\u4e86\u3002\u201d\u5fc3\u4e2d\u8fd8\u8ba4\u4e3a\u662f\u4e8e\u627f\u73e0\u542c\u4e86\u4ed6\u7684\u529d\u544a\uff0c\u6545 \u6b64\u8fdc\u79bb\u6b64\u5730\u3002\u54ea\u77e5\u4e8e\u627f\u73e0\u662f\u53e6\u6709\u4e00\u756a\u5fc3\u4e8b\uff0c\u4e0e\u94c1\u955c\u5fc3\u6240\u60f3\u7684\u5b8c\u5168\u4e24\u6837\u3002\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u6211\u4e5f \u60f3\u89c1\u89c1\u4e39\u67ab\uff0c\u90a3\u4e48\u54b1\u4eec\u5c31\u540c\u8def\u5427\u3002\u201d\u4e8e\u627f\u73e0\u672c\u6765\u60f3\u529d\u6f6e\u97f3\u548c\u5c1a\u4e5f\u4e0a\u5c6f\u6eaa\uff0c\u8f6c\u5ff5\u4e00\u60f3\uff0c\u53f6\u6210\u6797 \u5df2\u6709\u51cc\u4e91\u51e4\u3001\u6210\u6d77\u5c71\u3001\u77f3\u6587\u7ea8\u7b49\u5f97\u529b\u7684\u4eba\u624b\u76f8\u52a9\uff0c\u6f6e\u97f3\u548c\u5c1a\u53ea\u662f\u5339\u592b\u4e4b\u52c7\uff0c\u53bb\u4e0d\u53bb\u6ca1\u6709\u5927\u5173 \u7cfb\uff0c\u6709\u4ed6\u540c\u8def\uff0c\u4e0d\u6015\u94c1\u955c\u5fc3\u7ea0\u7f20\uff0c\u800c\u4e14\u5e08\u7236\u8fdb\u4eac\uff0c\u96be\u4fdd\u6ca1\u6709\u5371\u9669\uff0c\u6f6e\u97f3\u548c\u5c1a\u8fdb\u4eac\uff0c\u81ea\u6709\u4ed6\u7684 \u7528\u5904\uff0c\u4e5f\u4fbf\u6b23\u7136\u9053\u597d\u4e86\u3002 \u3000\u3000\u4e09\u4eba\u4e00\u8def\u540c\u884c\uff0c\u94c1\u955c\u5fc3\u6bcf\u6bcf\u501f\u6545\u4e0e\u4e8e\u627f\u73e0\u8c08\u8bf4\uff0c\u4f46\u89c1\u4e8e\u627f\u73e0\u7684\u795e\u6001\u603b\u662f\u6de1\u6de1\u6f20\u6f20\u7684\uff0c\u7aef \u5e84\u4e4b\u4e2d\u5e26\u7740\u77dc\u6301\uff0c\u6bcf\u5f53\u8bdd\u5934\u8bf4\u5230\u5979\u7684\u8eab\u4e0a\u4fbf\u626f\u4e86\u5f00\u53bb\uff0c\u53c8\u6709\u6f6e\u97f3\u548c\u5c1a\u5728\u65c1\uff0c\u66f4\u662f\u4e0d\u4fbf\u6df1\u8c08\uff0c \u9976\u662f\u94c1\u955c\u5fc3\u81ea\u8d1f\u806a\u660e\uff0c\u5bf9\u7740\u4e8e\u627f\u73e0\u8fd9\u6837\u7684\u6001\u5ea6\uff0c\u4e5f\u6709\u65e0\u53ef\u5948\u4f55\u4e4b\u611f\uff0c\u5fc3\u4e2d\u7aef\u7684\u662f\u53c8\u7231\u53c8\u607c\uff0c \u4e8e\u627f\u73e0\u5374\u53ea\u5f53\u4e0d\u77e5\uff0c\u4e00\u76f4\u628a\u4ed6\u5f53\u4f5c\u5144\u957f\u4e00\u822c\u770b\u5f85\uff0c\u5c0a\u656c\u4e4b\u4e2d\uff0c\u4fdd\u6301\u8ddd\u79bb\u3002\u611f\u60c5\u771f\u662f\u4e00\u4ef6\u5947\u5999 \u7684\u4e1c\u897f\uff0c\u94c1\u955c\u5fc3\u66fe\u5bf9\u4e8e\u627f\u73e0\u523b\u9aa8\u76f8\u601d\uff0c\u5728\u79bb\u5f00\u5979\u7684\u65f6\u5019\u5ff5\u5ff5\u4e0d\u5fd8\uff0c\u800c\u4eca\u671d\u5915\u76f8\u5904\uff0c\u5374\u53cd\u800c\u6e10 \u6e10\u5730\u51b7\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u5728\u94c1\u955c\u5fc3\u5fc3\u91cc\uff0c\u603b\u4ee5\u4e3a\u4ed6\u4e00\u5207\u90fd\u4e3a\u4e86\u4e8e\u627f\u73e0\uff0c\u7eb5\u7136\u4e8e\u627f\u73e0\u4e0d\u8868\u793a\u611f\u6fc0\uff0c\u4e5f\u603b\u8be5\u5bf9\u4ed6\u4eb2\u8fd1 \u4e00\u70b9\u624d\u662f\uff0c\u5c82\u77e5\u4e8e\u627f\u73e0\u7adf\u662f\u5bf9\u4ed6\u5982\u6b64\u51b7\u6de1\uff0c\u6bd4\u8d77\u5728\u5927\u7406\u4e4b\u65f6\uff0c\u53c8\u597d\u4f3c\u751f\u758f\u4e86\u8bb8\u591a\u3002\u5c24\u5176\u4ee4\u5f97 \u4ed6\u70e6\u607c\u7684\u662f\uff0c\u4ed6\u6bcf\u6bcf\u4e8e\u6709\u610f\u65e0\u610f\u4e4b\u95f4\uff0c\u8bd5\u51fa\u4e8e\u627f\u73e0\u5bf9\u53f6\u6210\u6797\u7684\u5fc3\u610f\u3002\u4e8e\u627f\u73e0\u597d\u50cf\u6781\u529b\u907f\u514d\u63d0 \u8d77\u53f6\u6210\u6797\uff0c\u4e00\u5f53\u522b\u4eba\u63d0\u8d77\u4ed6\u65f6\uff0c\u5979\u8138\u4e0a\u5c31\u4e0d\u81ea\u7981\u5730\u9732\u51fa\u4e00\u79cd\u5947\u5f02\u7684\u795e\u8272\uff0c\u773c\u775b\u4e5f\u5e73\u6dfb\u4e86\u5149 \u5f69\uff0c\u5374\u53c8\u4f3c\u5e26\u7740\u6de1\u6de1\u7684\u54c0\u6101\u3001\u4e0d\u5b89\u548c\u60f6\u60d1\uff0c\u94c1\u955c\u5fc3\u5728\u8fd9\u65b9\u9762\u6700\u4e3a\u654f\u611f\uff0c\u4ed6\u5728\u4e8e\u627f\u73e0\u7684\u773c\u775b\u91cc \u770b\u51fa\u4e86\u4e8e\u627f\u73e0\u5bf9\u53f6\u6210\u6797\u7684\u5fc3\u610f\uff0c\u518d\u8054\u60f3\u8d77\u81ea\u5df1\u8fd9\u6b21\u5192\u4e86\u8fd9\u4e48\u5927\u7684\u98ce\u9669\uff0c\u8d76\u6765\u4f1a\u5979\uff0c\u5979\u5374\u662f\u4e00 \u89c1\u9762\u5c31\u529d\u81ea\u5df1\u4e0a\u5c6f\u6eaa\u53bb\u52a9\u53f6\u6210\u6797\uff0c\u770b\u6765\u5979\u7adf\u662f\u6781\u4e3a\u770b\u91cd\u53f6\u6210\u6797\u7684\u4e8b\u4e1a\u3002\u4e8e\u627f\u73e0\u4e0d\u7231\u94c1\u955c\u5fc3\uff0c \u4e5f\u8bb8\u94c1\u955c\u5fc3\u8fd8\u80fd\u5fcd\u53d7\uff0c\u4f46\u5f53\u4ed6\u611f\u89c9\u5230\u4e8e\u627f\u73e0\u5c06\u53f6\u6210\u6797\u770b\u5f97\u5728\u4ed6\u4e4b\u4e0a\u7684\u65f6\u5019\uff0c\u5c31\u5927\u5927\u5730\u4f24\u53ca\u4e86 \u4ed6\u7684\u81ea\u5c0a\uff01 \u3000\u3000\u56e0\u6b64\uff0c\u5728\u6709\u4e9b\u65f6\u5019\uff0c\u4ed6\u4f1a\u5ffd\u7136\u60f3\u8d77\u6c90\u71d5\u6765\uff0c\u60f3\u8d77\u6c90\u71d5\u7684\u5584\u89e3\u4eba\u610f\uff0c\u60f3\u8d77\u6c90\u71d5\u8c08\u5410\u98ce\u96c5\uff0c \u60f3\u8d77\u6c90\u71d5\u4fcf\u4e3d\u7684\u989c\u5bb9\uff0c\u60f3\u8d77\u6c90\u71d5\u5bf9\u4ed6\u7684\u871c\u610f\u67d4\u60c5\uff0c\u800c\u5c24\u5176\u4ee4\u4ed6\u611f\u5230\u9a84\u50b2\u7684\u662f\u6c90\u71d5\u4ee5\u90a3\u6837\u5c0a\u8d35 \u7684\u8eab\u4efd\uff0c\u5bf9\u4ed6\u5982\u662f\u5982\u6b64\u503e\u5fc3\uff01\u5f53\u7136\uff0c\u82e5\u662f\u5c06\u4e8e\u627f\u73e0\u548c\u6c90\u71d5\u6bd4\u8f83\u7684\u8bdd\uff0c\u4e8e\u627f\u73e0\u662f\u5dfe\u5e3c\u4e4b\u4e2d\u7f55\u89c1 \u7684\u5947\u5973\u5b50\uff0c\u6c90\u71d5\u603b\u4f3c\u5c11\u5374\u90a3\u4e48\u4e00\u5c42\u5149\u5f69\uff0c\u6ca1\u6709\u4e8e\u627f\u73e0\u90a3\u79cd\u4ee4\u4eba\u5fc3\u7075\u9707\u64bc\u7684\u9b45\u529b\uff01\u7136\u800c\uff0c\u4f5c\u4e3a \u4e00\u4e2a\u5c11\u5973\u7684\u8bdd\uff0c\u6c90\u71d5\u5374\u53c8\u4f3c\u66f4\u4e3a\u60f9\u4eba\u559c\u7231\u3002\u800c\u4e14\u6bd4\u8d77\u9664\u4e86\u4e8e\u627f\u73e0\u4e4b\u5916\uff0c\u6240\u6709\u7684\u4ed6\u6240\u66fe\u89c1\u8fc7\u7684 \u5c11\u5973\u6765\uff0c\u90a3\u4e48\u6c90\u71d5\u5c31\u66f4\u4f3c\u9e64\u7acb\u9e21\u7fa4\u3002\u800c\u4e14\uff0c\u6700\u91cd\u8981\u7684\u4e00\u70b9\u662f\uff0c\u548c\u4e8e\u627f\u73e0\u5728\u4e00\u8d77\u7684\u65f6\u5019\uff0c\u4e0d\u77e5 \u600e\u7684\uff0c\u4f1a\u4ee4\u4ed6\u611f\u5230\u81ea\u5351\uff0c\u5f80\u5f80\u4e5f\u5c31\u56e0\u6b64\u4e0d\u5b89\u548c\u70e6\u8e81\uff0c\u548c\u6c90\u71d5\u5728\u4e00\u8d77\u7684\u65f6\u5019\uff0c\u5374\u4ee4\u4ed6\u611f\u5230\u81ea\u5df1 \u7684\u9ad8\u8d35\u548c\u5185\u5fc3\u7684\u6ee1\u8db3\uff0c\u56e0\u800c\u4e5f\u5c31\u611f\u5230\u559c\u60a6\u548c\u5fc3\u5883\u7684\u548c\u5e73\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u548c\u4e8e\u627f\u73e0\u7684\u611f\u60c5\uff0c\u968f\u7740\u65c5\u7a0b\u7684\u7f29\u51cf\uff0c\u8ddd\u79bb\u53cd\u800c\u8d8a\u6765\u8d8a\u589e\u5927\u4e86\uff0c\u5404\u4eba\u7684\u5185\u5fc3\u91cc\uff0c\u4e5f \u8d8a\u6765\u8d8a\u611f\u89c9\u5230\u8fd9\u4e00\u70b9\u4e86\uff0c\u53ea\u6709\u6f6e\u97f3\u548c\u5c1a\u8fd8\u662f\u4e00\u70b9\u4e5f\u770b\u4e0d\u51fa\u6765\uff0c\u8fd8\u4ee5\u4e3a\u4ed6\u4eec\u662f\u4e00\u5bf9\u5929\u9053\u5730\u8bbe\u7684 \u201c\u91d1\u7ae5\u7389\u5973\u201d\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u8e0f\u8fdb\u4e86\u6d59\u6c5f\u7684\u8fb9\u5883\uff0c\u8fd9\u5df2\u7ecf\u662f\u5b98\u519b\u548c\u4e49\u519b\u52bf\u529b\u7684\u4ea4\u754c\u4e4b\u5904\uff0c\u4e00\u8def\u4e0a\u4eba\u70df\u7a00\u5c11\uff0c\u8d70 \u4e86\u8bb8\u4e45\uff0c\u624d\u53d1\u73b0\u8def\u8fb9\u7684\u4e00\u5ea7\u8336\u4ead\uff0c\u8336\u4ead\u7684\u4e3b\u4eba\u662f\u4e2a\u8001\u5a46\u5a46\uff0c\u5979\u7684\u513f\u5b50\u88ab\u5b98\u519b\u62c9\u53bb\u5f53\u9a6c\u592b\uff0c\u5979 \u5e74\u7eaa\u8001\u4e86\uff0c\u65e0\u6cd5\u9003\u96be\uff0c\u800c\u4e14\u5728\u5979\u4e00\u751f\u4e4b\u4e2d\u9003\u96be\u7684\u6b21\u6570\u592a\u591a\u4e86\uff0c\u8fd9\u4e00\u6b21\u5979\u89c9\u5f97\u81ea\u5df1\u5df2\u8001\uff0c\u80fd\u6d3b \u5230\u51e0\u65f6\u4fbf\u7b97\u51e0\u65f6\uff0c\u4e5f\u5c31\u4e0d\u60f3\u518d\u9003\u96be\u4e86\uff0c\u56e0\u6b64\u4ecd\u50cf\u5f80\u65e5\u4e00\u6837\u5730\u5728\u8def\u65c1\u5356\u8336\u3002 \u3000\u3000\u4ed6\u4eec\u8d76\u4e86\u534a\u5929\u7684\u8def\uff0c\u6b63\u81ea\u611f\u5230\u53e3\u6e34\uff0c\u4fbf\u8fdb\u8336\u4ead\u559d\u8336\u6b47\u606f\uff0c\u548c\u90a3\u8001\u5a46\u5a46\u95f2\u804a\u4e86\u4e00\u4f1a\uff0c\u6709\u4e24 \u4e2a\u4eba\u4ece\u8def\u4e0a\u8d70\u8fc7\u6765\uff0c\u5176\u4e2d\u4e00\u4eba\uff0c\u53eb\u9053\uff1a\u201c\u597d\u9a6c\uff0c\u597d\u9a6c\uff01\u201d\u8bf4\u7684\u662f\u9887\u4e3a\u751f\u786c\u7684\u5317\u65b9\u8bdd\uff0c\u4e8e\u627f\u73e0 \u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u8499\u53e4\u88c5\u675f\uff0c\u76f8\u8c8c\u7c97\u91ce\u7684\u9b41\u68a7\u6c49\u5b50\u548c\u4e00\u4e2a\u8eab\u6750\u77ee\u5c0f\u3001\u7c7b\u4f3c\u516c\u5dee\u6a21\u6837\u7684\u4eba\u8d70 \u4e86\u8fdb\u6765\u3002\u6709\u5206\u6559\uff1a \u3000\u3000\u84e6\u5730\u65c5\u9014\u9022\u602a\u5ba2\uff0c\u7591\u4e91\u9635\u9635\u66f4\u96be\u6d88\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e00\u56de\u3000\u751f\u6b7b\u96be\u731c\u3000\u5973\u513f\u60c5\u66f2\u6298\u3000\u662f\u975e\u83ab\u8fa8\u3000\u516c\u5b50\u610f\u8ff7\u79bb \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e09\u5341\u4e00\u56de\u3000\u751f\u6b7b\u96be\u731c\u3000\u5973\u513f\u60c5\u66f2\u6298\u3000\u662f\u975e\u83ab\u8fa8\u3000\u516c\u5b50\u610f\u8ff7\u79bb \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8fd9\u4e2a\u516c\u5dee\u6a21\u6837\u7684\u4eba\u597d\u4f3c\u5728\u54ea\u513f\u89c1\u8fc7\u4f3c\u7684\uff0c\u4ed4\u7ec6\u4e00\u60f3\uff0c\u5374\u539f\u6765\u662f\u4e24\u5e74\u591a \u4ee5\u524d\uff0c\u66fe\u5728\u957f\u6c5f\u5317\u5cb8\u4e00\u4e2a\u5c0f\u9547\u7684\u9152\u5e97\u91cc\uff0c\u5e2e\u5fa1\u6797\u519b\u7edf\u9886\u5a04\u6850\u5b59\u6349\u62ff\u5468\u5c71\u6c11\u592b\u5987\u7684\u90a3\u4e2a\u5e26\u5200\u5f85 \u536b\u891a\u7384\u3002 \u3000\u3000\u891a\u7384\u4e5f\u8ba4\u51fa\u4e86\u4e8e\u627f\u73e0\uff0c\u4ed6\u66fe\u7ecf\u5403\u8fc7\u4e8e\u627f\u73e0\u7684\u4e8f\uff0c\u964c\u8def\u76f8\u9022\uff0c\u5fc3\u4e2d\u6697\u60ca\uff0c\u4f46\u4ecd\u7136\u4e0d\u52a8\u58f0\u8272 \u5730\u966a\u7740\u90a3\u4e2a\u8499\u53e4\u6b66\u58eb\u8fdb\u6765\u559d\u8336\u3002 \u3000\u3000\u90a3\u8499\u53e4\u6b66\u58eb\u5750\u4e0b\u4e4b\u540e\uff0c\u4e00\u5bf9\u773c\u775b\u5c31\u5c3d\u5f80\u4e8e\u627f\u73e0\u7684\u8eab\u4e0a\u77a7\uff0c\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e2d\u56fd\u5357\u65b9\u7684 \u5973\u5b50\u539f\u6765\u662f\u8fd9\u4e48\u5a07\u6ef4\u6ef4\u7684\uff0c\u82e5\u662f\u5230\u4e86\u54b1\u4eec\u6f20\u5916\uff0c\u4e00\u9635\u5927\u98ce\u5c31\u80fd\u628a\u5979\u522e\u8d77\uff01\u201d\u6f6e\u97f3\u548c\u5c1a\u53cc\u773c\u4e00 \u7741\uff0c\u4fbf\u60f3\u53d1\u4f5c\uff0c\u4e8e\u627f\u73e0\u629b\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u5c06\u4ed6\u6b62\u4f4f\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4ece\u6f20\u5916\u6765\u7684\u5417\uff1f\u597d\u8fdc\u7684\u8def \u5440\uff01\u201d\u90a3\u8499\u53e4\u6b66\u571f\u89c1\u4e8e\u627f\u73e0\u7b54\u8bdd\uff0c\u5927\u4e3a\u9ad8\u5174\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u7279\u6765\u770b\u770b\u4e2d\u539f\u7684\u98ce\u5149\uff0c\u53ef\u60dc\u78b0 \u4e0a\u4e86\u6253\u4ed7\u3002\u4f60\u8fd9\u4f4d\u5c0f\u59d1\u5a18\u662f\u4ece\u5357\u8fb9\u6765\u7684\u5417\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u662f\u5440\u3002\u201d\u90a3\u8499\u65b9\u6b66\u58eb\u9053\uff1a\u201c\u4f60\u4e0d \u6015\u90a3\u4e9b\u5f3a\u76d7\u62a2\u4f60\u505a\u538b\u5be8\u592b\u4eba\u5417\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u8c01\u8bf4\u4ed6\u4eec\u662f\u5f3a\u76d7\uff0c\u90a3\u4e9b\u4e49\u519b\u5927\u5c0f\u5b98\u5175\u5bf9\u4eba\u6c11 \u90fd\u662f\u548c\u548c\u6c14\u6c14\u7684\uff01\u201d\u90a3\u8499\u53e4\u6b66\u58eb\u9053\uff1a\u201c\u771f\u7684\uff1f\u4f60\u8fd9\u4e48\u8bf4\uff0c\u6211\u8fd8\u4e0d\u4fe1\u5462\u3002\u201d\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u542c\u8bf4 \u90a3\u8fb9\u6709\u4e00\u4e2a\u7ea2\u5dfe\u5973\u8d3c\uff0c\u5f88\u662f\u5389\u5bb3\uff0c\u662f\u771f\u7684\u5417\uff1f\u201d\u4e8e\u627f\u73e0\u5fc3\u5934\u4e00\u9707\uff0c\u9053\uff1a\u201c\u5343\u771f\u4e07\u786e\uff0c\u90a3\u4f4d\u5973 \u5934\u9886\u6211\u8fd8\u66fe\u89c1\u8fc7\uff0c\u540d\u53eb\u51cc\u4e91\u51e4\uff01\u4f60\u8ba4\u5f97\u5979\uff1f\u201d\u90a3\u8499\u53e4\u6b66\u58eb\u7ad9\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u8ba4\u5f97\uff0c\u4f46\u6211 \u6709\u51e0\u4f4d\u670b\u53cb\u524d\u4e24\u4e2a\u6708\u5c31\u52a8\u8eab\u5230\u5357\u65b9\u6765\uff0c\u6b63\u662f\u4e3a\u4e86\u627e\u5979\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u90a3\u51e0\u4f4d\u8d35\u53cb\u53eb\u4ec0\u4e48\u540d \u5b57\uff0c\u4e3a\u4ec0\u4e48\u8981\u627e\u5979\uff1f\u201d\u90a3\u8499\u53e4\u6b66\u58eb\u8be7\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u59d1\u5a18\u597d\u5947\u602a\uff0c\u6253\u542c\u8fd9\u4e9b\u6c5f\u6e56\u4e0a\u7684\u4e8b\u60c5\u505a\u4ec0 \u4e48\uff1f\u54c8\uff0c\u4f60\u8fd9\u6837\u5f31\u4e0d\u7981\u98ce\u7684\u59d1\u5a18\u4e5f\u4f69\u7740\u5b9d\u5251\uff0c\u4f60\u61c2\u5f97\u6b66\u827a\u5417\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u61c2\u662f\u4e0d\u61c2\uff0c\u4f46 \u8fd9\u4e16\u4e0a\u574f\u4eba\u592a\u591a\uff0c\u5e26\u4e00\u628a\u5251\u9632\u8eab\u4e5f\u662f\u597d\u7684\u3002\u201d\u90a3\u8499\u53e4\u6b66\u58eb\u5927\u7b11\uff0c\u9053\uff1a\u201c\u53ef\u60dc\u4e86\u8fd9\u628a\u5b9d\u5251\uff0c\u4e0d \u7792\u4f60\u8bf4\uff0c\u8981\u4e0d\u662f\u89c1\u4f60\u662f\u8fd9\u4e48\u9017\u4eba\u6b22\u559c\u7684\u5c0f\u59d1\u5a18\uff0c\u6211\u4e0d\u613f\u6b3a\u8d1f\u4f60\uff0c\u6211\u5c31\u8981\u505a\u4e00\u6b21\u574f\u4eba\u3002\u201d\u4e8e\u627f \u73e0\u4f5c\u4e86\u4e00\u4e2a\u5403\u60ca\u7684\u795e\u8272\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u662f\u574f\u4eba\uff1f\u201d\u90a3\u8499\u53e4\u6b66\u58eb\u9053\uff1a\u201c\u54b1\u4eec\u8499\u53e4\u7684\u6b66\u58eb\uff0c \u6700\u7231\u5b9d\u5200\u5b9d\u5251\uff0c\u62a2\u4eba\u7684\u5200\u5251\uff0c\u5728\u8499\u53e4\u7a00\u677e\u5e73\u5e38\u3002\u4f46\u4f60\u653e\u5fc3\uff0c\u6211\u4e0d\u62a2\u4f60\u7684\u3002\u201d\u8fb9\u8bf4\u8fb9\u8d70\u8fc7\u6765\uff0c \u5706\u788c\u788c\u7684\u773c\u775b\u76ef\u7740\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u957f\u5f97\u771f\u597d\u770b\uff0c\u5c31\u50cf\u54b1\u4eec\u4f20\u8bf4\u91cc\u90a3\u4e2a\u559c\u9a6c\u62c9\u96c5\u5c71\u7684\u4ed9\u5b50\u4e00 \u6837\u3002\u201d\u8bf4\u7740\uff0c\u8bf4\u7740\uff0c\u5df2\u6328\u5230\u4e86\u4e8e\u627f\u73e0\u8fd9\u5f20\u684c\u5b50\u4e0a\u6765\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u80e1\u8bf4\u516b\u9053\uff0c\u6562\u8c03\u620f\u5973\u5b50\u5417\uff1f\u201d\u90a3\u8499\u53e4\u6b66\u58eb\u7b11\u9053\uff1a\u201c\u4f60\u597d\u5c0f \u6c14\uff0c\u5728\u54b1\u4eec\u90a3\u8fb9\uff0c\u8c01\u6709\u4e86\u7f8e\u4e3d\u7684\u59bb\u5b50\uff0c\u522b\u4eba\u770b\u5979\uff0c\u505a\u4e08\u592b\u7684\u624d\u9ad8\u5174\u5462\u3002\u4f60\u662f\u5979\u7684\u4e08\u592b\u5417\uff1f\u201d \u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d\u8981\u80e1\u8bf4\uff0c\u54ce\uff0c\u6211\u6709\u8bdd\u95ee\u4f60\uff01\u201d\u90a3\u8499\u53e4\u6b66\u58eb\u5374\u5bf9\u7740\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u4f60\u8fd8 \u5e76\u4e0d\u662f\u5979\u7684\u4e08\u592b\uff0c\u90a3\u54b1\u770b\u5979\u4e24\u773c\uff0c\u66f4\u4e0d\u788d\u4f60\u7684\u4e8b\u4e86\u3002\u54c8\uff0c\u4f60\u8fd9\u4e2a\u6587\u5f31\u4e66\u751f\uff0c\u5c45\u7136\u4e5f\u4f69\u4e00\u628a\u5b9d \u5251\uff01\u201d\u94c1\u955c\u5fc3\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u773c\u7ea2\u5417\uff1f\u201d\u90a3\u8499\u53e4\u6b66\u58eb\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4e0d\u60f3\u62a2\u5979\u7684 \u5b9d\u5251\u5374\u60f3\u62a2\u4f60\u7684\uff01\u201d \u3000\u3000\u94c1\u955c\u5fc3\u201c\u563f\u201d\u7684\u4e00\u58f0\u51b7\u7b11\uff0c\u5de6\u624b\u4e00\u52fe\uff0c\u53f3\u638c\u659c\u7a7f\u800c\u51fa\uff0c\u5212\u4e86\u534a\u4e2a\u5706\u5f27\uff0c\u642d\u7740\u4e86\u90a3\u8499\u53e4\u6b66 \u58eb\u7684\u5bf8\u5173\u5c3a\u8155\u8109\uff0c\u8fd9\u6b63\u662f\u4e09\u5341\u516d\u624b\u5927\u64d2\u62ff\u624b\u4e2d\u7684\u4e00\u8bb0\u6781\u5389\u5bb3\u7684\u62db\u6570\uff0c\u94c1\u955c\u5fc3\u51fa\u624b\u5982\u98ce\uff0c\u66f4\u89c1 \u72e0\u8fa3\uff0c\u5b58\u5fc3\u8981\u628a\u8fd9\u8eab\u6750\u9b41\u609f\u7684\u8499\u53e4\u5927\u6c49\u5f53\u573a\u6454\u5012\uff0c\u5e76\u626d\u65ad\u4ed6\u7684\u624b\u8155\u3002 \u3000\u3000\u54ea\u77e5\u624b\u6307\u89e6\u5904\uff0c\u5982\u78b0\u94a2\u94c1\uff0c\u90a3\u8499\u53e4\u6b66\u58eb\u632f\u81c2\u4e00\u6325\uff0c\u201c\u556a\u201d\u7684\u4e00\u638c\u4fbf\u6253\u8fc7\u6765\uff0c\u94c1\u955c\u5fc3\u673a\u8b66 \u4e4b\u6781\uff0c\u4e00\u89c1\u4e0d\u5bf9\uff0c\u7acb\u523b\u8df3\u5f00\uff0c\u968f\u624b\u6284\u8d77\u4e86\u4e00\u5f20\u677f\u51f3\uff0c\u4f46\u542c\u5f97\u201c\u7830\u7830\u201d\u4e24\u58f0\u5927\u54cd\uff0c\u677f\u51f3\u7adf\u7ed9\u4ed6 \u4e00\u638c\u6253\u6298\u3002 \u3000\u3000\u90a3\u8499\u53e4\u6b66\u58eb\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u61c2\u5f97\u4e24\u624b\u6b66\u529f\uff0c\u8fd9\u66f4\u597d\u4e86\uff01\u201d\u6a2a\u8eab\u4e00\u6251\uff0c \u201c\u547c\u201d\u5730\u53c8\u662f\u4e00\u638c\uff0c\u94c1\u955c\u5fc3\u811a\u5c16\u4e00\u70b9\uff0c\u8df3\u8fc7\u680f\u6746\uff0c\u8fd9\u4e00\u638c\u6253\u5728\u652f\u6491\u8336\u4ead\u7684\u5706\u6728\u67f1\u4e0a\uff0c\u767b\u65f6\u74e6 \u7247\u788e\u843d\uff0c\u7070\u5c18\u84ec\u98de\uff0c\u90a3\u6728\u67f1\u659c\u503e\u6b32\u5012\uff0c\u6f6e\u97f3\u548c\u5c1a\u63d0\u8d77\u7985\u6756\uff0c\u5f80\u90a3\u67f1\u4e0a\u4e00\u9876\uff0c\u6728\u67f1\u6062\u590d\u4e86\u539f \u72b6\uff0c\u6f6e\u97f3\u548c\u5c1a\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u597d\u4e0d\u8bb2\u7406\uff0c\u62a2\u8fd9\u4f4d\u76f8\u516c\u7684\u4e1c\u897f\u5df2\u662f\u4e0d\u8be5\uff0c\u8fd8\u60f3\u6bc1\u4e86\u8001\u5a46\u5a46\u7684\u8336 \u4ead\u4e48\uff1f\u201d\u6b63\u6b32\u51fa\u624b\u52a9\u94c1\u955c\u5fc3\uff0c\u5374\u88ab\u4e8e\u627f\u73e0\u773c\u8272\u6240\u963b\u3002 \u3000\u3000\u90a3\u8499\u53e4\u6b66\u58eb\u89c1\u6f6e\u97f3\u548c\u5c1a\u9732\u4e86\u8fd9\u4e00\u624b\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\u8be5\u4e0e\u4e0d\u8be5\u3002\u5929\u4e0a\u7684\u5140 \u9e70\u6251\u5154\uff0c\u5730\u4e0b\u7684\u731b\u864e\u64d2\u7f8a\uff0c\u5929\u751f\u4e07\u7269\uff0c\u4ece\u6765\u90fd\u662f\u4ee5\u80dc\u8005\u4e3a\u5f3a\uff0c\u597d\uff0c\u4f60\u4e0d\u670d\u6c14\uff0c\u5f85\u54b1\u6536\u62fe\u4e86\u8fd9 \u5c0f\u5b50\u540e\uff0c\u518d\u4e0e\u4f60\u6bd4\u5212\u6bd4\u5212\uff01\u201d\u522b\u770b\u4ed6\u6c34\u725b\u822c\u7684\u8eab\u8eaf\uff0c\u817e\u632a\u7eb5\u8df3\u5012\u662f\u5229\u843d\u4e4b\u6781\uff0c\u98de\u8eab\u8dc3\u8fc7\u680f \u6746\uff0c\u51e0\u4e4e\u662f\u524d\u811a\u968f\u7740\u540e\u811a\uff0c\u8ffd\u5230\u4e86\u94c1\u955c\u5fc3\u7684\u80cc\u540e\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u94c1\u955c\u5fc3\u65e9\u5df2\u62d4\u5251\u51fa\u9798\uff0c\u4f46\u89c1\u4ed6\u53cd\u5251\u4e00\u6325\uff0c\u7d2b\u8679\u5982\u9713\uff0c\u8fd9\u628a\u5b9d\u5251\u4e43\u662f\u77f3\u60ca \u6d9b\u76d7\u81ea\u5927\u5185\u7684\u795e\u7269\u5229\u5668\uff0c\u6325\u52a8\u4e4b\u9645\uff0c\u5251\u5c16\u5c04\u51fa\u6de1\u7ea2\u8272\u7684\u5149\u6bd5\uff0c\u8000\u773c\u751f\u8f89\uff0c\u9976\u662f\u90a3\u8499\u53e4\u6b66\u58eb\u8eb2 \u95ea\u5f97\u5feb\uff0c\u5149\u8292\u63a0\u5904\uff0c\u5df2\u628a\u4ed6\u5934\u4e0a\u7684\u4e71\u53d1\u524a\u53bb\u4e86\u4e00\u5927\u7247\u3002 \u3000\u3000\u90a3\u8499\u53e4\u6b66\u58eb\u5403\u4e86\u4e00\u60ca\uff0c\u8d5e\u9053\uff1a\u201c\u597d\u4e00\u628a\u5b9d\u5251\uff01\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u6709\u672c\u4e8b\u4f60\u5c31\u62a2\u53bb\uff01\u201d\u5530\u3001 \u5530\u3001\u5530\u8fde\u73af\u4e09\u5251\uff0c\u7d2b\u8272\u7684\u5149\u534e\u4e00\u5708\u63a5\u7740\u4e00\u5708\uff0c\u7aef\u5982\u5927\u6d77\u6ce2\u7ffb\uff0c\u72c2\u6d9b\u62cd\u5cb8\u3002\u90a3\u8499\u53e4\u6b66\u58eb\u9053\uff1a \u201c\u5728\u6c49\u4eba\u4e4b\u4e2d\uff0c\u4f60\u7684\u6b66\u529f\u662f\u7f55\u89c1\u7684\u4e86\uff0c\u4f46\u8fd8\u4e0d\u914d\u8fd9\u628a\u5b9d\u5251\uff01\u201d\u638c\u529b\u4e00\u50ac\uff0c\u4e5f\u63a5\u7740\u8fde\u73af\u4e09\u638c\u53d1 \u51fa\uff0c\u638c\u98ce\u6fc0\u8361\u7802\u98de\u77f3\u8d70\uff0c\u94c1\u955c\u5fc3\u7684\u5b9d\u5251\uff0c\u8fd1\u4e0d\u4e86\u8eab\uff01 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u4e24\u4eba\u5fc3\u4e2d\u90fd\u662f\u6697\u6697\u53eb\u82e6\uff0c\u94c1\u955c\u5fc3\u7d20\u6765\u5bf9\u81ea\u5df1\u7684\u5251\u672f\u81ea\u8d1f\u4e4b\u6781\uff0c\u52a0\u4ee5\u53c8\u6709\u8fd9\u628a\u5927 \u5185\u5b9d\u5251\uff0c\u6ee1\u4ee5\u4e3a\u90a3\u8499\u53e4\u6b66\u58eb\u4f55\u582a\u4e00\u51fb\uff0c\u5c82\u77e5\u4ed6\u4e43\u662f\u4e00\u4e2a\u52b2\u654c\uff0c\u90a3\u8499\u53e4\u6b66\u58eb\u6a2a\u884c\u5927\u6f20\uff0c\u6240\u5411\u65e0 \u654c\uff0c\u5165\u5173\u4ee5\u6765\uff0c\u4e5f\u4ece\u672a\u9047\u8fc7\u5bf9\u624b\uff0c\u66f4\u662f\u6839\u672c\u672a\u66fe\u628a\u94c1\u955c\u5fc3\u653e\u5728\u773c\u5185\uff0c\u54ea\u77e5\u8fd9\u6837\u4e00\u4f4d\u201c\u6587\u5f31\u201d \u4e66\u751f\uff0c\u5251\u672f\u7adf\u7136\u7cbe\u5999\u5982\u65af\uff01 \u3000\u3000\u8f6c\u773c\u6597\u4e86\u4e94\u516d\u5341\u62db\uff0c\u90a3\u8499\u53e4\u6b66\u58eb\u7684\u638c\u529b\u8d8a\u50ac\u8d8a\u7d27\uff0c\u94c1\u955c\u5fc3\u7684\u5185\u529b\u652f\u6301\u4e0d\u4f4f\uff0c\u6e10\u89c9\u6c14\u5598\u529b \u75b2\uff0c\u96be\u4ee5\u4e3a\u7ee7\u3002\u6597\u5230\u5206\u9645\uff0c\u90a3\u8499\u53e4\u6b66\u571f\u5ffd\u5730\u8fde\u58f0\u602a\u5578\uff0c\u6709\u5982\u72fc\u55e5\uff0c\u53cc\u773c\u706b\u7ea2\uff0c\u548c\u8eab\u6251\u4e0a\uff01 \u3000\u3000\u4e8e\u627f\u73e0\u5403\u4e86\u4e00\u60ca\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5927\u6f20\u795e\u72fc\uff01\u201d\u90a3\u8499\u53e4\u6b66\u58eb\u6014\u4e86\u4e00\u6014\uff0c\u53bb\u52bf\u7a0d\u6162\uff0c\u88ab\u94c1\u955c \u5fc3\u56de\u8eab\u4e00\u5251\uff0c\u89e3\u4e86\u653b\u52bf\uff0c\u4f46\u90a3\u8499\u53e4\u6b66\u58eb\u7684\u6307\u5c16\u4ecd\u7136\u5212\u4e2d\u4e86\u94c1\u955c\u5fc3\u7684\u624b\u8155\uff0c\u5e78\u800c\u6709\u4e8e\u627f\u73e0\u8fd9\u4e48 \u4e00\u53eb\uff0c\u5206\u4e86\u4ed6\u7684\u5fc3\u795e\uff0c\u8981\u4e0d\u7136\u94c1\u955c\u5fc3\u7684\u5bf8\u5173\u5c3a\u8109\uff0c\u5fc5\u5c06\u88ab\u4ed6\u7684\u6307\u529b\u6240\u95ed\uff0c\u9976\u662f\u5982\u6b64\uff0c\u94c1\u955c\u5fc3 \u7684\u624b\u8155\u4e5f\u597d\u4f3c\u88ab\u706b\u7ef3\u70d9\u8fc7\u4e00\u822c\uff0c\u706b\u8fa3\u8fa3\u4f5c\u75db\uff0c\u5b9d\u5251\u4e5f\u51e0\u4e4e\u628a\u6301\u4e0d\u4f4f\u3002 \u3000\u3000\u90a3\u8499\u53e4\u6b66\u58eb\u5012\u8dc3\u4e09\u6b65\uff0c\u56de\u5934\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u662f\u8c01\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5927\u6f20\u795e\u72fc\uff0c\u4f60\u4e0d\u8ba4\u5f97 \u6211\uff0c\u6211\u8ba4\u5f97\u4f60\uff01\u201d\u8fd9\u8499\u53e4\u6b66\u58eb\u6b63\u662f\u6d51\u540d\u5524\u4f5c\u201c\u5927\u6f20\u795e\u72fc\u201d\u7684\u54c8\u6728\u56fe\uff0c\u4ed6\u867d\u7136\u540d\u9707\u6f20\u5317\uff0c\u5374\u662f \u521d\u5230\u4e2d\u539f\uff0c\u60f3\u4e0d\u5230\u7adf\u7ed9\u4e8e\u627f\u73e0\u53eb\u7834\u6765\u5386\uff0c\u5fc3\u4e2d\u5927\u7591\uff0c\u820d\u4e86\u94c1\u955c\u5fc3\uff0c\u56de\u8f6c\u8336\u4ead\uff0c\u5706\u7741\u53cc\u773c\uff0c\u5411 \u4e8e\u627f\u73e0\u6253\u91cf\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5fae\u5fae\u4e00\u7b11\uff0c\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u4f60\u60f3\u77e5\u9053\u6211\u662f\u8c01\uff1f\u201d\u5927\u6f20\u795e\u72fc\u9053\uff1a\u201c\u6b63\u8981\u8bf7\u6559\u4f60\u8fd9\u5c0f\u59d1 \u5a18\u4f55\u4ee5\u77e5\u9053\u4ffa\u7684\u6765\u5386\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u5c31\u6765\u4e00\u4e2a\u8d4c\u8d5b\u3002\u201d\u5927\u6f20\u795e\u72fc\u9053\uff1a\u201c\u600e\u4e48 \u8d4c\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u5a29\ue317\u5212\u6bd4\u5212\uff0c\u4f60\u4e0d\u662f\u5632\u7b11\u6211\u662f\u4e2a\u5f31\u4e0d\u7981\u98ce\u7684\u5973\u5b50\u5417\uff1f\u4f60\u4e0d\u662f\u60f3\u62a2\u4e00\u628a \u5b9d\u5251\u5417\uff1f\u597d\uff0c\u4f60\u82e5\u80dc\u5f97\u4e86\u6211\uff0c\u6211\u624b\u4e2d\u7684\u5b9d\u5251\u5949\u9001\uff1b\u4f60\u82e5\u7ed9\u6211\u6253\u8d25\u4e86\u5462\uff0c\u6211\u95ee\u4f60\u4e00\u53e5\uff0c\u4f60\u7b54\u6211 \u4e00\u53e5\uff0c\u4e0d\u8bb8\u6709\u534a\u53e5\u80e1\u8a00\u3002\u201d\u5927\u6f20\u795e\u72fc\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u59d1\u5a18\u4e0e\u6211\u6bd4\u5212\uff01\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48 \u4eba\uff1f\u82e5\u662f\u8fd9\u4f4d\u5927\u548c\u5c1a\u8981\u4e0e\u6211\u6bd4\u5212\uff0c\u90a3\u8fd8\u6709\u53ef\u8bf4\u3002\u4f60\u4e0e\u6211\u6bd4\u5212\uff1f\u54c8\u5403\uff0c\u4ffa\u5927\u6f20\u795e\u72fc\u867d\u7136\u6709\u65f6\u4e5f \u4e0d\u8bb2\u7406\uff0c\u5374\u8fd8\u4e0d\u81f3\u4e8e\u6b3a\u8d1f\u5c0f\u59d1\u5a18\uff01\u201d\u4e8e\u627f\u73e0\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u5e08\u6c14\u529b\u6bd4\u4f60\u5927\u5f97\u591a\uff0c\u4f60\u4e0e\u4ed6\u52a8 \u624b\uff0c\u4e0d\u8fc7\u5341\u62db\uff0c\u5fc5\u7136\u9001\u547d\uff0c\u54ea\u8fd8\u600e\u80fd\u4e0e\u6211\u8d4c\u8d5b\uff1f\u4f60\u6562\u77a7\u4e0d\u8d77\u6211\uff0c\u6211\u770b\u4f60\u7a7a\u6709\u4e00\u8eab\u86ee\u529b\uff0c\u6b66\u672f \u4e0a\u5934\uff0c\u4e5f\u8fd8\u7a00\u677e\u5f97\u5f88\u5462\uff01\u4e0d\u662f\u6211\u6709\u8bdd\u95ee\u4f60\uff0c\u6211\u8fd8\u771f\u4e0d\u5c51\u4e8e\u4e0e\u4f60\u8d4c\u8d5b\uff01\u201d \u3000\u3000\u5927\u6f20\u795e\u72fc\u5e7c\u9047\u5f02\u4eba\uff0c\u5728\u5185\u529f\u3001\u638c\u6cd5\u548c\u5175\u5203\u4e0a\u90fd\u6709\u7cbe\u6df1\u7684\u9020\u8be3\u3002\u5728\u6f20\u5916\u6a2a\u884c\u4e8c\u5341\u4f59\u5e74\u672a\u9047 \u654c\u624b\uff0c\u542c\u4e8e\u627f\u73e0\u8ba5\u7b11\u4ed6\u201c\u7a7a\u6709\u4e00\u8eab\u86ee\u529b\u201d\uff0c\u6c14\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8fd9\u5c0f\u59d1\u5a18\u4e0d\u77e5\u5929\u9ad8 \u5730\u539a\uff0c\u5f85\u6211\u62a2\u4e86\u4f60\u7684\u5b9d\u5251\u518d\u4e0e\u8fd9\u548c\u5c1a\u6bd4\u5212\u3002\u201d\u8fd9\u4f38\u6c14\u597d\u4f3c\u4e8e\u627f\u73e0\u4e0d\u582a\u4e00\u51fb\uff0c\u6f6e\u97f3\u548c\u5c1a\u53eb\u9053\uff1a \u201c\u5582\uff0c\u627f\u73e0\uff0c\u4f60\u4e0d\u8981\u91cd\u4f24\u4e86\u4ed6\uff0c\u5f85\u4f1a\u513f\u7559\u4e0e\u6211\u6d88\u9063\u6d88\u9063\uff01\u201d\u9488\u950b\u76f8\u5bf9\uff0c\u66f4\u662f\u4e0d\u628a\u5927\u6f20\u795e\u72fc\u653e \u5728\u773c\u5185\uff01 \u3000\u3000\u5927\u6f20\u795e\u72fc\u4e00\u58f0\u602a\u53eb\uff0c\u53cc\u81c2\u7b95\u5f20\uff0c\u5411\u4e8e\u627f\u73e0\u4fbf\u662f\u4e00\u6251\uff0c\u4e0e\u4ed6\u540c\u6765\u7684\u90a3\u4e2a\u891a\u7384\u53eb\u9053\uff1a\u201c\u4f60\u7684 \u72fc\u7259\u68d2\u5728\u8fd9\u513f\uff01\u201d\u793a\u610f\u53eb\u4ed6\u4e0d\u53ef\u7a7a\u624b\uff0c\u8bdd\u58f0\u672a\u4e86\uff0c\u53ea\u89c1\u91d1\u5149\u4e00\u95ea\uff0c\u4e8e\u627f\u73e0\u53cd\u624b\u4e00\u6735\u91d1\u82b1\uff0c\u6253 \u4e2d\u4e86\u891a\u7384\u7684\u817f\u5f2f\u7a74\u9053\uff0c\u891a\u7384\u201c\u5495\u549a\u201d\u4e00\u58f0\uff0c\u8dcc\u5012\u5730\u4e0a\uff0c\u722c\u4e0d\u8d77\u6765\uff0c\u4f46\u90a3\u72fc\u7259\u68d2\u5df2\u662f\u8131\u624b\u98de \u51fa\uff0c\u4e8e\u627f\u73e0\u62a2\u5148\u4e00\u6b65\uff0c\u628a\u72fc\u7259\u68d2\u63a5\u5230\u624b\u4e2d\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u9976\u4f60\u4e00\u547d\uff0c\u7559\u4f60\u5728\u8fd9\u513f\u505a\u4e2a\u8bc1\u4eba\u3002\u5927 \u6f20\u795e\u72fc\uff0c\u6211\u5c82\u80fd\u6b3a\u4f60\u7a7a\u624b\uff0c\u8fd9\u72fc\u7259\u68d2\u4f60\u62ff\u53bb\u5427\uff01\u201d \u3000\u3000\u5927\u6f20\u795e\u72fc\u90a3\u4e00\u6251\u5feb\u903e\u98d8\u98ce\uff0c\u7ed9\u4e8e\u627f\u73e0\u8f7b\u8f7b\u95ea\u5f00\uff0c\u5df2\u662f\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u65f6\u53c8\u89c1\u5979\u62a2\u5148\u63a5\u4e86\u72fc \u7259\u68d2\uff0c\u672a\u66fe\u52a8\u624b\uff0c\u5728\u8f7b\u529f\u4e0a\u5934\u5df2\u662f\u628a\u81ea\u5df1\u538b\u4e0b\u53bb\u4e86\uff0c\u4e0d\u7981\u9762\u7ea2\u8033\u8d64\uff01 \u3000\u3000\u6b32\u5f85\u4e0d\u63a5\uff0c\u4f46\u89c1\u9762\u524d\u4eba\u5f71\u4e00\u6643\uff0c\u4e8e\u627f\u73e0\u5012\u6301\u68d2\u67c4\uff0c\u5df2\u622e\u5230\u4e86\u81ea\u5df1\u7684\u80f8\u524d\uff0c\u6b63\u5bf9\u7740\u547d\u8109\u8981 \u7a74\uff0c\u5927\u6f20\u795e\u72fc\u6015\u5979\u9aa4\u4e0b\u6bd2\u624b\uff0c\u6a2a\u638c\u4e00\u5c01\uff0c\u5de6\u624b\u4e00\u52fe\uff0c\u4e8e\u627f\u73e0\u683c\u683c\u4e00\u7b11\uff0c\u638c\u5fc3\u4e00\u653e\uff0c\u90a3\u72fc\u7259\u68d2 \u5230\u4e86\u5927\u6f20\u795e\u72fc\u624b\u4e0a\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u53eb\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u624b\u4e2d\u90fd\u6709\u4e86\u5175\u5668\uff0c\u8c01\u4e5f\u6ca1\u6709\u591a\u5360\u4fbf\u5b9c\uff0c\u4f60\u7559\u795e\u63a5\u62db\u5427\uff01\u201d\u9752\u51a5 \u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u5530\u5530\u4e24\u5251\uff0c\u5de6\u523a\u201c\u7ae0\u95e8\u7a74\u201d\uff0c\u53f3\u523a\u201c\u73af\u8df3\u7a74\u201d\uff0c\u5251\u5149\u74e2\u95ea\uff0c\u4e24\u5251\u8fde\u73af\uff0c\u51e0\u4e4e\u662f \u5de6\u53f3\u4e24\u65b9\uff0c\u540c\u65f6\u5e76\u523a\uff01\u5927\u6f20\u795e\u72fc\u53eb\u9053\uff1a\u201c\u597d\uff0c\u602a\u4e0d\u5f97\u4f60\u6562\u5938\u5927\u53e3\uff0c\u4f60\u7684\u5251\u6cd5\u5728\u90a3\u5c0f\u5b50\u4e4b \u4e0a\uff01\u201d\u72fc\u7259\u68d2\u4e00\u5c01\u4e00\u78d5\uff0c\u547c\u547c\u5e26\u98ce\uff0c\u4ed6\u7684\u72fc\u7259\u68d2\u575a\u903e\u7cbe\u94a2\uff0c\u4e00\u767e\u96f6\u516b\u624b\u68d2\u6cd5\u4e5f\u90fd\u662f\u9633\u521a\u624b \u6cd5\uff0c\u4e00\u68d2\u6253\u51fa\uff0c\u529b\u903e\u5343\u65a4\uff0c\u7eb5\u9047\u5b9d\u5251\uff0c\u4ea6\u65e0\u6240\u60e7\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5374\u5e76\u4e0d\u4e0e\u4ed6\u786c\u63a5\uff0c\u4f7f\u51fa\u7a7f\u82b1\u7ed5\u6811\u7684\u8eab\u6cd5\uff0c\u53cd\u624b\u4e00\u7ed5\uff0c\u6709\u5982\u873b\u8712\u70b9\u6c34\uff0c\u500f\u5730\u5df2\u7ffb\u51fa \u72fc\u7259\u68d2\u5a01\u529b\u6240\u53ca\u7684\u5708\u5b50\uff0c\u5927\u6f20\u795e\u72fc\u559d\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u6562\u63a5\u62db\uff1f\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u98d2\u98d2\u8fde\u58f0\uff0c \u4e8e\u627f\u73e0\u5530\u5730\u4e00\u5251\uff0c\u53c8\u5230\u4e86\u5927\u6f20\u795e\u72e0\u80cc\u540e\uff0c\u5251\u5c16\u582a\u582a\u523a\u5230\uff01\u5927\u6f20\u795e\u72fc\u4ea6\u975e\u5f31\u8005\uff0c\u731b\u5730\u201c\u602a\u87d2\u7ffb \u8eab\u201d\uff0c\u98ce\u9a70\u7535\u63a3\u822c\u76f4\u8f6c\u8fc7\u6765\uff0c\u4e00\u62db\u201c\u91d1\u9e4f\u5c55\u7fc5\u201d\u7528\u8db3\u529b\u91cf\uff0c\u63d0\u8d77\u72fc\u7259\u68d2\u4fbf\u5f80\u4e8e\u627f\u73e0\u7684\u5251\u8eab\u786c \u7838\uff0c\u5c82\u77e5\u53c8\u662f\u4e00\u68d2\u843d\u7a7a\uff0c\u53ea\u89c1\u9752\u5149\u4e00\u7ed5\uff0c\u4e8e\u627f\u73e0\u500f\u8fdb\u500f\u9000\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u53c8\u4ece\u4ed6\u7684\u5de6\u4fa7\u653b\u4e0a\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u8fd9\u201c\u7a7f\u82b1\u7ed5\u6811\u201d\u8eab\u6cd5\u4e43\u662f\u6b66\u6797\u4ec5\u89c1\u7684\u4e00\u79cd\u4e0a\u4e58\u8f7b\u529f\uff0c\u5728\u8336\u4ead\u4e2d\u640f\u6597\uff0c\u5c24\u5176\u5360\u4e86\u4fbf \u5b9c\uff0c\u7aef\u7684\u662f\u77bb\u4e4b\u5728\u524d\uff0c\u5ffd\u7109\u5728\u540e\uff0c\u77bb\u4e4b\u5728\u5de6\uff0c\u5ffd\u7109\u5728\u53f3\u3002\u9976\u662f\u5927\u6f20\u795e\u72fc\u906e\u62e6\u5f97\u5f53\uff0c\u4e5f\u63a5\u8fde\u9047 \u4e86\u597d\u51e0\u6b21\u9669\u62db\uff01 \u3000\u3000\u53ea\u542c\u5f97\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u5927\u6f20\u795e\u72fc\u4e00\u68d2\u6253\u53bb\uff0c\u6253\u4e0d\u4e2d\u4e8e\u627f\u73e0\uff0c\u5374\u53c8\u6253\u788e\u4e86\u4e00\u5f20\u684c\u5b50\uff0c\u90a3\u8001 \u5a46\u5a46\u5fc3\u75db\u4e4b\u6781\uff0c\u4e71\u53eb\u4e71\u9a82\u3002\u5927\u6f20\u795e\u72fc\u98de\u8eab\u4e00\u8dc3\uff0c\u8df3\u8fc7\u680f\u6746\uff0c\u53cd\u5b50\u4e00\u62db\uff0c\u53eb\u9053\uff1a\u201c\u5f80\u5916\u9762\u6253 \u53bb\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u597d\uff0c\u603b\u4e4b\u53eb\u4f60\u8f93\u5f97\u5fc3\u670d\uff01\u201d\u98de\u8eab\u4e00\u63a0\uff0c\u5982\u5f71\u968f\u5f62\uff0c\u5251\u5c16\u53c8\u70b9\u5230\u4e86\u5927\u6f20\u795e \u72fc\u7684\u80cc\u5fc3\u3002 \u3000\u3000\u5927\u6f20\u795e\u72fc\u8fd9\u65f6\u5b66\u5f97\u4e56\u4e86\uff0c\u8eab\u5f62\u4e00\u8f6c\uff0c\u5927\u68d2\u62a1\u5706\uff0c\u4e0a\u4e00\u4e2a\u201c\u96ea\u82b1\u76d6\u9876\u201d\uff0c\u4e0b\u4e00\u4e2a\u201c\u67af\u6811\u76d8 \u6839\u201d\uff0c\u5c06\u5168\u8eab\u906e\u5f97\u4e2a\u98ce\u96e8\u4e0d\u900f\uff0c\u4f46\u4e8e\u627f\u73e0\u6e9c\u6ed1\u4e4b\u6781\uff0c\u4ecd\u662f\u4e00\u5473\u548c\u4ed6\u6e38\u6597\uff0c\u89c1\u9699\u5373\u653b\uff0c\u8fd9\u4e00 \u6765\uff0c\u5927\u6f20\u795e\u72fc\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\uff0c\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u6e10\u6e10\u7ed9\u4e8e\u627f\u73e0\u7d2f\u5f97\u6709\u4e9b\u6c14\u5598\uff01 \u3000\u3000\u94c1\u955c\u5fc3\u51ed\u680f\u89c2\u6218\uff0c\u89c1\u4e8e\u627f\u73e0\u5251\u6cd5\u7cbe\u5999\u5982\u65af\uff0c\u6bd4\u8d77\u521d\u89c1\u4e4b\u65f6\uff0c\u5df2\u4e0d\u77e5\u9ad8\u660e\u591a\u5c11\uff01\u4ed6\u8d77\u521d\u7ed9 \u5927\u6f20\u795e\u72fc\u8bf4\u4ed6\u4e0d\u53ca\u4e8e\u627f\u73e0\uff0c\u5fc3\u4e2d\u672c\u6765\u4e0d\u670d\uff0c\u8fd9\u65f6\u4e0d\u7531\u5f97\u81ea\u6127\u4e0d\u5982\uff01 \u3000\u3000\u4e8e\u627f\u73e0\u4e00\u662f\u5fc3\u4e2d\u6697\u6697\u7591\u60d1\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5927\u6f20\u795e\u72fc\u7684\u6b66\u529f\u867d\u7136\u4e0d\u5f31\uff0c\u770b\u6765\u5374\u5c1a\u975e\u4e91\u51e4\u59d0\u59d0 \u7684\u5bf9\u624b\uff0c\u51cc\u4e91\u51e4\u7684\u5251\u6cd5\u662f\u970d\u5929\u90fd\u4f20\u6388\u7684\uff0c\u8fd9\u5927\u6f20\u795e\u72fc\u5c82\u80fd\u4f24\u5f97\u4e86\u970d\u5929\u90fd\uff1f\u800c\u4e14\u8fd9\u4eba\u867d\u7136\u86ee\u4e0d \u8bb2\u7406\uff0c\u4e5f\u8fd8\u4e0d\u4f3c\u4e2a\u7a77\u51f6\u6781\u6076\u4e4b\u4eba\u3002\u201d\u5ffd\u542c\u5f97\u6f6e\u97f3\u548c\u5c1a\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u522b\u628a\u4ed6\u7d2f\u6b7b\u4e86\uff0c\u6211\u8fd8\u8981 \u4e0e\u4ed6\u6d88\u9063\u6d88\u9063\u5462\uff01\u201d\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6211\u5728\u4e09\u62db\u4e4b\u5185\uff0c\u5c06\u4ed6\u6253\u5f97\u8dea\u5730\u6c42\u9976\uff0c\u4e5f\u4fbf\u662f \u4e86\uff01\u201d \u3000\u3000\u5927\u6f20\u795e\u72fc\u6c14\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u72fc\u7259\u68d2\u4e00\u62db\u201c\u96f7\u7535\u4ea4\u950b\u201d\uff0c\u767b\u65f6\u597d\u50cf\u6709\u6570\u5341\u6761\u6746\u68d2\u540c\u65f6\u821e\u8d77\uff0c \u5728\u5468\u56f4\u5e03\u8d77\u4e86\u4e00\u9053\u94c1\u58c1\u94dc\u5899\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u597d\uff0c\u770b\u4f60\u5982\u4f55\u5728\u4e09\u62db\u4e4b\u5185\u5c06\u6211\u6253\u5012\uff0c\u9664\u975e\u6211\u662f\u6b7b \u4eba\uff01\u201d\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u4f11\u8981\u607c\u6012\uff0c\u4ed4\u7ec6\u63a5\u62db\uff01\u201d\u8eab\u5f62\u4e00\u6643\uff0c\u9752\u51a5\u5b9d\u5251\u4fe1\u624b\u4e00\u6325\uff0c\u5149\u8292\u66b4\u957f\uff0c \u7adf\u4ece\u5343\u5c42\u68d2\u5f71\u4e2d\u76f4\u7a7f\u800c\u5165\uff0c\u5927\u6f20\u795e\u72fc\u5fc3\u9053\uff1a\u201c\u4f60\u8981\u548c\u6211\u786c\u78b0\uff0c\u90a3\u662f\u627e\u6b7b\u3002\u201d\u8fd0\u8db3\u5185\u529b\uff0c\u5927\u6367 \u4e00\u8361\uff0c\u9661\u7136\u95f4\u5ffd\u89c1\u9762\u524d\u91d1\u5149\u75be\u95ea\uff0c\u5927\u6f20\u795e\u72fc\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u5973\u5a03\u5a03\u82b1\u6837\u771f\u591a\uff01\u201d\u72fc\u7259\u68d2\u5de6\u8d77\u53f3 \u843d\uff0c\u6325\u4e86\u4e00\u4e2a\u5706\u5f27\uff0c\u5c06\u4e8e\u627f\u73e0\u6240\u53d1\u7684\u4e09\u6735\u91d1\u82b1\u5168\u90fd\u9707\u98de\u3002\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u53d1\u6697\u5668\uff0c\u6211\u4ea6\u4e0d \u60e7\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4e8e\u627f\u73e0\u53c8\u662f\u5530\u7684\u4e00\u5251\u523a\u5230\uff0c\u5de6\u624b\u4e00\u626c\uff0c\u4e94\u6735\u91d1\u82b1\u968f\u7740\u5251\u5149\u9f50\u81f3\uff0c\u5927 \u6f20\u795e\u72fc\u821e\u68d2\u9632\u8eab\uff0c\u53ea\u542c\u5f97\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u6735\u91d1\u82b1\u5df2\u4ece\u4ed6\u7684\u5934\u9876\u63a0\u8fc7\uff0c\u524a\u53bb\u4e86\u4e00\u7247\u5934\u76ae\uff0c\u5927 \u6f20\u795e\u72fc\u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u540c\u65f6\u62b5\u6321\u5b9d\u5251\u91d1\u82b1\uff0c\u4e0d\u514d\u987e\u6b64\u5931\u5f7c\u3002\u5927\u6f20\u795e\u72fc\u5413\u4e86\u4e00\u8df3\uff0c\u4f46\u5fc3\u4e2d\u4ecd\u7136\u60f3 \u9053\uff1a\u201c\u53ea\u5269\u4e00\u62db\uff0c\u6211\u7528\u5168\u529b\u62b5\u6321\u5979\u7684\u6697\u5668\uff0c\u95ea\u5f00\u5979\u7684\u5251\u62db\u4e5f\u4fbf\u662f\u4e86\u3002\u201d\u5fc3\u5ff5\u65b9\u52a8\uff0c\u4e8e\u627f\u73e0\u5a07 \u53f1\u4e00\u58f0\uff0c\u7528\u201c\u5929\u5973\u6563\u82b1\u201d\u624b\u6cd5\uff0c\u4e00\u5927\u628a\u91d1\u82b1\u64a4\u4e86\u51fa\u53bb\uff0c\u5927\u6f20\u795e\u72fc\u4ecd\u7528\u524d\u6cd5\uff0c\u821e\u68d2\u9632\u8eab\uff0c\u53ea\u542c \u5f97\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u8fd9\u4e00\u628a\u91d1\u82b1\u5168\u90fd\u7ed9\u4ed6\u9707\u5f97\u56db\u5904\u98de\u505a\uff0c\u5927\u6f20\u795e\u72fc\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e09\u62db \u6ee1\u4e86\uff0c\u5982\u4f55\uff1f\u201d\u7b11\u58f0\u672a\u6b47\uff0c\u90a3\u7ed9\u4ed6\u9707\u5f97\u56db\u5904\u98de\u6563\u7684\u5341\u51e0\u6735\u91d1\u82b1\u5ffd\u7136\u6389\u5934\u98de\u56de\uff0c\u5927\u6f20\u795e\u72fc\u9632\u4e0d \u53ca\u9632\uff0c\u518d\u821e\u68d2\u6765\u906e\u62e6\u65f6\uff0c\u5185\u52b2\u5df2\u662f\u6bd4\u524d\u51cf\u5f31\uff0c\u88ab\u4e00\u6735\u91d1\u82b1\u6b63\u6b63\u6253\u4e2d\u4e86\u817f\u5f2f\u7684\u201c\u73af\u8df3\u7a74\u201d\uff0c\u767b \u65f6\u53cc\u817f\u9178\u9ebb\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u201c\u535c\u901a\u201d\u8dea\u4e0b\u3002\u539f\u6765\u4e8e\u627f\u73e0\u7684\u91d1\u82b1\u6697\u5668\u6709\u5404\u79cd\u4e0d\u540c\u7684\u624b\u6cd5\uff0c\u8fd9\u4e00\u6b21 \u5979\u6697\u4e2d\u8fd0\u7528\u4e86\u56de\u529b\uff0c\u5927\u6f20\u795e\u72fc\u5374\u8fd8\u662f\u7167\u65e7\u6cd5\u9632\u5fa1\uff0c\u8fd9\u4fbf\u7740\u4e86\u9053\u513f\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u5982\u4f55\uff1f\u6211\u8bf4\u4e09\u62db\uff0c\u5b9e\u9645\u53ea\u662f\u7528\u4e86\u4e24\u62db\u534a\u5462\uff01\u201d\u5927\u6f20\u795e\u72fc\u81ea\u5df1\u89e3\u4e86\u7a74\u9053\uff0c \u4e00\u8dc3\u800c\u8d77\uff0c\u5fc3\u4e2d\u5c1a\u662f\u672a\u670d\uff0c\u4f46\u5374\u65e0\u53ef\u5948\u4f55\uff0c\u4e8e\u627f\u73e0\u51b7\u7b11\u9053\uff1a\u201c\u770b\u4f60\u7684\u795e\u6c14\uff0c\u4f3c\u4e4e\u4e0d\u662f\u786c\u78b0\u786c \u5730\u8d62\u4e86\u4f60\uff0c\u4f60\u8fd8\u662f\u4e0d\u80af\u5fc3\u670d\u53e3\u670d\uff01\u4f60\u81ea\u6043\u529b\u5927\uff0c\u6562\u548c\u8fd9\u4f4d\u5927\u5e08\u518d\u8d4c\u8d5b\u4e00\u4e0b\u5417\uff1f\u201d\u5927\u6f20\u795e\u72fc\u53eb \u9053\uff1a\u201c\u6b63\u8981\u9886\u6559\uff0c\u6211\u82e5\u518d\u8f93\uff0c\u4ece\u6b64\u56de\u8f6c\u6f20\u5317\uff0c\u6c38\u4e0d\u518d\u5230\u4e2d\u539f\u3002\u201d \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u4f60\u6253\u7d2f\u4e86\uff0c\u6b47\u4e00\u6b47\u5427\u3002\u518d\u8bf4\u4f60\u6bc1\u574f\u4e86\u8fd9\u4f4d\u8001\u5a46\u5a46\u7684\u4e1c\u897f\uff0c\u4e5f\u8be5\u5148\u7ed3\u4e00\u7ed3 \u5e10\uff0c\u5c0f\u672c\u751f\u610f\uff0c\u5979\u53ef\u8d54\u4e0d\u8d77\u5440\u3002\u201d\u5927\u6f20\u795e\u72fc\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u79c3\u9a74\u6562\u5c0f\u89d1\u6211\uff01\u201d\u6478\u51fa\u4e00\u952d\u5927\u94f6\uff0c \u556a\u5730\u4e00\u63b7\uff0c\u90a3\u952d\u94f6\u5b50\u9677\u5165\u684c\u4e2d\uff0c\u5927\u6f20\u795e\u72fc\u9053\uff1a\u201c\u8fd9\u603b\u591f\u8d54\u4e86\u5427\uff0c\u597d\uff0c\u54b1\u4eec\u73b0\u5728\u5c31\u8d5b\u4e00\u4e0b\u529b \u6c14\u3002\u201d\u6f6e\u97f3\u548c\u5c1a\u8f7b\u8f7b\u4e00\u62cd\uff0c\u90a3\u952d\u94f6\u5b50\u4ece\u684c\u4e2d\u95f4\u8df3\u4e86\u51fa\u6765\u3002\u6f6e\u97f3\u548c\u5c1a\u6162\u6761\u65af\u7406\u5730\u8bf4\u9053\uff1a\u201c\u73b0\u5728 \u5c31\u6bd4\uff1f\u597d\uff0c\u4f46\u6211\u4e5f\u4e0d\u597d\u5360\u4f60\u7684\u4fbf\u5b9c\uff0c\u8fd9\u6837\u2014\u2014\u201d\u968f\u624b\u628a\u7985\u6756\u5f80\u5730\u4e0b\u4e00\u63d2\uff0c\u5355\u624b\u6276\u7740\u6756\u5934\uff0c\u7eed \u9053\uff1a\u201c\u4f60\u53cc\u624b\u6765\u6273\uff0c\u6273\u5f97\u52a8\u534a\u5206\u534a\u6beb\uff0c\u5c31\u7b97\u4f60\u8d62\uff01\u201d\u5927\u6f20\u795e\u72fc\u6012\u6781\uff0c\u9053\uff1a\u201c\u6211\u4f55\u987b\u53cc\u624b\uff1f\u201d \u201c\u547c\u201d\u7684\u4e00\u638c\u626b\u53bb\uff0c\u90a3\u7985\u6756\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u53cd\u800c\u6709\u4e00\u80a1\u5927\u529b\u53cd\u9707\u56de\u6765\uff0c\u5927\u6f20\u795e\u72fc\u7684\u94c1\u638c\u4e5f\u51e0\u4e4e\u7ed9 \u9707\u5f97\u62d7\u6298\uff01 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u8fd8\u662f\u53cc\u624b\u9f50\u6765\u7684\u597d\uff01\u201d\u5927\u6f20\u795e\u72fc\u9762\u7ea2\u8033\u8d64\uff0c\u7ad9\u4e86\u4e2a\u6869\uff0c\u8fd0\u8db3\u5185\u529b\uff0c\u53cc \u624b\u6765\u6273\uff0c\u6709\u5982\u873b\u8712\u64bc\u67f1\uff0c\u54ea\u91cc\u6273\u5f97\u5b83\u52a8\u3002\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u4f60\u518d\u7528\u529b\uff0c\u5c31\u8981\u53d7\u5185\u4f24\u4e86\uff0c\u770b\u4f60\u4e5f \u662f\u4e00\u6761\u597d\u6c49\uff0c\u8ba9\u4f60\u53bb\u5427\uff01\u201d\u7985\u6756\u8f7b\u8f7b\u4e00\u98a4\uff0c\u5927\u6f20\u795e\u72fc\u4e00\u8de4\u8dcc\u5012\uff0c\u8001\u7f9e\u6210\u6012\uff0c\u62fe\u8d77\u4e86\u72fc\u7259\u68d2\u559d \u9053\uff1a\u201c\u603b\u5f97\u89c1\u8fc7\u771f\u7ae0\uff01\u201d\u6f6e\u97f3\u548c\u5c1a\u6447\u5934\u7b11\u9053\uff1a\u201c\u597d\u52c7\u6597\u72e0\uff0c\u771f\u662f\u65e0\u53ef\u6551\u836f\uff0c\u9976\u4e86\u4f60\u4f60\u8fd8\u672a \u77e5\u3002\u201d\u968f\u624b\u4e00\u6293\uff0c\u5c06\u5927\u6f20\u795e\u72fc\u7684\u72fc\u7259\u68d2\u5288\u624b\u593a\u8fc7\uff0c\u5927\u6f20\u795e\u72fc\u4e00\u8eab\u6b66\u529f\uff0c\u7adf\u7136\u8eb2\u95ea\u4e0d\u5f00\u3002\u4f46\u89c1 \u6f6e\u97f3\u548c\u5c1a\u5c06\u90a3\u6839\u72fc\u7259\u68d2\u6401\u5728\u819d\u4e0a\uff0c\u7528\u529b\u4e00\u62d7\uff0c\u90a3\u6839\u7cbe\u94c1\u5927\u6367\u767b\u65f6\u5f2f\u66f2\u5982\u73af\uff0c\u6f6e\u97f3\u548c\u5c1a\u54c8\u54c8\u4e00 \u7b11\uff0c\u968f\u624b\u4e00\u63b7\uff0c\u62d7\u66f2\u7684\u94c1\u73af\u6ca1\u5165\u5730\u4e2d\uff0c\u8e2a\u8ff9\u4e0d\u89c1\u3002 \u3000\u3000\u5927\u6f20\u795e\u72fc\u6c14\u6cae\u795e\u4f24\uff0c\u8fd9\u624d\u77e5\u9053\u5929\u5916\u6709\u5929\uff0c\u4eba\u5916\u6709\u4eba\uff0c\u81ea\u5df1\u81ea\u8d1f\u4e00\u8eab\u795e\u529b\uff0c\u6bd4\u8d77\u8fd9\u548c\u5c1a \u6765\uff0c\u5374\u6709\u5982\u8424\u706b\u4e4b\u6bd4\u6708\u4eae\uff0c\u4e0d\u7531\u5f97\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6709\u4ec0\u4e48\u8bdd\uff1f\u95ee\u5427\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u970d\u5929\u90fd\uff0c\u53ef\u662f\u4f60\u628a\u4ed6\u5bb3\u4e86\uff1f\u201d\u5927\u6f20\u795e\u72fc\u9053\uff1a\u201c\u4ec0\u4e48\u970d\u5929\u90fd\uff1f\u4ffa\u4e0d\u8ba4 \u5f97\uff01\u201d\u4e8e\u627f\u73e0\u5927\u559c\uff0c\u9053\uff1a\u201c\u4f60\u771f\u4e0d\u8ba4\u5f97\uff1f\u201d\u5fc3\u4e2d\u5c1a\u6709\u6000\u7591\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u90dd\u4e91\u53f0\u53ef\u662f\u4f60\u7684\u670b \u53cb\uff1f\u201d\u5927\u6f20\u795e\u72fc\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u9519\uff0c\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u662f\u4f60\u8981\u4ed6\u4eec\u53bb\u627e\u51cc\u4e91\u51e4\u7684\u4e48\uff1f\u201d\u5927\u6f20\u795e\u72fc \u9053\uff1a\u201c\u662f\u4ed6\u4eec\u81ea\u5df1\u53bb\u6211\u7684\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4ed6\u4eec\u4e3a\u4f55\u8981\u53bb\u627e\u51cc\u4e91\u51e4\uff1f\u201d\u5927\u6f20\u795e\u72fc\u9053 \u9053\uff1a\u201c\u90dd\u4e91\u53f0\u548c\u6211\u505a\u6869\u4e70\u5356\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ec0\u4e48\u4e70\u5356\uff1f\u201d\u5927\u6f20\u795e\u72fc\u9053\uff1a\u201c\u6211\u5f97\u4e86\u4e00\u672c\u5251 \u8c31\uff0c\u751a\u662f\u5965\u5999\uff0c\u6211\u770b\u4e0d\u61c2\uff0c\u4e0e\u90dd\u4e91\u53f0\u4ed6\u4eec\u53c2\u8be6\uff0c\u4ed6\u8bf4\u8fd9\u662f\u5404\u79cd\u5251\u8c31\u7684\u7cbe\u534e\uff0c\u82e5\u5c06\u90a3\u5341\u51e0\u90e8\u5251 \u8c31\u90fd\u627e\u9f50\u4e86\uff0c\u518d\u7cbe\u7814\u8fd9\u90e8\u5251\u8c31\uff0c\u4e0d\u96be\u521b\u51fa\u5929\u4e0b\u72ec\u6b65\u7684\u5251\u6cd5\uff01\u6211\u8bf4\uff0c\u54ea\u80fd\u53bb\u627e\u9f50\u8fd9\u8bb8\u591a\u5251\u8c31\uff1f \u90dd\u4e91\u53f0\u8ba4\u5f97\u6c49\u5b57\uff0c\u4ed6\u8bf4\u5251\u8c31\u540e\u9762\u6240\u8bb0\uff0c\u90a3\u5341\u51e0\u90e8\u5251\u8c31\u90fd\u5728\u4e00\u4e2a\u540d\u5524\u51cc\u4e91\u51e4\u7684\u5973\u5b50\u624b\u4e2d\uff0c\u8fd9\u5973 \u5b50\u4ed6\u6070\u597d\u8ba4\u5f97\u3002\u56e0\u6b64\u4ed6\u4fbf\u8981\u548c\u6211\u505a\u8fd9\u6869\u4e70\u5356\uff0c\u7531\u4ed6\u53bb\u627e\u51cc\u4e91\u51e4\u627e\u9f50\u90a3\u4e9b\u5251\u8c31\uff0c\u518d\u6765\u4e0e\u6211\u540c \u53c2\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5927\u559c\u4e4b\u540e\u63a5\u7740\u5927\u5fe7\uff0c\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u90a3\u672c\u5251\u8c31\u4f60\u53c8\u662f\u600e\u4e48\u5f97\u6765\u7684\uff1f\u201d\u5927\u6f20\u795e\u72fc\u9053\uff1a \u201c\u6709\u4e00\u65e5\u6211\u5728\u5927\u6f20\u4e4b\u4e2d\uff0c\u53d1\u73b0\u4e00\u4e2a\u5c11\u5e74\u88ab\u57cb\u5728\u6c99\u5806\u4e4b\u4e0b\uff0c\u662f\u6211\u6551\u4ed6\u51fa\u6765\uff0c\u53ef\u60dc\u4ed6\u88ab\u57cb\u4e86\u591a \u65f6\uff0c\u6551\u51fa\u6765\u65f6\u5df2\u662f\u6df9\u6df9\u4e00\u606f\uff0c\u4ed6\u81ea\u77e5\u96be\u6d3b\uff0c\u4e34\u6b7b\u4e4b\u65f6\uff0c\u5c06\u8fd9\u5251\u8c31\u4ea4\u7ed9\u6211\uff0c\u53eb\u6211\u9001\u5230\u516b\u8fbe\u5cad\u627e \u4e00\u4f4d\u627e\u4e00\u4f4d\uff0c\u8bdd\u672a\u8bf4\u5b8c\u5c31\u54bd\u6c14\u4e86\u3002\u6211\u4e0d\u77e5\u9053\u4ed6\u8981\u6211\u7684\u662f\u8c01\uff0c\u53ea\u597d\u5c06\u8fd9\u90e8\u5251\u8c31\u85cf\u8d77\u3002\u6211\u60f3\u62a2\u4f60 \u4eec\u7684\u5b9d\u5251\uff0c\u5c31\u662f\u56e0\u4e3a\u6211\u65e2\u6709\u4e86\u8fd9\u672c\u5251\u8c31\uff0c\u53ef\u80fd\u771f\u7684\u80fd\u7ec3\u6210\u5929\u4e0b\u72ec\u6b65\u7684\u5251\u6cd5\uff0c\u6545\u6b64\u5fc5\u987b\u6709\u628a\u5b9d \u5251\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u5934\u98a4\u7c9f\uff0c\u5982\u5760\u51b0\u7a9f\uff0c\u6025\u9053\uff1a\u201c\u90a3\u672c\u5251\u8c31\u5462\uff1f\u201d\u5927\u6f20\u795e\u72fc\u8fdf\u7591\u534a\u664c\uff0c\u6478\u51fa\u4e86\u4e00\u672c \u4e66\u6765\uff0c\u9053\uff1a\u201c\u6211\u65e2\u8f93\u7ed9\u4f60\u4eec\uff0c\u4f60\u4eec\u5c31\u662f\u8981\u4e86\u8fd9\u672c\u8c31\uff0c\u6211\u4e5f\u6ca1\u6cd5\u3002\u201d\u4e8e\u627f\u73e0\u4e0d\u6687\u4e0e\u4ed6\u591a\u8bf4\uff0c\u63a5 \u8fc7\u5251\u8c31\uff0c\u8fde\u5fd9\u7ffb\u9605\uff0c\u4f46\u89c1\u5251\u8c31\u7684\u5b57\u8ff9\u4e0e\u90dd\u4e91\u53f0\u90a3\u5c01\u5047\u4fe1\u7684\u5b57\u8ff9\u5b8c\u5168\u4e00\u6837\uff0c\u51cc\u4e91\u51e4\u66fe\u8bf4\u8fc7\u90a3\u5c01 \u5047\u4fe1\u5192\u970d\u5929\u90fd\u7684\u7b14\u8ff9\u5192\u5f97\u903c\u771f\uff0c\u90a3\u4e48\u8fd9\u5251\u8c31\u5b9a\u7136\u662f\u970d\u5929\u90fd\u624b\u5199\u7684\u4e86\uff01\u52a0\u4ee5\u4ed6\u6240\u8bf4\u7684\u60c5\u51b5\u4e5f\u4e0e \u51cc\u4e91\u51e4\u6240\u8bf4\u7684\u76f8\u5408\uff0c\u96be\u9053\uff0c\u96be\u9053\u970d\u5929\u90fd\u771f\u7684\u6b7b\u4e86\uff01 \u3000\u3000\u4e8e\u627f\u73e0\u6402\u7740\u5251\u8c31\uff0c\u6296\u4e2a\u4e0d\u4f11\uff0c\u4f46\u89c9\u4e00\u9635\u9635\u51c9\u6c14\u76f4\u900f\u5fc3\u5934\uff0c\u597d\u50cf\u7075\u9b42\u5c31\u8981\u8131\u79bb\u4e86\u8eaf\u58f3\uff0c\u832b \u832b\u7136\u65e0\u6240\u4f9d\u5f52\u3002\u94c1\u955c\u5fc3\u5927\u4e3a\u5403\u60ca\uff0c\u9053\uff1a\u201c\u627f\u73e0\uff0c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u4e8e\u627f\u73e0\u4f3c\u662f\u542c\u800c\u4e0d\u95fb\uff0c\u53ea\u662f\u5446 \u5446\u5730\u671b\u7684\u7740\u5927\u6f20\u795e\u72fc\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4ed6\uff0c\u4ed6\u771f\u5730\u6b7b\u4e86\uff1f\u201d\u4f3c\u662f\u95ee\u4ed6\uff0c\u53c8\u4f3c\u662f\u81ea\u8a00\u81ea\u8bed\u3002\u5927\u6f20 \u795e\u72fc\u6478\u4e0d\u7740\u5934\u8111\uff0c\u89c1\u5979\u5982\u6b64\u4f24\u75db\uff0c\u4ea6\u81ea\u5fc3\u9178\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4eba\u662f\u4f60\u7684\u4eb2\u4eba\u5417\uff1f\u54ce\uff0c\u4eba\u6b7b\u4e0d\u80fd\u590d \u751f\uff0c\u59d1\u5a18\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u592a\u4f24\u5fc3\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u5fcd\u7740\u773c\u6cea\uff0c\u6325\u624b\u8bf4\u9053\uff1a\u201c\u6211\u7684\u8bdd\u5df2\u95ee\u5b8c\uff0c\u4f60\u53ef\u4ee5\u8d70 \u4e86\u3002\u90a3\u4f4d\u5c11\u5e74\u8981\u4f60\u627e\u7684\u4eba\u6b63\u662f\u6211\u7684\u597d\u53cb\uff0c\u8fd9\u672c\u5251\u8c31\u5e94\u8be5\u5f52\u5979\uff0c\u6211\u66ff\u5979\u7559\u4e0b\u5566\u3002\u201d\u5927\u6f20\u795e\u72fc \u9053\uff1a\u201c\u597d\uff0c\u53cd\u6b63\u6211\u4e5f\u770b\u5b83\u4e0d\u61c2\uff0c\u4f60\u6709\u5b9d\u5251\uff0c\u5c31\u6210\u5168\u4e86\u4f60\u5427\u3002\u4e0d\u7ba1\u4f60\u662f\u9001\u4eba\u6216\u81ea\u5df1\u8981\uff0c\u90fd\u7531\u5f97 \u4f60\u3002\u201d\u672c\u6765\u4e8e\u627f\u73e0\u8981\u4ed6\u5251\u8c31\uff0c\u4ed6\u5fc3\u4e2d\u5b9e\u662f\u4e0d\u613f\uff0c\u4f46\u4ed6\u63a5\u8fde\u53d7\u4e86\u4e24\u6b21\u60e8\u8d25\uff0c\u96c4\u5fc3\u5df2\u632b\uff0c\u58ee\u5fd7\u5168 \u7070\uff0c\u4e5f\u5c31\u4e50\u5f97\u505a\u4e2a\u987a\u6c34\u4eba\u60c5\u4e86\u3002 \u3000\u3000\u891a\u7384\u7a74\u9053\u672a\u89e3\uff0c\u8eba\u5728\u5730\u4e0a\u53eb\u9053\uff1a\u201c\u54c8\u6728\u56fe\uff0c\u4f60\u4e0d\u662f\u8981\u5230\u5cad\u5357\u5417\uff1f\u5c0f\u5f1f\u966a\u4f60\u5230\u6b64\uff0c\u4f60\u600e\u4e00 \u4eba\u72ec\u8d70\uff1f\u201d\u54c8\u6728\u56fe\u662f\u5927\u6f20\u795e\u72fc\u7684\u540d\u5b57\uff0c\u539f\u6765\u8fd9\u891a\u7384\u6b66\u529f\u867d\u7136\u4e0d\u9ad8\uff0c\u4e00\u5f20\u5634\u5374\u751a\u662f\u4e86\u5f97\uff0c\u4ed6\u4e13 \u66ff\u9633\u5b97\u6d77\u6e38\u8bf4\u6c5f\u6e56\u4e0a\u7684\u5404\u8272\u4eba\u7269\uff0c\u524d\u4e24\u5e74\u66fe\u8bf4\u5230\u4e86\u4e00\u4e2a\u72af\u4e86\u6e05\u89c4\u7684\u5c11\u6797\u5bfa\u548c\u5c1a\u4e86\u7f18\uff0c\u4e0d\u6599\u4e86 \u7f18\u540e\u6765\u53c8\u53cd\u4e86\u51fa\u53bb\uff0c\u4e3a\u6b64\u7740\u5b9e\u53d7\u9633\u5b97\u6d77\u8d23\u5907\u4e86\u4e00\u987f\uff0c\u8fd9\u6b21\u4ed6\u6253\u63a2\u5f97\u5927\u6f20\u795e\u72fc\u4ece\u6f20\u5317\u6765\u5230\u4e2d \u539f\uff0c\u4fbf\u53bb\u4e0e\u4ed6\u7ed3\u7eb3\uff0c\u966a\u4ed6\u5230\u5357\u8fb9\u6765\u5bfb\u89c5\u90dd\u4e91\u53f0\uff0c\u60f3\u8fd9\u5927\u6f20\u795e\u72fc\u6bd4\u4e86\u7f18\u548c\u5c1a\u80dc\u8fc7\u8bb8\u591a\uff0c\u82e5\u80fd\u5c06 \u4ed6\u62db\u63fd\uff0c\u8350\u7ed9\u9633\u5b97\u6d77\u81ea\u53ef\u5c06\u529f\u8d4e\u7f6a\u3002 \u3000\u3000\u54ea\u77e5\u5927\u6478\u795e\u72fc\u5df2\u662f\u96c4\u5fc3\u5c3d\u4e27\uff0c\u58ee\u5fd7\u5168\u629b\uff0c\u542c\u4ed6\u547c\u5524\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u672c\u5251\u8c31 \u6211\u4e5f\u4e0d\u8981\u5566\uff0c\u8fd8\u8981\u5230\u5357\u8fb9\u505a\u751a\uff1f\u4f60\u82e5\u9047\u5230\u90dd\u4e91\u53f0\uff0c\u5c31\u544a\u8bc9\u4ed6\u8fd9\u5b97\u4ea4\u6613\u7b97\u4f5c\u7f62\u8bba\u4e86\u3002\u4ed6\u82e5\u5f97\u4e86 \u51cc\u4e91\u51e4\u7684\u5341\u4e09\u672c\u5251\u8c31\uff0c\u90a3\u5c31\u5f52\u4ed6\u72ec\u6709\u3002\u201d\u8fd9\u8bdd\u8bf4\u5b8c\uff0c\u8eab\u5f62\u5df2\u5230\u4e86\u4e00\u91cc\u5f00\u5916\u3002\u891a\u7384\u5927\u6025\uff0c\u53eb \u9053\uff1a\u201c\u5582\uff0c\u5582\uff0c\u5582\uff0c\u4f60\u8d70\u4e86\u6211\u600e\u4e48\u529e\uff1f\u201d\u4e8e\u627f\u73e0\u6b63\u81ea\u4e0d\u8010\u70e6\uff0c\u63a5\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4ece\u4eca\u4ee5\u540e\u597d\u597d \u505a\u4eba\uff0c\u522b\u66ff\u9633\u5b97\u6d77\u8dd1\u817f\uff0c\u6211\u4fbf\u9976\u4f60\u4e00\u547d\u3002\u201d\u891a\u7384\u8fde\u58f0\u53eb\u9053\uff1a\u201c\u4f46\u51ed\u5973\u4fa0\u5429\u5490\uff01\u201d\u4e8e\u627f\u73e0\u5530\u5730 \u4e00\u5251\uff0c\u6311\u65ad\u4e86\u4ed6\u7684\u7435\u7436\u9aa8\uff0c\u987a\u624b\u89e3\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u559d\u9053\uff1a\u201c\u6eda\u5427\uff01\u201d\u891a\u7384\u4fdd\u5168\u4e86\u6027\u547d\uff0c\u4f46\u5374\u88ab \u5e9f\u4e86\u6b66\u529f\uff0c\u4ece\u6b64\u4e0d\u6562\u518d\u5728\u6c5f\u6e56\u884c\u8d70\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5e72\u5f97\u75db\u5feb\uff0c\u53ef\u6d6e\u5927\u767d\uff01\u201d\u4f46\u89c1\u4e8e\u627f\u73e0\u6cea\u73e0\u6eda\u6eda\u800c\u4e0b\uff0c\u6709\u5982\u5e26\u96e8\u68a8 \u82b1\u3002\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u5230\u5e95\u662f\u8c01\u6b7b\u4e86\uff0c\u4f60\u8fd9\u6837\u4f24\u5fc3\u3002\u201d\u4e8e\u627f\u73e0\u54fd\u54bd\u8bf4\u9053\uff1a\u201c\u970d\u5929\u90fd\u771f\u4e2a\u6b7b \u4e86\uff01\u201d\u94c1\u955c\u5fc3\u5fc3\u4e2d\u4e00\u51c9\uff0c\u9053\uff1a\u201c\u8c01\u662f\u970d\u5929\u90fd\uff1f\u201d\u53ea\u9053\u8fd9\u970d\u5929\u90fd\u5b9a\u662f\u4e8e\u627f\u73e0\u5173\u7cfb\u5bc6\u5207\u7684\u4eba\uff0c\u4e8e \u627f\u73e0\u4ee5\u8896\u62ed\u6cea\uff0c\u6b47\u4e86\u4e00\u6b47\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u662f\u51cc\u59d0\u59d0\u7684\u9752\u6885\u7af9\u9a6c\u4e4b\u4ea4\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u5c31\u662f\u90a3\u4e2a \u4ec0\u4e48\u51cc\u5be8\u4e3b\u51cc\u4e91\u51e4\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u51cc\u59d0\u59d0\u4e00\u76f4\u7b49\u7740\u4ed6\uff0c\u4f60\u4e0d\u77e5\u9053\u3002\u201d\u94c1\u955c\u5fc3\u5fc3\u4e2d \u4e00\u5bbd\uff0c\u51e0\u4e4e\u8981\u7b11\u51fa\u6765\uff0c\u5f3a\u5fcd\u7740\u9053\uff1a\u201c\u90a3\u4e48\u5e94\u8be5\u51cc\u4e91\u98ce\u4e3a\u4ed6\u75db\u54ed\u624d\u5bf9\u3002\u5440\uff0c\u4ed6\u4e5f\u8bb8\u662f\u4e2a\u4eba\u7269\uff0c \u4f46\u5929\u4e0b\u4e4b\u5927\uff0c\u82f1\u624d\u65e9\u6298\u8005\u6240\u591a\uff0c\u4f60\u54ea\u80fd\u54ed\u5f97\u8fd9\u8bb8\u591a\uff1f\u4f60\u8ba4\u8bc6\u4ed6\u5417\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u4f24\u5fc3\u5df2\u6781\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u751f\u6c14\u8bf4\u9053\uff1a\u201c\u6211\u4e0e\u970d\u5929\u90fd\u4ece\u672a\u89c1\u8fc7\u4e00\u9762\uff0c\u4ed6\u662f\u9ad8\u662f\u77ee\uff0c\u662f \u80a5\u662f\u7626\u6211\u5168\u4e0d\u77e5\u9053\u3002\u4f46\u6211\u4f69\u670d\u4ed6\u60f3\u72ec\u521b\u4e00\u6d3e\u7684\u8654\u5fc3\u6bc5\u529b\uff0c\u66f4\u75db\u60dc\u4ed6\u4e0e\u51cc\u59d0\u59d0\u7684\u6b7b\u522b\u751f\u79bb\uff0c\u4f60 \u4e3a\u4ec0\u4e48\u4e0d\u8bb8\u6211\u54ed\uff1f\u201d\u94c1\u955c\u5fc3\u78b0\u4e86\u4e00\u4e2a\u9489\u5b50\uff0c\u8d54\u7740\u7b11\u8138\u8bf4\u9053\uff1a\u201c\u54ed\u5427\uff0c\u54ed\u5427\uff0c\u53ea\u8981\u4e0d\u54ed\u574f\u4e86\u8eab \u4f53\u4fbf\u597d\u3002\u201d\u60f3\u9053\uff1a\u201c\u4f60\u539f\u6765\u662f\u4e3a\u522b\u4eba\u7684\u60c5\u90ce\u800c\u54ed\u3002\u201d\u5fc3\u4e2d\u867d\u65e0\u987e\u5fcc\uff0c\u4ecd\u89c9\u9887\u4e3a\u5947\u602a\u3002 \u3000\u3000\u5979\u54ea\u91cc\u77e5\u9053\u4e8e\u627f\u73e0\u4e4b\u54ed\u970d\u5929\u90fd\uff0c\u6709\u4e00\u534a\u662f\u4e3a\u51cc\u4e91\u51e4\uff0c\u53e6\u4e00\u534a\u5374\u4e5f\u662f\u4e3a\u5979\u81ea\u5df1\u3002\u5979\u867d\u7136\u65e9 \u5df2\u6709\u5fc3\u5c06\u53f6\u6210\u6797\u201c\u8ba9\u201d\u7ed9\u51cc\u4e91\u51e4\uff0c\u5fc3\u4e2d\u4ecd\u5b58\u7740\u4e07\u4e00\u7684\u5e0c\u671b\uff0c\u5e0c\u671b\u970d\u5929\u90fd\u7684\u6b7b\u8baf\u4e0d\u786e\u3002\u7136\u800c\u73b0 \u5728\u8fd9\u4e00\u7ebf\u5e0c\u671b\u4e5f\u65ad\u7edd\u4e86\uff0c\u5979\u5728\u75db\u54ed\u4e4b\u4e2d\u6697\u6697\u4e3a\u53f6\u6210\u6797\u4e0e\u51cc\u4e91\u51e4\u795d\u798f\uff0c\u800c\u53c8\u6697\u6697\u4e3a\u81ea\u5df1\u4f24\u5fc3\uff0c \u8fd9\u79cd\u590d\u6742\u9690\u79d8\u7684\u5c11\u5973\u5fc3\u60c5\uff0c\u94c1\u955c\u5fc3\u7109\u80fd\u731c\u6d4b\u3002 \u3000\u3000\u8fd9\u4e8b\u8fc7\u540e\uff0c\u4e8e\u627f\u73e0\u4e00\u8def\u90c1\u90c1\u5be1\u6b22\uff0c\u94c1\u955c\u5fc3\u66f4\u4e0d\u6562\u53bb\u62db\u60f9\u5979\u3002\u8fc7\u4e86\u4e24\u65e5\uff0c\u6765\u5230\u676d\u5dde\uff0c\u94c1\u955c \u5fc3\u7684\u8001\u5bb6\u6b63\u5728\u897f\u5b50\u6e56\u8fb9\uff0c\u575a\u9080\u4e8e\u627f\u73e0\u5230\u4ed6\u5bb6\u53bb\u4f4f\u4e24\u5929\uff0c\u4e8e\u627f\u73e0\u672c\u5f85\u4e0d\u5141\uff0c\u4f46\u60f3\u5230\u94c1\u955c\u5fc3\u79bb\u5bb6 \u591a\u5e74\uff0c\u8fd9\u6b21\u8d81\u7740\u8fdb\u4eac\u4e4b\u4fbf\uff0c\u8def\u8fc7\u5bb6\u95e8\uff0c\u56de\u5bb6\u7701\u4eb2\uff0c\u4ea6\u662f\u4eba\u4e4b\u5e38\u60c5\uff0c\u6070\u5de7\u6f6e\u97f3\u548c\u5c1a\u4e5f\u8981\u5230\u7075\u9690 \u5bfa\u53bb\u8bbf\u4e00\u4f4d\u670b\u53cb\uff0c\u4e8e\u627f\u73e0\u4e0d\u6b32\u4ee4\u4ed6\u96be\u582a\uff0c\u4fbf\u7b54\u5e94\u5230\u4ed6\u5bb6\u4e2d\u4f5c\u5ba2\u3002\u3001 \u3000\u3000\u94c1\u955c\u5fc3\u7684\u7236\u4eb2\u94c1\u94f1\u662f\u4e00\u4e2a\u5df2\u7ecf\u544a\u8001\u7684\u9000\u4f11\u5fa1\u53f2\uff0c\u5f53\u5e74\u66fe\u7ecf\u5f39\u52be\u8fc7\u5978\u5ba6\u738b\u632f\uff0c\u9887\u8457\u6b63\u58f0\u3002 \u89c1\u513f\u5b50\u5e26\u4e00\u4e2a\u7f8e\u8c8c\u5982\u82b1\u7684\u5c11\u5973\u540c\u56de\uff0c\u8001\u6000\u5f25\u6170\uff0c\u4e00\u95ee\u4e4b\u4e0b\uff0c\u59cb\u77e5\u4e8e\u627f\u73e0\u7adf\u662f\u4e8e\u8c26\u7684\u5973\u513f\uff0c\u5fc3 \u4e2d\u6697\u6697\u5403\u60ca\uff0c\u53ef\u662f\u4ecd\u7136\u5bf9\u5979\u6bb7\u52e4\u62db\u5f85\uff0c\u7559\u5979\u4f4f\u4e0b\u4e86\u3002\u4e8e\u627f\u73e0\u4e0e\u4ed6\u8c08\u8bba\uff0c\u94c1\u94f1\u5bf9\u4e8e\u671d\u4e2d\u4efb\u7528\u5978 \u90aa\uff0c\u867d\u7136\u4e5f\u9887\u591a\u975e\u8bae\uff0c\u4f46\u5374\u4e5f\u4e0d\u4ee5\u53f6\u5b97\u7559\u3001\u6bd5\u64ce\u5929\u7684\u4e3e\u5175\u4e3a\u7136\uff0c\u4ed6\u662f\u4e00\u6d3e\u6b63\u7edf\u7684\u5fe0\u541b\u601d\u60f3\uff0c \u8ba4\u4e3a\u98df\u541b\u4e4b\u7984\uff0c\u5f53\u5206\u541b\u4e4b\u5fe7\uff0c\u4ed6\u4f69\u670d\u4e8e\u8c26\u7684\u516c\u5fe0\u4e3a\u56fd\uff0c\u4e3a\u4e8e\u8c26\u7684\u6789\u6b7b\u60b2\u53f9\uff0c\u5374\u53c8\u4e0d\u4ee5\u201c\u4e71\u81e3 \u8d3c\u5b50\u201d\u4e3a\u7136\uff0c\u4ed6\u529d\u4e8e\u627f\u73e0\u8c28\u614e\u884c\u4e8b\uff0c\u4e0d\u8981\u9677\u5165\u5978\u4eba\u7f57\u7f51\uff0c\u53c8\u529d\u513f\u5b50\u56fe\u4e2a\u201c\u6b63\u9014\u51fa\u8eab\u201d\uff0c\u627f\u7ee7 \u201c\u4e66\u9999\u95e8\u7b2c\u201d\uff0c\u4e0d\u53ef\u8001\u662f\u5728\u6c5f\u6e56\u4e0a\u80e1\u6df7\u3002\u4e8e\u627f\u73e0\u4f69\u670d\u4ed6\u7684\u6b63\u76f4\uff0c\u4f46\u5374\u5e76\u4e0d\u5b8c\u5168\u540c\u610f\u4ed6\u7684\u8bae \u8bba\uff0c\u4e0d\u8fc7\u94c1\u94f1\u662f\u5979\u7236\u4eb2\u65e7\u65e5\u7684\u540c\u50da\uff0c\u5c5e\u4e8e\u5979\u7684\u957f\u8f88\uff0c\u5979\u5f53\u7136\u4e5f\u4e0d\u65b9\u4fbf\u53cd\u9a73\u3002\u5403\u8fc7\u665a\u996d\uff0c\u8c08\u4e86 \u4e00\u4f1a\uff0c\u4e8e\u627f\u73e0\u4fbf\u63a8\u8bf4\u65c5\u9014\u56f0\u5026\uff0c\u56de\u623f\u6b47\u4e86\u3002 \u3000\u3000\u94c1\u94f1\u7ed9\u5979\u5e03\u7f6e\u7684\u623f\u95f4\u5341\u5206\u96c5\u81f4\uff0c\u5bf9\u7a97\u4e00\u671b\uff0c\u9762\u4e34\u897f\u6e56\uff0c\u6b63\u5bf9\u5b64\u5c71\u3002\u4e8e\u627f\u73e0\u5fc3\u4e8b\u96be\u6392\uff0c\u4e2d \u5bb5\u4e0d\u5bd0\uff0c\u51ed\u7a97\u8fdc\u773a\uff0c\u4f46\u89c1\u660e\u6708\u5728\u5929\uff0c\u6e56\u5149\u6f4b\u6edf\uff0c\u5b64\u5c71\u50cf\u4e00\u4e2a\u7761\u7f8e\u4eba\u4f3c\u7684\u6795\u7740\u897f\u6e56\uff0c\u826f\u591c\u8fe2 \u8fe2\uff0c\u6e56\u5c71\u80dc\u666f\uff0c\u4e0d\u8f93\u4e8e\u5927\u7406\u7684\u6d31\u6d77\u82cd\u5c71\uff0c\u4e8e\u627f\u73e0\u60f3\u8d77\u4e86\u6d31\u6d77\u7684\u6cdb\u821f\u4e4b\u591c\uff0c\u60f3\u8d77\u4e86\u77f3\u6797\u4e2d\u7684\u5947 \u5ca9\u5f02\u77f3\uff0c\u5c0f\u6eaa\u6d41\u6c34\uff0c\u53ea\u662f\u540c\u6e38\u7684\u53f6\u6210\u6797\u5df2\u662f\u4eba\u9694\u5343\u91cc\u4e86\u3002\u60f3\u8d77\u4ed6\u72ec\u6297\u5341\u4e07\u5b98\u519b\uff0c\u9690\u5fe7\u91cd\u91cd\u3002 \u4f46\u4e8e\u627f\u73e0\u867d\u7136\u4e3a\u4ed6\u62c5\u5fe7\uff0c\u5374\u4e5f\u4e3a\u4ed6\u7684\u82f1\u96c4\u6c14\u6982\u800c\u6697\u81ea\u5fc3\u6298\u3002\u518d\u60f3\u8d77\u94c1\u955c\u5fc3\u7684\u610f\u6b32\u5728\u897f\u5b50\u6e56\u8fb9 \u6216\u6ec7\u6c60\u4e4b\u7554\u7ed3\u5e90\u8bfb\u4e66\u7684\u5fd7\u5411\uff0c\u4f46\u89c9\u8fd9\u5fd7\u5411\u867d\u4e0d\u7b97\u574f\uff0c\u5374\u662f\u8fdc\u4e0d\u5982\u53f6\u6210\u6797\u7684\u7537\u513f\u672c\u8272\u4e86\u3002\u6b63\u5728 \u601d\u6f6e\u6742\u8d77\u4e4b\u9645\uff0c\u5ffd\u95fb\u5f97\u697c\u4e0b\u9690\u6709\u4eba\u58f0\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5e7c\u7ec3\u6697\u5668\uff0c\u8033\u529b\u6781\u4f73\uff0c\u9690\u9690\u542c\u51fa\u90a3\u662f\u8083\u5ba2\u8fdb\u95e8\u7684\u58f0\u97f3\uff0c\u811a\u6b65\u4e0a\u53f0\u9636\u7684\u58f0\u97f3\uff0c\u5fc3\u4e2d \u5947\u9053\uff1a\u201c\u8fd9\u4e2a\u65f6\u5019\u8fd8\u6709\u5ba2\u6765\uff01\u54a6\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u542c\u95fb\u4ec6\u5f79\u7aef\u8336\u4e0e\u4e3b\u5ba2\u7684\u7b11\u8bed\uff1f\u201d\u94c1\u5bb6\u623f\u5c4b\u751a\u591a\uff0c \u5185\u5916\u9694\u7edd\uff0c\u8fd9\u58f0\u97f3\u6765\u81ea\u5916\u9762\u7684\u5ba2\u5385\uff0c\u82e5\u8bf4\u662f\u8fdc\u5ba2\u591c\u6765\uff0c\u7406\u8be5\u6709\u70b9\u55a7\u95f9\uff0c\u867d\u7136\u4e0d\u81f3\u4e8e\u60ca\u52a8\u5185\u8fdb \u7684\u5bb6\u4eba\uff0c\u4f46\u51ed\u4e8e\u627f\u73e0\u7684\u8033\u529b\uff0c\u4e00\u5b9a\u53ef\u4ee5\u542c\u89c1\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u6709\u6240\u7591\uff0c\u66f4\u96be\u5b89\u5bd0\uff0c\u60f3\u4e86\u4e00\u4f1a\uff0c\u7a81\u7136\u62ab\u8863\u800c\u8d77\uff0c\u51fa\u5916\u5077\u542c\u3002\u5979\u8f7b\u529f\u6781\u597d\uff0c\u7a7f\u623f \u8fc7\u5c4b\uff0c\u65e0\u58f0\u65e0\u606f\uff0c\u63a0\u4e0a\u5ba2\u5385\u7684\u74e6\u9762\uff0c\u6302\u5728\u6a90\u89d2\uff0c\u5f80\u5185\u5077\u77a7\uff0c\u8fd9\u4e00\u77a7\u767b\u65f6\u628a\u4e8e\u627f\u73e0\u5413\u7740\u4e86\u3002 \u3000\u3000\u4f46\u89c1\u5ba2\u5385\u91cc\u9762\u5750\u7740\u4e09\u4e2a\u4eba\uff0c\u7adf\u662f\u94c1\u94f1\u7236\u5b50\u548c\u5fa1\u6797\u519b\u7684\u6307\u6325\u5a04\u6850\u5b59\uff0c\u90a3\u5a04\u6850\u5b59\u538b\u4f4e\u58f0\u97f3\u8bf4 \u9053\uff1a\u201c\u94c1\u5927\u4eba\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u8336\u9152\u62db\u5f85\uff0c\u90fd\u8bf7\u514d\u4e86\u3002\u6211\u6b64\u6765\u53ea\u662f\u60f3\u8bf7\u6559\u94c1\u516c\u5b50\u51e0\u4ef6\u4e8b\u60c5\uff0c\u8bf4\u5b8c\u4e86 \u9a6c\u4e0a\u5c31\u8d70\uff0c\u4e0d\u6562\u60ca\u52a8\u4f60\u5bb6\u8d35\u5ba2\u3002\u201d \u3000\u3000\u94c1\u94f1\u5fc3\u4e2d\u4e00\u51db\uff0c\u9053\uff1a\u201c\u5a04\u5927\u4eba\u6709\u4f55\u6307\u6559\uff0c\u5c3d\u7ba1\u5429\u5490\u5c0f\u513f\u3002\u201d\u5a04\u6850\u5b59\u563b\u563b\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c \u9633\u5927\u603b\u7ba1\u8fd1\u4ece\u6606\u660e\u56de\u6765\uff0c\u542c\u8bf4\u94c1\u516c\u5b50\u751a\u5f97\u6c90\u56fd\u516c\u770b\u91cd\uff0c\u5982\u4eca\u66ff\u6c90\u56fd\u516c\u62dc\u8868\u4e0a\u4eac\uff0c\u771f\u662f\u524d\u9014\u4f3c \u9526\u554a\u3002\u7687\u4e0a\u524d\u4e9b\u65f6\u8fd8\u66fe\u4e0e\u6211\u4eec\u63d0\u8d77\u94c1\u8001\u5927\u4eba\uff0c\u5c06\u6765\u89c1\u4e86\u94c1\u516c\u5b50\uff0c\u5b9a\u7136\u9f99\u989c\u5927\u60a6\uff0c\u94c1\u516c\u5b50\u81ea\u5f97 \u5c01\u5b98\uff0c\u8001\u5927\u4eba\u53ea\u6015\u4e5f\u8981\u4e1c\u5c71\u518d\u8d77\u4e86\u3002\u201d\u94c1\u94f1\u9053\uff1a\u201c\u6211\u5e74\u8001\u4f53\u8870\uff0c\u5b98\u662f\u4e0d\u60f3\u518d\u505a\u4e86\u3002\u5c0f\u513f\u8fd8\u671b \u683d\u57f9\u3002\u201d\u5a04\u6850\u5b59\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\u3002\u4f46\u6709\u4e00\u4e8b\u63d0\u9192\u4e16\u5144\uff0c\u5c06\u6765\u965b\u89c1\u4e4b\u65f6\uff0c\u8fd9\u628a\u5b9d\u5251\u53ef\u4e0d\u8981\u4f69 \u5728\u8eab\u4e0a\u3002\u201d\u94c1\u94f1\u5947\u9053\uff1a\u201c\u4ec0\u4e48\u5b9d\u5251\uff1f\u201d\u5a04\u606b\u5b59\u4e00\u6307\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u516c\u5b50\u8eab\u4e0a\u7684\u4f69\u5251\uff0c\u90a3\u662f\u5927\u5185 \u4e4b\u7269\u3002\u201d\u94c1\u94f1\u5927\u5403\u4e00\u60ca\u9053\uff1a\u201c\u955c\u5fc3\uff0c\u4f60\u8fd9\u5251\u4f55\u5904\u5f97\u6765\uff1f\u201d\u5a04\u6850\u5b59\u9053\uff1a\u201c\u662f\u5440\uff0c\u8fd9\u6b63\u662f\u6211\u8981\u5411 \u94c1\u516c\u5b50\u8bf7\u6559\u7684\u4e8b\u60c5\u4e4b\u4e00\u3002\u201d \u3000\u3000\u94c1\u955c\u5fc3\u62fc\u7740\u8c41\u4e86\u51fa\u53bb\uff0c\u9053\uff1a\u201c\u5a04\u5927\u4eba\u95ee\u6211\u4ece\u4f55\u5904\u5f97\u6765\uff0c\u5148\u95ee\u5a04\u5927\u4eba\u4ece\u4f55\u5904\u5931\u53bb\uff01\u201d\u5a04\u6850 \u5b59\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5927\u5185\u8fd9\u628a\u5b9d\u5251\u662f\u7ed9\u98de\u8d3c\u77f3\u60ca\u6d9b\u76d7\u53bb\u7684\uff0c\u524d\u5e74\u627f\u8499\u516c\u5b50\u4ece\u77f3\u60ca\u6d9b\u624b\u4e2d\u8ba8\u8fd8\uff0c\u5a04 \u67d0\u4e0d\u624d\uff0c\u7ed9\u5f20\u4e39\u67ab\u7684\u515a\u7fbd\u4e4c\u8499\u592b\u593a\u53bb\uff0c\u5982\u4eca\u53c8\u5230\u4e86\u516c\u5b50\u8eab\u4e0a\uff0c\u539f\u6765\u516c\u5b50\u4e0d\u4f46\u4e0e\u77f3\u60ca\u6d9b\u6709\u5e08\u5f92 \u7684\u60c5\u4efd\uff0c\u800c\u4e14\u4e0e\u5f20\u4e39\u67ab\u4e5f\u5927\u6709\u6e0a\u6e90\u3002\u201d \u3000\u3000\u94c1\u94f1\u5413\u5f97\u5446\u4e86\uff0c\u98a4\u6144\u8bf4\u9053\uff1a\u201c\u5c0f\u513f\u65e0\u77e5\uff0c\u4e0d\u77e5\u5e95\u7ec6\u8bef\u4ea4\u532a\u4eba\uff0c\u4e5f\u662f\u6709\u7684\uff0c\u671b\u5a04\u5927\u4eba\u5305 \u6db5\u3002\u8fd9\u628a\u5251\u65e2\u662f\u5927\u5185\u4e4b\u7269\uff0c\u955c\u5fc3\uff0c\u4f60\u4ea4\u7ed9\u5a04\u5927\u4eba\uff0c\u7f34\u56de\u5927\u5185\u9500\u5dee\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u8fd9\u662f\u6211\u5e08 \u7236\u7684\u4e1c\u897f\uff0c\u5f53\u6740\u5f53\u5250\uff0c\u7531\u6211\u62c5\u627f\uff0c\u4e0e\u5bb6\u7236\u65e0\u5173\u3002\u201d \u3000\u3000\u94c1\u94f1\u60ca\u9053\uff1a\u201c\u955c\u5fc3\uff0c\u4f60\uff0c\u4f60\uff0c\u4f60\u600e\u4e48\u8fd9\u6837\u8bf4\u8bdd\uff1f\u201d\u5a04\u6850\u5b59\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u94c1\u516c\u5b50\u8a00\u91cd\u4e86\u3002 \u8fd9\u628a\u5251\u867d\u662f\u7a00\u4e16\u4e4b\u73cd\uff0c\u4e5f\u8fd8\u4e0d\u7b97\u4ec0\u4e48\u3002\u53ea\u8981\u94c1\u516c\u5b50\u518d\u7b54\u6211\u7b2c\u4e8c\u6869\u4e8b\u60c5\uff0c\u90a3\u4e48\u5b9d\u5251\u4ecd\u5f52\u516c\u5b50\uff0c \u6211\u51b3\u4e0d\u594f\u660e\u7687\u4e0a\u3002\u201d\u94c1\u955c\u5fc3\u5176\u5b9e\u4e5f\u6015\u8fde\u7d2f\u5bb6\u4eba\uff0c\u4ea6\u820d\u4e0d\u5f97\u8fd9\u628a\u5b9d\u5251\uff0c\u542c\u5a04\u6850\u5b59\u6709\u610f\u5356\u4ed6\u4ea4 \u60c5\uff0c\u4ed6\u7684\u53e3\u98ce\u4e5f\u5c31\u8f6f\u4e86\u4e00\u4e9b\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u8bf7\u8bf4\u3002\u201d\u5a04\u6850\u5b59\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u5bb6\u4e2d\u6765\u7684 \u8d35\u5ba2\u662f\u8c01\uff1f\u201d \u3000\u3000\u94c1\u94f1\u8fd9\u4e00\u4e0b\u5403\u60ca\u66f4\u751a\uff0c\u94c1\u955c\u5fc3\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u5a04\u5927\u4eba\u5802\u5802\u4e00\u4f4d\u4e8c\u54c1\u6307\u6325\uff0c\u8fde\u6c5f\u6e56\u4e0a\u8fd9\u7b49\u8ddf \u8e2a\u6697\u7f00\u7684\u52fe\u5f53\u4e5f\u4eb2\u81ea\u505a\u4e86\u3002\u201d\u5a04\u6850\u5b59\u7b11\u9053\uff1a\u201c\u82e5\u662f\u5bfb\u5e38\u4eba\u72af\uff0c\u5a04\u67d0\u81ea\u7136\u4e0d\u5fc5\u4eb2\u81ea\u51fa\u9a6c\uff0c\u53f5\u5948 \u8fd9\u4f4d\u662f\u4e8e\u9601\u8001\u4e8e\u8c26\u7684\u5343\u91d1\u5c0f\u59d0\uff0c\u90a3\u4e48\u6211\u5c31\u662f\u8ddf\u8e2a\u6697\u7f00\u4e5f\u8fd8\u4e0d\u7b97\u662f\u5931\u4e86\u8eab\u4efd\uff01\u94c1\u8001\u5927\u4eba\uff0c\u8fd9\u4f4d \u8d35\u5ba2\u8c05\u4f60\u4e5f\u77e5\u9053\u4e86\u5979\u7684\u8eab\u4efd\uff0c\u5979\u53ef\u662f\u4f60\u4eb2\u81ea\u6b3e\u5f85\u7684\u554a\uff01\u201d\u94c1\u955c\u5fc3\u52c3\u7136\u8272\u53d8\uff0c\u6309\u5251\u8bf4\u9053\uff1a\u201c\u5a04 \u5927\u4eba\uff0c\u4f60\u610f\u6b32\u5982\u4f55\uff1f\u201d\u5a04\u6850\u5b59\u9053\uff1a\u201c\u90a3\u5c31\u8981\u5148\u770b\u516c\u5b50\u610f\u6b32\u5982\u4f55\u4e86\uff1f\u201d\u94c1\u955c\u5fc3\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u82e5 \u662f\u4f60\u8981\u5c06\u5979\u4ece\u6211\u5bb6\u4e2d\u6355\u53bb\uff0c\u6211\u8ba4\u5f97\u4f60\uff0c\u8fd9\u628a\u5251\u53ef\u8ba4\u4e0d\u5f97\u4f60\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u542c\u5230\u6b64\u5904\uff0c\u5fc3\u4e2d\u6697\u6697\u611f\u52a8\uff0c\u5ffd\u542c\u5f97\u5a04\u606b\u5b59\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u94c1\u516c\u5b50\u5b9d\u5251\u867d\u5229\uff0c\u6211\u5a04\u6850 \u5b59\u5374\u8fd8\u4e0d\u60e7\u3002\u4f55\u51b5\u7eb5\u662f\u4f60\u5c06\u6211\u6740\u4e86\uff0c\u8fd9\u6284\u5bb6\u706d\u65cf\u4e4b\u7978\uff0c\u4f60\u4eec\u94c1\u5bb6\u4e5f\u4e0d\u65e0\u987e\u5fcc\u5427\uff1f\u201d\u94c1\u94f1\u672c\u6765 \u4e5f\u51c6\u5907\u8c41\u4e86\u51fa\u53bb\uff0c\u542c\u5a04\u606b\u5b59\u7684\u53e3\u98ce\u4f3c\u4e4e\u8fd8\u6709\u8f6c\u5708\u4e4b\u5730\uff0c\u7981\u4e0d\u4f4f\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u5a04\u5927\u4eba\u8bf7\u9ad8\u62ac\u8d35 \u624b\uff0c\u94c1\u94f1\u81ea\u5f53\u91cd\u8c22\u3002\u201d\u5a04\u6850\u5b59\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u4e2a\u5b98\u513f\u867d\u65e0\u6cb9\u6c34\uff0c\u4e5f\u8fd8\u4e0d\u81f3\u4e8e\u8d2a\u56fe\u94c1\u8001\u5927\u4eba\u7684\u8c22 \u793c\u3002\u8fd9\u4e8b\u8981\u6211\u4e0d\u95ee\uff0c\u94c1\u516c\u5b50\uff0c\u4f60\u53ef\u5f97\u7ed9\u6211\u5e2e\u5fd9\uff01\u201d \u3000\u3000\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u90a3\u4e5f\u5f97\u770b\u662f\u4ec0\u4e48\u4e8b\u60c5\u3002\u201d\u5a04\u6850\u5b59\u9053\uff1a\u201c\u542c\u8bf4\u516c\u5b50\u662f\u4ece\u5357\u8fb9\u6765\uff0c\u548c\u53f6\u5b97\u7559\u3001 \u6bd5\u64ce\u5929\u90fd\u662f\u4ea4\u60c5\u4e0d\u6d45\u3002\u201d\u94c1\u94f1\u6599\u4e0d\u5230\u4e00\u6ce2\u672a\u5e73\uff0c\u4e00\u6ce2\u53c8\u8d77\uff0c\u5fd9\u9053\uff1a\u201c\u5c0f\u513f\u5e7c\u8bfb\u8bd7\u4e66\uff0c\u867d\u7136\u7231 \u5728\u6c5f\u6e56\u4e0a\u6df7\uff0c\u4f46\u6e05\u8005\u81ea\u6e05\uff0c\u6d4a\u8005\u81ea\u6d4a\uff0c\u8c05\u4ed6\u8fd8\u4e0d\u81f3\u4e8e\u4e0e\u76d7\u532a\u540c\u6d41\u3002\u201d\u5a04\u6850\u5b59\u9053\uff1a\u201c\u516c\u5b50\u4e3a \u4eba\uff0c\u6211\u4e5f\u7a0d\u77e5\u4e00\u4e8c\uff0c\u8981\u4e0d\u7136\u6211\u4e5f\u4e0d\u4f1a\u4e0e\u516c\u5b50\u8bf4\u4e86\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u4f60\u5230\u5e95\u8981\u6211\u5e2e\u4ec0\u4e48\u5fd9\uff1f\u201d \u5a04\u6850\u5b59\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u671d\u5ef7\u5c06\u53f6\u3001\u6bd5\u4e8c\u8d3c\u89c6\u4e3a\u5fc3\u8179\u5927\u60a3\uff0c\u73b0\u4e0b\u5df2\u8c03\u4e86\u51e0\u8def\u5927\u519b\u56f4\u527f\uff0c\u6d59\u6c5f \u65b9\u9762\uff0c\u7531\u5de1\u629a\u5f20\u9aa5\u4eb2\u9886\u5927\u519b\uff0c\u6b63\u9762\u76f4\u6363\u532a\u5de2\u3002\u5a04\u67d0\u4e5f\u5728\u519b\u524d\u6548\u529b\u3002\u81ea\u4e0b\u671d\u5ef7\u6b63\u9700\u8981\u719f\u8bc6\u532a\u60c5 \u7684\u8c6a\u6770\u4e4b\u58eb\u76f8\u52a9\u3002\u94c1\u516c\u5b50\u4ea6\u6709\u610f\u5efa\u529f\u7acb\u4e1a\u4e4e\uff1f\u201d\u94c1\u955c\u5fc3\u7709\u5934\u4e00\u76b1\uff0c\u60f3\u9053\uff1a\u201c\u6211\u867d\u7136\u770b\u4e0d\u8d77\u6bd5 \u64ce\u5927\u3001\u53f6\u6210\u6797\uff0c\u4f46\u53eb\u6211\u9886\u5175\u53bb\u6253\u4ed6\u4eec\uff0c\u5c82\u4e0d\u4f24\u4e86\u627f\u73e0\u4e4b\u5fc3\uff1f\u201d\u7b54\u9053\uff1a\u201c\u6211\u65e0\u610f\u5728\u519b\u529f\u4e0a\u56fe\u4e2a \u51fa\u8eab\uff0c\u518d\u8bf4\u6211\u6b63\u5949\u4e86\u6c90\u56fd\u516c\u4e4b\u547d\uff0c\u62dc\u8868\u4e0a\u4eac\u3002\u201d\u5a04\u6850\u5b59\u9053\uff1a\u201c\u6c90\u56fd\u516c\u65e9\u5df2\u6709\u8868\u8fdb\u4eac\uff0c\u6c90\u56fd\u516c \u4e4b\u610f\uff0c\u4e0d\u8fc7\u662f\u5c06\u516c\u5b50\u8350\u7ed9\u7687\u4e0a\u7f62\u4e86\uff0c\u8361\u5e73\u53db\u9006\uff0c\u518d\u53bb\u671d\u5929\uff0c\u6b63\u8db3\u89c1\u516c\u5b50\u4e0d\u662f\u56e0\u4eba\u6210\u4e8b\u554a\uff01\u201d \u94c1\u955c\u5fc3\u597d\u6234\u9ad8\u5e3d\uff0c\u542c\u4e86\u6b64\u8a00\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4f46\u4ecd\u662f\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u53bb\uff01\u201d \u3000\u3000\u5a04\u6850\u5b59\u9634\u607b\u607b\u7b11\u9053\uff1a\u201c\u516c\u5b50\u575a\u6267\u4e0d\u53bb\uff0c\u6211\u4e5f\u65e0\u6cd5\u52c9\u5f3a\u3002\u53ea\u662f\u5927\u5185\u5b9d\u5251\u4e0e\u4e8e\u8c26\u4e4b\u5973\u8fd9\u4e24\u4e8b \u5982\u4f55\u4ea4\u4ee3\uff1f\u55ef\uff0c\u4e0d\u5982\u8fd9\u6837\u5427\uff0c\u7d20\u4ef0\u516c\u5b50\u6587\u6b66\u5168\u6750\uff0c\u7cbe\u901a\u97ec\u7565\u3002\u8bf7\u516c\u5b50\u5c06\u6240\u77e5\u7684\u532a\u60c5\u5199\u51fa\uff0c\u518d \u4e3a\u6211\u4eec\u62df\u4e00\u527f\u532a\u7684\u65b9\u6848\u5982\u4f55\uff1f\u201d\u94c1\u955c\u5fc3\u51b7\u7b11\u9053\uff1a\u201c\u6bd5\u64ce\u5929\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u503c\u5f97\u4f60\u4eec\u8fd9\u6837\u770b\u91cd\uff1f \u53f6\u5b97\u7559\u65e9\u5df2\u7ed9\u4ed6\u903c\u8d70\u4e86\uff0c\u4ed6\u73b0\u5728\u72ec\u6728\u96be\u652f\uff0c\u4f60\u4eec\u8fd8\u4e0d\u77e5\u9053\uff01\u201d\u5a04\u6850\u5b59\u5927\u559c\u9053\uff1a\u201c\u771f\u7684\uff1f\u54c8\uff0c \u8fd9\u5c31\u662f\u4e00\u4ef6\u91cd\u5927\u7684\u532a\u60c5\uff0c\u516c\u5b50\uff0c\u4f60\u518d\u5199\u51e0\u4ef6\uff1f\u201d\u4e8e\u627f\u73e0\u542c\u5230\u6b64\u5904\uff0c\u53c8\u6025\u53c8\u6012\uff0c\u53ea\u542c\u5f97\u4e0b\u9762\u65e0 \u58f0\u65e0\u606f\uff0c\u9690\u9690\u95fb\u5f97\u7b14\u950b\u5728\u7eb8\u9762\u79fb\u52a8\u7684\u5982\u8695\u98df\u53f6\u4e4b\u58f0\u3002\u4e8e\u627f\u73e0\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u3002\u6697\u6697\u53f9\u4e86\u53e3\u6c14\uff0c\u4e0d \u613f\u518d\u542c\uff0c\u56de\u5230\u623f\u4e2d\uff0c\u7acb\u523b\u6362\u4e86\u7537\u88c5\uff0c\u623f\u4e2d\u6709\u73b0\u6210\u7684\u7eb8\u7b14\uff0c\u5979\u6293\u8d77\u4e86\u7b14\u5c31\u7ed9\u94c1\u955c\u5fc3\u7559\u4e0b\u4e86\u8bc0\u522b \u7684\u4e66\u4fe1\u3002 \u3000\u3000\u5c3d\u7ba1\u4ee5\u5f80\u6709\u8fc7\u65e0\u6570\u6b21\u4e8e\u627f\u73e0\u5bf9\u94c1\u955c\u5fc3\u611f\u5230\u5931\u671b\uff0c\u4f46\u5374\u4ece\u65e0\u4e00\u6b21\u4f3c\u6b64\u523b\u7684\u4f24\uff0c\u4e8e\u627f\u73e0\u5bf9\u4ed6 \u4e0d\u4ec5\u662f\u201c\u5931\u671b\u201d\u7b80\u76f4\u662f\u201c\u7edd\u671b\u201d\u4e86\u3002\u5979\u60f3\u4e0d\u5230\u94c1\u955c\u5fc3\u7adf\u4f1a\u51fa\u5356\u519b\u60c5\uff0c\u4e3a\u5b98\u519b\u7b56\u5212\u5bf9\u4ed8\u4e49\u519b\u3002 \u867d\u8bf4\u94c1\u955c\u5fc3\u8fd9\u6837\u505a\u662f\u4e3a\u4e86\u201c\u5e87\u62a4\u201d\u5979\uff0c\u8fd9\u5374\u66f4\u4ee4\u5979\u75db\u5fc3\u75be\u9996\u3002\u5c3d\u7ba1\u5979\u5bf9\u6bd5\u64ce\u5929\u4e5f\u662f\u4e0d\u6ee1\uff0c\u4f46 \u5bf9\u4e49\u519b\u5979\u5374\u59cb\u7ec8\u5bc4\u4ee5\u540c\u60c5\uff0c\u5c3d\u7ba1\u5979\u65e9\u77e5\u9053\u4e86\u94c1\u955c\u5fc3\u548c\u53f6\u6210\u6797\u662f\u4e24\u6761\u8def\u4e0a\u7684\u4eba\uff0c\u4f46\u5bf9\u94c1\u955c\u5fc3\u8fd9 \u6837\u7684\u884c\u4e3a\u5374\u7edd\u4e0d\u80fd\u8c05\u89e3\u3002\u201c\u9053\u4e0d\u540c\u4e0d\u76f8\u4e3a\u8c0b\u201d\uff0c\u5979\u6df1\u6df1\u611f\u5230\u8fd9\u53e5\u53e4\u8bad\u7684\u610f\u4e49\u4e86\u3002 \u3000\u3000\u5979\u7559\u4e0b\u4e86\u8bc0\u522b\u7684\u4e66\u4fe1\uff0c\u6362\u4e0a\u4e86\u7537\u88c5\uff0c\u6084\u6084\u5730\u9a91\u4e0a\u767d\u9a6c\uff0c\u72ec\u81ea\u4e00\u4eba\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u7edd\u5c18\u800c \u53bb\u3002\u5230\u94c1\u955c\u5fc3\u53d1\u73b0\u4e4b\u65f6\uff0c\u90a3\u5df2\u7ecf\u662f\u8fdf\u4e86\uff0c\u592a\u8fdf\u4e86\uff01 \u3000\u3000\u534a\u4e2a\u6708\u4e4b\u540e\uff0c\u4e8e\u627f\u73e0\u5230\u4e86\u5317\u4eac\u3002\u5979\u662f\u5728\u5317\u4eac\u957f\u5927\u7684\uff0c\u90a3\u65f6\u5979\u662f\u9601\u8001\u7684\u5343\u91d1\u5c0f\u59d0\uff1b\u73b0\u5728\u56de \u6765\uff0c\u5374\u662f\u4e2a\u5386\u904d\u6c5f\u6e56\u98ce\u6d6a\u7684\u5973\u4fa0\uff0c\u517c\u4e14\u662f\u201c\u6f5c\u884c\u56de\u5883\u201d\u7684\u201c\u72af\u4eba\u201d\u8eab\u4efd\u4e86\uff0c\u56de\u9996\u524d\u5c18\uff0c\u81ea\u662f \u4e0d\u80dc\u611f\u6168\u3002\u5e78\u559c\u5979\u6362\u4e0a\u7537\u88c5\uff0c\u6ca1\u4eba\u8ba4\u51fa\u5979\uff0c\u4e00\u5165\u5317\u4eac\uff0c\u7acb\u523b\u627e\u5979\u7236\u4eb2\u7684\u8001\u670b\u53cb\u66f9\u5b89\u3002 \u3000\u3000\u8fd9\u66f9\u5b89\u662f\u4e00\u4e2a\u5e74\u8001\u9000\u4f11\u7684\u8001\u592a\u76d1\uff0c\u66fe\u4f8d\u5949\u5148\u5e1d\uff0c\u9887\u6709\u529f\u52b3\u3002\u6240\u4ee5\u5f53\u4eca\u7684\u7687\u5e1d\u51c6\u4ed6\u544a\u8001\u51fa \u5bab\uff0c\u5f52\u5bb6\u63a5\u53d7\u4f84\u5b50\u7684\u5949\u517b\u3002\u5f53\u5e74\u4e8e\u8c26\u88ab\u5728\u6740\u4e4b\u65f6\uff0c\u6ee1\u671d\u6587\u6b66\uff0c\u4e0d\u5c11\u662f\u4e8e\u8c26\u63d0\u62d4\u7684\uff0c\u65e0\u4eba\u6562\u51fa \u5934\u8bf4\u4e00\u53e5\u8bdd\uff0c\u53ea\u6709\u66f9\u5b89\u6562\u5411\u7687\u5e1d\u8bf7\u6c42\u6536\u6b93\u4e8e\u8c26\u7684\u9057\u9ab8\uff0c\u6070\u5de7\u90a3\u65f6\u9002\u503c\u4e8e\u8c26\u7684\u5934\u88ab\u6bd5\u64ce\u5929\u5077 \u53bb\uff0c\u7687\u5e1d\u4e5f\u77e5\u7fa4\u60c5\u6c79\u6d8c\uff0c\u4e50\u5f97\u505a\u4e2a\u4e00\u987a\u6c34\u4eba\u60c5\uff0c\u6279\u9053\uff1a\u201c\u59d1\u5ff5\u4e8e\u8c26\u4e43\u4e24\u671d\u5143\u8001\uff0c\u51c6\u4e88\u6536 \u6b93\u3002\u201d\u5176\u540e\u6bd5\u64ce\u5929\u4e5f\u662f\u9760\u4e86\u66f9\u592a\u76d1\u4e4b\u529b\uff0c\u624d\u5f97\u5c06\u4e8e\u8c26\u7684\u5c38\u9996\u5408\u4e00\uff0c\u846c\u4e8e\u676d\u5dde\uff08\u4e8b\u8be6\u672c\u4e66\u7b2c\u4e8c \u56de\uff09\u3002\u6bd5\u64ce\u5929\u65f6\u65f6\u4ee5\u6536\u6b93\u4e8e\u8c26\u4e4b\u4e8b\uff0c\u5bf9\u4e8e\u627f\u73e0\u793a\u6069\uff0c\u5176\u5b9e\u8fd8\u662f\u66f9\u592a\u76d1\u6240\u51fa\u7684\u529b\u6bd4\u6bd5\u64ce\u5929\u66f4 \u591a\u3002 \u3000\u3000\u66f9\u5b89\u89c1\u4e86\u4e8e\u627f\u73e0\uff0c\u975e\u5e38\u9ad8\u5174\uff0c\u4e8e\u627f\u73e0\u8fd8\u6015\u8fde\u7d2f\u4ed6\uff0c\u4ed6\u4e00\u53e3\u5e94\u627f\u8bf4\u9053\uff1a\u201c\u6211\u5386\u4f8d\u4e09\u671d\u7687 \u5e1d\uff0c\u5982\u4eca\u884c\u5c06\u5c31\u6728\uff0c\u5c31\u662f\u67e5\u51fa\u4e86\u6700\u591a\u4ea6\u662f\u4e00\u6b7b\uff0c\u4f55\u51b5\u672a\u5fc5\u4f1a\u8d50\u6b7b\u5462\u3002\u201d\u4e8e\u662f\u4e8e\u627f\u73e0\u4fbf\u653e\u5fc3\u5728 \u66f9\u592a\u76d1\u7684\u5bb6\u91cc\u4f4f\u4e0b\u3002 \u3000\u3000\u66f9\u5bb6\u9760\u8fd1\u897f\u95e8\uff0c\u8fdc\u79bb\u5e02\u533a\uff0c\u66f9\u592a\u76d1\u4e3a\u4e86\u66ff\u4e8e\u627f\u73e0\u6253\u542c\u6d88\u606f\uff0c\u4e0d\u60dc\u4ee5\u5782\u8001\u4e4b\u8eaf\uff0c\u4e09\u5929\u4e24\u5934 \u5730\u7b56\u6756\u5165\u5bab\uff0c\u5230\u76f8\u8bc6\u7684\u6267\u4e8b\u592a\u76d1\u5904\u95f2\u804a\uff0c\u4f46\u603b\u542c\u4e0d\u5230\u6709\u4ec0\u4e48\u6ce2\u65af\u516c\u4e3b\u5165\u671d\u7684\u6d88\u606f\u3002\u4e8e\u627f\u73e0\u9887 \u4e3a\u7126\u6025\u3002\u4f9d\u94c1\u955c\u5fc3\u6240\u8bf4\uff0c\u4ed6\u5e08\u7236\u62a4\u9001\u6ce2\u65af\u516c\u4e3b\u5165\u4eac\uff0c\u5927\u7ea6\u662f\u6bd4\u5979\u8fdf\u4e00\u4e2a\u6708\u52a8\u8eab\uff0c\u5979\u5728\u4e49\u519b\u4e4b \u4e2d\u803d\u6401\u4e86\u4e09\u4e2a\u6708\uff0c\u867d\u8bf4\u5979\u7684\u9a6c\u5feb\uff0c\u4f46\u4ee5\u8def\u7a0b\u63a8\u7b97\uff0c\u5979\u7684\u5e08\u7236\u4e5f\u5e94\u8be5\u5230\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u8fd9\u4e00\u4f4f\u5c31\u4f4f\u4e86\u4e00\u4e2a\u591a\u6708\uff0c\u9664\u4e86\u6302\u5ff5\u5e08\u7236\u4e4b\u5916\uff0c\u66f4\u6302\u5ff5\u53f6\u6210\u6797\uff0c\u60f3\u4ed6\u5728\u5b98\u519b\u5927\u4e3e\u56f4 \u653b\u4e4b\u4e0b\uff0c\u6bd5\u64ce\u5929\u53c8\u4e0e\u4ed6\u4e0d\u548c\uff0c\u53ea\u6015\u4ed6\u7eb5\u6709\u624d\u80fd\uff0c\u4ea6\u662f\u51f6\u591a\u5409\u5c11\u3002\u8fd9\u4e00\u65e5\u5979\u95f7\u95f7\u4e0d\u4e50\uff0c\u72ec\u81ea\u51fa \u5916\u6e9c\u8fbe\uff0c\u542c\u5f97\u897f\u95e8\u5916\u7684\u4e00\u5bb6\u5927\u9662\u5b50\u9f13\u4e50\u55a7\u5929\uff0c\u95ee\u770b\u70ed\u95f9\u7684\u4eba\uff0c\u539f\u6765\u662f\u8fd9\u5bb6\u5458\u5916\u4e3a\u513f\u5b50\u5b8c\u5a5a\uff0c \u4e8e\u627f\u73e0\u767e\u65e0\u804a\u8d56\uff0c\u4fe1\u6b65\u8d70\u53bb\uff0c\u770b\u770b\u70ed\u95f9\u3002\u8fd9\u4e00\u770b\uff0c\u6709\u5206\u6559\uff1a \u3000\u3000\u6ed4\u65e0\u98ce\u6d6a\u60ca\u5fc3\u9b44\uff0c\u9f99\u4e89\u864e\u6597\u95f9\u4eac\u534e\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e8c\u56de\u3000\u8840\u96e8\u8165\u98ce\u3000\u9b54\u5ca9\u95fb\u6076\u8baf\u3000\u5200\u5149\u5251\u5f71\u3000\u7981\u82d1\u9677\u91cd\u56f4 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e09\u5341\u4e8c\u56de\u3000\u8840\u96e8\u8165\u98ce\u3000\u9b54\u5ca9\u95fb\u6076\u8baf\u3000\u5200\u5149\u5251\u5f71\u3000\u7981\u82d1\u9677\u91cd\u56f4 \u3000\u3000\u90a3\u5458\u5916\u5927\u7ea6\u662f\u4e2a\u6709\u94b1\u4eba\u5bb6\uff0c\u9662\u5b50\u91cc\u642d\u4e86\u4e00\u4e2a\u6728\u68da\u6f14\u620f\uff0c\u5916\u9762\u6709\u8bb8\u591a\u4e5e\u4e10\u9c7c\u8d2f\u800c\u5165\u3002\u539f\u6765 \u5317\u4eac\u7684\u5927\u6237\u4eba\u5bb6\uff0c\u6709\u5a5a\u4e27\u5927\u4e8b\uff0c\u4f8b\u987b\u5e7f\u65bd\u7fa4\u4e10\uff0c\u800c\u5317\u4eac\u7684\u4e5e\u4e10\u4e5f\u6781\u6709\u79e9\u5e8f\uff0c\u6392\u961f\u5531\u540d\u9886\u8d4f\uff0c \u9886\u8fc7\u4e4b\u540e\u4fbf\u9000\uff0c\u4ece\u6765\u4e0d\u4f1a\u91cd\u9886\uff0c\u66f4\u4e0d\u4f1a\u9a9a\u6270\u4e3b\u5bb6\u3002\u5317\u4eac\u4eba\u4ee5\u5b88\u793c\u51fa\u540d\uff0c\u8fde\u4e5e\u4e10\u4e5f\u4e0d\u4f8b\u5916\u3002\u4e8e \u627f\u73e0\u81ea\u5c0f\u770b\u60ef\u4e86\uff0c\u4e5f\u4e0d\u89c9\u5f97\u5947\u602a\u3002 \u3000\u3000\u6b63\u5728\u4ec1\u7acb\u95f2\u671b\uff0c\u5ffd\u89c1\u4e00\u4e2a\u4e5e\u4e10\u5306\u5306\u800c\u6765\uff0c\u5e74\u7eaa\u751a\u8f7b\uff0c\u5927\u7ea6\u662f\u4e8c\u5341\u5c81\u5de6\u53f3\u5e74\u7eaa\uff0c\u6240\u80cc\u7684\u5e03 \u888b\u5374\u4e0e\u4f17\u4e0d\u540c\uff0c\u90a3\u662f\u7528\u7ea2\u9ed1\u767d\u4e09\u79cd\u7834\u5e03\u7f00\u6210\u7684\uff0c\u5e03\u888b\u4e0a\u6253\u4e86\u4e03\u4e2a\u7ed3\uff0c\u8bb8\u591a\u5e74\u8001\u4e5e\u4e10\uff0c\u90fd\u8ba9\u4ed6 \u5148\u4e0a\uff0c\u4e8e\u627f\u73e0\u5403\u4e86\u4e00\u60ca\u3002\u5979\u5728\u6c5f\u6e56\u4e0a\u51e0\u5e74\uff0c\u77e5\u9053\u4e10\u5e2e\u4e0a\u7684\u89c4\u77e9\uff0c\u80cc\u8fd9\u79cd\u5e03\u888b\u7684\u4e43\u662f\u7ed9\u4e10\u5e2e\u9996 \u9886\u9001\u6025\u4fe1\u7684\uff0c\u4e0a\u9762\u6253\u7740\u4e03\u4e2a\u7ed3\u5373\u662f\u8868\u793a\u5dee\u9063\u4ed6\u9001\u4fe1\u7684\u8fd9\u4e2a\u4eba\u4e43\u662f\u4e10\u5e2e\u7684\u201c\u4e03\u888b\u201d\u5f1f\u5b50\uff0c\u4e10\u5e2e \u9664\u4e86\u9f99\u5934\u5e2e\u4e3b\u4e4b\u5916\uff0c\u4ee5\u201c\u4e5d\u888b\u201d\u5f1f\u5b50\u4e3a\u6700\u9ad8\uff0c\u201c\u4e03\u888b\u201d\u5f1f\u5b50\u90a3\u4e5f\u662f\u5c11\u6709\u7684\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u751a\u662f\u5947\u602a\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u6bd5\u64ce\u5929\u4ee5\u5317\u65b9\u4e10\u5e2e\u9f99\u5934\u5e2e\u4e3b\u7684\u8eab\u4efd\uff0c\u81ea\u5c01\u5929\u4e0b\u5341\u516b\u7701\u5927\u9f99 \u5934\uff0c\u5728\u5357\u65b9\u9ad8\u4e3e\u4e49\u65d7\uff0c\u4e0d\u4e45\u5c31\u8981\u79f0\u7687\u79f0\u5e1d\u3002\u5317\u65b9\u4e10\u5e2e\u4e2d\u6709\u672c\u9886\u7684\u4eba\u7269\uff0c\u503e\u5de2\u5357\u4e0b\uff0c\u600e\u4e48\u5317\u4eac \u57ce\u4e2d\u8fd8\u6709\u4e00\u4e2a\u201c\u4e03\u888b\u201d\u5f1f\u5b50\uff0c\u5374\u672a\u5230\u5357\u8fb9\u6295\u4ed6\uff0c\u7559\u5fc3\u7ec6\u770b\uff0c\u53ea\u89c1\u90a3\u4e2a\u5c11\u5e74\u4e5e\u4e10\u5306\u5306\u6324\u5230\u524d \u9762\uff0c\u4e0e\u4e00\u4e2a\u5e74\u8001\u7684\u6b8b\u5e9f\u4e5e\u4e10\u8033\u8bed\u51e0\u53e5\uff0c\u7adf\u7136\u6ca1\u6709\u9886\u8d4f\uff0c\u4fbf\u5306\u5306\u9000\u51fa\uff0c\u663e\u7136\u53c8\u662f\u8981\u8d76\u5230\u7b2c\u4e8c\u5904 \u9001\u4fe1\u4e86\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5077\u5077\u5730\u8ddf\u5728\u4ed6\u540e\u9762\uff0c\u53ea\u89c1\u4ed6\u5306\u5306\u51fa\u57ce\uff0c\u76f4\u9a71\u897f\u5c71\u3002\u4e8e\u627f\u73e0\u77a7\u7740\u56db\u4e0b\u65e0\u4eba\uff0c\u8f7b\u8f7b\u4e00 \u63a0\uff0c\u8d8a\u8fc7\u4ed6\u7684\u524d\u5934\uff0c\u56de\u5934\u963b\u6b62\u4e86\u4ed6\u7684\u53bb\u8def\u3002\u90a3\u5c11\u5e74\u4e5e\u4e10\u7a81\u7136\u53d1\u73b0\u6709\u4eba\u8ddf\u8e2a\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u7741\u5927 \u773c\u775b\u95ee\u9053\uff1a\u201c\u76f8\u516c\uff0c\u4f60\u4e3a\u4f55\u62e6\u8def\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u662f\u90a3\u5bb6\u4eba\u5bb6\u7684\u77e5\u5ba2\uff0c\u66ff\u4ed6\u6d3e\u9152\u83dc\u8d4f\u94b1\u7ed9\u4f60\u4eec\u3002\u4f60\u4e3a\u4ec0\u4e48\u5230\u4e86\u9662\u5b50\u91cc\u4e5f\u4e0d \u53bb\u9886\u8d4f\uff0c\u8fd9\u5c82\u4e0d\u662f\u77a7\u4e0d\u8d77\u6211\u4eec\u4e3b\u4eba\u5bb6\u5417\uff1f\u201d\u90a3\u4e5e\u4e10\u6014\u4e86\u4e00\u6014\uff0c\u5531\u4e86\u4e2a\u8bfa\uff0c\u65bd\u793c\u8bf4\u9053\uff1a\u201c\u6211\u6765 \u5f97\u8fdf\uff0c\u8d76\u5230\u524d\u9762\u672c\u6765\u4e0d\u5408\u89c4\u77e9\uff0c\u4eca\u5929\u6765\u7684\u82b1\u5b50\u53c8\u591a\uff0c\u6211\u4e0d\u8010\u70e6\u6392\u961f\u7b49\u5019\u3002\u6240\u4ee5\u5230\u524d\u9762\u4e0e\u5144\u5f1f \u4eec\u8bf4\u51e0\u53e5\u8bdd\uff0c\u53eb\u4ed6\u9886\u4e86\u8d4f\u94b1\uff0c\u5404\u4eba\u5300\u51fa\u4e00\u70b9\u4e0e\u6211\uff0c\u4e5f\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u82e5\u6015\u9ebb\u70e6\uff0c\u8ddf\u6211\u56de\u53bb\u3002\u6211\u9a6c\u4e0a\u5148\u8d4f\u7ed9\u4f60\u3002\u201d\u90a3\u5c11\u5e74\u4e5e\u4e10\u9053\uff1a\u201c\u591a\u8c22\uff0c\u591a \u8c22\uff0c\u4e0d\u6562\u53e8\u6405\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d\u6210\uff01\u4e0d\u6210\uff01\u4f60\u4e0d\u8981\u5c31\u662f\u89e6\u4e86\u4e3b\u4eba\u5bb6\u7684\u9709\u5934\u3002\u201d\u90a3\u4e5e\u4e10\u751f \u6c14\u9053\uff1a\u201c\u6ca1\u542c\u8fc7\u8fd9\u4e2a\u89c4\u77e9\uff0c\u6211\u82b1\u5b50\u5927\u7237\u81ea\u613f\u4e0d\u8981\uff0c\u4f60\u8fd8\u80fd\u5f3a\u6211\u4e0d\u6210\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5bf9\u5566\uff0c \u6211\u5c31\u662f\u8981\u5f3a\u4f60\u56de\u53bb\u9886\u8d4f\u3002\u201d\u90a3\u4e5e\u4e10\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4e0e\u7a77\u53eb\u5316\u5bfb\u5f00\u5fc3\uff0c\u6211\u53ef\u6ca1\u6709\u5de5\u592b\u4e0e\u4f60\u778e \u7f20\uff0c\u4f60\u8ba9\u4e0d\u8ba9\u8def\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u6ca1\u5de5\u592b\uff1f\u54c8\uff0c\u8fde\u8981\u94b1\u4e5f\u6ca1\u6709\u5de5\u592b\uff1f\u90a3\u4f60\u6709\u4ec0\u4e48\u6025\u4e8b\uff1f\u201d\u90a3\u4e5e\u4e10\u6012\u9053\uff1a \u201c\u54b1\u4eec\u7a77\u5316\u5b50\u7684\u4e8b\u60c5\u4e0e\u4f60\u4eec\u6709\u94b1\u7684\u4eba\u5bb6\u4f55\u5e72\uff1f\u54fc\uff0c\u4f60\u4e0d\u8ba9\u8def\uff0c\u6211\u53ef\u8981\u5f97\u7f6a\u4f60\u5927\u7237\u5566\uff01\u201d\u6296\u8d77 \u7af9\u68d2\uff0c\u4e00\u68d2\u6253\u53bb\uff0c\u547c\u547c\u5e26\u98ce\uff0c\u7adf\u4f3c\u9887\u6709\u6b66\u529f\u5e95\u5b50\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u53ef\u6ca1\u89c1\u8fc7\u50cf\u4f60\u8fd9\u6837\u7684\u5316\u5b50\uff0c\u8fde\u8d4f\u94b1\u4e5f\u61d2\u5f97\u8981\u7684\u4e86\uff0c\u6211\u504f\u504f \u8981\u4f60\u56de\u53bb\uff01\u201d\u968f\u624b\u4e00\u62e8\uff0c\u5728\u4ed6\u68d2\u5934\u4e00\u6309\uff0c\u90a3\u4e5e\u4e10\u7ed9\u5979\u7684\u53cd\u529b\u63a8\u5f97\u8e09\u8e09\u8dc4\u8dc4\uff0c\u5012\u9000\u51e0\u6b65\uff0c\u8fd9\u4e00 \u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u6536\u8d77\u7af9\u6756\u559d\u9053\uff1a\u201c\u6211\u4e5f\u672a\u89c1\u8fc7\u4f60\u8fd9\u6837\u5f3a\u8feb\u522b\u4eba\u8981\u94b1\u7684\u4eba\uff0c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u683c\u683c\u4e00\u7b11\uff0c\u5de6\u624b\u6307\u5929\uff0c\u53f3\u624b\u6307\u5730\uff0c\u968f\u5373\u53cc\u624b\u6253\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u6717\u58f0\u5ff5\u9053\uff1a\u201c\u4ee5\u5929\u4e3a \u76d6\u5730\u4e3a\u5e90\uff0c\u4e94\u6e56\u56db\u6d77\u4e3a\u5bb6\u5b85\uff0c\u505a\u60ef\u4e5e\u513f\u60ca\u505a\u5bab\uff0c\u542c\u6211\u7ec6\u5531\u83b2\u82b1\u843d\u3002\u201d\u8fd9\u6b63\u662f\u4e10\u5e2e\u4e2d\u76f8\u4f20\u7684\u9690 \u8bed\uff0c\u4e8e\u627f\u73e0\u4ece\u6bd5\u64ce\u5929\u90a3\u91cc\u542c\u6765\u7684\u3002\u6bd5\u64ce\u5929\u5f53\u65f6\u5c06\u4e10\u5e2e\u4e2d\u7684\u4e00\u4e9b\u6709\u8da3\u4eea\u8282\u8bf4\u7ed9\u5979\u542c\uff0c\u4e0d\u8fc7\u662f\u60f3 \u535a\u5979\u4e00\u7b11\uff0c\u54ea\u77e5\u4eca\u65e5\u7ade\u6d3e\u4e86\u7528\u573a\u3002 \u3000\u3000\u90a3\u4e5e\u4e10\u60ca\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u4e5f\u662f\u672c\u5e2e\u5f1f\u5b50\uff1f\u201d\u773c\u775b\u77aa\u5f97\u53c8\u5706\u53c8\u5927\uff0c\u770b\u5979\u90a3\u8eab\u534e\u7f8e\u7684\u8863\u88f3\u3002\u4e8e \u627f\u73e0\u7b11\u9053\uff1a\u201c\u4f60\u5947\u602a\u6211\u7740\u5f97\u597d\u4e48\uff1f\u54b1\u4eec\u4e10\u5e2e\u4eca\u975e\u6614\u6bd4\uff0c\u54b1\u4eec\u7684\u6bd5\u5927\u9f99\u5934\u4e0d\u65e5\u8fd8\u8981\u7a7f\u4e0a\u9f99\u888d \u5462\u3002\u6211\u5728\u5357\u8fb9\u7684\u65f6\u5019\uff0c\u7a7f\u7684\u8fd8\u662f\u5b98\u670d\u5462\uff0c\u8fd9\u6709\u4ec0\u4e48\u7a00\u5947\uff1f\u201d \u3000\u3000\u90a3\u4e5e\u4e10\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u4ece\u5357\u8fb9\u6765\u7684\uff1f\u6664\uff0c\u54ea\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u8981\u95ee\u6211\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u662f\u6bd5\u5927 \u9f99\u5934\u6d3e\u6765\u6253\u63a2\u6d88\u606f\u7684\uff0c\u6765\u4e86\u4e24\u4e2a\u6708\u4e86\uff0c\u4e0d\u6562\u9732\u51fa\u8eab\u4efd\u3002\u4eca\u65e5\u89c1\u4f60\u66ff\u4e03\u888b\u5f1f\u5b50\u9001\u4fe1\uff0c\u53ea\u6015\u6709\u4ec0 \u4e48\u91cd\u5927\u7684\u4e8b\u60c5\uff0c\u662f\u4ee5\u95ee\u4f60\u4e00\u53e5\u3002\u201d\u90a3\u4e5e\u4e10\u89c1\u4e8e\u627f\u73e0\u8bf4\u5f97\u5934\u5934\u662f\u9053\uff0c\u5fc3\u4e2d\u7684\u6000\u7591\u6d88\u5931\u4e86\u516b\u4e5d\uff0c \u968f\u53e3\u7b54\u9053\uff1a\u201c\u4eca\u665a\u5348\u591c\u79d8\u9b54\u5ca9\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u79d8\u9b54\u5ca9\u505a\u4ec0\u4e48\uff1f\u8fd9\u4f4d\u4e03\u888b\u5f1f\u5b50\u662f\u8c01\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u4e5e\u4e10\u52c3\u7136\u53d8\u8272\uff0c\u6012\u559d\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u5b98\u5e9c\u7684\u722a\u7259\u3002\u201d\u5288\u5934\u4e00\u68d2\u4fbf\u6253\u3002\u539f\u6765\u4e10\u5e2e\u4e2d \u7684\u89c4\u77e9\uff0c\u51e1\u6709\u7ea6\u4f1a\uff0c\u4e0d\u8bb8\u5bfb\u6839\u95ee\u5e95\uff0c\u4e8e\u627f\u73e0\u8fd9\u4e00\u95ee\u4fbf\u9732\u51fa\u4e86\u7834\u7efd\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\u4e86\u3002\u6211\u4e0d\u662f\u5b98\u5e9c\u7684\u722a\u7259\uff0c\u4f46\u4e5f\u4e0d\u5bb9\u4f60\u8dd1\u4e86\u3002\u201d\u90a3\u4e5e\u4e10\u77e5\u9053\u6253\u4e8e\u627f \u73e0\u4e0d\u8fc7\uff0c\u90a3\u5288\u5934\u4e00\u68d2\uff0c\u660e\u662f\u8fdb\u653b\uff0c\u5b9e\u662f\u60f3\u9000\uff0c\u4e8e\u627f\u73e0\u4f55\u7b49\u672c\u9886\uff0c\u987a\u624b\u4e00\u6307\uff0c\u4fbf\u70b9\u4e86\u4ed6\u7684\u7a74 \u9053\uff0c\u5c06\u4ed6\u642c\u5230\u5c71\u811a\u4e00\u4e2a\u5ca9\u6d1e\u91cc\uff0c\u8fd9\u79cd\u70b9\u7a74\u8fc7\u4e86\u5341\u4e8c\u4e2a\u65f6\u8fb0\u53ef\u4ee5\u81ea\u89e3\uff0c\u4e8e\u627f\u73e0\u7ed9\u4ed6\u7559\u4e0b\u5e72\u7cae\uff0c \u8fd8\u7ed9\u4ed6\u7559\u4e0b\u4e00\u952d\u94f6\u5b50\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4eca\u665a\u4f60\u7a74\u9053\u89e3\u540e\uff0c\u8d76\u81f3\u79d8\u9b54\u5b95\u8fd8\u53ef\u4ee5\u89c1\u6211\u3002\u4f60\u5403\u4e86\u70b9 \u4e8f\uff0c\u5f97\u4e00\u952d\u5927\u94f6\uff0c\u4e5f\u603b\u53ef\u4ee5\u8865\u507f\u5f97\u8fc7\u4e86\u3002\u201d \u3000\u3000\u79d8\u9b54\u5ca9\u662f\u897f\u5c71\u4e00\u5904\u9690\u50fb\u7684\u6240\u5728\uff0c\u6709\u4e00\u5757\u5927\u5ca9\u77f3\u7c7b\u5982\u4eba\u50cf\uff0c\u8c8c\u9887\u72f0\u72de\uff0c\u602a\u77f3\u4e0b\u9762\u6709\u4e00\u5ca9 \u6d1e\uff0c\u5e7d\u6df1\u83ab\u6d4b\uff0c\u6545\u6b64\u53f7\u79f0\u79d8\u9b54\u5ca9\u3002\u4e8e\u627f\u73e0\u6280\u9ad8\u80c6\u5927\uff0c\u9ec4\u660f\u4e4b\u540e\uff0c\u4fbf\u6084\u4fcf\u6362\u4e86\u4e00\u8eab\u591c\u884c\u8863\u670d\u5728 \u5348\u591c\u4e4b\u524d\u8d76\u5230\u4e86\u79d8\u9b54\u5ca9\u3002 \u3000\u3000\u7b49\u4e86\u8bb8\u4e45\uff0c\u5140\u662f\u6773\u65e0\u4eba\u8ff9\uff0c\u770b\u770b\u6708\u4eae\u5c06\u5230\u4e2d\u5929\uff0c\u5ffd\u89c1\u5ca9\u77f3\u4e0a\u7684\u4e00\u68f5\u5927\u6811\u6811\u68a2\u4e00\u52a8\uff0c\u968f\u5373 \u9759\u6b62\u3002\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u8f7b\u529f\u672c\u9886\u4e0d\u4fd7\uff0c\u82e5\u7136\u4ed6\u662f\u4e10\u5e2e\u4e2d\u4eba\uff0c\u5e94\u8be5\u5728\u79d8\u9b54\u5ca9\u4e0b\u805a\u4f1a\uff0c\u4e3a \u4f55\u5077\u5077\u85cf\u5728\u6811\u4e0a\uff1f\u201d\u6b63\u60f3\u51fa\u53bb\u5bdf\u770b\uff0c\u5ffd\u542c\u5f97\u4e1c\u8fb9\u201c\u556a\u556a\u201d\u4e24\u4e0b\u7684\u51fb\u638c\u58f0\uff0c\u63a5\u7740\u5357\u8fb9\u5317\u8fb9\u51fb\u638c \u4e4b\u58f0\u56db\u5e94\u3002\u7247\u523b\u4e4b\u540e\uff0c\u4fbf\u6709\u8bb8\u591a\u4e5e\u4e10\u6765\u5230\u4e86\u79d8\u9b54\u5ca9\u4e0b\u3002 \u3000\u3000\u53fd\u53fd\u55b3\u55b3\u7684\u7ec6\u8bed\u58f0\u7eb7\u7eb7\u4f20\u81f3\u8033\u6735\uff0c\u4e8e\u627f\u73e0\u51dd\u795e\u7ec6\u542c\uff0c\u6709\u7fa1\u6155\u7684\u53e3\u543b\uff1a\u201c\u8001\u6bd5\uff0c\u4f60\u5982\u4eca\u53ef \u6296\u5566\uff01\u201d\u6709\u73a9\u7b11\u7684\u53e3\u543b\uff1a\u201c\u505a\u60ef\u4e5e\u513f\u61d2\u505a\u5b98\uff0c\u8001\u6bd5\uff0c\u4f60\u5012\u8bf4\u8bf4\u770b\uff0c\u662f\u505a\u82b1\u5b50\u5feb\u6d3b\u8fd8\u662f\u505a\u5b98\u513f \u5feb\u6d3b\uff1f\u201d\u6709\u62c5\u5fe7\u7684\u53e3\u543b\uff1a\u201c\u8001\u6bd5\uff0c\u662f\u4e0d\u662f\u5357\u8fb9\u7684\u4e8b\u60c5\u6709\u70b9\u4e0d\u5999\uff0c\u5927\u9f99\u5934\u6d3e\u4f60\u6765\u8ba8\u6551\u5175\uff1f\u201d\u968f \u5373\u542c\u5f97\u4e00\u4e2a\u6781\u5176\u719f\u6089\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u54e5\u513f\u4eec\u522b\u95f9\u5566\uff0c\u4eca\u65e5\u8bf7\u5404\u4f4d\u805a\u4f1a\uff0c\u6b63\u662f\u6709\u6781\u5927\u7684\u4e8b\u60c5\u5411 \u5404\u4f4d\u8bf7\u6559\u3002\u201d\u8bf4\u8bdd\u7684\u6b63\u662f\u6bd5\u613f\u7a77\uff0c\u4ed6\u7d20\u6765\u6ed1\u7a3d\uff0c\u6b64\u523b\u542c\u4ed6\u7684\u58f0\u8c03\u5374\u6b8a\u4e3a\u5e84\u91cd\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4e2a\u53ec\u96c6\u7fa4\u4e10\u805a\u4f1a\u7684\u4e10\u5e2e\u4e03\u888b\u5f1f\u5b50\u4e43\u662f\u6bd5\u613f\u7a77\u3002\u4ed6\u662f\u6bd5 \u64ce\u5929\u6700\u4eb2\u4fe1\u7684\u4eba\uff0c\u76ee\u4e0b\u519b\u60c5\u7d27\u6025\uff0c\u6bd5\u64ce\u5929\u4f55\u4ee5\u80af\u653e\u4ed6\u79bb\u5f00\u8eab\u8fb9\uff1f\u201d\u53ea\u542c\u5f97\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u5927\u9f99 \u5934\u5dee\u6211\u8fdb\u4eac\uff0c\u662f\u6d3e\u6211\u529e\u4e00\u6869\u6781\u79d8\u5bc6\u7684\u5dee\u4f7f\uff0c\u9664\u4e86\u5927\u9f99\u5934\u548c\u6211\u4e4b\u5916\uff0c\u4e0d\u80fd\u8ba9\u4e00\u4e2a\u4eba\u77e5\u9053\u3002\u201d\u6b64 \u559c\u4e00\u51fa\uff0c\u7fa4\u4e10\u60ca\u7591\u5df2\u6781\uff0c\u767b\u65f6\u9759\u5bc2\u5982\u6b7b\uff0c\u4e0d\u4e45\u6709\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6bd5\u8001\u5f1f\uff0c\u8fd9\u4e48\u8bf4 \u6765\uff0c\u4f60\u5c31\u4e0d\u8be5\u53ec\u96c6\u8fd9\u4e2a\u805a\u4f1a\u4e86\uff0c\u8fd9\u91cc\u7684\u54e5\u513f\u4eec\u6211\u867d\u7136\u4e2a\u4e2a\u90fd\u76f8\u4fe1\u5f97\u8fc7\uff0c\u4f46\u4e5f\u5f97\u9632\u5907\u6cc4\u4e86\u98ce \u58f0\u3002\u4e0d\u8be5\u542c\u7684\u6211\u4eec\u5c31\u4e0d\u542c\u3002\u201d \u3000\u3000\u6bd5\u613f\u7a77\u82e6\u7b11\u9053\uff1a\u201c\u672c\u5e2e\u7684\u89c4\u77e9\u6211\u5c82\u6709\u4e0d\u77e5\uff1f\u4f46\u8fd9\u4e8b\u60c5\u5173\u7cfb\u592a\u5927\uff0c\u6211\u8001\u6bd5\u62c5\u5f53\u4e0d\u4e86\u8fd9\u4e2a\u5173 \u7cfb\uff0c\u6ca1\u5948\u4f55\u53ea\u5f97\u8bf7\u5404\u4f4d\u5230\u6765\u4e00\u540c\u5546\u91cf\u3002\u201d\u90a3\u82cd\u8001\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u82e5\u662f\u5173\u7cfb\u5230\u672c\u5e2e\u5b58\u4ea1\u7684 \u5927\u4e8b\uff0c\u5927\u9f99\u5934\u6709\u4ec0\u4e48\u884c\u5dee\u8e0f\u9519\uff0c\u4f60\u4fbf\u53ef\u8bf4\u3002\u201d \u3000\u3000\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u8fd9\u6bd4\u672c\u5e2e\u7684\u5b58\u4ea1\uff0c\u8fd8\u8981\u4e25\u91cd\u5f97\u591a\uff01\u201d\u7fa4\u4e10\u8d8a\u53d1\u60ca\u9a87\uff0c\u5bc2\u9759\u65e0\u58f0\uff0c\u90fd\u770b\u7740\u6bd5 \u613f\u7a77\u3002\u53ea\u542c\u5f97\u6bd5\u613f\u7a77\u53f9\u4e86\u53e3\u6c14\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u7684\u6bd5\u5927\u9f99\u5934\u81ea\u5230\u5357\u8fb9\u4e4b\u540e\uff0c\u5e72\u4e0b\u4e86\u8f70\u8f70\u70c8 \u70c8\u4e4b\u4e8b\uff0c\u8fd9\u672c\u6765\u662f\u4e10\u5e2e\u81ea\u53e4\u4ee5\u6765\uff0c\u4ece\u6240\u672a\u6709\u7684\u76db\u4e8b\u3002\u201d\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u5927\u9f99\u5934\u505a\u4e86\u7687 \u5e1d\uff0c\u82b1\u5b50\u4eec\u5e73\u5730\u767b\u5929\u3002\u201d\u201c\u6731\u5143\u748b\u867d\u7136\u4e5f\u662f\u4e5e\u513f\u51fa\u8eab\uff0c\u4f46\u4ed6\u5e76\u672a\u5165\u5e2e\u3002\u54b1\u4eec\u7684\u5927\u9f99\u5934\u624d\u662f\u7b2c \u4e00\u4e2a\u4e3a\u4e10\u5e2e\u4e89\u6765\u5929\u4e0b\u7684\u4eba\u3002\u201d \u3000\u3000\u6bd5\u613f\u7a77\u53c8\u53f9\u4e86\u53e3\u6c14\u3001\u8bf4\u9000\uff1a\u201c\u53ef\u60dc\u8fd9\u5929\u4e0b\u53ef\u4e0d\u5bb9\u6613\u6253\u5440\u3002\u5927\u9f99\u5934\u4e0e\u53f6\u5b97\u7559\u95f9\u7ffb\u4e86\uff0c\u72ec\u6728 \u96be\u652f\u5927\u53a6\u3002\u201d\u6709\u4e9b\u5df2\u77e5\u9053\u8fd9\u4e2a\u4e8b\u60c5\uff0c\u6709\u4e9b\u8fd8\u672a\u77e5\u9053\uff0c\u7eb7\u7eb7\u8be2\u95ee\uff0c\u6bd5\u613f\u7a77\u7ea6\u7565\u8bf4\u4e86\u4e00\u904d\uff0c\u767b\u65f6 \u8bae\u8bba\u7eb7\u7eb7\uff0c\u6709\u4eba\u8bf4\u6bd5\u64ce\u5929\u505a\u5f97\u5bf9\uff0c\u8ba4\u4e3a\u6bd5\u64ce\u5929\u96c4\u624d\u5927\u7565\uff0c\u65e2\u7136\u53f6\u5b97\u7559\u4e0e\u4ed6\u610f\u89c1\u4e0d\u5408\uff0c\u4e3a\u4e86\u4e8b \u6743\u4e13\u4e00\uff0c\u6392\u65a5\u4e86\u53f6\u5b97\u7559\u6b63\u53ef\u653e\u624b\u53bb\u5e72\uff1b\u6709\u4eba\u5219\u8ba4\u4e3a\u6bd5\u64ce\u5929\u5927\u5927\u4e0d\u8be5\uff0c\u5927\u654c\u5f53\u524d\uff0c\u5c82\u53ef\u6392\u65a5\u5f02 \u5df1\uff1f \u3000\u3000\u90a3\u82cd\u8001\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u54b1\u4eec\u6682\u4e14\u4e0d\u8c08\uff0c\u5bf9\u4e0d\u5bf9\u90fd\u5df2\u505a\u4e86\u3002\u8fd9\u4e8b\u60c5\u8fd8\u672a\u5173\u7cfb\u5230\u672c\u5e2e \u7684\u5b58\u4ea1\u3002\u201d\u6709\u4eba\u63a5\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u4f60\u5feb\u8bf4\u5927\u9f99\u5934\u5230\u5e95\u6d3e\u4f60\u529e\u4ec0\u4e48\u4e8b\u60c5\uff0c\u8981\u903c\u5f97\u4f60\u4e0d\u987e\u672c\u5e2e \u7684\u89c4\u77e9\uff0c\u8981\u5c06\u4e8b\u60c5\u516c\u4e4b\u4e8e\u4f17\uff1f\u201d \u3000\u3000\u6bd5\u613f\u7a77\u6b47\u4e86\u534a\u54cd\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u73b0\u4e0b\u5b98\u519b\u5206\u4e09\u8def\u56f4\u653b\uff0c\u4e2d\u8def\u7684\u6d59\u6c5f\u5de1\u629a\u5f20\u9aa5\u5148\u950b\u5df2\u8fc7\u4e86 \u6e29\u5dde\uff0c\u9f99\u5934\u672c\u90e8\u4e5f\u5df2\u5728\u5b98\u519b\u56f4\u56f0\u4e4b\u4e2d\u4e86\u3002\u4e1c\u8def\u7684\u53f6\u6210\u6797\u88ab\u5207\u65ad\u4e86\uff0c\u81ea\u987e\u4e0d\u6687\uff0c\u66f4\u96be\u56de\u6551\u3002\u201d \u3000\u3000\u90a3\u82cd\u8001\u7684\u58f0\u97f3\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u7b97\u5f97\u4ec0\u4e48\uff1f\u54b1\u4eec\u7684\u6bd5\u5927\u9f99\u5934\u9ad8\u4e3e\u4e49\u65d7\uff0c\u5e72\u4e0b\u4e86\u8fd9\u7b49\u8f70\u8f70\u70c8 \u70c8\u4e4b\u4e8b\uff0c\u6210\u4e5f\u82f1\u96c4\uff0c\u8d25\u4e5f\u8c6a\u6770\uff01\u66f4\u4f55\u51b5\u6210\u8d25\u8fd8\u5728\u672a\u53ef\u77e5\u4e4b\u6570\uff0c\u8001\u5f1f\u4f55\u7528\u6c14\u9981\uff1f\u201d\u7fa4\u4e10\u7eb7\u7eb7\u4e00 \u8bf4\u9053\uff1a\u201c\u662f\u554a\uff01\u54b1\u4eec\u90fd\u613f\u5357\u4e0b\u6295\u519b\uff0c\u4e0e\u6bd5\u5927\u9f99\u5934\u798f\u7978\u4e0e\u5171\uff0c\u5e72\u4e0b\u4e86\u8fd9\u7b49\u8f70\u8f70\u70c8\u70c8\u4e4b\u4e8b\uff0c\u6b7b\u4e86 \u4e5f\u662f\u7518\u5fc3\uff01\u201d \u3000\u3000\u6bd5\u613f\u7a77\u53f9\u9053\uff1a\u201c\u53ef\u60dc\u5927\u9f99\u5934\u542c\u4e0d\u5230\u4f60\u4eec\u7684\u8bf4\u8bdd\uff0c\u8fdc\u6c34\u53c8\u4e0d\u80fd\u6551\u8fd1\u706b\u3002\u90a3\u5f20\u9aa5\u5df2\u6d3e\u9063\u5bc6\u4f7f \u5230\u56f4\u57ce\u4e4b\u4e2d\u5411\u5927\u9f99\u5934\u62db\u964d\uff01\u201d\u90a3\u82cd\u8001\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u62db\u964d\uff1f\u201d\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u62db \u964d\uff01\u5f20\u9aa5\u7b54\u5e94\u4fdd\u4e3e\u4ed6\u505a\u4e00\u4e2a\u603b\u5175\u3002\u201d\u90a3\u82cd\u8001\u7684\u58f0\u97f3\u95ee\u9053\uff1a\u201c\u6bd5\u64ce\u5929\u600e\u4e48\u6837\uff1f\u201d\u6bd5\u613f\u7a77\u9053\uff1a \u201c\u54b1\u4eec\u7684\u5927\u9f99\u5934\u8fd8\u6ca1\u6709\u7b54\u5e94\u3002\u201d\u7fa4\u4e10\u6b22\u547c\u9053\uff1a\u201c\u54b1\u4eec\u7684\u5927\u9f99\u5934\u53ef\u4e0d\u662f\u6ca1\u6709\u9aa8\u5934\u7684\u4eba\uff0c\u4e00\u4e2a\u603b \u5175\u5c82\u80fd\u53eb\u54b1\u4eec\u7684\u5927\u9f99\u5934\u4e0a\u94a9\u3002\u201d \u3000\u3000\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e00\u4e2a\u603b\u5175\u7684\u5b98\u8854\u81ea\u662f\u4e0d\u653e\u5728\u54b1\u4eec\u5927\u9f99\u5934\u7684\u5fc3\u4e0a\uff01\u662f\u4ee5\u4ed6\u4fee\u4e0b\u5bc6\u51fd\uff0c \u6d3e\u9063\u6211\u5230\u4eac\u57ce\uff0c\u8d70\u9633\u5b97\u6d77\u7684\u95e8\u8def\uff0c\u8bf7\u4ed6\u4ee3\u4e3a\u7980\u544a\u5f53\u4eca\u7684\u7687\u5e1d\u8001\u513f\uff0c\u8981\u6295\u964d\u4e5f\u5f97\u7687\u4e0a\u4eb2\u81ea\u62db \u964d\uff0c\u4ed6\u6700\u5c11\u8981\u505a\u4e00\u7701\u7684\u7763\u629a\uff01\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u4e00\u8bf4\uff0c\u767b\u65f6\u9759\u5f97\u8fde\u4e00\u6839\u9488\u8dcc\u843d\u5730\u4e0b\u90fd\u542c\u5f97\u89c1\u54cd\uff0c\u5c31\u50cf\u98ce\u66b4\u524d\u5915\u4e00\u6837\uff0c\u4eba\u4eba\u90fd\u95f7\u5f97 \u900f\u4e0d\u8fc7\u6c14\u6765\u3002\u53ea\u542c\u5f97\u6bd5\u613f\u7a77\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u53f6\u6210\u6797\u90a3\u652f\u519b\u5728\u5c6f\u6eaa\u6253\u4e86\u4e24\u6b21\u80dc\u4ed7\uff0c\u56e0\u6b64\u5b98\u519b\u52a0\u7d27 \u5411\u4ed6\u8fdb\u653b\uff0c\u6e29\u5dde\u867d\u7136\u88ab\u56f4\uff0c\u5374\u8fd8\u6ca1\u6709\u90a3\u4e48\u5403\u7d27\u3002\u6545\u6b64\u5927\u9f99\u5934\u6d3e\u6211\u51fa\u6765\u3002\u7167\u5927\u9f99\u5934\u7684\u770b\u6cd5\u662f\u8fd9 \u573a\u6218\u4e8b\u5df2\u4e8b\u65e0\u53ef\u4e3a\uff0c\u4e0e\u5176\u88ab\u5b98\u519b\u5c3d\u6570\u6d88\u706d\uff0c\u4e0d\u5982\u6682\u4e14\u56fe\u5b58\u3002\u201d\u90a3\u82cd\u8001\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4ed6\u771f\u662f \u8fd9\u4e2a\u610f\u601d\uff1f\u201d\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u5c31\u6015\u4ed6\u4e0d\u662f\u771f\u610f\u3002\u6211\u662f\u4ed6\u7684\u5802\u4f84\uff0c\u7d20\u6765\u5f97\u4ed6\u4fe1\u4efb\uff0c\u4ed6\u6d3e\u6211\u505a\u4ed6\u7684 \u5bc6\u4f7f\uff0c\u8981\u901a\u8fc7\u9633\u5b97\u6d77\u7684\u95e8\u8def\u4e0e\u7687\u4e0a\u9762\u8c08\uff0c\u5176\u4e2d\u7684\u6761\u6b3e\u4fbf\u5305\u62ec\u4e86\u4e49\u519b\u5c3d\u6570\u7531\u671d\u5ef7\u6536\u7f16\uff0c\u540c\u65f6\u8fd8 \u7b54\u5e94\u66ff\u671d\u5ef7\u89e3\u51b3\u53f6\u6210\u6797\u8fd9\u652f\u90e8\u961f\uff0c\u4f5c\u4e3a\u7acb\u529f\u8d4e\u7f6a\u3002\u201d\u767b\u65f6\u8f70\u53eb\u4e4b\u58f0\u56db\u8d77\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u54b1\u4eec \u4e10\u5e2e\u4eca\u540e\u8fd8\u6709\u4ec0\u4e48\u9762\u76ee\u89c1\u4eba\uff1f\u201d\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u662f\u5440\uff01\u5927\u9f99\u5934\u7684\u610f\u601d\u867d\u8bf4\u662f\u53d7\u4e86\u62db\u5b89\u4e4b\u540e\uff0c\u54b1 \u4eec\u4e10\u5e2e\u4e2d\u6709\u5934\u9762\u7684\u4eba\u7269\uff0c\u4eba\u4eba\u90fd\u6709\u5b98\u505a\u3002\u4f46\u8fd9\u7b49\u5b98\u513f\uff0c\u505a\u4e86\u4e5f\u5bf9\u4e0d\u8d77\u672c\u5e2e\u7684\u5217\u7956\u5217\u5b97\u3002\u8fd9\u4e8b \u60c5\u6211\u5b9e\u5728\u62c5\u5f53\u4e0d\u4e86\uff0c\u662f\u4ee5\u8ff8\u4eac\u4e4b\u540e\uff0c\u5230\u4eca\u5929\u5df2\u6709\u4e09\u5929\uff0c\u6211\u518d\u4e09\u8e0c\u8e87\uff0c\u7ec8\u662f\u4e0d\u6562\u6309\u7167\u5927\u9f99\u5934\u7684 \u547d\u4ee4\u884c\u4e8b\u3002\u8981\u8bf7\u5404\u4f4d\u8001\u54e5\u6307\u6559\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u6697\u4e2d\u5077\u542c\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u60ca\u8005\u662f\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u6bd5\u64ce\u5929\u4f1a\u53d7\u671d\u5ef7\u7684\u62db\u5b89\uff0c\u800c\u4e14\u5b89\u6392\u4e0b \u6bd2\u8ba1\uff0c\u8981\u9677\u53f6\u6210\u6797\u4e8e\u7edd\u5883\uff01\u559c\u8005\u662f\u6bd5\u613f\u7a77\u662f\u6bd5\u64ce\u5929\u6700\u4eb2\u4fe1\u7684\u4eba\uff0c\u5c45\u7136\u4e5f\u80fd\u8fa8\u522b\u662f\u975e\uff0c\u5c06\u6bd5\u64ce \u5929\u7684\u9634\u8c0b\u90fd\u6296\u9732\u51fa\u6765\u3002 \u3000\u3000\u90a3\u8001\u8005\u62cd\u4e86\u4e09\u4e0b\u624b\u638c\uff0c\u5c06\u55a7\u95f9\u4e4b\u58f0\u538b\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u786e\u5b9e\u6bd4\u672c\u5e2e\u5b58\u4ea1\u8fd8\u66f4\u4e25\u91cd\uff0c \u54b1\u4eec\u4ece\u957f\u8ba1\u8f83\u3002\u597d\uff0c\u6d3e\u4eba\u5230\u56db\u4e0b\u628a\u98ce\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u89c1\u5ca9\u4e0a\u6811\u68a2\u98ce\u52a8\uff0c\u90a3\u8001\u8005\u84e6\u7136\u559d\u9053\uff1a \u201c\u4ec0\u4e48\u4eba\u5728\u6b64\u5077\u542c\uff1f\u201d\u4e8e\u627f\u73e0\u5413\u4e86\u4e00\u8df3\uff0c\u4ee5\u4e3a\u81ea\u5df1\u5df2\u88ab\u53d1\u73b0\uff0c\u5b9a\u775b\u4e00\u770b\uff0c\u5374\u89c1\u4e00\u6761\u9ed1\u5f71\u4ece\u6811 \u4e0a\u8df3\u4e0b\u5ca9\u6765\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u770b\u6e05\u695a\u4e86\uff0c\u8fd9\u4e00\u559c\u975e\u540c\u5c0f\u53ef\uff0c\u4ece\u6811\u4e0a\u8dc3\u4e0b\u7684\u90a3\u4e2a\u5c0f\u4f19\u5b50\u8e66\u8e66\u8df3\u8df3\u7684\uff0c\u970e\u773c\u95f4\u5c31\u5230 \u4e86\u7fa4\u4e10\u805a\u4f1a\u7684\u5730\u70b9\uff0c\u8fd9\u4e0d\u662f\u5c0f\u864e\u5b50\u662f\u8c01\uff1f\u4e8e\u627f\u73e0\u672c\u6b32\u51fa\u58f0\u76f8\u5524\uff0c\u8f6c\u5ff5\u4e00\u60f3\uff0c\u4e14\u770b\u4ed6\u5230\u8fd9\u91cc\u505a \u4ec0\u4e48\uff1f\u4ecd\u7136\u85cf\u5728\u5ca9\u77f3\u540e\u9762\uff0c\u4e0d\u52a8\u58f0\u8272\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u5df2\u662f\u5341\u516d\u5c81\u7684\u5c11\u5e74\u4e86\uff0c\u4f46\u7a1a\u6c14\u672a\u6d88\uff0c\u4ecd\u662f\u5f80\u65e5\u90a3\u526f\u987d\u76ae\u6a21\u6837\uff0c\u8e66\u8e66\u8df3\u8df3\u5730\u8dd1\u6765\uff0c \u4e00\u9762\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4eec\u5403\u56db\u65b9\uff0c\u5c0f\u7237\u53ef\u8981\u5403\u4e94\u65b9\uff0c\u4f60\u70e4\u90a3\u53ea\u53eb\u5316\u9e21\u8bf7\u4e0d\u8bf7\u6211\u3002\u201d\u7fa4\u4e10\u5982\u4e34\u5927 \u654c\uff0c\u5ffd\u89c1\u6765\u7684\u662f\u4e00\u4e2a\u4e73\u81ed\u672a\u5e72\u7684\u5c11\u5e74\uff0c\u90fd\u6014\u7740\u4e86\u3002\u53ea\u6709\u90a3\u8001\u4e10\u770b\u51fa\u5c0f\u864e\u5b50\u8eab\u624b\u4e0d\u51e1\uff0c\u5fc3\u4e2d\u4e00 \u51db\uff0c\u75be\u8dc3\u800c\u524d\uff0c\u4f38\u624b\u4e00\u6293\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u5c0f\u864e\u5b50\u6c89\u80a9\u7f29\u80cc\uff0c\u811a\u6b65\u4e00\u8f6c\uff0c\u7adf\u628a\u90a3\u8001\u4e5e\u4e10\u7684\u5927\u64d2\u62ff\u624b\u6cd5\u5316\u89e3\u4e8e\u65e0\u5f62\uff0c\u8fd9\u4e00\u4e0b\u5168\u573a\u8038 \u52a8\uff0c\u7eb7\u7eb7\u559d\u95ee\uff1a\u201c\u597d\u5927\u80c6\u7684\u5c0f\u5978\u7ec6\uff0c\u8c01\u6d3e\u9063\u4f60\u6765\u7684\uff1f\u201d\u5c0f\u864e\u5b50\u54c8\u54c8\u4e00\u7b11\uff0c\u9762\u5bf9\u90a3\u8001\u4e5e\u4e10\u9053\uff1a \u201c\u4f60\u4e0d\u8ba4\u5f97\u6211\uff0c\u6211\u53ef\u8ba4\u5f97\u4f60\u3002\u90d1\u957f\u8001\uff0c\u6211\u5e08\u7236\u53eb\u6211\u5411\u4f60\u95ee\u597d\u3002\u201d\u8fd9\u8001\u4e5e\u4e10\u6b63\u662f\u7ba1\u9886\u5317\u4eac\u4e5e\u4e10 \u7684\u957f\u8001\uff0c\u5728\u4e10\u5e2e\u4e2d\u7684\u5730\u4f4d\u6bd4\u6bd5\u613f\u7a77\u8fd8\u9ad8\u4e00\u7ea7\uff0c\u662f\u4e00\u4e2a\u516b\u888b\u5f1f\u5b50\u3002 \u3000\u3000\u90d1\u957f\u8001\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\u81ea\u5df1\u719f\u6089\u7684\u4e5d\u6d41\u4e09\u6559\u4eba\u7269\u4e2d\uff0c\u53ef\u6ca1\u6709\u8c01\u6709\u8fd9\u6837\u673a\u4f36\u7684\u5f92\u5f1f\uff0c\u6a2a\u638c \u62a4\u80f8\uff0c\u4e1d\u6beb\u4e0d\u6562\u5927\u610f\uff0c\u5de1\u89c6\u7740\u5c0f\u864e\u5b50\u559d\u9053\uff1a\u201c\u4f60\u5e08\u7236\u662f\u8c01\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u82cf\u5dde\u5f20\u4e39\u67ab\u3002\u201d \u90d1\u957f\u8001\u201c\u554a\u5440\u201d\u4e00\u58f0\u53eb\u8d77\u6765\u9053\uff1a\u201c\u539f\u6765\u662f\u5f20\u5927\u4fa0\uff01\u4ed6\u51e0\u65f6\u6765\u7684\uff1f\u5c0f\u8001\u513f\u8033\u76ee\u4e0d\u5468\uff0c\u4e0d\u77e5\u5f20\u5927 \u4fa0\u8fdb\u4eac\uff0c\u6ca1\u6709\u524d\u5f80\u8bf7\u5b89\uff0c\u5012\u52b3\u70e6\u4e86\u5c0f\u54e5\u513f\u6765\u4e86\uff0c\u6055\u7f6a\u6055\u7f6a\u3002\u201d\u5c0f\u864e\u5b50\u5657\u55e4\u7b11\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6 \u4e0d\u7528\u5ba2\u5957\uff0c\u8bf4\u5b9e\u5728\u8bdd\uff0c\u6211\u5e08\u7236\u53eb\u6211\u6765\u5077\u542c\u4f60\u4eec\u805a\u4f1a\u5230\u5e95\u662f\u505a\u4ec0\u4e48\u7684\uff1f\u4ed6\u8fd8\u53eb\u6211\u5c0f\u5fc3\uff0c\u4e0d\u8981\u88ab \u4f60\u4eec\u62ff\u7740\u4e86\u5f53\u5c0f\u8d3c\u529e\u5462\uff01\u54c8\u54c8\uff0c\u4f60\u521a\u624d\u90a3\u8bb0\u64d2\u62ff\u624b\u51e0\u4e4e\u6293\u4f4f\u4e86\u6211\u7684\u7435\u7436\u9aa8\u5462\uff01\u5582\uff01\u5582\uff01\u8fd9\u53ea \u53eb\u5316\u9e21\u4f60\u5230\u5e95\u662f\u8bf7\u4e0d\u8bf7\u6211\uff1f\u201d\u90d1\u957f\u8001\u6b63\u4e3a\u7740\u6bd5\u64ce\u5929\u53d7\u62db\u964d\u8fd9\u4ef6\u610f\u5916\u7684\u5927\u4e8b\u6240\u56f0\u6270\uff0c\u4e00\u542c\u5f20\u4e39 \u67ab\u5728\u4eac\uff0c\u5f53\u771f\u662f\u559c\u51fa\u671b\u5916\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u8db3\u667a\u591a\u8c0b\uff0c\u5929\u4e0b\u95fb\u540d\uff0c\u6211\u4f55\u4e0d\u5411\u4ed6\u8bf7\u6559\uff1f\u201d \u5fd9\u9053\uff1a\u201c\u8bf7\uff0c\u8bf7\uff01\u5f20\u5927\u4fa0\u4e0b\u69bb\u4f55\u5904\uff0c\u8fd8\u671b\u5c0f\u54e5\u5f15\u89c1\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u6211\u5e08\u7236\u5fd9\u7740\u54e9\uff0c\u8fd9\u4e2a\u4e14 \u6162\u3002\u5582\uff0c\u5582\uff0c\u9664\u4e86\u6211\u4e4b\u5916\uff0c\u8fd9\u6797\u5b50\u91cc\u8fd8\u6709\u65c1\u4eba\uff0c\u4f60\u8bf7\u4e0d\u8bf7\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u5c0f\u5bb6\u4f19\u770b\u5230\u6211\u3002\u201d\u6b63\u60f3\u8df3\u51fa\uff0c\u90a3\u8001\u4e5e\u4e10\u8bf4\u9053\uff1a\u201c\u5c0f\u54e5\u4e0e\u8c01\u540c\u6765\uff0c \u5f53\u7136\u662f\u4e00\u5e76\u8bf7\u4e86\uff01\u201d\u5c0f\u864e\u5b50\u7b11\u9053\uff1a\u201c\u8fd9\u4eba\u53ef\u4e0d\u662f\u548c\u6211\u540c\u6765\u7684\uff0c\u6211\u770b\u4ed6\u8eab\u5f62\u9ad8\u5927\uff0c\u4e5f\u8bb8\u662f\u4e2a\u6d77 \u6d0b\u5927\u76d7\uff0c\u4e0d\u50cf\u662f\u4e2a\u5c0f\u5077\u5462\uff01\u201d\u90d1\u957f\u8001\u5403\u4e86\u4e00\u60ca\uff0c\u5411\u56db\u65b9\u4e00\u63d6\uff0c\u53eb\u9053\uff1a\u201c\u54ea\u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff0c\u8bf7 \u51fa\u6765\u76f8\u89c1\u3002\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u5ca9\u77f3\u540e\u9762\u4e00\u9635\u6d2a\u4eae\u7684\u7b11\u58f0\uff0c\u4e00\u4e2a\u9ad8\u5927\u7684\u6c49\u5b50\u8d70\u4e86\u51fa\u6765\uff0c\u6717\u58f0\u8bf4\u9053\uff1a \u201c\u5927\u6c34\u51b2\u5230\u9f99\u738b\u5e99\uff0c\u90fd\u662f\u81ea\u5bb6\u4eba\uff01\u201d\u6bd5\u613f\u7a77\u60ca\u53eb\u9053\uff1a\u201c\u987e\u5b5f\u7ae0\u5927\u54e5\uff0c\u4f60\u4e5f\u6765\u4e86\uff01\u201d\u5fc3\u60f3\u8fd9\u987e \u5b5f\u7ae0\u4e43\u662f\u6bd5\u64ce\u5929\u7684\u5fc3\u8179\uff0c\u5f97\u6bd5\u64ce\u5929\u7684\u4fe1\u4efb\uff0c\u4e0d\u4e9a\u4e8e\u81ea\u5df1\uff0c\u4f55\u4ee5\u6bd5\u64ce\u5929\u6d3e\u4e86\u81ea\u5df1\u5374\u53c8\u6d3e\u4ed6 \u6765\uff1f\u201d \u3000\u3000\u987e\u5b5f\u7ae0\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6bd5\u8001\u5f1f\u4f60\u4eec\u7684\u8bf4\u8bdd\u6211\u90fd\u542c\u89c1\u5566\uff0c\u6bd5\u8001\u5f1f\u4f60\u597d\u89c1\u8bc6\uff0c\u597d\u9b44\u529b\uff0c\u4ffa\u8001\u987e \u597d\u751f\u4f69\u670d\uff01\u201d\u6bd5\u613f\u7a77\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4e5f\u662f\u4e0e\u6211\u5fd7\u540c\u9053\u5408\u4e4b\u4eba\u3002\u201d\u4f38\u624b\u4e0e\u4ed6\u76f8\u63e1\uff0c \u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u505a\u5f97\u5bf9\u662f\u4e0d\u5bf9\uff0c\u8fd8\u671b\u8001\u5144\u6307\u6559\uff01\u201d\u201c\u6307\u6559\u201d\u4e24\u5b57\u521a\u521a\u51fa\u53e3\uff0c\u7a81\u7136\u95f4\u987e\u5b5f\u7ae0\u5927\u559d \u4e00\u58f0\uff0c\u53cd\u624b\u4e00\u626d\uff0c\u5c06\u6bd5\u613f\u7a77\u7684\u624b\u81c2\u626d\u5f97\u5f2f\u5230\u80cc\u540e\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4e8f\u4f60\u662f\u5927\u9f99\u5934\u7684\u4f84\u5b50\uff0c\u5c45\u7136 \u6562\u80cc\u53db\u4ed6\uff01\u201d\u8fd9\u4e00\u626d\u7528\u4e0a\u4e86\u9e70\u722a\u529b\u7684\u529f\u5929\uff0c\u6263\u7740\u4e86\u6bd5\u613f\u7a77\u7684\u5bf8\u5173\u5c3a\u8109\u95e8\u8981\u5bb3\uff0c\u6bd5\u613f\u7a77\u5168\u8eab\u9ebb \u8f6f\uff0c\u767b\u65f6\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u53d8\u51fa\u610f\u5916\uff0c\u7fa4\u4e10\u5168\u90fd\u60ca\u4f4f\uff0c\u90d1\u957f\u8001\u5927\u543c\u4e00\u58f0\uff0c\u63c9\u8eab\u6251\u4e0a\uff0c\u987e\u5b5f\u7ae0\u5927\u7b11\u9053\uff1a\u201c\u4f60\u518d \u4e0a\u4e00\u6b65\uff0c\u6211\u5c31\u628a\u4ed6\u5e9f\u4e86\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5ffd\u89c1\u91d1\u5149\u4e00\u95ea\uff0c\u987e\u5b5f\u7ae0\u5927\u53eb\u4e00\u58f0\uff0c\u53cc\u624b\u4e00\u677e\uff0c\u8dc4\u8dc4\u8e09 \u8e09\u5730\u5012\u9000\u4e09\u6b65\uff0c\u4e8e\u627f\u73e0\u98de\u51fa\uff0c\u4e00\u6735\u91d1\u82b1\u6253\u4e2d\u4e86\u4ed6\u7684\u624b\u8155\u7a74\u9053\uff0c\u7acb\u523b\u8df3\u4e86\u51fa\u6765\u3002 \u3000\u3000\u987e\u5b5f\u7ae0\u662f\u6bd5\u64ce\u5929\u5e10\u4e0b\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u867d\u7136\u51fa\u5176\u4e0d\u610f\u5730\u88ab\u91d1\u82b1\u6253\u4e2d\uff0c\u903c\u5f97\u653e\u5f00\u4e86\u6bd5\u613f\u7a77\uff0c\u4f46 \u5374\u5e76\u672a\u53d7\u4f24\uff0c\u8eab\u5f62\u4e00\u7a33\uff0c\u7acb\u523b\u89e3\u4e0b\u4e86\u866c\u9f99\u97ad\uff0c\u9634\u607b\u607b\u5730\u7b11\u9053\uff1a\u201c\u539f\u6765\u90fd\u5728\u8fd9\u91cc\uff0c\u54c8\u54c8\uff0c\u6559\u4f60 \u4eec\u4e00\u7f51\u6210\u64d2\uff0c\u7701\u5f97\u6211\u518d\u8d39\u529b\uff01\u201d\u866c\u9f99\u97ad\u626c\u7a7a\u4e00\u6298\uff0c\u5530\u5530\u4e24\u97ad\uff0c\u50fb\u556a\u4e24\u58f0\u54cd\u8fc7\uff0c\u8302\u6797\u4e1b\u8349\u4e4b \u95f4\uff0c\u7a81\u7136\u8df3\u51fa\u4e86\u5341\u591a\u540d\u9ed1\u8863\u6c49\u5b50\uff0c\u540c\u65f6\u79d8\u9b54\u5ca9\u4e0b\u7684\u5ca9\u6d1e\u4e2d\u4e5f\u5506\u5506\u5730\u5c04\u51fa\u4e86\u4e00\u6392\u51b7\u7bad\uff0c\u767b\u65f6\u6709 \u51e0\u4e2a\u4e5e\u4e10\u4e2d\u7bad\u5012\u5730\uff0c\u4e00\u4e2a\u9ed1\u8863\u6c49\u5b50\u821e\u5200\u76f4\u6251\u90d1\u957f\u8001\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u5fa1\u6797\u519b\u526f\u7edf\u9888\u4e1c\u65b9\u6d1b\u5728 \u6b64\uff0c\u53db\u56fd\u9006\u8d3c\uff0c\u8fd8\u4e0d\u675f\u624b\u5c31\u7f1a\uff0c\u8981\u5f85\u8001\u7237\u52a8\u624b\u4e48\uff1f\u201d\u90d1\u957f\u8001\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u6296\u8d77\u6746\u4ff8\uff0c\u683c\u5f00 \u4e86\u4ed6\u7684\u8fce\u9762\u4e09\u5200\uff0c\u767b\u65f6\u4e24\u65b9\u6df7\u6218\uff01 \u3000\u3000\u539f\u6765\u6bd5\u64ce\u5929\u5916\u8c8c\u7c97\u8c6a\uff0c\u5b9e\u662f\u5de5\u4e8e\u5fc3\u8ba1\uff0c\u6bd5\u613f\u7a77\u867d\u662f\u4ed6\u7684\u5802\u4f84\uff0c\u8fd9\u7b49\u5927\u4e8b\uff0c\u4ed6\u4ea6\u81ea\u653e\u5fc3\u4e0d \u8fc7\u3002\u56e0\u6b64\u53c8\u6d3e\u4e86\u987e\u5b5f\u7ae0\u524d\u6765\uff0c\u6697\u4e2d\u76d1\u89c6\u3002\u5fc3\u60f3\u7eb5\u662f\u6709\u4e00\u4e2a\u4eba\u80cc\u53db\u4e8e\u4ed6\uff0c\u4ed6\u6c42\u964d\u7684\u8ba1\u5212\u4e5f\u603b\u80fd \u4e0a\u8fbe\u671d\u5ef7\uff0c\u4e0d\u81f4\u8bef\u4e86\u5927\u4e8b\u3002\u987e\u5b5f\u7ae0\u672c\u6765\u662f\u5c71\u4e1c\u5927\u76d7\uff0c\u552f\u5229\u662f\u56fe\uff0c\u505a\u4e49\u519b\u7684\u5b98\u548c\u505a\u671d\u5ef7\u7684\u5b98\u90fd \u662f\u4e00\u6837\uff0c\u679c\u7136\u6b7b\u5fc3\u584c\u5730\u4e3a\u6bd5\u64ce\u5929\u6240\u7528\uff0c\u63a2\u51fa\u4e86\u7fa4\u4e10\u805a\u4f1a\u7684\u6d88\u606f\u540e\uff0c\u7acb\u523b\u901a\u77e5\u4e86\u9633\u5b97\u6d77\uff0c\u9633\u5b97 \u6d77\u6d3e\u9063\u4e86\u4ed6\u7684\u526f\u624b\u4e1c\u65b9\u6d1b\u51fa\u9a6c\uff0c\u540c\u6765\u7684\u8fd8\u6709\u5341\u6570\u540d\u9526\u8863\u536b\u7684\u6307\u6325\u548c\u5341\u6570\u540d\u5fa1\u6797\u519b\u7684\u9ad8\u624b\u6b66 \u58eb\u3002 \u3000\u3000\u987e\u5b5f\u7ae0\u52c7\u731b\u975e\u5e38\uff0c\u866c\u9f99\u97ad\u8fde\u73af\u75be\u626b\uff0c\u6253\u7ffb\u4e86\u51e0\u4e2a\u4e10\u5e2e\u5f1f\u5b50\uff0c\u62a2\u4e0a\u524d\u53bb\u6349\u62ff\u6bd5\u613f\u7a77\uff0c\u5c0f\u864e \u5b50\u8eab\u5f62\u6e9c\u6ed1\uff0c\u6e38\u9c7c\u822c\u94bb\u4e86\u8fc7\u53bb\uff0c\u65a5\u9053\uff1a\u201c\u6789\u4f60\u751f\u5f97\u725b\u9ad8\u9a6c\u5927\uff0c\u5374\u662f\u4e0d\u77e5\u5ec9\u803b\uff01\u201d\u987e\u5b5f\u7ae0\u9053\uff1a \u201c\u600e\u4e48\u4e0d\u77e5\u5ec9\u803b\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u5403\u91cc\u6252\u5916\uff0c\u5356\u53cb\u6c42\u8363\uff0c\u6709\u4f55\u5ec9\u803b\uff01\u201d\u987e\u5b5f\u7ae0\u89c1\u4ed6\u4e73\u81ed\u672a \u5e72\uff0c\u5c45\u7136\u6ee1\u53e3\u6c5f\u6e56\u672f\u8bed\uff0c\u5b66\u5927\u4eba\u7684\u8bf4\u8bdd\uff0c\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u559d\u9053\uff1a\u201c\u9ec4\u53e3\u5c0f\u513f\uff0c\u80e1\u8bf4\u516b\u9053\uff01\u201d \u53f3\u624b\u4e00\u97ad\uff0c\u8361\u5f00\u4e86\u6bd5\u613f\u7a77\u7684\u6746\u68d2\uff0c\u5de6\u624b\u4e00\u4f38\uff0c\u65bd\u5c55\u64d2\u62ff\u624b\u6cd5\u6765\u6293\u5c0f\u864e\u5b50\uff0c\u4ed6\u54ea\u91cc\u4f1a\u628a\u5c0f\u864e\u5b50 \u653e\u5728\u5fc3\u4e0a\u3002\u4e0d\u6599\u5c0f\u864e\u5b50\u4e43\u662f\u5c06\u95e8\u864e\u5b50\uff0c\u53c8\u5148\u540e\u5f97\u4e86\u9ed1\u767d\u6469\u8bc3\u548c\u5f20\u4e39\u67ab\u7684\u4f20\u6388\uff0c\u6b66\u529f\u5df2\u662f\u80dc\u8fc7 \u8bb8\u591a\u6c5f\u6e56\u597d\u624b\uff01 \u3000\u3000\u987e\u5b5f\u7ae0\u4e00\u6293\u6293\u4e0b\uff0c\u6251\u4e86\u4e2a\u7a7a\uff0c\u5c0f\u864e\u5b50\u6ed1\u4f3c\u6e38\u9c7c\u4e00\u6837\u4ece\u4ed6\u7684\u97ad\u68a2\u5e95\u4e0b\u94bb\u8fc7\uff0c\u201c\u7830\u201d\u7684\u4e00 \u62f3\uff0c\u6b63\u4e2d\u4ed6\u7684\u8170\u80ef\uff0c\u8fd9\u4e00\u62f3\u4e43\u662f\u9ed1\u767d\u6469\u8bc3\u6240\u6388\u7684\u4e94\u884c\u7f57\u6c49\u795e\u62f3\u4e2d\u7684\u201c\u9f99\u62f3\u201d\uff0c\u62f3\u52bf\u5a01\u731b\u65e0 \u6bd4\uff0c\u987e\u5b5f\u7ae0\u731d\u4e0d\u53ca\u9632\uff0c\u88ab\u6253\u5f97\u5f2f\u4e0b\u4e86\u8170\uff0c\u75db\u5f7b\u5fc3\u80ba\u3002\u5c0f\u864e\u5b50\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u518d\u63a5\u6211\u7684\u864e \u62f3\uff01\u201d\u53f3\u62f3\u4e00\u6536\uff0c\u5de6\u62f3\u968f\u5373\u6253\u51fa\uff0c\u5ffd\u542c\u5f97\u4e8e\u627f\u73e0\u53eb\u9053\uff1a\u201c\u5feb\u7528\u5206\u82b1\u62c2\u67f3\u624b\u6cd5\uff0c\u76d8\u9f99\u7ed5\u6811\uff0c\u5411 \u5de6\u95ea\u5f00\u3002\u201d\u53eb\u58f0\u672a\u5b8c\uff0c\u4f46\u89c1\u987e\u5b5f\u7ae0\u4e00\u4e2a\u8e6c\u811a\u98de\u8d77\uff0c\u811a\u5c16\u6b63\u5bf9\u51c6\u5c0f\u864e\u5b50\u7684\u80f8\u53e3\uff0c\u5c0f\u864e\u5b50\u90a3\u4e00\u62f3 \u82e5\u7136\u6253\u51fa\uff0c\u5c31\u521a\u597d\u662f\u51d1\u4e0a\u53bb\u7ed9\u4ed6\u8e22\u4e86\u3002 \u3000\u3000\u8981\u77e5\u5c0f\u864e\u5b50\u521a\u624d\u90a3\u4e00\u4e0b\uff0c\u8eab\u6cd5\u624b\u6cd5\u867d\u7136\u90fd\u662f\u4e0a\u4e58\u7684\u529f\u592b\uff0c\u5374\u4e5f\u5e26\u7740\u51e0\u5206\u4fa5\u5e78\uff0c\u8bba\u5230\u672c\u8eab \u7684\u529f\u529b\uff0c\u5374\u8fd8\u662f\u4e0e\u987e\u5b5f\u7ae0\u5dee\u5f97\u592a\u8fdc\u3002\u5e78\u597d\u4e8e\u627f\u73e0\u51fa\u8a00\u70b9\u9192\uff0c\u5c0f\u864e\u5b50\u6025\u5fd9\u8f6c\u6b65\u95ea\u5f00\uff0c\u9976\u662f\u5982 \u6b64\uff0c\u4e5f\u7ed9\u987e\u5b5f\u7ae0\u811a\u5c16\u626b\u7740\uff0c\u6454\u4e86\u4e00\u4e2a\u7b4b\u6597\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u54c7\u54c7\u5927\u53eb\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u6b63\u60f3\u6325\u62f3\u518d\u6253\uff0c\u5374\u89c1\u4e8e\u627f\u73e0\u5df2\u4e0e\u987e\u5b5f\u7ae0\u6597\u5728\u4e00\u8d77\u3002\u5c0f\u864e\u5b50 \u4eae\u51fa\u4e86\u5bb6\u4f20\u7f05\u5200\uff0c\u53ea\u542c\u5f97\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u53cc\u62f3\u6362\u4e00\u811a\uff0c\u5df2\u662f\u4f60\u5360\u4e86\u4fbf\u5b9c\uff0c\u8fd8\u4e0d\u77e5\u8db3\u4e48\uff1f\u4f60\u53bb \u5e2e\u90d1\u957f\u8001\u5427\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u597d\uff0c\u4f60\u7ed9\u6211\u6316\u6389\u4ed6\u7684\u62db\u5b50\u3002\u201d\u6012\u6c14\u672a\u6d88\uff0c\u6325\u5200\u731b\u65ab\uff0c\u6740\u5f00\u4e86\u4e00 \u6761\u8840\u8def\uff0c\u4ef2\u5230\u90d1\u957f\u8001\u7684\u8ddf\u524d\u3002 \u3000\u3000\u8fd9\u65f6\u53cc\u65b9\u6fc0\u6218\u6b63\u70c8\uff0c\u90d1\u957f\u8001\u5bf9\u4ed8\u7684\u662f\u9633\u5b97\u6d77\u7684\u526f\u624b\u4e1c\u65b9\u6d1b\u3002\u90d1\u957f\u8001\u6b66\u529f\u4e0d\u5f31\uff0c\u53ef\u60dc\u5e74\u8001 \u4f53\u8870\uff0c\u5f00\u5934\u5341\u4f59\u62db\u8fd8\u80fd\u5e94\u4ed8\u3002\u65f6\u95f4\u4e00\u957f\uff0c\u6e10\u89c9\u6c14\u5598\u96be\u652f\uff0c\u5c0f\u864e\u5b50\u6b63\u597d\u53ca\u65f6\u8d76\u5230\uff0c\u7acb\u523b\u5c55\u5f00\u4e86 \u4e94\u864e\u65ad\u95e8\u5200\u6cd5\uff0c\u5c06\u4e1c\u65b9\u6d1b\u7684\u62db\u6570\u63a5\u4e86\u516b\u6210\u3002 \u3000\u3000\u4e1c\u65b9\u6d1b\u5e26\u6765\u7684\u90fd\u662f\u5fa1\u6797\u519b\u4e0e\u9526\u8863\u536b\u4e2d\u7684\u9ad8\u624b\uff0c\u4eba\u6570\u4e5f\u6bd4\u4e10\u5e2e\u5f1f\u5b50\u4e3a\u591a\uff0c\u6df7\u6218\u4e86\u534a\u4e2a\u65f6 \u8fb0\uff0c\u6e10\u6e10\u5206\u51fa\u9ad8\u5f31\uff0c\u53cc\u65b9\u5747\u662f\u4f24\u4ea1\u8fc7\u534a\uff0c\u4f46\u4e10\u5e2e\u4eba\u5c11\uff0c\u60c5\u51b5\u81ea\u662f\u4e25\u91cd\u5f97\u591a\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u4e0e\u987e\u5b5f\u7ae0\u6597\u4e86\u6570\u5341\u56de\u5408\uff0c\u4e00\u4e2a\u80dc\u5728\u5251\u6cd5\u7cbe\u5999\uff0c\u4e00\u4e2a\u80dc\u5728\u5185\u529b\u6df1\u539a\uff0c\u5140\u662f\u4e0d\u5206\u80dc \u8d1f\u3002\u4e10\u5e2e\u7684\u5f62\u52bf\u8d8a\u6765\u8d8a\u9669\uff0c\u4e0d\u591a\u65f6\u53c8\u6709\u4e24\u4e2a\u516d\u888b\u5f1f\u5b50\u53d7\u4f24\u5012\u5730\u3002\u6bd5\u613f\u7a77\u672c\u6765\u5bf9\u6bd5\u64ce\u5929\u5c1a\u672a\u81f3 \u6069\u65ad\u4e49\u7edd\u7684\u5730\u6b65\uff0c\u867d\u7136\u5bf9\u4ed6\u4e0d\u6ee1\uff0c\u591a\u5c11\u8fd8\u6709\u53d4\u4f84\u4e4b\u60c5\uff0c\u8fd9\u65f6\u89c1\u4ed6\u6240\u6d3e\u9063\u7684\u987e\u5b5f\u7ae0\uff0c\u7adf\u7136\u52fe\u7ed3 \u671d\u5ef7\uff0c\u6b8b\u6740\u672c\u5e2e\u5f1f\u5b50\uff0c\u800c\u4ed6\u8fd8\u662f\u5927\u9f99\u5934\u7684\u8eab\u4efd\uff0c\u8fd9\u771f\u662f\u65f7\u53e4\u6240\u65e0\u9a87\u4eba\u542c\u95fb\u7684\u5e2e\u4e2d\u5947\u53d8\uff0c\u4e0d\u7531 \u5f97\u5fc3\u4e2d\u5927\u75db\uff0c\u6b32\u54ed\u65e0\u58f0\u3002\u5ffd\u542c\u5f97\u90d1\u957f\u8001\u53eb\u9053\uff1a\u201c\u7559\u5f97\u9752\u5c71\u5728\uff0c\u4e0d\u6015\u6ca1\u67f4\u70e7\uff01\u201d\u610f\u601d\u662f\u8981\u4e10\u5e2e \u5f1f\u5b50\u62fc\u529b\u7a81\u56f4\uff0c\u8d70\u5f97\u4e00\u4e2a\u4fbf\u662f\u4e00\u4e2a\uff0c\u6bd5\u613f\u7a77\u54ac\u4e00\u54ac\u7259\uff0c\u547c\u547c\u4e24\u68d2\uff0c\u6253\u5012\u4e86\u8eab\u524d\u7684\u4e00\u4e2a\u536b\u58eb\uff0c \u987e\u5b5f\u7ae0\u72de\u7b11\u9053\uff1a\u201c\u53db\u5e2e\u6076\u4e10\uff0c\u8fd8\u60f3\u8d70\u5417\uff1f\u201d \u3000\u3000\u53cd\u624b\u4e00\u97ad\uff0c\u7a81\u7136\u820d\u4e86\u4e8e\u627f\u73e0\uff0c\u4fbf\u6765\u6697\u88ad\u6bd5\u613f\u7a77\uff0c\u4ed6\u8fd9\u6761\u866c\u9f99\u97ad\uff0c\u65bd\u5c55\u5f00\u6765\uff0c\u957f\u8fbe\u4e00\u4e08\uff0c \u6bd5\u613f\u7a77\u6599\u4e0d\u5230\u4ed6\u58f0\u5230\u97ad\u5230\uff0c\u6746\u68d2\u6253\u51fa\uff0c\u521a\u597d\u7ed9\u4ed6\u7684\u957f\u97ad\u7f20\u7740\uff0c\u4e0e\u6b64\u540c\u65f6\uff0c\u65e9\u5df2\u6709\u53e6\u5916\u4e09\u540d\u5fa1 \u6797\u519b\u7684\u597d\u624b\u66ff\u4ee3\u4e86\u987e\u5b5f\u7ae0\u7684\u4f4d\u7f6e\uff0c\u5835\u622a\u7740\u4e8e\u627f\u73e0\u3002\u987e\u5b5f\u7ae0\u5927\u559d\u4e00\u58f0\u201c\u5012\u4e0b\u201d\uff0c\u7528\u529b\u4e00\u62c9\uff0c\u6bd5 \u613f\u7a77\u8eab\u5f62\u4e0d\u7a33\uff0c\u51e0\u4e4e\u5e94\u58f0\u683d\u5012\uff01 \u3000\u3000\u6bd5\u613f\u7a77\u4e0d\u662f\u987e\u5b5f\u7ae0\u7684\u5bf9\u624b\uff0c\u90a3\u4e09\u4e2a\u66ff\u4ee3\u987e\u5b5f\u7ae0\u7684\u5fa1\u6797\u519b\u7edf\u9886\u4e5f\u4e0d\u662f\u4e8e\u627f\u73e0\u7684\u5bf9\u624b\uff0c\u987e\u5b5f \u7ae0\u8fd8\u5e0c\u671b\u4ed6\u4eec\u80fd\u5835\u622a\u5f97\u4e00\u65f6\u534a\u523b\uff0c\u7b49\u4ed6\u64d2\u4e86\u6bd5\u613f\u7a77\u4e4b\u540e\uff0c\u518d\u56de\u5934\u6765\u5bf9\u4ed8\u4e8e\u627f\u73e0\u3002\u54ea\u77e5\u987e\u5b5f\u7ae0 \u7684\u8eab\u624b\u56fa\u7136\u77eb\u6377\uff0c\u4e8e\u627f\u73e0\u6bd4\u4ed6\u66f4\u5feb\uff0c\u51e0\u4e4e\u5c31\u5728\u987e\u5b5f\u7ae0\u7684\u957f\u97ad\u7f20\u7740\u4e86\u6bd5\u613f\u7a77\u7684\u540c\u4e00\u65f6\u95f4\uff0c\u4e8e\u627f \u73e0\u9661\u5730\u98de\u8d77\u4e00\u5251\uff0c\u4e00\u62db\u201c\u9f99\u95e8\u9f13\u6d6a\u201d\uff0c\u8fde\u73af\u4e09\u5251\uff0c\u5c06\u8fd9\u4e09\u4e2a\u5fa1\u6797\u519b\u624b\u4e2d\u7684\u5175\u5668\u5168\u90fd\u524a\u65ad\uff0c\u7acb \u523b\u817e\u51fa\u624b\u6765\uff0c\u638f\u51fa\u4e86\u4e00\u628a\u91d1\u82b1\uff0c\u201c\u94ee\u94ee\u201d\u4e24\u58f0\uff0c\u5148\u5411\u987e\u5b5f\u7ae0\u5f39\u51fa\u4e24\u6735\uff0c\u987e\u5b5f\u7ae0\u8bc6\u5f97\u5389\u5bb3\uff0c\u6025 \u5fd9\u62bd\u51fa\u957f\u97ad\uff0c\u76d8\u5934\u75be\u821e\uff0c\u767b\u65f6\u5377\u8d77\u4e86\u4e00\u56e2\u97ad\u5f71\uff0c\u98ce\u96e8\u96be\u4fb5\uff0c\u5c06\u4e8e\u627f\u73e0\u7684\u4e24\u6735\u91d1\u82b1\u8361\u5f97\u65e0\u8e2a\u65e0 \u5f71\uff0c\u4f46\u6bd5\u613f\u7a77\u5374\u4e5f\u8d81\u6b64\u65f6\u673a\uff0c\u6740\u51fa\u91cd\u56f4\u53bb\u4e86\uff01 \u3000\u3000\u987e\u5b5f\u7ae0\u7684\u672c\u9886\u4e0e\u9633\u5b97\u6d77\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u957f\u97ad\u98de\u821e\uff0c\u62a4\u7740\u5168\u8eab\uff0c\u5bf9\u91d1\u82b1\u6697\u5668\u81ea\u662f\u4e0d\u60e7\uff08\u53ef\u662f \u4ea6\u4ec5\u80fd\u9632\u5b88\u800c\u5df2\uff09\uff0c\u5176\u4ed6\u7684\u4eba\u5374\u6ca1\u6709\u4ed6\u8fd9\u822c\u672c\u9886\uff0c\u4e8e\u627f\u73e0\u4e00\u89e3\u4e86\u6bd5\u613f\u7a77\u4e4b\u56f4\uff0c\u7acb\u523b\u4ee5\u201c\u5929\u5973 \u6563\u82b1\u201d\u7684\u624b\u6cd5\uff0c\u4e94\u6307\u8f6e\u6362\uff0c\u8fde\u73e0\u75be\u5f39\uff0c\u4f46\u89c1\u91d1\u5149\u95ea\u95ea\uff0c\u56db\u9762\u98de\u5f00\uff0c\u201c\u54ce\u54df\u201d\u4e4b\u58f0\u56db\u8d77\uff01\u7247\u523b \u4e4b\u95f4\uff0c\u53c8\u6709\u516d\u4e03\u4e2a\u5fa1\u6797\u519b\u7edf\u9886\u88ab\u6253\u4e2d\u4e86\u7a74\u9053\uff0c\u6eda\u5012\u5730\u4e0a\uff0c\u722c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u89c1\u4e8e\u627f\u73e0\u5f97\u624b\uff0c\u7cbe\u795e\u4e00\u632f\uff0c\u8d81\u7740\u654c\u4eba\u6df7\u6218\u7684\u65f6\u673a\uff0c\u5530\u5530\u4e24\u5200\uff0c\u7a81\u7136\u5c55\u51fa\u4e86\u201c\u4e94\u864e \u65ad\u95e8\u5200\u201d\u7684\u5192\u9669\u6740\u7740\uff0c\u5200\u5149\u7535\u95ea\uff0c\u6b3a\u8eab\u903c\u8fdb\u3002\u4e1c\u65b9\u6d1b\u7684\u6708\u7259\u5f2f\u5200\u5584\u80fd\u52fe\u9501\u5175\u5668\uff0c\u89c1\u5c0f\u864e\u5b50\u8d2a \u653b\u5fd8\u5371\uff0c\u653b\u5165\u5185\u95e8\uff0c\u6b63\u5408\u5fc3\u610f\uff0c\u6708\u7259\u5200\u4e00\u52fe\u4e00\u9501\uff0c\u5927\u559d\u4e00\u58f0\u201c\u6492\u624b\u201d\uff0c\u54ea\u77e5\u5c0f\u864e\u5b50\u7684\u5200\u950b\u970d \u5730\u4e00\u8f6c\uff0c\u7a81\u7136\u4ece\u4e0b\u624b\u5200\u53d8\u4e3a\u4e86\u4e0a\u624b\u5200\uff0c\u7adf\u4ece\u4e1c\u65b9\u6d1b\u7edd\u5bf9\u610f\u60f3\u4e0d\u5230\u7684\u65b9\u5411\u65ab\u4e86\u8fdb\u6765\uff0c\u53ea\u89c1\u5200\u5149 \u8fc7\u5904\uff0c\u8840\u82b1\u98de\u6e85\uff0c\u201c\u5530\u5566\u201d\u4e00\u4e0b\uff0c\u4e1c\u65b9\u6d1b\u7684\u81c2\u818a\u5df2\u88ab\u7f05\u5200\u62c9\u4e0b\u4e86\u4e00\u9053\u957f\u957f\u7684\u53e3\u3002\u4f46\u4e1c\u65b9\u6d1b\u7684 \u6b66\u529f\u786e\u662f\u9ad8\u5f3a\uff0c\u773c\u89c1\u8fd9\u4e00\u5200\u65e0\u53ef\u95ea\u907f\uff0c\u5c45\u7136\u8fd8\u662f\u4ee5\u653b\u4e3a\u5b88\uff0c\u6708\u7259\u5200\u970d\u5730\u4e00\u7ffb\uff0c\u5200\u5934\u7684\u6708\u7259\u582a \u582a\u5c31\u8981\u52fe\u7740\u4e86\u5c0f\u864e\u5b50\u7684\u624b\u8155\uff0c\u90d1\u957f\u8001\u594b\u4e0d\u987e\u8eab\uff0c\u4e00\u68d2\u5288\u8fdb\uff0c\u4ed6\u5e74\u8001\u4f53\u8870\uff0c\u8fd9\u4e00\u68d2\u7528\u8db3\u6c14\u529b\uff0c \u4f46\u542c\u5f97\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u5200\u68d2\u76f8\u4ea4\uff0c\u90d1\u957f\u8001\u7684\u6746\u68d2\u88ab\u53cd\u5f39\u98de\u8d77\uff0c\u5c0f\u864e\u5b50\u867d\u7136\u8131\u51fa\u624b\u6765\uff0c\u6ca1\u6709\u53d7 \u4f24\uff0c\u90d1\u957f\u8001\u7684\u624b\u8155\u5374\u88ab\u90a3\u5200\u4e0a\u7684\u6708\u7259\u6495\u7834\u4e86\u597d\u5927\u4e00\u7247\u76ae\u8089\u3002 \u3000\u3000\u4e24\u65b9\u90fd\u53d7\u4e86\u91cd\u4f24\uff0c\u4e0d\u6562\u604b\u6218\uff0c\u5c0f\u864e\u5b50\u62d6\u7740\u90d1\u957f\u8001\uff0c\u4e00\u8f6e\u6cfc\u51e4\u5200\u6cd5\uff0c\u6740\u51fa\u91cd\u56f4\uff0c\u4e0e\u4e8e\u627f\u73e0 \u4f1a\u5408\uff0c\u987e\u5b5f\u7ae0\u5140\u81ea\u4e0d\u820d\uff0c\u8854\u5c3e\u6025\u8ffd\uff0c\u4e8e\u627f\u73e0\u5927\u6012\uff0c\u4e0e\u5c0f\u864e\u5b50\u4f7f\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u9661\u7136\u95f4\u4e24\u4eba\u4e00\u9f50 \u7eb5\u8eab\u98de\u8d77\uff0c\u53cd\u6251\u56de\u6765\uff0c\u5b9d\u5251\u4e00\u4e2a\u76d8\u65cb\uff0c\u7f05\u5200\u51cc\u7a7a\u4e0b\u523a\uff0c\u4f46\u89c1\u5728\u5200\u5149\u5251\u5f71\u4e4b\u4e0b\uff0c\u567c\u567c\u556a\u556a\u7684\u51e0 \u58f0\u75be\u54cd\uff0c\u987e\u5b5f\u7ae0\u7684\u90a3\u6761\u866c\u9f99\u97ad\u65ad\u6210\u56db\u6bb5\uff01\u539f\u6765\u5c0f\u864e\u5b50\u914d\u5408\u7740\u4e8e\u627f\u73e0\u7684\u5251\u62db\uff0c\u4e5f\u5c06\u767e\u53d8\u7384\u673a\u5251 \u6cd5\u5316\u5230\u5200\u6cd5\u4e0a\u6765\uff0c\u7384\u673a\u9038\u58eb\u6240\u521b\u7684\u8fd9\u5957\u5251\u6cd5\uff0c\u4e00\u7ecf\u914d\u5408\uff0c\u5999\u7528\u65e0\u7a77\uff0c\u4e24\u4eba\u5408\u4f7f\uff0c\u529f\u529b\u4f55\u6b62\u9661 \u589e\u4e00\u500d\uff01\u5373\u7b97\u987e\u5b5f\u7ae0\u672c\u4e8b\u518d\u9ad8\uff0c\u4ea6\u662f\u62b5\u6321\u4e0d\u4e86\u3002\u4e8e\u627f\u73e0\u51b7\u7b11\u9053\uff1a\u201c\u770b\u4f60\u8fd8\u6562\u518d\u8ffd\uff01\u201d\u4e00\u6296\u624b \u53d1\u51fa\u4e09\u6735\u91d1\u82b1\uff0c\u987e\u5b5f\u7ae0\u957f\u97ad\u5bf8\u65ad\uff0c\u65e0\u53ef\u62b5\u5fa1\uff0c\u95ea\u5f00\u4e86\u4e24\u6735\uff0c\u95ea\u4e0d\u5f00\u7b2c\u4e09\u6735\uff0c\u4f46\u89c1\u91d1\u5149\u95ea\u5904\uff0c \u987e\u5b5f\u7ae0\u7684\u5de6\u773c\u773c\u73e0\u5df2\u88ab\u6253\u778e\uff01\u5c0f\u864e\u5b50\u54c8\u54c8\u5927\u7b11\uff0c\u4e0e\u4e8e\u627f\u73e0\u5de6\u53f3\u6276\u6301\uff0c\u62c9\u7740\u90d1\u957f\u8001\uff0c\u4e00\u9635\u98de \u5954\uff0c\u8ffd\u4e0a\u4e86\u6bd5\u613f\u7a77\uff0c\u9003\u5230\u4e86\u897f\u5c71\u80cc\u540e\u3002 \u3000\u3000\u4e00\u573a\u6df7\u6218\uff0c\u5fa1\u6797\u519b\u4e0e\u9526\u8863\u536b\u5341\u4f24\u4e03\u516b\uff0c\u4f46\u4e10\u5e2e\u7684\u5f1f\u5b50\u4e5f\u53ea\u9003\u51fa\u4e86\u6bd5\u613f\u7a77\u4e0e\u90d1\u957f\u8001\u4e8c\u4eba\u3001 \u6bd5\u613f\u7a77\u5fc3\u75db\u5982\u5272\uff0c\u54bd\u6cea\u8bf4\u9053\uff1a\u201c\u59d1\u5976\u5976\uff0c\u4e0d\uff0c\u4e8e\u5973\u4fa0\uff0c\u591a\u8c22\u4f60\u5566\uff01\u201d\u4ed6\u7d20\u6027\u6ed1\u7a3d\uff0c\u8138\u4e0a\u7684\u795e \u8272\u4e0d\u8bba\u5728\u4ec0\u4e48\u65f6\u5019\u770b\u53bb\u90fd\u4f3c\u5e26\u7740\u7b11\u610f\uff0c\u4ed6\u5728\u4e49\u519b\u4e4b\u4e2d\u7ecf\u5e38\u4e0e\u4e8e\u627f\u73e0\u8c03\u4f83\uff0c\u603b\u662f\u5c06\u5979\u620f\u547c\u4e3a \u201c\u59d1\u5976\u5976\u201d\uff0c\u8fd9\u65f6\u5ffd\u89c9\u4e0d\u59a5\uff0c\u6539\u79f0\u201c\u5973\u4fa0\u201d\uff0c\u4e8e\u627f\u73e0\u5fcd\u4e0d\u4f4f\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u4f46\u542c\u4ed6\u8bed\u8c03\u9178 \u6da9\uff0c\u8138\u4e0a\u4f3c\u7b11\u975e\u7b11\u7684\u795e\u60c5\u6bd4\u54ed\u8fd8\u66f4\u4ee4\u4eba\u96be\u53d7\uff0c\u4e5f\u7981\u4e0d\u4f4f\u5fc3\u4e2d\u4e00\u9178\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6bd5\u5927\u54e5\uff0c\u4f60 \u522b\u96be\u8fc7\uff0c\u6211\u5bfb\u7740\u4e86\u5e08\u7236\uff0c\u7ec8\u987b\u4e3a\u4f60\u62a5\u4ec7\u3002\u201d\u56de\u5934\u95ee\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u5e08\u7236\u662f\u51e0\u65f6\u6765\u7684\uff1f\u4f4f\u5728\u54ea \u513f\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u5e08\u7236\u662f\u524d\u5929\u5230\u7684\uff0c\u4ed6\u6253\u542c\u5230\u4e10\u5e2e\u5f1f\u5b50\u805a\u4f1a\uff0c\u4ed6\u62bd\u4e0d\u51fa\u8eab\uff0c\u6240\u4ee5\u53eb\u6211\u6765\u6253 \u63a2\u3002\u54c8\uff0c\u5e08\u6bcd\u548c\u4e91\u5927\u4fa0\u90fd\u540c\u6765\u4e86\u5462\uff0c\u4ed6\u4eec\u5206\u505a\u4e24\u5904\u5730\u65b9\u5c45\u4f4f\uff0c\u4e91\u5927\u4fa0\u4f4f\u5728\u97e9\u5fa1\u53f2\u5bb6\u4e2d\uff0c\u54b1\u4eec \u7684\u5e08\u7236\u5e08\u6bcd\u548c\u6ce2\u65af\u516c\u4e3b\u592b\u59bb\u5374\u4f4f\u5728\u9760\u8fd1\u7687\u5bab\u7684\u4e00\u5bb6\u9556\u5c40\u91cc\uff0c\u70ed\u95f9\u5f97\u5f88\u5462\uff01\u201d\u4e8e\u627f\u73e0\u8f6c\u60b2\u4e3a \u559c\uff0c\u9053\uff1a\u201c\u5e08\u6bcd\u548c\u8205\u8205\u90fd\u6765\u4e86\uff01\u90a3\u4e48\u54b1\u4eec\u5c31\u66f4\u4e0d\u7528\u6015\u5566\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4e91\u5927\u4fa0\u5728\u82cd \u5c71\u4e4b\u65f6\uff0c\u4e2d\u4e86\u90a3\u4e2a\u5c60\u9f99\u5c0a\u8005\u7684\u6bd2\u5200\uff0c\u5728\u592a\u5e08\u7956\u7559\u4e0b\u6765\u7684\u77f3\u5c4b\u91cc\u9759\u517b\u4e86\u5c06\u8fd1\u4e00\u4e2a\u6708\uff0c\u8fd9\u624d\u590d \u539f\u3002\u8981\u4e0d\u7136\u6211\u4eec\u65e9\u5c31\u5230\u4e86\u4eac\u57ce\u4e86\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u6b63\u60f3\u518d\u95ee\uff0c\u5ffd\u89c1\u90d1\u957f\u8001\u9762\u5982\u91d1\u7eb8\uff0c\u9ed1\u6c14\u900f\u51fa\u7709\u5c16\uff0c\u8eab\u5b50\u4e5f\u6447\u6447\u6b32\u5760\uff0c\u7981\u4e0d\u4f4f\u5927\u60ca \u5931\u8272\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u957f\u8001\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u90d1\u957f\u8001\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u4e2d\u7528\u5566\uff0c\u4f60\u4eec\u8d76\u5feb\u53bb\u627e \u5f20\u5927\u4fa0\uff0c\u4e0d\u5fc5\u987e\u6211\u4e86\u3002\u6bd5\u613f\u7a77\uff0c\u4f60\u544a\u8bc9\u672c\u5e2e\u5f1f\u5b50\u77e5\u9053\uff0c\u8bf4\u6211\u662f\u7ed9\u4e1c\u65b9\u6d1b\u7684\u6bd2\u5200\u65ab\u6b7b\u7684\uff0c\u53eb\u4ed6 \u4eec\u7ed9\u6211\u62a5\u4ec7\uff01\u201d\u6bd5\u613f\u7a77\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u6bd2\u5200\uff1f\u201d\u4fef\u8eab\u4e00\u770b\uff0c\u4f46\u89c1\u4ed6\u7684\u4f24\u53e3\u88c2\u5f00\uff0c\u6d41\u51fa\u6c69\u6c69\u7684\u9ed1 \u8840\uff0c\u6458\u4e00\u7247\u6811\u53f6\u4e00\u8bd5\uff0c\u6811\u53f6\u7acb\u523b\u7126\u9ec4\uff0c\u6bd2\u6027\u5982\u6b64\u5389\u5bb3\uff0c\u5e74\u8f7b\u529b\u58ee\u7684\u4ea6\u7981\u53d7\u4e0d\u8d77\uff0c\u4f55\u51b5\u662f\u5e74\u7eaa \u8001\u8fc8\u800c\u53c8\u7ecf\u8fc7\u901a\u5bb5\u6fc0\u6218\u7684\u90d1\u957f\u8001\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7b49\u600e\u5fcd\u79bb\u5f00\uff0c\u8bd5\u7528\u968f\u8eab\u6240\u5e26\u7684\u201c\u795b\u6bd2\u6563\u201d\u66ff\u4ed6\u533b\u6cbb\uff0c\u8fd9\u79cd\u9ad8\u624b\u6240\u7528\u7684\u5582\u6bd2\u5175\u5668\uff0c \u5927\u90fd\u6709\u4e13\u95e8\u7684\u89e3\u836f\uff0c\u4e8e\u627f\u73e0\u7684\u201c\u53bb\u6bd2\u6563\u201d\u867d\u7136\u80fd\u6d88\u65e0\u540d\u80bf\u75db\uff0c\u5bf9\u90d1\u957f\u8001\u7684\u4f24\u5374\u662f\u65e0\u6d4e\u4e8e\u4e8b\uff0c \u89e6\u53ca\u4f24\u53e3\uff0c\u90d1\u957f\u8001\u767b\u65f6\u75c9\u631b\uff0c\u5f3a\u5fcd\u7740\u75db\u82e6\u65a5\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d\u5feb\u8d70\uff0c\u8981\u5f85\u5fa1\u6797\u519b\u8ffd\u6765\u5c06\u4f60\u4eec\u4e00 \u7f51\u6253\u5c3d\u5417\uff1f\u201d\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u5b81\u613f\u540c\u5f52\u4e8e\u5c3d\uff0c\u51b3\u4e0d\u820d\u4f60\u800c\u9003\u3002\u201d\u90d1\u957f\u8001\u5927\u6012\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u6b63\u60f3 \u7528\u4e10\u5e2e\u7684\u90a6\u89c4\u547d\u4ee4\u4ed6\u901f\u8d70\uff0c\u53ea\u89c1\u4e1c\u65b9\u5929\u9645\uff0c\u671d\u9633\u521d\u73b0\uff0c\u971c\u8f89\u4e3d\u5f69\uff0c\u8000\u773c\u751f\u7f2c\uff0c\u6e29\u6696\u7684\u9633\u5149\u4ee4 \u4eba\u611f\u5230\u751f\u547d\u7684\u559c\u60a6\uff0c\u51dd\u7738\u518d\u671b\uff0c\u897f\u5317\u8fb9\u7684\u4e07\u91cc\u957f\u57ce\u50cf\u4e00\u6761\u957f\u86c7\u822c\u5728\u5d07\u5c71\u5cfb\u5cad\u4e2d\u5a49\u8712\u800c\u8fc7\uff0c\u90d1 \u957f\u8001\u5fc3\u4e2d\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u8fd9\u662f\u897f\u5c71\u5317\u9762\u9760\u8fd1\u846b\u82a6\u8c37\u7684\u5730\u65b9\u3002\u201d \u90d1\u957f\u8001\u5ffd\u9053\uff1a\u201c\u597d\uff0c\u6276\u6211\u8fdb\u8c37\u4e2d\u53bb\u770b\u770b\u90a3\u91cc\u9762\u6709\u6ca1\u6709\u4eba\u5bb6\uff1f\u201d\u8bdd\u58f0\u65ad\u7eed\uff0c\u7ec6\u5982\u6e38\u4e1d\uff0c\u4f46\u5374\u66f4 \u4e3a\u6e05\u695a\uff0c\u6bd5\u613f\u7a77\u542c\u51fa\u4ed6\u8bed\u58f0\u6709\u5f02\uff0c\u6025\u5fd9\u4e0e\u5c0f\u864e\u5b50\u6276\u4ed6\u8d70\u8fdb\u5c71\u8c37\uff0c\u4f46\u89c1\u4ed6\u5634\u89d2\u6302\u7740\u4e9b\u8bb8\u7b11\u610f\uff0c \u773c\u775b\u5374\u6e10\u6e10\u778c\u4e0a\u4e86\u3002 \u3000\u3000\u8d70\u8fdb\u5c71\u8c37\uff0c\u679c\u7136\u89c1\u6709\u4e00\u5bb6\u519c\u5bb6\uff0c\u6ce5\u5c4b\u8305\u820d\u4e0e\u666e\u901a\u4eba\u5bb6\u65e0\u5f02\uff0c\u4f46\u82e5\u5927\u7684\u5c71\u8c37\u4e2d\u72ec\u6b64\u5b64\u96f6\u96f6 \u7684\u4e00\u5bb6\u4eba\u5bb6\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5bb6\u4eba\u5bb6\u6709\u70b9\u53e4\u602a\u3002\u201d\u4f46\u89c1\u6bd5\u613f\u7a77\u4e0a\u53bb\u62cd\u95e8\uff0c\u90a3\u95e8\u201c\u5440\u201d\u7684 \u4e00\u58f0\u5f00\u4e86\uff0c\u91cc\u9762\u8d70\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u7adf\u662f\u4e2a\u8001\u5112\u751f\u7684\u6253\u626e\uff0c\u7a7f\u7740\u4e00\u4ef6\u6de1\u84dd\u8272\u7684\u957f\u886b\uff0c\u5934\u4e0a\u8fd8\u675f\u7740 \u65b9\u5dfe\uff0c\u4e0e\u8fd9\u5bb6\u519c\u5bb6\u76f8\u886c\uff0c\u6b8a\u663e\u5f97\u4e0d\u4f26\u4e0d\u7c7b\u3002 \u3000\u3000\u5176\u5b9e\u4e8e\u627f\u73e0\u8fd9\u4e00\u884c\u4eba\uff0c\u4e00\u4e2a\u9e51\u8863\u767e\u7ed3\u7684\u8001\u4e5e\u4e10\uff0c\u4e00\u4e2a\u7a7f\u7740\u5e72\u5e72\u51c0\u51c0\u7684\u76f4\u6387\uff0c\u5374\u6545\u610f\u9489\u4e0a \u4e24\u4e2a\u7834\u8865\u4e01\uff0c\u6253\u626e\u5f97\u50cf\u4e5e\u4e10\u7684\u4e2d\u5e74\u58ee\u6c49\uff0c\u4e00\u4e2a\u5341\u4e94\u516d\u5c81\u7684\u5c11\u5e74\uff0c\u8fd8\u6709\u4e00\u4e2a\u5973\u626e\u7537\u88c5\u3001\u8863\u670d\u534e \u4e3d\u4e25\u5982\u8d35\u82a5\u516c\u5b50\u7684\u4e8e\u627f\u73e0\uff0c\u90a3\u66f4\u662f\u4e0d\u4f26\u4e0d\u7c7b\u3002\u90a3\u8001\u5112\u751f\u626b\u4e86\u4ed6\u4eec\u4e00\u773c\uff0c\u5fae\u201c\u566b\u201d\u4e00\u58f0\uff0c\u5374\u4e5f \u5e76\u4e0d\u600e\u4e48\u60ca\u8bb6\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u53e3\u5feb\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u4e00\u884c\u4eba\u5c71\u4e2d\u9047\u76d7\uff0c\u8fd9\u4f4d\u8001\u516c\u516c\u53d7\u4e86\u91cd\u4f24\uff0c\u8bf7\u501f\u4e2a\u5730\u65b9\u6b47 \u6b47\u3002\u201d\u90a3\u8001\u5112\u751f\u7b11\u9053\uff1a\u201c\u7adf\u6709\u8fd9\u7b49\u5f3a\u76d7\u6253\u52ab\u82b1\u5b50\u5927\u7237\uff0c\u6211\u6d3b\u4e86\u8fd9\u4e00\u5927\u628a\u5e74\u7eaa\uff0c\u53ef\u8fd8\u6ca1\u6709\u542c \u8fc7\u3002\u201d\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u54b1\u4eec\u4e0e\u8fd9\u4f4d\u5c11\u7237\u5c71\u4e2d\u76f8\u9047\uff0c\u5f3a\u76d7\u4eec\u6253\u52ab\u8fd9\u4f4d\u5c11\u7237\uff0c\u662f\u54b1\u4eec\u8fd9\u4e24\u4e2a\u7a77\u5316\u5b50 \u770b\u4e0d\u8fc7\u773c\uff0c\u66ff\u4ed6\u62b5\u6321\u5f3a\u76d7\uff0c\u6240\u4ee5\u53d7\u4f24\u5566\u3002\u201d\u8fd9\u8bdd\u52c9\u5f3a\u53ef\u4ee5\u81ea\u5706\u5176\u8bf4\uff0c\u90a3\u8001\u5112\u751f\u9053\uff1a\u201c\u5982\u6b64\u8bf4 \u6765\u4f60\u4eec\u4e24\u4f4d\u5012\u662f\u4e10\u4fa0\u4e86\uff0c\u5931\u656c\uff0c\u5931\u656c\uff01\u201d\u53e3\u6c14\u663e\u7136\u4ecd\u662f\u4e0d\u4fe1\uff0c\u4f46\u5374\u628a\u4ed6\u4eec\u8bf7\u8fdb\u5c4b\u4e2d\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u867d\u7136\u9648\u8bbe\u7b80\u964b\uff0c\u684c\u6905\u4e5f\u4e0d\u591a\u4e00\u5f20\uff0c\u4f46\u5374\u6536\u62fe\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u58c1\u4e0a\u8fd8\u6302\u6709\u5b57\u753b\uff0c\u54ea\u91cc \u50cf\u4e2a\u519c\u5bb6\u7684\u6837\u5b50\uff1f\u4e8e\u627f\u73e0\u6b63\u6253\u91cf\u4ed6\u5c4b\u4e2d\u7684\u9648\u8bbe\uff0c\u90a3\u8001\u5112\u751f\u5ffd\u5730\u201c\u563f\u563f\u201d\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u66ff\u4ed6\u62b5 \u6321\u5f3a\u76d7\uff0c\u54c8\u54c8\uff0c\u53ef\u522b\u7b11\u75db\u6211\u7684\u809a\u5b50\u3002\u6211\u770b\u4f60\u7ed9\u4ed6\u505a\u5f92\u5f1f\u5012\u8fd8\u5dee\u4e0d\u591a\uff0c\u53ef\u60dc\u5e74\u5c81\u4e0d\u5bf9\u3002\u800c\u4e14\u5927 \u95fa\u5973\u4e5f\u4e0d\u65b9\u4fbf\u6536\u5316\u5b50\u505a\u5f92\u5f1f\u3002\u201d\u6b64\u8bdd\u4e00\u51fa\uff0c\u4e8e\u627f\u73e0\u548c\u6bd5\u613f\u7a77\u90fd\u5413\u4e86\u4e00\u5927\u8df3\uff0c\u8fd9\u8001\u8005\u7684\u773c\u5149\u597d \u751f\u5389\u5bb3\uff0c\u975e\u4f46\u4e00\u773c\u770b\u51fa\u4e86\u4ed6\u4eec\u6b66\u529f\u7684\u6df1\u6d45\uff0c\u800c\u4e14\u770b\u51fa\u4e86\u4e8e\u627f\u73e0\u5973\u626e\u7537\u88c5\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u9762\u7ea2\u8fc7\u8033\uff0c\u6b63\u60f3\u8bf4\u8bdd\u3002\u90a3\u8001\u5112\u751f\u5ffd\u7136\u4e00\u624b\u62a2\u8fc7\u90d1\u957f\u8001\u7684\u7af9\u68d2\uff0c\u4e00\u624b\u62e8\u5f04\u4ed6\u80cc\u4e0a\u7684 \u9ebb\u888b\u3002\u90d1\u957f\u8001\u9886\u8896\u5317\u4eac\u7fa4\u4e10\uff0c\u8fd9\u516b\u8282\u7af9\u68d2\u6b63\u662f\u4ed6\u5e2e\u4e2d\u7684\u201c\u6cd5\u5668\u201d\uff0c\u8001\u5112\u751f\u5982\u6b64\u4f5c\u4e3a\uff0c\u5b9e\u662f\u72af \u4e86\u4e10\u5e2e\u4e4b\u5fcc\uff0c\u6bd5\u613f\u7a77\u559d\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u6025\u5fd9\u51fa\u624b\u62a2\u593a\u7af9\u6756\uff0c\u6bd5\u613f\u7a77\u5b66\u8fc7\u64d2\u62ff\u624b\u6cd5\uff0c\u76f8\u8ddd \u53c8\u8fd1\uff0c\u8fd9\u4e00\u51fa\u624b\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u6309\u8bf4\u6ca1\u6709\u62a2\u4e0d\u56de\u6765\u4e4b\u7406\uff0c\u54ea\u77e5\u8001\u5112\u751f\u8eab\u5b50\u53ea\u662f\u5fae\u5fae\u4e00\u6643\uff0c\u6bd5\u613f \u7a77\u7adf\u7136\u6251\u4e86\u4e2a\u7a7a\uff01 \u3000\u3000\u90d1\u957f\u8001\u4e00\u76f4\u778c\u7740\u773c\u775b\uff0c\u8fd9\u65f6\u5ffd\u5730\u5f20\u5f00\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u897f\u5c71\u533b\u9690\u53f6\u5927\u7237\uff0c\u4ffa\u90d1\u56fd\u6709\u767b\u95e8\u6c42 \u6cbb\u6765\u5566\uff0c\u671b\u4f60\u8001\u9ad8\u62ac\u8d35\u624b\uff01\u201d\u90a3\u8001\u5112\u751f\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff0c\u539f\u6765\u662f\u4e10\u5e2e\u7684\u90d1\u957f\u8001\uff0c \u54b1\u4eec\u540c\u4f4f\u5317\u4eac\uff0c\u672c\u8be5\u65e9\u5c31\u89c1\u9762\u3002\u597d\uff0c\u4ffa\u53f6\u5143\u7ae0\u4e0d\u533b\u516c\u4faf\u5c06\u76f8\uff0c\u4e13\u533b\u5947\u4eba\u5f02\u58eb\uff0c\u4f60\u5417\uff0c\u4e5f\u8fd8\u503c \u5f97\u4ffa\u66ff\u4f60\u4e00\u533b\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4e8e\u627f\u73e0\u548c\u6bd5\u613f\u7a77\u5747\u662f\u53c8\u60ca\u53c8\u559c\uff0c\u4ed6\u4eec\u8fd8\u5728\u7ae5\u5e74\u4e4b\u65f6\uff0c\u5c31\u66fe\u542c\u4eba\u8bf4\u8fc7\u5317\u4eac\u897f\u5c71 \u4e2d\u6709\u4e00\u4f4d\u533b\u9690\uff0c\u884c\u4e8b\u6781\u4e3a\u602a\u8bde\uff0c\u75c5\u4eba\u5343\u65b9\u767e\u8ba1\u60f3\u8bf7\u4ed6\u672a\u5fc5\u8bf7\u5f97\u5230\uff0c\u4ed6\u5374\u559c\u6b22\u627e\u4e0a\u95e8\u53bb\u66ff\u4eba\u533b \u75c5\uff0c\u4e8e\u627f\u73e0\u4ee5\u4e3a\u8fd9\u4eba\u65e9\u5df2\u6b7b\u4e86\uff0c\u6599\u4e0d\u5230\u773c\u524d\u8fd9\u4e2a\u8001\u5112\u751f\u5c31\u662f\u4ed6\uff01 \u3000\u3000\u8fd9\u4e8b\u60c5\u5df2\u662f\u751a\u602a\u3002\u4e8e\u627f\u73e0\u773c\u5149\u4e00\u77a5\uff0c\u518d\u770b\u5230\u58c1\u4e0a\u60ac\u6302\u7684\u5bf9\u8054\u548c\u6761\u5e45\uff0c\u66f4\u662f\u60ca\u5947\u5f97\u7591\u5728\u68a6 \u4e2d\uff01 \u3000\u3000\u5899\u4e0a\u6240\u6302\u7684\u90a3\u5e45\u5bf9\u8054\u662f\uff1a\u201c\u67f3\u7d6e\u6d6e\u840d\u6e38\u5b50\u610f\uff0c\u6843\u82b1\u6f6d\u6c34\u6545\u4eba\u60c5\u3002\u201d\u6761\u5e45\u4e0a\u5199\u7684\u5219\u662f\u82cf\u4e1c \u5761\u7684\u4e24\u9619\u6d63\u6eaa\u6c99\uff0c\u8bcd\u9053\uff1a\u201c\u9189\u68a6\u660f\u660f\u6653\u672a\u82cf\uff0c\u95e8\u524d\u8f98\u8f98\u4f7f\u541b\u8f66\uff0c\u6756\u5934\u4e00\u94b1\u600e\u751f\u65e0\uff1f\u5e9f\u5703\u5bd2\u852c \u6843\u7fe0\u7fbd\uff0c\u5c0f\u69fd\u6625\u9152\u6ef4\u771f\u73e0\uff0c\u6e05\u9999\u7ec6\u7ec6\u56bc\u6885\u987b\u3002\u201d\u201c\u5c71\u4e0a\u5170\u7259\u77ed\u6d78\u6eaa\uff0c\u677e\u95f4\u6c99\u8def\u51c0\u65e0\u6ce5\uff01\u8427\u8427 \u66ae\u96e8\u5b50\u89c4\u557c\uff0c\u96be\u9053\u4eba\u751f\u65e0\u518d\u5c11\uff1f\u95e8\u524d\u6d41\u6c34\u5c1a\u80fd\u897f\uff0c\u4f11\u5c06\u767d\u53d1\u5531\u9ec4\u9e21\u3002\u201d\u8054\u8bed\u548c\u6761\u5e45\u4e00\u8bf4\u4e0e\u6b64 \u95f4\u4e3b\u4eba\u7684\u4ea4\u60c5\uff0c\u4e00\u8bf4\u4e3b\u4eba\u5c71\u5c45\u7684\u9690\u9038\u60c5\u8da3\uff0c\u672c\u6765\u4ea6\u5c5e\u5bfb\u5e38\uff0c\u4ee4\u4e8e\u627f\u73e0\u60ca\u8be7\u4e07\u5206\uff0c\u7591\u771f\u7591\u5e7b\u7684 \u662f\uff1a\u8fd9\u8054\u8bed\u548c\u6761\u5e45\u7684\u5b57\u8ff9\uff0c\u7adf\u7136\u4e0e\u970d\u5929\u90fd\u7684\u4e00\u6a21\u4e00\u6837\u3002 \u3000\u3000\u90a3\u897f\u5c71\u533b\u9690\u53f6\u5143\u7ae0\u6b63\u5728\u5f00\u59cb\u52a8\u624b\u66ff\u90d1\u957f\u8001\u525c\u6389\u8150\u8089\uff0c\u542c\u5f97\u4e8e\u627f\u73e0\u60ca\u53eb\u4e4b\u58f0\uff0c\u7709\u5934\u4e00\u76b1\u8bf4 \u9053\uff1a\u201c\u4f60\u5927\u60ca\u5c0f\u602a\u4e9b\u4ec0\u4e48\uff1f\u6562\u60c5\u662f\u5acc\u8fd9\u5b57\u5199\u5f97\u4e0d\u597d\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u201d\u53f6\u5143\u7ae0\u9053\uff1a \u201c\u65e2\u7136\u662f\u597d\u5c31\u4e0d\u8981\u56b7\uff0c\u4f60\u4e00\u56b7\u6211\u5c31\u533b\u4e0d\u597d\u4e86\uff01\u201d\u4e8e\u627f\u73e0\u6ee1\u9762\u901a\u7ea2\uff0c\u6697\u6697\u8d23\u5907\u81ea\u5df1\u53ea\u6653\u5f97\u5173\u5fc3 \u4e0e\u81ea\u5df1\u6709\u5bc6\u5207\u5173\u7cfb\u7684\u4eba\uff0c\u5bf9\u90d1\u957f\u8001\u7684\u4f24\u53cd\u800c\u758f\u5ffd\u4e86\u3002 \u3000\u3000\u597d\u4e0d\u5bb9\u6613\u7b49\u5f85\u897f\u5c71\u533b\u9690\u52a8\u5b8c\u4e86\u624b\u672f\uff0c\u90d1\u957f\u8001\u6c89\u6c89\u7761\u53bb\uff0c\u9762\u8272\u4ea6\u5df2\u6e10\u89c1\u7ea2\u6da6\uff0c\u4e8e\u627f\u73e0\u8fd9\u624d \u653e\u4e0b\u4e86\u5fc3\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u95ee\u9053\uff1a\u201c\u8fd9\u8054\u8bed\u548c\u6761\u5e45\u90fd\u6ca1\u6709\u4e0a\u6b3e\u4e0b\u6b3e\uff0c\u5374\u662f\u8c01\u4eba\u5199\u7684\uff1f\u201d \u3000\u3000\u53f6\u5143\u7ae0\u9053\uff1a\u201c\u770b\u4f60\u76f8\u8c8c\u6e05\u79c0\uff0c\u5b9e\u4e43\u5dfe\u5e3c\u987b\u7709\uff0c\u600e\u7684\u51fa\u8bed\u4fbf\u4fd7\uff1f\u5fd7\u540c\u9053\u5408\uff0c\u503e\u76d6\u76f8\u9022\uff0c\u4fbf \u6210\u77e5\u5df1\uff0c\u53c8\u4f55\u5fc5\u7d6e\u7d6e\u4e0d\u4f11\u5730\u95ee\u59d3\u9053\u540d\uff1f\u201d\u4e8e\u627f\u73e0\u8fd8\u662f\u7b2c\u4e00\u6b21\u7ed9\u522b\u4eba\u8bf4\u5979\u201c\u4fd7\u201d\uff0c\u5fcd\u7740\u6c14\u8bf4 \u9053\uff1a\u201c\u8fd9\u5b57\u597d\u50cf\u662f\u6211\u4e00\u4f4d\u670b\u53cb\u7684\u7b14\u8ff9\uff0c\u662f\u4ee5\u8bf7\u95ee\u8001\u4e08\u3002\u201d\u53f6\u5143\u7ae0\u9053\uff1a\u201c\u65e2\u7136\u662f\u4f60\u7684\u670b\u53cb\uff0c\u4f60 \u5e94\u8be5\u77e5\u9053\u4ed6\u7684\u540d\u5b57\uff0c\u95ee\u6211\u4f5c\u4ec0\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u4e0e\u4ed6\u8bb8\u4e45\u6ca1\u89c1\u9762\u4e86\uff0c\u4e0d\u77e5\u4ed6\u4ec0\u4e48\u65f6\u5019\u5230 \u8fc7\u8fd9\u91cc\uff1f\u8fd8\u60f3\u77e5\u9053\u5230\u5e95\u662f\u4e0d\u662f\u4ed6\uff1f\u201d\u53f6\u5143\u7ae0\u9053\uff1a\u201c\u82e5\u662f\u4f60\u65e9\u6765\u4e00\u6708\uff0c\u4fbf\u53ef\u4e0e\u4ed6\u89c1\u9762\uff0c\u4e5f\u597d\u5e2e \u6211\u7559\u4e00\u7559\u4ed6\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5927\u5403\u4e00\u60ca\uff0c\u7167\u51cc\u4e91\u51e4\u548c\u5927\u6f20\u795e\u72fc\u7684\u8bf4\u6cd5\uff0c\u51cc\u4e91\u51e4\u5728\u4e09\u5e74\u4e4b\u524d\u4e0e\u970d\u5929\u90fd\u5728\u6c99\u6f20\u7684\u98ce \u66b4\u4e2d\u5931\u6563\uff0c\u5927\u6f20\u795e\u72fc\u5728\u4e09\u5e74\u524d\u57cb\u4e86\u4e00\u4e2a\u5728\u6c99\u6f20\u4e2d\u5012\u6bd9\u7684\u5c11\u5e74\u4eba\uff0c\u82e5\u7136\u90a3\u5c11\u5e74\u4eba\u662f\u970d\u5929\u90fd\u7684 \u8bdd\uff0c\u90a3\u4e48\u970d\u5929\u90fd\u5728\u4e09\u5e74\u4e4b\u524d\u5c31\u5df2\u6b7b\u4e86\uff0c\u600e\u7684\u4e00\u6708\u4e4b\u524d\u8fd8\u80fd\u5728\u6b64\u95f4\uff1f\u5fcd\u4e0d\u4f4f\u53c8\u95ee\u9053\uff1a\u201c\u4ed6\u662f\u600e \u4e48\u6765\u7684\uff1f\u201d\u53f6\u5143\u7ae0\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u4ed6\u6765\u627e\u6211\uff0c\u662f\u6211\u627e\u4ed6\u6765\u7684\uff0c\u4ed6\u751f\u4e86\u4e00\u79cd\u602a\u75c5\uff0c\u6211\u4ece\u6765\u6ca1\u6709\u89c1 \u8fc7\uff0c\u662f\u4ee5\u5f3a\u8feb\u4ed6\u7ed9\u6211\u533b\u3002\u60f3\u4e0d\u5230\u4e00\u533b\u5c31\u597d\uff0c\u54c8\u54c8\uff0c\u8fd9\u5bf9\u8054\u548c\u6761\u5e45\u4fbf\u662f\u4ed6\u7ed9\u6211\u7684\u916c\u91d1\u3002\u597d\uff0c\u4f60 \u65e2\u7136\u7d6e\u7d6e\u4e0d\u4f11\u5730\u95ee\u6211\uff0c\u8fd9\u4e24\u4e2a\u53eb\u5316\u5b50\u8eab\u65e0\u957f\u7269\uff0c\u4f60\u662f\u4ed6\u4eec\u7684\u670b\u53cb\uff0c\u4f60\u6709\u4ec0\u4e48\u4e1c\u897f\u4ed8\u6211\u4f5c \u916c\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u53ea\u6015\u6211\u4e00\u51fa\u624b\u53c8\u662f\u4fd7\u7684\u3002\u201d\u53f6\u5143\u7ae0\u9053\uff1a\u201c\u4fd7\u4e0e\u4e0d\u4fd7\u8981\u770b\u8fc7\u65b9\u77e5\u3002\u201d\u4e8e\u627f \u73e0\u968f\u624b\u5f39\u51fa\u4e09\u6735\u91d1\u82b1\uff0c\u5d4c\u5728\u5899\u4e0a\uff0c\u9547\u7740\u5b57\u753b\u7684\u6a2a\u5934\uff0c\u7b11\u9053\uff1a\u201c\u91d1\u5b50\u94f6\u5b50\u8fd8\u4e0d\u4fd7\u4e48\uff1f\u201d\u53f6\u5143\u7ae0 \u5ffd\u5730\u6539\u5bb9\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u4fd7\uff0c\u4e0d\u4fd7\uff01\u539f\u6765\u4f60\u662f\u6563\u82b1\u5973\u4fa0\uff0c\u90a3\u4f4d\u5c11\u5e74\u4fa0\u58eb\u4e5f\u66fe\u63d0\u8fc7\u4f60\u7684\u540d \u5b57\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u8be7\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u4f1a\u63d0\u8d77\u6211\u7684\u540d\u5b57\uff1f\u201d\u53f6\u5143\u7ae0\u9053\uff1a\u201c\u8fd9\u4f4d\u5c11\u5e74\u4fa0\u58eb\u7ecf\u6211\u533b\u597d\u4e4b\u540e\uff0c \u65e0\u4ee5\u4e3a\u916c\uff0c\u77e5\u9053\u6211\u7231\u597d\u5b57\u753b\u548c\u5251\u672f\uff0c\u9664\u4e86\u7ed9\u6211\u5199\u4e0b\u8fd9\u526f\u5bf9\u8054\u548c\u6761\u5e45\u4e4b\u5916\uff0c\u5e76\u5728\u4e00\u4e2a\u6708\u767d\u98ce\u6e05 \u4e4b\u591c\uff0c\u4e3a\u6211\u821e\u5251\u795d\u5bff\uff0c\u5251\u672f\u795e\u5999\uff0c\u771f\u662f\u6765\u5982\u96f7\u9706\u9707\u6012\uff0c\u7f62\u5982\u6c5f\u6d77\u51dd\u5149\uff0c\u8001\u592b\u66fe\u89c1\u8fc7\u5404\u6d3e\u5251 \u6cd5\uff0c\u4e5f\u4e0d\u7981\u4e3a\u4ed6\u62cd\u6848\u53eb\u7edd\u3002\u4ed6\u821e\u5251\u4e4b\u540e\uff0c\u95ee\u8d77\u4e2d\u539f\u7684\u5251\u672f\u540d\u5bb6\uff0c\u6211\u8bf4\u5f53\u4eca\u4e4b\u4e16\uff0c\u9664\u4e86\u5f20\u4e39\u67ab \u5927\u4fa0\u4e4b\u5916\uff0c\u53ea\u8bba\u5251\u672f\uff0c\u53ea\u6015\u6ca1\u6709\u8c01\u80fd\u4e0e\u4ed6\u6297\u624b\u4e86\uff0c\u8fd9\u4f4d\u5c11\u5e74\u4fa0\u58eb\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\u4ed6\u8fd9\u6b21\u6765\u5230 \u4e2d\u539f\uff0c\u5c31\u6b63\u662f\u4e3a\u4e86\u5bfb\u5f20\u5927\u4fa0\u6307\u6559\u5251\u6cd5\u3002\u6211\u8bf4\uff0c\u542c\u6b66\u6797\u670b\u53cb\u6240\u8a00\uff0c\u5f20\u5927\u4fa0\u4e45\u5df2\u95ed\u95e8\u5c01\u5251\uff0c\u672a\u5fc5 \u80af\u89c1\u5ba2\u4eba\u3002\u4ed6\u4e5f\u8bf4\u66fe\u77e5\u6b64\u4e8b\uff0c\u4e0d\u8fc7\u542c\u8bf4\u5f20\u5927\u4fa0\u6709\u4e00\u4e2a\u8863\u94b5\u771f\u4f20\u7684\u5973\u5f1f\u5b50\uff0c\u4eba\u79f0\u6563\u82b1\u5973\u4fa0\uff0c\u82e5 \u7136\u89c1\u4e0d\u5230\u5f20\u5927\u4fa0\uff0c\u80fd\u89c1\u89c1\u4ed6\u7684\u5973\u5f1f\u5b50\u4e5f\u662f\u597d\u7684\u3002\u201d\u4e8e\u627f\u73e0\u60f3\u4e0d\u5230\u81ea\u5df1\u7684\u58f0\u540d\u5c45\u7136\u8fdc\u64ad\uff0c\u5fc3\u4e2d \u9887\u4e3a\u6b22\u559c\uff0c\u53f6\u5143\u7ae0\u7eed\u9053\uff1a\u201c\u8fd9\u4f4d\u5c11\u5e74\u4fa0\u571f\u63d0\u4e86\u4f60\u7684\u540d\u5b57\u4e4b\u540e\uff0c\u63a5\u7740\u5c31\u4ef0\u5929\u957f\u53f9\u3002\u201d\u4e8e\u627f\u73e0\u6014 \u4e86\u4e00\u6014\uff0c\u6115\u7136\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u4e3a\u4f55\uff1f\u201d\u53f6\u5143\u7ae0\u9053\uff1a\u201c\u4ed6\u6709\u4e00\u4f4d\u672a\u5a5a\u59bb\u5b50\uff0c\u79bb\u6563\u4e09\u5e74\uff0c\u751f\u6b7b\u4e0d \u77e5\u3002\u4ed6\u4ece\u6b66\u6797\u670b\u53cb\u53e3\u4e2d\uff0c\u77e5\u9053\u4f60\u662f\u4e00\u4e2a\u5c11\u5e74\u5973\u4fa0\uff0c\u6240\u4ee5\u63d0\u8d77\u4f60\u7684\u540d\u5b57\uff0c\u4fbf\u8054\u60f3\u8d77\u4ed6\u7684\u672a\u5a5a\u59bb \u5b50\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u82b3\u5fc3\u52a8\u8361\uff0c\u53f6\u6210\u6797\u7684\u5f71\u5b50\u53c8\u4e00\u6b21\u6cdb\u4e86\u4e0a\u6765\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u8fd9\u5c11\u5e74\u4fa0\u58eb \u9664\u4e86\u662f\u970d\u5929\u90fd\u4e4b\u5916\u518d\u65e0\u522b\u4eba\u3002\u82e5\u7136\u4ed6\u8fd8\u5728\u4e16\u95f4\uff0c\u82e5\u7136\u4ed6\u8fd8\u5728\u4e16\u95f4\u2026\u2026\u5440\uff0c\u90a3\u6211\u60f3\u64ae\u5408\u51cc\u59d0\u59d0 \u4e0e\u53f6\u6210\u6797\u7684\u59fb\u7f18\u5c82\u975e\u5f04\u5de7\u53cd\u62d9\u3002\u201d\u4e00\u65f6\u82b3\u5fc3\u5927\u4e71\uff0c\u4e00\u7247\u832b\u7136\u3002\u53ea\u542c\u5f97\u53f6\u5143\u7ae0\u53c8\u9053\uff0c\u201c\u53ef\u501f\u6211 \u7559\u4ed6\u4e0d\u4f4f\uff0c\u5728\u4e00\u4e2a\u6708\u524d\uff0c\u4ed6\u5df2\u8fdb\u516b\u8fbe\u5cad\u53bb\u4e86\uff0c\u8bf4\u662f\u8981\u53bb\u627e\u4e00\u4e2a\u6b66\u6797\u4e2d\u9690\u9038\u7684\u5f02\u4eba\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u53c8\u662f\u4e00\u6014\uff0c\u60f3\u8d77\u5927\u6f20\u795e\u72fc\u6240\u8bf4\uff0c\u4ed6\u5728\u6c99\u6f20\u4e2d\u6240\u57cb\u846c\u7684\u90a3\u4e2a\u5c11\u5e74\uff0c\u4e34\u6b7b\u524d\u4e5f\u6258\u4ed6\u5230 \u516b\u8fbe\u5cad\u53bb\u627e\u4eba\uff0c\u53ef\u60dc\u6ca1\u8bf4\u5b8c\u4fbf\u6b7b\u4e86\u3002\u90a3\u4e2a\u5c11\u5e74\u82e5\u4e0d\u662f\u970d\u5929\u90fd\uff0c\u4ed6\u4eec\u4e4b\u95f4\u53c8\u6709\u4ec0\u4e48\u7275\u8fde\uff1f\u4e8e\u627f \u73e0\u771f\u60f3\u8fdb\u516b\u8fbe\u5cad\u53bb\u5bfb\u8e2a\u89c5\u8ff9\uff0c\u6253\u7834\u8fd9\u4e2a\u7591\u56e2\uff0c\u53ef\u662f\u76ee\u524d\u4e3a\u4e86\u4e10\u5e2e\u4e0e\u6c5f\u5357\u4e49\u519b\u7684\u5927\u4e8b\uff0c\u5979\u5374\u4e0d \u80fd\u4e0d\u5148\u53bb\u8c12\u89c1\u5e08\u7236\u3002 \u3000\u3000\u90d1\u957f\u8001\u4f24\u52bf\u5927\u51cf\uff0c\u4f46\u8fd8\u4e0d\u4fbf\u8d70\u52a8\uff0c\u4e8e\u627f\u73e0\u4e0e\u6bd5\u613f\u7a77\u4fbf\u7559\u4ed6\u5728\u53f6\u5bb6\u533b\u6cbb\uff0c\u8f9e\u522b\u4e86\u53f6\u5143\u7ae0\uff0c \u7531\u5c0f\u864e\u5b50\u5e26\u8def\uff0c\u5230\u98de\u9f99\u9556\u5c40\u627e\u5f20\u4e39\u67ab\u3002\u8fd9\u5bb6\u9556\u5c40\u5750\u843d\u5728\u7687\u57ce\u9644\u8fd1\uff0c\u4e3b\u4eba\u9f99\u817e\u4e43\u662f\u5f20\u4e39\u67ab\u7684\u5fd8 \u5e74\u4e4b\u4ea4\u3002\u4e8e\u627f\u73e0\u4e00\u8fdb\u9556\u5c40\uff0c\u4fbf\u542c\u89c1\u5e08\u7236\u723d\u6717\u7684\u7b11\u58f0\u3002 \u3000\u3000\u9556\u5c40\u7684\u4eba\u5e26\u4e8e\u627f\u73e0\u7b49\u4e09\u4eba\u7ed5\u8fc7\u56de\u5eca\uff0c\u7a7f\u8fc7\u5ead\u9662\uff0c\u8d70\u5230\u4e00\u95f4\u53a2\u623f\u5916\u9762\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u7684\u58f0 \u97f3\u8bf4\u9053\uff1a\u201c\u4e39\u67ab\u4f4f\u5728\u6b64\u95f4\uff0c\u5012\u6559\u9f99\u9556\u5934\u53d7\u60ca\u4e86\uff01\u201d\u4e00\u4e2a\u7c97\u8c6a\u7684\u58f0\u97f3\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5f20\u5927\u4fa0\u8fd9\u662f \u54ea\u91cc\u8bdd\u6765\uff1f\u9f99\u67d0\u8c2c\u627f\u5f20\u5927\u4fa0\u4ee5\u77e5\u5df1\u76f8\u5f85\uff0c\u5c48\u819d\u8717\u5c45\uff0c\u9f99\u67d0\u5c31\u662f\u7c89\u8eab\u788e\u9aa8\uff0c\u8fd9\u4e00\u751f\u4e5f\u4e0d\u7b97\u767d\u6d3b \u4e86\u3002\u6015\u53ea\u6015\u5f20\u5927\u4fa0\u540d\u5934\u592a\u5927\uff0c\u5978\u4eba\u7aa5\u4f3a\uff0c\u82e5\u6709\u610f\u5916\uff0c\u6559\u9f99\u67d0\u5982\u4f55\u62c5\u5f53\u5f97\u8d77\uff0c\u662f\u4ee5\u4e0d\u5f97\u4e0d \u9632\u3002\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u6211\u770b\u8fd9\u73ed\u9001\u793c\u7684\u670b\u53cb\u5b9a\u662f\u5f53\u4e16\u82f1\u8c6a\uff0c\u54b1\u4eec\u5c82\u53ef\u5984\u81ea\u731c\u6d4b\u3002\u5f20\u67d0\u4e00\u5251\u6d6a \u6e38\uff0c\u4e94\u9675\u7ed3\u5ba2\uff0c\u9ad8\u58eb\u5f53\u524d\uff0c\u7109\u80fd\u6020\u6162\u3002\u5c31\u8bf7\u9f99\u9556\u5934\u5c06\u90a3\u51e0\u4f4d\u670b\u53cb\u7684\u539a\u8d50\u9001\u6765\uff0c\u5f85\u6211\u5199\u4e0b\u62dc\u5e16 \u56de\u793c\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u5934\u6697\u6697\u5600\u5495\uff0c\u60f3\u9053\uff1a\u201c\u5e08\u7236\u6b64\u6b21\u6765\u4eac\uff0c\u884c\u8e2a\u79d8\u5bc6\uff0c\u542c\u4ed6\u4eec\u8fd9\u756a\u5bf9\u8bdd\uff0c\u5e08\u7236\u7adf\u4e0d \u77e5\u9053\u9001\u793c\u7684\u662f\u8c01\u3002\u602a\u4e0d\u5f97\u9f99\u9556\u5934\u8981\u62c5\u5fc3\u4e86\u3002\u201d\u53eb\u4e86\u4e00\u58f0\u201c\u5e08\u7236\u201d\uff0c\u63ed\u5e18\u800c\u5165\uff0c\u53ea\u89c1\u4e00\u4e2a\u7d2b\u8138 \u77a0\u7684\u6c49\u5b50\u5750\u5728\u5e08\u7236\u5bf9\u9762\uff0c\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u627f\u73e0\uff0c\u4f60\u4e5f\u6765\u4e86\u4e48\uff1f\u55ef\uff0c\u8fd9\u4f4d\u662f\u2014\u2014\u201d\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u8fd9\u4f4d\u662f\u4e10\u5e2e\u7684\u6bd5\u5927\u54e5\u3002\u201d\u6bd5\u613f\u7a77\u5531\u4e86\u4e2a\u558f\uff0c\u9053\uff1a\u201c\u4e10\u5e2e\u5f1f\u5b50\u6bd5\u613f\u7a77\u53c2\u89c1\u5f20\u5927\u4fa0\u3002\u201d\u5f20\u4e39\u67ab \u56de\u4e86\u4e2a\u793c\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e10\u5e2e\u5e72\u5f97\u8f70\u8f70\u70c8\u70c8\uff0c\u4e39\u67ab\u94a6\u4f69\u5f97\u7d27\u3002\u8fd9\u4f4d\u9f99\u9556\u5934\uff0c\u4f60\u6ca1\u89c1\u8fc7\u5427\uff1f\u201d \u3000\u3000\u6bd5\u613f\u7a77\u4e0e\u4e8e\u627f\u73e0\u4e0a\u524d\u89c1\u8fc7\u4e86\u9f99\u817e\uff0c\u5404\u9053\u4ef0\u6155\uff0c\u9f99\u817e\u9053\uff1a\u201c\u5f20\u5927\u4fa0\u4e0e\u6bd5\u7237\u6162\u53d9\uff0c\u9f99\u67d0\u53bb\u53bb \u5c31\u6765\u3002\u201d\u4e8e\u627f\u73e0\u60f3\u4ed6\u662f\u53bb\u53d6\u90a3\u201c\u793c\u7269\u201d\uff0c\u89c1\u4ed6\u9762\u6709\u5fe7\u8272\uff0c\u6599\u77e5\u8fd9\u91cc\u9762\u5b9a\u6709\u8e4a\u8df7\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e10\u5e2e\u6628\u665a\u5728\u79d8\u9b54\u5ca9\u805a\u4f1a\uff0c\u6211\u6ca1\u6709\u4eb2\u4e34\u9053\u8d3a\uff0c\u6211\u8fd9\u987d\u5f92\u6ca1\u6709\u9a9a\u6270\u4f60\u4eec \u5427\uff1f\u201d\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u591a\u8c22\u8fd9\u4f4d\u5c0f\u4fa0\u5e2e\u5fd9\uff0c\u8981\u4e0d\u7136\u6211\u53ea\u6015\u65e0\u7f18\u89c1\u5230\u5f20\u5927\u4fa0\u4e86\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a \u201c\u8fd9\u662f\u4e8e\u59d0\u59d0\u91d1\u82b1\u7684\u529f\u52b3\uff0c\u6211\u5e2e\u5f97\u4e86\u4ec0\u4e48\u5fd9\uff01\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u6bd5\u613f\u7a77\u9053\uff1a \u201c\u655d\u5e2e\u4e0d\u5e78\uff0c\u906d\u9022\u60e8\u53d8\uff0c\u6b63\u8981\u8bf7\u5f20\u5927\u4fa0\u6307\u70b9\u8ff7\u6d25\u3002\u201d\u4ed6\u867d\u751f\u6027\u8bd9\u8c10\u4e0d\u7f81\uff0c\u60f3\u8d77\u5e2e\u4e2d\u60e8\u53d8\uff0c\u5728 \u5f20\u4e39\u67ab\u9762\u524d\uff0c\u5fcd\u4e0d\u4f4f\u773c\u6cea\u7c0c\u7c0c\u800c\u4e0b\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5fae\u73b0\u8be7\u8272\uff0c\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u4eec\u8001\u5e2e\u4e3b\u6bd5\u9053\u51e1\u662f\u5fd8\u5e74\u4e4b\u4ea4\uff0c\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4f60\u5c3d\u7ba1 \u8bf4\u3002\u201d\u6bd5\u613f\u7a77\u5c06\u6bd5\u64ce\u5929\u4e0e\u671d\u5ef7\u8bae\u548c\u53db\u5e2e\u6c42\u8363\u4e4b\u4e8b\u4e00\u4e00\u8bf4\u4e86\uff0c\u5f20\u4e39\u67ab\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8270\u96be\u65b9 \u81ea\u89c1\u82f1\u96c4\uff01\u6bd5\u64ce\u5929\u4ee5\u82f1\u96c4\u81ea\u8bb8\uff0c\u5374\u5728\u5175\u8d25\u5371\u56f0\u4e4b\u65f6\u53d8\u8282\uff0c\u771f\u771f\u975e\u6211\u59cb\u6599\u6240\u53ca\u3002\u5440\uff0c\u9707\u4e09\u754c\u6bd5 \u9053\u51e1\u751f\u524d\u4f55\u7b49\u82f1\u8c6a\uff0c\u6bd5\u64ce\u5929\u5c06\u6765\u6709\u4f55\u9762\u76ee\u89c1\u4ed6\u7236\u4eb2\u4e8e\u5730\u4e0b\u3002\u201d\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u987e\u5b5f\u7ae0\u65e2 \u7136\u89c1\u8fc7\u4e86\u9633\u5b97\u6d77\uff0c\u6bd5\u64ce\u5929\u4e0e\u671d\u5ef7\u8bae\u548c\u4e4b\u4e8b\u65e0\u53ef\u633d\u56de\u3002\u4f46\u4ed6\u4eec\u4fe1\u4f7f\u867d\u901a\uff0c\u8bae\u548c\u5c1a\u9700\u65f6\u65e5\uff0c\u552f\u4eca \u4e4b\u8ba1\uff0c\u53ea\u6709\u8bf7\u4f60\u4eec\u4e10\u5e2e\u5feb\u9a6c\u8d77\u56de\u5357\u8fb9\uff0c\u53eb\u5e2e\u4e2d\u5b50\u5f1f\u4e0e\u53f6\u6210\u6797\u5408\u6d41\uff0c\u5373\u7b97\u4e0d\u80fd\u633d\u56de\u5927\u5c40\uff0c\u4e5f\u53ef \u907f\u514d\u635f\u4f24\u3002\u5f85\u98ce\u6d6a\u7a0d\u5e73\uff0c\u6211\u518d\u66ff\u4f60\u4eec\u51fa\u5934\uff0c\u53e6\u7acb\u5e2e\u4e3b\u3002\u201d\u6bd5\u613f\u7a77\u4e00\u60f3\uff0c\u4e5f\u53ea\u6709\u6b64\u6cd5\uff0c\u4e0d\u5f85\u9f99 \u817e\u56de\u6765\uff0c\u4fbf\u5306\u5306\u544a\u8f9e\u800c\u51fa\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u6ee1\u6000\u5fc3\u4e8b\uff0c\u6b63\u60f3\u5411\u5e08\u7236\u7980\u544a\uff0c\u53ea\u542c\u5f97\u5e08\u6bcd\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u73e0\u513f\u662f\u4f60\u6765\u4e86\u4e48\uff1f\u201d\u95e8 \u5e18\u4e00\u63ed\uff0c\u4e91\u857e\u7f13\u7f13\u8d70\u5165\uff0c\u4e00\u89c1\u4e8e\u627f\u73e0\uff0c\u5c31\u5c06\u5979\u63fd\u5165\u6000\u4e2d\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u597d\u50cf\u5a07\u5973\u89c1\u4e86\u4e45\u522b\u7684\u6bcd\u4eb2\u4e00\u6837\uff0c\u8eb2\u8fdb\u4e91\u857e\u6000\u4e2d\uff0c\u773c\u6cea\u7981\u4e0d\u4f4f\u593a\u7736\u800c\u51fa\uff0c\u4e91\u857e\u8f7b\u629a \u5979\u7684\u5934\u53d1\uff0c\u67d4\u58f0\u95ee\u9053\uff1a\u201c\u73e0\u513f\uff0c\u4f60\u53d7\u4e86\u4ec0\u4e48\u59d4\u5c48\u4e86\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u201d\u4e91\u857e\u9053\uff1a \u201c\u94c1\u955c\u5fc3\u5462\uff1f\u542c\u8bf4\u4ed6\u4e0e\u4f60\u4e00\u9053\u6765\u4eac\uff0c\u600e\u4e0d\u89c1\u4ed6\uff1f\u201d\u4e8e\u627f\u73e0\u5fc3\u4e2d\u9178\u695a\uff0c\u9053\uff1a\u201c\u4ed6\uff0c\u4ed6\uff0c\u6211\u4e0e\u4ed6 \u5404\u8d70\u5404\u7684\u8def\u5566\u3002\u201d\u773c\u6cea\u53c8\u7981\u4e0d\u4f4f\u7c0c\u7c0c\u800c\u4e0b\uff0c\u4e91\u857e\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u75f4\u5b69\u5b50\uff0c\u5c11\u5e74\u4eba\u5435\u5435\u67b6\u4e8b\u6781\u5bfb \u5e38\uff0c\u8fd9\u4e5f\u503c\u5f97\u54ed\u4e48\uff1f\u5f53\u5e74\u6211\u548c\u4f60\u7684\u5e08\u7236\u5c31\u4e0d\u77e5\u591a\u5c11\u6b21\u95f9\u5f97\u51e0\u4e4e\u51b3\u88c2\u4e86\u5462\uff01\u201d\u5728\u82cd\u5c71\u4e4b\u65f6\uff0c\u4e91 \u857e\u5c61\u6b21\u89c1\u94c1\u955c\u5fc3\u5411\u4e8e\u627f\u73e0\u5927\u732e\u6bb7\u52e4\uff0c\u8fd8\u53ea\u5f53\u94c1\u955c\u5fc3\u662f\u5979\u7684\u610f\u4e2d\u4eba\uff0c\u54ea\u77e5\u4ed6\u4eec\u4e4b\u95f4\u5374\u59cb\u7ec8\u662f\u8c8c \u5408\u795e\u79bb\u3002\u4e8e\u627f\u73e0\u54fd\u54bd\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u662f\u666e\u901a\u7684\u51b3\u88c2\uff0c\u4ed6\u5c06\u4e49\u519b\u7684\u519b\u60c5\u6cc4\u9732\u7ed9\u4e86\u5b98\u5bb6\u77e5\u9053\u3002\u201d \u5f20\u4e39\u67ab\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u94c1\u955c\u5fc3\u867d\u7136\u4e66\u751f\u6c14\u8d28\u592a\u91cd\uff0c\u770b\u6765\u5374\u8fd8\u4e0d\u662f\u8fd9\u6837\u7684\u4eba\uff0c\u8fd9\u662f\u600e\u4e48\u56de \u4e8b\uff1f\u201d\u4e8e\u627f\u73e0\u5c06\u676d\u5dde\u90a3\u4e00\u665a\u7684\u7ecf\u8fc7\u8bf4\u4e86\uff0c\u5f20\u4e39\u67ab\u53f9\u9053\uff1a\u201c\u539f\u6765\u4ed6\u662f\u4e3a\u4e86\u7ef4\u62a4\u7236\u4eb2\u548c\u4f60\uff0c\u4f60\u4ee5 \u524d\u5c06\u4ed6\u6bd4\u55bb\u4f5c\u6c5f\u5357\u56ed\u6797\u91cc\u7684\u73ab\u7470\u82b1\uff0c\u786e\u662f\u6709\u77e5\u4eba\u4e4b\u660e\uff0c\u4e00\u573a\u66b4\u98ce\u96e8\uff0c\u73ab\u7470\u82b1\u5c31\u5148\u51cb\u8c22\u4e86\u3002\u90a3 \u4e48\uff0c\u53f6\u6210\u6797\u5462\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4ed6\u5728\u5c6f\u6eaa\u72ec\u6297\u5341\u4e07\u5b98\u519b\u3002\u201d\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u773c\u4e2d\u6d41\u9732\u559c\u60a6\u3002\u5f20\u4e39 \u67ab\u7b11\u9053\uff1a\u201c\u90a3\u8fd8\u597d\uff0c\u73ab\u7470\u8c22\u4e86\uff0c\u8fd8\u6709\u5927\u9752\u6811\u6297\u7740\u72c2\u98ce\u66b4\u96e8\u5462\uff01\u201d\u4e8e\u627f\u73e0\u60f3\u7740\u53f6\u6210\u6797\u5904\u5883\u7684\u5371 \u9669\uff0c\u6b22\u60a6\u4e4b\u60c5\u970e\u53c8\u53d8\u4e3a\u5fe7\u60e7\uff0c\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u5f85\u8fd9\u91cc\u4e8b\u60c5\u4e00\u5b8c\uff0c\u6211\u548c\u4f60\u627e\u53f6\u6210\u6797\u53bb\u3002\u201d\u4e8e\u627f \u73e0\u5fc3\u4e2d\u7a0d\u7a0d\u5b89\u6170\uff0c\u4f46\u60f3\u8d77\u5176\u4e2d\u7684\u8bb8\u591a\u8bef\u4f1a\uff0c\u53c8\u7981\u4e0d\u4f4f\u9eef\u7136\u795e\u4f24\u3002 \u3000\u3000\u4e91\u857e\u9053\uff1a\u201c\u5c11\u5e74\u4eba\u591a\u7ecf\u4e00\u4e9b\u6298\u78e8\u4e5f\u672a\u5c1d\u6ca1\u6709\u597d\u5904\u3002\u55ef\uff0c\u542c\u8bf4\u6709\u4eba\u7ed9\u4f60\u9001\u793c\uff0c\u662f\u4ec0\u4e48\u4e1c \u897f\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u55ef\uff0c\u4f60\u77a7\uff0c\u9f99\u9556\u5934\u5c06\u793c\u7269\u62ff\u6765\u4e86\u3002\u201d \u3000\u3000\u53ea\u89c1\u9f99\u817e\u63d0\u7740\u4e00\u4e2a\u7ea2\u6f06\u91d1\u76d2\u8fdb\u6765\uff0c\u4e0a\u9762\u63cf\u91d1\u6f06\u5b57\u5199\u7740\uff1a\u201c\u656c\u5448\u5f20\u5927\u4fa0\u6652\u7eb3\u3002\u201d\u4e91\u857e\u9053\uff1a \u201c\u9001\u793c\u7684\u4eba\u5462\uff1f\u201d\u9f99\u817e\u9053\uff1a\u201c\u4eca\u65e5\u9556\u5c40\u5f00\u95e8\uff0c\u8fd9\u91d1\u76d2\u5c31\u6446\u5728\u5927\u5385\u6b63\u4e2d\u7684\u684c\u5b50\u4e0a\u4e86\u3002\u201d\u4e91\u857e\u5fc3 \u4e2d\u6697\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u9556\u5c40\u4e4b\u4e2d\u597d\u624b\u751a\u591a\uff0c\u8fd9\u4eba\u5c45\u7136\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u5730\u9001\u793c\u8fdb\u6765\uff0c\u53ef\u771f\u662f\u6709\u70b9\u90aa \u95e8\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5374\u4f3c\u4e1d\u6beb\u4e0d\u4ee5\u4e3a\u610f\uff0c\u4e00\u7b11\u8bf4\uff1a\u201c\u65e2\u627f\u539a\u8d50\uff0c\u5c82\u6562\u63a8\u8f9e\u3002\u201d\u9f99\u817e\u201c\u5c0f\u5fc3\u201d\u4e8c\u5b57\u8fd8\u672a \u8bf4\u51fa\uff0c\u4ed6\u5df2\u4e00\u4e0b\u5b50\u5c06\u76d2\u76d6\u63ed\u5f00\uff0c\u53ea\u89c1\u91cc\u9762\u6446\u7740\u56db\u5f0f\u82cf\u5dde\u5f0f\u7684\u7cd5\u997c\u70b9\u5fc3\uff0c\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d \u670b\u53cb\u771f\u662f\u53ef\u4eba\uff0c\u963f\u857e\uff0c\u6628\u665a\u6211\u521a\u548c\u4f60\u8bf4\u8d77\u82cf\u5f0f\u70b9\u5fc3\uff0c\u8bf4\u662f\u548c\u4eac\u90fd\u7684\u5404\u6709\u98ce\u5473\uff0c\u4f60\u8bf4\u4f60\u66f4\u559c\u6b22 \u82cf\u5dde\u7684\uff0c\u4eca\u65e9\u4ed6\u5c31\u9001\u6765\u4e86\u3002\u201d\u9f99\u817e\u66f4\u662f\u5403\u60ca\uff0c\u8bd5\u60f3\u5f20\u4e39\u67ab\u592b\u5987\u662f\u4f55\u7b49\u672c\u9886\uff0c\u7adf\u6709\u4eba\u5077\u542c\u4e86\u4ed6 \u4eec\u7684\u8bf4\u8bdd\u800c\u4e0d\u88ab\u53d1\u89c9\uff0c\u8fd9\u5c82\u975e\u4e00\u5927\u5947\u4e8b\uff1f\u4f46\u89c1\u5f20\u4e39\u67ab\u7adf\u662f\u6beb\u65e0\u987e\u5fcc\uff0c\u968f\u624b\u62c8\u8d77\u4e00\u4ef6\u9001\u5165\u53e3 \u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u5730\u9053\u7684\u82cf\u5f0f\u70b9\u5fc3\u3002\u4e91\u59b9\uff0c\u4f60\u4e5f\u5c1d\u4ed6\u4e00\u4ef6\u3002\u201d\u4e8e\u627f\u73e0\u4e00\u773c\u671b\u53bb\u53ea\u89c1\u76d2 \u4e2d\u7684\u5927\u7ea2\u62dc\u5e16\uff0c\u7f72\u540d\u662f\u201c\u516b\u8fbe\u5c71\u4eba\u201d\uff0c\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8fd8\u672a\u51fa\u58f0\uff0c\u53ea\u542c\u5f97\u5916\u9762\u4e00\u7247\u55a7\u95f9\uff0c \u6709\u4eba\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u6709\u4e00\u4f4d\u516c\u7239\u6c42\u89c1\u5f20\u5927\u4fa0\uff01\u201d\u9f99\u817e\u5927\u60ca\u5931\u8272\uff0c\u4e91\u857e\u4e5f\u76b1\u4e86\u53cc\u7709\uff0c\u5fc3\u9053\uff1a\u201c\u96be \u9053\u662f\u9001\u793c\u7684\u4eba\u6765\u4e86\uff1f\u5bab\u95e8\u4e2d\u4eba\u7adf\u6709\u8fd9\u6837\u7684\u8eab\u624b\uff1f\u201d\u5979\u62c8\u8d77\u4e00\u4ef6\u7cd5\u997c\uff0c\u5374\u4e0d\u6562\u5403\u5b83\u3002\u5f20\u4e39\u67ab\u4ecd \u662f\u795e\u8272\u81ea\u5982\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4e91\u59b9\uff0c\u54b1\u4eec\u4eca\u6b21\u5165\u4eac\uff0c\u672c\u610f\u4e0d\u6b32\u60ca\u52a8\u5404\u65b9\u670b\u53cb\uff0c\u60f3\u4e0d\u5230\u65e2\u6709\u9ad8\u8d24 \u9001\u793c\uff0c\u53c8\u6709\u5b98\u7237\u4e0b\u987e\uff0c\u5f53\u771f\u662f\u4ea4\u4e86\u8fd0\u4e86\u3002\u201d\u4e91\u857e\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u4eec\u662f\u4e24\u62e8 \u4eba\uff1f\u201d\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u9762\u5411\u9f99\u817e\u7b11\u9053\uff1a\u201c\u5b98\u5e9c\u5c48\u9a7e\u5149\u4e34\uff0c\u6211\u4e0d\u53bb\u8fce\u63a5\u5df2\u662f\u6258\u5927\uff0c\u600e\u597d\u963b\u62e6\uff0c\u5c31 \u8ba9\u4ed6\u4eec\u8fdb\u6765\u5427\u3002\u201d\u9f99\u817e\u89c1\u5f20\u4e39\u67ab\u8a00\u7b11\u81ea\u5982\uff0c\u65e9\u4f3c\u80f8\u6709\u6210\u7af9\uff0c\u5fc3\u4e2d\u4e5f\u5b9a\u4e86\u4e00\u534a\uff0c\u4fbf\u5429\u5490\u4e0b\u53bb\uff0c \u53eb\u9556\u5c40\u7684\u4f19\u8ba1\u8ba9\u90a3\u4eba\u8fdb\u6765\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u6293\u8d77\u7eb8\u7b14\uff0c\u5306\u5306\u5199\u4e86\u4e00\u4e2a\u8c22\u5e16\uff0c\u7b11\u9053\uff1a\u201c\u516b\u8fbe\u5c71\u4eba\u4e4b\u7ea6\uff0c\u53ea\u597d\u8fdf\u51e0\u5929\u4e86\u3002\u201d\u5728\u5e72 \u679c\u76d2\u4e2d\u968f\u624b\u6293\u4e86\u4e00\u628a\u9f99\u773c\uff0c\u585e\u5230\u5c0f\u864e\u5b50\u624b\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u998b\u5634\u7684\u5c0f\u5bb6\u4f19\u600e\u4e48\u53cd\u505c\u4e86\u5634\u4e86\u3002 \u8fdb\u91cc\u9762\u53bb\u5403\u5427\u3002\u201d\u539f\u6765\u5f20\u4e39\u67ab\u89c1\u9556\u5c40\u4e2d\u7684\u6c14\u6c1b\u592a\u8fc7\u7d27\u5f20\uff0c\u5c0f\u864e\u5b50\u634f\u62f3\u77aa\u773c\uff0c\u66f4\u662f\u8dc3\u8dc3\u6b32\u8bd5\uff0c \u6545\u6b64\u8bf4\u4e86\u51e0\u53e5\u8f7b\u677e\u7684\u8bdd\u513f\uff0c\u5e76\u5c06\u4ed6\u9063\u5f00\u3002 \u3000\u3000\u53a2\u623f\u7684\u95e8\u65e9\u5df2\u6253\u5f00\uff0c\u53ea\u89c1\u4e00\u4e2a\u7a7f\u7740\u5fa1\u6797\u519b\u670d\u9970\u7684\u6b66\u58eb\uff0c\u8e0f\u7740\u6c89\u91cd\u7684\u811a\u6b65\uff0c\u201c\u683c\u767b\u3001\u683c \u767b\u201d\u5730\u8d70\u4e86\u8fdb\u6765\uff0c\u6bcf\u8d70\u4e00\u6b65\uff0c\u9636\u7816\u4e0a\u5c31\u7559\u4e0b\u4e00\u4e2a\u8db3\u5370\uff0c\u5f20\u4e39\u67ab\u77e5\u4ed6\u6709\u610f\u70ab\u8000\u6b66\u529f\uff0c\u5fae\u7b11\u4e0d \u8bed\u3002 \u3000\u3000\u8fd9\u6b66\u58eb\u540d\u5524\u9f50\u5c01\uff0c\u662f\u5fa1\u6797\u519b\u4e94\u864e\u5c06\u4e4b\u4e00\uff0c\u6b66\u529f\u4ec5\u5728\u9633\u5b97\u6d77\u3001\u5a04\u6850\u5b59\u4e4b\u4e0b\uff0c\u800c\u5728\u4e1c\u65b9\u6d1b\u4e4b \u4e0a\uff0c\u6602\u6602\u7136\u5730\u8d70\u4e0a\u53f0\u9636\uff0c\u626c\u58f0\u8bf4\u9053\uff1a\u201c\u54ea\u4f4d\u662f\u5f20\u4e39\u67ab\uff1f\u5feb\u6452\u9000\u5de6\u53f3\uff0c\u524d\u6765\u63a5\u65e8\uff01\u201d\u8bdd\u58f0\u672a \u4e86\uff0c\u5ffd\u542c\u5f97\u5899\u5916\u4e00\u58f0\u51b7\u7b11\uff0c\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\u9707\u4eba\u5fc3\u9b44\uff0c\u9661\u7136\u95f4\u51e0\u652f\u91d1\u9556\u6253\u4e86\u8fdb\u6765\uff0c\u9f50\u5c01\u5927\u6012\u559d \u9053\uff1a\u201c\u53cd\u4e86\uff0c\u53cd\u4e86\uff01\u201d\u53cc\u638c\u4e00\u63a8\uff0c\u638c\u98ce\u547c\u547c\uff0c\u8fce\u7740\u6697\u5668\u7684\u65b9\u5411\u6253\u51fa\uff0c\u9f50\u5c01\u7ec3\u7684\u662f\u201c\u4f0f\u9b54\u638c\u201d \u7684\u529f\u592b\uff0c\u638c\u529b\u96c4\u52b2\uff0c\u54ea\u5c06\u8fd9\u79cd\u5bfb\u5e38\u7684\u91d1\u9556\u6697\u5668\u653e\u5728\u773c\u5185\uff0c\u6ee1\u4ee5\u4e3a\u4e00\u638c\u4fbf\u53ef\u51fb\u843d\uff0c\u54ea\u77e5\u638c\u529b\u53d1 \u51fa\uff0c\u90a3\u51e0\u652f\u91d1\u9556\u6765\u52bf\u867d\u7136\u7a0d\u7f13\uff0c\u5374\u5206\u5f00\u4ece\u4e94\u4e2a\u65b9\u5411\u6253\u6765\uff0c\u56db\u89d2\u548c\u4e2d\u592e\u90fd\u6709\u91d1\u9556\u5c04\u5230\uff0c\u7adf\u628a\u9f50 \u5c01\u7684\u8eab\u5f62\u90fd\u7b3c\u7f69\u5728\u6697\u5668\u7684\u5a01\u529b\u4e4b\u5185\u3002\u9f50\u5c01\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u90a3\u53d1\u6697\u5668\u7684\u4eba\u8eab\u5728\u5899\u5916\uff0c\u5185\u529b\u7adf \u7136\u5982\u6b64\u5f3a\u52b2\uff0c\u4e0d\u5355\u81ea\u5df1\u7684\u638c\u529b\u5c01\u95ed\u4e0d\u4f4f\uff0c\u6b64\u65f6\u8fde\u8eb2\u5f00\u4e5f\u4e0d\u53ef\u80fd\u4e86\uff01 \u3000\u3000\u773c\u89c1\u90a3\u51e0\u652f\u91d1\u9556\u5c31\u8981\u5c04\u5230\u9f50\u5c01\u8eab\u4e0a\uff0c\u5f20\u4e39\u67ab\u5ffd\u5730\u5fae\u5fae\u4e00\u7b11\uff0c\u968f\u624b\u6293\u4e86\u51e0\u7c92\u9f99\u773c\u6838\u6253\u51fa\uff0c \u6717\u58f0\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u5916\u9762\u7684\u670b\u53cb\u5173\u5fc3\uff0c\u4e39\u67ab\u81ea\u5df1\u4f1a\u77e5\u9053\u5e94\u4ed8\uff0c\u76db\u60c5\u5fc3\u9886\u4e86\u3002\u201d\u53ea\u542c\u5f97\u53ee\u5f53\u51e0 \u58f0\uff0c\u56db\u89d2\u5c04\u6765\u7684\u91d1\u9556\u5168\u7ed9\u9f99\u773c\u6838\u78b0\u8dcc\uff0c\u53ea\u6709\u4e2d\u592e\u7684\u90a3\u652f\u91d1\u9556\u4ecd\u5411\u9f50\u5c01\u7684\u592a\u9633\u7a74\u98de\u6765\u3002 \u3000\u3000\u4e91\u857e\u63a5\u7740\u7b11\u9053\uff1a\u201c\u9f50\u5927\u4eba\u522b\u52a8\uff0c\u4ee5\u514d\u8bef\u4f24\u3002\u201d\u4e5f\u5c06\u62c8\u5728\u624b\u4e0a\u7684\u90a3\u4ef6\u7cd5\u997c\u6253\u51fa\uff0c\u91d1\u9556\u88ab\u7cd5 \u997c\u4e00\u7c98\uff0c\u5c04\u5230\u8336\u51e0\u4e4b\u4e0a\uff0c\u8fde\u684c\u9762\u4e5f\u6ca1\u6709\u7559\u4e0b\u521b\u75d5\uff0c\u5f20\u4e39\u67ab\u592b\u5987\u8fd9\u624b\u6b66\u529f\u4e00\u663e\uff0c\u767b\u65f6\u628a\u9f50\u5c01\u5413 \u5f97\u9b42\u98de\u9b44\u6563\uff0c\u597d\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u4f46\u89c1\u5f20\u4e39\u67ab\u53c8\u628a\u90a3\u5f20\u8c22\u5e16\u5e73\u653e\u638c\u4e0a\uff0c\u9f13\u6c14\u4e00\u5439\uff0c\u90a3\u5f20\u8c22\u5e16\u7adf\u7136\u98de\u8fc7\u5899\u5934\uff0c\u5899\u5916\u6709\u58f0\u8d5e \u9053\uff1a\u201c\u597d\u529f\u592b\uff0c\u90a3\u4e48\u54b1\u4eec\u5728\u70b9\u5c06\u53f0\u518d\u89c1\u4e86\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u9f50\u5927\u4eba\u53d7\u60ca\u4e86\uff0c\u8bf7\u5750\u554a\uff01\u201d\u9f50\u5c01\u6218\u6218\u5162\u5162\uff0c\u54ea\u91cc\u6562\u5750\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a \u201c\u5fa1\u6797\u519b\u7edf\u9886\u9f50\u5c01\u5949\u65e8\u800c\u6765\uff0c\u53c2\u89c1\u5f20\u5927\u4fa0\uff0c\u8bf7\u5f20\u5927\u4fa0\u6452\u9000\u5de6\u53f3\u3002\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u662f\u4f60 \u7684\u4e0a\u53f8\uff0c\u4f60\u53c2\u89c1\u6211\u505a\u4ec0\u4e48\uff1f\u5750\u5440\uff0c\u4e91\u59b9\uff0c\u4f60\u548c\u627f\u73e0\u5230\u91cc\u9762\u53bb\u3002\u201d\u4f38\u51fa\u624b\u6765\u548c\u4e91\u857e\u8f7b\u8f7b\u4e00\u63e1\uff0c \u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u82cf\u5f0f\u70b9\u5fc3\u5f88\u597d\uff0c\u4f60\u7559\u4e0b\u4e24\u4ef6\u5f85\u6211\u56de\u6765\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d\u5ae3\u7136\u4e00\u7b11\uff0c \u643a\u4e86\u4e8e\u627f\u73e0\u8d70\u5165\u5185\u623f\u3002\u9f99\u817e\u89c1\u4e91\u857e\u672c\u6765\u795e\u8272\u5fe7\u8651\uff0c\u800c\u4eca\u5374\u4f3c\u4e00\u65e0\u7275\u6302\u5730\u79bb\u5f00\u5f20\u4e39\u67ab\uff0c\u6beb\u4e0d\u62c5 \u5fc3\uff0c\u751a\u662f\u7591\u60d1\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u9f99\u9556\u5934\u4e43\u662f\u6211\u7684\u597d\u53cb\uff0c\u5f85\u6211\u548c\u8001\u670b\u53cb\u8bf4\u51e0\u53e5\u8bdd\uff0c \u518d\u6765\u63a5\u65e8\uff0c\u4e5f\u4e0d\u8fdf\u5427\uff01\u201d\u9f50\u5c01\u90a3\u6562\u4e0d\u4f9d\uff0c\u4fa7\u7740\u534a\u8fb9\u8eab\u5b50\u5750\u4e0b\uff0c\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u9f50\u5927\u4eba\uff0c\u4f60\u4e0d\u5fc5 \u5ba2\u6c14\uff0c\u8bf7\u7528\u8336\u554a\uff0c\u5403\u4e24\u4ef6\u70b9\u5fc3\u3002\u201d\u8f6c\u8fc7\u5934\u5bf9\u9f99\u817e\u9053\uff1a\u201c\u9f99\u5927\u54e5\uff0c\u5c0f\u5f1f\u6709\u4e00\u4ef6\u4e1c\u897f\u7ed9\u4f60\u3002\u201d\u638f \u51fa\u4e00\u4e2a\u4fe1\u5c01\uff0c\u4ea4\u7ed9\u4e86\u9f99\u817e\uff0c\u9f99\u817e\u9000\u4e86\u4e0b\u53bb\uff0c\u62bd\u51fa\u4fe1\u6765\u4e00\u770b\uff0c\u53ea\u89c1\u91cc\u9762\u9644\u843d\u82cf\u5dde\u4e00\u4e2a\u6700\u8457\u540d\u94b1 \u5e84\u7684\u94f6\u7968\uff0c\u6570\u76ee\u5171\u662f\u4e09\u5341\u4e07\u4e24\u94f6\u5b50\uff0c\u4fe1\u4e0a\u6709\u4e24\u53e5\u8bdd\u9053\uff1a\u201c\u4e09\u65e5\u4e4b\u5185\uff0c\u8fd9\u9556\u5c40\u53ef\u4fdd\u65e0\u4e8b\u3002\u201d\u9f99 \u817e\u660e\u767d\u662f\u5f20\u4e39\u67ab\u53eb\u4ed6\u4ece\u901f\u5728\u4e09\u65e5\u4e4b\u5185\u9063\u6563\u9556\u884c\u4f19\u8ba1\uff0c\u8fd9\u94f6\u7968\u5728\u5317\u4eac\u7684\u94b1\u5e84\u4e5f\u53ef\u5151\u73b0\uff0c\u90a3\u81ea\u662f \u5f20\u4e39\u67ab\u7ed9\u4ed6\u4f5c\u9063\u6563\u4e4b\u7528\u7684\u4e86\u3002\u4ed6\u672c\u60f3\u4e0d\u53d7\uff0c\u4f46\u9556\u5c40\u4e2d\u7f3a\u4e4f\u73b0\u6b3e\uff0c\u53ea\u597d\u6253\u7b97\u5148\u884c\u7528\u4e86\uff0c\u7136\u540e\u518d \u56fe\u62a5\u7b54\u3002\u5fc3\u4e2d\u6697\u6697\u611f\u6fc0\u5f20\u4e39\u67ab\u60f3\u5f97\u5468\u5230\u3002\u60f3\u8d77\u4ed6\u6bcf\u4ef6\u4e8b\u60c5\u90fd\u4e25\u4f3c\u6d1e\u89c1\u5148\u673a\uff0c\u5fc3\u4e2d\u53c8\u5bbd\u4e86\u51e0 \u5206\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u4e0e\u9f50\u5c01\u8d70\u4e86\u51fa\u6765\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u770b\u6211\u8fd9\u6b21\u6765\u4eac\uff0c\u53ef\u771f\u662f\u4ea4\u4e86\u597d \u8fd0\u4e86\uff01\u4e0d\u4f46\u6709\u4eba\u9001\u793c\uff0c\u8fde\u5f53\u4eca\u7684\u7687\u4e0a\u4e5f\u8bf7\u6211\u8d74\u5bb4\u5462\u3002\u54c8\uff0c\u54c8\uff01\u9f99\u5927\u54e5\uff0c\u4f60\u597d\u559d\u9152\uff0c\u5f85\u6211\u5e26\u4e00 \u74f6\u5fa1\u9152\u56de\u6765\u7ed9\u4f60\u5c1d\u5c1d\u3002\u201d\u62cd\u4e00\u62cd\u8eab\u4e0a\u7684\u7070\u5c18\uff0c\u5c31\u50cf\u8d74\u4e00\u4e2a\u8001\u670b\u53cb\u7684\u9080\u5bb4\u4f3c\u7684\uff0c\u6f2b\u4e0d\u7ecf\u610f\u5730\u5c31 \u968f\u7740\u9f50\u5c01\u8d70\u4e86\u3002 \u3000\u3000\u5176\u5b9e\u5f20\u4e39\u67ab\u5fc3\u5185\u6b63\u81ea\u7ffb\u6765\u8986\u53bb\u5730\u76d8\u7b97\u8ba1\u8c0b\uff0c\u4ed6\u8fd9\u6b21\u6765\u4eac\uff0c\u672c\u6765\u5c31\u662f\u60f3\u627e\u4e00\u4e2a\u6700\u9002\u5f53\u7684\u673a \u4f1a\u9762\u89c1\u7687\u5e1d\u7948\u9547\uff0c\u597d\u6d88\u5f2d\u5927\u7406\u7684\u6218\u7978\uff0c\u5e76\u5b89\u6392\u4e2d\u56fd\u4e0e\u6ce2\u65af\u8054\u76df\u4e4b\u4e8b\uff0c\u53e6\u5916\u4e5f\u8fd8\u6709\u4e24\u4ef6\u4e8b\u60c5\u8981 \u4e0e\u7687\u5e1d\u9762\u8c08\uff0c\u4e0d\u8fc7\u4ed6\u4e5f\u6df1\u77e5\u7948\u9547\u5bf9\u4ed6\u6700\u4e3a\u5fcc\u6068\uff0c\u8fd9\u534a\u4e2a\u6708\u6765\uff0c\u4ed6\u5728\u4eac\u4e2d\u4e00\u5207\u7684\u5b89\u6392\uff0c\u5c31\u662f\u5728 \u5e03\u7f6e\u597d\u4e00\u4e2a\u6700\u9002\u5f53\u7684\u673a\u4f1a\uff0c\u60f3\u4e0d\u5230\u7948\u9547\u5df2\u5148\u77e5\u9053\u4e86\u4ed6\u7684\u8e2a\u8ff9\uff0c\u6d3e\u51fa\u6b66\u58eb\u6765\u9080\u8bf7\u4ed6\u8fdb\u5bab\u4e86\u3002 \u3000\u3000\u9556\u5c40\u9760\u8fd1\u7687\u57ce\uff0c\u4e0d\u8fc7\u534a\u4e2a\u65f6\u8fb0\uff0c\u9f50\u5c01\u5c31\u5e26\u4e86\u5f20\u4e39\u67ab\u4ece\u5fa1\u82b1\u56ed\u8fdb\u5165\uff0c\u7a7f\u8fc7\u4e86\u51e0\u5ea7\u5bab\u6bbf\uff0c\u76f4 \u5230\u4e07\u5bff\u9601\u524d\uff0c\u8fd9\u4e07\u5bff\u9601\u5728\u5fa1\u82b1\u56ed\u7684\u4e1c\u89d2\uff0c\u662f\u7687\u5e1d\u8d50\u5bb4\u8fd1\u81e3\u7684\u6240\u5728\uff0c\u8fd9\u65f6\u5df2\u8fd1\u9ec4\u660f\uff0c\u53ea\u89c1\u91cc\u9762 \u706f\u706b\u8f89\u714c\uff0c\u6446\u4e86\u4e09\u4e2a\u5e2d\u4f4d\uff0c\u7948\u9547\u5750\u5728\u4e0a\u5e2d\uff0c\u5de6\u9762\u7684\u4e00\u5e2d\u5750\u7684\u7ade\u662f\u4e91\u91cd\uff0c\u53f3\u9762\u4e00\u5e2d\u865a\u4f4d\u4ee5\u5f85\uff0c \u60f3\u5fc5\u662f\u7559\u7ed9\u81ea\u5df1\u7684\u4e86\u3002\u4e24\u4e2a\u6b66\u58eb\u4f8d\u7acb\uff0c\u5f20\u4e39\u67ab\u4e3e\u76ee\u4e00\u671b\uff0c\u7981\u4e0d\u4f4f\u5fc3\u4e2d\u5fae\u5fae\u4e00\u51db\u3002 \u3000\u3000\u53ea\u89c1\u5728\u7948\u9547\u7684\u4e24\u65c1\uff0c\u5206\u7ad9\u7740\u56db\u4e2a\u5e76\u4e0d\u7a7f\u7740\u6b66\u58eb\u670d\u9970\u7684\u4eba\uff0c\u4e00\u4e2a\u662f\u9053\u58eb\u88c5\u675f\uff0c\u5f20\u4e39\u67ab\u8ba4\u5f97 \u662f\u661f\u5bbf\u6d77\u7684\u6458\u661f\u4e0a\u4eba\uff0c\u4e00\u4e2a\u7a7f\u7740\u9ebb\u5e03\u5927\u8902\uff0c\u53ea\u6709\u4e00\u6761\u624b\u81c2\u7684\uff0c\u5219\u662f\u5c60\u9f99\u5c0a\u8005\uff0c\u4ed6\u7684\u53f3\u81c2\u4e43\u662f \u5728\u82cd\u5c71\u8f83\u6280\u4e4b\u65f6\uff0c\u88ab\u4e91\u91cd\u7528\u5927\u529b\u91d1\u521a\u624b\u62d7\u6298\u7684\uff0c\u8fd9\u65f6\u6b63\u864e\u89c6\u803d\u803d\u5730\u76ef\u7740\u4e91\u91cd\uff0c\u53e6\u5916\u4e24\u4e2a\u4e00\u4e2a \u662f\u56db\u5341\u5c81\u5de6\u53f3\u7684\u9b41\u68a7\u6c49\u5b50\uff0c\u5374\u7a7f\u7740\u4e00\u4ef6\u7ec9\u7eb1\u957f\u886b\uff0c\u5112\u51a0\u5112\u670d\u6253\u626e\u5f97\u4e0d\u4f26\u4e0d\u7c7b\uff0c\u8fde\u5f20\u4e39\u67ab\u4e5f\u4e0d \u77e5\u9053\u4ed6\u7684\u6765\u5386\uff1b\u8fd8\u6709\u4e00\u4e2a\u6700\u9760\u8fd1\u7687\u5e1d\u7684\u5374\u662f\u4e00\u4e2a\u8001\u5934\uff0c\u76f8\u8c8c\u751a\u662f\u7279\u522b\uff0c\u989d\u9aa8\u9ad8\u8038\uff0c\u592a\u9633\u7a74\u5fae \u5fae\u575f\u8d77\uff0c\u9e70\u9f3b\u6df1\u76ee\uff0c\u638c\u5fc3\u638c\u80cc\u90fd\u50cf\u6731\u7802\u4e00\u6837\u901a\u7ea2\u3002\u5f20\u4e39\u67ab\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u6458\u661f\u4e0a\u4eba\u548c\u5c60 \u9f99\u5c0a\u8005\u867d\u7136\u90fd\u53ef\u5217\u540d\u5f53\u4e16\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u81ea\u95ee\u8fd8\u53ef\u5bf9\u4ed8\u5f97\u4e86\u4ed6\u4eec\u3002\u770b\u8fd9\u8001\u5934\u513f\u7684\u6a21\u6837\uff0c\u4f3c\u4e4e\u662f \u4ee5\u5206\u7b4b\u9519\u9aa8\u624b\u79f0\u9738\u6b66\u6797\u7684\u8001\u6b66\u5e08\u77f3\u9e3f\u535a\uff0c\u5012\u4e0d\u53ef\u5c0f\u89c6\u4e86\u3002\u8fd9\u7c97\u6c49\u5b50\u770b\u6765\u4e5f\u662f\u4e00\u4e2a\u52b2\u654c\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5fc3\u4e2d\u6697\u6697\u6212\u5907\uff0c\u8138\u4e0a\u53ef\u6ca1\u6709\u9732\u51fa\u4e1d\u6beb\u795e\u8272\uff0c\u8d70\u4e0a\u4e86\u4e07\u5bff\u9601\uff0c\u53ea\u542c\u5f97\u7948\u9547\u5bf9\u9633\u5b97\u6d77 \u7b11\u9053\uff1a\u201c\u6211\u8bf4\u5f20\u5148\u751f\u4e00\u5b9a\u4f1a\u6765\uff0c\u4f60\u77a7\u6715\u6240\u6599\u4e0d\u5dee\u5427\u3002\u201d\u9633\u5b97\u6d77\u9053\uff1a\u201c\u5723\u4e0a\u5fa1\u65e8\u2014\u2014\u201d\u6b63\u60f3\u8bf4 \u4e0a\u51e0\u53e5\u5949\u627f\u7684\u8bf4\u8bdd\uff0c\u7948\u9547\u54c8\u54c8\u4e00\u7b11\uff0c\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\u9053\uff1a\u201c\u5f20\u5148\u751f\u662f\u5f53\u4eca\u7684\u5927\u82f1\u96c4\u3001\u5927\u8c6a\u6770\uff0c \u5c82\u6709\u4e0d\u6765\u4e4b\u7406\u3002\u201d\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u5e94\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u82f1\u96c4\u5927\u8c6a\u6770\u7684\u79f0\u547c\u53ef\u4e0d\u6562\u5f53\u3002\u53ea\u662f\u5341\u5e74 \u4e4b\u524d\uff0c\u4e39\u67ab\u5c1a\u6562\u5230\u74e6\u523a\u53bb\u9762\u89c1\u7687\u4e0a\uff0c\u4eca\u65e5\u5728\u672c\u56fd\u7684\u7586\u571f\u4e4b\u4e0a\uff0c\u5949\u7687\u4e0a\u7684\u5ba3\u53ec\uff0c\u5c82\u6709\u754f\u602f\u4e0d\u6765 \u4e4b\u7406\u3002\u201d\u7948\u9547\u542c\u4ed6\u63d0\u8d77\u5f53\u5e74\u4e4b\u4e8b\uff0c\u9762\u4e0a\u4e00\u7ea2\uff0c\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u4f55\u51b5\u6715\u4e0e\u5f20\u5148\u751f\u8fd8\u662f\u8001\u670b \u53cb\u5462\u3002\u201d\u5f20\u4e39\u67ab\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u53ef\u4e0d\u6562\u9ad8\u6500\uff0c\u4eca\u65f6\u4e0d\u6bd4\u5f80\u65e5\uff0c\u5f53\u5e74\u7687\u4e0a\u4f4f\u7684\u662f\u654c\u56fd\u56da \u7262\uff0c\u7a7f\u7684\u662f\u5355\u8863\uff0c\u5403\u7684\u662f\u7c97\u7c89\uff0c\u800c\u4eca\u4f4f\u7684\u662f\u96d5\u680f\u7389\u780c\uff0c\u7a7f\u7684\u662f\u9526\u7ee3\u9f99\u888d\uff0c\u5403\u7684\u662f\u5c71\u73cd\u6d77\u5473\uff0c \u54c8\u54c8\uff0c\u5f53\u771f\u662f\u5929\u6e0a\u4e4b\u522b\u4e86\u54ea\uff0c\u96be\u4e3a\u7687\u4e0a\u8fd8\u8bb0\u5f97\u6545\u65e7\u4e4b\u60c5\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u6ee1\u5ea7\u5931\u8272\uff0c\u7948\u9547\u5fc3\u4e2d \u6012\u6781\uff0c\u4f46\u4e3a\u4e86\u4fdd\u6301\u4eba\u541b\u7684\u98ce\u5ea6\u5a01\u4eea\uff0c\u6781\u529b\u6291\u5236\u4e86\u706b\u6c14\uff0c\u5e72\u7b11\u8bf4\u9053\uff1a\u201c\u5341\u5e74\u4e0d\u89c1\uff0c\u5f20\u5148\u751f\u7684\u72c2 \u50b2\u8fd8\u662f\u4e0d\u51cf\u5f53\u5e74\uff01\u9e3f\u535a\uff0c\u7aef\u6905\u5b50\u6765\u8bf7\u5f20\u5148\u751f\u5750\u4e0b\u5427\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u5251\u7709\u4e00\u7ad6\uff0c\u8fd9\u8001\u5934\u513f\u679c\u7136\u662f\u5927\u5185\u603b\u7ba1\u5a04\u6850\u5b59\u7684\u5e08\u7236\u77f3\u9e3f\u535a\uff0c\u6697\u6697\u7559\u4e86\u5fc3\u795e\uff0c\u53ea\u89c1 \u77f3\u9e3f\u535a\u5c0f\u5fc3\u7ffc\u7ffc\uff0c\u6709\u5982\u625b\u9f0e\u4e00\u6837\u5c06\u4e00\u5f20\u6905\u5b50\u4e3e\u4e86\u8d77\u6765\uff0c\u8f7b\u8f7b\u653e\u4e0b\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\u8d50 \u5750\u3002\u201d\u5f20\u4e39\u67ab\u662f\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u7cbe\u660e\u4e4b\u6781\uff0c\u4e00\u770b\u77f3\u9e3f\u535a\u7684\u624b\u6cd5\u4e0e\u795e\u60c5\uff0c\u5c31\u77e5\u4ed6\u5df2\u662f\u6697\u4e2d\u7528\u4e0a \u4e86\u5185\u5bb6\u771f\u529b\uff0c\u5c06\u90a3\u5f20\u501a\u5b50\u7684\u6728\u8d28\u9707\u5f97\u677e\u8f6f\u5982\u540c\u8c46\u8150\uff0c\u6559\u81ea\u5df1\u4e00\u5750\u4e0a\u53bb\u4fbf\u8981\u51fa\u4e11\uff0c\u5374\u4e0d\u70b9\u7834\uff0c \u5bf9\u90a3\u5f20\u501a\u5b50\u671b\u4e86\u4e00\u773c\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8c22\u5750\u3002\u201d\u5f20\u53e3\u4e00\u5439\uff0c\u4f5c\u52bf\u8981\u5439\u53bb\u90a3\u6905\u4e0a\u7684\u5c18\u57c3\uff0c\u4f46\u89c1\u4e00 \u5439\u4e4b\u4e0b\uff0c\u767b\u65f6\u54d7\u5566\u5566\u7684\u4e00\u7247\u54cd\u58f0\uff0c\u90a3\u5f20\u6905\u5b50\u5c31\u4f3c\u6ce5\u6c99\u5806\u6210\u7684\u4e00\u6837\uff0c\u4e00\u5439\u4fbf\u584c\uff0c\u88c2\u6210\u7247\u7247\uff0c\u7948 \u9547\u4e0d\u7531\u5f97\u5927\u60ca\u5931\u8272\uff0c\u77f3\u9e3f\u535a\u5927\u662f\u5c34\u5c2c\u3002 \u3000\u3000\u8fd9\u5f20\u6905\u5b50\uff0c\u867d\u7136\u5df2\u88ab\u77f3\u9e3f\u535a\u8fd0\u7528\u5185\u5bb6\u771f\u529b\u9707\u5f97\u6728\u8d28\u677e\u8f6f\uff0c\u5f20\u4e39\u67ab\u8fd9\u4e00\u5439\uff0c\u53ef\u8bf4\u5927\u534a\u662f\u9760 \u4e86\u77f3\u9e3f\u535a\u4e4b\u529b\uff0c\u4f46\u4e00\u5439\u5439\u584c\uff0c\u8fd9\u5185\u5bb6\u7684\u6c14\u529f\uff0c\u4e5f\u786e\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u5c24\u5176\u7948\u9547\u4e0d\u660e\u5c31\u91cc\uff0c\u66f4\u662f\u5fc3 \u5185\u5403\u60ca\u3002 \u3000\u3000\u77f3\u9e3f\u5085\u89c1\u5f20\u4e39\u67ab\u6697\u4e2d\u53d6\u5de7\uff0c\u5fc3\u4e2d\u751a\u662f\u4e0d\u5fff\uff0c\u4f46\u5374\u4e5f\u4e0d\u6562\u518d\u5f04\u7384\u865a\uff0c\u53e6\u5916\u7aef\u4e86\u4e00\u5f20\u6905\u5b50 \u6765\uff0c\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u5bab\u4e2d\u7684\u4e00\u4e9b\u65e7\u6905\u5b50\u4e5f\u8be5\u6362\u6362\u4e86\uff0c\u6664\uff0c\u8fd9\u4e00\u5f20\u4f3c\u4e4e\u8fd8\u5f88\u7ed3\u5b9e\u3002\u201d\u5927\u9a6c\u91d1\u5200 \u5730\u5750\u4e0b\uff0c\u5411\u77f3\u9e3f\u535a\u5fae\u5fae\u9894\u9996\uff0c\u9053\uff1a\u201c\u591a\u8c22\u4f60\u5566\u3002\u201d\u77f3\u9e3f\u535a\u81ca\u5f97\u8001\u8138\u6cdb\u7ea2\uff0c\u6545\u610f\u7acb\u5728\u5f20\u4e39\u67ab\u7684 \u80cc\u540e\uff0c\u53ea\u5f85\u7687\u5e1d\u773c\u8272\u4e00\u629b\uff0c\u4ed6\u5c31\u8981\u5bf9\u5f20\u4e39\u67ab\u65bd\u5c55\u5206\u7b4b\u9519\u9aa8\u7684\u6740\u624b\u3002 \u3000\u3000\u7948\u9547\u5f85\u5f20\u4e39\u67ab\u5750\u5b9a\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5f20\u5148\u751f\uff0c\u542c\u8bf4\u4f60\u6536\u4e86\u4e00\u4e2a\u5f97\u610f\u7684\u5973\u5f1f\u5b50\uff0c\u4e43\u662f\u4e8e\u8c26\u7684 \u5973\u513f\uff0c\u8fd9\u6b21\u53ef\u6709\u643a\u5979\u540c\u5165\u90fd\u95e8\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u5f85\u7687\u4e0a\u5c06\u4e8e\u9601\u8001\u7684\u6c89\u51a4\u662d\u96ea\uff0c\u662d\u544a\u5929\u4e0b\uff0c \u90a3\u65f6\u6211\u81ea\u4f1a\u5e26\u5979\u965b\u89c1\u3002\u201d\u7948\u9547\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u4e8e\u8c26\u5bf9\u6715\u5927\u9006\u4e0d\u9053\uff0c\u6715\u514d\u4ed6\u51cc\u8fdf\uff0c \u5df2\u662f\u989d\u5916\u65bd\u6069\u4e86\u3002\u201d\u53f6\u5f20\u4e39\u67ab\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\u4f60\u4e5f\u53ef\u8fd8\u8bb0\u5f97\u5f53\u5e74\u4e8e\u9601\u8001\u8fce\u4f60\u56de\u56fd\uff0c\u4f60\u66fe\u4eb2 \u53e3\u7b54\u5e94\u6211\u6c38\u4e0d\u4f1a\u6740\u4ed6\u7684\u8bdd\u4e48\uff1f\u201d\u9633\u5b97\u6d77\u559d\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u4f60\u597d\u65e0\u793c\uff01\u201d\u7948\u9547\u9053\uff1a\u201c\u4e8e\u8c26\u4e58\u6715\u8499 \u5c18\u4e4b\u9645\uff0c\u53e6\u7acb\u65b0\u541b\uff0c\u7eb5\u6709\u514d\u6b7b\u91d1\u724c\uff0c\u4ea6\u96be\u8d66\u7f6a\u3002\u5f20\u5148\u751f\uff0c\u6715\u4e0d\u660e\u767d\uff0c\u4f60\u4f55\u4ee5\u603b\u662f\u8981\u548c\u6715\u4f5c \u5bf9\uff1f\u201d\u5f20\u4e39\u67ab\u51b7\u7b11\u9053\uff1a\u201c\u6211\u82e5\u662f\u4e0e\u7687\u4e0a\u4f5c\u5bf9\uff0c\u53ea\u6015\u7687\u4e0a\u800c\u4eca\u8fd8\u5728\u74e6\u523a\u5fcd\u53d7\u90a3\u523a\u9aa8\u7684\u5bd2\u98ce \u5462\uff01\u201d\u7948\u9547\u52c3\u7136\u4f5c\u8272\u9053\uff1a\u201c\u4f60\u6614\u65e5\u66fe\u4e8e\u6715\u6709\u6069\uff0c\u80a4\u5df2\u8bb0\u4e0b\u6765\u4e86\uff0c\u4e0d\u52b3\u4f60\u518d\u4e09\u63d0\u8d77\u3002\u201d\u5f20\u4e39\u67ab \u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4e8b\u8fc7\u5883\u8fc1\uff0c\u65e7\u4e8b\u4e0d\u63d0\u4e5f\u7f62\u3002\u90a3\u4e48\uff0c\u4e14\u8bf4\u5982\u4eca\u2014\u2014\u201d\u7948\u9547\u9053\uff1a\u201c\u53f6\u5b97\u7559\u53d4\u4f84\u4e0e \u6bd5\u64ce\u5929\u5728\u6c5f\u5357\u5021\u4e71\uff0c\u5e78\u5728\u6bd5\u64ce\u5929\u8ff7\u9014\u77e5\u8fd4\uff0c\u5982\u4eca\u5df2\u5411\u6715\u901a\u6b3e\u8f93\u8bda\uff0c\u53f6\u5b97\u7559\u4ea6\u5df2\u4ea1\u547d\u6d77\u5916\uff0c\u53ea \u6709\u53f6\u6210\u6797\u5c1a\u5728\u6cea\u6eaa\u987d\u6297\u7687\u5e08\uff0c\u542c\u8bf4\u4ed6\u662f\u4f60\u7684\u5e08\u4f84\uff0c\u4f60\u82e5\u4e0d\u662f\u7acb\u5fc3\u8981\u4e0e\u6715\u4f5c\u5bf9\uff0c\u90a3\u4e48\u5c31\u8bf7\u4f60\u5199 \u4e0b\u4e00\u5c01\u7ed9\u53f6\u6210\u6797\u7684\u51fd\u4ef6\uff0c\u4e3a\u6715\u62db\u964d\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u539f\u6765\u4e39\u67ab\u7684\u4e00\u5c01\u4e66\u4fe1\uff0c\u7adf\u503c\u5f97\u7687\u4e0a\u9686\u91cd\u8d50\u5bb4\uff0c\u8fd9\u53ef\u4f7f\u4e39\u67ab\u53d7\u5ba0\u82e5\u60ca\u4e86\u3002 \u53ef\u662f\u4e39\u67ab\u4e5f\u6709\u4e09\u4ef6\u4e8b\u60c5\u8981\u6c42\u7687\u4e0a\u3002\u201d\u7948\u9547\u542c\u4ed6\u5982\u8ba5\u4f3c\u8bbd\uff0c\u5927\u662f\u4e0d\u60a6\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u5f20\u4e39\u67ab\u9053\uff1a\u201c\u7b2c\u4e00\u4ef6\u9002\u624d\u5df2\u7ecf\u8bf4\u8fc7\uff0c\u8bf7\u7687\u4e0a\u662d\u544a\u5929\u4e0b\uff0c\u4e3a\u4e8e\u9601\u8001\u6d17\u51a4\u3002\u201d\u7948\u9547\u9053\uff1a\u201c\u7b2c\u4e8c\u4ef6 \u5462\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u62db\u964d\u4e4b\u4fe1\uff0c\u6211\u7eb5\u80af\u5199\uff0c\u53f6\u6210\u6797\u4ea6\u793a\u5fc5\u80af\u964d\u3002\u4e24\u5168\u4e4b\u7b56\uff0c\u4e0d\u5982\u8ba9\u53f6\u6210\u6797\u7387 \u9886\u6240\u90e8\uff0c\u5230\u821f\u5c71\u7fa4\u5c9b\u53bb\uff0c\u65e2\u53ef\u4e3a\u671d\u5ef7\u62b5\u5fa1\u502d\u5974\uff0c\u53c8\u4e0d\u8981\u671d\u5ef7\u7684\u7cae\u664c\uff0c\u7687\u4e0a\u82e5\u4e3a\u4e86\u671d\u5ef7\u7684\u989c \u9762\uff0c\u4ea6\u53ef\u7531\u4ed6\u9065\u9886\u5c01\u53f7\uff0c\u6d77\u5916\u79f0\u738b\uff0c\u540d\u4e49\u4e0a\u4ecd\u7b97\u662f\u5927\u660e\u7684\u81e3\u5c5e\uff0c\u5c82\u975e\u4e24\u5168\u5176\u7f8e\u3002\u201d\u7948\u9547\u5fc3\u4e2d \u4e00\u52a8\uff0c\u4f3c\u968f\u5373\u60f3\u5230\u201c\u517b\u864e\u9057\u60a3\u201d\u7684\u53e4\u8bad\uff0c\u9ed8\u7136\u4e0d\u8bed\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u7b2c\u4e09\u4ef6\u2014\u2014\u201d\u7948\u9547\u9053\uff1a \u201c\u5f20\u5148\u751f\u8bf4\u5f97\u53e3\u5e72\u4e86\uff0c\u8bf7\u5148\u996e\u4e00\u676f\u6da6\u6da6\u5589\u5499\u3002\u4e91\u72b6\u5143\u4e5f\u4e00\u5e76\u8bf7\u4e86\u3002\u201d\u4ed6\u4eb2\u81ea\u63d0\u58f6\uff0c\u659f\u4e86\u4e09 \u676f\uff0c\u4ee5\u6c42\u65e0\u4ed6\uff0c\u53eb\u9633\u5b97\u6d77\u5c06\u90a3\u4e24\u676f\u9152\u5206\u656c\u5f20\u4e39\u67ab\u548c\u4e91\u91cd\u3002\u5f20\u4e39\u67ab\u5ffd\u5730\u628a\u4e91\u91cd\u90a3\u4e00\u676f\u9152\u4e5f\u62a2\u4e86 \u8fc7\u6765\uff0c\u7b11\u9053\uff1a\u201c\u4e91\u72b6\u5143\u9152\u91cf\u6d45\uff0c\u5f85\u6211\u4e0e\u4ed6\u559d\u4e86\u3002\u201d\u559d\u5165\u53e3\u4e2d\uff0c\u5ffd\u5730\u5f20\u53e3\u4e00\u55b7\uff0c\u4e00\u80a1\u9152\u6d6a\uff0c\u76f4 \u5411\u9633\u5b97\u6d77\u5c04\u53bb\uff01\u6b63\u662f\uff1a \u3000\u3000\u6740\u6c14\u9690\u85cf\u60ca\u7981\u82d1\uff0c\u6700\u65e0\u60c5\u4e49\u5e1d\u7687\u5bb6\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e09\u56de\u3000\u7b56\u732e\u7b75\u524d\u3000\u4e39\u5fc3\u56fe\u62a5\u56fd\u3000\u706b\u711a\u5927\u5185\u3000\u5f02\u571f\u6551\u82f1\u96c4 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e09\u5341\u4e09\u56de\u3000\u7b56\u732e\u7b75\u524d\u3000\u4e39\u5fc3\u56fe\u62a5\u56fd\u3000\u706b\u711a\u5927\u5185\u3000\u5f02\u571f\u6551\u82f1\u96c4 \u3000\u3000\u77f3\u9e3f\u535a\u6a2a\u80b1\u4e00\u649e\uff0c\u5c06\u9633\u5b97\u6d77\u649e\u8fc7\u4e00\u8fb9\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u5f20\u4e39\u67ab\uff0c\u4f60\u5728\u4e07\u5c81\u8ddf\u524d\uff0c\u7adf\u6562\u5982\u6b64 \u65e0\u793c\uff01\u201d\u53ea\u89c1\u90a3\u80a1\u9152\u6d6a\uff0c\u5c04\u5230\u4e86\u65c1\u7acb\u7684\u4e00\u4e2a\u6b66\u58eb\u9762\u4e0a\uff0c\u767b\u65f6\u8d77\u4e86\u65e0\u6570\u6ce1\u6ce1\uff0c\u8138\u76ae\u8fc5\u5373\u7126\u4e86\u4e00 \u7247\uff0c\u597d\u50cf\u88ab\u706b\u70e7\u8fc7\u4e00\u822c\u3002\u539f\u6765\u8fd9\u9152\u58f6\u5206\u4e3a\u4e24\u683c\uff0c\u58f6\u67c4\u4e2d\u85cf\u673a\u5173\uff0c\u7687\u5e1d\u559d\u7684\u624d\u662f\u7389\u6db2\u743c\u6d46\uff0c\u800c \u659f\u7ed9\u5f20\u4e39\u67ab\u4e0e\u4e91\u91cd\u7684\u5374\u7adf\u662f\u4e00\u676f\u6bd2\u9152\uff01\u5e78\u597d\u5f20\u4e39\u67ab\u89c1\u673a\u5f97\u65e9\uff0c\u55b7\u4e86\u51fa\u6765\uff0c\u800c\u9633\u5b97\u6d77\u4e5f\u5e78\u5f97\u77f3 \u9e3f\u535a\u90a3\u9002\u65f6\u7684\u4e00\u649e\uff0c\u8981\u4e0d\u7136\u4ed6\u5c31\u8981\u9996\u5f53\u5176\u51b2\uff0c\u5148\u88ab\u90a3\u6bd2\u9152\u5c04\u4e2d\u3002 \u3000\u3000\u8fd9\u51e0\u4e0b\u5b50\u52a8\u4f5c\u5feb\u5982\u7535\u5149\u77f3\u706b\uff0c\u4f46\u542c\u5f97\u53f1\u54a4\u4e00\u58f0\uff0c\u5200\u5149\u4e00\u95ea\uff0c\u5c60\u9f99\u5c0a\u8005\u9694\u7740\u4e00\u5f20\u684c\u5b50\uff0c\u4f38 \u51fa\u4e86\u957f\u81c2\uff0c\u4fbf\u628a\u5c60\u9f99\u5200\u821e\u52a8\u65ab\u6765\u3002\u5f20\u4e39\u67ab\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6211\u4ee5\u4e00\u4ecb\u5c0f\u6c11\uff0c\u7adf\u8499\u7687\u4e0a\u9752\u773c \u76f8\u52a0\uff0c\u8d50\u4ee5\u9e3f\u95e8\u5bb4\u4e86\uff01\u201d\u8863\u8896\u4e00\u62c2\uff0c\u5377\u7740\u4e86\u5c60\u9f99\u5c0a\u8005\u90a3\u53e3\u6bd2\u5200\uff0c\u5de6\u638c\u4e00\u62db\u201c\u4e58\u98ce\u7834\u6d6a\u201d\uff0c\u8361 \u5f00\u4e86\u77f3\u9e3f\u535a\u7684\u4e00\u6293\uff0c\u5c60\u9f99\u5c0a\u8005\u5927\u53eb\u4e00\u58f0\uff0c\u6bd2\u5200\u8131\u624b\u98de\u51fa\uff0c\u4eba\u4e5f\u7ed9\u5f20\u4e39\u67ab\u90a3\u80a1\u53cd\u9707\u4e4b\u529b\uff0c\u9707\u5012 \u5730\u4e0a\u3002\u6458\u661f\u4e0a\u4eba\u672c\u6765\u4e5f\u51c6\u5907\u51fa\u624b\uff0c\u89c1\u5f20\u4e39\u67ab\u8fd9\u8863\u8896\u4e00\u5377\uff0c\u7adf\u7136\u6709\u5982\u6b64\u7684\u5a01\u529b\uff0c\u4e0d\u7981\u5fc3\u4e2d\u4e00 \u51db\uff0c\u5012\u63d0\u5c18\u67c4\uff0c\u4e0d\u6562\u5192\u6627\u51fa\u6765\u3002 \u3000\u3000\u77f3\u9e3f\u535a\u4e00\u6293\u843d\u7a7a\uff0c\u5316\u4e3a\u9633\u638c\u62cd\u51fa\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u5f20\u4e39\u67ab\u5374\u53cd\u800c\u7ed9\u4ed6 \u9707\u9000\u4e86\u4e24\u6b65\u3002\u539f\u6765\u662f\u5f20\u4e39\u67ab\u6709\u610f\u8bd5\u4ed6\u7684\u638c\u529b\uff0c\u4e0d\u8fc7\u5f20\u4e39\u67ab\u56e0\u4e3a\u8981\u517c\u987e\u5c60\u9f99\u5c0a\u8005\uff0c\u5c06\u771f\u529b\u5206\u6210 \u4e24\u534a\u4f7f\u7528\uff0c\u77f3\u9e3f\u535a\u7684\u529f\u529b\u4e0e\u4ed6\u65d7\u9f13\u76f8\u5f53\uff0c\u5f20\u4e39\u67ab\u4ee5\u5355\u638c\u5e94\u654c\uff0c\u5f53\u7136\u843d\u4e86\u4e0b\u98ce\u3002 \u3000\u3000\u77f3\u9e3f\u535a\u662f\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u81ea\u662f\u77e5\u9053\u5176\u4e2d\u4e4b\u7406\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u53ea\u7528\u4e86\u4e94\u6210\u771f\u529b\uff0c\u5c45 \u7136\u80fd\u4ee5\u7edd\u5999\u7684\u5de7\u52b2\uff0c\u5378\u5f00\u4e86\u6211\u8fd9\u529b\u903e\u5343\u65a4\u7684\u638c\u529b\uff0c\u602a\u4e0d\u5f97\u8bb8\u591a\u6b66\u6797\u524d\u8f88\uff0c\u4e5f\u7518\u613f\u5949\u4ed6\u4e3a \u5c0a\uff01\u201d\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u8fde\u58f0\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d\u77f3\u9e3f\u535a\u9053\uff1a\u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a \u201c\u53ef\u60dc\u4f60\u4ee5\u5317\u65b9\u6b66\u5b66\u5927\u5e08\u7684\u8eab\u4efd\uff0c\u8fd9\u6837\u7684\u5e74\u7eaa\uff0c\u8fd8\u88ab\u5f92\u5f1f\u9a97\u4e86\u51fa\u6765\uff0c\u66ff\u4eba\u5bb6\u505a\u5974\u624d\uff01\u201d\u77f3\u9e3f \u535a\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5e08\u7236\u8c22\u5929\u534e\u89c1\u4e86\u6211\uff0c\u4e5f\u8981\u606d\u606d\u656c\u656c\u5c0a\u6211\u4e00\u58f0\u524d\u8f88\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u5f20\u4e39\u67ab \u7b11\u9053\uff1a\u201c\u6240\u4ee5\u8bf4\u4e00\u4e2a\u4eba\u7684\u7acb\u8eab\u5904\u4e16\uff0c\u4e0d\u53ef\u4e0d\u614e\uff0c\u4f60\u4e34\u8001\u80e1\u6d82\uff0c\u7518\u5fc3\u505a\u5974\u624d\u4e4b\u4e8b\uff0c\u662f\u4f60\u81ea\u5df1\u5148 \u53eb\u4eba\u770b\u5c0f\u4e86\uff0c\u4e0e\u6211\u4f55\u5e72\uff1f\u201d\u5f20\u4e39\u67ab\u5bd3\u529d\u4e8e\u8bbd\uff0c\u8fd9\u4e00\u756a\u8bdd\u77f3\u9e3f\u535a\u54ea\u91cc\u542c\u5f97\u8fdb\u53bb\uff0c\u66b4\u559d\u4e00\u58f0\uff0c\u5de6 \u638c\u5212\u4e86\u534a\u5f27\u5f62\uff0c\u5411\u5f20\u4e39\u67ab\u53c8\u662f\u6402\u5934\u4e00\u6293\u3002\u5f20\u4e39\u67ab\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\u907f\u5f00\uff0c\u77f3\u9e3f\u535a\u53f3\u638c\u53c8\u5230\uff0c\u8fd9\u4e24 \u638c\u8fde\u73af\u5288\u81f3\uff0c\u7aef\u7684\u662f\u5389\u5bb3\u5f02\u5e38\uff0c\u5176\u4e2d\u53c8\u6697\u85cf\u7740\u5206\u7b4b\u9519\u9aa8\u7684\u8bb8\u591a\u7cbe\u5999\u62db\u6570\uff0c\u53ef\u4ee5\u968f\u65f6\u5316\u638c\u4e3a \u6307\uff0c\u5316\u6307\u6233\u4e3a\u64d2\u62ff\uff0c\u4e0e\u6b66\u6797\u5404\u6d3e\u638c\u6cd5\uff0c\u8fe5\u7136\u76f8\u5f02\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e00\u638c\u62a4\u80f8\uff0c\u4e00\u638c\u5e94\u654c\uff0c\u4f7f\u7528\u987b\u5f25\u638c\u6cd5\uff0c\u5316\u89e3\u4e86\u4ed6\u7684\u4e09\u62db\uff0c\u659c\u773c\u4e00\u77a5\uff0c\u53ea\u89c1\u4e91\u91cd\u5dc5 \u5dcd\u5dcd\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u60b2\u58f0\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u8bf7\u95ee\u6211\u4e91\u5bb6\u5c61\u4ee3\uff0c\u5fe0\u5fc3\u4e3a\u56fd\uff0c\u4f55\u7f6a\u4f55\u8f9c\uff0c\u7adf\u8499\u7687\u4e0a\u4e24 \u756a\u8d50\u9152\uff1f\u201d \u3000\u3000\u539f\u6765\u4e91\u91cd\u7684\u7956\u7236\u4e91\u9756\uff0c\u5f53\u5e74\u51fa\u4f7f\u74e6\u523a\uff0c\u5386\u5c3d\u5343\u8f9b\u4e07\u82e6\u56de\u6765\uff0c\u4e5f\u662f\u88ab\u7948\u9547\u8d50\u4ee5\u6bd2\u9152\u9e20\u6740 \u7684\u3002\u4e91\u91cd\u60f3\u8d77\u7956\u7236\u7684\u60e8\u6b7b\uff0c\u7948\u9547\u4eca\u65e5\u53c8\u7528\u540c\u6837\u7684\u624b\u6bb5\u5bf9\u4ed8\u81ea\u5df1\uff0c\u4e0d\u7531\u5f97\u4f24\u75db\u4e4b\u6781\uff0c\u62fc\u7740\u820d\u4e86 \u6027\u547d\uff0c\u5f53\u7740\u7687\u5e1d\u7684\u9762\uff0c\u8d28\u95ee\u8d77\u6765\u3002 \u3000\u3000\u7948\u9547\u89c1\u5f20\u4e39\u67ab\u5c06\u6bd2\u9152\u5012\u8fdb\u53e3\u4e2d\uff0c\u867d\u7136\u7acb\u5373\u55b7\u51fa\uff0c\u4f46\u90a3\u9152\u6bd2\u6027\u751a\u70c8\uff0c\u6cbe\u8089\u8089\u88c2\uff0c\u6cbe\u8349\u8349 \u7126\uff0c\u800c\u4ed6\u7adf\u7136\u6beb\u65e0\u5f02\u72b6\uff0c\u5fc3\u4e2d\u5403\u60ca\u975e\u5c0f\uff0c\u6b63\u81ea\u5168\u795e\u6ce8\u89c6\u5f20\u4e39\u67ab\u4e0e\u77f3\u9e3f\u535a\u7684\u640f\u6597\uff0c\u60f3\u4e0d\u5230\u4e91\u91cd \u7a81\u7136\u6709\u6b64\u4e00\u95ee\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u7741\u76ee\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u4e91\u91cd\u60b2\u6124\u4e4b\u6781\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u8bf7\u95ee\u671d \u5ef7\u7684\u5927\u6cd5\uff0c\u662f\u5426\u5c3d\u5fe0\u4e3a\u56fd\u7684\uff0c\u90fd\u5f97\u53d7\u90a3\u6bd2\u9152\u4e4b\u5211\uff1f\u201d\u7948\u9547\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u8bdd\uff1f\u201d \u4e91\u91cd\u9053\uff1a\u201c\u6211\u7956\u7236\u51fa\u4f7f\u80e1\u8fb9\uff0c\u7267\u9a6c\u4e8c\u5341\u5e74\uff0c\u671d\u91ce\u79f0\u9882\uff0c\u8bf4\u662f\u4ed6\u8282\u6bd4\u82cf\u6b66\uff0c\u53ef\u767b\u53f2\u518c\uff0c\u4f46\u4ed6\u4e00 \u5165\u56fd\u95e8\uff0c\u4fbf\u9886\u53d7\u4e86\u7687\u4e0a\u7684\u4e00\u676f\u6bd2\u9152\uff01\u6211\u4e91\u91cd\u867d\u7136\u8fdc\u8fdc\u4e0d\u53ca\u4ed6\u8001\u4eba\u5bb6\uff0c\u4e5f\u66fe\u4e3a\u7687\u4e0a\u6548\u8fc7\u5fae\u52b3\uff0c \u51fa\u4f7f\u74e6\u523a\uff0c\u4eb2\u8fce\u7687\u4e0a\u56de\u56fd\uff0c\u8bf7\u95ee\u7687\u4e0a\u53c8\u4e3a\u751a\u8981\u7528\u5bf9\u6211\u7956\u7236\u7684\u624b\u6bb5\u6765\u5bf9\u4ed8\u6211\u3002\u201d\u7948\u9547\u88ab\u4ed6\u4e00 \u95ee\uff0c\u7b54\u4e0d\u51fa\u8bdd\uff0c\u90a3\u7a7f\u7740\u957f\u886b\u5112\u670d\u7684\u7c97\u8c6a\u6c49\u5b50\u559d\u9053\uff1a\u201c\u4e91\u91cd\u53e3\u51fa\u6028\u8a00\uff0c\u4fbf\u5f53\u4e00\u6b7b\uff01\u201d \u3000\u3000\u4e91\u91cd\u5927\u6012\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u5ffd\u542c\u5f97\u73af\u4f69\u53ee\u5f53\uff0c\u4f17\u6b66\u58eb\u7a81\u7136\u5bc2\u9759\u65e0\u58f0\uff0c\u90a3\u7c97\u8c6a\u6c49\u5b50\u4e5f\u655b\u624b\u606d \u5019\uff0c\u53ea\u89c1\u6709\u4e24\u5bf9\u7537\u5973\u8d70\u4e86\u8fdb\u6765\uff0c\u884c\u5728\u6700\u524d\u9762\u7684\u662f\u4e00\u4e2a\u4e8c\u5341\u591a\u5c81\u7684\u534e\u8d35\u5c11\u5e74\uff0c\u4e2d\u95f4\u7684\u4e00\u5bf9\u7537 \u5973\uff0c\u633d\u624b\u540c\u884c\uff0c\u72b6\u5982\u592b\u5987\uff0c\u5973\u7684\u7adf\u662f\u4e00\u4e2a\u897f\u65b9\u91d1\u53d1\u7f8e\u4eba\uff0c\u6700\u540e\u9762\u7684\u662f\u4e00\u4e2a\u4e2d\u5e74\u7f8e\u5987\uff0c\u4e91\u91cd\u8ba4 \u5f97\u6b63\u662f\u59b9\u5b50\u4e91\u857e\u3002 \u3000\u3000\u7948\u9547\u5ffd\u5730\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e91\u72b6\u5143\uff0c\u4f60\u8bef\u4f1a\u4e86\u3002\u4ee4\u7956\u662f\u5978\u5ba6\u738b\u632f\u6240\u5bb3\uff0c\u6715\u65e9\u5df2\u4e3a\u4ed6\u662d\u96ea \u6c89\u51a4\u3002\u4eca\u65e5\u8fd9\u9152\uff0c\u4e43\u662f\u5341\u5168\u5927\u8865\u7684\u836f\u9152\uff0c\u4f60\u600e\u7684\u80e1\u4e71\u731c\u7591\uff0c\u4f60\u4e0d\u89c1\u8054\u4e5f\u559d\u4e86\u4e48\uff1f\u201d\u4e91\u91cd\u5fc3 \u9053\uff1a\u201c\u4f60\u5f53\u6211\u662f\u5c0f\u5b69\u5b50\u4e48\uff1f\u201d\u6b63\u5f85\u4e0d\u987e\u4e00\u5207\uff0c\u62c6\u7834\u673a\u5173\uff0c\u8fd9\u65f6\u5f20\u4e39\u67ab\u4e0e\u77f3\u9e3f\u535a\u4ea6\u5df2\u7f62\u6597\uff0c\u4f46 \u89c1\u5f20\u4e39\u67ab\u773c\u89d2\u98d8\u6765\uff0c\u793a\u610f\u53eb\u4e91\u91cd\u4e0d\u53ef\u5984\u52a8\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u4eba\u8d70\u8fdb\u9601\u5b50\uff0c\u90a3\u5c11\u5e74\u4fef\u4f0f\u4e8e\u5730\uff0c\u5531\u9053\uff1a\u201c\u7236\u7687\u4e07\u5c81\uff0c\u81e3\u513f\u89c1\u9a7e\u3002\u201d\u7948\u9547\u9053\uff1a\u201c\u89c1 \u6df1\uff0c\u4f60\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u6ce2\u65af\u516c\u4e3b\uff0c\u8fdc\u9053\u6765\u671d\uff0c\u81e3\u513f\u966a\u5979\u89c1\u9a7e\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u5c11\u5e74\u6b63\u662f\u7948\u9547\u7684\u592a\u5b50\u6731\u89c1\u6df1\u3002\u539f\u6765\u5f20\u4e39\u67ab\u5165\u4eac\u4e4b\u540e\uff0c\u65e5\u5915\u7b79\u8c0b\uff0c\u8981\u627e\u4e00\u4e2a\u6700\u9002\u5f53\u7684 \u673a\u4f1a\u53bb\u89c1\u7687\u5e1d\u3002\u4ed6\u63a2\u542c\u5f97\u592a\u5b50\u5c1a\u6709\u5e74\u8f7b\u4eba\u7684\u4e00\u80a1\u52b2\uff0c\u9887\u6709\u632f\u594b\u56fe\u5f3a\u4e4b\u5fc3\uff0c\u4ed6\u60f3\u5c3d\u529e\u6cd5\uff0c\u6253\u901a \u4e86\u592a\u5b50\u7684\u95e8\u8def\uff0c\u4e0e\u4ed6\u5546\u91cf\u7531\u6ce2\u65af\u516c\u4e3b\u4f5c\u4e3a\u6865\u6881\uff0c\u5c06\u6765\u597d\u4e0e\u6ce2\u65af\u8054\u76df\uff0c\u5939\u51fb\u9791\u977c\u7684\u5927\u8ba1\u3002\u592a\u5b50 \u88ab\u5f20\u4e39\u67ab\u8bf4\u52a8\uff0c\u6b63\u60f3\u5f85\u6709\u5229\u7684\u65f6\u673a\u624d\u5e26\u4ed6\u4eec\u53bb\u89c1\u7236\u7687\u3002\u60f3\u4e0d\u5230\u7948\u9547\u5df2\u5148\u628a\u5f20\u4e39\u67ab\u8bf7\u6765\uff0c\u5f20\u4e39 \u67ab\u5728\u79bb\u5f00\u9556\u5c40\u4e4b\u524d\u9063\u4e91\u857e\u98de\u5feb\u62a5\u77e5\u592a\u5b50\uff0c\u90a3\u6ce2\u65af\u516c\u4e3b\u548c\u9a78\u9a6c\u6bb5\u6f84\u82cd\u6570\u65e5\u524d\u5df2\u79d8\u5bc6\u79fb\u5c45\u592a\u5b50\u5e9c \u4e2d\uff0c\u662f\u4ee5\u4e00\u63a5\u62a5\u8baf\uff0c\u4fbf\u80fd\u524d\u6765\uff0c\u5f20\u4e39\u67ab\u548c\u592a\u5b50\u90fd\u77e5\u9053\u6b64\u8ba1\u751a\u9669\uff0c\u4f46\u4e8b\u5230\u4e34\u5934\uff0c\u53ea\u6b64\u4e00\u7b56\uff0c\u518d \u65e0\u4ed6\u56fe\u3002 \u3000\u3000\u6ce2\u65af\u516c\u4e3b\u66f3\u8d77\u957f\u88d9\uff0c\u76c8\u76c8\u4e00\u798f\uff0c\u8f7b\u542f\u73e0\u5589\uff0c\u83ba\u58f0\u5456\u5456\u8bf4\u9053\uff1a\u201c\u6ce2\u65af\u516c\u4e3b\u5055\u9a78\u9a6c\u6bb5\u6f84\u82cd\u62dc \u89c1\u5927\u660e\u5929\u5b50\uff0c\u5e76\u4ee3\u8868\u6ce2\u65af\u5927\u7687\u5e1d\u5411\u5927\u660e\u5929\u5b50\u81f4\u4ee5\u6700\u9ad8\u656c\u793c\uff0c\u656c\u795d\u5927\u660e\u5929\u5b50\u798f\u5bff\u65e0\u7586\uff0c\u6c11\u5b89\u56fd \u6cf0\u3002\u201d\u8fd9\u51e0\u53e5\u6c49\u8bed\uff0c\u6ce2\u65af\u516c\u4e3b\u5b66\u4e86\u6570\u5341\u767e\u904d\uff0c\u8bf4\u6765\u5b57\u6b63\u8154\u5706\uff0c\u751c\u7f8e\u52a8\u542c\u3002\u7948\u9547\u5fc3\u4e2d\u5927\u4e50\uff0c\u8981 \u77e5\u660e\u671d\u56fd\u52bf\u65e5\u8870\uff0c\u4e00\u4e9b\u5c0f\u56fd\u85e9\u5c5e\u5c1a\u4e14\u4e0d\u4f9d\u671f\u8fdb\u8d21\uff0c\u8fdc\u65b9\u5927\u56fd\u7684\u4f7f\u8005\u6765\u671d\uff0c\u90a3\u66f4\u662f\u4ece\u6240\u672a\u6709\u4e4b \u4e8b\u3002 \u3000\u3000\u6bb5\u6f84\u82cd\u56e0\u4e3a\u4efd\u5c5e\u5927\u660e\u6cbb\u4e0b\u7684\u5b50\u6c11\uff0c\u867d\u7136\u662f\u6ce2\u65af\u9a78\u9a6c\u7684\u8eab\u4efd\uff0c\u4ecd\u7136\u884c\u4e86\u8dea\u62dc\u4e4b\u793c\uff0c\u592a\u5b50\u6731 \u89c1\u6df1\u4ee3\u594f\u9053\uff1a\u201c\u6bb5\u9a78\u9a6c\u662f\u4ee5\u524d\u5927\u7406\u6bb5\u5e73\u7ae0\u6bb5\u529f\u7684\u516b\u4e16\u5b50\u5b59\uff0c\u548c\u73b0\u4eca\u5927\u7406\u7684\u77e5\u5e73\u7ae0\u4e8b\u6bb5\u6f84\u5e73\u662f \u5802\u5144\u5f1f\u3002\u6bb5\u9a78\u9a6c\u4e03\u4ee3\u4ee5\u6765\uff0c\u5ba2\u4f4f\u6ce2\u65af\uff0c\u800c\u4eca\u65b9\u56de\u6545\u56fd\u3002\u201d \u3000\u3000\u7948\u9547\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5bf9\u6ce2\u65af\u516c\u4e3b\u9053\uff1a\u201c\u516c\u4e3b\u4e0e\u9a78\u9a6c\u6765\u671d\uff0c\u53ef\u6709\u4ec0\u4e48\u4e8b\u4e48\uff1f\u201d\u6ce2\u65af\u516c\u4e3b\u7684\u6c49\u8bed \u53ea\u662f\u4e00\u77e5\u534a\u89e3\uff0c\u8fd9\u51e0\u53e5\u8bdd\u542c\u5f97\u4e0d\u5927\u660e\u767d\uff0c\u6bb5\u6f84\u82cd\u7ed9\u5979\u7ffb\u8bd1\u4e86\uff0c\u6ce2\u65af\u516c\u4e3b\u76c8\u76c8\u4e00\u7b11\uff0c\u6307\u7740\u5f20\u4e39 \u67ab\u8bf4\u4e86\u51e0\u53e5\uff0c\u6bb5\u6f84\u82cd\u594f\u9053\uff1a\u201c\u6ce2\u65af\u516c\u4e3b\u6388\u6743\u7ed9\u8fd9\u4f4d\u5f20\u5148\u751f\uff0c\u8bf7\u4ed6\u5168\u6743\u4ee3\u594f\uff0c\u4e0e\u965b\u4e0b\u5546\u8bae\u4e2d\u56fd \u6ce2\u65af\u4e24\u56fd\u901a\u597d\u8054\u76df\u4e4b\u4e8b\u3002\u201d\u592a\u5b50\u8d70\u8fd1\u7687\u5e1d\u8eab\u8fb9\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u6ce2\u65af\u5e1d\u56fd\u662f\u4e2d\u4e9a\u7684\u7b2c\u4e00\u5927\u56fd\uff0c \u56fd\u529b\u4e0d\u5f31\u4e8e\u6211\u4eec\u4e2d\u56fd\uff0c\u8bf7\u7236\u7687\u7a0d\u7a0d\u4f18\u793c\u4f7f\u81e3\u3002\u201d\u8fd9\u756a\u5e94\u5bf9\u90fd\u662f\u5f20\u4e39\u67ab\u7684\u4e8b\u5148\u6240\u6559\uff0c\u7948\u9547\u542c \u4e86\uff0c\u53ea\u597d\u91cd\u65b0\u201c\u8d50\u5750\u201d\uff0c\u8bf7\u95ee\u5f20\u4e39\u67ab\u201c\u9ad8\u89c1\u201d\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u9002\u624d\u6240\u8981\u8bf4\u7684\u7b2c\u4e09\u4ef6\u4e8b\u4e86\u3002\u8bf7\u7687\u4e0a\u5c01\u6bb5\u6f84\u82cd\u4e3a\u5927\u7406\u4e16 \u88ad\u85e9\u738b\uff0c\u5927\u7406\u5e9c\u5c5e\u7684\u5404\u65cf\u5b98\u540f\uff0c\u7531\u4ed6\u7edf\u8f96\u3002\u7136\u540e\u6d3e\u9063\u4f7f\u81e3\uff0c\u524d\u5f80\u6ce2\u65af\uff0c\u8ba9\u6ce2\u65af\u7687\u5e1d\u77e5\u9053\uff0c\u4ed6 \u7684\u7231\u5973\u7231\u5a7f\uff0c\u5df2\u5f97\u5230\u4e2d\u56fd\u541b\u7687\u7684\u4f18\u539a\u793c\u9047\u3002\u201d\u7948\u9547\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u4e2a\u53ef\u4ee5\u5546\u91cf\uff0c\u4e0d\u8fc7\u4e91\u5357\u4e00 \u7701\uff0c\u5728\u592a\u7956\u7687\u5e1d\u5f00\u57fa\u5b9a\u56fd\u4e4b\u540e\uff0c\u5df2\u5c01\u7ed9\u6c90\u5bb6\u4e16\u88ad\u7f54\u66ff\uff0c\u5982\u4eca\u8981\u628a\u5927\u7406\u5272\u51fa\u6765\uff0c\u6715\u8fd8\u5f97\u4e0b\u65e8\u7ed9 \u6c90\u56fd\u516c\uff0c\u518d\u770b\u4ed6\u6709\u751a\u7980\u594f\uff0c\u4ee5\u793a\u6715\u5bf9\u529f\u81e3\u4e4b\u540e\u7684\u5c0a\u5d07\u3002\u201d\u5f20\u4e39\u67ab\u77e5\u9053\u8fd9\u4e0d\u8fc7\u662f\u671d\u5ef7\u7684\u4f8b\u884c\u516c \u4e8b\uff0c\u6709\u7687\u5e1d\u8bcf\u4e66\uff0c\u6c90\u56fd\u516c\u65ad\u65ad\u4e0d\u6562\u8fdd\u6297\uff0c\u60f3\u5230\u5927\u7406\u7684\u4e00\u573a\u5e72\u6208\uff0c\u4ece\u6b64\u53ef\u4ee5\u6d88\u5f2d\uff0c\u7eb5\u662f\u8eab\u5192\u5947 \u9669\uff0c\u4e5f\u7b97\u503c\u5f97\u7684\u4e86\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7eed\u9053\uff1a\u201c\u6ce2\u65af\u5f53\u5e74\u66fe\u53d7\u8499\u53e4\u94c1\u8e44\u8e81\u8e7f\uff0c\u63d0\u8d77\u2018\u9ec4\u7978\u2019\uff0c\u4eba\u4eba\u53d8\u8272\u3002\u5982\u4eca\u9791\u977c\u7684\u5c0f \u7687\u5b50\u4e4c\u8bc3\u514b\u56fe\uff0c\u7ee7\u627f\u74e6\u523a\u9738\u4e1a\uff0c\u56fd\u52bf\u66f4\u76db\uff0c\u5175\u529b\u76f4\u5230\u4e2d\u4e9a\u7ec6\u4e9a\uff0c\u51e0\u4e0e\u6ce2\u65af\u5e1d\u56fd\u63a5\u58e4\u3002\u7687\u4e0a\u82e5 \u6d3e\u9063\u4f7f\u81e3\uff0c\u5efa\u8bae\u4e0e\u6ce2\u65af\u8054\u76df\uff0c\u5171\u9632\u631e\u9791\u977c\uff0c\u60f3\u6765\u6ce2\u65af\u7687\u5e1d\uff0c\u5b9a\u8868\u8d5e\u540c\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u4e2d\u56fd\u897f\u5317 \u7684\u8fb9\u60a3\uff0c\u5f53\u53ef\u51cf\u8f7b\uff0c\u5b9e\u4e43\u4e24\u56fd\u4e4b\u5229\u4e5f\u3002\u201d\u7948\u9547\u4e4b\u613f\u5c01\u6bb5\u6f84\u82cd\u4e3a\u5927\u7406\u85e9\u738b\uff0c\u5c31\u6b63\u662f\u4e3a\u4e86\u8fd9\u4e2a\u7f18 \u6545\u3002\u867d\u7136\u5bf9\u5f20\u4e39\u67ab\u751a\u4e3a\u5fcc\u6068\uff0c\u4e5f\u4e0d\u5f97\u4e0d\u70b9\u5934\u8d5e\u9053\uff1a\u201c\u5f20\u5148\u751f\u6df1\u8c0b\u4e3a\u56fd\uff0c\u6715\u5931\u656c\u4e86\u3002\u518d\u8d50\u9152\u4e09 \u676f\uff0c\u5e76\u4f20\u65e8\u5185\u5ead\uff0c\u51c6\u5907\u539a\u8d4f\u3002\u201d\u4e91\u91cd\u5927\u60ca\u5931\u8272\uff0c\u53ea\u9053\u7948\u9547\u53c8\u8981\u5f04\u4ec0\u4e48\u624b\u6bb5\uff0c\u5374\u89c1\u5f20\u4e39\u67ab\u7b11 \u9053\uff1a\u201c\u539a\u8d4f\u4e0d\u6562\u9886\u53d7\uff0c\u8fd9\u9152\u5012\u53ef\u6da6\u6da6\u5589\u5499\u3002\u201d\u6beb\u4e0d\u8e0c\u8e87\u5730\u5c06\u4e09\u676f\u5fa1\u9152\u559d\u4e86\u3002 \u3000\u3000\u4e91\u91cd\u89c1\u5f20\u4e39\u67ab\u559d\u9152\u4e4b\u540e\uff0c\u6beb\u65e0\u5f02\u72b6\uff0c\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\uff0c\u60f3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u7948\u9547\u8981\u4e0e\u6ce2\u65af\u8054 \u76df\uff0c\u5bf9\u6ce2\u65af\u516c\u4e3b\u81ea\u987b\u7b3c\u7edc\uff0c\u5f20\u4e39\u67ab\u662f\u6ce2\u65af\u516c\u4e3b\u6700\u4fe1\u4efb\u7684\u4eba\uff0c\u6bc1\u4e86\u5f20\u4e39\u67ab\u5c31\u7b49\u5982\u6bc1\u4e86\u6865\u6881\uff0c\u7687 \u5e1d\u4ea6\u4e0d\u80fd\u4e0d\u65e0\u6240\u987e\u5fcc\u3002\u201d\u5176\u5b9e\u8fd9\u731c\u5ea6\u4e5f\u53ea\u5bf9\u4e86\u4e00\u534a\uff0c\u7948\u9547\u89c1\u5f20\u4e39\u67ab\u5982\u6b64\u795e\u901a\u5e7f\u5927\uff0c\u8fde\u5916\u56fd\u516c \u4e3b\u4e5f\u80af\u4e3a\u4ed6\u6240\u7528\uff0c\u5bf9\u5f20\u4e39\u67ab\u7684\u5fcc\u60ee\uff0c\u66f4\u662f\u6df1\u4e86\u4e00\u5c42\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7eed\u9053\uff1a\u201c\u73b0\u4e0b\u9791\u977c\u79f0\u96c4\u4e8e\u897f\u5317\uff0c\u502d\u5bc7\u867d\u88ab\u6c11\u519b\u632b\u8d25\uff0c\u4f46\u4ecd\u9a9a\u6270\u4e1c\u5357\uff0c\u66f4\u53ef\u8651\u8005\uff0c \u6ee1\u5dde\u53c8\u5d1b\u8d77\u4e8e\u4e1c\u5317\uff0c\u96c6\u5175\u5173\u5916\uff0c\u7aa5\u4f3a\u4e2d\u539f\u3002\u7687\u4e0a\u82e5\u4e0d\u5e7f\u65bd\u4ec1\u653f\uff0c\u5584\u7528\u6c11\u529b\uff0c\u53ea\u6015\u5c1a\u6709\u7b2c\u4e8c\u6b21 \u571f\u6728\u5821\u4e4b\u53d8\u3002\u201d\u7948\u9547\u9053\uff1a\u201c\u6715\u867d\u5fb7\u8584\u80fd\u9c9c\uff0c\u81ea\u95ee\u8fd8\u4e0d\u662f\u660f\u5eb8\u4e4b\u4e3b\uff0c\u5f20\u5148\u751f\u82e5\u80af\u8f85\u4f50\u671d\u5ef7\uff0c\u8054 \u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u82e5\u7136\u4e0d\u80af\uff0c\u4e5f\u8bf7\u4e0d\u8981\u53bb\u52a9\u957f\u53db\u9006\u4e4b\u52bf\u3002\u201d\u8bdd\u950b\u53c8\u8f6c\u5230\u4e86\u5f20\u4e39\u67ab\u76f8\u52a9\u6c5f\u5357\u4e49\u519b\u7684 \u4e8b\u60c5\u4e0a\u3002\u5f20\u4e39\u67ab\u795e\u8272\u4e0d\u53d8\uff0c\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\u82e5\u80af\u5916\u5fa1\u5f3a\u654c\uff0c\u5185\u65bd\u4ec1\u653f\uff0c\u5168\u56fd\u767e\u59d3\u90fd\u662f\u62e5\u62a4 \u7687\u4e0a\u7684\u4eba\u3002\u5982\u5176\u4e0d\u7136\uff0c\u7eb5\u6709\u4e00\u4e2a\u6bd5\u64ce\u5929\u6295\u964d\u4e86\uff0c\u8fd8\u6709\u7b2c\u4e8c\u4e2a\u53f6\u5b97\u7559\u4f1a\u518d\u8d77\u6765\u3002\u201d\u7948\u9547\u9ed8\u7136\u4e0d \u8bed\uff0c\u5f20\u4e39\u67ab\u7eed\u9053\uff1a\u201c\u6211\u6240\u8bf4\u7684\u4e09\u4e8b\uff0c\u81ea\u77e5\u662f\u9006\u8033\u4e4b\u8a00\uff0c\u5374\u65e0\u4e00\u4e0d\u662f\u4e3a\u7687\u4e0a\u6253\u7b97\u3002\u4e0e\u6ce2\u65af\u8054 \u76df\uff0c\u53ef\u5236\u9791\u977c\u2026\u2026\u201d\u7948\u9547\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u4e0d\u662f\u5df2\u5141\u4e86\u5148\u751f\u6240\u594f\u4e48\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8ba9\u53f6\u6210\u6797\u4e3a \u7687\u4e0a\u5b88\u62a4\u6d77\u5916\u8bf8\u5c9b\uff0c\u5373\u505c\u56f4\u88ad\u4e49\u519b\u4e4b\u4ee4\u3002\u201d\u7948\u9547\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u6b64\u4e8b\u518d\u4ece\u957f\u8ba1\u8bae\u3002\u201d\u5f20\u4e39 \u67ab\u4e0d\u7406\u7948\u9547\u7684\u63d2\u53e3\uff0c\u4e00\u53e3\u6c14\u8bf4\u4e0b\u53bb\u9053\uff1a\u201c\u4e3a\u4e8e\u9601\u8001\u96ea\u51a4\uff0c\u4e0b\u7f6a\u5df2\u8bcf\uff0c\u4f7f\u5929\u4e0b\u767e\u59d3\u54b8\u77e5\u7687\u4e0a\u662f \u77e5\u9519\u80fd\u6539\u7684\u8d24\u541b\uff0c\u767e\u59d3\u624d\u80fd\u4e3a\u7687\u4e0a\u5c3d\u5fe0\u6548\u6b7b\u3002\u201d\u7948\u9547\u9762\u8272\u4e00\u6c89\uff0c\u65cb\u5373\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u770b\u6765\u6715\u5012 \u5e94\u8be5\u8bf7\u5f20\u5148\u751f\u505a\u5fa1\u53f2\u5927\u592b\u4e86\u3002\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u987e\u5de6\u53f3\u800c\u8a00\u4ed6\uff0c\u6307\u7740\u4e91\u857e\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u966a\u4f34\u6ce2 \u65af\u516c\u4e3b\u7684\u5973\u5b98\u4e48\uff1f\u201d\u592a\u5b50\u594f\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u5f20\u5148\u751f\u7684\u592b\u4eba\uff0c\u6b63\u662f\u5979\u966a\u4f34\u516c\u4e3b\u6765\u7684\u3002\u201d\u4e91\u857e\u8fc8\u4e0a \u4e00\u6b65\uff0c\u9053\uff1a\u201c\u4e91\u9756\u5b59\u5973\u4e91\u857e\u62dc\u89c1\u7687\u4e0a\uff0c\u8c22\u7687\u4e0a\u5bf9\u6211\u4e91\u5bb6\u7684\u51e0\u4ee3\u5927\u6069\uff01\u201d\u7948\u9547\u9762\u8272\u5c34\u5c2c\uff0c\u5bf9\u4e91 \u91cd\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u7684\u59b9\u5b50\uff0c\u602a\u4e0d\u5f97\u4f60\u5b81\u613f\u629b\u4e86\u72b6\u5143\u4e0d\u505a\uff0c\u5374\u968f\u4f60\u7684\u59b9\u592b\u95ef\u8361\u6c5f\u6e56\u3002\u201d \u3000\u3000\u4e91\u91cd\u6ee1\u809a\u76ae\u6c14\u4e0d\u4fbf\u53d1\u4f5c\uff0c\u7948\u9547\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\uff0c\u5927\u5bb6\u518d\u996e\u9152\uff0c\u56fd\u4e8b\u4ee5\u540e\u518d\u8c08\u3002\u201d\u5f20\u4e39\u67ab \u6b63\u60f3\u8bf4\u8bdd\uff0c\u5ffd\u89c1\u4e00\u4e2a\u5185\u76d1\u8d70\u4e86\u51fa\u6765\uff0c\u5411\u7948\u9547\u4f4e\u58f0\u594f\u4e86\u51e0\u53e5\uff0c\u7948\u9547\u9053\uff1a\u201c\u7687\u540e\u542c\u8bf4\u6ce2\u65af\u516c\u4e3b\u8fdc \u9053\u6765\u671d\uff0c\u751a\u662f\u6b22\u559c\uff0c\u8bf7\u516c\u4e3b\u548c\u9a78\u9a6c\u8fdb\u5185\u5ef7\u76f8\u89c1\u3002\u89c1\u6df1\uff0c\u4f60\u966a\u4ed6\u4eec\u53bb\u89c1\u6bcd\u540e\u5427\u3002\u201d\u8fd9\u662f\u5bab\u5ef7\u4eea \u793c\uff0c\u6ce2\u65af\u516c\u5de6\u542c\u4e86\u9a78\u9a6c\u7684\u4f20\u8bd1\uff0c\u6b23\u7136\u7b54\u5141\u3002\u5f20\u4e39\u67ab\u5fc3\u4e2d\u4e00\u51db\uff0c\u4e8e\u52bf\u5374\u53c8\u4e0d\u4fbf\u963b\u62e6\u3002 \u3000\u3000\u5f85\u5230\u6ce2\u65af\u516c\u4e3b\u79bb\u5f00\uff0c\u7948\u9547\u7b11\u9053\uff1a\u201c\u5f20\u5148\u751f\u600e\u4e48\u53c8\u4e0d\u80af\u559d\u9152\u4e86\u3002\u201d\u77f3\u9e3f\u535a\u5ffd\u9053\uff1a\u201c\u5f20\u5148\u751f \u662f\u4e00\u4ee3\u6b66\u5b66\u5927\u5e08\uff0c\u9002\u624d\u5df2\u8499\u8d50\u6559\uff0c\u60dc\u672a\u5c3d\u5174\uff0c\u4e14\u5f85\u5974\u624d\u518d\u732e\u8584\u6280\uff0c\u52a9\u4ed6\u9152\u5174\u3002\u201d\u53cc\u6307\u8fde\u5f39\uff0c \u5f53\u5f53\u5f53\u7684\u4e09\u676f\u76db\u6ee1\u9152\u7684\u9152\u676f\uff0c\u76f8\u7ee7\u98de\u8d77\uff0c\u9694\u7740\u4e00\u5e2d\u5411\u5f20\u4e39\u67ab\u7684\u9762\u524d\u98de\u6765\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u77e5\u4ed6\u662f\u5356\u5f04\u6307\u4e0a\u7684\u529f\u592b\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5f20\u67d0\u600e\u6562\u53d7\u8001\u524d\u8f88\u7684\u656c\u9152\uff0c\u5c31\u501f\u8fd9\u9152 \u56de\u656c\u4e86\u5427\uff01\u201d\u4f7f\u51fa\u4e00\u6307\u7985\u7684\u529f\u592b\uff0c\u5c06\u8fd9\u4e09\u4e2a\u9152\u676f\u53c8\u5f39\u4e86\u56de\u53bb\u3002\u4f17\u6b66\u58eb\u4f46\u89c1\u9152\u676f\u98de\u6765\u98de\u53bb\uff0c\u76db \u6ee1\u676f\u4e2d\u7684\u7f8e\u9152\u7adf\u7136\u70b9\u6ef4\u4e0d\u6e85\uff0c\u5fc3\u4e2d\u5747\u662f\u6697\u6697\u559d\u5f69\u3002\u77f3\u9e3f\u535a\u6b63\u60f3\u8fd0\u6307\u518d\u5f39\uff0c\u9152\u676f\u98de\u5230\u4ed6\u7684\u9762 \u524d\uff0c\u5ffd\u5730\u4e00\u9f50\u788e\u88c2\uff0c\u8fd9\u51e0\u4e2a\u9152\u676f\u90fd\u662f\u767d\u7389\u6240\u5236\uff0c\u8d28\u5730\u751a\u575a\uff0c\u7ade\u88ab\u5f20\u4e39\u67ab\u6697\u8fd0\u6307\u529b\u6240\u788e\uff0c\u5927\u51fa \u77f3\u9e3f\u535a\u610f\u5916\uff0c\u90a3\u4e09\u80a1\u9152\u6d6a\uff0c\u5982\u7bad\u5f84\u5c04\uff0c\u77f3\u9e3f\u535a\u52c3\u7136\u5927\u6012\uff0c\u8863\u8896\u4e00\u626c\uff0c\u9152\u82b1\u56db\u6e85\uff0c\u4e24\u80a1\u771f\u529b\u4e00 \u8feb\uff0c\u96e8\u70b9\u822c\u7684\u201c\u9152\u73e0\u201d\u5c04\u5230\u4e24\u65c1\u4f8d\u7acb\u7684\u6b66\u58eb\u9762\u4e0a\uff0c\u4e5f\u50cf\u5f39\u4e38\u4e00\u822c\uff0c\u5413\u5f97\u4f17\u6b66\u7eb7\u7eb7\u8d70\u907f\u3002 \u3000\u3000\u7948\u9547\u7b11\u9053\uff1a\u201c\u597d\u529f\u592b\uff0c\u4e00\u4eba\u732e\u6280\u4f55\u5982\u4e24\u4eba\u5408\u6f14\uff0c\u65e2\u7136\u662f\u5c06\u9047\u826f\u6750\uff0c\u77f3\u8001\u5e08\u4f60\u5c31\u4e0e\u5f20\u5148\u751f \u7a0d\u4e8b\u5468\u65cb\uff0c\u8ba9\u4ed6\u4eec\u5f00\u5f00\u773c\u754c\u5427\uff01\u201d\u77f3\u9e3f\u535a\u5927\u53eb\u4e00\u58f0\u201c\u5949\u65e8\u201d\uff0c\u98de\u8eab\u8dc3\u8fc7\u684c\u5b50\uff0c\u63d0\u817f\u4fbf\u8e22\uff0c\u7aef \u7684\u662f\u5feb\u5982\u95ea\u7535\uff0c\u4f17\u6b66\u571f\u89c1\u5f20\u4e39\u67ab\u4ecd\u662f\u795e\u8272\u81ea\u5982\u5730\u5750\u5728\u6905\u4e0a\uff0c\u90fd\u9053\u8fd9\u4e00\u8bb0\u201c\u7a9d\u5fc3\u817f\u201d\u975e\u4e2d\u4e0d \u53ef\uff0c\u867d\u7136\u4ed6\u4eec\u90fd\u5df2\u6697\u4e2d\u5949\u65e8\uff0c\u5c06\u5f20\u4e39\u67ab\u5f53\u4f5c\u52b2\u654c\uff0c\u6709\u4e9b\u4ecd\u662f\u4e0d\u81ea\u7981\u5730\u53eb\u51fa\u58f0\u6765\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u8f70\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u4eba\u5f71\u98de\u817e\uff0c\u773c\u82b1\u64a9\u4e71\uff0c\u4f17\u6b66\u58eb\u60ca\u9b42\u7a0d\u5b9a\uff0c\u4f46\u89c1\u90a3\u5f20\u6905\u5b50\u88ab\u8e22 \u4e0b\u7389\u9636\uff0c\u788e\u6210\u7247\u7247\uff0c\u800c\u5f20\u4e39\u67ab\u5374\u7acb\u5728\u9601\u5b50\u7684\u5c0f\u5fc3\uff0c\u6b66\u58eb\u4e2d\u4e0d\u4e4f\u9ad8\u624b\uff0c\u7adf\u7136\u770b\u4e0d\u6e05\u695a\u4ed6\u7528\u4ec0\u4e48 \u8eab\u6cd5\u5728\u90a3\u7edd\u9669\u4e4b\u9645\u8131\u8eab\u800c\u51fa\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u4ef0\u5929\u5927\u7b11\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u597d\u4e00\u573a\u9e3f\u95e8\u5bb4\u5440\uff01\u965b\u4e0b\u4e5f\u592a\u62ac\u4e3e\u6211\u4e86\u3002\u201d\u7b11\u58f0\u672a\u7edd\uff0c\u77f3 \u9e3f\u535a\u65e9\u98de\u8eab\u6251\u5230\uff0c\u5de6\u638c\u4e00\u62e8\uff0c\u53f3\u638c\u659c\u5288\u3002\u5f20\u4e39\u67ab\u8ba4\u5f97\u5176\u4e2d\u85cf\u7740\u5206\u7b4b\u9519\u9aa8\u624b\u7684\u6700\u4e0a\u4e58\u624b\u6cd5\uff0c\u4e0d \u6562\u6020\u6162\uff0c\u4e00\u632b\u8eab\u4e00\u7ffb\u638c\uff0c\u53cd\u624b\u5288\u53bb\uff0c\u77f3\u9e3f\u535a\u53cc\u638c\u4e00\u5408\uff0c\u84e6\u7136\u5f80\u5916\u4e00\u5206\uff0c\u89e3\u5f00\u5f20\u4e39\u67ab\u7684\u653b\u52bf\uff0c \u4f38\u5f00\u5341\u6307\u4fbf\u6293\uff0c\u770b\u6765\u7528\u7684\u662f\u9e70\u722a\u529f\uff0c\u53ea\u8981\u88ab\u4ed6\u642d\u4e0a\uff0c\u7acb\u523b\u4fbf\u662f\u7b4b\u65ad\u9aa8\u788e\u4e4b\u707e\uff0c\u4ed6\u5e95\u5b50\u91cc\u4ecd\u662f \u5206\u7b4b\u9519\u9aa8\u7684\u529f\u592b\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u9000\u540e\u4e24\u6b65\uff0c\u4e00\u638c\u62cd\u51fa\uff0c\u547c\u547c\u5e26\u98ce\uff0c\u63a5\u7740\u53c8\u662f\u4e00\u8bb0\u957f\u62f3\uff0c\u5de6\u638c\u53f3\u638c\uff0c\u76f4\u5982\u5de8\u65a7\u5f00 \u5c71\uff0c\u94c1\u6376\u51ff\u77f3\uff0c\u62f3\u98ce\u6240\u81f3\uff0c\u903c\u5f97\u4f17\u6b66\u58eb\u7eb7\u7eb7\u9000\u540e\uff0c\u767b\u65f6\u817e\u51fa\u4e00\u7247\u7a7a\u5730\uff0c\u90a3\u4e07\u6625\u9601\u5360\u5730\u751a\u5e7f\uff0c \u53ef\u4ee5\u7b75\u5f00\u767e\u5e2d\uff0c\u4e0d\u89c9\u62e5\u6324\uff0c\u90a3\u51e0\u5f20\u5706\u684c\u9694\u7740\u4e86\u4e00\u5835\u4eba\u5899\uff0c\u800c\u4e14\u79bb\u5f00\u4e24\u4eba\u6bd4\u6b66\u7684\u573a\u5fc3\u4e5f\u6709\u4e09\u4e08 \u5f00\u5916\uff0c\u684c\u4e0a\u7684\u676f\u76d8\u7897\u789f\uff0c\u4ecd\u662f\u9707\u5f97\u54d7\u5566\u5566\u5730\u4e00\u7247\u4f5c\u54cd\uff0c\u5e78\u800c\u90fd\u662f\u9ec4\u94dc\u6216\u767d\u7389\u7684\u5668\u76bf\uff0c\u8981\u4e0d\u7136 \u5b9a\u7ed9\u9707\u788e\u65e0\u7591\u3002 \u3000\u3000\u77f3\u9e3f\u535a\u7684\u5206\u7b4b\u9519\u9aa8\u624b\u867d\u7136\u662f\u5929\u4e0b\u7b2c\u4e00\uff0c\u82e6\u4e8e\u88ab\u5f20\u4e39\u67ab\u7684\u62f3\u98ce\u6240\u903c\uff0c\u8fd1\u4e0d\u4e86\u8eab\uff0c\u6597\u4e86\u4e09\u5341 \u6765\u62db\uff0c\u4ecd\u662f\u4e0d\u5206\u80dc\u8d1f\uff0c\u77f3\u9e3f\u535a\u65e9\u5728\u7687\u5e1d\u9762\u524d\u5938\u4e0b\u6d77\u53e3\uff0c\u8fd9\u65f6\u6218\u5f20\u4e39\u67ab\u4e0d\u4e0b\uff0c\u6df1\u89c9\u9762\u4e0a\u65e0\u5149\uff0c \u5fc3\u4e2d\u7126\u8e81\uff0c\u84e6\u5730\u4e00\u58f0\u5927\u559d\uff0c\u6b3a\u8eab\u6251\u8fdb\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u77f3\u9e3f\u640f\u7684\u80a9\u4e0a\u6328\u4e86\u4e00\u62f3\uff0c\u4f46\u5374 \u5df2\u62a2\u8fdb\u5185\u5708\uff0c\u6765\u626d\u5f20\u4e39\u67ab\u7684\u624b\u8155\uff0c\u5f20\u4e39\u67ab\u62f3\u52bf\u4e00\u6536\uff0c\u56de\u638c\u62a4\u8eab\uff0c\u7adf\u7ed9\u4ed6\u903c\u5f97\u8fde\u8fde\u540e\u9000\uff01 \u3000\u3000\u5206\u7b4b\u9519\u9aa8\u7684\u624b\u6cd5\u5229\u4e8e\u8fd1\u8eab\u8089\u640f\uff0c\u77f3\u9e3f\u535a\u4ee5\u8fd9\u95e8\u7edd\u5b66\u79f0\u9738\u6b66\u6797\uff0c\u88ab\u4ed6\u62a2\u5165\u5185\u5708\uff0c\u653b\u52bf\u66f4\u89c1 \u51cc\u5389\uff0c\u4ee5\u5f20\u4e39\u67ab\u7684\u529f\u529b\uff0c\u62f3\u52bf\u4e5f\u81ea\u65bd\u5c55\u4e0d\u5f00\u3002\u5fa1\u6797\u519b\u7edf\u9886\u5a04\u6850\u5b59\u89c1\u5e08\u7236\u5360\u4e86\u4e0a\u98ce\uff0c\u5927\u58f0\u559d \u5f69\u3002\u9163\u6597\u95f4\u5ffd\u89c1\u5f20\u4e39\u67ab\u547c\u547c\u547c\u8fde\u5288\u4e09\u638c\uff0c\u8fd9\u4e09\u638c\u7a81\u7136\u8f6c\u5b88\u4e3a\u653b\uff0c\u80f8\u524d\u95e8\u6237\u5927\u5f00\uff0c\u5a04\u6850\u5b59\u5fc3 \u9053\uff1a\u201c\u53ef\u7b11\u4f60\u4ee5\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\u81ea\u547d\uff0c\u7adf\u4e0d\u61c2\u5f97\u6211\u5e08\u7236\u8fd9\u624b\u5206\u7b4b\u9519\u9aa8\u624b\u7684\u795e\u5999\uff0c\u4f60\u5982\u6b64\u6b3a\u654c\u5f3a \u653b\u81ea\u9732\u7834\u7efd\uff0c\u5f53\u771f\u662f\u81ea\u53d6\u5176\u8fb1\u4e86\uff01\u201d\u6b63\u5f85\u5927\u58f0\u53eb\u597d\uff0c\u53ea\u89c1\u77f3\u9e3f\u535a\u53f3\u638c\u4e00\u8fce\uff0c\u5de6\u638c\u4e00\u642d\uff0c\u642d\u4e0a \u4e86\u5f20\u4e39\u67ab\u7684\u638c\u80cc\uff0c\u53f3\u638c\u7acb\u523b\u53cd\u624b\u65ab\u4e0b\uff0c\u773c\u89c1\u5f20\u4e39\u67ab\u7684\u624b\u8155\u5c31\u8981\u7ed9\u4ed6\u626d\u65ad\uff0c\u800c\u4e14\u4e0b\u4e00\u624b\u5de6\u638c\u53ea \u8981\u5f80\u4e0a\u4e00\u52fe\uff0c\u5f20\u4e39\u67ab\u7684\u80f8\u9aa8\u4e5f\u5fc5\u7136\u8981\u88ab\u4ed6\u626d\u65ad\uff0c\u4e91\u91cd\u89c1\u4e86\u8fd9\u4e2a\u60c5\u5f62\uff0c\u4e5f\u7981\u4e0d\u4f4f\u5927\u60ca\u5931\u8272\uff0c\u8981 \u77e5\u8fd9\u4e24\u624b\u90fd\u662f\u6700\u4e0a\u4e58\u7684\u5206\u7b4b\u9519\u9aa8\u624b\u7684\u72e0\u6bd2\u7edd\u62db\uff0c\u5f20\u4e39\u67ab\u7eb5\u662f\u6b66\u5b66\u901a\u7384\uff0c\u8fd9\u4e24\u8bb0\u7edd\u62db\uff0c\u4e5f\u672a\u5fc5 \u80fd\u4e00\u9f50\u907f\u8fc7\uff01 \u3000\u3000\u5a04\u6850\u5b59\u7684\u201c\u597d\uff01\u201d\u5b57\u521a\u521a\u558a\u51fa\uff0c\u5ffd\u89c1\u77f3\u9e3f\u535a\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u53cc\u638c\u90fd\u64a4\u4e86\u56de\u6765\uff0c\u201c\u767b\uff0c \u767b\uff0c\u767b\uff01\u201d\u5730\u5012\u9000\u4e09\u6b65\uff0c\u8138\u4e0a\u73b0\u51fa\u60ed\u6127\u7684\u795e\u8272\uff0c\u539f\u6765\u5f20\u4e39\u67ab\u5728\u8fde\u5288\u4e09\u638c\u4e4b\u65f6\uff0c\u65e9\u5df2\u6599\u5230\u77f3\u9e3f \u535a\u4f1a\u4f7f\u51fa\u90a3\u4e24\u8bb0\u6bd2\u62db\u3002\u4ed6\u81ea\u9732\u7834\u7efd\uff0c\u5176\u5b9e\u662f\u8bf1\u654c\u4e4b\u8ba1\uff0c\u628a\u771f\u6c14\u5168\u63d0\u5230\u80f8\u53e3\u201c\u7487\u7391\u7a74\u201d\u7684\u5468\u56f4 \u4e09\u5bf8\u4e4b\u5904\uff0c\u679c\u7136\u77f3\u9e3f\u535a\u5de6\u624b\u90a3\u4e00\u6293\u6b63\u6b63\u5411\u7740\u8fd9\u4e2a\u65b9\u4f4d\u6293\u4e0b\uff0c\u4f46\u89c9\u5f20\u4e39\u67ab\u80f8\u53e3\u7684\u808c\u8089\u8f6f\u7ef5\u7ef5\u5730 \u7adf\u628a\u4ed6\u7684\u4e94\u6307\u5438\u4f4f\uff0c\u84e6\u7136\u95f4\u4e00\u80a1\u65e0\u5f62\u7684\u52b2\u529b\u53cd\u5f39\u51fa\u6765\uff0c\u77f3\u9e3f\u535a\u864e\u53e3\u9178\u9ebb\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u626d\u4f4f\u5f20 \u4e39\u67ab\u624b\u8155\u7684\u90a3\u53ea\u53f3\u624b\uff0c\u672a\u66fe\u4f7f\u51fa\u52b2\u529b\uff0c\u4e5f\u7ed9\u5f20\u4e39\u67ab\u4e00\u94ee\u94ee\u5f00\uff0c\u4f46\u89c1\u5f20\u4e39\u67ab\u5de6\u624b\u4e2d\u6307\u6307\u5c16\u4e00 \u7fd8\uff0c\u6b63\u6b63\u5bf9\u7740\u81ea\u5df1\u7684\u54bd\u5589\u8981\u5bb3\uff0c\u77f3\u9e3f\u535a\u9886\u6559\u8fc7\u4ed6\u7684\u4e00\u6307\u7985\u529f\u592b\uff0c\u77e5\u9053\u53ea\u51ed\u8fd9\u4e00\u6307\u4e4b\u529b\uff0c\u4fbf\u53ef \u4ee5\u7a7f\u5899\u6d1e\u58c1\uff0c\u4f55\u51b5\u662f\u5589\u5934\u7684\u8106\u9aa8\uff0c\u77f3\u9e3f\u535a\u8fd9\u4e00\u5413\u9b42\u98de\u9b44\u6563\uff0c\u614c\u4e0d\u8fed\u5730\u628a\u53cc\u638c\u5c3d\u64a4\u51fa\u6765\uff0c\u53ea\u89c1 \u5f20\u4e39\u67ab\u5fae\u5fae\u4e00\u7b11\uff0c\u5e76\u672a\u4e58\u4ed6\u53cc\u638c\u8fd8\u6765\u4e0d\u53ca\u56de\u9632\u4e4b\u9645\uff0c\u4e58\u52bf\u6233\u6765\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e5f\u81ea\u5fc3\u4e2d\u6697\u547c\u201c\u4fa5\u5e78\u201d\uff0c\u5fc3\u9053\uff1a\u201c\u82e5\u7136\u8fd9\u8001\u5934\u513f\u770b\u7834\u6211\u7684\u9884\u8c0b\uff0c\u90a3\u4e00\u6293\u53ea\u8981\u79bb\u5f00 \u7487\u7391\u7a74\u4e09\u5bf8\u4e4b\u5730\uff0c\u6211\u5c31\u8981\u4e0e\u4ed6\u540c\u5f52\u4e8e\u5c3d\u3002\u601c\u60dc\u4ed6\u8fd9\u8eab\u7edd\u5b66\u6b66\u529f\uff0c\u66f4\u517c\u770b\u5728\u4ed6\u662f\u8001\u8f88\u7684\u4efd\u4e0a\uff0c \u66f4\u4e0d\u5fcd\u53d6\u4ed6\u6027\u547d\uff0c\u4e2d\u6307\u4e00\u52fe\uff0c\u6536\u4e86\u53e3\u6765\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u77f3\u8001\u524d\u8f88\u7684\u5206\u7b4b\u9519\u9aa8\u624b\u6cd5\uff0c\u679c\u7136\u662f\u4e16 \u4e0a\u65e0\u53cc\uff0c\u5f20\u67d0\u5fc3\u670d\u53e3\u670d\uff0c\u54b1\u4eec\u53ef\u4e0d\u7528\u518d\u8f83\u91cf\u4e86\u5427\u3002\u201d \u3000\u3000\u77f3\u9e3f\u535a\u6ee1\u9762\u6da8\u7ea2\uff0c\u4e0d\u77e5\u6240\u63aa\uff0c\u90a3\u7a7f\u7740\u957f\u886b\u3001\u5934\u6234\u5112\u51a0\u7684\u7c97\u8c6a\u5927\u6c49\u5ffd\u5730\u8df3\u4e86\u51fa\u6765\uff0c\u624b\u634f\u4e86 \u628a\u94c1\u6247\uff0c\u8fce\u98ce\u4e00\u7ad9\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5f20\u4e39\u67ab\uff0c\u695a\u67d0\u4e0d\u624d\uff0c\u8eac\u9022\u76db\u4f1a\uff0c\u975e\u5f97\u9886\u6559\u4f60\u5929\u4e0b\u7b2c\u4e00\u5251\u7684 \u5251\u6cd5\u4e0d\u53ef\uff01\u201d\u4e0d\u7531\u5206\u8bf4\uff0c\u94c1\u6247\u4e00\u6307\uff0c\u4fbf\u63d2\u8fdb\u4e24\u4eba\u4e2d\u95f4\u3002\u4e91\u91cd\u3001\u4e91\u857e\u542c\u4ed6\u81ea\u62a5\u540d\u5934\uff0c\u8fd9\u624d\u77e5\u9053 \u4ed6\u662f\u94c1\u6247\u4e66\u751f\u695a\u5927\u9f50\u3002\u6b64\u4eba\u8bfb\u4e66\u4e0d\u6210\uff0c\u8f6c\u800c\u4e60\u6b66\uff0c\u957f\u76f8\u7c97\u8c6a\uff0c\u5374\u504f\u504f\u98ce\u6d41\u81ea\u8d4f\uff0c\u7231\u4f5c\u5112\u751f\u6253 \u626e\uff0c\u6b22\u559c\u6389\u6587\uff0c\u4f46\u4ed6\u867d\u7136\u7c97\u91ce\u65e0\u6587\uff0c\u90a3\u8eab\u6b66\u529f\u5374\u662f\u975e\u540c\u5c0f\u53ef\uff01 \u3000\u3000\u773c\u89c1\u5f20\u4e39\u67ab\u4fbf\u8981\u88ab\u695a\u5927\u9f50\u4e0e\u77f3\u9e3f\u535a\u8054\u624b\u56f4\u653b\uff0c\u4e91\u91cd\u52c3\u7136\u5927\u6012\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u5c06\u5835\u5728\u524d\u9762\u7684 \u6b66\u58eb\u626b\u5f97\u6b6a\u6b6a\u659c\u659c\uff0c\u8d8a\u4f17\u800c\u51fa\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u5f53\u771f\u662f\u9e3f\u95e8\u5bb4\u4e48\uff1f\u201d\u53cd\u624b\u4e00\u638c\uff0c\u628a\u695a\u5927\u9f50\u7684\u94c1 \u6247\u8361\u5f00\uff0c\u6b63\u5f85\u8fdb\u62db\uff0c\u5374\u89c1\u5f20\u4e39\u67ab\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u8fd9\u8bdd\u5e94\u8be5\u8bf7\u95ee\u7687\u4e0a\uff01\u201d\u98de\u8eab\u4e00\u63a0\uff0c\u5feb\u5982\u95ea\u7535\uff0c \u7adf\u7136\u4ece\u90a3\u5835\u4eba\u5899\u4e0a\u7a7a\u98de\u8fc7\uff0c\u76f4\u6251\u5fa1\u5ea7\u3002\u4f17\u6b66\u58eb\u60ca\u9192\u4e4b\u65f6\uff0c\u5f20\u4e39\u67ab\u5df2\u6251\u5230\u4e86\u7687\u5e1d\u7684\u8eab\u65c1\uff0c\u4f17\u6b66 \u571f\u767b\u65f6\u5927\u4e71\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c31\u5728\u5f20\u4e39\u67ab\u4e00\u722a\u6293\u4e0b\u4e4b\u65f6\uff0c\u7948\u9547\u5411\u540e\u4e00\u9760\u3002\u5899\u58c1\u5ffd\u5730\u88c2\u5f00\u4e00\u9053\u95e8\u6237\uff0c \u5f85\u5f20\u4e39\u67ab\u6251\u5230\uff0c\u7948\u9547\u5df2\u662f\u8eb2\u8fdb\u53bb\u4e86\u3002\u5c31\u5728\u8fd9\u4e2a\u65f6\u5019\uff0c\u4f8d\u5019\u5728\u7687\u5e1d\u8eab\u8fb9\u7684\u5c60\u9f99\u5c0a\u8005\u548c\u6458\u661f\u4e0a\u4eba \u4e5f\u5df2\u62a1\u5200\u53d1\u638c\uff0c\u963b\u6b62\u4e86\u5f20\u4e39\u67ab\u7684\u53bb\u8def\u3002 \u3000\u3000\u53ea\u542c\u5f97\u7948\u9547\u5728\u590d\u58c1\u4e4b\u5185\u4f20\u58f0\u53eb\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u610f\u6b32\u5f11\u541b\uff0c\u5927\u9006\u4e0d\u9053\uff0c\u7740\u5373\u64d2\u6765\uff0c\u683c\u6740\u4e0d \u8bba\u3002\u4e91\u91cd\u5fc3\u6000\u4e0d\u5fff\uff0c\u4f4e\u6bc1\u541b\u4e0a\uff0c\u4ea6\u5c5e\u7f6a\u65e0\u53ef\u8d66\uff0c\u4e00\u5e76\u64d2\u4e86\u3002\u201d\u5f20\u4e39\u67ab\u5927\u7b11\u9053\uff1a\u201c\u8fde\u4e8e\u9601\u8001\u4e5f \u7ed9\u4f60\u4ee5\u53db\u9006\u4e0d\u9053\u4e4b\u7f6a\u5904\u6b7b\uff0c\u4e39\u67ab\u627f\u53d7\u6b64\u7f6a\uff0c\u8363\u5e78\u4e4b\u81f3\uff0c\u867d\u6b7b\u4f55\u8f9e\uff01\u201d\u4ed6\u672c\u6b32\u64d2\u7740\u7948\u9547\uff0c\u4f5c\u4e3a \u4eba\u8d28\uff0c\u51b2\u51fa\u91cd\u56f4\uff0c\u54ea\u77e5\u7948\u9547\u4e5f\u65e9\u5c31\u5e03\u4e0b\u673a\u5173\uff0c\u5b58\u5fc3\u5c06\u4ed6\u9664\u6389\u3002\u4f46\u89c1\u4f17\u6b66\u571f\u5982\u6f6e\u6d8c\u5230\uff0c\u5f20\u4e39\u67ab \u8fd9\u4e00\u751f\u5c61\u7ecf\u98ce\u6d6a\uff0c\u5374\u8fd8\u672a\u6709\u8fc7\u4eca\u6b21\u4e4b\u9669\uff0c\u5fc3\u4e2d\u81ea\u601d\uff1a\u53ea\u6015\u5f53\u771f\u8981\u8c41\u51fa\u6027\u547d\u4e86\uff01 \u3000\u3000\u6458\u661f\u4e0a\u4eba\u7684\u201c\u6458\u661f\u624b\u201d\u4ee5\u5feb\u3001\u72e0\u3001\u53d8\u4e09\u5b57\u8457\u540d\u6b66\u6797\uff0c\u90a3\u4e00\u638c\u5288\u6765\uff0c\u540e\u53d1\u5148\u81f3\uff0c\u5f20\u4e39\u67ab\u4e00 \u58f0\u51b7\u7b11\uff0c\u671d\u7740\u4ed6\u7684\u864e\u53e3\uff0c\u4e2d\u6307\u4e00\u5f39\uff0c\u82e5\u662f\u6b66\u529f\u7a0d\u5f31\uff0c\u8fd9\u4e00\u5f39\u975e\u7ed9\u4ed6\u5f39\u65ad\u7b4b\u8109\u4e0d\u53ef\uff0c\u6458\u661f\u4e0a\u4eba \u7684\u638c\u6cd5\u53d8\u5316\u751a\u591a\uff0c\u4e00\u89c1\u4e0d\u5999\uff0c\u624b\u8155\u4e00\u62e7\uff0c\u638c\u950b\u7acb\u523b\u504f\u5f00\uff0c\u6362\u4e86\u4e00\u4e2a\u65b9\u5411\uff0c\u5316\u638c\u4e3a\u62ff\uff0c\u8f6c\u6293\u5f20 \u4e39\u67ab\u7684\u7435\u7436\u8f6f\u9aa8\uff0c\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u5feb\u3001\u72e0\u3001\u53d8\u4e09\u5b57\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u518d\u7ec3\u5341\u5e74\uff0c\u53ef\u4ee5\u6210\u4e3a\u7b2c\u4e00 \u6d41\u9ad8\u624b\u3002\u201d\u80a9\u5934\u4e00\u649e\uff0c\u4e00\u4e2a\u65cb\u8eab\u4fbf\u53cd\u81c2\u64d2\u62ff\uff0c\u8fd9\u6837\u4e00\u62db\u4e24\u7528\uff0c\u6bd4\u6458\u661f\u4e0a\u4eba\u66f4\u5feb\u66f4\u72e0\uff0c\u4e00\u9762\u7528 \u94c1\u80a9\u818a\u7684\u9633\u521a\u4e4b\u529b\uff0c\u4e00\u9762\u7528\u64d2\u62ff\u624b\u7684\u9634\u67d4\u624b\u6cd5\uff0c\u6458\u661f\u4e0a\u4eba\u9976\u662f\u6b66\u529f\u591a\u53d8\uff0c\u4e5f\u65e0\u5584\u6cd5\u62db\u67b6\uff01 \u3000\u3000\u4f46\u89c1\u7d2b\u58a8\u8272\u7684\u5200\u5149\u4e00\u95ea\uff0c\u5c60\u9f99\u5c0a\u8005\u8fd9\u4e00\u5200\u89d1\u51c6\u4e86\u5f20\u4e39\u67ab\u7684\u80a9\u80db\u9aa8\u780d\u4e0b\uff0c\u4ed6\u5728\u82cd\u5c71\u88ab\u4e91\u91cd \u62d7\u6298\u4e86\u4e00\u6761\u624b\u81c2\uff0c\u4e24\u5e74\u6765\u82e6\u70bc\u72ec\u81c2\u5200\u6cd5\uff0c\u867d\u7136\u51fa\u624b\u8f83\u6458\u661f\u4e0a\u4eba\u7a0d\u6162\uff0c\u4f46\u8fd9\u4e00\u5200\u780d\u4e0b\uff0c\u53c8\u72e0\u53c8 \u51c6\uff0c\u6bd4\u4e00\u822c\u5200\u6cd5\u5374\u5389\u5bb3\u5f97\u591a\u3002 \u3000\u3000\u5374\u89c1\u5f20\u4e39\u67ab\u65e2\u4e0d\u62db\u67b6\uff0c\u4e5f\u4e0d\u95ea\u907f\uff0c\u4ecd\u7136\u4f38\u638c\u653b\u51fb\u6458\u661f\u4e0a\u4eba\uff0c\u5c60\u9f99\u5c0a\u8005\u5fc3\u4e2d\u4e00\u51db\uff0c\u53cd\u800c\u4e0d \u6562\u6063\u610f\u5288\u4e0b\uff0c\u4f46\u542c\u5f97\u54ce\u54df\u4e00\u58f0\uff0c\u6458\u661f\u4e0a\u4eba\u7ed9\u5f20\u4e39\u67ab\u4e00\u638c\u51fb\u5012\uff0c\u5e78\u800c\u4ed6\u53d8\u5316\u5f97\u5feb\uff0c\u8981\u4e0d\u7136\u624b\u8155 \u4e5f\u88ab\u626d\u6298\u3002\u5c31\u5728\u8fd9\u540c\u4e00\u65f6\u95f4\uff0c\u5f20\u4e39\u67ab\u7684\u80a9\u818a\u4e00\u649e\uff0c\u5374\u628a\u4e00\u4e2a\u8eab\u6750\u9b41\u4f1f\u7684\u6b66\u58eb\u649e\u5f97\u6070\u6070\u5411\u7740\u5c60 \u9f99\u5c0a\u8005\u98de\u6765\uff0c\u6c34\u725b\u822c\u7684\u8eab\u8eaf\u649e\u5f97\u5c60\u9f99\u5c0a\u8005\u4e5f\u51e0\u4e4e\u8dcc\u5012\uff0c\u5c60\u9f99\u5c0a\u8005\u7edd\u5bf9\u6599\u60f3\u4e0d\u5230\u5f20\u4e39\u67ab\u7adf\u7136\u4f1a \u51fa\u6b64\u602a\u62db\uff0c\u90a3\u67c4\u542b\u6709\u5267\u6bd2\u7684\u5c60\u9f99\u5200\u7adf\u7136\u63d2\u8fdb\u4e86\u81ea\u5df1\u4eba\u7684\u5fc3\u7a9d\uff01 \u3000\u3000\u6458\u661f\u4e0a\u4eba\u72b9\u672a\u722c\u8d77\uff0c\u5c60\u9f99\u5c0a\u8005\u88ab\u90a3\u6b66\u58eb\u538b\u4f4f\uff0c\u6bd2\u5200\u4e5f\u8fd8\u672a\u6765\u5f97\u53ca\u62d4\u51fa\u6765\uff0c\u5f20\u4e39\u67ab\u8eab\u624b\u4f55 \u7b49\u5feb\u6377\uff0c\u8d81\u8fd9\u65f6\u673a\uff0c\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u907f\u5f00\u4e86\u5de6\u9762\u88ad\u6765\u7684\u4e00\u5200\uff0c\u53cd\u624b\u4e00\u62ff\uff0c\u53c8\u628a\u53f3\u9762\u51b2\u6765\u7684\u4e00 \u540d\u6b66\u58eb\u7684\u8109\u95e8\u6263\u7740\uff0c\u4e00\u628a\u63d0\u4e86\u8d77\u6765\uff0c\u5c31\u5c06\u4ed6\u4f5c\u4e3a\u5175\u5668\uff0c\u4e00\u4e2a\u65cb\u98ce\u6025\u821e\uff0c\u626b\u5012\u4e86\u51e0\u4e2a\u8fd1\u8eab\u7684\u5fa1 \u6797\u519b\u7edf\u9886\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u4ee5\u5927\u6454\u7891\u624b\u7684\u529f\u592b\uff0c\u5c06\u90a3\u6b66\u58eb\u671d\u7740\u4eba\u4e1b\u4e4b\u4e2d\u63b7\u53bb\uff0c\u767b\u65f6\u51b2\u5f00\u4e86\u4e00\u6761\u51fa \u8def\uff0c\u773c\u5149\u4e00\u5c04\uff0c\u53ea\u89c1\u4e91\u91cd\u3001\u4e91\u857e\u5df2\u5728\u5408\u6218\u90a3\u4e2a\u94c1\u6247\u4e66\u751f\u695a\u5927\u9f50\u3002 \u3000\u3000\u4e91\u91cd\u7684\u5e08\u7236\u8463\u5cb3\u72ec\u5f97\u7384\u673a\u9038\u58eb\u201c\u5927\u529b\u91d1\u521a\u624b\u201d\u7684\u79d8\u4f20\uff0c\u4ee5\u5916\u5bb6\u786c\u529f\u517c\u6709\u5185\u5bb6\u52b2\u529b\uff0c\u6b66\u6797 \u4e4b\u4e2d\uff0c\u65e0\u4eba\u53ef\u4e0e\u5339\u654c\uff0c\u4e91\u91cd\u82e6\u7ec3\u4e86\u5341\u5e74\uff0c\u867d\u7136\u5c1a\u672a\u53ca\u5e08\u7236\u76db\u5e74\uff0c\u4f46\u4e5f\u6709\u4e86\u516b\u4e5d\u6210\u706b\u5019\uff0c\u6ee1\u4ee5 \u4e3a\u53ef\u4ee5\u4e00\u638c\u5c06\u90a3\u695a\u5927\u9f50\u51fb\u6bd9\uff0c\u54ea\u77e5\u695a\u5927\u9f50\u7684\u6b66\u529f\uff0c\u8def\u6570\u602a\u5f02\u4e4b\u6781\uff0c\u4e91\u91cd\u90a3\u91d1\u521a\u731b\u6251\u7684\u638c\u529b\uff0c \u8fde\u73af\u4e09\u638c\uff0c\u7adf\u7136\u88ab\u4ed6\u7684\u94c1\u6247\u4e00\u7275\u4e00\u642d\u4e00\u5f15\uff0c\u8f7b\u63cf\u6de1\u5199\u5730\u4fbf\u5c06\u90a3\u5a01\u731b\u65e0\u4f26\u7684\u638c\u529b\u5378\u6389\u4e86\u3002\u5f20\u4e39 \u67ab\u5c1a\u672a\u51b2\u51fa\u91cd\u56f4\uff0c\u89c1\u8fd9\u60c5\u5f62\uff0c\u6025\u5fd9\u626c\u58f0\u53eb\u9053\uff1a\u201c\u521a\u67d4\u517c\u6d4e\uff0c\u9634\u638c\u9632\u8eab\uff0c\u9633\u638c\u51fb\u654c\u3002\u201d\u539f\u6765\u82e5 \u8bba\u5230\u672c\u8eab\u7684\u529f\u529b\uff0c\u695a\u5927\u9f50\u5b9e\u662f\u4e0d\u5982\u4e91\u91cd\uff0c\u4f46\u4ed6\u8fd9\u94c1\u6247\u529f\u957f\u4e8e\u4ee5\u5de7\u964d\u529b\uff0c\u76f8\u540c\u4e8e\u592a\u6781\u62f3\u7684\u201c\u56db \u4e24\u62e8\u5e72\u65a4\u201d\u4e4b\u7406\uff0c\u53ea\u8981\u88ab\u4ed6\u7684\u94c1\u6247\u642d\u4e0a\uff0c\u4e0d\u4f46\u53ef\u4ee5\u5378\u5f00\u654c\u4eba\u7684\u52b2\u529b\uff0c\u800c\u4e14\u53ef\u4ee5\u8feb\u4ee4\u654c\u4eba\u5931\u53bb \u5e73\u8861\uff0c\u91cd\u5fc3\u4e0d\u7a33\uff0c\u5e78\u800c\u4e91\u91cd\u7684\u5185\u5916\u529f\u592b\u5747\u5df2\u5230\u4e86\u4e00\u6d41\u5883\u754c\uff0c\u5b9a\u7740\u91cd\u5fc3\uff0c\u8fd8\u4e0d\u81f3\u4e8e\u7ed9\u4ed6\u501f\u529b\u53cd \u51fb\u3002 \u3000\u3000\u4e91\u91cd\u5f97\u5f20\u4e39\u67ab\u4f20\u58f0\u63d0\u9192\uff0c\u4e00\u638c\u62a4\u80f8\uff0c\u4e00\u638c\u5e94\u654c\uff0c\u4ee5\u521a\u67d4\u517c\u6d4e\u7684\u638c\u529b\u8c28\u614e\u5468\u65cb\uff0c\u695a\u5927\u9f50\u679c \u7136\u4e0d\u6562\u6b3a\u8eab\u8e81\u8fdb\uff0c\u4f46\u89c1\u4ed6\u6247\u5b50\u500f\u5f20\u500f\u5408\uff0c\u5408\u8d77\u6765\u65f6\uff0c\u4fbf\u5f53\u4f5c\u70b9\u7a74\u6485\u4f7f\uff0c\u5f20\u8d77\u6765\u65f6\uff0c\u5374\u53c8\u662f\u5ce8 \u5d4b\u523a\u548c\u5200\u5251\u7684\u8def\u6570\uff0c\u90a3\u5341\u51e0\u652f\u6247\u9aa8\uff0c\u90fd\u662f\u7cbe\u94a2\u6240\u94f8\uff0c\u652f\u652f\u950b\u5229\uff0c\u7684\u786e\u662f\u4e00\u4ef6\u7f55\u89c1\u7684\u5916\u95e8\u5175 \u5668\uff01\u4e91\u91cd\u4e00\u65f6\u672a\u80fd\u9002\u5e94\uff0c\u7adf\u7136\u7ed9\u4ed6\u903c\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u5374\u65e0\u8fd8\u624b\u4e4b\u529b\u3002 \u3000\u3000\u4e91\u857e\u89c1\u72b6\u4e0d\u5999\uff0c\u63c9\u8eab\u6251\u4e0a\uff0c\u695a\u5927\u9f50\u6247\u5b50\u4e00\u5f20\uff0c\u53cd\u624b\u6325\u53bb\uff0c\u5ffd\u5730\u773c\u795e\u4e00\u4e71\uff0c\u4f46\u89c1\u597d\u50cf\u6709\u56db \u4e94\u4e2a\u7ea2\u5986\u5c11\u5987\uff0c\u540c\u65f6\u6251\u4e86\u4e0a\u6765\uff0c\u624b\u4e2d\u94c1\u6247\uff0c\u51e0\u4e4e\u7ed9\u4e91\u857e\u5288\u624b\u593a\u53bb\uff0c\u521a\u521a\u907f\u8fc7\uff0c\u201c\u535c\u201d\u7684\u4e00 \u58f0\uff0c\u80a9\u5934\u5df2\u662f\u4e2d\u4e86\u4e91\u857e\u4e00\u638c\uff0c\u5e78\u800c\u4ed6\u957f\u4e8e\u5185\u529b\u5316\u52b2\u7684\u529f\u592b\uff0c\u4e91\u857e\u90a3\u4e00\u638c\u867d\u7136\u51fb\u4e2a\u6b63\u7740\uff0c\u4ed6\u80a9 \u5934\u4e00\u6c89\uff0c\u90a3\u638c\u529b\u4e5f\u5b8c\u5168\u6d88\u89e3\u4e86\u3002 \u3000\u3000\u5e76\u4e0d\u662f\u4e91\u857e\u7684\u6b66\u529f\u80dc\u4e8e\u4e91\u91cd\uff0c\u539f\u6765\u6b66\u5b66\u4e4b\u9053\uff0c\u76f8\u751f\u76f8\u514b\uff0c\u4e91\u91cd\u7684\u6b66\u529f\uff0c\u4ee5\u521a\u731b\u4e3a\u4e3b\uff0c\u9047 \u4e0a\u4e86\u5584\u4e8e\u4ee5\u5de7\u964d\u529b\u7684\u4e00\u7b49\u4e00\u9ad8\u624b\uff0c\u5c31\u8981\u53cd\u4e3a\u6240\u514b\u3002\u4e91\u857e\u81ea\u5e7c\u4fbf\u4e60\u7a7f\u82b1\u7ed5\u6811\u7684\u8f7b\u529f\u8eab\u6cd5\uff0c\u82e5\u53ea \u8bba\u8eab\u6cd5\u7684\u8f7b\u7075\uff0c\u5979\u8fd8\u5728\u4e08\u592b\u5f20\u4e39\u67ab\u4e4b\u4e0a\uff0c\u695a\u5927\u9f50\u7684\u94c1\u6247\u4f11\u60f3\u6cbe\u5f97\u7740\u5979\uff0c\u800c\u695a\u5927\u9f50\u53c8\u4e0d\u4f3c\u4e91 \u857e\uff0c\u6709\u5f3a\u52b2\u7684\u638c\u529b\u9632\u8eab\uff0c\u56e0\u6b64\u78b0\u5230\u4e86\u4e91\u857e\uff0c\u53c8\u6070\u6070\u88ab\u5979\u514b\u4f4f\uff0c\u4e0d\u8fc7\u6570\u62db\uff0c\u7acb\u523b\u5904\u4e8e\u4e0b\u98ce\uff0c\u53ea \u6709\u6328\u6253\u7684\u4efd\u513f\uff01 \u3000\u3000\u77f3\u9e3f\u535a\u7ad9\u5728\u573a\u8fb9\uff0c\u72b9\u81ea\u53d1\u6123\u3002\u8981\u77e5\u4ed6\u662f\u6b66\u6797\u4e2d\u9876\u513f\u5c16\u513f\u7684\u89d2\u8272\uff0c\u8f93\u7ed9\u4e86\u5f20\u4e39\u67ab\uff0c\u82e5\u975e\u81ea \u5c3d\uff0c\u5c31\u8be5\u7acb\u5373\u56de\u4e61\uff0c\u4ece\u6b64\u95ed\u95e8\u6d17\u624b\uff0c\u8fd9\u624d\u5408\u4e4e\u4ed6\u7684\u8eab\u4efd\uff0c\u6b63\u8e0c\u8e87\u95f4\uff0c\u5a04\u6850\u5b59\u8d70\u4e86\u51fa\u6765\uff0c\u5bf9\u4ed6 \u606d\u606d\u656c\u656c\u5730\u65bd\u4e86\u4e2a\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u5e08\u5c0a\u52a9\u695a\u5e08\u53d4\u4e00\u81c2\u4e4b\u529b\u3002\u201d\u77f3\u9e3f\u535a\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u6850 \u5b59\uff0c\u96be\u9053\u4f60\u4e0d\u77e5\u9053\u6c5f\u6e56\u4e0a\u7684\u89c4\u77e9\u4e48\uff1f\u201d\u5a04\u6850\u5b59\u9053\uff1a\u201c\u7980\u5e08\u7236\uff0c\u8fd9\u91cc\u662f\u7687\u5bab\u5927\u5185\uff0c\u5e76\u4e0d\u662f\u6c5f\u6e56 \u9053\u4e0a\u3002\u201d\u77f3\u9e3f\u535a\u6014\u4e86\u4e00\u6014\uff0c\u60f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u7687\u4e0a\u539a\u793c\u8058\u6765\uff0c\u867d\u7136\u6ca1\u6709\u53d7\u4efb\u4f55\u804c\u4f4d\uff0c\u4e5f\u7b97 \u662f\u98df\u541b\u4e4b\u7984\u7684\u4e86\uff0c\u600e\u53ef\u4e0d\u5206\u541b\u4e4b\u5fe7\uff1f\u800c\u4e14\uff0c\u6211\u82e5\u5c31\u6b64\u4e00\u8d70\u4e86\u4e4b\uff0c\u7687\u4e0a\u4ed6\u80fd\u539f\u8c05\u6211\u4e48\uff1f\u201d\u5a04\u606b \u5b59\u53c8\u9053\uff1a\u201c\u5e08\u5c0a\u5076\u4e00\u5931\u624b\uff0c\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u9664\u4e86\u695a\u5e08\u53d4\u548c\u5f1f\u5b50\uff0c\u4e5f\u6ca1\u4eba\u770b\u5f97\u51fa\u6765\u3002\u5e08\u5c0a\u82e5\u7136\u81ea \u5df1\u8ba4\u8f93\uff0c\u4ece\u6b64\u95ed\u95e8\u6d17\u624b\uff0c\u90a3\u4e0d\u4f46\u662f\u6298\u4e86\u6211\u6d3e\u7684\u5a01\u540d\uff0c\u800c\u4e14\uff0c\u800c\u4e14\u2026\u2026\u55ef\uff0c\u7687\u4e0a\u4e07\u4e00\u8d77\u7591\uff0c\u5e08 \u5c0a\u4f60\u5728\u592a\u539f\u6709\u5bb6\u6709\u4e1a\uff0c\u4e5f\u6709\u70b9\u4e0d\u5927\u4fbf\u5f53\u554a\u3002\u201d\u77f3\u9e3f\u535a\u52be\u7136\u8272\u53d8\uff0c\u65cb\u5373\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0d \u5fc5\u591a\u8bf4\uff0c\u6211\u660e\u767d\u5566\uff01\u201d \u3000\u3000\u62ac\u5934\u4e00\u770b\uff0c\u4f46\u89c1\u695a\u5927\u9f50\u5df2\u7ed9\u4e91\u91cd\u3001\u4e91\u857e\u903c\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u9669\u8c61\u73af\u751f\uff0c\u77f3\u9e3f\u535a\u559d\u9053\uff1a\u201c\u4e91\u72b6 \u5143\uff0c\u4f60\u7a76\u7adf\u66fe\u662f\u671d\u5ef7\u81e3\u5b50\uff0c\u80c6\u6562\u4e0d\u9075\u7687\u547d\uff0c\u5984\u81ea\u62d2\u6355\uff01\u201d\u9aa4\u7136\u51fa\u624b\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u4e00\u722a\u6293\u4e0b\uff0c \u4e91\u91cd\u53cd\u624b\u4e00\u638c\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u4e24\u4eba\u90fd\u5404\u81ea\u9707\u9000\u4e09\u6b65\uff0c\u695a\u5927\u9f50\u53eb\u9053\uff1a\u201c\u8ba9\u6211\u6765\u5bf9\u4ed8\u4ed6\u3002\u4f60\u6765 \u6536\u62fe\u8fd9\u4e2a\u5973\u8d3c\u3002\u201d\u695a\u5927\u9f50\u5fcc\u60ee\u4e91\u857e\uff0c\u5bf9\u4e91\u91cd\u5374\u81ea\u95ee\u6709\u53d6\u80dc\u7684\u628a\u63e1\u3002 \u3000\u3000\u77f3\u9e3f\u535a\u7709\u5934\u4e00\u76b1\uff0c\u4ed6\u5012\u5e76\u4e0d\u662f\u754f\u60e7\u4e91\u857e\uff0c\u5374\u56e0\u4ed6\u7684\u5206\u7b4b\u9519\u9aa8\u624b\u6cd5\u5fc5\u987b\u8fd1\u8eab\u8089\u640f\uff0c\u624d\u80fd\u514b \u654c\u5236\u80dc\uff0c\u5b9e\u662f\u4e0d\u613f\u7528\u6765\u5bf9\u4ed8\u5973\u6d41\uff0c\u4f46\u89c1\u695a\u5927\u9f50\u5df2\u62a2\u4e0a\u524d\u53bb\u7f20\u7740\u4e91\u91cd\uff0c\u5728\u52bf\u4e0d\u80fd\u4e0e\u4ed6\u201c\u4e89 \u529f\u201d\uff0c\u4e91\u857e\u53cd\u624b\u4e00\u626c\uff0c\u94ee\u3001\u94ee\u3001\u94ee\uff0c\u4e09\u6735\u91d1\u82b1\u9f50\u53d1\uff0c\u5206\u53d6\u77f3\u9e3f\u535a\u3001\u695a\u5927\u9f50\u3001\u5a04\u6850\u5b59\u4e09\u4eba\uff0c\u77f3 \u9e3f\u535a\u8863\u8896\u4e00\u63b7\uff0c\u5c06\u91d1\u82b1\u6536\u53bb\uff0c\u695a\u5927\u9f50\u94c1\u6247\u4e00\u6325\uff0c\u4e5f\u5c06\u91d1\u82b1\u6253\u98de\uff0c\u5a04\u6850\u5b59\u529f\u529b\u7a0d\u900a\uff0c\u5374\u7ed9\u91d1\u82b1 \u6253\u7a7f\u4e86\u80a9\u5934\u8f6f\u9aa8\uff0c\u767b\u65f6\u8840\u6d41\u5982\u6ce8\uff0c\u4e0d\u6562\u4e0a\u524d\u52a9\u6218\uff0c\u614c\u5fd9\u8df3\u51fa\u5708\u5b50\uff0c\u6068\u6068\u8bf4\u9053\uff1a\u201c\u7eb5\u7b97\u4f60\u4e09\u4eba \u6709\u5929\u5927\u795e\u901a\uff0c\u4eca\u65e5\u4e5f\u96be\u9003\u51fa\u6211\u7684\u5929\u7f57\u5730\u7f51\u3002\u201d\u81ea\u5230\u5fa1\u82b1\u56ed\u53bb\u4eb2\u81ea\u5e03\u7f6e\u4e0d\u63d0\u3002 \u3000\u3000\u77f3\u9e3f\u535a\u867d\u7136\u5377\u53bb\u4e86\u5979\u7684\u91d1\u82b1\uff0c\u5fc3\u4e2d\u4e5f\u81ea\u5fae\u5fae\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u82e5\u7136\u5979\u518d\u8fde\u73af\u75be\u53d1\uff0c\u6211\u53ef\u62b5 \u6321\u4e0d\u4f4f\u3002\u201d\u4e0d\u6562\u8ba9\u4e91\u857e\u518d\u6709\u7a7a\u6687\u5077\u53d1\u6697\u5668\uff0c\u6025\u5fd9\u98de\u6b65\u4e0a\u524d\uff0c\u53cc\u8896\u9f50\u626c\uff0c\u4e00\u62db\u201c\u53cc\u9f99\u6c72\u6c34\u201d\uff0c \u8981\u7528\u201c\u98de\u8896\u6d41\u4e91\u201d\u7684\u7edd\u6280\u5c06\u4e91\u857e\u6454\u5012\uff0c\u54ea\u77e5\u4e91\u857e\u7684\u8eab\u6cd5\u5feb\u5982\u95ea\u7535\uff0c\u77f3\u9e3f\u535a\u53cc\u8896\u672a\u66fe\u5377\u5230\uff0c\u5979 \u5df1\u500f\u7136\u95f4\u4ece\u53e6\u4e00\u4e2a\u610f\u60f3\u4e0d\u5230\u7684\u65b9\u4f4d\u6251\u4e86\u8fc7\u6765\uff0c\u4e00\u638c\u5288\u4e0b\u3002 \u3000\u3000\u4e91\u857e\u65b9\u5e86\u5f97\u624b\uff0c\u5ffd\u542c\u5f97\u77f3\u9e3f\u640f\u559d\u9053\uff1a\u201c\u7ed9\u6211\u5012\u4e0b\uff01\u201d\u624b\u6307\u7a81\u7136\u4ece\u8896\u7ba1\u4e2d\u7a7f\u4e86\u51fa\u6765\uff0c\u4e91\u857e \u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u624d\u84e6\u7136\u60f3\u8d77\uff0c\u77f3\u9e3f\u5085\u7684\u5206\u7b4b\u9519\u9aa8\u624b\u6b63\u662f\u957f\u4e8e\u8fd1\u8eab\u8089\u640f\uff0c\u5df4\u671b\u4e0d\u5f97\u81ea\u5df1\u8fd1\u4ed6\u8eab \u524d\uff0c\u8fd9\u4e00\u638c\u5288\u4e0b\uff0c\u6b63\u597d\u88ab\u4ed6\u5c31\u52bf\u4e00\u626d\uff0c\u624b\u8155\u975e\u6298\u65ad\u4e0d\u53ef\uff01 \u3000\u3000\u597d\u4e2a\u4e91\u857e\uff0c\u5c31\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u4e00\u4e2a\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\u5012\u7eb5\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u4e24\u4eba\u90fd\u6697\u6697\u53eb \u4e86\u4e00\u58f0\u201c\u597d\u9669\uff01\u201d\u4f46\u6bd4\u5c06\u8d77\u6765\uff0c\u4e91\u857e\u7684\u8f7b\u529f\u867d\u597d\uff0c\u8fd1\u4e0d\u4e86\u8eab\uff0c\u7ec8\u662f\u5403\u4e8f\u3002\u77f3\u9e3f\u535a\u51e0\u4e4e\u5403\u4e86\u4e91 \u857e\u4e00\u638c\uff0c\u5fc3\u4e2d\u4e5f\u662f\u53c8\u60ca\u53c8\u6012\uff0c\u6076\u6c14\u9661\u751f\uff0c\u518d\u65e0\u987e\u5fcc\uff0c\u6b65\u6b65\u903c\u8fd1\uff0c\u53cc\u638c\u7ffb\u98de\uff0c\u5341\u6307\u5982\u94a9\uff0c\u7eb5\u6a2a \u7a7f\u63d2\uff0c\u7acb\u5fc3\u8981\u7528\u5206\u7b4b\u9519\u9aa8\u624b\u6765\u5c06\u4e91\u857e\u632b\u8d25\u3002 \u3000\u3000\u4e91\u857e\u7528\u7a7f\u82b1\u7ed5\u6811\u8eab\u6cd5\uff0c\u5de6\u515c\u53f3\u7ed5\uff0c\u597d\u51e0\u6b21\u4ece\u4ed6\u7684\u638c\u4e0b\u7a7f\u8fc7\uff0c\u5374\u8fde\u8863\u89d2\u4e5f\u6ca1\u6709\u7ed9\u4ed6\u52fe\u7740\uff0c \u867d\u7136\u5982\u6b64\uff0c\u7a76\u975e\u5584\u6cd5\uff0c\u51e0\u5ea6\u76d8\u65cb\u8fdb\u9000\u4e4b\u540e\uff0c\u4e91\u857e\u5ffd\u5730\u4e00\u58f0\u957f\u5578\uff0c\u7389\u624b\u4e00\u626c\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u6761 \u7ef8\u5e26\uff0c\u8fd9\u672c\u6765\u662f\u5979\u675f\u8170\u7528\u7684\uff0c\u5982\u4eca\u5374\u8981\u62ff\u6765\u5f53\u4f5c\u5175\u5668\u3002 \u3000\u3000\u7ef8\u5e26\u821e\u52a8\uff0c\u5929\u77eb\u5982\u9f99\uff0c\u7adf\u7136\u5e26\u7740\u52b2\u98ce\uff0c\u5411\u77f3\u9e3f\u535a\u7684\u9762\u95e8\u5237\u4e0b\uff0c\u77f3\u9e3f\u535a\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a \u201c\u5979\u5c45\u7136\u80fd\u628a\u7ef8\u5e26\u4f7f\u5f97\u4f3c\u8f6f\u97ad\u4e00\u6837\uff0c\u867d\u7136\u5185\u529f\u8fd8\u4e0d\u82e5\u5979\u7684\u4e08\u592b\uff0c\u4e5f\u7b97\u96be\u5f97\u7684\u4e86\u3002\u201d\u53cd\u624b\u4e00 \u6293\uff0c\u4ed6\u4ee5\u5206\u7b4b\u9519\u9aa8\u624b\u51a0\u7edd\u6b66\u6797\uff0c\u624b\u6cd5\u4f55\u7b49\u5feb\u6377\uff0c\u4e00\u722a\u6293\u53bb\uff0c\u7adf\u7136\u6293\u4e86\u4e2a\u7a7a\uff0c\u90a3\u6761\u7ef8\u5e26\u53ea\u5fae\u5fae \u4e00\u504f\uff0c\u53c8\u201c\u523a\u201d\u5411\u4ed6\u7684\u201c\u80a9\u4e95\u7a74\u201d\uff0c\u8fd9\u6761\u7ef8\u5e26\uff0c\u88ab\u4e91\u857e\u4f7f\u4e0a\u4e86\u5185\u5bb6\u771f\u529b\uff0c\u4e0d\u4f46\u53ef\u4ee5\u5f53\u4f5c\u8f6f \u97ad\uff0c\u8fd8\u53ef\u4ee5\u5f53\u70b9\u7a74\u7684\u5229\u5668\u3002\u77f3\u9e3f\u535a\u66f4\u4e0d\u6562\u8f7b\u89c6\uff0c\u968f\u7740\u7ef8\u5e26\u7684\u821e\u52a8\uff0c\u8d77\u843d\u8df3\u8dc3\uff0c\u970e\u65f6\u95f4\u8fc7\u4e86\u5341 \u591a\u4e8c\u5341\u62db\uff0c\u4e91\u857e\u56fa\u7136\u8fd1\u4e0d\u4e86\u4ed6\uff0c\u4ed6\u5728\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e5f\u6293\u4e0d\u7740\u4e91\u857e\u7684\u8170\u5e26\u3002 \u3000\u3000\u90a3\u4e00\u8fb9\u4e91\u91cd\u548c\u695a\u5927\u9f50\u4e5f\u6253\u5f97\u4e2a\u96be\u89e3\u96be\u5206\uff0c\u4e91\u91cd\u89e3\u4e0b\u56f4\u5728\u8170\u95f4\u7684\u8f6f\u5200\uff0c\u5c55\u5f00\u4e94\u864e\u65ad\u95e8\u5200 \u6cd5\uff0c\u5200\u5149\u95ea\u95ea\uff0c\u970d\u970d\u751f\u51e4\uff0c\u6bcf\u4e00\u5200\u6240\u51fa\uff0c\u90fd\u662f\u529b\u6c89\u62db\u6377\uff0c\u695a\u5927\u9f50\u4ecd\u7136\u7528\u4ee5\u5de7\u964d\u529b\u7684\u6253\u6d3b\uff0c\u94c1 \u6247\u5ffd\u5f20\u5ffd\u5408\uff0c\u906e\u62e6\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u4e91\u91cd\u8fd9\u4e00\u8def\u6781\u521a\u731b\u7684\u5200\u6cd5\uff0c\u7adf\u662f\u88ab\u4ed6\u89c1\u62db\u62c6\u62db\uff0c\u89c1\u5f0f\u62c6\u5f0f\uff0c \u867d\u7136\u4e91\u91cd\u7684\u6bcf\u4e00\u5200\u90fd\u6c89\u91cd\u4e4b\u6781\uff0c\u5374\u90fd\u88ab\u4ed6\u8f7b\u63cf\u6de1\u5199\u5730\u5316\u5f00\u3002\u6240\u4ee5\u5728\u8868\u9762\u6765\uff0c\u4e91\u91cd\u4f3c\u662f\u5360\u4e86\u516b \u6210\u653b\u52bf\uff0c\u5b9e\u5219\u662f\u695a\u5927\u9f50\u4ee5\u9038\u4ee3\u52b3\uff0c\u7a33\u5f85\u5148\u624b\uff0c\u6d88\u8017\u4e91\u91cd\u7684\u6c14\u529b\uff0c\u800c\u4e14\u4ed6\u4e5f\u5e76\u4e0d\u662f\u53ea\u5b88\u4e0d\u653b\uff0c \u90a3\u94c1\u6247\u4e00\u5408\u4e4b\u65f6\uff0c\u4fbf\u7acb\u5373\u4e58\u7f0e\u62b5\u9699\uff0c\u70b9\u6253\u4e91\u91cd\u7684\u4e09\u5341\u516d\u9053\u5927\u7a74\u3002\u5e78\u800c\u4e91\u91cd\u5f97\u5f20\u4e39\u67ab\u7684\u6307\u70b9\uff0c \u4e00\u624b\u8fd0\u5200\uff0c\u4e00\u638c\u4ecd\u7136\u4ee5\u5927\u529b\u91d1\u521a\u624b\u6cd5\u62a4\u8eab\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u8fd8\u662f\u5f7c\u6b64\u76f8\u6301\u4e4b\u5c40\u3002 \u3000\u3000\u518d\u8bf4\u5f20\u4e39\u67ab\u51fb\u5012\u4e86\u6458\u661f\u4e0a\u4eba\u4e0e\u5c60\u9f99\u5c0a\u8005\u4e4b\u540e\uff0c\u7acb\u5373\u51b2\u5165\u6b66\u58eb\u4e1b\u4e2d\uff0c\u638c\u5288\u6307\u622e\uff0c\u4e0d\u8fc7\u4e00\u76cf \u8336\u7684\u5de5\u592b\uff0c\u4fbf\u63a5\u8fde\u4f24\u4e86\u5341\u6570\u540d\u654c\u624b\uff0c\u4f46\u56f4\u653b\u7684\u6b66\u571f\uff0c\u4e0d\u4e0b\u767e\u4eba\uff0c\u91cd\u91cd\u56f4\u56f0\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u662f \u4e0d\u6613\u51b2\u51fa\uff0c\u5f20\u4e39\u67ab\u5728\u767e\u5fd9\u4e2d\u62bd\u773c\u4e00\u770b\uff0c\u4f46\u89c1\u4e91\u91cd\u3001\u4e91\u857e\u90fd\u5df2\u9677\u4e8e\u52a3\u52bf\uff0c\u5fc3\u4e2d\u4e00\u6025\uff0c\u9661\u7136\u594b\u8d77 \u795e\u5a01\uff0c\u8fd9\u65f6\u6b63\u6709\u4e24\u4e2a\u624b\u821e\u516b\u89d2\u91d1\u9524\u7684\u5fa1\u524d\u4f8d\u536b\uff0c\u5de6\u53f3\u5408\u51fb\uff0c\u53cc\u9524\u6253\u4e0b\uff0c\u8ddd\u79bb\u5f20\u4e39\u67ab\u7684\u5934\u9876\u4e0d \u5230\u4e94\u5bf8\uff0c\u5f20\u4e39\u67ab\u4e00\u58f0\u5927\u559d\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u4e00\u624b\u6267\u7740\u4e00\u4e2a\u4f8d\u536b\uff0c\u731b\u5730\u4e00\u78b0\uff0c\u53cc\u9524\u4ea4\u51fb\uff0c\u8f70\u9686\u5730\u4e00 \u58f0\u5927\u54cd\uff0c\u5f20\u4e39\u67ab\u677e\u624b\u8f7b\u8f7b\u4e00\u63a8\uff0c\u8fd9\u4e24\u4e2a\u5f85\u536b\u88ab\u4ed6\u78b0\u5f97\u5934\u660f\u773c\u82b1\uff0c\u91d1\u9524\u5140\u81ea\u821e\u52a8\u4e0d\u4f11\uff0c\u5c06\u5468\u56f4 \u7684\u6b66\u571f\u6253\u5f97\u5934\u5d29\u989d\u88c2\uff0c\u7eb7\u7eb7\u8d70\u907f\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7eb5\u58f0\u5927\u7b11\uff0c\u53c8\u51b2\u51fa\u4e86\u4e08\u8bb8\u4e4b\u5730\uff0c\u53e6\u4e24\u540d\u4f7f\u5251\u7684\u6b66\u58eb\u662f\u6606\u4ed1\u6d3e\u6717\u6708\u7985\u5e08\u7684\u9ad8\u8db3\uff0c\u4e00 \u624b\u6606\u4ed1\u5251\u6cd5\uff0c\u4e5f\u66fe\u5728\u6c5f\u6e56\u4e0a\u5f97\u8fc7\u76db\u540d\uff0c\u540d\u5217\u5927\u5185\u516b\u5927\u9ad8\u624b\u4e4b\u5185\uff0c\u4e0d\u77e5\u5389\u5bb3\uff0c\u98de\u8eab\u6025\u4e0a\uff0c\u4e24\u4eba \u4e0d\u7ea6\u800c\u540c\u5730\u6362\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u540c\u65f6\u51fa\u624b\uff0c\u4e00\u4e2a\u5251\u523a\u5f20\u4e39\u67ab\u7684\u53f3\u80a9\u4e95\u7a74\uff0c\u4e00\u4e2a\u5251\u523a\u5f20\u4e39\u67ab\u7684\u5de6\u80a9 \u4e95\u7a74\uff0c\u53cc\u5251\u9f50\u51fa\uff0c\u52bf\u9053\u51cc\u5389\u4e4b\u6781\uff0c\u5f20\u4e39\u67ab\u5927\u7b11\u9053\uff1a\u201c\u6765\u5f97\u6b63\u597d\uff0c\u501f\u5251\u4e00\u7528\uff01\u201d\u5288\u556a\u4e24\u58f0\uff0c\u8fd9 \u4e24\u4e2a\u4eba\u5c1a\u672a\u770b\u6e05\u4ed6\u7528\u7684\u662f\u4ec0\u4e48\u624b\u6cd5\uff0c\u5df2\u662f\u5404\u81ea\u88ab\u6253\u4e86\u4e00\u8bb0\u8033\u5149\uff0c\u624b\u4e2d\u7684\u957f\u5251\u4e5f\u88ab\u5f20\u4e39\u67ab\u5288\u624b \u593a\u53bb\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u770b\u5728\u660e\u6708\u7985\u5e08\u7684\u9762\u4e0a\uff0c\u9976\u4f60\u4e0d\u6b7b\uff01\u4f60\u4e24\u4e2a\u8fd8\u4e0d\u914d\u7528\u5251\uff0c\u5feb\u56de \u6606\u4ed1\u5c71\u53bb\u518d\u7ec3\u5341\u5e74\uff01\u201d\u53cc\u5251\u4e00\u5c55\uff0c\u767b\u65f6\u5982\u864e\u6dfb\u7ffc\uff0c\u53ea\u89c1\u5251\u950b\u6240\u81f3\uff0c\u558a\u58f0\u56db\u8d77\uff0c\u5175\u5668\u629b\u6ee1\u4e00 \u5730\uff0c\u5f20\u4e39\u67ab\u5c55\u5f00\u4e86\u53cc\u5251\u5408\u58c1\u7684\u6218\u672f\uff0c\u4e13\u523a\u654c\u4eba\u624b\u8155\u4e0a\u7684\u5173\u8282\u8981\u5bb3\uff0c\u53ea\u4e00\u62db\u5c31\u8981\u53eb\u4ed6\u5175\u5668\u64a4 \u624b\uff0c\u53cc\u5251\u75be\u53d1\u5982\u98ce\uff0c\u8fde\u4f24\u4e86\u4e8c\u4e09\u5341\u540d\u6b66\u58eb\uff0c\u5f53\u8005\u8f9f\u6613\u3002\u8fd9\u65f6\u6458\u661f\u4e0a\u4eba\u4e0e\u5c60\u9f99\u5c0a\u8005\u65b9\u81ea\u8ffd\u5230\uff0c \u5f20\u4e39\u67ab\u5df2\u51b2\u51fa\u91cd\u56f4\uff01 \u3000\u3000\u4e91\u857e\u89c1\u4e08\u592b\u51b2\u51fa\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u4e00\u4e2a\u758f\u795e\uff0c\u88ab\u77f3\u9e3f\u535a\u6293\u7740\u4e86\u7ef8\u5e26\uff0c\u4e00\u626f\u626f\u65ad\uff01\u5f20\u4e39\u67ab\u53eb \u9053\uff1a\u201c\u4e91\u59b9\uff01\u63a5\u5251\uff01\u201d\u957f\u5251\u4e00\u629b\uff0c\u77f3\u9e3f\u535a\u4e5f\u7eb5\u8eab\u6765\u62a2\uff0c\u4e91\u857e\u624b\u5feb\uff0c\u628a\u5251\u62a2\u5230\u624b\u4e2d\uff0c\u77f3\u9e3f\u535a\u4e09 \u6307\u4e00\u4f38\uff0c\u6263\u5979\u7684\u624b\u8155\uff1b\u8bf4\u65f6\u8fd8\uff0c\u90a3\u65f6\u5feb\uff0c\u4f46\u542c\u5f97\u5f20\u4e39\u67ab\u4e00\u58f0\u957f\u7b11\uff0c\u53cc\u5251\u5408\u58c1\uff0c\u914d\u5408\u5f97\u5999\u5230\u6beb \u5dc5\uff0c\u5b9b\u5982\u4e24\u9053\u94f6\u86c7\uff0c\u75be\u98de\u800c\u51fa\uff0c\u4e00\u500f\u5411\u5de6\uff0c\u4e00\u500f\u5411\u53f3\uff0c\u4e00\u4e2a\u76d8\u65cb\uff0c\u4fbf\u5c06\u77f3\u9e3f\u535a\u5708\u5728\u5f53\u4e2d\uff0c\u77f3 \u9e3f\u535a\u5927\u5403\u4e00\u60ca\uff0c\u4e0d\u6687\u7ec6\u601d\uff0c\u4ecd\u7136\u7167\u7740\u539f\u6765\u7684\u65b9\u5411\uff0c\u5f13\u8eab\u4e00\u8dc3\uff0c\u4f38\u624b\u4e00\u6293\uff0c\u63a5\u7740\u4f7f\u4e00\u4e2a\u201c\u71d5\u9752 \u5341\u516b\u7ffb\u201d\u7684\u62db\u6570\uff0c\u8eab\u5f62\u5760\u5730\uff0c\u6eda\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u4f9d\u7a00\u542c\u5f97\u5f20\u4e39\u67ab\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u8fd9\u624d\u89c9\u5f97 \u9876\u4e0a\u4e00\u7247\u6c81\u51c9\uff0c\u5934\u9876\u4e0a\u672c\u6765\u5c31\u5df2\u7a00\u758f\u7684\u5934\u53d1\u7adf\u88ab\u524a\u4e86\u4e2a\u5e72\u5e72\u51c0\u51c0\uff01 \u3000\u3000\u77f3\u9e3f\u535a\u8001\u7f9e\u6210\u6012\uff0c\u5389\u58f0\u53eb\u9053\uff1a\u201c\u5f20\u4e39\u67ab\u4f60\u8fb1\u6211\u592a\u751a\uff0c\u8fd9\u51e0\u6839\u8001\u9aa8\u5934\u9001\u7ed9\u4f60\u5427\uff01\u201d\u5176\u5b9e\u5f20 \u4e39\u67ab\u8fd9\u4e2a\u201c\u597d\u201d\u5b57\u786e\u662f\u7531\u8877\u4e4b\u8a00\uff0c\u539f\u6765\u77f3\u9e3f\u535a\u90a3\u4e00\u62db\u4ee5\u653b\u4e3a\u5b88\uff0c\u6070\u6070\u903c\u5f97\u4e91\u857e\u811a\u6b65\u659c\u79fb\u4e00 \u6b65\uff0c\u9664\u4e86\u7528\u8fd9\u5192\u9669\u7684\u4e00\u62db\uff0c\u7edd\u4e0d\u80fd\u8131\u51fa\u53cc\u5251\u5408\u56f4\u7684\u5708\u5b50\uff01 \u3000\u3000\u77f3\u9e3f\u535a\u5374\u628a\u5f20\u4e39\u67ab\u7684\u8d5e\u8bed\u5f53\u4e3a\u8ba5\u8bee\uff0c\u594b\u4e0d\u987e\u8eab\uff0c\u53c8\u518d\u6251\u6765\uff0c\u5f20\u4e39\u67ab\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u8fd9 \u8001\u513f\u813e\u6c14\u5012\u786c\uff0c\u4e91\u59b9\uff0c\u523a\u4ed6\u624b\u8155\u5173\u8282\uff01\u201d\u53cc\u5251\u5de6\u53f3\u4e00\u5708\uff0c\u500f\u5730\u53c8\u540c\u65f6\u523b\u51fa\uff0c\u77f3\u9e3f\u535a\u53cc\u624b\u7b3c\u5728 \u8896\u4e2d\uff0c\u53cc\u8896\u4e00\u62c2\uff0c\u4f46\u542c\u5f97\u55e4\u55e4\u4e24\u58f0\uff0c\u4e24\u6761\u957f\u8896\u53c8\u88ab\u6234\u65ad\uff0c\u8fd9\u672c\u5728\u77f3\u9e3f\u535a\u7684\u610f\u6599\u4e4b\u4e2d\uff0c\u6b63\u62df\u5f85 \u4ed6\u4eec\u672a\u53ca\u6362\u62db\u4e4b\u9645\uff0c\u51fa\u624b\u653b\u654c\uff0c\u54ea\u6599\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u7684\u53cc\u5251\u5408\u58c1\u4e4b\u672f\uff0c\u5df2\u7ec3\u5230\u5fc3\u610f\u76f8\u901a\u3001\u53d8\u5e7b \u65e0\u65b9\u7684\u5999\u5883\uff0c\u53cc\u5251\u6839\u672c\u4e0d\u7528\u6362\u62db\uff0c\u5251\u950b\u4e00\u98a4\uff0c\u4e24\u67c4\u5251\u9661\u7136\u95f4\uff0c\u9012\u51fa\u4e94\u5bf8\uff0c\u8981\u77e5\u9ad8\u624b\u62fc\u6597\uff0c\u6240 \u4e89\u4e0d\u8fc7\u6beb\u5398\u4e4b\u5dee\uff0c\u77f3\u9e3f\u535a\u4ee5\u4e3a\u4ed6\u4eec\u7684\u62db\u6570\u5df2\u7ecf\u7528\u8001\uff0c\u54ea\u6599\u4ed6\u4eec\u7684\u5251\u52bf\u7adf\u7136\u672a\u8870\uff0c\u8fd9\u5927\u5927\u51fa\u4e4e \u77f3\u9e3f\u535a\u7684\u610f\u6599\u4e4b\u5916\uff01 \u3000\u3000\u5251\u98ce\u638c\u5f71\u4e4b\u4e2d\uff0c\u4f46\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u77f3\u9e3f\u535a\u7684\u8170\u5e26\u53c8\u7ed9\u5f20\u4e39\u67ab\u5251\u5c16\u6311\u65ad\uff0c\u8fd9\u8fd8\u662f\u4ed6\u8d8b \u907f\u5f97\u5feb\uff0c\u800c\u5f20\u3001\u4e91\u4e8c\u4eba\u7684\u5251\u52bf\u53c8\u521a\u521a\u653e\u5c3d\u7684\u7f18\u6545\uff0c\u8981\u4e0d\u7136\u518d\u5f85\u53cc\u5251\u4e00\u5408\uff0c\u7eb5\u7136\u4ed6\u6b66\u529f\u518d\u5f3a\u5341 \u500d\uff0c\u4e0d\u6b7b\u4e5f\u5f97\u91cd\u4f24\u3002 \u3000\u3000\u77f3\u9e3f\u535a\u8d39\u5c3d\u5fc3\u673a\uff0c\u5192\u9669\u8fdb\u62db\uff0c\u5c61\u906d\u632b\u8d25\uff0c\u53cd\u5f04\u5f97\u8863\u88f3\u7834\u788e\uff0c\u72fc\u72c8\u4e0d\u582a\uff0c\u53ea\u542c\u4e39\u67ab\u53c8\u8d5e\u4e86 \u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u77f3\u8001\u524d\u8f88\uff0c\u4f60\u80fd\u5728\u53cc\u5251\u5408\u58c1\u4e4b\u4e0b\uff0c\u8fde\u6321\u4e09\u62db\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u80fd\u4e0e \u4f60\u5e76\u9a7e\u9f50\u9a71\u7684\u4e5f\u53ea\u662f\u6709\u9650\u7684\u3002\u665a\u8f88\u4f69\u670d\uff0c\u4ee5\u4f60\u7684\u6b66\u529f\u5a01\u671b\uff0c\u5fb7\u90b5\u5e74\u5c0a\uff0c\u8fd8\u4fa7\u8eab\u5728\u8fd9\u73ed\u5974\u624d\u4e4b \u4e2d\uff0c\u542c\u4eba\u5dee\u9063\uff0c\u5b9e\u5728\u662f\u6709\u8fb1\u8eab\u4efd\uff01\u8bf7\u542c\u665a\u8f88\u4e00\u8a00\uff0c\u65e9\u65e9\u56de\u5bb6\u53bb\u5427\uff01\u201d \u3000\u3000\u77f3\u9e3f\u535a\u5012\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u5f20\u4e39\u67ab\u8fd9\u756a\u8bf4\u8bdd\uff0c\u53e5\u53e5\u523a\u5728\u4ed6\u7684\u5fc3\u4e0a\uff0c\u5176\u5b9e\u4ed6\u8fd9\u6b21\u51fa\u5c71\uff0c\u5012\u5e76 \u4e0d\u662f\u4e3a\u4e86\u6c42\u53d6\u529f\u540d\u5bcc\u8d35\uff0c\u800c\u662f\u60f3\u4ee4\u4ed6\u8fd9\u6d3e\u6b66\u529f\uff0c\u626c\u540d\u5929\u4e0b\uff0c\u800c\u4ed6\u81ea\u5df1\uff0c\u4e5f\u4ece\u6765\u4e0d\u4f5c\u6b66\u6797\u4e2d\u7b2c \u4e8c\u4eba\u60f3\uff0c\u54ea\u77e5\u8fdb\u4e86\u5927\u5185\u4e4b\u540e\uff0c\u7b2c\u4e00\u6b21\u4ea4\u624b\u5c31\u78b0\u5230\u4e86\u5f20\u4e39\u67ab\u592b\u5987\uff0c\u7b97\u8d77\u8f88\u4efd\u6765\uff0c\u8fd8\u662f\u6bd4\u81ea\u5df1\u665a \u4e86\u4e24\u8f88\u7684\u4eba\uff0c\u800c\u81ea\u5df1\u5374\u4ec5\u4ec5\u53ea\u80fd\u62b5\u654c\u4e09\u62db\uff0c\u8fd8\u51e0\u4e4e\u4f24\u5728\u53cc\u5251\u5408\u58c1\u4e4b\u4e0b\u3002\u767b\u65f6\u96c4\u5fc3\u5c3d\u5931\uff0c\u58ee\u5fd7 \u5168\u7070\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u7acb\u523b\u8df3\u8fc7\u680f\u6746\uff0c\u9003\u51fa\u7687\u5bab\uff0c\u4ece\u6b64\u679c\u7136\u542c\u4e86\u5f20\u4e39\u67ab\u4e4b\u529d\uff0c\u5f03\u6389\u5bb6\u4e1a\uff0c\u643a\u5e26\u5bb6 \u4eba\uff0c\u9690\u5c45\u5c71\u6797\uff0c\u518d\u4e5f\u4e0d\u95ee\u4e16\u4e8b\u3002 \u3000\u3000\u90a3\u8fb9\u53a2\uff0c\u4e91\u91cd\u548c\u695a\u5927\u9f50\u6b63\u6597\u5230\u5403\u7d27\u7684\u5173\u5934\uff0c\u4e91\u91cd\u7684\u4e09\u5341\u516d\u624b\u4e94\u864e\u65ad\u95e8\u5200\u6cd5\u521a\u521a\u4f7f\u5b8c\uff0c\u6b63 \u62df\u5468\u800c\u590d\u59cb\uff0c\u53d8\u62db\u6362\u529b\u4e4b\u65f6\uff0c\u695a\u5927\u9f50\u7a81\u7136\u53d8\u5b88\u4e3a\u653b\uff0c\u94c1\u6247\u4e00\u5f20\uff0c\u500f\u5730\u642d\u7740\u5200\u80cc\uff0c\u4e91\u91cd\u90a3\u4e00\u5200 \u521a\u521a\u65ab\u51fa\uff0c\u88ab\u4ed6\u94c1\u6247\u4e00\u5f15\uff0c\u91cd\u5fc3\u4e0d\u7a33\uff0c\u8eab\u5b50\u524d\u503e\uff0c\u695a\u5927\u9f50\u7acb\u4e0b\u6bd2\u624b\uff0c\u4e00\u638c\u62cd\u51fa\uff0c\u5ffd\u89c1\u773c\u524d\u9752 \u5149\u4e00\u95ea\uff0c\u5f20\u4e39\u67ab\u7684\u5251\u5c16\u5df2\u523a\u5230\u4e86\u4ed6\u7684\u864e\u53e3\uff0c\u695a\u5927\u9f50\u5927\u6012\u53eb\u9053\uff1a\u201c\u597d\u54c7\uff0c\u8fd9\u6837\u5b50\u5077\u65bd\u6697\u88ad\uff0c\u7b97 \u54ea\u95e8\u5b50\u7684\u597d\u6c49\uff01\u201d\u94c1\u6247\u4e00\u8f6c\uff0c\u95ea\u8fc7\u4e91\u91cd\u7684\u8eab\u540e\uff0c\u597d\u4e0d\u5bb9\u6613\uff0c\u624d\u907f\u8fc7\u4e86\u8fd9\u4e00\u9669\u62db\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u9ad8\u624b\u4e34\u654c\uff0c\u7406\u8be5\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u6211\u5230\u4e86\u4f60\u7684\u8ddf\u524d\uff0c\u4f60\u8fd8\u4e0d \u77e5\uff0c\u5c1a\u6562\u81ea\u5938\u597d\u6c49\u4e48\uff1f\u6211\u82e5\u6709\u5fc3\u6740\u4f60\uff0c\u8fd9\u4e00\u5251\u4e0d\u523a\u4f60\u7684\u864e\u53e3\uff0c\u523a\u4f60\u7684\u54bd\u5589\uff0c\u4f60\u7684\u94c1\u6247\u600e\u4e48\u8f6c \u5f97\u8fc7\u6765\uff1f\u518d\u8bf4\u5230\u6c5f\u6e56\u89c4\u77e9\uff0c\u4eca\u65e5\u6211\u7b49\u53ea\u6709\u4e09\u4eba\uff0c\u4f60\u4eec\u7684\u5927\u5185\u9ad8\u624b\uff0c\u5374\u5df2\u503e\u5de2\u800c\u51fa\uff0c\u8fd9\u53c8\u662f\u600e \u4e48\u4e2a\u8bf4\u6cd5\uff1f\u201d\u695a\u5927\u9f50\u51b7\u6c57\u6c81\u808c\uff0c\u5fc3\u4e2d\u81ea\u601d\uff1a\u201c\u4ed6\u90a3\u5251\u82e5\u7136\u6539\u523a\u54bd\u5589\uff0c\u5f53\u771f\u662f\u907f\u65e0\u53ef\u907f\uff01\u201d\u5f3a \u989c\u7b54\u9053\uff1a\u201c\u5f20\u4e39\u67ab\uff0c\u6211\u4e0d\u4e0e\u4f60\u6597\u5634\uff0c\u6765\uff0c\u6765\uff01\u54b1\u5a29\ue317\u5212\u51e0\u62db\uff01\u201d\u5f20\u4e39\u67ab\u53eb\u9053\uff1a\u201c\u4e91\u5144\uff0c\u4f60\u66ff \u6211\u6682\u65f6\u6321\u4e00\u6321\u8fd9\u4e00\u73ed\u5974\u624d\uff01\u201d\u4e00\u4e2a\u76c6\u9f99\u7ed5\u6b65\uff0c\u4e0e\u4e91\u91cd\u6210\u4e86\u7284\u89d2\u4e4b\u52bf\uff0c\u626c\u58f0\u8bf4\u9053\uff1a\u201c\u695a\u5927\u9f50\uff0c \u4f60\u53ea\u8981\u80fd\u6321\u6211\u592b\u5987\u4e09\u62db\uff0c\u6211\u592b\u5987\u4e00\u9f50\u81ea\u7f1a\uff0c\u6210\u5168\u4f60\u4e00\u4ef6\u5927\u529f\uff01\u201d\u9661\u7136\u95f4\uff0c\u53cc\u5251\u4e00\u5408\uff0c\u5c06\u695a\u5927 \u9f50\u5708\u5728\u5f53\u4e2d\u3002\u5c31\u5728\u8fd9\u540c\u4e00\u7684\u65f6\u95f4\uff0c\u53ea\u542c\u5f97\u7830\u7830\u4e24\u58f0\uff0c\u539f\u6765\u662f\u4e91\u91cd\u65bd\u5c55\u4e86\u5927\u529b\u91d1\u521a\u624b\u6cd5\uff0c\u5c06\u6700 \u5148\u8ffd\u6765\u7684\u4e24\u540d\u6b66\u58eb\u6454\u4e0b\u4e86\u77f3\u9636\uff01 \u3000\u3000\u8fd9\u53cc\u5251\u5408\u58c1\u4e4b\u672f\uff0c\u4e43\u662f\u7384\u673a\u9038\u571f\u6bd5\u751f\u5fc3\u8840\u4e4b\u6240\u805a\uff0c\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u5f53\u5e74\u672a\u7ecf\u7ec3\u4e60\uff0c\u7b2c\u4e00\u6b21 \u51fa\u624b\uff0c\u5c31\u632b\u8d25\u4e86\u9ed1\u767d\u6469\u8bc3\uff08\u8be6\u89c1\u62d9\u8457\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\uff0c\u800c\u4eca\u505a\u4e86\u5341\u591a\u5e74\u592b\u5987\uff0c\u914d\u5408\u5f97\u66f4\u662f\u5929 \u8863\u65e0\u7f1d\uff0c\u53cc\u5251\u4e00\u5408\uff0c\u767b\u65f6\u628a\u695a\u5927\u9f50\u524d\u540e\u5de6\u53f3\u7684\u9000\u8def\uff0c\u5168\u90fd\u5c01\u7740\uff0c\u695a\u5927\u9f50\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4ed7 \u7740\u4ed6\u90a3\u4e00\u8eab\u602a\u5f02\u7684\u6b66\u529f\uff0c\u5728\u53cc\u5251\u4ea4\u53c9\u4e4b\u4e0b\uff0c\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u94c1\u6247\u4e00\u6325\uff0c\u4ee5\u7edd\u5999\u7684\u5378\u529b\u529f\u592b\uff0c\u5378 \u53bb\u4e86\u4e91\u857e\u7684\u4e94\u6210\u52b2\u529b\uff0c\u6247\u67c4\u4e00\u683c\uff0c\u5494\u5693\u4e00\u58f0\uff0c\u88ab\u5f20\u4e39\u67ab\u622a\u4e86\u4e24\u5904\u7f3a\u53e3\uff0c\u51fa\u5c3d\u5e73\u751f\u7edd\u6280\uff0c\u624d\u582a \u582a\u5730\u62c6\u5f00\u4e86\u7b2c\u4e00\u62db\u3002\u4e91\u857e\u5fc3\u4e2d\u6697\u53eb\u53ef\u60dc\uff0c\u53ea\u56e0\u5f20\u4e39\u67ab\u60f3\u4e0e\u7687\u5e1d\u8c08\u5224\uff0c\u4e0d\u613f\u5e26\u5251\u5165\u5bab\u3002\u8981\u662f\u4ed6 \u4eec\u628a\u9752\u51a5\u4e0e\u767d\u4e91\u4e24\u628a\u5b9d\u5251\u5e26\u6765\uff0c\u695a\u5927\u9f50\u7684\u94c1\u6247\u65e9\u5df2\u622a\u4e3a\u4e24\u6bb5\u4e86\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u6321\u5f97\u8fd8\u7b97\u4e0d\u9519\u3002\u201d\u9752\u94a2\u5251\u4fe1\u624b\u4e00\u62db\u51fa\uff0c\u5251\u52bf\u903c\u5f97\u695a\u5927\u9f50\u659c\u8d70\u4e09 \u6b65\uff0c\u4e91\u857e\u90a3\u4e00\u5251\u5374\u521a\u597d\u4ece\u8fd9\u65b9\u5411\u523a\u6765\uff0c\u695a\u5927\u9f50\u65e0\u6cd5\u62b5\u6321\uff0c\u7ffb\u8eab\u4ec6\u5730\uff0c\u4e91\u857e\u5251\u950b\u8fc7\u5904\uff0c\u5c06\u695a\u5927 \u9f50\u5934\u4e0a\u7684\u65b9\u5e03\u524a\u53bb\uff0c\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u722c\u8d77\u6765\uff0c\u518d\u63a5\u8fd9\u7b2c\u4e09\u62db\u3002\u201d\u5f20\u4e39\u67ab\u8fd9\u4e00\u62db\u5176\u5b9e\u5c31\u53ef\u53d6\u4ed6 \u6027\u547d\uff0c\u6240\u4ee5\u4e0d\u53d6\uff0c\u4e43\u662f\u6545\u610f\u8ba9\u4e91\u857e\u6298\u8fb1\u4ed6\uff0c\u4ee4\u4e91\u857e\u6d88\u4e00\u53e3\u6c14\u7684\u3002 \u3000\u3000\u695a\u5927\u9f50\u660e\u77e5\u4e0d\u654c\uff0c\u62fc\u4e86\u4e00\u6b7b\uff0c\u84e6\u7136\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u53cd\u624b\u4e00\u626c\uff0c\u94c1\u6247\u4e00\u6296\uff0c\u5206 \u70b9\u4e91\u857e\u7684\u4e03\u5904\u5927\u7a74\uff0c\u4ed6\u77e5\u9053\u4e91\u857e\u6b66\u529f\u7a0d\u900a\uff0c\u5192\u6b7b\u53cd\u51fb\uff0c\u7aef\u7684\u662f\u51fa\u624b\u5982\u7535\uff0c\u51cc\u5389\u975e\u5e38\uff0c\u54ea\u77e5\u4ed6 \u5feb\uff0c\u522b\u4eba\u66f4\u5feb\uff0c\u5c31\u5728\u4ed6\u90a3\u6247\u5b50\u626c\u8d77\u4e4b\u65f6\uff0c\u5f20\u3001\u4e91\u4e8c\u4eba\u4ea6\u5df2\u662f\u53cc\u5251\u9f50\u51fa\uff0c\u4f46\u89c1\u5251\u5149\u70b9\u70b9\uff0c\u6709\u5982 \u7e41\u661f\u6b92\u843d\uff0c\u6d6a\u82b1\u98de\u6e85\uff0c\u695a\u5927\u9f50\u5927\u53eb\u4e00\u58f0\uff0c\u94c1\u6247\u622a\u4e3a\u56db\u6bb5\uff0c\u5de6\u624b\u88ab\u524a\u53bb\u4e86\u4e24\u6307\uff0c\u8eab\u4e0a\u4e5f\u540c\u65f6\u53d7 \u4e86\u4e03\u5904\u5251\u4f24\uff01\u5f20\u4e39\u67ab\u559d\u9053\uff1a\u201c\u9976\u4f60\u4e00\u547d\uff0c\u8fd8\u4e0d\u5feb\u9003\uff01\u201d\u539f\u6765\u5f20\u4e39\u67ab\u5ff5\u4ed6\u7684\u6b66\u529f\u4e5f\u5df2\u7ec3\u5230\u4e86\u7b2c \u4e00\u6d41\u5883\u754c\uff0c\u5728\u53cc\u5251\u5408\u58c1\u4e4b\u4e0b\uff0c\u4e5f\u80fd\u786c\u63a5\u4e00\u62db\uff0c\u6545\u6b64\u90a3\u4e03\u5904\u5251\u4f24\uff0c\u90fd\u5e76\u4e0d\u662f\u622e\u4ed6\u8981\u5bb3\u3002 \u3000\u3000\u8fd9\u65f6\u6458\u661f\u4e0a\u4eba\u4e0e\u5c60\u9f99\u5c0a\u8005\u7387\u9886\u6570\u5341\u540d\u6b66\u571f\uff0c\u4e5f\u5df2\u5408\u56f4\uff0c\u4f46\u5f20\u4e39\u67ab\u592b\u5987\u53cc\u5251\u5728\u624b\uff0c\u8fd9\u5e72\u4eba \u54ea\u91cc\u62e6\u6321\u5f97\u4f4f\uff0c\u89c1\u53cc\u5251\u8d77\u5904\uff0c\u78b0\u4e0a\u7684\u4e0d\u6b7b\u4fbf\u4f24\u3002\u6458\u661f\u4e0a\u4eba\u53eb\u9053\uff1a\u201c\u9000\u51fa\u9601\u5b50\uff0c\u518d\u56f4\u56f0\u4ed6\uff01\u201d \u8bdd\u672a\u8bf4\u5b8c\uff0c\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u4e00\u5de6\u4e00\u53f3\uff0c\u53cc\u5251\u5df2\u4f3c\u5954\u96f7\u95ea\u7535\u822c\u5730\u6740\u5230\uff01\u6458\u661f\u4e0a\u4eba\u8eab\u8eaf\u4e00\u77ee\uff0c\u6293\u8d77 \u4e86\u4e24\u4e2a\u6b66\u58eb\uff0c\u5de6\u53f3\u4e00\u6321\uff0c\u90a3\u4e24\u540d\u6b66\u58eb\u90fd\u7ed9\u957f\u5251\u7a7f\u8fc7\u4e86\u524d\u5fc3\u3002\u5f20\u4e39\u67ab\u559d\u9053\uff1a\u201c\u597d\u72e0\u6bd2\u7684\u6076 \u8d3c\uff01\u201d\u62bd\u5251\u518d\u523a\uff0c\u6458\u661f\u4e0a\u4eba\u5df2\u6e9c\u51fa\u9601\u5b50\uff0c\u9003\u5165\u82b1\u56ed\uff0c\u4f17\u6b66\u58eb\u89c1\u6458\u661f\u4e0a\u4eba\u4e3a\u4e86\u4fdd\u5168\u81ea\u5df1\uff0c\u4e0d\u60dc \u627e\u4eba\u66ff\u6b7b\uff0c\u66f4\u662f\u5bd2\u5fc3\uff0c\u5f53\u4e0b\u7eb7\u7eb7\u9003\u547d\uff0c\u4e00\u54c4\u800c\u6563\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u3001\u4e91\u91cd\u95ef\u51fa\u4e86\u4e07\u6625\u9601\uff0c\u8e0f\u5165\u4e86\u5fa1\u82b1\u56ed\uff0c\u5ffd\u542c\u5f97\u5a04\u6850\u5b59\u54c8\u54c8\u7684\u5927\u7b11\u4e4b\u58f0\uff0c\u53eb \u9053\uff1a\u201c\u5f20\u4e39\u67ab\uff0c\u7eb5\u7b97\u4f60\u6709\u5929\u5927\u7684\u795e\u901a\uff0c\u4eca\u65e5\u4e5f\u96be\u9003\u8fc7\u6211\u7684\u5929\u7f57\u5730\u7f51\uff01\u201d\u4f46\u89c1\u82b1\u6728\u4e1b\u4e2d\uff0c\u4eba\u5f71 \u7ef0\u7ef0\uff0c\u539f\u6765\u662f\u5a04\u6850\u5b59\u8c03\u6765\u4e86\u4e00\u5343\u540d\u795e\u7bad\u8425\u7684\u5f13\u7bad\u624b\uff0c\u65e9\u5df2\u57cb\u4f0f\u5728\u5916\uff0c\u4e00\u58f0\u4ee4\u4e0b\uff0c\u5f3a\u5f13\u731b\u5f29\uff0c \u56db\u9762\u5c04\u6765\uff0c\u5343\u7bad\u5982\u8757\uff0c\u628a\u5f20\u4e39\u67ab\u7b49\u4e09\u4eba\u5f53\u4f5c\u4e86\u6d3b\u9776\u5b50\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u4e0e\u4e91\u857e\u53cc\u5251\u4ea4\u821e\uff0c\u903c\u8d77\u4e86\u4e00\u5708\u94f6\u8679\uff0c\u5229\u7bad\u5c04\u5165\u5708\u4e2d\uff0c\u7eb7\u7eb7\u6298\u65ad\uff0c\u4e91\u91cd\u4e5f\u4ee5\u5927\u529b\u91d1 \u521a\u624b\u6cd5\uff0c\u5c06\u5c04\u6765\u7684\u7bad\uff0c\u5728\u79bb\u8eab\u516b\u5c3a\u4e4b\u5916\u9707\u843d\u3002\u4f46\u90a3\u4e00\u5343\u540d\u795e\u7bad\u624b\uff0c\u90fd\u662f\u4ece\u5fa1\u6797\u519b\u4e2d\u7cbe\u9009\u51fa\u6765 \u7684\uff0c\u5f3a\u5f13\u731b\u5f29\uff0c\u4ece\u56db\u9762\u516b\u65b9\u5c04\u6765\uff0c\u53ea\u8981\u7a0d\u4e00\u758f\u795e\uff0c\u4e2d\u4e86\u4e00\u652f\uff0c\u4fbf\u4f11\u60f3\u9003\u547d\u3002\u5343\u7bad\u5982\u8757\uff0c\u5bc6\u96c6 \u5982\u96e8\uff0c\u5f20\u4e39\u67ab\u7b49\u4e09\u4eba\u672c\u4e8b\u518d\u5927\uff0c\u4e5f\u96be\u4ee5\u51b2\u7834\u8fd9\u4e2a\u7bad\u9635\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u51c4\u7136\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4eca\u5929\u53ea\u6015\u662f\u54b1\u4eec\u6700\u540e\u4e00\u6b21\u7684\u8054\u5251\u5bf9\u654c\u4e86\u3002\u4f60\u8bf4\uff0c\u54b1\u4eec\u662f\u518d \u62fc\u6389\u4ed6\u767e\u6570\u5341\u4e2a\u9e70\u722a\u5b59\u5462\uff0c\u8fd8\u662f\u518d\u8fd9\u6837\u5730\u6328\u4e0b\u53bb\u5462\uff1f\u201d\u5f20\u4e39\u67ab\u5728\u5341\u4f59\u5e74\u524d\u521d\u9047\u4e91\u857e\u4e4b\u65f6\uff0c\u4e91 \u857e\u6b63\u662f\u521a\u79bb\u5e08\u95e8\uff0c\u5973\u626e\u7537\u88c5\uff0c\u884c\u8d70\u6c5f\u6e56\uff0c\u5f20\u4e39\u67ab\u53eb\u5979\u505a\u201c\u5c0f\u5144\u5f1f\u201d\u5df2\u53eb\u60ef\u4e86\uff0c\u7ed3\u5a5a\u4e4b\u540e\uff0c\u6539 \u79f0\u201c\u4e91\u59b9\u201d\uff0c\u4f46\u6709\u65f6\u5728\u95fa\u623f\u4e4b\u4e2d\u7b11\u8c11\uff0c\u8fd9\u201c\u5c0f\u5144\u5f1f\u201d\u4e09\u5b57\u4ecd\u4f1a\u51b2\u53e3\u800c\u51fa\u3002\u8fd9\u65f6\u5728\u6b64\u6781\u5ea6\u7d27\u5f20 \u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u5f20\u4e39\u67ab\u53eb\u51fa\u65e7\u65e5\u7684\u79f0\u547c\uff0c\u4e91\u857e\u60c5\u4e0d\u81ea\u7981\u5730\u751c\u751c\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f46\u51ed\u4f60\u7684 \u610f\u601d\uff01\u201d\u8bdd\u8bed\u4e2d\u5145\u6ee1\u4e86\u5bf9\u5f20\u4e39\u67ab\u7684\u4fe1\u8d56\u3002 \u3000\u3000\u5728\u8fd9\u6837\u5bc6\u96c6\u7684\u7bad\u96e8\u4e4b\u4e0b\uff0c\u82e5\u7136\u5f3a\u884c\u51b2\u51fa\uff0c\u81ea\u662f\u4e5d\u6b7b\u4e00\u751f\uff0c\u82e5\u50cf\u76ee\u524d\u8fd9\u6837\uff0c\u53cc\u5251\u914d\u5408\uff0c\u4e92 \u76f8\u7167\u5e94\uff0c\u4e0d\u79fb\u52a8\u8eab\u5f62\uff0c\u867d\u7136\u6682\u53ef\u652f\u6301\uff0c\u4f46\u7ec8\u662f\u5750\u4ee5\u5f85\u6bd9\u3002\u5f20\u4e39\u67ab\u4e00\u751f\u4e2d\u7ecf\u5386\u8fc7\u65e0\u6570\u8270\u9669\uff0c\u5c1a \u80fd\u5f53\u673a\u7acb\u65ad\uff0c\u4f46\u8fd9\u4e00\u56de\u5374\u6709\u70b9\u8e0c\u8e87\u83ab\u51b3\u4e86\u3002\u5c31\u5728\u4ed6\u4eec\u8bf4\u8bdd\u4e4b\u9645\uff0c\u7a0d\u4e00\u5206\u5fc3\uff0c\u6709\u4e24\u652f\u5229\u7bad\u5c45\u7136 \u5c04\u5165\u5251\u5149\u5c01\u9501\u7684\u5708\u4e2d\uff0c\u5f20\u4e39\u67ab\u8863\u8896\u4e00\u7529\uff0c\u5c06\u5b83\u62c2\u843d\uff0c\u4f46\u89c9\u52b2\u529b\u4e0d\u5c0f\uff0c\u663e\u7136\u662f\u9ad8\u624b\u6240\u5c04\uff0c\u5f20\u4e39 \u67ab\u54ac\u4e00\u54ac\u7259\uff01\u6b63\u60f3\u8bf4\u9053\uff1a\u201c\u51b2\u51fa\u53bb\u5427\uff01\u201d\u5ffd\u89c9\u90a3\u7bad\u96e8\u597d\u4f3c\u6bd4\u8f83\u758f\u4e86\uff0c\u5f20\u4e39\u67ab\u51dd\u795e\u4e00\u542c\uff0c\u5ffd\u5730 \u53eb\u9053\uff0c\u201c\u706b\u7130\u5f39\uff01\u201d\u53ea\u542c\u5f97\u567c\u567c\u556a\u556a\u7684\u70b8\u88c2\u58f0\uff0c\u5929\u7a7a\u4e2d\u7a81\u7136\u98de\u4e0b\u5341\u6570\u6735\u706b\u82b1\uff0c\u7206\u88c2\u5f00\u6765\uff0c\u706b \u82b1\u56db\u6e85\uff0c\u4e91\u857e\u9053\uff1a\u201c\u54a6\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8981\u653e\u8fd9\u79cd\u6697\u5668\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8fd9\u706b\u7130\u5f39\u662f\u4ece\u5916\u8fb9\u5c04\u6765 \u7684\uff01\u201d \u3000\u3000\u8f6c\u773c\u4e4b\u95f4\uff0c\u53c8\u662f\u5341\u51e0\u679a\u201c\u706b\u7130\u5f39\u201d\u548c\u201c\u86c7\u7130\u7bad\u201d\u5c04\u4e86\u8fdb\u6765\uff0c\u706b\u7130\u5f39\u4e13\u5728\u5f13\u7bad\u624b\u7684\u5934\u9876\u4e0a \u7a7a\u7206\u70b8\uff0c\u706b\u661f\u6e85\u5904\uff0c\u89e6\u7740\u5934\u53d1\u8863\u88f3\uff0c\u4fbf\u70e7\u8d77\u6765\uff0c\u86c7\u7130\u7bad\u631f\u7740\u4e00\u6e9c\u706b\u5149\uff0c\u5374\u4f3c\u6beb\u65e0\u76ee\u7684\u5730\u4e71 \u5c04\uff0c\u5c04\u5230\u82b1\u6728\u4e1b\u4e2d\uff0c\u4fbf\u7acb\u5373\u71c3\u8d77\u4e00\u7247\u706b\u5934\uff0c\u770b\u6765\u706b\u7130\u5f39\u4e43\u662f\u5bf9\u4eba\uff0c\u86c7\u7130\u7bad\u4e43\u662f\u5bf9\u7269\u3002 \u3000\u3000\u4f24\u4eba\u4e5f\u8fd8\u7f62\u4e86\uff0c\u5fa1\u82b1\u56ed\u4e2d\u8d77\u706b\uff0c\u53ef\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u5a04\u6850\u5b59\u5206\u51fa\u4e00\u90e8\u5206\u4eba\u53bb\u6551\u706b\uff0c\u86c7\u7130\u7bad\u56db \u5904\u4e71\u5c04\uff0c\u6251\u706d\u4e86\u4e00\u7247\u706b\u5934\u53c8\u8d77\u4e00\u7247\u706b\u5934\uff0c\u5fa1\u82b1\u56ed\u53c8\u662f\u5929\u4e0b\u6700\u5927\u7684\u82b1\u56ed\uff0c\u90a3\u4e00\u5343\u540d\u5f13\u7bad\u624b\u4ea6\u4e0d \u8fc7\u4ec5\u4ec5\u5305\u56f4\u5728\u4e07\u6625\u9601\u7684\u5468\u56f4\uff0c\u5360\u7740\u56ed\u5b50\u7684\u4e00\u89d2\u800c\u5df2\uff0c\u518d\u8fc7\u4e00\u9635\uff0c\u4e0d\u4f46\u8fd9\u4e00\u89d2\u8d77\u706b\uff0c\u9760\u8fd1\u5185\u5bab \u90a3\u4e00\u89d2\u4e5f\u8d77\u4e86\u706b\u5934\uff0c\u5bab\u5a25\u592a\u76d1\u7684\u547c\u53f7\u58f0\u5954\u8dd1\u58f0\u4e5f\u4f20\u51fa\u6765\u3002\u5f20\u4e39\u67ab\u5927\u53eb\u9053\uff1a\u201c\u5feb\u51b2\u51fa\u53bb\uff01\u201d\u8fd9 \u65f6\u5c04\u6765\u7684\u5f13\u7bad\u66f4\u758f\u4e86\uff0c\u6458\u661f\u4e0a\u4eba\u7387\u9886\u6709\u4e8c\u4e09\u5341\u540d\u6b66\u58eb\u5835\u7740\u7b2c\u4e00\u5708\uff0c\u5f20\u4e39\u67ab\u5154\u8d77\u9e58\u843d\uff0c\u500f\u5730\u5c31 \u6251\u8fdb\u7b2c\u4e00\u5c42\u5305\u56f4\u5708\uff0c\u4e00\u4f38\u624b\u6293\u7740\u4e86\u6458\u661f\u4e0a\u4eba\u7684\u80a9\u80db\u9aa8\uff0c\u53ea\u542c\u5f97\u201c\u52d2\u201d\u7684\u4e00\u58f0\uff0c\u5374\u626f\u4e0b\u4e86\u4ed6\u62ab \u5728\u8eab\u4e0a\u7684\u8888\u88df\u3002\u6458\u661f\u4e0a\u4eba\u7684\u6b66\u529f\u957f\u4e8e\u53d8\u5316\uff0c\u4ee5\u5f20\u4e39\u67ab\u7684\u529f\u529b\uff0c\u82e5\u7136\u6362\u4e86\u522b\u4eba\uff0c\u8fd9\u4e00\u6293\u4e07\u96be\u9003 \u8131\uff0c\u6458\u5c4b\u4eba\u4e0a\u5c45\u7136\u80fd\u5728\u5371\u6025\u4e4b\u9645\uff0c\u65bd\u5c55\u201c\u91d1\u8749\u8131\u58f3\u201d\u4e4b\u8ba1\uff0c\u820d\u6389\u4e86\u8eab\u4e0a\u7684\u8888\u88df\uff0c\u5f20\u4e39\u67ab\u5148\u662f \u4e00\u6123\uff0c\u7ee7\u800c\u7b11\u9053\uff1a\u201c\u770b\u5728\u4f60\u8fd9\u624b\u4e4c\u9f9f\u7f29\u9888\u529f\u592b\u7684\u4efd\u4e0a\uff0c\u5c31\u518d\u9976\u4f60\u4e00\u6b21\u3002\u201d\u8888\u88df\u4e00\u5c55\uff0c\u53c8\u626b\u7ffb \u4e86\u51e0\u4e2a\u6b66\u58eb\u3002\u8fd9\u65f6\u5fa1\u82b1\u56ed\u91cc\u53c8\u8d77\u4e86\u5341\u51e0\u5904\u706b\u5934\uff0c\u5fa1\u82b1\u56ed\u4e2d\u4f4f\u7684\uff0c\u591a\u662f\u7687\u5e1d\u5ba0\u7231\u7684\u5983\u5ad4\u5bab\u5a25\uff0c \u800c\u4ead\u53f0\u697c\u9601\u5efa\u7b51\u7684\u534e\u4e3d\uff0c\u66f4\u80dc\u4e8e\u6b63\u5bab\u5927\u6bbf\uff0c\u82e5\u7ed9\u706b\u52bf\u8513\u5ef6\uff0c\u90a3\u771f\u662f\u4e0d\u582a\u8bbe\u60f3\u4e4b\u4e8b\u3002\u5c31\u5728\u5bab\u5a25 \u592a\u76d1\u7684\u547c\u53f7\u58f0\u4e2d\uff0c\u897f\u5317\u89d2\u5ffd\u7136\u4f20\u6765\u4e00\u58f0\u957f\u5578\uff0c\u7ee7\u800c\u4e1c\u5357\u89d2\u4e5f\u4f20\u51fa\u4e86\u7c97\u8c6a\u7684\u5578\u58f0\uff0c\u7247\u523b\u4e4b\u95f4\uff0c \u5578\u58f0\u6b64\u8d77\u5f7c\u843d\uff0c\u5fa1\u82b1\u56ed\u4e2d\u5404\u5904\u7684\u5b88\u671b\u53f0\u7eb7\u7eb7\u9f13\u8d77\u8b66\u949f\uff0c\u62a5\u9053\u53d1\u73b0\u523a\u5ba2\uff0c\u8fd9\u4e00\u6765\u56ed\u4e2d\u7684\u6b66\u58eb\u66f4 \u662f\u4e71\u6210\u4e00\u7247\u3002\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u6765\u4eba\u771f\u662f\u806a\u660e\u7edd\u9876\uff0c\u9664\u4e86\u653e\u706b\u4e4b\u5916\uff0c\u786e\u662f\u518d\u65e0\u522b\u6cd5\u9000\u6389\u8fd9\u4e00\u5927 \u6279\u7684\u5fa1\u6797\u519b\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u542c\u6765\u7684\u5171\u662f\u51e0\u4eba\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8fd9\u5578\u58f0\u58f0\u58f0\u4e0d\u540c\uff0c\u597d \u50cf\u662f\u6df7\u8fdb\u4e86\u8bb8\u591a\u4eba\uff0c\u5176\u5b9e\u53ea\u662f\u4e24\u4eba\u6240\u53d1\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u7684\u6b66\u529f\uff0c\u4e0d\u5728\u4f60\u6211\u4e4b\u4e0b\u3002\u5927 \u54e5\uff0c\u6211\u53ef\u5e76\u672a\u542c\u4f60\u8bf4\u8fc7\u5728\u4eac\u5e08\u6709\u8fd9\u7b49\u672c\u4e8b\u7684\u597d\u670b\u53cb\u3002\u201d\u5f20\u4e39\u67ab\u5fc3\u4e2d\u4e00\u52a8\uff0c\u7b11\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u672a \u66fe\u76f8\u8bc6\uff0c\u800c\u6709\u610f\u4e0e\u54b1\u4eec\u7ed3\u4ea4\u7684\u670b\u53cb\u5427\u3002\u54c8\uff0c\u4ed6\u4eec\u8fd9\u4efd\u793c\u7269\u9001\u5f97\u592a\u539a\u4e86\uff0c\u4e0d\u7531\u5f97\u54b1\u4eec\u4e0d\u53bb\u56de\u62dc \u4ed6\u4e86\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u7b49\u4e09\u4eba\u8212\u4e86\u53e3\u6c14\uff0c\u5a04\u6850\u5b59\u3001\u9633\u5b97\u6d77\u7b49\u4eba\u53ef\u662f\u7740\u6025\u975e\u5e38\uff0c\u6743\u8861\u5229\u5bb3\uff0c\u53ea\u5f97\u653e\u677e\u5bf9\u5f20 \u4e39\u67ab\u7684\u5305\u56f4\uff0c\u9633\u5b97\u6d77\u5927\u53eb\u9053\uff1a\u201c\u6551\u706b\u8981\u7d27\uff01\u201d\u5a04\u6850\u5b59\u5927\u53eb\u9053\uff1a\u201c\u4fdd\u9a7e\u7b2c\u4e00\uff01\u201d\u6458\u661f\u4e0a\u4eba\u4e5f\u5927 \u53eb\u9053\uff1a\u201c\u5feb\u968f\u6211\u6765\u6349\u62ff\u523a\u5ba2\u5440\uff01\u201d\u201c\u6551\u706b\uff01\u201d\u201c\u4fdd\u9a7e\uff01\u201d\u201c\u51b3\u62ff\u523a\u5ba2\uff01\u201d\u79cd\u79cd\u53eb\u58f0\uff0c\u4e71\u6210\u4e00 \u7247\uff0c\u970e\u65f6\u95f4\uff0c\u90a3\u4e00\u5343\u540d\u795e\u7bad\u624b\u6563\u53bb\u4e86\u516b\u4e5d\u767e\u4eba\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u6458\u661f\u8001\u9053\u9001\u7ed9\u6211\u8fd9\u4ef6\u8888\u88df\uff0c\u6b63\u597d\u6d3e\u4e0a\u7528\u573a\uff01\u201d\u8888\u88df\u4e00\u6296\uff0c\u6709\u5982\u5927\u9e4f\u5c55 \u7ffc\uff0c\u51b2\u5165\u706b\u573a\uff0c\u706b\u52bf\u8fd8\u672a\u5f88\u5927\uff0c\u88ab\u8888\u88df\u4e00\u62e8\uff0c\u706b\u7130\u4e24\u9762\u5206\u5f00\uff0c\u4e91\u857e\u3001\u4e91\u91cd\u968f\u5728\u5f20\u4e39\u67ab\u8eab\u540e\uff0c \u98de\u63a0\u800c\u8fc7\uff0c\u8d8a\u8fc7\u4e86\u56db\u4e94\u5904\u706b\u573a\uff0c\u8888\u88df\u70e7\u4e86\u8d77\u6765\uff0c\u4f46\u4ed6\u4eec\u4e5f\u5230\u4e86\u5fa1\u82b1\u56ed\u7684\u540e\u95e8\uff0c\u5c60\u9f99\u5c0a\u8005\u548c\u5341 \u591a\u540d\u6b66\u58eb\u6b63\u5728\u90a3\u91cc\u6551\u706b\uff0c\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u5f20\u4e39\u67ab\u7adf\u7136\u6765\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c\u706b\u70df\u906e\u773c\uff0c\u8fd8\u4ee5\u4e3a\u662f\u81ea\u5df1 \u4eba\uff0c\u5f85\u5230\u9aa4\u7136\u8ba4\u51fa\u4e86\u662f\u5f20\u4e39\u67ab\u65f6\uff0c\u201c\u554a\u5440\u201d\u4e00\u58f0\u521a\u521a\u51fa\u53e3\uff0c\u5c31\u88ab\u5f20\u4e39\u67ab\u7528\u90a3\u7740\u706b\u7684\u8888\u88df\u8fce\u5934 \u4e00\u7f69\uff0c\u4e91\u91cd\u65bd\u5c55\u5927\u6454\u7891\u624b\u7684\u529f\u592b\uff0c\u4e00\u628a\u5c06\u4ed6\u6293\u8d77\uff0c\u629b\u5165\u4e86\u706b\u5806\u91cc\u9762\u3002 \u3000\u3000\u8fd9\u5341\u591a\u540d\u6b66\u58eb\u54ea\u91cc\u8fd8\u6562\u62b5\u654c\uff0c\u5f53\u4e0b\u4e00\u54c4\u800c\u6563\uff0c\u4e91\u91cd\u594b\u8d77\u795e\u5a01\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u201c\u5927\u529b\u91d1\u521a \u624b\u201d\u4ee5\u5341\u6210\u771f\u529b\u53d1\u51fa\uff0c\u53ea\u4e00\u638c\u5c31\u9707\u574d\u4e86\u90a3\u5305\u7740\u94c1\u76ae\u7684\u539a\u6728\u5bab\u95e8\uff0c\u5fa1\u82b1\u56ed\u7684\u540e\u95e8\u5916\u9762\u5c31\u662f\u666f \u5c71\uff0c\u5f20\u4e39\u67ab\u7b49\u4e09\u4eba\u5b89\u7136\u8131\u9669\uff0c\u9003\u81f3\u5c71\u4e0a\uff0c\u56de\u5934\u4e00\u671b\uff0c\u5fa1\u82b1\u56ed\u4e2d\u7684\u706b\u52bf\uff0c\u8fd8\u6ca1\u6709\u6251\u706d\uff01\u6b63\u662f\uff1a \u3000\u3000\u53ef\u601c\u62a5\u56fd\u82f1\u96c4\u5fd7\uff0c\u90fd\u88ab\u51b2\u5929\u4e00\u706b\u711a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e09\u5341\u56db\u56de\u3000\u4e16\u4e71\u89c1\u4eba\u5fc3\u3000\u6765\u5bfb\u4fa0\u8ff9\u3000\u75be\u98ce\u77e5\u52b2\u8349\u3000\u72ec\u5b88\u5371\u57ce \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e09\u5341\u56db\u56de\u3000\u4e16\u4e71\u89c1\u4eba\u5fc3\u3000\u6765\u5bfb\u4fa0\u8ff9\u3000\u75be\u98ce\u77e5\u52b2\u8349\u3000\u72ec\u5b88\u5371\u57ce \u3000\u3000\u4e91\u91cd\u6012\u6c14\u672a\u6d88\uff0c\u6068\u6068\u8bf4\u9053\uff1a\u201c\u771f\u662f\u4e2a\u5fe0\u5978\u4e0d\u8fa8\u7684\u660f\u541b\uff0c\u54b1\u4eec\u8fd9\u6837\u4e3a\u4ed6\u6253\u7b97\uff0c\u4ed6\u5374\u60f3\u628a\u54b1 \u4eec\u4e00\u7f51\u6253\u5c3d\uff0c\u597d\uff0c\u8fd9\u4e00\u628a\u706b\u82e5\u628a\u4ed6\u7684\u4e09\u5bab\u516d\u9662\u70e7\u4e3a\u5e73\u5730\uff0c\u5012\u4e5f\u5927\u5feb\u4eba\u5fc3\uff01\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a \u201c\u8fd9\u7687\u5bab\u4e5f\u662f\u8001\u767e\u59d3\u7684\u8840\u6c57\u5efa\u6210\u7684\uff0c\u771f\u7684\u4e00\u628a\u706b\u70e7\u4e86\uff0c\u4e5f\u592a\u53ef\u60dc\u3002\u800c\u4e14\u70e7\u4e86\u4ed6\u4e0d\u4f1a\u518d\u5efa\u4e48\uff1f \u66f4\u82e6\u4e86\u5929\u4e0b\u7684\u767e\u59d3\u3002\u201d\u4e91\u91cd\u9053\uff1a\u201c\u6211\u53ea\u662f\u6c14\u8fd9\u660f\u541b\u4e0d\u8fc7\uff01\u201d\u56de\u60f3\u8d77\u81ea\u5df1\u5e74\u8f7b\u65f6\u5019\u90a3\u4e00\u80a1\u5fe0\u541b \u7231\u56fd\u7684\u70ed\u8bda\uff0c\u518d\u4fef\u89c6\u7687\u5bab\u4e2d\u7684\u5927\u706b\uff0c\u4e0d\u89c9\u611f\u6168\u4e07\u5206\uff0c\u4f46\u89c9\u4e00\u8154\u70ed\u8840\uff0c\u62a5\u56fd\u65e0\u4ece\uff0c\u5c11\u5e74\u65f6\u5019\u7684 \u5929\u771f\u613f\u671b\uff1a\u8f85\u4f50\u541b\u7687\u3001\u5b89\u90a6\u5b9a\u56fd\u7684\u96c4\u5fc3\u58ee\u5fd7\uff0c\u7adf\u4f3c\u88ab\u8fd9\u573a\u5927\u706b\u70e7\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u4ed6\u662f\u660f\u541b\uff0c\u6211\u770b\u4ed6\u81ea\u5df1\u4e00\u5b9a\u8ba4\u4e3a\u81ea\u5df1\u7cbe\u660e\u5f97\u5f88\u5462\u3002\u54b1\u90fd\u662f\u88ab\u4ed6\u8ba4 \u4e3a\u4f1a\u5371\u5bb3\u53ca\u4ed6\u7684\u7687\u4f4d\u7684\u4eba\uff0c\u62db\u4ed6\u6240\u5fcc\u662f\u5fc5\u7136\u4e4b\uff0c\u54c8\uff0c\u54c8\uff01\u4e0d\u62db\u4eba\u5fcc\u662f\u5eb8\u624d\uff0c\u53d7\u7687\u5e1d\u4e4b\u5fcc\uff0c\u4e5f \u5927\u8db3\u4ee5\u81ea\u8c6a\u4e86\u5462\uff01\u201d\u8fde\u8bf4\u5e26\u529d\uff0c\u5c06\u4e91\u91cd\u7684\u6012\u6c14\u6d88\u4e86\u3002\u4e91\u857e\u8bb0\u6302\u6ce2\u65af\u516c\u4e3b\uff0c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u770b \u6ce2\u65af\u516c\u4e3b\u548c\u9a78\u9a6c\u88ab\u7687\u5e1d\u6263\u5728\u5bab\u4e2d\uff0c\u6709\u65e0\u5371\u9669\uff1f\u201d\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u975e\u4f46\u6ca1\u6709\u5371\u9669\uff0c\u7948\u9547\u4e00\u5b9a\u8fd8 \u4f1a\u4ee5\u56fd\u5bbe\u4e4b\u793c\u3002\u4ed6\u4e0d\u6bd4\u54b1\u4eec\uff0c\u7948\u9547\u4e3a\u4e86\u81ea\u5df1\u7684\u6c5f\u5c71\u7740\u60f3\uff0c\u4e0d\u7ba1\u5c06\u6765\u662f\u5426\u80fd\u591f\u548c\u6ce2\u65af\u8054\u76df\uff0c\u4ed6 \u65bd\u4e9b\u5c0f\u6069\u5c0f\u60e0\uff0c\u7ed3\u597d\u4e00\u4e2a\u5916\u90a6\u516c\u4e3b\uff0c\u53c8\u4f55\u4e50\u800c\u4e0d\u4e3a\u3002\u201d\u4e91\u857e\u4e5f\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u8fd9\u6b21\u5165 \u5bab\uff0c\u4e3a\u4ed6\u5256\u9648\u5229\u5bb3\uff0c\u4ed6\u867d\u7136\u60f3\u9664\u6389\u4f60\uff0c\u4e5f\u4e0d\u5f97\u4e0d\u542c\u4ece\u4f60\u4e09\u7b56\u4e4b\u4e2d\u8fdc\u4ea4\u8fd1\u653b\u7684\u90a3\u4e00\u7b56\u7565\u5462\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5fa1\u82b1\u56ed\u4e2d\u7684\u706b\u52bf\u5df2\u6e10\u6e10\u51cf\u5f31\uff0c\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u54b1\u4eec\u53ef\u522b\u53ea\u987e\u8bf4\u8bdd\u4e86\uff0c\u53ea\u6015\u706b\u5934 \u6251\u706d\u4e4b\u540e\uff0c\u4ed6\u4eec\u53c8\u8981\u8ffd\u51fa\u6765\u4e86\u3002\u5feb\u56de\u53bb\u5427\u3002\u201d\u4e91\u857e\u9053\uff1a\u201c\u56de\u53bb\u54ea\u513f\uff1f\u201d\u5f20\u4e39\u67ab\u60f3\u4e86\u4e00\u60f3\uff0c\u7b11 \u9053\uff1a\u201c\u4f60\u5fd8\u8bb0\u4e86\u7ed9\u54b1\u4eec\u9001\u793c\u7684\u4eba\u4e48\uff1f\u597d\u5427\uff0c\u54b1\u4eec\u5c31\u8fde\u591c\u5230\u516b\u8fbe\u5cad\u53bb\u5411\u4ed6\u4eec\u56de\u62dc\uff0c\u8fd9\u4e24\u4e2a\u670b\u53cb \u5012\u662f\u503c\u5f97\u4ea4\u4ea4\u3002\u201d\u4e91\u857e\u4e5f\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6bcf\u6b21\u6599\u4e8b\u90fd\u6599\u5f97\u4e0d\u9519\uff0c\u770b\u770b\u8fd9\u56de\u6599\u5f97\u5982\u4f55\uff1f\u6211\u5374\u662f \u60f3\u4e0d\u660e\u767d\uff0c\u4ed6\u4eec\u600e\u77e5\u9053\u54b1\u4eec\u5728\u7687\u5bab\u4e2d\u6709\u6027\u547d\u4e4b\u5371\uff0c\u800c\u53c8\u80af\u8fd9\u6837\u5730\u5192\u9669\u6765\u6551\uff1f\u201d \u3000\u3000\u5f20\u4e39\u67ab\u548c\u4e91\u857e\u5144\u59b9\u90fd\u6068\u4e0d\u5f97\u7acb\u5373\u8d76\u5230\u516b\u8fbe\u5cad\u6253\u7834\u8fd9\u4e2a\u7591\u56e2\u3002\u4f46\u5f20\u4e39\u67ab\u5374\u60f3\u4e0d\u5230\u4ed6\u7684\u5f92\u5f1f \u4e5f\u4e3a\u4e86\u8981\u6253\u7834\u4e00\u4e2a\u7591\u56e2\uff0c\u5df2\u5148\u5230\u516b\u8fbe\u5cad\u53bb\u4e86\u3002 \u3000\u3000\u4e14\u8bf4\u5f20\u4e39\u67ab\u79bb\u5f00\u4e86\u98de\u9f99\u9556\u5c40\u4e4b\u540e\uff0c\u9f99\u9556\u5934\u7acb\u5373\u9063\u6563\u4e86\u9556\u5c40\u4e2d\u7684\u4f19\u8ba1\uff0c\u4e8e\u627f\u73e0\u672c\u6765\u60f3\u5e26\u5c0f \u864e\u5b50\u5230\u66f9\u592a\u76d1\u5bb6\u4e2d\u53bb\u6682\u907f\u4e00\u65f6\u7684\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u4e34\u65f6\u53d8\u8ba1\uff0c\u4e0e\u5c0f\u864e\u5b50\u540c\u4e58\u767d\u9a6c\uff0c\u51fa\u4e86\u897f\u95e8\uff0c\u76f4 \u5230\u4e86\u5c45\u5eb8\u5173\u5916\u3002\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u54a6\uff0c\u627f\u73e0\u59d0\u59d0\uff0c\u4f60\u5e26\u6211\u5230\u8fd9\u8352\u5c71\u91ce\u5cad\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u7b11 \u9053\uff1a\u201c\u5c0f\u987d\u7ae5\uff0c\u4f60\u6700\u8d2a\u73a9\uff0c\u6211\u800c\u4eca\u5e26\u4f60\u6765\u770b\u5929\u4e0b\u7684\u5947\u666f\u2014\u2014\u957f\u57ce\uff0c\u4f60\u8fd8\u4e0d\u9ad8\u5174\u5417\uff1f\u201d\u63a8\u5c0f\u864e \u5b50\u4e0b\u9a6c\uff0c\u5c06\u9a6c\u653e\u5165\u5c71\u6797\uff0c\u8fd9\u65f6\u6b63\u662f\u9ec4\u660f\u65f6\u5206\uff0c\u5728\u82cd\u832b\u5915\u7167\u4e4b\u4e2d\uff0c\u8fdc\u671b\u4e07\u91cc\u957f\u57ce\uff0c\u5c31\u50cf\u4e00\u6761\u91d1 \u9ec4\u8272\u7684\u957f\u86c7\uff0c\u5728\u7fa4\u5c71\u4e4b\u4e2d\u5a49\u8712\u800c\u8fc7\u3002 \u3000\u3000\u4e07\u91cc\u957f\u57ce\u662f\u4e2d\u56fd\u5386\u53f2\u4e0a\u6700\u4f1f\u5927\u7684\u5efa\u7b51\uff0c\u4ece\u5609\u5cea\u5173\u5230\u5c71\u6d77\u5173\uff0c\u5728\u4e1b\u5c71\u5cfb\u5cad\u4e2d\u5a49\u8712\u4e00\u4e07\u4e8c\u5343 \u4f59\u91cc\uff0c\u5c45\u5eb8\u5173\u8fd9\u6bb5\u901a\u8fc7\u516b\u8fbe\u5cad\u3002\u4e8e\u627f\u73e0\u548c\u5c0f\u864e\u5b50\u4ece\u5c45\u5eb8\u5173\u7684\u5357\u9762\u5c71\uff0c\u4e0a\u4e86\u957f\u57ce\uff0c\u4f46\u89c1\u5c71\u5cf0\u91cd \u53e0\uff0c\u4e00\u671b\u65e0\u5c3d\uff0c\u4e07\u91cc\u957f\u57ce\uff0c\u6709\u5982\u4e00\u6761\u770b\u4e0d\u89c1\u9996\u5c3e\u7684\u957f\u86c7\uff0c\u5c0f\u864e\u5b50\u5728\u57ce\u5899\u4e0a\u62ab\u895f\u8fce\u98ce\uff0c\u5927\u547c\u723d \u5feb\uff0c\u5ffd\u800c\u6000\u7591\u95ee\u9053\uff1a\u201c\u627f\u73e0\u59d0\u59d0\uff0c\u4f60\u771f\u7684\u53ea\u662f\u4e3a\u4e86\u5e26\u6211\u6765\u770b\u4e07\u91cc\u957f\u57ce\u3002\u201d\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u600e \u4e48\uff0c\u96be\u9053\u4e0d\u597d\u770b\u5417\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u597d\u770b\uff0c\u4f46\u5929\u8272\u5df2\u665a\uff0c\u56de\u53bb\u65f6\u57ce\u95e8\u6015\u5df2\u5173\u4e86\u3002\u54a6\uff0c\u6211\u4e0d\u4fe1 \u4f60\u4eca\u65e5\u6709\u8fd9\u6837\u95f2\u60c5\u9038\u81f4\uff0c\u5e26\u6211\u6e38\u5c71\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5657\u55e4\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u4eca\u665a\u5c31\u5728\u516b\u8fbe\u5cad\u4e2d\u5bfb\u627e\u4e00\u5904\u4f4f\u5bbf\u7684\u5730\u65b9\u3002\u5c0f\u864e\u5b50\uff0c\u4f60\u6015\u5c71\u4e2d\u7684 \u91ce\u72fc\u5403\u4e86\u4f60\u5417\uff1f\u5b9a\u8981\u8d76\u56de\u5317\u4eac\uff1f\u201d\u5c0f\u864e\u7b11\u9053\uff1a\u201c\u6211\u6015\u91ce\u72fc\u4e86\uff0c\u54c8\uff0c\u6211\u6b63\u60f3\u627e\u4e00\u53ea\u91ce\u72fc\u70e4\u6765\u5403 \u5462\uff0c\u53ef\u662f\u54b1\u4eec\u7684\u5e08\u7236\u53eb\u54b1\u4eec\u5728\u66f9\u592a\u76d1\u7684\u5bb6\u91cc\u7b49\u4ed6\u4eec\uff0c\u4ed6\u4eec\u627e\u4e0d\u7740\u54b1\u4eec\uff0c\u90a3\u600e\u4e48\u529e\uff1f\u55ef\uff0c\u627f\u73e0 \u59d0\u59d0\uff0c\u4f60\u4e00\u5411\u542c\u5e08\u7236\u7684\u8bdd\uff0c\u4eca\u6b21\u5374\u64c5\u4f5c\u4e3b\u5f20\uff0c\u5e26\u6211\u5230\u8fd9\u91cc\u6765\uff0c\u5176\u4e2d\u5b9a\u6709\u7f18\u6545\uff0c\u597d\uff0c\u4f60\u518d\u4e0d \u8bf4\uff0c\u6211\u5c06\u6765\u5411\u5e08\u7236\u544a\u4f60\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u6211\u5e26\u4f60\u5230\u8fd9\u91cc\u6765\uff0c\u5c31\u662f\u4e3a\u4e86\u7b49\u5019\u5e08\u7236\uff0c\u4f60\u4e0d\u8bb0\u5f97\u5e08\u7236\u66fe\u4e0e\u90a3\u4e2a\u4ec0\u4e48\u2018\u516b \u8fbe\u5c71\u4eba\u2019\u7ea6\u5b9a\uff0c\u5728\u516b\u8fbe\u5cad\u7684\u70b9\u5c06\u53f0\u4f1a\u9762\u5417\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u5e08\u7236\u53ef\u5e76\u6ca1\u6709\u7ea6\u5b9a\u65f6\u95f4\u554a\u3002\u201d\u4e8e \u627f\u73e0\u9053\uff1a\u201c\u4ed6\u4eec\u5728\u7687\u5bab\u4e2d\u51fa\u6765\u4e4b\u540e\uff0c\u8fdf\u65e9\u90fd\u4f1a\u5230\u90a3\u91cc\u7684\u3002\u6211\u7740\u6025\u8981\u89c1\u90a3\u4e2a\u4ec0\u4e48\u516b\u8fbe\u5c71\u4eba\uff0c\u5148 \u6765\u63a2\u542c\u4e00\u4e0b\uff0c\u60f3\u5e08\u7236\u4e5f\u4e0d\u4f1a\u602a\u8d23\u3002\u201d \u3000\u3000\u5c0f\u864e\u5b50\u5947\u9053\uff1a\u201c\u4f60\u8ba4\u8bc6\u90a3\u4e2a\u516b\u8fbe\u5c71\u4eba\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d\u8ba4\u5f97\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u90a3\u4f60\u600e \u6837\u627e\u4ed6\uff1f\u4ed6\u548c\u5e08\u7236\u53c8\u4e0d\u662f\u7ea6\u5b9a\u4eca\u665a\u89c1\u9762\uff0c\u4f60\u600e\u4e48\u4fdd\u5f97\u4f4f\u4ed6\u4f1a\u5728\u70b9\u5c06\u53f0\u7b49\u4f60\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u54b1\u4eec\u628a\u516b\u8fbe\u5cad\u641c\u904d\uff0c\u4e0d\u4fe1\u627e\u4e0d\u7740\u4ed6\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u503c\u5f97\u4f60\u8fd9\u6837\u6025 \u4e8e\u5bfb\u89c5\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6211\u5e0c\u671b\u4ed6\u662f\u4e00\u4f4d\u6211\u95fb\u540d\u5df2\u4e45\u5374\u4ece\u672a\u89c1\u8fc7\u9762\u7684\u670b\u53cb\u3002\u597d\uff0c\u5c0f\u864e\u5b50\uff0c\u4e0d \u5fc5\u591a\u95ee\u4e86\uff0c\u54b1\u4eec\u4e0b\u4e86\u957f\u57ce\uff0c\u8fdb\u6797\u5b50\u53bb\u5427\u3002\u201d \u3000\u3000\u5c0f\u864e\u5b50\u6000\u7591\u4e4b\u6781\uff0c\u7981\u4e0d\u4f4f\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u672a\u89c1\u8fc7\u9762\u7684\u670b\u53cb\uff0c\u662f\u7537\u7684\u8fd8\u662f\u5973\u7684\uff1f\u201d\u4e8e\u627f \u73e0\u9053\uff1a\u201c\u662f\u7537\u7684\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u4e0d\u559c\u6b22\u53f6\u6210\u6797\u54e5\u54e5\u4e86\u5417\uff1f\u201d\u4e8e\u627f\u73e0\u5550\u4e86\u4e00\u53e3\u9053\uff1a \u201c\u4f60\u8fd9\u5c0f\u9b3c\u5934\uff0c\u6ee1\u8111\u5b50\u4e0d\u6b63\u7ecf\uff0c\u4f60\u518d\u80e1\u8bf4\uff0c\u6211\u5c31\u6253\u4f60\uff01\u201d\u5c0f\u864e\u5b50\u4f38\u4f38\u820c\u5934\uff0c\u4e0d\u6562\u518d\u8bf4\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u60f3\u627e\u7684\u662f\u970d\u5929\u90fd\uff0c\u5979\u542c\u897f\u5c71\u836f\u9690\u53f6\u5143\u7ae0\u8bf4\u90a3\u4e2a\u201c\u5c11\u5e74\u4fa0\u58eb\u201d\u4f4f\u5728\u516b\u8fbe\u5cad\uff0c\u7ed9\u5e08\u7236 \u9001\u793c\u7684\u4eba\u53c8\u81ea\u79f0\u201c\u516b\u8fbe\u5c71\u4eba\u201d\uff0c\u5fc3\u4e2d\u4fbf\u6000\u7591\u8fd9\u4e24\u4eba\u5373\u662f\u4e00\u4eba\uff0c\u591a\u534a\u4fbf\u662f\u970d\u5929\u90fd\u3002\u4ed6\u7a76\u7adf\u662f\u6b7b \u662f\u751f\uff0c\u8fd9\u7591\u56e2\u5b9a\u8981\u6253\u7834\u3002\u4e8e\u627f\u73e0\u81ea\u5e7c\u5728\u4eac\u5e08\u957f\u5927\uff0c\u4e07\u91cc\u957f\u57ce\u4e5f\u662f\u65e7\u6e38\u4e4b\u5730\uff0c\u53ef\u662f\u201c\u70b9\u5c06\u53f0\u201d \u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u5979\u5374\u4e0d\u77e5\u9053\uff0c\u8fd9\u65f6\u66ae\u971e\u5df2\u5408\uff0c\u591c\u8272\u82cd\u832b\uff0c\u8352\u5c71\u91cc\u6773\u65e0\u4eba\u8ff9\uff0c\u4e8e\u627f\u73e0\u6000\u7740\u4e00\u80a1\u6fc0 \u60c5\u800c\u6765\uff0c\u8fd9\u65f6\u5fc3\u4e2d\u5374\u4e0d\u7981\u6697\u6697\u7740\u6025\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u4e0e\u5c0f\u864e\u5b50\u641c\u904d\u4e86\u5468\u56f4\u5341\u91cc\u4e4b\u5730\uff0c\u8fde\u8305\u5c4b\u4e5f\u6ca1\u6709\u4e00\u95f4\uff0c\u591c\u8272\u66f4\u6d53\uff0c\u6708\u4eae\u4e5f\u5347\u8d77\u6765 \u4e86\uff0c\u68ee\u6797\u91cc\u591c\u98ce\u547c\u5578\uff0c\u65f6\u4e0d\u65f6\u4f20\u6765\u733f\u557c\u72fc\u55e5\u7684\u58f0\u97f3\uff0c\u5c0f\u864e\u5b50\u7b11\u9053\uff1a\u201c\u5e78\u800c\u4eca\u665a\u6708\u4eae\u6b63\u5706\uff0c\u8981 \u4e0d\u7136\u82e5\u662f\u6709\u91ce\u72fc\u5077\u88ad\u4e5f\u4e0d\u77e5\u9053\u5462\uff1f\u600e\u4e48\u6837\uff1f\u54b1\u4eec\u4eca\u665a\u5c31\u5728\u8fd9\u6797\u5b50\u91cc\u884c\u5230\u5929\u660e\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u5ffd \u5730\u4ef0\u5929\u541f\u9053\uff1a\u201c\u98de\u5c3d\u8fbd\u5929\u5bfb\u6bd4\u7ffc\uff0c\u51cc\u4e91\u4e00\u51e4\u843d\u8c01\u5bb6\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u4e8f\u4f60\u8fd8\u6709\u8fd9\u6837\u5174\u81f4\u541f \u8bd7\uff01\u201d\u4e8e\u627f\u73e0\u7684\u5185\u529f\u5df2\u6709\u76f8\u5f53\u6839\u5e95\uff0c\u58f0\u97f3\u80fd\u591f\u9f13\u6c14\u884c\u8fdc\uff0c\u4f46\u542c\u5f97\u201c\u51cc\u4e91\u4e00\u51e4\u201d\u201c\u4e00\u51e4\u201d\u201c\u4e00 \u51e4\u201d\u201c\u51e4\u3001\u51e4\u2026\u2026\u201d\u7684\u56de\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u81ea\u5fd6\u5341\u6570\u91cc\u5185\uff0c\u82e5\u7136\u6709\u4eba\uff0c\u5b9a\u80fd\u542c\u5230\uff0c\u53ef\u662f\u76f4\u5230\u90a3\u56de \u97f3\u8d8a\u4f20\u8d8a\u8fdc\uff0c\u542c\u4e0d\u89c1\u4e86\uff0c\u6797\u5b50\u91cc\u4ecd\u662f\u6beb\u65e0\u53cd\u5e94\u3002 \u3000\u3000\u6708\u5149\u5012\u662f\u751a\u4e3a\u660e\u4eae\uff0c\u4e00\u4e1b\u4e1b\u4e0d\u77e5\u540d\u7684\u9c9c\u82b1\u5728\u591c\u98ce\u4e2d\u98a4\u6296\uff0c\u666f\u8272\u6e05\u5e7d\u4e4b\u6781\uff0c\u4ee4\u4eba\u6709\u70b9\u4e0d\u5bd2 \u800c\u6817\u3002\u4e8e\u627f\u73e0\u5ffd\u5730\u60f3\u8d77\u5728\u8299\u84c9\u5c71\u4e4b\u591c\uff0c\u4e0e\u51cc\u4e91\u51e4\u8e0f\u96ea\u5bfb\u6885\uff0c\u503e\u8c08\u5fc3\u4e8b\uff0c\u90a3\u666f\u8272\u5c31\u50cf\u4eca\u665a\u4e00 \u822c\u3002\u90a3\u4e00\u665a\u5979\u7b2c\u4e00\u6b21\u5728\u51cc\u4e91\u51e4\u53e3\u4e2d\u77e5\u9053\u970d\u5929\u90fd\u7684\u6545\u4e8b\uff0c\u800c\u4eca\u665a\u5219\u4e3a\u5979\u5bfb\u627e\u970d\u5929\u90fd\uff1b\u4e8e\u627f\u73e0\u4e0d \u65ad\u5730\u5728\u5fc3\u4e2d\u9ed8\u7977\uff1a\u201c\u4f46\u613f\u51cc\u4e91\u51e4\uff0c\u80fd\u5bfb\u56de\u5979\u7684\u4f34\u4fa3\uff0c\u6bd4\u7ffc\u540c\u98de\uff01\u201d \u3000\u3000\u5ffd\u542c\u5f97\u201c\u55d6\u201d\u7684\u4e00\u58f0\uff0c\u6253\u7834\u4e86\u6797\u5b50\u7684\u5bc2\u9759\uff0c\u4e5f\u6253\u65ad\u4e86\u4e8e\u627f\u73e0\u7684\u9ed8\u60f3\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00 \u9897\u77f3\u5b50\uff0c\u6b63\u597d\u843d\u5728\u5979\u7684\u8ddf\u524d\uff0c\u5c0f\u864e\u5b50\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u662f\u4eba\u662f\u9b3c\uff1f\u6211\u4f3c\u4e4e\u89c1\u5230\u4e00\u6761\u9ed1\u5f71\uff0c\u6643\u773c \u5c31\u4e0d\u89c1\u4e86\u3002\u201d\u8bdd\u58f0\u521a\u505c\uff0c\u53c8\u662f\u4e00\u9897\u77f3\u5b50\u843d\u5728\u4ed6\u4eec\u4e24\u4eba\u7684\u4e2d\u95f4\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u671d\u7740\u90a3\u77f3\u5b50\u98de\u6765\u7684\u65b9\u5411\uff0c\u98de\u8eab\u4fbf\u8ffd\uff0c\u4e8e\u627f\u73e0\u7684\u8f7b\u529f\u4f55\u7b49\u5feb\u6377\uff0c\u8ffd\u4e86\u4e00\u4f1a\uff0c\u4ecd\u7136\u4e0d \u89c1\u4eba\u8ff9\uff0c\u4e8e\u627f\u73e0\u5fc3\u4e2d\u6697\u6c14\uff1a\u201c\u6211\u4e3a\u51e4\u59d0\u8fd9\u6837\u82e6\u5fc3\u5bfb\u4f60\uff0c\u4f60\u5374\u6765\u8f83\u8003\u6211\u7684\u6b66\u529f\u3002\u201d\u811a\u6b65\u4e00\u505c\uff0c \u5ffd\u5730\u53c8\u662f\u55d6\u7684\u4e00\u58f0\uff0c\u98de\u6765\u4e86\u4e00\u9897\u77f3\u5b50\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u65bd\u5c55\u873b\u8712\u4e09\u63a0\u6c34\u7684\u4e0a\u4e0a\u8f7b\u529f\uff0c\u4e09\u8d77\u4e09\u4f0f\uff0c\u63a0\u51fa\u4e86\u5341\u6570\u4e08\u5730\uff0c\u9690\u9690\u89c1\u5230\u4e00\u6761\u9ed1\u5f71\uff0c \u521a\u6362\u4e00\u53e3\u6c14\uff0c\u518d\u65bd\u5c55\u8f7b\u529f\u63d0\u7eb5\u672f\u65f6\uff0c\u90a3\u9ed1\u5f71\u53c8\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u9ed1\u5f71\u65f6\u9690\u65f6\u73b0\uff0c\u4e8e\u627f\u73e0\u8ffd\u4e86\u4e00\u9635\uff0c\u5ffd\u89c1\u4e00\u5757\u7855\u5927\u65e0\u6bd4\u7684\u5706\u77f3\uff0c\u5149\u6ed1\u6e9c\u4eae\uff0c\u5728\u6708\u5149\u53cd\u5c04\u4e4b \u4e0b\uff0c\u5982\u660e\u955c\u4e00\u822c\uff0c\u77f3\u4e0a\u51ff\u6709\u201c\u70b9\u5c06\u53f0\u201d\u4e09\u4e2a\u5927\u5b57\uff0c\u4e8e\u627f\u73e0\u6014\u4e86\u4e00\u6014\uff0c\u604d\u7136\u5927\u609f\uff1a\u201c\u539f\u6765\u4ed6\u662f \u6545\u610f\u5f15\u6211\u6765\u6b64\u3002\u5bf9\u5566\uff0c\u4ed6\u4e0e\u5e08\u7236\u7ea6\u5b9a\u5728\u6b64\u4f1a\u9762\uff0c\u4ed6\u6015\u6211\u5e08\u7236\u6765\u65f6\u5bfb\u4e0d\u89c1\u4ed6\u3002\u201d\u4e8e\u662f\u6717\u58f0\u8bf4 \u9053\uff1a\u201c\u51cc\u4e91\u51e4\u4e4b\u53cb\u3001\u5f20\u4e39\u67ab\u4e4b\u5f92\uff0c\u4e8e\u627f\u73e0\u5230\u6b64\u62dc\u4f1a\u516b\u8fbe\u5c71\u4eba\u3002\u201d\u5979\u6050\u6015\u8fd9\u4eba\u4e07\u4e00\u4e0d\u662f\u970d\u5929 \u90fd\uff0c\u6240\u4ee5\u4e0d\u6562\u5f84\u547c\u540d\u5b57\uff0c\u4f46\u5374\u7279\u522b\u628a\u201c\u51cc\u4e91\u51e4\u201d\u4e09\u5b57\u5148\u884c\u8bf4\u51fa\u3002 \u3000\u3000\u201c\u70b9\u5c06\u53f0\u201d\u9644\u8fd1\u6811\u6728\u7a00\u758f\uff0c\u6708\u5149\u4e0b\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5374\u5e76\u6ca1\u6709\u4eba\u73b0\u51fa\u8eab\u6765\uff0c\u4e8e\u627f\u73e0\u7b49\u4e86\u4e00 \u9635\uff0c\u751a\u4e3a\u751f\u6c14\uff0c\u5f85\u60f3\u79bb\u5f00\uff0c\u5c0f\u864e\u5b50\u5374\u8fd8\u6ca1\u6709\u8d76\u5230\uff0c\u77f3\u53f0\u65c1\u8fb9\u53e6\u6709\u4e00\u5757\u77f3\u7891\uff0c\u8bf4\u660e\u8fd9\u4e2a\u53e4\u8ff9\u7684 \u6765\u5386\uff0c\u4e8e\u627f\u73e0\u62c2\u62ed\u6b8b\u7891\uff0c\u7ec6\u8bfb\u7891\u6587\uff0c\u59cb\u77e5\u8fd9\u662f\u5b8b\u671d\u5973\u6770\u7a46\u6842\u82f1\u66fe\u5728\u8fd9\u91cc\u70b9\u5c06\u7684\u77f3\u53f0\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5fc3\u9053\uff1a\u201c\u51cc\u59d0\u59d0\u7684\u6587\u624d\u6b66\u7565\uff0c\u53ef\u4e0e\u7a46\u6842\u82f1\u6bd4\u7f8e\uff0c\u4f46\u98ce\u4e91\u9645\u4f1a\uff0c\u4e24\u4eba\u7684\u9645\u9047\u5374\u53c8\u5927 \u5927\u4e0d\u540c\u4e86\u3002\u54ce\uff0c\u8fd9\u4eba\u662f\u4e0d\u662f\u970d\u5927\u90fd\uff1f\u201d\u80cc\u540e\u811a\u6b65\u58f0\u4e00\u54cd\uff0c\u4e8e\u627f\u73e0\u56de\u5934\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u5c0f\u864e \u5b50\uff0c\u53ea\u89c1\u4ed6\u8138\u4e0a\u4e00\u526f\u60ca\u8be7\u7684\u795e\u8272\uff0c\u6307\u7740\u77f3\u53f0\u8bf4\u9053\uff1a\u201c\u4e8e\u59d0\u59d0\uff0c\u8fd9\u4e2a\u4eba\u5c31\u662f\u4f60\u7684\u670b\u53cb\u5417\uff1f\u4f60\u4e3a \u4ec0\u4e48\u8fd8\u4e0d\u53eb\u9192\u4ed6\uff1f\u201d\u4e8e\u627f\u73e0\u8fd9\u65f6\u80cc\u5411\u77f3\u53f0\uff0c\u6025\u5fd9\u8f6c\u8eab\u770b\u65f6\uff0c\u53ea\u89c1\u77f3\u53f0\u4e0a\u679c\u7136\u7761\u6709\u4e00\u4e2a\u4eba\uff0c\u4e8e \u627f\u73e0\u81ea\u5e7c\u7ec3\u4e60\u6697\u5668\uff0c\u8033\u6735\u6700\u4e3a\u7075\u654f\uff0c\u7adf\u4e0d\u77e5\u8fd9\u4eba\u662f\u4ec0\u4e48\u65f6\u5019\u6765\u7684\uff0c\u8fd9\u4e00\u5413\u975e\u540c\u5c0f\u53ef\uff0c\u5446\u4e86\u4e00 \u4f1a\uff0c\u597d\u534a\u664c\u624d\u8bf4\u51fa\u8bdd\u9053\uff1a\u201c\u970d\uff0c\u970d\u2026\u2026\u516b\u8fbe\u5c71\u4eba\uff0c\u7ea6\u6211\u5e08\u7236\u7684\u662f\u4e0d\u662f\u4f60\uff1f\u201d\u8fd9\u4eba\u7528\u4e00\u4ef6\u84dd\u5e03 \u5927\u8902\uff0c\u8499\u5934\u5927\u7761\uff0c\u770b\u4e0d\u6e05\u4ed6\u7684\u9762\u8c8c\uff0c\u4e8e\u627f\u73e0\u4e0d\u6562\u65ad\u5b9a\u4ed6\u662f\u4e0d\u662f\u970d\u5929\u90fd\u3002 \u3000\u3000\u5c0f\u864e\u5b50\u8df3\u4e0a\u77f3\u53f0\uff0c\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u597d\u751f\u65e0\u793c\uff01\u201d\u4e00\u628a\u5c06\u4ed6\u7ffb\u4e86\u8f6c\u6765\uff0c\u63ed\u5f00\u90a3\u4ef6\u84dd\u5e03\u5927 \u8902\uff0c\u8fd9\u4e00\u4e0b\u66f4\u628a\u4e8e\u627f\u73e0\u5413\u5f97\u5446\u4e86\uff0c\u8fd9\u4eba\u7adf\u7136\u662f\u4e2a\u767d\u53d1\u82cd\u82cd\uff0c\u6709\u7740\u4e00\u4e2a\u9152\u7cdf\u9f3b\u7684\u7cdf\u8001\u5934\u5b50\uff0c\u9762 \u8c8c\u867d\u7136\u4e0d\u7b97\u592a\u4e11\u964b\uff0c\u4f46\u4e0e\u51cc\u4e91\u51e4\u6240\u63cf\u8ff0\u7684\u90a3\u4e2a\u5c11\u5e74\u82f1\u4fca\u7684\u970d\u5929\u90fd\uff0c\u7edd\u65e0\u4e1d\u6beb\u76f8\u7c7b\uff01\u8fd9\u8001\u5934\u513f \u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u9053\uff1a\u201c\u54ea\u91cc\u6765\u7684\u987d\u7ae5\uff0c\u4e3a\u4f55\u6270\u4eba\u6e05\u68a6\u3002\u201d\u5c0f\u864e\u5b50\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u90a3\u8001\u5934\u513f\u9053\uff1a\u201c\u4f60\u8981\u627e\u8c01\uff1f\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u516b\u8fbe\u5c71\u4eba\uff1f\u201d\u90a3\u8001\u5934\u513f\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60 \u6765\u627e\u6211\uff1f\u6211\u8001\u513f\u53ef\u4e0d\u8ba4\u8bc6\u4f60\u8fd9\u6837\u987d\u76ae\u7684\u91ce\u5b69\u5b50\u3002\u201d\u626f\u8fc7\u5927\u8902\u7eb3\u5934\u53c8\u7761\uff0c\u5c0f\u864e\u5b50\u518d\u53eb\uff0c\u4ed6\u7adf\u7136 \u547c\u547c\u5730\u6253\u8d77\u9f3e\u6765\uff0c\u5c0f\u864e\u5b50\u6012\u9053\uff1a\u201c\u504f\u53eb\u4f60\u7761\u4e0d\u5b89\u5fc3\uff01\u201d\u53cc\u6307\u4e00\u4f38\uff0c\u94b3\u90a3\u8001\u4eba\u7684\u9f3b\u5b50\uff0c\u5c0f\u864e\u5b50 \u5c0f\u65f6\u6700\u7231\u8fd9\u6837\u548c\u540c\u4f34\u5f00\u73a9\u7b11\uff0c\u8fd9\u8001\u4eba\u7684\u9152\u7cdf\u9f3b\u53c8\u7ea2\u53c8\u5927\uff0c\u5c0f\u864e\u5b50\u5fcd\u4e0d\u4f4f\u7ae5\u5fc3\u5927\u8d77\uff0c\u53cc\u6307\u94b3 \u4e0b\uff0c\u60f3\u8c61\u90a3\u8001\u4eba\u7684\u7a98\u6001\uff0c\u201c\u54c8\u201d\u7684\u4e00\u58f0\u5148\u7b11\u8d77\u6765\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u89c1\u8fd9\u6ed1\u7a3d\u6a21\u6837\uff0c\u4ea6\u81ea\u5fcd\u4fca\u4e0d\u7981\uff0c\u6b63\u60f3\u51fa\u58f0\u559d\u6b62\uff0c\u53ea\u89c1\u90a3\u8001\u4eba\u7565\u4e00\u4fa7\u8eab\uff0c\u5c0f\u864e\u5b50\u7adf \u7136\u94b3\u4e86\u4e2a\u7a7a\uff0c\u5c0f\u864e\u5b50\u8fd8\u4e0d\u670d\u6c14\uff0c\u89d1\u51c6\u90a3\u8001\u4eba\u7684\u5927\u7ea2\u9f3b\u5b50\uff0c\u53c8\u94b3\u4e0b\u53bb\uff0c\u5c0f\u864e\u5b50\u5e74\u7eaa\u867d\u5c0f\uff0c\u6b66\u529f \u751a\u9ad8\uff0c\u5728\u6c5f\u6e56\u4e0a\u5df2\u662f\u7f55\u9022\u654c\u624b\uff0c\u8fd9\u6b21\u51fa\u624b\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u6ee1\u4ee5\u4e3a\u5b9a\u80fd\u94b3\u4e2d\uff0c\u60f3\u4e0d\u5230\u6307\u5c16\u521a\u8981\u89e6 \u53ca\u9f3b\u6881\uff0c\u90a3\u8001\u4eba\u53c8\u662f\u7565\u4e00\u4fa7\u8eab\uff0c\u5c0f\u864e\u5b50\u6536\u52bf\u4e0d\u53ca\uff0c\u53cc\u6307\u4e00\u76f4\u6233\u5230\u77f3\u53f0\u4e4b\u4e0a\uff0c\u51e0\u4e4e\u8dcc\u4e86\u4e2a\u72d7\u5403 \u5c4e\uff0c\u4e8e\u627f\u73e0\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u8001\u4eba\u8eab\u6cd5\u7684\u602a\u5f02\uff0c\u7adf\u662f\u89c1\u6240\u672a\u89c1\uff0c\u6025\u5fd9\u7eb5\u4e0a\u77f3\u53f0\uff0c\u5ffd\u542c\u5f97\u90a3\u8001\u4eba\u65a5 \u9053\uff1a\u201c\u987d\u7ae5\u65e0\u793c\uff0c\u7ed9\u6211\u6253\u4ed6\u5c41\u80a1\uff01\u201d\u5c31\u5728\u4e8e\u627f\u73e0\u7eb5\u4e0a\u4e4b\u65f6\uff0c\u4e00\u6761\u4eba\u5f71\u500f\u5730\u4e5f\u4ece\u53f3\u8fb9\u7eb5\u4e0a\uff0c\u6bd4 \u4e8e\u627f\u73e0\u5148\u5230\u4e86\u4e00\u6b65\uff0c\u201c\u556a\u5566\u201d\u4e00\u58f0\uff0c\u4e00\u638c\u62cd\u4e2d\u4e86\u5c0f\u864e\u5b50\u7684\u5c41\u80a1\uff0c\u5c06\u4ed6\u6454\u4e0b\u77f3\u53f0\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5bf9\u5c0f\u864e\u5b50\u6700\u4e3a\u7231\u62a4\uff0c\u89c1\u8fd9\u4eba\u51fa\u624b\u5947\u5feb\uff0c\u4e0d\u77e5\u662f\u8f7b\u662f\u91cd\uff0c\u53ea\u6050\u4ed6\u4f24\u4e86\u5c0f\u864e\u5b50\uff0c\u4e0d\u6687 \u601d\u7d22\uff0c\u4fbf\u4e00\u5251\u5411\u4ed6\u523a\u53bb\uff0c\u5251\u5230\u4e4b\u65f6\uff0c\u5c0f\u864e\u5b50\u5df2\u88ab\u6454\u4e0b\u77f3\u53f0\uff0c\u90a3\u4eba\u53cd\u624b\u4e00\u8fce\uff0c\u624b\u4e0a\u62ff\u7684\u662f\u4e00\u6839 \u6811\u679d\uff0c\u4f7f\u7684\u7adf\u7136\u662f\u5200\u5251\u7684\u8def\u6570\uff0c\u4e8e\u627f\u73e0\u90a3\u4e00\u5251\u6ca1\u6709\u523a\u4e2d\u4ed6\uff0c\u53cd\u800c\u51e0\u4e4e\u7ed9\u4ed6\u7684\u6811\u679d\u523a\u4e2d\u624b\u8155\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u6025\u6025\u53d8\u62db\uff0c\u9752\u51a5\u5251\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u5de6\u4e00\u62db\u201c\u534e\u679d\u6625\u6696\u201d\uff0c\u53f3\u4e00\u62db\u201c\u5929\u5fc3\u6708\u5706\u201d\uff0c \u8fd9\u672c\u6765\u662f\u53cc\u5251\u5408\u58c1\u7684\u62db\u6570\uff0c\u5f20\u4e39\u67ab\u4ece\u4e24\u4eba\u5206\u4f7f\u7684\u5251\u672f\u5999\u609f\u51fa\u6765\u7684\uff0c\u5982\u4eca\u4e8e\u627f\u73e0\u4e00\u5251\u53cc\u5206\uff0c\u5de6 \u53f3\u5e76\u8fdb\uff0c\u5c31\u50cf\u4e24\u4eba\u5408\u4f7f\u90a3\u5957\u5947\u5999\u65e0\u6bd4\u7684\u767e\u53d8\u7384\u673a\u5251\u6cd5\uff0c\u90a3\u4eba\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u8d5e\u9053\uff1a\u201c\u5929\u4e0b\u7b2c \u4e00\u5251\u5ba2\u7684\u9ad8\u8db3\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff01\u201d\u6811\u679d\u4e00\u6311\uff0c\u4f3c\u622e\u4f3c\u523a\uff0c\u500f\u5730\u8df3\u51fa\u4e86\u5251\u5149\u5708\u5b50\uff0c\u4e8e\u627f\u73e0\u4e00 \u770b\uff0c\u53ea\u89c1\u6765\u4eba\u4e43\u662f\u4e2a\u6d53\u7709\u5927\u773c\u7684\u5c11\u5e74\uff0c\u867d\u7136\u8fdc\u4e0d\u53ca\u94c1\u955c\u5fc3\u7684\u4e30\u795e\u4fca\u79c0\uff0c\u4f46\u5374\u53e6\u6709\u4e00\u80a1\u8c6a\u8fc8\u4e4b \u6c14\uff0c\u4e8e\u627f\u73e0\u5fc3\u4e2d\u4e00\u52a8\uff0c\u77e5\u9053\u8fd9\u4e2a\u5c11\u5e74\u5b9a\u662f\u970d\u5929\u90fd\u65e0\u7591\u4e86\u3002 \u3000\u3000\u90a3\u8001\u5934\u513f\u5750\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u6708\u591c\u6bd4\u5251\uff0c\u5927\u662f\u96c5\u4e8b\uff0c\u6709\u5251\u672f\u53ef\u77a7\uff0c\u6211\u8001\u5934\u513f\u5012\u4e0d\u60f3\u7761 \u4e86\u3002\u201d\u4e8e\u627f\u73e0\u672c\u60f3\u4f4f\u624b\uff0c\u542c\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5fc3\u4e2d\u52a8\u5ff5\uff1a\u201c\u4e14\u770b\u8fd9\u970d\u5929\u90fd\u7684\u5251\u672f\u5982\u4f55\uff1f\u201d\u9752\u51a5\u5251 \u633d\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u53c8\u662f\u4e00\u62db\u523a\u51fa\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u4e5f\u6b63\u662f\u62b1\u7740\u540c\u6837\u7684\u5fc3\u601d\uff0c\u60f3\u770b\u770b\u4e8e\u627f\u73e0\u7684\u5251\u6cd5\uff0c\u4e24\u4eba\u90fd\u4e0d\u53eb\u7834\uff0c\u4fbf\u5728\u8fd9\u77f3\u53f0\u4e4b\u4e0a \u6bd4\u5212\u8d77\u6765\uff0c\u90a3\u5c11\u5e74\u867d\u7136\u4f7f\u7684\u4ec5\u662f\u4e00\u6839\u6811\u679d\uff0c\u6325\u52a8\u8d77\u6765\uff0c\u5374\u4e5f\u547c\u547c\u5e26\u98ce\uff0c\u52b2\u9053\u5341\u8db3\uff01\u4e8e\u627f\u73e0\u4e0d \u6562\u6020\u6162\uff0c\u5c06\u5e08\u95e8\u5251\u6cd5\u75be\u5c55\u5f00\u6765\uff0c\u4e00\u5251\u7d27\u4f3c\u4e00\u5251\uff0c\u9752\u51a5\u5251\u53c8\u662f\u4e00\u628a\u5b9d\u5251\uff0c\u65bd\u5c55\u5f00\u6765\uff0c\u6676\u8292\u56db \u5c04\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u5c31\u5c06\u90a3\u5c11\u5e74\u88f9\u5728\u5251\u5149\u4e4b\u4e2d\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u7acb\u5fc3\u8981\u524a\u65ad\u4ed6\u7684\u6811\u679d\uff0c\u5251\u5251\u7d27\u903c\uff0c\u90a3\u5c11\u5e74\u6e9c\u6ed1\u4e4b\u6781\uff0c\u62db\u6570\u7740\u7740\u4e0d\u540c\uff0c\u88ab\u903c\u5f97\u7d27 \u4e86\uff0c\u5251\u6cd5\u7a81\u7136\u4e00\u53d8\uff0c\u6811\u679d\u4e71\u98a4\uff0c\u6709\u5982\u94f6\u86c7\u75be\u7a9c\uff0c\u767b\u65f6\u56db\u9762\u516b\u65b9\u90fd\u662f\u90a3\u5c11\u5e74\u7684\u5f71\u5b50\uff0c\u5ffd\u800c\u662f\u6b66 \u5f53\u7684\u8fde\u73af\u593a\u547d\u5251\u6cd5\uff0c\u5ffd\u800c\u662f\u592a\u6781\u5341\u4e09\u5251\u52bf\uff0c\u5ffd\u800c\u662f\u5d06\u5cd2\u7684\u8ffd\u9b42\u5251\u672f\uff0c\u5ffd\u800c\u662f\u9752\u9633\u7684\u67d4\u4e91\u5251 \u672f\uff0c\u5ffd\u800c\u662f\u5929\u9f99\u7684\u65cb\u98ce\u5251\u6cd5\uff0c\u5c31\u50cf\u6709\u6570\u5341\u540d\u9ad8\u624b\uff0c\u540c\u4f7f\u672c\u95e8\u7edd\u5b66\uff0c\u548c\u5979\u52a8\u624b\u8fc7\u62db\uff01 \u3000\u3000\u4e8e\u627f\u73e0\u6c89\u4f4f\u4e86\u6c14\uff0c\u5c06\u9752\u51a5\u5b9d\u5251\u821e\u5f97\u970d\u970d\u751f\u98ce\uff0c\u89c1\u62db\u62c6\u62db\uff0c\u89c1\u5f0f\u7834\u5f0f\uff0c\u5b88\u5b9a\u5fc3\u795e\uff0c\u4e0d\u4e3a\u6240 \u52a8\uff0c\u90a3\u5c11\u5e74\u7684\u5251\u6cd5\u867d\u7136\u5947\u8be1\u767e\u51fa\uff0c\u5374\u662f\u7834\u4e0d\u4e86\u5979\uff0c\u4f46\u4e8e\u627f\u73e0\u8d39\u5c3d\u5fc3\u795e\uff0c\u5374\u4e5f\u524a\u4e0d\u65ad\u4ed6\u624b\u4e2d\u7684 \u6811\u679d\u3002 \u3000\u3000\u6218\u5230\u5206\u9645\uff0c\u864e\u5b50\u7a81\u7136\u5c06\u4e24\u5757\u77f3\u5934\u63b7\u4e0a\u53f0\u6765\uff0c\u5206\u6253\u8001\u5934\u548c\u5c11\u5e74\uff0c\u8001\u5934\u513f\u54c8\u54c8\u4e00\u7b11\uff0c\u4f38\u6307\u4e00 \u5f39\uff0c\u5c06\u77f3\u5757\u5f39\u98de\uff0c\u6070\u6070\u78b0\u7740\u4e86\u98de\u5411\u5c11\u5e74\u7684\u90a3\u5757\u77f3\u5934\uff0c\u4e24\u5757\u77f3\u5934\u4e00\u9f50\u98de\u5760\uff0c\u4e8e\u627f\u73e0\u548c\u90a3\u5c11\u5e74\u6b63 \u81ea\u6597\u5f97\u7d27\u5f20\uff0c\u4e92\u76f8\u62a2\u653b\uff0c\u90a3\u4e24\u5757\u77f3\u5934\u4ece\u4ed6\u4eec\u7684\u9762\u524d\u63a0\u8fc7\uff0c\u5404\u81ea\u4e00\u95ea\uff0c\u4e24\u4eba\u53c8\u90fd\u662f\u62b1\u7740\u540c\u4e00\u5fc3 \u601d\uff0c\u89d1\u51c6\u4e86\u5bf9\u65b9\u6240\u95ea\u907f\u7684\u65b9\u4f4d\u8fdb\u5251\uff0c\u4e8e\u627f\u73e0\u4e00\u5251\u523a\u5230\u4e86\u5c11\u5e74\u7684\u80a9\u80db\u9aa8\uff0c\u5c11\u5e74\u7684\u6811\u679d\u4e5f\u70b9\u5230\u4e86 \u4e8e\u627f\u73e0\u7684\u624b\u8155\uff01 \u3000\u3000\u7a81\u7136\u95f4\u542c\u5f97\u4e24\u4e2a\u58f0\u97f3\u540c\u65f6\u53eb\u9053\uff1a\u201c\u591f\u4e86\uff01\u591f\u4e86\uff01\u201d\u90a3\u5c11\u5e74\u548c\u4e8e\u627f\u73e0\u90fd\u89c9\u5f97\u624b\u8155\u4e00\u9ebb\uff0c\u5b9d \u5251\u548c\u6811\u679d\u90fd\u7ed9\u6765\u4eba\u5288\u624b\u593a\u53bb\uff0c\u5b9a\u775b\u770b\u65f6\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u548c\u90a3\u8001\u5934\u624b\u643a\u7740\u624b\uff0c\u4e00\u9f50\u98de\u4e0b\u77f3\u53f0\uff0c\u4e24 \u4eba\u7a7a\u7740\u7684\u90a3\u53ea\u624b\uff0c\u5f20\u4e39\u67ab\u6267\u7684\u662f\u4e8e\u627f\u73e0\u7684\u9752\u51a5\u5251\uff0c\u90a3\u8001\u4eba\u6267\u7684\u5219\u662f\u5c11\u5e74\u6240\u4f7f\u7684\u67f3\u679d\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88\u53ef\u662f\u6614\u5e74\u5a01\u9707\u6b66\u6797\u7684\u516b\u81c2\u54ea\u53f1\u5468\u8c37\u9690\u5417\uff1f\u201d\u90a3\u8001\u5934\u513f\u9053\uff1a \u201c\u4e0d\u6562\uff0c\u4e0d\u6562\u3002\u597d\u6c49\u4e0d\u63d0\u5f53\u5e74\u52c7\uff0c\u6211\u800c\u4eca\u9690\u8ff9\u8352\u5c71\uff0c\u5df2\u81ea\u7518\u505a\u516b\u8fbe\u5c71\u4eba\u4e86\u3002\u201d\u4e91\u91cd\u548c\u4e91\u857e\u4e5f \u8ddf\u7740\u4e0a\u4e86\u70b9\u5c06\u53f0\uff0c\u5411\u90a3\u8001\u4eba\u65bd\u793c\u3002 \u3000\u3000\u539f\u6765\u8fd9\u8001\u5934\u513f\u4e43\u662f\u548c\u7384\u673a\u9038\u571f\u4e00\u8f88\u7684\u4eba\u7269\uff0c\u5c11\u5e74\u4e4b\u65f6\u66fe\u529b\u8d25\u5341\u516b\u540d\u8499\u53e4\u6b66\u58eb\uff0c\u4ee5\u52c7\u6562\u77eb \u6377\u8457\u79f0\uff0c\u5916\u53f7\u516b\u81c2\u54ea\u53f1\uff0c\u4ed6\u548c\u970d\u884c\u4ef2\u4e43\u662f\u516b\u62dc\u4e4b\u4ea4\uff0c\u970d\u884c\u4ef2\u540e\u6765\u9690\u5c45\u585e\u5916\uff0c\u4ed6\u4e5f\u9690\u5c45\u6df1\u5c71\uff0c \u6b66\u6797\u540e\u8f88\uff0c\u8fde\u4ed6\u4eec\u7684\u540d\u5b57\u4e5f\u5f88\u5c11\u77e5\u9053\u7684\u4e86\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u53d9\u8fc7\u5e08\u95e8\u6e0a\u6e90\uff0c\u53c8\u65bd\u793c\u9053\uff1a\u201c\u8001\u524d\u8f88\u4eca\u665a\u8fd9\u4efd\u539a\u793c\uff0c\u4e39\u67ab\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d\u5468\u8c37\u9690\u54c8 \u54c8\u7b11\u9053\uff1a\u201c\u6211\u8001\u5934\u513f\u7d20\u6765\u4e0d\u548c\u4eba\u5ba2\u6c14\uff0c\u6211\u9001\u4f60\u7684\u793c\u7269\u5b9e\u662f\u6307\u671b\u4f60\u62a5\u7b54\u7684\u5440\uff01\u201d\u5f20\u4e39\u67ab\u6014\u4e86\u4e00 \u6014\uff0c\u773c\u5149\u4e00\u77a5\uff0c\u4f46\u89c1\u90a3\u5c11\u5e74\u6b63\u6ce8\u89c6\u7740\u4ed6\u624b\u4e2d\u7684\u5b9d\u5251\uff0c\u5f20\u4e39\u67ab\u4f55\u7b49\u806a\u660e\uff0c\u4e00\u731c\u4fbf\u7740\uff0c\u5fae\u5fae\u7b11 \u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u2014\u2014\u201d\u5468\u8c37\u9690\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6211\u4ea1\u6545\u7684\u628a\u5f1f\u970d\u884c\u4ef2\u7684\u513f\u5b50\uff0c\u4ed6\u540d\u5b57\u53eb\u505a\u970d\u5929 \u90fd\uff01\u201d \u3000\u3000\u679c\u7136\u662f\u970d\u5929\u90fd\uff01\u4e8e\u627f\u73e0\u867d\u7136\u65e9\u5df2\u6599\u5230\uff0c\u4f46\u5982\u4eca\u7684\u786e\u8bc1\u5b9e\u4e86\u662f\u4ed6\uff0c\u5fc3\u4e2d\u4ecd\u4e0d\u7981\u611f\u5230\u6781\u5927\u7684 \u559c\u60a6\uff0c\u60f3\u9053\uff1a\u201c\u51cc\u59d0\u59d0\u5f97\u914d\u6b64\u4eba\uff0c\u53ef\u4ee5\u7ec8\u8eab\u65e0\u61be\u4e86\u3002\u201d\u968f\u5373\u60f3\u5230\u4e86\u51cc\u4e91\u51e4\u548c\u53f6\u6210\u6797\u5c1a\u5728\u5371 \u57ce\uff0c\u751f\u6b7b\u672a\u535c\uff0c\u53c8\u60f3\u5230\u4ed6\u4eec\u76f8\u5904\u65e5\u4e45\uff0c\u51cc\u4e91\u51e4\u65e2\u4e0d\u77e5\u970d\u5929\u90fd\u5c1a\u5728\u4eba\u95f4\uff0c\u53f6\u6210\u6797\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1 \u5bf9\u4ed6\u6709\u610f\uff0c\u201c\u5440\uff0c\u6211\u4f1a\u4e0d\u4f1a\u5f04\u5de7\u6210\u62d9\uff1f\u5f53\u65f6\u6709\u610f\u76f8\u8ba9\uff0c\u53cd\u5f15\u8d77\u96be\u89e3\u7684\u7ea0\u7eb7\uff1f\u201d\u610f\u5ff5\u53ca\u6b64\uff0c\u82b3 \u5fc3\u64a9\u4e71\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u5e08\u7236\u7b11\u9053\uff1a\u201c\u597d\u5427\uff0c\u53ea\u6015\u6211\u7684\u7b54\u793c\u592a\u8584\uff0c\u96be\u916c\u76db\u610f\u3002\u201d\u5012\u6301\u5251\u67c4\uff0c\u5c06\u90a3\u67c4\u9752\u51a5\u5b9d \u5251\u9012\u5230\u4e86\u970d\u5929\u90fd\u7684\u624b\u4e2d\uff0c\u4e8e\u627f\u73e0\u4e00\u6123\uff0c\u5fc3\u9053\uff1a\u201c\u987a\u7236\u600e\u628a\u5e08\u7956\u7684\u4f20\u5bb6\u5b9d\u5251\u9001\u7ed9\u522b\u4eba\uff1f\u201d\u970d\u5929 \u90fd\u4e5f\u8bef\u4f1a\u4e86\u5f20\u4e39\u67ab\u7684\u610f\u601d\uff0c\u8138\u4e0a\u4e00\u7ea2\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u5f20\u4e39\u67ab\u5374\u4ece\u5468\u8c37\u9690\u624b\u4e2d\u63a5\u8fc7\u4e86\u90a3\u679d\u67f3\u679d\uff0c \u5fae\u5fae\u7b11\u9053\uff1a\u201c\u627f\u73e0\uff0c\u4f60\u518d\u7559\u795e\u770b\u970d\u4e16\u5144\u7684\u5251\u6cd5\u5427\u3002\u201d \u3000\u3000\u970d\u5929\u90fd\u8fd9\u624d\u559c\u4e0a\u7709\u68a2\uff0c\u8981\u77e5\u4ee5\u5f20\u4e39\u67ab\u7684\u8eab\u4efd\uff0c\u65ad\u65e0\u4e0e\u4ed6\u6bd4\u5251\u4e4b\u7406\uff0c\u6240\u4ee5\u5982\u6b64\uff0c\u5b9e\u662f\u6697\u5bd3 \u613f\u6307\u70b9\u4ed6\u5251\u6cd5\u7684\u610f\u601d\u3002\u970d\u5929\u90fd\u7236\u5b50\u4e24\u4ee3\uff0c\u82e6\u5fc3\u641c\u96c6\u5929\u4e0b\u5251\u8c31\uff0c\u7acb\u5fd7\u521b\u7acb\u5929\u5c71\u6d3e\u7684\u5251\u6cd5\uff0c\u82e6\u4e8e \u672a\u5f97\u9ad8\u624b\u6307\u70b9\uff0c\u800c\u8981\u5f00\u521b\u4e00\u6d3e\uff0c\u5f80\u5f80\u8d39\u6570\u767e\u5e74\u7684\u5fc3\u8840\uff0c\u7236\u6b7b\u5b50\u7ee7\uff0c\u5e08\u6b7b\u5f92\u627f\uff0c\u4e5f\u672a\u5fc5\u80fd\u591f\u505a \u5230\u3002\u5f20\u4e39\u67ab\u82e5\u80af\u4f20\u4ed6\u5fc3\u6cd5\uff0c\u8fd9\u4efd\u539a\u793c\u81ea\u662f\u6bd4\u9001\u4e00\u628a\u5b9d\u5251\u66f4\u96be\u5f97\u6570\u5343\u4e07\u500d\uff01 \u3000\u3000\u516b\u8fbe\u5c71\u4eba\u8fd9\u6b21\u7684\u82e6\u5fc3\u7b79\u5212\uff0c\u6253\u542c\u5f20\u4e39\u67ab\u7684\u884c\u8e2a\uff0c\u7ed9\u4ed6\u9001\u793c\uff0c\u6551\u4ed6\u51fa\u5bab\uff0c\u5f15\u4ed6\u5230\u70b9\u5c06\u53f0\uff0c \u76ee\u7684\u4e5f\u65e0\u975e\u662f\u60f3\u9020\u5c31\u970d\u5929\u90fd\uff0c\u5982\u4eca\u89c1\u5f20\u4e39\u67ab\u6168\u7136\u63d0\u51fa\uff0c\u8981\u4e0e\u970d\u5929\u90fd\u6bd4\u5251\uff0c\u5fc3\u4e2d\u7684\u559c\u60a6\uff0c\u4e0d\u5728 \u970d\u5929\u90fd\u4e4b\u4e0b\uff0c\u60f3\u9053\uff1a\u201c\u82e5\u662f\u970d\u5929\u90fd\u80fd\u6210\u4e3a\u4e00\u4ee3\u540d\u5bb6\uff0c\u6211\u4e49\u5f1f\u5728\u6cc9\u4e0b\u4e5f\u53ef\u7791\u76ee\u3002\u201d\u5f53\u5373\u8bf4\u9053\uff1a \u201c\u5929\u90fd\uff0c\u5f20\u5927\u4fa0\u80af\u6307\u6559\u4f60\uff0c\u4f60\u8fd8\u4e0d\u8d76\u5feb\u4eae\u62db\u3002\u201d \u3000\u3000\u970d\u5929\u90fd\u9053\u58f0\u201c\u6055\u7f6a\u201d\uff0c\u4e00\u62db\u96f7\u7535\u4ea4\u8f70\uff0c\u5251\u52bf\u4e00\u53d1\uff0c\u4fbf\u4f3c\u5954\u96f7\u9a87\u7535\uff0c\u5411\u5f20\u4e39\u67ab\u6740\u5230\uff01 \u3000\u3000\u8fd9\u4e00\u62db\u662f\u6b66\u5f53\u6d3e\u8fde\u73af\u593a\u547d\u5251\u6cd5\u4e2d\u6700\u521a\u731b\u7684\u4e00\u62db\uff0c\u4e24\u4eba\u8ddd\u79bb\u53c8\u8fd1\uff0c\u8fd9\u4e00\u5251\u5288\u4e0b\uff0c\u5251\u5149\u56db \u5c55\uff0c\u628a\u5bf9\u624b\u5b8c\u5168\u5c01\u95ed\u5728\u5251\u5708\u4e4b\u5185\uff0c\u4e8e\u627f\u73e0\u6b63\u81ea\u601d\u7d22\u8be5\u5982\u4f55\u62b5\u5fa1\uff0c\u5fc3\u5ff5\u65b9\u52a8\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u6811\u679d \u4e00\u6296\uff0c\u4e00\u9053\u9752\u5149\uff0c\u500f\u5730\u817e\u7a7a\u98de\u8d77\uff0c\u539f\u6765\u662f\u970d\u5929\u90fd\u6240\u6301\u7684\u9752\u51a5\u5b9d\u5251\u7adf\u7136\u8131\u624b\u98de\u51fa\uff0c\u5c0f\u864e\u5b50\u62cd\u638c \u53eb\u9053\uff1a\u201c\u5999\u554a\uff0c\u5999\u554a\uff01\u201d\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u5999\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u4f60\u8bf4\u8bf4\u770b\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u5999\u5c31\u662f \u5999\uff0c\u6709\u4ec0\u4e48\u53ef\u8bf4\u7684\u3002\u8981\u662f\u4e0d\u5999\uff0c\u4e3a\u4ec0\u4e48\u53ea\u4e00\u62db\u5c31\u6559\u4ed6\u7684\u5175\u5668\u6254\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u7f9e\u4ed6\u9053\uff1a\u201c\u4f60\u53ea \u61c2\u5f97\u778e\u56b7\uff0c\u4e0d\u6015\u7ed9\u4eba\u7b11\u6389\u4e86\u5927\u7259\uff01\u201d \u3000\u3000\u970d\u5929\u90fd\u6ee1\u9762\u901a\u7ea2\uff0c\u62fe\u56de\u5b9d\u5251\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u4e0d\u7b97\uff0c\u518d\u6765\uff0c\u518d\u6765\u3002\u201d\u5c0f\u864e \u5b50\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u7b97\uff1f\u201d\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u5251\u7684\u786e\u662f\u6beb\u65e0\u5999\u5904\u53ef\u8a00\uff0c\u5c0f\u864e\u5b50\u4f60\u4e0d\u61c2\u88c5\u61c2\uff0c \u4ee5\u540e\u5207\u5207\u4e0d\u53ef\u3002\u201d\u970d\u5929\u90fd\u9053\uff1a\u201c\u5f20\u5927\u4fa0\uff0c\u6211\u8fd9\u4e00\u5251\u7684\u62db\u5f0f\u662f\u4e0d\u662f\u7528\u5f97\u8001\u4e86\uff1f\u201d\u5f20\u4e39\u67ab\u70b9\u70b9\u5934 \u9053\uff1a\u201c\u5c0f\u864e\u5b50\u4f60\u542c\uff0c\u8fd9\u624d\u662f\u884c\u5bb6\u7684\u8bf4\u8bdd\u3002\u8fd9\u4e00\u62db\u6211\u4e0d\u662f\u4ee5\u5251\u672f\u53d6\u80dc\uff0c\u800c\u662f\u4ee5\u5185\u529b\u9707\u98de\u4ed6\u7684\u5b9d \u5251\u7684\u3002\u4e0d\u8fc7\uff0c\u867d\u7136\u5982\u6b64\uff0c\u4ed6\u8fd9\u4e00\u62db\u96f7\u7535\u4ea4\u8f70\uff0c\u4e5f\u7528\u5f97\u4e0d\u5927\u9002\u5f53\u3002\u8981\u77e5\u8fd9\u62db\u96f7\u7535\u4ea4\u8f70\uff0c\u5a01\u731b\u65e0 \u4fe6\uff0c\u7528\u4e4b\u5bf9\u4ed8\u529f\u529b\u6089\u654c\u7684\u5bf9\u624b\u5c1a\u53ef\uff0c\u82e5\u78b0\u4e0a\u4e86\u529f\u529b\u6bd4\u81ea\u5df1\u9ad8\u7684\u5bf9\u624b\uff0c\u4e00\u4e2a\u53cd\u51fb\uff0c\u529b\u5f3a\u5219\u80dc\uff0c \u8fd9\u5176\u95f4\u5c31\u6beb\u65e0\u53d6\u5de7\u7684\u4f59\u5730\u4e86\u3002\u201d\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u522b\u4eba\u7684\u529f\u529b\u6bd4\u4f60\u9ad8\uff0c\u8fd8\u6709\u53ef\u4ee5\u53d6\u5de7\u7684\u4e48\uff1f\u201d\u5f20 \u4e39\u67ab\u9053\uff1a\u201c\u6b66\u5b66\u4e4b\u9053\uff0c\u529f\u529b\u4e0e\u62db\u6570\u5e76\u91cd\uff0c\u5251\u672f\u7ec3\u5230\u901a\u7384\u4e4b\u5883\uff0c\u53ef\u4ee5\u501f\u654c\u4e4b\u529b\u4ee5\u4e3a\u5df1\u7528\uff0c\u53ef\u4ee5 \u56e0\u654c\u4e4b\u52bf\u800c\u7834\u654c\u950b\uff0c\u8fd9\u4e24\u53e5\u8bdd\uff0c\u4efb\u4f55\u5251\u8bc0\u4e0a\u90fd\u5199\u6709\u7684\uff0c\u6211\u53ea\u9053\u9ed1\u767d\u6469\u8bc3\u5df2\u6559\u8fc7\u4f60\u4e86\u3002\u201d\u5c0f\u864e \u5b50\u9053\uff1a\u201c\u6559\u662f\u6559\u8fc7\u4e86\uff0c\u53ea\u6211\u4e0d\u61c2\u3002\u201d \u3000\u3000\u5f20\u4e39\u67ab\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u970d\u4e16\u5144\uff0c\u4f60\u518d\u6765\u7528\u8fd9\u4e00\u62db\u3002\u5c0f\u864e\u5b50\uff0c\u4f60\u77a7\u6e05\u695a\u4e86\u3002\u201d\u970d\u5929\u90fd\u4f9d \u8a00\u53c8\u662f\u4e00\u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\uff0c\u53ea\u89c1\u5f20\u4e39\u67ab\u6811\u679d\u4e00\u5f15\uff0c\u8f7b\u63cf\u6de1\u5199\u5730\u5c06\u4ed6\u7684\u5251\u52bf\u4e00\u4e3e\u5378\u5f00\uff0c\u53cd\u624b\u5c31 \u70b9\u4ed6\u7684\u624b\u8155\uff0c\u65f6\u95f4\u62ff\u634f\u5f97\u6070\u5230\u597d\u5904\uff0c\u624b\u6cd5\u4e4b\u5de7\uff0c\u5251\u672f\u4e4b\u5999\uff0c\u53ea\u53ef\u610f\u4f1a\uff0c\u96be\u4ee5\u8a00\u4f20\u3002 \u3000\u3000\u970d\u5929\u90fd\u9000\u540e\u4e00\u6b65\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u5251\u950b\u6ef4\u6e9c\u6e9c\u5730\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u624b\u8155\u5c45\u7136\u6ca1\u7ed9\u70b9\u4e2d\uff0c\u5f20\u4e39\u67ab \u8d5e\u9053\uff1a\u201c\u597d\uff0c\u4e3e\u4e00\u53cd\u4e09\uff0c\u53ef\u4ee5\u5b66\u5f97\u4e0a\u4e58\u5251\u6cd5\u4e86\u3002\u201d\u539f\u6765\u970d\u5929\u90fd\u8fd9\u4e00\u62db\u7559\u4e86\u540e\u52b2\uff0c\u6240\u4ee5\u4e00\u51fb\u4e0d \u4e2d\uff0c\u4fbf\u7acb\u523b\u53ef\u4ee5\u53d8\u62db\uff0c\u867d\u7136\u88ab\u5f20\u4e39\u67ab\u62a2\u4e86\u5148\u624b\uff0c\u5374\u5e76\u672a\u5b8c\u5168\u53d7\u5236\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u6709\u5fc3\u770b\u4ed6\u7684\u5251\u6cd5\uff0c\u6240\u4f7f\u7684\u5251\u62db\u70b9\u5230\u4e3a\u6b62\uff0c\u5e76\u4e0d\u8fc7\u4efd\u8fdb\u903c\u3002\u53ea\u89c1\u7247\u523b\u4e4b\u95f4\uff0c\u970d\u5929\u90fd \u5df2\u8fde\u6362\u4e86\u5341\u51e0\u79cd\u5251\u6cd5\uff0c\u6218\u5230\u9163\u5904\uff0c\u970d\u65e0\u90fd\u7684\u5251\u62db\u8d8a\u5c55\u8d8a\u5feb\uff0c\u5c31\u50cf\u521a\u624d\u5bf9\u4ed8\u4e8e\u627f\u73e0\u4e00\u6837\uff0c\u4fe8\u5982 \u6709\u5341\u6570\u540d\u5251\u672f\u9ad8\u624b\uff0c\u540c\u65f6\u4f7f\u51fa\u672c\u95e8\u7edd\u5b66\uff0c\u5411\u5f20\u4e39\u67ab\u56f4\u653b\uff0c\u628a\u5c0f\u864e\u5b50\u770b\u5f97\u773c\u82b1\u64a9\u4e71\uff0c\u53c8\u820d\u4e0d\u5f97 \u4e0d\u770b\uff0c\u518d\u8fc7\u7247\u523b\uff0c\u53ea\u542c\u5f97\u6709\u4eba\u5c16\u53eb\u4e00\u58f0\uff0c\u8dcc\u5012\u5730\u4e0a\uff0c\u539f\u6765\u662f\u5c0f\u864e\u5b50\u770b\u5f97\u5165\u8ff7\uff0c\u4f46\u89c9\u8eab\u5b50\u4e5f\u597d \u4f3c\u8ddf\u7740\u4ed6\u4eec\u65cb\u8f6c\u4e00\u6837\uff0c\u4ed6\u5b9a\u529b\u5c1a\u6d45\uff0c\u5982\u4f55\u7981\u53d7\u5f97\u4f4f\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u5657\u55e4\u4e00\u7b11\uff0c\u628a\u5c0f\u864e\u5b50\u6276\u4e86\u8d77\u6765\uff0c\u638f\u51fa\u4e00\u6761\u4e1d\u5e15\uff0c\u7f1a\u4f4f\u4e86\u4ed6\u7684\u773c\u775b\u3002\u518d\u770b\u6597\u573a\uff0c\u4f46 \u89c1\u970d\u5929\u90fd\u5251\u52bf\u6709\u5982\u72c2\u98ce\u9aa4\u96e8\uff0c\u50ac\u903c\u5f97\u66f4\u7d27\u4e86\u3002 \u3000\u3000\u4f46\u89c1\u5f20\u4e39\u67ab\u624b\u6267\u6811\u679d\uff0c\u987a\u7740\u5251\u98ce\uff0c\u5de6\u53f3\u6447\u6643\uff0c\u9aa4\u773c\u671b\u53bb\uff0c\u7ade\u4f3c\u8f7b\u98d8\u98d8\u7684\u6728\u7247\u4e00\u6837\uff0c\u8d34\u5728 \u970d\u5929\u90fd\u7684\u9752\u51a5\u5251\u4e0a\uff0c\u4e4d\u89c1\u970d\u5929\u90fd\u7684\u5b9d\u5251\u7eb5\u6a2a\u6325\u821e\uff0c\u5374\u603b\u65e0\u6cd5\u7528\u529b\u524a\u65ad\u4ed6\u7684\u6811\u679d\u3002\u82e5\u662f\u970d\u5929\u90fd \u7684\u653b\u52bf\u7a0d\u4e00\u7f13\u6162\uff0c\u90a3\u6839\u6811\u679d\u53c8\u500f\u5730\u7a7f\u8fc7\u5251\u5708\uff0c\u523a\u4e86\u8fdb\u6765\u3002\u7aef\u7684\u662f\u9759\u5982\u5904\u5b50\uff0c\u52a8\u7740\u8131\u5154\uff0c\u8f7b\u7075 \u7fd4\u52a8\uff0c\u53d8\u5316\u65e0\u65b9\uff01\u4e8e\u627f\u73e0\u770b\u5e08\u7236\u6240\u4f7f\u7684\u5251\u6cd5\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u81ea\u5df1\u5b66\u8fc7\u7684\uff0c\u4f46\u5e94\u4ed8\u970d\u5929\u90fd\u90a3\u6837\u590d \u6742\u591a\u53d8\u7684\u5251\u6cd5\uff0c\u5374\u53c8\u662f\u6bcf\u4e00\u62db\u90fd\u51fa\u4e4e\u81ea\u5df1\u610f\u6599\u4e4b\u5916\u3002\u6597\u4e86\u4e00\u767e\u591a\u62db\uff0c\u5f20\u4e39\u67ab\u5356\u4e86\u4e00\u4e2a\u7834\u7efd\uff0c \u6545\u610f\u8ba9\u970d\u5929\u90fd\u653b\u8fdb\uff0c\u970d\u5929\u90fd\u4e5f\u6781\u6e9c\u6ed1\uff0c\u9752\u51a5\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u5212\u4e86\u534a\u9053\u5706\u5f27\uff0c\u500f\u5730\u5411\u5de6\u4e00\u6487\uff0c\u5411 \u53f3\u4e00\u6311\uff0c\u4e00\u62db\u56db\u5f0f\uff0c\u4e00\u6c14\u5475\u6210\uff0c\u7adf\u662f\u5728\u77ac\u606f\u4e4b\u95f4\uff0c\u6478\u8fde\u4f7f\u51fa\u4e86\u6b66\u5f53\u5c11\u6797\u6606\u4ed1\u5d06\u5cd2\u56db\u79cd\u5251\u6cd5\u3002 \u4e8e\u627f\u73e0\u6b63\u81ea\u601d\u7d22\u8be5\u7528\u54ea\u4e00\u62db\u5316\u89e3\uff0c\u5fc3\u5934\u4e0a\u521a\u521a\u95ea\u8fc7\u4e24\u62db\u590d\u6742\u7684\u62db\u5f0f\uff0c\u4f46\u89c1\u5e08\u7236\u6811\u679d\u4e00\u98a4\uff0c\u5530 \u7684\u4e00\u58f0\uff0c\u5df2\u523a\u4e2d\u4e86\u970d\u5929\u90fd\u7684\u624b\u8155\uff0c\u7528\u7684\u7adf\u662f\u4e00\u62db\u6781\u7b80\u5355\u7684\u5251\u5f0f\u201c\u767d\u8679\u8d2f\u65e5\uff01\u201d \u3000\u3000\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u970d\u5929\u90fd\u7684\u9752\u51a5\u5b9d\u5251\u518d\u5ea6\u8131\u624b\u98de\u51fa\uff0c\u8dcc\u843d\u77f3\u53f0\u3002\u4e8e\u627f\u73e0\u62fe\u8d77\u5b9d\u5251\uff0c\u60c5\u4e0d\u81ea \u7981\u8fde\u8fde\u58f0\u53eb\u597d\uff0c\u5c0f\u864e\u5b50\u6025\u6025\u626f\u5f00\u8499\u773c\u7684\u4e1d\u5dfe\uff0c\u4f46\u89c1\u5f20\u4e39\u67ab\u5df2\u548c\u970d\u5929\u90fd\u7f62\u624b\u6b62\u6597\uff0c\u6b63\u5728\u90a3\u91cc\u7528 \u6811\u679d\u6bd4\u5212\uff0c\u8bb2\u89e3\u5251\u6cd5\u4e86\u3002\u5c0f\u864e\u5b50\u6c14\u5f97\u987f\u8db3\uff0c\u57cb\u6028\u4e8e\u627f\u73e0\u6ca1\u6709\u53ca\u65f6\u7ed9\u4ed6\u89e3\u5f00\u4e1d\u4e2d\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u8bf4\u9053\uff1a\u201c\u4f60\u6240\u5b66\u7684\u5341\u4e09\u5bb6\u5251\u6cd5\uff0c\u90fd\u5df2\u719f\u6781\u5982\u6d41\uff0c\u53ef\u4ee5\u968f\u610f\u8fd0\u7528\u4e86\u3002\u53ef\u60dc\u8fd8 \u6ca1\u6709\u878d\u4f1a\u8d2f\u901a\uff0c\u5c06\u5341\u4e09\u5bb6\u5251\u6cd5\u7684\u7cbe\u534e\u63c9\u5408\u8d77\u6765\uff0c\u81ea\u6210\u65b0\u6d3e\uff0c\u4e0d\u8fc7\uff0c\u6709\u4e86\u4f60\u8fd9\u6837\u7684\u57fa\u7840\uff0c\u82e5\u518d \u9886\u609f\u4e0a\u4e58\u5251\u8bc0\uff0c\u4e00\u7406\u901a\uff0c\u767e\u7406\u878d\uff0c\u518d\u82e6\u5fc3\u94bb\u7814\u4e09\u4e94\u5341\u5e74\uff0c\u4e0d\u96be\u521b\u4e00\u5929\u4e0b\u65e0\u654c\u7684\u5251\u6cd5\uff0c\u4e3a\u6b66\u5b66 \u653e\u4e00\u5f02\u5f69\u3002\u201d\u970d\u5929\u90fd\u5927\u559c\uff0c\u4fbf\u6b32\u62dc\u5e08\uff0c\u5f20\u4e39\u67ab\u963b\u6b62\u4e86\u4ed6\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u662f\u4e3a\u4e86\u5ba2\u6c14\uff0c \u81ea\u521b\u4e00\u6d3e\uff0c\u8270\u5de8\u4e4b\u6781\u3002\u4f60\u5bf9\u5404\u5bb6\u5404\u6d3e\u7684\u5251\u6cd5\u7684\u94bb\u7814\uff0c\u5df2\u6709\u5fc3\u5f97\uff0c\u5b9e\u662f\u80dc\u6211\u591a\u591a\u3002\u6240\u6b20\u7f3a\u7684\u4e0d \u8fc7\u4e0a\u4e58\u6b66\u529f\u7684\u8bc0\u7a8d\uff0c\u4e0e\u6c34\u78e8\u7684\u529f\u592b\u800c\u5df2\uff0c\u8bc0\u7a8d\u65b9\u9762\uff0c\u6211\u53ef\u4ee5\u4e0e\u4f60\u4e92\u76f8\u5207\u78cb\uff0c\u4f59\u4e0b\u7684\u529f\u592b\uff0c\u8fd8 \u5f97\u4f60\u81ea\u5df1\u5316\u6570\u5341\u5e74\u7684\u5149\u9634\u53bb\u6f5c\u5fc3\u82e6\u5b66\u3002\u5f02\u65e5\u4f60\u81ea\u6210\u4e00\u5bb6\uff0c\u90a3\u662f\u4f60\u81ea\u5df1\u7684\u6210\u5c31\uff0c\u6211\u5c82\u53ef\u63a0\u4eba\u4e4b \u7f8e\uff0c\u5206\u4f60\u4e4b\u529f\uff0c\u8fd9\u5e08\u5f92\u7684\u540d\u4efd\uff0c\u4e07\u4e07\u4e0d\u53ef\u3002\u201d\u5468\u8c37\u9690\u4e00\u542c\uff0c\u5fc3\u4e2d\u6697\u6697\u4f69\u670d\uff1a\u201c\u5230\u5e95\u662f\u5927\u4fa0\u7684 \u98ce\u5ea6\uff01\u201d\u8981\u77e5\u970d\u5927\u90fd\u82e5\u80fd\u5c06\u5404\u5bb6\u5404\u6d3e\uff0c\u878d\u6c47\u8d2f\u901a\uff0c\u7ec3\u6210\u5251\u672f\uff0c\u90a3\u5c31\u662f\u4e00\u6d3e\u7684\u5f00\u5c71\u5b97\u7956\u4e86\u3002\u5f20 \u4e39\u67ab\u4e4b\u4e0d\u80af\u6536\u4ed6\u4e3a\u5f92\uff0c\u6240\u8bf4\u7684\u7406\u7531\u56fa\u662f\u5b9e\u60c5\uff0c\u4f46\u4e5f\u542b\u6709\u8fd9\u6837\u7684\u5fc3\u610f\uff01\u4fdd\u5168\u4ed6\u5f00\u5c71\u5b97\u7956\u7684\u5730 \u4f4d\uff0c\u540e\u6765\u970d\u5929\u90fd\u5f97\u4e86\u5f20\u4e39\u67ab\u7684\u6307\u70b9\uff0c\u53c8\u5f97\u4e86\u767e\u53d8\u7384\u673a\u5251\u6cd5\u7684\u7cbe\u534e\uff0c\u679c\u7136\u5c06\u5929\u4e0b\u5251\u6cd5\u51b6\u4e8e\u4e00 \u7089\uff0c\u76f4\u5230\u4e94\u516d\u5341\u5e74\u4e4b\u540e\uff0c\u65b9\u4e0e\u4ed6\u7684\u9ad8\u8db3\u5cb3\u9e23\u8bc3\uff08\u5373\u660e\u671d\u672a\u5e74\u7684\u6b66\u5b66\u5927\u5e08\u6666\u660e\u7985\u5e08\uff09\u521b\u7acb\u4e86\u5929 \u5c71\u5251\u6d3e\u3002\u8fd9\u662f\u540e\u8bdd\uff0c\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u4e14\u8bf4\u4e8e\u627f\u73e0\u6025\u4e8e\u6253\u7834\u5fc3\u5185\u7684\u7591\u56e2\uff0c\u5374\u89c1\u970d\u5929\u90fd\u7d6e\u7d6e\u4e0d\u4f11\u5730\u4e0e\u5e08\u7236\u8c08\u8bba\u5251\u672f\uff0c\u5fc3\u4e2d\u5ffd\u7136\u8d77 \u4e86\u4e00\u79cd\u5947\u5f02\u7684\u611f\u89c9\u3002 \u3000\u3000\u4e8e\u627f\u73e0\u770b\u970d\u5929\u90fd\u4e0e\u5e05\u7236\u7d6e\u7d6e\u4e0d\u4f11\u5730\u8c08\u8bba\u5251\u672f\uff0c\u7981\u4e0d\u4f4f\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u51cc\u59d0\u59d0\u662f\u90a3\u7b49\u6302\u5ff5 \u4ed6\uff0c\u4ed6\u5374\u53ea\u987e\u7814\u8ba8\u6b66\u529f\uff0c\u8fde\u95ee\u4e5f\u4e0d\u95ee\u5979\u4e00\u95ee\u3002\u201d\u5f20\u4e39\u67ab\u89c1\u5979\u9762\u8272\u6709\u5f02\uff0c\u95ee\u9053\uff1a\u201c\u627f\u73e0\uff0c\u4f60\u60f3 \u8bf4\u4ec0\u4e48\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u970d\u5927\u54e5\u542c\u5f97\u8fd9\u6837\u5165\u8ff7\uff0c\u6211\u600e\u6562\u6253\u65ad\u4f60\u4eec\u7684\u8bdd\u5934\uff1f\u55ef\uff0c\u970d\u5927\u54e5\uff0c\u4f60\u7684 \u5b9a\u529b\uff0c\u771f\u771f\u53eb\u4eba\u4f69\u670d\uff01\u201d\u970d\u5929\u90fd\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u5b9a\u529b\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u60f3\u6765\u9664\u4e86 \u5251\u672f\u4e4b\u5916\uff0c\u662f\u518d\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e8b\u60c5\u53ef\u4ee5\u4ee4\u4f60\u5206\u5fc3\u7684\u4e86\uff1f\u201d\u970d\u5929\u90fd\u53c8\u662f\u4e00\u6014\uff0c\u5ffd\u5730\u77bf\u7136\u82e5\u60ca\uff0c\u95ee \u9053\uff1a\u201c\u4e8e\u59d1\u5a18\uff0c\u6211\u6b63\u8981\u5411\u4f60\u8bf7\u6559\uff0c\u4f60\u521a\u624d\u8bf4\u7684\u51cc\u4e91\u51e4\uff0c\u90a3\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u5f53\u771f\u4e0d\u77e5\uff1f\u201d\u970d\u5929\u90fd\u4e00\u6d3e\u8ff7\u60d8\u7684\u795e\u60c5\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u51cc\u4e91\u4e00\u51e4\uff0c\u51cc\u4e91\u4e00 \u51e4\u2026\u2026\u5440\uff0c\u4f60\u8bf4\u7684\u662f\uff0c\u662f\u2026\u2026\u201d\u4e8e\u627f\u73e0\u84e6\u7136\u9192\u8d77\u51cc\u4e91\u51e4\u7684\u672c\u6765\u540d\u5b57\u53eb\u505a\u201c\u51cc\u6155\u534e\u201d\uff0c\u201c\u4e91 \u51e4\u201d\u4e8c\u5b57\u662f\u5979\u505a\u4e86\u5973\u5be8\u4e3b\u4e4b\u540e\u624d\u8d77\u7684\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u8bf4\u7684\u6b63\u662f\u4ece\u6f20\u5916\u98de\u6765\uff0c\u4e2d\u9014\u5931 \u4fa3\u7684\u5b64\u51e4\uff01\u201d\u5c0f\u864e\u5b50\u5947\u9053\uff1a\u201c\u6f20\u5916\u4e5f\u6709\u51e4\u51f0\uff1f\u201d \u3000\u3000\u970d\u5929\u90fd\u66f4\u662f\u60ca\u5947\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8ba4\u8bc6\u5e55\u534e\uff1f\u5979\u662f\u6211\u7684\u8868\u59b9\uff0c\u4f60\u662f\u8bf4\u5979\u5427\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u5979\u73b0\u5728\u5df2\u6539\u540d\u53eb\u51cc\u4e91\u51e4\u4e86\u3002\u201d\u970d\u65e0\u90fd\u9053\uff1a\u201c\u5979\u5728\u54ea\u513f\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5728\u6c5f\u5357\u7684\u4e49 \u519b\u4e4b\u4e2d\u3002\u51cc\u59d0\u59d0\u5e38\u5e38\u548c\u6211\u8bf4\u8d77\u4f60\uff0c\u4f60\u53ef\u77e5\u9053\u5979\u5728\u68a6\u91cc\u9192\u91cc\u90fd\u5728\u60f3\u5ff5\u7740\u4f60\u5417\uff1f\u201d\u970d\u5929\u90fd\u9762\u4e0a\u4e00 \u7ea2\uff0c\u7b11\u9053\uff1a\u201c\u662f\u4e48\uff1f\u6211\u662f\u770b\u7740\u5979\u957f\u5927\u7684\uff0c\u96be\u602a\u5979\u60f3\u5ff5\u6211\u3002\u6211\u548c\u5979\u5728\u6c99\u6f20\u5931\u6563\u7684\u65f6\u5019\uff0c\u5979\u8fd8\u662f \u4e2a\u4e0d\u5927\u61c2\u4e8b\u7684\u5341\u516d\u5c81\u7684\u5c0f\u59d1\u5a18\u5462\u3002\u73b0\u5728\u60f3\u5fc5\u5df2\u957f\u5927\u6210\u4eba\u4e86\uff1f\u201d\u4e8e\u627f\u73e0\u5657\u55e4\u4e00\u7b11\u9053\uff1a\u201c\u51cc\u59d0\u59d0 \u73b0\u5728\u662f\u5a01\u9707\u6c5f\u5357\u7684\u5973\u4e2d\u8c6a\u6770\uff0c\u4f60\u8fd8\u5f53\u5979\u662f\u4e0d\u61c2\u4e8b\u7684\u5c0f\u59d1\u5a18\u4e48\uff1f\u5979\u56e0\u6253\u542c\u4e0d\u5230\u4f60\u7684\u97f3\u8baf\uff0c\u5df2\u4e0d \u77e5\u54ed\u8fc7\u591a\u5c11\u573a\u4e86\uff01\u4f60\u5c31\u4e0d\u4e3a\u5979\u6302\u5fc3\u4e48\uff1f\u201d\u5c0f\u864e\u5b50\u53c8\u63d2\u53e3\u9053\uff1a\u201c\u65e2\u662f\u8c6a\u6770\uff0c\u8c6a\u6770\u4e5f\u54ed\u7684\u4e48\uff1f\u201d \u4e91\u857e\u542c\u5f97\u5fcd\u4fca\u4e0d\u7981\uff0c\u5c06\u5c0f\u864e\u5b50\u62cd\u5f00\u9053\uff1a\u201c\u5927\u4eba\u8bf4\u8bdd\uff0c\u4e0d\u8981\u6253\u5c94\u3002\u201d \u3000\u3000\u970d\u5929\u90fd\u60ca\u559c\u4ea4\u96c6\uff0c\u4ee5\u5f80\u4ed6\u53ea\u662f\u628a\u51cc\u4e91\u51e4\u5f53\u505a\u5c0f\u59b9\u59b9\u770b\u5f85\uff0c\u60f3\u4e0d\u5230\u8fd9\u4f4d\u201c\u5c0f\u59b9\u59b9\u201d\u957f\u6210\u540e \u5374\u662f\u5bf9\u4ed6\u53e6\u6709\u4e00\u756a\u60c5\u610f\u3002\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u5f53\u7136\u6211\u4e5f\u6302\u5ff5\u5979\u3002\u201d\u5c0f\u864e\u5b50\u53c8\u63d2\u53e3\u9053\uff1a\u201c\u4f60\u54ed\u4e86\u6ca1 \u6709\uff1f\u201d\u970d\u5929\u90fd\u7b11\u9053\uff1a\u201c\u5979\u5927\u7ea6\u4ee5\u4e3a\u6211\u5728\u6c99\u6f20\u4e4b\u4e2d\u6b7b\u4e86\uff0c\u6240\u4ee5\u62c5\u5fc3\u3002\u6211\u5374\u77e5\u9053\u5979\u6ca1\u6709\u6b7b\uff0c\u5012\u662f \u672a\u66fe\u54ed\u8fc7\u3002\u90a3\u5929\u5728\u6c99\u6f20\u4e4b\u4e2d\u9047\u9669\uff0c\u6211\u6240\u5904\u7684\u65b9\u5411\u6b63\u5bf9\u7740\u65cb\u98ce\u7684\u4e2d\u5fc3\uff0c\u5979\u7684\u65b9\u5411\u5219\u5728\u4fa7\u8fb9\uff0c\u6211 \u6599\u60f3\u4ee5\u5979\u7684\u6b66\u529f\uff0c\u5b9a\u7136\u80fd\u591f\u8131\u9669\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4f60\u662f\u600e\u4e48\u8131\u9669\u7684\uff1f\u201d\u970d\u5929\u90fd\u9053\uff1a\u201c\u6211\u9669\u4e9b\u88ab\u6d41\u6c99\u6d3b\u57cb\u4e86\uff0c\u5e78\u4e8f\u9047\u4e0a\u4e86\u4e24\u4e2a \u4eba\uff0c\u624d\u4fdd\u5168\u4e86\u8fd9\u6761\u6027\u547d\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u53ef\u662f\u5927\u6f20\u795e\u72fc\u54c8\u6728\u56fe\u548c\u897f\u5c71\u533b\u9690\u53f6\u5143\u7ae0\uff1f\u201d\u970d\u5929\u90fd \u8be7\u9053\uff1a\u201c\u4f60\u90fd\u77e5\u9053\u4e86\uff1f\u201d\u4e8e\u627f\u73e0\u5c06\u8def\u9047\u54c8\u6728\u56fe\u548c\u53f6\u5143\u7ae0\u4ee5\u53ca\u90dd\u4e91\u53f0\u6765\u9a97\u51cc\u4e91\u51e4\u5251\u8c31\u7b49\u4e8b\u8bf4 \u4e86\u3002\u970d\u5929\u90fd\u5927\u7b11\u9053\uff1a\u201c\u539f\u6765\u8fd8\u6709\u8fd9\u8bb8\u591a\u4e8b\u60c5\uff01\u201d\u4e8e\u662f\u7ed9\u4e8e\u627f\u73e0\u7ec6\u8bf4\u4ed6\u8131\u9669\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u539f\u6765\u90a3\u65e5\u5728\u5927\u6f20\u4e4b\u4e2d\u9047\u9669\uff0c\u970d\u5929\u90fd\u88ab\u522e\u5012\u5730\u4e0a\uff0c\u5927\u98ce\u5377\u8d77\u4e86\u6ee1\u5929\u9ec4\u6c99\uff0c\u98ce\u8fc7\u4e86\u540e\uff0c\u5e73\u5730 \u5806\u8d77\u4e86\u65e0\u6570\u6c99\u4e18\uff0c\u970d\u5929\u90fd\u5c31\u88ab\u538b\u5728\u4e00\u4e2a\u6c99\u4e18\u4e0b\u9762\uff0c\u9669\u4e9b\u88ab\u6d3b\u57cb\u4e86\u3002\u5e78\u800c\u5927\u6f20\u795e\u72fc\u6765\u5f97\u53ca\u65f6\uff0c \u5c06\u4ed6\u6551\u51fa\uff0c\u53ef\u662f\u4ea6\u5df2\u5944\u5944\u4e00\u606f\uff0c\u970d\u5929\u90fd\u4e5f\u4ee5\u4e3a\u81ea\u5df1\u96be\u6d3b\u4e86\uff0c\u60f3\u8d77\u7236\u4eb2\u751f\u524d\u66fe\u4e0e\u4ed6\u8bf4\u8fc7\uff0c\u6709\u4e00 \u4e2a\u7ed3\u62dc\u5144\u5f1f\u53eb\u505a\u5468\u8c37\u9690\uff0c\u4f4f\u5728\u516b\u8fbe\u5cad\u70b9\u5c06\u53f0\u7684\u9644\u8fd1\uff0c\u4f46\u5df2\u6709\u6570\u5341\u5e74\u4e0d\u901a\u97f3\u8baf\uff0c\u4e0d\u77e5\u662f\u5426\u5c1a\u5728 \u4eba\u95f4\uff1f\u970d\u5929\u90fd\u56e0\u4e3a\u90a3\u672c\u5251\u8c31\u4e43\u662f\u4ed6\u7236\u5b50\u4e24\u4eba\u5fc3\u8840\u7684\u7ed3\u6676\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u65e0\u4eba\u53ef\u4ee5\u4ed8\u6258\uff0c\u53ea\u597d\u8bf7 \u5927\u6f20\u795e\u72fc\u5c06\u5251\u8c31\u4ea4\u7ed9\u5468\u8c37\u9690\uff0c\u4ed6\u672c\u5c31\u5df2\u5944\u5944\u4e00\u606f\uff0c\u6551\u51fa\u4e4b\u540e\uff0c\u53c8\u5728\u5927\u6bd2\u65e5\u5934\u4e4b\u4e0b\u6652\u4e86\u534a\u65e5\uff0c \u7aed\u5c3d\u6c14\u529b\uff0c\u521a\u8bf4\u51fa\u5730\u5740\uff0c\u4e00\u53e3\u6c14\u900f\u4e0d\u8fc7\u6765\uff0c\u7ade\u81ea\u6655\u5012\u3002\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u90a3\u5927\u6f20\u795e\u72fc\u4ee5 \u4e3a\u4f60\u5df2\u6b7b\u4e86\u3002\u540e\u6765\u4ed6\u5c06\u90a3\u672c\u5251\u8c31\u4e0e\u90dd\u4e91\u53f0\u53c2\u8be6\uff0c\u5251\u8c31\u4e0a\u6709\u4f60\u7684\u5730\u5740\uff0c\u90dd\u4e91\u53f0\u5374\u53eb\u4eba\u5192\u4f60\u7684\u5b57 \u8ff9\uff0c\u53bb\u9a97\u51cc\u4e91\u51e4\u3002\u201d \u3000\u3000\u970d\u5929\u90fd\u9053\uff1a\u201c\u6211\u88ab\u629b\u5f03\u5728\u5927\u6f20\u4e4b\u4e2d\uff0c\u672c\u6765\u662f\u6d3b\u4e0d\u6210\u4e86\u3002\u65e0\u5de7\u4e0d\u5de7\uff0c\u5927\u98ce\u8fc7\u540e\u7684\u7b2c\u4e8c\u5929\uff0c \u6c99\u6f20\u4e0a\u5ffd\u7136\u843d\u4e86\u4e00\u573a\u767e\u5e74\u7f55\u89c1\u7684\u5927\u96e8\uff0c\u6211\u7ed9\u96e8\u6c34\u6ecb\u6da6\uff0c\u5c45\u7136\u590d\u82cf\u3002\u4f46\u56e0\u96e8\u6dcb\u65e5\u6652\uff0c\u57cb\u5728\u6c99\u5806 \u4e0b\u9762\u65f6\uff0c\u53c8\u88ab\u90a3\u70bd\u70ed\u7684\u6c99\u5b50\u5c06\u70ed\u6bd2\u8feb\u5165\u4e94\u810f\uff0c\u56e0\u6b64\u5f97\u4e86\u4e00\u79cd\u602a\u75c5\uff0c\u7ecf\u5e38\u53d1\u51b7\u53d1\u70ed\u3002\u6211\u5230\u4e86\u516b \u8fbe\u5cad\u6765\u5bfb\u4e16\u4f2f\uff0c\u5e78\u9047\u897f\u5c71\u533b\u9690\u53f6\u5143\u7ae0\uff0c\u5728\u4ed6\u5bb6\u4e2d\u4f4f\u4e86\u6570\u6708\uff0c\u8fd9\u624d\u5c06\u75c5\u6839\u5f7b\u5e95\u6cbb\u597d\u3002\u201d \u3000\u3000\u970d\u5929\u90fd\u5c06\u81ea\u5df1\u7684\u6545\u4e8b\u8bf4\u5b8c\u4e4b\u540e\uff0c\u4e8e\u627f\u73e0\u4e5f\u5c06\u51cc\u4e91\u51e4\u7684\u60c5\u5f62\u8bf4\u7ed9\u4ed6\u77e5\u9053\u3002\u970d\u5929\u90fd\u542c\u8bf4\u51cc\u4e91 \u51e4\u5728\u5c6f\u6eaa\u9677\u5165\u5b98\u519b\u7684\u5305\u56f4\uff0c\u751a\u4e3a\u7740\u6025\u3002\u5f53\u4e0b\u5546\u8bae\uff0c\u7b2c\u4e8c\u65e5\u4fbf\u4e0e\u5f20\u4e39\u67ab\u592b\u5987\u7b49\u4eba\u7ed3\u4f34\u540c\u884c\uff0c\u5230 \u5c6f\u6eaa\u53bb\u89c1\u673a\u884c\u4e8b\u3002 \u3000\u3000\u5468\u8c37\u9690\u5728\u70b9\u5c06\u53f0\u7684\u540e\u9762\u7b51\u6709\u4e09\u95f4\u77f3\u5c4b\uff0c\u8fd9\u4e00\u5929\uff0c\u5927\u5bb6\u4fbf\u5728\u77f3\u5c4b\u4e2d\u6b22\u805a\u3002\u5468\u8c37\u9690\u89c1\u5f20\u4e39\u67ab \u80af\u6307\u70b9\u970d\u5929\u90fd\u7684\u5251\u672f\uff0c\u6781\u4e3a\u6b22\u559c\uff0c\u6295\u6843\u62a5\u674e\uff0c\u81ea\u613f\u4f20\u6388\u4e8e\u627f\u73e0\u548c\u5c0f\u864e\u5b50\u201c\u79fb\u5f62\u6362\u5f71\u201d\u7684\u4e0a\u4e58 \u8f7b\u529f\uff0c\u4ed6\u521a\u624d\u5728\u77f3\u53f0\u4e0a\u5ffd\u9690\u5ffd\u73b0\uff0c\u620f\u5f04\u4e8e\u627f\u73e0\uff0c\u6240\u7528\u7684\u4fbf\u662f\u8fd9\u79cd\u529f\u592b\u3002\u5c0f\u864e\u5b50\u672c\u6765\u6068\u4ed6\u53eb\u970d \u5929\u90fd\u6253\u81ea\u5df1\u7684\u5c41\u80a1\uff0c\u5f97\u4ed6\u4f20\u6388\u8fd9\u79cd\u4e0a\u4e58\u8f7b\u529f\uff0c\u6c14\u4e5f\u6d88\u4e86\u3002\u8fd9\u4e00\u665a\u5927\u5bb6\u8c08\u8bba\u6b66\u529f\uff0c\u76f4\u95f9\u5230\u4e94\u66f4 \u65b9\u7761\u3002\u4e8e\u627f\u73e0\u5374\u8fd8\u662f\u7761\u4e0d\u7740\u89c9\uff0c\u7ffb\u6765\u8986\u53bb\uff0c\u603b\u662f\u60f3\u7740\u53f6\u6210\u6797\u3002\u53f6\u6210\u6797\u600e\u4e48\u6837\u4e86\u5462\uff1f\u51cc\u4e91\u51e4\u53c8 \u600e\u4e48\u6837\u4e86\u5462\uff1f \u3000\u3000\u53f6\u6210\u6797\u548c\u51cc\u4e91\u51e4\u5728\u5c6f\u6eaa\u4e5f\u662f\u4e00\u6837\u5730\u601d\u5ff5\u4e8e\u627f\u73e0\uff0c\u4ed6\u4eec\u88ab\u56f4\u5728\u77f3\u57ce\u4e4b\u4e2d\uff0c\u5df2\u7ecf\u6570\u6708\u4e86\uff0c\u867d \u7136\u6253\u4e86\u8bb8\u591a\u6b21\u80dc\u4ed7\uff0c\u4f46\u5b98\u519b\u8d8a\u6765\u8d8a\u591a\uff0c\u4e00\u65e0\u63f4\u5175\uff0c\u4e8c\u65e0\u7cae\u8349\uff0c\u6e10\u6e10\u8fde\u9a6c\u5339\u4e5f\u51e0\u4e4e\u6740\u5f97\u5e72\u51c0\uff0c \u53f6\u6210\u6797\u624b\u4e0b\u7684\u4e00\u4e07\u7cbe\u5175\u4e5f\u53ea\u5269\u4e0b\u4e86\u56db\u5343\uff0c\u5f62\u52bf\u8d8a\u6765\u8d8a\u9669\u3002\u8fd9\u4e00\u5929\u4ed6\u4eec\u5728\u5e10\u4e2d\u8bae\u4e8b\uff0c\u65d7\u724c\u5b98\u8fdb \u6765\u4f20\u62a5\uff0c\u8bf4\u662f\u6bd5\u613f\u7a77\u524d\u6765\u6c42\u89c1\uff0c\u53f6\u6210\u6797\u5927\u4e3a\u8be7\u5f02\uff0c\u7acb\u523b\u8bf7\u4ed6\u8fdb\u6765\u3002\u4f46\u89c1\u6bd5\u613f\u7a77\u6ee1\u9762\u98ce\u5c18\u4e4b \u8272\uff0c\u4e00\u5411\u60f9\u4eba\u53d1\u7b11\u7684\u795e\u60c5\u4e5f\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u53f6\u6210\u6797\u6ee1\u8179\u72d0\u7591\uff0c\u5fc3\u9053\uff1a\u201c\u6bd5\u613f\u7a77\u4e43\u662f\u6bd5\u64ce\u5927\u7684\u5802\u4f84\uff0c\u4ea6\u662f\u4ed6\u6700\u4eb2\u4fe1\u7684\u4eba\uff0c\u6bd5\u64ce\u5929\u4e0e\u6211 \u7684\u53d4\u53d4\u95f9\u7ffb\uff0c\u4f55\u6545\u4ed6\u5374\u5355\u9a91\u5230\u6b64\uff1f\u201d\u6025\u5fd9\u5c06\u4ed6\u63a5\u5165\u4e2d\u5802\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u90a3\u8fb9\u7684\u519b\u60c5\u5982\u4f55\uff1f\u5927 \u654c\u5f53\u524d\uff0c\u5404\u8def\u4e49\u519b\u7406\u8be5\u8054\u7ed3\u4e00\u81f4\uff0c\u5171\u540c\u5fa1\u4fae\u3002\u5144\u5f1f\u4e4b\u4e89\uff0c\u6682\u4e14\u6401\u8fc7\u4e00\u8fb9\u3002\u6211\u8fd9\u91cc\u65e9\u5df2\u60f3\u6d3e\u51fa \u4eba\u53bb\u5411\u6bd5\u5927\u9f99\u5934\u8bf7\u793a\u4e86\uff0c\u53ef\u60dc\u56f4\u57ce\u65e5\u7d27\uff0c\u6d3e\u4e0d\u51fa\u53bb\u3002\u6bd5\u5927\u54e5\u4eca\u65e5\u5230\u6b64\uff0c\u4f55\u5e78\u5982\u4e4b\uff0c\u8bf7\u95ee\u53ef\u5e26 \u6709\u6bd5\u5927\u9f99\u5934\u7684\u4e66\u4fe1\u6765\u4e48\uff1f\u63f4\u519b\u4ec0\u4e48\u65f6\u5019\u53ef\u5230\uff1f\u201d\u53f6\u6210\u6797\u5c3d\u5f80\u597d\u91cc\u60f3\uff0c\u8fd8\u4ee5\u4e3a\u6bd5\u613f\u7a77\u662f\u6bd5\u64ce\u5929 \u6d3e\u6765\u62a5\u4fe1\u7684\u4eba\u3002 \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u4f46\u89c1\u6bd5\u613f\u7a77\u864e\u76ee\u8574\u6cea\uff0c\u60e8\u7b11\u8bf4\u9053\uff1a\u201c\u6bd5\u5927\u9f99\u5934\u7684\u4e66\u4fe1\u5012\u6709\u4e00\u5c01\uff0c\u53ef\u4e0d\u662f\u7ed9\u4f60 \u7684\u3002\u4ed6\u7684\u519b\u961f\u4e5f\u5f00\u6765\u4e86\uff0c\u53ef\u4e0d\u662f\u63f4\u519b\u3002\u201d\u51cc\u4e91\u51e4\u53eb\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u6bd5\u5927\u9f99\u5934\u5df2 \u53d7\u671d\u5ef7\u62db\u5b89\uff0c\u6211\u5077\u8fc7\u6e29\u5dde\u4e4b\u65f6\uff0c\u57ce\u4e2d\u5df2\u6362\u4e86\u671d\u5ef7\u7684\u65d7\u5e1c\uff0c\u542c\u8bf4\u90a3\u91cc\u7684\u4e49\u519b\u4e0e\u5b98\u519b\u5408\u7f16\uff0c\u8fd8\u8bf4 \u6bd5\u5927\u9f99\u5934\u4e5f\u8981\u4eb2\u81ea\u5e26\u5175\u6765\u6253\u4f60\u4eec\u3002\u201d\u53f6\u6210\u6797\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u201c\u5f53\u5577\u201d\u4e00\u58f0\uff0c\u624b\u4e0a\u7684\u8336\u676f\u8dcc \u5f97\u7c89\u788e\uff0c\u53eb\u9053\uff1a\u201c\u5f53\u771f\u6709\u8fd9\u7b49\u4e8b\u4e48\uff1f\u201d\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u5c01\u4fe1\u3002\u201d\u90a3\u662f\u6bd5\u64ce\u5929\u53eb\u4ed6\u9001\u7ed9\u9633 \u5b97\u6d77\u7684\u5bc6\u4fe1\uff0c\u4ed6\u4ecd\u7136\u539f\u5c01\u5e26\u56de\u3002\u4fe1\u4e2d\u6240\u8bf4\u7684\uff0c\u5c31\u662f\u4e0e\u671d\u5ef7\u8ba8\u4ef7\u8fd8\u4ef7\u63a5\u53d7\u62db\u5b89\u7684\u6761\u4ef6\u3002\u53f6\u6210\u6797 \u770b\u4e86\uff0c\u4f5c\u58f0\u4e0d\u5f97\u3002\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u4f60\u5230\u4e86\u5317\u4eac\u4e48\uff1f\u201d\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u6211\u5c31\u662f\u4ece\u5317\u4eac\u65e5\u591c\u4e0d\u505c\u5730\u8d76 \u56de\u6765\u7684\uff01\u55ef\uff0c\u5317\u4eac\u6211\u662f\u5230\u4e86\uff0c\u53ef\u5e76\u6ca1\u6709\u8e0f\u8fdb\u5927\u5185\u603b\u7ba1\u7684\u95e8\uff01\u201d\u53f6\u6210\u6797\u4e00\u628a\u62b1\u4f4f\u4e86\u6bd5\u613f\u7a77\uff0c\u8bf4 \u9053\uff1a\u201c\u6bd5\u5144\u5f1f\uff0c\u597d\u6c49\u5b50\uff01\u75be\u98ce\u77e5\u52b2\u8349\uff0c\u4e16\u4e71\u89c1\u4eba\u5fc3\uff01\u4eca\u65e5\u6211\u65b9\u77e5\u9053\u4f60\u7684\u4e3a\u4eba\u3002\u8bf7\u53d7\u5c0f\u5f1f\u4e00 \u62dc\u3002\u201d\u6bd5\u613f\u7a77\u62e6\u4f4f\u4e86\u4ed6\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u8bdd\u4e0d\u5fc5\u8bf4\u5566\u3002\u6211\u5728\u5317\u4eac\u78b0\u5230\u4e86\u5f20\u5927\u4fa0\u3002\u5f20\u5927\u4fa0\u7684\u610f\u601d\u8bf7 \u4f60\u4ece\u901f\u9000\u5175\uff0c\u80fd\u4fdd\u5168\u5f97\u591a\u5c11\u5c31\u662f\u591a\u5c11\uff01\u201d \u3000\u3000\u53f6\u6210\u6797\u9053\uff1a\u201c\u597d\uff0c\u6211\u73b0\u5728\u5c31\u53bb\u5e03\u7f6e\u3002\u4e91\u51e4\uff0c\u4f60\u966a\u6bd5\u5927\u54e5\u8c08\u8c08\uff0c\u5546\u8ba8\u4e00\u4e0b\u600e\u6837\u4e34\u5371\u5e94 \u53d8\u3002\u201d\u5306\u5306\u800c\u51fa\uff0c\u51cc\u4e91\u51e4\u53f9\u9053\uff1a\u201c\u8fd9\u4e9b\u65e5\u5b50\uff0c\u4e5f\u591f\u4ed6\u7d2f\u7684\u4e86\uff0c\u53ef\u60dc\u627f\u73e0\u59b9\u59b9\u4e0d\u5728\u8fd9\u91cc\uff0c\u6ca1\u4eba \u7ed9\u4ed6\u5206\u52b3\u3002\u201d\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u6211\u5728\u5317\u4eac\u5df2\u89c1\u5230\u4e8e\u59d1\u5a18\u4e86\u3002\u201d\u51cc\u4e91\u51e4\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u6709\u4e00\u4f4d\u94c1 \u516c\u5b50\uff0c\u662f\u4e0d\u662f\u548c\u5979\u4e00\u8d77\uff1f\u201d\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u94c1\u955c\u5fc3\u5417\uff1f\u6ca1\u6709\u89c1\u4ed6\uff01\u201d\u51cc\u4e91\u51e4\u5401\u4e86\u4e00\u53e3 \u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u4ed6\u4eec\u5728\u5317\u4eac\u7684\u4e8b\u4f55\u65f6\u53ef\u4e86\uff1f\u5440\uff0c\u6211\u771f\u662f\u60f3\u89c1\u89c1\u5979\u3002\u201d \u3000\u3000\u51cc\u4e91\u51e4\u53c8\u95ee\u4e86\u4e00\u4e9b\u5b98\u519b\u7684\u60c5\u5f62\uff0c\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u6211\u8e0f\u8fdb\u6d59\u6c5f\uff0c\u4f46\u89c1\u5b98\u519b\u7edc\u7ece\u4e0d\u7edd\uff0c\u6211\u662f\u7ed5 \u5c0f\u8def\u6765\u7684\uff0c\u6cbf\u9014\u57ce\u9547\u90fd\u4e0d\u6562\u505c\u7559\u3002\u53ea\u662f\u542c\u8bf4\u6d59\u6c5f\u5de1\u629a\u5f20\u9aa5\u4eb2\u81ea\u9886\u5175\uff0c\u5176\u4ed6\u5b98\u519b\u8c03\u52a8\u7684\u60c5\u5f62\u6211 \u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d\u6b63\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u542c\u5f97\u5916\u8fb9\u8f70\u9686\u9686\u7684\u51e0\u58f0\u70ae\u54cd\uff0c\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u6bd5\u5144\u5f1f\uff0c\u54b1\u4eec\u540c\u53bb \u770b\u770b\u3002\u4f60\u7d2f\u4e0d\u7d2f\uff1f\u6211\u60f3\u4e0a\u57ce\u5899\u53bb\u4e0e\u5144\u5f1f\u4eec\u4e00\u540c\u5b88\u57ce\u3002\u201d\u521a\u521a\u7ad9\u8d77\uff0c\u53ea\u89c1\u53f6\u6210\u6797\u65cb\u98ce\u822c\u5730\u8dd1\u8fdb \u6765\u3002\u53eb\u9053\uff1a\u201c\u57ce\u5df2\u7834\u5566\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u8c01\u662f\u82f1\u96c4\u4eca\u59cb\u89c1\uff0c\u9f99\u5934\u7adf\u4f5c\u53db\u5f92\u6765\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e94\u56de\u3000\u7b95\u8c46\u7adf\u76f8\u714e\u3000\u9f99\u5934\u53d8\u8282\u3000\u98ce\u4e91\u60ca\u53d8\u5e7b\u3000\u516c\u5b50\u591a\u60c5 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e09\u5341\u4e94\u56de\u3000\u7b95\u8c46\u7adf\u76f8\u714e\u3000\u9f99\u5934\u53d8\u8282\u3000\u98ce\u4e91\u60ca\u53d8\u5e7b\u3000\u516c\u5b50\u591a\u60c5 \u3000\u3000\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u57ce\u5899\u524d\u65e5\u65b9\u624d\u4fee\u597d\uff0c\u600e\u4e48\u53ea\u542c\u5f97\u51e0\u58f0\u70ae\u54cd\uff0c\u57ce\u5c31\u7834\u4e86\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u662f \u6bd5\u64ce\u5929\u6253\u4e86\u8fdb\u6765\uff0c\u5b88\u57ce\u7684\u5144\u5f1f\u4e0d\u77e5\u9053\u4ed6\u5df2\u964d\u4e86\u5b98\u519b\uff0c\u7ed9\u4ed6\u4eec\u6253\u5f00\u4e86\u57ce\u95e8\u3002\u90a3\u51e0\u58f0\u70ae\u54cd\u5927\u7ea6\u662f \u5b98\u519b\u793a\u5a01\u7684\u3002\u54b1\u4eec\u5feb\u4ece\u4e1c\u95e8\u64a4\u8d70\uff01\u201d \u3000\u3000\u5954\u51fa\u5e05\u5e10\uff0c\u4f46\u89c1\u57ce\u4e2d\u5df2\u8d77\u4e86\u65e0\u6570\u706b\u5934\uff0c\u5e78\u800c\u53f6\u6210\u6797\u65e9\u5f97\u8baf\u606f\uff0c\u9884\u6709\u5b89\u6392\uff0c\u5c06\u57ce\u4e2d\u7684\u5175\u58eb \u90fd\u96c6\u7ed3\u8d77\u6765\uff0c\u8981\u4e0d\u7136\u66f4\u662f\u4e0d\u582a\u8bbe\u60f3\uff01 \u3000\u3000\u706b\u5149\u4e2d\u53ae\u6740\u58f0\u547c\u53f7\u58f0\u4e71\u6210\u4e00\u7247\uff0c\u57ce\u4e2d\u7684\u767e\u59d3\u6276\u8001\u643a\u5e7c\uff0c\u5404\u81ea\u9003\u751f\uff0c\u60e8\u4e0d\u5fcd\u7779\u3002\u51cc\u4e91\u51e4\u54ac \u7259\u5207\u9f7f\uff0c\u5927\u6012\u8bf4\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u6bd5\u64ce\u5929\uff0c\u8fd9\u6837\u72e0\u5fc3\uff0c\u770b\u4f60\u6709\u4ec0\u4e48\u9762\u76ee\u89c1\u6211\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u4f46\u89c1 \u4e00\u5f6a\u5b98\u519b\u6740\u4e86\u8fc7\u6765\uff0c\u9886\u5934\u7684\u6b63\u662f\u6bd5\u64ce\u5929\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u51cc\u5be8\u4e3b\uff0c\u8bc6\u65f6\u52a1\u8005\u4e3a\u4fca\u6770\uff0c\u4f60\u4f55\u5fc5\u8fd8\u966a\u53f6\u6210\u6797\u90a3\u5c0f\u5b50\u9001\u547d\uff1f\u201d\u51cc\u4e91 \u51e4\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u6bd5\u5927\u9f99\u5934\uff0c\u4f60\u6765\u5440\uff01\u201d\u62a2\u4e86\u4e00\u5f20\u5927\u5f13\uff0c\u5506\u5730\u4e00\u7bad\u5c04\u53bb\uff0c\u6bd5\u64ce\u5929\u4e00\u68d2\u6253\u98de\uff0c\u8fd9 \u65f6\u4e24\u4eba\u76f8\u8ddd\u4e0d\u8fc7\u6570\u4e08\u4e4b\u5730\uff0c\u51cc\u4e91\u51e4\u5ffd\u5730\u98de\u8eab\u4e00\u63a0\uff0c\u9752\u94a2\u5251\u5530\u5730\u51fa\u9798\uff0c\u5530\u3001\u5530\u3001\u5530\u4fbf\u662f\u8fde\u73af\u4e09 \u5251\uff01 \u3000\u3000\u8fd9\u4e09\u5251\u5f62\u540c\u62fc\u547d\uff0c\u6bd5\u64ce\u5929\u867d\u662f\u6b66\u529f\u9ad8\u5f3a\uff0c\u4e5f\u7ed9\u5979\u6740\u5f97\u5fc3\u60ca\u80c6\u98a4\uff0c\u6bd5\u64ce\u5929\u7684\u968f\u8eab\u536b\u58eb\u4e00\u62e5 \u800c\u4e0a\uff0c\u4f46\u542c\u5f97\u5530\u7684\u4e00\u58f0\uff0c\u51cc\u4e91\u51e4\u7684\u80a9\u5934\u7740\u4e86\u4e00\u97ad\uff0c\u6bd5\u64ce\u5929\u7684\u8863\u8896\u4e5f\u7ed9\u51cc\u4e91\u51e4\u524a\u53bb\u4e00\u622a\u3002 \u3000\u3000\u53f6\u6210\u6797\u6b63\u81ea\u6307\u6325\u4e49\u519b\u79bb\u57ce\uff0c\u5ffd\u7136\u4e0d\u89c1\u51cc\u4e91\u51e4\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u6025\u5fd9\u6298\u56de\uff0c\u53ea\u89c1\u51cc\u4e91\u51e4 \u5df2\u9677\u5165\u5305\u56f4\uff0c\u4e0e\u5341\u51e0\u4e2a\u536b\u58eb\u6df7\u6218\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u53eb\u9053\uff1a\u201c\u53f6\u5927\u54e5\uff0c\u4f60\u5feb\u8d70\uff01\u201d\u53f6\u6210\u6797\u54ea\u91cc\u80af\u4f9d\uff0c\u6325\u52a8\u5927\u5200\uff0c\u5288\u7ffb\u4e86\u51e0\u540d\u536b\u58eb\uff0c\u51b2 \u5165\u91cd\u56f4\uff0c\u9aa4\u7136\u89c1\u7740\u6bd5\u64ce\u5929\uff0c\u53f6\u6210\u6797\u559d\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u5341\u516b\u7701\u5927\u9f99\u5934\uff0c\u4f60\u7f9e\u4e5f\u4e0d\u7f9e\uff1f\u201d\u6bd5\u64ce\u5929\u5927 \u7b11\u9053\uff1a\u201c\u53f6\u6210\u6797\uff0c\u4f60\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u6562\u7b11\u6211\uff1f\u5927\u4e08\u592b\u597d\u574f\u4e5f\u8981\u7acb\u4e00\u756a\u529f\u4e1a\uff0c\u4ed6\u65e5\u6211\u88c2\u571f\u5c01\u738b\uff0c \u5927\u9f99\u5934\u53c8\u7b97\u5f97\u4ec0\u4e48\uff1f\u201d\u53f6\u6210\u6797\u594b\u8d77\u795e\u5a01\uff0c\u53c8\u5288\u7ffb\u4e86\u4e24\u540d\u536b\u58eb\uff0c\u4f46\u6bd5\u64ce\u5929\u5468\u56f4\u6b66\u58eb\u5982\u6797\uff0c\u53f6\u6210 \u6797\u54ea\u91cc\u51b2\u5f97\u8fdb\u53bb\u3002 \u3000\u3000\u53f6\u6210\u6797\u559d\u9053\uff1a\u201c\u6709\u80c6\u7684\u524d\u6765\u4e0e\u6211\u51b3\u4e00\u6b7b\u6218\uff01\u201d\u6bd5\u64ce\u5929\u7b11\u9053\uff1a\u201c\u4f60\u597d\u7cca\u6d82\uff0c\u4f60\u5f53\u6211\u8fd8\u662f\u5728 \u7eff\u6797\u4e2d\u7684\u9ed1\u9053\u4eba\u7269\u5417\uff1f\u6211\u800c\u4eca\u5df2\u662f\u671d\u5ef7\u5927\u5c06\uff0c\u8c01\u4e0e\u4f60\u4e00\u822c\u89c1\u8bc6\u3002\u201d\u5176\u5b9e\u6bd5\u64ce\u5929\u7684\u6b66\u529f\u5e76\u4e0d\u5728 \u51cc\u3001\u53f6\u4e8c\u4eba\u4e4b\u4e0b\uff0c\u4f46\u5929\u4e0b\u7684\u53db\u5f92\u5fc3\u7406\u90fd\u662f\u76f8\u540c\uff0c\u4e3a\u4e86\u6c42\u53d6\u5bcc\u8d35\u8363\u534e\uff0c\u54ea\u91cc\u8fd8\u80af\u548c\u4eba\u62fc\u547d\uff1f \u3000\u3000\u53f6\u6210\u6797\u6012\u6781\u6c14\u6781\uff0c\u6325\u5200\u529b\u6218\uff0c\u6bd5\u64ce\u5929\u72fc\u7259\u6367\u4e00\u6307\uff0c\u5c06\u8eab\u8fb9\u51e0\u540d\u5f97\u529b\u7684\u536b\u571f\u4e5f\u8c03\u4e86\u4e0a\u6765\uff0c \u53f6\u6210\u6797\u4e00\u770b\uff0c\u56f4\u653b\u4ed6\u7684\u536b\u58eb\u7fa4\u4e2d\uff0c\u6709\u597d\u51e0\u4e2a\u8fd8\u662f\u4ed6\u53d4\u53d4\u7684\u90e8\u4e0b\uff0c\u5fcd\u4e0d\u4f4f\u5927\u58f0\u53eb\u9000\uff1a\u201c\u53f6\u7edf\u9886 \u4ee5\u524d\u600e\u4e48\u6559\u8bad\u4f60\u4eec\uff1f\u4f60\u4eec\u4eca\u65e5\u4e3a\u864e\u4f5c\u6005\uff0c\u5c06\u6765\u6709\u4f55\u9762\u76ee\u89c1\u4ed6\uff1f\u201d\u90a3\u51e0\u4e2a\u4eba\u88ab\u6bd5\u64ce\u5929\u76d1\u89c6\uff0c\u4e0d \u6562\u653e\u677e\uff0c\u4f46\u5175\u5668\u65ab\u6765\uff0c\u5374\u5728\u6709\u610f\u65e0\u610f\u907f\u5f00\u4e86\u53f6\u6210\u6797\u7684\u8981\u5bb3\u3002\u6bd5\u64ce\u5929\u770b\u4e86\u4e00\u9635\uff0c\u5ffd\u5730\u53eb\u9053\uff1a \u201c\u4f60\u8fd9\u51e0\u4e2a\u9000\u4e0b\uff01\u201d\u6362\u4e86\u4ed6\u7684\u4eb2\u4fe1\u536b\u58eb\uff0c\u4e0e\u53f6\u6210\u6797\u7f20\u8eab\u8feb\u6597\uff01 \u3000\u3000\u53f6\u6210\u6797\u6d74\u8840\u6b7b\u6218\uff0c\u4f17\u5be1\u4e0d\u654c\uff0c\u9669\u8c61\u73af\u751f\u3002\u6709\u4e00\u80a1\u4e49\u519b\u53d1\u89c9\u4e3b\u5e05\u9677\u5165\u91cd\u56f4\uff0c\u6298\u56de\u6765\u6551\uff0c\u5374 \u88ab\u5b98\u519b\u622a\u4f4f\uff0c\u800c\u4e14\u5b98\u519b\u8d8a\u6765\u8d8a\u591a\uff0c\u53f6\u6210\u6797\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u9003\uff0c\u9003\u5f97\u51fa\u4e00\u4e2a\u7b97\u4e00\u4e2a\uff01\u201d\u7740\u6025\u4e4b \u4e0b\uff0c\u7a0d\u7a0d\u5206\u5fc3\uff0c\u80a9\u5934\u53c8\u7740\u4e86\u4e24\u5200\u3002 \u3000\u3000\u5ffd\u89c1\u6bd5\u64ce\u5929\u5468\u56f4\u7684\u536b\u58eb\u8ba9\u5f00\u6761\u8def\uff0c\u6bd5\u613f\u7a77\u6ee1\u8eab\u8840\u6c61\uff0c\u8dc4\u8dc4\u8e09\u8e09\u5730\u5954\u6765\u3002\u6bd5\u64ce\u5929\u53eb\u9053\uff1a \u201c\u54a6\uff0c\u4f60\u600e\u4e48\u5374\u5728\u8fd9\u513f\uff1f\u4f60\u5230\u4e86\u5317\u4eac\u6ca1\u6709\uff0c\u9633\u603b\u7ba1\u7684\u4e66\u4fe1\u600e\u4e48\u6ca1\u63d0\u8d77\u4f60\uff1f\u201d\u539f\u6765\u6bd5\u613f\u7a77\u65e5\u591c \u4e0d\u505c\u5730\u4ece\u5317\u4eac\u8d76\u56de\uff0c\u987e\u5b5f\u7ae0\u544a\u5bc6\u7684\u4e66\u4fe1\u5230\u6e29\u5dde\u65f6\uff0c\u6bd5\u64ce\u5929\u53c8\u5df2\u79bb\u5f00\u4e86\u6e29\u5dde\uff0c\u6545\u6b64\u6bd5\u64ce\u5929\u76f4\u5230 \u5982\u4eca\u8fd8\u672a\u77e5\u9053\u6bd5\u613f\u7a77\u80cc\u5f03\u4e86\u4ed6\u3002 \u3000\u3000\u6bd5\u613f\u7a77\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\uff0c\u6211\u6709\u673a\u5bc6\u7684\u4e8b\u60c5\u8981\u544a\u8bc9\u4f60\u3002\u201d\u6bd5\u64ce\u5929\u7a0d\u4e00\u8fdf\u7591\uff0c\u6325\u624b\u8bf4\u9053\uff1a \u201c\u597d\uff0c\u4f60\u4eec\u90fd\u53bb\u52a9\u6218\uff0c\u52a1\u5fc5\u8981\u628a\u90a3\u53f6\u6210\u6797\u751f\u64d2\u3002\u201d\u628a\u8eab\u8fb9\u7684\u536b\u58eb\u5c3d\u90fd\u9063\u6563\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6 \u5feb\uff0c\u6bd5\u613f\u7a77\u4e00\u4e2a\u864e\u8df3\uff0c\u53cd\u624b\u4e00\u6263\uff0c\u62ff\u7740\u7531\u64ce\u5929\u7684\u8109\u95e8\u8981\u7a74\uff0c\u5de6\u624b\u55d6\u7684\u4e00\u58f0\uff0c\u62bd\u51fa\u4e86\u4e00\u628a\u5315 \u9996\uff0c\u62b5\u7740\u4e86\u6bd5\u64ce\u5929\u7684\u54bd\u5589\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u5feb\u5c06\u4ed6\u4eec\u4e8c\u4eba\u653e\u8d70\uff01\u201d \u3000\u3000\u6bd5\u64ce\u5929\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u613f\u7a77\uff0c\u4f60\uff0c\u4f60\u2026\u2026\u4f60\u75af\u4e86\u5417\uff1f\u201d\u6bd5\u613f\u7a77\u5200\u950b\u8d34\u7740\u4e86\u4ed6\u7684\u54bd\u5589\u6c89\u58f0\u559d \u9053\uff1a\u201c\u628a\u4ed6\u4eec\u4e8c\u4eba\u653e\u8d70\uff01\u201d\u6bd5\u64ce\u5929\u9053\uff1a\u201c\u4f60\u662f\u6211\u4e00\u624b\u63d0\u62d4\u7684\u4f84\u513f\uff0c\u80f3\u818a\u53cd\u5411\u5916\u8fb9\u5f2f\u5417\uff1f\u201d\u6bd5 \u613f\u7a77\u5200\u950b\u4e00\u522e\uff0c\u8f7b\u8f7b\u4e00\u524a\uff0c\u524a\u53bb\u4e86\u6bd5\u64ce\u5929\u5589\u5934\u65c1\u8fb9\u7684\u4e00\u7247\u76ae\u8089\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u518d\u4e0d\u653e\u4eba\uff0c\u54b1 \u4eec\u4eca\u65e5\u5c31\u540c\u5f52\u4e8e\u5c3d\uff01\u201d\u6bd5\u64ce\u5929\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u8d76\u5feb\u64a4\u5f00\uff0c\u8ba9\u4ed6\u4eec\u8d70\uff01\u201d \u3000\u3000\u53f6\u6210\u6797\u770b\u4e86\u6bd5\u613f\u7a77\u4e00\u4e0b\uff0c\u5fc3\u4e2d\u6b63\u81ea\u72b9\u7591\uff0c\u8fd8\u672a\u80af\u8d70\u3002\u6bd5\u613f\u7a77\u53eb\u9053\uff1a\u201c\u7559\u5f97\u9752\u5c71\u5728\uff0c\u4e0d\u6015 \u6ca1\u67f4\u70e7\uff0c\u5f20\u5927\u4fa0\u53eb\u4f60\u4eec\u8d70\uff01\u201d\u53f6\u6210\u6797\u611f\u52a8\u4e4b\u6781\uff0c\u4ed6\u6709\u751f\u4ee5\u6765\uff0c\u4ece\u672a\u54ed\u8fc7\uff0c\u8fd9\u65f6\u4e5f\u4e0d\u7981\u6d12\u4e0b\u4e86 \u82f1\u96c4\u4e4b\u6cea\u3002\u201d \u3000\u3000\u6bd5\u613f\u7a77\u76ee\u9001\u53f6\u3001\u51cc\u4e8c\u4eba\u6df7\u5165\u4e49\u519b\u4e4b\u4e2d\uff0c\u51b2\u51fa\u4e86\u5b98\u519b\u5305\u56f4\u5708\u5b50\uff0c\u8fd9\u624d\u957f\u53f9\u4e00\u58f0\uff0c\u60e8\u7136\u7b11 \u9053\uff1a\u201c\u53d4\u53d4\uff0c\u6211\u5bf9\u5f97\u4f4f\u6bd5\u5bb6\u7684\u5217\u7956\u5217\u5b97\uff0c\u613f\u4f60\u4e5f\u987e\u5168\u53d4\u7956\u5728\u751f\u4e4b\u65e5\u90a3\u9707\u4e09\u754c\u7684\u5a01\u540d\uff0c\u4ed6\u65e5\u5730 \u4e0b\u76f8\u9022\uff0c\u4e5f\u597d\u6709\u4e2a\u4ea4\u4ee3\uff01\u201d\u6bc5\u7136\u5c06\u5315\u9996\u64a4\u4e86\u56de\u6765\uff0c\u5411\u81ea\u5df1\u7684\u80f8\u53e3\u4e00\u63d2\uff0c\u767b\u65f6\u5c38\u6a2a\u5730\u4e0a\uff0c\u8840\u6e85 \u5c18\u57c3\u3002 \u3000\u3000\u6bd5\u63a3\u5929\u5446\u5446\u53d1\u6123\uff0c\u7247\u523b\u4e4b\u4e2d\uff0c\u5fc3\u4e2d\u8f6c\u4e86\u65e0\u6570\u5ff5\u5934\uff0c\u4f46\u89c1\u51e0\u4e2a\u5b98\u519b\u65b9\u9762\u7684\u5c06\u9886\u73af\u7acb\u8eab\u65c1\uff0c \u90fd\u5728\u542c\u4ed6\u7684\u5429\u5490\uff0c\u4ed6\u54ac\u4e00\u54ac\u7259\uff0c\u9a82\u9053\uff1a\u201c\u8be5\u6b7b\uff01\u201d\u5429\u5490\u536b\u58eb\u9053\uff1a\u201c\u5c06\u6bd5\u613f\u7a77\u67ad\u9996\u793a\u4f17\uff0c\u4ee5\u4e3a \u5927\u9006\u4e0d\u9053\u4e4b\u6212\uff01\u201d\u72fc\u7259\u68d2\u4e00\u6307\u6325\uff0c\u6307\u6325\u5b98\u519b\u8854\u5c3e\u6025\u8ffd\u3002 \u3000\u3000\u53f6\u6210\u6797\u7387\u9886\u56db\u5343\u4e49\u519b\u4e14\u6218\u4e14\u8d70\uff0c\u9ec4\u660f\u65f6\u5206\u5df2\u5230\u4e86\u79bb\u57ce\u4e09\u5341\u91cc\u5916\uff0c\u56db\u5343\u4e49\u519b\u6b7b\u4f24\u4e86\u5341\u4e4b\u4e03 \u516b\uff0c\u5269\u4e0b\u7684\u4e0d\u8fc7\u4e00\u5343\u4eba\u5de6\u53f3\uff0c\u5e78\u800c\u524d\u9762\u5c31\u662f\u4e00\u5ea7\u5c71\u6797\uff0c\u53f6\u6210\u6797\u5c06\u519b\u961f\u90fd\u96c6\u7ed3\u5728\u5c71\u4e0a\uff0c\u5929\u8272\u5df2 \u9ed1\uff0c\u9760\u7740\u6811\u6797\u63a9\u62a4\uff0c\u5b98\u519b\u5012\u4e5f\u4e0d\u6562\u5192\u9669\u51b2\u4e0a\u3002\u4e0d\u591a\u65f6\uff0c\u6bd5\u64ce\u5929\u4e5f\u8ffd\u6765\u4e86\uff0c\u4e0b\u4ee4\u70b9\u8d77\u677e\u679d\u706b \u628a\uff0c\u5b88\u7740\u4e0b\u5c71\u7684\u54bd\u5589\u8981\u9053\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u7eb5\u9a6c\u4e0a\u5c71\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u53f6\u6210\u6797\uff0c\u4f60\u4eec\u5df2\u662f\u7f51\u5e95\u4e4b\u9f9f\uff0c\u74ee\u4e2d\u4e4b\u9cd6\uff0c\u5feb\u5feb\u5f52\u964d\uff0c\u8fd8 \u53ef\u4fdd\u5168\u6027\u547d\uff01\u201d\u53f6\u6210\u6797\u5927\u6012\u559d\u9053\uff1a\u201c\u5927\u4e08\u592b\u6b7b\u5219\u6b7b\u8033\uff0c\u5c82\u80fd\u50cf\u4f60\u8fd9\u7b49\u80cc\u5f03\u5f1f\u5144\u3001\u4e2d\u9014\u53d8\u8282\u7684 \u65e0\u803b\u53db\u5f92\uff01\u201d\u53d6\u8d77\u4e00\u5f20\u5927\u5f13\uff0c\u55d6\uff0c\u55d6\uff0c\u55d6\uff01\u4e09\u7bad\u5c04\u51fa\uff01\u4ed6\u662f\u82e6\u7ec3\u8fc7\u91d1\u521a\u638c\u7684\u4eba\uff0c\u8155\u529b\u5927\u5f97\u60ca \u4eba\uff0c\u6bd5\u64ce\u5929\u72fc\u7259\u68d2\u4e00\u626c\uff0c\u683c\u5f00\u4e86\u4e00\u652f\u5229\u7bad\uff0c\u7b2c\u4e8c\u652f\u5c04\u4e2d\u4e86\u4ed6\u7684\u6218\u9a6c\uff0c\u767b\u65f6\u9a6c\u4ef0\u4eba\u7ffb\uff0c\u8bf4\u65f6 \u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7b2c\u4e09\u652f\u7bad\u53c8\u95ea\u7535\u822c\u5c04\u81f3\uff0c\u6bd5\u64ce\u5927\u4f7f\u4e86\u4e2a\u201c\u71d5\u9752\u5341\u516b\u7ffb\u201d\u7684\u529f\u592b\uff0c\u5c31\u5730\u4e00\u6eda\u907f \u8fc7\uff0c\u90a3\u652f\u7bad\u5374\u5c04\u4e2d\u4e86\u4ed6\u8eab\u540e\u7684\u4e00\u540d\u536b\u58eb\uff0c\u4ece\u524d\u5fc3\u76f4\u81f3\u540e\u5fc3\uff01\u64ce\u5929\u722c\u4e86\u8d77\u6765\uff0c\u72fc\u72c8\u4e4b\u6781\uff0c\u4e0d\u6562 \u518d\u4e0a\u5c71\u9a82\u9635\uff0c\u4e0b\u53bb\u90e8\u7f72\uff0c\u51c6\u5907\u5230\u5927\u660e\u4e4b\u540e\uff0c\u518d\u5927\u4e3e\u653b\u5c71\u3002 \u3000\u3000\u9ed1\u591c\u4e4b\u4e2d\u4e24\u519b\u76f8\u5cd9\uff0c\u8c01\u4e5f\u4e0d\u6562\u5984\u52a8\uff0c\u6708\u660e\u661f\u7a00\uff0c\u6797\u4e2d\u7684\u9e1f\u96c0\uff0c\u90fd\u5df2\u88ab\u60ca\u8d77\u4ed6\u98de\uff0c\u7a7a\u6c14\u7d27 \u5f20\u6c89\u5bc2\u3002\u51cc\u4e91\u98ce\u95ea\u52a8\u7740\u4e00\u53cc\u660e\u4eae\u7684\u773c\u775b\uff0c\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u53f6\u5927\u54e5\uff0c\u8d81\u8fd9\u9ed1\u591c\uff0c\u4f60\u9003\u8d70\u4e86\u5427\u3002\u201d \u53f6\u6210\u6797\u9053\uff1a\u201c\u6211\u5c82\u80fd\u820d\u6389\u8fd9\u4e00\u5927\u7fa4\u540c\u751f\u5171\u6b7b\u7684\u5f1f\u5144\u3002\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u5f20\u5927\u4fa0\u4e5f\u8bf4\uff0c\u80fd\u9003\u51fa\u4e00 \u4eba\u5c31\u662f\u4e00\u4eba\uff0c\u4f60\u662f\u4e00\u519b\u4e3b\u5e05\uff0c\u80fd\u8131\u51fa\u5b98\u519b\u638c\u63e1\uff0c\u4ed6\u65e5\u8fd8\u53ef\u4e1c\u5c71\u518d\u8d77\uff0c\u5c82\u4e0d\u80dc\u5982\u5728\u8fd9\u91cc\u5750\u4ee5\u5f85 \u6bd9\u3002\u201d \u3000\u3000\u53f6\u6210\u6797\u4ecd\u662f\u6447\u5934\uff0c\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u627f\u73e0\u59b9\u59b9\u5728\u5317\u4eac\u95fb\u77e5\u6bd5\u64ce\u5929\u53db\u53d8\u7684\u6d88\u606f\uff0c\u4e0d\u77e5\u591a\u6302\u5ff5\u4f60 \u5462\uff01\u201d\u53f6\u6210\u6797\u9ed8\u7136\u4e0d\u8bdd\uff0c\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u55ef\uff0c\u53f6\u5927\u54e5\uff0c\u4f60\u5c31\u4e0d\u60f3\u518d\u89c1\u5979\u4e86\u5417\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a \u201c\u8fd9\u6837\u9003\u51fa\uff0c\u53eb\u6211\u6709\u4ec0\u4e48\u9762\u76ee\u89c1\u5979\uff1f\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u5df2\u7ecf\u5c3d\u4e86\u6700\u5927\u7684\u52aa\u529b\u652f\u6491\uff0c\u5929\u660e \u4e4b\u540e\uff0c\u518d\u4e0e\u4ed6\u4eec\u51b3\u4e00\u6b7b\u6218\uff0c\u4e5f\u4e0d\u89c1\u5f97\u6ca1\u6709\u751f\u673a\u3002\u201d\u53f6\u6210\u6797\u77e5\u9053\u5979\u662f\u60f3\u820d\u4e86\u6027\u547d\uff0c\u63a9\u62a4\u81ea\u5df1\u9003 \u751f\uff0c\u611f\u52a8\u4e4b\u6781\uff0c\u63e1\u7740\u5979\u7684\u624b\u9053\uff1a\u201c\u51cc\u59d0\u59d0\uff0c\u591a\u8c22\u4f60\u5566\uff01\u201d\u4ecd\u7136\u6447\u4e86\u6447\u5934\u3002\u51cc\u4e91\u51e4\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u591a\u6b7b\u4f60\u4e00\u4e2a\u4eba\u53c8\u4f55\u8865\u5e72\u4e8b\uff1f\u4f60\u82e5\u4e0d\u8d70\uff0c\u627f\u73e0\u59b9\u59b9\uff0c\u53ef\u8981\u62b1\u61be\u7ec8\u751f\uff0c\u4f60\u5c31\u5168\u4e0d\u4e3a\u5979\u7740\u60f3 \u4e48\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u6211\u77e5\u9053\u5979\u4f1a\u4e00\u65f6\u60b2\u75db\uff0c\u4f46\u5374\u53c8\u4f55\u81f3\u4e8e\u62b1\u61be\u7ec8\u751f\uff1f\u5979\u65e9\u5df2\u6709\u4e86\u610f\u4e2d\u4eba\uff0c\u6211 \u653e\u5fc3\u5f97\u5f88\uff01\u51cc\u4e0d\u98ce\u9053\uff1a\u201c\u4ec0\u4e48\u610f\u4e2d\u4eba\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u94c1\u955c\u5fc3\u6587\u6b66\u53cc\u5168\uff0c\u4e0e\u5979\u6b63\u662f\u4e00\u5bf9\u3002\u201d \u51cc\u4e91\u51e4\u9053\uff1a\u201c\u5440\uff0c\u4f60\u600e\u4e48\u8fd8\u4e0d\u77e5\u9053\u5979\u7684\u5fc3\uff0c\u6211\u4e0e\u5979\u59d0\u59b9\u60c5\u6df1\uff0c\u5979\u7eb5\u4e0d\u8bf4\u4e00\u53e5\u8bdd\uff0c\u6211\u4e5f\u5168\u77e5\u5979 \u7684\u5fc3\u4e8b\u3002\u4f55\u51b5\u5979\u8fd8\u5904\u5904\u900f\u9732\u51fa\u6765\u3002\u201d\u5f53\u4e0b\u5c06\u4e00\u4e9b\u81ea\u5df1\u89c2\u5bdf\u5165\u5fae\u7684\u5730\u65b9\u90fd\u4e00\u4e00\u8bf4\u4e86\uff0c\u751a\u81f3\u8fde\u4e8e \u627f\u73e0\u5728\u68a6\u4e2d\u66fe\u53eb\u8fc7\u53f6\u6210\u6797\u540d\u5b57\u7684\u4e8b\u4e5f\u8bf4\u4e86\u3002\u8981\u77e5\u51cc\u4e91\u51e4\u4f55\u7b49\u806a\u660e\uff0c\u4e8e\u627f\u73e0\u5f53\u65f6\u53eb\u5979\u5230\u5c6f\u6eaa\u53bb \u52a9\u53f6\u6210\u6797\uff0c\u8fc7\u540e\u4e0d\u4e45\uff0c\u5979\u5c31\u731c\u5230\u4e86\u4e8e\u627f\u73e0\u7684\u7528\u610f\uff0c\u90a3\u662f\u60f3\u5c06\u4ed6\u4eec\u64ae\u5408\u7684\u610f\u601d\u3002\u51cc\u4e91\u51e4\u600e\u4f1a\u9886 \u8fd9\u4e2a\u201c\u60c5\u201d\uff1f\u6240\u4ee5\u5728\u6b64\u523b\u751f\u6b7b\u5173\u5934\uff0c\u5979\u4e00\u5b9a\u8981\u529d\u53f6\u6210\u6797\u9003\u8d70\uff0c\u4ee5\u62a5\u59d0\u59b9\u7684\u77e5\u5df1\u4e4b\u5fc3\u3002 \u3000\u3000\u53f6\u6210\u6797\u542c\u4e86\u51cc\u4e91\u51e4\u7684\u8bdd\uff0c\u9ed8\u9ed8\u56de\u60f3\uff0c\u4e8e\u627f\u73e0\u5bf9\u767d\u5df1\u679c\u7136\u662f\u4e07\u7f15\u67d4\u60c5\uff0c\u5728\u8fc7\u53bb\u867d\u4f3c\u82e5\u5373\u82e5 \u79bb\uff0c\u4f46\u7ec6\u7ec6\u60f3\u6765\uff0c\u5374\u8fd8\u662f\u53ef\u4ee5\u4ece\u5fc3\u574e\u7684\u6df1\u5904\u611f\u5230\u3002 \u3000\u3000\u6708\u5149\u900f\u8fc7\u7e41\u679d\u5bc6\u53f6\uff0c\u4f46\u89c1\u51cc\u4e91\u51e4\u53cc\u7709\u7d27\u7ad6\uff0c\u7126\u7684\u7684\u795e\u60c5\u4ece\u773c\u5149\u4e2d\u90fd\u8868\u9732\u51fa\u6765\uff0c\u53f6\u6210\u6797\u7d27 \u7d27\u63e1\u7740\u5979\u98a4\u6296\u7684\u624b\u6307\uff0c\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u51cc\u59d0\u59d0\uff0c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u4eba\u591a\u7a81\u56f4\uff0c\u5927\u662f\u4e0d\u6613\uff0c\u4f60\u667a\u52c7\u53cc \u5168\uff0c\u8f7b\u529f\u8d8a\u5353\uff0c\u8fd8\u662f\u8d81\u8fd9\u673a\u4f1a\uff0c\u4f60\u8d70\u4e86\u5427\uff01\u55ef\uff0c\u4f60\u89c1\u4e86\u627f\u73e0\uff0c\u66ff\u6211\u3001\u66ff\u6211\u95ee\u5019\u5979\u3002\u53eb\u5979\u3001\u53eb \u5979\u4e0d\u8981\u518d\u60f3\u5ff5\u6211\u4e86\u3002\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u5728\u5916\u9762\u6ca1\u6709\u7275\u6302\u7684\u4eba\uff0c\u8fd8\u662f\u4f60\u81ea\u5df1\u8d70\u5427\u3002\u201d\u53f6\u6210 \u6797\u9053\uff1a\u201c\u5728\u5916\u9762\uff0c\u6211\u53ea\u6302\u5ff5\u5979\u4e00\u4e2a\u4eba\uff1b\u4f46\u5728\u8fd9\u91cc\uff0c\u5374\u6709\u6211\u9700\u8981\u987e\u5168\u7684\u5343\u591a\u5144\u5f1f\uff0c\u51cc\u59d0\u59d0\uff0c\u4e0d \u8981\u518d\u8bf4\u4e86\uff0c\u8d76\u5feb\u8d70\u5427\u3002\u201d \u3000\u3000\u542c\u4e86\u8fd9\u6837\u7684\u53e3\u6c14\uff0c\u51cc\u4e91\u51e4\u77e5\u9053\u662f\u518d\u4e5f\u529d\u4e0d\u52a8\u7684\u4e86\u3002\u5979\u7d20\u6027\u521a\u5f3a\uff0c\u5373\u7b97\u9047\u5230\u4e86\u6781\u4f24\u5fc3\u4e4b \u4e8b\uff0c\u4e5f\u4e0d\u80af\u5728\u4eba\u524d\u6d41\u6cea\uff0c\u8fd9\u65f6\u5374\u4e0d\u81ea\u7981\u5730\u6c81\u51fa\u4e86\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u624d\u662f\u9876\u5929\u7acb\u5730 \u7684\u82f1\u96c4\u6c14\u6982\uff0c\u4e0d\u6789\u627f\u73e0\u59b9\u59b9\u7231\u4ed6\u4e00\u573a\u3002\u5440\uff0c\u6211\u5728\u5916\u9762\u4f55\u5c1d\u6ca1\u6709\u7275\u6302\u7684\u4eba\uff1f\u4f46\u5374\u4e0d\u77e5\u4ed6\u662f\u5426\u5c1a \u6d3b\u5728\u4e16\u95f4\uff1f\u82e5\u8fd8\u6d3b\u7740\uff0c\u53c8\u4e0d\u77e5\u9053\u4ed6\u53d8\u5f97\u600e\u4e48\u6837\u4e86\uff1f\u201d\u970d\u5929\u90fd\u7684\u5f71\u5b50\u518d\u4e00\u6b21\u5728\u5979\u8111\u6d77\u4e2d\u6d6e\u73b0\uff0c \u201c\u4f46\u613f\u4ed6\u80fd\u50cf\u53f6\u6210\u6797\u90a3\u6837\u5730\u575a\u5f3a\uff0c\u7eb5\u7136\u6ca1\u6709\u4e86\u6211\u4e5f\u80fd\u591f\u72ec\u521b\u4e00\u5bb6\u3002\u201d\u60f3\u5230\u8fd9\u91cc\uff1a\u751c\u751c\u4e00\u7b11\uff0c \u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u53f6\u5927\u54e5\uff0c\u4f60\u4e0d\u80af\u8d70\uff0c\u6211\u4e5f\u4e0d\u8d70\u5566\u3002\u201d \u3000\u3000\u53f6\u6210\u6797\u5c06\u63e1\u7740\u5979\u7684\u624b\u8f7b\u8f7b\u653e\u5f00\uff0c\u76f8\u5904\u4e86\u8fd9\u4e48\u591a\u65f6\u65e5\uff0c\u4ed6\u4e5f\u77e5\u9053\u4e86\u51cc\u4e91\u51e4\u7684\u6027\u683c\uff0c\u6b63\u50cf\u4ed6 \u81ea\u5df1\u4e00\u7965\uff0c\u8bf4\u8fc7\u4e86\u7684\u8bdd\uff0c\u4ece\u4e0d\u80af\u6536\u56de\u3002\u9ed1\u6697\u4e2d\u4e24\u4eba\u9ed8\u9ed8\u76f8\u5bf9\uff0c\u4f46\u89c9\u8fd9\u79cd\u6218\u53cb\u7684\u60c5\u8c0a\uff0c\u73cd\u8d35\u5904 \u4e5f\u5b9e\u4e0d\u5728\u7231\u60c5\u4e4b\u4e0b\u3002 \u3000\u3000\u5c71\u4e0b\u7684\u5b98\u519b\u867d\u7136\u4e0d\u6562\u5f3a\u653b\uff0c\u4f46\u5374\u4e0d\u65f6\u5411\u5c71\u4e0a\u653e\u7bad\uff0c\u65f6\u5bc6\u65f6\u758f\uff0c\u6ca1\u6709\u505c\u8fc7\uff0c\u4e24\u4eba\u5728\u6797\u5b50\u91cc \u542c\u90a3\u7c41\u7c41\u7684\u5229\u7bad\u7834\u7a7a\u4e4b\u58f0\uff0c\u5fc3\u4e2d\u5747\u662f\u601d\u6f6e\u8d77\u4f0f\uff0c\u60f3\u7740\u5916\u9762\u81ea\u5df1\u6240\u5949\u6302\u7684\u4eba\uff0c\u60f3\u7740\u660e\u6668\u5c06\u6765\u5230 \u7684\u51b3\u6218\u3002 \u3000\u3000\u5ffd\u7136\u90a3\u7bad\u96e8\u7531\u5bc6\u800c\u758f\uff0c\u5ffd\u7136\u505c\u6b62\u4e86\u3002\u53f6\u6210\u6797\u6014\u4e86\u4e00\u6014\uff0c\u6b63\u8981\u51fa\u53bb\u4e86\u671b\uff0c\u5ffd\u89c1\u4e00\u6761\u9ed1\u5f71\u6251 \u5165\u6797\u4e2d\uff0c\u53f6\u6210\u6797\u624b\u6309\u4f69\u5200\uff0c\u5389\u58f0\u65a5\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u90a3\u9ed1\u5f71\u811a\u6b65\u4e0d\u505c\uff0c\u6765\u5f97\u6781\u5feb\uff0c\u500f\u5730\u5230\u4e86\u4e24 \u4eba\u9762\u524d\uff0c\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u662f\u6211\uff01\u201d \u3000\u3000\u6de1\u6708\u6d41\u661f\u4e4b\u4e0b\uff0c\u73b0\u51fa\u4e86\u90a3\u6e05\u79c0\u7684\u8138\u5e9e\uff0c\u53f6\u6210\u6797\u53eb\u9053\uff1a\u201c\u5440\uff0c\u94c1\u955c\u5fc3\uff0c\u662f\u4f60\uff01\u201d\u94c1\u955c\u5fc3 \u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9664\u4e86\u6211\u94c1\u955c\u5fc3\uff0c\u8c01\u8fd8\u6562\u5728\u8fd9\u65f6\u5019\u5230\u6765\uff1f\u201d \u3000\u3000\u51cc\u4e91\u51e4\u5b9a\u775b\u4e00\u770b\uff0c\u4f46\u89c1\u4ed6\u8f7b\u88d8\u7f13\u5e26\uff0c\u4ecd\u7136\u662f\u90a3\u526f\u8d35\u4ecb\u516c\u5b50\u7684\u6d3e\u5934\uff0c\u8863\u670d\u4e0a\u6ca1\u6709\u4e00\u70b9\u8840 \u8ff9\uff0c\u5fc3\u4e2d\u5927\u7591\uff0c\u6309\u5251\u95ee\u9053\uff1a\u201c\u4f60\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u6211\u5e26\u4f60\u4eec\u7a81\u56f4\u51fa\u53bb\uff01\u201d\u53f6\u6210\u6797 \u9053\uff1a\u201c\u5b98\u519b\u600e\u4e48\u653e\u4f60\u4e0a\u6765\uff1f\u6bd5\u64ce\u5929\u4ed6\u89c1\u7740\u4f60\u6ca1\u6709\uff1f\u201d\u94c1\u955c\u5fc3\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u76f8\u4fe1\u6211\u4fbf\u968f\u6211\u6765\uff0c \u4e0d\u76f8\u4fe1\u6211\uff0c\u5c31\u4e0d\u5fc5\u591a\u8bf4\u3002\u6bd5\u64ce\u5929\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u503c\u5f97\u6211\u4e0e\u4ed6\u76f8\u89c1\uff1f\u201d\u51cc\u4e91\u51e4\u4e00\u58f0\u4e0d\u54cd\u5730\u77a7\u7740\u4ed6 \u7684\u773c\u775b\uff0c\u4f46\u89c9\u4ed6\u6709\u4e09\u5206\u6127\u610f\uff0c\u4e03\u5206\u50b2\u6001\uff0c\u8138\u4e0a\u7684\u795e\u8272\u975e\u5e38\u5947\u5f02\uff01 \u3000\u3000\u51cc\u4e91\u51e4\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u597d\uff0c\u94c1\u955c\u5fc3\uff0c\u6211\u76f8\u4fe1\u4f60\u3002\u4f46\u53ea\u60f3\u95ee\u4f60\u4e00\u53e5\uff1a\u4f60\u4e3a\u4ec0\u4e48\u5192\u6b64\u5927 \u9669\uff0c\u524d\u6765\u63f4\u6551\u6211\u4eec\u3002\u201d\u94c1\u955c\u5fc3\u51b7\u51b7\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53ef\u4e0d\u8981\u4f60\u4eec\u9886\u60c5\uff0c\u6211\u662f\u5b8c\u5168\u770b\u5728\u4e8e\u5c0f\u59d0 \u7684\u4efd\u4e0a\uff01\u201d\u8fd9\u7b11\u58f0\u4e2d\u4e5f\u6709\u51e0\u5206\u50b2\u610f\uff0c\u4f46\u66f4\u591a\u7684\u5374\u662f\u5185\u5fc3\u7684\u51c4\u51c9\u3002 \u3000\u3000\u539f\u6765\u90a3\u4e00\u665a\u94c1\u955c\u5fc3\u5728\u676d\u5dde\u5bb6\u4e2d\u5411\u5a04\u6850\u5b59\u6cc4\u6f0f\u4e86\u4e49\u519b\u7684\u519b\u60c5\uff0c\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u4fbf\u53d1\u89c9\u4e8e\u627f\u73e0 \u4e0d\u522b\u800c\u884c\uff0c\u53ea\u7559\u4e0b\u4e86\u4e00\u5c01\u8bc0\u522b\u7684\u4e66\u4fe1\uff0c\u90a3\u5c01\u4fe1\u8d23\u5907\u94c1\u955c\u5fc3\u51fa\u5356\u670b\u53cb\uff0c\u53d1\u8a93\u4ee5\u540e\u6c38\u4e0d\u518d\u4e0e\u4ed6\u89c1 \u9762\u3002\u94c1\u955c\u5fc3\u8bfb\u4e86\u8fd9\u4e00\u5c01\u4fe1\uff0c\u624d\u611f\u89c9\u5230\u4e8b\u60c5\u51fa\u4e4e\u81ea\u5df1\u60f3\u8c61\u4e4b\u5916\u5730\u4e25\u91cd\uff0c\u5fc3\u4e2d\u5148\u662f\u57cb\u6028\uff0c\u57cb\u6028\u4e8e \u627f\u73e0\u4e0d\u89e3\u4ed6\u7684\u6df1\u60c5\uff0c\u201c\u5440\uff0c\u8fd9\u4e00\u5207\u8fd8\u4e0d\u90fd\u662f\u4e3a\u4f60\uff01\u201d\u7ee7\u800c\u540e\u6094\uff0c\u4ed6\u540e\u6094\u7684\u5012\u4e0d\u662f\u56e0\u4e3a\u635f\u5bb3\u4e86 \u4e49\u519b\uff0c\u800c\u662f\u6015\u4e49\u519b\u8986\u8d25\u4e4b\u540e\uff0c\u5929\u4e0b\u82f1\u96c4\u4e5f\u4f1a\u50cf\u4e8e\u627f\u73e0\u90a3\u6837\u7684\u60f3\u6cd5\uff0c\u628a\u7f6a\u8fc7\u201c\u63a8\u201d\u5230\u4ed6\u7684\u5934 \u4e0a\uff0c\u201c\u8fd9\u7fa4\u4e4c\u5408\u4e4b\u4f17\uff0c\u672c\u6765\u5c31\u4e0d\u80fd\u62b5\u6321\u5b98\u519b\u7684\u56f4\u527f\uff0c\u6211\u6cc4\u4e0d\u6cc4\u6f0f\u519b\u60c5\uff0c\u4e5f\u6539\u53d8\u4e0d\u4e86\u4ed6\u4eec\u7684\u5fc5 \u8d25\u4e4b\u5c40\u3002\u4e0d\u8fc7\u4e8e\u59d0\u59d0\u65e2\u7136\u8fd9\u6837\u8d23\u5907\u6211\uff0c\u6211\u5012\u4e0d\u53ef\u4e0d\u8868\u660e\u5fc3\u8ff9\u4e86\u3002\u7eb5\u6559\u8eab\u6b7b\u540d\u88c2\uff0c\u6211\u4e5f\u8981\u5411\u5979 \u8bc1\u660e\u6211\u662f\u4e00\u4e2a\u82f1\u96c4\u3002\u201d\u7ec8\u800c\u60f3\u8d77\u4e86\u4e00\u4e2a\u5ff5\u5934\uff0c\u8981\u505a\u4e00\u4e2a\u4ee4\u4e8e\u627f\u73e0\u5d07\u62dc\u7684\u82f1\u96c4\uff0c\u51b3\u610f\u6765\u52a9\u53f6\u6210 \u6797\u8131\u9669\u3002 \u3000\u3000\u4ed6\u672c\u6765\u806a\u660e\uff0c\u7f16\u597d\u4e00\u5957\u8bf4\u8bcd\uff0c\u7d22\u6027\u5c31\u6295\u5230\u6d59\u6c5f\u5de1\u629a\u5f20\u9aa5\u7684\u519b\u4e2d\uff0c\u8fd9\u65f6\u6bd5\u64ce\u5929\u5df2\u7ecf\u6295\u964d\uff0c \u5f20\u9aa5\u7684\u5927\u519b\u6b63\u6307\u5411\u5c6f\u6eaa\u3002\u5f20\u9aa5\u662f\u4ed6\u7236\u4eb2\u94c1\u94b9\u7684\u5b66\u751f\uff0c\u8fd9\u6b21\u62c6\u6563\u4e49\u519b\uff0c\u62db\u964d\u6bd5\u64ce\u5929\u7b49\u4e8b\uff0c\u53c8\u90fd \u662f\u56e0\u4e3a\u5148\u4ece\u94c1\u955c\u5fc3\u5904\u5f97\u77e5\u4e86\u4e49\u519b\u7684\u519b\u60c5\uff0c\u8fd9\u624d\u80fd\u987a\u5229\u8fdb\u884c\u7684\u3002\u89c1\u94c1\u955c\u5fc3\u6295\u5230\uff0c\u81ea\u7136\u6536\u7eb3\uff0c\u51c6 \u5907\u5b8c\u5168\u201c\u5e73\u5b9a\u201d\u4e86\u201c\u53db\u4e71\u201d\u4e4b\u540e\uff0c\u7ed9\u94c1\u955c\u5fc3\u594f\u62a5\u4e00\u4e2a\u5927\u5927\u7684\u519b\u529f\u3002\u8fd9\u4e00\u665a\u5b98\u519b\u5c06\u53f6\u6210\u6797\u56f0\u5728 \u5c71\u4e0a\uff0c\u94c1\u955c\u5fc3\u4fbf\u5411\u5f20\u9aa5\u8bf7\u6c42\uff0c\u524d\u6765\u62db\u964d\u53f6\u6210\u6797\uff0c\u5f20\u9aa5\u679c\u7136\u4e00\u70b9\u90fd\u4e0d\u7591\u5fc3\uff0c\u8fd8\u7ed9\u4e86\u4ed6\u4e00\u5c01\u4eb2\u7b14 \u62db\u964d\u7684\u4fe1\u4ef6\u3002 \u3000\u3000\u53f6\u6210\u6797\u54ea\u91cc\u77e5\u9053\u94c1\u955c\u5fc3\u8fd9\u6837\u590d\u6742\u7684\u5fc3\u60c5\uff0c\u5fc3\u4e2d\u6b63\u5728\u5224\u65ad\u94c1\u955c\u5fc3\u7684\u6765\u610f\uff0c\u53ea\u542c\u5f97\u94c1\u955c\u5fc3\u7f13 \u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u82e5\u60f3\u8131\u9669\uff0c\u53ea\u6709\u4e24\u6761\u8def\u8d70\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u613f\u95fb\u5176\u8be6\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u7b2c\u4e00 \u6761\u8def\u662f\u50cf\u6bd5\u64ce\u5929\u90a3\u6837\u6295\u964d\u671d\u5ef7\uff0c\u5f20\u9aa5\u7b54\u5e94\u7ed9\u4e00\u4e2a\u6c34\u5e08\u63d0\u7763\u4f60\u505a\u3002\u558f\uff1f\u8fd9\u662f\u4ed6\u7684\u62db\u964d\u4fe1\u4ef6\u3002\u201d \u53f6\u6210\u6797\u52c3\u7136\u5927\u6012\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u5f53\u6211\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u3001\u201d\u94c1\u955c\u5fc3\u7eb5\u58f0\u5927\u7b11\uff0c\u4e00\u628a\u5c06\u90a3\u5c01\u62db\u964d\u4fe1\u626f\u5f97\u7a00\u70c2\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u4e0d\u662f\u50cf\u6bd5\u64ce\u5929 \u90a3\u6837\u6ca1\u6709\u9aa8\u5934\u7684\u5974\u624d\uff0c\u8981\u4e0d\u7136\u6211\u4e5f\u4e0d\u4f1a\u6765\u4e86\u3002\u4e0d\u8fc7\uff0c\u4f60\u4e5f\u4e0d\u662f\u5c06\u624d\uff0c\u4e3a\u4ec0\u4e48\u8981\u6b7b\u5b88\u5c6f\u6eaa\u4e00 \u5730\uff1f\u201d\u51cc\u4e91\u51e4\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u94c1\u516c\u5b50\uff0c\u4f60\u662f\u6765\u803b\u7b11\u54b1\u4eec\uff0c\u8fd8\u662f\u8bda\u5fc3\u52a9\u54b1\u4eec\u8131\u9669\uff1f\u4f60\u662f\u5c06 \u624d\uff0c\u7a81\u56f4\u4e4b\u540e\uff0c\u81ea\u4eec\u5949\u4f60\u505a\u5341\u516b\u7701\u7684\u5927\u9f99\u5934\u3002\u201d\u94c1\u955c\u5fc3\u5927\u7b11\u9053\uff0c\u201c\u6211\u7a00\u7f55\u505a\u4f60\u4eec\u7684\u5927\u9f99\u5934\uff1f \u6211\u65e9\u8bf4\u8fc7\uff0c\u5168\u662f\u770b\u5728\u4e8e\u5c0f\u59d0\u7684\u4efd\u4e0a\u3002\u201d\u51cc\u4e91\u51e4\u5b9e\u5728\u770b\u4e0d\u60ef\u94c1\u955c\u5fc3\u7684\u6c14\u7130\uff0c\u4f46\u4e3a\u4e86\u8981\u8ba9\u53f6\u6210\u6797 \u8131\u9669\uff0c\u5fcd\u6c14\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u5c31\u5411\u4f60\u8bf7\u6559\u9526\u56ca\u5999\u8ba1\uff01\u201d \u3000\u3000\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u4f60\u65e2\u4e0d\u613f\u6295\u964d\uff0c\u90a3\u4e48\u54b1\u4eec\u53ea\u6709\u8d70\u7b2c\u4e8c\u6761\u8def\uff0c\u4e58\u591c\u7a81\u56f4\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u5b98 \u519b\u91cd\u91cd\u56f4\u56f0\uff0c\u5c31\u7b97\u51b2\u5f97\u4e0b\u5c71\uff0c\u4e5f\u8fd8\u662f\u5728\u5b98\u519b\u5305\u56f4\u4e4b\u4e2d\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u6211\u81ea\u6709\u795e\u673a\u5999\u7b97\uff0c\u4f55 \u987b\u4f60\u4eec\u591a\u8651\uff1f\u4e00\u5207\u542c\u6211\u6307\u6325\uff0c\u7ba1\u4fdd\u4f60\u80fd\u7a81\u56f4\u4fbf\u662f\u3002\u201d\u51cc\u4e91\u51e4\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u627f\u73e0\u59b9\u59b9\u4e0d\u559c\u6b22 \u4ed6\uff0c\u4ed6\u5192\u4e86\u8fd9\u4e48\u5927\u7684\u5371\u9669\uff0c\u6765\u6551\u54b1\u4eec\u7a81\u56f4\uff0c\u672c\u6765\u4ee4\u4eba\u6b23\u4f69\uff0c\u4f46\u4ed6\u8fd9\u526f\u795e\u6c14\uff0c\u5374\u662f\u50cf\u6765\u65bd\u6069\u4f3c \u7684\uff0c\u90a3\u5374\u6559\u4eba\u53cd\u611f\u4e86\u3002\u54fc\uff0c\u8981\u4e0d\u662f\u4e3a\u4e86\u53f6\u5927\u54e5\u548c\u8fd9\u5343\u591a\u5f1f\u5144\uff0c\u6211\u5c31\u5b81\u613f\u6218\u6b7b\u4e5f\u4e0d\u53d7\u4ed6\u7684\u6069 \u60e0\u3002\u201d\u4f46\u89c1\u53f6\u6210\u6797\u62b1\u62f3\u65bd\u793c\u9053\uff1a\u201c\u4eca\u65d7\u5728\u6b64\uff0c\u4e00\u5207\u542c\u51ed\u516c\u5b50\u8c03\u5ea6\u3002\u201d\u6beb\u65e0\u6120\u8272\uff0c\u51cc\u4e91\u51e4\u6697\u6697 \u4f69\u670d\u4ed6\u7684\u6c14\u5ea6\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u63a5\u8fc7\u4ee4\u65d7\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5c71\u540e\u6709\u6761\u5c0f\u8def\uff0c\u53ef\u4ee5\u76f4\u901a\u5a7a\u6e90\uff0c\u8fd9\u4e00\u8def\u5b98\u519b\u7684\u5175\u529b\u6700\u4e3a \u8584\u5f31\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u8fd9\u4e00\u6761\u8def\u5168\u662f\u5d0e\u5c96\u7684\u5c71\u8def\u3002\u6211\u5df2\u770b\u8fc7\u5730\u5f62\uff0c\u901a\u5411\u5916\u9762\u7684\u90a3\u6761\u5ce1\u8c37\u8346\u68d8 \u906e\u9053\uff0c\u751a\u4e0d\u6613\u8d70\uff0c\u53ea\u8981\u6709\u6570\u767e\u5b98\u519b\u627c\u5b88\u5bf9\u5c71\uff0c\u54b1\u4eec\u5c31\u90fd\u662f\u74ee\u4e2d\u4e4b\u9cd6\u3002\u201d\u94c1\u955c\u5fc3\u6120\u9053\uff1a\u201c\u5175\u6d01 \u6709\u4e91\uff1a\u4e34\u5371\u7528\u9669\uff0c\u53c8\u4e91\uff1a\u865a\u8001\u5b9e\u4e4b\uff0c\u5b9e\u8001\u865a\u4e4b\u3002\u5b98\u519b\u5c31\u56e0\u4e3a\u6599\u5230\u4f60\u4eec\u4e0d\u6562\u4ece\u8fd9\u6761\u8def\u7a81\u56f4\uff0c\u6240 \u4ee5\u624d\u4e0d\u5b89\u7f6e\u91cd\u5175\u3002\u5176\u4ed6\u51e0\u6761\u8def\u662f\u597d\u8d70\u5f97\u591a\uff0c\u4f46\u90fd\u4f0f\u4e0b\u4e86\u6570\u5343\u5f13\u7bad\u624b\u4e0e\u6320\u94a9\u624b\uff0c\u51f6\u9669\u66f4\u751a\u3002\u597d \u5427\uff0c\u4f9d\u4e0d\u4f9d\u4ece\u6211\u7684\u8ba1\u7b56\uff0c\u5168\u90fd\u542c\u4f60\u3002\u201d\u539f\u6765\u94c1\u955c\u5fc3\u5728\u5f20\u9aa5\u7684\u6155\u4e2d\uff0c\u5b98\u519b\u8fdb\u519b\u7684\u8ba1\u5212\uff0c\u4ed6\u90fd\u4e86 \u5982\u6307\u638c\uff0c\u201c\u5175\u6cd5\u201d\u4e91\u4e91\uff0c\u4e0d\u8fc7\u662f\u4ed6\u6545\u610f\u70ab\u8000\u624d\u534e\uff0c\u8981\u60f3\u6298\u670d\u53f6\u6210\u6797\u7f62\u4e86\u3002 \u3000\u3000\u53f6\u6210\u6797\u53cc\u76ee\u70af\u70af\uff0c\u8fc7\u4e86\u534a\u523b\uff0c\u65bd\u793c\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u89c1\u8bc6\u4f4e\u5fae\uff0c\u611a\u8005\u591a\u8651\uff0c\u94c1\u516c\u5b50\u8bf7\u52ff\u89c1 \u602a\u3002\u201d\u53f6\u6210\u6797\u806a\u660e\u5185\u8574\uff0c\u89c1\u94c1\u955c\u5fc3\u80fd\u591f\u4ece\u5b98\u519b\u90a3\u8fb9\u4ece\u5bb9\u8d70\u6765\uff0c\u4e5f\u731c\u5230\u4e86\u4ed6\u5fc5\u5b9a\u662f\u5229\u7528\u4ed6\u7236\u4eb2 \u7684\u5173\u7cfb\uff0c\u4e0e\u5b98\u519b\u5c06\u9886\u7ed3\u7eb3\uff0c\u77e5\u6089\u4e86\u5b98\u519b\u7684\u5175\u529b\u90e8\u7f72\u3002\u518d\u7ec6\u60f3\u94c1\u955c\u5fc3\u7684\u4e3a\u4eba\uff0c\u4e0d\u50cf\u662f\u5351\u9119\u5c0f \u4eba\uff0c\u6240\u4ee5\u624d\u4fe1\u4efb\u4ed6\u3002\u81f3\u4e8e\u94c1\u955c\u5fc3\u66fe\u6cc4\u6f0f\u4e49\u519b\u519b\u60c5\u4e4b\u4e8b\uff0c\u4ed6\u662f\u505a\u68a6\u4e5f\u6ca1\u6709\u60f3\u5230\u3002\u4e0d\u8fc7\uff0c\u8fd9\u4e00\u6b21 \u4ed6\u4fe1\u4efb\u94c1\u955c\u5fc3\u5374\u662f\u505a\u5f97\u5bf9\u4e86\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u89c1\u53f6\u6210\u6797\u4f4e\u58f0\u4e0b\u6c14\uff0c\u8fd9\u624d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u6709\u591a\u5c11\u6218\u9a6c\uff0c\u90fd\u96c6\u4e2d\u8d77\u6765\u3002\u5269 \u4e0b\u7684\u6b8b\u519b\u8d25\u5352\uff0c\u4e5f\u90fd\u805a\u96c6\u8d77\u6765\uff0c\u51c6\u5907\u51fa\u53d1\u3002\u201d\u53ef\u601c\u4e49\u519b\u56e0\u4e3a\u7f3a\u7cae\uff0c\u5bb0\u9a6c\u5145\u9965\uff0c\u5269\u4e0b\u7684\u6218\u9a6c\u4e0d \u8fc7\u4e09\u5341\u6765\u5339\u4e86\u3002\u94c1\u955c\u5fc3\u4e0b\u4ee4\u624e\u8d77\u4e86\u51e0\u5341\u4e2a\u7a3b\u8349\u4eba\uff0c\u90fd\u7f1a\u5728\u9a6c\u80cc\u4e0a\uff0c\u6bcf\u5339\u9a6c\u90fd\u7528\u4e00\u6761\u957f\u7ef3\u7cfb \u4f4f\uff0c\u7f1a\u5728\u6811\u4e0a\u3002\u4e34\u8d70\u4e4b\u65f6\uff0c\u5c06\u7ef3\u5b50\u7684\u4e00\u7aef\u70b9\u71c3\uff0c\u4e00\u5343\u4e49\u519b\u4fbf\u6084\u6084\u5730\u4ece\u5c71\u8c37\u4e4b\u4e2d\u51fa\u8d70\u3002 \u3000\u3000\u90a3\u6761\u5ce1\u8c37\u8346\u68d8\u906e\u9053\uff0c\u751a\u662f\u96be\u8d70\uff0c\u94c1\u955c\u5fc3\u62d4\u51fa\u5e08\u7236\u5077\u81ea\u5927\u5185\u7684\u90a3\u628a\u7d2b\u8679\u5b9d\u5251\uff0c\u594b\u52c7\u5f00\u8def\uff0c \u5251\u5149\u970d\u970d\uff0c\u8f6c\u77ac\u95f4\u4fbf\u62d4\u9664\u4e86\u4e00\u5927\u7247\u8346\u68d8\uff0c\u4ed6\u7684\u957f\u8863\u4e5f\u88ab\u94a9\u70c2\uff0c\u624b\u6307\u811a\u6307\u90fd\u6dcc\u51fa\u8840\u6765\uff0c\u51cc\u4e91\u51e4 \u89c1\u4ed6\u5982\u6b64\u5356\u529b\uff0c\u6c14\u4e5f\u6d88\u4e86\u4e00\u534a\uff0c\u6325\u821e\u53cc\u5251\uff0c\u5e2e\u4ed6\u5f00\u8def\uff0c\u94c1\u955c\u5fc3\u89c1\u4f17\u4eba\u670d\u4ed6\uff0c\u751a\u662f\u5f97\u610f\uff0c\u5fc3\u4e2d \u60f3\u9053\uff1a\u201c\u53ef\u60dc\u4e8e\u627f\u73e0\u4e0d\u5728\u8fd9\u513f\u3002\u5440\uff0c\u6211\u4eca\u65e5\u8fd9\u756a\u529f\u52b3\uff0c\u4e0d\u77e5\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u8bf4\u4e0e\u627f\u73e0\u77e5\u9053\u3002\u201d \u3000\u3000\u521a\u521a\u8d70\u51fa\u8c37\u53e3\uff0c\u53ea\u542c\u5f97\u540e\u9762\u9a6c\u5636\u4eba\u53eb\u3001\u6218\u9f13\u96f7\u9e23\uff0c\u56de\u5934\u4e00\u671b\uff0c\u4f46\u89c1\u6797\u5b50\u4e0a\u7a7a\u5df2\u5347\u8d77\u6d53 \u70df\uff0c\u5c04\u51fa\u706b\u7130\uff0c\u539f\u6765\u90a3\u4e9b\u7cfb\u9a6c\u7684\u957f\u7ef3\u4e00\u88ab\u70e7\u65ad\uff0c\u6218\u9a6c\u88ab\u706b\u707c\u75db\uff0c\u5728\u68ee\u6797\u91cc\u56db\u5904\u5954\u8dd1\uff0c\u54ea\u6d88\u7247 \u523b\uff0c\u4fbf\u71c3\u8d77\u4e86\u6570\u5341\u5904\u706b\u5934\u3002\u90a3\u51e0\u5341\u5339\u9a6c\u8d1f\u75db\u957f\u5636\uff0c\u70df\u817e\u706b\u8d77\uff0c\u58f0\u52bf\u4e4b\u58ee\uff0c\u7adf\u5982\u4e07\u9a6c\u5954\u817e\uff0c\u5343 \u519b\u8d74\u654c\uff01\u6797\u6df1\u6811\u5bc6\uff0c\u9ed1\u591c\u4e2d\u5b98\u519b\u54ea\u770b\u5f97\u6e05\u695a\uff0c\u4f46\u89c1\u9a6c\u80cc\u4e0a\u4eba\u5f71\u5e62\u5e62\uff08\u90a3\u662f\u8fd8\u672a\u70e7\u7740\u7684\u7a3b\u8349 \u4eba\uff09\uff0c\u53ea\u9053\u662f\u4e49\u519b\u5c31\u8981\u5f3a\u884c\u51b2\u51fa\uff0c\u65e0\u4e0d\u6212\u5907\u3002\u5b98\u519b\u7684\u7edf\u5e05\u5f20\u9aa5\uff0c\u4e43\u662f\u6df1\u901a\u5175\u6cd5\u7684\u4eba\uff0c\u60f3\u9053\uff1a \u201c\u7a77\u5bc7\u62fc\u6b7b\uff0c\u5f53\u907f\u5176\u950b\u3002\u201d\u4e0b\u4ee4\u5c06\u5f13\u7bad\u624b\u8c03\u5728\u524d\u5217\uff0c\u5200\u65a7\u624b\u4e0e\u6320\u94a9\u624b\u5728\u540e\u9762\u4e25\u9635\u76f8\u5f85\uff0c\u53ea\u5f85 \u4e49\u519b\u51b2\u4e0b\uff0c\u4fbf\u7528\u5bc6\u96c6\u7684\u7bad\u96e8\u5c04\u6563\u4ed6\u4eec\uff0c\u518d\u7528\u5200\u65a7\u624b\u3001\u6320\u94a9\u624b\u64d2\u62ff\u65a9\u6740\u3002\u54ea\u77e5\u8fc7\u4e86\u8bb8\u4e45\uff0c\u8fd8\u672a \u89c1\u6709\u4eba\u51b2\u51fa\u6765\uff0c\u5fc3\u4e2d\u751a\u662f\u5947\u602a\uff0c\u60f3\u9053\uff1a\u201c\u7a77\u5bc7\u653e\u706b\u70e7\u5c71\uff0c\u518d\u4e0d\u51b2\u51fa\uff0c\u96be\u9053\u5728\u91cc\u9762\u5750\u4ee5\u5f85\u6bd9 \u4e48\uff1f\u201d\u518d\u8fc7\u4e00\u4f1a\uff0c\u9a6c\u80cc\u4e0a\u7684\u7a3b\u8349\u4eba\u4e5f\u5c3d\u90fd\u7740\u706b\uff0c\u70e7\u5f97\u90a3\u4e9b\u6218\u9a6c\uff0c\u66f4\u662f\u6012\u53eb\u72c2\u5954\uff0c\u6709\u4e9b\u6218\u9a6c\u88ab \u70e7\u6b7b\u4e86\uff0c\u6709\u4e9b\u6218\u9a6c\u5728\u6811\u6797\u91cc\u6454\u5012\uff0c\u88ab\u540c\u4f34\u8df5\u8e0f\u6b7b\u4e86\uff0c\u8fd8\u6709\u5341\u591a\u5339\u6218\u9a6c\uff0c\u4e71\u51b2\u4e71\u95ef\uff0c\u5c45\u7136\u4ece\u5bc6 \u6797\u6df1\u5904\u51b2\u4e0b\u5c71\u6765\u3002\u8fd9\u65f6\u5b98\u519b\u624d\u53d1\u73b0\u5176\u4e2d\u7384\u5999\uff0c\u4f46\u8fd9\u65f6\u68ee\u6797\u4e2d\u4e5f\u70e7\u6210\u4e86\u4e00\u7247\u706b\u6d77\uff0c\u5b98\u519b\u65e0\u6cd5\u653b \u5c71\uff0c\u4e49\u519b\u4e5f\u65e9\u5c31\u4ece\u5c71\u540e\u7684\u5ce1\u8c37\u4e2d\u9003\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u9065\u671b\u706b\u5149\uff0c\u629a\u638c\u5927\u7b11\u3002\u53f6\u6210\u6797\u8d5e\u9053\uff1a\u201c\u53e4\u4ee3\u7530\u5355\u7528\u706b\u725b\u9635\u5927\u7834\u9f50\u519b\uff0c\u800c\u4eca\u94c1\u516c\u5b50 \u7528\u706b\u9a6c\u9635\u6270\u60d1\u654c\u4eba\uff0c\u963b\u6b62\u8ffd\u5175\uff0c\u4ece\u5bb9\u8131\u9669\u3002\u771f\u4e2a\u662f\u5148\u540e\u8f89\u6620\uff0c\u5999\u7b97\u795e\u673a\u3002\u201d\u94c1\u955c\u5fc3\u6d0b\u6d0b\u81ea \u5f97\uff0c\u4e00\u70b9\u4e5f\u4e0d\u8c26\u8ba9\uff0c\u5c06\u4f17\u4eba\u7684\u79f0\u8d5e\uff0c\u7167\u5355\u5168\u6536\uff0c\u7738\u773c\u56db\u987e\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u53f6\u6210\u6797\u6709\u4ec0\u4e48\u80fd \u4e3a\uff0c\u504f\u504f\u4e8e\u627f\u73e0\u5bf9\u4ed6\u90a3\u4e48\u8d4f\u8bc6\uff1f\u201d\u5176\u5b9e\u53f6\u6210\u6797\u5728\u5c6f\u6eaa\u72ec\u6297\u5b98\u519b\uff0c\u7cae\u5c3d\u63f4\u7edd\uff0c\u5c1a\u575a\u5b88\u4e86\u6570\u6708\u4e4b \u4e45\uff0c\u90a3\u624d\u662f\u5927\u5c06\u4e4b\u6750\u3002\u94c1\u955c\u5fc3\u81ea\u7136\u4e5f\u6709\u4ed6\u7684\u806a\u660e\u667a\u8ba1\uff0c\u8fd0\u7528\u5175\u6cd5\uff0c\u5076\u5c14\u4e5f\u80fd\u594f\u6548\uff0c\u4f46\u6bd4\u8d77\u53f6 \u6210\u6797\u6765\uff0c\u90a3\u5374\u662f\u4e00\u4e2a\u6df1\u85cf\uff0c\u4e00\u4e2a\u6d45\u9732\uff0c\u6709\u5982\u5927\u6d77\u4e4b\u4e0e\u5c0f\u6eaa\u4e86\u3002\u51cc\u4e91\u51e4\u51b7\u773c\u65c1\u89c2\uff0c\u770b\u51fa\u4e86\u4e24\u4eba \u4e0d\u540c\u7684\u98ce\u683c\uff0c\u5fc3\u4e2d\u4e0d\u4f4f\u5730\u79f0\u8d5e\u4e8e\u627f\u73e0\u5927\u6709\u773c\u5149\u3002 \u3000\u3000\u5929\u660e\u65f6\u5206\uff0c\u4e49\u519b\u5df2\u8fc7\u4e86\u5a7a\u6e90\uff0c\u4e00\u8def\u4e0a\u679c\u7136\u6ca1\u6709\u78b0\u8fc7\u5927\u961f\u7684\u5b98\u519b\uff0c\u53ea\u6709\u4e00\u4e9b\u5b88\u5728\u6cbf\u8def\u5821\u5792 \u4e0a\u7684\u5b98\u519b\u3002\u4ed6\u4eec\u4e0d\u6562\u51fa\u6765\u62e6\u622a\uff0c\u4e49\u519b\u4e5f\u4e0d\u53bb\u653b\u6253\u4ed6\u4eec\uff0c\u8fc7\u4e86\u5a7a\u6e90\uff0c\u524d\u9762\u5df2\u662f\u5e73\u9633\u5927\u9053\uff0c\u53f6\u6210 \u6797\u7b79\u601d\u518d\u4e09\uff0c\u8ffd\u5175\u53ea\u80fd\u6682\u963b\u4e00\u65f6\uff0c\u81ea\u5df1\u53ea\u5269\u4e0b\u4e00\u5343\u591a\u4eba\uff0c\u65ad\u4e0d\u80fd\u518d\u96c6\u7ed3\u4e00\u5904\uff0c\u4ee5\u81f4\u53c8\u9677\u91cd \u56f4\uff0c\u4e8e\u662f\u53ea\u597d\u6325\u6cea\u89e3\u6563\u4e49\u519b\uff0c\u53eb\u4ed6\u4eec\u5c3d\u901f\u5206\u5934\u9003\u8d70\uff0c\u5148\u6c42\u6027\u547d\u4fdd\u5168\uff0c\u7136\u540e\u5f90\u56fe\u540e\u8ba1\u3002 \u3000\u3000\u89e3\u6563\u4e86\u4e49\u519b\u4e4b\u540e\uff0c\u53f6\u6210\u6797\u3001\u51cc\u4e91\u51e4\u4e0e\u94c1\u955c\u5fc3\u4e09\u4eba\u518d\u6298\u5165\u5c71\u533a\uff0c\u53f6\u6210\u6797\u767b\u9ad8\u9065\u671b\uff0c\u6005\u89e6\u4e0d \u5df2\u3002\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u597d\u597d\u4e00\u573a\u4e8b\u4e1a\u90fd\u88ab\u6bd5\u64ce\u5929\u846c\u9001\u4e86\u3002\u201d\u94c1\u955c\u5fc3\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5728\u5927\u7406\u4e4b\u65f6\uff0c\u65e9 \u5df2\u65ad\u5b9a\u4f60\u4eec\u4e0d\u80fd\u6210\u4e8b\uff0c\u6709\u8bf4\u9519\u4e48\uff1f\u800c\u4eca\u6211\u529f\u6210\u8eab\u9000\uff0c\u4f46\u6c42\u4f60\u4eec\u4e00\u4ef6\u4e8b\u60c5\u2026\u2026\u201d \u3000\u3000\u53f6\u6210\u6797\u9053\uff1a\u201c\u8bf7\u94c1\u516c\u5b50\u5429\u5490\u3002\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u751f\u6050\u6015\u518d\u89c1\u4e0d\u5230\u4e8e\u627f\u73e0\u4e86\uff0c\u4f60\u82e5\u89c1 \u5230\u5979\u65f6\uff0c\u8bf7\u4ee3\u6211\u8f6c\u544a\u5979\u51e0\u53e5\u8bf4\u8bdd\u3002\u201d\u53f6\u6210\u6797\u6014\u4e86\u4e00\u6014\uff0c\u60f3\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u4ed6\u662f\u4e3a\u4e86\u4e8e\u627f \u73e0\uff01\u201d\u5fc3\u4e2d\u6709\u8bf4\u4e0d\u51fa\u7684\u6ecb\u5473\uff0c\u51cc\u4e91\u51e4\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u627f\u73e0\u82e5\u77e5\u9053\u4f60\u4eca\u65e5\u6240\u505a\u4e4b\u4e8b\uff0c\u5b9a\u7136\u6b22\u559c\uff0c \u4f60\u4eec\u672c\u6765\u5c31\u662f\u670b\u53cb\uff0c\u53c8\u4f55\u81f3\u4e8e\u6c38\u4e0d\u89c1\u9762\u3002\u597d\u5427\uff0c\u4f60\u6709\u4ec0\u4e48\u4e0d\u65b9\u4fbf\u8bf4\u7684\uff0c\u6211\u4ee3\u4f60\u8bf4\u4fbf\u662f\uff0c\u53ea\u8981 \u4e0d\u662f\u975e\u4efd\u4e4b\u6c42\uff0c\u60f3\u6765\u5979\u4f1a\u7b54\u5e94\u3002\u201d \u3000\u3000\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u4f60\u544a\u8bc9\u5979\uff0c\u5979\u6240\u5e0c\u671b\u6211\u505a\u7684\u4e8b\u60c5\uff0c\u7eb5\u7136\u662f\u6211\u4e0d\u613f\u610f\u505a\u7684\uff0c\u6211\u4e5f\u90fd\u505a\u4e86\uff0c\u4efb \u51ed\u5979\u5fc3\u4e2d\u60f3\u6211\u662f\u600e\u6837\u7684\u4eba\uff0c\u8fd9\u4e00\u70b9\u5fc3\u610f\uff0c\u5979\u5e94\u8be5\u77e5\u9053\u3002\u201d\u51cc\u4e91\u51e4\u542c\u4e86\uff0c\u6781\u4e0d\u8212\u670d\uff0c\u5fc3\u4e2d\u60f3 \u9053\uff1a\u201c\u539f\u6765\u4eca\u65e5\u4e4b\u4e8b\uff0c\u672c\u662f\u4ed6\u4e0d\u613f\u610f\u505a\u7684\u3002\u4ed6\u662f\u4e3a\u4e86\u5f97\u5230\u627f\u73e0\u59b9\u59b9\u7684\u5fc3\u3002\u54fc\uff0c\u8fd9\u4eba\u8c8c\u4f3c\u6e05 \u9ad8\uff0c\u5b9e\u662f\u5eb8\u4fd7\u5f97\u5f88\uff0c\u8fd9\u548c\u505a\u4e70\u5356\u53c8\u6709\u4ec0\u4e48\u5206\u522b\uff1f\u201d\u4f46\u60f3\u5230\u4ee5\u4ed6\u8fd9\u6837\u7684\u4eba\uff0c\u5c45\u7136\u80af\u5192\u9669\u63f4\u6551\u4e49 \u519b\uff0c\u4e5f\u7b97\u662f\u5f88\u96be\u5f97\u7684\u4e86\uff0c\u4e0d\u5fcd\u8ba5\u523a\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c06\u4f60\u8fd9\u4efd\u5fc3\u610f\u8f6c\u8fbe\u4fbf\u662f\u3002\u4f60\u8fd8\u6709\u4ec0 \u4e48\u8bf4\u8bdd\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u6211\u5e0c\u671b\u5979\u80fd\u591f\u5b89\u5b89\u9038\u9038\u5730\u8fc7\u4e00\u8f88\u5b50\uff0c\u4e0d\u8981\u518d\u5728\u6c5f\u6e56\u4e0a\u6df7\u4e86\u3002\u4e0d\u4f46\u50cf \u6bd5\u64ce\u5929\u8fd9\u6837\u7684\u4eba\uff0c\u5e94\u8be5\u8fdc\u8fdc\u907f\u5f00\uff0c\u4e0e\u671d\u5ef7\u4f5c\u5bf9\u7684\u4e8b\uff0c\u4e5f\u4ee5\u5c11\u6cbe\u60f9\u4e3a\u5999\u3002\u4e89\u738b\u4e89\u9738\u7684\u4e8b\uff0c\u90a3\u662f \u67ad\u96c4\u6240\u4e3a\uff0c\u5b9e\u975e\u5979\u8fd9\u6837\u7389\u8d28\u51b0\u5fc3\u7684\u5973\u513f\u6240\u9002\u5b9c\u505a\u3002\u201d\u51cc\u4e91\u51e4\u9762\u8272\u4e00\u6c89\uff0c\u5374\u539f\u6765\u94c1\u955c\u5fc3\u7684\u60f3\u6cd5 \u548c\u5979\u4eec\u7684\u8ddd\u79bb\u662f\u5982\u6b64\u4e4b\u8fdc\uff01 \u3000\u3000\u53f6\u6210\u6797\u9053\uff1a\u201c\u4e8e\u59d1\u5a18\u81ea\u6709\u4e3b\u5f20\uff0c\u4ec0\u4e48\u8be5\u505a\uff0c\u4ec0\u4e48\u4e0d\u8be5\u505a\uff0c\u5979\u4f1a\u61c2\u5f97\u3002\u4e0d\u8fc7\uff0c\u8fd9\u4e9b\u8bdd\u6211\u8fd8 \u662f\u4f1a\u7ed9\u4f60\u8f6c\u8fbe\u7684\u3002\u201d\u51cc\u4e91\u51e4\u8fd8\u60f3\u518d\u8bf4\uff0c\u5ffd\u89c1\u5c71\u5761\u90a3\u8fb9\u6765\u4e86\u5341\u51e0\u9a91\u5065\u9a6c\u3002 \u3000\u3000\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u4f60\u4eec\u8d70\u5427\uff0c\u6211\u662f\u53cd\u6b63\u8d70\u4e0d\u4e86\u7684\uff0c\u518d\u66ff\u4f60\u4eec\u4e00\u9000\u8ffd\u5175\u3002\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u54b1\u4eec \u751f\u6b7b\u540c\u5f53\uff0c\u60a3\u96be\u4e0e\u5171\uff0c\u8981\u4e0d\u8d70\u5c31\u5927\u5bb6\u4e0d\u8d70\u3002\u201d\u94c1\u955c\u5fc3\u53cc\u773c\u4e00\u7ffb\uff0c\u9053\uff1a\u201c\u4f60\u61c2\u5f97\u4ec0\u4e48\uff1f\u6211\u81ea\u6709 \u9000\u5175\u4e4b\u8ba1\uff0c\u4f60\u5e2e\u5f97\u4e86\u6211\u4e48\uff1f\u54fc\uff0c\u4f60\u6b7b\u4e0d\u6253\u7d27\uff0c\u627f\u73e0\u77e5\u9053\uff0c\u53ef\u53c8\u8981\u602a\u6211\u4e86\u3002\u201d \u3000\u3000\u53f6\u6210\u6797\u7ed9\u4ed6\u4e00\u987f\u62a2\u767d\uff0c\u53ea\u5f97\u8baa\u8baa\u8d70\u5f00\uff0c\u51cc\u4e91\u51e4\u4e5f\u53ea\u9053\u94c1\u955c\u5fc3\u4e0e\u5b98\u519b\u751a\u6709\u6e0a\u6e90\uff0c\u89c1\u4ed6\u8bf4\u5f97 \u751a\u6709\u628a\u63e1\uff0c\u4e5f\u50ac\u53f6\u6210\u6797\u5feb\u8d70\u3002\u4e24\u4eba\u5954\u8dd1\u4e86\u6570\u5341\u6b65\uff0c\u4f46\u542c\u5f97\u94c1\u955c\u5fc3\u7eb5\u58f0\u957f\u7b11\uff0c\u5df2\u5411\u524d\u8fce\u4e0a\u4e86\u5b98 \u519b\u3002 \u3000\u3000\u4ed6\u4eec\u54ea\u91cc\u77e5\u9053\u94c1\u955c\u5fc3\u590d\u6742\u7684\u5fc3\u60c5\uff0c\u4ed6\u8fd9\u6b21\u672c\u6765\u5c31\u6253\u7b97\u5b64\u6ce8\u4e00\u63b7\uff0c\u727a\u7272\u81ea\u5df1\uff0c\u4ee5\u6d17\u8131\u4e8e\u627f \u73e0\u5bf9\u81ea\u5df1\u7684\u9a82\u540d\u3002\u4f55\u51b5\u4ed6\u7684\u7236\u4eb2\u8fd8\u5728\u676d\u5dde\uff0c\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u613f\u4e0e\u53f6\u6210\u6797\u4e00\u9f50\u9003\u8dd1\u3002 \u3000\u3000\u6765\u7684\u6b63\u662f\u5927\u5185\u603b\u7ba1\u9633\u5b97\u6d77\u548c\u5fa1\u6797\u519b\u7684\u7edf\u9886\u5a04\u6850\u5b59\uff0c\u89c1\u94c1\u955c\u5fc3\u957f\u7b11\u800c\u6765\uff0c\u751a\u662f\u8be7\u5f02\uff0c\u9633\u5b97 \u6d77\u9053\uff1a\u201c\u53f6\u6210\u6797\u8fd9\u80a1\u6b8b\u532a\u600e\u4e48\u6837\u4e86\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u90fd\u88ab\u70e7\u6b7b\u5728\u5c71\u4e0a\u4e86\u3002\u201d \u3000\u3000\u5a04\u6850\u5b59\u9053\uff1a\u201c\u660e\u4eba\u9762\u524d\u4e0d\u8bf4\u5047\u8bdd\uff0c\u6211\u542c\u5f20\u5de1\u629a\u8bf4\uff0c\u662f\u4f60\u53bb\u62db\u964d\u4ed6\u4eec\uff0c\u4ed6\u4eec\u70e7\u6b7b\uff0c\u4e3a\u4ec0\u4e48 \u4f60\u53c8\u80fd\u72ec\u81ea\u9003\u51fa\uff1f\u201d\u94c1\u955c\u5fc3\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u660e\u4eba\u9762\u524d\u4e0d\u8bf4\u5047\u8bdd\uff0c\u90a3\u4e48\uff0c\u6211\u5c31\u544a\u8bc9\u4f60 \u4eec\uff0c\u4ed6\u4eec\u90fd\u7ed9\u6211\u653e\u8d70\u4e86\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u7ffb\u624b\u4e3a\u4e91\u8986\u624b\u96e8\uff0c\u4e66\u751f\u6c14\u8d28\u62a5\u7ea2\u989c\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e09\u5341\u516d\u56de\u3000\u4e91\u7834\u6708\u660e\u3000\u6c5f\u6e56\u7559\u5251\u5f71\u3000\u6c34\u6d41\u82b1\u8c22\u3000\u5404\u81ea\u4e86\u60c5\u7f18 \u6881\u7fbd\u751f\u300a\u6563\u82b1\u5973\u4fa0\u300b \u7b2c\u4e09\u5341\u516d\u56de\u3000\u4e91\u7834\u6708\u660e\u3000\u6c5f\u6e56\u7559\u5251\u5f71\u3000\u6c34\u6d41\u82b1\u8c22\u3000\u5404\u81ea\u4e86\u60c5\u7f18 \u3000\u3000\u9633\u5b97\u6d77\u5927\u5403\u4e00\u60ca\uff0c\u84e6\u5730\u53cc\u773c\u4e00\u7ffb\uff0c\u559d\u9053\uff1a\u201c\u90a3\u662f\u4e0d\u662f\u4ed6\u4eec\uff1f\u201d\u8fd9\u65f6\u53f6\u6210\u6797\u4e0e\u51cc\u4e91\u51e4\u5df2\u8f6c \u8fc7\u4e00\u5904\u5c71\u5773\uff0c\u53bb\u5f97\u8fdc\u4e86\u3002\u5a04\u6850\u5b59\u7b56\u9a6c\u4fbf\u8ffd\uff0c\u94c1\u955c\u5fc3\u95ea\u7535\u822c\u5730\u62d4\u51fa\u7d2b\u7ea2\u5b9d\u5251\uff0c\u53cd\u624b\u4e00\u6325\uff0c\u5a04\u6850 \u5b59\u4e00\u4e2a\u7b4b\u6597\u7ffb\u4e0b\u9a6c\u6765\uff0c\u53ea\u89c1\u90a3\u5339\u6218\u9a6c\u7684\u4e24\u6761\u524d\u817f\u5df2\u88ab\u94c1\u955c\u5fc3\u65a9\u65ad\uff0c\u5a04\u6850\u5b59\u5927\u6012\u559d\u9053\uff1a\u201c\u94c1\u955c \u5fc3\uff0c\u4f60\u5bb6\u4e16\u53d7\u7687\u6069\uff0c\u7adf\u7136\u7518\u5fc3\u9644\u9006\uff01\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u7518\u5fc3\u9644\u9006\u4e86\u3002\u201d\u5a04\u6850\u5b59\u9053\uff1a\u201c\u4f60 \u4e3a\u4ec0\u4e48\u653e\u8d70\u4ed6\u4eec\uff1f\u201d\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u5175\u6cd5\u6709\u4e91\uff1a\u56f0\u517d\u72b9\u6597\uff0c\u4e0d\u53ef\u4e0d\u9632\u3002\u4f60\u4eec\u8ffd\u5f97\u7d27\u4e86\uff0c\u53f6\u6210\u6797 \u53ef\u8981\u548c\u4f60\u4eec\u62fc\u547d\u3002\u54c8\uff0c\u6211\u662f\u4e0d\u5fcd\u89c1\u4f60\u4eec\u4e24\u8d25\u4ff1\u4f24\u3002\u540d\u5c06\u7528\u5175\uff0c\u4e5f\u8981\u8bb2\u7f51\u5f00\u4e00\u9762\uff0c\u53f6\u6210\u6797\u7684\u5175 \u529b\u90fd\u5df2\u6d88\u6563\uff0c\u653e\u8d70\u4ed6\u4eec\u4e00\u4e24\u4e2a\u4eba\u53c8\u7b97\u5f97\u4ec0\u4e48\uff1f\u201d\u9633\u5b97\u6d77\u9053\uff1a\u201c\u8c01\u4e0e\u4f60\u8bb2\u4ec0\u4e48\u9e1f\u5175\u6cd5\uff1f\u201d\u94c1\u955c \u5fc3\u80e1\u626f\u4e71\u9053\uff0c\u5b9e\u662f\u60f3\u5ef6\u963b\u65f6\u95f4\uff0c\u8fd9\u65f6\u4f30\u91cf\u53f6\u6210\u6797\u4e0e\u51cc\u4e91\u51e4\u5df2\u9003\u51fa\u6570\u91cc\u4e4b\u5916\uff0c\u9633\u5b97\u6d77\u4ed6\u4eec\u5c31\u662f \u8981\u8ffd\u4e5f\u8ffd\u4e0d\u4e0a\u3002\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u8bb2\u5c31\u4e0d\u8bb2\uff0c\u4f60\u4eec\u5374\u5f85\u5982\u4f55\uff1f\u201d\u5a04\u6850\u5b59\u4e00\u62db\u201c\u91d1\u8c79\u63a2\u722a\u201d\uff0c \u65bd\u5c55\u5927\u64d2\u62ff\u624b\u6cd5\u53cd\u6263\u94c1\u955c\u5fc3\u7684\u8109\u95e8\uff0c\u94c1\u955c\u5fc3\u7b11\u9053\uff1a\u201c\u541b\u5b50\u52a8\u53e3\u4e0d\u52a8\u624b\uff0c\u6211\u968f\u4f60\u4eec\u8d70\u4fbf\u662f\uff0c\u626f \u624b\u626f\u811a\u505a\u4ec0\u4e48\uff1f\u201d\u5012\u63d0\u5b9d\u5251\uff0c\u5c06\u5251\u67c4\u585e\u5230\u5a04\u6850\u5b59\u7684\u624b\u4e2d\uff0c\u5a04\u6850\u5b59\u53cd\u800c\u6014\u4e86\u4e00\u6014\uff0c\u6765\u4e0d\u53ca\u63a5\uff0c \u90a3\u628a\u7d2b\u8679\u5b9d\u5251\u53ee\u5f53\u4e00\u58f0\u8dcc\u843d\u5730\u4e0a\u3002\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u5927\u4e08\u592b\u4e00\u4eba\u505a\u4e8b\u4e00\u4eba\u5f53\uff0c\u662f\u6211\u653e\u4e86\u53f6\u6210\u6797\uff0c \u4f60\u62ff\u6211\u53bb\u89c1\u5f20\u9aa5\uff0c\u4e5f\u5c3d\u53ef\u4ee5\u4ea4\u5dee\u4e86\u5427\u3002\u8fd9\u628a\u5927\u5185\u5b9d\u5251\uff0c\u4e5f\u7531\u4f60\u62ff\u56de\u53bb\u7f34\u4ea4\u5185\u5e93\uff0c\u4f60\u4e00\u65e5\u4e4b \u95f4\uff0c\u7acb\u4e86\u4e24\u4ef6\u5927\u529f\uff0c\u5c1a\u4e0d\u723d\u5fc3\u5feb\u610f\u4e48\uff1f\u201d\u53cd\u624b\u5c31\u7f1a\uff0c\u5a04\u6850\u5b59\u56e0\u4ed6\u662f\u524d\u671d\u7684\u8001\u81e3\u4e4b\u5b50\uff0c\u5012\u4e5f\u4e0d \u6562\u8650\u5f85\u4e8e\u4ed6\u3002 \u3000\u3000\u534a\u4e2a\u6708\u540e\uff0c\u5b98\u519b\u201c\u52d8\u4e71\u201d\u7684\u519b\u4e8b\u5927\u5b9a\uff0c\u9003\u6563\u7684\u4e49\u519b\u90fd\u5df2\u85cf\u533f\u6c11\u95f4\uff0c\u53f6\u6210\u6797\u4e0e\u51cc\u4e91\u51e4\u50fb\u5c45 \u5728\u676d\u5dde\u5317\u9762\u6768\u6885\u5cad\u7684\u4e5d\u6eaa\u5341\u516b\u6da7\u4e4b\u95f4\u3002\u676d\u5dde\u4e43\u662f\u5f20\u9aa5\u7684\u5de1\u629a\u8859\u95e8\u6240\u5728\u4e4b\u5730\uff0c\u9a7b\u6709\u91cd\u5175\uff0c\u90a3\u4e5d \u6eaa\u5341\u516b\u6da7\u867d\u8bf4\u662f\u5c71\u4e2d\u7684\u50fb\u9759\u6240\u5728\uff0c\u4f46\u5730\u8fd1\u676d\u5dde\uff0c\u7ec8\u5c5e\u5371\u9669\uff0c\u53f6\u6210\u6797\u9009\u62e9\u8fd9\u4e2a\u5730\u65b9\u907f\u96be\uff0c\u5b9e\u662f \u53e6\u6709\u539f\u56e0\u3002 \u3000\u3000\u539f\u6765\u4ed6\u5df2\u6253\u63a2\u5230\u6d88\u606f\uff0c\u8bf4\u662f\u94c1\u955c\u5fc3\u5df2\u88ab\u56da\u5728\u676d\u57ce\uff0c\u7b49\u5019\u5fa1\u65e8\u53d1\u843d\u3002\u53f6\u6210\u6797\u751a\u662f\u4e0d\u5b89\uff0c\u4efb \u51ed\u65e7\u90e8\u82e6\u529d\uff0c\u4ed6\u600e\u6837\u4e5f\u4e0d\u80af\u8fdc\u8d70\u9ad8\u98de\uff0c\u975e\u5f97\u8981\u628a\u94c1\u955c\u5fc3\u6551\u51fa\u4e0d\u53ef\u3002\u51cc\u4e91\u51e4\u867d\u7136\u5bf9\u94c1\u955c\u5fc3\u6b8a\u65e0 \u597d\u611f\uff0c\u4f46\u60f3\u8d77\u4ed6\u8fd9\u6b21\u6551\u51fa\u4e00\u5343\u591a\u4e49\u519b\u7684\u529f\u52b3\uff0c\u4e5f\u5c31\u4e0d\u613f\u610f\u518d\u8bf4\u4ec0\u4e48\u4e86\u3002 \u3000\u3000\u53f6\u6210\u6797\u907f\u5c45\u5728\u4e00\u4e2a\u8336\u519c\u7684\u5bb6\u91cc\uff0c\u8fd9\u8336\u519c\u7684\u4e24\u4e2a\u513f\u5b50\u90fd\u66fe\u5f53\u8fc7\u4e49\u519b\uff0c\u7edd\u5bf9\u53ef\u9760\u3002\u53f6\u6210\u6797\u9760 \u8336\u519c\u6253\u63a2\u6d88\u606f\uff0c\u8bf4\u662f\u676d\u5dde\u5b88\u5907\u68ee\u4e25\uff0c\u94c1\u955c\u5fc3\u56da\u5728\u57ce\u4e2d\u4ec0\u4e48\u5730\u65b9\u4e5f\u4e0d\u77e5\u9053\u3002\u53f6\u6210\u6797\u4e0e\u51cc\u4e91\u51e4\u66fe \u4e24\u6b21\u5192\u9669\uff0c\u63a2\u8fc7\u676d\u5dde\u7684\u5927\u7262\u548c\u629a\u8859\uff0c\u975e\u4f46\u6ca1\u6709\u53d1\u73b0\u94c1\u955c\u5fc3\uff0c\u53cd\u800c\u51e0\u4e4e\u5931\u624b\uff0c\u4ed7\u7740\u7edd\u9876\u8f7b\u529f\uff0c \u8fd9\u624d\u9003\u5f97\u51fa\u6765\u3002\u5149\u9634\u4f3c\u7bad\uff0c\u4e0d\u77e5\u4e0d\u89c9\u53c8\u8fc7\u4e86\u5341\u6570\u65e5\uff0c\u8ba1\u7b97\u65f6\u95f4\uff0c\u82e5\u4ee5\u516b\u767e\u91cc\u52a0\u7d27\u7684\u5feb\u9a6c\u9a70 \u62a5\uff0c\u90a3\u5fa1\u65e8\u4e5f\u5e94\u8be5\u8bf7\u56de\u6765\u4e86\uff0c\u53f6\u6210\u6797\u548c\u51cc\u4e91\u51e4\u90fd\u6781\u4e3a\u7126\u6025\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u53f6\u6210\u6797\u5bf9\u51cc\u4e91\u51e4\u8bf4\u9053\uff1a\u201c\u5fa1\u65e8\u82e5\u7136\u53d1\u4e0b\uff0c\u53ea\u6709\u4e24\u4e2a\u53ef\u80fd\uff0c\u4e00\u662f\u6cbb\u4ed6\u4ee5\u53db\u9006\u4e4b\u7f6a\uff0c \u5c31\u5730\u5904\u51b3\u3002\u4e00\u662f\u5ff5\u4ed6\u662f\u8001\u81e3\u4e4b\u5b50\uff0c\u5c06\u4ed6\u89e3\u5f80\u4eac\u90fd\u5b9a\u7f6a\uff0c\u4f9d\u7167\u671d\u5ef7\u5f8b\u4f8b\uff0c\u6700\u5c11\u4e5f\u8981\u76d1\u7981\u4ed6\u5341 \u5e74\u3002\u5373\u7b97\u5f80\u597d\u5904\u60f3\uff0c\u4ed6\u7eb5\u5f97\u4fdd\u5168\u6027\u547d\uff0c\u4e00\u88ab\u76d1\u7981\u5927\u7262\uff0c\u90a3\u5c31\u66f4\u4e0d\u5bb9\u6613\u52ab\u72f1\u4e86\u3002\u201d\u51cc\u4e91\u51e4\u9053\uff1a \u201c\u54b1\u4eec\u5df2\u5c3d\u4e86\u5fc3\u529b\uff0c\u4e24\u6b21\u5192\u9669\u5165\u57ce\u591c\u63a2\uff0c\u90fd\u5f97\u4e0d\u5230\u4ed6\u7684\u6d88\u606f\uff0c\u8fd8\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a \u201c\u6211\u6b63\u5728\u5947\u602a\uff0c\u54b1\u4eec\u4e24\u6b21\u591c\u63a2\uff0c\u57ce\u4e2d\u867d\u8bf4\u7981\u536b\u68ee\u4e25\uff0c\u5374\u5e76\u65e0\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\u62e6\u622a\uff0c\u6bd5\u64ce\u5929\u9a7b\u5728 \u57ce\u4e2d\uff0c\u4e5f\u4ece\u4e0d\u89c1\u4ed6\u51fa\u73b0\uff0c\u4e0d\u77e5\u662f\u4f55\u9053\u7406\uff1f\u201d\u51cc\u4e91\u98ce\u9053\uff1a\u201c\u96be\u9053\u9633\u5b97\u6d77\u3001\u5a04\u6850\u5b59\u4e4b\u6d41\uff0c\u90fd\u53bb\u770b \u5b88\u94c1\u955c\u5fc3\u53bb\u4e86\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u4e2a\u53ef\u80fd\u3002\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u53ef\u80fd\uff1f\u201d\u53f6\u6210\u6797\u6c89 \u541f\u534a\u664c\uff0c\u8bf4\u6e32\uff1a\u201c\u57ce\u4e2d\u7ecf\u54b1\u4eec\u95f9\u4e86\u4e24\u6b21\u4e4b\u540e\uff0c\u542c\u8bf4\u672c\u8981\u641c\u7d22\u56db\u4e61\uff0c\u4f46\u81f3\u4eca\u672a\u6709\u52a8\u9759\uff0c\u83ab\u975e\u5f20 \u9aa5\u4ed6\u4eec\u53e6\u6709\u91cd\u5927\u7684\u4e8b\u60c5\u9700\u8981\u5bf9\u4ed8\uff1f\u201d\u51cc\u4e91\u98ce\u9053\uff1a\u201c\u8fd9\u4e0e\u94c1\u955c\u5fc3\u4f55\u5173\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u82e5\u7136\u662f \u88ab\u6211\u6599\u4e2d\uff0c\u54b1\u4eec\u6b63\u597d\u8d81\u6b64\u65f6\u673a\uff0c\u518d\u63a2\u4e00\u6b21\u3002\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u4e8b\u4e0d\u8fc7\u4e09\uff0c\u82e5\u7136\u8fd9\u6b21\u5931\u9677\uff0c\u6211\u4e0d \u6253\u7d27\uff0c\u4f60\u662f\u4e49\u519b\u4e3b\u5e05\u5440\uff0c\u5c82\u5e94\u518d\u6b21\u4e09\u756a\u5730\u5192\u9669\uff1f\u201d\u53f6\u6210\u6797\u9053\uff1a\u201c\u94c1\u955c\u5fc3\u4f55\u5c1d\u4e0d\u662f\u5192\u4e86\u6027\u547d\u4e4b \u9669\u63f4\u6551\u54b1\u4eec\u3002\u201d\u51cc\u4e91\u51e4\u76b1\u7709\u4e0d\u8bed\uff0c\u795e\u8272\u4e4b\u95f4\uff0c\u751a\u4e0d\u4ee5\u4e3a\u7136\u3002\u53f6\u6210\u6797\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7684\u60f3\u6cd5\u3002 \u60f3\u90a3\u94c1\u955c\u5fc3\u867d\u7136\u4e0d\u662f\u54b1\u4eec\u4e00\u8def\u7684\u4eba\uff0c\u4f46\u54b1\u4eec\u5e94\u8be5\u770b\u4ed6\u7684\u884c\u4e8b\uff0c\u4e0d\u5fc5\u52c9\u5f3a\u4ed6\u8d5e\u540c\u54b1\u4eec\u7684\u4e3b\u5f20\u3002 \u4ed6\u8fd9\u6b21\u7684\u884c\u4e8b\uff0c\u5b9e\u662f\u5bf9\u4e49\u519b\u6709\u6781\u5927\u7684\u6069\u5fb7\uff0c\u54b1\u4eec\u5c82\u53ef\u505a\u5fd8\u6069\u8d1f\u4e49\u7684\u4eba\uff1f\u201d\u51cc\u4e91\u51e4\u67f3\u7709\u4e00\u5c55\uff0c \u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u53bb\u5427\uff01\u201d\u5fc3\u4e2d\u81ea\u601d\uff1a\u201c\u53f6\u6210\u6797\u660e\u660e\u77e5\u9053\u94c1\u955c\u5fc3\u5b8c\u5168\u662f\u4e3a\u4e86\u4e8e\u627f\u73e0\uff0c\u5374\u8fd8\u8981\u4e24 \u6b21\u4e09\u756a\uff0c\u51c6\u5907\u820d\u4e86\u6027\u547d\uff0c\u6551\u4ed6\u51fa\u6765\uff0c\u76f8\u6bd4\u4e4b\u4e0b\uff0c\u5012\u663e\u5f97\u6211\u7684\u80f8\u895f\u72ed\u7a84\u4e86\u3002\u201d \u3000\u3000\u53f6\u6210\u6797\u9053\uff1a\u201c\u6211\u5df2\u63a2\u542c\u5f97\u94c1\u5bb6\u6240\u5728\uff0c\u542c\u8bf4\u94c1\u8001\u5fa1\u53f2\u8fd8\u5728\u5bb6\u4e2d\uff0c\u4e5f\u5df2\u4e0a\u4e86\u8bf7\u7f6a\u7684\u594f\u8868\uff0c\u5f20 \u9aa5\u662f\u4ed6\u7684\u5b66\u751f\uff0c\u4e0d\u6562\u5c06\u4ed6\u96be\u4e3a\uff0c\u5c31\u8ba9\u4ed6\u5728\u5bb6\u4e2d\u5f85\u7f6a\u3002\u54b1\u4eec\u8fd9\u6b21\u53ef\u4ee5\u5230\u94c1\u5bb6\u53bb\u63a2\u8bbf\u4e00\u4e0b\uff0c\u60f3\u90a3 \u94c1\u8001\u5fa1\u53f2\u5fc5\u4f1a\u77e5\u9053\u513f\u5b50\u7684\u6d88\u606f\uff0c\u4e5f\u8bb8\u4ed6\u5df2\u63a2\u8fc7\u76d1\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u201d\u51cc\u4e91\u51e4\u4e00\u60f3\uff0c\u5230\u94c1\u5bb6\u591c\u63a2\uff0c\u867d \u7136\u4e5f\u5c5e\u5192\u9669\uff0c\u7a76\u7adf\u4e0d\u82e5\u524d\u4e24\u6b21\u4e4b\u5927\u95f9\u629a\u8859\u548c\u5927\u7262\u7684\u98ce\u9669\u4e4b\u5927\uff0c\u6b23\u7136\u540c\u610f\uff0c\u7acb\u5373\u6362\u4e86\u591c\u884c\u670d \u88c5\uff0c\u548c\u53f6\u6210\u6797\u4ece\u57ce\u5317\u7684\u6816\u971e\u5cad\u6084\u6084\u6e9c\u4e0b\uff0c\u76f4\u5230\u897f\u5b50\u6e56\u8fb9\u3002 \u3000\u3000\u94c1\u5bb6\u5750\u843d\u6e56\u6ee8\uff0c\u9762\u5bf9\u5b64\u5c71\uff0c\u8fd9\u65f6\u5df2\u662f\u5348\u591c\u65f6\u5206\uff0c\u6e56\u6ee8\u9759\u6084\u6084\u7684\uff0c\u6e56\u4e0a\u7684\u6e14\u821f\u90fd\u5df2\u6b47\u6069 \u4e86\u3002\u4e24\u4eba\u8d70\u8fd1\u94c1\u5bb6\uff0c\u4f46\u89c1\u6731\u95e8\u7d27\u95ed\uff0c\u91cc\u9762\u7684\u706f\u706b\u4e5f\u5b8c\u5168\u7184\u706d\u4e86\u3002\u5468\u56f4\u4e5f\u6ca1\u6709\u5175\u628a\u5b88\uff0c\u53f6\u6210\u6797 \u5fc3\u4e2d\u6697\u53eb\u5947\u602a\uff0c\u7a0d\u4e00\u8e0c\u8e87\uff0c\u4fbf\u548c\u51cc\u4e91\u51e4\u98de\u8eab\u5165\u5185\u3002 \u3000\u3000\u4f46\u89c1\u91cc\u9762\u843d\u82b1\u6ee1\u5730\uff0c\u82b1\u68da\u5012\u584c\uff0c\u4e71\u8349\u4e5f\u65e0\u4eba\u526a\u7406\uff0c\u51b7\u6e05\u5f97\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\uff0c\u53f6\u6210\u6797\u5728\u5916\u9762 \u628a\u98ce\uff0c\u51cc\u4e91\u51e4\u7a7f\u623f\u5165\u5ba4\uff0c\u8fc7\u4e86\u597d\u4e45\uff0c\u51fa\u6765\u53eb\u9053\uff1a\u201c\u8fd9\u771f\u5947\u602a\u6781\u4e86\uff0c\u91cc\u9762\u4e00\u4e2a\u4eba\u4e5f\u6ca1\u6709\uff01\u201d \u3000\u3000\u53f6\u6210\u6797\u5947\u9053\uff1a\u201c\u96be\u9053\u94c1\u94f1\u7adf\u662f\u5f03\u5bb6\u9003\u8d70\u4e86\u4e48\uff1f\u201d\u7acb\u5373\u60f3\u5230\u94c1\u94f1\u662f\u4e00\u4e2a\u9000\u4f11\u7684\u5927\u81e3\uff0c\u513f\u5b50 \u72af\u4e86\u6cd5\uff0c\u867d\u8bf4\u5de1\u629a\u5f20\u9aa5\u662f\u4ed6\u7684\u5b66\u751f\uff0c\u5bf9\u4ed6\u5b58\u6709\u51e0\u5206\u5ba2\u6c14\uff0c\u4f46\u53d7\u5230\u6697\u4e2d\u76d1\u89c6\uff0c\u90a3\u662f\u5fc5\u7136\u514d\u4e0d\u4e86 \u7684\uff0c\u4ed6\u53c8\u662f\u4e00\u4e2a\u6587\u5b98\uff0c\u4e0d\u901a\u6b66\u827a\uff0c\u600e\u80fd\u8bf4\u9003\u4fbf\u9003\uff0c\u800c\u4e14\u53c8\u662f\u4e3e\u5bb6\u9003\u8d70\uff1f \u3000\u3000\u4e24\u4eba\u6b63\u81ea\u731c\u7591\u4e0d\u5b9a\uff0c\u5ffd\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u94c1\u5bb6\u7684\u5927\u95e8\u7ed9\u4eba\u6253\u7834\uff0c\u4e00\u4e2a\u4eba\u95ef\u4e86\u8fdb\u6765\uff0c\u53f6 \u6210\u6797\u4ee5\u4e3a\u662f\u671d\u5ef7\u6b66\u58eb\uff0c\u6025\u5fd9\u8df3\u4e0a\u5c4b\u9876\uff0c\u5b9a\u775b\u4e00\u671b\uff0c\u5374\u539f\u6765\u662f\u6f6e\u97f3\u548c\u5c1a\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u5012\u62d6\u7985\u6756\uff0c\u6ee1\u8eab\u8840\u6c61\uff0c\u8eab\u4e0a\u4e2d\u4e86\u51e0\u652f\u7bad\u8fd8\u672a\u62d4\u51fa\uff0c\u53f6\u6210\u6797\u5927\u5403\u4e00\u60ca\uff0c\u6f6e\u97f3\u548c\u5c1a\u5df2 \u5148\u53d1\u73b0\u4e86\u4ed6\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4e24\u4eba\u600e\u4e48\u4e5f\u5728\u8fd9\u513f\uff1f\u94c1\u94f1\u90a3\u8001\u5934\u513f\u5462\uff1f\u201d \u3000\u3000\u53f6\u6210\u6797\u548c\u51cc\u4e91\u51e4\u8df3\u4e0b\u6765\u4e0e\u4ed6\u76f8\u89c1\uff0c\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u6b63\u5728\u627e\u4ed6\uff0c\u8fd9\u91cc\u5374\u4e00\u4e2a\u4eba\u4e5f\u6ca1 \u6709\uff0c\u60f3\u5fc5\u662f\u5f03\u5bb6\u8d70\u4e86\uff0c\u6f6e\u97f3\u5927\u5e08\uff0c\u4f60\u600e\u4e48\u8fd9\u4e2a\u6837\u5b50\uff1f\u201d \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u6211\u53bb\u627e\u94c1\u955c\u5fc3\u4e86\u3002\u201d\u53f6\u6210\u6797\u53eb\u9053\uff1a\u201c\u89c1\u7740\u4e86\u6ca1\u6709\uff1f\u201d\u6f6e\u97f3\u9053\uff1a\u201c\u6ca1\u6709\u3002 \u524d\u51e0\u5929\u6211\u4ece\u94c1\u94f1\u8fd9\u8001\u5934\u513f\u53e3\u4e2d\uff0c\u6253\u542c\u51fa\u4ed6\u7684\u513f\u5b50\u662f\u88ab\u56da\u7981\u5728\u516d\u548c\u5854\u5185\uff0c\u6211\u5c31\u8981\u53bb\u52ab\u4ed6\u51fa\u6765\uff0c \u662f\u8fd9\u8001\u5934\u513f\u6b7b\u62c9\u7740\u6211\uff0c\u4e0d\u8bb8\u6211\u8fd9\u6837\u505a\u3002\u6211\u5fcd\u4e86\u51e0\u5929\uff0c\u5230\u4e86\u4eca\u5929\uff0c\u542c\u8bf4\u5fa1\u65e8\u5df2\u5230\uff0c\u518d\u4e0d\u6551\u4ed6\uff0c \u4ed6\u660e\u65e5\u5c31\u8981\u88ab\u89e3\u8fdb\u4eac\u4e86\u3002\u6211\u4e0d\u7406\u4e00\u5207\uff0c\u4e5f\u4e0d\u613f\u518d\u4e0e\u8fd9\u8001\u5934\u51e0\u5546\u91cf\uff0c\u51c6\u5907\u4e00\u987f\u7985\u6756\u6253\u788e\u4e86\u516d\u548c \u5854\uff0c\u5c06\u4ed6\u513f\u5b50\u6551\u4e86\u51fa\u6765\uff0c\u518d\u8ba9\u4ed6\u6b22\u559c\u3002\u54ea\u77e5\u516d\u548c\u5854\u91cc\u867d\u5173\u6709\u51e0\u4e2a\u4eba\uff0c\u5374\u6ca1\u6709\u94c1\u955c\u5fc3\uff0c\u767d\u767d\u7ed9 \u6211\u6253\u6b7b\u4e86\u51e0\u4e2a\u536b\u58eb\u3002\u201d \u3000\u3000\u53f6\u6210\u6797\u9053\uff1a\u201c\u5e08\u4f2f\u7956\uff0c\u4f60\u4e14\u6b47\u6b47\u3002\u201d\u51cc\u4e91\u51e4\u4e0a\u524d\u7ed9\u4ed6\u62d4\u7bad\u88f9\u4f24\uff0c\u95ee\u9053\uff1a\u201c\u9633\u5b97\u6d77\u548c\u5a04\u6850 \u5b59\u5728\u516d\u548c\u5854\u90a3\u8fb9\u4e48\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u5927\u624b\u4e00\u6325\uff0c\u9053\uff1a\u201c\u522b\u5fd9\u88f9\u4f24\uff0c\u8d76\u5feb\u9003\u8d70\uff01\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u6211 \u4eec\u5df2\u7ec6\u5fc3\u5bdf\u770b\u8fc7\u4e86\uff0c\u5916\u9762\u6ca1\u6709\u4f0f\u5175\u3002\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u5916\u9762\u6ca1\u6709\u4f0f\u5175\uff0c\u57ce\u4e2d\u7684\u5b98\u519b\u5374\u6b63\u5728\u5df7 \u6218\uff01\u201d\u3002\u53f6\u6210\u6797\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u5df7\u6218\uff1f\u662f\u54ea\u8def\u4eba\u9a6c\u548c\u5b98\u519b\u5df7\u6218\uff1f\u201d \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u6211\u5206\u8fa8\u4e0d\u6e05\uff0c\u4e5f\u4e0d\u8010\u70e6\u53bb\u6253\u542c\uff0c\u5413\uff0c\u6211\u5927\u95f9\u4e86\u516d\u548c\u5854\u540e\uff0c\u627e\u4e0d\u5230\u94c1\u955c \u5fc3\uff0c\u8d8a\u60f3\u8d8a\u6c14\uff0c\u60f3\u8fd9\u4e00\u5207\u90fd\u662f\u4e3a\u4e86\u6bd5\u64ce\u5929\u800c\u8d77\uff0c\u4fbf\u72ec\u81ea\u53bb\u95ef\u6bd5\u64ce\u5929\u7684\u5927\u8425\uff0c\u54c8\uff0c\u54ea\u77e5\u6b63\u78b0\u4e0a \u4e24\u519b\u4ea4\u6218\uff0c\u5728\u4e71\u519b\u4e4b\u4e2d\uff0c\u6211\u5403\u4e86\u65e0\u6570\u4e71\u7bad\uff0c\u8fde\u6bd5\u64ce\u5929\u7684\u5f71\u5b50\u4e5f\u6ca1\u89c1\u7740\u3002\u597d\u5728\u6211\u8fd9\u6839\u7985\u6756\u8fd8\u591f \u65a4\u4e24\uff0c\u4e00\u987f\u6cfc\u98ce\u7985\u6756\uff0c\u6253\u51fa\u57ce\u6765\uff0c\u90a3\u4e9b\u5b98\u519b\uff0c\u81ea\u987e\u53ae\u6740\uff0c\u4e5f\u6ca1\u6709\u4eba\u8ffd\u6211\uff01\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5df2\u662f\u6709\u70b9\u58f0\u5636\u529b\u7aed\uff0c\u53f6\u6210\u6797\u5fc3\u9053\uff1a\u201c\u5e08\u4f2f\u7956\u771f\u662f\u4e2a\u83bd\u548c\u5c1a\uff01\u201d\u51cc\u4e91\u51e4\u521a\u521a\u7ed9 \u4ed6\u62d4\u6389\u8eab\u4e0a\u7684\u90a3\u51e0\u652f\u65ad\u7bad\uff0c\u8fd8\u60f3\u95ee\u4ed6\uff0c\u6f6e\u97f3\u548c\u5c1a\u53c8\u53eb\u9053\uff1a\u201c\u7094\u8d70\uff0c\u5feb\u8d70\uff01\u6211\u6b7b\u4e0d\u4e86\uff0c\u4f46\u5927\u519b \u82e5\u7136\u6765\u5230\u8fd9\u513f\uff0c\u6211\u53ef\u6ca1\u6c14\u529b\u518d\u6253\u5566\u3002\u201d\u8bdd\u521a\u8bf4\u5b8c\uff0c\u4fbf\u542c\u5f97\u57ce\u4e2d\u4f20\u6765\u51e0\u58f0\u95f7\u96f7\u4e5f\u4f3c\u7684\u70ae\u54cd\u3002\u53f6 \u6210\u6797\u3001\u51cc\u4e91\u51e4\u6025\u5fd9\u6276\u6301\u6f6e\u97f3\u548c\u5c1a\u8d70\u51fa\u94c1\u5bb6\uff0c\u4f46\u542c\u5f97\u6218\u9a6c\u5636\u9e23\uff0c\u4e00\u5f6a\u5b98\u519b\u5df2\u51b2\u5230\u897f\u5b50\u6e56\u8fb9\u3002 \u3000\u3000\u53f6\u6210\u6797\u4e00\u770b\uff0c\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u5728\u524d\u9762\u72fc\u72c8\u800c\u9003\u7684\u7adf\u7136\u662f\u6bd5\u64ce\u5929\uff01\u4f46\u89c1\u4ed6\u9a6c\u5931 \u978d\uff0c\u4eba\u5f03\u7532\uff0c\u5728\u4ed6\u5468\u56f4\u4fdd\u62a4\u7684\u536b\u58eb\uff0c\u4e0d\u8fc7\u4e8c\u4e09\u5341\u9a91\u3002\u540e\u9762\u7684\u5927\u961f\u5b98\u519b\u5982\u6f6e\u6d8c\u81f3\uff0c\u9886\u5934\u7684\u4fbf\u662f \u5927\u5185\u603b\u7ba1\u9633\u5b97\u6d77\u4e0e\u5fa1\u6797\u519b\u7684\u603b\u6307\u6325\u5a04\u6850\u5b59\uff0c\u4f46\u542c\u5f97\u5406\u559d\u58f0\u4e2d\uff0c\u5f13\u5982\u9739\u96f7\uff0c\u7bad\u4f3c\u5f26\u60ca\uff0c\u9633\u5b97\u6d77 \u201c\u55d6\u201d\u7684\u4e00\u7bad\uff0c\u5c06\u6bd5\u64ce\u5929\u8de8\u4e0b\u7684\u9ec4\u9aa0\u9a6c\u5148\u5c04\u6b7b\u4e86\uff01 \u3000\u3000\u539f\u6765\u671d\u5ef7\u7684\u62db\u5b89\u6bd5\u64ce\u5929\uff0c\u4e0d\u8fc7\u662f\u6743\u5b9c\u4e4b\u8ba1\uff0c\u4ed6\u8981\u6c42\u6700\u5c11\u505a\u4e00\u7701\u7684\u7763\u629a\uff0c\u6b63\u72af\u4e86\u7687\u5e1d\u4e4b \u5fcc\uff0c\u60f3\u8fd9\u6bd5\u64ce\u5929\u91ce\u5fc3\u52c3\u52c3\uff0c\u7687\u5e1d\u600e\u80af\u8ba9\u4ed6\u636e\u5730\u81ea\u96c4\uff1f\u6240\u4ee5\u7687\u5e1d\u5728\u62db\u5b89\u6bd5\u64ce\u5927\u7684\u540c\u65f6\uff0c\u5c31\u4e0b\u4e86 \u4e00\u9053\u5bc6\u4ee4\u7ed9\u5b98\u519b\u7684\u7edf\u5e05\u6d59\u6c5f\u5de1\u629a\u5f20\u9aa5\uff0c\u5bc6\u4ee4\u4ed6\u5728\u201c\u53db\u4e71\u201d\u52d8\u5e73\u4e4b\u65f6\uff0c\u5373\u9010\u6e10\u89e3\u9664\u6bd5\u64ce\u5929\u7684\u5175 \u6743\uff0c\u6700\u540e\u5c06\u4ed6\u62ff\u5230\u4eac\u5e08\u95ee\u7f6a\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u8c8c\u4f3c\u7c97\u8c6a\uff0c\u5b9e\u662f\u5de5\u4e8e\u5fc3\u8ba1\uff0c\u5b98\u519b\u7684\u8fd9\u4e00\u756a\u5e03\u7f6e\uff0c\u4ed6\u77a7\u5728\u773c\u91cc\uff0c\u6697\u81ea\u751f\u7591\uff0c\u5230\u4e86\u676d \u57ce\u4e4b\u540e\uff0c\u6bd5\u64ce\u5929\u7684\u90e8\u5c5e\u5341\u4e5d\u5df2\u88ab\u6539\u7f16\uff0c\u8c03\u9a7b\u5404\u5730\uff0c\u800c\u671d\u5ef7\u5bf9\u4ed6\u7684\u5c01\u8d4f\u53c8\u53e3\u60e0\u800c\u5b9e\u4e0d\u81f3\uff0c\u6bd5\u64ce \u5929\u4ee5\u524d\u541e\u5e76\u53f6\u5b97\u7559\u4e4b\u65f6\uff0c\u4e5f\u662f\u5c06\u4ed6\u7684\u5ae1\u7cfb\u90e8\u961f\u8c03\u5f00\uff0c\u7136\u540e\u4e3e\u4e8b\u7684\uff0c\u800c\u4eca\u5b98\u519b\u5bf9\u4ed8\u4ed6\u7684\u624b\u6cd5\uff0c \u5c31\u6b63\u4e0e\u4ed6\u4ee5\u524d\u5bf9\u4ed8\u53f6\u5b97\u7559\u7684\u624b\u6cd5\u4e00\u6a21\u4e00\u6837\uff0c\u4ed6\u9759\u591c\u601d\u91cf\uff0c\u7109\u5f97\u4e0d\u60ca\uff1f \u3000\u3000\u4e8e\u662f\u6bd5\u64ce\u5929\u5bf9\u5f20\u9aa5\u5904\u5904\u6212\u5907\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u66f4\u4ee4\u5f97\u5f20\u9aa5\u4e0d\u80fd\u4e0d\u52a0\u5feb\u52a8\u624b\uff0c\u8fd9\u4e00\u65e5\u5f20\u9aa5\u8981\u4ed6 \u8d74\u4eac\u9762\u594f\u7687\u4e0a\u5173\u4e8e\u8fd9\u6b21\u201c\u5e73\u4e71\u201d\u7684\u7ecf\u8fc7\uff0c\u6bd5\u64ce\u5929\u63a8\u75c5\uff0c\u8fde\u5f20\u9aa5\u6d3e\u6765\u7684\u4f7f\u8005\u4e5f\u4e0d\u80af\u63a5\u89c1\u3002\u5f20\u9aa5 \u5927\u6012\uff0c\u4fbf\u7acb\u5373\u6d3e\u5175\u653b\u6253\u4ed6\uff0c\u8d23\u4ed6\u4ee5\u6297\u547d\u4e4b\u7f6a\uff0c\u4e0d\u6d88\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5c31\u5c06\u6bd5\u64ce\u5929\u6709\u9650\u7684\u4eb2\u5175\u5168\u90e8\u6d88 \u706d\uff0c\u6bd5\u64ce\u5929\u603b\u7b97\u6b66\u529f\u9ad8\u5f3a\uff0c\u5728\u6570\u5341\u500d\u5b98\u519b\u5305\u56f4\u4e4b\u4e2d\uff0c\u5c45\u7136\u8fd8\u80fd\u591f\u5e26\u9886\u5341\u591a\u4e2a\u536b\u58eb\uff0c\u51b2\u51fa\u57ce \u95e8\uff0c\u9003\u5230\u897f\u5b50\u6e56\u8fb9\u3001 \u3000\u3000\u9633\u5b97\u6d77\u4e00\u7bad\u5c06\u6bd5\u64ce\u5929\u7684\u6218\u9a6c\u5c04\u6b7b\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u671d\u5ef7\u6709\u547d\uff0c\u53ea\u7f6a\u6bd5\u64ce\u5929\u4e00\u4eba\uff0c\u8c01\u4eba\u80fd\u5c06 \u4ed6\u751f\u64d2\u7684\u8d4f\u4ee5\u9ec4\u91d1\u5343\u4e24\uff0c\u5b98\u5c01\u603b\u5175\uff1b\u5c06\u4ed6\u683c\u6bd9\u7684\uff0c\u4e5f\u8d4f\u4e09\u767e\u4e24\u9ec4\u91d1\uff0c\u4e94\u54c1\u9876\u6234\uff01\u201d\u6b64\u8a00\u4e00 \u51fa\uff0c\u767b\u65f6\u6709\u4e24\u4e2a\u968f\u884c\u536b\u58eb\u53cd\u6208\u76f8\u5411\uff0c\u4e58\u7740\u6bd5\u64ce\u5929\u8fd8\u6ca1\u6709\u8dc3\u8d77\uff0c\u4e24\u652f\u957f\u77db\uff0c\u7acb\u523b\u523a\u4e0b\u3002\u6bd5\u64ce\u5929 \u6b66\u529f\u771f\u4e2a\u9ad8\u5f3a\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u628a\u4e24\u652f\u957f\u77db\u683c\u5f00\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u6211\u5f85\u4f60\u7b49\u4e0d\u8584\uff0c\u4f55\u6545\u4e34\u5371\u53db \u6211\uff1f\u201d\u62fe\u8d77\u72fc\u7259\u94c1\u68d2\uff0c\u4e00\u62db\u201c\u6a2a\u626b\u5e72\u519b\u201d\uff0c\u53c8\u5c06\u53e6\u5916\u4e24\u6839\u523a\u6765\u7684\u94c1\u67aa\u6253\u6298\uff0c\u8fd9\u51e0\u4e2a\u536b\u58eb\u7d20\u77e5 \u6bd5\u64ce\u5929\u6709\u9738\u738b\u4e4b\u52c7\uff0c\u4e00\u6765\u4e3a\u4e86\u81ea\u8eab\u6d3b\u547d\uff0c\u4e8c\u6765\u4e3a\u4e86\u8d2a\u56fe\u91cd\u8d4f\uff0c\u4e09\u6765\u89c1\u6bd5\u64ce\u5929\u88ab\u5c04\u5760\u9a6c\uff0c\u8fd9\u624d \u6562\u53cd\u6208\u76f8\u5411\uff0c\u6697\u88ad\u4e0d\u6210\uff0c\u4e2a\u4e2a\u60ca\u5fc3\uff0c\u62fc\u4e86\u4e00\u6b7b\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u53f6\u7edf\u9886\u4ee5\u524d\u4e5f\u5bf9\u4f60\u4e0d\u8584\uff0c\u4f60\u53c8 \u4f55\u6545\u53cd\u4ed6\uff1f\u201d \u3000\u3000\u6bd5\u64ce\u5929\u6014\u4e86\u4e00\u6014\uff0c\u7a81\u7136\u6012\u53eb\u4e00\u58f0\uff0c\u72fc\u7259\u68d2\u72e0\u72e0\u5288\u4e0b\uff0c\u5c06\u4e24\u4e2a\u53cd\u53db\u7684\u536b\u58eb\uff0c\u6253\u5f97\u5934\u9885\u788e \u88c2\uff0c\u968f\u884c\u7684\u536b\u58eb\u53d1\u4e00\u58f0\u558a\uff0c\u5c3d\u90fd\u6563\u4e86\u3002\u6bd5\u64ce\u5929\u53d1\u529b\u72c2\u5954\uff0c\u51b2\u8fc7\u4e86\u897f\u51b7\u6865\uff0c\u9003\u4e0a\u5b64\u5c71\uff0c\u5b98\u519b\u8854 \u5c3e\u6025\u9053\uff0c\u7bad\u5982\u96e8\u843d\uff01 \u3000\u3000\u8fd9\u65f6\uff0c\u53f6\u6210\u6797\u3001\u51cc\u4e91\u51e4\u4e0e\u6f6e\u97f3\u548c\u5c1a\u4e09\u4eba\u4e5f\u5df2\u9003\u5230\u5c71\u4e0a\uff0c\u4f46\u89c1\u5b98\u519b\u6492\u7f51\u4f3c\u7684\uff0c\u56db\u65b9\u516b\u9762\u800c \u6765\uff0c\u6f6e\u97f3\u548c\u5c1a\u5468\u8eab\u53d7\u4e86\u5341\u51e0\u5904\u7bad\u4f24\uff0c\u8df3\u8dc3\u4e0d\u4fbf\uff0c\u53f6\u6210\u6797\u62c9\u7740\u4ed6\u8d70\uff0c\u51cc\u4e91\u51e4\u5fc3\u6025\u5982\u711a\uff0c\u8fde\u58f0\u50ac \u9053\uff1a\u201c\u5feb\u8d70\uff0c\u5feb\u8d70\uff01\u201d\u8981\u77e5\u53f6\u6210\u6797\u82e5\u88ab\u5b98\u519b\u53d1\u73b0\uff0c\u5728\u5b98\u519b\u7684\u5fc3\u76ee\u4e4b\u4e2d\uff0c\u81ea\u662f\u6bd4\u6bd5\u64ce\u5929\u8fd8\u8981\u91cd \u8981\u5f97\u591a\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u66f4\u662f\u4e00\u4e2a\u5fc3\u6025\u7684\u4eba\uff0c\u7adf\u7136\u6323\u8131\u4e86\u53f6\u6210\u6797\u7684\u624b\uff0c\u9053\uff1a\u201c\u6211\u8fd8\u4f1a\u8dd1\u8def\uff0c\u4e0d\u5fc5\u52b3\u4f60\u62db \u547c\u3002\u201d\u53f6\u6210\u6797\u60f3\u4e0d\u5230\u8fd9\u4f4d\u83bd\u5e08\u4f2f\u7956\u5982\u6b64\u8981\u5f3a\uff0c\u751a\u662f\u5c34\u5c2c\uff0c\u6f6e\u97f3\u548c\u5c1a\u594b\u8d77\u795e\u529b\uff0c\u679c\u7136\u4e00\u9f13\u4f5c \u6c14\uff0c\u8dd1\u8fc7\u4e86\u51e0\u5904\u5c71\u5773\uff0c\u76f4\u5230\u4e86\u5cb3\u738b\u5e99\u540e\u7684\u6816\u971e\u5cad\u4e0a\u3002\u9ed1\u591c\u4e4b\u4e2d\uff0c\u5c71\u8def\u5d0e\u5c96\uff0c\u5ffd\u7136\u78b0\u5230\u4e86\u4e00\u5757 \u5927\u77f3\uff0c\u6f6e\u97f3\u548c\u5c1a\u594b\u5200\u4e00\u8dc3\uff0c\u811a\u8e1d\u8131\u81fc\uff0c\u8eab\u4e0a\u7684\u7bad\u4f24\u521b\u53e3\u4e5f\u88c2\u5f00\u6765\uff0c\u4efb\u4ed6\u5982\u4f55\u9a81\u52c7\uff0c\u4e5f\u81ea\u62b5\u53d7 \u4e0d\u4f4f\uff0c\u201c\u535c\u901a\u201d\u5012\u5730\uff0c\u600e\u6837\u6323\u624e\u4e5f\u7ad9\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u53f6\u6210\u6797\u6025\u5fd9\u5c06\u4ed6\u6276\u8d77\uff0c\u6f6e\u9752\u548c\u5c1a\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u8d70\u5427\uff0c\u5c71\u4e0a\u8fd9\u4e48\u5927\uff0c\u5b98\u519b\u672a\u5fc5\u5c31\u627e\u5f97\u5230 \u6211\u3002\u201d\u53f6\u6210\u6797\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u4ed6\u4eec\u4e5f\u672a\u5fc5\u627e\u5f97\u5230\u6211\u3002\u201d\u4e0d\u7531\u5206\u8bf4\uff0c\u5c06\u6f6e\u97f3\u548c\u5c1a\u6276\u5230\u4e00\u5757\u5927\u5ca9\u77f3 \u7684\u540e\u9762\uff0c\u51cc\u4e91\u51e4\u4e00\u770b\uff0c\u53ea\u89c1\u4ed6\u5341\u51e0\u5904\u4f24\u53e3\uff0c\u90fd\u5728\u6c69\u6c69\u6d41\u8840\uff0c\u5fc3\u4e2d\u751a\u662f\u62b1\u6b49\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u4e0b\u5b98 \u519b\u5206\u80a1\u641c\u5c71\uff0c\u7eb5\u7b97\u7ed9\u4ed6\u627e\u5230\uff0c\u5c0f\u80a1\u5b98\u519b\uff0c\u4e5f\u4e0d\u653e\u5728\u54b1\u4eec\u5fc3\u4e0a\u3002\u6f6e\u97f3\u5927\u5e08\u6211\u5148\u66ff\u4f60\u88f9\u4f24\u3002\u201d\u4ece \u5c71\u4e0a\u671b\u4e0b\uff0c\u4f46\u89c1\u706b\u628a\u5a49\u8712\uff0c\u7edc\u7ece\u4e0d\u7edd\uff0c\u597d\u5728\u4ed6\u4eec\u5148\u641c\u5b64\u5c71\uff0c\u8fd8\u6ca1\u6709\u6765\u5230\u6816\u971e\u5cad\u4e0a\u3002 \u3000\u3000\u53f6\u6210\u6797\u60e6\u8bb0\u7740\u94c1\u955c\u5fc3\uff0c\u4e00\u9762\u66ff\u6f6e\u97f3\u548c\u5c1a\u88f9\u4f24\uff0c\u4e00\u9762\u95ee\u9053\uff1a\u201c\u5e08\u4f2f\u7956\uff0c\u4f60\u600e\u77e5\u9053\u94c1\u516c\u5b50\u843d \u5728\u5b98\u519b\u624b\u4e2d\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u6211\u4e00\u76f4\u4f4f\u5728\u94c1\u955c\u5fc3\u7684\u5bb6\u4e2d\u5462\u3002\u51cc\u5973\u4fa0\u548c\u4e8e\u627f\u73e0\u90a3\u6b21\u884c\u523a\u6bd5 \u64ce\u5929\u7684\u4e8b\uff0c\u6211\u5168\u90e8\u77e5\u9053\u3002\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u4e0d\u662f\u884c\u523a\uff0c\u662f\u4e8e\u59d0\u59d0\u7528\u8ba1\u8981\u8feb\u6bd5\u64ce\u5929\u4ea4\u51fa\u5175\u7b26\uff0c\u8c03 \u52a8\u7cae\u8349\uff0c\u63a5\u6d4e\u53f6\u5927\u54e5\u3002\u540e\u6765\u4e8e\u59d0\u59d0\u8981\u6211\u81ea\u53bb\u5c6f\u6eaa\uff0c\u5979\u5927\u7ea6\u662f\u72ec\u81ea\u56de\u53bb\u6551\u94c1\u955c\u5fc3\u4e86\u3002\u201d\u6f6e\u97f3\u548c \u5c1a\u9053\uff1a\u201c\u4e0d\u9519\u3002\u5979\u5c06\u94c1\u955c\u5fc3\u6551\u51fa\u4e4b\u540e\uff0c\u6070\u597d\u9047\u89c1\u6211\uff0c\u6211\u4eec\u4e00\u9053\u8d74\u4eac\u3002\u201d\u53f6\u6210\u6797\u5fd9\u95ee\u9053\uff1a\u201c\u6211 \u542c\u6bd5\u613f\u7a77\u8bf4\uff0c\u4ed6\u5728\u5317\u4eac\u5df2\u89c1\u5230\u4e8e\u627f\u73e0\uff0c\u600e\u4e48\u4f60\u548c\u94c1\u955c\u5fc3\u5374\u7559\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u6b63\u662f\u5462\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec\u5c11\u5e74\u4eba\u95f9\u7684\u4ec0\u4e48\u4e8b\u60c5\u3002\u94c1\u955c\u5fc3\u5012\u662f\u5904\u5904\u62a4\u7740\u4e8e \u627f\u73e0\u7684\uff0c\u4e8e\u627f\u73e0\u5374\u6765\u4e00\u4e2a\u548c\u4ed6\u4e0d\u8f9e\u800c\u884c\u3002\u201d\u53f6\u6210\u6797\u5fc3\u91cc\u53c8\u751c\u53c8\u9178\uff0c\u60f3\u9053\uff1a\u201c\u54ce\uff0c\u5728\u5916\u4eba\u773c\u4e2d \u770b\u6765\uff0c\u4ed6\u4eec\u90fd\u662f\u5929\u9020\u5730\u8bbe\u7684\u4e00\u5bf9\u3002\u94c1\u955c\u5fc3\u8fd9\u6b21\u53c8\u6709\u6069\u4e8e\u6211\uff0c\u6211\u5c82\u53ef\u63d2\u5728\u4ed6\u4eec\u4e2d\u95f4\u3002\u201d\u5fc3\u5982\u8f98 \u8f73\uff0c\u60c5\u601d\u4e0d\u5b9a\uff0c\u4f46\u542c\u51cc\u4e91\u98ce\u95ee\u9053\uff1a\u201c\u90a3\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u6211\u4eec\u4e09\u4eba\u4e00\u540c\u4e0a\u4eac\uff0c \u8def\u8fc7\u676d\u5dde\uff0c\u94c1\u955c\u5fc3\u575a\u8bf7\u6211\u548c\u627f\u73e0\u5728\u4ed6\u5bb6\u91cc\u5148\u6b47\u606f\u51e0\u5929\uff0c\u6211\u6709\u4e00\u4f4d\u65b9\u5916\u7684\u670b\u53cb\u5728\u7075\u9690\u5bfa\u505a\u4e3b \u6301\uff0c\u90a3\u4e00\u65e5\u6211\u5230\u7075\u9690\u5bfa\u8bbf\u4ed6\uff0c\u5728\u5bfa\u4e2d\u4f4f\u4e86\u4e00\u665a\uff0c\u7b2c\u4e8c\u65e5\u56de\u5230\u94c1\u5bb6\uff0c\u8fd9\u624d\u77e5\u9053\u4e8e\u627f\u73e0\u5df2\u5728\u6628\u665a \u5077\u5077\u8d70\u4e86\uff0c\u53ea\u7559\u4e0b\u4e00\u5c01\u4fe1\u7ed9\u94c1\u955c\u5fc3\u3002\u94c1\u955c\u5fc3\u8bb2\u7ed9\u6211\u542c\u7684\u65f6\u5019\uff0c\u624b\u4e0a\u8fd8\u62ff\u7740\u4e8e\u627f\u73e0\u5199\u7684\u90a3\u51e0\u5f20 \u4fe1\u7b3a\uff0c\u54c8\uff0c\u4e8e\u627f\u73e0\u4e0d\u77e5\u600e\u4e48\u6709\u90a3\u4e48\u591a\u8bdd\u8bf4\uff0c\u4fe1\u5199\u5f97\u90a3\u4e48\u957f\u3002\u54c8\uff0c\u4f60\u731c\u94c1\u955c\u5fc3\u8fd9\u50bb\u5c0f\u5b50\u600e \u6837\uff1f\u201d \u3000\u3000\u51cc\u4e91\u51e4\u542c\u5f97\u5947\u602a\uff0c\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u6837\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u4ed6\u628a\u90a3\u51e0\u5f20\u4fe1\u7b3a\uff0c\u56e2\u6210\u4e00\u56e2\uff0c\u541e \u5230\u809a\u5185\u53bb\u4e86\uff01\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u61c2\u5440\u3002\u8fd8\u6709\u66f4\u53e4\u602a\u7684 \u5462\uff0c\u4ed6\u628a\u4fe1\u541e\u4e86\u4e4b\u540e\uff0c\u7adf\u50cf\u5973\u5b69\u5b50\u4e00\u6837\u54ed\u4e86\u8d77\u6765\u3002\u201d\u51cc\u4e91\u51e4\u9053\uff1a\u201c\u54ed\u4e9b\u4ec0\u4e48\uff1f\u201d\u5fc3\u60f3\u94c1\u955c\u5fc3 \u6b64\u4eba\u771f\u4f1a\u505a\u4f5c\u3002\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u4ed6\u53cd\u53cd\u590d\u590d\u5730\u53ea\u662f\u8bf4\u4e00\u53e5\u8bdd\uff0c\u8bf4\u662f\u5bf9\u4e0d\u8d77\u4e8e\u59d1\u5a18\uff0c\u8bf4\u662f\u4e8e\u59d1 \u5a18\u4e0d\u8c05\u89e3\u5730\u3002\u6211\u8bf4\u5c11\u5e74\u4eba\u5435\u5435\u95f9\u95f9\uff0c\u4e8b\u5c5e\u5bfb\u5e38\uff0c\u5f85\u8001\u8872\u66ff\u4f60\u529d\u8bf4\u5979\u4fbf\u662f\u3002\u4ed6\u8bb8\u4e45\u4e0d\u8bed\uff0c\u5374\u5ffd \u7136\u5411\u8001\u8872\u884c\u8d77\u5927\u793c\u6765\u3002\u201d\u51cc\u4e91\u51e4\u7b11\u9053\uff1a\u201c\u8fd9\u5374\u662f\u4e3a\u4f55\uff1f\u201d\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u4ed6\u8bf4\u4ed6\u4e3a\u4e86\u4e8e\u59d1\u5a18 \u8981\u5e72\u4e00\u6869\u5927\u4e8b\uff0c\u52a1\u5fc5\u8981\u4ee4\u5f97\u4e8e\u59d1\u5a18\u79f0\u5fc3\u6ee1\u610f\u3002\u4f46\u4ed6\u8fd9\u4e00\u53bb\u53ea\u6015\u5c31\u6b64\u4e0d\u80fd\u56de\u6765\uff0c\u6258\u8001\u8872\u7167\u987e\u4ed6 \u7684\u8001\u7236\uff0c\u6211\u95ee\u4ed6\u662f\u4ec0\u4e48\u4e8b\u60c5\u4ed6\u4e0d\u7740\u8bf4\uff0c\u5440\uff0c\u5982\u4eca\u6211\u624d\u77e5\u9053\u4ed6\u662f\u72ec\u4e0a\u5c6f\u6eaa\u4e3a\u4e49\u519b\u5c3d\u529b\u53bb\u7684\u3002\u201d \u3000\u3000\u53f6\u6210\u6797\u542c\u4e86\u4e0d\u80dc\u611f\u52a8\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e0d\u77e5\u4ed6\u4e0e\u627f\u73e0\u4e4b\u95f4\u6709\u4ec0\u4e48\u8bef\u4f1a\uff1f\u54ce\uff0c\u4ed6\u65e2\u7136\u80af\u727a\u7272 \u81ea\u5df1\u63f4\u6551\u6211\u4eec\uff0c\u6211\u96be\u9053\u4e0d\u53ef\u4ee5\u727a\u7272\u81ea\u5df1\u6210\u5168\u4ed6\u4eec\u4e48\uff1f\u201d\u51cc\u4e91\u51e4\u7684\u60f3\u6cd5\u5374\u53c8\u4e0d\u540c\uff0c\u5979\u53cd\u590d\u5480\u56bc \u94c1\u955c\u5fc3\u90a3\u53e5\u201c\u5bf9\u4e0d\u8d77\u4e8e\u59d1\u5a18\u201d\u7684\u8bf4\u8bdd\uff0c\u5fc3\u4e2d\u60f3\u5230\uff1a\u201c\u627f\u73e0\u59b9\u59b9\u4e0d\u662f\u4e2a\u5fc3\u80f8\u72ed\u7a84\u7684\u4eba\uff0c\u5979\u4e0d\u522b \u800c\u884c\uff0c\u7559\u4e0b\u7684\u90a3\u5c01\u4fe1\u516b\u4e5d\u6210\u662f\u5c01\u8bc0\u522b\u7684\u4e66\u4fe1\uff0c\u8fd9\u5b9a\u7136\u4e0d\u662f\u4e00\u4ef6\u5c0f\u5c0f\u7684\u8bef\u4f1a\u3002\u201d \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u7eed\u9053\uff0c\u201c\u4e00\u4e2a\u6708\u524d\uff0c\u94c1\u955c\u5fc3\u88ab\u62bc\u56de\u676d\u5dde\uff0c\u628a\u94c1\u94f1\u6025\u5f97\u4e0d\u5f97\u4e86\u3002\u6211\u7b54\u5e94\u4e86\u94c1\u955c\u5fc3 \u7167\u6599\u4ed6\u7684\u7236\u4eb2\uff0c\u4e00\u76f4\u6ca1\u6709\u79bb\u5f00\u676d\u5dde\u3002\u5e78\u800c\u5f20\u9aa5\u53ea\u662f\u6d3e\u4eba\u76d1\u89c6\u94c1\u94f1\uff0c\u5012\u6ca1\u6709\u5230\u94c1\u5bb6\u7f57\u5506\u3002\u94c1\u94f1 \u8fd8\u66fe\u7792\u7740\u6211\u5230\u516d\u548c\u5854\u5929\u53bb\u770b\u8fc7\u4ed6\u88ab\u56da\u7684\u513f\u5b50\u3002\u53ef\u662f\u8fd9\u4e8b\u60c5\u5374\u771f\u5947\u602a\uff0c\u5f85\u8001\u8872\u5f97\u77e5\u6d88\u606f\uff0c\u5230\u516d \u548c\u5854\u53bb\u5927\u95f9\u4e4b\u65f6\uff0c\u5374\u53c8\u4e0d\u89c1\u4e86\u94c1\u955c\u5fc3\u4e86\u3002\u4eca\u65e5\u8d76\u56de\u94c1\u5bb6\uff0c\u8fde\u94c1\u94f1\u7684\u5168\u5bb6\u4e5f\u4e0d\u77e5\u53bb\u5411\u4e86\u3002\u8fd9\u91cc \u9762\u7a76\u7adf\u662f\u6709\u751a\u7384\u865a\uff1f\u201d \u3000\u3000\u4e09\u4eba\u53cd\u590d\u63a8\u6572\uff0c\u90fd\u662f\u731c\u60f3\u4e0d\u900f\uff0c\u8fd9\u65f6\u767b\u9ad8\u9065\u671b\uff0c\u4f46\u89c1\u5b98\u519b\u7684\u706b\u628a\uff0c\u5df2\u4ece\u5b64\u5c71\u90a3\u8fb9\u873f\u8712\u800c \u6765\uff0c\u51cc\u4e91\u51e4\u7ed9\u6f6e\u97f3\u548c\u5c1a\u624e\u597d\u4e86\u4f24\uff0c\u53f6\u6210\u6797\u9053\uff1a\u201c\u5e08\u4f2f\u7956\uff0c\u6211\u80cc\u4f60\u8d70\u5427\u3002\u201d\u6f6e\u97f3\u548c\u5c1a\u6447\u4e00\u6447 \u5934\uff0c\u6b63\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u89c1\u6709\u51e0\u6761\u9ed1\u5f71\u4ece\u5bf9\u8fb9\u7684\u5c71\u5934\u98de\u5954\u800c\u6765\uff0c\u53f6\u6210\u6797\u6025\u5fd9\u5c06\u6f6e\u97f3\u548c\u5c1a\u62c9\u5230\u4e86\u5ca9\u77f3 \u7684\u540e\u9762\u3002 \u3000\u3000\u84e6\u7136\u95f4\uff0c\u5ffd\u542c\u4e00\u58f0\u5389\u53eb\uff0c\u4e00\u4e2a\u80cc\u4e0a\u5e26\u7bad\uff0c\u6ee1\u8eab\u6d74\u8840\u7684\u6c49\u5b50\u51b2\u4e86\u8fc7\u6765\uff0c\u98de\u8eab\u4e00\u8df3\uff0c\u8df3\u8fc7\u8fd9 \u5757\u5ca9\u77f3\uff0c\u5927\u7ea6\u4e5f\u662f\u60f3\u627e\u5bfb\u85cf\u533f\u7684\u5730\u65b9\uff0c\u8fd9\u4e00\u8df3\u6b63\u5de7\u843d\u5728\u53f6\u6210\u6797\u7684\u9762\u524d\u3002\u53f6\u6210\u6797\u5931\u58f0\u558a\u9053\uff1a \u201c\u6bd5\u64ce\u5929\uff01\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6f6e\u97f3\u548c\u5c1a\u4e0d\u77e5\u54ea\u91cc\u6765\u7684\u6c14\u529b\uff0c\u7a81\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u7985\u62a1\u5706\uff0c\u4e00\u6756\u5c31\u5411\u6bd5\u64ce \u5929\u5f53\u5934\u6253\u4e0b\uff0c\u53f6\u6210\u6797\u53eb\u9053\uff1a\u201c\u4e14\u6162\u3002\u201d\u54ea\u91cc\u963b\u6321\u5f97\u4f4f\uff0c\u4f46\u542c\u5f97\u8f70\u7136\u5de8\u54cd\uff0c\u6bd5\u64ce\u5929\u7684\u72fc\u7259\u68d2\u65ad \u4e3a\u4e24\u6bb5\uff0c\u6f6e\u97f3\u548c\u5c1a\u7684\u90a3\u6839\u7985\u6756\u4e5f\u98de\u4e0a\u4e86\u534a\u5929\u3002\u672c\u6765\u6f6e\u97f3\u548c\u5c1a\u7684\u795e\u529b\uff0c\u4e16\u6240\u7f55\u89c1\uff0c\u53ea\u56e0\u53d7\u4e86\u91cd \u4f24\uff0c\u800c\u6bd5\u64ce\u5929\u53c8\u662f\u62fc\u547d\u4e00\u51fb\uff0c\u6070\u597d\u65a4\u4e24\u6089\u654c\uff0c\u6f6e\u97f3\u548c\u5c1a\u6c14\u529b\u4f7f\u5c3d\uff0c\u6012\u543c\u4e00\u58f0\u4e00\u8de4\u683d\u5012\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u8ba9\u4ed6\u8d70\u4e86\u3002\u201d\u6599\u60f3\u53f6\u6210\u6797\u4e00\u4eba\u80fd\u5bf9\u4ed8\u5f97\u4e86\uff0c\u4fef\u8eab\u5bdf\u770b\u6f6e\u97f3\u548c\u5c1a\u7684\u4f24 \u52bf\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u9aa4\u7136\u95f4\u89c1\u7740\u4e86\u53f6\u6210\u6797\uff0c\u7f9e\u60ed\u3001\u6050\u60e7\u3001\u61ca\u607c\u3001\u5992\u6068\uff0c\u8bf8\u822c\u60c5\u7eea\uff0c\u970e\u65f6\u95f4\u90fd\u6d8c\u4e0a\u5fc3 \u5934\uff0c\u63d0\u7740\u534a\u622a\u72fc\u7259\u68d2\u5446\u5446\u53d1\u6123\uff0c\u53f6\u6210\u6797\u62d4\u51fa\u4f69\u5200\uff0c\u5200\u67c4\u4e00\u6a2a\uff0c\u5200\u950b\u5728\u80f8\u524d\u5212\u4e86\u534a\u9053\u5706\u5f27\uff0c\u5374 \u6ca1\u6709\u65ab\u4e0b\u53bb\u3002\u6bd5\u64ce\u5929\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u53f6\u5144\u5f1f\u6551\u6211\uff01\u201d\u4f46\u89c1\u4e00\u6761\u9ed1\u5f71\uff0c\u51cc\u7a7a\u4e0b\u51fb\uff0c\u5374\u539f\u6765\u662f\u9633\u5b97 \u6d77\u8ffd\u5230\u4e86\u3002 \u3000\u3000\u53f6\u6210\u6797\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u5200\u6a2a\u626b\uff0c\u9633\u5b97\u6d77\u5530\u5530\u4e24\u5251\uff0c\u821e\u8d77\u4e86\u7897\u53e3\u822c\u5927\u7684\u5251\u82b1\uff0c\u8fd9\u53e3\u5251\u662f\u4ed6\u4ece \u5a04\u6850\u5b59\u624b\u4e2d\u6682\u65f6\u501f\u7528\u7684\u90a3\u628a\u5927\u5185\u5b9d\u5251\uff0c\u5251\u5149\u6620\u6708\uff0c\u7167\u89c1\u4e86\u53f6\u6210\u6797\u7684\u9762\u5e9e\uff0c\u9633\u5b97\u6d77\u5403\u4e86\u4e00\u60ca\uff0c \u968f\u5373\u559c\u800c\u53eb\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u662f\u4f60\uff01\u201d\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u62ff\u7740\u4e86\u53f6\u6210\u6797\uff0c\u53ef\u8981\u6bd4\u6bd5\u64ce\u5929\u8fd8\u503c\u4ef7\u5f97 \u591a\uff01\u201d\u5b9d\u5251\u4e00\u4e2a\u76d8\u65cb\uff0c\u4e00\u62db\u201c\u62e6\u6c5f\u622a\u6597\u201d\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u628a\u53f6\u6210\u6797\u7684\u4f69\u5200\u524a\u53bb\u4e00\u622a\u3002 \u3000\u3000\u6bd5\u64ce\u5929\u8d81\u8fd9\u4e2a\u65f6\u673a\uff0c\u4fbf\u60f3\u9003\u8d70\uff0c\u521a\u521a\u8e0f\u51fa\u4e00\u6b65\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53c8\u662f\u4e00\u6761\u9ed1\u5f71\u51cc\u7a7a\u98de \u4e0b\uff0c\u624b\u81c2\u4e00\u4f38\uff0c\u5c31\u642d\u4e0a\u4e86\u6bd5\u64ce\u5929\u7684\u80a9\u5934\uff0c\u6bd5\u64ce\u5929\u4f46\u89c9\u597d\u50cf\u94a2\u94b3\u4e00\u6837\u7d27\u7d27\u94b3\u7740\u81ea\u5df1\uff0c\u767e\u9ab8\u6b32 \u88c2\uff0c\u75db\u5f7b\u5fc3\u80ba\uff0c\u8fd9\u4eba\u6b63\u662f\u5fa1\u6797\u519b\u7684\u603b\u6307\u6325\u5a04\u6850\u5b59\uff0c\u6bd5\u64ce\u5929\u53d7\u4f24\u4e4b\u540e\u52a0\u4ee5\u5fc3\u795e\u672a\u5b9a\uff0c\u7adf\u7136\u5728\u7167 \u9762\u4e4b\u9645\uff0c\u5c31\u7ed9\u4ed6\u7684\u5206\u7b4b\u9519\u9aa8\u624b\u642d\u4e0a\u4e86\u3002 \u3000\u3000\u53f6\u6210\u6797\u5927\u53eb\u9053\uff1a\u201c\u4e91\u51e4\uff0c\u51fa\u624b\u6551\u4ed6\uff01\u201d\u51cc\u4e91\u51e4\u7a0d\u7a0d\u72b9\u7591\uff0c\u53ea\u542c\u5f97\u53f6\u6210\u6797\u6c89\u58f0\u53eb\u9053\uff1a\u201c\u8fd9 \u662f\u519b\u4ee4\uff01\u201d\u51cc\u4e91\u51e4\u9752\u94a2\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u52bf\u6377\u5982\u7535\uff0c\u523a\u5411\u5a04\u6850\u5b59\u7684\u80cc\u5fc3\uff0c\u5a04\u6850\u5b59\u903c\u5f97\u64a4\u638c\u5e94\u654c\uff0c \u62ff\u7740\u6bd5\u64ce\u5929\u7684\u90a3\u53ea\u624b\u4e00\u677e\uff0c\u201c\u5495\u549a\u201d\u4e00\u58f0\uff0c\u6bd5\u64ce\u5929\u4e5f\u8dcc\u5012\u5730\u4e0a\uff0c\u6655\u4e86\u8fc7\u53bb\uff0c\u6070\u6070\u5012\u5728\u6f6e\u97f3\u548c \u5c1a\u7684\u65c1\u8fb9\u3002 \u3000\u3000\u53f6\u6210\u6797\u521d\u65f6\u672a\u77e5\u9053\u9633\u5b97\u6d77\u6240\u4f7f\u7684\u4e43\u662f\u5b9d\u5251\uff0c\u4f69\u5200\u51e0\u4e4e\u7ed9\u4ed6\u622a\u65ad\uff0c\u9633\u5b97\u6d77\u62a2\u4e86\u4e0a\u98ce\uff0c\u72c2\u50b2 \u4e4b\u6781\uff0c\u4e00\u62db\u201c\u76f4\u6307\u5929\u5357\u201d\uff0c\u5251\u5c16\u523a\u5230\u4e86\u53f6\u6210\u6797\u7684\u624b\u8155\uff0c\u903c\u5f97\u53f6\u6210\u6797\u53c8\u9000\u4e86\u51e0\u6b65\uff0c\u9633\u5b97\u6d77\u54c8\u54c8 \u7b11\u9053\uff1a\u201c\u53f6\u6210\u6797\uff0c\u4f60\u73b0\u5728\u5df2\u662f\u7a77\u9014\u672a\u8def\uff0c\u8fd8\u4e0e\u6211\u6253\u505a\u4ec0\u4e48\uff0c\u8d81\u65e9\u5c06\u6bd5\u64ce\u5929\u7f1a\u4e86\uff0c\u5f52\u987a\u671d\u5ef7\uff0c \u8d4f\u4f60\u603b\u5175\u4e00\u4e2a\uff01\u201d\u731b\u542c\u5f97\u53f6\u6210\u6797\u4e00\u58f0\u5927\u559d\uff0c\u547c\u7684\u4e00\u638c\u5288\u51fa\uff0c\u638c\u98ce\u6240\u53ca\uff0c\u7802\u98de\u77f3\u8d77\uff0c\u9633\u5b97\u6d77\u8fd8 \u771f\u6599\u4e0d\u5230\u4ed6\u5982\u6b64\u62fc\u547d\uff0c\u5c45\u7136\u7a7f\u5251\u8fdb\u62db\uff0c\u731d\u4e0d\u53ca\u9632\uff0c\u80a9\u5934\u7ed9\u626b\u4e86\u4e00\u4e0b\uff0c\u706b\u8fa3\u8fa3\u822c\u4f5c\u75db\u3002\u9633\u5b97\u6d77 \u5927\u6012\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4e0d\u8bc6\u62ac\u4e3e\uff0c\u8fde\u4f60\u4e5f\u4e00\u5e76\u5bb0\u4e86\u3002\u201d\u957f\u5251\u6325\u821e\uff0c\u7d2b\u8679\u7535\u5c04\uff0c\u4e00\u62db\u7d27\u4f3c\u4e00 \u62db\uff0c\u4ed6\u540d\u5217\u5929\u4e0b\u56db\u5927\u5251\u5ba2\u4e4b\u4e00\uff0c\u867d\u7136\u662f\u56db\u5927\u5251\u5ba2\u4e2d\u6700\u5f31\u7684\u4e00\u4e2a\uff0c\u4f46\u8bba\u5230\u6b66\u529f\u9020\u8be3\uff0c\u5374\u8fd8\u5728\u53f6 \u6210\u6797\u4e4b\u4e0a\uff0c\u52a0\u4e0a\u6240\u7528\u7684\u4e43\u662f\u5927\u5185\u5b9d\u5251\uff0c\u5251\u5149\u970d\u970d\u5c55\u5f00\uff0c\u767b\u65f6\u628a\u53f6\u6210\u6797\u7b3c\u7f69\u5728\u5185\uff0c\u4f46\u53f6\u6210\u6797\u5200 \u638c\u5e76\u7528\uff0c\u53f3\u624b\u4f7f\u51fa\u4e94\u864e\u4e27\u95e8\u5200\u6cd5\uff0c\u6bcf\u4e00\u5200\u90fd\u662f\u62fc\u547d\u7684\u62db\u6570\uff0c\u5de6\u624b\u5374\u662f\u5927\u529b\u91d1\u521a\u624b\u6cd5\uff0c\u90a3\u66f4\u662f \u6b66\u6797\u7edd\u5b66\uff0c\u52c7\u731b\u65e0\u4f26\uff01 \u3000\u3000\u9633\u5b97\u6d77\u7684\u5251\u672f\u867d\u7136\u7cbe\u5999\uff0c\u4f46\u5728\u53f6\u6210\u6797\u5200\u3001\u638c\u517c\u65bd\u8c41\u51fa\u6027\u547d\u7684\u6b7b\u62fc\u4e4b\u4e0b\uff0c\u5374\u4e5f\u4e0d\u80fd\u65e0\u6240\u987e \u5fcc\uff0c\u4f46\u89c1\u5200\u5f71\u5251\u5149\uff0c\u5b9b\u4f3c\u94f6\u86c7\u4e71\u6500\uff0c\u638c\u98ce\u4eba\u5f71\uff0c\u8d5b\u5982\u8774\u8776\u7a7f\u82b1\uff0c\u7247\u523b\u4e4b\u95f4\u6597\u4e86\u4e00\u767e\u4f59\u62db\uff0c\u9633 \u5b97\u6d77\u867d\u662f\u7a0d\u5360\u4e0a\u98ce\uff0c\u8feb\u5207\u4e4b\u95f4\uff0c\u5374\u4e5f\u5948\u4f55\u4e0d\u5f97\u3002 \u3000\u3000\u90a3\u8fb9\u53a2\u51cc\u4e91\u51e4\u4ee5\u4e00\u67c4\u9752\u94a2\u5251\uff0c\u6076\u6597\u5a04\u6850\u5b59\u7684\u5206\u7b4b\u9519\u9aa8\u624b\uff0c\u4e5f\u662f\u6740\u5f97\u96be\u89e3\u96be\u5206\uff0c\u51cc\u4e91\u98ce\u7684 \u5251\u52bf\u5c55\u5f00\uff0c\u6781\u5f97\u8f7b\u7075\u7fd4\u52a8\u4e4b\u5999\uff0c\u5a04\u6850\u5b59\u65e0\u9699\u53ef\u4e58\uff0c\u5206\u7b4b\u9519\u9aa8\u624b\u7684\u5a01\u529b\u6253\u4e86\u4e00\u534a\u6298\u6263\u3002\u4f46\u51cc\u4e91 \u51e4\u4e5f\u4e0d\u6562\u6b3a\u8eab\u903c\u8fd1\uff0c\u4e24\u4eba\u90fd\u662f\u500f\u8fdb\u500f\u9000\uff0c\u89c5\u9699\u5bfb\u6687\uff0c\u770b\u6765\u6253\u5f97\u6bd4\u53f6\u6210\u6797\u90a3\u5bf9\u8fd8\u8981\u70ed\u95f9\uff0c\u5176\u5b9e \u53cc\u65b9\u90fd\u662f\u5c0f\u5fc3\u7ffc\u7ffc\uff0c\u7ed5\u8eab\u6e38\u6597\u3002\u5a04\u6850\u5b59\u529f\u529b\u8f83\u9ad8\uff0c\u4e5f\u50cf\u9633\u5b97\u6d77\u4e00\u6837\uff0c\u7a0d\u7a0d\u5360\u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u8fd9\u65f6\u5b98\u519b\u7684\u706b\u628a\u5df2\u4ece\u5b64\u5c71\u90a3\u8fb9\u873f\u8712\u800c\u6765\uff0c\u5f53\u524d\u7684\u4e00\u80a1\u5df2\u8fc7\u4e86\u9ec4\u9f99\u6d1e\uff0c\u9633\u5b97\u6d77\u53d1\u58f0\u957f\u5578\uff0c \u4f5c\u4e3a\u8baf\u53f7\uff0c\u4e0d\u4e45\u5c31\u542c\u5230\u4e86\u4e0b\u9762\u5b98\u519b\u5439\u8d77\u4e86\u545c\u545c\u53f7\u89d2\u4e4b\u58f0\uff0c\u4e00\u4e2a\u5b8f\u4eae\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u5b97\u6d77\uff0c\u662f \u4f60\u5728\u4e0a\u9762\u5417\uff1f\u201d\u9633\u5b97\u6d77\u5e94\u58f0\u9053\uff1a\u201c\u5927\u5e08\u54e5\uff0c\u6211\u5df2\u7f20\u4e0a\u4e86\u53f6\u6210\u6797\uff0c\u8d76\u5feb\u4e0a\u6765\u5e2e\u6211\u4e00\u81c2\u4e4b\u529b\uff01\u201d \u7387\u9886\u8fd9\u80a1\u5b98\u519b\u7684\u4eba\u6b63\u662f\u8d64\u971e\u9053\u4eba\u7684\u5927\u5f1f\u5b50\u76d8\u5929\u7f57\uff0c\u9633\u5b97\u6d77\u7279\u5730\u4ece\u82d7\u7586\u8bf7\u4ed6\u6765\u52a9\u9635\u7684\u3002 \u3000\u3000\u53f6\u6210\u6797\u6697\u53eb\u4e0d\u5999\uff0c\u6f6e\u97f3\u548c\u5c1a\u548c\u6bd5\u64ce\u5929\u53d7\u4f24\u4e4b\u540e\uff0c\u5c1a\u8fd8\u660f\u8ff7\u672a\u9192\uff0c\u4ed6\u548c\u51cc\u4e91\u98ce\u529b\u6218\u5f3a\u654c\uff0c \u4ec5\u80fd\u5e94\u4ed8\uff0c\u4f11\u8bf4\u8131\u8eab\u4e0d\u6613\uff0c\u5373\u7b97\u80fd\u591f\u62fc\u547d\u51b2\u51fa\uff0c\u4ed6\u4eec\u53c8\u600e\u5fcd\u820d\u4e86\u6f6e\u97f3\u548c\u5c1a\u800c\u9003\u3002 \u3000\u3000\u5f62\u52bf\u5371\u6025\u4e4b\u6781\uff0c\u9633\u5b97\u6d77\u8d81\u52bf\u653b\u51fb\uff0c\u5251\u950b\u4e00\u8f6c\uff0c\u4e00\u62db\u201c\u6597\u8f6c\u661f\u79fb\u201d\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253 \u5317\uff0c\u201c\u5f53\u5577\u201d\u4e00\u58f0\uff0c\u53c8\u5c06\u53f6\u6210\u6797\u7684\u4f69\u5200\u524a\u53bb\u4e00\u622a\uff0c\u53f6\u6210\u6797\u4e00\u58f0\u864e\u543c\uff0c\u5c06\u534a\u622a\u4f69\u5200\u4e00\u63b7\uff0c\u547c\u7684 \u4e00\u638c\u6a2a\u626b\u51fa\u53bb\u3002 \u3000\u3000\u9633\u5b97\u6d77\u54c8\u54c8\u5927\u5956\uff0c\u53eb\u9053\uff1a\u201c\u8c01\u548c\u4f60\u62fc\u547d\uff01\u201d\u6a2a\u5251\u62a4\u80f8\uff0c\u628a\u90a3\u534a\u622a\u4f69\u5200\u78b0\u98de\uff0c\u53f6\u6210\u6797\u8fd9\u4e00 \u638c\u5288\u6765\uff0c\u521a\u597d\u5c31\u8981\u78b0\u5230\u4ed6\u7684\u5251\u950b\u4e4b\u4e0a\u3002 \u3000\u3000\u731b\u542c\u5f97\u8f70\u9686\u9686\u95f7\u96f7\u4e5f\u4f3c\u7684\u58f0\u97f3\uff0c\u4f46\u89c1\u51e0\u5757\u78e8\u76d8\u822c\u7684\u5de8\u77f3\u4ece\u5c71\u9876\u4e0a\u6eda\u4e0b\uff0c\u90a3\u4e00\u80a1\u5b98\u519b\u53d1\u4e00 \u58f0\u558a\uff0c\u7eb7\u7eb7\u8eb2\u95ea\uff0c\u5927\u77f3\u4e00\u5757\u63a5\u7740\u4e00\u5757\uff0c\u6eda\u6eda\u800c\u4e0b\uff0c\u9707\u5f97\u5c71\u8c37\u8f70\u9e23\uff0c\u58f0\u5a01\u9a87\u4eba\uff0c\u770b\u60c5\u5f62\uff0c\u5c71\u9876 \u7adf\u7136\u53e6\u6709\u80fd\u4eba\uff0c\u6697\u52a9\u53f6\u6210\u6797\u62d2\u654c\u3002 \u3000\u3000\u9633\u5b97\u6d77\u5403\u4e86\u4e00\u60ca\uff0c\u987e\u4e0d\u5f97\u4f24\u4eba\uff0c\u4e3e\u76ee\u4e00\u770b\uff0c\u4f46\u89c1\u4e24\u6761\u4eba\u5f71\u98de\u9a70\u800c\u81f3\uff0c\u53f6\u6210\u6797\u770b\u5230\u4e86\uff0c\u6025 \u5fd9\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u56de\u638c\u62a4\u8eab\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u627f\u73e0\u59b9\u59b9\uff0c\u771f\u662f\u4f60\u4e48\uff1f\u201d \u3000\u3000\u4f46\u89c1\u4e8e\u627f\u73e0\u8863\u8882\u98ce\u98d8\uff0c\u81ea\u5bf9\u9762\u7684\u5c71\u5934\u4e0a\u75be\u9a70\u800c\u81f3\uff0c\u604d\u5982\u4ed9\u5973\u7d20\u5a25\uff0c\u51cc\u7a7a\u98de\u964d\uff0c\u5979\u7684\u80cc\u540e \u8fd8\u8ddf\u7740\u4e00\u4e2a\u957f\u8eab\u7389\u7acb\u7684\u5c11\u5e74\u4eba\uff0c\u53f6\u6210\u6797\u6014\u4e86\u4e00\u6014\uff0c\u65b9\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u7adf\u7136\u6709\u8fd9\u6837\u4fca\u7684 \u8f7b\u529f\uff1f\u201d\u4f46\u542c\u5f97\u4e8e\u627f\u73e0\u7eb5\u58f0\u957f\u7b11\uff0c\u9065\u9065\u62db\u624b\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6211\u3002\u51cc\u59d0\u59d0\uff0c\u4f60\u770b\uff0c\u6211 \u628a\u8c01\u5e26\u6765\u4e86\uff1f\u201d \u3000\u3000\u51cc\u4e91\u51e4\u62bd\u773c\u4e00\u770b\uff0c\u559c\u6781\u5982\u72c2\uff0c\u7591\u5728\u68a6\u4e2d\uff0c\u968f\u7740\u4e8e\u627f\u73e0\u800c\u6765\u7684\u8fd9\u4e2a\u5c11\u5e74\u4eba\uff0c\u6b63\u662f\u5979\u65e5\u601d\u591c \u60f3\u7684\u970d\u5929\u90fd\uff01\u5979\u5f20\u53e3\u6b32\u547c\uff0c\u201c\u970d\u54e5\u54e5\u201d\u4e09\u4e2a\u5b57\u5728\u820c\u5934\u4e0a\u6253\u6eda\u4e86\u65e0\u6570\u904d\uff0c\u5374\u662f\u53eb\u4e0d\u51fa\u6765\uff0c\u539f\u6765 \u5589\u5934\u5df2\u54bd\u4f4f\u4e86\u3002 \u3000\u3000\u9ad8\u624b\u6bd4\u6597\uff0c\u90a3\u5bb9\u5982\u6b64\u5206\u795e\uff0c\u5a04\u6850\u5b59\u75be\u653b\u51e0\u8bb0\uff0c\u84e6\u5730\u4e00\u62db\u201c\u733f\u7334\u6458\u679c\u201d\uff0c\u5c06\u51cc\u4e91\u51e4\u7684\u5251\u67c4 \u6293\u7740\uff0c\u4f46\u4e8e\u627f\u73e0\u65e9\u5df2\u6599\u5230\u5a04\u6850\u5b59\u4f1a\u8d81\u51cc\u4e91\u51e4\u8bf4\u8bdd\u4e4b\u9645\u5f3a\u653b\uff0c\u4e00\u6296\u624b\u98de\u51fa\u4e86\u4e09\u6735\u91d1\u82b1\uff0c\u4e0a\u6253\u53cc \u76ee\uff0c\u4e2d\u6253\u80f8\u53e3\uff0c\u4e0b\u6253\u819d\u76d6\uff0c\u5a04\u6850\u5b59\u987e\u4e0d\u5f97\u4f24\u5bb3\u51cc\u4e91\u51e4\uff0c\u6025\u5fd9\u4e00\u4e2a\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\uff0c\u4ee5\u7edd\u5999\u7684 \u8eab\u6cd5\u5012\u7eb5\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u800c\u4e14\u5728\u5012\u7eb5\u4e4b\u65f6\uff0c\u624b\u8155\u7528\u529b\u4e00\u5e26\uff0c\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u7adf\u628a\u51cc\u4e91\u51e4\u7684\u9752\u94a2 \u5251\u6298\u65ad\uff0c\u65ad\u5251\u8ddf\u7740\u5c04\u51fa\uff0c\u4ee4\u5f97\u51cc\u4e91\u51e4\u4e5f\u4e0d\u6562\u4e58\u673a\u8ffd\u6740\uff0c\u786e\u662f\u4e00\u6d41\u9ad8\u624b\u7684\u529f\u592b\u3002 \u3000\u3000\u5a04\u6850\u5b59\u5feb\uff0c\u4e8e\u627f\u73e0\u66f4\u5feb\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4e8e\u627f\u73e0\u98de\u8eab\u4e00\u63a0\uff0c\u9752\u51a5\u5b9d\u5251\u5410\u51fa\u78a7\u83b9\u83b9\u7684\u5bd2 \u5149\uff0c\u5251\u950b\u4e5f\u5df2\u582a\u582a\u523a\u5230\u5a04\u6850\u5b59\u80cc\u540e\u3002\u5a04\u6850\u5b59\u53cd\u624b\u4e00\u8bb0\u64d2\u62ff\uff0c\u89e3\u62db\u8fdb\u62db\uff0c\u7acb\u5373\u548c\u4e8e\u627f\u73e0\u6597\u5728\u4e00 \u8d77\u3002\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u51cc\u59d0\u59d0\u4f60\u4eec\u4e45\u522b\u91cd\u9022\uff0c\u8fd9\u53ae\u4ea4\u7ed9\u6211\u5427\u3002\u201d\u51cc\u4e91\u51e4\u53e3\u5507\u98a4\u52a8\uff0c\u201c\u970d\u54e5\u54e5\u201d \u4e09\u4e2a\u5b57\u76f4\u5230\u5982\u4eca\u624d\u53eb\u51fa\u53e3\u6765\u3002\u970d\u5929\u90fd\u5fae\u7b11\u9053\uff1a\u201c\u51cc\u59b9\u59b9\uff0c\u4f60\u6b47\u6b47\u53bb\u3002\u53f6\u5927\u54e5\uff0c\u4f60\u4e5f\u628a\u8fd9\u8d3c\u5b50 \u4ea4\u7ed9\u6211\u5427\u3002\u201d\u957f\u5251\u4e00\u5c55\uff0c\u642d\u4e0a\u9633\u5b97\u6d77\u7684\u5251\u810a\uff0c\u5c06\u53f6\u6210\u6797\u66ff\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u51cc\u4e91\u51e4\u53c8\u662f\u5931\u671b\uff0c\u53c8\u662f\u6b22\u559c\uff0c\u4f46\u90a3\u4e9b\u5fae\u7684\u5931\u671b\u8fc5\u5373\u88ab\u5de8\u5927\u7684\u559c\u60a6\u63a9\u76d6\u4e86\uff0c\u6b63\u5982\u6de1\u4e91\u906e\u4e0d \u4f4f\u71c3\u70e7\u7684\u592a\u9633\u3002\u5979\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u7684\u970d\u54e5\u54e5\u4e0d\u5931\u82f1\u96c4\u672c\u8272\uff0c\u662f\u554a\uff0c\u82e5\u7136\u6362\u6211\u662f\u4ed6\uff0c\u6211\u4e5f\u4f1a\u5148 \u66ff\u4e0b\u4e86\u53f6\u6210\u6797\u7684\uff0c\u513f\u5973\u79c1\u60c5\u6162\u6162\u8fd8\u53ef\u4ee5\u8c08\uff0c\u5f3a\u654c\u5374\u4e07\u4e07\u4e0d\u80fd\u653e\u8fc7\u3002\u53ea\u662f\u9633\u5b97\u6d77\u540d\u5217\u5929\u4e0b\u56db\u5927 \u5251\u5ba2\uff0c\u970d\u54e5\u54e5\uff0c\u4ed6\uff0c\u4ed6\u4e0d\u77e5\u53ef\u62b5\u6321\u5f97\u4f4f\uff1f\u201d \u3000\u3000\u4f46\u89c1\u9633\u5b97\u6d77\u8d8a\u6597\u8d8a\u72e0\uff0c\u4e00\u62db\u201c\u957f\u6cb3\u843d\u65e5\u201d\uff0c\u5251\u5149\u5982\u7ec3\uff0c\u5530\u5730\u4fbf\u5411\u970d\u5929\u90fd\u5de6\u80a9\u523a\u6765\uff0c\u8fd9\u4e00 \u62db\u865a\u4e2d\u5957\u5b9e\uff0c\u5b9e\u4e2d\u5957\u865a\u72e0\u8fa3\u72e1\u733e\uff0c\u517c\u800c\u6709\u4e4b\uff0c\u7aef\u7684\u5389\u5bb3\u3002\u54ea\u77e5\u970d\u5929\u90fd\u5140\u7136\u6728\u52a8\uff0c\u5f85\u4ed6\u5251\u5c16\u79bb \u8eab\u6570\u5bf8\uff0c\u770b\u770b\u5c31\u8981\u6cbe\u8863\u4e4b\u9645\uff0c\u624b\u8155\u500f\u7ffb\uff0c\u75be\u5982\u95ea\u7535\u80a1\u5730\u8fd8\u4e86\u4e00\u62db\u201c\u91d1\u96d5\u5c55\u7fc5\u201d\uff0c\u62ff\u634f\u65f6\u5019\uff0c \u5999\u5230\u6beb\u5dc5\uff0c\u9633\u5b97\u6d77\u8fd9\u4e00\u62db\u82e5\u7136\u653e\u5c3d\uff0c\u90a3\u5c31\u662f\u5c06\u4e00\u6761\u624b\u81c2\u9001\u4e0a\u7ed9\u970d\u5929\u90fd\u780d\u4e86\u3002 \u3000\u3000\u9633\u5b97\u6d77\u5927\u5403\u4e00\u60ca\uff0c\u6599\u4e0d\u5230\u8fd9\u4e00\u4e2a\u964c\u751f\u7684\u5c11\u5e74\uff0c\u5251\u672f\u7adf\u662f\u5982\u6b64\u7cbe\u6e5b\uff0c\u6025\u6025\u53d8\u62db\uff0c\u518d\u4e0d\u6562\u4e1d \u6beb\u8f7b\u654c\u3002\u970d\u5929\u90fd\u8fd0\u5251\u5982\u51e4\uff0c\u9e70\u7fd4\u96bc\u523a\uff0c\u6bcf\u4e00\u62db\u4f7f\u51fa\uff0c\u90fd\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u9633\u5b97\u6d77\u867d\u7136\u6709\u4e00\u67c4 \u5927\u5185\u5b9d\u5251\uff0c\u7adf\u7136\u88ab\u4ed6\u7684\u51cc\u5389\u653b\u52bf\u903c\u5f97\u53ea\u6709\u9632\u5b88\u7684\u4efd\u513f\uff0c\u970d\u5929\u90fd\u4e00\u5251\u7d27\u8fc7\u4e00\u5251\uff0c\u4e00\u70b9\u5373\u6536\uff0c\u524d \u5251\u521a\u6536\uff0c\u540e\u5251\u53c8\u51fa\uff0c\u6709\u5982\u957f\u6c5f\u5927\u6cb3\uff0c\u6eda\u6eda\u800c\u4e0a\uff0c\u9633\u5b97\u6d77\u60f3\u5c3d\u529e\u6cd5\u8981\u524a\u65ad\u4ed6\u7684\u5175\u5203\uff0c\u4f46\u970d\u5929\u90fd \u6df1\u5f97\u201c\u5feb\u3001\u72e0\u3001\u7a33\u3001\u51c6\u201d\u56db\u5b57\u5251\u8bc0\u7684\u7cbe\u534e\uff0c\u4e00\u6cbe\u5373\u8d70\uff0c\u4e00\u8d70\u5373\u653b\uff0c\u4e24\u67c4\u5251\u4ece\u4e0d\u76f8\u4ea4\uff0c\u5df2\u628a\u9633 \u5b97\u6d77\u6740\u5f97\u6709\u70b9\u624b\u5fd9\u811a\u4e71\uff01 \u3000\u3000\u51cc\u4e91\u51e4\u770b\u5f97\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u51e0\u5e74\u4e0d\u89c1\uff0c\u60f3\u4e0d\u5230\u4ed6\u7684\u5251\u672f\u7adf\u7136\u7cbe\u8fdb\u5982\u65af\u3002\u8bb0\u5f97\u5c0f \u65f6\u5019\u4e0e\u4ed6\u5728\u5929\u5c71\u4e4b\u4e0a\u4e00\u540c\u5b66\u5251\uff0c\u4ed6\u7acb\u8a93\u8981\u7ee7\u627f\u7236\u5fd7\uff0c\u72ec\u521b\u4e00\u5bb6\u3002\u6211\u5f53\u65f6\u66fe\u4e0e\u4ed6\u620f\u8bdd\uff1a\u4f60\u82e5\u81ea \u6210\u4e00\u5bb6\uff0c\u6211\u4e5f\u8981\u521b\u51fa\u4e00\u6d3e\u5251\u672f\u4e13\u7834\u4f60\u7684\u3002\u5440\uff0c\u73b0\u5728\u4ed6\u5728\u522b\u540e\u7b2c\u4e00\u6b21\u4e0e\u6211\u76f8\u89c1\uff0c\u89c1\u6211\u7684\u5251\u88ab\u4eba \u7a7a\u624b\u6298\u65ad\uff0c\u4e0d\u77e5\u4ed6\u5fc3\u4e2d\u53ef\u5728\u7b11\u8bdd\u6211\u4e48\uff1f\u201d\u770b\u4e00\u770b\u5730\u4e0a\u7684\u65ad\u5251\uff0c\u9ad8\u5174\u4e4b\u4e2d\u53c8\u6709\u51e0\u5206\u60ed\u6127\u3002\u51cc\u4e91 \u51e4\u662f\u4e2a\u5fc3\u9ad8\u6c14\u50b2\u800c\u4e14\u5fd7\u5728\u56db\u65b9\u7684\u5973\u5b50\uff0c\u540e\u6765\u5979\u4e0e\u970d\u5929\u90fd\u7ed3\u4e86\u5a5a\uff0c\u7531\u4e8e\u6027\u683c\u7684\u4e0d\u540c\u3001\u4e24\u592b\u59bb\u867d \u6781\u6069\u7231\uff0c\u7ec8\u4e8e\u4e0d\u80fd\u5055\u8001\uff0c\u800c\u5728\u51e0\u5341\u5e74\u540e\uff0c\u5979\u679c\u7136\u4e5f\u521b\u51fa\u4e00\u6d3e\u5251\u672f\uff0c\u8fd9\u662f\u540e\u8bdd\uff0c\u4e0d\u5728\u672c\u4e66\u8303 \u56f4\uff0c\u6682\u4e14\u4e0d\u8868\u3002 \u3000\u3000\u518d\u770b\u5a04\u6850\u5b59\u4ee5\u5206\u7b4b\u9519\u9aa8\u624b\u6076\u6597\u4e8e\u627f\u73e0\u3002\u4e8e\u627f\u73e0\u8fd9\u4e00\u5e74\u6765\uff0c\u5728\u5e08\u7236\u8eab\u65c1\u5f97\u5230\u8bb8\u591a\u6307\u70b9\uff0c\u5251 \u672f\u4e5f\u5927\u6709\u8fdb\u5883\uff0c\u518d\u52a0\u4e0a\u5979\u7528\u7684\u662f\u7384\u673a\u5e08\u7956\u7684\u9547\u5c71\u5b9d\u5251\uff0c\u5a04\u5b59\u5b59\u88ab\u5979\u903c\u5f97\u79bb\u8eab\u4e00\u4e08\u5f00\u5916\uff0c\u5206\u7b4b \u9519\u9aa8\u624b\u53ea\u80fd\u81ea\u4fdd\uff0c\u6839\u672c\u5c31\u65e0\u6cd5\u8fdb\u653b\u3002 \u3000\u3000\u5b98\u519b\u7684\u706b\u628a\u4ece\u5b64\u5c71\u90a3\u8fb9\u873f\u8712\u800c\u6765\uff0c\u6709\u767e\u6570\u5341\u540d\u5b98\u519b\u5df2\u5728\u5411\u6816\u971e\u5cad\u4e0a\u6500\u767b\uff0c\u5c71\u9876\u4e0a\u7684\u5927\u77f3 \u4ecd\u662f\u6e90\u6e90\u4e0d\u65ad\u5730\u6eda\u6eda\u800c\u4e0b\uff0c\u770b\u6765\u9664\u4e86\u5df2\u7ecf\u5230\u6765\u52a9\u6218\u7684\u970d\u5929\u90fd\u4e0e\u4e8e\u627f\u73e0\u4e4b\u5916\uff0c\u8fd8\u6709\u9ad8\u624b\u5e2e\u5fd9\uff0c \u53f6\u6210\u6797\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u83ab\u975e\u662f\u5f20\u5927\u4fa0\u4e5f\u6765\u4e86\uff1f\u201d\u60f3\u51fa\u53bb\u52a9\u6218\uff0c\u5374\u653e\u5fc3\u4e0d\u4e0b\u6f6e\u97f3\u548c\u5c1a\uff0c\u4e8e \u662f\u5148\u53bb\u5bdf\u770b\u6f6e\u97f3\u7684\u4f24\u52bf\u3002 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u529f\u529b\u6df1\u539a\uff0c\u4e00\u65f6\u865a\u8131\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u4fbf\u60a0\u60a0\u9192\u8f6c\uff0c\u8fd9\u65f6\u6bd5\u64ce\u5929\u4e5f\u521a\u597d\u9192\u8f6c\uff0c\u4ed6\u88ab \u5a04\u6850\u5b59\u634f\u788e\u4e86\u7b4b\u8109\uff0c\u4f46\u89c9\u9aa8\u8282\u5267\u75db\uff0c\u767e\u9a87\u6b32\u88c2\uff0c\u4e00\u70b9\u529b\u6c14\u4e5f\u4f7f\u4e0d\u51fa\u6765\u3002\u7741\u5f00\u773c\u4e00\u770b\uff0c\u7a81\u7136\u89c1 \u6f6e\u97f3\u548c\u5c1a\u6b63\u5750\u5728\u4ed6\u7684\u5bf9\u9762\uff0c\u4e24\u53ea\u773c\u775b\u7741\u5f97\u53c8\u5706\u53c8\u5927\uff0c\u767b\u65f6\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff01 \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u770b\u6e05\u695a\u4e86\u662f\u6bd5\u64ce\u5929\uff0c\u7aef\u7684\u662f\u6012\u4ece\u5fc3\u4e0a\u8d77\uff0c\u6076\u5411\u80c6\u8fb9\u751f\uff0c\u634f\u8d77\u4e24\u53ea\u62f3\u5934\uff0c\u201c\u563f\u201d \u7684\u4e00\u58f0\u51b7\u7b11\u9053\uff1a\u201c\u5929\u7f51\u6062\u6062\uff0c\u758f\u800c\u4e0d\u6f0f\u3002\u563f\uff0c\u4f60\u7ec8\u987b\u8fd8\u662f\u649e\u5728\u6d12\u5bb6\u624b\u4e0a\uff01\u201d\u6765\u4e0d\u53ca\u8df3\u8d77\uff0c\u4fbf \u662f\u4e00\u62f3\u5288\u9762\u6363\u53bb\u3002 \u3000\u3000\u53f6\u6210\u6797\u53eb\u9053\uff1a\u201c\u5e08\u4f2f\u7956\u4e14\u6162\u52a8\u624b\uff01\u201d\u6f6e\u97f3\u548c\u5c1a\u6b63\u5728\u6c14\u5934\uff0c\u4e00\u62f3\u6253\u51fa\uff0c\u6536\u4e5f\u6536\u4e0d\u56de\u6765\uff0c\u5ffd \u542c\u5f97\u6709\u4eba\u7b11\u9053\uff1a\u201c\u5e08\u4f2f\uff0c\u4f60\u771f\u662f\u59dc\u6842\u4e4b\u6027\uff0c\u8001\u800c\u5f25\u8fa3\uff01\u4e5f\u7528\u4e0d\u7740\u4e3a\u8fd9\u53ae\u751f\u6c14\u554a\uff01\u201d\u4f46\u89c1\u5fae\u98ce \u98d2\u7136\uff0c\u767d\u8863\u4e00\u95ea\uff0c\u5374\u539f\u6765\u662f\u5f20\u4e39\u67ab\u5230\u4e86\uff01 \u3000\u3000\u5f20\u4e39\u67ab\u5de6\u624b\u63a5\u7740\u6f6e\u97f3\u548c\u5c1a\uff0c\u53f3\u624b\u6309\u7740\u6bd5\u64ce\u5929\u3002\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u4e39\u67ab\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u5f20\u4e39 \u67ab\u5fae\u7b11\u9053\uff1a\u201c\u6211\u6709\u8bdd\u8bf4\u3002\u201d\u4e24\u9053\u773c\u5149\u6709\u5982\u5229\u5251\uff0c\u671d\u7740\u6bd5\u64ce\u5929\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u60f3\u5411\u6211\u8ba8\u5f6d \u548c\u5c1a\u90a3\u4efd\u5730\u56fe\uff0c\u4e0e\u6731\u660e\u5929\u5b50\u4e00\u4e89\u5929\u4e0b\uff0c\u5374\u600e\u7684\u8fd9\u6837\u6ca1\u6709\u9aa8\u6c14\uff0c\u4f60\u5c06\u6765\u6709\u4f55\u9762\u81ea\u89c1\u4f60\u53bb\u7236\u4eb2\u4e8e \u5730\u4e0b\uff1f\u201d \u3000\u3000\u6bd5\u64ce\u5929\u6068\u4e0d\u5f97\u6709\u4e2a\u5730\u6d1e\u94bb\u4e86\u8fdb\u53bb\uff0c\u7f9e\u60ed\u6127\u4fae\u4e4b\u6781\uff0c\u4e00\u54ac\u7259\u6839\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e8b\u5df2\u81f3\u6b64\uff0c \u4e0d\u5fc5\u591a\u8a00\uff0c\u5f20\u4e39\u67ab\uff0c\u4f60\u5c31\u4e00\u5251\u628a\u6211\u6740\u4e86\u5427\uff01\u201d \u3000\u3000\u5f20\u4e39\u67ab\u4ef0\u5929\u5927\u7b11\uff0c\u500f\u5730\u7b11\u58f0\u4e00\u6536\uff0c\u655b\u5bb9\u8bf4\u9053\uff1a\u201c\u6211\u8981\u6740\u4f60\uff0c\u4e5f\u4e0d\u5f85\u4eca\u5929\u3002\u60f3\u4f60\u6bd5\u5bb6\u4e16\u4ee3 \u82f1\u8c6a\uff0c\u4f60\u66fe\u7956\u6bd5\u6e05\u6cc9\u521b\u7acb\u4e10\u5e2e\uff0c\u4f60\u7956\u7236\u6bd5\u51cc\u865a\u52a9\u5f20\u58eb\u8bda\u9a71\u9010\u5143\u5175\uff0c\u4f60\u7236\u4eb2\u9707\u4e09\u754c\u6bd5\u9053\u51e1\u66f4\u662f \u82f1\u96c4\u76d6\u4e16\uff0c\u6b66\u6797\u5171\u4ef0\uff0c\u4f60\u60f3\u60f3\u4f60\u7684\u5217\u7956\u5217\u5b97\uff0c\u5f53\u771f\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u6127\u6094\u4e48\uff1f\u201d \u3000\u3000\u6bd5\u64ce\u5929\u9762\u4e0a\u4e00\u9635\u7ea2\u4e00\u9635\u767d\uff0c\u84e6\u5730\u568e\u5555\u5927\u54ed\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u5c31\u5411\u5927\u5ca9\u77f3\u4e00\u5934\u649e\u53bb\uff0c\u5374\u88ab\u5f20\u4e39 \u67ab\u8f7b\u8f7b\u5730\u628a\u4ed6\u6551\u4e86\u56de\u6765\uff0c\u53ea\u542c\u5f97\u5f20\u4e39\u67ab\u7f13\u7f13\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u5c0f\u65f6\u5019\u6211\u5728\u5b98\u519b\u624b\u4e2d\u5c06\u4f60\u6551\u8fc7\u4e00\u6b21 \uff08\u8be6\u89c1\u300a\u840d\u8e2a\u4fa0\u5f71\u5f55\u300b\uff09\uff0c\u4eca\u5929\u4e4b\u4e8b\uff0c\u662f\u4f60\u81ea\u5df1\u9020\u5b7d\uff0c\u81ea\u4f5c\u5b7d\uff0c\u4e0d\u53ef\u6d3b\uff0c\u6309\u8bf4\u4e0d\u518d\u6551\u4f60\u4e86\u3002 \u4f46\u4e00\u6765\u770b\u5728\u4f60\u7956\u7236\u3001\u7236\u4eb2\u7684\u4efd\u4e0a\uff0c\u4e8c\u6765\u4f60\u6bd5\u5bb6\u72ec\u95e8\u6b66\u529f\uff0c\u4e10\u5e2e\u4e16\u4ee3\u76f8\u4f20\u7684\u8863\u94b5\uff0c\u4e5f\u4e0d\u5f53\u81f3\u4f60 \u800c\u7edd\uff01\u597d\u5427\uff0c\u6211\u4fbf\u5728\u5b98\u519b\u624b\u4e2d\uff0c\u518d\u6551\u4f60\u4e00\u6b21\uff01\u201d \u3000\u3000\u6f6e\u97f3\u548c\u5c1a\u706b\u6c14\u867d\u5927\uff0c\u5176\u5b9e\u5fc3\u80a0\u6781\u8f6f\uff0c\u542c\u5f20\u4e39\u67ab\u63d0\u8d77\u6bd5\u5bb6\u5386\u4ee3\u7684\u82f1\u96c4\uff0c\u60f3\u8d77\u4e86\u6bd5\u64ce\u5929\u7684\u7236 \u4eb2\u6bd5\u9053\u51e1\u6b63\u662f\u4ed6\u7684\u6700\u597d\u670b\u53cb\uff0c\u66f4\u56e0\u4e3a\u770b\u89c1\u4e86\u6bd5\u64ce\u5929\u6d41\u4e86\u773c\u6cea\uff0c\u90a3\u4e00\u5bf9\u62f3\u5934\u65e9\u5df2\u4e0d\u77e5\u4e0d\u89c9\u5730\u6536 \u4e86\u56de\u6765\uff0c\u4f46\u4ecd\u662f\u653e\u5fc3\u4e0d\u8fc7\uff0c\u95ee\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u6c5f\u5c71\u6613\u6539\uff0c\u54c1\u6027\u96be\u79fb\uff0c\u4f60\u4e0d\u6015\u4ed6\u518d\u9020\u5b7d\u4e48\uff1f\u201d \u3000\u3000\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u4ed6\u53d7\u4e86\u8fd9\u4e00\u6b21\u6559\u8bad\uff0c\u6599\u60f3\u4e0d\u4f1a\u518d\u8e48\u8986\u8f99\u4e86\u3002\u4f55\u51b5\u4ed6\u5df2\u88ab\u5a04\u6850\u5b59\u7684\u5206\u7b4b\u9519\u9aa8 \u624b\u5f04\u7834\u4e86\u5341\u4e00\u4e8c\u6761\u7b4b\u8109\uff0c\u8fd9\u4e00\u8eab\u6b66\u529f\uff0c\u5df2\u662f\u5e9f\u4e86\u3002\u4ed6\u4eca\u540e\u53ea\u53ef\u4ee5\u6307\u70b9\u522b\u4eba\u7684\u6b66\u529f\uff0c\u81ea\u5df1\u662f\u4e0d \u80fd\u518d\u4e0e\u522b\u4eba\u6597\u80dc\u4e89\u5f3a\u4e86\u3002\u201d \u3000\u3000\u6bd5\u64ce\u5929\u521a\u624d\u5168\u795e\u8d2f\u6ce8\uff0c\u542c\u5f20\u4e39\u67ab\u5bf9\u81ea\u5df1\u7684\u5904\u51b3\u3002\u8fd9\u65f6\u677e\u4e86\u53e3\u6c14\uff0c\u60f3\u8d77\u81ea\u5df1\u5df2\u7ecf\u6b8b\u5e9f\u65f6\u53c8 \u89c9\u5468\u8eab\u5267\u75db\uff0c\u4e00\u7c92\u7c92\u9ec4\u8c46\u822c\u5927\u7684\u6c57\u73e0\u76f4\u6dcc\u51fa\u6765\u3002\u5f20\u4e39\u67ab\u638f\u51fa\u4e86\u4e00\u9897\u78a7\u7eff\u8272\u7684\u4e39\u4e5d\uff0c\u8bf4\u9053\uff1a \u201c\u8fd9\u662f\u6211\u81ea\u7ec3\u7684\u5c11\u9633\u5c0f\u8fd8\u4e39\uff0c\u53ef\u4ee5\u4fdd\u5f97\u4f4f\u4f60\u4e09\u5929\u7684\u5143\u6c14\uff0c\u8d81\u7740\u6211\u4eec\u7ed9\u4f60\u6321\u7740\u5b98\u519b\uff0c\u4f60\u8d76\u5feb\u4ece \u5c71\u540e\u9003\u8d70\u5427\uff01\u201d \u3000\u3000\u6bd5\u64ce\u5929\u53eb\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u6b21\u4e43\u662f\u6b7b\u540e\u91cd\u751f\uff0c\u6628\u65e5\u7684\u6bd5\u64ce\u5929\u7b97\u662f\u57cb\u5230\u575f\u5893\u91cc\u4e86\uff01\u201d\u5411\u5f20\u4e39\u67ab \u78d5\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u7acb\u5373\u8f6c\u8eab\u4fbf\u8dd1\u3002 \u3000\u3000\u4f17\u4eba\u76ee\u9001\u4ed6\u7684\u80cc\u5f71\u4e0b\u5c71\uff0c\u65e0\u4e0d\u611f\u53f9\u3002\u5ffd\u89c1\u5c0f\u864e\u5b50\u8e66\u8e66\u8df3\u8df3\u5730\u8dd1\u6765\uff0c\u53eb\u9053\uff1a\u201c\u53c8\u6709\u4e00\u80a1\u5b98 \u519b\u4e0a\u5c71\u6765\u4e86\u3002\u5e08\u7236\uff0c\u4f60\u4e0d\u53bb\u5e2e\u5fd9\u5e08\u53d4\u4e48\uff1f\u201d\u539f\u6765\u4e91\u91cd\u3001\u4e91\u857e\u90fd\u4e0e\u5f20\u4e39\u67ab\u540c\u6765\uff0c\u5c71\u4e0a\u7684\u77f3\u5934\uff0c \u6b63\u662f\u4e91\u91cd\u4ee5\u91d1\u94a2\u638c\u529b\u63a8\u4e0b\u53bb\u7684\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u7b49\u4f60\u5e08\u59d0\u548c\u970d\u5927\u54e5\u4e00\u4f1a\u597d\u5417\uff1f\u4f60\u7559\u5fc3\u770b\u770b\u4f60\u970d\u5927\u54e5\u7684\u5251\u6cd5\u3002\u201d \u3000\u3000\u9633\u5b97\u6d77\u89c1\u5f20\u4e39\u67ab\u7a81\u5982\u5176\u6765\uff0c\u65e9\u5df2\u614c\u4e86\u624b\u811a\uff0c\u4f46\u88ab\u970d\u5929\u90fd\u5236\u4e86\u673a\u5148\uff0c\u65e0\u6cd5\u8131\u8eab\u903c\u5f97\u6b7b\u4e2d\u6c42 \u6d3b\uff0c\u8fed\u4f7f\u9669\u62db\uff0c\u970d\u5929\u90fd\u4ee5\u9759\u5236\u52a8\uff0c\u4ee5\u63f4\u5236\u6025\uff0c\u4e00\u53e3\u5251\u4e0d\u75be\u4e0d\u5f90\uff0c\u5374\u662f\u7d27\u7d27\u5c01\u4e86\u9633\u5b97\u6d77\u7684\u9000 \u8def\uff0c\u7aef\u7684\u6709\u6d41\u6c34\u884c\u4e91\uff0c\u6781\u5f97\u8f7b\u7075\u7fd4\u52a8\u4e4b\u5999\uff01\u5f20\u4e39\u67ab\u9891\u9891\u70b9\u9996\uff0c\u5bf9\u6f6e\u97f3\u548c\u5c1a\u9053\uff1a\u201c\u4ece\u6b64\u4e4b\u540e\uff0c \u6b66\u6797\u4e2d\u53c8\u5c06\u591a\u4e00\u5251\u6d3e\u4e86\uff01\u201d \u3000\u3000\u5c0f\u864e\u5b50\u9053\uff1a\u201c\u4e8e\u59d0\u59d0\u7684\u5251\u672f\u4e5f\u4e0d\u89c1\u5f97\u5c31\u8f93\u4e8e\u4ed6\u4e86\u3002\u201d\u5c0f\u864e\u5b50\u56e0\u4e3a\u7b2c\u4e00\u6b21\u9047\u89c1\u970d\u5929\u90fd\u4e4b \u65f6\uff0c\u4fbf\u906d\u4ed6\u620f\u5f04\uff0c\u6545\u6b64\u5bf9\u4ed6\u603b\u662f\u4e0d\u5927\u670d\u6c14\u3002\u4f17\u4eba\u770b\u65f6\uff0c\u53ea\u89c1\u4e8e\u627f\u73e0\u7684\u9752\u51a5\u5b9d\u5251\u970d\u970d\u5c55\u5f00\uff0c\u7aef \u7684\u662f\u67d4\u5982\u67f3\u7d6e\uff0c\u7ffb\u82e5\u60ca\u9e3f\uff0c\u52a0\u4e0a\u5b9d\u5251\u7684\u5149\u8292\u56db\u5c04\uff0c\u4e0e\u5a04\u6850\u5b59\u6253\u5f97\u96be\u5206\u96be\u89e3\uff0c\u4e24\u4e2a\u4eba\u90fd\u4f3c\u88f9\u5728 \u7cbe\u5149\u51b7\u7535\u4e4b\u4e2d\uff0c\u770b\u4e0a\u53bb\u6bd4\u970d\u5929\u90fd\u7684\u5251\u52bf\u8fd8\u66f4\u8981\u7f8e\u5999\u597d\u770b\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4f60\u5e08\u59d0\u8fd9\u4e00\u5e74\u6765\u8fdb\u5883\u751a\u901f\uff0c\u5927\u662f\u4e0d\u6613\uff0c\u4f46\u970d\u5929\u90fd\u7684\u5251\u6cd5\u5df2\u6e10\u81f3\u878d\u4f1a\u8d2f \u901a\uff0c\u72ec\u521b\u4e00\u5bb6\u4e4b\u5883\uff0c\u5c06\u6765\u8fde\u6211\u4e5f\u672a\u5fc5\u6bd4\u5f97\u4e0a\u4ed6\u3002\u201d\u51cc\u4e91\u51e4\u628a\u773c\u770b\u65f6\uff0c\u4f46\u89c1\u9633\u5b97\u6d77\u5ffd\u5730\u731b\u653b\uff0c \u5251\u8d77\u5904\uff0c\u201c\u6012\u6d9b\u5377\u7a7a\u201d\u201c\u9ec4\u6c99\u853d\u65e5\u201d\uff0c\u4e00\u8fde\u4e24\u62db\u6700\u51cc\u5389\u7684\u62db\u6570\uff0c\u5251\u5149\u604d\u4f3c\u6e14\u7fc1\u6492\u7f51\uff0c\u4e00\u5927\u7247 \u5149\u7f51\u5f53\u5934\u76f4\u7f69\u4e0b\u6765\uff0c\u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u9633\u5b97\u6d77\u60c5\u6025\u62fc\u547d\uff0c\u66f4\u4fc3\u5176\u8d25\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u970d\u5929\u90fd \u7ed5\u8eab\u6643\u6b65\uff0c\u53cd\u8e0f\u6d2a\u95e8\uff08\u4e2d\u8def\u65b9\u4f4d\uff09\uff0c\u84e6\u7136\u95f4\u820c\u7efd\u6625\u96f7\uff0c\u5927\u559d\u4e00\u58f0\u201c\u6492\u5251\u201d\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u5577\u201d \u4e00\u58f0\uff0c\u7d2b\u8679\u7535\u5c04\uff0c\u9633\u5b97\u6d77\u7684\u90a3\u628a\u5927\u5185\u5b9d\u5251\uff0c\u679c\u7136\u8131\u624b\u98de\u53bb\uff0c\u970d\u5929\u90fd\u98de\u8eab\u4e00\u63a0\uff0c\u628a\u5b9d\u5251\u62a2\u5230\u624b \u4e2d\uff0c\u9633\u5b97\u6d77\u6b66\u529f\u4e5f\u786e\u7b97\u9ad8\u5f3a\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u4e4b\u95f4\uff0c\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c\u843d\u4e0b\u5c71\u5761\uff0c\u5982 \u98de\u5954\u8dd1\uff0c\u5f20\u4e39\u67ab\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5b9d\u5251\u6613\u624b\u3002\u4ece\u4eca\u4e4b\u540e\uff0c\u5929\u4e0b\u56db\u5927\u5251\u5ba2\u4e5f\u6362\u4e86\u65b0\u4eba\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u89c1\u970d\u5929\u90fd\u5f97\u80dc\uff0c\u81ea\u5df1\u4e0e\u5a04\u6850\u5b59\u5374\u4ecd\u662f\u76f8\u6301\u4e0d\u4e0b\uff0c\u5fc3\u4e2d\u7126\u8e81\uff0c\u84e6\u7136\u95f4\u5251\u6cd5\u4e00\u53d8\uff0c\u4f7f \u5230\u75be\u5904\uff0c\u4e00\u7247\u9752\u5149\u6325\u970d\uff0c\u8fde\u4eba\u5f71\u4e5f\u6df9\u6ca1\u5728\u5251\u5149\u4e4b\u4e2d\uff0c\u5a04\u6850\u5b59\u6e10\u611f\u96be\u4ee5\u5e94\u4ed8\uff0c\u4f46\u4ed6\u529f\u529b\u7a76\u7adf\u6bd4 \u4e8e\u627f\u73e0\u5c1a\u80dc\u4e00\u7b79\uff0c\u638c\u6307\u517c\u65bd\uff0c\u6bcf\u6bcf\u5c06\u4e8e\u627f\u73e0\u7684\u5251\u70b9\u9707\u6b6a\uff0c\u5230\u4e86\u7d27\u5f20\u5173\u5934\uff0c\u4fbf\u7a81\u7136\u8fd0\u7528\u4e00\u4e24\u62db \u6781\u7cbe\u5999\u7684\u5206\u7b4b\u9519\u9aa8\u624b\u6cd5\uff0c\u963b\u788d\u4e8e\u627f\u73e0\u7684\u653b\u52bf\uff0c\u5c0f\u864e\u5b50\u53eb\u9053\uff1a\u201c\u5e08\u59d0\uff0c\u4f60\u53f7\u79f0\u6563\u82b1\u5973\u4fa0\uff0c\u4e3a\u4ec0 \u4e48\u4e0d\u7528\u91d1\u82b1\u6697\u5668\uff1f\u201d\u8bdd\u58f0\u672a\u505c\uff0c\u53ea\u89c1\u4e8e\u627f\u73e0\u53cd\u624b\u4e00\u5251\uff0c\u5728\u5251\u5149\u8000\u773c\u4e4b\u4e2d\uff0c\u4e09\u6735\u91d1\u82b1\u5df2\u662f\u7535\u5c04 \u800c\u51fa\u3002 \u3000\u3000\u5a04\u6850\u5b59\u8eab\u56de\u52bf\u8f6c\uff0c\u7b2c\u4e00\u6735\u91d1\u82b1\u8d34\u7740\u808b\u65c1\uff0c\u500f\u7136\u7a7f\u8fc7\uff0c\u6325\u8896\u4e00\u62c2\uff0c\u7eb5\u8eab\u4e00\u8dc3\uff0c\u4e8c\u4e09\u4e24\u6735\u91d1 \u82b1\u4e00\u88ab\u62c2\u843d\u4e00\u88ab\u95ea\u5f00\u3002\u4e8e\u627f\u73e0\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u770b\u4f60\u80fd\u95ea\u5f97\u51e0\u65f6\uff1f\u201d\u8d8a\u6253\u8d8a\u72e0\uff0c\u63a5\u8fde\u6253\u51fa\u4e86\u4e09\u5341 \u516d\u6735\u91d1\u82b1\uff0c\u4f46\u89c1\u91d1\u82b1\u4ea4\u7ec7\uff0c\u6ee1\u7a7a\u98de\u821e\uff0c\u98de\u6765\u98de\u53bb\uff0c\u4e92\u76f8\u78b0\u51fb\uff0c\u6216\u8d70\u76f4\u7ebf\uff0c\u6216\u8d70\u5f27\u5f62\uff0c\u7ade\u65e0\u4e00 \u6735\u8dcc\u843d\u5730\u4e0a\uff0c\u800c\u4e14\u4e09\u5341\u516d\u6735\u91d1\u82b1\uff0c\u5206\u6253\u4eba\u8eab\u7684\u4e09\u5341\u516d\u9053\u5927\u7a74\uff0c\u8ba4\u7a74\u4e4b\u51c6\uff0c\u6beb\u4e0d\u6df7\u4e71\uff0c\u5999\u5230\u6beb \u5dc5\uff01\u5f20\u4e39\u67ab\u4e5f\u6697\u6697\u53eb\u597d\u3002\u539f\u6765\u4e8e\u627f\u73e0\u8fd9\u4e2a\u201c\u91d1\u82b1\u6253\u7a74\u201d\u7684\u624b\u6cd5\uff0c\u9664\u4e86\u5f97\u81ea\u4e91\u857e\u4f20\u6388\u4e4b\u5916\uff0c\u8fd8 \u53c2\u609f\u4e86\u897f\u57df\u5f02\u4eba\u963f\u8428\u739b\u7684\u91d1\u7403\u624b\u6cd5\uff0c\u9664\u4e86\u529f\u529b\u7a0d\u5dee\u4e4b\u5916\uff0c\u5df2\u662f\u9752\u51fa\u4e8e\u84dd\uff0c\u5728\u5e08\u6bcd\u4e4b\u4e0a\u4e86\u3002 \u3000\u3000\u5a04\u6850\u5b59\u5728\u4e09\u5341\u516d\u6735\u91d1\u82b1\u5305\u56f4\u4e4b\u4e0b\uff0c\u50cf\u715e\u4e00\u53ea\u65e0\u5934\u82cd\u8747\uff0c\u4e71\u98de\u4e71\u95ef\uff0c\u5ffd\u5730\u91cc\u4e00\u58f0\u60e8\u53eb\uff0c\u524d \u5fc3\u540e\u5fc3\u819d\u76d6\u811a\u8e1d\u4e00\u8fde\u4e2d\u4e86\u4e03\u516b\u6735\u91d1\u82b1\u3002\u5f20\u4e39\u67ab\u53eb\u9053\uff1a\u201c\u73e0\u513f\uff0c\u53ef\u4ee5\u4f4f\u624b\u4e86\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u7684\u91d1\u82b1\u6697\u5668\u4e0d\u4f46\u53ef\u4ee5\u6253\u7a74\uff0c\u800c\u4e14\u82b1\u74e3\u950b\u5229\uff0c\u8d5b\u5982\u5315\u9996\uff0c\u4f4f\u624b\u4e00\u770b\uff0c\u4f46\u89c1\u5a04\u6850\u5b59\u5df2 \u6210\u4e86\u4e00\u4e2a\u8840\u4eba\u3002\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u770b\u5728\u4f60\u5e08\u7236\u4efd\u4e0a\uff0c\u9976\u4f60\u4e0d\u6b7b\uff0c\u8fd8\u4e0d\u5feb\u8d70\uff01\u201d\u5a04\u6850\u5b59\u4e00\u8df7\u4e00\u62d0\u5730 \u5954\u4e0b\u5c71\u5761\uff0c\u4ed6\u7684\u7435\u7436\u9aa8\u5df2\u88ab\u6253\u7a7f\uff0c\u819d\u76d6\u7684\u7b4b\u8109\u4e5f\u7ed9\u524a\u65ad\uff0c\u50cf\u6bd5\u64ce\u5929\u4e00\u6837\uff0c\u8fd9\u8eab\u6b66\u529f\u4ea6\u5df2\u5e9f \u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u5b98\u519b\u5df2\u6c47\u96c6\u4e86\u6570\u767e\u4eba\u653b\u4e0a\u5c71\u5934\uff0c\u76d8\u5929\u7f57\u6325\u821e\u952f\u9f7f\u957f\u97ad\uff0c\u9996\u5148\u653b\u5230\uff0c\u5f20\u4e39\u67ab\u9053\uff1a\u201c\u8fd9 \u662f\u4e00\u4e2a\u6d51\u4eba\uff0c\u5c0f\u864e\u5b50\uff0c\u4f60\u7ed9\u6211\u6253\u4ed6\u51e0\u4e2a\u5634\u5df4\uff0c\u53eb\u4ed6\u5feb\u6eda\uff01\u201d\u76d8\u5929\u7f57\u542c\u5f97\u5f20\u4e39\u67ab\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c \u5df2\u5148\u614c\u4e86\uff0c\u4f46\u89c1\u5c0f\u864e\u5b50\u679c\u7136\u626c\u624b\u6765\u6253\u4ed6\u7684\u5634\u5df4\uff0c\u6012\u6c14\u53c8\u751f\u3002\u952f\u9f7f\u97ad\u970d\u5730\u4e00\u626b\uff0c\u8981\u5c06\u5c0f\u864e\u5b50\u62e6 \u8170\u5377\u5012\uff0c\u54ea\u77e5\u97ad\u68a2\u521a\u8d77\uff0c\u624b\u8155\u5173\u8282\u5ffd\u5730\u4e00\u9635\u9178\u9ebb\uff0c\u529b\u4e0d\u4ece\u5fc3\uff0c\u7ade\u88ab\u5c0f\u864e\u5b50\u72e0\u72e0\u7684\u6253\u4e86\u4e24\u5df4 \u638c\uff0c\u556a\u556a\u4e24\u58f0\uff0c\u4e24\u9897\u95e8\u7259\u7adf\u7136\u6253\u6298\u3002\u5c0f\u864e\u5b50\u5728\u8d35\u5dde\u82d7\u7586\u4e4b\u65f6\uff0c\u66fe\u88ab\u76d8\u5929\u7f57\u6b3a\u4fae\uff0c\u8fd9\u65f6\u4ed7\u7740\u5e08 \u7236\u6697\u52a9\uff0c\u5f97\u4ee5\u4eb2\u624b\u62a5\u4ec7\uff0c\u5feb\u610f\u4e4b\u6781\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6211\u5e08\u7236\u53eb\u4f60\u5feb\u6eda\uff0c\u8fd8\u4e0d\u6eda\u4e48\uff1f\u201d\u556a\u7684\u53c8\u662f \u4e00\u8bb0\u5634\u5df4\uff0c\u8fd9\u4e00\u8bb0\u6253\u5f97\u66f4\u91cd\uff0c\u6253\u5f97\u76d8\u5929\u7f57\u679c\u7136\u629b\u4e86\u957f\u97ad\uff0c\u62b1\u5934\u75be\u6eda\uff0c\u5c0f\u864e\u5b50\u4e50\u5f97\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7387\u9886\u4f17\u4eba\u524d\u53bb\u4e0e\u4e91\u91cd\u3001\u4e91\u857e\u4f1a\u5408\uff0c\u62d4\u8d77\u4e86\u5341\u51e0\u68f5\u5927\u6811\uff0c\u5728\u5c71\u4e0a\u6eda\u4e0b\uff0c\u4e91\u91cd\u53c8\u65bd\u5c55 \u4e86\u91d1\u521a\u5927\u529b\u624b\u6cd5\uff0c\u63a8\u5012\u4e86\u51e0\u5757\u5927\u5ca9\u77f3\uff0c\u90a3\u4e9b\u5b98\u519b\u53ea\u6068\u7239\u5a18\u751f\u5c11\u4e86\u4e24\u6761\u817f\uff0c\u907f\u4e4b\u552f\u6050\u4e0d\u53ca\uff0c\u54ea \u8fd8\u6562\u518d\u653b\u4e0a\u5c71\u5934\u3002 \u3000\u3000\u5f20\u4e39\u67ab\u7b49\u4e00\u884c\u4eba\u7acb\u523b\u4ece\u540e\u5c71\u9003\u8d70\uff0c\u6816\u971e\u5c71\u8ddd\u79bb\u53f6\u6210\u6797\u6240\u4f4f\u7684\u4e5d\u6eaa\u5341\u516b\u6da7\u4e0d\u8fc7\u4e8c\u4e09\u5341\u91cc \u8def\uff0c\u8d70\u5230\u4e86\u6768\u6885\u575e\u65f6\uff0c\u521a\u597d\u662f\u4e09\u66f4\u65f6\u5206\uff0c\u4f17\u4eba\u653e\u6162\u811a\u6b65\uff0c\u970d\u5929\u90fd\u4e0e\u51cc\u4e91\u51e4\u63e1\u624b\u5e76\u80a9\uff0c\u4e92\u95ee\u522b \u540e\u4e4b\u60c5\uff0c\u5f53\u771f\u662f\u604d\u5982\u9694\u4e16\u3002 \u3000\u3000\u53f6\u6210\u6797\u4e0e\u4e8e\u627f\u73e0\u76f8\u805a\uff0c\u4e5f\u81ea\u6709\u4e00\u756a\u611f\u6168\uff0c\u4f46\u89c9\u5fc3\u4e8b\u5982\u9ebb\uff0c\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\u3002\u4e8e\u627f\u73e0\u6b63\u60f3\u95ee \u4ed6\u5728\u5c6f\u6eaa\u7684\u60c5\u5f62\uff0c\u53f6\u6210\u6797\u5374\u5149\u95ee\u5979\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u94c1\u955c\u5fc3\u7684\u4e0b\u843d\u5417\uff1f\u201d\u4e8e\u627f\u73e0\u7709\u5934\u4e00\u76b1\uff0c \u9053\uff1a\u201c\u521a\u4e00\u89c1\u9762\uff0c\u4f60\u4e3a\u4ec0\u4e00\u4e48\u5c31\u63d0\u8d77\u4ed6\u6765\uff0c\u8ba8\u538c\u6b7b\u4e86\uff01\u201d\u53f6\u6210\u6797\u6014\u4e86\u4e00\u6014\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8981 \u4e0d\u662f\u94c1\u955c\u5fc3\uff0c\u6211\u4e0e\u51cc\u4e91\u51e4\u59d0\u59d0\u90fd\u4e0d\u80fd\u4e0e\u4f60\u76f8\u89c1\u4e86\u3002\u201d\u5c06\u94c1\u955c\u5fc3\u6551\u4e49\u519b\u8131\u9669\u7684\u7ecf\u8fc7\uff0c\u8be6\u8be6\u7ec6 \u7ec6\uff0c\u4e00\u4e00\u8bf4\u7ed9\u4e8e\u627f\u73e0\u77e5\u9053\uff0c\u4e8e\u627f\u73e0\u5446\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4ed6\u80fd\u591f\u8fd9\u6837\u3002\u55ef\uff0c\u8fd9\u8fd8\u50cf\u662f\u4e00\u4e2a \u4eba\uff01\u6211\u672c\u6765\u662f\u5f53\u4ed6\u6b7b\u4e86\uff0c\u73b0\u5728\u6211\u5012\u5e0c\u671b\u4ed6\u80fd\u591f\u6d3b\u7740\u3002\u201d\u53f6\u6210\u6797\u672c\u4ee5\u4e3a\u4e8e\u627f\u73e0\u5bf9\u94c1\u955c\u5fc3\u7684\u4fa0\u4e49 \u884c\u4e3a\u4f1a\u6709\u4e00\u756a\u8d5e\u53f9\uff0c\u89c1\u5979\u5982\u6b64\uff0c\u6b8a\u51fa\u610f\u6599\u4e4b\u5916\uff0c\u4f46\u6240\u5f97\u4e8e\u627f\u73e0\u5e7d\u5e7d\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u9000\uff1a\u201c\u5728\u676d \u5dde\u4e4b\u65f6\u2026\u2026\u201d\u5f20\u4e39\u67ab\u5ffd\u5730\u63d2\u53e3\u7b11\u9053\uff1a\u201c\u77e5\u4eba\u8bba\u4e16\uff0c\u82e5\u662f\u529f\u80fd\u63a9\u8fc7\uff0c\u90a3\u4e48\u5076\u7136\u7684\u5931\u8db3\uff0c\u90a3\u5c31\u4e0d \u63d0\u4e5f\u7f62\u3002\u55ef\uff0c\u6210\u6797\uff0c\u4f60\u771f\u7684\u60f3\u89c1\u94c1\u955c\u5fc3\u4e48\uff1f\u201d\u53f6\u6210\u6797\u5927\u559c\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u4f60\u77e5\u9053\u4ed6\u7684\u4e0b\u843d\uff1f\u201d \u5f20\u4e39\u67ab\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4eca\u665a\u5b89\u5fc3\u5730\u7761\u4e00\u89c9\uff0c\u660e\u65e5\u6211\u4fbf\u5e26\u4f60\u4eec\u53bb\u89c1\u4ed6\u3002\u201d\u53f6\u6210\u6797\u559c\u51fa\u671b\u5916\uff0c\u4e8e\u627f \u73e0\u66f4\u662f\u60ca\u7591\u4e0d\u5b9a\uff0c\u60f3\u4e0d\u5230\u5e08\u7236\u6709\u4ec0\u4e48\u795e\u901a\u3002\u4f46\u5979\u7d20\u6765\u6700\u4fe1\u670d\u5e08\u7236\uff0c\u5e08\u7236\u65e2\u7136\u8fd9\u4e48\u8bf4\uff0c\u90a3\u4e48\u660e \u5929\u5c31\u4e00\u5b9a\u80fd\u89c1\u7740\u94c1\u955c\u5fc3\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u4e8e\u627f\u73e0\u548c\u51cc\u4e91\u51e4\u8054\u5e8a\u591c\u8bdd\uff0c\u5979\u4eec\u4e8c\u4eba\uff0c\u60c5\u540c\u59d0\u59b9\uff0c\u65e0\u8bdd\u4e0d\u8c08\u3002\u51cc\u4e91\u51e4\u542c\u8bf4\u970d\u5929\u90fd \u5f97\u5230\u5f20\u4e39\u67ab\u6307\u70b9\u5251\u672f\u8981\u8bc0\uff0c\u4e0a\u4e58\u5fc3\u6cd5\uff0c\u5341\u5206\u6b22\u559c\uff0c\u518d\u542c\u5230\u94c1\u955c\u5fc3\u5728\u676d\u5dde\u66fe\u5411\u5a04\u6850\u5b59\u6cc4\u6f0f\u4e49\u519b \u7684\u519b\u6e05\uff0c\u53c8\u4e0d\u7981\u5927\u9a82\u94c1\u955c\u5fc3\u7684\u7cca\u6d82\uff0c\u4f46\u9a82\u5b8c\u4e4b\u540e\uff0c\u5374\u53c8\u7b11\u9053\uff1a\u201c\u94c1\u955c\u5fc3\u7ecf\u8fc7\u8fd9\u6b21\u6559\u8bad\uff0c\u4e5f\u672a \u5c1d\u65e0\u76ca\u3002\u8fd9\u6b21\u4ed6\u6765\u6551\u4e49\u519b\u8131\u9669\uff0c\u5927\u5bb6\u5c31\u5f88\u611f\u6fc0\u4ed6\uff0c\u5f20\u5927\u4fa0\u8bf4\u5f97\u597d\uff0c\u77e5\u4eba\u8bba\u4e16\uff0c\u82e5\u662f\u529f\u80fd\u8865 \u8fc7\uff0c\u90a3\u4e48\u5076\u7136\u7684\u5931\u8db3\uff0c\u4e5f\u5c31\u4e0d\u5fc5\u518d\u63d0\u4e86\u3002\u55ef\uff0c\u6211\u770b\u4ed6\u5bf9\u4f60\u5012\u662f\u60c5\u6df1\u4e00\u7247\u5462\u3002\u201d\u4e8e\u627f\u73e0\u53f9\u4e86\u53e3 \u6c14\u9053\uff1a\u201c\u5e08\u7236\u662f\u6709\u610f\u9690\u6076\u626c\u5584\uff0c\u6211\u770b\u94c1\u955c\u5fc3\u8fd9\u4e2a\u4eba\uff0c\u4e0d\u662f\u4e00\u6b21\u4e24\u6b21\u7684\u6559\u8bad\u6240\u80fd\u6539\u53d8\u7684\u3002\u6211\u603b \u662f\u611f\u5230\uff0c\u4ed6\u7ec8\u7a76\u4e0d\u662f\u548c\u54b1\u4eec\u4e00\u4e2a\u8def\u5b50\u7684\u4eba\uff0c\u8fd9\u6b21\u4e5f\u5e76\u4e0d\u89c1\u5f97\u662f\u5076\u7136\u7684\u5931\u8db3\u5462\u3002\u201d\u4e8e\u627f\u73e0\u53ef\u7b97 \u5f97\u662f\u6700\u770b\u5f97\u900f\u94c1\u955c\u5fc3\u7684\u4eba\uff0c\u60f3\u8d77\u5f80\u65e5\u8bf8\u822c\u60c5\u4e8b\uff0c\u5fc3\u5934\u4e0d\u89c9\u60d8\u7136\uff0c\u8f97\u8f6c\u53cd\u4fa7\uff0c\u5c06\u8fd1\u5929\u4eae\uff0c\u624d\u548c \u4e0a\u773c\u775b\u3002 \u3000\u3000\u4e00\u89c9\u9192\u6765\uff0c\u53ea\u542c\u5f97\u5c0f\u864e\u5b50\u5431\u5431\u55b3\u55b3\u5730\u548c\u4eba\u8c08\u8bdd\uff0c\u8d77\u6765\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u6c90\u78f7\u3002\u6c90\u78f7\u53eb\u9053\uff1a \u201c\u627f\u73e0\u59d0\u59d0\uff0c\u4f60\u679c\u7136\u5728\u8fd9\u513f\u3002\u4f60\u770b\u6211\u662f\u4e0d\u662f\u957f\u5f97\u9ad8\u4e86\uff1f\u201d\u4e8e\u627f\u73e0\u5947\u9053\uff1a\u201c\u4f60\u600e\u4e48\u6765\u5230\u8fd9\u513f\uff1f \u4f60\u59d0\u59d0\u597d\u5417\uff1f\u201d\u6c90\u78f7\u9053\uff1a\u201c\u6211\u59d0\u59d0\u7b49\u7740\u4f60\u5462\uff0c\u4e0d\u8fc7\u5e08\u7236\u5429\u5490\uff0c\u53eb\u6211\u5148\u5e26\u4f60\u770b\u94c1\u955c\u5fc3\u53bb\u3002\u201d\u4e8e \u627f\u73e0\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u5e26\u6211\u53bb\u770b\u94c1\u955c\u5fc3\uff1f\u201d\u5c0f\u864e\u5b50\u672a\u66fe\u56de\u7b54\uff0c\u5f20\u4e39\u67ab\u5df2\u8d70\u51fa\u6765\u62db\u624b\u7b11\u9053\uff1a \u201c\u73e0\u513f\uff0c\u5e08\u7236\u6ca1\u9a97\u4f60\u5427\uff0c\u6211\u8bf4\u4eca\u5929\u80fd\u89c1\u7740\u94c1\u955c\u5fc3\u5c31\u4e00\u5b9a\u80fd\u89c1\u7740\u94c1\u955c\u5fc3\u3002\u201d \u3000\u3000\u539f\u6765\u6c90\u56fd\u516c\u89c1\u94c1\u955c\u5fc3\u4e45\u4e45\u4e0d\u56de\uff0c\u653e\u5fc3\u4e0d\u4e0b\uff0c\u53e6\u5916\u6d3e\u4eba\u8fdb\u4eac\u594f\u7980\u7687\u5e1d\uff0c\u8bf4\u662f\u5927\u7406\u4e4b\u4e8b\uff0c\u94c1 \u955c\u5fc3\u5e2e\u4ed6\u5904\u7406\uff0c\u4e71\u5b50\u5f97\u4ee5\u4e0d\u81f3\u95f9\u5927\uff0c\u56e0\u6b64\u4fdd\u594f\u94c1\u955c\u5fc3\u505a\u4ed6\u7684\u53c2\u519b\uff0c\u6c90\u71d5\u3001\u6c90\u78f7\u4e5f\u8ddf\u4e86\u4e13\u4f7f\u8fdb \u4eac\uff0c\u6253\u542c\u5f97\u94c1\u955c\u5fc3\u5df2\u5728\u676d\u5dde\u88ab\u62bc\uff0c\u7acb\u523b\u8bf7\u671d\u5ef7\u7684\u5927\u81e3\u4fdd\u91ca\uff0c\u90a3\u65f6\u5f20\u9aa5\u7684\u594f\u6298\u8fd8\u672a\u5230\u4eac\uff0c\u5927\u5b66 \u58eb\uff08\u76f8\u5f53\u5bb0\u76f8\u804c\u4f4d\uff09\u6768\u5ba3\u662f\u5f20\u9aa5\u7684\u4eb2\u621a\uff0c\u548c\u6c90\u56fd\u516c\u53c8\u662f\u81f3\u4ea4\uff0c\u7acb\u523b\u65a1\u65cb\u6b64\u4e8b\uff0c\u5c06\u5f20\u9aa5\u7684\u594f\u6298 \u7559\u4e0b\u4e0d\u53d1\uff0c\u5199\u4e86\u4e00\u5c01\u8be6\u7ec6\u7684\u4fe1\u7ed9\u5f20\u9aa5\u53eb\u4ed6\u5356\u6c90\u56fd\u516c\u7684\u4eba\u60c5\uff0c\u5f20\u9aa5\u5f53\u7136\u5949\u547d\u552f\u8c28\uff0c\u5728\u5fa1\u65e8\u672a\u5230 \u4e4b\u524d\uff0c\u4fbf\u5c06\u94c1\u955c\u5fc3\u8f6c\u79fb\u4e00\u4e2a\u5730\u65b9\u8f6f\u7981\uff0c\u6781\u4e3a\u4f18\u5f85\uff0c\u5f20\u4e39\u67ab\u8033\u76ee\u4f17\u591a\uff0c\u4ed6\u4e00\u5230\u676d\u5dde\u5c31\u77e5\u9053\u8fd9\u4e2a \u6d88\u606f\uff0c\u8fd9\u65f6\u6c90\u71d5\u3001\u6c90\u78f7\u4e5f\u5df2\u5230\u4e86\u676d\u5dde\uff0c\u5e26\u6765\u4e86\u786e\u5b9e\u7684\u6d88\u606f\uff0c\u8bf4\u662f\u7687\u5e1d\u5df2\u51c6\u4e88\u6240\u8bf7\uff0c\u5c31\u6d3e\u6c90\u56fd \u516c\u7684\u4e13\u4f7f\u6765\u4f20\u9012\u5fa1\u65e8\uff0c\u8fd9\u4e00\u4e24\u65e5\u4fbf\u4f1a\u5230\u676d\u5dde\u6765\u8fce\u63a5\u94c1\u955c\u5fc3\uff0c\u6c90\u71d5\u3001\u6c90\u78f7\u4f4f\u5728\u676d\u5dde\u629a\u8859\uff0c\u5f20\u4e39 \u67ab\u6084\u6084\u53bb\u4f1a\u4ed6\u4eec\uff0c\u4e8e\u627f\u73e0\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u8fd9\u4e9b\u53d8\u5316\u94c1\u955c\u5fc3\u4e5f\u4e0d\u77e5\u9053\uff0c\u4ed6\u672c\u6765\u88ab\u56da\u5728\u516d\u548c\u5854\uff0c\u5ffd\u7136\u6709\u4e00\u65e5\u5f20\u9aa5\u6d3e\u4e86\u676d\u5dde\u77e5\u5e9c\u5c06\u4ed6\u63a5 \u51fa\u6765\uff0c\u5b89\u987f\u5728\u94b1\u5858\u6c5f\u7554\u7684\u4e00\u5e62\u522b\u5885\u5185\uff0c\u9526\u8863\u7389\u98df\uff0c\u6781\u4e3a\u4f18\u5f85\uff0c\u94c1\u955c\u5fc3\u5411\u77e5\u5e9c\u8be2\u95ee\uff0c\u77e5\u5e9c\u53ea\u662f \u53eb\u4ed6\u5b89\u5fc3\u9759\u517b\u3002\u94c1\u955c\u5fc3\u4e00\u5207\u884c\u52a8\u81ea\u7531\uff0c\u672c\u6765\u53ef\u4ee5\u9003\u8dd1\uff0c\u4f46\u4ed6\u6015\u8fde\u7d2f\u7236\u4eb2\uff0c\u800c\u4e14\u4ed6\u4e5f\u62b1\u4e86\u51b3 \u5fc3\uff0c\u8981\u4e3a\u4e8e\u627f\u73e0\u727a\u7272\uff0c\u6240\u4ee5\u4e5f\u53ea\u597d\u6000\u7740\u95f7\u846b\u82a6\u5728\u676d\u5dde\u77e5\u5e9c\u7684\u522b\u5885\u5185\u9759\u517b\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u94c1\u955c\u5fc3\u8d77\u5f97\u5f88\u65e9\uff0c\u5c48\u6307\u4e00\u7b97\uff0c\u642c\u5230\u8fd9\u513f\u6765\u5df2\u7ecf\u6709\u56db\u4e94\u5929\u4e86\uff0c\u4ec0\u4e48\u6d88\u606f\u4e5f\u6ca1\u6709\u3002\u94c1 \u955c\u5fc3\u4e5f\u70e6\u5f97\u5f88\uff0c\u8d70\u51fa\u5c0f\u697c\uff0c\u501a\u680f\u8fdc\u773a\uff0c\u5317\u671b\u662f\u6797\u6728\u90c1\u701a\u7684\u51e4\u51f0\u5c71\uff0c\u5357\u671b\u662f\u6674\u7a7a\u4e00\u78a7\u7684\u94b1\u5858 \u6c5f\uff0c\u94e3\u955c\u5fc3\u53f9\u4e86\u53e3\u6c14\uff0c\u6717\u58f0\u541f\u9053\uff1a\u201c\u6c5f\u5c71\u5982\u753b\u4eba\u4f55\u5728\uff1f\u95ee\u82b1\u65e0\u8bed\u6c34\u7a7a\u6d41\uff01\u201d\u697c\u524d\u7684\u51e0\u6811\u78a7\u6843 \u84d3\u857e\u5df2\u7efd\uff0c\u770b\u6765\u7528\u4e0d\u4e86\u51e0\u5929\uff0c\u5c31\u5c06\u5f00\u6ee1\u679d\u5934\u4e86\u3002\u6c5f\u5357\u7684\u6625\u5929\u6765\u5f97\u65e9\uff0c\u5bd2\u51ac\u65b9\u8fc7\uff0c\u56ed\u5b50\u5185\u5df2\u662f \u6625\u610f\u76ce\u7136\u3002 \u3000\u3000\u53ef\u662f\u94c1\u955c\u5fc3\u7684\u5fc3\u4e2d\u5374\u662f\u5f02\u6837\u5730\u9634\u51b7\uff0c\u4ed6\u773c\u770b\u6843\u82b1\uff0c\u8033\u542c\u6c5f\u6f6e\uff0c\u9661\u7136\u95f4\u53c8\u60f3\u8d77\u4e86\u4e8e\u627f\u73e0 \u6765\uff0c\u60f3\u8d77\u4e86\u5728\u6ce2\u6d9b\u6c79\u6d8c\u7684\u957f\u6c5f\uff0c\u548c\u5979\u7b2c\u4e00\u6b21\u76f8\u9022\u7684\u60c5\u666f\uff0c\u800c\u773c\u524d\u7684\u94b1\u5858\u6c5f\u5374\u662f\u8fd9\u4e48\u5e73\u9759\u3002 \u201c\u54ce\uff0c\u6211\u8fd9\u6837\u4e3a\u4e86\u5979\uff0c\u5979\u53ef\u77e5\u9053\uff0c\u4eca\u751f\u4eca\u4e16\uff0c\u96be\u9053\u6211\u5c31\u662f\u8fd9\u6837\u5730\u548c\u5979\u6c38\u8bc0\u4e86\u4e48\uff1f\u201d\u4ed6\u77e5\u9053\u53ea \u5f85\u5fa1\u65e8\u4e00\u4e0b\uff0c\u4ed6\u7684\u547d\u8fd0\u5c31\u51b3\u5b9a\u4e86\uff0c\u4ed6\u4e5f\u66fe\u62b1\u8fc7\u4e07\u4e00\u7684\u5e0c\u671b\uff0c\u5e0c\u671b\u7687\u4e0a\u4f1a\u5ff5\u53ca\u4ed6\u7684\u7236\u4eb2\u662f\u524d\u671d \u8001\u81e3\uff0c\u5bf9\u4ed6\u4ece\u5bbd\u53d1\u843d\uff0c\u4f46\u60f3\u5230\u81ea\u5df1\u6240\u72af\u7684\u7f6a\u540d\u662f\u5982\u6b64\u4e25\u91cd\uff0c\u8fd9\u5e0c\u671b\u53c8\u50cf\u65e0\u8fb9\u4e00\u95ea\u7684\u5f69\u8679\uff0c\u8fc5 \u5373\u6d88\u5931\u4e86\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u8f7b\u8f7b\u7684\u811a\u6b65\u58f0\u8d70\u4e0a\u697c\u68af\uff0c\u94c1\u955c\u5fc3\u56de\u5934\u4e00\u671b\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u6781\u637b\u719f\u7684\u58f0\u97f3\u8f7b\u8f7b\u5524 \u9053\uff1a\u201c\u955c\u5fc3\uff01\u201d\u94c1\u955c\u5fc3\u5fc3\u5f26\u98a4\u52a8\uff0c\u51e0\u4e4e\u4e0d\u80fd\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u5fc3\u60c5\u624d\u7a0d\u7a0d\u5e73 \u9759\u4e0b\u6765\uff0c\u53eb\u9053\uff1a\u201c\u627f\u73e0\uff0c\u4f60\u662f\u600e\u4e48\u6765\u7684\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u53f6\u6210\u6797\u5c06\u4f60\u7684\u4e8b\u90fd\u544a\u8bc9\u6211\u4e86\u3002\u201d\u94c1\u955c\u5fc3\u79c0\u7709\u4e00\u5c55\uff0c\u9053\uff1a\u201c\u6211\u62fc\u7740\u820d\u4e86\u6027 \u547d\uff0c\u5c06\u4ed6\u4ece\u4e5d\u6b7b\u4e00\u751f\u7684\u5883\u5730\u4e4b\u4e2d\u6551\u4e86\u51fa\u6765\uff0c\u4ed6\u90fd\u544a\u8bc9\u4e86\u4f60\u4e48\u3002\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6ca1\u6709\u4e00\u70b9\u9057 \u6f0f\u3002\u5012\u662f\u6211\u5c06\u4f60\u5728\u676d\u5dde\u6240\u505a\u7684\u4e8b\u60c5\u7792\u4e86\u4ed6\u4e86\u3002\u4ed6\u4eec\u5bf9\u4f60\u5f88\u662f\u611f\u8c22\u3002\u201d \u3000\u3000\u94c1\u955c\u5fc3\u201c\u563f\u563f\u201d\u4e00\u7b11\uff1b\u9053\uff1a\u201c\u627f\u73e0\uff0c\u8981\u4e0d\u662f\u4e3a\u4e86\u4f60\uff0c\u6211\u624d\u61d2\u5f97\u7406\u4f1a\u4ed6\u5462\u3002\u627f\u73e0\uff0c\u4f60\u90a3\u5c01 \u4fe1\u9a82\u5f97\u6211\u597d\u60e8\uff0c\u73b0\u5728\u4f60\u603b\u8be5\u770b\u6e05\u695a\u4e86\u6211\u94c1\u955c\u5fc3\u662f\u600e\u6837\u7684\u4e00\u4e2a\u4eba\u7269\u4e86\u5427\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u4e0d \u9519\uff0c\u7ecf\u8fc7\u4e86\u8fd9\u4e00\u4f1a\uff0c\u6211\u662f\u770b\u5f97\u66f4\u6e05\u695a\u4e86\u3002\u4f60\u6015\u6211\u770b\u4e0d\u8d77\u4f60\uff0c\u66f4\u6015\u5929\u4e0b\u82f1\u96c4\u803b\u7b11\uff0c\u8bf4\u4f60\u51fa\u5356\u670b \u53cb\uff0c\u56e0\u6b64\u4f60\u603b\u7b97\u505a\u51fa\u4e86\u4e00\u6869\u597d\u4e8b\u3002\u4f60\u6709\u70b9\u7cca\u6d82\uff0c\u5374\u4e5f\u8fd8\u7b97\u5f97\u662f\u6709\u70b9\u6b63\u4e49\u611f\u7684\u8bfb\u4e66\u4eba\u3002\u201d\u94c1\u955c \u5fc3\u597d\u50cf\u6cc4\u4e86\u6c14\u7684\u76ae\u7403\uff0c\u6124\u7136\u8bf4\u9053\uff1a\u201c\u5c31\u4ec5\u4ec5\u662f\u8fd9\u6837\u5417\uff1f\u201d\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u4f60\u8981\u6211\u628a\u4f60\u8bf4\u6210\u662f \u4e00\u4e2a\u53e4\u5f80\u4eca\u6765\uff0c\u7edd\u65e0\u4ec5\u6709\u7684\u5927\u82f1\u96c4\u5927\u8c6a\u6770\u4e48\uff1f\u201d \u3000\u3000\u94c1\u955c\u5fc3\u50b2\u7136\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\uff0c\u6211\u5f53\u7136\u4e0d\u662f\u4ec0\u4e48\u82f1\u96c4\u8c6a\u6770\uff0c\u4f46\u53f6\u6210\u6797\u8981\u4e0d\u662f\u6211\uff0c\u4ed6 \u65e9\u5df2\u88ab\u5b98\u519b\u6240\u4fd8\uff0c\u73b0\u5728\u5728\u76d1\u7262\u91cc\u5c06\u662f\u4ed6\u800c\u4e0d\u662f\u6211\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u7709\u5934\u4e00\u76b1\uff0c\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u8981\u4e0d \u662f\u4f60\u505a\u4e86\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6211\u8fd8\u4f1a\u5c06\u4f60\u5f53\u4f5c\u4e00\u4e2a\u4eba\u770b\u5f85\u5417\uff1f\u8981\u4e0d\u662f\u4f60\u6cc4\u6f0f\u4e86\u4e49\u519b\u7684\u519b\u60c5\uff0c\u4ed6\u4eec\u4e5f\u4e0d \u81f3\u4e8e\u4e00\u8d25\u6d82\u5730\uff0c\u955c\u5fc3\uff0c\u4e00\u4e2a\u81ea\u50b2\u7684\u4eba\u4e5f\u5e94\u8be5\u662f\u4e00\u4e2a\u5584\u4e8e\u81ea\u8d23\u7684\u4eba\uff01\u201d\u8fd9\u4e00\u77ac\u95f4\uff0c\u53ea\u89c1\u94c1\u955c\u5fc3 \u500f\u7136\u53d8\u4e86\u989c\u8272\uff0c\u4ed6\u60f3\u4e0d\u5230\u4e8e\u627f\u73e0\u6b64\u6765\uff0c\u7adf\u7136\u5e76\u6ca1\u6709\u8868\u793a\u4ec0\u4e48\u611f\u6fc0\uff0c\u5374\u662f\u5411\u4ed6\u8bf4\u51fa\u8fd9\u4e00\u756a\u8bf4 \u8bdd\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u94c1\u955c\u5fc3\u51b7\u7b11\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u8fd9\u4e00\u7fa4\u8349\u83bd\u82f1\u96c4\uff0c\u4e4c\u5408\u4e4b\u4f17\uff0c\u5f04\u5230\u4eca\u5929\u8fd9\u4e2a\u6837 \u5b50\uff0c\u5c31\u5b8c\u5168\u662f\u4e3a\u4e86\u6211\u94c1\u67d0\u4eba\uff1f\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u662f\u5b8c\u5168\u4e3a\u4f60\uff0c\u53ef\u662f\u4f60\u6cc4\u6f0f\u519b\u60c5\uff0c\u4e5f\u6b63\u662f \u50cf\u843d\u4e95\u6295\u77f3\u4e00\u822c\uff0c\u4e49\u519b\u5728\u5371\u96be\u4e4b\u9645\uff0c\u4f60\u5374\u5e2e\u7687\u519b\u63a8\u4e86\u4ed6\u4eec\u4e00\u628a\uff01\u201d\u94c1\u955c\u5fc3\u6c14\u9053\uff1a\u201c\u6211\u505a\u7684\u4e8b \u60c5\uff0c\u6837\u6837\u90fd\u662f\u4e3a\u4e86\u4f60\u3002\u6211\u4e5f\u4e0d\u77e5\u6211\u8fd8\u6709\u51e0\u5929\u6027\u547d\uff0c\u4f60\u5374\u5728\u6211\u4e34\u6b7b\u4e4b\u524d\uff0c\u7279\u5730\u8dd1\u6765\u5411\u6211\u8d23 \u5907\u3002\u201d \u3000\u3000\u4e8e\u627f\u73e0\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u955c\u5fc3\uff0c\u6211\u662f\u4e3a\u4e86\u4f60\u597d\uff0c\u53ef\u601c\u4f60\u5374\u4e0d\u61c2\u3002\u4e0d\u8fc7\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\uff0c\u4f60 \u6b7b\u4e0d\u4e86\u3002\u975e\u4f46\u6b7b\u4e0d\u4e86\uff0c\u8fd8\u4f1a\u6709\u5927\u5b98\u505a\uff0c\u8fd9\u662f\u6211\u5e08\u7236\u63a2\u542c\u5230\u7684\u786e\u5b9e\u6d88\u606f\uff0c\u518d\u8fc7\u4e00\u4f1a\uff0c\u5c31\u4f1a\u6709\u4eba \u6765\u63a5\u4f60\u4e86\u3002\u201d\u94c1\u955c\u5fc3\u8fd9\u4e00\u559c\u975e\u540c\u5c0f\u53ef\uff0c\u4f46\u5374\u5c3d\u529b\u6291\u5236\u7740\u4e0d\u8ba9\u5b83\u6d41\u9732\u51fa\u6765\uff0c\u4ed6\u8fd8\u8981\u505a\u6700\u540e\u7684\u6323 \u624e\uff0c\u60f3\u83b7\u5f97\u4e8e\u627f\u73e0\u7684\u5fc3\uff0c\u4ed6\u53f9\u4e86\u53e3\u6c14\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u7eb5\u7b97\u4f60\u8fd9\u6d88\u606f\u662f\u771f\uff0c\u6211\u6b7b\u4e0d\u4e86\uff0c\u4f46\u603b\u4e5f \u53ef\u4ee5\u8868\u660e\uff0c\u6211\u4e3a\u4e86\u4f60\uff0c\u4e0d\u60dc\u53bb\u6b7b\uff01\u201d\u4e8e\u627f\u73e0\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4eca\u65e5\u624d\u6765\u770b\u4f60\u3002\u54ce\uff0c\u955c\u5fc3\uff0c\u53ef\u601c\u4f60 \u603b\u662f\u4e0d\u61c2\u3002\u5982\u679c\u6211\u79f0\u8d5e\u4f60\uff0c\u8fc7\u4efd\u5730\u5c06\u4f60\u6367\u4e0a\u4e09\u5341\u4e09\u5929\uff0c\u90a3\u5c31\u662f\u53cd\u800c\u7d2f\u4e86\u4f60\u4e86\u3002\u770b\u6765\u54b1\u4eec\u7ec8\u7a76 \u4e0d\u662f\u4e00\u6761\u8def\u4e0a\u7684\u4eba\u3002\u201d\u94c1\u955c\u5fc3\u4ece\u5979\u6e29\u67d4\u7684\u58f0\u97f3\u4e2d\u542c\u51fa\u4e86\u51c4\u51c9\u60cb\u60dc\uff0c\u5fc3\u5934\u4e00\u7247\u832b\u7136\uff0c\u53c8\u53f9\u4e86\u53e3 \u6c14\u9053\uff1a\u201c\u6211\u771f\u662f\u4e0d\u61c2\u3002\u627f\u73e0\uff0c\u6bcf\u6b21\u6211\u548c\u4f60\u89c1\u9762\uff0c\u4f60\u90fd\u4f3c\u4e4e\u6bd4\u4e0a\u4e00\u6b21\u53c8\u53d8\u4e86\uff0c\u8d8a\u6765\u8d8a\u53d8\u5f97\u4f7f\u4eba \u96be\u4e8e\u7406\u89e3\u4e86\uff0c\u8d8a\u6765\u8d8a\u53d8\u5f97\u4ee4\u6211\u611f\u5230\u4f60\u597d\u50cf\u662f\u4e00\u4e2a\u964c\u751f\u4eba\u4e86\uff01\u201d \u3000\u3000\u4e8e\u627f\u73e0\u601c\u60af\u5730\u770b\u4ed6\u4e00\u773c\u3002\u94b1\u5858\u6c5f\u65e9\u6f6e\u65b9\u8d77\uff0c\u773c\u5149\u770b\u51fa\u697c\u5916\uff0c\u4f46\u89c1\u6d77\u9e25\u4e09\u4e94\uff0c\u6b63\u968f\u7740\u6f6e\u5934 \u4e0a\u4e0b\uff0c\u9010\u6d6a\u98de\u7fd4\u3002\u94c1\u955c\u5fc3\u9053\uff1a\u201c\u627f\u73e0\uff0c\u4f60\u53ef\u8fd8\u8bb0\u5f97\u54b1\u4eec\u5728\u957f\u6c5f\u5171\u5ea6\u7684\u65f6\u523b\uff0c\u4e5f\u6709\u8fd9\u6837\u7684\u62cd\u5cb8 \u60ca\u6d9b\uff0c\u9010\u6ce2\u6d77\u71d5\uff1f\u201d\u4e8e\u627f\u73e0\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u94b1\u5858\u6c5f\u867d\u7136\u4e0d\u53ca\u957f\u6c5f\u6d69\u8361\uff0c\u4f46\u4e24\u8005\u90fd\u6d41\u5230\u5927 \u6d77\u4e4b\u4e2d\u3002\u201d\u4e8e\u627f\u73e0\u7684\u601d\u60f3\u8dd1\u5f97\u592a\u5feb\uff0c\u94c1\u955c\u5fc3\u8ddf\u5979\u4e0d\u4e0a\uff0c\u8bb8\u4e45\u8bb8\u4e45\u8fd8\u4f1a\u4e0d\u8fc7\u610f\u6765\uff0c\u53ea\u662f\u559f\u7136\u53f9 \u9053\uff1a\u201c\u8fc7\u53bb\u7684\u65e5\u5b50\u771f\u50cf\u6c5f\u6c34\u4e00\u6837\uff0c\u4e00\u6d41\u8fc7\u53bb\u5c31\u4e0d\u4f1a\u56de\u6765\u3002\u627f\u73e0\uff0c\u6211\u771f\u4e0d\u61c2\u5f97\u4f60\u4e3a\u4ec0\u4e48\u4e0e\u6211\u8d8a \u79bb\u8d8a\u8fdc\uff1f\u201d \u3000\u3000\u4e8e\u627f\u73e0\u51c4\u7136\u4e00\u7b11\uff0c\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u61c2\u5f97\u4f60\u7684\u4eba\u6765\u4e86\uff0c\u6211\u8be5\u8d70\u5566\u3002\u201d\u94c1\u955c\u5fc3\u6115\u7136\u56de \u987e\uff0c\u53ea\u89c1\u6c90\u71d5\u7b11\u76c8\u76c8\u5730\u8dd1\u4e0a\u697c\u6765\uff0c\u8fce\u7740\u94c1\u955c\u5fc3\u7b11\u9053\uff1a\u201c\u6664\uff0c\u8fd9\u91cc\u7684\u666f\u8272\u8fd8\u5c45\u7136\u4e0d\u9519\u54e9\u3002\u4e0d\u8fc7 \u6606\u660e\u6625\u65e5\uff0c\u6bd4\u8fd9\u91cc\u66f4\u4f73\uff0c\u8fd9\u4e2a\u65f6\u5019\uff0c\u6843\u82b1\u3001\u674e\u82b1\u3001\u8774\u8776\u82b1\u60f3\u6765\u90fd\u5df2\u5f00\u4e86\u3002\u955c\u5fc3\uff0c\u6211\u7239\u7239\u5df2\u5c06 \u4f60\u4fdd\u91ca\u4e86\uff0c\u4e13\u4f7f\u5e26\u4e86\u5fa1\u65e8\uff0c\u9a6c\u4e0a\u5c31\u6765\uff0c\u4f60\u4e0e\u6211\u4e00\u540c\u5230\u6606\u660e\u53bb\u5427\u3002\u55ef\uff0c\u4e8e\u59d1\u5a18\uff0c\u5e08\u7236\u548c\u53f6\u5927\u54e5 \u90fd\u5728\u4e0b\u9762\uff0c\u600e\u4e48\uff0c\u4f60\u4e0d\u591a\u7559\u4e00\u4f1a\u513f\uff0c\u5c31\u8981\u8d70\u4e86\u3002\u201d\u4e8e\u627f\u73e0\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5728\u8fd9\u697c\u5934\u8d4f\u8d4f\u82b1\u5427\uff0c \u6211\u4e0d\u6253\u6270\u4f60\u4eec\u4e86\uff0c\u55ef\uff0c\u8fd9\u56ed\u4e2d\u4ec0\u4e48\u82b1\u8349\u90fd\u6709\uff0c\u5c31\u53ef\u60dc\u6ca1\u6709\u5927\u9752\u6811\u3002\u201d\u94c1\u955c\u5fc3\u76ee\u9001\u5979\u4e0b\u697c\u800c \u53bb\uff0c\u53ea\u89c1\u53f6\u6210\u6797\u5728\u4e00\u68f5\u5927\u6811\u65c1\u8fb9\uff0c\u6b63\u5728\u5411\u4ed6\u62db\u624b\u3002\u94c1\u955c\u5fc3\u5fc3\u4e2d\u4e00\u9178\uff0c\u51e0\u4e4e\u4e5f\u60f3\u8ffd\u4e0b\u697c\u53bb\uff0c\u4f46 \u5374\u8fd8\u662f\u7ed9\u6c90\u71d5\u7684\u8f7b\u98a6\u6d45\u7b11\u7559\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u6c90\u71d5\u5c06\u524d\u56e0\u540e\u679c\u8bf4\u6e05\uff0c\u94c1\u955c\u5fc3\u8fd9\u624d\u604d\u7136\u5927\u609f\u81ea\u5df1\u4e3a\u4ec0\u4e48\u88ab\u79fb\u5230\u522b\u5885\u4e2d\u5907\u53d7\u4f18\u5f85\uff0c\u95ee\u9053\uff1a \u201c\u6211\u7239\u7239\u5462\uff1f\u201d\u6c90\u71d5\u9053\uff1a\u201c\u5bb6\u7236\u4e45\u4ef0\u4eca\u5c0a\u5927\u4eba\u7684\u9053\u5fb7\u6587\u7ae0\uff0c\u4e5f\u5df2\u8bf7\u51c6\u7687\u4e0a\uff0c\u5c06\u4ed6\u63a5\u5f80\u6606\u660e\u53bb \u4e86\u3002\u201d\u94c1\u955c\u5fc3\u611f\u6fc0\u4e4b\u6781\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u6c90\u5bb6\u7236\u5973\u5bf9\u6211\u8fd9\u6837\u4f53\u8d34\u5165\u5fae\u3002\u6211\u7684\u624d\u5b66\u5230\u5e95\u8fd8\u662f\u6709\u4eba \u8d4f\u8bc6\uff01\u201d \u3000\u3000\u6c90\u71d5\u76ee\u6ce8\u623f\u4e2d\uff0c\u62bf\u5634\u7b11\u9053\uff1a\u201c\u4f60\u7684\u4e1c\u897f\u8fd9\u6837\u51cc\u4e71\uff0c\u54b1\u4eec\u5c31\u8981\u8d70\u5566\uff0c\u6211\u66ff\u4f60\u6536\u62fe\u6536\u62fe\u3002\u201d \u94c1\u955c\u5fc3\u4e0d\u77e5\u4e0d\u89c9\u5730\u8ddf\u5979\u5165\u623f\uff0c\u53ea\u89c1\u6c90\u71d5\u62c8\u8d77\u4e00\u5f20\u8bcd\u7b3a\uff0c\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u6709\u5174\u81f4\u586b\u8bcd\u3002\u201d\u8f7b \u8f7b\u5ff5\u9053\uff1a\u201c\u671b\u91cc\u6625\u5c71\u63a5\u7fe0\u5fae\uff0c\u65e0\u60c5\u98ce\u81ea\u9001\u6f6e\u5f52\uff0c\u94b1\u5858\u6c5f\u4e0a\u6005\u659c\u8f89\u3002\u6211\u4ee5\u6c5f\u6f6e\u6765\u53c8\u53bb\uff0c\u541b\u5982\u9e25 \u9e33\u9010\u6ce2\u98de\uff0c\u4eba\u751f\u77e5\u5df1\u603b\u76f8\u8fdd\u3002\u201d\u94c1\u955c\u5fc3\u5c34\u5c2c\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u56da\u5c45\u90c1\u90c1\uff0c\u7528\u5761\u8001\u8bcd\u610f\u586b\u4e86\u8fd9\u4e00 \u9619\u2018\u6d63\u6eaa\u6c99\u2019\u8c03\uff0c\u6559\u4f60\u89c1\u7b11\u4e86\u3002\u201d\u539f\u6765\u4ed6\u8fd9\u9996\u8bcd\u4e43\u662f\u6000\u5ff5\u4e8e\u627f\u73e0\u7684\uff0c\u8fd9\u65f6\u5fc3\u4e2d\u5374\u662f\u60f3\u9053\uff1a \u201c\u6211\u628a\u4e8e\u627f\u73e0\u5f53\u4f5c\u6211\u7684\u77e5\u5df1\uff0c\u5979\u5374\u5e76\u672a\u628a\u6211\u5f53\u4f5c\u77e5\u5df1\u3002\u54ce\uff0c\u53ea\u6015\u5929\u4e0b\u4e4b\u5927\uff0c\u53ea\u6709\u8fd9\u4f4d\u6c90\u5c0f\u59d0 \u624d\u662f\u6211\u7684\u7ea2\u989c\u77e5\u5df2\u4e86\u3002\u201d \u3000\u3000\u6c90\u71d5\u76c8\u76c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u59b9\u4e0d\u8f9e\u73ed\u95e8\u5f04\u65a7\u4e4b\u8bee\uff0c\u7528\u97e6\u5e84\u8bcd\u610f\uff0c\u4e5f\u6765\u586b\u4e00\u9619\u6d63\u6eaa\u6c99\uff0c\u8bf7 \u4f60\u6307\u6b63\u3002\u201d\u5c31\u63a5\u5728\u94c1\u955c\u5fc3\u8bcd\u7a3f\u4e0b\u9762\uff0c\u6325\u7b14\u5199\u9053\uff1a\u201c\u9152\u51b7\u8bd7\u6b8b\u68a6\u672a\u6b8b\uff0c\u4f24\u5fc3\u660e\u6708\u501a\u680f\u5e72\uff0c\u601d\u541b \u90c1\u90c1\u9526\u887e\u5bd2\u3002\u54ab\u5c3a\u5929\u6daf\u51ed\u68a6\u63a5\uff0c\u5fc6\u6765\u552f\u628a\u65e7\u8bd7\u770b\uff0c\u51e0\u65f6\u643a\u624b\u5165\u957f\u5b89\uff1f\u201d\u97e6\u5e84\u662f\u5510\u671d\u79c0\u624d\uff0c\u540e \u6765\u5949\u4f7f\u5165\u8700\uff0c\u88ab\u524d\u8700\u738b\u738b\u5efa\u7559\u5728\u56db\u5ddd\u505a\u201c\u8bb0\u5ba4\u201d\uff0c\u6c90\u71d5\u7528\u97e6\u5e84\u8bcd\u610f\u586b\u5411\uff0c\u4e0d\u4f46\u66f2\u66f2\u6298\u6298\u5730\u8868 \u8fbe\u4e86\u5979\u7684\u5fc3\u4e8b\uff0c\u800c\u4e14\u662f\u529d\u94c1\u955c\u5fc3\u5b66\u97e6\u5e84\u4e00\u6837\uff0c\u65e2\u7136\u5728\u4e2d\u539f\u4e0d\u5f97\u5fd7\uff0c\u90a3\u5c31\u4e0d\u5982\u5230\u4e91\u5357\u53bb\u4f50\u5979\u4e08 \u4eb2\u3002\u94c1\u955c\u5fc3\u8bfb\u4e86\u6b64\u8bcd\uff0c\u6697\u6697\u79f0\u8d5e\u6c90\u71d5\u7684\u806a\u660e\uff0c\u624b\u6367\u8bcd\u7b3a\uff0c\u6b63\u5f85\u8bf4\u8bdd\uff0c\u4f46\u89c1\u6c90\u71d5\u56de\u7738\u4e00\u7b11\uff0c\u4e24 \u4eba\u5fc3\u610f\u76f8\u901a\uff0c\u4e00\u5207\u7684\u8bdd\u90fd\u4e0d\u5fc5\u518d\u8bf4\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u6c90\u71d5\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u5728\u4e0b\u9762\uff0c\u4f60\u4e0d\u4e0b\u53bb\u548c\u4ed6\u4eec\u89c1\u89c1\u4e48\uff1f\u201d\u94c1\u955c\u5fc3\u4e0e\u6c90\u71d5\u6b65\u51fa \u697c\u5934\uff0c\u53ea\u542c\u5f97\u6c90\u78f7\u5927\u53eb\u5927\u56b7\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u5feb\u5411\u627f\u73e0\u59d0\u59d0\u9053\u559c\uff0c\u54b1\u4eec\u5feb\u8981\u559d\u5979\u7684\u559c\u9152\u5566\u3002\u201d \u539f\u6765\u6c90\u78f7\u4ece\u5c0f\u864e\u5b50\u53e3\u4e2d\uff0c\u63a2\u542c\u5230\u5343\u627f\u73e0\u5df2\u7531\u5f20\u4e39\u67ab\u4f5c\u4e3b\uff0c\u4e0e\u53f6\u6210\u6797\u7684\u5a5a\u4e8b\u5b9a\u4e86\u3002\u6c90\u78f7\u6709\u70b9\u5931 \u671b\uff0c\u4f46\u5374\u662f\u9ad8\u9ad8\u5174\u5174\u5730\u5927\u53eb\u5927\u56b7\u51fa\u6765\u3002 \u3000\u3000\u6c90\u71d5\u7b11\u9053\uff1a\u201c\u662f\u4e48\uff1f\u201d\u4f46\u89c1\u4e8e\u627f\u73e0\u6ee1\u9762\u98de\u7ea2\uff0c\u9053\uff1a\u201c\u4f60\u542c\u8fd9\u5c0f\u9b3c\u5934\u4e71\u8bf4\uff0c\u6c90\u78f7\uff0c\u4f60\u7b49\u7740 \u5148\u559d\u4f60\u59d0\u59d0\u7684\u559c\u9152\u5427\u3002\u55ef\uff0c\u6211\u5f97\u56de\u53bb\u89c1\u5e08\u7236\u5566\uff0c\u4f60\u4eec\u4e0d\u5fc5\u4e0b\u697c\u76f8\u9001\u4e86\u3002\u201d\u94c1\u955c\u5fc3\u501a\u697c\u51dd\u671b\uff0c \u53ea\u89c1\u53f6\u6210\u6797\u5df2\u4e0e\u4e8e\u627f\u73e0\u8d70\u51fa\u56ed\u95e8\uff0c\u5411\u4ed6\u6325\u624b\u9053\u522b\u4e86\u3002\u94c1\u955c\u5fc3\u6709\u4e9b\u60c6\u6005\uff0c\u53ea\u542c\u5f97\u6c90\u71d5\u5a07\u58f0\u8bf4 \u9053\uff1a\u201c\u4e1c\u897f\u6536\u62fe\u597d\u4e86\uff0c\u54b1\u4eec\u4e5f\u8be5\u8d70\u5566\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u60c6\u6005\u6653\u83ba\u6b8b\u6708\u68a6\uff0c\u68a6\u4e2d\u957f\u8bb0\u8bef\u968f\u8f66\uff0c\u6b64\u4e2d\u60c5\u610f\u603b\u582a\u55df\uff01 \u3000\u3000\u5927\u6811\u51cc\u4e91\u6297\u98ce\u96ea\uff0c\u6c5f\u5357\u73ab\u7470\u7c07\u671d\u971e\uff0c\u5404\u968f\u7f18\u5206\u522b\u5929\u6daf\u3002 \u3000\u3000\uff08\u5168\u4e66\u5b8c\uff09"}),showOnHomepage:!0}]}}]);